"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[34728],{22041:(e,X,n)=>{n.r(X),n.d(X,{default:()=>t});var s=n(74848);const t=[{name:"\u6b66\u6797\u5929\u9a84",content:(0,s.jsx)(s.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u9e33\u9e2f\u540c\u547d \u3000\u3000\u62c2\u62ed\u6b8b\u7891\uff0c\u6555\u98de\u5b57\u4f9d\u7a00\u582a\u8bfb\uff0c\u6168\u5f53\u521d\u501a\u98de\u4f55\u91cd\uff0c\u540e\u6765\u4f55\u9177\uff0c\u679c\u662f\u529f\u6210\u8eab\u5408\u6b7b\uff0c\u53ef\u601c\u4e8b\u53bb \u8a00\u96be\u8d4e\u3002\u6700\u65e0\u8f9c\uff0c\u582a\u6068\u66f4\u582a\u60b2\uff0c\u98ce\u6ce2\u72f1\uff01\u5c82\u4e0d\u5ff5\uff0c\u4e2d\u539f\u8e59\uff1f\u5c82\u4e0d\u5ff5\uff0c\u5fbd\u94a6\u8fb1\uff1f\u5ff5\u5fbd\u94a6\u65e2\u8fd4\uff0c \u6b64\u8eab\u5c5e\u8c01\uff1f\u5343\u8f7d\u4f11\u8c08\u5357\u6e21\u9519\uff0c\u5f53\u65f6\u81ea\u6015\u4e2d\u539f\u590d\uff0c\u7b11\u533a\u533a\uff0c\u7b11\u533a\u533a\uff0c\u4e00\u6867\u4ea6\u4f55\u80fd\uff0c\u9022\u5176\u6b32\uff0e\u2014 \u2014\u6587\u5fb5\u660e\u6ee1\u6c5f\u7ea2\u5915\u7167\u82cd\u82d4\u4e0a\uff0c\u9e1f\u9e23\u5c71\u66f4\u5e7d\u3002\u8fd9\u6761\u5c71\u8def\uff0c\u663e\u7136\u662f\u5f88\u5c11\u4eba\u884c\uff0c\u5ca9\u77f3\u4e0a\u6ee1\u662f\u6854\u7ea2 \u7684\u3001\u96ea\u9752\u7684\uff0c\u6216\u8349\u9ec4\u8272\u7684\u9c9c\u82d4\u3002\u82cd\u677e\u6620\u886c\u7ea2\u5d16\uff0c\u91ce\u82b1\u67ab\u53f6\u4e89\u8273\uff0c\u5728\u8fd9\u79cb\u672b\u51ac\u521d\uff0c\u5df2\u5bd2\u672b\u51b7\u7684 \u65f6\u5019\uff0c\u5c71\u4e0a\u5230\u5904\u8fd8\u662f\u7470\u4e3d\u7684\u8272\u5f69\u3002 \u3000\u3000\u5728\u8fd9\u5c11\u4eba\u884c\u8d70\u7684\u8352\u5c71\u50fb\u5f84\uff0c\u6b64\u9645\u5374\u6709\u4e00\u4e2a\u5c11\u5987\uff0c\u6311\u7740\u4e24\u6346\u67f4\u8349\u56de\u5bb6\u3002 \u3000\u3000\u867d\u7136\u662f\u8346\u9497\u88d9\u5e03\uff0c\u4e5f\u63a9\u76d6\u4e0d\u4e86\u5979\u79c0\u4e3d\u7684\u5bb9\u989c\u3002 \u3000\u3000\u5979\u662f\u4e00\u4e2a\u730e\u6237\u7684\u59bb\u5b50\uff0c\u6216\u8bb8\u662f\u56e0\u8d70\u60ef\u5c71\u8def\u4e86\uff0c\u5979\u6311\u7740\u67f4\u8349\uff0c\u8e0f\u5728\u957f\u6ee1\u82cd\u82d4\u7684\u77f3\u5934\u4e0a\uff0c\u6b65 \u5c65\u4f9d\u7136\u751a\u662f\u5b89\u7965\u3002 \u3000\u3000\u5e73\u65f6\u5979\u5f88\u559c\u6b22\u770b\u4e91\u770b\u5c71\uff0c\u4f46\u6b64\u9645\u5c71\u95f4\u7684\u666f\u8272\u867d\u7136\u4efd\u5916\u6e05\u5e7d\uff0c\u5979\u7684\u5fc3\u60c5\u5374\u6709\u70b9\u513f\u4e0d\u5927\u5e73 \u9759\u3002 \u3000\u3000\u524d\u4e24\u5929\uff0c\u6709\u8bb8\u591a\u96be\u6c11\u4ece\u5c71\u4e0b\u7ecf\u8fc7\uff0c\u542c\u8bf4\u662f\u91d1\u56fd\u53c8\u8981\u548c\u5b8b\u56fd\u6253\u4ed7\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u5c71\u662f\u5ea7\u843d\u5728\u9655\u897f\u5927\u6563\u897f\u5317\u9762\u7684\u76d8\u9f99\u5c71\uff0c\u65f6\u4e3a\u5357\u5b8b\u7ecd\u5174\u5341\u5e74\uff0c\u91d1\u5b8b\u8bae\u548c\uff0c\u4ee5\u5927\u6563\u5173\u4e3a \u754c\uff0c\u897f\u5317\u9762\u672c\u6765\u5c5e\u4e8e\u5b8b\u56fd\u7684\u5730\u65b9\uff0c\u5982\u4eca\u5df2\u662f\u5c5e\u4e8e\u91d1\u56fd\u7edf\u6cbb\u3001\u8fd9\u4e2a\u5c11\u5987\u662f\u6c49\u4eba\uff0c\u542c\u5f97\u91d1\u5175\u653b\u5b8b \u7684\u6d88\u606f\uff0c\u5fc3\u60c5\u56de\u81ea\u662f\u6709\u70b9\u4e0d\u5b89\u3002 \u3000\u3000\u4e0d\u8fc7\u5979\u4e00\u60f3\u5230\u6b63\u5728\u7b49\u5f85\u5979\u56de\u5bb6\u7684\u4e08\u592b\uff0c\u60f3\u5230\u5979\u90a3\u6d3b\u6cfc\u53ef\u7231\u7684\u5b69\u5b50\uff0c\u5979\u7684\u5fc3\u4e2d\u53c8\u5145\u6ee1\u559c\u60a6 \u4e86\u3002 \u3000\u3000\u5916\u95f4\u867d\u7136\u70fd\u706b\u5f25\u5929\uff0c\u8fd9\u5ea7\u8352\u5c71\u5374\u4e00\u5411\u662f\u5f20\u96ea\u6ce2\u7684\u3002\u9664\u4e86\u4e08\u592b\u548c\u5b69\u5b50\uff0c\u5979\u7684\u7236\u4eb2\u548c\u516c\u516c\u4e5f \u8fd8\u5065\u5728\uff0c\u4e24\u5bb6\u65e9\u5df2\u5408\u6210\u4e00\u5bb6\u3002\u5979\u6709\u4e2a\u6e29\u6696\u7684\u5bb6\uff0c\u53ea\u76fc\u4e00\u751f\u80fd\u8fc7\u8fd9\u6837\u5e73\u9759\u7684\u65e5\u5b50\uff0c\u4e8e\u613f\u5df2\u8db3\u3002 \u5fc3\u4e2d\u6b63\u81ea\u5145\u6ee1\u871c\u610f\u67d4\u60c5\uff0c\u5ffd\u5730\u65e0\u7aef\u522e\u6765\u4e00\u80a1\u72c2\u98ce\uff0c\u5413\u4e86\u5979\u4e00\u8df3\u3002 \u3000\u3000\u8fd9\u80a1\u602a\u98ce\u7a81\u5982\u5176\u6765\uff0c\u968f\u7740\u8fd9\u80a1\u602a\u98ce\u51fa\u73b0\u7684\u662f\u4e00\u53ea\u540a\u775b\u767d\u989d\u864e\u3002 \u3000\u3000\u5c11\u5987\u88ab\u731b\u864e\u4e00\u6251\uff0c\u6254\u5f00\u67f4\u8349\uff0c\u62a1\u8d77\u6241\u62c5\u5c31\u6253\u3002\u5979\u773c\u660e\u624b\u5feb\uff0c\u8fd9\u4e00\u6253\u5012\u662f\u6253\u4e2a\u6b63\u7740\uff0c\u6070\u597d \u6253\u7740\u4e86\u8001\u864e\u7684\u989d\u5934\u3002\u4f46\u53ef\u60dc\u8001\u864e\u76ae\u7c97\u8089\u539a\uff0c\u5934\u9885\u7adf\u4f3c\u6bd4\u77f3\u5934\u8fd8\u786c\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u6241\u62c5\u65ad \u4e86\u3002 \u3000\u3000\u8001\u864e\u8d1f\u4f24\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u597d\u4f3c\u6674\u5929\u8d77\u4e2a\u9739\u96f3\uff0c\u9707\u5f97\u5c71\u5c97\u4e5f\u52a8\uff0c\u731b\u5730\u6251\u6765\u3002 \u3000\u3000\u5c11\u5987\u4e00\u95ea\uff0c\u95ea\u5728\u8001\u864e\u80cc\u540e\uff0c\u8001\u864e\u524d\u722a\u63b0\u642d\u5730\uff0c\u8170\u80ef\u4e00\u6380\uff0c\u5c11\u5987\u624b\u4e2d\u6ca1\u6709\u6b66\u5668\uff0c\u53ea\u51ed\u4e00\u53cc \u8089\u638c\uff0c\u81ea\u5fd6\u5bf9\u4ed8\u4e0d\u4e86\u8fd9\u53ea\u8001\u864e\uff0c\u53ea\u80fd\u518d\u95ea\u3002\u8001\u864e\u6380\u5979\u4e0d\u7740\uff0c\u628a\u94c1\u68d2\u4f3c\u7684\u864e\u5c3e\u7ad6\u8d77\u6765\u4e00\u526a\uff0c\u8fd9 \u4e00\u526a\u626c\u8d77\u98ce\u6c99\uff0c\u5c11\u5987\u773c\u4e2d\u5439\u8fdb\u4e00\u7c92\u6c99\u5b50\uff0c\u6d41\u51fa\u773c\u6cea\uff0c\u770b\u4e0d\u771f\u5207\uff0c\u51e0\u7ed9\u5b83\u6251\u7740\u3002\u5c11\u5987\u614c\u5fd9\u65bd\u5c55 \u8f7b\u529f\u9003\u8dd1\u3002\u5979\u5fc3\u91cc\u4e00\u614c\uff0c\u811a\u6b65\u5c31\u4e0d\u80fd\u8e0f\u5f97\u90a3\u4e48\u7a33\u4e86\uff0c\u8e0f\u7740\u77f3\u4e0a\u7684\u82cd\u82d4\uff0c\u811a\u6b65\u4e00\u6ed1\uff0c\u7adf\u7136\u5728\u8fd9 \u7d27\u6025\u7684\u5173\u5934\uff0c\u6454\u4e86\u4e00\u8de4\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8001\u864e\u5df2\u7ecf\u6251\u5230\u5979\u7684\u80cc\u540e\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u96ea\u59b9\u83ab\u614c\uff0c\u6211\u6765\u4e86\uff01\u201d\u4eba\u672a\u5230\uff0c\u77f3\u5934\u5148\u6253\u8fc7 \u6765\u3002 \u3000\u3000\u8fd9\u5757\u77f3\u5934\u4e5f\u6253\u4e2a\u6b63\u7740\uff0c\u8001\u864e\u88ab\u6253\u5f97\u5934\u7834\u8840\u6d41\uff0c\u4e00\u6251\u6251\u4e86\u4e2a\u7a7a\uff0c\u5c11\u5987\u6eda\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5979\u7684\u4e08\u592b\u5df2\u7ecf\u8fce\u4e0a\u90a3\u5934\u731b\u864e\u3002\u4e24\u53ea\u624b\u628a\u8001\u864e\u5934\u76ae\u63ea\u4f4f\uff0c\u4e00\u6309\u6309\u5c06\u4e0b \u6765\uff0c\u94c1\u62f3\u731b\u51fb\u3002\u4ed6\u7684\u62f3\u5934\u6bd4\u5c11\u5987\u7684\u6241\u62c5\u66f4\u4e3a\u6709\u529b\uff0c\u6253\u4e86\u4e09\u56db\u62f3\uff0c\u8001\u864e\u8111\u6d46\u8ff8\u6d41\uff0c\u5929\u7075\u76d6\u7adf\u7136 \u88ab\u4ed6\u7684\u62f3\u6253\u7834\uff0c\u6b7b\u4e86\u3002 \u3000\u3000\u4e08\u592b\u6276\u8d77\u59bb\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u5c11\u5987\u60ca\u9b42\u7a0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u64e6\u7834\u4e00\u70b9\u8868\u76ae\uff0c\u773c\u775b\u6e17\u8fdb\u4e00\u7c92\u6c99\u5b50\uff0c\u4e0d\u5927\u8212 \u670d\u3002\u201d \u3000\u3000\u4e08\u592b\u4ed4\u7ec6\u5bdf\u770b\uff0c\u679c\u7136\u53ea\u662f\u64e6\u7834\u4e00\u70b9\u8089\u76ae\uff0c\u8fde\u8f7b\u4f24\u90fd\u7b97\u4e0d\u4e0a\uff0c\u4ed6\u7ed9\u59bb\u5b50\u62df\u8ba2\u773c\u775b\uff0c\u5439\u4e00\u53e3 \u6c14\uff0c\u90a3\u7c92\u6c99\u5b50\u4e5f\u5c31\u968f\u7740\u773c\u6cea\u6d41\u51fa\u6765\u4e86\u3002\u201c\u96ea\u59b9\uff0c\u4f60\u7684\u8fd0\u6c14\u8fd8\u7b97\u4e0d\u574f\u3002\u201d\u4e08\u592b\u7b11\u9053\u3002\u59bb\u5b50\u8ddf\u7740 \u7b11\u9053\uff1a\u201c\u6211\u7684\u8fd0\u6c14\u5f53\u7136\u4e0d\u574f\uff0c\u6211\u6700\u5927\u7684\u5e78\u8fd0\u5c31\u662f\u78b0\u4e0a\u4f60\uff0c\u80fd\u591f\u5f97\u9053\u4e00\u4e2a\u4f60\u8fd9\u6837\u597d\u7684\u4e08\u592b\u3002 \u6210\uff0c\u8fd9\u662f\u4f60\u7b2c\u4e8c\u6b21\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d\u539f\u6765\u8fd9\u5c11\u5987\u53eb\u5f20\u96ea\u6ce2\uff0c\u5979\u7684\u4e08\u592b\u53eb\u8c2d\u9053 \u6210\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u81ea\u5c0f\u4e00\u540c\u5728\u8fd9\u5c71\u4e2d\u957f\u5927\u7684\u3002\u4e0d\u8fc7\u4ed6\u4eec\u90fd\u4e0d\u662f\u672c\u5730\u4eba\uff0c\u90fd\u662f\u4e3a\u4e86\u8eb2\u907f\u6218\u4e89\u7684\u707e\u96be\u9003 \u5230\u8fd9\u5ea7\u8352\u5c71\u7684\uff0c\u8c2d\u5bb6\u5148\u6765\uff0c\u5f20\u5bb6\u540e\u5230\u3002 \u3000\u3000\u4e03\u5e74\u524d\u5f20\u96ea\u6ce2\u66fe\u7ecf\u5728\u6811\u6797\u91cc\u78b0\u4e0a\u4e00\u6761\u5927\u9752\u72fc\uff0c\u90a3\u6b21\u4e5f\u662f\u8c2d\u9053\u6210\u628a\u6076\u72e0\u6253\u6b7b\u7684\u3002\u4e0d\u8fc7\u90a3\u6b21 \u8c2d\u9053\u6210\u6765\u5f97\u66f4\u65e9\uff0c\u9752\u72fc\u521a\u51fa\u73b0\uff0c\u4eba\u517d\u5c1a\u672b\u76f8\u6597\uff0c\u8c2d\u9053\u6210\u5c31\u5df2\u6765\u5230\u5979\u7684\u9762\u524d\uff0c\u6740\u4e86\u6076\u72e0\u3002\u5f20\u96ea \u6ce2\u4e5f\u662f\u5728\u90a3\u6b21\u9047\u9669\u4e4b\u540e\u4e0d\u4e45\uff0c\u5ac1\u7ed9\u8c2d\u9053\u6210\u505a\u59bb\u5b50\u7684\u3002 \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u90a3\u5934\u9752\u72fc\u662f\u54b1\u4eec\u7684\u5a92\u4eba\uff0c\u6211\u600e\u80fd\u5fd8\u8bb0\u3002\u4e0d\u8fc7\u6211\u5374\u4e00\u76f4\u4e0d\u77e5\u4f60\u4f1a\u6b66\u529f\uff0c\u4f60 \u4e3a\u4f55\u7792\u4f4f\u6211\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u88ab\u5927\u592b\u8d28\u95ee\uff0c\u4f3c\u4e4e\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u5ff8\u5ff8\u6029\u6029\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e24\u4e0b\u628a\u5f0f\u4e5f\u79f0\u5f97\u662f\u6b66 \u529f\u5417\uff1f\u6562\u60c5\u53ea\u80fd\u7b97\u662f\u4e09\u811a\u732b\u7684\u529f\u592b\u5427\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e09\u811a\u732b\u529f\u592b\uff1f\u4e09\u811a\u732b\u662f\u8fde\u8001\u9f20\u4e5f\u6349\u4e0d\u5230\u7684\uff0c\u4f60\u8fd9\u2018\u4e09\u811a\u63cf\u2019\u7684 \u529f\u592b\u5374\u80fd\u6253\u8001\u864e\uff01\u6211\u4e0d\u77e5\u9053\u662f\u771f\u7684\u4e0d\u77e5\u8fd8\u662f\u5047\u7684\u4e0d\u77e5\uff0c\u4f46\u4f60\u7ec3\u7684\u53ef\u662f\u4e0a\u4e58\u7684\u6b66\u529f\u5462\uff01\u201d\u5f20\u96ea \u6ce2\u9053\uff1a\u201c\u54e6\uff0c\u4e0a\u4e58\u6b66\u529f\uff1f\u201d\u8a00\u4e0b\u4f3c\u4e4e\u8fd8\u662f\u4e0d\u6562\u76f8\u4fe1\u7684\u795e\u6c14\u3002 \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u600e\u4f1a\u9a97\u4f60\uff1f\u4f60\u7ec3\u7684\u672c\u6765\u662f\u4e0a\u4e58\u6b66\u529f\uff0c\u53ea\u53ef\u60dc\u4f60\u5b8c\u5168\u6ca1\u6709\u5bf9\u654c\u7684\u7ecf\u9a8c\uff0c\u7ed9 \u8001\u864e\u5413\u614c\u4e86\u3002\u5047\u5982\u4f60\u7a0d\u4e3a\u9547\u5b9a\u4e00\u4e9b\uff0c\u7528\u4e0d\u7740\u6211\u5e2e\u624b\uff0c\u4f60\u81ea\u5df1\u5c31\u53ef\u4ee5\u628a\u8001\u864e\u6253\u6b7b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u4f46\u6211\u521a\u624d\u5df2\u7ecf\u662f\u7528\u529b\u6253\u5b83\u4e86\u3002\u4e00\u6253\u6241\u62c5\u5c31\u65ad\uff0c\u6211\u8d64\u624b\u7a7a\u62f3\uff0c\u5982\u4f55 \u8fd8\u80fd\u6253\u6b7b\u8001\u864e\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u5f53\u7136\u8fd8\u5f97\u6709\u70b9\u730e\u864e\u7684\u7ecf\u9a8c\uff0c\u6211\u6559\u4f60\u600e\u6837\u6253\u8001\u864e\u5427\u3002\u8001\u864e\u7684\u5934\u9885\u6700\u786c\uff0c\u4f60 \u6c14\u529b\u4e0d\u8db3\uff0c\u5c31\u4e0d\u8981\u5148\u6253\u5b83\u7684\u5934\u90e8\uff0c\u6700\u7701\u6c14\u529b\u7684\u529e\u6cd5\u662f\u5148\u628a\u5b83\u7684\u773c\u775b\u6253\u778e\uff0c\u5b83\u53d1\u4e86\u72c2\uff0c\u7136\u540e\u4f60 \u518d\u8eb2\u5230\u60ac\u5d16\u65c1\u8fb9\uff0c\u6545\u610f\u5f04\u51fa\u4e00\u70b9\u58f0\u97f3\uff0c\u5f15\u8bf1\u5b83\u6765\u6251\u4f60\uff0c\u8fd9\u6837\u5b83\u5c31\u4f1a\u81ea\u5df1\u8dcc\u4e0b\u60ac\u5d16\u6b7b\u6389\u3001\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u77bf\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u8fd9\u4e2a\u529e\u6cd5\u771f\u597d\u3002\u6211\u600e\u7684\u6ca1\u6709\u60f3\u5230\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8f7b\u529f\u8eab\u6cd5\u8f7b\u7075\u4f73\u5999\uff0c\u53ea\u53ef\u60dc\u4e5f\u662f\u7ed9\u5413\u5f97\u614c\u4e86\uff0c\u624d\u4f1a\u6454\u90a3\u8de4\uff0c\u8f7b \u529f\u63d0\u7eb5\u672f\u662f\u5fc5\u987b\u61c2\u5f97\u5982\u4f55\u8fd0\u7528\u771f\u6c14\u7684\uff0c\u8fd9\u5c31\u5df2\u7ecf\u662f\u5c5e\u4e8e\u5185\u529f\u7684\u8303\u56f4\u4e86\u3002\u4e0a\u4e58\u6b66\u529f\u662f\u4ee5\u5185\u529f\u4e3a \u57fa\u7840\u7684\uff0c\u4ee5\u4f60\u76ee\u524d\u7684\u9020\u8be3\u6765\u8bf4\uff0c\u867d\u7136\u8fd8\u4e0d\u80fd\u8bf4\u662f\u6df1\u539a\uff0c\u4f46\u6211\u8bf4\u4f60\u7ec3\u7684\u662f\u4e0a\u4e58\u6b66\u529f\uff0c\u5219\u662f\u6ca1\u6709 \u9519\u7684\u3002\u5bf9\u5566\uff0c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9\u6211\uff0c\u4f60\u61c2\u5f97\u6b66\u529f\u3002\u5374\u4e3a\u4f55\u7792\u4f4f\u6211\u5462\uff1f\u201d\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u6211\u7684\u529f\u592b \u662f\u7239\u7239\u6559\u7684\uff0c\u7239\u7239\u8bf4\u8fd9\u53ea\u4e61\u4e0b\u4eba\u7684\u628a\u5f0f\uff0c\u89c1\u4e0d\u5f97\u884c\u5bb6\u7684\u3002\u6211\u5c0f\u65f6\u5019\u8eab\u5b50\u5f31\uff0c\u7239\u7239\u6559\u6211\u7ec3\u6b66\u3002 \u53ea\u662f\u5e0c\u671b\u80fd\u591f\u795b\u75c5\u5ef6\u5e74\u3002\u4ed6\u5429\u5490\u8fc7\u6211\uff0c\u4e0d\u8981\u7ed9\u5916\u4eba\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u6e29\u9053\uff1a\u201c\u6211\u662f\u5916\u4eba\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u662f\u5916\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6211\u77e5\u9053\u4f60\u7684\u6b66\u529f\u5f88\u597d\uff0c\u6211\u8fd9\u70b9\u4e61\u4e0b\u4eba\u7684\u628a\u5f0f\uff0c \u6015\u4f60\u7b11\u8bdd\uff0c\u6240\u4ee5\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u4f60\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u73b0\u5728\u4f60\u544a\u8bc9\u6211\u662f\u4e0a\u4e58\u6b66\u529f\uff0c\u6211\u8fd8\u4e0d\u5927\u6562\u76f8\u4fe1 \u5462\u3002\u6210\u54e5\uff0c\u6211\u4e0d\u662f\u5b58\u5fc3\u7792\u4f60\u7684\uff0c\u4f60\u607c\u6211\u5417\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u662f\u4ec0\u4e48\u7d27\u8981\u4e8b\u60c5\uff0c\u6211\u4e0d\u8fc7\u56e0\u4e3a\u4e00\u5411\u4e0d\u77e5\u4f60\u4f1a\u6b66\u529f\uff0c\u5fcd\u4e0d\u4f4f\u5728\u6709\u70b9 \u597d\u5947\uff0c\u624d\u95ee\u4e00\u95ee\u4f60\u3002\u539f\u6765\u4f60\u771f\u7684\u4e0d\u77e5\u8fd9\u662f\u4e0a\u4e58\u6b66\u529f\u3001\u6211\u600e\u4f1a\u607c\u4f60\u3002\u201d \u3000\u3000\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u5728\u4ed6\u7684\u5fc3\u91cc\u53ef\u662f\u7740\u5b9e\u6709\u70b9\u7591\u60d1\uff0c\u89c9\u5f97\u59bb\u5b50\u7684\u89e3\u91ca\uff0c\u7406\u7531\u4f3c\u4e4e\u4e0d\u600e\u4e48\u5145\u8db3\u3002 \u518d\u8bf4\uff0c\u5373\u4f7f\u59bb\u5b50\u662f\u771f\u7684\u4e0d\u77e5\u8fd9\u662f\u4e0a\u4e58\u6b66\u529f\uff0c\u4f46\u8eab\u6000\u7edd\u6280\u7684\u5cb3\u7236\uff0c\u5374\u53c8\u4e3a\u4f55\u8fd9\u8bb8\u591a\u5e74\u6765\u4e00\u76f4\u6df1 \u85cf\u4e0d\u9732\uff1f\u4f46\u867d\u7136\u5fc3\u4e2d\u5df2\u6709\u601d\u7591\uff0c\u4ed6\u8fd8\u662f\u4e0d\u4f1a\u6000\u7591\u59bb\u5b50\u5bf9\u4ed6\u7684\u611f\u60c5\u7684\uff0c\u4ed6\u4eec\u662f\u4ece\u5c0f\u4e00\u8d77\u957f\u5927\u7684 \u6069\u7231\u592b\u59bb\uff0c\u5f7c\u6b64\u90fd\u662f\u7231\u5bf9\u65b9\u751a\u4e8e\u7231\u81ea\u5df1\u7684\u3002 \u3000\u3000\u4e0d\u4f46\u4e0d\u4f1a\u6000\u7591\u59bb\u5b50\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u6000\u7591\u5cb3\u7236\u5bf9\u4ed6\u7684\u75bc\u7231\u3002\u5cb3\u7236\u53ea\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u5c82\u4ec5\u53ea\u662f\u628a\u4ed6 \u89c6\u540c\u201c\u4f34\u5b50\u201d\uff0c\u7b80\u76f4\u662f\u628a\u4ed6\u5f53\u4f5c\u4eb2\u751f\u513f\u5b50\u4e00\u822c\uff0c\u8fd9\u79cd\u60c5\u5982\u9aa8\u8089\u4e4b\u7231\uff0c\u4ed6\u4e5f\u662f\u4e0d\u80fd\u7f6e\u7591\u3002\u201c\u5cb3 \u7236\u4e0d\u8ba9\u6211\u77e5\u9053\u4ed6\u4f1a\u4e0a\u4e58\u6b66\u529f\uff0c\u60f3\u5fc5\u5176\u4e2d\u5b9a\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u672b\u5230\u65f6\u673a\uff0c\u4ed6\u5c31\u4e0d\u80fd\u8ba9\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u56fa\u7136\u601d\u7591\u4e0d\u5b9a\uff0c\u6b8a\u4e0d\u77e5\u4ed6\u7684\u59bb\u5b50\u4e5f\u662f\u548c\u4ed6\u6709\u7740\u540c\u6837\u7684\u601d\u7591\u3002\u539f\u6765\u5979\u7684\u7239\u7239\u662f\u6697\u4e2d \u6559\u5979\u7ec3\u6b66\u7684\uff0c\u4e0d\u4ec5\u53ee\u5631\u5979\u4e0d\u8bb8\u5411\u201c\u5916\u4eba\u201d\u6cc4\u9732\u7684\u3002\u800c\u4e14\u662f\u53ee\u5631\u5979\u4e0d\u8bb8\u8fd9\u201c\u4efb\u4f55\u4eba\u201d\u6cc4\u9732\u7684\u3002 \u8fd9\u201c\u4efb\u4f55\u4eba\u201d\u5f53\u7136\u5305\u62ec\u5979\u7684\u4e08\u592b\u5728\u5185\u3002 \u3000\u3000\u4e0d\u4ec5\u8fd9\u4e8b\u60c5\uff0c\u5979\u7684\u7239\u7239\u8fd8\u6709\u66f4\u5927\u7684\u79d8\u5bc6\u4e86\uff0c\u8fd9\u6b21\u5979\u5df2\u662f\u4e08\u592b\u77e5\u9053\u5979\u7684\u7239\u7239\u61c2\u5f97\u4e0a\u4e58\u6b66\u529f \u7684\u79d8\u5bc6\u4e86\uff0c\u597d\u5728\u8fd8\u672a\u77e5\u9053\u66f4\u5927\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5728\u5979\u7684\u60f3\u6cd5\uff0c\u5979\u7684\u4efb\u4f55\u79d8\u5bc6\u90fd\u662f\u4e0d\u8be5\u7792\u4f4f\u4e08\u592b\u7684\uff0c\u4f46\u7239\u7239\u90d1\u91cd\u7684\u53ee\u549b\uff0c\u5979\u5374\u4e0d\u80fd\u8fdd\u80cc\u3002 \u3000\u3000\u6b64\u65f6\u5979\u7684\u5fc3\u91cc\u96be\u514d\u6709\u70b9\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u201c\u7239\u7239\u77e5\u9053\u6211\u5730\u9732\u4e86\u5bb6\u4f20\u6b66\u529f\u7684\u79d8\u5bc6\uff0c\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u9a82 \u6211\uff1f\u5509\uff0c\u4f46\u6211\u78b0\u4e0a\u8001\u864e\uff0c\u5374\u53c8\u600e\u80fd\u4e0d\u4f7f\u51fa\u6b66\u529f\uff1f\u7ed9\u6210\u54e5\u7740\u7834\uff0c\u6211\u53c8\u600e\u80fd\u7ee7\u7eed\u7792\u4ed6\uff1f\u5982\u4eca\u6211\u4e0d \u8be5\u8bf4\u7684\u90fd\u5df2\u8bf4\u4e86\uff0c\u53ea\u6709\u5f85\u6211\u56de\u5bb6\u4e4b\u540e\uff0c\u4eca\u665a\u518d\u5411\u7239\u7239\u7980\u660e\uff0c\u6c42\u7239\u7239\u539f\u8c05\u4e86\u3002\u201d \u3000\u3000\u6b63\u81ea\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5ffd\u542c\u5f97\u4e08\u592b\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u6709\u4e00\u53e5\u8bdd\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\u201d\uff1f\u5f20\u96ea\u6ce2\u5fc3\u5934\u4e00 \u8df3\uff0c\u7b11\u9053\u201c\u54b1\u4eec\u90fd\u5df1\u7ecf\u505a\u4e86\u4e94\u516d\u5e74\u592b\u59bb\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u4e0d\u80fd\u8bf4\u7684\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u5450\u5450\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u89c9\u5f97\u4f60\u7239\u7239\u6709\uff02\u4e00\u6709\u70b9\u5947\u602a\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u5b9a\u7740\u773c\u775b\u770b\u4ed6\uff0c\u201c\u6211\u7239\u7239\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u89c9\u5f97\u4f60\u4eec \u7236\u5973\u548c\u4e00\u822c\u4eba\u5bb6\u7684\u7236\u5973\u597d\u50cf\u6709\u70b9\u4e0d\u5927\u4e00\u6837\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fc3\u5934\u535c\u901a\u4e00\u8df3\uff1a\u201c\u83ab\u975e\u4ed6\u5df2\u77e5\u9053\u7239\u7239\u7684\u4e00\u4e9b\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u548c\u7239\u7239 \u4e0d\u4e5f\u662f\u548c\u522b\u4eba\u5bb6\u7684\u7236\u5973\u4e00\u822c\u5417\uff1f\u53c8\u6709\u4ec0\u4e48\u4e24\u6837\u4e86\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u82e5\u6709\u6240\u601d\uff0c\u534a\u664c\u65b9\u59cb\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u8bb0\u5f97\u5c0f\u65f6\u5019\u54b1\u4eec\u4fe9\u90fd\u662f\u4e00\u6837\u987d\u76ae\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u628a\u81ea\u5df1\u62c9\u6765\u4f5c\u966a\u886c\uff0c\u8fd9\u70b9\u6211\u8fd8\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u987d\u76ae\u7684\u53ea\u662f\u6211\uff0c\u4f60\u53ef \u662f\u4e56\u5b69\u5b50\u5462\u3002\u6211\u5e38\u5e38\u6b3a\u8d1f\u4f60\uff0c\u4f60\u90fd\u5bf9\u6211\u5fcd\u8ba9\u7684\u3002\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4e0d\uff0c\u6709\u65f6\u5019\u6211\u4e5f\u5fcd\u4e0d\u4f4f\u751f\u4f60 \u7684\u6c14\u7684\u3002\u8fd8\u8bb0\u5f97\u5417\uff0c\u6709\u4e00\u6b21\u6211\u6050\u5413\u4f60\uff0c\u8bf4\u8981\u6253\u4f60\u7684\u8033\u5149\uff0c\u6211\u4e00\u5413\u4f60\uff0c\u4f60\u5c31\u54ed\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u6211\u4e00\u54ed\uff0c\u4f60\u5c31\u5411\u6211\u6c42\u9976\u3002\u7ed3\u679c\u4e0d\u662f\u4f60\u6253\u4e86\u6211\uff0c\u800c\u662f\u6211\u6253\u4e86\u4f60\u3002\u201d\u5979\u987f\u4e86 \u4e00\u987f\uff0c\u542b\u7740\u51e0\u5206\u8be7\u5f02\u7684\u76ee\u5149\u6ce8\u89c6\u7740\u4e08\u592b\u8bf4\u9053\uff1a\u201c\u4f60\u63d0\u8d77\u54b1\u4eec\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\u5e72\u5417\uff1f\u8fd9\u548c\u6211\u4eec\u7236 \u5973\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f3c\u4e4e\u79bb\u9898\u592a\u8fdc\u4e86\u5427\uff1f\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u89c9\u5f97\u5947\u602a\uff0c\u5c31\u662f\u56e0\u4e3a\u4ece\u4f60\u5c0f\u65f6\u5019\u7684 \u987d\u76ae\u60f3\u8d77\u7684\u3002\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u54e6\uff0c\u60f3\u8d77\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u5c0f\u65f6\u5019\u4f60\u5f88\u987d\u76ae\uff0c\u4f46\u6211\u597d\u50cf\u4ece\u672a\u89c1\u8fc7\u4f60\u7684\u7239\u7239\u6253\u4f60\u9a82\u4f60\uff0c\u83ab\u8bf4\u6253\u9a82\uff0c\u8fde\u751f \u4f60\u7684\u6c14\u6211\u90fd\u672a\u89c1\u8fc7\u3002\u53ea\u6709\u4f60\u5411\u4ed6\u4e71\u53d1\u813e\u6c14\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u6211\u5988\u65e9\u6b7b\uff0c\u6211\u81ea\u5c0f\u5c31\u662f\u4e0e\u7239\u7239\u76f8\u4f9d\u4e3a\u547d\u7684\u3002\u7239\u7239\u7279\u522b\u75bc\u6211\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48 \u7a00\u5947\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u4e5f\u662f\u81ea\u5c0f\u5c31\u6ca1\u6709\u5988\u5988\u7684\uff0c\u4f46\u6211\u7684\u7239\u7239\u7ba1\u6559\u6211\u5374\u662f\u5f88\u4e25\uff0c\u6211\u4e00\u505a\u9519\u4e8b\u60c5\uff0c \u4ed6\u5c31\u6253\u6211\u624b\u5fc3\u3002\u9a82\u6211\u90a3\u66f4\u662f\u5bb6\u5e38\u4fbf\u996d\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u6211\u662f\u5973\u5b69\u5b50\uff0c\u5f53\u7136\u8981\u6bd4\u7537\u5b69\u5b50\u5360\u4e00\u70b9\u4fbf\u5b9c\u7684\u3002\u522b\u4eba\u5bb6\u7684\u7236\u6bcd\u4e5f\u662f\u5bf9\u7537\u5b69 \u5b50\u7ba1\u5f97\u6bd4\u8f83\u4e25\u5417\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u8ddf\u7239\u7239\u4e0a\u5c71\u6253\u732a\uff0c\u6211\u603b\u662f\u8ddf\u5728\u7239\u7684\u5c41\u80a1\u540e\u9762\uff0c\u6709\u65f6\u5019\u4e0d\u5c0f\u5fc3\u68d2\u4e86 \u8de4\uff0c\u603b\u662f\u6211\u81ea\u5df1\u722c\u8d77\u6765\uff0c\u7239\u662f\u4e0d\u4f1a\u56de\u5934\u6765\u6276\u6211\u7684\u3002\u4f60\u548c\u4f60\u7239\u4e0a\u5c71\u73a9\u800d\uff0c\u5374\u662f\u4f60\u7239\u8ddf\u5728\u4f60\u7684\u540e \u5934\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u4fdd\u62a4\u4f60\uff0c\u751f\u6015\u4f60\u4f1a\u8dcc\u5012\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u5f88\u7ec6\u5fc3\u554a\uff0c\u8fd9\u70b9\u5c0f\u4e8b\u90fd\u6ce8\u610f\u5230\u4e86\u3002\u4f46\u8c01\u53eb\u4f60\u662f\u7537\u5b69\u5b50\u5462\uff0c\u5973\u5b69\u5b50 \u5728\u7236\u6bcd\u773c\u4e2d\u603b\u6bd4\u7537\u5b69\u5b50\u5a07\u5ae9\u7684\u554a\uff01\u4f60\u5992\u5fcc\u6211\u7239\u5ba0\u6211\uff0c\u4e0d\u5982\u4f60\u6c42\u795e\u602b\u4fdd\u4f51\uff0c\u4fdd\u4f51\u4f60\u6765\u751f\u4e5f\u53d8\u4f5c \u5973\u5b50\u5427\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4f46\u5fc3\u91cc\u7684\u7591\u56e2\u5374\u672a\u89e3\u5f00\u3002\u5f20\u96ea\u6ce2\u671b\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201d\u8fd8\u6709\u4ec0\u4e48\u662f\u4f60\u89c9 \u5f97\u5947\u602a\u7684\u5417\uff1f\u201c\u8c2d\u9053\u6210\u7684\u786e\u662f\u8fd8\u6709\u7591\u60d1\u4e4b\u5904\uff0c\u4f46\u5374\u4e0d\u4fbf\u76f4\u7387\u5730\u95ee\u4ed6\u59bb\u5b50\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u7537\u5b69\u5b50\u548c\u5973\u5b69\u5b50\u4e0d\u540c\uff0c\u59bb\u5b50\u7684\u89e3\u91ca\u4f3c\u4e4e\u4e5f\u5f88\u5408\u7406\u3002\u4f46\u4ed6\u8fd8\u7981\u4e0d\u4f4f\u6709\u4e2a\u5947\u602a\u7684\u611f \u89c9\u3002\u5f53\u7136\uff0c\u4ed6\u7edd\u4e0d\u6000\u7591\u5cb3\u7236\u5bf9\u4ed6\u7684\u59bb\u5b50\u662f\u7279\u522b\u75bc\u7231\uff0c\u4f46\u5374\u597d\u50cf\u548c\u4e00\u822c\u7684\u7236\u7231\u53c8\u6709\u4e0d\u540c\u3002\u4e0d\u53ea \u662f\u4e00\u822c\u7684\u7236\u4eb2\u5bf9\u5b69\u5b50\u7684\u7231\u62a4\uff0c\u66f4\u591a\u7684\u662f\u50cf\u201c\u4f8d\u5949\u201d\u5c0f\u4e3b\u4eba\u90a3\u6837\u7684\u5475\u62a4\u5907\u81f3\u3002 \u3000\u3000\u5fc3\u4e2d\u84e6\u5730\u5192\u8d77\u201c\u4f8d\u5949\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u60f3\u5f97\u592a\u8fc7\u8352\u5510\uff0c\u56e0\u6b64\u81ea\u662f\u4e0d\u6562\u548c\u59bb\u5b50\u8bf4 \u4e86\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u8bf4\u51fa\u6765\uff0c\u5f20\u96ea\u6ce2\u5df2\u662f\u5fc3\u4e2d\u614c\u4e71\u4e86\u3002\u201c\u770b\u6837\u5b50\u6210\u54e5\u4f3c\u4e4e\u5df2\u7ecf\u8d77\u4e86\u7591\u5fc3\uff0c\u4ed6\u731c\u5230\u4ec0 \u4e48\u5462\uff1f\u5509\uff0c\u6211\u672c\u4e0d\u8be5\u7792\u4f4f\u4ed6\u7684\uff0c\u4f46\u7239\u7239\u4e0d\u8bb8\u6211\u8bf4\uff0c\u6211\u53c8\u600e\u80fd\u76f4\u8a00\u65e0\u9690\uff1f\u4f55\u51b5\u8fd8\u6709\u8bb8\u591a\u4e8b\u60c5\uff0c \u7239\u7239\u4e5f\u8fd8\u672a\u66fe\u544a\u8bc9\u6211\u5462\uff01\u201d \u3000\u3000\u5979\u7684\u201c\u6765\u5386\u201d\u5982\u4f55\uff0c\u4e00\u76f4\u662f\u5728\u5979\u7684\u5fc3\u5934\u5c1a\u672a\u89e3\u5f00\u7684\u8c1c\uff01\u4e08\u592b\u7684\u731c\u60f3\u5e76\u4e0d\u8352\u5510\uff0c\u539f\u6765\u5979\u7684 \u201c\u7239\u7239\u201d\u679c\u7136\u5e76\u4e0d\u662f\u5979\u751f\u8eab\u4e4b\u7236\u3002\u5979\u7684\u201c\u7239\u7239\u201d\u672c\u662f\u5979\u5bb6\u7684\u8001\u4ec6\u4eba\uff0c\u540d\u53eb\u5f20\u708e\u3002\u5728\u5979\u521a\u521a\u65ad \u5976\u7684\u65f6\u5019\uff0c\u662f\u5979\u7684\u6bcd\u4eb2\u6240\u5979\u4ea4\u6258\u7ed9\u8fd9\u4f4d\u8001\u4ec6\u4eba\u7684\u3002\u90a3\u65f6\u53eb\u5468\u5c81\uff0c\u5979\u53ea\u77e5\u9053\u5979\u7684\u7236\u4eb2\u662f\u5728\u5b8b\u671d \u4e3a\u5b98\uff0c\u540e\u6765\u4e0d\u77e5\u600e\u7684\u5f97\u7f6a\u671d\u5ef7\uff0c\u88ab\u6284\u5bb6\u7684\u3002\u5979\u7684\u6bcd\u4eb2\u4f4f\u5728\u4e61\u4e0b\uff0c\u5b98\u5dee\u6765\u5230\u4e4b\u524d\uff0c\u5c06\u5979\u6258\u4e0e\u5f20 \u708e\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u540e\u6765\u5f20\u708e\u8bf4\u7ed9\u5979\u542c\u7684\uff0c\u5979\u8fde\u7236\u4eb2\u7684\u540d\u5b57\u90fd\u4e0d\u77e5\u9053\u3002\u53ea\u77e5\u9053\u7236\u4eb2\u59d3\u5f20\uff0c\u548c\u5f20\u708e\u540c \u65cf\u3002\u56e0\u6b64\u6bcd\u4eb2\u5c06\u5979\u4ea4\u6258\u7ed9\u5f20\u708e\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u8981\u5f20\u708e\u5192\u5145\u5979\u7684\u7236\u4eb2\u3002 \u3000\u3000\u5f53\u7136\u5979\u662f\u60f3\u591a\u77e5\u9053\u4e00\u4e9b\u6709\u5173\u7236\u6bcd\u7684\u4e8b\u60c5\u7684\uff0c\u4f46\u5f20\u708e\u5374\u4e0d\u80af\u544a\u8bc9\u5979\u4e86\u3002 \u3000\u3000\u5979\u662f\u7531\u5f20\u708e\u629a\u517b\u6210\u4eba\u7684\uff0c\u4e5f\u65e9\u5df2\u4e60\u60ef\u4e8e\u628a\u5f20\u708e\u5f53\u4f5c\u4eb2\u751f\u7684\u7236\u4eb2\u4e86\u3002 \u3000\u3000\u5f20\u708e\u6700\u521d\u672c\u6765\u7b54\u5e94\u5979\uff0c\u5230\u5979\u6ee1\u5341\u516d\u5c81\u7684\u65f6\u5019\uff0c\u628a\u5979\u7684\u8eab\u4e16\u544a\u8bc9\u5979\u7684\uff0c\u4f46\u5341\u516d\u5c81\u90a3\u5e74\uff0c\u5979 \u521a\u597d\u5728\u751f\u65e5\u90a3\u5929\u548c\u8c2d\u9053\u6210\u6210\u4eb2\uff0c\u5728\u51fa\u9601\u524d\u5915\uff0c\u4ea6\u5373\u662f\u5f20\u708e\u7b54\u5e94\u4e3a\u5979\u63ed\u5f00\u8eab\u4e16\u4e4b\u9690\u7684\u65e5\u671f\u3002\u5f20 \u708e\u5374\u6d41\u7740\u773c\u6cea\u548c\u5979\u8bf4\u9053\uff1a\u201c\u8bf7\u539f\u8c05\u6211\uff0c\u65f6\u673a\u672b\u81f3\uff0c\u6211\u8fd8\u4e0d\u80fd\u628a\u4f60\u7684\u8eab\u4e16\u544a\u8bc9\u4f60\u3002\u201d\u5979\u95ee\uff1a \u201c\u90a3\u4e48\u4ec0\u4e48\u65f6\u5019\u4f60\u624d\u80fd\u544a\u8bc9\u6211\uff1f\u201d\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u8981\u5230\u4f55\u65f6\uff0c\u4e0d\u8fc7\uff0c\u5047\u5982\u65f6\u673a\u4e00\u76f4 \u672a\u81f3\u7684\u8bdd\uff0c\u5230\u6211\u4e34\u7ec8\u7684\u65f6\u5019\u6211\u4f1a\u6709\u9057\u4e66\u7559\u7ed9\u4f60\u7684\u3002\u9057\u4e66\u6211\u65e9\u5df2\u5199\u597d\u4e86\u3002\u201d\u517b\u7236\u6069\u6df1\u5982\u6d77\uff0c\u5979 \u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff1f\u5979\u5bf9\u751f\u8eab\u7684\u7236\u6bcd\u6beb\u65e0\u8bb0\u5fc6\uff0c\u60f3\u8981\u77e5\u9053\u4ed6\u4eec\u7684\u4e8b\u60c5\uff0c\u5176\u5b9e\u591a\u534a\u8fd8\u662f\u7531\u4e8e\u597d\u5947\u800c \u5df2\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u8fc7\u60ef\u4e86\u5c71\u4e2d\u5e73\u9759\u7684\u65e5\u5b50\uff0c\u53c8\u5df2\u7ecf\u6709\u4e86\u6df1\u7231\u5979\u7684\u4e08\u592b\uff0c\u5979\u5f88\u6ee1\u8db3\u4e8e\u76ee\u524d\u6240\u8fc7\u7684\u65e5 \u5b50\u3002\u5728\u5979\u5185\u5fc3\u6df1\u5904\u5012\u662f\u6709\u70b9\u5bb3\u6015\u77e5\u9053\u7236\u6bcd\u4e0d\u5e78\u7684\u906d\u9047\u4f1a\u6270\u4e71\u5979\u7684\u5fc3\u7075\u4e86\u3002\uff08\u7236\u6bcd\u662f\u5426\u5df2\u906d\u4e0d \u5e78\uff0c\u5176\u5b9e\u5979\u5df2\u662f\u8fd8\u672a\u77e5\u9053\u7684\u3002\u4e0d\u8fc7\u4ece\u5f20\u708e\u90a3\u665a\u548c\u5979\u8bf4\u8bdd\u7684\u8bed\u6c14\u548c\u795e\u6001\u4e4b\u4e2d\uff0c\u5979\u9690\u9690\u611f\u89c9\u5f97 \u5230\uff0c\u7236\u6bcd\u5927\u6982\u662f\u5df2\u906d\u4e0d\u5e78\u4e86\u3002\uff09\u65e5\u5b50\u4e00\u5929\u5929\u8fc7\u53bb\uff0c\u5982\u4eca\u5979\u7684\u513f\u5b50\u4e5f\u6709\u4e94\u5c81\u4e86\u3002\u201c\u7239\u7239\u201d\u8fd8\u6ca1 \u7b49\u5230\u53ef\u4ee5\u628a\u79d8\u5bc6\u544a\u8bc9\u5979\u7684\u201c\u65f6\u673a\u201d\uff0c\u5979\u4e5f\u4e0d\u60f3\u63ed\u5f00\u81ea\u5df1\u7684\u8eab\u4e16\u4e4b\u8c1c\u4e86\u3002 \u3000\u3000\u5979\u5e38\u60f3\uff1a\u201c\u8981\u662f\u80fd\u591f\u8fd9\u6837\u5e73\u9759\u5ea6\u8fc7\u4e00\u751f\u3002\u54ea\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u4f55\u5fc5\u81ea\u5bfb\u70e6\u607c\uff1f\u4f46\u5982\u4eca\u5979\u7684 \u4e08\u592b\u5374\u6311\u8d77\u5979\u7684\u70e6\u607c\uff01 \u3000\u3000\u5979\u611f\u89c9\u5f97\u5230\uff0c\u4e08\u592b\u5bf9\u5979\u7684\u6765\u5386\u5df2\u6709\u6000\u7591\uff0c\u5509\uff0c\u4f46\u53ef\u60dc\u7684\u662f\uff0c\u5979\u81ea\u5df1\u90fd\u672a\u6e05\u695a\u77e5\u9053\u81ea\u5df1\u7684 \u8eab\u4e16\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u614c\u4e71\uff0c\u65e2\u7136\u4e0d\u6562\u5410\u9732\u79d8\u5bc6\uff0c\u5c31\u53ea\u80fd\u8bd5\u63a2\u4e08\u592b\u7684\u53e3\u98ce\uff0c\u770b\u770b\u4ed6\u662f\u5426\u77e5\u9053\u4e00\u4e9b\u4ec0\u4e48\u79d8 \u5bc6\u4e86\u3002 \u3000\u3000\u8c2d\u9053\u6210\u4e5f\u662f\u548c\u59bb\u5b50\u4e00\u6837\uff0c\u5fc3\u4e2d\u6709\u8bdd\uff0c\u5374\u4e0d\u4fbf\u76f4\u8bf4\u51fa\u6765\u3002\u201c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u662f\u4f60\u89c9\u5f97\u5947\u602a\u7684 \u5417\uff1f\u201d\u5f20\u96ea\u6ce2\u95ee\u9053\u3002 \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u4ec0\u4e48\u3002\u4e0d\u8fc7\uff0c\u6211\u521a\u624d\u5012\u662f\u78b0\u89c1\u4e00\u4ef6\u7f55\u6709\u7684\u4e8b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7741\u5927\u773c\u775b\uff0c\u201c\u4ec0\u4e48\u7f55\u6709\u7684\u4e8b\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u770b\u89c1\u4f60\u7684\u7239\u7239\u5728\u4e00\u5904\u5ca9\u77f3\u540e\u9762\u548c\u4e00\u4e2a\u964c\u751f\u4eba\u8bf4\u8bdd\u3002\u8fd9\u4e48\u591a\u5e74\uff0c\u597d\u50cf\u4ece\u6765 \u6ca1\u6709\u89c1\u8fc7\u6709\u5916\u9762\u7684\u4eba\u627e\u4f60\u7239\u7239\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u54e6\uff0c\u662f\u600e\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u6ca1\u770b\u89c1\u4ed6\u7684\u8138\u5b54\uff0c\u53ea\u77e5\u4e0d\u662f\u5c71\u4e0a\u76f8\u8bc6\u7684\u730e\u6237\u3002\u4ed6\u4eec\u4e5f\u6ca1\u770b\u89c1\u6211\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u6211\u600e\u80fd\u5077\u542c\u4f60\u7239\u7239\u7684\u8c08\u8bdd\uff1f\u4ed6\u4eec\u5c0f\u58f0\u8bf4\u8bdd\uff0c\u6211\u5306\u5306\u8d70\u8fc7\uff0c\u4e5f\u542c\u4e0d\u6e05\u695a\u3002 \u4e0d\u8fc7\u90a3\u964c\u751f\u4eba\u7684\u53e3\u97f3\uff0c\u5374\u4f3c\u4e4e\u662f\u5357\u8fb9\u7684\u53e3\u97f3\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u662f\u4ece\u5927\u6563\u5173\u5357\u8fb9\u9003\u96be\u6765\u7684\uff0c\u8fd9\u4e2a\u4eba\u6050\u6015\u662f\u7239\u7239\u4ee5\u524d\u5728\u4e61\u4e0b\u76f8\u8bc6\u7684 \u4e5f\u8bf4\u4e0d\u5b9a\u3002\u5f85\u6211\u4eca\u665a\u518d\u95ee\u4ed6\u5427\u3001\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u770b\u8fd8\u662f\u8ba9\u7239\u7239\u81ea\u5df1\u544a\u8bc9\u4f60\u597d\u4e9b\uff0c\u56e0\u4e3a\u8bf4\u4e0d\u5b9a\u4ed6\u4e0d\u60f3\u4f60\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u601d\uff0c\u4f60\u662f\u6015\u7239\u7239\u95ee\u6211\u600e\u7684\u4f1a\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u5230\u65f6\u5019\u5c31\u96be\u514d\u6709 \u5077\u542c\u7684\u5acc\u7591\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u5b66\u5f97\u8fd9\u6837\u591a\u5fc3\u4e86\uff0c\u6211\u53ea\u662f\u60f3\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5018\u82e5\u53ef\u4ee5\u8ba9\u4f60\u77e5\u9053\uff0c\u4f60\u7684 \u7239\u7239\u5f53\u7136\u4f1a\u544a\u8bc9\u4f60\u3002\u201d\u5f20\u96ea\u6ce2\u62ac\u773c\u671b\u4ed6\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6015\u4f60\u8bf4\u6211\u591a\u5fc3\uff0c\u6211\u4e0d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4f60\u522b\u5455\u6211\u7684\u6c14\u597d\u4e0d\u597d\uff0c\u548c\u4f60\u8bf4\u53e5\u7b11\u8bdd\uff0c\u4f60\u5c31\u5f53\u771f\u8d77\u6765\u4e86\u3002\u8bf4\u5427\uff0c\u54b1\u4eec\u592b\u59bb \u8fd8\u6709\u4ec0\u4e48\u4e0d\u80fd\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4f60\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u662f\u5947\u602a\uff0c\u600e\u4e48\u5ba2\u4eba\u8981\u561b\u90fd\u4e0d\u6765\uff0c\u8981\u561b\u5ffd\u7136\u90fd\u6765\u4e86\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u8bf4\u524d\u5929\u6709\u4e2a\u5ba2\u4eba\u6765\u627e\u6211\u7239\u7239\u7684\u4e8b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u5bb6\u907f\u96be\u8352\u5c71\u3002\u5341\u591a\u5e74\u6765\uff0c\u4e00\u76f4\u6ca1\u6709\u5ba2\u4eba\u6765\u8bbf\uff0c\u8fd9\u4e24\u5929\u5374\u4e0d\u7ea6\u800c\u540c\u4f3c \u7684\uff0c\u5148\u662f\u6709\u4eba\u6765\u627e\u4f60\u7684\u7239\u7239\uff0c\u8ddf\u7740\u53c8\u6709\u4eba\u6765\u627e\u6211\u7684\u7239\u7239\uff0c\u4f60\u8bf4\u8fd9\u662f\u5de7\u5408\u5462\uff0c\u8fd8\u662f\uff0c\u8fd8\u662f\u2014 \u2014\u201d\u8c2d\u9053\u6210\u7684\u9762\u8272\u4e0d\u77e5\u4e0d\u89c9\u4e5f\u51dd\u91cd\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u4f60\u522b\u7b11\u6211\u591a\u5fc3\uff0c\u6211\u603b\u89c9\u5f97\u50cf\u662f\u6709\u70b9\u4e0d\u7965\u4e4b\u5146\uff0c\u524d\u5929\u6211\u4e00\u65e9\u51fa\u95e8\uff0c\u78b0\u4e0a\u4e00 \u5934\u4e4c\u9e26\uff0c\u4eca\u65e9\u51fa\u95e8\uff0c\u53c8\u78b0\u4e0a\u4e00\u5934\u4e4c\u9e26\u2026\u2026\u201d \u3000\u3000\u8c2d\u9053\u6210\u5931\u7b11\u9053\uff1a\u201c\u4f60\u600e\u80fd\u628a\u4e24\u4f4d\u5ba2\u4eba\uff0c\u6bd4\u4f5c\u4e24\u5934\u4e4c\u9e26\uff1f\u201d\u5f20\u96ea\u6ce2\u6ca1\u6709\u56e0\u4ed6\u7684\u63d2\u5634\u800c\u6b62 \u53e3\uff0c\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u771f\u7684\u662f\u6709\u70b9\u62c5\u5fe7\uff0c\u62c5\u5fe7\u8fd9\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4f1a\u50cf\u662f\u4e0d\u7965\u4e4b\u4e4c\u9e26\uff0c\u7ed9\u54b1\u4eec\u6765 \u6076\u8fd0\uff01\u201d \u3000\u3000\u8c2d\u9053\u6210\u5b89\u6170\u59bb\u5b50\u9053\uff1a\u201c\u4e0d\u8981\u8fd9\u6837\u8ff7\u4fe1\uff0c\u6211\u770b\u8fd9\u53ea\u4e0d\u8fc7\u662f\u5de7\u5408\u7f62\u4e86\u3002\u6700\u8fd1\u4e0d\u662f\u542c\u8bf4\u53c8\u6253\u4ed7 \u4e86\u5417\uff1f\u524d\u5929\u6765\u627e\u7239\u7684\u90a3\u4e2a\u5ba2\u4eba\uff0c\u662f\u907f\u96be\u7ecf\u8fc7\u5c71\u4e0b\uff0c\u4ed6\u6765\u81ea\u7239\u7239\u7684\u6545\u4e61\uff0c\u77e5\u9053\u6211\u7239\u5728\u8fd9\u5c71\u4e0a\u9690 \u5c45\uff0c\u8fd9\u624d\u7279\u5730\u6765\u627e\u7239\u7239\u7684\u3002\u56e0\u6b64\u6211\u731c\u60f3\u4eca\u5929\u6765\u627e\u7239\u7684\u90a3\u4e2a\u5ba2\u4eba\uff0c\u6216\u8bb8\u4e5f\u662f\u540c\u6837\u60c5\u5f62\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f46\u613f\u5982\u4f60\u6240\u8a00\u3002\u53ea\u662f\u5de7\u5408\u3002\u201d\u4f46\u773c\u795e\u5374\u662f\u832b\u7136\u82e5\u6709\u6240\u601d\uff0c\u4f4e\u4e0b\u5934\u53c8\u4e0d\u8bf4\u8bdd \u4e86\u3002 \u3000\u3000\u8c2d\u9053\u6210\u53e3\u4e2d\u5b89\u6170\u59bb\u5b50\uff0c\u5fc3\u91cc\u5374\u4e5f\u7740\u5b9e\u662f\u6709\u70b9\u7591\u60d1\u4e0d\u5b89\u3002\u524d\u5929\u6765\u627e\u4ed6\u7236\u4eb2\u7684\u90a3\u4e2a\u5ba2\u4eba\uff0c\u5728 \u4ed6\u5bb6\u91cc\u53ea\u559d\u4e86\u4e00\u676f\u8336\uff0c\u5e2d\u4e0d\u6687\u6696\uff0c\u5c31\u8981\u8d70\u4e86\u3002\u4ed6\u7684\u7236\u4eb2\u9001\u90a3\u5ba2\u4eba\u4e0b\u5c71\uff0c\u5f88\u665a\u5f88\u665a\u65b9\u59cb\u56de\u5bb6\u3002 \u4ed6\u66fe\u7ecf\u95ee\u8fc7\u7236\u4eb2\u90a3\u4e2a\u5ba2\u4eba\u662f\u8c01\uff0c\u7236\u4eb2\u5374\u50cf\u5fc3\u4e8b\u91cd\u91cd\u7684\u6837\u5b50\uff0c\u53eb\u4ed6\u4e0d\u8981\u591a\u95ee\u3002\u8bf4\u662f\u5230\u4e86\u53ef\u4ee5\u544a \u8bc9\u4ed6\u7684\u65f6\u5019\uff0c\u81ea\u7136\u4f1a\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u81ea\u4ece\u90a3\u5ba2\u4eba\u6765\u8fc7\u4e4b\u540e\uff0c\u4ed6\u7684\u7236\u4eb2\u4e00\u76f4\u50cf\u662f\u95f7\u95f7\u4e0d\u4e50\uff0c\u6628\u5929\u4eca\u5929\u90fd\u6ca1\u51fa\u53bb\u6253\u730e\u3002 \u3000\u3000\u56e0\u6b64\u4ed6\u867d\u7136\u90a3\u6837\u5b89\u6170\u59bb\u5b50\uff0c\u5fc3\u91cc\u5176\u5b9e\u4e5f\u662f\u548c\u59bb\u5b50\u4e00\u6837\uff0c\u6709\u4e86\u4e00\u4e1d\u4e0d\u7965\u4e4b\u611f\u3002 \u3000\u3000\u4ed6\u53c8\u518d\u60f3\u9053\uff1a\u201c\u524d\u5929\u6765\u7684\u90a3\u4eba\u5ba2\u4eba\uff0c\u6765\u5f97\u867d\u7136\u5947\u602a\uff0c\u53ef\u8fd8\u662f\u6765\u5230\u6211\u7684\u5bb6\u4eba\u4e2d\u627e\u7239\u7239\u3002\u4eca \u5929\u627e\u5cb3\u7236\u90a3\u4e2a\u5ba2\u4eba\uff0c\u5374\u5e76\u6ca1\u6709\u627e\u4e0a\u95e8\u6765\uff0c\u4ed6\u4eec\u5728\u60ac\u5ca9\u540e\u9762\u8bf4\u8bdd\uff0c\u4e5f\u597d\u50cf\u662f\u7279\u610f\u8981\u627e\u90a3\u6837\u50fb\u9759 \u5730\u65b9\uff0c\u96be\u9053\u5cb3\u7236\u771f\u7684\u6015\u6211\u5077\u542c\u5417\uff1f\u8fd9\u5c31\u662f\u66f4\u5947\u602a\u4e86\uff01\u201d\u592b\u59bb\u5fc3\u91cc\u90fd\u662f\u6000\u7740\u7591\u56e2\uff0c\u8c2d\u9053\u6210\u4e5f\u53ea \u80fd\u50cf\u59bb\u5b50\u90a3\u6837\uff0c\u628a\u7591\u56e2\u85cf\u5728\u5fc3\u4e2d\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u5df2\u7ecf\u628a\u6563\u843d\u5728\u5730\u4e0a\u7684\u67f4\u8349\u91cd\u65b0\u6346\u597d\uff0c\u5728\u67f4\u8349\u91cc\u4ed6\u8fd8\u53d1\u73b0\u4e00\u5305\u8349\u83c7\u3002\u201c\u6628\u5929\u4f60\u624d\u91c7 \u4e86\u8bb8\u591a\u8349\u83c7\u56de\u6765\uff0c\u5982\u4eca\u53c8\u662f\u8fd9\u4e48\u4e00\u5927\u5305\uff0c\u54c8\uff0c\u6050\u6015\u4e09\u5929\u90fd\u5403\u4e0d\u5b8c\u3002\u201d\u8c2d\u9053\u6210\u8bf4\u9053\u3002\u5f20\u96ea\u6ce2\u7b11 \u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u7237\u513f\u4fe9\u90fd\u559c\u6b22\u5403\u65b0\u9c9c\u7684\u8349\u83c7\uff0c\u660e\u5929\u4f60\u53bb\u730e\u4e24\u53ea\u5c71\u9e21\u56de\u6765\uff0c\u548c\u8349\u83c7\u4e00\u540c\u7096 \u5403\uff0c\u5473\u9053\u5c31\u66f4\u597d\u4e86\u3002 \u3000\u3000\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u8fd8\u7528\u4f60\u8bf4\uff0c\u4f60\u7239\u521a\u624d\u5df2\u7ecf\u6253\u4e86\u4e24\u53ea\u5c71\u9e21\u56de\u6765\u4e86\u3002\u6211\u7684\u70f9\u8c03\u624b\u6bb5\u8fdc\u4e0d\u53ca \u4f60\uff0c\u6240\u4ee5\u624d\u7279\u5730\u6765\u627e\u4f60\u8fd9\u4f4d\u5927\u53a8\u5e08\u56de\u53bb\u70f9\u8c03\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u8fd9\u6837\u597d\u5fc3\u51fa\u6765\u627e\u6211\uff0c\u539f\u6765\u5982\u6b64\u3002\u597d\uff0c\u90a3\u54b1\u4eec\u5c31\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u591a\u6b47\u4e00\u4f1a\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u65e9\u5c31\u6ca1\u4e8b\u5566\uff0c\u518d\u4e0d\u56de\u53bb\uff0c\u5929\u5c31\u8981\u9ed1\u4e86\u3002\u201d\u8c2d\u9053\u6210\u6298\u4e0b\u4e00\u6839\u7c97\u5982\u624b\u81c2\u7684\u6811\u679d \u7ed9\u5979\u5f53\u4f5c\u6241\u62c5\u3002\u81ea\u5df1\u625b\u8d77\u90a3\u5934\u6b7b\u8001\u864e\u4e0e\u59bb\u5b50\u5e76\u80a9\u540c\u884c\u3002 \u3000\u3000\u8d70\u4e86\u51e0\u6b65\uff0c\u5f20\u96ea\u6ce2\u5ffd\u5730\u7709\u5934\u4e00\u76b1\uff0c\u811a\u6b65\u6709\u70b9\u6b6a\u659c\u3002\u8c2d\u9053\u6210\u5403\u4e00\u60ca\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u600e\u4e48 \u5566\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u80f8\u53e3\u597d\u50cf\u6709\u70b9\u4f5c\u95f7\u3002\u201d\u8c2d\u9053\u6210\u8fde\u5fd9\u653e\u4e0b\u6b7b\u8001\u864e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u77a7\u662f\u5427\uff0c\u4f60\u90fd\u672a\u66fe\u6062\u590d\u4f53\u529b\u5462\u3002\u522b\u901e\u5f3a\u4e86\uff0c\u67f4\u8349\u653e\u4e0b\uff0c\u8ba9\u6211\u6765\u6311\u3002\u201d\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u66ff\u59bb\u5b50 \u63c9\u6413\u3002\u4e0d\u63c9\u6413\u8fd8\u597d\uff0c\u4ed6\u4e00\u66ff\u59bb\u5b50\u63c9\u6413\uff0c\u5f20\u96ea\u6ce2\u53cd\u800c\u54c7\u5730\u628a\u9ec4\u80c6\u6c34\u90fd\u5455\u4e86\u51fa\u6765\u3002\u5f20\u96ea\u6ce2\u63a8\u5f00\u4ed6 \u9053\uff1a\u201c\u4f60\u522b\u6270\u6211\uff0c\u6211\u4e0d\u662f\u75c5\uff0c\u4e5f\u4e0d\u662f\u75b2\u52b3\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u90a3\u4f60\u600e\u4e48\u4f1a\u5455\u5f97\u8fd9\u6837\u5389\u5bb3\uff1f\u201d\u5f20\u96ea\u6ce2\u4f4e\u58f0\u9053\uff1a\u201c\u6211\uff0c\u6211\u597d\u50cf\u662f\u53c8\u3001\u53c8\u6709 \u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u9645\uff0c\u6ee1\u9762\u901a\u7ea2\u3002\u8c2d\u9053\u6210\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u3001\u6709\u4ec0\u4e48\uff1f\u554a\u3002\u6211\u660e\u767d\u5566\uff0c\u6211\u53c8 \u8981\u505a\u7238\u7238\u5566\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5927\u53eb\u5927\u56b7\u505a\u4ec0\u4e48\uff0c\u7ed9\u4eba\u542c\u89c1\u7b11\u8bdd\u3002\u201d\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u6700\u8fd1\u7684\u4e00\u5bb6\u730e \u6237\uff0c\u4e5f\u9694\u7740\u4e00\u5ea7\u5c71\u5934\u5462\u3002\u54ea\u4f1a\u6709\u4eba\u542c\u89c1\uff0c\u9664\u975e\u662f\u4f60\u7239\u7239\u2014\u2014\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u671b\u7740\u4ed6\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4e9b\u4ec0\u4e48\u3002\u8c2d\u9053\u6210\u77bf\u7136\u4e00\u7701\uff0c\u60f3\u8d77\u90a3\u4e2a\u5ba2\u4eba\uff0c\u65b9\u59cb\u53d1\u89c9\u81ea\u5df1\u8bdd\u8bf4 \u7684\u592a\u6ee1\u3002\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5929\u90fd\u5feb\u8981\u9ed1\u4e86\uff0c\u4f60\u7239\u7239\u7684\u90a3\u4e2a\u5ba2\u4eba\u6599\u60f3\u65e9\u5df2\u8d70\u4e86\u3002\u4f60\u7239 \u5012\u662f\u6709\u53ef\u80fd\u6765\u627e\u4f60\u7684\uff0c\u4e0d\u8fc7\u4f60\u8fd8\u6015\u7ed9\u4ed6\u77e5\u9053\u5417\uff1f\u4ed6\u4e45\u5df2\u76fc\u671b\u591a\u6dfb\u4e00\u4e2a\u5916\u5b59\u8fc7\u7ee7\u7ed9\u4ed6\uff0c\u8981\u662f\u4ed6 \u77e5\u9053\u4e86\uff0c\u6050\u6015\u6bd4\u6211\u8fd8\u66f4\u559c\u6b22\u5462\u3002\u96ea\u59b9\uff0c\u4f60\u6084\u6084\u544a\u8bc9\u6211\u5427\uff0c\u6709\u4e86\u51e0\u4e2a\u6708\u4e86\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7f9e\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u5929\u624d\u53d1\u73b0\u7684\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u539f\u6765\u8fd9\u662f\u56e0\u4e3a\u4f60\u5df2\u7ecf\u53d1\u73b0\u4e86\u81ea\u5df2\u6709\u5b55\u7684\u7f18\u6545\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u8f7b\u529f\u9020\u8be3\uff0c\u672c\u6765\u5e94\u8be5\u8dd1\u5f97\u6bd4\u90a3\u5934\u8001\u864e\u66f4\u5feb\u7684\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u6709\u70b9\u62c5\u5fc3\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u6454\u4e86\u4e00\u8de4\uff0c\u4f1a\u4e0d\u4f1a\uff0c\u4f1a\u4e0d\u4f1a\u2014\u2014\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7ea2\u7740\u8138\u9053\uff1a\u201c\u524d\u5929\u624d\u53d1\u73b0\u6709\u7684\uff0c\u5b69\u5b50\u8fd8\u672a\u6210\u5f62\u5462\u3002\u54ea\u80fd\u5c31\u6454\u574f\u4e86\u4ed6\u3002\u522b\u80e1\u626f\u4e86\u3002 \u8d70\u5427\uff0c\u8d70\u5427\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u628a\u67f4\u8349\u7ed9\u6211\uff0c\u8ba9\u6211\u6765\u6311\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u4f5c\u95f7\u800c\u5df2\uff0c\u73b0\u5728\u4ea6\u5df2\u597d\u4e86\u3002\u8fd9\u5934\u8001\u864e\u6211\u625b\u4e0d\u8d77\uff0c\u4e24\u6346\u67f4\u8349\uff0c\u4f60\u8fd8\u6015 \u6211\u6311\u4e0d\u52a8\u5417\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u809a\u5b50\u91cc\u7684\u5b69\u5b50\u8981\u7d27\u3002\u4f60\u6311\u52a8\u5f97\uff0c\u6211\u4e5f\u653e\u5fc3\u4e0d\u4e0b\uff0c\u542c\u8bdd\uff0c\u542c\u8bdd\uff0c\u4e56 \u4e56\u5730\u7ed9\u6211\u5427\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u611f\u53d7\u5230\u4e08\u592b\u7684\u7231\u62a4\uff0c\u5fc3\u91cc\u751c\u4e1d\u4e1d\u7684\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u5934\u8001\u864e \u5462\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u653e\u5728\u8fd9\u91cc\uff0c\u4e5f\u6ca1\u4eba\u4f1a\u8981\u54b1\u4eec\u7684\u3002\u5403\u8fc7\u665a\u996d\uff0c\u6211\u518d\u6765\u642c\u5b83\u56de\u53bb\u3002\u201d\u5f20\u96ea\u6ce2 \u9053\uff1a\u201c\u96be\u5f97\u6253\u5230\u4e86\u8fd9\u6837\u91cd\u7684\u5927\u8001\u864e\uff0c\u4f60\u65e9\u70b9\u625b\u56de\u53bb\uff0c\u4e5f\u597d\u8ba9\u4e24\u4f4d\u8001\u4eba\u5bb6\u5f00\u5fc3\u3002\u6210\u54e5\uff0c\u6211\u77e5\u9053 \u4f60\u75bc\u6211\uff0c\u4f46\u6211\u771f\u7684\u8fd8\u6311\u5f97\u52a8\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8fd9\u6837\u5427\u3002\u6211\u5272\u4e00\u6761\u8001\u864e\u817f\u56de\u53bb\uff0c\u8d81\u65b0\u9c9c\uff0c\u4eca\u665a\u70e4\u864e\u8089\u5403\uff0c\u8001\u4eba\u5bb6\u4e5f\u5f00\u5fc3 \u4e86\u3002\u4f46\u8981\u662f\u7ed9\u4ed6\u4eec\u77e5\u9053\u4f60\u6709\u4e86\u8eab\u5b55\uff0c\u6211\u8fd8\u8ba9\u4f60\u6311\u67f4\u8349\uff0c\u90a3\u6050\u6015\u4ed6\u4eec\u5c31\u8981\u4e0d\u5f00\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u62d7\u4e0d\u8fc7\u4e08\u592b\uff0c\u5fc3\u91cc\u4e5f\u7684\u786e\u662f\u559c\u6b22\u4e08\u592b\u5bf9\u5979\u8fd9\u6837\u7231\u62a4\uff0c\u4fbf\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f9d\u4f60\u5c31\u662f\u3002 \u4f46\u6210\u54e5\uff0c\u4f60\u53ef\u5f97\u5f53\u5fc3\uff0c\u522b\u5ba0\u574f\u6211\u554a\u3002\u201d\u8c2d\u9053\u6210\u6311\u8d77\u67f4\u8349\uff0c\u548c\u59bb\u5b50\u5e76\u80a9\u800c\u884c\uff0c\u7b11\u95ee\u59bb\u5b50\uff1a\u201c\u96ea \u59b9\uff0c\u8fd9\u4e2a\u5b69\u5b50\u4f60\u559c\u6b22\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u674f\u8138\u98de\u971e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u672c\u6765\u6211\u662f\u5e0c\u671b\u662f\u4e2a\u5973\u513f\u7684\uff0c\u4f46\u4f60\u7239\u60f3\u8981\u4e2a\u5916\u5b59\u627f\u7ee7\u5f20\u5bb6\u7684\u9999\u706f\uff0c\u53ea\u80fd\u76fc\u4f60 \u518d\u751f\u4e00\u4e2a\u7537\u5b69\u5b50\u4e86\u3002\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5176\u5b9e\u7537\u7684\u5973\u7684\u90fd\u662f\u4e00\u6837\uff0c\u6211\u5c31\u4e0d\u61c2\uff0c\u4e3a\u4ec0\u4e48\u53ea\u6709\u7537\u7684\u624d \u80fd\u7ee7\u627f\u9999\u706f\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u91cd\u7537\u8f7b\u5973\uff0c\u672c\u6765\u662f\u4e0d\u516c\u9053\uff0c\u4f46\u4e60\u4fd7\u76f8\u4f20\uff0c\u54b1\u4eec\u6539\u53d8\u4e0d\u4e86\uff0c\u4f60\u4eec\u505a\u5973\u4eba\u7684\uff0c \u53ea\u6709\u53d7\u70b9\u59d4\u66f2\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u51b2\u513f\u4eca\u5e74\u5df2\u4e94\u5c81\u4e86\u3002\u5f1f\u59b9\u5e74\u9f84\u8981\u662f\u548c\u4ed6\u76f8\u5dee\u592a\u8fdc\uff0c\u73a9\u5728\u4e00\u8d77\u5c31\u8bbe\u6709\u4ec0\u4e48\u5473 \u513f\u4e86\u3002\u4e0d\u7ba1\u7537\u7684\u4e5f\u597d\u3002\u5973\u7684\u4e5f\u597d\uff0c\u6211\u53ea\u76fc\u8fd9\u4e2a\u5b69\u5b50\u80fd\u591f\u987a\u5229\u751f\u4e0b\u6765\uff0c\u548c\u51b2\u513f\u4f5c\u4f34\u3002\u201d\u8c2d\u9053\u6210 \u6ca1\u6709\u8bf4\u8bdd\uff0c\u5f20\u96ea\u6ce2\u89c1\u4ed6\u795e\u60c5\u6709\u70b9\u5947\u7279\uff0c\u95ee\u9053\u3002\u2018\u6210\u54e5\uff0c\u4f60\u5728\u60f3\u8ba1\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u8138\u4e0a\u6302\u7740\u4e00\u4e1d\u82e6\u7b11\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u6211\u6b63\u60f3\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u51b2\u513f\u660e\u5929\u6050\u6015 \u8981\u79bb\u5f00\u54b1\u4eec\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5927\u5403\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4f60\u522b\u5403\u60ca\uff0c\u7239\u7239\u53ea\u662f\u60f3\u628a\u4ed6\u9001\u5f80\u5916\u5730\u5c31\u5b66\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ed6\u624d\u4e94\u5c81\u5462\u3002\u96be\u9053\u516c\u516c\u4e0d\u4f1a\u6559\u4ed6\u5417\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u7239\u7239\u8bf4\uff0c\u5e0c\u671b\u51b2\u513f\u5f97\u5230\u540d\u5e08\u6559\u5bfc\u3002\u4ed6\u8bf4\u524d\u5929\u6765\u627e\u4ed6\u7684\u90a3\u4e2a\u5ba2\u4eba\uff0c\u6587\u6b66\u5168 \u624d\uff0c\u4ed6\u5df2\u7ecf\u7b54\u5e94\u6536\u51b2\u513f\u505a\u5f92\u5f1f\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4e0d\u80fd\u5728\u8352\u5c71\u9690\u5c45\uff0c\u6240\u4ee5\u5fc5\u987b\u51b2\u513f\u8ddf\u4ed6\u5c31\u5b66\u3002\u201d\u5f20 \u96ea\u6ce2\u9053\uff1a\u201c\u516c\u516c\u4e0d\u4e5f\u662f\u6587\u6b66\u5168\u624d\u5417\uff1f\u6b66\u529f\u65b9\u9762\uff0c\u4ed6\u6559\u51fa\u6765\u7684\u513f\u5b50\uff0c\u4e09\u62f3\u5c31\u53ef\u4ee5\u6253\u6b7b\u4e00\u5934\u8001 \u864e\uff0c\u90a3\u662f\u8db3\u591f\u7528\u4e86\u3002\u6587\u5b66\u65b9\u9762\uff0c\u6211\u6240\u77e5\u6709\u9650\uff0c\u4f46\u6211\u4e5f\u770b\u89c1\u516c\u516c\u5e38\u5e38\u6367\u7740\u4e66\u6765\u541f\u54e6\uff0c\u60f3\u5fc5\u4e5f\u662f \u4e0d\u9519\u3002\u4e3a\u4ec0\u4e48\u8fd8\u8981\u8bf7\u5916\u4eba\u6559\u81ea\u5df1\u7684\u5b59\u513f\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u7239\u7239\u8bf4\uff0c\u4ed6\u51e1\u4e8b\u90fd\u662f\u60f3\u6c42\u6700\u597d\u7684\u3002\u4eba\u5916\u6709\u4eba\uff0c\u5929\u5916\u6709\u5929\uff0c\u4ed6\u8bf4\u90a3\u4eba\u7684\u6587\u5b66 \u6b66\u529f\u5c31\u662f\u80dc\u4ed6\u5341\u500d\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fc3\u4e71\u5982\u9ebb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u5e0c\u671b\u51b2\u513f\u80fd\u591f\u6210\u624d\uff0c\u4e0d\u8fc7\u4ed6\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u6211\u771f\u662f\u6709\u70b9\u820d\u4e0d \u5f97\u4ed6\u3002\u4f46\u516c\u516c\u65e2\u7136\u6709\u8fd9\u4e2a\u5ff5\u5934\uff0c\u4e3a\u4f55\u90a3\u5929\u4ed6\u4e0d\u628a\u51b2\u513f\u4ea4\u7ed9\u90a3\u4e2a\u4eba\u5e26\u8d70\u5462\uff1f\u5374\u8981\u81ea\u5df1\u591a\u8d70\u4e00 \u8d9f\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u7239\u7239\u4e5f\u662f\u548c\u4f60\u4e00\u6837\uff0c\u820d\u4e0d\u5f97\u5b59\u513f\u7684\u3002\u8fd9\u4e24\u5929\u4f60\u4e0d\u89c1\u4ed6\u4e00\u76f4\u90fd\u662f\u5fc3\u4e8b\u91cd\u91cd\u7684 \u6a21\u6837\u5417\uff1f\u6211\u731c\u4ed6\u6b63\u662f\u4e3a\u4e86\u6b64\u4e8b\u51b3\u65ad\u4e0d\u4e0b\u554a\u3002\u518d\u8bf4\uff0c\u51b2\u513f\u7684\u4e8b\u60c5\uff0c\u4e5f\u603b\u5f97\u4f60\u505a\u6bcd\u4eb2\u7684\u70b9\u5934\u624d\u884c \u554a\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6c89\u541f\u9053\uff1a\u201c\u4e0d\u662f\u542c\u8bf4\u5916\u9762\u6b63\u8981\u6253\u4ed7\u5417\uff1f\u5b69\u5b50\u5e74\u7eaa\u5c0f\uff0c\u4e0d\u5982\u7b49\u4f19\u6253\u5b8c\u4e86\uff0c\u518d\u9001\u4ed6\u51fa \u53bb\u4e0d\u8fdf\u3002\u5175\u8352\u9a6c\u4e71\u5e74\u5934\uff0c\u5728\u5c71\u4e0a\u603b\u6bd4\u8f83\u5e73\u5b89\u4e00\u4e9b\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u8fd9\u5ea7\u5c71\u5e73\u65e5\u867d\u7136\u4eba\u8ff9\u7f55\u5230\uff0c\u4f46\u5230\u5e95\u662f\u5728 \u4e24\u56fd\u4ea4\u754c\u4e4b\u5904\uff0e\u91d1\u5b8b\u4ee5\u5927\u6563\u5173\u4e3a\u754c\uff0c\u8fd9\u5ea7\u5c71\u548c\u5927\u6563\u5173\u7684\u8ddd\u79bb\u867d\u7136\u4e0d\u7b97\u592a\u8fd1\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u767e\u91cc\u4e4b \u9065\u3002\u91d1\u5175\u653b\u5b8b\uff0c\u5c71\u4e0b\u662f\u5fc5\u7ecf\u4e4b\u5730\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8fc7\u53bb\u5927\u4ed7\u5c0f\u4ed7\u4e5f\u6253\u8fc7\u4e0d\u77e5\u591a\u5c11\u6b21\uff0c\u4ece\u672a\u89c1\u8fc7\u4e00\u4e2a\u5175\u58eb\u8dd1\u5230\u8fd9\u5c71\u4e0a\u6765\u7684\u3001\u201d \u8c2d\u9053\u6210\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u5b8b\u56fd\u52bf\u5f31\uff0c\u6bcf\u6b21\u6253\u4ed7\uff0c\u90fd\u662f\u5b88\u4e0d\u4f4f\u8fb9\u5173\uff0c\u5f88\u5feb\u5c31\u7ed9\u91d1\u5175\u957f\u9a71\u76f4\u5165\u4e86\u3002 \u4f46\u6211\u542c\u7239\u7239\u8bf4\uff0c\u4e8c\u5341\u5e74\u524d\uff1b\u60c5\u5f62\u5374\u975e\u5982\u6b64\u3002\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u542c\u7239\u7239\u8bf4\u8fc7\uff0c\u542c\u8bf4\u90a3\u65f6\u54b1 \u4eec\u5b8b\u56fd\u6709\u4e2a\u5927\u5c06\u540d\u53eb\u5cb3\u98de\uff0c\u5f88\u4f1a\u6253\u4ed7\uff0c\u91d1\u56fd\u6d41\u884c\u4e24\u540d\u8bdd\u9053\uff1a\u201d\u64bc\u5c71\u6613\uff0c\u61be\u5cb3\u5bb6\u519b\u96be\u3002\u4ed6\u4eec\u5bf9 \u5cb3\u98de\u7684\u754f\u60e7\uff0c\u53ef\u4ee5\u60f3\u89c1\u3002\u4f46\u53ef\u60dc\u542c\u8bf4\u5cb3\u98de\u65e9\u5df2\u7ed9\u5978\u4eba\u5bb3\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u662f\u5440\uff0c\u8981\u662f\u5cb3\u98de\u8fd8\u5728\uff0c\u91d1\u5175\u5c31\u4e0d\u80fd\u957f\u9a71\u76f4\u5165\u4e86\u3002\u4f46\u91d1\u5175\u4e0d\u80fd\u9a71\u76f4\u5165\uff0c\u5927\u6563 \u5173\u9644\u8fd1\u8fd9\u4e00\u5e26\u4e5f\u5c31\u8981\u53d8\u6210\u6218\u573a\u4e86\u3002\u90a3\u65f6\u91d1\u56fd\u7684\u5927\u519b\u5f00\u6765\uff0c\u8fd9\u5ea7\u8352\u5c71\u6050\u6015\u4e5f\u96be\u514d\u8981\u9a7b\u5175\u4e86\u3002\u201d \u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u8bf4\u3002\u662f\u4e0d\u662f\u5b8b\u56fd\u65e9\u5df2\u6709\u4e86\u597d\u50cf\u5cb3\u98de\u4e00\u6837\u7684\u540d\u5c06\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u8fd9\u6211\u5012\u662f\u6ca1\u6709\u542c\u8bf4\uff0c\u4e0d\u8fc7\u542c\u8bf4\u5f53\u5e74\u5bb3\u6b7b\u5cb3\u98de\u90a3\u4e2a\u5978\u81e3\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5b8b\u56fd\u90a3\u4e2a \u660f\u541b\u4e5f\u5df2\u6b7b\u4e86\u3002\u65b0\u7687\u5e1d\u542c\u8bf4\u5012\u597d\u50cf\u662f\u4e2a\u6bd4\u8f83\u5e74\u9752\u6709\u4e3a\u7684\u7687\u5e1d\u3002\u8fd9\u4e9b\u90fd\u662f\u524d\u5929\u6765\u7684\u90a3\u4e2a\u5ba2\u4eba\u544a \u8bc9\u6211\u7239\u7239\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u516c\u516c\u662f\u6050\u6015\u8fd9\u4e00\u6b21\u6253\u4ed7\uff0c\u54b1\u4eec\u5b8b\u56fd\u6216\u8bb8\u4f1a\u575a\u51b3\u6297\u654c\uff0c\u91d1\u5175\u6253\u4e0d \u4e0b\u5927\u6563\u5173\u3002\u90a3\u65f6\u5c31\u6050\u6015\u8981\u5728\u8fd9\u5ea7\u5c71\u4e0a\u5b89\u8425\u7acb\u6848\u4e86\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u5f53\u7136\u8fd9\u53ea\u662f\u4e07\u4e00\u7684\u987e\u8651\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u9632\u3002\u91d1\u5175\u4e0a\u5c71\uff0c\u54b1\u4eec\u5927\u4eba\u5bb9\u6613\u8eb2\u907f\uff0c \u5b69\u5b50\u5374\u96be\u7167\u987e\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u867d\u7136\u5e0c\u671b\u8fc7\u592a\u5e73\u7684\u65e5\u5b50\uff0c\u6781\u4e0d\u613f\u610f\u7ed9\u91d1\u5175\u4e0a\u5c71\u9a9a\u6270\u3002 \u3000\u3000\u4f46\u54b1\u4eec\u5230\u5e95\u662f\u6c49\u4eba\uff0c\u6211\u8fd8\u662f\u5e0c\u671b\u54b1\u4eec\u5b8b\u56fd\u80fd\u591f\u518d\u51fa\u4e00\u4e2a\u5cb3\u98de\u7684\u3002\u6210\u54e5\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d\u8c2d \u9053\u6210\u8138\u4e0a\u73b0\u51fa\u4e00\u4e1d\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u60f3\u6cd5\u5f53\u7136\u548c\u4f60\u4e00\u6837\u3002 \u3000\u3000\u56e0\u6b64\u4e3a\u4e86\u9884\u9632\u4e07\u4e00\u3002\u6211\u89c9\u5f97\u8ba9\u5b69\u5b50\u51fa\u53bb\u4e5f\u4e0d\u662f\u574f\u4e8b\u3002\u90a3\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e00\u5b9a\u53ef\u4ee5\u4fdd\u62a4\u54b1\u4eec \u7684\u5b69\u5b50\u5e73\u5b89\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u90a3\u4eba\u65e2\u7136\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e3a\u4f55\u4ed6\u81ea\u5df1\u8fd8\u8981\u9003\u96be\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u6b66\u529f\u518d\u9ad8\u3002\u4e5f\u662f\u62b5\u6321\u4e0d\u4f4f\u5343\u519b\u4e07\u9a6c\u3002\u518d\u8bf4\uff1b\u90a3\u4eba\u4e4b\u6240\u4ee5\u8981\u9003\u96be\uff0c \u4e5f\u8fd8\u6709\u4ed6\u7684\u539f\u56e0\u5462\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u90a3\u4eba\u610f\u6b32\u6f5c\u5fc3\u7ec3\u6b66\uff0c\u5f00\u521b\u4e00\u6d3e\u7684\u6b66\u5b66\u5b97\u5e08\uff0c\u6545\u6b64\u8981\u8eb2\u907f\u5230\u8fdc\u79bb\u6218\u706b\u7684\u5730 \u65b9\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e00\u65f6\u5b9e\u662f\u59d4\u51b3\u4e0d\u4e0b\u3002 \u3000\u3000\u8c2d\u9053\u6210\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54ea\u4e2a\u7236\u6bcd\u820d\u5f97\u5b69\u5b50\u79bb\u5f00\uff1f\u4e0d\u8fc7\uff0c\u7236\u6bcd\u4e5f\u603b\u662f\u5e0c\u671b\u5b69\u5b50\u80fd\u591f\u6210\u624d\u7684\u3002 \u8fd9\u6b21\u4e8b\u51fa\u975e\u5e38\uff0c\u7239\u7239\u6050\u6015\u6218\u706b\u4f1a\u71c3\u5230\u5c71\u4e0a\uff0c\u51d1\u5de7\u53c8\u6709\u8fd9\u4e48\u597d\u673a\u4f1a\u53ef\u4ee5\u8ba9\u51b2\u513f\u5f97\u5230\u660e\u5e08\u3002\u7239\u7239 \u8981\u9001\u51b2\u513f\u51fa\u5916\u5c31\u5b66\uff0c\u90a3\u4e5f\u662f\u4e3a\u4e86\u51b2\u513f\u6253\u7b97\u3002\u600e\u4e48\u6837\uff0c\u4f60\u8fd8\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u51b2\u513f\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u516c\u516c\u662f\u4e00\u5bb6\u4e4b\u4e3b\uff0c\u4ed6\u51b3\u5b9a\u4e86\u7684\u4e8b\u60c5\uff0c\u6211\u505a\u513f\u5ab3\u5987\u7684\u81ea\u7136\u53ea\u597d\u4f9d\u4ece\u3002\u201d\u8c2d\u9053 \u6210\u9053\uff1a\u201c\u4e0d\uff0c\u7239\u7239\u5e76\u4e0d\u60f3\u52c9\u5f3a\u4f60\u548c\u5b69\u5b50\u5206\u5f00\uff0c\u8981\u662f\u4f60\u4e0d\u540c\u610f\uff0c\u7239\u7239\u53ef\u4ee5\u91cd\u65b0\u8003\u8651\u3002\u201d\u5f20\u96ea\u6ce2 \u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u505a\u4e00\u4e2a\u53ea\u77e5\u6eba\u7231\u5b69\u5b50\u7684\u6bcd\u4eb2\uff0c\u6211\u77e5\u9053\u516c\u516c\u662f\u4e3a\u4e86\u51b2\u513f\u7684\u597d\uff0c\u6211\u82e5\u8fd8\u56fa\u6267\uff0c \u90a3\u5012\u662f\u6211\u4e0d\u8bc6\u5927\u4f53\u4e86\u3002\u597d\u5427\uff0c\u4f60\u544a\u8bc9\u516c\u516c\uff0c\u8bf4\u6211\u548c\u4f60\u4e00\u6837\uff0c\u8d5e\u540c\u4ed6\u7684\u4e3b\u5f20\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u77e5\u9053\u59bb\u5b50\u7b54\u5e94\u7684\u6709\u70b9\u52c9\u5f3a\uff0c\u53ea\u597d\u966a\u5979\u82e6\u7b11\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4e0d\u60f3\u4ee4\u4e08\u592b\u96be\u8fc7\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e2a\u80f8\u65e0\u5927\u5fd7\u7684\u5973\u6d41\u4e4b\u8f88\uff0c\u53ea\u76fc\u5728\u8fd9\u5c71\u4e0a\u80fd\u591f \u5e73\u5e73\u5b89\u5b89\u5ea6\u8fc7\u4e00\u751f\u3002\u4f46\u5b69\u5b50\u6709\u5b69\u5b50\u7684\u60f3\u6cd5\uff0c\u5373\u4f7f\u6218\u706b\u6ca1\u6709\u70e7\u5230\u5c71\u4e0a\u6765\uff0c\u4ed6\u957f\u5927\u4e86\u4e5f\u672a\u5fc5\u613f\u610f \u548c\u54b1\u4eec\u4e00\u6837\u8fc7\u8fd9\u6df7\u6df7\u6c8c\u6c8c\u7684\u65e5\u5b50\u3002\u591a\u89c1\u6811\u6728\u5c11\u89c1\u4eba\u3002\u4ed6\u80fd\u591f\u6210\u624d\u56fa\u7136\u6700\u597d\uff0c\u4e0d\u80fd\u591f\u6210\u624d\uff0c\u8ba9 \u4ed6\u5230\u5916\u9762\u7684\u4e16\u754c\u957f\u70b9\u89c1\u8bc6\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u559c\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u7ec8\u4e8e\u60f3\u901a\u4e86\u3002\u6211\u65e9\u77e5\u9053\u4f60\u662f\u660e\u767d\u9053\u7406\u53c8\u6709\u89c1\u8bc6\u7684\uff0c\u4f60\u4e0d\u5fc5\u592a\u8fc7 \u81ea\u8c26\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u522b\u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\u4e86\uff0c\u5feb\u70b9\u8d70\u5427\u3002\u4e24\u4f4d\u8001\u4eba\u7b49\u54b1\u4eec\u56de\u53bb\uff0c\u6050\u6015\u809a\u5b50\u90fd\u997f\u6241 \u4e86\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u4f46\u4f60\u8eab\u6000\u516d\u7532\uff0c\u8d70\u8def\u53ef\u5f97\u5f53\u5fc3\u4e00\u4e9b\u3002\u201d\u6b64\u65f6\u5915\u9633\u65e9\u5df2\u843d\u5c71\u3002\u5929\u8272 \u5f00\u59cb\u4eba\u9ed1\u4e86\u3002 \u3000\u3000\u867d\u7136\u8bf4\u662f\u8981\u8d76\u7740\u56de\u53bb\uff0c\u4f46\u8d70\u4e86\u4e00\u7a0b\uff0c\u5f20\u96ea\u6ce2\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u53c8\u8981\u548c\u4e08\u592b\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u548c\u6211\u5206\u5f00\uff1f\u201d\u8c2d\u9053\u6210\u8be7\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u600e\u7684\u4f60\u6709\u8fd9\u4e2a\u60f3 \u6cd5\u3002\u54b1\u4eec\u662f\u8981\u540c\u5055\u767d\u9996\u7684\u592b\u59bb\uff0c\u600e\u4f1a\u5206\u5f00\uff1f\u201d\u8bf4\u7f62\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u4e0d\u653e\u5fc3\uff0c\u6211\u5531\u652f\u5c71\u6b4c\u7ed9\u4f60 \u542c\uff0c\u8868\u8fbe\u6211\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u4ed6\u5e73\u65f6\u662f\u5f88\u5c11\u5531\u5c71\u6b4c\u7684\uff0c\u5f20\u96ea\u6ce2\u592e\u6c42\u4ed6\uff0c\u4e5f\u96be\u5f97\u4ed6\u5531\u4e00\u4e24\u4f1a\u3002\u6b64\u65f6\u4e3a\u4e86\u54c4\u59bb\u5b50\u559c\u6b22\uff0c\u4ed6 \u81ea\u52a8\u5531\u8d77\u6765\u4e86\u3002\u201c\u8fde\u5c31\u8fde\uff0c\u6211\u4fe9\u7f14\u4ea4\u5b9a\u767e\u5e74\u3002\u8c01\u82e5\u4e5d\u5341\u4e03\u5c81\uff0c\u5948\u4f55\u6865\u4e0a\u7b49\u4e09\u5e74\u3002\u201d \u3000\u3000\u5f20\u96ea\u5f7c\u7b11\u5f97\u6709\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u5531\u5f97\u5f88\u4e0d\u9519\u5440\uff0c\u4f46\u8fd9\u652f\u5c71\u6b4c\uff0c\u5176\u5b9e\u4f60\u65e9\u5c31\u5e94\u8be5\u5531 \u7684\u3002\u73b0\u5728\u624d\u5531\uff0c\u5df2\u7ecf\u5acc\u8fdf\u4e86\u3002\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u54e6\uff0c\u6211\u5e94\u8be5\u4ec0\u4e48\u65f6\u5019\u5531\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7b11\u9053\uff1a\u201c\u5e94\u8be5\u5728\u4f60\u5411\u6211\u6c42\u5a5a\u7684\u65f6\u5019\u5531\u3002\u201d \u3000\u3000\u4e24\u4eba\u7b11\u8fc7\u4e4b\u540e\uff0c\u5f20\u96ea\u6ce2\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5bf9\u4f60\u4e0d\u653e\u5fc3\uff0c\u4f46\u6709\u53e5\u4fd7\u8bdd\u8bf4\u5f97\u597d\uff0c\u592b\u59bb\u672c\u662f \u540c\u6797\u4e4c\uff0c\u5927\u96be\u6765\u65f6\u5404\u81ea\u98de\u3002\u5982\u4eca\u4e3a\u4e86\u6050\u9632\u6218\u706b\u6ce2\u53ca\u6b64\u95f4\uff0c\u54b1\u4eec\u5df2\u7ecf\u88ab\u8feb\u8981\u548c\u51b2\u513f\u5206\u5f00\u3002\u5982\u679c \u6218\u706b\u771f\u7684\u70e7\u5230\u5c71\u4e0a\u6765\uff0c\u5230\u4e86\u5927\u96be\u4e34\u5934\u7684\u65f6\u5019\uff0c\u90a3\u65f6\uff0c\u90a3\u65f6\uff0c\u2014\u2014\u201d\u8c2d\u9053\u6210\u65a9\u9489\u622a\u94c1\u7684\u9053\uff1a \u201c\u54b1\u4eec\u751f\u5219\u540c\u751f\uff0c\u6b7b\u5219\u540c\u6b7b\uff01\u201d \u3000\u3000\u201c\u751f\u5219\u540c\u751f\uff0c\u6b7b\u5219\u540c\u6b7b\uff01\u201d\u8fd9\u516b\u4e2a\u5b57\u4ece\u4e08\u592b\u53e3\u4e2d\u4e00\u8bf4\u51fa\u6765\uff0c\u59bb\u5b50\u7684\u6cea\u6c34\u4e5f\u4ece\u773c\u4e2d\u6d41\u51fa\u6765 \u4e86\u3002 \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4f60\u8fd9\u6837\u7231\u6211\uff0c\u6211\u559c\u6b22\u5f97\u8981\u54ed\u5566\uff0c\u4e0d\u8fc7\u8c2d\u9053\u6210\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5f53\u7136\u6211 \u4e0d\u5e0c\u671b\u771f\u7684\u4f1a\u6709\u90a3\u4e48\u4e00\u5929\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4e0d\u660e\u767d\u6211\u7684\u610f\u601d\u3002\u4e0d\u9519\uff0c\u6211\u4e5f\u4e0d\u5e0c\u671b\u6709\u90a3\u4e48\u4e00\u5929\u3002\u4f46\u82e5\u771f\u5047\u7684\u5927\u96be \u4e34\u5934\uff0c\u6211\u5012\u4e0d\u5e0c\u671b\u4f60\u548c\u6211\u540c\u6b7b\uff0c\u4f60\u4e00\u5b9a\u8981\u6d3b\u4e0b\u6765\uff01\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e3a\u4e86\u51b2\u513f\u3002\u4f60\u7684\u672c\u9886\u6bd4\u6211\u5927\uff0c\u4f60\u53ef\u4ee5\u66f4\u597d\u7167\u987e\u51b2\u513f\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u51b2\u51e0\u4f1a\u6709\u5e08\u7236\u7167\u987e\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5e08\u7236\u600e\u6bd4\u5f97\u4eb2\u751f\u7236\u6bcd\uff1f\u6210\u54e5\uff0c\u4f60\u4e00\u5b9a\u8981\u7b54\u5e94\u6211\uff0c\u4e0d\u7ba1\u5c06\u6765\u78b0\u4e0a\u4ec0\u4e48\uff0c\u4f60\u8981 \u4e3a\u7740\u51b2\u513f\uff0c\u6d3b\u4e0b\u6765\uff01\u201d\u59bb\u5b50\u8fd9\u6837\u8ba4\u771f\u7684\u6001\u5ea6\uff0c\u5413\u5f97\u8c2d\u9053\u6210\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8fc7 \u662f\u7528\u8fd9\u51e0\u4e2a\u5b57\u6765\u8868\u8fbe\u81ea\u5df1\u7684\u5fc3\u610f\uff0c\u54ea\u91cc\u771f\u7684\u5c31\u4f1a\u78b0\u4e0a\u8fd9\u79cd\u4e0d\u5e78\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u7684\u5fc3\u610f\uff0c\u6211\u4e0d\u8981\u4f60\u771f\u7684\u53bb\u505a\uff0c\u6211\u6b7b\u4e86\u4e5f\u7518\u5fc3\u4e86\u3002\u6210\u54e5\uff0c\u4f60\u522b\u7741\u5927 \u773c\u775b\u77aa\u6211\uff0c\u597d\uff0c\u597d\uff0c\u54b1\u4eec\u90fd\u83ab\u8bf4\u4e0d\u5409\u5229\u7684\u8bdd\u4e86\uff0c\u8d70\u5427\uff0c\u8d70\u5427\u3002\u201d \u3000\u3000\u592b\u59bb\u4fe9\u5fc3\u4e2d\u90fd\u662f\u5145\u6ee1\u871c\u610f\u67d4\u60c5\uff0c\u4f46\u4e5f\u9690\u9690\u6709\u70b9\u201c\u4e0d\u7965\u4e4b\u5146\u201d\u7684\u987e\u8651\u3002 \u3000\u3000\u5c3d\u7ba1\u4ed6\u4eec\u90fd\u5728\u907f\u514d\u8bf4\u4e0d\u5409\u5229\u7684\u8bdd\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u4ed6\u4eec\u5df2\u56de\u5230\u5bb6\u95e8\u3002\u53ea\u89c1\u708a\u70df\u8885\u8885\uff0c\u968f\u98ce\u98d8\u6563\u3002\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u771f\u4e0d\u597d\u610f\u601d\uff0c\u4e24\u4f4d \u8001\u4eba\u5bb6\u5df1\u7ecf\u81ea\u5df1\u70e7\u996d\u5566\u3002\u201d \u3000\u3000\u90a3\u4e24\u4f4d\u8001\u4eba\u5bb6\u679c\u7136\u662f\u7b49\u5f97\u809a\u76ae\u90fd\u997f\u6241\u4e86\u3002\u6b64\u65f6\uff0c\u8c2d\u9053\u6210\u7684\u7236\u4eb2\u6b63\u5728\u5c4b\u5b50\u91cc\u8bf4\u9053\uff1a\u201c\u600e\u5f97 \u8fd8\u4e0d\u89c1\u4ed6\u4eec\u56de\u6765\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u522b\u7b49\u4ed6\u4eec\u4e86\uff0c\u5148\u559d\u4e00\u7897\u9e21\u6c64\u5427\u3002\u8fd9\u662f\u6211\u7528\u96ea\u513f\u4eca\u65e9\u91c7\u56de\u6765\u7684\u65b0\u9c9c\u8349\u8611\u83c7\u7096 \u7684\u5c71\u9e21\uff0c\u4f60\u8bd5\u8bd5\u6211\u7684\u624b\u827a\u3002\u201d\u8c2d\u9053\u6210\u7684\u7236\u4eb2\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4e56\u5973\u513f\u91c7\u56de\u6765\u7684\u65b0\u9c9c\u8349\u83c7\uff0c\u4e0d\u7b49 \u5979\u56de\u6765\uff0c\u4e0d\u5927\u516c\u9053\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u4eb2\u5bb6\uff0c\u4f60\u771f\u662f\u4eba\u5982\u5176\u540d\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u8981\u8bb2\u4e2a\u516c\u9053\u3002\u6211\u662f\u6015\u997f\u574f\u4f60\uff0c \u5929\u5bd2\u5730\u51bb\uff0c\u5148\u559d\u4e00\u7897\u9e21\u6c64\uff0c\u4e5f\u597d\u8ba9\u8eab\u5b50\u6696\u548c\u66a7\u548c\u3002\u96ea\u513f\u662f\u4f60\u7684\u513f\u5ab3\u5987\u3002\u8981\u662f\u5f53\u771f\u997f\u574f\u4e86\u4f60\uff0c \u96ea\u513f\u5fc3\u91cc\u4e5f\u4e0d\u5b89\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u62a2\u5148\u8fdb\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u5973\u513f\u56de\u6765\u665a\u4e86\uff0c\u516c\u516c\uff0c\u4f60\u8fd8\u662f\u542c\u6211\u7239\u7239\u7684\u8bdd\uff0c\u5148\u559d \u9e21\u6c64\u5427\u3002\u4f60\u548c\u627e\u5ba2\u6c14\u505a\u4ec0\u4e48\uff0c\u8fd9\u9e21\u6c64\u5018\u82e5\u662f\u6211\u7096\u7684\uff0c\u6211\u4e5f\u5e94\u5f53\u5148\u5b5d\u656c\u4f60\u4eec\u4e24\u4f4d\u8001\u4eba\u5bb6\u3002\u201d\u5f20 \u708e\u7b11\u9053\uff1a\u201c\u4f60\u542c\u89c1\u6ca1\u6709\uff0c\u8fd9\u53ef\u662f\u4f60\u7684\u8d24\u5ab3\u5987\u8bf4\u7684\uff0c\u6ca1\u6709\u4ec0\u4e48\u6240\u8c13\u516c\u9053\u4e0d\u516c\u9053\u4e86\u5427\uff1f\u201d\u539f\u6765\u8c2d \u9053\u6210\u7684\u7236\u4eb2\u540d\u53eb\u516c\u76f4\uff0c\u51e1\u4e8b\u4e5f\u603b\u559c\u6b22\u8fdb\u4e2a\u9053\u7406\uff0c\u6240\u4ee5\u5f20\u708e\u65f6\u5e38\u62ff\u4ed6\u7684\u540d\u5b57\u53d6\u7b11\u3002\u4ed6\u4eec\u4e24\u4eb2\u5bb6 \u6b63\u5728\u5f00\u73a9\u7b11\uff0c\u4f46\u4e00\u770b\u89c1\u8fd9\u5bf9\u5c0f\u592b\u59bb\u56de\u6765\u7684\u6a21\u6837\u5374\u662f\u4e0d\u7981\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u867d\u7136\u6ca1\u6709\u8dcc\u4f24\uff0c\u4f46\u8863\u88f3\u7834\u88c2\u51e0\u5904\uff0c\u800c\u4e14\u6cbe\u6ee1\u6c61\u6ce5\u3002\u90a3\u4e24\u6346\u67f4\u8349\u662f\u8c2d\u9053\u6210\u6311\u7684\uff0c\u7528 \u7684\u4e5f\u4e0d\u662f\u6241\u62c5\u800c\u4e00\u6839\u6811\u679d\u3002\u6700\u4ee4\u4ed6\u4eec\u5403\u60ca\u7684\u662f\uff1a\u8c2d\u9053\u6210\u8eab\u4e0a\u867d\u7136\u6ca1\u6709\u6cbe\u90a3\u4e48\u591a\u6c61\u6ce5\uff0c\u4f46\u5374\u6709 \u8840\u8ff9\u3002 \u3000\u3000\u8c2d\u9053\u6210\u628a\u67f4\u8349\u653e\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6253\u4e86\u4e00\u53ea\u8001\u864e\uff0c\u7239\uff0c\u4f60\u522b\u5bb3\u6015\uff0c\u8fd9\u662f\u8001\u864e\u8840\uff0c\u4e0d\u662f\u6211 \u7684\u8840\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u90a3\u6761\u864e\u817f\u4ece\u67f4\u8349\u4e1b\u4e2d\u62ff\u51fa\u6765\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u60f3\u4eca\u665a\u7ed9\u4f60\u4eec\u6dfb\u4e00\u9053\u83dc\uff0c\u505a\u70e4\u8001\u864e\u817f\u5403\u7684\u3002\u53ea\u597d\u660e\u5929\u518d\u5f04\u4e86\u3002\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u730e\u4e86\u4e24\u53ea\u5c71\u9e21\u56de\u6765\uff0c\u4eca\u665a\u7684\u83dc\u80b4\u662f\u591f\u4e30\u5bcc\u7684\u4e86\u3002 \u3000\u3000\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u76ee\u5149\u4e2d\u5ffd\u5730\u597d\u50cf\u5e26\u7740\u7591\u60d1\u7684\u795e\u6c14\uff0c\u76ef\u7740\u5973\u513f\u95ee\u9053\uff1a\u201c\u4f60\u4e5f\u6709\u5e2e\u5fd9\u6210\u54e5\u6253\u8001 \u864e\u5417\uff1f\u4f60\u867d\u7136\u4e0d\u6bd4\u5bfb\u5e38\u7684\u5f31\u8d28\u5973\u6d41\uff0c\u4f46\u6ca1\u7ec3\u8fc7\u6b66\u529f\uff0c\u53ef\u4e0d\u80fd\u4e0d\u81ea\u91cf\u529b\u554a\uff01\u201d \u3000\u3000\u5f20\u96ea\u88ab\u9053\uff1a\u201c\u6211\u521a\u78b0\u4e0a\u8001\u864e\uff0c\u6210\u54e5\u5c31\u6765\u4e86\u3002\u4ed6\u8bf4\u662f\u2018\u6211\u4eec\u2019\u6253\u7684\uff0c\u53ea\u662f\u60f3\u8ba9\u6211\u4e5f\u5206\u70b9\u529f \u52b3\u3002\u201d\u5979\u6015\u7239\u7239\u77e5\u9053\u5979\u66fe\u51fa\u624b\uff0c\u66f4\u4f1a\u8d23\u602a\u5979\u5fd8\u8bb0\u4ed6\u7684\u53ee\u5631\u3002\u5fc3\u60f3\u8fd8\u662f\u6682\u65f6\u9690\u7792\uff0c\u5f85\u5230\u53ea\u662f\u4e24 \u7236\u5973\u7684\u65f6\u5019\uff0c\u518d\u548c\u7239\u7239\u8bf4\u771f\u8bdd\u7684\u597d\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u6709\u8bb8\u591a\u7591\u56e2\u3002\u4e5f\u53ea\u80fd\u7b49\u5230\u6ca1\u4eba\u7684\u65f6\u5019\u518d\u95ee\u7239\u7239\u3002\u8c2d\u9053\u6210\u4f3c\u4e4e\u4ea6\u5df2\u77e5\u9053\u59bb\u5b50\u7684\u5fc3 \u601d\u3002\u53ea\u662f\u7b11\u7b11\uff0c\u6ca1\u6709\u62c6\u7a7f\u59bb\u5b50\u7684\u8c0e\u8bdd\u3002\u4f46\u4ed6\u5fc3\u91cc\u5374\u4e5f\u52a0\u6df1\u4e86\u4e00\u5c42\u7591\u60d1\uff1a\u4e3a\u4ec0\u4e48\u5cb3\u7236\u597d\u50cf\u5bb3\u6015 \u7ed9\u6211\u77e5\u9053\u96ea\u59b9\u61c2\u5f97\u6b66\u529f\uff1f\u5f20\u708e\u5f97\u77e5\u5973\u513f\u672a\u66fe\u663e\u9732\u6b66\u529f\uff0c\u65b9\u59cb\u653e\u4e0b\u5fc3\u4e0a\u4e00\u5757\u77f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u602a \u9053\u4f60\u5f04\u5f97\u8fd9\u6837\u72fc\u72c8\uff0c\u539f\u6765\u662f\u78b0\u4e0a\u8001\u864e\uff0c\u6389\u4e86\u4e00\u8de4\uff0c\u6ca1\u6454\u574f\u4f60\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\uff0c\u53ea\u4e0d\u8fc7\u64e6\u4f24\u4e00\u70b9\u8868\u76ae\uff0c\u8863\u88f3\u6709\u51e0\u5904\u52fe\u7834\u3002 \u3000\u3000\u51b2\u513f\u5462\uff1f\u201d\u6bcf\u6b21\u5979\u56de\u5230\u5bb6\u4e2d\uff0c\u603b\u662f\u5b69\u5b50\u6700\u5148\u8dd1\u51fa\u6765\u8fce\u63a5\u5979\u7684\u3002\u8fd9\u6b21\u56de\u5bb6\u3002 \u3000\u3000\u76f4\u5230\u5982\u4eca\u8fd8\u6ca1\u6709\u770b\u89c1\u5b69\u5b50\uff0c\u5979\u662f\u65e9\u5c31\u60f3\u95ee\u7239\u7239\u7684\u4e86\u3002\u6b64\u9645\u65b9\u6709\u673a\u4f1a\u53d1\u95ee\u3002 \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u51b2\u513f\u73a9\u4e86\u5927\u534a\u5929\u3002\u73b0\u5728\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4e0d\u89c9\u6709\u70b9\u5947\u602a\u201c\u51b2\u513f\u600e\u7684\u8fd9\u4e48\u65e9\u5c31\u7761\u4e86\u3002\u201d \u3000\u3000\u5979\u662f\u77e5\u9053\u5b69\u5b50\u7684\u4e60\u60ef\u7684\uff0c\u4e0d\u9519\uff0c\u5b69\u5b50\u662f\u559c\u6b22\u8e66\u8e66\u8df3\u8df3\uff0c\u73a9\u5f97\u5026\u4e86\u4e5f\u4f1a\u5c0f\u7761\u7247\u523b\uff0c\u4f46\u591a\u6570 \u662f\u5728\u5348\u996d\u4e4b\u540e\u90a3\u4e24\u4e09\u4e2a\u65f6\u8fb0\uff0c\u665a\u996d\u524d\u4ed6\u662f\u5f88\u5c11\u4f1a\u7761\u89c9\u7684\uff0c\u8fd9\u6bb5\u65f6\u95f4\u4ed6\u4e5f\u5f88\u5c11\u5230\u5916\u9762\u4e71\u8dd1\uff0c\u901a \u5e38\u662f\u5750\u5728\u5bb6\u4e2d\u8ddf\u7956\u7236\u6216\u8005\u5916\u516c\u8ba4\u5b57\uff0c\u8fd9\u6bb5\u65f6\u95f4\u662f\u4ed6\u4e00\u5929\u5185\u6700\u201c\u5b89\u9759\u201d\u7684\u65f6\u95f4\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u867d\u7136\u89c9\u5f97\u5b69\u5b50\u4eca\u5929\u6709\u70b9\u201c\u53cd\u5e38\u201d\uff0c\u4f46\u8fd9\u662f\u5c0f\u4e8b\u4e00\u6869\u3002\u5979\u4e5f\u6839\u672c\u6ca1\u653e\u5728\u5fc3\u4e0a\u3002\u5f53 \u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u56de\u623f\u95f4\u6362\u4e00\u5957\u8863\u88f3\uff0c\u770b\u770b\u51b2\u513f\u9192\u4e86\u6ca1\u6709\u3001\u201d\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4ed6\u7761\u5f97\u6b63\u6c89\uff0c\u4f60 \u522b\u5524\u9192\u4ed6\u3002\u7761\u524d\u4ed6\u5df2\u7ecf\u5403\u8fc7\u4e1c\u897f\uff0c\u7528\u4e0d\u7740\u62c5\u5fc3\u997f\u574f\u4ed6\u7684\u3002\u6211\u7559\u4e00\u6761\u9e21\u817f\u7ed9\u4ed6\u5c31\u662f\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u5f85\u6211\u6362\u8fc7\u8863\u88f3\uff0c\u5c31\u51fa\u6765\u5f00\u996d\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u7b11\u901a\uff1a\u201c\u4e0d\u7528\u52b3\u70e6\u4f60\u51fa\u6765\u624d\u5f00\u996d\u4e86\uff0c\u6211\u4e0d\u4f1a\u70e7\u5f04\u83dc\uff0c\u96be\u9053\u6446\u6446\u7897\u7b77\u90fd\u4e0d\u4f1a\u5417\uff1f\u201d \u5f20\u96ea\u6ce2\u77e5\u9053\u4e08\u592b\u7231\u62a4\u81ea\u5df2\uff0c\u5fc3\u5934\u4e00\u80a1\u751c\u610f\uff0c\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u5012\u662f\u6211\u80e1\u6d82\u4e86\uff0c\u54b1\u4eec\u5df2\u7ecf\u56de\u6765 \u665a\u4e86\uff0c\u600e\u80fd\u8fd8\u8ba9\u516c\u516c\u548c\u7239\u7239\u4e45\u7b49\u4e86\uff0c\u90a3\u4f60\u8d76\u5feb\u5f00\u996d\u4e86\uff0c\u6211\u4eec\u5148\u5403\u7f62\u3002\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u4e89\u5728\u8fd9\u523b\u65f6\u95f4\uff0c\u4e0d\u8fc7\u9e21\u6c64\u8fd8\u662f\u8d81\u70ed\u559d\u7684\u597d\u3002\u201d \u3000\u3000\u4e24\u7897\u9e21\u573a\u662f\u65e9\u5df2\u653e\u5728\u996d\u684c\u4e0a\u7684\uff1b\u867d\u7136\u5df2\u4e0d\u662f\u70ed\u817e\u817e\u7684\uff0c\u4e5f\u8fd8\u6709\u70ed\u6c14\u5192\u8d77\u3002 \u3000\u3000\u8c2d\u516c\u76f4\u7b11\u9053\uff1a\u201c\u8d24\u5ab3\u5987\u4f60\u77a7\uff0c\u4f60\u7684\u7239\u7239\u4e0d\u662f\u597d\u50cf\u5728\u5411\u6211\u732e\u5b9d\u4f3c\u7684\uff1f\u597d\u5427\uff0c\u8001\u5f20\uff0c\u4f60\u7b49\u6211 \u54c1\u8bc4\uff0c\u6211\u6765\u8bd5\u8bd5\u4f60\u7684\u624b\u827a\u5427\uff0c\u770b\u770b\u662f\u4f60\u505a\u8001\u5b50\u7684\u624b\u827a\u9ad8\uff0c\u8fd8\u662f\u4f60\u5973\u513f\u7684\u624b\u827a\u597d\uff1f\u201d\u5f20\u708e\u7b11 \u9053\uff1a\u201c\u8bba\u5230\u70f9\u8c03\u8fd9\u95e8\u529f\u592b\uff0c\u6211\u8fd9\u4e2a\u505a\u8001\u5b50\u662f\u4e0d\u80fd\u81ea\u8ba4\u6bd4\u4e0d\u4e0a\u5973\u513f\u7684\u3002\u201d\u8c2d\u516c\u76f4\u7b11\u9053\uff1a\u201d\u6211\u662f \u4f9d\u7406\u7c7b\u63a8\uff0c\u6709\u5176\u7236\u5fc5\u6709\u5176\u5973\uff0c\u8fd9\u540d\u8bdd\u4e5f\u53ef\u4ee5\u53cd\u8fc7\u6765\u8bf4\uff0c\u5973\u513f\u624b\u827a\u9ad8\uff0c\u4f60\u8fd9\u4e2a\u505a\u8001\u5b50\u7684\u5927\u6982\u4e5f \u4e0d\u4f1a\u5dee\u5230\u54ea\u91cc\u3002\u201d\u8bf4\u7f62\uff0c\u548c\u5f20\u708e\u540c\u65f6\u7aef\u8d77\u9e21\u6c64\u5c31\u559d\u3002 \u3000\u3000\u8c2d\u516c\u76f4\u559d\u4e86\u4e00\u53e3\u9e21\u6c64\u3002\u8138\u4e0a\u7684\u795e\u8272\u867d\u7136\u6ca1\u4ec0\u4e48\uff0c\u7709\u5934\u5374\u662f\u7565\u76b1\u3002 \u3000\u3000\u5f20\u708e\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4f9d\u7406\u7c7b\u63a8\uff0c\u8fd9\u6b21\u6050\u6015\u662f\u63a8\u9519\u4e86\u5427\uff1f\u662f\u4e0d\u662f\u6bd4\u96ea\u513f\u5e73\u662f\u7096\u7684\u9e21\u6c64\uff0c\u6ecb\u5473 \u5dee\u5f97\u592a\u8fdc\uff1f\u201d \u3000\u3000\u8c2d\u516c\u76f4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u8fd8\u597d\uff0c\u8fd8\u597d\uff0c\u53ea\u4e0d\u8fc7\u5dee\u90a3\u4e48\u4e00\u70b9\u513f\u3002\u201d\u539f\u6765\u9e21\u6c64\u7a0d\u7a0d\u6709\u70b9\u82e6\u5473\uff0c \u8c2d\u516c\u76f4\u6599\u60f3\u662f\u56e0\u5c71\u9e21\u70e7\u7126\u4e86\u7684\u539f\u56e0\uff0c\u8c2d\u9053\u6210\u7b11\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u5dee\u90a3\u4e48\u4e00\u70b9\uff0c\u90a3\u5c31\u4e0d\u53ea\u662f\u8fd8\u597d \u4e86\u3002\u201d \u3000\u3000\u8c2d\u516c\u76f4\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u96be\u5f97\u4f60\u7684\u8001\u4e08\u4eba\u7cbe\u5fc3\u6ce1\u5236\uff0c\u6211\u53ea\u8d5e\u8fd8\u597d\uff0c\u90a3\u7684\u786e\u662f\u4e0d\u516c \u9053\u4e86\uff0c\u597d\uff0c\u5f88\u597d\u3002\u201d\u8bf4\u7f62\uff0c\u5927\u53e3\u5927\u53e3\u5730\u559d\u3002\u5f20\u708e\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53e5\u2018\u5f88\u597d\u2019\uff0c\u90a3\u662f\u7740\u5728\u513f\u5b50\u7684 \u4efd\u4e0a\u5427\uff0c\u6211\u5012\u662f\u53d7\u4e4b\u6709\u6127\u4e86\u3002\u201d \u3000\u3000\u6f6d\u516c\u76f4\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u4eba\u5bb6\u8bf4\u5973\u751f\u5916\u5411\uff0c\u6211\u8fd9\u4e2a\u513f\u5b50\u5374\u662f\u504f\u7740\u8001\u4e08\u4eba\u5462\u3002\u8001\u5f20\uff0c\u4f60\u5012\u662f\u6709 \u81ea\u77e5\u4e4b\u660e\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5728\u4e24\u8001\u7684\u7b11\u58f0\u4e2d\uff0c\u6df1\u6df1\u611f\u5230\u5929\u4f26\u4e4b\u4e50\uff0c\u597d\u6ee1\u6000\u559c\u60a6\u5730\u56de\u81ea\u5df1\u7684\u5367\u623f\u3002 \u3000\u3000\u5b69\u5b50\u679c\u7136\u7761\u5f97\u5f88\u6c89\uff0c\u4e5f\u8f7b\u8f7b\u5728\u5b69\u5b50\u7eef\u7ea2\u7684\u8138\u5e9e\u4e0a\u4eb2\u4e00\u4e86\u4e00\u4eb2\uff0c\u5b69\u5b50\u6beb\u65e0\u77e5\u89c9\u3002 \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\u5b69\u5b50\u7684\u7761\u76f8\u6709\u70b9\u5947\u7279\uff0c\u5979\u8bd5\u8bd5\u628a\u5b69\u5b50\u66f2\u8d77\u7684\u53cc\u819d\u8f7b\u8f7b\u6447\u76f4\uff0c\u5b69\u5b50\u8fd8\u662f\u52a8\u4e5f\u4e0d \u52a8\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u53ef\u80fd\u662f\u51fa\u4e8e\u6bcd\u6027\u672c\u80fd\u7684\u53cd\u5e94\uff0c\u4e0d\u89c9\u7a0d\u7a0d\u8d77\u4e86\u4e00\u70b9\u7591\u5fc3\uff0c\u6155\u5730\u5979\u60f3\u8d77\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4e0d\u8fc7\u662f\u4e0a\u4e2a\u6708\u7684\u4e8b\u60c5\uff0c\u7239\u7239\u6697\u4e2d\u6559\u5979\u5b66\u70b9\u7a74\u7684\u529f\u592b\u3002\u4e0a\u4e2a\u6708\u662f\u519c\u4e5d\u6708\uff0c\u6b63\u662f\u6253\u730e\u6700\u597d\u7684 \u5b63\u8282\uff0c\u79cb\u9ad8\u6c14\u723d\uff0c\u91ce\u517d\u5c1a\u672a\u201c\u51ac\u85cf\u201d\u3002\u8c2d\u516c\u76f4\u7236\u5b50\u51e0\u4e4e\u5929\u5929\u51fa\u53bb\u6253\u730e\uff0c\u5f20\u708e\u5c31\u5728\u5bb6\u91cc\u6559\u5973\u513f \u7ec3\u70b9\u7a74\u529f\u592b\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u8bb0\u5f97\u7236\u4eb2\u66fe\u544a\u8beb\u8fc7\u5979\u201c\u70b9\u7a74\u529f\u592b\u4e0d\u8981\u8f7b\u6613\u4f7f\u7528\uff0c\u82e5\u7136\u70b9\u7740\u6b7b\u7a74\uff0c\u8f7b\u8f7b\u4e00\u622e\uff0c\u5c31\u4f1a \u81f4\u4eba\u4e8e\u6b7b\u5730\u3001\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u90a3\u4e48\u6211\u53ea\u70b9\u654c\u4eba\u7684\u9ebb\u7a74\u6216\u6655\u7761\u7a74\u5c31\u884c\u4e86\uff1f\u201d\u5979\u7239\u7239\u8bf4\uff1a\u201c\u4e0d \u9519\uff0c\u4f46\u4ea4\u624b\u4e4b\u9645\u8981\u70b9\u5f97\u8fd9\u4e48\u51c6\u53ef\u662f\u96be\u4e8b\u3002\u8fd8\u6709\uff0c\u5373\u4f7f\u70b9\u666e\u901a\u7a74\u9053\uff0c\u65f6\u95f4\u957f\u4e86\uff0c\u672a\u80fd\u89e3\u7a74\uff0c\u5bf9 \u8eab\u4f53\u4e5f\u8fd8\u662f\u6709\u59a8\u5bb3\u3002 \u3000\u3000\u9664\u975e\u4f60\u7ec3\u5230\u6211\u7684\u4e00\u79cd\u72ec\u95e8\u70b9\u7a74\u529f\u592b\uff0c\u90a3\u624d\u53ef\u4ee5\u907f\u514d\u4f24\u4eba\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u597d\u5947\u5fc3\u91cd\uff0c\u5f53\u7136\u8ffd\u95ee\u4e0b\u53bb\uff0c\u7a76\u7adf\u4ec0\u4e48\u72ec\u95e8\u70b9\u7a74\u529f\u592b\u3002\u5979\u7239\u7239\u544a\u8bc9\u5979\uff0c\u8fd9\u79cd\u72ec\u95e8\u70b9 \u7a74\u529f\u592b\uff0c\u662f\u70b9\u5bf9\u65b9\u6655\u7761\u7684\uff0c\u4e0d\u4f46\u4e0d\u4f1a\u4f24\u4eba\uff0c\u800c\u4e14\u6709\u52a9\u4e8e\u5b89\u7720\uff0c\u53ef\u4ee5\u4e3a\u60a3\u4e0a\u5931\u7720\u75c7\u7684\u4eba\u4f5c\u6cbb\u7597 \u4e4b\u7528\uff0c\u975e\u4f46\u65e0\u5bb3\u800c\u4e14\u6709\u76ca\u3002\u5979\u7239\u7239\u8fd8\u544a\u8bc9\u5979\uff0c\u9664\u4e86\u5931\u7720\u75c7\uff0c\u70b9\u7a74\u53ef\u4ee5\u6cbb\u5176\u4ed6\u7684\u75c5\u3002 \u3000\u3000\u7239\u7239\u544a\u8bc9\u5979\uff1a\u201c\u70b9\u7a74\u4e5f\u5206\u4e24\u79cd\uff0c\u4e00\u79cd\u662f\u4f5c\u4e3a\u4e0a\u4e58\u6b66\u529f\u7684\u70b9\u7a74\uff0c\u53ef\u4ee5\u6740\u4eba\u4f24\u4eba\u7684\u70b9\u7a74\uff1b\u4e00 \u79cd\u662f\u533b\u672f\u4e0a\u7684\u70b9\u7a74\uff0c\u53ef\u4ee5\u6cbb\u75c5\u6551\u4eba\u7684\u70b9\u7a74\u3002\u533b\u672f\u4e0a\u7684\u70b9\u7a74\u662f\u4e00\u9879\u6781\u4e3a\u6df1\u5965\u7684\u5b66\u95ee\u3002\u6211\u6839\u672c\u672a \u5165\u95e8\u3002\u4e0d\u8fc7\u6211\u70b9\u6655\u7761\u7a74\u7684\u72ec\u95e8\u529f\u592b\uff0c\u5012\u662f\u628a\u6b66\u529f\u4e0e\u533b\u672f\u5408\u800c\u4e3a\u4e00\u7684\uff0c\u53ef\u60dc\u6211\u53ea\u61c2\u4e00\u79cd\u4e8e\u4eba\u6709 \u76ca\u7684\u70b9\u7a74\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8352\u5c71\u4e0a\u9690\u5c45\uff0c\u654c\u4eba\u662f\u4e0d\u4f1a\u6709\u7684\u3002\u7239\u7239\uff0c\u4f60\u5148\u628a\u8fd9\u79cd\u4e8e\u4eba\u6709\u76ca\u7684\u70b9\u7a74 \u529f\u592b\u6559\u7ed9\u6211\u597d\u4e0d\u597d\u3002\u201d\u5979\u7684\u7239\u7239\u4e00\u542c\u5c31\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u8fd9\u79cd\u72ec\u95e8\u70b9\u7a74\u529f\u592b\u662f\u5bb9\u6613\u7ec3 \u7684\u4e48\uff0c\u5373\u4f7f\u4f60\u6709\u4e86\u6211\u73b0\u5728\u7684\u6b66\u529f\u5e95\u5b50\uff0c\u6700\u597d\u4e5f\u8fd8\u5f97\u82e6\u7ec3\u5341\u5e74\u3002 \u3000\u3000\u666e\u901a\u7684\u70b9\u7a74\u529f\u592b\u5bb9\u6613\u5f97\u591a\u4e86\uff0c\u53ea\u8981\u4f60\u52e4\u5b66\u82e6\u7ec3\uff0c\u5927\u6982\u534a\u5e74\u4e4b\u5185\u5c31\u53ef\u4ee5\u7ec3\u6210\u3002\u201d \u3000\u3000\u6240\u8c13\u201c\u666e\u904d\u7684\u70b9\u7a74\u529f\u592b\u201d\u4ea6\u5373\u662f\u53ef\u4ee5\u6740\u4eba\u4f24\u4eba\u7684\u90a3\u79cd\u70b9\u7a74\u529f\u592b\uff0c\u5979\u8bb0\u5f97\u5f53\u65f6\u5979\u8fd8\u7b11\u9053\uff1a \u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5c82\u4e0d\u662f\u6740\u4eba\u5bb9\u6613\u6551\u4eba\u96be\u5417\uff1f\u201d \u3000\u3000\u5979\u7239\u7239\u82e6\u7b11\u9053\uff1a\u201c\u6740\u4eba\u5bb9\u6613\u6551\u4eba\u96be\uff01\u5440\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u81ea\u53e4\u4ee5\u6765\u5c31\u662f\u5982\u6b64\u3002\u201d\u5979\u4e5f\u4e0d\u77e5 \u7239\u7239\u56e0\u4f55\u6709\u6b64\u611f\u6168\u3002 \u3000\u3000\u60f3\u8d77\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6b64\u9645\u5979\u770b\u7740\u6c89\u7761\u7684\u5b69\u5b50\uff0c\u5979\u4e5f\u7981\u4e0d\u4f4f\u82e6\u7b11\u4e86\u3002\u5f53\u7136\u5979\u4e0d\u662f\u5bb3\u6015\u7239\u7239\u4f1a\u4f24 \u5bb3\u5979\u7684\u5b69\u5b50\uff0c\u4f46\u5b69\u5b50\u7761\u5f97\u8fd9\u6837\u6c89\uff0c\u5979\u5374\u53ef\u4ee5\u65ad\u5b9a\u662f\u7ed9\u70b9\u4e86\u6655\u7761\u7a74\u4e86\u3002 \u3000\u3000\u70b9\u4e86\u5b69\u5b50\u7a74\u9053\u4eba\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u662f\u522b\u7684\u4eba\uff0c\u53ea\u80fd\u662f\u5979\u7684\u201c\u7239\u7239\u201d\u3002 \u3000\u3000\u867d\u7136\u201c\u7239\u7239\u201d\u53ea\u662f\u5979\u7684\u517b\u7236\uff0c\u4f46\u5bf9\u5b59\u513f\u75bc\u7231\uff0c\u548c\u522b\u4eba\u5bb6\u7684\u7956\u7236\u5e76\u65e0\u5206\u522b\uff0c\u751a\u4e14\u662f\u53ea\u6709\u8fc7 \u4e4b\u65e0\u4e0d\u53ca\u7684\u3002 \u3000\u3000\u5f53\u7136\uff0c\u5979\u7edd\u5bf9\u4e0d\u4f1a\u7591\u5fc3\u7239\u7239\u5bb3\u5979\u7684\u5b69\u5b50\uff0c\u4e8b\u5b9e\u4e0a\u5979\u4ea6\u77e5\u9053\u4e86\u7239\u7239\u8fd9\u79cd\u70b9\u6655\u7761\u7a74\u7684\u72ec\u95e8\u529f \u592b\uff1b\u5bf9\u5b69\u5b50\u4e43\u662f\u6709\u76ca\u65e0\u5bb3\u7684\u3002 \u3000\u3000\u4f46\u5979\u53ef\u4e0d\u80fd\u4e0d\u7591\u4e5f\u4e3a\u4ec0\u4e48\u7239\u7239\u8981\u70b9\u5b59\u513f\u7684\u7a74\u9053\uff1f\u5979\u7684\u5b69\u5b50\u6ca1\u6709\u5931\u7720\u75c7\uff0c\u5e73\u65f6\u8e66\u8e66\u8df3\u8df3\uff0c \u6d3b\u529b\u5145\u6c9b\uff0c\u4e5f\u65e0\u987b\u7528\u70b9\u7a74\u7684\u529f\u592b\u66ff\u4ed6\u6cbb\u75c5\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u96be\u9053\u53ea\u662f\u4e3a\u4e86\u8981\u8ba9\u5b69\u5b50\u6c89\u7761\u5417\uff1f\u5b69\u5b50\u591a\u7761\u4e00\u4e24\u4e2a\u65f6\u8fb0\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b \u7684\u597d\u5904\u7684\uff0c\u53cd\u800c\u8bef\u4e86\u4ed6\u5403\u665a\u996d\u7684\u65f6\u95f4\uff01 \u3000\u3000\u6000\u7740\u7591\u56e2\uff0c\u5979\u5306\u5306\u6362\u4e86\u8863\u88f3\uff0c\u4fbf\u5373\u51fa\u53bb\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u4e8c \u56de\u3000\u4eb2\u53cb\u6210\u4ec7 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u4e8c \u56de\u3000\u4eb2\u53cb\u6210\u4ec7 \u3000\u3000\u5f20\u708e\u6b63\u5728\u529d\u5973\u5a7f\u559d\u9e21\u6c64\u3002 \u3000\u3000\u201c\u6211\u6b63\u662f\u8981\u4f60\u8d81\u7740\u96ea\u513f\u8fd8\u672a\u51fa\u6765\u7684\u65f6\u5019\uff0c\u7ed9\u6211\u54c1\u8bc4\u54c1\u6026\uff0c\u5426\u5219\u4f60\u5c31\u4e0d\u597d\u610f\u601d\u5f53\u7740\u59bb\u5b50\u7684 \u9762\u8c08\u8001\u4e08\u4eba\u7684\u624b\u827a\u4e86\u3002\u201d\u8001\u4e08\u4eba\u7684\u8bf4\u8bdd\u8fd9\u6837\u98ce\u8da3\uff0c\u9017\u5f97\u5973\u5a7f\u4e5f\u4e0d\u7981\u7b11\u4e86\u8d77\u6765\u3002\u7b11\u8bed\u58f0\u4e2d\uff0c\u8c2d \u9053\u6210\u7aef\u8d77\u9e21\u6c64\u4fbf\u559d\u3002 \u3000\u3000\u4e0d\u6599\u7897\u8fb9\u521a\u521a\u6cbe\u5507\uff0c\u9e21\u6c64\u5c1a\u672a\u5165\u53e3\uff0c\u5ffd\u5730\u4e00\u80a1\u52b2\u98ce\u626b\u6765\uff0c\u6c64\u7897\u843d\u5730\u5f00\u82b1\uff0c\u788e\u6210\u7247\u7247\uff01 \u3000\u3000\u6c64\u7897\u7684\u7834\u88c2\u58f0\u548c\u4ed6\u7236\u4eb2\u7684\u66b4\u559d\u58f0\u540c\u65f6\u54cd\u8d77\u3002 \u3000\u3000\u201c\u8fd9\u6c64\u4e0d\u80fd\u559d\uff01\u201d \u3000\u3000\u539f\u6765\u662f\u8c2d\u516c\u76f4\u4ee5\u5288\u7a7a\u638c\u529b\u6253\u788e\u513f\u5b50\u624b\u4e2d\u7684\u6c64\u7897\u7684\u3002\u4ed6\u5148\u53d1\u638c\u540e\u53d1\u58f0\uff0c\u663e\u7136\u662f\u6015\u6765\u4e0d\u53ca\u963b \u6b62\u513f\u5b50\u559d\u4e0b\u9e21\u6c64\u3002 \u3000\u3000\u4e8b\u60c5\u6765\u5f97\u5982\u6b64\u7a81\u7136\uff0c\u8c2d\u9053\u6210\u60ca\u6115\u5f97\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff01 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8fd9\u573a\u4e0d\u80fd\u559d\uff1f\u65e2\u7136\u4e0d\u80fd\u559d\uff0c\u4e3a\u4ec0\u7239\u7239\u53c8\u559d\u4e86\u5462\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u7684\u7591\u95ee\u8fd8\u672a\u8bf4\u51fa\u53e3\u6765\uff0c\u4ed6\u5df2\u542c\u5230\u4e86\u7236\u4eb2\u7684\u89e3\u7b54\u4e86\uff01 \u3000\u3000\u201c\u5f20\u708e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6bd2\u6b7b\u6211\u4eec\u7236\u5b50\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u5c1a\u5728\u53d1\u5446\uff0c\u4ed6\u7684\u7236\u4eb2\u5df2\u662f\u4e00\u58f0\u6012\u543c\uff0c\u5411\u4ed6\u7684\u4e08\u4eba\u6251\u8fc7\u53bb\u4e86\uff01 \u3000\u3000\u8fd9\u771f\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u8c2d\u9053\u6210\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff01\u600e\u7684\u4f1a\u6709\u8fd9\u4e2a\u53ef\u80fd\u5462\uff0c \u5cb3\u7236\u610f\u7136\u8981\u6bd2\u6b7b\u81ea\u5df1\u7684\u5973\u5a7f\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u60ca\u5f97\u5446\u4e86\uff01 \u3000\u3000\u7236\u4eb2\u548c\u5cb3\u7236\u5df2\u7ecf\u6253\u8d77\u6765\u4e86\uff0c\u8c2d\u516c\u76f4\u7684\u773c\u775b\u597d\u50cf\u8981\u55b7\u51fa\u706b\u6765\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u91cd\u624b\uff0c\u653b\u5411\u5f20\u708e \u7684\u8981\u5bb3\u3002\u5f20\u708e\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e5f\u662f\u62db\u62db\u72e0\u8fa3\u3002\u4e24\u4eb2\u5bb6\u90fd\u597d\u4f3c\u6068\u4e0d\u5f97\u4e00\u62f3\u6253\u6b7b\u5bf9\u65b9\u3002\u90a3\u91cc\u8fd8\u662f\u4e24\u4eb2 \u5bb6\uff0c\u7b80\u76f4\u662f\u597d\u50cf\u548c\u4ec7\u4eba\u62fc\u547d\uff01\u5f20\u708e\u6697\u6697\u5403\u60ca\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u7684\u5185\u529f\u7adf\u7136\u6df1\u539a\u5982\u65af\uff0c\u559d\u4e86\u6bd2\u6c64\uff0c \u4e5f\u8fd8\u8fd9\u6837\u4e86\u5f97\uff01\u201d \u3000\u3000\u4ed6\u62fc\u547d\u62b5\u6321\uff0c\u53ea\u76fc\u80fd\u591f\u652f\u6301\u5230\u8c2d\u516c\u76f4\u6bd2\u53d1\u7684\u65f6\u5019\u3002 \u3000\u3000\u8c2d\u516c\u76f4\u4e5f\u662f\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\uff0c\u5728\u81ea\u5df1\u6bd2\u53d1\u4e4b\u524d\uff0c\u628a\u6697\u7b97\u81ea\u5df1\u7684\u4ec7\u4eba\u6bd9\u4e8e\u638c\u4e0b\u3002 \u3000\u3000\u6076\u6597\u4e2d\u6f6d\u516c\u76f4\u4e00\u4e2a\u201c\u79fb\u5f62\u6613\u4f4d\u201d\uff0c\u8f6c\u5230\u5f20\u708e\u8eab\u540e\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u51fb\u4ed6\u540e\u5fc3\u3002\u5f20\u708e\u8981\u5411\u524d \u7a9c\uff0c\u6015\u4ed6\u5c31\u62db\u8d76\u62db\uff0c\u529b\u4e0a\u52a0\u529b\uff0c\u518d\u63a8\u4e00\u4e0b\uff0c\u83ab\u8bf4\u88ab\u4ed6\u6253\u7740\uff0c\u53ea\u8fd9\u5288\u7a7a\u638c\u529b\uff0c\u5c31\u80fd\u4ee4\u4ed6\u91cd\u4f24\u3002 \u82e5\u7136\u5411\u65c1\u95ea\u907f\uff0c\u4e5f\u52bf\u5fc5\u9732\u51fa\u7a7a\u95e8\uff0c\u9ad8\u624b\u640f\u6597\uff0c\u88ab\u4eba\u653b\u5165\u7a7a\u95e8\uff0c\u90a3\u4ea6\u7b49\u4e8e\u662f\u628a\u6027\u547d\u4ea4\u5230\u5bf9\u65b9\u624b \u4e0a\u4e86\u3002\u5f20\u708e\u96be\u4ee5\u6551\u62db\uff0c\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u65e0\u6687\u8003\u8651\uff0c\u53ea\u80fd\u4e0e\u5bf9\u65b9\u62da\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff01\u4ed6\u811a\u8ddf \u4e00\u65cb\uff0c\u56de\u8eab\u51fa\u638c\uff0c\u7adf\u4e0d\u6551\u62db\u3002\u53cd\u53d6\u653b\u52bf\u3002\u53f3\u638c\u5411\u5916\u4e00\u6302\uff0c\u5de6\u62f3\u7ffb\u8d77\uff0c\u201c\u7f9a\u7f8a\u6302\u89d2\u201d\uff0c\u6076\u72e0\u72e0 \u5730\u671d\u7740\u8c2d\u516c\u76f4\u7684\u592a\u9633\u7a74\u731b\u51fb\uff01 \u3000\u3000\u8c2d\u516c\u76f4\u4e5f\u6b63\u5728\u62f3\u638c\u517c\u65bd\uff0c\u72e0\u4e0b\u6740\u624b\u3002 \u3000\u3000\u773c\u770b\u5c31\u8981\u6709\u4eba\u8840\u6e85\u5c18\u57c3\uff0c\u8bf4\u4e0d\u5b9a\u751a\u81f3\u662f\u53cc\u65b9\u540c\u65f6\u5012\u6bd9\uff01 \u3000\u3000\u8c2d\u9053\u6210\u60ca\u9b42\u672a\u5b9a\uff0c\u4f46\u5df2\u6062\u590d\u51e0\u5206\u6e05\u9192\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u5413\u5f97\u8df3\u8d77\u6765\u5927\u53eb\uff1a\u201c\u4e0d\u8981\u6253\u4e86\uff0c\u6211\u6c42 \u6c42\u4f60\u4eec\u4e0d\u8981\u6253\u4e86\uff0c\u6709\u3001\u6709\u8bdd\u597d\u3001\u597d\u8bf4\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u5f20\u708e\u5de6\u81c2\u8f6f\u7ef5\u7ef5\u5730\u540a\u4e86\u4e0b\u6765\uff0c\u53f3\u638c\u79bb\u6f6d\u516c\u76f4\u7684\u592a\u9633\u7a74 \u4e0d\u5230\u4e09\u5bf8\uff0c\u4f46\u5df2\u65e0\u6cd5\u5411\u524d\u6253\u53bb\uff0c\u6f6d\u516c\u76f4\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u4ed6\u8e22\u7ffb\uff01 \u3000\u3000\u539f\u6765\u8c2d\u516c\u76f4\u662f\u8d81\u4ed6\u4f7f\u7528\u9669\u62db\u4e4b\u9645\uff0c\u9aa4\u4e0b\u6740\u624b\uff0c\u7a7f\u5fc3\u638c\u6539\u4e3a\u64d2\u62ff\u624b\uff0c\u5411\u4ed6\u81c2\u6253\u53bb\uff0c\u4ed6\u662f\u7ec3 \u6709\u9e70\u722a\u529f\u7684\uff0c\u5f20\u708e\u7684\u5173\u8282\u8981\u5bb3\u4e2d\u4e86\u4e00\u638c\u5df2\u4e0d\u5f97\u4e86\uff0c\u66f4\u90a3\u582a\u53c8\u7ed9\u4ed6\u987a\u52bf\u4e00\u62d7\uff0c\u5de6\u81c2\u5173\u8282\uff0c\u767b\u65f6 \u5c31\u7ed9\u6298\u65ad\u4e86\u3002 \u3000\u3000\u4f46\u5bf9\u5f20\u708e\u800c\u8a00\uff0c\u8fd9\u8fd8\u662f\u4e0d\u5e78\u4e2d\u7684\u5927\u5e78\uff0c\u5047\u5982\u8c2d\u516c\u76f4\u4e0d\u628a\u7a7f\u5fc3\u638c\u6539\u4e3a\u64d2\u62ff\u624b\uff0c\u65e9\u5df2\u53d6\u4e86\u5f20 \u708e\u7684\u6027\u547d\u3001\u4e0d\u8fc7\u82e5\u7136\u8fd9\u6837\u7684\u8bdd\uff0c\u8c2d\u516c\u76f4\u7684\u592a\u9633\u7a74\u4e5f\u6709\u7ed9\u5f20\u708e\u51fb\u4e2d\u7684\u5371\u9669\u3002\u8c2d\u516c\u76f4\u6ca1\u6709\u628a\u63e1\u907f \u5f00\u4ed6\u8fd9\u4e00\u51fb\uff0c\u53ea\u80fd\u5148\u628a\u5bf9\u65b9\u4e00\u6761\u624b\u81c2\u62d7\u6298\uff0c\u6d88\u89e3\u654c\u65b9\u81f4\u547d\u7684\u653b\u52bf\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u4ed6\u5012\u662f\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u4ed6\u81ea\u77e5\u4e2d\u7684\u4e43\u662f\u5267\u6bd2\u3002\u5f85\u5230\u53d1\u89c9\u4e4b\u65f6\uff0c\u5df2\u662f\u4e2d\u6bd2\u751a\u6df1\u3002\u800c\u4e14 \u53c8\u7ecf\u8fc7\u8fd9\u573a\u6076\u6597\uff0c\u6050\u6015\u7eb5\u6709\u89e3\u836f\uff0c\u4e5f\u96be\u6d3b\u547d\u3002 \u3000\u3000\u4ed6\u907f\u8fc7\u4e86\u4e0e\u5bf9\u65b9\u540c\u5f52\u4e8e\u5c3d\u7684\u5371\u9669\uff0c\u53ea\u56e0\u4e3a\u4e0d\u613f\u610f\u6b7b\u5728\u654c\u4eba\u7684\u524d\u5934\uff0c\u5e76\u975e\u662f\u8981\u9976\u6055\u654c\u4eba\u3002 \u3000\u3000\u4ed6\u4e00\u811a\u8e22\u7ffb\u5f20\u708e\uff0c\u773c\u775b\u5df2\u662f\u4e00\u9635\u9635\u53d1\u9ed1\uff0c\u4ed6\u5927\u543c\u4e00\u58f0\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8981\u6bd2\u6b7b \u6211\uff0c\u6211\u5148\u8981\u4f60\u7684\u6027\u547d\uff01\u201d\u53cc\u624b\u627c\u4f4f\u5f20\u708e\u7684\u5589\u5499\uff0c\u8c2d\u9053\u6210\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4e0d\u53ef\uff01\u201d \u3000\u3000\u8c2d\u516c\u76f4\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u5f53\u4ed6\u662f\u5cb3\u7236\u5417\uff0c\u4ed6\u662f\u8981\u6bd2\u6b7b\u4f60\u7684\u5978\u4eba\uff01\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u4f60\u53eb\u4ed6\u628a\u89e3 \u836f\u62ff\u51fa\u6765\uff0c\u9976\u4ed6\u4e00\u6b7b\u5427\uff01\u201d \u3000\u3000\u8c2d\u516c\u76f4\u9053\uff1a\u201c\u4ed6\u5904\u5fc3\u79ef\u8651\uff0c\u8c0b\u5bb3\u54b1\u4eec\u7236\u5b50\u3002\u7528\u5fc3\u5982\u6b64\u6076\u6bd2\uff0c\u6211\u7edd\u4e0d\u80fd\u9976\u4ed6\uff01\u6211\u4e00\u751f\u5149\u660e \u78ca\u843d\uff0c\u4e0d\u5c51\u9a97\u4ed6\u89e3\u836f\uff01\u201d\u4f46\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u7cbe\u795e\u4e0d\u80fd\u4e13\u6ce8\uff0c\u627c\u4f4f\u5f20\u708e\u5589\u5499\u7684\u53cc\u624b\uff0c\u5374\u662f\u4e0d\u514d \u7a0d\u5fae\u677e\u5f00\u5730\u4e86\u3002 \u3000\u3000\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u8df3\u8d8a\u53d1\u52a0\u5267\uff0c\u6307\u5934\u4e5f\u5728\u6e10\u6e10\u50f5\u786c\u4e86\u3002\u4ed6\u5438\u4e00\u53e3\u6c14\uff0c\u91cd\u65b0\u7528\u529b\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u90fd\u8981\u4eb2\u624b\u62a5\u4ec7\uff01\u201d\u8c2d\u9053\u6210\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u4ed6\u542c\u89c1\u59bb\u5b50\u8d70\u6765\u7684 \u811a\u6b65\u58f0\u3002 \u3000\u3000\u4eba\u672a\u5230\uff0c\u58f0\u97f3\u5148\u5230\u3002 \u3000\u3000\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u6210\u54e5\uff0c\u6210\u54e5\uff01\u201d\u60ca\u60f6\u7d27\u4fc3\u7684\u547c\u53eb\uff01 \u3000\u3000\u5f20\u708e\u88ab\u6390\u4f4f\u5589\u5499\uff0c\u5f53\u7136\u8bf4\u4e0d\u51fa\u6d3b\u3002 \u3000\u3000\u8c2d\u9053\u6210\u60ca\u5fc3\u5de8\u53d8\uff0c\u4e00\u7247\u832b\u7136\uff0c\u597d\u50cf\u662f\u5728\u6076\u68a6\u4e4b\u4e2d\uff0c\u795e\u667a\u5c1a\u672a\u6062\u590d\u6e05\u9192\u3002\u4ed6\u4e5f\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u8d70\u51fa\u5367\u623f\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u9690\u9690\u542c\u5230\u4e86\u5406\u559d\u3001\u6bb4\u6253\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u8fd9\u662f\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u867d\u7136\u542c\u5230\u7684\u58f0\u97f3\u5206\u660e\u662f\u6253\u67b6\u7684\u58f0\u97f3\uff0c\u5979\u8fd8\u4e0d\u6562\u76f8\u4fe1\u662f\u6709 \u4eba\u6253\u67b6\u3002\uff08\u996d\u5385\u91cc\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u516c\u516c\u3001\u7239\u7239\u548c\u4e08\u592b\uff0c\u8c01\u548c\u8c01\u6253\u67b6\u5462\uff1f\uff09\u5979\u52a0\u5feb\u811a\u6b65\uff0c\u8dd1\u5230\u996d \u5385\u524d\u9762\u7684\u5929\u4e95\uff0c\u8fd9\u624d\u6e05\u6e05\u695a\u695a\u542c\u5230\u4e86\u516c\u516c\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u90a3\u53e5\u8bdd\u662f\u9a82\u4ed6\u7684\u4e08\u592b\u7684\u3002 \u3000\u3000\u201c\u4f60\u8fd8\u5f53\u4ed6\u662f\u5cb3\u7236\u5417\uff1f\u4ed6\u662f\u8981\u6bd2\u6b7b\u4f60\u7684\u5978\u4eba\uff01\u201d \u3000\u3000\u597d\u50cf\u6674\u5929\u8d77\u4e86\u9739\u96f3\uff0c\u5934\u9876\u54cd\u8d77\u7126\u96f7\uff0c\u8f70\u7684\u4e00\u58f0\uff0c\u53ea\u89c9\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u5fc3\u5934\u9707\u8361\u4e0d\u4f11\uff0c\u4e0b \u9762\u4e08\u592b\u8bf4\u7684\u4ec0\u4e48\uff0c\u5979\u5df2\u662f\u542c\u800c\u4e0d\u95fb\u4e86\u3002 \u3000\u3000\u516c\u516c\u8bf4\u7684\u90a3\u53e5\u8bdd\u5979\u867d\u7136\u542c\u5f97\u6e05\u695a\uff0c\u4f46\u56e0\u4e3a\u8fd9\u6837\u7684\u4e8b\u60c5\u662f\u5979\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u7684\uff0c\u867d\u7136\u6bcf\u4e00\u4e2a \u5b57\u5730\u90fd\u542c\u89c1\u4e86\uff0c\u5979\u8fd8\u662f\u4e0d\u80fd\u63a5\u53d7\u8fd9\u4e2a\u4e8b\u5b9e\uff01 \u3000\u3000\u5979\u516d\u795e\u65e0\u4e3b\uff0c\u53ea\u80fd\u5927\u58f0\u547c\u53eb\uff0c\u547c\u53eb\u5979\u81f3\u4eb2\u81f3\u7231\u7684\u4eba\uff01\u517b\u7236\u548c\u4e08\u592b\u5728\u5979\u5fc3\u4e2d\u96be\u5206\u8f69\u8f7e\uff0c\u4e00 \u6837\u7684\u90fd\u662f\u5979\u81f3\u4eb2\u81f3\u7231\u7684\u4eba\uff01 \u3000\u3000\u7239\u7239\uff01\u6210\u54e5\uff01\u7239\u7239\uff01\u6210\u54e5\uff01\u7239\u7239\u548c\u6210\u54e5\u90fd\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u542c\u4e0d\u89c1\u4ed6\u4eec\u7684\u56de\u7b54\uff0c\u5979\u66f4\u52a0\u614c\u4e71\u4e86\uff0c\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u51b2\u8fdb\u996d\u5385\u3002 \u3000\u3000\u773c\u524d\u7684\u60c5\u666f\uff0c\u5413\u5f97\u5979\u9b42\u98de\u9b44\u6563\uff01 \u3000\u3000\u4f46\u65e0\u8bba\u600e\u6837\u60ca\u614c\uff0c\u7239\u7239\u7684\u6027\u547d\u5979\u662f\u4e0d\u80fd\u4e0d\u6551\u7684\u3002 \u3000\u3000\u4e0d\u662f\u60ca\u614c\u7684\u65f6\u5019\uff0c\u4e0d\u662f\u4f24\u5fc3\u7684\u65f6\u5019\uff0c\u66f4\u4e0d\u662f\u72b9\u7591\u7684\u65f6\u5019\uff01\u5979\u65e0\u6687\u601d\u7d22\uff0c\u7acb\u5373\u8dd1\u8fc7\u53bb\u6273\u5979 \u516c\u516c\u7684\u624b\u3002 \u3000\u3000\u6f6d\u516c\u76f4\u7684\u624b\u867d\u7136\u6b63\u5728\u5f00\u59cb\u50f5\u786c\uff0c\u4f46\u4e24\u4eba\u7684\u529f\u529b\u76f8\u5dee\u592a\u8fdc\uff0c\u5ab3\u5987\u8fd8\u662f\u6273\u4e0d\u5f00\u516c\u516c\u7684\u624b\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u53eb\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4f60\u5feb\u6765\u5e2e\u5e2e\u5fd9\u5440\u2019\u201d \u3000\u3000\u59bb\u5b50\u501a\u9760\u4e08\u592b\u3002\u8fd9\u4e0d\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u5417\uff1f\u5c24\u5176\u5bf9\u5979\u800c\u8a00\uff0c\u66f4\u662f\u5982\u6b64\u3002 \u3000\u3000\u4eca\u5929\u5979\u51e0\u4e4e\u547d\u4e27\u864e\u53e3\uff0c\u4e0d\u4e5f\u6b63\u662f\u4e08\u592b\u6551\u4e86\u5979\u7684\u5417\uff1f\u6b63\u56e0\u5979\u501a\u9760\u4e08\u592b\u5df2\u6210\u4e60\u60ef\uff0c\u5728\u8fd9\u7d27\u8981 \u7684\u5173\u5934\u3002\u5979\u4e0d\u81ea\u89c9\u5730\u5c31\u5411\u4e08\u592b\u6c42\u63f4\u4e86\u3002\u7adf\u6ca1\u60f3\u5230\u5979\u662f\u8981\u4e08\u592b\u53bb\u5bf9\u4ed8\u4ed6\u7684\u7236\u4eb2\u3002 \u3000\u3000\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u5019\uff0c\u8c2d\u516c\u76f4\u4e5f\u5728\u559d\u9053\uff1a\u201c\u6210\u513f\uff0c\u7ed9\u6211\u628a\u8fd9\u8d31\u4eba\u6740\u6389\uff01\u201d \u3000\u3000\u8d31\u4eba\uff0c\u8c01\u662f\u8d31\u4eba\uff1f\u8c2d\u9053\u6210\u4e0e\u59bb\u5b50\u4e00\u5411\u662f\u76f8\u4eb2\u76f8\u7231\uff0c\u66f4\u517c\u76f8\u656c\u5982\u5bbe\u7684\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u628a \u201c\u8d31\u4eba\u201d\u4e0e\u201c\u7231\u59bb\u201d\u653e\u5728\u4e00\u8d77\u8054\u60f3\u3002\u8c2d\u516c\u76f4\u60f3\u9053\uff1a\u201c\u4f60\u662f\u8981\u59bb\u5b50\u8fd8\u662f\u8981\u7236\u4eb2\uff1f\u4f60\u4e0d\u6740\u8fd9\u4e2a\u8d31 \u4eba\uff0c\u96be\u9053\u8981\u8ba9\u5979\u6740\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u8bf7\u7236\u4eb2\u606f\u6012\u3002\u201d\u8c2d\u9053\u6210\u9053\uff1a\u201c\u5ab3\u5987\u5df1\u6709\u8eab\u5b55\uff0c\u7eb5\u7136\u5979\u6709\u7f6a\uff0c\u5979\u809a\u5b50\u91cc\u7684\u5b69\u5b50\u603b\u662f\u54b1\u4eec \u8c2d\u5bb6\u7684\u9aa8\u8089\uff01\u201d \u3000\u3000\u8c2d\u516c\u76f4\u6c14\u5e73\u4e86\u4e00\u4e9b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8bdd\u4e5f\u8bf4\u5f97\u4e0d\u9519\uff0c\u867d\u7136\u4ed6\u7236\u5973\u8981\u8c0b\u6740\u6211\uff0c\u4f46\u5b69\u5b50\u662f\u65e0 \u8f9c\u7684\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u4f3c\u4e4e\u77e5\u9053\u7236\u4eb2\u7684\u5fc3\u601d\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e00\u5411\u662f\u6700\u8bb2\u9053\u7406\u7684\uff0c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff0c\u4e00 \u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u96ea\u59b9\u5979\u7239\u505a\u7684\u4e8b\u60c5\u5e94\u8be5\u4e0e\u5979\u65e0\u5173\uff0c\u8981\u662f\u5c06\u5979\u4e00\u5e76\u6740\u6389\uff0c\u5c82\u975e\u592a\u4e0d\u516c\u9053\uff1f\u201d\u8c2d \u516c\u76f4\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u7236\u5973\uff0c\u7236\u5973\u81ea\u662f\u540c\u8c0b\uff0c\u600e\u80fd\u8bf4\u4e0e\u5979\u65e0\u5173\uff1f\u201d \u3000\u3000\u59bb\u5b50\u5411\u4ed6\u6c42\u52a9\uff0c\u7236\u4eb2\u5374\u5728\u559d\u4ee4\u4ed6\u6740\u59bb\uff0c\u600e\u4e48\u529e\u5462\uff1f\u600e\u4e48\u529e\u5462\uff1f\u4ed6\u7edd\u5bf9\u76f8\u4fe1\u59bb\u5b50\u662f\u4e0d\u4f1a\u6740 \u4ed6\u7684\u7236\u4eb2\u7684\uff0c\u4f46\u5728\u7236\u4eb2\u76db\u6012\u4e4b\u4e0b\uff0c\u4ed6\u53c8\u600e\u80fd\u53bb\u5e2e\u59bb\u5b50\u62c9\u5f00\u7236\u4eb2\uff1f\u8ff7\u832b\u6df7\u4e71\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u7236\u4eb2 \u4e00\u7b11\u3002\u7b11\u58f0\u53e4\u602a\u4e4b\u6781\uff0c\u4f46\u6740\u6c14\u817e\u817e\u7684\u5c40\u9762\uff0c\u5374\u4f3c\u4e4e\u56e0\u6b64\u7f13\u548c\u4e00\u4e9b\u3002 \u3000\u3000\u8c2d\u9053\u6210\u4e0d\u61c2\u7236\u4eb2\u56e0\u4f55\u53d1\u7b11\uff0c\u53ea\u9053\u4e8b\u60c5\u6216\u6709\u8f6c\u673a\u3002\u6b63\u60f3\u4e0a\u524d\u529d\u67b6\uff0c\u9661\u7136\u95f4\u5c40\u9762\u53c8\u5927\u53d8\u4e86\u3002 \u3000\u3000\u539f\u6765\u5f20\u96ea\u6ce2\u56e0\u4e3a\u677f\u4e0d\u5f00\u516c\u516c\u7684\u624b\uff0c\u773c\u770b\u7239\u7239\u5c31\u8981\u7ed9\u516c\u516c\u6390\u6b7b\uff0c\u4eba\u6025\u667a\u751f\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u65b0 \u8fd1\u5b66\u4f1a\u7684\u4e00\u79cd\u70b9\u7a74\u624b\u6cd5\u3002 \u3000\u3000\u7239\u7239\u6559\u5979\u70b9\u7a74\u529f\u592b\uff0c\u5979\u6700\u4e0d\u613f\u610f\u5b66\u7684\u662f\u70b9\u6b7b\u7a74\u7684\u624b\u6cd5\uff0c\u800c\u6700\u559c\u6b22\u7ec3\u7684\u5219\u662f\u70b9\u9ebb\u7a74\u624b\u6cd5\u3002 \u7239\u7239\u867d\u7136\u7b11\u5979\u8fd9\u662f\u201c\u5987\u4eba\u4e4b\u4ec1\u201d\uff0c\u4f46\u4e5f\u540c\u610f\u5979\u5148\u70b9\u9ebb\u7a74\u3002\u56e0\u4e3a\u70b9\u6b7b\u7a74\u8981\u7528\u91cd\u624b\u6cd5\uff0c\u5979\u7684\u529f\u529b \u8fd8\u5acc\u4e0d\u591f\u3002\u8fd9\u534a\u4e2a\u6708\u6765\uff0c\u5979\u7ec3\u7684\u90fd\u662f\u70b9\u9ebb\u7a74\u7684\u624b\u6cd5\uff0c\u65e9\u5df2\u7ec3\u5f97\u5341\u5206\u7eaf\u719f\u4e86\u3002 \u3000\u3000\u5982\u4eca\u5979\u70b9\u7684\u5c31\u662f\u516c\u516c\u7684\u201c\u7b11\u8170\u7a74\u201d\uff0c\u7b11\u8170\u7a74\u662f\u4e0a\u534a\u8eab\u4e09\u5341\u516d\u4e2a\u9ebb\u7a74\u4e4b\u4e00\uff0c\u800c\u4e14\u662f\u6700\u6613\u89c1 \u6548\u7684\u9ebb\u7a74\u3002 \u3000\u3000\u5979\u4e00\u70b9\u70b9\u4e2a\u6b63\u7740\uff01 \u3000\u3000\u53ef\u60dc\u5979\u7684\u529f\u529b\u548c\u516c\u516c\u76f8\u5dee\u592a\u8fdc\uff0c\u70b9\u9ebb\u7a74\u4e0d\u5fc5\u7528\u91cd\u624b\u6cbb\uff0c\u4f46\u4e5f\u8fd8\u662f\u8981\u7528\u4e0a\u5185\u529b\u7684\uff0c\u5185\u529b\u4e0d \u5230\uff0c\u5c31\u5c01\u95ed\u4e0d\u4e86\u7a74\u9053\u3002\u8fd8\u6709\u88ab\u70b9\u7a74\u8005\u7684\u5185\u529f\u5018\u82e5\u6bd4\u70b9\u7a74\u8005\u7684\u5185\u529f\u9ad8\u51fa\u592a\u591a\uff0c\u70b9\u7a74\u4ea6\u96be\u751f\u6548\u3002 \u3000\u3000\u7ed3\u679c\u5979\u7684\u516c\u516c\u867d\u7136\u7b11\u51fa\u4e86\u58f0\uff0c\u5374\u6ca1\u9ebb\u8f6f\uff0c\u66f4\u4e0d\u7528\u8bf4\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u8c2d\u516c\u76f4\u7b11\u4e86\u51fa\u6765\uff0c\u4e5f\u4e0d\u514d\u6cc4\u4e86\u53e3\u6c14\uff0c\u6390\u4f4f\u5f20\u708e\u5589\u5499\u7684\u90a3\u4e00\u53cc\u624b\u4f7f\u4e0d\u4e0a\u52b2\u3002 \u3000\u3000\u4ed6\u607c\u6012\u5ab3\u5987\u7684\u9a9a\u6270\uff0c\u66f4\u607c\u6012\u513f\u5b50\u4e0d\u80af\u542c\u4ed6\u7684\u8bdd\u6740\u59bb\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u7d22\u6027\u5148\u653e\u677e\u5f20\u708e\uff0c\u6a2a\u8098 \u4e00\u649e\uff0c\u628a\u5ab3\u5987\u649e\u7ffb\u3002\u4ed6\u8df3\u8d77\u6765\u559d\u9053\uff1a\u201c\u6211\u5148\u6bd9\u4e86\u4f60\u8fd9\u4e2a\u8d31\u4eba\uff01\u201d\u4e00\u811a\u671d\u5ab3\u5987\u80f8\u53e3\u8e29\u4e0b\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u7a81\u7136\u6709\u4e00\u4e2a\u4eba\u6251\u5230\u5f20\u96ea\u6ce2\u8eab\u4e0a\u3002 \u3000\u3000\u662f\u4ed6\u7684\u513f\u5b50\u8c2d\u9053\u6210\uff01 \u3000\u3000\u513f\u5b50\u7528\u8eab\u4f53\u63a9\u62a4\u5ab3\u5987\uff0c\u8c2d\u516c\u76f4\u8fd9\u4e00\u811a\u5f53\u7136\u662f\u8e0f\u4e0d\u4e0b\u53bb\u4e86\u3002\u201c\u755c\u7272\uff0c\u4f60\u53ea\u77e5\u6709\u59bb\u5b50\uff0c\u773c\u775b \u91cc\u8fd8\u6709\u6211\u8fd9\u7236\u4eb2\u4e48\uff1f\u201d\u8c2d\u516c\u76f4\u6c14\u547c\u547c\u5730\u5927\u9a82\u3002 \u3000\u3000\u8c2d\u9053\u6210\u5728\u529d\u7236\u4eb2\u7684\u65f6\u5019\u3002\u5f20\u96ea\u6ce2\u4e5f\u5728\u95ee\u5979\u7684\u7239\u7239\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u708e\u5df1\u7ecf\u5750\u4e86\u8d77\u6765\uff0c\u989d\u4e0a\u7684\u6c57\u73e0\u597d\u50cf\u9ec4\u8c46\u7c92\u5927\u5c0f\u4e00\u9897\u9897\u6ef4\u4e0b\u6765\u3002\u4ed6\u6c89\u7740\u8138\u4e0d\u8bf4\u8bdd\uff0c\u53ea\u6307 \u4e00\u6307\u65ad\u81c2\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u7684\u5fc3\u4e2d\u75db\u5982\u8eab\u53d7\uff0c\u81ea\u5df1\u8d23\u602a\u81ea\u5df1\uff1a\u201c\u7239\u7239\u6050\u6015\u8fde\u8bf4\u8bdd\u7684\u6c14\u529b\u90fd\u6ca1\u6709\u4e86\uff0c\u6211\u600e\u80fd\u5728 \u8fd9\u4e2a\u65f6\u5019\u95ee\u4ed6\uff01\u201d\u5979\u6258\u8d77\u5f20\u708e\u7684\u624b\u81c2\uff0c\u786c\u751f\u751f\u7684\u5f80\u4e0a\u4e00\u63a5\uff0c\u624b\u6cd5\u867d\u7136\u4e0d\u5f88\u719f\u7ec3\uff0c\u5374\u662f\u628a\u8131\u81fc \u63a5\u597d\u4e86\u3002 \u3000\u3000\u5979\u89c1\u7239\u7239\u5982\u6b64\u53d7\u82e6\uff0c\u5728\u66ff\u4ed6\u63a5\u597d\u8131\u81fc\u4e4b\u540e\u3002\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u6c14\u6124\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u516c\uff0c\u4f60\u4e3a\u4ec0 \u4e48\u8981\u6740\u6211\u7684\u7239\u7239\uff1f\u201d \u3000\u3000\u8c2d\u516c\u76f4\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8d31\u4eba\u8fd8\u597d\u610f\u601d\u95ee\u6211\uff0c\u6210\u513f\uff0c\u4f60\u544a\u8bc9\u5979\uff1f\u201d\u4e0d\u77e5\u662f\u56e0\u4e3a\u6c14\u653b\u5fc3\u8fd8\u662f \u6bd2\u5df2\u53d1\u4f5c\uff0c\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e0d\u4f46\u58f0\u97f3\u98a4\u9707\uff0c\u9762\u8272\u4ea6\u5df2\u5927\u53d8\u3002 \u3000\u3000\u8c2d\u9053\u6210\u4f27\u7136\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u7684\u7239\u7239\u8981\u6740\u6211\u7684\u7239\u7239\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u82e5\u662f\u4ece\u5979\u7684\u516c\u516c\u53e3\u91cc\u8bf4\u51fa\u6765\uff0c\u5979\u8fd8\u4e0d\u80fd\u76f8\u4fe1\uff0c\u4ece\u5979\u7684\u4e08\u592b\u53e3\u91cc\u8bf4\u51fa\u6765\uff0c\u5979\u53ef\u662f\u4e0d \u80fd\u4e0d\u4fe1\u51e0\u5206\u4e86\u3002 \u3000\u3000\u5fc3\u5934\u5982\u53d7\u649e\u51fb\uff0c\u4e5f\u65e0\u6687\u987e\u8651\u90a3\u8bb8\u591a\u4e86\uff0c\u5979\u56de\u8fc7\u5934\u6765\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u8bf7\u4f60\u8001\u5b9e\u544a\u8bc9 \u6211\uff0c\u516c\u516c\u548c\u6210\u54e5\u8bf4\u7684\u662f\u771f\u7684\u5417\uff1f\u201d\u5f20\u708e\u8fd9\u624d\u5f20\u53e3\u8bf4\u9053\uff1a\u201c\u662f\u771f\u7684\uff01\u201d\u5f20\u96ea\u6ce2\u767b\u65f6\u5446\u4e86\uff01 \u3000\u3000\u5f20\u708e\u8f7b\u8f7b\u629a\u5979\u7684\u79c0\u53d1\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u96ea\u513f\uff0c\u6211\u6ca1\u5de5\u592b\u548c\u4f60\u7ec6\u8bf4\u4e86\uff0c\u4f46\u4f60\u4e00\u5b9a\u8981\u76f8\u4fe1\u6211\uff0c \u4f60\u76f8\u4fe1\u6211\u5417\uff1f\u201d\u8bf4\u5230\u6700\u540e\u4e00\u53e5\uff0c\u4ece\u8bed\u6c14\u4e2d\u4e5f\u53ef\u542c\u5f97\u51fa\u6765\uff0c\u4ed6\u5bf9\u5973\u513f\u7684\u4fe1\u4efb\u4ea6\u6709\u70b9\u52a8\u6447\u4e86\u3002\u5f20 \u96ea\u6ce2\u7684\u5fc3\u75db\u5982\u5200\u5272\uff0c\u4e0d\u9519\uff0c\u5979\u7684\u5fc3\u91cc\u662f\u6709\u8bb8\u591a\u7591\u56e2\uff0c\u4f46\u5979\u8fd8\u662f\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u54b1\u4eec\u7236\u5973\u662f\u4e00 \u6761\u5fc3\uff0c\u6211\u600e\u80fd\u4e0d\u76f8\u4fe1\u4f60\uff01\u201d \u3000\u3000\u5979\u662f\u542b\u6cea\u8bf4\u7684\u3002\u8bf4\u7684\u4e5f\u662f\u771f\u5fc3\u8bdd\u3002\u4ece\u5c0f\u5979\u5c31\u662f\u4e0e\u7239\u7239\u76f8\u4f9d\u4e3a\u547d\uff0c\u5979\u4fe1\u5f97\u8fc7\u7239\u7239\u7684\u4e3a\u4eba\uff0c \u7239\u7239\u662f\u7edd\u4e0d\u4f1a\u505a\u574f\u4e8b\u7684\u3002\u82e5\u7136\u4ed6\u662f\u505a\u51fa\u4e86\u81ea\u5df1\u4e0d\u80fd\u7406\u89e3\u7684\u4e8b\u60c5\uff0c\u90a3\u5c31\u4e00\u5b9a\u6709\u4ed6\u7684\u7406\u7531\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u7239\u7239\u8bf4\u9053\uff1a\u201c\u96ea\u513f\uff0c\u591a\u8c22\u4f60\u4fe1\u5f97\u8fc7\u6211\uff0c\u6211\u4e0d\u80fd\u591a\u8bf4\u4e86\uff0c\u6211\u53ea\u80fd\u544a \u8bc9\u4f60\uff0c\u4f60\u7684\u516c\u516c\u9a82\u6211\u662f\u5978\u4eba\uff0c\u8fd9\u662f\u5047\u7684\uff0c\u4ed6\u624d\u662f\u5978\u4eba\uff01 \u3000\u3000\u201d\u6f6d\u516c\u76f4\u5438\u4e00\u53e3\u6c14\uff0c\u652f\u6491\u81ea\u5df1\uff0c\u5636\u54d1\u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6210\u513f\uff0c\u4f60\u542c\u89c1\u6ca1\u6709\uff0c\u8fd9\u8001\u8d3c\u8981\u6bd2\u6b7b \u54b1\u4eec\u7236\u5b50\uff0c\u4ed6\u8fd8\u6562\u8bf4\u6211\u662f\u5978\u4eba\uff01\u4f60\u8fd8\u4e0d\u8d76\u5feb\u8fc7\u53bb\u628a\u4ed6\u4eec\u7236\u5973\u6740\u6389\uff01\u4f60\u4e0d\u542c\u6211\u7684\u8bdd\uff0c\u4f60\u5c31\u4e0d\u662f \u6211\u7684\u513f\u5b50\uff01\u201d\u539f\u6765\u4ed6\u4e2d\u7684\u6bd2\u5df2\u7ecf\u53d1\u4f5c\uff0c\u53ea\u662f\u4ed7\u7740\u5185\u529f\u6df1\u539a\uff0c\u52c9\u5f3a\u8fd8\u53ef\u4ee5\u652f\u6301\u800c\u4e14\uff0c\u4ed6\u5df2\u662f\u65e0 \u529b\u6740\u4eba\u4e86\u3002\u8c2d\u9053\u6210\u5927\u5403\u4e00\u60ca\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u90fd\u6740\u6389\uff1f\u7239\u7239\uff0c\u6211\u4e0d\u662f\u5df2\u7ecf\u544a\u8bc9\u4e86\u4f60\u5417\uff1f \u5ab3\u5987\uff0c\u5979\uff0c\u5979\uff0c\u5979\u6709\u2026\u201d \u3000\u3000\u8c2d\u516c\u76f4\u6253\u65ad\u513f\u5b50\u7684\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6ca1\u542c\u89c1\u4f60\u7684\u5ab3\u5987\u521a\u624d\u662f\u600e\u6837\u8bf4\u7684\u5417\uff0c\u4ed6\u4eec\u7236\u5973\u4e00\u6761 \u5fc3\uff01\u65a9\u8349\u5fc5\u987b\u9664\u6839\uff0c\u5979\u809a\u5b50\u91cc\u7684\u5b69\u5b50\u54b1\u4eec\u53ea\u80fd\u4e0d\u8981\u4e86\uff01\u201d \u3000\u3000\u8c2d\u9053\u6210\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u4eec\u5e76\u4e0d\u662f\u4eb2\u751f\u7236\u5973\uff01\u201d \u3000\u3000\u4e3a\u4e86\u633d\u6551\u59bb\u5b50\u7684\u6027\u547d\uff0c\u4ed6\u65e0\u6687\u8003\u8651\uff0c\u51b2\u53e3\u800c\u51fa\uff0c\u8bf4\u51fa\u81ea\u5df1\u5fc3\u5e95\u7684\u6000\u7591\u3002\u4ed6\u672c\u6765\u4e0d\u77e5\u9053\u81ea \u5df1\u7684\u6000\u7591\u662f\u5426\u662f\u4e8b\u5b9e\uff0c\u4f46\u5982\u4eca\u53ea\u80fd\u628a\u5b83\u5f53\u4f5c\u4e8b\u5b9e\u4e86\u3001\u8c2d\u516c\u76f4\u5446\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u4e00 \u8bf4\uff0c\u6211\u4e5f\u60f3\u8d77\u6765\u4e86\u3002\u4e0d\u9519\uff0c\u662f\u6709\u8bb8\u591a\u8ff9\u8c61\uff0c\u503c\u5f97\u4ee4\u4eba\u6000\u7591\u4ed6\u4eec\u5e76\u4e0d\u662f\u4eb2\u751f\u7236\u5973\uff01\u4f60\u662f\u51e0\u65f6\u77e5 \u9053\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u544a\u8bc9\u6211\uff1f\u201d\u5f20\u96ea\u6ce2\u5ffd\u7136\u542c\u89c1\u4e08\u592b\u63ed\u7a7f\u5979\u7684\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u5979\u4e5f\u4e0d\u77e5\u4e08\u592b\u7a76\u7adf\u77e5 \u9053\u591a\u5c11\uff0c\u4e0d\u7981\u4e5f\u662f\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u8c2d\u9053\u6210\u4e00\u770b\u59bb\u5b50\u8fd9\u4e2a\u795e\u60c5\uff0c\u77e5\u9053\u6000\u7591\u5df2\u662f\u4e8b\u5b9e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u521a\u521a\u77e5\u9053\u7684\u3002\u4ed6\u3001\u4ed6\u8981 \u6c42\u96ea\u59b9\u4fe1\u4efb\u4ed6\uff0c\u4ed6\u5411\u96ea\u59b9\u9053\u8c22\uff0c\u82e5\u662f\u751f\u8eab\u4e4b\u7236\uff0c\u600e\u4f1a\u7528\u8fd9\u79cd\uff24\u543b\u548c\u4eb2\u751f\u5973\u513f\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u8c2d\u516c\u76f4\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u5229\u7528\u517b\u5973\u9a97\u5a5a\uff0c\u90a3\u66f4\u662f\u5904\u5fc3\u79ef\u8651\u8981\u5bb3\u54b1\u4eec\u4e86\u3002 \u3000\u3000\u597d\u5427\uff0c\u65e2\u7136\u4f60\u7684\u5ab3\u5987\u4e0d\u662f\u4ed6\u7684\u4eb2\u751f\u5973\u513f\uff0c\u90a3\u5c31\u9976\u4ed6\u4e00\u547d\u5427\u3002\u4f60\u8fc7\u53bb\u628a\u8001\u8d3c\u6740\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7ad9\u7acb\u8d77\u6765\uff0c\u6321\u5728\u5f20\u708e\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4e0d\u662f\u6211\u7684\u751f\u8eab\u4e4b\u7236\uff0c\u4f46\u4ed6\u5c06\u6211\u629a\u517b \u6210\u4eba\u3002\u6211\u521a\u4f1a\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5c31\u4e00\u76f4\u662f\u628a\u4ed6\u5f53\u4f5c\u7236\u4eb2\u7684\u4e86\u3002\u4ed6\u5bf9\u6211\u7684\u7231\u62a4\u53ef\u8bf4\u662f\u65e0\u5fae\u4e0d\u81f3\uff0c\u517b \u7236\u4e4b\u6069\uff0c\u66f4\u80dc\u751f\u7236\uff0c\u4f60\u8981\u6740\u4ed6\uff0c\u8bf7\u5148\u6740\u6211\uff01\u201d \u3000\u3000\u8981\u8c2d\u9053\u6210\u624b\u6740\u7231\u59bb\uff0c\u4ed6\u600e\u80fd\u4e0b\u5f97\u8fd9\u4e2a\u6bd2\u624b\uff1f\u4ed6\u4e0b\u4e0d\u4e86\u6bd2\u624b\uff0c\u4ed6\u7236\u4eb2\u4e2d\u7684\u6bd2\u5374\u53d1\u4f5c\u4e86\u3002 \u3000\u3000\u8c2d\u516c\u76f4\u5012\u5728\u5730\u4e0a\uff0c\u9762\u8272\u6709\u5982\u4e00\u5f20\u767d\u7eb8\uff0c\u54ac\u7740\u7259\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e0d\u80fd\u4eb2\u624b\u62a5\u4ec7\uff0c\u6210\u513f\uff0c\u4f60\u662f \u6211\u7684\u513f\u5b50\u3001\u6211\u8981\u4eb2\u773c\u770b\u89c1\u4ec7\u4eba\u6b7b\u5728\u6211\u7684\u9762\u524d\uff0c\u5426\u5219\u6211\u6b7b\u4e0d\u7791\u76ee\uff01\u201d \u3000\u3000\u7236\u4ec7\u4e0d\u62a5\uff0c\u4f55\u4ee5\u4e3a\u4eba\uff1f\u8c2d\u9053\u6210\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u96ea\u59b9\uff0c\u8bf7\u4f60\u8ba9\u5f00\uff01\u201d\u5f20\u96ea\u6ce2\u5ffd\u5730\u60f3 \u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4f60\u522b\u9c81\u83bd\u4ece\u4e8b\uff0c\u4f60\u7684\u7239\u7239\u4e0d\u4e00\u5b9a\u4f1a\u6b7b\u7684\u3002\u201d\u8f6c\u8eab\u62b1\u7740\u5f20\u708e\u3002\u53eb\u9053\uff1a \u201c\u7239\u7239\uff0c\u8bf7\u4f60\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u628a\u89e3\u836f\u62ff\u51fa\u6765\u5427\u3002\u4e0d\u7ba1\u8c01\u662f\u8c01\u975e\uff0c\u5148\u6551\u6d3b\u4e86\u516c\u516c\u518d\u8bf4\uff01\u201d \u3000\u3000\u5f20\u708e\u559d\u9053\uff1a\u201c\u653e\u5f00\u6211\uff0c\u8ba9\u4ed6\u6765\u6740\u6211\u597d\u4e86\uff01\u83ab\u8bf4\u6211\u6ca1\u6709\u89e3\u836f\uff0c\u6709\u89e3\u836f\u6211\u4e5f\u4e0d\u4f1a\u7ed9\u4ed6\u3002\u6211\u5b81 \u613f\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\uff01\u201d \u3000\u3000\u8c2d\u516c\u76f4\u4e5f\u5728\u559d\u9053\uff1a\u201c\u6210\u513f\uff0c\u4e0d\u8bb8\u4f60\u6c42\u89e3\u836f\u3002\u6211\u4e5f\u5b81\u613f\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\uff0c\u4f46\u8981\u4ed6\u6b7b\u5728\u6211\u7684\u524d \u5934\uff01\u201d \u3000\u3000\u8c2d\u9053\u6210\u864e\u76ee\u8574\u6cea\uff0c\u5530\u7684\u62d4\u51fa\u4f69\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u53ef\u60f3\uff0c\u53ea\u6709\u5bf9\u4e0d \u8d77\u4f60\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201d\u4e14\u6162\uff01\u201c\u62b1\u7740\u5f20\u708e\u7684\u817f\uff0c\u8dea\u5728\u4ed6\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u77e5\u9053\u4f60\u6709\u89e3\u836f \u7684\uff0c\u8bf7\u4f60\u62ff\u51fa\u6765\u5427\uff01\u4f60\u8981\u77e5\u9053\uff0c\u4f60\u82e5\u6b7b\u4e86\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ddf\u4f60\u6b7b\u7684\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53c8\u671b\u7740\u4e08\u592b\u8bf4\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4e0e\u5176\u4e24\u4e2a\u4eba\u4e00\u8d77\u6b7b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u90fd\u6c42\u751f\uff1f\u6211\u8981\u7239\u7239\u4ea4\u51fa \u89e3\u836f\uff0c\u8bf7\u4f60\u4ee3\u6c42\u516c\u516c\u9976\u6211\u7239\u7239\u4e00\u547d\uff01\u201d\u5f20\u708e\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u600e\u53ef\u5411\u4ec7\u4eba\u4e5e\u601c\uff1f\u201d\u5f20\u96ea\u6ce2 \u9053\uff1a\u201c\u7239\u7239\u3002\u6211\u77e5\u9053\u662f\u59d4\u5c48\u4e86\u4f60\u3002\u4f46\u4f60\u66ff\u6211\u60f3\u60f3\uff0c\u4f60\u4e0d\u662f\u6700\u7231\u6211\u7684\u5417\uff0c\u4f60\u5fcd\u5fc3\u8ba9\u6211\u8ddf\u4f60\u4e00\u8d77 \u53bb\u6b7b\uff1f\u6211\u6b7b\u4e86\uff0c\u53c8\u6709\u8c01\u7167\u987e\u6211\u7684\u5b69\u5b50\uff1f\u6211\u809a\u5b50\u91cc\u8fd8\u6709\u4e00\u4e2a\u5462\uff0c\u6211\u8bf4\u597d\u4e86\u8fd9\u4e2a\u5b69\u5b50\u5c06\u6765\u7ed9 \u4f60\uff01\u201d \u3000\u3000\u5f20\u708e\u53f9\u4e86\u53e3\u6c14\uff0c\u610f\u601d\u597d\u50cf\u6709\u70b9\u6d3b\u52a8\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4f60\u5462\uff0c\u4f60\u80af\u7b54\u5e94\u6211\u5417\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u4e0d\u6740\u4f60\u7684\u7239\u7239\uff0c\u53ea\u8981\u4ed6\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u5f20\u708e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u4f60\u6740\u6211\uff0c\u6211\u662f\u4e3a\u4e86\u96ea\u513f\uff01\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u521a\u624d\u662f\u9a97 \u4f60\u7684\uff0c\u6211\u8eab\u4e0a\u662f\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u8c2d\u516c\u76f4\u5636\u54d1\u7740\u58f0\u97f3\u559d\u9053\uff1a\u201c\u6210\u513f\uff0c\u522b\u76f8\u4fe1\u4ed6\u4eec\u7684\u82b1\u8a00\u5de7\u8bed\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u8d76\u5feb\u628a\u4ed6\u4eec\u6740 \u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4e00\u9897\u5fc3\u51e0\u4e4e\u8981\u4ece\u53e3\u8154\u91cc\u8df3\u51fa\u6765\uff0c\u5979\u7528\u7792\u8138\u51c4\u82e6\u7684\u795e\u60c5\u671b\u7740\u4e08\u592b\uff0c\u597d\u50cf\u5728\u8bf4\uff1a\u201c\u6210 \u54e5\uff0c\u4f60\u90fd\u4e0d\u76f8\u4fe1\u6211\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u9053\u6210\u8fdf\u7591\u7247\u523b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u96ea\u59b9\u662f\u7edd\u4e0d\u4f1a\u6b3a\u9a97\u6211\u7684\uff0c\u5979\u7684\u7239\u7239\u4e3a\u4e86\u5979\u7f18\u5e2e\u624d\u80af\u4ea4\u51fa\u89e3 \u836f\uff0c\u76f8\u4fe1\u4e5f\u4e0d\u4f1a\u662f\u5047\u7684\u3002\u96ea\u59b9\u662f\u4ed6\u6700\u4eb2\u7231\u7684\u4eba\uff0c\u96be\u9053\u4ed6\u8fd8\u80fd\u9a97\u96ea\u59b9\u4e0d\u6210\uff1f\u201d\u4ed6\u8fdf\u7591\u7247\u523b\uff0c\u7ec8 \u4e8e\u8d70\u4e0a\u524d\u53bb\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u4f55\u51b5\u5f20\u8c2d\u4e24\u5bb6\u672c\u6765\u5c31\u662f\u4eb2\u5bb6\u3002\u7239\u7239\uff0c\u8bf7\u4f60\u770b\u5728 \u5b59\u513f\u4efd\u4e0a\uff0c\u63a5\u53d7\u4ed6\u7684\u89e3\u836f\uff0c\u4e24\u5bb6\u548c\u89e3\u4e86\u5427\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u89c1\u7239\u7239\u5df2\u7ecf\u62ff\u51fa\u89e3\u836f\uff0c\u4e08\u592b\u5df2\u7ecf\u4e0a\u53bb\u63a5\u53d7\u89e3\u836f\u4e86\uff0c\u5979\u7ef7\u7d27\u7684\u5fc3\u5f26\u65b9\u59cb\u7a0d\u5fae\u653e\u677e\uff0c \u8138\u4e0a\u4e5f\u5f00\u59cb\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u591a\u8c22\u4f60\u5bf9\u6211\u8fd9\u6837\u597d\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u6302\u5728\u5979\u8138\u4e0a\u7684\u7b11\u5bb9\u7a81\u7136\u201c\u51dd\u7ed3\u201d\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u53ea\u89c1\u8c2d\u9053\u6210\u7684\u8eab\u5b50\u6643\u4e86\u51e0\u6643\uff0c\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002\u539f\u6765\u5f20\u708e\u662f\u8d81 \u7740\u5973\u5a7f\u672a\u63a5\u89e3\u836f\u7684\u65f6\u5019\uff0c\u7a81\u7136\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff01 \u3000\u3000\u5728\u5f20\u708e\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u800c\u4e14\u5de6\u81c2\u53d7\u4f24\u4e4b\u540e\uff0c\u8c2d\u9053\u6210\u7684\u6b66\u529f\u672c\u6765\u53ef\u4ee5\u80dc\u8fc7\u5cb3\u7236\u7684\u3002\u4f46\u4ed6\u600e \u60f3\u5f97\u5230\u5cb3\u7236\u7adf\u4f1a\u9a97\u4ed6\uff0c\u5728\u53e3\u4e2d\u8bf4\u8981\u548c\u89e3\u7684\u540c\u65f6\u7a81\u7136\u5411\u4ed6\u5077\u88ad\uff1f\u4ed6\u88ab\u70b9\u4e2d\u7684\u662f\u9ebb\u7a74\uff0c\u4eba\u5012\u672a\u66fe \u6655\u8ff7\uff0c\u4f46\u4e5f\u6c14\u5f97\u51e0\u4e4e\u8981\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u5f20\u96ea\u6ce2\u66f4\u52a0\u610f\u60f3\u4e0d\u5230\uff0c\u5979\u60ca\u5f97\u5446\u4e86\uff01 \u3000\u3000\u8c2d\u516c\u76f4\u53f9\u6c14\u9053\uff1a\u201c\u6210\u513f\uff0c\u4f60\u770b\u6e05\u695a\u4e86\u4f60\u8fd9\u4f4d\u597d\u4e08\u4eba\u7684\u771f\u9762\u76ee\u4e86\u5427\uff1f\u5509\uff0c\u4f60\u8fd9\u4e2a\u5f53\u4e5f\u672a\u514d \u4e0a\u5f97\u592a\u5927\u4e86\uff01\u201d \u3000\u3000\u8c2d\u9053\u6210\u5636\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u540e\u6094\u6ca1\u542c\u4f60\u7684\u8bdd\uff01\u5f20\u708e\uff0c\u4f60\u600e\u80fd\u7528\u8fd9\u6837\u65e0\u803b\u7684\u624b\u6bb5\u6765\u5bf9\u4ed8 \u6211\uff0c\u4f60\uff0c\u4f60\u8fd9\u5351\u9119\u7684\u8001\u3001\u8001\u2026\u2026\u201d\u7a81\u7136\u4ed6\u63a5\u89e6\u5230\u59bb\u5b50\u51c4\u82e6\u4e4b\u6781\u7684\u76ee\u5149\uff0c\u201c\u8001\u8d3c\u201d\u4e8c\u5b57\u7ec8\u4e8e\u8fd8 \u662f\u6ca1\u6709\u9a82\u51fa\u65e5\u6765\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4ee5\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c\u4f46\u4ee4\u4ed6\u7a0d\u611f\u5b89\u6170\u7684\u662f\uff0c\u4ed6\u77e5\u9053\u4ed6\u7684\u59bb\u5b50\u5e76\u4e0d\u662f\u6210\u5fc3\u9a97\u4ed6\u7684\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5446\u4e86\u7247\u523b\uff0c\u7a81\u7136\u53d1\u4e86\u75af\u4f3c\u7684\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u4e0d\u76f8\u4fe1\u4f60\u662f\u4e2a\u5351\u9119\u5c0f\u4eba\uff0c\u4f46\u4f60\u4e3a\u4ec0 \u4e48\u8981\u8fd9\u6837\u505a\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u4f60\uff0c\u4f60\uff0c\u4f60\u9a97\u4e86\u6210\u54e5\uff0c\u4e5f\u9a97\u4e86\u6211\u2026\u201d \u3000\u3000\u5f20\u708e\u82e6\u7b11\u9053\uff1a\u201c\u96ea\u513f\uff0c\u539f\u8c05\u6211\u9a97\u4f60\u3002\u4e8b\u51fa\u975e\u5e38\uff0c\u65a9\u8349\u5fc5\u987b\u9664\u6839\uff0c\u6211\u4e0d\u8fd9\u6837\u505a\u4e0d\u884c\uff01\u201d \u3000\u3000\u8bf4\u5230\u201c\u4e0d\u884c\u201d\u4e8c\u5b57\uff0c\u4ed6\u7684\u8138\u4e0a\u5df2\u662f\u5e03\u6ee1\u6740\u6c14\uff0c\u8fc8\u6b65\u5411\u524d\uff0c\u4e00\u638c\u5411\u8c2d\u9053\u6210\u7684\u5929\u7075\u76d6\u51fb\u4e0b\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4e00\u58f0\u5c16\u53eb\uff0c\u51b2\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u5e78\u597d\u5f20\u708e\u53d7\u4f24\u4e4b\u540e\uff0c\u884c\u52a8\u4e0d\u53ca\u5e73\u65f6\u5feb\u6377\uff0c\u5f20\u96ea\u6ce2\u65cb\u98ce\u4e5f\u4f3c\u5730\u6251\u8fc7\u6765\uff0c\u6070\u597d\u5728\u4ed6\u7684\u624b\u638c\u5c06 \u8981\u51fb\u843d\u7684\u65f6\u5019\uff0c\u6251\u5230\u4e86\u4e08\u592b\u8eab\u4e0a\uff0c\u53cc\u81c2\u7d27\u7d27\u62b1\u7740\u4e08\u592b\u3002 \u3000\u3000\u201c\u7239\u7239\uff0c\u4f60\u8981\u6740\u4ed6\uff0c\u8bf7\u5148\u6740\u6211\uff01\u201d\u5f20\u96ea\u6ce2\u53eb\u9053\u3002\u5f20\u708e\u4e00\u58f0\u957f\u53f9\uff0c\u624b\u81c2\u8f6f\u8f6f\u5730\u5782\u4e0b\u6765\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u6c14\u82e6\u4e4b\u6781\uff0c\u706b\u7ea2\u7684\u773c\u775b\u76ef\u7740\u5f20\u708e\uff0c\u597d\u50cf\u5f20\u708e\u662f\u4e00\u4e2a\u5979\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u7684\u964c\u751f\u4eba\u4e00 \u6837\uff0c\u53eb\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u662f\u4f60\u7684\u5973\u513f\uff0c\u5982\u4eca\u4f60\u4e5f\u4e0d\u628a\u6211\u5f53\u4f5c\u5973\u513f\u4e86\uff1f\u4f60\u6740\u4e86\u6211\u5427\uff01\u53cd\u6b63\u6211\u4e5f\u4e0d \u60f3\u6d3b\u4e86\uff01\u201d \u3000\u3000\u5f20\u708e\u5446\u82e5\u6728\u9e21\uff0c\u534a\u664c\uff0c\u7a81\u7136\u53eb\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u600e\u53ef\u8bf4\u8fd9\u6837\u7684\u8bdd\uff01\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u672c\u6765\u4e5f\u662f \u6709\u513f\u5973\u7684\uff0c\u4e3a\u4e86\u4f60\uff0c\u6211\u5b81\u613f\u820d\u5f03\u4ed6\u4eec\uff0c\u4f60\u5374\u8bf4\u6211\u4e0d\u628a\u4f60\u5f53\u4f5c\u4eb2\u751f\u5973\u513f\uff01\u201d\u5f20\u96ea\u6ce2\u7684\u5fc3\u8f6f\u4e86\u4e0b \u6765\uff0c\u6d41\u7740\u773c\u6cea\u53eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5bf9\u6211\u597d\uff0c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u6211\u7684\u4e08\u592b\uff1f\u592b\u59bb\u5982\u540c\u4e00\u4f53\uff0c\u4f60\u6740\u4e86 \u4ed6\uff0c\u6211\u8fd8\u80fd\u591f\u6d3b\u5728\u4e16\u4e0a\u53eb\u4f60\u7239\u7239\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u53f9\u53e3\u6c14\u9053\uff1a\u201d\u4e0d\u662f\u6211\u72e0\u5fc3\u8981\u62c6\u6563\u4f60\u4eec\u592b\u59bb\uff0c\u6162\u6162\u6211\u4f1a\u544a\u8bc9\u4f60\u7684\u3002 \u3000\u3000\u597d\u5427\uff0c\u6211\u7b54\u5e94\u4f60\u4e0d\u6740\u4ed6\uff0c\u4f60\u53bb\u628a\u51b2\u513f\u62b1\u51fa\u6765\uff0c\u968f\u6211\u4e0b\u5c71\u5427\u3002\u201d\u5f20\u96ea\u6ce2\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c \u6211\u4e0d\u80fd\u8fd9\u6837\u5c31\u8d70\uff01\u201d\u5f20\u708e\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u96ea\u513f\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u4e00\u4e0b\u4e86\u5c71\uff0c\u6211\u5c31\u539f\u539f\u672c \u672c\u5730\u8bf4\u7ed9\u4f60\u77e5\u9053\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u90a3\u65f6\u5df2\u7ecf\u8fdf\u4e86\uff0c\u5df2\u7ecf\u8fdf\u4e86\uff01\u6211\u4e0d\u80fd\u8d70\uff0c\u6211\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u4ec0\u4e48\u8fdf\u4e86\uff01\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u516c\u516c\u4e2d\u4e86\u6bd2\uff0c\u6210\u54e5\u7684\u7a74\u9053\u4e5f\u672a\u89e3\u5f00\u3002\u6211\u4e00\u8d70\uff0c\u8c01 \u7167\u987e\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5f20\u708e\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u53eb\u8fd9\u8001\u8d3c\u505a\u516c\u516c\uff1f\u521a\u624d\u4f60\u5df2\u7ecf\u770b\u89c1\u4e86\uff0c\u4f60\u5e94\u8be5\u660e\u767d\uff0c\u82e5\u4e0d\u662f\u6211\u6740\u4e86 \u4ed6\uff0c\u5c31\u4e00\u5b9a\u662f\u4ed6\u6740\u4e86\u6211\uff01\u4f60\u4ee5\u4e3a\u6211\u8fd8\u53ef\u4ee5\u7ed9\u4ed6\u89e3\u836f\uff1f\u201d\u5f20\u96ea\u6ce2\u6cea\u5982\u96e8\u4e0b\uff0c\u4ecd\u7136\u662f\u7d27\u7d27\u62b1\u7740\u4e08 \u592b\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u548c\u516c\u516c\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u8fd8\u662f\u8981\u53eb\u4ed6\u516c\u516c\uff0c\u4e0d\u77e5\u9053\u4f60\u4eec\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c \u4f60\u4e0d\u80af\u7ed9\u4ed6\u89e3\u836f\uff0c\u6211\u4e5f\u4e0d\u6562\u5f3a\u6c42\u3002\u4f46\u6211\u7684\u4e08\u5c71\uff0c\u5c31\u6709\u997f\u72fc\u628a\u4ed6\u5403\u6389\u4e86\uff01\u4f60\u4e0d\u8bb8\u6211\u7406\u4ed6\uff0c\u8fd9\u4e0d \u7b49\u4e8e\u8981\u4ed6\u81ea\u751f\u81ea\u81ea\u706d\u5417\uff1f\u201d\u5f20\u708e\u7684\u786e\u662f\u60f3\u8981\u5973\u5a7f\u81ea\u751f\u81ea\u706d\u7684\u3002\u4ed6\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u96ea \u513f\uff0c\u6211\u8001\u5b9e\u548c\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u73b0\u5728\u5df2\u662f\u6253\u4e0d\u8fc7\u4f60\u7684\u4e08\u592b\u4e86\u3002\u5047\u5982\u6211\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u90a3\u4e0d\u662f\u7b49\u4e8e \u628a\u6027\u547d\u4ea4\u5230\u4ed6\u624b\u4e0a\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u8981\u903c\u627e\u3002\u4f60\u8981\u8d70\uff0c\u4f60\u81ea\u5df1\u8d70\uff01\u201d\u5f20\u708e\u9053\uff1a\u201c\u4f60\u7559\u5728\u8fd9\u91cc\u4e5f\u5e2e\u4e0d \u4e0a\u4ed6\u4eec\u7684\u5fd9\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\uff0c\u6211\u4e0d\u7ba1\uff01\u6211\u53ea\u77e5\u9053\u4e0e\u6210\u54e5\u6b7b\u5219\u540c\u6b7b\uff0c\u751f\u5219\u540c\u751f\uff01\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u51b2\u513f\u5462\uff1f\u4f60\u4e5f\u4e0d\u7ba1\u4e86\u5417\uff1f\u4f60\u8981\u77e5\u9053\u6211\u5df2\u7ecf\u5e74\u8001\u4e86\uff0c\u6211\u4e0d\u80fd\u50cf\u7167\u987e\u4f60\u4e00\u6837\uff0c\u628a \u51b2\u513f\u629a\u517b\u6210\u4eba\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fc3\u5982\u5200\u5272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u72e0\u5fc3\u4e0d\u7406\u6211\u7684\u6b7b\u6d3b\uff0c\u6211\u4e5f\u53ea\u80fd\u72e0\u5fc3\u4e0d\u7406\u51b2\u513f\u7684\u6b7b\u6d3b\u4e86\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u5ffd\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u4e0d\u662f\u4f60\u7684\u72e0\u5fc3\uff0c\u8fd9\u662f\u522b\u4eba\u7684\u72e0\u5fc3\u5bb3\u4e86\u4f60\u4e5f\u5bb3\u4e86\u4f60\u7684\u513f\u5b50\u7684\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6210\u54e5\uff0c\u4ed6\u597d\u6b79\u4e5f\u662f\u5bf9\u6211\u6069\u91cd\u5982\u5c71\u7684\u7239\u7239\uff0c\u4f60\u4e0d\u8981\u8fd9\u6837\u8bf4\u4ed6\uff01\u201d \u3000\u3000\u5f20\u708e\u5750\u4e0b\uff0c\u72b6\u82e5\u6728\u9e21\u3002\u8981\u77e5\u9053\u4ed6\u6240\u505a\u7684\u90fd\u662f\u4e3a\u4e86\u5f20\u96ea\u6ce2\u7684\uff0c\u5f20\u96ea\u6ce2\u4e0d\u80af\u8d70\uff0c\u4ed6\u53c8\u600e\u80fd\u8d70 \u5f97\u4e86\uff1f\u6f6d\u516c\u76f4\u8bb8\u4e45\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6b64\u65f6\u5ffd\u5730\u5f00\u53e3\u9053\uff1a\u201c\u5f20\u708e\uff0c\u6211\u4e2d\u6bd2\u5df2\u6df1\uff0c\u8fd9\u662f\u4f60\u4e0b\u7684\u6bd2\u3002\u6bd2\u6027 \u5982\u4f55\uff0c\u4f60\u5f53\u7136\u6bd4\u6211\u66f4\u6e05\u695a\uff0c\u6211\u662f\u7edd\u8ba1\u6d3b\u4e0d\u8fc7\u4eca\u665a\u7684\u4e86\u3002\u4f46\u6211\u60f3\u77e5\u9053\u4e00\u6869\u4e8b\u60c5\uff0c\u5426\u5219\u6211\u6b7b\u4e0d\u660e \u76ee\uff01\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u4f60\u8981\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c2d\u516c\u76f4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u56e0\u4f55\u8981\u5904\u5fc3\u79ef\u8651\uff0c\u8c0b\u5bb3\u6211\u4eec\u7236\u5b50\uff1f\u201d \u3000\u3000\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u6050\u6015\u4f60\u65e9\u5df2\u77e5\u9053\u4e86\u5427\uff0c\u8fd8\u4f55\u987b\u95ee\u6211\uff1f\u8bf4\u5230\u5904\u5fc3\u79ef\u8651\uff0c\u66f4\u7b11 \u8bdd\u4e86\uff0c\u8fd9\u53e5\u8bdd\u5e94\u8be5\u7531\u6211\u95ee\u4f60\u624d\u5bf9\uff01\u201d \u3000\u3000\u8c2d\u516c\u76f4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4e5f\u662f\u50cf\u4f60\u4e00\u6837\uff0c\u5341\u51e0\u5e74\u6765\u90fd\u662f\u6234\u7740\u5047\u9762\u5177\u9a97\u4eba\uff01 \u3000\u3000\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u9a97\u6211\uff0c\u4f60\u809a\u91cc\u660e\u767d\u3002\u201d \u3000\u3000\u8c2d\u9053\u6210\u9a82\u9053\uff1a\u201c\u51e1\u4e8b\u603b\u5f97\u8bb2\u4e2a\u9053\u7406\uff0c\u6446\u5728\u773c\u524d\u7684\u4e8b\u5b9e\uff0c\u662f\u4f60\u4e0b\u6bd2\u5bb3\u6211\u7684\u7239\u7239\uff0c\u4e0d\u662f\u6211\u7239 \u7239\u4e0b\u6bd2\u6b7b\u4f60\uff01\u4f60\u5047\u88c5\u4e0d\u61a7\u6b66\u529f\uff0c\u8fd8\u8981\u96ea\u513f\u5e2e\u4f60\u9a97\u6211\uff01\u8fd9\u8fd8\u4e0d\u662f\u5904\u5fc3\u79ef\u8651\u8981\u5bb3\u6211\u4eec\u7236\u5b50\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u4e5f\u4e0d\u61c2\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff0c\u6211\u662f\u51b3\u610f\u4e0d\u8d70\u7684\u4e86\uff0c\u4f60\u53ef\u4ee5\u73b0\u5728\u544a\u8bc9 \u6211\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8981\u662f\u4e0d\u544a\u8bc9\u5979\uff0c\u5979\u662f\u4e0d\u4f1a\u8ddf\u6211\u8d70\u7684\u3002\u201d \u3000\u3000\u4ed6\u6b63\u5728\u8e0c\u8e87\uff0c\u8c2d\u516c\u76f4\u81ea\u5df1\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u6211\u662f\u5feb\u6b7b\u7684\u4eba\u3002\u5373\u4f7f\u4f60\u7684\u79d8\u5bc6\u7ed9\u6211\u77e5\u9053\uff0c\u4f60\u4e5f\u4e0d \u5fc5\u5bb3\u6015\u6211\u62a5\u590d\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u591f\u8bf4\u51fa\u4e2a\u9053\u7406\u6765\uff0c\u5426\u5219\u8bf7\u539f\u8c05\u6211\u4e0d\u80fd\u8ba4\u4f60\u505a\u7239 \u7239\uff01\u201d \u3000\u3000\u5f20\u708e\u4e00\u54ac\u7259\u6839\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u90fd\u8981\u6211\u8bf4\uff0c\u6211\u5c31\u8bf4\u5427\uff01\u201d \u3000\u3000\u65e0\u8272\u5df2\u7ecf\u9ed1\u4e86\uff0c\u4ed6\u70b9\u8d77\u6cb9\u706f\uff0c\u628a\u6905\u5b50\u79fb\u5230\u8c2d\u516c\u76f4\u8eab\u8fb9\uff0c\u671b\u7740\u4ed6\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u53e5\u8bdd\u6211\u60f3\u8bf4 \u7684\uff0c\u4f60\u662f\u4e2a\u4f2a\u541b\u5b50\uff01\u54fc\uff0c\u54fc\uff0c\u4f60\u53e3\u91cc\u5e38\u8bf4\u51e1\u4e8b\u8981\u8bb2\u9053\u7406\uff0c\u8981\u6c42\u516c\u9053\uff0c\u8fd9\u90fd\u662f\u9a97\u4eba\u7684\u8bdd\uff01\u201d \u3000\u3000\u8c2d\u516c\u76f4\u5012\u5f88\u51b7\u9759\uff0c\u5e76\u6ca1\u6709\u52a8\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8bf7\u4f60\u62ff\u51fa\u4e8b\u5b9e\uff0c\u522b\u9a82\u4eba\uff01\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5bf9\u4f60\u7684\u9690\u7792\u6b66\u529f\uff0c\u9690\u7792\u8eab\u4efd\uff0c\u4f60\u4e00\u5b9a\u8981\u8bf4\u6211\u9a97\u4f60\u7684\u8bdd\uff0c\u8fd9\u4e24\u70b9 \u5c31\u7b97\u662f\u6211\u9a97\u4f60\u5427\uff0c\u4f46\u4f60\u6709\u6ca1\u6709\u9a97\u6211\u5462\uff1f\u201d\u8c2d\u516c\u76f4\u9053\uff1a\u201c\u6211\u9a97\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u4f60\u4e0d\u662f\u6c49\u4eba\uff1b\u7b2c\u4e8c\uff0c\u4f60\u4e5f\u4e0d\u662f\u59d3\u8c2d\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u89c9\u4e5f\u628a\u773c\u5411\u671b\u7740\u4e08\u592b\uff0c\u76ee\u5149\u4f3c\u5728\u8d28\u95ee\uff0c\u8fd9\u662f\u771f\u7684\u5427\uff1f\u8c2d\u9053\u6210\u4f4e\u58f0 \u9053\uff1a\u201c\u96ea\u59b9\uff0c\u6e05\u539f\u8c05\u6211\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u4f60\uff0c\u56e0\u4e3a\u6211\u6015\u4f60\u77e5\u9053\u6211\u4e0d\u662f\u6c49\u4eba\uff0c\u5c31\u4e0d\u80af\u5ac1\u6211\u3002\u201d\u53e6\u4e00 \u4e2a\u539f\u56e0\u4ed6\u672a\u66fe\u8bf4\u51fa\u6765\u7684\u662f\uff1a\u6b63\u5982\u5f20\u708e\u8981\u5973\u513f\u4fdd\u5b88\u79d8\u5bc6\u4e00\u6837\uff0c\u4ed6\u7684\u7236\u4eb2\u4e5f\u662f\u66fe\u7ecf\u544a\u8bc9\u4ed6\uff0c\u8981\u4ed6 \u9690\u7792\u8eab\u4efd\u7684\u3002 \u3000\u3000\u8c2d\u516c\u7acb\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u91d1\u4eba\uff0c\u4e0d\u662f\u6c49\u4eba\uff0c\u4f46\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u548c\u6c49\u4eba\u6253\u8fc7\u4ed7\uff01\u201d \u3000\u3000\u5f20\u708e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u6ca1\u4eba\u80fd\u66ff\u4f60\u8bc1\u660e\u3002\u518d\u8bf4\uff0c\u4e0e\u6c49\u4eba\u4e3a\u654c\uff0c\u4e5f\u5e76\u4e0d\u9650 \u4e8e\u4e24\u9635\u5bf9\u5792\uff0c\u52a8\u5200\u52a8\u67aa\uff01\u201d \u3000\u3000\u6f6d\u516c\u76f4\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u8fd9\u6837\u731c\u7591\u6211\uff0c\u90a3\u6211\u6ca1\u6709\u8bdd\u8bf4\u3002\u201d\u8c2d\u9053\u6210\u671b\u7740\u59bb\u5b50\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c \u6211\u5e0c\u671b\u4f60\u80fd\u591f\u76f8\u4fe1\u6211\u7239\u7239\u7684\u8bf4\u8bdd\uff0c\u4f60\u662f\u660e\u767d\u9053\u7406\u7684\uff0c\u4f60\u60f3\u60f3\u5047\u5982\u6211\u7239\u7239\u771f\u7684\u5982\u3001\u5982\u4f60\u7239\u7239\u6240 \u8bf4\uff0c\u662f\u84c4\u610f\u548c\u6c49\u4eba\u4e3a\u654c\uff0c\u90a3\u4e48\u4ed6\u4f55\u5fc5\u5728\u8fd9\u8352\u5c71\u9690\u5c45\uff1f\u518d\u8bf4\u5230\u6211\uff0c\u6211\u662f\u4e03\u5c81\u90a3\u5e74\u5c31\u8ddf\u7239\u7239\u4e0a\u5c71 \u7684\uff0c\u6211\u6ca1\u6709\u4f24\u5bb3\u8fc7\u4efb\u4f55\u4eba\u3002\u91d1\u4eba\u6c49\u4eba\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff0c\u96be\u9053\u53ea\u56e0\u4e3a\u91d1\u56fd\u548c\u5b8b\u56fd\u6253\u4ed7\u3002\u4f60\u5c31\u8981\u628a \u6211\u5f53\u4f5c\u654c\u4eba\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u521d\u65f6\u7684\u786e\u662f\u601d\u60f3\u6709\u70b9\u6df7\u4e71\uff0c\u5979\u4ece\u6765\u6ca1\u6709\u63a5\u89e6\u8fc7\u8fd9\u6837\u7684\u95ee\u9898\uff0c\u542c\u5f97\u4e08\u592b\u662f\u91d1\u56fd\u4eba\uff0c \u5403\u60ca\u5b9e\u5728\u4e0d\u5c0f\u3002 \u3000\u3000\u91d1\u5b8b\u4e43\u662f\u654c\u56fd\uff0c\u4e0d\u77e5\u6253\u4e86\u591a\u5c11\u5e74\u7684\u4ed7\u4e86\uff0c\u76ee\u524d\u91d1\u5175\u5c31\u6b63\u5c06\u5927\u4e3e\u4fb5\u5b8b\uff0c\u524d\u4e24\u5929\u5979\u8fd8\u89c1\u5230\u5c71 \u4e0b\u7ecf\u8fc7\u7684\u96be\u6c11\u3002\u77e5\u9053\u4e08\u592b\u662f\u654c\u56fd\u7684\u4eba\uff0c\u5fc5\u91cc\u603b\u662f\u4e0d\u5927\u8212\u670d\u3002 \u3000\u3000\u4f46\u5979\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u628a\u4e08\u592b\u4e0e\u201c\u654c\u4eba\u201d\u8fde\u5728\u4e00\u8d77\uff0c\u60f3\u90fd\u4e0d\u80fd\u8fd9\u6837\u60f3\uff01 \u3000\u3000\u5979\u81ea\u5c0f\u5c31\u662f\u548c\u8c2d\u9053\u6210\u5728\u4e00\u8d77\u6e38\u73a9\uff0c\u8c2d\u9053\u6210\u50cf\u54e5\u54e5\u4e00\u6837\u7231\u62a4\u5979\uff0c\u5979\u60f3\u5230\u7684\u53ea\u662f\u8c2d\u9053\u6210\u7684\u597d \u5904\u3002 \u3000\u3000\u5979\u505a\u9519\u4e86\u4e8b\u8c2d\u9053\u6210\u4e3a\u5979\u62c5\u5f53\uff0c\u5979\u559c\u6b22\u7684\u4e1c\u897f\u8c2d\u9053\u6210\u4e3a\u5979\u730e\u53d6\uff0c\u5979\u53d7\u5230\u4f24\u5bb3\u9a8c\u65f6\u5019\uff1b\u4e5f\u603b \u662f\u8c2d\u9053\u6210\u5728\u5979\u7684\u9762\u524d\uff0c\u4e3a\u5979\u6321\u4f4f\u707e\u96be\uff01 \u3000\u3000\u201c\u662f\u554a\uff0c\u91d1\u4eba\u548c\u6c49\u4eba\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u6210\u54e5\u5c31\u662f\u6210\u54e5\uff0c\u662f\u75bc\u6211\u7231\u6211\u7684\u6210\u54e5\uff01\u5c71\u5916\u9762\u91d1\u4eba\u548c \u6c49\u4eba\u6253\u4ed7\u53c8\u4e0e\u6210\u54e5\u4f55\u5e72\uff0c\u6211\u7684\u6210\u54e5\u6253\u7684\u53ea\u662f\u6076\u72fc\uff0c\u53ea\u662f\u731b\u864e\u3002\u4eca\u82e5\u4e0d\u662f\u4ed6\uff0c\u6211\u65e9\u5df2\u7ed9\u731b\u864e\u5403 \u4e86\uff01\u201d\u5fc3\u5934\u7684\u7ed3\u89e3\u5f00\uff0c\u5979\u62ac\u8d77\u5934\u6765\u3002 \u3000\u3000\u5979\u7684\u7239\u7239\u6b63\u5728\u7ee7\u7eed\u5411\u8c2d\u516c\u76f4\u53d1\u95ee\u3002 \u3000\u3000\u201c\u4f60\u975e\u4f46\u4e0d\u662f\u6c49\u4eba\uff0c\u4f60\u8fd9\u4e2a\u59d3\u4e5f\u662f\u5047\u7684\uff0c\u4f60\u4e0d\u662f\u59d3\u8c2d\uff0c\u4f60\u662f\u59d3\u6a80\uff0c\u6a80\u9999\u7684\u6a80\u3002\u6211\u8bf4\u5f97\u5bf9 \u5417\uff1f\u201d \u3000\u3000\u8c2d\u516c\u76f4\u6ca1\u6709\u56de\u7b54\uff0c\u6709\u7684\u53ea\u662f\u51b7\u7b11\u3002\u4f3c\u4e4e\u662f\u5728\u8bf4\uff0c\u4f60\u90fd\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u8fd8\u95ee\u6211\u5e72\u5417\uff1f\u5012\u662f\u8c2d \u9053\u6210\u6050\u5979\u591a\u7591\uff0c\u4f4e\u58f0\u4e3a\u5979\u89e3\u91ca\uff1a\u201c\u6c49\u4eba\u5f88\u5c11\u59d3\u6a80\uff0c\u56e0\u6b64\u6211\u4eec\u624d\u6539\u59d3\u8c2d\u3002\u8fd9\u4e0d\u8fc7\u662f\u5c0f\u4e8b\u4e00\u6869\uff0c \u96ea\u59b9\uff0c\u4f60\u4e0d\u4f1a\u602a\u6211\u6b3a\u9a97\u4f60\u5427\uff1f\u201d \u3000\u3000\u6539\u59d3\u53ea\u662f\u4e3a\u4e86\u8981\u5192\u5145\u6c49\u4eba\uff0c\u4ed6\u5192\u5145\u6c49\u4eba\u5f20\u96ea\u6ce2\u90fd\u5df2\u7ecf\u539f\u8c05\uff0c\u53c8\u600e\u4f1a\u8ba1\u8f83\u4ed6\u59d3\u4ec0\u4e48\u3002 \u3000\u3000\u5979\u62ac\u8d77\u5934\uff0c\u5bf9\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\u90fd\u662f\u6709\u597d\u4eba\u4e5f\u6709\u574f\u4eba\uff0c\u7239\u7239\uff0c\u8fd9\u53e5\u8bdd\u597d\u50cf\u662f\u4f60\u8bf4\u8fc7 \u7684\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u662f\u6211\u8bf4\u8fc7\u7684\u3002\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e0d\u7ba1\u662f\u91d1\u4eba\u8fd8\u662f\u6c49\u4eba\uff0c\u6c49\u4eba\u6709\u597d\u4eba\u574f\u4eba\u4e4b\u5206\uff0c\u91d1\u4eba\u4e5f\u6709\u597d\u4eba\u574f\u4eba\u4e4b\u5206\uff0c\u5bf9\u5417\uff1f\u53c8 \u4e0d\u7ba1\u662f\u59d3\u8c2d\u8fd8\u662f\u59d3\u6a80\u7684\uff0c\u54ea\u4e00\u4e2a\u59d3\u4e5f\u90fd\u662f\u6709\u597d\u4eba\u4e5f\u6709\u574f\u4eba\u7684\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u73b0\u5728\u5c31\u662f\u8981\u4f60\u660e\u767d\uff0c\u8c01\u662f\u597d\u4eba\uff0c\u8c01\u662f\u574f\u4eba\uff01\u201d \u3000\u3000\u4ed6\u56de\u8fc7\u5934\u6765\u3002\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6a80\u516c\u76f4\uff0c\u4f60\u975e\u4f46\u4e0d\u662f\u6c49\u4eba\uff0c\u800c\u4e14\u4e0d\u662f\u666e\u901a\u91d1\u4eba\u3002\u4f60\u662f\u91d1\u56fd\u7684 \u8d35\u65cf\uff0c\u4f60\u7684\u7236\u4eb2\u6a80\u79d1\u9686\u66fe\u4e3a\u91d1\u56fd\u5175\u9a6c\u5927\u5143\u5e05\uff0c\u4f60\u7684\u59d1\u59d1\u662f\u5168\u56fd\u5f53\u4eca\u7684\u7687\u592a\u540e\uff0c\u4f60\u7684\u8eab\u4efd\uff0c\u662f \u91d1\u56fd\u7684\u738b\u7237\uff01\u201d \u3000\u3000\u5c3d\u7ba1\u5f20\u96ea\u6ce2\u5df2\u7ecf\u5e76\u4e0d\u5728\u4e4e\u4e08\u592b\u662f\u6c49\u4eba\u8fd8\u662f\u91d1\u4eba\uff0c\u4f46\u542c\u5f97\u4ed6\u8fd9\u6837\u663e\u8d6b\u7684\u8eab\u4e16\uff0c\u4ecd\u662f\u4e0d\u7981\u5fc3 \u5934\u4e00\u9707\uff0c\u8138\u8272\u4e5f\u90fd\u53d8\u4e86\u3002 \u3000\u3000\u6a80\u516c\u76f4\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u5f20\u708e\u77e5\u9053\u4ed6\u7684\u8eab\u4e16\u3002\u4f3c\u4e4e\u65e9\u5df2\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\uff0c\u5012\u662f\u4ed6\u7684\u513f\u5b50\uff08\u73b0 \u5728\u5e94\u8be5\u6539\u79f0\u6a80\u9053\u6210\u4e86\uff09\u8138\u4e0a\u73b0\u51fa\u4e00\u6d3e\u832b\u7136\u7684\u795e\u8272\u3002\u539f\u6765\u4ed6\u4e5f\u662f\u548c\u5f20\u96ea\u6ce2\u4e00\u6837\uff0c\u5c1a\u672a\u77e5\u9053\u81ea\u5df1 \u7684\u8eab\u4e16\u7684\u3002 \u3000\u3000\u6a80\u516c\u76f4\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8eab\u4e16\uff0c\u4f60\u6253\u542c\u5f97\u5982\u6b64\u4ed4\u7ec6\uff0c\u5012\u771f\u662f\u96be\u4e3a\u4f60\u4e86\uff01\u201d \u3000\u3000\u6a80\u9053\u6210\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u7239\u7239\u521a\u624d\u9a82\u4ed6\u662f\u5904\u5fc3\u79ef\u8651\uff0c\u8981\u60f3\u8c0b\u5bb3\u6211\u4eec\u7236\u5b50\u3002\u83ab\u975e\u5c31\u662f\u56e0 \u4e3a\u4ed6\u65e9\u5df2\u6253\u542c\u4e86\u7239\u7239\u7684\u8eab\u4e16\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u60f3\u5f97\u5230\u7684\u5f20\u708e\u5f53\u7136\u4e5f\u5df2\u60f3\u5230\u4e86\uff0c\u4ed6\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u516c\u76f4\uff0c\u4f60\u8fd9\u662f\u4ee5\u5c0f\u4eba\u4e4b \u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u4e0d\u9519\uff0c\u6211\u662f\u65e9\u5df2\u5bf9\u4f60\u8fd9\u4e2a\u8d77\u7591\uff0c\u4f46\u5374\u6ca1\u6709\u5982\u4f60\u6240\u60f3\u90a3\u6837\u8d39\u5c3d\u5fc3\u673a\u6253\u542c\u4f60\u7684\u8eab \u4e16\u3002\u201d \u3000\u3000\u6a80\u9053\u6210\u9053\uff1a\u201c\u90a3\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u4f55\u5f97\u77e5\uff0c\u4f60\u4e0d\u5fc5\u7ba1\u3002\u6211\u53ea\u95ee\u4f60\uff0c\u6211\u8bf4\u7684\u8fd9\u4e9b\u662f\u4e0d\u662f\u4e8b\u5b9e\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u66fe\u7ecf\u662f\u91d1\u56fd\u7684\u738b\u7237\uff0e\u4f46\u73b0\u5728\u65e9\u5df2\u4e0d\u662f\u4e86\uff01\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u662f\u4e0e\u4e0d\u662f\uff0c\u53ea\u6709\u4f60\u81ea\u5df2\u77e5\u9053\uff0c\u8c01\u80fd\u66ff\u4f60\u8bc1\u660e\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u5fc3\u4e2d\u8d8a\u53d1\u8ff7\u8292\uff0c\u60f3\u9053\uff1a\u201c\u7239\u7239\u82e5\u7136\u771f\u662f\u91d1\u56fd\u7684\u738b\u7237\uff0c\u4e3a\u4f55\u4ed6\u8981\u548c\u6211\u5728\u8fd9\u5c71\u4e0a\u53d7 \u82e6\uff1f\u201d\u4f46\u4ece\u5f20\u708e\u4e0e\u4ed6\u7236\u4eb2\u7684\u5bf9\u7b54\u4e4b\u4e2d\uff0c\u4ed6\u5df1\u77e5\u9053\u5f20\u708e\u6240\u8a00\u975e\u5047\u3002 \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u60f3\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u56e0\u4f55\u7b49\u4eca\u5929\uff0c\u65b9\u4e0b\u6bd2\u624b\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u8fd9\u6211\u5012\u4e0d\u6015\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u7684\u8eab\u4e16\uff0c\u6211\u662f\u524d\u5929\u624d\u77e5\u9053\u7684\u3002 \u3000\u3000\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u5077\u542c\u4e86\u6211\u548c\u5ba2\u4eba\u7684\u8c08\u8bdd\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\uff01\u201d \u3000\u3000\u6697\u4e2d\u5077\u542c\u522b\u4eba\u7684\u8c08\u8bdd\uff0c\u672c\u6765\u662f\u4e00\u4ef6\u4e0d\u5149\u91c7\u7684\u4e8b\u3002\u4f46\u6a80\u516c\u76f4\u5e76\u6ca1\u9a82\u4ed6\u5351\u9119\uff0c\u53cd\u800c\u597d\u50cf\u662f\u677e \u4e86\u53e3\u6c14\u4f3c\u7684\u3002\u8138\u8272\u4e5f\u6ca1\u6709\u90a3\u4e48\u9634\u6c89\u4e86\u3002\u6a80\u9053\u6210\u8bf4\u9053\uff1a\u201c\u6211\u7684\u7239\u7239\u7eb5\u7136\u66fe\u662f\u91d1\u56fd\u7684\u738b\u7237\uff0c\u90a3\u53c8 \u4e0e\u4f60\u4f55\u5e72\uff1f\u4ed6\u6ca1\u505a\u574f\u4e8b\uff0c\u4e5f\u6ca1\u6253\u8fc7\u4f60\u4eec\u6c49\u4eba\uff01\u201d \u3000\u3000\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u6012\u9053\uff1a\u201c\u6211\u7239\u7239\u7684\u4e3a\u4eba\uff0c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u4ed6\u7239\u5e74\u5c11\u65f6\u5019\u505a\u7684\u4e8b\u60c5\uff0c\u4ed6\u6216\u8bb8\u4e0d\u77e5\uff0c\u4f46\u6700\u5c11\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u4ed6\u662f \u8ddf\u7740\u7236\u4eb2\u540c\u5728\u8352\u5c71\u5ea6\u65e5\u7684\uff01\u201d\u5f20\u708e\u82e6\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4e5f\u76f8\u4fe1\u4ed6\u662f\u597d\u4eba\uff0c\u602a\u6211\u505a\u5f97\u8fc7\u4efd \u4e86\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6ca1\u6709\u56de\u7b54\uff0c\u5fc3\u4e2d\u6df7\u4e71\u5f02\u5e38\u3002 \u3000\u3000\u6a80\u516c\u76f4\u6c89\u58f0\u9053\uff1a\u201c\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u5df2\u7ecf\u77e5\u9053\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u4e5f\u5e94\u8be5\u544a\u8bc9\u6211\u4e86\u5427\uff01\u201d \u3000\u3000\u5f20\u708e\u89c1\u4ed6\u8bf4\u8bdd\u7684\u795e\u60c5\u4e0d\u50cf\u4f2a\u88c5\uff0c\u5fc3\u91cc\u4e0d\u7981\u8d77\u4e86\u70b9\u7591\u4e91\u3002\u76ef\u7740\u4ed6\u9053\uff1a\u201c\u4f60\u5f53\u771f\u5c1a\u672a\u77e5 \u9053\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6000\u7591\u6211\u662f\u5904\u5fc3\u79ef\u8651\u8981\u8c0b\u5bb3\u4f60\u7684\u5417\uff1f\u6211\u82e5\u7136\u65e9\u5df2\u77e5\u9053\u4f60\u7684\u5e95\u7ec6\uff0c \u6211\u8fd8\u4e0d\u62a2\u5148\u4e0b\u624b\uff0c\u5c82\u80fd\u4e2d\u4f60\u6bd2\u8ba1\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u7ba1\u4f60\u771f\u7684\u4e0d\u77e5\u8fd8\u662f\u5047\u7684\u4e0d\u77e5\uff0c\u4e3a\u4e86\u516c\u5e73\u8d77\u89c1\uff0c\u5728\u4f60\u4e34\u6b7b\u4e4b\u524d\uff0c\u6211\u662f \u5e94\u8be5\u8ba9\u4f60\u77e5\u9053\u7684\uff0c\u6211\u662f\u4f55\u4eba\uff0c\u6211\u53c8\u56e0\u4f55\u6740\u4f60\u3002\u201d\u76ee\u5149\u8ddf\u7740\u79fb\u5230\u5973\u513f\u8eab\u4e0a\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u522b\u77aa\u7740 \u773c\u775b\u671b\u6211\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u6709\u8bb8\u591a\u7591\u56e2\uff0c\u4f60\u4e5f\u60f3\u6211\u7ed9\u4f60\u8bf4\u4e2a\u660e\u767d\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u662f\u554a\uff0e\u6211\u4e5f\u5f88\u60f3\u77e5\u9053\u4f60\u4e3a\u4f55\u5c06\u6211\u8bb8\u914d\u7ed9\u6210\u54e5\u5374\u53c8\u8981\u6bd2\u6b7b\u6210\u54e5\uff1f\u5373\u4f7f\u4ed6\u662f\u5c0f \u738b\u7237\u7684\u8eab\u4efd\u4f60\u4e5f\u4e0d\u5e94\u8be5\u4e0b\u6b64\u6bd2\u624b\u554a\uff01\u6211\u8fd8\u60f3\u77e5\u9053\u3001\u77e5\u9053\u201d \u3000\u3000\u5f20\u708e\u5df2\u7ecf\u77e5\u9053\u5979\u60f3\u8bf4\u4ec0\u4e48\uff0c\u67d4\u58f0\u6253\u65ad\u5979\u7684\u8bdd\u9053\uff1a\u201c\u6211\u66fe\u7ecf\u7b54\u5e94\u8fc7\u4f60\uff0c\u5230\u4e86\u9002\u5f53\u7684\u65f6\u673a\uff0c \u6211\u4f1a\u628a\u4f60\u7684\u8eab\u4e16\u6765\u5386\u544a\u8bc9\u4f60\u7684\uff0c\u5982\u4eca\u5df2\u662f\u5230\u4e86\u6211\u5e94\u8be5\u544a\u8bc9\u4f60\u7684\u65f6\u5019\u4e86\u3002\u4f60\u522b\u5fc3\u6025\uff0c\u4f60\u60f3\u8981\u77e5 \u9053\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9759\u4e86\u4e0b\u6765\u3002\u7559\u5fc3\u542c\u5979\u7239\u7239\u8bf4\u8bdd\u3002 \u3000\u3000\u5f20\u708e\u5374\u6ca1\u6709\u9a6c\u4e0a\u5c31\u8bf4\u3002\u4ed6\u81ea\u659f\u81ea\u996e\uff0c\u559d\u4e86\u4e24\u676f\u3002\u8fd9\u624d\u5ffd\u5730\u95ee\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u5c0f\u65f6\u5019\u6211\u7ed9 \u4f60\u8bf4\u8fc7\u5cb3\u98de\u7684\u6545\u4e8b\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d\u5f20\u96ea\u6ce2\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u89e3\u7239\u7239\u56e0\u4f55\u8981\u4ece\u5cb3\u98de\u7684\u6545\u4e8b\u8bf4\u8d77\u3002 \u534a\u664c\u7b54\u9053\uff1a\u201c\u8bb0\u5f97\u3002\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5cb3\u98de\u662f\u5b8b\u56fd\u7684\u540d\u5c06\uff0c\u4e5f\u662f\u5b8b\u56fd\u7684\u5927\u5fe0\u81e3\uff0c\u4ed6\u548c\u91d1\u56fd\u6253\u4ed7\uff0c\u51e0\u4e4e\u6218\u65e0\u4e0d\u80dc\u3002 \u91d1\u56fd\u7684\u519b\u961f\u91cc\u6d41\u884c\u7684\u4e24\u53e5\u8bdd\u9053\uff1a\u201c\u64bc\u5c71\u6613\uff0c\u64bc\u5cb3\u5bb6\u519b\u96be\u2019\u4ed6\u4eec\u5bf9\u5cb3\u98de\u7684\u754f\u60e7\uff0c\u53ef\u4ee5\u60f3\u89c1\u3002\u5f53 \u65f6\u91d1\u56fd\u7edf\u5175\u662f\u56db\u592a\u5b50\u5140\u672f\uff0c\u7ed9\u4ed6\u6253\u5f97\u5927\u8d25\u3002\u53ef\u60dc\u4ed6\u6b63\u8981\u4e58\u80dc\u8ffd\u51fb\uff0c\u6536\u590d\u5931\u571f\u7684\u65f6\u5019\uff0c\u5374\u7ed9\u7687 \u5e1d\u4e00\u5929\u7528\u5341\u4e8c\u9053\u91d1\u724c\u53ec\u4e86\u56de\u53bb\u3002\u540e\u6765\u88ab\u5978\u4eba\u5bb3\u6b7b\u4e86\u3002\u4e0d\u8fc7\u90a3\u5978\u4eba\u662f\u8c01\uff0c\u7239\u7239\u4f60\u597d\u50cf\u8fd8\u6ca1\u6709\u544a \u8bc9\u6211\uff0c\u4f60\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u5bb3\u6b7b\u5cb3\u5c11\u4fdd\u7684\u662f\u4e2a\u540d\u53eb\u79e6\u6867\u7684\u5927\u5978\u81e3\uff0c\u4ed6\u662f\u5b8b\u56fd\u7684\u5bb0\u76f8\uff0c\u6211\u7ed9\u4f60\u8bf4\u5cb3\u98de\u7684 \u6545\u4e8b\u4e4b\u65f6\uff0c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u6240\u4ee5\u6211\u4e5f\u6ca1\u544a\u8bc9\u4f60\u3002\u5cb3\u98de\u4e34\u6b7b\u4e4b\u524d\u7684\u5b98\u804c\u662f\u67a2\u5bc6\u526f\u4f7f\u52a0\u592a\u5b50\u5c11\u4fdd\uff0c \u4ed6\u7684\u90e8\u4e0b\u90fd\u79f0\u4ed6\u4e3a\u5cb3\u5c11\u4fdd\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4e0d\u7981\u5fc3\u4e2d\u7591\u60d1\uff0c\u4e3a\u4ec0\u4e48\u79e6\u6867\u6ca1\u6b7b\u7239\u7239\u5c31\u4e0d\u6562\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\u5462\uff1f \u3000\u3000\u4f46\u5979\u4e0d\u60f3\u6253\u65ad\u7239\u7239\u7684\u8bf4\u8bdd\uff0c\u8fd9\u4e00\u679d\u8282\u95ee\u9898\u4e5f\u5c31\u6682\u65f6\u4e0d\u53d1\u95ee\u4e86\u3002 \u3000\u3000\u4f46\u64c5\u516c\u76f4\u5374\u5ffd\u7136\u6253\u65ad\u5f20\u708e\u7684\u8bf4\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u6ca1\u6709\u7687\u5e1d\u7684\u6491\u8170\uff0c\u79e6\u6867\u6050\u6015\u4e5f\u4e0d\u80fd\u5bb3\u6b7b \u4f60\u4eec\u7684\u5cb3\u5c11\u4fdd\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4f60\u662f\u8981\u7ed9\u5978\u81e3\u5f00\u8131\uff1f\u54fc\uff0c\u54fc\uff0c\u4e0d\u9519\uff0c\u79e6\u6867\u662f\u6211\u4eec\u5b8b\u56fd\u7684 \u5927\u5978\u81e3\uff0c\u53ef\u662f\u4f60\u4eec\u91d1\u56fd\u7684\u5927\u5fe0\u81e3\uff0c\u4ed6\u662f\u4f60\u4eec\u6d3e\u56de\u6765\u7684\u5978\u7ec6\uff0c\u602a\u4e0d\u5f97\u4f60\u8981\u5e2e\u4ed6\u8bf4\u8bdd\u4e86\u3002\uff08\u6309\uff1b \u79e6\u6867\u66fe\u88ab\u5168\u56fd\u4fd8\u63b3\uff0c\u540e\u6765\u53d8\u8282\u6295\u964d\uff0c\u5949\u91d1\u4e3b\u4e4b\u547d\uff0c\u5047\u79f0\u662f\u6740\u4e86\u91d1\u4eba\u770b\u5b88\u9003\u56de\u672c\u56fd\uff0c\u4e3a\u91d1\u56fd\u5bf9 \u5b8b\u9ad8\u5b97\u8fdb\u884c\u62db\u964d\u8ba1\u5212\uff0c\u6210\u4e3a\u4e3b\u548c\u6d3e\u7684\u9886\u8896\u3002\u5cb3\u98de\u672a\u7ed9\u4ed6\u5bb3\u6b7b\u4e4b\u524d\uff0c\u8001\u767e\u59d3\u5df2\u7ecf\u6000\u7591\u4ed6\u662f\u5978\u7ec6 \u4e86\uff0c\u676d\u5dde\u7684\u5927\u8857\u5c0f\u5df7\u66fe\u7ecf\u8d34\u6ee1\u8fc7\u201c\u79e6\u76f8\u516c\u662f\u5978\u7ec6\u201c\u7684\u6807\u8bed\u3002\uff09\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u9519\u4e86\uff0c\u6211 \u5e76\u4e0d\u662f\u5e2e\u79e6\u6867\u8bf4\u8bdd\uff0c\u79e6\u6867\u5f53\u7136\u662f\u6b7b\u6709\u4f59\u8f9c\u3002\u4f46\u4f60\u8bd5\u60f3\u60f3\uff0c\u4f60\u4eec\u5b8b\u56fd\u7684\u767e\u59d3\u90fd\u77e5\u9053\u4ed6\u662f\u5978\u7ec6\uff0c \u4e3a\u4f55\u4f60\u4eec\u7684\u7687\u5e1d\u8fd8\u8981\u91cd\u7528\u4ed6\u5462\uff1f\u5bb3\u6b7b\u5cb3\u98de\u7684\u4e3b\u51f6\u6015\u8fd8\u8f6e\u4e0d\u5230\u79e6\u6867\u5427\uff1f\u6211\u8bf4\u7684\u53ea\u662f\u516c\u9053\u8bdd\uff01\u201d \u3000\u3000\u5cb3\u98de\u88ab\u5bb3\u4e4b\u540e\uff0c\u5f20\u708e\u5728\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u9053\u591a\u5c11\u6b21\u9a82\u8fc7\u7687\u5e1d\u662f\u660f\u541b\uff0c\u4f46\u8fd8\u6ca1\u6709\u6a80\u516c\u76f4\u8bf4\u5f97\u90a3\u4e48 \u900f\u5f7b\uff0c\u6562\u4e8e\u6307\u63a7\u7687\u5e1d\u624d\u662f\u4e3b\u51f6\u7684\u3002\u4ed6\u5446\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u9a82\u6211\u4eec\u7684\u7687\u5e1d\uff1f\u4e0d\u9519\uff0c\u6211\u4eec \u7684\u7687\u5e1d\u662f\u660f\u541b\uff0c\u4f46\u8fd9\u4e0d\u6b63\u662f\u4f60\u4eec\u6240\u5e0c\u671b\u7684\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u8bf4\u7684\u53ea\u662f\u516c\u9053\u8bdd\uff0c\u5509\uff0c\u505a\u7687\u5e1d\u7684\u4eba\u591a\u534a\u4e0d\u662f\u597d\u4eba\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4f3c\u6709\u65e0\u9650\u611f\u6168\uff01 \u3000\u3000\u5f20\u708e\u601d\u7591\u4e0d\u5b9a\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bf4\u98ce\u51c9\u8bdd\u4e86\uff0c\u4f60\u4ee5\u4e3a\u4f60\u987a\u7740\u6211\u7684\u53e3\u6c14\u8bf4\u8bdd\uff0c\u5047\u88c5\u540c \u60c5\u6211\u4eec\u7684\u5cb3\u5c11\u4fdd\uff0c\u6211\u5c31\u4f1a\u9976\u4f60\u5417\uff1f\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u5411\u4f60\u6c42\u9976\uff0c\u4ec0\u4e48\u662f\u771f\uff0c\u4ec0\u4e48\u662f\u5047\uff0c \u8c05\u4f60\u4e5f\u96be\u5206\u522b\u3002\u4f60\u8fd8\u662f\u7ee7\u7eed\u8bf4\u4f60\u7684\u8bdd\uff0c\u6211\u4e0d\u6253\u5c94\u4e86\u3002\u201d\u5f20\u708e\u5446\u4e86\u534a\u664c\uff0c\u56de\u5934\u95ee\u9053\uff1a\u201c\u96ea\u513f\uff0c \u6211\u521a\u624d\u8bf4\u9053\u90a3\u91cc\uff1f \u3000\u3000\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8bf4\u9053\u79e6\u6867\u5bb3\u6b7b\u5cb3\u98de\u3002\u201d \u3000\u3000\u5f20\u708e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u65e5\u5b50\u8fc7\u5f97\u771f\u5feb\uff0c\u5cb3\u5c11\u4fdd\u662f\u5728\u7ecd\u5174\u5341\u4e00\u5e74\u4e00\u6708\u4e8c\u5341\u4e03\u65e5\u7ed9\u5bb3\u6b7b\u7684\uff0c\u5230\u5982 \u4eca\u5df2\u662f\u4e8c\u5341\u4e00\u5e74\u4e86\u3002\u4f60\u8ddf\u6211\u51fa\u8d70\u90a3\u5e74\uff0c\u4e5f\u5373\u662f\u5cb3\u5c11\u4fdd\u88ab\u902e\u89e3\u4e0a\u4eac\u4e0b\u72f1\u90a3\u5e74\uff0c\u4f60\u624d\u5468\u5c81\uff0c\u5982\u4eca \u4f60\u7684\u5b69\u5b50\u5df2\u6709\u4e03\u5c81\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fc3\u4e2d\u4e00\u52a8\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u5cb3\u5c11\u4fdd\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u5979\u611f\u89c9\u5f97\u5230\uff0c\u5f20\u708e\u5bf9\u5cb3 \u98de\u7684\u60bc\u5ff5\uff0c\u7edd\u4e0d\u4ec5\u6b62\u4e8e\u662f\u4e00\u822c\u767e\u59d3\u5bf9\u5fe0\u81e3\u7684\u60bc\u5ff5\uff01 \u3000\u3000\u5f20\u708e\u53f9\u9053\uff1a\u201c\u6211\u53ea\u6068\u6211\u65e0\u7f18\u8ffd\u968f\u5cb3\u5c11\u4fdd\uff01\u201d \u3000\u3000\u8fd9\u4e00\u56de\u7b54\u9887\u51fa\u5f20\u96ea\u6ce2\u610f\u6599\u4e4b\u5916\uff0c\u5979\u81ea\u5931\u671b\uff0c\u53ea\u542c\u5f97\u5f20\u708e\u5df2\u5728\u7ee7\u7eed\u8bf4\u9053\u3002\u201c\u4e0d\u8fc7\uff0c\u8bf4\u8d77\u6765 \u4e5f\u6709\u591a\u5c11\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7cbe\u795e\u4e00\u632f\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u6709\u4e24\u540d\u5bb6\u5c06\uff0c\u4e00\u540d\u5f20\u4fdd\uff0c\u4e00\u540d\u738b\u6a2a\u3002\u5cb3\u5c11\u4fdd\u6bcf\u6b21\u51fa\u5f81\u3002\u90fd\u662f\u7531\u4ed6\u4eec \u4e8c\u4eba\u6267\u97ad\u968f\u884c\u7684\uff0c\u6545\u6b64\u4eba\u8c13\uff1a\u9a6c\u524d\u5f20\u4fdd\uff0c\u9a6c\u540e\u738b\u6a2a\u3002\u4ed6\u4eec\u5bf9\u5cb3\u5c11\u4fdd\u5fe0\u5fc3\u803f\u803f\uff0c\u5cb3\u5c11\u4fdd\u5c61\u6b21\u8981 \u63d0\u62d4\u4ed6\u4eec\u505a\u5e26\u5175\u7684\u5c06\u5b98\uff0c\u4ed6\u4eec\u90fd\u662f\u5b81\u613f\u53ea\u505a\u6267\u884c\u7684\u5bb6\u5c06\uff0c\u4e0d\u80af\u79bb\u5f00\u5cb3\u5c11\u4fdd\u8eab\u8fb9\u3002\u5cb3\u5c11\u4fdd\u4e5f\u662f \u628a\u4ed6\u4eec\u5f53\u4f5c\u624b\u8db3\u4e00\u822c\u3002\u7518\u82e6\u4e0e\u5171\u7684\u3002\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u4ed6\u773c\u4e2d\u6ef4\u4e0b\u4e24\u9897\u773c\u6cea\uff0c\u65b9\u628a\u81ea\u5df1\u7684\u8eab\u4efd\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u5cb3\u5c11\u4fdd\u7684\u9a6c\u524d\u5f20\u4fdd\uff0c\u5c31 \u662f\u6211\u7684\u7236\u4eb2\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u53c8\u662f\u5403\u4e00\u60ca\uff0c\u53c8\u662f\u7591\u60d1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u4ed6\u7684\u7236\u4eb2\u65e2\u7136\u662f\u5cb3\u5c11\u4fdd\u7684\u5f97\u529b\u5bb6\u5c06\uff0c\u4f55\u4ee5\u4ed6 \u53c8\u4f1a\u662f\u6211\u5bb6\u7684\u4ec6\u4eba\uff1f\u96be\u9053\u6211\u548c\u5cb3\u5c11\u4fdd\u4e5f\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u4e0d\uff0c\u4e0d\u4f1a\u5427\uff0c\u5cb3\u98de\u59d3\u5cb3\uff0c\u6211\u662f\u59d3\u5f20\uff0c\u6211 \u7edd\u4e0d\u4f1a\u662f\u5cb3\u5bb6\u7684\u4eba\u3002 \u3000\u3000\u5f20\u708e\u62b9\u53bb\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u63a2\u624b\u6000\u4e2d\uff0c\u62ff\u51fa\u4e00\u4e2a\u5c0f\u5de7\u73b2\u73d1\u7684\u9526\u76d2\uff0c\u4f3c\u662f\u5973\u5b50\u7684\u7528\u5177\uff0c\u5f20\u96ea\u6ce2 \u6b63\u81ea\u5947\u602a\uff0c\u4e0d\u77e5\u4ed6\u62ff\u51fa\u8fd9\u4e2a\u9526\u76d2\u4f55\u7528\uff0c\u53ea\u89c1\u4ed6\u5df2\u7ecf\u628a\u9526\u76d2\u6253\u5f00\uff0c\u98a4\u6296\u7684\u624b\u6307\u8f7b\u8f7b\u628a\u4e00\u5f20\u8272\u6cfd \u5df2\u53d8\u5f97\u6697\u9ec4\u7684\u7eb8\u5f20\u62bd\u4e86\u51fa\u6765\uff0c\u9012\u7ed9\u5f20\u96ea\u6ce2\u3002\u201c\u8fd9\u662f\u5cb3\u5c11\u4fdd\u4eb2\u7b14\u5199\u7684\u4e00\u9996\u8bcd\uff0c\u8bcd\u724c\u540d\u6ee1\u6c5f\u7ea2\uff0c \u662f\u90a3\u5e74\u4ed6\u5927\u7834\u91d1\u5140\u672f\u4e4b\u540e\u5199\u7684\uff0c\u6211\u4e3a\u4f60\u73cd\u85cf\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u5982\u4eca\u5e94\u8be5\u4ea4\u7ed9\u4f60\u4e86\u3002\u4f60\u5148\u770b\u4e00\u904d\uff0c \u770b\u770b\u6709\u6ca1\u6709\u4e0d\u8ba4\u5f97\u7684\u5b57\u3002\u201d\u5f20\u708e\u4e0d\u5f85\u5979\u53d1\u95ee\uff0c\u5c31\u5148\u8bf4\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5c0f\u65f6\u5019\u867d\u7136\u4e5f\u66fe\u8ddf\u5f20\u708e\u8bfb\u4e66\u5199\u5b57\uff0c\u4f46\u56e0\u5f20\u708e\u8bfb\u4e66\u65e0\u591a\uff0c\u5979\u6240\u8ba4\u8bc6\u7684\u5b57\u4e5f\u662f\u6709\u9650\u3002 \u666e\u901a\u5e38\u7528\u7684\u5b57\u5979\u662f\u8ba4\u5f97\u7684\uff0c\u8f83\u6df1\u8f83\u50fb\u7684\u5c31\u8ba4\u4e0d\u5f97\u4e86\u3002\u5cb3\u98de\u7684\u8fd9\u9996\u6ee1\u6c5f\u7ea2\u8bcd\u5012\u6ca1\u6709\u4ec0\u4e48\u50fb\u5b57\uff0c \u4f46\u56e0\u4e3a\u5199\u5f97\u9f99\u98de\u51e4\u821e\uff0c\u6709\u51e0\u4e2a\u5b57\u7b14\u5212\u4e5f\u6bd4\u8f83\u590d\u6742\uff0c\u5bf9\u5979\u800c\u8a00\u8fd8\u662f\u5c5e\u65bc\u201c\u6df1\u5b57\u201d\u7684\u3002\u4e0d\u8fc7\u5f53\u5979 \u6b63\u5728\u4ed4\u7ec6\u8ba4\u5b57\u4e4b\u65f6\uff0c\u5f20\u708e\u5df1\u662f\u60c5\u4e0d\u81ea\u7981\u6717\u8bf5\u8d77\u6765\u4e86\u3002\uff08\u8fd9\u9996\u8bcd\u4ed6\u4e0d\u77e5\u80cc\u8fc7\u591a\u5c11\u904d\uff0c\u65e9\u5df2\u719f\u6781 \u5982\u6d41\u4e86\u3002\uff09\u201c\u6012\u53d1\u51b2\u51a0\uff0c\u51ed\u680f\u5904\uff0c\u6f47\u6f47\u96e8\u6b47\u3002 \u3000\u3000\u62ac\u671b\u773c\uff0c\u4ef0\u5929\u957f\u5578\uff0c\u58ee\u6000\u6fc0\u70c8\u3002 \u3000\u3000\u4e09\u5341\u529f\u540d\u5c18\u4e0e\u571f\uff0c\u516b\u5343\u91cc\u8def\u4e91\u548c\u6708\u3002 \u3000\u3000\u83ab\u7b49\u95f2\uff0c\u767d\u4e86\u5c11\u5e74\u5934\uff0c\u7a7a\u60b2\u5207\u3002 \u3000\u3000\u9756\u5eb7\u803b\uff0c\u72b9\u672a\u96ea\u3002\u81e3\u5b50\u6068\uff0c\u4f55\u65f6\u706d\uff1f\u9a7e\u957f\u8f66\uff0c\u8e0f\u7834\u8d3a\u5170\u5c71\u7f3a\uff01 \u3000\u3000\u58ee\u5fd7\u9965\u9910\u80e1\u864f\u8089\uff0c\u7b11\u8c08\u6e34\u996e\u5308\u5974\u8840\u3002 \u3000\u3000\u4f8d\u4ece\u5934\uff0c\u6536\u62fe\u65e7\u5c71\u6cb3\uff0c\u671d\u5929\u9619\u3002\u201d \u3000\u3000\u957f\u54e5\u5f53\u54ed\uff0c\u5f20\u708e\u5ff5\u5b8c\u4e86\u8fd9\u9996\u201c\u6ee1\u6c5f\u7ea2\u201d\uff0c\u4e0d\u7531\u5f97\u8001\u6cea\u7eb5\u6a2a\uff0c\u4ef0\u5929\u957f\u5578\uff0c\u62cd\u6848\u53eb\u9053\uff1a\u201c\u58ee \u5fd7\u9965\u9910\u80e1\u864f\u8089\uff0c\u7b11\u8c08\u6e34\u996e\u5308\u5974\u8840\uff01\u6211\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u4e86\u5cb3\u5c11\u4fdd\u7684\u9057\u8bad\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4e5f\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u8fc7\u5979\u548c\u5f20\u708e\u4e0d\u540c\u7684\u662f\uff0c\u9664\u4e86\u6fc0\u60c5\uff0c\u5979\u8fd8\u6709\u7591\u60d1\u3002 \u3000\u3000\u5979\u7b49\u4f8d\u5f20\u708e\u7a0d\u5fae\u51b7\u9759\u4e0b\u6765\uff0c\u65b9\u59cb\u95ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u5cb3\u5c11\u4fdd\u4eb2\u7b14\u5199\u7684\u8fd9\u5e45\u5b57\u662f\u4f60\u6700\u5b9d\u8d35\u7684 \u5427\uff1f\u201c\u5f20\u708e\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff0c\u5b83\u5728\u6211\u7684\u5fc3\u4e2d\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u6211\u7231\u62a4\u5b83\u751a\u65bc\u6211\u7684\u751f\u547d\uff01\u201c\u5f20\u96ea\u6ce2 \u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8981\u7ed9\u6211\uff1f\u4e0d\u9519\uff0c\u6211\u77e5\u9053\u4f60\u628a\u6211\u5f53\u4f5c\u4eb2\u751f\u5973\u513f\uff0c\u4f46\u7eb5\u7136\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u80fd\u8981\u4f60 \u6700\u5b9d\u8d35\u7684\u4e1c\u897f\u5440\u3002\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5df2\u7ecf\u544a\u8bc9\u4e86\u4f60\u5417\uff1f\u5cb3\u5c11\u4fdd\u8fd9\u5e45\u4e66\u6cd5\u672c\u5e94\u662f\u5c5e\u65bc\u4f60\u7684\uff0c\u6211\u4e0d\u8fc7\u4e3a\u4f60\u6536 \u85cf\u800c\u5df1\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8d8a\u53d1\u60ca\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u7237\u7237\u6c42\u5cb3\u5c11\u4fdd\u5199\u7684\uff0c\u4ee5\u4e3a\u662f\u7237\u7237\u7559\u7ed9\u4f60\u505a\u4f20\u5bb6\u4e4b \u5b9d\u7684\u3002\u201d\u5979\u53eb\u60ef\u4e86\u5f20\u708e\u505a\u7239\u7239\uff0c\u5982\u4eca\u5979\u6240\u8bf4\u7684\u201c\u7237\u7237\u201d \u3000\u3000\u5b9e\u5373\u662f\u6307\u5f20\u708e\u7684\u7236\u4eb2\u5f20\u4fdd\u3002\u539f\u6765\u5979\u8bef\u89e3\u4e86\u5f20\u708e\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u5979\u4ee5\u4e3a\u5f20\u708e\u8bf4\u7684\u4e3a\u5730\u73cd\u85cf\uff0c \u4e43\u662f\u56e0\u4e3a\u5f20\u708e\u5df2\u7ecf\u6ca1\u6709\u522b\u7684\u4eb2\u4eba\uff0c\u6545\u800c\u8981\u628a\u81ea\u5df1\u6700\u73cd\u8d35\u7684\u4e1c\u897f\u4fdd\u7559\u7ed9\u5979\u3002 \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u8fd9\u4ef6\u65e0\u4ef7\u4e4b\u5b9d\u662f\u4f60\u7684\u6bcd\u4eb2\u4ea4\u7ed9\u6211\u4ee3\u4e3a\u4fdd\u7ba1\u7684\uff0c\u4f60\u957f\u5927\u4e86\u3002\u6211\u5f53 \u7136\u5e94\u8be5\u628a\u4f60\u6bcd\u4eb2\u7684\u9057\u7269\u4ea4\u8fd8\u7ed9\u4f60\u3002\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u7684\u5a18\u4eb2\u4f1a\u6709\u5cb3\u5c11\u4fdd\u5199\u7684\u5b57\u5462\uff1f \u201c\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u5fc3\u6025\uff0c\u5cb3\u5c11\u4fdd\u7684\u6545\u4e8b\u6211\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5462\uff0c\u4e00\u5f85\u6211\u8bf4\u5b8c\uff0c\u4f60\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u659f\u81ea\u996e\uff0c\u559d\u4e86\u4e24\u676f\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u624b\u4e0b\u6709\u4e24\u5458\u5927\u5c06\uff0c\u4e00\u4e2a\u662f\u4ed6\u7684\u517b\u5b50\u5cb3 \u4e91\uff0c\u4e00\u4e2a\u662f\u4ed6\u7684\u5973\u5a7f\u5f20\u5baa\u3002\u5cb3\u4e91\u52c7\u731b\u8fc7\u4eba\uff0c\u5f20\u5baa\u5219\u4e0d\u4f46\u6253\u4ed7\u52c7\u6562\uff0c\u66f4\u517c\u7cbe\u901a\u5175\u6cd5\uff0c\u5728\u5cb3\u5bb6\u519b \u4e2d\uff0c\u5730\u4f4d\u5728\u8bf8\u5c06\u4e4b\u4e0a\u3002\u5cb3\u5c11\u4fdd\u5c31\u662f\u56e0\u4e3a\u4ed6\u5c61\u7acb\u6218\u529f\uff0c\u6545\u800c\u628a\u540d\u53eb\u826e\u74f6\u7684\u5973\u513f\u5ac1\u7ed9\u4ed6\u7684\u3002 \uff08\u6309\uff1b\u5f20\u5baa\u4e3a\u5cb3\u98de\u5973\u5a7f\u4e00\u4e8b\uff0c\u6b63\u53f2\u4e0d\u8f7d\uff0c\u53ea\u89c1\u4e8e\u7a17\u5b98\u91ce\u53f2\u3002\u4f46\u676d\u5dde\u5efa\u6709\u5f20\u70c8\u6587\u5019\uff08\u5f20\u5baa\u6ea2 \u53f7\uff09\u7960\uff0c\u5851\u826e\u74f6\u50cf\u4ee5\u914d\u4e4b\u3002\u6e0a\u96c5\u4e4b\u58eb\uff0c\u4ea6\u5f15\u4e4b\u5165\u6587\uff0c\u5982\u6e05\u4ee3\u5434\u9521\u9e9f\u4e4b\u5cb3\u738b\u8bba\u4e2d\uff0c\u5373\u6709\u201c\u5171\u7231 \u5a7f\u4ee5\u540c\u5f52\uff0c\u5408\u4f73\u513f\u4e3a\u4e00\u4f20\u201d\u4e4b\u53e5\uff09\u201c\u79e6\u6867\u8981\u5bb3\u5cb3\u5c11\u4fdd\uff0c\u5f53\u7136\u4e0d\u80fd\u653e\u8fc7\u5f20\u5baa\u548c\u5cb3\u4e91\uff0c\u4ed6\u9996\u91cd\u72af \u5148\u5c31\u662f\u4ece\u9677\u5bb3\u5f20\u5baa\u548c\u5cb3\u4e91\u5f00\u59cb\u7684\u3002\u4ed6\u6307\u4f7f\u5927\u7406\u5bfa\u537f\uff08\u76f8\u5f53\u65bc\u73b0\u4ee3\u6700\u9ad8\u6cd5\u9662\u7684\u5ba1\u5224\u957f\uff09\u5468\u4e09\u754f \u8bec\u544a\u5f20\u5baa\u548c\u5cb3\u4e91\u8c0b\u53cd\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u544a\u4eba\u8c0b\u53cd\uff0c\u4e5f\u603b\u5f97\u6709\u4e2a\u8bc1\u636e\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u65e9\u5df2\u6709\u4eba\u8fd9\u6837\u8d28\u95ee\u8fc7\u79e6\u6867\u4e86\u3002\u8fd9\u4e2a\u4eba\u662f\u5f53\u65f6\u548c\u5cb3\u5c11\u4fdd\u9f50\u540d\u7684\u4e00\u4f4d\u5927\u5c06\uff0c\u540d\u53eb \u97e9\u4e16\u5fe0\u3002\u4ed6\u7684\u5b98\u804c\u6bd4\u5cb3\u5c11\u4fdd\u8fd8\u9ad8\u4e00\u7ea7\uff0c\u662f\u6b63\u67a2\u5bc6\u4f7f\u3001\uff08\u76f8\u5f53\u65bc\u56fd\u9632\u90e8\u957f\uff09\u201c\u79e6\u6867\u6307\u4f7f\u5468\u4e09\u754f \u8bec\u544a\u5f20\u5baa\u548c\u5cb3\u4e91\u8c0b\u53cd\uff0c\u6700\u540e\u628a\u5cb3\u5c11\u4fdd\u4e5f\u7275\u8fde\u4e0a\u4e86\u3002\u8fd8\u4e0d\u4ec5\u662f\u2018\u7275\u8fde\u2019\u800c\u5df2\uff0c\u4ed6\u4eec\u7adf\u6562\u628a\u5cb3\u5c11 \u4fdd\u8bf4\u6210\u662f\u9020\u53cd\u7684\u4e3b\u8c0b\uff0c\u662f\u4ed6\u6307\u4f7f\u513f\u5b50\u548c\u5973\u5a7f\u5bc6\u8c0b\u9020\u53cd\u7684\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u4e00\u53e3\u54ac\u5b9a\u5f20\u5baa\u548c\u5cb3\u4e91\u6709\u4e66\u4fe1\u5f80\u8fd4\uff0c\u5546\u91cf\u5728\u8944\u9633\u53d1\u52a8\u5175\u8c0f\u3002\u6240\u8c13\u2018\u5175\u8c0f\u2018\u5373\u662f\u8981\u53cd \u53db\u4e86\u3002\u4f46\u662f\u6240\u8c13\u53cd\u4e66\u4ed6\u4eec\u53c8\u62ff\u4e0d\u51fa\u6765\uff0c\u4ed6\u4eec\u62ff\u5f97\u51fa\u6765\u7684\u53ea\u662f\u4e00\u5f20\u7531\u4ed6\u4eec\u634f\u9020\u7684\u5f20\u5baa\u7684\u4f9b\u8f9e\u3002 \u3000\u3000\u201d\u97e9\u4e16\u5fe0\u5f53\u7136\u77e5\u9053\u8fd9\u4e2a\u51a4\u72f1\u5c31\u662f\u79e6\u6867\u4e00\u624b\u9020\u6210\u7684\uff0c\u4ed6\u5c31\u8dd1\u53bb\u95ee\u79e6\u6867\uff1a\u2018\u76f8\u516c\uff0c\u5cb3\u98de\u7eb5\u6709 \u4e0d\u662f\uff0c\u4f46\u4e07\u4e07\u4e0d\u81f3\u65bc\u8c0b\u53cd\u3002\u8fd9\u6837\u5bf9\u4ed8\u529f\u81e3\uff0c\u5c06\u4f7f\u4eba\u5fc3\u6da3\u6563\uff0c\u6050\u975e\u56fd\u5bb6\u4e4b\u798f\u3002\u8bf7\u95ee\u76f8\u516c\uff0c\u5cb3\u98de \u8c0b\u53cd\uff0c\u6709\u4f55\u8bc1\u636e\uff1f\u201c\u79e6\u6867\u7b54\u9053\uff1a\u201c\u98de\u5b50\u4e91\u4e0e\u5f20\u5baa\u7684\u4fe1\uff0c\u867d\u7136\u4e0d\u660e\u4e0b\u843d\uff0c\u4f46\u5cb3\u98de\u6709\u7f6a\uff0c\u7f6a\u540d\u662f \u5b9e\uff01\u2019\u97e9\u4e16\u5fe0\uff1a\u201c\u4ed6\u7684\u7f6a\u540d\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4ed6\u987f\u4e86\u4e00\u987f\u3002\u5f20\u96ea\u6ce2\u542c\u5f97\u51fa\u4e86\u795e\uff0c\u6025\u4e8e\u77e5\u9053\u7ed3\u679c\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u600e\u4e48\u4e0d \u8bf4\u4e0b\u53bb\uff0c\u5cb3\u98de\u7684\u7f6a\u540d\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u708e\u4e00\u58f0\u957f\u53f9\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201d\u97e9\u4e16\u5fe0\u731c\u60f3\u4e0d\u5230\uff0c\u4efb\u4f55\u4eba\u6050\u6015\u4e5f\u731c\u60f3\u4e0d\u5230\uff01\u79e6\u6867\u8bf4\u7684\u5cb3\u5c11 \u4fdd\u7684\u7f6a\u540d\uff0c\u53ea\u6709\u4e09\u4e2a\u5b57\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u662f\u54ea\u4e09\u4e2a\u5b57\uff1f\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u83ab\u987b\u6709\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5446\u4e86\u534a\u664c\u3002\u8bf4\u9053\uff1a\u201c\u771f\u662f\u5c82\u5b58\u6b64\u7406\uff01\u97e9\u4e16\u5fe0\u600e\u6837\u8bf4\uff1f\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u79e6\u6867\u4ee5\u5bb0\u76f8\u4e4b\u5c0a\uff0c\u7adf\u6562\u8bf4\u51fa\u8fd9\u6837\u65e0\u8d56\u7684\u8bdd\uff0c\u97e9\u4e16\u5fe0\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff1f\u4ed6\u53ea\u80fd\u62c2 \u8896\u800c\u8d77\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u76f8\u516c\uff0c\u8fd9\u2018\u83ab\u987b\u6709\u2019\u4e09\u5b57\uff0c\u4f55\u4ee5\u670d\u5929\u4e0b\uff1f\u2019\u8bf4\u7f62\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u5927\u8e0f\u6b65 \u8d70\u51fa\u76f8\u5e9c\u3002\u201d \u3000\u3000\u6a80\u9053\u6210\u542c\u5f97\u4e5f\u4e0d\u7981\u6fc0\u52a8\u8d77\u6765\uff0c\u6c89\u58f0\u9a82\u9053\uff1a\u201c\u8be5\u6b7b\uff0c\u8be5\u6b7b\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u56de\u5934\u671b\u4ed6\uff0c\u76ee\u5149\u9887\u6709\u8be7\u610f\u3002\u201c\u6210\u54e5\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u9053\uff1a\u201c\u6211\u662f\u8bf4\u79e6\u6867\u8be5\u6b7b\uff1b\u96ea\u59b9\uff0c\u6211\u548c\u4f60\u4e00\u6837\uff0c\u6211\u53ea\u77e5\u9053\u6709\u597d\u4eba\u574f\u4eba\u4e4b\u5206\uff0c\u96be\u9053\u4f60 \u4ee5\u4e3a\u6211\u4f1a\u5e2e\u79e6\u6867\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8138\u4e0a\u7efd\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6210\u54e5\uff0c\u539f\u6765\u4f60\u6211\u8fd8\u662f\u4e24\u5fc3\u5982\u4e00\uff01\u201d\u5f20\u708e\u53f9\u9053\uff1a \u201c\u53ef\u60dc\u8be5\u6b7b\u7684\u4eba\u504f\u504f\u957f\u5bff\uff0c\u4e0d\u8be5\u6b7b\u7684\u4eba\u5374\u51a4\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u6700\u540e\u5224\u6848\u90a3\u5929\u6765\u5230\u4e86\uff0c\u5927\u7406\u5bfa\uff08\u6700\u9ad8\u6cd5\u9662\uff09\u6b63\u5802\u4e0a\u8bbe\u4e0b\u516c\u6848\uff0c\u4e2d\u95f4 \u662f\u5723\u65e8\uff0c\u5de6\u8fb9\u662f\u79e6\u6867\u6d3e\u6765\u76d1\u89c6\u5ba1\u5224\u7684\u4e2d\u4e1e\u4f55\u94f8\uff0c\u53f3\u8fb9\u662f\u4e3b\u5ba1\u7684\u5927\u7406\u5bfa\u537f\u5468\u4e09\u754f\uff0c\u4e24\u4fa7\u662f\u966a\u5ba1 \u5b98\u5fa1\u53f2\u5927\u592b\u4e07\u4fdf\u9ad8\u548c\u7f6a\u6c5d\u63d6\u3002\u201d \u3000\u3000\u201c\u5cb3\u5c11\u4fdd\u53cd\u9a73\uff1a\u5982\u679c\u662f\u4e32\u80fd\u8c0b\u53cd\uff0c\u5c82\u6709\u4e66\u4fe1\u5f80\u8fd8\u4e4b\u7406\uff1f\u800c\u4e14\u5982\u6709\u6b64\u610f\uff0c\u4f55\u4e0d\u53d1\u52a8\u65bc\u6731\u4ed9 \u9547\u5927\u6377\u4e4b\u5f79\uff1f\u90a3\u65f6\u672c\u4eba\u624b\u63e1\u91cd\u5175\uff0c\u6cb3\u5317\u4e49\u519b\u7eb7\u7eb7\u54cd\u5e94\uff0c\u82e5\u8981\u9020\u53cd\uff0c\u53ea\u987b\u63d0\u51fa\u8083\u6e05\u541b\u4fa7\u7684\u53e3 \u53f7\uff0c\u5c82\u4e0d\u4e8b\u534a\u529f\u500d\uff1f\u7136\u671d\u5ef7\u9881\u9886\u9000\u5175\uff0c\u98de\u5373\u5949\u547d\u552f\u8c28\uff0c\u9000\u56de\u4e34\u5b89\u3002\u98de\u82e5\u6709\u5f02\u5fc3\uff0c\u600e\u80fd\u505a\u51fa\u8fd9 \u79cd\u81ea\u6295\u7f57\u7f51\u7684\u8822\u4e8b\uff1f \u3000\u3000\u201c\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u9a73\u5f97\u6709\u7406\u554a\uff01\u201d \u3000\u3000\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u79e6\u6867\u8fd9\u73ed\u722a\u7259\uff0c\u624d\u4e0d\u7ba1\u4f60\u6709\u7406\u65e0\u7406\u5462\u3002\u5468\u4e09\u754f\u8fa8\u4e0d\u8fc7\u5cb3\u5c11\u4fdd\u3002\u53c8\u7ed9\u4ed6\u634f \u9020\u4e00\u6761\u7f6a\u540d\uff0c\u8fd9\u6761\u7f6a\u540d\uff0c\u66f4\u7b11\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5468\u4e09\u754f\u8bf4\uff1a\u201c\u5cb3\u98de\uff0c\u4f60\u662f\u4e09\u5341\u4e8c\u5c81\u90a3\u5e74\u505a\u8282\u5ea6\u4f7f\u7684\uff08\u5b8b\u4ee3\u8282\u5ea6\u4f7f\u76f8\u5f53\u4ece\u8fd1\u4ee3\u517c\u7ba1\u884c\u653f\u7684 \u4e00\u4e2a\u5927\u519b\u533a\u53f8\u4ee4\u957f\u5b98\uff09\uff0c\u4f60\u66fe\u5411\u4eba\u5938\u8000\uff1a\u201c\u4e09\u5341\u4e8c\u5c81\u4e0a\u5efa\u8282\uff0c\u81ea\u53e4\u5c11\u6709\u3002\u2019\u4f60\u53ef\u77e5\u9053\u592a\u7956\u7687 \u5e1d\uff08\u8d75\u5321\u80e4\uff09\u4e5f\u662f\u4e09\u5341\u4e8c\u5c81\u505a\u4e86\u8282\u5ea6\u4f7f\u7684\u3002\u6b64\u8a00\u50ed\u8d8a\u72c2\u6096\uff0c\u81ea\u6bd4\u592a\u7956\uff0c\u4e0e\u8c0b\u53cd\u4f55\u5f02\uff1f\u201d \u3000\u3000\u201c\u79e6\u6867\u6d3e\u6765\u542c\u5ba1\u7684\u4f55\u94f8\u5728\u65c1\u51b7\u51b7\u63d2\u8bdd\uff0c\u8fd9\u8bdd\u6709\u597d\u591a\u4eba\u542c\u89c1\uff0c\u5f20\u5baa\u90fd\u5df2\u62db\u8ba4\u4e86\u3002\u4f46\u5f20\u5baa\u65e9 \u5df2\u88ab\u9177\u5211\u62f7\u6253\u3002\u5728\u72f1\u4e2d\u5944\u5944\u5f85\u6bd9\u4e86\u7684\u3002\u83ab\u8bf4\u4ed6\u6839\u672c\u5c31\u4e0d\u80fd\u51fa\u5ead\u5bf9\u8d28\uff0c\u5373\u4f7f\u80fd\u591f\u51fa\u5ead\uff0c\u53ea\u6015\u4e5f \u6ca1\u6709\u8bf4\u8bdd\u7684\u6c14\u529b\u3002 \u3000\u3000\u201c\u5cb3\u5c11\u4fdd\u53ea\u80fd\u51b7\u7b11\uff1a\u201c\u6b32\u52a0\u4e4b\u7f6a\uff0c\u4f55\u60a3\u65e0\u8f9e\uff1f\u2019\u201c\u6700\u540e\u4ed6\u4eec\u8981\u5ba3\u5224\u4e86\uff0c\u5728\u5ba3\u5e03\u4e4b\u524d\uff0c\u5faa \u4f8b\u8981\u95ee\u4e00\u53e5\uff1a\u201c\u5cb3\u98de\uff0c\u4f60\u8fd8\u6709\u4f55\u8bdd\u8bf4\uff1f\u2019\u56db\u4e2a\u4eba\u4e00\u9f50\u559d\u95ee\u3002 \u3000\u3000\u201c\u5cb3\u5c11\u4fdd\u4e00\u8a00\u4e0d\u53d1\uff0c\u7a81\u7136\u9664\u53bb\u51a0\u5e26\uff0c\u5378\u4e0b\u888d\u670d\uff0c\u8f6c\u8eab\u5411\u5916\uff0c\u80cc\u5bf9\u516c\u6848\uff0c\u63b7\u5730\u6709\u58f0\u8bf4\u9053\uff1a \u201c\u8bf8\u516c\u8bf7\u770b\u5cb3\u98de\u80cc\u4e0a\u5148\u6bcd\u624b\u523a\u7684\u8fd9\u56db\u4e2a\u5b57\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u6731\u7ea2\u7684\u9488\u8ff9\uff0c\u5927\u4e66\uff1a\u201c\u7cbe\u5fe0\u62a5\u56fd\u2019\u56db\u4e2a\u5b57\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u62bd\u6ce3\uff0c\u6a80\u9053\u6210\u4e5f\u7ed9\u611f\u52a8\u5f97\u4f4e\u4e0b\u5934\u4e3a\u5cb3\u98de\u9ed8\u54c0\u3002 \u3000\u3000\u6c89\u9ed8\u4e86\u4e00\u9635\uff0c\u5f20\u96ea\u6ce2\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u5c31\u8fd9\u6837\u7ed9\u4eba\u5bb3\u6b7b\u4e86\u4e48\uff1f\u6ca1\u6709\u4eba\u8981\u6551\u4ed6\u4e48\uff1f\u90a3\u65f6 \u4ed6\u7684\u9a6c\u524d\u5f20\u4fdd\u3001\u9a6c\u540e\u738b\u6839\u8fd9\u4e24\u4e2a\u4eba\u53c8\u600e\u4e48\u6837\uff1f\u5f20\u4fdd\u53ef\u662f\u6211\u7684\u7237\u7237\u554a\uff01\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u738b\u6a2a\u5728\u5cb3\u5c11\u4fdd\u88ab\u6355\u4e4b\u524d\u5df2\u6218\u6b7b\u4e86\u3002\u6211\u7684\u7236\u4eb2\u5219\u6b63\u5728\u4e34\u5b89\u8bbe\u6cd5\u8425\u6551\u4e3b\u516c\u3002\u201d \u3000\u3000\u770b\u5b88\u5cb3\u5c11\u4fdd\u7684\u76d1\u72f1\u5b98\u502a\u5b8c\u662f\u4eba\u5fe0\u4e49\u4e4b\u58eb\uff0c\u6211\u7239\u548c\u53e6\u4e00\u4f4d\u5cb3\u5c11\u4fdd\u7684\u5fc3\u8179\u5c06\u519b\u540d\u53eb\u65bd\u5168\u7684\u548c \u4ed6\u8054\u7edc\u4e0a\u4e86\u3002\u4e00\u665a\u5077\u5165\u76d1\u7262\uff0c\u502a\u5b8c\u7b54\u5e94\u727a\u7272\u81ea\u5df1\uff0c\u653e\u5cb3\u5c11\u4fdd\u9003\u8d70\u3002 \u3000\u3000\u201c\u4f46\u5cb3\u5c11\u4fdd\u4e0d\u80af\u8d70\uff0c\u4ed6\u6b7b\u4e5f\u8981\u505a\u4e2a\u5fe0\u81e3\u3002\u6211\u7239\u5c61\u529d\u5c11\u4fdd\u90fd\u4e0d\u80af\u542c\u3002\u6211\u7239\u6ca1\u6cd5\u3002\u6700\u540e\u4ed6\u3001 \u4ed6\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u7237\u7237\uff0c\u4ed6\uff0c\u4ed6\u600e\u6837\uff1f\u201d \u3000\u3000\u5f20\u708e\u773c\u6cea\u593a\u5321\u800c\u51fa\uff0c\u5636\u54d1\u7740\u58f0\u97f3\u9053\uff1a\u201c\u6211\u7239\u8bf4\uff0c\u2018\u5c11\u4fdd\uff0c\u4f60\u4e0d\u80af\u8d70\uff0c\u90a3\u4e48\u53ea\u6709\u5c0f\u4eba\u5148 \u8d70\uff0c\u66ff\u4f60\u5f00\u8def\u4e86\u3002\u2019\u8bf4\u7f62\uff0c\u4ed6\u8eab\u5df2\u8dc3\u8d77\uff0c\u5411\u7262\u623f\u7684\u77f3\u5899\u4e0a\u4e00\u5934\u649e\u53bb\uff0c\u767b\u65f6\u8111\u6d46\u8fdb\u88c2\uff0c\u6b7b \u4e86\uff01\u201d\u5f20\u96ea\u6ce2\u5446\u4e86\uff0c\u996e\u6ce3\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u539f\u6765\u4f60\u8eab\u8d1f\u56fd\u4ec7\u5bb6\u6068\uff0c\u6211\u4e00\u76f4\u4e0d\u77e5\u3002\u201d \u3000\u3000\u5f20\u708e\u559d\u4e86\u4e24\u676f\u9152\uff0c\u52c9\u5f3a\u4f7f\u81ea\u5df1\u9547\u9759\u4e0b\u6765\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u665a\uff0c\u79e6\u6867\u6d3e\u4f55\u94f8\u6765\u76d1\u72f1\u89c1\u72f1 \u5b98\u502a\u5b8c\uff0c\u95ee\u502a\u5b8c\u9053\uff1a\u201c\u8fd9\u72f1\u4e2d\u4f55\u5904\u6709\u907f\u9759\u7684\u7a7a\u5730\uff1f\u201c\u502a\u5b8c\u83ab\u540d\u5176\u5999\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6709 \u5ea7\u98ce\u6ce2\u4ead\uff0c\u90a3\u91cc\u56db\u9762\u60ac\u7a7a\uff0c\u6700\u662f\u50fb\u9759\u3002\u4e0d\u77e5\u5927\u4eba\u8981\u4f5c\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u770b\u7239\u7239\u795e\u8272\uff0c\u5df2\u77e5\u5b9a\u7136\u4e0d\u662f\u597d\u4e8b\uff0c\u5979\u5fc3\u91cc\u5728\u53d1\u6296\uff0c\u63e1\u7740\u5f20\u708e\u7684\u624b\u3002 \u3000\u3000\u5f20\u708e\u7ee7\u7eed\u8bb2\u8ff0\uff1a\u201c\u90a3\u4f55\u94f8\u51b7\u773c\u671b\u7740\u502a\u5b8c\uff0c\u8bf4\u9053\uff1a\u201c\u5949\u4e1e\u76f8\u94a7\u8c15\uff0c\u4eca\u665a\u5c31\u5728\u8fd9\u72f1\u4e2d\u5904\u51b3\u5cb3 \u98de\u7236\u5b50\u4e0e\u5f20\u5baa\u4e09\u4eba\u3002\u4f60\u5feb\u628a\u4ed6\u4eec\u62bc\u5230\u98ce\u6ce2\u4ead\u7b49\u5f85\u5904\u51b3\uff01 \u3000\u3000\u2019\u539f\u6765\u79e6\u6867\u662f\u6015\u516c\u5f00\u5904\u51b3\u5cb3\u5c11\u4fdd\u4f1a\u5f15\u8d77\u516c\u6124\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u52ab\u6cd5\u573a\u7684\u4e8b\u60c5\u53d1\u751f\uff0c\u6240\u4ee5\u8981\u79d8 \u5bc6\u5904\u51b3\uff0c\u4e0d\u8ba9\u5916\u4eba\u77e5\u9053\u3002 \u3000\u3000\u201c\u4f55\u94f8\u5949\u4e86\u79e6\u6867\u4e4b\u547d\uff0c\u5728\u5904\u51b3\u5cb3\u5c11\u4fdd\u4e4b\u524d\uff0c\u8fd8\u8981\u4eba\u7b7e\u4e00\u5f20\u4f9b\u72b6\uff0c\u4ee5\u4fbf\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u5cb3\u5c11\u4fdd\u9053\uff1a\u201c\u597d\uff0c\u6211\u5199\u2019\u3002\u4ed6\u63d0\u8d77\u7b14\u6765\uff0c\u5199\u4e86\u516b\u4e2a\u5b57\uff1a\u5929\u65e5\u662d\u662d\uff0c\u5929\u65e5\u662d\u662d\uff01\u5cb3\u5c11\u4fdd\u6700 \u540e\u7684\u51e0\u53e5\u8bdd\u662f\u5bf9\u5f20\u5baa\u8bf4\u7684\uff0c\u4ed6\u8bf4\uff1a\u201c\u5f20\u5baa\uff0c\u53ef\u501f\u4f60\u4e00\u8eab\u795e\u52c7\uff0c\u4e5f\u966a\u6211\u6b7b\u5728\u8fd9\u91cc\u3002\u2019\u201c\u5f20\u5baa \u9053\uff1a\u201c\u5143\u5e05\u76d6\u4e16\u5c06\u624d\uff0c\u5c1a\u4e14\u65e0\u6028\uff0c\u5c0f\u5a7f\u5339\u592b\u4e4b\u52c7\uff0c\u80fd\u591f\u751f\u6b7b\u8ffd\u968f\u5143\u5e05\uff0c\u6b7b\u53c8\u4f55\u8f9e\uff1f\u9057\u61be\u7684\u53ea \u662f\u4e0d\u80fd\u751f\u62a5\u6b64\u4ec7\uff0c\u4f46\u613f\u6b7b\u540e\u5316\u4e3a\u5389\u9b3c\uff0c\u593a\u79e6\u8d3c\u4e4b\u9b44\uff01\u2019\u201c\u5cb3\u5c11\u4fdd\u9053\uff1a\u201c\u4f60\u53c8\u9519\u4e86\uff0c\u5373\u4f7f\u5316\u4e3a \u540e\u9b3c\uff0c\u4e5f\u5f53\u5148\u53bb\u6740\u80e1\u864f\uff0c\u6551\u767e\u59d3\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u8bdd\u90fd\u662f\u502a\u5b8c\u540e\u6765\u4f20\u51fa\u6765\u7684\u3002\u96ea\u513f\uff0c\u8bf7\u4f60\u7262\u8bb0\uff0c\u5cb3\u5c11\u4fdd\u6700\u540e\u7684\u9057\u8a00\u5c31\u662f\u6740\u80e1\u864f\uff0c\u6551 \u767e\u59d3\uff01\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u4e09 \u56de\u3000\u79bb\u5947\u8eab\u4e16 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u4e09 \u56de\u3000\u79bb\u5947\u8eab\u4e16 \u3000\u3000\u5f20\u96ea\u6ce2\u7684\u5634\u89d2\u5728\u62bd\u6410\uff0c\u4f3c\u4e4e\u60f3\u8981\u8bf4\u8bdd\uff0c\u5374\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5979\u7684\u5fc3\u91cc\u5728\u62bd\u6410\uff0c\u56e0\u4e3a\u5f20\u708e\u7684\u5f26\u5916\u4e4b\u97f3\u662f\u592a\u660e\u663e\u4e86\uff0c\u5979\u5f53\u7136\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u5979\u51c4\u82e6\u7684\u76ee\u5149\u843d\u5728\u4e08\u592b\u8eab\u4e0a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u4ed6\u4e0d\u662f\u80e1\u864f\uff0c\u66f4\u4e0d\u662f\u5cb3\u5c11\u4fdd\u6240\u8981\u6740\u7684\u80e1 \u864f\u3002\u4ed6\u662f\u6211\u7684\u6210\u54e5\uff0c\u662f\u6211\u7518\u613f\u751f\u6b7b\u4e0e\u5171\u7684\u6210\u54e5\uff01\u201d\u592b\u59bb\u672c\u662f\u5fc3\u610f\u76f8\u901a\uff0c\u4f46\u8fd9\u6b21\u6a80\u9053\u6210\u5374\u597d\u4f3c \u6ca1\u6709\u660e\u767d\u59bb\u5b50\u773c\u5149\u4e2d\u7684\u542b\u610f\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u5cb3\u98de\u7684\u6545\u4e8b\uff0c\u4ed6\u88ab\u8fd9\u4e2a\u611f\u4eba\u6545\u4e8b\u5b8c\u5168\u5438\u5f15\u4e86\u3002\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u628a\u201c\u80e1 \u864f\u201d\u4e0e\u81ea\u5df1\u7684\u8054\u60f3\u5728\u4e00\u8d77\u3002\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201d\u8fd8\u6709\u4ec0\u4e48\u600e\u6837\uff1f\u201d\u6a80\u9053\u6210\u9053\uff1a\u201c\u96be\u9053\u5cb3\u98de\u5c31\uff0c \u5c31\u8fd9\u6837\u2026\u2026\u201d\u88ab\u4eba\u5bb3\u6b7b\u8fd9\u51e0\u4e2a\u5b57\u4ed6\u4e0d\u5fcd\u8bf4\u51fa\uff24\u6765\uff0c\u201c\u4e5f\u6ca1\u4eba\u7ed9\u4ed6\u4f38\u51a4\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u4f38\u51a4\uff1f\u97e9\u4e16\u5fe0\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5c31\u7ed9\u7f62\u4e86\u5b98\uff0c\u67a2\u5bc6\u4f7f\u505a\u4e0d\u6210\u4e86\u3002\u8fde\u97e9\u4e16\u5fe0\u90fd\u9669 \u53d7\u7275\u8fde\uff0c\u8fd8\u6709\u8c01\u80fd\u4e3a\u5cb3\u5c11\u4fdd\u4f38\u51a4\uff1f\u8fd8\u6709\u8c01\u6562\u4e3a\u5cb3\u5c11\u4fdd\u4f38\u51a4\uff1f\u201c\u540e\u6765\u600e\u6837\uff1f\u8fd8\u80fd\u6709\u4ec0\u4e48\u600e\u6837\uff1f \u5f20\u5baa\u548c\u5cb3\u4e91\u5c31\u5728\u98ce\u6ce2\u4ead\u4e0a\u88ab\u4ed6\u4eec\u79c1\u5211\u5904\u51b3\uff0c\u603b\u7b97\u4ed6\u4eec\u5bf9\u5cb3\u5c11\u4fdd\u2018\u5ba2\u6c14\u2019\u4e00\u4e9b\uff0c\u2018\u6069\u8d50\u2019\u5cb3\u5c11 \u4fdd\u5168\u5c38\uff0c\u5cb3\u5c11\u4fdd\u662f\u7ed9\u4ed6\u4eec\u7528\u6bd2\u9152\u5bb3\u6b7b\u7684\uff01\u8c0b\u53cd\u7684\u7f6a\u540d\u662f\u8981\u6ee1\u95e8\u6284\u65a9\u7684\uff0c\u83ab\u8bf4\u4f38\u51a4\u4e86\uff0c\u5cb3\u5c11\u4fdd \u7684\u5bb6\u5c5e\u90fd\u4e0d\u80fd\u4fdd\u5168\uff01\u201c\u5cb3\u4e91\u6b7b\u7684\u90a3\u5e74\u53ea\u6709\u4e8c\u5341\u4e09\u5c81\u3002\u5c1a\u672a\u5a36\u59bb\u3002\u5f20\u5baa\u5219\u662f\u6709\u59bb\u5b50\u548c\u5973\u513f\u7684\u3002 \u4ed6\u7684\u59bb\u5b50\u5c31\u662f\u5cb3\u5c11\u4fdd\u7684\u5973\u513f\uff0c\u79e6\u6867\u5f53\u7136\u66f4\u52a0\u4e0d\u80fd\u653e\u8fc7\u5979\u4eec\u6bcd\u5973\u3002 \u3000\u3000\u5e78\u597d\u65bd\u5168\u62a5\u8baf\u5f88\u5feb\uff0c\u90a3\u4e00\u665a\u4ed6\u548c\u5f20\u4fdd\u53bb\u529d\u5cb3\u5c11\u4fdd\u9003\u72f1\uff0c\u5cb3\u5c11\u4fdd\u4e0d\u4ece\uff0c\u5f20\u4fdd\u81ea\u6740\u6b89\u4e3b\uff0c\u65bd \u5168\u4fbf\u7acb\u5373\u9003\u51fa\u4e34\u5b89\uff0c\u53bb\u7ed9\u5f20\u5baa\u7684\u59bb\u5b50\u62a5\u8baf\u3002 \u3000\u3000\u201c\u5f20\u592b\u4eba\u4e0d\u80af\u9003\u79bb\uff0c\u5979\u628a\u521a\u6ee1\u5468\u5c81\u7684\u5973\u513f\u4ea4\u7ed9\u4e00\u4e2a\u5979\u8ba4\u4e3a\u6700\u53ef\u9760\u7684\u4ec6\u4eba\uff0c\u7136\u540e\u5979\u4e5f\u81ea\u6740 \u6b89\u592b\u4e86\u3002\u8fd9\u4e2a\u4ec6\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u5c31\u662f\u5f20\u4fdd\u7684\u513f\u5b50\uff0c\u4ea6\u5373\u662f\u6211\uff01\u201c\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u5341\u5206\u4f4e\u6c89\uff0c\u542c\u5728 \u5f20\u96ea\u6ce2\u8033\u4e2d\uff0c\u5374\u597d\u50cf\u70b8\u54cd\u7126\u96f7\uff0c\u5979\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u5a74\u662f\uff0c\u662f\u2014\u2014\u201d\u5f20\u708e\u5636\u54d1 \u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u660e\u767d\u5417\uff0c\u5cb3\u5c11\u4fdd\u5c31\u662f\u4f60\u7684\u5916\u516c\uff0c\u4f60\u7684\u6bcd\u4eb2\u662f\u5cb3\u826e\u74f6\uff0c\u4f60\u7684\u7236\u4eb2\u662f\u5f20 \u5baa\uff01\u79e6\u6867\u6743\u52bf\u6ed4\u5929\uff0c\u83ab\u8bf4\u4f60\u6b66\u529f\u5e73\u5e38\uff0c\u518d\u597d\u5341\u500d\u4e5f\u662f\u62a5\u4e0d\u4e86\u8fd9\u4e2a\u4ec7\u7684\u3002\u7ed9\u4f60\u77e5\u9053\u53cd\u800c\u5bb3\u4e86 \u4f60\uff0c\u6240\u4ee5\u6211\u4e00\u76f4\u4e0d\u6562\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5446\u82e5\u6728\u9e21\uff0c\u5fc3\u4e2d\u5982\u53d7\u5200\u5272\u3002 \u3000\u3000\u4f46\u73b0\u5728\u8fd8\u4e0d\u662f\u5979\u60b2\u75db\u7684\u65f6\u5019\uff01 \u3000\u3000\u6b7b\u8005\u5df2\u77e3\uff0c\u751f\u8005\u4f55\u8f9c\uff0c\u4e5f\u8981\u53d7\u5230\u7275\u7d2f\uff1f\u5916\u516c\u548c\u7236\u6bcd\u7684\u60e8\u6b7b\u5f53\u7136\u4ee4\u5979\u5fc3\u4f24\u4e4b\u6781\uff0c\u4f46\u4e08\u592b\u66f4 \u662f\u5979\u7684\u4eb2\u4eba\uff01 \u3000\u3000\u5916\u516c\u5979\u6ca1\u89c1\u8fc7\u9762\uff0c\u7236\u4eb2\u5979\u6709\u6ca1\u6709\u89c1\u8fc7\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002\uff08\u5979\u51fa\u751f\u4e4b\u540e\u90a3\u5e74\uff0c\u7236\u4eb2\u662f\u5426 \u56de\u8fc7\u5bb6\u91cc\u3002\u5f20\u4fdd\u4e0d\u8bf4\uff0c\u5979\u7684\u8bb0\u5fc6\u5c31\u53ea\u80fd\u662f\u4e00\u7247\u7a7a\u767d\u3002\uff09 \u3000\u3000\u5916\u516c\u548c\u6bcd\u4eb2\uff0c\u53ea\u6709\u6bcd\u4eb2\u662f\u66fe\u7ecf\u548c\u5979\u540c\u5728\u4e00\u8d77\u7684\u3002\u4f46\u5468\u5c81\u591a\u4e00\u70b9\u7684\u5b69\u5b50\u80fd\u591f\u77e5\u9053\u4ec0\u4e48\u5462\uff1f \u6bcd\u4eb2\u4e5f\u65e9\u5df2\u5728\u5979\u7684\u8bb0\u5fc6\u4e2d\u6a21\u7cca\u4e86\u3002 \u3000\u3000\u4f46\u4e08\u592b\u5374\u662f\u4ece\u5c0f\u548c\u5979\u5728\u4e00\u76f4\u957f\u5927\u7684\uff0c\u5341\u591a\u5e74\u6765\uff0c\u53ef\u8bf4\u662f\u548c\u5979\u5f62\u5f71\u4e0d\u79bb\u3002 \u3000\u3000\u5916\u516c\u548c\u7236\u6bcd\u90fd\u5df2\u6b7b\u4e86\uff0c\u4e08\u592b\u5219\u662f\u6d3b\u751f\u751f\u5979\u7684\u773c\u524d\u3002 \u3000\u3000\u6709\u63a7\u5979\u7684\u201c\u7239\u7239\u201d\u5374\u8981\u628a\u5979\u7684\u4e08\u592b\u7f6e\u4e4b\u6b7b\u5730\uff01 \u3000\u3000\u8fd8\u6709\u516c\u516c\uff0c\u516c\u516c\u867d\u7136\u4e0d\u53ca\u4e08\u592b\u4e4b\u4eb2\uff0c\u4f46\u8fd9\u4e48\u591a\u5e74\uff0c\u516c\u516c\u5bf9\u5979\u4e5f\u662f\u5341\u5206\u75bc\u7231\u7684\u3002\u800c\u73b0\u5728\uff0c \u516c\u516c\u5c31\u5feb\u8981\u6b7b\u5728\u5979\u7684\u9762\u524d\u4e86\u3002\u5979\u5df2\u7ecf\u9884\u6599\u5230\u7239\u7239\u5c31\u8981\u8bf4\u5230\u773c\u524d\u4e4b\u4e8b\u4e86\uff0c\u5fc3\u5ff5\u672a\u5df1\uff0c\u679c\u7136\u4fbf\u542c \u5f97\u5f20\u708e\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\u4eec\uff0c\u73b0\u5728\u4f60\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5979\u4e00\u7247\u8ff7\u832b\uff0c\u4f3c\u4e4e\u660e\u767d\u3002\u660e\u767d\u7684\u662f\u5979\u7239\u7239\u7684\u60f3\u6cd5\u3002\u4e0d\u660e\u767d\u7684\u662f\u7239\u7239\u8fd9\u6837\u505a\u8be5\u662f\u4e0d\u8be5\uff1f\u5979 \u7ec8\u4e8e\u9f13\u8d77\u52c7\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u660e\u767d\uff01\u201d \u3000\u3000\u5f20\u708e\u76b1\u8d77\u7709\u5934\uff0c\u597d\u50cf\u6709\u70b9\u607c\u6012\u4e86\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u4e0d\u660e\u767d\uff1f\u4f60\u7684\u5916\u516c\uff0c\u4f60\u7684\u7239\u7239\uff0c\u4e00\u751f \u548c\u91d1\u4eba\u6253\u4ed7\u3002\u4f60\u600e\u80fd\u5ac1\u7ed9\u4e00\u4e2a\u91d1\u56fd\u7684\u5c0f\u738b\u7237\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4f4e\u4e0b\u5934\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4e0d\u5ac1\u6211\u4e5f\u5df2\u7ecf\u5ac1\u4e86\u3002\u201d \u3000\u3000\u5f20\u708e\u77aa\u7740\u5979\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u540d\u5b57\u7684\u7531\u6765\uff1f\u201c\u5f20\u96ea\u6ce2\u907f\u5f00\u4ed6\u7684\u76ee\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u7239\u7239 \u8bf4\u7ed9\u6211\u542c\u3002\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u542c\u7740\u3002\u8fd9\u4e2a\u540d\u5b57\uff0c\u662f\u4f60\u7684\u6bcd\u4eb2\u628a\u4f60\u4ea4\u7ed9\u6211\u7684\u65f6\u5019\uff0c\u4e3a\u4f60\u53d6\u7684\u3002\u4f60\u7684 \u5916\u516c\u548c\u7239\u7239\u5728\u98ce\u6ce2\u4ead\u9047\u5bb3\uff0c\u6240\u4ee5\u4f60\u7684\u540d\u5b57\u53eb\u505a\u96ea\u6ce2\u3002\u610f\u601d\u5c31\u662f\u8981\u4f60\u8bb0\u4f4f\u98ce\u6ce2\u4ead\u7684\u51a4\u72f1\uff0c\u8981\u4e3a \u5916\u516c\u548c\u751f\u8eab\u4e4b\u7236\u96ea\u51a4\u3002\u201d \u3000\u3000\u6a80\u9053\u6210\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8981\u96ea\u51a4\uff0c\u4f46\u8fd9\u7b14\u5e10\u5e94\u8be5\u7b97\u5728\u5b8b\u56fd\u7684\u7687\u5e1d\u548c\u79e6\u6867\u7684\u5934\u4e0a\u5427\u3002\u201d \u3000\u3000\u5f20\u708e\u559d\u9053\uff1a\u201c\u79e6\u6867\u662f\u4f60\u4eec\u7684\u5978\u7ec6\uff0c\u5cb3\u5c11\u4fdd\u82e5\u4e0d\u662f\u4e3a\u4e86\u6297\u91d1\uff0c\u4e5f\u4e0d\u4f1a\u88ab\u79e6\u6867\u5bb3\u6b7b\u3002\u5cb3\u5c11\u4fdd \u4e34\u7ec8\u7684\u5631\u5490\uff0c\u5c31\u662f\u8981\u6211\u4eec\u6740\u80e1\u864f\uff0c\u6551\u767e\u59d3\uff01\u201d \u3000\u3000\u6a80\u9053\u6210\u51b7\u7b11\u9053\uff1a\u201c\u91d1\u56fd\u7684\u4eba\u4e5f\u4e0d\u89c1\u5f97\u4e2a\u4e2a\u8be5\u6740\u4e86\u5427\uff1f\u201d\u5f20\u708e\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u91d1\u56fd\u7684\u666e \u901a\u767e\u59d3\uff0c\u662f\u91d1\u56fd\u7684\u8d1d\u52d2\u3001\u8d1d\u5b50\uff01\u6211\u548c\u96ea\u513f\u8bf4\u8bdd\uff0c\u4e0d\u8bb8\u4f60\u80e1\u626f\uff0c\u518d\u80e1\u626f\uff0c\u5148\u6253\u6b7b\u4f60\uff01\u201d\u5f20\u96ea\u6ce2 \u6321\u5728\u4e08\u592b\u8eab\u524d\uff0c\u5f20\u708e\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u62a4\u4f4f\u4ed6\u4eec\uff1f\u8bb0\u4f4f\uff0c\u4f60\u662f\u5cb3\u5c11\u4fdd\u7684\u5916\u5b59\u5973\u513f\uff01\u201d\u5f20\u96ea \u6ce2\u7684\u5fc3\u5df2\u7ecf\u788e\u4e86\uff0c\u832b\u7136\u53cd\u95ee\uff1a\u201c\u662f\u5cb3\u5c11\u4fdd\u7684\u5916\u5b59\u5973\u513f\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5f20\u708e\u4ea2\u58f0\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u53ea\u80fd\u628a\u4ed6\u4eec\u5f53\u4f5c\u654c\u3001\u4e0d\u80fd\u628a\u4ed6\u4eec\u5f53\u4f5c\u4eb2\u4eba\u4e86\uff01 \u3000\u3000\u5bf9\u5f85\u654c\u4eba\u5e94\u8be5\u600e\u6837\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u61c2\uff1f\u201d\u5f20\u96ea\u6ce2\u62bd\u564e\u9053\uff1a\u201c\u6211\u3001\u6211\u3001\u6211\u2026\u201d \u3000\u3000\u5f20\u708e\u5fc3\u91cc\u53f9\u6c14\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u7a0d\u5fae\u67d4\u548c\u4e00\u4e9b\uff1a\u201c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u3001\u6211\u6ca1\u6cd5\u5b50\u628a\u4ed6\u4eec\u5f53\u4f5c\u654c\u4eba\u3002\u4ed6\u4eec\u6ca1\u5bb3\u8fc7\u6c49\u4eba\uff0c\u4ed6\u4eec\u6ca1\u505a\u8fc7\u574f\u4e8b\uff0c\u4ed6\u4eec \u5bf9\u6211\u5f88\u597d\u3002\u201d \u3000\u3000\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u91d1\u56fd\u7684\u738b\u7237\u8fd8\u80fd\u662f\u597d\u4eba\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8fd9\u5341\u591a\u5e74\u6765\u4ed6\u4eec\u4e5f\u662f\u50cf\u54b1\u4eec\u4e00\u6837\uff0c\u5728\u8fd9\u5c71\u4e0a\u8fc7\u5e73\u9759\u65e5\u5b50\uff0c\u6253\u7684\u53ea\u662f\u91ce\u517d\u3002 \u7239\u7239\uff0c\u5f53\u521d\u4e5f\u662f\u4f60\u628a\u6211\u8bb8\u914d\u7ed9\u6210\u54e5\u7684\uff01\u201d \u3000\u3000\u5f20\u708e\u6376\u80f8\u9053\uff1a\u201c\u8981\u662f\u6211\u65e9\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\uff0c\u6211\u7109\u80fd\u94f8\u6b64\u5927\u9519\u3002\u4f46\u5982\u4eca\u65e2\u5df2\u77e5\u9053\uff0c\u4f60\u5c31\u4e0d\u8be5 \u4e3a\u513f\u5973\u4e4b\u60c5\u5fd8\u5bb6\u56fd\u4e4b\u6068\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6210\u54e5\u662f\u6211\u4e08\u592b\u3002\u6211\u53c8\u6ca1\u89c1\u8fc7\u4ed6\u505a\u8fc7\u574f\u4e8b\uff0c\u6211\u6068\u4e0d\u8d77\u6765\uff01 \u3000\u3000\u201d \u3000\u3000\u5f20\u708e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6ca1\u505a\u574f\u4e8b\uff1f\u4ed6\u8bbe\u6cd5\u548c\u54b1\u4eec\u4f4f\u5728\u4e00\u8d77\uff0c\u662f\u4f55\u5c45\u5fc3\uff1f\u4ed6\u628a\u4f60\u9a97\u5f97\u4f5c\u4ed6\u7684\u513f \u5ab3\u5987\uff0c\u6050\u6015\u5c31\u662f\u4e00\u4e2a\u9634\u8c0b\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5728\u54b1\u4eec\u4e4b\u524d\uff0c\u5c31\u6765\u5230\u8fd9\u91cc\u7684\u3002\u7239\u7239\uff0c\u4f60\u600e\u4ee5\u6000\u7591\u4ed6\u4eec\u662f\u65e9\u5df2\u77e5\u9053\u54b1 \u4eec\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u96ea\u513f\uff0c\u4f60\u4e0d\u61c2\u5f97\u4eba\u5fc3\u9669\u6076\u3002\u5f53\u5e74\uff0c\u6211\u4e3a\u4ec0\u4e48\u548c\u4f60\u8eb2\u4e0a\u8fd9\u5ea7\u8352\u5c71\u5462\uff0e\u56e0 \u4e3a\u6211\u4e0d\u6562\u4f4f\u5728\u5b8b\u56fd\u7684\u5730\u65b9\uff0c\u4e5f\u4e0d\u613f\u610f\u88ab\u91d1\u4eba\u7edf\u6cbb\uff0c\u5f53\u5e74\u8fd9\u5ea7\u8352\u5c71\u8fd8\u662f\u5728\u5b8b\u56fd\u7586\u754c\u4e4b\u5185\uff0c\u4f46\u5374 \u662f\u4e09\u4e0d\u7ba1\u5730\u5e26\uff0c\u6240\u4ee5\u6211\u53ea\u80fd\u9009\u62e9\u8fd9\u4e2a\u5730\u65b9\u907f\u96be\u3002\u5f53\u5e74\u8eb2\u4e0a\u8fd9\u5ea7\u8352\u5c71\u907f\u96be\u7684\u4eba\u867d\u4e0d\u5f88\u591a\u3002\u4e5f\u4e0d \u53ea\u54b1\u4eec\u4e00\u5bb6\u7684\u3002\u8fd9\u79cd\u60c5\u5f62\uff0c\u6599\u60f3\u4ed6\u4eec\u4e5f\u77e5\u9053\u7684\u3002\u201c\u4ed6\u4eec\u4e0d\u8fc7\u6bd4\u54b1\u4eec\u5148\u6765\u51e0\u4e2a\u6708\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f \u5148\u6765\u6b64\u5904\u4fa6\u5bdf\u7684\u5462\uff1f\u4fa6\u5bdf\u4e00\u65f6\u6ca1\u6709\u7ed3\u679c\uff0c\u4ed6\u4eec\u5c31\u7d22\u6027\u5b9a\u4e0b\u653e\u957f\u94b1\uff0c\u9493\u5927\u9c7c\u7684\u8ba1\u5212\uff0c\u7b49\u5f85\u54b1\u4eec \u4e0a\u94a9\u5462\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u53ea\u662f\u4f60\u7684\u731c\u60f3\u800c\u5df1\u3002\u516c\u516c\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u4ed6\u662f\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4f60\u7684\u6765\u5386 \u7684\u3002\u201d \u3000\u3000\u5f20\u708e\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u79f0\u4ed6\u516c\u516c\uff0c\u4f60\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff0c\u8fd8\u662f\u76f8\u4fe1\u6211\u7684\u8bdd\u3002\u5373\u4f7f\u521d\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u8fd8 \u4e0d\u77e5\u9053\u6211\u7684\u8eab\u4efd\uff0c\u4f46\u4ed6\u548c\u6211\u7ed3\u6210\u4eb2\u5bb6\uff0c\u90a3\u8fd8\u6709\u4e0d\u6253\u542c\u6211\u7684\u5e95\u7ec6\u4e4b\u7406\uff1f\u53ea\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u4ec0\u4e48\u65f6 \u5019\u77e5\u9053\u7f62\u4e86\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u4e00\u76f4\u9759\u542c\u4ed6\u4eec\u7236\u5973\u8fa9\u8bba\uff0c\u6b64\u65f6\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u8981\u662f\u4f60\u80af\u8bb2\u7406\u7684\u8bdd\uff0c\u6211\u5012\u60f3 \u591a\u8bf4\u51e0\u53e5\u3002\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u597d\u4f60\u8bf4\uff0c\u53cd\u6b63\u8bf4\u4ec0\u4e48\u6211\u4e5f\u4e0d\u4f1a\u9976\u4f60\uff0c\u4f60\u662f\u6b7b\u5b9a\u7684\u4e86\uff0c\u8ba9\u4f60\u591a\u8bf4\u51e0\u53e5\uff0c\u4e5f\u597d\u4ee4 \u4f60\u5fc3\u670d\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u6211\u4e0d\u5426\u8ba4\u4f60\u662f\u4e00\u6761\u597d\u6c49\uff0c\u4f46\u4f60\u4e5f\u672a\u514d\u81ea\u89c6\u8fc7\u9ad8\u4e86\u5427\uff1f\u201d\u5f20 \u708e\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u662f\u5f20\u5bb6\u7684\u4ec6\u4eba\uff0c\u4f60\u8fd9\u8bdd\u662f\u8ba5\u8bbd\u6211\u5462\u8fd8\u662f\u4e0d\u670d\u6c14\u6b7b\u5728\u6211\u7684\u624b \u4e0b\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u8bf4\u771f\u8bdd\uff0c\u4f60\u7684\u5fe0\u4e49\u884c\u4e3a\uff0c\u6211\u662f\u4ece\u5fc3\u5e95\u656c\u91cd\u4f60\u7684\u3002\u4f46\u4f9d\u4f60\u7684 \u8bf4\u6cd5\uff0c\u6211\u662f\u4e00\u4e2a\u73af\u5fc3\u80a0\u7684\u91d1\u56fd\u738b\u7237\uff0c\u8fd9\u6837\u7684\u4eba\uff0c\u53c8\u600e\u80af\u4e3a\u5f20\u5baa\u7684\u4e00\u4e2a\u4ec6\u4eba\u5728\u8352\u5c71\u6371\u82e6\u76f4\u516b \u5e74\uff1f\u4f60\u522b\u8bef\u4f1a\uff0c\u6211\u4e0d\u662f\u770b\u8f7b\u4f60\uff0c\u4f46\u4f9d\u4e16\u4fd7\u4e4b\u89c1\u548c\u4e00\u4e2a\u738b\u7237\u5e94\u6709\u7684\u60f3\u6cd5\uff0c\u6211\u7684\u8eab\u4efd\u4f3c\u4e4e\u662f\u548c\u4f60 \u6709\u9887\u5927\u8ddd\u79bb\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4ec6\u4eba\u3001\u4f46\u96ea\u513f\u53ef\u662f\u5cb3\u5c11\u4fdd\u7684\u5916\u5b59\u5973\u513f\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u522b\u6025\uff0c\u6211\u6b63\u8981\u8bf4\u5230\u8fd9\u70b9\u3002\u4ee5\u6211\u7684\u8eab\u4efd\uff0c\u5018\u82e5\u662f\u4e3a\u4e86\u8981\u5bb3\u5cb3\u5c11\u4fdd\u800c\u6371\u82e6\u90a3 \u8fd8\u8bf4\u5f97\u8fc7\u53bb\uff0c\u5cb3\u5c11\u4fdd\u7684\u5916\u5b59\u5973\u4f3c\u4e4e\u8fd8\u4e0d\u503c\u5f97\u6211\u4e3a\u5979\u629b\u5f03\u8363\u534e\u5bcc\u5458\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u8fd8\u6709\u8bb8\u591a\u65e7\u90e8\u5728\u751f\uff0c\u4f60\u7684\u513f\u5b50\u5a36\u4e86\u4ed6\u7684\u5b59\u5973\u513f\uff0c\u53ef\u4ee5 \u7528\u6765\u7b3c\u7ec8\u4ed6\u7684\u65e7\u90e8\u3002\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5979\u505a\u6211\u513f\u5ab3\u4e5f\u6709\u4e03\u5e74\u4e86\uff0c\u6211\u82e5\u6709\u6b64\u5fc3\uff0c\u4e3a\u4f55\u76f4\u5230\u5982\u4eca\u8fd8 \u7559\u5728\u8352\u5c71\uff1f\u201d\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u5979\u8fd8\u6709\u6211\u8fd9\u4e48\u4e00\u4e2a\u7239\u7239\uff0c\u53ea\u8981\u6211\u4e00\u5929\u8bdd\u7740\uff0c\u4f60\u4eec\u5c31\u4f11 \u60f3\u5229\u7528\u5979\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u90a3\u4e48\u6211\u4e3a\u4f55\u4e0d\u65e9\u65e5\u5bb3\u6b7b\u4f60\u5462\uff1f\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u8fd9\u6837\u7b28\u8fde\u8fd9\u70b9\u90fd\u60f3\u4e0d\u5230 \u5417\uff1f\u4f60\u7684\u6b66\u529f\u6bd4\u6211\u5f31\uff0c\u6211\u53ef\u4ee5\u5b8c\u5168\u7792\u8fc7\u96ea\u513f\uff0c\u53eb\u4f60\u8eab\u4e0a\u6ca1\u5e26\u534a\u70b9\u4f24\u75d5\u5c31\u5c06\u4f60\u5bb3\u6b7b\u3002\u201d\u5f20\u708e\u7a92 \u4e86\u4e00\u7a92\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u53ef\u80fd\u662f\u4f60\u8ba4\u4e3a\u65f6\u673a\u672a\u5230\u5427\uff1f\u603b\u800c\u8a00\u4e4b\u3002\u4f60\u662f\u91d1\u56fd\u7684\u738b\u7237\u6211\u5c31\u8981\u6740\u4f60\uff01 \u201c\u8bdd\u867d\u5982\u6b64\uff0c\u663e\u7136\u4ed6\u5bf9\u81ea\u5df1\u7684\u5224\u65ad\u4ea6\u5df2\u6709\u70b9\u6000\u7591\u4e86\u3002\u7ed9\u5f20\u96ea\u6ce2\u7684\u611f\u89c9\u662f\uff0c\u4ed6\u53ea\u80fd\u6267\u7740\u516c\u516c\u662f \u91d1\u56fd\u738b\u7237\u8fd9\u70b9\u201c\u7406\u7531\u201d\uff0c\u522b\u7684\u5c31\u4e0d\u6562\u548c\u516c\u516c\u8bb2\u7406\u4e86\u3002\u690d\u9053\u6210\u53eb\u9053\uff1a\u201c\u4f60\u600e\u80fd\u8fd9\u6837\u86ee\u4e0d\u8bb2\u7406\uff0c \u8fd9\u5341\u591a\u5e74\u6765\uff0c\u6211\u4eec\u548c\u4f60\u8fc7\u7684\u90fd\u662f\u4e00\u6837\u65e5\u5b50\uff0c\u6211\u7239\u7239\u65e9\u5df2\u4e0d\u662f\u91d1\u56fd\u7684\u8d1d\u52d2\u4e86\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u5ffd\u9053\uff1a\u201c\u5b69\u513f\uff0c\u4f60\u4e0d\u8981\u9a82\u4ed6\uff0c\u6211\u53ea\u662f\u4e3a\u4ed6\u53ef\u60dc\uff01\u201d\u5f20\u708e\u8bc1\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u4f60\u4e3a\u6211 \u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u53ef\u501f\u4f60\u5728\u5cb3\u5c11\u4fdd\u751f\u524d\uff0c\u6ca1\u6709\u673a\u4f1a\u53d7\u8fc7\u4ed6\u7684\u6559\u5bfc\u3002\u201d \u3000\u3000\u5f20\u708e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u662f\u9075\u5949\u5cb3\u5c11\u4fdd\u7684\u9057\u8bad\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u53e3\u53e3\u58f0\u58f0\u8bf4\u662f\u9075\u5949\u5cb3\u5c11\u4fdd\u7684\u9057\u8bad\uff0c\u5cb3\u5c11\u4fdd\u82e5\u662f\u6cc9\u4e0b\u6709\u77e5\uff0c\u4e5f\u4f1a\u4ece\u68fa\u6750\u91cc \u8df3\u51fa\u6765\u6253\u4f60\u7684\u8033\u5149\uff01\u201d\u5f20\u708e\u5927\u6012\u9053\uff1a\u201c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u5bf9\u6211\u4fae\u8fb1\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u7684\u9057\u8bad\u53eb\u4f60\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u5728\u4e71\u6740\u4eba\u7684\u4e48\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u5cb3\u5c11\u4fdd\u5728\u6731\u4ed9 \u9547\u5927\u6377\u4e4b\u540e\uff0c\u66fe\u53d1\u8fc7\u4e00\u9053\u6a84\u6587\u3002\u6a84\u6587\u8bf4\u4ed6\u5c06\u6e21\u6cb3\u6536\u590d\u5931\u5730\uff0c\u53eb\u91d1\u56fd\u7684\u8001\u767e\u59d3\u4e0d\u8981\u9644\u4ece\u5140\u672f\u4e0e \u4ed6\u4e3a\u654c\uff0c\u6a84\u6587\u8bf4\u53ea\u987b\u9075\u4ece\u4ed6\u7684\u53f7\u4ee4\uff0c\u4ed6\u5bf9\u91d1\u5165\u6c49\u4eba\u90fd\u662f\u4e00\u89c6\u540c\u4ec1\u3002\u5728\u6731\u4ed9\u9547\u5927\u6377\u4e4b\u524d\uff0c\u4ed6\u53c8 \u66fe\u4e0a\u8fc7\u4e00\u9053\u594f\u7ae0\uff0c\u662f\u7ed9\u5b8b\u56fd\u7684\u7687\u5e1d\u8d75\u6784\u7684\uff0c\u4ed6\u53cd\u5bf9\u8d75\u6784\u548c\u79e6\u6867\u5411\u91d1\u56fd\u6c42\u548c\uff0c\u4f46\u4e5f\u8bf4\u660e\u4ed6\u5e76\u4e0d \u662f\u53cd\u5bf9\u548c\u5e73\uff0c\u53ea\u662f\u8981\u5728\u5e73\u7b49\u7684\u5730\u4f4d\u5abe\u548c\u3002\u53ef\u89c1\u5cb3\u5c11\u4fdd\u4e5f\u5e76\u975e\u8981\u4e0e\u6240\u6709\u7684\u91d1\u56fd\u4eba\u4e3a\u654c\uff0c\u8981\u4e0d\u8981 \u6211\u628a\u8fd9\u9053\u594f\u7ae0\u5ff5\u7ed9\u4f60\u542c\uff1f\u201d\u5f20\u708e\u5446\u4e86\uff0c\u5446\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u5bf9\u5cb3\u5c11\u4fdd\u7684\u8a00\u884c\u5012\u4f3c\u6bd4\u6211\u8fd8\u8981\u719f\u6089\uff01 \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u79e6\u6867\u66fe\u7ecf\u628a\u4ed6\u8fd9\u9053\u594f\u7ae0\u6284\u4e86\u4e00\u4efd\uff0c\u53eb\u4eba\u9001\u7ed9\u91d1\u56fd\u7684\u7687\u5e1d\u3002\u90a3\u65f6\u6211\u8fd8\u662f\u91d1\u56fd \u7684\u8d1d\u5b50\uff0c\u800c\u4e14\u548c\u7687\u5e1d\u662f\u8fd1\u4eb2\uff0c\u6211\u770b\u8fc7\u8fd9\u9053\u594f\u7ae0\uff0c\u4f46\u540e\u6765\u4e0d\u4e45\uff0c\u6211\u5c31\u629b\u5f03\u4e86\u91d1\u56fd\u7684\u738b\u4f4d\u4e86\u3002\u201d \u5f20\u708e\u600e\u6562\u76f8\u4fe1\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u56e0\u4e3a\u770b\u4e86\u5cb3\u5c11\u4fdd\u8fd9\u9053\u594f\u7ae0\uff0c\u53d7\u4ed6\u611f\u52a8\uff0c\u56e0\u800c\u629b\u5f03\u738b\u4f4d\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5f53\u7136\u8fd8\u6709\u5176\u4ed6\u539f\u56e0\uff0c\u5373\u4f7f\u6211\u6ca1\u770b\u5230\u8fd9\u9053\u594f\u7ae0\uff0c\u6211\u4e5f\u8981\u9003\u4ea1\u7684\uff01\u201d\u5f20\u708e\u542c\u5f97 \u2018\u9003\u4ea1\u2019\u4e8c\u5b57\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u7684\u59d1\u59d1\u662f\u738b\u592a\u540e\u3002\u91d1\u56fd\u7684\u5f53\u4eca \u7687\u5e1d\u662f\u4f60\u7684\u8868\u54e5\uff0c\u4f60\u4e5f\u8981\u9003\u4ea1\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\uff0c\u6211\u65e0\u987b\u5411\u4f60\u7ec6\u8bf4\uff01\u201d \u3000\u3000\u5f20\u708e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e09\u5c81\u5c0f\u5b69\uff0c\u4f60\u4ee5\u4e3a\u4f60\u7528\u82b1\u8a00\u5de7\u8bed\u5c31\u53ef\u4ee5\u9a97\u6211\u76f8\u4fe1\uff0c\u653e\u8fc7\u4f60\u5417\uff1f \u201c\u8bf4\u81f3\u6b64\u5904\uff0c\u63d0\u9ad8\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5cb3\u5c11\u4fdd\u6740\u7684\u53ea\u662f\u654c\u4eba\u548c\u574f\u4eba\uff0c\u4f46\u8c01\u80fd\u8bc1\u660e\u4f60\u5df2\u7ecf\u4e0d\u662f \u91d1\u56fd\u7684\u738b\u7237\uff0c\u66f4\u53ef\u6709\u8c01\u80fd\u8bc1\u660e\u4f60\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u5ffd\u5730\u8f7b\u8f7b\u4e00\u5618\uff0c\u8bf4\u9053\uff1a\u201c\u7981\u58f0\uff0c\u597d\u4f3c\u6709\u4eba\u6765\u4e86\uff01\u201d\u5f20\u708e\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4f60 \u7684\u624b\u4e0b\u6765\u4e86\u4e48\uff1f\u201d\u76ee\u5149\u9661\u9732\u6740\u673a\uff0c\u5f20\u96ea\u6ce2\u6050\u5730\u4f24\u5bb3\u4e08\u592b\uff0c\u8fde\u5fd9\u6273\u7740\u4ed6\u7684\u624b\u3002 \u3000\u3000\u6a80\u516c\u76f4\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u4eec\u7236\u5973\u5feb\uff0c\u5feb\u8eb2\u8fc7\u590d\u58c1\u53bb\uff0c\u522b\u591a\u95ee\uff0c\u8fdf\u5c31\u6765\u4e0d\u53ca\u4e86\uff01\u201d\u58f0\u97f3\u4f4e \u6c89\uff0c\u4f46\u5f88\u575a\u5b9a\u3002 \u3000\u3000\u5f20\u708e\u672c\u6765\u662f\u4e0d\u6562\u76f8\u4fe1\u4ed6\u7684\u8bdd\u7684\uff0c\u4f46\u6a80\u516c\u76f4\u7684\u8bdd\u8bed\u5374\u4f3c\u6709\u4e00\u80a1\u4ee4\u4ed6\u4e0d\u80fd\u6297\u62d2\u7684\u529b\u91cf\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e14\u7740\u4ed6\u5f04\u4ec0\u4e48\u7384\u865a\uff1f\u201d\u5f53\u4e0b\u5728\u5899\u58c1\u4e0a\u8f7b\u8f7b\u4e00\u6309\uff0c\u5899\u58c1\u6253\u5f00\u4e00\u9053\u6697\u95e8\u3002\u5f20\u708e\u5c31 \u628a\u96ea\u6ce2\u62c9\u8fdb\u6697\u95e8\u3002 \u3000\u3000\u8fd9\u9053\u590d\u58c1\u7684\u6697\u95e8\uff0c\u662f\u5f20\u708e\u6697\u4e2d\u5e03\u7f6e\u7684\u3002\u6a80\u516c\u76f4\u7236\u5b50\u6bcf\u5e74\u603b\u6709\u5927\u534a\u7684\u65f6\u95f4\u5916\u51fa\u6253\u730e\uff0c\u6bcf\u9022 \u4ed6\u4eec\u7236\u5b50\u51fa\u53bb\u6253\u730e\uff0c\u5f20\u708e\u5c31\u628a\u5973\u513f\u652f\u5f00\uff0c\u53eb\u5979\u53bb\u6361\u91ce\u83dc\u6216\u5272\u67f4\u8349\uff0c\u4ed6\u5219\u7559\u5728\u5bb6\u4e2d\u5e03\u7f6e\u673a\u5173\u3002 \u540e\u6765\u4e24\u5bb6\u5408\u800c\u4e3a\u4e00\u3002\u590d\u58c1\u5374\u6ca1\u62c6\u6389\uff0c\u4ed6\u4ecd\u7136\u4f4f\u5728\u590d\u58c1\u53e6\u4e00\u9762\u4ed6\u81ea\u5df1\u539f\u6765\u7684\u623f\u95f4\uff0c\u5229\u7528\u8fd9\u9762\u590d \u58c1\u6765\u76d1\u89c6\u8fd9\u8fb9\u7684\u52a8\u9759\u3002\u90a3\u5929\u6a80\u516c\u76f4\u548c\u5ba2\u4eba\u8bf4\u8bdd\uff0c\u4ed6\u5c31\u662f\u85cf\u5728\u590d\u58c1\u91cc\u5077\u542c\u7684\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u6a80\u516c\u76f4\u4e0d\u77e5\u9053\u8fd9\u590d\u58c1\u7684\u79d8\u5bc6\uff0c\u4e0d\u6599\u6a80\u516c\u76f4\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u4ed6\u8fdb\u4e86\u590d\u58c1\uff0c\u6697\u95e8\u8ddf\u7740\u5173 \u4e0a\u3002\u5f20\u96ea\u6ce2\u8be7\u5f02\u4e4b\u6781\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u662f\u4e2a\u5de7\u5320\uff0c\u4f60\u5e03\u7f6e\u7684\u673a\u5173\uff0c\u8fde\u6211\u4e5f\u7792\u8fc7 \u4e86\u3002\u201d \u3000\u3000\u5f20\u708e\u5219\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6a80\u516c\u76f4\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u65f6\u5019\u77e5\u9053\u7684\uff0c\u90a3\u5929\u6211\u5077\u542c\u4ed6 \u548c\u5ba2\u4eba\u8c08\u8bdd\uff0c\u4ed6\u82e5\u662f\u65e9\u5df2\u77e5\u9053\uff0c\u4e3a\u4f55\u4e0d\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u5f20\u708e\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f0f\u5728\u5899\u89d2\uff0c\u628a\u8033\u6735\u8d34\u5730\u542c\u58f0\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u7a81\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u6210\u54e5\u7684\u7a74\u9053\u672a\u89e3\u5f00\u5462\uff0c\u6765\u7684\u82e5\u662f\u574f\u4eba\uff0c\u8fd9\uff0c\u8fd9\uff0c \u7239\u7239\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u5979\u60f3\u53eb\u7239\u7239\u51fa\u53bb\u7ed9\u4e08\u592b\u89e3\u5f00\u7a74\u9053\uff0c\u4f46\u77e5\u9053\u7239\u7239\u662f\u7edd\u4e0d\u80af\u7b54\u5e94\u7684\uff0c\u6b63\u5728\u60f3\u7528\u4ec0\u4e48\u6cd5\u5b50\u201c\u80c1 \u8feb\u201d\u7239\u7239\u7b54\u5141\uff0c\u5f20\u708e\u5df1\u662f\u63e1\u7740\u5979\u7684\u624b\uff0c\u5728\u5979\u638c\u5fc3\u5199\u9053\uff1a\u201c\u522b\u4f5c\u58f0\uff01\u201d \u3000\u3000\u539f\u6765\u5f20\u96ea\u6ce2\u8fd8\u6ca1\u6709\u542c\u89c1\u4ec0\u4e48\u58f0\u97f3\uff0c\u4ed6\u5374\u5df2\u9690\u9690\u542c\u89c1\u6709\u811a\u6b65\u58f0\u4e86\u3002 \u3000\u3000\u8fd9\u201c\u4f0f\u5730\u542c\u58f0\u201d\u7684\u672c\u9886\u4ed6\u81ea\u5c0f\u5c31\u7ec3\u6210\u7684\uff0c\u79ef\u6570\u5341\u5e74\u7ecf\u9a8c\uff0c\u4ed6\u542c\u5f97\u51fa\u662f\u6709\u4e09\u4e2a\u4eba\u8d70\u6765\uff0c\u4f46 \u79bb\u5f00\u4ed6\u4eec\u7684\u5bb6\u5c11\u8bf4\u4e5f\u8fd8\u6709\u767e\u6b65\u4e4b\u5916\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u5728\u8fd9\u6837\u8fdc\u7684\u8ddd\u79bb\uff0c\u672c\u6765\u54ac\u7740\u8033\u6735\u8bf4\u8bdd\uff0c\u6765\u4eba\u8fd8\u662f\u542c\u4e0d\u89c1\u7684\uff0c\u4f46\u4ed6\u4e0d\u6562\u5192\u8fd9\u4e2a\u9669\u3002\u800c\u4e14\u4ed6 \u5df2\u7ecf\u77e5\u9053\u5973\u513f\u7684\u610f\u601d\u662f\u8981\u4ed6\u51fa\u53bb\u89e3\u7a74\u7684\u4e86\uff0c\u83ab\u8bf4\u4ed6\u4e0d\u613f\u610f\u7ed9\u6a80\u9053\u6210\u89e3\u7a74\uff0c\u5373\u4f7f\u613f\u610f\uff0c\u4e5f\u662f\u6765 \u4e0d\u53ca\u4e86\u3002\u65e2\u7136\u662f\u505a\u4e0d\u5012\u7684\u4e8b\uff0c\u90a3\u53c8\u4f55\u5fc5\u591a\u8bf4\uff1f\u4ed6\u542c\u51fa\u4e86\u679c\u7136\u662f\u6709\u811a\u6b65\u58f0\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u9661\u5730\u4e00 \u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u6709\u6570\u5341\u5e74\u4f0f\u5730\u542c\u58f0\u7684\u7ecf\u9a8c\uff0c\u4e5f\u8981\u6765\u4eba\u5230\u4e86\u76f8\u8fd1\u767e\u6b65\u4e4b\u5185\u65b9\u59cb\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u6a80\u516c\u76f4\u4e2d\u4e86\u5267\u6bd2\uff0c\u8fc7\u4e86\u8fd9\u8bb8\u591a\u65f6\u5019\uff0c\u6309\u8bf4\u5df2\u662f\u79bb\u6b7b\u4e0d\u8fdc\u4e86\u3002\u5c06\u6b7b\u7684\u4eba\uff0c\u542c\u89c9\u600e\u80fd\u8fd8\u5982\u6b64 \u654f\u9510\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u4ed6\u5ffd\u5730\u53c8\u542c\u89c1\u6a80\u516c\u76f4\u5728\u8bf4\u8bdd\u4e86\u3002\u662f\u7528\u201c\u4f20\u97f3\u4eba\u5bc6\u7684\u529f\u592b\u8bf4\u8bdd\u3002\u58f0\u97f3\u51dd\u6210\u4e00 \u7ebf\uff0c\u6bd4\u868a\u5b50\u7684\u53eb\u58f0\u8fd8\u5c0f\uff0c\u5f20\u96ea\u6ce2\u5c31\u542c\u4e0d\u89c1\u3002\u4e0d\u8fc7\u4ed6\u5374\u662f\u542c\u5f97\u5f88\u6e05\u695a\u7684\u3002 \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u88ab\u70b9\u7a74\u7684\u662f\u54ea\u4e2a\u7a74\u9053\u5417\uff1f\u201d\u6a80\u9053\u6210\u9053\uff1a\u201c\u6108\u6c14\u7a74\u3002\u201d\u5f20\u708e\u628a\u5f20\u96ea\u6ce2 \u62c9\u8fd1\u8d34\u7740\u5899\uff0c\u8be5\u5904\u5899\u4e0a\u6709\u4e00\u9053\u5c0f\u5c0f\u7684\u7f1d\u9699\uff0c\u773c\u775b\u8d34\u7740\u7f1d\u9699\uff0c\u770b\u5f97\u89c1\u5916\u9762\u60c5\u666f\u3002\u53ea\u89c1\u6a80\u516c\u76f4\u53cc \u6307\u631f\u8d77\u4e00\u9897\u9ec4\u8c46\uff0c\u8fd9\u76d8\u9ec4\u8c46\u7092\u8089\u672c\u6765\u662f\u665a\u996d\u7684\u5c0f\u83dc\u4e4b\u4e00\uff0c\u4e0d\u8fc7\u4ed6\u631f\u8d77\u4e00\u9897\u9ec4\u8c46\uff0c\u5374\u4e0d\u662f\u9001\u5165 \u53e3\u4e2d\uff0c\u800c\u662f\u628a\u5b83\u8f7b\u8f7b\u4e00\u5f39\uff0c\u5411\u6a80\u9053\u6210\u98de\u53bb\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u9897\u9ec4\u8c46\u4e00\u5f39\uff0c\u6a80\u9053\u6210\u5c31\u7ad9\u8d77\u6765\u4e86\u3002\u4e0d\u4f46\u7ad9\u8d77\u6765\uff0c\u800c\u4e14\u8d70\u5230\u7236\u4eb2\u7684\u8eab\u8fb9\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u867d\u7136\u770b\u4e0d\u89c1\u9ec4\u8c46\u6253\u5728\u4e08\u592b\u8eab\u4e0a\u54ea\u4e2a\u90e8\u4f4d\uff0c\u4f46\u770b\u89c1\u4e08\u592b\u80fd\u591f\u8d70\u52a8\uff0c\u4ea6\u5df2\u77e5\u9053\u662f\u516c\u516c \u7528\u8fd9\u9897\u5c0f\u5c0f\u7684\u9ec4\u8c46\u66ff\u4e08\u592b\u89e3\u5f00\u4e86\u88ab\u5c01\u7684\u7a74\u9053\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u653e\u4e0b\u5fc3\u5934\u4e00\u5757\u5927\u77f3\uff0c\u5401\u4e86\u53e3\u6c14\u3002\u5979\u53c8\u559c\u53c8\u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u516c\u516c\u8fd8\u6709\u89e3\u7a74 \u4e4b\u80fd\u3002\u4ed6\u80fd\u591f\u66ff\u513f\u5b50\u89e3\u7a74\uff0c\u5927\u6982\u81ea\u5df1\u4e5f\u4e0d\u4f1a\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u677e\u4e86\u53e3\u6c14\uff0c\u5f20\u708e\u5219\u662f\u4e0d\u7531\u5f97\u5927\u5927\u5403\u60ca\u3002\u8fd9\u65f6\u4ed6\u65b9\u59cb\u77e5\u9053\u4ed6\u662f\u4f4e\u4f30\u4e86\u6a80\u516c\u76f4\u5185\u529f\u9020 \u8be3\uff0c\u4ed6\u6697\u9a82\u81ea\u5df1\u80e1\u6d82\uff1a\u201c\u4ed6\u548c\u6211\u8bf4\u4e86\u8fd9\u8bb8\u4e45\u7684\u8bdd\uff0c\u8fd8\u80fd\u591f\u652f\u6301\u5f97\u4f4f\uff0c\u6211\u65e9\u5c31\u5e94\u8be5\u60f3\u5230\u4ed6\u662f\u5728 \u62d6\u5ef6\u65f6\u95f4\u8fd0\u529f\u89e3\u6bd2\u7684\u4e86\u3002\u5509\uff0c\u6211\u4e5f\u662f\u592a\u8fc7\u76f8\u4fe1\u8fd9\u6bd2\u836f\u7684\u5389\u5bb3\u4e86\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\uff0c\u6211\u2014\u2014\u201d \u3000\u3000\u65e9\u77e5\u5982\u6b64\uff0c\u8be5\u600e\u6837\u5462\uff1f\u6b64\u9645\uff0c\u4ed6\u81ea\u5df1\u4e5f\u662f\u7b54\u4e0d\u4e0a\u6765\u3002\u662f\u8be5\u65e9\u5c31\u628a\u4ed6\u6740\u6389\u5417\uff1f\u8fd9\u8bdd\u8001\u662f\u65e9 \u534a\u4e2a\u65f6\u8fb0\u95ee\u4ed6\uff0c\u4ed6\u53ef\u4ee5\u6beb\u4e0d\u72b9\u8c6b\u7684\u7b54\u662f\u3002\u4f46\u73b0\u5728\u4ed6\u5374\u662f\u4e0d\u6562\u8bf4\u975e\u6740\u6a80\u516c\u76f4\u4e0d\u53ef\u4e86\u3002\u56e0\u4e3a\u4ed6\u81ea \u5df1\u4ea6\u5df2\u662f\u5728\u601d\u7591\uff0c\u4e0d\u77e5\u9053\u6a80\u516c\u76f4\u5230\u5e95\u662f\u4f55\u7b49\u6837\u4eba\u4e86\u3002 \u3000\u3000\uff38\uff38\uff38\u6a80\u516c\u76f4\u5728\u5598\u6c14\uff0c\u8ddf\u7740\u5927\u58f0\u54b3\u55fd\u3002 \u3000\u3000\u6a80\u9053\u6210\u6276\u4ed6\u5750\u7a33\uff0c\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u5750\u5728\u677f\u51f3\u4e0a\uff0c\u80cc\u9760\u7740\u5899\uff0c\u4e00\u8fb9\u54b3\u55fd\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u6211\u4e0d\u884c\u4e86\uff01\u201d\u4ed6\u7528\u5f39\u6307\u795e\u901a \u7684\u529f\u592b\u66ff\u513f\u5b50\u89e3\u7a74\u3002\u7684\u786e\u662f\u5dee\u4e0d\u591a\u8017\u635f\u4e86\u4ed6\u521a\u521a\u51dd\u805a\u7684\u771f\u6c14\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u4e09\u4e2a\u9ec4\u8863\u4eba\u8d70\u8fdb\u4e86\u5c4b\u5b50\u4e86\u3002 \u3000\u3000\u4e3a\u9996\u7684\u90a3\u4e2a\u6b66\u58eb\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u6a80\u8d1d\u52d2\uff0c\u522b\u6765\u65e0\u6059\uff0c\u8fd8\u8ba4\u5f97\u5c0f\u4eba \u4e48\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u8fde\u8fde\u54b3\u55fd\uff0c\u5598\u7740\u6c14\u8bf4\u9053\uff1a\u201c\u539f\uff0c\u539f\u6765\u662f\u54c8\u90fd\u5c09\uff0c\u8bf7\uff0c\u8bf7\u6055\u5931\u8fce\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u54c8\u5fc5\u56fe\u662f\u9f99\u9a91\u5175\u4e2d\u8457\u540d\u7684\u52c7\u5c06\uff0c\u6211\u5018\u82e5\u6ca1\u6709\u4e2d\u6bd2\uff0c\u81ea\u4e0d\u6015\u4ed6\u3002\u4f46\u5982\u4eca\u6211\u7684\u771f\u6c14\u5c1a\u672a\u51dd\u805a\uff0c \u529f\u529b\u6700\u591a\u4e0d\u8fc7\u6062\u590d\u4e24\u5206\uff0c\u53ea\u6015\u662f\u6253\u4e0d\u8fc7\u4ed6\u4e86\u3002\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u591a\u8c22\u738b\u7237\u8fd8\u8bb0\u5f97\u5c0f\u4eba\uff0c\u4f46\u6211\u65e9 \u5df2\u4e0d\u662f\u9f99\u9a91\u5175\u7684\u4e00\u4e2a\u90fd\u5c09\u4e86\uff0c\u5341\u5e74\u524d\u7687\u4e0a\u5df2\u7ecf\u5c06\u6211\u5185\u8c03\u5165\u5bab\uff0c\u5982\u4eca\u6211\u662f\u4e00\u7b49\u5fa1\u524d\u5e26\u5200\u5df4\u56fe \u9c81\u3002 \u3000\u3000\u201d\u9f99\u9a91\u5175\u662f\u7981\u536b\u519b\uff0c\u5df4\u56fe\u9c81\u5219\u672c\u6765\u662f\u4e2a\u5c01\u53f7\uff0c\u610f\u4e49\u4e3a\u201c\u52c7\u58eb\u201d\uff0c\u6709\u529f\u52b3\u7684\u5c06\u519b\uff0c\u4e5f\u5e38\u6709 \u88ab\u5c01\u4e3a\u201c\u5df4\u56fe\u9c81\u201d\u7684\u3002\u4f46\u201c\u5fa1\u524d\u5df4\u56fe\u9c81\u201d\u5219\u662f\u5168\u56fd\u7687\u5e1d\u7684\u8d34\u8eab\u4f8d\u536b\uff0c\u4f8d\u536b\u800c\u52a0\u4e0a\u201c\u5df4\u56fe\u9c81\u201d \u8854\uff0c\u5730\u4f4d\u5df2\u7ecf\u5728\u4e00\u80a1\u4f8d\u536b\u4e4b\u4e0a\uff0c\u201c\u4e00\u7b49\u5fa1\u524d\u5e26\u5200\u4f8d\u536b\u201d\u90a3\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5730\u4f4d\u5df2\u662f\u4e0d\u5728\u201c\u9f99 \u9a91\u5175\u603b\u5c09\u201d\uff08\u76f8\u5f53\u4e8e\u5fa1\u6797\u519b\u7edf\u9886\uff09\u4e4b\u4e0b\u4e86\u3002\u82e5\u8bba\u548c\u7687\u5e1d\u7684\u4eb2\u5bc6\u5173\u7cfb\uff0c\u9f99\u9a91\u5175\u90fd\u5c09\u90fd\u4e0d\u80fd\u76f8 \u6bd4\u3002\u54c8\u5fc5\u56fe\u81ea\u62a5\u5b98\u8854\uff0c\u5f97\u610f\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002\u6a80\u516c\u76f4\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6a80\u67d0\u50fb\u5904\u8352\u5c71\uff0c\u5b64\u964b\u5be1 \u95fb\uff0c\u606d\u559c\u54c8\u5927\u4eba\u5347\u5b98\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\u6211\u7684\u540c\u50da\u3002\u4ed6\u4eec\u662f\u4e00\u6bcd\u6240\u751f\u7684\u540c\u80de\uff0c\u8001\u5927\u53eb\u547c\u6c99\u9f99\u3002\u8001\u4e8c\u53eb\u547c\u6c99 \u864e\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u9ec4\u8863\u6b66\u58eb\u8ddf\u5728\u54c8\u5fc5\u56fe\u540e\u9762\uff0c\u9f50\u9f50\u8e0f\u4e0a\u4e00\u6b65\uff0c\u5782\u624b\u8d34\u819d\uff0c\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u4e8c\u7b49\u5fa1\u524d\u5df4 \u56fe\u9c81\u547c\u6c99\u9f99\u547c\u6c99\u864e\u62dc\u89c1\u738b\u7237\u3002\u201d\u6a80\u516c\u76f4\u4ecd\u7136\u80cc\u9760\u7740\u5899\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u55ef\uff0c\u4e09 \u4f4d\u3001\u4e09\u4f4d\u5df4\u9c81\u540c\u65e5\u5149\u4e34\uff0c\u53ef\u771f\u662f\u4ee4\u6211\u53d7\u5ba0\u82e5\u60ca\u4e86\u3002\u8bf7\u539f\u8c05\uff0c\u539f\u8c05\u6211\u4e0d\u80fd\u8d77\u7acb\uff0c\u591a\u6709\u5931\u793c\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e9b\u505a\u5974\u624d\u7684\u4eba\u3002\u600e\u6562\u6709\u52b3\u4f60\u738b\u7237\u8d77\u7acb\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u662f\u5949\u4e86\u7687\u4e0a \u4e4b\u547d\u800c\u6765\u7684\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u9661\u5730\u63d0\u9ad8\u58f0\u97f3\u559d\u9053\uff1a\u201c\u6a80\u516c\u76f4\uff0c\u7687\u4e0a\u5ba3\u53ec\u4f60\u5165\u4eac\uff0c\u5feb\u5feb\u8dea\u4e0b\u63a5 \u65e8\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\u3002\u547c\u6c99\u9f99\u53d8\u4e86\u9762\u8272\uff0c\u559d\u9053\uff1a\u201c\u6a80\u516c\u76f4\uff0c\u4f60\u6562\u8fdd\u6297\u5723\u65e8\u5417\uff1f\u4f60\u77e5\u4e0d\u77e5 \u9053\uff0c\u8fdd\u6297\u5723\u65e8\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d\u6a80\u516c\u76f4\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u662f\u4e2a\u6b7b\u5427\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5411\u547c\u6c99\u9f99\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u793a\u610f\u53eb\u4ed6\u4e0d\u53ef\u5984\u52a8\uff0c\u653e\u5bbd\u8bed\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u522b\u60ca \u7591\uff0c\u5ff5\u5728\u5f80\u65e5\u7684\u4ea4\u60c5\uff0c\u5f85\u6211\u548c\u4f60\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\u5427\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\uff01\u201d\u8fde\u58f0\u54b3\u55fd\u3002\u6a80\u9053\u6210\u8f7b\u8f7b\u7ed9\u7236\u4eb2\u6376\u80cc\uff0c\u5fc3\u91cc\u7740\u6025\u4e4b\u6781\u3002\u539f\u6765\u4ed6 \u7684\u7a74\u9053\u867d\u89e3\uff0c\u529f\u529b\u5c1a\u672a\u80fd\u591f\u6062\u590d\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u4f9d\u4f60\u5f53\u5e74\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u7687\u4e0a\u786e\u5b9e\u662f\u5bf9\u4f60\u5341\u5206\u4e0d\u6ee1\u3002\u4f46\u4f60\u53ef\u77e5\u9053 \u4f60\u4eca\u5f97\u7687\u4e0a\u6700\u607c\u6012\u7684\u662f\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u505a\u8fc7\u7684\u4e8b\u60c5\u51e0\u4e4e\u6ca1\u6709\u4e00\u6837\u662f\u5408\u7687\u4e0a\u5fc3\u610f\u7684\uff0c\u4f46\u4ee5\u4f55\u8005\u4e3a\u6700\uff0c\u8bf7\u6055\u6211\u7f3a\u4e4f \u81ea\u77e5\u4e4b\u660e\uff0c\u5012\u8981\u8bf7\u4f60\u6307\u6559\u3002\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8d1d\u52d2\u8a00\u91cd\u4e86\uff0c\u6307\u6559\u4e8c\u5b57\uff0c\u5974\u624d\u5982\u4f55\u62c5\u5f53\u5f97\u8d77\uff1f\u8fd9 \u53ea\u662f\u7687\u4e0a\u7684\u610f\u601d\uff0c\u662f\u6211\u8fd9\u6b21\u5949\u547d\u51fa\u4eac\u4e4b\u65f6\uff0c\u7687\u4e0a\u548c\u6211\u8bf4\u53ca\u8d1d\u52d2\u5f53\u5e74\u4e4b\u4e8b\uff0c\u6211\u624d\u77e5\u9053\u8d1d\u52d2\u83b7\u7f6a \u4e4b\u7531\u7684\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u7b97\u662f\u7687\u4e0a\u5bf9\u6211\u7684\u6307\u6559\u5427\uff0c\u8bf7\u4f60\u8f6c\u8ff0\u3002\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u6700\u607c\u6012 \u7684\u662f\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4e00\u3001\u4f60\u8981\u6740\u79e6\u6867\u3002\u90a3\u65f6\u79e6\u6867\u5df2\u7ecf\u6295\u964d\u54b1\u4eec\u91d1\u56fd\u3002\u7687\u4e0a\u6b63\u8981\u5c06\u4ed6\u91cd\u7528\uff0c\u4e0d\u8fc7\u4e8b \u5173\u673a\u5bc6\uff0c\u4e0d\u4fbf\u516c\u5f00\uff0c\u4e5f\u4e0d\u4fbf\u548c\u4f60\u8be6\u8a00\uff0c\u4f46\u7687\u4e0a\u6599\u4f60\u4e5f\u4f1a\u591a\u5c11\u77e5\u9053\u4ed6\u7684\u7528\u610f\u7684\u3002\u4f60\u5374\u4e00\u800c\u518d\uff0c \u518d\u800c\u4e09\u5730\u529d\u7687\u4e0a\u6740\u6389\u79e6\u6867\uff0c\u7687\u4e0a\u771f\u4e0d\u77e5\u4f60\u662f\u4f55\u5c45\u5fc3\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u8981\u6740\u79e6\u6867\u7684\u7406\u7531\u3002\u5f53\u5e74\u4e5f\u66fe\u7980\u544a\u8fc7\u7687\u4e0a\u7684\uff0c\u7687\u4e0a\u6ca1\u544a\u8bc9\u4f60\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u8bf4\u4e86\u3002\u7687\u4e0a\u8bf4\uff0c\u4e0d\u9519\uff0c\u79e6\u6867\u662f\u4e2a\u53cd\u590d\u65e0\u803b\u7684\u5c0f\u4eba\uff0c\u4f46\u4f60\u8981\u7528\u8fd9\u4e2a\u7406\u7531 \u6740\u4ed6\uff0c\u5374\u662f\u5927\u5927\u7684\u4e0d\u5bf9\u3002\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u597d\u50cf\u542c\u5230\u4e86\u6700\u8352\u8c2c\u7684\u95ee\u9898\uff0c\u6115\u4e86\u4e00\u6115\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u738b\u7237\uff0c\u4f60\u662f\u88c5\u80e1\u6d82\u5462\u8fd8\u662f\u771f \u7684\u4e0d\u61c2\uff1f\u4e8b\u5b9e\u65e9\u5df2\u8bc1\u660e\uff0c\u79e6\u6867\u7684\u53cd\u590d\u65e0\u803b\uff0c\u90a3\u53ea\u662f\u5bf9\u5b8b\u56fd\u6709\u5bb3\uff0c\u5bf9\u54b1\u4eec\u91d1\u56fd\u5374\u662f\u5927\u5927\u6709\u529f\u3002 \u82e5\u4e0d\u662f\u4ed6\uff0c\u600e\u80fd\u5bb3\u6b7b\u5cb3\u98de\uff0c\u5cb3\u98de\u4e0d\u6b7b\uff0c\u4e2d\u539f\u4e4b\u5730\u90fd\u8981\u88ab\u4ed6\u6536\u590d\u3002\u8fd8\u8c08\u5f97\u5230\u541e\u5e76\u5b8b\u56fd\u4e48\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u541e\u5e76\u5b8b\u56fd\uff0c\u4e0d\u77e5\u8fd8\u8981\u6253\u591a\u5c11\u5e74\u7684\u4ed7\uff0c\u8fde\u7978\u7ed3\uff0c\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u5723\u660e\u5929\u5b50\uff0c \u5e94\u8be5\u4ee5\u5fb7\u670d\u5929\u4e0b\uff0c\u5f92\u4ed7\u6b66\u529f\uff0c\u4eba\u5fc3\u4e0d\u670d\uff0c\u53ea\u6709\u57cb\u4e0b\u7978\u6839\u3002\u82e5\u7136\u4f9d\u9760\u9634\u8c0b\u8be1\u8ba1\uff0c\u4fb5\u5bb3\u90bb\u90a6\uff0c\u7eb5 \u7136\u5f97\u76ca\u4e00\u65f6\uff0c\u957f\u8fdc\u800c\u8a00\uff0c\u6050\u6015\u66f4\u975e\u5584\u7b56\uff01\u8bd5\u770b\u79e6\u6867\u5bb3\u6b7b\u5cb3\u98de\u4e4b\u540e\uff0c\u5b8b\u56fd\u7684\u767e\u59d3\uff0e\u53c8\u6709\u54ea\u4e2a\u4e0d \u60bc\u5ff5\u5cb3\u98de\u7684\uff0c\u4e0d\u75db\u6068\u79e6\u6867\uff0c\u6c11\u5fc3\u6c9b\u7136\u83ab\u4e4b\u80fd\u5fa1\uff0c\u541e\u5e76\u5b8b\u56fd\u53c8\u5c82\u6613\u8a00\uff1f\u201d\u4ed6\u8bf4\u4e86\u8fd9\u4e00\u756a\u8bdd\uff0c\u8fde \u8fde\u54b3\u55fd\uff0c\u6c14\u5598\u5401\u5401\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5927\u9053\u7406\u7559\u5f85\u89c1\u5230\u7687\u4e0a\u518d\u8bf4\u5427\uff0c\u6211\u4e0d\u548c\u4f60\u4e89\u8fa9\u3002 \u3000\u3000\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u672a\u5fc5\u80fd\u591f\u89c1\u5230\u7687\u4e0a\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u8bf4\u7684\u4e5f\u5bf9\uff0c\u65f6\u95f4\u65e0\u591a\uff0c\u8fd8\u662f\u8a00\u5f52\u6b63\u4f20 \u5427\u3002\u7687\u4e0a\u6700\u607c\u6012\u6211\u7684\u7b2c\u4e8c\u4ef6\u4e8b\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u4e00\u9762\u66ff\u7236\u4eb2\u6376\u80cc\uff0c\u4e00\u9762\u8bf4\u9053\u201c\u5509\uff0c\u4f60\u5bf9\u725b\u5f39\u7434\u53c8\u6709\u4f55\u7528\uff0c\u7239\u7239\uff0c\u4f60\u8fd8\u662f\u7701\u70b9\u6c14\u529b \u5427\u3002\u201d \u3000\u3000\u8eb2\u5728\u590d\u58c1\u91cc\u5077\u542c\u7684\u5f20\u708e\u5fc3\u91cc\u5374\u662f\u660e\u767d\uff0c\u6a80\u516c\u76f4\u90a3\u756a\u8bdd\u5e76\u4e0d\u662f\u8bf4\u7ed9\u8fd9\u4e09\u4e2a\u201c\u5df4\u56fe\u9c81\u201d\u542c \u7684\uff0c\u662f\u8bf4\u7ed9\u4ed6\u542c\u7684\u3002\u201c\u539f\u6765\u6a80\u516c\u76f4\u66fe\u529d\u8fc7\u91d1\u5e1d\u6740\u79e6\u6867\uff0c\u6211\u771f\u662f\u9519\u602a\u4ed6\u4e86\u3002\u201d\u542c\u89c1\u6a80\u516c\u76f4\u5598\u6c14 \u7684\u58f0\u97f3\uff0c\u5fc3\u91cc\u597d\u751f\u96be\u8fc7\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u6a2a\u4e86\u6a80\u9053\u6210\u4e00\u773c\uff0c\u5bf9\u64c5\u516c\u76f4\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\uff0c\u4f60\u5df2\u7ecf\u8bf4\u5230\u4e86\u7687\u4e0a\u4e4b\u6240\u4ee5\u607c\u6012 \u4f60\uff0c\u5c31\u662f\u56e0\u4e3a\u4f60\u53cd\u5bf9\u4ed6\u5bf9\u5b8b\u56fd\u7528\u5175\uff0c\u54fc\uff0c\u7687\u4e0a\u4eb2\u53e3\u5bf9\u6211\u8bf4\uff0c\u56e0\u4e3a\u4f60\u53cd\u5bf9\u4ed6\u7528\u5175\u3001\u4ed6\u8fd8\u66fe\u6000\u7591 \u8fc7\u4f60\u5462\uff1f\u201d\u690d\u516c\u76f4\u9053\uff1a\u201c\u54e6\uff0c\u6000\u7591\u4ec0\u4e48\uff0c\u6000\u7591\u6211\u662f\u91cc\u901a\u654c\u56fd\u7684\u5978\u7ec6\u4e48\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u90a3\u5012 \u4e0d\u81f3\u4e8e\uff0c\u4ee5\u4f60\u7684\u8eab\u4efd\u5f53\u7136\u4e5f\u4e0d\u7518\u4e8e\u53ea\u505a\u5978\u7ec6\u3002\u8001\u5b9e\u8bf4\u3002\u7687\u4e0a\u5bf9\u4f60\u7684\u7591\u5fc3\uff0c\u53ef\u6bd4\u5978\u7ec6\u8fd9\u4e2a\u7f6a\u540d \u5927\u5f97\u591a\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u6211\u66f4\u975e\u77e5\u9053\u4e0d\u53ef\u4e86\uff0c\u8bf7\u76f4\u8bf4\u5427\uff01\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u6000\u7591\u4f60\u662f\u60f3\u62e2 \u7edc\u4eba\u5fc3\uff0c\u56fe\u8c0b\u7be1\u4f4d\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u5c31\u662f\u4f60\u8981\u9020\u53cd\uff01\u56e0\u4e3a\u4f60\u77e5\u9053\u6709\u4e00\u90e8\u5206\u5b98\u5175\u4e0d\u60f3\u6253\u4ed7\u7684\u3002\u4f60\u53cd \u5bf9\u7687\u4e0a\u5bf9\u5b8b\u56fd\u7528\u5175\uff0c\u5c31\u53ef\u4ee5\u6536\u4e70\u4eba\u5fc3\u3002\u8fd8\u6709\uff0c\u4f60\u867d\u7136\u4e0d\u662f\u91cc\u901a\u6545\u56fd\uff0c\u4f46\u4f60\u4e3b\u5f20\u4e0e\u5b8b\u56fd\u5e73\u7b49\u8c08 \u548c\uff0c\u5b8b\u56fd\u4e5f\u5fc5\u5b9a\u4e50\u4e8e\u52a9\u4f60\u7be1\u4f4d\u3002\u7ed3\u679c\u548c\u91cc\u901a\u6545\u56fd\u4e5f\u662f\u4e00\u6837\u4e86\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u7687\u4e0a\u4e5f\u77e5\u9053\u4eba\u5fc3\u4e0d\u60f3\u6253\u4ed7\u5417\uff1f\u4f46\u7687\u4e0a\u65e2\u7136\u5bf9\u6211\u7591\u5fc3\u8fd9\u6837\u5927\uff0c\u4e3a\u4f55 \u8fd8\u8981\u53ec\u6211\u8fdb\u4eac\uff1f\u4f60\u53c8\u4e3a\u4f55\u53eb\u6211\u4e0d\u5fc5\u5bb3\u6015\u5462\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u5bf9\u4f60\u7684\u6000\u7591\u90a3\u662f\u5df2\u7ecf\u8fc7\u53bb \u4e86\u3002\u201d\u5176\u5b9e\u4ed6\u77e5\u9053\u662f\u672a\u66fe\u201c\u8fc7\u53bb\u201d\u7684\uff0c\u53ea\u662f\u4ed6\u5949\u4e86\u7687\u5e1d\u4e4b\u547d\u4e0d\u80fd\u4e0d\u8fd9\u6837\u8bf4\u4ee5\u5b89\u6a80\u516c\u76f4\u4e4b\u5fc3\u3002 \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u7687\u4e0a\u73b0\u5728\u5c31\u4e0d\u6000\u7591\u4e86\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u7687\u4e0a\u6700\u521d\u4e5f\u8fd8\u662f\u7591\u5fc3\u7684\u3002\u4f46\u7ecf\u8fc7\u8fd9\u4e48\u591a\u5e74\uff0c\u7687\u4e0a\u5df2\u7ecf\u67e5\u5f97\u6e05 \u695a\uff0c\u4f60\u5e76\u6ca1\u6709\u9003\u5230\u5b8b\u56fd\uff0c\u4e5f\u6ca1\u6709\u548c\u4efb\u4f55\u4e00\u4f4d\u63e1\u6709\u5175\u6743\u7684\u5c06\u519b\u6765\u5f80\uff0c\u5dee\u4e0d\u591a\u4e8c\u5341\u5e74\u90fd\u662f\u5728\u8352\u5c71 \u9690\u5c45\uff0c\u7687\u4e0a\u624d\u4e0d\u7591\u5fc3\u4e86\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f46\u6211\u7684\u4e3b\u5f20\u8fd8\u662f\u548c\u539f\u6765\u4e00\u6837\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u8bf4\u4f60\u90a3\u4e9b\u8fc2\u8150\u4e4b\u89c1\u4e0d\u503c\u4e00\u9a73\uff0c\u4f46\u53ea\u8981\u4f60\u8fd8\u6ca1\u6709\u5b9e\u9645\u7684\u8d77\u5175\u53cd\u4ed6\uff0c\u4ed6\u5c31 \u53ef\u4ee5\u5927\u5ea6\u5305\u542b\uff0c\u4e0d\u548e\u5df2\u5f80\u3002\u800c\u4e14\u79e6\u4fa9\u4ea6\u5df2\u6b7b\u4e86\uff0c\u7687\u4e0a\u4e5f\u4e0d\u5728\u4e4e\u4f60\u66fe\u7ecf\u8981\u6740\u79e6\u6867\u4e86\u3002\u7687\u4e0a\u8ba4\u4e3a \u4f60\u662f\u4e2a\u4eba\u624d\uff0c\u4ed6\u8fd8\u662f\u8981\u7528\u4f60\u7684\u3002\u597d\uff0c\u7687\u4e0a\u7684\u8bdd\u3002\u6211\u90fd\u5bf9\u4f60\u5b9e\u8bdd\u5b9e\u8bf4\u4e86\uff0c\u4f60\u53ef\u4ee5\u5b89\u5fc3\u4e86\u5427\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5b89\u5fc3\u53c8\u600e\u4e48\u6837\uff1f\u4e0d\u5b89\u5fc3\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u5bf9\u4f60\u8fd9\u6837\u5bbd\u539a\u3002\u8001\u5b9e\u8bf4\u6211\u4e5f\u4e3a\u4f60\u5e86\u5e78\u3002\u4f60\u82e5\u6ca1\u6709\u522b\u7684\u6000\u7591\uff0c\u90a3\u5c31\u5b89\u4e0b \u5fc3\u6765\uff0c\u8d76\u5feb\u63a5\u65e8\u5427\uff01\u201d\u6853\u516c\u76f4\u9053\uff1a\u201c\u8bf7\u6055\u6211\u4e0d\u80fd\u63a5\u65e8\uff01 \u3000\u3000\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u52c3\u7136\u53d8\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u8fd9\u8bb8\u591a\u8bdd\uff0c\u90fd\u662f\u767d\u8bf4\u4e86\uff01\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u4e0d\u63a5\u65e8\u7684\u540e \u679c\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u53ef\u501f\u4f60\u4e0d\u65e9\u6765\u4e24\u65f6\u8fb0\uff0c\u5982\u4eca\u6211\u60f3\u63a5\u65e8\u4e5f\u4e0d\u80fd\u4e86\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201d\u4f60\u77a7\u6211\u73b0\u5728\u8fd9\u4e2a\u6a21\u6837\uff0c\u8fd8\u80fd\u548c\u4f60\u4e0a\u4eac\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7d2f\u77e5\u6a80\u516c\u76f4\u6b66\u529f\u9ad8\u5f3a\uff0c\u4ed6\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u770b\u89c1\u6a80\u516c\u76f4\u8fd9\u526f\u840e\u9761\u4e0d\u632f\u7684\u6a21\u6837\uff0c\u5df2\u7ecf\u6709 \u70b9\u7591\u5fc3\uff0c\u8fd8\u9053\u8fd9\u662f\u6a80\u516c\u76f4\u5047\u88c5\u51fa\u6765\u7684\uff0c\u4f46\u7ecf\u8fc7\u4e86\u8fd9\u534a\u679d\u9999\u8c08\u8bdd\u7684\u65f6\u95f4\uff0c\u770b\u6765\u53c8\u4e0d\u50cf\u662f\u5047\u88c5\uff0c \u4ed6\u4e0d\u7981\u5fc3\u5934\u4e00\u8df3\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u600e\u7684\u5f04\u6210\u8fd9\u4e2a\u6a21\u6837\uff0c\u662f\u6709\u75c5\u5417\uff1f\u201d \u3000\u3000\u6a80\u516c\u7acb\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u6211\u65e9\u5c31\u6599\u5230\u4f60\u4eec\u4f1a\u6765\u7684\u3002\u6211\u60f3\u4e0d\u5230\u7687\u4e0a\u4f1a\u8d66\u514d\u6211\uff0c \u4e0e\u5176\u8fdf\u6b7b\uff0c\u4e0d\u5982\u65e9\u6b7b\u3002\u56e0\u6b64\u6211\u5728\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u524d\uff0c\u5df2\u7ecf\u670d\u6bd2\u4e86\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5927\u5403\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u5df2\u7ecf\u670d\u6bd2\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u56e0\u4e3a\u770b\u89c1\u4f60\u4eec\u6765\u4e86\uff0c\u60f3\u542c\u542c\u7687\u4e0a\u6709\u4ec0\u4e48\u8bdd\u5bf9\u6211\u8bf4\uff0c\u52c9\u5f3a\u8fd0\u7528\u5185 \u529f\u624d\u80fd\u591f\u652f\u6301\u5230\u6b64\u523b\u7684\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u53eb\u9053\uff1a\u201c\u738b\u7237\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u4f60\u8d76\u7d27\u8fd0\u7528\u5185\u529f\uff0c\u591a\u652f\u6301\u4e00\u4e9b\u65f6\u5019\u5427\u3002\u5f85\u6211\u7ed9\u4f60\u89e3 \u6bd2\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u884c\u4e86\uff0c\u6211\u5df2\u7ecf\u7b4b\u75b2\u529b\u7aed\uff0c\u652f\u6301\u4e0d\u4e86\u5566\uff01\u8fd9\u5267\u6bd2\u4e5f\u4e0d\u662f\u4f60\u80fd\u89e3\u7684\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u5f85\u6211\u770b\u770b\uff01\u201c\u4ed6\u5bf9\u6a80\u516c\u76f4\u7684\u6b66\u529f\u9887\u4e3a\u5fcc\u60ee\uff0c\u5fc3\u91cc\u8fd8\u6709\u70b9\u6050\u6015\u4ed6 \u5f04\u5047\u3002\u5f53\u4e0b\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8e0f\u6b65\u4e0a\u524d\u3002 \u3000\u3000\u6a80\u9053\u6210\u62e6\u5728\u7236\u4eb2\u9762\u524d\uff0c\u53cc\u76ee\u5411\u4ed6\u6012\u89c6\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u8d1d\u5b50\u5427\uff0c\u8bf7\u8ba9\u5f00\uff01\u201d \u3000\u3000\u6a80\u9053\u6210\u6012\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4ec0\u4e48\u8d1d\u52d2\u8d1d\u5b50\uff0c\u6211\u53ea\u77e5\u9053\u8fd9\u91cc\u662f\u6211\u7684\u5bb6\uff0c\u6211\u662f\u7ade\u7239\u7239\u7684\u513f\u5b50\u3002\u4f60 \u4eec\u64c5\u81ea\u8fdb\u6765\uff0c\u5df2\u5c5e\u65e0\u7406\uff0c\u6211\u4e0d\u8bb8\u4f60\u78b0\u6211\u7684\u7239\u7239\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u65e0\u6687\u591a\u8a00\uff0c\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u201d\u4e00\u638c\u5c31\u5411\u6a80\u9053\u6210\u6253\u53bb\u3002\u6a80\u516c\u76f4\u53eb\u9053\uff1a\u201c\u54c8\u5927\u4eba\u624b\u4e0b \u7559\u60c5\uff0c\u6211\u8fd9\u5b69\u5b50\u662f\u4e0d\u61c2\u6b66\u529f\u7684\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7ec3\u7684\u662f\u5927\u529b\u9e70\u722a\u529f\uff0c\u4f7f\u51fa\u6765\u7684\u5374\u662f\u8ff7\u8e2a\u638c\u6cd5\u3002\u672c\u6765\u9e70\u722a\u529f\u5c5e\u4e8e\u9633\u521a\u4e00\u8def\uff0c\u8ff7\u8e2a\u638c \u6cd5\u5219\u4ee5\u98d8\u5ffd\u89c1\u957f\uff0c\u5e76\u975e\u4ee5\u4e3a\u53d6\u80dc\uff0c\u4e24\u79cd\u4e0d\u540c\u8def\u5b50\u7684\u6b66\u529f\u662f\u5f88\u96be\u517c\u7ec3\u7684\u3002\u6a80\u516c\u76f4\u89c1\u4ed6\u51fa\u624b\uff0c\u4e5f \u4e0d\u7981\u6709\u70b9\u4f69\u670d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u80fd\u591f\u628a\u6781\u5176\u521a\u731b\u7684\u638c\u529b\u85cf\u4e8e\u9634\u67d4\u7684\u638c\u6cd5\u4e4b\u4e2d\uff0c\u7eb5\u7136\u8fd8\u4e0d\u80fd\u8bf4 \u81ea\u6210\u4e00\u5bb6\uff0c\u4e5f\u662f\u5f88\u96be\u5f97\u4e86\u3002\u602a\u5f97\u7687\u4e0a\u5c06\u4ed6\u91cd\u7528\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u54c8\u5fc5\u56fe\u8fd9\u4e00\u638c\u5df2\u6253\u5230\u4e86\u6a80\u9053\u6210\u7684\u80f8\u524d\uff0c\u8fd9\u4e00\u638c\u53d8\u5e7b\u65e0\u65b9\uff0c\u53ef\u865a\u53ef\u5b9e\uff0c\u82e5 \u7136\u662f\u6253\u5b9e\u4e86\uff0c\u6a80\u9053\u6210\u4e0d\u6b7b\u6050\u6015\u4e5f\u5f97\u91cd\u4f24\u3002\u5b66\u6b66\u4e4b\u4eba\uff0c\u5728\u751f\u547d\u53d7\u5230\u5371\u9669\u7684\u65f6\u5019\uff0c\u81ea\u662f\u672c\u80fd\u7684\u4f1a \u7528\u5168\u529b\u62b5\u5fa1\u7684\u3002\u6a80\u9053\u6210\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u7acb\u5373\u8fd8\u51fb\u3002 \u3000\u3000\u4ed6\u4f7f\u7684\u8fd9\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u94c1\u95e8\u95e9\u201d\uff0c\u662f\u653b\u5b88\u517c\u5907\u7684\u62db\u6570\u3002\u4e00\u638c\u62a4\u80f8\uff0c\u4e00\u638c\u53cd\u62e8\u654c \u8155\u3002 \u3000\u3000\u4f46\u54c8\u5fc5\u56fe\u7684\u638c\u6cd5\u771f\u662f\u5947\u5e7b\u65e0\u6bd4\uff0c\u6a80\u9053\u6210\u7684\u201c\u94c1\u95e8\u95e9\u201d\u4e5f\u95e9\u4e0d\u4f4f\uff0c\u53ea\u542c\u5f97\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c \u4ed6\u8fd9\u4e00\u638c\u5df2\u662f\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u5728\u6a80\u9053\u6210\u7684\u80f8\u819b\u4e0a\uff0c\u8fd9\u4e00\u5239\u90a3\uff0c\u6a80\u516c\u76f4\u4e0d\u7531\u5f97\u51b7\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u6697 \u53eb\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u521a\u624d\u8bf4\u51fa\u513f\u5b50\u4e0d\u61c2\u6b66\u529f\uff0c\u8bf7\u54c8\u5fc5\u56fe\u624b\u4e0b\u7559\u60c5\u7684\u90a3\u53e5\u8bdd\uff0c\u771f\u6b63\u7684\u7528\u610f\u5176\u5b9e\u8fd8\u4e0d\u662f\u771f \u7684\u8981\u5411\u54c8\u5fc5\u56fe\u6c42\u60c5\uff0c\u800c\u662f\u63d0\u9192\u513f\u5b50\u7684\u3002 \u3000\u3000\u8981\u77e5\u54c8\u5fc5\u56fe\u662f\u5949\u547d\u6765\u53ec\u6a80\u516c\u76f4\u5165\u4eac\u7684\uff0c\u5f53\u7136\u662f\u4e0d\u80fd\u505a\u5f97\u592a\u7edd\uff0c\u8981\u662f\u6a80\u9053\u6210\u5047\u88c5\u4e0d\u61c2\u6b66 \u529f\uff0c\u4e5f\u4e0d\u7528\u5185\u529b\u62db\u67b6\uff0c\u54c8\u5fc5\u56fe\u4e00\u5b9a\u4e0d\u4f1a\u65bd\u5c55\u6740\u624b\u3001\u4f46\u82e5\u7ed9\u4ed6\u77e5\u9053\u6a80\u9053\u6210\u7684\u6b66\u529f\u51e0\u4e4e\u53ef\u4ee5\u548c\u4ed6 \u68cb\u9f13\u76f8\u5f53\uff0c\u90a3\u5c31\u975e\u903c\u4ed6\u65bd\u5c55\u6740\u624b\u4e0d\u53ef\u4e86\u3002\u6a80\u516c\u76f4\u6697\u793a\u513f\u5b50\u653e\u5f03\u62b5\u5fa1\uff0c\u8fd9\u4e00\u7740\u770b\u6765\u867d\u662f\u201c\u9669 \u68cb\u201d\uff0c\u5176\u5b9e\u662f\u53ea\u6709\u5982\u6b64\uff0c\u624d\u80fd\u4fdd\u5f97\u4f4f\u513f\u5b50\u7684\u6027\u547d\u3002 \u3000\u3000\u4f46\u89c1\u53ea\u4e00\u62db\uff0c\u513f\u5b50\u5c31\u7ed9\u54c8\u5fc5\u56fe\u6253\u4e2a\u6b63\u7740\uff0c\u8fd9\u5374\u4e5f\u662f\u5927\u51fa\u6a80\u516c\u76f4\u610f\u6599\u4e4b\u5916\u7684\uff01 \u3000\u3000\u4f46\u8fd8\u6709\u66f4\u52a0\u4ee4\u4ed6\u610f\u6599\u4e4b\u5916\u7684\uff01 \u3000\u3000\u4f46\u8fd8\u6709\u66f4\u4ee4\u4ed6\u610f\u6599\u4e0d\u5230\u7684\u4e8b\u60c5\u5728\u540e\u5934\u3002 \u3000\u3000\u6a80\u9053\u6210\u88ab\u54c8\u5fc5\u56fe\u4e00\u62f3\u6253\u7740\uff0c\u6574\u4e2a\u8eab\u5b50\u98de\u4e86\u8d77\u6765\uff0c\u4f46\u5728\u6a80\u9053\u6210\u7684\u611f\u89c9\uff0c\u5374\u597d\u50cf\u662f\u88ab\u4e00\u53ea\u65e0 \u5f62\u7684\u624b\u63d0\u4e86\u8d77\u6765\uff0c\u53c8\u8f7b\u8f7b\u653e\u4e0b\u4f3c\u7684\uff0c\u4ed6\u7684\u811a\u6cbe\u5b9e\u5730\uff0c\u53d1\u89c9\u81ea\u5df1\u7adf\u7136\u6beb\u53d1\u65e0\u4f24\u3002\u8fd9\u4e2a\u7ed3\u679c\uff0c\u4e0d \u4f46\u662f\u4ed6\u7684\u7236\u4eb2\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u662f\u5b8c\u5168\u610f\u60f3\u4e0d\u5230\u7684\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u4e0d\u89c9\u4e00\u7247\u832b\u7136\uff0c \u5446\u5446\u5730\u671b\u7740\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u5012\u4e5f\u4e0d\u7b97\u9a97\u6211\u3002\u4ee4\u90ce\u867d\u7136\u61c2\u5f97\u4e00\u70b9\u6b66\u529f\uff0c\u4f46\u6b66\u529f\u5374\u751a\u5e73 \u5eb8\uff0c\u4ee5\u4f60\u7684\u6240\u4e3a\uff0c\u8bf4\u4ed6\u4e0d\u61c2\u6b66\u529f\u4e5f\u4e0d\u4e3a\u8fc7\u4e86\u3002\u6211\u53ea\u5947\u602a\uff0c\u4f60\u4e00\u8eab\u60ca\u4eba\u672c\u9886\uff0c\u4e3a\u4f55\u4e0d\u4f20\u513f \u5b50\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u53ea\u8981\u5bf9\u65b9\u4e00\u51fa\u624b\uff0c\u4ed6\u5c31\u80fd\u770b\u51fa\u8fd9\u4eba\u7684\u6b66\u529f\u6df1\u6d45\u3002\u5728\u4ed6\u7684\u4f30\u8ba1\uff0c \u54c8\u5fc5\u56fe\u7684\u6b66\u529f\u5e94\u8be5\u662f\u548c\u4ed6\u7684\u513f\u5b50\u76f8\u5dee\u4e0d\u8fdc\u7684\uff0c\u4f46\u5982\u4eca\u54c8\u5fc5\u56fe\u7adf\u7136\u8bf4\u4ed6\u7684\u513f\u5b50\u7684\u6b66\u529f\u5e73\u5eb8\uff0c\u800c \u4e14\u770b\u6837\u5b50\u53c8\u4e0d\u50cf\u662f\u8bf4\u201c\u53cd\u8bed\u201d\u3002 \u3000\u3000\u201c\u96be\u9053\u662f\u6210\u513f\u7ec8\u4e8e\u542c\u61c2\u4e86\u6211\u7684\u6697\u793a\uff0c\u4ed6\u5728\u6700\u540e\u4e00\u523b\u7ec8\u4e8e\u5192\u4e86\u751f\u547d\u7684\u5371\u9669\uff0c\u5047\u88c5\u4e0d\u61c2\u6b66 \u529f\uff1f\u201d\u4f46\u770b\u513f\u5b50\u90a3\u4e00\u6d3e\u832b\u7136\u7684\u795e\u6001\uff0c\u53c8\u4e0d\u50cf\u662f\u5047\u88c5\u5f97\u6765\u3002 \u3000\u3000\u4ed6\u5927\u60d1\u4e0d\u89e3\uff0c\u4e5f\u53ea\u80fd\u5047\u88c5\u80e1\u6d82\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u5c0f\u513f\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u6211 \u4e5f\u53ea\u662f\u76fc\u671b\u4ed6\u80fd\u591f\u65e0\u707e\u65e0\u96be\uff0c\u5728\u5c71\u4e0a\u6253\u730e\u8fc7\u8fd9\u4e00\u751f\u7684\uff0c\u4e00\u4e2a\u5e73\u51e1\u7684\u730e\u4eba\uff0c\u53c8\u4f55\u987b\u61c2\u5f97\u4ec0\u4e48\u9ad8 \u6df1\u7684\u6b66\u529f\uff1f\u4f55\u51b5\u5373\u4f7f\u7ec3\u6210\u4e86\u7edd\u4e16\u7684\u6b66\u529f\uff0c\u4e5f\u662f\u96be\u514d\u4e00\u6b7b\uff0c\u7ec3\u6210\u529f\u4e86\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u6709\u4e2a\u539f\u56e0\uff0c\u6a80\u516c\u76f4\u4e00\u65f6\u5c1a\u672a\u60f3\u5230\uff0c\u539f\u6765\u4ed6\u7684\u513f\u5b50\u662f\u7ed9\u5f20\u708e\u4ee5\u72ec\u95e8\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\u7684\u3002\u800c \u4e14\u4ed6\u5728\u559d\u4e86\u6bd2\u6c64\u4e4b\u540e\uff0c\u5185\u529b\u5269\u4e0b\u6765\u7684\u4ea6\u5df2\u4e0d\u53ca\u539f\u6765\u7684\u4e24\u6210\u3002\u867d\u7136\u4ed6\u4ecd\u7136\u662f\u53ef\u4ee5\u7528\u4e00\u9897\u5c0f\u5c0f\u7684 \u9ec4\u8c46\uff0c\u5c31\u7ed9\u513f\u5b50\u89e3\u5f00\u7a74\u9053\uff0c\u4f46\u5374\u672a\u80fd\u4ee4\u513f\u5b50\u6c14\u8840\u7545\u901a\u3002\u8fd9\u79cd\u7528\u91cd\u624b\u6cd5\u6240\u70b9\u7684\u7a74\u9053\uff0c\u52c9\u5f3a\u89e3\u5f00 \u4e4b\u540e\uff0c\u6700\u5c11\u8fd8\u5f97\u534a\u4e2a\u65f6\u8fb0\uff0c\u65b9\u59cb\u80fd\u591f\u6062\u590d\u539f\u6709\u7684\u529f\u529b\u3002 \u3000\u3000\u69fd\u516c\u76f4\u8bdd\u72b9\u672a\u4e86\uff0c\u54c8\u5fc5\u56fe\u5df2\u662f\u4e00\u8dc3\u800c\u524d\uff0c\u638c\u5fc3\u5e16\u4e0a\u4e86\u4ed6\u7684\u5927\u690e\u7a74\uff0c\u539f\u6765\u54c8\u5fc5\u56fe\u5bf9\u4ed6\u8fd8\u662f \u4e0d\u65e0\u987e\u5fcc\uff0c\u6240\u4ee5\u690d\u516c\u76f4\u82e6\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u6211\u5df2\u662f\u5feb\u8981\u6b7b\u7684\u4eba\u4e86\uff0c\u8981\u662f\u4f60\u80af\u7ed9\u6211\u4e00\u4e2a\u75db\u5feb\u3002\u8ba9\u6211 \u9a6c\u4e0a\u6b7b\u4ea1\uff0c\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u522b\u8fd9\u6837\u60f3\uff0c\u4f60\u7684\u8363\u534e\u5bcc\u8d35\u8fd8\u5728\u540e\u5934 \u5462\uff0c\u4f60\u8981\u6b7b\u6211\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u6b7b\u7684\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\u3002\u4ed6\u5df2\u66ff\u6a80\u516c\u76f4\u628a\u8fc7\u4e86\u8109\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u8109\u8c61\u662f\u8870\u5f31\u5df2\u6781\uff0c\u79bb\u6b7b\u4e0d\u8fdc\u4e86\u3002\u96be \u9053\u5f53\u771f\u662f\u670d\u4e86\u6bd2\uff1f\u201d\u5f53\u4e0b\u56de\u8fc7\u5934\u6765\u3002\u5411\u547c\u6c99\u9f99\u62db\u4e00\u62db\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u770b\u770b\u6a80\u8d1d\u52d2\u4e2d\u7684\u662f\u4ec0 \u4e48\u6bd2\uff1f\u201d \u3000\u3000\u539f\u6765\u547c\u5bb6\u5144\u5f1f\u7684\u6240\u5b66\u5404\u6709\u6240\u957f\uff0c\u547c\u6c99\u9f99\u662f\u5bf9\u836f\u7269\u5b66\u751a\u6709\u7814\u7a76\u7684\uff0c\u800c\u4e14\u64c5\u4e8e\u89e3\u6bd2\u3002 \u3000\u3000\u4ed6\u4e0a\u6765\u4ed4\u7ec6\u5bdf\u89c6\uff0c\u4e0d\u89c9\u76b1\u8d77\u7709\u5934\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u7684\u5fc3\u4e0a\u597d\u50cf\u60ac\u4e86\u5341\u4e94\u4e2a\u540a\u6876\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u547c\u6c99\u9f99\u9053\uff1a\u201c\u6a80\u8d1d\u52d2\u7684\u786e \u662f\u670d\u4e86\u5267\u6bd2\uff0c\u4e3b\u836f\u662f\u5b54\u96c0\u80c6\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u867d\u7136\u5bf9\u836f\u7269\u5b66\u65e0\u751a\u7814\u7a76\uff0c\u4e5f\u77e5\u5b54\u96c0\u62c5\u662f\u5929\u4e0b\u4e03\u5927\u5267\u6bd2\u4e4b\u4e00\uff0c\u5b54\u96c0\u80c6\u7814\u6210\u7c89\u672b\u53ea\u987b \u8638\u4e0a\u4e00\u70b9\uff0c\u653e\u5728\u8336\u9152\u4e4b\u4e2d\u7ed9\u4eba\u670d\u4e0b\uff0c\u5c31\u53ef\u4ee5\u7acb\u5373\u4ee4\u4eba\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\uff0c\u8fd9\u79cd\u6bd2\u51e0\u4e4e\u662f\u65e0\u836f\u53ef\u89e3 \u7684\uff01 \u3000\u3000\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u5230\uff1a\u201c\u8fd8\u6709\u6551\u4e48\uff1f\u201d \u3000\u3000\u547c\u6c99\u9f99\u6c89\u541f\u4e0d\u8bed\uff0c\u54c8\u5fc5\u56fe\u5927\u4e3a\u7740\u6025\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u547c\u8001\u5927\uff0c\u8bf7\u5c3d\u4f60\u7684\u6240\u80fd\uff0c\u633d\u6551\u6a80\u8d1d\u52d2 \u7684\u6027\u547d\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u54b1\u4eec\u4e5f\u5f97\u8ba9\u4ed6\u89c1\u5230\u7687\u4e0a\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u56fd\u7684\u7687\u5e1d\uff0c\u8981\u4ed6\u4eec\u628a\u6a80\u516c\u76f4\u6293\u6765\uff0c\u771f\u6b63\u7684\u76ee\u7684\u5f53\u7136\u5e76\u4e0d\u662f\u8981\u91cd\u7528\u6a80\u516c\u76f4\u800c\u662f\u6709\u4ef6 \u5173\u4e8e\u738b\u5ba4\u7684\u79d8\u5bc6\uff0c\u4ed6\u8981\u5957\u51fa\u6a80\u516c\u76f4\u7684\u53e3\u4f9b\u3002\u53e6\u5916\u4ed6\u8fd8\u8981\u5229\u7528\u6a80\u516c\u76f4\u6765\u6536\u4e70\u4eba\u5fc3\uff08\u6a80\u516c\u76f4\u662f\u53cd \u6218\u6d3e\u6240\u62e5\u6234\u7684\u4eba\u3002\uff09\u91d1\u56fd\u7684\u7687\u5e1d\u5e74\u5df2\u8001\u8fc8\uff0c\u6b63\u60f3\u4f20\u4f4d\u7ed9\u592a\u5b50\u60f3\u5728\u4f20\u4f4d\u4e4b\u524d\u3002\u4eb2\u81ea\u81ea\u7406\u597d\u8fd9\u4ef6 \u4e8b\u60c5\u3002 \u3000\u3000\u7687\u5e1d\u5f53\u7136\u4e0d\u4f1a\u628a\u81ea\u5df1\u7684\u4f01\u56fe\u660e\u660e\u767d\u767d\u5730\u544a\u8bc9\u54c8\u5fc5\u56fe\uff0c\u4f46\u4ed6\u7684\u5723\u65e8\u5374\u662f\u8bf4\u5f97\u5341\u5206\u660e\u767d\uff0c\u8981 \u6d3b\u7684\uff0c\u4e0d\u8981\u6b7b\u7684\uff01\u662f\u4ee5\u54c8\u5fc5\u56fe\u5fc5\u987b\u8bbe\u6cd5\u633d\u56de\u6a80\u516c\u76f4\u7684\u6027\u547d\u3002\u4ed6\u5bf9\u547c\u6c99\u9f99\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u5176\u5b9e\u4ea6 \u5373\u662f\u5411\u547c\u6c99\u9f99\u9053\uff1a\u201c\u8fd9\u8001\u5934\u8981\u6b7b\uff0c\u4e5f\u5f97\u8ba9\u4ed6\u89c1\u5230\u4e86\u7687\u4e0a\u624d\u6b7b\uff01\u201d \u3000\u3000\u547c\u6c99\u9f99\u9053\uff1a\u201c\u54c8\u7edf\u9886\uff0c\u4f60\u8eab\u4e0a\u53ef\u5907\u6709\u5927\u5185\u79d8\u5236\u7684\u7eed\u547d\u91d1\u4e39\u4e48\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6709\uff01\u201d\u547c \u6c99\u9f99\u9053\uff1a\u201c\u5148\u7ed9\u4ed6\u670d\u4e0b\u4e00\u9897\u3002\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u5df2\u4e0d\u60f3\u6d3b\u4e86\uff0c\u53c8\u4f55\u5fc5\u7cdf\u584c\u4f60\u4eec\u7684\u7eed\u547d\u91d1 \u4e39\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u6d3b\u4e5f\u4e0d\u6210\uff01\u201d\u4e00\u6258\u4ed6\u7684\u4e0b\u5df4\uff0c\u628a\u4e00\u9897\u7eed\u547d\u91d1\u4e39\u786c\u585e\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u903c \u4ed6\u54bd\u4e0b\u3002 \u3000\u3000\u547c\u6c99\u9f99\u9053\uff1a\u201c\u8fd9\u836f\u4e38\u867d\u7136\u79f0\u4e3a\u7eed\u547d\u91d1\u4e39\uff0c\u4f46\u662f\u5426\u80fd\u591f\u7eed\u547d\uff0c\u8fd9\u53ef\u8fd8\u5f97\u770b\u6a80\u8d1d\u52d2\u81ea\u5df1\u3002\u201d \u6a80\u516c\u76f4\u677f\u8d77\u9762\u5b54\u4e0d\u7406\u4f1a\u4ed6\u3002\u54c8\u5fc5\u56fe\u5219\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u547c\u6c99\u9f99\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u7eed\u547d\u91d1\u4e39\u4e5f\u662f\u89e3\u4e0d\u4e86\u5b54\u96c0\u80c6\u4e4b\u6bd2\u7684\uff0c\u4f46\u53ef\u4ee5\u7565\u4e3a\u7f13\u548c\u6bd2\u6027\u7684\u53d1 \u4f5c\u3002\u5018\u82e5\u6362\u4e86\u53e6\u4e00\u4e2a\u4eba\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u2018\u7eed\u547d\u2019\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u5230\u4e86\u660e\u5929\uff0c\u4ecd\u662f\u4e0d\u514d\u4e00\u6b7b\u3002\u4e0d \u8fc7\uff0c\u64c5\u8d1d\u52d2\u548c\u522b\u4eba\u4e0d\u540c\uff0c\u4ed6\u662f\u7ec3\u6709\u4e0a\u4e58\u5185\u529f\u7684\uff0c\u53ea\u8981\u4ed6\u6709\u6c42\u751f\u4e4b\u5ff5\uff0c\u8fd0\u7528\u5185\u529f\u8c03\u5300\u6c14\u606f\u914d\u5408 \u836f\u529b\u7684\u8fd0\u884c\uff0c\u90a3\u4e48\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u89c1\u5f97\u5230\u7687\u4e0a\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5fae\u7b11\u9053\uff1a\u201c\u877c\u8681\u5c1a\u4e14\u8d2a\u751f\uff0c\u4f55\u51b5\u662f\u4eba\u3002\u6a80\u738b\u7237\uff0c\u4f60\u662f\u6050\u6015\u7687\u4e0a\u964d\u7f6a\u624d\u670d\u6bd2\u7684\uff0c\u73b0 \u5728\u4ec0\u4e48\u90fd\u8bf4\u660e\u767d\u4e86\u3002\u7687\u4e0a\u5bf9\u4f60\u5b9e\u5728\u662f\u5bbd\u539a\u65e0\u6bd4\uff0c\u4f60\u5e94\u8be5\u53ef\u4ee5\u629b\u5f00\u987e\u8651\uff0c\u4e0d\u518d\u6c42\u6b7b\u4e86\u5427\uff1f\u201d\u6a80 \u516c\u76f4\u4e5f\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u624d\u529d\u6211\u6c42\u751f\u3002\u4e0d\u5acc\u592a\u8fdf\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u4f1a\u8fdf\u3002\u4f60\u6ca1\u542c\u89c1\u547c\u6c99\u9f99\u8bf4\u5417\uff0c\u4f60\u5df2\u7ecf\u670d\u4e86\u7eed\u547d\u91d1\u4e39\uff0c\u53ea\u8981\u4f60\u6709\u6c42\u751f\u4e4b \u5ff5\uff0c\u4f60\u5c31\u53ef\u4ee5\u6d3b\u4e0b\u53bb\uff01\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u80fd\u591f\u6d3b\u591a\u4e45\uff1f\u201d \u3000\u3000\u547c\u6c99\u9f99\u9053\uff1a\u201c\u4eba\u5bff\u96be\u6d4b\uff0c\u4e0d\u8fc7\u80fd\u591f\u591a\u6d3b\u4e00\u5929\u90fd\u662f\u597d\u7684\u3002\u201d\u6a80\u516c\u76f4\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u591a\u6d3b\u4e00 \u5929\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u547c\u6c99\u9f99\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u53ea\u591a\u6d3b\u4e00\u5929\u3002\u6a80\u8d1d\u52d2\uff0c\u6211\u548c\u4f60\u8bf4\u8001\u5b9e\u8bdd\uff0c\u4e0d\u9519\uff0c\u7eed\u547d\u91d1\u4e39\u5e76\u975e\u5bf9\u75c7 \u89e3\u836f\uff0c\u6211\u4e0d\u662f\u795e\u4ed9\uff0c\u4e5f\u4e0d\u6562\u5984\u65ad\u4f60\u7684\u5bff\u5143\u3002\u4f46\u4ee5\u4f60\u7684\u5185\u529f\u9020\u8be3\uff0c\u52a0\u4e0a\u6211\u4eec\u7684\u5c0f\u5fc3\u7167\u6599\uff0c\u6211\u6562 \u62c5\u4fdd\u3002\u4f60\u603b\u53ef\u4ee5\u6d3b\u7740\u89c1\u5230\u7687\u4e0a\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6211\u6d3b\u4e0b\u53bb\uff0c\u539f\u6765\u662f\u4e3a\u4e86\u65b9\u4fbf\u4f60\u4eec\u4ea4\u5dee\u3002\u591a\u8c22\u4e86\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4e86\u4f60\u7684\u597d\u5440\uff0c\u877c\u8681\u5c1a\u4e14\u8d2a\u751f\u5462\uff0c\u6211\u4eec\u8981\u4f60\u6d3b\u4e0b\u53bb\uff0c\u96be \u9053\u4f60\u53cd\u800c\u4e0d\u613f\u610f\u4e48\uff1f\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u662f\u65e0\u77e5\u65e0\u8bad\u7684\u877c\u8681\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u4e0d\u8981\u52a8\u4ec0\u4e48\u80e1\u6d82\u5ff5\u5934\u4e86\u3002\u8bf7\u4f60\u63a5\u8fc7\u5723\u65e8\uff0c\u8ddf\u6211\u4eec\u4e0a\u4eac\u5427\u3002\u4f60\u8d70 \u4e0d\u52a8\u4e5f\u4e0d\u6253\u7d27\uff0c\u6211\u4eec\u4f1a\u62ac\u4f60\u4e0b\u5c71\uff0c\u5c71\u4e0b\u6709\u8f66\u9a6c\u5907\u7528\uff0c\u6211\u4eec\u4f1a\u7167\u6599\u4f60\u4e00\u8def\u5e73\u5b89\u7684\u3002\u201d\u6a80\u516c\u76f4 \u9053\uff1a\u201c\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u6211\u4e0d\u80fd\u8ddf\u4f60\u4eec\u4e0a\u4eac\uff01\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd8\u662f\u4e0d\u80fd\uff1f\u96be\u9053\u4f60\u4e0d\u60f3\u6d3b\u7740 \u89c1\u7687\u4e0a\uff1f\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u53cd\u6b63\u8fdf\u65e9\u90fd\u662f\u4e00\u6b7b\uff0c\u6211\u60f3\u6b7b\u5f97\u5b89\u4e50\u4e00\u4e9b\uff0c\u8fd9\u91cc\u662f\u6211\u7684\u5bb6\uff0c\u6211\u60f3\u6b7b\u5728 \u5bb6\u91cc\uff0c\u7701\u5f97\u957f\u9014\u8dcb\u6d89\uff0c\u5230\u4e86\u4eac\u4e5f\u662f\u4e2a\u6b7b\u3002\u540c\u65f6\u4e5f\u53ef\u7701\u6389\u4f60\u4eec\u6cbf\u9014\u7167\u6599\u6211\u7684\u9ebb\u70e6\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f46\u8fd9\u662f\u5723\u65e8\u5440\uff0c\u4f60\u600e\u80fd\u8f9c\u8d1f\u7687\u4e0a\u4e4b\u6069\uff0c\u62d2\u7edd\u4e0a\u4eac\u9762\u5723\uff1f \u3000\u3000\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u4eec\u66ff\u6211\u8c22\u5723\u4e0a\u6d2a\u6069\u5427\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u8fd8\u51c6\u5907\u91cd\u7528\u4f60\u5462\uff0c\u4f60\u5230\u4e86\u4eac\u5e08\uff0c\u7687\u4e0a\u4e00\u5b9a\u4f1a\u60f3\u5c3d\u529e\u6cd5\u633d\u6551\u4f60\u7684\u6027\u547d\u3002 \u5927\u5185\u6709\u7684\u662f\u7075\u4e39\u5999\u836f\uff0c\u8fd8\u6709\u5f7b\u533b\u66ff\u4f60\u533b\u75c5\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u8fd8\u53ef\u4ee5\u957f\u547d\u767e\u5c81\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u7b11\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u7687\u4e0a\u662f\u8ba4\u4e3a\u6211\u8fd8\u6709\u7528\u5904\uff0c\u624d\u5e0c\u671b\u6211\u6d3b\u4e0b\u53bb\u7684\uff0c\u4f46\u6211\u5bf9 \u7687\u4e0a\u4e1d\u6beb\u6ca1\u6709\u7528\u5904\uff0c\u7687\u4e0a\u4e5f\u4e0d\u5728\u4e4e\u6211\u662f\u751f\u662f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u6587\u6b66\u5168\u624d\uff0c\u600e\u4e48\u80fd\u8bf4\u662f\u6ca1\u7528\uff1f\u201d\u6a80\u516c\u76f4\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u591a\u8c22\u4f60 \u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\u3002\u4f46\u597d\u50cf\u521a\u624d\u4e5f\u8bf4\u8fc7\uff0c\u6211\u90a3\u4e9b\u4e3b\u5f20\u3002\u7687\u4e0a\u8ba4\u4e3a\u662f\u2018\u8fc2\u8150\u4e4b\u89c1\u201d\uff0c\u76f4\u5230\u4eca\u5929\uff0c\u7687 \u4e0a\u4ecd\u662f\u5341\u5206\u4e0d\u6ee1\u7684\u3002\u6211\u4e0d\u4f1a\u6539\u53d8\u6211\u7684\u4e3b\u5f20\uff0c\u90a3\u4e48\u4f55\u5fc5\u4f1a\u60f9\u7687\u4e0a\u7684\u8ba8\u538c\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7981\u4e0d\u4f4f\u52c3\u7136\u53d1\u4f5c\uff0c\u8bf4\u9053\uff1a\u201c\u6297\u65e8\u4e4b\u7f6a\uff0c\u6a80\u738b\u7237\uff0c\u4f60\u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u4e0d\u9519\uff1b\u4f60\u670d\u4e86\u6bd2\uff0c\u4f60\u5df1\u62da\u4e86\u4e00\u6b7b\uff0c\u4f46\u4ee4\u90ce\u5462\uff0c\u4f60\u4e0d\u60f3\u4ee4\u90ce\u53d7\u5230\u8fde\u7d2f\u5427\uff1f\u4f60\u82e5\u80af\u5949\u65e8\uff0c\u4ee4 \u90ce\u53ef\u4ee5\u7ee7\u627f\u4f60\u7684\u7235\u4f4d\uff0c\u6709\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc\u8d35\u4f9b\u4ed6\u4eab\u53d7\uff1b\u4f46\u8981\u662f\u4f60\u4e0d\u80af\u63a5\u65e8\uff0c\u563f\u563f\uff0c\u540e\u679c\u5982\u4f55\uff0c \u90a3\u6211\uff0c\u6211\u53ef\u5c31\u4e0d\u6562\u8bf4\u4e86\uff01\u201d\u6a80\u9053\u6210\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u6562\u8bf4\uff0c\u5927\u4e0d\u4e86\u628a\u6211\u5904\u6b7b\uff0c\u6211\u80fd\u591f\u548c \u7236\u4eb2\u540c\u751f\u5171\u6b7b\uff0c\u8fd9\u6b63\u662f\u6211\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\uff01\u201c\u5411\u7236\u4eb2\u78d5\u4e86\u4e2a\u5934\uff0c\u7ee7\u7eed\u8bf4\u9053\u3002\u201d\u7239\u7239\uff0c\u4f60\u4e3a\u4e86\u91d1 \u56fd\u767e\u59d3\uff0c\u53cd\u5bf9\u6253\u4ed7\uff0c\u4f60\u624d\u662f\u771f\u6b63\u7684\u5fe0\u81e3\uff01\u7239\u7239\uff0c\u4f60\u820d\u751f\u53d6\u4e49\uff0c\u4e0d\u60dc\u629b\u5f03\u5bcc\u8d35\u8363\u534e\uff0c\u4f60\u771f\u662f\u6211 \u7684\u597d\u7236\u4eb2\uff01\u6211\u4e5f\u4e0d\u8981\u505a\u4ec0\u4e48\u8d1d\u5b50\uff0c\u6211\u53ea\u8981\u505a\u4f60\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u690d\u516c\u76f4\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u6127\u662f\u6211\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u653e\u8f6f\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u53ef\u4ee5\u6162\u6162\u5546\u91cf\uff0c\u4e0d\u5fc5\u5fd9\u7740\u5bfb\u6b7b\u89c5\u6d3b\u3002\u5bf9\u5566\uff0c\u542c\u8bf4\u8d1d\u5b50 \u5df2\u7ecf\u5a36\u4e86\u4e00\u4e2a\u6c49\u4eba\u4e4b\u5973\u505a\u59bb\u5ba4\uff0c\u4f60\u4eec\u7684\u4eb2\u5bb6\u5462\uff1f\u201c\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5728\u6211\u670d\u6bd2\u4e4b\u524d\uff0c\u6211\u5df2\u53eb\u4ed6\u4eec \u4e0b\u5c71\u53bb\u81ea\u5bfb\u751f\u8def\u4e86\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f60\u90a3\u4eb2\u5bb6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u662f\u9003\u907f\u6218\u7978\uff0c\u6765\u5230\u8fd9\u5c71\u4e0a\u5f00\u8352\u7684\u666e\u901a\u767e\u59d3\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u666e\u901a\u767e\u59d3\uff1f\u4f60\u80af\u548c\u4e00\u4e2a\u666e\u901a\u767e\u59d3\u7ed3\u6210\u4eb2\u5bb6\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u5fc3\u91cc\u60f3\u5230\uff1a\u201c\u542c\u8fd9\u53e3\u6c14\uff0c\u5927\u6982\u4ed6\u5bf9\u5f20\u708e\u4ea6\u5df2\u8d77\u4e86\u6000\u7591\uff0c\u4f46\u8fd8\u672a\u77e5\u4ed6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u65e9\u5df2\u662f\u666e\u901a\u767e\u59d3\u4e86\u3002\u800c\u4e14\u5728\u4eca\u65e5\u4e4b\u524d\uff0c\u6211\u7684\u5b69\u5b50\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4ed6\u8eab\u4e16\u3002\u201d\u6a80\u516c\u76f4 \u9053\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f60\u90a3\u4eb2\u5bb6\u77e5\u4e0d\u77e5\u9053\u4f60\u662f\u91d1\u56fd\u738b\u7237\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4ed6\u4e0d\u77e5\u9053\u3002\u201d\u8fd9\u662f\u4ed6\u5e73\u751f\u7b2c\u4e00\u6b21\u8bf4\u8c0e\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u90a3\u4f60\u7528\u4ec0\u4e48\u7406\u7531\u8981\u4ed6\u4eec\u9003\u8d70\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u53eb\u4ed6\u4eec\u9003\u8d70\u8d70\uff0c\u6211\u662f\u53eb\u4ed6\u4eec\u907f\u96be\u3002\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u90a3\u53c8\u6709\u4ec0\u4e48\u4e0d \u540c\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u8c01\u90fd\u77e5\u9053\u76ee\u4e0b\u5c31\u8981\u6253\u4ed7\u4e86\uff0c\u8fd9\u5ea7\u5c71\u4e5f\u53ef\u80fd\u6709\u519b\u961f\u624e\u8425\u7684\u3002\u56e0\u6b64\u6211\u53eb\u4ed6\u4eec\u56de \u5b8b\u56fd\u53bb\u8eb2\u907f\u6218\u7978\uff0c\u5e76\u975e\u662f\u56e0\u4e3a\u6211\u6015\u66b4\u9732\u8eab\u4efd\u624d\u53eb\u4ed6\u4eec\u9003\u8d70\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4ed6\u4eec\u771f\u7684\u662f\u5df2\u7ecf\u9003\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u53bb\u907f\u96be\uff01\u4f46\u4f60\u4e00\u5b9a\u8981\u7528\u2018\u9003\u8d70\u2019\u4e8c\u5b57\u6211\u4e5f\u4e0d\u548c\u4f60\u4e89\u8bba\u3002\u4f60\u4e0d\u4fe1\u5927\u53ef \u81ea\u5df1\u641c\uff0c\u53cd\u6b63\u53ea\u6709\u4e24\u95f4\u5c4b\u5b50\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u597d\uff0c\u547c\u8001\u4e8c\uff0c\u4f60\u53bb\u641c\u4e00\u641c\u770b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8eb2\u5728\u590d\u58c1\u91cc\u5fc3\u91cc\u5934\u535c\u535c\u5730\u8df3\uff0c\u5728\u5f20\u708e\u7684\u638c\u5fc3\u5199\u5b57\uff1a\u7239\u7239\u3002\u4f60\u6253\u5f97\u8fc7\u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u5728\u5979\u638c\u5fc3\u5199\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u4f46\u76ee\u524d\u4e0d\u5b9c\u5984\u52a8\u3002 \u3000\u3000\u8bf4\u8bdd\u5df2\u7ecf\u6b62\u3002\u590d\u58c1\u91cc\u7684\u5f20\u708e\u201c\u7236\u5973\u201d\uff0c\u623f\u95f4\u5185\u7684\u6a80\u516c\u76f4\u7237\u5b50\uff0c\u56db\u4e2a\u4eba\u90fd\u662f\u7ef7\u7d27\u4e86\u5fc3\u5f26\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5b69\u5b50\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u4e0d\u8981\u4f60\u62b1\uff0c\u653e\u5f00\u6211\uff0c\u653e\u5f00 \u6211\uff01\u201d\u547c\u6c99\u864e\u9053\uff1a\u201c\u6211\u662f\u4f60\u7239\u7239\u7684\u670b\u53cb\uff0c\u5982\u4eca\u6211\u5c31\u5e26\u4f60\u53bb\u89c1\u7239\u7239\u3002\u201d \u3000\u3000\u6a80\u9053\u6210\u7684\u5fc3\u5f80\u4e0b\u4e00\u6c89\uff0c\u4ed6\u7684\u513f\u5b50\u5df2\u7ecf\u7ed9\u547c\u6c99\u864e\u62b1\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u5b69\u5b50\u5145\u6ee1\u60f6\u60d1\u7684\u773c\u795e\u5411\u7236\u4eb2\u6c42\u52a9\uff0c\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u8fd9\u4eba\u4e0d\u80af\u653e\u5f00\u6211\u3002 \u3000\u3000\u4ed6\u8fd8\u8bf4\u662f\u4f60\u7684\u670b\u53cb\u5462\uff01\u201d \u3000\u3000\u6a80\u9053\u6210\u7981\u4e0d\u4f4f\u8981\u8dd1\u8fc7\u53bb\uff0c\u5374\u7ed9\u547c\u6c99\u9f99\u5c06\u4ed6\u4e00\u63a8\uff0c\u559d\u9053\uff1a\u201c\u5750\u4e0b\u4e0d\u8bb8\u4e71\u52a8\uff01\u201d \u3000\u3000\u4ed6\u4eec\u8fd9\u4e2a\u5b69\u5b50\u867d\u7136\u53ea\u6709\u4e03\u5c81\uff0c\u5374\u6bd4\u4e00\u822c\u540c\u5e74\u9f84\u7684\u5b69\u5b50\u806a\u660e\u5f97\u591a\u3002\u4e00\u89c1\u8fd9\u4e2a\u60c5\u5f62\u5c31\u56b7\uff1a \u201c\u4f60\u4eec\u9a97\u6211\uff0c\u4f60\u4eec\u6b3a\u8d1f\u6211\u7684\u7239\u7239\uff0c\u4e00\u5b9a\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\u3002\u7239\u7239\uff0c\u4f60\u544a\u8bc9\u6211\uff0c\u4ed6\u4eec\u662f\u5417\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4f60\u771f\u806a\u660e\u3002\u4ed6\u4eec\u5f53\u7136\u4e0d\u662f\u7239\u7239\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5b69\u5b50\u53c8\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u4e3a\u4ec0\u4e48\u54b3\u55fd\u5f97\u8fd9\u6837\u5389\u5bb3\u3002\u662f\u4ed6\u4eec\u6b3a\u8d1f\u4e86\u4f60\u5417\uff1f\u201d \u3000\u3000\u547c\u6c99\u864e\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u4e71\u53eb\u4e71\u56b7\uff0c\u518d\u53eb\u56b7\u6211\u634f\u6b7b\u4f60\uff01\u201d\u5b69\u5b50\u54c7\u5730\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6a80\u516c\u76f4\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4e0d\u8bb0\u5f97\u7237\u7237\u548c\u4f60\u8bf4\u8fc7\u7684\u8bdd\u5417\uff0c\u597d\u7537\u513f\u662f\u6ce8\u5b9a\u6d41\u8840\u4e0d\u6d41\u6cea\u7684\u3002 \u6076\u4eba\u6b3a\u4f60\u4e5f\u4e0d\u8981\u54ed\uff0c\u5f85\u4f60\u957f\u5927\u4e86\u518d\u627e\u6076\u4eba\u7b97\u5e10\uff01\u201d \u3000\u3000\u547c\u6c99\u864e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5e0c\u671b\u8fd9\u5b69\u5b50\u80fd\u591f\u957f\u5927\u6210\u4eba\uff0c\u4f60\u5148\u5f97\u542c\u6211\u4eec\u7684\u8bdd\u3002 \u3000\u3000\u201d \u3000\u3000\u5b69\u5b50\u679c\u7136\u4e0d\u54ed\u4e86\uff0c\u53ea\u662f\u72e0\u72e0\u5730\u76ef\u7740\u6b3a\u8d1f\u4ed6\u7684\u4eba\u3002 \u3000\u3000\u547c\u6c99\u864e\u9053\uff1a\u201c\u4f60\u8981\u6211\u653e\u5f00\u4f60\uff0c\u53ef\u8981\u8001\u5b9e\u56de\u7b54\u6211\u3002\u4f60\u7684\u5916\u516c\u5462\uff1f\u4f60\u7684\u5988\u5988\u5462\uff1f\u4f60\u77e5\u9053\u4ed6\u4eec \u5728\u90a3\u91cc\u5417\uff1f\u201d \u3000\u3000\u5b69\u5b50\u6ca1\u6709\u56de\u7b54\u4ed6\uff0c\u4f46\u8fd9\u4e2a\u95ee\u9898\u53ef\u6b63\u662f\u4ed6\u60f3\u77e5\u9053\u7684\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u5411\u7236\u4eb2\u53d1\u95ee\uff1a\u201c\u7239\u7239\uff0c\u516c\u516c \u548c\u5988\u5988\u5462\uff1f\u516c\u516c\u521a\u624d\u8fd8\u548c\u6211\u73a9\u800d\u7684\uff0c\u4e0d\u77e5\u600e\u7684\u6211\u5c31\u7761\u7740\u4e86\uff0c\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u4e45\uff0c\u8fd8\u4ee5\u4e3a\u662f\u521a\u624d \u7684\u4e8b\u60c5\u3002\u201d\u6a80\u9053\u6210\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4f60\u522b\u591a\u95ee\uff0c\u53ea\u8981\u4f60\u4e56\uff0c\u516c\u516c\u548c\u5988\u5988\u4f1a\u56de\u6765\u3002\u201d \u3000\u3000\u547c\u6c99\u864e\u89c1\u5957\u4e0d\u51fa\u5b69\u5b50\u7684\u53e3\u98ce\uff0c\u8f6c\u800c\u9762\u5411\u6a80\u516c\u76f4\u51b7\u7b11\u53d1\u8bdd\uff1a\u201c\u4f60\u8bf4\u4f60\u90a3\u6c49\u4eba\u662f\u666e\u901a\u767e\u59d3\uff0c \u6050\u6015\u4e0d\u5bf9\u628a\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u547c\u6c99\u864e\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u7ed9\u4eba\u70b9\u4e86\u7761\u7a74\uff0c\u666e\u901a\u767e\u59d3\u7109\u80fd\u61c2\u5f97\u4e0a\u4e58\u70b9\u7a74\u529f\u592b\uff1f\u201d\u6a80\u516c\u76f4\u9053\uff1a \u201c\u662f\u6211\u70b9\u7684\u3002\u201d \u3000\u3000\u547c\u6c99\u864e\u51b7\u7b11\u9053\uff1a\u201c\u6a80\u738b\u7237\uff0c\u6211\u77e5\u9053\u4f60\u6b66\u52ab\u9ad8\u5f3a\u3002\u4f46\u8fd9\u79cd\u70b9\u7761\u7a74\u7684\u529f\u592b\uff0c\u5374\u662f\u6c5f\u5357\u6c49\u4eba\u7684 \u6b66\u5b66\uff0c\u548c\u6a80\u8d1d\u52d2\u4f60\u6240\u5b66\u7684\u5b8c\u5168\u4e0d\u540c\u3002\u597d\u5728\u6211\u5bf9\u8fd9\u95e8\u7a74\u7684\u529f\u592b\u7565\u77e5\u76ae\u6bdb\uff0c\u8fd9\u4eba\u7528\u7684\u4e5f\u662f\u6700\u8f7b\u7684 \u624b\u6cd5\uff0c\u6211\u624d\u80fd\u591f\u7ed9\u8fd9\u5b69\u5b50\u89e3\u5f00\u3002\u201d\u539f\u6765\u547c\u6c99\u864e\u7684\u5e08\u7236\u662f\u91d1\u56fd\u6709\u6570\u7684\u70b9\u7a74\u540d\u5bb6\uff0c\u5929\u4e0b\u5404\u5bb6\u5404\u6d3e \u7684\u70b9\u7a74\u529f\u592b\u5730\u5dee\u4e0d\u591a\u901a\u6653\u5341\u4e4b\u4e03\u516b\u3002 \u3000\u3000\u6a80\u516c\u76f4\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u662f\u5417\uff1f\u6211\u53ef\u4e0d\u77e5\u6211\u8fd9\u4eb2\u5bb6\u61c2\u5f97\u6b66\u529f\u3002\u4f46\u4ed6\u4eec\u5df2\u7ecf\u8d70\u4e86\u3002\u4f60\u4eec\u82e5\u662f\u95f2 \u7740\u6ca1\u4e8b\u505a\uff0c\u5c31\u81ea\u5df1\u53bb\u8bbf\u67e5\u4ed6\u5427\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u51b7\u51b7\u8bf4\u771f\uff1a\u201c\u6a80\u738b\u7237\uff0c\u4f60\u7684\u4eb2\u5bb6\u8d70\u4e86\u3002\u4f60\u8fd9\u5b59\u513f\u53ef\u662f\u8d70\u4e0d\u4e86\uff01 \u3000\u3000\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4ed6\u4e0d\u8fc7\u662f\u4e2a\u4e03\u5c81\u7684\u5b69\u5b50\uff0c\u4f60\u8981\u5c06\u4ed6\u600e\u6837\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fdd\u6297\u5723\u65e8\uff0c\u8be5\u5f53\u4f55\u7f6a\uff0c\u6a80\u8d1d\u52d2\uff0c\u4f60\u5e94\u8be5\u6bd4\u6211\u6e05\u695a\u3002\u6ee1\u95e8\u6284\u65a9\uff0c\u90a3\u4e0d\u8fc7\u53ea\u662f \u6700\u8f7b\u7684\u5211\u7f5a\uff0c\u8bba\u5f8b\u4f8b\u8981\u8bdb\u4e09\u65cf\u7684\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u6012\u9053\uff1a\u201c\u4e00\u4e2a\u5c0f\u5b69\u5b50\u4f60\u4eec\u4e5f\u4e0d\u653e\u8fc7\uff0c\u7528\u5b69\u5b50\u6765\u5a01\u80c1\u6211\uff0c\u592a\u5351\u9119\u4e86\u5427\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u8bdd\u4f60\u5e94\u8be5\u5411\u7687\u4e0a\u53bb\u8bf4\uff0c\u6211\u4eec\u53ea\u77e5\u5949\u65e8\u884c\u4e8b\u3002\u201d \u3000\u3000\u6a80\u9053\u6210\u5f3a\u6291\u5fc3\u4e2d\u60b2\u6124\uff0c\u54fd\u58f0\u8bf4\u9053\uff1a\u201c\u8986\u5de2\u4e4b\u4e0b\uff0c\u7109\u6709\u5b8c\u5375\u3002\u7239\u7239\uff0c\u54b1\u4eec\u884c\u53ef\u4f46\u6c42\u65e0\u6127\u4e8e \u5fc3\uff0c\u6050\u6015\u4e5f\u987e\u4e0d\u5f97\u51b2\u513f\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8eb2\u5728\u590d\u58c1\u91cc\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5fc3\u4e2d\u60ca\u60f6\u5df2\u6781\uff0c\u4e0d\u81ea\u89c9\u5730\u98a4\u6296\u8d77\u6765\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u7ad9\u4e86\u8d77\u6765\uff0c\u773c\u73e0\u6ef4\u6ef4\u6e9c\u6e9c\u5730\u8f6c\uff0c\u8033\u6735\u4e5f\u4f3c\u4e4e\u7ad6\u8d77\u6765\u542c\u3002\u547c\u6c99\u9f99\u6115\u4e86\u4e00\u6115\uff0c\u95ee\u9053\uff1a \u201c\u54c8\u603b\u7ba1\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u4f3c\u4e4e\u85cf\u6709\u4eba\u3002\u201d \u3000\u3000\u547c\u6c99\u864e\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff0c\u91cc\u91cc\u5916\u5916\uff0c\u6211\u90fd\u5df2\u7ecf\u7406\u8fc7\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u5df2\u7ecf\u8e0f\u51fa\u95e8\u5916\u5f20\u671b \u4e00\u4e0b\uff0c\u53c8\u518d\u56de\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5916\u9762\u4e5f\u6ca1\u89c1\u6709\u4eba\u6765\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u6211\u63a5\u65e8\uff01\u201d \u3000\u3000\u201c\u6211\u63a5\u65e8\u201d\u8fd9\u4e09\u5b57\uff0c\u767b\u65f6\u628a\u4ed6\u4eec\u7684\u6ce8\u610f\u529b\u5438\u5f15\u8fc7\u53bb\u4e86\u3002\u54c8\u5fc5\u56fe\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u7ba1\u8fd9\u5c4b\u5b50 \u91cc\u662f\u5426\u85cf\u6709\u4eba\uff0c\u6211\u8d70\u7684\u65f6\u5019\uff0c\u653e\u4e00\u628a\u706b\uff0c\u70e7\u5b83\u4e2a\u5e72\u5e72\u51c0\u51c0\u5c31\u662f\u3002\u201d \u3000\u3000\u6a80\u9053\u6210\u53eb\u9053\uff1a\u201c\u7239\u7239\u4e00\u4e00\u201d \u3000\u3000\u6a80\u516c\u76f4\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e0d\u4f46\u662f\u54b1\u4eec\u6a80\u5bb6\u7684\uff0c\u4e5f\u662f\u5f20\u5bb6\u7684\u3002\u4e3a\u4e86\u4fdd\u5b58\u4e24\u5bb6\u7684\u9aa8\u8089\uff0c\u51b3\u610f\u63a5 \u65e8\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6a80\u8d1d\u52d2\u3001\u4f60\u65e9\u8bf4\u65e9\u5c31\u597d\u4e86\uff0c\u7d2f\u8fd9\u5b69\u5b50\u591a\u53d7\u60ca\u6050\u3002 \u3000\u3000\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6055\u6211\u4e0d\u80fd\u8dea\u4e0b\u63a5\u65e8\uff0c\u4f60\u9012\u7ed9\u6211\u5427\u3002\u5723\u65e8\u8bf4\u7684\u4ec0\u4e48\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\uff0c\u5ba3\u8bfb\u7684\u4eea \u5f0f\u4e5f\u53ef\u514d\u4e86\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u4f46\u6c42\u4ed6\u80af\u63a5\u65e8\uff0c\u8fd9\u4e9b\u201c\u5c0f\u8282\u201d\u81ea\u662f\u4e0d\u60f3\u548c\u4ed6\u8ba1\u8f83\u4e86\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u738b\u7237\u662f\u7687\u4eb2\u56fd \u621a\uff0c\u8fd9\u4e9b\u671d\u5ef7\u4e0a\u7684\u4eea\u793c\uff0c\u81ea\u662f\u4e0d\u5fc5\u52a0\u5728\u738b\u7237\u8eab\u4e0a\u3002\u738b\u7237\u8bf4\u53ef\u514d\u90a3\u5c31\u514d\u4e86\u3002\u201d\u5c31\u8fd9\u6837\u597d\u50cf\u201c\u79c1 \u81ea\u6388\u53d7\u201d\u4e00\u822c\uff0c\u628a\u5723\u65e8\u9012\u7ed9\u4e86\u6a80\u516c\u76f4\u3002 \u3000\u3000\u64c5\u516c\u76f4\u9053\uff1a\u201c\u6211\u8d70\u4e0d\u52a8\uff0c\u9ebb\u70e6\u4f60\u4eec\u7ed9\u6211\u51c6\u5907\u4e00\u526f\u62c5\u67b6\u3002\u201d\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u6211\u80cc\u4f60\u4e0b\u5c71\u4e5f \u53ef\u4ee5\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u662f\u4e00\u7b49\u5df4\u56fe\u9c81\uff0c\u6211\u600e\u6562\u628a\u4f60\u54c8\u5927\u4eba\u5f53\u9a6c\u6765\u9a91\uff0c\u8fd8\u662f\u8ba9\u6211\u8eba\u5728\u62c5\u67b6\u4e0a\uff0c\u4f60 \u4eec\u53eb\u4eba\u62ac\u6211\u4e0b\u53bb\u7684\u597d\u3002\u201d\u54c8\u5fc5\u56fe\u5fc3\u91cc\u6697\u9a82\uff1a\u201c\u5f85\u4f60\u8fd9\u5339\u592b\u8fdb\u4e86\u4eac\u518d\u6ce1\u5236\u4f60\uff0c\u76ee\u524d\u6682\u4e14\u7531\u5f97\u4f60 \u51b7\u8bed\u8ba5\u5632\u3002\u201d\u5fc3\u91cc\u6068\u6a80\u516c\u76f4\uff0c\u8138\u4e0a\u5374\u662f\u5806\u6ee1\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u53cd\u6b63\u5c71\u4e0a\u591a\u7684\u662f\u6728 \u6750\uff0c\u9020\u4e00\u526f\u62c5\u67b6\u4e5f\u8d39\u4e0d\u4e86\u591a\u5c11\u5de5\u592b\uff0c\u4f60\u662f\u7687\u4eb2\u56fd\u621a\uff0c\u6211\u4eec\u80fd\u670d\u5f85\u4f60\u8001\u4eba\u5bb6\u8fdb\u4eac\uff0c\u8fd9\u662f\u6211\u4eec\u7684 \u5149\u8363\u3002\u62c5\u67b6\u7528\u4e0d\u7740\u627e\u522b\u4eba\u62ac\u4e86\u3002\u201d \u3000\u3000\u690d\u516c\u76f4\u9053\uff1a\u201c\u597d\uff0c\u968f\u4fbf\u4f60\u4eec\u5427\u3002\u4f46\u6211\u8fd9\u5c0f\u5b59\u5b59\u2014\u2014\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6a80\u8d1d\u52d2\u5df2\u7ecf\u63a5\u4e86\u65e8\uff0c\u547c\u8001\u4e8c\uff0c\u4f60\u653e\u4e86\u8fd9\u5b69\u5b50\u5427\u3002\u201d \u3000\u3000\u547c\u6c99\u864e\u9053\uff1a\u201c\u6211\u662f\u62c5\u5fc3\u8fd9\u5c0f\u5b69\u5b50\u4e00\u4e2a\u4eba\u7559\u5728\u5c71\u4e0a\u2014\u2014\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u66ff\u6211\u62c5\u5fc3\u3002\u51b2\u513f\uff0c\u4f60\u5411\u5c71\u4e0b\u8dd1\uff0c\u4f60\u7684\u5916\u516c\u548c\u5988\u5988\u4ed6\u4eec\u81ea\u7136\u4f1a\u627e\u5f97 \u7740\u7684\u3002\u201d \u3000\u3000\u547c\u6c99\u864e\u9053\uff1a\u201c\u662f\uff01\u201d\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u8fd9\u5b69\u5b50\u7684\u5916\u516c\u548c\u5988\u5988\u4e00\u5b9a\u5c1a\u672a\u4e0b\u5c71\uff0c\u60f3\u5fc5\u662f\u8eb2\u5728\u9644\u8fd1 \u7684\u6811\u6797\u91cc\uff0c\u6545\u6b64\u6a80\u516c\u76f4\u624d\u6562\u53eb\u8fd9\u5b69\u5b50\u81ea\u5df1\u4e0b\u5c71\u5bfb\u627e\u4eb2\u4eba\u3002\u54c8\uff0c\u8fd9\u8001\u5339\u592b\u4ee5\u4e3a\u81ea\u5df1\u806a\u660e\uff0c\u5374\u4e0d \u77e5\u6b63\u662f\u80e1\u6d82\u3002\u6709\u8fd9\u5b69\u5b50\u505a\u9975\uff0c\u4ed6\u7684\u6c49\u4eba\u4eb2\u5bb6\u4e5f\u975e\u843d\u7f51\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4ed6\u54ea\u77e5\u9053\uff0c\u6a80\u516c\u76f4\u6b63\u662f\u8981\u4ed6\u4eec\u76f8\u4fe1\u4ed6\u7684\u4eb2\u5bb6\u5e76\u975e\u85cf\u5728\u5c4b\u5185\uff0c\u800c\u6a80\u516c\u76f4\u4ea6\u5df2\u53e6\u6709\u6253\u7b97\u7684 \u4e86\u3002 \u3000\u3000\u4f46\u5374\u6709\u4e00\u4ef6\u4e8b\u51fa\u4e4e\u6a80\u516c\u76f4\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u547c\u6c99\u864e\u653e\u5f00\u4e86\u4ed6\u7684\u5b59\u513f\uff0c\u4ed6\u7684\u5b59\u513f\u5374\u4e0d\u80af\u8d70\u3002 \u3000\u3000\u4ed6\u63a5\u4e86\u5723\u65e8\u4e4b\u540e\uff0c\u4f0f\u5728\u684c\u4e0a\u54b3\u55fd\u3002 \u3000\u3000\u90a3\u5c0f\u5b69\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u6211\u4e0d\u8bb8\u522b\u4eba\u6b3a\u8d1f\u4f60\uff0c\u5bf9\u4e0d\u8d77\uff0c\u6211\u8981\u966a\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8dd1\u4e0a\u524d\u53bb\u4f38\u51fa\u5c0f\u7275\u5934\u5c31\u5728\u54c8\u5fc5\u56fe\u8eab\u4e0a\u731b\u64c2\u3002\u6b64\u65f6\u54c8\u5fc5\u56fe\u6b63\u5728\u6276\u7740\u6a80\u516c\u76f4\u3002\u6a80\u516c\u76f4\u9053\uff1a \u201c\u51b2\u513f\uff0c\u542c\u8bdd\u3002\u4f60\u4e0d\u662f\u8981\u5988\u5988\u5417\uff1f\u5feb\u53bb\u627e\u5988\u5988\u5427\u3002\u201d \u3000\u3000\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u6211\u8981\u5988\u5988\uff0c\u4e5f\u8981\u7239\u7239\u548c\u7237\u7237\uff0c\u8981\u8d70\uff0c\u54b1\u4eec\u4e00\u8d77\u8d70\u3002\u201d\u4e00\u9762\u53eb\uff0c\u4e00\u9762\u8fd8\u662f\u5728 \u54c8\u5fc5\u56fe\u8eab\u4e0a\u731b\u64c2\u3002 \u3000\u3000\u5ffd\u5730\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u5b69\u5b50\u98de\u4e86\u8d77\u6765\uff0c\u597d\u50cf\u76ae\u7403\u4e00\u822c\u7ed9\u629b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b69\u5b50\u662f\u7ed9\u54c8\u5fc5\u56fe\u7684\u5185\u529b\u5f39\u5f00\u7684\uff0c\u4ed6\u7684\u5185\u529b\u8fd0\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u5b69\u5b50\u7ed9\u629b\u4e86\u8d77\u6765\uff0c\u53c8\u8f7b\u8f7b\u843d \u4e0b\uff0c\u5c31\u50cf\u7ed9\u4e00\u53ea\u65e0\u5f62\u7684\u624b\u5c06\u4ed6\u63d0\u8d77\uff0c\u653e\u5728\u95e8\u5916\u3001\u8fd9\u5b69\u5b50\u5014\u5f3a\u5f97\u5f88\uff0c\u843d\u5728\u95e8\u5916\u3002\u4e00\u7ad9\u7a33\uff0c\u53c8\u8dd1 \u8fdb\u6765\u4e86\u3002\u5927\u53eb\u5927\u56b7\uff1a\u201c\u6211\u4e0d\u8d70\uff0c\u6211\u8981\u7239\u7239\uff0c\u6211\u8981\u7237\u7237\uff01\u201c\u547c\u6c99\u864e\u559d\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u4f60\u4e0d\u8d70\u6211 \u6253\u6b7b\u4f60\uff01\u201d \u3000\u3000\u679c\u7136\u4ed6\u8bf4\u6253\u5c31\u6253\uff0c\u567c\u567c\u556a\u556a\uff0c\u6253\u4e86\u5c0f\u5b69\u5b50\u4e24\u5df4\u638c\u3002\u4e0b\u624b\u867d\u7136\u4e0d\u6562\u592a\u91cd\uff0c\u4f46\u5bf9\u4e00\u4e2a\u5c0f\u5b69\u5b50 \u6765\u8bf4\uff0c\u4e5f\u4e0d\u80fd\u7b97\u662f\u8f7b\u4e86\uff0c\u4ed6\u662f\u60f3\u628a\u5b69\u5b50\u6253\u5f97\u77e5\u9053\u75bc\u75db\u4f46\u53c8\u4e0d\u81f3\u4f24\u4e86\u5b69\u5b50\uff0c\u597d\u8ba9\u5b69\u5b50\u5bb3\u6015\u975e\u8dd1 \u4e0d\u53ef\u3002\u5b69\u5b50\u7ed9\u6253\u5f97\u201c\u54c7\u201d\u5730\u4e00\u58f0\u54ed\u4e86\uff0c\u4f46\u60f3\u8d77\u7237\u7237\u201c\u6d41\u8840\u4e0d\u6d41\u6cea\u201d\u7684\u6559\u5bfc\uff0c\u53ea\u54ed\u4e86\u4e00\u58f0\uff0c\u5c31 \u558a\u9053\uff1a\u201c\u4f60\u6253\u6b7b\u6211\u5427\uff0c\u4f60\u6253\u6b7b\u6211\u4e5f\u4e0d\u8d70\uff01\u201d \u3000\u3000\u4fd7\u8bed\u8bf4\uff1a\u6253\u5728\u513f\u8eab\uff0c\u75db\u5728\u5a18\u5fc3\u3002\u7236\u6bcd\u7231\u5b50\u4e4b\u5fc3\u90fd\u662f\u4e00\u6837\u7684\uff0c\u5f20\u96ea\u6ce2\u8eb2\u5728\u590d\u58c1\u91cc\u3002\u5fc3\u4e2d\u75db \u5982\u5200\u5272\uff0c\u4f46\u56e0\u7ed9\u5f20\u708e\u6309\u4f4f\uff0c\u65e0\u6cd5\u51fa\u53bb\uff0c\u690d\u9053\u6210\u5374\u662f\u6309\u637a\u4e0d\u4f4f\u81ea\u5df1\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u51b2\u4e0a\u524d\u53bb\u5bf9\u547c \u6c99\u864e\u5c31\u662f\u4e00\u62f3\u3002\u6b64\u65f6\u8ddd\u79bb\u4ed6\u7684\u7a74\u9053\u89e3\u5f00\u5dee\u4e0d\u591a\u5df2\u6709\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u7684\u529f\u529b\u6062\u590d\u4e86\u4e03\u516b\u5206\u4e86\u3002 \u3000\u3000\u547c\u6c99\u864e\u4e00\u638c\u9694\u5f00\uff0c\u611f\u89c9\u5bf9\u65b9\u6c14\u529b\u4e0d\u5c0f\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6a80\u9053\u6210\u8fd0\u638c\u5982\u98ce\uff0c \u5df2\u662f\u8fde\u4f7f\u4e24\u8bb0\u72e0\u62db\uff0c\u5f62\u540c\u62fc\u547d\u3002\u6253\u5f97\u547c\u6c99\u864e\u5374\u4e0d\u80fd\u4e0d\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u547c\u6c99\u864e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u6740\u4f60\u7684\u513f\u5b50\uff0c\u4f60\u5c31\u8981\u548c\u6211\u62fc\u547d\u4e48\uff1f\u201d\u6a80\u9053\u6210\u82e5\u662f\u529f\u529b\u5b8c\u5168\u6062 \u590d\uff0c\u53ef\u4ee5\u548c\u4ed6\u65d7\u9f13\u76f8\u5f53\u3002\u4f46\u7eb5\u7136\u529f\u529b\u76f8\u5f53\uff0c\u4ed6\u4e5f\u662f\u6253\u4e0d\u8fc7\u547c\u6c99\u864e\u7684\uff0c\u56e0\u4e3a\u4ed6\u53ea\u6709\u730e\u517d\u7684\u7ecf \u9a8c\uff0c\u548c\u9ad8\u624b\u6253\u6597\uff0c\u4ed6\u662f\u6beb\u65e0\u7ecf\u9a8c\u7684\u3002\u6765\u52bf\u8d8a\u731b\uff0e\u8d25\u5f97\u8d8a\u5feb\u3002\u6a80\u9053\u6210\u6325\u62f3\u731b\u51fb\uff0c\u547c\u6c99\u864e\u7b11\u9053\uff1a \u201c\u6a80\u8d1d\u5b50\u6b66\u529f\u4e0d\u9519\u554a\uff01\u201d\u5de6\u4e3e\u53d8\u638c\u5411\u5185\u4e00\u5708\uff0c\u53f3\u81c2\u4e00\u6eda\u4e00\u62e7\uff0c\u628a\u6a80\u9053\u6210\u7684\u53f3\u624b\u5708\u4f4f\uff0c\u53ea\u8981\u4e00 \u53d1\u529b\uff0c\u6a80\u9053\u6210\u8fd9\u6761\u624b\u81c2\u975e\u65ad\u4e0d\u53ef\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5728\u5899\u58c1\u5077\u7aa5\uff0c\u4e00\u9897\u5fc3\u51e0\u4e4e\u5728\u8df3\u51fa\u53e3\u8154\uff0c\u867d\u7136\u7ed9\u5f20\u708e\u6309\u4f4f\uff0c\u5df1\u662f\u53d1\u51fa\u4e00\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u53ef\u4f24\u5bb3\u8d1d\u5b50\uff01\u201d\u547c\u6c99\u864e\u4e00\u58f0\u51b7\u7b11\uff0c\u8fd0\u529f\u4e00\u63a8\uff0c\u628a\u6a80\u9053\u6210\u8dcc\u4e86\u4e2a\u56db\u811a\u671d \u5929\u3002 \u3000\u3000\u547c\u6c99\u864e\u51b7\u7b11\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u7ed9\u9a97\u4e86\u3002\u64c5\u8d1d\u5b50\u975e\u4f46\u4e0d\u662f\u4e0d\u61c2\u6b66\u529f\uff0c\u4ed6\u7b80\u76f4\u6709\u8d44\u683c\u53ef\u4ee5\u5f53 \u4e00\u540d\u5df4\u56fe\u9c81\u5462\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5ffd\u5730\u7ad9\u8d77\u6765\uff0c\u628a\u8033\u6735\u8d34\u7740\u5899\u58c1\u3002 \u3000\u3000\u6b63\u5f53\u4ed6\u60f3\u7528\u91cd\u62f3\u51fb\u7834\u5899\u58c1\u4e4b\u9645\uff0c\u7a81\u7136\u542c\u5230\u55e4\u55e4\u51e0\u58f0\u8f7b\u54cd\u3002 \u3000\u3000\u6a80\u516c\u76f4\u628a\u5723\u65e8\u6495\u7834\u4e86\uff01 \u3000\u3000\u54c8\u5fc5\u56fe\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8d76\u5fd9\u56de\u8fc7\u8eab\u6765\uff0c\u98a4\u58f0\u559d\u9053\uff1a\u201c\u6a80\u8d1d\u52d2\uff0c\u4f60\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5723\u65e8 \u65e9\u5df2\u7ed9\u5f97\u5316\u6210\u7247\u7247\u8774\u8776\uff0c\u4ed6\u8981\u963b\u6b62\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u547c\u6c99\u864e\u5df2\u7ecf\u6ce8\u610f\u5230\u54c8\u5fc5\u56fe\u521a\u624d\u7684\u52a8\u4f5c\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u8fd9\u5899\u58c1\u91cc\u6709\u4ec0\u4e48\u53e4\u602a\uff1f\u201d\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u5899\u58c1\u7a81\u7136\u88c2\u5f00\uff0e\u5f20\u708e\u6251\u4e86\u51fa\u6765\uff01 \u3000\u3000\u547c\u6c99\u864e\u60f3\u4e0d\u5230\u5899\u58c1\u91cc\u85cf\u6709\u4eba\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u5f20\u708e\u7684\u4e00\u628a\u950b\u5229\u7684\u5315\u9996\u5df1\u7ecf\u523a\u8fdb\u4ed6\u7684\u5c0f \u8179\uff01\u547c\u6c99\u864e\u5927\u543c\u4e00\u58f0\u3002\u4e00\u638c\u628a\u5f20\u708e\u63a8\u5f97\u649e\u5411\u5899\u58c1\uff0c\u4f46\u8fd9\u628a\u5315\u9996\u523a\u5f97\u5f88\u6df1\u4ed6\u6643\u4e86\u51e0\u6643\u5c31\u50cf\u4e00\u6839 \u6728\u5934\u4f3c\u7684\u201c\u535c\u901a\u201d\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5f20\u708e\u53eb\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u548c\u51b2\u513f\u5feb\u8d70\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u62b1\u8d77\u5b69\u5b50\uff0c\u5374\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u547c\u6c99\u9f99\u5df2\u7ecf\u548c\u5f20\u708e\u6253\u4e86\u8d77\u6765\u3002\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u5feb\u53bb\u5e2e\u5916\u516c\u6253\u67b6\u5427\uff0c\u6211\u4e0d\u8d70\uff01\u201d \u3000\u3000\u5f20\u708e\u53eb\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u4eec\u6bcd\u5b50\u8d76\u5feb\u9003\u751f\u3002\u51b2\u513f\uff0c\u542c\u5916\u516c\u7684\u8bdd\uff0c\u7ec3\u597d\u672c\u9886\uff0c\u518d\u66ff\u5916\u516c\u62a5 \u4ec7\uff01\u201d \u3000\u3000\u547c\u6c99\u864e\u5728\u5730\u4e0a\u6eda\u4e86\u4e24\u6eda\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u8981\u7ed9\u6211\u62a5\u4ec7\uff01\u201d\u53cc\u817f\u4e00\u4f38\uff0e\u6b7b\u4e86\u3002 \u3000\u3000\u547c\u6c99\u9f99\u6012\u6781\u5927\u543c\uff1a\u201c\u4f60\u4eec\u4e00\u4e2a\u4e5f\u8d70\u4e0d\u4e86\uff0c\u6211\u8981\u628a\u4f60\u4eec\u901a\u901a\u6740\u6389\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u53ea\u770b\u4e00\u773c\u3002\u5c31\u77e5\u9053\u547c\u6c99\u9f99\u51b3\u4e0d\u4f1a\u8f93\u7ed9\u5f20\u708e\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u5934\u5018\u82e5\u6c89\u5f97\u4f4f\u6c14\uff0c \u5927\u6982\u8fd8\u53ef\u4ee5\u6253\u4e2a\u4e09\u4e94\u5341\u62db\u3002\u4ed6\u82e5\u62fc\u547d\u3002\u53ea\u6709\u8f93\u5f97\u66f4\u5feb\uff01 \u3000\u3000\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u4e86\u5fc3\uff0c\u56de\u8fc7\u5934\u7ee7\u7eed\u5bf9\u6a80\u516c\u76f4\u65bd\u4ee5\u5a01\u80c1\uff1a\u201c\u6a80\u8d1d\u52d2\uff0c\u4f60\u8bf4\u8fc7\u7684\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u628a\u5723\u65e8 \u62fe\u8d77\u6765\uff0c\u5426\u5219\u4f60\u7684\u513f\u5b50\u3001\u5ab3\u5987\u3001\u5b59\u513f\u3001\u4eb2\u5bb6\uff0c\u4e00\u4e2a\u90fd\u4e0d\u80fd\u6d3b\u547d\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u5c1a\u5dee\u4e00\u9053\u7ecf\u8109\u672a\u66fe\u6253\u901a\uff0c\u60c5\u77e5\u6b64\u65f6\u52a8\u624b\uff0c\u51b3\u8ba1\u6253\u4e0d\u8fc7\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u53ea\u76fc\u5f20\u708e\u80fd\u591f\u652f\u6301\u4e09\u4e8c\u5341\u62db\uff0c\u4f46\u76ee\u524d\u7684\u5f62\u52bf\uff0c\u54c8\u5fc5\u56fe\u5df2\u662f\u903c\u5f97\u4ed6\u65e0\u6cd5\u62d6\u5ef6\u65f6\u5019\u4e86\u3002 \u3000\u3000\u4ed6\u54b3\u4e86\u51e0\u58f0\u3001\u5598\u7740\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u63a5\u65e8\u7684\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u63a5\u65e8\u4e86\u5440\u3002\u5723\u65e8\u5df2\u7ecf\u5728\u6211\u7684\u624b\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u6211\u628a\u5b83\u6495\u788e\u7f62\u4e86\uff0c\u4f60 \u4e0d\u80fd\u8bf4\u6211\u6ca1\u6709\u63a5\u8fc7\u5723\u65e8\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7ed9\u4ed6\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u4e0d\u8981\u80e1\u626f\uff0c\u5e72\u8106\u7b54\u4e00\u53e5\uff1a\u4f60\u8ddf\u4e0d\u8ddf\u6211\u4e0a \u4eac\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u8bf4\u8fc7\u63a5\u65e8\uff0c\u53ef\u6ca1\u7b54\u5e94\u8ddf\u4f60\u4e0a\u4eac\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u4e0a\u4eac\uff0c\u6211\u7b2c\u4e00\u4e2a\u5148\u6740\u4f60\u7684\u513f\u5b50\uff0c\u7b2c\u4e8c\u4e2a\u518d\u6740\u4f60\u5b59\u513f\uff01\u201d \u3000\u3000\u6a80\u9053\u6210\u521a\u521a\u722c\u8d77\u6765\uff0c\u811a\u6b65\u8fd8\u672a\u7ad9\u7a33\uff0c\u54c8\u5fc5\u56fe\u5411\u4ed6\u6251\u6765\u4e86\uff01 \u3000\u3000\u773c\u770b\u6a80\u9053\u6210\u5c31\u8981\u7ed9\u4ed6\u6293\u4f4f\uff0c\u4ed6\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u6a80\u516c\u76f4\u5df2\u662f\u4e00\u638c\u5411\u4ed6\u80cc\u5fc3\u51fb\u4e0b\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u4e0d\u6127\u662f\u5168\u56fd\u7684\u4e00\u7b49\u5df4\u56fe\u9c81\uff0c\u5f53\u771f\u662f\u773c\u89c2\u516b\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u4e00\u89c9\u80cc\u540e\u6709\u4eba\u5077\u88ad\uff0c\u53cd \u624b\u5c31\u662f\u4e00\u638c\u3002 \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u84ec\u201d\u5730\u4e00\u58f0\uff0c\u6a80\u516c\u76f4\u6643\u4e86\u51e0\u6643\uff0c\u54c8\u5fc5\u56fe\u4e5f\u7ed9\u9707\u5f97\u659c\u7a9c\u4e24\u6b65\u3002 \u3000\u3000\u6a80\u516c\u76f4\u53eb\u9053\uff1a\u201c\u6210\u513f\uff0c\u5feb\u53bb\u5e2e\u4f60\u5cb3\u7236\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u6a80\u516c\u76f4\u3001\u4f60\u7adf\u6562\u9a97\u6211\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u7b11\u9053\uff1a\u201c\u6211\u662f\u670d\u4e86\u6bd2\uff0c\u4f46\u53ef\u6ca1\u9a97\u4f60\u6211\u5df2\u4e0d\u80fd\u52a8\u6b66!\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u548c\u4ed6\u63a5\u4e86\u4e00\u62db\uff0c\u4ea6\u5df2\u77e5\u9053\u4ed6\u7684\u6b66\u529f\u867d\u672a\u6d88\u5931\u3001\u4f46\u5185\u529b\u5374\u662f\u6bd4\u4e0d\u4e0a\u81ea\u5df1\uff0c\u4e2d\u4e86\u6bd2\u662f \u4e0d\u5047\u7684\u3002\u4e8e\u662f\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u5b81\u613f\u6b7b\u4e5f\u4e0d\u613f\u610f\u53bb\u89c1\u5723\u4e0a\uff0c\u90a3\u6211\u5c31\u6210\u5168\u4f60\u3002\u8ba9\u4f60\u53bb\u89c1 \u960e\u7f57\u5427\u201d\u3002 \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u54c8\u5927\u4eba\u3002\u4f60\u80af\u6210\u5168\u6211\uff0c\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u4e0d\u8fc7\uff0c\u53ef\u5f97\u8bf7\u54c8\u5927\u4eba\u4f60\u5148\u5230\u9ec4\u6cc9\u66ff \u6211\u5f00\u8def\uff01\u201d\u4e00\u8bb0\u201c\u94c1\u7435\u7436\u624b\u201d\uff0c\u624b\u80cc\u5411\u5916\u4e00\u6325\uff0c\u8fc5\u5982\u95ea\u7535\u5730\u5411\u54c8\u5fc5\u56fe\u9762\u95e8\u63b4\u53bb\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5fc3\u4e2d\u4e00\u51db\uff1a\u60f3\u4e0d\u5230\u4ed6\u4e2d\u4e86\u6bd2\u8eab\u624b\u8fd8\u662f\u8fd9\u6837\u77eb\u6377\uff01\u201d\u5f53\u4e0b\u8eab\u5f62\u4e00\u95ea\uff0c\u63a2\u638c\u6765\u5207\u6a80\u516c \u76f4\u53f3\u81c2\uff0c\u53cc\u6307\u70b9\u5411\u4ed6\u7684\u66f2\u6c60\u7a74\u3002\u6a80\u516c\u76f4\u7a81\u7136\u7f29\u638c\uff0c\u54c8\u5fc5\u56fe\u8eab\u5f62\u51b2\u4e0a\uff0e\u5de6\u638c\u7a81\u51fa\uff0c\u53d8\u6210\u201c\u8098\u5e95 \u770b\u9524\u201d\uff0c\u62f3\u5934\u4e00\u62b5\u638c\u5fc3\uff0c\u54c8\u5fc5\u56fe\u8fd9\u6b21\u53ea\u662f\u6643\u4e86\u4e00\u6643\uff0c\u6a80\u516c\u5728\u5374\u9000\u4e86\u4e24\u6b65\u3001\u8fd9\u4e00\u62db\u6a80\u516c\u76f4\u5403\u4e8f \u66f4\u5927\u4e86\u3002 \u3000\u3000\u5f20\u708e\u4e0e\u547c\u6c99\u9f99\u53cc\u65b9\u90fd\u5728\u62fc\u547d\uff0c\u5f20\u708e\u88ab\u4ed6\u51fb\u4e2d\u4e00\u62f3\uff0c\u201c\u54c7\u201d\u5730\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u8d1f\u4f24\u6076 \u6597\uff0c\u72c2\u547c\u6709\u5982\u75af\u864e\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u653e\u4e0b\u5b69\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4f60\u81ea\u5df1\u9003\u751f\u4e86\uff0c\u5a18\u4eb2\u987e\u4e0d\u5f97\u4f60\u4e86\uff01 \u3000\u3000\u201d \u3000\u3000\u690d\u9053\u6210\u84e6\u5730\u5927\u53eb\uff1a\u201c\u5a18\u5b50\uff0c\u4f60\u5feb\u62b1\u51b2\u51e0\u9003\u751f\uff0c\u8fd9\u91cc\u6709\u6211\uff01\u201d\u62ff\u8d77\u4e00\u67c4\u732a\u53c9\uff0c\u7acb\u5373\u51b2\u4e0a\u524d \u53bb\u4e0e\u5cb3\u7236\u8054\u624b\u3002 \u3000\u3000\u547c\u6c99\u9f99\u6b66\u529f\u6bd4\u547c\u6c99\u864e\u9ad8\u5f97\u591a\u3002\u6a80\u9053\u6210\u662f\u521a\u521a\u53d7\u4e86\u4f24\u7684\uff0c\u4f24\u5f97\u867d\u7136\u4e0d\u7b97\u5f88\u91cd\uff0c\u4e5f\u4e0d\u80fd\u7b97 \u8f7b\uff0c\u5982\u4f55\u8fd8\u80fd\u62b5\u654c\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u547c\u6c99\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6765\u9001\u6b7b\uff01\u201d\u6325\u81c2\u4e00\u683c\uff0c\u907f\u8fc7\u53c9\u5c16\uff0c\u5728\u6746\u4e0a\u91cd\u91cd\u4e00\u51fb\uff0c\u6a80\u9053 \u6210\u864e\u53e3\u9707\u88c2\uff0c\u730e\u864e\u53c9\u8131\u624b\u98de\u51fa\u95e8\u5916\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6a80\u516c\u76f4\uff0c\u4f60\u4e0d\u4f4f\u624b\uff0c\u6211\u53ef\u8981\u5f97\u7f6a\u4e86\uff01\u201d\u5de6\u4e3e\u75be\u53d1\u5982\u98ce\uff0c\u4e00\u4e2a\u201c\u6512\u62f3\u201d\uff0c\u81ea \u53f3\u81c2\u7684\u52fe\u624b\u5708\u4e2d\u76f4\u6512\u51fa\u6765\uff0c\u51b2\u6253\u6a80\u516c\u76f4\u7684\u592a\u9633\u7a74\uff0c\u7531\u4e8e\u6a80\u516c\u76f4\u5df2\u662f\u8c41\u51fa\u6027\u547d\u7684\u6253\u6cd5\uff0c\u51fa\u624b\u62db \u62db\u72e0\u8fa3\uff0c\u54c8\u5fc5\u56fe\u82e5\u4f30\u634e\u6709\u987e\u5fcc\uff0c\u53ea\u6015\u81ea\u5df1\u7684\u6027\u547d\u5148\u81ea\u4e0d\u4fdd\u3002\u5728\u8fd9\u751f\u6b7b\u5173\u5934\uff0c\u6027\u547d\u5f53\u7136\u6bd4\u7ed3\u65e8 \u66f4\u7d27\u8981\u4e86\uff0c\u6a80\u516c\u76f4\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u6b7b\uff0c\u4f46\u4e0d\u80fd\u7d2f\u4eb2\u5bb6\u4e3a\u6211\u4e27\u751f\uff01\u201d\u54ac\u7834\u820c\u5934\uff0c\u4e00\u53e3\u9c9c\u8840 \u55b7\u4e86\u51fa\u6765\uff01 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6\u8fd9\u53e3\u9c9c\u8840\u4e00\u55b7\uff0c\u5374\u66f4\u6700\u5f97\u7cbe\u795e\uff0e\u51fa\u62f3\u7684\u529b\u9053\u6bd4\u4ee5\u524d\u5927\u5f97\u591a\u3002\u54c8\u5fc5\u56fe\u89c1\u4ed6\u5410 \u8840\uff0c\u521d\u65f6\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u53d7\u4e86\u5185\u4f24\uff0c\u90a3\u77e5\u6b22\u559c\u672a\u8fc7\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u5185\u529b\u5df2\u662f\u6709\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff01 \u3000\u3000\u539f\u6765\u6a80\u5bb6\u672c\u662f\u91d1\u56fd\u7684\u8d35\u65cf\uff0c\u641c\u7f57\u7684\u6b66\u5b66\u5178\u7c4d\u751a\u591a\uff0c\u6709\u4e00\u95e8\u90aa\u6d3e\u6b66\u529f\u53eb\u505a\u201c\u5929\u9b54\u89e3\u4f53\u201d\u5927 \u6cd5\uff0c\u81ea\u6b8b\u80a2\u4f53\uff0c\u53ef\u4ee5\u529f\u529b\u500d\u589e\u3002\u8fd9\u95e8\u90aa\u6d3e\u6b66\u529f\uff0c\u6a80\u516c\u76f4\u5f53\u4e5f\u66fe\u770b\u8fc7\u79d8\u7b7e\uff0c\u53ea\u56e0\u5b83\u662f\u90aa\u6d3e\u6b66 \u529f\uff0c\u5f53\u521d\u53ea\u662f\u4e3a\u4e86\u597d\u5947\u800c\u5b66\uff0c\u5e76\u672a\u6253\u7b97\u4f7f\u7528\u7684\u3002 \u3000\u3000\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u672c\u6765\u6700\u4f24\u5143\u6c14\uff0c\u5373\u4f7f\u5b66\u5f97\u7cbe\u7eaf\uff0c\u4f7f\u7528\u4e4b\u540e\uff0c\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\uff0c\u6a80\u516c\u76f4\u5f53\u521d\u53ea \u662f\u597d\u5947\u6d89\u730e\uff0c\u5b66\u5f97\u5e76\u4e0d\u7cbe\u7eaf\uff0c\u9c9c\u8840\u4e00\u5410\uff0c\u4e39\u7530\u5c31\u597d\u50cf\u6709\u4e00\u56e2\u706b\u4f3c\u7684\u3002\u4ee4\u5f97\u4ed6\u70e6\u71e5\u4e4b\u6781\uff0c\u975e\u628a \u5185\u529b\u8017\u635f\u4e0d\u53ef\uff0c\u5426\u5219\u5c31\u4e0d\u80fd\u8212\u670d\uff0c\u4ed6\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7684\u6027\u547d\u6050\u6015\u662f\u6d3b\u4e0d\u8fc7\u660e\u5929\u4e86\u3002\u201d \u3000\u3000\u4f46\u4e5f\u662f\u7531\u4e8e\u4ed6\u5b66\u5f97\u4e0d\u7cbe\uff0c\u5185\u529b\u81ea\u5df1\u4e5f\u4e0d\u80fd\u63a7\u5236\uff0c\u8fd9\u4e00\u6765\u5c31\u66f4\u4e3a\u9738\u9053\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5927\u60ca\u8981\u9003\uff0c\u80cc\u5fc3\u5df2\u662f\u4e2d\u4e86\u4ed6\u7684\u4e00\u62f3\u3002\u8fd9\u4e00\u6b21\u662f\u54c8\u5fc5\u56fe\u72c2\u55b7\u9c9c\u8840\u4e86\uff01 \u3000\u3000\u53e6\u4e00\u8fb9\u7684\u5267\u6597\u5df2\u6709\u4e86\u7ed3\u679c\u3002 \u3000\u3000\u5267\u6597\u4e2d\u6a80\u9053\u6210\u6c14\u529b\u4e0d\u652f\uff0c\u6b65\u6cd5\u7a0d\u89c1\u7f13\u6162\u3002\u547c\u6c99\u9f99\u4e00\u53d1\u73b0\u6709\u673a\u53ef\u4e58\uff0c\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u4ed6 \u8e22\u7ffb\u3002 \u3000\u3000\u90a3\u77e5\u6a80\u9053\u6210\u867d\u7ed9\u8e22\u7ffb\uff0c\u4ecd\u662f\u987d\u5f3a\u4e4b\u6781\uff0c\u7adf\u7136\u62b1\u4f4f\u4ed6\u7684\u53cc\u817f\uff0c\u8fd9\u4e00\u6293\u521a\u597d\u6293\u4f4f\u4ed6\u819d\u76d6\u7684\u73af \u8df3\u7a74\u3002 \u3000\u3000\u547c\u6c99\u9f99\u98de\u811a\u8e22\u6a80\u9053\u6210\u4e4b\u65f6\uff0c\u5df2\u7ecf\u7ed9\u5f20\u708e\u91cd\u91cd\u5288\u4e86\u4e00\u638c\uff0c\u6b64\u65f6\u53cc\u817f\u9ebb\u8f6f\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u8dea\u4e0b \u53bb\uff0c\u4ed6\u6b63\u60f3\u627c\u6a80\u9053\u6210\u5589\u5499\u4e4b\u9645\uff0c\u5f20\u708e\u5df2\u7ecf\u6251\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u53cc\u624b\u7528\u529b\u4e00\u62d7\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u628a \u4ed6\u7684\u9888\u62d7\u6298\u4e86\u3002\u547c\u6c99\u9f99\u53d1\u51fa\u4e00\u58f0\u6495\u5fc3\u88c2\u80ba\u7684\u60e8\u53eb\uff0c\u8ddf\u7740\u4ed6\u7684\u5f1f\u5f1f\u53bb\u89c1\u960e\u738b\u4e86\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u53e3\u5410\u9c9c\u8840\uff0c\u72c2\u5954\u51b2\u51fa\u5927\u95e8\u3002 \u3000\u3000\u4e00\u573a\u8840\u96e8\u8165\u98ce\u7684\u6076\u6597\uff0c\u5f52\u4e8e\u9759\u5bc2\u3002\u6a80\u516c\u76f4\u652f\u6301\u4e0d\u4f4f\uff0c\u6643\u4e86\u51e0\u6643\uff0c\u9893\u7136\u5750\u4e0b\u3002\u5f20\u708e\u5fc3\u5982\u5200 \u7ede\u3002\u5c06\u4ed6\u62b1\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u6211\u9519\u602a\u4f60\u4e86\uff01\u201d\u6a80\u516c\u76f4\u5fae\u7b11\u9053\uff1a\u201c\u5f97\u4f60\u660e\u767d\uff0c\u6211\u5df2\u662f\u6b7b\u800c\u65e0 \u61be\u3002\u6b64\u5730\u4e0d\u53ef\u7559\u3002\u4f60\u4eec\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u5f20\u708e\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d\u53d6\u51fa\u4e00\u4e2a\u94f6\u74f6\uff0c\u628a\u74f6\u4e2d\u4ec5\u5b58\u7684\u4e24\u9897\u836f\u4e38\u90fd\u7ed9\u4ed6\u670d\u4e0b\u3002\u6a80 \u516c\u76f4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u4f24\u6050\u6015\u662f\u65e0\u836f\u53ef\u89e3\u7684\u4e86\u3002\u4f55\u5fc5\u7cdf\u584c\u4f60\u7684\u836f\u4e38\u3002\u4e0d\u5fc5\u4e3a\u6211\u8d39\u795e\u4e86\uff0c\u96be\u4fdd\u4ed6 \u4eec\u4e0d\u4f1a\u518d\u6765\u3002\u4f60\u4eec\u8fd8\u662f\u5feb\u8d70\u7684\u597d\u3002\u201d \u3000\u3000\u5f20\u708e\u4e0d\u77e5\u9053\u4ed6\u662f\u7531\u4e8e\u65bd\u5c55\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u4ee5\u81f3\u5143\u6c14\u8017\u635f\u592a\u751a\uff0c\u53ea\u9053\u4ed6\u662f\u56e0\u5b54\u96c0\u80c6\u5267\u6bd2\u65b9\u51fa \u6b64\u8a00\u3002 \u3000\u3000\u201c\u4eb2\u5bb6\uff0c\u6211\u548c\u4f60\u8bf4\u5b9e\u8bdd\u3002\u6211\u771f\u662f\u975e\u5e38\u62b1\u6b49\uff0c\u5b54\u96c0\u80c6\u7684\u6bd2\u7684\u786e\u4e0d\u662f\u8fd9\u836f\u4e38\u6240\u80fd\u51c0\u5c3d\u89e3\u6d88 \u7684\u3002\u4e0d\u8fc7\uff0c\u6027\u547d\u5374\u662f\u53ef\u4ee5\u4fdd\u5168\u3002\u4eb2\u5bb6\uff0c\u4f60\u4ee5\u540e\u6050\u6015\u4e0d\u80fd\u4f7f\u7528\u6b66\u529f\u3002\u4f46\u53ea\u8981\u4e0d\u4e0e\u4eba\u52a8\u6b66\uff0c\u4f60\u7684 \u5bff\u547d\u4e0d\u4f1a\u53d7\u635f\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6b63\u5728\u6276\u8d77\u4ed6\u7684\u4e08\u592b\uff0c\u95fb\u8a00\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u516c\uff0c\u54b1\u4eec\u4e00\u8d77\u8d70\u5427\uff0c\u53e6\u627e\u4e00\u5ea7\u8352 \u5c71\u8eb2\u8d77\u6765\uff0c\u4f60\u4e0d\u80fd\u52a8\u6b66\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u56db \u56de\u3000\u6bc1\u5bb6\u9003\u96be \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u56db \u56de\u3000\u6bc1\u5bb6\u9003\u96be \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4f60\u4eec\u5148\u8d70\u4e00\u6b65\uff0c\u5f85\u6211\u517b\u597d\u4e86\u4f24\uff0c\u518d\u53bb\u5bfb\u627e\u4f60\u4eec\u3002\u201d\u5176\u5b9e\u4ed6\u867d\u7136\u5f97\u4e86\u5f20\u708e\u7684 \u89e3\u836f\uff0c\u4e5f\u8fd8\u662f\u6d3b\u4e0d\u8fc7\u660e\u5929\u3002\u53ea\u662f\u4ed6\u4e0d\u60f3\u7ed9\u513f\u5b50\u548c\u5ab3\u5987\u77e5\u9053\u800c\u5df2\u3002\u5f20\u96ea\u6ce2\u4e0d\u77e5\u771f\u76f8\uff0c\u8bf4\u9053\uff1a \u201c\u516c\u516c\uff0c\u4f60\u4e0d\u662f\u8bf4\u8fc7\uff0c\u96be\u4fdd\u90a3\u4e9b\u4eba\u4e0d\u4f1a\u518d\u6765\u4e48\uff0c\u4f60\u600e\u53ef\u5192\u9669\u7559\u4e0b\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u603b\u6bd4\u8f83\u5bb9\u6613\u9690\u85cf\u4e00\u4e9b\uff0c\u518d\u8bf4\u6211\u7684\u4f24\u867d\u7136\u4e0d\u7b97\u592a\u91cd\uff0c\u4f46\u6050\u6015\u4e5f\u662f\u8d70 \u4e0d\u52a8\u7684\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u7167\u987e\u4f60\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u548c\u4f60\u7684\u4e08\u592b\u4e5f\u90fd\u53d7\u4e86\u4f24\u7684\u554a\uff0c\u4ed6\u4eec\u6216\u8bb8\u52c9\u5f3a\u8d70\u5f97\u52a8\uff0c\u4e5f\u8fd8\u662f \u9700\u8981\u4f60\u7684\u7167\u6599\u7684\u3002\u66f4\u7d27\u8981\u7684\u662f\uff0c\u51b2\u513f\u662f\u54b1\u4eec\u4e24\u5bb6\u552f\u4e00\u7684\u5e7c\u82d7\uff0c\u4ed6\u66f4\u52a0\u9700\u8981\u4f60\u7684\u7167\u6599\uff0c\u96be\u9053\u6211 \u8fd8\u80fd\u8981\u4f60\u6276\u6211\u4e0b\u5c71\u4e48\uff1f\u201d\u5f20\u708e\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u6211\u548c\u4f60\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u4e5f\u662f\u8d70\u4e0d\u52a8\u7684\uff0c\u6211\u966a\u4f60\u5728\u6b64 \u517b\u4f24\u3002\u201d\u6a80\u9053\u6210\u9053\uff1a\u201c\u6211\u4e5f\u7559\u4e0b\u3002\u96ea\u59b9\uff0c\u597d\u5728\u4f60\u6ca1\u53d7\u4f24\uff0c\u4f60\u643a\u5e26\u51b2\u513f\u4e0b\u5c71\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fc3\u4e71\u5982\u9ebb\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8d70\u5927\u5bb6\u8d70\uff0c\u4e0d\u8d70\uff0c\u5927\u5bb6\u90fd\u4e0d\u8d70\u3002\u6210\u54e5\uff0c\u79bb\u5f00\u4f60\uff0c\u6211\u8fd8\u80fd\u72ec \u81ea\u6d3b\u4e0b\u53bb\u4e48\uff1f\u201d \u3000\u3000\u6a80\u9053\u6210\u9053\uff1a\u201c\u4e3a\u4e86\u5b69\u5b50\uff0c\u4f60\u4e00\u5b9a\u8981\u6d3b\u4e0b\u53bb\uff01\u201d \u3000\u3000\u5f20\u708e\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u96ea\u513f\uff0c\u4f60\u7684\u516c\u516c\u8bf4\u7684\u662f\u5bf9\u7684\uff0c\u51b2\u513f\u662f\u54b1\u4eec\u4e24\u5bb6\u7684\u552f\u4e00\u5e7c\u82d7\uff0c\u4f60\u4e00\u5b9a\u8981 \u6276\u517b\u4ed6\u6210\u4eba\u3002\u96ea\u513f\uff0c\u6211\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u8fd9\u4e48\u591a\u5e74\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u52c9\u5f3a\u8fc7\u4f60\u505a\u4efb\u4f55\u4e8b\u60c5\uff0c\u5982\u4eca \u5c31\u7b97\u662f\u6211\u6c42\u4f60\u5427\uff01\u201d\u5f20\u96ea\u6ce2\u54ed\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u522b\u8fd9\u6837\u8bf4\uff0c\u6211\u53ea\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u4f60 \u4eec\u3002\u201d\u6b63\u81ea\u4e89\u6301\u4e0d\u4e0b\uff0c\u6a80\u516c\u76f4\u5ffd\u9053\uff1a\u201c\u7981\u58f0\uff0c\u597d\u50cf\u53c8\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u679c\u7136\u662f\u53c8\u6709\u4eba\u6765\u4e86\uff01 \u3000\u3000\u8fd9\u6b21\u6765\u7684\u4e0d\u662f\u91d1\u56fd\u7684\u6b66\u58eb\uff0c\u662f\u56db\u4e2a\u6c49\u4eba\u3002\u4ed6\u4eec\u672a\u66fe\u8e0f\u5165\u5c4b\u5185\uff0c\u5c31\u5148\u542c\u89c1\u5176\u4e2d\u4e00\u4e2a\u4eba\u8bf4\u8bdd \u7684\u58f0\u97f3\u4e86\u3002 \u3000\u3000\u201c\u54c8\u5fc5\u56fe\u867d\u7136\u8bf4\u4ed6\u4eec\u90fd\u53d7\u4e86\u4f24\uff0c\u4f46\u54b1\u4eec\u8fd8\u662f\u5c0f\u5fc3\u4e00\u70b9\u7684\u597d\u3002\u201d\u5f20\u708e\u5f81\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a \u201c\u8fd9\u4eba\u50cf\u662f\u719f\u4eba\uff0c\u4ed6\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u8c1c\u5e95\u9a6c\u4e0a\u63ed\u5f00\uff0c\u90a3\u4e2a\u4eba\u5df2\u7ecf\u51fa\u73b0\u5728\u4ed6\u7684\u9762\u524d\u3002 \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u708e\uff0c\u4f60\u60f3\u4e0d\u5230\u6211\u4f1a\u627e\u5230\u8fd9\u91cc\u6765\u5427\uff1f\u201d\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u7518\u5fc5\u80dc\uff0c \u542c\u8bf4\u5cb3\u5c11\u4fdd\u5f52\u5929\u4e4b\u540e\uff0c\u4f60\u5728\u79e6\u6867\u624b\u4e0b\u505a\u4e8b\uff0c\u5f88\u5f97\u610f\u554a\uff0c\u4f60\u6765\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u7518\u5fc5\u80dc\u672c\u662f\u5cb3\u98de\u7684\u90e8\u4e0b\uff0c\u66fe\u7ecf\u5230\u8fc7\u5f20\u5baa\u7684\u5bb6\u91cc\u3002 \u3000\u3000\u7518\u5fc5\u80dc\u9053\uff1a\u201c\u5f20\u5144\uff0c\u591a\u8c22\u4f60\u8fd8\u8bb0\u5f97\u6211\u3002\u8001\u6bb5\u4e5f\u662f\u5230\u8fc7\u5f20\u5bb6\u7684\uff0c\u4e0d\u8fc7\u4ed6\u53ea\u53bb\u8fc7\u4e00\u6b21\uff0c\u4f60\u4e0d \u8ba4\u8bc6\u4ed6\u4e86\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u5de5\u592b\u548c\u4f60\u4eec\u53d9\u65e7\uff0c\u6709\u8bdd\u5feb\u8bf4\uff0c\u6709\u5c41\u5feb\u653e\u3002\u201d \u3000\u3000\u7518\u5fc5\u80dc\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u662f\u5949\u547d\u6765\u6349\u62ff\u72af\u4eba\u7684\u5bb6\u5c5e\u7684\u3002\u672c\u6765\u4f60\u4e5f\u8131\u4e0d\u4e86\u5173\u7cfb\uff0c\u4e0d\u8fc7\u54b1 \u4eec\u662f\u8001\u670b\u53cb\uff0c\u53ea\u8981\u4f60\u61c2\u5f97\u8f6c\u98ce\u4f7f\u8235\uff0c\u6211\u5f53\u7136\u4e0d\u4f1a\u96be\u4e3a\u8001\u670b\u53cb\u7684\u3002\u201d \u3000\u3000\u5f20\u708e\u62cd\u6848\u800c\u8d77\uff1a\u201c\u72af\u4eba\uff0c\u8c01\u662f\u72af\u4eba\uff1f\u201d \u3000\u3000\u7518\u5fc5\u80dc\u9053\uff1a\u201c\u8fd9\u4f4d\u5a18\u5b50\u662f\u5f20\u5baa\u7684\u5973\u513f\u5427\uff1f\u201d \u3000\u3000\u5f20\u708e\u559d\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u628a\u6211\u62ff\u53bb\u597d\u4e86\uff0c\u53ef\u522b\u4f24\u5bb3\u6211\u7684\u7239 \u7239\u3002\u201d \u3000\u3000\u7518\u5fc5\u80dc\u4e0d\u7406\u4f1a\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u5cb3\u98de\u548c\u5f20\u5baa\u72af\u4e86\u8c0b\u53cd\u4e4b\u7f6a\uff0c\u65e9\u5df2\u660e\u6b63\u5178\u5211\uff0c\u5f20\u5baa\u7684\u5973\u513f\u4e0d\u662f \u72af\u4eba\u7684\u5bb6\u5c5e\u662f\u4ec0\u4e48\uff1f\u201d\u5f20\u708e\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u53db\u4e3b\u6c42\u8363\u7684\u597d\u8d3c\uff0c\u7adf\u6562\u8bf4\u51fa\u8fd9\u6837\u4e27\u5c3d\u5929\u826f\u7684\u8bdd\u3002\u6211 \u8bf4\uff0c\u79e6\u6867\u624d\u662f\u72af\u4eba\uff01\u201d \u3000\u3000\u7518\u5fc5\u80dc\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e0d\u7b97\u6570\uff0c\u8981\u7687\u4e0a\u8bf4\u7684\u624d\u7b97\u6570\u5cb3\u98de\u3001\u5f20\u5baa\u72af\u4e86\u8c0b\u53cd\u4e4b\u7f6a\uff0c\u662f\u7687 \u4e0a\u5b9a\u6848\u7684\u3002\u79e6\u76f8\u516c\u53ef\u662f\u4e00\u76f4\u53d7\u5230\u7687\u4e0a\u91cd\u7528\u7684\u5bb0\u76f8\u3002\u5929\u65e0\u4e8c\u65e5\uff0c\u6c11\u65e0\u4e8c\u4e3b\uff0c\u6211\u53ea\u77e5\u9053\u7687\u4e0a\u662f\u6211 \u7684\u4e3b\u5b50\u3002\u4e0d\u50cf\u4f60\u773c\u4e2d\u53ea\u77e5\u6709\u5cb3\u98de\u5f20\u5baa\uff0c\u4e0d\u77e5\u6709\u7687\u4e0a\u3002\u53db\u4e3b\u6c42\u8363\u8fd9\u56db\u4e2a\u5b57\uff0c\u8bf7\u4f60\u6536\u56de\u53bb\u81ea\u7528 \u5427\uff01\u201d\u5cb3\u98de\u7684\u51a4\u72f1\u5c1a\u672a\u5f97\u5230\u5e73\u53cd\uff0c\u4ed6\u8bf4\u7684\u8fd9\u756a\u8bdd\u5012\u4e5f\u4e0d\u80fd\u7b97\u662f\u5f3a\u8bcd\u593a\u7406\u3002\u5f20\u708e\u4e0d\u6562\u9a82\u7687\u5e1d\uff0c \u4e5f\u5c31\u4e0d\u80fd\u9488\u950b\u76f8\u5bf9\u5730\u53cd\u9a73\u4ed6\u4e86\u3002\u53ea\u597d\u79fb\u8f6c\u77db\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u79e6\u6867\u4e4b\u5978\uff0c\u5929\u4e0b\u5171\u89c1\u3002\u79e6\u6867\u5df1\u7ecf\u6b7b \u4e86\uff0c\u4f60\u4f55\u5fc5\u8fd8\u505a\u4ed6\u7684\u722a\u7259\uff0c\u6765\u6b8b\u5bb3\u5fe0\u826f\u4e4b\u540e\u3002\u201d \u3000\u3000\u90a3\u59d3\u6bb5\u7684\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u708e\uff0c\u4f60\u9519\u4e86\uff0c\u7518\u5927\u54e5\u5982\u4eca\u662f\u5927\u5185\u4f8d\u536b\uff0c \u4ed6\u662f\u5949\u4e86\u7687\u4e0a\u4e4b\u547d\u6765\u62ff\u94a6\u72af\u3002\u6211\u548c\u4ed6\u4e00\u6837\uff0c\u4e5f\u662f\u65e9\u5df2\u7531\u79e6\u76f8\u516c\u4fdd\u8350\u7ed9\u7687\u4e0a\uff0c\u5f53\u4e0a\u4e86\u5927\u5185\u4f8d\u536b \u4e86\u3002\u201d \u3000\u3000\u5f20\u708e\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u7cbe\u5fe0\u62a5\u56fd\uff0c\u4ed6\u7684\u5916\u5973\u513f\u5728\u4ed6\u53d7\u5bb3\u4e4b\u65f6\u521a\u6ee1\u5468\u5c81\uff0c\u66f4\u662f\u6839\u672c\u5c31\u4e0d \u53ef\u80fd\u72af\u7f6a\u3002\u6211\u4e0d\u7ba1\u4f60\u4eec\u662f\u5426\u5949\u4e86\u5723\u65e8\uff0c\u6211\u7edd\u4e0d\u8bb8\u4f60\u4eec\u4f24\u5bb3\u5979\uff01\u201d \u3000\u3000\u90a3\u59d3\u6bb5\u7684\u51b7\u7b11\u9053\uff1a\u201c\u5f20\u708e\uff0c\u4f60\u522b\u6446\u51fa\u4e00\u526f\u7ef4\u62a4\u5fe0\u826f\u7684\u9762\u5b54\u4e86\uff0c\u4f60\u53e3\u53e3\u58f0\u58f0\u8bf4\u751a\u5fe0\u5978\uff0c\u6211 \u95ee\u4f60\uff0c\u4f60\u662f\u5fe0\u662f\u5978\uff1f\u201c\u5f20\u708e\u6012\u9053\uff1a\u201c\u6211\u662f\u5fe0\u4e8e\u5b8b\u56fd\u7684\u8001\u767e\u59d3\uff01\u201d \u3000\u3000\u90a3\u59d3\u6bb5\u7684\u6307\u7740\u6a80\u516c\u76f4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u4f60\u7684\u4eb2\u5bb6\u5427\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u4e5f\u662f\u91d1\u56fd\u7684\u738b\u7237\uff0c\u5bf9 \u5417\uff1f\u201d \u3000\u3000\u5f20\u708e\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u59d3\u6bb5\u7684\u51b7\u7b11\u9053\uff1a\u201c\u5f20\u5baa\u7684\u5973\u513f\u4ece\u4f60\u4e3a\u7236\uff0c\u4f60\u628a\u5979\u8bb8\u914d\u7ed9\u91d1\u56fd\u7684\u738b\u7237\u4e4b\u5b50\uff0c\u4e8f\u4f60\u8fd8\u6562\u8bf4\u4e2a \u5fe0\u5b57\u3002\u201d \u3000\u3000\u5f20\u708e\u6c14\u5f97\u5927\u9a82\uff1a\u201c\u4ed6\u662f\u53cd\u5bf9\u91d1\u56fd\u7684\u7687\u5e1d\u4fb5\u5b8b\u7684\uff0c\u8981\u8bf4\u4e0d\u5fe0\uff0c\u53ea\u80fd\u8bf4\u4ed6\u662f\u5bf9\u91d1\u56fd\u7684\u7687\u5e1d\u4e0d \u5fe0\u3002\u4f60\u4eec\u6839\u672c\u5c31\u4e0d\u914d\u548c\u4ed6\u76f8\u6bd4\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8eab\u4efd\u662f\u54c8\u5fc5\u56fe\u544a\u8bc9\u4f60\u4eec\u7684\u5427\uff1f\u201d\u7518\u5fc5\u80dc\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u3002\u4f60 \u4eec\u81ea\u5df1\u4eba\u8bf4\u7684\u5f53\u7136\u4e0d\u4f1a\u662f\u5047\u8bdd\u3002\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u4ed6\u8bf4\u6211\u7684\u8eab\u4efd\u70b9\u4e0d\u5047\uff0c\u4f46\u6709\u6869\u4e8b\u60c5\uff0c\u4f60\u5374\u8bf4\u9519\u4e86\u3002\u201d\u7518\u5fc5\u80dc\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b \u60c5\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u80af\u548c\u4f60\u4eec\u8bf4\u771f\u8bdd\uff0c\u4f3c\u4e4e\u4f60\u4eec\u624d\u79f0\u5f97\u4e0a\u548c\u4ed6\u662f\u81ea\u5df1\u4eba\uff01\u201d \u3000\u3000\u7518\u5fc5\u80dc\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u5de5\u592b\u4e0e\u4f60\u80e1\u626f\uff0c\u4f60\u4eec\u901a\u901a\u90fd\u662f\u72af\u4eba\uff01 \u3000\u3000\u600e\u6837\uff0c\u4f60\u60f3\u62d2\u6355\u5417\uff1f\u201c\u5728\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u6a80\u516c\u76f4\u5df2\u7ecf\u7ad9\u4e86\u8d77\u6765\uff0c\u53cc\u76ee\u4e0d\u6012\u800c\u5a01\uff0c\u51b7\u51b7\u5730\u76ef \u7740\u7518\u5fc5\u80dc\uff0c\u7518\u5fc5\u80dc\u867d\u7136\u77e5\u9053\u4ed6\u53d7\u4e86\u4f24\u3002\u5fc3\u4e2d\u4ea6\u662f\u6709\u70b9\u6050\u614c\u3002 \u3000\u3000\u60f3\u9053\uff1a\u201c\u91d1\u56fd\u7684\u4e09\u4e2a\u5df4\u56fe\u9c81\uff0c\u5728\u4ed6\u624b\u4e0b\u4e24\u6b7b\u4e00\u4f24\uff0c\u8981\u662f\u4ed6\u4f24\u5f97\u4e0d\u91cd\uff0c\u6211\u6050\u6015\u672a\u5fc5\u6253\u5f97\u8fc7 \u4ed6\u3002\u201d\u90a3\u59d3\u6bb5\u7684\u8bf4\uff1a\u201c\u5f20\u708e\uff0c\u6211\u529d\u4f60\u4eec\u8fd8\u662f\u675f\u624b\u5c31\u64d2\u7684\u597d\uff0c\u514d\u5f97\u591a\u53d7\u76ae\u8089\u4e4b\u82e6\u3002\u4f60\u53d7\u5f97\u4e86\uff0c \u4f60\u7684\u4e49\u5973\u548c\u5916\u5b59\u672a\u5fc5\u53d7\u5f97\u4e86\uff01\u201d \u3000\u3000\u5f20\u708e\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u63d0\u9192\u6211\uff0c\u4e0d\u9519\uff0c\u4eba\u751f\u7ec8\u6709\u4e00\u6b7b\uff0c\u4f55\u4e0d\u6b7b\u5f97\u75db\u5feb\u4e00\u4e9b\u3002 \u597d\uff0c\u6211\u675f\u624b\u5c31\u64d2\u4fbf\u662f\uff01\u201d\u4ed6\u8d70\u5230\u90a3\u59d3\u6bb5\u7684\u9762\u524d\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u59d3\u6bb5\u7684\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6bb5\u7cbe\u5fe0\uff0c\u600e\u7684\u4f60\u8fde\u6211\u7684\u540d\u5b57\u90fd\u5fd8\u8bb0\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5f20\u708e\u9661\u5730\u51b7\u7b11\u559d\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u624d\u662f\u7cbe\u5fe0\u62a5\u56fd\uff0c\u51ed\u4f60\u8fd9\u5978\u8d3c\u4e5f\u914d\u7528\u7cbe\u5fe0\u4e8c\u5b57\uff1f\u201d\u5927\u559d\u58f0 \u4e2d\uff0c\u6574\u4e2a\u8eab\u4f53\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u548c\u6bb5\u7cbe\u5fe0\u4e00\u6253\u8d77\u6765\uff0c\u767b\u65f6\u9664\u4e86\u5b69\u5b50\u4e4b\u5916\uff0c\u6240\u6709\u7684\u4eba\u90fd\u6253\u8d77\u6765\u4e86\u3002\u6a80\u516c\u76f4\u65e9\u5df2\u84c4\u52bf\u5f85 \u53d1\uff0c\u4e00\u51fa\u624b\u5f53\u771f\u662f\u52a8\u5982\u8131\u5154\uff0c\u7b2c\u4e00\u62db\u5c31\u62db\u5c31\u6253\u4e2d\u4e86\u7518\u5fc5\u80dc\u3002 \u3000\u3000\u7518\u5fc5\u80dc\u7ed9\u4ed6\u4e00\u638c\u6253\u7740\u80f8\u819b\uff0c\u5148\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8ddf\u7740\u5374\u662f\u5927\u559c\u3002 \u3000\u3000\u539f\u6765\u4ed6\u867d\u7136\u89c9\u5f97\u6709\u70b9\u75bc\u75db\uff0c\u5374\u8fd8\u4e0d\u5982\u9884\u6599\u4e4b\u751a\u3002\u6309\u8bf4\u9ad8\u624b\u62fc\u6597\uff0c\u5bf9\u65b9\u82e5\u662f\u7528\u4e0a\u5185\u529b\u7684 \u8bdd\uff0c\u7ed9\u6253\u7740\u80f8\u819b\uff0c\u90a3\u662f\u975e\u5f97\u5f53\u573a\u5455\u8840\u4e0d\u53ef\u7684\u3002\u6a80\u516c\u76f4\u5f53\u7136\u4e0d\u4f1a\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u6709\u5185\u529b\u800c\u4e0d\u4f7f\u7528 \u7684\u3002\u201c\u539f\u6765\u4ed6\u679c\u7136\u662f\u53d7\u4e86\u91cd\u4f24\uff0c\u771f\u6c14\u90fd\u5df1\u6cf1\u6563\u4e86\uff01\u201d \u3000\u3000\u7518\u5fc5\u80dc\u5728\u56db\u4e2a\u4eba\u4e2d\u6b66\u529f\u6700\u9ad8\uff0c\u4e34\u654c\u7684\u7ecf\u9a8c\u4e5f\u6700\u4e30\u5bcc\uff0c\u7acb\u5373\u62fe\u8d77\u4e86\u5730\u4e0a\u7684\u4e00\u67c4\u730e\u53c9\u3002\u4e00\u5f53 \u4f5c\u5175\u5668\uff0c\u4e0d\u548c\u6a80\u516c\u76f4\u6bd4\u62fc\u62f3\u811a\u4e86\u3002 \u3000\u3000\u8fd9\u67c4\u730e\u53c9\u6709\u4e03\u5c3a\u591a\u957f\uff0c\u6a80\u516c\u76f4\u5185\u529b\u6d88\u5931\uff0c\u593a\u4e0d\u4e86\u4ed6\u7684\u730e\u864e\u53c9\u3002\u5373\u4f7f\u4ed6\u4e00\u65f6\u95f4\u523a\u4e0d\u4e2d\u6a80\u516c \u76f4\u3002\u4ea6\u5df2\u662f\u5904\u4e8e\u4e0d\u8d25\u8fd9\u5730\u3002\u53e6\u5916\u4e24\u540d\u536b\u58eb\u3002\u4e00\u4e2a\u53eb\u674e\u5927\u6210\u3002\u4e00\u4e2a\u53eb\u90d1\u5fb7\u4e1a\u3002\u90d1\u5fb7\u4e1a\u5728\u56db\u4e2a\u4eba \u4e2d\u672c\u9886\u6700\u4f4e\uff0c\u4ed6\u53ea\u9053\u5973\u5b50\u5bb9\u6613\u6b3a\u8d1f\uff0c\u4e8e\u662f\u5c31\u8dd1\u4e0a\u53bb\u6293\u5f20\u96ea\u6ce2\u3002\u6a80\u9053\u6210\u6293\u51fa\u8170\u5200\u3002\u5c31\u51b2\u4e0a\u53bb\uff0c \u5374\u7ed9\u674e\u5927\u6210\u62e6\u4f4f\u3002 \u3000\u3000\u674e\u5927\u6210\u7528\u7684\u662f\u53cc\u80a1\u5251\uff0c\u82e5\u8bba\u771f\u5b9e\u672c\u9886\uff0c\u6a80\u9053\u6210\u672c\u6765\u80dc\u4ed6\u4e00\u7b79\uff0c\u4f46\u53ef\u60dc\u5df2\u53d7\u4e86\u4f24\uff0c\u8df3\u8dc3\u4e0d \u7075\uff0c\u88ab\u4ed6\u62e6\u4f4f\u3002\u5374\u662f\u51b2\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u4e2d\uff0c\u5012\u662f\u5f20\u96ea\u6ce2\u53ef\u4ee5\u548c\u5bf9\u65b9\u6253\u6210\u5e73\u624b\u3002\u5979\u7528\u5f20\u708e\u7684\u5315\u9996\u5e94\u654c\uff0c\u53d1\u6325\u4e86\u201c\u4e00\u5bf8\u77ed\uff0c \u4e00\u5bf8\u9669\u201d\u7684\u5a01\u529b\u3002 \u3000\u3000\u90d1\u5fb7\u4e1a\u7684\u53cc\u5200\u51e0\u4e4e\u906e\u62e6\u4e0d\u4f4f\u3002\u8981\u4e0d\u662f\u5979\u6b20\u7f3a\u4e34\u654c\u7ecf\u9a8c\uff0c\u65e9\u5df2\u523a\u4f24\u654c\u624b\u3002 \u3000\u3000\u5f20\u708e\u4f24\u52bf\u4e4b\u91cd\uff0c\u4ec5\u6b21\u4e8e\u6a80\u516c\u76f4\uff0c\u4ed6\u81ea\u77e5\u4e0d\u8010\u4e45\u6218\uff0c\u5fc5\u987b\u901f\u6218\u901f\u51b3\uff0c\u662f\u4ee5\u4ed6\u7684\u6253\u6cd5\u4e5f\u4e0e\u4f17 \u4e0d\u540c\u3002\u4e00\u4e0a\u6765\u5c31\u662f\u86ee\u6253\u3002 \u3000\u3000\u5927\u559d\u58f0\u4e2d\uff0c\u5f20\u708e\u6574\u4e2a\u8eab\u4f53\u6251\u4e0a\u524d\u53bb\uff0c\u53cc\u81c2\u9f50\u5f20\uff0c\u597d\u4f3c\u4e24\u628a\u94c1\u94b3\uff0c\u5c06\u6bb5\u7cbe\u5fe0\u62e6\u8170\u7b8d\u4efb\uff0c\u4e24 \u4eba\u53d8\u4f5c\u4e86\u5012\u5730\u846b\u82a6\u3002\u6bb5\u7cbe\u5fe0\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201c\u4ed6\u7528\u7684\u662f\u4e00\u67c4\u4e09\u5c3a\u591a\u957f\u7684\u9752\u94a2 \u5251\uff0c\u4ed6\u7684\u8eab\u4f53\u5df2\u7ecf\u88ab\u538b\u5728\u4e0b\u9762\uff0c\u624b\u81c2\u7f29\u4e0d\u56de\u6765\uff0c\u53ea\u597d\u5c3d\u529b\u5f2f\u66f2\u624b\u8155\uff0c\u53cd\u624b\u628a\u5251\u5c16\u63d2\u5165\u5f20\u708e\u80cc \u5fc3\u3002 \u3000\u3000\u5251\u5c16\u5df2\u7ecf\u523a\u4e86\u8fdb\u53bb\u4e09\u5bf8\u6709\u591a\uff0c\u6bb5\u7cbe\u5fe0\u6b63\u8981\u6709\u529b\u63d2\u8fc7\u4ed6\u7684\u5fc3\u810f\uff0c\u4e0d\u6599\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u624b\u81c2 \u8f6f\u7ef5\u7ef5\u5730\u5782\u4e86\u4e0b\u6765\u3002\u6b63\u597d\u5728\u8fd9\u751f\u6b7b\u5173\u5934\uff0c\u5f20\u708e\u7684\u62c7\u6307\u6309\u4f4f\u4e86\u4ed6\u7684\u6c14\u6108\u7a74\u3002\u6c14\u6108\u7a74\u4e43\u662f\u4e09\u9633\u7ecf \u8109\u6c47\u5408\u4e4b\u70b9\uff0c\u4e00\u88ab\u6309\u4f4f\uff0c\u534a\u70b9\u6c14\u529b\u4e5f\u4f7f\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5f20\u708e\u594b\u8d77\u795e\u5a01\u628a\u654c\u4eba\u7684\u5934\u9885\u5f80\u5730\u4e0a\u731b\u649e\uff0c\u4e00\u9762\u649e\u4e00\u9762\u559d\u9a82\uff1a\u201c\u4f60\u8fd9\u80cc\u4e3b\u6c42\u8363\u7684\u5978\u8d3c\uff0c\u4e5f \u914d\u53eb\u505a\u7cbe\u5fe0\uff01\u201d\u6bb5\u7cbe\u5fe0\u8111\u888b\u5f00\u82b1\uff0c\u7ec8\u4e8e\u7ed9\u4ed6\u6253\u6b7b\u3002\u5f20\u708e\u677e\u4e86\u53e3\u6c14\uff0c\u65b9\u59cb\u9690\u9690\u89c9\u5f97\u5168\u8eab\u53d1\u9ebb\uff0c \u4ed6\u7684\u6c14\u529b\u4ea6\u5df2\u7528\u5c3d\u4e86\u3002 \u3000\u3000\u90d1\u5fb7\u4e1a\u6253\u4e0d\u8fc7\u5f20\u96ea\u6ce2\u3002\u6076\u5ff5\u9661\u751f\uff0c\u7a81\u7136\u5411\u5979\u7684\u5b69\u5b50\u6251\u53bb\u3002 \u3000\u3000\u6a80\u9053\u6210\u4e00\u89c1\u5b69\u5b50\u5371\u9669\uff0c\u4e5f\u594b\u4e0d\u987e\u8eab\u7684\u5411\u524d\u731b\u6251\u3002\u4ed6\u672c\u662f\u88ab\u674e\u5927\u6210\u62e6\u4f4f\u7684\u3002\u4ed6\u786c\u51b2\u8fc7\u53bb\u773c \u4e2d\u53ea\u6709\u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u674e\u5927\u6210\u5728\u4ed6\u80cc\u540e\u7acb\u65bd\u6740\u624b\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u8dcc\u5012\u5730\u4e0a\uff0c\u90d1\u5fb7\u4e1a\u6b63\u8981\u4e00\u811a\u8e0f\u4e0b\u53bb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0e\u6a80\u9053\u6210\u5df2\u662f\u4e00\u62f3\u5411\u4ed6\u6253 \u6765\u3002\u90d1\u5fb7\u4e1a\u89c1\u4ed6\u52bf\u5982\u75af\u864e\uff0c\u4e0d\u6562\u62b5\u6321\uff0c\u614c\u5fd9\u8eb2\u95ea\u3002\u4f46\u4ed6\u4eec\u662f\u4e00\u4e2a\u8ddf\u7740\u4e00\u4e2a\u7684\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u674e \u5927\u6210\u7684\u5de6\u624b\u5251\u4ea6\u5df2\u4ece\u6a80\u9053\u6210\u7684\u53f3\u808b\u523a\u5165\u3002\u6a80\u9053\u6210\u559d\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u4e94\u6307\u5982\u7ea6\uff0c\u53cd\u624b\u6293 \u7834\u4e86\u674e\u5927\u6210\u7684\u54bd\u5589\uff0c\u674e\u5927\u6210\u5012\u4e86\u4e0b\u53bb\uff0c\u8840\u6d41\u6ee1\u5730\u3002\u4f46\u6a80\u9053\u6210\u7684\u4f24\u53e3\u6269\u5927\uff0c\u9c9c\u8840\u4ea6\u5df2\u5728\u5927\u91cf\u6d41 \u51fa\u3002\u6a80\u9053\u6210\u53eb\u9053\uff1a\u201c\u51b2\u513f\u5feb\u9003\uff0c\u957f\u5927\u4e86\u7ed9\u7239\u7239\u62a5\u4ec7\u3002\u201d\u4ed6\u7684\u5b69\u5b50\u4e5f\u4e0d\u77e5\u662f\u5426\u7ed9\u5413\u5f97\u50bb\u4e86\uff0c\u6b64 \u65f6\u867d\u7136\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u5374\u6ca1\u6709\u9003\u3002\u5f20\u96ea\u6ce2\u6b64\u9645\u773c\u4e2d\u4e5f\u662f\u53ea\u89c1\u5b69\u5b50\uff0c\u987e\u4e0d\u5f97\u9632\u5907\u654c\u4eba\u4e86\u3002\u5f20\u96ea\u6ce2 \u5411\u5b69\u5b50\u8dd1\u53bb\uff0c\u90d1\u5fb7\u4e1a\u821e\u52a8\u53cc\u5200\uff0c\u4ece\u5979\u80cc\u540e\u780d\u6765\u3002\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u4f60\u6562\u780d\u6211\u5a18\u4eb2\uff0c\u6211\u6253\u6b7b\u4f60\uff01\u201d \u4ed6\u975e\u4f46\u6ca1\u6709\u9003\uff0c\u53cd\u800c\u5411\u90d1\u5fb7\u4e1a\u6251\u53bb\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5927\u60ca\uff0c\u614c\u5fd9\u659c\u8eab\u7a9c\u4e0a\uff0c\u60f3\u8981\u62b1\u4e86\u5b69\u5b50\u9009\u8d70\u3002\u4e5f\u5e78\u800c\u6709\u8fd9\u5b69\u5b50\u628a\u5979\u5f15\u5f00\uff0c\u5979\u7684\u8eab\u6cd5 \u6bd4\u90d1\u5fb7\u4e1a\u5feb\uff0c\u8fd9\u624d\u6ca1\u6709\u7ed9\u90d1\u5fb7\u4e1a\u780d\u7740\u3002 \u3000\u3000\u90d1\u5fb7\u4e1a\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5b69\u5b50\u5e76\u6ca1\u7ed9\u4ed6\u8e22\u4e2d\uff0c\u4f46\u5374\u4e0d\u77e5\u662f\u5426\u7ed9\u5413\u5f97\u614c\u4e86\uff0c\u53cc\u8db3\u7ad9\u7acb\u4e0d\u7a33\uff0c \u53c8\u8dcc\u5012\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u559d\u9053\uff1a\u201c\u8c01\u6562\u4f24\u5bb3\u6211\u513f\u3002\u6211\u8981\u4ed6\u6b7b\uff01\u201d\u5315\u9996\u53cd\u8eab\u523a\u51fa\uff0c\u62fc\u547d\u4fdd\u62a4\u4eb2\u513f\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u5979\u5df2\u6c89\u4e0d\u4f4f\u6c14\u3002\u4e3a\u4e86\u4fdd\u62a4\u513f\u5b50\uff0c\u4e5f\u4e0d\u80fd\u7528\u7ed5\u8eab\u6e38\u6597\u6765\u53d1\u6325\u5979\u7684\u6240\u957f\u4e86\u3002\u5315\u9996\u53ea\u6709 \u4e03\u5bf8\u957f\uff0c\u53ef\u662f\u62b5\u654c\u4e0d\u8fc7\u90d1\u5fb7\u4e1a\u7684\u53cc\u5200\u3002 \u3000\u3000\u7a81\u7136\uff0c\u90d1\u5fb7\u4e1a\u5ffd\u89c9\u5267\u75db\u900f\u5fc3\uff0c\u4e00\u58f0\u60e8\u53eb\uff0c\u8eab\u8eaf\u77ee\u4e86\u534a\u622a\u3002\u5f20\u96ea\u6ce2\u5315\u9996\u63d2\u4e0b\uff0c\u767b\u65f6\u523a\u7a7f\u4e86 \u4ed6\u7684\u5934\u9885\u3002\u539f\u6765\u90a3\u5b69\u5b50\u5728\u4ed6\u80ef\u4e0b\u4e00\u6293\uff0c\u6b63\u597d\u7b26\u5408\u4e86\u201c\u795e\u4ed9\u6458\u8304\u201d\u7684\u624b\u6cd5\u3002\u628a\u4ed6\u7684\u9634\u56ca\u6293\u7834 \u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u62d4\u51fa\u5315\u9996\uff0c\u53ea\u89c1\u90d1\u5fb7\u4e1a\u540e\u8111\u7a7f\u4e86\u4e2a\u6d1e\uff0c\u8111\u6d46\u548c\u9c9c\u8840\u8ff8\u6d41\u3002\u7ffb\u8d77\u6b7b\u9c7c\u4e00\u6837\u7684\u773c\u775b\uff0c \u7ec8\u4e8e\u5012\u4e86\u4e0b\u53bb\u3002\u5f20\u96ea\u6ce2\u4ece\u6765\u6ca1\u6709\u6740\u8fc7\u4eba\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5315\u9996\u8dcc\u5728\u5730\u4e0a\u3002\u5b69\u5b50\u6251\u5165\u5979\u7684\u6000\u4e2d\u3002\u5f20 \u96ea\u6ce2\u7d27\u7d27\u5c06\u5b69\u5b50\u6402\u4f4f\u3002\u6bcd\u5b50\u4e24\u4eba\u3002\u90fd\u662f\u7ed9\u5413\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u7518\u5fc5\u80dc\u4e00\u770b\uff0c\u81ea\u5df1\u5e26\u6765\u7684\u4e09\u4e2a\u4eba\u90fd\u5df2\u6b7b\u6389\uff0c\u81ea\u662f\u4e0d\u514d\u5fc3\u614c\u3002\u4e0d\u8fc7\u5bf9\u65b9\u4ea6\u5df2\u6709\u4e24\u4e2a\u4eba\u2014\u4e00 \u5f20\u708e\u548c\u6a80\u9053\u6210\u53d7\u4e86\u91cd\u4f24\uff0c\u8fd8\u6709\u4e00\u4e2a\u5f20\u96ea\u6ce2\u867d\u6ca1\u53d7\u4f24\uff0c\u663e\u7136\u4ea6\u5df2\u662f\u65e0\u529b\u518d\u6218\u4e86\u3002\u6b64\u65f6\u4ed6\u6b63\u5728\u548c \u6a80\u516c\u76f4\u6076\u6597\uff0c\u5df2\u7ecf\u5360\u5230\u7edd\u5bf9\u4e0a\u98ce\uff0c\u4f30\u91cf\u4e0d\u51fa\u5341\u62db\u3002\u5c31\u53ef\u5236\u6a80\u516c\u76f4\u6b7b\u547d\u3002\u53ea\u8981\u5236\u4f4f\u4e86\u6a80\u516c\u76f4\uff0c \u6740\u5f20\u96ea\u6ce2\u6bcd\u5b50\u6613\u5982\u53cd\u638c\u3002 \u3000\u3000\u65e2\u7136\u662f\u7a33\u64cd\u80dc\u5238\uff0c\u7518\u5fc5\u80dc\u5f53\u7136\u662f\u4e0d\u80af\u9003\u8d70\uff0c\u53cd\u800c\u6539\u5f97\u66f4\u52a0\u6025\u4e86\u3002 \u3000\u3000\u6a80\u516c\u76f4\u76ee\u5149\u5446\u6ede\uff0c\u597d\u50cf\u5df2\u7ecf\u4e0d\u77e5\u9053\u95ea\u8eb2\u4f3c\u7684\uff0c\u7518\u5fc5\u80dc\u7684\u730e\u53c9\u523a\u6765\uff0c\u4ed6\u7adf\u7136\u633a\u80f8\u8fce\u4e0a\uff0c \u201c\u6734\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u730e\u53c9\u523a\u5165\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u7518\u5fc5\u80dc\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6a80\u8d1d\u52d2\uff0c\u8c01\u53eb\u4f60\u4e0d\u63a5\u65e8\uff0c\u4f60\u6b7b\u4e86\u4e5f\u6028\u4e0d\u5f97\u6211\uff01\u201c\u5ffd\u5730\u542c\u5f97\u6a80\u516c\u76f4\u4e5f \u5728\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u58f0\u5636\u54d1\uff0c\u96be\u542c\u975e\u5e38\u3002\u53d7\u4e86\u91cd\u4f24\u7684\u4eba\uff0c\u600e\u4e48\u8fd8\u7b11\u5f97\u51fa\uff1f\u7518\u5fc5\u80dc\u7ed9\u4ed6\u7b11\u5f97\u6bdb\u9aa8\u609a \u7136\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u89c9\u5f97\u4f60\u540d\u5b57\u597d\u7b11\u3002\u201d \u3000\u3000\u7518\u5fc5\u80dc\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u53e3\u51fa\u72c2\u8a00\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u6fc0\u6211\u52a8\u6012\uff0c\u8ba9\u4f60\u6b7b\u4e2a\u75db\u5feb\uff0c\u6211\u504f\u4e0d\u5982\u4f60\u6240\u613f\uff01\u201d \u3000\u3000\u7518\u5fc5\u80dc\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u53e3\u51fa\u72c2\u8a00\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u5f53\u771f\u6740\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u7518\u5fc5\u80dc\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u6fc0\u6211\u52a8\u6012\uff0c\u8ba9\u4f60\u6b7b\u4e2a\u75db\u5feb\uff0c\u6211\u504f\u4e0d\u5982\u4f60\u6240\u613f\uff01\u201d \u3000\u3000\u4ed6\u7684\u730e\u53c9\u5df2\u7ecf\u523a\u5165\u4e86\u6a80\u516c\u76f4\u7684\u80f8\u819b\uff0c\u53ea\u8981\u518d\u7528\u4e00\u70b9\u6c14\u529b\uff0c\u628a\u730e\u53c9\u63d2\u5f97\u6df1\u4e9b\uff0c\u5c31\u53ef\u53d6\u4e86\u6a80 \u516c\u76f4\u7684\u6027\u547d\u3002\u4f46\u56e0\u4ed6\u662f\u4f70\u4e86\u91d1\u4e3b\u4e4b\u547d\u65b9\u8981\u628a\u6a80\u516c\u76f4\u62bc\u5f80\u4eac\u5e08\u7684\uff0c\u6545\u6b64\u672a\u6562\u7acb\u65bd\u6740\u624b\u3002\u90a3\u77e5\u6a80 \u516c\u76f4\u5374\u633a\u8d77\u80f8\u819b\uff0c\u5411\u524d\u8e0f\u4e0a\u4e00\u6b65\uff0c\u6545\u610f\u8ba9\u90a3\u67c4\u730e\u53c9\u5728\u4ed6\u7684\u80f8\u819b\u5212\u6df1\u4e09\u5bf8\u3002 \u3000\u3000\u7518\u5fc5\u80dc\u5403\u4e86\u4e00\u60ca\u3002\u7ed9\u6a80\u516c\u76f4\u7684\u51b7\u7b11\u58f0\u7b11\u5f97\u5fc3\u91cc\u53d1\u6bdb\uff0c\u5fc3\u60f3\u4ed6\u4f24\u5f97\u8fd9\u6837\u91cd\uff0c\u6599\u60f3\u4e5f\u6551\u4e0d\u6d3b \u4e86\uff0c\u5fc3\u91cc\u53d1\u6bdb\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5b9a\u8981\u627e\u6b7b\u3002\u90a3\u6211\u5c31\u6210\u5168\u4f60\u5427\uff01\u201d \u3000\u3000\u6a80\u516c\u76f4\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u4f60\u6740\u4e0d\u4e86\u6211\uff0c\u90a3\u6211\u53ea\u80fd\u6740\u4f60\u4e86\uff01\u201d\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u628a\u730e\u53c9\u62d4\u4e86\u51fa \u6765\uff01 \u3000\u3000\u7518\u5fc5\u80dc\u672c\u6765\u662f\u628a\u730e\u53c9\u523a\u5165\u4ed6\u7684\u80f8\u819b\uff0c\u90a3\u77e5\u7ed9\u4ed6\u4e00\u62d4\uff0c\u7518\u5fc5\u80dc\u6240\u7528\u7684\u529b\u5ea6\u975e\u4f46\u7ed9\u4ed6\u62b5\u6d88\uff0c \u523a\u4e0d\u8fdb\u53bb\u3002\u730e\u53c9\u4e00\u62e8\u51fa\u6765\uff0c\u7518\u5fc5\u80dc\u53cd\u800c\u7ed9\u9707\u5f97\u51e0\u4e4e\u6454\u5012\u3002\u6a80\u516c\u76f4\u5927\u559d\u4e00\u58f0\uff0c\u5c31\u6251\u4e0a\u53bb\u3002 \u3000\u3000\u7518\u5fc5\u80dc\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u62a1\u8d77\u730e\u53c9\u6a2a\u6321\uff0c\u90a3\u77e5\u4ecd\u662f\u963b\u6321\u4e0d\u4e86\u3001\u6a80\u516c\u76f4\u547c\u5730\u4e00\u638c\u5288\u51fa\uff0c\u730e \u53c9\u767b\u65f6\u65ad\u4e3a\u4e24\u622a\uff0c\u7559\u5728\u7518\u5fc5\u80dc\u624b\u4e0a\u7684\u534a\u622a\u730e\u53c9\uff0c\u7ed9\u6a80\u516c\u76f4\u8fd9\u4e00\u51fb\u4e4b\u529b\uff0c\u53cd\u6233\u56de\u53bb\u3002\u867d\u7136\u53ea\u662f \u6728\u6746\u3002\u4e5f\u6233\u5165\u4e86\u4ed6\u7684\u80f8\u819b\u3002\u7518\u5fc5\u80dc\u53d1\u51fa\u4e00\u58f0\u6495\u5fc3\u88c2\u80ba\u7684\u60e8\u547c\uff0c\u5012\u6bd9\u5728\u8840\u6cca\u4e4b\u4e2d\u3002 \u3000\u3000\u6a80\u516c\u76f4\u7684\u80f8\u53e3\u5f00\u4e86\u4e00\u4e2a\u6d1e\uff0c\u9c9c\u8840\u4e5f\u50cf\u7bad\u4e00\u6837\u5c04\u51fa\u6765\u3002\u4ed6\u5140\u662f\u7eb5\u58f0\u5927\u7b11\uff1a\u201c\u6211\u8bf4\u4f60\u662f\u5fc5 \u8d25\uff0c\u6ca1\u8bf4\u9519\u5427\uff01\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u662f\u501f\u7518\u5fc5\u80dc\u4e4b\u529b\uff0c\u6545\u610f\u8ba9\u730e\u53c9\u523a\u5165\u80f8\u819b\uff0c\u6765\u65bd\u5c55\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u7684\u3002 \u3000\u3000\u56db\u4e2a\u5b8b\u56fd\u7684\u5927\u5185\u536b\u58eb\u90fd\u5df2\u6b7b\u4e86\uff0c\u4f46\u4ed6\u4eec\u8fd9\u4e24\u5bb6\u4eba\uff0c\u9664\u4e86\u5f20\u96ea\u6ce2\u6bcd\u5b50\u4e4b\u5916\uff0c\u4e09\u4e2a\u5927\u4eba\u4e5f\u90fd \u91cd\u4f24\uff0c\u547d\u5728\u987b\u81fe\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5413\u5f97\u4e0d\u77e5\u6240\u63aa\uff0c\u7239\u7239\u3001\u516c\u516c\u3001\u4e08\u592b\uff0c\u90fd\u662f\u8840\u6d41\u4e0d\u6b62\uff0c\u5148\u6551\u54ea\u4e00\u4e2a\u5462\uff1f\u4ed6\u4eec\u4f24\u5f97\u8fd9 \u6837\u91cd\uff0c\u6050\u6015\u54ea\u4e00\u4e2a\u4e5f\u6551\u4e0d\u6d3b\u4e86\uff01 \u3000\u3000\u5f20\u708e\u5ffd\u5730\u4ece\u8eab\u4e0a\u638f\u51fa\u4e00\u4e2a\u5c0f\u94f6\u74f6\uff0c\u629b\u7ed9\u5f20\u96ea\u6ce2\u3002 \u3000\u3000\u201c\u8fd9\u662f\u5cb3\u5c11\u4fdd\u519b\u4e2d\u6240\u7528\u7684\u91d1\u521b\u836f\uff0c\u5feb\uff0c\u5feb\u7ed9\u4f60\u7684\u516c\u516c\u6577\u836f\u2026\u2026\u201d\u5f20\u708e\u5636\u58f0\u53eb\u9053\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u63a5\u8fc7\u91d1\u521b\u836f\uff0c\u53ea\u542c\u5f97\u516c\u516c\u4e5f\u5728\u53eb\u9053\uff1a\u201c\u522b\u7ba1\u6211\uff0c\u5feb\u7ed9\u4f60\u7684\u7239\u7239\u6577\u836f\uff01\u201c\u5f20\u96ea\u6ce2\u5411 \u516c\u516c\u8d70\u8fd1\u4e24\u6b65\uff0c\u7565\u4e00\u8e0c\u8e87\u3001\u56de\u5934\u770b\u4e00\u770b\u5f20\u708e\u3002 \u3000\u3000\u5f20\u708e\u5636\u54d1\u7740\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u6211\u505a\u4e86\u9519\u4e8b\uff0c\u4eb2\u5bb6\uff0c\u4f60\u5c31\u8ba9\u6211\u4ee5\u6b7b\u8d4e\u7f6a\u5427\u3002 \u3000\u3000\u6211\u662f\u6551\u4e0d\u6d3b\u4e86\u7684\uff0c\u96ea\u513f\uff0c\u4f60\u8981\u628a\u5b69\u5b50\u629a\u517b\u6210\u4eba\uff0c\u6211\uff0c\u6211\u5c31\u5b89\u5fc3\u53bb\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5927\u53eb\uff1a\u201c\u7239\u7239\uff01\u201d\u53ea\u662f\u5f20\u708e\u5df2\u7ecf\u56e2\u4e0a\u773c\u775b\uff0c\u5979\u8dd1\u53bb\u63a2\u5f20\u708e\u7684\u9f3b\u606f\uff0c\u5f20\u708e\u5df1\u662f\u65ad\u4e86 \u6c14\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u6b32\u54ed\u65e0\u6cea\uff0c\u8fd9\u4e2a\u65f6\u5019\u4e5f\u8fd8\u4e0d\u662f\u60b2\u4f24\u7684\u65f6\u5019\uff0c\u5979\u5446\u4e86\u4e00\u5446\uff0c\u62ff\u8d77\u74f6\u91d1\u521b\u836f\uff0c\u53c8\u5411\u516c \u516c\u8dd1\u53bb\u3002 \u3000\u3000\u6a80\u516c\u76f4\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8d24\u5ab3\uff0c\u4f60\u542c\u7740\uff0c\u6211\u5df2\u7ecf\u7ed9\u51b2\u513f\u627e\u4e86\u5e08\u7236\uff0c\u6211\u7684\u623f\u95f4\u91cc\u6709\u4e00\u628a\u6a80\u9999\u6247 \u662f\u4ed6\u753b\u7684\uff0c\u4f60\u8981\u73cd\u91cd\u6536\u85cf\uff0c\u7559\u4f5c\u51b2\u513f\u4ed6\u65e5\u5e08\u5f92\u76f8\u8ba4\u7684\u4fe1\u7269\u3002\u201d\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u5c0f\uff0c\u5f20\u96ea\u6ce2\u628a\u90a3\u74f6 \u91d1\u521b\u836f\u5012\u4e86\u4e00\u534a\u5728\u4ed6\u7684\u4f24\u53e3\uff0c\u6a80\u516c\u76f4\u5df2\u7ecf\u95ed\u4e0a\u7684\u773c\u775b\uff0c\u5ffd\u5730\u7741\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u522b\u7cdf\u8e4b\u91d1\u521b\u836f\uff0c \u90a3\u4eba\u53eb\u8036\u3001\u8036\u5f8b\u2026 \u3000\u3000\u201d\u5f20\u96ea\u6ce2\u77e5\u9053\u516c\u516c\u8981\u544a\u8bc9\u5979\u3002\u4ed6\u7684\u90a3\u4f4d\u670b\u53cb\u7684\u540d\u5b57\uff0c\u4ea6\u5373\u662f\u5979\u7684\u513f\u5b50\u7684\u5e08\u7236\u540d\u5b57\uff0c\u4f46\u516c \u516c\u53ea\u80fd\u8bf4\u51fa\u8fd9\u4e2a\u4eba\u7684\u590d\u59d3\uff0c\u540d\u5b57\u5374\u662f\u8bf4\u4e0d\u5168\u4e86\u3002\u6a80\u516c\u76f4\u7ec6\u5982\u868a\u53eb\u7684\u58f0\u97f3\u4e5f\u4e2d\u65ad\u4e86\uff0c\u5f20\u96ea\u6ce2\u628a \u8033\u6735\u8d34\u5230\u4ed6\u7684\u5507\u8fb9\uff0c\u53ea\u89c9\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u90fd\u5df2\u7ecf\u53d8\u5f97\u50f5\u786c\u51b0\u51b7\u4e86\uff0c\u5f53\u7136\u4e5f\u662f\u4ec0\u4e48\u58f0\u97f3\u90fd\u542c\u4e0d\u89c1 \u4e86\u3002\u6a80\u9053\u6210\u5012\u5728\u8840\u6cca\u4e4b\u4e2d\uff0c\u6b64\u65f6\u4ed6\u7684\u5934\u4e5f\u6b63\u5728\u6162\u6162\u5411\u4e0b\u5782\uff0c\u773c\u775b\u4e5f\u5728\u6162\u6162\u9616\u4e0a\u4e86\u3002\u5f20\u96ea\u6ce2\u53eb \u9053\uff1a\u201c\u6210\u54e5\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201c\u6a80\u9053\u6210\u9053\uff1a\u201c\u96ea\uff0c\u96ea\u59b9\uff0c\u8bf7\u539f\u8c05\u6211\uff0c\u8fd9\u526f\u62c5\u5b50\u6211\u53ea\u80fd \u8ba9\u4f60\u72ec\u81ea\u6311\u4e86\uff01\u201d\u5f20\u96ea\u6ce2\u5fc3\u60c5\u6fc0\u52a8\u4e4b\u6781\uff0c\u62ff\u8d77\u5f20\u708e\u7ed9\u5979\u7684\u90a3\u67c4\u5315\u9996\uff0c\u8bf4\u9053\uff1a\u201c\u6210\u54e5\uff0c\u54b1\u4eec\u662f \u8bf4\u8fc7\u540c\u751f\u5171\u6b7b\u7684\uff0c\u4f60\u8981\u8d70\u6211\u548c\u4f60\u4e00\u8d77\u8d70\uff01 \u3000\u3000\u201d \u3000\u3000\u5979\u6b63\u8981\u628a\u5315\u9996\u523a\u5165\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u6a80\u9053\u6210\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u6c14\u529b\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u4f60\u7239 \u7239\u5429\u5490\u5417\uff1f\u8981\u6b7b\u5bb9\u6613\uff0c\u6d3b\u7740\u629a\u5b64\u5374\u96be\uff01\u96be\u7684\u7559\u7ed9\u4f60\u505a\uff0c\u6211\u8981\u4f60\u4e3a\u4e86\u54b1\u4eec\u7684\u5b69\u5b50\u6d3b\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u96ea\u6ce2\u7684\u5315\u9996\u8dcc\u843d\u4e86\u3002 \u3000\u3000\u6a80\u9053\u6210\u8138\u4e0a\u7efd\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u96ea\u59b9\uff0c\u4f60\u662f\u6211\u7684\u597d\u59bb\u5b50\uff0c\uff0e\u6211\u77e5\u9053\u4f60\u4f1a\u7b54\u5e94\u6211 \u7684\uff01\u201d\u773c\u775b\u7ec8\u4e8e\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u5b69\u5b50\u5927\u53eb\u201c\u7239\u7239\uff01\u201d\u6251\u5230\u7236\u4eb2\u8eab\u4e0a\u3002\u5f20\u96ea\u6ce2\u5446\u7740\u6728\u9e21\uff0c\u597d\u50cf\u7075\u9b42\u8131\u79bb\u8eaf\u58f3\uff0c\u4e5f\u968f\u4e08\u592b\u53bb \u4e86\u3002 \u3000\u3000\u5b69\u5b50\u7684\u54ed\u58f0\u628a\u5979\u4ece\u5669\u68a6\u4e2d\u60ca\u9192\u8fc7\u6765\u3002\u5979\u5fcd\u4f4f\u773c\u6cea\uff0c\u628a\u5b69\u5b50\u6402\u5728\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u8bb0\u7740\u7237\u7237 \u7684\u8bdd\uff0c\u597d\u5b69\u5b50\u662f\u4e0d\u54ed\u7684\uff0c\u957f\u5927\u4e86\u7ed9\u7239\u7239\u62a5\u4ec7\uff01\u201d \u3000\u3000\u53ef\u601c\u5979\u5728\u8fd9\u6837\u8bf4\u7684\u65f6\u5019\uff0c\u4ea6\u5df2\u662f\u54fd\u54bd\u4e0d\u80fd\u6210\u58f0\u4e86\u3002\u773c\u6cea\u6ca1\u6709\u6d41\u51fa\u6765\uff0c\u4f46\u5374\u5012\u6d41\u5728\u5979\u7684\u5fc3 \u91cc\u3002 \u3000\u3000\uff3c\uff3c\uff38\u65e5\u5f71\u897f\u659c\uff0c\u4e00\u4e2a\u9ed1\u8863\u5c11\u5987\u80cc\u7740\u5b69\u5b50\u4ece\u76d8\u9f99\u5c71\u4e0a\u8d70\u8fc7\u6765\uff0c\u8fd9\u4e2a\u9ed1\u8863\u5c11\u5987\u5c31\u662f\u521a\u521a\u906d \u9047\u5bb6\u6563\u4eba\u4ea1\u4e4b\u75db\u7684\u5f20\u96ea\u6ce2\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u662f\u5fd9\u4e86\u4e00\u4e2a\u4e0a\u5348\uff0c\u8349\u8349\u57cb\u846c\u4e86\u516c\u516c\u3001\u7239\u7239\u548c\u4e08\u592b\u4e4b\u540e\uff0c\u542b\u7740\u773c\u6cea\uff0c\u80cc\u8d77\u5979\u7684\u513f\u5b50 \u6a80\u7fbd\u51b2\u4e0b\u5c71\u9003\u96be\u7684\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u5931\u5c3d\u4eb2\u4eba\uff0c\u5929\u5730\u867d\u5927\uff0c\u5374\u4e0d\u77e5\u4f55\u5904\u53ef\u4ee5\u5bb9\u8eab\u3002 \u3000\u3000\u516c\u516c\u9057\u5631\uff0c\u8981\u5979\u53bb\u627e\u90a3\u4e2a\u7b54\u5e94\u4e86\u6536\u6a80\u7fbd\u51b2\u505a\u5f92\u5f1f\u7684\u4eba\uff0c\u4f46\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u5979\u5374\u8fd8\u672a\u77e5\u9053\u3002 \u4eba\u6d77\u832b\u832b\uff0c\u53c8\u600e\u77e5\u600e\u77e5\u4f55\u65f6\u53ef\u4ee5\u78b0\u4e0a\uff0c\u8bf4\u4e0d\u5b9a\u6c38\u8fdc\u4e5f\u78b0\u4e0d\u4e0a\uff01 \u3000\u3000\u5979\u4e5f\u4e0d\u77e5\u9053\u5916\u9762\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4e16\u754c\uff0c\u53ea\u77e5\u9053\u5916\u9762\u7684\u4e16\u754c\u66f4\u52a0\u8346\u68d8\u6ee1\u9014\u3002\u5c71\u4e0a\u7684\u8346\u68d8\u662f\u6709 \u5f62\u7684\u8fd8\u53ef\u4ee5\u907f\u5f00\uff0c\u5c71\u5916\u9762\u7684\u8346\u68d8\u662f\u65e0\u5f62\u7684\uff0c\u8981\u907f\u4e5f\u907f\u4e0d\u8fc7\u3002 \u3000\u3000\u4f46\u4e3a\u4e86\u5b69\u5b50\uff0c\u5979\u5fc5\u987b\u6d3b\u53bb\u4e0b\uff01 \u3000\u3000\u5fc3\u5934\u7684\u521b\u4f24\u8fd8\u5728\u6ef4\u8840\uff0c\u5979\u62d6\u7740\u6c89\u91cd\u7684\u811a\u6b65\uff0c\u548c\u8fc7\u53bb\u7684\u65e5\u5b50\u544a\u522b\uff0c\u548c\u957f\u7720\u5728\u8fd9\u5c71\u4e0a\u7684\u4eb2\u4eba \u544a\u522b\uff0c\u4e00\u6b65\u4e00\u4e2a\u811a\u5370\u5730\u8d70\u4e0b\u5c71\u3002 \u3000\u3000\u4eb2\u4eba\u5df2\u7ecf\u57cb\u846c\uff0c\u611f\u60c5\u5374\u4e0d\u80fd\u57cb\u846c\u3002\u8fd9\u5c71\u4e0a\u7684\u4e00\u8349\u4e00\u6728\uff0c\u90fd\u5728\u7275\u52a8\u5979\u7684\u6101\u6000\uff0c\u4ee4\u5979\u6709\u7740\u4f9d \u4f9d\u4e0d\u820d\u7684\u60c5\u611f\u3002\u5979\u5fcd\u4e0d\u4f4f\u8d70\u51e0\u6b65\u56de\u4e00\u56de\u5934\u3002 \u3000\u3000\u5b69\u5b50\u65e0\u77e5\uff0c\u4ee5\u4e3a\u6bcd\u4eb2\u662f\u56e0\u80cc\u7740\u4ed6\u8d70\u5f97\u7d2f\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u653e\u6211\u4e0b\u6765\uff0c\u6211\u8d70\u5f97\u52a8 \u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u77bf\u7136\u4e00\u7701\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u591a\u8c22\u4f60\u63d0\u9192\u6211\uff0c\u54b1\u4eec\u662f\u5e94\u6838\u8d70\u5f97\u5feb\u4e00\u70b9\u4e86\u201d\u5979 \u8fd9\u624d\u53d1\u89c9\uff0c\u8d70\u4e86\u534a\u5929\u4e0b\u5c71\u8def\u7a0b\u8fd8\u672a\u8d70\u4e86\u4e00\u534a\u3002\u867d\u8bf4\u5c71\u8def\u96be\u884c\uff0c\u8fd8\u662f\u6bd4\u666e\u901a\u4eba\u8d70\u5f97\u66f4\u6162\u4e86\u3002 \u3000\u3000\u6b63\u5f53\u5979\u52a0\u5feb\u811a\u6b65\u4e4b\u9645\uff0c\u5ffd\u5730\u542c\u5f97\u8bb8\u591a\u4eba\u4e00\u9f50\u53f1\u559d\u7684\u58f0\u97f3\uff0c\u524d\u9762\u7684\u5c71\u5761\u4e0a\u51fa\u73b0\u4e86\u4e00\u961f\u91d1 \u5175\uff01\u5979\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u548c\u5b69\u5b50\u85cf\u5728\u9ad8\u903e\u4eba\u5934\u7684\u4e71\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u65e5\u90fd\u662f\u559c\u6b22\u4ece\u90a3\u9762\u5c71\u5761\u4e0b\u5c71\u7684\uff0c\u5f20\u96ea\u6ce2\u662f\u4e3a\u4e86\u9884\u9632\u4e07\u4e00\uff0c\u6015\u4e07\u4e00\u78b0\u4e0a\u654c\u4eba\uff0c\u8fd9\u624d\u6545\u610f\u6311 \u9009\u8fd9\u9762\u8346\u68d8\u6ee1\u9014\u7684\u5c71\u5761\u4e0b\u5c71\u7684\u3002 \u3000\u3000\u5979\u672c\u6765\u4ee5\u4e3a\u654c\u4eba\u4e0d\u4f1a\u6765\u5f97\u8fd9\u6837\u5feb\uff0c\u90a3\u77e5\u8fd8\u662f\u6765\u4e86\uff01 \u3000\u3000\u5979\u4eec\u6bcd\u5b50\u85cf\u533f\u4e4b\u5904\uff0c\u548c\u5bf9\u9762\u7684\u5c71\u5761\u82e5\u662f\u62c9\u6210\u76f4\u7ebf\uff0c\u8ddd\u79bb\u4e0d\u8fc7\u534a\u91cc\u8def\u9014\uff0c\u90a3\u8fb9\u7684\u60c5\u666f\u53ef\u4ee5 \u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u8fd9\u961f\u91d1\u5175\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e8c\u4e09\u5341\u4eba\uff0c\u5018\u82e5\u6563\u5f00\u6765\u641c\u7d22\uff0c\u5979\u4eec\u6bcd\u5b50\u52bf\u5fc5\u96be\u9003\u9b54\u722a\u3002 \u3000\u3000\u4f46\u4ed6\u5728\u90a3\u961f\u91d1\u5175\u5e76\u6ca1\u6563\u5f00\u6765\u641c\u7d22\uff0c\u4ed6\u4eec\u5927\u58f0\u5406\u559d\uff0c\u539f\u6765\u8ffd\u6355\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5934\u6234\u7af9\u7b20\uff0c\u4ece\u5c71\u4e0a\u8d70\u4e0b\u6765\uff0c\u9762\u8c8c\u867d\u7136\u770b\u5f97\u4e0d\u5f88\u6e05\u695a\uff0c\u4f46\u5374\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u5e76\u975e\u5c71 \u4e0a\u7684\u730e\u6237\u3002\u5c71\u4e0a\u7684\u730e\u6237\u53ea\u6709\u5341\u6765\u5bb6\uff0c\u6bcf\u4e00\u4e2a\u4eba\u5f20\u96ea\u6ce2\u90fd\u719f\u6089\u7684\u3002\u8fd9\u4eba\u6b65\u5c65\u5982\u98de\uff0c\u770b\u6765\u6b66\u529f\u4e5f \u4f3c\u4e0d\u5f31\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u7ed9\u6211\u7ad9\u4f4f\uff01\u201c\u91d1\u5175\u5df2\u7ecf\u4e00\u62e5\u800c\u4e0a\uff0c\u5c06\u90a3\u4eba\u56f4\u56f0\u5728\u5f53\u4e2d\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u56e0\u4f55\u963b\u8def\uff1f\u201d \u3000\u3000\u91d1\u5175\u961f\u957f\u6014\u4e86\u4e00\u6014\uff0c\u597d\u50cf\u89c9\u5f97\u6b64\u4eba\u8352\u8c2c\u4e4b\u6781\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u559d\u9053\uff1a\u201c\u4f60\u778e\u4e86\u773c\u5417\uff1f\u6211\u4eec\u662f \u5927\u91d1\u56fd\u7684\u5b98\u5175\uff01\u201d\u90a3\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u5b98\u5175\u53c8\u600e\u6837\uff1f\u8fd9\u5ea7\u5c71\u603b\u4e0d\u662f\u4f60\u4eec\u7684\u5427\uff1f\u4f60\u4eec\u8d70\u5f97\uff0c\u6211 \u4e3a\u4f55\u8d70\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u91d1\u5175\u961f\u957f\u5927\u6012\uff0c\u6b63\u8981\u4e0b\u4ee4\u62ff\u4ed6\uff1b\u5ffd\u5730\u53c8\u6709\u4e24\u4e2a\u519b\u5b98\u6109\u9a6c\u9a70\u6765\uff0c\u8fd9\u4e24\u4e2a\u519b\u5b98\u7684\u804c\u4f4d\u4f3c\u662f\u5728 \u4ed6\u4e4b\u4e0a\uff0c\u5176\u4e2d\u4e00\u4e2a\u53eb\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\uff01\u201d\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4f60\u9000\u4e0b\u53bb\uff0c\u5f85\u6211\u95ee\u4ed6\u3002\u201d\u8fd9\u519b\u5b98\u52d2\u4f4f \u9a6c\u5934\uff0c\u5411\u90a3\u866c\u9aef\u6c49\u5b50\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e00\u756a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6c49\u4eba\uff1f\u201d \u3000\u3000\u90a3\u866c\u9aef\u6c49\u5b50\u9053\uff1a\u201c\u662f\u6c49\u4eba\u53c8\u600e\u6837\uff1f\u4e0d\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u519b\u5b98\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u662f\u7518\u5fc5\u80dc\u90a3\u4e00\u4f19\u7684\u6c49\u4eba\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u866c\u9aef\u6c49\u5b50\u9053\uff1a\u201c\u7518\u5fc5\u80dc\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u519b\u5b98\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u662f\u88c5\u80e1\u6d82\u5427\uff1f\u4f60\u83ab\u591a\u7591\uff0c\u6211\u4eec\u662f\u5df2\u7ecf\u548c\u54c8\u5fc5\u56fe\u89c1\u8fc7\u9762\u7684\uff0c\u7518\u5fc5\u80dc \u662f\u5b8b\u56fd\u7684\u5927\u5185\u536b\u58eb\uff0c\u4ed6\u4e5f\u662f\u54c8\u5927\u4eba\u7684\u65b0\u4ea4\u3002\u201d \u3000\u3000\u866c\u9aef\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u91d1\u7684\u4ec0\u4e48\u5b98\u513f\u5df2\u7ecf\u505a\u4e86\u4e00\u4f19\u5417\uff1f\u6211\u662f\u666e\u901a\u767e\u59d3\uff0c\u4e0d\u8bba\u91d1\u56fd\u7684\u5b98 \u513f\u548c\u5b8b\u56fd\u7684\u5b98\u513f\uff0c\u6211\u90fd\u9ad8\u6500\u4e0d\u8d77\uff01\u201d \u3000\u3000\u4e24\u4e2a\u519b\u5b98\u9762\u8272\u767b\u65f6\u5927\u53d8\uff01 \u3000\u3000\u80d6\u7684\u90a3\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u662f\u7518\u5fc5\u80dc\u90a3\u4e00\u4f19\uff0c\u72ec\u81ea\u4e00\u4e2a\u4eba\u8dd1\u6765\u76d8\u9f99\u5c71\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u866c\u9aef\u6c49\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u60f3\u95ee\u4f60\u4eec\u5462\uff0c\u4f60\u4eec\u8fd9\u4e00\u5927\u5806\u4eba\u53c8\u8dd1\u6765\u76d8\u9f99\u5c71\u5e72\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u7626\u7684\u90a3\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u6df7\u5e10\u4e1c\u897f\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u6027\u547d\uff0c\u8981\u6027\u547d\u7684\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u662f\u4e0d\u662f\u6765\u627e \u6a80\u516c\u76f4\u7684\uff1f\u201d \u3000\u3000\u866c\u9aef\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5999\u6781\uff0c\u6211\u6b63\u6101\u6ca1\u5904\u6253\u542c\u6a80\u516c\u76f4\u7684\u6d88\u606f\uff0c\u4f60\u4eec\u5374\u51d1\u4e0a\u6765 \u4e86\uff01\u201d \u3000\u3000\u80d6\u7684\u90a3\u4e2a\u519b\u5b98\u6447\u4e00\u6447\u624b\uff0c\u793a\u610f\u53eb\u90e8\u4e0b\u4e0d\u53ef\u5984\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6253\u542c\u4ec0\u4e48\uff1f\u201d\u866c\u9aef\u6c49\u5b50 \u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u4f60\u4eec\u4e0d\u6765\u95ee\u6211\u3002\u6211\u4e5f\u8981\u95ee\u4f60\u4eec\u3002\u6211\u8981\u95ee\u4f60\u4eec\u8fd9\u73ed\u6df7\u5e10\u4e1c\u897f\uff0c\u5230\u5e95\u628a\u6a80\u516c\u76f4 \u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u7626\u7684\u90a3\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u5927\u80c6\u6df7\u86cb\uff0c\u4e71\u5200\u628a\u4ed6\u5bb0\u4e86\uff01\u201d \u3000\u3000\u80d6\u7684\u90a3\u4e2a\u519b\u5b98\u5374\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u522b\u5fd9\uff0c\u8c05\u4ed6\u5df2\u662f\u63d2\u7ffc\u96be\u9003\uff0c\u5f85\u6211\u95ee\u4ed6\uff0c\u4ed6\u82e5\u7136\u8fd8\u6562\u653e\u8086\uff0c \u518d\u6740\u4e0d\u8fdf\uff01\u201d\u56de\u8fc7\u5934\u6765\uff0c\u9634\u6d4b\u607b\u5730\u5bf9\u90a3\u866c\u9aef\u6c49\u5b50\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u7684\u80c6\u6c14\u6211\u5f88\u4f69\u670d\u3002\u4f46 \u4fd7\u8bed\u8bf4\u5f97\u597d\uff0c\u53cc\u62f3\u96be\u654c\u56db\u624b\uff0c\u7eb5\u7136\u4f60\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u4e5f\u53ea\u80fd\u767d\u9001\u4e00\u6761\u6027\u547d\u3002\u4e0d\u8fc7\uff0c\u770b\u5728\u4f60\u662f\u4e00 \u6761\u597d\u6c49\u7684\u4efd\u4e0a\u3002\u53ea\u8981\u4f60\u80af\u8bf4\u5b9e\u8bdd\uff0c\u6211\u5012\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u662f\u4e0d\u662f\u6a80\u516c\u76f4\u7ea6\u6765\u7684\uff1f\u4ed6 \u7684\u5bb6\u4eba\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u866c\u9aef\u6c49\u5b50\u559d\u9053\uff1a\u201c\u4f60\u542c\u7740\uff0c\u8001\u5b50\u5e73\u751f\u4ece\u6765\u4e0d\u60ef\u53d7\u4eba\u52a9\u95ee\uff0c\u5982\u4eca\u662f\u6211\u76d8\u95ee\u4f60\u4eec\uff0c\u4f60\u61c2\u4e0d \u61c2\uff1f\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u6a80\u516c\u76f4\u662f\u7ed9\u4f60\u4eec\u5bb3\u4e86\u3002\u8fd8\u662f\u5df2\u7ecf\u7ed9\u4f60\u4eec\u62bc\u4e0a\u4eac\u5e08\uff1f\u54fc\uff0c\u4f60\u4eec\u82e5\u662f\u4e0d\u80fd\u5c06\u6a80\u516c \u76f4\u4ea4\u51fa\u6765\uff0c\u6211\u53eb\u4f60\u4eec\u4e00\u4e2a\u4e2a\u90fd\u6d3b\u4e0d\u4e86\uff01 \u3000\u3000\u201d \u3000\u3000\u90a3\u4e2a\u5c0f\u961f\u957f\u63a5\u637a\u4e0d\u4f4f\uff0c\u9996\u5148\u51b2\u4e0a\u524d\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6df7\u5e10\u4e1c\u897f\uff0c\u4e14\u770b\u662f\u8c01\u4e0d\u80fd\u6d3b\u4e86\u2014\u2014\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e53\u7684\u4e00\u58f0\uff0c\u90a3\u5c0f\u961f\u957f\u5df2\u662f\u7ed9\u866c\u9aef\u6c49\u5b50\u6293\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u6454\u4e86 \u51fa\u53bb\u3002 \u3000\u3000\u201c\u5f53\u7136\u662f\u4f60\u4e0d\u80fd\u6d3b\u547d\uff01\u201d\u866c\u9aef\u6c49\u5b50\u559d\u9053\uff0c\u90a3\u5c0f\u961f\u957f\u7ed9\u4ed6\u731b\u529b\u6454\u51fa\u53bb\uff0c\u649e\u5230\u4e86\u4e24\u540d\u5b98\u5175\uff0c \u90a3\u4e24\u540d\u5b98\u5175\u767b\u65f6\u4e5f\u9aa8\u788c\u788c\u5730\u6eda\u4e0b\u5c71\u5761\uff0c\u77ed\u4fc3\u7684\u60e8\u53eb\u58f0\u4e00\u53d1\u5373\u6b62\uff0c\u663e\u7136\u662f\u90fd\u5df2\u6c14\u7edd\u800c\u4ea1\u4e86\uff01 \u3000\u3000\u8679\u9aef\u6c49\u5b50\u98de\u8eab\u8dc3\u8d77\uff0c\u4e52\u4e53\u4e24\u58f0\uff0c\u53c8\u8e22\u7ffb\u4e86\u4e24\u540d\u5b98\u5175\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u671d\u7740\u90a3\u4e2a\u9a91 \u5728\u9a6c\u4e0a\u7684\u80d6\u519b\u5b98\u6251\u4e0b\u3002 \u3000\u3000\u90a3\u4e2a\u80d6\u519b\u5b98\u8eab\u6750\u867d\u7136\u80a5\u80d6\uff0c\u8eab\u624b\u5012\u5f88\u7075\u6d3b\uff0c\u4e00\u4e2a\u8e6c\u91cc\u85cf\u8eab\uff0c\u5b9d\u5200\u5df2\u662f\u51fa\u9798\uff0c\u4e00\u62db\u201c\u659c\u5207 \u85d5\u201d\u65a9\u90a3\u6c49\u5b50\u624b\u81c2\u3002 \u3000\u3000\u866c\u9aef\u6c49\u5b50\u8eab\u5b50\u60ac\u7a7a\uff0c\u773c\u770b\u8fd9\u4e00\u5200\u5c31\u8981\u5c06\u4ed6\u7684\u4e00\u6761\u624b\u81c2\u5378\u4e0b\uff0c\u53ea\u542c\u5f97\u4ed6\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u4e0d \u77e5\u600e\u7684\uff0c\u5374\u662f\u90a3\u4e2a\u80d6\u519b\u5b98\u8dcc\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u80d6\u519b\u5b98\u5760\u9a6c\u3002\u90a3\u5339\u9a6c\u53d7\u60ca\uff0c\u5411\u524d\u4e00\u51b2\uff0c\u866c\u9aef\u6c49\u5b50\u4e5f\u672a\u80fd\u591f\u843d\u5728\u9a6c\u978d\uff0c\u8ddf\u7740\u6251\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7626\u7684\u90a3\u4e2a\u519b\u5b98\u6296\u8d77\u4e00\u6839\u957f\u77db\u5df2\u662f\u4ece\u9a6c\u4e0a\u671d\u7740\u4ed6\u731b\u523a\u3002 \u3000\u3000\u866c\u9aef\u6c49\u5b50\u8eab\u5f62\u4e00\u95ea\uff0c\u907f\u8fc7\u77db\u5173\uff0c\u4e00\u6293\u6293\u7740\u77db\u6746\uff0c\u9661\u5730\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u7626\u519b\u5b98\u4e5f\u7ed9\u4ed6\u62d6\u5f97 \u6eda\u4e0b\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u5b98\u5175\u5927\u60ca\uff0c\u56db\u9762\u516b\u65b9\u56f4\u4e0a\uff0c\u866c\u9aef\u6c49\u5b50\u62a2\u4e86\u80d6\u519b\u5b98\u90a3\u628a\u5b9d\u5200\uff0c\u201c\u94ee\u201d\u4e00\u7684\u4e00\u5f39\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5200\uff0c\u6b63\u5408\u6211\u7528\uff01\u201d\u5b9d\u5200\u6325\u51fa\uff0c\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u9707\u8033\u6b32\u804b\uff0c\u4e24\u67c4\u94a2\u5200\uff0c\u4e00\u6746\u82b1\u67aa \u5168\u90fd\u7ed9\u4ed6\u8fd9\u67c4\u5b9d\u5200\u524a\u65ad\u3002 \u3000\u3000\u4ed6\u5200\u780d\u638c\u5288\uff0c\u9ad8\u547c\u9163\u6597\uff0c\u8fc5\u731b\u6709\u5982\u6012\u72ee\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4ece\u9ad8\u903e\u4eba\u5934\u7684\u8305\u7ae0\u4e1b\u4e2d\u770b\u51fa\u53bb\u3002\u53ea\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u90a3\u866c\u9aef\u6c49\u5b50\u7684\u5f71\u5b50\uff0c\u5200\u5149\u4fe8\u82e5 \u94f6\u8679\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u770b\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u5200\u5149\u6eda\u6eda\uff0c\u8fde\u4ed6\u7684\u5f71\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u56f4\u653b\u4ed6\u7684\uff0c \u5c3d\u7ba1\u6709\u4e8c\u4e09\u5341\u4eba\uff0c\u5200\u5149\u6240\u5230\u4e4b\u5904\uff0c\u5374\u662f\u5982\u6c64\u6cfc\u96ea\uff0c\u6321\u8005\u8f9f\u6613\uff01 \u3000\u3000\u76ee\u7779\u8fd9\u6837\u60e8\u70c8\u7684\u53ae\u6740\uff0c\u83ab\u8bf4\u90a3\u4e9b\u548c\u4ed6\u640f\u6597\u7684\u5b98\u5175\uff0c\u8eb2\u5728\u8349\u4e1b\u4e2d\u5077\u770b\u7684\u5f20\u96ea\u6ce2\u4ea6\u662f\u4e3a\u4e4b\u5fc3 \u60b8\u3002\u53ea\u542c\u5f97\u6495\u5fc3\u88c2\u80ba\u7684\u60e8\u53eb\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u4e0d\u7edd\u4e8e\u8033\u3001\u56f4\u653b\u4ed6\u7684\u5b98\u5175\u5012\u4e86\u4e00\u4e2a\u53c8\u4e00\u4e2a\uff0c\u6700\u540e\u53ea \u5269\u4e0b\u90a3\u4e24\u540d\u519b\u5b98\u4e86\u3002\u90a3\u4e2a\u80d6\u519b\u5b98\u89c1\u52bf\u4e0d\u5999\uff0c\u8f6c\u8eab\u4fbf\u9003\uff0c\u866c\u9aef\u6c49\u5b50\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8dd1\uff1f\u4f60\u7684\u5b9d \u5200\uff0c\u8bf7\u4f60\u53d7\u7528\uff01\u201d\u624b\u8d77\u5200\u843d\uff0c\u628a\u90a3\u80d6\u519b\u5b98\u5288\u4e3a\u4e24\u534a\u3002 \u3000\u3000\u7626\u519b\u5b98\u5413\u5f97\u53cc\u817f\u8f6f\u4e86\uff0c\u535c\u5730\u8dea\u5012\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u662f\u8036\u5f8b\u2026\u201d\u866c\u9aef\u6c49\u5b50\u559d\u9053\uff1a\u201c\u60f3\u6c42\u9976 \u5417\uff1f\u201d\u90a3\u7626\u519b\u5b98\u5782\u4e0b\u5934\u762b\u4f5c\u4e00\u56e2\uff0c\u5374\u5df2\u53d1\u4e0d\u51fa\u58f0\u97f3\u3002\u539f\u6765\u7adf\u662f\u7ed9\u4ed6\u5413\u6b7b\u4e86\u3002 \u3000\u3000\u8679\u9aef\u6c49\u5b50\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u8bf4\u8fc7\u8981\u4f60\u4eec\u4e00\u4e2a\u90fd\u4e0d\u80fd\u6d3b\u547d\u7684\uff0c\u6211\u4ece\u6765\u8a00\u51fa\u5fc5\u884c\uff0c\u5982 \u4eca\u4f60\u4eec\u8be5\u76f8\u4fe1\u4e86\u5427\uff1f\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u4ed6\u5df2\u62a2\u4e86\u4e00\u5339\u5750\u9a91\uff0c\u7edd\u5c18\u800c\u53bb\u4e86\uff01 \u3000\u3000\u4ef0\u5929\u957f\u5578\uff0c\u58ee\u6000\u6fc0\u70c8\u3002\u8fd9\u866c\u9aef\u6c49\u5b50\u5c3d\u6b7c\u91d1\u5175\u5927\u7b11\u800c\u53bb\uff0c\u548c\u5cb3\u5c11\u4fdd\u5f53\u5e74\u5728\u6731\u4ed9\u9547\u5927\u6377\u4e4b\u540e \u4ef0\u5929\u957f\u5578\u7684\u8c6a\u60c5\u5c82\u4e0d\u6b63\u662f\u76f8\u540c\uff1f\u5feb\u610f\u6069\u4ec7\uff0c\u4eba\u751f\u80fd\u5f97\u51e0\u56de\u6709\uff1f\u4ed6\u53d1\u6cc4\u4e86\u5fc3\u5934\u7684\u60b2\u6124\uff0c\u4e5f\u6292\u53d1 \u4e86\u75db\u5feb\u7684\u5fc3\u60c5\u3002\u4eba\u5df2\u7edd\u5c18\u800c\u53bb\uff0c\u7b11\u58f0\u5c1a\u5728\u5c71\u8c37\u56de\u65cb\uff0c\u597d\u50cf\u662f\u8981\u5f20\u96ea\u6ce2\u5206\u4eab\u4ed6\u7684\u75db\u5feb\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u50cf\u662f\u5728\u6076\u68a6\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u4f46\u5979\u7684\u5fc3\u5934\u5374\u662f\u5982\u9644\u94c5\u5757\uff0c\u60f3\u7b11\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u201c\u4f60\uff0c\u4f60\u662f\u8036\u5f8b\u2026\u2026\u201d\u8fd9\u662f\u88ab\u866c\u9aef\u6c49\u5b50\u5413\u6b7b\u7684\u90a3\u4e2a\u519b\u5b98\u6700\u540e\u53eb\u51fa\u6765\u7684\uff0c\u4e00\u53e5\u5c1a\u672a\u8bf4\u5f97\u5b8c \u5168\u7684\u8bdd\u3002\u5f20\u96ea\u6ce2\u6e05\u9192\u8fc7\u6765\uff0c\u9996\u5148\u60f3\u5230\u7684\u4e5f\u5c31\u662f\u8fd9\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u201c\u554a\uff0c\u539f\u6765\u4ed6\u5c31\u662f\u51b2\u513f\u7684\u5e08\u7236\uff0c\u662f\u516c\u516c\u8981\u6211\u4eec\u53bb\u5bfb\u627e\u7684\u90a3\u4e2a\u4eba\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5979\u7684\u5b69\u5b50\u4ea6\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u8fd9\u4e2a\u4eba\u662f\u7237\u7237\u7684\u670b\u53cb\uff0c\u4ed6\u662f\u4e3a\u4e86\u66ff \u7237\u7237\u62a5\u4ec7\uff0c\u628a\u8fd9\u4e9b\u5f3a\u76d7\u90fd\u6740\u5149\u7684\uff01\u54c8\uff0c\u4ed6\u4e00\u5b9a\u662f\u7237\u7237\u66ff\u6211\u627e\u7684\u90a3\u4e2a\u5e08\u7236\uff0c\u6211\u6709\u8fd9\u4e2a\u5e08\u7236\uff0c\u771f \u597d\uff0c\u771f\u597d\uff01\u201d \u3000\u3000\u201c\u6211\u771f\u80e1\u6d82\uff0c\u5b69\u5b50\u90fd\u60f3\u5f97\u5230\u7684\u4e8b\u60c5\uff0c\u6211\u5374\u5931\u4e4b\u4ea4\u81c2\uff01\u201d\u5f20\u96ea\u6ce2\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u5df2\u7ecf \u8d70\u4e86\u3002\u90fd\u662f\u5988\u5988\u4e0d\u597d\uff0c\u9519\u8fc7\u4e86\u8fd9\u6b21\u673a\u4f1a\u3002\u201d\u5176\u5b9e\u8fd9\u53c8\u600e\u80fd\u602a\u5979\uff0c\u5728\u521a\u624d\u90a3\u6837\u9a87\u4eba\u5fc3\u9b44\u7684\u9ad8\u547c \u9163\u6597\u4e4b\u4e2d\uff0c\u5979\u53c8\u600e\u6562\u51fa\u58f0\u547c\u5524\u3002\u83ab\u8bf4\u521a\u624d\uff0c\u5982\u4eca\u5979\u5140\u662f\u60ca\u9b42\u672a\u5b9a\u3002\u51b2\u513f\u53cd\u800c\u5b89\u6170\u5979\u9053\uff1a\u201c\u5988 \u5988\uff0c\u4e0d\u8981\u7d27\u7684\u3002\u54b1\u4eec\u627e\u4e0d\u5230\u5e08\u7236\uff0c\u5e08\u7236\u4e5f\u4f1a\u6765\u627e\u54b1\u4eec\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u7fbd\u51b2\u9053\uff1a\u201c\u7237\u7237\u4e0d\u662f\u8bf4\u8fc7\uff0c\u8981\u4eb2\u81ea\u9001\u6211\u53bb\u62dc\u5e08\u7684\u4e48\uff1f\u4f46\u5e08\u7236\u4e0d\u5f85\u7237\u7237\u628a\u6211\u9001\u5230\u4ed6\u90a3\u91cc\uff0c \u4ed6\u5c31\u56de\u6765\u627e\u7237\u7237\u4e86\u3002\u6211\u60f3\uff0c\u4e00\u5b9a\u662f\u4ed6\u5df2\u77e5\u9053\u6709\u574f\u4eba\u8981\u6765\u5bb3\u7237\u7237\uff0c\u4ed6\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8fd9\u624d\u8dd1\u56de\u6765 \u7684\u3002\u4ed6\u4e0d\u6015\u5371\u9669\u4e5f\u8981\u6765\u627e\u7237\u7237\uff0c\u4ed6\u7b54\u5e94\u4e86\u7684\u4e8b\u60c5\u53c8\u600e\u80fd\u4e0d\u505a\uff1f\u6211\u60f3\uff0c\u4ed6\u8981\u627e\u54b1\u4eec\uff0c\u53ef\u80fd\u6bd4\u54b1 \u4eec\u8981\u627e\u4ed6\u8fd8\u66f4\u5fc3\u6025\uff01 \u3000\u3000\u201d\u5f20\u96ea\u6ce2\u5446\u4f4f\u4e86\uff0c\u5b69\u5b50\u4e0d\u8fc7\u4e03\u5c81\uff0c\u5728\u5979\u7684\u5fc3\u76ee\u4e2d\u662f\u4e2a\u4ec0\u4e48\u4e5f\u4e0d\u61c2\u7684\u5b69\u5b50\uff0c\u5982\u4eca\u5979\u624d\u53d1 \u73b0\uff0c\u5979\u4ee5\u4e3a\u4ec0\u4e48\u4e5f\u4e0d\u61c2\u7684\u5b69\u5b50\u7adf\u7136\u8fd9\u6837\u806a\u660e\uff0c\u751a\u81f3\u6bd4\u5979\u8fd8\u8981\u806a\u660e\u3002\u4ed6\u7adf\u7136\u61c2\u5f97\u4f9d\u7406\u63a8\u6d4b\uff0c\u800c \u4e14\u8bf4\u5f97\u6761\u7406\u5206\u660e\u3002 \u3000\u3000\u5915\u9633\u5df2\u7ecf\u843d\u5c71\u4e86\uff0c\u5929\u8fb9\u665a\u971e\u5982\u8840\uff0c\u8840\u8165\u7684\u6c14\u5473\u4ece\u90a3\u8fb9\u7684\u5c71\u5761\u968f\u98ce\u5439\u6765\u3002 \u3000\u3000\u201c\u5988\u5988\uff0c\u5929\u8272\u5df2\u665a\uff0c\u4eca\u5929\u6050\u6015\u4e0d\u80fd\u4e0b\u5c71\u4e86\u3002\u54b1\u4eec\u5230\u90a3\u8fb9\u7684\u5c71\u5761\u8fc7\u4e00\u665a\u597d\u4e0d\u597d\uff12\u201d\u5b69\u5b50\u8bf4 \u9053\u3002\u4ed6\u4eec\u6240\u5728\u7684\u8fd9\u9762\u5c71\u5761\u6ee1\u662f\u8346\u68d8\uff0c\u90a3\u8fb9\u7684\u5c71\u5761\u5219\u662f\u6bd4\u8f83\u5e73\u5766\u7684\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u90a3\u5806\u6b7b\u5c38\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff0c\u4ed6\u4eec\u90fd\u5df2\u7ed9\u5e08\u7236\u6740\u4e86\u3002\u201d \u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8840\u8165\u6c14\u5473\u4e5f\u662f\u96be\u95fb\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54b1\u4eec\u53c8\u4e0d\u662f\u7761\u5728\u5c38\u9996\u5806\u4e2d\uff0c\u79bb\u8fdc\u4e00\u4e9b\u4e5f\u5c31 \u884c\u4e86\u3002\u603b\u6bd4\u7761\u5728\u8346\u68d8\u4e1b\u4e2d\u597d\u3002\u201d\u5f20\u96ea\u6ce2\u62d7\u4e0d\u8fc7\u4ed6\uff0c\u53ea\u597d\u7b54\u5141\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u5230\u4e0a\u98ce\u5904 \u627e\u4e2a\u5e72\u51c0\u7684\u5730\u65b9\u8fc7\u591c\uff0c\u4f46\u90a3\u4e9b\u5c38\u9996\u7684\u5f62\u72b6\u4e00\u5b9a\u5f88\u53ef\u6015\uff0c\u4f60\u6700\u597d\u95ed\u4e0a\u773c\u775b\u3002\u201d\u5979\u54ea\u77e5\u9053\u5b69\u5b50\u7684 \u597d\u5947\u5fc3\u7406\u3002\u4ed6\u6b63\u662f\u8981\u53bb\u770b\u4ed6\u5e08\u7236\u7684\u82f1\u96c4\u4e1a\u7ee9\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c\u6628\u5929\u4f60\u4e0d\u662f\u4e5f\u66fe\u6740\u8fc7\u4eba \u4e48\uff0c\u600e\u7684\u5ffd\u7136\u80c6\u5b50\u5c0f\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6740\u4eba\u662f\u8feb\u4e0d\u5f97\u5df1\u7684\u4e8b\uff0c\u4f60\u957f\u5927\u4e86\u53ea\u53ef\u4ee5\u6740\u6b3a\u8d1f\u4f60\u7684\u6076\u4eba\uff0c\u7edd\u4e0d\u53ef\u968f \u4fbf\u6740\u4eba\u3002\u4e00\u4e2a\u4eba\u603b\u5e94\u8be5\u6709\u6148\u60b2\u4e4b\u5fc3\u7684\uff0c\u4f60\u61c2\u5417\uff1f\u201d\u6a80\u7fbd\u51b2\u4f38\u4f38\u820c\u5934\uff0c\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u8bf4\u9053\uff1a \u201c\u7237\u7237\u65e9\u5df2\u6559\u8fc7\u6211\u4e86\uff0c\u4f46\u7237\u7237\u4e5f\u6559\u6211\u5148\u8981\u5b66\u4f1a\u6740\u4eba\u7684\u672c\u9886\u624d\u4e0d\u6015\u6076\u4eba\u6b3a\u8d1f\uff0c\u73b0\u5728\u6211\u672a\u5b66\u4f1a\u6740 \u4eba\u7684\u672c\u9886\u5462\u3002\u5988\u5988\uff0c\u4f60\u5c31\u8ba9\u6211\u5148\u5b66\u597d\u4e86\u672c\u9886\u518d\u6559\u8bad\u6211\u5427\u3002\u201d\u5f20\u96ea\u6ce2\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4 \u7684\u662f\u505a\u4eba\u7684\u5927\u9053\u7406\uff0c\u5509\uff0c\u4f60\u8fd9\u5b69\u5b50\u5c31\u7231\u548c\u5988\u5988\u9a73\u5634\u3002\u201d\u6a80\u7fbd\u51b2\u5ffd\u9053\uff1a\u201c\u5077\u4e1c\u897f\u662f\u4e0d\u597d\u7684\uff0c\u6211 \u77e5\u9053\u3002\u4f46\u574f\u4eba\u7684\u4e1c\u897f\u53ef\u4e0d\u53ef\u4ee5\u62ff\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5f81\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u6837\u95ee\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7239\u7239\u53ea\u7559\u4e0b\u4e00\u67c4\u5315\u9996\u7ed9\u6211\uff0c\u5988\uff0c\u4f60\u90fd\u8fd8\u6ca1\u6709\u5175\u5668\u5462\u3002\u54b1\u4eec\u53ef\u4e0d\u53ef\u4ee5\u68c0\u4e00\u628a \u5200\u6216\u5251\u7559\u4e3a\u5df1\u7528\uff1f\u201d\u53cd\u6b63\u8fd9\u4e9b\u6492\u4e86\u6ee1\u5730\u7684\u5200\u5251\u672c\u6765\u5c31\u662f\u90a3\u73ed\u574f\u4eba\u8981\u7528\u6765\u6740\u54b1\u4eec\u7684\uff0c\u54b1\u4eec\u62ff\u4e86 \u53bb\u5c06\u6765\u6740\u574f\u4eba\uff0c\u60f3\u5fc5\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\u5427\uff1f\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e0d\u597d\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d \u597d\uff1f\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u62ff\u574f\u4eba\u7684\u5200\u5251\u6765\u6740\u574f\u4eba\u672c\u6765\u662f\u53ef\u4ee5\u7684\uff0c\u4f46\u5374\u8981\u770b\u60c5\u5f62\u800c\u5b9a\u3002\u54b1\u4eec\u73b0\u5728\u662f \u9003\u96be\uff0c\u4f60\u662f\u4e00\u4e2a\u5b69\u5b50\uff0c\u8981\u662f\u85cf\u4e86\u5927\u4eba\u7684\u5200\u5251\uff0c\u5f88\u5bb9\u6613\u7ed9\u4eba\u770b\u5f97\u51fa\u6765\u3002\u4e0d\u4f46\u662f\u4f60\uff0c\u6211\u8eab\u4e0a\u85cf\u4e86 \u5200\u5251\uff0c\u7ed9\u4eba\u770b\u51fa\uff0c\u4e5f\u4f1a\u60f9\u7978\u6b83\u7684\u3002\u62db\u60f9\u707e\u7978\uff0c\u90a3\u5f53\u7136\u662f\u4e0d\u597d\u4e86\u3002\u5509\uff0c\u51b2\u513f\uff0c\u4f60\u5e74\u7eaa\u5c0f\uff0c\u4f60\u8fd8 \u4e0d\u61c2\u5f97\u4ec0\u4e48\u53eb\u505a\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u5f85\u5988\u5988\u6162\u6162\u548c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u5979\u7528\u5b69\u5b50\u542c\u5f97\u61c2\u7684\u8bed\u8a00\u53cd\u590d\u7533\u8ff0\u201c\u5fcd\u8fb1\u8d1f\u91cd\u201d\u7684\u610f\u4e49\uff0c\u4e0d\u8fc7\u6a80\u7fbd\u51b2\u867d\u7136\u65e9\u719f\uff0c\u5374\u8fd8\u662f\u542c \u5f97\u4f3c\u61c2\u975e\u61c2\u3002\u4ed6\u53ea\u80fd\u8bf4\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u53ea\u987b\u544a\u8bc9\u6211\u6740\u574f\u4eba\u662f\u53ef\u4ee5\u7684\u90a3\u5c31\u5f97\u4e86\uff0c\u6211\u5f53\u7136\u4e5f\u4e0d\u4f1a \u628a\u6740\u4eba\u5f53\u4f5c\u73a9\u800d\u7684\u3002\u201d \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u5df1\u662f\u8d70\u5230\u4e86\u5bf9\u9762\u5c71\u5761\uff0c\u90a3\u60e8\u9177\u7684\u573a\u9762\u679c\u7136\u662f\u76ee\u4e0d\u5fcd\u7779\uff0c\u5f20\u96ea\u6ce2\u82e6\u7b11\u4e00\u58f0\uff0c\u4e5f\u5c31 \u4e0d\u518d\u548c\u5b69\u5b50\u8bf4\u4e86\u3002\u5979\u6b63\u60f3\u7ed5\u9053\u800c\u884c\uff0c\u5ffd\u5730\u9690\u9690\u542c\u5f97\u4e00\u58f0\u547b\u541f\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5403\u4e86\u4e00\u60ca\uff0c\u505c\u4e0b\u811a\u6b65\u3002\u4fa7\u8033\u503e\u542c\u3001\u547b\u541f\u58f0\u65ad\u65ad\u7eed\u7eed\u542c\u5f97\u66f4\u6e05\u695a\u4e86\u3002 \u3000\u3000\u5979\u5927\u7740\u80c6\u5b50\u8d70\u5230\u5c38\u9996\u5806\u4e2d\u4e00\u627e\uff0c\u679c\u7136\u53d1\u73b0\u4e86\u4e00\u4e2a\u6d3b\u4eba\u3002\u8fd9\u4eba\u770b\u6765\u53ea\u662f\u53d7\u4e86\u8f7b\u4f24\uff0c\u8eba\u4e0b\u6765 \u88c5\u6b7b\u7684\u3002\u4ed6\u770b\u89c1\u5f20\u96ea\u6ce2\u6765\u5230\u4ed6\u7684\u9762\u524d\uff0c\u7adf\u7136\u5750\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u4f24\u5f97\u4e0d\u7b97\u91cd\uff0c\u4f46\u4f53\u529b\u5374\u6062\u590d\uff0c\u4e3a\u4e86\u9a97\u53d6\u5f20\u96ea\u6ce2\u7684\u540c\u60c5\uff0c\u4ecd\u7136\u88c5\u4f5c\u662f\u53d7\u4e86\u91cd \u4f24\u7684\u6837\u5b50\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5413\u4e86\u4e00\u8df3\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8fd8\u6ca1\u6b7b\uff1f\u201d\u8fd9\u53e5\u8bdd\u5176\u5b9e\u95ee\u5f97\u6781\u5176\u53ef\u7b11\uff0c \u6b7b\u4eba\u53c8\u600e\u80fd\u591f\u8bf4\u8bdd\uff1f\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u6551\uff0c\u6551\u547d\uff01\u6211\uff0c\u6211\u6e34\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5b9a\u4e86\u5b9a\u795e\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u522b\u614c\uff0c\u4f60\u4e0d\u4f1a\u6b7b\u7684\uff0c\u6211\u7ed9\u4f60\u6c34\u559d\u3002\u201d\u5979\u79bb\u5bb6 \u7684\u65f6\u5019\uff0c\u662f\u51c6\u5907\u6709\u53ef\u4f9b\u4e24\u65e5\u4e4b\u7528\u7684\u5e72\u7cae\u7684\u98df\u6c34\u7684\uff0c\u5f53\u4e0b\u6253\u5f00\u90a3\u76db\u6ee1\u98df\u6c34\u7684\u846b\u82a6\uff0c\u53eb\u90a3\u4eba\u5f20\u5f00 \u53e3\u628a\u6c34\u5012\u5165\u4ed6\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c\u4ed6\u4e0d\u662f\u574f\u4eba\u5417\uff1f\u4f60\u4e3a\u4ec0\u4e48\u8981\u6551\u574f\u4eba\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\u3002\u4ed6\u4e0d\u8fc7\u662f\u4e00\u4e2a\u666e\u901a\u7684\u58eb\u5175\uff0c\u7f6a\u4e0d\u81f3\u6b7b\uff0c\u800c\u4e14\u4ed6 \u53c8\u53d7\u4e86\u91cd\u4f24\uff0c\u4e0d\u4f1a\u4f24\u5bb3\u54b1\u4eec\u4e86\u3002\u6240\u4ee5\u7eb5\u7136\u4ed6\u662f\u574f\u4eba\uff0c\u54b1\u4eec\u4e5f\u5e94\u8be5\u6551\u4ed6\u3002\u201d \u3000\u3000\u90a3\u4eba\u559d\u4e86\u5c0f\u534a\u846b\u82a6\u7684\u6c34\uff0c\u4f53\u529b\u6062\u590d\u51e0\u5206\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u8bf4\u9053\uff1a\u201c\u5a18\u5b50\uff0c\u591a\u8c22\u4f60\uff0c\u4f60\u771f\u662f\u4e00 \u4e2a\u5927\u6148\u5927\u60b2\u7684\u5973\u83e9\u8428\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u89c1\u4ed6\u6ee1\u8eab\u8840\u6c61\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u601c\uff0c\u53ef\u601c\uff0c\u5f85\u6211\u77a7\u77a7\uff0c\u4f60\u4f24\u5728\u54ea\u91cc\uff0c\u6211\u7ed9\u4f60\u6577\u4e0a\u91d1\u521b \u836f\u3002\u201d \u3000\u3000\u90a3\u4eba\u8272\u5fc3\u987f\u8d77\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c\u8fd9\u6f02\u4eae\u7684\u5a18\u513f\u60f3\u5fc5\u662f\u54ea\u5bb6\u730e\u6237\u4eba\u5bb6\u7684\u5c0f\u5ab3\u5987 \u513f\uff0c\u96be\u5f97\u5979\u968f\u8eab\u8fd8\u5e26\u6709\u91d1\u521b\u836f\uff0c\u8fd9\u56de\u6211\u53ef\u771f\u662f\u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d\u4ed6\u53d7\u7684\u53ea\u662f\u8f7b\u4f24\uff0c\u4e0d\u60f3\u7ed9\u5f20\u96ea \u6ce2\u53d1\u73b0\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u5201\uff0c\u6263\u7740\u4e86\u5f20\u96ea\u6ce2\u7684\u8109\u95e8\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4eba\u7adf\u4f1a\u6069\u5c06\u4ec7\u62a5\u3002\u8109\u95e8\u88ab\u4ed6\u6263\u4f4f\uff0c\u534a\u8fb9\u8eab\u5b50\u9165\u9ebb\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u5931 \u58f0\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u52b3\u70e6\u4f60\u4e86\uff0c\u836f\uff0c\u6211\u4f1a\u81ea\u5df1\u6577\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u662f\u836f\u4e5f\u8981\uff0c\u4eba\u4e5f\u8981\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6c14\u5f97\u5927\u9a82\uff1a\u201c\u4f60\u8fd9\u755c\u7272\uff01\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u6807\u81f4\u7684\u59d0\u513f\uff0c\u6211\u662f\u8981\u5b9a\u4f60\u4e86\u3002\u4f60\u8ddf\u6211\u4e0d\u4f1a\u5403\u4e8f\u7684\u3002\u6765\uff0c\u6765\uff0c\u6765\uff01\u54b1 \u4eec\u5148\u6765\u4eb2\u4e2a\u5634\u513f\uff01\u201d\u6a80\u7fbd\u51b2\u559d\u9053\uff1a\u201c\u72d7\u4e1c\u897f\uff0c\u4f60\u6562\u6b3a\u4fae\u6211\u7684\u5988\u5988\uff01\u201d\u62d4\u51fa\u5315\u9996\uff0c\u6251\u4e0a\u53bb\u523a\u90a3 \u91d1\u5175\u3002\u4ed6\u6251\u4e0a\u53bb\u4e00\u5200\u523a\u7740\u90a3\u91d1\u5175\u7684\u5c0f\u817f\uff0c\u523a\u662f\u523a\u7740\u4e86\uff0c\u53ef\u60dc\u4ed6\u4e0d\u8fc7\u662f\u4e2a\u4e03\u5c81\u7684\u5c0f\u5b69\uff0c\u80fd\u6709\u591a \u5927\u6c14\u529b\uff0c\u90a3\u91d1\u5175\u5168\u4ed6\u7684\u5315\u9996\u5212\u4f24\u4e86\u4e00\u70b9\u76ae\u8089\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u8e22\u6b7b\u4f60\u8fd9\u5c0f\u6742\u79cd\uff01\u201d\u4e00\u4e2a\u201c\u864e\u5c3e \u811a\u201d\u5012\u8e6c\u8e22\u51fa\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u628a\u6a80\u7fbd\u51b2\u7684\u5315\u9996\u8e22\u98de\uff0c\u5e78\u800c\u690d\u7fbd\u51b2\u8fd8\u7b97\u7075\u6d3b\uff0c\u8eab\u4f53\u6ca1\u6709\u7ed9\u4ed6 \u8e22\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u867d\u8bf4\u53ea\u662f\u4f24\u4e86\u4e00\u70b9\u76ae\u8089\uff0c\u75bc\u75db\u7684\u611f\u89c9\u8fd8\u662f\u6709\u7684\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u90a3\u4e2a\u88ab\u523a\u4e86\u4e00\u5200\u7684\u91d1\u5175\uff0c\u4ed6 \u7684\u4e00\u53ea\u624b\u672c\u6765\u662f\u6293\u7740\u5f20\u96ea\u6ce2\u8109\u95e8\u7684\uff0c\u4e00\u75db\u4e4b\u4e0b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e5f\u5c31\u7a0d\u5fae\u677e\u4e86\u4e00\u4e9b\uff0c\u6293\u5f97\u6ca1\u90a3\u4e48\u7262 \u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u6bd5\u7adf\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u5973\u5b50\uff0c\u521a\u624d\u4e0d\u8fc7\u662f\u6beb\u65e0\u9632\u5907\uff0c\u8fd9\u624d\u53d7\u5bf9\u65b9\u6240\u5236\u800c\u5df2\u3002\u6b64\u65f6\u5979\u60c5 \u6025\u62fc\u547d\uff0c\u4e00\u89c9\u6709\u673a\u53ef\u4e58\uff0c\u6b66\u529f\u81ea\u7136\u800c\u7136\u7684\u5c31\u767b\u8098\u65bd\u5c55\u51fa\u6765\u4e86\u3002\u5979\u6a2a\u8098\u4e00\u649e\uff0c\u6323\u8131\u4e86\u9b54\u722a\u3002 \u3000\u3000\u8fd9\u91d1\u5175\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u53ea\u9053\u5979\u4e0d\u8fc7\u662f\u6709\u51e0\u5206\u6c14\u529b\u7684\u5973\u730e\u4eba\uff0c\u7ed9\u5979\u6323\u8131\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u201c\u8d3c\u5a46 \u5a18\uff0c\u80c6\u6562\u884c\u51f6\uff01\u6211\u770b\u5f97\u8d77\u4f60\u624d\u8981\u4f60\u505a\u5c0f\u8001\u5a46\uff0c\u4f60\u82e5\u4e0d\u8bc6\u59cb\u4e3e\uff0c\u6211\u628a\u4f60\u4eec\u4e24\u6bcd\u5b50\u5168\u90fd\u6740\u4e86\uff0c\u770b \u4f60\u5982\u4f55\u9003\u5f97\u51fa\u6211\u638c\u5fc3\uff01\u201d\u53e3\u4e2d\u7c97\u8a00\u79fd\u8bed\u5927\u9a82\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u6251\u4e0a\u6765\u53c8\u8981\u6293\u5f20\u96ea\u6ce2\u3002 \u3000\u3000\u90a3\u67c4\u5315\u9996\u534a\u7a7a\u843d\u4e0b\uff0c\u5f20\u96ea\u6ce2\u62a2\u5148\u4e00\u6b65\u63a5\u4e86\u4e0b\u6765\uff0c\u9a82\u9053\uff1a\u201c\u755c\u7272\uff01\u201c\u90a3\u91d1\u5175\u4e00\u6734\u88ab\u5979\u95ea \u8fc7\u3002\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u90a3\u628a\u5315\u9996\u5df1\u662f\u523a\u5165\u4e86\u4ed6\u7684\u54bd\u5589\u3002\u5f20\u96ea\u6ce2\u677e\u4e86\u53e3\u6c14\uff0c\u62e8\u51fa\u5315\u9996\uff0c\u53eb\u9053\uff1a \u201c\u51b2\u513f\uff0c\u4f60\u6ca1\u4e8b\u5427\uff1f\u201c\u54ea\u77e5\u5979\u8fd8\u672a\u56de\u8fc7\u6765\uff0c\u5df2\u662f\u542c\u5f97\u5979\u7684\u513f\u5b50\u4e00\u58f0\u5c16\u53eb\u3002 \u3000\u3000\u8fd9\u4e00\u53eb\u975e\u540c\u5c0f\u53ef\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5979\u7684\u513f\u5b50\u5df2\u662f\u88ab\u53e6\u5916\u4e00\u4e2a\u6ee1\u9762\u8840\u6c61\u7684\u91d1\u5175\u6293\u5728\u624b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e2a\u91d1\u5175\u66f4\u52a0\u72e1\u733e\uff0c\u4ed6\u662f\u5b8c\u5168\u6ca1\u6709\u53d7\u4f24\u88c5\u6b7b\u7684\u3002\u4ed6\u4f0f\u5728\u5c38\u9996\u5806\u4e2d\u88c5\u6b7b\uff0c\u9a97\u8fc7\u4e86\u90a3\u866c\u9aef\u6c49 \u5b50\uff0c\u5728\u4ed6\u7684\u540c\u4f34\u548c\u5f20\u96ea\u6ce2\u640f\u6597\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u4e1d\u6beb\u4e0d\u9732\u58f0\u606f\uff0c\u6b64\u65f6\u65b9\u59cb\u7a81\u7136\u8dc3\u8d77\u3002 \u3000\u3000\u201c\u54fc\uff0c\u4f60\u8fd8\u60f3\u8fc7\u6765\u548c\u6211\u62fc\u547d\u5417\uff1f\u4e56\u4e56\u5730\u7ed9\u6211\u7ad9\u7740\uff0c\u5426\u5219\u6211\u634f\u6b7b\u4f60\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u624b\u4e2d\u62ff\u7740\u5315\u9996\uff0c\u53cc\u773c\u51e0\u4e4e\u8981\u55b7\u51fa\u706b\u6765\uff0c\u4f46\u5374\u662f\u4e0d\u80fd\u4e0d\u505c\u4e0b\u811a\u6b65\u4e86\u3002 \u3000\u3000\u90a3\u91d1\u5175\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4ed6\u90a3\u4e48\u7b28\uff0c\u6211\u65e9\u5df2\u770b\u51fa\u4f60\u4e0d\u662f\u666e\u901a\u7684\u730e\u5987\u4e86\u3002\u542c\u8bf4\u6a80\u516c\u76f4\u7684 \u513f\u5b50\u5a36\u4e86\u4e00\u4e2a\u6c49\u5973\u4e3a\u59bb\uff0c\u60f3\u5fc5\u4f60\u5c31\u662f\u90a3\u4e2a\u6c49\u5973\u5427\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u662f\u7684\u3002\u6c42\u6c42\u4f60\u884c\u4e2a\u597d\uff0c\u653e\u4e86\u6211\u7684\u513f\u5b50\u5427\uff0c\u4f60\u53d7\u4e86\u4f24\uff0c\u6211\u53ef\u4ee5\u7528\u91d1 \u521b\u836f\u548c\u4f60\u4ea4\u6362\u3002\u201d\u91d1\u5175\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8c0e\u7684\u672c\u9886\u592a\u5dee\uff0c\u773c\u529b\u4e5f\u592a\u5dee\uff01\u201d \u3000\u3000\u4ed6\u563f\u563f\u51b7\u7b11\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u53d7\u4f24\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u8eab\u4e0a\u7684\u8840\u4e0d\u8fc7\u662f\u540c\u4f34\u7684\u8840\u3002\u4f60 \u7684\u91d1\u521b\u836f\u7559\u7740\u81ea\u5df1\u7528\u5427\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8981\u6211\u653e\u8fc7\u4f60\u7684\u5b69\u5b50\uff0c\u90a3\u4e5f\u4e0d\u96be\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\u7684\u6761\u4ef6\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u91d1\u5175\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u600e\u6837\uff0c\u4f60\u957f\u5f97\u4e0d\u9519\uff0c\u6211\u53ea\u60f3\u4f60\u505a\u6211\u7684\u8001\u5a46\u3002\u6211\u662f\u5c1a\u672a\u5a36\u59bb\u7684\uff0c\u4e0d\u4f1a \u50cf\u90a3\u4e2a\u4eba\u4e00\u6837\u8981\u59d4\u5c48\u4f60\u505a\u5c0f\u8001\u5a46\u3002\u201d\u5f20\u96ea\u6ce2\u5fcd\u4e0d\u5f80\u53c8\u9a82\uff1a\u201c\u755c\u7272\uff01\u201d \u3000\u3000\u90a3\u91d1\u5175\u5012\u4e0d\u52a8\u6012\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u7b54\u5e94\uff0c\u90a3\u4e5f\u7531\u4f60\uff0c\u53ea\u662f\u4f60\u7684\u513f\u5b50\u6211\u53ef\u8981\u62ff\u56de\u4eac\u5e08 \u732e\u7ed9\u7687\u4e0a\u4e86\u3002\u563f\u563f\uff0c\u6a80\u8d1d\u52d2\u8bf7\u4e0d\u5230\uff0c\u8fd9\u5b69\u5b50\u7eb5\u662f\u6742\u79cd\uff0c\u6bd5\u7adf\u4e5f\u8fd8\u662f\u4ed6\u7684\u5b59\u513f\u3002\u6211\u5927\u7684\u529f\u52b3\u635e \u4e0d\u5230\uff0c\u5c0f\u5c0f\u7684\u529f\u52b3\u90a3\u662f\u5230\u624b\u4e86\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u5730\u9a82\u9053\uff1a\u201c\u4f60\u6562\u9a82\u6211\u662f\u5c0f\u6742\u79cd\uff0c\u4f60\u624d\u662f\u6742\u79cd\uff01\u201d\u7a81\u7136\u5f20\u53e3\u5728\u4ed6\u80a9\u5934\u4e00\u54ac\u3002 \u3000\u3000\u91d1\u5175\u5927\u6012\u559d\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u4f60\u4e0d\u60f3\u6d3b\u4e86\uff01\u201d\u4e0d\u8fc7\u4ed6\u53ef\u820d\u4e0d\u5f97\u8fd9\u4e2a\u4eba\u8d28\uff0c\u53ea\u6709\u628a\u64c5\u7fbd\u51b2\u9ad8 \u9ad8\u4e3e\u8d77\uff0c\u4f5c\u52bf\u8981\u628a\u4ed6\u6454\u6b7b\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u6050\u6015\u4ed6\u771f\u8981\u6454\u6b7b\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u628a\u624b\u4e00\u626c\u5315\u9996\u98de\u51fa\u3002 \u3000\u3000\u90a3\u91d1\u5175\u6b63\u5728\u5f20\u53e3\u5927\u9a82\uff0c\u5315\u9996\u98de\u6765\uff0c\u6070\u597d\u98de\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u7a7f\u8fc7\u4e86\u4ed6\u7684\u5589\u5499\uff01\u90a3\u91d1\u5175\u53eb\u4e5f\u53eb \u4e0d\u51fa\u6765\uff0c\u8eab\u8eaf\u5411\u540e\u5012\u4e0b\uff0c\u5b69\u5b50\u7ed9\u629b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4e00\u63a0\u800c\u524d\uff0c\u63a5\u4e0b\u513f\u5b50\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u90a3\u91d1\u5175\u5df2\u662f\u5012\u5728\u5730\u4e0a\uff0c\u9c9c\u8840\u597d\u4f3c\u7bad\u4e00\u6837\u4ece\u4e2d\u91cc \u5c04\u51fa\u6765\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4e0d\u6562\u770b\u8fd9\u60e8\u72b6\uff0c\u8fde\u5fd9\u62d4\u51fa\u5315\u9996\uff0c\u62d6\u4e86\u5b69\u5b50\uff0c\u8dd1\u5230\u6811\u6797\u91cc\u9762\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c \u4f60\u771f\u597d\u672c\u9886\uff0c\u4f60\u6559\u6211\u7528\u98de\u5200\u597d\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u7684\u6697\u5668\u529f\u592b\u662f\u8ddf\u5f20\u708e\u5077\u5077\u5b66\u7684\uff0c\u5176\u5b9e\u8fd8\u672a\u7ec3\u6210\uff0c\u5979\u60f3\u8d77\u521a\u624d\u90a3\u6837\u5371\u9669\u7684\u5883\u51b5\uff0c\u5fc3 \u4e2d\u72b9\u6709\u4f59\u60b8\uff0c\u8fd9\u98de\u5200\u4e00\u63b7\uff0c\u5018\u82e5\u4e07\u4e00\u5931\u624b\uff0c\u540e\u679c\u771f\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u201c\u51b2\u513f\uff0c\u5728\u4f60\u672a\u627e\u5230\u5e08\u7236\u4e4b\u524d\uff0c\u5988\u5988\u4f1a\u7684\u672c\u9886\uff0c\u53ea\u8981\u4f60\u80af\u5b66\uff0c\u5988\u5988\u5f53\u7136\u4f1a\u6559\u7ed9\u4f60\u3002\u4e0d \u8fc7\uff0c\u4f60\u4e00\u5b9a\u8981\u9075\u4ece\u5988\u5988\u7684\u5429\u5490\uff0c\u5426\u5219\u6211\u5b81\u613f\u4f60\u4e0d\u61c2\u6b66\u529f\u3002 \u3000\u3000\u4f60\u7b54\u5e94\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u8981\u6211\u7b54\u5e94\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4f60\u5f88\u61c2\u4e8b\uff0c\u54b1\u4eec\u5c31\u597d\u597d\u5730\u8c08\u4e00\u8c08\u5427\u3002\u5148\u62ff\u4eca\u5929\u53d1\u751f\u7684\u4e8b\u60c5\u6765\u8c08\u4e00 \u8c08\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\uff0c\u6211\u505a\u9519\u4e86\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u6ca1\u6709\u9519\uff1b\u9519\u7684\u662f\u6211\u3002\u6211\u540e\u6094\u6ca1\u6709\u542c\u4f60\u7684\u8bdd\uff0c\u4e5f\u5fd8\u8bb0\u4e86\u516c\u516c\uff08\u5f20 \u708e\uff09\u5e38\u5e38\u5bf9\u6211\u8bf4\u7684\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u516c\u516c\u8bf4\u7684\u90a3\u53e5\u8bdd\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5bf9\u654c\u4eba\u6148\u60b2\uff0c\u5c31\u662f\u5bf9\u81ea\u5df1\u6b8b\u5fcd\u3002\u4e0d\u9519\uff0c\u654c\u4eba\u4e5f\u5206\u597d\u51e0\u79cd\uff0c\u6709\u7684\u7f6a\u5927\u6076\u6781\uff0c \u6709\u7684\u53ea\u662f\u5949\u547d\u800c\u4e3a\uff0c\u8eab\u4e0d\u7531\u5df1\uff1b\u6709\u624b\u4e0a\u62ff\u7740\u5200\u7684\u654c\u4eba\uff0c\u6709\u7b11\u91cc\u85cf\u5200\u7684\u654c\u4eba\uff0c\u4f46\u4e5f\u6709\u4e1a\u5df2\u653e\u4e0b \u5c60\u5200\uff0c\u613f\u610f\u6094\u6539\u7684\u654c\u4eba\u3002\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\uff0c\u4e00\u5473\u6ee5\u6740\u3002\u4f46\u90a3\u4e2a\u5047\u88c5\u53d7\u4e86\u91cd\u4f24\u7684\u91d1\u5175\uff0c\u4e3a\u5a18\u7684\u6ca1 \u6709\u4ed4\u7ec6\u5bdf\u89c6\uff0c\u5c31\u53bb\u6551\u4ed6\u3002\u5bf9\u654c\u4eba\u6beb\u4e0d\u63d0\u9632\uff0c\u8fd9\u5c31\u662f\u5927\u9519\u7279\u9519\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fc7\uff1a\u201c\u5988\uff0c\u6211\u4e5f\u542c\u5f97\u516c\u516c\u8bf4\u8fc7\uff0c\u516c\u516c\u8bf4\u4e00\u4e2a\u4eba\u603b\u662f\u96be\u514d\u4f1a\u72af\u9519\u7684\uff0c\u53ea\u8981\u5728\u505a\u9519\u7684 \u4e8b\u60c5\u4e2d\u5f97\u5230\u6559\u8bad\uff0c\u90a3\u4e48\u574f\u4e8b\u4e5f\u5c31\u53d8\u6210\u597d\u4e8b\u4e86\u3002\u90a3\u4e24\u4e2a\u574f\u4eba\u5df2\u7ecf\u6076\u6709\u6076\u62a5\uff0c\u5b69\u513f\u4e5f\u6ca1\u53d7\u4f24\uff0c \u5988\uff0c\u4f60\u4e5f\u5c31\u4e0d\u5fc5\u96be\u8fc7\u5566\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u60ca\u5947\u4e8e\u5b69\u5b50\u7684\u9886\u609f\u80fd\u529b\u4e4b\u5f3a\uff0c\u8bf4\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4f60\u8bb0\u5f97\u516c\u516c\u6559\u5bfc\uff0c\u6bd4\u5988\u8fd8\u5f3a\uff0c\u771f\u662f \u4e2a\u597d\u5b69\u5b50\u3002\u4e0d\u8fc7\uff0c\u4eca\u5929\u4f60\u4e5f\u505a\u4e86\u4e00\u4ef6\u5341\u5206\u9c81\u83bd\u7684\u4e8b\uff0c\u5f80\u5f80\u6bd4\u505a\u9519\u4e86\u4e8b\u540e\u679c\u66f4\u574f\uff0c\u4f60\u77e5\u9053 \u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u949f\u9053\uff1a\u201c\u6211\u505a\u4e86\u4ec0\u4e48\u9c81\u83bd\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u4e0d\u5e94\u62d4\u5200\u523a\u90a3\u91d1\u5175\uff0c\u4f60\u7684\u672c\u9886\u548c\u4ed6\u5dee\u5f97\u592a\u8fdc\uff0c\u6ca1\u6709\u8d54\u4e0a\u4e00\u6761\u5c0f\u547d\uff0c\u90a3\u771f \u662f\u5929\u5927\u7684\u4fa5\u5e78\u3001\u4f60\u8bd5\u60f3\u60f3\uff0c\u8981\u662f\u4ed6\u5f53\u65f6\u4e00\u811a\u8e22\u4e2d\u4e86\u4f60\uff0c\u4f60\u8fd8\u80fd\u591f\u6d3b\u7740\u548c\u5988\u5988\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\uff0c\u5f53\u65f6\u90a3\u4e2a\u91d1\u5175\u662f\u6349\u7740\u4f60\u7684\u5440\uff0c\u5988\uff0c\u6211\u53ea\u662f\u8981\u5e2e\u4f60\u5440\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6211\u77e5\u9053\u4f60\u8981\u5e2e\u6211\uff0c\u4f60\u662f\u4e00\u7247\u597d\u5fc3\u3002\u4e0d\u8fc7\uff0c\u4f60\u7684\u5e2e\u5fd9\u662f\u65e0\u8865\u4e8e\u4e8b\u7684\uff0c \u53cd\u800c\u4ee4\u5988\u5988\u8981\u5206\u5fc3\u7167\u987e\u4f60\u3002\u90a3\u4e2a\u91d1\u5175\u7684\u672c\u9886\u6bd4\u4e0d\u4e0a\u6211\uff0c\u6211\u867d\u7136\u88ab\u4ed6\u6349\u4f4f\uff0c\u4f46\u8fd8\u662f\u6709\u628a\u63e1\u628a\u4ed6 \u6740\u6389\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f46\u5f53\u65f6\u6211\u7ed9\u5413\u614c\u4e86\uff0c\u6211\u5bb3\u6015\u4f60\u6253\u4e0d\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5c31\u662f\u6211\u6253\u4e0d\u8fc7\u4ed6\uff0c\u4f60\u4e5f\u4e0d\u5e94\u8be5\u5e2e\u6211\u3001\u8bd5\u60f3\u60f3\uff0c\u6211\u82e5\u6253\u4e0d\u8fc7\u4ed6\uff0c\u4f60\u53c8\u600e\u80fd\u6253 \u5f97\u8fc7\u4ed6\uff1f\u90a3\u4e0d\u662f\u54b1\u4eec\u6bcd\u5b50\u90fd\u8981\u4e27\u547d\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7237\u7237\u6b7b\u4e86\uff0c\u7239\u7239\u6b7b\u4e86\uff0c\u5916\u516c\u4e5f\u6b7b\u4e86\u3002\u5988\u5988\uff0c\u5018\u82e5\u4f60\u4e5f\u6027\u547d\u4e0d\u4fdd\uff0c\u5b69\u513f\u80fd\u6d3b \u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5c31\u662f\u8981\u4f60\u4e0d\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e4b\u4e0b\uff0c\u90fd\u8981\u6d3b\u4e0b\u53bb\u3002\u4f60\u8fd8\u8bb0\u5f97\u516c\u516c\u8981\u4f60\u957f \u5927\u4e86\u5b66\u597d\u672c\u9886\uff0c\u66ff\u4ed6\u62a5\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u773c\u4e2d\u542b\u6cea\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bb0\u5f97\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8bb0\u5f97\u5c31\u597d\u3002\u51b2\u513f\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u90a3\u4e9b\u574f\u4eba\u5df2\u7ecf\u5bb3\u6b7b\u4f60\u7684\u7237\u7237\uff0c\u5bb3\u6b7b\u4f60\u7684\u7239 \u7239\uff0c\u53c8\u5bb3\u6b7b\u4e86\u4f60\u7684\u5916\u516c\uff0c\u5fc5\u5b9a\u4e0d\u80af\u653e\u8fc7\u54b1\u4eec\u7684\uff0c\u4f60\u662f\u5f20\u5bb6\u548c\u6a80\u5bb6\u4e24\u5bb6\u4eba\u552f\u4e00\u7684\u5e7c\u82d7\uff0c\u4eca\u540e\u5373 \u4f7f\u78b0\u4e0a\u6bd4\u4eca\u5929\u66f4\u5927\u7684\u707e\u96be\uff0c\u4f60\u90fd\u8981\u5fcd\u53d7\uff0c\u4e0d\u80fd\u8ba9\u4eba\u770b\u7834\u4f60\u7684\u6765\u5386\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c\u90a3 \u4e9b\u574f\u4eba\u4e3a\u4ec0\u4e48\u8981\u5bb3\u6b7b\u7237\u7237\u3001\u7239\u7239\u548c\u5916\u516c\uff1f\u554a\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5916\u516c\u4e34\u6b7b\u65f6\u5019\u8bf4\u4e86\u4e00\u53e5\u5f88\u5947\u602a \u7684\u8bdd\uff0c\u4ed6\u4e5f\u50cf\u4f60\u521a\u624d\u5bf9\u6211\u8bf4\u7684\u90a3\u6837\uff0c\u8981\u4f60\u6d3b\u4e0b\u53bb\u3002\u4e0d\u8fc7\uff0c\u4ed6\u8bf4\u662f\u8981\u4f60\u4e3a\u4e86\u81ea\u5df1\u7684\u5916\u516c\u548c\u7239\u7239 \u4e5f\u8981\u6d3b\u4e0b\u53bb\u3002\u4ed6\u8fd8\u8bf4\u4ed6\u8fd9\u4e00\u751f\u603b\u7b97\u5bf9\u5f97\u4f4f\u4f60\u7684\u7239\u7239\uff0c\u5988\u5988\uff0c\u4f60\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u7239\u7239\u7684\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u64e6\u6cea\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u662f\u53e6\u5916\u8fd8\u6709\u4e00\u4f4d\u5916\u516c\u3002\u4e0d\u8fc7\u8fd9\u4e2a\u5916\u516c\u662f\u628a\u6211\u6276\u517b\u6210\u4eba\u7684\uff0c \u4ed6\u5bf9\u6211\u6bd4\u4eb2\u7239\u8fd8\u4eb2\uff0c\u5bf9\u4f60\u4e5f\u662f\u6bd4\u4eb2\u5916\u5b59\u66f4\u75bc\u7231\u7684\u3002\u6240\u4ee5\u4f60\u4e5f\u5fc5\u987b\u8bb0\u7740\u8fd9\u4e2a\u5916\u516c\u5e73\u65e5\u5bf9\u4f60\u7684\u6559 \u5bfc\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u7684\u3002\u6211\u7684\u53e6\u5916\u4e00\u4e2a\u5916\u516c\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u90a3\u4e2a\u5916\u516c\u65e9\u5df2\u6b7b\u6389\u4e86\u3002\u5b69\u5b50\uff0c\u4f60\u7684\u7956\u7236\u548c\u4f60\u7684\u5916\u516c\u90fd\u4e0d\u662f\u5bfb\u5e38\u4eba\uff0c\u4ed6\u4eec\u4e8b \u60c5\uff0c\u5f85\u4f60\u957f\u5927\u4e00\u4e9b\uff0c\u6211\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u73b0\u5728\u4f60\u5374\u5fc5\u987b\u8bb0\u4f4f\uff0c\u4e0d\u80fd\u7ed9\u5916\u4eba\u77e5\u9053\u4f60\u7684\u8eab\u4e16\uff0c\u8bb0\u4f4f\u4f60 \u53ea\u662f\u4e00\u4e2a\u666e\u901a\u730e\u4eba\u7684\u513f\u5b50\uff0c\u7239\u7239\u6b7b\u4e86\uff0c\u8ddf\u5988\u5988\u9003\u8352\u7684\u3002\u603b\u4e4b\u5988\u5988\u5343\u8a00\u4e07\u8bed\uff0c\u5c31\u662f\u6559\u4f60\u4e00\u4e2a \u2018\u5fcd\u2019\u5b57\uff0c\u660e\u767d\u4e48\uff1f \u3000\u3000\u201c\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c\u6211\u7b54\u5e94\u4f60\u3002\u4ee5\u540e\u4f60\u4e0d\u559c\u6b22\u6211\u505a\u7684\uff0c\u6211\u90fd\u4e0d\u505a\u3002\u201d\u5f20\u96ea\u6ce2\u9053\uff1a \u201c\u597d\uff0c\u8fd9\u624d\u662f\u5988\u5988\u7684\u597d\u5b69\u5b50\u3002\u4f60\u4e5f\u5f88\u75b2\u52b3\u4e86\uff0c\u6709\u8bdd\u660e\u5929\u518d\u8bf4\uff0c\u7761\u5427\uff0c\u7761\u5427\u3002\u201d\u5b69\u5b50\u5f88\u5feb\u5c31\u7761 \u7740\u4e86\u3002\u5f20\u96ea\u6ce2\u5374\u65e0\u6cd5\u5165\u7761\u3002\u91d1\u56fd\u7684\u7687\u5e1d\u8981\u6349\u4ed6\u4eec\u6bcd\u5b50\uff0c\u5b8b\u56fd\u7684\u5978\u81e3\u4e5f\u8981\u6349\u4ed6\u4eec\u6bcd\u5b50\uff0c\u5982\u4f55\u9003 \u5f97\u8fc7\u4ed6\u4eec\u7684\u9b54\u722a\u5462\uff1f\u5b8b\u56fd\u6d3e\u6765\u7684\u90a3\u56db\u4e2a\u536b\u58eb\u867d\u7136\u90fd\u5df2\u6b7b\u4e86\uff0c\u91d1\u56fd\u6d3e\u6765\u7684\u90a3\u4e09\u4e2a\u4ec0\u4e48\u5df4\u56fe\u9c81\uff0c \u53ef\u8fd8\u9003\u4e86\u4e00\u4e2a\u54c8\u5fc5\u56fe\u3002\u8fd8\u6709\uff0c\u81ea\u5df1\u662f\u4e00\u4e2a\u5e74\u8f7b\u7684\u5be1\u5987\uff0c\u5728\u8fd9\u5175\u8352\u9a6c\u4e71\u7684\u5e74\u5934\uff0c\u6050\u6015\u8fd8\u4f1a\u78b0\u4e0a \u4e0d\u77e5\u591a\u5c11\u6b21\u597d\u50cf\u4eca\u5929\u7684\u4e8b\u3002\u5979\u5fc3\u5982\u4e71\u9ebb\uff0c\u7ec8\u4e8e\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u552f\u6709\u6bc1\u6389\u81ea\u5df1\u7684\u5bb9\u8c8c\uff0c\u624d\u80fd\u591f \u5728\u8fd9\u4e71\u4e16\u6c42\u751f\u3002\u5979\u54ac\u7259\uff0c\u62d4\u51fa\u5315\u9996\uff0c\u5728\u81ea\u5df1\u7684\u8138\u4e0a\uff0c\u5de6\u4e00\u5200\uff0c\u53f3\u4e00\u5200\uff0c\u7eb5\u6a2a\u4ea4\u9519\u5212\u4e86\u5341\u51e0 \u5200\uff0c\u5979\u54ac\u7740\u7259\uff0c\u4e0d\u6562\u60ca\u9192\u81ea\u5df1\u7684\u5b69\u5b50\u3002\u867d\u7136\u5979\u77e5\u9053\u5b69\u5b50\u660e\u5929\u9192\u6765\uff0c\u4ecd\u662f\u514d\u4e0d\u4e86\u5927\u5927\u5403\u60ca\u7684\u3002 \u4f46\u5979\u4e0d\u613f\u8ba9\u5b69\u5b50\u5206\u62c5\u81ea\u5df1\u7684\u75db\u82e6\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u4e94 \u56de\u3000\u5b98\u8859\u8d4f\u82b1 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u4e94 \u56de\u3000\u5b98\u8859\u8d4f\u82b1 \u3000\u3000\u5ead\u524d\u828d\u836f\u5996\u65e0\u683c\uff0c\u6c60\u4e0a\u8299\u84c9\u51c0\u5c11\u60f0\u3002\u552f\u6709\u7261\u4e39\u771f\u56fd\u8272\uff0c\u82b1\u5f00\u65f6\u8282\u52a8\u4eac\u57ce\u3002\u5076\u7136\u76f8\u9047\u4eba\u95f4 \u4e16\uff0c\u4f1a\u5728\u5c42\u53f0\u963f\u59e5\u5bb6\u3002\u6709\u6b64\u503e\u57ce\u597d\u989c\u8272\uff0c\u5929\u6559\u665a\u53d1\u8d5b\u8bf8\u82b1\u3002 \u3000\u3000\u80e1\u59ec\u732e\u66f2\uff0c\u66fc\u821e\u8f7b\u6b4c\u3002\u821e\u5f71\u8e41\u8df9\uff0c\u4fe8\u4f3c\u7a7f\u82b1\u8774\u8776\uff1b\u6b4c\u58f0\u7f8e\u5999\uff0c\u80dc\u4e8e\u51fa\u8c37\u9ec4\u83ba\u3002\u4e3b\u4eba\u529d \u9152\uff0c\u5ba2\u4eba\u5927\u4e50\u3002 \u3000\u3000\u201c\u821e\u5f97\u597d\uff0c\u5531\u5f97\u5999\u3002\u53ef\u60dc\u6709\u4e00\u53e5\u5531\u8bcd\u8bf4\u5f97\u4e0d\u5bf9\u3002\u201d\u5ba2\u4eba\u8bf4\u9053\u3002 \u3000\u3000\u90a3\u6b4c\u59ec\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u662f\u54ea\u4e00\u53e5\u4e0d\u5bf9\uff0c\u8bf7\u54c8\u5927\u4eba\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u552f\u6709\u7261\u4e39\u771f\u56fd\u8272\u201d\uff0c\u5ba2\u4eba\u8bf4\u9053\uff1a\u201c\u7261\u4e39\u600e\u4e48\u6bd4\u5f97\u4e0a\u4f60\u3002\u201d\u8bf4\u7f62\u54c8\u54c8\u5927\u7b11\u3002\u6b4c\u59ec\u4f6f\u7f9e\u8bf4 \u9053\uff1a\u201c\u54c8\u5927\u4eba\u62ff\u6211\u53d6\u7b11\uff0c\u6211\u3001\u6211\u4e0d\u5e72\u5566\u3002\u201d \u3000\u3000\u4e3b\u4eba\u7b11\u9053\uff1a\u201c\u54c8\u5927\u4eba\u559c\u6b22\u542c\u6b4c\uff0c\u6211\u53eb\u5979\u4eec\u518d\u5531\u4e00\u66f2\u3002\u201d \u3000\u3000\u5ba2\u4eba\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u5e94\u8be5\u8bf4\u662f\u82b1\u5a07\u4eba\u66f4\u5a07\u624d\u5bf9\u3002\u5b8c\u989c\u5c06\u519b\uff0c\u8bf4\u771f\u4e2a\u7684\uff0c\u4eac\u57ce\u7684\u7261\u4e39\u53ef \u8fd8\u5f53\u771f\u6bd4\u4e0d\u4e0a\u4f60\u5bb6\u7684\u7261\u4e39\u5462\uff01\u201d\u5ba2\u4eba\u7684\u79f0\u8d5e\u5012\u4e0d\u662f\u5ba2\u5957\u7684\u5e94\u916c\u8bf4\u8bdd\u3002 \u3000\u3000\u56ed\u4e2d\u82b1\u5703\u9526\u7ee3\uff0c\u4f46\u5374\u5e76\u975e\u767e\u82b1\u9f50\u653e\u3002 \u3000\u3000\u56ed\u4e2d\u65e0\u6742\u6728\uff0c\u6709\u7684\u53ea\u662f\u7261\u4e39\u3002 \u3000\u3000\u6ee1\u56ed\u5b50\u90fd\u662f\u7261\u4e39\uff01 \u3000\u3000\u653e\u773c\u770b\u53bb\uff0c\u53ea\u8bf4\u82b1\u7684\u5f62\u72b6\u4fbf\u6709\u697c\u5b50\u3001\u51a0\u5b50\u3001\u5e73\u5934\u3001\u7ee3\u7403\u3001\u83b2\u53f0\u3001\u7897\u5f62\u3001\u76d8\u5f62\u7b49\u7b49\u7c7b\u578b\u3002 \u82b1\u74e3\u4e5f\u6709\u83b2\u82b1\u74e3\u3001\u65cb\u74e3\u3001\u4e1d\u74e3\u3001\u5377\u7b52\u74e3\u3001\u88c2\u74e3\u3001\u5c16\u957f\u74e3\u7b49\u7b49\u2026\u989c\u8272\u65b9\u9762\u5219\u66f4\u52a0\u591a\u59ff\u591a\u91c7\u4e86\uff0c \u6709\u7ea2\u3001\u7d2b\u3001\u9ec4\u3001\u767d\u3001\u7eff\u7b49\u8272\uff0c\u800c\u53ea\u662f\u7ea2\u8272\u53c8\u53ef\u4e3a\u6df1\u7ea2\u3001\u6de1\u7ea2\u3001\u6731\u7802\u7ea2\u3001\u6885\u7ea2\u3001\u80ed\u8102\u7ea2\u3001\u7c89 \u7ea2\u3001\u971e\u7ea2\u7b49\u2026\u2026\u771f\u4e2a\u662f\u82b1\u5149\u6fc0\u8273\uff0c\u7f8e\u4e0d\u80dc\u6536\u3002 \u3000\u3000\u201c\u591a\u8c22\u54c8\u5927\u4eba\u8d5e\u8d4f\uff0c\u5f85\u770b\u7f62\u8fd9\u573a\u6b4c\u821e\uff0c\u54b1\u4eec\u518d\u53bb\u8d4f\u82b1\u3002\u201d\u4e3b\u4eba\u8bf4\u9053\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u5728\u4e00\u4e2a\u5973\u4ec6\u5728\u4fee\u82b1\u526a\u8349\uff0c\u4f46\u5ba2\u4eba\u6b63\u5728\u76ee\u8ff7\u4e94\u8272\uff0c\u5f53\u7136\u4e0d\u4f1a\u6ce8\u610f\u53ca\u5979\u3002 \u3000\u3000\u5ba2\u4eba\u6ca1\u6ce8\u610f\u5979\uff0c\u5979\u53ef\u6ce8\u610f\u5230\u8fd9\u4e2a\u5ba2\u4eba\u4e86\u3002\u201c\u54a6\uff0c\u8fd9\u4e2a\u5ba2\u4eba\u4e0d\u5c31\u662f\u90a3\u4e2a\u4ec0\u4e48\u91d1\u56fd\u4e00\u7b49\u5df4\u56fe \u9c81\u7684\u54c8\u5fc5\u56fe\u5417\uff1f\u201d\u5979\u6ca1\u770b\u9519\u4eba\uff0c\u4e0d\u8fc7\u54c8\u5fc5\u56fe\u65e9\u5df2\u52a0\u5b98\u664b\u7235\uff0c\u6bd4\u4e00\u7b49\u5df4\u56fe\u9c81\u804c\u4f4d\u66f4\u9ad8\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u662f\u91d1\u56fd\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\uff0c\u5949\u4e86\u65b0\u7687\u5e1d\u5b8c\u989c\u4eae\u7684\u547d\u4ee4\uff0c\u79d8\u5bc6\u51fa\u4eac\uff0c\u6765\u5230\u5546\u5dde\u7684\u3002 \u3000\u3000\u6b64\u9645\u6b3e\u5f85\u4ed6\u7684\u4e3b\u4eba\uff0c\u5c31\u6b63\u662f\u5546\u5dde\u8282\u5ea6\u4f7f\u5b8c\u989c\u9274\u3002 \u3000\u3000\u5546\u5dde\u5728\u5927\u6563\u5173\u4e4b\u5317\uff0c\u4e0e\u5b8b\u56fd\u63a5\u58e4\uff0c\u662f\u4e00\u4e2a\u91cd\u8981\u7684\u8fb9\u9645\u5730\u533a\u3002\u5546\u5dde\u8282\u5ea6\u4f7f\u7684\u804c\u4f4d\u4e0d\u662f\u5f85\u95f2 \u4e4b\u8f88\u53ef\u4ee5\u505a\u7684\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u4f46\u662f\u5b97\u5ba4\uff0c\uff08\u7ec6\u7b97\u8d77\u6765\uff0c\u4ed6\u548c\u91d1\u56fd\u7684\u5f53\u4eca\u7687\u4e0a\u8fd8\u662f\u5144\u5f1f\u8f88\u5462\uff0c\u867d\u7136\u8fd9\u4e2a\u201c\u7ec6 \u7b97\u201d\uff0c\u8981\u7528\u7b97\u76d8\u624d\u7b97\u5f97\u6e05\u695a\uff0c\u5f53\u7136\u4ed6\u4e5f\u4e0d\u6562\u4ee5\u7687\u4eb2\u81ea\u5c45\u3002 \u3000\u3000\uff09\u800c\u4e14\u4ed6\u6709\u4e00\u4e2a\u5927\u540d\u9f0e\u9f0e\u7684\u4f2f\u7236\u3002 \u3000\u3000\u4ed6\u7684\u4f2f\u7236\u5b8c\u989c\u957f\u4e4b\u662f\u4e16\u88ad\u4eb2\u738b\uff0c\u73b0\u4efb\u7684\u91d1\u56fd\u5175\u9a6c\u5927\u5143\u5e05\u3002\u4ed6\u7684\u804c\u6743\u8fd8\u53ef\u4ee5\u517c\u7ba1\u5fa1\u6797\u519b\u3002 \u672c\u6765\u5fa1\u6797\u519b\u4e43\u662f\u7687\u5e1d\u7684\u4eb2\u5175\uff0c\u4f9d\u7167\u60ef\u4f8b\uff0c\u4e00\u5411\u662f\u7531\u5fa1\u6797\u519b\u7edf\u9886\u76f4\u63a5\u5411\u7687\u5e1d\u8d1f\u8d23\u3002\u5982\u4eca\u91d1\u56fd\u7684 \u7687\u5e1d\u5374\u51c6\u8bb8\u4ed6\u517c\u7ba1\u5fa1\u6797\u519b\uff0c\u4ed6\u7684\u6743\u529b\u4e4b\u5927\uff0c\u4ea6\u53ef\u89c1\u4e00\u6591\u4e86\u3002 \u3000\u3000\u4ece\u804c\u4f4d\u4e0a\u6765\u8bf4\uff0c\u5b8c\u989c\u957f\u4e4b\u4e5f\u53ef\u8bf4\u5f97\u54c8\u5fc5\u56fe\u7684\u9876\u5934\u4e0a\u53f8\u3002\u5b8c\u989c\u957f\u4e4b\u4e4b\u6240\u4ee5\u5f97\u4eab\u5927\u540d\uff0c\u8fd8\u4e0d \u4ec5\u4ec5\u662f\u56e0\u4e3a\u4ed6\u5b98\u9ad8\u6743\u91cd\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u662f\u516c\u8ba4\u7684\u91d1\u56fd\u7b2c\u4e00\u6b66\u5b66\u9ad8\u624b\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5e76\u4e0d\u662f\u4ed6\u7684\u4eb2\u4f84\uff0c\u4f46\u56e0\u5b8c\u989c\u9274\u6587\u6b66\u5168\u6750\uff0c\u4eba\u53c8\u7cbe\u660e\u80fd\u5e72\uff0c\u6545\u6b64\u5b8c\u989c\u957f\u4e4b\u624d\u628a\u5546\u5dde \u8282\u5ea6\u4f7f\u7684\u4f4d\u7f6e\uff0c\u7ed9\u4e86\u8fd9\u4e2a\u758f\u5802\u4f84\u513f\u3002 \u3000\u3000\u8bba\u5b98\u804c\uff0c\u8282\u5ea6\u4f7f\u7684\u5b98\u8854\u6bd4\u5fa1\u6797\u519b\u526f\u7edf\u9886\u9ad8\uff1b\u8bba\u80cc\u666f\uff0c\u5b8c\u989c\u9274\u6709\u4f2f\u7236\u6491\u8170\uff0c\u4e5f\u7edd\u4e0d\u5728\u54c8\u5fc5 \u56fe\u4e4b\u4e0b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u73b0\u5728\u5374\u5fc5\u987b\u5df4\u7ed3\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u54c8\u5fc5\u56fe\u4e0d\u5355\u662f\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\u3002\u800c\u4e14\u662f\u7687\u4e0a\u79d8\u5bc6\u6d3e\u6765\u7684\u94a6\u5dee\u3002 \u3000\u3000\u4e3a\u4e86\u5df4\u7ed3\u94a6\u5dee\uff0c\u4ed6\u7cbe\u9009\u5973\u4e50\uff0c\u6b4c\u821e\u5a31\u5bbe\u3002 \u3000\u3000\u53e6\u4e00\u961f\u80e1\u59ec\u53c8\u5728\u8e41\u8df9\u8d77\u821e\u4e86\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u772f\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u53ef\u771f\u4f1a\u4eab\u798f\uff0c\u54ea\u91cc\u5bfb\u6765\u7684\u8fd9\u8bb8\u591a\u5929\u4ed9\u4f3c\u7684\u7f8e\u4eba \u513f\uff1f\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u770b\u4e0a\u54ea\u4e00\u4e2a\uff0c\u4e0d\u59a8\u643a\u5979\u56de\u4eac\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u8fd9\u6211\u53ef\u4e0d\u6562\uff0c\u7ed9\u7687\u4e0a\u77e5\u9053\u4e86\uff0c\u6211\u7684\u8111\u888b\u53ef\u5f97\u642c\u5bb6\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u4f38\u4f38\u820c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u5389\u5bb3\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5927\u5bb6\u81ea\u5df1\u4eba\uff0c\u6211\u4e0d\u6015\u548c\u4f60\u8bf4\uff0c\u8001\u7687\u4e0a\u5df2\u7ecf\u662f\u591f\u7cbe\u660e\u3001\u591f\u5389\u5bb3\u7684\u4e86\uff0c\u65b0\u7687\u4e0a \u53ef\u6bd4\u8001\u7687\u4e0a\u8fd8\u66f4\u7cbe\u660e\u5389\u5bb3\u5f97\u591a\u3002\u8fd8\u6709\u4e00\u5c42\uff0c\u8001\u7687\u4e0a\u867d\u7136\u5389\u5bb3\uff0c\u5bf9\u5f97\u529b\u7684\u5927\u81e3\u8fd8\u662f\u5bbd\u539a\u7684\uff0c\u8fd9 \u4f4d\u65b0\u7687\u4e0a\u5374\u662f\u559c\u6012\u65e0\u5e38\uff0c\u813e\u6c14\u751a\u4e3a\u66b4\u8e81\u3002\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u4e5f\u5f97\u5f53\u5fc3\u70b9\u5462\u3002\u201d\u5b8c\u989c\u9274\u5fd9\u9053\uff1a\u201c\u591a \u8c22\u5927\u4eba\u6307\u70b9\u3002\u4e0d\u77e5\u5f53\u4eca\u7687\u4e0a\u559c\u7231\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u7687\u4e0a\u4e5f\u662f\u751a\u597d\u5973\u8272\u7684\uff0c\u4e0d\u8fc7\u4f60\u53ef\u4e0d\u80fd\u660e\u91cc\u9001\u53bb\uff0c\u4e5f\u4e0d\u80fd\u7531\u6211\u4ee3 \u9001\u3002\u6211\u662f\u5949\u547d\u5355\u9a91\u51fa\u4eac\u7684\u94a6\u5dee\uff0c\u4e0d\u80fd\u62db\u6447\u7684\uff0c\u5e26\u4e86\u5973\u4eba\u540c\u884c\u6210\u4ec0\u4e48\u6837\u5b50\u3002\u4f60\u53ef\u4ee5\u5148\u628a\u7f8e\u5973\u9001 \u5165\u4eac\u4e2d\uff0c\u7136\u540e\u518d\u7531\u7687\u4e0a\u4eb2\u4fe1\u7684\u592a\u76d1\u7ed9\u4f60\u79d8\u5bc6\u732e\u7ed9\u7687\u4e0a\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u600e\u77e5\u9053\u54ea\u4e2a\u662f\u7687\u4e0a\u4eb2\u4fe1\u7684\u592a\u76d1\uff0c\u77e5\u9053\u4e86\u53c8\u600e\u6837\u80fd\u591f\u63a5\u5f97\u4e0a\u5934\uff1f\u201d\u54c8\u5fc5\u56fe \u9053\uff1a\u201c\u8fd9\u4f60\u5012\u4e0d\u7528\u62c5\u5fc3\uff0c\u5230\u65f6\u6211\u53ef\u4ee5\u5e2e\u4f60\u5b89\u6392\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u5fc3\u82b1\u6012\u653e\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u9001\u7ed9\u4ed6\u7684\u9ec4\u91d1\u5b9d\u77f3\u679c\u7136\u89c1\u6548\u4e86\u3002\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5148 \u591a\u8c22\u54c8\u5927\u4eba\u7684\u5e2e\u5fd9\u4e86\u3002\u201d\u4ed6\u4e3e\u8d77\u9152\u676f\uff0c\u6b63\u60f3\u7ed9\u54c8\u5fc5\u56fe\u656c\u9152\uff0c\u53ea\u89c1\u7ed9\u5fc3\u56fe\u5df2\u662f\u770b\u5f97\u51fa\u4e86\u795e\uff0c\u5bf9 \u4ed6\u8fd9\u4e2a\u656c\u9152\u7684\u4e3e\u52a8\u6beb\u65e0\u53cd\u5e94\u3002\u539f\u6765\u9886\u961f\u7684\u90a3\u4e2a\u6b4c\u59ec\u5df2\u5728\u8f7b\u542f\u6731\u5507\u4e86\u3002\u8fd9\u4e2a\u6b4c\u59ec\u4e0d\u4f46\u957f\u5f97\u8273 \u4e3d\uff0c\u6b4c\u5589\u4e5f\u5f88\u7f8e\u5999\u3002 \u3000\u3000\u5b8c\u989c\u9274\u77e5\u8da3\uff0c\u653e\u4e0b\u9152\u676f\uff0c\u966a\u4ed6\u542c\u6b4c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u6b4c\u59ec\u66fc\u58f0\u5531\u9053\uff1a\u201c\u6d53\u7d2b\u6df1\u9ec4\u4e00\u753b\u56fe\uff0c\u4e2d\u95f4\u66f4\u6709\u7389\u76d8\u5b5f\u3002\u201d \u3000\u3000\u5148\u88c1\u7fe1\u7fe0\u88c5\u6210\u76d6\uff0c\u66f4\u70b9\u80ed\u8102\u67d3\u900f\u9165\u3002 \u3000\u3000\u9999\u6f4b\u8273\uff0c\u9526\u6a21\u7cca\uff0c\u4e3b\u4eba\u957f\u5f97\u9189\u5de5\u592b\u3002 \u3000\u3000\u83ab\u643a\u5f04\u7389\u68da\u8fb9\u53bb\uff0c\u7f9e\u5f97\u82b1\u679d\u4e00\u6735\u65e0\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u8bfb\u4e66\u65e0\u591a\uff0c\u5176\u5b9e\u542c\u5f97\u4e0d\u5927\u61c2\uff0c\u542c\u5f97\u4e00\u4e2a\u201c\u82b1\u201d\u5b57\u5c31\u95ee\u9053\uff1a\u201c\u8fd9\u652f\u66f2\u5b50\u5531\u5f97\u53c8\u662f\u4ec0 \u4e48\u82b1\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd8\u662f\u7261\u4e39\u3002\u201d \u3000\u3000\u4e00\u4e2a\u6b4c\u5973\u8bf4\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u7684\u592b\u4eba\u6700\u559c\u6b22\u7684\u82b1\u5c31\u662f\u7261\u4e39\u4e86\u3002\u6240\u4ee5\u56ed\u5b50\u91cc \u683d\u7684\u90fd\u662f\u7261\u4e39\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u8ba8\u597d\u4e3b\u4eba\uff0c\u4e3e\u8d77\u9152\u676f\u8d5e\u9053\uff1a\u201c\u98ce\u96c5\u3001\u98ce\u96c5\uff01\u7261\u4e39\u82b1\u662f\u5bcc\u8d35\u82b1\uff0c\u4e5f\u53ea\u6709\u7261\u4e39\u82b1\u624d\u624d \u914d\u5f97\u8d77\u5b8c\u989c\u5c06\u519b\u7684\u8eab\u4efd\u3002\u201d\u63a5\u7740\u7b11\u9053\uff1a\u201c\u5531\u5f97\u597d\uff0c\u6b4c\u8bcd\u4e5f\u5199\u5f97\u597d\uff0c\u662f\u8c01\u5199\u7684\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u5446\u4e86\u4e00\u5446\uff0c\u90a3\u4e2a\u6b4c\u5973\u5df2\u662f\u66ff\u4ed6\u7b54\u9053\uff1a\u201c\u8fd9\u9996\u8bcd\u540d\u53eb\u9e67\u9e2a\u5929\uff0c\u542c\u8bf4\u662f\u6c5f\u5357\u4e00\u4e2a\u540d\u53eb \u8f9b\u5f03\u75be\u7684\u624d\u5b50\u5199\u7684\u3002\u201d \u3000\u3000\u60f3\u4e0d\u5230\u54c8\u5fc5\u56fe\u7adf\u7136\u77e5\u9053\u8f9b\u5f03\u75be\u7684\u540d\u5b57\uff0c\u4ed6\u6115\u7136\u653e\u4e0b\u9152\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u624d\u5b50\uff1f\u542c\u8bf4\u8f9b\u5f03 \u75be\u662f\u5357\u671d\uff08\u5b8b\u56fd\uff09\u4e00\u4e2a\u9887\u6709\u540d\u6c14\u7684\u6b66\u5c06\uff0c\u662f\u803f\u4eac\u7684\u5f97\u529b\u90e8\u4e0b\uff0c\u539f\u6765\u4ed6\u8fd8\u662f\u4e2a\u4f1a\u541f\u8bd7\u505a\u8bcd\u7684\u624d \u5b50\u5417\uff1f\u201d \u3000\u3000\u9009\u5531\u8fd9\u9996\u8bcd\u7684\u6b4c\u5973\u77e5\u9053\u95ef\u4e86\u7978\uff0c\u5413\u5f97\u53d1\u6296\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5fc3\u91cc\u4e5f\u662f\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u53ea\u597d\u4ece\u65c1\u89e3\u91ca\uff1a\u201c\u5357\u671d\u8bcd\u98ce\u751a\u76db\uff0c\u6bcf\u6709\u65b0\u8bcd\u4e00\u51fa\uff0c\u6c11\u95f4\u827a\u4eba \u5c31\u62ff\u6765\u8c31\u66f2\uff0c\u5230\u5904\u90fd\u6709\u4eba\u5531\u3002\u5546\u5dde\u548c\u5357\u671d\u4ea4\u754c\uff0c\u81ea\u4ece\u90a3\u5e74\u505c\u6218\u4e4b\u540e\uff0c\u81f3\u4eca\u672a\u518d\u91cd\u542f\u5e72\u6208\uff0c\u767e \u59d3\u5f80\u6765\u4e5f\u6e10\u6e10\u591a\u4e86\u3002\u5357\u671d\u6d41\u884c\u7684\u8bcd\u66f2\uff0c\u5f80\u5f80\u4e5f\u5728\u5357\u5dde\u6d41\u884c\u3002\u5018\u82e5\u4e0d\u662f\u4ed4\u7ec6\u67e5\u95ee\uff0c\u8fde\u6211\u4e5f\u4e0d\u77e5 \u9053\u66f2\u8bcd\u662f\u8c01\u5199\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u5e74\u6211\u7ba1\u519b\u52a1\u591a\u4e86\u4e00\u70b9\uff0c\u8fd9\u4e9b\u5c0f\u4e8b\u60c5\u4e5f\u6ca1\u5de5\u592b\u53bb\u7ec6\u67e5\u5566\u3002\u4e0d\u8fc7\uff0c\u8fd9\u9996\u8bcd\u867d\u7136\u662f\u8f9b\u5f03\u75be \u5199\u7684\uff0c\u548f\u8d5e\u7684\u53ea\u662f\u7261\u4e39\uff0c\u5012\u4f3c\u4e4e\u6ca1\u6709\u4ec0\u4e48\u72af\u5fcc\u4e4b\u5904\u3002\u5927\u4eba\u82e5\u8ba4\u4e3a\u4e0d\u5f53\uff0c\u6211\u613f\u4ee3\u5979\u53d7\u8fc7\u3002\u201d\u4ed6 \u770b\u5f97\u51fa\u54c8\u5fc5\u56fe\u5f88\u559c\u6b22\u90a3\u4e2a\u6b4c\u59ec\uff0c\u4ed6\u4e5f\u820d\u4e0d\u5f97\u5c06\u90a3\u6b4c\u59ec\u8d23\u6253\uff0c\u662f\u4ee5\u5927\u80c6\u4ee3\u5979\u6c42\u60c5\u3002\u54c8\u5fc5\u56fe\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u5c06\u519b\u8fc7\u8651\uff0c\u5531\u5357\u671d\u6d41\u884c\u7684\u66f2\u5b50\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u54b1\u4eec\u7684\u7687\u4e0a\u8fd8\u5199\u6c49\u8bd7\u5462\u3002\u5b8c\u989c\u5c06\u519b\uff0c\u4f60 \u77e5\u9053\u5cb3\u98de\u5427\uff1f\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5cb3\u98de\u6211\u600e\u80fd\u4e0d\u77e5\uff0c\u4ed6\u662f\u54b1\u4eec\u91d1\u4eba\u7684\u6b7b\u5bf9\u5934\uff01\u201d \u3000\u3000\u4e00\u4e2a\u6b4c\u59ec\u9053\uff1a\u201c\u4e0d\u662f\u542c\u8bf4\u5cb3\u98de\u65e9\u5df2\u6b7b\u4e86\u591a\u5e74\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u7684\u9aa8\u5934\u5316\u4e86\u7070\u4e5f\u8fd8\u662f\u54b1\u4eec\u7684\u6b7b\u5bf9\u5934\u3002\u4f60\u4e00\u4e2a\u5a18\u513f\u54ea\u91cc\u61c2\u5f97\uff0c\u5cb3 \u98de\u867d\u7136\u6b7b\u4e86\uff0c\u4ed6\u7684\u65e7\u5c5e\u8fd8\u672a\u6b7b\u7edd\uff0c\u8981\u5949\u4ed6\u7684\u4ec0\u4e48\u9057\u5fd7\u548c\u54b1\u4eec\u4f5c\u5bf9\u3002\u54c8\u5927\u4eba\uff0c\u56e0\u4f55\u4f60\u63d0\u8d77\u5cb3 \u98de\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u548c\u5cb3\u98de\u6709\u5173\u7684\u5417\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u662f\u4e00\u4ef6 \u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u559d\u4e86\u4e00\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u5929\u6211\u4eec\u89c1\u7687\u4e0a\u6447\u5934\u6643\u8111\u5730\u5ff5\u8bd7\uff0c\u8fde\u8bf4\u5199\u5f97\u597d\uff0c\u5199\u5f97 \u597d\u3002\u6211\u95ee\u662f\u8c01\u5199\u7684\uff0c\u4ed6\u8bf4\u662f\u5cb3\u98de\u5199\u7684\u4ec0\u4e48\u6ee1\u5802\u7ea2\u3002\u201d \u3000\u3000\u6b4c\u59ec\u5fcd\u7740\u7b11\u9053\uff1a\u201c\u662f\u6ee1\u6c5f\u7ea2\u5427\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u4e00\u62cd\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u662f\u6ee1\u6c5f\u7ea2\u3002\u4e0d\u8fc7\u4f9d\u6211\u770b\u6765\uff0c\u6ee1\u5802\u7ea2\u53ef\u8981\u6bd4\u6ee1\u6c5f\u7ea2\u597d\u542c\uff0c \u6700\u5c11\u4e5f\u591a\u4e00\u70b9\u5409\u5229\u7684\u5146\u5934\u3002\u5cb3\u98de\u5199\u7684\u8bd7\u4e0d\u53eb\u6ee1\u5802\u7ea2\uff0c\u602a\u4e0d\u5f97\u4ed6\u4e0d\u4ee5\u4ed6\u4e0d\u80fd\u9022\u51f6\u5316\u5409\uff0c\u8981\u7ed9\u79e6 \u6867\u6740\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u6562\u6307\u51fa\u201c\u6ee1\u6c5f\u7ea2\u201d\u662f\u8bcd\u4e0d\u662f\u8bd7\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u6211\u5012\u771f\u6599\u60f3\u4e0d\u5230\uff0c\u7687\u4e0a\u600e\u7684\u5ff5 \u5cb3\u98de\u7684\u8bd7\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u8bf4\u5cb3\u98de\u7684\u53e3\u6c14\u5f88\u5927\uff0c\u6211\u5012\u8981\u548c\u4ed6\u6bd4\u4e00\u6bd4\u3002\u4ed6\u5938\u53e3\u8981\u76f4\u6363\u9ec4\u9f99\uff0c\u4f46\u7ec8\u4ed6 \u4e00\u751f\u90fd\u505a\u4e0d\u5230\u3002\u6211\u5374\u8981\u5728\u6709\u751f\u4e4b\u5e74\uff0c\u706d\u4e86\u5b8b\u56fd\u3002\u7687\u4e0a\u8fd8\u56e0\u6b64\u5199\u4e86\u4e00\u9996\u6c49\u8bd7\uff0c\u8bf4\u662f\u8981\u548c\u5cb3\u98de\u6bd4 \u4e00\u6bd4\u9ad8\u4e0b\u5462\uff01\u201d\u5b8c\u989c\u9274\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u8fd9\u9996\u8bd7\u4e0d\u77e5\u54c8\u5927\u4eba\u53ef\u8bb0\u5f97\u5426\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7687\u4e0a\u7684\u8bd7\uff0c\u6211\u600e\u6562\u4e0d\u5ff5\u5f97\u6eda\u74dc\u70c2\u719f\u3002\u201d\u5f53\u4e0b\u655e\u5f00\u5589\u5499\uff0c\u628a\u8fd9\u9996\u8bd7\u6717\u8bf5\u51fa \u6765\uff1a\u201c\u6df7\u4e00\u8f66\u4e66\u56db\u6d77\u540c\uff0c\u6c5f\u5357\u5c82\u6709\u522b\u7586\u5c01\u3002 \u3000\u3000\u63d0\u5175\u767e\u4e07\u897f\u6e56\u4e0a\uff0c\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bd7\u4ed6\u5ff5\u8fc7\u4e0d\u77e5\u591a\u5c11\u904d\uff0c\u679c\u7136\u662f\u719f\u6781\u5982\u6d41\uff0c\u80cc\u5f97\u4e00\u5b57\u4e0d\u5dee\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4f5c\u6d17\u8033\u606d\u542c\u72b6\uff0c\u542c\u7f62\uff0c\u51fb\u8282\u5927\u8d5e\uff1a\u201c\u7687\u4e0a\u6b64\u4e00\u5fa1\u8bd7\uff0c\u6c14\u76d6\u4eca\u53e4\uff0c\u5cb3\u98de\u600e\u80fd\u548c\u7687\u4e0a \u76f8\u6bd4\uff0c\u8981\u6bd4\u4e5f\u53ea\u6709\u2014\u2014\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u54e6\uff0c\u53ea\u6709\u8c01\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u53ea\uff0c\u53ea\u6709\u5386\u53f2\u4e0a\u529f\u4e1a\u6700\u5927\u7684\u7687\u5e1d\uff0c\u624d\u80fd\u548c\u7687\u4e0a\u76f8\u6bd4\uff0c\u5cb3\u98de\u4f55\u8db3\u9053\u54c9\uff1f\u201d \u3000\u3000\u539f\u6765\u91d1\u4e3b\u5b8c\u989c\u4eae\u867d\u8bf4\u662f\u8981\u548c\u5cb3\u98de\u4e00\u6bd4\u9ad8\u4e0b\uff0c\u4f46\u8fd9\u9996\u8bd7\u5374\u662f\u81ea\u6bd4\u79e6\u59cb\u7687\u7684\u3002\u53ea\u56e0\u79e6\u59cb\u7687\u529f \u4e1a\u867d\u76db\uff0c\u4f46\u5728\u5386\u53f2\u4e0a\u4e5f\u4ee5\u6b8b\u66b4\u8457\u540d\uff0c\u6545\u6b64\u5b8c\u989c\u9274\u4e0d\u6562\u76f4\u8a00\u3002 \u3000\u3000\u79e6\u59cb\u7687\u7edf\u4e00\u516d\u56fd\u540e\uff0c\u201c\u4e66\u540c\u6587\uff0c\u8f66\u540c\u8f68\u201d\uff0c\u5373\u662f\u628a\u6587\u5b57\u7edf\u4e00\u4e86\uff0c\u628a\u5ea6\u91cf\u8861\uff08\u5305\u62ec\u8f66\u8f68\u7684 \u957f\u77ed\uff0c\u7530\u4ea9\u7684\u5927\u5c0f\u3001\u94b1\u5e01\u7684\u8f7b\u91cd\u548c\u5f62\u5f0f\u7b49\u7b49\uff09\u7684\u5236\u5ea6\u4e5f\u7edf\u4e00\u4e86\u3002\u201c\u6df7\u4e00\u8f66\u4e66\u201d\u4ea6\u5373\u662f\u4ee3\u8868\u7edf \u4e00\u5929\u4e0b\u7684\u610f\u601d\u3002 \u3000\u3000\u5b8c\u989c\u4eae\u6b64\u8bd7\uff0c\u610f\u601d\u662f\u8bf4\u3002\u4ed6\u8981\u50cf\u79e6\u59cb\u7687\u4e00\u6837\u7edf\u4e00\u5929\u4e0b\uff0c\u4e0d\u5bb9\u8bb8\u6c5f\u5357\u6709\u5b8b\u56fd\u53e6\u5212\u7586\u754c\u3002\u897f \u6e56\u4e0e\u5434\u5c71\u90fd\u5728\u5357\u5b8b\u7684\u9996\u90fd\u4e34\u5b89\uff08\u5373\u4eca\u676d\u5dde\uff09\u5883\u5185\uff0c\u201c\u63d0\u5175\u767e\u4e07\u897f\u6e56\u4e0a\uff0c\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\u3002\u201d \u5373\u662f\u8981\u706d\u4ea1\u5b8b\u56fd\u7684\u610f\u601d\u3002 \u3000\u3000\u4fee\u526a\u82b1\u90a3\u4e2a\u5973\u82b1\u5320\u542c\u5f97\u54c8\u5fc5\u56fe\u6717\u8bf5\u6b64\u8bd7\uff0c\u5fc3\u5934\u5927\u6124\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u526a\u65ad\u4e86 \u4e00\u679d\u4e0d\u8be5\u526a\u7684\u679d\u5934\u4e0a\u5f00\u6709\u7261\u4e39\u82b1\u7684\u82b1\u679d\uff0c\u5e78\u800c\u5979\u7684\u4e3b\u4eba\u5546\u5dde\u8282\u5ea6\u4f7f\u5b8c\u989c\u9274\u6b63\u5728\u628a\u5168\u526f\u7cbe\u795e\u7528 \u4e8e\u548c\u94a6\u5dee\u5bf9\u8bdd\uff0c\u5927\u62cd\u4ed6\u4eec\u7687\u4e0a\u7684\u9a6c\u5c3c\uff0c\u6ca1\u6ce8\u610f\u53ca\u5979\u3001\u54c8\u5fc5\u56fe\u54c8\u54c8\u5927\u7b11\u3002\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u5cb3 \u98de\u600e\u80fd\u548c\u54b1\u4eec\u7684\u7687\u4e0a\u76f8\u6bd4\uff0c\u5cb3\u98de\u7684\u2018\u76f4\u6363\u9ec4\u9f99\u2019\u53ea\u662f\u68a6\u60f3\uff0c\u54b1\u4eec\u7687\u4e0a\u7684\u2018\u7acb\u9a6c\u5434\u5c71\u2019\u5219\u662f\u5fc5 \u5b9a\u53ef\u4ee5\u5b9e\u73b0\u7684\uff01\u201d \u3000\u3000\u4ed6\u559d\u4e86\u4e00\u676f\u9152\uff1b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5cb3\u98de\u4e0d\u80fd\u548c\u54b1\u4eec\u7684\u7687\u4e0a\u76f8\u6bd4\uff0c\u8f9b\u5f03\u75be\u4e5f\u4e0d\u80fd\u548c\u5cb3\u98de\u76f8\u6bd4\uff0c \u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\u6781\u4e86\uff01\u5cb3\u98de\u6700\u9ad8\u7684\u5b98\u8854\u662f\u5c11\u4fdd\uff0c\u8f9b\u5f03\u75be\u5982\u4eca\u8fd8\u4e0d\u8fc7\u662f\u803f\u4eac\u624b\u4e0b\u7684\u53c2 \u519b\u3002\u5f53\u7136\u4e0d\u80fd\u76f8\u6bd4\uff0c\u4e0d\u80fd\u76f8\u6bd4\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u7687\u4e0a\u8fde\u5cb3\u98de\u7684\u4ec0\u4e48\u3001\u4ec0\u4e48\u6ee1\u5802\u7ea2\u90fd\u5ff5\u5f97\u6eda\u74dc\u70c2\u719f\uff0c\u4f60\u4eec\u5531\u4e00\u5531\u8f9b \u5f03\u75be\u7684\u4ec0\u4e48\u3001\u4ec0\u4e48\u2014\u2014\uff08\u6b4c\u5973\u8f7b\u8f7b\u63d0\u9192\u4ed6\u9053\uff1a\u201c\u9e67\u9e2a\u5929\u201d\uff09\u5bf9\u3001\u5bf9\uff0c\u4ec0\u4e48\u9e67\u9e2a\u5929\uff0c\u90a3\u53c8\u4ec0\u4e48 \u5173\u7cfb\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u653e\u4e0b\u5fc3\u4e0a\u77f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5927\u4eba\u901a\u60c5\u8fbe\u7406\uff0c\u4e0d\u52a0\u8d23\u602a\u3002\u4f46\u5979\u4eec\u9009\u8bcd\u4e0d\u5f53\uff0c\u8fd8\u5f97 \u7f5a\u5979\u4eec\u591a\u5531\u4e00\u66f2\u3002\u201d \u3000\u3000\u9886\u961f\u7684\u6b4c\u5973\u5df2\u6709\u6212\u5fc3\uff0c\u8fde\u5fd9\u8bf7\u793a\uff1a\u201c\u4e0d\u77e5\u5927\u4eba\u559c\u6b22\u4ec0\u4e48\u66f2\u5b50\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u6211\u600e\u6837\u6740\u4eba\uff0c\u6211\u5012\u6562\u81ea\u5938\u662f\u4e2a\u884c\u5bb6\uff0c\u95ee\u6211\u66f2\u5b50\u7684\u597d\u574f\uff0c\u90a3\u53ef \u662f\u5411\u778e\u5b50\u95ee\u8def\u4e86\uff0c\u8fd8\u662f\u8bf7\u5b8c\u989c\u5c06\u519b\u8bf4\u9053\u5427\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u54c8\u5927\u4eba\u8fc7\u8c26\u4e86\u3002\u4f46\u54c8\u5927\u4eba\u5373\u7136\u6709\u547d\uff0c\u6211\u4e5f\u4e0d\u6562\u63a8\u8f9e\uff0c\u5c31\u66ff\u54c8\u5927\u4eba\u70b9\u4e00\u66f2 \u5427\u3002\u548f\u7261\u4e39\u7684\u8bd7\u8bcd\u3002\u53e4\u5f80\u4eca\u6765\uff0c\u4e0d\u77e5\u591a\u5c11\uff0c\u4f46\u4f3c\u4e4e\u4ee5\u5510\u4ee3\u8bd7\u4eba\u674e\u767d\u7684\u6e05\u5e73\u8c03\u4e09\u7ae0\u6700\u4e3a\u810d\u7099\u4eba \u53e3\uff0c\u5c31\u53eb\u5979\u4eec\u5531\u674e\u767d\u7684\u6e05\u5e73\u8c03\u5982\u4f55\uff1f\u201d \u3000\u3000\u8981\u77e5\u674e\u767d\u7684\u6e05\u5e73\u8c03\u662f\u4e3a\u5510\u660e\u7687\u4e0e\u6768\u8d35\u5983\u8d4f\u7261\u4e39\u5199\u7684\uff0c\u8fd9\u662f\u201c\u5949\u65e8\u9898\u8bd7\u201d\uff0c\u5fc5\u987b\u8ba8\u597d\u7687\u5e1d \u548c\u6768\u8d35\u5983\u7684\uff0c\u5728\u674e\u767d\u7684\u8bd7\u7bc7\u5176\u5b9e\u662f\u5eb8\u4fd7\u4e4b\u4f5c\uff0c\u4f46\u5374\u4e0d\u4f1a\u72af\u9519\u3002\uff08\u8fd9\u91cc\u7684\u72af\u9519\u662f\u6307\u72af\u7ed9\u54c8\u5fc5\u56fe \u4e4b\u5fcc\uff0c\u81f3\u4e8e\u8bcd\u4e2d\u7684\u8d75\u98de\u71d5\u72af\u6768\u8d35\u5983\u4e4b\u5fcc\uff0c\u90a3\u662f\u53e6\u4e00\u56de\u4e8b\u4e86\u3002\uff09\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u5c06\u519b\u8bf4\u662f\u597d \u7684\uff0c\u90a3\u5c31\u4e00\u5b9a\u662f\u597d\u7684\uff0c\u5531\u5427\uff0c\u5531\u5427\uff01\u201d \u3000\u3000\u6b4c\u5973\u91cd\u5c55\u6b4c\u5589\uff0c\u5531\u9053\uff1a\u201c\u4e91\u60f3\u8863\u88f3\u82b1\u60f3\u5bb9\uff0c\u6625\u98ce\u62c2\u69db\u9732\u534e\u6d53\u3002 \u3000\u3000\u82e5\u975e\u7fa4\u7389\u5c71\u5934\u89c1\uff0c\u4f1a\u5411\u7476\u53f0\u6708\u4e0b\u9022\u3002 \u3000\u3000\u4e00\u679d\u7ea2\u8273\u9732\u7591\u9999\uff0c\u4e91\u96e8\u5deb\u5c71\u6789\u65ad\u80a0\u3002 \u3000\u3000\u501f\u95ee\u6c49\u5bab\u8c01\u5f97\u4f3c\uff0c\u53ef\u601c\u98de\u71d5\u5fa1\u65b0\u5986\u3002 \u3000\u3000\u540d\u82b1\u503e\u56fd\u4e24\u76f8\u6b22\uff0c\u957f\u5f97\u541b\u738b\u5e26\u7b11\u770b\u3002 \u3000\u3000\u89e3\u91ca\u6625\u98ce\u65e0\u9650\u6068\uff0c\u6c89\u9999\u4ead\u5317\u501a\u680f\u6746\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u62cd\u638c\u8d5e\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c\u4f60\u82e5\u662f\u88ab\u9009\u5bab\u4e2d\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u5f97\u5230\u5f53\u4eca\u7687\u4e0a\u5e26\u7b11\u4e0a\u770b \u7684\u3002\u201d\u6b4c\u5973\u72b9\u6709\u4f59\u60b8\uff0c\u4e0d\u6562\u591a\u8bf4\uff0c\u751a\u81f3\u8fde\u6253\u60c5\u9a82\u4fcf\u7684\u8bdd\u90fd\u6ca1\u5fc3\u601d\u8bf4\u4e86\uff0c\u53ea\u9053\uff1a\u201c\u5927\u4eba\uff0c\u53d6\u7b11 \u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u77e5\u5927\u4eba\u662f\u5426\u8fd8\u60f3\u518d\u542c\u65b0\u6b4c\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6211\u5012\u60f3\u518d\u542c\u4e00\u904d\u8f9b\u5f03\u75be\u7684\u90a3\u9996\u4ec0\u4e48\u3001\u4ec0\u4e48\u9e67\u9e2a\u5929\uff0c\u4e0d\u8fc7\u4e0d\u5fc5\u8d77\u821e\u4e86\uff0c\u53ea\u6e05 \u5531\u5c31\u884c\u3002\u8fd8\u6709\uff0c\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u77e5\u9053\u6211\u809a\u5b50\u91cc\u58a8\u6c34\u4e0d\u591a\uff0c\u8981\u8bf7\u4f60\u4e3a\u6211\u8bb2\u89e3\u8bb2\u89e3\u8bcd\u610f\u624d\u597d\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u81ea\u601d\uff1a\u201c\u6211\u5df2\u7ecf\u9001\u4ed6\u9ec4\u91d1\u5b9d\u77f3\uff0c\u6599\u60f3\u4ed6\u4e0d\u4f1a\u6545\u610f\u627e\u6211\u7684\u5c94\u5b50\u3001\u8fd9\u9996\u8bcd\u4e5f\u6ca1\u4ec0\u4e48\u72af \u5fcc\u4e4b\u5904\uff0c\u4e0d\u6015\u4e3a\u4ed6\u89e3\u91ca\u3002\u201d\u4e8e\u662f\u7a0d\u4f5c\u5ba2\u6c14\u4e00\u756a\uff0c\u4fbf\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u8fd9\u9996\u8bcd\u662f\u8f9b\u5f03\u75be\u5728\u4e00\u4e2a\u59d3\u795d\u7684\u670b\u53cb\u5bb6\u91cc\u8d4f\u7261\u4e39\u4f5c\u7684\uff0c\u4e0a\u534a\u7bc7\u5199\u82b1\uff0c\u540e\u534a\u7bc7\u5199\u4e3b\u4eba\u548c\u966a\u9152 \u7684\u5973\u5b50\uff08\u5927\u6982\u4e5f\u662f\u4e3b\u4eba\u5bb6\u7684\u6b4c\u5973\u4e4b\u7c7b\uff09\uff0c\u6b63\u662f\u53ef\u8bf4\u5f97\u4e0a\u662f\u5e94\u4ed6\u4eec\u7684\u773c\u524d\u4e4b\u666f\u7684\u3002\u540e\u534a\u7bc7\u6b4c\u8bcd \u662f\uff1a\u201c\u9999\u6f4b\u8273\uff0c\u9526\u6a21\u7cca\uff0c\u4e3b\u4eba\u957f\u5f97\u9189\u529f\u592b\u3002\u83ab\u643a\u5f04\u7389\u680f\u8fb9\u53bb\uff0c\u5dee\u5f97\u82b1\u679d\u4e00\u6735\u65e0\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u542c\u7f62\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u4e3b\u4eba\u4e5f\u7b97\u5f97\u662f\u8d24\u4e3b\u4eba\u4e86\uff0c\u4ed6\u559d\u9189\u4e86\u4e5f\u6709\u5de5\u592b\u966a\u5ba2\u3002\u4f46\u559d\u9189\u4e86 \u8d4f\u82b1\u6050\u6015\u4e0d\u771f\u5207\uff0c\u54b1\u4eec\u8fd8\u662f\u522b\u559d\u9189\u7684\u597d\u3002\u201d\u5b8c\u989c\u9274\u5fd9\u6015\u4ed6\u7684\u9a6c\u5c41\uff1a\u201c\u5bf9\uff0c\u5bf9\u6781\u4e86\u3002\u54c8\u5927\u4eba\u8fd9 \u6837\u8bf4\u624d\u662f\u771f\u6b63\u61a7\u5f97\u98ce\u96c5\u4e4b\u9053\u5440\uff01\u9189\u773c\u6a21\u7cca\uff0c\u8d4f\u82b1\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u54b1\u4eec\u8fd9\u5c31\u8d4f\u7261\u4e39\u5427\u3002\u201d\u54c8\u5fc5 \u56fe\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5927\u4eba\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u5f04\u7389\u662f\u4e2a\u5f88\u7f8e\u8c8c\u7684\u5973\u5b50\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u77e5\u9053\u4ed6\u8bfb\u4e66\u65e0\u591a\uff0c\u5bf9\u6709\u5173\u79e6\u5f04\u7389\u7684\u6545\u4e8b\u6050\u6015\u4ed6\u542c\u5f97\u4e0d\u8010\u70e6\uff0c\u56e0\u6b64\u53ea\u5c31\u8bd7\u53e5\u89e3\u91ca\uff0c \u4e00\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u6b63\u56e0\u4e3a\u5f04\u7389\u662f\u4e00\u4e2a\u975e\u5e38\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c\u6240\u4ee5\u5ba2\u4eba\u529d\u4e3b\u4eba\u4e0d\u8981\u5e26\u5979\u5230\u680f\u8fb9 \u8d4f\u82b1\uff0c\u6050\u9632\u7261\u4e39\u82b1\u89c1\u4e86\u8fd9\u6837\u7f8e\u8c8c\u7684\u59d1\u5a18\uff0c\u4e5f\u8981\u81ea\u6127\u4e0d\u5982\u3002\u8bd7\u4e2d\u7684\u5f04\u7389\uff0c\u662f\u5ba2\u4eba\u501f\u7528\u53e4\u4ee3\u7684\u7f8e \u5973\u6765\u6bd4\u55bb\u4e3b\u4eba\u5bb6\u4e2d\u90a3\u4f4d\u966a\u9152\u7684\u5973\u5b50\u7684\u7f8e\u8c8c\u7684\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u7684\u8fd9\u73ed\u6b4c\u5973\u90fd\u957f\u5f97\u5929\u4ed9\u4e00\u822c\uff0c\u4f9d\u6211\u770b\uff0c\u968f\u4fbf\u4f60\u54ea\u4e00\u4e2a \u90fd\u6bd4\u5f97\u4e0a\u5f04\u7389\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e00\u542c\u4fbf\u77e5\u5176\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u7684\u610f\u601d\uff0c\u662f\u53eb\u5979\u4eec\u4e0d\u8981\u2014\uff0d\u201d\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u662f \u5440\uff0c\u8bf7\u4f60\u4e0d\u8981\u53eb\u5979\u4eec\u966a\u6211\u4eec\u8d4f\u82b1\u4e86\u3002\u8bd5\u60f3\u5979\u4eec\u4e00\u4e2a\u4e2a\u8fd9\u6837\u7f8e\u8c8c\uff0c\u5979\u4eec\u90fd\u53bb\u8d4f\u82b1\uff0c\u7261\u4e39\u82b1\u6050\u6015 \u90fd\u7f9e\u5f97\u4e0d\u6562\u5f00\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u5c4f\u9000\u6b4c\u5973\uff0c\u5176\u4ed6\u7528\u4eba\u4e5f\u90fd\u9000\u4e0b\uff0c\u5929\u9999\u4ead\u91cc\u5c31\u53ea\u6709\u4e3b\u5ba2\u4e8c\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u5929\u9999\u4e8b\u662f\u5b8c\u989c\u9274\u4e13\u4e3a\u8d4f\u7261\u4e39\u800c\u5efa\u7684\uff0c\u6bd4\u738b\u4faf\u5de8\u5ba4\u7684\u5ba2\u5385\u8fd8\u5927\u3002\u53ea\u6709\u4e24\u4e2a\u4eba\u9887\u6709\u7a7a\u9614 \u4e4b\u611f\uff0c\u4f46\u76ee\u529b\u6240\u6781\uff0c\u5bf9\u56ed\u4e2d\u7684\u666f\u7269\uff0c\u5374\u4e5f\u770b\u5f97\u6e05\u695a\u591a\u4e86\u3002\u90a3\u4e2a\u5973\u4f63\u4f3c\u4e4e\u6050\u6015\u60ca\u52a8\u4ed6\u4eec\uff0c\u5728\u56ed \u5b50\u4e00\u89d2\u7684\u82b1\u4e1b\u91cc\u8f7b\u8f7b\u4fee\u526a\u82b1\u679d\uff0c\u4e0d\u6562\u51fa\u6765\u3002\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u8fd9\u771f\u662f\u795e\u4ed9\u65e5\u5b50\uff0c\u4f46 \u518d\u8fc7\u4e9b\u65f6\uff0c\u6050\u6015\u4f60\u5c31\u8981\u5fd9\u5f97\u6ca1\u529f\u592b\u4e5f\u6ca1\u5fc3\u60f0\u8d4f\u82b1\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u5403\u4e86\u4e00\u60ca\uff0c\u8bd5\u63a2\u4ed6\u7684\u53e3\u98ce\u9053\uff1a\u201c\u5927\u4eba\u662f\u8bf4\u5c06\u6709\u5927\u4e8b\u53d1\u751f\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u662f\u5440\uff0c \u6240\u4ee5\u54b1\u4eec\u8fd8\u662f\u5148\u8c08\u6b63\u7ecf\u7684\u5927\u4e8b\uff0c\u8d4f\u82b1\u53ef\u4ee5\u7a0d\u7a0d\u62bc\u540e\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5927\u4eba\u5949\u7687\u4e0a\u5bc6\u4ee4\u51fa\u4eac\uff0c\u4e0d\u77e5\u6709\u4f55\u5927\u4e8b\u89c1\u544a\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5927\u7b11\u9053\uff1a\u201c\u5b8c\u989c\u5c06\u519b\uff0c\u7687\u4e0a\u53eb\u4f60\u5728\u5546\u5dde\u6574\u519b\u7ecf\u6b66\uff0c\u7687\u4e0a\u60f3\u8981\u505a\u7684\u8fd9\u4ef6\u5927\u4e8b\u662f\u4ec0 \u4e48\uff0c\u4f60\u4e5f\u5e94\u8be5\u60f3\u5f97\u5230\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u7687\u4e0a\u662f\u5426\u5c06\u63d0\u5175\u767e\u4e07\u897f\u6e56\u4e0a\uff0c\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\u662f \u2018\u5373\u5c06\u2019\uff0c\u4f46\u4f10\u5b8b\u4e4b\u4e3e\uff0c\u52bf\u5728\u5fc5\u884c\uff0c\u6700\u81f3\u8fdf\u6050\u6015\u4e5f\u4e0d\u4f1a\u8fdf\u8fc7\u660e\u5e74\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8bf7\u54c8\u5927\u4eba\u7980\u544a\u7687\u4e0a\uff0c\u5351\u804c\u5949\u547d\u9547\u5b88\u5546\u5dde\u3002\u4e0d\u6562\u7a0d\u6709\u677e\u61c8\uff0c\u519b\u9a6c\u7cae\u8349\u662f\u90fd\u5df2 \u6709\u4e86\u51c6\u5907\u7684\u3002\u4f10\u5b8b\u4e4b\u4ee4\u4e00\u4e0b\uff0c\u5351\u804c\u613f\u4e3a\u524d\u9a71\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5c06\u519b\u5fe0\u5fc3\u4e3a\u56fd\uff0c\u7687\u4e0a\u662f\u77e5\u9053\u7684\u3002\u6211\u8fd9\u6b21\u56de\u53bb\uff0c\u81ea\u5fc5\u4e5f\u4f1a\u628a\u5c06\u519b\u5982\u4f55\u6089\u5fc3\u6574 \u519b\u7ecf\u6b66\u7684\u529f\u52b3\u594f\u660e\u7687\u4e0a\u3002\u201d \u3000\u3000\u63a5\u7740\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u54b1\u4eec\u5982\u4f55\u996e\u9152\u4f5c\u4e50\uff0c\u542c\u6b4c\u8d4f\u821e\u8fd9\u4e9b\u5c0f\u4e8b\uff0c\u6211\u4e0d\u4f1a\u5bf9\u7687 \u4e0a\u8bf4\u7684\uff0c\u5176\u5b9e\u7687\u4e0a\u4e5f\u559c\u6b22\u5973\u8272\uff0c\u53ea\u662f\u4e0d\u80fd\u660e\u8a00\u7f62\u4e86\u3002\u201d \u3000\u3000\u4e24\u4e2a\u76f8\u89c6\u800c\u7b11\uff0c\u83ab\u9006\u4e8e\u5fc3\uff0c\u54c8\u5fc5\u56fe\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u9664\u4e86\u519b\u56fd\u5927\u4e8b\uff0c\u7687\u4e0a\u8fd8\u6709\u4e24\u4ef6\u4e8b\u4ea4\u7ed9 \u4f60\u3002\u8fd9\u4e24\u4ef6\u4e8b\u867d\u7136\u8bf4\u4e0d\u662f\u519b\u56fd\u5927\u4e8b\uff0c\u4f46\u4e5f\u8db3\u4ee5\u5f71\u54cd\u519b\u56fd\u5927\u4e8b\u7684\u3002\u201d \u3000\u3000\u4f10\u5b8b\u8fd9\u4ef6\u5927\u4e8b\uff0c\u5176\u5b9e\u7528\u4e0d\u7740\u54c8\u5fc5\u56fe\u4f20\u8fbe\u7687\u4e0a\u7684\u610f\u65e8\uff0c\u5b8c\u989c\u9274\u5df1\u77e5\u9053\u7684\u4e86\u3002\u91d1\u56fd\u8981\u541e\u5e76\u5b8b \u56fd\uff0c\u8fd9\u65e9\u5df2\u662f\u8def\u4eba\u7686\u77e5\u7684\u516c\u5f00\u79d8\u5bc6\uff0c\u4ed6\u8981\u77e5\u9053\u7684\u662f\u7687\u4e0a\u6709\u4f55\u5bc6\u4ee4\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ef\u8bf4\u5230\u6b63 \u9898\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u662f\u54ea\u4e24\u4ef6\u4e8b\u60c5\uff1f\u201d\u5b8c\u989c\u9274\u95ee\u9053\u3002\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7687\u4e0a\u6700\u987e\u5fcc\u7684\u662f\u54ea\u4e24\u4e2a\u4eba \u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u5176\u5b9e\u662f\u7565\u6709\u6240\u95fb\u7684\uff0c\u4f46\u5f53\u7136\u4ed6\u4e0d\u6562\u76f4\u8bf4\u662f\u4e1a\u5df2\u77e5\u9053\u3002 \u3000\u3000\u201c\u5351\u804c\u4e0d\u77e5\uff0c\u8bf7\u5927\u4eba\u8e22\u793a\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u662f\u6a80\u516c\u76f4\uff0c\u4ed6\u662f\u8d1d\u52d2\u8eab\u4efd\uff0c\u5c06\u519b\u60f3\u5fc5\u4e0d\u4f1a\u4e0d\u77e5\u3002 \u3000\u3000\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u4f4d\u6a80\u8d1d\u52d2\u4e0d\u662f\u542c\u8bf4\u5728\u5eff\u5341\u5e74\u524d\u5c31\u5df2\u83ab\u660e\u5176\u5999\u7684\u5730\u5931\u8e2a\u4e86\u5417\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u54b1\u4eec\u662f\u81ea\u5df1\u4eba\uff0c\u5927\u5bb6\u90fd\u4e0d\u5fc5\u987e\u5fcc\u3002\u4ece\u73b0\u5728\u8d77\uff0c\u54b1\u4eec\u77e5\u9053\u4ec0\u4e48\u5c31\u8bf4\u4ec0\u4e48\uff0c\u597d \u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u591a\u8c22\u7687\u4e0a\u548c\u54c8\u5927\u4eba\u8fd9\u6837\u4fe1\u4efb\u6211\uff0c\u6211\u82e5\u7136\u6709\u6240\u77e5\uff0c\u81ea\u662f\u4e0d\u6562\u9690\u7792\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e09\u5e74\u524d\u6211\u4e5f\u66fe\u5949\u8001\u7687\u4e0a\u4e4b\u547d\uff0c\u79d8\u5bc6\u51fa\u4eac\uff0c\u4f60\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u54c8\u5927\u4eba\u90a3\u5e74\u51fa\u4eac\u4e4b\u4e8b\u6211\u662f\u77e5\u9053\u7684\u3001\u4f46\u8001\u7687\u4e0a\u5bc6\u4ee4\u6211\u5f53\u7136\u4e0d\u5f97\u4e0e\u95fb\u4e86\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u90a3\u5e74\u6211\u79d8\u5bc6\u51fa\u4eac\uff0c\u5c31\u662f\u5949\u4e86\u8001\u7687\u4e0a\u4e4b\u547d\uff0c\u53ec\u6a80\u516c\u76f4\u56de\u4eac\u9762\u5723 \u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u88c5\u4f5c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u6a80\u8d1d\u52d2\u8fd8\u6d3b\u5728\u4eba\u95f4\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u53ef\u60dc\u73b0\u5728\u4ed6\u662f\u5426\u5c1a\u6d3b\u5728\u4eba\u95f4\u6211\u5374\u4e0d\u77e5\u4e86\u3002\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5f53\u5e74\u4ed6\u6ca1\u5949 \u8bcf\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201d\u662f\u5440\uff0c\u6211\u4e5f\u60f3\u4e0d\u5230\u4ed6\u90a3\u4e48\u5927\u80c6\uff0c\u7adf\u6562\u6495\u7834\u8bcf\u4e66\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u90a3\u4f60\u600e\u80fd\u5bb9\u4ed6\u5982\u6b64\u653e\u8086\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u80fd\u5bb9\u4ed6\u5982\u6b64\u653e\u8086\uff0c\u5f53\u65f6\u5c31\u8981\u5c06\u4ed6\u902e\u6355\u56de\u4eac\u3002\u4e0d\u6599\u4ed6\u975e\u4f46\u6562\u6495\u7834\u8bcf \u4e66\uff0c\u8fd8\u6562\u516c\u7136\u62d2\u6355\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u771f\u662f\u65e0\u6cd5\u65e0\u5929\uff01\u4f46\u542c\u8bf4\u8fd9\u4f4d\u6a80\u8d1d\u52d2\u6b66\u529f\u5f88\u597d\uff0c\u662f\u771f\u7684\u5417\uff1f\u201d\u4ed6\u5df2\u731c\u60f3\u5f97 \u5230\uff0c\u54c8\u5fc5\u56fe\u5b9a\u662f\u5728\u6a80\u516c\u76f4\u624b\u4e0b\u5403\u4e86\u5927\u4e8f\uff0c\u4e3a\u4e86\u66ff\u54c8\u5fc5\u56fe\u906e\u7f9e\uff0c\u552f\u6709\u62ac\u9ad8\u4ed6\u7684\u5bf9\u624b\u7684\u6b66\u529f\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u662f\u5f88\u4e0d\u9519\uff0c\u4f9d\u6211\u770b\uff0c\u672c\u56fd\u9664\u4e86\u4ee4\u53d4\u4e4b\u5916\uff0c\u6b66\u529f\u80fd\u80dc\u8fc7\u4ed6\u7684\u6050\u6015\u4e5f \u662f\u5be5\u5be5\u65e0\u51e0\u3002\u4e0d\u8fc7\uff0c\u4ed6\u7684\u6b66\u529f\u867d\u597d\uff0c\u6211\u672c\u6765\u8fd8\u662f\u53ef\u4ee5\u5c06\u4ed6\u64d2\u83b7\u7684\u3002\u53ea\u53ef\u60dc\u6211\u90a3\u4e24\u4e2a\u968f\u4ece\u672c\u4e8b \u4e0d\u6d4e\uff0c\u4ed6\u4eec\u5374\u6253\u4e0d\u8fc7\u6a80\u516c\u76f4\u7684\u4eb2\u5bb6\u548c\u513f\u5b50\u3002\u90a3\u65f6\u6a80\u516c\u76f4\u5df2\u7ecf\u7ed9\u6211\u7528\u5927\u529b\u91d1\u521a\u624b\u6253\u7684\u91cd\u4f24\uff0c \u6211\uff0c\u6211\u4e5f\u53d7\u4e86\u4e00\u70b9\u8f7b\u4f24\u3002\u4f46\u56e0\u6211\u90a3\u4e24\u4e2a\u968f\u4ece\u4e27\u547d\uff0c\u6211\uff0c\u6211\u53ea\u597d\u653e\u3001\u653e\u8fc7\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4ed6\u4e2d\u4e86\u54c8\u5927\u4eba\u7684\u5927\u529b\u91d1\u521a\u638c\uff0c\u6599\u60f3\u4e5f\u662f\u4e0d\u80fd\u6d3b\u547d\u7684\u4e86\uff01 \u3000\u3000\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u4e0d\u8fc7\u5f53\u4eca\u7687\u4e0a\u5374\u662f\u653e\u5fc3\u4e0d\u4e0b\u3002\u201d\u4ed6\u5e26\u7740\u82e6\u7b11\uff0c\u559d\u4e86\u6ee1\u6ee1\u4e00 \u676f\u9152\u3002\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u90a3\u5e74\u6211\u5949\u8001\u7687\u4e0a\u4e4b\u547d\u79d8\u5bc6\u51fa\u4eac\u4e4b\u65f6\u3002\u8001\u7687\u4e0a\u5df2\u662f\u9f99\u4f53\u6b20\u5b89\uff0c\u51c6\u5907 \u4f20\u4f4d\u7ed9\u5f53\u4eca\u7687\u4e0a\u7684\u3002\u8001\u7687\u4e0a\u6b64\u4e3e\u662f\u6050\u4ed6\u4e07\u4e00\u9a7e\u5d29\u4e4b\u540e\uff0c\u65b0\u7687\u4e0a\u5236\u4f0f\u4e0d\u4e86\u6a80\u516c\u76f4\u3002\u6545\u800c\u8d81\u5728\u751f \u4e4b\u65e5\uff0c\u6a80\u516c\u76f4\u9664\u6389\u514d\u9664\u540e\u60a3\u3002\u5f53\u7136\u6240\u8c13\u201c\u9664\u6389\u201d\uff0c\u4e0d\u4e00\u5b9a\u5c31\u662f\u5c06\u4ed6\u6740\u6389\u3002\u8001\u7687\u4e0a\u7684\u4e3b\u610f\u662f\u8981 \u5c06\u6a80\u516c\u76f4\u62bc\u56de\u4eac\u5e08\u4e4b\u540e\u3001\u518d\u884c\u5904\u7f6e\u7684\u3002\u4ed6\u548c\u8001\u7687\u4e0a\u662f\u4e2d\u8868\u4e4b\u5bd2\uff0c\u4ed6\u5728\u671d\u4e4b\u65f6\uff0c\u867d\u7136\u6709\u67d0\u4e9b\u653f \u89c1\u548c\u8001\u7687\u4e0a\u4e0d\u540c\uff0c\u5bf9\u8001\u7687\u4e0a\u4e5f\u8fd8\u662f\u6709\u51e0\u5206\u5fe0\u5fc3\uff0c\u4e0d\u6562\u5927\u8fc7\u653e\u8086\u7684\u3002\u6545\u6b64\u8001\u7687\u4e0a\u4ee5\u4e3a\u4ed6\u5f53\u4f1a\u5949 \u8bcf\uff0c\u90a3\u77e5\u4ed6\u7adf\u6562\u505a\u51fa\u8fd9\u7b49\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u5462\uff01\u5509\uff0c\u4ed6\u62d2\u4e0d\u5949\u8bcf\uff0c\u53ef\u82e6\u4e86\u6211\u4e86\uff01\u201d\u4ed6\u8ffd\u601d\u5f80\u4e8b\uff0c \u5fc3\u4e2d\u72b9\u6709\u4f59\u60b8\uff0c\u62b9\u4e86\u62b9\u989d\u4e0a\u6dcc\u51fa\u6765\u7684\u51b7\u6c57\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u62ff\u4e0d\u5230\u4eba\uff0c\u81ea\u5df1\u8fd8\u53d7\u4e86\u4f24\uff0c\u5e26\u4f24 \u8d76\u8def\uff0c\u4e24\u4e2a\u6708\u4e4b\u540e\u624d\u56de\u5230\u4eac\u5e08\uff0c\u6b63\u4e0d\u77e5\u5982\u4f55\u5411\u8001\u7687\u4e0a\u4ea4\u5dee\uff0c\u5e78\u597d\uff0c\u4e0d\uff0c\u4e0d\uff0c\u4e0d\u6599\uff0c\u4e0d\u5e78\u2014 \u2014\u201d\u4ed6\u4e00\u65f6\u95f4\u672a\u6709\u8003\u8651\uff0c\u8bf4\u51fa\u201c\u5e78\u597d\u201d\u4e8c\u5b57\uff0c\u65b9\u59cb\u7701\u89c9\u5931\u8a00\u3002 \u3000\u3000\u5b8c\u989c\u9274\u8fde\u5fd9\u66ff\u4ed6\u63a9\u9970\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u90a3\u4e00\u5e74\u6211\u4eec\u6b63\u5728\u8ba1\u5212\u5927\u4e3e\u4f10\u5b8b\uff0c\u6211\u9886\u4e00\u8def\u4eba\u9a6c\u5df2 \u7ecf\u653b\u5165\u5927\u6563\u5173\u4e86\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\uff0c\u4e0d\u6599\uff0c\u4e0d\u6599\u8001\u7687\u4e0a\u7adf\u7136\u4e0d\u5e78\u9a7e\u5d29\uff0c\u65b0\u7687\u4e0a\u5373\u4f4d\uff0c\u5b89\u5185\u91cd\u4e8e\u6518 \u5916\uff0c\u6211\u4eec\u53ea\u4e5f\u73ed\u5e08\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u5f53\u4eca\u7687\u4e0a\u5373\u4f4d\uff0c\u8981\u529e\u7684\u4e8b\u60c5\u5f88\u591a\uff0c\u4e00\u65f6\u95f4\u4e5f\u5c31\u65e0\u6687\u53bb\u7406\u4f1a\u6a80\u516c\u76f4\u7684 \u6b7b\u6d3b\u4e86\u3002\u4f46\u73b0\u5728\u53ef\u4e0d\u540c\u4e86\uff0c\u5b8c\u989c\u5c06\u519b\uff0c\u6211\u60f3\u4f60\u5e94\u8be5\u61c2\u5f97\u6211\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u7687\u4e0a\uff0c\u6587\u624d\u6b66\u7565\uff0c\u6bd4\u8d77\u8001\u7687\u4e0a\u53ea\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u7528\u3002\u5728\u5f53 \u4eca\u7687\u4e0a\u52b1\u7cbe\u56fe\u6cbb\u4e4b\u4e0b\uff0c\u56fd\u5bb6\u5df2\u662f\u5b89\u5982\u78d0\u77f3\u3002\u201d\u5176\u5b9e\u8fd9\u4e00\u5927\u4e32\u8bf4\u8bdd\uff0c\u53ea\u987b\u56db\u4e2a\u5b57\u5c31\u53ef\u4ee5\u8bf4\u660e \u767d\uff0c\u65e0\u975e\u662f\u6307\u65b0\u7687\u4e0a\u7684\u5730\u4f4d\u5df2\u7ecf\u5de9\u56fa\uff0c\u4e0d\u8fc7\uff0c\u201c\u5730\u4f4d\u5de9\u56fa\u201d\u8fd9\u56db\u4e2a\u5b57\u5374\u662f\u4e0d\u80fd\u7531\u81e3\u5b50\u6765\u201c\u5984 \u52a0\u8bae\u8bba\u201d\u7684\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u56fd\u5bb6\u5b89\u5982\u78d0\u77f3\uff0c\u5f53\u4eca\u7687\u4e0a\u7ee7\u627f\u8001\u7687\u4e0a\u7684\u9057\u5fd7\uff0c\u8981\u5174\u5e08\u4f10\u5b8b \u4e86\u3002\u65e2\u7136\u51c6\u5907\u4f10\u5b8b\uff0c\u6a80\u516c\u76f4\u7684\u6b7b\u6d3b\u5c31\u5fc5\u987b\u5f04\u6e05\u695a\u4e86\u3002\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u5f53\u7136\u77e5\u9053\uff0c\u6a80\u516c\u76f4\u5728\u4e8c\u5341 \u591a\u5e74\u524d\u662f\u505a\u8fc7\u5175\u9a6c\u5927\u5143\u5e05\u7684\uff0c\u76ee\u524d\u4e5f\u8fd8\u6709\u8bb8\u591a\u5e26\u5175\u7684\u5c06\u9886\u662f\u4ed6\u7684\u90e8\u4e0b\uff0c\u4ed6\u5bf9\u519b\u5fc3\u7684\u5f71\u54cd\u3002\u4e0d \u80fd\u5ffd\u89c6\uff01\u201d \u3000\u3000\u8fd9\u6837\u8bf4\u5176\u5b9e\u662f\u548c\u201c\u5b89\u5982\u78d0\u77f3\u201d\u56db\u4e2a\u5b57\u6709\u77db\u76fe\u7684\uff0c\u4f46\u54c8\u5fc5\u56fe\u5df2\u662f\u60f3\u4e0d\u5230\u66f4\u597d\u7684\u8bf4\u6cd5\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u76ee\u524d\u7684\u7b2c\u4e00\u4ef6\u4e8b\u5c31\u662f\u8981\u67e5\u660e\u6a80\u516c\u76f4\u7684\u6b7b\u6d3b\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u5f53\u5e74\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u793a\u77e5\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5c31\u5728\u4f60\u7ba1\u8f96\u7684\u9ad8\u5dde\u5883\u5185\uff0c\u63a5\u8fd1\u5927\u6563\u5173\u7684\u76d8\u9f99\u5c71\u4e0a\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5927\u4eba\u8981\u4e0d\u8981\u6211\u6d3e\u5175\u524d\u5f80\u76d8\u9f99\u5c71\u641c\u67e5\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6211\u60f3\u6a80\u516c\u76f4\u6ca1\u6709\u8fd9\u6837\u7b28\uff0c\u5373\u4f7f\u4ed6\u6ca1\u6709\u6b7b\uff0c\u6599\u60f3\u4e5f\u4e0d\u6562\u85cf\u5728\u76d8\u9f99\u5c71\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\uff0c\u662f\u3001\u591a\u8c22\u5927\u4eba\u6559\u5bfc\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5fd9\u628a\u8bed\u6c14\u515c\u56de\u6765\u9053\uff1a\u201c\u5c06\u519b\uff0c\u4f60\u522b\u8bef\u4f1a\uff0c\u6211\u4e0d\u662f\u8bf4\u4f60\u8fd9\u662f\u7b28\u4e3b\u610f\u2014\u2014\u201d \u3000\u3000\u201c\u4e3a\u4e86\u5fe0\u541b\u4e4b\u7231\uff0c\u5373\u4f7f\u660e\u77e5\u4ed6\u4e0d\u4f1a\u8eb2\u5728\u76d8\u9f99\u5c71\u4e0a\uff0c\u4e3a\u4e86\u9884\u9632\u4e07\u4e00\uff0c\u54b1\u4eec\u4e5f\u5e94\u8be5\u53bb\u67e5\u4e00\u67e5 \u7684\u3002\u4e0d\u8fc7\u6211\u5df2\u7ecf\u6d3e\u4eba\u53bb\u67e5\u8fc7\u4e86\u3002\u8981\u662f\u518d\u6d3e\u5175\u53bb\uff0c\u90a3\u5c31\u6050\u6015\u8981\u6253\u8349\u60ca\u86c7\u5566\uff01\u201d\u54c8\u5fc5\u56fe\u9053\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5546\u5dde\u7684\u6237\u53e3\u662f\u7f16\u6709\u540d\u518c\u7684\uff0c\u5f85\u6211\u518d\u4e0b\u4e00\u9053\u4e25\u4ee4\uff0c\u8981\u4ed6\u4eec\u6ce8\u610f\u53ef\u7591\u7684\u6237\u53e3\u3002 \u5018\u82e5\u6a80\u516c\u76f4\u8fd8\u6ca1\u6b7b\u6389\uff0c\u4ed6\u6562\u85cf\u5728\u5546\u5dde\u7684\u8bdd\uff0c\u6211\u4e00\u5b9a\u628a\u4ed6\u63ea\u51fa\u6765\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5c06\u519b\u80af\u8fd9\u6837\u5c3d\u5fc3\u5c3d\u529b\uff0c\u81ea\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u4f46\u4e5f\u8981\u907f\u514d\u5f20\u626c\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5351\u804c\u61c2\u5f97\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u82e5\u6709\u6240\u601d\uff0c\u6c89\u541f\u534a\u664c\uff0c\u65b9\u59cb\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6ce8\u610f\u53ef\u7591\u7684\u6237\u53e3\u662f\u4e00\u4e2a\u529e\u795b\uff0c\u4f46\u6050\u6015 \u8981\u5f88\u5927\u7684\u4eba\u529b\uff0c\u5374\u672a\u5fc5\u80fd\u591f\u5f97\u5230\u7ed3\u679c\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u987a\u7740\u4ed6\u7684\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u6240\u8651\u751a\u662f\uff0c\u6ce8\u610f\u53ef\u7591\u7684\u6237\u53e3\uff0e\u4e0d\u8fc7\u662f\u6ca1\u6709\u529e\u6cd5\u4e2d\u7684\u529e \u6cd5\u7f62\u4e86\u3002\u4f9d\u6211\u60f3\uff0c\u90a3\u6a80\u516c\u76f4\u5df2\u7ecf\u4e2d\u4e86\u5927\u4eba\u7684\u91d1\u521a\u62ff\u3002\u4ed6\u6d3b\u4e0b\u53bb\u7684\u5e0c\u671b\u5b9e\u662f\u5fae\u4e4e\u5176\u5fae\uff0c\u4e0d\u8fc7\u4ed6 \u7684\u6b7b\u82e5\u4e0d\u67e5\u660e\u5c5e\u5b9e\uff0c\u53c8\u4e0d\u80fd\u89e3\u7687\u4e0a\u4e4b\u5fe7\uff0c\u54b1\u4eec\u505a\u81e3\u5b50\u53ea\u80fd\u5c3d\u529b\u800c\u4e3a\u3002\u4e0d\u77e5\u5927\u4eba\u8fd8\u6709\u66f4\u597d\u7684\u529e \u6cd5\uff0c\u53ef\u4ee5\u8fc5\u901f\u660e\u771f\u76f8\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5ffd\u9053\uff1a\u201c\u6211\u4eec\u662f\u8fd8\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u4e0d\u8fc7\u4f60\u542c\u4e86\u53ef\u522b\u5403\u60ca\u3002\u201d\u5f53\u4e0b\u5c0f\u58f0\u8bf4\u4e86\u51e0\u53e5\u3002 \u3000\u3000\u5c3d\u7ba1\u4ed6\u5df2\u6709\u201c\u9884\u544a\u201d\uff0c\u5b8c\u989c\u9274\u542c\u4e86\uff0c\u4ecd\u662f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8fd9\u6839\u7ebf\u7d22\uff0c\u7adf \u7136\u662f\u5728\u5b8b\u56fd\u7684\u8fb9\u5173\u603b\u5175\u7684\u5b98\u8859\u4e4b\u5185\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5927\u60ca\u5c0f\u602a\uff0c\u6211\u8fd8\u53ef\u4ee5\u544a\u8bc9\u4f60\u4e00\u4e2a\u79d8\u5bc6\uff0c\u5728\u6211\u4e0a\u76d8\u9f99\u5c71\u627e\u6a80\u516c\u76f4\u90a3 \u5929\uff0c\u5b8b\u56fd\u4e5f\u6d3e\u4e86\u56db\u4e2a\u536b\u58eb\u524d\u53bb\uff0c\u4e0d\u8fc7\uff0c\u6211\u5728\u5148\uff0c\u4ed6\u4eec\u5728\u540e\uff0c\u6211\u662f\u5728\u4e0b\u5c71\u4e4b\u65f6\uff0c\u624d\u78b0\u4e0a\u4ed6\u4eec \u7684\u3002\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u53bb\u627e\u6a80\u516c\u76f4\u505a\u4ec0\u4e48\uff1f\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u627e\u7684\u662f\u53e6\u4e00\u4e2a\u4eba\uff0c\u8fd9 \u4e2a\u4eba\u6070\u597d\u662f\u6a80\u516c\u76f4\u7684\u5ab3\u5987\u3002\u4f60\u522b\u5403\u60ca\uff0c\u4e5f\u5148\u522b\u591a\u95ee\uff0c\u5f85\u4f1a\u513f\u6211\u518d\u544a\u8bc9\u4f60\u4ed6\u7684\u5ab3\u5987\u662f\u4ec0\u4e48\u4eba\u3002 \u8fd9\u4e2a\u4eba\u662f\u79e6\u6867\u63d0\u62e8\u7684\uff0c\u4f60\u77e5\u9053\u7684\uff0c\u79e6\u6867\u751f\u524d\u662f\u548c\u54b1\u4eec\u6709\u8054\u7edc\uff0c\u6240\u4ee5\u6211\u8ba4\u8bc6\u4ed6\u4eec\u5f53\u4e2d\u7684\u4e00\u4e2a\u3002 \u4ed6\u4eec\u8d77\u521d\u4e0d\u77e5\u9053\u6a80\u516c\u76f4\u7684\u8eab\u4efd\uff0c\u4e5f\u662f\u6211\u544a\u8bc9\u4ed6\u4eec\u3002\u90a3\u65f6\u6a80\u516c\u76f4\u5df2\u7ecf\u53d7\u4e86\u4f24\uff0c\u4f9d\u6211\u731c\u60f3\uff0c\u6a80\u516c \u76f4\u53ef\u80fd\u5df2\u7ecf\u7ed9\u4ed6\u4eec\u6253\u6b7b\uff0c\u4e5f\u53ef\u80fd\u662f\u4e24\u8d25\u4ff1\u4ea1\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u548c\u4ed6\u4eec\u8054\u7edc\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u4eba\u6c93\u97f3\u8baf\uff0c\u6211\u4eec\u7684\u4eba\u5230\u76d8\u9f99\u5c71\u67e5\u8fc7\uff0c\u4e5f\u6ca1\u53d1\u73b0\u4ed6\u4eec\u7684\u5c38\u4f53\u3002\u201d \u3000\u3000\u201c\u636e\u5b8b\u56fd\u6765\u7684\u6d88\u606f\uff0c\u90a3\u56db\u4e2a\u4eba\u662f\u4e00\u53bb\u65e0\u8e2a\uff0c\u6050\u6015\u662f\u5df2\u7ecf\u6b7b\u4e86\u3002\u53e6\u5916\u4e00\u4e2a\u5b8b\u56fd\u6765\u7684\u6d88\u606f\uff0c \u6a80\u516c\u76f4\u90a3\u4e2a\u6c49\u4eba\u4eb2\u5bb6\uff0c\u5927\u6982\u5341\u4e5d\u4ea6\u5df2\u6b7b\u4e86\u3002\u4e0d\u8fc7\u6d88\u606f\u7684\u6765\u6e90\u8bed\u7109\u4e0d\u8be6\uff0c\u8fd9\u4ea6\u5373\u662f\u8bf4\uff0c\u6211\u4eec\u8fd8 \u6ca1\u6709\u548c\u5728\u5b8b\u56fd\u90a3\u8fb9\u66ff\u54b1\u4eec\u505a\u4e8b\u7684\u6bd4\u8f83\u91cd\u8981\u7684\u4eba\u7269\u76f4\u63a5\u89c1\u8fc7\u9762\u3002\u4f46\u73b0\u5728\u5374\u662f\u63a5\u4e0a\u7ebf\u5934\u7684\u673a\u4f1a \u4e86\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4ed6\u7684\u58f0\u97f3\u66f4\u4f4e\uff0c\u5dee\u4e0d\u591a\u5df2\u662f\u63a5\u8fd1\u4e8e\u201c\u8033\u8bed\u201d\u7684\u7a0b\u5ea6\u4e86\u3002 \u3000\u3000\u201c\u79e6\u6867\u5728\u751f\u65f6\u7684\u4e00\u4e2a\u5fc3\u8179\u536b\u571f\u548c\u54b1\u4eec\u4ee5\u524d\u4e5f\u662f\u6709\u8054\u7edc\u7684\uff0c\u4ed6\u73b0\u5728\u7684\u8eab\u4efd\u662f\u5b8b\u56fd\u7684\u4e00\u7b49\u5927 \u5185\u536b\u571f\u3002\u6700\u8fd1\u6d3e\u6765\u8fb9\u5173\u505a\u76d1\u519b\uff0c\u4e0d\u8fc7\u4ed6\u7684\u76d1\u519b\u8eab\u4efd\u4e5f\u4e0d\u662f\u516c\u5f00\u7684\uff0c\u4f60\u53ef\u4ee5\u6d3e\u4eba\u6697\u5730\u91cc\u53bb\u89c1 \u4ed6\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4f1a\u77e5\u9053\u6a80\u516c\u76f4\u7684\u6b7b\u751f\u4e4b\u8c1c\uff0c\u5982\u679c\u6853\u516c\u76f4\u9003\u5230\u5b8b\u56fd\uff0c\u8bf7\u4ed6\u4fa6\u67e5\u4e5f\u5bb9\u6613\u4e9b\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u5927\u559c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4eba\u5728\u5b8b\u56fd\u8fb9\u5173\uff0c\u771f\u662f\u5929\u52a9\u54b1\u4eec\u5927\u91d1\u4e86\u3002\u83ab\u8bf4\u53ef\u4ee5\u6253\u63a2\u6a80\u516c\u76f4 \u7684\u6d88\u606f\uff0c\u5373\u4f7f\u4ed6\u6beb\u65e0\u6240\u77e5\uff0c\u6211\u4e5f\u662f\u5fc5\u987b\u548c\u4ed6\u63a5\u4e0a\u7ebf\u7684\u3001\u8fd9\u4e2a\u4eba\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u5728\u5929\u9999\u4ead\u5185\u7ec6\u8bed\u5581\u5581\uff0c\u90a3\u4e2a\u5728\u82b1\u4e1b\u4e2d\u4fee\u526a\u82b1\u8349\u7684\u5973\u4ec6\u4fa7\u8033\u7ec6\u542c\uff0c\u542c\u4e0d\u6e05\u695a\uff0c\u7d22\u6027\u4f0f \u5730\u542c\u58f0\uff0c\u4f46\u53ef\u60dc\u5f97\u5f88\uff0c\u8bdd\u8bed\u5012\u662f\u65ad\u65ad\u7eed\u7eed\u542c\u5230\u51e0\u53e5\uff0c\u90a3\u4e2a\u91d1\u56fd\u5978\u7ec6\u7684\u540d\u5b57\u5374\u542c\u4e0d\u89c1\u3002\u5979\u6015\u7ed9 \u53d1\u73b0\uff0c\u4e0d\u6562\u4f0f\u5730\u8fc7\u4e45\uff0c\u5f85\u5230\u54c8\u5fc5\u56fe\u548c\u5b8c\u989c\u9274\u8bf4\u8bdd\u8f83\u4e3a\u5927\u58f0\u4e4b\u65f6\uff0c\u5979\u5c31\u7ad9\u4e86\u8d77\u6765\u91cd\u65b0\u4fee\u526a\u82b1\u8349 \u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u54c8\u5fc5\u56fe\u8bf4\u9053\uff1a\u201c\u6a80\u516c\u76f4\u662f\u6b7b\u662f\u6d3b\u6211\u4eec\u6682\u4e14\u4e0d\u7ba1\uff0c\u4f46\u4ed6\u7684\u5ab3\u5987\u90a3\u5929\u4e1d\u6beb\u6ca1\u6709\u53d7\u4f24\uff0c \u6599\u60f3\u662f\u8fd8\u5728\u4eba\u95f4\u7684\u3002\u8fd9\u4e2a\u5a18\u513f\u8eab\u4efd\u7684\u91cd\u8981\uff0c\u7eb5\u7136\u4e0d\u80fd\u8bf4\u662f\u8d85\u8fc7\u6a80\u516c\u76f4\uff0c\u6050\u6015\u4e5f\u4e0d\u5728\u6a80\u516c\u76f4\u4e4b \u4e0b\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u8bf4\u9053\uff1a\u201c\u5979\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5979\u7684\u7236\u4eb2\u662f\u5f20\u5baa\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u5f20\u5baa\u4e0d\u662f\u5cb3\u98de\u7684\u5973\u5a7f\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u548c\u5cb3\u98de\u4e00\u540c\u5728\u98ce\u6ce2\u4ead\u88ab\u79e6\u6867\u6240\u6740\u7684\u5f20\u5baa\u3002\u56e0\u6b64\u6a80\u516c\u76f4\u8fd9\u4e2a\u5ab3\u5987 \u672c\u867d\u7136\u65e0\u8db3\u8f7b\u91cd\uff0c\u4f46\u56e0\u5979\u7684\u5916\u516c\u7684\u5cb3\u98de\uff0c\u5979\u7684\u8eab\u4efd\u5c31\u91cd\u8981\u4e86\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u5cb3\u98de\u7684\u5916\u5b59\u5973\u513f\uff0c\u7adf\u7136\u4f1a\u5ac1\u7ed9\u54b1 \u4eec\u5927\u91d1\u56fd\u7684\u4e00\u4e2a\u8d1d\u5b50\uff01\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u662f\u5440\uff0c\u5979\u505a\u522b\u4e2a\u4eba\u5bb6\u7684\u5ab3\u5987\u4e5f\u8fd8\u7f62\uff0c\u505a\u6a80\u516c\u76f4\u7684\u513f\u5ab3 \u5987\uff0c\u90a3\u5c31\u66f4\u52a0\u53ef\u8651\u55661\u201d \u3000\u3000\u5b8c\u989c\u9274\u77bf\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5cb3\u98de\u7684\u65e7\u90e8\u4e5f\u8fd8\u6709\u8bb8\u591a\u5728\u751f\u7684\uff0c\u6709\u7684\u5df2\u7ecf\u53d8\u6210\u8349\u91ce\u4e4b \u96c4\uff0c\u6709\u7684\u5219\u8fd8\u5728\u5b8b\u56fd\u519b\u4e2d\u4efb\u4e8b\uff0c\u6a80\u516c\u76f4\u4e3a\u513f\u5b50\u8ba8\u8fd9\u95e8\u5ab3\u5987\uff0c\u5176\u5fd7\u6050\u4e0d\u5728\u5c0f\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u60f3\u5229 \u7528\u5cb3\u98de\u7684\u5916\u5b59\u5973\u513f\uff0c\u8054\u5408\u5cb3\u98de\u7684\u65e7\u90e8\uff0c\u548c\u54b1\u4eec\u4f5c\u5bf9\u3002\u201d\u54c8\u5fc5\u56fe\u901a\uff1a\u201c\u5982\u679c\u5979\u7684\u516c\u516c\u548c\u4e08\u592b\u6b7b \u4e86\uff0c\u5979\u5c31\u4f1a\u66f4\u52a0\u4ec7\u6068\u54b1\u4eec\u5927\u91d1\uff0c\u7528\u4e0d\u7740\u5979\u7684\u516c\u516c\u6307\u4f7f\uff0c\u5979\u4e00\u6837\u4e5f\u8981\u548c\u54b1\u4eec\u4f5c\u5bf9\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u61c2\uff0c\u5979\u672c\u4eba\u867d\u7136\u4e0d\u662f\u4ec0\u4e48\u5962\u680f\uff08\u4e86\u4e0d\u8d77\uff09\u4eba\u7269\uff0c\u4f46\u56e0\u5979\u662f\u5cb3\u98de\u7684\u5916\u5b59\u5973 \u513f\uff0c\u5979\u7684\u8eab\u4efd\u5c31\u91cd\u8981\u4e86\u3002\u54b1\u4eec\u4e0d\u80fd\u8ba9\u5979\u53d7\u4eba\u5229\u7528\uff0c\u505a\u51fa\u5bf9\u54b1\u4eec\u5927\u91d1\u4e0d\u5229\u7684\u4e8b\uff0c\u6240\u4ee5\u5c31\u5fc5\u987b\u5c06 \u5979\u9664\u53bb\uff0c\u4ee5\u514d\u540e\u60a3\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u8ddf\u7740\u95ee\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6a80\u516c\u76f4\u662f\u54b1\u4eec\u5168\u56fd\u7684\u8001\u8d1d\u52d2\uff0c\u6709\u8bb8\u591a\u4eba\u8ba4 \u8bc6\u4ed6\uff0c\u4f46\u4ed6\u8fd9\u4e2a\u5ab3\u5987\uff0c\u6211\u7684\u624b\u4e0b\u5374\u662f\u6ca1\u6709\u4eba\u89c1\u8fc7\u5979\u7684\uff0c\u5982\u4f55\u624d\u80fd\u5c06\u5979\u7f09\u62ff\u5f52\u6848\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u5979\uff0c\u6211\u51ed\u81ea\u5df1\u7684\u8bb0\u5fc6\u5df1\u7ecf\u8bf7\u4e00\u4f4d\u753b\u5e08\u753b\u51fa\u4e86\u5979\u7684\u5bb9\u8c8c\uff0c\u73b0\u5728\u6211\u5c31\u628a \u8fd9\u5f20\u753b\u56fe\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u5c55\u5f00\u753b\u56fe\u4e00\u770b\uff0c\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u5cb3\u98de\u7684\u5973\u5a7f\u5f20\u5baa\u662f\u4e00\u5458\u52c7\u731b\u7edd\u4f26\u7684\u864e\u5c06\uff0c\u60f3\u4e0d\u5230\u4ed6 \u7684\u5916\u5b59\u5973\u513f\uff0c\u7adf\u7136\u8fd8\u957f\u5f97\u76f8\u5f53\u6f02\u4eae\u5462\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5979\u7684\u540d\u5b57\u6211\u4e5f\u67e5\u51fa\u6765\u4e86\uff0c\u662f\u76d8\u9f99\u5c71\u7684\u730e\u6237\u8bf4\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u53eb\u5f20\u96ea\u6ce2\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u53eb\u5f20\u96ea\u6ce2\uff1f\u54e6\uff0c\u6211\u61c2\u4e86\uff0c\u8fd9\u4e2a\u540d\u5b57\u662f\u542b\u6709\u6df1\u610f\u7684\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u542b\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5cb3\u98de\u548c\u5f20\u5baa\u4e0d\u662f\u540c\u65f6\u540c\u5730\u5728\u98ce\u6ce2\u4ead\u88ab\u79e6\u6867\u5bb3\u6b7b\u7684\u5417\uff1f\u96ea\u6ce2\u7684\u610f\u601d\u5c31\u662f\u8981\u96ea \u98ce\u6ce2\u4ead\u4e4b\u6068\uff01\u201d \u3000\u3000\u90a3\u4e2a\u4fee\u526a\u679d\u7684\u5973\u4ec6\u542c\u89c1\u201c\u5f20\u96ea\u6ce2\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e0d\u89c9\u9661\u7136\u4e00\u9707\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u53c8\u628a\u4e00\u679d \u4e0d\u8be5\u526a\u7684\u679d\u5934\u4e0a\u5f00\u6709\u7261\u4e39\u7684\u82b1\u679d\u526a\u65ad\u4e86\u3002 \u3000\u3000\u5929\u9999\u4ead\u91cc\u5df2\u7ecf\u6ca1\u6709\u95f2\u6742\u4eba\u58f0\uff0c\u8fd9\u6b21\u53ef\u662f\u5f15\u8d77\u4e86\u54c8\u5fc5\u56fe\u7684\u6ce8\u610f\u4e86\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u62ac\u8d77\u5934\u6765\uff0c\u628a\u773c\u671b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u8eb2\u5728\u82b1\u4e1b\u91cc\u7684\u5973\u4eba\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u4e13\u53f8\u6599\u7406\u7261\u4e39\u7684\u5973\u4ec6\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u54e6\uff0c\u5979\u4f1a\u79cd\u82b1\uff1f\u5979\u662f\u6c49\u4eba\u7684\u5973\u5b50\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u5979\u662f\u91d1\u4eba\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5979\u662f\u2018\u5bb6\u751f\u2019\u7684\u8fd8\u662f\u4e70\u6765\u7684\uff1f\u201d\u5f53\u65f6\u4e00\u822c\u5bcc\u8d35\u4eba\u5bb6\u7684\u5974\u5a62\u5206\u4e3a\u4e24\u79cd\uff0c\u4e00\u79cd \u662f\u7528\u94b1\u4e70\u6765\u7684\uff0c\u4e00\u79cd\u662f\u539f\u6709\u7684\u5974\u5a62\u751f\u4e0b\u7684\u513f\u5973\uff0c\u4e00\u751f\u4e0b\u6765\uff0c\u8eab\u4efd\u4e5f\u6ce8\u5b9a\u662f\u5974\u5a62\u7684\u4e86\uff0c\u8fd9\u79cd\u5974 \u5a62\uff0c\u79f0\u4e3a\u201c\u5bb6\u751f\u201d\u5974\u5a62\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u5bf9\u4e00\u4e2a\u5973\u4ec6\u95ee\u5f97\u8fd9\u6837\u4ed4\u7ec6\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u8005\u90fd\u4e0d\u662f\u3002 \u3000\u3000\u5979\u672c\u6765\u662f\u4e2a\u96be\u6c11\uff0c\u5185\u5b50\u89c1\u5979\u53ef\u601c\uff0c\u6536\u5bb9\u5979\u7684\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5979\u5f88\u5f97\u592b\u4eba\u5ba0\u7231\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u7684\uff0c\u5185\u5b50\u89c1\u5979\u6709\u51e0\u5206\u6c14\u529b\uff0c\u53c8\u4f1a\u683d\u82b1\u526a\u8349\uff0c\u6240\u4ee5\u6536\u4e86\u5979\u505a\u8d34\u8eab\u5973\u4ec6\u3002\u201d \u5176\u5b9e\u8fd9\u4e2a\u5973\u4ec6\u4e4b\u6240\u4ee5\u4f1a\u201c\u683d\u82b1\u526a\u8349\u201d\uff0c\u8fd8\u662f\u5230\u4e86\u5979\u7684\u5bb6\u4e2d\u4e4b\u540e\u624d\u5b66\u4f1a\u7684\uff0c\u4e0d\u8fc7\u5b8c\u989c\u9274\u6050\u6015\u60f9 \u8d77\u54c8\u5fc5\u56fe\u7684\u591a\u7591\uff0c\u7d2f\u53ca\u4ed6\u59bb\u5b50\uff0c\u6545\u800c\u6ca1\u6709\u8be6\u7ec6\u8bf4\u660e\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u53eb\u5979\u6765\uff01\u201d \u3000\u3000\u5b8c\u989d\u9274\u53eb\u9053\uff1a\u201c\u5170\u59d1\uff0c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u540d\u5b57\u53eb\u201c\u5170\u59d1\u201d\u7684\u5973\u82b1\u5320\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca\uff0c\u5e94\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u53eb\u6211\u5417\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u4e0d\u89c9\u76b1\u8d77\u7709\u5934\uff0c\u539f\u6765\u8fd9\u4e2a\u201c\u5170\u59d1\u201d\u7684\u540d\u5b57\u867d\u7f8e\uff0c\u58f0\u97f3\u5374\u50cf\u7834\u7f50\u4e00\u822c\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u82b1\u56ed\u91cc\u53c8\u6ca1\u6709\u522b\u7684\u4eba\uff0c\u5f53\u7136\u662f\u53eb\u4f60\u3002\u4f60\u4e0d\u5fc5\u60ca\u614c\uff0c\u8fd9\u4f4d\u54c8\u5927\u4eba\u6709\u8bdd\u95ee \u4f60\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5170\u59d1\u662f\u5426\u60ca\u614c\u4e0d\u5f97\u800c\u77e5\uff0c\u4f46\u5f53\u5979\u8d70\u5230\u54c8\u5fc5\u56fe\u9762\u524d\u7684\u65f6\u5019\uff0c\u54c8\u5fc5\u56fe\u5012\u662f\u88ab\u5979\u5413\u4e86\u4e00\u60ca \u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u8c8c\u4e11\u7684\u5973\u4eba\uff0c\u8138\u4e0a\u6a2a\u4e03\u7ad6\u516b\u7684\u6709\u8bb8\u591a\u75ae\u75a4\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5bf9\u7261\u4e39\u82b1\u61c2\u5f97\u5f88\u591a\u3002\u662f\u5417\uff1f\u201d \u3000\u3000\u5170\u59d1\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u56ed\u5b50\u91cc\u7684\u7261\u4e39\u82b1\u90fd\u662f\u6211\u6599\u7406\u7684\uff0c\u7a0d\u5fae\u61c2\u5f97\u4e00\u4e9b\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u597d\uff0c\u6211\u548c\u5c06\u519b\u6b63\u8981\u53bb\u8d4f\u7261\u4e39\uff0c\u8bf7\u4f60\u4f5c\u966a\uff0c\u7ed9\u6211\u4eec\u89e3\u91ca\u3002 \u3000\u3000\u201d \u3000\u3000\u5170\u59d1\u9053\uff1a\u201c\u5974\u5a62\u9075\u547d\uff0c\u8bf7\u5b57\u53ef\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u542c\u5979\u53e3\u97f3\uff0c\u867d\u7136\u6781\u4e3a\u96be\u542c\uff0c\u5374\u7684\u786e\u662f\u5546\u5dde\u4e00\u822c\u571f\u751f\u571f\u957f\u7684\u91d1\u4eba\u53e3\u97f3\u3002 \u3000\u3000\u8fd9\u4e2a\u5170\u59d1\u966a\u4ed6\u4eec\u53bb\u8d4f\u7261\u4e39\uff0c\u679c\u7136\u662f\u6709\u95ee\u5fc5\u7b54\u3002 \u3000\u3000\u5979\u6307\u51fa\u4e86\u8bb8\u591a\u8457\u540d\u7684\u7261\u4e39\u54c1\u79cd\uff1a\u6cf0\u7ea2\u3001\u59da\u9ec4\u3001\u91d1\u7c89\u3001\u767d\u7389\u3001\u4e8c\u4e54\u3001\u7476\u6c60\u6625\u3001\u9732\u73e0\u7c89\u3001\u84dd \u7530\u7389\u3001\u94f6\u76cf\u91d1\u9f99\u2026\u2026\u6700\u540e\u6307\u7740\u4e00\u79cd\u9ed1\u7261\u4e39\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6700\u540d\u8d35\u7684\u4e00\u79cd\u3001\u53eb\u505a\u9752\u9f99\u5367\u58a8\u6c60\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u79cd\u9ed1\u7261\u4e39\u6211\u5728\u5fa1\u82d1\u4e16\u89c1\u8fc7\uff0c\u53ef\u60dc\u53ea\u5f00\u4e86\u4e00\u5e74\u5c31\u67af\u840e\u4e86\u3002\u90a3\u5e74\u5f00\u7684\u82b1\u4e5f\u6ca1 \u4f60\u8fd9\u679d\u9ed1\u7261\u4e39\u597d\u770b\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u201c\u9752\u9f99\u5367\u58a8\u6c60\u201d\u7684\u82b1\u540d\u56e0\u4e3a\u6bd4\u8f83\u7279\u522b\uff0c\u4ed6\u8fd8\u8bb0\u5f97\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e2a\u5973\u82b1\u5320\u5012 \u4e0d\u662f\u5192\u5145\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u79cd\u9ed1\u7261\u4e39\u7684\u539f\u4ea7\u5730\u662f\u5728\u5c71\u4e1c\u83cf\u6cfd\uff0c\u82b1\u8c31\u4e0a\u4e5f\u6709\u540d\u7684\u3002 \u3000\u3000\u6709\u8fd9\u6837\u4e24\u53e5\u8bdd\u8bf4\uff1a\u201c\u8377\u6cfd\u7261\u4e39\u7532\u5929\u4e0b\uff0c\u5929\u4e0b\u7261\u4e39\u51fa\u8377\u6cfd\u3002\u2019\u4f46\u53ef\u60dc\u6216\u8005\u662f\u56e0\u63a5\u79cd\u4e0d\u5f97\u5176 \u6cd5\uff0c\u8377\u6cfd\u7684\u540d\u79cd\u7261\u4e39\u79fb\u690d\u5916\u5730\uff0c\u5927\u90fd\u4e0d\u80fd\u751f\u957f\u3002\u8fd9\u679d\u9ed1\u7261\u4e39\u80fd\u591f\u6210\u957f\u3001\u76db\u5f00\uff0c\u8bf4\u8d77\u6765\u8fd8\u662f\u9760 \u4e86\u5170\u59d1\u7684\u529f\u52b3\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u54e6\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u5012\u771f\u662f\u4e13\u5bb6\u4e86\uff0c\u5982\u4f55\u57f9\u690d\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u6211\u4e5f\u60f3\u77e5\u9053 \u5462\u3002\u201d \u3000\u3000\u5170\u59d1\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u56f0\u96be\uff0c\u7261\u4e39\u662f\u559c\u6b22\u751f\u957f\u4e8e\u9633\u5149\u5145\u8db3\uff0c\u6392\u6c34\u826f\u597d\uff0c\u571f\u58e4\u6df1\u539a\u80a5 \u6c83\u7684\u571f\u58e4\u4e2d\u7684\u3002\u8fd9\u91cc\u7684\u571f\u58e4\u90fd\u662f\u7ecf\u8fc7\u52a0\u5de5\u65bd\u80a5\u7684\u3002\u5728\u6ca1\u6709\u9633\u5149\u7684\u9634\u5929\uff0c\u6211\u4eec\u5c31\u5229\u7528\u70ad\u57f9\u7684\u65b9 \u6cd5\u8ba9\u5b83\u5f97\u5230\u6696\u6c14\u3001\u9009\u79cd\u65f6\u9009\u53d6\u5728\u539f\u5730\u5df2\u5b9a\u690d\u751f\u957f\u4e86\u4e09\u56db\u5e74\u7684\u7261\u4e39\uff0c\u7528\u79cd\u5b50\u80b2\u82d7\u548c\u5206\u682a\u7684\u65b9\u6cd5 \u7e41\u6b96\uff0c\u5206\u679d\u7e41\u6b96\u7684\u65f6\u95f4\u4e5f\u8981\u6ce8\u610f\uff0c\u5fc5\u987b\u662f\u5728\u6bcf\u5e74\u79cb\u5206\u81f3\u5bd2\u9732\u4e4b\u95f4\u3002\u6316\u51fa\u6839\u90e8\uff0c\u526a\u4e0b\u7c97\u6839\u3002\u5b58 \u4e0b\u7ec6\u6839\uff0c\u89c6\u6bcf\u8538\u82bd\u5934\u591a\u5c11\uff0c\u6309\u5176\u751f\u957f\u60c5\u51b5\u7528\u7af9\u5200\u5c06\u6839\u8538\u5206\u5f00\u82e5\u5e72\u5757\uff0c\u6bcf\u5757\u4fdd\u7559\u4e8c\u4e09\u4e2a\u65b0\u82bd\u79fb \u683d\u3002\u82e5\u662f\u7528\u79cd\u5b50\u80b2\u82d7\u6cd5\u5219\u5fc5\u987b\u4e8e\u4e03\u6708\u4efd\u6000\u53d6\u79cd\u5b50\uff0c\u4e8e\u5f53\u5e74\u4e5d\u6708\u64ad\u4e0b\uff0c\u64ad\u79cd\uff0c\u540e\u5e7c\u7ec6\u82d7\u7ecf\u8fc7\u8db3 \u4e8c\u5e74\u751f\u957f\uff0c\u624d\u80fd\u4e8e\u4e5d\u6708\u4efd\u6216\u5341\u4efd\u8d77\u82d7\u79fb\u683d\u5b9a\u6807\u690d\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u56e0\u4e3a\u5e38\u5e38\u966a\u7687\u5e1d\u5728\u5fa1\u82d1\u82b1\uff0c\u5bf9\u82b1\u4e8b\u4e5f\u662f\u4e00\u77e5\u534a\u89e3\uff0c\u542c\u5170\u59d1\u8bf4\u884c\u5934\u5934\u662f\u9053\uff0c\u5fc3\u91cc \u60f3\uff1a\u201c\u53ef\u60dc\u5979\u957f\u5f97\u592a\u8fc7\u4e11\u964b\uff0c\u5426\u5219\u5012\u662f\u53ef\u4ee5\u5c06\u5979\u8350\u5165\u5bab\u4e2d\u5f53\u4e2a\u82b1\u5320\u3002\u201d \u3000\u3000\u4ed6\u672c\u6765\u662f\u6709\u70b9\u7591\u5fc3\uff0c\u81f3\u6b64\u65b9\u59cb\u6d88\u9664\uff0c\u5fc3\u4e2d\u6697\u81ea\u5931\u7b11\uff1a\u201c\u6211\u4e5f\u771f\u662f\u592a\u591a\u7591\u4e86\uff0c\u5979\u548c\u90a3\u4e2a\u4eba \u4e0d\u8fc7\u662f\u80cc\u5f71\u7a0d\u4e3a\u76f8\u4f3c\u800c\u5df2\uff0c\u600e\u80fd\u771f\u7684\u5c31\u662f\u90a3\u4e2a\u4eba\uff1f\u90a3\u4eba\u662f\u5728\u76d8\u9f99\u5c71\u957f\u5927\u4eba\u7684\uff0c\u6050\u6015\u538b\u6839\u513f\u5c31 \u6ca1\u89c1\u8fc7\u7261\u4e39\u3002\u9762\u8c8c\u7eb5\u7136\u53ef\u4ee5\u6539\u53d8\uff0c\u4e5f\u6539\u53d8\u4e0d\u4e86\u8fd9\u6837\u5927\uff0c\u800c\u4e14\u4ee5\u90a3\u4eba\u7684\u8eab\u4e16\u4ee5\u53ca\u906d\u9047\u4e4b\u60e8\uff0c\u5979 \u53c8\u600e\u80fd\u6709\u95f2\u5fc3\u5b66\u79cd\u7261\u4e39\uff1f\u751a\u81f3\u61c2\u5f97\u6bd4\u5fa1\u82d1\u7684\u82b1\u5320\u8fd8\u591a\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u770b\u4e86\u8fd9\u8bb8\u591a\u540d\u79cd\u7261\u4e39\uff0c\u771f\u662f\u4ee4\u6211\u5927\u9971\u773c\u798f\uff0c\u4e0d\u8fc7\u4eca\u5929\u6050\u6015\u662f\u770b\u4e0d\u5b8c\u7684\u4e86\uff0c \u4e0d\u5982\u7559\u5f85\u660e\u5929\u518d\u4ed4\u7ec6\u8d4f\u73a9\u5427\u3002\u201d \u3000\u3000\u5b8c\u989d\u9274\u4f1a\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5170\u59d1\uff0c\u4f60\u56de\u53bb\u4f3a\u5019\u592b\u4eba\u5427\uff0c\u8fd9\u4e9b\u82b1\u8349\uff0c\u660e\u5929\u4fee\u526a\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5170\u59d1\u9075\u547d\u9000\u4e0b\uff0c\u4f46\u5979\u8d70\u5230\u4e00\u5ea7\u5047\u5c71\u80cc\u540e\uff0c\u5374\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u8282\u5ea6\u8859\u7684\u82b1\u56ed\u5f88\u5927\uff0c\u7ecf\u8fc7\u8fd9\u5ea7\u5047\u5c71\uff0c\u8fd8\u8981\u8d70\u4e00\u6bb5\u82b1\u5f84\uff0c\u624d\u80fd\u8d70\u51fa\u56ed\u95e8\u3002\u4f46\u5979\u8eb2\u5728\u5047\u5c71\u540e \u9762\uff0c\u5b8c\u989c\u9274\u5df2\u662f\u770b\u4e0d\u89c1\u5979\u4e86\u3002 \u3000\u3000\u5979\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u4ec6\u4eba\u8eab\u4efd\uff0c\u5b8c\u989c\u9274\u5f53\u7136\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u5979\u6562\u8fd9\u6837\u5927\u80c6\u3002 \u3000\u3000\u800c\u4e14\u90a3\u5ea7\u5047\u5c71\u548c\u5929\u9999\u4ead\u7684\u8ddd\u79bb\u5c11\u8bf4\u4e5f\u6709\u534a\u91cc\u4e4b\u9065\uff0c\u5373\u4f7f\u6709\u4eba\u8eb2\u5728\u5047\u5c71\u540e\u9762\uff0c\u4e5f\u542c\u4e0d\u89c1\u5929 \u9999\u4ead\u8fd9\u8fb9\u7684\u8c08\u8bdd\u3002\u662f\u4ee5\u4ed6\u6839\u672c\u5c31\u6ca1\u8d77\u8fc7\u6000\u7591\uff0c\u8fd9\u4e2a\u5170\u59d1\u7adf\u7136\u6562\u5728\u5047\u5c71\u540e\u9762\u5077\u542c\u3002 \u3000\u3000\u5b8c\u989c\u9274\u548c\u54c8\u5fc5\u56fe\u56de\u5230\u4e86\u5929\u9999\u4ead\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5170\u59d1\u5012\u662f\u6709\u70b9\u672c\u4e8b\u7684\u3002\u53ea\u53ef\u60dc\u9762\u8c8c\u592a \u4e11\u3002\u54c8\u5927\u4eba\uff0c\u6211\u4ee5\u4e3a\u4f60\u53ea\u559c\u6b22\u7f8e\u5973\uff0c\u60f3\u4e0d\u5230\u4f60\u5bf9\u5979\u5012\u4e5f\u4f3c\u4e4e\u9887\u6709\u5174\u8da3\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7adf\u7136\u4e00\u672c\u6b63\u7ecf\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5bf9\u5979\u662f\u9887\u6709\u5174\u8da3\u3002\u5bf9\u5566\uff0c\u4f60\u8bf4\u5979\u662f\u96be\u6c11\uff0c\u5979\u600e \u6837\u9047\u96be\u7684\uff1f\u4f60\u548c\u5979\u53c8\u662f\u600e\u6837\u78b0\u4e0a\u7684\uff1f\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5c31\u662f\u90a3\u5e74\u6211\u4ece\u5927\u6563\u5173\u73ed\u5e08\u56de\u6765\uff0c\u5728\u8def\u4e0a \u78b0\u4e0a\u7684\u3002\u636e\u5979\u8bf4\u4ed6\u7684\u5168\u5bb6\u90fd\u5df1\u88ab\u5b8b\u5175\u6240\u6740\uff0c\u5185\u5b50\u89c1\u5979\u53ef\u601c\uff0c\u5c31\u6536\u7559\u5979\u4e86\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5979\u6ca1\u6709\u5b69\u5b50\u5417\uff1f\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u8bf4\u6f0f\u4e86\u4e00\u70b9\uff0c\u5979\u5168\u5bb6\u9047\u5bb3\uff0c\u662f\u6307\u5979\u7684\u7236 \u6bcd\u548c\u516c\u5a46\u4e08\u592b\u7b49\u4eba\u901a\u901a\u88ab\u5b8b\u5175\u6240\u6740\uff0c\u5979\u7684\u5b69\u5b50\u5012\u8fd8\u6ca1\u6709\u9047\u96be\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5979\u7684\u5b69\u5b50\u6709\u51e0\u5c81\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u6697\u6697\u5947\u602a\uff1a\u201c\u4e3a\u4ec0\u4e48\u54c8\u5fc5\u56fe\u95ee\u5f97\u8fd9\u6837\u4ed4\u7ec6\uff1f\u96be\u9053\u4ed6\u662f\u6000\u7591\u5170\u59d1\u6765\u5386\u4e0d\u660e\uff1f\u201d \u3000\u3000\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u7b54\u9053\uff1a\u201c\u5979\u6709\u4e24\u4e2a\u5b69\u5b50\uff0c\u4e00\u4e2a\u5341\u4e8c\u5c81\uff0c\u4e00\u4e2a\u4e09\u5c81\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u542c\u8bf4\u5170\u59d1\u6709\u5b69\u5b50\u4e4b\u65f6\uff0c\u672c\u6765\u53c8\u5df2\u8d77\u4e86\u51e0\u5206\u7591\uff0c\u4f46\u4e00\u542c\u5f97\u5979\u6709\u4e24\u4e2a\u5b69\u5b50\uff0c\u8fd9\u51e0\u5206\u7591 \u5fc3\u53c8\u6d88\u9664\u4e86\u3002\u4ed6\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e09\u5e74\u524d\u90a3\u5a18\u513f\u53ea\u6709\u4e00\u4e2a\u5b69\u5b50\uff0c\u5373\u4f7f\u5979\u662f\u592b\u6b7b\u518d\u5ac1\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5c31 \u751f\u51fa\u4e00\u4e2a\u4e09\u5c81\u5927\u7684\u5b69\u5b50\u6765\u3002\u201d\u4ed6\u672c\u6765\u4e0d\u662f\u7c97\u5fc3\u7684\u4eba\uff0c\u4f46\u5728\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\uff0c\u5374\u672a\u591f\u7ec6\u5fc3\u63a8\u6572\u4e86\u3002 \u4ed6\u4e00\u65f6\u95f4\u53ef\u6ca1\u60f3\u5230\uff0c\u8fd9\u4e2a\u4e09\u5c81\u5927\u7684\u5b69\u5b50\u53ef\u80fd\u662f\u9057\u8179\u5b50\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u7684\u7c97\u5fc3\u4e5f\u5e76\u975e\u6ca1\u6709\u539f\u56e0\u7684\uff0c\u56e0\u4e3a\u4ed6\u6240\u6000\u7591\u7684\u90a3\u4e2a\u201c\u5a18\u513f\u201d\uff0c\u4e09\u5e74\u524d\u8fd8\u66fe\u7ecf\u662f\u6253\u8fc7 \u864e\u7684\u5973\u82f1\u96c4\uff0c\u53ea\u54c8\u5fc5\u56fe\u5c31\u662f\u5728\u5979\u6253\u8fc7\u8001\u864e\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u5230\u8fc7\u5979\u7684\u5bb6\u91cc\u7684\u3002 \u3000\u3000\u867d\u7136\u54c8\u5fc5\u56fe\u6ca1\u89c1\u8fc7\u5979\u6253\u864e\u7684\u8eab\u624b\uff0c\u4f46\u8bd5\u60f3\u4e00\u4e2a\u5728\u5f53\u5929\u8fd8\u80fd\u591f\u6253\u8001\u864e\u7684\u5973\u4eba\uff0c\u5982\u4f55\u4f1a\u7ed9\u522b \u4eba\u770b\u51fa\u5979\u662f\u5b55\u5987\uff1f\u56e0\u6b64\u5728\u54c8\u5fc5\u56fe\u7684\u5370\u8c61\u4e2d\uff0c\u4ed6\u89c1\u8fc7\u7684\u90a3\u4e2a\u201c\u5a18\u513f\u201d\u662f\u600e\u6837\u4e5f\u4e0d\u53ef\u80fd\u548c\u4e00\u4e2a\u5b55 \u5987\u8054\u60f3\u8d77\u6765\u7684\u3002 \u3000\u3000\u4ed6\u53bb\u4e86\u7591\u5fc3\uff0c\u968f\u53e3\u7b11\u95ee\uff1a\u201c\u5979\u7684\u5b69\u5b50\u957f\u5f97\u6ca1\u5979\u8fd9\u6837\u4e11\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u7b11\u9053\uff1a\u201c\u8bf4\u4e5f\u5947\u602a\uff0c\u4e4c\u9e26\u4e5f\u4f1a\u517b\u51fa\u51e4\u51f0\u6765\u5462\u3002\u5979\u7684\u5b69\u5b50\u975e\u4f46\u4e0d\u4e11\uff0c\u800c\u4e14\u6bd4\u4e00\u822c\u5b69 \u5b50\u8fd8\u8981\u4fca\u7f8e\u5f97\u591a\uff0c\u5c24\u5176\u662f\u5979\u90a3\u4e2a\u4e09\u5c81\u5927\u7684\u5973\u5b69\uff0c\u5185\u4eba\u559c\u6b22\u5f97\u4e0d\u5f97\u4e86\uff0c\u7b80\u76f4\u60f3\u8981\u6536\u5979\u505a\u5e72\u5973 \u513f\u3002\u201d \u3000\u3000\u90a3\u77e5\u54c8\u5fc5\u56fe\u5bf9\u5973\u5b69\u5b50\u4e0d\u611f\u5174\u8d77\uff0c\u5bf9\u7537\u5b69\u5b50\u5374\u611f\u5174\u8da3\uff0c\u4ed6\u5f88\u7559\u795e\u5730\u542c\u5b8c\u989c\u9274\u8bf4\u8bdd\uff0c\u542c\u7f62\uff0c \u82e5\u6709\u6240\u601d\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5170\u59d1\u90a3\u4e2a\u5341\u4e8c\u5c81\u5927\u7684\u7537\u5b69\u5b50\u6211\u5012\u60f3\u89c1\u4ed6\u4e00\u89c1\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u6709\u70b9\u4e3a\u96be\u795e\u8272\u3002\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5f88\u91ce\uff0c\u6211\u4e5f\u4e0d\u5e38\u89c1\u5230\u4ed6\uff0c\u4f46\u542c\u8bf4\u4ed6\u662f\u5f88\u559c\u6b22\u5230\u5c71 \u4e0a\u8dd1\u7684\u3002\u6211\u53eb\u4eba\u53bb\u627e\u4ed6\u5c31\u662f\uff0c\u4f46\u6050\u6015\u4e00\u65f6\u95f4\u672a\u5fc5\u627e\u5f97\u7740\u4ed6\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u53ea\u662f\u7565\u8d77\u7591\u5fc3\u800c\u5df2\uff0c\u5e76\u975e\u4e00\u5b9a\u8981\u89c1\u90a3\u5b69\u5b50\u4e0d\u53ef\u7684\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u7528\u8fd9\u6837\u7740 \u5fd9\uff0c\u53cd\u6b63\u6211\u8fd8\u8981\u8fc7\u4e24\u5929\u624d\u8d70\u3002\u660e\u5929\u4f60\u518d\u53eb\u90a3\u5b69\u5b50\u6765\u89c1\u6211\u5427\u3002\u4eca\u5929\u54b1\u4eec\u5148\u8c08\u6b63\u4e8b\u3002\u201d \u3000\u3000XXX\u5170\u59d1\u8eb2\u5728\u90a3\u5ea7\u5047\u5c71\u540e\u9762\uff0c\u5077\u542c\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u4e00\u9762\u542c\u4e00\u9762\u634f\u7740\u51b7\u6c57\uff0c\u8d8a\u542c\u8d8a\u662f\u5403\u60ca\u3002 \u3000\u3000\u672c\u6765\u5929\u9999\u4ead\u548c\u5979\u85cf\u8eab\u4e4b\u5904\u8ddd\u79bb\u751a\u8fdc\uff0c\u6362\u4e86\u522b\u4e00\u4e2a\u4eba\uff0c\u751a\u81f3\u5373\u4f7f\u662f\u5b66\u8fc7\u6b66\u529f\u7684\u4eba\uff0c\u4e5f\u4e0d\u80fd \u542c\u89c1\u5929\u9999\u4ead\u8fd9\u8fb9\u7684\u8c08\u8bdd\u3002 \u3000\u3000\u4f46\u5979\u5374\u542c\u5f97\u4e00\u5b57\u4e0d\u6f0f\uff0c\u56e0\u4e3a\u5979\u662f\u81ea\u5c0f\u5c31\u5728\u76d8\u9f99\u5c71\u957f\u5927\u7684\uff01 \u3000\u3000\u5979\u7684\u4e08\u592b\u662f\u730e\u4eba\uff0c\u5979\u4e5f\u5e38\u5e38\u8ddf\u4e08\u592b\u53bb\u6253\u730e\u7684\u3002\u5728\u5c71\u4e0a\u957f\u5927\u7684\u4eba\u542c\u89c9\u5df2\u662f\u8981\u6bd4\u666e\u901a\u4eba\u654f\u9510 \u7684\u4e86\uff0c\u4f55\u51b5\u662f\u4ee5\u6253\u730e\u4e3a\u751f\u7684\u4eba\uff1f\u730e\u4eba\u5fc5\u987b\u5177\u5907\u7684\u672c\u4e8b\u4e4b\u4e00\uff0c\u5c31\u662f\u80fd\u591f\u5728\u5f88\u8fdc\u7684\u5730\u65b9\u542c\u5f97\u89c1\u91ce \u517d\u8d70\u8def\u7684\u58f0\u97f3\u3002\u4ed6\u4eec\u4f0f\u5730\u542c\u58f0\u672c\u9886\u662f\u6bd4\u6c5f\u6e56\u4eba\u7269\u66f4\u9ad8\u7684\u3002 \u3000\u3000\u5979\u4e00\u9762\u542c\u4e00\u9762\u624b\u91cc\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u76f4\u5230\u542c\u89c1\u4e86\u54c8\u5fc5\u56fe\u8bf4\u660e\u5929\u624d\u8981\u627e\u5979\u7684\u5b69\u5b50\uff0c\u5979\u624d\u677e\u4e86 \u53e3\u6c14\uff0c\u7a0d\u7a0d\u653e\u4e86\u70b9\u5fc3\u3002 \u3000\u3000\u4f46\u60f3\u8d77\u5b69\u5b50\uff0c\u5979\u5374\u4e0d\u7981\u5fc3\u5934\u82e6\u7b11\u4e86\u3002 \u3000\u3000\u5979\u7684\u5bb9\u8c8c\u5e76\u4e0d\u662f\u5929\u751f\u8fd9\u6837\u4e11\u964b\u7684\uff0c\u5979\u662f\u4e3a\u4e86\u907f\u96be\uff0c\u4e0d\u80fd\u4e0d\u81ea\u5df1\u6bc1\u5bb9\u7684\u3002 \u3000\u3000\u5979\u60f3\u8d77\u90a3\u5929\u65e9\u4e0a\u3002\u5979\u7684\u5b69\u5b50\u9192\u6765\uff0c\u7b2c\u4e00\u6b21\u770b\u89c1\u6bcd\u4eb2\u53d8\u5f97\u8fd9\u6837\u4e11\u964b\u7684\u65f6\u5019\uff0c\u662f\u5982\u4f55\u5413\u5f97\u54ed \u4e86\u8d77\u6765\uff01 \u3000\u3000\u201c\u597d\u5728\u6211\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5426\u5219\u4e00\u5b9a\u9003\u4e0d\u8fc7\u54c8\u5fc5\u56fe\u7684\u773c\u775b\uff01\u201d \u3000\u3000\u201c\u51b2\u513f\u54ed\u90a3\u4e00\u573a\u4e5f\u662f\u503c\u5f97\u7684\uff0c\u4ed6\u603b\u7b97\u5b66\u4f1a\u4e00\u4e2a\u5fcd\u5b57\u4e86\u3002\u82e5\u4e0d\u662f\u4ed6\u5b66\u4f1a\u4e00\u4e2a\u5fcd\u5b57\uff0c\u4e09\u5e74\u524d \u90a3\u573a\u707e\u96be\u6211\u4eec\u5c31\u907f\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5170\u59d1\u4e0d\u662f\u522b\u4eba\uff0c\u5979\u6b63\u662f\u54c8\u5fc5\u56fe\u6240\u8981\u7f09\u62ff\u7684\u5f20\u96ea\u6ce2\uff0c\u8eab\u4efd\u662f\u5cb3\u98de\u7684\u5916\u5b59\u5973\u513f\u7684\u5f20 \u96ea\u6ce2\u3002 \u3000\u3000\u4e09\u5e74\u524d\u90a3\u573a\u201c\u56e0\u7978\u5f97\u798f\u201d\u7684\u5947\u9047\u5728\u5979\u5fc3\u5934\u91cd\u65b0\u6d6e\u73b0\u3002 \u3000\u3000\u5929\u5730\u832b\u832b\uff0c\u5979\u548c\u5b69\u5b50\u4e0d\u77e5\u5e94\u8be5\u8d70\u5230\u54ea\u91cc\u53bb\u89c5\u5bb9\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u5979\u60f3\u56de\u5230\u5b8b\u56fd\u53bb\uff0c\u5b8b\u56fd\u5bf9\u5979\u6765\u8bf4\u867d\u7136\u6bd4\u91d1\u56fd\u66f4\u52a0\u964c\u751f\uff0c\u4f46\u603b\u662f\u5979\u7684\u6545\u56fd\u3002\u5979\u7684\u7236\u6bcd\u548c\u4eb2 \u4eba\u662f\u57cb\u5728\u5b8b\u56fd\u7684\u571f\u5730\u4e0a\u7684\u3002 \u3000\u3000\u4e0d\u6599\u672a\u5230\u5927\u6563\u5173\uff0c\u5df2\u7ecf\u78b0\u4e0a\u5b8c\u989c\u9274\u4ece\u5927\u6563\u5173\u64a4\u56de\u6765\u7684\u5175\u9a6c\u4e86\u3002 \u3000\u3000\u91d1\u5175\u5305\u56f4\u5979\u4eec\u6bcd\u5b50\uff0c\u6709\u7684\u8bf4\u5979\u662f\u5b8b\u4eba\u7684\u5978\u7ec6\uff0c\u8981\u628a\u5979\u6253\u6b7b\uff1b\u6709\u7684\u89c1\u5979\u957f\u5f97\u58ee\u5065\uff0c\u8981\u5730\u505a \u968f\u519b\u7684\u6c11\u592b\u3002\u597d\u5728\u5979\u5df2\u7ecf\u6bc1\u5bb9\uff0c\u5426\u5219\u6050\u6015\u8fd8\u8981\u53d7\u66f4\u5927\u7684\u4fae\u8fb1\u3002 \u3000\u3000\u5ffd\u7136\u6709\u4e00\u9876\u8f7f\u5b50\u505c\u5728\u5979\u7684\u9762\u524d\uff0c\u4e00\u4e2a\u8d35\u5987\u4eba\u63ed\u5f00\u8f7f\u5e18\uff0c\u5ff5\u4e86\u4e00\u58f0\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u4e2a\u96be\u5987\u5982\u6b64\u53ef\u601c\uff0c\u4f60\u4eec\u8fd8\u6b3a\u4fae\u5979\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u8d35\u5987\u4eba\u662f\u5b8c\u989c\u9274\u7684\u59bb\u5b50\u3002 \u3000\u3000\u51fa\u4e4e\u5979\u7684\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u4f4d\u5c06\u519b\u592b\u4eba\u5fc3\u5730\u5012\u5f88\u4ec1\u6148\uff0c\u800c\u4e14\u548c\u5979\u201c\u6295\u7f18\u201d \u3000\u3000\uff0c\u4e0d\u4f46\u6536\u7559\u4e86\u5979\uff0c\u800c\u4e14\u8981\u5979\u505a\u8d34\u8eab\u5973\u4ec6\u3002 \u3000\u3000\u5979\u7f16\u9020\u7684\u90a3\u6bb5\u8c0e\u8bdd\uff0c\u7531\u4e8e\u592b\u4eba\u90fd\u5df2\u76f8\u4fe1\u4e86\uff0c\u8282\u5ea6\u4f7f\u8859\u95e8\u90a3\u4e9b\u4e0b\u4eba\u4e5f\u5c31\u6ca1\u4eba\u6562\u6000\u7591\u4e86\u3002\u5176 \u5b9e\uff0c\u8ba4\u771f\u8bf4\u6765\uff0c\u4e5f\u4e0d\u7b97\u5168\u662f\u8c0e\u8bdd\uff0c\u5979\u7684\u7236\u6bcd\u548c\u5916\u516c\u7684\u786e\u662f\u88ab\u201c\u5b8b\u56fd\u4eba\u201d\u5bb3\u6b7b\u7684\u3002 \u3000\u3000\u5979\u6539\u59d3\u9102\uff0c\u8fd9\u662f\u91d1\u4eba\u666e\u901a\u7684\u59d3\u6c0f\uff0c\u6070\u597d\u548c\u5979\u7684\u5916\u516c\u5cb3\u98de\u7684\u201c\u5cb3\u201d\u5b57\u540c\u97f3\u3002\u5170\u59d1\u8fd9\u4e2a \u201c\u5170\u201d\u5b57\u662f\u5979\u672c\u8eab\u7684\u59d3\u548c\u5979\u592b\u5bb6\u7684\u59d3\uff0c\u201c\u5f20\u201d\u5b57\u548c\u201c\u6a80\u201d\u5b57\u62fc\u51fa\u6765\u7684\u3002 \u3000\u3000\u7b2c\u4e8c\u5e74\u6625\u5929\u5979\u751f\u4e86\u4e00\u4e2a\u5973\u513f\uff0c\u53d6\u540d\u7fbd\u6a31\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6ca1\u751f\u4e0b\u513f\u5973\u3002\u5bf9\u5979\u7684\u5973\u513f\u7279\u522b\u75bc\u7231\u3001\u75bc\u7231\u5f97\u7b80\u76f4\u6709\u70b9\u201c\u8fc7\u4efd\u201d\uff0c\u5979\u4e3a\u5979\u7684\u5973\u513f \u8bf7\u4e86\u5976\u5988\uff0c\u7ecf\u5e38\u628a\u5979\u7684\u5973\u513f\u7559\u5728\u8eab\u8fb9\u3002\u201c\u8fc7\u4efd\u201d\u7684\u7a0b\u5ea6\uff0c\u51e0\u4e4e\u4e0d\u50cf\u662f\u5979\u7684\u5973\u513f\uff0c\u800c\u662f\u5c06\u519b\u592b \u4eba\u7684\u5973\u513f\u4e86\u3002 \u3000\u3000\u5979\u7684\u5973\u513f\u50cf\u662f\u4ece\u8377\u6cfd\u79fb\u690d\u6765\u7684\u540d\u79cd\u7261\u4e39\uff0c\u88ab\u653e\u8fdb\u201c\u6e29\u5ba4\u201d\u57f9\u517b\uff0c\u4e0d\u4f46\u548c\u5916\u9762\u7684\u5927\u5730\u9694 \u79bb\uff0c\u4e5f\u9694\u79bb\u4e86\u6bcd\u4f53\u3002\u5979\u8981\u89c1\u81ea\u5df1\u7684\u5973\u513f\uff0c\u4e5f\u5f97\u5148\u8bf7\u6c42\u592b\u4eba\u7684\u51c6\u8bb8\u3002 \u3000\u3000\u8859\u95e8\u91cc\u7684\u4eba\u90fd\u8bf4\u5979\u6709\u798f\u6c14\u3002\u5979\u5fc3\u5934\u82e6\u7b11\uff0c\u5374\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u662f\u4e00\u79cd\u201c\u798f\u6c14\u201d\u3002 \u3000\u3000\u5979\u53ea\u6709\u5341\u6708\u6000\u80ce\u4e4b\u82e6\uff0c\u5374\u514d\u4e86\u4e09\u5e74\u54fa\u80b2\u4e4b\u52b3\u3002 \u3000\u3000\u6000\u80ce\u867d\u82e6\uff0c\u4f46\u6bd4\u8f83\u8d77\u6765\uff0c\u5230\u5e95\u8fd8\u662f\u751f\u5b69\u5b50\u5bb9\u6613\uff0c\u629a\u517b\u5b69\u5b50\u8f83\u96be\u7684\u3002 \u3000\u3000\u5979\u88ab\u514d\u9664\u4e86\u629a\u517b\u513f\u5973\u7684\u201c\u9ebb\u70e6\u201d\uff0c\u5979\u662f\u53ef\u4ee5\u4e13\u5fc3\u6559\u81ea\u5df1\u7684\u513f\u5b50\u4e86\u3002 \u3000\u3000\u5979\u767d\u5929\u5e2e\u592b\u4eba\u6599\u7406\u7261\u4e39\uff0c\u665a\u4e0a\u5c31\u5077\u5077\u6559\u5979\u7684\u513f\u5b50\u6a80\u7fbd\u51b2\uff08\u73b0\u5728\u5df2\u6539\u540d\u9102\u51b2\uff09\u7ec3\u6b66\u3002\uff08\u5047 \u5982\u5979\u7684\u5973\u513f\u4e0d\u662f\u53e6\u6709\u5976\u5988\u7167\u6599\u7684\u8bdd\uff0c\u5979\u5728\u665a\u4e0a\u54ea\u91cc\u8fd8\u6709\u7cbe\u795e\u505a\u522b\u7684\u4e8b\u60c5\uff1f\u201d\uff09\u5979\u5728\u76d8\u9f99\u5c71\u7684 \u65f6\u5019\uff0c\u672c\u6765\u662f\u8fde\u7261\u4e39\u82b1\u4e5f\u6ca1\u89c1\u8fc7\u7684\uff0c\u73b0\u5728\u5df2\u7ecf\u6210\u4e3a\u79cd\u690d\u7261\u4e39\u7684\u201c\u4e13\u5bb6\u201d\u4e86\u3002 \u3000\u3000\u8fd9\u65b9\u9762\u7684\u77e5\u8bc6\uff0c\u662f\u4e24\u4e2a\u8001\u82b1\u738b\u4f20\u6388\u7ed9\u5979\u7684\u3002\u592b\u4eba\u559c\u6b22\u7261\u4e39\uff0c\u5979\u7528\u91cd\u91d1\u8bf7\u6765\u7684\u8fd9\u4e24\u4e2a\u201c\u82b1 \u738b\u201d\uff0c\u636e\u8bf4\u662f\u6bd4\u5fa1\u82d1\u82b1\u5320\u8fd8\u66f4\u9ad8\u660e\u7684\u3002\u592b\u4eba\u5174\u81f4\u597d\u7684\u65f6\u5019\uff0c\u6709\u65f6\u4e5f\u4f1a\u6307\u70b9\u5979\u3002\u73b0\u5728\u5979\u5df2\u7ecf\u662f \u4e13\u5bb6\u4e86\uff0c\u4ee5\u5979\u73b0\u5728\u4e13\u5bb6\u7684\u773c\u5149\u770b\u6765\uff0c\u592b\u4eba\u5bf9\u7261\u4e39\u82b1\u7684\u77e5\u8bc6\uff0c\u662f\u7edd\u4e0d\u5728\u90a3\u4e24\u4e2a\u82b1\u738b\u4e4b\u4e0b\u7684\u3002 \u3000\u3000\u201c\u5947\u602a\uff0c\u592b\u4eba\u4e3a\u4ec0\u4e48\u53ea\u559c\u6b22\u7261\u4e39\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\uff0c\u5979\u4ece\u6765\u6ca1\u6709\u95ee\u8fc7\u592b\u4eba\u3002\u4f46\u4e0d\u7ba1\u600e\u6837\uff0c\u592b\u4eba\u8fd9\u79cd\u7279\u6b8a\u7231\u597d\uff0c\u4eca\u5929\u6551\u4e86\u5979\u7684\u547d\u3002 \u3000\u3000\u8981\u4e0d\u662f\u5979\u5f97\u592b\u4eba\u5c06\u5979\u57f9\u517b\u6210\u4e3a\u4e00\u4e2a\u79cd\u58ee\u4e39\u7684\u201c\u4e13\u5bb6\u201d\uff0c\u521a\u624d\u54c8\u5fc5\u56fe\u76d8\u95ee\u5979\uff0c\u53ea\u6015\u95ee\u4e0d\u4e0a \u4e09\u53e5\uff0c\u5979\u5c31\u5bf9\u7b54\u4e0d\u6765\u4e86\u3002\u90a3\u4e24\u4e2a\u82b1\u738b\u56e0\u4e3a\u5e74\u7eaa\u592a\u8001\uff0c\u867d\u7136\u5c1a\u672a\u9000\u4f11\uff0c\u4f46\u6599\u7406\u7261\u4e39\u7684\u4e8b\u60c5\uff0c\u4e3b \u8981\u5df2\u662f\u7531\u5979\u8d1f\u8d23\u3002 \u3000\u3000\u5979\u767d\u5929\u6599\u7406\u7261\u4e39\uff0c\u665a\u4e0a\u4f20\u6388\u513f\u5b50\u6b66\u529f\uff0c\u8fd9\u4e24\u65b9\u9762\u90fd\u5df1\u6709\u4e86\u4ee4\u5979\u6ee1\u610f\u6210\u7ee9\u3002 \u3000\u3000\u6ee1\u610f\u5f97\u7b80\u76f4\u8d85\u8fc7\u5979\u539f\u6765\u7684\u671f\u671b\uff01\u5979\u7684\u513f\u5b50\u672c\u6765\u806a\u660e\u7edd\u9876\uff0c\u867d\u7136\u9650\u4e8e\u5e74\u7eaa\uff0c\u8fd8\u4e0d\u80fd\u8bf4\u662f\u5df2 \u7ecf\u6210\u4e3a\u201c\u9ad8\u624b\u201d\uff0c\u4f46\u5bf9\u6a80\u5bb6\u7684\u5bb6\u4f20\u6b66\u5b66\uff0c\u5374\u5df1\u5b66\u5f97\u70c2\u719f\u4e8e\u80f8\uff0c\u53ea\u8c08\u201c\u6b66\u5b66\u201d\u7684\u9020\u8be3\u751a\u81f3\u662f\u6bd4 \u4ed6\u7684\u6bcd\u4eb2\u8fd8\u66f4\u9ad8\u660e\u4e86\u3002\uff08\u6a80\u5bb6\u7684\u6b66\u529f\u79d8\u7b7e\uff0c\u662f\u5979\u7684\u516c\u516c\u4e34\u6b7b\u4e4b\u524d\u4ea4\u7ed9\u5979\u7684\u3002\u5979\u53ea\u80fd\u7167\u672c\u5ba3 \u79d1\uff0c\u4f20\u7ed9\u5979\u7684\u513f\u5b50\u3002 \u3000\u3000\u5728\u8282\u5ea6\u4f7f\u8859\u95e8\u91cc\uff0c\u5979\u662f\u4e0d\u6562\u5077\u7ec3\u7684\u3002\u5979\u7684\u513f\u5b50\u53ef\u4ee5\u8dd1\u5230\u5916\u9762\u53bb\u73a9\uff0c\u7ec3\u529f\u7684\u673a\u4f1a\u53cd\u800c\u6bd4\u5979 \u591a\u3002\uff09\u5979\u592b\u4eba\u8ddf\u524d\u7684\u7279\u6b8a\u5730\u4f4d\uff0c\u8fd8\u7ed9\u5979\u7684\u513f\u5b50\u5e26\u6765\u4e86\u53e6\u5916\u4e00\u79cd\u201c\u798f\u6c14\u201d\u3002 \u3000\u3000\u7531\u4e8e\u5979\u7684\u7279\u6b8a\u5730\u4f4d\uff0c\u8282\u5ea6\u4f7f\u8859\u95e8\u7684\u4e0a\u4e0b\u4eba\u7b49\uff0c\u5bf9\u5979\u7684\u513f\u5b50\u4e5f\u90fd\u53e6\u773c\u76f8\u770b\u3002 \u3000\u3000\u8282\u5ea6\u4f7f\u8859\u95e8\u9ad8\u624b\u5982\u4e91\uff0c\u5728\u5b8c\u989c\u9274\u91cd\u91d1\u793c\u8058\u4e4b\u4e0b\uff0c\u6709\u8bb8\u591a\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\u90fd\u505a\u4e86\u4ed6\u7684\u536b \u58eb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6700\u559c\u6b22\u770b\u90a3\u4e9b\u536b\u58eb\u7ec3\u6b66\u3002\u90a3\u4e9b\u536b\u58eb\u4e3a\u4e86\u8ba8\u597d\u4ed6\uff0c\u4e5f\u5e38\u5e38\u6559\u4ed6\u4e09\u62db\u4e24\u5f0f\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8ddf\u6bcd\u4eb2\u5b66\u7684\u53ea\u662f\u6b66\u5b66\u7684\u539f\u7406\uff08\u4e3b\u8981\u662f\u5185\u529f\u5fc3\u6cd5\uff09\uff0c\u5728\u5c1a\u672a\u5927\u6210\u4e4b\u524d\uff0c\u53cd\u4e0d\u5982\u90a3\u4e9b \u536b\u58eb\u6559\u4ed6\u7684\u62db\u5f0f\u66f4\u5207\u5b9e\u7528\u3002 \u3000\u3000\u5176\u4e2d\u6709\u4e24\u4e2a\u548c\u4ed6\u7279\u522b\u8981\u597d\u7684\u536b\u58eb\uff0c\u65f6\u5e38\u966a\u4ed6\u5230\u5c71\u4e0a\u7ec3\u6b66\u3002\u56e0\u4e3a\u5728\u5c71\u4e0a\u7ec3\u6b66\uff0c\u6709\u8bb8\u591a\u597d \u5904\uff0c\u4f8b\u5982\u8981\u7ec3\u8f7b\u529f\uff0c\u5728\u5e73\u5730\u7ec3\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u6bd4\u4e0d\u5728\u5c71\u4e0a\u7ec3\u7684\u3002\uff08\u8fd8\u6709\u4e00\u4e2a\u597d\u5904\uff0c\u5728\u5c71\u4e0a\u7ec3\u53ef \u4ee5\u907f\u514d\u7ed9\u5b8c\u989c\u9274\u770b\u89c1\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u70b9\u6a80\u7fbd\u51b2\u5f53\u7136\u662f\u4e0d\u4f1a\u544a\u8bc9\u90a3\u4e9b\u536b\u58eb\u7684\u4e86\u3002\uff09\u6ca1\u6709\u536b\u58eb\u966a\u4ed6 \u7684\u65f6\u5019\uff0c\u4ed6\u4e00\u4e2a\u4eba\u4e5f\u559c\u6b22\u5230\u5c71\u4e0a\u53bb\u201c\u73a9\u201d\u3002\u4e00\u4eba\u8eb2\u5728\u6ca1\u6709\u4eba\u5230\u7684\u5730\u65b9\uff0c\u7ec3\u4ed6\u7684\u5bb6\u4f20\u6b66\u5b66\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u770b\u89c1\u5979\u7684\u513f\u5b50\u6b66\u529f\u8fdb\u5c55\u795e\u901f\uff0c\u5f53\u7136\u662f\u5f88\u559c\u6b22\u7684\u3002\u5979\u5e38\u5e38\u60f3\u3002\u8fd9\u6837\u4e0b\u53bb\u3002\u5b69\u5b50\u672a\u5230 \u5341\u516d\u5c81\u5c31\u53ef\u80fd\u6210\u4e3a\u4e00\u6d41\u9ad8\u624b\u4e86\uff0c\u867d\u7136\u672a\u5fc5\u6bd4\u5f97\u4e0a\u4ed6\u7684\u7237\u7237\uff0c\u4f46\u8981\u6740\u50cf\u54c8\u5fc5\u56fe\u8fd9\u6837\u7684\u4ec7\u4eba\uff0c\u8bf4 \u4e0d\u5b9a\u4e5f\u53ef\u4ee5\u505a\u5f97\u5230\u4e86\u3002 \u3000\u3000\u4f46\u60f3\u4e0d\u5230\u7684\u662f\u3002\u5b69\u5b50\u8fd8\u672a\u5230\u5341\u516d\u5c81\uff0c\u53ea\u662f\u5341\u4e8c\u5c81\u521a\u6ee1\uff0c\u4ed6\u4eec\u4e24\u5bb6\u7684\u4ec7\u4eba\u4e4b\u4e00\u7684\u5c31\u5df2\u7ecf\u51fa \u73b0\u5728\u5979\u7684\u9762\u524d\u3002 \u3000\u3000\u5979\u7684\u5b69\u5b50\u8fd8\u672a\u6709\u80fd\u529b\u62a5\u4ec7\u3002\u54c8\u5fc5\u56fe\u6b63\u662f\u8981\u627e\u5979\u7684\u5b69\u5b50\uff01\u800c\u4e14\u671f\u9650\u5df2\u5b9a\uff0c\u81f3\u8fdf\u4e0d\u8fc7\u660e\u5929\uff0c \u5b8c\u989c\u9274\u5c31\u8981\u628a\u5979\u7684\u5b69\u5b50\u627e\u6765\uff0c\u8ba9\u54c8\u5fc5\u56fe\u5ba1\u95ee\u4ed6\u3002 \u3000\u3000\uff08\u54c8\u5fc5\u56fe\u4e3a\u4ec0\u4e48\u8981\u201c\u89c1\u4e00\u89c1\u201d\u5979\u7684\u5b69\u5b50\uff0c\u8fd9\u539f\u56e0\u5b8c\u989c\u9274\u4e0d\u77e5\u9053\uff0c\u5979\u5f53\u7136\u662f\u77e5\u9053\u7684\u3002\uff09 \u201c\u597d\u5728\u54c8\u5fc5\u56fe\u73b0\u5728\u5c1a\u672a\u8ba4\u51fa\u6211\uff0c\u4e5f\u672a\u6562\u65ad\u5b9a\u4ef2\u513f\u5c31\u662f\u6a80\u5bb6\u7684\u5c0f\u8d1d\u5b50\uff0c\u4f46\u82e5\u7ed9\u4ed6\u89c1\u5230\uff0c\u4ed6\u8fd8\u4f1a \u8ba4\u51fa\u662f\u51b2\u513f\u5417\uff1f\u5341\u4e8c\u5c81\u7684\u5b69\u5b50\u548c\u4e5d\u5c81\u7684\u5b69\u5b50\u867d\u6709\u5dee\u522b\uff0c\u5dee\u522b\u4e5f\u4e0d\u662f\u5f88\u5927\u7684\u3002\u201d \u3000\u3000\u600e\u4e48\u529e\u5462\uff1f\u6b63\u5f53\u5979\u5fc3\u4e71\u52a0\u9ebb\u7684\u65f6\u5019\uff0c\u5b8c\u989c\u9274\u548c\u54c8\u5fc5\u56fe\u5728\u5929\u9999\u4ead\u90a3\u8fb9\u8c08\u8bdd\u7684\u58f0\u97f3\uff0c\u53c8\u4f20\u5230 \u5979\u8033\u6735\u4e2d\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u8c08\u8bdd\u7684\u5185\u5bb9\uff0c\u7acb\u5373\u5438\u5f15\u4e86\u5f20\u96ea\u6ce2\u7684\u6ce8\u610f\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8c08\u5230\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u6b63\u662f\u5f20\u96ea\u88ab\u60f3\u8981\u627e\u5bfb\uff0c\u5374\u8fde\u4ed6\u7684\u534a\u70b9\u6d88\u606f\u90fd\u542c\u4e0d\u5230\u7684\u3002 \u3000\u3000\u201c\u5bf9\u5566\uff0c\u4e00\u4e2a\u5973\u4ec6\u65e0\u5173\u91cd\u8981\uff0c\u54b1\u4eec\u8fd8\u662f\u8c08\u6b63\u7ecf\u4e8b\u5427\u3002\u521a\u624d\u8bf4\u5230\u54ea\u91cc\uff1f \u3000\u3000\u201d\u5b8c\u989c\u9274\u9053\u3002\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8bf4\u5230\u5f53\u4eca\u7687\u4e0a\u6700\u987e\u5fcc\u7684\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5bf9\uff0c\u7b2c\u4e00\u4e2a\u662f\u6a80\u516c\u76f4\u3002\u4f60\u5df2\u7ecf\u8bf4\u8fc7\u4e86\uff0c\u7b2c\u4e8c\u4e2a\u662f\u5426\u5373\u662f\u4ed6\u7684\u5ab3\u5987\u5f20\u96ea \u6ce2\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\uff0c\u5f20\u96ea\u6ce2\u662f\u53ea\u80fd\u548c\u6a80\u516c\u76f4\u7b97\u5728\u4e00\u8d77\u7684\uff0c\u7b2c\u4e8c\u4e2a\u7687\u4e0a\u6240\u987e\u5fcc\u7684\u53e6\u6709\u5176\u4eba\u3002 \u8fd9\u4e2a\u4eba\u8bba\u5730\u4f4d\u548c\u8bba\u6b66\u529f\uff0c\u6bd4\u8d77\u6a80\u516c\u76f4\u6765\u90fd\u662f\u53ea\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff01\u7687\u4e0a\u5bf9\u4ed6\u7684\u987e\u5fcc\uff0c\u6050\u6015\u4e5f\u8981 \u6bd4\u5bf9\u64c5\u516c\u76f4\u7684\u987e\u5fcc\u66f4\u591a\u4e00\u4e9b\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u516c\u76f4\u5df2\u7ecf\u662f\u54b1\u4eec\u5927\u91d1\u56fd\u7684\u4eb2\u738b\uff0c\u6709\u8c01\u6bd4\u4ed6\u7684\u5730\u4f4d\u66f4\u9ad8\uff1f\u8bba \u6b66\u529f\uff0c\u6211\u7684\u4f2f\u7236\u5b8c\u989c\u957f\u4e4b\u662f\u516c\u8ba4\u7684\u672c\u56fd\u7b2c\u4e00\u9ad8\u624b\uff0c\u6a80\u516c\u76f4\u7684\u6b66\u529f\u516c\u6b21\u4e8e\u6211\u4f2f\u7236\uff0c\u867d\u7136\u6ca1\u6709\u91d1 \u56fd\u7b2c\u4e8c\u9ad8\u624b\u7684\u79f0\u53f7\uff0c\u5b9e\u9645\u4ea6\u5df2\u7b97\u5f97\u662f\u7b2c\u4e8c\u9ad8\u624b\u4e86\u3002\u5bf9\u7687\u4e0a\u4e0d\u5fe0\u7684\u738b\u516c\u5927\u81e3\uff0c\u53c8\u6709\u8c01\u7684\u6b66\u529f\u80fd \u591f\u6bd4\u6a80\u516c\u76f4\u66f4\u9ad8\uff1f \u3000\u3000\u201d\u8bf4\u5230\u6b64\u5904\u3002\u4e0d\u89c9\u5fc3\u91cc\u6709\u70b9\u53d1\u6bdb\uff1a\u201c\u83ab\u975e\u7687\u4e0a\u987e\u5fcc\u7684\u7b2c\u4e8c\u4e2a\u513f\u5c31\u662f\u6211\u7684\u4f2f\u7236\uff1f\u201d\u4ed6\u7684\u4f2f \u7236\u5b8c\u989c\u957f\u4e4b\u662f\u73b0\u4efb\u7684\u5175\u9a6c\u5927\u5e05\u5fa1\u6797\u519b\u7edf\u9886\uff0c\u53c8\u662f\u7687\u53d4\u8eab\u4efd\uff0c\u8bba\u5730\u4f4d\u4e5f\u8981\u6bd4\u5f53\u5e74\u7684\u6a80\u516c\u76f4\u66f4 \u9ad8\u3002\u54c8\u5fc5\u56fe\u6240\u8bf4\u7684\u90a3\u4e2a\u4eba\u5177\u5907\u7684\u90a3\u4e9b\u6761\u4ef6\uff0c\u7adf\u4f3c\u975e\u4ed6\u7684\u4f2f\u7236\u83ab\u5c5e\u7684\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u7b11\u4e86\u4e00\u7b11\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e00\u4e2a\u4eba\u4e86\uff0c\u90a3\u4e2a\u4eba\u662f\u6709\u8d44\u683c\u53ef\u4ee5\u505a\u8fbd\u56fd\u7684\u7687\u5e1d\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u653e\u4e86\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u3001\u4f46\u8fd8\u6709\u70b9\u6000\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u8fbd\u56fd\u4e0d\u662f\u65e9\u5df2\u5728\u4e8c\u5341\u5e74\u524d\uff0c\u5c31\u5df2 \u7ed9\u54b1\u4eec\u706d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u8fd9\u4e2a\u6709\u8d44\u683c\u505a\u8fbd\u56fd\u7684\u7687\u5e1d\u7684\u4eba\uff0c\u4f3c\u4e4e\u53ea\u6709\u8036\u5f8b\u5ef6\u79a7\u7684\u513f\u5b50 \u5427\uff1f\u201d\u8036\u5f8b\u5ef6\u79a7\u662f\u8fbd\u56fd\u6700\u540e\u4e00\u4e2a\u7687\u5e1d\uff0c\u56fd\u4ea1\u4e4b\u540e\uff0c\u88ab\u91d1\u4eba\u56da\u4e8e\u4e94\u5e1d\u57ce\u4e09\u5e74\uff0c\u7ec8\u88ab\u6740\u5bb3\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e2a\u4eba\u6b63\u662f\u8036\u5f8b\u5ef6\u79a7\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8036\u5f8b\u5ef6\u79a7\u7684\u4e94\u4e2a\u513f\u5b50\u516d\u4e2a\u5973\u513f\uff0c\u4e0d\u662f\u542c\u8bf4\u90fd\u5df2\u88ab\u6740\u4e48\uff1f \u3000\u3000\u201d\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u8036\u5f8b\u5ef6\u79a7\u7684\u7b2c\u516d\u4e2a\u513f\u5b50\uff0c\u662f\u8036\u5f8b\u5ef6\u79a7\u672a\u505a\u8fbd\u56fd\u7687\u5e1d\u4e4b\u524d\u7684\u79c1\u751f \u5b50\uff0c\u4e0d\u77e5\u4ec0\u4e48\u539f\u56e0\uff0c\u5728\u4ed6\u5373\u4f4d\u4e4b\u540e\uff0c\u5374\u6ca1\u6709\u4e3a\u4ed6\u7684\u8fd9\u4e2a\u79c1\u751f\u5b50\u6b63\u540d\u4efd\uff0c\u8fd9\u79c1\u751f\u5b50\u4e5f\u4e0d\u662f\u5728\u5bab \u4e2d\u957f\u5927\u7684\u3002\u4e0d\u8fc7\uff0c\u8eab\u4efd\u867d\u6ca1\u516c\u5f00\uff0c\u8fbd\u56fd\u7684\u738b\u5ba4\u4e2d\u4eba\uff0c\u8fd8\u662f\u6709\u8bb8\u591a\u4eba\u77e5\u9053\u6bd4\u8fd9\u4e2a\u4eba\u5bc6\u8c0b\u6062\u590d\u8fbd \u56fd\uff0c\u7687\u4e0a\u548c\u4ee4\u4f2f\u7236\u4e5f\u662f\u77e5\u9053\u7684\u3002\u4eca\u4f2f\u7236\u6ca1\u6709\u5bf9\u4f60\u8bf4\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8bf4\u662f\u8bf4\u8fc7\u4e00\u70b9\uff0c\u4f46\u6ca1\u8bf4\u51fa\u90a3\u4e2a\u4eba\u7684\u771f\u6b63\u8eab\u4efd\u3002\u6211\u53ea\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u60f3\u5bc6\u8c0b\u9020 \u53cd\u7684\u8fbd\u56fd\u4eba\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u53e6\u4f2f\u7236\u662f\u5728\u4f60\u51fa\u9547\u5546\u5dde\u4e4b\u524d\u8bf4\u7684\u5427\uff1f\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54c8\u5fc5\u56fe\u9053\uff1a \u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ee4\u4f2f\u7236\u5f53\u65f6\u53ef\u80fd\u8fd8\u672a\u77e5\u9053\u8fd9\u4e2a\u4eba\u7684\u771f\u6b63\u8eab\u4efd\u3002\u8ddf\u7740\u95ee\u9053\uff1a\u201c\u5173\u4e8e\u8fd9\u4e2a\u4eba\uff0c\u4ee4\u4f2f \u7236\u8fd8\u8bf4\u4e86\u4e00\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5bb6\u4f2f\u7236\u662f\u5728\u548c\u6211\u8c08\u53ca\u5f53\u4eca\u6b66\u6797\u9ad8\u624b\u4e4b\u65f6\uff0c\u63d0\u53ca\u8fd9\u4e2a\u4eba\u7684\u3002\u4ed6\u8bf4\u542c\u8bf4\u8fd9\u4e2a\u4eba \u7684\u6b66\u529f\u5f88\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4ee4\u4f2f\u7236\u662f\u5f53\u4eca\u7b2c\u4e00\u9ad8\u624b\uff0c\u4ed6\u8bf4\u2018\u4e0d\u9519\u2019\uff0c\u90a3\u5df2\u7ecf\u662f\u975e\u540c\u5c0f\u53ef\u4e86\u3002\u6211\u4e5f\u66fe\u542c \u5230\u4e00\u4e9b\u6b66\u6797\u4eba\u7269\u7684\u8c08\u8bba\uff0c\u8bf4\u51fa\u6765\u4f60\u522b\u751f\u6c14\u3002\u201d\u5b8c\u989c\u9274\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u60f3\u548c\u8fd9\u4e2a\u4eba\u4e00\u8f83\u9ad8\u4e0b\u3002 \u522b\u4eba\u8bf4\u4ed6\u7684\u6b66\u529f\u597d\uff0c\u6211\u53c8\u600e\u4f1a\u751f\u6c14\uff1f \u3000\u3000\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4ee4\u4f2f\u7236\u53ef\u662f\u60f3\u548c\u6b64\u4eba\u4e00\u8f83\u9ad8\u4e0b\u7684\u554a\uff01\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u90a3\u6211\u4e0d\u544a\u8bc9\u4ed6\u5c31\u662f \u4e86\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u5012\u4e0d\u662f\u8ba4\u4e3a\u6b64\u4eba\u7684\u6b66\u529f\u4e00\u5b9a\u5728\u4ee4\u4f2f\u7236\u4e4b\u4e0a\uff0c\u53ea\u662f\u8bf4\u6b64\u4eba\u7684\u6b66\u529f\u6bd4\u6a80 \u516c\u76f4\u9ad8\u660e\u5f97\u591a\uff0c\u4f46\u82e5\u4e0e\u4ee4\u4f2f\u7236\u6bd4\u8f83\uff0c\u4ed6\u4eec\u5c31\u4e0d\u6562\u5984\u5730\u8bae\u8bba\uff0c\u4e0d\u77e5\u8c01\u9ad8\u8c01\u4e0b\u4e86\u3002\u201d\u5b8c\u989c\u9274\u9053\uff1a \u201c\u4e0d\u77e5\u5bb6\u4f2f\u7236\u5e38\u65e5\u8bf4\u7684\u90a3\u4e2a\u4eba\uff0c\u662f\u5426\u5373\u662f\u54b1\u4eec\u73b0\u5728\u8bf4\u7684\u8fd9\u4e2a\u4eba\uff1f\u201d\u54c8\u5fc5\u56fe\u5ffd\u9053\uff1a\u201c\u5b8c\u989c\u5c06 \u519b\uff0c\u542c\u8bf4\u4f60\u7684\u91d1\u521a\u6307\u529f\u592b\u7ec3\u5f97\u5f88\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6765\u5f97\u5f88\u7a81\u5140\uff0c\u5b8c\u989c\u9274\u4e0d\u77e5\u4ed6\u7684\u7528\u610f\uff0c\u5c0f\u5fc3\u7b54\u9053\uff1a\u201c\u6211\u662f\u8ddf\u5bb6\u4f2f\u7236\u5b66\u7684\uff0c\u4e0d\u8fc7\u7565\u5f97 \u76ae\u6bdb\u800c\u5df2\uff0c\u600e\u6bd4\u5f97\u4e0a\u54c8\u5927\u4eba\u7ec3\u7684\u5927\u529b\u91d1\u521a\u62ff\u529f\u592b\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u5c06\u519b\u4e0d\u5fc5\u5ba2\u6c14\u3002\u54b1\u4eec\u5404\u81ea\u5c06\u90a3\u4e2a\u4eba\u7684\u540d\u5b57\u5199\u5728\u8fd9\u5f20\u64c5\u9999\u684c\u4e0a\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u5f53\u7136\u61c2\u5f97\uff0c\u6240\u8c13\u201c\u5199\u201d\u5373\u662f\u8981\u4ed6\u4ee5\u6307\u4ee3\u7b14\u201c\u5199\u201d\u51fa\u6765\u7684\u610f\u601d\u3002 \u3000\u3000\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u5927\u4eba\u60f3\u8003\u6211\u3002\u6211\u662f\u552f\u4ece\u547d\u3001\u5199\u5f97\u4e0d\u597d\uff0c\u5927\u4eba\u53ef\u83ab\u89c1\u7b11\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5728\u5047\u5c71\u90a3\u8fb9\u5077\u542c\uff0c\u5f53\u7136\u770b\u4e0d\u89c1\u4ed6\u4eec\u5728\u684c\u5b50\u4e0a\u5199\u7684\u662f\u4ec0\u4e48\u5b57\u3002\u534a\u664c\uff0c\u53ea\u542c\u5f97\u54c8\u5fc5\u56fe \u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u540c\u4e00\u4e2a\u4eba\u3002\u5c06\u519b\u7684\u6307\u529b\u5165\u6728\u4e09\u5206\uff0c\u5bb6\u4f20\u7edd\u6280\uff0c\u786e\u662f\u975e\u540c\u5c0f\u53ef\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u591a\u8c22\u5927\u4eba\u5938\u8d5e\uff0c\u4f46\u8fd9\u4eba\u7684\u540d\u5b57\u7559\u5728\u684c\u4e0a\uff0c\u6050\u6709\u4e0d\u4fbf\uff0c\u5f85\u6211\u7528\u5200\u5c06\u5b83\u94f2\u53bb \u5427\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u8fd9\u6837\u9ebb\u70e6\u2014\u2014\u201d\u7b11\u58f0\u672a\u7edd\uff0c\u53ea\u542c\u5f97\u5b8c\u989c\u9274\u5df1\u5728\u5927\u58f0\u559d\u5f69\u8d77\u6765\uff0c\u8bf4 \u9053\uff1a\u201c\u5927\u4eba\u7684\u91d1\u521a\u638c\u529b\uff0c\u624d\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\u5462\uff0c\u53ea\u8fd9\u4e48\u8f7b\u8f7b\u4e00\u62b9\u5c31\u62b9\u5e73\u4e86\uff01\u201d \u3000\u3000\u90a3\u4eba\u7684\u540d\u5b57\u5df2\u7ecf\u7ed9\u54c8\u5fc5\u56fe\u4ee5\u91d1\u521a\u62ff\u529b\u62b9\u53bb\uff0c\u4f46\u5f20\u96ea\u6ce2\u867d\u7136\u770b\u4e0d\u89c1\uff0c\u4ea6\u5df2\u77e5\u9053\u8fd9\u4eba\u662f\u8c01 \u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u8fbd\u56fd\u672b\u4ee3\u7687\u5e1d\u7684\u79c1\u751f\u5b50\uff0c\u8fbd\u56fd\u7687\u5e1d\u590d\u59d3\u8036\u5f8b\uff0c\u5b50\u4ece\u7236\u59d3\uff0c\u8fd9\u4e2a\u4e60\u60ef\uff0c\u5b8b\u91d1\u8fbd\u4e09\u56fd \u90fd\u662f\u4e00\u6837\u7684\u3002\u56fa\u6b64\u5f20\u96ea\u6ce2\u867d\u7136\u770b\u4e0d\u89c1\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u4f46\u6700\u5c11\u4ea6\u5df2\u77e5\u9053\u4ed6\u662f\u590d\u59d3\u8036\u5f8b\u7684\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u77bf\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u83ab\u975e\u5c31\u662f\u516c\u516c\u8981\u6211\u5bfb\u627e\u7684\u51b2\u513f\u7684\u5e08\u7236\uff1f\u201d\u8fd9\u662f\u5979 \u7684\u516c\u516c\u5728\u4e34\u6b7b\u4e4b\u524d\u5631\u5490\u5979\u7684\uff0c\u4e34\u6b7b\u4e4b\u524d\uff1b\u6c14\u606f\u5944\u5944\uff0c\u8bf4\u5f97\u5f53\u7136\u751a\u4e3a\u7b80\u7565\uff0c\u59d3\u540d\u90fd\u8bf4\u5f97\u4e0d\u5168\u3001 \u4f46\u4ece\u516c\u516c\u7b80\u7565\u7684\u5631\u5490\u4e2d\u3002\u5979\u4e5f\u77e5\u9053\u4e86\u56db\u70b9\u4e8b\u5b9e\uff0c\u4e00\u3001\u8fd9\u4eba\u662f\u516c\u516c\u7684\u597d\u670b\u53cb\uff1b\u4e8c\u3001\u8fd9\u4e2a\u4eba\u6b66\u529f \u5728\u516c\u516c\u4e4b\u4e0a\uff1b\u4e09\u3001\u8fd9\u4e2a\u4eba\u662f\u590d\u59d3\u8036\u5f8b\uff1b\u56db\u3001\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u7b54\u5e94\u4e86\u516c\u516c\uff0c\u6536\u5979\u7684\u51b2\u513f\u505a\u5f92\u5f1f\u3002 \u4e8c\u3001\u4e09\u4e24\u70b9\uff0c\u5df2\u7ecf\u662f\u548c\u54c8\u5fc5\u56fe\u6240\u8bf4\u7684\u76f8\u7b26\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u672b\u5df2\uff0c\u53ea\u542c\u5f97\u5929\u9999\u4ead\u90a3\u8fb9\uff0c\u54c8\u5fc5\u56fe\u53c8\u5728\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u8fbd\u4ea1\u81f3\u4eca\u3002\u5df2\u6709\u4e8c\u5341\u4f59\u5e74\uff0c\u8fd9\u4e2a\u4eba\u54b1\u4eec\u8fd8\u662f\u59cb\u7ec8\u6293\u4e0d\u5230\u4ed6\u3002\u5b8c\u989c\u5c06\u519b\uff0c\u4f60\u53ef\u77e5\u9053\u5176\u4e2d \u7f18\u6545\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u5426\u56e0\u4e3a\u6b64\u4eba\u6b66\u529f\u592a\u9ad8\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u6b66\u529f\uff0c\u662f\u5728\u8fbd\u56fd\u706d\u4ea1\u4e4b\u540e\uff0c\u624d\u7ec3\u5f97\u8fd9\u4e48\u9ad8\u7684\u3002 \u3000\u3000\u5728\u8fbd\u56fd\u706d\u4ea1\u4e4b\u65f6\uff0c\u4ed6\u8fd8\u672a\u5230\u4e8c\u5341\u5c81\uff0c\u867d\u7136\u61c2\u5f97\u4e00\u70b9\u6b66\u529f\uff0c\u5374\u8fd8\u53ca\u4e0d\u4e0a\u54b1\u4eec\u4e00\u4e2a\u666e\u901a\u7684\u5df4 \u56fe\u9c81\uff01\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u5f53\u65f6\u54b1\u4eec\u7684\u4eba\u8fd8\u672a\u77e5\u9053\u4ed6\u7684\u8eab\u5206\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\uff0c\u8001\u7687\u4e0a\u662f\u65e9\u5df2\u77e5\u9053\u4ed6\u7684\u8eab\u5206\u3002\u4e5f\u77e5\u9053\u4ed6\u662f\u77e2\u5fd7\u60f3\u6062\u590d\u6c5f\u56fd\u7684\u4e86\uff0c\u8001\u7687 \u4e0a\u5728\u706d\u4e86\u8fbd\u56fd\u4e4b\u540e\uff0c\u5c31\u53d1\u51fa\u5bc6\u4ee4\uff0c\u8981\u4e03\u4e2a\u91d1\u5e10\u6b66\u58eb\u8d1f\u8d23\u53bb\u7f09\u62ff\u8fd9\u4eba\u5f52\u6848\uff0c\u6211\u5c31\u662f\u8fd9\u4e03\u4e2a\u4e2d\u4e4b \u4e00\u4eba\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u90a3\u4e3a\u4ec0\u4e48\u6293\u4e0d\u5230\u5462\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u56e0\u4e3a\u6709\u6a80\u516c\u76f4\u5305\u5e87\u4ed6\u3002\u6a80\u516c\u76f4\u5f53\u65f6\u8fd8\u662f\u54b1\u4eec\u91d1\u56fd\u7684\u8d1d\u52d2\uff0c\u800c\u4e14\u662f\u63e1\u6709\u519b\u6743 \u7684\u8d1d\u52d2\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u58f0\u97f3\u7565\u4f4e\uff1a\u201c\u540e\u6765\u6a80\u516c\u76f4\u4e4b\u6240\u4ee5\u8981\u9003\u4ea1\uff0c\u548c\u8001\u7687\u4e0a\u653f\u89c1\u4e0d\u540c\uff0c\u56fa\u7136\u662f \u6700\u5927\u7684\u539f\u56e0\u3002\u4f46\u4ed6\u77e5\u9053\u4e86\u8001\u7687\u4e0a\u77e5\u9053\u4ed6\u5305\u5e87\u90a3\u4eba\u7684\u79d8\u5bc6\uff0c\u4e5f\u662f\u4fc3\u4f7f\u4ed6\u9003\u4ea1\u7684\u539f\u56e0\u4e4b\u4e00\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\u3002\u5f20\u96ea\u6ce2\u4e00\u9897\u5fc3\u6026\u7136\u800c\u52a8\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u4eba\u548c\u516c\u516c\u662f\u6709\u8fc7\u8fd9\u6837\u4e00\u6bb5\u4ea4\u60c5\uff0c\u602a\u4e0d\u5f97 \u516c\u516c\u653e\u5fc3\u628a\u51b2\u513f\u6258\u4ed8\u7ed9\u4ed6\u4e86\uff01\u201d\u4ece\u54c8\u5fc5\u56fe\u7684\u53e3\u4e2d\u5df1\u8bc1\u5b9e\u4e86\u8fd9\u4e2a\u4eba\u662f\u5979\u516c\u516c\u7684\u597d\u670b\u53cb\u4e86\u3002\u516c\u516c \u8bf4\u7684\u90a3\u4e2a\u4eba\uff0c\u5c31\u662f\u4ed6\u4eec\u8bf4\u7684\u8fd9\u4eba\u4e0a\u4eba\uff0c\u90a3\u662f\u4e00\u5b9a\u4e0d\u4f1a\u9519\u4e86\uff01\u5979\u7b2c\u4e00\u6b21\u542c\u89c1\u8fd9\u4e2a\u4eba\u7684\u6d88\u606f\uff0c\u4f46 \u8fd9\u4e2a\u4eba\u76ee\u524d\u5728\u4f55\u5904\u5462\uff1f\u54c8\u5fc5\u56fe\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u90a3\u5e74\u6211\u5949\u8001\u7687\u4e0a\u7684\u5bc6\u4ee4\uff0c\u53bb\u5ba3\u64c5\u516c\u76f4\u56de\u671d\uff0c\u540e\u6765 \u65b9\u59cb\u77e5\u9053\uff0c\u8fd9\u4e2a\u4eba\u5148\u6211\u4e09\u5929\uff0c\u5df2\u7ecf\u5230\u8fc7\u76d8\u9f99\u5c71\u89c1\u8fc7\u6a80\u516c\u76f4\u4e86\u3002\u4e0d\u4f46\u5982\u6b64\uff0c\u4ed6\u5728\u6211\u8d1f\u4f24\u4e4b\u540e\u7684 \u7b2c\u4e8c\u5929\uff0c\u53c8\u91cd\u56de\u76d8\u9f99\u5c71\u3002\u8fd9\u4e00\u6b21\u6070\u5de7\u9047\u4e0a\u524d\u5f80\u641c\u5c71\u7684\u4e00\u5c0f\u961f\u5fa1\u6797\u519b\uff0c\u4ed6\u628a\u8fd9\u5c0f\u961f\u5171\u6709\u4e09\u5341\u591a \u4eba\u7684\u5fa1\u6797\u519b\uff0c\u8fde\u540c\u4e24\u4e2a\u6709\u5df4\u56fe\u9c81\u8854\u7684\u90fd\u5c09\u5728\u5185\uff0c\u6740\u5f97\u4e00\u4e2a\u4e0d\u7559\uff01\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5bb6\u4f2f \u7236\u4e5f\u66fe\u548c\u6211\u8bf4\u8fc7\uff0c\u4ed6\u8bf4\u8fd9\u662f\u5fa1\u6797\u519b\u7684\u5947\u803b\u5927\u8fb1\u3002\u53ea\u6068\u4e0d\u77e5\u6b64\u4eba\u9003\u5f80\u4f55\u65b9\uff0c\u65e0\u6cd5\u7f09\u62ff\u5f52\u6848\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u597d\u5728\u73b0\u5728\u5df2\u7ecf\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u5728\u54ea\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u90a3\u6b21\u4ed6\u9003\u51fa\u76d8\u9f99\u5c71\u4e4b\u540e\uff0c\u636e\u8bf4\u662f\u9003\u5f80\u5b8b\u56fd\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u5728\u53bb\u62dc\u8bbf\u4e2d\u539f\u56db\u5927 \u95e8\u6d3e\u7684\u638c\u95e8\u7814\u8ba8\u6b66\u529f\u7684\uff1b\u4e5f\u6709\u4eba\u8bf4\uff0c\u4ed6\u662f\u53bb\u627e\u5cb3\u98de\u7684\u65e7\u5c5e\u3002\u610f\u6b32\u4e0e\u5cb3\u98de\u7684\u65e7\u5c5e\u7ed3\u76df\u53cd\u91d1\u7684\u3002 \u4f17\u8bf4\u7eb7\u7ead\u3002\u4e0d\u77e5\u771f\u5047\u3002\u4f46\u6709\u4e00\u70b9\uff0c\u73b0\u5728\u5374\u662f\u53ef\u4ee5\u8bc1\u5b9e\u7684\u4e86\u3002\u4ed6\u4e0a\u4e2a\u6708\u5df2\u7ecf\u79bb\u5f00\u5b8b\u56fd\uff0c\u76ee\u524d\u5f88 \u53ef\u80fd\u5c31\u662f\u5728\u8d35\u8282\u4f7f\u6240\u8f96\u5883\u5185\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u5546\u5dde\u5185\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u6211\u51ed\u4ed6\u7684\u884c\u7a0b\u63a8\u65ad\u7684\uff0c\u6216\u8005\u5728\u9014\u4e2d\u9017\u7559\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u4f46\u603b\u4e4b\u4e0d\u53ef\u4e0d \u9632\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u7acb\u5373\u4e0b\u4ee4\uff0c\u8981\u4ed6\u4eec\u6ce8\u610f\u5916\u6765\u7684\u53ef\u7591\u4eba\u7269\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e5f\u4e0d\u5fc5\u9a6c\u4e0a\u5c31\u53bb\u3002\u6b64\u4eba\u6b66\u529f\u592a\u9ad8\u3002\u5207\u5fcc\u6253\u8349\u60ca\u86c7\uff0c\u86c7\u6349\u4e0d\u5230\u3002\u53cd\u88ab\u86c7\u54ac\u3002 \u660e\u5929\u6709\u4e09\u4e2a\u91d1\u8d26\u6b66\u58eb\u4f1a\u6765\u5546\u5dde\u3002\u5f85\u4ed6\u4eec\u6765\u4e86\uff0c\u54b1\u4eec\u518d\u5408\u8ba1\u5408\u8ba1\uff0c\u5982\u4f55\u5bf9\u4ed8\u6b64\u4eba\uff01\u201d\u4ece\u8a00\u8bed\u4e2d \u4e5f\u53ef\u542c\u5f97\u51fa\u6765\uff0c\u54c8\u5fc5\u56fe\u5bf9\u8fd9\u4e2a\u4eba\u5b9e\u662f\u5bb3\u6015\u4e4b\u6781\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5728\u5047\u5c71\u90a3\u8fb9\u5077\u542c\u3002\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u559c\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u6765\u5230\u5546\u5dde\uff0c\u78b0\u4e0a\u7684\u673a\u4f1a\u867d\u7136 \u5fae\u4e4e\u5176\u5fae\u3002\u4f46\u603b\u6bd4\u4ee5\u524d\u5b8c\u5168\u4e0d\u77e5\u9053\u4ed6\u7684\u6d88\u606f\u597d\u591a\u4e86\uff01\u201d \u3000\u3000\u4f46\u968f\u5373\u53c8\u662f\u5fc3\u5934\u4e00\u6c89\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u54c8\u5fc5\u56fe\u660e\u5929\u5c31\u8981\u6211\u7684\u51b2\u513f\u53bb\u89c1\u4ed6\uff0c\u51b2\u513f\u7684\u5e08\u7236\u7eb5\u7136\u6765 \u5230\u4e86\u5546\u5dde\uff0c\u4e5f\u662f\u8fdc\u6c34\u4e0d\u6551\u8fd1\u706b\u3002\u6211\u7684\u51b2\u513f\u5982\u4f55\u624d\u80fd\u907f\u8fc7\u8fd9\u573a\u707e\u96be\u5462\uff1f\u201d\u6b63\u5f53\u5979\u60ca\u559c\u4ea4\u96c6\u4e4b \u9645\uff0c\u5ffd\u542c\u5f97\u6709\u811a\u6b65\u58f0\u5411\u5979\u4e4b\u5904\u8d70\u6765\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u7ed9\u4ed6\u53d1\u73b0\u66f4\u52a0\u4e0d\u5999\uff0c\u7d22\u6027\u81ea\u5df2\u4ece\u6697\u5904\u5148\u8d70\u51fa\u6765\u3002\u8fd9\u4e2a\u4eba\u662f\u5b8c\u989c\u9274\u7684\u624b\u4e0b\u7684\u536b\u58eb\uff0c \u548c\u5979\u4e5f\u662f\u76f8\u719f\u7684\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5f20\u96ea\u6ce2\u5c31\u628a\u4e00\u6839\u6307\u5934\u575a\u4e86\u8d77\u6765\uff0c\u8d34\u7740\u5634\u5507\uff0c\u8f7b\u8f7b\u5618\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8fd9\u4e2a\u536b\u58eb\u662f\u77e5\u9053\u5979\u7684\u8eab\u4efd\u7684\uff0c\u89c1\u5979\u5982\u6b64\u793a\u610f\uff0c\u8fde\u5fd9\u8e51\u624b\u8e51\u811a\u5730\u548c\u5979\u8d70\u51fa\u56ed\u95e8\uff0c\u65b9\u6562\u5f00 \u53e3\u3002 \u3000\u3000\u201c\u54c8\u5927\u4eba\u8fd8\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u662f\u521a\u4ece\u5916\u9762\u56de\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u90a3\u536b\u58eb\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u70b9\u4e8b\u60c5\u60f3\u7980\u544a\u5c06\u519b\uff0c\u4f46\u542c\u5f97\u5d14\u603b\u7ba1\u8bf4\u3002\u5c06\u519b\u966a\u94a6\u5dee\u5927\u4eba \u7740\u4e86\u4e00\u56de\u6b4c\u821e\uff0c\u5c31\u53eb\u4f17\u4eba\u9000\u4e0b\uff0c\u5d14\u603b\u7ba1\u4e5f\u4e0d\u6562\u66ff\u6211\u901a\u62a5\uff0c\u4f46\u4ed6\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u5949\u4e86\u592b\u4eba\u4e4b\u547d\uff0c \u4fee\u526a\u82b1\u679d\u7684\u3002 \u3000\u3000\u4f60\u4e0d\u6bd4\u6211\u4eec\uff0c\u5c06\u519b\u5bf9\u4f60\u65e0\u987b\u907f\u5fcc\uff0c\u6240\u4ee5\u5d14\u603b\u7ba1\u53eb\u6211\u5148\u627e\u4f60\u6253\u542c\u6253\u542c\u3002 \u3000\u3000\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u662f\u5c06\u519b\u7684\u4eb2\u4fe1\u536b\u58eb\uff0c\u8981\u89c1\u5c06\u519b\uff0c\u4f55\u987b\u5148\u6765\u5411\u6211\u6253\u542c\u3002 \u3000\u3000\u201d\u90a3\u536b\u58eb\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u6837\u8bf4\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u6211\u5f53\u7136\u65e0\u987b\u7980\u62a5\uff0c\u4f46\u6b64\u9645\u5374\u662f\u6709\u94a6\u5dee\u5927\u4eba\u5728 \u91cc\u8fb9\u7684\u5440\u3002\u4e07\u4e00\u4ed6\u4eec\u6b63\u5546\u8bae\u4ec0\u4e48\u519b\u56fd\u5927\u4e8b\uff0c\u6211\u8fdb\u53bb\u6253\u6270\uff0c\u90a3\u5c31\u4e0d\u597d\u4e86\u3002\u5bf9\u5566\uff0c\u5170\u59d1\uff0c\u4f60\u600e\u4e48 \u4e5f\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5c06\u519b\u8981\u6211\u966a\u90a3\u4f4d\u54c8\u5927\u4eba\u770b\u4e86\u4e00\u4f1a\u7261\u4e39\uff0c\u7136\u540e\u4ed6\u8bf4\uff0c\u82b1\u679d\u660e\u5929\u4fee\u526a\u4e0d\u8fdf\uff0c\u6211 \u5f53\u7136\u4e50\u5f97\u6109\u61d2\u4e86\u3002\u201d \u3000\u3000\u90a3\u536b\u58eb\u9053\uff1a\u201c\u5170\u59d1\uff0c\u591a\u8c22\u4f60\u63d0\u9192\u6211\uff0c\u4f60\u60f3\u8981\u4ec0\u4e48\u4e1c\u897f\uff0c\u660e\u5929\u6211\u5c31\u4e70\u6765\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u6cb3\u5e76\u6ca1\u6709\u63d0\u9192\u4f60\u4ec0\u4e48\u5440\u3002\u201d \u3000\u3000\u90a3\u536b\u58eb\u7b11\u9053\uff1a\u201c\u5f7c\u6b64\u5fc3\u7167\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u539f\u6765\u4ed6\u662f\u8fd9\u6837\u60f3\u7684\uff0c\u5170\u59d1\u662f\u592b\u4eba\u8eab\u8fb9\u6700\u5f97\u5ba0\u7684 \u5973\u4f63\uff0c\u5b8c\u989c\u9274\u90fd\u8981\u5979\u907f\u5f00\uff0c\u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u4ed6\u548c\u54c8\u5fc5\u56fe\u6240\u8bf4\u7684\u4e8b\u60c5\uff0c\u662f\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u542c\u89c1\u7684 \u4e86\u3002\u5170\u59d1\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4ed6\u3002\u5373\u7b49\u4e8e\u63d0\u9192\u4ed6\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u5728\u5546\u91cf\u4ec0\u4e48\u3001\u4e0d\u8fc7\u5982\u679c\u4f60\u7684\u4e8b\u60c5\u786e\u5b9e\u975e\u5e38\u7d27\u8981\u2014\u2014\u201d \u3000\u3000\u90a3\u536b\u58eb\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8bf4\u5927\u4e0d\u5927\uff0c\u8bf4\u5c0f\u4e0d\u5c0f\u3002\u5546\u5dde\u57ce\u5185\uff0c\u53d1\u73b0\u4e00\u4e2a\u8e2a\u8ff9\u53ef\u7591\u7684\u4eba\u7269\uff0c\u6211\u60f3 \u6c42\u5c06\u519b\u6307\u793a\u3002\u4f60\u8fd9\u6837\u95ee\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u66ff\u6211\u2014\u2014\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5176\u5b9e\u5f88\u60f3\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u4f46\u4e0d\u6562\u592a\u7740\u75d5\u8ff9\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u968f\u4fbf\u95ee\u95ee\uff0c\u5c06\u519b\u521a \u521a\u53eb\u6211\u56de\u53bb\u4f3a\u5019\u592b\u4eba\uff0c\u6211\u8fd8\u600e\u6562\u591a\u4e8b\u3002\u201d \u3000\u3000\u90a3\u536b\u58eb\u9053\uff1a\u201c\u597d\u5728\u8fd9\u4ef6\u4e8b\u4e5f\u5e76\u975e\u9a6c\u4e0a\u5c31\u8981\u529e\u7684\u3002\u6211\u53ef\u4ee5\u5728\u8fd9\u91cc\u7b49\u5019\u3002 \u3000\u3000\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5728\u8fd9\u91cc\u7b49\u5019\u597d\u4e86\u3002\u201d\u90a3\u536b\u58eb\u4e3a\u4e86\u8ba8\u597d\u5979\u3002\u8bf4\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c \u4f60\u77e5\u9053\u4e86\u4e00\u5b9a\u4f1a\u9ad8\u5174\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u536b\u58eb\u9053\uff1a\u201c\u6211\u56de\u6765\u7684\u65f6\u5019\uff0c\u521a\u597d\u89c1\u4ee4\u90ce\u5728\u573a\u5b50\u4e0a\u8ddf\u8001\u696e\u7ec3\u6b66\uff0c\u4e00\u5957\u4f0f\u864e\u62f3\u6253\u5f97\u864e\u864e\u751f \u98ce\uff0c\u771f\u662f\u597d\u5f97\u4e0d\u5f97\u4e86\uff01\u201d\u8fd9\u4e2a\u201c\u8001\u696e\u201d\u5355\u540d\u4e00\u4e2a\u201c\u5ca9\u201d \u3000\u3000\u5b57\uff0c\u662f\u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u5728\u4e00\u4f17\u536b\u58eb\u4e4b\u4e2d\u3002\u4ed6\u6559\u6a80\u7fbd\u51b2\u7ec3\u6b66\uff0c\u662f\u6559\u5f97\u6700\u4e3a\u7528\u5fc3\u7684\u4e00 \u4e2a\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u73a9\u800d\uff0c\u4e5f\u503c\u5f97\u62ff\u6765\u5938\u5956\u201d\u90a3\u536b\u58eb\u7b11\u9053\uff1a\u201c\u5355\u6211\u5938\u5956\uff0c\u6ca1\u6709\u4ec0 \u4e48\u7a00\u5947\u3002\u8fd8\u6709\u4e00\u4e2a\u4eba\u6bd4\u6211\u66f4\u4e3a\u5938\u8d5e\u4ed6\u5462\uff0c\u4f60\u731c\u662f\u8c01\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5e9c\u4e2d\u536b\u58eb\u5c11\u8bf4\u4e5f\u6709\u4e00\u767e\u6570\u5341\u4eba\uff0c\u6211\u600e\u4e48\u731c\u5f97\u4e2d\u662f\u8c01\uff1f\u518d\u8bf4\uff0c\u4f60\u4eec\u5938\u5956\u4ed6\uff0c \u4e5f\u4e0d\u8fc7\u662f\u54c4\u5c0f\u5b69\u5b50\u559c\u6b22\u7f62\u4e86\u3002\u6211\u53ef\u4e0d\u662f\u5c0f\u5b69\u5b50\u3002\u201d \u3000\u3000\u90a3\u536b\u58eb\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53ef\u4e0d\u662f\u666e\u901a\u7684\u536b\u58eb\uff0c\u662f\u6211\u4eec\u536b\u58eb\u7684\u5934\u5934\u3002\u6709\u5df4\u56fe\u9c81\u5934\u8854\u7684\u519b\u526f\u961f \u957f\u8f66\u7f2d\uff01\u4f60\u4e5f\u77e5\u9053\u4ed6\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\u7684\uff0c\u4ed6\u4e00\u5411\u6c89\u9ed8\u5be1\u8a00\uff0c\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f\u4e8c\uff0c\u6211\u8ddf\u4ed6\u8fd9 \u4e48\u591a\u5e74\uff0c\u8fd8\u4ece\u6765\u6ca1\u6709\u542c\u89c1\u5730\u5938\u5956\u8fc7\u522b\u4eba\u3002\u4f46\u8fd9\u6b21\u4ed6\u5bf9\u4ee4\u90ce\u53ef\u662f\u5927\u8d5e\u7279\u8d5e\uff0c\u8bf4\u662f\u8fd9\u5957\u4f0f\u864e\u62f3\u4ee4 \u90ce\u624d\u4e0d\u8fc7\u5b66\u4e86\u5341\u6765\u5929\uff0c\u6253\u51fa\u6765\u975e\u4f46\u4e2d\u89c4\u4e2d\u77e9\uff0c\u751a\u81f3\u6bd4\u8bb8\u591a\u51fa\u8eab\u5c11\u6797\u5bfa\u7684\u5f1f\u5b50\u8fd8\u8981\u9ad8\u660e\u3002\u4ed6\u8bf4 \u4ee4\u90ce\u662f\u5929\u751f\u7684\u7ec3\u6b66\u8d44\u8d28\uff0c\u8fde\u8f66\u7f2d\u90fd\u5938\u5956\u4f60\u7684\u513f\u5b50\uff0c\u8fd8\u4e0d\u503c\u5f97\u4f60\u9ad8\u5174\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6447\u5934\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5c31\u662f\u559c\u6b22\u7ec3\u6b66\uff0c\u6211\u5012\u62c5\u5fc3\u4ed6\u4e0d\u52a1\u6b63\u4e1a\u5462\u3002\u201d \u3000\u3000\u5f53\u7136\u5979\u662f\u6545\u610f\u8fd9\u6837\u8bf4\u7684\u3002\u5176\u8f9e\u82e5\u6709\u61be\u4e4b\uff0c\u5176\u4e5f\u5219\u5b9e\u559c\u4e4b\u3002\u90a3\u536b\u58eb\u9053\uff1a\u201c\u5170\u59d1\uff0c\u4f60\u8fd9\u8bdd\u53ef \u8bf4\u5f97\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002\u600e\u80fd\u8bf4\u7ec3\u6b66\u4e0d\u662f\u6b63\u4e1a\u5462\uff1f\u54b1\u4eec\u7684\u5b8c\u989c\u5c06\u519b\u5c31\u662f\u6b66\u529f\u7ec3\u5f97\u5f88\u597d\u7684\uff0c\u4ee4\u90ce\u5c06\u6765 \u2014\u2014\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u5de5\u592b\u548c\u4f60\u95f2\u78d5\u7259\u4e86\uff0c\u6211\u7684\u5b69\u5b50\u600e\u80fd\u548c\u5c06\u519b\u6765\u6bd4\uff0c\u6211\u4e5f\u4e0d\u6307\u671b\u4ec0\u4e48\u5bcc \u8d35\uff0c\u53ea\u76fc\u5b69\u5b50\u80fd\u5b89\u5b89\u4efd\u4efd\u7684\u5b88\u5728\u6211\u7684\u8eab\u8fb9\u3002\u5bf9\u4e0d\u4f4f\u3002\u6211\u8981\u56de\u53bb\u4f8d\u5019\u592b\u4eba\u4e86\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49 \u5427\u3002\u201d\u5979\u4e00\u4e2a\u4eba\u8d70\u5f00\uff0c\u5fc3\u91cc\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u62c5\u5fe7\u3002\u6b22\u559c\u7684\u513f\u5b50\u7ec3\u6b66\uff0c\u8fdb\u5883\u795e\u901f\uff0c\u80fd\u591f\u535a\u5f97\u8f66 \u7f2d\u7684\u79f0\u8d5e\uff0c\u7684\u786e\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u7684\u3002 \u3000\u3000\u62c5\u5fe7\u7684\u662f\uff0c\u513f\u5b50\u5c31\u5728\u5e9c\u4e2d\u7684\u7ec3\u6b66\u573a\u4e0a\u7ec3\u6b66\uff0c\u90a3\u4e48\u8bf4\u4e0d\u5b9a\u5b8c\u989c\u9274\u4eca\u5929\u5c31\u4f1a\u628a\u5979\u7684\u513f\u5b50\u53eb\u53bb \u89c1\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u201c\u4ed6\u82e5\u662f\u5728\u5916\u9762\u73a9\u800d\u8fd8\u672a\u56de\u6765\u90a3\u8fd8\u597d\u4e9b\uff0c\u53ef\u4ee5\u591a\u4e00\u4e2a\u665a\u4e0a\u7684\u65f6\u95f4\u7ed9\u6211\u60f3\u5e94\u4ed8\u7684\u529e\u6cd5\u3002\u4f46 \u82e5\u8fd9\u4e2a\u536b\u571f\u5f85\u4f1a\u513f\u4e07\u2014\u548c\u5c06\u519b\u63d0\u53ca\u51b2\u513f\u4eca\u5929\u7ec3\u6b66\u7684\u4e8b\uff0c\u54c8\u5fc5\u56fe\u6050\u6015\u7acb\u523b\u5c31\u4f1a\u53eb\u5c06\u519b\u53eb\u4ed6\u6765\uff0c \u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u8282\u5ea6\u4f7f\u8859\u95e8\u89c4\u77e9\u5f88\u4e25\uff0c\u5185\u5802\u7684\u4f63\u4eba\u662f\u4e0d\u80fd\u8e0f\u51fa\u5916\u95e8\u7684\u3002\u5979\u867d\u7136\u5f97\u5ba0\uff0c\u4e5f\u8fd8\u662f\u4e2a\u5973\u4f63\u4eba\u7684 \u8eab\u4efd\u3002\u4ee5\u4e00\u4e2a\u5973\u4f63\u4eba\u7684\u8eab\u4efd\uff0c\u8dd1\u5230\u7ec3\u6b66\u573a\u4e0a\u770b\u4eba\u7ec3\u6b66\uff0c\u90a3\u662f\u8fde\u60f3\u4e5f\u4e0d\u80fd\u60f3\u7684\u4e8b\u60c5\u3002\u987b\u77e5\u7ec3\u6b66 \u573a\u8fd9\u79cd\u5730\u65b9\uff0c\u867d\u7136\u6ca1\u6709\u660e\u6587\u89c4\u5b9a\u7981\u6b62\u5973\u4eba\u8fdb\u53bb\uff0c\u5b9e\u9645\u4e0a\u4e5f\u7b49\u4e5f\u662f\u201c\u5973\u4eba\u7684\u7981\u5730\u201d\u7684\u4e86\u3002\u4f55\u51b5 \u5373\u4f7f\u90a3\u4e9b\u536b\u58eb\u4e0d\u8d76\u5979\u8d70\uff0c\u5979\u8dd1\u5230\u7ec3\u6b66\u573a\u53bb\u53eb\u5979\u7684\u513f\u5b50\u56de\u6765\uff0c\u90a3\u4e5f\u662f\u592a\u7740\u75d5\u8ff9\u7684\u3002 \u3000\u3000\u600e\u4e48\u529e\u5462\uff1f\u5f20\u96ea\u6ce2\u5fc3\u4e71\u5982\u9ebb\uff0c\u7ec8\u4e8e\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u5979\u6ca1\u6709\u56de\u53bb\u201c\u4f3a\u5019\u201d\u592b\u4eba\uff0c\u800c\u662f\u5230\u4e00\u4e2a\u8001\u82b1\u738b\u7684\u4f4f\u6240\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u8001\u82b1\u738b\u53eb\u4f5f\u7389\u6842\uff0c\u662f\u6559\u5979\u79cd\u7261\u4e39\u7684\u5e08\u5085\u3002\u7531\u4e8e\u5e74\u7eaa\u8001\u8fc8\uff0c\u5982\u4eca\u5df2\u662f\u7b49\u4e8e\u534a\u9000\u4f11\u3002\u8282 \u5ea6\u4f7f\u8859\u95e8\u6709\u4e24\u4e2a\u82b1\u56ed\uff0c\u5185\u82b1\u56ed\u662f\u4e13\u683d\u7261\u4e39\u7684\uff0c\u8fd8\u6709\u4e00\u4e2a\u5916\u82b1\u56ed\u517c\u79cd\u5176\u4ed6\u82b1\u6728\uff0c\u4f5f\u7389\u6842\u5c31\u4f4f\u5916 \u82b1\u56ed\uff0c\u5f20\u96ea\u6ce2\u662f\u65f6\u5e38\u5230\u4ed6\u90a3\u91cc\u201c\u4e32\u95e8\u5b50\u201d\u7684\uff0c\u4e0d\u4f1a\u5f15\u8d77\u522b\u4eba\u7591\u5fc3\u3002\u8001\u82b1\u738b\u89c1\u5979\u6765\u5230\uff0c\u751a\u662f\u559c \u6b22\u3002 \u3000\u3000\u201c\u542c\u8bf4\u4ece\u4eac\u57ce\u6765\u7684\u54c8\u5927\u4eba\u548c\u5c06\u519b\u5728\u8d4f\u7261\u4e39\uff0c\u4ed6\u4eec\u5f88\u8d4f\u8bc6\u4f60\u79cd\u7684\u7261\u4e39\u5427\uff1f\u201d \u3000\u3000\u201c\u7261\u4e39\u79cd\u5f97\u597d\uff0c\u8fd9\u90fd\u662f\u4f5f\u5e08\u5085\u4f60\u7684\u529f\u52b3\u3002\u54c8\u5927\u4eba\u95ee\u4e86\u6211\u4e00\u4e9b\u79fb\u690d\u83cf\u6cfd\u7261\u4e39\u7684\u65b9\u6cd5\uff0c\u6211\u7684 \u8fd9\u70b9\u73a9\u827a\u90fd\u662f\u5e08\u5085\u4f60\u6559\u4f1a\u6211\u7684\uff0c\u6211\u6309\u5e08\u5085\u6240\u6559\u7684\u8bf4\u7ed9\u4ed6\u542c\uff0c\u5e94\u4ed8\u4e86\u8fc7\u53bb\uff0c\u6ca1\u7ed9\u4f60\u8001\u4e22\u8138\u3002\u201d \u3000\u3000\u4f5f\u7389\u6842\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u65e9\u5df1\u9752\u51fa\u4e8e\u84dd\u4e86\uff0c\u6211\u665a\u5e74\u6536\u4e86\u4f60\u8fd9\u6837\u4e00\u4f4d\u597d\u5f92\u5f1f\uff0c\u5b9e\u5728\u662f\u5e73\u751f\u6700 \u5f97\u610f\u7684\u4e8b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u6765\u5411\u5e08\u7236\u9053\u8c22\u7684\uff0c\u8981\u4e0d\u662f\u4f5f\u5e08\u5085\u4f60\u628a\u5e73\u751f\u7684\u6280\u827a\u90fd\u4f20\u7ed9\u6211\uff0c\u6211\u54ea \u91cc\u6709\u4eca\u5929\u7684\u597d\u65e5\u5b50\u8fc7\u3002\u201d\u4f5f\u7389\u6842\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u8bf4\u8d77\u4f60\u7684\u513f\u5b50\uff0c\u90a3\u66f4\u662f\u524d\u9014\u5982\u9526\u4e86\u3002\u4ed6\u5b66\u7684\u53ef \u662f\u505a\u519b\u5b98\u7684\u672c\u9886\uff0c\u5c06\u6765\u4e00\u5b9a\u4f1a\u6709\u51fa\u606f\u7684\u3002\u5170\u59d1\uff0c\u4f60\u771f\u597d\u798f\u6c14\u3002\u201d\u8bf4\u81f3\u6b64\u5904\u3002\u4e0d\u89c9\u6709\u70b9\u9eef\u7136\u3002 \u56e0\u4e3a\u4ed6\u662f\u65e0\u513f\u65e0\u5973\u7684\u3002 \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u5f20\u96ea\u6ce2\u4e5f\u6b63\u4e5f\u53ef\u4ee5\u7275\u5165\u6b63\u9898\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f5f\u5e08\u5085\uff0c\u4f60\u559c\u6b22\u6211\u8fd9\u5b69\u5b50\uff0c\u6211\u53eb\u4ed6 \u8ba4\u4f60\u505a\u5e72\u7239\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4f5f\u7389\u6842\u9053\uff1a\u201c\u8fd9\u6211\u600e\u4e48\u6562\u5f53\uff1f\u5170\u59d1\uff0c\u4f60\u6709\u8fd9\u5fc3\u610f\u6211\u5df2\u611f\u6fc0\u4f60\u4ed6\u5462\uff0c\u4f60\u731c\u662f\u8c01\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5e9c\u4e2d\u536b\u58eb\u5c11\u8bf4\u4e5f\u6709\u4e00\u767e\u6570\u5341\u4eba\uff0c\u6211\u600e\u4e48\u731c\u5f97\u4e2d\u662f\u8c01\uff1f\u518d\u8bf4\uff0c\u4f60\u4eec\u5938\u5956\u4ed6\uff0c \u4e5f\u4e0d\u8fc7\u662f\u54c4\u5c0f\u5b69\u5b50\u559c\u6b22\u7f62\u4e86\u3002\u6211\u53ef\u4e0d\u662f\u5c0f\u5b69\u5b50\u3002\u201d \u3000\u3000\u90a3\u536b\u58eb\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53ef\u4e0d\u662f\u666e\u901a\u7684\u536b\u58eb\uff0c\u662f\u6211\u4eec\u536b\u58eb\u7684\u5934\u5934\u3002\u6709\u5df4\u56fe\u9c81\u5934\u8854\u7684\u519b\u526f\u961f \u957f\u8f66\u7f2d\uff01\u4f60\u4e5f\u77e5\u9053\u4ed6\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\u7684\uff0c\u4ed6\u4e00\u5411\u6c89\u9ed8\u5be1\u8a00\uff0c\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f\u4e8c\uff0c\u6211\u8ddf\u4ed6\u8fd9 \u4e48\u591a\u5e74\uff0c\u8fd8\u4ece\u6765\u6ca1\u6709\u542c\u89c1\u5730\u5938\u5956\u8fc7\u522b\u4eba\u3002\u4f46\u8fd9\u6b21\u4ed6\u5bf9\u4ee4\u90ce\u53ef\u662f\u5927\u8d5e\u7279\u8d5e\uff0c\u8bf4\u662f\u8fd9\u5957\u4f0f\u864e\u62f3\u4ee4 \u90ce\u624d\u4e0d\u8fc7\u5b66\u4e86\u5341\u6765\u5929\uff0c\u6253\u51fa\u6765\u975e\u4f46\u4e2d\u89c4\u4e2d\u77e9\uff0c\u751a\u81f3\u6bd4\u8bb8\u591a\u51fa\u8eab\u5c11\u6797\u5bfa\u7684\u5f1f\u5b50\u8fd8\u8981\u9ad8\u660e\u3002\u4ed6\u8bf4 \u4ee4\u90ce\u662f\u5929\u751f\u7684\u7ec3\u6b66\u8d44\u8d28\uff0c\u8fde\u8f66\u7f2d\u90fd\u5938\u5956\u4f60\u7684\u513f\u5b50\uff0c\u8fd8\u4e0d\u503c\u5f97\u4f60\u9ad8\u5174\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u6447\u5934\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5c31\u662f\u559c\u6b22\u7ec3\u6b66\uff0c\u6211\u5012\u62c5\u5fc3\u4ed6\u4e0d\u52a1\u6b63\u4e1a\u5462\u3002\u201d \u3000\u3000\u5f53\u7136\u5979\u662f\u6545\u610f\u8fd9\u6837\u8bf4\u7684\u3002\u5176\u8f9e\u82e5\u6709\u61be\u4e4b\uff0c\u5176\u4e5f\u5219\u5b9e\u559c\u4e4b\u3002\u90a3\u536b\u58eb\u9053\uff1a\u201c\u5170\u59d1\uff0c\u4f60\u8fd9\u8bdd\u53ef \u8bf4\u5f97\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002\u600e\u80fd\u8bf4\u7ec3\u6b66\u4e0d\u662f\u6b63\u4e1a\u5462\uff1f\u54b1\u4eec\u7684\u5b8c\u989c\u5c06\u519b\u5c31\u662f\u6b66\u529f\u7ec3\u5f97\u5f88\u597d\u7684\uff0c\u4ee4\u90ce\u5c06\u6765 \u2014\u2014\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u5de5\u592b\u548c\u4f60\u95f2\u78d5\u7259\u4e86\uff0c\u6211\u7684\u5b69\u5b50\u600e\u80fd\u548c\u5c06\u519b\u6765\u6bd4\uff0c\u6211\u4e5f\u4e0d\u6307\u671b\u4ec0\u4e48\u5bcc \u8d35\uff0c\u53ea\u76fc\u5b69\u5b50\u80fd\u5b89\u5b89\u4efd\u4efd\u7684\u5b88\u5728\u6211\u7684\u8eab\u8fb9\u3002\u5bf9\u4e0d\u4f4f\u3002\u6211\u8981\u56de\u53bb\u4f8d\u5019\u592b\u4eba\u4e86\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49 \u5427\u3002\u201d\u5979\u4e00\u4e2a\u4eba\u8d70\u5f00\uff0c\u5fc3\u91cc\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u62c5\u5fe7\u3002\u6b22\u559c\u7684\u513f\u5b50\u7ec3\u6b66\uff0c\u8fdb\u5883\u795e\u901f\uff0c\u80fd\u591f\u535a\u5f97\u8f66 \u7f2d\u7684\u79f0\u8d5e\uff0c\u7684\u786e\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u7684\u3002 \u3000\u3000\u62c5\u5fe7\u7684\u662f\uff0c\u513f\u5b50\u5c31\u5728\u5e9c\u4e2d\u7684\u7ec3\u6b66\u573a\u4e0a\u7ec3\u6b66\uff0c\u90a3\u4e48\u8bf4\u4e0d\u5b9a\u5b8c\u989c\u9274\u4eca\u5929\u5c31\u4f1a\u628a\u5979\u7684\u513f\u5b50\u53eb\u53bb \u89c1\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u201c\u4ed6\u82e5\u662f\u5728\u5916\u9762\u73a9\u800d\u8fd8\u672a\u56de\u6765\u90a3\u8fd8\u597d\u4e9b\uff0c\u53ef\u4ee5\u591a\u4e00\u4e2a\u665a\u4e0a\u7684\u65f6\u95f4\u7ed9\u6211\u60f3\u5e94\u4ed8\u7684\u529e\u6cd5\u3002\u4f46 \u82e5\u8fd9\u4e2a\u536b\u571f\u5f85\u4f1a\u513f\u4e07\u2014\u548c\u5c06\u519b\u63d0\u53ca\u51b2\u513f\u4eca\u5929\u7ec3\u6b66\u7684\u4e8b\uff0c\u54c8\u5fc5\u56fe\u6050\u6015\u7acb\u523b\u5c31\u4f1a\u53eb\u5c06\u519b\u53eb\u4ed6\u6765\uff0c \u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u8282\u5ea6\u4f7f\u8859\u95e8\u89c4\u77e9\u5f88\u4e25\uff0c\u5185\u5802\u7684\u4f63\u4eba\u662f\u4e0d\u80fd\u8e0f\u51fa\u5916\u95e8\u7684\u3002\u5979\u867d\u7136\u5f97\u5ba0\uff0c\u4e5f\u8fd8\u662f\u4e2a\u5973\u4f63\u4eba\u7684 \u8eab\u4efd\u3002\u4ee5\u4e00\u4e2a\u5973\u4f63\u4eba\u7684\u8eab\u4efd\uff0c\u8dd1\u5230\u7ec3\u6b66\u573a\u4e0a\u770b\u4eba\u7ec3\u6b66\uff0c\u90a3\u662f\u8fde\u60f3\u4e5f\u4e0d\u80fd\u60f3\u7684\u4e8b\u60c5\u3002\u987b\u77e5\u7ec3\u6b66 \u573a\u8fd9\u79cd\u5730\u65b9\uff0c\u867d\u7136\u6ca1\u6709\u660e\u6587\u89c4\u5b9a\u7981\u6b62\u5973\u4eba\u8fdb\u53bb\uff0c\u5b9e\u9645\u4e0a\u4e5f\u7b49\u4e5f\u662f\u201c\u5973\u4eba\u7684\u7981\u5730\u201d\u7684\u4e86\u3002\u4f55\u51b5 \u5373\u4f7f\u90a3\u4e9b\u536b\u58eb\u4e0d\u8d76\u5979\u8d70\uff0c\u5979\u8dd1\u5230\u7ec3\u6b66\u573a\u53bb\u53eb\u5979\u7684\u513f\u5b50\u56de\u6765\uff0c\u90a3\u4e5f\u662f\u592a\u7740\u75d5\u8ff9\u7684\u3002 \u3000\u3000\u600e\u4e48\u529e\u5462\uff1f\u5f20\u96ea\u6ce2\u5fc3\u4e71\u5982\u9ebb\uff0c\u7ec8\u4e8e\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u5979\u6ca1\u6709\u56de\u53bb\u201c\u4f3a\u5019\u201d\u592b\u4eba\uff0c\u800c\u662f\u5230\u4e00\u4e2a\u8001\u82b1\u738b\u7684\u4f4f\u6240\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u8001\u82b1\u738b\u53eb\u4f5f\u7389\u6842\uff0c\u662f\u6559\u5979\u79cd\u7261\u4e39\u7684\u5e08\u5085\u3002\u7531\u4e8e\u5e74\u7eaa\u8001\u8fc8\uff0c\u5982\u4eca\u5df2\u662f\u7b49\u4e8e\u534a\u9000\u4f11\u3002\u8282 \u5ea6\u4f7f\u8859\u95e8\u6709\u4e24\u4e2a\u82b1\u56ed\uff0c\u5185\u82b1\u56ed\u662f\u4e13\u683d\u7261\u4e39\u7684\uff0c\u8fd8\u6709\u4e00\u4e2a\u5916\u82b1\u56ed\u517c\u79cd\u5176\u4ed6\u82b1\u6728\uff0c\u4f5f\u7389\u6842\u5c31\u4f4f\u5916 \u82b1\u56ed\uff0c\u5f20\u96ea\u6ce2\u662f\u65f6\u5e38\u5230\u4ed6\u90a3\u91cc\u201c\u4e32\u95e8\u5b50\u201d\u7684\uff0c\u4e0d\u4f1a\u5f15\u8d77\u522b\u4eba\u7591\u5fc3\u3002\u8001\u82b1\u738b\u89c1\u5979\u6765\u5230\uff0c\u751a\u662f\u559c \u6b22\u3002 \u3000\u3000\u201c\u542c\u8bf4\u4ece\u4eac\u57ce\u6765\u7684\u54c8\u5927\u4eba\u548c\u5c06\u519b\u5728\u8d4f\u7261\u4e39\uff0c\u4ed6\u4eec\u5f88\u8d4f\u8bc6\u4f60\u79cd\u7684\u7261\u4e39\u5427\uff1f\u201d \u3000\u3000\u201c\u7261\u4e39\u79cd\u5f97\u597d\uff0c\u8fd9\u90fd\u662f\u4f5f\u5e08\u5085\u4f60\u7684\u529f\u52b3\u3002\u54c8\u5927\u4eba\u95ee\u4e86\u6211\u4e00\u4e9b\u79fb\u690d\u83cf\u6cfd\u7261\u4e39\u7684\u65b9\u6cd5\uff0c\u6211\u7684 \u8fd9\u70b9\u73a9\u827a\u90fd\u662f\u5e08\u5085\u4f60\u6559\u4f1a\u6211\u7684\uff0c\u6211\u6309\u5e08\u5085\u6240\u6559\u7684\u8bf4\u7ed9\u4ed6\u542c\uff0c\u5e94\u4ed8\u4e86\u8fc7\u53bb\uff0c\u6ca1\u7ed9\u4f60\u8001\u4e22\u8138\u3002\u201d \u3000\u3000\u4f5f\u7389\u6842\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u65e9\u5df1\u9752\u51fa\u4e8e\u84dd\u4e86\uff0c\u6211\u665a\u5e74\u6536\u4e86\u4f60\u8fd9\u6837\u4e00\u4f4d\u597d\u5f92\u5f1f\uff0c\u5b9e\u5728\u662f\u5e73\u751f\u6700 \u5f97\u610f\u7684\u4e8b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u6765\u5411\u5e08\u7236\u9053\u8c22\u7684\uff0c\u8981\u4e0d\u662f\u4f5f\u5e08\u5085\u4f60\u628a\u5e73\u751f\u7684\u6280\u827a\u90fd\u4f20\u7ed9\u6211\uff0c\u6211\u54ea \u91cc\u6709\u4eca\u5929\u7684\u597d\u65e5\u5b50\u8fc7\u3002\u201d\u4f5f\u7389\u6842\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u8bf4\u8d77\u4f60\u7684\u513f\u5b50\uff0c\u90a3\u66f4\u662f\u524d\u9014\u5982\u9526\u4e86\u3002\u4ed6\u5b66\u7684\u53ef \u662f\u505a\u519b\u5b98\u7684\u672c\u9886\uff0c\u5c06\u6765\u4e00\u5b9a\u4f1a\u6709\u51fa\u606f\u7684\u3002\u5170\u59d1\uff0c\u4f60\u771f\u597d\u798f\u6c14\u3002\u201d\u8bf4\u81f3\u6b64\u5904\u3002\u4e0d\u89c9\u6709\u70b9\u9eef\u7136\u3002 \u56e0\u4e3a\u4ed6\u662f\u65e0\u513f\u65e0\u5973\u7684\u3002 \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u5f20\u96ea\u6ce2\u4e5f\u6b63\u4e5f\u53ef\u4ee5\u7275\u5165\u6b63\u9898\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f5f\u5e08\u5085\uff0c\u4f60\u559c\u6b22\u6211\u8fd9\u5b69\u5b50\uff0c\u6211\u53eb\u4ed6 \u8ba4\u4f60\u505a\u5e72\u7239\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4f5f\u7389\u6842\u9053\uff1a\u201c\u8fd9\u6211\u600e\u4e48\u6562\u5f53\uff1f\u5170\u59d1\uff0c\u4f60\u6709\u8fd9\u5fc3\u610f\u6211\u5df2\u611f\u6fc0\u4f60\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4eec\u6bcd\u5b50\u90fd\u662f\u4f60\u683d\u57f9\u7684\uff0c\u4f60\u522b\u5ba2\u6c14\uff0c\u62e9\u4e2a\u597d\u65e5\u4e8e\u6211\u53eb\u4ed6\u5411\u4f60\u78d5\u5934\uff0c\u4f60\u4e00\u5b9a \u8981\u6536\u4ed6\u505a\u5e72\u513f\u5b50\u3002\u4e0d\u8fc7\u8bf4\u8d77\u8fd9\u4e2a\u5b69\u5b50\uff0c\u6211\uff0c\u6211\u2014\u2014\u201d\u4f5f\u7389\u6842\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u4f46\u8bf4\u65e0 \u59a8\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u8fd9\u5b69\u5b50\u4eca\u5929\u6211\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u4ed6\u603b\u662f\u559c\u6b22\u5728\u5916\u9762\u4e71\u8dd1\uff0c \u6211\u4e0d\u60f3\u4ed6\u53d8\u5f97\u592a\u91ce\u6027\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff0c\u53ef\u4ee5\u5e2e\u6211\u627e\u4ed6\u56de\u6765\u5417\uff1f\u201d \u3000\u3000\u4f5f\u7389\u6842\u7b11\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u5230\u5c71\u4e0a\u53bb\u4e86\uff0c\u4f46\u53ef\u4e0d\u662f\u53bb\u73a9\u7684\u3002\u201d\u5f20\u96ea\u6ce2\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a \u201c\u4ed6\u4e0d\u662f\u5728\u7ec3\u6b66\u573a\u4e0a\u7ec3\u6b66\u5417\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4e0a\u5c71\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4f5f\u7389\u6842\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u53eb\u4f60\u9ad8\u5174\uff0c\u4e0d\u9519\uff0c\u4ed6\u534a\u4e2a\u65f6\u8fb0\u4e4b\u524d\u8fd8\u5728\u548c\u8001\u696e\u7ec3\u6b66\u7684\uff0c\u540e\u6765\u8f66\u90fd\u5c09 \uff08\u8f66\u7f2d\u7684\u5b98\u8854\u3002\u4ed6\u662f\u4ee5\u90fd\u5c09\u7684\u804c\u8854\u62c5\u4efb\u536b\u58eb\u7684\u526f\u961f\u957f\u7684\uff09\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u4f3c\u4e4e\u5f88\u5938\u5956\u4ed6\uff0c\u4ed6\u4eec \u4e09\u4e2a\u4eba\u5c31\u4e00\u540c\u53bb\u4e86\u3002\u4ed6\u4eec\u4ece\u8fd9\u4e2a\u56ed\u5b50\u7684\u540e\u95e8\u8d70\u51fa\u53bb\u4e86\u3002\u6211\u521a\u597d\u770b\u89c1\u3002\u81f3\u4e8e\u7ec3\u6b66\u573a\u4e0a\u7684\u60c5\u5f62\uff0c \u5219\u662f\u53e6\u4e00\u4e2a\u536b\u58eb\u544a\u8bc9\u6211\u7684\uff0c\u4ed6\u77e5\u9053\u6211\u4eec\u65f6\u5e38\u89c1\u9762\uff0c\u56e0\u6b64\u7279\u5730\u544a\u8bc9\u6211\uff0c\u597d\u8ba9\u6211\u8bf4\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u542c\u4e86\uff0c\u505a\u58f0\u4e0d\u5f97\uff0c\u539f\u6765\u5979\u662f\u60f3\u8981\u513f\u5b50\u5728\u672a\u5949\u8bcf\u4e4b\u524d\u5077\u5077\u9003\u8d70\u7684\uff0c\u5982\u4eca\u53ea\u80fd\u542c\u5929\u7531 \u547d\u4e86\u3002 \u3000\u3000\u4f5f\u7389\u6842\u9053\uff1a\u201c\u542c\u90a3\u536b\u58eb\u8bf4\uff0c\u8f66\u90fd\u5c09\u4f3c\u4e4e\u8981\u6536\u4ee4\u90ce\u505a\u5f92\u5f1f\uff0c\u8fd9\u6b21\u4ed6\u4eec\u4e00\u540c\u4e0a\u5c71\uff0c\u662f\u60f3\u5728\u5c71 \u4e0a\u53eb\u4ee4\u90ce\u7ec3\u4e00\u4e9b\u5e73\u5730\u4e0a\u4e0d\u65b9\u4fbf\u7ec3\u7684\u6b66\u529f\u7ed9\u4ed6\u770b\u7684\u3002\u201d\u5ffd\u7136\u53d1\u73b0\u5f20\u96ea\u6ce2\u7684\u9762\u8272\u6709\u70b9\u4e0d\u5bf9\uff0c\u4ed6\u505c \u4e86\u4e0b\u6765\u3001\u54a6\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8f66\u90fd\u53a8\u770b\u4e0a\u4f60\u7684\u513f\u5b50\u3002\u4f60\u600e\u4e48\u6709\u70b9\u4e0d\u592a\u9ad8\u5174\u5462\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u6b63\u662f\u56e0\u4e3a\u592a\u9ad8\u5174\u4e86\uff0c\u53cd\u800c\u6709\u70b9\u5bb3\u6015\uff0c\u6015\uff0c\u6015\u8fd9\u5b69\u5b50\u798f\u8584\u6d88\u53d7\u4e0d \u8d77\u3002\u201d\u8001\u82b1\u738b\u770b\u7740\u5979\u7684\u773c\u775b\uff0c\u4f3c\u4e4e\u8981\u4ece\u5979\u7684\u773c\u775b\u91cc\u770b\u51fa\u5979\u5185\u5fc3\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5185\u5fc3\u7684\u79d8\u5bc6\u4ed6\u6216\u8bb8\u8fd8\u672a\u770b\u5f97\u51fa\u6765\u3002\u4f46\u4ed6\u5df2\u7ecf\u770b\u89c1\u5979\u7684\u773c\u775b\u7684\u4e00\u6ef4\u6cea\u6c34\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5728\u4e0a\u5c71\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u663e\u9732\u4e86\u4e00\u70b9\u7eb5\u8dcc\u7684\u529f\u592b\uff0c\u4ed6\u6839\u672c\u5c31\u662f\u5728\u5c71\u4e0a\u957f\u5927\u7684\u5b69\u5b50\uff0c\u722c \u60ac\u5d16\u5ced\u58c1\uff0c\u81ea\u5c0f\u5c31\u4e60\u60ef\u4e86\uff0c\u867d\u7136\u6ca1\u6709\u8ba4\u771f\u7ec3\u8fc7\u8f7b\u529f\uff0c\u4f46\u52a0\u4e0a\u73b0\u6709\u7684\u5185\u529f\u5e95\u5b50\uff0c\u7eb5\u8dcc\u7684\u529f\u592b\u6bd4 \u8d77\u8282\u5ea6\u4f7f\u8859\u95e8\u7684\u4e00\u822c\u536b\u58eb\u5df2\u662f\u4e0d\u9051\u591a\u8ba9\u3002 \u3000\u3000\u4f46\u7531\u4e8e\u4ed6\u8fd9\u79cd\u529f\u592b\u4e0d\u662f\u201c\u6b63\u89c4\u201d\u7684\u8f7b\u529f\uff0c\u843d\u5728\u6b66\u5b66\u7684\u5927\u884c\u5bb6\u773c\u4e2d\uff0c\u8fd8\u662f\u770b\u51fa\u5176\u4e2d\u7684\u5206\u522b \u7684\u3002\u800c\u8f66\u7f2d\u5c31\u6b63\u662f\u8fd9\u6837\u7684\u4e00\u4f4d\u6b66\u5b66\u5927\u884c\u5bb6\u3002 \u3000\u3000\u8f66\u7f2d\u770b\u5728\u773c\u5185\uff0c\u5374\u4e0d\u51fa\u58f0\u3002 \u3000\u3000\u4ed6\u4eec\u5230\u5c71\u4e0a\u7684\u4e00\u5757\u8349\u576a\uff0c\u8f66\u7f2d\u53eb\u696e\u5ca9\u548c\u4ed6\u201c\u5582\u62db\u201d\uff0c\u7ec3\u4e86\u4e00\u5957\u62f3\u548c\u4e00\u5957\u5200\u6cd5\u3002\u7136\u540e\u8f66\u7f2d \u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6765\uff0c\u6211\u548c\u4f60\u62c6\u62db\uff0c\u4f60\u53ef\u4ee5\u65bd\u5c55\u516d\u5408\u5200\u6cd5\u548c\u6211\u7a7a\u624b\u5bf9\u62c6\u3002\u201d \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u516d \u56de\u3000\u8427\u5fc3\u5251\u6c14 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u516d \u56de\u3000\u8427\u5fc3\u5251\u6c14 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u7a7a\u624b\u548c\u6211\u5bf9\u5200\u3002\u4e07\u4e00\uff0c\u6211\uff0c\u6211\u2014\u2014\u201d\u8f66\u7f2d\u9053\uff1a\u201c\u4f60\u6015\u5931\u624b\u4f24\u4e86\u6211\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u628a\u5200\u662f\u5f88\u950b\u5229\u7684\uff0c\u4f60\u77a7\uff01\u201d\u5200\u5149\u4e00\u8d77\uff0c\u5c31\u5288\u65ad\u4e00\u679d\u6811\u679d\u3002 \u8f66\u7f2d\u54c8\u54c8\u5927\u7b11\u3002\u696e\u5ca9\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\u65e0\u77e5\uff0c\u8f66\u5927\u4eba\u4f60\u83ab\u602a\u4ed6\u3002\u7fbd\u51b2\uff0c\u8fd8\u4e0d\u5feb\u5411\u8f66\u5927\u4eba\u966a\u4e2a\u4e0d \u662f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u83ab\u660e\u5176\u5999\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u9519\u4e86\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u8bf8\u5ca9\u9053\uff1a\u201c\u51ed\u4f60\u600e\u4e48\u4f24\u7684\u4e86\u8f66\u5927\u4eba\uff0c\u83ab\u8bf4\u4e00\u628a\u94a2\u5200\uff0c\u5c31\u662f\u5728\u5200\u67aa\u5251\u4e1b\u4e2d\uff0c\u8f66\u5927\u4eba\u4e5f\u662f\u8bf4 \u6765\u5c31\u6765\uff0c\u8bf4\u53bb\u5c31\u53bb\u3002\u4f60\u8fd9\u628a\u94a2\u5200\uff0c\u5728\u8f66\u5927\u4eba\u773c\u4e2d\uff0c\u4e0d\u8fc7\u662f\u5c0f\u5b69\u5b50\u73a9\u7684\u6728\u5200\u800c\u5df2\u3002\u201d\u6a80\u7fbd\u51b2\u4f38 \u51fa\u820c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u6709\u8fd9\u6837\u5386\u5bb3\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u53ef\u4ee5\u8bd5\u8bd5\uff0c\u5c3d\u7ba1\u653e\u80c6\u5411\u6211\u523a\u6765\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5c55\u5f00\u516d\u5408\u5200\u6cd5\uff0c\u7b2c\u4e00\u62db\uff1a\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d\uff0c\u94a2\u5200\u4e3e\u8fc7\u5934\u9876\uff0c\u76f4\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u8f66\u7f2d\u659c\u8eab\u4e00\u95ea\uff0c\u5374\u6545\u610f\u53cd\u624b\u4e00\u64e6\uff0c\u8ba9\u4ed6\u7684\u5200\u950b\u78b0\u7740\u624b\u81c2\u3002\u64c5\u7fbd\u51b2\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a \u201c\u554a\u5440\uff0c\u4e0d\u597d\uff01\u201d \u3000\u3000\u8f66\u7f2d\u7b11\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u4f60\u77a7\u6211\u8fd9\u6761\u624b\u81c2\u4e0d\u662f\u597d\u597d\u7684\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5b9a\u6e05\u4e00\u77a7\uff0c\u53ea\u89c1\u4ed6\u8fd9\u6761\u624b\u81c2\u679c\u7136\u662f\u4e00\u5982\u539f\u72b6\uff0c\u8fde\u8840\u8ff9\u90fd\u6ca1\u6709\u4e00\u70b9\u3002\u975e\u4f46\u6ca1\u6709\u53d7 \u4f24\uff0c\u751a\u81f3\u8863\u88f3\u90fd\u6ca1\u6709\u88c2\u75d5\u3002 \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e5f\u7b97\u4e0d\u9519\u4e86\uff0c\u5c45\u7136\u80fd\u591f\u4ee4\u6211\u7684\u8863\u8896\u8d77\u4e00\u9053\u76b1\u75d5\u3002\u597d\uff0c\u518d\u6765\uff0c\u518d\u6765\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8f66\u5927\u4eba\uff0c\u4f60\u7684\u529f\u592b\u771f\u597d\u3002\u4f46\u6211\u4e0d\u61c2\uff0c\u4e3a\u4ec0\u4e48\u6211\u7684\u5200\u780d\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u4f1a\u81ea\u5df1 \u6ed1\u8fc7\u4e00\u8fb9\u7684\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u79cd\u5378\u5200\u7684\u529f\u592b\u3002\u5176\u5b9e\uff0c\u53ea\u8981\u6709\u4eba\u6307\u70b9\u4f60\uff0c\u4f60\u73b0\u5728\u5c31\u53ef\u4ee5\u8fd0\u7528\u8fd9\u79cd\u529f\u592b \u7684\u3002\u201d \u3000\u3000\u8fd9\u8bdd\uff0c\u8fde\u696e\u5ca9\u90fd\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b66\u5b66\u4e2d\u7684\u5378\u5b57\u8bc0\uff0c\u5fc5\u987b\u6709\u4e0a\u4e58\u7684\u5185\u529f\u505a\u57fa\u7840\u624d \u80fd\u8fd0\u7528\u7684\u3002\u8f66\u7f2d\u4e3a\u4f55\u8fd9\u6837\u8bf4\u5462\uff1f\u82e5\u8bf4\u53ea\u662f\u5bf9\u5b69\u5b50\u7684\u5938\u5956\u5427\uff0c\u8fd9\u6837\u7684\u5938\u5956\u4e5f\u672a\u514d\u592a\u8fc7\u4efd\u4e86\u3002\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u548c\u6211\u62c6\u62db\u5427\uff0c\u77a7\uff0c\u6211\u8fd9\u6837\u653b\u4f60\uff0c\u4f60\u5982\u4f55\u906e\u62e6\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53bb\u4e86\u987e\u5fcc\uff0c\u8ba4\u771f\u5730\u6309\u7167\u516d\u5408\u5200\u6cd5\u548c\u4ed6\u5bf9\u62c6\uff0c\u8f66\u7f2d\u4e3a\u4e86\u8981\u4ed4\u7ec6\u89c2\u5bdf\u4ed6\u6b66\u529f\u7a76\u7adf\u6709\u591a \u6df1\u6d45\uff0c\u4e0d\u518d\u8ba9\u4ed6\u780d\u4e2d\u4e86\u3002\u5200\u5149\u638c\u5f71\uff0c\u8f6c\u773c\u8fc7\u4e86\u6570\u5341\u62db\uff0c\u6a80\u7fbd\u51b2\u7684\u94a2\u5200\u8fde\u4ed6\u7684\u8863\u670d\u90fd\u6ca1\u6cbe\u7740\u3002 \u3000\u3000\u8f66\u7f2d\u4e00\u58f0\u957f\u5578\uff0c\u638c\u98ce\u8fc7\u5904\uff0c\u5494\u5693\u4e00\u58f0\uff0c\u5288\u65ad\u4e00\u679d\u7c97\u5982\u513f\u81c2\u7684\u6811\u679d\u3002\u524a\u53e3\u6709\u5982\u5200\u780d\u3002\u8f66\u7f2d \u559d\u9053\uff1a\u201c\u6211\u7684\u638c\u5200\u950b\u5229\u8fd8\u662f\u4f60\u7684\u94a2\u5200\u950b\u5229\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u60a6\u6210\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u8f66\u5927\u4eba\uff0c\u662f\u4f60\u7684\u638c\u5200\u5389\u5bb3\u3002\u6211\u8fd9\u5957\u516d\u5408\u5200\u6cd5\u5df2\u7ecf\u7528\u5b8c\u4e86\uff0c\u8bf7 \u4f60\u6307\u70b9\u6211\u5427\uff01\u201d \u3000\u3000\u8f66\u7f2d\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\u4f55\u987b\u6c42\u6211\u6307\u70b9\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u6a80\u7fbd\u51b2\u56fa\u7136\u83ab\u540d\u5176\u5999\uff0c\u696e\u5ca9\u542c\u4e86\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u8f66\u5927 \u4eba\uff0c\u6211\uff0c\u6211\u53ef\u6ca1\u6709\u4ec0\u4e48\u5f97\u7f6a\u4f60\u8001\u4eba\u5bb6\u5427\uff1f\u4f60\uff0c\u4f60\u8fd9\u8bdd\u2014\u2014\u201d\u53ea\u9053\u8f66\u7f2d\u6b64\u8a00\u4e43\u662f\u9488\u5bf9\u4ed6\u7684\u3002 \u3000\u3000\u8f66\u7f2d\u4e0d\u7406\u4f1a\u4ed6\uff0c\u4e5f\u4e0d\u5f85\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u9661\u5730\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4f60\u8fd9\u5c0f\u9b3c\u5934\u592a\u8fc7\u53ef\u6076\uff0c\u8fde\u6211 \u90fd\u51e0\u4e4e\u7740\u4e86\u4f60\u7684\u9a97\uff01\u4eca\u65e5\u4f60\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u6211\u5c31\u6bd9\u4e86\u4f60\uff01\u201d \u3000\u3000\u5927\u559d\u58f0\u4e2d\uff0c\u53cc\u638c\u9f50\u98de\uff0c\u638c\u98ce\u5982\u72c2\u98da\u3002\u5468\u56f4\u5341\u6570\u4e08\u5185\uff0c\u6c99\u98de\u8d70\u77f3\uff0c\u6811\u53f6\u7eb7\u7eb7\u843d\u4e0b\u3002\u6a80\u7fbd\u51b2 \u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u529b\u6392\u5c71\u5012\u6d77\u800c\u6765\u3002\u4ed6\u662f\u8fde\u547c\u5438\u90fd\u51e0\u4e4e\u7a92\u606f\u4e86\uff0c\u54ea\u91cc\u8fd8\u80fd\u9012\u7684\u51fa\u62db\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u94a2\u5200\u843d\u5730\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8f66\u7f2d\u5df2\u7ecf\u4e00\u628a\u63ea\u4f4f\u4e86\u4ed6\uff0c\u53f3\u638c\u5411\u4ed6\u80f8\u819b\u5288\u4e0b\uff01 \u3000\u3000\u696e\u5ca9\u5413\u5f97\u201d\u554a\u5440\u201d\u201d\u4e00\u58f0\u8df3\u8d77\u3002\u53eb\u9053\uff1a\u8f66\u5927\u4eba\uff0c\u624b\u4e0b\u2014\u2014\u201d \u3000\u3000\u201c\u624b\u4e0b\u7559\u60c5\u201d\u8fd9\u56db\u4e2a\u5b57\u53ea\u8bf4\u5f97\u4e00\u534a\uff1a\u8f66\u7f2d\u90a3\u4e00\u638c\u5df2\u662f\u91cd\u91cd\u7684\u6253\u5728\u6a80\u7fbd\u51b2\u7684\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u8fd9\u6837\u521a\u731b\u7684\u638c\u529b\u8db3\u53ef\u88c2\u5f00\u77f3\u7891\uff0c\u4e00\u4e2a\u5c0f\u5b69\u5b50\u5982\u4f55\u80fd\u7981\u5f97\u8d77\uff1f\u696e\u5ca9\u95ed\u4e0a\u773c\u775b\u4e0d\u6562\u89c2\u770b\uff0c\u53ea \u9053\u6a80\u7fbd\u51b2\u5728\u4ed6\u8fd9\u4e00\u638c\u91cd\u51fb\u4e4b\u4e0b\uff0c\u7acb\u5373\u4fbf\u662f\u5f00\u819b\u5256\u8179\u4e4b\u707e\u3002 \u3000\u3000\u4ed6\u95ed\u4e0a\u773c\u775b\uff0c\u5374\u542c\u4e0d\u89c1\u6a80\u7fbd\u51b2\u7684\u60e8\u53eb\u58f0\uff0c\u201c\u96be\u9053\u8fd9\u5b69\u5b50\u5df2\u7ecf\u53d8\u6210\u4e00\u56e2\u8089\u6ce5\uff1f\u201d\u5ffd\u542c\u5f97\u8f66 \u7f2d\u7b11\u9053\uff1a\u201c\u8001\u696e\u4f60\u600e\u4e48\u5413\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u770b\u6765\u8fd9\u5c0f\u9b3c\u5934\u7684\u80c6\u5b50\u4f3c\u4e4e\u6bd4\u4f60\u8fd8\u5927\u5f97\u591a\u3002\u201d \u3000\u3000\u696e\u5ca9\u7741\u5f00\u773c\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u6a80\u7fbd\u51b2\u867d\u7136\u5df2\u88ab\u8f66\u7f2d\u6293\u4f4f\uff0c\u4f46\u4f3c\u4e4e\u5e76\u6ca1\u53d7\u4f24\uff0c\u4e00\u53cc\u773c\u775b\u7741\u5f97\u5927 \u5927\u7684\u770b\u7740\u8f66\u7f2d\u3002\u795e\u60c5\u867d\u7136\u663e\u5f97\u60ca\u614c\uff0c\u5374\u4e0d\u5982\u4ed6\u60f3\u8c61\u4e4b\u751a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u60ca\u9b42\u7a0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8f66\u5927\u4eba\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6253\u6b7b\u6211\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u80fd\u8ba9\u4e00\u4e2a\u5c0f\u5b69\u5b50\u9a97\u6211\uff01\u4f60\u542c\u7740\uff0c\u6211\u73b0\u5728\u95ee\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u5fc5\u987b\u8001\u8001 \u5b9e\u5b9e\u56de\u7b54\u6211\uff01\u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4f4d\u696e\u53d4\u53d4\u5440\u3001\u8f66\u5927\u4eba\uff0c\u4f60\u4e0d\u662f\u65e9\u5df2\u77e5\u9053 \u4e86\u5417\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201d\u6211\u662f\u95ee\u4f60\u4ee5\u524d\u7684\u5e08\u7236\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4ee5\u524d\u7684\u5e08\u7236\uff0c\u6700\u65e9\u6559\u6211\u6b66\u529f\u7684\u4e5f\u5c31\u662f\u8fd9\u4f4d\u696e\u53d4\u53d4\u5440\uff01\u8fd8\u6709\u970d\u4f8d\u536b\u97e9\u4f8d\u536b\u3001 \u5218\u4f8d\u536b\u4e5f\u5dee\u4e0d\u591a\u662f\u540c\u4e00\u4e2a\u65f6\u5019\u6559\u6211\u7ec3\u6b66\u7684\u3002\u201d \u3000\u3000\u8f66\u7f2d\u559d\u6e32\uff1a\u201c\u4f60\u522b\u88c5\u849c\uff0c\u6211\u95ee\u7684\u4e0d\u662f\u8fd9\u4e9b\u4eba\uff0c\u662f\u5728\u4f60\u672a\u6765\u8fd9\u91cc\u4e4b\u524d\u7684\u90a3\u4e2a\u5e08\u7236\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u6ca1\u5e08\u7236\u3002\u201d \u3000\u3000\u8f66\u7f2d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u5e08\u7236\uff1f\u4f60\u4ee5\u4e3a\u4f60\u8fd8\u80fd\u9a97\u8fc7\u6211\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u6ca1\u6709\u9a97\u4f60\u3002\u8bf4\u8001\u5b9e \u8bdd\uff0c\u6211\u662f\u5f88\u5e0c\u671b\u627e\u5230\u4e00\u4e2a\u597d\u5e08\u7236\uff0c\u53ef\u60dc\u6ca1\u627e\u5230\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u5012\u7684\u786e\u662f\u4ed6\u7684\u8001\u5b9e\u8bdd\u3002\u8f66\u7f2d\u51b7\u7b11 \u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u627e\u5230\u5e08\u7236\u5417\uff1f\u90a3\u4e48\u4f60\u7684\u5185\u529f\u662f\u8c01\u6559\u7684\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5185\u529f\uff0c\u4ec0\u4e48\u5185\u529f\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u4ec0\u4e48\u53eb\u5185\u529f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u5185\u529f\u8fd9\u4e24\u4e2a\u5b57\u6211\u662f\u542c\u8fc7\u7684\u3002\u4f46\u6ca1\u7ec3\u8fc7\u3002\u4e0d\u4fe1\u4f60\u53ef\u4ee5\u95ee\u696e\u53d4\u53d4\u3002\u201d \u3000\u3000\u696e\u5ca9\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u6ca1\u6709\u6559\u4ed6\u8fc7\u5185\u529f\u3002\u4e0d\u8fc7\u5185\u529f\u548c\u5916\u529f\u7684\u5206\u522b\uff0c\u6211\u662f\u548c\u4ed6\u8bf4\u8fc7 \u7684\u3002\u201d \u3000\u3000\u8f66\u7f2d\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e0d\u662f\u4f60\u6559\u4ed6\u5185\u529f\u3002\u8001\u696e\u3002\u6211\u4e0d\u6015\u5f97\u7f6a\u4f60\uff0c\u4f60\u6240\u5b66\u7684\u5c11\u6797\u6d3e\u7684\u5185 \u529f\u867d\u7136\u662f\u5404\u5927\u95e8\u6d3e\u4e4b\u51a0\uff0c\u4f46\u4f60\u5374\u4f3c\u4e4e\u5c1a\u672a\u5f97\u5230\u5c11\u6797\u5bfa\u5185\u529f\u7684\u4e0a\u4e58\u5fc3\u6cd5\u3002\u201d \u3000\u3000\u696e\u5ca9\u6ee1\u9762\u901a\u7ea2\uff1b\u8bf4\u9053\uff1a\u201c\u8f66\u5927\u4eba\u8bf4\u5f97\u4e0d\u9519\u3002\u8fd9\u70b9\u81ea\u77e5\u4e4b\u660e\u6211\u4e5f\u8fd8\u662f\u6709\u7684\u3002\u6211\u6240\u5b66\u7684\u5c11\u6797 \u5185\u529f\u53ea\u4e0d\u8fc7\u662f\u7565\u5f97\u76ae\u6bdb\u800c\u5df2\u3002\u201d \u3000\u3000\u8f66\u7f2d\u8bf4\u9053\uff1a\u201c\u4f60\u5373\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u90a3\u5c31\u6700\u597d\u3002\u6211\u5ba1\u95ee\u8fd9\u5c0f\u9b3c\uff0c\u4f60\u4e0d\u5fc5\u63fd\u5728\u81ea\u5df1\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u696e\u5ca9\u5c34\u5c2c\u4e4b\u6781\uff0c\u558f\u558f\u8fde\u58f0\uff0c\u9000\u8fc7\u4e00\u65c1\u3002 \u3000\u3000\u8f66\u7f2d\u53ef\u80fd\u4e5f\u89c9\u5f97\u81ea\u5df1\u8bf4\u5f97\u8fc7\u4efd\u4e86\u4e9b\uff0c\u653e\u5bbd\u9762\u8272\uff0c\u5bf9\u696e\u5ca9\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u696e\uff0c\u4f60\u4e0d \u77e5\u9053\uff0c\u4f60\u7740\u4e86\u8fd9\u5c0f\u9b3c\u7684\u7f16\u4e86\u3002\u4e0d\u8fc7\uff0c\u4e5f\u602a\u4e0d\u5f97\u4f60\uff0c\u6211\u4e5f\u662f\u521a\u521a\u624d\u8bd5\u51fa\u4ed6\u5185\u529f\u7684\u6df1\u6d45\u7684\u3002\u201d \u3000\u3000\u696e\u5ca9\u60ca\u5947\u4e4b\u6781\uff0c\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e0d\u8fc7\u5341\u5c81\u591a\u70b9\uff0c\u4ed6\u5f53\u771f\u61c2\u5f97\u5185\u529f\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u4f60\u8981\u6211\u8bf4\u771f\u8bdd\u5417\uff1f\u8bf4\u51fa\u6765\u4f60\u53ef\u4e0d\u8981\u96be\u8fc7\uff0c\u8fd9\u5c0f\u9b3c\u6240\u5b66\u7684\u5185\u529f\u6bd4\u4f60\u9ad8\u660e\u5f97\u591a\uff0c \u53ea\u4e0d\u8fc7\u4ed6\u706b\u5019\u672a\u591f\uff0c\u529f\u529b\u4e0d\u8db3\u800c\u5df2\uff0c\u5185\u529f\u7684\u4e0a\u4e58\u5fc3\u6cd5\u5df2\u662f\u5f97\u4e86\u3002\u6211\u90a3\u4e00\u638c\u5047\u610f\u53d6\u4ed6\u6027\u547d\uff0c\u8fd9 \u624d\u8bd5\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u6a80\u7fbd\u51b2\u5b66\u7684\u867d\u7136\u662f\u4e0a\u4e58\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\u600e\u6837\u8fd0\u7528\u7684\uff0e\u4e0d\u8fc7\uff0c\u5b66\u8fc7\u4e0a\u4e58\u5185 \u529f\u7684\u4eba\uff0c\u5728\u9762\u4e34\u751f\u6b7b\u5173\u5934\u4e4b\u9645\uff0c\u81ea\u7136\u800c\u7136\u5c31\u4f1a\u751f\u51fa\u53cd\u5e94\u3002\u8f66\u7f2d\u6b63\u662f\u4ece\u4ed6\u53cd\u5e94\u4e2d\u6d4b\u51fa\u4ed6\u7684\u5185\u529f \u6df1\u6d45\u7684\u3002 \u3000\u3000\u8f66\u7f2d\u63ed\u7834\u4e86\u6a80\u7fbd\u51b2\u5b66\u8fc7\u5185\u529f\u7684\u201c\u79d8\u5bc6\u201d\u4e4b\u540e\u3002\u56de\u8fc7\u5934\u6765\uff0c\u628a\u58f0\u8c03\u653e\u5f97\u8f83\u4e3a\u67d4\u548c\u3002\u5bf9\u4ed6\u8bf4 \u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u77e5\u9053\u4f60\u662f\u7792\u4e0d\u8fc7\u6211\u7684\u4e86\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u5b9e\u8bdd\u5b9e\u8bf4\u7684\u597d\u3002\u4f60\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u6211\u975e\u4f46 \u4e0d\u4f1a\u6740\u4f60\uff0c\u6211\u8fd8\u53ef\u4ee5\u6536\u4f60\u505a\u5f92\u5f1f\u3002\u597d\u5b69\u5b50\uff0c\u544a\u8bc9\u6211\u5427\uff0c\u6559\u7ed9\u4f60\u5185\u529f\u7684\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d\u6a80\u7fbd\u51b2 \u9053\uff1a\u201c\u771f\u7684\u6ca1\u4eba\u6559\u8fc7\u6211\u7684\u5185\u529f\uff0c\u6211\u600e\u80fd\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u664c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u54c8\u5927\u4eba\u8981\u6211\u7684\u90a3\u4e2a\u5b69\u5b50\u4e0d\u77e5\u662f\u4e0d\u662f\u4ed6\uff0c\u4f46\u603b\u4e4b\u4ed6\u662f \u6781\u5176\u53ef\u7591\u7684\u4e86\uff0c\u4e14\u8bd5\u4ed6\u4e00\u8bd5\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u76ef\u7740\u6a80\u7fbd\u51b2\u5ffd\u5730\u5411\u9053\uff1a\u201c\u6a80\u516c\u76f4\u662f\u4f60\u7684\u4ec0\u4e48 \u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8138\u4e0a\u89c2\u51fa\u4e00\u6d3e\u8ff7\u60d8\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u7f13\u7f13\u5410\u51fa\uff1a\u201c\u6211\u8bf4\u7684\u662f\u6a80\u516c\u76f4\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6447\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff01\u201d \u3000\u3000\u8f66\u7f2d\u4e0d\u89c9\u4e5f\u7591\u60d1\u8d77\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u6309\u8bf4\u4e00\u4e2a\u5b69\u5b50\u662f\u4e0d\u4f1a\u8fd9\u6837\u9547\u5b9a\u7684\uff0c\u83ab\u975e\u771f\u662f\u6211\u731c\u9519 \u4e86\uff1f\u201d \u3000\u3000\u4ed6\u54ea\u77e5\u9053\uff0c\u6a80\u7fbd\u51b2\u8fd9\u4efd\u9547\u5b9a\u7684\u529f\u592b\u5f97\u6765\u4e0d\u6613\uff0c\u662f\u7ecf\u8fc7\u8bb8\u591a\u6c89\u75db\u7684\u6559\u8bad\uff0c\u751a\u81f3\u662f\u4ed6\u7684\u6bcd\u4eb2 \u7528\u8840\u548c\u6cea\u8bad\u7ec3\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u7684\u6bcd\u4eb2\u81ea\u6bc1\u5bb9\u989c\uff0c\u4e3a\u7684\u5c31\u662f\u4ee5\u8eab\u4f5c\u5219\uff0c\u6559\u4ed6\u77e5\u9053\u4fdd\u5b88\u79d8\u5bc6\u7684\u91cd\u8981\u3002 \u3000\u3000\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u4e43\u662f\u5373\u5728\u610f\u6599\u4e4b\u5916\u53c8\u5728\u610f\u6599\u4e4b\u4e2d\u7684\uff01\u8bf4\u662f\u201c\u610f\u6599\u4e4b\u5916\u201d\uff0c\u662f\u56e0\u4e3a \u5728\u4ed6\u4e0a\u5c71\u4e4b\u65f6\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8f66\u7f2d\u4f1a\u8fd9\u6837\u5bf9\u5f85\u4ed6\u7684\uff1a\u4f46\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7ec8\u6709\u4e00\u65e5\u53d1\u751f\uff0c\u5219\u662f\u65e9 \u5df2\u5728\u4ed6\u6bcd\u4eb2\u7684\u610f\u6599\u4e4b\u4e2d\u3002\u5728\u4ed6\u6bcd\u4eb2\u610f\u6599\u4e4b\u4e2d\uff0c\u5373\u662f\u4ed6\u65e9\u5df2\u6709\u4e86\u5e94\u4ed8\u8fd9\u79cd\u201c\u610f\u5916\u201d\u7684\u5fc3\u7406\u51c6\u5907 \u4e86\u3002\u201c\u5018\u6709\u4e00\u5929\uff0c\u6709\u4eba\u76d8\u95ee\u4f60\u7684\u8eab\u4e16\uff0c\u4f60\u53ef\u5343\u4e07\u4e0d\u80fd\u8bf4\u51fa\u4f60\u7237\u7237\u7684\u540d\u5b57\u3002\u201d\u8fd9\u53e5\u8bdd\u662f\u6bcd\u4eb2\u4e0d \u77e5\u5bf9\u4ed6\u8bf4\u8fc7\u591a\u5c11\u904d\u7684\uff01 \u3000\u3000\u6240\u8c13\u201c\u610f\u5916\u201d\u4e0d\u8fc7\u662f\u6ca1\u60f3\u5230\u76d8\u95ee\u4ed6\u7684\u4eba\u4f1a\u662f\u8f66\u7f2d\uff0c\u800c\u53c8\u6765\u5f97\u8fd9\u6837\u5feb\u800c\u5df2\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u7237\u7237\u7684\u540d\u5b57\u5df2\u7ecf\u7531\u8f66\u7f2d\u53e3\u4e2d\u8bf4\u51fa\u6765\u4e86\uff0c\u8fd9\u548c\u6bcd\u4eb2\u7684\u4f30\u8ba1\u4e0d\u540c\uff0c\u4f46\u8981\u76d8\u95ee\u4ed6\u7684\u8eab \u4e16\u5219\u662f\u4e00\u6837\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u795e\u8272\u4e0d\u53d8\uff0e\u5012\u662f\u696e\u5ca9\u542c\u4e86\u201c\u6a80\u516c\u76f4\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u4e86\u3002 \u3000\u3000\u201c\u6a80\u516c\u76f4\uff1f\u662f\u4e0d\u662f\u4e8c\u5341\u591a\u5e74\u524d\u7a81\u7136\u5931\u8e2a\u7684\u90a3\u4f4d\u6a80\u8d1d\u52d2\uff1f\u201d\u696e\u5ca9\u95ee\u9053\u3002 \u3000\u3000\u8f66\u7f2d\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u4ed6\u662f\u54b1\u4eec\u91d1\u56fd\u7684\u8d1d\u52d2\uff0c\u5982\u4eca\u4ed6\u5df2\u7ecf\u662f\u7687\u4e0a\u6240\u8981\u7f09\u62ff \u7684\u94a6\u72af\u4e86\uff01\u201d \u3000\u3000\u696e\u5ca9\u8bf4\u9053\uff1a\u201c\u4f46\u8fd9\u5b69\u5b50\u7684\u6bcd\u4eb2\u4e0d\u8fd9\u662f\u4e2a\u5973\u4f63\uff0c\u4ed6\uff0c\u4ed6\u600e\u80fd\u548c\u66fe\u8d35\u4e3a\u8d1d\u52d2\u7684\u6a80\u516c\u76f4\u6709\u4ec0\u4e48 \u5173\u7cfb\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u5c0f\u9b3c\u8fd8\u662f\u6a80\u516c\u76f4\u7684\u5b59\u513f\u5462\uff01\u201d \u3000\u3000\u696e\u5ca9\u5413\u5f97\u4e0d\u6562\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u8f66\u7f2d\u62ff\u51fa\u4e00\u6761\u76ae\u97ad\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u4f60\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u6211\u6253\u6b7b\u4f60\uff01\u6211\u518d\u95ee\u4f60\u4e00\u904d\uff0c\u6a80\u516c\u76f4\u662f \u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u54ac\u7740\u7259\u5bf9\u4ed6\u6012\u76ee\u800c\u89c6\u3002\u8f66\u7f2d\u5530\u7684\u4e00\u97ad\u5c31\u6253\u4e0b\u53bb\u3002\u4ed6\u7528\u7684\u529b\u5ea6\u201c\u6070\u5230\u597d\u5904\u201d\uff0c\u6253\u5f97 \u6a80\u7fbd\u51b2\u76ae\u5f00\u8089\u88c2\uff0c\u5374\u4e0d\u81f3\u4e8e\u4f24\u53ca\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u4ed6\u6253\u4e00\u97ad\u5c31\u559d\u95ee\u4e00\u53e5\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d\u4e00\u97ad\u3001\u4e24\u97ad\u3001\u4e09\u97ad\u4e00\u6a80\u7fbd\u51b2\u5df2\u662f\u6ee1\u8eab\u4f24\u75d5\uff0c\u4f46\u59cb \u7ec8\u8fd8\u662f\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u8bf4\u3002\u696e\u5ca9\u770b\u4e0d\u8fc7\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u7684\u813e\u6c14\u4e00\u5411\u5f88\u5014\u5f3a\uff0c\u518d\u6253\u6050\u6015\u771f\u7684 \u8981\u6253\u6b7b\u4ed6\u4e86\uff0c\u4e0d\u5982\u53e6\u5916\u60f3\u4e2a\u529e\u6cd5\u95ee\u4ed6\u5427\uff01\u201d\u8f66\u7f2d\u9053\uff1a\u201c\u4f60\u5c11\u64cd\u5fc3\uff0c\u6211\u4e0d\u4f1a\u8fd9\u6837\u4fbf\u5b9c\u4ed6\u7684\u3002\u4e0d \u628a\u4ed6\u6298\u78e8\u4e2a\u591f\uff0c\u6211\u80af\u8ba9\u4ed6\u6b7b\u5417\uff1f\u201d\u4e0d\u8fc7\u8bdd\u5ca9\u90a3\u53e5\u201c\u4e0d\u5982\u53e6\u5916\u60f3\u4e2a\u529e\u6cd5\u95ee\u201d\uff0c\u5012\u662f\u63d0\u9192\u4e86\u4ed6\uff0c \u4ed6\u5fc3\u4e00\u52a8\uff0c\u7a81\u7136\u51b7\u7b11\u8fe2\uff1a\u201c\u597d\uff0c\u6211\u59d1\u4e14\u76f8\u4fe1\u4f60\u548c\u6a80\u516c\u76f4\u6ca1\u6709\u5173\u7cfb\uff0c\u4f46\u4f60\u5373\u7136\u548c\u4ed6\u6ca1\u6709\u5173\u7cfb\uff0c \u90a3\u5c31\u4e0d\u6015\u9a82\u4ed6\u4e86\u3002\u6211\u9a82\u4e00\u53e5\uff0c\u4f60\u8ddf\u6211\u9a82\u4e00\u53e5\uff0c\u9a82\u5b8c\u4e86\u6211\u5c31\u653e\u8fc7\u4f60\u3002\u6a80\u516c\u76f4\u662f\u8001\u738b\u516b\uff01\u201d \u3000\u3000\u4ed6\u77e5\u9053\u8d8a\u662f\u6027\u60c5\u5014\u5f3a\u7684\u5b69\u5b50\uff0c\u8d8a\u662f\u4e0d\u80fd\u522b\u4eba\u7684\u4fae\u8fb1\uff0c\u679c\u7136\u4ed6\u770b\u89c1\u6a80\u7fbd\u51b2\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u8f66\u7f2d\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u4f60\u6ca1\u542c\u89c1\u6211\u9a7e\u6a80\u516c\u76f4\u662f\u738b\u516b\u86cb\u5417\uff1f \u3000\u3000\u4f60\u4e0d\u8ddf\u6211\u9a82\uff0c\u4f60\u4e00\u5b9a\u662f\u8fd9\u8001\u6742\u79cd\u517b\u4e0b\u6765\u7684\u5c0f\u6742\u79cd\u518d\u517b\u4e0b\u6765\u7684\u5c0f\u5c0f\u6742\u79cdI\u201d \u3000\u3000\u4ed6\u7528\u8fd9\u79cd\u6ce2\u5987\u9a82\u8857\u7684\u65b9\u5f0f\u76d8\u95ee\u53e3\u4f9b\uff0c\u770b\u4f3c\u513f\u5b09\uff0c\u4f46\u7528\u6765\u5bf9\u4ed8\u4e00\u4e2a\u5b69\u5b50\u5374\u662f\u5f53\u771f\u6709\u6548\u3002\u6a80 \u7fbd\u51b2\u679c\u7136\u53ea\u80fd\u53d7\u8089\u4f53\u7684\u4fae\u8fb1\uff0c\u5374\u4e0d\u80fd\u53d7\u7cbe\u795e\u7684\u4fae\u8fb1\u3002 \u3000\u3000\u201c\u4f60\u624d\u662f\u72d7\u5a18\u517b\u6742\u79cd\u3002\u4f60\u624d\u662f\u738b\u516b\u86cb\u201d\u6a80\u7fbd\u51b2\u5fcd\u4e0d\u4f4f\u548c\u4ed6\u5bf9\u9a82\u4e86\uff0c\u8f66\u7f2d\u4e00\u542c\uff0c\u975e\u4f46\u6ca1\u6709 \u52a8\u6012\uff0c\u53cd\u800c\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u5c0f\u6742\u79cd\uff0c\u8fd9\u4f60\u53ef\u6cc4\u5e95\u4e86\u5427\uff1f\u4f60\u8fd8\u6562\u8bf4\u4f60\u548c\u6a80\u516c\u76f4\u6ca1\u6709\u5173\u7cfb\u2014\u2014\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u5730\u542c\u5f97\u6709\u4eba\u51b7\u7b11\uff0c\u7b11\u58f0\u597d\u4f3c\u4ece\u5f88\u8fdc\u7684\u5730\u65b9\u4f20\u6765\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u5b9b\u82e5\u6e38\u4e1d\u5f53 \u7a7a\uff0c\u82e5\u65ad\u82e5\u7eed\uff0c\u542c\u5230\u8033\u6735\u91cc\u5374\u662f\u4e0d\u7981\u5fc3\u810f\u6447\u6447\uff0c\u8f66\u7f2d\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7b11\u58f0\u7a81\u7136\u4e00\u53d8\uff0c\u53d8\u5f97\u6e05\u5cfb\u4e4b\u6781\uff0c\u9707\u5f97\u8f66\u7f2d\u7684\u8033\u9aa8\u55e1\u55e1\u4f5c\u54cd\uff0c\u53ea\u4e00\u7728\u773c\uff0c\u90a3\u4e2a\u4eba\u5c31\u51fa\u73b0\u5728 \u4ed6\u7684\u9762\u524d\u4e86\u3002 \u3000\u3000\u662f\u4e2a\u4e66\u751f\u6253\u626e\u7684\u4e2d\u5e74\u5165\uff0c\u624b\u91cc\u5408\u7740\u4e00\u7ba1\u7389\u8427\uff0c\u4e30\u795e\u4fca\u79c0\uff0c\u6c14\u6001\u6f47\u6d12\u3002 \u3000\u3000\u4ed6\u7684\u4e00\u53cc\u773c\u775b\u76ef\u7740\u8f66\u7f2d\uff0c\u76ee\u5149\u6709\u5982\u5bd2\u51b0\uff0c\u51b7\u5cfb\u4e4b\u4e2d\u9690\u9690\u6709\u9119\u89c6\u4e4b\u610f\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u521a\u521a\u722c\u8d77\u6765\uff0c\u548c\u8fd9\u4eba\u6253\u4e86\u4e00\u4e2a\u7167\u9762\u3001\u4e0d\u89c9\u4e5f\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4eba \u597d\u50cf\u6211\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\u3002\u201d\u8fd9\u4eba\u5f00\u53e3\u4e86\uff0c\u4ed6\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u91d1\u56fd\u7684\u4e00\u7b49\u5df4\u56fe\u9c81\uff0c\u5f53\u771f\u662f\u597d\u5a01\u98ce \u554a\uff01\u201d\u8f66\u7f2d\u6b63\u662f\u5177\u6709\u4e00\u7b49\u5df4\u56fe\u9c81\u8854\u5934\u7684\u4eba\u3002\u8f66\u7f2d\u559d\u9053\uff1a\u201c\u4f60\u5373\u7136\u77e5\u9053\u6211\u7684\u8eab\u4efd\uff0c\u90a3\u5c31\u5fc5\u987b\u8001 \u8001\u5b9e\u5b9e\u56de\u7b54\u6211\u7684\u95ee\u9898\uff01\u201d \u3000\u3000\u4e2d\u5e74\u4e66\u751f\u9053\uff1a\u201c\u4f60\u95ee\u6211\u4ec0\u4e48\uff1f\u201d\u8f66\u7f2d\u559d\u9053\uff1a\u201c\u4f60\u6ca1\u542c\u89c1\u5417\uff1f\u6211\u95ee\u4f60\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u56de\u7b54\u4f60\u4e86\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u7b28\uff0c\u8fd8\u8981\u95ee\u6211\u3002\u6211\u662f\u7279\u5730\u6765\u77a7\u77a7\u91d1\u56fd\u7684 \u4e00\u7b49\u5df4\u56fe\u9c81\u7684\u5a01\u98ce\uff0c\u5c31\u662f\u4f1a\u6b3a\u8d1f\u5b69\u5b50\uff01\u201d \u3000\u3000\u8f66\u7f2d\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e3a\u8fd9\u5b69\u5b50\u4ed6\u62b1\u4e0d\u5e73\u6765\u7684\uff0c\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7a81\u7136\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u4e2a\u4e2d\u5e74\u4e66\u751f\uff0c\u6b63\u662f\u5728\u4ed6\u548c\u6bcd\u4eb2\u4e3a\u4e86\u907f\u96be\u800c\u79bb\u5f00\u76d8\u9f99\u5c71\u90a3\u5929\uff0c\u9694 \u7740\u4e00\u4e2a\u5c71\u5934\uff0c\u770b\u89c1\u7684\u90a3\u4e2a\u5927\u6740\u91d1\u5175\u7684\u4eba\uff01 \u3000\u3000\u4ed6\u4e0d\u7981\u60ca\u559c\u4ea4\u96c6\uff0c\u51b2\u53e3\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff0c\u6211\u627e\u5f97\u4f60\u597d\u82e6\uff01 \u3000\u3000\u201d\u8f66\u7f2d\u5927\u611f\u610f\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u4ed6\u7684\u5e08\u7236\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u867d\u7136\u6ca1\u6709\u6559\u8fc7\u4ed6\u7684\u6b66\u529f\uff0c\u4f46\u4ed6\u65e9\u5df2\u662f\u6211\u7684\u8bb0\u540d\u5f1f\u5b50\uff01\u201d \u3000\u3000\u8f66\u7f2d\u559d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u6b63\u8981\u627e\u4eba\uff01\u5feb\u5feb\u8bf4\u51fa\u6a80\u516c\u76f4\u7684\u4e0b\u843d\uff0c\u5426\u5219\u5c31\u62ff\u51fa\u4f60\u7684\u672c\u9886\u8ba9\u6211 \u77a7\u77a7\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u6a80\u516c\u76f4\u7684\u4e0b\u843d\u6211\u6b63\u8981\u95ee\uff1b\u7b2c\u4e8c\uff0c\u4f60\u8981\u770b\u6211\u7684\u672c\u9886\uff0c\u6211\u53ef\u6ca1\u6709 \u4ec0\u4e48\u672c\u9886\u62ff\u51fa\u6765\u89c1\u4eba\uff0c\u53ea\u80fd\u5439\u4e2a\u66f2\u5b50\u7ed9\u4f60\u542c\uff01\u201d \u3000\u3000\u8f66\u7f2d\u53ea\u5f53\u4ed6\u662f\u5b58\u5fc3\u620f\u5f04\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u66f2\u5b50\u6700\u597d\u662f\u7559\u5230\u960e\u738b\u6bbf\u4e0a\u5439\u53bb\uff0c\u6211\u53ef \u6ca1\u6709\u8fd9\u4e2a\u96c5\u5174\uff01\u201d\u5f20\u5f00\u5927\u624b\u7acb\u5373\u5411\u90a3\u4e66\u751f\u6293\u53bb\u3002 \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u542c\u4e5f\u5f97\u542c\uff0c\u56e0\u4e3a\u4f60\u5fc5\u5b9a\u6bd4\u6211\u5148\u89c1\u960e\u738b\uff0c\u4eca\u65e5\u4e0d\u542c\uff0c\u4f60\u5c31\u6ca1\u6709\u673a\u4f1a\u542c \u4e86\u3002\u201d \u3000\u3000\u8f66\u7f2d\u7ec3\u7684\u662f\u5927\u529b\u9e70\u722a\u529f\u3002\u8fd9\u4e00\u6293\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\u3002\u90a3\u4e66\u751f\u7adf\u7136\u5373\u4e0d\u95ea\u907f\uff0c\u4e5f\u4e0d\u62db\u67b6\u3002\u773c \u770b\u8fd9\u4e00\u6293\u5df2\u662f\u6293\u5411\u4ed6\u7684\u8111\u95e8\uff0c\u4ed6\u53cc\u624b\u8fd8\u662f\u63e1\u7740\u7389\u8427\uff0c\u800c\u4e14\u628a\u7389\u8427\u51d1\u8fd1\u5507\u8fb9\uff0c\u5f53\u771f\u5439\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u751f\u6b7b\u5173\u5934\uff0c\u4ed6\u5c45\u7136\u8fd8\u6709\u95f2\u60c5\u9038\u81f4\u5439\u8427\uff0c\u8fd9\u4e0d\u662f\u628a\u6027\u547d\u4ea4\u5230\u5bf9\u65b9\u624b\u4e0a\u5417\uff1f\u6a80\u7fbd\u51b2\u90fd\u5413 \u5f97\u8df3\u8d77\u6765\u4e86\u3002 \u3000\u3000\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u8427\u58f0\u54cd\u8d77\uff0c\u8f66\u7f2d\u4e94\u6307\u5982\u94a9\uff0c\u8ddd\u79bb\u4ed6\u7684\u8111\u95e8\u5df2\u4e0d\u5230\u4e09\u5bf8\u3002\u5ffd\u5730\u53ea\u89c9\u4e00\u80a1\u70ed \u98ce\u8fce\u9762\u5439\u6765\uff0c\u864e\u53e3\u70ed\u8fa3\u8fa3\u7684\u987f\u65f6\u4f7f\u4e0d\u51fa\u6c14\u529b\uff0c\u5173\u5143\u7a74\u4e5f\u5ffd\u5730\u4e00\u9ebb\uff0c\u90a3\u611f\u89c9\u6709\u51e0\u5206\u50cf\u662f\u7ed9\u4eba\u70b9 \u7740\u7a74\u9053\uff0c\u53c8\u50cf\u662f\u7ed9\u9999\u706b\u707c\u7740\u4e00\u822c\u3002\u4f46\u4e66\u751f\u7684\u53cc\u624b\u8fd8\u662f\u63e1\u7740\u7389\u8427\uff0c\u8fde\u4e00\u6839\u5c0f\u6307\u5934\u90fd\u6ca1\u4f38\u51fa\u3002 \u3000\u3000\u8f66\u7f2d\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u77e5\u4ed6\u8fd9\u7389\u8427\u53e4\u602a\uff0c\u751f\u6015\u8fd8\u6709\u4ec0\u4e48\u6697\u5668\u4e4b\u7c7b\u4ece\u8427\u7ba1\u4e2d\u5439\u51fa\u6765\uff0c\u4e00 \u6293\u6293\u4e0b\u53bb\uff1f\u6025\u5fd9\u659c\u8eab\u5230\u7eb5\uff0c\u4e66\u751f\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u8bf4\u8fc7\uff0c\u8fd9\u652f\u66f2\u5b50\u4f60\u662f\u975e\u542c\u4e0d\u53ef\u7684\uff01\u201d \u3000\u3000\u8f66\u7f2d\u659c\u8dcc\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u811a\u8ddf\u521a\u521a\u7740\u5730\uff0c\u53ea\u89c1\u90a3\u4e66\u751f\u5df2\u662f\u6321\u5728\u4ed6\u7684\u9762\u524d\u3002 \u3000\u3000\u8f66\u7f2d\u6bd5\u7adf\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\u3002\u7a81\u7136\u60f3\u8d77\u4e00\u79cd\u6781\u4e3a\u5389\u5bb3\u7684\u6b66\u529f\uff0c\u636e\u8bf4\u5185\u529f\u7ec3\u5230\u6700\u9ad8\u7684\u5883\u754c \u65f6\uff0c\u53ef\u4ee5\u7ec3\u6210\u4f24\u4eba\u4e8e\u65e0\u5f62\u7684\u6c14\uff0c\u53ea\u987b\u5439\u4e00\u53e3\u6c14\uff0c\u5c31\u53ef\u4ee5\u514b\u654c\u5236\u80dc\u3002\u4f46\u8fd9\u79cd\u529f\u592b\uff0c\u53ea\u662f\u89c1\u4e8e\u4f20 \u8bf4\uff0c\u4ece\u6ca1\u6709\u542c\u8fc7\u8c01\u771f\u6b63\u7ec3\u6210\u529f\u7684\u3002\u201d\u96be\u9053\u8fd9\u9178\u4e01\u4ece\u7389\u8427\u4e2d\u5439\u51fa\u6765\uff0c\u5c31\u662f\u4f20\u8bf4\u4e2d\u53ef\u4ee5\u4f24\u4eba\u4e8e\u65e0 \u5f62\u7684\u7f61\u6c14\uff1f\u201d\u8f66\u7f2d\u6ca1\u6709\u731c\u9519\uff0c\u8fd9\u4e66\u751f\u624b\u4e2d\u7684\u7389\u8427\u4e43\u4e2d\u4e00\u4ef6\u7a00\u4e16\u4e4b\u5b9d\uff0c\u7528\u897f\u6606\u4ed1\u7684\u66a7\u7389\u9020\u6210 \u7684\uff0c\u540d\u5b57\u5c31\u53eb\u201c\u6696\u7389\u8427\u201d\uff0c\u4e66\u751f\u7684\u7f61\u6c14\u5176\u5b9e\u8fd8\u672a\u7ec3\u5f97\u6210\u529f\uff0c\u53ea\u662f\u5177\u6709\u51e0\u5206\u529f\u529b\u800c\u5df2\u3002\u4f46\u501f\u52a9 \u8fd9\u6696\u7389\u8427\u4e4b\u529b\uff0c\u5439\u51fa\u6765\u7684\u7f61\u6c14\u5374\u5df2\u662f\u53ef\u4ee5\u4f24\u4eba\u7684\u4e86\u3002\u4e0d\u8fc7\u8f66\u7f2d\u4e5f\u975e\u7b49\u95f2\u4e4b\u8f88\uff0c\u4ed6\u7684\u5185\u529b\u53d7\u4e86 \u5f71\u54cd\u3002\u8eab\u4f53\u5e76\u6ca1\u53d7\u4f24\uff0c\u811a\u8ddf\u521a\u4e00\u7740\u5730\uff0c\u4e1a\u5df2\u628a\u771f\u6c14\u7eb3\u5165\u4e39\u7530\uff0c\u7a74\u9053\u7684\u75bc\u9ebb\u4e4b\u611f\uff0c\u4ea6\u5df2\u89e3\u4e86\u3002 \u3000\u3000\u597d\u5728\u90a3\u4e66\u751f\u4ecd\u662f\u81ea\u987e\u81ea\u5730\u5439\u8427\uff0c\u5e76\u672a\u8fd8\u51fb\u3002\u8f66\u7f2d\u907f\u5f00\u6b63\u9762\uff0c\u7acb\u5373\u5c55\u5f00\u7ed5\u8eab\u6e38\u6597\u7684\u6253\u6cd5\u3002 \u7f61\u6c14\u4e0d\u4ece\u6b63\u9762\u88ad\u6765\uff0c\u4ed6\u7684\u5185\u529f\u6240\u53d7\u7684\u5f71\u54cd\u5c31\u51cf\u8f7b\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u8f66\u7f2d\u7684\u6b66\u529f\u662f\u5185\u5916\u517c\u4fee\u7684\u3002\u4e0d\u4f46\u638c\u529b\u521a\u731b\uff0c\u8eab\u6cd5\u4e5f\u5f88\u8f7b\u7075\u3002 \u3000\u3000\u4ed6\u907f\u5f00\u6b63\u9762\u548c\u7f61\u6c14\u63a5\u89e6\uff0c\u4e3a\u7684\u5c31\u662f\u60f3\u4e58\u6687\u62b5\u9699\uff0c\u4e00\u51fb\u5f97\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u5c55\u5f00\u6302\u7684\u8eab\u6cd5\u548c\u5bf9\u65b9\u6e38\u6597\u3002\u5374\u8fde\u5bf9\u65b9\u7684\u8863\u89d2\u90fd\u6ca1\u6cbe\u4e0a\u3002 \u3000\u3000\u90a3\u4e66\u751f\u597d\u4f3c\u95f2\u5ead\u4fe1\u6b65\uff0c\u968f\u968f\u4fbf\u4fbf\u8e0f\u4e0a\u4e00\u6b65\uff0c\u5c31\u6070\u597d\u907f\u5f00\u4e86\u4ed6\u7684\u653b\u51fb\u3002 \u3000\u3000\u8f66\u7f2d\u5fc3\u5934\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5929\u7f57\u6b65\u6cd5\uff1f\u201d \u3000\u3000\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5012\u8bc6\u8d27\u3002\u201d \u3000\u3000\u5929\u7f57\u6b65\u6cd5\u5c31\u50cf\u201c\u7f61\u6c14\u201d\u4e00\u822c\uff0c\u662f\u53ea\u89c1\u4e4b\u4e8e\u4f20\u8bf4\u4e2d\u7684\u4e00\u95e8\u4e0a\u4e58\u6b66\u529f\u3002\u636e\u8bf4\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c \u53ef\u4ee5\u5728\u767e\u4e07\u519b\u4e2d\u6765\u53bb\u81ea\u5982\uff0c\u522b\u4eba\u4f11\u60f3\u78b0\u7740\u4ed6\u4e00\u6839\u6c57\u6bdb\u3002 \u3000\u3000\u8fd9\u4e66\u751f\u867d\u672a\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u4f46\u7528\u6765\u5bf9\u4ed8\u8f66\u7f2d\u7684\u6e38\u6597\uff0c\u5374\u5df2\u7ef0\u7ef0\u6709\u4f59\u3002 \u3000\u3000\u8f66\u7f2d\u7684\u5fc3\u4e0d\u7531\u5f97\u4e00\u6c89\u3002\u5fc3\u60f3\u8fd9\u4e66\u751f\u82e5\u771f\u7684\u7ec3\u6210\u4e86\u5929\u7f57\u6b65\u6cd5\uff0c\u5c82\u975e\u4e1a\u5df2\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002 \u3000\u3000\u4f46\u4ed6\u5df2\u662f\u6b32\u7f62\u4e0d\u80fd\u3002 \u3000\u3000\u90a3\u4e66\u751f\u4ecd\u7136\u6ca1\u6709\u51fa\u624b\uff0c\u7ee7\u7eed\u5439\u8427\u3002 \u3000\u3000\u8427\u58f0\u9ad8\u4ea2\uff0c\u54cd\u904f\u884c\u4e91\uff0c\u5439\u5230\u6025\u5904\uff0c\u5b9b\u5982\u4e07\u9a6c\u5954\u817e\uff0c\u5343\u519b\u8d74\u654c\uff01 \u3000\u3000\u8f66\u7f2d\u542c\u5f97\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8dd1\u5f97\u8d8a\u5feb\u8d8a\u6025\u3002\u6325\u62f3\u8e22\u8db3\uff0c\u867d\u7136\u660e\u77e5\u6253\u4e0d\u4e2d\u5bf9\u65b9\uff0c\u5374\u4e5f\u5728 \u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u8d8a\u6765\u8d8a\u662f\u7528\u529b\uff0c\u8fd9\u60c5\u5f62\u5c31\u597d\u50cf\u662f\u4e00\u4e2a\u7cbe\u529b\u8fc7\u5269\u7684\u5c0f\u4f19\u5b50\uff0c\u505a\u4e00\u4e9b\u65e0\u804a\u7684\u52a8 \u4f5c\uff0c\u53ea\u6c42\u53d1\u6cc4\u4e00\u822c\u3002 \u3000\u3000\u4f46\u8f66\u7f2d\u65e9\u5df2\u4e0d\u662f\u6bdb\u5934\u5c0f\u4f19\u5b50\u4e86\uff0c\u4ed6\u662f\u4e00\u4e2a\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6b66\u5b66\u5927\u884c\u5bb6\u3002 \u3000\u3000\u7a81\u7136\u4ed6\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002\u82e5\u8fd8\u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\uff0c\u8fd9\u6837\u5f88\u8d39\u6c14\u529b\u4e0b\u53bb\uff0c\u4e0d\u5fc5\u5bf9\u65b9\u8fd8\u624b\uff0c\u4ed6\u81ea \u5df1\u5c31\u8981\u5012\u4e0b\u3002 \u3000\u3000\u5fc3\u5934\u4e00\u6e05\u9192\uff0c\u4ed6\u6025\u5fd9\u8dc3\u51fa\u5708\u5b50\uff0c\u548c\u90a3\u4e66\u751f\u4fdd\u6301\u4e09\u4e08\u5f00\u5916\u7684\u8ddd\u79bb\uff0c\u7ed5\u8eab\u6e38\u6597\u7684\u6253\u6cd5\u867d\u6ca1\u6539 \u53d8\uff0c\u4f46\u53ea\u662f\u8ddf\u7740\u5bf9\u65b9\u7684\u8eab\u5f62\u79fb\u52a8\u4e86\u3002 \u3000\u3000\u4e66\u751f\u7684\u8427\u58f0\u5ffd\u53c8\u4e00\u53d8\uff0c\u4ece\u9ad8\u4ea2\u53d8\u4e3a\u4f4e\u6c89\uff0c\u66f2\u8c03\u8d8a\u6765\u8d8a\u662f\u51c4\u6006\uff0c\u5b9b\u5982\u4e09\u5ce1\u733f\u557c\uff0c\u9c9b\u4eba\u591c \u6ce3\u3002 \u3000\u3000\u8f66\u7f2d\u542c\u5f97\u5fc3\u4e2d\u5982\u5760\u94c5\u5757\uff0c\u8ddf\u7740\u8282\u62cd\uff0c\u811a\u6b65\u4e5f\u4e0d\u77e5\u4e0d\u89c9\uff0c\u6162\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u65c1\u89c2\u8005\u6e05\uff0c\u696e\u5ca9\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8f66\u5927\u4eba\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u7fdf\u7136\u4e00\u7701\uff0c\u8fd9\u4e66\u751f\u8fd8\u6ca1\u51fa\u624b\uff0c\u4ed6\u7684\u5fc3\u7075\u5df2\u53d7\u63a7\u5236\uff0c\u4ed6\u662f\u60c5\u77e5\u6253\u4e0d\u8fc7\u5bf9\u65b9\u7684\u4e86\u3002\u4f46\u4ed6 \u53ef\u4e0d\u7518\u5fc3\u8fd9\u662f\u8fd9\u6837\u83ab\u540d\u5176\u5999\u5730\u8d25\u7ed9\u5bf9\u65b9\u3002 \u3000\u3000\u4ed6\u6ef4\u6e9c\u6e9c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u624b\u4e2d\u500f\u5730\u591a\u4e86\u4e00\u628a\u7cbe\u8292\u8000\u76ee\u7684\u957f\u5200\u3002 \u3000\u3000\u8fd9\u628a\u5200\u7684\u5f62\u5f0f\u5341\u5206\u53e4\u602a\uff0c\u5200\u8eab\u7ec6\u957f\uff0c\u5200\u950b\u8584\u5f97\u900f\u660e\uff0c\u5200\u67c4\u548c\u5200\u8eab\u76f8\u6bd4\uff0c\u77ed\u5f97\u4e0d\u6210\u6bd4\u4f8b\uff0c \u82e5\u662f\u62ff\u6765\u548c\u666e\u901a\u7684\u94a2\u5200\u76f8\u6bd4\uff0c\u751a\u81f3\u6839\u672c\u4e0d\u80fd\u8bf4\u662f\u201c\u5200\u67c4\u201d\uff0c\u53ea\u662f\u7528\u4e24\u5757\u5c0f\u5c0f\u7684\u94c1\u7247\u9576\u5d4c\u5728 \u201c\u5e94\u8be5\u662f\u5200\u70b3\u201d\u7684\u90e8\u4f4d\u3002\u539f\u6765\u8fd9\u662f\u4e00\u628a\u7528\u767e\u70bc\u7cbe\u94a2\u6253\u6210\u7684\u201c\u7f05\u5200\u201d\u3002\u2014\u2014\u5f53\u65f6\u94f8\u9020\u5200\u5251\u7684\u6280 \u672f\uff0c\u4ee5\u7f05\u7538\u6700\u4e3a\u4f18\u826f\uff0c\u8d28\u91cf\u6700\u4f73\u7684\u5b9d\u5200\uff0c\u662f\u5f53\u771f\u53ef\u4ee5\u628a\u767e\u70bc\u94a2\u5316\u6210\u5f20\u6307\u67d4\u7684\uff0c\u8f66\u7f2d\u8fd9\u628a\u7f05\u5200 \u5c31\u6b63\u662f\u6700\u597d\u7684\u4e00\u79cd\uff0c\u4e0d\u7528\u4e4b\u65f6\uff0c\u4ed6\u662f\u5f53\u6210\u8170\u5e26\u5377\u5728\u8170\u95f4\u7684\u3002 \u3000\u3000\u521d\u65f6\u4ed6\u89c1\u8fd9\u4e66\u751f\u624b\u4e2d\u53ea\u6709\u4e00\u652f\u7389\u8427\uff0c\u4ed6\u4ee5\u91d1\u56fd\u4e00\u7b49\u5df4\u56fe\u9c81\u7684\u8eab\u4efd\uff0c\u81ea\u662f\u4e0d\u80fd\u501a\u4ed7\u8fd9\u79cd\u5b9d \u5200\u53d6\u80dc\u3002\u800c\u4e14\u4ed6\u539f\u6765\u7684\u8ba1\u5212\uff0c\u4e5f\u53ea\u662f\u60f3\u628a\u8fd9\u4e66\u751f\u6d3b\u6349\uff0c\u4ee5\u6c42\u903c\u51fa\u4ed6\u7684\u53e3\u4f9b\u7684\uff0c\u4ed6\u6709\u5927\u529b\u9e70\u722a \u529f\uff0c\u4ee5\u4e3a\u5df1\u662f\u53ef\u4ee5\u7a33\u64cd\u80dc\u5238\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u5df2\u7ecf\u77e5\u9053\u5bf9\u65b9\u7684\u6b66\u529f\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u5f53\u7136\u662f\u4e0d\u8bba\u4ec0\u4e48\u624b\u6bb5\u90fd\u8981\u4f7f\u7528\u4e86\u3002 \u3000\u3000\u4ed6\u628a\u7f05\u5200\u4e00\u6296\uff0c\u500f\u5730\u53d8\u6210\u4e00\u628a\u4e09\u5c3a\u591a\u957f\u7684\u8f6f\u5200\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u90aa\u95e8\u90aa\u9053\uff0c\u6536\u8d77\u6765\u5427\u3002 \u6709\u672c\u9886\u7684\u548c\u6211\u89c1\u4e2a\u6b63\u7ae0\u3001\u201d\u5200\u5149\u970d\u970d\uff0c\u4fe8\u5982\u4e00\u9053\u94f6\u8679\u76d8\u65cb\u98de\u821e\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u628a\u8fd9\u4e66\u751f\u7684\u8eab \u5f62\u7b3c\u7f69\u5728\u5200\u5149\u4e4b\u4e0b\uff0c\u4f46\u90a3\u4e66\u751f\u4ecd\u662f\u610f\u6001\u60a0\u95f2\uff0c\u81ea\u987e\u81ea\u5730\u5439\u8427\uff0c\u4ed6\u7684\u5929\u7f57\u6b65\u6cd5\u5c55\u5f00\uff0c\u968f\u610f\u6240 \u4e4b\uff0c\u6709\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u8f66\u7f2d\u7684\u7f05\u5200\u4ecd\u662f\u780d\u4ed6\u4e0d\u4e2d\u3002 \u3000\u3000\u8f66\u7f2d\u8d8a\u53d1\u614c\u4e86\uff0c\u5ffd\u5730\u5fc3\u4e2d\u4e00\u8ba1\uff0c\u559d\u9053\uff1a\u201c\u8001\u696e\uff0c\u4f60\u95f2\u7740\u53cc\u624b\u5e72\u4ec0\u4e48\uff0c\u8fd8\u4e0d\u5077\u628a\u90a3\u5c0f\u6742\u79cd \u7ed9\u6211\u62ff\u4e0b\u3002\u201d\u53ea\u8981\u696e\u5ca9\u5e2e\u4eba\u628a\u6a80\u7fbd\u51b2\u62ff\u6765\u5f53\u4f5c\u4eba\u8d28\uff1b\u90a3\u5c31\u53ef\u4ee5\u8981\u80c1\u8fd9\u4e2a\u4e66\u751f\u4e86\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u696e\u5ca9\u4e00\u5b9a\u61c2\u5f97\u4ed6\u7684\u7528\u610f\u7684\uff0c\u54ea\u77e5\u696e\u5ca9\u4e5f\u4e0d\u77e5\u662f\u771f\u7684\u4e0d\u61c2\u8fd8\u662f\u5047\u7684\u4e0d\u61c2\uff0c\u4ed6\u542c\u4e86\u8f66 \u7f2d\u7684\u8bdd\uff0e\u9732\u51fa\u4e00\u8138\u6115\u7136\u7684\u795e\u8272\uff0c\u5374\u6ca1\u6709\u7acb\u5373\u52a8\u624b\u3002 \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\uff0c\u4e66\u751f\u7684\u4e00\u652f\u66f2\u5b50\u4e5f\u6070\u597d\u594f\u5b8c\u4e86\u3002 \u3000\u3000\u4ed6\u505c\u6b62\u5439\u8427\uff0c\u5ffd\u5730\u6717\u58f0\u541f\u9053\uff1a\u201c\u5c11\u5b64\u4e3a\u5ba2\u65e9\uff0c\u591a\u96be\u8bc6\u541b\u5468\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u7389\u8427\u671d\u7740\u6a80\u7fbd \u51b2\u4e00\u6307\u8bf4\u9053\uff1a\u201c\u51b2\u513f\uff0c\u540e\u9762\u4e24\u53e5\u4f60\u7ed9\u6211\u5ff5\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u5f00\u59cb\u6717\u541f\u7684\u65f6\u5019\uff0c\u6a80\u7fbd\u51b2\u7684\u8138\u4e0a\u5df2\u7ecf\u51fa\u975e\u5e38\u5947\u602a\u7684\u8868\u60c5\uff0c\u4f3c\u662f\u53c8\u60ca\u53c8\u559c\u3002 \u3000\u3000\u696e\u5ca9\u66f4\u662f\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u4e5f\u662f\u83ab\u540d\u5176\u5999\uff0c\u5728\u5200\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5c45\u7136\u8fd8\u6709\u5fc3\u5ff5\u8bd7\uff1f \u8fd9\u5b69\u5b50\u4e0d\u8fc7\u662f\u4e2a\u4ec6\u4eba\u7684\u5b69\u5b50\uff0c\u6211\u4ece\u6ca1\u89c1\u8fc7\u4ed6\u624b\u4e2d\u6367\u8fc7\u4e66\u672c\uff0c\u53c8\u61c2\u5f97\u4ec0\u4e48\u8bd7\u4e66\uff1f\u201d\u54ea\u77e5\u4ed6\u5fc3\u5ff5 \u672a\u5df2\uff0c\u6a80\u7fbd\u51b2\u5df2\u7ecf\u63a5\u4e0b\u53bb\u5ff5\u9053\uff1a\u201c\u63a9\u6ce3\u7a7a\u76f8\u5411\uff0c\u98ce\u5c18\u4f55\u6240\u671f\uff1f\u201d\u4e66\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4e0d\u9519\uff0c\u597d\u5b69\u5b50\u3001\u4f60\u679c\u7136\u662f\u6211\u7684\u5f92\u5f1f\uff01\u201d \u3000\u3000\u696e\u5ca9\u53c8\u662f\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u61c2\u56e0\u4f55\u51ed\u7740\u8fd9\u4e24\u53e5\u8bd7\u4ed6\u4eec\u624d\u80fd\u5e08\u5f92\u76f8\u8ba4\u3002\u201c\u8fd9\u4e66\u751f\u4e00\u51fa\u73b0\u7684\u65f6 \u5019\uff0c\u65e9\u5df2\u8bf4\u660e\u81ea\u5df1\u7684\u8eab\u4efd\u662f\u8fd9\u5b69\u5b50\u7684\u5e08\u7236\u4e86\uff0c\u4e3a\u4f55\u53c8\u8981\u4ed6\u5ff5\u51fa\u4e24\u53e5\u8bd7\u624d\u80fd\u786e\u5b9a\u4ed6\u662f\u81ea\u5df1\u7684\u5f92 \u5f1f\u5462\uff1f\u201d\u4ed6\u60f3\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e66\u751f\u5728\u7b54\u5e94\u6a80\u516c\u76f4\u7684\u8bf7\u6c42\uff0c\u6536\u4ed6\u7684\u5b59\u513f\u505a\u5f92\u5f1f\u4e4b\u65f6\uff0c\u4e3a\u4e86\u9884\u9632\u6709\u610f\u5916\u7684\u53d1\u751f\uff0c\u66fe \u7559\u4e0b\u4e00\u628a\u6247\u5b50\uff0c\u4f5c\u4e3a\u4ed6\u65e5\u5e08\u5f92\u76f8\u8ba4\u7684\u4fe1\u7269\uff0c\u6247\u4e3b\u9898\u6709\u4e00\u9996\u8bd7\uff0c\u5c31\u6b63\u662f\u4ed6\u4eec\u73b0\u5728\u6240\u5ff5\u7684\u8fd9\u9996 \u8bd7\u3002\u8fd9\u5176\u4e2d\u539f\u59d4\uff0c\u696e\u5ca9\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e66\u751f\u66fe\u7ecf\u5386\u8fc7\u65e0\u6570\u9669\u6076\u7684\u98ce\u6ce2\uff0e\u8bef\u4e2d\u522b\u4eba\u9677\u9631\u7684\u4e8b\u60c5\u4e5f\u66fe\u6709\u8fc7\u3002\u56e0\u6b64\u4ed6\u867d\u7136\u76f8\u4fe1\u6a80\u7fbd \u51b2\u5c31\u662f\u4ed6\u8981\u627e\u7684\u5f92\u5f1f\uff0c\u4f46\u8fd9\u53ea\u662f\u201c\u76f8\u4fe1\u201d\u800c\u5df2\uff0c\u8fd8\u5fc5\u987b\u5f97\u5230\u786e\u5b9e\u7684\u51ed\u636e\uff0c\u4ed6\u624d\u80fd\u51b3\u5b9a\u4ee5\u540e\u7684 \u4e8b\u60c5\u600e\u6837\u53bb\u505a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6bd4\u4ed6\u8fd8\u66f4\u6b22\u559c\uff0c\u8df3\u8d77\u6765\u53eb\u9053\uff1a\u201c\u5e08\u7236\u3002\u5e08\u7236\uff0c\u4f60\u679c\u7136\u662f\u6211\u7684\u5e08\u7236\uff01\u201d\u8f66\u7f2d\u559d\u9053\uff1a \u201c\u696e\u5ca9\uff0c\u4f60\u804b\u4e86\u5417\uff1f\u6211\u5429\u5490\u4f60\u628a\u8fd9\u5c0f\u6742\u79cd\u62ff\u4e0b\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u52a8\u624b\uff1f\u201d \u3000\u3000\u4f46\u6b64\u65f6\u52a8\u624b\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u4e66\u751f\u5728\u5927\u7b11\u58f0\u4e2d\uff0c\u7389\u8427\u500f\u5730\u6325\u51fa\uff01 \u3000\u3000\u7f05\u5200\u4e0e\u7389\u8427\u78b0\u4e2a\u6b63\u7740\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u6e85\u8d77\u70b9\u70b9\u706b\u82b1\u3002\u7389\u8352\u65e0\u635f\uff0c\u7f05\u5200\u5df2\u6709\u7f3a\u53e3\u3002 \u3000\u3000\u8f66\u7f2d\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u60f3\u6536\u56de\u7f05\u5200\uff0c\u5ffd\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u7f05\u5200\u5760\u5730\uff0c\u4eba\u4e5f\u9000\u4e86\u4e0b\u53bb\u3002\u4e66\u751f\u51fa\u624b\u5982 \u95ea\u7535\uff0c\u4ed6\u6765\u4e0d\u53ca\u62db\u67b6\u3002\u5c31\u5df2\u7ed9\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u696e\u5ca9\u89c1\u8f66\u7f2d\u5012\u4e0b\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u8dd1\u8fc7\u53bb\u6293\u6a80\u7fbd\u51b2\u3001\u6b64\u65f6\u4ed6\u624d\u53bb\u6293\u6a80\u7fbd\u51b2\uff0c\u5df2\u4e0d\u662f\u4e3a\u4e86 \u8f66\u7f2d\u7684\u7f18\u6545\uff0c\u800c\u662f\u4e3a\u4e86\u66ff\u81ea\u5df1\u627e\u201c\u62a4\u8eab\u7b26\u201d\u4e86\u3002 \u3000\u3000\u4e66\u751f\u811a\u5c16\u4e00\u6311\uff0c\u628a\u8dcc\u5728\u5730\u4e0a\u90a3\u7f05\u5200\u6311\u8d77\uff0c\u7f05\u5200\u5316\u4f5c\u4e00\u9053\u94f6\u8679\uff0c\u5411\u696e\u5ca9\u98de\u53bb\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u662f\u4e0d\u80fd\u5feb\u8fc7\u98de\u5200\u7684\u4e86\u3002\u98de\u5200\u6765\u52bf\u6025\u52b2\uff0c\u8981\u8eb2\u4e5f\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u51db\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u5fc3\u4e2d\u6697\u53eb\uff0c\u6211\u547d\u4f11\u77e3\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5413\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u624b\u4e0b\u7559\u2014\u2014\u201d\uff0c\u4e00\u4e2a\u201c\u60c5\u201d\u5b57\u8fd8\u672a\u8bf4\u5f97\u51fa \u6765\uff0c\u696e\u5ca9\u4e5f\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u696e\u5ca9\u53ea\u9053\u5fc5\u6b7b\u65e0\u7591\uff0c\u54ea\u77e5\u53ea\u89c9\u80a9\u5934\u4e00\u9ebb\uff0c\u4f7f\u5373\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u867d\u7136\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u5374\u5df2\u77e5\u9053\u4ed6\u53ea\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u5e76\u6ca1\u53d7\u4f24\u3002 \u3000\u3000\u539f\u6765\u4e66\u751f\u98de\u5200\u7684\u624b\u6cd5\u5999\u5230\u6beb\u5dc5\uff0c\u98de\u5230\u696e\u8005\u80cc\u540e\u7684\u65f6\u5019\uff0c\u7a81\u7136\u8f6c\u4e86\u65b9\u5411\uff0c\u53ea\u662f\u201c\u5200\u67c4\u201d\u662f \u90e8\u5206\u649e\u7740\u4ed6\u7684\u80a9\u5e76\u7a74\u3002\u8fd9\u628a\u7f05\u5200\u7684\u201c\u5200\u67c4\u201d\u662f\u7528\u4e24\u5757\u8584\u8584\u7684\u94c1\u7247\u5305\u7740\u7684\uff0c\u867d\u7136\u94c1\u7247\u5f88\u8584\uff0c\u5df2 \u7ecf\u8d77\u4e86\u4fdd\u62a4\u4f5c\u7528\uff0c\u8fde\u4ed6\u7684\u76ae\u8089\u90fd\u6ca1\u4f24\u7740\u3002 \u3000\u3000\u4e66\u751f\u5fae\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u4e2a\u4eba\u5bf9\u4f60\u8fd8\u7b97\u4e0d\u9519\uff0c\u6211\u6ca1\u4f24\u4eba\u3002\u8fd9\u628a\u7f05\u5200\u5f03\u4e4b\u53ef\u60dc\uff0c\u4f60\u6536\u4e0b\u6765 \u5c31\u5f53\u4f5c\u5e08\u7236\u7ed9\u4f60\u7684\u89c1\u9762\u793c\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u770b\uff0c\u696e\u5ca9\u8eab\u4e0a\u5e76\u6ca1\u9c9c\u8840\u6d41\u51fa\u3002\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u4e0a\u4e00\u5757\u77f3\u5934\u3001\u4ed6\u62fe\u8d77\u7f05\u5200\uff0c\u90a3\u4e66\u751f \u4e5f\u5df2\u6765\u5230\u4eba\u7684\u9762\u524d\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u627e\u5f97\u4f60\u597d\u82e6\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u80fd\u591f\u89c1\u5f97\u7740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u6251\u5165\u90a3\u4e66\u751f\u7684\u6000\u4e2d\uff0c\u5c31\u50cf\u89c1\u5230\u4eb2\u4eba\u4e00\u6837\uff0c\u4e0d\u77e5\u4e0d\u89c9\u6d41\u51fa\u773c\u6cea\u3002 \u3000\u3000\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u522b\u54ed\u3002\u522b\u54ed\u3002\u4f60\u7237\u7237\u4e0d\u662f\u5e38\u8bf4\uff0c\u597d\u5b69\u5b50\u6d41\u8840\u4e0d\u6d41\u6cea\u7684\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4f60\u7237\u7237\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u5e73\u65f6\u7684\u4e60\u60ef\u7528\u8bed\uff0c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u5509\uff0c\u4e8c\u5341\u5e74\u524d\uff0c \u4ed6\u4e5f\u66fe\u5bf9\u6211\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u628a\u6247\u5b50\u5462\uff1f\u201d\u4e66\u751f\u89c1\u6a80\u7fbd\u51b2\u5df2\u7ecf\u62b9\u5e72\u4e86\u773c\u6cea\uff0c\u4fbf\u5373\u95ee\u4ed6\u3002\u201c\u4ed6\u5988\u5988\u624b\u91cc\u3002\u201d\u6a80\u7fbd\u51b2 \u9053\u3002 \u3000\u3000\u201c\u4f60\u7237\u7237\u5462\uff1f\u201d\u4e66\u751f\u95ee\u9053\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7237\u7237\u5df1\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4e66\u751f\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u6b7b\u4e86\uff1f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7ed9\u574f\u4eba\u5bb3\u6b7b\u7684\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u4f60\u7239\u7239\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7239\u7239\u4e5f\u6b7b\u4e86\uff0c\u8fd8\u6709\uff0c\u5916\u516c\u4e5f\u6b7b\u4e86\uff01\u4ed6\u4eec\u90fd\u662f\u7ed9\u574f\u4eba\u5bb3\u6b7b\u7684\uff0c\u6b7b\u7684\u597d\u60e8\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u90a3\u4e9b\u574f\u4eba\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u542c\u5988\u5988\u8bf4\uff0c\u90a3\u4e9b\u574f\u4eba\u6709\u91d1\u56fd\u7687\u5e1d\u6d3e\u6765\u7684\uff0c\u4e5f\u6709\u5b8b\u56fd\u7687\u5e1d\u6d3e\u6765 \u7684\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5988\u5988\u8fd8\u6d3b\u7740\u5427\uff1f\u5feb\u5feb\u544a\u8bc9\u6211\uff0c\u4f60\u5988\u5988\u5728\u54ea\u91cc\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5979\u5728\u5546 \u5dde\u8282\u5ea6\u4f7f\u8857\u95e8\u3002\u201d \u3000\u3000\u4e66\u751f\u5f81\u4e86\u4e00\u6014\uff0c\u8bf4\u9047\uff1a\u201c\u5546\u5dde\u8282\u5ea6\u4f7f\u8859\u95e8\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u51e0\u5e74\u6211\u548c\u5988\u5988\u90fd\u662f\u4f4f\u5728\u90a3\u91cc\u3002\u201d\u4ed6\u662f\u4e00\u4e2a\u5341\u5206\u806a\u660e\u7684\u5b69\u5b50\uff0c\u77e5\u9053 \u5e08\u7236\u4e00\u5b9a\u662f\u56e0\u4e3a\u542c\u89c1\u4ed6\u4eec\u6bcd\u5b50\u4f4f\u5728\u8282\u5ea6\u4f7f\u8857\u95e8\u800c\u611f\u89c9\u5947\u602a\uff0c\u4ed6\u60f3\u548c\u5e08\u7236\u89e3\u91ca\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\u5374 \u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u4e66\u751f\u4e5f\u77e5\u201c\u8bf4\u6765\u8bdd\u957f\u201d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f85\u6211\u89c1\u4e86\u4ed6\u6bcd\u4eb2\u518d\u95ee\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4ed6\u60bc\u5ff5\u597d\u53cb\u4e4b\u6b7b\uff0c\u60c5\u7eea\u6fc0\u52a8\u4e4b\u6781\uff0c\u60b2\u58f0\u541f\u9053\uff1a\u201c\u63a9\u6ce3\u7a7a\u76f8\u5411\uff0c\u98ce\u5c18\u4f55\u6240\u671f\uff0c\u6a80\u516c\uff0c\u6a80\u516c \u5f53\u65f6\u6211\u5728\u6247\u4e0a\u9898\u8fd9\u9996\u8bd7\uff0c\u60f3\u4e0d\u5230\u7adf\u6210\u8bd7\u8c36\uff0c\u4f46\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u8f9c\u8d1f\u4f60\u7684\u671f\u671b\u7684\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\u8e22\u4e86\u8f66\u7f2d\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u8e22\u5f97\u5e76\u4e0d\u91cd\uff0c\u4f46\u8f66\u7f2d\u5df2\u662f\u50cf\u6740\u732a\u822c\u53f7\u53eb\u8d77\u6765\u3002\u4e0d\u4f46\u53f7\u53eb\uff0c\u800c\u4e14\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u597d\u50cf \u6b63\u5728\u53d7\u7740\u9177\u5211\uff0c\u6709\u4e00\u6761\u65e0\u5f62\u7684\u97ad\u5b50\uff0c\u4e0d\u65ad\u97ad\u6253\u4ed6\u3002 \u3000\u3000\u696e\u5ca9\u548c\u8f66\u7f2d\u4e00\u6837\uff0c\u90fd\u662f\u88ab\u70b9\u4e86\u7a74\u9053\u4f46\u5c1a\u672a\u5931\u6389\u77e5\u89c9\uff01\u696e\u5ca9\u89c1\u8f66\u7f2d\u5982\u6b64\u60e8\u72b6\uff0c\u53c8\u662f\u6015\uff0c\u53c8 \u662f\u6709\u70b9\u5947\u602a\uff0c\u8f66\u7f2d\u7684\u5185\u529f\u751a\u662f\u4e0d\u5f31\uff0c\u800c\u4e14\u4ed6\u7684\u813e\u6c14\u53c8\u662f\u5341\u5206\u5014\u5f3a\uff0c\u600e\u7684\u8fd9\u4e00\u811a\u90fd\u6371\u4e0d\u8d77\u3002 \u3000\u3000\u4ed6\u54ea\u77e5\u9053\uff0c\u539f\u6765\u8fd9\u4e66\u751f\u7684\u4e00\u8e22\uff0c\u4e43\u662f\u7528\u72ec\u95e8\u7684\u70b9\u7a74\u529f\u592b\uff0c\u8e22\u7740\u4e86\u8f66\u7f2d\u201c\u5927\u6a35\u7a74\u201d\u8fd9\u5927\u6a35 \u7a74\u7684\u90e8\u5206\u6b63\u5f53\u80cc\u9aa8\u7684\u795e\u7ecf\u672b\u68a2\uff0c\u8f66\u7f2d\u7684\u201c\u5927\u6a35\u7a74\u201d\u53d7\u4e86\u4e66\u751f\u5185\u529f\u7684\u51b2\u51fb\uff0c\u767b\u65f6\u5168\u8eab\u516b\u4e07\u56db\u5343 \u4e2a\u6bdb\u5b54\u90fd\u597d\u50cf\u6709\u4e00\u6839\u5229\u9488\u5728\u94bb\u523a\u4e00\u822c\u3002\u75db\u82e6\u7684\u611f\u89c9\uff0c\u96be\u4ee5\u5f62\u5bb9\uff0c\u5c82\u53ea\u50cf\u53d7\u5217\u5f62\u97ad\u6790\uff0c\u7b80\u76f4\u662f \u8d85\u8fc7\u5929\u4e0b\u7684\u4efb\u4f55\u4e00\u79cd\u9177\u5211\u3002 \u3000\u3000\u4e66\u5750\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4f1a\u6298\u78e8\u5b69\u5b50\uff0c\u5982\u4eca\u6211\u4e5f\u53eb\u4f60\u5c1d\u5c1d\u8be5\u53d7\u6298\u78e8\u7684\u6ecb\u5473\uff0c\u201d\u8f66\u7f2d\u53eb\u9053\uff1a \u201c\u4f60\uff0c\u4f60\u6740\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u4e66\u751f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u4fbf\u5b9c\u7684\u4e8b\u3002\u201d \u3000\u3000\u8f66\u7f2d\u547b\u541f\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u5212\u51fa\u9053\u513f\u5427\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u4f60\u7edd\u4e0d\u4f1a\u65e0\u6545\u6000\u7591\u8fd9\u5b69\u5b50\u662f\u6a80\u516c\u76f4\u7684\u5b59\u513f\uff0c\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u9053\uff1a\u201c\u662f\u54c8\u5fc5\u56fe\u3002\u201d \u3000\u3000\u4e66\u751f\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u95ee\uff1a\u201c\u54c8\u5fc5\u56fe\u5df2\u7ecf\u6765\u5546\u5dde\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u6b63\u5728\u5fcd\u53d7\u7740\u96be\u4ee5\u5f62\u5bb9\u7684\u75db\u82e6\uff0c\u597d\u50cf\u8fde\u8bf4\u8bdd\u7684\u6c14\u529b\u90fd\u6ca1\u6709\u4e86\u3002\u4ed6\u53ea\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4e66\u751f\u9053\uff1a\u54c8\u5fc5\u56fe\u5df2\u7ecf\u89c1\u8fc7\u4e86\u8fd9\u5b69\u5b50\u4e48\uff1f\u201c\u8f66\u9563\u9053\uff1a\u201c\u8fd8\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u5373\u7136\u6ca1\u6709\u89c1\u8fc7\u3002\u4f55\u4ee5\u4f60\u53c8\u8bf4\u662f\u4ed6\u544a\u8bc9\u4f60\u7684\uff1f\u201d\u8f66\u7f2d\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2026\u201d\u5728\u5730\u4e0a \u6253\u4e86\u4e24\u4e2a\u6eda\uff0c\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\u7684\u547b\u541f\u9053\uff1a\u201c\u6211\uff0c\u6211\u8981\u6b7b\u5566\uff01\u201d \u3000\u3000\u4e66\u751f\u98de\u8d77\u4e00\u811a\u3002\u8fd9\u4e00\u811a\u8e22\u5728\u4ed6\u7684\u5c3e\u9aa8\u4e0a\u3002\u8e22\u5f97\u5f88\u91cd\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u91cd\u91cd\u7684\u4e00\u811a\u8e22\u8fc7\u4e4b \u540e\u3002\u8f66\u7f2d\u8eab\u4e0a\u6240\u611f\u53d7\u7684\u90a3\u79cd\u6709\u5982\u7ed9\u65e0\u6570\u5229\u9488\u94bb\u523a\u4e4b\u82e6\u3002 \u3000\u3000\u5374\u662f\u987f\u7136\u6d88\u5931\u4e86\uff0c\u4e66\u751f\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u5b9e\u56de\u7b54\u6211\uff0c\u6211\u53ef\u4ee5\u8ba9\u4f60\u4fdd\u5168\u4e00\u6761\u6027\u547d\uff0c\u5426\u5219\u6211 \u8fd8\u6709\u66f4\u5389\u5bb3\u7684\u624b\u6bb5\u53eb\u4f60\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff01\u201d \u3000\u3000\u6c5f\u6e56\u4e0a\u4e60\u60ef\u7684\u8bf4\u8bdd\uff0c\u6240\u8c13\u201c\u53ef\u4ee5\u8ba9\u4f60\u4fdd\u5b58\u4e00\u6761\u6027\u547d\uff0c\u90a3\u5c31\u662f\u8981\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u7684\u610f\u601d\u3002 \u3000\u3000\u4e66\u751f\u4e00\u65f6\u95f4\u6ca1\u6709\u8be6\u52a0\u8003\u8651\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u7528\u4e86\u8fd9\u53e5\u6c5f\u6e56\u4e0a\u7684\u60ef\u8bed\uff0c\u672c\u6765\u5df2\u7ecf\u6062\u590d\u4e86\u51e0\u5206\u8840 \u8272\u7684\u8f66\u7f2d\u7684\u9762\u5b54\uff0c\u767b\u65f6\u53c8\u53d8\u5f97\u82cd\u767d\u5982\u7eb8\u4e86\u3002\u4e66\u751f\u8fd8\u6ca1\u89c9\u5bdf\uff0c\u559d\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u5440\uff01\u6211\u5df2\u7ecf\u66ff\u4f60 \u89e3\u4e86\u7a74\u9053\uff0c\u4f60\u8fd8\u5728\u8d56\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u8f66\u7f2d\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u624b\u6bb5\u53ef\u4ee5\u5f3a\u52a0\u4e8e\u6211\u4e86\uff01\u201d\u51b7\u7b11\u58f0\u4e2d\uff0c\u53ea\u89c1\u4ece\u773c\u8033\u76ee \u9f3b\u90fd\u6d41\u51fa\u8840\u6765\uff0c\u5c31\u50cf\u4e00\u68f5\u67af\u840e\u7684\u6811\u4f3c\u7684\uff0c\u6162\u6162\u5730\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8d81\u7740\u81ea\u5df1\u8fd0\u7528\u5185\u529f\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u81ea\u5df1\u9707\u65ad\u4e86\u81ea\u5df1\u7684\u5fc3\u8109\u4e86\u3002 \u3000\u3000\u4e66\u751f\u5446\u4e86\u7247\u523b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u867d\u7136\u53ef\u6076\uff0c\u5012\u8fd8\u7b97\u5f97\u662f\u4e00\u6761\u786c\u6c49\u3002 \u3000\u3000\u201d\u4e3a\u4e86\u8ba9\u8f66\u7f2d\u5728\u65ad\u6c14\u4e4b\u524d\u514d\u53d7\u75db\u82e6\uff0c\u7ed9\u4ed6\u8865\u4e0a\u4e00\u638c\u3002 \u3000\u3000\u8f66\u7f2d\u65ad\u65ad\u7eed\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6211\u5e73\u751f\u89c1\u8fc7\u7684\u6b66\u529f\u6700\u597d\u7684\u4eba\uff0c\u6b7b\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u4e5f\u4e0d\u7b97\u51a4\u6789 \u4e86\u3002\u201d\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u65b9\u59cb\u771f\u7684\u6b7b\u4e86\u3002 \u3000\u3000\u4e66\u5f80\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u89e3\u5f00\u696e\u5ca9\u7684\u7a74\u9053\u3002 \u3000\u3000\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5546\u5dde\u8282\u4f7f\u5b8c\u989c\u9274\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u696e\u5ca9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4ed6\u7684\u536b\u58eb\u3001\u4f60\u82e5\u8981\u706d\u53e3\uff0c\u5c3d\u7ba1\u6740\u6211\u3002\u201d \u3000\u3000\u4e66\u751f\u54c8\u54c8\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u522b\u7684\u8eab\u4efd\u3002\u4f60\u5fd8\u8bb0\u4e86\uff1f\u201d \u3000\u3000\u696e\u5ca9\u4f0d\u4e86\u4e00\u5f81\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8eab\u4efd\u7792\u4e0d\u8fc7\u4ee4\u5f92\uff0c\u4f60\u5bf9\u6211\u6709\u4ec0\u4e48\u6000\u7591\uff0c\u5927\u53ef\u95ee\u4f60\u7684\u5f92 \u5f1f\u3002\u201d \u3000\u3000\u4e66\u751f\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u4f60\u4e5f\u662f\u51b2\u513f\u7684\u5e08\u7236\u4e48\u3002\u4f60\u66ff\u6211\u6559\u4ed6\u51e0\u5e74\uff0c\u6211\u8fd8\u672a\u66fe\u5411\u4f60\u9053\u8c22\u5462\uff0c \u600e\u4f1a\u5c06\u4f60\u4e3a\u96be\u3002\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u4f60\u4e5f\u628a\u6211\u5f53\u4f5c\u670b\u53cb\u770b\u5f85\u3002 \u3000\u3000\u201d \u3000\u3000\u696e\u5ca9\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u77e5\u9053\u4ec0\u4e48\uff0c\u4f60\u5c3d\u7ba1\u95ee\u3002\u4f46\u6211\u53ef\u5f97\u6709\u8a00\u5728\u5148\uff0c\u80fd\u8bf4\u7684\u6211\u624d\u8bf4\uff0c\u4e0d\u80fd\u8bf4 \u7684\u4f60\u6740\u4e86\u6211\u4e5f\u4e0d\u8bf4\u3002\u201d\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u8d70\u4e86\u6ca1\u6709\uff1f \u3000\u3000\u201d \u3000\u3000\u696e\u5ca9\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u3002\u6211\u79bb\u5f00\u8859\u95e8\u7684\u65f6\u5019\uff0c\u5b8c\u989c\u5c06\u519b\u5728\u5728\u56ed\u4e2d\u8bbe\u5bb4\uff0c\u8bf7\u4ed6\u8d4f\u7261\u4e39\u82b1\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u54e6\uff0c\u8bf7\u4ed6\u8d4f\u7261\u4e39\uff01\u201d\u4e0d\u77e5\u600e\u7684\uff0c\u5f53\u5730\u8bf4\u5230\u201c\u7261\u4e39\u201d\u4e8c\u5b57\u4e4b\u65f6\u3002\u58f0\u97f3\u7adf\u662f\u5fae\u5fae \u98a4\u6296\uff0c\u4f3c\u4e4e\u9887\u6709\u4ec0\u4e48\u611f\u89e6\u4f3c\u7684\u3002 \u3000\u3000\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7559\u5728\u5e9c\u8859\u966a\u5ba2\uff1f\u201d\u4e66\u751f\u4e3a\u4e86\u63a9\u9970\u81ea\u5df1\u201c\u5931\u6001\u201d\uff0c\u7b11\u95ee\u696e\u5ca9\u3002\u696e\u5ca9\u672a\u7b54\u3002 \u3000\u3000\u4e66\u751f\u63a5\u7740\u53c8\u95ee\uff1a\u201c\u542c\u8bf4\u8f66\u7f2d\u672c\u662f\u54c8\u5fc5\u56fe\u7684\u4eba\uff0c\u7531\u54c8\u5fc5\u56fe\u4fdd\u8350\u5916\u8c03\u5546\u5dde\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u696e\u5ca9\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u6bd4\u6211\u8fd8\u66f4\u6e05\u695a\u3002\u4f60\u53eb\u6211\u8fd8\u80fd\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8f66\u7f2d\u5b8c\u5168\u662f\u4e3a\u4e86\u76d8\u67e5\u8fd9\u5b69\u5b50\u7684\u6765\u5386\uff0c\u8fd9\u624d\u5b81\u53ef\u653e\u5f03\u4f3a\u5019\u65e7\u65e5\u4e0a\u53f8 \u7684\u673a\u4f1a\u7684\u3002\u4f46\u4ed6\u8bf4\u54c8\u5fc5\u56fe\u8fd8\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u5b69\u5b50\uff0c\u662f\u771f\u7684\u5417\uff1f\u201d\u696e\u5ca9\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u201d\u4ed6\u77e5\u9053 \u4e66\u751f\u62c5\u5fc3\u7684\u662f\u4ec0\u4e48\uff0c\u8ddf\u7740\u52a0\u4ee5\u89e3\u91ca\uff1a\u201c\u54c8\u5fc5\u56fe\u77e5\u9053\u6a80\u8d1d\u52d2\u7684\u5ab3\u5987\u548c\u5b59\u513f\u5f53\u65e5\u5e76\u672a\u9047\u96be\uff0c\u5c1a\u5728 \u9003\u4ea1\uff0e\u60f3\u5fc5\u662f\u54c8\u5fc5\u56fe\u544a\u8bc9\u4e86\u8f66\u7f2d\uff0c\u8f66\u7f2d\u60f3\u8d77\u4e86\u8fd9\u5b69\u5b50\u6765\u5386\u4e0d\u4e0d\u660e\uff0c\u5e74\u9f84\u548c\u6a80\u8d1d\u52d2\u7684\u5b59\u513f\u76f8 \u7b26\uff0c\u800c\u4e14\u7ec3\u6b66\u53c8\u8fd9\u4e48\u8fdb\u5883\u795e\u901f\uff0c\u8fd9\u8bb8\u591a\u7591\u70b9\u52a0\u8d77\u6765\uff0c\u4ed6\u8fd9\u624d\u6000\u7591\u5230\u4ee4\u5f92\u8eab\u4e0a\u7684\u3002\u4f46\u636e\u6211\u6240 \u77e5\uff0c\u4ed6\u4eca\u5929\u4e5f\u8fd8\u6ca1\u6709\u89c1\u8fc7\u54c8\u5fc5\u56fe\uff0c\u6240\u4ee5\u4f60\u5927\u53ef\u653e\u5fc3\uff0c\u54c8\u5fc5\u56fe\u60f3\u5fc5\u8fd8\u6ca1\u6709\u77e5\u9053\u4ed6\u4eec\u6bcd\u5b50\u7adf\u662f\u548c \u4ed6\u4e00\u540c\u4f4f\u5728\u8282\u5ea6\u4f7f\u7684\u8859\u95e8\u3002\u201d\u8fd9\u4e66\u751f\u7684\u786e\u662f\u5728\u4e3a\u6a80\u7fbd\u51b2\u7684\u6bcd\u4eb2\u76ee\u524d\u7684\u5904\u5883\u62c5\u5fe7\uff0c\u542c\u4e86\u696e\u5ca9\u7684 \u8bdd\uff0c\u65b9\u7a0d\u7a0d\u5b9a\u5fc3\u3002\u4e66\u751f\u8138\u4e0a\u4f3c\u4e4e\u9732\u51fa\u4e00\u70b9\u5947\u602a\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u81ea\u52a8\u544a\u8bc9\u6211\u8fd9\u4e48\u591a \u4e8b\u60c5\uff1f\u201d \u3000\u3000\u696e\u5ca9\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u6a80\u8d1d\u52d2\u72af\u4e86\u4ec0\u4e48\u5927\u7f6a\uff0c\u6211\u53ea\u77e5\u9053\u4ed6\u505a\u7684\u4e8b\u60c5\u662f\u5bf9\u7684\uff0c \u4e0d\u7ba1\u4ed6\u662f\u5426\u72af\u4e86\u7f6a\uff0c\u4ed6\u90fd\u662f\u6211\u5fc3\u4e2d\u4f69\u670d\u7684\u4eba\uff01\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u4f60\u4e5f\u540c\u60c5\u6a80\u5ba4\u5b64\u513f\u5be1\u5987\uff1f\u201d \u3000\u3000\u696e\u5ca9\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f46\u6211\u8eab\u4e3a\u5b8c\u989c\u5c06\u519b\u7684\u536b\u58eb\uff0c\u5018\u82e5\u662f\u5b8c\u989c\u5c06\u519b\u4e0b\u4ee4\u8981\u6211\u6349\u62ff\u4ed6 \u4eec\uff0c\u6211\u8fd8\u662f\u4e0d\u80fd\u4e0d\u4ece\uff0c\u6240\u4ee5\u4f60\u82e5\u662f\u4e3a\u4e86\u9884\u9632\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u4f60\u6740\u4e86\u6211\u6211\u4e5f\u6b7b\u800c\u65e0\u6028\u3002 \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u770b\u6765\u4f60\u4e0d\u50cf\u662f\u5b8c\u989c\u9274\u7684\u5fc3\u8179\u536b\u58eb\u3002\u201d \u3000\u3000\u696e\u5ca9\u9053\uff1a\u201c\u7684\u786e\u4e0d\u662f\u3002\u4e0d\u8fc7\uff0c\u4ed6\u662f\u6211\u7684\u4e3b\u4eba\u3002\u5e76\u4e14\u6211\u66fe\u53d7\u8fc7\u4ed6\u7684\u6069\u60e0\u3002\u4e0d\u7ba1\u4ed6\u662f\u5426\u7c91\u6211 \u5f53\u4f5c\u5fc3\u8179\uff0c\u6211\u8fd8\u662f\u8981\u5fe0\u5fc3\u4e8e\u4ed6\u7684\u3002\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u4e8b\u3002\u4f46\u7b2c\u4e00\uff0c\u5b8c\u989c\u9274\u672a\u5fc5\u4f1a\u628a\u8fd9\u4ef6\u5dee\u4e8b\u4ea4\u7ed9\u4f60\uff0c\u7b2c\u4e8c\uff0c\u6211\u4e5f\u6709 \u529e\u6cd5\u53eb\u4f60\u907f\u8fc7\u8fd9\u4ef6\u5dee\u4e8b\u3002\u6240\u4ee5\u76ee\u524d\u4f60\u4e0d\u5fc5\u4e3a\u6b64\u62c5\u5fc3\uff0c\u6211\u60f3\u518d\u95ee\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u696e\u5ca9\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u4e66\u751f\u9053\uff1a\u201c\u5b8c\u989c\u9274\u7684\u592b\u4eba\u662f\u5426\u4e5f\u5728\u5546\u5dde\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u95ee\u8d77\u5b8c\u989c\u9274\u7684\u59bb\u5b50\u5df2\u662f\u4e00\u5947\uff0c\u9762\u5bf9\u5b8c\u989c\u9274\u76f4\u547c\u5176\u540d\uff0c\u5bf9\u4ed6\u7684\u59bb\u5b50\u5219\u5c0a\u4e3a\u201c\u592b \u4eba\u201d\uff0c\u4e5f\u662f\u4e0d\u5927\u5408\u4e4e\u201c\u5e38\u7406\u201d\u7684\u3002\u696e\u5ca9\u83ab\u540d\u5176\u5999\uff0c\u4f46\u60f3\u8fd9\u4ef6\u4e8b\u8bf4\u7ed9\u4ed6\u542c\u4e5f\u65e0\u59a8\uff0c\u4fbf\u9053\uff1a\u201c\u5b8c \u989c\u5c06\u519b\u662f\u548c\u592b\u4eba\u4e00\u540c\u4e0a\u4efb\u7684\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u4eec\u592b\u5987\u6069\u7231\u975e\u5e38\uff0c\u5b8c\u989c\u5c06\u519b\u4ece\u524d\u9886\u5175\u51fa\u5916\u5f81\u6218\uff0c \u4ed6\u7684\u592b\u4eba\u4e5f\u80fd\u968f\u884c\u7684\u3002 \u3000\u3000\u201d\u696e\u5ca9\u9053\uff1a\u201c\u5b8c\u989c\u9274\u82b1\u56ed\u4e2d\u90a3\u4e9b\u7261\u4e39\uff0c\u662f\u592b\u4eba\u8981\u79cd\u7684\u5427\uff1f\u201d\u696e\u5ca9\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u77e5 \u9053\uff1f\u201d \u3000\u3000\u4e66\u751f\u6ca1\u6709\u56de\u7b54\u4ed6\u7684\u95ee\u9898\uff0c\u5374\u4f3c\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u5728\u5546\u5dde\uff0c\u5b8c\u989c\u9274\u5728\u5546\u5dde\uff0c\u5b8c\u989c \u592b\u4eba\u4e5f\u5728\u5546\u5dde\uff0c\u597d\uff0c\u597d\uff0c\u597d\uff01\u201d \u3000\u3000\u696e\u5ca9\u4e0d\u61c2\u4ed6\u8fde\u58f0\u53eb\u201c\u597d\u201d\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u7741\u5927\u773c\u775b\u770b\u4ed6\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e66\u751f\u5ffd\u5730\u6717\u58f0\u541f\u9053\uff1a\u201c\u5341\u5e74\u78e8\u4e00\u5251\uff0c\u6709\u65e5\u5feb\u6069\u4ec7\uff01\u5018\u80fd\u5728\u4e00\u65e5\u4e4b\u95f4\u4e86\u7ed9\u6069\u4ec7\uff0c\u5b9e \u662f\u4eba\u751f\u4e00\u5927\u5feb\u4e8b\u3002\u4e0d\u7ba1\u5546\u5dde\u8282\u5ea6\u4f7f\u7684\u8859\u95e8\u662f\u9f99\u6f6d\u8fd8\u662f\u864e\u7a74\u6211\u90fd\u8981\u53bb\u95ef\u4e00\u95ef\u7684\u4e86\uff01\u201d\u696e\u5ca9\u5403\u4e00 \u60ca\u9053\uff1a\u201c\u5b8c\u989d\u5c06\u519b\u548c\u4f60\u6709\u4ec7\uff1f\u4f60\u8981\u53bb\u6740\u4ed6\u5417\uff1f\u201d \u3000\u3000\u4e66\u751f\u8bf4\u4e86\u4e00\u53e5\u5f88\u5947\u602a\u7684\u8bdd\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u8bb2\u7a76\u7684\u662f\u6069\u6028\u5206\u660e\uff0c\u6709\u4ec7\u5c31\u662f\u6709\u4ec7\uff0c\u6ca1\u4ec7\u5c31\u662f\u6ca1\u4ec7\uff0c\u4f46\u4ed6\u7684\u56de\u7b54\u7adf\u662f\uff1a\u201c\u6211\u4e0d \u77e5\u9053\u3002\u201d\u8fd9\u4e00\u56de\u7b54\uff0c\u4ee4\u696e\u5ca9\u4e0d\u89c9\u4e3a\u4e4b\u4e00\u6115\u3002 \u3000\u3000\u201c\u90a3\u4e48\u54c8\u5fc5\u56fe\u5462\uff1f\u201d\u696e\u5ca9\u518d\u95ee\u3002 \u3000\u3000\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u662f\u6211\u6700\u597d\u7684\u670b\u53cb\u7684\u4ec7\u4eba\u4e4b\u4e00\uff0c\u4ea6\u662f\u6211\u7684\u4ec7\u4eba\u3002\u4e0d\u8fc7\u4ed6\u4e0d\u662f\u5bb3\u6b7b\u6211\u90a3 \u4f4d\u670b\u53cb\u7684\u4e3b\u51f6\uff0c\u8981\u4e0d\u8981\u6740\u4ed6\uff0c\u5982\u4eca\u6211\u8fd8\u672a\u77e5\u9053\u3002\u770b\u4ed6\u600e\u6837\uff0c\u5230\u9644\u518d\u8bf4\u3002\u201d\u867d\u7136\u4ed6\u6ca1\u6709\u8bf4\u51fa\u4ed6 \u90a3\u4f4d\u201c\u6700\u597d\u670b\u53cb\u201d\u7684\u540d\u5b57\uff0c\u696e\u5ca9\u4ea6\u5df2\u77e5\u9053\u4ed6\u8bf4\u7684\u662f\u6a80\u516c\u76f4\u4e86\u3002\u696e\u5ca9\u8bf4\u9053\uff1a\u201c\u4f60\u6740\u54c8\u5fc5\u56fe\u6211\u4e0d \u7ba1\uff0c\u4f46\u4f60\u82e5\u8981\u6740\u5b8c\u987e\u5c06\u519b\uff0c\u6211\u867d\u4e0d\u582a\u4f60\u7684\u4e00\u51fb\uff0c\u6211\u3001\u6211\u2026\u2026\u201d \u3000\u3000\u4e66\u751f\u4e0d\u5f85\u4ed6\u628a\u8bdd\u8bf4\u6765\u3002\u4fbf\u5373\u7b11\u9053\u201c\u696e\u5144\uff0c\u4f60\u5df2\u7ecf\u592a\u7d2f\u4e86\uff0c\u4e0d\u5e94\u8be5\u4e3a\u8fd9\u4e9b\u4e8b\u64cd\u5fc3\u4e86\uff0c\u4f60\u597d \u597d\u7761\u4e00\u89c9\u5427\uff01\u201d \u3000\u3000\u696e\u5ca9\u672c\u6765\u60f3\u8bf4\u7684\u662f\uff1a\u201c\u6211\u867d\u4e0d\u582a\u4f60\u7684\u4e00\u51fb\uff0c\u6211\u4e5f\u975e\u5f97\u548c\u4f60\u62fc\u547d\u4e0d\u53ef\u7684\u3002\u201d\u8bf4\u5230\u201d\u6211\u201d\u5b57 \u4e4b\u65f6\uff0c\u7a81\u7136\u4fbf\u89c9\u5f97\u660f\u660f\u6b32\u7761\uff0c\u5f85\u5230\u4e66\u5148\u8bf4\u5230\u4e00\u4e2a\u201c\u7761\u201d \u3000\u3000\u5b57\uff0c\u4ed6\u679c\u7136\u5c31\u5012\u5728\u5730\u4e0a\uff1b\u800c\u4e14\u5f88\u5feb\u5c31\u6253\u8d77\u9f3e\u6765\u3002\u771f\u7684\u50cf\u662f\u719f\u7761\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u770b\u5f97\u597d\u50cf\u50bb\u4e86\u3002\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u696e\u53d4\u53d4\u4e0d\u662f\u6b7b\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e66\u751f\u5fae\u7b11\u901a\u3002\u201c\u4ed6\u5f53\u7136\u6ca1\u6709\u6b7b\u3002\u6211\u53ea\u662f\u70b9\u4e86\u4ed6\u7684\u6655\u7761\u7a74\u3002\u800c\u4e14\u662f\u7528\u6700\u8f7b\u7684\u4e00\u79cd\u624b\u6cd5\u70b9\u4ed6 \u7684\u6655\u7761\u7a74\uff0c\u53ea\u987b\u8fc7\u4e86\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4ed6\u5c31\u4f1a\u81ea\u5df1\u9192\u6765\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u677e\u4e86\u53e3\u6c14\u3002\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u3002\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u6740\u4eba\u7684\uff0c\u56e0\u4e3a\u4ed6\u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e08\u7236\u662f\u4ece\u6765\u4e0d\u6740\u597d\u4eba\u7684\u3002\u4e0d\u8fc7\u4e09\u4e2a\u65f6\u8fb0\u6211\u53ef\u4ee5\u53bb\u505a\u8bb8\u591a\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f60\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u53bb\u66ff\u4f60\u7684\u7237\u7237\u62a5\u4ec7\uff0c\u540c\u65f6\u4e5f\u662f\u53bb\u63a5\u4f60\u7684\u5988\u5988\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f60\u7b49\u4e00\u7b49\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f60\u7684\u5927\u540d\u6211\u8fd8\u672a\u77e5\u9053\u5462\u3002\u201d \u3000\u3000\u201c\u6211\u590d\u59d3\u8036\u5f8b\uff0c\u540d\u53eb\u7384\u5143\u3002\u7531\u4e8e\u7384\u5143\u540c\u97f3\uff0c\u8fd9\u4e66\u751f\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u6307\u5934\u5728\u5730\u4e0a\u5199\u51fa\u8fd9\u4e24\u4e2a\u5b57 \u6765\uff0c\u5199\u5b8c\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4ed6\u7ad9\u8d77\u6765\u6478\u6478\u6a80\u7fbd\u51b2\u7684\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\u3002\u6211\u8d70 \u4e86\u3002\u201d\u6a80\u7fbd\u51b2\u5ffd\u5730\u53c8\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u7b49\u4e00\u7b49\u201d\u201c\u54e6\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u8036\u5f8b\u7384\u5143\u95ee\u9053\u3002 \u3000\u3000\u201c\u5e08\u7236\uff0c\u90a3\u4f4d\u5b8c\u989c\u592b\u4eba\uff0c\u90a3\u4f4d\u5b8c\u989c\u592b\u4eba\uff0c\u5979\u3001\u5979\u2026\u2026\u6a80\u7fbd\u51b2\u4f3c\u4e4e\u5f88\u96be\u5f00\u53e3\u4f3c\u7684\uff0c\u8981\u9f13\u8d77 \u5f88\u5927\u7684\u52c7\u6c14\u3002\u624d\u80fd\u591f\u8bf4\u51fa\u6765\u3002\u8036\u5f8b\u7384\u5143\u5fc3\u5934\u4e00\u51db\uff0c\u62ac\u8d77\u773c\u775b\u671b\u7740\u4ed6\u9053\uff1a\u201c\u90a3\u4f4d\u5b8c\u989c\u592b\u4eba\u600e\u4e48 \u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5979\u3001\u5979\u662f\u597d\u4eba\uff0c\u6211\u5e0c\u671b\u4f60\u4e0d\u8981\u6740\u5979\uff01\u201d\u8036\u5f8b\u7384\u5143\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f60 \u600e\u4e48\u77e5\u9053\u5979\u662f\u597d\u4eba\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u548c\u5988\u5988\u7684\u6027\u547d\u662f\u5979\u6551\u7684\uff0c\u6211\u5988\u5988\u66ff\u5979\u79cd\u7261\u4e39\u3002\u5979\u5e76\u6ca1 \u6709\u5c06\u6211\u4eec\u5f53\u4f5c\u4ec6\u4eba\u770b\u5f85\u3002\u5979\u5bf9\u6211\u7684\u59b9\u59b9\u66f4\u662f\u597d\u5f97\u4e0d\u5f97\u4e86\u3002\u201d\u8bf4\u81f3\u6b64\u5904\u3002\u5fc3\u91cc\u7a0d\u5fae\u6709\u70b9\u4e0d\u5927\u81ea \u7136\u7684\u611f\u89c9\uff0c\u597d\u50cf\u81ea\u5df1\u8bf4\u4e86\u8c0e\u8bdd\u4e00\u822c\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u5f53\u7136\u4e0d\u662f\u8c0e\u8bdd\uff0c\u5b8c\u989c\u592b\u4eba\u7684\u786e\u662f\u5bf9\u4ed6\u7684\u59b9\u59b9\u597d\u5230\u4e0d\u5f97\u4e86\u7684\uff0c\u8282\u5ea6\u4f7f\u8859\u95e8\u7684\u5a62\u4ec6\u90fd \u8bf4\u3002\u592b\u4eba\u7b80\u76f4\u662f\u628a\u4ed6\u7684\u59b9\u59b9\u5f53\u4f5c\u81ea\u5df1\u7684\u4eb2\u751f\u5973\u513f\u4e00\u6837\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u7684\u6bcd\u4eb2\u5374\u4e0d\u613f\u610f\u63a5\u53d7\u592b\u4eba\u8fd9\u79cd\u2018\u597d\u610f\u201d\u3002\u5979\u79c1\u5e95\u4e0b\u4e5f\u66fe\u5bf9\u513f\u5b50\u8bf4\u8fc7\u3002\u592b\u4eba\u6837\u6837\u90fd \u597d\uff0c\u5c31\u662f\u8fd9\u4ef6\u4e8b\u201c\u4e0d\u597d\u201d\uff0c\u56e0\u4e3a\u592b\u4eba\u628a\u5979\u7684\u5973\u513f\u642c\u5230\u5185\u5802\u629a\u517b\uff0c\u5979\u60f3\u89c1\u81ea\u5df1\u7684\u4eb2\u751f\u5973\u513f\u90fd\u56f0 \u96be\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u56e0\u4e3a\u5f88\u96be\u89c1\u5230\u59b9\u59b9\u800c\u89c9\u5f97\u201c\u4e0d\u597d\u201d\u3002\u4f46\u73b0\u5728\u4ed6\u62c5\u5fc3\u5e08\u7236\u4e00\u5230\u8282\u5ea6\u4f7f\u8859\u95e8\uff0c\u4ee5\u5e08\u7236\u7684 \u6b66\u529f\u3002\u53ea\u6015\u5c31\u8981\u5f04\u6210\u201c\u7389\u77f3\u4ff1\u711a\u201d\uff0c\u56e0\u6b64\u4ed6\u4e0d\u80fd\u4e0d\u5c3d\u91cf\u8bf4\u5b8c\u98a0\u592b\u4eba\u7684\u597d\u8bdd\uff0c\u8fde\u4ed6\u672c\u6765\u89c9\u5f97\u662f \u201c\u4e0d\u597d\u201d\u7684\uff0c\u4e5f\u8981\u8bf4\u6210\u201c\u597d\u201d\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u51b7\u6da9\u5730\u7b11\u4e86\u4e00\u7b11\uff1b\u8bf4\u9053\uff1a\u201c\u5979\u7684\u4e08\u592b\u600e\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u5fe1\u9053\uff1a\u201c\u5b8c\u989c\u5c06\u519b\u5bf9\u6211\u4eec\u4e0d\u597d\u4e5f\u4e0d\u574f\u3002\u4ed6\u7684\u773c\u775b\u91cc\u597d\u50cf\u6ca1\u6709\u6211\u4eec\u6bcd\u5b50\u5b58\u5728\uff0c\u8bf4\u8001\u5b9e \u8bdd\u3001\u6211\u662f\u6709\u70b9\u8ba8\u538c\u8fd9\u4e2a\u4eba\u7684\u3002\u4ed6\u5e38\u5e38\u8bf4\u8981\u53bb\u6253\u5b8b\u56fd\uff0c\u559c\u6b22\u6253\u4ed7\u7684\u4eba\uff0c\u5927\u6982\u4e5f\u4e0d\u4f1a\u662f\u597d\u4eba\u5427\uff1f \u4e0d\u8fc7\u4ed6\u7684\u59bb\u5b50\u548c\u4ed6\u5e76\u4e0d\u4e00\u6837\uff0c\u4ed6\u7684\u59bb\u5b50\u662f\u4e0d\u559c\u6b22\u6253\u4ed7\u7684\uff0c\u5bf9\u4eba\u4e5f\u5f88\u548c\u6c14\uff0c\u5b8c\u5168\u4e0d\u50cf\u5c06\u519b\u90a3\u6837 \u51b7\u9177\u3002\u6240\u4ee5\u4f60\u6740\u5979\u7684\u4e08\u592b\u4e0d\u6253\u7d27\uff0c\u4f46\u53ef\u4e0d\u8981\u6740\u5979\uff0e\u56e0\u4e3a\u5979\u662f\u597d\u4eba\uff01\u201d\u4ed6\u91cd\u8bf4\u4e00\u904d\u201c\u5979\u662f\u597d \u4eba\uff01\u201d\u4ee5\u6c42\u52a0\u5f3a\u8bed\u6c14\u3002 \u3000\u3000\u5b69\u5b50\u7684\u201c\u597d\u201d\u201c\u574f\u201d\u6807\u51c6\u5f88\u7b80\u5355\u3002\u4f46\u6a80\u7fbd\u51b2\u5bf9\u5b8c\u989c\u9274\u592b\u59bb\u7684\u201c\u8bc4\u8bba\u201d \u3000\u3000\u5374\u597d\u50cf\u8bf4\u5230\u4e86\u8036\u5f8b\u7384\u5143\u7684\u5fc3\u91cc\u53bb\uff0c\u4ee4\u5f97\u4ed6\u7684\u773c\u775b\u90fd\u6709\u70b9\u6f6e\u6e7f\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u4e00\u6b21\u51b7\u6da9\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u8bf4\u5f97\u5f88\u5bf9\u3002\u5176\u5b9e\uff0c\u4e5f\u7528\u4e0d\u7740\u4f60\u544a\u8bc9\u6211\u3002\u6211 \u65e9\u5df2\u77e5\u9053\u5979\u662f\u597d\u4eba\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u5ffd\u5730\u51c4\u7136\u541f\u9053\uff1a\u201c\u6545\u4fa3\u6545\u56ed\u90fd\u4e0d\u89c1\uff0c\u6cb3\u5c71\u975e\u65e7\u6211\u91cd\u6765\uff01\u201d \u3000\u3000\u51c4\u541f\u58f0\u4e2d\uff0c\u8036\u5f8b\u7384\u5143\u8d70\u4e86\u3002\u8d70\u5f97\u5f88\u5feb\uff0c\u8f6c\u773c\u5c31\u4e0d\u89c1\u8e2a\u8ff9\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u61c2\u4ed6\u541f\u7684\u8fd9\u4e24\u53e5\u8bd7\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5fc3\u91cc\u53ea\u5728\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u5e08\u7236\u600e\u4e48\u65e9\u5c31\u77e5\u9053\u5b8c \u989c\u592b\u4eba\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u201c\u54e6\uff0c\u7687\u4e0a\u4e5f\u8981\u5fcc\u60ee\u4ed6\u5417\uff1f\u8fd9\u4e2a\u4eba\u540d\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8036\u5f8b\u7384\u5143\uff01\u201d \u3000\u3000\u6b64\u65f6\u5b8c\u989c\u9274\u6b63\u548c\u59bb\u5b50\u5728\u5367\u5ba4\u4e2d\u5bc6\u8c08\u3002 \u3000\u3000\u4ed6\u662f\u56e0\u4e3a\u201c\u5170\u59d1\u201d\u6bcd\u5b50\u7684\u4e8b\u60c5\u62c5\u7740\u5fc3\u4e8b\uff0c\u6545\u6b64\u56de\u5230\u623f\u4e2d\u95ee\u4ed6\u7684\u59bb\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u628a\u54c8\u5fc5\u56fe\u7684\u8bdd\u544a\u8bc9\u59bb\u5b50\u3002 \u3000\u3000\u201c\u6211\u5df2\u7ecf\u66ff\u4ed6\u4eec\u6bcd\u5b50\u906e\u63a9\u4e86\u3001\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u6bcd\u5b50\u7684\u786e\u662f\u6709\u8bb8\u591a\u53ef\u7591\u4e4b\u5904\uff0c\u90a3\u5b69\u5b50\u7684\u5e74\u9f84\u4e5f \u76f8\u7b26\uff0c\u8bf4\u4e0d\u5b9a\u771f\u7684\u5c31\u662f\u6a80\u516c\u76f4\u7684\u5ab3\u5987\u548c\u5b59\u513f\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5bf9\u201c\u5170\u59d1\u201d\u6bcd\u5b50\u7684\u4e8b\u60c5\u5374\u597d\u50cf\u6beb\u65e0\u201c\u5174\u8da3\u201d\uff0c\u5979\u53ea\u544a\u8bc9\u4e08\u592b\u5979\u5e76\u6ca1\u6709\u53d1\u73b0\u8fd9\u4e24 \u6bcd\u5b50\u6709\u4ec0\u4e48\u201c\u5f02\u72b6\u201d\uff0c\u5979\u8bf4\u201c\u4e0d\u4f1a\u7684\uff0c\u4e0d\u4f1a\u7684\uff1a\u5170\u59d1\u662f\u91d1\u4eba\uff0c\u592b\u5bb6\u59d3\u9102\u3002\u5979\u600e\u4f1a\u662f\u6a80\u8d1d\u52d2\u7684 \u6c49\u4eba\u513f\u5ab3\u5f20\u96ea\u6ce2\uff1f\u201d\u5b8c\u989c\u9274\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9102\u662f\u54b1\u4eec\u91d1\u4eba\u7684\u59d3\u6c0f\uff0c\u6c49\u4eba\u662f\u6ca1 \u6709\u8fd9\u4e2a\u59d3\u7684\u3002\u4f46\u9102\u5b57\u548c\u5cb3\u5b57\u4e0d\u6b63\u662f\u540c\u97f3\u3002\u5f20\u96ea\u6ce2\u5f53\u7136\u8981\u6539\u6362\u59d3\uff0c\u5979\u7684\u5916\u516c\u662f\u5cb3\u98de\uff0c\u8bf4\u4e0d\u5b9a\uff0c \u8bf4\u4e0d\u5b9a\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u8868\u73b0\u51fa\u5f88\u4e0d\u8010\u70e6\u7684\u6837\u5b50\u6253\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u4f60\u771f\u662f\u592a\u8fc7\u60f3\u5165\u975e\u975e\u4e86\uff01\u597d\u5566\uff0c\u597d \u5566\uff0c\u6211\u66ff\u4f60\u591a\u7559\u610f\u4ed6\u4eec\u6bcd\u5b50\u5c31\u662f\uff0c\u5018\u82e5\u53d1\u73b0\u4ed6\u4eec\u6709\u751a\u53ef\u7591\u4e4b\u5904\u6211\u518d\u544a\u8bc9\u4f60\u5427\u3002\u201d\u201c\u4f46\u90a3\u5b69\u5b50 \u2014\u2014\u201d\u5b8c\u989c\u9274\u9053\u3002 \u3000\u3000\u201c\u90a3\u5b69\u5b50\u4e00\u56de\u6765\uff0c\u6211\u5c31\u53eb\u5170\u59d1\u5e26\u53bb\u89c1\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u662f\u8981\u89c1\u54c8\u5fc5\u56fe\uff01\u201d \u3000\u3000\u201c\u968f\u4fbf\u4f60\u559c\u6b22\u53eb\u4ed6\u53bb\u89c1\u8c01\u5c31\u89c1\u8c01\uff0c\u597d\u4e86\uff0c\u522b\u518d\u628a\u4e0b\u4eba\u7684\u4e8b\u60c5\u70e6\u6211\u4e86\u3002 \u3000\u3000\u6211\u53ea\u60f3\u542c\u4f60\u8bb2\u4e00\u8bb2\u7687\u4e0a\u4ed6\u5fcc\u60ee\u7684\u90a3\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5979\u5bf9\u201c\u5170\u59d1\u201d\u6bcd\u5b50\u6ca1\u201c\u5174\u8da3\u201d\uff0c\u5bf9\u8fd9\u4e24\u4e2a\u4eba\u5374\u5f88\u6709\u201c\u5174\u8da3\u201d\uff0c\u5c24\u5176\u5bf9\u8036\u5f8b\u7384\u5143\u7684\u540d\u5b57\u6781 \u4e3a\u6ce8\u610f\u3002 \u3000\u3000\u201c\u54e6\uff0c\u4f60\u542c\u8fc7\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\uff1f\u201d\u5b8c\u989c\u9274\u4e0d\u89c9\u8d77\u4e86\u4e00\u70b9\u7591\u5fc3\uff0c\u95ee\u4ed6\u59bb\u5b50\u3002 \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5b8c\u989c\u592b\u4eba\u7d20\u6765\u4e0d\u559c\u6b22\u591a\u8bdd\uff0c\u53ea\u7b54\u4e86\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u4f46\u4f60\u542c\u89c1\u4ed6\u7684\u540d\u5b57\u597d\u50cf\u6709\u70b9\u60ca\u8be7\uff1f\u201d\u5b8c\u989c\u9274\u9053\u3002\u4ed6\u88c5\u4f5c\u6f2b\u4e0d\u7ecf\u610f\u95ee\u4ed6\u7684\u59bb\u5b50\uff0c\u4f46\u5df2\u6709 \u70b9\u63a9\u9970\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u80fd\u4ee4\u5f97\u54b1\u4eec\u7687\u4e0a\u987e\u5fcc\u7684\u4eba\uff0c\u6211\u600e\u80fd\u4e0d\u611f\u89c9\u60ca\u8be7\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4f1a\u77e5\u9053\u8fd9\u4e2a\u4eba\u5462\u3002\u201d\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u4f1a\u8fd9\u6837 \u4ee5\u4e3a\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u8fbd\u56fd\u6700\u540e\u4e00\u4e2a\u7687\u5e1d\u8036\u5f8b\u5ef6\u79a7\u7684\u79c1\u751f\u5b50\u3002\u8036\u5f8b\u5ef6\u79a7\u5728\u672a\u88ab\u7acb\u592a\u5b50\u4e4b \u524d\uff0c\u662f\u4e3a\u4ed6\u7684\u7236\u738b\u9547\u5b88\u966a\u90fd\u7684\u3002\u6240\u4ee5\u8fd9\u4e2a\u79c1\u751f\u5b50\u8036\u5f8b\u7384\u5143\u4e5f\u662f\u5728\u966a\u90fd\u957f\u5927\u7684\u3002\u8fbd\u56fd\u7684\u966a\u90fd\u5f53 \u65f6\u79f0\u4e3a\u2018\u5357\u4eac\u2019\uff0c\u53c8\u79f0\u2018\u71d5\u4eac\u2019\uff0c\u5982\u4eca\u5219\u5df2\u662f\u54b1\u4eec\u91d1\u56fd\u7684\u4eac\u57ce\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u540e\u6765\u8036\u5f8b\u5ef6\u79a7\u505a\u4e86\u7687\u5e1d\u4e4b\u540e\uff0c\u628a\u4ed6\u8fd9\u79c1\u751f\u5b50\u4ece\u71d5\u4eac\u63a5\u56de\u53bb\uff0c\u8fd9\u4ef6\u4e8b\u867d\u7136\u505a \u5f97\u79d8\u5bc6\uff0c\u4f46\u5176\u5b9e\u4ea6\u5df2\u7b49\u4e8e\u662f\u516c\u5f00\u7684\u79d8\u5bc6\u4e86\u3002\u636e\u8bf4\u8fd8\u662f\u5f53\u5e74\u8f70\u52a8\u4e00\u65f6\u7684\u65b0\u95fb\u7684\u3002\u5f53\u65f6\u4f60\u4eec\u4e00\u5bb6 \u597d\u50cf\u4e5f\u662f\u4f4f\u5728\u8fbd\u56fd\u7684\u71d5\u4eac\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u50cf\uff0c\u6211\u4eec\u4e00\u76f4\u90fd\u662f\u4f4f\u5728\u71d5\u4eac\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4ee5\u4e3a\u4f60\u6216\u8005\u4f1a\u542c\u8fc7\u8fd9\u4ef6\u4e09\u5341\u5e74\u524d\u8fbd\u56fd\u738b\u5ba4\u7684\u79d8\u95fb\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u6211\u5bb6\u867d\u7136\u4f4f\u5728\u71d5\u4eac\uff0c\u4f46\u6211\u548c\u4f60\u4e00\u6837\uff0c\u90fd\u662f\u5973\u771f\u65cf\u4eba\uff0c\u548c\u8fbd\u56fd\u7684\u5951\u4e39\u8d35\u65cf \u662f\u6781\u5c11\u5f80\u6765\u7684\u3002\u6211\u53c8\u662f\u4e00\u4e2a\u811a\u6b65\u4e0d\u51fa\u95fa\u95e8\u7684\u5973\u5b69\u5b50\uff0c\u600e\u77e5\u9053\u5916\u9762\u7684\u65b0\u95fb\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u5c31\u7b97\u4e86\u3002\u4f46\u5982\u4eca\u53ef\u53c8\u6709\u4ed6\u7684\u65b0\u95fb\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u65b0\u95fb\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u4e2a\u8036\u5f8b\u7384\u5143\u4e09\u5e74\u524d\u9003\u5230\u5b8b\u56fd\u53bb\uff0c\u5982\u4eca\u5df2\u7ecf\u56de\u6765\u4e86\u3002\u800c\u4e14\u53ef\u80fd\u6b63\u662f\u5728\u6211\u6240 \u8f96\u4e0b\u7684\u5546\u5dde\u5883\u5185\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5fc3\u5934\u5267\u8df3\uff0c\u6781\u529b\u6291\u5236\u81ea\u5df1\uff0c\u4e0d\u5728\u795e\u8272\u4e0a\u8868\u9732\u51fa\u6765\uff0c\u6545\u610f\u8bf4\u9053\uff1a\u201c\u5c06\u519b\uff0c\u90a3\u4e0d\u6b63 \u662f\u7ed9\u4e86\u4f60\u4e00\u4e2a\u53ef\u4ee5\u7acb\u529f\u7684\u673a\u4f1a\u5417\uff1f\u201d\u5b8c\u989c\u9274\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u6b66\u529f\u9ad8\u5f3a\u4e4b\u6781\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c \u6211\u8fd8\u6709\u70b9\u62c5\u5fc3\uff0c\u4ed6\u4f1a\u8dd1\u6765\u8fd9\u65f6\u66ff\u4ed6\u7684\u597d\u53cb\u6a80\u516c\u76f4\u62a5\u4ec7\u5462\u3002\u636e\u6211\u6240\u77e5\uff0c\u6a80\u516c\u76f4\u5341\u4e4b\u516b\u4e5d\u5df2\u7ecf\u6b7b \u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u6a80\u516c\u76f4\u53c8\u4e0d\u662f\u4f60\u5bb3\u6b7b\u7684\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u524d\u4e24\u5929\u6765\u7684\u8fd9\u4f4d\u94a6\u5dee\u5927\u4eba\u54c8\u5fc5\u56fe\u53ef\u6b63\u662f\u6740\u5bb3\u6a80\u516c\u76f4\u7684\u4eba\u4e4b\u4e00\u3001\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u5c06\u519b\uff0c\u90a3\u4f60\u53ef\u8981\u5c0f\u5fc3\u4e00\u70b9\u624d\u597d\u3002\u201d\u58f0\u97f3\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u6296\u98a4\uff0c\u8ddf\u7740\u518d\u95ee\uff1a \u201c\u4f60\u4ee5\u4e3a\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u4f1a\u6765\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u89c1\u59bb\u5b50\u5982\u6b64\u5173\u5fc3\u81ea\u5df1\uff0c\u5fc3\u91cc\u751c\u4e1d\u4e1d\u5730\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u8fc7\u62c5\u5fc3\u3002\u4e0d\u9519\uff0c \u4ed6\u7684\u6b66\u529f\u662f\u5f88\u9ad8\u5f3a\uff0c\u4f46\u6211\u624b\u4e0a\u7684\u80fd\u4eba\u4e5f\u5f88\u4e0d\u5c11\u3002\u5982\u4eca\u6211\u4e0d\u662f\u6015\u4ed6\u8981\u8981\u6765\uff0c\u53ea\u662f\u6015\u4ed6\u4e0d\u6765\uff0c\u65e9 \u6765\u6bd4\u8fdf\u6765\u66f4\u597d\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u98a4\u58f0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u56e0\u4e3a\u6709\u54c8\u5fc5\u56fe\u5728\u8fd9\u91cc\u3002\u54c8\u5fc5\u56fe\u662f\u5927\u5185\u7b2c\u4e8c\u9ad8\u624b\uff0c\u6b66\u529f\u4ec5\u6b21\u4e8e\u5927\u5185\u603b\u7ba1\u9102\u5c14 \u6cf0\uff0c\u867d\u7136\u4ed6\u672a\u5fc5\u80dc\u5f97\u8fc7\u8036\u5f8b\u7384\u5143\uff0c\u5927\u6982\u4e5f\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u6211\u7684\u624b\u4e0b\uff0c\u6b66\u529f\u8db3\u4ee5\u548c\u4e00\u7b49\u5df4\u56fe\u9c81 \u76f8\u5f53\u7684\u6709\u5341\u6570\u4eba\u4e4b\u591a\uff0c\u8036\u5f8b\u7384\u5143\u672c\u9886\u518d\u5f3a\uff0c\u4ed6\u4e5f\u7edd\u5bf9\u8ba8\u4e0d\u4e86\u597d\u53bb\u3002\u6b64\u4eba\u2014\u65e5\u4e0d\u9664\uff0c\u603b\u662f\u6211\u7684 \u5fc3\u8179\u4e4b\u60a3\uff0c\u56e0\u6b64\u6211\u5012\u5df4\u4e0d\u5f97\u4ed6\u4eca\u65e5\u5c31\u6765\uff0c\u65e9\u65e9\u4f5c\u4e2a\u4e86\u7ed3\u3002\u201d\u5b8c\u989c\u592b\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d \u4f1a\u6765\u5f97\u8fd9\u6837\u5feb\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u9664\u975e\u4ed6\u4e0d\u5728\u5546\u5dde\uff0c\u5426\u5219\u4ed6\u5373\u4f7f\u4eca\u5929\u4e0d\u6765\uff0c\u660e\u5929\u4e5f\u4f1a\u6765\u7684\u3002\u56e0\u4e3a\u4ed6\u548c\u6a80\u516c\u76f4 \u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u4ed6\u4e5f\u60f3\u8d81\u7740\u54c8\u5fc5\u56fe\u8fd8\u5728\u8fd9\u91cc\uff0c\u8d76\u6765\u4e3a\u4ed6\u7684\u670b\u53cb\u62a5\u4ec7\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u8fd8\u5728\u9999\u4ead\u90a3\u8fb9\u7b49\u6211\uff0c\u6211\u662f\u62bd\u7a7a\u56de\u6765\u95ee\u4f60\u5173\u4e8e\u5170 \u59d1\u7684\u4e8b\u7684\uff0c\u6211\u53ef\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u5c06\u519b\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u5fc3\u91cc\u6709\u70b9\u5bb3\u6015\u3002\u4f60\uff0c\u4f60\u6709\u6b63\u4e8b\u5728\u8eab\uff0c\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u5b89\u6170\u5979\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u73b0\u5728\u5c31\u662f\u53bb\u548c\u54c8\u5fc5\u56fe\u5e03\u7f6e\u600e\u6837\u52a0\u5f3a\u9632\u536b\uff0c\u8036\u5f8b\u7384\u5143\u9664\u975e \u4e0d\u6765\uff0c\u6765\u4e86\u5b9a\u5fc5\u81ea\u6295\u7f57\u7f51\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5446\u5446\u671b\u7740\u4ed6\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5fc3\u91cc\u53ef\u662f\u5341\u5206\u6b22\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5979\u5e73\u65f6\u5bf9\u6211\u51b7\u51b7\u6de1\u6de1\uff0c\u5374\u539f\u6765\u8fd8\u662f\u5bf9\u6211\u5982\u6b64\u5173 \u5fc3\u7684\u3002\u5509\uff0c\u5979\u5bf9\u6211\u51b7\u6de1\uff0c\u5176\u5b9e\u4e5f\u602a\u4e0d\u5f97\u5979\u3002\u6211\u5e73\u7684\u5fd9\u4e8e\u516c\u52a1\uff0c\u5f88\u5c11\u548c\u5979\u5171\u4eab\u95fa\u623f\u4e4b\u4e50\uff0c\u5979\u54ea \u80fd\u4e0d\u6028\u6211\u5462\u3002\u5f85\u8fd9\u4ef6\u4e8b\u60c5\u8fc7\u53bb\uff0c\u6211\u53ef\u8981\u591a\u62bd\u4e00\u70b9\u65f6\u95f4\u966a\u4f34\u5979\u4e86\u3002\u201d\u4ed6\u8f7b\u8f7b\u543b\u4e86\u59bb\u5b50\u4e00\u4e0b\uff0c\u91cd \u590d\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u653e\u5fc3\u3002\u4ed6\u7edd\u8ba1\u4f24\u5bb3\u4e0d\u4e86\u6211\uff0c\u66f4\u4f24\u5bb3\u4e0d\u4e86\u4f60\u3001\u4f60\u7684\u7cbe\u795e\u4f3c\u4e4e\u4e0d\u5927\u597d\uff0c\u4f60\u629b \u5f00\u5fe7\u8651\uff0c\u653e\u5fc3\u5148\u7761\u4e00\u4e2a\u5348\u89c9\u5f97\u7740\u5427\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u7761\u5f97\u7740\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4f60\u7761\u4e0d\u7740\uff0c\u90a3\u5c31\u5728\u8fd9\u91cc\u7b49\u6211\u3002\u4f60\u82e5\u89c9\u5f97\u65e0\u804a\uff0c\u53ef\u4ee5\u53eb\u5170\u59d1\u6765\u4f34\u4f60\uff0c\u987a\u4fbf\u4f60 \u4e5f\u53ef\u4ee5\u5957\u95ee\u5979\u7684\u53e3\u4f9b\u3002\u201d\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u5170\u59d1\u7684\u4e8b\u6211\u6ca1\u5fc3\u60c5\u7ba1\u4e86\u3002\u5c06\u519b\uff0c\u4f60\u8981\u5f88\u665a\u624d\u56de\u6765 \u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u665a\u996d\u6211\u4e0d\u56de\u6765\u5403\u4e86\uff0c\u4e0d\u8fc7\u665a\u4e0a\u6211\u4f1a\u56de\u6765\u966a\u4f60\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u4ed6\u3001\u4ed6\u4eca\u5929\u5c31\u4f1a\u6765\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u6709\u6b64\u53ef\u80fd\u800c\u5df2\uff0c\u4f46\u4f9d\u6211\u770b\uff0c\u4ed6\u6700\u65e9\u6050\u6015\u4e0d\u5f97\u5230\u660e\u5929\u665a\u4e0a\u624d\u6765\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u56e0\u4e3a\u636e\u6211\u63a5\u5230\u7684\u6d88\u606f\uff0c\u4ed6\u6628\u5929\u624d\u5230\u5927\u6563\u5173\uff0c\u5373\u4f7f\u8d70\u5f97\u5feb\uff0c\u4eca\u5929\u4e5f\u624d\u80fd\u8e0f\u5165 \u5546\u5dde\u5883\u5185\u3002\u4ed6\u603b\u5f97\u6709\u70b9\u51c6\u5907\uff0c\u624d\u6562\u8dd1\u6765\u6211\u8fd9\u8282\u5ea6\u4f7f\u7684\u8859\u95e8\u3002\u591c\u884c\u4eba\u5f53\u7136\u662f\u5fc5\u5b9a\u9009\u62e9\u665a\u4e0a\u7684\uff0c \u6240\u4ee5\u6211\u4f30\u8ba1\u4ed6\u6700\u65e9\u4e5f\u5f97\u7b49\u5230\u660e\u5929\u665a\u4e0a\u624d\u6765\u3002\u201c\u8bf4\u7f62\u53c8\u8f7b\u8f7b\u543b\u4e86\u59bb\u5b50\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u4f46\u6211\u77e5\u9053 \u4f60\u5fc3\u91cc\u5bb3\u6015\uff0c\u6240\u4ee5\u4eca\u665a\u6211\u5fc5\u5b9a\u56de\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u8d70\u4e86\uff0c\u5b8c\u989c\u592b\u4eba\u8fd8\u5728\u72ec\u81ea\u5446\u5446\u5730\u51fa\u795e\u3002 \u3000\u3000\u5979\u7684\u670d\u89d2\u6c81\u51fa\u4e00\u9897\u6cea\u73e0\uff0c\u8fd9\u662f\u5979\u5fcd\u4e86\u597d\u4e45\u7684\u6cea\u6c34\uff0c\u5728\u4e08\u592b\u8d70\u4e86\u4e4b\u540e\uff0c\u624d\u4e0d\u77e5\u4e0d\u89c9\u6d41\u4e86\u51fa \u6765\u3002 \u3000\u3000\u5979\u6ca1\u62ed\u773c\u6cea\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u597d\u50cf\u4e00\u5c0a\u77f3\u50cf\u3002 \u3000\u3000\u5916\u8868\u662f\u4e00\u5c0a\u77f3\u50cf\uff0c\u5fc3\u4e2d\u5374\u662f\u7ffb\u6eda\u7684\u6ce2\u6d9b\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u5979\u662f\u5728\u60f3\u5fc3\u4e8b\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u662f\u5bb3\u6015\u8036\u5f8b\u7384\u5143\u4f1a\u6765\u4f24\u5bb3\u5979\uff0c\u751a\u81f3\u4e5f\u4e0d\u662f\u4e3a\u4e08\u592b\u62c5\u5fc3\uff0c\u867d\u7136\u8036\u5f8b\u7384\u5143\u5e76\u975e\u6ca1\u6709\u53ef \u80fd\u4f24\u5bb3\u7684\u5979\u7684\u4e08\u592b\uff0c\u4f46\u5979\u8ba4\u4e3a\u8fd9\u4e2a\u53ef\u80fd\u6027\u5e76\u4e0d\u5f88\u5927\u3002 \u3000\u3000\u5979\u6700\u62c5\u5fc3\u7684\u662f\uff0c\u8036\u5f8b\u7384\u5143\u6765\u4e86\uff0c\u4e0d\u77e5\u4f1a\u95f9\u51fa\u4ec0\u4e48\u4e8b\u60c5\uff01\u6b63\u56e0\u4e3a\u7ed3\u679c\u96be\u4ee5\u9884\u6599\uff0c\u5979\u624d\u62c5 \u5fc3\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u5979\u4e5f\u62c5\u5fc3\u8036\u5f8b\u7384\u5143\u6765\u201c\u81ea\u6295\u7f57\u7f51\u201d\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4f46\u8fd9\u4e2a\u62c5\u5fc3\u8fd8\u5728\u5176 \u6b21\u3002\u56e0\u4e3a\u4ed6\u77e5\u9053\u8036\u5f8b\u7384\u5143\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u8fd8\u5728\u5979\u4e08\u592b\u7684\u4f30\u8ba1\u4e4b\u4e0a\u3002\u4f46\u4e5f\u6b63\u56e0\u4e3a\u6597\u6210\u4e24\u8d25\u4ff1\u4f24\u7684 \u5c40\u9762\u4e5f\u6709\u53ef\u80fd\u51fa\u73b0\uff0c\u5979\u5fc5\u987b\u9632\u6b62\u8fd9\u4e2a\u5c40\u9762\u7684\u51fa\u73b0\u3002 \u3000\u3000\u201c\u4f46\u6211\u53c8\u4e0d\u80fd\u51fa\u9762\u53bb\u529d\u963b\u4ed6\uff0c\u600e\u6837\u529e\u5462\uff1f\u201d\u5979\u60f3\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u5979\u4f1a\u8fd9\u6837\u60f3\uff1f\u56e0\u4e3a\u53ea\u6709\u5979\u77e5\u9053\uff0c\u8036\u5f8b\u7384\u5143\u5047\u5982\u771f\u7684\u8dd1\u6765\u5e9c\u8859\uff0c\u90a3\u5c31\u6050\u6015\u4e0d\u4ec5\u662f\u4e3a \u4e86\u627e\u54c8\u5fc5\u56fe\u66ff\u597d\u53cb\u62a5\u4ef7\uff0c\u66f4\u5927\u7684\u539f\u56e0\u662f\u4e3a\u4e86\u627e\u5979\uff01\u4f46\u5979\u73b0\u5728\u662f\u8282\u5ea6\u4f7f\u592b\u4eba\uff0c\u53c8\u600e\u80fd\u548c\u4ed6\u89c1\u9762 \u5462\uff1f\u56e0\u6b64\u5979\u6700\u62c5\u5fc3\u7684\u5c31\u6b63\u662f\u8fd9\u4e00\u70b9\uff0c\u6015\u4ed6\u6765\u4e86\uff0c\u628a\u4e8b\u60c5\u95f9\u5f97\u4e0d\u53ef\u6536\u62fe\uff01\u201d \u3000\u3000\u201c\u4ed6\u5c0f\u65f6\u5019\u7684\u80dc\u683c\u662f\u5f88\u5bb9\u6613\u51b2\u52a8\u7684\uff0c\u9694\u4e86\u4e09\u5341\u5e74\uff0c\u4e0d\u77e5\u4ed6\u8fd8\u662f\u4e0d\u662f\u50cf\u4ee5\u524d\u90a3\u6837\uff1f\u5509\uff0c\u53e4 \u8bed\u6709\u4e91\uff1a\u6c5f\u5c71\u6613\u6539\uff0c\u672c\u6027\u96be\u79fb\u3002\u53ea\u6015\u4ed6\u8fd8\u662f\u50cf\u4ee5\u524d\u90a3\u6837\uff01\u201d \u3000\u3000\u65f6\u5149\u5012\u6d41\uff0c\u56de\u5230\u4e09\u5341\u5e74\u524d\u3002 \u3000\u3000\u4e09\u5341\u5e74\u524d\uff0c\u5979\u8fd8\u662f\u4e2a\u5341\u4e8c\u4e09\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u5bb6\u4f4f\u71d5\u4eac\u5373\u4eca\u65e5\u7684\u5317\u4eac\u3002 \u3000\u3000\uff08\u6309\uff1a\u5317\u4eac\u5728\u516c\u5143\u4e8c\u5343\u5e74\u524d\u662f\u53f7\u79f0\u201c\u6218\u56fd\u4e03\u96c4\u201d\u4e4b\u4e00\u7684\u71d5\u56fd\u56fd\u90fd\uff0c\u5f53\u65f6\u7684\u6b63\u5f0f\u540d\u79f0\u53eb\u505a \u201c\u84df\u201d\u3002\u5510\u672b\uff0c\u6b8b\u5510\u4e94\u4ee3\u4e2d\u7684\u540e\u664b\u77f3\u656c\u5858\u5272\u71d5\u4e91\u5341\u516d\u5dde\u4e0e\u5951\u4e39\uff0c\u84df\u57ce\u5305\u62ec\u5728\u5185\u3001\u5951\u4e39\u4ee5\u84df\u57ce \u4e3a\u966a\u90fd\uff0c\u53f7\u79f0\u201c\u5357\u4eac\u201d\uff0c\u4e5f\u79f0\u71d5\u4eac\u3002 \u3000\u3000\u5e76\u6539\u56fd\u53f7\u4e3a\u201c\u8fbd\u201d\u3002\u91d1\u706d\u8fbd\u540e\uff0c\u6b63\u5f0f\u5efa\u90fd\u71d5\u4eac\uff0c\u53f7\u79f0\u201c\u4e2d\u90fd\u201d\u3002\uff09\u71d5\u4eac\u867d\u7136\u662f\u8fbd\u56fd\u7684\u966a \u90fd\uff0c\u4f46\u5c45\u6c11\u5374\u4ee5\u5973\u771f\u65cf\u6700\u591a\uff0c\u5176\u6b21\u662f\u6c49\u65cf\uff0c\u5951\u4e39\u4eba\u53cd\u800c\u8f83\u5c11\uff0c\u53ea\u80fd\u6392\u5230\u7b2c\u4e09\u3002\u5979\u8fd9\u4e00\u5bb6\u662f\u5973 \u771f\u65cf\u4e2d\u9887\u6709\u540d\u671b\u7684\u4e16\u5bb6\u3002 \u3000\u3000\u7236\u4eb2\u53ea\u6709\u5979\u4e00\u4e2a\u5973\u513f\uff0c\u4f46\u5979\u5374\u5e76\u4e0d\u662f\u5982\u5979\u5bf9\u4e08\u592b\u6240\u8bf4\u90a3\u6837\uff0c\u662f\u4e00\u4e2a\u8db3\u8ff9\u4e0d\u51fa\u95fa\u95e8\u7684\u6dd1 \u5973\u3002 \u3000\u3000\u5979\u7684\u7236\u4eb2\u5f88\u5e0c\u671b\u6709\u4e2a\u513f\u5b50\uff0c\u53ef\u60dc\u6ca1\u6709\u3002\u56e0\u6b64\u5979\u81ea\u5c0f\u5c31\u662f\u7ed9\u7236\u4eb2\u5f53\u4f5c\u7537\u5b69\u5b50\u629a\u517b\u7684\uff0c\u7a7f\u7537 \u5b69\u5b50\u7684\u8863\u670d\uff0c\u4e5f\u50cf\u7537\u5b69\u5b50\u4e00\u6837\uff0c\u559c\u6b22\u5728\u5916\u9762\u4e71\u8dd1\u3002 \u3000\u3000\u548c\u5979\u540c\u5728\u4e00\u6761\u80e1\u540c\u5c45\u4f4f\u7684\u6709\u4e00\u5bb6\u4eba\u5bb6\uff0c\u8fd9\u5bb6\u4eba\u5bb6\u6709\u4e2a\u5927\u82b1\u56ed\uff0c\u82b1\u56ed\u91cc\u79cd\u7684\u90fd\u662f\u7261\u4e39\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u5bb6\u4e2d\u6709\u6bcd\u5b50\u4e24\u4eba\uff0c\u6709\u4eba\u8bf4\u5973\u4e3b\u4eba\u662f\u5be1\u5987\uff0c\u4e5f\u6709\u4eba\u8bf4\u5979\u7684\u4e08\u592b\u5176\u5b9e\u8fd8\u5728\uff0c\u53ea\u662f\u5979\u5df2 \u7ecf\u88ab\u4e08\u592b\u629b\u5f03\u4e86\u3002\u5230\u5e95\u662f\u5be1\u5987\u8fd8\u662f\u5f03\u5987\uff0c\u771f\u76f8\u4e0d\u5f97\u800c\u77e5\u3002\u6ca1\u4eba\u89c1\u8fc7\u5979\u7684\u4e08\u592b\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u7684 \u4e08\u592b\u662f\u4ec0\u4e48\u8eab\u4efd\u3002\u77e5\u9053\u7684\u53ea\u662f\u5973\u4e3b\u4eba\u662f\u4ece\u6c5f\u5357\u6765\u7684\u6c49\u4eba\u3002\u7ed9\u5979\u6599\u7406\u7261\u4e39\u7684\u4e24\u4e2a\u82b1\u738b\u4e5f\u662f\u4ece\u6c5f \u5357\u7528\u91cd\u91d1\u8bf7\u6765\u7684\u540d\u5320\u3002\u8fd9\u5bb6\u4eba\u5bb6\u4ee5\u7261\u4e39\u51fa\u540d\u3002\u4e0d\u8fc7\u5979\u5374\u5e76\u4e0d\u662f\u88ab\u8fd9\u5bb6\u4eba\u5bb6\u7684\u7261\u4e39\u6240\u5438\u5f15\uff0c\u800c \u662f\u88ab\u90a3\u4e2a\u7537\u5b69\u5b50\u7684\u8427\u58f0\u6240\u5438\u5f15\u7684\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u6625\u5149\u660e\u5a9a\u7684\u65e5\u5b50\uff0c\u5979\u5728\u82b1\u56ed\u5916\u542c\u5230\u6709\u5982\u9ec4\u83ba\u51fa\u8c37\u7684\u8427\u58f0\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u8d70\u8fdb\u56ed\u5b50 \u53bb\u4e86\u3002\u56ed\u95e8\u662f\u865a\u63a9\u7684\u3002 \u3000\u3000\u90a3\u4e2a\u7537\u5b69\u5b50\u597d\u50cf\u6ca1\u6709\u770b\u89c1\u5979\uff0c\u4ecd\u7136\u81ea\u987e\u81ea\u5730\u5439\u8427\u3002 \u3000\u3000\u7261\u4e39\u76db\u5f00\uff0c\u8774\u8776\u5728\u82b1\u4e1b\u98de\u821e\u3002 \u3000\u3000\u90a3\u4e2a\u7537\u5b69\u5b50\u5439\u4e86\u4e00\u652f\u66f2\u5b50\uff0c\u5ffd\u7136\u6536\u8d77\u7389\u8427\uff0c\u968f\u624b\u5728\u5730\u4e0a\u6293\u8d77\u4e00\u628a\u6ce5\u6c99\u3002 \u3000\u3000\u5979\u6b63\u5728\u5947\u602a\uff0c\u5fc3\u60f3\uff0c\u4ed6\u5df2\u7ecf\u662f\u4e2a\u5927\u5b69\u5b50\u4e86\uff0c\u770b\u6765\u662f\u5e94\u8be5\u6bd4\u6211\u8fd8\u8981\u5927\u4e24\u5c81\u5427\uff0c\u600e\u4e48\u8fd8\u50cf\u51e0 \u5c81\u5927\u7684\u5c0f\u5b69\u5b50\u4e00\u6837\u559c\u6b22\u73a9\u6ce5\u6c99\uff1f\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u5927\u5b69\u5b50\u5df2\u662f\u628a\u968f\u624b\u6293\u8d77\u7684\u6ce5\u6c99\u5411\u6811\u4e0a\u6d12\u53bb\uff0c\u8774 \u8776\u7eb7\u7eb7\u5760\u5730\uff0c\u5979\u7981\u4e0d\u4f4f\u5c16\u58f0\u53eb\u4e86\u8d77\u6765\uff01 \u3000\u3000\u201c\u4ec0\u4e48\u4eba\u5728\u8fd9\u91cc\u5927\u547c\u5c0f\u53eb\uff0c\u7ed9\u6211\u51fa\u6765\uff01\u201d\u90a3\u5927\u5b69\u5b50\u7528\u7389\u8427\u6307\u7740\u5979\u8eb2\u85cf\u7684\u65b9\u5411\u3002\u201d \u3000\u3000\u5979\u77e5\u9053\u5df2\u7ecf\u7ed9\u5bf9\u65b9\u53d1\u73b0\uff0c\u96be\u4ee5\u8eb2\u85cf\uff0c\u7d22\u6027\u8dd1\u51fa\u6765\u9a82\u90a3\u5b69\u5b50\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u7c89\u8776\u513f\u91c7\u82b1\uff0c\u53c8\u788d\u4e86\u4f60\u4ec0\u4e48\u4e8b\uff1f\u4f60\u5e72\u561b\u628a\u5b83\u4eec\u6253\u6b7b\uff1f\u54fc\uff0c\u6211\u771f\u662f\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u50cf \u4f60\u8fd9\u6837\u6b8b\u5fcd\u7684\u91ce\u86ee\u4eba\uff01\u201d \u3000\u3000\u90a3\u5927\u5b69\u5b50\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\u8fd9\u4e9b\u8774\u8776\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5979\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5b83\u4eec\u4ece\u7a7a\u4e2d\u8dcc\u4e0b\u6765\uff0c\u5982\u4eca\u90fd\u4e00\u52a8\u4e5f\u4e0d\u4f1a\u52a8\u4e86\uff0c\u96be\u9053\u8fd8\u4e0d\u662f\u6b7b\u4e86 \u5417\uff1f\u201d \u3000\u3000\u90a3\u4e2a\u5b69\u5b50\u4f3c\u7b11\u975e\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\u6e05\u695a\uff0c\u6211\u53d8\u4e2a\u620f\u6cd5\u7ed9\u4f60\u77a7\u77a7\uff01\u201d \u3000\u3000\u4ed6\u628a\u624b\u4e00\u626c\uff0c\u4e00\u7728\u773c\u95f4\u53ea\u89c1\u90a3\u4e9b\u5979\u4ee5\u4e3a\u662f\u5df2\u7ecf\u201c\u6b7b\u4e86\u201d\u7684\u8774\u8776\uff0c\u53c8\u518d\u91cd\u65b0\u5c55\u7fc5\uff0c\u7eb7\u7eb7\u98de \u8d77\u3002 \u3000\u3000\u5979\u770b\u5f97\u5446\u4e86\uff0c\u4e0d\u7981\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u620f\u6cd5\u679c\u7136\u53d8\u5f97\u795e\u5947\uff01\u201d \u3000\u3000\u201c\u53ef\u7b11\u6211\u5f53\u65f6\u4ec0\u4e48\u4e5f\u4e0d\u61c2\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u771f\u7684\u662f\u53d8\u620f\u6cd5\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u5728\u5979\u5f53\u7136\u61c2\u4e86\uff0c\u8fd9\u662f\u4e00\u95e8\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u90a3\u4e9b\u8774\u8776\u53ea\u662f\u7ed9\u4ed6\u7684\u6ce5\u6c99\u6253\u6655\u7684\u3002\u4f46\u4ed6\u6d12\u51fa \u7684\u8fd9\u4e00\u628a\u6ce5\u6c99\uff0c\u7adf\u7136\u80fd\u591f\u540c\u65f6\u6253\u4e2d\u51e0\u5341\u53ea\u8774\u8776\uff0c\u7528\u7684\u529b\u5ea6\u53c8\u80fd\u591f\u8fd9\u6837\u6070\u5230\u597d\u5904\uff0c\u76f4\u5230\u73b0\u5728\uff0c \u5979\u8fd8\u662f\u89c9\u5f97\u7b80\u76f4\u662f\u532a\u5937\u6240\u601d\uff01\u5f04\u4e0d\u61c2\u8fd9\u6837\u795e\u5947\u7684\u6b66\u529f\u4ed6\u662f\u600e\u4e48\u7ec3\u6210\u529f\u7684\u3002 \u3000\u3000\u201c\u4ed6\u53ea\u6bd4\u6211\u5927\u4e09\u5c81\uff0c\u5f53\u65f6\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5341\u4e94\u516d\u5c81\u7684\u5927\u5b69\u5b50\u7f62\u4e86\uff0c\u5f53\u65f6\u4ed6\u5df2\u7ecf\u6709\u4e86\u8fd9\u6837\u795e\u5947 \u7684\u6b66\u529f\uff0c\u5982\u4eca\u53c8\u8fc7\u4e86\u4e09\u5341\u5e74\uff0c\u4ed6\u7684\u6b66\u529f\u66f4\u4e0d\u77e5\u5df1\u7ecf\u7ec3\u5230\u4ec0\u4e48\u5883\u754c\u4e86\u3002\u54c8\u5fc5\u56fe\u8fd9\u4e9b\u4eba\u600e\u80fd\u662f\u4ed6 \u7684\u5bf9\u624b\uff1f\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u6562\u518d\u60f3\u8ddf\u524d\u4e4b\u4e8b\u3002\u5728\u5979\u773c\u524d\u201c\u51fa\u73b0\u201d\u7684\u53c8\u662f\u5f53\u5e74\u90a3\u4e2a\u5927\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u90a3\u4e2a\u5927\u5b69\u5b50\u54c8\u54c8\u7b11\u8fc7\u4e4b\u540e\uff0c\u5ffd\u7136\u4e00\u628a\u6293\u4f4f\u4e86\u5979\u3002 \u3000\u3000\u5979\u5403\u4e86\u4e00\u60ca\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u6253\u4f60\u7684\u5c41\u80a1\uff01\u201d\u90a3\u5927\u5b69\u5b50\u677f\u7740\u8138\u5b54\u8bf4\u9053\u3002 \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u600e\u80fd\u8fd9\u6837\u6b3a\u8d1f\u6211\uff01\u201d\u5979\u5728\u6323\u624e\uff0c\u4f46\u5374\u600e\u80fd\u6323\u8131\u5bf9\u65b9\u7684\u638c\u63e1\u3002 \u3000\u3000\u90a3\u5927\u5b69\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5077\u5077\u8dd1\u8fdb\u6211\u7684\u82b1\u56ed\uff0c\u8fd8\u6562\u9a82\u6211\u3002\u54fc\uff0c\u4f60\u4e0d\u662f\u521a\u521a\u8bf4\u8fc7\u6211\u662f\u91ce\u86ee \u4eba\u5417\uff0c\u91ce\u86ee\u4eba\u7528\u7684\u5c31\u662f\u91ce\u86ee\u624b\u6bb5\uff0c\u5982\u4eca\u53ea\u6253\u4f60\u7684\u5c41\u80a1\uff0c\u5df2\u7ecf\u662f\u5bf9\u4f60\u624b\u4e0b\u7559\u60c5\u4e86\uff01\u201d\u4ed6\u628a\u53f3\u624b \u9ad8\u4e3e\u8d77\uff0c\u4f5c\u52bf\u771f\u7684\u8981\u6253\u5979\u5c41\u80a1\u3002\u5979\u5413\u5f97\u5c16\u58f0\u5927\u53eb\uff1a\u201c\u5c31\u7b97\u6211\u9a82\u9519\u4e86\u4f60\uff0c\u4f60\u4e5f\u4e0d\u80fd\u6253\u6211\u5c41 \u80a1\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6253\u4f60\u5c41\u80a1\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u3001\u6211\u3001\u6211\u2026\u2026\u201d\u5979\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u7c89\u8138\u513f\u90fd\u7ea2\u5f97\u50cf\u719f\u900f\u7684\u67ff\u5b50\u4e86\u3002 \u3000\u3000\u90a3\u5927\u5b69\u5b50\u5ffd\u5730\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5973\u5b69\u5b50\u662f\u4e0d\u662f\uff1f\u4e0d\u9519\uff0c\u5973\u5b69\u5b50\u662f\u4e0d\u80fd\u88ab\u4eba\u6253\u5c41\u80a1 \u7684\uff01\u201d\u628a\u5979\u653e\u5f00\u4e86\u3002 \u3000\u3000\u5979\u53c8\u5dee\u53c8\u607c\uff0c\u7ea2\u7740\u8138\u9a82\u9053\uff1a\u201c\u4f60\u574f\u900f\u4e86\uff01\u201c\u8f6c\u8eab\u5c31\u8d70\u3002 \u3000\u3000\u90a3\u5927\u5b69\u5b50\u5374\u4e0d\u8ba9\u5979\u8d70\uff0c\u62e6\u4f4f\u5979\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6253\u4f60\u4e5f\u4e0d\u9a82\u4f60\u3002 \u3000\u3000\u4f60\u8fd8\u8bf4\u6211\u73af\uff1f\u5582\uff0c\u5582\uff0c\u54b1\u4eec\u4ea4\u4e2a\u670b\u53cb\u597d\u4e0d\u597d\u3001\u6211\u53eb\u8036\u5f8b\u7384\u5143\uff0c\u6211\u77e5\u9053\u4f60\u662f\u9f50\u5bb6\u90a3\u4e2a\u91ce\u4e2b \u5934\u3002\u544a\u8bc9\u4f60\u5b9e\u8bdd\u5427\u3002\u6211\u65e9\u5df2\u6ce8\u610f\u4f60\u4e86\u3002\u4f60\u559c\u6b22\u626e\u7537\u5b69\u5b50\uff0c\u6211\u89c9\u5f97\u4f60\u5f88\u6709\u8da3\u3002\u563f\u3001\u563f\uff0c\u6211\u662f\u91ce \u86ee\u7684\uff0c\u4f60\u662f\u91ce\u4e2b\u5934\uff0c\u54b1\u4eec\u4e0d\u6b63\u597d\u662f\u4e00\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5979\u7ed9\u90a3\u5927\u5b69\u5b50\u63ed\u7a7f\uff0c\u5df2\u662f\u751a\u611f\u5c34\u5c2c\uff0c\u201c\u65e0\u8da3\u201d\u6781\u4e86\u3002\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u91ce\u4e2b\u5934\uff0c\u6211\u4e5f\u4e0d\u60f3 \u548c\u4f60\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u54e6\uff0c\u4f60\u4e0d\u60f3\u548c\u6211\u4ea4\u670b\u53cb\uff0c\u90a3\u4f60\u4e3a\u4f55\u4e0d\u8bf7\u81ea\u6765\uff1f\u201d \u3000\u3000\u5979\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u4e0d\u77e5\u600e\u6837\u56de\u7b54\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u4f5c\u72b6\u60f3\u4e86\u4e00\u60f3\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u4e00\u5b9a\u662f\u60f3\u5077\u6458\u6211\u5bb6\u7684\u7261\u4e39\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8ba9\u6211\u518d\u731c\u3002\u4f60\u662f\u5728\u6211\u5439\u8427\u7684\u65f6\u5019\u8fdb\u6765\u7684\uff0c\u2014\u2014\u5979\u8fd9\u624d\u77e5\u9053\uff0c\u539f \u6765\u4ed6\u65e9\u5df1\u53d1\u73b0\u4e86\u5979\u4e86\uff0c\u5979\u7684\u8138\u4e5f\u66f4\u52a0\u7ea2\u4e86\u3002\u2014\u2014\u6562\u60c5\u4f60\u559c\u6b22\u542c\u6211\u5439\u8427\uff1f\u201d \u3000\u3000\u5979\u867d\u7136\u6709\u65f6\u5019\u4e5f\u8bf4\u8c0e\uff0c\u4f46\u8fd9\u4e00\u6b21\u5374\u4e0d\u60f3\u8bf4\u8c0e\u4e86\uff0c\u5979\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f60\u548c\u6211\u505a\u670b\u53cb\uff0c\u6211\u6559\u4f60\u5439\u8427\u3002\u201d \u3000\u3000\u60ca\u614c\u5df1\u8fc7\uff0c\u5979\u4e5f\u89c9\u5f97\u8fd9\u5927\u5b69\u5b50\u201c\u6709\u8da3\u201d\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u60f3\u4f60\u6559\u6211\u53d8\u90a3\u5957\u620f\u6cd5\u3002\u201d\u8036\u5f8b \u7384\u5143\u7b11\u9053\uff1a\u201c\u90a3\u5957\u620f\u6cd5\u53ef\u4e0d\u662f\u5bb9\u6613\u5b66\u7684\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u53ef\u4ee5\u6559\u4f60\u53e6\u5916\u4e00\u4e9b\u6709\u8da3\u7684\u73a9\u610f\u3002\u6162\u6162\u518d \u6559\u4f60\u5b66\u90a3\u5957\u620f\u6cd5\u3002\u201d \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u4ed6\u4eec\u4ea4\u4e0a\u4e86\u670b\u53cb\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u679c\u7136\u6ca1\u6709\u98df\u8a00\uff0c\u4e0d\u4f46\u6559\u5979\u5439\u8427\uff0c\u8fd8\u6559\u5979\u8bfb\u6c49\u4eba\u7684\u8bd7\u4e66\uff0c\u6559\u5979\u4e00\u4e9b\u6bd4\u8f83\u5bb9\u6613\u5b66\u7684 \u6b66\u529f\uff0c\u6559\u5979\u6b23\u8d4f\u7261\u4e39\u7684\u201c\u5b66\u95ee\u201d\u3002\u4e0d\u77e5\u4e0d\u89c9\u5979\u4e5f\u517b\u6210\u4e86\u559c\u6b22\u7261\u4e39\u7684\u50fb\u597d\u4e86\u3002 \u3000\u3000\u5979\u4e5f\u66fe\u95ee\u8fc7\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u56ed\u5b50\u91cc\u53ea\u79cd\u7261\u4e39\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u7684\u7239\u7239\u6700\u559c\u6b22\u7261\u4e39\uff0c\u4ed6\u8bf4\u53ea\u6709\u7261\u4e39\u624d\u914d\u5f97\u4e0a\u4ed6\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u4f60\u7684\u7239\u7239\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u4ed6\uff0c\u4ed6\u559c\u6b22\u7261\u4e39\uff0c\u6211\u53ea\u662f\u4ece\u5988\u5988\u53e3\u4e2d\u77e5\u9053\u7684\u3002\u5988\u5988\u4e5f\u4f3c\u4e4e\u4e0d \u77e5\u9047\u4ed6\u662f\u4ec0\u4e48\u8eab\u4efd\u3002\u201d\u201c\u6211\u60f3\u4f60\u7684\u7239\u7239\u4e00\u5b9a\u662f\u4e2a\u5bcc\u8d35\u53cc\u5168\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4f60\u8fd9\u6837\u60f3\uff1f\u201d \u3000\u3000\u201c\u7261\u4e39\uff0c\u82b1\u4e4b\u5bcc\u8d35\u8005\u4e5f\u3002\u524d\u4e24\u5929\u6211\u5ff5\u8fc7\u7684\u4e00\u7bc7\u6587\u7ae0\u5c31\u6709\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0c\u4f60\u7239\u7239\u559c\u6b22\u7261\u4e39\uff0c \u56e0\u6b64\uff0c\u6211\u731c\u4ed6\u4e00\u5b9a\u662f\u5bcc\u8d35\u4e2d\u4eba\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9ed8\u7136\u4e0d\u8bed\uff0c\u534a\u664c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u5acc\u5f03\u7261\u4e39\u4fd7\u6c14\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5acc\u3002\u56e0\u4e3a\u4f60\u4e5f\u662f\u7231\u7261\u4e39\u7684\u4eba\uff0c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u4fd7\u6c14\u3002\u201d\u201c\u591a\u8c22\u4f60\u56e0\u4e3a\u6211\u8fd9\u4e2a\u4eba\u800c\u559c\u6b22\u7261 \u4e39\u3002\u201d\u8036\u5f8b\u7384\u5143\u7b11\u4e86\uff0c\u5979\u4ece\u672a\u89c1\u8fc7\u4ed6\u8fd9\u6837\u5f00\u5fc3\u3002 \u3000\u3000\u201c\u5176\u5b9e\u7261\u4e39\u4e5f\u662f\u82b1\u4e2d\u54c1\u79cd\u6700\u591a\u7684\u4e00\u79cd\u82b1\uff0c\u8bf4\u7261\u4e39\u4fd7\u6c14\u7684\u4eba\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u89c1\u8fc7\u540d\u79cd \u7261\u4e39\u7684\u7f18\u6545\u3002\u6b63\u5982\u4ece\u6ca1\u89c1\u8fc7\u7f8e\u4eba\u7684\u4eba\uff0c\u5c31\u4fe1\u53e3\u96cc\u9ec4\uff0c\u8bf4\u5929\u4e0b\u5973\u4eba\u90fd\u662f\u5eb8\u8102\u4fd7\u7c89\u4e00\u6837\u3002\u8fd9\u4e9b\u4eba \u53c8\u600e\u77e5\u6709\u897f\u5b50\u738b\u5af1\u4e4b\u7f8e\uff1f\u201d\u8036\u5f8b\u7384\u5143\u8bf4\u9053\u3002\u5979\u4e5f\u7b11\u4e86\uff0c\u201c\u6211\u6ca1\u6709\u4f60\u8fd9\u6837\u806a\u660e\uff0c\u61c2\u5f97\u62ff\u82b1\u6765\u6bd4 \u5973\u4eba\u3002\u6211\u53ea\u89c9\u5f97\u7261\u4e39\u82b1\u5f00\u5f97\u597d\u770b\uff0c\u6211\u5c31\u559c\u6b22\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u770b\u7740\u5979\u7b11\u3002 \u3000\u3000\u5979\u4e0d\u7b11\u4e86\uff0c\u6545\u610f\u677f\u7740\u8138\u5b54\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff0c\u4f60\u4ee5\u4e3a\u6211\u53ea\u662f\u56e0\u4e3a\u4f60\u559c\u6b22\u7261\u4e39\uff0c\u6211\u624d\u559c\u6b22\u7684 \u5417\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u6709\u4e00\u534a\u539f\u56e0\u662f\u4e3a\u4e86\u6211\uff0c\u6211\u5df2\u7ecf\u5f00\u5fc3\u6b7b\u4e86\uff01\u201d\u8036\u5f8b\u7384\u5143\u8bf4\u9053\u3002 \u3000\u3000\u201c\u4e00\u534a\u4e5f\u6ca1\u6709\uff01\u201d \u3000\u3000\u201c\u771f\u7684\u5417\uff1f\u201d\u8036\u5f8b\u7384\u5143\u5ffd\u7136\u9760\u8fd1\u5979\uff0c\u76ef\u7740\u5979\u53d1\u95ee\uff0c\u773c\u775b\u90fd\u51e0\u4e4e\u8d34\u5230\u5979\u7684\u8138\u4e0a\u3002 \u3000\u3000\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5979\u8d76\u5fd9\u63a8\u5f00\u4ed6\u3002 \u3000\u3000\u201c\u6211\u8981\u770b\u4f60\u5fc3\u91cc\u7684\u90a3\u53e5\u8bdd\uff01\u201d\u4ed6\u7684\u4e00\u53cc\u773c\u775b\uff0c\u5f53\u771f\u5c31\u597d\u4f3c\u53ef\u4ee5\u770b\u7a7f\u5979\u7684\u5185\u5fc3\u4f3c\u7684\u3002 \u3000\u3000\u5979\u602a\u53eb\u8eb2\u907f\uff0c\u8036\u5f8b\u7384\u5143\u5982\u5f71\u968f\u5f62\u5730\u8ffd\u5979\u3002 \u3000\u3000\u4e24\u5c0f\u65e0\u731c\uff0c\u8fd9\u4e9b\u751c\u871c\u7684\u56de\u5fc6\u5982\u4eca\u5df2\u662f\u5982\u68a6\u5982\u70df\u4e86\u3002 \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u90a3\u65f6\u6211\u53ea\u731c\u5f97\u5230\u4ed6\u7684\u7236\u4eb2\u662f\u5bcc\u8d35\u4e2d\u4eba\uff0c\u5374\u600e\u77e5\u4ed6\u7684\u7236\u4eb2\u7adf\u7136 \u662f\u8d35\u4e3a\u4e00\u56fd\u4e4b\u4e3b\u7684\u8fbd\u56fd\u7687\u5e1d\u3002\u201d \u3000\u3000\u5979\u77e5\u9053\u771f\u76f8\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u5728\u5979\u548c\u5f8b\u7384\u5143\u7ed3\u4ea4\u4e09\u5e74\u4e4b\u540e\u7684\u4e8b\u60c5\u4e86\u3002 \u3000\u3000\u4e09\u5e74\u7684\u65f6\u95f4\u8bf4\u957f\u4e0d\u957f\uff0c\u8bf4\u77ed\u4e0d\u77ed\uff0c\u4f46\u5df2\u8db3\u4ee5\u4ee4\u5979\u4ece\u4e00\u4e2a\u201c\u9ec4\u6bdb\u4e2b\u5934\u201d \u3000\u3000\u53d8\u6210\u4e00\u4e2a\u60c5\u7aa6\u521d\u5f00\u7684\u201c\u5927\u59d1\u5a18\u201d\u4e86\u3002 \u3000\u3000\u5341\u516d\u5c81\uff0c\u8fd9\u4e5f\u6b63\u662f\u8bf4\u5927\u4e0d\u5927\uff0c\u8bf4\u5c0f\u4e0d\u5c0f\u7684\u5e74\u9f84\uff1b\u5bf9\u7231\u60c5\u8bf4\u61c2\u4e0d\u61c2\uff0c\u8bf4\u4e0d\u61c2\u53c8\u61c2\u7684\u5e74\u9f84\u3002 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u5979\u6b63\u5728\u51c6\u5907\u5378\u88c5\u7761\u89c9\u7684\u65f6\u5019\uff0c\u7a97\u5b50\u5ffd\u7136\u65e0\u98ce\u81ea\u5f00\uff0c\u8036\u5f8b\u7384\u5143\u51fa\u73b0\u5728\u5979\u7684\u9762 \u524d\uff0c\u628a\u5979\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u201c\u8fd9\u4e48\u665a\u4e86\uff0c\u4f60\u8fd8\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5979\u6015\u7236\u4eb2\u542c\u89c1\uff0c\u5c0f\u58f0\u8bf4\u7b54\u3002 \u3000\u3000\u201c\u90a3\u4e24\u682a\u9b4f\u7d2b\u3001\u59da\u9ec4\u90fd\u5df2\u5f00\u4e86\uff0c\u6211\u662f\u8bf7\u4f60\u8fc7\u53bb\u8d4f\u82b1\u7684\u3002\u8fd9\u4e24\u682a\u4e0a\u54c1\u7261\u4e39\uff0c\u6700\u9002\u5b9c\u5728\u6708\u4e0b \u6b23\u8d4f\u3002\u201d\u8036\u5f8b\u7384\u5143\u8bf4\u9053\u3002\u8fc7\u53bb\uff0c\u5979\u4e0e\u8036\u5f8b\u7384\u5143\u540c\u6e38\uff0c\u603b\u662f\u5728\u65e5\u95f4\u7684\uff0c\u665a\u4e0a\u5c31\u5f88\u5c11\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u867d\u8bf4\u7236\u4eb2\u4e00\u5411\u90fd\u662f\u4e0d\u5927\u7ba1\u675f\u5979\u7684\uff0c\u4f46\u5979\u603b\u662f\u5973\u5b69\u5b50\u554a\uff01 \u3000\u3000\u800c\u4eca\u8036\u5f8b\u7384\u5143\u7adf\u7136\u6df1\u591c\u6765\u8bf7\u5979\u53bb\u8d4f\u7261\u4e39\uff0c\u8fd9\u4e5f\u5b9e\u5728\u662f\u592a\u8fc7\u51fa\u4e4e\u5979\u7684\u610f\u6599\u4e4b\u5916\u4e86\u3002\u5c3d\u7ba1\u5979 \u6709\u70b9\u4e0d\u7f81\u7684\u6027\u683c\uff0c\u4f46\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u5979\u8fd8\u662f\u89c9\u5f97\u4f3c\u4e4e\u6709\u70b9\u201c\u8352\u8bde\u4e0d\u7ecf\u201d\u3002 \u3000\u3000\u6df1\u591c\uff0c\u966a\u4e00\u4e2a\u7537\u5b69\u5b50\u53bb\u8d4f\u7261\u4e39\uff0c\u8981\u662f\u7ed9\u7239\u7239\u77e5\u9053\u2014\u2014\u8036\u5f8b\u7384\u5143\u4f3c\u4e4e\u77e5\u9053\u5979\u7684\u5fc3\u601d\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4f60\u7239\u7239\u5df2\u7ecf\u719f\u7761\u4e86\uff0c\u6211\u6562\u62c5\u4fdd\uff0c\u4ed6\u8fd9\u4e00\u89c9\uff0c\u4e00\u5b9a\u8981\u7761\u5230\u660e\u5929\u5929\u4eae\u624d\u80fd\u9192 \u6765\u3002\u201d\u5979\u77e5\u9053\u8036\u5f8b\u7384\u5143\u201c\u795e\u901a\u5e7f\u5927\u201d\uff0c\u4e5f\u76f8\u4fe1\u4ed6\u6709\u8fd9\u79cd\u53ef\u4ee5\u53eb\u5979\u7684\u7239\u7239\u4e00\u89c9\u7761\u5230\u5927\u5929\u5149\u7684\u672c \u9886\uff0c\u4f46\u5979\u8fd8\u662f\u4e0d\u80fd\u4e0d\u6709\u987e\u8651\u3002 \u3000\u3000\u201c\u4e00\u5b9a\u8981\u5728\u4eca\u5929\u665a\u4e0a\u5417\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u665a\u4e0a\u672a\u5fc5\u8fd8\u6709\u8fd9\u4e48\u597d\u7684\u6708\u5149\u3002\u201d \u3000\u3000\u201c\u660e\u5929\u4e5f\u4e0d\u884c\u5417\uff1f\u6211\u7684\u610f\u601d\u662f\u6700\u597d\u4e0d\u8981\u5728\u665a\u4e0a\u3002\u767d\u5929\u8d4f\u82b1\uff0c\u867d\u7136\u8bf7\u8c03\u7a0d\u5dee\uff0c\u4f46\u540d\u79cd\u7261\u4e39 \u603b\u8fd8\u662f\u540d\u79cd\u7261\u4e39\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u662f\u559c\u6b22\u8ffd\u6c42\u5b8c\u7f8e\u7684\u5883\u754c\u7684\uff0c\u9664\u975e\u529e\u4e0d\u5230\uff0c\u90a3\u4e2a\u53e6\u5f53\u522b\u8bba\u3002 \u3000\u3000\u4f55\u51b5\u5929\u6709\u4e0d\u6d4b\u4e4b\u98ce\u4e91\uff0c\u8bf4\u4e0d\u5b9a\u660e\u5929\u7a81\u7136\u6765\u4e86\u4e00\u573a\u98ce\u66b4\uff0c\u628a\u7261\u4e39\u90fd\u6467\u6b8b\u4e86\u5462\uff1f\u201d\u8036\u5f8b\u7384\u5143 \u9eef\u7136\u8bf4\u9053\u3002 \u3000\u3000\u623f\u95f4\u91cc\u6ca1\u6709\u70b9\u706f\uff0c\u53ea\u6709\u6726\u80e7\u7684\u6708\u5149\u900f\u8fc7\u7a97\u6237\u3002\u4f46\u4ece\u8036\u5f8b\u7384\u5143\u90a3\u4e24\u9897\u6f06\u9ed1\u53d1\u4eae\u7684\u773c\u73e0\uff0c\u770b \u5f97\u51fa\u4ed6\u662f\u5145\u6ee1\u6025\u5207\u7684\u671f\u5f85\u7684\u3002 \u3000\u3000\u5979\u672c\u6765\u4e0d\u60f3\u53bb\u4e86\uff0c\u7ec8\u4e8e\u8fd8\u662f\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u682a\u540d\u79cd\u7261\u4e39\uff0c\u679c\u7136\u5f00\u5f97\u975e\u5e38\u597d\u770b\uff0c\u5728\u6708\u5149\u4e0b\u8d4f\u82b1\uff0c\u66f4\u662f\u53e6\u6709\u4e00\u79cd\u795e\u79d8\u7684\u7f8e\u611f\u3002\u4f46\u8036 \u5f8b\u7384\u5143\u5374\u4f3c\u4e4e\u5e76\u4e0d\u662f\u600e\u4e48\u5f00\u5fc3\uff0c\u76f8\u53cd\uff0c\u8fd8\u4f3c\u4e4e\u5e26\u6709\u51e0\u5206\u5fe7\u90c1\u3002 \u3000\u3000\u201c\u4f60\u597d\u50cf\u6709\u70b9\u5fc3\u4e8b\u3002\u662f\u5417\uff1f\u201d\u5979\u95ee\u3002 \u3000\u3000\u201c\u6ca1\u3001\u6ca1\u4ec0\u4e48\u3002\u6211\u5439\u8427\u7ed9\u4f60\u542c\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\u5440\uff0c\u6211\u6b63\u662f\u6700\u559c\u6b22\u542c\u4f60\u5439\u8427\uff01\u201d \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u5f00\u59cb\u6709\u4e86\u7b11\u5bb9\uff0c\u8bf4\u9053\u201c\u662f\u5417\uff1f\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u8bf7\u4f60\u6765\u6211\u5bb6\uff0c\u56fa\u7136\u662f\u4e3a\u4e86\u8d4f\u82b1\uff0c\u4f46 \u4e5f\u662f\u4e3a\u4e86\u60f3\u8981\u591a\u5f97\u4e00\u4e2a\u673a\u4f1a\uff0c\u5439\u8427\u7ed9\u4f60\u542c\u7684\u3002\u201d \u3000\u3000\u5439\u8427\u4e5f\u8981\u8bb2\u201c\u673a\u4f1a\u201d\u5417\uff1f\u8fd9\u4e09\u5e74\u6765\uff0c\u5979\u51e0\u4e4e\u6bcf\u5929\u90fd\u542c\u89c1\u4ed6\u7684\u8427\u58f0\u7684\u3002 \u3000\u3000\u5979\u4e0d\u61c2\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\uff0c\uff08\u4e0d\u8fc7\uff0c\u4e5f\u53ea\u662f\u9694\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u5929\u5979\u5c31\u61c2\u4e86\u3002 \u3000\u3000\uff09\u4f46\u4e3a\u4e86\u60f3\u65e9\u4e00\u70b9\u542c\u5230\u4ed6\u90a3\u7f8e\u5999\u7684\u8427\u58f0\uff0c\u5979\u4e5f\u6ca1\u6709\u518d\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u6211\u7ed9\u4f60\u5439\u4e00\u9619\u4ece\u5357\u671d\u6d41\u4f20\u5230\u5317\u65b9\u7684\u65b0\u8bcd\uff0c\u8bcd\u5bc4\u9e67\u9e2a\u5929\uff0c\u66f2\u5b50\u662f\u6211\u81ea\u5df1\u8c31\u7684\u3002\u201d \u3000\u3000\u7389\u5b87\u65e0\u57a0\uff0c\u94f6\u6cb3\u768e\u6d01\uff0c\u6708\u5149\u4e0b\uff0c\u7261\u4e39\u65c1\uff0c\u4ed6\u5f00\u59cb\u5439\u8d77\u7389\u8427\u6765\u4e86\u3002 \u3000\u3000\u6708\u4e0b\u82b1\u524d\uff0c\u542c\u81ea\u5df1\u559c\u6b22\u7684\u4eba\u5439\u8427\uff0c\u5bf9\u5979\u6765\u8bf4\uff0c\u4e5f\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002\u672c\u6765\u5e94\u662f\u8d4f\u5fc3\u4e50\u4e8b\uff0c\u4f46\u53ef \u60dc\u4ed6\u7684\u8427\u58f0\u4e5f\u50cf\u4ed6\u7684\u5fc3\u60c5\u4e00\u6837\uff0c\u5e26\u6709\u51e0\u5206\u5fe7\u90c1\u3002 \u3000\u3000\u8fd9\u4e00\u65b0\u8bcd\uff0c\u5979\u4e5f\u66fe\u8bfb\u8fc7\uff0c\u5f53\u4e0b\u63a5\u7740\u8282\u62cd\uff0c\u6f2b\u58f0\u541f\u548f\uff1a\u6d1b\u6d66\u98ce\u5149\u70c2\u6f2b\u65f6\uff0c\u5343\u91d1\u5f00\u5bb4\u9189\u4e3a \u671f\u3002 \u3000\u3000\u82b1\u65b9\u8457\u96e8\u72b9\u542b\u7b11\uff0c\u8776\u4e0d\u7981\u5bd2\u603b\u662f\u75f4\u3002 \u3000\u3000\u6a80\u6655\u5410\uff0c\u7389\u534e\u6ecb\uff0c\u4e0d\u968f\u6843\u674e\u7adf\u6625\u83f2\u3002 \u3000\u3000\u4e1c\u541b\u81ea\u6709\u56de\u5929\u529b\uff0c\u770b\u628a\u82b1\u679d\u5e26\u6708\u5f52\u3002 \u3000\u3000\u8427\u58f0\u521d\u8d77\uff0c\u5012\u662f\u76f8\u5f53\u8f7b\u5feb\uff0c\u5f53\u771f\u597d\u50cf\u5e26\u6765\u4e86\u4e00\u7247\u660e\u5a9a\u7684\u6625\u5149\u3002\u4f46\u6e10\u6e10\u5c31\u6709\u4e86\u51c4\u51c9\u7684\u610f\u5473 \u4e86\uff0c\u4e0d\u8fc7\u5728\u51c4\u51c9\u4e4b\u4e2d\uff0c\u4e5f\u8fd8\u662f\u6709\u7740\u201c\u671f\u5f85\u201d\u7684\u3002 \u3000\u3000\u5509\uff0c\u4e1c\u541b\u81ea\u6709\u56de\u5929\u529b\uff0c\u770b\u628a\u82b1\u679d\u5e26\u6708\u5f52\u3002\u201c\u4e1c\u541b\u201d\u662f\u8c01\uff0c\u201c\u82b1\u679d\u201d\u662f\u8c01\uff1f\u5979\u90a3\u65f6\u5e74\u7eaa\u592a \u5c0f\uff0c\u8fd8\u672a\u771f\u6b63\u61c2\u5f97\u8fd9\u4e24\u53e5\u8bdd\u7684\u542b\u4e49\u3002\u4f46\u4e5f\u9690\u9690\u611f\u89c9\u5f97\u5230\uff0c\u4ed6\u662f\u501f\u8bcd\u5bd3\u610f\uff0c\u6697\u793a\u53ef\u80fd\u4f1a\u6709\u4ec0\u4e48 \u98ce\u6ce2\u6765\u5230\u4e86\u3002 \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u544a\u8bc9\u6211\u5462\uff1f\u201c\u5979\u7981\u4e0d\u4f4f\u518d\u6b21\u8ffd\u95ee\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4f3c\u7b11\u975e\u7b11\u7684\u671b\u7740\u5979\u8bf4\u9053\uff1a\u201c\u4f60\u559c\u6b22\u548c\u6211\u5728\u4e00\u8d77\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53e5\u8bdd\u4f60\u597d\u50cf\u95ee\u8fc7\u6211\u4e0d\u77e5\u591a\u5c11\u6b21\u4e86\uff0c\u6211\u4e5f\u7b54\u8fc7\u4f60\u4e0d\u53ea\u4e00\u6b21\u4e86\u3002\u201d\u4e0d\u7b54\u81ea\u7b54\u3002\u201c\u73b0\u5728\u559c \u6b22\uff0c\u5c06\u6765\u4e5f\u559c\u6b22\u5417\uff1f\u56e0\u4e3a\u6211\u8981\u77e5\u9053\u7684\u4e0d\u4ec5\u662f\u73b0\u5728\uff0c\u8fd8\u6709\u5c06\u6765\u3002\u201d\u5341\u516d\u5c81\uff0c\u8fd9\u6b63\u662f\u5bf9\u7231\u60c5\u8bf4\u61c2 \u4e0d\u61c2\uff0c\u8bf4\u4e0d\u61c2\u53c8\u61c2\u7684\u5e74\u9f84\u3002\u4f46\u8fd9\u4e24\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u5979\u603b\u8fd8\u662f\u61c2\u7684\u3002 \u3000\u3000\u5979\u4f4e\u4e0b\u4e86\u5934\uff0c\u7c89\u8138\u5730\u7ea2\u5f97\u7b80\u76f4\u50cf\u90a3\u682a\u540d\u79cd\u7684\u7261\u4e39\u201c\u79e6\u7ea2\u201d\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4f60\u95ee\u6211\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u6211\u662f\u6709\u7740\u4e00\u6869\u5fc3\u4e8b\u3002\u5fc3\u4e8b\u5c31\u662f\uff0c\u53ea\u76fc\u80fd\u591f\u548c\u4f60\u6c38\u8fdc \u5728\u4e00\u8d77\uff01\u201d \u3000\u3000\u5979\u7684\u5934\u4fef\u5f97\u66f4\u4f4e\uff0c\u51e0\u4e4e\u542c\u5f97\u89c1\u81ea\u5df1\u7684\u5fc3\u8df3\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f46\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u98ce\u6ce2\uff0c\u54b1\u4eec\u6682\u65f6\u5206 \u624b\u7684\u8bdd\uff0c\u6211\u60f3\u95ee\u4f60\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u7b49\u6211\u56de\u6765\uff1f\u201d \u3000\u3000\u5979\u65e0\u6cd5\u6297\u62d2\u4ed6\u90a3\u79cd\u5145\u6ee1\u671f\u5f85\u7684\u76ee\u5149\uff0c\u5979\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f46\u6211\u8bf4\u7684\u2018\u6682\u65f6\u2019\u53ef\u80fd\u662f\u534a\u5e74\uff0c\u53ef\u80fd\u662f\u4e00\u5e74\uff0c\u4e5f\u53ef\u80fd\u662f\u4e09\u5e74\u4e94\u8f7d\uff0c\u751a\u81f3\u5341\u5e74\u516b\u5e74 \u7684\uff01\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4f60\u53bb\u591a\u4e45\uff0c\u603b\u4e4b\u6211\u7b49\u4f60\u56de\u6765\u201d\u5979\u7684\u58f0\u97f3\u50cf\u868a\u53eb\uff0c\u4f46\u8036\u5f8b\u7384\u5143\u5df2\u7ecf\u542c\u5f97\u6e05\u6e05\u695a\u695a \u4e86\u3002 \u3000\u3000\u4ed6\u5927\u559c\u5982\u72c2\uff0c\u7a81\u7136\u6765\u4e86\u4e00\u4e2a\u5979\u7adf\u60f3\u4e0d\u5230\u7684\u52a8\u4f5c\uff0c\u5c06\u5979\u62e5\u5165\u6000\u53d6\uff0c\u543b\u4e86\u5979\u7684\u988a\uff0c\u543b\u4e86\u5979\u7684 \u7751\uff0c\u543b\u4e86\u5979\u7684\u5507\uff01\u4e00\u4e2a\u6bd4\u4e00\u4e2a\u70ed\u70c8\uff0c\u543b\u5f97\u5979\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u4e86\uff01 \u3000\u3000\u8fd9\u4e09\u5e74\u6765\uff0c\u5979\u867d\u7136\u51e0\u4e4e\u5929\u5929\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u4f46\u53ef\u8fd8\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u5c31\u662f\u7231\u60c5\u7684\u3002 \u3000\u3000\u7231\u60c5\u7a81\u7136\u6765\u4e86\uff0c\u6765\u5f97\u6709\u5982\u72c2\u98ce\u9aa4\u96e8\uff01\uff08\u5509\uff0c\u60f3\u4e0d\u5230\u6765\u5f97\u5feb\uff0c\u53bb\u5f97\u4e5f\u5feb\uff01\uff09\u8fd9\u8fd8\u662f\u5979\u7b2c\u4e00 \u6b21\u5c1d\u5230\u7684\u521d\u543b\uff0c\u521d\u543b\u5c31\u50cf\u8fd9\u6837\u70ed\u70c8\uff01\uff08\u5509\uff0c\u5979\u53c8\u600e\u60f3\u5f97\u5230\u5979\u5c1d\u5230\u7684\u7adf\u662f\u7231\u60c5\u7684\u82e6\u676f\uff0c\u4e00\u543b\u4e4b \u540e\uff0c\u5c31\u662f\u751f\u79bb\uff01\uff09\u5979\u7684\u5fc3\u5728\u72c2\u8df3\uff0c\u4e0d\u77e5\u662f\u559c\u6b22\uff0c\u8fd8\u662f\u5bb3\u6015\u3002\uff0d\uff0d\uff0d\uff0d\u5bb3\u6015\u4ed6\u7684\u72c2\u70ed\uff0c\u5bb3\u6015\u518d \u7559\u4e0b\u53bb\uff0c\u4e0d\u77e5\u4ed6\u8fd8\u4f1a\u505a\u51fa\u4ec0\u4e48\u4ee4\u5979\u5fc3\u8df3\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u6708\u5f71\u5df1\u897f\u659c\uff0c\u5979\u63a8\u5f00\u4e86\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u4f60\u8d70\u5427\uff01\u4f60\u8d70\u4e86\u3002\u6211\u4e5f\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u53ef\u60dc\u5979\u5f53\u65f6\u5fc3\u614c\u610f\u4e71\uff0c\u672a\u80fd\u9886\u4f1a\u4ed6\u7684\u8bdd\u4e2d\u4e4b\u610f\u3002\u7b2c\u4e8c\u5929\u5979\u624d\u77e5\u9053\uff0c\u4ed6\u662f\u771f\u7684\u201c\u8d70\u201d\u4e86\u3002 \u3000\u3000\u5979\u662f\u5728\u5c06\u8fd1\u5929\u660e\u7684\u65f6\u5019\uff0c\u65b9\u59cb\u6726\u80e7\u5165\u68a6\u7684\u3002 \u3000\u3000\u5979\u7236\u4eb2\u4eca\u5929\u8d77\u5e8a\u867d\u7136\u5df2\u662f\u6bd4\u8f83\u5e73\u65f6\u8fdf\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f46\u8fd8\u662f\u9192\u5f97\u6bd4\u5979\u65e9\u3002 \u3000\u3000\u5979\u662f\u7ed9\u7236\u4eb2\u5524\u9192\u7684\u3002 \u3000\u3000\u201c\u6628\u5929\u665a\u4e0a\uff0c\u4f60\u505a\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u7236\u4eb2\u4e00\u5f00\u53e3\u5c31\u8fd9\u6837\u95ee\u3002 \u3000\u3000\u5979\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u3001\u6ca1\uff0c\u6211\u6ca1\u505a\u4ec0\u4e48\u5440\uff01\u201d\u7236\u4eb2\u9053\uff1a\u201c\u90a3\u4e3a\u4f55\u7761\u5230\u65e5\u4e0a\u4e09\u7aff\u8fd8\u672a \u8d77\u6765\uff0c\u5e73\u65f6\u4f60\u6bd4\u6211\u8d77\u5f97\u65e9\u7684\u3002\u201d \u3000\u3000\u542c\u89c1\u7236\u4eb2\u8fd9\u6837\u8bf4\uff0c\u5979\u65b9\u59cb\u653e\u4e0b\u5fc3\u4e0a\u4e00\u5757\u77f3\u5934\uff0c\u201c\u539f\u6765\u7239\u7239\u5e76\u4e0d\u77e5\u9053\u6628\u665a\u6211\u53bb\u4e86\u4ed6\u7684\u5bb6 \u91cc\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u4f1a\u7761\u5f97\u8fd9\u6837\u719f\u3002\u7239\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u5979\u5f00\u59cb\u6ce8\u610f\u5230\u7236\u4eb2\u7684\u9762\u8272\u597d \u50cf\u548c\u5e73\u65f6\u6709\u70b9\u4e24\u6837\u4e86\u3002\u7236\u4eb2\u8bf4\u9053\uff1a\u201c\u6709\u3002\u800c\u4e14\u8fd9\u4ef6\u4e8b\u548c\u4f60\u4e5f\u591a\u5c11\u6709\u70b9\u5173\u7cfb\u7684\u3002\u201d \u3000\u3000\u5979\u4e0d\u7981\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u4ec0\u4e48\u4e8b\u548c\u6211\u6709\u5173\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f4d\u8036\u5f8b\u5927\u5a18\u7684\u513f\u5b50\uff0c\u4ed6\u662f\u53eb\u8036\u5f8b\u7384\u5143\u5427\uff0c\u4f60\u548c\u4ed6\u5f88\u8981\u597d\uff0c\u51e0\u4e4e\u662f\u5929\u5929\u5728\u4e00\u8d77\u7684\uff0c\u662f \u5417\uff1f\u201d \u3000\u3000\u5979\u7ea2\u7740\u8138\u9053\uff1a\u201c\u6211\u559c\u6b22\u4ed6\u5bb6\u91cc\u7684\u7261\u4e39\uff0c\u4ed6\u53c8\u5f88\u4f1a\u5439\u8427\uff0c\u56e0\u6b64\u6211\u662f\u65f6\u5e38\u53bb\u4ed6\u5bb6\u91cc\u7684\u3002\u4ed6\u4e0d \u4f46\u6559\u6211\u5439\u8427\uff0c\u8fd8\u6559\u6211\u5ff5\u8bd7\u5462\u3002\u7239\uff0c\u6211\u8bb0\u5f97\u6211\u4e5f\u66fe\u544a\u8bc9\u8fc7\u4f60\u7684\uff0c\u4f60\u4e5f\u5e76\u6ca1\u6709\u8bf4\u662f\u4e0d\u80fd\u53bb\u627e\u4ed6\u7684 \u5440\uff01\u7236\u4eb2\u6446\u4e86\u6446\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u7981\u6b62\u4f60\u548c\u4ed6\u6765\u5f80\u3002\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u5979\u5446\u4e86\u4e00\u5446\uff0c\u201c\u4e0d\u77e5\u9053\u3002\u4ed6\u3001\u4ed6\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u8fd9\u4e48\u8981\u597d\uff0c\u4ed6\u4ece\u6765\u6ca1\u6709\u544a\u8bc9\u8fc7\u4f60\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u771f\u7684\u6ca1\u6709\uff01\u201d \u3000\u3000\u7236\u4eb2\u7b11\u9053\uff1a\u201c\u4f60\u522b\u614c\u5f20\uff0c\u6211\u5f53\u7136\u76f8\u4fe1\u4f60\u662f\u4e0d\u4f1a\u5bf9\u6211\u8bf4\u8c0e\u7684\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u597d\u5728\u4f60\u4ee5\u5f80 \u4e00\u76f4\u662f\u626e\u4f5c\u7537\u5b69\u5b50\u548c\u4ed6\u6e38\u73a9\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u6ce8\u610f\u4f60\u4eec\u5b69\u5b50\u7684\u4e8b\u60c5\u3002\u4ece\u4eca\u5929\u8d77\uff0c\u6211\u8981\u4f60\u6062\u590d\u95fa\u5973 \u7684\u8eab\u4efd\uff0c\u4e0d\u51c6\u4f60\u5230\u5916\u9762\u4e71\u8dd1\u4e86\u3002\u8fd8\u6709\uff0c\u4f60\u8fd9\u4f4d\u5c0f\u670b\u53cb\uff0c\u4f60\u6700\u597d\u5fd8\u8bb0\u4e86\u4ed6\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5979\u66f4\u52a0\u5403\u60ca\u4e86\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u6709\u4e00\u4e2a\u7279\u6b8a\u7684\u8eab\u4efd\uff01\u201d \u3000\u3000\u201c\u7a76\u7adf\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u8fbd\u56fd\u7684\u738b\u5b50\u2014\u2014\u201d\u5979\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u738b\u5b50\uff0c\u8fd9\u600e\u4e48\u53ef\u80fd \u2014\u2014\u201d \u3000\u3000\u201c\u662f\u771f\u7684\u3002\u4ed6\u4eec\u6bcd\u5b50\u4e4b\u6240\u4ee5\u4f4f\u5728\u6c11\u95f4\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u7684\u6bcd\u4eb2\u8fd8\u6ca1\u6709\u540d\u4efd\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u53eb\u505a\u8fd8\u6ca1\u6709\u540d\u4efd\uff1f\u201d \u3000\u3000\u5979\u7684\u7236\u4eb2\u597d\u50cf\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u8fbd\u56fd\u7687\u5e1d\u7684\u79c1\u751f\u5b50\u3001\u4ed6\u7684\u6bcd\u4eb2\u672b\u5165\u5bab \u7684\u3002\u201d\u5979\u5403\u60ca\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u7236\u4eb2\u9053\uff1a\u201c\u4eca\u5929\u4e00\u65e9\uff0c\u6709\u4e00\u8f86\u56db\u5339\u767d\u9a6c\u62c9\u7684\u91d1\u9a6c\u8f66\u63a5\u4ed6\u4eec\u6bcd\u5b50\u53bb\u4e86\uff0c\u62a4\u9001\u7684\u516b\u4e2a\u4eba\u662f\u5378 \u6797\u519b\u7684\u519b\u5b98\u3002\u6211\u867d\u7136\u4e0d\u5728\u5b98\u573a\uff0c\u4e5f\u6709\u5b98\u573a\u4e0a\u7684\u670b\u53cb\uff0c\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u5df2\u7ecf\u4e0d\u662f\u79d8\u5bc6 \u4e86\u3002\u8fd9\u662f\u6211\u521a\u521a\u6253\u542c\u5230\u7684\u3002\u201d \u3000\u3000\u60f3\u4e0d\u5230\u6628\u665a\u7684\u4e00\u543b\u5b9a\u60c5\uff0c\u4eca\u65e9\u9192\u6765\uff0c\u5df2\u662f\u53d8\u6210\u8bc0\u522b\uff1f\u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c \u6b64\u4e8b\u53e4\u96be\u5168\uff01\u201d\u8036\u5f8b\u7384\u5143\u6628\u665a\u7684\u548f\u53f9\u8fd8\u7559\u5728\u5979\u7684\u8033\u8fb9\uff0c\u4ed6\u7684\u4eba\u5374\u5df2\u8fdc\u79bb\u5979\u4e86\uff01\u201d \u3000\u3000\u6628\u665a\u90a3\u4e9b\u4e0d\u53ef\u89e3\u7684\u8bdd\u8bed\uff0c\u5982\u4eca\u4e5f\u5168\u90fd\u660e\u767d\u4e86\uff01 \u3000\u3000\u5979\u61c2\u5f97\u4e86\u4ec0\u4e48\u662f\u8036\u5f8b\u7384\u5143\u6240\u8bf4\u7684\u201c\u4e0d\u53ef\u6d4b\u7684\u98ce\u6ce2\u201d\u4e86\uff0c\u5509\uff0c\u6628\u665a\u4ed6\u8bf4\u8fd9\u4e2a\u8bdd\u7684\u65f6\u5019\uff0c\u662f \u52a0\u4e0a\u2018\u4e07\u4e00\u2019\u8fd9\u4e24\u4e2a\u5b57\u7684\uff0c\u4f46\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u675e\u4eba\u5fe7\u5929\u5462\u3001\u8c01\u77e5\u4e0d\u662f\u2018\u4e07\u4e00\u2019\uff0c\u800c\u662f\u5df2\u6210\u7684\u4e8b \u5b9e\uff01\u6628\u665a\u5728\u4ed6\u7684\u7ea6\u4f1a\u4e4b\u65f6\uff0c\u8fd9\u4e2a\u98ce\u6ce2\u662f\u65e9\u5df2\u6765\u5230\u7684\uff01\u201d \u3000\u3000\u5979\u5fc3\u4e71\u5982\u9ebb\uff0c\u5bf9\u7740\u5979\u7684\u7236\u4eb2\uff0c\u4e5f\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u624d\u597d\u4e86\u3002 \u3000\u3000\u7236\u4eb2\u597d\u50cf\u4ea6\u5df2\u61c2\u5f97\u5973\u513f\u7684\u5fc3\u4e8b\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5973\u771f\u65cf\u81ea\u4ece\u5728\u4e1c\u5317\u5d1b\u8d77\u4ee5 \u6765\uff0c\u65e5\u76ca\u5f3a\u76db\uff0c\u5982\u4eca\u5df2\u662f\u5b9a\u4e86\u56fd\u53f7\u4e3a\uff1a\u201c\u91d1\u201d\uff0c\u4e0d\u7518\u518d\u505a\u8fbd\u56fd\u7684\u5c5e\u9886\u4e86\u3002\uff08\u6309\uff1a\u5973\u771f\u65cf\u5373\u6ee1 \u65cf\u7684\u524d\u8eab\uff0c\u4e94\u4ee3\u65f6\u5c45\u4e8e\u6df7\u540c\u6c5f\uff0c\u5373\u4eca\u4e4b\u677e\u82b1\u6c5f\u4ee5\u5317\u3002\u81ea\u54c8\u5c14\u6ee8\u4ee5\u4e1c\u5730\u65b9\u8005\u540d\u201c\u751f\u5973\u771f\u201d\uff0c\u6df7 \u540c\u6c5f\u4ee5\u5357\u8005\u540d\u201c\u719f\u5973\u771f\u201d\uff0c\u5747\u5148\u540e\u6210\u4e3a\u8fbd\u7684\u5c5e\u9886\u3002\u81f3\u5317\u5b8b\u795e\u5b97\u65f6\u671f\uff0c\u5973\u771f\u65cf\u914b\u957f\u963f\u9aa8\u6253\u7edf\u4e00 \u5404\u90e8\u843d\uff0c\u516c\u5143\u4e00\u4e00\u4e00\u4e94\u963f\u9aa8\u6253\u5373\u5e1d\u4f4d\uff0c\u5373\u4f4d\u4e0d\u8fc7\u5341\u5e74\uff0c\u81f3\u516c\u5143\u4e00\u4e00\u4e8c\u4e94\u5e74\uff0c\u4fbf\u5373\u706d\u8fbd\u3002\uff09\u4f9d \u6211\u770b\u8fd9\u4e2a\u5f62\u52bf\u91d1\u56fd\u548c\u8fbd\u56fd\u8fdf\u65e9\u5fc5\u5b9a\u8981\u6253\u4e00\u573a\u5927\u4ed7\uff0c\u4e0d\u662f\u4f60\u6b7b\u4fbf\u662f\u6211\u4ea1\u7684\u5927\u4ed7\uff01\u5c31\u5f62\u52bf\u800c\u8a00\uff0c \u6211\u76f8\u4fe1\u54b1\u4eec\u91d1\u56fd\u4e5f\u4e00\u5b9a\u80fd\u591f\u6253\u80dc\u3002\u4f46\u8036\u5f8b\u7384\u5143\u662f\u8fbd\u56fd\u7684\u738b\u5b50\uff0c\u6240\u4ee5\u4f60\u548c\u4ed6\u7684\u8fd9\u6bb5\u4ea4\u60c5\uff0c\u6700\u597d \u662f\u5fd8\u8bb0\u5f97\u5e72\u5e72\u51c0\u51c0\u7684\u597d\uff01\u5426\u5219\u4e0d\u4f46\u7d2f\u4e86\u4f60\u7684\u7ec8\u8eab\uff0c\u6050\u6015\u8fd8\u8981\u5e26\u7ed9\u54b1\u4eec\u5168\u5bb6\u4ee5\u83ab\u6d4b\u4e4b\u7978\uff0c\u4f60\u660e \u767d\u5417\uff1f\u201d\u5979\u5df2\u7ecf\u4e0d\u662f\u5c0f\u5b69\u5b50\uff0c\u7236\u4eb2\u53c8\u8bf4\u5f97\u8fd9\u6837\u6e05\u695a\uff0c\u8fd9\u5176\u4e2d\u7684\u5229\u5bb3\u5173\u7cfb\uff0c\u5979\u53c8\u7109\u80fd\u4e0d\u660e\uff1f\u4e0d \u8fc7\u3002\u8981\u5979\u201c\u5fd8\u8bb0\u5f97\u5e72\u5e72\u51c0\u51c0\u201d\uff0c\u90a3\u5374\u662f\u5979\u7edd\u8ba1\u505a\u4e0d\u5230\u7684\u3002\u53ea\u662f\u5979\u53c8\u600e\u80fd\u628a\u5fc3\u4e8b\u90fd\u5411\u7236\u4eb2\u8bf4 \u4e86\uff1f\u5728\u7236\u4eb2\u5145\u6ee1\u7231\u610f\uff0c\u5145\u6ee1\u6073\u6c42\u7684\u76ee\u5149\u6ce8\u89c6\u4e4b\u4e0b\uff0c\u5979\u4e5f\u53ea\u80fd\u8fdd\u5fc3\u70b9\u4e00\u70b9\u5934\u4e86\u3002 \u3000\u3000\u7236\u4eb2\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u5c31\u7ed9\u6211\u5b89\u5b89\u4efd\u4efd\u5730\u7559\u5728\u5bb6\u4e2d\u505a\u6211\u7684\u95fa\u5973 \u5427\uff0c\u8036\u5f8b\u4e00\u5bb6\u548c\u54b1\u4eec\u662f\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u4e86\u3002\u4f60\u53ef\u4ee5\u5f53\u4f5c\u6839\u672c\u5c31\u4e0d\u8ba4\u8bc6\u4ed6\u4eec\u8fd9\u4e00\u5bb6\u4eba\uff01\u201d \u3000\u3000\u4f46\u201c\u53ef\u60dc\u201d\u8fd9\u6bb5\u6df1\u60c5\u5374\u4e0d\u662f\u8bf4\u62b9\u53bb\u5c31\u80fd\u62b9\u53bb\u7684\uff0c\u4e24\u5bb6\u7684\u5173\u7cfb\u4e5f\u4e0d\u80fd\u4ece\u6b64\u6d88\u706d\u65e0\u75d5\u3002 \u3000\u3000\u5c31\u5728\u5979\u7684\u7236\u4eb2\u8bf4\u8fd9\u4e2a\u8bdd\u7684\u65f6\u5019\uff0c\u6709\u8036\u5f8b\u5bb6\u7684\u5bb6\u4eba\u627e\u4e0a\u95e8\u6765\u4e86\u3002 \u3000\u3000\u6765\u7684\u662f\u4ed6\u5bb6\u7684\u90a3\u4e24\u4e2a\u82b1\u738b\u3002 \u3000\u3000\u4ed6\u4eec\u5e26\u6765\u4e86\u8036\u5f8b\u7384\u5143\u4eb2\u7b14\u5199\u7684\u4fe1\uff0c\u8981\u6c42\u5979\u6536\u7559\u8fd9\u4e24\u4e2a\u82b1\u738b\u3002\u4ed6\u8bf4\u8fd9\u4e24\u4e2a\u82b1\u738b\u53ef\u4ee5\u4e3a\u5979\u79cd \u51fa\u540d\u79cd\u7261\u4e39\uff0c\u8981\u662f\u201c\u4e07\u4e00\u201d\u4ed6\u5341\u5e74\u516b\u8f7d\u90fd\u8fd8\u672a\u80fd\u56de\u6765\u7684\u8bdd\uff0c\u5979\u5728\u8d4f\u7261\u4e39\u4e4b\u65f6\uff0c\u4e5f\u4f1a\u611f\u89c9\u5f97\u5230 \u4ed6\u662f\u966a\u4f34\u5728\u5979\u7684\u8eab\u65c1\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u8d70\u4e86\uff0c\u8fd8\u8981\u5728\u5979\u7684\u5bb6\u4e2d\u79cd\u4e0b\u201c\u60c5\u82b1\u201d\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5979\u7684\u7236\u4eb2\u5f53\u7136\u662f\u5f88\u4e0d\u613f\u610f \u7684\uff0c\u4f46\u5f53\u65f6\u7684\u71d5\u4eac\u8fd8\u662f\u8fbd\u56fd\u7684\u966a\u90fd\uff0c\u8fbd\u56fd\u738b\u5b50\u7684\u8bf7\u6c42\uff0c\u5979\u7684\u7236\u4eb2\u4ecd\u662f\u4e0d\u80fd\u4e0d\u5141\u3002 \u3000\u3000\u9664\u4e86\u8036\u5f8b\u7384\u5143\u90a3\u5c01\u4eb2\u7b14\u5199\u7684\u4fe1\uff0c\u4ed6\u4eec\u8fd8\u5e26\u6765\u4e86\u8036\u5f8b\u7384\u5143\u5e73\u65e5\u6240\u5439\u7684\u90a3\u7ba1\u7389\u8427\u3002 \u3000\u3000XXX\u6b64\u9645\uff0c\u5b8c\u989c\u592b\u4eba\u62ff\u8d77\u8fd9\u7ba1\u7389\u8427\uff0c\u501a\u7a97\u9065\u671b\uff0c\u5979\u5fc3\u60c5\u7684\u70e6\u4e71\uff0c\u6bd4\u8d77\u5f53\u65e5\u6536\u5230\u8fd9\u7ba1\u7389\u8427 \u7684\u65f6\u5019\u66f4\u751a\u3002 \u3000\u3000\u4e0d\u662f\u5979\u4e0d\u80b2\u7b49\u4ed6\uff0c\u800c\u662f\u88ab\u5f62\u52bf\u6240\u901a\uff0c\u5979\u4e0d\u80fd\u591f\u7b49\u4ed6\uff01 \u3000\u3000\u4ed6\u4eec\u5206\u624b\u4e0d\u8fc7\u4e09\u5e74\uff0c\u8fbd\u56fd\u5c31\u7ed9\u91d1\u56fd\u706d\u4e86\u3002\u8fbd\u56fd\u7684\u966a\u90fd\u53d8\u6210\u4e86\u91d1\u56fd\u7684\u56fd\u90fd\u3002\u71d5\u4eac\u6539\u540d\u4e2d \u90fd\uff0c\u5728\u4e2d\u90fd\uff0c\u9664\u4e86\u91d1\u56fd\u7684\u7687\u5e1d\u4e4b\u5916\uff0c\u6700\u6709\u52bf\u529b\u7684\u4eba\u662f\u7edf\u7387\u5378\u6797\u519b\u7684\u4e00\u5b57\u5e76\u80a9\u738b\u5b8c\u989c\u957f\u4e4b\u3002 \u3000\u3000\u5b8c\u989c\u957f\u4e4b\u4eb2\u81ea\u4e3a\u4ed6\u7684\u4f84\u513f\u5b8c\u989c\u9274\u5411\u9f50\u5bb6\u6c42\u5a5a\u3002 \u3000\u3000\u5979\u7684\u7236\u4eb2\u600e\u80fd\u4e0d\u7b54\u5e94\u5462\uff1f\u5c31\u8fd9\u6837\u5979\u53d8\u6210\u4e86\u5b8c\u989c\u592b\u4eba\u4e86\u3002\u592b\u5a7f\u5c11\u5e74\u5f97\u5fd7\uff0c\u5982\u4eca\u4ed6\u624d\u4e0d\u5230\u56db \u5341\u591a\u5c81\uff0c\u5c31\u505a\u5230\u4e86\u5546\u5dde\u8282\u5ea6\u4f7f\uff0c\u8c01\u5bb6\u7684\u59d1\u5a18\u4e0d\u7fa1\u6155\u5979\u7684\u201c\u798f\u6c14\u201d\uff0c\u4f46\u5374\u53c8\u6709\u8c01\u77e5\u9053\u5979\u5fc3\u4e2d\u7684 \u82e6\u60c5\uff01 \u3000\u3000\u8036\u5f8b\u7384\u5143\u751f\u6b7b\u4e0d\u77e5\uff0c\u5c3d\u7ba1\u5979\u8fd8\u5b58\u7740\u201c\u4e07\u4e00\u201d\u5e0c\u671b\uff0c\u4f46\u5979\u4e5f\u77e5\u9053\u8fd9\u5e0c\u671b\u662f\u6781\u5176\u6e3a\u832b\uff0c\u4e0d\u6562 \u76f8\u4fe1\u8036\u5f8b\u7384\u5143\u8fd8\u6709\u751f\u8fd8\u4e4b\u65e5\u3002\u4f46\u60f3\u4e0d\u5230\u8fd9\u4e00\u6b21\u7684\u201c\u4e07\u4e00\u201d\u5374\u662f\u771f\u7684\u5b9e\u73b0\u4e86\uff0c\u5979\u4eb2\u8033\u4eb2\u89c1\u4e08\u592b \u6240\u8bf4\u7684\u6709\u5173\u8036\u5f8b\u7384\u5143\u7684\u6d88\u606f\u3002\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4ed6\u8fd8\u6d3b\u5728\u4eba\u95f4\uff01\u800c\u4e14\u5982\u4eca\u5df2\u662f\u56de\u5230\u5546\u5dde\uff0c\u8bf4\u4e0d\u5b9a\u5c31 \u5728\u4eca\u5929\u6216\u8005\u660e\u5929\uff0c\u4ed6\u5c31\u6709\u53ef\u80fd\u51fa\u73b0\u5728\u5979\u7684\u9762\u524d\uff01 \u3000\u3000\u554a\uff0c\u4f46\u4ed6\u7684\u56de\u6765\u662f\u592a\u8fdf\u4e86\uff01 \u3000\u3000\u5206\u624b\u4e4b\u65f6\uff0c\u4ed6\u6240\u4f30\u8ba1\u7684\u201c\u4e07\u4e00\u201d\u4e5f\u4e0d\u8fc7\u662f\u5341\u5e74\u516b\u8f7d\u800c\u5df2\uff0c\u4f46\u5982\u4eca\u5df2\u662f\u70d9\u8fd1\u4e09\u4e2a\u5341\u5e74\u8fc7\u53bb \u4e86\u3002\u548c\u4ed6\u76f8\u8bc6\u4e4b\u65f6\uff0c\u5979\u662f\u5341\u4e8c\u3001\u4e09\u5c81\u7684\u201c\u91ce\u4e2b\u5934\u201d\uff0c\u5982\u4eca\u5df2\u662f\u56db\u5341\u4e09\u5c81\u7684\u5c06\u519b\u592b\u4eba\u4e86\uff01\u5979\u7684 \u4e08\u592b\u662f\u8282\u5ea6\u4f7f\uff0c\u800c\u4ed6\u5219\u5df2\u662f\u53d8\u6210\u4e86\u5979\u7684\u4e08\u592b\u6240\u8981\u6349\u62ff\u7684\u94a6\u72af\u4e86\uff01 \u3000\u3000\u4e0d\u4e3a\u65c1\u4eba\u7f9e\u4e0d\u8d77\uff0c\u4e3a\u90ce\u6194\u60b4\u5374\u7f9e\u90ce\u3002\u5f53\u5e74\u5f8b\u7384\u5143\u4e3a\u4e86\u8981\u6062\u590d\u738b\u5b50\u7684\u8eab\u4efd\uff0c\u548c\u5979\u5206\u624b\uff0c\u5df2 \u7ecf\u662f\u6ce8\u5b9a\u4e86\u4ed6\u4eec\u4eca\u5929\u7684\u547d\u8fd0\u4e86\u3002 \u3000\u3000\u4ee5\u5979\u73b0\u5728\u7684\u8eab\u4efd\uff0c\u5979\u8fd8\u600e\u80fd\u89c1\u4ed6\uff1f\u4f46\u53ea\u662f\u4e0d\u89c1\u4ed6\u4e5f\u8fd8\u4e0d\u80fd\u4e86\u4e8b\u7684\uff0c\u5979\u77e5\u9053\u968f\u4ed6\u800c\u6765\u7684\u5fc5 \u6709\u96be\u4ee5\u9884\u6d4b\u7684\u707e\u7978\uff0c\u5979\u4e0d\u613f\u4ed6\u53d7\u5230\u4f24\u5bb3\uff0c\u540c\u6837\uff0c\u4e5f\u4e0d\u613f\u610f\u4e08\u592b\u53d7\u5230\u4f24\u5bb3\u3002\u800c\u8fd9\u79cd\u201c\u4f24\u5bb3\u201d\uff0c \u5f88\u53ef\u80fd\u662f\u4e25\u91cd\u5230\u201c\u6027\u547d\u4e0d\u4fdd\u201d\u7684\u3002 \u3000\u3000\u5979\u8fd8\u6ca1\u6709\u628a\u81ea\u5df1\u53d7\u5230\u7684\u201c\u4f24\u5bb3\u201d\u8ba1\u7b97\u5728\u5185\uff0c\u4e0d\u8fc7\u5979\u662f\u77e5\u9053\u5979\u5c06\u53d7\u5230\u4f55\u79cd\u4f24\u5bb3\u7684\u3002 \u3000\u3000\u201c\u4f24\u5bb3\u201d\u6709\u8bb8\u591a\u79cd\uff0c\u201c\u8eab\u8d25\u540d\u88c2\u201d\u7684\u201c\u4f24\u5bb3\u201d\uff0c\u5f80\u5f80\u6bd4\u6b7b\u4ea1\u8fd8\u66f4\u53ef\u6015\u3002\u800c\u8fd9\u4e5f\u6b63\u662f\u5979\u53ef \u80fd\u53d7\u5230\u7684\u4f24\u5bb3\u3002 \u3000\u3000\u4e3a\u4e86\u8036\u5f8b\u7384\u5143\uff0c\u4e3a\u4e86\u4e08\u592b\uff0c\u4e5f\u4e3a\u4e86\u5979\u81ea\u5df1\uff08\u867d\u7136\u5979\u6ca1\u6709\u8ba1\u7b97\u5728\u5185\uff09\uff0c\u5979\u90fd\u5fc5\u987b\u8bbe\u6cd5\u6d88\u5f2d \u90a3\u201c\u96be\u4ee5\u9884\u6d4b\u7684\u707e\u7978\uff01\u201d \u3000\u3000\u600e\u4e48\u529e\u5462\uff1f\u600e\u4e48\u529e\u5462\uff1f\u5979\u7684\u5fc3\u60c5\u4e71\u6781\u4e86\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u62ff\u8d77\u8036\u5f8b\u7384\u5143\u7559\u7ed9\u5979\u7684\u90a3\u7ba1\u7389\u8427\u5439 \u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4e07\u4e07\u82b1\u4e2d\u7b2c\u4e00\u6d41\uff0c\u6b8b\u971e\u8f7b\u67d3\u5ae9\u94f6\u74ef\u3002 \u3000\u3000\u80fd\u72c2\u7d2b\u964c\u5343\u91d1\u5b50\uff0c\u4e5f\u611f\u6731\u95e8\u4e07\u6237\u4faf\u3002 \u3000\u3000\u671d\u65e5\u7167\u5f00\u643a\u9152\u770b\uff0c\u66ae\u98ce\u5439\u843d\u7ed5\u680f\u6536\u3002 \u3000\u3000\u8bd7\u4e66\u6ee1\u67b6\u5c18\u57c3\u6251\uff0c\u5c3d\u65e5\u65e0\u4eba\u7565\u4e3e\u5934\u3002\u201d \u3000\u3000\u8fd9\u662f\u5510\u4ee3\u8bd7\u4eba\u5f90\u7b90\u7684\u548f\u7261\u4e39\u8bd7\uff0c\u5979\u7b2c\u4e00\u6b21\u5077\u5165\u8036\u5f8b\u7384\u5143\u7684\u82b1\u56ed\uff0c\u542c\u5230\u4ed6\u5439\u594f\u7684\u90a3\u652f\u66f2 \u5b50\uff0c\u5c31\u662f\u7528\u8fd9\u9996\u8bd7\u6765\u8c31\u66f2\u7684\u3002 \u3000\u3000\u8bd7\u4e2d\u6709\u6b22\u4e50\u4e5f\u6709\u611f\u53f9\uff0c\u8036\u5f8b\u7384\u5143\u662f\u5c06\u5979\u6bd4\u4f5c\u201c\u80fd\u72c2\u7d2b\u964c\u5343\u91d1\u5b50\uff0c\u4e5f\u611f\u5931\u95e8\u4e07\u6237\u4faf\u201d\u7684 \u201c\u4e07\u4e07\u82b1\u4e2d\u7b2c\u4e00\u6d41\u201d\u7684\u7261\u4e39\u82b1\u7684\u3002\u4f46\u201c\u66ae\u98ce\u5439\u843d\u7ed5\u680f\u6536\u201d \u3000\u3000\uff0c\u4e0d\u4e5f\u662f\u6b63\u6210\u201c\u8bd7\u8c36\u201d\u4e48\uff1f\u90c1\u95f7\u96be\u6392\uff0c\u5979\u53c8\u5439\u8d77\u522b\u79bb\u90a3\u665a\uff0c\u8036\u5f8b\u7384\u5143\u6700\u540e\u7ed9\u5979\u5439\u7684\u90a3\u652f \u66f2\u5b50\u3002\u5439\u5230\u201c\u4e1c\u541b\u81ea\u6709\u56de\u5929\u529b\uff0c\u770b\u628a\u82b1\u679d\u5e26\u6708\u5f52\u201d\u8fd9\u4e24\u53e5\u66f2\u8f9e\u7684\u65f6\u5019\uff0c\u5979\u5fc3\u4e2d\u82e6\u7b11\uff0c\u773c\u89d2\u5df1 \u662f\u6d41\u51fa\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u201c\u592b\u4eba\uff0c\u4f55\u4e8b\u5fc3\u4e2d\u4e0d\u4e50\uff1f\u5a62\u5b50\u966a\u4f60\u53bb\u770b\u7261\u4e39\u597d\u5417\uff1f\u201d \u3000\u3000\u8fdb\u6765\u7684\u662f\u5979\u7684\u4e00\u4e2a\u8d34\u8eab\u4e2b\u73af\uff0c\u66fe\u7ecf\u542c\u8fc7\u5979\u4e0d\u77e5\u591a\u5c11\u6b21\u5439\u8fd9\u652f\u66f2\u5b50\u7684\u3002 \u3000\u3000\u5979\u5ffd\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5f97\u5230\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u4e0d\u60f3\u53bb\u770b\u7261\u4e39\u3002\u6211\u53ea\u60f3\u4f60\u66ff\u6211 \u529e\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u8bf7\u592b\u4eba\u5429\u5490\u3002\u201d\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u4f60\u53eb\u4ed6\u4eec\u7ed9\u627e\u51c6\u5907\u4e00\u8f86\u9a6c\u8f66\uff0c\u4f46\u4e0d\u5fc5\u7ed9\u5c06\u519b\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u4e2b\u73af\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8981\u4e0a\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u7ba1\uff0c\u4f46\u4f60\u8fd8\u8981\u66ff\u6211\u505a\u4e00\u4e9b\u4e8b\u60c5\u3002\u5509\uff0c\u5982\u4eca\u6050\u6015\u4e5f\u53ea\u6709\u4f60\u624d\u80fd\u5e2e\u5fd9 \u6211\u4e86\u3002\u201d \u3000\u3000\u5c0f\u4e2b\u9b1f\u53d7\u5ba0\u82e5\u60ca\uff0c\u8dea\u4e0b\u53bb\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8fd9\u6837\u8bf4\uff0c\u5a62\u5b50\u53ef\u62c5\u5f53\u4e0d\u8d77\u3002 \u3000\u3000\u592b\u4eba\u5c3d\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u628a\u5979\u62c9\u8d77\u6765\uff0c\u8d34\u7740\u5979\u7684\u8033\u6735\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u8d8a\u542c\u8d8a\u662f\u5403\u60ca\uff0c\u4f46\u8fd8\u662f\u63a5\u53d7\u4e86\u592b\u4eba\u7684\u547d\u4ee4\u3002 \u3000\u3000\u6700\u540e\uff0c\u5b8c\u989c\u592b\u4eba\u628a\u90a3\u652f\u7389\u8427\u4e5f\u7ed9\u4e86\u5c0f\u4e2b\u73af\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u521a\u624d\u5439\u7684\u90a3\u652f\u66f2\u5b50\uff0c\u6211\u77e5\u9053\u4f60\u4e5f \u5df2\u7ecf\u4f1a\u5439\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u5a62\u5b50\u5439\u5f97\u4e0d\u597d\u3002\u6050\u6015\u662f\u52c9\u5f3a\u53ef\u6210\u66f2\u8c03\u3002\u201d \u3000\u3000\u201c\u80fd\u6210\u66f2\u8c03\u5c31\u597d\uff0c\u4f60\u7167\u6211\u7684\u5429\u5490\u53bb\u505a\u5427\u3002\u73b0\u5728\u4f60\u5148\u53bb\u627e\u8001\u4f5f\u548c\u5170\u59d1\u3002 \u3000\u3000\u201d \u3000\u3000\u4e2b\u5934\u8d70\u540e\uff0c\u5979\u8d70\u8fc7\u90bb\u623f\uff0c\u5170\u59d1\u7684\u90a3\u4e2a\u4e09\u5c81\u5927\u7684\u5c0f\u5973\u513f\u5c31\u662f\u7761\u5728\u8fd9\u95f4\u623f\u95f4\u7684\u3002\u7761\u5f97\u6b63\u9163\u3002 \u82f9\u679c\u822c\u7684\u5c0f\u8138\u597d\u50cf\u85cf\u7740\u65e0\u7a77\u6b22\u4e50\uff0c\u4ee4\u5979\u4e00\u770b\u5c31\u5fd8\u8bb0\u5fc3\u5e95\u7684\u6101\u70e6\u3002 \u3000\u3000\u5979\u62b1\u8d77\u4e86\u8fd9\u5c0f\u5973\u5b69\uff0c\u543b\u4e86\u5979\u82f9\u679c\u822c\u7684\u8138\u5e9e\uff0c\u5c06\u5979\u653e\u4e0b\uff0c\u4f46\u770b\u4e86\u4e00\u770b\uff0c\u53c8\u5c06\u5979\u62b1\u8d77\u3002 \u3000\u3000\u62b1\u8d77\u3001\u653e\u4e0b\u3001\u653e\u4e0b\u3001\u62b1\u8d77\u3002\u2014\u2014\u7ec8\u4e8e\u5979\u4e0b\u4e86\u51b3\u5fc3\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u8fd9\u5973\u5a03\u513f\u7adf\u7136\u662f\u6a80\u8d1d\u52d2\u7684 \u5b59\u5973\uff0c\u800c\u5979\u7684\u6bcd\u4eb2\uff0c\u53c8\u662f\u5cb3\u98de\u7684\u5b59\u5973\u513f\uff01\u5982\u4eca\u54c8\u5fc5\u56fe\u5df2\u5728\u6000\u7591\u5170\u59d1\u7684\u8eab\u4efd\u4e86\uff0c\u4f46\u613f\u5979\u80fd\u8eb2\u8fc7 \u8fd9\u573a\u707e\u7978\u3002\u4f46\u4e5f\u53ea\u6015\u4e8b\u60c5\u672a\u5fc5\u80fd\u5982\u6211\u6240\u613f\uff0c\u5979\u7684\u513f\u5b50\u5982\u4eca\u4e0d\u5728\u5bb6\uff0c\u6700\u5c11\u6211\u4e5f\u5e94\u7ed9\u5979\u4fdd\u5168\u5979\u8fd9 \u5c0f\u5973\u513f\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u5316\u540d\u5170\u59d1\u7684\u5f20\u96ea\u6ce2\u8fd8\u5728\u8001\u4f5f\u7684\u5c4b\u5b50\u91cc\u3002\u8001\u4f5f\u5c31\u662f\u90a3\u4e2a\u5e74\u7eaa\u8f83\u5927\u7684\u82b1\u738b\u3001\u8001\u4f5f\u4f3c\u4e4e\u5f00\u59cb\u611f \u89c9\u5230\u6709\u4ec0\u4e48\u4e0d\u5bf9\u4e86\uff0c\u4ed6\u671b\u7740\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5170\u59d1\uff0c\u4f60\u4e3a\u4ec0\u4e48\u6025\u4e8e\u8981\u627e\u4f60\u7684\u513f\u5b50\u56de\u6765\uff1f\u201d\u5f20\u96ea\u6ce2 \u9053\uff1a\u201c\u6211\u662f\u6015\u4ed6\u5728\u5916\u9762\u95f9\u4e8b\u3002\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u4ed6\u662f\u548c\u8f66\u7f2d\u3001\u696e\u5ca9\u4e00\u8d77\u51fa\u53bb\u7684\uff0c\u591a\u534a\u662f\u5230\u5c71\u4e0a\u7ec3\u6b66\uff0c\u600e\u4f1a\u95f9\u4e8b\uff1f\u201d\u5f20\u96ea\u6ce2 \u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u559c\u6b22\u4ed6\u7ec3\u6b66\uff0c\u6211\u5012\u662f\u5b81\u613f\u4ed6\u591a\u4e9b\u65f6\u5019\u5728\u6211\u8eab\u8fb9\uff0c\u4eca\u5929\u6211\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4ed6\u5462\uff01\u201d \u8001\u4f5f\u5ffd\u9053\uff1a\u201c\u5170\u59d1\uff01\u54b1\u4eec\u867d\u7136\u4e0d\u662f\u4eb2\u4eba\uff0c\u4f46\u4e5f\u50cf\u4eb2\u4eba\u4e00\u6837\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f5f\u5e08\u7236\uff0c\u6211\u4eec\u6bcd\u5b50\u5f97\u6709\u4eca\u65e5\uff0c\u90fd\u662f\u5168\u51ed\u4f60\u7684\u7231\u62a4\uff0c\u4f60\u6bd4\u6211\u4eec\u7684\u4eb2\u4eba\u8fd8 \u4eb2\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u662f\u628a\u6211\u5f53\u4f5c\u4eb2\u4eba\uff0c\u4f60\u5fc3\u91cc\u6709\u4ec0\u4e48\u4e3a\u96be\u4e4b\u4e8b\uff0c\u5bf9\u6211\u8bf4\u5427\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6ca1\u3001\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u8001\u4f5f\u76ef\u7740\u5979\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u7792\u6211\uff0c\u6211\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5728\u4ed6\u7684\u9510\u5229\u76ee\u5149\u4e4b\u4e0b\uff0c\u5fc3\u91cc\u53d1\u614c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f5f\u5e08\u7236\u6211\u662f\u4fe1\u5f97\u8fc7\u4ed6\u4e0d\u4f1a\u51fa\u5356\u6211 \u7684\uff0c\u4f46\u6211\u7684\u8eab\u4e16\u4e4b\u75db\uff0c\u5173\u7cfb\u592a\u5927\uff0c\u53c8\u600e\u80fd\u8bf4\u7ed9\u4ed6\u542c\uff1f\u4ed6\u77e5\u9053\u4e86\uff0c\u53ea\u62cd\u53cd\u800c\u8fde\u7d2f\u4e86\u4ed6\u201d \u3000\u3000\u201c\u592b\u4eba\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u6211\u600e\u4f1a\u6709\u4e3a\u96be\u4e4b\u4e8b\uff1f\u201c\u5f20\u96ea\u6ce2\u8bf4\u9053\u3002 \u3000\u3000\u8001\u4f5f\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u5bf9\u4f60\u597d\u662f\u4e00\u4ef6\u4e8b\uff0c\u4f60\u6709\u6ca1\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u53c8\u662f\u53e6\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u591a\u8c22\u4f60\u8001\u4eba\u5bb6\u5173\u5fc3\u6211\uff0c\u4f46\u6211\u771f\u7684\u6ca1\u6709\u4e3a\u96be\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\uff0c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\uff0c\u90a3\u4e48\uff0c\u4f60\u5728\u8fd9\u91cc\uff0c\u5df2\u7ecf\u89c9\u5f97\u6ee1\u8db3\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u662f\u7684\u3002\u201d\u8001\u4f5f\u518d\u95ee\uff1a\u201c\u4e00\u8f88\u5b50\u90fd\u613f\u610f\u5728\u8fd9\u91cc\u4e48\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u592b\u4eba\u5230\u54ea\u91cc\uff0c\u6211\u5c31\u8ddf\u5979\u5230\u54ea\u91cc\uff0c\u9664\u975e\u5979\u4e0d\u8981\u6211\u3002\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u592b\u4eba\u6700\u559c\u6b22\u7261\u4e39\uff0c\u6211\u5df2\u7ecf\u4e0d\u80fd\u4e3a\u5979\u6599\u7406\u7261\u4e39\uff0c\u6709\u4f60\u5f97\u6211\u7684\u8863\u94b5\uff0c\u6211\u4e5f\u5e0c\u671b\u4f60 \u80fd\u591f\u4ee3\u66ff\u6211\u7684\u804c\u52a1\uff0c\u4e00\u8f88\u5b50\u8ddf\u968f\u592b\u4eba\uff0c\u4f46\uff0c\u4e00\u6765\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4e16\u4e8b\u5f80\u5f80\u662f\u4eba\u96be\u6599\uff1b\u4e8c\u6765\uff0c\u8fd9 \u6837\u505a\u4e5f\u672a\u514d\u592a\u59d4\u5c48\u4f60\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u542c\u662f\u201c\u59d4\u5c48\u201d\u4e8c\u5b57\uff0c\u4e0d\u89c9\u5fc3\u5934\u4e00\u8df3\uff0c\u4e0d\u77e5\u9053\u8001\u82b1\u738b\u7a76\u7adf\u77e5\u9053\u4e86\u5979\u7684\u4ec0\u4e48\uff0c\u8fde\u5fd9\u8bf4 \u9053\uff1a\u201c\u6211\u4e24\u6bcd\u5b50\u672c\u662f\u65e0\u4f9d\u65e0\u9760\u7684\u96be\u6c11\uff0c\u5168\u4ed7\u592b\u4eba\u6536\u7559\uff0c\u624d\u5f97\u7acb\u8db3\u3002\u6211\u771f\u7684\u662f\u613f\u610f\u4e3a\u592b\u4eba\u79cd\u4e00 \u8f88\u5b50\u7261\u4e39\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\uff1a\u201c\u592b\u4eba\u7684\u786e\u662f\u597d\u4eba\uff0c\u5509\uff0c\u4f46\u4e0d\u8fc7\uff01\u201d\u4e0d\u8fc7\u4ec0\u4e48\u5462\uff1f\u4ed6\u5728\u957f\u53f9\u4e00\u58f0\u4e4b\u540e\uff0c\u5374\u5e76\u6ca1 \u6709\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4e5f\u4e0d\u6562\u95ee\u4ed6\uff0c\u534a\u664c\uff0c\u8001\u4f5f\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6700\u521d\u6211\u5e76\u4e0d\u662f\u4e3a\u592b\u4eba\u79cd\u7261\u4e39 \u7684\u3002\u201d\u5f20\u96ea\u6ce2\u4ecd\u7136\u53ea\u662f\u542c\u4ed6\u8bf4\uff0c\u4e0d\u6562\u63d2\u5634\u3002 \u3000\u3000\u8001\u4f5f\u7a81\u7136\u53c8\u95ee\u4e86\u4e00\u4e2a\u5947\u602a\u7684\u95ee\u9898\uff1a\u201c\u5170\u59d1\u4f60\u662f\u54ea\u91cc\u4eba\u6c0f\uff1f\u201d\u4ece\u8c08\u79cd\u7261\u4e39\u800c\u5ffd\u7136\u95ee\u5230\u5979\u7684 \u7c4d\u8d2f\uff0c\u8fd9\u4e00\u95ee\u4e5f\u672a\u514d\u592a\u7a81\u5140\u4e86\u3002 \u3000\u3000\u6211\u672c\u662f\u672c\u5dde\u7684\u5c71\u5730\u4eba\u5440\uff0c\u4f60\u4e0d\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e48\uff1f\u201d\u5f20\u96ea\u6ce2\u8fdf\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\u3002 \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u77e5\u9053\u4f60\u662f\u5728\u5546\u5dde\u957f\u5927\u7684\uff0c\u4f60\u7684\u53e3\u97f3\u548c\u672c\u5730\u4eba\u5b8c\u5168\u4e00\u6837\u3002\u4f46\u6211\u89c9\u5f97 \u4f60\u7684\u4f53\u6001\u6709\u70b9\u50cf\u662f\u6c5f\u5357\u7684\u6c49\u4eba\uff0c\u6216\u8005\u662f\u4ece\u6c5f\u5357\u79fb\u5c45\u6765\u6b64\u7684\u5427\uff1f\u4f60\u522b\u4ecb\u610f\uff0c\u6211\u53ea\u662f\u968f\u4fbf\u95ee \u95ee\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u59d3\u9102\uff0c\u6211\u7684\u786e\u662f\u91d1\u56fd\u4eba\u3002\u201d\u81ea\u4ece\u5979\u53d8\u6210\u5b8c\u989c\u592b\u4eba\u7684\u5973\u4ec6\uff0c\u5979\u4e00 \u76f4\u662f\u8fd9\u6837\u7f16\u9020\u81ea\u5df1\u7684\u8eab\u4e16\u3002\u4f46\u6b64\u523b\u9762\u5bf9\u8fd9\u4e2a\u597d\u50cf\u662f\u5979\u957f\u8f88\u4eb2\u4eba\u7684\u8001\u5e08\u7236\u7ee7\u7eed\u8bf4\u8c0e\uff0c\u5979\u5374\u662f\u4e0d \u7981\u6709\u70b9\u5185\u6127\u4e8e\u5fc3\u4e86\u3002 \u3000\u3000\u201c\u5728\u8fd9\u91cc\uff0c\u6216\u8005\u6709\u4e00\u4e9b\u4eba\u628a\u6c49\u4eba\u5f53\u4f5c\u4ec7\u654c\uff0c\u4f46\u6211\u7684\u770b\u6cd5\u548c\u4ed6\u4eec\u4e0d\u4e00\u6837\u3002\u201d\u8001\u4f5f\u610f\u5473\u6df1\u957f \u7684\u8bf4\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\uff1a\u662f\u54ea\u4e00\u56fd\u7684\u4eba\u5e76\u4e0d\u91cd\u8981\uff0c\u91cd\u8981\u7684\u662f\u4ed6\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\u3002\u6211\u60f3\uff0c\u5373\u4f7f\u4f60\u662f \u6c49\u4eba\uff0c\u592b\u4eba\u4e5f\u4e0d\u4f1a\u6b67\u89c6\u4f60\u7684\u3001\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u6c49\u4eba\uff0c\u662f\u5728\u6c5f\u5357\u957f\u5927\u7684 \u6c49\u4eba\u3002\u201d \u3000\u3000\u8001\u4f5f\u672c\u662f\u6c49\u4eba\uff0c\u8fd9\u662f\u5f20\u96ea\u6ce2\u65e9\u5df2\u77e5\u9053\u7684\uff0c\u6240\u4e0d\u77e5\u7684\u53ea\u662f\u4ed6\u751f\u957f\u7684\u5730\u65b9\u800c\u5df2\u3002\u201c\u539f\u6765\u4ed6\u662f \u6c5f\u5357\u957f\u5927\u7684\u6c49\u4eba\uff0c\u6211\u7684\u7236\u6bcd\u4e5f\u90fd\u662f\u6c5f\u5357\u7684\u6c49\u4eba\uff0c\u602a\u4e0d\u5f97\u4ed6\u80fd\u591f\u5728\u6211\u7684\u8eab\u4e0a\u770b\u51fa\u6765\u3002\u5927\u6982\u6211\u7684 \u4f53\u6001\u548c\u4e00\u822c\u5e38\u89c1\u7684\u6c5f\u5357\u6c49\u4eba\u76f8\u5dee\u4e0d\u8fdc\u3002\u201d\u5f20\u96ea\u6ce2\u5fc3\u60f3\u3002 \u3000\u3000\u4f46\u8001\u4f5f\u518d\u8bf4\u4e0b\u53bb\uff0c\u5979\u5c31\u4e0d\u80fd\u4e0d\u5927\u4e3a\u60ca\u8be7\u4e86\u3002 \u3000\u3000\u201c\u6211\u7b2c\u4e00\u4e2a\u4e3b\u4eba\u4e5f\u4e0d\u662f\u91d1\u56fd\u7684\u5973\u771f\u65cf\u4eba\uff0c\u4ed6\u662f\u8fbd\u56fd\u7684\u5951\u4e39\u4eba\u3002\u800c\u4e14\u662f\u548c\u91d1\u56fd\u7687\u5e1d\u4f5c\u5bf9\u7684 \u8fbd\u56fd\u4eba\uff01\u201d \u3000\u3000\u201c\u548c\u91d1\u56fd\u7687\u5e1d\u4f5c\u5bf9\u7684\u8fbd\u56fd\u4eba\uff01\u201d\u83ab\u975e\u3001\u83ab\u975e\u2014\u2014\u5f20\u96ea\u6ce2\u60f3\u8d77\u4e86\u521a\u624d\u5077\u542c\u5230\u7684\u54c8\u5fc5\u56fe\u548c\u5b8c \u989c\u9274\u7684\u5bc6\u8c08\u2014\u2014\u201c\u83ab\u975e\u4ed6\u7684\u7b2c\u4e00\u4e3b\u4eba\uff0c\u5c31\u662f\u54c8\u5fc5\u56fe\u8bf4\u7684\u90a3\u4e2a\u4ee4\u91d1\u56fd\u987e\u5fcc\u7684\u8fbd\u56fd\u738b\u5b50\uff1f\u201d \u3000\u3000\u8001\u4f5f\u4e3a\u4ec0\u4e48\u6562\u4e8e\u628a\u8fd9\u4e2a\u79d8\u5bc6\u544a\u8bc9\u5979\u5462\uff1f\u96be\u9053\u8001\u4f5f\u5df2\u7ecf\u77e5\u9053\u5979\u6b63\u662f\u60f3\u8981\u5bfb\u627e\u8fd9\u4e2a\u5951\u4e39\u4eba\uff1f \u5979\u7684\u5fc3\u6026\u6026\u5730\u8df3\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u60c5\u5173\u7cfb\u592a\u5927\u4e86\u3002\u5979\u53ef\u4e0d\u6562\u5411\u4ed6\u53d1\u95ee\u3002 \u3000\u3000\u5979\u53ea\u80fd\u65c1\u6572\u4fa7\u51fb\uff1a\u201c\u592b\u4eba\uff0c\u77e5\u3001\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u539f\u6765\u7684\u4e3b\u4eba\u5c06\u6211\u9001\u7ed9\u592b\u4eba\u7684\uff0c\u8fd9\u4e2a\u79d8\u5bc6\u4e5f\u53ea\u6709\u592b\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u548c\u5c06\u519b\u867d\u7136\u662f\u592b\u5987\u4e4b\u4eb2\uff0c\u4f46\u6211\u60f3\u592b\u4eba\u4e5f\u4e0d\u4fbf\u544a\u8bc9\u5c06\u519b\u7684\uff01\u201d\u5f20\u96ea\u6ce2\u66f4\u52a0\u5403\u60ca\u4e86\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u4e03 \u56de\u3000\u592b\u4eba\u51fa\u8d70 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u4e03 \u56de\u3000\u592b\u4eba\u51fa\u8d70 \u3000\u3000\u201c\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u8fd9\u4e2a\u79d8\u5bc6\u544a\u8bc9\u6211\u5462\uff1f\u201d\u5f20\u96ea\u6ce2\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u95ee\u51fa\u6765\u4e86\u3002\u5c31\u5728\u6b64\u65f6\uff0c \u5ffd\u542c\u5f97\u6709\u811a\u6b65\u58f0\u8d70\u6765\u3002 \u3000\u3000\u6765\u7684\u662f\u592b\u4eba\u7684\u8d34\u8eab\u4e2b\u73af\u98d8\u9999\u3002 \u3000\u3000\u201c\u54a6\uff0c\u5c0f\u98d8\u9999\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u7ed9\u5439\u6765\u7684\uff1f\u201d\u8001\u4f5f\u7b11\u7740\u95ee\u5979\u3002\u8fd9\u5c0f\u4e2b\u5934\u662f\u5f88\u5c11\u5230\u4ed6\u7684\u5c4b\u5b50 \u7684\u3002 \u3000\u3000\u98d8\u9999\u9762\u8272\u5374\u662f\u751a\u4e3a\u6c89\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u592b\u4eba\u53eb\u6211\u6765\u7684\u3002\u5170\u59d1\uff0c\u96be\u5f97\u4f60\u4e5f\u5728\u8fd9\u91cc\uff0c\u592b\u4eba\u4e5f\u8981 \u6211\u627e\u4f60\u7684\u3002\u201d\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201c\u5f20\u96ea\u6ce2\u548c\u90a3\u8001\u82b1\u738b\u9f50\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u5f53\u7136\u662f\u6709\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u592b\u4eba\u624d\u8981\u6211\u6765\u77e5\u4f1a\u4f60\u4eec\u3002\u8ba9\u6211\u548c\u8001\u4f5f\u5148\u8bf4\u5427\u3002\u201d \u3000\u3000\u98d8\u9999\u62ff\u51fa\u4e24\u5305\u94f6\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4f5f\uff0c\u8fd9\u4e00\u5305\u662f\u7ed9\u4f60\u7684\uff0c\u8fd9\u4e00\u5305\u662f\u7ed9\u8001\u4f55\u7684\u3002\u201d\u8001\u4f55\u662f\u53e6 \u4e00\u4e2a\u5e74\u7eaa\u8f83\u8f7b\u7684\u82b1\u738b\uff0c\u548c\u5f20\u96ea\u6ce2\u7684\u4ea4\u60c5\u6ca1\u6709\u8001\u4f5f\u548c\u5979\u7684\u597d\u3002\u4ed6\u53d7\u592b\u4eba\u91cd\u7528\u7684\u7a0b\u5ea6\u4e5f\u4e0d\u53ca\u8001 \u4f5f\u3002\u8001\u4f5f\u4e0d\u63a5\u94f6\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u592b\u4eba\u65e0\u7aef\u7ed9\u6211\u8fd9\u5305\u94f6\u5b50\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u8bf4\u662f\u7ed9\u4f60\u56de\u4e61\u517b\u8001\u7684\u3002\u8001\u4f55\u5728\u6545\u4e61\u597d\u50cf\u6ca1\u6709\u4eb2\u4eba\u4e86\uff0c\u4f46\u4ed6\u53ef\u4ee5\u62ff\u8fd9\u70b9\u94f6\u5b50\u53e6\u505a\u8425 \u751f\u3002\u201d\u98d8\u9999\u9053\u3002 \u3000\u3000\u201c\u592b\u4eba\u4e0d\u8981\u6211\u4eec\u4e86\u5417\uff1f\u201c\u8001\u4f5f\u95ee\u9053\u3002 \u3000\u3000\u201c\u4e0d\u662f\u3002\u53ea\u662f\u592b\u4eba\u5df2\u7ecf\u79bb\u5f00\u6b64\u5730\u4e86\u3002\u5979\u8bf4\u5979\u5f88\u62b1\u6b49\uff0c\u8fd9\u6b21\u5979\u662f\u4e0d\u80fd\u5e26\u4f60\u4eec\u4e00\u8d77\u8d70\u4e86\u3002\u201d \u98d8\u9999\u9053\u3002\u8001\u4f5f\u4e0d\u63a5\u94f6\u5b50\uff0c\u5979\u628a\u90a3\u4e24\u5305\u94f6\u5b50\u6401\u5728\u51e0\u4e0a\u3002 \u3000\u3000\u592b\u4eba\u5df2\u7ecf\u8d70\u4e86\uff01\u8fd9\u4e2a\u6d88\u606f\u597d\u50cf\u6674\u5929\u9739\u96f3\uff0c\u628a\u5f20\u96ea\u6ce2\u548c\u8001\u4f5f\u9707\u5f97\u5446\u4e86\u3002 \u3000\u3000\u201c\u592b\u4eba\uff0c\u5979\u3001\u5979\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d\u5f20\u96ea\u6ce2\u4e00\u5446\u4e4b\u540e\uff0c\u5931\u58f0\u53eb\u9053\u3002 \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u98d8\u9999\u677f\u7740\u8138\u8bf4\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u7701\u609f\uff0c\u8fd9\u8bdd\u4e0d\u662f\u5979\u5e94\u8be5\u95ee\u7684\u3002\u4f46\u6b64\u65f6\u5979\u5b9e\u5728\u662f\u5fe7\u6025\u4ea4\u52a0\uff0c\u5df2\u662f\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\u3002 \u3000\u3000\u201c\u592b\u4eba\u662f\u4e0a\u54ea\u513f\uff0c\u98d8\u9999\u59ca\u4f60\u77e5\u9053\u5417\uff1f\u201d\u5f20\u96ea\u6ce2\u518d\u95ee\u3002 \u3000\u3000\u98d8\u9999\u8138\u4e0a\u6d6e\u73b0\u4e00\u4e1d\u601c\u60af\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u6ca1\u6709\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u8981\u53bb\u54ea\u91cc\u3002\u6211 \u53ea\u80fd\u628a\u592b\u4eba\u8bf4\u7684\u8bdd\u8f6c\u544a\u4f60\u3002\u201d\u5979\u62ff\u51fa\u7b2c\u4e09\u5305\u94f6\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u5170\u59d1\uff0c\u8fd9\u5305\u94f6\u5b50\u662f\u7ed9\u4f60\u7684\uff0c\u4f60\u53ef \u8981\u9547\u5b9a\u4e00\u4e9b\uff0c\u542c\u6211\u8f6c\u8ff0\u592b\u4eba\u7684\u8bdd\u3002\u7b2c\u4e00\u3001\u592b\u4eba\u8981\u4f60\u4eec\u6bcd\u5b50\u79bb\u5f00\u6b64\u5730\uff0c\u8d8a\u5feb\u8d8a\u597d\uff01\u7b2c\u4e8c\u3001\u592b\u4eba \u53eb\u6211\u4ee3\u5979\u5411\u4f60\u9053\u6b49\uff01\u201d\u9053\u6b49\uff1f\u5c06\u519b\u592b\u4eba\u5411\u4e00\u4e2a\u5973\u4ec6\u9053\u6b49\uff0c\u8fd9\u3001\u8fd9\u8bdd\u4ece\u4f55\u8bf4\u8d77\uff1f\u201c\u98d8\u9999\u59ca\uff0c\u4f60 \u4e0d\u662f\u8bf4\u7b11\u5427\uff0c\u8fd9\u6211\u600e\u4e48\u62c5\u5f53\u5f97\u8d77\uff1f\u2014\u2014\u201d\u98d8\u9999\u4f3c\u4e4e\u4e0d\u77e5\u9053\u600e\u4e48\u63aa\u8f9e\u624d\u597d\uff0c\u8e0c\u8e87\u7247\u523b\uff0c\u65b9\u59cb\u628a \u771f\u76f8\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u4e8b\u51fa\u4ed3\u5352\uff0c\u592b\u4eba\u6765\u4e0d\u53ca\u5f81\u6c42\u4f60\u7684\u540c\u610f\u4e86\u3002\u4f46\u5979\u7b54\u5e94\uff0c\u4e00\u5b9a\u4f1a\u628a\u4f60\u7684\u5973\u513f\u5f53\u4f5c\u4eb2 \u751f\u4e00\u6837\uff0c\u5c06\u5979\u629a\u517b\u6210\u4eba\u3002\u201d\u5f20\u96ea\u6ce2\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u6211\u7684\u5973\u513f\uff0c\u96be\u9053\u592b\u4eba\u5df2 \u7ecf\u3001\u5df2\u7ecf\u2014\u2014\u201d \u3000\u3000\u98d8\u9999\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u5973\u513f\uff0c\u592b\u4eba\u5df2\u7ecf\u5e26\u8d70\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8fd8\u6ca1\u627e\u5230\u513f\u5b50\uff0c\u5982\u4eca\u53c8\u542c\u5f97\u5973\u513f\u88ab\u4eba\u5e26\u8d70\uff0c\u5982\u4f55\u4e0d\u6025\uff1f\u5373\u4f7f\u5979\u5bf9\u592b\u4eba\u6781\u5177\u597d\u611f\uff0c \u4e5f\u76f8\u4fe1\u5f97\u8fc7\u592b\u4eba\u4e0d\u4f1a\u8650\u5f85\u5979\u7684\u5973\u513f\uff0c\u5979\u4e5f\u662f\u4e0d\u80fd\u51b7\u9759\u4e0b\u6765\u7684\u4e86\u3002 \u3000\u3000\u201c\u592b\u4eba\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u7684\u5973\u513f\u5e26\u8d70\uff0c\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u6211\u8981\u95ee\u95ee\u5979\u53bb\uff0c\u6211\u8981\u95ee\u95ee\u5979\u53bb\uff01 \u3000\u3000\u603b\u7b97\u5979\u8fd8\u80fd\u591f\u7a0d\u7a0d\u4fdd\u6301\u51b7\u9759\uff0c\u6ca1\u6709\u5927\u53eb\u5927\u56b7\uff0c\u4f46\u5979\u4ea6\u5931\u6389\u63a7\u5236\u81ea\u5df1\u7684\u7406\u667a\u4e86\u3002\u5979\u8f6c\u8fc7 \u8eab\uff0c\u7acb\u5373\u51b2\u51fa\u8001\u4f5f\u7684\u5c4b\u5b50\u3002\u201c\u5170\u59d1\uff0c\u5170\u59d1\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u592b\u4eba\u8fd9\u6837\u505a\uff0c\u4e5f\u662f\u4e3a\u4e86\u4f60\u7684\u597d\uff0d\uff0d\u201d \u3000\u3000\u4f46\u5f20\u96ea\u6ce2\u5df2\u662f\u542c\u800c\u4e0d\u95fb\uff0c\u5979\u4e00\u5fc3\u53ea\u662f\u60f3\u53bb\u8ffd\u8d76\u592b\u4eba\u3002 \u3000\u3000\u5979\u6b63\u5728\u72c2\u5954\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u6709\u4eba\u8ffd\u4e86\u4e0a\u6765\uff0c\u62e6\u5728\u5979\u7684\u524d\u9762\u3002\u53ef\u662f\u5979\u8dd1\u5f97\u6b63\u6025\uff0c\u54ea\u80fd\u8bf4 \u505c\u5c31\u505c\uff0c\u800c\u4e14\u5979\u4e00\u5fc3\u5728\u53bb\u8ffd\u8d76\u592b\u4eba\uff0c\u5df2\u662f\u5230\u4e86\u8eab\u4e0d\u7531\u5df1\u7684\u5730\u6b65\u3002 \u3000\u3000\u867d\u7136\u4eba\u6709\u62e6\u4f4f\u5979\uff0c\u5979\u8fd8\u662f\u5411\u524d\u5954\u8dd1\u3002 \u3000\u3000\u90a3\u4eba\u4e5f\u65e9\u5df2\u6599\u5230\u5979\u4f1a\u5982\u6b64\uff0c\u6240\u4ee5\u4e0d\u4ece\u540e\u9762\u62c9\u5979\u3002\uff08\u8fd9\u6837\u505a\u7684\u8bdd\uff0c\u4e24\u80a1\u529b\u9053\u76f8\u53cd\uff0c\u4f1a\u4ee4\u5979 \u53d7\u5230\u5185\u4f24\u7684\u3002\uff09\u4ed6\u4f38\u51fa\u624b\u8f7b\u8f7b\u5c06\u5f20\u96ea\u6ce2\u4e00\u62d6\uff0c\u987a\u7740\u5979\u7684\u524d\u5954\u4e4b\u52bf\u8dd1\u4e86\u51e0\u6b65\uff0c\u8fd9\u624d\u80fd\u591f\u4ee4\u5f20\u96ea \u6ce2\u505c\u4e0b\u6765\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4e00\u770b\uff0c\u8fd9\u4e2a\u4eba\u539f\u6765\u5c31\u662f\u8001\u4f5f\u3002\u5979\u7684\u6b66\u529f\u867d\u7136\u4e0d\u662f\u5f88\u597d\uff0c\u4f46\u603b\u662f\u7ec3\u4e86\u591a\u5e74\u529f\u592b\u7684\uff0c \u4e09\u5e74\u524d\u5979\u5df2\u7ecf\u548c\u731b\u864e\u76f8\u6597\u4e86\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u7684\u5979\uff0c\u6b66\u529f\u6bd4\u8d77\u4e09\u5e74\u524d\u53c8\u9ad8\u4e86\u8bb8\u591a\u7684\u5979\uff0c\u88ab\u8001\u4f5f\u4e00 \u628a\u62c9\u7740\uff0c\u7adf\u662f\u4e0d\u80fd\u4e0d\u6b62\u6b65\u3002 \u3000\u3000\u5979\u8fd9\u624d\u77e5\u9053\uff0c\u8fd9\u4e2a\u8001\u82b1\u738b\u7684\u6b66\u529f\u7adf\u662f\u4e0d\u5f31\u4e8e\u5979\uff0c\u800c\u8fd9\u4e00\u62c9\u4e5f\u597d\u50cf\u5f53\u5934\u4e00\u68d2\uff0c\u4f7f\u5979\u660f\u4e71\u7684 \u5934\u8111\uff0c\u6e05\u9192\u4e86\u51e0\u5206\u3002 \u3000\u3000\u201c\u5170\u59d1\uff0c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u4f60\u7684\u8eab\u4efd\uff0c\u4f46\u6211\u77e5\u9053\u4f60\u7edd\u4e0d\u662f\u666e\u901a\u7684\u5973\u4eba\u3002\u4f60\u8fd9\u6837\u505a\u662f\u5f88\u5371\u9669 \u7684\uff0c\u56e0\u4e3a\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u603b\u4e4b\u5df2\u7ecf\u6709\u4eba\u6000\u7591\u4f60\u7684\u8eab\u4efd\u4e86\uff0c\u4f60\u8fd9\u6837\u505a\u53ea\u80fd\u66b4\u9732\u81ea\u5df1\u7684\u8eab \u4efd\uff0c\u5bb3\u4e86\u4f60\uff0c\u4e5f\u5bb3\u4e86\u4f60\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u201c\u800c\u4e14\u592b\u4eba\u662f\u5750\u9a6c\u8f66\u8d70\u7684\uff0c\u90a3\u8f86\u9a6c\u8f66\u662f\u7528\u56db\u5339\u6700\u597d\u7684\u9a6c\u62c9\u7684\uff01\u5df2\u7ecf\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\u4e86\uff0c\u6211 \u4e0d\u77e5\u4f60\u662f\u5426\u7ec3\u8fc7\u8f7b\u529f\uff0c\u4f46\u5373\u4f7f\u4f60\u6709\u516b\u6b65\u8d76\u8749\u7684\u8f7b\u529f\uff0c\u4f60\u4e5f\u662f\u7edd\u5bf9\u8ffd\u8d76\u4e0d\u4e0a\u90a3\u8f86\u9a6c\u8f66\u7684\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5e76\u4e0d\u662f\u6ca1\u6709\u7406\u667a\u7684\u4eba\uff0c\u5979\u4e5f\u66fe\u7ecf\u5c61\u6b21\u6559\u8bad\u8fc7\u513f\u5b50\uff0c\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u8981\u5fcd\u8fb1\u624d\u80fd\u8d1f\u91cd\uff0c \u6700\u7d27\u8981\u7684\u662f\u4e00\u4e2a\u201c\u5fcd\u201d\u5b57\u3002\u60f3\u4e0d\u5230\u8fc7\u53bb\u5979\u662f\u600e\u6837\u6559\u8bad\u513f\u5b50\u7684\uff0c\u5982\u4eca\u5374\u8981\u522b\u4eba\u6765\u529d\u544a\u5979\u4e86\u3002\u867d \u7136\u7528\u7684\u5b57\u53e5\u4e0d\u5c3d\u76f8\u540c\u3002 \u3000\u3000\u5979\u7fdf\u7136\u4e00\u7701\uff0c\u7ec8\u4e8e\u51b7\u9759\u4e0b\u6765\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5df2\u7ecf\u6709\u4eba\u6000\u7591\u6211\u7684\u8eab\u4efd\uff1f\u201c\u5979\u4f4e\u58f0\u95ee\u8001\u4f5f\u3002 \u3000\u3000\u201c\u4ece\u592b\u4eba\u8981\u98d8\u9999\u8f6c\u544a\u4f60\u7684\u90a3\u4e9b\u8bf4\u8bdd\u4e5f\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\u3002\u592b\u4eba\u8bf4\uff0c\u5979\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u4f60\u7684\u597d\u3002 \u5979\u8981\u4f60\u4eec\u6bcd\u5b50\u8d76\u5feb\u9003\u8d70\uff0c\u4f46\u4e16\u4e8b\u96be\u6d4b\uff0c\u5979\u4e5f\u4e0d\u80fd\u4e0d\u4e3a\u4f60\u4eec\u4f5c\u6700\u574f\u7684\u6253\u7b97\uff0c\u5979\u5e26\u8d70\u4f60\u7684\u5973\u513f\uff0c \u4e00\u65b9\u9762\u56fa\u7136\u662f\u56e0\u4e3a\u5979\u559c\u6b22\u4f60\u7684\u5973\u513f\uff0c\u53e6\u4e00\u65b9\u9762\u5c11\u4e5f\u53ef\u4ee5\u4fdd\u5168\u4f60\u7684\u4e00\u4e2a\u5b69\u5b50\uff01\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u5f97\u975e\u5e38\u59d4\u5a49\uff0c\u610f\u601d\u5176\u5b9e\u5c31\u662f\u6050\u6015\u5979\u4eec\u6bcd\u5b50\u4f1a\u6709\u6740\u8eab\u4e4b\u7978\uff0c\u56e0\u6b64\u624d\u8981\u8bbe\u6cd5\u4fdd\u5168\u5979 \u7684\u4e00\u4e2a\u5973\u513f\u7684\u6027\u547d\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5f53\u7136\u4e5f\u660e\u767d\u81ea\u8eab\u7684\u5904\u5883\u4e4b\u5371\uff0c\u611f\u52a8\u5f97\u6d41\u4e0b\u773c\u6cea\uff0c\u201c\u6211\u660e\u767d\u592b\u4eba\u7684\u82e6\u5fc3\uff0c\u521a\u624d\u6211\u662f \u9519\u602a\u5979\u4e86\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u4f9d\u6211\u731c\u60f3\uff0c\u592b\u4eba\u6050\u6015\u4ea6\u65e9\u5df2\u77e5\u9053\u4f60\u4e0d\u662f\u5bfb\u5e38\u7684\u5973\u5b50\u4e86\uff0c\u6216\u8bb8\u4f60\u4e0d\u77e5\u9053\uff0c\u592b \u4eba\u4e5f\u662f\u61c2\u5f97\u6b66\u529f\uff0c\u6211\u90fd\u770b\u5f97\u51fa\u6765\u3002\u592b\u4eba\u5f53\u7136\u66f4\u52a0\u770b\u5f97\u51fa\u6765\u3002\u4f46\u73b0\u5728\u65f6\u673a\u7d27\u8feb\uff0c\u6211\u4e5f\u4e0d\u60f3\u77e5\u9053 \u4f60\u662f\u4ec0\u4e48\u6765\u5934\u4e86\u3002\u592b\u4eba\u53eb\u4f60\u8d70\uff0c\u4f60\u8d76\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u8d70\uff01\u201c\u5f20\u96ea\u6ce2\u575a\u51b3\u8bf4\u9053\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u7684\u5b69\u5b50\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u6211\u4e0d\u80fd\u629b\u4e0b\u5b69\u5b50\u4e0d\u7ba1\uff0c\u662f\u751f\u662f\u6b7b\uff0c\u6211\u90fd\u8981\u7b49\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u66ff\u4f60\u7b49\u4ed6\u56de\u6765\uff0c\u6211\u4f1a\u5c3d\u6211\u7684\u529b\u91cf\u5e2e\u4ed6\u9003\u8d70\u7684\u3002\u201d \u3000\u3000\u201c\u592b\u4eba\u4e0d\u662f\u4e5f\u53eb\u4f60\u79bb\u5f00\u6b64\u5730\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u66f4\u52a0\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5df2\u7ecf\u77e5\u9053\u592b\u4eba\u4e3a\u4ec0\u4e48\u8981\u8d70\u7684\u539f\u56e0\u4e86\u3001\u592b\u4eba\u975e\u8d70\u4e0d\u53ef\uff0c\u6211\u662f\u975e\u7559\u4e0d\u53ef\uff0c\u90fd\u662f\u4e3a\u4e86 \u540c\u4e00\u539f\u56e0\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5f53\u7136\u4e0d\u61c2\uff0c\u770b\u7740\u4ed6\u53d1\u695e\u3002 \u3000\u3000\u8001\u4f5f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u56e0\u4f55\u8981\u8d70\uff0c\u9664\u4e86\u8981\u907f\u5f00\u4e00\u4e2a\u4eba\u4e4b\u5916\uff0c\u6211\u60f3\u4e0d\u51fa\u522b\u7684\u539f \u56e0\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u731c\u5230\u51e0\u5206\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u662f\u6211\u7684\u65e7\u4e3b\u4eba\u3002\u4e09\u5341\u5e74\u524d\uff0c\u4ed6\u3001\u4ed6\u4eec\u2014\u2014\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u6837\uff1f\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5728\u4e00\u4e2a\u5730\u65b9\u957f\u5927\u7684\u3002\u201d\u4f3c\u662f\u6b32\u8bf4\u8fd8\u4f11\uff0c\u795e\u8272\u9887\u4e3a\u5f02\u6837\u3002 \u3000\u3000\u201c\u5728\u4e00\u4e2a\u5730\u65b9\u957f\u5927\u201d\u53c8\u600e\u80fd\u6210\u4e3a\u8981\u8eb2\u907f\u4ed6\u7684\u539f\u56e0\uff1f\u4f46\u5f20\u96ea\u6ce2\u7528\u4e0d\u7740\u4ed6\u753b\u86c7\u6dfb\u8db3\uff0c\u5df2\u662f\u5fc3 \u4e2d\u96ea\u4eae\u4e86\u3002 \u3000\u3000\u8001\u4f5f\u795e\u8272\u9eef\u7136\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u4ed6\u4eec\u51e0\u4e4e\u5929\u5929\u89c1\u9762\uff0c\u4f46\u4e09\u5341\u5e74\u7684\u53d8\u5316\u5b9e\u5728\u592a\u5927\uff0c\u4ee5\u592b \u4eba\u4eca\u5929\u7684\u8eab\u4efd\uff0c\u5f53\u7136\u662f\u4e0d\u5b9c\u518d\u89c1\u4ed6\u4e86\uff0c\u4f46\u6211\u5374\u662f\u975e\u89c1\u4ed6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5ffd\u9053\uff1a\u201c\u4f60\u7684\u4e3b\u4eba\u662f\u4e0d\u662f\u8fbd\u56fd\u7684\u738b\u5b50\uff1f\u201d \u3000\u3000\u8001\u4f5f\u5927\u5403\u60ca\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u521a\u624d\u6211\u5728\u5929\u9999\u4ead\u90a3\u8fb9\uff0c\u5077\u542c\u4ed6\u4eec\u8bf4\u7684\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u6b63\u5728\u5e03\u7f6e\u9677\u9636\uff0c \u7b49\u5f85\u4f60\u7684\u4e3b\u4eba\u81ea\u6295\u7f57\u7f51\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u6211\u7684\u65e7\u4e3b\u4eba\u662f\u91d1\u56fd\u7687\u5e1d\u7684\u773c\u4e2d\u9489\uff0c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\u54c8\u5fc5\u56fe\u5b8c\u989c\u9274\u662f\u7edd\u4e0d\u80fd \u653e\u8fc7\u4ed6\u7684\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u5728\u8fd9\u6837\u60c5\u5f62\u5e95\u4e0b\uff0c\u4f60\u8fd8\u8981\u53bb\u8ba4\u65e7\u4e3b\u4eba\u4e48\uff1f\u201d\u5f26\u5916\u4e4b\u97f3\uff0c\u4f3c\u4e4e\u89c9\u5f97\u4ed6\u8fd9\u6837\u7684 \u201c\u611a\u5fe0\u201d\uff0c\u672a\u514d\u6709\u70b9\u8fc7\u4efd\u3002\u56e0\u4e3a\u5728\u8fd9\u6837\u60c5\u5f62\u5e95\u4e0b\uff0c\u53bb\u8ba4\u8eab\u4e3a\u94a6\u72af\u7684\u4e3b\u4eba\uff0c\u662f\u6781\u53ef\u80fd\u6709\u6740\u8eab\u4e4b \u7978\u7684\u3002\u8001\u4f5f\u53f9\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u53ea\u662f\u5c3d\u2018\u5fe0\u4ec6\u2019\u7684\u672c\u4efd\u4e48\uff0c\u4f60\u9519\u4e86\uff01\u6211\u662f\u770b\u7740\u4ed6\u957f\u5927\u7684\uff0c\u6216\u8bb8 \u4f60\u4f1a\u7b11\u6211\u4e0d\u77e5\u81ea\u91cf\uff0c\u4f46\u6211\u786e\u5b9e\u5bf9\u4ed6\u6709\u4e00\u4efd\u5bb6\u4eba\u7684\u611f\u60c5\uff0c\u800c\u4e14\u8bf4\u6765\u4f60\u90fd\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4ed6\u628a\u6211\u9001\u7ed9 \u592b\u4eba\u7684\u90a3\u5e74\uff0c\u867d\u7136\u4ed6\u4e0d\u8fc7\u5341\u516b\u5c81\uff0c\u4f46\u6211\u5bf9\u4ed6\u5df2\u6709\u77e5\u5df1\u4e4b\u611f\uff0c\u56e0\u4e3a\u4ed6\u6700\u61c2\u5f97\u6b23\u8d4f\u6211\u79cd\u7684\u7261\u4e39\uff0c \u53e4\u4eba\u4e91\u58eb\u4e3a\u77e5\u5df2\u8005\u6b7b\uff0c\u4f55\u51b5\u4ed6\u8fd8\u662f\u6211\u7684\u4e3b\u4eba\uff01\u201d\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u61c2\u3002\u4f46\u4f60\u53ef\u77e5\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80af \u53bb\u4e48\uff1f\u9664\u4e86\u6211\u4e0d\u80fd\u629b\u5f00\u5b69\u5b50\u4e0d\u7ba1\u4e4b\u5916\uff0c\u4e3a\u7684\u4e5f\u662f\u8981\u7b49\u4f60\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u8001\u4f5f\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4ece\u672a\u89c1\u8fc7\u3002\u751a\u81f3\u8fde\u4ed6\u7684\u540d\u5b57\u6211\u4e5f\u4e0d\u77e5\uff0c\u53ea\u77e5\u4ed6\u662f\u590d\u59d3\u8036\u5f8b\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u8981\u7b49\u4ed6\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e08\u592b\u662f\u6a80\u8d1d\u5b50\uff01\u201d \u3000\u3000\u8001\u4f5f\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff1f\u91d1\u56fd\u4eb2\u738b\u79f0\u4e3a\u8d1d\u52d2\uff0c\u53ea\u6709\u8d1d\u52d2\u7684\u513f\u5b50\u79f0\u4e3a\u8d1d\u5b50\u7684\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u8001\u4f5f\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u5168\u56fd\u53ea\u6709\u4e00\u4e2a\u6a80\u8d1d\u52d2\uff0c\u5c31\u662f\u66fe\u7ecf\u505a\u8fc7\u5175\u9a6c\u5927\u5143\u5e05\u7684 \u6a80\u516c\u76f4\u3002\u6a80\u516c\u76f4\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u662f\u6211\u7684\u516c\u516c\u3002\u4f5f\u5e08\u5085\u3002\u5982\u4eca\u6211\u4e5f\u4e0d\u6015\u544a\u8bc9\u4f60\u4e86\uff0c\u6211\u662f\u56e0\u4e3a\u516c\u516c\u548c\u4e08\u592b\u90fd\u5df2 \u7ecf\u7ed9\u91d1\u56fd\u7684\u7687\u5e1d\u5bb3\u6b7b\uff0c\u6211\u624d\u843d\u5230\u8fd9\u822c\u7530\u5730\u7684\u3002\u201d\u8001\u4f5f\u9053\uff1a\u201c\u539f\u6765\u4f60\u7684\u516c\u516c\u662f\u6a80\u8d1d\u52d2\uff0c\u8fd9\u5c31\u602a \u4e0d\u5f97\u4f60\u60f3\u89c1\u6211\u7684\u4e3b\u4eba\u4e86\u3002\u636e\u6211\u6240\u77e5\uff0c\u6211\u7684\u4e3b\u4eba\u548c\u4f60\u7684\u516c\u516c\u4e43\u662f\u5fd8\u5e74\u4e4b\u4ea4\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u7684\u4e3b\u4eba\u4e0d\u4ec5\u662f\u6211\u7684\u516c\u516c\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u8fd8\u662f\u6211\u513f\u7684\u5e08\u7236\u3002\u867d\u7136\u4ed6\u6ca1\u6709\u89c1\u8fc7 \u6211\u7684\u51b2\u513f\uff0c\u4f46\u4ed6\u5df1\u662f\u7b54\u5e94\u4e86\u6211\u7684\u516c\u516c\u6536\u51b2\u513f\u4e3a\u5f92\u4e86\u3002\u8001\u4f5f\u6062\u590d\u5e73\u9759\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u59d1\uff0c\u8bf7 \u4f60\u542c\u6211\u529d\u544a\uff0c\u4f60\u8fd8\u662f\u8d70\u5427\uff01\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u6211\u90fd\u53ef\u4ee5\u66ff\u4f60\u505a\u7684\u3002\u6211\u4f1a\u628a\u4f60\u7684\u6d88\u606f\u544a\u8bc9\u4ed6\uff0c\u4ed6\u672c\u4e8b \u5f88\u5927\uff0c\u4f60\u8981\u627e\u4ed6\u5f88\u96be\uff0c\u4ed6\u8981\u627e\u4f60\u5374\u6613\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u8ba9\u4f60\u4e00\u4e2a\u4eba\u53bb\u5192\u6027\u547d\u4e4b\u5371\uff01\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u6211\u4e0d\u540c\u3002\u6211\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u5355\u8eab\u4e00\u4e2a\uff0c\u6765\u53bb\u65e0\u7275\u6302\uff0c\u4f60\u5e74\u7eaa\u8f7b\uff0c\u6709\u513f\u6709 \u5973\uff0c\u4f60\u4e0d\u4e3a\u81ea\u5df1\u7740\u60f3\uff0c\u4e5f\u5f97\u4e3a\u513f\u5973\u7740\u60f3\u3002\u800c\u4e14\u6211\u4e0d\u4e00\u5b9a\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u54c4\u6211\u3002\u4e0d\u9519\uff0c\u6211\u77e5\u9053\u4f60\u7684\u4e3b\u4eba\u6b66\u529f\u5f88\u9ad8\uff0c\u4f46\u4ee5\u4e00\u654c\u767e\uff0c\u53ea\u6015\u4ed6\u4e5f\u662f \u81ea\u987e\u4e0d\u6687\u3001\u9664\u975e\u5c06\u519b\u4e0d\u77e5\u9053\u4f60\u548c\u4ed6\u7684\u5173\u7cfb\uff0c\u5426\u5219\u4f60\u7684\u4e3b\u4eba\u7eb5\u7136\u8131\u9669\uff0c\u4f60\u5374\u662f\u96be\u4fdd\u6027\u547d\u4e86\uff01\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u60f3\u8ba9\u5c06\u519b\u77e5\u9053\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4f46\u4f60\u53c8\u8bf4\u975e\u89c1\u4e3b\u4eba\u4e0d\u53ef\uff0c\u4f60\u5728\u8fd9\u91cc\u7684\u8eab\u4efd\u4e0d\u8fc7\u662f\u82b1\u5320\uff0c\u516c\u7136\u9732\u9762\u53bb\u8ba4\u65e7 \u4e3b\uff0c\u8fd9\uff0c\u8fd9\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u6211\u4e0d\u4e00\u5b9a\u8981\u5728\u5e9c\u8859\u89c1\u4ed6\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u773c\u775b\u4e00\u4eae\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u77e5\u9053\u4ed6\u8fdf\u65e9\u8981\u6765\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u8001\u4f5f\u53ea\u597d\u628a\u81ea\u5df1\u7684\u8ba1\u5212\u8bf4\u51fa\u6765\uff1a\u201c\u6211\u4f30\u8ba1\u4e3b\u4eba\u6700\u65e9\u4e5f\u8981\u660e\u5929\u624d\u6765\uff0c\u660e\u5929\u6211\u5728\u4ed6\u6765\u8fd9\u91cc\u7684 \u5fc5\u7ecf\u4e4b\u9053\u7b49\u4ed6\uff0c\u544a\u8bc9\u4ed6\u592b\u4eba\u5df2\u7ecf\u8d70\u4e86\uff0c\u6211\u60f3\u4ed6\u662f\u4e0d\u4f1a\u518d\u5230\u8fd9\u5e9c\u8859\u6765\u7684\u3001\u4f46\u5c06\u519b\u5fd9\u4e8e\u5e03\u7f6e\u4eba\u9a6c \u53bb\u5e94\u4ed8\u4ed6\uff0c\u4eca\u5929\u665a\u4e0a\u5c31\u672a\u5fc5\u6709\u7a7a\u5ba1\u95ee\u4ee4\u90ce\u4e86\u3002\u6240\u4ee5\u4f60\u73b0\u5728\u5148\u8d70\uff0c\u5f85\u665a\u4e0a\u4ee4\u90ce\u56de\u6765\uff0c\u6211\u8fd8\u53ef\u4ee5 \u5e2e\u4ed6\u9003\u8d70\u3002\u8fd9\u4e0d\u662f\u4e24\u5168\u5176\u7f8e\u5417\uff1f\u201d \u3000\u3000\u8001\u4f5f\u7684\u7b97\u76d8\u6253\u5f97\u5f88\u5982\u610f\uff0c\u53ef\u60dc\u4e8b\u60c5\u7684\u53d1\u5c55\u5374\u662f\u4e0d\u5982\u4ed6\u6240\u6599\u3002 \u3000\u3000XXX\u5173\u952e\u95ee\u9898\u5728\u4e8e\u65f6\u95f4\u7684\u5224\u65ad\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5df2\u7ecf\u6253\u542c\u5230\u786e\u5b9e\u7684\u6d88\u606f\uff0c\u8036\u5f8b\u7384\u5143\u662f\u6628\u5929\u65b9\u59cb\u51fa\u5173\uff08\u5927\u6563\u5173\uff09 \u3000\u3000\u7684\uff0c\u56e0\u6b64\u4ed6\u5224\u65ad\u8036\u5f8b\u7384\u5143\u5373\u4f7f\u8981\u6765\uff0c\u6700\u65e9\u4e5f\u5f97\u660e\u5929\u624d\u5230\u3002 \u3000\u3000\u5224\u65ad\u6839\u636e\u662f\uff1b\u8036\u5f8b\u7384\u5143\u6700\u5c11\u4e5f\u5f97\u6709\u4e00\u5929\u7684\u65f6\u95f4\u6765\u505a\u51c6\u5907\u5de5\u592b\u3002\u4ed6\u6628\u5929\u624d\u51fa\u5927\u6563\u5173\uff0c\u7eb5\u7136 \u517c\u7a0b\u8d76\u8def\uff0c\u5728\u8def\u4e0a\u4e5f\u8981\u82b1\u4e00\u5929\u65f6\u95f4\uff0c\u4e0d\u53ef\u80fd\u4eca\u5929\u4e00\u5230\u5546\u5dde\uff0c\u4fbf\u7acb\u5373\u76f4\u5954\u8282\u5ea6\u8859\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u548c\u8001\u4f5f\u7684\u60f3\u6cd5\u4e5f\u662f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u6839\u636e\u8fd9\u4e2a\u5224\u65ad\u6765\u51b3\u5b9a\u4ed6\u4eec\u7684\u505a\u6cd5\u3002 \u3000\u3000\u8001\u4f5f\u51b3\u5b9a\u62bc\u540e\u4e00\u5929\u624d\u8d70\uff0c\u4e3a\u7684\u662f\u8981\u7b49\u5f85\u4ed6\u7684\u65e7\u4e3b\u4eba\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u51b3\u5b9a\u63d0\u524d\u4e00\u5929\u79bb\u5f00\u5546\u5dde\uff0c\u4e3a\u7684\u662f\u8981\u907f\u514d\u4e0e\u8036\u5f8b\u7384\u5143\u89c1\u9762\u3002 \u3000\u3000\u5979\u662f\u63d0\u524d\u79bb\u5f00\uff0c\u5f53\u7136\u8fd8\u4e0d\u4ec5\u4ec5\u53ea\u662f\u4e3a\u4e86\u9003\u907f\u3002\u800c\u662f\u4e3a\u4e86\u9632\u6b62\u96be\u4ee5\u9884\u6d4b\u7684\u707e\u7978\u53d1\u751f\u3002 \u3000\u3000\u7eb8\u662f\u5305\u4e0d\u4f4f\u706b\u7684\uff0c\u5c06\u519b\u592b\u4eba\u7a81\u7136\u51fa\u8d70\uff0c\u8fd9\u6837\u60ca\u4eba\u7684\u6d88\u606f\u662f\u4e00\u5b9a\u63a9\u76d6\u4e0d\u4e86\u7684\u3002\u5b8c\u989c\u592b\u4eba\u4e58 \u9a6c\u8f66\u51fa\u8d70\uff0c\u7528\u6765\u62c9\u8f66\u7684\u9a6c\u662f\u4e08\u592b\u6240\u755c\u7684\u4e00\u5339\u540d\u9a79\u3002\u4e0d\u9519\uff0c\u5979\u79bb\u5f00\u7684\u65f6\u5019\u6ca1\u6709\u544a\u8bc9\u4e08\u592b\uff0c\u4f46\u8fd9 \u6837\u201c\u5802\u800c\u7687\u4e4b\u201d\u7684\u51fa\u8d70\uff0c\u6d88\u606f\u5f53\u7136\u5f88\u5feb\u5c31\u4f1a\u4f20\u5f00\u3001\u5979\u4e5f\u6b63\u662f\u60f3\u8981\u6d88\u606f\u5f88\u5feb\u4f20\u5f00\u3002\u5230\u4e86\u660e\u5929\uff0c \u5916\u9762\u7684\u4eba\uff0c\u6599\u60f3\u4e5f\u90fd\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u8981\u662f\u77e5\u9053\u5979\u5df2\u7ecf\u79bb\u5f00\u5546\u5dde\uff0c\u6599\u60f3\u4ed6\u4e5f\u4e0d\u4f1a\u518d\u5230\u8282\u5ea6\u4f7f\u7684\u8859\u95e8\u6765\u4e86\u3002 \u3000\u3000\u592b\u4eba\u8fd9\u6837\u60f3\u6cd5\uff0c\u8001\u4f5f\u4e5f\u662f\u8fd9\u6837\u60f3\u6cd5\u3002\u8001\u4f5f\u8fd8\u4f5c\u4e86\u4e07\u4e00\u7684\u51c6\u5907\uff0c\u51c6\u5907\u8036\u5f8b\u7384\u5143\u4e07\u4e00\u5c1a\u672a\u77e5 \u9053\u8fd9\u4e2a\u6d88\u606f\uff0c\u660e\u5929\u4e00\u65e9\u4ed6\u5c31\u5728\u8036\u5f8b\u7384\u5143\u5fc5\u7ecf\u7684\u8def\u4e0a\u622a\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u60f3\u6cd5\u662f\u5bf9\uff0c\u53ef\u60dc\u65f6\u95f4\u7684\u5224\u65ad\u5374\u662f\u9519\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8001\u4f5f\u548c\u5f20\u96ea\u6ce2\u8bf4\u8bdd\u65f6\u5019\uff0c\u8036\u5f8b\u7384\u5143\u5df2\u7ecf\u8fdb\u5165\u5e9c\u8859\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u79bb\u5f00\u5e9c\u8859\u8fd8\u672a\u5230\u4e00\u4e2a\u65f6\u8fb0\u3002\u6b64\u65f6\u5b8c\u989c\u9274\u8fd8\u5728\u5929\u9999\u4ead\u4e0e\u54c8\u5fc5\u56fe\u5bc6\u5546\uff0c\u4ed6\u7684\u624b\u4e0b\u4e5f \u8fd8\u672a\u6562\u628a\u592b\u4eba\u79c1\u81ea\u51fa\u8d70\u7684\u6d88\u606f\u7980\u544a\u4ed6\u3002 \u3000\u3000XXX\u5b8c\u989c\u9274\u5df2\u7ecf\u548c\u54c8\u5fc5\u56fe\u8bae\u5b9a\u5bf9\u4ed8\u8036\u5f8b\u7384\u5143\u7684\u529e\u6cd5\uff0c\u6b63\u51c6\u5907\u8c03\u5175\u9063\u5c06\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u5916 \u9762\u4e71\u54c4\u54c4\u7684\u4e00\u7247\u547c\u559d\u58f0\uff1a\u201c\u4ec0\u4e48\u4eba\u80c6\u6562\u4e71\u95ef\uff1f\u201d\u201c\u6709\u523a\u5ba2\uff0c\u5feb\u6765\u4eba\u5440\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5df2\u7ecf\u95ef\u8fdb\u82b1\u56ed\u4e86\uff01 \u3000\u3000\u53ea\u542c\u5f97\u8036\u5f8b\u7384\u5143\u6c89\u58f0\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u5f00\uff01\u201d\u4e5f\u4e0d\u89c1\u4eba\u52a8\u624b\uff0c\u4e24\u540d\u62e6\u963b\u4ed6\u7684\u536b\u58eb\u5df2\u662f\u8eab\u4e0d \u7531\u5df2\u8ddf\u8e09\u8dc4\u8dc4\u7684\u9000\u51fa\u4e86\u516d\u4e03\u6b65\u3002\u9000\u51fa\u4e86\u516d\u4e03\u6b65\u8fd8\u672a\u80fd\u7a33\u4f4f\u8eab\u5f62\uff0c\u597d\u50cf\u88ab\u4e00\u53ea\u65e0\u5f62\u7684\u9b54\u624b\u7275\u626f \u4f3c\u7684\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u4e24\u4e2a\u76d8\u65cb\uff0c\u535c\u901a\uff0c\u535c\u901a\u5c31\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7528\u7684\u662f\u4e00\u79cd\u4ee5\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u53d1\u51fa\u6765\u7684\u201c\u72ee\u5b50\u543c\u201d\u529f\u3002\u72ee\u5b50\u543c\u529f\u662f\u4f5b\u95e8\u7684\u4e0a\u4e58\u5185 \u529f\uff0c\u72ee\u5b50\u4e00\u543c\uff0c\u767e\u517d\u6151\u670d\uff0c\u9ad8\u618e\u800c\u4f5c\u201c\u72ee\u5b50\u543c\u201d\uff0c\u5219\u662f\u4e07\u9b54\u8f9f\u6613\u4e86\u3002\u4e0d\u8fc7\u4f20\u81ea\u5929\u7afa\u7684\u201c\u72ee\u5b50 \u543c\u529f\u201d\u662f\u58f0\u5982\u9739\u96f3\u7684\uff0c\u8036\u5f8b\u7384\u5143\u7684\u201c\u72ee\u5b50\u543c\u529f\u201d\u58f0\u97f3\u4f4e\u6c89\u3002\u90a3\u662f\u56e0\u4e3a\u4ed6\u4e0d\u613f\u591a\u4f24\u65c1\u4eba\uff0c\u52a0\u4e0a \u201c\u4f20\u97f3\u4eba\u5bc6\u201d\u7684\u529f\u592b\u4e4b\u6545\u3002\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u53ef\u4ee5\u628a\u58f0\u97f3\u51dd\u6210\u4e00\u7ebf\uff0c\u4ed6\u8981\u8bf4\u7ed9\u8c01\u542c\uff0c\u5c31\u4f20\u5165\u8c01\u7684 \u8033\u9f13\u3002\u8fd9\u79cd\u529f\u592b\uff0c\u7ec3\u5230\u6700\u4e0a\u4e58\u5883\u754c\uff0c\u53ef\u4ee5\u4f20\u5230\u4e8c\u4e09\u91cc\u5916\u3002\u201c\u72ee\u5b50\u543c\u529f\u201d\u800c\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684 \u529f\u592b\u53d1\u51fa\uff0c\u58f0\u97f3\u867d\u4e0d\u5b8f\u4eae\uff0c\u4f46\u56e0\u58f0\u97f3\u201c\u51dd\u7ed3\u201d\uff0c\u529f\u6548\u66f4\u5927\u3002\u8fd9\u4e24\u540d\u536b\u58eb\u5728\u4ed6\u4e00\u201c\u543c\u201d\u4e4b\u4e0b\uff0c \u5fc3\u8109\u5df1\u53d7\u9707\u4f24\uff0c\u6545\u800c\u7ec8\u4e8e\u652f\u6301\u4e0d\u4f4f\u3002\uff3f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u5df2\u7ecf\u96be\u7ec3\uff0c\u201c\u72ee\u5b50\u543c\u529f\u201d\u66f4\u52a0\u96be\u7edf\uff0c\u4e24 \u79cd\u4e0a\u4e58\u7684\u5185\u529f\u8fd8\u5728\u878d\u5408\u4e3a\u4e00\uff0c\u90a3\u66f4\u662f\u96be\u4e0a\u52a0\u96be\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u5177\u6709\u5982\u6b64\u201c\u795e\u901a\u201d\u7684\u4eba\uff0c\u6050\u6015\u4e5f \u4e0d\u8fc7\u4e09\u4e94\u4e2a\u800c\u5df2\uff01\u5b8c\u989c\u9274\u4e0e\u54c8\u5fc5\u56fe\u90fd\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u89c1\u8036\u5f8b\u7384\u5143\u6296\u9732\u4e86\u8fd9\u4e00\u624b\u4e0a\u4e58\u5185 \u529f\uff0c\u4e0d\u7981\u90fd\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u5176\u4ed6\u7684\u536b\u58eb\u6ca1\u6709\u4ed6\u4eec\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u5374\u662f\u4e0d\u61c2\u5176\u4e2d\u5965\u5999\uff0c\u4ed6\u4eec\u770b\u89c1\u540c\u4f34\u83ab\u540d\u5176\u5999\u7684\u5012\u4e0b \u53bb\uff0c\u8fd8\u53ea\u9053\u8036\u5f8b\u7384\u5143\u662f\u4f7f\u4ec0\u4e48\u201c\u5996\u6cd5\u201d\u3002 \u3000\u3000\u545c\u545c\u58f0\u54cd\uff0c\u8eb2\u5728\u5047\u5c71\u4e0a\u7684\u4e09\u540d\u536b\u58eb\uff0c\u540c\u65f6\u53d1\u51fa\u6697\u5668\u3002\u4e00\u4e2a\u662f\u900f\u9aa8\u9489\uff0c\u4e00\u4e2a\u662f\u8774\u8776\u9556\uff0c\u4e00 \u4e2a\u662f\u6dec\u8fc7\u6bd2\u7684\u94c1\u84ba\u85dc\u3002\u900f\u9aa8\u9489\u548c\u8774\u8776\u9556\u6253\u8036\u5f8b\u7384\u5143\u540e\u5fc3\u7a74\u9053\uff0c\u6bd2\u84ba\u85dc\u6253\u540e\u8111\u7684\u7389\u6795\u7a74\u3002\u4ed6\u4eec \u53ea\u9053\u7528\u6697\u5668\u4f24\u4eba\uff0c\u90a3\u5c31\u5373\u4f7f\u5bf9\u65b9\u771f\u6709\u201c\u5996\u6cd5\u201d\uff0c\u4e2d\u95f4\u9694\u4e86\u4e00\u6bb5\u8ddd\u79bb\uff0c\u4e5f\u53ef\u4ee5\u6bd4\u8f83\u5b89\u5168\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u8036\u5f8b\u7384\u5143\u7684\u6b66\u529f\uff0c\u6bd4\u4ed6\u4eec\u60f3\u50cf\u7684\u201c\u5996\u6cd5\u201d\u8fd8\u66f4\u5389\u5bb3\uff01 \u3000\u3000\u8036\u5f8b\u7384\u5143\u5934\u4e5f\u4e0d\u56de\uff0c\u53ea\u662f\u53cd\u624b\u4e00\u6325\u8863\u8896\uff0c\u4e09\u679a\u6697\u5668\u5168\u90fd\u53cd\u5c04\u56de\u53bb\uff0c\u800c\u4e14\u6070\u597d\u90fd\u662f\u6253\u4e2d\u4e86 \u6697\u5668\u7684\u4e3b\u4eba\uff01 \u3000\u3000\u900f\u9aa8\u9488\u5c04\u5165\u4e86\u7269\u751f\u80cc\u5fc3\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u5f53\u771f\u662f\u540d\u7b26\u5176\u5b9e\uff0c\u900f\u9aa8\u7a7f\u7a74\uff0c\u63d2\u8fdb\u9aa8\u7f1d\u3002 \u3000\u3000\u800c\u8fd9\u4e2a\u4eba\u4e5f\u6b63\u662f\u8981\u6253\u8036\u5f8b\u7384\u5143\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u7684\u3002 \u3000\u3000\u8774\u8776\u9556\u6253\u4e2d\u4e86\u7269\u4e3b\u7684\u201c\u5929\u67f1\u7a74\u201d\uff0c\u540c\u6837\u662f\u7ed9\u8036\u5f8b\u7384\u5143\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u4e2a\u7528\u94c1\u84ba\u85dc\u6253\u8036\u5f8b\u7384\u5143\u540e\u8111\u7389\u6795\u7a74\u7684\u4eba\u66f4\u60e8\u3002 \u3000\u3000\u4ed6\u7684\u94c1\u84ba\u85dc\u662f\u6dec\u8fc7\u6bd2\u7684\uff0c\u540e\u8111\u7389\u6795\u7a74\u53c8\u662f\u81f4\u547d\u7684\u7a74\u9053\uff0c\u5982\u4eca\u7ed9\u8036\u5f8b\u7384\u5143\u5373\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8 \u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u6bd2\u84ba\u85dc\u4e5f\u662f\u521a\u597d\u5c04\u8fc7\u4ed6\u7684\u7389\u6795\u7a74\uff0c\u767b\u65f6\u4e00\u547d\u545c\u547c\uff01 \u3000\u3000\u8036\u5f8b\u7384\u5143\u5934\u4e5f\u4e0d\u56de\uff0c\u5927\u8e0f\u6b65\u8d70\u5411\u5929\u9999\u4ead\u3002 \u3000\u3000\u5f53\u4ed6\u7ecf\u8fc7\u53e6\u5916\u4e00\u5ea7\u5047\u5c71\u4e4b\u65f6\uff0c\u5c71\u6d1e\u91cc\u53c8\u7a9c\u51fa\u4e24\u540d\u536b\u58eb\u3002\u8fd9\u4e24\u4eba\u662f\u5b8c\u989c\u9274\u7684\u968f\u8eab\u4f8d\u536b\uff0c\u6b66 \u529f\u6bd4\u5176\u4ed6\u536b\u58eb\u597d\u5f97\u591a\u3002\u66f4\u96be\u5f97\u7684\u662f\u4ed6\u4eec\u7ec3\u597d\u4e86\u4e00\u5957\u64d2\u62ff\u624b\u6cd5\uff0c\u4e92\u76f8\u914d\u5408\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c \u62ff\u4eba\u5173\u8282\uff0c\u9519\u9aa8\u5206\u7b4b\uff0c\u4e07\u65e0\u4e00\u5931\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u672c\u6765\u662f\u4e0d\u5e94\u8be5\u5728\u80cc\u540e\u5077\u88ad\u7684\uff0c\u4f46\u6b64\u9645\u5df2\u987e\u4e0d\u4e86\u90a3\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u4ecd\u7136\u597d\u50cf\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u9000\u81ea\u524d\u884c\u3002 \u3000\u3000\u9661\u7136\u95f4\u53ea\u542c\u5f97\u60e8\u53eb\u58f0\u58f0\uff0c\u90a3\u4e24\u4eba\u597d\u50cf\u76ae\u7403\u4e00\u6837\u7ed9\u629b\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u624b\u8155\u6298\u65ad\uff0c\u4eba\u7ed9\u629b\u5230\u6570\u4e08\u5f00\u5916\uff0c\u5f53\u771f\u662f\u75db\u5f7b\u5fc3\u80ba\u8981\u5145\u597d\u6c49\u4e5f\u5145\u4e0d\u8d77\u6765\uff0c\u5728\u5730\u4e0a\u6253 \u6eda\uff0c\u6740\u732a\u822c\u5730\u72c2\u53f7\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u7528\u7684\u662f\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u4e0a\u4e58\u5185\u529f\u3002\u8fd9\u79cd\u5185\u529f\uff0c\u82e5\u662f\u6bd4\u8d77\u4ed6\u521a\u624d\u6240\u7528\u7684\u72ee\u5b50\u543c\u529f\uff0c \u5176\u5b9e\u8fd8\u662f\u7a0d\u900a\u4e00\u7b79\u7684\uff0c\u4e0d\u8fc7\u53d7\u8005\u7684\u60e8\u72b6\uff0c\u5374\u662f\u66f4\u8db3\u4ee5\u4ee4\u770b\u8005\u60ca\u5fc3\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u8fde\u95ef\u4e09\u5173\uff0c\u56ed\u4e2d\u536b\u58eb\u4e00\u6b7b\u516d\u4f24\uff01 \u3000\u3000\u7531\u4e8e\u5b8c\u989c\u9274\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u8036\u5f8b\u7384\u5143\u6765\u5f97\u8fd9\u6837\u5feb\uff0c\u4ed6\u5728\u56ed\u4e2d\uff0c\u53ea\u5e03\u7f6e\u4e86\u4e5d\u540d\u536b\u58eb\uff0c\u4e5d\u540d\u536b \u58eb\u4e5f\u4e0d\u7b97\u5c11\u4e86\u3002 \u3000\u3000\u4f46\u6b64\u9645\uff0c\u5728\u4e00\u6b7b\u516d\u4f24\u4e4b\u540e\uff0c\u5b89\u7136\u65e0\u6059\u7684\u536b\u58eb\u5df2\u662f\u53ea\u5269\u4e0b\u4e24\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u76ee\u7779\u540c\u50da\u7684\u60e8\u72b6\uff0c\u867d\u6ca1\u53d7\u4f24\uff0c\u4ea6\u5df1\u662f\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8fde\u201c\u6765\u4eba\u54ea\u201d\u90fd\u4e0d\u6562\u558a\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u624b\u4e0b\u80fd\u4eba\u751a\u591a\uff0c\u9664\u4e86\u4ed6\u4ece\u4eac\u4e2d\u5e26\u6765\u7684\u536b\u58eb\u4e4b\u5916\uff0c\u8fd8\u6709\u4ed6\u4ece\u5404\u5730\u91cd\u91d1\u793c\u8058\u6765\u7684\u6c5f\u6e56 \u5f02\u4eba\u3001\u9ed1\u9053\u9ad8\u624b\u3002\u4f46\u8fdc\u6c34\u4e0d\u6551\u8fd1\u706b\uff0c\u6b64\u9645\u4ed6\u4e5f\u53ea\u80fd\u6545\u4f5c\u9547\u5b9a\uff0c\u5148\u770b\u8036\u5f8b\u7384\u5143\u6765\u610f\u5982\u4f55\u4e86\u3002\u8036 \u5f8b\u7384\u5143\u8d70\u8fdb\u5929\u9999\u4ead\uff0c\u4e24\u76ee\u5149\u5982\u5bd2\u51b0\u3001\u5982\u5229\u526a\uff0c\u770b\u4e00\u770b\u5b8c\u989c\u9274\uff0c\u53c8\u770b\u4e00\u770b\u54c8\u5fc5\u56fe\uff0c\u770b\u5f97\u4e24\u4eba\u5fc3 \u91cc\u53d1\u6bdb\u3002 \u3000\u3000\u201c\u597d\uff0c\u597d\uff01\u6709\u5c06\u519b\uff0c\u53c8\u6709\u94a6\u5dee\uff0c\u597d\uff0c\u597d\uff01\u201c\u8036\u5f8b\u7384\u5143\u76ef\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u8fd9\u624d\u5927\u7b11\u8bf4\u9053\u3002 \u3000\u3000\u201c\u4f60\u662f\u4f55\u4eba\uff0c\u80c6\u6562\u5982\u6b64\u65e0\u793c\uff1f\u201d\u54c8\u5fc5\u56fe\u662f\u94a6\u5dee\u8eab\u4efd\uff0c\u4e0d\u80fd\u4e0d\u7aef\u51fa\u51e0\u5206\u5b98\u67b6\u5b50\u3002\u5176\u5b9e\uff0c\u4ed6 \u5f53\u7136\u662f\u65e9\u5df2\u77e5\u9053\u8036\u5f8b\u7384\u5143\u662f\u8c01\u7684\u3002\u8036\u5f8b\u7384\u5143\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u6211\u5c31\u662f\u4f60\u4eec\u6240\u8981 \u6240\u8981\u6349\u62ff\u7684\u94a6\u72af\u8036\u5f8b\u7384\u5143\u201d \u3000\u3000\u5b8c\u989c\u9274\u52c9\u5f3a\u6324\u51fa\u4e00\u70b9\u7b11\u5bb9\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u8036\u5f8b\u738b\u5b50\u9a7e\u4e34\uff0c\u5931\u656c\u3002\u8bf7\u5bb9\u6211\u7a0d\u5c3d\u5730\u4e3b \u4e4b\u8c0a\uff0c\u656c\u738b\u5b50\u4e00\u676f\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4eec\u4e0d\u662f\u5949\u547d\u8981\u6349\u62ff\u6211\u8fd9\u4e2a\u94a6\u72af\u7684\u4e48\uff1f\u5982\u4eca\u6211\u5c31\u7ad9\u5728\u4f60\u4eec\u9762\u524d\uff0c\u4f60 \u4eec\u53cd\u800c\u8981\u8bf7\u94a6\u72af\u559d\u9152\uff0c\u8fd9\u5012\u771f\u662f\u5947\u95fb\u4e86\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u63a5\u8fc7\u8fd9\u9053\u547d\u4ee4\uff0c\u6211\u770b\uff0c\u6216\u8005\u662f\u4e2a\u8bef\u4f1a\u5427\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u51b7\u7b11\u9053\uff1a\u201c\u8bef\u4f1a\uff0c\u6211\u8fd9\u4e2a\u94a6\u72af\u8eab\u4efd\u5df2\u7ecf\u505a\u4e86\u4e8c\u5341\u5e74\u4e86\uff0c\u4f60\u600e\u80fd\u4e0d\u77e5\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u662f\u771f\u7684\u4e0d\u77e5\u3002\u201d\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u90a3\u8bf7\u95ee\u8fd9\u4f4d\u54c8\u5927\u4eba\u662f\u56e0\u4f55\u51fa\u4eac\u7684\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u53ea\u60f3\u62d6\u65f6\u95f4\u4ee5\u5f85\u8f6c\u673a\uff0c\u5f53\u4e0b\u679c\u7136\u88c5\u6a21\u4f5c\u6837\u7684\u95ee\u54c8\u5fc5\u56fe\u53d1\u95ee\uff1a\u201c\u8036\u5f8b\u738b\u5b50\u662f\u4e0d\u662f\u94a6 \u72af\u672b\u5c06\u4e0d\u77e5\u3002\u54c8\u5927\u4eba\uff0c\u4f60\u662f\u4ece\u4eac\u4e2d\u51fa\u6765\u7684\uff0c\u53c8\u670d\u4f8d\u8fc7\u8001\u7687\u4e0a\uff0c\u4f60\u53ef\u77e5\u9053\u2014\u2014\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u597d\u50cf\u662f\u6709\u8fc7\u8fd9\u56de\u6479\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e5f\u662f\u4e8c\u5341\u5e74\u524d\u6240\u5b9a\u7684\u6848\uff0c\u5373\u4f7f\u5728\u5f53\u65f6\u6765\u8bf4\uff0c \u5176\u5b9e\u4e5f\u4e0d\u8fc7\u662f\u4f8b\u884c\u516c\u4e8b\u800c\u5df2\u3002\u6211\u8fd9\u6b21\u51fa\u4eac\u4e0d\u8fc7\u662f\u4ee3\u7687\u4e0a\u6170\u52b3\u5546\u5dde\u58eb\u5352\uff0c\u5e76\u65e0\u522b\u4e8b\u3002\u201d\u5b8c\u989c\u9274 \u7acb\u5373\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u4e0d\u8fc7\u662f\u4f8b\u884c\u516c\u4e8b\u800c\u5df2\uff0c\u800c\u4e14\u662f\u5df2\u7ecf\u8fc7\u4e86\u4e8c\u5341\u5e74\uff0c\u65e0\u987b\u8ba4\u771f\u3002\u65e0 \u987b\u8ba4\u771f\u3002\u4f55\u51b5\u6211\u4e5f\u662f\u771f\u7684\u5e76\u6ca1\u6709\u5949\u547d\u6349\u62ff\u4f60\u5462\u3002\u8fd8\u662f\u8bf7\u738b\u5b50\u5750\u4e0b\u6765\u559d\u9152\u5427\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u628a\u6211\u5f53\u94a6\u72af\u770b\u5f85\uff0c\u6211\u4e5f\u6ca1\u5de5\u592b\u966a\u4f60\u4eec\u559d\u9152\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u738b\u5b50\u4e0d\u4fbf\u5728\u6b64\u4e45\u7559\uff0c\u4eca\u662f\u65e5\u96be\u5f97\u4e00\u4f1a\uff0c\u672b\u5c06\u5df2\u611f\u83ab\u5927\u8363\u5ba0\u3002\u738b\u5b50 \u65e2\u7136\u53e6\u5916\u6709\u4e8b\uff0c\u6211\u4e5f\u4e0d\u6562\u5f3a\u7559\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u4f5c\u51fa\u4e00\u4e2a\u9001\u5ba2\u7684\u59ff\u52bf\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u51b7\u7b11\u9053\uff1a\u201c\u522b\u88c5\u80e1\u6d82\u4e86\uff0c\u4f60\u6709\u6ca1\u6709\u542c\u8fc7\u8fd9\u53e5\u4fd7\u8bdd\uff1a\u65e0\u4e8b\u4e0d\u767b\u4e09\u5b9d\u6bbf\uff0c\u6211\u7684\u4e8b\u60c5 \u662f\u8981\u5728\u8fd9\u91cc\u529e\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u53d8\u4e86\u9762\u8272\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u738b\u5b50\u6709\u4f55\u4e8b\u8981\u529e\u3002\u672b\u5c06\u505a\u5f97\u5230\u7684\u5b9a\u5fc5\u6548\u52b3\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4e5f\u4e0d\u5fc5\u4f60\u600e\u6837\u2018\u6548\u52b3\u201d\uff0c\u4f60\u542c\u7740\uff0c\u53ea\u987b\u4f60\u4eec\u4f9d\u6211\u4e09\u4ef6\u4e8b\u60c5\uff0c\u6211\u4fbf\u79bb\u5f00\u6b64 \u5730\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\uff0c\u6211\u8981\u8bf7\u8fd9\u4f4d\u94a6\u5dee\u5927\u4eba\u966a\u6211\u4e0a\u76d8\u9f99\u5c71\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0a\u76d8\u9f99\u5c71\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u8981\u4f60\u5728\u6a80\u516c\u76f4\u5893\u524d\u78d5\u4e09\u4e2a\u5934\u966a\u7f6a\uff0c\u56e0\u4e3a\u4f60\u662f\u5bb3\u6b7b\u4ed6\u7684\u51f6\u624b\u4e4b\u4e00\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u54fc\u4e86\u4e00\u58f0\uff0c\u60f3\u53d1\u4f5c\u53c8\u672a\u7acb\u5373\u53d1\u4f5c\uff0c\u9762\u8272\u96be\u770b\u4e4b\u6781\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u8bf4\u9053\uff1a\u201c\u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u5df1\u662f\u4fbf\u5b9c\u4f60\u4e86\u3002\u201d\u54c8\u5fc5\u56fe\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u6028\u58f0\u8bf4\u9053\uff1a\u201c\u8036 \u5f8b\u738b\u5b50\uff0c\u4f60\u4e5f\u672a\u514d\u6b3a\u4eba\u592a\u751a\u4e86\u3002\u5f53\u5e74\u6211\u662f\u5949\u7687\u4e0a\u4e4b\u547d\u8bf7\u4ed6\u8fdb\u4eac\u7684\uff0c\u8c01\u53eb\u4ed6\u62d2\u4e0d\u5949\u8bcf\uff0c\u518d\u8bf4\u4e5f \u4e0d\u662f\u6211\u4e00\u4e2a\u4eba\u6253\u6b7b\u4ed6\u7684\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u5355\u51ed\u4f60\u4e00\u4e2a\u4eba\u5f53\u7136\u4f24\u4e0d\u4e86\u6a80\u516c\u76f4\uff0c\u4e5f\u6b63\u56e0\u4e3a\u4ed6\u7684\u6b7b\u56e0\u4e0d\u8be5\u7531\u4f60\u5b8c\u5168\u8d1f \u8d23\uff0c\u6211\u624d\u8981\u4f60\u78d5\u5934\u966a\u7f6a\u4fbf\u7b97\u4e86\u7ed3\uff0c\u4f46\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f60\u4f3c\u4e4e\u4e0d\u613f\u78d5\u5934\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u5b81\u6b7b\u4e0d\u8fb1\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u78d5\u5934\uff0c\u6211\u4e5f\u4e0d\u52c9\u5f3a\u4f60\u3002\u542c\u8bf4\u4f60\u7ec3\u5f97\u5927\u529b\u91d1\u521a\u638c\u529f\u592b\uff0c\u5bf9 \u5417\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u5e74\u6a80\u516c\u76f4\u5c31\u66fe\u53d7\u8fc7\u6211\u7684\u4e00\u638c\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6ca1\u600e\u4e48\u6837\uff0c\u53ea\u4e0d\u8fc7\u60f3\u7ed9\u4e00\u4e2a\u673a\u4f1a\u4e0e\u4f60\u505a\u5927\u4e08\u592b\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u5927\u4e08\u592b\u6b7b\u4e5f\u4e0d\u6015\uff0c\u5f53\u7136\u66f4\u4e0d\u6015\u75db\u4e86\u3002\u4f60\u628a\u8fd9\u53cc\u624b\u7ed9\u7559\u4e0b\u6765\u5427\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u5df2\u7ed9\u9000\u5230\u65e0\u8def\u53ef\u8d70\uff0c\u552f\u6709\u4e00\u62fc\u4e86\uff01\u5979\u4e00\u58f0\u51b7\u7b11\uff0c\u9661\u5730\u559d\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u53cc\u624b\u7ed9\u4f60\uff01 \u201c\u529b\u8d2f\u638c\u5fc3\uff0c\u53cc\u519b\u9f50\u53d1\uff01 \u3000\u3000\u6709\u8eab\u4efd\u7684\u4eba\u662f\u4e0d\u80af\u5077\u88ad\u7684\uff0c\u4e0d\u8fc7\u4ed6\u4e4b\u6240\u4ee5\u5148\u559d\u4e00\u58f0\u65b9\u59cb\u52a8\u624b\uff0c\u5012\u4e0d\u662f\u4e3a\u4e86\u8981\u4fdd\u6301\u8eab\u4efd\u7684 \u539f\u6545\uff0c\u800c\u662f\u5e0c\u671b\u5b8c\u989c\u9274\u4e0e\u4ed6\u540c\u65f6\u51fa\u624b\u3002 \u3000\u3000\u5b8c\u989c\u9274\u7684\u53d4\u7236\u5b8c\u989c\u957f\u4e4b\u662f\u91d1\u56fd\u7b2c\u4e00\u9ad8\u624b\uff0c\u4ed6\u867d\u7136\u8fd8\u4e0d\u80fd\u8bf4\u662f\u5df2\u7ecf\u5f97\u4e86\u53d4\u7236\u8863\u94b5\u771f\u4f20\u6b66\u529f \u4ea6\u5df2\u975e\u540c\u6cdb\u6cdb\u3002\u54c8\u5fc5\u56fe\u6562\u4e8e\u52a8\u6b66\uff0c\u4e00\u65b9\u9762\u56fa\u7136\u662f\u4e3a\u52bf\u6240\u903c\uff0c\u4e00\u65b9\u9762\u4e5f\u662f\u56e0\u4e3a\u6709\u5b8c\u989c\u9274\u5728\u65c1\u4e4b \u6545\u3002\u8054\u624b\u5bf9\u4ed8\u8036\u5f8b\u7384\u5143\uff0c\u4ed6\u76f8\u4fe1\u6709\u51e0\u5206\u53d6\u80dc\u7684\u628a\u63e1\u3002 \u3000\u3000\u867d\u7136\u6ca1\u6709\u4e8b\u5148\u7ea6\u597d\uff0c\u4f46\u5728\u8fd9\u6837\u60c5\u5f62\u5e95\u4e0b\uff0c\u6309\u8bf4\u5b8c\u989c\u7d27\u4e5f\u8be5\u4e0e\u4ed6\u7978\u798f\u540c\u5f53\u7684\u3002 \u3000\u3000\u90a3\u77e5\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5b8c\u989c\u9274\u5e76\u6ca1\u4e0e\u4ed6\u8054\u624b\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u53cc\u638c\u9f50\u53d1\uff0c\u52bf\u5982\u5954\u96f7\u9a87\u7535\uff0c\u8036\u5f8b\u7384\u5143\u77e5\u9053\u4e0d\u662f\u6cbe\u8863\u5341\u516b\u8dcc\u4e4b\u7c7b\u7684\u529f\u592b\u6240\u4ee5\u80fd\u5e94 \u4ed8\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u4e5f\u662f\u53cc\u638c\u63a5\u62db\u3002 \u3000\u3000\u56db\u638c\u76f8\u4ea4\u3002\u54c8\u5fc5\u56fe\u4e00\u70b9\u4e5f\u611f\u89c9\u4e0d\u5230\u5bf9\u65b9\u53cd\u51fb\u7684\u529b\u9053\u3002\u6b63\u81ea\u6b22\u559c\uff0c\u201c\u539f\u6765\u8fd9\u53ae\u4e43\u6d6a\u5f97\u865a\u540d \u2014\u2014\u201d\u5fc3\u5ff5\u6765\u5df2\uff0c\u7a81\u7136\u611f\u89c9\u4e0d\u5999\u4e86\u3002 \u3000\u3000\u4ed6\u7ec3\u7684\u662f\u91d1\u521a\u62ff\u529f\u592b\uff0c\u5185\u529b\u96c4\u6d51\uff0c\u5177\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u90a3\u77e5\u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u5fc3\u8f7b\u8f7b\u65cb\u8f6c\uff0c \u4ed6\u90a3\u4e48\u96c4\u6d51\u7684\u5185\u529b\uff0c\u53d1\u5c04\u51fa\u53bb\uff0c\u7adf\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u4e00\u53bb\u65e0\u8e2a\uff0c\u8fde\u6d6a\u82b1\u4e5f\u6ca1\u6fc0\u8d77\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u4e0d\u662f\u6ca1\u6709\u89c1\u8bc6\u7684\u4eba\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u6df1\u77e5\u5bf9\u65b9\u7684\u5185\u529f\u9020\u8be3\u8fdc\u80dc\u4e8e\u5df2\uff0c\u81f3\u6b64\uff0c\u4ed6\u5982\u4f55\u8fd8 \u6562\u548c\u5bf9\u65b9\u62fc\u4e0b\u53bb\uff1f\u4e0d\u6599\u4ed6\u60f3\u64a4\u638c\u62bd\u8eab\u4ea6\u5df2\u4e0d\u80fd\u4e86\u3002\u5bf9\u65b9\u7684\u638c\u5fc3\u7adf\u4f3c\u6709\u7740\u4e00\u5c42\u7c98\u529b\u6781\u5f3a\u7684\u80f6\u8d28 \u4f3c\u7684\uff0c\u628a\u4ed6\u7684\u53cc\u638c\u7262\u7262\u5438\u4f4f\u3002 \u3000\u3000\u8fdb\u65e2\u4e0d\u80fd\u3002\u9000\u4ea6\u4e0d\u80fd\uff0c\u54c8\u5fc5\u56fe\u552f\u6709\u62fc\u7740\u8017\u635f\u5185\u529b\u4e0e\u5bf9\u65b9\u76f8\u6301\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u89c1\u4ed6\u5df2\u662f\u5927\u6c57\u6dcb\u6f13\uff0c\u5934\u9879\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e5f\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u4e00\u89c1\u8fd9\u4e2a\u60c5\u5f62\uff0c\u4fbf\u77e5\u53cc\u65b9\u6b63\u5728\u6bd4\u62fc\u5185\u529b\uff0c\u800c\u4e14\u662f\u5230\u4e86\u5373\u5c06\u51b3 \u80dc\u8d1f\u7684\u5173\u952e\u65f6\u523b\u4e86\u3002 \u3000\u3000\u80dc\u8d1f\u662f\u65e0\u5f85\u535c\u9f9f\u7684\u3002\u54c8\u5fc5\u56fe\u5df2\u662f\u5373\u5c06\u5230\u4e86\u6cb9\u5c3d\u706f\u67af\u7684\u5730\u6b65\uff0c\u53ea\u80fd\u4f5c\u4e34\u6b7b\u7684\u6323\u624e\u4e86\u3002\u4f46\u5728 \u8fd9\u6837\u5173\u952e\u7684\u65f6\u523b\uff0c\u8036\u5f8b\u7384\u5143\u4e5f\u5fc5\u987b\u5168\u529b\u4ee5\u8d74\uff0c\u4ee5\u514d\u529f\u4e8f\u4e00\u6e83\u3002 \u3000\u3000\u5b8c\u989c\u9274\u672c\u6765\u662f\u53ef\u4ee5\u8d81\u8fd9\u4e2a\u673a\u4f1a\u9003\u8d70\u7684\uff0c\u4f46\u4ed6\u5374\u6539\u53d8\u4e86\u4e3b\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u5bf9\u4ed6\u6765\u8bf4\uff0c\u8fd9\u4e5f\u662f\u9664\u6389\u8036\u5f8b\u7384\u5143\u7684\u5343\u8f7d\u96be\u9022\u4e4b\u673a\uff01\u5b8c\u989c\u9274\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u6253\u4e86\u4e2a\u54c8 \u54c8\uff01\u8bf4\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f55\u5fc5\u975e\u5f97\u62fc\u4e2a\u4f60\u6b7b\u7684\u6d3b\u4e0d\u53ef\uff01\u201d \u3000\u3000\u89c1\u8036\u5f8b\u7384\u5143\u6ca1\u6709\u53cd\u5e94\uff0c\u4ed6\u7684\u80c6\u5b50\u66f4\u5927\u4e86\u3002 \u3000\u3000\u4ed6\u88c5\u4f5c\u529d\u67b6\u7684\u6a21\u6837\uff0c\u7a81\u7136\u51fa\u624b\uff0c\u95ea\u7535\u822c\u7684\u70b9\u4e86\u8036\u5f8b\u7384\u5143\u56db\u5904\u6b7b\u7a74\uff01 \u3000\u3000\u5b8c\u989c\u4e00\u5bb6\u7684\u70b9\u7a74\u529f\u592b\u662f\u5b8c\u989c\u957f\u4e4b\u4ece\u7a74\u9053\u94dc\u4eba\u7684\u56fe\u89e3\u4e0a\u7cbe\u7814\u51fa\u6765\u7684\uff08\u7a74\u9053\u94dc\u4eba\u6765\u5386\u8be6\u89c1\u62d9 \u8457\u98de\u51e4\u6f5c\u9f99\uff09\uff0c\u8fd9\u662f\u5929\u4e0b\u7b2c\u4e00\u7684\u70b9\u7a74\u529f\u592b\u3002\u5b8c\u989c\u9274\u5df2\u7ecf\u5f97\u4e86\u53d4\u7236\u5728\u94b5\u771f\u4f20\uff0c\u53ea\u662f\u529f\u529b\u4e0d\u5982\u800c \u5df2\u3002 \u3000\u3000\u5929\u4e0b\u7b2c\u4e00\u7684\u70b9\u7a74\u529f\u592b\uff0c\u53ea\u987b\u70b9\u4e2d\u4e00\u5904\u6b7b\u7a74\uff0c\u5bf9\u65b9\u6b66\u529f\u591a\u597d\uff0c\u4e5f\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c\u4f55\u51b5\u662f\u70b9\u4e2d \u4e86\u56db\u5904\u6b7b\u7a74\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u6709\u4eba\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u4e2a\u4eba\u5374\u4e0d\u662f\u8036\u5f8b\u7384\u5143\uff0c\u800c\u662f\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8036\u5f8b\u7384\u5143\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u563b\u563b\uff0c\u5b8c\u989c\u5c06\u519b\u4f60\u600e\u4e48\u7ed9\u6211\u6293\u75d2\u6765\u4e86\uff1f \u3000\u3000\u6211\u53ef\u771f\u662f\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u7ec3\u6709\u4e00\u95e8\u975e\u5e38\u5947\u7279\u7684\u529f\u592b\uff0c\u53ef\u4ee5\u632a\u79fb\u7a74\u9053\uff0c\u5728\u8fd9\u4e2a\u5173\u952e\u65f6\u523b\uff0c\u4ed6\u867d\u7136\u4e0d\u80fd\u907f\u5f00\uff0c \u4f46\u7a74\u9053\u632a\u79fb\u4e4b\u540e\uff0c\u7ed9\u5b8c\u989c\u9274\u70b9\u4e2d\u7684\u5df2\u7ecf\u4e0d\u662f\u6b7b\u7a74\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u7684\u5077\u88ad\u867d\u6ca1\u6210\u52ab\uff0c\u5374\u4e5f\u5e76\u975e\u6beb\u65e0\u7528\u5904\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u79fb\u7a74\u9053\u53ea\u662f\u6d88\u6781\u9632\u5b88\uff0c\u4e0d\u7528\u4e8e\u8fd0\u52a8\u53cd\u51fb\uff0c\u4f46\u4e5f\u8fd8\u662f\u8981\u635f\u8017\u4e00\u70b9\u771f\u6c14\uff0c\u5185\u529b\u968f\u4e4b\u800c \u706d\u3002\u4e5f\u6b63\u662f\u56e0\u6b64\u3002\u54c8\u5fc5\u56fe\u624d\u80fd\u8131\u51fa\u8036\u5f8b\u7384\u5143\u7684\u638c\u63e1\u3002\u6240\u4ee5\uff0c\u5b8c\u989c\u9274\u7684\u5077\u88ad\uff0c\u53ef\u4ee5\u8bf4\u5f97\u662f\u95f4\u63a5 \u6551\u4e86\u4ed6\u7684\u547d\u3002\u201d \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u5e78\u4fdd\u6027\u547d\uff0c\u5185\u529b\u6d88\u8017\u592a\u751a\uff0c\u4ea6\u5df2\u662f\u75b2\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u4ed6\u7ed9\u9707\u5012\u5730\u4e0a\uff0c\u53ea\u89c9\u4e94\u810f\u516d\u8151\u597d\u50cf\u8981\u7ffb\u822c\uff0c\u773c\u524d\u91d1\u661f\u4e71\u821e\uff0c\u54ea\u91cc\u8fd8\u80fd\u722c\u5f97\u8d77\u6765\uff1f\u5b8c\u989c\u9274 \u70b9\u7a74\u65e0\u529f\uff0c\u53c8\u89c1\u54c8\u5fc5\u56fe\u4e1a\u5df1\u5012\u5728\u5730\u4e0a\uff0c\u8fd9\u4e00\u60ca\u81ea\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e09\u5341\u516d\u8ba1\u8d70\u4e3a\u4e0a\u8ba1\uff0c\u8f6c\u8eab\u5c31 \u8dd1\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u8fd9\u65f6\u624d\u60f3\u9003\u8dd1\uff0c\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u610f\u5b58\u4e39\u7530\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u628a\u8017\u635f\u7684\u5185\u529b\u6062\u590d\u8fc7\u540e\uff0c\u659c\u659c\u53d1\u51fa\u4e00\u638c\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5c1a\u672a\u8dd1\u51fa\u5929\u9999\u4ead\u5916\uff0c\u9661\u7136\u95f4\u5c31\u597d\u50cf\u78b0\u4e0a\u4e86\u6f5c\u6d41\u6025\u6e4d\u4f3c\u7684\uff0c\u4e00\u80a1\u7a81\u5982\u5176\u6765\u7684\u529b\u9053\uff0c \u5c06\u4ed6\u8feb\u5f97\u5012\u9000\u56de\u6765\u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u65b9\u5411\u518d\u9009\uff0c\u63a5\u8fde\u8bd5\u4e86\u4e09\u6b21\uff0c\u90fd\u7ed9\u8036\u5f8b\u7384\u5143\u7684\u5288\u7a7a\u638c\u529b\u903c\u9000\uff0c\u5230\u4e86\u7b2c\u4e09\u6b21\uff0c\u53ea\u89c9\u80f8 \u53e3\u5df2\u662f\u9690\u9690\u4f5c\u75db\u3002\u4ed6\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u518d\u8bd5\u4e86\u3002 \u3000\u3000\u201c\u5b8c\u989c\u5c06\u519b\uff0c\u521a\u624d\u4f60\u8fd8\u5728\u6bb7\u52e4\u7559\u5ba2\uff0c\u5982\u4eca\u6211\u8fd9\u4e2a\u505a\u5ba2\u4eba\u7684\u8fd8\u6ca1\u8d70\uff0c\u4f60\u505a\u4e3b\u4eba\u7684\u5012\u8981\u5148 \u8d70\uff0c\u592a\u4e0d\u793c\u8c8c\u4e86\u5427\uff1f\u201d\u8036\u5f8b\u7384\u5143\u51b7\u51b7\u8bf4\u9053\u3002 \u3000\u3000\u201c\u54c8\u5fc5\u56fe\u90fd\u5df1\u7ed9\u4f60\u6253\u5f97\u91cd\u4f24\u4e86\uff0c\u4f60\u8fd8\u8981\u600e\u6837\u624d\u80af\u8d70\uff1f\u201d\u5b8c\u989c\u9274\u8fde\u58f0\u8bf4\u9053\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c06\u519b\uff0c\u4f60\u7684\u8bb0\u6027\u4e5f\u672a\u514d\u592a\u574f\u4e86\uff0c\u6211\u4e0d\u662f\u8bf4\u8fc7\u6709\u4e09\u4ef6\u4e8b\u60c5\u8981 \u529e\u5417\uff0c\u5982\u4eca\u53ea\u529e\u4e86\u7b2c\u4e00\u4ef6\u4e8b\u60c5\uff0c\u600e\u80fd\u5c31\u8d70\uff1f\u8bf7\u5750\u4e0b\u6765\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u6b64\u65f6\uff0c\u5b8c\u989c\u9274\u624b\u4e0b\u7684\u536b\u58eb\u4ee5\u53ca\u4ece\u5404\u5904\u91cd\u91d1\u793c\u8058\u6765\u7684\u9ad8\u624b\u5df2\u662f\u7eb7\u7eb7\u51fa\u6765\uff0c\u6709\u516d\u4e03\u4e2a\u5df2\u7ecf\u903c \u8fd1\u5929\u9999\u4ead\u4e86\u3002 \u3000\u3000\u201c\u5c06\u519b\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff0c\u6b64\u9645\u6211\u8981\u6740\u4f60\u6613\u5982\u53cd\u638c\uff1f\u6211\u548c\u4f60\u8c08\u8bdd\uff0c\u4e0d\u559c\u6b22\u6709\u4eba\u5728\u65c1\u6253\u6270\uff01\u201d\u8036 \u5f8b\u7384\u5143\u51b7\u51b7\u8bf4\u9053\u3002\u5b8c\u989c\u9274\u5f53\u7136\u4e0d\u6562\u4e0d\u4fe1\uff0c\u8fde\u5fd9\u6325\u4e00\u6325\u624b\uff0c\u559d\u9053\uff1a\u201c\u7ed9\u6211\u9000\u4e0b\uff0c\u8c01\u90fd\u4e0d\u8782\u8e0f\u5165 \u5929\u9999\u4ead\u5185\uff01\u201d \u3000\u3000\u4ed6\u56de\u5230\u539f\u6765\u7684\u5ea7\u4f4d\u5750\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\u3002\u8bf7\u4f60\u8bf4\u7b2c\u4e8c\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u60c5\uff0c\u6211\u60f3\u89c1\u5c0a\u592b\u4eba\u4e00\u9762\u3002\u4e00\u5ba2\u4e0d\u70e6\u4e8c\u4e3b\uff0c\u8fd9\u5c31\u8bf7\u4f60\u66ff\u6211\u5b89\u6392\u5427\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u53d8\u4e86\u9762\u8272\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4e0d\u6562\u53d1\u4f5c\uff0c\u8fd8\u662f\u5df2\u7ed9\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ea\u542c\u5f97\u4ed6\u7684\u7259\u9f7f\u54ac \u5f97\u683c\u683c\u4f5c\u54cd\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u662f\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u65e0\u5948\u6211\u662f\u975e\u89c1\u5c0a\u592b\u4eba\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u9053\u662f\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u6211\u5c31\u4e0d\u80fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u4e0d\u52c9\u5f3a\u4f60\u3002\u4e0d\u8fc7\uff0c\u6211\u82e5\u662f\u89c1\u4e0d\u5230\u5c0a\u592b\u4eba\uff0c\u6211\u5c31\u53ea\u80fd\u8bf7\u4f60\u8ddf\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9762\u8272\u66f4\u52a0\u96be\u770b\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8981\u5e26\u5979\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9ed8\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5df2\u7ecf\u8fdf\u4e86\u4e8c\u5341\u5e74\uff0c\u6211\u6ca1\u6709\u6743\u5229\u5411\u5979\u63d0\u51fa\u8fd9\u4e2a\u8981\u6c42\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u90a3\u3001\u90a3\u3001\u90a3\u4f60\u8981\u89c1\u7531\u4e8e\u662f\u4f55\u5c45\u5fc3\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u6211\u53ea\u662f\u8981\u89c1\u5979\u4e00\u9762\u3001\u81f3\u4e8e\u5979\u8d70\u4e0d\u8d70\uff0c\u90a3\u5c31\u662f\u5979\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u90a3\u4e9b\u624b\u4e0b\u662f\u5df1\u7ecf\u9000\u5230\u4e86\u5047\u5c71\u90a3\u8fb9\u5e03\u6210\u5305\u56f4\u9635\u52bf\u7684\uff0c\u6b64\u65f6\u5ffd\u542c\u5f97\u90a3\u8fb9\u6709\u4eba\u5435\u95f9\u3002 \u3000\u3000\u201c\u5c06\u519b\u6709\u547d\u4e86\uff0c\u4e0d\u7ba1\u4f55\u4eba\uff0c\u4e0d\u8bb8\u8fdb\u53bb\uff01\u201d \u3000\u3000\u201c\u662f\u592b\u4eba\u53eb\u6211\u6765\u7684\uff0c\u4e5f\u4e0d\u8bb8\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u62ac\u5934\u671b\u53bb\uff0c\u8fd9\u4e2a\u536b\u58eb\u5435\u95f9\u7684\u4eba\u662f\u7ed9\u4ed6\u7ba1\u7406\u9a6c\u53a9\u7684\u5934\u5b50\uff0c\u4ed6\u5fc3\u91cc\u9887\u89c9\u5947\u602a\uff0c\u56de\u8fc7\u5934 \u53bb\uff0c\u671b\u671b\u8036\u5f8b\u7384\u5143\u3001\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u5524\u8fd9\u4eba\u8fdb\u6765\u3002\u201d \u3000\u3000\u90a3\u9a6c\u53a9\u7ba1\u4e8b\u7ad9\u5728\u5929\u9999\u4ead\u8fb9\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u7980\u5c06\u519b\uff0c\u4f60\u90a3\u56db\u5339\u5750\u9a91\u2014\u2014\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5fc3\u6025\u5982\u711a\uff0c\u7709\u5934\u4e00\u76b1\u3002\u8bf4\u9053\uff1a\u201c\u5c06\u519b\u90a3\u6709\u95f2\u5fc3\u542c\u755c\u7272\u7684\u4e8b\uff0c\u4f60\u5feb\u8bf4\u592b\u4eba\u8981\u4f60\u7980 \u544a\u4f55\u4e8b\u5427\uff1f\u201d \u3000\u3000\u4ed6\u55a7\u5bbe\u593a\u4e3b\uff0c\u90a3\u9a6c\u53a9\u7ba1\u4e8b\u4e0d\u77e5\u4ed6\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u8981\u6211\u7980\u62a5\u7684\u6b63\u662f\u8fd9\u4ef6\u4e8b \u554a\uff01\u5927\u4eba\uff0c\u4f60\u4e0d\u660e\u767d\uff0c\u8fd9\u56db\u5339\u5750\u9a91\u662f\u5c06\u519b\u6700\u5fc3\u7231\u7684\uff0c\u5018\u4e0d\u662f\u592b\u4eba\u8981\u7684\u8bdd\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u9274\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u56db\u5339\u5750\u9a91\uff0c\u53ef\u662f\u6843\u82b1\u806a\u3001\u83ca\u82b1\u9752\u3001\u4e94\u9879\u8d64\u548c\u4e94\u660e \u9aa5\uff1f\u201d\u8fd9\u56db\u5339\u5750\u9a91\u90fd\u662f\u65e5\u884c\u5343\u91cc\u7684\u9a8f\u9a6c\uff0c\u5b8c\u989c\u9274\u89c6\u540c\u62f1\u58c1\uff0c\u66fe\u5429\u5490\u9a6c\u53a9\u7279\u522b\u5c0f\u5fc3\u6599\u7406\uff0c\u4efb\u4f55 \u4eba\u90fd\u4e0d\u8bb8\u501f\u7528\u7684\u3002\u9a6c\u53a9\u7ba1\u4e8b\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u592b\u4eba\u5168\u90fd\u8981\u4e86\u53bb\uff1f\u201d\u9a6c\u53a9\u7ba1\u4e8b\u53c8\u662f\u8fd9\u4e24\u4e2a\u5b57\u56de\u7b54\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u592b\u8981\u8fd9\u56db\u5339\u5750\u9a91\u505a\u4ec0\u4e48\uff1f\u201d\u9a6c\u53a9\u7ba1\u4e8b\u9053\uff1a\u201c\u592b\u4eba\u7528\u6765\u62c9\u9a6c\u8f66\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u592b\u4eba\u7528\u56db\u5339\u5343\u91cc\u9a6c\u6765\u62c9\u8f66\uff1f\u201c\u9a6c\u53a9\u7ba1\u4e8b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u592b\u4eba\u5df2\u7ecf\u5750\u9a6c \u8f66\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u5018\u82e5\u4e0d\u662f\u592b\u4eba\u4eb2\u81ea\u6765\u8981\uff0c\u6211\u4e5f\u4e0d\u6562\u7ed9\u7684\u3002\u8bf7\u5c06\u519b\u6055\u7f6a\u3002\u201d\u90a3\u7ba1\u4e8b\u8bda\u60f6\u8bda\u6050\u7684\u8bf4\u9053\u3002 \u3000\u3000\u201c\u592b\u4eba\u53bb\u4e86\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4eba\u4e0d\u77e5\u3002\u5c0f\u4eba\u53ea\u662f\u5949\u4e86\u592b\u4eba\u4e4b\u547d\u7980\u62a5\u5c06\u519b\uff0c\u592b\u4eba\u8bf4\u53eb\u5c06\u519b\u4e0d\u5fc5\u627e\u5979\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u65f6\u5019\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u201c\u4f55\u4ee5\u6b64\u65f6\u65b9\u6765\u7980\u62a5\uff1f\u201d \u3000\u3000\u201c\u5c06\u519b\u4f60\u4e5f\u770b\u89c1\u4e86\u662f\u536b\u58eb\u4e0d\u8bb8\u5c0f\u4eba\u8fdb\u6765\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u53c8\u662f\u6124\u6012\uff0c\u53c8\u662f\u6b22\u559c\u3002\u6124\u6012\u7684\u662f\u59bb\u5b50\u4e0d\u544a\u800c\u522b\uff0c\u6b22\u559c\u7684\u662f\u53ef\u4ee5\u907f\u5f00\u4e00\u4ef6\u4ee4\u4ed6\u5c34\u5c2c \u7684\u4e8b\u4e86\u3002\u4ed6\u65a5\u9000\u9a6c\u53a9\u7ba1\u4e8b\uff0c\u5bf9\u8036\u5f8b\u7384\u5143\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u4eb2\u8033\u542c\u89c1\u4e86\uff0c\u5185\u5b50\u5df2\u7ecf\u8d70\u5f97\u8fdc\u5566\u3002\u6211\u8fd9 \u56db\u5339\u540d\u9a79\u90fd\u662f\u5343\u91cc\u9a6c\uff0c\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c11\u8bf4\u4e5f\u5df2\u79bb\u5f00\u5546\u5dde\u4e09\u4e94\u5341\u91cc\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5446\u4e86\u7247\u523b\uff0c\u9661\u5730\u559d\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u77e5\u9053\u4f60\u8981\u6765\uff0c\u4f60\u4ee5\u4e3a\u6211\u4f1a\u8ddf\u4e00\u4e2a\u9a6c\u592b\u4e32\u901a\u4e86\u6765\u9a97\u4f60\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u7f15\u8427\u58f0\u4ece\u82b1\u95f4\u4f20\u51fa\uff0c\u5982\u60f3\u5982\u5e55\uff0c\u5982\u6ce3\u5982\u8bc9\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u6697\u97f3\u5f8b\uff0c\u53ea\u662f\u5947\u602a\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u600e\u7684\u7adf\u7136\u6709\u4eba\u6562\u5728\u8fd9\u4e2a\u56ed\u5b50\u91cc\u5439\u8427\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u5219\u662f\u4e00\u542c\u5c31\u77e5\uff0c\u8fd9\u4eba\u5439\u7684\u6b63\u662f\u4e8c\u5341\u5e74\u524d\u4ed6\u4eec\u5206\u624b\u524d\u5915\uff0c\u4ed6\u4e3a\u5979\u5439\u7684\u90a3\u652f\u66f2\u5b50\u3002 \u3000\u3000\u201c\u4e07\u4e07\u82b1\u4e2d\u7b2c\u4e00\u6d41\uff0c\u6b8b\u971e\u8f7b\u67d3\u5ae9\u94f6\u74ef\u3002 \u3000\u3000\u80fd\u72c2\u7d2b\u964c\u5343\u91d1\u4e86\uff0c\u4e5f\u611f\u6731\u95e8\u4e07\u6237\u4faf\u3002 \u3000\u3000\u671d\u65e5\u7167\u5f00\u643a\u9152\u770b\uff0c\u66ae\u98ce\u5439\u843d\u7ed5\u680f\u6536\u3002 \u3000\u3000\u8bd7\u4e66\u6ee1\u67b6\u5c18\u57c3\u6251\uff0c\u5c3d\u65e5\u65e0\u4eba\u7565\u4e3e\u5934\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u795e\u601d\u8ff7\u832b\uff0c\u5ffd\u5730\u53eb\u8d77\u6765\u9053\uff1a\u201c\u539f\u6765\u4f60\u679c\u7136\u662f\u9a97\u6211\u7684\uff0c\u5979\u6ca1\u6709\u8d70\uff01\u201d\u4e00\u5f2f\u8170\u6293\u8d77 \u4e86\u762b\u5728\u5730\u4e0a\u7684\u54c8\u5fc5\u56fe\u5c31\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u56f4\u5728\u5916\u9762\u7684\u536b\u58eb\u90fd\u5df2\u5f20\u5f13\u642d\u7bad\uff0c\u5f15\u6ee1\u5f85\u53d1\uff0c\u4f46\u4e00\u89c1\u94a6\u5dee\u5927\u4eba\u5df2\u88ab\u5bf9\u65b9\u62ff\u6765\u5f53\u4f5c\u76fe\u724c\uff0c\u7bad \u53c8\u5982\u4f55\u6562\u5c04\u51fa\u53bb\uff1f\u8036\u5f8b\u7384\u5143\u5728\u82b1\u4e1b\u4e2d\u627e\u5230\u90a3\u4e2a\u5439\u8427\u7684\u4eba\uff0c\u4e0d\u7981\u5927\u5931\u6240\u671b\uff0c\u8fd9\u4e2a\u4eba\u662f\u4e2a\u5c0f\u4e2b \u73af\u3002 \u3000\u3000\u5176\u5b9e\u8036\u5f8b\u7384\u5143\u4ea6\u5df2\u6709\u70b9\u6000\u7591\u4e86\u7684\u3002\u5047\u5982\u662f\u5b8c\u989c\u592b\u4eba\u7684\u8fd9\u652f\u66f2\u5b50\uff0c\u5f53\u7136\u4f1a\u6bd4\u8fd9\u5c0f\u4e2b\u73af\u5439\u5f97 \u597d\u542c\u5f97\u591a\u3002\u4ed6\u4e0d\u8fc7\u5728\u795e\u601d\u8ff7\u832b\u4e2d\u8ffd\u6c42\u4e00\u7ebf\u5e0c\u671b\u800c\u5df2\u3002 \u3000\u3000\u201c\u6211\u662f\u592b\u4eba\u7684\u8d34\u8eab\u4f8d\u5973\uff0c\u662f\u592b\u4eba\u53eb\u6211\u6765\u5439\u8fd9\u652f\u66f2\u5b50\u7684\u3002\u201d \u3000\u3000\u5c0f\u4e2b\u73af\u4e0d\u5f85\u4ed6\u95ee\uff0c\u5c31\u653e\u4e0b\u7389\u8427\u548c\u4ed6\u8bf4\u9053\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u60ca\u7591\u4e0d\u5b9a\uff0c\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u662f\uff0c\u662f\u592b\u4eba\u53eb\u4f60\u6765\u6b64\u5439\u8427\uff1f\u201d \u3000\u3000\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u592b\u4eba\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\uff0c\u5979\u53eb\u6211\u5439\u8fd9\u652f\u66f2\u5b50\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5598\u7740\u6c14\u53d1\u95ee\uff1b\u201d\u592b\u4eba\u5462\uff1f\u201d \u3000\u3000\u5c0f\u4e86\u73af\u9053\uff1a\u201c\u592b\u4eba\u5df2\u7ecf\u8d70\u4e86\uff01\u201d \u3000\u3000\u53c8\u4e00\u6b21\u542c\u5230\u540c\u6837\u7684\u56de\u7b54\uff1a\u201c\u592b\u4eba\u5df2\u7ecf\u8d70\u4e86\uff01\u201d\u8036\u5f8b\u7384\u5143\u53ef\u4ee5\u6000\u7591\u90a3\u4e2a\u9a6c\u53a9\u7ba1\u4e8b\u548c\u5b8c\u989c\u9274 \u4e32\u901a\u6765\u9a97\u4ed6\uff0c\u4f46\u4ed6\u600e\u53ef\u4ee5\u6000\u7591\u8fd9\u4e2a\u4e2b\u73af\uff0c\u4ece\u5979\u61c2\u5f97\u5439\u8fd9\u652f\u66f2\u5b50\u5df2\u7ecf\u53ef\u4ee5\u8bc1\u660e\u5979\u662f\u592b\u4eba\u5fc3\u8179\u7684 \u4e2b\u73af\u3002\u5e0c\u671b\u5df2\u7ecf\u706d\u4e86\uff0c\u4f46\u4ed6\u8fd8\u662f\u72c2\u53eb\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u6211\u4e0d\u76f8\u4fe1\uff01\u201c\u5176\u5b9e\u8fd9\u53ea\u662f\u4ed6\u201c\u4e0d\u613f\u610f\u76f8 \u4fe1\u201d\u800c\u5df2\uff0c\u5fc3\u91cc\u5df2\u77e5\u9053\u8fd9\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u5c0f\u4e2b\u73af\u53f9\u9053\uff1a\u201c\u4f60\u600e\u6837\u624d\u80fd\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u8fd8\u8981\u89c1\u4e00\u4e2a\u4eba\u3002\u201d\u4ed6\u56de\u8fc7\u5934\u671b\u5411\u5929\u9999\u4ead\u90a3\u8fb9\uff0c\u559d\u9053\uff1a\u201c\u5b8c\u989c\u9274\u4f60\u7ed9\u6211 \u628a\u5170\u59d1\u5524\u6765\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5170\u59d1\u679c\u7136\u5c31\u662f\u54c8\u5fc5\u56fe\u6240\u8981\u8ffd\u67e5\u7684\u90a3\u4f4d\u8d1d\u5b50\u592b\u4eba\uff01\u201c\u767b\u65f6 \u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f60\u8981\u6c42\u7684\u7b2c\u4e09\u4ef6\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u89c1\u4e86\u5170\u59d1\uff0c\u6211\u5c31\u8d70\uff01\u201d \u3000\u3000\u4e0d\u5f85\u5b8c\u989c\u9274\u6d3e\u4eba\u53bb\u627e\uff0c\u5170\u59d1\u5df2\u7ecf\u6765\u4e86\uff01 \u3000\u3000\u5979\u662f\u65e9\u5df2\u8eb2\u5728\u56ed\u5b50\u91cc\u7684\uff0c\u542c\u5f97\u8036\u5f8b\u7384\u5143\u8981\u89c1\u5979\uff0c\u5979\u4e5f\u4e0d\u5f85\u5b8c\u989c\u9274\u7684\u7b54\u590d\uff0c\u4e0d\u987e\u4e00\u5207\uff0c\u5c31 \u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5c1a\u672a\u66fe\u4e0b\u4ee4\uff0c\u5f53\u7136\u7acb\u5373\u5c31\u6709\u536b\u58eb\u4e0a\u524d\u62e6\u622a\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u5979\u65bd\u5c55\u8f7b\u529f\u4ece\u4e00\u4e2a\u536b\u58eb\u8eab\u65c1\u63a0\u8fc7\uff0c\u53e6\u5916\u56db\u540d\u536b\u58eb\u62e6\u5728\u5979\u7684\u524d \u9762\uff0c\u7ed9\u5979\u7528\u5f20\u708e\u6240\u4f20\u7684\u70b9\u7a74\u529f\u592b\uff0c\u4e00\u4e2a\u5de6\u53f3\u5f00\u5f13\uff0c\u4e24\u4e2a\u536b\u58eb\u90fd\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u201c\u8ba9\u5f00\u201d\u4e24\u4e2a\u5b57\u521a\u521a\u8bf4\u5b8c\uff1b\u5495\u549a\uff0c\u5495\u549a\uff0c\u4e24\u6761\u5927\u6c49\u8dcc\u5012\u5730\u4e0a\uff01\u548c\u201c\u5170\u59d1\u201d\u76f8\u719f\u7684\u536b\u58eb\u60f3 \u4e0d\u5230\u5979\u7adf\u6709\u5982\u6b64\u654f\u6377\u7684\u8eab\u624b\uff0c\u4e0d\u89c9\u90fd\u662f\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u5b8c\u989c\u9274\u559d\u9053\uff1a\u201c\u5c06\u5979\u62ff\u4e0b\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u968f\u7740\u5b8c\u989c\u9274\u7684\u559d\u58f0\uff0c\u5df2\u6709\u4e24\u4eba\u8ffd\u4e0a\u201c\u5170\u59d1\u201d\u3002\u8fd9\u4e24\u4eba\u662f\u5b8c\u989c\u9274\u6e05\u6765\u7684 \u5ba2\u537f\uff0c\u672c\u9886\u5728\u4e00\u822c\u536b\u58eb\u4e4b\u4e0a\u3002\u8036\u5f8b\u7384\u5143\u628a\u54c8\u5fc5\u56fe\u631f\u5728\u52a9\u4e0b\uff0c\u5954\u5411\u5170\u59d1\u3002 \u3000\u3000\u4ed6\u521a\u8dd1\u5f00\u51e0\u6b65\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u60e8\u53eb\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5c0f\u4e2b\u73af\u5df2\u7ecf\u7ed9\u5f13\u7bad\u5c04\u6b7b\u4e86\u3002\u8036\u5f8b\u7384 \u5143\u597d\u751f\u540e\u6094\u3002\u540e\u6094\u81ea\u5df1\u4e00\u65f6\u758f\u5ffd\uff0c\u5fd8\u8bb0\u4fdd\u62a4\u8fd9\u4e2a\u4e2b\u73af\u3002 \u3000\u3000\u7adf\u7d2f\u5730\u6b7b\u4e8e\u975e\u547d\u3002 \u3000\u3000\u4f46\u6b64\u9645\u4ed6\u4ea6\u5df2\u65e0\u6687\u540e\u6094\u4e86\uff0c\u56e0\u4e3a\u8fd8\u6709\u4e00\u4e2a\u6bd4\u8fd9\u5c0f\u4e2b\u73af\u66f4\u91cd\u8981\u7684\u4eba\u7b49\u5f85\u4ed6\u53bb\u6551\u63f4\u3002 \u3000\u3000\u622a\u51fb\u5f20\u96ea\u6ce2\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u662f\u5b8c\u989c\u9274\u91cd\u91d1\u8058\u6765\u7684\u9ed1\u9053\u9ad8\u624b\uff0c\u672c\u9886\u6bd4\u4e00\u822c\u536b\u58eb\u9ad8\u660e\u5f97\u591a\u3002\u5f20 \u96ea\u6ce2\u5728\u4ed6\u4eec\u5939\u653b\u4e4b\u4e0b\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u5df2\u662f\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u4e3e\u8d77\u54c8\u5fc5\u56fe\uff0c\u4f5c\u4e86\u4e00\u4e2a\u65cb\u98ce\u821e\uff0c\u559d\u9053\uff1a\u201c\u8c01\u6562\u4f24\u5bb3\u5170\u59d1\uff0c\u6211\u5c31\u8981\u4f60\u4eec\u8fd9\u4f4d\u94a6\u5dee \u5927\u4eba\u507f\u547d\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5728\u54c8\u5fc5\u56fe\u7684\u7b11\u8170\u7a74\u4e0a\u7528\u529b\u4e00\u634f\uff0c\u54c8\u5fc5\u56fe\u54c7\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u8fd9\u4e00\u58f0 \u53eb\u8bc1\u660e\u4ed6\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5f20\u96ea\u6ce2\u5df2\u88ab\u65a9\u4e86\u4e00\u5200\uff0c\u8eab\u5f62\u597d\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u5e78\u4e8f\u8036\u5f8b\u7384\u5143\u6765\u5f97\u53ca\u65f6\uff0c\u90a3\u4eba\u542c\u89c1\u54c8\u5fc5\u56fe\u7684\u53eb\u58f0\uff0c\u7b2c\u4e8c\u5200\u4e0d\u6562\u65a9\u4e0b\u53bb\u3002 \u3000\u3000\u8fd8\u6709\u4e03\u516b\u4e2a\u4eba\u6b63\u5411\u7740\u5f20\u96ea\u6ce2\u8dd1\u6765\u7684\uff0c\u4ed6\u4eec\u6050\u9632\u8036\u5f8b\u7384\u5143\u4f24\u4e86\u94a6\u5dee\u7684\u6027\u547d\uff0c\u4e5f\u987f\u65f6\u6b62\u6b65 \u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u58f0\u5230\u4eba\u5230\uff0c\u5341\u6b65\u5f00\u5916\uff0c\u4e00\u8bb0\u5288\u7a7a\u638c\u5148\u53d1\u51fa\u53bb\u3002\u780d\u4f24\u5f20\u96ea\u6ce2\u90a3\u4e2a\u9ed1\u9053\u9ad8\u624b\u672c\u6765\u662f \u7ec3\u6709\u94c1\u5e03\u886b\u529f\u592b\u7684\uff0c\u867d\u7136\u5c1a\u672a\u7ec3\u5f97\u5200\u67aa\u4e0d\u5165\uff0c\u8f7b\u6613\u5df2\u662f\u4f24\u4ed6\u4e0d\u5f97\u3002\u4f46\u5728\u8036\u5f8b\u7384\u5143\u8fd9\u80a1\u5288\u7a7a\u638c \u4e4b\u4e0b\uff0c\u4ed6\u53ea\u89c9\u80f8\u53e3\u5982\u53d7\u94c1\u9524\u4e00\u51fb\uff0c\u4e94\u810f\u516d\u8151\u90fd\u597d\u4f3c\u8981\u7ffb\u8f6c\u8fc7\u6765\uff0c\u53e3\u5410\u9c9c\u8840\uff0c\u4eba\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u597d\u5728\u4f60\u6ca1\u6709\u65a9\u7b2c\u4e8c\u5200\uff0c\u5426\u5219\u6211\u5c31\u8981\u4e86\u4f60\u7684\u6027\u547d\uff01\u7ed9\u6211\u6eda\u5427\uff01\u201c\u8036\u5f8b\u7384\u5143\u559d\u9053\u3002 \u3000\u3000\u90a3\u4eba\u5fcd\u7740\u5267\u75db\uff0c\u722c\u4e86\u8d77\u6765\u3002\u542c\u5f97\u2014\u4e2a\u201c\u6eda\u201d\u5b57\uff0c\u5f53\u771f\u662f\u5982\u5949\u8c15\u65e8\uff0c\u6492\u817f\u5c31\u8dd1\u3002 \u3000\u3000\u5728\u5f20\u96ea\u6ce2\u9644\u8fd1\u7684\u536b\u58eb\u4e5f\u90fd\u907f\u5f00\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u51fa\u6307\u70b9\u4e86\u5f20\u96ea\u6ce2\u4f24\u53e3\u9644\u8fd1\u7684\u4e09\u5904\u7a74\u9053\uff0c\u4ed6\u8fd9\u70b9\u7a74\u662f\u53ef\u4ee5\u6b62\u8840\u4e4b\u7528\u7684\u3002\u7136\u540e\u628a\u54c8 \u5fc5\u56fe\u653e\u5728\u5730\u4e0a\uff0c\u4e00\u811a\u8e0f\u7740\u4ed6\u7684\u80f8\u53e3\u3002\u201c\u4f60\u662f\u5170\u59d1\uff1f\u201d\u8036\u5f8b\u7384\u5143\u76ef\u7740\u5979\u95ee\u3002\u4ed6\u77e5\u9053\u5b8c\u989c\u9274\u8be1\u8ba1 \u591a\u7aef\uff0c\u867d\u7136\u4eb2\u773c\u770b\u89c1\u201c\u5170\u59d1\u201d\u53d7\u4f24\u4e0d\u5047\uff0c\u4ed6\u8fd8\u662f\u4e0d\u80fd\u4e0d\u8981\u8bc1\u660e\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5170\u59d1\uff0c\u6211\u662f\u51b2\u513f\u7684\u6bcd\u4eb2\u3002\u201d\u5979\u62ff\u51fa\u4e00\u628a\u6247\u5b50\u6447\u4e86\u4e00\u6447\uff0c\u201c\u6211\u4e5f\u662f\u4ece \u51b2\u513f\u7237\u7237\u7684\u624b\u4e2d\u63a5\u8fc7\u8fd9\u628a\u6247\u5b50\u7684\u4eba\u3002\u201d\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u8fd9\u628a\u6247\u5b50\u7684\u4e3b\u4eba\uff0c\u73b0\u5728\u53ef\u4ee5\u7269\u5f52\u539f\u4e3b\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u6536\u56de\u8fd9\u628a\u6247 \u5b50\uff0c\u5c31\u5f97\u6536\u6211\u7684\u51b2\u513f\u3002\u201d\u8036\u5f8b\u7384\u5143\u63a5\u8fc7\u6247\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u8bf4\u8fc7\u7684\u8bdd\u4e00\u5b9a\u7b97\u6570\uff01\u201d \u3000\u3000\u4ed6\u63a5\u8fc7\u6247\u5b50\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u5c11\u5b64\u4e3a\u5ba2\u65e9\uff0c\u591a\u96be\u8bc6\u541b\u8fdf\u3002\u63a9\u6ce3\u7a7a\u76f8\u5411\uff0c\u98ce\u5c16\u4f55\u6240\u671f\uff1f\u6a80 \u516c\u3001\u6a80\u516c\uff0c\u6211\u8f9c\u8d1f\u4e86\u4f60\u7684\u671f\u671b\uff0c\u4f46\u4f60\u7684\u9057\u547d\uff0c\u6211\u4e00\u5b9a\u66ff\u4f60\u505a\u5230\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8036\u5f8b\u5148\u751f\uff0c\u591a\u8c22\u4f60\u7b54\u5141\u6211\u516c\u516c\u7684\u8bf7\u6c42\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u628a\u51b2\u513f\u627e\u56de\u6765\u884c\u62dc\u5e08 \u793c\uff0c\u8fd9\u3001\u8fd9\u2014\u2014\u201d \u3000\u3000\u201c\u600e\u4e48\u529e\uff1f\u201d\u8fd9\u4e09\u4e2a\u5b57\u5c1a\u672a\u51fa\u53e3\uff0c\u53ea\u542c\u5f97\u8036\u5f8b\u7384\u5143\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u884c\u8fc7\u62dc\u5e08\u793c \u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u53c8\u559c\u53cc\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u627e\u5230\u4e86\u8fd9\u4e2a\u5b69\u5b50\uff1f\u201d\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u6b63\u5728\u4e00 \u5904\u5730\u65b9\u7b49\u7740\u4f60\u5462\uff0c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u8981\u4f60\u6062\u590d\u5170\u59d1\u7684\u8eab\u4efd\uff0c\u7b54\u6211\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6211\u77e5\u9053\u5170\u59d1\u662f\u5f97\u5230\u592b\u4eba\u53e6\u773c\u76f8\u770b\u7684\uff0c\u4e5f\u53ea\u6709\u5170\u59d1\u7684\u8bdd\u6211\u624d\u76f8\u4fe1\u3002\u6240\u4ee5\u6211 \u8981\u5170\u59d1\u544a\u8bc9\u6211\uff0c\u90a3\u5c0f\u4e2b\u73af\u521a\u624d\u8bf4\u7684\u8bdd\u662f\u4e0d\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u4f4e\u4e0b\u4e86\u5934\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u4e0d\u4f46\u662f\u592b\u4eba\u8d70\u4e86\uff0c\u6211\u7684\u5c0f\u5973\u513f\u4e5f\u7ed9\u592b\u4eba\u5e26\u8d70 \u4e86\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5446\u4e86\u7247\u523b\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u6211\u6765\u8fdf\u4e86\u3002\u6211\u7b97\u662f\u6765\u8fdf\u4e86\uff01\u201d\u6155\u5730\u72c2\u7b11\u8d77\u6765\uff0c\u541f\u9053\uff1a \u4e1c\u541b\u81ea\u6709\u56de\u5929\u529b\uff0c\u7740\u628a\u82b1\u679d\u5e26\u6708\u5f52\uff01\u563f\u563f\uff0c\u8bf4\u4ec0\u4e48\u56de\u5929\u4e4b\u529b\uff0c\u53ea\u8d62\u5f97\u6c34\u6d41\u82b1\u8c22\u4e24\u65e0\u60c5\uff01\u773c\u524d \u7a7a\u6709\u6ee1\u56ed\u9526\u7ee3\uff0c\u8d4f\u82b1\u7684\u4eba\u5df2\u7ecf\u4e0d\u662f\u4f60\u4e86\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u89c1\u4ed6\u5982\u75af\u4f3c\u75f4\uff0c\u4e0d\u89c9\u5fc3\u91cc\u53d1\u614c\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u8036\u5f8b\u5148\u751f\uff0c\u8036\u5f8b\u5148\u751f\uff0c\u54b1\u4eec\u8be5\u8d70\u4e86 \u5427\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u597d\u50cf\u4ece\u68a6\u4e2d\u7ed9\u60ca\u9192\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8be5\u8d70\u4e86\u3002\u4f60\u8d70\u5f97\u52a8\u5417\uff1f\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6211\u60f3\uff0c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u638f\u51fa\u91d1\u521b\u836f\uff0c\u6b63\u5f85\u7ed9\u5979\u6577\u4e0a\uff0c\u5ffd\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u4e24\u6761\u957f\u7ef3\u7a81\u7136\u6a2a\u626b\u8fc7\u6765\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u7684\u6b66\u529f\u8f83\u5f31\uff0c\u8eb2\u907f\u4e0d\u53ca\uff0c\u767b\u65f6\u7ed9\u7ef3\u5708\u5957\u4e0a\u4e86\u8116\u5b50\u3002 \u3000\u3000\u5e78\u597d\u8036\u5f8b\u7384\u5143\u51fa\u624b\u4e5f\u5feb\uff0c\u53cc\u6307\u4e00\u631f\uff0c\u8d5b\u5982\u5229\u526a\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u628a\u521a\u624d\u5957\u4e0a\u5f20\u96ea\u6ce2\u8116\u5b50 \u90a3\u6761\u7ef3\u7d22\u526a\u65ad\u3002\u4f46\u53e6\u4e00\u6761\u957f\u7ef3\u5374\u5df2\u628a\u54c8\u5fc5\u56fe\u5377\u53bb\u4e86\uff01 \u3000\u3000\u539f\u6765\u5b8c\u989c\u9274\u624b\u4e2d\u6709\u4e24\u540d\u5584\u4e8e\u4f7f\u7ef3\u5708\u6355\u517d\u7684\u9ad8\u624b\uff0c\u8d81\u7740\u4ed6\u5fc3\u795e\u4e0d\u5b9a\u800c\u53c8\u521a\u5728\u66ff\u5f20\u96ea\u6ce2\u6577\u836f \u4e4b\u9645\uff0c\u6765\u4e00\u4e2a\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u628a\u54c8\u5fc5\u56fe\u593a\u53bb\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e00\u89c1\u54c8\u5fc5\u56fe\u8131\u8138\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u7ed9\u6211\u628a\u4ed6\u4eec\u62ff\u4e0b\uff0c\u6d3b\u7684\u4e0d\u6210\uff0c\u6b7b\u7684\u4e5f\u8981\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u6012\u9053\uff1a\u201c\u6211\u7528\u4e0d\u7740\u631f\u6301\u4eba\u8d28\uff0c\u770b\u4f60\u4eec\u53c8\u80fd\u5948\u6211\u4f55\u54c9\uff01\u563f\u563f\uff0c\u5b8c\u989c\u9274\uff0c\u4f60\u60f3\u6740 \u6211\uff0c\u6050\u6015\u4e5f\u6ca1\u90a3\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u5927\u559d\u58f0\u4e2d\uff0c\u5288\u7a7a\u638c\u518d\u5ea6\u53d1\u51fa\u3002 \u3000\u3000\u7528\u957f\u7ef3\u628a\u54c8\u5fc5\u56fe\u5377\u8d70\u90a3\u4e2a\u6c49\u5b50\u9996\u5f53\u5176\u51b2\uff0c\u4e00\u4e2a\u5012\u683d\u8471\u4ece\u5047\u5c71\u4e0a\u6eda\u4e0a\u6765\u3002\u4f46\u54c8\u5fc5\u56fe\u65e9\u5df2\u7ed9 \u522b\u4eba\u63a5\u8fc7\u53bb\uff0c\u8dd1\u5f00\u4e86\u3002\u8036\u5f8b\u7384\u5143\u593a\u8fc7\u90a3\u6761\u957f\u7ef3\uff0c\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u957f\u7ef3\u6325\u53bb\uff0c\u5957 \u4e0a\u53e6\u4e00\u4e2a\u6c49\u5b50\u7684\u8116\u5b50\uff0c\u8fd9\u4e2a\u6c49\u5b50\u6b63\u662f\u521a\u624d\u7528\u7ef3\u5708\u5957\u4e0a\u5f20\u96ea\u6ce2\u7684\u90a3\u4e2a\u4eba\uff0c\u5982\u4eca\u8eab\u53d7\u5176\u82e6\uff0c\u88ab\u8036 \u5f8b\u7384\u5143\u4e00\u52d2\uff0c\u767b\u65f6\u6bd9\u547d\uff01 \u3000\u3000\u4f17\u536b\u571f\u7eb7\u7eb7\u62e5\u4e0a\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u559d\u9053\uff1a\u201c\u5b8c\u989c\u9274\uff0c\u6211\u5e76\u4e0d\u60f3\u6ee5\u6740\u65e0\u8f9c\uff0c\u4eca\u65e5\u662f\u4f60\u903c\u6211\u5927\u5f00\u6740\u6212\uff01\u201d \u3000\u3000\u201c\u6a80\u592b\u4eba\uff0c\u4f60\u7d27\u8ddf\u7740\u6211\uff01\u201d\u4ed6\u5429\u5490\u4e86\u5f20\u96ea\u6ce2\uff0c\u4fbf\u5373\u51b2\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u5f53\u771f\u662f\u6709\u5982\u864e\u4eba\u7f8a\u7fa4\uff0c\u53ea\u89c1\u4ed6\u62f3\u6253\u811a\u8e4b\uff0c\u638c\u5288\u6307\u6233\uff0c\u6321\u8005\u65e0\u4e0d\u62ab\u9761\uff01 \u3000\u3000\u6709\u7684\u7ed9\u4ed6\u6253\u65ad\u808b\u9aa8\uff0c\u6709\u7684\u7ed9\u4ed6\u5288\u7834\u5934\u9885\uff0c\u6709\u7684\u7ed9\u4ed6\u6233\u7740\u5173\u8282\u8981\u5bb3\uff0c\u6b7b\u7684\u6b7b\uff0c\u4f24\u7684\u4f24\uff0c\u60e8 \u53eb\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u7a81\u7136\u51fa\u73b0\u4e86\u56db\u540d\u9ed1\u8863\u9053\u58eb\uff0c\u4e00\u5f0f\u6253\u626e\uff0c\u624b\u4e2d\u62ff\u7684\u4e5f\u662f\u4e00\u5f0f\u660e\u6643\u6643\u7684\u957f\u5251\u3002 \u3000\u3000\u56db\u67c4\u957f\u5251\u4ece\u4e1c\u5357\u897f\u5317\u540c\u65f6\u653b\u5230\uff0c\u62db\u5f0f\u6839\u8fa3\uff0c\u5feb\u6377\u5f02\u5e38\uff01 \u3000\u3000\u4ed6\u4eec\u5251\u5feb\uff0c\u8036\u5f8b\u7384\u5143\u7684\u8eab\u6cd5\u66f4\u5feb\uff0c\u6ef4\u6e9c\u6e9c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4f7f\u51fa\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\uff0c\u94ee\u3001 \u94ee\u3001\u94ee\u3001\u6323\u56db\u4e0b\u65ad\u91d1\u65a9\u7389\u4e4b\u58f0\uff0c\u628a\u56db\u67c4\u957f\u5251\u5168\u90fd\u5f39\u5f00\u4e86\u3002 \u3000\u3000\u4f46\u53ea\u80fd\u5f39\u5f00\uff0c\u5374\u672a\u80fd\u628a\u4ed6\u4eec\u7684\u957f\u5251\u5f39\u51fa\u624b\u53bb\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5f20\u96ea\u6ce2\u7684\u8863\u8896\u5df1\u7ecf\u7ed9\u4e00\u628a\u957f\u5251\u524a\u53bb\u4e86\u4e00\u5e45\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u7684\u201c\u5f39\u6307\u795e\u901a\u201d\u529f\u592b\u975e\u540c\u5c0f\u53ef\uff0c\u80fd\u591f\u62b5\u6321\u5f97\u4f4f\u4ed6\u4e00\u5f39\u4e4b\u529b\u7684\u3002\u5df2\u7ecf\u7b97\u5f97\u662f\u4e00\u6d41 \u9ad8\u624b\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u51b7\u7b11\u559d\u9053\uff1a\u201c\u60f3\u4e0d\u80fd\u6b66\u5f53\u6d3e\u7684\u9ad8\u4eba\u7adf\u4e5f\u7518\u5fc3\u6765\u505a\u91d1\u864f\u7684\u9e70\u722a\uff01\u201d\u90a3\u56db\u4e2a\u9ed1\u8863\u9053 \u4e0a\u53ea\u662f\u4f7f\u4eba\u4e00\u62db\uff0c\u5c31\u7ed9\u4ed6\u559d\u7834\u6765\u5386\uff0c\u4e5f\u662f\u4e0d\u7981\u5fc3\u91cc\u4e00\u60ca\u3002 \u3000\u3000\u4e3a\u9996\u7684\u9053\u58eb\u559d\u9053\uff1a\u201c\u5982\u4eca\u662f\u5927\u91d1\u5929\u4e0b\uff0c\u987a\u8005\u660c\uff0c\u9006\u8005\u4ea1\uff01\u4f60\u65e2\u8bc6\u5f97\u6211\u4eec\u6765\u5386\uff0c\u8fd8\u4e0d\u675f\u624b \u5c31\u64d2\uff01\u201d \u3000\u3000\u5927\u559d\u58f0\u4e2d\uff0c\u65e9\u5df2\u5e03\u6210\u5251\u9635\uff0c\u72c2\u98ce\u66b4\u96e8\u5730\u5411\u4ed6\u8fdb\u88ad\u3002\u5f20\u96ea\u6ce2\u5728\u8036\u5f8b\u7384\u5143\u4fdd\u62a4\u4e4b\u4e0b\uff0c\u597d\u51e0\u6b21 \u4e5f\u9669\u4e9b\u88ab\u4ed6\u4eec\u523a\u4e2d\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u9661\u5730\u559d\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u672c\u662f\u597d\u7684\uff0c\u53ef\u501f\u4f60\u4eec\u5b66\u5f97\u8fd8\u672a\u5230\u5bb6\uff01 \u3000\u3000\u201d\u4e00\u4e2a\u201c\u7a7f\u9488\u5f15\u7ebf\u201d\u7684\u624b\u6cd5\uff0c\u865a\u7a7a\u4e00\u5f15\uff0c\u6307\u5934\u5e76\u672a\u78b0\u5230\u5251\u5c16\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e24 \u67c4\u5411\u4ed6\u523a\u6765\u7684\u957f\u5251\u5df2\u7ecf\u78b0\u5728\u4e00\u8d77\u3002 \u3000\u3000\u6b66\u5f53\u56db\u9053\u5e03\u6210\u7684\u5251\u9635\u672c\u662f\u5929\u8863\u65e0\u7f1d\u7684\uff0c\u8fd9\u4e00\u4e0b\u5b50\u53ef\u9732\u51fa\u4e86\u7834\u7efd\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8036\u5f8b \u7384\u5143\u865a\u62db\u5316\u5b9e\uff0c\u4f7f\u51fa\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u5df2\u7ecf\u662f\u628a\u7b2c\u4e09\u4e2a\u9053\u58eb\u7684\u957f\u5251\u62a2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u201c\u8ba9\u4f60\u4eec\u4e5f\u89c1\u8bc6\u6211\u7684\u5251\u6cd5\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u7b2c\u56db\u4e2a\u9053\u58eb\u5df2\u7ed9\u4ed6\u523a\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u957f\u5251\u4e92\u76f8\u78b0\u53bb\u7684\u9053\u58eb\u521a\u521a\u5206\u5f00\uff0c\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u4e24\u4eba\u90fd\u611f\u89c9\u80f8\u53e3\u4e00\u9ebb\uff0c\u6765\u4e0d \u53ca\u6a2a\u5251\u62db\u67b6\uff0c\u4ea6\u90fd\u5df2\u7ed9\u523a\u7740\u4e86\u7a74\u9053\u3002\u8fd9\u65f6\uff0c\u8036\u5f8b\u7384\u5143\u90a3\u53e5\u8bdd\u624d\u521a\u521a\u8bf4\u5b8c\uff01 \u3000\u3000\u7ed9\u4ed6\u64d2\u4e86\u5175\u5203\u7684\u90a3\u540d\u9053\u58eb\u614c\u5fd9\u9003\u8d70\uff0c\u8036\u5f8b\u7384\u5143\u559d\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u539a\u6b64\u8584\u5f7c\uff0c\u4ed6\u4eec\u8eba\u4e0b\uff0c\u4f60 \u4e5f\u8eba\u4e0b\u5427\uff01\u201d\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u53ea\u4e00\u4e2a\u8d77\u4f0f\uff0c\u5c31\u8ffd\u4e0a\u90a3\u4eba\uff0c\u523a\u7740\u4ed6\u7684\u540e\u5fc3\u7a74\u9053\u3002 \u3000\u3000\u56db\u4e2a\u9053\u58eb\u90fd\u5012\u4e0b\u53bb\u4e86\uff01\u5b8c\u989c\u9274\u8bf7\u6765\u90a3\u4e9b\u80fd\u4eba\uff0c\u89c1\u6b66\u5f53\u6d3e\u7684\u56db\u540d\u9ad8\u624b\u90fd\u7ed9\u4ed6\u632b\u8d25\uff0c\u5413\u5f97\u6709 \u534a\u6570\u4ee5\u4e0a\u754f\u7f29\u4e0d\u524d\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u559d\u9053\uff1a\u201c\u6321\u6211\u90fd\u6b7b\uff0c\u907f\u6211\u8005\u751f\uff01\u201d\u643a\u5f20\u96ea\u6ce2\u7ee7\u7eed\u5411\u524d\u95ef\uff0c\u4e00\u4e2a\u8eab\u8eaf\u5982\u540c\u94c1\u5854\u4f3c \u7684\u5927\u6c49\uff0c\u624b\u821e\u72ec\u811a\u94dc\u4eba\u6321\u7740\u4ed6\u4eec\u53bb\u8def\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u5b8c\u989c\u9274\u624b\u4e0b\u7b2c\u4e00\u5927\u529b\u58eb\uff0c\u624b\u6301\u7684\u72ec\u811a\u94dc\u4eba\u91cd\u903e\u4e03\u5341\u4e8c\u65a4\u3002\u4ed6\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\u628a\u72ec \u811a\u94dc\u4eba\u671d\u7740\u8036\u5f8b\u7384\u5143\u6253\u4e0b\u6765\uff0c\u559d\u9053\uff1a\u201c\u9006\u8d3c\u6562\u51fa\u72c2\u8a00\uff0c\u4e14\u770b\u8c01\u6b7b\u8c01\u8bdd\uff01\u201d \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5251\u523a\u7740\u94dc\u4eba\uff0c\u706b\u82b1\u98de\u6e85\u3002 \u3000\u3000\u957f\u5251\u5e76\u6ca1\u65ad\u6298\uff0c\u94dc\u4eba\u8eab\u4e0a\u5374\u5df2\u73b0\u88c2\u75d5\u3002\u8fd9\u67c4\u957f\u5251\u5e76\u975e\u5b9d\u5251\uff0c\u91cd\u91cf\u4e0d\u8fc7\u4e09\u65a4\u3002\u7adf\u7136\u80fd\u6321\u4e03 \u5341\u4e8c\u65a4\u91cd\u94dc\u4eba\u7684\u4e00\u51fb\uff0c\u5f53\u7136\u662f\u7531\u4e8e\u8036\u5f8b\u7384\u5143\u5143\u6df1\u539a\u7684\u5185\u529f\u6240\u81f4\u4e86\u3002 \u3000\u3000\u5927\u529b\u58eb\u5403\u4e86\u4e00\u60ca\uff0c\u5012\u9000\u51e0\u6b65\uff0c\u8036\u5f8b\u7384\u5143\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u77e5\u9053\u86ee\u529b\u4e0d\u8db3\u6043\u4e86\u5427\uff1f\u4e0d\u8fc7\u3002\u4f60 \u8fd9\u83bd\u592b\u5012\u8fd8\u4e0d\u503c\u5f97\u6211\u53d6\u4f60\u7684\u6027\u547d\u4e00\u4e00\u201d\u8bf4\u5230\u201c\u547d\u201d\u5b57\uff0c\u5927\u529b\u58eb\u8098\u5c16\u7684\u66f2\u6c60\u7a74\u3001\u819d\u76d6\u7684\u73af\u8df3 \u7a74\uff0c\u864e\u53e3\u7684\u5173\u5143\u7a74\u90fd\u5df4\u4e2d\u5251\uff01 \u3000\u3000\u201c\u6254\u6389\u94dc\u4eba\uff0c\u4f60\u4e5f\u7ed9\u6211\u8eba\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u5927\u529b\u58eb\u4e00\u58f0\u543c\uff0c\u679c\u7136\u5c31\u597d\u50cf\u5949\u4e86\u5723\u65e8\u4f3c\u7684\uff0c\u4e00\u4e00\u7167\u529e\uff0c\u94dc\u4eba\u8131\u624b\u98de\u51fa\uff0c\u4ed6\u90a3\u94c1\u5854 \u4f3c\u7684\u8eab\u8eaf\u4e5f\u5012\u4e0b\u53bb\u3002\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u94dc\u4eba\u98de\u51fa\u6253\u584c\u4e86\u5047\u5c71\u4e00\u89d2\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u54c8\u54c8\u5927\u7b11\uff0c\u7ee7\u7eed\u5f80\u524d\u95ef\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u7ed9\u6211\u7ad9\u4f4f\uff01\u201d\u662f\u4e24\u4e2a\u4eba\u540c\u58f0\u8bf4\u7684\uff0c\u8ddd\u79bb\u7ea6\u5728\u5341\u6b65\u4e4b\u5916\uff0c\u58f0\u51fa\u638c\u53d1\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u867d\u7136\u6ca1\u6709\u201c\u7ad9\u4f4f\u201d\uff0c\u524d\u5954\u4e4b\u52bf\uff0c\u4e5f\u767b\u65f6\u53d7\u963b\u4e86\u3002\u8fd9\u4e24\u4eba\u7684\u5288\u7a7a\u638c\u529b\u5408\u800c\u4e3a\u4e00\uff0c \u7adf\u7136\u5927\u5f97\u51fa\u5947\uff0c\u4ee5\u8036\u5f8b\u7384\u5143\u90a3\u4e48\u6df1\u539a\u7684\u5185\u529f\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u547c\u5438\u4ea6\u662f\u4e3a\u4e4b\u4e0d\u7545\u3002\u8036\u5f8b\u7384\u5143\u5fc3\u5934 \u4e00\u51db\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u62e6\u963b\u4ed6\u4eec\u53bb\u8def\u7684\u662f\u4e24\u4e2a\u76f8\u8c8c\u76f8\u540c\u7684\u8eab\u6750\u7684\u9ad8\u5927\u7684\u8001\u4eba\u3002\u8036\u5f8b\u7384\u5143\u547c\u4e86\u4e00 \u58f0\u9053\uff1a\u201c\u7941\u8fde\u4e8c\u8001\u4e5f\u6765\u52a9\u7ea3\u4e3a\u8650\uff01\u201d \u3000\u3000\u201c\u7941\u8fde\u4e8c\u8001\u201d\u662f\u4e00\u6bcd\u6240\u751f\u7684\u5144\u5f1f\uff0c\u8001\u5927\u53eb\u5e05\u514b\u6bb7\uff0c\u8001\u4e8c\u53eb\u5e05\u514b\u5546\uff0c\u5144\u5f1f\u4e8c\u4eba\u5c11\u5e74\u65f6\u5019\u66fe \u6a2a\u884c\u6cb3\u6eaf\uff0c\u4e2d\u5e74\u4ee5\u540e\u5728\u7941\u8fde\u5c71\u9690\u5c45\uff0c\u5df2\u6709\u5c06\u8fd1\u4e09\u5341\u5e74\u6c5f\u6e56\u4e0a\u4e0d\u95fb\u4ed6\u4eec\u7684\u6d88\u606f\u4e86\u3002 \u3000\u3000\u201c\u4f60\u8fd9\u5c0f\u5b50\u592a\u8fc7\u7316\u72c2\uff0c\u8001\u592b\u770b\u4e0d\u987a\u773c\uff01\u201d\u5144\u5f1f\u5fc3\u610f\u76f8\u901a\uff0c\u8bf4\u8bdd\u4e5f\u662f\u4e0d\u7ea6\u800c\u540c\uff0c\u5b57\u53e5\u5982 \u4e00\u3002 \u3000\u3000\u4e0d\u8fc7\u51fa\u624b\u53ef\u4e0d\u540c\u4e86\u3002\u5e05\u8001\u5927\u4f7f\u7684\u662f\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u4f38\u51fa\u94a2\u7b4b\u822c\u7684\u5927\u624b\uff0c\u4e94\u6307\u6709\u5982\u9e70 \u722a\uff0c\u7adf\u7136\u8fce\u7740\u8036\u5f8b\u7384\u5143\u523a\u8fc7\u6765\u7684\u957f\u5251\u5c31\u6293\uff01 \u3000\u3000\u5e05\u8001\u4e8c\u5219\u53cc\u638c\u9f50\u53d1\uff0c\u4f7f\u7684\u662f\u4e00\u62db\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u649e\u51fb\u8036\u5f8b\u7384\u5143\u7684\u592a\u9633\u7a74\u3002 \u3000\u3000\u201c\u7941\u8fde\u4e8c\u8001\u201d\u622a\u51fb\u8036\u5f8b\u7384\u5143\u4e4b\u9645\uff0c\u5f20\u96ea\u6ce2\u4e5f\u5728\u540c\u65f6\u53d7\u5230\u653b\u51fb\u3002 \u3000\u3000\u653b\u51fb\u5979\u7684\u662f\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\uff0c\u653b\u51fb\u7684\u65b9\u6cd5\u4e0e\u4f17\u4e0d\u540c\uff0c\u4ed6\u50cf\u4e00\u4e2a\u8089\u7403\u5728\u5730\u4e0a\u6eda\u52a8\uff0c\u624b\u6301 \u4e24\u628a\u94a2\u5200\uff0c\u51e0\u4e4e\u662f\u8d34\u7740\u5730\u9762\u800c\u6765\u780d\u5f20\u96ea\u6ce2\u7684\u53cc\u8db3\uff0c\u539f\u6765\u8fd9\u4e2a\u662f\u7cbe\u4e8e\u7528\u201c\u5730\u5802\u5200\u201d\u7684\u9ad8\u624b\uff0c\u4ed6 \u4eec\u6311\u9009\u4e00\u4e2a\u957f\u4e8e\u201c\u6eda\u5730\u638c\u201d\u529f\u592b\u7684\u4eba\u6765\u653b\u51fb\u5f20\u96ea\u6ce2\uff0c\u90a3\u662f\u56e0\u4e3a\u5f20\u96ea\u6ce2\u5728\u8036\u5f8b\u7384\u5143\u7684\u4fdd\u62a4\u4e4b \u4e0b\uff0c\u53ea\u6709\u4e13\u653b\u4e0b\u76d8\uff0c\u53ef\u671b\u5f97\u901e\u3002 \u3000\u3000\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u4e09\u5927\u9ad8\u624b\u90fd\u4f7f\u51fa\u4e86\u5e73\u751f\u6240\u5b66\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u5251\u638c\u517c\u65bd\uff0c\u5251\u6cd5\u5feb\u5982\u95ea\u7535\uff0c\u523a\u5411\u5e05\u8001\u5927\u7684\u54bd\u5589\u3002\u5de6\u638c\u4e00\u62db\u201c\u9f99\u95e8\u53e0\u6d6a\u201d\uff0c\u4ee5\u5355 \u638c\u4e4b\u529b\u4e0e\u5e05\u8001\u4e8c\u53cc\u638c\u4e4b\u529b\u76f8\u6297\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u5feb\u5982\u95ea\u7535\uff0c\u5fc3\u91cc\u60f3\u9053\u770b\u4f60\u5982\u4f55\u6562\u593a\u6211\u7684\u201c\u5175\u5203\u201d\uff0c\u90a3\u77e5\u5e08\u8001\u5927\u7adf\u6562\u786c\u62a2\u3002\u970d\u7684 \u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8036\u5f8b\u7384\u5143\u7684\u5251\u5c16\u672a\u523a\u7740\u4ed6\u7684\u54bd\u5589\uff0c\u5251\u5c31\u7ed9\u4ed6\u6293\u7740\u4e86\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\u3002\u8036\u5f8b\u7384\u5143\u4ece\u6b66\u5f53\u9053\u58eb\u624b\u4e2d\u593a\u6765\u7684\u957f\u5251\uff0c\u7adf\u7136\u7ed9\u62d7\u65ad\u4e86\u3002 \u3000\u3000\u5e05\u8001\u5927\u62d7\u65ad\u4e86\u4ed6\u7684\u957f\u5251\uff0c\u6309\u8bf4\u5df2\u662f\u5360\u4e86\u7edd\u5bf9\u4e0a\u98ce\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5374\u672a\u6562\u7eed\u65bd\u6740\u624b\uff0c\u53cd \u800c\u597d\u50cf\u6015\u5bf9\u65b9\u8ffd\u51fb\u4f3c\u7684\uff0c\u6025\u5fd9\u659c\u8dc3\u6570\u6b65\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u58f0\u97f3\u6c89\u54d1\uff01 \u3000\u3000\u539f\u6765\u5e05\u514b\u6bb7\u4e4b\u6240\u4ee5\u6562\u7528\u8089\u638c\u53bb\u8036\u5f8b\u7384\u5143\u7684\u5251\uff0c\u5e76\u975e\u56e0\u4e3a\u4ed6\u7684\u529f\u529b\u5728\u8036\u5f8b\u7384\u5143\u4e4b\u4e0a\uff0c\u4e5f\u5e76 \u975e\u56e0\u4e3a\u4ed6\u7684\u624b\u6cd5\u6bd4\u8036\u5f8b\u7384\u5143\u7684\u5251\u6cd5\u66f4\u5feb\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u6234\u6709\u767d\u91d1\u7f16\u7ec7\u7684\u624b\u5957\u4e4b\u6545\u3002\u4ed6\u8fd9\u624b\u5957\u593a \u5bfb\u5e38\u7684\u5200\u5251\u662f\u523a\u4e0d\u7a7f\uff0c\u523a\u4e0d\u7834\u7684\uff0c\u800c\u4ed6\u65e9\u5df2\u77e5\u9053\u8fd9\u628a\u5251\u4e0d\u662f\u5b9d\u5251\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u7ed3\u679c\u5c31\u5f04\u6210\u4e86\u5251\u65ad\u3001\u4eba\u4f24\u3002\u65ad\u5251\u7684\u662f\u8036\u5f8b\u7384\u5143\uff0c\u53d7\u4f24\u7684\u5374\u662f\u5e05\u514b\u6bb7\u3002\u56e0\u4e3a\u8036 \u5f8b\u7384\u5143\u4ece\u522b\u4eba\u624b\u4e2d\u593a\u6765\u7684\u8fd9\u628a\u5251\uff0c\u5251\u8d28\u867d\u7136\u4e0d\u4f73\uff0c\u4f46\u8036\u5f8b\u7384\u5143\u8d2f\u6ce8\u5251\u5c16\u7684\u5185\u529b\u5374\u662f\u975e\u540c\u5c0f \u53ef\u3002\u5e05\u514b\u6bb7\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\u88ab\u4ed6\u8fd9\u80a1\u5185\u529b\u649e\u7531\uff0c\u4e00\u6761\u53f3\u81c2\u767b\u65f6\u9178\u9ebb\uff0c\u8f6f\u7ef5\u7ef5\u7684\u5782\u4e0b\u53bb\uff0c\u4e0d \u542c\u4f7f\u5524\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u662f\u540c\u65f6\u5e94\u4ed8\u7941\u8fde\u4e8c\u8001\u7684\uff0c\u638c\u529b\u4ea4\u51fb\uff0c\u58f0\u5982\u95f7\u96f7\uff0c\u548c\u957f\u5251\u7ed9\u62d7\u6298\u7684\u65ad\u91d1\u5207\u7389\u4e4b\u58f0 \u6df7\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5e05\u514b\u5546\u9000\u540e\u4e09\u6b65\uff0c\u6253\u4e86\u4e24\u4e2a\u76d8\u65cb\uff0c\u65b9\u59cb\u7a33\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u53ef\u662f\u8036\u5f8b\u7384\u5143\u4e5f\u4e0d\u80fd\u4e58\u80dc\u8ffd\u51fb\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4ec5\u8981\u5e94\u4ed8\u7941\u8fde\u4e8c\u8001\uff0c\u8fd8\u8981\u66ff\u5f20\u96ea\u6ce2\u6253\u53d1\u654c\u4eba\u3002 \u3000\u3000\u4ed6\u5728\u5251\u523a\u5e05\u514b\u6bb7\uff0c\u638c\u5288\u5e05\u514b\u5546\u7684\u540c\u65f6\uff0c\u53cd\u8db3\u4e00\u811a\u8e22\u51fa\u3002\u5f20\u96ea\u6ce2\u6b63\u5728\u7ed9\u90a3\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49 \u5b50\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u773c\u770b\u90a3\u6c49\u5b50\u7684\u53cc\u5200\u8d34\u5730\u780d\u6765\uff0c\u5f20\u96ea\u6ce2\u53d7\u4f24\u4e4b\u540e\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u5c0f\u817f\u975e\u4e2d\u5200\u4e0d \u53ef\uff0c\u8036\u5f8b\u7384\u5143\u8fd9\u4e00\u811a\u8e22\u5f97\u6070\u662f\u65f6\u5019\u3002 \u3000\u3000\u5728\u4ed6\u7684\u80cc\u540e\uff0c\u90a3\u6c49\u5b50\u5c31\u597d\u50cf\u76ae\u7403\u4e00\u6837\u98de\u4e86\u8d77\u6765\uff0c\u6454\u5728\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u80fd\u4e00\u52a8\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u867d\u7136\u4f7f\u51fa\u4e86\u6d51\u8eab\u89e3\u6570\uff0c\u8fd8\u662f\u4e0d\u80fd\u5bf9\u5f20\u96ea\u6ce2\u4fdd\u62a4\u5468\u5168\u3002 \u3000\u3000\u4e00\u679d\u6697\u7bad\u98de\u6765\uff0c\u5c04\u7740\u4e86\u5f20\u96ea\u6ce2\u7684\u540e\u5fc3\u3002\u6b63\u662f\u8036\u5f8b\u7384\u5143\u53cd\u8db3\u8e22\u51fa\u7684\u65f6\u5019\u3002\u4ed6\u5df2\u662f\u53cc\u624b\u4e00\u8db3 \u540c\u65f6\u4f7f\u7528\u4e86\uff0c\u4e0d\u53ef\u80fd\u66ff\u5f20\u96ea\u6ce2\u6253\u843d\u90a3\u679a\u4ece\u80cc\u540e\u5c04\u6765\u7684\u6697\u7bad\uff01 \u3000\u3000\u5f20\u96ea\u6ce2\u8fd9\u6b21\u6240\u53d7\u7684\u7bad\u4f24\u6bd4\u5979\u521a\u624d\u6240\u53d7\u7684\u5200\u4f24\u66f4\u91cd\uff0c\u767b\u65f6\u597d\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u7941\u8fde\u4e8c\u8001\u5598\u606f\u7a0d\u5b9a\uff0c\u53c8\u653b\u4e0a\u6765\u4e86\uff01 \u3000\u3000\u5e05\u8001\u4e8c\u559d\u9053\uff1a\u201c\u4f60\u5175\u5203\u5df2\u6298\uff0c\u8fd8\u4e0d\u6295\u964d\uff01\u201d \u3000\u3000\u5e05\u8001\u5927\u5219\u5ba2\u6c14\u5f97\u591a\uff0c\u8bf4\u9053\uff1a\u201c\u8bc6\u82f1\u96c4\u91cd\u82f1\u96c4\uff0c\u6211\u53ef\u60dc\u4f60\u8fd9\u8eab\u6b66\u529f\uff0c\u529d\u4f60\u8fd8\u662f\u6295\u964d\u7684 \u597d\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u51b7\u7b11\u9053\uff1a\u201c\u521a\u624d\u90a3\u62db\uff0c\u8c01\u80dc\u8c01\u8d1f\uff1f\u4f60\u4eec\u7adf\u6562\u5927\u8a00\u4e0d\u60ed\uff0c\u8981\u6211\u6295\u964d\uff0c\u77e5\u4e0d\u77e5 \u7f9e\uff1f\u201d \u3000\u3000\u7941\u8001\u5927\u80c0\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u521a\u624d\u90a3\u62db\uff0c\u662f\u4f60\u7a0d\u5360\u4e0a\u98ce\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u4e00\u65f6\u4fa5\u5e78\u7f62\u4e86\u3002 \u8ba4\u771f\u6253\u4e0b\u53bb\uff0c\u4f60\u81ea\u95ee\u80fd\u5728\u767e\u62db\u4e4b\u5185\uff0c\u80dc\u5f97\u6211\u4eec\u4e24\u4e2a\u5417\uff1f \u3000\u3000\u4f60\u4e0d\u8981\u5fd8\u8bb0\uff0c\u6a80\u592b\u4eba\u5df2\u7ecf\u53d7\u4f24\u4e86\uff01\u201d \u3000\u3000\u7941\u8fde\u4e8c\u8001\u7684\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u82e5\u8bba\u771f\u624d\u5b9e\u5b66\uff0c\u8036\u5f8b\u7384\u5143\u786e\u5b9e\u662f\u6ca1\u6709\u5728\u767e\u62db\u4e4b\u5185\u53d6\u80dc\u7684\u628a \u63e1\u3002\u5f20\u96ea\u6ce2\u53d7\u7684\u7bad\u4f24\u751a\u91cd\uff0c\u5018\u82e5\u8036\u5f8b\u7384\u5143\u5728\u767e\u62db\u4e4b\u5185\u4e0d\u80fd\u51fb\u8d25\u5bf9\u65b9\uff0c\u53ea\u6015\u5f20\u96ea\u6ce2\u5df2\u662f\u91cd\u4f24\u8eab \u4ea1\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u5e05\u8001\u5927\uff0c\u591a\u8c22\u4f60\u63d0\u9192\u6211\u3002\u6211\u672c\u6765\u60f3\u591a\u770b\u51e0\u62db\u4f60\u4eec\u7941\u8fde\u6d3e\u7684\u6b66\u529f \u4e86\uff0c\u73b0\u5728\u6700\u591a\u53ea\u80fd\u8ba9\u4f60\u65bd\u5c55\u4e09\u62db\u4e86\uff01\u201d \u3000\u3000\u5e05\u8001\u5927\u52c3\u7136\u53d8\u8272\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u597d\u8a00\u529d\u4f60\uff0c\u4f60\u7adf\u5982\u6b64\u72c2\u5984\uff01\u201d \u3000\u3000\u5e05\u8001\u4e8c\u6025\u6b32\u62a5\u521a\u624d\u7684\u4e00\u5251\u4e4b\u4ec7\uff0c\u559d\u9053\uff1a\u201c\u4ed6\u4e0d\u542c\u826f\u8a00\uff0c\u529d\u4ea6\u65e0\u76ca\uff0c\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u4e24\u5144\u5f1f\u5fc3\u610f\u76f8\u901a\uff0c\u540c\u65f6\u51fa\u624b\u3002\u4e00\u653b\u4e00\u5b88\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\u3002\u4ed6\u4eec\u81ea\u4ee5\u4e3a\u5df1\u7ecf\u6478\u5230\u4e86\u8036\u5f8b \u7384\u5143\u7684\u5e95\u7ec6\uff0c\u5982\u6b64\u6253\u6cd5\uff0c\u5148\u6c42\u7a33\u800c\u540e\u6c42\u80dc\uff0c\u7eb5\u7136\u80dc\u4e0d\u4e86\uff0c\u6700\u5c11\u4e5f\u53ef\u62b5\u6321\u767e\u62db\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u53d6\u51fa\u7389\u8427\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u624d\u662f\u6211\u7684\u5175\u5668\uff0c\u8ba9\u4f60\u4eec\u89c1\u8bc6\u89c1\u8bc6\u5427\uff01 \u3000\u3000\u201d \u3000\u3000\u5b8c\u989c\u9274\u5728\u5929\u9999\u4ead\u90a3\u8fb9\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u66a7\u7389\u8427\uff0c\u4f60\u4eec\u5c0f\u5fc3\u4e00\u4e00\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u8036\u5f8b\u7384\u5143\u5df2\u662f\u4ece\u6696\u4e3b\u8427\u4e2d\u5439\u51fa\u4e86\u4e00\u80a1\u7f61\u6c14\u3002 \u3000\u3000\u7941\u8fde\u4e8c\u8001\u66fe\u542c\u8fc7\u6696\u7389\u8427\u662f\u4ef6\u5f02\u5b9d\uff0c\u4f46\u8fd9\u4ef6\u5f02\u5b9d\u201c\u5f02\u201d\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4ed6\u4eec\u53ef\u5c31\u4e0d\u50cf\u5b8c\u989c\u9274 \u90a3\u6837\u662f\u4eb2\u8eab\u201c\u9886\u6559\u201d\u8fc7\u7684\u4e86\u3002 \u3000\u3000\u5e05\u8001\u5927\u6043\u7740\u6234\u7740\u91d1\u4e1d\u624b\u5957\uff0c\u4e00\u628a\u5411\u4ed6\u7684\u6696\u7389\u8427\u6293\u6765\uff01 \u3000\u3000\u8fd8\u672a\u6293\u7740\u7389\u8427\uff0c\u90a3\u80a1\u7f61\u6c14\u5df2\u662f\u89e6\u624b\u5982\u70eb\uff0c\u66f4\u8981\u547d\u7684\u662f\uff0c\u4ed6\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\u5df2\u88ab\u7f61\u6c14\u4fb5 \u5165\u3002\u8fd9\u4e00\u4e0b\u6bd4\u521a\u624d\u53d7\u5185\u529b\u6240\u9707\u66f4\u60e8\uff0c\u4e0d\u4f46\u4e00\u6761\u624b\u81c2\u4e0d\u542c\u4f7f\u5524\uff0c\u6574\u4e2a\u4eba\u4e5f\u597d\u50cf\u7a81\u7136\u89e6\u7535\u4e00\u822c\uff0c \u5168\u8eab\u9ebb\u75f9\uff01\u5e05\u8001\u5927\u521a\u521a\u5012\u4e0b\uff0c\u4ed6\u7684\u7389\u8427\u53c8\u8fce\u4e0a\u4e86\u5e05\u8001\u4e8c\u7684\u53cc\u638c\u3002 \u3000\u3000\u5e05\u8001\u4e8c\u624b\u638c\u4e00\u7ffb\uff0c\u5316\u638c\u4e3a\u6293\uff0c\u6293\u5411\u8036\u5f8b\u7384\u5143\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u3002\u540c\u65f6\u5de6\u638c\u6a2a\u79fb\uff0c\u5288\u95ee\u8036\u5f8b\u7384 \u5143\u7684\u808b\u9aa8\u3002\u4ed6\u8eab\u624d\u9ad8\u5927\uff0c\u6bd4\u8036\u5f8b\u7384\u5143\u9ad8\u51fa\u534a\u4e2a\u5934\uff0c\u8fd1\u8eab\u640f\u6597\uff0c\u8fd9\u4e00\u6293\u4e00\u5288\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5148\u81ea \u5360\u4e86\u8eab\u578b\u4e0a\u7684\u4fbf\u5b9c\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u53d8\u62db\u7684\u5feb\u3002\u8036\u5f8b\u7384\u5143\u6bd4\u4ed6\u8fd8\u5feb\u3002\u4ed6\u4e00\u6293\u6293\u7a7a\uff0c\u53ea\u89c1\u4e00\u7247\u78a7\u83b9\u83b9\u7684\u7eff\u5f71\uff0c\u8036\u5f8b\u7384\u5143 \u7684\u6696\u7389\u8427\u6b63\u662f\u6709\u5982\u7075\u86c7\u5410\u4fe1\uff0c\u201c\u55e4\u201d\u523a\u5411\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u672c\u6765\u662f\u628a\u6696\u7389\u8427\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\u7528\uff0c\u70b9\u4ed6\u7a74\u9053\u7684\uff0c\u8fd9\u4e00\u4e0b\u5b50\u7a81\u7136\u53d8\u4e3a\u5251\u6cd5\uff0c\u7531\u70b9 \u7a74\u800c\u53d8\u4e3a\u523a\u5589\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u535c\u2019\u7684\u4e00\u58f0\u3001\u5e78\u5f97\u5e05\u8001\u4e8c\u8eb2\u95ea\u5f97\u5feb\uff0c\u6ca1\u7ed9\u70b9\u7740\u54bd\u5589\uff0c\u4f46\u5de6\u80a9\u7684\u7435\u7436\u9aa8\uff0c\u5df2\u662f\u7ed9 \u7389\u8427\u6233\u788e\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u8981\u77e5\u7941\u8fde\u4e8c\u8001\u8054\u624b\uff0c\u8bba\u5b9e\u529b\u4e4b\u5f3a\uff0c\u5b9e\u662f\u4e0d\u5728\u4ed6\u4e4b\u4e0b \u7684\u3002\u4ed6\u6240\u4ee5\u80fd\u591f\u8fc5\u901f\u53d6\u80dc\uff0c\u4e00\u6765\u662f\u51ed\u7740\u6696\u7389\u8427\u8fd9\u4ef6\u6b66\u6797\u5f02\u5b9d\uff0c\u4e8c\u6765\u4e5f\u662f\u5e05\u8001\u4e8c\u4e2d\u4e86\u4ed6\u7684\u6fc0\u5c06 \u4e4b\u8ba1\uff0c\u5148\u5c31\u7ed9\u4ed6\u6fc0\u6012\u4e4b\u6545\u3002\u7ed3\u679c\uff0c\u679c\u7136\u662f\u4e0d\u51fa\u4e09\u62db\uff0c\u4ed6\u4eec\u5144\u5f1f\u5c31\u7ed9\u8036\u5f8b\u7384\u5143\u51fb\u5012\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u559d\u9053\uff1a\u201c\u770b\u5728\u4f60\u4eec\u4e24\u4e2a\u8001\u5bb6\u4f19\u4fee\u4e3a\u4e0d\u6613\uff0c\u6211\u53ea\u5e9f\u4e86\u4f60\u4eec\u4e00\u534a\u6b66\u529f\u3002\u6211\u529d\u4f60\u4eec\u4f24 \u6108\u4e4b\u540e\u3001\u8fd8\u662f\u56de\u5230\u7941\u8fde\u5c71\u4e0a\u53bb\u5427\u3002\u201d\u4ed6\u5728\u53d1\u8bdd\u7684\u540c\u65f6\uff0c\u8f6c\u8fc7\u8eab\u6276\u7a33\u4e86\u5df2\u5728\u6447\u6447\u6b32\u5012\u7684\u5f20\u96ea \u6ce2\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u54ac\u7740\u7259\u6839\uff0c\u4e0d\u6562\u53d1\u51fa\u547b\u541f\uff0c\u5fcd\u7740\u75bc\u75db\u8bf4\u9053\uff1a\u201c\u8036\u5f8b\u5148\u751f\u3002\u4e0d\u8981\u987e\u6211\u4e86\uff0c\u6211\u3001\u6211\u4e0d \u884c\u2014\u2014\u201d \u3000\u3000\u8036\u6d25\u7384\u5143\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u4f60\u90fd\u5fc5\u987b\u6c42\u751f\uff01\u4f60\u7684\u5b69\u5b50\u6b63\u5728\u7b49\u7740\u4f60\u5462\uff01\u4f60\u53ef\u4ee5\u4e0d\u7406\u4f60 \u7684\u5b69\u5b50\u5417\uff1f\u201d\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u518d\u6b21\u4f7f\u51fa\u70b9\u7a74\u6b62\u8840\u7684\u6307\u6cd5\uff0c\u5c01\u95ed\u4e86\u4f24\u53e3\u56db\u65c1\u7684\u51e0\u5904\u76f8\u5e94\u7a74\u9053\uff0c \u8ddf\u7740\u628a\u4e00\u9897\u836f\u4e38\u585e\u5165\u5979\u7684\u53e3\u4e2d\uff0c\u8fd9\u662f\u4ed6\u4ece\u5c11\u6797\u5bfa\u5f97\u6765\u7684\u5c0f\u8fd8\u4e39\uff0c\u6cbb\u4f24\u6b62\u75db\uff0c\u529f\u6548\u5982\u795e\u3002 \u3000\u3000\u4f46\u90a3\u652f\u7bad\u662f\u5c04\u7740\u5f20\u96ea\u6ce2\u80cc\u5fc3\u7684\u8981\u5bb3\u4e4b\u5904\u7684\u3002\u7bad\u6746\u90fd\u5df2\u63d2\u8fdb\u53bb\u4e00\u534a\u3002\u5c0f\u8fd8\u4e39\u867d\u7136\u662f\u6cbb\u4f24\u6b62 \u75db\u7684\u7075\u836f\uff0c\u4e5f\u4e0d\u80fd\u7acb\u5373\u4ee4\u5979\u590d\u5143\u3002\u5979\u4f24\u5f97\u592a\u91cd\uff0c\u5df2\u662f\u4e0d\u80fd\u884c\u8d70\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u63e1\u7740\u5979\u7684\u4e00\u53cc\u624b\uff0c\u4e00\u6bb5\u771f\u6c14\u4ece\u5979\u638c\u5fc3\u8f93\u9001\u8fdb\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u592b\u4eba\uff0c\u4f60\u8981\u89c1\u4f60\u7684 \u5b69\u5b50\uff0c\u5c31\u5f97\u632f\u4f5c\u7cbe\u795e\uff0c\u8ddf\u7740\u6211\u8d70\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u5ffd\u7136\u6c89\u5f97\u6709\u6c14\u529b\uff0c\u5728\u4ed6\u6276\u6301\u4e4b\u4e0b\uff0c\u679c\u7136\u80fd\u591f\u8ddf\u7740\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u4e5f\u5e78\u4e8f\u6709\u4e2a\u513f\u5b50\u4ee4\u5979\u7275\u6302\uff0c\u5979\u82e5\u662f\u4e0d\u80fd\u9f13\u8d77\u6551\u751f\u7684\u610f\u5fd7\uff0c\u7eb5\u6709\u5916\u529b\u76f8\u63f4\uff0c\u4e5f\u662f\u652f\u6301\u4e0d\u4f4f \u7684\u3002 \u3000\u3000\u4e00\u540d\u536b\u58eb\uff0c\u4ee5\u4e3a\u6709\u4fbf\u5b9c\u53ef\u6361\uff0c\u4ed6\u672c\u6765\u662f\u4e00\u5757\u5927\u77f3\u5934\u7684\u540e\u9762\u7684\u3002\u5f53\u8036\u5f8b\u7384\u5143\u62d6\u7740\u5f20\u96ea\u6ce2\u7ecf \u8fc7\u4e4b\u65f6\uff0c\u4ed6\u7a81\u7136\u8df3\u51fa\u6765\uff0c\u4e00\u5200\u5411\u5f20\u96ea\u6ce2\u780d\u4e0b\u3002 \u3000\u3000\u8fd9\u4eba\u4e5f\u662f\u5b8c\u989c\u9274\u624b\u4e0b\u6709\u540d\u7684\u5927\u529b\u58eb\uff0c\u7528\u7684\u5927\u780d\u5200\u91cd\u8fbe\u4e09\u5341\u516d\u65a4\u3002\u53ea\u9053\u8fd9\u4e00\u5200\u780d\uff0c\u5373\u4f7f\u4f24 \u4e0d\u4e86\u8036\u5f8b\u7384\u5143\uff0c\u4e5f\u80fd\u53d6\u4e86\u5f20\u96ea\u6ce2\u7684\u6027\u547d\u3002\u8036\u5f8b\u7384\u5143\u542c\u5f97\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\u5934\u4e5f\u4e0d\u56de\u3002\u968f\u624b\u628a\u7389\u8427 \u4e00\u6321\u3002\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u91cd\u8fbe\u4e09\u5341\u516d\u65a4\u7684\u5927\u780d\u5200\u65ad\u4e3a\u4e24\u622a\uff01 \u3000\u3000\u90a3\u540d\u5927\u529b\u58eb\u7ed9\u9707\u5f97\u6655\u5012\u5730\u4e0a\uff0c\u773c\u8033\u9f3b\u53e3\u90fd\u6709\u9c9c\u8840\u6d41\u4e86\u51fa\u6765\uff0c\u867d\u7136\u672a\u66fe\u65ad\u6c14\uff0c\u4e5f\u662f\u6b7b\u591a\u6d3b \u5c11\u4e86\u3002 \u3000\u3000\u672c\u6765\u60f3\u62a2\u201c\u4fbf\u5b9c\u201d\u7684\u4e0d\u6b62\u4e00\u4e2a\u4eba\uff0c\u4e00\u89c1\u8036\u5f8b\u7384\u5143\u7684\u7389\u8427\u7adf\u6709\u5982\u6b64\u5a01\u529b\uff0c\u5413\u5f97\u4ed6\u4eec\u90fd\u662f\u7fd8 \u820c\u96be\u4e0b\u3002\u4e0d\u6562\u5411\u524d\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u8036\u5f8b\u7384\u5143\u867d\u7136\u5413\u9000\u4e86\u8fd9\u4e9b\u4eba\uff0c\u4ed6\u7684\u5fc3\u5934\u5374\u4e5f\u662f\u4e0d\u7981\u5fae\u5fae\u4e00\u51db\u4e86\u3002\u539f\u6765\u4ed6\u5728\u51fb\u65ad\u4e86 \u90a3\u628a\u5927\u780d\u5200\u4e4b\u540e\uff0c\u864e\u53e3\u4ea6\u5df2\u5fae\u89c9\u9178\u9ebb\u3002 \u3000\u3000\u4ed6\u5728\u5bf9\u4ed8\u90a3\u540d\u5927\u529b\u58eb\u4e4b\u65f6\uff0c\u4e00\u53ea\u624b\u4e5f\u8fd8\u662f\u62d6\u7740\u5f20\u96ea\u6ce2\u7684\u3002\u4e0d\u677e\u662f\u62d6\u7740\u5979\u76f4\u8d70\u8def\uff0c\u540c\u65f6\u8fd8 \u8981\u628a\u771f\u6c14\u900f\u8fc7\u5979\u7684\u638c\u5fc3\u8f93\u5165\u5979\u7684\u4f53\u5185\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u4f24\u5f97\u5f88\u91cd\uff0c\u5018\u82e5\u4ed6\u4e00\u65e6\u505c\u6b62\u8f93\u9001\u771f\u6c14\u7ed9\u5979\uff0c\u53ea\u6015\u5979\u5c31\u6709\u6027\u547d\u4e4b\u5371\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u679c\u7136\u4e3a\u4ed6\u7684\u58f0\u5a01\u6240\u6444\uff0c\u4e0d\u6562\u5411\u524d\u3002 \u3000\u3000\u4f46\u53ea\u662f\u4e0d\u6562\u5355\u72ec\u4e0a\u6765\u548c\u4ed6\u62fc\u547d\u800c\u5df2\uff0c\u5374\u5e76\u6ca1\u6709\u653e\u5f03\u5728\u56ed\u4e2d\u8bbe\u9632\u3002 \u3000\u3000\u5b8c\u989c\u9274\u5df1\u7ecf\u8c03\u6765\u4e00\u6279\u5f13\u7bad\u624b\uff0c\u5899\u5934\u4e0a\u3001\u5047\u5c71\u4e0a\u90fd\u6709\u4eba\u5f20\u5f13\u642d\u7bad\uff0c\u5230\u5904\u90fd\u662f\u95ea\u4eae\u7684\u7bad\u7c07\uff0c \u6709\u5982\u9ed1\u591c\u7684\u70b9\u70b9\u5bd2\u661f\u3002 \u3000\u3000\u5b8c\u989c\u9274\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8036\u5f8b\u738b\u5b50\uff0c\u4f60\u95ef\u4e0d\u51fa\u53bb\u7684\u3002\u5373\u4f7f\u4f60\u95ef\u5f97\u51fa\u53bb\uff0c\u8fd9\u4f4d\u6a80\u592b\u4eba\u4e5f \u662f\u7edd\u5bf9\u4e0d\u80fd\u6d3b\u7740\u51fa\u53bb\u7684\uff01\u4f60\u82e5\u60f3\u4fdd\u5b58\u5979\u7684\u6027\u547d\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u6295\u964d\u7684\u597d\uff01\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8036\u5f8b\u5148\u751f\uff0c\u4f60\u3001\u4f60\u8fd8\u662f\u2014\u2014\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u6a80\u592b\u4eba\uff0c\u4f60\u653e\u5fc3\uff0c\u4f60\u4f1a\u89c1\u5f97\u7740\u4f60\u7684\u513f\u5b50\u7684\uff01\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u4e0d\u8fc7\u662f\u7a7a\u8a00\u5b89\u6170\u800c\u5df2\uff0c\u5fc3\u4e2d\u5b9e\u662f\u5e76\u65e0\u628a\u63e1\u95ef\u5f97\u51fa\u53bb\u3002 \u3000\u3000\u5b8c\u989c\u9274\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u771f\u8981\u5192\u8fd9\u4e2a\u9669\u5417\uff1f\u6211\u544a\u8bc9\u4f60\uff0c\u5728\u8fd9\u4e2a\u56ed\u5b50\u5916\u9762\uff0c\u6211\u8fd8\u6709\u4e09\u5343\u540d \u7cbe\u5175\u5728\u7b49\u5f85\u4f60\u4eec\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u559d\u9053\uff1a\u201c\u7ba1\u4f60\u5343\u519b\u4e07\u9a6c\uff0c\u8981\u6211\u7684\u6027\u547d\u53ef\u4ee5\uff0c\u8981\u6211\u5c48\u819d\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u54fc\uff0c\u4f60\u4eec \u60f3\u8981\u6211\u7684\u6027\u547d\uff0c\u53ea\u6015\u4e5f\u6ca1\u90a3\u5bb9\u6613\uff01\u201d\u5b8c\u989c\u9274\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u826f\u8a00\uff0c\u90a3\u4e5f\u4e2d\u53ea\u80fd\u7531\u4f60\uff0c\u4e0d \u8fc7\u2014\u2014\u201c\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u7ed9\u4f60\u4e00\u70b9\u65f6\u95f4\u8003\u8651\uff0c\u6682\u65f6\u4e0d\u653e\u7bad\uff0c\u8ba9\u4f60\u4e09\u601d\u800c\u884c\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5b8c\u989c\u9274\uff0c\u4f60\u4e5f\u4e0d\u7167\u773c\u955c\u5b50\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u8bf4\u5f97\u7a81\u5140\u4e4b\u6781\uff0c\u5b8c\u989c\u9274\u83ab\u540d\u5176\u5999\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f\u914d\u5411\u6211\u62db\u964d\uff1f\u521a\u624d\u4f60\u8fd8\u5728\u5411\u6211\u6c42\u9976\u5462\uff01\u6211\u8981\u6740\u4f60\u3002\u65e9 \u5c31\u53ef\u4ee5\u628a\u4f60\u6740\u4e86\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u7ed9\u4ed6\u9a82\u5f97\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6211\u751f\u5e73\u8fd8\u6ca1\u89c1\u8fc7\u50cf\u4f60\u8fd9\u7684\u6837\u72c2\u5984\u7684\u4eba\uff0c\u597d\u5427\uff0c\u4f60\u65e2 \u7136\u662f\u4e0d\u5403\u656c\u9152\uff0c\u8981\u5403\u7f5a\u9152\u3002\u90a3\u6211\u4e5f\u53ea\u80fd\u8bf7\u4f60\u5c1d\u5c1d\u4e07\u7bad\u7a7f\u5fc3\u7684\u6ecb\u5473\u4e86\uff0c\u4f60\u5f80\u9b3c\u95e8\u5173\u4e0a\u95ef\u5427\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53d1\u51fa\u547d\u4ee4\uff1a\u201c\u53ea\u8981\u8fd9\u4e24\u4eba\u8d70\u51fa\u90a3\u4e00\u7247\u82b1\u6811\uff0c\u7acb\u5373\u628a\u4ed6\u4eec\u4e71\u7bad\u5c04\u6b7b\uff01\u201d \u3000\u3000\u53d1\u51fa\u547d\u4ee4\u4e4b\u540e\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u6015\u6bc1\u574f\u4e86\u6211\u7684\u540d\u79cd\u7261\u4e39\uff0c\u4e5f\u7ed9\u4f60\u4e00\u4e2a\u6700\u540e\u7684\u673a\u4f1a\uff01\u662f\u8981 \u6b7b\u8fd8\u662f\u8981\u751f\uff0c\u5168\u51ed\u4f60\u81ea\u5df1\u4e86\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u563f\u563f\u51b7\u7b11\uff0c\u62c9\u7740\u5f20\u96ea\u6ce2\u50b2\u7136\u524d\u884c\u3002 \u3000\u3000\u4e07\u6728\u65e0\u987b\u5f85\u96e8\u6765\uff0c\u56ed\u5b50\u91cc\u53ea\u542c\u5f97\u89c1\u4ed6\u4eec\u4e24\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u6563\u5728\u82b1\u56ed\u4e2d\u7684\u536b\u58eb\u65e9\u5df2\u64a4\u4e0a\u5047\u5c71 \u53bb\u4e86\u3002\u6709\u4e9b\u6765\u4e0d\u53ca\u9003\u8d70\u7684\u5de5\u5320\u3001\u5a62\u4ec6\u4e4b\u7c7b\u539f\u672c\u662f\u5728\u56ed\u4e2d\u6267\u5f79\u7684\u4e0b\u4eba\uff0c\u4e5f\u65e9\u5df2\u8eb2\u8fdb\u4ed6\u4eec\u7684\u5c4b\u5b50 \u5173\u4e0a\u5927\u95e8\u3002 \u3000\u3000\u8fd9\u5ea7\u82b1\u56ed\u5f88\u5927\uff0c\u6267\u5f79\u7684\u4e0b\u4eba\u672c\u6765\u4e0d\u5c11\uff0c\u4f46\u5728\u7a7f\u8fc7\u8fd9\u7247\u7261\u4e39\u82b1\u6797\u7684\u5fc5\u7ecf\u4e4b\u5730\uff0c\u5374\u662f\u53ea\u6709\u51e0 \u95f4\u7ed9\u4ec6\u4eba\u4f4f\u7684\u5c0f\u5c4b\u5b50\u3002 \u3000\u3000\u5f53\u8036\u5f8b\u7384\u5143\u7ecf\u8fc7\u4e00\u95f4\u5c4b\u5b50\u4e4b\u65f6\uff0c\u5c4b\u5b50\u7684\u4e24\u6247\u95e8\u7a81\u7136\u6253\u5f00\uff0c\u6709\u4e2a\u4eba\u52a8\u4f5c\u8fc5\u901f\u4e4b\u6781\uff0c\u628a\u4ed6\u548c \u5f20\u96ea\u6ce2\u62c9\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5341\u51e0\u5f20\u5f13\u7bad\u540c\u65f6\u53d1\u5c04\uff0c\u4f46\u4ea6\u8fdf\u4e86\u4e00\u6b65\uff0c\u53ea\u542c\u5f97\u201c\u593a\u3001\u593a\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u6570\u5341\u679d\u7bad\uff08\u5f13 \u7bad\u624b\u53d1\u7684\u662f\u8fde\u73e0\u7bad\uff09\u628a\u90a3\u4e24\u6247\u95e8\u677f\u5c04\u5f97\u6709\u5982\u8702\u7a9d\u3002 \u3000\u3000\u6709\u4e2a\u536b\u58eb\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4eba\u4e0d\u662f\u82b1\u738b\u8001\u4f5f\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff0c\u8001\u4f5f\u7684\u8eab\u624b\u54ea\u6709\u8fd9\u6837\u77eb\u6377\uff1f\u201d\u53e6\u4e00\u4e2a\u571f\u9053\u3002 \u3000\u3000\u201c\u660e\u660e\u662f\u4ed6\u7684\u5c4b\u5b50\uff0c\u6211\u4e5f\u77a7\u6e05\u695a\u662f\u4ed6\u4e86\u3002\u4ed6\u548c\u5170\u59d1\u4e00\u5411\u5f88\u597d\uff0c\u83ab\u975e\u662f\u4ed6\u6551\u5170\u59d1\uff1f\u201d \u3000\u3000\u2018\u54ea\u4e2a\u8fbd\u56fd\u738b\u5b50\u6b66\u529f\u4f55\u7b49\u9ad8\u5f3a\uff0c\u4ed6\u53c8\u4e0d\u77e5\u9053\u4ed6\u548c\u5170\u59d1\u7684\u4ea4\u60c5\uff0c\u600e\u7684\u53c8\u4f1a\u7ed9\u4ed6\u4e00\u62c9\u5c31\u62c9\u4e86 \u8fdb\u53bb\uff1f\u6211\u770b\u662f\u4f60\u773c\u82b1\u7f62\uff1f\u8bf4\u4e0d\u5b9a\u662f\u90a3\u8fbd\u56fd\u738b\u5b50\u7684\u540c\u515a\u8eb2\u5728\u8001\u4f5f\u7684\u5c4b\u5185\uff1f\u201d\u536b\u58eb\u8bae\u8bba\u672a\u5b9a\uff0c\u5b8c \u989c\u9274\u5df2\u662f\u6c14\u5f97\u7834\u53e3\u5927\u9a82\uff01 \u3000\u3000\u5b8c\u989c\u9274\u9a82\u9053\uff1a\u201c\u4f5f\u7389\u6842\uff0c\u4f60\u53d1\u4e86\u75af\u5417\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u662f\u7a9d\u85cf\u94a6\u72af\u7684\u7f6a\u540d\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u8fd9\u4e48\u4e00\u9a82\uff0c\u4f17\u536b\u58eb\u65b9\u6562\u786e\u5b9a\uff0c\u90a3\u4e2a\u628a\u8036\u5f8b\u7384\u5143\u62c9\u8fdb\u5c0f\u5c4b\u7684\u4eba\u679c\u7136\u662f\u82b1\u738b\u8001\u4f5f\u3002 \u3000\u3000\u6709\u4e2a\u548c\u8001\u4f5f\u79c1\u4ea4\u751a\u539a\u7684\u536b\u58eb\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u4f5f\u4e0e\u5170\u59d1\u60c5\u5982\u7236\u5973\uff0c\u4ed6\u7684\u76ee\u7684\u53ef\u80fd\u662f\u60f3\u6551\u5170 \u59d1\u7684\uff0c\u5c06\u519b\uff0c\u4f60\u770b\u662f\u4e0d\u662f\u53ef\u4ee5\u8ba9\u4ed6\u5c06\u529f\u8d4e\u7f6a\uff1f\u201d \u3000\u3000\u5e95\u4e0b\u7684\u8bdd\uff0c\u65e0\u987b\u8fd9\u4e2a\u536b\u58eb\u518d\u8bf4\u4e0b\u53bb\uff0c\u5b8c\u989c\u9274\u5df2\u7ecf\u77e5\u9053\u4ed6\u7684\u732e\u8bae\u662f\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u54b3\u4e86\u4e00\u58f0\uff0c\u653e\u5bbd\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u4f5f\u7389\u6842\uff0c\u59d1\u5ff5\u4f60\u66ff\u6211\u79cd\u4e86\u51e0\u5341\u5e74\u7261\u4e39\u7684\u529f\u52b3\uff0c\u6211\u7ed9 \u4f60\u4e00\u4e2a\u8d4e\u7f6a\u7684\u673a\u4f1a\u3002\u4f60\u53eb\u5170\u59d1\u51fa\u6765\u5411\u6211\u6295\u964d\uff0c\u6211\u7b54\u5e94\u9976\u5979\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u8001\u4f5f\u6ca1\u6709\u56de\u8bdd\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u6709\u4eba\u51fa\u6765\u3002 \u3000\u3000\u5b8c\u989c\u9274\u53ea\u9053\u662f\u8036\u5f8b\u7384\u5143\u4f5c\u6897\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8036\u5f8b\u738b\u5b50\uff0c\u4f60\u4e0d\u80af\u6295\u964d\uff0c\u8fd9\u662f\u4f60\u7684\u4e8b\u3001\u4f46\u4f60 \u5c82\u80fd\u8fde\u7d2f\u4f60\u7684\u597d\u670b\u53cb\u7684\u513f\u5ab3\u4e3a\u4f60\u65e0\u8f9c\u4e27\u547d\uff0c\u4f60\u8ba9\u4f5f\u7389\u6842\u5e26\u5979\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u8fb9\u4ecd\u662f\u6beb\u65e0\u52a8\u9759\u3002 \u3000\u3000\u5b8c\u989c\u9274\u6012\u9053\uff1a\u201c\u8fd9\u8001\u5974\u624d\u4e0d\u8bc6\u597d\u6b79\uff0c\u4f60\u4eec\u8fd8\u4e0d\u8d76\u5feb\u53bb\u7ed9\u6211\u628a\u4ed6\u63ea\u51fa\u6765\uff01\u201d \u3000\u3000\u53ef\u662f\u5728\u82b1\u738b\u90a3\u5ea7\u5c4b\u5b50\u91cc\uff0c\u662f\u6709\u8036\u5f8b\u7384\u5143\u5728\u5185\u7684\u3002 \u3000\u3000\u4f17\u536b\u58eb\u6562\u53bb\u201c\u63ea\u201d\u82b1\u738b\u8001\u4f5f\uff0c\u5374\u4e0d\u6562\u53bb\u201c\u63ea\u201d\u8036\u5f8b\u7384\u5143\u3002 \u3000\u3000\u6709\u4eba\u732e\u8ba1\uff0c\u627e\u4e00\u6839\u56db\u4e94\u7acb\u957f\u7684\u5927\u6728\u5934\u6765\uff0c\u649e\u5f00\u677f\u95e8\uff0c\u95e8\u4e00\u649e\u5f00\uff0c\u5c31\u4e71\u7bad\u9f50\u5c04\u3002\u8fd9\u6837\u867d\u7136 \u4e5f\u8981\u5192\u8036\u5f8b\u7384\u5143\u548c\u4ed6\u4eec\u62fc\u547d\u7684\u5371\u9669\uff0c\u4f46\u4eba\u591a\u80c6\u58ee\u603b\u597d\u4e00\u4e9b\u3002 \u3000\u3000\u53ef\u662f\u6025\u5207\u4e4b\u95f4\uff0c\u53c8\u90a3\u91cc\u53bb\u627e\u8fd9\u6837\u4e00\u6839\u73b0\u6210\u7684\u6728\u5934\uff1f\u6709\u4eba\u60f3\u5230\u4e86\u653e\u706b\u7684\u4e3b\u610f\u3001\u5bf9\u5b8c\u989c\u9274 \u9053\uff1a\u201c\u6211\u4eec\u4e3a\u4e86\u5c06\u519b\u767e\u6b7b\u65e0\u8f9e\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u662f\u74ee\u4e2d\u4e4b\u9cd6\uff0c\u8981\u662f\u903c\u4ed6\u4eec\u4f5c\u56f0\u517d\u4e4b\u6597\uff0c\u4ed6\u4eec\u6b7b\u4e09 \u4e2a\u4eba\uff0c\u54b1\u4eec\u8981\u6b7b\u4f24\u51e0\u5341\u4e2a\u4eba\u7684\u8bdd\uff0c\u4f3c\u4e4e\u5c31\u4e0d\u5927\u503c\u5f97\u4e86\u3002\u5c06\u519b\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u77e5\u4ed6\u4eec\u6015\u6b7b\uff0c\u4e0d\u8fc7\u4ed6\u4e5f\u820d\u4e0d\u5f97\u727a\u7272\u8bb8\u591a\u5f97\u529b\u7684\u536b\u58eb\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u653e\u706b\u5c31 \u653e\u706b\u5427\u3002\u4e0d\u8fc7\u4f60\u4eec\u5f97\u4f5c\u597d\u51c6\u5907\u3002\u4e0d\u8981\u8ba9\u706b\u52bf\u8513\u5ef6\u3002\u70e7\u6bc1\u4e86\u6211\u7684\u540d\u79cd\u7261\u4e39\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u559d\u9053\uff1a\u201c\u8036\u5f8b\u7384\u5143\uff0c\u4f60\u542c\u7740\uff0c\u6211\u6570\u5230\u5341\u4e0b\uff0c\u4f60\u4e0d\u51fa\u6765\uff0c\u6211\u53ef\u8981\u653e\u706b\u4e86\u3002\u4f60\u82e5\u4e0d\u60f3 \u8fde\u7d2f\u5170\u59d1\u548c\u4f5f\u7389\u6842\u4e3a\u4f60\u966a\u4e27\uff0c\u6700\u5c11\u4f60\u4e5f\u8be5\u8ba9\u4ed6\u4eec\u51fa\u6765\u6295\u964d\u3002\u201d \u3000\u3000\u548c\u8001\u4f5f\u76f8\u719f\u7684\u536b\u58eb\u4e5f\u5728\u53eb\u9053\uff1a\u201c\u8001\u4f5f\uff0c\u4f60\u5feb\u6253\u5f00\u95e8\u8dd1\u51fa\u6765\u5427\uff0c\u5426\u5219\u8fde\u4f60\u4e5f\u70e7\u6b7b\u5728\u91cc \u5934\uff01\u201d \u3000\u3000\u6ca1\u6709\u56de\u58f0\uff0c\u5b8c\u989c\u9274\u5df2\u7ecf\u6570\u5230\u201c\u5341\u201d\u5b57\uff0c\u706b\u70e7\u4e86\u8d77\u6765\u4e86\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u8fd9\u5ea7\u5c0f\u5c4b\u5b50\u70e7\u6210\u4e86\u4e00 \u74e6\u783e\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5e76\u6ca1\u6709\u627e\u4e00\u5177\u9aa8\u9ab8\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u65e9\u5df2\u51c6\u5907\u597d\u4e00\u767e\u51e0\u5341\u6876\u6c34\u6765\u6551\u706b\u7684\uff0c\u6cfc\u7184\u4e86\u706b\u4e4b\u540e\uff0c\u6709\u536b\u58eb\u5192\u7740\u707c\u70ed\u7684\u6c99\u77f3\u62e8\u5f00 \u74e6\u783e\u5bdf\u89c6\uff0c\u8fd9\u624d\u53d1\u73b0\u4e86\u4e00\u6761\u5730\u9053\u3002 \u3000\u3000\u8001\u4f5f\u628a\u4ed6\u4eec\u62c9\u8fdb\u4e86\u5730\u9053\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u5c0f\u4e3b\u4eba\uff0c\u6211\u7b49\u4e86\u4f60\u8fd9\u8bb8\u591a\u5e74\uff0c\u7ec8\u4e8e\u7ed9\u6211\u76fc\u5230\u4e86\uff01\u201d\u4ed6\u8bf4\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u82e6\u7b11\u9053\uff1a\u201c\u8001\u4f5f\uff0c\u4f60\u8fd9\u662f\u505a\u4e86\u50bb\u4e8b\uff0c\u6211\u4f1a\u8fde\u7d2f\u4f60\u7684\uff01\u201d\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u5c0f\u4e3b\u4eba\uff0c \u4f60\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u6316\u8fd9\u6761\u5730\u9053\u5417\uff1f\u201d\u4ed6\u81ea\u95ee\u81ea\u7b54\uff1a\u201c\u5c31\u662f\u4e3a\u4e86\u9884\u9632\u4eca\u65e5\u4e4b\u4e8b\u554a\uff01\u6211\u77e5\u9053\u4f60\u8fdf \u65e9\u8981\u6765\u7684\uff0c\u6211\u4e00\u5230\u5546\u5dde\uff0c\u6bcf\u5929\u665a\u4e0a\uff0c\u5c31\u5077\u5077\u6316\u8fd9\u6761\u5730\u9053\u3001\u8fd9\u6761\u5730\u9053\u662f\u53ef\u4ee5\u901a\u5230\u5916\u9762\u4e00\u6761\u6a2a\u8857 \u51b7\u5df7\u7684\u3002\u51fa\u53e3\u5904\u662f\u4e00\u4e2a\u8352\u5e9f\u7684\u74e6\u7a91\uff0c\u6ca1\u6709\u4eba\u7684\u3002\u5c0f\u4e3b\u4eba\uff0c\u6211\u4e3a\u4f60\u6316\u8fd9\u6761\u5730\u9053\uff0c\u6316\u4e86\u4e09\u5e74\uff0c\u4f60 \u8fd8\u8bf4\u4ec0\u4e48\u8fde\u7d2f\u4e0d\u8fde\u661f\u7684\u8bdd\uff0c\u4f60\u4e5f\u4e0d\u6015\u4f24\u4e86\u6211\u7684\u5fc3\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u7ed9\u4ed6\u611f\u52a8\u5f97\u773c\u89d2\u6c81\u51fa\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u4f5f\u5927\u53d4\uff0c\u4f60\u53eb\u6211\u7684\u5c0f\u540d\u5427\u3002\u4f60\u7684\u5927\u6069\u6211\u662f \u65e0\u6cd5\u62a5\u7b54\u4e86\u3002\u201d \u3000\u3000\u8001\u4f5f\u8bf4\u9053\uff1a\u201c\u5143\u54e5\u513f\uff0c\u6211\u5df2\u7ecf\u6d3b\u4e86\u8fd9\u628a\u5e74\u7eaa\u3002\u80fd\u591f\u89c1\u4f60\u4e00\u9762\uff0c\u6b7b\u4ea6\u65e0\u61be\u4e86\u3002\u6316\u8fd9\u6761\u5730\u9053 \u4e5f\u4e0d\u5168\u662f\u6211\u7684\u529f\u52b3\uff0c\u8001\u4f55\u4e5f\u90fd\u5e2e\u8fc7\u6211\u6316\u7684\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4f55\u7389\u67f1\u5417\uff1f\u201d \u3000\u3000\u4f55\u7389\u67f1\u662f\u53e6\u4e00\u4e2a\u82b1\u738b\u7684\u540d\u5b57\u3002 \u3000\u3000\u8001\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5982\u4eca\u4e5f\u5e74\u8fd1\u516d\u65ec\u4e86\uff0c\u4e0d\u8fc7\u6211\u53eb\u60ef\u4e86\u4ed6\u5c0f\u4f55\uff0c\u603b\u662f\u6539\u4e0d\u8fc7\u79f0\u547c\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u65e0\u9053\uff1a\u201c\u5c0f\u4f55\u5462\uff1f\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u4ed6\u53e6\u6709\u4f4f\u5904\uff0c\u5143\u54e5\u513f\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u4ed6\u53ef\u4ee5\u628a\u4e8b\u60c5\u90fd\u63a8\u5230\u6211\u7684\u5934\u4e0a\u7684\u3002 \u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4e5f\u7684\u786e\u662f\u5e76\u4e0d\u77e5\u60c5\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5e76\u4e0d\u77e5\u60c5\u662f\u6307\u2014\u2014\u201d \u3000\u3000\u8001\u4f5f\u9053\uff1a\u201c\u662f\u6307\u4eca\u5929\u7684\u4e8b\u3002\u5c0f\u4f55\u5e76\u4e0d\u77e5\u9053\u4f60\u4eca\u5929\u8981\u6765\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u662f\u8fd9\u6837\u7684\u3002\u6211\u5077\u542c\u4e86\u5b8c\u989c\u9274\u548c\u54c8\u5fc5\u56fe\u7684\u8c08\u8bdd\uff0c\u77e5\u9053\u4f60\u5df2\u7ecf\u5230\u4e86\u5546\u5dde\u3002\u8fd9\u4ef6 \u4e8b\u60c5\uff0c\u6211\u53ea\u8bf4\u7ed9\u8001\u4f5f\u5e08\u5085\u77e5\u9053\u3002\u201d \u3000\u3000\u8001\u4f5f\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u521d\u65f6\u6211\u4ee5\u4e3a\u4f60\u6765\u5f97\u65e9\u4e9b\u4e5f\u8981\u660e\u5929\u624d\u80fd\u6765\u5230\uff0c\u6211\u8fd8\u6253\u7b97\u5728\u4f60\u7684\u5fc5\u7ecf\u4e4b\u8def \u62e6\u622a\u4f60\u5462\u3002\u90a3\u77e5\u9053\u5728\u6211\u77e5\u9053\u4f60\u7684\u6d88\u606f\u4e4b\u540e\u534a\u4e2a\u65f6\u8fb0\u4f60\u5c31\u6765\u4e86\uff0c\u6240\u4ee5\u6211\u6ca1\u6709\u901a\u77e5\u5c0f\u4f55\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u672c\u6765\u6211\u4e5f\u60f3\u89c1\u89c1\u4ed6\u7684\uff0c\u5982\u4eca\u5df2\u662f\u65e0\u6687\u53ca\u6b64\u4e86\uff0c\u4f46\u613f\u4ed6\u4e0d\u8981\u53d7\u5230\u7275\u8fde\u624d \u597d\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\u5df2\u6709\u6d53\u70df\u704c\u5165\u5730\u9053\uff0c\u5f20\u96ea\u6ce2\u547c\u5438\u4e0d\u8212\uff0c\u8fde\u8fde\u54b3\u55fd\u3002\u8001\u4f5f\u4e5f\u88ab\u718f\u5f97\u5934\u6655\u76ee\u7729\uff0c\u8fde \u5fd9\u52a0\u5feb\u811a\u6b65\uff0c\u8dd1\u5728\u524d\u5934\uff0c\u6253\u5f00\u51fa\u53e3\u7684\u673a\u5173\u3002 \u3000\u3000\u90a3\u77e5\u9053\u4ed6\u7684\u5934\u90e8\u521a\u521a\u4f38\u51fa\u53bb\uff0c\u5c31\u53d7\u5230\u7a81\u5982\u5176\u6765\u7684\u88ad\u51fb\u3002 \u3000\u3000\u90a3\u4eba\u5728\u4ed6\u5934\u90e8\u91cd\u91cd\u6253\u4e86\u4e00\u62f3\uff0c\u8ddf\u7740\u70b9\u4e86\u4ed6\u80f8\u53e3\u7684\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u53ea\u53eb\u5f97\u51fa\u201c\u5c0f\u4f55\u201d\u8fd9\u4e24\u4e2a\u5b57\u5c31\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u4e00\u89c9\u6709\u53d8\u3001\u5288\u7a7a\u638c\u5148\u53d1\u51fa\u53bb\u3002\u90a3\u4eba\u65e9\u5df2\u907f\u5f00\u3002 \u3000\u3000\u5f85\u5230\u8036\u5f8b\u7384\u5143\u6276\u7740\u5f20\u96ea\u6ce2\u94bb\u51fa\u5730\u9053\u4e4b\u65f6\uff0c\u8001\u4f5f\u65e9\u5df2\u843d\u5165\u90a3\u4eba\u7684\u624b\u4e2d\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u5077\u88ad\u8001\u4f5f\u7684\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u548c\u4ed6\u5408\u4f5c\u4e86\u51e0\u5341\u5e74\u7684\u53e6\u4e00\u4e2a\u82b1\u738b\u201c\u5c0f\u4f55\u201d\u3002\u521a\u521a\u4ed6\u8fd8 \u5bb3\u6015\u8fde\u53ca\u4ed6\u7684\u90a3\u4e2a\u201c\u5c0f\u4f55\u201d\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u6014\u4e86\u4e00\u6014\uff0c\u559d\u9053\uff1a\u201c\u4f55\u7389\u67f1\uff0c\u4f60\u5e72\u4ec0\u4e48\uff0c\u5feb\u628a\u8001\u4f5f\u653e\u5f00\uff01\u201d \u3000\u3000\u4f55\u7389\u67f1\u9053\uff1a\u201c\u5c0f\u4e3b\u4eba\u3002\u4f60\u628a\u5170\u59d1\u653e\u4e0b\uff0c\u6211\u4e0d\u548c\u4f60\u4e3a\u96be\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u662f\u6253\u7b97\u5a01\u80c1\u6211\u5417\uff1f\u201d \u3000\u3000\u4f55\u7389\u67f1\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u53ea\u662f\u8981\u4f60\u628a\u5170\u59d1\u7559\u4e0b\u800c\u5df2\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u5927\u80c6\u5974\u624d\uff0c\u6211\u4fbf\u8981\u7740\u4f60\u600e\u6837\u5c06\u6211\u4e3a\u96be\uff1f\u201d\u4f55\u7389\u67f1\u51b7\u7b11\u9053\uff1a\u201c\u5143\u54e5\u513f\uff0c\u8bf7 \u4f60\u8bf4\u8bdd\u5ba2\u6c14\u4e9b\uff0c\u6211\u65e9\u5df2\u4e0d\u662f\u4f60\u7684\u4ec6\u4eba\u4e86\uff0c\u6211\u73b0\u5728\u7684\u4e3b\u4eba\u662f\u5b8c\u989c\u5c06\u519b\uff01\u8bf7\u4f60\u7ad9\u4f4f\uff0c\u4f60\u82e5\u518d\u8de8\u524d \u4e00\u6b65\uff0c\u6211\u7acb\u523b\u53d6\u4e86\u8001\u4f5f\u6027\u547d\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4ed6\u7684\u624b\u638c\u5df2\u662f\u7d27\u7d27\u8d34\u7740\u8001\u4f5f\u80cc\u5fc3\u7684\u6b7b\u7a74\u3002 \u3000\u3000\u201c\u5143\u54e5\u513f\uff0c\u4f60\u6b66\u529f\u76d6\u4e16\uff0c\u6211\u5f53\u7136\u662f\u96be\u5948\u4f60\u4f55\uff0c\u4f46\u8981\u53d6\u8001\u4f5f\u6027\u547d\uff0c\u5374\u662f\u6613\u5982\u53cd\u638c\uff01\u201d\u4f55\u7389 \u67f1\u51b7\u7b11\u9053\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u5728\u4ed6\u5a01\u80c1\u4e4b\u4e0b\uff0c\u679c\u7136\u4e0d\u6562\u5411\u524d\u8e0f\u8fdb\u4e00\u6b65\u3002 \u3000\u3000\u8001\u4f5f\u521a\u624d\u88ab\u4ed6\u4e00\u62f3\u6253\u6655\u3002\u597d\u5728\u4e0d\u662f\u91cd\u4f24\uff0c\u6b64\u65f6\u9192\u8fc7\u4e86\u3002\u4f46\u4f55\u7389\u67f1\u521a\u624d\u5728\u6253\u6655\u4ed6\u7684\u540c\u65f6\uff0c \u53c8\u70b9\u4e86\u4ed6\u80f8\u53e3\u7684\u9ebb\u7a74\u7684\uff0c\u4ed6\u867d\u7136\u9192\u6765\uff0c\u4ecd\u662f\u52a8\u5f39\u4e0d\u5f97\uff0c\u800c\u4e14\u4f55\u7389\u67f1\u7684\u624b\u638c\u662f\u8d34\u7740\u4ed6\u80cc\u5fc3\u7684\u6b7b \u7a74\u7684\uff0c\u8981\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u786e\u5b9e\u662f\u6613\u5982\u53cd\u638c\u3002\u8001\u4f5f\u867d\u7136\u6ca1\u6709\u542c\u89c1\u4ed6\u548c\u8036\u5f8b\u7384\u5143\u7684\u5bf9\u8bdd\uff0c\u4ea6\u5df2\u77e5\u9053 \u4ed6\u7684\u4f01\u56fe\uff0c\u4e00\u9192\u6765\u5c31\u9053\uff1a\u201c\u5c0f\u4f55\uff0c\u4f60\u62ff\u6211\u53bb\u9886\u529f\u8bf7\u8d4f\u5427\uff0c\u6211\u4e0d\u6028\u4f60\uff0c\u53ea\u8bf7\u4f60\u653e\u8fc7\u5170\u59d1\u3002\u201d \u3000\u3000\u4f55\u7389\u67f1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8eab\u4ef7\u600e\u4e48\u6bd4\u5f97\u4e0a\u5170\u59d1\uff0c\u563f\uff0c\u563f\u4f60\u5f53\u6211\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u6211\u65e9\u5df2\u77e5\u9053\u5170 \u59d1\u662f\u6a80\u8d1d\u5b50\u7684\u592b\u4eba\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4ee5\u4e3a\u8001\u4f5f\u5b9a\u8981\u7834\u53e3\u5927\u9a82\u7684\uff0c\u90a3\u77e5\u8001\u4f5f\u5374\u662f\u95ed\u4e0a\u4e86\u5634\uff0c\u4e0d\u4f5c\u4e00\u58f0\u3002 \u3000\u3000\u4f55\u7389\u67f1\u5f97\u610f\u6d0b\u6d0b\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u8001\u4f5f\u7684\u8eab\u4ef7\u867d\u7136\u6bd4\u4e0d\u4e0a\u5170\u59d1\uff0c\u4f46\u5374\u662f\u4f60 \u7684\u6551\u547d\u6069\u4eba\uff0c\u4f60\u4e0d\u60f3\u4ed6\u4e27\u5728\u6211\u7684\u638c\u4e0b\u5427\uff1f\u201d \u3000\u3000\u4e00\u70b9\u4e0d\u9519\uff0c\u5bf9\u8036\u5f8b\u7384\u6765\u8bf4\uff0c\u6551\u5170\u59d1\u51fa\u53bb\u56fa\u7136\u91cd\u8981\u3002\u4f46\u8001\u4f5f\u7684\u6027\u547d\u4e5f\u662f\u540c\u6837\u91cd\u8981\u7684\uff01 \u3000\u3000\u9976\u662f\u4ed6\u8eab\u6000\u7edd\u4e16\u6b66\u529f\uff0c\u4e00\u65f6\u95f4\u4e5f\u4e0d\u77e5\u9053\u5e94\u8be5\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e2a\u5c40\u9762\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u5df2\u662f\u6447\u6447\u6b32\u5760\uff0c\u5f3a\u529b\u652f\u6301\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u4ed6\u4f24\u4e86\u8001\u4f5f\uff0c\u8036\u5f8b\u5148\u751f\uff0c\u4f60\uff0c\u4f60\u628a\u6211 \u7559\u4e0b\u5427\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u4e0d\u80fd\u8fd9\u6837\uff01\u201d \u3000\u3000\u4f55\u7389\u67f1\u5df1\u5728\u53d1\u51fa\u201c\u547d\u4ee4\u201d\u4e86\uff0c\u4ed6\u4e00\u58f0\u51b7\u7b11\uff0c\u559d\u9053\uff1a\u201c\u6211\u6ca1\u5de5\u592b\u7b49\u4f60\uff0c\u653e\u4e86\u5170\u59d1\uff0c\u9000\u540e\u5341 \u6b65\uff01\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u600e\u80af\u8ba9\u5f20\u96ea\u6ce2\u53c8\u7ed9\u9001\u5165\u864e\u53e3\uff0c\u4ed6\u54ac\u4e86\u54ac\u7259\uff0c\u6b63\u5f85\u5192\u9669\u51fa\u51fb\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97 \u4e00\u58f0\u6495\u5fc3\u88c2\u80ba\u7684\u60e8\u547c\uff0c\u4f55\u7389\u67f1\u7a81\u7136\u5012\u4e86\u4e0b\u53bb\uff0c\u539f\u6765\u8001\u4f5f\u7684\u5185\u529f\u9020\u8be3\u5728\u4f55\u7389\u67f1\u4f30\u8ba1\u4e4b\u4e0a\uff0c\u5728\u4ed6 \u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u8001\u4f5f\u5df2\u7ecf\u9006\u8fd0\u771f\u6c14\uff0c\u51b2\u5f00\u4e86\u88ab\u5c01\u7684\u7a74\u9053\u3002\u4ed6\u62d4\u51fa\u8eab\u85cf\u7684\u5315\u9996\uff0c\u53cd\u624b\u4e00\u523a\u63d2\u5165\u4f55 \u7389\u67f1\u7684\u80f8\u819b\u3002 \u3000\u3000\u4f46\u4f55\u7389\u67f1\u7684\u4e00\u53cc\u624b\u638c\u662f\u8d34\u7740\u4ed6\u80cc\u5fc3\u7684\u6b7b\u7a74\u7684\uff0c\u4ed6\u523a\u6b7b\u4e86\u4f55\u7389\u67f1\uff0c\u4ed6\u7684\u5fc3\u8109\u4ea6\u5df2\u7ed9\u4f55\u7389\u67f1 \u7684\u638c\u529b\u9707\u65ad\u4e86\uff01 \u3000\u3000\u201c\u6211\u5df2\u7ecf\u96c7\u4e86\u4e00\u8f86\u9a6c\u8f66\uff0c\u505c\u3001\u505c\u5728\u74e6\u7a91\u4e1c\u8fb9\u90a3\u6761\u51b7\u5df7\u3002\u201d\u53ea\u80fd\u4ea4\u5f85\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8001\u4f5f\u5c31 \u65ad\u4e86\u6c14\uff01\u5f20\u96ea\u6ce2\u53d7\u4e0d\u8d77\u8fd9\u523a\u6fc0\uff0e\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5730\u9053\u51fa\u53e3\u5904\u662f\u4e00\u5ea7\u8352\u5e9f\u7684\u74e6\u7a91\uff0c\u5de5\u5730\u4e0a\u65e9\u5df2\u957f\u6ee1\u91ce\u8349\uff0c\u9b3c\u5f71\u4e5f\u6ca1\u4e00\u4e2a\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u6761\u5730\u9053\u53ea\u6709\u4e00\u91cc\u591a\u957f\uff0c\u4ea6\u5373\u662f\u8bf4\u548c\u8282\u5ea6\u4f7f\u7684\u8859\u95e8\u8ddd\u79bb\u4e0d\u8fdc\u3002 \u3000\u3000\u4e09\u5343\u5b98\u5175\u5df2\u628a\u8859\u95e8\u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\uff0c\u53ea\u5f85\u8036\u5f8b\u7384\u5143\u95ef\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u4e0d\u89c1\u8036\u5f8b\u7384\u5143\uff0c\u8036\u5f8b\u7384\u5143\u5728\u5de5\u5730\u7684\u9ad8\u5904\u671b\u8fc7\u53bb\uff0c\u5374\u662f\u53ef\u4ee5\u770b\u89c1\u5251\u5c31\u5982\u6797\uff0c\u5200\u5982 \u96ea\u3002 \u3000\u3000\u6ca1\u6709\u65f6\u95f4\u4e3a\u8001\u4f5f\u6599\u7406\u540e\u4e8b\u4e86\uff0c\u8036\u5f8b\u7384\u5143\u53ea\u597d\u5fcd\u7740\u60b2\u75db\uff0c\u80cc\u8d77\u5f20\u96ea\u6ce2\u5c31\u8dd1\u3002 \u3000\u3000\u74e6\u7a91\u4e1c\u9762\u6709\u4e00\u6761\u51b7\u5df7\uff0c\u5df7\u53e3\u679c\u7136\u505c\u6709\u4e00\u8f86\u9a6c\u8f66\u3002 \u3000\u3000\u8f66\u592b\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5df2\u7ecf\u8e0f\u4e0a\u9a6c\u8f66\u4e86\uff0c\u4ed6\u628a\u4e00\u679a\u91d1\u952d\u585e\u5230\u8f66\u592b\u624b\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8001\u4f5f\u7684\u670b\u53cb\uff0c\u522b \u591a\u95ee\uff0c\u5feb\u9a7e\u8f66\u51fa\u57ce\uff01\u201d\u8001\u4f5f\u662f\u66fe\u7ecf\u548c\u8f66\u592b\u8bf4\u8fc7\u8981\u548c\u4e00\u4f4d\u670b\u53cb\u51fa\u57ce\u7684\uff0c\u6b64\u65f6\u8f66\u592b\u867d\u7136\u4e0d\u89c1\u8001 \u4f5f\uff0c\u4f46\u6709\u4e86\u8fd9\u952d\u91d1\u5b50\uff0c\u4ed6\u81ea\u662f\u5949\u547d\u552f\u8c28\u4e86\u3002\u4e5f\u5e78\u4e8f\u5b8c\u989c\u9274\u7edd\u6599\u60f3\u4e0d\u5230\u8036\u5f8b\u7384\u5143\u5c45\u7136\u80fd\u591f\u5728\u5343 \u519b\u4e07\u9a6c\u7684\u5305\u56f4\u4e4b\u4e0b\u9003\u51fa\u53bb\uff0c\u4ed6\u8fd8\u672a\u66fe\u9881\u4e0b\u6212\u4e25\u4ee4\uff0c\u5b88\u57ce\u95e8\u7684\u5175\u58eb\u751a\u81f3\u90fd\u8fd8\u672a\u66fe\u77e5\u9053\u8282\u5ea6\u4f7f\u7684 \u8859\u95e8\u53d1\u751f\u90a3\u6837\u60ca\u4eba\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u9a6c\u8f66\u987a\u5229\u51fa\u57ce\uff0c\u4f46\u5230\u4e86\u90a3\u5ea7\u5c71\u8fb9\u4e4b\u65f6\uff0c\u4ea6\u5df2\u662f\u5c06\u8fd1\u9ec4\u660f\u7684\u65f6\u5206\u4e86\u3002\u8036\u5f8b\u7384\u5143\u4e0b\u4e86\u8f66\uff0c\u5bf9 \u90a3\u8f66\u592b\u9053\uff1a\u201c\u4f60\u56de\u53bb\u7edd\u4e0d\u80fd\u6cc4\u6f0f\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5426\u5219\u4f60\u7684\u8111\u888b\u5c31\u8981\u642c\u5bb6\u3002\u201d \u3000\u3000\u56db\u987e\u65e0\u4eba\uff0c\u4ed6\u80cc\u7740\u5f20\u96ea\u6ce2\u5c31\u5411\u5c71\u4e0a\u8dd1\u3002\u5f20\u96ea\u6ce2\u6b64\u65f6\u4ea6\u5df2\u9192\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u60f3\u8d77\u8001\u4f5f\u4e3a\u5979\u60e8\u6b7b\uff0c\u6cea\u4e0b\u5982\u96e8\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u9053\uff1a\u201c\u64c5\u592b\u4eba\uff0c\u4f60\u5fcd\u7740\u70b9\u513f\uff0c\u4f60\u5c31\u53ef\u4ee5\u89c1\u5f97\u7740\u4f60\u7684\u513f\u5b50\u4e86\u3002 \u3000\u3000\u201d \u3000\u3000\u66ae\u971e\u7b3c\u5c71\uff0c\u8036\u5f8b\u7384\u5143\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u548c\u4ed6\u7ea6\u6700\u591a\u4e09\u4e2a\u65f6\u8fb0\u5c31\u56de\u6765\u7684\uff0c\u73b0\u5728\u6050\u6015\u5df2\u7ecf\u8fc7\u4e86 \u4e09\u4e2a\u65f6\u8fb0\u4e86\u3002\u8fd9\u5b69\u5b50\u6599\u60f3\u662f\u4e0d\u4f1a\u4e71\u8dd1\u7684\uff0c\u4f46\u4e00\u5b9a\u7b49\u5f97\u5fc3\u7126\u4e86\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u53eb\u6a80\u7fbd\u51b2\uff0c\u5c71\u4e0a\u5374\u5148\u4f20\u6765\u547c\u53eb\u7684\u58f0\u97f3\u3002\u4e0d\u662f\u6a80\u7fbd\u51b2\u7684\u58f0\u97f3\uff0c\u662f\u696e\u5ca9\u7684\u53eb\u58f0\uff01 \u3000\u3000\u8036\u5f8b\u7384\u5143\u79bb\u5f00\u4e4b\u65f6\uff0c\u662f\u70b9\u4e86\u696e\u5ca9\u7684\u6655\u7761\u7a74\u7684\uff0c\u7528\u7684\u662f\u8f7b\u624b\u6cd5\u70b9\u7a74\uff0c\u7b97\u51c6\u4e09\u4e2a\u65f6\u8fb0\u4ed6\u7684\u7a74 \u9020\u5c31\u80fd\u81ea\u89e3\u3002\u542c\u89c1\u696e\u5ca9\u7684\u53eb\u58f0\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u4ee4\u5f97\u8036\u5f8b\u7384\u5143\u5927\u4e3a\u5403\u60ca\u7684\u662f\u696e\u5ca9\u8fd9\u53e5\u8bdd\u7684\u5185\u90e8\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u8bdd\u53ea\u6709\u4e03\u4e2a\u5b57\uff1a\u201c\u5feb\u628a\u5b69\u5b50\u653e\u5f00\uff01\u201d \u3000\u3000\u58f0\u97f3\u5145\u6ee1\u60ca\u6050\u548c\u6124\u6012\uff0c\u5c71\u4e0a\u6ca1\u6709\u522b\u7684\u201c\u5b69\u5b50\u201d\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u663e\u7136\u662f\u6a80\u7fbd\u51b2\u8fd9\u5b69\u5b50\u4e1a\u5df2\u843d \u5728\u654c\u4eba\u624b\u91cc\u3002 \u3000\u3000\u53ef\u60dc\u8036\u5f8b\u7384\u5143\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u4e8b\u60c5\u662f\u521a\u5728\u534a\u679d\u9999\u7684\u65f6\u523b\u4e4b\u524d\u53d1\u751f\u7684\u3002 \u3000\u3000\u592a\u9633\u5df2\u7ecf\u843d\u5c71\u4e86\uff0c\u6a80\u7fbd\u51b2\u4f38\u957f\u9888\u5b50\u76fc\u671b\uff0c\u8fd8\u672a\u770b\u89c1\u5e08\u7236\u56de\u6765\u3002 \u3000\u3000\u4ed6\u6025\u5f97\u597d\u50cf\u70ed\u9505\u4e0a\u7684\u8682\u8681\uff0c\u201c\u5e08\u7236\u90a3\u4e48\u597d\u7684\u6b66\u529f\uff0c\u5e94\u8be5\u4e0d\u4f1a\u662f\u51fa\u4e86\u4ec0\u4e48\u4e8b\u5427\uff1f\u201d\u4ed6\u5bf9\u5e08 \u7236\u7684\u6b66\u529f\u662f\u6709\u4fe1\u5fc3\u7684\u201c\u4f46\u8859\u95e8\u91cc\u53ea\u536b\u58eb\u5c31\u6709\u51e0\u767e\u4eba\u4e4b\u591a\uff0c\u5e08\u7236\u4e00\u4e2a\u4eba\u53c8\u80fd\u6253\u5f97\u8fc7\u4ed6\u4eec\u5417\uff01\u201d \u4ed6\u5f00\u59cb\u6709\u70b9\u62c5\u5fc3\u4e86\u3002 \u3000\u3000\u6b63\u81ea\u7b49\u5f97\u5fc3\u7126\uff0c\u5ffd\u7136\u770b\u89c1\u6709\u4e2a\u4eba\u98de\u5feb\u7684\u8dd1\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8fd8\u672a\u770b\u5f97\u6e05\u695a\uff0c\u5c31\u5927\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01\u201d \u3000\u3000\u53ef\u60dc\u6765\u7684\u4e0d\u662f\u4ed6\u7684\u5e08\u7236\u3002\u4ed6\u7684\u58f0\u97f3\u597d\u50cf\u7a81\u7136\u7ed9\u5bd2\u51b0\u5c01\u4f4f\uff0c\u51dd\u7ed3\u4e86\u3002 \u3000\u3000\u6765\u7684\u662f\u5b8c\u989c\u9274\u536b\u961f\u91cc\u7684\u5c0f\u961f\u957f\uff0c\u540d\u53eb\u9ad8\u5360\u9b41\uff0c\u4ed6\u662f\u5949\u4e86\u5b8c\u989c\u9274\u4e4b\u547d\uff0c\u6765\u627e\u8f66\u7f2d\u56de\u53bb\u7684\uff0c \u4ed6\u51fa\u6765\u7684\u65f6\u5019\uff0c\u90a3\u8036\u5f8b\u7384\u5143\u8fd8\u672a\u6765\u5230\u5e9c\u8859\uff0c\u8f66\u7f2d\u662f\u5b8c\u989c\u9274\u7684\u536b\u58eb\u961f\u957f\uff0c\u4e5f\u662f\u5b8c\u989c\u9274\u536b\u58eb\u4e2d\u7684 \u7b2c\u4e00\u9ad8\u624b\uff0c\u5b8c\u989c\u9274\u6b63\u662f\u4e3a\u4e86\u8981\u96c6\u4e2d\u4eba\u529b\u6765\u5bf9\u4ed8\u8036\u5f8b\u7384\u5143\uff0c\u624d\u53eb\u4ed6\u51fa\u6765\u627e\u8f66\u7f2d\u56de\u53bb\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7740\u89c1\u662f\u4ed6\uff0c\u56fa\u7136\u5927\u5403\u4e00\u60ca\uff1a\u4ed6\u770b\u89c1\u4ed6\u8981\u5bfb\u627e\u7684\u8f66\u7f2d\u5df2\u7ecf\u53d8\u6210\u4e00\u5177\u5c38\u4f53\uff0c\u4e03\u7a83\u6d41\u8840 \u5012\u5728\u5730\u4e0a\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u9ad8\u5360\u9b41\u7740\u8f66\u7f2d\u7684\u5c38\u4f53\u5411\u6a80\u7fbd\u51b2\u559d\u95ee\u3002 \u3000\u3000\u201c\u6211\u3001\u6211\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u4e8b\u60c5\u771f\u76f8\u5f53\u7136\u662f\u4e0d\u80fd\u544a\u8bc9\u9ad8\u5360\u9b41\u7684\uff0c\u6a80\u7fbd\u51b2\u7eb5\u7136\u806a\u660e\uff0c\u5728\u6025\u5207\u95f4\u4e5f\u96be\u7f16\u9020\u8c0e\u8bdd\uff0c\u4ed6\u53ea\u597d \u8fd9\u6837\u8bf4\u4e86\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u76ee\u5149\u4e00\u8f6c\uff0c\u53c8\u770b\u89c1\u4e86\u8eba\u5728\u5730\u4e0a\u7684\u696e\u5ca9\uff0c\u696e\u5ca9\u662f\u88ab\u8036\u5f8b\u7384\u5143\u70b9\u4e86\u7a74\u9053\u7684\uff0c\u8eab\u4e0a\u5e76\u65e0 \u8840\u8ff9\uff0c\u770b\u5728\u6765\u50cf\u662f\u88ab\u6253\u6655\u4e86\u8fc7\u53bb\u7684\u6837\u5b50\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u65e0\u6687\u63a8\u6572\uff0c\u53ea\u662f\u731c\u6d4b\uff0c\u7acb\u5373\u53c8\u518d\u559d\u95ee\uff1a\u201c\u662f\u696e\u5ca9\u8fd9\u53ae\u6740\u4e86\u8f66\u90fd\u5c09\u7684\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d\u5230\u5e95\u662f\u5c0f\u5b69\u5b50\uff0c\u8fd9\u4e00\u4e0b\u7acb\u5373\u9732\u51fa\u7834\u7efd\u4e86\uff0c\u9ad8\u5360\u9b41\u559d\u9053\uff1a \u201c\u4f60\u53c8\u8bf4\u4e0d\u77e5\u9053\uff0c\u4f46\u4f60\u5374\u77e5\u9053\u4e0d\u662f\u696e\u5ca9\u6740\u7684\uff01\u54fc\u54fc\uff0c\u4f60\u8fd9\u5c0f\u9b3c\u5934\u7adf\u6562\u5bf9\u6211\u9690\u7792\uff01\u201d\u64c5\u7fbd\u51b2\u8bf4 \u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u521a\u624d\u53eb\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u7136\u563b\u563b\u9053\uff1a\u201c\u6211\u53eb\u7684\u5e08\u7236\u5c31\u662f\u4f60\u5440\uff0c\u4f60\u4e0d\u662f\u6559\u8fc7\u6211\u529f\u592b\u7684\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u5360\u9b41\u4e00\u6014\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u6559\u8fc7\u4f60\u4ec0\u4e48\u529f\u592b\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u5fd8\u8bb0\u4e86\u4e48\uff0c\u8fd9\u62db\u9ed1\u864e\u5077\u5fc3\u4e0d\u5c31\u662f\u4f60\u6559\u7684\u5417\uff1f\u4f60\u5fd8\u8bb0\u6211\u53ef\u6ca1\u6709\u5fd8 \u8bb0\uff0c\u6211\u6f14\u7ed9\u4f60\u770b\u3002\u201d \u3000\u3000\u9ad8\u5360\u9b41\u60f3\u8d77\u6765\u4e86\uff0c\u4e0d\u9519\uff0c\u5927\u7ea6\u4e00\u5e74\u4e4b\u524d\uff0c\u81ea\u5df1\u597d\u50cf\u662f\u6559\u8fc7\u8fd9\u5b69\u5b50\u4e00\u62db\u201c\u9ed1\u864e\u5077\u5fc3\u201d\uff0c\u6a80 \u7fbd\u51b2\u672c\u6765\u5c31\u4f1a\u7684\uff0c\u6709\u4e00\u6b21\u4ed6\u770b\u89c1\u6a80\u7fbd\u51b2\u7ec3\u62f3\uff0c\u4e00\u65f6\u9ad8\u5174\uff0c\u6539\u6b63\u4e86\u4ed6\u51fa\u62f3\u7684\u67d0\u4e2a\u59ff\u52bf\u800c\u5df2\u3002 \u3000\u3000\u4e00\u6765\u201c\u9ed1\u864e\u5077\u5fc3\u201d\u662f\u6700\u666e\u901a\u7684\u62f3\u62db\uff0c\u4f55\u51b5\u4e5f\u8fd8\u4e0d\u80fd\u8bf4\u662f\u4ed6\u6559\u7684\uff1b\u4e8c\u6765\u6559\u8fc7\u6a80\u7fbd\u4ef2\u6b66\u827a\u7684 \u4eba\u5f88\u591a\u3001\u6559\u5f97\u6700\u591a\u7684\u662f\u696e\u5ca9\uff0c\u6a80\u7fbd\u51b2\u5e73\u65f6\u5bf9\u696e\u5ca9\u4e5f\u53ea\u662f\u79f0\u201c\u53d4\u53d4\u201d\uff0c\u800c\u4e0d\u79f0\u201c\u5e08\u7236\u201d\uff0c\u4ed6\u53ea \u6307\u6b63\u8fc7\u4e00\u62db\uff0c\u5c31\u7b97\u53ef\u4ee5\u7528\u201c\u6559\u201d\u5b57\u5427\uff0c\u4e5f\u662f\u6559\u5f97\u6700\u5c11\u7684\u3002\u6a80\u7fbd\u51b2\u4ece\u6765\u4e5f\u4e0d\u79f0\u4ed6\u201c\u5e08\u7236\u201d\uff0c\u4e3a \u4f55\u7a81\u7136\u53eb\u8d77\u6765\u4e86\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u5fc3\u4e2d\u597d\u7b11\uff1a\u201c\u4f60\u8fd9\u5c0f\u9b3c\u5934\u5206\u660e\u5fc3\u91cc\u6709\u9b3c\uff0c\u5012\u60f3\u54c4\u6211\u6b22\u559c\u3002\u4f60\u4ee5\u4e3a\u8fd9\u6837\uff0c\u6211\u5c31\u4e0d\u4f1a \u8ffd\u7a76\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u96be\u5f97\u4f60\u8fd8\u8bb0\u5f97\u6211\u6559\u8fc7\u4f60\u8fd9\u4e00\u62db\u3002\u201d\u4ed6\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u9b3c\u6562\u73a9\u82b1\u62db\uff0c\u6211\u5148\u6253\u4f60\u5c41\u80a1\uff01\u201d \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u6293\uff0c\u4e0d\u6599\u7adf\u7136\u6293\u4e86\u4e2a\u7a7a\u3002\u4ed6\u65b9\u81ea\u4e00\u6115\uff1a\u201c\u8fd9\u5c0f\u9b3c\u5934\u7684\u8eab\u6cd5\u600e\u7684\u5982\u6b64\u6e9c\u6ed1\uff1f\u201d\u53ea \u542c\u5f97\u6a80\u7fbd\u51b2\u5df2\u5728\u626e\u9b3c\u8138\u9053\uff1a\u201c\u5e08\u7236\u6211\u8fd9\u4e00\u62db\u7ec3\u5f97\u600e\u6837\uff1f \u3000\u3000\u4f60\u8bf4\u8fc7\uff0c\u7ec3\u5f97\u597d\u6709\u8d4f\u7684\uff0c\u600e\u4e48\u53cd\u800c\u8981\u6253\u8d77\u6211\u7684\u5c41\u80a1\u6765\u4e86\uff1f\u201d \u3000\u3000\u9ad8\u5360\u9b41\u66f4\u8d77\u7591\u5fc3\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e3a\u5e08\u7684\u8d4f\u4f60\uff01\u201d\u53cc\u81c2\u9f50\u5f20\uff0c\u51b7\u7b11\u58f0\u4e2d\u5411\u6a80\u7fbd\u51b2\u6253\u53bb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u501f\u7740\u7ec3\u8fd9\u62db\u201c\u9ed1\u864e\u5077\u5fc3\u201d\u4e3a\u540d\uff0c\u5c55\u5f00\u8eab\u6cd5\uff0c\u7a81\u7136\u62d4\u51fa\u5315\u9996\uff0c\u5c31\u5411\u4ed6\u523a\u53bb\u3002 \u3000\u3000\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u9ad8\u5360\u9b41\u7684\u8896\u5b50\u7ed9\u5315\u9996\u524a\u53bb\u4e86\u4e00\u5e45\u3002\u53ef\u60dc\u4e24\u4eba\u6b66\u529f\u76f8\u5dee\u751a\u8fdc\uff0c\u9ad8\u5360\u9b41\u51b7\u4e0d \u53ca\u9632\u3002\u9669\u7ed9\u4ed6\u523a\u4f24\u4f24\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff01\u201d\u811a\u5c16\u4e00\u52fe\uff0c\u6a80\u7fbd\u51b2\u7ad9\u7acb\u4e0d\u7a33\uff0c\u767b\u65f6\u7ed9\u4ed6\u6253\u843d \u5315\u9996\uff0c\u6293\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u6293\u8d77\u6a80\u7fbd\u51b2\uff0c\u7acb\u5373\u628a\u6a80\u7fbd\u51b2\u53cc\u624b\u62d7\u5411\u80cc\u540e\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u8f66\u5927\u4eba\u662f\u600e\u4e48\u6b7b\u7684\uff0c \u4f60\u8bf4\u4e0d\u8bf4\uff0c\u4e0d\u8bf4\u6211\u5c31\u8981\u4f60\u5c0f\u547d\uff01\u201d \u3000\u3000\u4ed6\u624b\u4e0a\u591a\u52a0\u4e24\u5206\u529b\u9053\uff0c\u6a80\u7fbd\u51b2\u597d\u50cf\u5df2\u7ecf\u542c\u5f97\u89c1\u81ea\u5df1\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\u4e86\uff0c\u4f46\u4ed6\u8fd8\u662f\u54ac\u7d27\u7259 \u5173\u5fcd\u53d7\uff0c\u4e0d\u542d\u4e00\u58f0\u3002\u4ed6\u6ca1\u6709\u53eb\u51fa\u58f0\u6765\uff0c\u53e6\u4e00\u4e2a\u4eba\u5374\u53eb\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e09\u4e2a\u65f6\u8fb0\u5df2\u7ecf\u8fc7\u53bb\uff0c\u696e\u5ca9\u7684\u7a74\u9053\u4e0d\u89e3\u81ea\u89e3\uff0c\u6070\u597d\u5728\u8fd9\u4e2a\u65f6\u5019\u9192\u8fc7\u6765\u4e86\u3002\u4ed6\u4e00\u5f20\u773c\u775b\u3002\u5c31 \u770b\u89c1\u6a80\u7fbd\u51b2\u6b63\u5728\u88ab\u9ad8\u5360\u9b41\u8650\u5f85\u7684\u60c5\u5f62\uff0c\u5413\u5f97\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u4f60\u4eec\u8fde\u4e00\u4e2a\u5c0f\u5b69\u5b50\u90fd\u4e0d\u80fd\u653e\u8fc7\u5417\uff0c\u6709\u4ec0\u4e48\u7f6a\u6211\u6765\u627f\u62c5\uff0c\u653e\u5f00\u8fd9\u4e2a\u5c0f\u5b69\u5b50\u518d\u8bf4\uff01\u201d\u696e\u5ca9 \u559d\u9053\u3002 \u3000\u3000\u4ed6\u53ea\u9053\u9ad8\u5360\u9b41\u662f\u5df2\u77e5\u9053\u4e86\u5170\u59d1\u7684\u8eab\u4efd\uff0c\u5949\u547d\u6765\u6349\u62ff\u5170\u59d1\u7684\u513f\u5b50\u7684\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u4e0d\u8bf4\u8fd8\u597d\uff0c\u8fd9\u4e00\u8bf4\u66f4\u52a0\u9732\u51fa\u7834\u7efd\u4e86\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u653e\u8fd9\u4e2a\u5c0f\u6742\u79cd\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u4f60\u544a\u8bc9\u6211\uff0c\u4f60\u8981\u66ff\u4ed6\u627f\u62c5\u7684\u7f6a\u662f\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u696e\u5ca9\u559d\u9053\uff1a\u201c\u6211\u6ca1\u5de5\u592b\u548c\u4f60\u8bf4\uff0c\u4f60\u653e\u4e0d\u653e\uff1f\u201d \u3000\u3000\u9ad8\u5360\u9b41\u6e32\uff1a\u201c\u4e0d\u653e\uff01\u201d\u66f4\u52a0\u7528\u529b\u7684\u634f\u6a80\u7fbd\u51b2\u4e86\u3002 \u3000\u3000\u696e\u5ca9\u6251\u8fc7\u53bb\u559d\u9053\uff1a\u201c\u9ad8\u5360\u9b41\uff0c\u4f60\u4e0d\u4e70\u6211\u8fd9\u4e2a\u60c5\u9762\uff0c\u6211\u548c\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u696e\u5ca9\u7684\u804c\u4f4d\u548c\u6b66\u529f\u90fd\u6bd4\u9ad8\u5360\u9b41\u9ad8\uff0c\u672c\u4ee5\u4e3a\u53ef\u4ee5\u9707\u6151\u4ed6\u7684\uff0c\u90a3\u77e5\u9ad8\u5360\u9b41\u770b\u4ed6\u6251\u4e0a\u6765\u65f6\u811a\u6b65 \u8e09\u8dc4\uff0c\u5df2\u662f\u770b\u51fa\u4e86\u4ed6\u7a74\u9053\u65b9\u89e3\uff0c\u529f\u529b\u672a\u590d\u7684\u5f31\u70b9\u3002\u4ed6\u5fc3\u91cc\u60f3\uff1a\u6211\u867d\u7136\u6709\u8fd9\u5c0f\u6742\u79cd\u4f5c\u76fe\u3002\u4f46\u8981 \u628a\u8fd9\u5c0f\u6742\u79cd\u987a\u5229\u5e26\u56de\u53bb\u3002\u53ef\u8fd8\u5f97\u6446\u8131\u8fd9\u53ae\u7684\u7ea0\u7f20\u3002\u4ed6\u4e5f\u52a8\u4e86\u6740\u673a\u3002 \u3000\u3000\u696e\u5ca9\u6251\u4e0a\u6765\u65f6\uff0c\u9ad8\u5360\u9b41\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u6742\u79cd\u7ed9\u4f60\uff01\u201d\u51b7\u7b11\u58f0\u4e2d\uff0c\u628a\u6a80\u7fbd\u51b2\u9ad8\u9ad8\u4e3e \u8d77\uff0c\u4f5c\u4e86\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u7a81\u7136\u5c31\u629b\u51fa\u53bb\u3002 \u3000\u3000\u696e\u5ca9\u5927\u60ca\u4e4b\u4e0b\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u62a2\u4e0a\u53bb\u63a5\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u767d\u5149\u2014\u95ea\uff0c\u9ad8\u5360\u9b41\u98de\u5200\u51fa\u624b\uff0c\u5df2\u63d2\u5165 \u4ed6\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u201c\u4f60\u8981\u62fc\u547d\uff0c\u90a3\u4f60\u53bb\u89c1\u960e\u738b\u5427\uff01\u201d\u9ad8\u5360\u9b41\u52a0\u4e00\u811a\uff0c\u628a\u4e2d\u4e86\u98de\u5200\u7684\u696e\u5ca9\u8e22\u7ffb\uff0c\u9aa8\u788c\u788c\u7684\u6eda \u4e0b\u5c71\u5761\u3002 \u3000\u3000\u8fd9\u4e24\u4e0b\u5b50\u514d\u8d77\u9e58\u843d\uff0c\u4ed6\u6740\u4e86\u696e\u5ca9\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u521a\u597d\u63a5\u7740\u4ece\u534a\u7a7a\u4e2d\u843d\u4e0b\u6765\u7684\u6a80\u7fbd\u51b2\u3002\u6a80\u7fbd \u51b2\u843d\u5165\u4ed6\u7684\u624b\u4e2d\uff0c\u53c8\u662f\u52a8\u5f39\u4e0d\u5f97\u4e86\u3002 \u3000\u3000XXX\u5f20\u96ea\u6ce2\u542c\u89c1\u696e\u5ca9\u60e8\u53eb\u7684\u58f0\u97f3\uff0c\u5413\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u8036\u5f8b\u5148\u751f\uff0c\u4f60\u5feb\u4e0a\u53bb\uff0c \u6551\u6551\u6211\u90a3\u5b69\u5b50\uff01\u201d \u3000\u3000\u6551\u4eba\u8981\u7d27\uff0c\u8036\u5f8b\u7384\u5143\u53ea\u597d\u5c06\u5979\u653e\u4e0b\uff0c\u98de\u6b65\u4e0a\u5c71\u3002 \u3000\u3000\u53ef\u60dc\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u696e\u5ca9\u6eda\u5230\u4ed6\u7684\u8ddf\u524d\uff0c\u5df2\u662f\u904d\u4f53\u9cde\u4f24\u3002\u201c\u8036\u5f8b\u5148\u751f\uff0c\u6211\u540e\u6094\u6ca1\u6709\uff0c\u6ca1\u6709\u542c\u4f60\u7684\u8bdd\u3002\u201d\u4ed6\u53ea \u80fd\u8bf4\u51fa\u6700\u540e\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5c31\u54bd\u6c14\u4e86\u3002 \u3000\u3000\u201c\u5e08\u7236\uff01\u201d\u6a80\u7fbd\u51b2\u53ea\u53eb\u5f97\u4e00\u58f0\uff0c\u5c31\u7ed9\u9ad8\u5360\u9b41\u627c\u4f4f\u4e86\u5589\u5499\uff01\u8036\u5f8b\u7384\u5143\u559d\u9053\uff1a\u201c\u628a\u624b\u653e\u5f00\uff0c \u5426\u5219\u6211\u8a93\u5fc5\u6740\u4f60\uff01\u54fc\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ad8\u5360\u9b41\u51b7\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u671d\u5ef7\u7684\u94a6\u72af\u8036\u5f8b\u7384\u5143\uff0c\u4e0d\u8fc7\u5374\u8fd8\u672a\u77e5\u9053\u4f60\u662f\u8fd9\u5c0f\u6742\u79cd\u7684\u5e08 \u7236\u3002\u4e0d\u9519\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u7684\u6b66\u529f\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f46\u53ef\u501f\u4f60\u7684\u5f92\u5f1f\u5df2\u7ecf\u843d\u5728\u6211\u7684\u624b\u4e2d\uff0c\u4f60\u7eb5\u7136\u80fd \u591f\u6740\u6211\uff0c\u4e5f\u53ea\u80fd\u62a2\u56de\u4f60\u5f92\u5f1f\u7684\u5c38\u4f53\uff01 \u3000\u3000\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u51b7\u7b11\u559d\u9053\uff1a\u201c\u7ed9\u6211\u7ad9\u4f4f\uff0c\u4f60\u6562\u8e0f\u4e0a\u524d\u4e00\u6b65\uff0c\u6211\u5c31\u523a\u8fd9\u5c0f\u6742\u79cd\u4e00\u5200\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u600e\u6837\uff1f\u201d\u8036\u5f8b\u7384\u5143\u559d\u9053\u3002 \u3000\u3000\u201c\u6ca1\u600e\u4e48\u6837\uff0c\u53ea\u8981\u4f60\u4e0d\u63d2\u624b\u7ba1\u8fd9\u95f2\u4e8b\u3002\u6211\u5c31\u4e0d\u4f1a\u4f24\u4f60\u5b9d\u8d1d\u5f92\u5f1f\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u201c\u51b2\u513f\uff0c\u51b2\u513f\uff01\u653e\u5f00\u6211\u7684\u51b2\u513f\uff01\u201d\u5f20\u96ea\u6ce2\u5636\u58f0\u547c\u53eb\uff0c\u4e5f\u8dd1\u4e86\u4e0a\u6765\uff01\u3002 \u3000\u3000\u5979\u624d\u6765\u662f\u53d7\u4f24\u751a\u91cd\uff0c\u8fde\u8d70\u8def\u90e8\u8d70\u4e0d\u52a8\u7684\u3002\u5982\u4eca\u7adf\u7136\u80fd\u591f\u81ea\u5df1\u722c\u4e0a\u8fd9\u5ea7\u5c71\u5cf0\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc \u6765\u7684\u6c14\u529b\uff01 \u3000\u3000\u4f46\u8fd9\u4e48\u4e00\u9635\u72c2\u5954\uff0c\u5979\u7684\u4f24\u53e3\u53c8\u88c2\u5f00\u4e86\uff0c\u8036\u5f8b\u7384\u65e0\u6240\u7528\u7684\u95ed\u7a74\u6b62\u8840\u6cd5\u4e5f\u5931\u6548\u7528\uff0c\u9c9c\u8840\u53c8\u5728 \u6c69\u6c69\u6d41\u51fa\u4e86\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6050\u6015\u975e\u5f97\u7528\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u624b\u6bb5\u6765\u89e3\u51b3\u4e0d\u53ef \u4e86\uff0c\u5426\u5219\u6a80\u592b\u4eba\u7684\u6027\u547d\u5148\u4fdd\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u9ad8\u5360\u9b41\u4e00\u89c1\u5f20\u96ea\u6ce2\u5982\u6b64\u60c5\u5f62\uff0c\u66f4\u4e3a\u5f97\u610f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5170\u59d1\uff0c\u4f60\u820d\u4e0d\u5f97\u4f60\u7684\u513f\u5b50\u5417\uff1f\u90a3 \u4e5f\u5bb9\u6613\uff0c\u4f60\u8ddf\u6211\u4e00\u8d77\u56de\u53bb\u597d\u4e86\u3002\u4f60\u662f\u592b\u4eba\u7684\u4eb2\u4fe1\uff0c\u6599\u60f3\u592b\u4eba\u4e5f\u80fd\u4fdd\u5f97\u4f60\u4eec\u6bcd\u5b50\u5e73\u5b89\u3002\u201d\u4ed6\u660e \u77e5\u592b\u4eba\u5df2\u7ecf\u51fa\u8d70\uff0c\u8fd9\u6837\u8bf4\u81ea\u662f\u6709\u610f\u5632\u8bbd\u201c\u5170\u59d1\u201d\u7684\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u6c14\u5f97\u53cc\u773c\u53d1\u767d\uff0c\u9a82\u4e5f\u9a82\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u4e0d\u8981\u6c42\u4ed6\u3002\u5e08\u7236\u5728\u8fd9\u91cc\uff0c\u4ed6\u4e0d\u6562\u4f24\u6211\u7684\u3002\u4ed6\u4f24\u4e86\u6211\uff0c\u4ed6\u5c31\u5f97\u507f \u547d\u3002\u5e08\u7236\u4f1a\u6551\u6211\u7684\uff01\u201d \u3000\u3000\u9ad8\u5360\u9b41\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u6211\u672c\u6765\u4e0d\u8981\u4f24\u4eba\uff0c\u53ea\u662f\u8981\u628a\u4f60\u62ff\u56de\u53bb\u732e\u7ed9\u5b8c\u989c\u5c06\u519b\u3001\u4f60\u5e08\u7236\u795e\u901a\u518d \u5927\uff0c\u4e5f\u4e0d\u80fd\u4ece\u5b8c\u989c\u5c06\u519b\u624b\u4e2d\u6551\u4f60\u8131\u9669\u5427\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u6211\u65e0\u9700\u4ece\u5b8c\u989c\u9274\u624b\u4e2d\u62a2\u4ed6\u56de\u6765\uff01\u201d\u9661\u5730\u4e00\u58f0\u5927\u559d\uff1a\u201c\u6211\u8981\u4f60\u653e \u4eba\uff0c\u4f60\u6562\u4e0d\u653e\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u559d\u597d\u4f3c\u5728\u9ad8\u5360\u9b41\u5934\u4e0a\u54cd\u8d77\u7126\u96f7\uff0c\u4eca\u5f97\u4ed6\u5fc3\u5934\u5927\u9707\u3002\u4ed6\u672c\u6765\u8981\u8bf4\u201c\u4e0d\u653e\u201d\u7684\uff0c\u4e0d\u77e5\u600e \u7684\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e0d\u4f46\u8bdd\u8bf4\u4e0d\u51fa\u6765\uff0c\u5728\u8fd9\u4e00\u5a01\u4e25\u4e4b\u4e0b\uff0c\u4ed6\u7684\u624b\u4e5f\u98a4\u6296\u8d77\u6765\u4e86\u3002\u63e1\u5728\u624b\u4e2d\u7684\u5315\u9996\u6643\u4e86\u51e0\u6643\uff0c \u98ce\u4e4e\u523a\u7740\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u539f\u6765\u8036\u5f8b\u7384\u5143\u7528\u7684\u662f\u4f5b\u95e8\u7684\u72ee\u5b50\u543c\u529f\uff0c\u8fd9\u4e00\u559d\u80fd\u4ee4\u5978\u4eba\u4e27\u80c6\u3002\u53ef\u60dc\u4ed6\u8fd9\u95e8\u529f\u592b\u8fd8\u672a\u7edf\u5230 \u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5426\u5219\u5df2\u662f\u53ef\u4ee5\u4ee4\u5f97\u9ad8\u5360\u9b41\u7684\u5315\u9996\u4e5f\u638c\u63e1\u4e0d\u7262\u3002 \u3000\u3000\u4f46\u9ad8\u5360\u9b41\u8fd9\u9661\u7136\u4e00\u9707\uff0c\u5374\u5df2\u662f\u7ed9\u4e86\u8036\u5f8b\u7384\u5143\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u8036\u5f8b\u7384\u5143\u65e9\u5df2\u85cf\u5728\u638c\u5fc3\u7684\u4e00\u679a\u94dc\u94b1\u98de\u51fa\uff0c\u6253\u843d\u4e86\u9ad8\u5360\u9b41\u7684\u5315\u9996\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u5fd9\u628a\u6a80\u7fbd\u51b2\u5f53\u4f5c\u76fe\u724c\u3002\u5f80\u524d\u4e00\u8fce\u559d\u9053\uff1a\u201c\u4f60\u6562\u4e0a\uff1f\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u975e\u4f46\u4e0a\u6765\uff0c\u800c\u4e14\u4e00\u62f3\u6253\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u5f53\u7136\u662f\u6253\u5728\u6a80\u7fbd\u51b2\u8eab\u4e0a\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u60ca\u5f97\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u66f4\u5403\u60ca\u7684\u8fd8\u662f\u9ad8\u5360\u9b41\uff0c\u4ed6\u662f\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u8036\u5f8b\u7384\u5143\u6562\u6253\u51fa\u8fd9\u4e00\u62f3\u7684\u3002 \u3000\u3000\u539f\u6765\u8036\u5f8b\u7384\u5143\u7528\u7684\u662f\u65b0\u7ec3\u6210\u7684\u201c\u9694\u7269\u4f20\u529f\u201d\uff0c\u8fd9\u4e00\u62f3\u867d\u7136\u662f\u6253\u5728\u6a80\u7fbd\u51b2\u8eab\u4e0a\uff0c\u4f46\u53d7\u5230\u4ed6 \u8fd9\u4e00\u62f3\u7684\u529b\u5ea6\u7684\u51b2\u51fb\u7684\u5374\u662f\u9ad8\u5360\u9b41\u3002 \u3000\u3000\u9ad8\u5360\u9b41\u5e9e\u5927\u7684\u8eab\u8eaf\u7ed9\u629b\u4e86\u8d77\u6765\uff0c\u5012\u8dcc\u51fa\u53bb\u3002\u6a80\u7fbd\u51b2\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u63a5\u8fc7\u6a80\u7fbd\u51b2\uff0c\u770b\u9ad8\u5360\u9b41\u65f6\uff0c\u9ad8\u5360\u9b41\u5df2\u662f\u4e03\u7a8d\u6d41\u8840\u65e9\u5df2\u5012\u6bd9\u3002 \u3000\u3000\u201d \u3000\u3000XXX\u5f20\u96ea\u6ce2\u6726\u6726\u80e7\u80e7\u7684\u542c\u89c1\u4e86\u6700\u719f\u8bc6\u7684\u3001\u6700\u4eb2\u5207\u7684\u547c\u5524\u3002 \u3000\u3000\u201c\u5988\u5988\u3001\u5988\u5988\uff0c\u4f60\u9192\u9192\u5440\uff0c\u4f60\u9192\u9192\u5440\uff01\u201d \u3000\u3000\u5979\u5f20\u5f00\u773c\u775b\uff0c\u679c\u7136\u5c31\u770b\u89c1\u5979\u7684\u513f\u5b50\u3002\u513f\u5b50\u6b63\u5728\u66ff\u5979\u6577\u836f\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u4e0d\u7528\u62c5 \u5fc3\u4e86\uff0c\u574f\u4eba\u5df2\u7ecf\u7ed9\u5e08\u7236\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u51b2\u513f\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u6577\u836f\u4e86\u3002\u5988\u6709\u8bdd\u548c\u4f60\u8bf4\u3002\u201d\u690d\u7fbd\u51b2\u9053\uff1a\u201c\u5988\uff0c\u4f60\u7684\u4f24\u53e3 \u6b63\u5728\u6d41\u8840\u5462\uff0c\u91d1\u521b\u836f\u600e\u80fd\u4e0d\u6577\uff1f\u4f60\u8bf4\u5427\uff0c\u6211\u5728\u542c\u7740\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u53c8\u662f\u6b22\u559c\u3002\u53c8\u662f\u60b2\u4f24\u3002 \u3000\u3000\u5979\u628a\u60b2\u4f24\u85cf\u5728\u5fc3\u91cc\uff0c\u6b22\u559c\u653e\u5728\u8138\u4e0a\uff0c\u5fcd\u7740\u773c\u6cea\u3002\u7070\u767d\u7684\u8138\u4e0a\u73b0\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u51b2\u513f\uff0c\u4f60\u5df2\u627e\u5230\u5e08\u7236\uff0c\u6211\u662f\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u51b2\u513f\uff0c\u4f60\u80a9\u4e0a\u7684\u62c5\u5b50\u5f88\u91cd\uff0c\u4f60\u660e\u767d\u5417\uff1f\u4f60\u4e00\u5b9a\u8981\u542c \u5e08\u7236\u7684\u6559\u5bfc\uff0c\u5b66\u597d\u6b66\u529f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\uff0c\u6211\u660e\u767d\u7684\uff0c\u516c\u516c\u7684\u4ec7\uff0c\u7237\u7237\u7684\u4ec7\uff0c\u7239\u7239\u7684\u4ec7\uff0c\u8fd8\u6709\u90a3\u4f4d\u6211\u4ece\u672a\u89c1\u8fc7\u7684 \u5916\u516c\u7684\u4ec7\uff0c\u90fd\u5e94\u8be5\u7531\u6211\u66ff\u4ed6\u4eec\u53bb\u62a5\u7684\u3002\u6211\u600e\u80fd\u4e0d\u7ec3\u597d\u6b66\u529f\uff1f\u201d\u5f20\u96ea\u6ce2\u53f9\u9053\uff1a\u201c\u51b2\u513f\u3002\u4f60\u8fd8\u662f \u672a\u80fd\u61c2\u5f97\u5988\u7684\u610f\u601d\uff0c\u6211\u8bf4\u7684\u62c5\u5b50\u4e0d\u5355\u662f\u6307\u62a5\u4ec7\u3002\u5509\uff0c\u8fd9\u4e24\u5e74\u6211\u60f3\u5f97\u8bb8\u591a\uff0c\u6e10\u6e10\u4e5f\u61c2\u5f97\u4e00\u70b9\u9053 \u7406\uff0c\u6211\u60f3\u8bf4\u7684\u662f\u62a5\u4ec7\u4ee5\u5916\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u54b3\u4e86\u4e24\u58f0\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u4f4e\u6c89\u4e86\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7684\u4eb2\u4eba\uff0c\u6709\u7684\u662f\u7ed9\u5b8b\u56fd\u7684\u7687\u5e1d \u548c\u5978\u81e3\u5bb3\u6b7b\u7684\uff0c\u6709\u7684\u662f\u7ed9\u91d1\u56fd\u7684\u7687\u5e1d\u548c\u5978\u81e3\u5bb3\u6b7b\u7684\uff0c\u54b3\uff0c\u54b3\uff0c\u8981\u62a5\u4ec7\u4e5f\u4e0d\u77e5\u4ece\u4f55\u62a5\u8d77\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8f7b\u8f7b\u7ed9\u5979\u63c9\u6413\u80cc\u90e8\uff0c\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u6b47\u4e00\u4f1a\u518d\u8bf4\u5427\u3002\u201d\u4f46\u5f20\u96ea\u6ce2\u8fd8\u662f\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u6211\u8eab\u4e0a\u85cf\u6709\u4e00\u4e2a\u9526\u76d2\uff0c\u4f60\u62ff\u51fa\u6765\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u662f\u3002\u201d\u5fc3\u60f3\u5988\u5988\u8fd9\u6837\u90d1\u91cd\u5176\u4e8b\uff0c\u9526 \u76d2\u91cc\u85cf\u7684\u662f\u4ec0\u4e48\u73cd\u91cd\u4e1c\u897f\u3002\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u6253\u5f00\u6765\u770b\uff01\u201d \u3000\u3000\u9526\u76d2\u91cc\u85cf\u7684\u4e0d\u662f\u5947\u73cd\u5f02\u5b9d\uff0c\u662f\u4e00\u5f20\u6b8b\u65e7\u53d1\u9ec4\u7684\u5b57\u7eb8\u3002 \u3000\u3000\u5f20\u96ea\u6ce2\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5916\u516c\u4eb2\u7b14\u5199\u7684\u4e00\u9996\u8bcd\uff0c\u4ed6\u662f\u5b8b\u56fd\u7684\u540d\u5c06\uff0c\u59d3\u5cb3\u540d\u98de\u540e\u6765\u7ed9\u548c\u4e00\u4e2a\u540d \u53eb\u79e6\u6867\u7684\u5978\u81e3\u5bb3\u6b7b\u7684\u3002\u4ed6\u5199\u7684\u8fd9\u9996\u8bcd\u540d\u53eb\u6ee1\u6c5f\u7ea2\u3002\u4ed6\u7684\u4e66\u6cd5\uff0c\u662f\u6211\u7684\u4e49\u7236\u5192\u4e86\u751f\u547d\u7684\u5371\u9669\u4e3a \u6211\u4fdd\u5b58\u4e0b\u6765\u7684\uff0c\u73b0\u5728\u4ea4\u7ed9\u4f60\u4e86\u3002\u4f60\u8bfb\u4e0d\u61c2\uff0c\u53ef\u4ee5\u8bf7\u5e08\u7236\u8bb2\u89e3\u3002\u73b0\u4eca\u5bb3\u4ed6\u7684\u5978\u81e3\u4ea6\u5df2\u6b7b\u4e86\uff0c\u4ed6 \u7684\u51a4\u6789\u76f8\u4fe1\u603b\u6709\u4e00\u5929\u4f1a\u662d\u96ea\u7684\u3002\u6211\u7684\u7239\u7239\u548c\u4ed6\u540c\u65f6\u88ab\u5bb3\uff0c\u846c\u5728\u4e00\u8d77\u3002\u6211\u5e0c\u671b\u5c06\u6765\u4f60\u80fd\u591f\u5230\u4ed6 \u4eec\u575f\u524d\u4e00\u796d\uff0c\u4ee5\u8865\u6211\u7684\u9057\u61be\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5988\uff0c\u6211\u4f1a\u548c\u4f60\u4e00\u8d77\u53bb\u7684\u3002\u201d \u3000\u3000\u5f20\u96ea\u6ce2\u82e6\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e0d\u80fd\u53bb\u4e86\u3002\u5509\uff0c\u6ca1\u65f6\u5019\u7ed9\u6211\u591a\u8bf4\u4e86\uff0c\u4f60\u542c\u7740\u2014\u2014\u5979\u8bf4\u4e86\u8bb8\u591a\u8bdd\uff0c \u6c14\u5598\u8d8a\u53d1\u52a0\u4fc3\u4e86\uff0c\u6a80\u7fbd\u51b2\u5fc3\u75db\u5982\u7ede\uff0c\u5374\u65e0\u6cd5\u963b\u6b62\u5979\u4e0d\u8bf4\u3002 \u3000\u3000\u201c\u6211\u8bf4\u7684\u662f\u62a5\u4ec7\u4ee5\u5916\u7684\u4e8b\u60c5\uff0c\u8bb0\u7740\uff0c\u4f60\u7684\u7236\u4eb2\u662f\u91d1\u56fd\u4eba\uff0c\u4f60\u6bcd\u4eb2\u662f\u5b8b\u56fd\u4eba\uff0c\u91d1\u5b8b\u867d\u662f\u654c \u56fd\uff0c\u4f60\u7684\u7236\u6bcd\u5374\u662f\u6069\u7231\u592b\u59bb\u2014\u2014\u201d\u5979\u5b9e\u5728\u8bf4\u4e0d\u4e0b\u53bb\u4e86\uff0c\u6700\u540e\u53ea\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u660e\u767d\u5417\uff1f\u201d\u8036 \u5f8b\u7384\u5143\u77e5\u9053\u4e0d\u5999\uff0c\u8fde\u5fd9\u628a\u624b\u638c\u8d34\u5728\u5979\u80cc\u5fc3\uff0c\u771f\u6c14\u8f93\u9001\u8fdb\u53bb\u3002\u5f20\u96ea\u6ce2\u7741\u5f00\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d \u61c2\u3002\u4f60\u53ef\u4ee5\u95ee\u4f60\u5e08\u7236\u3002\u8036\u5f8b\u5148\u751f\u4e3a\u6211\u6211\u7684\u7f18\u6545\uff0c\u5df2\u7ecf\u8fde\u7d2f\u4e86\u4f5f\u5e08\u5085\u3001\u696e\u5ca9\u7b49\u4eba\u4e3a\u6211\u8eab\u4ea1\u3002\u6211 \u4e0d\u80fd\u518d\u8fde\u7d2f\u4f60\u4eec\u4e86\u3002\u6709\u4f60\u7167\u6599\u51b2\u513f\uff0c\u6211\u653e\u5fc3\u5f97\u5f88\u3002\u6211\u53ef\u4ee5\u65e9\u70b9\u53bb\u89c1\u4ed6\u7684\u7239\u7239\u4e86\u3002\u201d \u3000\u3000\u8036\u5f8b\u7384\u5143\u53eb\u9053\uff1a\u201c\u6a80\u592b\u4eba\u3001\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d\u4f46\u5f20\u96ea\u6ce2\u5df2\u7ecf\u7791\u76ee\u4e86\u3002\u5979\u53d7\u4f24\u6781\u91cd\uff0c\u5168\u51ed\u8981\u89c1 \u513f\u5b50\u7684\u613f\u671b\u652f\u6301\u7740\u5979\uff0c\u5982\u4eca\u5fc3\u613f\u5df1\u4e86\uff0c\u7eb5\u6709\u7384\u5143\u5c06\u771f\u6c14\u8f93\u5165\u5979\u7684\u4f53\u5185\uff0c\u4ea6\u5df2\u662f\u8fd8\u9b42\u65e0\u672f\uff01 \u3000\u3000\u65f6\u5149\u6d41\u5931\uff0c\u8f6c\u77ac\u8fc7\u4e86\u4e03\u5e74\u3002 \u3000\u3000\u8fd9\u4e03\u5e74\u5f53\u4e2d\uff0c\u91d1\u5b8b\u4e24\u56fd\u6253\u6253\u8c08\u8c08\uff0c\u8c08\u8c08\u6253\u6253\uff0c\u5927\u4ed7\u6253\u8fc7\u4e00\u6b21\uff0c\u5c0f\u4ed7\u4e0d\u4e0b\u6570\u5341\uff0c\u6700\u540e\u8fd8\u662f \u4ee5\u5b8b\u56fd\u7f14\u7ed3\u4e86\u5c48\u8fb1\u7684\u548c\u7ea6\u7ed3\u675f\u4e86\u6218\u4e89\u3002 \u3000\u3000\u8c01\u4e5f\u77e5\u9053\u8fd9\u6837\u7684\u548c\u5e73\u662f\u4e0d\u80fd\u7ef4\u6301\u957f\u4e45\u7684\uff0c\u6218\u4e89\u968f\u65f6\u53ef\u4ee5\u91cd\u5f00\uff0c\u6240\u8c13\u201c\u7ed3\u675f\u201d\u53ea\u662f\u6682\u65f6\u7ed3 \u675f\u800c\u5df2\u3002 \u3000\u3000\u4f46\u6218\u4e89\u603b\u7b97\u505c\u6b62\u4e0b\u6765\uff0c\u867d\u7136\u8001\u767e\u59d3\u4ecd\u662f\u672a\u80fd\u5598\u8fc7\u6c14\u6765\uff0c\u4f46\u4e5f\u6709\u4e00\u4e9b\u4eba\u5374\u662f\u53c8\u53ef\u4ee5\u91cd\u8fc7\u6b4c \u821e\u5347\u5e73\u7684\u65e5\u5b50\u4e86\u3002 \u3000\u3000\u4eca\u65e5\u7684\u5f52\u4e91\u5c71\u5e84\u5c31\u6b63\u662f\u8fd9\u6837\u4e00\u4e2a\u6b4c\u821e\u5347\u5e73\u4e4b\u5730\u3002"}),showOnHomepage:!0},{name:" \u7b2c \u516b \u56de\u3000\u5927\u95f9\u5bff\u7b75 ",content:(0,s.jsx)(s.Fragment,{children:"\u5bfb\u4e91\u5e84\u5728\u6d1b\u9633\u57ce\u5916\u4e1c\u5357\u5341\u91cc\u4e4b\u5730\uff0c\u5e84\u4e3b\u5f52\u5143\u9f99\u662f\u6b66\u6797\u5927\u5bb6\uff0c\u5bb6\u8d22\u767e\u4e07\u3002 \u3000\u3000\u4ed6\u4e0d\u53ea\u6709\u94b1\uff0c\u800c\u4e14\u6709\u52bf\u3002\u5927\u5f92\u5f1f\u73ed\u5b9a\u5c71\u662f\u6d1b\u9633\u864e\u5a01\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u864e\u5a01\u9556\u5c40\u662f\u548c\u4e2d\u90fd \uff08\u5317\u4eac\uff09\u9707\u8fdc\u9556\u5c40\u9f50\u540d\u7684\u5927\u9556\u5c40\uff1b\u4e8c\u5f92\u5f1f\u9b4f\u8fde\u9b41\u662f\u6d1b\u9633\u603b\u5175\u76d6\u5929\u96c4\u624b\u4e0b\u7684\u53c2\u5c06\uff0c\u53c2\u5956\u867d\u4e0d\u8fc7 \u662f\u4e94\u54c1\u6b66\u5b98\uff0c\u4f46\u56e0\u4ed6\u751a\u5f97\u603b\u5175\u5ba0\u4fe1\uff0c\u4e5f\u662f\u4e00\u4e2a\u53ef\u4ee5\u624b\u64cd\u751f\u6740\u4e4b\u6743\u7684\u5b98\u513f\u4e86\u3002\u4ed6\u53c8\u6709\u201c\u5c0f\u76c2 \u5c1d\u201d\u4e4b\u79f0\uff0c\u95e8\u5ba2\u867d\u7136\u4e0d\u53ca\u5b5f\u5c1d\u541b\u7684\u4e09\u5343\u4e4b\u591a\uff0c\u4ea6\u662f\u6570\u4ee5\u767e\u8ba1\u3002 \u3000\u3000\u4eca\u5929\u662f\u4ed6\u7684\u516d\u5341\u5927\u5bff\u7684\u5bff\u8fb0\u3002 \u3000\u3000\u4ed6\u672c\u8eab\u7684\u4ea4\u6e38\u5df1\u662f\u6781\u4e3a\u5e7f\u9614\uff0c\u518d\u52a0\u4e0a\u6709\u4e24\u4e2a\u201c\u5962\u62e6\u201d\u7684\u5f92\u5f1f\u4e3a\u4ed6\u505a\u5bff\uff0c\u6d1b\u9633\u57ce\u91cc\u6709\u5934\u9762 \u7684\u4eba\u7269\u8c01\u4e0d\u60f3\u6765\u5df4\u7ed3\uff0c\u5f53\u771f\u662f\u8d3a\u5ba2\u5982\u4e91\u3002 \u3000\u3000\u597d\u5728\u4ed6\u5bb6\u6709\u4e2a\u5927\u82b1\u56ed\uff0c\u8db3\u4ee5\u5bb9\u7eb3\u5bbe\u5ba2\u3002 \u3000\u3000\u5bff\u7b75\u5c1a\u672a\u6446\u5f00\uff0c\u5bbe\u5ba2\u6709\u7684\u5728\u56ed\u4e2d\u8d4f\u82b1\uff0c\u6709\u7684\u5728\u82b1\u56ed\u4e2d\u542c\u620f\uff0c\u4e5f\u6709\u501f\u8fd9\u4e2a\u673a\u4f1a\u4e0e\u5e73\u65f6\u5c11\u89c1 \u9762\u7684\u670b\u53cb\u76f8\u53d9\u7684\u3001\u5404\u9002\u5176\u9002\uff0c\u70ed\u95f9\u5f02\u5e38\u3002 \u3000\u3000\u83ca\u82b1\u3001\u5170\u82b1\u3001\u6c34\u4ed9\u3001\u94f6\u67f3\u3001\u828d\u836f\u3001\u91d1\u949f\u7eb5\u7136\u8fd8\u8bf4\u4e0d\u4e0a\u767e\u82b1\u9f50\u653e\uff0c\u82b1\u5349\u7684\u54c1\u79cd\u4e4b\u591a\uff0c\u4e5f\u8db3 \u4ee5\u591f\u77a7\u7684\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u56ed\u4e2d\u79cd\u7684\u6700\u591a\u7684\u8fd8\u662f\u7261\u4e39\u3002 \u3000\u3000\u5ba2\u4eba\u4eec\u5728\u5567\u5567\u79f0\u8d4f\uff0c\u6307\u70b9\u5404\u79cd\u7f55\u89c1\u7684\u540d\u79cd\u7261\u4e39\uff1a\u5927\u80e1\u7ea2\uff0c\u5927\u4e2d\u7d2b\u3001\u70df\u9f99\u7d2b\u3001\u7167\u7c89\u3001\u767d \u7389\u3001\u845b\u4e2d\u7d2b\u3001\u84dd\u7389\u2014\u2014\u201c\u771f\u591a\uff0c\u6211\u770b\u9664\u4e86\u5fa1\u82b1\u56ed\uff0c\u6050\u6015\u6ca1\u6709\u54ea\u5bb6\u4eba\u5bb6\u7684\u82b1\u56ed\u91cc\u6709\u8fd9\u4e48\u591a\u7261\u4e39 \u4e86\u3002\u201d\u4e00\u4e2a\u5ba2\u4eba\u9053\u3002 \u3000\u3000\u201c\u8001\u5144\uff0c\u4f60\u771f\u662f\u5c11\u89c1\u591a\u602a\u3002\u636e\u6211\u6240\u77e5\uff0c\u6709\u4e00\u5bb6\u4eba\u5bb6\uff0c\u4ed6\u82b1\u56ed\u91cc\u7684\u7261\u4e39\u5c31\u6bd4\u8fd9\u91cc\u591a\u5f97 \u591a\u3002\u201d\u53e6\u4e00\u4e2a\u5ba2\u4eba\u9053\u3002 \u3000\u3000\u201c\u662f\u54ea\u4e00\u5bb6\uff1f\u201d \u3000\u3000\u201c\u5546\u5dde\u8282\u5ea6\u4f7f\u5b8c\u989c\u5c06\u519b\u6709\u4e00\u4e2a\u82b1\u56ed\u662f\u4e13\u79cd\u7261\u4e39\u7684\u3002\u8fd9\u4e9b\u540d\u79cd\u7261\u4e39\u4ed6\u90fd\u6709\uff0c\u8fd9\u91cc\u6ca1\u6709\u7684\u4ed6 \u4e5f\u6709\uff0c\u8d5b\u7ea2\u3001\u59da\u9ec4\u3001\u7476\u6c60\u6625\u662f\u6700\u540d\u8d35\u7684\u4e09\u79cd\u7261\u4e39\uff0c\u4f60\u89c1\u8fc7\u6ca1\u6709\uff1f\u6211\u5c31\u5728\u5b8c\u989c\u5c06\u519b\u7684\u82b1\u56ed\u91cc\u89c1 \u8fc7\u3002\u6709\u4eba\u8bf4\u5fa1\u82b1\u56ed\u91cc\u7684\u7261\u4e39\uff0c\u4e5f\u6ca1\u6709\u4ed6\u7684\u7261\u4e39\u597d\u5462\u3002\u201d \u3000\u3000\u7b2c\u4e00\u4e2a\u5ba2\u4eba\u672c\u662f\u60f3\u62cd\u4e3b\u4eba\u5bb6\u7684\u9a6c\u5c41\u7684\uff0c\u4e00\u542c\u4ed6\u62ac\u51fa\u5b8c\u989c\u5c06\u519b\uff0c\u5c31\u4e0d\u548c\u4ed6\u62ac\u6760\u4e86\uff0c\u53ea\u6562\u5600 \u5495\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u4e00\u822c\u4eba\u5bb6\uff0c\u4f60\u8bf4\u7684\u662f\u5c06\u519b\u5e9c\uff0c\u518d\u6709\u94b1\u7684\u5bcc\u8d35\u4eba\u5bb6\u4e5f\u4e0d\u80fd\u548c\u5b8c\u989c\u5c06\u519b\u6bd4 \u5440\u3002\u201d\u53ef\u662f\u4ed6\u4e0d\u6562\u548c\u90a3\u4eba\u62fe\u625b\uff0c\u53e6\u5916\u5374\u6709\u4eba\u8981\u548c\u90a3\u4eba\u62ac\u6760\u3002 \u3000\u3000\u8fd9\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u66fe\u7ecf\u5230\u8fc7\u5b8c\u989c\u5c06\u519b\u7684\u82b1\u56ed\u8d4f\u82b1\u7684\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5c48\u6307\u4e00\u7b97\uff0c\u8bf4\u9053\uff1a\u201c\u516b\u5e74\u4e4b\u524d\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u96be\u602a\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5176\u4e8c\uff1f\u201d \u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u5b8c\u989c\u5c06\u519b\u5df2\u7ecf\u6709\u4e03\u5e74\u6ca1\u6709\u9080\u8bf7\u5ba2\u4eba\u53bb\u4ed6\u5bb6\u8d4f\u7261\u4e39\u4e86\u3002\u636e\u8bf4\u662f\u4ed6\u5bb6\u4e24\u4e2a\u6700\u6709 \u672c\u4e8b\u7684\u82b1\u738b\u5df2\u7ecf\u6b7b\u53bb\uff0c\u7261\u4e39\u6ca1\u4eba\u6599\u7406\uff0c\u65e9\u5df2\u96f6\u843d\u4e86\u3002\u201d \u3000\u3000\u6b64\u65f6\u5374\u53e6\u6709\u4e00\u4e2a\u5f52\u5bb6\u7684\u95e8\u5ba2\u5728\u4e00\u68f5\u7261\u4e39\u4e0b\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u4f60\u4e5f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u201d \u3000\u3000\u5bbe\u5ba2\u5927\u90fd\u53bb\u89c2\u8d4f\u201c\u540d\u79cd\u7261\u4e39\u201d\uff0c\u8fd9\u682a\u7261\u4e39\u6ca1\u4eba\u7279\u522b\u4ecb\u7ecd\uff0c\u4f3c\u4e4e\u4e0d\u662f\u4ec0\u4e48\u540d\u79cd\uff0c\u56e0\u6b64\u5728\u82b1 \u4e0b\u7684\u53ea\u6709\u4ed6\u4e00\u4eba\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u6ca1\u4eba\u542c\u89c1\uff0c\u5ffd\u5730\u6709\u4e00\u4e2a\u4eba\u4ece\u65c1\u95e8\u51fa\u6765\uff0c\u7b11\u7740\u95ee\u4ed6\u9053\uff1a\u201c\u8001\u5019\uff0c\u4ec0\u4e48\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d \u77e5\u5176\u4e8c\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u5f52\u5143\u9f99\u7684\u8001\u4ec6\u4eba\uff0c\u5728\u5f52\u5bb6\u662f\u9887\u6709\u5730\u4f4d\u7684\uff0c\u59d3\u5a04\u540d\u963f\u9f20\uff0c\u6392\u884c\u7b2c\u4e03\u3002\u56e0\u4ed6\u540d\u5b57\u4e0d \u96c5\uff0c\u5f52\u5bb6\u7684\u95e8\u5ba2\u90fd\u5c0a\u4ed6\u4e3a\u4e03\u53d4\u3002 \u3000\u3000\u90a3\u59d3\u5019\u7684\u95e8\u5ba2\u5355\u540d\u4e00\u4e2a\u201c\u6606\u201d\u5b57\uff0c\u5eb8\u788c\u65e0\u80fd\u3002\u4e00\u5411\u6ca1\u4eba\u77a7\u5f97\u8d77\u4ed6\uff0c\u6b64\u65f6\u5374\u6709\u70b9\u5f97\u610f\u7684\u795e \u60c5\u8bf4\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u53ea\u77e5\u9053\u8d5b\u7ea2\u3001\u59da\u9ec4\u548c\u7476\u6c60\u6625\u662f\u540d\u79cd\u7261\u4e39\uff0c\u5374\u4e0d\u77e5\u8fd9\u682a\u2018\u9752\u9f99\u5367\u58a8\u5730\u2019\u66f4\u662f \u7261\u4e39\u4e2d\u7684\u6781\u54c1\uff0c\u5c82\u4e0d\u53ef\u7b11\uff1f\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u5ffd\u5730\u4f3c\u7b11\u975e\u7b11\u7684\u8bf4\u9053\uff1a\u201c\u8001\u4faf\uff0c\u4f60\u662f\u5728\u5b8c\u989c\u5c06\u519b\u624b\u4e0b\u5f53\u8fc7\u5dee\u7684\uff0c\u5c06\u519b\u7684\u82b1\u56ed\u91cc \u60f3\u5fc5\u4e5f\u6709\u8fd9\u79cd\u7261\u4e39\uff0c\u4f60\u89c1\u5f97\u591a\uff0c\u602a\u4e0d\u5f97\u773c\u754c\u8fd9\u6837\u9ad8\u4e86\uff01 \u3000\u3000\u201d \u3000\u3000\u5019\u6606\u5403\u4e86\u4e00\u60ca\uff0c\u98a4\u58f0\u8bf4\uff1a\u201c\u4f60\u3001\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u8001\u4ec6\u4eba\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u4f60\u7684\u6765\u5386\uff0c\u4e3b\u4eba\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u4e0d\u8fc7\u4ed6\u53ea\u544a\u8bc9\u6211\u4e00\u4e2a\u4eba\u3002\u201d\u5019\u6606 \u9053\uff1a\u201c\u5e84\u4e3b\u53ef\u662f\u6000\u7591\u6211\u3001\u6211\u201d\u8001\u4ec6\u4eba\u9053\uff1a\u201c\u4e3b\u4eba\u662f\u6709\u6000\u7591\uff0c\u4ed6\u6000\u7591\u4f60\u662f\u5b8c\u989c\u5c06\u519b\u6d3e\u6765\u76d1\u89c6\u4ed6 \u7684\u3002\u201d\u5019\u6606\u5fd9\u9053\uff1a\u201c\u7edd\u5bf9\u6ca1\u6709\u8fd9\u56de\u4e8b\u3002 \u3000\u3000\u4e03\u53d4\u8bf7\u4f60\u4ee3\u5411\u4e3b\u8868\u767d\uff0c\u6211\u6765\u6295\u9760\u4ed6\u53ea\u662f\u4e3a\u4e86\u6df7\u4e24\u7897\u996d\u5403\u7684\u3002\u201d\u8001\u4ec6\u4eba\u9053\uff1a\u201c\u8001\u5019\uff0c\u6211\u4eec \u7684\u4ea4\u60c5\u8fd8\u7b97\u8fc7\u5f97\u53bb\u5427\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u4e03\u53d4\uff0c\u4f60\u662f\u5728\u5f52\u4e91\u5e84\u91cc\u5bf9\u6211\u6700\u597d\u7684\u4eba\u3002\u201d \u3000\u3000\u8001\u4ec6\u4eba\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u5bf9\u6211\u4e5f\u4e0d\u8bf4\u771f\u8bdd\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u771f\u8bdd\u5440\u3002\u201d \u3000\u3000\u8001\u4ec6\u4eba\u628a\u4ed6\u5230\u5047\u5c71\u77f3\u540e\uff0c\u8fd9\u624d\u5fae\u5e26\u51b7\u5632\u7684\u7b11\u9053\uff1a\u201c\u8001\u4faf\uff0c\u4f60\u662f\u5b8c\u989c\u5c06\u519b\u7684\u536b\u58eb\uff0c\u8fd8\u6101\u6ca1 \u996d\u5403\u4e48\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u4e03\u53d4\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u6211\u56e0\u5927\u75c5\u4e00\u573a\uff0c\u6b66\u529f\u592b\u4e86\u4e00\u5927\u534a\uff0c\u6ca1\u8d44\u683c\u505a\u5b8c\u989c\u5c06\u519b\u7684 \u536b\u58eb\u4e86\uff0c\u8feb\u4e0d\u5f97\u5df1\u624d\u6765\u6295\u9760\u8d35\u5e84\u4e3b\u7684\u3002\u201d \u3000\u3000\u8001\u4ec6\u4eba\u5f53\u7136\u4e0d\u76f8\u4fe1\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u771f\u662f\u5047\uff0c\u4f46\u8ba9\u4e3b\u4eba\u6709\u90a3\u4e2a\u6000\u7591\u5bf9\u4f60\u662f\u597d\u5904\u6ca1 \u6709\u574f\u5904\u7684\uff0c\u4f60\u53c8\u4f55\u987b\u89e3\u91ca\u3002\u4ed6\u4ee5\u4e3a\u4f60\u662f\u5b8c\u989c\u5c06\u519b\u7684\u4eba\uff0c\u5bf9\u4f60\u5df4\u7ed3\u8fd8\u6765\u4e0d\u53ca\u5462\u3002\u4f46\u8bdd\u8bf4\u56de\u6765\uff0c \u6211\u5bf9\u5e84\u4e3b\uff0c\u53ef\u4e5f\u4e0d\u80fd\u6709\u4e1d\u6beb\u6000\u7591\u624d\u597d\uff0c\u5e84\u4e3b\u7684\u786e\u662f\u5fe0\u4e8e\u671d\u5ef7\u7684\u3002\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u624d\u5230\u8fd9\u91cc\u6295\u5954\u3002\u4e0d\u8fc7\u2014\u2014\u201d\u8001\u4ec6\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u6211\u81ea\u77e5\u672c\u9886\u4e0d\u6d4e\uff0c\u65e0\u989c\u5728\u8fd9\u91cc\u6df7\u4e0b\u53bb\u4e86\u3002\u201d\u8001\u4ec6\u9053\uff1a\u201c\u4f60\u60f3\u8d70\uff1f\u201d\u5019\u6606\u70b9 \u5934\u3002\u8bf4\u9053\uff1a\u201c\u4e03\u53d4\uff0c\u8bf7\u4f60\u4ee3\u4e3a\u7980\u544a\u5e84\u4e3b\uff0c\u6055\u6211\u4e0d\u8f9e\u800c\u522b\u3002 \u3000\u3000\u201d \u3000\u3000\u8001\u4ec6\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u6211\u5c31\u4e0d\u653e\u4f60\u8d70\uff01\u201d \u3000\u3000\u5019\u6606\u4e00\u60f3\uff0c\u8fd9\u8001\u4ec6\u4eba\u867d\u7136\u662f\u5e84\u4e3b\u7684\u5fe0\u4ec6\uff0c\u4f46\u548c\u522b\u7684\u5f97\u52bf\u4ec6\u4eba\u4e0d\u4e00\u6837\uff0c\u4ed6\u4ece\u4e0d\u4ed7\u52bf\u6b3a\u4eba\uff0c \u7b97\u5f97\u662f\u6bd4\u8f83\u5fe0\u539a\u8001\u5b9e\u7684\u3002\u4fbf\u9053\uff1a\u201c\u4e03\u53d4\uff0c\u8fd9\u70b9\u6211\u53ea\u80fd\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u8001\u4ec6\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u8bf4\u5427\uff0c\u6211\u4e0d\u544a\u8bc9\u4e3b\u4eba\u5c31\u662f\u3002\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u8fd9\u682a\u9752\u9f99\u5367\u58a8\u6c60\u4eca\u65e5\u5f00\u82b1\uff0c\u6211\u9690\u9690\u89c9\u5f97\u662f\u4e0d\u7965\u4e4b\u5146\u3002\u201d\u8001\u4ec6\u4eba\u8be7\u9053\uff1a\u201c\u4e3a\u4f55 \u4f60\u4f1a\u8fd9\u6837\u60f3\u5462\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u4e03\u5e74\u524d\u7684\u67d0\u4e00\u5929\uff0c\u5b8c\u989c\u5c06\u519b\u82b1\u56ed\u91cc\u7684\u90a3\u682a\u9752\u9f99\u5367\u58a8\u6c60\u5f00\u82b1\u3002\u90a3\u5929\u5c06\u519b\u5c31\u78b0\u4e0a \u4e86\u4e0d\u5982\u610f\u7684\u4e8b\u3002\u201d \u3000\u3000\u8001\u4ec6\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5982\u610f\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u8fd9\u4e2a\u3001\u8fd9\u2014\u2014\u201d\u84e6\u5730\u60f3\u8d77\u521a\u624d\u90a3\u4e24\u4e2a\u53ea\u77e5\u9053\u542c\u9014\u8bf4\u7684\u5ba2\u4eba\u6240\u8bf4\u7684\u6709\u5173\u5b8c\u989c\u9274 \u7684\u5bb6\u4e8b\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u90a3\u5929\uff0c\u5c06\u519b\u7684\u4e24\u4e2a\u8001\u82b1\u738b\u5ffd\u7136\u540c\u4e00\u5929\u66b4\u75c5\u800c\u4ea1\uff0c\u6211\u4e5f\u662f\u5728 \u90a3\u5929\u5f97\u4e86\u91cd\u75c5\u7684\uff0c\u4e5f\u8bb8\u7eaf\u5c5e\u5de7\u5408\uff0c\u4f46\u6211\u4e00\u89c1\u8fd9\u682a\u7261\u4e39\u5f00\u82b1\uff0c\u5fc3\u91cc\u603b\u662f\u96be\u514d\u6709\u70b9\u6050\u60e7\u3002\u201d \u3000\u3000\u5176\u5b9e\u90a3\u4e00\u5929\u5c82\u4ec5\u53ea\u662f\u6b7b\u4e86\u4e24\u4e2a\u82b1\u738b\uff0c\u90a3\u4e00\u5929\u8036\u5f8b\u7384\u5143\u5927\u95f9\u8282\u5ea6\u4f7f\u5e9c\uff0c\u6740\u6b7b\u4e86\u4e0d\u77e5\u591a\u5c11\u5b8c \u989c\u9274\u7684\u536b\u571f\uff0c\u5019\u6606\u4e5f\u662f\u5728\u90a3\u4e00\u5929\u7ed9\u8036\u5f8b\u7384\u5143\u6253\u4f24\u7684\u3002\u60f3\u8d77\u90a3\u5929\u6b7b\u4f24\u60e8\u70c8\u7684\u60c5\u5f62\uff0c\u4ed6\u81f3\u4eca\u72b9\u6709 \u4f59\u60b8\u3002 \u3000\u3000\u4ed6\u4e5f\u6b63\u662f\u56e0\u4e3a\u5bb3\u6015\u8036\u5f8b\u7384\u5143\u518d\u6765\uff0c\u4ed6\u624d\u4e0d\u6562\u518d\u5f53\u5b8c\u9274\u7684\u536b\u58eb\u7684\u3002 \u3000\u3000\u8001\u4ec6\u4eba\u542c\u4ed6\u8bf4\u7f62\uff0c\u4e0d\u7981\u7b11\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4e0d\u8fc7\u5de7\u5408\u800c\u5df2\uff0c\u6211\u53ef\u4e0d\u4fe1\u90aa\u3002 \u3000\u3000\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u4e03\u53d4\u3002\u4f60\u547d\u5927\u798f\u5927\u3002\u53ef\u4ee5\u4e0d\u4fe1\u90aa\u3002\u6211\u662f\u65f6\u8fd0\u4e0d\u6d4e\u4e4b\u4eba\uff0c\u9ed1\u7261\u4e39\u5f00\uff0c\u60f3\u8d77\u90a3\u5929 \u7684\u4e8b\u60f0\u3002\u5c31\u7981\u4e0d\u4f4f\u5fc3\u91cc\u5bb3\u6015\u3002\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u53ea\u9053\u4ed6\u662f\u56e0\u4e3a\u81ea\u5df1\u63ed\u7834\u4e86\u4ed6\u7684\u8eab\u4efd\u3002\u6545\u6b64\u8bf7\u8f9e\u8981\u8d70\uff0c\u4fbf\u9053\uff1a\u201c\u8001\u5019\uff0c\u4f60\u653e\u5fc3\uff0c \u4f60\u7684\u79d8\u5bc6\uff0c\u9664\u4e86\u4e3b\u4eba\u548c\u6211\u4e4b\u5916\uff0c\u5e76\u6ca1\u6709\u7b2c\u4e09\u4e2a\u4eba\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u66fe\u544a\u8bc9\u65c1\u4eba\u7684\u3002\u201d\u5019\u6606\u9053\uff1a \u201c\u4e03\u53d4\uff0c\u6211\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u4e2a\uff0c\u6211\u53ea\u662f\u5bb3\u6015\u6076\u8fd0\u4e34\u5934\uff0c\u60f3\u8981\u6682\u4e14\u907f\u5f00\u3002\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u76b1\u7709\u9053\uff1a\u201c\u8001\u5019\uff0c\u4f60\u5c31\u662f\u8981\u8d70\uff0c\u4e5f\u5f97\u66ff\u5e84\u4e3b\u62dc\u8fc7\u5927\u5bff\u4e4b\u540e\u624d\u8d70\uff0c\u518d\u8bf4\u53e5\u7b11\u8bdd\uff0c \u4f60\u77a7\uff0c\u4eca\u65e5\u6d1b\u9633\u57ce\u4e2d\u6587\u6b66\u5b98\u5458\u90fd\u6765\u4e86\u4e0d\u5c11\u3002\u5373\u4f7f\u4f60\u771f\u662f\u6d41\u5e74\u4e0d\u5229\uff0c\u4eca\u65e5\u4f1a\u6709\u6076\u8fd0\u4e34\u5934\u3002\u5728\u5b98 \u661f\u62f1\u7167\u4e4b\u4e0b\uff0c\u4eca\u65e5\u4e5f\u4f1a\u6210\u5409\u65e5\u5566\u3002\u201d \u3000\u3000\u5019\u6606\u4e00\u60f3\uff0c\u7acb\u65f6\u8d70\uff0c\u786e\u662f\u6709\u70b9\u4e0d\u8fd1\u4eba\u60c5\u3002\u53ea\u597d\u5e94\u627f\u5f85\u62dc\u8fc7\u4e86\u5bff\u624d\u8d70\u3002\u5019\u6606\u9053\uff1a\u201c\u6628\u665a\u6765 \u4e86\u4e00\u4e2a\u5ba2\u4eba\uff0c\u4e3b\u4eba\u5bf9\u4ed6\u5f88\u606d\u656c\u3002\u6211\u4eec\u90fd\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u5947\u602a\u5c31\u662f\u5947\u602a\u5728\u8fd9\u91cc\u4e86\u3002\u4e3b\u4eba\u4e0d\u8bba\u4ec0\u4e48\u4e8b\u60c5\uff0c\u7684\u786e\u662f\u4ece\u6765\u4e0d\u7792\u6211 \u7684\u3002\u53ea\u6709\u8fd9\u6b21\u4f8b\u5916\uff0c\u90a3\u5ba2\u4eba\u7531\u4e3b\u4eba\u4eb2\u81ea\u62db\u5f85\uff0c\u59d3\u751a\u540d\u8c01\uff0c\u4e3b\u4eba\u90fd\u6ca1\u5411\u6211\u900f\u9732\uff0c\u6211\u731c\u4ed6\u82e6\u4e0d\u662f \u6b66\u6797\u540d\u4eba\uff0c\u5c31\u4e00\u5b9a\u662f\u4ec0\u4e48\u5fae\u670d\u51fa\u5de1\u7684\u5927\u5b98\u3002\u8001\u5019\uff0c\u4f60\u662f\u5728\u5b8c\u989c\u5c06\u519b\u624b\u4e0b\u5f53\u8fc7\u5dee\u7684\uff0c\u5927\u5b98\u8d35\u4eba \u8bc6\u5f88\u591a\uff0c\u6b66\u6797\u4e2d\u7684\u540d\u4eba\u4f60\u4e5f\u89c1\u8fc7\u4e0d\u5c11\u3002\u6211\u60f3\u8bf7\u4f60\u53bb\u7740\u4e00\u770b\uff0c\u6216\u8bb8\u4f60\u4f1a\u77e5\u9053\u8fd9\u4e2a\u4eba\u7684\u6765\u5386\u3002\u4e3b \u4eba\u73b0\u5728\u6b63\u966a\u7740\u4ed6\u5728\u90a3\u8fb9\u8bf4\u8bdd\uff0c\u5b88\u7565\u5927\u4eba\u662f\u5ba2\u4eba\u4e2d\u5b98\u9636\u6700\u4e3b\u7684\uff0c\u4e5f\u53ea\u80fd\u5750\u5728\u4ed6\u7684\u4e0b\u9996\u5462\u3002\u201d\u5019 \u6606\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u548c\u4ed6\u8d70\u8fc7\u53bb\u5077\u770b\u90a3\u4e2a\u795e\u79d8\u5ba2\u4eba\u3002 \u3000\u3000\u4e00\u770b\u4e4b\u4e0b\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u6b64\u65f6\u620f\u53f0\u4e0a\u6b63\u5728\u5531\u4e00\u51fa\u201c\u7f57\u6210\u53eb\u5173\u201d\u7684\u6b66\u6253\u620f\uff0c\u9523\u9f13\u55a7\u5929\uff0c\u53f0\u4e0a\u7684\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u7ed9\u7f57\u9f13 \u58f0\u6df9\u6ca1\u4e86\u3002\u9664\u975e\u7279\u522b\u7559\u795e\uff0c\u5426\u5219\u5c31\u662f\u7ad9\u5728\u65c1\u8fb9\u4e5f\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u8d34\u7740\u5019\u6606\u7684\u8033\u6735\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u8ba4\u8bc6\u3002\u771f\uff0c\u771f\u7684\u662f\u4e0d\u8ba4\u8bc6\uff01\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u53d1\u73b0\u5019\u6606\u9762\u8272\u6709\u5f02\uff0c\u5fc3\u91cc\u8d8a\u53d1\u6000\u7591\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e0d\u8ba4\u8bc6\uff0c\u662f\u4e0d\u80af\u544a\u8bc9\u6211 \u5427\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u6709\u53e6\u4e00\u4e2a\u4ec6\u4eba\u8d70\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e03\u53d4\uff0c\u4e3b\u4eba\u8bf7\u4f60\u8fc7\u53bb\u3002 \u3000\u3000\u201d \u3000\u3000\u8001\u4ec6\u4eba\u4e00\u8d70\uff0c\u4faf\u6606\u7acb\u5373\u6e9c\u51fa\u4eba\u5806\u3002 \u3000\u3000\u5f52\u5143\u9f99\u6b63\u5728\u966a\u90a3\u5ba2\u4eba\u8c08\u5929\uff0c\u89c1\u4ec6\u4eba\u6765\u5230\uff0c\u4fbf\u5373\u95ee\u9053\uff1a\u201c\u5c11\u7237\u56de\u6765\u6ca1\u6709\uff1f\u201d\u5f52\u5143\u9f99\u53ea\u6709\u4e00 \u4e2a\u513f\u5b50\uff0c\u540d\u5524\u6d1b\u82f1\u3002\u5ba2\u4eba\u53ef\u80fd\u662f\u4e3a\u4e86\u793c\u8c8c\uff0c\u6b63\u5411\u4e3b\u4eba\u95ee\u8d77\u4ed6\u7684\u513f\u5b50\u3002 \u3000\u3000\u8001\u4ec6\u4eba\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5c11\u7237\uff0c\u4ed6\u3001\u4ed6\u8fd8\u6ca1\u56de\u6765\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u76b1\u7709\u9053\uff1a\u201c\u771f\u662f\u8352\u5510\uff0c\u4f60\u7ed9\u6211\u627e\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u8001\u4ec6\u5e94\u4e86\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u8d76\u5fd9\u9000\u4e0b\u3002\u4ed6\u867d\u7136\u5949\u547d\u552f\u8c28\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u6697\u53eb\u82e6\uff0c\u6697\u9a82\u5c11\u7237\u8352 \u5510\u3002 \u3000\u3000\u539f\u6765\u5f52\u6d1b\u82f1\u4e00\u65e9\u7531\u4e24\u4e2a\u95e8\u5ba2\u966a\u540c\uff0c\u5230\u6d1b\u9633\u57ce\u4e2d\u53bb\u4e70\u4ed6\u5b9a\u5236\u7684\u70df\u82b1\u53bb\u4e86\u3002\u6d1b\u9633\u57ce\u548c\u5f52\u4e91\u5e84 \u4e0d\u8fc7\u5341\u91cc\u8def\u7a0b\uff0c\u4ed6\u662f\u9a91\u9a6c\u53bb\u7684\uff0c\u6309\u8bf4\u65e9\u5c31\u5e94\u8be5\u6b63\u5348\u4e4b\u524d\u56de\u6765\u7684\uff0c\u4f46\u73b0\u5728\u65e5\u659c\u504f\u897f\uff0c\u4ed6\u7adf\u7136\u8fd8 \u672a\u56de\u6765\u3002 \u3000\u3000\u201c\u5509\uff0c\u5c11\u7237\u4e5f\u771f\u662f\u8352\u5510\uff0c\u6562\u60c5\u662f\u5728\u54ea\u5bb6\u79e6\u697c\u695a\u9986\u5477\u5993\u3001\u8d4c\u94b1\u3001\u73a9\u660f\u4e86\u5934\uff0c\u8fde\u56de\u6765\u7ed9\u7236\u4eb2 \u62dc\u5bff\u90fd\u5fd8\u8bb0\u4e86\u3002\u5374\u53eb\u6211\u5230\u5904\u627e\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u6b63\u5728\u5600\u5495\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u5c11\u7237\u56de\u6765\u4e86\uff01\u201d\u5c11\u7237\u56de\u6765\u4e86\u6709\u4ec0\u4e48\u4e0d\u597d\u5462\uff1f\u8001 \u4ec6\u4eba\u89c9\u5f97\u5947\u602a\uff0c\u5b9a\u775b\u77a7\u65f6\uff0c\u4ed6\u4e5f\u4e0d\u7981\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u679c\u7136\u771f\u662f\u201c\u4e0d\u597d\u201d\uff0c\u539f\u6765\u4ed6\u7684\u5c11\u7237\u662f\u7ed9\u653e\u5728\u7ef3\u5e8a\u4e0a\uff0c\u7531\u90a3\u4e24\u4e2a\u95e8\u5ba2\u62ac\u56de\u6765\u7684\u3002 \u3000\u3000\u201c\u9ed1\u7261\u4e39\u5f00\uff0c\u4e0d\u7965\u4e4b\u5146\uff0c\u60f3\u4e0d\u5230\u8001\u4faf\u8bf4\u7684\u679c\u7136\u5e94\u9a8c\uff01\u66f4\u60f3\u4e0d\u5230\u7684\u662f\u5e76\u975e\u5e94\u9a8c\u5728\u4ed6\u8eab\u4e0a\uff0c \u662f\u5e94\u5728\u5c0f\u7237\u8eab\u4e0a\uff01\u554a\u5440\uff0c\u8001\u4faf\u5462\uff1f\u4ed6\u53c8\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u201d\u5f52\u5143\u9f99\u770b\u89c1\u513f\u5b50\u7ed9\u6253\u5f97\u91cd\u4f24\u56de\u6765\uff0c\u52c3\u7136\u5927\u6012\uff0c\u559d\u95ee\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u95e8\u5ba2\u9762\u9762\u76f8\u89d1\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5c11\u5e84\u4e3b\u662f\u7ed9\u4eba\u6253\u4f24\u7684\u3002\u60f3\u6211\u4eec\u65e0\u80fd\uff0c\u4fdd\u62a4\u4e0d\u4e86\u5c11\u5e84 \u4e3b\u3002\u4f46\u597d\u5728\u5c11\u5728\u4e3b\u4e0d\u662f\u4f24\u7740\u8981\u5bb3\uff0c\u6211\u4eec\u5df2\u7ecf\u7ed9\u4ed6\u6577\u4e0a\u91d1\u521b\u836f\u4e86\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u6c14\u5f97\u987f\u8db3\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u80e1\u6d82\uff0c\u4ed6\u5f53\u7136\u662f\u53d7\u4e86\u4f24\u624d\u8981\u4f60\u4eec\u62ac\u56de\u6765\uff0c\u4f55\u987b\u591a\u8bf4\uff1f \u6211\u8981\u77e5\u9053\u662f\u8c01\u5c06\u4ed6\u6253\u5f97\u4f24\u6210\u8fd9\u6837\uff1f\u201d \u3000\u3000\u5f52\u6d1b\u82f1\u5ffd\u5730\u53d1\u51fa\u547b\u541f\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d \u3000\u3000\u5f52\u5143\u9f99\u89c1\u513f\u5b50\u80fd\u591f\u8bf4\u8bdd\uff0c\u7a0d\u7a0d\u653e\u5fc3\uff0c\u628a\u8033\u6735\u8d34\u5230\u513f\u5b50\u7684\u5634\u8fb9\u53bb\u542c\uff0c\u53ea\u542c\u5f97\u5f52\u6d1b\u82f1\u65ad\u65ad\u7eed \u7eed\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u5173\u4ed6\u4eec\u4e24\u4eba\u7684\u4e8b\uff0c\u90fd\u662f\u5b69\u513f\u5b66\u827a\u4e0d\u7cbe\uff0c\u4ee5\u81f3\u6709\u8fb1\u5bb6\u95e8\u3002\u5f85\u5b69\u513f\u597d\u4e86\uff0c\u6162\u6162\u7980 \u544a\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u53ea\u9053\u513f\u5b50\u8981\u8bf4\u4e86\u4ec7\u4eba\u540d\u5b57\u7684\uff0c\u4e0d\u6599\u4ed6\u975e\u4f46\u4e0d\u8bf4\u4ec7\u4eba\u7684\u540d\u5b57\uff0c\u8fde\u8fd9\u4ef6\u4e8b\u7a76\u7adf\u662f\u600e\u6837 \u53d1\u751f\u7684\u4e5f\u53ea\u5b57\u4e0d\u63d0\u3002\u8fd9\u6837\u7684\u56de\u7b54\uff0c\u5b9e\u662f\u4ed6\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\u3002\u800c\u8fd9\u6837\u7684\u56de\u7b54\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u4ee4\u89e3\u4ed6\u6ee1 \u8179\u7684\u7591\u56e2\u4e86\u3002 \u3000\u3000\u77e5\u5b50\u83ab\u82e5\u7236\u3002\u5f52\u6d1b\u82f1\u5e73\u65e5\u501a\u4ed7\u7236\u4eb2\u7684\u540d\u5934\uff0c\u6a2a\u884c\u9738\u9053\uff0c\u65c1\u4eba\u7eb5\u7136\u4e0d\u6562\u544a\u8bc9\u4ed6\uff0c\u4ed6\u4e5f\u662f\u6709 \u6240\u77e5\u95fb\u7684\u3002\u513f\u5b50\u7684\u6027\u683c\uff0c\u4ed6\u662f\u5e94\u8be5\u4e00\u56de\u6765\u5c31\u5411\u7236\u4eb2\u54ed\u62c6\uff0c\u6c42\u7236\u4eb2\u66ff\u4ed6\u62a5\u4ec7\u7684\u3002\u201c\u96be\u9053\u662f\u4ed6\u505a \u9519\u4e86\u4e8b\uff0c\u81ea\u77e5\u7406\u4e8f\uff1f\u201d\u5f52\u5143\u9f99\u5fc3\u60f3\u3002 \u3000\u3000\u4f46\u518d\u4e00\u60f3\u3002\u5374\u4ece\u672a\u6709\u8fc7\uff0c\u6216\u8005\u66f4\u786e\u5207\u5730\u8bf4\uff0c\u5373\u4f7f\u4ed6\u201c\u81ea\u77e5\u7406\u4e8f\u201d\uff0c\u4e5f\u4ece\u4e0d\u4f1a\u5bf9\u4eba\u627f\u8ba4\uff0c \u5305\u62ec\u4ed6\u7684\u7236\u4eb2\u5728\u5185\u3002\u751a\u81f3\u4ed6\u6709\u7684\u505a\u9519\u4e86\u4e8b\uff0c\u7236\u4eb2\u67e5\u95ee\u8d77\u6765\uff0c\u4ed6\u8fd8\u8981\u628a\u66f2\u7684\u8bf4\u6210\u76f4\u7684\u5462\u3002 \u3000\u3000\u800c\u4e14\u201c\u5b66\u827a\u4e0d\u7cbe\u201d\u8fd9\u56db\u4e2a\u5b57\uff0c\u5728\u4ed6\u542c\u6765\uff0c\u4e5f\u89c9\u5f97\u9887\u4e3a\u523a\u8033\u3002 \u3000\u3000\u5f52\u6d1b\u82f1\u8d4b\u6027\u867d\u7136\u4f7b\u631e\uff0c\u5b66\u6b66\u5012\u662f\u9887\u4e3a\u6709\u70b9\u806a\u660e\u7684\u3002\u4eca\u5e74\u867d\u7136\u53ea\u4e8c\u5341\u5c81\uff0c\u5374\u5b9e\u5b9e\u5728\u5728\uff0c\u5df2 \u7ecf\u8bf4\u5f97\u662f\u5f97\u4e86\u7236\u4eb2\u7684\u8863\u94b5\u771f\u4f20\u3002\u9664\u4e86\u529f\u529b\u4e0d\u6709\u53ca\u4e24\u4f4d\u5e08\u5144\u4e4b\u5916\u4e00\u4e00\u4e00\u4ed6\u7684\u529f\u592b\u653e\u5728\u6c5f\u6e56\u4e0a\u4e5f \u7b97\u662f\u7b2c\u4e00\u6d41\u7684\u4e86\u3002 \u3000\u3000\u83ab\u8bf4\u505a\u513f\u5b50\u7684\u4e0d\u4f1a\u8fd9\u6837\u8c26\u865a\uff0c\u505a\u7236\u4eb2\u7684\u5373\u4f7f\u53e3\u5934\u4e0a\u4f1a\u4e3a\u513f\u5b50\u8c26\u865a\u4e00\u756a\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u4f1a\u627f\u8ba4 \u513f\u5b50\u662f\u201c\u5b66\u827a\u4e0d\u7cbe\u201d\u7684\u3002 \u3000\u3000\u4ed6\u7591\u795e\u7591\u9b3c\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u9047\u4e0a\u4e86\u54ea\u4e2a\u5927\u5b98\u7684\u5b50\u5f1f\uff0c\u5f7c\u6b64\u4e0d\u77e5\u5bf9\u65b9\u6765\u8def\uff0c\u6545\u800c\u6709\u8fd9 \u4e00\u573a\u8bef\u6253\u3001\u8bef\u4f24\uff1f\u53c8\u6216\u624d\u90a3\u4eba\u662f\u4ece\u5916\u5730\u6765\u7684\uff0c\u5c31\u50cf\u8fd9\u4f4d\u54c8\u5927\u4eba\u7559\u5728\u57ce\u4e2d\u7684\u968f\u4ece\uff1f\u201d \u3000\u3000\u5750\u5728\u5f52\u65e0\u9f99\u8eab\u8fb9\u90a3\u4e2a\u795e\u79d8\u5ba2\u4eba\uff0c\u5bf9\u5f52\u6d1b\u82f1\u7684\u53d7\u4f24\u4f3c\u4e4e\u4e5f\u5f88\u91cd\u89c6\uff0c\u4ed6\u4ed4\u7ec6\u770b\u4e86\u5f52\u6d1b\u82f1\u7684\u4f24 \u52bf\u3002\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u53ea\u662f\u5355\u8eab\u4e00\u4e2a\u4eba\u6765\u5230\u6d1b\u9633\uff0c\u60f3\u4e0d\u5230\u5c31\u78b0\u8fd9\u6837\u5947\u602a\u7684\u4e8b\u3002\u201d\u4ed6\u8bf4\u7684\u8fd9 \u53e5\u8bdd\u6beb\u65e0\u8fde\u5e26\u5173\u7cfb\uff0c\u65c1\u4eba\u90fd\u542c\u4e0d\u61c2\uff0c\u5355\u8eab\u4e00\u4eba\u548c\u201c\u8fd9\u6837\u5947\u602a\u7684\u4e8b\u201d\u6709\u4f55\u76f8\u5173\u3002 \u3000\u3000\u53ea\u6709\u5f52\u5143\u9f99\u542c\u61c2\u4e00\u534a\u3002\u4ed6\u8bf4\u53ea\u662f\u4ed6\u5355\u8eab\u4e00\u4eba\u6765\u5230\u6d1b\u9633\uff0c\u5373\u662f\u8868\u660e\uff0c\u6253\u4f24\u5f52\u6d1b\u82f1\u7684\u4eba\u4e0d\u662f \u4ed6\u7684\u968f\u4ece\uff0c\u4e5f\u4e0d\u53ef\u80fd\u662f\u53e6\u4e00\u4e2a\u8eab\u4efd\u548c\u4ed6\u76f8\u82e5\u7684\u4eba\u3002 \u3000\u3000\u4f46\u201c\u8fd9\u6837\u5947\u602a\u7684\u4e8b\u201d\u53c8\u662f\u6307\u7684\u4ec0\u4e48\u5462\uff1f\u4e0d\u9519\uff0c\u5a01\u9707\u4e00\u65b9\u7684\u6b66\u6797\u5927\u5bb6\u7684\u513f\u5b50\u7ed9\u4eba\u6253\u5f97\u91cd \u4f24\uff0c\u7684\u786e\u662f\u53ef\u4ee5\u7b97\u5f97\u201c\u602a\u4e8b\u201d\u7684\u4e86\uff0c\u4f46\u8fd9\u53e5\u8bdd\u662f\u4ece\u8fd9\u4f4d\u795e\u79d8\u5ba2\u4eba\u53e3\u4e2d\u8bf4\u51fa\u6765\u7684\uff0c\u5f52\u5143\u9f99\u5fc3\u91cc \u660e\u767d\uff0c\u90a3\u5c31\u6050\u6015\u4e0d\u4ec5\u662f\u6307\u4e8b\u4ef6\u672c\u8eab\u8fd9\u6837\u7b80\u5355\u7684\u4e86\u3002 \u3000\u3000\u5f52\u5143\u9f99\u662f\u77e5\u9053\u8fd9\u4e2a\u5ba2\u4eba\u7684\u8eab\u4efd\u7684\uff0c\u4e0d\u6562\u591a\u95ee\u3002\u4f46\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u5374\u662f\u4e0d\u77e5\u6b64\u4eba\u8eab\u4efd\u7684\uff0c\u4e8c\u5f92 \u5f1f\u9b4f\u8fde\u9b41\u4e0d\u4f4f\u7acb\u5373\u95ee\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5ba2\u4eba\u987f\u4e86\u4e00\u987f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6253\u4f24\u4ee4\u5e08\u5f1f\u8fd9\u4eba\u7684\u6b66\u529f\u5947\u602a\u5f97\u5f88\u3002\u4f3c\u4e4e\u4e0d\u5c5e\u4e8e\u4e2d\u539f\u4efb\u4f55 \u4e00\u4e2a\u95e8\u6d3e\u3002\u201d \u3000\u3000\u9b4f\u8fde\u9b41\u5b98\u5c45\u53c2\u5c06\uff0c\u662f\u4e2a\u6027\u60c5\u9c81\u83bd\u7684\u6b66\u592b\uff0c\u4f5c\u5a01\u4f5c\u798f\u53c8\u662f\u60ef\u4e86\u7684\u4e86\uff0c\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u4e0d\u7ba1 \u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u6562\u6253\u4f24\u6211\u7684\u5e08\u5f1f\uff0c\u5e08\u7236\u4f60\u4e0d\u8ffd\u7a76\uff0c\u6211\u4e5f\u8981\u8ffd\u7a76\uff01\u201d \u3000\u3000\u9b4f\u8fde\u9b41\u5927\u53d1\u813e\u6c14\u8ffd\u95ee\u90a3\u4e24\u4e2a\u95e8\u5ba2\uff1a\u201c\u4f60\u4eec\u662f\u5728\u573a\u7684\u4eba\uff0c\u4fdd\u62a4\u516c\u5b50\u4e0d\u5468\u4e4b\u7f6a\u6211\u59d1\u4e14\u4e0d\u602a\u9a82 \u4f60\u4eec\uff0c\u90a3\u4e2a\u5927\u80c6\u6253\u4f24\u516c\u5b50\u7684\u4eba\u662f\u8c01\uff1f\u5feb\u8bb2\uff01\u201d\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u201c\u6253\u4f24\u4ed6\u7684\u90a3\u4e2a\u4eba\u662f\u6211\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u5973\u5b50\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u7684\uff0c\u7a81\u7136\u5c31\u51fa\u73b0\u5728\u4ed6\u4eec\u9762\u524d\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u7a7f\u540d\u8d35\u7684\u9ed1\u7eb1\u7f51\uff0c\u8170\u675f\u7ea2\u7eeb\uff0c\u53d1\u7efe\u91d1\u9497\uff0c\u8c08\u626b\u80ed\u8102\uff0c\u7709\u957f\u5165\u9b13\u3002\u6e05\u4e3d\u4e4b\u4e2d\u53c8\u5e26\u7740 \u51e0\u5206\u201c\u9a84\u7eb5\u91ce\u6027\u201d\u7684\u5473\u9053\u3002 \u3000\u3000\u8fd9\u6837\u7684\u6253\u626e\uff0c\u8fd9\u6837\u7684\u201c\u6c14\u8d28\u201d\uff0c\u8bf4\u5979\u662f\u5927\u5bb6\u95f0\u79c0\u5f53\u7136\u4e0d\u50cf\uff0c\u4f46\u4e5f\u4e0d\u50cf\u5bfb\u5e38\u7684\u98ce\u5c18\u5973\u5b50\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u4e00\u51fa\u73b0\uff0c\u5c31\u6709\u4e00\u4e2a\u95e8\u5ba2\u5bf9\u5f52\u5143\u9f99\u6084\u6084\u7980\u544a\uff1a\u201c\u8fd9\u96cc\u513f\u662f\u524d\u51e0\u5929\u6765\u7684\uff0c\u5728\u57ce\u4e2d\u5356\u89e3 \u7684\u5973\u5b50\u3002\u4e0d\u77e5\u9053\u5979\u7684\u59d3\u540d\u6765\u5386\u3002\u4eba\u5bb6\u90fd\u53eb\u5979\u505a\u9ed1\u7261\u4e39\u3002 \u3000\u3000\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u5a04\u963f\u9f20\u6697\u6697\u5600\u5495\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u9ed1\u7261\u4e39\u5f00\u3002\u4e0d\u7965\u4e4b\u5146\u3002\u8001\u5019\u8bf4\u7684\u679c\u7136\u4e0d\u9519\u3002\u201d \u5f52\u5e9c\u7684\u4ec6\u4eba\u5c5e\u4ed6\u5730\u4f4d\u6700\u9ad8\uff0c\u6709\u4e9b\u4ec6\u4eba\u60f3\u4e0a\u53bb\u52a8\u624b\uff0c\u89c1\u4ed6\u6fc0\u52a8\uff0c\u4e5f\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u4e86\uff0c\u5a04\u963f\u9f20\u5bf9 \u4ed6\u4eec\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u591a\u7559\u5bbf\u7684\u4eba\u5728\u8fd9\u91cc\uff0c\u7528\u5f97\u7740\u54b1\u4eec\u52a8\u624b\u5417\uff1f\u4e3b\u4eba\u4e5f\u81ea\u6709\u4e3b\u5f20\uff0c\u54b1\u4eec\u82e5\u662f\u4e00\u95ef \u800c\u4e0a\uff0c\u53cd\u800c\u574f\u4e86\u4e3b\u4eba\u7684\u540d\u5934\u4e86\u3002\u201d \u3000\u3000\u9b4f\u8fde\u9b41\u6b63\u5728\u6c14\u5934\uff0c\u89c1\u8bc6\u53cd\u800c\u4e0d\u53ca\u8fd9\u4e2a\u8001\u4ec6\u3002\u4ed6\u4e5f\u4e0d\u60f3\u8fd9\u4e2a\u5973\u5b50\u65e2\u7136\u80fd\u591f\u6253\u4f24\u5f52\u6d1b\u82f1\uff0c\u5f53 \u7136\u662f\u201c\u5584\u8005\u4e0d\u6765\uff0c\u6765\u8005\u4e0d\u5584\u3002\u201d\u4ed6\u4ed7\u7740\u5b98\u5a01\uff0c\u6c14\u547c\u547c\u7684\u559d\u9053\uff1a\u201c\u4e00\u4e2a\u5356\u89e3\u5973\u5b50\uff0c\u80c6\u6562\u5982\u6b64\u7316 \u72c2\uff0c\u7ed9\u6211\u62ff\u4e0b\uff01\u201d\u4ed6\u53e3\u91cc\u559d\u4eba\u7ed9\u4ed6\u62ff\u4e0b\uff0c\u81ea\u5df1\u5374\u5df2\u5148\u5fcd\u4e0d\u4f4f\u53bb\u6293\u90a3\u5973\u5b50\u4e86\u3002 \u3000\u3000\u90a3\u5973\u5b50\u6325\u62bd\u4e00\u62c2\uff0c\u8bf4\u9053\uff1a\u201c\u5b98\u8001\u7237\u8981\u6293\u6211\u53bb\u5ba1\u95ee\u4e48\uff1f\u201d \u3000\u3000\u5979\u53ea\u662f\u6325\u8896\u8f7b\u8f7b\u4e00\u62c2\uff0c\u9b4f\u8fde\u9b41\u5df2\u662f\u7981\u4e0d\u4f4f\u8ddf\u8ddf\u8dc4\u8dc4\u9000\u51fa\u4e86\u516d\u4e03\u6b65\uff0c\u51e0\u81ea\u4e0d\u80fd\u7a33\u4f4f\u8eab\u5f62\uff0c \u8981\u65c1\u4eba\u6276\u4ed6\uff0c\u65b9\u80fd\u56de\u5230\u539f\u6765\u5ea7\u4f4d\u3002 \u3000\u3000\u5f52\u5143\u9f99\u54fc\u4e86\u58f0\uff0c\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u5996\u5973\u7528\u7684\u4f3c\u4e4e\u662f\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u4e0a\u4e58\u5185\u529f\uff0c \u602a\u4e0d\u5f97\u82f1\u513f\u4f1a\u7ed9\u5979\u5f97\u91cd\u4f24\u3002\u201d \u3000\u3000\u9b4f\u8fde\u9b41\u4e00\u5c41\u80a1\u5750\u4e0b\u6765\uff0c\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u90a3\u5973\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u662f\u5bb3\u6015\u4f60 \u4eec\u7fa4\u6bb4\uff0c\u6211\u4e5f\u4e0d\u4f1a\u6765\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u542c\u5f97\u5f52\u5e84\u4e3b\u7d20\u6765\u4ee5\u4ec1\u4e49\u81ea\u547d\uff0c\u6211\u5012\u4e0d\u6562\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50 \u4e4b\u8179\u3002\u201d\u5f52\u5143\u9f99\u9053\uff1a\u201c\u4f60\u6253\u4f24\u4e86\u6211\u7684\u513f\u5b50\uff0c\u8fd8\u60f3\u6211\u4ee5\u4e0a\u5bbe\u4e4b\u793c\u5f85\u4f60\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u6253\u4f24\u4e86\u4ee4\u90ce\u3002\u8bf7\u95ee\u5728\u4e3b\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4f60\u662f\u60f3\u8ba9\u5b98\u5e9c\u4e86\u65ad\u5462\uff0c\u8fd8\u662f \u6309\u7167\u6c5f\u6e56\u89c4\u77e9\u529e\u4e8b\uff1f\u201d\u5f52\u5143\u9f99\u9053\uff1a\u201c\u8ba9\u5b98\u5e9c\u4e86\u65ad\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u90a3\u5f88\u7b80\u5355\uff0c\u4f60\u6709\u4e00\u5343\u51e0\u767e\u5bb6\u4e01\u95e8\u5ba2\uff0c\u5728\u5ea7\u7684\u8fd8\u6709\u8bb8\u591a\u5927\u5927\u5c0f\u5c0f\u7684\u5b98\u513f\uff0c\u4f60\u53ef \u4ee5\u53eb\u5bb6\u4e01\u95e8\u5ba2\u4e00\u62e5\u800c\u4e0a\uff0c\u5c06\u6211\u9001\u5b98\u7a76\u529e\u3002\u5bb6\u4e01\u95e8\u5ba2\u62ff\u4e0d\u4e86\u6211\uff0c\u8fd8\u53ef\u4ee5\u52a8\u7528\u5b98\u5175\u3002\u53cd\u6b63\u4f60\u8fd9\u4f4d \u5b98\u5c45\u53c2\u5c06\u7684\u9ad8\u8db3\u5df2\u7ecf\u52a0\u7ed9\u6211\u4e00\u9876\u9020\u53cd\u7684\u5e3d\u5b50\u4e86\uff0c\u9020\u53cd\u53cd\u7f6a\u540d\u4e0d\u8f7b\uff0c\u52a8\u7528\u5b98\u5175\u4e5f\u4e0d\u7b97\u5c0f\u9898\u5927 \u4f5c\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u662f\u6b66\u6797\u9886\u8896\u81ea\u5c45\u7684\u4eba\u7269\u3002\u6cbd\u540d\u9493\u8a89\u7684\u4e8b\u60c5\u7684\u786e\u505a\u4e86\u4e0d\u5c11\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u4ed6\u6709\u8d22\u6709 \u52bf\uff0c\u5728\u5b98\u573a\u4e2d\u53c8\u662f\u4ee5\u5927\u7ec5\u58eb\u7684\u8eab\u4efd\u51fa\u73b0\u7684\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u5018\u82e5\u5f53\u771f\u6309\u7167\u5c11\u5973\u6240\u8bf4\u7684\u529e\u6cd5\uff0c\u501f\u52a9\u5b98\u5e9c\u4e4b\u52bf\u9677\u5bb3\u5979\u7684\u8bdd\uff0c\u4ed6\u5728\u6b66\u6797\u8fd8\u5982 \u4f55\u80fd\u591f\u7acb\u8db3\uff1f\u5728\u5b98\u573a\u4e0a\u4e5f\u5c06\u5931\u5c3d\u4f53\u9762\u3002 \u3000\u3000\u5f52\u5143\u9f99\u677f\u7740\u8138\u5b54\u9053\uff1a\u201c\u4f60\u4e5f\u628a\u5f52\u67d0\u770b\u5f97\u5fd2\u5c0f\u3002\u522b\u4eba\u627e\u4e0a\u95e8\u6765\uff0c\u5f52\u67d0\u5e94\u4ed8\u4e0d\u4e86\uff0c\u53ea\u597d\u4ece \u683d\uff0c\u8fd8\u7528\u5f97\u8005\u60ca\u5b98\u52a8\u5e9c\u5417\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u662f\u8981\u4f60\u8fd9\u4e00\u53e5\u8bdd\u3002\u90a3\u4e48\uff0c\u4f60\u662f\u613f\u610f\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\u529e\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u5f52\u5143\u9f99\u9053\uff1a\u201c\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\u529e\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u5c31\u5f97\u6c42\u4e2a\u516c\u9053\u3001\u8c01\u7684\u7406\u4e8f\uff0c\u5c31\u5f97\u5411\u5bf9\u65b9\u78d5\u5934\u8d54\u7f6a\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u9053\uff1a\u201c\u9053\u7406\u6709\u65f6\u4e5f\u4e0d\u662f\u5bb9\u6613\u8fa8\u7684\uff0c\u5404\u6267\u4e00\u8f9e\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6c5f\u6e56\u89c4\u77e9\uff0c\u4f60\u5e94\u8be5\u6bd4\u6211\u66f4\u52a0\u6e05\u695a\uff0c\u79c1\u4ec7\u79c1\u4e86\uff0c\u5355\u6253\u72ec\u6597\uff0c\u62f3\u5934\u4e0a\u5206\u51fa\u9053\u7406 \u6765\uff01\u201d \u3000\u3000\u5f52\u5143\u9f99\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u6211\u73b0\u5728\u867d\u7136\u606f\u9690\u7530\u56ed\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u8fd8\u53eb\u5f97\u54cd\u5b57\u53f7\u3002\u54b1 \u4eec\u5c31\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\u529e\u4e8b\u597d\u4e86\u3002\u4f60\u8bf4\u5427\uff0c\u6211\u513f\u4e0e\u4f60\u4f55\u51a4\u4f55\u4ec7\uff0c\u4f60\u5c06\u4ed6\u4f24\u6210\u8fd9\u6837\uff1f\u4ed6\u5df2\u6253\u5b9a\u4e3b \u610f\uff0c\u4e0d\u7ba1\u513f\u5b50\u662f\u5426\u7406\u4e8f\uff0c\u4ed6\u90fd\u8981\u4f7f\u8fd9\u5973\u5b50\u6709\u7406\u53d8\u6210\u65e0\u7406\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4ee4\u90ce\u884c\u4e3a\u751a\u662f\u4e0d\u7aef\uff01\u201d \u3000\u3000\u521a\u8bf4\u5f97\u4e00\u53e5\uff0c\u5f52\u5143\u9f99\u7acb\u65e2\u677f\u8d77\u9762\u5b54\u5207\u65ad\u5979\u7684\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u513f\u7ed9\u4f60\u6253\u6210\u91cd\u4f24\uff0c\u4f60\u600e\u4e48 \u7f16\u6d3e\u4ed6\u7684\u4e0d\u662f\uff0c\u4ed6\u90fd\u4e0d\u80fd\u548c\u4f60\u5206\u8fa9\u3002\u5404\u4f4d\u6e05\u8bc4\u8bc4\u7406\uff0c\u5355\u51ed\u7247\u9762\u4e4b\u8f9e\uff0c\u662f\u5426\u53ef\u5b9a\u4eba\u4ee5\u7f6a\u3002\u201d \u3000\u3000\u4e00\u4e2a\u8859\u95e8\u529e\u65b9\u6848\u7684\u5e08\u7237\u4f3c\u7b11\u975e\u7b11\u7684\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u3002\u4f60\u8bf4\u5f52\u516c\u5b50\u884c\u4e3a\u4e0d\u7aef\uff0c\u5927\u6982\u662f\u6307\u4ed6\u66fe \u7ecf\u8c03\u620f\u4f60\u5427\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5e08\u7237\u9053\uff1a\u201c\u4f60\u662f\u5728\u6d1b\u9633\u57ce\u91cc\u516c\u4f17\u7684\u5730\u65b9\u5356\u89e3\u7684\uff0c\u8fd9\u6837\u4e8b\u60c5\u662f\u5728\u5356\u89e3\u7684\u573a\u6240\u53d1\u751f\u7684\u5427\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u518d\u9053\uff1a\u201c\u4e0d\u9519\u201d \u3000\u3000\u5e08\u7237\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\u3002\u5e94\u8be5\u6709\u8bb8\u591a\u4eba\u770b\u89c1\u7684\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u518d\u9053\uff1a\u201c\u4e0d\u9519\u201d \u3000\u3000\u5e08\u7237\u9053\uff1a\u201c\u90a3\u4f60\u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u8bc1\u4eba\u4e86\uff1f\u201d\u773c\u775b\u772f\u6210\u4e00\u6761\u7f1d\uff0c\u624b\u91cc\u8f7b\u8f7b\u6447\u7740\u9e45\u6bdb\u6247\uff0c\u597d\u50cf \u662f\u5728\u5632\u7b11\u90a3\u5973\u5b50\uff1a\u8fd9\u4e00\u6b21\u770b\u4f60\u8fd8\u80fd\u8bf4\u201c\u4e0d\u9519\u201d\u5417\uff1f\u5f52\u5143\u9f99\u5fc3\u82b1\u6012\u653e\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5e08\u7237\u5012 \u662f\u77e5\u60c5\u8bc6\u8da3\uff0c\u5e2e\u6211\u7684\u5fd9\uff0c\u5e2e\u5f97\u6070\u5230\u597d\u5904\u3002\u4e8b\u60c5\u8fc7\u540e\uff0c\u6211\u5f97\u5404\u4e00\u4efd\u539a\u793c\u8c22\u4ed6\u624d\u662f\u3002\u201d\u5fc3\u91cc\u672a \u5df2\uff0c\u53ea\u542c\u5f97\u90a3\u5973\u5b50\u5df2\u518d\u7b2c\u56db\u6b21\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8fd9\u4e00\u56de\u7b54\uff0c\u4e0d\u4ec5\u662f\u90a3\u5e08\u7237\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u6240\u6709\u7684\u4eba\uff0c\u8c01\u90fd\u610f\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u8981\u77e5\u5f52\u5143\u9f99\u4e43\u662f\u6d1b\u9633\u4e00\u9738\uff0c\u57ce\u91cc\u57ce\u5916\uff0c\u8c01\u4e0d\u77e5\u9053\u4ed6\u7684\u5389\u5bb3\uff0c\u5728\u8857\u5934\u770b\u5356\u89e3\u7684\u66f4\u5927\u90fd\u662f\u5bfb \u5e38\u7684\u5c0f\u6c11\uff0c\u53c8\u6709\u54ea\u4e2a\u6562\u505a\u8fd9\u4e2a\u5973\u5b50\u7684\u8bc1\u4eba\u3001\u660e\u76ee\u5f20\u80c6\u4e0e\u5f52\u4e91\u5e84\u7684\u5e84\u4e3b\u4f5c\u5bf9\uff1f\u5f52\u5143\u9f99\u559d\u9053\uff1a \u201c\u8bc1\u4eba\u5728\u4f55\u5904\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u5c31\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u4f60\u8fd9\u4e2a\u95e8\u5ba2\u662f\u5728\u573a\u76ee\u51fb\u7684\u4eba\uff01\u201d \u3000\u3000\u8fd9\u4e0b\u66f4\u662f\u201c\u5947\u5cf0\u7a81\u8d77\u201d\uff0c\u6709\u4eba\u6697\u66ff\u90a3\u5973\u5b50\u62c5\u5fe7\uff1a\u201c\u4e00\u4e2a\u8dd1\u6c5f\u6e56\u7684\u5973\u5b50\u600e\u7684\u7adf\u4e5f\u5982\u6b64\u4e0d\u901a \u4e16\u52a1\uff0c\u5f52\u5143\u5e84\u4e3b\u662f\u8fd9\u4e24\u4e2a\u95e8\u5ba2\u7684\u8863\u98df\u7236\u6bcd\uff0c\u5c0f\u5e84\u4e3b\u8fd8\u662f\u4ed6\u62ac\u56de\u6765\u7684\u3002\u4ed6\u8fd8\u80fd\u591f\u5e2e\u4f60\u8bf4\u8bdd\u5417\uff1f \u4e0d\u780c\u8f9e\u8bec\u8511\u4f60\u5df2\u662f\u597d\u4e86\u3002\u201d\u5f52\u5143\u9f99\u88c5\u6a21\u4f5c\u6837\u7684\u53eb\u90a3\u4e24\u4e2a\u95e8\u5ba2\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u8981\u4f60\u4eec \u4f5c\u8bc1\u4eba\uff0c\u4f60\u4eec\u5b9e\u8bdd\u5b9e\u8bf4\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u95e8\u5ba2\uff0c\u4e00\u4e2a\u59d3\u5f20\uff0c\u4e00\u4e2a\u59d3\u674e\u3002\u4e0d\u7ea6\u800c\u540c\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u77e5\uff0c\u4e0d\u77e5\u8be5\u600e\u4e48\u6837 \u8bf4\u624d\u662f\u3002\u201d \u3000\u3000\u5f52\u4e91\u9f99\u4e00\u76b1\u7709\u5934\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u77e5\uff0c\u6211\u53eb\u4f60\u4eec\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u4f60\u4eec\u5c31\u7167\u76f4\u8bf4\u597d \u4e86\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4eba\u600e\u7684\u5982\u6b64\u7cca\u6d82\uff0c\u96be\u9053\u8fde\u6211\u7684\u610f\u601d\u90fd\u542c\u4e0d\u61c2\u3002\u201d\u4ed6\u628a\u5b9e\u8bdd\u5b9e\u8bf4\u8fd9\u56db\u4e2a\u5b57 \u91cd\u590d\u4e00\u904d\uff0c\u800c\u4e14\u5728\u8bf4\u5230\u201c\u6211\u53eb\u4eec\u4f60\u201d\u8fd9\u56db\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u58f0\u97f3\u7684\u53c8\u7279\u522b\u9ad8\uff0c\u7528\u610f\u5c31\u662f\u5728\u8ba9\u4f60\u4eec \u542c\u61c2\uff0c\u6240\u8c13\u201c\u5b9e\u8bdd\u5b9e\u8bf4\u201d\u4e43\u662f\u8981\u4ed6\u4eec\u7f16\u9020\u8c0e\u8a00\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u5e84\u4e3b\u90fd\u53eb\u4f60\u4eec\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u4f60\u4eec\u8fd8\u5bb3\u6015\u4ec0\u4e48\uff1f\u8d6b\u8d6b\u5f52\u4e91\u5e84\u7684\u4efb\u4e3b\u7d20\u6709 \u4fa0\u4e49\u4e4b\u540d\uff0c\u4ed6\u7684\u4fa0\u4e49\u4e4b\u540d\u82e5\u8bf4\u662f\u5929\u4e0b\u77e5\u95fb\u6216\u8bb8\u5938\u5927\u4e00\u4e9b\uff0c\u6700\u5c11\uff0c\u5728\u8fd9\u91cc\u7684\u6ee1\u56ed\u5bbe\u5ba2\uff0c\u5219\u4e00\u5b9a \u662f\u4eba\u4eba\u77e5\u9053\u4e86\u3002\u96be\u9053\u4ed6\u8fd8\u80fd\u4e0d\u987e\u4fa0\u4e49\u4e4b\u540d\uff0c\u5f53\u7740\u6ee1\u56ed\u5bbe\u5ba2\uff0c\u5c06\u4f60\u4eec\u96be\u4e3a\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e00\u4f17\u5bbe\u5ba2\u5fc3\u4e2d\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u597d\u5389\u5bb3\uff0c\u5979\u628a\u8bdd\u5148\u8bf4\u5728\u5934\u91cc\uff0c\u5373\u4f7f\u8fd9\u4e24\u4eba\u51fa\u4e86\u8fd9\u4e2a\u56ed \u5b50\uff0c\u5f52\u4e91\u9f99\u4e5f\u4e0d\u6562\u6740\u5bb3\u4ed6\u4eec\u7684\u4e86\u3002\u201d\u5f52\u4e91\u9f99\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u770b\u4f60\u4eec\u4e0d\u662f\u5bb3\u6015\u6211\uff0c\u662f\u5bb3\u6015 \u4f60\u3002\u201d \u3000\u3000\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u6211\u65e0\u6743\u65e0\u52bf\uff0c\u4ed6\u4eec\u5bb3\u6015\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f52\u4e91\u9f99\u9053\uff1a\u201c\u4f60\u65e0\u6743\u65e0\u52bf\uff0c\u53ef\u6709\u4e00\u8eab\u597d\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4e5f\u662f\u563f\u563f\u7684\u51b7\u7b11\u51e0\u58f0\uff0c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u4f60\u8fde\u7684\u6211\u513f\u5b50\u90fd\u6562\u6253\u7684\u534a\u6b7b\u4e0d\u6d3b\uff0c\u4ed6\u4eec \u8bf4\u4e86\u5b9e\u8bdd\uff0c\u4e0d\u6015\u4f60\u62a5\u590d\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u5fae\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u8fd9\u6837\u7ea6\u5b9a\u5427\uff0c\u4ed6\u4eec\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u8c01\u90fd\u4e0d\u8bb8\u4f24\u5bb3\u4ed6\u4eec\uff0c\u5018\u6709\u5931\u8a00\uff0c\u4efb \u51ed\u5bf9\u65b9\u5904\u7f6e\uff0c\u8bf7\u5929\u4e0b\u82f1\u96c4\u4e3a\u8bc1\uff01\u201d \u3000\u3000\u5f52\u5143\u9f99\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u600e\u7684\u5979\u6562\u8fd9\u6837\u81ea\u4fe1\uff0c\u96be\u9053\u5979\u6709\u628a\u63e1\u53eb\u6211\u8fd9\u4e24\u4e2a\u95e8\u5ba2\u771f\u7684\u8bf4\u51fa\u5b9e \u8bdd\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u95e8\u5ba2\u9053\uff1a\u201c\u5f52\u5e84\u4e3b\u548c\u8fd9\u4f4d\u59d1\u5a18\u90fd\u8981\u6211\u4eec\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u6211\u4eec\u5c31\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u4eca\u65e9\uff0c\u6211\u4eec\u966a\u7740\u5c11\u5e84\u4e3b\u662f\u5230\u57ce\u91cc\u738b\u9ebb\u5b50\u90a3\u91cc\u53d6\u70df\u82b1\uff0c\u738b\u9ebb\u5b50\u5236\u9020\u7684\u70df\u82b1\u662f\u6d1b\u9633\u57ce\u6700\u6709 \u540d\u7684\uff0c\u5c11\u5e84\u4e3b\u591a\u52a0\u94f6\u4e24\uff0c\u5b9a\u9020\u5341\u4e5d\u79cd\u6700\u597d\u7684\u70df\u82b1\uff0c\u6709\u98de\u96ea\u8fce\u6625\u3001\u6709\u91d1\u5782\u6768\u67f3\u3001\u6709\u6625\u8272\u6ee1\u56ed\uff0c \u6709\u96ea\u91cc\u7ea2\u6885\uff0c\u8fd8\u6709\u5b54\u96c0\u5f00\u5c4f\u3001\u8702\u9e1f\u95f4\u6765\u2014\uff0d\u201d\u90a3\u59d3\u5f20\u7684\u95e8\u5ba2\u5148\u8bf4\u3002\u8bdd\u5fe7\u672a\u4e86\uff0c\u5f52\u4e91\u9f99\u5df2\u662f\u76b1 \u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8981\u4f60\u4eec\u62a5\u70df\u82b1\u540d\u79f0\uff0c\u5feb\u70b9\u8a00\u5f52\u6b63\u4f20\u3002\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u4e24\u4e2a\u95e8\u5ba2\u5e76\u975e\u8981\u62a5\u70df\u82b1\u540d\u79f0\uff0c\u4ed6\u4eec\u4e4b\u6240\u4ee5\u5982\u6b64\u62d6\u5ef6\u65f6\u95f4\u3002\u6b63\u662f\u4e3a\u4e86\u51c6\u5907\u5728\u201c\u8bdd\u4eba \u6b63\u9898\u201d\u4e4b\u65f6\uff0c\u600e\u6837\u8bf4\u624d\u662f\u6700\u4e3a\u59a5\u5f53\u3002 \u3000\u3000\u201c\u662f\uff0c\u662f\u201d\uff0c\u90a3\u59d3\u5f20\u7684\u95e8\u5ba2\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff1a\u201c\u6211\u4eec\u672c\u6765\u8981\u5230\u738b\u9ebb\u5b50\u90a3\u91cc\u7684\uff0c\u4f46\u8d70\u5230\u4e86\u738b\u6bcd \u5a18\u5a18\u5e99\u91cc\u7684\u65f6\u5019\uff0c\u770b\u89c1\u8fd9\u4f4d\u59d1\u5a18\u5356\u89e3\uff0c\u5c11\u5e84\u4e3b\u5c31\u4e0d\u80af\u8d70\u4e86\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u4e0d\u89c9\u53c8\u76b1\u8d77\u7709\u5934\u4e86\uff0c\u201c\u4e3a\u4ec0\u4e48\u5c11\u5728\u4e3b\u4e0d\u80af\u8d70\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5f52\u5728\u4e3b\uff0c\u8bf7\u4f60\u4e0d\u8981\u6253\u65ad\u4ed6\u4eec\u7684\u8bf4\u8bdd\u597d\u4e0d\u597d\uff1f\u6211\u60f3\u4f60\u4e0d\u95ee\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u81ea\u4f1a \u8bf4\u4e0b\u53bb\u7684\u3002\u201d\u5f52\u65e0\u9f99\u9ed1\u8d77\u8138\u5b54\u5927\u9a6c\u91d1\u5200\u5730\u91cd\u65b0\u5750\u597d\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u4e24\u4e2a\u5bb6\u4f19\u7528\u7684\u662f\u6211\u7684\u996d\u7897\uff0c \u8c05\u4f60\u4eec\u4e5f\u4e0d\u6562\u8bf4\u4e0d\u51fa\u4e2d\u542c\u7684\u8bdd\u6765\u3002\u201d \u3000\u3000\u54ea\u77e5\u5927\u51fa\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u90a3\u4e2a\u95e8\u5ba2\u7adf\u7136\u771f\u7684\u8bf4\u51fa\uff1a\u201c\u4e0d\u4e2d\u542c\u201d\u7684\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u6211\u95ee\u5c11\u5e84\u4e3b\u56e0\u4f55\u4e0d\u8d70\uff0c\u5c11\u5728\u4e3b\u8bf4\u4ed6\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u6f02\u4eae\u7684\u59d1\u5a18\uff0c\u4ed6\u8bf4\u5bb6\u91cc\u90a3\u682a\u9ed1\u7261 \u4e39\u4e5f\u6bd4\u4e0d\u4e0a\u8fd9\u4f4d\u59d1\u5a18\u7684\u9ed1\u91cc\u4fcf\u3002\u201d \u3000\u3000\u5728\u8859\u95e8\u91cc\u529e\u6587\u7684\u5e08\u7237\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u77e5\u597d\u8272\u5219\u5e55\u5c11\u5973\uff0c\u5e74\u8f7b\u7684\u5c0f\u4f19 \u5b50\u89c1\u4e86\u6f02\u4eae\u7684\u5973\u4eba\uff0c\u8bf4\u51e0\u53e5\u4e0d\u592a\u6b63\u7ecf\u7684\u8bdd\u513f\uff0c\u90a3\u4e5f\u662f\u5c0f\u4e8b\u4e00\u5e84\uff0c\u65e0\u8db3\u4e3a\u5947\u3002\u201d\u5c11\u5973\u9053\uff1a\u201c\u4f60 \u600e\u77e5\u4ed6\u53ea\u662f\u8bf4\u51e0\u53e5\u4e0d\u592a\u6b63\u7ecf\u7684\u8bdd\u513f\uff1f \u3000\u3000\u54fc\uff0c\u597d\u5728\u4f60\u53ea\u662f\u529e\u6587\u51ed\u7684\u5e08\u7237\uff0c\u4e0d\u662f\u4f1a\u5802\u5ba1\u6848\u7684\u6cd5\u5b98\uff0c\u6848\u60c5\u8fd8\u672a\u4f9b\u8ff0\uff0c\u4f60\u5c31\u8981\u4ece\u8f7b\u53d1\u843d \u4e86\uff01\u201d \u3000\u3000\u5e08\u7237\u7ed9\u4ed6\u62a2\u767d\uff0c\u6c14\u547c\u547c\u7684\u5750\u4e0b\uff0c\u5374\u4e5f\u4e0d\u6562\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u59d3\u674e\u7684\u95e8\u5ba2\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u540e\u6765\uff0c\u5c11\u5e84\u4e3b\u53eb\u6211\u4eec\u628a\u95f2\u4eba\u8d76\u5f00\uff0c\u4ed6\u8d70\u8fdb\u573a\u90ed\uff0c\u4eb2\u81ea\u548c\u8fd9\u4f4d\u59d1 \u5a18\u8bf4\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u6c89\u58f0\u9053\uff1a\u201c\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u59d3\u674e\u7684\u95e8\u5ba2\u9053\uff1a\u201c\u5509\uff0c\u6211\u53ef\u6709\u70b9\u4e0d\u5927\u597d\u610f\u601d\u8bf4\u3002\u201d\u628a\u773c\u775b\u671b\u7740\u90a3\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u4e0d\u5fcc\u8fdd\uff0c\u4f60\u4eec\u7167\u76f4\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u5c11\u751f\u4e3b\u8981\u548c\u8fd9\u4f4d\u59d1\u5a18\u2018\u76f8\u597d\u2019\uff0c\u53eb\u5979\u522b\u518d\u629b\u5934\u9732\u9762\uff0c\u4ed6\u613f\u610f\u4e3a\u8fd9\u4f4d\u59d1\u5a18\u91d1\u5c4b\u85cf\u5a07\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u6c14\u6124\u4ea4\u52a0\u3001\u4f46\u56e0\u6709\u8a00\u5728\u5148\u3002\u5374\u53c8\u4e0d\u80fd\u53d1\u4f5c\uff0c\u552f\u6709\u987f\u8db3\u9053\uff1a\u201c\u8352\u5510\uff0c\u8352\u5510\uff01\u201d\u5c11\u5973 \u9053\uff1a\u201c\u8fd8\u6709\u66f4\u8352\u5510\u7684\u5462\uff0c\u4f60\u542c\u4ed6\u4eec\u8bf4\u4e0b\u53bb\u5427\u3002\u201d\u90a3\u59d3\u5f20\u7684\u95e8\u5ba2\u8bf4\u9053\uff1a\u201c\u540e\u6765\u8fd9\u4f4d\u59d1\u5a18\u9a82\u5c11\u5e84 \u4e3b\u662f\u765e\u87c6\u6a21\uff0c\u5c11\u5e84\u4e3b\u5927\u6012\u9053\uff1a\u201c\u4f60\u9a82\u6211\u662f\u765e\u86e4\u87c6\u6211\u8fd9\u765e\u86e4\u87c6\u504f\u504f\u8981\u5403\u4f60\u7684\u5929\u9e45\u8089\u3002\u4ed6\u3001\u4ed6\u5c31 \u52a8\u624b\uff0c\u62a2\u3001\u62a2\u8fd9\u4f4d\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u90a3\u59d3\u674e\u7684\u95e8\u5ba2\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5c11\u5e84\u4e3b\u8fd8\u6ca1\u78b0\u7740\u8fd9\u4f4d\u59d1\u5a18\uff0c\u53ea\u542c\u5f97\u50fb\u50fb\u556a\u556a\u58f0\u54cd\uff0c\u5c11\u5e84\u4e3b \u5df2\u662f\u7ed9\u8fd9\u4f4d\u59d1\u5a18\u6253\u4e86\u51e0\u8bb0\u8033\u5149\u3002\u8fd9\u4f4d\u59d1\u5a18\u8bf4\uff0c\u4f60\u518d\u65e0\u793c\uff0c\u53ef\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201d\u5f52\u5e84\u4e3b\uff0c\u4f60\u542c\u89c1\u6ca1\u6709\uff1f\u7b2c\u4e00\u3001\u662f\u4f60\u7684\u5b9d\u8d1d\u513f\u5b50\u5148\u52a8\u624b\uff0c\u7b2c\u4e8c\u3001\u6211\u5df2\u7ecf\u8b66\u544a \u8fc7\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u5df2\u662f\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u59d3\u5f20\u7684\u95e8\u5ba2\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5c11\u5e84\u4e3b\u66f4\u52a0\u66b4\u6012\u5982\u96f7\uff0c\u7acb\u5373\u5c31\u548c\u8fd9\u4f4d\u59d1\u5a18\u6253\u8d77\u6765\u4e86\u3002\u8fd8\u8981\u6211 \u4eec\u5e2e\u5fd9\u4ed6\u6253\uff0c\u540e\u6765\uff0c\u540e\u6765\u7684\u4e8b\u60c5\u5c31\u4e0d\u5fc5\u8bf4\u4e86\u3002\u6211\u4eec\u5e2e\u4e0d\u4e0a\u5c11\u5728\u751f\u7684\u5fd9\uff0c\u53ea\u80fd\u62c6\u6389\u738b\u6bcd\u5a18\u5a18\u5e99 \u7684\u4e24\u5757\u95e8\u677f\uff0c\u628a\u4ed6\u62ac\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u8bc1\u4eba\u4f5c\u4f9b\u5b8c\u4e86\uff0c\u5bbe\u5ba2\u4eec\u9762\u9762\u76f8\u89d1\u3002 \u3000\u3000\u6709\u4eba\u5bbe\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u7751\u7684\u4e1c\u897f\uff0c\u5403\u91cc\u6252\u5916\u3002\u4e3b\u4eba\u5927\u5c55\u5bbd\u5bb9\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9\u4f60\u4eec\u8d70\u5f97\u8fd9\u6837\u4fbf \u5b9c\uff01\u201d\u4f38\u624b\u5c31\u6253\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u6b66\u529f\u5176\u5b9e\u5e76\u4e0d\u6bd4\u90a3\u4e24\u4e2a\u95e8\u5ba2\u9ad8\u5f3a\uff0c\u4f46\u90a3\u4e24\u4e2a\u95e8\u5ba2\u5374\u4e0d\u8fd8\u624b\uff0c\u8ba9\u4ed6\u72e0\u72e0\u63cd\u4e86\u51e0\u62f3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e24\u58f0\u60e8\u53eb\uff0c\u63a5\u7740\u201c\u94ee\u94ee\u201d\u4e24\u58f0\uff0c\u4e24\u679a\u94dc\u94b1\u843d\u5728\u4ed6\u4eec\u8eab\u65c1\u7684\u5047\u5c71\u77f3\u4e0a\u3002 \u3000\u3000\u63a5\u7740\u4e24\u53ea\u8840\u6dcb\u6dcb\u7684\u8033\u6735\u968f\u7740\u94dc\u94b1\u843d\u4e0b\u3002 \u3000\u3000\u90a3\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u4ee5\u4e0d\u7406\u4f1a\u6211\u8bf4\u7684\u8bdd\uff0c\u4f46\u4f60\u4eec\u5e84\u4e3b\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4f60\u4eec\u4e5f\u5f53\u4f5c\u662f\u653e\u5c41 \u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u4e00\u53cc\u8033\u6735\u6b63\u662f\u7ed9\u5979\u7528\u94b1\u5272\u4e0b\u7684\u3002 \u3000\u3000\u7528\u78e8\u5229\u7684\u94dc\u94b1\u6765\u5272\u8033\u6735\uff0c\u5272\u7684\u597d\u50cf\u5200\u524a\u4e00\u822c\uff0c\u9f50\u6839\u5207\u53bb\uff0c\u8fd9\u4efd\u672c\u9886\uff0c\u5df2\u662f\u8db3\u4ee5\u4ee4\u5f97\u5f52\u5e9c \u7684\u5bb6\u4e01\u95e8\u5ba2\u80c6\u5bd2\u3002 \u3000\u3000\u66f4\u96be\u7684\u662f\uff0c\u6ee1\u56ed\u5b50\u6324\u6ee1\u4e86\u4eba\uff0c\u521a\u597d\u662f\u524a\u4e86\u90a3\u4e2a\u4eba\u7684\u8033\u6735\uff0c\u5e76\u6ca1\u8bef\u4f24\u65c1\u4eba\u3002 \u3000\u3000\u8fd9\u6837\u7684\u6697\u5668\u529f\u592b\uff0c\u8fde\u5a01\u9707\u4e00\u65b9\u7684\u5f52\u5143\u9f99\u90fd\u4e0d\u7981\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\uff0c\u4ed6\u7684\u624b\u4e0b\u81ea\u662f\u66f4\u52a0\u5403\u60ca \u4e86\u3002 \u3000\u3000\u5f52\u5143\u9f99\u4e0b\u4e0d\u4e86\u53f0\uff0c\u53ea\u4e5f\u88c5\u6a21\u4f5c\u6837\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u62e6\u963b\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5176\u5b9e\u7528\u4e0d\u7740\u5f52\u5143\u9f99\u4e0b\u4ee4\uff0c\u4ed6\u7684\u624b\u4e0b\u89c1\u8fc7\u8fd9\u5c11\u5973\u72e0\u8fa3\u7684\u624b\u6bb5\uff0c\u65e9\u5df2\u662f\u5fc3\u60ca\u80c6\u4e27\uff0c\u54ea\u91cc\u8fd8\u6562 \u65e0\u4e8b\u751f\u975e\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u662f\u767e\u601d\u83ab\u5f97\u5176\u89e3\uff0c\u4e3a\u4ec0\u4e48\u90a3\u4e2a\u95e8\u5ba2\u8981\u201c\u5403\u91cc\u6252\u5916\u201d\uff1f\u8fd9\u5c11\u5973\u7ed9\u4e86\u4ed6\u4eec\u4ec0\u4e48\u597d\u5904\uff1f \u8fd9\u5c11\u5973\u5e76\u6ca1\u7ed9\u4ed6\u4eec\u4ec0\u4e48\u597d\u5904\uff0c\u53ea\u662f\u628a\u4ed6\u4eec\u7684\u6027\u547d\u8fd8\u7ed9\u4ed6\u4eec\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u662f\u65e9\u5df2\u7740\u4e86\u90a3\u5c11\u5973\u7684\u9053\u513f\u7684\u3002 \u3000\u3000\u8868\u9762\u770b\u6765\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u53d7\u4f24\uff0c\u5176\u5b9e\u4ed6\u4eec\u7684\u80f8\u53e3\u90fd\u6709\u4e00\u4e2a\u94dc\u94b1\u822c\u5927\u5c0f\u7684\u7ea2\u5370\uff0c\u8fd9\u4e0d\u662f\u666e\u901a \u7684\u4f24\u75d5\uff0c\u662f\u8db3\u4ee5\u7f6e\u4eba\u6b7b\u547d\u7684\u6bd2\u4f24\u3002\u56e0\u6b64\u4ed6\u4eec\u624d\u88ab\u8feb\u505a\u8fd9\u5b97\u4ea4\u6613\uff0c\u7528\u8bf4\u5b9e\u8bdd\u6765\u6362\u53d6\u89e3\u836f\u3002 \u3000\u3000\u5f52\u4e91\u5e84\u91cc\u5bbe\u5ba2\u4eec\u8bae\u8bba\u7eb7\u7eb7\uff0c\u5f52\u4e91\u5e84\u4e3b\u5374\u662f\u505a\u58f0\u4e0d\u5f97\u3002\u4ed6\u7684\u95e8\u5ba2\u53cd\u800c\u5e2e\u4e86\u4ed6\u7684\u5bf9\u5934\uff0c\u4ed6\u6709 \u4ec0\u4e48\u597d\u8bf4\u7684\uff1f\u90a3\u529e\u6587\u6848\u7684\u5e08\u7237\u5ffd\u9053\uff1a\u201c\u5e84\u4e3b\uff0c\u4f60\u662f\u4e2a\u5fe0\u539a\u8001\u5b9e\u7684\u4eba\uff0c\u4e0d\u8bc6\u4eba\u5fc3\u9669\u8bc8\u3002\u5b50\u65e5\uff1a \u541b\u5b50\u53ef\u4ee5\u6b3a\u4e4b\u4ee5\u65b9\uff0c\u4f60\u662f\u7ed9\u4eba\u5bb6\u4e32\u540c\u6b3a\u9a97\u4e86\uff01\u201d \u3000\u3000\u5f52\u5143\u9f99\u7cbe\u795e\u4e00\u632f\uff0c\u77e5\u9053\u8fd9\u5e08\u7237\u80fd\u8a00\u5584\u8fa9\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8bf7\u5e08\u7237\u6307\u6559\u3002 \u3000\u3000\u201d \u3000\u3000\u90a3\u5e08\u7237\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u95e8\u5ba2\u662f\u5403\u4f60\u7684\u996d\u7684\uff0c\u6309\u666e\u901a\u60c5\u7406\u800c\u8bba\uff0c\u5373\u4f7f\u771f\u7684\u662f\u4ee4\u90ce\u7406\u4e8f\uff0c\u4ed6\u4eec \u4e5f\u4f1a\u5e2e\u4ee4\u90ce\u63a9\u9970\u7684\u3002\u8fd9\u4e2a\u5973\u5b50\u4e5f\u7edd\u4e0d\u6562\u8bf7\u4ed6\u4eec\u4f5c\u8bc1\u3002\u4f46\u5982\u4eca\u4ed6\u5374\u505a\u51fa\u4e86\u4e0d\u5408\u60c5\u7406\u7684\u4e8b\uff0c\u4f60\u4e0d \u89c9\u5f97\u5947\u602a\u5417\uff1f\u201d \u3000\u3000\u5f52\u5143\u9f99\u88c5\u4f5c\u5982\u68a6\u521d\u9192\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u6211\u771f\u7684\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u6211\u53ea\u9053\u4ed6\u4eec\u662f\u56e0\u4e3a\u77e5\u9053\u6211\u5e73\u751f\u6b63\u76f4\uff0c\u662f\u4ee5\u624d\u6562\u76f4\u8a00\u65e0\u5fcc\u3002\u5374\u6ca1\u60f3\u5230\u8fd9\u662f\u4e0d\u5408\u4e00\u822c\u60c5\u7406\u7684 \u4e8b\u3002\u5e08\u7237\uff0c\u4f9d\u4f60\u770b\u2014\u2014\u201d \u3000\u3000\u5e08\u7237\u8bf4\u9053\uff1a\u201c\u51e1\u662f\u4e0d\u5408\u6e05\u7406\u7684\u4e8b\uff0c\u5176\u4e2d\u5fc5\u6709\u9b3c\u3002\u4f9d\u6211\u770b\uff0c\u4ed6\u4eec\u591a\u534a\u662f\u53d7\u4e86\u8fd9\u5973\u5b50\u6536 \u4e70\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5356\u89e3\u5973\u5b50\uff0c\u8981\u662f\u4f60\u4eec\u8fd9\u4e24\u4e2a\u95e8\u5ba2\u53ea\u9700\u4e09\u4e94\u4e24\u94f6\u5b50\u5c31\u53ef\u4ee5\u6536\u4e70\u7684 \u8bdd\uff0c\u6211\u5927\u6982\u8fd8\u51fa\u7684\u8d77\u3002\u8bf7\u95ee\u5f52\u4efb\u4e3b\uff0c\u4f60\u7684\u95e8\u5ba2\u662f\u4e09\u4e94\u4e24\u94f6\u5b50\u53ef\u4ee5\u6536\u4e70\u7684\u4e48\uff1f\u201d \u3000\u3000\u56ed\u5b50\u91cc\u6324\u6ee1\u5ba2\u4eba\uff0c\u6709\u7684\u5ba2\u4eba\u524d\u9762\u6709\u5047\u5c71\u906e\u6321\u7684\uff0c\u5df2\u662f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u5e08\u7237\u53ef\u662f\u4e00\u7751\u6b63\u7ecf\uff0c\u8bf4\u9053\uff1a\u201c\u6536\u4e70\u4e00\u4e2a\u4eba\u4e0d\u9650\u4e8e\u53ea\u8981\u94f6\u5b50\u7684\uff01\u201c\u5c11\u5973\u9053\uff1a\u201c\u90a3\u6211\u7528\u4ec0 \u4e48\u6536\u4e70\uff1f\u201d \u3000\u3000\u5e08\u7237\u4e0d\u7406\u4f1a\u5979\uff0c\u5374\u5bf9\u5f52\u5143\u9f99\u9053\uff1a\u201c\u4e00\u4e2a\u5356\u89e3\u5973\u5b50\uff0c\u6b66\u529f\u662f\u5982\u6b64\u9ad8\u5f3a\uff0c\u4f9d\u6211\u770b\u5979\u7684\u6765\u5386\u7740 \u5b9e\u53ef\u7591\u3002\u4f60\u90a3\u4e24\u4e2a\u95e8\u5ba2\uff0c\u884c\u4e8b\u4e0d\u5408\u7406\uff0c\u540c\u6837\u53ef\u7591\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u672c\u6765\u5c31\u662f\u4e00\u515a\u7684\uff0c\u8fd9\u5973\u5b50\u5f88\u53ef \u80fd\u662f\u4ed6\u4eec\u7684\u9996\u9886\u3002\u90e8\u4e0b\u5411\u9996\u9886\u90ca\u5fe0\uff0c\u7acb\u4e86\u529f\u52b3\uff0c\u597d\u5904\u591a\u7740\u5462\uff0c\u4f55\u9700\u94f6\u5b50\u6536\u4e70\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u8fd9\u756a\u8bdd\u867d\u7136\u662f\u5f3a\u8f9e\u593a\u7406\uff0c\u4f46\u82e5\u8981\u548c\u4ed6\u8ba4\u771f\u8fa9\u9a73\u7684\u8bdd\uff0c\u8fd8\u662f\u4f1a\u7ea0\u7f20\u4e0d\u6e05\u7684\u3002 \u3000\u3000\u90a3\u5973\u5b50\u5ffd\u503c\uff1a\u201c\u5f52\u5e84\u4e3b\uff0c\u4f9d\u4f60\u770b\uff0c\u8fd9\u4f4d\u5e08\u7237\u4f1a\u4e0d\u4f1a\u662f\u6211\u7684\u540c\u515a\uff1f\u201d \u3000\u3000\u5e08\u7237\u52c3\u7136\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u600e\u4f1a\u662f\u4f60\u7684\u540c\u515a\uff1f\u201c\u8981\u4e0d\u662f\u5fcc\u60ee\u8fd9\u5973\u5b50\u6b66\u529f\u4e86\u5f97\uff0c\u6015\u5979 \u91cd\u65bd\u94b1\u9556\u5272\u8033\u7684\u624b\u6bb5\uff0c\u4ed6\u5df2\u662f\u8981\u7834\u53e3\u5927\u9a82\u4e86\u3002 \u3000\u3000\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u4f60\u5f53\u7136\u4e0d\u4f1a\u662f\u6211\u7684\u540c\u515a\u3002\u4f46\u6211\u8bf4\uff0c\u4f60\u4e5f\u53ef\u4ee5\u548c\u90a3\u4e24\u4e2a\u95e8\u5ba2\u4e00\u6837\uff0c\u7ed9 \u6211\u4f5c\u8bc1\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5e08\u7237\u83ab\u540d\u5176\u5999\uff0c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u8bc1\u660e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u8bc1\u660e\u5f52\u5e84\u4e3b\u8fd9\u4e2a\u5b9d\u8d1d\u513f\u5b50\u7f6a\u8be5\u5904\u6b7b\uff01\u201d \u3000\u3000\u5e08\u7237\u662f\u53c8\u6c14\u53c8\u597d\u7b11\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u53d1\u75af\u5427\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u65e5\u8bf4\u65e0\u51ed\uff0c\u6211\u53ef\u4ee5\u62ff\u51fa\u8bc1\u636e\u3002\u201d \u3000\u3000\u5e08\u7237\u9053\uff1a\u201c\u597d\uff0c\u4f60\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5f52\u6d1b\u82f1\u66fe\u903c\u5978\u4e00\u4e2a\u59d3\u5b54\u7684\u5e74\u8f7b\u5be1\u5987\uff0c\u5be1\u5987\u4e0d\u582a\u53d7\u8fb1\uff0c\u81ea\u7f22\u8eab\u4ea1\u3002\u5979 \u7684\u516c\u516c\u662f\u4e2a\u7a77\u79c0\u624d\u3002\u867d\u7136\u660e\u77e5\u5f52\u5bb6\u6709\u8d22\u6709\u52bf\uff0c\u6253\u5b98\u53f8\u4e00\u5b9a\u5403\u4e8f\u3002\u4f46\u6c14\u6124\u4e0d\u8fc7\uff0c\u4ed6\u8fd8\u662f\u4eb2\u81ea\u5199 \u4e86\u4e00\u5f20\u72b6\u7eb8\uff0c\u628a\u5f52\u6d1b\u82f1\u544a\u5230\u5b98\u5e9c\u3002\u8bf7\u77e5\u5e9c\u5927\u4eba\u4e3a\u4ed6\u5ab3\u5987\u4f38\u51a4\u3002\u201d \u3000\u3000\u201c\u8fd9\u5f20\u72b6\u7eb8\u843d\u5230\u8fd9\u4f4d\u5e08\u7237\u624b\u4e0a\uff0c\u4ed6\u6050\u6015\u77e5\u5e9c\u5927\u4eba\u4e0d\u77e5\u5f52\u6d1b\u82f1\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u4e8e\u662f\u9644\u4e0a\u7b7e\u5448 \uff08\u5373\u9644\u52a0\u81ea\u5df1\u5bf9\u5e94\u8be5\u5982\u4f55\u529e\u7406\u8fd9\u4ef6\u6848\u7684\u610f\u89c1\uff0c\u5199\u5728\u53e6\u4e00\u5f20\u7eb8\u4e0a\uff09\u7b7e\u5448\u8bf4\u660e\u4e86\u88ab\u544a\u662f\u4e0d\u80fd\u88ab\u5f97 \u7f6a\u7684\u4eba\u7269\u4e4b\u540e\u4ed6\u8fd8\u62df\u4e86\u6279\u8f9e\uff0c\u7531\u77e5\u5e9c\u53d1\u7ed9\u5ba1\u6848\u7684\u6cd5\u5b98\uff0c\u5982\u62df\u529e\u6848\u3002\u7ed3\u679c\u662f\u5c06\u90a3\u79c0\u624d\u8d23\u6253\u4e09\u5341 \u5927\u677f\uff0c\u9769\u53bb\u529f\u540d\uff0c\u6240\u544a\u4e0d\u4e88\u53d7\u7406\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u5979\u62ff\u51fa\u5e08\u7237\u90a3\u5f20\u7b7e\u5448\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5f20\u7b7e\u5448\u5c31\u662f\u771f\u51ed\u5b9e\u636e\u3001\u5e08\u7237\uff0c\u4f60\u662f\u4e0d\u662f\u8be5\u5f53\u5904 \u65a9\uff1f\u8d2a\u8d43\u6789\u6cd5\u7684\u5b98\u513f\uff0c\u662f\u4e0d\u662f\u4e5f\u8be5\u95ee\u7f6a\uff1f\u201c\u5e08\u7237\u53c8\u60ca\u53c8\u6012\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u54ea\u6709\u8fd9 \u79cd\u4e8b\u60c5\uff0c\u4f60\u3001\u4f60\u662f\u634f\u9020\u7684\u3002\u201d \u3000\u3000\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4eba\u8bf4\u6211\u634f\u9020\u7684\uff0c\u90a3\u4e48\u54b1\u4eec\u53ef\u4ee5\u5bf9\u5bf9\u7b14\u8ff9\uff0c\u8ba9\u5927\u5bb6\u770b\u770b\uff0c\u662f\u5426\u4f60\u4eb2\u7b14\u6240 \u4e66\uff1f\u201d \u3000\u3000\u5e08\u7237\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u5047\u5192\u6211\u7b14\u8ff9\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6765\u5230\u6d1b\u9633\u4e0d\u8fc7\u4e09\u5929\uff0c\u4f60\u5728\u8859\u95e8\u91cc\u5f53\u6587\u6848\uff0c\u6211\u600e\u80fd\u53bb\u641c\u96c6\u4f60\u7684\u2018\u58a8 \u5b9d\u2019\uff1f\u4e09\u5929\u4e4b\u5185\uff0c\u6211\u4e5f\u6ca1\u6709\u597d\u4e48\u5927\u672c\u9886\u53bb\u6a21\u4eff\u4f60\u7684\u7b14\u8ff9\u5440\uff01 \u3000\u3000\u518d\u8bf4\u96be\u9053\u6211\u662f\u672a\u535c\u5148\u77e5\u7684\u9b3c\u8c37\u5b50\uff0c\u77e5\u9053\u4eca\u5929\u4f60\u8981\u5728\u8fd9\u91cc\u66ff\u5f52\u5c11\u5e84\u4e3b\u8fa9\u62a4\uff0c\u76d8\u95ee\u6211\u5417\uff1f\u201d \u3000\u3000\u5e08\u7237\u9053\uff1a\u201c\u90a3\uff0c\u90a3\u4f60\u662f\u600e\u6837\u53d6\u5f97\u6211\u8fd9\u5f20\u7b7e\u5448\u7684\uff1f\u201d \u3000\u3000\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6253\u81ea\u62db\u4e86\u5427\uff1f\u563f\u563f\uff0c\u53ea\u8981\u4f60\u627f\u4fe1\u662f\u4f60\u5199\u7684\u5c31\u884c\uff0c\u81f3\u4e8e\u627e\u600e\u6837\u53d6\u5f97\uff1b\u90a3 \u4f60\u7ba1\u4e0d\u7740\u4e86\u3002\u201d \u3000\u3000\u5e08\u7237\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u627f\u8ba4\uff0c\u6211\u4e0d\u627f\u8ba4\uff01\u201d\u4e0d\u8fc7\u4ed6\u5374\u8bf4\u4e0d\u51fa\u7406\u7531\uff0c\u8fde\u5f52\u5143\u9f99\u90fd\u89c9\u5f97\u4ed6\u8fd9\u6837\u80e1 \u95f9\uff0c\u662f\u8d8a\u95f9\u8d8a\u81ed\u4e86\u3002 \u3000\u3000\u5f52\u5143\u9f99\u9053\uff1a\u201c\u5b54\u5bb6\u5be1\u5987\u81ea\u7f22\u8eab\u4ea1\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u662f\u6709\u7684\u3002\u4f46\u5979\u7684\u6b7b\u56e0\uff0c\u8a00\u4eba\u6b8a\uff0c\u6b7b\u65e0\u5bf9\u8bc1\uff0c \u5374\u662f\u65e0\u4ece\u67e5\u8003\u4e86\u3002\u5c0f\u513f\u867d\u7136\u987d\u52a3\uff0c\u901a\u5978\u5be1\u5987\u8fd9\u79cd\u4e8b\u60c5\u6211\u76f8\u4fe1\u4ed6\u662f\u7edd\u5bf9\u505a\u4e0d\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u6216\u8005\u4f1a\u95ee\uff0c\u90a3\u4e3a\u4ec0\u4e48\u5979\u7684\u516c\u516c\u4e0d\u544a\u522b\u4eba\uff0c\u504f\u504f\u53ea\u544a\u4f60\u7684\u513f\u5b50\u3002 \u3000\u3000\u6211\u5e73\u751f\u5fe0\u539a\uff0c\u4eba\u6240\u5171\u77e5\u3002\u672c\u8be5\u9690\u6076\u626c\u5584\u7684\u3002\u5509\uff0c\u4f46\u4e8b\u5230\u5982\u4eca\uff0c\u6211\u4e5f\u53ea\u597d\u8bf4\u51fa\u6765\u4e86\u3002\u90a3\u662f \u56e0\u4e3a\u90a3\u59d3\u5b54\u7684\u7a77\u79c0\u624d\uff0c\u7a77\u8ff7\u5fc3\u7a8d\uff0c\u60f3\u85c9\u5ab3\u5987\u7684\u6a2a\u6b7b\uff0c\u8bb9\u8bc8\u6211\u4e00\u7b14\u3002\u8c01\u53eb\u6211\u6709\u51e0\u4e2a\u94b1\u5462\uff1f\u201d \u3000\u3000\u201c\u81f3\u4e8e\u8bf4\u5230\u9a86\u5e08\u7237\u90a3\u5f20\u7b7e\u5448\uff0c\u6211\u4e5f\u76f8\u4fe1\u662f\u522b\u4eba\u5047\u5192\u4ed6\u7684\u7b14\u8ff9\u3002\u8fd9\u4e2a\u6240\u8c13\u2018\u522b\u4eba\u2019\uff0c\u5f53\u7136 \u5e76\u4e0d\u4e00\u5b9a\u662f\u6307\u8fd9\u4f4d\u59d1\u5a18\u3002\u4e0d\u8fc7\u8fd9\u4f4d\u59d1\u5a18\u795e\u901a\u5e7f\u5927\uff0c\u5979\u65e2\u7136\u80fd\u591f\u4ece\u8859\u95e8\u91cc\u5077\u51fa\u72b6\u7eb8\uff0c\u627e\u4e00\u4e2a\u719f \u6089\u9a86\u5e08\u7237\u7b14\u8ff9\u7684\u4eba\u6765\u5199\u7b7e\u5448\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u7a00\u5947\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u5f52\u5e84\u4e3b\uff0c\u5047\u5982\u4f60\u4e0d\u505a\u5e84\u4e3b\uff0c\u8dd1\u5230\u8859\u95e8\u91cc\u505a\u5e08\u7237\u7684\u8bdd\uff0c\u4e00\u5b9a\u6bd4\u8fd9\u4f4d\u5e08\u7237 \u66f4\u80fd\u5e72\u3002\u9a86\u5e08\u7237\uff0c\u6211\u770b\u4f60\u5e94\u8be5\u62dc\u5e84\u4e3b\u4e3a\u5e08\uff01\u201d \u3000\u3000\u9a86\u5e08\u7237\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u6267\u4e00\u8f9e\uff0c\u8bf4\u5230\u660e\u5e74\u4e5f\u8bf4\u4e0d\u6e05\u695a\u3002\u4f60\u662f\u4e0d\u662f\u51c6\u5907\u7559\u5728\u6d1b\u9633\u548c \u6211\u4eec\u6253\u4e00\u5e74\u534a\u8f7d\u5b98\u53f8\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u5f52\u5e84\u4e3b\u5728\u6d1b\u9633\u7eb5\u7136\u8fd8\u4e0d\u80fd\u8bf4\u662f\u53ea\u624b\u906e\u5929\uff0c\u52a0\u4e0a\u4e86\u50cf\u4f60\u8fd9\u6837\u5927\u5927\u5c0f\u5c0f\u7684\u9a86\u5e08 \u7237\u3001\u725b\u5e08\u7237\u3001\u9a6c\u5e08\u7237\u2026\u2026\u6700\u5c11\u4e5f\u53ef\u4ee5\u906e\u4e86\u5927\u534a\u8fb9\u5929\u4e86\uff0c\u6211\u5982\u4f55\u80fd\u591f\u548c\u4f60\u4eec\u6253\u5b98\u53f8\uff1f\u201d \u3000\u3000\u5f52\u5143\u9f99\u970d\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u8fd9\u4f4d\u59d1\u5a18\u65e9\u5df2\u8bf4\u597d\u4e86\u662f\u6309\u6c5f\u6e56\u89c4\u77e9\u529e\u4e8b\u7684\uff0c\u9a86\u5e08 \u7237\uff0c\u4f60\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u53ef\u4ee5\u4e0d\u5fc5\u7406\u4e86\u3002\u201d\u4ed6\u6068\u8fd9\u4e2a\u9a86\u5e08\u7237\u5e2e\u4e86\u4ed6\u7684\u5012\u5fd9\uff0c\u7d22\u6027\u5c06\u4ed6 \u6487\u8fc7\u4e00\u8fb9\u3002\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u7b2c\u4e00\u6b65\u662f\u8bc4\u7406\uff0c\u5018\u82e5\u53cc\u65b9\u90fd\u4e0d\u627f\u8ba4\u7406\u4e8f\uff0c\u90a3\u5c31\u53ea\u80fd\u7528\u6b66\u529b\u89e3\u51b3\uff0c \u8d25\u7684\u4e00\u65b9\uff0c\u5fc5\u987b\u63a5\u53d7\u5bf9\u65b9\u6761\u4ef6\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u5929\u4e0b\u82f1\u96c4\u4f5c\u8bc1\u3002\u6211\u82e5\u8f93\u4e86\uff0c\u6027\u547d\u4e5f\u8f93\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u76ae\u7b11\u5411\u4e0d\u7b11\u7684\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5fc5\u5982\u6b64\uff0c\u6211\u513f\u7ed9\u4f60\u6253\u5f97\u91cd\u4f24\uff0c\u53ea\u987b\u4f60 \u7559\u5728\u5f52\u5143\u5728\uff0c\u5c06\u4ed6\u670d\u4f8d\u597d\u4e86\u5c31\u884c\u3002\u201d \u3000\u3000\u6240\u8c13\u201c\u670d\u4f8d\u201d\uff0c\u5176\u5b9e\u90fd\u662f\u8981\u5979\u4e3a\u5a62\u4e3a\u59be\uff0c\u90a3\u5973\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u670d\u4f8d\u4f60\u4e5f \u884c\u3002\u4f60\u8f93\u4e86\u53c8\u5982\u4f55\uff1f\u201d\u5f52\u65e0\u9f99\u201c\u8bdd\u4e2d\u85cf\u8bdd\u201d\uff0c\u672c\u662f\u60f3\u8981\u4fae\u8fb1\u5979\u7684\u3002\u89c1\u5979\u76ee\u5149\u5b9e\u5982\u5229\u526a\uff0c\u80dc\u4f3c \u5bd2\u51b0\uff0c\u201c\u600e\u7684\u5979\u6562\u5982\u6b64\u81ea\u4fe1\uff0c\u83ab\u975e\u771f\u6709\u7684\u6043\uff1f\u201d\u4e0d\u89c9\u6253\u4e86\u4e2a\u5bd2\u6218\uff0c\u53ea\u80fd\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u5f52\u67d0\u4e0d\u60f3\u4e0e\u4f60\u8d4c\u6027\u547d\uff0c\u4ef7\u94b1\u6050\u6015\u7ed9\u5f97\u4e0d\u5408\u59d1\u5a18\u5fc3\u610f\u8fd8\u662f\u4f60\u81ea\u5df1\u5212\u51fa\u9053\u513f\u6765\u5427\u3002\u201d\u5f52\u5143 \u9f99\u9053\u3002 \u3000\u3000\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u5f52\u5e84\u4e3b\uff0c\u4f60\u607c\u6068\u6211\u5c06\u4ee4\u90ce\u6253\u5f97\u91cd\u4f24\u3002\u7167\u5b9e\u8bdd\uff0c\u6211\u6ca1\u6709\u5c06\u4ed6\u6253\u6b7b\uff0c\u5df2\u7ecf\u662f\u7ed9 \u4e86\u4f60\u7684\u9762\u5b50\u4e86\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u51b7\u7b11\u6e32\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5f52\u67d0\u5012\u662f\u53d7\u5ba0\u82e5\u60ca\u4e86\u3002\u201d\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u6f2b\u5929\u8ba8\u4ef7 \u7684\uff0c\u4f60\u82e5\u8f93\u4e86\uff0c\u6211\u53ea\u8981\u4f60\u78d5\u4e09\u4e2a\u54cd\u5934\u3002\u563f\uff0c\u563f\u3002\u4e09\u4e2a\u54cd\u5934\uff0c\u6362\u4e00\u6761\u4eba\u547d\uff0c\u8fd9\u4ef7\u94b1\u53ef\u7b97\u516c\u9053 \u5427\uff1f\u201d \u3000\u3000\u5f52\u5143\u9f99\u607c\u6012\u5df2\u6781\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u522b\u4eba\u5411\u5f52\u67d0\u78d5\u5934\u3002\u201d\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u4e8b\u60c5\uff0c \u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u63a5\u4e0d\u63a5\u53d7\u6211\u5212\u51fa\u7684\u9053\u513f\uff1f\u201d \u3000\u3000\u5f52\u5143\u9f99\u6c14\u5f97\u8138\u8272\u94c1\u9752\uff0c\u5f3a\u6291\u6012\u706b\u3002\u8bf4\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u6ca1\u6709\u8fd9\u672c\u9886\u80fd\u4ee4\u5f52\u67d0\u6298\u8170\u3002\u597d\u5427\uff0c\u5c31 \u7167\u4f60\u5212\u51fa\u7684\u9053\u513f\uff0c\u6211\u82e5\u8f93\u4e86\uff0c\u8fde\u8111\u888b\u4e5f\u5272\u4e0b\u6765\u7ed9\u4f60\uff01\u201d \u3000\u3000\u6d1b\u9633\u864e\u5a01\u9556\u5934\u73ed\u5b9a\u5c71\u7ad9\u4e86\u8d77\u6765\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u606f\u6012\uff0c\u8ba9\u6211\u6559\u8bad\u6559\u8bad\u8fd9\u4e2a\u4e2b\u5934\uff01\u201d \u3000\u3000\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201d\u73ed\u5b9a\u5c71\uff0c\u4e8f\u4f60\u8eab\u4e3a\u6d1b\u9633\u7b2c\u4e00\u5927\u9556\u5c40\u7684\u603b\u9556\u5934\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u8bc6\u4e0d\u8bc6\u5f97\u6c5f\u6e56 \u89c4\u77e9\uff1f\u8fd9\u2018\u6559\u8bad\u2019\u4e8c\u5b57\uff0c\u7528\u5f97\u6050\u6015\u4e0d\u5408\u4f60\u7684\u8eab\u4efd\u5427\uff1f\u4f60\u6c42\u6211\u6559\u8bad\u4f60\uff0c\u8fd8\u5f97\u6211\u7b54\u5e94\u4f60\u5462\uff01\u201d \u3000\u3000\u539f\u6765\u73ed\u5b9a\u5c71\u662f\u5f52\u5143\u9f99\u7684\u5f1f\u5b50\uff0c\u5982\u4eca\u662f\u4ed6\u7684\u5e08\u7236\u548c\u8fd9\u5c0f\u5973\u5b50\u7ea6\u597d\u4e86\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\u6bd4\u6b66\uff0c\u6bd4 \u6b66\u7684\u53cc\u65b9\uff0c\u5730\u4f4d\u662f\u76f8\u7b49\u7684\uff0c\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u201c\u6559\u8bad\u201d\u8c01\u3002\u73ed\u5b9a\u5c71\u7eb5\u7136\u60f3\u8981\u66ff\u4ee3\u5e08\u7236\u51fa\u9a6c\uff0c\u4e5f\u662f\u5fc5 \u987b\u90a3\u5c11\u5973\u70b9\u5934\u624d\u884c\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u4e00\u65f6\u5931\u8a00\uff0c\u7ed9\u90a3\u5c11\u5973\u595a\u843d\uff0c\u4e0d\u7981\u6ee1\u9762\u901a\u7ea2\uff0c\u8001\u62f3\u5e08\u7530\u4e58\u8349\u7ad9\u8d77\u6765\u66ff\u4ed6\u6253\u5706\u573a\u9053\uff1a \u201c\u5e08\u5f92\u6709\u5982\u7236\u5b50\uff0c\u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\uff0c\u90a3\u4e5f\u4e0d\u7b97\u4e0d\u5408\u6c5f\u6e56\u89c4\u77e9\u3002\u8bf7\u59d1\u5a18\u7ed9\u8001\u592b\u8fd9\u4e2a\u9762\u5b50\uff0c\u5148\u4e0a \u53f0\u5427\uff01\u522b\u6597\u53e3\u4e86\u3002\u201d \u3000\u3000\u5728\u5f52\u6d1b\u82f1\u7ed9\u62ac\u56de\u6765\u7684\u65f6\u5019\uff0c\u53f0\u4e0a\u7684\u620f\u65e9\u5df2\u5531\u4e0d\u4e0b\u53bb\uff0c\u6b63\u597d\u53ef\u4ee5\u4f5c\u64c2\u53f0\u3002 \u3000\u3000\u5c11\u5973\u8bf4\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4f7f\u5373\u8eab\u5982\u98de\u71d5\uff0c\u8dc3\u4e0a\u201c\u64c2\u53f0\u201d\u3002\u73ed\u5b9a\u5c71\u5219\u521a\u597d\u548c\u5979\u76f8\u53cd\uff0c\u4ed6\u662f \u4e00\u6b65\u4e00\u6b65\uff0c\u8d70\u4e0a\u53f0\u53bb\u3002 \u3000\u3000\u5f52\u5143\u9f99\u770b\u89c1\u4ed6\u4eec\u4fe9\u90fd\u4e0a\u4e86\u64c2\u53f0\uff0c\u65b9\u59cb\u653e\u4e0b\u4e00\u5757\u5fc3\u5934\u5927\u77f3\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u7a33\u6b65\u4e0a\u53f0\uff0c\u663e\u793a\u4e86\u4ed6\u7684\u4e0b\u76d8\u529f\u592b\u3002\u5916\u884c\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u4ed6\u7684\u5e08\u7236\u5219\u662f\u4e00\u770b\u5c31\u77e5\uff0c\u4ed6 \u7684\u529f\u592b\u53c8\u5df2\u6709\u4e86\u8fdb\u5883\u3002 \u3000\u3000\u800c\u4ed6\u8fd9\u6837\u7a33\u6b65\u4e0a\u53f0\uff0c\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u8981\u5e08\u7236\u77e5\u9053\u4ed6\u7684\u8fdb\u5883\u800c\u5df2\uff0c\u53e6\u4e00\u91cd\u7528\u610f\u662f\u5411\u5e08\u7236\u6697\u793a\uff0c \u4ed6\u5c06\u91c7\u7528\u6c89\u7a33\u575a\u5b9e\u7684\u6253\u6cd5\uff0c\u548c\u5bf9\u65b9\u529b\u62fc\u3002 \u3000\u3000\u5f52\u5143\u9f99\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u4ed6\u5f53\u7136\u77e5\u9053\u8fd9\u79cd\u6253\u6cd5\u6b63\u662f\u53ef\u4ee5\u5236\u90a3\u5c11\u5973\u7684\u6253\u6cd5\u3002\u90a3\u5c11\u5973\u8f7b\u529f\u9ad8 \u660e\uff0c\u8f7b\u529f\u9ad8\u660e\u7684\u516b\u5341\u4e4b\u4e5d\u90fd\u662f\u5185\u529b\u4e0d\u8db3\u7684\u3002\u4e0d\u51fa\u5f52\u5143\u9f99\u6240\u6599\uff0c\u90a3\u5c11\u5973\u5bf9\u73ed\u5b9a\u5c71\u7684\u6253\u6cd5\u679c\u7136\u597d \u50cf\u662f\u65e0\u8ba1\u53ef\u65bd\u3002 \u3000\u3000\u53ea\u89c1\u73ed\u5b9a\u5c71\u6c89\u8170\u5750\u9a6c\uff0c\u957f\u62f3\u6363\u51fa\uff0c\u864e\u864e\u751f\u98ce\uff0c\u5728\u53f0\u4e0b\u7ad9\u5f97\u8f83\u8fd1\u7684\u4eba\u90fd\u611f\u89c9\u5230\u62f3\u98ce\u6251\u9762\u3002 \u5c11\u5973\u7684\u638c\u6cd5\u867d\u7136\u6643\u52a8\uff0c\u5374\u662f\u65e0\u6cd5\u8fd1\u8eab\u3002\u6709\u7ecf\u9a8c\u7684\u4eba\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u5979\u5fc5\u8d25\u65e0\u7591\u3002 \u3000\u3000\u5728\u4f17\u4eba\u7ed9\u73ed\u8d70\u5c71\u7684\u559d\u5f69\u58f0\u4e2d\uff0c\u90a3\u5973\u5b50\u9000\u800c\u590d\u4e0a\uff0c\u6253\u6cd5\u7a81\u53d8\u3002\u867d\u7136\u4ecd\u662f\u7ed5\u8eab\u6e38\u6597\uff0c\u4f46\u5df2\u6613 \u638c\u4e3a\u6307\u3002\u5979\u9a88\u6307\u5982\u621f\uff0c\u7528\u7684\u5374\u4e0d\u662f\u70b9\u7a74\u529f\u592b\uff0c\u800c\u662f\u4ee5\u523a\u6233\u4e3a\u4e3b\u7684\u5251\u6cd5\u3002\u4e24\u6839\u6307\u5934\uff0c\u5b9b\u5982\u4e00\u67c4 \u77ed\u5251\u3002 \u3000\u3000\u4ee5\u6307\u4ee3\u5251\uff0c\u5df2\u662f\u96be\u80fd\u3002\u800c\u5979\u7684\u201c\u5251\u6cd5\u201d\u7a76\u7adf\u5c5e\u4e8e\u4f55\u5bb6\u4f55\u6d3e\uff0c\u53f0\u4e0b\u7684\u4eba\uff0c\u7adf\u662f\u6ca1\u6709\u4e00\u4e2a\u770b \u5f97\u51fa\u6765\u3002 \u3000\u3000\u53cc\u6307\u6240\u53d7\u7684\u53cd\u5e94\u4e4b\u529b\u6bd4\u4f38\u5f00\u624b\u638c\u4e3a\u8f7b\uff0c\u66f4\u52a0\u53ef\u4ee5\u63a5\u8fd1\u5bf9\u65b9\u4e86\u3002\u65c1\u4eba\u770b\u6765\uff0c\u53ea\u89c1\u5979\u7684\u4e24\u6839 \u6307\u5934\u5728\u73ed\u5b9a\u5c71\u773c\u76ae\u5e95\u4e0b\u70b9\u70b9\u6233\u6233\uff0c\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u4ee5\u6316\u4ed6\u7684\u4e00\u53cc\u773c\u73e0\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u773c\u82b1\u64a9\u4e71\uff0c\u5fc3\u91cc\u4ea6\u662f\u4e0d\u7981\u6709\u70b9\u7740\u614c\uff0c\u53ea\u6015\u7a0d\u4e00\u758f\u795e\uff0c\u5c31\u8981\u53d8\u6210\u778e\u5b50\u3002\u4ed6\u7684\u6253\u6cd5\u672c \u6765\u662f\u4ee5\u6c89\u7a33\u4e3a\u4e3b\u7684\uff0c\u6b64\u65f6\u552f\u6050\u6709\u5931\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u6709\u70b9\u66b4\u8e81\u8d77\u6765\uff0c\u53ea\u76fc\u901f\u6218\u901f\u51b3\u3002 \u3000\u3000\u5267\u6597\u4e2d\u90a3\u5973\u5b50\u5ffd\u98de\u8eab\u8dc3\u8d77\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5012\u7ffb\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u53cc\u6307\u4f7f\u51fa\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\u7684 \u5251\u6cd5\uff0c\u75be\u523a\u4ed6\u7684\u773c\u73e0\u3002\u8eab\u5b50\u60ac\u7a7a\uff0c\u7a7a\u95e8\u56db\u9732\u3002\u73ed\u5b9a\u5c71\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u5979\u7684\u8fd9\u4e2a\u7834\u7efd\uff0c\u5fc3\u5934\u5927 \u559c\uff0c\u7acb\u5373\u4fbf\u662f\u62f3\u638c\u517c\u65bd\uff0c\u4e00\u62db\u201c\u949f\u9f13\u9f50\u9e23\u201d\uff0c\u62e6\u8170\u622a\u51fb\uff01 \u3000\u3000\u54ea\u77e5\u5c11\u5973\u8fd9\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\u4e43\u662f\u865a\u62db\uff0c\u9661\u7136\u95f4\u53d8\u9a88\u6307\u523a\u622a\u4e4b\u52bf\u4e3a\u4e09\u6307\u52fe\u62ff\uff0c\u5feb\u5982\u95ea \u7535\uff0c\u6263\u4f4f\u4e86\u73ed\u5b9a\u5c71\u7684\u8109\u95e8\u3002\u53ea\u662f\u8f7b\u8f7b\u4e00\u5e26\uff0c\u767b\u65f6\u628a\u73ed\u5b9a\u5c71\u94c1\u5854\u822c\u8eab\u8eaf\u7529\u4e86\u8d77\u6765\u3002\u4e24\u4eba\u626f\u7ebf\u4f3c \u7684\u5728\u534a\u7a7a\u4e2d\u6253\u4e86\u4e2a\u5927\u7ffb\uff0c\u5c11\u5973\u811a\u843d\u5b9e\u5730\uff0c\u73ed\u5b9a\u5c71\u5219\u5df2\u8eab\u5b50\u60ac\u7a7a\u3002 \u3000\u3000\u5c31\u5728\u4f17\u4eba\u76ee\u77aa\u53e3\u5446\u4e4b\u9645\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u6e05\u8106\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u4e0b\u53bb\u5427\uff01\u201d\u4e00\u4e2a\u65cb\u98ce \u6025\u821e\uff0c\u628a\u73ed\u5b9a\u5c71\u629b\u843d\u201c\u64c2\u53f0\u201d\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u8dcc\u843d\u53f0\u4e0b\uff0c\u53cc\u819d\u7740\u5730\uff0c\u597d\u50cf\u662f\u7ed9\u5c11\u5973\u78d5\u5934\u4e00\u822c\u3002\u5c11\u5973\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u89c4\u77e9\u662f \u65e9\u5df2\u8bb2\u597d\u4e86\u7684\uff0c\u4f60\u4e0d\u5fc5\u66ff\u5e08\u7236\u78d5\u5934\u3002\u201d \u3000\u3000\u5f52\u5143\u9f99\u5927\u6012\u559d\u9053\uff1a\u201c\u5996\u5973\u80c6\u6562\u53e3\u51fa\u72c2\u8a00\u5f52\u67d0\u4e2a\u65e5\u4e0e\u4f60\u2014\u2014\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5750\u5728\u4ed6\u8eab\u8fb9\u7684\u795e\u79d8\u5ba2\u4eba\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5c06\u4ed6\u6309\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5f52\u5e84\u4e3b\u4f55\u5fc5\u4e3a\u8fd9 \u70b9\u5c0f\u4e8b\u52a8\u6012\u3002\u201d\u4ed6\u8d70\u5230\u53f0\u524d\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u5954\u8dc3\u84c4\u52bf\u3002\u8eab\u5f62\u4fbf\u5373\u5e73\u5730\u62d4\u8d77\uff0c\u4e0a\u4e86\u201c\u64c2\u53f0\u201d\u3002\u5c11\u5973 \u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u7684\u53bb\u4e86\u4e00\u4e2a\uff0c\u53c8\u6765\u4e86\u4e00\u4e2a\uff1f\u4f60\u7684\u5e74\u7eaa\u597d\u50cf\u6bd4\u5f52\u5143\u9f99\u4e5f\u5e74\u8f7b\u4e0d\u4e86 \u51e0\u5c81\uff0c\u96be\u9053\u4f60\u4e5f\u662f\u5f52\u5143\u9f99\u7684\u5f1f\u5b50\u5417\uff1f\u201d \u3000\u3000\u90a3\u795e\u79d8\u5ba2\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5f52\u5e84\u4e3b\u8bf7\u6765\u7684\u5ba2\u4eba\uff0c\u770b\u4e0d\u8fc7\u773c\u4f60\u4fae\u8fb1\u6210\u540d\u7684\u524d\u8f88\uff01\u201d \u3000\u3000\u5c11\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u80fd\u591f\u66ff\u5f52\u5728\u4e3b\u63a5\u4e0b\u6211\u521a\u624d\u5212\u51fa\u7684\u9053\u513f\u5417\uff1f\u201c\u5f52\u5143\u9f99\u7acb\u5373\u7ad9\u8d77\u6765\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4f60\u82e5\u8d62\u5f97\u4e86\u4ed6\uff0c\u6211\u7ed9\u4f60\u78d5\u4e00\u767e\u4e2a\u54cd\u5934\u4e5f\u53ef\u4ee5\uff01 \u3000\u3000\u201d \u3000\u3000\u90a3\u795e\u79d8\u4eba\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5462\u3002\u59d1\u5a18\uff0c\u4f60\u522b\u4ee5\u4e3a\u6211\u4eec\u662f\u60f3\u7528\u8f66\u8f6e\u6218\u5360\u4f60\u7684\u4fbf \u5b9c\uff0c\u8981\u662f\u6211\u8f93\u7ed9\u4e86\u4f60\uff0c\u5f52\u5e84\u56fa\u7136\u4efb\u51ed\u4f60\u6765\u5904\u7f6e\uff0c\u6211\u4e5f\u53ef\u4ee5\u628a\u8111\u888b\u5272\u4e0b\u6765\u7ed9\u4f60\u3002\u800c\u4e14\u2014\u2014\u201d\u8bf4 \u5230\u6b64\u5904\uff0c\u4e00\u53cc\u773c\u671d\u90a3\u5c11\u5973\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u3002\u5c11\u5973\u9053\uff1a\u201c\u54e6\uff0c\u8fd8\u6709\u4ec0\u4e48\u800c\u4e14\u5417\uff1f\u6709\u8bdd\u5feb\u8bf4\uff0c\u76ef\u7740 \u6211\u5e72\u5417\uff1f\u201d \u3000\u3000\u90a3\u795e\u79d8\u5ba2\u4eba\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u6253\u4e86\u4e00\u573a\uff0c\u6211\u4e5f\u4e0d\u60f3\u5360\u4f60\u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u597d\uff0c\u4f46\u4ee5\u6307\u4ee3\u5251\uff0c\u6050\u6015\u4e0d\u80fd\u66f2\u5c3d\u5176\u5999\u3002\u6211\u77e5\u9053\u4f60\u8eab\u4e0a\u85cf\u6709\u5b9d\u5251\uff0c \u5f88\u60f3\u5f00\u5f00\u773c\u754c\uff0c\u8bf7\u4f60\u4eae\u5251\u8d50\u62db\uff01\u201d \u3000\u3000\u5c11\u5973\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8fd9\u4eba\u7684\u773c\u529b\u5012\u662f\u4e0d\u9519\uff0c\u5c45\u7136\u80fd\u591f\u770b\u51fa\u6211\u8eab\u85cf\u5b9d\u5251\u3002 \u3000\u3000\u4ed6\u7684\u6b66\u529f\u6df1\u6d45\u672a\u77e5\uff0c\u4f46\u51ed\u4ed6\u8fd9\u4efd\u773c\u529b\u548c\u521a\u624d\u6296\u9732\u7684\u90a3\u624b\u8f7b\u529f\uff0c\u5012\u4e5f\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002 \u3000\u3000\u201c\u597d\u5427\uff0c\u201d\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u89c1\u8bc6\u6211\u7684\u5251\u6cd5\uff0c\u90a3\u54b1\u4eec\u5c31\u8f83\u91cf\u5175\u5203\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u89e3\u4e0b\u8170\u5e26\uff0c\u8fce\u98ce\u4e00\u79d1\uff0c\u597d\u50cf\u91d1\u8749\u892a\u58f3\u4e00\u822c\uff0c\u5916\u9762\u4e00\u5c42\u8584\u8584\u7684\u76ae\u5957\u892a\u4e0b\uff0c\u9732\u51fa\u4e00\u67c4 \u8584\u5f97\u900f\u660e\u7684\u5b9d\u5251\uff0c\u5251\u7684\u5f00\u5f0f\u751a\u4e3a\u5947\u7279\uff0c\u5251\u8eab\u72ed\u957f\uff0c\u5251\u67c4\u6781\u77ed\uff0c\u8bf4\u662f\u201d\u5251\u67c4\u201d\u3002\u5176\u5b9e\u53ea\u662f\u5728\u4e00 \u7aef\u88c5\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u94dc\u73af\uff0c\u5c11\u5973\u7684\u624b\u6307\u52fe\u5728\u73af\u4e2d\uff0c\u53ea\u7528\u4e24\u6839\u6307\u5934\u7684\u529b\u91cf\u4f7f\u52a8\u8fd9\u628a\u5b9d\u5251\uff0c\u5251\u8eab\u53ef \u4ee5\u968f\u610f\u5f2f\u66f2\uff0c\u8bf4\u5b83\u662f\u5251\uff0c\u6bcb\u5b81\u8bf4\u66f4\u50cf\u4e00\u6761\u8f6f\u97ad\u3002 \u3000\u3000\u539f\u6765\u5c11\u5973\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u4ee5\u767e\u7ec3\u7cbe\u94a2\u6253\u6210\u7684\u8f6f\u5251\uff0c\u53ef\u4ee5\u5316\u4f5c\u7ed5\u6307\u67d4\u7684\u3002 \u3000\u3000\u4e0d\u7528\u4e4b\u65f6\uff0c\u85cf\u4e8e\u76ae\u5957\uff0c\u7f20\u5728\u8eab\u4e0a\uff0c\u5916\u8868\u770b\u6765\uff0c\u5c31\u662f\u4e00\u6761\u76ae\u5e26\u4e86\u3002 \u3000\u3000\u201c\u4f60\u7528\u4ec0\u4e48\u5175\u5668\uff0c\u4eae\u51fa\u6765\u5427\uff01\u201c\u5c11\u5973\u53cc\u6307\u6263\u7740\u5b9d\u5251\uff0c\u811a\u6b65\u4e0d\u4e01\u4e0d\u516b\uff0c\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u8bf4 \u9053\u3002 \u3000\u3000\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u6211\u5c31\u7528\u53cc\u8089\u638c\uff0c\u9886\u6559\u59d1\u5a18\u7684\u7cbe\u5999\u5251\u672f\u3002\u4f60\u65e0\u987b\u987e\u5fcc\uff0c\u6211\u82e5\u4f24\u5728\u4f60\u7684\u5251\u4e0b\u3002 \u6b7b\u800c\u65e0\u6028\u3002\u800c\u4e14\u6211\u8fd8\u53ef\u4ee5\u4e00\u767e\u62db\u4e3a\u9650\uff0c\u767e\u62db\u4e4b\u5185\uff0c\u5373\u4f7f\u4f60\u4f24\u4e0d\u4e86\u6211\uff0c\u4e5f\u7b97\u6211\u8f93\u3002\u8fd9\u6837\uff0c\u5927\u6982 \u53ef\u8bf4\u5f97\u662f\u6211\u6ca1\u5360\u4f60\u5148\u6253\u4e86\u4e00\u573a\u7684\u4fbf\u5b9c\u4e86\u5427\uff1f\u201d \u3000\u3000\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u5c11\u5973\u8fd9\u628a\u5b9d\u5251\u975e\u540c\u51e1\u54c1\uff0c\u8fd9\u4e2a\u5ba2\u4eba\u53ea\u51ed\u8089\u638c\u5bf9\u4ed8\uff0c\u800c\u4e14\u8fd8\u9650\u5b9a\u767e\u62db\uff0c\u7684 \u786e\u662f\u53ef\u4ee5\u62b5\u6d88\u5c11\u5973\u5148\u6253\u4e00\u573a\u7684\u201c\u5403\u4e8f\u201d\u800c\u767e\u4f59\u4e86\u3002 \u3000\u3000\u5c11\u5973\u4fa7\u76ee\u659c\u7768\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7528\u4f60\u8ba9\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8ba9\u4f60\u3002\u4f60\u559c\u6b22\u7528\u4ec0\u4e48\u5175\u5668\u5c31 \u7528\u4ec0\u4e48\u5175\u5668\uff0c\u66f4\u65e0\u987b\u9650\u5b9a\u767e\u62db\uff0c\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\u3002\u6211\u8bf4\u8fc7\u6b7b\u800c\u65e0\u6028\uff0c\u5c31\u7edd\u4e0d\u4f1a\u53cd\u6094\u3002\u59d1\u5a18\uff0c\u4f60\u8fdc\u6765\u662f \u5ba2\uff0c\u4e5f\u522b\u5ba2\u6c14\uff0c\u5feb\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u5c11\u5973\u4f3c\u4e4e\u5fcd\u53d7\u4e0d\u4e86\u4ed6\u7684\u8fd9\u4efd\u8f7b\u89c6\uff0c\u5fae\u6709\u6012\u8272\uff0c\u8bf4\u4e86\u4e00\u4e2a\u201c\u597d\u201d\uff0c\u5530\u7684\u4e00\u5251\u5c31\u523a\u8fc7\u53bb\uff0e\u5c11 \u5973\u5c55\u5f00\u5251\u52bf\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u5de6\u53f3\u4e00\u62d0\uff0c\u53f3\u8fb9\u4e00\u515c\uff0c\u8eab\u5f62\u771f\u662f\u77ac\u606f\u767e\u53d8\u3002\u795e\u79d8\u5ba2\u8fde\u5288\u4e09\u638c\uff0c\u90fd\u6ca1 \u6709\u5288\u7740\u3002\u4f46\u638c\u98ce\u6fc0\u70c8\uff0c\u5374\u5df2\u5439\u5f97\u5979\u8863\u8882\u98d8\u98d8\u3002\u4e0d\u8fc7\uff0c\u90a3\u5c11\u5973\u7684\u5251\u6cd5\u867d\u7136\u5947\u602a\uff0c\u5374\u4e5f\u6ca1\u6709\u523a\u7740 \u4ed6\u3002\u4e00\u8fd1\u8eab\uff0c\u5251\u7684\u843d\u70b9\u5c31\u7ed9\u4ed6\u7684\u638c\u529b\u8361\u6b6a\u4e86\u3002 \u3000\u3000\u5c11\u5973\u8d8a\u8f6c\u8d8a\u5feb\uff0c\u5251\u6cd5\u4e5f\u8d8a\u53d8\u8d8a\u5947\uff0c\u7adf\u4f3c\u628a\u201c\u516b\u5366\u6e38\u8eab\u638c\u201d\u7684\u638c\u6cd5\u878d\u4f1a\u5728\u5251\u6cd5\u4e4b\u4e2d\uff0c\u5bf9\u65b9 \u7684\u638c\u529b\u53ea\u8981\u7a0d\u6709\u7167\u987e\u4e0d\u5230\u4e4b\u5904\uff0c\u5c31\u4f1a\u7ed9\u5979\u4e58\u865a\u800c\u5165\u3002 \u3000\u3000\u795e\u79d8\u5ba2\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u4f7f\u51fa\u770b\u5bb6\u672c\u9886\uff0c\u53ea\u6015\u5f53\u771f\u96be\u4ee5\u5236\u670d\u8fd9\u4e2a\u4e2b\u5934\u3002\u201d \u6253\u6cd5\u4e00\u53d8\uff0c\u820d\u521a\u731b\u7684\u638c\u6cd5\u4e0d\u7528\uff0c\u5374\u7528\u4e24\u6839\u6307\u5934\u70b9\u70b9\u6233\u6233\u3002 \u3000\u3000\u5c11\u5973\u521a\u624d\u4e5f\u66fe\u4ee5\u6307\u4ee3\u5251\uff0c\u4e0d\u8fc7\u8fd9\u795e\u79d8\u5ba2\u5374\u5e76\u4e0d\u662f\u7528\u6307\u5934\u6765\u4f7f\u51fa\u5251\u6cd5\uff0c\u751a\u81f3\u4e5f\u4e0d\u50cf\u662f\u7528\u70b9 \u7a74\u7684\u624b\u6cd5\uff0c\u573a\u4e2d\u7684\u6b66\u5b66\u884c\u5bb6\u90fd\u770b\u5f97\u83ab\u540d\u5176\u5999\uff0c\u4e5f\u5728\u4e3a\u4ed6\u62c5\u5fc3\u3002\u4ed6\u90a3\u4e48\u521a\u731b\u7684\u638c\u6cd5\u90fd\u4f3c\u4e4e\u5c01\u95ed \u4e0d\u4f4f\uff0c\u53ea\u51ed\u4e24\u6839\u6307\u5934\uff0c\u5c31\u80fd\u62b5\u6321\u5f97\u4e86\uff1f\u5c11\u5973\u7684\u5251\u6cd5\u7adf\u4f3c\u53d7\u4e86\u514b\u5236\uff0c\u6ca1\u6709\u521a\u624d\u90a3\u4e48\u7075\u6d3b\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u662f\u795e\u79d8\u5ba2\u72ec\u95e8\u7684\u201c\u91d1\u521a\u6307\u201d\u529f\u592b\uff0c\u4ed6\u82e6\u7ec3\u4e86\u5341\u5e74\uff0c\u65b9\u59cb\u7ec3\u6210\u7684\u3002\u5c11\u6797\u6d3e\u4e5f\u6709\u201c\u91d1 \u521a\u6307\u201d\uff0c\u4e0d\u8fc7\u4ed6\u5148\u7ec3\u6210\u91d1\u94a2\u638c\u548c\u7ef5\u638c\uff0c\u518d\u628a\u8fd9\u4e24\u79cd\u638c\u529b\u878d\u5408\u3002\u51dd\u805a\u800c\u7ec3\u6210\u91d1\u521a\u6307\u7684\uff0c\u91d1\u521a\u62ff \u81f3\u521a\uff0c\u7ef5\u638c\u81f3\u67d4\uff0c\u521a\u67d4\u76f8\u6d4e\uff0c\u800c\u51dd\u6210\u4ed6\u72ec\u521b\u91d1\u521a\u6307\u529b\uff0c\u867d\u7136\u672a\u5fc5\u5c31\u80dc\u5f97\u8fc7\u5c11\u6797\u6d3e\u7684\u91d1\u521a\u6307\u3002 \u5374\u6bd4\u5c11\u6797\u6d3e\u7684\u66f4\u96be\u5e94\u4ed8\u3002\u5c24\u5176\u5bf9\u65b9\u82e5\u662f\u4e00\u4e2a\u7ecf\u9a8c\u4e0d\u592a\u4e30\u5bcc\u7684\u65b0\u624b\uff0c\u90a3\u5c31\u66f4\u52a0\u5bb9\u6613\u53d7\u4ed6\u8ff7\u60d1 \u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6309\u8bf4\u89c1\u95fb\u4e0d\u4f1a\u5341\u5206\u5e7f\u535a\uff0c\u4e34\u9635\u7684\u7ecf\u9a8c\u4e5f\u4e0d\u4f1a\u592a\u8fc7\u4e30\u5bcc\u7684\u3002\u4f46\u5979\u5374\u4f3c\u770b \u5f97\u51fa\u8fd9\u4e0d\u662f\u5c11\u6797\u6d3e\u7684\u91d1\u521a\u6307\uff0c\u5e76\u6ca1\u4e0a\u5f53\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u55e4\u3001\u55e4\u58f0\u54cd\uff0c\u4e5f\u4e0d\u77e5\u662f\u90a3\u5c11\u5973\u5251\u5c16\u6296\u52a8\u7684\u58f0\u54cd\u8fd8\u662f\u4ed6\u8fd9\u91d1\u521a\u6307\u529b\u7684\u7834\u7a7a\u4e4b \u58f0\u3002\u5c11\u5973\u5df1\u7ecf\u5c3d\u529b\u907f\u514d\u548c\u4ed6\u7684\u91d1\u521a\u62cd\u786c\u78b0\uff0c\u4f46\u8fd8\u662f\u8eb2\u907f\u4e0d\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5c11\u5973\u7684 \u5b9d\u5251\u7ed9\u4ed6\u5f39\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u795e\u5bc6\u5ba2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8ba4\u8f93\u5427\uff01\u201d\u4ed6\u8fd9\u91d1\u521a\u6307\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u5bfb\u5e38\u5200\u5251\uff0c\u7ed9\u4ed6\u4e00 \u5f39\uff0c\u5c31\u53ef\u4ee5\u65ad\u4e3a\u4e24\u622a\u3002\u8fd9\u5c11\u5973\u7684\u529f\u529b\u8fdc\u4e0d\u5982\u4ed6\uff0c\u6599\u60f3\u4e5f\u7981\u53d7\u4e0d\u8d77\u8fd9\u4e00\u5f39\u4e4b\u529b\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u7684\u4f30\u8ba1\u8fd8\u662f\u72af\u4e86\u9519\u8bef\u3002 \u3000\u3000\u5c11\u5973\u8eab\u5f62\u503e\u4fa7\uff0c\u6643\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u5012\u4e0b\u53bb\u3002\u5979\u8fd9\u4e00\u4fa7\u4e00\u6643\uff0c\u6b63\u662f\u8fd0 \u7528\u6b66\u5b66\u4e2d\u7684\u201c\u5378\u201d\u5b57\u8bc0\uff0c\u89e3\u6d88\u4e86\u5bf9\u65b9\u90a3\u80a1\u4e00\u5f39\u7684\u529b\u9053\u3002 \u3000\u3000\u66f4\u51fa\u4e4e\u795e\u79d8\u5ba2\u610f\u6599\u4e0d\u5230\u7684\u662f\uff0c\u5c11\u5973\u7684\u5251\u4e5f\u6ca1\u6709\u7ed9\u4ed6\u5f39\u5f97\u8131\u624b\u5760\u5730\u3002 \u3000\u3000\u5979\u7684\u5b9d\u5251\u662f\u53ef\u4ee5\u5316\u4f5c\u7ed5\u6307\u67d4\u7684\u8f6f\u5251\uff0c\u53d7\u4e86\u91d1\u521a\u6307\u529b\uff0c\u5f2f\u66f2\u6210\u4e3a\u5f27\u5f62\uff0c\u7a81\u7136\u4e00\u4e2a\u53cd\u5f39\uff0c\u968f \u7740\u5c11\u5973\u7684\u8eab\u5f62\u659c\u6251\uff0c\u7adf\u7136\u4ece\u90a3\u795e\u79d8\u5ba2\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u523a\u6765\u3002\u4ece\u4ed6\u7684\u80a9\u540e\u7ecf\u8fc7\uff0c\u523a\u5411\u4ed6\u7684\u54bd \u5589\u3002 \u3000\u3000\u4e0d\u8fc7\u795e\u79d8\u5ba2\u4ea6\u662f\u4e34\u5371\u4e0d\u4e71\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u6293\u3002\u4ed6\u7684\u8111\u540e\u5c31\u50cf\u957f\u7740\u773c\u775b\u4e00\u80a1\u3002\u8fd9 \u4e00\u6293\u4e5f\u6b63\u662f\u6293\u5411\u5c11\u5973\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u662f\u5c11\u5973\u7684\u5251\u5feb\u5462\uff1f\u8fd8\u662f\u4ed6\u7684\u624b\u5feb\u5462\uff1f\u6216\u662f\u4e00\u4e2a\u88ab\u523a\u7a91\u54bd\u5589\uff0c\u4e00\u4e2a\u88ab\u6293\u788e\u7435\u7436\u9aa8\uff0c\u5f04\u6210\u4e24 \u8d25\u4ff1\u4f24\u5462\uff1f\u8fd9\u5239\u90a3\u95f4\uff0c\u5168\u573a\u9759\u5f97\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff0c\u5f53\u771f\u662f\u4eba\u4eba\u5c4f\u606f\u4ee5\u5f85\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\u3002\u5ffd\u542c\u5f97\u6709\u5c16\u9510\u7684\u7834\u7a7a\u4e4b\u58f0\uff01 \u3000\u3000\u795e\u79d8\u5ba2\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u4e00\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u4fbf\u77e5\u662f\u6709\u6697\u5668\u88ad\u6765\u3002\u800c\u4e14\u53d1\u6697\u5668\u4e4b\u4eba\uff0c\u529f\u529b\u975e \u540c\u5c0f\u53ef\uff0c\u767e\u5fd9\u4e2d\u4ed6\u53ea\u597d\u9661\u5730\u4e00\u4e2a\u51e4\u70b9\u5934\uff0c\u5148\u8eb2\u5f00\u6697\u5668\u518d\u8bf4\u3002 \u3000\u3000\u6697\u5668\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u6253\u7740\u90a3\u5c11\u5973\u7684\u5251\u5c16\uff0c\u8fd9\u4e00\u6b21\u5c11\u5973\u7684\u5251\u8131\u624b\u843d\u5728\u53f0\u4e0a \u4e86\u3002 \u3000\u3000\u8fd9\u6697\u5668\u7adf\u7136\u662f\u4e00\u679a\u5c0f\u5c0f\u7684\u94dc\u94b1\u3002 \u3000\u3000\u53d1\u6697\u5668\u7684\u4eba\u662f\u4e2a\u6d53\u7709\u5927\u773c\u7684\u5c11\u5e74\uff0c\u6b64\u65f6\u4ea6\u5df2\u8df3\u4e0a\u64c2\u53f0\u6765\u4e86\u3002\u5c11\u5973\u6012\u9053\uff1a\u201c\u4f60\u4eec\u8981\u4e0d\u8981 \u8138\uff0c\u7528\u8f66\u8f6e\u6218\u8fd8\u4e0d\u7b97\uff0c\u8fd8\u8981\u7528\u5230\u5077\u88ad\u7684\u624b\u6bb5\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u4e5f\u5728\u9a82\u90a3\u5c11\u5e74\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u4e0e\u8fd9\u4f4d\u59d1\u5a18\u6bd4\u6b66\uff0c\u4f60\u56e0\u4f55\u4e0a\u6765\u63d2\u624b\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u90fd\u628a\u8fd9\u4e2a\u5c11\u5e74\u5f53\u4f5c\u5bf9\u65b9\u7684\u5e2e\u624b\u3002 \u3000\u3000\u5c11\u5e74\u82e6\u7b11\u9053\uff1a\u201c\u771f\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\u3002\u6211\u662f\u4e0d\u613f\u89c1\u5230\u6b7b\u4f24\uff0c\u624d\u66ff\u4f60\u4eec\u5316\u89e3\u8fd9\u4e00 \u62db\u7684\u3002\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u4ed6\u5148\u6307\u7740\u90a3\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u628a\u8fd9\u4f4d\u59d1\u5a18\u7684\u5251\u6253\u843d\uff0c\u5979\u8fd9\u4e00\u5251\u5c31\u53ef\u4ee5\u7a7f \u8fc7\u4f60\u7684\u5589\u5499\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u5316\u89e3\uff0c\u5979\u8fd9\u4e00\u5251\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u523a\u6b7b\u6211\uff01\u201d \u3000\u3000\u5c11\u5e74\u4f3c\u7b11\u975e\u7b11\u7684\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff0c\u4f46\u53ef\u60dc\u521a\u624d\u90a3\u62db\u662f\u4e0d\u53ef\u80fd\u4e1d\u6beb\u4e0d\u5dee\u7684\u91cd\u6f14\u7684\u3002\u201d\u8981\u77e5 \u9ad8\u624b\u6bd4\u6597\uff0c\u662f\u8bb2\u7a76\u51fa\u5947\u5236\u80dc\u7684\u3002\u6b66\u529f\u8f83\u9ad8\u7684\u4e00\u65b9\uff0c\u7531\u4e8e\u6ca1\u6709\u5fc3\u7406\u4e0a\u7684\u51c6\u5907\uff0c\u78b0\u4e0a\u5bf9\u65b9\u7684\u5947 \u62db\uff0c\u5f80\u5f80\u4e5f\u4f1a\u843d\u8d25\u3002\u4f46\u5728\u91cd\u6f14\u7684\u65f6\u5019\uff0c\u5f7c\u6b64\u90fd\u5df2\u77e5\u9053\u5bf9\u65b9\u5c06\u7528\u4ec0\u4e48\u62db\u6570\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u201c\u51fa \u5947\u201d\u53ef\u8a00\uff1f\u800c\u4e14\u51fa\u62db\u7684\u5feb\u6162\uff0c\u8eab\u6cd5\u7684\u5de7\u62d9\u7b49\u7b49\uff0c\u5728\u91cd\u6f14\u7684\u65f6\u5019\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd\u548c\u4e0a\u4e00\u6b21\u4e1d\u6beb\u4e0d \u5dee\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u751f\u6b7b\u5feb\u4e8e\u4e00\u77ac\uff0c\u6781\u5fae\u5c0f\u7684\u5dee\u522b\uff0c\u5c31\u8db3\u4ee5\u9020\u6210\u4e0d\u540c\u7684\u7ed3\u679c\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u795e\u79d8\u5ba2\u8bf4\u90a3\u5c11\u5973\u6839\u672c\u4e0d\u53ef\u80fd\u5c06\u4ed6\u523a\u6b7b\uff0c\u5012\u4e5f\u4e0d\u662f\u4e8b\u540e\u7684\u5439\u725b\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u6b66\u529f\u9020\u8be3\uff0c\u62fc\u7740\u53d7\u4e00\u70b9\u4f24\uff0c\u8fd8\u662f\u53ef\u4ee5\u64d2\u83b7\u90a3\u5c11\u5973\u7684\u3002\u4e0d\u8fc7\uff0c\u4ed6\u5bf9\u8fd9\u5c11\u5e74\u8bf4\u7684\u65e0\u6cd5 \u53cd\u9a73\uff0c\u53ea\u80fd\u6c14\u5728\u5fc3\u5934\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6216\u8bb8\u4f60\u771f\u6709\u628a\u63e1\u3002\u4f46\u6211\u53ef\u4e0d\u6562\u8ba9\u4f60\u628a\u6027\u547d\u6765\u8bd5\u3002\u4f60\u82e5\u602a\u6211\u591a \u4e8b\uff0c\u5f85\u4e00\u4f1a\uff0c\u6211\u81ea\u6709\u529e\u6cd5\u8865\u507f\u4f60\u7684\u635f\u5931\u3002\u201d \u3000\u3000\u4ec0\u4e48\u53eb\u505a\u201c\u8865\u507f\u635f\u5931\uff0c\u5c11\u5e74\u6ca1\u6709\u7acb\u5373\u89e3\u91ca\u3002\u4f17\u4eba\u90fd\u662f\u4e0d\u61c2\uff0c\u795e\u79d8\u5ba2\u4ea6\u662f\u731c\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u5c11\u5e74\u63a5\u7740\u5bf9\u90a3\u5c11\u5973\u9053\uff1a\u201c\u521a\u624d\u4f60\u90a3\u4e00\u5251\uff0c\u867d\u7136\u6709\u53ef\u80fd\u523a\u7a7f\u4ed6\u7684\u5589\u5499\uff0c\u4f46\u4f60\u6050\u6015\u4e5f\u96be\u514d\u53d7 \u4f24\uff0c\u4f60\u627f\u8ba4\u5417\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u6012\u9053\uff1a\u201c\u4f55\u53ea\u53d7\u4f24\uff0c\u6211\u90a3\u4e00\u6293\u53ef\u4ee5\u6293\u788e\u5979\u7684\u7435\u7436\u9aa8\uff01\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u5c31\u7b97\u662f\u6709\u8fd9\u4e2a\u53ef\u80fd\u5427\uff0c\u4f46\u6293\u788e\u7435\u7436\u9aa8\u4e5f\u53ea\u80fd\u8bf4\u662f\u53d7\u4f24\u5440\u3002\u6bd4\u5979\u6709\u53ef\u80fd\u523a \u7a7f\u4f60\u7684\u5589\u5499\uff0c\u603b\u8fd8\u7b97\u662f\u597d\u4e9b\uff01\u201d \u3000\u3000\u4ed6\u8bc4\u8bba\u53cc\u65b9\u7684\u6740\u7740\u90fd\u7528\u4e0a\u201c\u53ef\u80fd\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u795e\u79d8\u5ba2\u53c8\u662f\u8981\u53cd\u9a73\u4e5f\u65e0\u4ece\u53cd\u9a73\u3002 \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u597d\uff0c\u4eba\u8fd9\u6837\u8bf4\u8fd8\u7b97\u516c\u5e73\uff0c\u6211\u53ef\u4ee5\u63a5\u53d7\u3002\u90a3\u4e48\u4f9d\u4f60\u4e4b\u89c1\u2014\u2014\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4f9d\u6211 \u4e4b\u89c1\uff0c\u4f60\u548c\u4ed6\u8fd9\u4e00\u573a\u53ef\u4ee5\u7b97\u4f5c\u6253\u548c\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6664\uff0c\u4f60\u7684\u51b3\u65ad\uff0c\u867d\u7136\u6709\u70b9\u504f\u8892\u6211\u7684\u5bf9\u624b\uff0c\u6211\u4e5f\u53ef\u4ee5\u63a5\u53d7\u3002 \u3000\u3000\u597d\uff0c\u5c31\u7b97\u8fd9\u4e00\u573a\u6253\u548c\u5427\uff0c\u90a3\u4e48\u6309\u89c4\u77e9\uff0c\u4ed6\u66ff\u5f52\u5143\u9f99\u63a5\u4e0b\u6765\u7684\u9053\u513f\u5c31\u4e0d\u80fd\u7b97\u6570\u4e86\uff01\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u82e5\u4f60\u4eec\u53cc\u65b9\u540c\u610f\u7b97\u662f\u6253\u548c\uff0c\u90a3\u5f53\u7136\u662f\u53ea\u80fd\u7531\u5f52\u5e84\u4e3b\u6765\u548c\u4f60\u4f5c\u4e2a\u4e86\u65ad \u4e86\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u597d\uff0c\u5f52\u5e84\u4e3b\uff0c\u4f60\u542c\u89c1\u6ca1\u6709\u3002\u6211\u4e0d\u6015\u5403\u4e86\u591a\u6253\u4e24\u573a\u7684\u4e8f\uff0c\u4f60\u4e0a\u53f0\u548c\u6211\u51b3\u4e00\u80dc\u8d1f \u5427\uff01\u201d\u795e\u79d8\u5ba2\u5df2\u7ecf\u8bd5\u8fc7\u8fd9\u5c11\u5973\u7684\u6b66\u529f\uff0c\u6df1\u77e5\u5f52\u5143\u9f99\u7edd\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002 \u3000\u3000\u201c\u4e0d\uff0c\u6211\u4e0d\u540c\u610f\uff01\u201d\u795e\u79d8\u5ba2\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u662f\u66ff\u5f52\u5e84\u4e3b\u63a5\u4e0b\u8fd9\u4f4d\u59d1\u5a18\u5212\u4e0b\u7684\u9053\u513f\u7684\uff0c\u5fc5 \u987b\u548c\u5979\u5206\u51fa\u80dc\u8d1f\u624d\u80fd\u7f62\u4f11\uff01\u201d\u6068\u610f\u672a\u6d88\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5b9e\u8981\u4e0d\u662f\u4f60\u8dd1\u6765\u6405\u5c40\uff0c\u6211 \u65e9\u5df2\u628a\u5979\u6349\u4f4f\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8ba4\u771f\u8bf4\u6765\uff0c\u4f60\u4eec\u7684\u80dc\u8d1f\u65e9\u5df2\u5206\u4e86\u3002\u201d \u3000\u3000\u795e\u79d8\u5ba2\u50b2\u7136\u9053\uff1a\u201c\u600e\u4e48\u5206\u6cd5\uff1f\u54fc\uff0c\u4f60\u603b\u4e0d\u80fd\u8bf4\u662f\u6211\u6253\u8f93\u4e86\u5427\uff01\u201d \u3000\u3000\u5c11\u5e74\u4e00\u672c\u6b63\u7ecf\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6562\u8bf4\u662f\u4f60\u6253\u8f93\uff0c\u4f46\u4f60\u662f\u5e94\u8be5\u5411\u8fd9\u4f4d\u59d1\u5a18\u8ba4\u8f93\u7684\uff01\u201d \u3000\u3000\u795e\u79d8\u5ba2\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u5e94\u8be5\u8ba4\u8f93\uff0c\u771f\u662f\u5947\u8c08\u602a\u8bba\uff01\u201d\u5c11\u5e74\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5947\u8c08\u602a\u8bba\u53ef\u6b63 \u662f\u4f60\u81ea\u5df1\u8bf4\u8fc7\u7684\uff01\u201d \u3000\u3000\u795e\u79d8\u5ba2\u4e00\u6014\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201c\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u5fd8\u8bb0\u4e86\uff1f\u4f60\u4e00\u4e0a\u53f0\u7684\u65f6 \u5019\u4e0d\u662f\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u9650\u5728\u767e\u62db\u4e4b\u5185\uff0c\u4f60\u5c31\u53ef\u4ee5\u53d6\u80dc\u7684\u4e48\uff1f \u3000\u3000\u4f60\u6700\u540e\u90a3\u4e00\u62db\uff0c\u5df2\u7ecf\u662f\u7b2c\u4e00\u767e\u96f6\u4e09\u62db\u4e86\uff01\u4f60\u82e5\u662f\u8bf4\u8bdd\u7b97\u6570\uff0c\u5230\u4e86\u7b2c\u4e00\u767e\u96f6\u4e00\u62db\uff0c\u4f60\u5df2\u7ecf \u5e94\u8be5\u5411\u8fd9\u4f4d\u59d1\u5a18\u8ba4\u8f93\uff01\u201d \u3000\u3000\u795e\u79d8\u5ba2\u5fc3\u4e2d\u6709\u6570\uff0c\u4ed4\u7ec6\u4e00\u60f3\uff0c\u679c\u7136\u201c\u4f3c\u4e4e\u201d\u662f\u5df2\u7ecf\u8fc7\u4e86\u767e\u62db\u3002\u4ed6\u6ee1\u9762\u901a\u7ea2\uff0c\u72e1\u8fa9\u9053\uff1a \u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u4eec\u4e24\u4eba\u51fa\u62db\u90fd\u662f\u5feb\u5230\u6781\u70b9\uff0c\u8c01\u4e5f\u6570\u4e0d\u6e05\u695a\u3002\u4f60\u8bf4\u662f\u4e00\u767e\u96f6\u4e09\u62db\uff0c\u4ed6\u8bf4\u662f\u672a\u6ee1 \u767e\u62db\uff0c\u8fd9\u662f\u65e0\u4ece\u5bf9\u8bc1\u7684\u3002\u800c\u4e14\u2014\u2014\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u800c\u4e14\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7530\u79c9\u5355\u53ea\u597d\u4e0d\u70e6\u795e\u79d8\u5ba2\u7684\u9762\u5b50\uff0c\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u800c\u4e14\u8fd9\u4f4d\u59d1\u5a18\u4e5f\u8bf4\u8fc7\uff0c\u5bf9\u65b9\u867d\u7136\u4ee5\u767e\u62db\u4e3a \u9650\uff0c\u5979\u5374\u5e76\u4e0d\u8981\u5360\u8fd9\u4e2a\u4fbf\u5b9c\u7684\uff01\u201d \u3000\u3000\u90a3\u4e2a\u5c11\u5973\u843d\u843d\u5927\u65b9\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u662f\u8fd9\u6837\u8bf4\u8fc7\u3002\u4e0d\u5fc5\u8981\u4ed6\u8ba4\u8f93\uff0c\u4ecd\u7136\u5f53\u4f5c\u548c\u5c40 \u597d\u4e86\u3002\u201d \u3000\u3000\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u4e0d\u5206\u80dc\u8d1f\uff0c\u4e0d\u80fd\u4f5c\u548c\uff0c\u7b2c\u4e00\uff0c\u8fd9\u5c0f\u5b50\u7b97\u662f\u54ea\u53f7\u4eba\u7269\uff0c\u600e\u80fd\u51ed\u4ed6\u8bf4\u548c\u5c31\u5f53\u4f5c \u548c\uff1b\u7b2c\u4e8c\uff0c\u6211\u672c\u6765\u5df1\u7ecf\u7a33\u64cd\u80dc\u5238\uff0c\u65c1\u4eba\u63d2\u624b\uff0c\u5148\u5c31\u4e0d\u5408\u89c4\u5b9a\uff0c\u600e\u80fd\u7b97\u6570\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u8981\u6253\u4e0b\u53bb\uff0c\u90a3\u4e5f\u53ef\u4ee5\u3002\u4f60\u662f\u66ff\u5f52\u5e84\u4e3b\u6253\u7684\uff0c\u6211\u4e5f\u66ff\u8fd9\u4f4d\u59d1\u5a18\u6253\u3002\u7167 \u4f60\u81ea\u5df1\u6240\u5b9a\u7684\u529e\u6cd5\uff0c\u6211\u82e5\u8f93\u4e86\u7ed9\u4f60\uff0c\u8fd9\u4f4d\u59d1\u5a18\u56fa\u7136\u53ef\u4ee5\u4efb\u51ed\u4f60\u6765\u5904\u7f6e\uff0c\u6211\u4e5f\u53ef\u4ee5\u4efb\u4f60\u5904 \u7f6e\uff01\u201d\u8fd9\u53ef\u6b63\u662f\u4ee5\u5b50\u4e4b\u77db\u653b\u5b50\u4e4b\u76fe\uff0c\u795e\u79d8\u5ba2\u8fd8\u80fd\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u7530\u79c9\u5355\u4ee5\u516c\u8bc1\u4eba\u81ea\u5c45\uff0c\u5374\u9053\uff1a \u201c\u4f60\u548c\u8fd9\u4f4d\u59d1\u5a18\u662f\u6cbe\u4eb2\u8fd8\u662f\u5e26\u6545\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u975e\u4eb2\u975e\u6545\u3002\u6211\u53ea\u662f\u77a7\u7740\u8fd9\u4ef6\u4e8b\u60c5\u4e0d\u592a\u987a\u773c\uff0c\u5fcd\u4e0d\u4f4f\u8981\u6253\u62b1\u4e00\u4e2a\u4e0d\u5e73\u800c\u5df2\u3002\u4f60 \u60f3\u60f3\uff0c\u8fd9\u4f4d\u59d1\u5a18\u5df2\u7ecf\u6253\u4e86\u4e24\u573a\uff0c\u800c\u4e14\u5728\u521a\u624d\u8fd9\u573a\uff0c\u4ea6\u4e5f\u8d85\u8fc7\u4e86\u8fd9\u4f4d\u5ba2\u4eba\u81ea\u5df1\u9650\u5b9a\u7684\u4e00\u767e\u62db \u4e86\uff0c\u4f60\u4eec\u5018\u82e5\u8981\u5979\u518d\u6253\u4e0b\u53bb\uff0c\u4e0d\u6015\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\u4f60\u4eec\u662f\u7528\u8f66\u8f6e\u6218\u6765\u6b3a\u8d1f\u4e00\u4e2a\u5f02\u4e61\u5973\u5b50\u5417\uff1f\u201d \u3000\u3000\u7530\u79c9\u5355\u521a\u624d\u6ca1\u6709\u53cd\u5bf9\u90a3\u795e\u79d8\u5ba2\u4eba\u66ff\u5f52\u5143\u9f99\u51fa\u573a\u6b64\u523b\u5f53\u7136\u4e5f\u6ca1\u6709\u7406\u7531\u53cd\u5bf9\u8fd9\u4e2a\u5c11\u5e74\u66ff\u90a3\u5979 \u5973\u5b50\u51fa\u573a\uff0c\u53ea\u597d\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u8bef\u4f1a\u4e86\uff0c\u6211\u95ee\u4f60\u4eec\u662f\u5426\u4eb2\u5e26\u6545\uff0c\u5e76\u975e\u8fd9\u4e2a\u610f\u601d\u3002\u65e0\u987b\u679d\u8282\u6a2a \u751f\uff0c\u626f\u5230\u4ec0\u4e48\u516c\u5e73\u4e0d\u516c\u5e73\u4e0a\u53bb\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7530\u79c9\u5355\u9053\uff1a\u201c\u4f60\u5982\u4eca\u662f\u66ff\u8fd9\u4f4d\u59d1\u5a18\u63a5\u4e0b\u5979\u548c\u5f52\u5e84\u4e3b\u5212\u51fa\u7684\u9053\u513f\uff0c\u8981\u662f\u4f60\u8ba4\u8f93\u4e86\u7684\u8bdd\uff0c\u4f60 \u4efb\u51ed\u5bf9\u65b9\u5904\u7f6e\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u60c5\u3002\u4f46\u8fd9\u4f4d\u59d1\u5a18\u53ef\u4e5f\u5f97\u4efb\u51ed\u4e5f\u5e84\u4e3b\u5904\u7f6e\u4e86\uff0c\u65e2\u7136\u4f60\u548c\u5979\u7d20\u6627\u5e73 \u751f\uff0c\u5979\u80fd\u591f\u76f8\u4fe1\u4f60\u4e0d\u4f1a\u51fa\u5356\u5979\u5417\uff1f\u53c8\u5373\u4f7f\u76f8\u4fe1\u5f97\u8fc7\u4f60\u80af\u5c3d\u529b\u800c\u4e3a\uff0c\u4f46\u8981\u662f\u4f60\u5c3d\u4e86\u529b\u4e5f\u6253\u4e0d\u8fc7 \u8fd9\u4f4d\u5ba2\u4eba\u5462\uff1f\u90a3\u5c82\u4e0d\u53d8\u6210\u4f60\u610f\u6b32\u52a9\u5979\uff0c\u53cd\u800c\u5bb3\u4e86\u5979\u4e86\uff1f\u201d \u3000\u3000\u7530\u79c9\u5355\u662f\u4e2a\u8001\u7316\u72c2\u72d0\u72f8\uff0c\u4ed6\u7684\u6b66\u529f\u867d\u7136\u8fdc\u4e0d\u53ca\u90a3\u4e2a\u795e\u79d8\u5ba2\u4eba\uff0c\u5374\u4e5f\u770b\u5f97\u51fa\u8fd9\u4e2a\u5c11\u5e74\u6bd4\u90a3 \u5973\u5b50\u66f4\u96be\u5bf9\u4ed8\u3002\u4ed6\u8bf4\u8fd9\u756a\u8bdd\u7684\u76ee\u7684\uff0c\u65e0\u975e\u662f\u60f3\u5f15\u8d77\u8fd9\u4e2a\u5973\u5b50\u987e\u8651\uff0c\u6700\u597d\u8ba9\u5979\u81ea\u52a8\u8bf4\u51fa\uff0c\u62d2\u7edd \u8fd9\u4e2a\u5c11\u5e74\u66ff\u5979\u6253\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u8fd9\u4e2a\u5973\u5b50\u5374\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\u66ff\u6211\u6253\u62b1\u4e0d\u5e73\uff0c\u90a3\u662f\u628a\u6211\u5f53\u4f5c\u670b\u53cb\u770b\u5f85\u4e86\u3002\u83ab\u8bf4\u4ed6\u4e0d\u4f1a \u6253\u8f93\uff0c\u5373\u4f7f\u6253\u8f93\uff0c\u6211\u4e5f\u8ba4\u547d\uff01\u201d\u5c11\u5e74\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u90a3\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5df2\u7ecf\u540c\u610f\u4e86\u3002\u4f60 \u5462\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u5b9a\u7740\u53cc\u773c\u5411\u4ed6\u6ce8\u89c6\uff0c\u76ee\u5149\u4f3c\u4e4e\u5145\u6ee1\u60f6\u60d1\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u5c31\u8bf4\u8fc7\u6211\u662f\u4e2a\u65e0\u540d\u5c0f\u5352\u3002\u6211\u5728\u4f60\u7684\u773c\u4e2d\uff0c\u6211\u6839\u672c\u5c31\u7b97\u4e0d\u4e0a\u662f\u4e2a\u4eba \u7269\u3002\u8fd9\u8bdd\u4f60\u4f3c\u4e4e\u4e5f\u662f\u8bf4\u8fc7\u7684\u4e86\u3002\u53c8\u4f55\u987b\u518d\u95ee\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u521a\u624d\u62d2\u7edd\u8ba9\u4ed6\u8c03\u505c\uff0c\u7684\u786e\u662f\u9a82\u8fc7\u4ed6\u201c\u4f60\u8fd9\u5c0f\u5b50\u7b97\u662f\u54ea\u53f7\u4eba\u7269\uff1f \u3000\u3000\u201d\u7684\u3002 \u3000\u3000\u795e\u79d8\u5ba2\u7ed9\u4ed6\u62ff\u7740\u8bdd\u67c4\u3002\u54fc\u4e86\u4e00\u58f0\u3002\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u6709\u540d\u8fd8\u662f\u65e0\u540d\uff0c\u4f46\u4f60\u65e2\u7136\u662f\u66ff\u8fd9 \u4f4d\u59d1\u5a18\u63a5\u4e0b\u9053\u513f\uff0c\u4f60\u5c31\u5f97\u62a5\u4e0a\u540d\u6765\uff0c\u8fd9\u662f\u89c4\u77e9\uff01\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u54e6\uff0c\u6709\u8fd9\u4e48\u591a\u89c4\u77e9\u3002\u90a3\u4e48\uff0c\u4f60\u521a\u624d\u66ff\u5f52\u5e84\u4e3b\u51fa\u573a\uff0c\u5374\u53c8\u4e3a\u4f55\u4e0d\u8bb2\u8fd9\u4e2a\u89c4 \u77e9\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u8981\u4ed6\u62a5\u4e0a\u540d\u6765\uff0c\u5148\u5f97\u81ea\u5df1\u62a5\u4e0a\u540d\u6765\uff01\u201d\u795e\u79d8\u5ba2\u51b7\u7b11\u4e0d\u7b54\u3002 \u3000\u3000\u8001\u62f3\u5e08\u7530\u79c9\u5355\u53c8\u4ee5\u516c\u8bc1\u4eba\u81ea\u5c45\uff0c\u66ff\u4ed6\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd9\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u5176\u4e8c\uff1f\u201d \u3000\u3000\u7530\u79c9\u5355\u6e32\uff1a\u201c\u4ed6\u662f\u5f52\u5e84\u4e3b\u7684\u5ba2\u4eba\uff0c\u4ed6\u7684\u59d3\u540d\u6765\u5386\uff0c\u5f52\u5e84\u4e3b\u65e9\u5df2\u77e5\u9053\u3002 \u3000\u3000\u4f46\u8fd9\u4f4d\u5c0f\u54e5\u7684\u59d3\u540d\u6765\u5386\uff0c\u5374\u4e0d\u77e5\u6709\u8c01\u77e5\u9053\uff1f\u9664\u975e\u4ed6\u80fd\u591f\u627e\u5230\u4e00\u4f4d\u6211\u4eec\u8ba4\u53ef\u7684\u4eba\u62c5\u4fdd\u4ed6\uff0c \u5426\u5219\u4ee5\u5f52\u4e91\u5e84\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u5f52\u4e91\u5e84\u7684\u4e8b\u60c5\uff0c\u53ef\u4e0d\u80fd\u8ba9\u4e00\u4e2a\u6765\u5386\u4e0d\u660e\u7684\u4eba\u63d2\u624b\uff01\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u59d3\u540d\u8fd8\u5728\u5176\u6b21\uff0c\u6700\u7d27\u8981\u7684\u662f\u6765\u5386\u5206\u660e\u4e86\uff1f\u201d \u3000\u3000\u7530\u79c9\u5355\u9053\uff1a\u201c\u4e0d\u9519\u3002\u56e0\u4e3a\u4f60\u548c\u8fd9\u4f4d\u59d1\u5a18\u975e\u4eb2\u975e\u6545\uff0c\u5374\u8981\u4f60\u6765\u63d2\u624b\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6309\u6c5f\u6e56\u89c4 \u77e9\uff0c\u4f60\u5c31\u5f97\u8ba9\u5f53\u4e8b\u7684\u4e00\u65b9\uff0c\u77e5\u9053\u4f60\u662f\u4f55\u6765\u5386\uff01\u201d \u3000\u3000\u5c11\u5e74\u4f3c\u7b11\u975e\u7b11\uff0c\u5ffd\u5730\u8f6c\u8fc7\u5934\u6765\uff0c\u5bf9\u90a3\u795e\u79d8\u5ba2\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u77e5\u9053\u6211\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u795e\u79d8\u5ba2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u52c9\u5f3a\u4f60\u8bf4\u51fa\u6765\uff0c\u4f46\u600e\u6837\u53eb\u505a\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\u529e\u4e8b\uff0c\u7530\u8001\u5148\u751f\u5df2\u7ecf \u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\u3002\u201d\u8a00\u5916\u4e4b\u610f\uff0c\u4ed6\u4e0d\u80af\u8bf4\uff0c\u90a3\u5c31\u53ea\u6709\u8bf7\u4ed6\u4e0b\u53f0\u3002 \u3000\u3000\u5c11\u5e74\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u662f\u8d35\u4eba\u4e8b\u5fd9\uff0c\u4f60\u5927\u6982\u8bb0\u4e0d\u8d77\u6211\u662f\u8c01\u4e86\u3002\u4f46\u4f60\u4e0d \u8ba4\u8bc6\u6211\uff0c\u6211\u53ef\u8ba4\u8bc6\u4f60\u7684\uff01\u54c8\u5927\u4eba\uff0c\u4f60\u518d\u60f3\u60f3\u770b\uff0c\u6216\u8005\u4f60\u4f1a\u8bb0\u8d77\u6211\u8fd9\u4e2a\u65e0\u540d\u5c0f\u5352\u4e5f\u8bf4\u4e0d\u5b9a\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u795e\u79d8\u5ba2\u56fa\u7136\u5403\u60ca\uff0c\u6ee1\u56ed\u5bbe\u5ba2\u3001\u4e5f\u90fd\u662f\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u4ee4\u5f97\u4ed6\u4eec\u5927\u60ca\u7684\u662f\u5c11\u5e74\u53e3\u8bf4\u51fa\u7684\u201c\u54c8\u5927\u4eba\u201d\u8fd9\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u77e5\u9053\u91d1\u56fd\u7684\u5fa1\u6797\u519b\u526f\u7edf\u9886\u662f\u54c8\u5fc5\u56fe\uff0c\u54c8\u5fc5\u56fe\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u8fd1\u8eab\u5f85\u536b\u51fa\u8eab\uff0c\u65e9\u5728\u4ed6 \u5347\u4efb\u5f7b\u6797\u519b\u526f\u7edf\u9886\u4e4b\u524d\uff0c\u5c31\u6709\u4e00\u7b49\u5df4\u56fe\u9c81\uff08\u52c7\u58eb\u4e4b\u610f\uff09 \u3000\u3000\u7684\u5c01\u53f7\u7684\u3002 \u3000\u3000\u4f46\u4e5f\u662f\u6b63\u56e0\u4e3a\u54c8\u5fc5\u56fe\u662f\u5728\u4eac\u4e2d\u4f34\u966a\u7687\u5e1d\uff0c\u6240\u4ee5\u4ed6\u7684\u5927\u540d\uff0c\u5728\u5168\u56fd\u867d\u7136\u662f\u5bb6\u4f20\u6237\u6653\uff0c\u5730\u65b9 \u4e0a\u7684\u6b66\u6797\u4eba\u7269\uff0c\u89c1\u8fc7\u4ed6\u7684\u5374\u662f\u6781\u5c11\u3002\u6b64\u65f6\u4f17\u4eba\u4e0d\u7981\u5728\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u53d7\u5230\u5f52\u4e91\u9f99\u7279\u6b8a\u793c \u9047\uff0c\u5949\u4e3a\u4e0a\u5bbe\u7684\u795e\u79d8\u5ba2\uff0c\u83ab\u975e\u5c31\u662f\u5fa1\u6797\u519b\u526f\u7edf\u9886\u54c8\u5fc5\u56fe\uff1f\u201d \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u4e2a\u795e\u79d8\u5ba2\u6b63\u662f\u91d1\u56fd\u7684\u5fa1\u6797\u519b\u526f\u7edf\u9886\u54c8\u5fc5\u56fe\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\u6709\u8fd9\u6837\u5de7\u5427\uff1f\u96be\u9053\u8fd9\u4e2a\u5c11\u5e74\u5c31\u662f\u6a80\u5bb6\u90a3\u5b69\u5b50\uff1f\u201d\u54c8\u5fc5\u56fe\u4e0d\u7531\u5f97\u5fc3\u5934\u5927\u9707\u4e86\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u662f\uff01\u201d \u3000\u3000\u5c11\u5e74\u51b7\u73b2\u8bf4\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u60f3\u8d77\u6765\u4e86\u5427\uff0c\u8fd8\u8981\u4e0d\u8981\u6211\u81ea\u62a5\u59d3\u540d\uff1f\u201d \u3000\u3000\u5f53\u7136\u662f\u7528\u4e0d\u7740\u4ed6\u901a\u540d\u9053\u59d3\u4e86\u3002\u54c8\u5fc5\u56fe\u5df2\u7ecf\u77e5\u9053\u4ed6\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5bf9\u7740\u8fd9\u5c11\u5e74\u4ec7\u6068\u7684\u76ee\u5149\uff0c\u542c\u4e86\u8fd9\u5c11\u5e74\u51b7\u9177\u7684\u8bed\u6c14\uff0c\u4ed6\u662f\u518d\u4e5f\u6ca1\u6709\u6000\u7591\u4e86\u3002\u8fd9\u4e2a\u5c11\u5e74\u5c31\u662f \u6a80\u5bb6\u7684\u90a3\u4e2a\u5b69\u5b50\uff0c\u6a80\u5bb6\u552f\u4e00\u5e78\u5b58\u7684\u5b64\u513f\u6a80\u7fbd\u51b2\uff01 \u3000\u3000\u60f3\u8d77\u81ea\u5df1\u548c\u6a80\u5bb6\u7ed3\u4e0b\u7684\u6df1\u4ec7\uff0c\u9976\u662f\u54c8\u5fc5\u56fe\u6709\u201c\u4e00\u7b49\u5df4\u56fe\u9c81\u201d\uff08\u52c7\u58eb\uff09 \u3000\u3000\u7684\u8854\u5934\uff0c\u800c\u5bf9\u6a80\u7fbd\u51b2\u7684\u76ee\u5149\uff0c\u4e5f\u662f\u4e0d\u7981\u5fc3\u5934\u98a4\u6817\u3002 \u3000\u3000\u90a3\u573a\u8840\u6218\uff0c\u54c8\u5fc5\u56fe\u7684\u624b\u4e0b\u5168\u90fd\u6b7b\u6389\uff0c\u53ea\u6709\u4ed6\u4e00\u4e2a\u4eba\u4fa5\u5e78\u9003\u5f97\u6027\u547d\u3002 \u3000\u3000\u4f46\u6a80\u5bb6\u7684\u4eba\uff0c\u5305\u62ec\u6a80\u7fbd\u51b2\u7684\u7956\u7236\u6a80\u516c\u76f4\uff0c\u7236\u4eb2\u6a80\u9053\u6210\u548c\u4ed6\u7684\u5916\u516c\uff08\u5176\u5b9e\u662f\u4ed6\u6bcd\u4eb2\u7684\u4e49 \u7236\uff09\u5f20\u708e\u5728\u5185\uff0c\u4e5f\u5168\u90fd\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u867d\u7136\u4e0d\u662f\u4ed6\u6740\u7684\uff0c\u4f46\u82e5\u4e0d\u662f\u6a80\u516c\u76f4\u548c\u4ed6\u6597\u5f97\u4e24\u8d25\u4ff1\u4f24\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u6b7b\u5728\u5b8b\u56fd\u7687\u5e1d \u6d3e\u6765\u7684\u5927\u5185\u536b\u58eb\u624b\u4e0b\u3002\u5b8b\u56fd\u7684\u536b\u58eb\u662f\u521a\u597d\u5728\u4ed6\u9003\u51fa\u6a80\u5bb6\u4e4b\u540e\u5c31\u8ddf\u7740\u6765\u7684\u3002\u4ed6\u540e\u6765\u65b9\u59cb\u77e5\u9053\uff0c \u63a5\u7740\u5728\u6a80\u5bb6\u7684\u90a3\u573a\u8840\u6218\uff0c\u5b8b\u56fd\u7684\u536b\u58eb\u4e5f\u90fd\u5c3d\u6570\u4e27\u547d\u3002\u76d8\u9f99\u5c71\u90a3\u573a\u8840\u6218\uff0c\u6a80\u5bb6\u9003\u51fa\u6765\u7684\u53ea\u6709\u5f20 \u96ea\u6ce2\u548c\u6a80\u7fbd\u51b2\u8fd9\u5bf9\u6bcd\u5b50\u3002\u54c8\u5fc5\u56fe\u53c8\u518d\u60f3\u8d77\u4e86\u4e03\u5e74\u524d\u5728\u5546\u5dde\u8282\u5ea6\u4f7f\u8859\u95e8\u91cc\u7684\u4e00\u573a\u8840\u6218\u3002 \u3000\u3000\u90a3\u573a\u8840\u6218\uff0c\u5b8c\u989c\u9274\u7684\u624b\u4e0b\uff0c\u6b7b\u5728\u8036\u5f8b\u7384\u5143\u4e4b\u624b\u7684\u4e0d\u8ba1\u5176\u6570\uff0c\u54c8\u5fc5\u56fe\u81ea\u5df1\u4e5f\u51e0\u4e4e\u88ab\u8036\u5f8b\u7384 \u65e0\u6349\u53bb\u3002 \u3000\u3000\u4f46\u5f20\u96ea\u6ce2\u5374\u662f\u5728\u4ed6\u4eb2\u81ea\u53d1\u53f7\u65bd\u4ee4\u4e4b\u4e0b\uff0c\u88ab\u4e71\u7bad\u5c04\u4f24\uff0c\u7ec8\u4e8e\u6bd9\u547d\u7684\u3002 \u3000\u3000\u60f3\u8d77\u81ea\u5df1\u548c\u6a80\u5bb6\u7ed3\u4e0b\u7684\u6df1\u4ec7\uff0c\u4ed6\u77e5\u9053\u548c\u6a80\u7fbd\u51b2\u8fd9\u573a\u6076\u6597\u5df2\u662f\u65e0\u53ef\u907f\u514d\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u6709\u610f\u51b2\u7740\u6211\u6765\u7684\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u8bf4\u5bf9\u4e86\u4e00\u534a\u3002\u4e0d\u9519\uff0c\u6211\u662f\u60f3\u8981\u627e\u4f60\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u78b0\u4e0a\u4e86 \u4f60\u3002\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u559d\u9053\uff1a\u201c\u4f60\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u5df2\u8bf4\u8fc7\u4e86\u5417\uff1f\u6211\u662f\u770b\u4e0d\u8fc7\u773c\uff0c\u6765\u66ff\u8fd9\u4f4d\u59d1\u5a18\u63a5\u4e0b\u5979\u548c\u5f52\u5e84\u4e3b\u5212 \u51fa\u7684\u9053\u513f\uff01\u201d \u3000\u3000\u5f52\u5143\u9f99\u7684\u4e8c\u5f92\u5f1f\u9b4f\u8fde\u9b41\u662f\u6d1b\u9633\u603b\u5175\u5e10\u4e0b\u7684\u53c2\u5c06\uff0c\u4f5c\u5a01\u4f5c\u798f\u60ef\u4e86\uff0c\u4f46\u5374\u662f\u4e2a\u8349\u5305\uff0c\u5fcd\u4e0d\u4f4f \u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u4e8e\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u770b\u4e0d\u7ba1\u4ed6\u662f\u4ec0\u4e48\u3002\u4e5f\u4e0d\u914d\u548c\u54c8\u5927\u4eba\u4ea4\u624b\uff1f\u201d\u4ed6\u662f\u4e0d\u81ea\u89c9\u7684 \u6309\u7167\u5b98\u573a\u7684\u4e60\u60ef\uff0c\u538b\u4f4e\u8fd9\u201c\u5c0f\u5b50\u201d\u6765\u5949\u627f\u54c8\u5fc5\u56fe\u7684\u3002 \u3000\u3000\u90a3\u77e5\u8fd9\u6b63\u89e6\u4e86\u54c8\u5fc5\u56fe\u4e4b\u5fcc\uff0c\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u90fd\u7528\u4e0d\u7740 \u65c1\u4eba\u6765\u7ba1\uff01\u201d\u8981\u77e5\u4ed6\u662f\u5949\u4e86\u7687\u4e0a\u7684\u5bc6\u4ee4\u51fa\u4eac\uff0c\u4ed6\u7684\u8eab\u4efd\u56fa\u7136\u4e0d\u60f3\u516c\u5f00\uff0c\u6a80\u7fbd\u51b2\u7684\u8eab\u4efd\uff0c\u4ed6\u4e5f \u662f\u4e0d\u4fbf\u5f53\u4f17\u8bf4\u51fa\u6765\u7684\u3002\uff08\u6a80\u7fbd\u51b2\u7684\u7956\u7236\u662f\u91d1\u56fd\u738b\u7237\uff0c\u8fd9\u79cd\u6d89\u53ca\u7687\u65cf\u5185\u90e8\u79c1\u6597\u7684\u4e8b\u60c5\uff1b\u5c82\u80fd\u7ed9 \u4e00\u822c\u767e\u59d3\u77e5\u9053\u3002\uff09 \u3000\u3000\u90a3\u5c11\u5973\u4f3c\u4e4e\u5df2\u7ecf\u770b\u51fa\u4e00\u70b9\u8e4a\u8df7\uff0c\u6545\u610f\u76ef\u7740\u54c8\u5fc5\u56fe\u95ee\u9053\uff1a\u201c\u54c8\u5927\u4eba\uff0c\u4f60\u5df2\u7ecf\u77e5\u9053\u4ed6\u662f\u8c01 \u4e86\uff0c\u662f\u5427\uff1f\u90a3\u4f60\u8ba4\u4e3a\u4ed6\u6709\u6ca1\u6709\u8d44\u683c\u548c\u4f60\u8fc7\u62db\uff1f\u563f\uff0c\u563f\uff0c\u6211\u8fd9\u662f\u6309\u6c5f\u6e56\u89c4\u77e9\uff0c\u4e0d\u80fd\u4e0d\u6709\u6b64\u4e00 \u95ee\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56f9\u60c5\u77e5\u6b64\u6218\u5df1\u65e0\u53ef\u907f\u514d\uff0c\u53ea\u80fd\u5e72\u7b11\u8bf4\u9053\uff1a\u201c\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u4ed6\u548c\u6211\u8fc7\u62db\uff0c\u90a3\u662f\u770b\u5f97\u8d77 \u6211\u4e86\uff01\u4e0d\u8fc7\u2026\u2026\u201d\u201d\u8981\u77e5\u6a80\u7fbd\u51b2\u662f\u6a80\u5bb6\u552f\u4e00\u7684\u7537\u4e01\uff0c\u6309\u7167\uff08\u738b\u65cf\u7684\u201c\u4e16\u88ad\u201d\u6cd5\u89c4\uff0c\u4ed6\u4e5f\u5e94\u8be5 \u662f\u8d1d\u52d2\u7684\u8eab\u4efd\u7684\u3002\uff09\u4f17\u4eba\u542c\u4e86\u54c8\u5fc5\u56fe\u7684\u8bf4\u8bdd\u90fd\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u6b8a\u4e0d\u77e5\u4ed6\u6240\u8bf4\u7684\u53ef\u662f\u4e00\u70b9\u4e0d \u5047\uff0c\u8d1d\u52d2\u7684\u8eab\u4efd\u5f53\u7136\u6bd4\u4ed6\u8fd9\u5fa1\u6797\u519b\u526f\u7edf\u9886\u7684\u8eab\u4efd\u9ad8\u51fa\u8bb8\u591a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u54c8\u54c8\u4e00\u7b11\uff0c\u6253\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u53ea\u8981\u4f60\u8ba4\u4e3a\u6211\u6709\u8d44\u683c\u548c\u4f60\u8fc7\u62db\uff0c\u90a3\u5c31\u884c\u4e86\u3002\u8fd8\u7528\u662f \u7740\u4ec0\u4e48\u2018\u4e0d\u8fc7\u2019\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u5c31\u53ea\u95ee\u4f60\u73b0\u5728\u7684\u8eab\u4efd\uff0c\u4e0d\u7406\u4f60\u672c\u6765\u7684\u8eab\u4efd\u4e86\u3002 \u3000\u3000\u201d \u3000\u3000\u4f17\u4eba\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u4ec0\u4e48\u53eb\u505a\u201c\u73b0\u5728\u7684\u8eab\u4efd\u201d\uff0c\u4ec0\u4e48\u53eb\u505a\u201c\u672c\u6765\u7684\u8eab\u4efd\u201d\uff0c\u8fd9\u4e24\u79cd\u8eab\u4efd \u53c8\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u4f46\u6a80\u7fbd\u51b2\u5219\u662f\u542c\u5f97\u61c2\u7684\uff0c\u8fd9\u4e24\u79cd\u8eab\u4efd\u5176\u5b9e\u5927\u6709\u533a\u522b\u3002\u4ed6\u662f\u7528\u672c\u6765\u7684\u8eab\u4efd\uff0c\u90a3 \u5c31\u662f\u4e3a\u4e86\u62a5\u4ec7\uff1a\u82e5\u662f\u73b0\u5728\u7684\u8eab\u4efd\uff0c\u5219\u662f\u548c\u54c8\u5fc5\u56fe\u4e00\u6837\uff0c\u90fd\u662f\u66ff\u522b\u4eba\u6bd4\u6b66\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u5c42\uff0c\u4ed6\u672c\u6765\u7684\u8eab\u4efd\u662f\u5c0f\u8d1d\u52d2\uff0c\u82e5\u7136\u6309\u7167\u738b\u5ba4\u7684\u89c4\u77e9\uff0c\u54c8\u5fc5\u56fe\u6839\u672c\u6ca1\u6709\u8d44\u683c\u7ad9\u5728\u5e73 \u7b49\u7684\u5730\u4f4d\u548c\u4ed6\u6bd4\u6b66\uff0c\u53ea\u80fd\u966a\u4ed6\u201c\u7ec3\u62db\u201d\u3002\u5426\u5219\u90a3\u5c31\u662f\u201c\u4ee5\u4e0b\u72af\u4e0a\u3002\u201d\u5f53\u7136\uff0c\u54c8\u5fc5\u56fe\u4e0d\u5fc5\u9075\u5b88 \u8fd9\u4e2a\u89c4\u77e9\uff0c\u91d1\u56fd\u7684\u7687\u5e1d\u5df2\u7ecf\u9881\u4e0b\u5bc6\u4ee4\u4e0e\u4ed6\uff0c\u4ed6\u662f\u53ef\u4ee5\u628a\u8fd9\u4e2a\u5c0f\u8d1d\u52d2\u5f53\u4f5c\u201c\u94a6\u72af\u201d\u62ff\u529e\u7684\u3002\u4f46 \u5bc6\u4ee4\u662f\u4e0d\u80fd\u516c\u5f00\u7684\uff0c\u6545\u6b64\u5728\u53e3\u5934\u4e0a\u5148\u6765\u4e00\u4e2a\u201c\u4ea4\u5f85\u201d\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u73b0\u5728\u662f\u66ff\u8fd9\u4f4d \u59d1\u5a18\u63a5\u4e0b\u5979\u548c\u4f60\u4eec\u5212\u51fa\u9053\u513f\uff0c\u54b1\u4eec\u5c31\u6309\u7167\u8fd9\u5df2\u7ecf\u5212\u51fa\u7684\u9053\u513f\u529e\u4e8b\u3002\u662f\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d\u54c8\u5fc5 \u56fe\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u5df2\u7ecf\u6253\u8fc7\u534a\u573a\uff0c\u6211\u4e5f\u4e0d\u80fd\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u4f60\u521a\u624d\u81ea\u9650\u767e\u62db\uff0c\u6211\u5c31 \u53ea\u662f\u81ea\u9650\u5341\u62db\u597d\u4e86\u3002\u5341\u62db\u4e4b\u5185\uff0c\u6211\u82e5\u662f\u4e0d\u80fd\u5c06\u4f60\u6253\u4e0b\u64c2\u53f0\uff0c\u7b97\u662f\u6211\u8f93\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u53f0\u4e0b\u7684\u5bbe\u5ba2\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002\u4ed6\u4eec\u662f\u89c1\u8fc7\u54c8\u5fc5\u56fe\u7684\u6b66\u529f\u7684\uff0c\u8c01\u90fd\u89c9 \u5f97\u8fd9\u5c0f\u5b50\u672a\u514d\u592a\u72c2\u5984\u3002\u51fa\u4e4e\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\uff0c\u54c8\u5fc5\u56fe\u5374\u6ca1\u6709\u6c14\u6012\u3002 \u3000\u3000\u4ed6\u542c\u4e86\u8fd9\u8bdd\uff0c\u5012\u597d\u50cf\u662f\u5403\u4e86\u4e00\u9897\u5b9a\u5fc3\u4e38\u4e86\u3002 \u3000\u3000\u8981\u77e5\u4ed6\u82e6\u7ec3\u4e86\u5341\u5e74\u7684\u6b66\u529f\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u5bf9\u4ed8\u8036\u5f8b\u7384\u5143\u3002\u6a80\u7fbd\u51b2\u662f\u8036\u5f8b\u7384\u5143\u7684\u5f92\u5f1f\uff0c\u4ed6\u5df2 \u7ecf\u77e5\u9053\u3001\u5018\u82e5\u4e0d\u9650\u62db\u6570\uff0c\u4ed6\u5bf9\u6a80\u7fbd\u51b2\u8fd8\u591a\u5c11\u6709\u70b9\u987e\u5fcc\u3002 \u3000\u3000\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u91cc\u6697\u601d\uff1a\u201c\u5373\u4fbf\u662f\u4f60\u7684\u5e08\u7236\u73b0\u4eca\u548c\u6211\u6bd4\u6b66\uff0c\u4ed6\u4e5f\u4e0d\u89c1\u5f97\u80fd\u591f\u5728\u5341\u62db\u4e4b\u5185\u5c06 \u6211\u6253\u8d25\uff0c\u4f55\u51b5\u662f\u4f60\u8fd9\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\u3002\u8fd9\u8bdd\u53ef\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff01\u201d \u3000\u3000\u53c8\u4e00\u6b21\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u54c8\u5fc5\u56fe\u4ee5\u5f7b\u6797\u519b\u7684\u526f\u7edf\u9886\u7684\u8eab\u4efd\uff0c\u4e0d\u4f46\u63a5\u53d7\u4e86\u5bf9\u65b9\u7684\u81ea\u9650\u5341 \u62db\uff0c\u800c\u4e14\u8fd8\u597d\u50cf\u5bb3\u6015\u5bf9\u65b9\u53cd\u6094\uff0c\u903c\u7d27\u4e00\u53e5\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8bf4\u8fc7\u8981\u8d54\u507f\u4f60\u7684\u635f\u5931\u7684\uff0c\u4f60\u82e5\u8fd8\u5acc\u4e0d\u591f\u7684\u8bdd\uff01 \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4ed6\u62ff\u51fa\u4e86\u4e00\u652f\u901a\u4f53\u6676\u83b9\u7684\u7389\u8427\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5403\u4e86\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5e08\u7236\u7684\u90a3\u652f\u6696\u7389\u8427\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u4f60\u4e0d\u7528\u60ca\u614c\u3002\u6211\u53ea\u662f\u7528\u8fd9\u652f\u7389\u8427\u5439\u4e2a\u66f2\u5b50\u966a\u4f60\u73a9\u73a9\u3002\u8981\u662f\u6211\u653e\u5f00 \u7389\u8427\uff0c\u51fa\u624b\u62db\u67b6\u5c31\u7b97\u6211\u8f93\uff0c\u8fd9\u652f\u7389\u8427\u82e5\u662f\u6253\u5230\u4f60\u8eab\u4e0a\uff0c\u4e5f\u7b97\u6211\u8f93\uff01\u201d \u3000\u3000\u4f17\u4eba\u867d\u7136\u4e0d\u77e5\u9053\u201c\u6696\u7389\u8427\u201d\u662f\u4ec0\u4e48\u5b9d\u8d1d\uff0c\u4f46\u6309\u7167\u6a80\u7fbd\u51b2\u8fd9\u4e2a\u8bf4\u6cd5\uff0c\u4ed6\u6839\u672c\u4e0d\u80fd\u628a\u8fd9\u652f\u7389 \u8427\u5f53\u4f5c\u5175\u5668\u4f7f\u7528\uff0c\u90a3\u5c31\u7b97\u662f\u5b9d\u8d1d\uff0c\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u4f55\u51b5\u53ea\u662f\u9650\u5b9a\u5341\u62db\uff0c\u4f17\u4eba\u4ff1\u662f\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u8d54\u507f\u635f\u5931\u4e43\u662f\u5982\u6b64\uff0c\u8fd9\u6837\u7684\u8d54\u507f\u4e5f\u592a\u8fc7\u4fbf\u5b9c\u5bf9\u65b9\u4e86\uff0c\u54fc\uff0c\u7b80\u76f4\u53ef\u8bf4\u662f\u81ea\u5bfb\u6b7b \u8def\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u5ffd\u9053\uff1a\u201c\u8981\u662f\u4ed6\u6545\u610f\u78b0\u649e\u4f60\u8fd9\u652f\u7389\u8427\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u603b\u4e4b\u6211\u4e0d\u7528\u8fd9\u652f\u7389\u8427\u53bb\u6253\u4ed6\uff0c\u4ed6\u82e5\u662f\u6765\u78b0\u6211\u8fd9\u652f\u7389\u8427\uff0c\u5403\u4e86\u82e6\u5934\uff0c\u90a3\u5c31\u53ea \u80fd\u7b97\u662f\u4ed6\u81ea\u8ba8\u82e6\u5934\u5403\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u60f3\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u81f3\u4e8e\u8fd9\u6837\u65e0\u8d56\u3002\u4e0d\u9519\uff0c\u4ea4\u624b\u4e4b\u65f6\uff0c\u8bf4\u4e0d\u5b9a\u6211\u662f\u8981\u62a2\u4f60\u8fd9\u652f\u7389\u8427 \u7684\uff0c\u5373\u4f7f\u4f60\u6253\u7740\u4e86\u6211\u3002\u6211\u4e5f\u4e0d\u602a\u4f60\u3002\u201d\u8981\u77e5\u6a80\u7fbd\u51b2\u5df2\u662f\u5bf9\u81ea\u5df1\u201c\u8bf8\u591a\u9650\u5236\u201d\u4ed6\u82e5\u662f\u4e0d\u8868\u793a\u5f97 \u201c\u5927\u65b9\u201d\u4e00\u4e9b\uff0c\u6050\u6015\u4ee4\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u53e5\u8bdd\uff0c\u6211\u53ef\u8981\u8bf4\u5728\u524d\u5934\u3002\u6211\u662f\u4e0d\u613f\u89c1\u5230\u6b7b\u4f24\u7684\uff0c\u6240\u4ee5\u521a\u624d\u6211\u624d\u5316\u89e3 \u4f60\u4eec\u3002\u4f46\u73b0\u5728\u6211\u548c\u4f60\u8fc7\u62db\uff0c\u53ea\u6015\u6ca1\u6709\u4eba\u80fd\u591f\u5316\u89e3\uff0c\u6211\u82e5\u6b7b\u4e86\uff0c\u5f53\u7136\u4ece\u547d\uff0c\u4f60\u82e5\u6b7b\u4e86\u2014\u2014\u201d \u3000\u3000\u5c11\u5973\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u53ea\u5439\u8427\u966a\u4ed6\u73a9\u7684\uff0c\u4f60\u4e0d\u51fa\u624b\u6253\u4ed6\uff0c\u4ed6\u600e\u4e48\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u53ef\u8bf4\u4e0d\u5b9a\u554a\uff0c\u4ed6\u81ea\u884c\u5931\u8db3\uff0c\u4e5f\u4f1a\u8dcc\u6b7b\u7684\uff01\u201d \u3000\u3000\u54c8\u5fc5\u56fe\u51e0\u66fe\u53d7\u8fc7\u522b\u4eba\u5982\u6b64\u8f7b\u89c6\uff0c\u5f3a\u6291\u6076\u6c14\uff0c\u5927\u7b11\u4e09\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8c01\u6b7b\u4e86\u90fd\u4e0d\u80fd\u62b1 \u6028\uff01\u6211\u5012\u8981\u770b\u4f60\u5982\u4f55\u5728\u5341\u62db\u4e4b\u5185\u5c06\u6211\u51fb\u8d25\uff1f\u201d\u8bf4\u7f62\uff0c\u6a2a\u638c\u5f53\u80f8\u3002\u4e00\u6293\u5411\u6a80\u7fbd\u51b2\u7684\u7435\u7436\u9aa8\u6293 \u4e0b\uff01 \u3000\u3000\u5f53\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u6a80\u7fbd\u51b2\u5374\u8f6c\u8fc7\u8eab\u5b50\uff0c\u80cc\u5411\u7740\u4ed6\uff0c\u597d\u6574\u4ee5\u6687\u7684\u4e3e\u8d77\u738b\u8427\uff0c\u51d1\u8fd1\u5634\u8fb9\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5fc3\u5934\u5927\u6012\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u80c6\u6562\u5982\u6b64\u8f7b\u89c6\u4e8e\u6211\uff01\u201d\u624b\u4e0a\u52a0\u4e86\u628a\u52b2\uff0c\u4f7f\u51fa\u201c\u9f99\u722a\u624b\u201d\u7684 \u7edd\u62db\uff01 \u3000\u3000\u8fde\u90a3\u5c11\u5973\u90fd\u4e0d\u7981\u4e3a\u6a80\u7fbd\u51b2\u62c5\u5fc3\u4e86\uff0c\u5979\u662f\u89c1\u8bc6\u8fc7\u54c8\u5fc5\u56fe\u201c\u9f99\u722a\u624b\u201d\u7684\u5389\u5bb3\u7684\uff0c\u8fd9\u4e00\u6293\u53ef\u6b63 \u662f\u671d\u7740\u6a80\u7fbd\u51b2\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u6765\u7684\u554a\uff01\u6a80\u7fbd\u51b2\u5df2\u7ecf\u8bd5\u4e86\u4e24\u4e2a\u97f3\uff0c\u8fd8\u6ca1\u6709\u5439\u51fa\u66f2\u5b50\uff0c\u5ffd\u5730\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u4f60\u7684\u9f99\u722a\u5b50\u7ec3\u5f97\u8fd8\u7b97\u4e0d\u9519\uff0c\u4f46\u53ea\u51ed\u4e00\u638c\u4e4b\u529b\uff0c\u5982\u4f55\u4f24\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5012\u9000\u56de\u6765\uff0c\u975e\u4f46\u6ca1\u6709\u8eb2\u907f\uff0c\u800c\u4e14\u8fce\u7740\u54c8\u5fc5\u56fe\u7684\u90a3\u4e00\u6293\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u611f\u89c9\u4e00\u80a1\u65e0\u5f62\u529b\u9053\u5411\u4ed6\u88ad\u6765\uff0c\u4e0d\u89c9\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8fd9\u5c0f\u5b50\u80c6\u6562 \u5982\u6b64\u72c2\u5984\uff0c\u5b9a\u6709\u6240\u6043\u3002\u96be\u9053\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u5df2\u7ec3\u6210\u91d1\u521a\u4e0d\u574f\u8eab\u6cd5\u6216\u8005\u662f\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u4e0a\u4e58\u6b66 \u529f\uff1f\u201d \u3000\u3000\u4e24\u4eba\u8eab\u6cd5\u90fd\u5feb\uff0c\u6a80\u7fbd\u51b2\u597d\u50cf\u80cc\u540e\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u7a81\u7136\u4e00\u8038\u80a9\u5934\uff0c\u5c31\u5411\u4ed6\u5012\u649e\u8fc7\u53bb\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5fc3\u5934\u4e00\u51db\uff0c\u8fde\u5fd9\u7f29\u624b\u3002\u4f46\u6307\u5934\u4ecd\u7136\u662f\u7ed9\u78b0\u4e86\u4e00\u4e0b\uff0c\u89e6\u89c9\u679c\u7136\u597d\u50cf\u662f\u78b0\u7740\u94a2\u677f\u4e00 \u822c\u3002\u5e78\u4e8f\u4ed6\u7f29\u624b\u5f97\u5feb\uff0c\u9f99\u722a\u624b\u7684\u529b\u9053\u4ea6\u5df1\u6536\u56de\uff0c\u5426\u5219\u4ee5\u786c\u78b0\u786c\uff0c\u8fd9\u53ea\u624b\u53ea\u6015\u975e\u5f97\u62d7\u6298\u4e0d\u53ef\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53cc\u638c\u4e00\u8d77\u6765\u5427\uff01\u4f60\u7684\u5927\u6454\u7891\u624b\u52a0\u4e0a\u7ef5\u638c\u7684\u529f\u592b\uff0c\u6bd4\u53ea\u7528\u64d2\u9f99\u624b\u6216\u8005\u4f1a\u597d\u4e00 \u4e9b\u3002\u201d \u3000\u3000\u76d8\u9f99\u5c71\u90a3\u4e00\u6218\u4e4b\u540e\uff0c\u4ed6\u82e6\u7ec3\u4e86\u5341\u5e74\uff0c\u7ec3\u6210\u4e86\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592d\uff0c\u672c\u6765\u8981\u7528\u6765\u5bf9\u4ed8\u8036\u5f8b \u7384\u5143\u7684\uff0c\u8fd9\u4e00\u4e0b\u53cc\u638c\u9f50\u51fa\uff0c\u5bf9\u65b9\u7eb5\u7136\u662f\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4f46\u9664\u975e\u662f\u62e3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u5426\u5219\u90a3\u62a4\u4f53\u795e \u529f\u4e5f\u4f1a\u7ed9\u4ed6\u6240\u7834\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6a80\u7fbd\u51b2\u5df2\u7ecf\u8f6c\u8fc7\u4e86\u8eab\uff0c\u9762\u5411\u7740\u4ed6\u3002\u90a3\u652f\u7389\u8427\u51d1\u5728\u5634\u8fb9\uff0c\u4f46\u8427\u7684\u4e00\u7aef\u5374 \u662f\u6307\u5411\u54c8\u5fc5\u56fe\u7684\u638c\u5fc3\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u4e00\u770b\u4ed6\u7684\u8eab\u6cd5\uff0c\u81ea\u5df1\u82e5\u4e0d\u6536\u638c\uff0c\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\u5b9a\u4f1a\u7ed9\u4ed6\u7684\u7389\u8427\u6233\u4e2a\u6b63\u7740\u3002\u4ed6 \u5e76\u6ca1\u6709\u52a8\u624b\uff0c\u90a3\u662f\u81ea\u5df1\u51d1\u4e0a\u53bb\u6328\u4ed6\u7684\u7389\u8427\u4e00\u6233\u7684\u3002\u4ed6\u4e0d\u7b97\u8fdd\u80cc\u8bfa\u8a00\u3002 \u3000\u3000\u201c\u52b3\u5bab\u7a74\u201d\u82e5\u7136\u662f\u7ed9\u6696\u7389\u8427\u6233\u4e2a\u6b63\u7740\uff0c\u54c8\u5fc5\u56fe\u8fd9\u8eab\u5185\u529f\u975e\u7ed9\u5e9f\u4e86\u4e0d\u53ef\uff01\u54c8\u5fc5\u56fe\u6ca1\u6709\u628a\u63e1 \u4e00\u638c\u6253\u6b7b\u6a80\u7fbd\u51b2\uff0c\u4ed6\u53ef\u4e0d\u6562\u5192\u8fd9\u4e2a\u5371\u9669\u4e0e\u6a80\u7fbd\u51b2\u62fc\u547d\uff0c\u767e\u5fd9\u4e2d\u53ea\u597d\u79fb\u5f62\u6362\u4f4d\uff0c\u628a\u53cc\u638c\u6253\u51fa\u7684 \u65b9\u4f4d\u504f\u659c\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u82e5\u89c6\u4e0d\u89c1\uff0c\u60a0\u7136\u5439\u8d77\u8427\u6765\u3002\u5439\u7684\u662f\u5510\u4eba\u738b\u4e4b\u6da3\u4e00\u9996\u9898\u4e3a\u201c\u51c9\u5dde\u8bcd\u201d\u7684\u7684\u4e03\u8a00\u7edd \u53e5\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5999\u89e3\u97f3\u5f8b\uff0c\u6309\u62cd\u8f7b\u6b4c\uff1a\u201c\u9ec4\u6cb3\u8fdc\u4e0a\u767d\u4e91\u95f4\uff0c\u4e00\u7247\u5b64\u57ce\u4e07\u4ede\u5c71\u3002 \u3000\u3000\u2014\u2014\u2019\u54c8\u5fc5\u56fe\u51e0\u66fe\u53d7\u8fc7\u522b\u4eba\u5982\u6b64\u8511\u89c6\uff0c\u6c14\u607c\u4ea6\u52a0\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u82e5\u6536\u4e0d\u4e0b\u8fd9\u4e2a\u5c0f\u5b50\uff0c \u7ed9\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\u8fd8\u4e0d\u6253\u7d27\uff0c\u5fa1\u519b\u526f\u7edf\u9886\u7684\u5ea7\u4f4d\u53ea\u6015\u4e5f\u5750\u4e0d\u7a33\u4e86\uff01\u201d\u800c\u4ed6\u5374\u662f\u4e00\u5fc3\u60f3\u5347\u4efb\u6b63\u7edf \u9886\u7684\u3002 \u3000\u3000\u7a81\u7136\u4ed6\u5df2\u77e5\u9053\u6a80\u7fbd\u51b2\u7684\u6b66\u529f\u5b9e\u662f\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4f46\u6b64\u6218\u6709\u5173\u4ed6\u7684\u4e00\u751f\u8363\u8fb1\uff0c\u4ed6\u4e5f\u53ea\u597d\u6293 \u4f4f\u4e00\u63b7\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u54ac\u7259\u6839\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u62f3\u638c\u517c\u65bd\uff0c\u4e00\u62db\u201c\u4e94\u4e01\u5f00\u5c71\u201d\uff0c\u8ddf\u7740\u4e00\u62db\u201c\u5434\u521a\u4f10\u6842\u201d\u3002\u8fd9\u4e24 \u76f8\u90fd\u662f\u521a\u731b\u4e4b\u6781\u7684\u62db\u6570\uff0c\u4ed6\u53c8\u662f\u4ece\u4fa7\u9762\u653b\u51fb\uff0c\u907f\u514d\u7ed9\u6a80\u7fbd\u51b2\u7684\u7389\u8427\u201c\u51d1\u5de7\u201d\u70b9\u7740\u4ed6\u7684\u7a74\u9053\u3002 \u6a80\u7fbd\u51b2\u8bfa\u7136\u9075\u5b88\u8bfa\u8a00\uff0c\u4e0d\u7528\u7389\u8427\u5f53\u4f5c\u5175\u5668\u62db\u67b6\uff0c\u7eb5\u7136\u7ec3\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e5f\u975e\u5f97\u7ed9\u4ed6\u6253\u4f24\u4e0d\u53ef\u3002 \u3000\u3000\u8427\u58f0\u60a0\u626c\uff0c\u604d\u7136\u6d41\u6c34\u884c\u4e91\uff0c\u8c6a\u65e0\u963b\u6ede\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u8eab\u6cd5\u4e5f\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u5728\u638c\u98ce\u5f71\u4e4b\u4e2d\u6709\u82e5\u95f2\u5ead\u4fe1\u6b65\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u8fd9\u4e24\u62db\u90fd\u843d\u4e86\u7a7a\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6cd5\u7adf\u4f3c\u548c\u8bd7\u4e2d\u7684\u5883\u754c\u7b26\u5408\uff0c\u98d8\u9038\u6f47\u6d12\u800c\u53c8\u95e8\u6237\u68ee\u4e25\u3002 \u3000\u3000\u4f46\u54c8\u5fc5\u56fe\u4ea6\u5df2\u65e9\u6709\u51c6\u5907\uff0c\u8ddf\u7740\u7b2c\u4e09\u62db\u653b\u51fa\uff0c\u500f\u5730\u5316\u638c\u4e3a\u6307\uff0c\u70b9\u7740\u4e86\u6a80\u7fbd\u51b2\u80c1\u4e0b\u7684\u6108\u6c14 \u7a74\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u72ec\u95e8\u201c\u91d1\u521a\u6307\u201d\u7684\u529f\u592b\uff0c\u4ed6\u7ec3\u7684\u8fd9\u95e8\u529f\u592b\u4e5f\u6b63\u662f\u8981\u7528\u6765\u5bf9\u4ed8\u8036\u5f8b\u7384\u5143\u7684\uff0c\u6b64\u9645 \u5148\u7528\u8036\u5f8b\u7384\u5143\u7684\u5f92\u5f1f\u8eab\u4e0a\u3002\u91d1\u521a\u6307\u4e5f\u662f\u53ef\u4ee5\u7834\u5f97\u201c\u91d1\u949f\u7f69\u201d\u201c\u94c1\u5e03\u886b\u201d\u4e4b\u7c7b\u7684\u62a4\u4f53\u5185\u529f\u7684\u3002 \u201c\u62a4\u4f53\u795e\u529f\u201d\u6700\u9ad8\u7684\u5883\u754c\u662f\u201c\u91d1\u521a\u4e0d\u574f\u8eab\u6cd5\u201d\uff0c\u4f46\u90a3\u975e\u5f97\u6709\u6570\u5341\u5e74\u529f\u529b\u4e0d\u884c\u3002\u6a80\u7fbd\u51b2\u5e74\u7eaa\u8f7b \u8f7b\uff0c\u54c8\u5fc5\u56fe\u6599\u60f3\u4ed6\u9876\u591a\u4e0d\u8fc7\u7ec3\u6210\u201c\u91d1\u949f\u7f69\u201d\u6216\u201c\u94c1\u5e03\u886b\u201d\u800c\u5df1\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u70b9\u4e2a\u6b63\u7740\uff0c\u800c\u4e14\u65e0\u53cd\u9707\u4e4b\u529b\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u54c8\u5fc5\u56fe\u4e0d\u7981\u5fc3\u5934\u5927\u559c\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u8fde \u91d1\u949f\u7f69\u548c\u94c1\u5e03\u886b\u7684\u529f\u592b\u90fd\u8fd8\u672a\u7ec3\u6210\uff01\u201d \u3000\u3000\u90a3\u77e5\u4ed6\u8fd8\u662f\u6b22\u559c\u5f97\u592a\u65e9\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u597d\u50cf\u65e0\u77e5\u89c9\uff0c\u4ed6\u975e\u4f46\u6ca1\u6709\u5012\u4e0b\u53bb\uff0c\u53cd\u800c\u4e00\u4e2a\u8098\u9524\u5411\u54c8\u5fc5\u56fe\u649e\u8fc7\u6765\u3002 \u3000\u3000\u539f\u6765\u6a80\u7fbd\u51b2\u7684\u786e\u662f\u5c1a\u672a\u7ec3\u6210\u4e0a\u4e58\u62a4\u4f53\u795e\u529f\uff0c\u4ee5\u4ed6\u73b0\u6709\u7684\u5185\u529f\u9020\u8be3\uff0c\u62b5\u5fa1\u64d2\u9f99\u722a\u624b\u53ef\u4ee5\uff0c \u62b5\u5fa1\u91d1\u521a\u6307\u70b9\u7a74\u7684\u529f\u592b\u8fd8\u662f\u4e0d\u884c\u7684\u3002\u4f46\u4ed6\u5374\u53e6\u6709\u4e00\u95e8\u975e\u5e38\u602a\u5f02\u7684\u529f\u592b\u3002 \u3000\u3000\u8fd9\u95e8\u529f\u592b\u53eb\u505a\u632a\u79fb\u7a74\u9053\uff0c\u7ecf\u8fc7\u632a\u79fb\uff0c\u7a74\u9053\u539f\u6765\u7684\u4f4d\u7f6e\u5df2\u53d8\uff0c\u7eb5\u7136\u662f\u70b9\u7740\u6b7b\u7a74\uff0c\u90a3\u4e5f\u65e0 \u59a8\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u6a80\u7fbd\u51b2\u7ed9\u4ed6\u7684\u91d1\u521a\u6307\u70b9\u4e2a\u6b63\u7740\uff0c\u5df2\u6709\u70b9\u706b\u8fa3\u8fa3\u7684\u611f\u89c9\uff0c\u5fc3\u91cc\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5 \u5e78\uff01\u201d\u3002 \u3000\u3000\u539f\u6765\u4ee5\u4ed6\u539f\u6709\u5185\u529f\u9020\u8be3\uff0c\u5047\u5982\u4e0d\u662f\u8fd0\u7528\u632a\u79fb\u7a74\u9053\u7684\u529f\u592b\uff0c\u867d\u7136\u8fd8\u662f\u4e0d\u81f3\u4e8e\u6b7b\uff0c\u4f46\u5374\u5f88\u6709 \u53ef\u80fd\u53d8\u6210\u4e24\u8d25\u4ff1\u4f24\u3002\u9634\u5dee\u9633\u9519\uff0c\u54c8\u5fc5\u56fe\u66fe\u7ecf\u5728\u548c\u90a3\u4e2a\u5c11\u5973\u4ea4\u624b\u4e4b\u65f6\uff0c\u5c31\u4f7f\u51fa\u4e86\u4ed6\u7684\u72ec\u95e8\u91d1\u521a \u6307\u529f\u592b\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u7b49\u4e8e\u6cc4\u4e86\u5e95\uff0c\u6a80\u7fbd\u51b2\u6709\u4e86\u51c6\u5907\uff0c\u5f53\u7136\u61c2\u5f97\u7528\u6700\u9002\u5f53\u7684\u65b9\u6cd5\u53bb\u5e94\u4ed8\u4ed6\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5316\u89e3\u4e86\u7a74\u9053\u88ab\u5c01\u4e4b\u5371\uff0c\u9a6c\u4e0a\u6709\u201c\u8098\u9524\u201d\u8fd8\u51fb\uff0c\u8427\u58f0\u4e5f\u672a\u505c\u6b62\uff0c\u53ea\u662f\u8098\u5c16\u5411\u5bf9\u65b9\u649e \u53bb\uff0c\u5f53\u7136\u4e0d\u80fd\u7b97\u662f\u8fdd\u80cc\u8bfa\u8a00\u3002\u800c\u4e14\u4ed6\u7684\u8eab\u6cd5\u6b65\u6cd5\u914d\u5408\u5f97\u6070\u5230\u597d\u5904\uff0c\u770b\u8d77\u6765\u5c31\u597d\u50cf\u54c8\u5fc5\u56fe\u4e3b\u52a8 \u649e\u4ed6\u4e00\u822c\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5927\u60ca\u4e4b\u4e0b\uff0c\u54ea\u91cc\u8fd8\u6562\u5f3a\u653b\uff0c\u6025\u5fd9\u53d8\u62db\uff0c\u4f7f\u51fa\u4e00\u62db\u201c\u5982\u5c01\u4f3c\u95ed\u201d \u3000\u3000\u3002\u8fd9\u4e00\u62db\u662f\u4ee5\u9632\u5b88\u4e3a\u4e3b\u7684\uff0c\u4f7f\u5f97\u8fd8\u7b97\u9002\u5f53\uff0c\u638c\u5fc3\u5728\u6a80\u7fbd\u51b2\u8fd9\u4e00\u201c\u8098\u9524\u201d\u7684\u4e09\u5206\u529b\u9053\uff0c\u8fd8 \u662f\u4e0d\u7531\u5df2\u7684\u8e09\u8dc4\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5531\u51fa\u4e86\u66f2\u8c03\u7684\u7b2c\u4e09\u53e5\uff1a\u201c\u7f8c\u7b1b\u4f55\u987b\u6028\u6768\u67f3\u3002\u201d\u63a5\u7740\u7b11\u9053\uff1a\u201c\u5341\u62db\u5df2\u8fc7\u4e86\u4e00\u534a\uff0c\u5df2 \u662f\u7b2c\u516d\u62db\u4e86\uff0c\u8bf7\u5927\u5bb6\u8bf4\uff0c\u6211\u6ca1\u6570\u9519\u5427\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6709\u8a00\u5728\u5148\uff0c\u5341\u62db\u4e4b\u5185\uff0c\u82e5\u662f\u4e0d\u80fd\u628a\u54c8\u5fc5\u56fe\u6253\u4e0b\u53f0\u53bb\uff0c\u5c31\u7b97\u8f93\u3002 \u3000\u3000\u53f0\u4e0b\u4e00\u4f17\u5bbe\u5ba2\uff0c\u4eba\u4eba\u90fd\u662f\u62b1\u7740\u597d\u5947\u4e4b\u5fc3\uff0c\u8981\u770b\u4ed6\u600e\u6837\u5439\u7740\u8427\uff0c\u4e0d\u51fa\u624b\uff0c\u5c31\u628a\u5bf9\u65b9\u6253\u4e0b\u64c2 \u53f0\uff0c\u6bcf\u4e00\u62db\u6bcf\u4e00\u5f0f\u5f53\u7136\u90fd\u662f\u51dd\u795e\u6ce8\u89c6\u3002\u6bcf\u4e00\u4e2a\u4eba\u90fd\u770b\u5f97\u6e05\u695a\uff0c\u7684\u7684\u786e\u786e\u662f\u5df2\u7ecf\u8fc7\u4e86\u516d\u62db\u3002\u4e0d \u8fc7\u522b\u7684\u4eba\u6ca1\u6709\u50cf\u90a3\u5c11\u5973\u53eb\u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u4f46\u5c11\u5973\u8fd9\u4e00\u9ad8\u58f0\u62a5\u6570\u5374\u4e5f\u63d0\u9192\u4e86\u54c8\u5fc5\u56fe\uff0c\u4ed6\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u4f55\u5fc5\u8ddf\u8fd9\u5c0f\u5b50\u8fd1\u8eab\u7f20 \u6597\uff0c\u8d76\u5feb\u628a\u5269\u4e0b\u7684\u56db\u62db\u4f7f\u5b8c\u4e5f\u5c31\u662f\u4e86\uff01\u201d\u6b64\u65f6\u4ed6\u5df2\u662f\u4e0d\u6562\u5962\u671b\u6c42\u80dc\uff1b\u53ea\u6c42\u80fd\u591f\u5728\u5341\u62db\u4e4b\u5185\u4fdd \u6301\u4e0d\u8d25\u4e8e\u613f\u4ea6\u5df2\u662f\u7b97\u4ed6\u201c\u80dc\u201d\u4e86\u3002 \u3000\u3000\u4e3b\u610f\u6253\u5b9a\uff0c\u4ed6\u8d76\u5fd9\u9000\u51fa\u4e09\u4e08\u4e4b\u5916\uff0c\u4ee5\u5168\u529b\u4f7f\u51fa\u62a4\u8eab\u7684\u56db\u62db\u3002 \u3000\u3000\u8fd9\u201c\u64c2\u53f0\u201d\u662f\u501f\u7528\u56ed\u4e2d\u539f\u6709\u7684\u620f\u53f0\u7684\uff0c\u54c8\u5fc5\u56fe\u9000\u51fa\u4e09\u4e08\u4e4b\u5916\uff0c\u5df2\u7ecf\u662f\u63a5\u8fd1\u620f\u53f0\u7684\u8fb9\u7f18 \u4e86\u3002\u4ed6\u53ea\u60f3\u5230\u8981\u907f\u514d\u4e0e\u6a80\u7fbd\u51b2\u8ddd\u79bb\u592a\u8fd1\uff0c\u5374\u6ca1\u60f3\u5230\u6709\u4e00\u5229\u4ea6\u6709\u4e00\u5f0a\u3002 \u3000\u3000\u4ed6\u907f\u5f00\u6a80\u7fbd\u51b2\uff0c\u6a80\u7fbd\u51b2\u5374\u5411\u4ed6\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u98de\u5feb\u53d1\u62db\uff0c\u800c\u4e14\u662f\u5168\u529b\u65bd\u4e3a\u3002\u7ad9\u5728\u53f0\u4e0b\u7684\u4eba\u90fd\u611f\u89c9\u52b2\u98ce\u6251\u9762\u3002 \u3000\u3000\u5018\u82e5\u6a80\u7fbd\u51b2\u4e0d\u662f\u51fa\u624b\u653b\u51fb\u7684\u8bdd\uff0c\u5728\u4ed6\u8fd9\u6837\u5168\u529b\u9632\u5b88\u4e4b\u4e0b\uff0c\u662f\u7edd\u4e0d\u53ef\u80fd\u5c06\u4ed6\u6253\u4e0b\u64c2\u53f0\u7684\u3002 \u4f55\u51b5\u53ea\u5269\u4e0b\u56db\u62db\u3002 \u3000\u3000\u4e0d\u4f46\u53f0\u4e0b\u7684\u89c2\u6218\u8005\u8fd9\u6837\u60f3\uff0c\u53f0\u4e0a\u7684\u54c8\u5fc5\u56fe\u4e5f\u662f\u8fd9\u6837\u60f3\u3002\u4ed6\u907f\u514d\u4e0e\u6a80\u7fbd\u51b2\u8fd1\u8eab\u7f20\u6597\uff0c\u4e3a\u7684 \u5c31\u662f\u62c9\u8fdc\u8ddd\u79bb\u8fd9\u7247\u523b\u95f4\u98de\u5feb\u53d1\u62db\u3002 \u3000\u3000\u7b2c\u4e03\u62db\u3001\u7b2c\u516b\u62db\u3001\u7b2c\u4e5d\u62db\u2026\u2026\u6a80\u7fbd\u51b2\u8d70\u5230\u4ed6\u9762\u524d\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u662f\u53d1\u51fa\u7b2c\u4e5d\u62db\u4e86\uff01 \u3000\u3000\u8fde\u90a3\u5c11\u5973\u90fd\u4ed6\u62c5\u5fc3\uff0c\u5fd8\u4e86\u6570\u7b2c\u51e0\u62db\u4e86\u3002 \u3000\u3000\u5728\u4ed6\u53d1\u51fa\u7b2c\u4e5d\u62db\u7684\u65f6\u5019\uff0c\u6a80\u7fbd\u51b2\u7684\u8fd9\u652f\u66f2\u5b50\u521a\u597d\u5439\u594f\u5b8c\u6bd5\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u53f9\u4e86\u4e00\u58f0\uff1a\u201c\u4e16\u65e0\u77e5\u97f3\uff0c\u771f\u662f\u4ee4\u6211\u5931\u671b\uff01\u201d \u3000\u3000\u9661\u5730\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u542c\u6211\u5439\u8427\uff0c\u4f60\u5c31\u7ed9\u6211\u6eda\u4e0b\u53bb\u5427\uff01\u6211\u5439\u6211\u7684\uff0c\u7528\u4e0d\u7740\u4f60\u5728\u53f0\u4e0a\u542c\uff01\u201d \u3000\u3000\u8427\u58f0\u201c\u545c\u201d\u7684\u4e00\u58f0\u53c8\u54cd\u8d77\u6765\u3002\u54c8\u5fc5\u56fe\u7684\u7b2c\u5341\u628a\u521a\u521a\u201c\u8d77\u5f0f\u201d\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u968f\u7740\u90a3\u4e00\u58f0\u201c\u6eda\u201d\u5b57\uff0c\u54c8\u5fc5\u56fe\u5f53\u771f\u5982\u5949\u8c15\u65e8\uff0c\u7a81\u7136\u95f4\u5c31\u4ece\u53f0\u4e0a\u8dcc\u4e0b\u6765\u3002 \u3000\u3000\u5f52\u4e91\u5e84\u7684\u4eba\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u7eb7\u7eb7\u8dd1\u6765\u6276\u4ed6\u3002\u53ea\u89c1\u54c8\u5fc5\u56fe\u4e03\u7a83\u6d41\u8840\uff0c\u4e00\u63a2\u9f3b\u606f\uff0c\u547c\u5438 \u5df1\u7edd\uff0c\u679c\u7136\u771f\u7684\u662f\u6b7b\u4e86\u3002 \u3000\u3000\u539f\u6765\u6a80\u7fbd\u51b2\u8fd9\u652f\u6696\u7389\u8427\u4e43\u662f\u6b66\u6797\u5f02\u5b9d\uff0c\u4ed6\u5728\u8427\u4e2d\u5439\u51fa\u7eaf\u9633\u7f61\u6c14\uff0c\u5a01\u529b\u6781\u5927\uff0c\u8fd9\u80a1\u7f61\u6c14\uff0c \u662f\u521a\u597d\u5bf9\u7740\u54c8\u5fc5\u56fe\u638c\u5fc3\u7684\u52b3\u529f\u7a74\u5439\u8fc7\u53bb\u7684\u3002 \u3000\u3000\u54c8\u5fc5\u56fe\u5185\u529f\u88ab\u7834\uff0c\u6700\u540e\u8fd9\u4e09\u62db\u51fa\u7684\u638c\u529b\uff0c\u53c8\u88ab\u6a80\u7fbd\u51b2\u5728\u4e00\u6325\u8896\u95f4\u903c\u4e86\u56de\u53bb\u3002\u4ed6\u5931\u4e86\u5185 \u529f\uff0c\u5982\u4f55\u7981\u53d7\u5f97\u8d77\uff0c\u4e00\u8dcc\u843d\u53f0\u4e0b\uff0c\u6027\u547d\u7acb\u5373\u4e0d\u4fdd\uff01 \u3000\u3000\u5c11\u5973\u8fd9\u624d\u677e\u4e86\u53e3\uff0c\u5531\u51fa\u4e86\u6700\u540e\u4e00\u53e5\u8bd7\uff1a\u201c\u6625\u98ce\u4e0d\u5ea6\u7389\u95e8\u5173\u3002\u201d\u63a5\u7740\u7b11\u9053\uff1a\u201c\u4f60\u7684\u66f2\u5b50\u5439 \u5f97\u771f\u5947\u5999\uff0c\u66f2\u7ec8\u4eba\u6563\uff0c\u5b89\u6392\u5f97\u6070\u5230\u597d\u5904\uff0c\u521a\u597d\u662f\u7b2c\u4e5d\u62db\uff01\u4f17\u4f4d\u82f1\u96c4\uff0c\u6211\u6ca1\u6570\u9519\u628a\uff1f\u7b2c\u5341\u62db\u672b \u4f7f\u5230\u4e00\u534a\uff0c\u662f\u8fd8\u4e0d\u80fd\u7b97\u4f5c\u4e00\u62db\u7684\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u53f0\u4e0b\u4e00\u4f17\u5bbe\u5ba2\u90fd\u60ca\u5f97\u5446\u4e86\u3002\u5373\u4f7f\u6709\u4eba\u8d77\u8981\u53bb\u62ff\u6a80\u7fbd\u51b2\u9886\u529f\uff0c\u4f46\u4e00\u60f3\uff0c\u54c8\u5fc5\u56fe\u4ee5\u5fa1\u6797\u519b\u526f \u7edf\u9886\u7684\u8eab\u4efd\uff0c\u8fd9\u5c11\u5e74\u4e0d\u51fa\u624b\u5c31\u80fd\u5c06\u4ed6\u201c\u6cbb\u6b7b\u201d\uff0c\u81ea\u5df1\u7684\u6b66\u529f\u8fde\u54c8\u5fc5\u56fe\u7684\u5341\u5206\u4e4b\u4e00\u90fd\u6bd4\u4e0d\u4e0a\uff0c \u5982\u4f55\u8fd8\u6562\u4e0a\u53bb\u9001\u6b7b\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u53f0\u4e0b\u9759\u5bc2\u5982\u6b7b\u3002\u5c11\u5973\u7684\u53d1\u95ee\uff0c\u5f53\u7136\u662f\u6ca1\u6709\u4eba\u56de\u7b54\u7684\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u9519\u4e86\uff0c\u5728\u8fd9\u4e16\u4e0a\u6211\u6700\u5c11\u8fd8\u6709\u4e00\u4e2a\u77e5\u97f3\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9765\u5982\u82b1\uff0c\u8bf4\u9053\u4e8c\u201c\u591a\u8c22\u3002\u4f46\u8fd9\u77e5\u97f3\u4e0d\u6bd4\u90a3\u77e5\u97f3\uff0c\u6211\u8fd9\u77e5\u97f3\uff0c\u53ea\u662f\u542c\u5f97\u61c2\u4f60\u7684\u8427 \u58f0\u7684\u77e5\u97f3\u3002\u201d \u3000\u3000\u5fa1\u6797\u519b\u526f\u7edf\u9886\u88ab\u6740\uff0c\u8fd9\u662f\u4f55\u7b49\u5927\u4e8b\uff1b\u5f52\u4e91\u5e84\u7684\u4eba\uff0c\u5df2\u662f\u90fd\u7ed9\u5413\u7684\u60ca\u614c\u5931\u63aa\uff0c\u4e0d\u77e5\u600e\u6837\u5bf9 \u4ed8\u773c\u524d\u4e4b\u4e8b\u624d\u597d\u3002\u53cd\u800c\u662f\u6740\u4e86\u4eba\u7684\u201c\u4e3b\u72af\u201d\uff08\u6a80\u7fbd\u51b2\uff09 \u3000\u3000\u548c\u534f\u52a9\u6740\u4eba\u7684\u201c\u4ece\u72af\u201d\uff08\u90a3\u4f4d\u5c11\u5973\uff09\uff0c\u5374\u50cf\u6ca1\u4e8b\u4eba\u4f3c\u7684\uff0c\u8fd8\u5728\u53f0\u4e0a\u597d\u4f3c\u4e24\u60c5\u76f8\u60a6\u7684\u7537\u5973 \u5728\u201c\u6253\u60c5\u9a82\u4fcf\u201d\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u80e1\u626f\u4e86\uff0c\u4f60\u4e0d\u8d70\u6211\u53ef\u8981\u8d70\u5566\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u662f\u5e94\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u8bf4\u9053\u4e00\u4e2a\u201c\u8d70\u201d\u5b57\uff0c\u4e24\u4eba\u540c\u65f6\u98de\u8eab\u800c\u8d77\u3002\u5c31\u50cf\u4e24\u53ea\u5927\u9e1f\u4e00\u822c\uff0c\u4ece\u53f0\u4e0b\u4e00\u7247\u9ed1\u538b\u538b\u7684\u4eba\u5934 \u4e0a\u98de\u63a0\u8fc7\u53bb\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u4e5d \u56de\u3000\u6d6e\u840d\u9aa4\u6563 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u4e5d \u56de\u3000\u6d6e\u840d\u9aa4\u6563 \u3000\u3000\u4ed6\u4eec\u5728\u5728\u5916\u62a2\u4e86\u5b98\u519b\u7684\u4e24\u5339\u5750\u9a91\u3002 \u3000\u3000\u8fdb\u5165\u5c71\u533a\uff0c\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u54b1\u4eec\u53ef\u4ee5\u6b47\u4e00\u6b47\u4e86\u5427\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\u201d\uff0c\u4e0b\u9a6c\u4e0e \u90a3\u5c11\u5973\u5e76\u80a9\u800c\u5750\u3002 \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u5927\u54e5\u591a\u8c22\u4f60\u5e2e\u4e86\u6211\u7684\u5927\u5fd9\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u4f60\u7684\u8d35\u59d3\u5927\u540d\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4f60\u548c\u54c8\u5fc5\u56fe\u5148\u6253\u4e00\u626c\uff0c\u6211\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u5bb9\u6613\u5c31\u6740\u4e86\u4ed6\u3002\u54b1\u4eec\u540c\u4ec7\u654c \u5ffe\uff0c\u8bf4\u4e0d\u4e0a\u8c01\u5e2e\u8c01\u7684\u5fd9\u3002\u201d\u5c11\u5973\u9053\uff1a\u201c\u4f60\u8bf4\u5440\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u7684\u59d3\u540d\u5440\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u59d3\u540d\u4e0d\u8fc7\u662f\u4e2a\u8bb0\u53f7\uff0c\u6211\u5df2\u7ecf\u8bf4\u4e86\u54b1\u4eec\u8c01\u4e5f\u4e0d\u7528\u611f\u8c22\u8c01\uff0c\u4f60\u8fd8\u8981\u77e5\u9053\u6211\u7684\u59d3 \u540d\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4ed6\u65e5\u76f8\u9022\uff0c\u6211\u603b\u4e0d\u80fd\u8001\u662f\u53eb\u4f60\u505a\u5927\u54e5\u5440\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u662f\u5076\u7136\u76f8\u9047\uff0c\u597d\u6bd4\u6d6e\u840d\u9aa4\u6563\uff0c\u4e24\u7247\u6d6e\u840d\u968f\u6c34\u98d8\u6d41\uff0c\u4e00\u5206\u5f00\u53ea\u662f\u6015\u518d \u96be\u76f8\u805a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u662f\u56e0\u4e3a\u81ea\u5df1\u7684\u8eab\u4e16\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u53ea\u6015\u5728\u901a\u9053\u59d3\u540d\u4e4b\u540e\uff0c\u8fd9\u5c11\u5973\u8fd8\u8981\u76d8\u6839\u95ee\u5e95\uff0c\u6545\u800c\u4e0d \u60f3\u548c\u8fd9\u5c11\u5973\u8fdb\u4e00\u6b65\u7ed3\u4ea4\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u5c11\u5973\u660e\u8273\u52a8\u4eba\uff0c\u60f3\u5230\u540e\u4f1a\u65e0\u671f\uff0c\u4ed6\u5728\u8bf4\u4e86\u8fd9\u756a\u8bdd\u4e4b\u540e\uff0c\u5374\u4e5f\u7981\u6709\u70b9\u9eef\u7136\u3002\u5c11\u5973\u6ce8\u89c6 \u4ed6\u7684\u795e\u8272\uff0c\u4f46\u4e5f\u6ca1\u6709\u8ffd\u95ee\u4e0b\u53bb\u4e86\u3002\u5c11\u5973\u4e0d\u5f00\u53e3\uff0c\u4ed6\u5012\u662f\u9887\u6709\u6b49\u610f\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff0c \u4e0d\u662f\u602a\u6211\u5427\uff01\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u597d\uff0c\u4eba\u751f\u79bb\u5408\uff0c\u672c\u4f3c\u6d6e\u840d\u9aa4\u6563\uff0c\u6211\u602a\u4f60\u505a\u4ec0\u4e48\uff1f\u4e0d\u8fc7\uff0c\u6211\u5374\u7684\u786e\u662f\u5728 \u60f3\u7740\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u8fd9\u652f\u7389\u8427\u771f\u662f\u4e00\u4ef6\u5b9d\u7269\uff0c\u53ef\u4e0d\u53ef\u4ee5\u501f\u6211\u77a7\u77a7\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u662f\u77e5\u97f3\u5165\uff0c\u53ef\u60dc\u8fd9\u652f\u7389\u8427\u4e0d\u662f\u5c5e\u4e8e\u6211\u7684\uff0c\u5426\u5219\u9001\u7ed9\u4f60\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u6562\u3002\u201d\u63a5\u8fc7\u66a7\u7389\u8427\uff0c\u6469\u5a11\u4e00\u4f1a\uff0c\u5ffd\u5730\u5439\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u542c\uff0c\u4e0d\u89c9\u5927\u4e3a\u8be7\u5f02\u3002 \u3000\u3000\u539f\u6765\u5979\u5439\u7684\u8fd9\u652f\u66f2\u5b50\uff0c\u4e5f\u662f\u4ed6\u7684\u5e08\u7236\u6700\u559c\u6b22\u5439\u594f\u7684\u4e00\u652f\u66f2\u5b50\u3002\u4ed6\u5728\u5fc3\u4e2d\u6309\u7740\u8282\u62cd\uff0c\u9ed8\u5ff5 \u6b4c\u8f9e\u3002 \u3000\u3000\u201c\u6d1b\u6d66\u98ce\u5149\u70c2\u6f2b\u65f6\uff0c\u5e72\u91d1\u5f00\u5bb4\u9189\u4e3a\u671f\u3002 \u3000\u3000\u82b1\u65b9\u7740\u96e8\u72b9\u542b\u7b11\uff0c\u8776\u4e0d\u7981\u5bd2\u603b\u662f\u75f4\u3002 \u3000\u3000\u6a80\u6655\u5410\uff0c\u7389\u534e\u6ecb\uff0c\u4e0d\u968f\u6843\u674e\u51b5\u6625\u975e\u3002 \u3000\u3000\u4e1c\u541b\u81ea\u6709\u56de\u5929\u529b\uff0c\u770b\u628a\u82b1\u679d\u5e26\u6708\u5f52\u3002\u201d \u3000\u3000\u751a\u81f3\u8fde\u5439\u594f\u51fa\u6765\u7684\u90a3\u79cd\u201c\u97f5\u5473\u201d\uff0c\u4e5f\u662f\u548c\u4ed6\u7684\u5e08\u7236\u4e00\u6837\u3002\u8427\u58f0\u521d\u8d77\uff0c\u76f8\u5f53\u8f7b\u5feb\uff0c\u597d\u50cf\u5e26 \u6765\u4e00\u7247\u660e\u5a9a\u7684\u6625\u5149\uff0c\u4f46\u6e10\u6e10\u5c31\u6709\u4e86\u51c4\u51c9\u7684\u610f\u5473\uff0c\u4e0d\u8fc7\u5728\u51c4\u51c9\u4e4b\u4e2d\uff0c\u4e5f\u8fd8\u662f\u6709\u7740\u201c\u671f\u5f85\u201d\u7684\u3002 \u5c11\u5973\u594f\u7f62\uff0c\u8bf4\u9053\uff1a\u201c\u73ed\u95e8\u5f04\u65a7\uff0c\u89c1\u7b11\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u4f46\u662f\u77e5\u97f3\u4eba\uff0c\u8fd8\u662f\u6b64\u9053\u9ad8\u624b\u5462\uff1f\u55ef\uff0c\u6211\u8bf4\u7684\u4e0d\u662f\u5ba2\u5957\u8bdd\uff0c\u4f60\u771f\u662f \u5439\u7684\u5f88\u597d\u3002\u201d \u3000\u3000\u5c11\u5973\u7b11\u9765\u5982\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u201d\uff0c\u628a\u7389\u8427\u4ea4\u8fd8\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5fcd\u4e0d\u4f4f\u597d\u5947\u5fc3\uff0c\u8fdf\u7591\u7247\u523b\uff0c\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u6559\u4f60\u5439\u8fd9\u652f\u66f2\u5b50\u7684\u4eba\u662f\u8c01\uff0c\u4f60\u53ef\u4ee5\u544a\u8bc9 \u6211\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u662f\u3002\u6211\u53ea\u662f\u4e00\u65f6\u597d\u5947\uff0c\u968f\u53e3\u95ee\u95ee\u800c\u5df2\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5012\u60f3\u95ee\u4f60\uff0c\u77e5\u4e0d\u77e5\u9053\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u8036\u5f8b\u7384\u5143\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u56e0\u4f55\u8981\u95ee\u6211\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u8036\u5f8b\u7384\u5143\u662f\u5f53\u4eca\u4e4b\u4e16\uff0c\u8427\u5439\u5f97\u6700\u597d\u7684\u4eba\u3002\u542c\u8bf4\u4ed6\u6709\u4e00\u652f\u7389\u8427\uff0c\u5439\u51fa\u6765\u7684\u4e50\u58f0 \u7279\u522b\u597d\u58f0\uff0c\u800c\u4e14\u4ed6\u8fd9\u652f\u7389\u8427\u8fd8\u53ef\u4ee5\u5f53\u4f5c\u5175\u5668\u7684\u3002\u4f60\u7684\u8427\u5439\u5f97\u5f88\u597d\uff0c\u4f60\u7684\u8427\u8fd8\u53ef\u4ee5\u5f53\u4f5c\u5175\u5668 \u7684\uff0c\u4f60\u7684\u7389\u8427\u540c\u6837\u4e5f\u662f\u4e00\u4ef6\u5b9d\u8d1d\u3002\u6545\u6b64\u6211\u5fcd\u4e0d\u4f4f\u597d\u5947\uff0c\u5c31\u8981\u95ee\u4e00\u95ee\u4f60\u4e86\u3002\u6211\u60f3\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053 \u8fd9\u4e2a\u4eba\u7684\uff0c\u662f\u5427\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6ca1\u8bf4\u201c\u662f\u201d\uff0c\u4e5f\u6ca1\u8bf4\u201c\u4e0d\u662f\u201d\uff0c\u5374\u53cd\u95ee\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5bf9\u8036\u5f8b\u7384\u5143\u5012\u597d\u4f3c\u77e5\u9053\u5f97 \u4e0d\u5c11\uff0c\u8bf7\u95ee\u4f60\u8fd8\u77e5\u9053\u4ed6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6211\u8fd8\u77e5\u9053\u4ed6\u662f\u8fbd\u56fd\u7684\u738b\u5b50\uff0c\u4e0d\u8fc7\u5374\u662f\u4e2a\u79c1\u751f\u5b50\u3002\u4ed6\u7684\u6b66\u529f\u548c\u4ed6\u7684\u5439\u8427\u4e00\u6837\uff0c \u90fd\u662f\u4e16\u4e0a\u65e0\u53cc\u3002\u53ef\u60dc\u4ed6\u6837\u6837\u90fd\u597d\uff0c\u5c31\u662f\u547d\u8fd0\u4e0d\u597d\u3002\u4ed6\u559c\u6b22\u7684\u5973\u5b50\u5ac1\u4e86\u522b\u4eba\uff0c\u800c\u4e14\u4e5f\u662f\u505a\u4e86\u51e0 \u5e74\u738b\u5b50\uff0c\u5c31\u906d\u53d7\u56fd\u7834\u5bb6\u4ea1\u4e4b\u7978\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u60ca\u7591\u4e0d\u5b9a\uff0c\u76ef\u7740\u5979\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u544a\u8bc9\u6211\uff0c\u5374\u8981\u6211\u544a\u8bc9\u4f60\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9ed8\u7136\u4e0d\u8bed\uff0c\u5c11\u5973\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4ea4\u6362\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u600e\u4e48\u4ea4\u6362\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u6211\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\u540c\u6837\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u8bf4\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u4e2a\u4eba\u771f\u662f\u534a\u70b9\u4e5f\u4e0d\u80af\u5403\u4e8f\u3002\u4e5f\u7f62\uff0c\u4f60\u4e0d\u80af\u5403\u4e8f\uff0c\u5c31\u8ba9\u6211\u5148\u8bf4\u3002\u6211\u590d \u59d3\u8d6b\u8fde\uff0c\u53cc\u540d\u6e05\u6ce2\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u59d3\u6a80\uff0c\u540d\u7fbd\u51b2\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6a80\u59d3\u662f\u91d1\u56fd\u7684\u5927\u59d3\uff0c\u4f60\u662f\u91d1\u56fd\u4eba\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u5c31\u602a\u4e86\uff0c\u81ea\u5df1\u662f\u54ea\u4e00\u56fd\u4eba\u600e\u7684\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\u5947\u602a\uff0c\u6211\u7684\u7239\u7239\u662f\u91d1\u56fd\u4eba\uff0c\u5988\u5988\u662f\u5b8b\u56fd\u4eba\uff0c\u4f60\u8bf4\u6211\u5e94\u8be5\u662f\u91d1\u56fd\u4eba \u8fd8\u662f\u5b8b\u56fd\u4eba\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u6211\u662f\u8fd9\u8fbd\u56fd\u4eba\uff0c\u56fd\u4e3a\u6211\u7684\u7239\u7239\u662f\u8fbd\u56fd\u4eba\uff0c\u5988\u5988\u4e5f\u662f\u8fbd\u56fd\u4eba\u3002\u201d\u5176 \u5b9e\u6a80\u7fbd\u51b2\u662f\u65e9\u77e5\u9053\u5979\u662f\u8fbd\u56fd\u4eba\u4e86\uff0c\u56e0\u4e3a\u201c\u8d6b\u8fde\u201d\u4e5f\u662f\u8fbd\u56fd\u4eba\u7684\u5927\u59d3\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u77e5\u9053\u8eab\u4efd\u662f\u8fbd\u56fd\u738b\u5b50\u7684\u8036\u5f8b\u7384\u5143\uff0c\u4f60\u662f\u8fbd\u56fd\u7684\u8d35\u65cf\u5427\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u4f3c\u4e4e\u5e94\u8be5\u8f6e\u5230\u4f60\u5148\u8bf4\u4e86\u5427\uff1f\u201d\u6a80\u7fbd\u51b2\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u6211\u4e0d\u60f3\u7ed9\u5979\u77e5 \u9053\u6211\u7684\u6765\u5386\uff0c\u5374\u5982\u4f55\u53ef\u4ee5\u95ee\u5979\u7684\u8eab\u4e16\uff1f\u201d\u8981\u77e5\u4ed6\u4eec\u662f\u6709\u7ea6\u5728\u5148\uff0c\u5bf9\u65b9\u544a\u8bc9\u544a\u8bc9\u4ed6\u4ec0\u4e48\u4e8b\u60c5\uff0c \u4ed6\u5c31\u5f97\u544a\u8bc9\u5bf9\u65b9\u540c\u6837\u7684\u4e8b\u60c5\u7684\u3002 \u3000\u3000\u201c\u6055\u6211\u95ee\u5f97\u5192\u6627\uff0c\u4f60\u4e0d\u613f\u610f\u8bf4\uff0c\u90a3\u5c31\u7b97\u4e86\u3002\u201d\u6a80\u7fbd\u51b2\u9053\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u662f\u8d35\u65cf\u4e5f\u597d\uff0c\u662f\u5e73\u6c11\u4e5f\u597d\uff0c\u56fd\u7834\u5bb6\u4ea1\u4e4b\u540e\uff0c\u8fd8\u4e0d \u90fd\u662f\u4e00\u6837\u3002\u4e0d\u8fc7\uff0c\u4f60\u82e5\u60f3\u77e5\u9053\uff0c\u6211\u544a\u8bc9\u4e5f\u65e0\u59a8\u3002\u6211\u4eec\u8fd9\u4e00\u5bb6\u4e8c\u5341\u5e74\u524d\u662f\u4f4f\u5728\u71d5\u4eac\u7684\u4e00\u5bb6\u666e\u901a \u4eba\u5bb6\u3002\u201d\u8bf4\u7f62\uff0c\u597d\u50cf\u6709\u70b9\u5bb3\u6015\u6a80\u7fbd\u51b2\u4e0d\u76f8\u4fe1\u7684\u6837\u5b50\uff0c\u53c8\u518d\u52a0\u4e0a\u4e00\u53e5\uff1a\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u534a\u4fe1\u534a\u7591\uff0c\u597d\u5728\u4ed6\u4ece\u5bf9\u65b9\u7684\u56de\u7b54\u4e4b\u4e2d\u5df2\u7ecf\u5f97\u5230\u201c\u542f\u53d1\u201d\uff0c\u4fbf\u5373\u6a21\u4eff\u8d6b\u8fde\u6e05\u6ce2\u7684\u53e3 \u6c14\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u5bb6\u5341\u5e74\u524d\u662f\u4f4f\u5728\u76d8\u9f99\u5c71\u4e0a\u7684\u4e00\u5bb6\u666e\u901a\u730e\u6237\uff0c\u6211\u7684\u7236\u6bcd\u90fd\u662f\u730e\u4eba\u3002\u201d\u540c\u6837\u52a0 \u4e0a\u4e00\u53e5\uff1a\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\u3002\u201d\u4ed6\u8fd9\u8bdd\u5012\u4e0d\u80fd\u7b97\u662f\u8bf4\u8c0e\uff0c\u4e0d\u9519\u4ed6\u7684\u7956\u7236\u662f\u91d1\u56fd\u7684\u738b\u7237\uff0c\u4f46\u9003\u81f3\u76d8 \u9f99\u5c71\u4e4b\u65f6\uff0c\u65e9\u5df2\u653e\u5f03\u4e86\u738b\u4f4d\uff0c\u4ed6\u7684\u7236\u6bcd\u7684\u786e\u662f\u4ee5\u6253\u730e\u4e3a\u751f\u7684\u3002\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u80af\u76f8\u4fe1\u6211\uff0c \u6211\u5c31\u76f8\u4fe1\u4f60\u3002\u4f60\u8fd8\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u4f46\u8fd9\u6b21\u603b\u8be5\u8f6e\u5230\u4f60\u5148\u8bf4\u4e86\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u6211\u8bf4\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u4f60\u8bf4\u7684\u90a3\u4f4d\u8fbd\u56fd\u738b\u5b50\u8036\u5f8b\u7384\u5143\u6b63\u662f\u6211\u7684\u5e08\u7236\uff0c\u8fd9\u652f \u7389\u8427\u4e5f\u662f\u4ed6\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u548c\u5439\u8427\u90fd\u662f\u6211\u7684\u5a18\u4eb2\u6559\u7684\u3002\u201d\u6a80\u7fbd\u51b2\u5f81\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5439 \u7684\u90a3\u652f\u66f2\u5b50\u4e5f\u662f\u4ee4\u5802\u6559\u7684\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60\u89c9\u5f97\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6ca1\u3001\u6ca1\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7b11\u9053\uff1a\u201c\u4f60\u9a97\u4e0d\u8fc7\u6211\u7684\uff0c\u6211\u4ece\u4f60\u7684\u773c\u795e\u4e4b\u4e2d\uff0c\u770b\u5f97\u51fa\u4f60\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53ea\u56e0\u6211\u542c\u8fc7\u5e08\u7236\u5439\u8fc7\u8fd9\u652f\u66f2\u5b50\uff0c\u6240\u4ee5\u5fcd\u4e0d\u4f4f\u95ee\u95ee\u800c\u5df2\u3002 \u3000\u3000\u8981\u8bf4\u662f\u597d\u5947\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u66ff\u4f60\u89e3\u5f00\u7591\u56e2\u5427\u3002\u521a\u624d\u6211\u8fd8\u672a\u8bf4\u5b8c\u5462\uff0c\u4e0d\u9519\uff0c\u8fd9\u652f\u66f2\u5b50\u662f\u5bb6 \u6bcd\u6559\u6211\u5439\u7684\uff0c\u4f46\u5979\u4e5f\u662f\u6709\u5979\u7684\u5e08\u7236\u7684\u5440\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u4ee4\u5802\u7684\u5e08\u7236\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5979\u662f\u91d1\u5170\u5bc6\u53cb\uff0c\u4e5f\u662f\u4f4f\u5728\u5979\u90bb\u5bb6\u7684\u4e00\u4f4d\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5e08\u7236\u6709\u4e2a\u79d8\u5bc6\uff0c\u4e0d\u77e5\u4f60\u77e5\u4e0d\u77e5\u9053\u3002\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u5728\u4ed6\u672a\u66fe\u6210\u4e3a\u738b\u5b50\u4e4b\u524d\uff0c\u4ed6\u4e5f\u662f \u4f4f\u5728\u71d5\u4eac\u7684\uff0c\u548c\u666e\u901a\u767e\u59d3\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff1a\u201c\u90a3\u65f6\uff0c\u8036\u5f8b\u7384\u5143\u559c\u6b22\u4e00\u4f4d\u59d3\u9f50\u7684\u59d1\u5a18\uff0c\u65f6\u5e38\u5439\u8427\u7ed9\u5979\u542c\u3002\u8fd9\u4f4d \u59d3\u9f50\u7684\u59d1\u5a18\u5c31\u662f\u5bb6\u6bcd\u7684\u5f53\u5e74\u7684\u597d\u670b\u53cb\uff0c\u5979\u4eec\u662f\u6bd4\u90bb\u800c\u5c45\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8fd9\u6837\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u8fd8\u6ca1\u51fa\u751f\u5462\u3002\u201d\u8d6b\u8fde\u6e05\u6ce2\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f46\u5bb6\u6bcd\u5012\u662f\u5f88\u60f3\u5ff5\u8fd9\u4f4d\u6027\u9f50\u7684\u59d1\u5a18\u7684\uff0c \u542c\u8bf4\u5979\u540e\u6765\u6539\u5ac1\u4e86\u522b\u4eba\uff0c\u4f60\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u6a80\u7fbd\u51b2\u9053\u3002\u5176\u5b9e\uff0c\u4ed6\u5f53\u7136\u662f\u77e5\u9053\u7684\uff0c\u8fd9\u4f4d\u201c\u9f50\u59d1\u5a18\u201d\uff0c\u5c31\u662f\u5546\u5dde\u8282 \u5ea6\u4f7f\u5b8c\u989c\u9274\u7684\u592b\u4eba\uff0c\u8fd9\u4f4d\u5b8c\u989c\u592b\u4eba\u4e0d\u4f46\u662f\u4ed6\u7684\u5e08\u7236\u7684\u65e7\u60c5\u4eba\uff0c\u548c\u4ed6\u4e00\u5bb6\u4e5f\u662f\u6709\u7740\u7279\u6b8a\u5173\u7cfb \u7684\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u8bf4\u8c0e\uff0c\u4e0d\u89c9\u5f97\u8138\u4e0a\u6709\u70b9\u70ed\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4f3c\u4e4e\u5e76\u6ca1\u6ce8\u610f\u5230\u4ed6\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u6562\u518d\u95ee\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u4e86\u3002\u5929\u8272\u4e0d\u65e9\uff0c\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\uff0c\u54b1\u4eec\u5c31\u6b64\u5206\u624b\u4e86 \u5427\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u4e0a\u90a3\u513f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u4e00\u5b9a\u53bb\u5904\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u771f\u7684\u5417\uff0c\u8fd9\u53ef\u771f\u662f\u5de7\u6781\u4e86\uff0c\u6211\u4e5f\u6ca1\u6709\u4e00\u5b9a\u53bb\u5904\u7684\u3002\u201d \u3000\u3000\u542c\u5979\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u60f3\u548c\u6a80\u7fbd\u51b2\u7ed3\u4f34\u540c\u884c\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5728\u77e5\u9053\u4e86\u4ed6\u548c\u81ea\u5df1\u7684\u5e08\u7236\u4e5f\u6709\u4e00\u6bb5\u6e0a\u6e90\u4e4b\u540e\uff0c\u5bf9\u5979\u66f4\u589e\u597d\u611f\uff0c\u4e0d\u8fc7\u4ed6\u8eab\u8d1f\u56fd\u6068\u5bb6 \u4ec7\uff0c\u7eb5\u7136\u662f\u6709\u597d\u611f\uff0c\u4e5f\u4e0d\u6562\u548c\u5979\u76f8\u5904\u592a\u6df1\u3002\u56e0\u4e3a\u5373\u4f7f\u4e0d\u6015\u6cc4\u6f0f\u4e86\u81ea\u5df1\u7684\u79d8\u5bc6\uff0c\u4e5f\u6015\u8fde\u7d2f\u4e86 \u5979\u3002 \u3000\u3000\u201c\u6211\u60f3\u5148\u56de\u5230\u76d8\u9f99\u5c71\u796d\u626b\u7239\u5a18\u7684\u575f\u5893\uff0c\u4e0d\u6562\u59d4\u5c48\u59d1\u5a18\u4f5c\u4f34\uff0c\u54b1\u4eec\u5c31\u6b64\u522b\u8fc7\u3002\u201d\u8bf4\u7f62\uff0c\u6a80 \u7fbd\u51b2\u7eb5\u9a6c\u4e0a\u5c71\u3002\u4ed6\u8fd9\u6837\u8bf4\u8fc7\uff0c\u8d6b\u8fde\u6e05\u6ce2\u4e5f\u662f\u4e0d\u597d\u610f\u601d\u8ddf\u4ed6\u4e0a\u5c71\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u597d\uff0c\u6d6e\u840d\u9aa4\u6563\u672c\u65e0\u7aef\uff0c\u8fd9\u6837\u6563\u4e86\u4e5f\u597d\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u5934\u4e00\u70ed\uff0c\u5fcd\u4e0d\u4f4f\u51b2\u53e3\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u4e24\u7247\u6d6e\u840d\u5c06\u6765\u8fd8\u6709\u78b0\u5728\u4e00\u8d77\u7684\u65f6 \u5019\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5df2\u7ecf\u8de8\u4e0a\u5750\u9a91\uff0c\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u4e00\u5728\u5c71\u4e0a\uff0c\u4e00\u5728\u5c71\u4e0b\uff0c\u8d6b\u8fde\u6e05\u6ce2\u7684\u80cc\u5f71\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u4f46\u6a80\u7fbd\u51b2\u4ecd\u7136\u9690\u9690\u542c\u89c1\u4e86\u968f\u98ce\u5439\u6765 \u7684\u5979\u7684\u4e00\u58f0\u53f9\u606f\u3002 \u3000\u3000\u201c\u6d6e\u840d\u805a\u6563\u672c\u65e0\u7aef\u201d\uff0c\u6a80\u7fbd\u51b2\u7684\u5fc3\u91cc\u4e0d\u89c9\u4e5f\u662f\u5174\u8d77\u4e00\u7247\u65e0\u53ef\u5948\u4f55\u7684\u611f\u89c9\uff0c\u6005\u6005\u60d8\u60d8\uff0c\u72ec \u81ea\u4e0a\u5c71\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5f15\u8d77\u4ed6\u7684\u611f\u89e6\u8fd8\u4e0d\u53ea\u6b64\u3002\u5728\u4ed6\u548c\u8d6b\u8fde\u6e05\u6ce2\u4e4b\u95f4\uff0c\u662f\u8fd8\u6709\u4e00\u6761\u201c\u7ebd\u5e26\u201d\u8fde\u7cfb\u7740 \u7684\uff0c\u8fd9\u6761\u201c\u7ebd\u5e26\u201d\u7528\u73b0\u4ee3\u7684\u8bed\u8a00\u6765\u8bf4\uff0c\u4ea6\u5373\u662f\u201c\u4eba\u9645\u5173\u7cfb\u201d\u3002\u4ed6\u4e0d\u7981\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4e16\u754c \u4e5f\u771f\u662f\u592a\u7ec6\u5c0f\u4e86\uff0c\u60f3\u4e0d\u5230\u6211\u6bcd\u4eb2\u7684\u6069\u4eba\uff0c\u4e5f\u662f\u5979\u6bcd\u4eb2\u7684\u597d\u53cb\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u5b8c\u989c\u9274\u65e0\u597d\u611f\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u6709\u4ec7\uff0c\u56e0\u4e3a\u5979\u7684\u6bcd\u4eb2\u662f\u88ab\u5b8c\u989c\u9274\u7684\u624b\u4e0b\u5c04\u6740\u7684\u3002\u4f46\u5b8c \u989c\u9274\u7684\u59bb\u5b50\u5374\u66fe\u6551\u8fc7\u4ed6\u4eec\u6bcd\u5b50\u7684\u6027\u547d\uff0c\u800c\u4e14\u82e5\u6ca1\u6709\u5979\u7684\u6536\u7559\uff0c\u5728\u90a3\u4e2a\u5175\u8352\u9a6c\u4e71\u7684\u5e74\u5934\uff0c\u4ed6\u4eec \u6bcd\u5b50\u4e5f\u7684\u786e\u662f\u96be\u4ee5\u627e\u5230\u5bb9\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u4f46\u8fd9\u4e2a\u6069\u4eba\uff0c\u4e5f\u5e26\u8d77\u4e86\u4ed6\u7684\u59b9\u59b9\u3002\u5f53\u65f6\u8fd8\u672a\u6ee1\u4e09\u5c81\u7684\u59b9\u59b9\u3002 \u3000\u3000\u5f53\u7136\u4ed6\u77e5\u9053\u5b8c\u989c\u9274\u592b\u4eba\u5e26\u8d70\u4ed6\u7684\u59b9\u59b9\u3002\u662f\u51fa\u4e8e\u4e00\u756a\u597d\u610f\uff0c\u4f46\u8fd9\u4e2a\u59b9\u59b9\uff0c\u4ed6\u603b\u662f\u8981\u627e\u56de\u6765 \u624d\u884c\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u5e08\u7236\u7684\u5fc3\u4e8b\uff0c\u5e08\u7236\u867d\u7136\u4e1a\u5df2\u9690\u5c45\u6df1\u5c71\uff0c\u4e0d\u95ee\u4e16\u4e8b\uff0c\u51b3\u610f\u8981\u7ec3\u6210\u7edd\u4e16\u6b66\u529f\u3002\u4ed6\u628a \u81ea\u5df1\u7684\u7406\u60f3\u548c\u62b1\u8d1f\u90fd\u5df1\u5bc4\u6258\u5728\u4ed6\u7684\u8eab\u4e0a\u4e86\uff0c\u4f46\u4ed6\u77e5\u9053\uff0c\u4ed6\u7684\u5e08\u7236\u8fd8\u6709\u4e00\u4e2a\u629b\u4e0d\u5f00\u7684\u4eba\uff0c\u90a3\u4eba \u5c31\u662f\u4ed6\u7684\u65e7\u65e5\u60c5\u4eba\uff0c\u4ea6\u5373\u662f\u5b8c\u989c\u9274\u7684\u592b\u4eba\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u662f\u5728\u4e03\u5e74\u524d\u79bb\u5f00\u4e08\u592b\uff0c\u8036\u5f8b\u7384\u5143\u4e0d\u77e5\u5979\u7684\u4e0b\u843d\uff0c\u4e5f\u6ca1\u6253\u542c\u8fc7\u5979\u7684\u6d88\u606f\u3002\u4ed6\u7684\u5fc3 \u4e8b\u53ea\u6709\u5f92\u5f1f\u77e5\u9053\u3002 \u3000\u3000\u4e3a\u4e86\u627e\u5bfb\u81ea\u5df1\u7684\u59b9\u59b9\uff0c\u4e3a\u4e86\u5e08\u7236\u7684\u76f8\u5ff5\uff0c\u4ed6\u90fd\u5e94\u8be5\u8bbe\u6cd5\u53bb\u6253\u542c\u5b8c\u989c\u592b\u4eba\u7684\u6d88\u606f\u3002 \u3000\u3000\u201c\u4e0d\u77e5\u5b8c\u989c\u592b\u4eba\u662f\u5426\u5df2\u7ecf\u56de\u5230\u71d5\u4eac\u8001\u5bb6\uff0c\u53ef\u60dc\u6211\u521a\u624d\u5fd8\u8bb0\u4e86\u5411\u6e05\u6ce2\u6253\u542c\u5979\u7684\u6bcd\u4eb2\u65e7\u5bb6\u7684 \u4f4f\u5740\u3002\u5979\u7684\u6bcd\u4eb2\u548c\u5b8c\u989c\u592b\u4eba\u672c\u662f\u90bb\u5c45\u7684\u3002\u201d \u3000\u3000\u4ed6\u56de\u5230\u4e86\u4e03\u5e74\u524d\u7684\u65e7\u5bb6\uff0c\u6240\u6709\u7684\u4eb2\u4eba\u90fd\u5df2\u957f\u57cb\u9ec4\u571f\uff0c\u4ed6\u5b51\u7136\u4e00\u8eab\uff0c\u4e0d\u7981\u6006\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u4f46\u4e0d\u5e78\u4e2d\u4e4b\u4e07\u5e78\u7684\u662f\u3002\u4ed6\u7684\u7236\u6bcd\u548c\u7237\u7237\u3001\u5916\u516c\uff08\u5f20\u708e\uff09\u7b49\u4eba\u7684\u57cb\u846c\u5730\u70b9\u662f\u5728\u4e24\u9762\u60ac\u5d16\u5939 \u5cd9\u4e0b\u7684\u4e00\u4e2a\u5e7d\u8c37\uff0c\u662f\u5916\u4eba\u5f88\u96be\u53d1\u73b0\u7684\u9690\u79d8\u4e4b\u6240\uff0c\u5012\u6ca1\u6709\u53d7\u5230\u7834\u574f\u3002 \u3000\u3000\u56db\u4e2a\u4eb2\u4eba\uff0c\u4e09\u5ea7\u575f\u5893\u3002\u4e3a\u4e86\u6015\u522b\u4eba\u53d1\u73b0\uff0c\u4e09\u5ea7\u575f\u5893\u90fd\u6ca1\u6562\u7acb\u4e0b\u5893\u7891\uff0c\u4e5f\u4e0d\u50cf\u4e00\u822c\u575f\u5893\u7684 \u5f62\u5f0f\uff0c\u53ea\u662f\u4e09\u5806\u201c\u571f\u9992\u5934\u201d\u3002\u5982\u4eca\u571f\u5806\u4e0a\u5df2\u662f\u91ce\u8349\u4e1b\u751f\u4e86\u3002\u5de6\u8fb9\u90a3\u4e00\u5806\u9ec4\u571f\u57cb\u7684\u662f\u4ed6\u7684\u201c\u5916 \u516c\u201d\u5f20\u708e\uff0c\u4e2d\u95f4\u90a3\u5806\u7684\u662f\u4ed6\u7684\u7237\u7237\u6a80\u516c\u76f4\uff0c\u53f3\u8fb9\u90a3\u5806\u9ec4\u571f\u5219\u662f\u4ed6\u7684\u7236\u6bcd\u5408\u846c\u3002\u4f46\u9664\u4e86\u4ed6\u4e4b \u5916\uff0c\u53c8\u6709\u8c01\u80fd\u77e5\u9053\uff0c\u8fd9\u4e09\u576f\u9ec4\u571f\u4e4b\u4e0b\uff0c\u57cb\u846c\u7684\u7adf\u662f\u91d1\u56fd\u7684\u8d1d\u52d2\u3001\u8d1d\u5b50\u3001\u5927\u5b8b\u7684\u4e49\u58eb\u548c\u6297\u91d1\u540d \u5c06\u5cb3\u98de\u7684\u5916\u5b59\u5973\u513f\uff1f\u5929\u8272\u5ffd\u5730\u8f6c\u4e3a\u9634\u6c89\uff0c\u843d\u4e0b\u5c0f\u96e8\u3002\u82e6\u96e8\u51c4\u98ce\uff0c\u5929\u516c\u4e5f\u4f3c\u4e3a\u4ed6\u60b2\u6ce3\u3002\u6a80\u7fbd\u51b2 \u64ae\u571f\u4e3a\u9999\uff0c\u5728\u7237\u7237\u575f\u524d\u7980\u544a\uff1a\u201c\u7237\u7237\uff0c\u6211\u5df2\u7ecf\u6740\u4e86\u54c8\u5fc5\u56fe\uff0c\u66ff\u4f60\u62a5\u4e86\u4ec7\u4e86\uff01\u201d \u3000\u3000\u4f46\u771f\u7684\u62a5\u4e86\u4ec7\u4e48\uff0c\u4e00\u9635\u51b7\u98ce\u5439\u6765\uff0c\u4ed6\u4ece\u6fc0\u52a8\u4e2d\u6062\u590d\u4e86\u6e05\u9192\uff0c\u4ed6\u77e5\u9053\u7237\u7237\u771f\u6b63\u7684\u4ec7\u4eba\u5176\u5b9e \u662f\u91d1\u56fd\u7684\u7687\u5e1d\uff0c\u54c8\u5fc5\u56fe\u4e0d\u8fc7\u662f\u5949\u547d\u884c\u4e8b\u7684\u5974\u624d\u5934\u76ee\u800c\u5df2\u3002\u4ed6\u7684\u6b66\u529f\u518d\u597d\uff0c\u8fd9\u4e2a\u4ec7\u53ea\u6015\u4e5f\u662f\u96be \u4ee5\u62a5\u3002\u7237\u7237\u4e5f\u672a\u5fc5\u5e0c\u671b\u4ed6\u771f\u7684\u53bb\u6740\u4e86\u91d1\u56fd\u7684\u7687\u5e1d\u66ff\u81ea\u5df1\u62a5\u4ec7\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u82e6\u7b11\uff0c\u8f6c\u8fc7\u8eab\u5728\u7236\u6bcd\u575f\u524d\u8dea\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u7238\u7238\u3001\u5988\u5988\uff0c\u6211\u56de\u6765\u4e86\u3002\u5988\u5988\uff0c\u6211\u6ca1\u6709 \u8f9c\u8d1f\u7684\u4f60\u7684\u65af\u671b\uff1b\u6211\u5df2\u7ecf\u8ddf\u5e08\u7236\u5b66\u597d\u6b66\u529f\u56de\u6765\u4e86\u3002\u4f60\u7684\u6559\u5bfc\uff0c\u6211\u7edd\u4e0d\u6562\u5fd8\u8bb0\u3002\u201d\u4ed6\u8fce\u7740\u82e6\u96e8 \u51c4\u98ce\uff0c\u8d70\u5230\u201c\u5916\u516c\u201d\u575f\u524d\u8dea\u4e0b\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\u8fd9\u4e2a\u201c\u5916\u516c\u201d\u5e76\u4e0d\u662f\u4ed6\u7684\u4eb2\u5916\u516c\uff0c\u4f46\u8fd9\u4e2a\u5916\u516c\u5bf9 \u4ed6\u6bcd\u5b50\u6069\u91cd\u5982\u5c71\uff0c\u800c\u4e14\u4e5f\u662f\u6700\u75bc\u4ed6\u7684\u3002\u4ed6\u6000\u7740\u60b2\u75db\u4e0e\u6b49\u759a\u7684\u5fc3\u60c5\uff0c\u8dea\u5728\u5f20\u708e\u575f\u524d\u8bf4\u9053\uff1a\u201c\u516c \u516c\uff0c\u4f60\u5bf9\u6211\u4eec\u6bcd\u5b50\u7684\u5927\u6069\u5927\u5fb7\u6211\u662f\u6c38\u96be\u62a5\u7b54\u7684\u4e86\u3002\u4f60\u6682\u4e14\u5728\u8fd9\u91cc\u5b89\u6b47\u5427\u3002 \u3000\u3000\u4f60\u7684\u5fc3\u613f\u6211\u5c06\u6765\u5fc5\u5b9a\u4e3a\u4f60\u505a\u5230\u7684\u3002\u201d\u5f20\u708e\u7684\u5fc3\u613f\u662f\u4ec0\u4e48\uff0c\u5c31\u662f\u5e0c\u671b\u5728\u4ed6\u6b7b\u540e\uff0c\u5c38\u9ab8\u80fd\u591f \u91cd\u5f52\u6545\u65e7\uff0c\u5b89\u846c\u5728\u4ed6\u6545\u4e3b\u5f20\u5baa\u7684\u575f\u5893\u65c1\u8fb9\u3002 \u3000\u3000\u4ed6\u7684\u8fd9\u4e2a\u5fc3\u613f\uff0c\u662f\u5728\u4ed6\u7684\u751f\u524d\uff0c\u544a\u8bc9\u6a80\u7fbd\u51b2\u7684\u6bcd\u4eb2\u7684\uff0c\u6a80\u7fbd\u51b2\u7684\u6bcd\u4eb2\u5728\u5979\u4e34\u6b7b\u4e4b\u524d\uff0c\u4e5f \u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5979\u8fd9\u4e2a\u4e49\u7236\u7684\u5fc3\u613f\u5f53\u4f5c\u9057\u5631\u5429\u5490\u81ea\u5df1\u7684\u513f\u5b50\u3002 \u3000\u3000\u5f20\u708e\u7684\u6545\u4e3b\u5f20\u5baa\u5c31\u662f\u6a80\u7fbd\u51b2\u771f\u6b63\u7684\u5916\u516c\u3002\u800c\u6a80\u7fbd\u51b2\u4ea6\u5df2\u77e5\u9053\u4e86\u6bcd\u4eb2\u7684\u5916\u516c\uff08\u4ea6\u5373\u662f\u4ed6\u7684 \u5916\u66fe\u7956\u7236\uff09\u4e43\u662f\u5b8b\u671d\u7684\u6297\u91d1\u540d\u5c06\u5cb3\u98de\u3002\u4ed6\u7684\u5916\u516c\u5f20\u5baa\u4e0d\u4f46\u662f\u5cb3\u98de\u7684\u5973\u5a7f\uff0c\u4e5f\u662f\u5cb3\u98de\u624b\u4e0b\u7684\u7b2c \u4e00\u5458\u731b\u5c06\u3002 \u3000\u3000\u5916\u516c\u548c\u66fe\u7956\u7236\u4ed6\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u4ed6\u7684\u6bcd\u4eb2\u4e5f\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u6bcd\u4eb2\u751f\u524d\u5374\u6e34\u671b\u80fd\u591f\u56de\u53bb\u796d\u626b\u4ed6\u4eec\u7684\u575f\u5893\u7684\u3002\u800c\u6a80\u7fbd\u51b2\u5bf9\u8fd9\u4e24\u4e2a\u672a\u89c1\u8fc7\u9762\u7684\u65e9\u5df2 \u6b7b\u4e86\u591a\u5e74\u7684\u5c0a\u957f\uff0c\u4e5f\u6000\u7740\u6781\u5176\u656c\u6155\u7684\u5fc3\u60c5\u7684\u3002 \u3000\u3000\u5988\u5988\u7559\u7ed9\u4ed6\u7684\u4f20\u5bb6\u4e4b\u5b9d\u8fd8\u85cf\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u90a3\u662f\u4e00\u4e2a\u9526\u76d2\uff0c\u9526\u76d2\u91cc\u85cf\u7684\u662f\u4e00\u5f20\u8272\u6cfd\u5df2\u53d8\u5f97 \u6697\u9ec4\u7684\u7eb8\u6761\u3002\u4f46\u5728\u8fd9\u5f20\u6b8b\u7834\u7684\u7eb8\u5f20\u4e0a\u5374\u6709\u5cb3\u98de\u4eb2\u7b14\u5199\u7684\u4e00\u9996\u8bcd\uff0c\u8fd9\u5f20\u5cb3\u98de\u7684\u7b14\u8ff9\u662f\u5f20\u708e\u820d\u4e86 \u6027\u547d\u4fdd\u5b58\u4e0b\u6765\uff0c\u5728\u4e34\u6b7b\u4e4b\u524d\u4ea4\u7ed9\u4ed6\u7684\u5988\u5988\uff0c\u4ed6\u7684\u5988\u5988\u53c8\u5728\u4e34\u6b7b\u4e4b\u524d\u4ea4\u7ed9\u4ed6\u7684\u3002 \u3000\u3000\u8fd9\u9996\u6ee1\u6c5f\u7ea2\u8bcd\uff0c\u4ed6\u65e9\u5df2\u719f\u8bb0\u5fc3\u4e2d\uff0c\u7528\u4e0d\u7740\u6253\u5f00\u9526\u76d2\uff0c\u62ff\u51fa\u6765\u770b\u4e86\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u98ce\u96e8\u4e4b\u4e2d\uff0c\u624b\u6307\u89e6\u6478\u9526\u76d2\uff0c\u80f8\u4e2d\u5c3d\u662f\u6fc0\u60c5\uff0c\u653e\u58f0\u541f\u9053\uff1a\u201c\u6012\u53d1\u51b2\u51a0\uff0c\u51ed\u680f\u5904\uff0c\u6f47 \u6f47\u96e8\u6b47\u3002 \u3000\u3000\u62ac\u671b\u773c\uff0c\u4ef0\u5929\u957f\u5578\uff0c\u58ee\u6000\u6fc0\u70c8\u3002 \u3000\u3000\u4e09\u5341\u529f\u540d\u5c18\u4e0e\u571f\u3002 \u3000\u3000\u516b\u5343\u91cc\u8def\u4e91\u548c\u6708\u3002 \u3000\u3000\u83ab\u7b49\u95f2\uff0c\u767d\u4e86\u5c11\u5e74\u5934\uff0c\u7a7a\u60b2\u5207\u3002\u201d \u3000\u3000\u9065\u671b\u5357\u5929\uff0c\u4f9d\u7a00\u53ef\u4ee5\u60f3\u89c1\u4ed6\u7684\u5916\u66fe\u7956\u7236\u5f53\u5e74\u7b56\u9a6c\u6a2a\u5200\uff0c\u9ad8\u547c\u201c\u76f4\u6363\u9ec4\u9f99\uff0c\u4e0e\u8bf8\u541b\u75db\u996e\u201d \u7684\u8c6a\u60c5\uff1b\u6a80\u7fbd\u51b2\u4e0d\u7981\u60a0\u7136\u795e\u5f80\u3002 \u3000\u3000\u4ed6\u4ece\u5e08\u7236\u53e3\u4e2d\u77e5\u9053\uff0c\u5bb3\u6b7b\u5cb3\u98de\u7684\u90a3\u4e2a\u5927\u5978\u81e3\u79e6\u6867\u4ea6\u65e9\u5df2\u6b7b\uff0c\u5982\u4eca\u5cb3\u98de\u7684\u51a4\u867d\u7136\u8fd8\u672a\u5f97\u5230 \u7687\u5e1d\u6b63\u5f0f\u4e0b\u8bcf\u662d\u96ea\uff0c\u4f46\u5cb3\u98de\u7684\u575f\u5893\u5219\u5df2\u662f\u5f97\u5230\u7687\u5e1d\u7684\u9ed8\u8bb8\u5728\u897f\u6e56\u65c1\u8fb9\u5efa\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5373\u4f7f\u6ca1\u6709\u6bcd\u4eb2\u7684\u9057\u5631\uff0c\u4ed6\u4e5f\u662f\u591a\u4e48\u7684\u60f3\u5230\u8fd9\u4f4d\u6297\u91d1\u540d\u5c06\u7684\u5893\u524d\uff0c\u4e00\u81f4\u5fc3\u4e2d\u7684\u60bc\u5ff5\u554a\uff01 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u662f\u96e8\u6536\u4e91\u6563\uff0c\u4f46\u4ed6\u7684\u5fc3\u60c5\u8fd8\u662f\u50cf\u98ce\u96e8\u5982\u6666\u4e4b\u9645\u7684\u4e00\u6837\u51c4\u8ff7\u3002 \u3000\u3000\u662f\u5357\u8d74\u4e34\u5b89\uff0c\u8fd8\u662f\u5317\u4e0a\u4e2d\u90fd\u3002 \u3000\u3000\u4ed6\u671b\u5411\u8fdc\u65b9\uff0c\u5728\u60f3\u9053\u81ea\u5df1\u8981\u8d70\u90a3\u4e00\u6761\u8def\u3002 \u3000\u3000\u5ffd\u5730\u770b\u89c1\u5c71\u4e0b\u5c18\u5934\u5927\u8d77\uff0c\u6709\u4e00\u961f\u91d1\u5175\u62bc\u7740\u4e00\u7fa4\u201c\u58ee\u4e01\u201d\u7ecf\u8fc7\uff0c\u8bf4\u662f\u201c\u58ee\u4e01\u201d\uff0c\u6709\u8bb8\u591a\u5176 \u5b9e\u5df2\u662f\u997f\u5f97\u9762\u9ec4\u808c\u7626\u7684\u75c5\u592b\u4e86\u3002\u5175\u58eb\u6b63\u5728\u97ad\u6253\u90a3\u4e9b\u8d70\u4e0d\u52a8\u7684\u201c\u58ee\u4e01\u201d\uff0c\u5f3a\u903c\u4ed6\u4eec\u8ddf\u4e0a\u961f\u4f0d\u3002 \u3000\u3000\u7ad9\u5728\u9ad8\u5c71\u4e0a\u7684\u6a80\u7fbd\u51b2\u5f53\u7136\u770b\u4e0d\u89c1\u201c\u58ee\u4e01\u201d\u7684\u75c5\u5bb9\uff0c\u97ad\u6253\u7684\u52a8\u4f5c\u4e5f\u770b\u4e0d\u89c1\u3002\u4f46\u4ed6\u5374\u542c\u5f97\u89c1 \u4ed6\u4eec\u54c0\u53f7\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6709\u90a3\u4e48\u591a\u4eba\u5e0c\u671b\u8fc7\u592a\u5e73\u7684\u65e5\u5b50\uff0c\u90a3\u5c31\u603b\u6709\u529e\u6cd5\u53ef\u4ee5\u963b\u6b62\u6218\u4e89\u5427\uff1f\u4ed6\u60f3\u3002\u4e5f\u552f\u6709\u963b\u6b62\u6218 \u4e89\uff0c\u624d\u80fd\u591f\u6551\u90a3\u4e9b\u4eba\u7684\u82e6\u96be\u3002 \u3000\u3000\u201c\u6211\u4e0d\u5165\u5730\u72f1\uff0c\u8c01\u5165\u5730\u72f1\uff1f\u201d\u7ec8\u4e8e\u4ed6\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8d70\u4e0b\u5c71\u53bb\uff0c\u8d70\u5411\u5317\u65b9\u3002 \u3000\u3000\u592a\u9633\u91cd\u65b0\u4ece\u4e4c\u4e91\u91cc\u722c\u51fa\u6765\uff0c\u4e4c\u4e91\u6e10\u6e10\u6d88\u6563\uff0c\u4ed6\u5fc3\u5e95\u7684\u9634\u973e\u4e5f\u6e10\u6e10\u6d88\u6563\u4e86\u3002 \u3000\u3000\u773c\u5e95\u662f\u201c\u79cb\u5149\u201d\uff0c\u5fc3\u5e95\u5374\u662f\u201c\u6625\u5149\u201d\uff0c\u662f\u660e\u5a9a\u7684\u6625\u5149\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4e5f\u6b63\u662f\u5728\u5317\u4e0a\u91d1\u4eac\u7684\u9014\u4e2d\u3002 \u3000\u3000\u548c\u6a80\u7fbd\u51b2\u4e00\u6837\uff0c\u6b64\u9645\u5979\u4e5f\u6b63\u662f\u5fc3\u6f6e\u6f8e\u6e43\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002 \u3000\u3000\u4e0d\u540c\u7684\u662f\uff0c\u6a80\u7fbd\u51b2\u5c1a\u672a\u77e5\u9053\u5979\u7684\u6765\u5386\uff0c\u800c\u5979\u5219\u5df2\u662f\u77e5\u9053\u6a80\u7fbd\u51b2\u6765\u5386\u4e86\u3002 \u3000\u3000\u201c\u770b\u6765\u8fd9\u4e2a\u59d3\u6a80\u7684\u5c11\u5e74\uff0c\u591a\u534a\u5c31\u662f\u6a80\u516c\u76f4\u7684\u5b59\u513f\u4e86\u3002\u201d\u56e0\u4e3a\u6a80\u516c\u76f4\u548c\u8036\u5f8b\u7384\u5143\u6709\u6df1\u539a\u7684 \u4ea4\u60c5\uff0c\u8fd9\u662f\u5979\u65e9\u5df2\u77e5\u9053\u7684\u4e8b\u3002\u6a80\u7fbd\u51b2\u59d3\u201c\u6a80\u201d\uff0c\u53c8\u662f\u8036\u5f8b\u7384\u5143\u7684\u5f92\u5f1f\uff0c\u81ea\u662f\u7528\u4e0d\u7740\u6a80\u7fbd\u51b2\u81ea \u5df1\u8bf4\u51fa\u6765\uff0c\u5979\u4e5f\u731c\u5f97\u5230\u4ed6\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5979\u8d70\u7684\u662f\u4e00\u6761\u5c71\u8def\uff0c\u5c71\u8272\u6e05\u5e7d\uff0c\u4f46\u5979\u7684\u5fc3\u60c5\u5374\u662f\u70e6\u4e71\u4e4b\u6781\u3002 \u3000\u3000\u5979\u7684\u70e6\u607c\u6b63\u662f\u7531\u4e8e\u4e1a\u5df2\u77e5\u9053\u6a80\u7fbd\u51b2\u7684\u8eab\u4efd\u6240\u81f4\u3002\u6a80\u7fbd\u51b2\u65e2\u662f\u6a80\u516c\u76f4\u7684\u5b59\u513f\uff0c\u53c8\u662f\u8036\u5f8b\u7384 \u5143\u7684\u5f92\u5f1f\u3002 \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u4e43\u662f\u5f53\u4eca\u7687\u4e0a\u6700\u987e\u5fcc\u7684\u4eba\uff0c\u6a80\u516c\u76f4\u542c\u8bf4\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f46\u6b7b\u8baf\u8fd8\u6ca1\u8bc1\u5b9e\u3002\u8036\u5f8b\u7384\u5143 \u8fd9\u51e0\u5e74\u6765\u6d88\u58f0\u533f\u8ff9\uff0c\u4e5f\u4e0d\u77e5\u8eb2\u5230\u54ea\u513f\u3002\u60f3\u4e0d\u5230\u6211\u5374\u4f1a\u5728\u5f52\u4e91\u5e84\u91cc\u78b0\u4e0a\u4ed6\u7684\u5f92\u5f1f\u3002\u6211\u672c\u6765\u53ea\u60f3 \u60e9\u6212\u5f52\u5143\u9f99\u7684\uff0c\u60f3\u4e0d\u5230\u53c8\u6740\u51fa\u4e00\u4e2a\u54c8\u5fc5\u56fe\u3002\u6211\u4e0d\u60f3\u5bf9\u54c8\u5fc3\u56fe\u8bf4\u660e\u6211\u7684\u6765\u5386\uff0c\u9633\u9519\u9633\u5dee\uff0c\u8fd9\u59d3 \u6a80\u7684\u5c0f\u5b50\u7adf\u7136\u53d8\u6210\u4e86\u6211\u7684\u6551\u547d\u6069\u4eba\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u53ef\u4ee5\u7792\u4f4f\u7687\u4e0a\uff0c\u4f46\u82e5\u662f\u7236\u738b\u95ee\u8d77\uff0c\u6211\u53ef\u600e\u80fd\u9690\u7792\u5462\uff1f\u7236\u738b\u53ef\u6b63\u662f\u8981\u6211\u6253\u542c \u8036\u5f8b\u7384\u5143\u7684\u4e0b\u843d\u554a\uff01\u4ed6\u867d\u7136\u4e0d\u662f\u6211\u7684\u751f\u8eab\u4e4b\u7236\uff0c\u4f46\u5374\u662f\u5c06\u6211\u5f53\u4f5c\u4eb2\u751f\u5973\u513f\u4e00\u6837\u629a\u517b\u7684\u3002 \u3000\u3000\u201c\u6d6e\u840d\u805a\u6563\u672c\u65e0\u7aef\u201d\u4e0d\u77e5\u4e0d\u89c9\u3002\u5979\u53c8\u60f3\u8d77\u6a80\u7fbd\u51b2\u548c\u5979\u8bf4\u8fc7\u7684\u8fd9\u53e5\u8bd7\u4e86\u3002 \u3000\u3000\u5979\u552f\u6709\u82e6\u7b11\uff0c\u9664\u4e86\u82e6\u7b11\uff0c\u5979\u8fd8\u80fd\u600e\u6837\u5462\uff1f\u4e24\u7247\u968f\u7740\u6c34\u6f02\u6d41\u7684\u6d6e\u840d\uff0c\u5076\u7136\u78b0\u5728\u4e00\u8d77\uff0c\u518d\u6b21 \u76f8\u805a\u7684\u673a\u4f1a\u5c31\u5fae\u4e4e\u5176\u5fae\u4e86\u3002 \u3000\u3000\u201c\u6211\u4e5f\u5b81\u613f\u4e0d\u518d\u78b0\u4e0a\u4ed6\u4e86\u3002\u4f46\u4ed6\u5374\u54ea\u91cc\u77e5\u9053\uff0c\u6211\u53ef\u5e76\u4e0d\u662f\u968f\u6c34\u6f02\u6d41\u7684\u6d6e\u840d\uff0c\u6211\u53ea\u662f\u64cd\u7eb5 \u5728\u522b\u4eba\u624b\u91cc\u7684\u98ce\u7b5d\u3002\u4e0d\u7ba1\u98de\u5f97\u591a\u9ad8\uff0c\u98de\u5f97\u591a\u8fdc\uff0c\u9664\u975e\u98ce\u7a33\u7684\u7ebf\u65ad\u4e86\uff0c\u5426\u5219\u6211\u603b\u662f\u8981\u56de\u5230\u522b\u4eba \u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u524d\u9762\u6709\u5ea7\u5c71\u5c97\uff0c\u5c71\u8def\u662f\u7ed5\u7740\u5c71\u5c97\u800c\u8fc7\u7684\uff0c\u5c71\u5c97\u4e0a\u6709\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u597d\u50cf\u88ab\u5979\u7684\u5750\u9a91\u7684\u94c1 \u8e44\u8e0f\u5730\u58f0\u97f3\u60ca\u52a8\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u671b\u4e86\u4e00\u671b\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u672c\u6765\u662f\u4e0d\u5728\u610f\u7684\uff0c\u4f46\u5f53\u5979\u9a91\u9a6c\u8dd1\u4e0a\u8fd9\u5ea7\u5c71\u5c97\u7684\u65f6\u5019\uff0c\u90a3\u4e2a\u4eba\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u672c\u6765\u662f\u4e0d\u628a\u8fd9\u4e2a\u4eba\u653e\u5728\u5fc3\u4e0a\u7684\uff0c\u4f46\u5ffd\u7136\u4e0d\u89c1\u4e86\u4ed6\uff0c\u5374\u662f\u4e0d\u80fd\u4e0d\u6709\u70b9\u5947\u602a\u3002 \u3000\u3000\u8981\u77e5\u5979\u867d\u7136\u4e0d\u662f\u7eb5\u9a6c\u6025\u9a70\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u9a6c\u603b\u662f\u6bd4\u4eba\u8dd1\u7684\u5feb\u7684\u3002\u5979\u7acb\u9a6c\u5c71\u5c97\uff0c\u5411\u524d\u8def\u770b \u53bb\uff0c\u4e5f\u662f\u4e0d\u89c1\u90a3\u4eba\u8e2a\u8ff9\u3002 \u3000\u3000\u201c\u5947\u602a\uff0c\u8fd9\u4e2a\u4eba\u4e3a\u4ec0\u4e48\u8981\u8eb2\u6211\u5462\uff1f\u201d\u5979\u5fcd\u4e0d\u4f4f\u597d\u5947\u4e4b\u5fc3\uff0c\u567c\u556a\u7684\u54cd\u4e86\u4e00\u4e0b\u9a6c\u97ad\uff0c\u559d\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\u9b3c\u9b3c\u795f\u795f\u7684\u8eb2\u5728\u8fd9\u91cc\uff1f\u7ed9\u6211\u6eda\u51fa\u6765\uff01\u201d \u3000\u3000\u6ca1\u4eba\u56de\u7b54\uff0c\u4e5f\u6ca1\u4eba\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4eba\u4e0d\u522b\u4eba\uff0c\u6b63\u662f\u90a3\u66fe\u7ecf\u5f53\u8fc7\u5b8c\u989c\u9274\u7684\u536b\u58eb\uff0c\u540e\u6765\u5374\u53d8\u6210\u4e86\u5f52\u5143\u9f99\u95e8\u4e0b\u98df\u5ba2\u7684\u90a3 \u4e2a\u4faf\u6606\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u6b63\u5728\u76d8\u7b97\u7528\u4ec0\u4e48\u65b9\u6cd5\u903c\u4ed6\u81ea\u52a8\u51fa\u6765\uff0c\u5ffd\u7136\u770b\u89c1\u6709\u4e8c\u4e2a\u4eba\u9a91\u9a6c\u4e0a\u5c71\u6765\uff0c\u8fd8\u672a\u770b\u6e05 \u695a\uff0c\u4fbf\u542c\u5f97\u4e00\u4e2a\u4f3c\u66fe\u76f8\u8bc6\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u4e2a\u5996\u5973\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5b9a\u775b\u4e00\u770b\uff0c\u8bf4\u8bdd\u8fd9\u4e2a\u4eba\u539f\u6765\u662f\u5f52\u5143\u9f99\u7684\u5927\u5f1f\u5b50\u73ed\u5b9a\u5c71\u3002 \u3000\u3000\u8d70\u5728\u73ed\u5b9a\u5c71\u524d\u5934\u7684\u662f\u4e00\u4e2a\u7ea2\u8863\u756a\u50e7\u3002 \u3000\u3000\u542c\u4ed6\u4eec\u7684\u8bed\u6c14\uff0c\u7ea2\u5728\u756a\u50e7\u662f\u5e94\u73ed\u5b9a\u5c71\u4e4b\u8bf7\uff0c\u524d\u6765\u8ffd\u8e2a\u5979\u7684\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4e0d\u7406\u4f1a\u90a3\u4e2a\u756a\u50e7\uff0c\u541f\u7b11\u8bf4\u9053\uff1a\u201c\u73ed\u5b9a\u5c71\u5728\u5f52\u4e91\u5e84\u4e2d\uff0c\u4f60\u5df2\u7ecf\u5bf9\u6211\u78d5\u8fc7\u4e86\u5934\uff0c\u65e0 \u987b\u8fd9\u4e48\u591a\u793c\uff0c\u518d\u6765\u9001\u884c\u3002\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4f4d\u5927\u5e08\u662f\u8c01\uff1f\u4ed6\u662f\u9001\u4f60\u4e0a\u897f\u5929\u7684\uff0c\u4f60 \u6b7b\u5230\u4e34\u5934\uff0c\u5c1a\u6562\u53e3\u51fa\u72c2\u8a00\u3002\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7ea2\u756a\u4e00\u9a6c\u5f53\u5148\uff0c\u5df2\u7136\u6765\u5230\u3002 \u3000\u3000\u7ea2\u8863\u756a\u50e7\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u4e0b\u9a6c\u6765\uff01\u201d\u58f0\u51fa\u638c\u53d1\u3002\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u8fd8\u4e0d\u5eff\u516b\u4e08\u8fdc\uff0c\u8d6b\u8fde\u6e05 \u6ce2\u90a3\u5339\u5750\u9a91\u5df1\u662f\u5982\u53d7\u94c1\u9524\u51fb\u4f3c\u7684\uff0c\u4e00\u58f0\u957f\u5636\uff0c\u56db\u8e44\u5c48\u5730\u3002\u8d6b\u8fde\u6e05\u6ce2\u4ece\u9a6c\u80cc\u4e0a\u98de\u8eab\u8dc3\u8d77\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u6b63\u5728\u7ed9\u90a3\u756a\u50e7\u559d\u91c7\uff0c\u8d5e\u4ed6\u7684\u5288\u638c\u529f\u592b\u5929\u4e0b\u65e0\u53cc\uff0c\u90a3\u77e5\u638c\u58f0\u672a\u7edd\uff0c\u5ffd\u89c1\u7ea2\u8863\u756a\u50e7\u7684 \u5750\u9a91\uff0c\u4e5f\u4f3c\u53d1\u4e86\u72c2\u4f3c\u7684\uff0c\u5411\u77f3\u5d16\u51b2\u53bb\u3002\u7ea2\u8863\u756a\u50e7\u5927\u60ca\uff0c\u6025\u5fd9\u8df3\u4e0b\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u53cc\u8db3\u7740\u5730\u3002\u4e0d\u4f46\u59ff\u5f0f\u7f8e\u5999\uff0c\u800c\u4e14\u662f\u5728\u756a\u50e7\u7740\u5730\u4e4b\u540e\u65b9\u59cb \u843d\u4e0b\u3002 \u3000\u3000\u7ea2\u8863\u756a\u618e\u7684\u5750\u9a91\u649e\u5728\u5d16\u77f3\u4e0a\uff0c\u63e1\u5f97\u8111\u6d46\u6d82\u5730\uff0c\u767b\u65f6\u6b7b\u4e86\u3002\u8d6b\u8fde\u6e05\u6ce2\u7684\u5750\u9a91\u756a\u50e7\u7684\u5288\u7a7a\u638c \u529b\u9707\u7ffb\uff0c\u8dcc\u4e0b\u60ac\u5d16\uff0c\u53ea\u542c\u4e00\u58f0\u6781\u4e3a\u523a\u8033\u7684\u51c4\u60e8\u5636\u9e23\uff0c\u6599\u60f3\u4e5f\u662f\u6b7b\u4e86\u3002 \u3000\u3000\u539f\u6765\u756a\u50e7\u7684\u5750\u9a91\uff0c\u662f\u7ed9\u8d6b\u8fde\u6e05\u6ce2\u7684\u4e24\u679a\u6885\u82b1\u9488\u5c04\u778e\u4e86\u773c\u775b\u3002\u6885\u82b1\u9488\u662f\u6700\u5c0f\u7684\u4e00\u79cd\u6697\u5668\uff0c \u5979\u53c8\u662f\u5728\u7a7a\u4e2d\u5c04\u51fa\u3002\u7ea2\u8863\u756a\u50e7\u6839\u672c\u5c31\u9632\u4e0d\u5230\u5979\u8fd8\u6709\u8fd9\u624b\u529f\u592b\uff0c\u4e0d\u8fc7\uff0c\u5047\u5982\u5979\u4e0d\u662f\u5c04\u9a6c\u800c\u662f\u5c04 \u4eba\u7684\u8bdd\uff0c\u5219\u662f\u7edd\u8ba1\u4f24\u5bb3\u4e0d\u4e86\u90a3\u7ea2\u8863\u756a\u50e7\u3002\u7ea2\u8863\u756a\u50e7\u6709\u4e00\u8eab\u6a2a\u7ec3\u529f\u592b\uff0c\u4e00\u679a\u7ec6\u5c0f\u7684\u6885\u82b1\u9488\u5373\u80fd \u7a7f\u7834\u4ed6\u7684\u8863\u88f3\uff0c\u4e5f\u523a\u4e0d\u8fdb\u4ed6\u7684\u4f53\u5185\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u614c\u5fd9\u8eb2\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u795e\u8272\u81ea\u5982\uff0c\u811a\u4e00\u6cbe\u5730\uff0c\u4fbf\u5373\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u60f3\u4e0d\u5230\u4f60\u7684\u6eda\u4e0b\u9a6c\u6765\uff0c\u6eda\u5f97\u6bd4\u6211 \u8fd8\u5feb\u3002\u5927\u54e5\u83ab\u8bf4\u4e8c\u54e5\uff0c\u5f7c\u6b64\u5f7c\u6b64\uff0c\u591a\u52b3\u8fce\u5019\u3002\u201d \u3000\u3000\u7ea2\u8863\u756a\u50e7\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5996\u5973\u5012\u8fd8\u6709\u4e9b\u9b3c\u95e8\u9053\uff0c\u4f46\u96d5\u866b\u5c0f\u6280\uff0c\u603b\u662f\u96be\u767b\u5927\u96c5\u4e4b \u5802\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5927\u548c\u5c1a\u8001\u8fdc\u8dd1\u6765\u505a\u4e00\u4e2a\u571f\u9738\u7684\u6253\u624b\uff0c\u5f52\u4e91\u5e84\u7684\u5ba2\u5385\u4e5f\u7b97\u4e0d\u5f97\u662f\u4ec0 \u4e48\u5927\u96c5\u4e4b\u5802\u5427\uff1f\u201d \u3000\u3000\u7ea2\u8863\u756a\u50e7\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u4f60\u82e5\u4e0d\u662f\u80e1\u4e71\u5413\u552c\u4eba\uff0c\u6211\u4e5f\u4e0d\u4f1a\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u83ab\u540d\u5176\u5999\uff0c\u5012\u662f\u4e0d\u89c9\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5413\u552c\u8c01\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u8863\u756a\u50e7\u9053\uff1a\u201c\u4f60\u662f\u5938\u53e3\u8bf4\u4f60\u80fd\u591f\u7528\u5316\u8840\u5200\u53d6\u4eba\u6027\u547d\u4e48\uff0c\u6211\u662f\u7279\u5730\u6765\u8bd5\u8bd5\u4f60\u8fd9\u5316\u8840\u5200\u662f \u771f\u662f\u5047\u7684\uff1f\u201d \u3000\u3000\u201c\u5316\u8840\u5200\u201d\u662f\u4ece\u5929\u7afa\u4f20\u6765\u7684\u4e00\u79cd\u6781\u4e3a\u602a\u5f02\u7684\u6b66\u529f\uff0c\u540d\u4e3a\u201c\u5200\u201d\uff0c\u5176\u5b9e\u5e76\u975e\u771f\u5200\uff0c\u4e43\u662f\u4ee5 \u638c\u4f5c\u5200\u3002\u8fd9\u79cd\u602a\u5f02\u6b66\u529f\u7528\u638c\u529b\u53d1\u51fa\uff0c\u636e\u8bf4\u80fd\u4ee4\u4eba\u8840\u6db2\u4e2d\u6bd2\uff0c\u75c5\u75c7\u4e00\u65e5\u4e00\u65e5\u52a0\u91cd\uff0c\u53d7\u5c3d\u8bf8\u822c\u75db \u82e6\uff0c\u65b9\u59cb\u6b7b\u4ea1\uff0c\u56e0\u6b64\u4e5f\u53ef\u8bf4\u5f97\u662f\u4e00\u79cd\u6bd2\u529f\u548c\u5185\u529f\u7ed3\u5408\u7684\u6bd2\u638c\u3002\u4e2d\u4e86\u5316\u8840\u5200\uff0c\u8eab\u4e0a\u4f1a\u7559\u4e0b\u7ea2\u8272 \u7684\u638c\u5370\u548c\u8d6b\u8fde\u6e05\u6ce2\u90a3\u65e5\u7559\u5728\u5f52\u4e91\u5e84\u90a3\u4e24\u4e2a\u95e8\u5ba2\u8eab\u4e0a\u7684\u5370\u76f8\u4f3c\uff0c\u90a3\u65e5\u8d6b\u8fde\u6e05\u6ce2\u4e3a\u4e86\u6050\u5413\u4ed6\u4eec\uff0c \u662f\u66fe\u628a\u81ea\u5df1\u7684\u6bd2\u638c\u5192\u5145\u4e3a\u5316\u8840\u5200\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u597d\uff0c\u8981\u8bd5\u5c31\u6765\u8bd5\u5427\uff01\u770b\u5200\uff01\u201d\u6a2a\u638c\u5982\u5200\uff0c\u5411\u6606\u5e03\u7985\u5e08\u5288\u53bb\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5996\u5973\u5927\u8a00\u4e0d\u60ed\uff0c\u8fd9\u662f\u4ec0\u4e48\u5316\u8840\u5200\uff1f\u53ea\u662f\u62db\u5f0f\u7a0d\u5fae\u76f8\u4f3c\u800c\u5df2\uff0c\u563f \u563f\uff0c\u4f60\u8981\u89c1\u8bc6\u771f\u7684\u5316\u8840\u5200\uff0c\u770b\u6211\u7684\u5427\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u8d6b\u8fde\u6e05\u6ce2\u7684\u624b\u4e2d\u7a81\u7136\u591a\u4e86\u4e00\u628a\u5200\uff0c\u662f\u771f\u7684\u94a2\u5200\uff0c\u5e76\u975e\u201c\u638c \u5200\u201d\u3002 \u3000\u3000\u539f\u6765\u5979\u8fd9\u628a\u5200\u4e43\u662f\u201c\u767e\u70bc\u94a2\u53ef\u4ee5\u5316\u4e3a\u7ed5\u6307\u67d4\u201d\u7684\u771f\u6b63\u5b9d\u5200\uff0c\u85cf\u4e8e\u8896\u5b50\u4e2d\uff0c\u4ee5\u638c\u52bf\u4f5c\u4e3a\u63a9 \u9970\uff0c\u7a81\u7136\u5c31\u4eae\u51fa\u6765\u7684\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u8fc7\u867d\u60ca\u4e0d\u4e71\uff0c\u767e\u5fd9\u4e2d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u6325\u638c\u53cd\u51fb\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6 \u53ea\u89c9\u5f97\u5934\u53d1\u4e00\u7247\u6c81\u51c9\uff0c\u5200\u950b\u51e0\u4e4e\u662f\u64e6\u7740\u4ed6\u7684\u5149\u5934\u524a\u8fc7\u3002 \u3000\u3000\u4ed6\u90a3\u4e00\u638c\u4e5f\u6ca1\u6253\u7740\u8d6b\u8fde\u6e05\u6ce2\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u88ab\u4ed6\u7684\u638c\u529b\u8361\u6b6a\u5200\u950b\uff0c\u6697\u53eb\u201c\u53ef\u60dc\u201d\uff0c\u8eab\u968f\u5200\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u628a\u5200\u80fd\u996e\u4f60\u7684 \u8840\uff0c\u600e\u4e48\u4e0d\u662f\u5316\u8840\u5200\uff1f\u201d\u53e3\u4e2d\u8bf4\u7b11\uff0c\u5200\u6cd5\u4e1d\u8c6a\u4e0d\u7f13\uff0c\u5979\u5c55\u5f00\u7ed5\u8eab\u6e38\u6597\u7684\u6253\u6cd5\uff0c\u8f6c\u773c\u95f4\u5c31\u5288\u4e86 \u516d\u516d\u4e09\u5341\u516d\u5200\u3002\u6606\u5e03\u7985\u5e08\u88ab\u5979\u5236\u4e86\u5148\u673a\uff0c\u4ed6\u90a3\u771f\u7684\u201c\u5316\u8840\u5200\u201d\u7adf\u7136\u8fd8\u672a\u80fd\u4f7f\u5f97\u51fa\u6765\u3002 \u3000\u3000\u6218\u5230\u6b64\u9645\uff0c\u6606\u5e03\u7985\u5e08\u84e6\u5730\u559d\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u8ba9\u4f60\u89c1\u8bc6\u771f\u7684\u5316\u8840\u5200\u5427\uff1f \u3000\u3000\u201d \u3000\u3000\u53f3\u638c\u5f20\u5f00\u3002\u638c\u5fc3\u9c9c\u7ea2\u5982\u8840\uff0c\u4e00\u80a1\u523a\u9f3b\u7684\u8165\u98ce\u4ee4\u5f97\u8d6b\u8fde\u6e05\u6ce2\u51e0\u4e4e\u4f5c\u5455\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7684\u201c\u5316\u8840\u5200\u201d\u5c1a\u672a\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u5728\u4f7f\u7528\u7684\u65f6\u5019\uff0c\u8fd8\u8981\u9ed8\u8fd0\u7384\u529f\u7684\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u8d6b\u8fde\u6e05\u6ce2\u5df2\u662f\u7981\u53d7\u4e0d\u8d77\u4e86\u3002\u5979\u4ec5\u7740\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u8eb2\u4e86\u51e0\u62db\uff0c\u8d8a\u6765\u8d8a\u89c9\u5f97\u80f8 \u4e2d\u4f5c\u95f7\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e45\u6218\u4e0b\u53bb\uff0c\u6211\u6ca1\u7ed9\u4ed6\u7684\u5316\u8840\u5200\u5288\u5012\uff0c\u53ea\u6015\u4e5f\u4f1a\u6655\u5012\u3002\u6253\u4e0d\u8fc7\u8fd8\u662f\u8dd1 \u5427\u3002\u201d \u3000\u3000\u5c31\u662f\u6b64\u65f6\uff0c\u5c71\u5773\u90a3\u8fde\u6709\u58f0\u97f3\u4f20\u6765\u3002 \u3000\u3000\u201c\u54a6\uff0c\u90a3\u4e2a\u5973\u5b69\u5b50\u597d\u50cf\u662f\u90e1\u4e3b\u3002\u201d \u3000\u3000\u201c\u8ba9\u6211\u8fc7\u53bb\u770b\uff0c\u4f60\u4eec\u4e0d\u5fc5\u591a\u8a00\uff01\u201d\u8d6b\u8fde\u6e05\u6ce2\u542c\u5f97\u8fd9\u4e2a\u719f\u6089\u7684\u58f0\u97f3\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u8fde\u5fd9\u53eb \u9053\uff1a\u201c\u5927\u54e5\u5feb\u6765\uff01\u201d \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u4eba\u5df2\u7ecf\u6765\u5230\u3002\u5e74\u7ea6\u4e8c\u5341\u591a\u5c81\uff0c\u5934\u6234\u7d2b\u91d1\u51a0\uff0c\u8eab\u62ab\u767d\u72d0\u88d8\uff0c\u770b\u6765\u50cf\u662f\u4e2a\u8d35\u516c \u5b50\uff0c\u76f8\u8c8c\u548c\u8d6b\u8fde\u6e05\u6ce2\u5374\u4e0d\u76f8\u50cf\u3002\u5728\u4ed6\u540e\u9762\u8fd8\u8ddf\u7740\u4e24\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u4f3c\u4e4e\u662f\u4ed6\u7684\u968f\u4ece\u3002 \u3000\u3000\u6700\u4ee4\u5f97\u6606\u5e03\u7985\u5e08\u60ca\u8be7\u7684\u8fd8\u662f\u4ed6\u624b\u4e2d\u62ff\u7684\u4e00\u6839\u7af9\u6756\u3002\u8fd9\u6839\u7af9\u6756\u6676\u83b9\u5982\u7389\uff0c\u4f46\u53ef\u4ee5\u770b\u5f97\u51fa\u5e76 \u975e\u7389\u8d28\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u79c3\u9a74\u6b3a\u8d1f\u6211\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u516c\u5b50\u9053\uff1a\u201c\u597d\uff0c\u4f60\u9000\u4e0b\u53bb\uff0c\u8ba9\u6211\u6559\u8bad\u6559\u8bad\u4ed6\uff01\u201d\u6606\u5e03\u7985\u5e08\u597d\u751f\u7eb3\u7f55\uff0c\u95ee\u9053\uff1a\u201c\u4f60 \u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u5c11\u5e74\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u6b3a\u8d1f\u6211\u59b9\u59b9\uff0c\u90a3\u5c31\u4e0d\u884c\uff01\u201d\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5bf9 \u5566\uff0c\u5927\u54e5\uff0c\u6211\u8fd8\u544a\u8bc9\u4f60\u5462\u3002\u8fd9\u79c0\u9a74\u662f\u8981\u7528\u5316\u8840\u5200\u6740\u6211\u7684\uff01 \u3000\u3000\u201d\u5f26\u5916\u4e4b\u97f3\uff0c\u53ea\u201c\u6559\u8bad\u201d\u662f\u4e0d\u591f\u7684\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u516c\u5b50\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u6740\u4e86\u4ed6\u66ff\u4f60\u51fa\u6c14\u5c31\u662f\u4e86\uff01\u201d\u8bf4\u5230\u4e00\u4e2a\u201c\u6740\u201d \u3000\u3000\u5b57\uff0c\u53ea\u89c1\u7eff\u8272\u7684\u5149\u534e\u95ea\u8000\uff0c\u4ed6\u624b\u4e2d\u7684\u90a3\u6839\u7af9\u6756\u5df1\u662f\u597d\u50cf\u6bd2\u86c7\u51fa\u6d1e\u4f3c\u7684\uff0c\u5411\u7740\u6606\u5e03\u7985\u5e08\u7684 \u54bd\u5589\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u6012\u9053\uff1a\u201c\u72c2\u5984\u5c0f\u5b50\uff0c\u6211\u5012\u8981\u770b\u4f60\u5982\u4f55\u6740\u5f97\u4e86\u6211\uff01\u201d\u53cc\u6307\u4e00\u6307\uff0c\u5411\u7af9\u6756\u5f39\u53bb\u3002 \u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5f39\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u4ee5\u4e3a\u51ed\u81ea\u5df1\u7684\u6b66\u529b\uff0c\u8fd9\u4e00\u5f39\u5c31\u53ef\u4ee5\u628a\u5c11\u5e74\u7684\u7af9\u6756\u5f39\u51fa\u53bb\u3002\u90a3\u77e5\u8fd9\u4e2a\u7af9\u6756\u575a\u903e\u7cbe \u94a2\uff0c\u4ed6\u975e\u4f46\u6ca1\u6709\u628a\u7af9\u6756\u5f39\u5f00\uff0c\u4e24\u6839\u6307\u5934\u53cd\u800c\u75db\u5f97\u597d\u4f3c\u7ed9\u94c1\u9524\u7838\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u8981\u4e0d\u662f\u4ed6\u7ec3\u8fc7\u91d1 \u521a\u6307\u7684\u529f\u592b\uff0c\u53ea\u6015\u6307\u9aa8\u90fd\u8981\u788e\u88c2\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u614c\u5fd9\u4e00\u4e2a\u79fb\u5f62\u6613\u4f4d\uff0c\u53cd\u624b\u5288\u51fa\u3002\u8fd9\u4e00\u638c\u5df2\u662f\u7528\u5230\u516b\u4e5d\u5206\u529f \u529b\u3002\u5c11\u5e74\u4e5f\u4f3c\u77e5\u9053\u4ed6\u7684\u5389\u5bb3\uff0c\u4e0d\u6562\u548c\u4ed6\u786c\u78b0\u3002\u7acb\u5373\u628a\u5411\u524d\u5e73\u6311\u7684\u5c0f\u82b1\u67aa\u62db\u6570\u53d8\u4e3a\u4e24\u7ffc\u659c\u98de\u7684 \u5224\u5b98\u7b14\u62db\u6570\u3002\u4ed6\u8fd9\u6839\u7af9\u6756\uff0c\u5f53\u771f\u6d3b\u50cf\u7075\u86c7\uff0c\u4f38\u7f29\u4e0d\u5b9a\u3002\u6606\u5e03\u7985\u5e08\u4e00\u638c\u5288\u7a7a\uff0c\u5c11\u5e74\u7684\u7af9\u679a\u5df2\u662f \u5728\u4e00\u62db\u4e4b\u5185\uff0c\u904d\u88ad\u4ed6\u7684\u4e03\u5904\u7a74\u9053\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u4f7f\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u597d\u4e0d\u5bb9\u6613\u907f\u8fc7\u4ed6\u8fd9\u4e00\u62db\uff0c\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8d76\u5fd9\u4e00\u4e2a\u5012\u7eb5\uff0c\u8dc3 \u51fa\u4e09\u4e08\u5f00\u5904\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u548c\u8fd9\u5c0f\u5996\u5973\u5927\u6982\u4e0d\u662f\u4eb2\u5144\u59b9\u80a5\uff01\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u505a\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5c11\u5e74\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u77e5\u9053\u5979\u505a\u7684\u4e8b\u60c5\uff0c\u4f60\u505a\u7684\u4e8b\u60c5\u6211\u5374\u5df2\u89c1\u5230\u4e86\uff0c\u5c31\u51ed\u4f60\u9a82\u8fd9\u58f0\u5996 \u5973\uff0c\u6211\u5c31\u4e0d\u80fd\u9976\u4f60\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5df2\u662f\u5982\u5f71\u968f\u5f62\uff0c\u8ddf\u8e2a\u6251\u4e0a\u3002\u7af9\u6756\u8d77\u5904\uff0c\u62db\u62db\u6307\u5411\u6606\u5e03\u7985\u5e08 \u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u601d\u5230\uff1a\u201c\u6211\u4e0d\u4f24\u4ed6\uff0c\u6027\u547d\u5148\u81ea\u4e0d\u4fdd\uff1f\u201d\u53ef\u5c31\u4e0d\u987e\u90a3\u4e48\u591a\u4e86\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6606\u5e03\u7985\u5e08\u6ef4\u6e9c\u6e9c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7a81\u7136\u95f4\u597d\u50cf\u5e73\u5730\u4e0a\u8d77\u4e86\u4e00\u7247\u7ea2\u5e03\uff0c\u6321\u4f4f\u4e86\u90a3\u5c11\u5e74\u7684\u7af9 \u6756\u3002\u4ed6\u662f\u8131\u4e0b\u4e86\u8eab\u4e0a\u6240\u62ab\u7684\u5927\u7ea2\u8888\u88df\uff0c\u5f53\u4f5c\u5175\u5668\u3002 \u3000\u3000\u4ed6\u7684\u5185\u529f\u672c\u6765\u6bd4\u8fd9\u5c11\u5e74\u6df1\u539a\uff0c\u8fd9\u4ef6\u8888\u88df\u5728\u4ed6\u624b\u4e2d\u8fd0\u7528\u8d77\u6765\uff0c\u80dc\u4e8e\u4e00\u9762\u76fe\u724c\u3002 \u3000\u3000\u5c11\u5e74\u7684\u7af9\u6756\u653b\u4e0d\u8fc7\u53bb\uff0c\u6606\u5e03\u7985\u5e08\u5598\u606f\u5df2\u5b9a\uff0c\u91cd\u65b0\u8fd0\u8d77\u201c\u5316\u8840\u795e\u529f\u201d\uff0c\u559d\u9053\uff1a\u201c\u5978\uff0c\u4f60\u8fd9 \u5c0f\u5b50\u4e0d\u80af\u7f62\u4f11\uff0c\u6211\u5c31\u53eb\u4f60\u4e5f\u5c1d\u5c1d\u6211\u8fd9\u5316\u8840\u5200\u7684\u6ecb\u5473\uff01\u201d \u3000\u3000\u4ed6\u5de6\u624b\u6325\u821e\u8888\u88df\uff0c\u5728\u8888\u88df\u63a9\u62a4\u4e4b\u4e0b\uff0c\u51fa\u638c\u4f3a\u673a\u88ad\u654c\uff0c\u4ed6\u7684\u53f3\u638c\u53ef\u4e0d\u662f\u5bfb\u5e38\u8089\u638c\uff0c\u800c\u662f\u53ef \u4ee5\u81f4\u547d\u7684\u201c\u5316\u8840\u5200\u201d\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u88c5\u4f5c\u770b\u4e0d\u51fa\u5371\u673a\u6240\u5728\uff0c\u8d5e\u9053\uff1a\u201c\u5999\u554a\uff0c\u5999\u554a\uff01\u60f3\u4e0d\u5230\u6211\u548c\u54e5\u54e5\u5206\u624b\u4e0d\u8fc7\u6570\u6708\uff0c \u4ed6\u7684\u60ca\u795e\u7b14\u6cd5\u5df2\u7ecf\u7ec3\u5f97\u7cbe\u5999\u5982\u65af\uff01\u201d \u3000\u3000\u5e74\u957f\u90a3\u968f\u4ece\u9053\uff1a\u201c\u662f\u5440\uff0c\u8001\u3001\u8001\u4e3b\u4eba\u5c31\u662f\u56e0\u4e3a\u5c0f\u3001\u5c0f\u516c\u5b50\u7ec3\u6210\u4e86\u60ca\u795e\u7b14\u6cd5\uff0c\u624d\u628a\u7eff\u7389\u6756 \u7ed9\u4ed6\u4f7f\u7528\u7684\u3002\u201d \u3000\u3000\u201c\u8001\u4e3b\u4eba\u201d\u7684\u79f0\u547c\u8fd8\u4e0d\u7b97\u5947\u602a\uff0c\u4f46\u201c\u5c0f\u516c\u5b50\u201d\u7684\u79f0\u547c\uff0c\u4e00\u822c\u4eba\u5374\u662f\u6ca1\u6709\u8fd9\u79cd\u4e60\u60ef\u7684\u53eb \u6cd5\u3002\u539f\u6765\u90a3\u968f\u4ece\u60f3\u8bf4\u201c\u8001\u738b\u7237\u201d\u548c\u201c\u5c0f\u738b\u7237\u201d\u7684\uff0c\u88ab\u8d6b\u8fde\u6e05\u6ce2\u4e00\u77aa\u773c\u775b\uff0c\u65b9\u59cb\u7701\u609f\uff0c\u6539\u4e86\u79f0 \u547c\u3002 \u3000\u3000\u6606\u5e03\u7985\u5e08\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u4ee4\u5c0a\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c11\u5e74\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u77e5\u9053\u6211\u7239\u7239\u4e4b\u540d\u5b57\uff1f\u201d\u7af9\u6756\u4e00\u6311\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u6606 \u5e03\u7985\u5e08\u90a3\u4ef6\u8888\u88df\u7a7f\u4e86\u4e00\u4e2a\u5b54\u3002\u539f\u6765\u4ed6\u5728\u5927\u60ca\u4e4b\u4e0b\uff0c\u5185\u529f\u5df1\u662f\u4e0d\u80fd\u8d2f\u6ce8\u5230\u8888\u88df\u4e0a\uff0c\u5c11\u5e74\u8d81\u8fd9\u65f6 \u673a\uff0c\u987f\u65f6\u53cd\u593a\u5148\u624b\u3002 \u3000\u3000\u8888\u88df\u4e00\u7834\uff0c\u5f53\u4f5c\u76fe\u724c\u7684\u529f\u529b\u5df2\u662f\u6253\u4e86\u4e00\u4e2a\u6298\u6263\u3002\u5c11\u5e74\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u60ca\u795e\u7b14\u6cd5\u970d\u970d\u5c55\u5f00\uff0c \u6bcf\u4e00\u62db\u90fd\u662f\u5f88\u8fa3\u4e4b\u6781\u7684\u6740\u624b\u3002\u6606\u5e03\u7985\u5e08\u5728\u4ed6\u72c2\u98ce\u66b4\u96e8\u7684\u6025\u653b\u4e4b\u4e0b\uff0c\u53c8\u518d\u9677\u4e8e\u82e6\u6218\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u5df2\u9690\u9690\u731c\u5230\u8fd9\u5c11\u5e74\u516c\u5b50\u8eab\u4efd\uff0c\u4f46\u5374\u82e6\u4e8e\u4e0d\u80fd\u5206\u795e\u8bf4\u8bdd\u3002 \u3000\u3000\u8eb2\u5728\u5ca9\u77f3\u540e\u9762\u7684\u73ed\u5b9a\u5c71\u7a81\u7136\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u51fa\u73b0\uff0c\u90a3\u4e24\u4e2a\u968f\u4ece\u5c31\u8dd1\u8fc7\u6765\u3002\u8d6b\u8fde\u6e05\u6ce2\u5374\u4f3c\u5728\u5168\u795e\u89c2\u6218\uff0c\u4e00\u70b9\u4e0d\u52a0\u7406\u4f1a\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u8ba4\u8bc6\u5176\u4e2d\u4e00\u4e2a\u968f\u4ece\uff0c\u8fde\u5fd9\u8fce\u4e0a\u524d\u53bb\uff0c\u6253\u4e2a\u62db\u547c\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u6d4e\u738b\u5e9c\u7684\u7ebd\u5927\u4eba\u5427\uff0c \u4e45\u8fdd\u4e86\u3002\u53ef\u8fd8\u8bb0\u5f97\u5728\u4e0b\uff1f\u201d\u201c\u6d4e\u738b\u201d\u662f\u5b8c\u989c\u957f\u4e4b\u7684\u81ea\u53f7\u3002\u8fd9\u540d\u968f\u4ece\u540d\u5524\u7ebd\u795c\u7984\uff0c\u6b63\u662f\u5b8c\u989c\u957f \u4e4b\u7684\u4e00\u540d\u4f8d\u536b\u3002\u53e6\u4e00\u4e2a\u968f\u4ece\u540d\u5524\u963f\u5c14\u91d1\uff0c\u548c\u4ed6\u804c\u4f4d\u76f8\u540c\u3002 \u3000\u3000\u7ebd\u795c\u7984\u5b9a\u775b\u4e00\u770b\uff0c\u4f9d\u7a00\u4f3c\u66fe\u76f8\u8bc6\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u2014\u2014\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u9053\uff1a\u201c\u5728\u4e0b\u662f\u6d1b\u9633\u864e\u5a01\u9556\u5c40\u7684\u73ed\u5b9a\u5c71\uff0c\u5341\u5e74\u524d\u66fe\u7ecf\u5230\u8fc7\u738b\u5e9c\u9001\u8bb0\u793c\u7684\u3002\u201d \u3000\u3000\u7ebd\u795c\u7984\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u864e\u5a01\u9556\u5c40\u7684\u73ed\u603b\u9556\u5934\uff0c\u6211\u8bb0\u8d77\u6765\u4e86\uff0c\u90a3\u5929\u8fd8\u662f\u6211\u66ff\u738b\u7237\u6536\u4e0b\u4f60 \u7684\u5927\u793c\u7684\u5462\uff1f\u201d\u90a3\u5929\u73ed\u5b9a\u5c71\u9664\u4e86\u9001\u7ed9\u738b\u7237\u4e00\u4efd\u201c\u5927\u793c\u201d \u3000\u3000\u4e4b\u5916\uff0c\u8fd8\u6709\u9001\u7ed9\u4ed6\u7684\u4e00\u4efd\u4e0d\u5927\u4e0d\u5c0f\u7684\u793c\u7269\uff0c\u6240\u4ee5\u4ed6\u5bf9\u73ed\u5b9a\u5c71\u7684\u5370\u8c61\u4e5f\u6bd4\u8f83\u6df1\u523b\uff0c\u4e00\u8bf4\u5c31 \u8bb0\u8d77\u6765\u4e86\u3002 \u3000\u3000\u73ed\u5b9a\u5c71\u9053\uff1a\u201c\u7ebd\u5927\u4eba\u597d\u8bb0\u6027\u3002\u8fd9\u4f4d\u516c\u5b50\u60f3\u5fc5\u662f\u5c0f\u738b\u7237\u5427\uff1f\u201d\u7ebd\u795c\u7984\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u8fd9\u4f4d \u516c\u5b50\u662f\u8c01\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u600e\u4e48\u4f1a\u8dd1\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u6478\u4e0d\u6e05\u5c0f\u738b\u7237\u548c\u8d6b\u8fde\u6e05\u6ce2\u7684\u5173\u7cfb\uff0c\u6b63\u5728\u7422\u78e8\u8981\u600e\u6837\u8bf4\u51fa\u6765\u65b9\u59cb\u5f97\u5f53\uff0c\u6606\u5e03\u7985\u5e08\u5df1 \u662f\u6309\u637a\u4e0d\u4f4f\u4e86\u3002\u4ed6\u4e00\u6454\u8888\u88df\uff0c\u628a\u5c0f\u738b\u7237\u903c\u9000\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u738b\u7237\uff0c\u54b1\u4eec\u662f\u81ea\u5df1\u4eba\u3002\u8bf7\u6055\u5c0f \u50e7\u5192\u72af\u4e4b\u7f6a\uff0c\u6682\u4e14\u4f4f\u624b\uff0c\u5bb9\u5c0f\u50e7\u7980\u544a\uff01\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u9053\uff1a\u201c\u54e6\uff0c\u6211\u600e\u4e48\u4f1a\u548c\u4f60\u662f\u81ea\u5df1\u4eba\uff1f\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u5c0f\u618e\u7684\u5e08\u53d4\u6cd5\u53f7\u8fe6\u5362\uff0c\u5728\u4ee4\u5c0a\u7684\u738b\u5e9c\u8499\u53d7\u4f9b\u5949\u5df1\u6709\u5341\u591a\u5e74\u4e86\u3002\u5c0f\u50e7\u4e5f\u66fe \u5230\u8fc7\u738b\u5e9c\u7684\uff0c\u4e0d\u8fc7\u90a3\u65f6\u5019\u5c0f\u738b\u7237\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u6050\u6015\u8bb0\u4e0d\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u738b\u7237\u201d\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8fe6\u5362\u58eb\u4eba\u7684\u5e08\u4f84\uff0c\u602a\u4e0d\u5f97\u4f60\u4f1a\u4f7f\u5316\u8840\u5200\u3002\u4f55\u4e8b \u7980\u544a\uff0c\u8bf4\u5427\uff1f\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u73ed\u5b9a\u5c71\u548c\u5c0a\u9a7e\u6240\u8bf4\u7684\u8bdd\uff0c\u5c0f\u738b\u7237\u542c\u89c1\u4e86\u5427\uff01\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u542c\u89c1\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u5378\u6797\u519b\u526f\u7edf\u9886\u54c8\u5fc5\u56fe\u54c8\u5927\u4eba\u5949\u5723\u65e8\u79d8\u5bc6\u51fa\u4eac\uff0c\u524d\u4e24\u5929\u6765\u5230\u6d1b\u9633\uff0c\u6b64\u4e8b\u5c0f \u738b\u7237\u77e5\u9053\u5426\uff1f\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u6211\u77e5\u4e0d\u77e5\u9053\uff0c\u6709\u8bdd\u4f60\u53ea\u7ba1\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u54c8\u5927\u4eba\u524d\u5929\u6765\u5230\u5f52\u4e91\u5e84\uff0c\u8d3a\u5f52\u5e84\u4e3b\u7684\u516d\u5341\u5927\u5bff\uff0c\u60f3\u4e0d\u5230\u5374\u5728\u5f52\u4e91\u5e84\u91cc\uff0c \u7ed9\u4eba\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5c0f\u738b\u7237\u4f6f\u4f5c\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u8c01\u6562\u8fd9\u6837\u5927\u80c6\uff1f\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u6765\u5386\u4e0d\u660e\u7684\u5c0f\u5b50\uff0c\u90a3\u5c0f\u5b50\u548c\u8fd9\u4f4d\u59d1\u5a18\u4e00\u540c\u6765\u5230\u5f52\u4e91\u5e84\uff0c\u53c8\u4e00\u540c\u79bb \u5f00\u5f52\u4e91\u5e84\u7684\u3002\u5c0f\u50e7\u4e0d\u6562\u5984\u81ea\u63e3\u6d4b\uff0c\u4e0d\u8fc7\u770b\u6765\u4ed6\u4eec\u4f3c\u4e4e\u662f\u76f8\u5f53\u719f\u8bc6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u548c\u73ed\u5b9a\u5c71\u4e0d\u540c\uff0c\u4ed6\u662f\u77e5\u9053\u5b8c\u989c\u957f\u4e4b\u53ea\u6709\u4e00\u4e2a\u513f\u5b50\uff0c\u5e76\u65e0\u5973\u513f\u7684\u3002\u6545\u800c\u8bf4\u8bdd\u5c31\u6bd4 \u73ed\u5b9a\u5c71\u5927\u80c6\u5f97\u591a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5996\u5973\u9887\u6709\u51e0\u5206\u59ff\u8272\uff0c\u6599\u60f3\u662f\u4e0d\u77e5\u600e\u7684\u5c0f\u738b\u7237\u7ed9\u5979\u52fe\u642d\u4e0a \u4e86\uff0c\u5c0f\u738b\u7237\u9690\u7792\u8eab\u4efd\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u5728\u4eba\u524d\u4fbf\u4e0e\u5979\u4ee5\u5144\u59b9\u76f8\u79f0\uff0c\u4f46\u4ee5\u5c0f\u738b\u7237\u7684\u8eab\u4efd\uff0c\u5929\u4e0b\u4f73 \u4e3d\u4f55\u6c42\u4e0d\u5f97\u3002\u6599\u60f3\u4ed6\u4e5f\u4e0d\u4f1a\u4e3a\u4e86\u79c1\u60c5\uff0c\u628a\u54c8\u5fc5\u56fe\u88ab\u6740\u7684\u8fd9\u4ef6\u5927\u6848\u4e5f\u4e0d\u8ffd\u7a76\u5427\uff1f\u54c8\u5fc5\u56fe\u53ef\u662f\u4ed6 \u7239\u7239\u7684\u526f\u624b\u554a\uff01\u201d \u3000\u3000\u5c0f\u738b\u7237\u679c\u7136\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u5018\u82e5\u662f\u771f\uff0c\u8fd9\u4ef6\u4e8b\u6211\u5012\u4e0d\u80fd\u4e0d\u7ba1\u4e86\uff1f\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u771f\uff1f\u73ed\u5b9a\u5c71\u662f\u5f52\u4e91\u5e84\u7684\u5927\u5f1f\u4e86\uff0c\u90a3\u5929\u4ed6\u4e5f\u5728\u573a\u7684\u3002\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u5728\u90a3\u8fb9\u8fde\u5fd9\u56de\u7b54\uff1a\u201c\u7980\u5c0f\u738b\u7237\uff0c\u6606\u5e03\u7985\u5e08\u8bf4\u7684\u53e5\u53e5\u662f\u771f\u3002\u5c0f\u7684\u60f3\u8981\u7980\u544a\u7684\u90a3\u4ef6\u5927 \u4e8b\uff0c\u4ed6\u5df2\u7ecf\u66ff\u6211\u8bf4\u4e86\u3002\u5c0f\u738b\u7237\u82e5\u8fd8\u4e0d\u4fe1\uff0c\u53ef\u4ee5\u5230\u5f52\u4e91\u5e84\u67e5\u95ee\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u60c5\u662f\u8bb8\u591a\u4eb2\u773c\u89c1\u5230\u7684\uff0c\u6d1b\u9633\u7684\u77e5\u5e9c\u5927\u4eba\u4e5f\u662f\u8bc1\u4eba\u4e4b\u4e00\u3002 \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u5462\uff1f\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u9053\uff1a\u201c\u8fd9\u6211\u4eec\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u5c0f\u738b\u7237\u60f3\u8981\u77e5\u9053\u90a3\u5c0f\u5b50\u7684\u4e0b\u843d\uff0c\u6050\u6015\u5f97\u95ee\u2026\u2026\u201d\u8bf4 \u8bdd\u4e4b\u65f6\uff0c\u773c\u775b\u671d\u8d6b\u8fde\u6e05\u6ce2\u90a3\u8fb9\u671b\u53bb\u3002 \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u597d\uff0c\u6211\u548c\u4f60\u53bb\u95ee\u5979\u3002\u201d \u3000\u3000\u6606\u5e03\u7985\u5e08\u5fc3\u4e2d\u5927\u559c\uff0c\u4e0d\u7591\u6709\u4ed6\u3002\u90a3\u77e5\u5c0f\u738b\u7237\u8d81\u4ed6\u5bb6\u65e0\u9632\u5907\u4e4b\u9645\uff0c\u53cd\u624b\u4e00\u6756\uff0c\u7a81\u7136\u5411\u4ed6\u6233 \u53bb\u3002 \u3000\u3000\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u6606\u5e03\u7985\u5e08\u8fde\u53eb\u4e5f\u53eb\u4e0d\u51fa\u6765\uff0c\u5c31\u5411\u540e\u7ffb\u9aa8\u788c\u788c\u7684\u6eda\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u5e78\u4e8f\u4f60\u6765\u5f97\u53ca\u65f6\uff0c\u8fd9\u79c3\u9a74\u597d\u4e0d\u5389\u5bb3\uff01\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u7ed9\u6211\u70b9\u4e2d\u6b7b\u7a74\uff0c\u4f60\u8981\u4e0d\u8981\u770b\u770b\u4ed6\u7684\u5c38\u4f53\uff0c\u65b9\u80fd\u5b89\u5fc3\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7b11\u9053\uff1a\u201c\u7ed9\u60ca\u795e\u7b14\u6cd5\u70b9\u4e2d\u6b7b\u7a74\uff0c\u8981\u662f\u90a3\u4eba\u8fd8\u80fd\u6d3b\u7684\u8bdd\uff0c\u60ca\u795e\u7b14\u6cd5\u8fd8\u79f0\u7684\u4e0a\u662f\u5929 \u4e0b\u7b2c\u4e00\u70b9\u7a74\u529f\u592b\u5417\uff1f\u4f55\u51b5\u4f60\u7528\u7684\u53c8\u662f\u6b66\u6797\u5f02\u5b9d\u7684\u7eff\u7389\u6756\uff0c\u4e0d\u7528\u770b\u4e86\u3002\u201d\u90a3\u4e2a\u66fe\u7ecf\u505a\u8fc7\u5b8c\u989c\u9274 \u536b\u58eb\u7684\u4faf\u6606\uff0c\u8eb2\u5728\u4e71\u77f3\u4e1b\u4e2d\uff0c\u4ed6\u662f\u8ba4\u5f97\u5c0f\u738b\u7237\u7684\uff0c\u89c1\u5c0f\u738b\u7237\u5982\u6b64\u5fc3\u72e0\uff0c\u7981\u4e0d\u4f4f\u6d51\u6709\u98a4\u6296\u3002 \u3000\u3000\u597d\u5728\u8fd8\u6709\u4e00\u4e2a\u6bd4\u4ed6\u53d1\u6296\u5f97\u66f4\u5389\u5bb3\u7684\u73ed\u5b9a\u5c71\uff0c\u4ed6\u7684\u8eab\u4f53\u548c\u77f3\u5934\u78b0\u7740\u7684\u58f0\u97f3\uff0c\u624d\u4e0d\u81f3\u4e8e\u53d7\u5230 \u5c0f\u738b\u7237\u7684\u6ce8\u610f\u3002 \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u8fd9\u4f4d\u73ed\u603b\u9556\u5934\uff0c\u4f60\u770b\u54b1\u4eec\u5e94\u8be5\u5c06\u4ed6\u600e\u6837\uff1f\u201d\u73ed\u5b9a\u5c71\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u738b\u7237\uff0c \u9976\u547d\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7b11\u9053\uff1a\u201c\u8bba\u7406\u4ed6\u66fe\u5411\u6211\u78d5\u8fc7\u5934\uff0c\u6211\u662f\u5e94\u8be5\u9976\u4ed6\u7684\u3002\u4f46\u4ed6\u5df2\u77e5\u9053\u4f60\u662f\u5c0f\u738b\u7237\uff0c\u6b64 \u4e8b\u6050\u6015\u4e0d\u5927\u5999\uff01\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u53eb\u9053\uff1a\u201c\u5c0f\u738b\u7237\uff0c\u4f60\u9976\u4e86\u6211\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6211\u7edd\u4e0d\u6562\u5bf9\u4eba\u8bf4\u534a\u4e2a\u5b57\uff01\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u5272\u4e86\u4f60\u7684\u820c\u5934\u6211\u4e5f\u4e0d\u80fd\u653e\u5fc3\uff0c\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u201c\u9664\u975e\u201d\u4ec0\u4e48\uff0c\u4ed6\u597d\u50cf\u8fd8\u6ca1\u6709\u60f3\u51fa\u6765\uff0c\u5c1a\u5728\u6c89\u541f\u3002 \u3000\u3000\u968f\u4ece\u4e4b\u4e00\u7684\u7ebd\u795c\u7984\u6700\u80fd\u4f53\u4f1a\u4e3b\u4eba\u7684\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u529e\u6cd5\uff0c\u6211\u53ef\u4ee5\u53eb\u4ed6\u53d8\u6210\u767d\u75f4\uff0c\u5931 \u6389\u8bb0\u5fc6\u3002\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u8fd9\u4e2a\u529e\u6cd5\u4e0d\u9519\uff0c\u5c31\u8fd9\u6837\u5904\u7f6e\u5427\uff01\u201d \u3000\u3000\u73ed\u5b9a\u5c71\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u6b63\u8981\u6c42\u9976\uff0c\u7ebd\u795c\u7984\u5df1\u662f\u4e00\u638c\u6253\u5728\u4ed6\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u8ddf\u7740\u4e00\u811a\u5c06\u4ed6 \u8e22\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u201c\u4ed6\u8981\u6655\u8fc7\u53bb\u5927\u7ea6\u5341\u4e8c\u4e2a\u65f6\u8fb0\u65b9\u80fd\u9192\u8f6c\uff0c\u8981\u662f\u6ca1\u78b0\u4e0a\u91ce\u517d\u5c06\u4ed6\u541e\u98df\u7684\u8bdd\uff0c\u4ed6\u5012\u662f\u8fd8\u53ef\u4ee5 \u6d3b\u547d\u7684\u3002\u662f\u6b7b\u662f\u751f\uff0c\u8981\u5c31\u770b\u4ed6\u7684\u9020\u5316\u4e86\u3002\u201d\u7ebd\u795c\u7984\u9053\u3002 \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u4ed6\u7684\u6b7b\u6d3b\u6211\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u90e1\u4e3b\u7b54\u5e94\u8fc7\u9976\u4ed6\u4e00\u547d\uff0c\u6211\u624d\u8ba9\u4f60\u8fd9\u6837 \u5904\u7f6e\u4ed6\u7684\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u597d\u50cf\u8fd8\u6709\u70b9\u4e0d\u5927\u653e\u5fc3\u4f3c\u7684\uff0c\u95ee\u9053\uff1a\u201c\u4f46\u4f60\u6562\u62c5\u4fdd\u8fd9\u6837\u5904\u7f6e\u7edd\u5bf9\u6709\u6548 \u5417\uff1f\u201d\u7ebd\u795c\u7984\u9053\uff1a\u201c\u7980\u738b\u7237\uff0c\u6211\u8fd9\u4e00\u638c\u5df2\u7ecf\u9707\u65ad\u4e86\u4ed6\u7684\u5fc3\u8109\uff01\u201d\u5c0f\u738b\u7237\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u6211 \u5c31\u653e\u5fc3\u4e86\u3002\u4f60\u7684\u6b66\u529f\u867d\u4e0d\u53ca\u6211\uff0c\u4f46\u73ed\u5b9a\u5c71\u7684\u6b66\u529f\u66f4\u662f\u8fdc\u8fdc\u4e0d\u53ca\u6606\u5e03\u7985\u5e08\uff0c\u4ed6\u7ed9\u4f60\u5288\u65ad\u5fc3\u8109\uff0c \u5373\u4f7f\u80fd\u591a\u6d3b\u51e0\u5e74\uff0c\u4e5f\u662f\u5e9f\u4eba\u4e00\u4e2a\u4e86\uff0c\u54c8\u54c8\uff01\u54c8\u54c8\uff01\u201d \u3000\u3000\u4faf\u6606\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u5fc3\u91cc\u60f3\u5230\uff1a\u201c\u53ea\u6709\u77e5\u89c9\u7684\u5e9f\u7269\uff0c\u5012\u4e0d\u5982\u6b7b\u4e86\u8fd8\u597d\u3002\u201d\u53ea\u76fc\u5c0f\u738b\u7237\u548c \u8d6b\u8fde\u6e05\u6ce2\u5feb\u5feb\u79bb\u5f00\u3002\u90a3\u77e5\u4ed6\u4eec\u5374\u597d\u50cf\u4e0d\u6025\u4e8e\u79bb\u5f00\uff0c\u8fd8\u662f\u7ad9\u90a3\u91cc\u6162\u6761\u65af\u7406\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u6211\u6b63\u662f\u56e0\u4e3a\u542c\u5f97\u54c8\u5fc5\u56fe\u5728\u5f52\u4e91\u5e84\u88ab\u6740\u4e00\u4e8b\uff0c\u65b9\u59cb\u517c\u7a0b\u8d76\u6765\u7684\u3002\u59b9\u5b50\uff0c\u4f60\u95ef \u7684\u7978\u53ef\u771f\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u6211\u603b\u4e0d\u80fd\u5bf9\u54c8\u5fc5\u56fe\u8bf4\uff0c\u6211\u662f\u5949\u4e86\u7236\u738b\u4e4b\u547d\u6765\u6d1b\u9633\u5356\u89e3\u7684 \u5427\uff01\u201d \u3000\u3000\u5c0f\u738b\u7237\u7b11\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u672c\u662f\u7687\u4e0a\u7684\u5fc3\u8179\u536b\u58eb\uff0c\u53bb\u5e74\u624d\u8c03\u6765\u5378\u6797\u519b\u5f53\u526f\u7edf\u9886\u7684\u3002\u8fd9\u4ef6\u4e8b\u53ea \u6015\u7687\u4e0a\u975e\u5f97\u8d23\u6210\u7239\u7239\u7f09\u51f6\u4e0d\u53ef\u3002\u201d\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u66ff\u6211\u906e\u7792\u906e\u7792\u5427\uff01\u4f60\u4e0d\u8bf4\uff0c\u7236\u738b \u5c31\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u738b\u7237\u7b11\u9053\uff1a\u201d\u4f60\u8981\u6211\u66ff\u4f60\u906e\u7792\uff0c\u53ef\u6709\u4ec0\u4e48\u597d\u5904\u7ed9\u6211\uff1f\u201d\u8d6b\u8fde\u6e05\u6ce2\u5c0f\u5634\u513f\u4e00\u6485\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u628a\u4f60\u5f53\u4f5c\u4eb2\u54e5\u54e5\u4e00\u6837\u4e86\uff0c\u8fd8\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c0f\u738b\u7237\u4e5f\u4e0d\u597d\u610f\u601d\u5728\u4eba\u524d\u6253\u60c5\u9a82\u4fcf\uff0c\u4f46\u4ecd\u662f\u8bed\u5e26\u53cc\u5173\u7684\u8bf4\u9053\uff1a\u201c\u6211\u5012\u4e0d\u662f\u5e0c\u671b\u628a\u6211\u5f53\u6210 \u4eb2\u54e5\u54e5\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u597d\u50cf\u542c\u800c\u4e0d\u95fb\uff0c\u53ea\u662f\u50ac\u4ed6\uff1a\u201c\u4f60\u5230\u5e95\u7b54\u4e0d\u7b54\u5e94\u66ff\u6211\u906e\u7792\uff0c\u4f60\u4e0d\u7b54\u5e94\uff0c\u6211\u5c31\u4e0d\u56de \u53bb\u4e86\u3002\u201d \u3000\u3000\u5c0f\u738b\u7237\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5373\u4f7f\u544a\u8bc9\u7236\u738b\u4e5f\u6ca1\u4e8b\u7684\uff0c\u6211\u62c5\u4fdd\u4ed6\u9a82\u4e5f\u4e0d\u4f1a\u9a82\u4f60\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u4f60\u4e00\u5411\u806a\u660e\uff0c\u600e\u7684\u8fde\u8fd9\u70b9\u4e5f\u60f3\u4e0d\u900f\uff1f\u54c8\u5fc5\u56fe\u662f\u7687\u4e0a\u7684\u5fc3\u8179\uff0c\u53ef\u4e0d\u662f\u7236\u738b\u7684 \u5fc3\u8179\u554a\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u88c5\u4f5c\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u61c2\u4e86\uff0c\u54c8\u5fc5\u56fe\u6765\u4f5c\u5fa1\u6797\u519b\u526f\u7edf\u9886\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f \u7687\u4e0a\u6d3e\u6765\u2014\u2014\u201d \u3000\u3000\u5979\u7684\u201c\u76d1\u89c6\u201d\u4e8c\u5b57\u5c1a\u672a\u51fa\u53e3\uff0c\u5c0f\u738b\u7237\u5fd9\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u61c2\u4e86\u5c31\u597d\uff0c\u522b\u591a\u8bf4\u4e86\u3002\u4f46\u6709\u4e00\u4ef6\u4e8b \u6211\u5374\u662f\u5fc5\u987b\u95ee\u4e2a\u660e\u767d\uff0c\u6253\u6b7b\u54c8\u5fc5\u56fe\u7684\u90a3\u5c0f\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u628a\u6765\u5386\u544a\u8bc9\u6211\u3002\u636e\u6211\u731c\u6d4b\uff0c\u4ed6\u53ef\u80fd\u662f\u8036\u5f8b\u7384\u5143\u7684\u5f1f\u5b50\u3002\u201d\u5c0f\u738b\u7237 \u9053\uff1a\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u53eb\u5f20\u4e09\u3002\u201d \u3000\u3000\u5c0f\u738b\u7237\u9053\uff1a\u201c\u4f60\u7ed9\u4ed6\u9a97\u4e86\uff0c\u5f20\u4e09\u600e\u4f1a\u662f\u4ed6\u7684\u771f\u540d\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u5f20\u4e09\u4e0d\u662f\u771f\u540d\uff0c\u4f46\u6211\u548c\u4ed6\u8fd8\u8c08\u4e0d\u4e0a\u76f8\u8bc6\uff0c\u6211\u53c8 \u600e\u80fd\u95ee\u4ed6\uff1a\u5582\uff0c\u6211\u6000\u7591\u5f20\u4e09\u4e0d\u662f\u4f60\u7684\u771f\u540d\uff0c\u8bf7\u4f60\u5c06\u771f\u540d\u544a\u8bc9\u6211\u597d\u4e0d\u597d\uff1f\u6362\u4e86\u662f\u4f60\u4f60\u4e5f\u4e0d\u4f1a\u5bf9 \u4e00\u4e2a\u7d20\u6627\u5e73\u751f\u7684\u4eba\u9053\u51fa\u771f\u540d\u5b9e\u59d3\u5427\uff1f \u3000\u3000\u201d \u3000\u3000\u5c0f\u738b\u7237\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5012\u662f\u6211\u7684\u80e1\u6d82\u4e86\u3002\u201d\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u60f3\u641c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c0f\u738b\u7237\u76b1\u7709\u9053\uff1a\u201c\u90a3\u53ef\u5f97\u8d39\u591a\u5927\u529f\u592b\uff0c\u4e0d\u5982\u5feb\u70b9\u6740\u4e86\u4ed6\u5427\uff1f\u201d\u8d6b\u8fde\u6e05\u6ce2\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765 \u4e0d\u60f3\u628a\u4e00\u4e2a\u65e0\u8f9c\u7684\u4eba\u7f6e\u4e8e\u6b7b\u5730\uff0c\u4f46\u53c8\u6015\u4ed6\u5077\u542c\u4e86\u54b1\u4eec\u7684\u8c08\u8bdd\uff0c\u4f60\u65e2\u7136\u8fd9\u6837\u8bf4\uff0c\u90a3\u6211\u4e5f\u53ea\u597d\u72e0 \u8d77\u5fc3\u80a0\u4e86\u3002\u8bf4\u7f62\uff0c\u638f\u51fa\u4e00\u9897\u7403\u5f62\u7684\u6697\u5668\uff0c\u53eb\u9053\uff1a\u201d\u5927\u5bb6\u8d76\u5feb\u4e0a\u9a6c\uff01\u201d\u6697\u5668\u4e00\u6454\uff0c\u53ea\u542c\u5f97 \u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u53d1\u51fa\u4e00\u80a1\u6d53\u70df\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u4eec\u65e9\u5df2\u8de8\u4e0a\u9a6c\u80cc\uff0c\u8fce\u7740\u98ce\u5411\uff0c\u907f\u5f00\u70df\u96fe\uff0c\u8dd1\u4e86\u3002\u4faf\u6606\u7a81\u7136\u611f\u89c9\u4e00\u80a1\u5947\u602a\u7684\u9999\u6c14\uff0c \u4ee4\u4ed6\u5934\u6655\u76ee\u7729\u3002\u4ed6\u95ed\u4e86\u547c\u5438\uff0c\u4e00\u65f6\u95f4\u5c1a\u672a\u81f3\u4e8e\u6655\u5012\uff0c\u9690\u9690\u7ea6\u7ea6\u542c\u5f97\u8d6b\u8fde\u6e05\u6ce2\u8bf4\u7684\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u201c\u6211\u7528\u7684\u6bd2\u9999\u5f39\u5927\u6982\u53ef\u4ee5\u7b3c\u7f69\u534a\u4e2a\u5c71\u5934\uff0c\u5185\u529f\u6df1\u539a\u7684\u4e00\u6d41\u9ad8\u624b\u5438\u8fdb\u5c11\u8bb8\u6216\u65e0\u59a8\uff0c\u90a3\u5bb6\u4f19 \u89c1\u6211\u5c31\u8eb2\uff0c\u6599\u60f3\u7edd\u4e0d\u4f1a\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u90a3\u662f\u975e\u6b7b\u4e0d\u53ef\u7684\u4e86\u3002\u201d \u3000\u3000\u4e0d\u77e5\u662f\u8d6b\u8fde\u6e05\u6ce2\u8fd9\u4e00\u884c\u4eba\u8dd1\u8fdc\u4e86\uff0c\u8fd8\u662f\u4faf\u6606\u7684\u7cbe\u795e\u4e1a\u5df2\u4e0d\u652f\uff0c\u4e0b\u9762\u5c0f\u738b\u7237\u8bf4\u7684\u8bdd\u4ed6\u5c31\u542c \u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4faf\u6606\u7684\u786e\u4e0d\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u5185\u529f\u4e5f\u8fd8\u4e0d\u9519\uff0c\u5728\u8fd9\u6027\u547d\u5173\u5934\uff0c\u8fde\u5fd9\u722c\u51fa\u6765\uff0c\u4ed6\u4e5f\u662f\u60f3\u9006\u7740 \u98ce\u5411\u3002\u8d76\u5feb\u79bb\u5f00\u8fd9\u91cd\u70df\u96fe\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u8dd1\u4e86\u51e0\u6b65\uff0c\u773c\u775b\u4e00\u9ed1\uff0c\u5730\u8f6c\u5929\u65cb\uff0c\u767b\u65f6\u5c31\u5012\u4e86\u4e0b\u53bb\uff0c\u4e0d\u7701\u4eba\u4e8b\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4faf\u6606\u5ffd\u7136\u6709\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u597d\u50cf\u6709\u4eba\u628a\u4e00\u9897\u836f\u4e38\u585e\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u90a3\u4e2a\u4eba\u7684\u624b\u638c\u8fd8\u5728\u4ed6\u7684\u80f8\u53e3\u63c9\u6413\u3002\u836f\u4e38\u54bd\u4e86 \u4e0b\u53bb\uff0c\u904d\u4f53\u751f\u51c9\uff0c\u6709\u90a3\u4eba\u63c9\u6413\u4e4b\u540e\uff0c\u66f4\u89c9\u8212\u9002\u65e0\u6bd4\u3002\u4ed6\u4e0d\u77e5\u662f\u68a6\u662f\u771f\uff0c\u773c\u775b\u6162\u6162\u5f20\u5f00\u4e86\u3002 \u3000\u3000\u201c\u4f60\u662f\u8c01\uff1f\u8fd9\u91cc\u662f\u5730\u5e9c\u8fd8\u662f\u4eba\u95f4\uff1f\u201d\u4ed6\u7684\u58f0\u97f3\u7ec6\u5982\u868a\u53eb\uff0c\u4e0d\u8fc7\u90a3\u4eba\u8fd8\u662f\u542c\u89c1\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u6211\u5df2\u7ecf\u66ff\u4f60\u6253\u901a\u7ecf\u8109\uff0c\u4f60\u53ef\u4ee5\u548c\u6211\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4faf\u6606\u91cd\u65b0\u5f20\u5f00\u773c\u775b\uff0c\u5bf9\u4ed6\u7684\u6551\u547d\u6069\u4eba\uff0c\u4ed6\u5df2\u662f\u770b\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002 \u3000\u3000\u51fa\u4e4e\u4ed6\u7684\u610f\u5916\uff0c\u597d\u50cf\u662f\u4e00\u4e2a\u8fd8\u672a\u6ee1\u4e8c\u5341\u5c81\u7684\u5c11\u5e74\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e2a\u5c11\u5e74\u4ed6\u7adf\u662f\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u4ed6\u7741\u5927\u773c\u775b\uff0c\u60ca\u7591\u4e0d\u5b9a\u3002\u7981\u4e0d\u4f4f\u91cd\u590d\u95ee\u9053\uff1a\u201c\u4f60\u3001\u4f60\u662f\u8c01\uff1f\u6211\u597d\u50cf\u5728\u54ea\u91cc\u89c1\u8fc7\u4f60\u4f3c \u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4faf\u5927\u53d4\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u4e48\uff1f\u4f60\u518d\u60f3\u60f3\uff01\u201d \u3000\u3000\u4faf\u6606\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u4f60\u3001\u4f60\u662f\u51b2\u54e5\u513f\uff01\u201d\u8fd9\u662f\u6a80\u7fbd\u51b2\u7684\u5c0f\u540d\uff0c\u4ed6\u548c\u6bcd\u4eb2\u5f80\u5728 \u5546\u5dde\u8282\u5ea6\u884c\u4e2d\u90a3\u51e0\u5e74\u65f6\u5149\uff0c\u5b8c\u989c\u9274\u7684\u536b\u58eb\u90fd\u662f\u53eb\u4ed6\u505a\u201c\u51b2\u54e5\u513f\u201d\u7684\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u5c11\u5e74\u6b63\u662f\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u4e8b\uff0c\u4e0d\u5fc5\u63d0\u4e86\u3002\u4f60\u56e0\u4f55\u6362\u4e86\u88c5\uff0c\u6765\u5230\u8fd9\u513f\uff1f\u662f\u5b8c\u989c\u9274\u6d3e\u4f60\u6765\u7684 \u5417\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u4e0d\u5728\u5b8c\u989c\u5c06\u519b\u90a3\u91cc\u5f53\u73ed\u4e86\u3002\u8bf4\u8d77\u6765\u6211\u6b63\u662f\u56e0\u4e3a\u90a3\u5929\u7684\u4e8b\u5185\u759a\u4e8e\u5fc3\uff0c\u6545 \u6b64\u5728\u4f60\u79bb\u5f00\u8282\u5ea6\u8859\u95e8\u7684\u7b2c\u4e8c\u5929\uff0c\u6211\u4e5f\u5077\u5077\u9003\u8dd1\u4e86\u3002\u201d\u5176\u5b9e\u4ed6\u4e4b\u6240\u4e0d\u6562\u505a\u5b8c\u989c\u9274\u7684\u536b\u58eb\uff0c\u771f\u6b63 \u7684\u539f\u56e0\u4e43\u662f\u56e0\u4e3a\u5bb3\u6015\u8036\u5f8b\u7384\u5143\u518d\u6765\u5bfb\u4ec7\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4e48\u8fd9\u51e0\u5e74\u4f60\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u521a\u624d\u4f60\u662f\u4e2d\u6bd2\u660f\u8ff7\u7684\u5427\uff0c\u8fd9\u53c8\u662f\u600e\u4e48\u56de \u4e8b\uff1f\u201d \u3000\u3000\u4faf\u6606\u5450\u5450\u9053\uff1a\u201c\u8fd9\u3001\u8fd9\u4e2a\u3001\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4faf\u5927\u53d4\uff0c\u4f60\u82e5\u662f\u6709\u4ec0\u4e48\u987e\u5fcc\uff0c\u6211\u4e0d\u52c9\u5f3a\u4f60\u8bf4\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u51b2\u54e5\u513f\uff0c\u4f60\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\uff0c\u6211\u600e\u4e48\u80fd\u4e0d\u548c\u4f60\u8bf4\uff1f\u4e0d\u8fc7\u3001\u4e0d\u8fc7\u8bf4\u6765\u8bdd\u957f\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9274\u8c8c\u8fa8\u8272\uff0c\u5fc3\u77e5\u4ed6\u5b9a\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u6b63\u5728\u5fc3\u4e2d\u76d8\u7b97\uff0c\u8981\u4e0d\u8981\u5bf9\u81ea\u5df1\u5c3d\u5410\u5b9e\u8a00\u3002\u6a80\u7fbd \u51b2\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u8981\u662f\u8bf4\u6765\u8bdd\u957f\uff0c\u90a3\u5c31\u6162\u6162\u518d\u8bf4\u5427\u3002\u6211\u60f3\u5148\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u95ee\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u5e74\u7eaa\u548c\u6211\u5dee\u4e0d\u591a\u7684\u5973\u5b50\u3002\u201d\u5f53\u4e0b\u628a\u8d6b\u8fde\u6e05\u6ce2\u7684\u5bb9\u8c8c\u7528\u8a00\u8bed\u63cf\u7ed8\u51fa \u6765\u3002\u201d \u3000\u3000\u4faf\u6606\u8fdf\u7591\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u51b2\u54e5\u513f\uff0c\u8bf7\u6055\u6211\u7684\u5192\u6627\uff0c\u6709\u4e00\u53e5\u8bdd\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u95ee\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u95ee\u597d\u4e86\u3002\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u8bf7\u95ee\u4f60\u548c\u90a3\u4f4d\u59d1\u5a18\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u5979\u662f\u4f60\u7684\u597d\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u95ee\u9053\uff1a\u201c\u6211\u548c\u5979\u4e0d\u8fc7\u662f\u4e09\u5929\u524d\u624d\u76f8\u8bc6\u7684\uff0c\u6050\u6015\u8fd8\u8bf4\u4e0d\u4e0a\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u6055\u6211\u591a\u95ee\uff0c\u4f60\u662f\u600e\u4e48\u548c\u8fd9\u4f4d\u59d1\u5a18\u76f8\u8bc6\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u4e5f\u662f\u4e00\u6b21\u5947\u9047\uff0c\u6211\u6709\u4e2a\u4ec7\u4eba\u548c\u5979\u4e3a\u96be\uff0c\u6070\u5de7\u7ed9\u6211\u78b0\u4e0a\u3002\u6211\u66fe\u548c\u5979\u8054 \u624b\u5bf9\u654c\u3002\u201d \u3000\u3000\u4faf\u6606\u5446\u4e86\u4e00\u5446\u3002\u5931\u58f0\u53eb\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u83ab\u540d\u5176\u5999\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u662f\u54ea\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u5728\u5f52\u4e91\u5e84\u91cc\u6253\u6b7b\u54c8\u5fc5\u56fe\u7684\u90a3\u4e2a\u5e74\u8f7b\u4eba\uff0c\u51b2\u54e5\u513f\uff0c\u8bf7\u4f60\u4e0d\u8981\u7792\u6211\uff0c\u662f\u4f60\u624b\u7684 \u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6d88\u606f\u5012\u662f\u7075\u901a\u5f97\u5f88\u3002\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u662f\u6211\u5e72\u7684\u3002 \u3000\u3000\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u66fe\u7ecf\u548c\u4f60\u8054\u624b\u5bf9\u654c\u7684\u90a3\u4f4d\u59d1\u5a18\uff0c\u662f\u4e0d\u662f\u590d\u59d3\u5b8c\u989c\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u95ee\u6765\u5f97\u66f4\u5176\u7a81\u5140\uff0c\u6a80\u7fbd\u51b2\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u590d\u59d3\u5012\u662f\u590d\u59d3\uff0c\u4e0d\u8fc7\u5979\u4e0d\u662f\u590d\u59d3\u5b8c \u989c\uff0c\u800c\u662f\u590d\u59d3\u8d6b\u8fde\u3002\u4faf\u5927\u53d4\uff0c\u4f60\u56e0\u4f55\u8fd9\u6837\u95ee\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u51b2\u54e5\u513f\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u548c\u8fd9\u4f4d\u59d1\u5a18\u4ea4\u60c5\u6df1\u6d45\uff0c\u4f46\u8bf7\u4f60\u52a1\u5fc5\u76f8\u4fe1\u6211\u7684\u8bdd\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u8bf4\u5462\uff0c\u600e\u77e5\u9053\u6211\u4e0d\u80fd\u76f8\u4fe1\u4f60\u3002\u201d\u4faf\u6606\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8bf4\u51fa\u6765\u7684\u4e8b \u60c5\uff0c\u53ef\u80fd\u662f\u5b8c\u5168\u51fa\u4e4e\u4f60\u7684\u610f\u6599\u4e4b\u5916\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u867d\u5c0f\uff0c\u78b0\u5230\u79bb\u5947\u53e4\u602a\u7684\u4e8b\u60c5\u5374\u4e0d\u7b97\u5c11\u3002\u4f60\u8bf4\u5427\uff0c\u6211\u76f8\u4fe1\u4f60\u3002\u201d \u3000\u3000\u4faf\u6606\u8bf4\u4e86\u521a\u624d\u6240\u89c1\u6240\u95fb\uff0c\u6a80\u7fbd\u51b2\u867d\u7136\u6709\u70b9\u5947\u602a\uff0c\u77e5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u6709\u4e00\u4e2a\u6b66\u529f\u8fd9\u4e48\u9ad8\u5f3a \u7684\u54e5\u54e5\uff0c\u6211\u8fd8\u672a\u77e5\u9053\u5462\u3001\u4e0d\u8fc7\uff0c\u6b64\u4e8b\u867d\u5c5e\u5de7\u9047\uff0c\u4f46\u54e5\u54e5\u5e2e\u59b9\u59b9\u9000\u654c\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5947\u602a\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5979\u7684\u54e5\u54e5\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u6014\u9053\uff1a\u201c\u54e5\u54e5\u5c31\u662f\u54e5\u54e5\uff0c\u8fd8\u80fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u4faf\u6606\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u4eb2\u5144\u59b9\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fae\u6709\u9178\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4e49\u5144\u59b9\u4e5f\u6ca1\u4ec0\u4e48\u5947\u602a\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4ed6\u8fd9\u4e49\u5144\u590d\u6027\u5b8c\u989c\u3002\u53cc\u540d\u5b9a\u56fd\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5b8c\u989c\u5b9a\u56fd\uff1f\u201d\u7ec6\u60f3\u5e08\u7236\u548c\u4ed6\u8bf4\u8fc7\u4e00\u4e9b\u6b66\u529f\u540e\u8d77\u4e4b\u79c0\u7684\u540d\u5b57\uff0c\u5374\u4f3c\u4e4e\u6ca1\u6709\u8fd9 \u4e2a\u5b8c\u989c\u5b9a\u56fd\u3002 \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u5b8c\u989c\u5b9a\u56fd\u8fd9\u4e2a\u540d\u5b57\u6216\u8bb8\u4f60\u6ca1\u542c\u4eba\u8bf4\u8fc7\uff0c\u4f46\u4ed6\u7684\u7236\u4eb2\u4f60\u4e00\u5b9a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5b8c\u989c\u5b9a\u56fd\u7684\u7236\u4eb2\u662f\u8c01\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4ed6\u7684\u7236\u4eb2\u5c31\u662f\u5927\u91d1\u56fd\u7684\u662f\u53d4\uff0c\u5b98\u5c01\u5175\u9a6c\u5927\u5143\u5e05\u517c\u5f7b\u6797\u519b\u7edf\u9886\u7684\u6d4e\u4eb2\u738b\u5b8c\u989c\u957f \u4e4b\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8d6b\u8fde\u6e05\u6ce2\u59d1\u5a18\u8fd9\u4e49\u5144\u7684\u8eab\u4efd\u7adf\u662f\u5c0f\u738b\u7237 \u4e86\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u4ed6\u662f\u5982\u5047\u5305\u6362\u7684\u5c0f\u738b\u7237\u3002\u4f60\u662f\u77e5\u9053\u7684\uff0c\u6211\u7684\u65e7\u4e3b\u4eba\u5b8c\u989c\u9274\u5c06\u519b\u662f \u5b8c\u989c\u738b\u7237\u7684\u4f84\u513f\uff0c\u6211\u66fe\u7ecf\u4ee5\u5b8c\u989c\u5c06\u519b\u536b\u58eb\u7684\u8eab\u4efd\uff0c\u5230\u8fc7\u738b\u5e9c\uff0c\u8fd9\u4f4d\u5c0f\u738b\u7237\uff0c\u6211\u662f\u66fe\u7ecf\u89c1\u8fc7\u4e0d \u53ea\u4e00\u6b21\u7684\u3002\u7edd\u4e0d\u4f1a\u8ba4\u9519\u4eba\u7684\u3002\u201d \u3000\u3000\u4faf\u6606\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u53ef\u4ee5\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u5f53\u4eca\u7687\u4e0a\u6700\u987e\u5fcc\u7684\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u4ee4\u7956\u6a80 \u8001\u8d1d\u52d2\uff0c\u4e00\u4e2a\u662f\u4ee4\u5e08\u8036\u5f8b\u738b\u5b50\u3002\u5b8c\u989c\u738b\u7237\u5c31\u6b63\u662f\u5949\u4e86\u7687\u4e0a\u7684\u5bc6\u4ee4\u8981\u6349\u62ff\u8fd9\u4e24\u4e2a\u7591\u72af\u7684\u4eba\uff0c\u800c \u4f60\u548c\u8fd9\u4e24\u4eba\u7591\u72af\u90fd\u6709\u5bc6\u5207\u7684\u5173\u7cfb\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u4e5f\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u4e0d\u6539\u53d8\u4e3b\u610f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c\u6211\u53ef\u8fd8\u662f\u767e\u601d\u83ab\u5f97\u5176\u89e3\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u54ea\u4e00\u4ef6\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8d6b\u8fde\u6e05\u6ce2\u56e0\u4f55\u4e0e\u6211\u8054\u624b\u5bf9\u4ed8\u54c8\u5fc5\u56fe\uff1f\u800c\u4e14\u5728\u6b64\u4e4b\u524d\uff0c\u5979\u5df2\u7ecf\u5927\u95f9\u5f52\u4e91\u5e84 \u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u60f3\u8981\u548c\u5979\u7ed3\u4ea4\uff0c\u53ea\u662f\u60f3\u628a\u4e8b\u60c5\u5f04\u4e2a\u6e05\u695a\uff0c\u5979\u6562\u548c\u6211\u8054\u624b\u54c8\u5fc5\u56fe\uff0c \u6b64\u4e8b\u4f60\u53c8\u5982\u4f55\u770b\u6cd5\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u54c8\u5fc5\u56fe\u548c\u5b8c\u989c\u738b\u7237\u672c\u6765\u662f\u9762\u548c\u5fc3\u4e0d\u548c\u7684\u3002\u201d\u5f53\u4e0b\u628a\u4ed6\u5077\u542c\u5230\u7684\u201c\u5c0f\u738b\u7237\u201d\u90a3 \u756a\u8bdd\u5bf9\u6a80\u7fbd\u51b2\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u8fc7\u5728\u8d6b\u8fde\u59d1\u5a18\u672a\u89c1\u5230\u5c0f\u738b\u7237\u4e4b\u524d\uff0c\u5979\u662f\u5c1a\u672a\u77e5\u9053\u5b8c\u989c\u957f\u4e4b\u6709\u8fd9\u731c\u7591\u7684 \u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5979\u6562\u4e8e\u5e2e\u6211\u6740\u54c8\u5fc5\u56fe\uff0c\u8fd9\u5c31\u66f4\u52a0\u96be\u80fd\u53ef\u8d35\u4e86\u3002\u201d \u3000\u3000\u4faf\u6606\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u77e5\u9053\u8d6b\u8fde\u59d1\u5a18\u662f\u4f60\u5fc3\u76ee\u4e2d\u7684\u597d\u4eba\u3002\u6211\u8bf4\u4ec0\u4e48\u4f60\u90fd\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4f46\u6211\u53ea \u662f\u4e3a\u4e86\u62a5\u7b54\u4f60\u7684\u6551\u547d\u4e4b\u6069\uff1b\u6211\u53ea\u662f\u3001\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u6211\u7684\u597d\u3002\u6211\u4e5f\u5e76\u4e0d\u662f\u4e0d\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u4e0d\u8fc7\u8fd9\u4e9b\u4e8b\u60c5\u90fd\u662f\u5927 \u51fa\u60c5\u7406\u4e4b\u5916\uff0c\u6211\u96be\u514d\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4e0d\u4f46\u4f60\u89c9\u5f97\u5947\u602a\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u6211\u4e5f\u662f\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u8a00\u8bed\u4e4b\u95f4\u3002\u4e0d\u77e5\u4e0d\u89c9\u773c\u775b\u4e2d\u6d41\u9732\u51fa\u6050\u60e7\u7684\u795e\u8272\uff0c\u597d\u50cf\u5fc3\u4e2d\u8fd8\u6709\u4f59\u60b8\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u5c0f\u738b\u7237\u7a81\u7136\u5bf9\u6606\u5e03\u7985\u5e08\u4e0b\u6bd2\u624b\u7684\u4e8b\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u662f\u5440\uff01\u4ed6\u90a3\u624b\u6bb5\u7684\u72e0\u8fa3\uff0c\u771f\u662f\u4ee4\u6211\u6bdb\u9aa8\u609a\u7136\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u6709\u4eb2\u773c\u770b\u89c1\u4ed6\u7684\u6b7b\u4ea1\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u90a3\u65f6\u6211\u8eb2\u5728\u4e71\u77f3\u4e1b\u4e2d\uff0c\u8fde\u5927\u6c14\u90fd\u4e0d\u6562\u900f\uff0c\u600e\u6562\u5077\u770b\uff1f\u4e0d\u8fc7\u636e\u5c0f\u738b\u7237\u8bf4\uff0c\u6606\u5e03 \u7985\u5e08\u662f\u7ed9\u4ed6\u70b9\u4e2d\u6b7b\u7a74\u7684\uff0c\u6211\u4e5f\u4eb2\u8033\u542c\u89c1\u4e86\u4ed6\u7684\u5c38\u4f53\u88ab\u8e22\u5f97\u6eda\u4e0b\u5c71\u5761\u53bb\u7684\u58f0\u97f3\uff0c\u5bf9\u5566\uff01\u4ed6\u7684\u5c38 \u4f53\u6599\u60f3\u5c31\u5728\u9644\u8fd1\uff0c\u4e0d\u4f1a\u6eda\u5f97\u592a\u8fdc\u7684\u3002 \u3000\u3000\u54b1\u4eec\u53bb\u627e\u5bfb\u4ed6\u7684\u5c38\u4f53\uff0c\u4e0d\u5c31\u53ef\u4ee5\u8bc1\u5b9e\u4e86\uff1f\u201d \u3000\u3000\u4e0d\u6599\u4ed6\u5e26\u6a80\u7fbd\u51b2\u53bb\u627e\u6606\u5e03\u7985\u5e08\u7684\u5c38\u4f53\uff0c\u8d70\u5230\u4e86\u5c71\u4e0b\uff0c\u8fd8\u6ca1\u53d1\u73b0\u3002 \u3000\u3000\u4faf\u6606\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u662f\u6211\u542c\u9519\u4e86\u58f0\u97f3\u7684\u65b9\u5411\uff1f\u201d\u8fd9\u5ea7\u5c71\u867d\u7136\u5e76\u4e0d\u662f\u5f88\u9ad8\u5927\uff0c\u4f46 \u82e5\u8981\u904d\u641c\u56db\u65b9\u3002\u4e5f\u4e0d\u662f\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u64c5\u7fbd\u51b2\u9053\uff1a\u201c\u7b97\u4e86\u5427\u3002\u5373\u4fbf\u627e\u5230\u4e86\u5c38\u4f53\uff0c\u6b7b\u4eba\u4e5f\u4e0d\u4f1a\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u4faf\u6606\u5ffd\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u534a\u6b7b\u534a\u6d3b\u7684\u4eba\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u73ed\u5b9a\u5c71\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7ed9\u5c0f\u738b\u7237\u7684\u536b\u58eb\u4e00\u638c\u9707\u65ad\u5fc3\u8109\uff0c\u636e\u8bf4\u7eb5\u7136\u4e0d\u6b7b\uff0c\u4e5f\u8981\u53d8\u6210\u767d\u75f4\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u548c\u6b7b\u4eba\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u4ed6\u53d8\u6210\u767d\u75f4\uff0c\u8bb0\u5fc6\u4e00\u5b9a\u5df2\u7ecf\u6d88\u5931\u3002\u867d\u662f\u2018\u6d3b \u53e3\u2019\uff0c\u4e5f\u95ee\u4e0d\u51fa\u4ec0\u4e48\u7684\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u51b2\u54e5\u513f\uff0c\u4ee4\u5e08\u6b66\u529f\u7edd\u4e16\uff0c\u4f60\u5df2\u5f97\u4e86\u4ee4\u5e08\u8863\u94b5\u771f\u4f20\uff0c\u4e0d\u77e5\u53ef\u5426\u7528\u4e0a\u4e58\u5185\u529f\uff0c\u4e3a \u4ed6\u5316\u5f00\u963b\u585e\u5fc3\u8109\u7684\u7600\u8840\u3002\u201d \u3000\u3000\u8fd9\u6837\uff0c\u7eb5\u7136\u4e0d\u80fd\u4ee4\u4ed6\u6062\u590d\u5982\u521d\uff0c\u4e5f\u53ef\u4ee4\u4ed6\u6062\u590d\u6e05\u9192\uff0c\u6709\u5982\u5e38\u4eba\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6c89\u541f\u7247\u523b\u3002\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5185\u529f\u8fd8\u672a\u7ec3\u5230\u8fd9\u6837\u9ad8\u7684\u5883\u754c\uff0c\u59d1\u4e14\u4e00\u8bd5\u5427\u3002\u201d \u3000\u3000\u4faf\u6606\u8d70\u5230\u524d\u5934\u9886\u8def\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u505c\u4e0b\uff0c\u8138\u4e0a\u663e\u51fa\u8e0c\u8e87\u83ab\u6289\u7684\u795e\u6c14\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4faf\u5927\u53d4\u3002\u4f60\u53ef\u662f\u6709\u751a\u4e3a\u96be\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u636e\u90a3\u536b\u58eb\u8bf4\uff0c\u73ed\u5b9a\u5c71\u8981\u5728\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u540e\u65b9\u59cb\u9192\u6765\uff0c\u4fbf\u5374\u4e0d\u77e5\u662f\u5426\u4e00\u5982\u4ed6\u7684 \u6240\u6599\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u6015\u4ed6\u73b0\u5df2\u7ecf\u9192\u6765\uff0c\u5047\u5982\u4e0d\u662f\u90a3\u536b\u58eb\u6240\u6599\u4e1a\u5df2\u53d8\u6210\u767d\u75f4 \u7684\u8bdd\uff0c\u5c31\u4f1a\u8ba4\u51fa\u4e86\u4f60\u3002\u201d \u3000\u3000\u5019\u6606\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u4ed6\u867d\u7136\u8f83\u597d\uff0c\u4f46\u4e5f\u4e0d\u60f3\u7ed9\u4ed6\u77e5\u9053\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5f52\u4e91\u5e84\u8bf4\u4e0d\u5b9a\u4e5f\u8fd8\u4f1a\u6709\u4eba\u6765\u7684\u3002\u4faf\u5927\u53d4\uff0c\u4f60\u5df2\u7ecf\u5e2e\u4e86\u6211\u4e0d\u5c11\u5fd9\u4e86\uff0c\u4f60\u5148\u8d70 \u5427\u3002\u201d \u3000\u3000\u4faf\u6606\u9053\uff1a\u201c\u4ed6\u662f\u4ece\u8fd9\u8fb9\u6eda\u4e0b\u53bb\u7684\uff0c\u6211\u60f3\u6211\u4e0d\u4f1a\u8bb0\u9519\u3002\u51b2\u54e5\u513f\uff0c\u591a\u8c22\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211 \u6b64\u53bb\u5c06\u9690\u59d3\u57cb\u540d\u8fc7\u8fd9\u4e0b\u534a\u751f\u4e86\u3002\u6551\u547d\u4e4b\u6069\uff0c\u65e0\u4ee5\u4e3a\u62a5\uff0c\u53ea\u76fc\u4f60\u80fd\u591f\u542c\u6211\u5fe0\u8a00\u4e00\u53e5\uff0c\u6700\u597d\u522b\u53bb \u4eac\u5e08\uff0c\u5047\u5982\u4e00\u5b9a\u8981\u53bb\u7684\u8bdd\uff0c\u4e5f\u5207\u83ab\u6cbe\u610f\u90a3\u4f4d\u8d6b\u8fde\u59d1\u5a18\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u6211\u4f1a\u628a\u4f60\u7684\u8bdd\u65f6\u523b\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u4faf\u6606\u8d70\u540e\uff0c\u6a80\u7fbd\u51b2\u65bd\u5c55\u8f7b\u529f\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u641c\u904d\u4e86\u5c71\u811a\u65b9\u5706\u6570\u91cc\u4e4b\u5730\uff0c\u5374\u6ca1\u89c1\u7740\u73ed\u5b9a \u5c71\u3002\u201c\u4faf\u5927\u53d4\u8be5\u4e0d\u4f1a\u9a97\u6211\u5427\uff1f\u201d \u3000\u3000\u6309\u8bf4\u5fc3\u8109\u88ab\u9707\u65ad\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u80fd\u5728\u51e0\u4e2a\u65f6\u8fb0\u4e4b\u5185\u81ea\u5df1\u884c\u8d70\u7684\u3002\u4ed6\u5bf9\u4faf\u6606\u7684\u8bdd\u4e0d\u89c9\u534a\u4fe1\u534a \u7591\u4e86\u3002 \u3000\u3000\u201c\u6211\u7684\u59b9\u59b9\u5728\u5b8c\u989c\u592b\u4eba\u90a3\u91cc\uff0c\u5373\u4f7f\u4e0d\u662f\u4e3a\u4e86\u67e5\u7a76\u6e05\u6ce2\u7684\u6765\u5386\uff0c\u6211\u4e5f\u5e94\u8be5\u628a\u59b9\u59b9\u5bfb\u627e\u56de \u6765\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u610f\u5df2\u51b3\uff0c\u4e0d\u7406\u4faf\u6606\u4e34\u522b\u65f6\u7684\u8b66\u544a\uff0c\u7ec8\u4e8e\u7ee7\u7eed\u884c\u7a0b\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c \u5341 \u56de\u3000\u5ba2\u9014\u5947\u9047 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c \u5341 \u56de\u3000\u5ba2\u9014\u5947\u9047 \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u5750\u9a91\u5df2\u7ecf\u4e2d\u6bd2\u5012\u6bd9\uff0c\u53ea\u80fd\u6b65\u884c\u3002\u9752\u5929\u767d\u65e5\uff0c\u8def\u4e0a\u4e0d\u80fd\u65bd\u5c55\u8f7b\u529f\uff0c\u6bcf\u5929\u4e0d\u8fc7\u8d70\u4e00 \u767e\u591a\u91cc\uff0c\u8d70\u4e86\u4e09\u5929\uff0c\u65b9\u59cb\u6765\u5230\u6cb3\u5357\u4e0e\u76f4\u96b6\uff08\u4eca\u6cb3\u5317\u7701\uff09 \u3000\u3000\u4ea4\u754c\u7684\u5b89\u9633\u3002\u5b89\u9633\u662f\u4e2a\u6bd4\u8f83\u5927\u7684\u57ce\u5e02\uff0c\u57ce\u4e2d\u6709\u4e2a\u9aa1\u9a6c\u5e02\u573a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6025\u4e8e\u8d76\u8def\uff0c\u8d81\u5929\u8272\u672b\u665a\uff0c\u4fbf\u5230\u9aa1\u9a6c\u5e02\u573a\u53bb\u6311\u4e00\u5339\u5750\u9a91\u3002 \u3000\u3000\u4ed6\u662f\u66fe\u7ecf\u5728\u5546\u5dde\u8282\u5ea6\u4f7f\u7684\u5b98\u8859\u4f4f\u8fc7\u4e09\u5e74\uff0c\u5546\u5dde\u8282\u5ea6\u4f7f\u5b8c\u989c\u9274\u559c\u6b22\u540d\u9a79\uff0c\u4ed6\u89c1\u8fc7\u7684\u5404\u5730\u7684 \u826f\u9a6c\u53ef\u771f\u4e0d\u5c11\uff0c\u4e5f\u591a\u5c11\u61c2\u5f97\u4e00\u70b9\u9a6c\u76f8\u4e4b\u672f\u3002 \u3000\u3000\u4ed6\u5728\u9aa1\u9a6c\u5e02\u573a\u770b\u4e86\u8bb8\u591a\u9a6c\u5339\u90fd\u4e0d\u6ee1\u610f\uff0c\u5ffd\u5730\u773c\u775b\u4e00\u4eae\uff0c\u4e00\u5339\u706b\u7ea2\u7684\u9a8f\u9a6c\u6620\u5165\u4ed6\u7684\u773c\u5e18\u3002 \u3000\u3000\u5728\u9aa1\u9a6c\u5e02\u573a\uff0c\u6709\u4e13\u95e8\u6599\u7406\u9a6c\u5339\u7684\u5e97\u94fa\uff0c\u94fa\u4e2d\u6709\u517d\u533b\uff0c\u6709\u4eba\u7ed9\u9a6c\u5339\u6d17\u6da4\uff0c\u8fd8\u6709\u9972\u6599\u4f9b\u5e94\u3002 \u6709\u4e9b\u5e97\u94fa\u517c\u5356\u9a91\u9a6c\u6240\u7528\u7684\u7528\u5177\u3002 \u3000\u3000\u8fd9\u5339\u9a6c\u6b63\u5728\u8fd9\u6837\u4e00\u95f4\u201c\u9a6c\u5177\u5e97\u201d\u7684\u95e8\u524d\u9971\u9910\uff0c\u5403\u7684\u662f\u9ec4\u8c46\uff0c\u7a3b\u58f3\u548c\u5ae9\u8349\u6df7\u5408\u7684\u4e0a\u597d\u9972 \u6599\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4ed4\u7ec6\u6253\u91cf\u8fd9\u5339\u9a8f\u9a6c\uff0c\u53ea\u89c1\u5b83\u6d51\u8eab\u662f\u80ed\u8102\u8272\uff0c\u53ea\u6709\u5934\u9879\u4e0a\u4e00\u5757\u7389\u767d\u8272\u3002\u6a80\u7fbd\u51b2\u4e00\u89c1 \u5c31\u77e5\u662f\u5927\u5b9b\u7684\u540d\u79cd\u826f\u9a79\uff0c\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u7389\u9879\u8d64\u201d\u7684\u3002 \u3000\u3000\u4ed6\u7981\u4e0d\u4f4f\u5567\u5567\u79f0\u8d4f\uff0c\u95ee\u65c1\u8fb9\u4e00\u4e2a\u9aa1\u9a6c\u8d29\u5b50\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u662f\u5356\u7684\u5417\uff1f\u4e0d\u77e5\u591a\u5c11\u4ef7\u94b1\uff1f\u201d \u3000\u3000\u4ed6\u6b65\u884c\u4e24\u5929\uff0c\u672c\u6765\u662f\u534a\u65b0\u534a\u65e7\u7684\u8863\u88f3\uff0c\u5df2\u7ecf\u6cbe\u6ee1\u5c18\u571f\uff0c\u90a3\u9aa1\u9a6c\u8d29\u5b50\u5148\u770b\u7f57\u8863\u540e\u770b\u4eba\uff0c\u54fc \u4e86\u4e00\u58f0\uff0c\u5e26\u7740\u8f7b\u8511\u7684\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u5927\u7684\u53e3\u6c14\uff0c\u8981\u4e70\u8fd9\u5339\u540d\u9a79\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u662f\u65e0\u4ef7 \u5b9d\u5417\uff1f\u201d\u9aa1\u9a6c\u8d29\u5b50\u9053\uff1a\u201c\u6709\u4ef7\u65e0\u4ef7\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u8fd9\u5339\u9a6c\u662f\u90a3\u4f4d\u516c\u5b50\u9a91\u6765\u7684\uff0c\u4f60\u4e0d\u770b\u89c1\u5417\uff1f \u4ed6\u6b63\u5728\u4e3a\u8fd9\u5339\u9a6c\u914d\u4e00\u526f\u8f94\u5934\u5462\u3002\u4f60\u53bb\u95ee\u95ee\u4ed6\uff0c\u80af\u4e0d\u80af\u5356\u7ed9\u4f60\u5427\uff01\u201d\u6a80\u7fbd\u51b2\u7684\u6ce8\u610f\u529b\u521a\u624d\u5168\u90e8 \u96c6\u4e2d\u5728\u90a3\u5339\u201c\u7389\u9879\u8d64\u201d\u4e0a\uff0c\u6b64\u65f6\u65b9\u59cb\u53d1\u73b0\u9a6c\u5177\u5e97\u4e2d\u90a3\u4e2a\u5c11\u5e74\u3002\u90a3\u5c11\u5e74\u8863\u670d\u534e\u4e3d\u3002\u6b63\u5728\u5e97\u4e3b\u624b \u4e2d\u63a5\u8fc7\u8f94\u5934\u3002\u9aa1\u9a6c\u8d29\u5b50\u548c\u6a80\u7fbd\u51b2\u7684\u5bf9\u8bdd\uff0c\u5e97\u4e3b\u548c\u90a3\u5c11\u5e74\u90fd\u542c\u89c1\u4e86\u3002\u5e97\u4e3b\u4ea4\u4e86\u8f94\u4e0e\u90a3\u5c11\u5e74\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u526f\u8f94\u5934\uff0c\u603b\u5171\u662f\u516b\u5341\u4e24\u94f6\u5b50\u3002\u563f\uff0c\u5728\u6211\u4eec\u8fd9\u4e2a\u5c0f\u5730\u65b9\uff0c\u516b\u5341\u4e24\u94f6\u5b50\u53ef\u4ee5\u4e70\u5341\u5339\u5065\u9a6c \u4e86\u3002\u60f3\u4e0d\u5230\u5c45\u7136\u6709\u60f3\u4e70\u4f60\u8fd9\u5339\u5750\u9a91\u3002\u201d\u5f26\u5916\u4e4b\u97f3\uff0c\u81ea\u662f\u5632\u7b11\u6a80\u7fbd\u51b2\u8fd9\u7a77\u5c0f\u5b50\u201c\u75f4\u86e4\u87c6\u60f3\u5403\u5929 \u9e45\u8089\u201d\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9762\u7ea2\u8033\u8d64\uff0c\u6b63\u8981\u8d70\u5f00\u3002\u90a3\u5c11\u5e74\u5df2\u7ecf\u56de\u8fc7\u5934\u6765\uff0c\u4ed6\u4e5f\u60f3\u770b\u770b\u8fd9\u4e2a\u60f3\u4e70\u4ed6\u7684\u5750\u9a91\u7684\u662f \u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u63a5\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u6a80\u7fbd\u51b2\u4e0d\u7531\u5f97\u4e00\u5446\uff0c\u51e0\u4e4e\u5c16\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5c11\u5e74\u7684\u9762\u8c8c\uff0c\u7adf\u662f\u5229\u8d6b\u8fde\u6e05\u6ce2\u5341\u5206\u76f8\u4f3c\u3002 \u3000\u3000\u4ed6\u867d\u6ca1\u53eb\u51fa\u58f0\u6765\uff0c\u4f46\u53cc\u811a\u5df1\u662f\u4e0d\u7531\u81ea\u4e3b\u5411\u90a3\u5c11\u5e74\u8d70\u53bb\u3002\u4ed6\u7684\u4e00\u53cc\u773c\u775b\uff0c\u4e5f\u662f\u7262\u7262\u7684\u76ef\u7740 \u90a3\u5c11\u5e74\u770b\u3002 \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u662f\u6e05\u6ce2\u5973\u626e\u7537\u88c5\u5462\uff1f\u201d\u4f46\u90a3\u5c11\u5e74\u5374\u5e76\u6ca1\u6709\u5bf9\u4ed6\u4f7f\u51fa\u6697\u793a\u4ec0\u4e48\u7684\u773c\u8272\uff0c\u5047\u5982\u4ed6\u662f \u8d6b\u8fde\u6e05\u6ce2\uff0c\u6309\u8bf4\u4ed6\u662f\u5e94\u8be5\u6709\u6240\u6697\u793a\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5f85\u4ed6\u8d70\u8fd1\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u5f88\u559c\u6b22\u6211\u8fd9\u5750\u9a91\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u542c\u4ed6\u8bf4\u8bdd\uff0c\u5c31\u77e5\u9053\u4ed6\u4e0d\u662f\u8d6b\u8fde\u6e05\u6ce2\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u8bf4\u7684\u662f\u4e00\u53e3\u5b57\u6b63\u8154\u5706\u7684\u201c\u4eac\u7247\u5b50\u201d\u2014\u2014\u5317\u4eac\u5b98\u8bdd\uff0c\u8fd9\u4e2a\u5c11\u5e74\u8bf4\u7684\u5374\u4e0d\u77e5\u662f\u54ea\u4e2a \u5730\u65b9\u7684\u65b9\u8a00\uff0c\u4e0d\u8fc7\u4e5f\u662f\u751a\u4e3a\u6e05\u8106\u60a6\u8033\uff0c\u4f3c\u4e4e\u8fd8\u5e26\u7740\u4e00\u70b9\u91cd\u97f3\u3002\u5e74\u7eaa\u548c\u8d6b\u8fde\u6e05\u6ce2\u4e5f\u662f\u4e0d\u76f8\u4e0a \u4e0b\u3002 \u3000\u3000\u4ed4\u7ec6\u6253\u91cf\u4e4b\u4e0b\uff0c\u4ed6\u53c8\u53d1\u73b0\u8fd9\u5c11\u5e74\u7684\u7709\u5fc3\u6709\u9897\u75d4\uff0c\u4ed6\u7684\u8138\u4e0a\u4e5f\u6ca1\u6709\u8d6b\u8fde\u6e05\u6ce2\u90a3\u79cd\u7279\u6709\u7684 \u201c\u59a9\u5a9a\u201d\uff08\u8d6b\u8fde\u6e05\u6ce2\u5916\u53f7\u7389\u9762\u5996\u72d0\uff09\uff0c\u6a80\u7fbd\u51b2\u773c\u4e2d\u7684\u59a9\u5a9a\uff0c\u5c31\u662f\u522b\u4eba\u773c\u4e2d\u7684\u5996\u51b6\u3002 \u3000\u3000\u201c\u8981\u662f\u6e05\u5f7c\u5973\u626e\u7537\u88c5\uff0c\u5979\u8138\u4e0a\u7279\u6709\u7684\u59a9\u5a9a\u662f\u4e0d\u4f1a\u6d88\u5931\u7684\uff0c\u8fd9\u5c11\u5e74\u7709\u5fc3\u7684\u9ed1\u75e3\u3002\u770b\u6765\u4e5f\u4e0d \u662f\u4eba\u5de5\u70b9\u4e0a\u53bb\u7684\u3002\u4f46\u60f3\u4e0d\u5230\u4e16\u4e0a\u7adf\u6709\u76f8\u8c8c\u8fd9\u6837\u76f8\u4f3c\u7684\u4eba\uff0c\u5dee\u522b\u4e0d\u8fc7\u5982\u6b64\u7ec6\u5fae\u3002\u53ef\u60dc\u6211\u6ca1\u6709\u95ee \u8fc7\u6e05\u6ce2\uff0c\u5979\u672c\u8eab\u6709\u6ca1\u6709\u5144\u5f1f\uff1f\u201d\u6a80\u7fbd\u51b2\u5fc3\u60f3\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u89c1\u6a80\u7fbd\u51b2\u53ea\u662f\u5b9a\u7740\u773c\u795e\uff0c\u76ef\u7740\u81ea\u5df1\uff0c\u4e0d\u89c9\u6709\u70b9\u7740\u614c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u95ee\u4f60\u662f\u4e0d\u662f\u559c\u6b22 \u6211\u8fd9\u5339\u5750\u9a91\uff0c\u4f60\u600e\u4e48\u4e0d\u4f5c\u58f0\u5440\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fd9\u624d\u5982\u68a6\u521d\u9192\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8bf7\u95ee\u5144\u53f0\u8fd9\u5339\u5750\u9a91\uff0c\u662f\u4e0d\u662f\u53eb\u505a\u7389\u9879\u8d64\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7684\u6120\u8272\u51cf\u4e86\u51e0\u5206\uff0c\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5012\u662f\u4e2a\u8bc6\u8d27\u7684\u4eba\u3002\u4f46\u559c\u6b22\u5c31\u662f\u559c\u6b22\uff0c\u4e0d\u559c\u6b22\u5c31 \u662f\u4e0d\u559c\u6b22\uff0c\u4f60\u8bf4\u4e0d\u6562\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u662f\u4e0d\u6562\u559c\u6b22\u3002\u56e0\u4e3a\u6211\u81ea\u77e5\u4e0d\u914d\u6709\u8fd9\u6837\u7684\u540d\u9a79\u3002\u201d \u3000\u3000\u9a6c\u5177\u5e97\u7684\u4e3b\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5012\u662f\u9887\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u90a3\u5c31\u4e0d\u5fc5\u8d70\u8fdb\u6211\u7684\u5e97\u5b50 \u91cc\u6765\u591a\u8bf4\u5e9f\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u6446\u4e86\u6446\u624b\uff0c\u793a\u610f\u53eb\u90a3\u5e97\u4e3b\u4e0d\u53ef\u595a\u843d\u5ba2\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u4fd7\u8bdd\u8bf4\u5f97\u597d\uff0c\u5b9d\u5251\u8d60\u70c8\u58eb\uff0c\u7ea2\u7c89 \u8d60\u4f73\u4eba\u3002\u53ea\u53ef\u60dc\u6211\u8fd8\u8981\u8fd9\u5339\u5750\u9a91\u4ee3\u6b65\uff0c\u5426\u5219\u9001\u7ed9\u4f60\u4e5f\u53ef\u4ee5\u3001\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fd9\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u756a\u597d\u610f\uff0c\u6211\u5df2\u7ecf\u662f\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u201d\u4ed6\u60f3\u8bf7\u6559\u5bf9\u65b9\u7684\u59d3\u540d\uff0c\u53c8\u89c9\u5f97 \u4f3c\u4e4e\u6709\u70b9\u5192\u6627\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u90a3\u5c11\u5e74\u5df2\u662f\u622a\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u8fd8\u8981\u8d76\u8def\u3002\u795d\u4f60\u6311\u9009\u5230 \u4e00\u5339\u597d\u5750\u9a91\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u62ff\u4e86\u8f94\u5934\u7ed9\u5750\u9a91\u5957\u4e0a\uff0c\u867d\u7136\u8fd8\u6ca1\u6709\u79bb\u5f00\u5e02\u96c6\uff0c\u5374\u4e0d\u548c\u4ed6\u8bf4\u8bdd\u4e86\u3002\u4ed6\u8fd9\u6001\u5ea6\uff0c\u7b49\u4e8e \u662f\u6446\u660e\u4e86\u544a\u8bc9\u6a80\u7fbd\u51b2\uff0c\u4ed6\u867d\u7136\u6709\u70b9\u6b23\u8d4f\u6a80\u7fbd\u51b2\uff0c\u4f46\u4e5f\u6709\u70b9\u8ba8\u538c\u6a80\u7fbd\u51b2\u4e86\u3002\u6a80\u7fbd\u51b2\u5927\u611f\u5c34\u5c2c\uff0c \u5728\u90a3\u5e97\u5b50\u91cc\u4e0d\u6562\u8ddf\u90a3\u5c11\u5e74\u51fa\u53bb\u3002 \u3000\u3000\u5e97\u4e3b\u4eba\u76b1\u8d77\u7709\u5934\u8bf4\u9053\uff1a\u201c\u5c0f\u5e97\u53ea\u662f\u5356\u9a6c\u5177\u7684\uff0c\u4f60\u7559\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u8981\u4e70\u4e00\u526f\u8f94\u5934\uff0c\u5c31\u8981\u8fd9\u516c\u5b50\u521a\u624d\u4e70\u7684\u540c\u6837\u4e00\u526f\u8f94\u5934\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1b\u201d\u4f60\u662f\u5403\u9971\u4e86\u6ca1\u4e8b\u505a\uff0c\u8dd1\u6765\u6d88\u9063\u6211\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u7981\u6012\u9053\uff1a\u201c\u4f60\u5f53\u6211\u51fa\u4e0d\u8d77\u4ef7\u94b1\u5417\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u4e5f\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u53ea\u89c1\u6a80\u7fbd\u51b2\u9762\u6709\u6012\u8272\uff0c\u4e5f\u81ea\u89c9\u5f97\u8bf4\u8bdd\u6709\u70b9\u8fc7\u4efd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9 \u7a77\u5c0f\u5b50\u867d\u7136\u6599\u60f3\u4ed6\u4e5f\u4e70\u4e0d\u8d77\u516b\u5341\u4e24\u94f6\u5b50\u7684\u4e00\u526f\u8f94\u5934\uff0c\u4f46\u90a3\u4f4d\u516c\u5b50\u7237\u90fd\u4e0d\u6562\u5f97\u7f6a\u4ed6\uff0c\u6211\u53c8\u4f55\u5fc5 \u4ee4\u4ed6\u592a\u8fc7\u96be\u582a\uff0c\u505a\u751f\u610f\u8bb2\u7a76\u548c\u6c14\u751f\u8d22\uff0c\u8fd8\u662f\u4ee5\u4e0d\u5f97\u7f6a\u5ba2\u4eba\u4e3a\u5b9c\u3002\u201d\u4e8e\u662f\u5f3a\u5806\u51fa\u7b11\u8bf4\u9053\uff1a\u201c\u5ba2 \u5b98\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u5750\u9a91\uff0c\u6211\u600e\u80fd\u5c31\u7ed9\u4f60\u914d\u4e00\u526f\u8f94\u5934\uff0c\u9a6c\u6709\u9ad8\u77ee\u80a5\u7626\uff0c\u90a3\u662f\u5fc5\u987b\u914d\u4e0a \u5408\u9002\u7684\u8f94\u5934\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u54d1\u7136\u5931\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u5931\u53bb\u6311\u4e00\u5339\u5750\u9a91\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u6709\u4e2a\u519c\u592b\u6a21\u6837\u7684\u4eba\uff0c\u7275\u7740\u4e00\u5339\u7626\u9aa8\u68f1\u68f1\u7684\u9a6c\u5230\u5e02\u573a\u6765\u53eb\u5356\u3002 \u3000\u3000\u8fd9\u5339\u9a6c\u4e0d\u4f46\u7626\u5f97\u76ae\u5305\u9aa8\uff0c\u800c\u4e14\u6bdb\u8272\u67af\u9ec4\uff0c\u6837\u8c8c\u840e\u7410\u3002\u9a6c\u5177\u5e97\u65c1\u8fb9\u7684\u90a3\u4e2a\u9aa1\u9a6c\u68da\u7684\u8d29\u5b50\u7b11 \u9053\uff1a\u201c\u4f60\u8fd9\u5339\u7626\u9a6c\u4e5f\u7275\u6765\u5356\uff1f\u201d \u3000\u3000\u90a3\u519c\u5bb6\u82e6\u7740\u8138\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u5339\u9a6c\u957f\u76f8\u4e0d\u597d\uff0c\u813e\u6c14\u53c8\u81ed\uff0c\u6211\u90fd\u7ed9\u5b83\u8e22\u5f97\u6015\u4e86\u3002\u4f46\u5b83\u7684\u529b \u6c14\u5012\u662f\u6bd4\u6211\u7528\u6765\u62c9\u8f66\u7684\u90a3\u51e0\u5339\u9a6c\u8fd8\u5927\u7684\u3002\u968f\u4fbf\u4f60\u7ed9\u6211\u51e0\u4e24\u94f6\u5b50\u5427\u3002\u201d \u3000\u3000\u9a6c\u8d29\u5b50\u9053\uff1a\u201c\u5bb0\u4e86\u6765\u5356\uff0c\u8fd9\u4e5f\u6ca1\u6709\u51e0\u4e24\u8089\uff0c\u503c\u5f97\u4ec0\u4e48\u4ef7\u94b1\u3002\u597d\uff0c\u5f53\u4f5c\u53ef\u601c\u4f60\uff0c\u7ed9\u4f60\u4e09\u4e24 \u94f6\u5b50\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u519c\u592b\u9053\uff1a\u201c\u7ed9\u6211\u4e94\u4e24\u94f6\u5b50\u5427\u3002\u8fd9\u5339\u9a6c\u867d\u7136\u7626\uff0c\u4f46\u6c14\u529b\u5f88\u5927\u3002\u8981\u662f\u62a4\u7406\u7684\u597d\uff0c\u8fd9\u8fd8\u662f\u6709 \u7528\u7684\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u82e5\u4e0d\u662f\u5acc\u8fd9\u813e\u6c14\u81ed\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5356\u8fd9\u4e2a\u4ef7\u94b1\u7684\u3002\u201d \u3000\u3000\u9a6c\u8d29\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4e94\u4e24\u94f6\u5b50\uff0c\u4f60\u771f\u662f\u5999\u60f3\u5929\u5f00\uff0c\u9876\u591a\u4e09\u4e24\u94f6\u5b50\uff0c\u94c1\u4ef7\u4e0d\u4e8c\uff0c\u4e0d\u5356\u62c9 \u5012\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u5730\u8d70\u6765\u8bf4\u9053\uff1a\u201c\u6211\u4e70\uff01\u201d \u3000\u3000\u9a6c\u8d29\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e94\u4e24\u94f6\u5b50\u4e70\u8fd9\u5339\u7626\u9a6c\uff01\u54fc\u3001\u8fd9\u4e2a\u771f\u662f\u5e94\u4e86\u4e00\u540d\u4fd7\u8bdd\uff0c\u778e\u732b\u78b0 \u4e0a\u6b7b\u9f20\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u7406\u776c\u522b\u4eba\u7684\u95f2\u8a00\u95f2\u8bed\uff0c\u628a\u8eab\u4e0a\u7684\u94f6\u5b50\u90fd\u62ff\u51fa\u6765\u3002 \u3000\u3000\u90a3\u519c\u592b\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u8981\u4e94\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u8fd9\u5339\u9a6c\u5c82\u53ea\u503c\u4e94\u4e24\u94f6\u5b50\uff1f\u53ef\u60dc\u6211\u8eab\u4e0a\u53ea\u6709\u8fd9\u70b9\u94f6\u5b50\uff0c\u4f60\u6570\u4e00\u6570\uff0c\u5927 \u6982\u662f\u4e94\u5341\u4e24\u5de6\u53f3\u5427\u3002\u4f60\u82e5\u4e0d\u5acc\u5403\u4e8f\u3002\u8bf7\u4f60\u62ff\u53bb\uff01\u201d \u3000\u3000\u90a3\u519c\u592b\u5413\u5f97\u4e0d\u6562\u4f38\u624b\u3002\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e2a\u8001\u5b9e\u4eba\uff0c\u6211\u53eb\u4f60\u62ff\uff0c\u4f60\u5c31\u62ff\u5427\u3002\u6211\u82e5\u662f \u6709\u8db3\u591f\u7684\u94f6\u5b50\uff0c\u4e00\u767e\u4e24\u6211\u4e5f\u4f1a\u7ed9\u4f60\uff01\u201d \u3000\u3000\u90a3\u519c\u592b\u542c\u4ed6\u8fd9\u6837\u8bf4\uff0c\u65b9\u59cb\u6562\u63a5\uff0c\u5fc3\u91cc\u5374\u4ecd\u662f\u601d\u7591\u4e0d\u5b9a\uff0c\u6478\u6478\u90a3\u5339\u7626\u9a6c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u96be \u9053\u8fd9\u5339\u9a6c\u771f\u662f\u6709\u751a\u597d\u5904\u3002\u6211\u770b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u90a3\u9a6c\u8d29\u5b50\u5df2\u662f\u7981\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u90fd\u662f\u6f2b\u5929\u8ba8\u4ef7\uff0c\u5c31\u5730\u8fd8\u94b1\u3002\u50cf\u8fd9\u6837\u7684\u4e70\u5356\u6211\u8fd8\u662f\u7b2c\u4e00 \u6b21\u89c1\u5230\u3002\u5ba2\u5b98\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u8fd9\u91cc\u6700\u597d\u7684\u9a6c\u5339\u4e5f\u4e0d\u8fc7\u503c\u4e09\u5341\u4e24\u94f6\u5b50\uff01\u201d\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60 \u5f53\u6211\u53d1\u795e\u7ecf\u75c5\u5417\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u8fd9\u5339\u9a6c\u6709\u4e2a\u540d\u5802\u53eb\u505a\u4e4c\u9f99\u9a79\uff0c\u5b83\u662f\u5343\u91cc\u9a6c\uff0c\u7528\u6765\u62c9\u8f66\uff0c\u5b83\u600e\u4e48 \u80fd\u4e0d\u53d1\u813e\u6c14\uff0c\u4f60\u8fd9\u91cc\u6700\u597d\u7684\u9a6c\u5339\uff0c\u4e00\u5929\u6700\u591a\u4e5f\u662f\u53ea\u8dd1\u4e8c\u4e09\u767e\u91cc\u5427\uff0c\u600e\u80fd\u548c\u5b83\u76f8\u6bd4\uff1f\u4f9d\u6211\u770b\uff0c \u8fd9\u548c\u90a3\u4f4d\u516c\u5b50\u7684\u7389\u9879\u8d64\u4e5f\u5dee\u4e0d\u591a\uff01\u201d\u90a3\u5c11\u5e74\u6b64\u65f6\u5df2\u9a91\u4e0a\u9a6c\u80cc\uff0c\u56de\u8fc7\u5934\uff0c\u770b\u4e86\u770b\u8fd9\u5339\u7626\u9a6c\uff0c\u5ffd \u5730\u53f9\u9053\uff1a\u201c\u5343\u91cc\u9a6c\u5e38\u6709\uff0c\u4f2f\u4e50\u4e0d\u5e38\u6709\u3002\u53e4\u4eba\u4e4b\u8a00\uff0c\u679c\u4e0d\u6211\u6b3a\uff01\u201d \u3000\u3000\u9a6c\u8d29\u5b50\u4e0d\u61c2\uff0c\u5fc3\u60f3\uff1a\u201c\u770b\u6765\u8fd9\u4e24\u4e2a\u4eba\u90fd\u6709\u70b9\u795e\u7ecf\u75c5\u3002\u201d\u6a80\u7fbd\u51b2\u5374\u4f9b\u4e86\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u591a \u8c22\u5144\u53f0\u8c2c\u8d5e\uff0c\u5176\u5b9e\u6211\u90a3\u91cc\u662f\u4ec0\u4e48\u4f2f\u4e50\uff0c\u4e0d\u8fc7\u591a\u5c11\u61c2\u5f97\u4e00\u70b9\u76f8\u9a6c\u4e4b\u672f\u7f62\u4e86\u3002\u201d\u5c11\u5e74\u4e0d\u518d\u56de\u7b54\uff0c \u9a91\u4e0a\u4ed6\u90a3\u5339\u201c\u7389\u9879\u8d64\u201d\u79bb\u5f00\u5e02\u96c6\u3002\u6a80\u7fbd\u51b2\u7275\u90a3\u5339\u9a6c\u56de\u5230\u9a6c\u5177\u5e97\uff0c\u8bf4\u9053\uff1a\u201c\u521a\u624d\u90a3\u4f4d\u516c\u5b50\u4e70\u7684 \u8f94\u5934\u662f\u516b\u5341\u4e24\u94f6\u5b50\uff0c\u5bf9\u5427\uff1f\u201d\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u690d\u7fbd\u51b2\u638f\u51fa\u4e24\u9897\u91d1\u8c46\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u770b\u770b\uff0c\u7ed3\u4e24\u9897\u91d1\u8c46\u53ef\u503c\u516b\u5341\u4e24\u94f6\u5b50\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u53c8\u559c\u53c8\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8db3\u9053\u4e00\u767e\u94f6\u5b50\u6709\u591a\u4e86\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u7ed9\u5b83\u7684\u65e7\u4e3b\u4eba \u7528\u6765\u62c9\u8f66\uff0c\u9a6c\u4e0a\u64e6\u4f24\u51e0\u5904\uff0c\u8bf7\u4f60\u4e3a\u5b83\u6577\u4e0a\u4f24\u836f\u3002\u591a\u4f59\u7684\u94f6\u5b50\u90fd\u7ed9\u4f60\u3002\u201d\u9a6c\u5177\u5e97\u7684\u4e3b\u4eba\u591a\u662f\u517c \u4efb\u517d\u533b\u7684\uff0c\u63a5\u8fc7\u91d1\u8c46\u3002\u7709\u5f00\u773c\u7b11\uff0c\u8fde\u58f0\u5e94\u8bfa\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u5c1a\u672a\u6765\u5f97\u53ca\u5bdf\u7740\u4f24\u52bf\uff0c\u624b\u521a\u521a\u89e6\u53ca\u9a6c\u8eab\uff0c\u90a3\u5339\u9a6c\u626c\u8e44\u5c31\u8e22\uff0c\u597d\u5728\u6a80\u7fbd\u51b2\u773c\u660e\u7684\u624b \u5feb\uff0c\u6293\u4f4f\u9a6c\u7684\u524d\u8e44\uff0c\u529b\u5ea6\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u90a3\u5339\u9a6c\u4e5f\u4f3c\u4e4e\u77e5\u9053\u9047\u4e0a\u771f\u4e3b\uff0c\u8fd9\u624d\u670d\u670d\u8d34\u8d34\u7684\u8ba9\u5e97 \u4e3b\u4eba\u7ed9\u4ed6\u6577\u4e0a\u4f24\u836f\u3002\u8ddf\u7740\u53c8\u628a\u4e0a\u597d\u7684\u9972\u6599\u7ed9\u5b83\u9971\u9910\u4e00\u987f\u3002\u8fd9\u5339\u9a6c\u9887\u6709\u7075\u6027\uff0c\u77e5\u9053\u8fd9\u4e2a\u65b0\u4e3b\u4eba \u786e\u5b9e\u662f\u5bf9\u5b83\u597d\uff0c\u6328\u7740\u6a80\u7fbd\u51b2\u53ae\u78e8\uff0c\u6602\u9996\u957f\u5636\uff0c\u72b6\u751a\u559c\u60a6\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7ed9\u8fd9\u5957\u4e0a\u8f94\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u81ed\u813e\u6c14\u4e5f\u5f97\u6539\u4e00\u6539\u4e86\u3002\u201d\u5728\u4f17\u4eba\u60ca\u5f02\u7684\u76ee\u5149\u6ce8\u89c6\u4e4b \u4e0b\uff0c\u8de8\u4e0a\u5750\u9a91\uff0c\u79bb\u5f00\u5e02\u573a\u3002\u7b2c\u56db\u5929\u5230\u4e86\u897f\u5883\u5185\u7684\u957f\u6cbb\u53bf\u5c5e\uff0c\u5728\u8fd9\u56db\u5929\u5f53\u4e2d\uff0c\u4ed6\u5c0f\u5fc3\u6599\u7406\u8fd9\u5339 \u9a6c\u9f99\u9a79\uff0c\u665a\u4e0a\u5728\u5ba2\u5e97\u6295\u5bbf\uff0c\u90fd\u662f\u7ed9\u4ed6\u4e0a\u597d\u7684\u9972\u6599\u3002\u4e4c\u9f99\u9a79\u7684\u76ae\u8089\u4e4b\u4f24\u4e5f\u65e9\u5df2\u597d\u4e86\u3002\u4e00\u5929\u8dd1\u5f97 \u5feb\u8fc7\u4e00\u5929\u3002 \u3000\u3000\u8fd9\u5929\u4ed6\u4efb\u7531\u90a3\u5339\u9a6c\u9f99\u9a79\u53d1\u529b\u5954\u9a70\uff0c\u4e0d\u52a0\u97ad\u7b56\uff0c\u53ea\u89c1\u8def\u65c1\u7684\u6811\u6728\uff0c\u95ea\u7535\u822c\u7684\u540e\u9000\uff0c\u5fc3\u4e2d\u5927 \u4e50\uff0c\u60f3\u60f3\uff1a\u201c\u4eba\u4e0d\u53ef\u8c8c\u76f8\uff0c\u9a6c\u4e5f\u4e0d\u53ef\u8c8c\u76f8\u3002\u53ef\u60dc\u8fd9\u9053\u7406\u5374\u662f\u5c11\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u6b63\u81ea\u5f97\u610f\uff0c\u5ffd\u89c1\u524d\u9762\u6709\u4e00\u5339\u5750\u9a91\uff0c\u8dd1\u5f97\u4e5f\u662f\u6709\u5982\u98ce\u9a70\u7535\u63a3\u3002\u6a80\u7fbd\u51b2\u5b9a\u775b\u770b\u53bb\uff0c\u53ef\u4e0d\u6b63\u662f \u56db\u65e5\u4e4b\u524d\u5728\u5b89\u9633\u9a6c\u5e02\u78b0\u4e0a\u7684\u90a3\u4e2a\u5c11\u5e74\u9a91\u7684\u90a3\u5339\u201c\u7389\u9879\u8d64\u201d\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u53d1\u73b0\u6709\u4eba\u8ffd\u6765\uff0c\u56de\u5934\u4e00\u671b\uff0c\u7a0d\u7f13\u4e00\u7f13\uff0c\u6a80\u7fbd\u51b2\u5df2\u662f\u8ffd\u4e0a\u4ed6\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u53c8\u4e0e\u5144\u53f0\u76f8\u4f1a\uff0c\u4e5f\u53ef\u8bf4\u662f\u6709\u7f18\u4e86\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u4ed6\u8fd9\u5339\u7389\u9879\u8d64\u7684\u811a \u529b\u662f\u4e0d\u5728\u4e4c\u9f99\u9a79\u4e4b\u4e0b\uff0c\u60f3\u5fc5\u4ed6\u662f\u5728\u9014\u4e2d\u56e0\u4e8b\u803d\u6401\uff0c\u5426\u5219\u6211\u7edd\u8ba1\u8ffd\u4e0d\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u542c\u5f97\u201c\u6709\u7f18\u201d\u4e8c\u5b57\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u5730\u53cc\u7709\u4e00\u6311\uff0c\u8138\u4e0a\u53d8\u8272\uff0c\u9690\u9690\u51e0\u5206\u6012\u6c14\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8d8a\u770b\u4ed6\u8d8a\u4f3c\u8d6b\u8fde\u6e05\u6ce2\uff0c\u5374\u6ca1\u5bdf\u89c9\u4ed6\u7684\u6012\u8272\uff0c\u8ffd\u4e0a\u53bb\u4e0e\u4ed6\u5e76\u8f94\u800c\u884c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65e5 \u5c1a\u672a\u5f97\u8bf7\u6559\u5144\u53f0\u7684\u9ad8\u6027\u5927\u540d\uff0c\u4e0d\u77e5\u53ef\u80af\u8d50\u544a\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u7a81\u7136\u54fc\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u559c\u4f60\u83b7\u5f97\u4e00\u5339\u5343\u91cc\u9a79\uff0c\u4f46\u6211\u4e5f\u6709\u4e00\u4e8b\u8981\u5411\u4f60\u8bf7\u6559\uff01\u201d\u6a80 \u7fbd\u51b2\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u4e0d\u77e5\u5144\u53f0\u8981\u77e5\u9053\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u51b7\u51b7\u7b11\u8bf4\uff1a\u201c\u4f60\u80cc\u540e\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6115\u7136\u9053\uff1a\u201c\u6211\u80cc\u540e\u90a3\u6709\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c11\u5e74\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u522b\u88c5\u849c\u4e86\uff0c\u4f60\u7792\u4e0d\u8fc7\u6211\u7684\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u771f\u7684\u4e0d\u61c2\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5c11\u5e74\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u548c\u4f60\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\u5427\uff0c\u662f\u8c01\u6307\u4f7f\u4f60\u6765\u8ffd\u6211\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5931\u7b11\u9053\uff1a\u201c\u4f60\u8bef\u4f1a\u4e86\uff0c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u5c11\u5e74\u6446\u51fa\u4e00\u526f\u4e0d\u613f\u542c\u4ed6\u8bf4\u5e9f\u8bdd\u7684\u795e\u6c14\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff0c\u82e5\u975e\u6709\u4eba\u6307\u4f7f\uff0c\u4f60\u5e72\u561b \u51a4\u9b42\u4e0d\u606f\u4f3c\u7684\uff0c\u8001\u662f\u8ddf\u7740\u6211\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5f3a\u5fcd\u6012\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6839\u672c\u5c31\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u662f\u6070\u8d70\u540c\u4e00\u6761\u8def\u7f62\u4e86\uff01\u201d \u3000\u3000\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u6211\u5012\u8981\u8bf7\u95ee\u4f60\u4e86\uff0c\u8bf7\u95ee\u4f60\u662f\u4e0d\u662f\u6709\u8fd9\u4e2a\u4e60\u60ef\uff0c\u78b0\u4e0a\u4e86\u4e0d\u76f8\u8bc6\u7684\u4eba\uff0c\u5c31 \u8981\u5b9a\u7740\u773c\u775b\uff0c\u76ef\u7740\u4eba\u5bb6\u770b\u7684\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u60f3\u4e0d\u5230\u4ed6\u6709\u5982\u6b64\u76f4\u7387\u7684\u4e00\u95ee\uff0c\u4ed6\u600e\u80fd\u5411\u4ed6\u89e3\u91ca\uff0c\u4ed6\u662f\u56e0\u4e3a\u4ed6\u7684\u9762\u8c8c\u9177\u4f3c\u8d6b\u8fde\u6e05\u6ce2 \u624d\u76ef\u7740\u4ed6\u770b\u7684\u5462\uff1f\u201c\u5bf9\u4e0d\u4f4f\uff0c\u5728\u5b89\u9633\u90a3\u65e5\uff0c\u6211\u56e0\u89c1\u5144\u53f0\u7684\u5750\u9a91\u975e\u540c\u51e1\u54c1\u800c\u50cf\u5144\u53f0\u8fd9\u6837\u4fca\u96c5\u7684 \u4eba\uff0c\u5728\u95f9\u5e02\u4e2d\u4e5f\u6709\u5982\u9e64\u7acb\u9e29\u7fa4\uff0c\u6211\u4e0d\u89c9\u5931\u4eea\u4e4b\u7f6a\uff0c\u8bf7\u5144\u53f0\u83ab\u602a\u3002\u201d \u3000\u3000\u5c11\u5e74\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4fca\u96c5\u4e5f\u597d\uff0c\u4e11\u602a\u4e5f\u597d\uff0c\u8fd9\u90fd\u4e0d\u5173\u4f60\u7684\u4e8b\uff1f\u597d\uff0c\u4f60\u8bf4\u4f60\u4e0d\u662f\u8ddf\u8e2a\u6211 \u7684\uff0c\u6211\u59d1\u4e14\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u90a3\u5c31\u5404\u8d70\u5404\u8def\uff0c\u7cbe\u4f60\u522b\u518d\u7f20\u7740\u6211\uff01\u201d\u9a6c\u97ad\u626c\u7a7a\u4e00\u6296\uff0c\u5530\u5530\u8fde\u58f0\uff0c\u865a \u6253\u4e24\u97ad\uff0c\u80ef\u4e0b\u7684\u5750\u9a91\u88ab\u4e3b\u4eba\u4e00\u50ac\u8dd1\u5f97\u98de\u5feb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9a91\u7684\u8fd9\u5339\u4e4c\u9f99\u9a79\uff0c\u82e5\u662f\u53d1\u529b\u5954\u9a70\uff0c\u672c\u6765\u53ef\u4ee5\u8ffd\u4e0a\u5c11\u5e74\u6240\u9a91\u7684\u90a3\u5339\u7389\u9879\u8d64\u7684\uff0c\u4f46\u4ed6 \u88ab\u90a3\u5c11\u5e74\u4e00\u987f\u6392\u6863\uff0c\u5374\u8fd8\u600e\u80fd\u539a\u7740\u8138\u76ae\uff0c\u518d\u8ffd\u4e0a\u53bb\uff1f\u5929\u8272\u672c\u6765\u662f\u597d\u597d\u7684\uff0c\u5ffd\u7136\u4e0b\u8d77\u6765\u96e8\u6765\uff0c \u8d8a\u4e0b\u8d8a\u5927\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5c11\u5e74\u4e0d\u80af\u548c\u6211\u7ed3\u4ea4\uff0c\u90a3\u5c31\u7b97\u4e86\u3002\u8fd8\u662f\u8d76\u5230\u524d\u5934\u6253\u4e2a\u5bbf\u5934\u5427\u3002\u522b\u60f3\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8981\u77e5\u4ed6\u662f\u975e\u5e38\u7231\u62a4\u4ed6\u65b0\u5f97\u8fd9\u5339\u4e4c\u9f99\u9a79\u7684\uff0c\u4eba\u78b0\u4e0a\u5927\u96e8\u8fd8\u4e0d\u6253\u7d27\uff0c\u8fd9\u5339\u9a6c\u4ed6\u521a\u521a\u8c03\u7406\u5f97\u5b83 \u6062\u590d\u4e86\u672c\u6765\u7684\u795e\u9a8f\uff0c\u5374\u662f\u820d\u4e0d\u5f97\u5b83\u5728\u5927\u96e8\u4e4b\u4e2d\u8dd1\u6ce5\u6cde\u7684\u5c71\u8def\u4e86\u3002\u4f55\u51b5\u53c8\u5df2\u662f\u5929\u9ed1\u65f6\u5206\u3002 \u3000\u3000\u5929\u4ece\u4eba\u613f\uff0c\u6b63\u5f53\u4ed6\u8dd1\u4e0a\u5c71\u8def\u4e86\u60f3\u5728\u6811\u6797\u627e\u4e2a\u5730\u65b9\u907f\u96e8\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u53d1\u73b0\u5c71\u8170\u5904\u6709\u4e00\u6237\u4eba \u5bb6\uff0c\u8d70\u8fd1\u4e00\u770b\uff0c\u7ea2\u5899\u7eff\u74e6\u4f3c\u4e4e\u8fd8\u4e0d\u662f\u5bfb\u5e38\u7684\u767e\u59d3\u4eba\u5bb6\u3002 \u3000\u3000\u800c\u4e14\u53ea\u6709\u8fd9\u5bb6\u5b64\u4f36\u4f36\u7684\u4eba\u5bb6\u3002 \u3000\u3000\u4eba\u4e0d\u8981\u6b47\uff0c\u9a6c\u4e5f\u8981\u6b47\u606f\u7684\u3002\u987e\u4e0d\u5f97\u8fd9\u4e48\u591a\uff0c\u6a80\u7fbd\u51b2\u8d70\u4e0a\u53bb\u62cd\u95e8\u3002 \u3000\u3000\u5c4b\u5185\u7684\u4eba\u7adf\u7136\u6ca1\u6709\u53d1\u95ee\uff0c\u5c31\u6253\u5f00\u4e86\u95e8\u3002\u51fa\u6765\u8fce\u63a5\u4ed6\u7684\u662f\u4e00\u4e2a\u8001\u6c49\u548c\u4e00\u4e2a\u6253\u7740\u706f\u7b3c\u7684\u5c0f \u5b69\u3002 \u3000\u3000\u8fd9\u5c0f\u5b69\u7ea6\u6709\u5341\u4e8c\u3001\u4e09\u5c81\u5e74\u7eaa\uff0c\u628a\u706f\u7b3c\u63d0\u8d77\uff0c\u671d\u7740\u6a80\u7fbd\u51b2\u7167\u4e86\u4e00\u7167\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4 \u9053\uff1a\u201c\u539f\u6765\u4e0d\u662f\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u8001\u6c49\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4ed6\u5c31\u6ca1\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u6211\u662f\u8fc7\u8def\u7684\u5ba2\u4eba\uff0c\u78b0\u4e0a\u5927\u96e8\u7279\u5730\u6765\u6c42\u5bbf\uff0c\u8bf7\u4f60\u4eec\u884c\u4e2a\u65b9\u4fbf\u3002\u201d\u6a80\u7fbd\u51b2\u9053\u3002 \u3000\u3000\u90a3\u8001\u6c49\u5fc3\u5730\u6148\u60b2\uff0c\u7a0d\u4e00\u8fdf\u7591\u4fbf\u5373\u7b54\u5141\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u8bf7\u8fdb\u6765\u5427\u3002\u91d1\u54e5\uff0c\u4f60\u53bb\u7980 \u544a\u5a46\u5a46\u3002\u201d\u91cc\u9762\u4f20\u6765\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\uff1a\u201c\u662f\u8c01\u5440\uff1f\u201d\u90a3\u8001\u6c49\u9053\uff1a\u201c\u662f\u4e2a\u8fc7\u8def\u7684\u5ba2\u4eba\u9047\u96e8\u501f \u5bbf\uff0c\u8001\u5974\u64c5\u81ea\u4f5c\u4e3b\uff0c\u8bf7\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u8fd8\u6ca1\u56de\u7b54\uff0c\u6a80\u7fbd\u51b2\u5148\u542c\u89c1\u4e00\u4e2a\u597d\u4f3c\u5973\u5b50\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4ec0\u4e48\u6837\u8c8c\uff1f\u201d\u58f0 \u97f3\u8bf4\u5f97\u5f88\u8f7b\uff0c\u597d\u50cf\u662f\u548c\u522b\u4eba\u54ac\u7740\u8033\u6735\u8bf4\u8bdd\u4e00\u822c\u3002\u82e5\u4e0d\u662f\u6a80\u7fbd\u51b2\u81ea\u5e7c\u7ec3\u6b66\uff0c\u542c\u89c9\u6bd4\u5e38\u4eba\u654f\u9510\uff0c \u6050\u6015\u662f\u4e00\u4e2a\u5b57\u90fd\u542c\u4e0d\u89c1\uff0c\u6a80\u7fbd\u51b2\u5fc3\u60f3\uff1a\u201c\u5979\u8bf4\u5f97\u8fd9\u6837\u8f7b\uff0c\u5916\u9762\u7684\u5ba2\u4eba\u662f\u542c\u4e0d\u89c1\u7684\u3002\u6562\u6e05\u662f\u95e8 \u521a\u624d\u8fdb\u53bb\u7684\u90a3\u4e2a\u5c0f\u5b69\u3002\u201d \u3000\u3000\u679c\u7136\u4fbf\u542c\u89c1\u90a3\u4e2a\u540d\u53eb\u91d1\u54e5\u7684\u5c0f\u5b69\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u76fc\u671b\u7684\u90a3\u4e2a\u4eba\u3002\u4f60\u7684\u90a3 \u4e2a\u4eba\u6211\u662f\u89c1\u8fc7\u7684\uff0c\u4ed6\u5982\u4f55\u6253\u626e\uff0c\u6211\u90fd\u8ba4\u5f97\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u5fc3\u7126\uff0c\u6211\u77e5\u9053\u4ed6\u662f\u4ece\u6765\u4e0d\u4f1a\u9a97 \u4eba\u7684\uff0c\u4f60\u7ea6\u597d\u4e86\u4ed6\uff0c\u4ed6\u5c31\u4e00\u5b9a\u4f1a\u6765\uff01 \u3000\u3000\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u8c01\uff0c\u5927\u96e8\u6ec2\u6cb1\uff0c\u54b1\u4eec\u90fd\u5e94\u8be5\u7559\u5ba2\uff01\u201d\u8ddf\u7740\u63d0\u9ad8\u58f0\u97f3 \u9053\uff1a\u201c\u597d\uff0c\u4f60\u66ff\u6211\u62db\u547c\u5ba2\u4eba\u5427\u3002\u544a\u8bc9\u5ba2\u4eba\uff0c\u6055\u6211\u4e0d\u51fa\u6765\u4e86\u3002\u201d\u663e\u7136\u524d\u4e00\u53e5\u8bdd\u662f\u5bf9\u90a3\u5c11\u5973\u8bf4 \u7684\uff0c\u540e\u4e00\u53e5\u8bdd\u624d\u662f\u5429\u5490\u8fd9\u4e2a\u8001\u4ec6\u3002 \u3000\u3000\u90a3\u4e2a\u8001\u4ec6\u4eba\u62db\u547c\u6a80\u7fbd\u51b2\u8fdb\u5165\u5c4b\u5185\uff0c\u4e00\u9762\u8d70\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u751f\u6bcd\u5b40\u5c45\u591a\u5e74\uff0c\u4e08\u592b\u548c\u513f \u5b50\u3001\u5ab3\u5987\u5df2\u6b7b\u4e86\uff0c\u53ea\u6709\u4e00\u4e2a\u5b59\u513f\u3002\u9664\u4e86\u81f3\u4eb2\u4e4b\u5916\uff0c\u5f88\u5c11\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u591a\u8499\u4f60\u5bb6\u4e3b\u6bcd\u501f\u665a\u5df2\u611f\u6fc0\u4e0d\u5c3d\uff0c\u600e\u4e48\u8fd8\u6562\u60ca\u52a8\u5979\u8001\u4eba\u5bb6\uff1f\u201c\u5fc3\u91cc\u5374\u5728\u60f3 \u9053\uff1a\u201c\u5979\u65e2\u7136\u53ea\u6709\u4e00\u4e2a\u5b59\u513f\uff0c\u90a3\u5973\u77e5\u4e3b\u4eba\u662f\u8c01\uff1f\u201d\u89c9\u5f97\u8fd9\u5bb6\u4eba\u4e5f\u4f3c\u4e4e\u6709\u70b9\u53e4\u602a\uff0c\u4f46\u81ea\u662f\u4e0d\u4fbf \u5411\u90a3\u8001\u4ec6\u6253\u542c\u3002 \u3000\u3000\u201c\u4f60\u5bb6\u9065\u9a6c\u53a9\u4e48\uff0c\u6211\u60f3\u5148\u6599\u7406\u8fd9\u5339\u5750\u9a91\u3002\u201d\u6a80\u7fbd\u51b2\u95ee\u9053\u3002 \u3000\u3000\u201c\u6709\uff0c\u4f60\u968f\u6211\u6765\u3002\u6211\u5e2e\u4f60\u7167\u6599\u5b83\u5c31\u662f\u3002\u201d\u524d\u5934\u5f15\u8def\uff0c\u5e26\u9886\u6a80\u7fbd\u51b2\u628a\u5750\u9a91\u7275\u4eba\u9a6c\u53a9\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u773c\u775b\u9661\u5730\u4e00\u4eae\uff0c\u539f\u6765\u53a9\u4e2d\u6709\u4e24\u5339\u9a6c\uff0c\u5176\u4e2d\u4e00\u5339\u5c31\u6b63\u662f\u8def\u4e0a\u76f8\u9022\u7684\u90a3\u4e2a\u5c11\u5e74\u7684\u5750\u9a91 \u7389\u9876\u8d64\u3002\u6a80\u7fbd\u51b2\u4e0d\u89c9\u201c\u54a6\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u90a3\u8001\u4ec6\u6115\u7136\u7684\u671b\u7740\u4ed6\u3002 \u3000\u3000\u6a80\u7fbd\u4ef2\u81ea\u62d3\u5931\u6001\uff0c\u4fbf\u52a0\u63a9\u9970\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u795e\u9a8f\u5f02\u5e38\uff0c\u4f46\u6211\u597d\u50cf\u89c1\u8fc7\u5b83\u7684\u3002\u4e0d\u8fc7\u4eba\u6709 \u76f8\u4f3c\uff0c\u7269\u6709\u540c\u6837\uff0c\u6216\u8bb8\u662f\u770b\u9519\u4e86\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u90a3\u8001\u4ec6\u5916\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u8bf4\u5c31\u6050\u6015\u662f\u5bf9\u4e86\u3002\u8fd9\u5339\u9a6c\u4e0d\u662f\u6211\u5bb6\u7684\uff0c\u5b83\u662f\u4e00\u4e00\u55ef\uff0c\u5b83\u7684\u4e3b\u4eba\u5df2 \u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u56de\u5934\u4e00\u770b\uff0c\u5411\u4ed6\u8d70\u6765\u7684\u4e0d\u662f\u90a3\u4e2a\u5c11\u5e74\u662f\u8c01\uff1f\u90a3\u5c11\u5e74\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6ca1\u770b\u9519\u3002\u6211\u4e5f \u6ca1\u6709\u770b\u9519\uff01\u201d\u524d\u4e00\u53e5\u201c\u6ca1\u6709\u770b\u9519\u201d\u610f\u601d\u660e\u663e\u3001\u662f\u6307\u90a3\u5339\u5750\u9a91\uff0c\u540e\u4e00\u53e5\u201c\u6ca1\u6709\u770b\u9519\u201d\uff0c\u5374\u662f\u4ee4 \u5f97\u6a80\u7fbd\u51b2\u6709\u70b9\u83ab\u6d4b\u9ad8\u6df1\u4e86\u3002 \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u770b\u7740\u4ed6\u4eec\uff0c\u795e\u60c5\u4eff\u4e4e\u66f4\u52a0\u8be7\u5f02\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u62f1\u4e00\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u4f4f\u5728\u8fd9\u513f\u3002\u9644\u8fd1\u6ca1\u6709\u4eba\u5bb6\uff0c\u6211\u53ea\u597d\u8dd1\u5230 \u6258\u5e87\u3002\u201d\u8bed\u6c14\u8bf4\u5f97\u751a\u4e3a\u8bda\u6073\uff0c\u4e5f\u4e0d\u6562\u76ef\u7740\u5bf9\u65b9\u770b\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\u3002\u6211\u53c8\u4e0d\u662f\u8fd9\u91cc\u7684\u4e3b\u4eba\uff0c\u4f60\u4e5f\u65e0\u987b\u5411\u6211\u8bf4\u660e\u3001\u201d\u8bf4 \u7f62\uff0c\u4fbf\u5373\u79bb\u53bb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9690\u9690\u542c\u5f97\u90a3\u4e2a\u540d\u53eb\u91d1\u54e5\u7684\u5b69\u5b50\u5728\u65c1\u95ee\u4ed6\uff1a\u201c\u4e91\u8868\u54e5\uff0c\u539f\u6765\u4f60\u548c\u90a3\u5ba2\u4eba\u662f\u76f8\u8bc6\u7684 \u5417\uff1f\u201d\u201c\u8868\u201d\u5b57\u62d6\u5f97\u5f88\u957f\uff0c\u90a3\u5c11\u5e74\u54b3\u4e86\u4e00\u58f0\uff0c\u91d1\u54e5\u65b9\u8d77\u7ee7\u7eed\u8bf4\u51fa\u90a3\u4e2a\u201c\u54e5\u201d\u5b57\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u8def\u4e0a\u5076\u7136\u78b0\u89c1\u8fc7\u7684\u964c\u751f\u4eba\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u76f8\u8bc6\u3002\u201d\u4e24\u4eba\u7684\u811a\u6b65\u58f0\u5411\u7740\u53cd\u65b9 \u5411\uff0c\u4ed6\u6b63\u5728\u56de\u8f6c\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u800c\u91d1\u54e5\u5219\u548c\u90a3\u8001\u4ec6\u62db\u547c\u5ba2\u4eba\uff0c\u4e24\u4eba\u7684\u8c08\u8bdd\u5c31\u6ca1\u6709\u7ee7\u7eed\u4e0b\u53bb\u4e86\u3002 \u90a3\u8001\u4ec6\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u8fde\u76f8\u516c\u662f\u6211\u5bb6\u4e3b\u6bcd\u7684\u8fdc\u4eb2\uff0c\u4ed6\u6070\u597d\u4e5f\u662f\u4eca\u5929\u6765\u5230\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002\u5fc3\u91cc\u5219\u60f3\u5230\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u59d3\u8fde\uff0c\u540d\u5b57\u5927\u6982\u6709\u4e2a\u2018\u4e91\u2019 \u7684\u3002\u6e05\u6ce2\u590d\u59d3\u2018\u8d6b\u8fde\u2019\uff0c\u8d6b\u8fde\u662f\u8fbd\u59d3\u3002\u4ed6\u662f\u5355\u59d3\u4e00\u4e2a\u2018\u8fde\u2019\u5b57\uff0c\u59d3\u8fde\u7684\u8fbd\u4eba\u6c49\u4eba\u90fd\u6709\u3002\u771f \u5999\uff0c\u4ed6\u548c\u6e05\u6ce2\u4e0d\u4ec5\u76f8\u8c8c\u76f8\u4f3c\uff0c\u59d3\u4e5f\u53ea\u5dee\u4e86\u4e00\u4e2a\u5b57\uff01\u201d\u4ece\u59d3\u6c0f\u5f15\u8d77\u7684\u8fde\u60f3\uff0c\u4ee4\u5f97\u6a80\u7fbd\u51b2\u4e0d\u7981\u66f4 \u52a0\u601d\u7591\uff0c\u601d\u7591\u8fd9\u4e2a\u5c11\u5e74\u662f\u548c\u8d6b\u8fde\u6e05\u6ce2\u6709\u7740\u4eb2\u5c5e\u7684\u5173\u7cfb\u3002 \u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u96e8\u52bf\u7a0d\u4e3a\u5c0f\u4e00\u70b9\uff0c\u8fd8\u672a\u505c\u4e0a\u3002\u5927\u7ea6\u521d\u66f4\u65f6\u5206\u3002\u5ffd\u7136\u53c8\u542c\u89c1\u6709\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u6b21\u6765\u7684\u662f\u4e00\u4e2a\u548c\u5c1a\uff0c\u4e00\u4e2a\u9053\u58eb\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e2d\u5e74\u5987\u4eba\u3002\u8fd9\u4e2a\u5987\u4eba\u6d82\u8102\u62b9\u7c89\uff0c\u6253\u626e\u5f97\u9887\u4e3a \u5996\u8273\u3002\u8fd9\u4e09\u4e2a\u4eba\u7ed3\u4f34\u800c\u6765\uff0c\u90a3\u8001\u4ec6\u4eba\u4e00\u89c1\u5c31\u77e5\uff0c\u4ed6\u4eec\u9053\u8def\u4e0d\u6b63\u3002\u4f46\u5df2\u7ecf\u62db\u547c\u4e86\u6a80\u7fbd\u51b2\u8fd9\u4e2a\u5ba2 \u4eba\uff0c\u4e0d\u4fbf\u539a\u6b64\u8584\u5f7c\uff0c\u5f97\u5230\u4e3b\u6bcd\u5141\u51c6\uff0c\u5c31\u5f00\u95e8\u8ba9\u4ed6\u4eec\u8fdb\u6765\u4e86\u3002\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u53ea\u6709\u4e00\u95f4\u5ba2\u623f\uff0c \u6709\u4f4d\u5ba2\u4eba\u5df2\u7ecf\u5148\u6765\u4e86\u3002\u201d\u90a3\u8001\u4ec6\u4eba\u8bf4\u9053\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4f4d\u5ba2\u4eba\u591a\u5927\u5e74\u7eaa\uff0c\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d \u3000\u3000\u8001\u4ec6\u4eba\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4f55\u8981\u6253\u542c\u5f97\u8fd9\u6837\u4ed4\u7ec6\uff1f\u201d \u3000\u3000\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u4e00\u95f4\u5ba2\u623f\u6700\u5c11\u4e5f\u53ef\u5bb9\u5f97\u4e24\u4e2a\u4eba\u7761\u5427\uff1f\u82e5\u662f\u7537\u7684\uff0c\u6211\u548c\u8fd9\u4f4d\u9053\u5144\u90fd\u53ef\u4ee5\u4e0e\u4ed6 \u540c\u623f\uff0c\u82e5\u662f\u5973\u7684\uff0c\u6211\u4eec\u8fd9\u4f4d\u9c8d\u4e09\u5a18\u5b50\u4e5f\u53ef\u4e0e\u5979\u5171\u69bb\u3002\u201d \u3000\u3000\u90a3\u9053\u58eb\u7b11\u9053\uff1a\u201c\u767d\u4e91\u5927\u5e08\u6211\u8bf4\u9519\u4e86\u3002\u82e5\u662f\u7537\u7684\uff0c\u9c8d\u4e09\u5a18\u5b50\u6050\u6015\u66f4\u52a0\u559c\u6b22\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5987\u4eba\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u653e\u4f60\u5988\u7684\u5c41\uff0c\u8001\u5a18\u5b88\u5be1\u6211\u5fa1\uff0c\u8fd9\u4e2a\u73a9\u7b11\u4e5f\u662f\u5f00\u5f97\u7684\u5417\uff1f\u201d \u3000\u3000\u8001\u4ec6\u4eba\u677f\u8d77\u8138\u5b54\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5bb6\u7684\u89c4\u77e9\uff0c\u662f\u4e0d\u80fd\u5931\u793c\u5ba2\u4eba\u7684\u3002\u90a3\u4f4d\u5ba2\u4eba\u5df2\u7ecf\u5148\u6765\uff0c\u4ed6 \u662f\u4e0d\u662f\u613f\u610f\u548c\u4f60\u4eec\u540c\u623f\uff0c\u6211\u53ef\u5f97\u5148\u95ee\u4e00\u95ee\u4ed6\u3002\u201d\u4ed6\u9690\u5fcd\u4e0d\u53d1\uff0c\u6001\u5ea6\u8fd8\u662f\u597d\u50cf\u521a\u624d\u90a3\u6837\uff0c\u5bf9\u4efb \u4f55\u5ba2\u4eba\u90fd\u606d\u606d\u656c\u656c\u7684\u3002 \u3000\u3000\u90a3\u9053\u58eb\u9053\uff1a\u201c\u7528\u4e0d\u7740\u9ebb\u70e6\u4f60\u4e86\uff0c\u6211\u4eec\u81ea\u5df1\u4f1a\u8fdb\u53bb\u95ee\u4ed6\uff01\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u8d64\u677e\u9053\u5144\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u9c81\u83bd\uff1f\u4f60\u4e0d\u6015\u5931\u793c\uff0c\u6211\u4e5f\u6015\u5931\u793c\uff01\u201d \u3000\u3000\u90a3\u9053\u58eb\u9053\uff1a\u201d\u563b\uff0c\u9c8d\u4e09\u5a18\u5b50\u4e5f\u6015\u5931\u793c\uff0c\u5947\u95fb\uff01\u201c\u4f46\u4ed6\u597d\u50cf\u6709\u70b9\u5bb3\u6015\u8fd9\u4e2a\u4e2d\u5e74\u5987\u4eba\uff0c\u53e3\u4e2d \u5c3d\u7ba1\u8bf4\u7b11\uff0c\u5374\u662f\u4e0d\u6562\u8fdd\u6297\u5979\u7684\u547d\u4ee4\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u5c0f\u7684\u4f46\u6c42\u4e00\u5bbf\uff0c\u5728\u5ba2\u623f\u4e0a\u6253\u5730\u94fa\u4e5f\u884c\u3002\u8fd9\u4f4d\u5927\u5a76\uff0c\u8bf7\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u4ed4\u7ec6\u6253\u91cf\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u628a\u5ba2\u623f\u8ba9\u7ed9\u6211\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u793c\u8be5\u5982\u6b64\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u662f\u8bfb\u4e66\u4eba\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6545\u610f\u88c5\u51fa\u62d8\u8c28\u7684\u6837\u5b50\uff0c\u56de\u907f\u5979\u7684\u76ee\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u8499\u9986\u91cc\u80e1\u4e71\u8bfb\u8fc7\u51e0\u5e74\uff0c\u4e0d\u6562\u4ee5 \u8bfb\u4e66\u4eba\u81ea\u5c45\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u772f\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\u5427\uff0c\u5c31\u8bfb\u8fc7\u51e0\u5e74\u4e66\u4e86\uff0c\u563f\u563f\u3002\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d \u8d77\uff01\u602a\u4e0d\u5f97\u4f60\u8fd9\u6837\u65af\u6587\u6709\u793c\u3002\u201d \u3000\u3000\u90a3\u9053\u53f7\u8d64\u677e\u7684\u9053\u58eb\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u4ec5\u65af\u6587\u6709\u793c\uff0c\u8fd8\u957f\u5f97\u633a\u4fca\u5462\uff01\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u751f\u6015\u4ed6\u8bf4\u51fa\u4e0d\u4e2d\u542c\u7684\u8bdd\uff0c\u559d\u9053\uff1a\u201c\u5bf9\u8bfb\u4e66\u7684\u76f8\u516c\u4e0d\u5f97\u653e\u8086\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u4f60\u4eec\u8bfb\u4e66\u4eba\u662f\u8bb2\u7a76\u4ec0\u4e48\u7537\u5973\u4ec0\u4e48\u4ec0\u4e48\u4e0d\u4eb2\u7684\uff0c\u90a3\u53e5\u8bdd\u600e\u8bf4\u7684\uff1f\u201d \u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u5bf9\u4e86\uff01\u5bf9\u4e86\u3002\u7537\u5973\u6388\u4eb2\u4e0d\u4eb2\u3002\u8fd9\u610f\u601d\u662f\u8bf4\uff0c\u9664\u4e86\u4e08\u592b\u4e4b\u5916\uff0c\u5973\u4eba\u5728\u522b\u7684 \u7537\u5b50\u624b\u4e0a\u63a5\u8fc7\u4e00\u4ef6\u4e1c\u897f\u90fd\u4e0d\u53ef\u4ee5\uff0c\u662f\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u539f\u6765\u5927\u5a76\u4e5f\u662f\u77e5\u4e66\u660e\u7406\u7684\uff0c\u4f69\u670d\uff0c\u4f69\u670d\u3002\u201d\u9c8d\u4e09\u5a18\u5b50\u5927\u7b11\u9053\uff1a\u201d\u6211\u61c2\u5f97\u4e2a \u5c41\u8bfb\u4e66\u4eba\u7684\u9053\u7406\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u662f\u5728\u7537\u5b50\u5806\u4e2d\u6df7\u5927\u7684\uff0c\u53bb\u4ed6\u5988\u7684\u6388\u53d7\u4e0d\u4eb2\uff0c\u6211\u81ea\u95ee\u53ea\u8981\u884c\u5f97 \u6b63\uff0c\u548c\u7537\u4eba\u5728\u4e00\u8d77\u8fc7\u591c\u4e5f\u4e0d\u5728\u4e4e\u3002\u4f60\u56de\u623f\u95f4\u53bb\u5427\u6211\u4e0d\u8981\u4f60\u8ba9\u3002\u201d \u3000\u3000\u539f\u6765\u5979\u89c1\u6a80\u7fbd\u51b2\u662f\u4e2a\u4e66\u751f\u7684\u6837\u5e72\uff0c\u76f8\u8c8c\u548c\u4ed6\u4eec\u6240\u8981\u627e\u7684\u90a3\u4eba\u4e5f\u4e0d\u76f8\u540c\uff0c\u5fc3\u60f3\u529e\u6b63\u7ecf\u4e8b\u8981 \u7d27\uff0c\u4fbf\u9002\u53ef\u800c\u6b62\uff0c\u4e0d\u518d\u548c\u6a80\u7fbd\u51b2\u7ea0\u7f20\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u9752\u677e\u9053\u4eba\u62cd\u62cd\u809a\u76ae\uff1a\u201c\u809a\u76ae\u8981\u9020\u53cd\u4e86\uff01\u5f97\u5148\u796d\u796d\u4e94\u810f\u5e99\u3002\u201d \u3000\u3000\u8001\u4ec6\u9053\uff1a\u201c\u8bf7\u6055\u6211\u4eec\u6ca1\u6709\u4e0a\u7d20\uff0c\u82e5\u4e0d\u5acc\u5f03\uff0c\u6211\u7528\u54b8\u83dc\u7ed9\u4f60\u4eec\u7092\u789f\u51b7\u996d\u3002\u201d \u3000\u3000\u90a3\u6cd5\u53f7\u201c\u767d\u4e91\u201d\u7684\u548c\u5c1a\u9053\uff1a\u201c\u8c01\u5403\u4f60\u7684\u54b8\u83dc\u51b7\u996d\uff0c\u6d12\u5bb6\u662f\u9152\u8089\u548c\u5c1a\uff0c\u975e\u8089\u4e0d\u9971\uff0c\u975e\u9152\u4e0d \u996e\uff0c\u6d12\u5bb6\u65e9\u5df2\u81ea\u5907\u4e86\uff0c\u4f60\u53ea\u987b\u7ed9\u6211\u751f\u4e00\u76c6\u706b\u6765\u3002\u201d \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u539f\u6765\u5927\u548c\u5c1a\u65e9\u5df2\u81ea\u5907\u9152\u8089\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u9152\u662f\u73b0\u6210\u7684\uff0c\u9a6c\u4e0a\u7ed9\u4f60 \u7aef\u6765\u3002\u201d\u767d\u4e91\u7985\u5e08\u9053\uff1a\u201c\u597d\u5728\u5348\u95f4\u5bb0\u7684\u90a3\u6761\u72d7\u53c8\u80a5\u53c8\u5927\uff0c\u6211\u7559\u4e0b\u7684\u8fd9\u6761\u72d7\u817f\u5927\u6982\u4e5f\u591f\u54b1\u4eec\u4e09 \u4eba\u9971\u9910\u4e00\u987f\u4e86\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fcc\u8bb3\uff1f\u201d \u3000\u3000\u767d\u4e91\u7985\u5e08\u9053\uff1a\u201c\u72d7\u8089\u6211\u5403\u4e86\u51e0\u5341\u5e74\u8fd8\u6709\u4ec0\u4e48\u5fcc\u8bb3\uff1f\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u7b11\u9053\uff1a\u201c\u72d7\u8089\u6211\u6ca1\u6709\u5fcc\u8bb3\uff0c\u4f46\u2018\u72d7\u817f\u5b50\u2019\u6709\u6761\u5fcc\u8bb3\u5427\uff1f\u201d \u3000\u3000\u767d\u4e91\u7985\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u9192\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u5a18\uff0c\u4f60\u8fd9\u73a9\u7b11\u5f00\u5f97\u4e0d\u5927\u9ad8\u660e\u4e86\u3002\u6d12\u5bb6\u82e5\u662f\u72d7 \u817f\u5b50\uff0c\u90a3\u4f60\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u6211\u662f\u5403\u72d7\u817f\u7684\u4eba\u3002\u7b97\u5566\uff0c\u7b97\u5566\uff0c\u548c\u4f60\u5f00\u5f00\u73a9\u7b11\uff0c\u522b\u8fd9\u6837\u8ba4\u771f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5728\u623f\u95f4\u91cc\u542c\u89c1\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u98a4\uff0c\u60f3\u9053\uff1a\u201c\u72d7\u817f\u5b50\u662f\u9e70\u722a\u5b59\u7684\u540c\u610f\u8bed\uff0c \u96be\u9053\u8fd9\u4e24\u4e2a\u51fa\u5bb6\u4eba\u7adf\u7136\u662f\u671d\u5ef7\u7684\u5bc6\u63a2\u4e48\uff1f\u201d\u5728\u4ed6\u4e0b\u5c71\u4e4b\u524d\uff0c\u4ed6\u7684\u5e08\u7236\u662f\u66fe\u7ecf\u548c\u4ed6\u8bf4\u8fc7\u6c5f\u6e56\u4e0a \u6bd4\u8f83\u6709\u540d\u7684\u5404\u53f7\u4eba\u7269\u7684\uff0c\u5e08\u7236\u8bf4\uff0c\u8fbd\u4e1c\u6709\u4e2a\u9a6c\u8d3c\uff0c\u53eb\u505a\u5feb\u9a6c\u9c8d\u4e09\uff0c\u662f\u8fbd\u4e1c\u9ed1\u9053\u4e2d\u6570\u4e00\u6570\u4e8c\u7684 \u4eba\u7269\u3001\u4ed6\u7684\u59bb\u5b50\u6b66\u529f\u6bd4\u4ed6\u66f4\u597d\u3002\u8fd9\u4e2a\u5987\u4eba\u4ed6\u4eec\u53eb\u4ed6\u9c8d\u4e09\u5a18\u5b50\uff0c\u83ab\u975e\u5c31\u662f\u5feb\u9a6c\u9c8d\u4e09\u7684\u59bb\u5b50\u3002 \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u5df2\u7ecf\u628a\u72d7\u817f\u70e4\u719f\uff0c\u767d\u4e91\u7985\u5e08\u548c\u8d64\u677e\u9053\u4eba\u90fd\u80cc\u6709\u4e00\u4e2a\u5927\u846b\u82a6\u3002 \u3000\u3000\u846b\u82a6\u91cc\u90fd\u662f\u76db\u6ee1\u4e86\u9152\u3002\u767d\u4e91\u7985\u5e08\u6495\u5f00\u72d7\u817f\uff0c\u5206\u7ed9\u9c8d\u4e09\u5a18\u5b50\uff0c\u9152\u9999\u8089\u9999\u56db\u6ea2\u3002 \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u5403\u4e0d\u5403\u72d7\u8089\uff1f\u4e0d\u5403\u72d7\u8089\uff0c\u4e5f\u51fa\u6765\u559d\u70b9\u9152\u5427\uff01\u201d\u9c8d\u4e09\u5a18\u5b50\u8bf4\u9053\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u6211\u4e0d\u5403\u72d7\u8089\uff0c\u4e5f\u4e0d\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7537\u5b50\u6c49\u8fde\u9152\u90fd\u4e0d\u4f1a\u559d\uff0c\u771f\u662f\u626b\u5174\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u6765\u966a\u4f60\u4eec\u9ad8\u5174\u5427\uff0c\u6211\u662f\u9152\u4e5f\u559d\u72d7\u8089\u4e5f\u5403\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4ece\u95e8\u7f1d\u91cc\u770b\u51fa\u53bb\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e00\u4e2a\u4e94\u77ed\u8eab\u6750\u7684\u8001\u5934\uff0c\u540e\u9762\u8ddf\u7740\u4e00\u4e2a\u5e74\u7ea6\u4e8c\u5341\u6765\u5c81 \u7684\u5e74\u8f7b\u4eba\uff0c\u8eab\u6750\u9ad8\u7626\u3002\u4e24\u4eba\u7684\u811a\u6b65\u90fd\u8d70\u5f97\u5f88\u8f7b\uff0c\u7a81\u7136\u51fa\u73b0\uff0c\u5982\u540c\u9b3c\u9b45\u3002\u628a\u90a3\u4e09\u4e2a\u4eba\u5413\u4e86\u4e00 \u8df3\u3002 \u3000\u3000\u767d\u4e91\u7985\u5e08\u554a\u5440\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u5411\u8001\u7237\u5b50\uff0c\u8fd9\u53ef\u771f\u662f\u76f8\u8bf7\u4e0d\u5982\u5076\u9047\u4e86\u3002\u8bf7\u5750\uff0c\u8bf7\u5750\uff0c \u6211\u5148\u7ed9\u4f60\u656c\u9152\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u5b50\u9053\uff1a\u201c\u6211\u5728\u5916\u9762\u542c\u89c1\u4f60\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u662f\u4ee5\u672a\u66fe\u5f97\u4e3b\u4eba\u5141\u51c6\uff0c\u5c31\u4e0d\u8bf7\u81ea\u6765 \u4e86\u3002\u201d \u3000\u3000\u8d64\u677e\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u5bb6\u4eba\u5bb6\u5341\u5206\u597d\u5ba2\uff0c\u4e3b\u4eba\u6599\u60f3\u4e5f\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d\u4ed6\u66ff\u4e3b\u4eba\u5bb6\u8bf4\u8bdd\uff0c\u90a3\u8001 \u5a46\u5a46\u4e5f\u4e0d\u77e5\u7761\u7740\u6ca1\u6709\uff0c\u6ca1\u6709\u4f20\u51fa\u58f0\u97f3\u3002\u8fde\u90a3\u8001\u4ec6\u4eba\u4e5f\u6ca1\u51fa\u73b0\u3002 \u3000\u3000\u90a3\u8001\u5934\u5b50\u9053\uff1a\u201c\u4e3b\u4eba\u597d\u5ba2\uff0c\u53ea\u4e0d\u77e5\u9c8d\u4e09\u5a18\u5b50\u5bf9\u6211\u8001\u5934\u513f\u662f\u5426\u6b22\u8fce\uff1f\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u6211\u60f3\u8868\u793a\u6b22\u8fce\uff0c\u5374\u53c8\u4e0d\u6562\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u9053\uff1a\u201c\u54e6\uff0c\u4e3a\u6cb3\u4e0d\u6562\uff1f\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u5411\u8001\u7237\u5b50\uff0c\u4f60\u662f\u4eac\u5e08\u7b2c\u4e00\u5927\u6355\u5934\uff0c\u6211\u600e\u77e5\u4f60\u662f\u4e0d\u662f\u51b2\u7740\u6211\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u4e8e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e09\u5a18\u8bf4\u7b11\u4e86\uff0c\u83ab\u8bf4\u6211\u4e0d\u662f\u51fa\u6765\u529e\u6848\uff0c\u5c31\u7b97\u662f\u4e5f\u4e0d\u6562\u5728\u4f60\u7684\u592a\u5c81\u5934 \u4e0a\u52a8\u571f\u5440\uff01\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u51fa\u6765\u529e\u6848\u7684\uff1f\u6211\u53ef\u4e0d\u6562\u76f8\u4fe1\u3002\u4f60\u5728\u4eac\u5e08\u6b63\u53d7\u91cd\u7528\uff0c\u5018\u82e5\u4e0d\u662f\u6709\u5927 \u6848\u5730\u65b9\u7684\u6355\u5feb\u529e\u4e0d\u4e86\uff0c\u6050\u6015\u4f60\u8001\u4eba\u5bb6\u4e5f\u4e0d\u4f1a\u8fdc\u79bb\u4eac\u5e08\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5728\u623f\u95f4\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u5934\u5b50\u59d3\u5411\uff0c\u83ab\u975e\u5c31\u662f\u5e08\u7236\u66fe\u7ecf\u548c\u6211\u8bf4\u8fc7\u7684\u90a3\u4e2a\u4eac\u5e08\u7b2c \u4e00\u540d\u94fa\u5411\u5929\u51b2\uff1f\u542c\u8bf4\u5e08\u7236\u8bf4\u4ed6\u7684\u6b66\u529f\u867d\u7136\u6bd4\u4e0d\u4e0a\u5b8c\u989c\u957f\u4e4b\uff0c\u4f46\u4e03\u5341\u4e8c\u628a\u5927\u64d2\u62ff\u624b\u6cd5\u4e5f\u7b97\u662f\u6b66 \u6797\u4e00\u7edd\u3002\u4ed6\u8fdc\u79bb\u4eac\u5e08\uff0c\u83ab\u975e\u5c31\u662f\u51b2\u7740\u6211\u8fd9\u4ef6\u6848\u5b50\u6765\u7684\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u8d64\u677e\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u534a\u4e2a\u6708\u524d\uff0c\u6d1b\u9633\u5f52\u4e91\u5e84\u51fa\u4e86\u4e00\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u6848 \u4ef6\uff0c\u6709\u4e2a\u4e0d\u77e5\u6765\u5386\u7684\u5c0f\u5b50\uff0c\u6740\u4e86\u5f52\u4e91\u5e84\u4e3b\u7684\u5ba2\u4eba\uff0c\u8fd9\u4e2a\u5ba2\u4eba\u3001\u542c\u8bf4\u8fd8\u662f\u4ece\u4eac\u5e08\u6765\u7684\u8d35\u4eba\u5462\uff1f \u8fd9\u4e2a\u8d35\u4eba\u7684\u8eab\u4efd\u7aef\u7684\u662f\u975e\u540c\u5c0f\u53ef\u7684\uff01 \u3000\u3000\u5411\u8001\u7237\u5b50\u662f\u6765\u67e5\u529e\u8fd9\u4ef6\u6848\u5b50\u7684\u5427\uff1f\u201d\u4ece\u4ed6\u7684\u53e3\u6c14\u770b\u6765\uff0c\u663e\u7136\u4ed6\u5df2\u7ecf\u77e5\u9053\u90a3\u4e2a\u201c\u4ece\u4eac\u5e08\u6765 \u7684\u8d35\u4eba\u201d\u662f\u4ec0\u4e48\u4eba\u7684\u4e86\uff0c\u4e0d\u8fc7\u4e0d\u6562\u8bf4\u51fa\u6765\u800c\u5df2\u3001\u5411\u5929\u51b2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8bf4\u8fc7\u6211\u4e0d\u662f\u51fa\u6765\u529e\u6848 \u7684\uff0c\u7ba1\u5b83\u60ca\u5929\u4e5f\u597d\uff0c\u52a8\u5730\u4e5f\u597d\uff0c\u90fd\u4e0e\u6211\u65e0\u5173\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u5373\u4f7f\u4f60\u771f\u7684\u4e0d\u662f\u51fa\u6765\u529e\u6848\uff0c\u4f60\u603b\u8fd8\u662f\u4eac\u5e08\u5e94\u5929\u5e9c\u8859\u95e8\u91cc\u7684\u603b\u6355\u5934\u5427\uff1f\u5916 \u5730\u51fa\u4e86\u4e00\u4ef6\u548c\u4eac\u5e08\u8d35\u4eba\u6709\u5173\u7684\u5927\u6848\u4ef6\uff0c\u600e\u80fd\u8bf4\u4e0e\u4f60\u65e0\u5173\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u5404\u4f4d\u6709\u6240\u4e0d\u77e5\uff0c\u4e0a\u4e2a\u6708\u6211\u5df2\u7ecf\u544a\u8001\u9000\u4f11\u4e86\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u8859\u95e8\u8bb8\u4f60\u9000\u4f11\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u516d\u5341\u4e09\u5c81\u4e86\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u83ab\u8bf4\u5411\u8001\u7237\u5b50\u8fd8\u662f\u8001\u5f53\u76ca\u58ee\uff0c\u5373\u4f7f\u4f60\u8dd1\u4e0d\u52a8\u4e86\uff0c\u6709\u4f60\u5750\u9547\u4eac\u5e08\uff0c\u563f\u563f\uff0c \u6211\u9c8d\u4e09\u5a18\u5b50\u5c31\u4e0d\u6562\u5728\u4eac\u5e08\u72af\u6848\u3002\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u591a\u8c22\u4e09\u59d0\u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u80fd\u591f\u5728\u4eac\u5e08\u6df7\u51e0\u5341\u5e74\u516c\u95e8\u996d\u5403\uff0c\u4fa5 \u5e78\u6ca1\u683d\u7b4b\u6597\uff0c\u4e5f\u662f\u591a\u4e8f\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\u7ed9\u6211\u9762\u5b50\u7684\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u7ee7\u4efb\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u662f\u6211\u7684\u526f\u624b\u6c99\u8001\u4e09\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u6c99\u8001\u4e09\u7ec3\u7684\u94c1\u7802\u638c\u867d\u7136\u4e0d\u9519\uff0c\u6bd4\u8d77\u8001\u7237\u5b50\u53ef\u5dee\u5f97\u592a\u8fdc\u4e86\u3002\u8bba\u5a01\u671b\u3001\u8bba\u6b66 \u529f\uff0c\u6028\u6211\u76f4\u8a00\uff0c\u6050\u6015\u4ed6\u5728\u4eac\u5e08\u90fd\u9547\u4e0d\u4f4f\u5427\uff0c\u4ed6\u600e\u6562\u63a5\u4f60\u8fd9\u603b\u6355\u5934\u4e4b\u804c\uff1f\u201d\u5411\u5929\u51b2\u9053\uff1a\u201c\u4e09\u4f4d \u90fd\u662f\u548c\u6211\u6709\u591a\u5e74\u4ea4\u60c5\u7684\u670b\u53cb\uff0c\u6211\u4e5f\u4e0d\u6015\u5bf9\u4f60\u4eec\u8bf4\u5b9e\u8bdd\uff0c\u6c99\u8001\u4e09\u7684\u786e\u662f\u672c\u6765\u4e0d\u6562\u63a5\u4efb\u7684\uff0c\u6211\u628a \u6211\u8fd9\u5e08\u4f84\u63a8\u8350\u7ed9\u4ed6\uff0c\u4ed6\u624d\u6562\u7b54\u5e94\u7684\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u90a3\u5c11\u5e74\u4ecb\u7ecd\u7ed9\u9c8d\u4e09\u5a18\u5b50\u7b49\u4eba\uff0c\u4ed6\u4eec\u624d\u77e5\u9053\u8fd9 \u5c11\u5e74\u7684\u59d3\u540d\u53eb\u94c1\u4e00\u7b14\u3002 \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u94c1\u4e00\u7b14\uff0c\u8fd9\u540d\u5b57\u5012\u5f88\u6709\u610f\u601d\uff0c\u662f\u4ee4\u5e08\u7ed9\u4f60\u6539\u540d\u7684\u5427\uff1f \u3000\u3000\u201d \u3000\u3000\u5411\u5929\u51b2\u4ee3\u4ed6\u56de\u7b54\uff1a\u201c\u4e0d\u9519\uff0c\u655d\u5e08\u662f\u53ea\u6709\u4ed6\u4e00\u4e2a\u5f1f\u5b50\uff0c\u5e0c\u671b\u4ed6\u80fd\u591f\u6210\u4e3a\u81ea\u5df1\u7684\u8863\u94b5\u4f20\u4eba\uff0c \u6545\u6b64\u7ed9\u4ed6\u6539\u4e86\u8fd9\u4e2a\u540d\u5b57\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\u3002\u4f60\u7684\u53cc\u7b14\u70b9\u56db\u8109\u7684\u529f\u592b\u60f3\u5fc5\u5df2\u7ecf\u7ec3\u6210\u4e86\uff1f \u3000\u3000\u201d\u5979\u9762\u5411\u94c1\u4e00\u7b14\u53d1\u95ee\u3002 \u3000\u3000\u94c1\u4e00\u7b14\u4ecd\u7136\u6ca1\u4f5c\u58f0\uff0c\u53ea\u662f\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u201c\u5484\u201d\u5b57\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u54d1\u5df4\u5427\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u4e09\u5a18\u5b50\u83ab\u602a\u4ed6\uff0c\u4ed6\u751f\u6027\u4e0d\u559c\u6b22\u8bf4\u8bdd\u7684\u3002\u53cc\u7b14\u70b9\u56db\u8109\u7684\u7b14\u6cd5\u7e41\u590d\u5f02\u5e38\uff0c\u8bf4\u5230 \u7ec3\u6210\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u5f53\u5e74\u6211\u5c31\u662f\u81ea\u77e5\u7b28\u62d9\uff0c\u4e0d\u6562\u8d2a\u591a\u9a9b\u5f97\uff0c\u653e\u5f03\u8fd9\u5957\u7b14\u6cd5\u4e0d\u7ec3\uff0c\u53ea\u7ec3\u5927\u64d2\u62ff\u624b \u7684\u3002\u4ed6\u73b0\u5728\u5927\u6982\u53ea\u7ec3\u6210\u4e86\u4e09\u7b14\u70b9\u4e24\u8109\u7684\u529f\u592b\u3002\u201d\u539f\u6765\u5411\u5929\u51b2\u7684\u5e08\u5144\u76c2\u5929\u6e38\u4e43\u662f\u4ee5\u5224\u5b98\u7b14\u70b9\u7a74 \u7684\u5927\u540d\u5bb6\uff0c\u4ed6\u7684\u201c\u53cc\u7b14\u70b9\u56db\u8109\u201d\u529f\u592b\u582a\u79f0\u6b66\u6797\u4e00\u7edd\u3002 \u3000\u3000\u6b64\u65f6\u5927\u96e8\u5df2\u7ecf\u6b62\u4e86\uff0c\u5ffd\u53c8\u542c\u5f97\u6709\u6572\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u51fa\u53bb\u5f00\u95e8\uff0c\u6765\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u6765\u8fdf\u4e86\u2014\u2014\u201d\u4f46\u53ea\u8bf4\u4e86\u534a\u53e5\uff0c\u8bed\u97f3\u4fbf\u5373\u621b\u7136\u800c\u6b62\u3002 \u539f\u6765\u4ed6\u5df2\u8e0f\u8fdb\u5927\u95e8\uff0c\u770b\u89c1\u91cc\u9762\u7684\u60c5\u5f62\u4e86\u3002 \u3000\u3000\u6765\u7684\u662f\u4e2a\u5c11\u5e74\u519b\u5b98\u3002 \u3000\u3000\u4ed6\u89c1\u5ba2\u5385\u4e0a\u6709\u7537\u6709\u5973\u3001\u6709\u8001\u6709\u5c11\uff0c\u6709\u548c\u5c1a\u53c8\u6709\u9053\u58eb\uff0c\u4e0d\u89c9\u6709\u4e9b\u8be7\u5f02\uff0c\u7709\u5934\u7565\u76b1\uff0c\u8bf4\u9053\uff1a \u201c\u554a\uff0c\u8fd9\u4e48\u591a\u5ba2\u4eba\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u8d64\u677e\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4e0d\u662f\u4ec0\u4e48\u5ba2\u4eba\u7684\u8eab\u4efd\uff0c\u53ea\u56e0\u907f\u96e8\uff0c\u4e0d\u7ea6\u800c\u540c\u8d70\u5230\u8fd9\u91cc\u6765 \u7684\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8bf8\u4f4d\u662f\u5e76\u4e0d\u76f8\u8bc6\u7684\u5417\uff1f\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u957f\u5b98\u67e5\u95ee\uff0c\u6211\u4eec\u4e0d\u6562\u4e0d\u8bf4\u5b9e\u8bdd\u3002\u76f8\u8bc6\u6211\u4eec\u5012\u662f\u672c\u6765\u76f8\u8bc6\uff0c\u4e0d\u8fc7\uff0c\u5e76\u975e\u4e8b \u524d\u7ea6\u4f1a\u3002\u8fd9\u4f4d\u8001\u7237\u5b50\u662f\u4eac\u5e08\u603b\u6355\u5934\uff0c\u6211\u548c\u4ed6\u76f8\u8bc6\u4e5f\u6709\u5341\u591a\u5e74\u4e86\u3002\u201d\u5f26\u5916\u4e4b\u97f3\uff0c\u6709\u603b\u6355\u5934\u4f5c \u4fdd\uff0c\u8fd9\u519b\u5b98\u5927\u53ef\u4e0d\u5fc5\u6000\u7591\u4ed6\u4eec\u6765\u8def\u4e0d\u6b63\u3002\u5979\u662f\u6599\u51c6\u4e86\u5411\u5929\u51b2\u4e0d\u6562\u6296\u9732\u51fa\u5979\u662f\u9ed1\u9053\u4eba\u7269\u7684\u3002 \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4eac\u5e08\u7b2c\u4e00\u540d\u6355\u5411\u8001\u524d\u8f88\u3002\u5931\u656c\u5931\u656c\u3002\u5411\u603b\u6355\u5934\u662f\u51fa\u6765\u529e\u6848\u7684 \u5417\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u6211\u4e0a\u4e2a\u6708\u5df2\u7ecf\u544a\u8001\u9000\u4f11\u4e86\u3002\u5b98\u957f\u662f\u2014\u2014\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6211\u4e5f\u5e76\u975e\u56e0\u516c\u4e8b\u51fa\u5dee\u3002\u6211\u662f\u6765\u63a2\u4eb2\u7684\u3002\u201d\u4ed6\u672c\u6765\u65e0\u987b\u8bf4\u660e\u81ea\u5df1\u7684\u6765\u610f\u7684\uff0c \u53ea\u56e0\u4ed6\u4e0d\u613f\u548c\u8fd9\u4e9b\u4eba\u6df7\u5728\u4e00\u8d77\u3002\u8fd9\u624d\u8bf4\u660e\u4e00\u4e0b\uff0c\u4ee5\u514d\u8fd9\u4e9b\u4eba\u6709\u4e3b\u4eba\u5bb6\u539a\u6b64\u7c3f\u5f7c\u7684\u611f\u89c9\u3002\u56e0\u4e3a \u90a3\u8001\u4ec6\u4eba\u6b63\u5728\u51c6\u5907\u5e26\u9886\u4ed6\u8fdb\u5165\u5185\u9662\u5b89\u6b47\u3002 \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u5ffd\u9053\uff1a\u201c\u5b98\u957f\u3001\u4f60\u5403\u4e0d\u5403\u72d7\u8089\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u6211\u8dd1\u4e86\u4e00\u6574\u5929\u8def\uff0c\u73b0\u5728\u53ea\u60f3\u5b89\u5b89\u9759\u9759\u7684\u7761\u4e00\u5927\u89c9\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4ece\u95e8\u7f1d\u91cc\u671b\u51fa\u53bb\uff0c\u5ffd\u7136\u89c9\u5f97\u8fd9\u4e2a\u519b\u5b98\u4f3c\u66fe\u76f8\u8bc6\uff0c\u60f3\u4e86\u597d\u4e00\u4f1a\uff0c\u65b9\u59cb\u60f3\u4e86\u8d77\u6765\uff0c\u539f \u6765\u8fd9\u4e2a\u519b\u5b98\u7684\u76f8\u8c8c\u6709\u70b9\u50cf\u4ed6\u5e08\u7236\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u81ea\u79f0\u59d3\u8fde\u7684\u5c11\u5e74\uff0c\u76f8\u8c8c\u9177\u4f3c\u8d6b\u8fde\u6e05\u6ce2\uff0c\u8fd9\u4e2a\u5c11\u5e74\u519b\u5b98\u53c8\u4f3c\u6211\u7684\u5e08\u7236\uff0c\u5012\u771f\u662f\u65e0\u72ec \u6709\u5076\uff0c\u53ef\u79f0\u5947\u9047\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u519b\u5b98\u53ea\u662f\u4e24\u4e09\u5206\u76f8\u4f3c\u800c\u5df2\u3002\u8fd8\u6ca1\u6709\u90a3\u59d3\u8fde\u7684\u5c11\u5e74\u548c\u6e05\u6ce2\u76f8\u4f3c \u4e4b\u751a\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df1\uff0c\u5ffd\u542c\u5f97\u9c8d\u4e09\u5a18\u5b50\u51b7\u73b2\u8bf4\u9053\uff1a\u201c\u5b98\u67b6\u5b50\u5012\u662f\u4e0d\u5c0f\uff0c\u4f60\u4eec\u542c\u51fa\u6765\u6ca1\u6709\uff0c\u8fd9\u5b98\u513f\u597d \u50cf\u662f\u8981\u66ff\u4e3b\u4eba\u9010\u5ba2\u4ee4\u5462\uff01\u201d\u5411\u5929\u51b2\u9053\uff1a\u201c\u4e09\u5a18\uff0c\u662f\u4f60\u591a\u5fc3\u5427\uff1f\u6211\u770b\u4ed6\u5012\u662f\u76f8\u5f53\u968f\u548c\u7684\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u968f\u548c\uff1f\u4f60\u6ca1\u542c\u89c1\u4ed6\u8bf4\u6211\u53ea\u60f3\u5b89\u5b89\u9759\u9759\u7684\u7761\u4e00\u5927\u89c9\u5417\uff1f\u90a3\u8fd8\u4e0d\u662f\u5206\u660e \u8ba8\u538c\u54b1\u4eec\u8fd9\u73ed\u6076\u5ba2\u5728\u8fd9\u91cc\u55a7\u95f9\uff1f\u201d \u3000\u3000\u8d64\u677e\u7b11\u9053\uff1a\u201c\u7ba1\u4ed6\u559c\u6b22\u4e0d\u559c\u6b22\uff0c\u96be\u9053\u4f60\u9c8d\u4e09\u5a18\u4e86\u8fd8\u4f1a\u5bb3\u6015\u4e00\u4e2a\u5c0f\u5b98\u513f\u4e0d\u6210\uff1f\u201d \u3000\u3000\u767d\u4e91\u7985\u5e08\u9053\uff1a\u201c\u6050\u6015\u4e0d\u662f\u4e00\u4e2a\u5c0f\u5b98\u513f\u5462\uff01\u201d \u3000\u3000\u8d64\u677e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u767d\u4e91\u7985\u5e08\u9053\uff1a\u201c\u5c0f\u5b98\u513f\u6ca1\u6709\u8fd9\u6837\u6c14\u6d3e\u7684\u3002\u800c\u4e14\u6211\u6709\u4e00\u4e2a\u5947\u602a\u7684\u611f\u89c9\uff0c\u89c9\u5f97\u4ed6\u81ea\u7136\u800c\u7136\u4f3c \u4e4e\u6709\u4e00\u79cd\u9ad8\u8d35\u7684\u6c14\u5ea6\u3002\u201d\u8d64\u677e\u51b7\u7b11\u9053\uff1a\u201c\u5373\u4f7f\u4ed6\u662f\u5fae\u670d\u51fa\u5de1\u7684\u5927\u5b98\uff0c\u54b1\u4eec\u4e5f\u4e0d\u7528\u5bb3\u6015\u4ed6 \u5427\uff1f\u201d\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u6837\u8bf4\uff0c\u5373\u4f7f\u4e0d\u662f\u5bfb\u5e38\u767e\u59d3\uff0c\u4ed6\u4e5f\u662f\u4e3b\u4eba\u5bb6\u7684\u4eb2\u621a\u3002\u4e0d\u559c\u6b22\u54b1 \u4eec\uff0c\u54b1\u4eec\u53c8\u4f55\u5fc5\u60f9\u4eba\u8ba8\u538c\uff1f\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u5411\u8001\u7237\u5b50\uff0c\u4f60\u662f\u4eac\u5e08\u7684\u603b\u6355\u5934\uff0c\u867d\u7136\u4e0d\u662f\u638c\u6b63\u5370\u7684\u5b98\u513f\uff0c\u4f46\u6709\u804c \u6709\u6743\uff0c\u7b49\u95f2\u7684\u5b98\u513f\u8fd8\u662f\u8981\u5949\u627f\u4f60\u5462\u3002\u4fd7\u8bed\u8bf4\u5b98\u5b98\u76f8\u62a4\uff0c\u4f60\u548c\u90a3\u4e2a\u5b98\u513f\u600e\u4e48\u80fd\u4e0d\u7b97\u662f\u81ea\u5df1 \u4eba\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u4e0d\u662f\u5c5e\u4e8e\u5b98\u573a\u7684\u4e86\uff0c\u4e09\u5a18\uff0c\u4f60\u600e\u4e48\u8fd8\u8bf4\u8fd9\u6837\u7684\u8bdd\uff1f\u8bf4\u771f\u7684\uff0c\u6211\u5168\u662f \u60f3\u4f60\u4eec\u628a\u6211\u4eec\u5f53\u6210\u81ea\u5df1\u4eba\u5462\u3002\u201d \u3000\u3000\u9c8d\u4e09\u5a18\u5b50\u9053\uff1a\u201c\u5411\u8001\u7237\u5b50\uff0c\u4f60\u82e5\u771f\u7684\u662f\u80af\u628a\u6211\u5f53\u4f5c\u81ea\u5df1\u4eba\uff0c\u6211\u53ef\u662f\u6c42\u4e4b\u4e0d\u5f97\u4e86\u3002\u8bf4\u8001\u5b9e \u8bdd\uff0c\u6709\u4f60\u8fd9\u6837\u4e00\u4e2a\u4eac\u5e08\u540d\u6355\u5728\u6211\u8eab\u65c1\uff0c\u6211\u603b\u662f\u6709\u70b9\u63d0\u5fc3\u540a\u80c6\u3002\u8981\u662f\u4f60\u628a\u6211\u5f53\u4f5c\u81ea\u5df1\u4eba\uff0c\u6211\u505a \u6848\u7684\u65f6\u5019\uff0c\u5c31\u4e0d\u6015\u4f60\u6765\u6349\u62ff\u6211\u4e86\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u5411\u8001\u7237\u5b50\uff0c\u4f60\u4e0d\u6015\u6211\u73b0\u5728\u5c31\u662f\u51fa\u53bb\u505a\u6848\u5417\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u9c8d\u4e09\u5a18\u5b50\uff0c\u4f60\u662f\u51fa\u4e86\u540d\u7684\uff0c\u51e4\u51f0\u65e0\u5b9d\u4e0d\u843d\u3002\u563f\u563f\uff0c\u5728\u8fd9\u8352 \u6751\u50fb\u91ce\u505a\u6848\uff1f\u53ea\u6015\u4f60\u534a\u70b9\u6cb9\u6c34\u4e5f\u635e\u4e0d\u5230\uff0c\u90a3\u65f6\uff0c\u4e0d\u662f\u4f60\u548c\u6211\u8fd9\u8001\u5934\u5b50\u5f00\u73a9\u7b11\uff0c\u662f\u4f60\u81ea\u5df1\u548c\u81ea \u5df1\u5f00\u73a9\u7b11\u4e86\u201d \u3000\u3000\u4e24\u4eba\u90fd\u662f\u8bed\u5e26\u53cc\u5173\uff0c\u9c8d\u4e09\u5a18\u5b50\u8fd9\u4e00\u4f19\u5c31\u5728\u563b\u563b\u54c8\u54c8\u58f0\u4e2d\uff0c\u5f00\u95e8\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u8001\u4ec6\u4eba\u4e5f\u4e0d\u77e5\u7761\u4e86\u6ca1\u6709\uff0c\u5e76\u6ca1\u51fa\u6765\u9001\u5ba2\u3002 \u3000\u3000\u5411\u5929\u51b2\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u4e0d\u4e45\u53d1\u51fa\u9f3e\u58f0\u3002\u94c1\u4e00\u7b14\u4ecd\u662f\u7b14\u76f4\u7ad9\u5728\u4ed6\u7684\u540e\u9762\uff0c\u76f8\u7ee7\u4e5f\u53d1\u51fa\u9f3e \u58f0\u3002\u6a80\u7fbd\u51b2\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u80fd\u591f\u7ad9\u7740\u7761\u89c9\uff1b\u5012\u4e5f\u662f\u4e00\u6869\u96be\u7ec3\u7684\u672c\u4e8b\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u5730\u9690\u9690\u542c\u5f97\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u6a80\u7fbd\u51b2\u5fc3\u5934\u4e00\u52a8\uff0c\u5fd9\u628a\u706f\u7184\u706d\uff0c\u4e5f\u88c5\u4f5c\u719f\u7761\uff0c \u53d1\u51fa\u9f3e\u58f0\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u90a3\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\u4ece\u4ed6\u8fd9\u95f4\u5367\u623f\u7684\u5c4b\u9876\u63a0\u8fc7\uff0c\u8fc5\u5373\u6d88\u5931\u3002\u82e5\u4e0d\u662f\u6a80\u7fbd\u51b2\u7684\u5185\u529f \u5df2\u6709\u5f88\u6df1\u7684\u9020\u8be3\uff0c\u542c\u89c9\u5927\u5f02\u5e38\u4eba\uff0c\u7edd\u96be\u5bdf\u89c9\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8f7b\u529f\u9ad8\u660e\u4e4b\u6781\uff0c\u6050\u6015\u53ea\u6709\u5728\u6211\u4e4b\u4e0a\uff0c\u51b3\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u4e0d\u7528\u8bf4\u4ed6 \u662f\u6765\u63a2\u6211\u7684\u52a8\u9759\u7684\u4e86\u3002\u53ea\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u90a3\u4e2a\u519b\u5b98\u8fd8\u662f\u4e2a\u81ea\u79f0\u59d3\u8fde\u7684\u5c11\u5e74\uff1f\u201d \u3000\u3000\u4ed6\u597d\u5947\u5fc3\u8d77\uff0c\u5f85\u90a3\u591c\u884c\u4eba\u8fc7\u53bb\u4e4b\u540e\uff0c\u6084\u6084\u8d77\u6765\uff0c\u4e5f\u65bd\u5c55\u8f7b\u529f\uff0c\u5230\u540e\u9662\u7aa5\u63a2\u3002\u4ed6\u4ee5\u4e0a\u4e58\u5185 \u529f\uff0c\u95ed\u4e86\u547c\u5438\uff0c\u4ee4\u5bf9\u65b9\u4e00\u70b9\u58f0\u606f\u90fd\u542c\u4e0d\u5230\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u5728\u4e00\u95f4\u623f\u7684\u540e\u7a97\u505c\u4e0b\u3001\u8f7b\u8f7b\u5f39\u4e86\u4e00\u5f39\uff0c\u540e\u5bcc\u5c31\u6253\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u90fd\u7761\u7740\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8bc1\u4e86\u4e00\u6014\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u600e\u4e48\u7a81\u7136\u53c8\u591a\u4e86\u4e00\u4e2a\u5973\u5b50\uff1f\u201d\u8981\u77e5\u9c8d\u4e09\u5a18\u5b50\u5df2\u7ecf\u8d70\u4e86\uff0c \u8fd9\u5bb6\u4eba\u552f\u4e00\u7684\u5973\u6027\u5c31\u662f\u90a3\u4e2a\u4ece\u6765\u672a\u9732\u8fc7\u9762\u7684\u8001\u5a46\u5a46\uff0c\u4f46\u542c\u8fd9\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u7edd\u5bf9\u4e0d\u662f\u8001\u5a46 \u5a46\u3002\u66f4\u5947\u602a\u7684\u662f\uff0c\u8fd9\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u6a80\u7fbd\u51b2\u4e5f\u597d\u50cf\u201c\u4f3c\u66fe\u76f8\u8bc6\u201d\u3002 \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u9c8d\u4e09\u5a18\u548c\u90a3\u548c\u5c1a\u9053\u4e0a\u90fd\u5df2\u7ecf\u8d70\u4e86\u3002\u5411\u5929\u51b2\u548c\u4ed6\u7684\u5e08\u4f84\u5df2\u7ecf\u719f\u7761\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u5df2\u7ecf\u719f\u7761\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u6211\u542c\u89c1\u4ed6\u4eec\u7684\u9f3e\u58f0\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u5411\u5929\u51b2\u662f\u4eac\u5e08\u7684\u7b2c\u4e00\u540d\u6355\uff0c\u804c\u4e1a\u7684\u4e60\u60ef\u4e5f\u4f1a\u975e\u5e38\u201c\u9192\u7761\u201d\u7684\uff0c\u6211\u4e0d\u76f8\u4fe1\u4ed6 \u5728\u7761\u89c9\u7684\u65f6\u5019\u4f1a\u53d1\u51fa\u9f3e\u58f0\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u5411\u5929\u51b2\u662f\u5728\u738b\u5e9c\u89c1\u8fc7\u6211\u7684\uff0c\u8c05\u4ed6\u4e5f\u4e0d\u4f1a\u6000\u7591\u5230\u6211\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u5374\u6015\u4ed6\u662f\u51b2\u7740\u6211\u6765\u7684\u5462\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u8981\u662f\u4ed6\u5f53\u771f\u6562\u6765\uff0c\u6211\u5e2e\u4f60\u5bf9\u4ed8\u4ed6\u5c31\u662f\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u4ed6\uff0c\u4f46\u4e0d\u60f3\u5728\u8fd9\u91cc\u95f9\u4e86\u4e8b\u6765\u3002\u800c\u4e14\u8fd8\u6709\u90a3\u59d3\u6a80\u7684\u5c11\u5e74\u2014\u2014\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u90a3\u59d3\u6a80\u7684\u5c11\u5e74\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f9d\u6211\u770b\uff0c\u90a3\u59d3\u6a80\u5c11\u5e74\uff0c\u6b66\u529f\u53ea\u6015\u8fd8\u5728\u9c8d\u4e09\u5a18\u5b50\u548c\u4f55\u5929\u51b2\u8fd9\u4e9b\u4eba\u4e4b\u4e0a\u3002\u4ed6\u884c \u52a8\u8be1\u79d8\uff0c\u6211\u6709\u70b9\u6000\u7591\u4ed6\u662f\u6697\u5730\u8ffd\u8e2a\u6211\u7684\u3002\u201d \u3000\u3000\u81f3\u6b64\u5904\uff0c\u6a80\u7fbd\u51b2\u65b9\u624d\u604d\u7136\u5927\u609f\uff0c\u201c\u539f\u6765\u90a3\u4e2a\u6027\u8fde\u7684\u5c11\u5e74\u679c\u7136\u662f\u5973\u626e\u7537\u88c5\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e5f\u5df1\u7761\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u5b9c\u54e5\uff0c\u4f60\u7684\u672c\u9886\u663e\u7136\u6bd4\u6211\u9ad8\uff0c\u6c5f\u6e56\u7ecf\u9a8c\u6050\u6015\u5c31\u4e0d\u53ca\u6211\u4e86\u3002\u600e\u80fd\u542c\u89c1\u9f3e\u58f0\u3002 \u5c31\u4ee5\u4e3a\u522b\u4eba\u5df2\u7ecf\u719f\u7761\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u4ed6\u82e5\u662f\u88c5\u7761\uff0c\u6211\u4e5f\u53ef\u4ee5\u53eb\u4ed6\u719f\u7761\u7684\u3002\u4f60\u7b49\u4e00\u7b49\uff0c\u6211\u56de\u53bb\u70b9\u4e86\u4ed6 \u7684\u7a74\u9053\u518d\u6765\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u53ef\u9c81\u83bd\u3002\u8fd9\u5c0f\u5b50\u7684\u6b66\u529f\u6050\u6015\u53ea\u6709\u5728\u4f60\u4e4b\u4e0a\uff0c\u7edd\u4e0d\u5728\u4f60\u4e4b\u4e0b\u3002\u95f9\u51fa\u4e8b\u6765\uff0c \u66f4\u52a0\u4e0d\u5999\u3002\u201d \u3000\u3000\u519b\u5b98\u534a\u4fe1\u534a\u7591\uff0c\u4f46\u4ed6\u4e5f\u786e\u5b9e\u4e0d\u60f3\u6253\u8349\u60ca\u86c7\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u7684\u6c5f\u6e56\u7ecf\u9a8c\u6bd4\u6211\u4e30\u5bcc\uff0c\u90a3\u4f60\u8bf4 \u5427\uff0c\u54b1\u4eec\u5e94\u8be5\u600e\u6837\u505a\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u53e6\u5916\u627e\u4e2a\u8bf4\u8bdd\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u519b\u5b98\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5230\u540e\u5c71\u7684\u6811\u6797\u91cc\u3002\u201d \u3000\u3000\u6b63\u5f53\u4ed6\u62ff\u5b9a\u4e3b\u610f\uff0c\u51c6\u5907\u7ee7\u7eed\u8ddf\u8e2a\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\u3002 \u3000\u3000\u90a3\u4eba\u6765\u5f97\u597d\u5feb\uff0c\u6a80\u7fbd\u51b2\u521a\u521a\u5bdf\u89c9\u4e0d\u5999\uff0c\u767b\u65f6\u5c31\u7ed9\u90a3\u4eba\u6293\u7740\u3002\u90a3\u4eba\u4e24\u53ea\u624b\u81c2\u597d\u50cf\u94c1\u94b3\u4e00 \u6837\uff0c\u7adf\u7136\u94b3\u5f97\u4ed6\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u80fd\u591f\u52a8\u5f39\u7684\u3002\u4ed6\u7ec3\u6709\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u53cd\u5e94\u6781\u5feb\u3002 \u3000\u3000\u4e0d\u80fd\u52a8\u5f39\u53ea\u4e0d\u8fc7\u5229\u90a3\u95f4\u4e8b\uff0c\u5185\u529b\u4e00\u5230\uff0c\u767b\u65f6\u5c31\u628a\u90a3\u4eba\u5f39\u5f00\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e5f\u8fd8\u672a\u6765\u5f97\u53ca\u53cd\u51fb\uff0c\u521a\u60f3\u56de\u8fc7\u5934\u6765\uff0c\u8eab\u5f62\u672a\u8d77\uff0c\u53c8\u7ed9\u53e6\u4e00\u4e2a\u4eba\u70b9\u7740\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u4eba\u70b9\u7a74\u7684\u624b\u6cd5\u53c8\u5feb\u53c8\u51c6\uff0c\u9ed1\u6697\u4e2d\u8ba4\u7a74\u4e0d\u5dee\u6beb\u5398\uff0c\u800c\u4e14\u662f\u5728\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u70b9\u7740\u4e86\u4ed6\u4e09 \u5904\u4e0d\u540c\u7ecf\u8109\u7684\u7a74\u9053\u3002\u4e24\u5904\u662f\u9ebb\u7a74\uff0c\u4e00\u5904\u662f\u7761\u7a74\u3002\u6a80\u7fbd\u51b2\u5012\u5728\u5730\u4e0a\uff0c\u8fd9\u56de\u53ef\u771f\u662f\u4e0d\u80fd\u52a8\u5f39\u4e86\u3002 \u3000\u3000\u867d\u7136\u4e0d\u80fd\u52a8\u5f39\uff0c\u5fc3\u4e2d\u5374\u662f\u660e\u767d\u3002\u4ece\u4e24\u4eba\u7684\u624b\u6cd5\uff0c\u4ed6\u77e5\u9053\u7b2c\u4e00\u4e2a\u6765\u6293\u4ed6\u7684\u4eba\u5fc3\u5b9a\u662f\u4eac\u5e08\u7b2c \u4e00\u540d\u6355\u5411\u5929\u51b2\uff0c\u7b2c\u4e8c\u4e2a\u6765\u70b9\u4ed6\u7a74\u9053\u7684\u4eba\u5219\u662f\u5411\u5929\u51b2\u7684\u5e08\u4f84\u94c1\u4e00\u7b14\u3002 \u3000\u3000\u4ee5\u6b66\u529f\u800c\u8bba\uff0c\u4ed6\u672c\u662f\u7edd\u4e0d\u4f1a\u8f93\u7ed9\u8fd9\u5bf9\u5e08\u4f84\u7684\uff0c\u53ea\u56e0\u4ed6\u5168\u795e\u8d2f\u6ce8\uff0c\u653e\u5728\u90a3\u4e2a\u519b\u5b98\u8eab\u4e0a\uff0c\u8fd9 \u624d\u51b7\u4e0d\u9632\u7740\u4e86\u9053\u513f\uff0c\u552f\u6709\u81ea\u53f9\u5012\u9709\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u5411\u5929\u51b2\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u6b66\u529f\u597d\u5f97\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff0c\u94c1 \u5e08\u4f84\uff0c\u5e78\u4e8f\u4f60\u51fa\u624b\u5f97\u5feb\uff0c\u5426\u5219\u6015\u5f53\u771f\u5236\u4ed6\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u94c1\u4e00\u7b14\u6697\u6697\u53eb\u4e86\u4e00\u53e5\u201c\u4fa5\u5e78\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u5e08\u53d4\u7684\u5927\u64d2\u62ff\u624b\u6293\u7740\u4e86\u4ed6\u3002\u6211\u600e\u80fd\u70b9\u4e2d \u4ed6\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u6211\u770b\u8fd9\u5bb6\u4eba\u5bb6\u6709\u70b9\u53e4\u602a\uff0c\u8d81\u4ed6\u4eec\u8fd8\u672a\u53d1\u89c9\uff0c\u54b1\u4eec\u8d76\u5feb\u8d70\u5427\u3002\u201d\u8bf4\u7f62\u56de\u8fc7\u5934 \u6765\uff0c\u8e22\u6a80\u7fbd\u51b2\u4e00\u811a\uff0c\u6a80\u7fbd\u51b2\u88c5\u4f5c\u5df2\u7ecf\u660f\u7761\u3002\u7ffb\u4e86\u4e2a\u8eab\uff0c\u4ecd\u7136\u76f4\u633a\u7684\u8eba\u5728\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u94c1\u4e00\u7b14\u9053\uff1a\u201c\u5e08\u53d4\u653e\u5fc3\uff0c\u8fd9\u5c0f\u5b50\u5df2\u7ecf\u662f\u7ed9\u6211\u70b9\u4e2d\u4e86\u4e24\u5904\u9ebb\u7a74\uff0c\u4e00\u4e2a\u7761\u7a74\u7684\u3002\u5373\u4f7f\u4ed6\u660e\u5929 \u9192\u6765\uff0c\u6050\u6015\u4e5f\u8fd8\u5f97\u5927\u534a\u5929\u624d\u80fd\u8d70\u8def\u3002\u201d \u3000\u3000\u5411\u5929\u51b2\u8e22\u4e86\u6a80\u7fbd\u51b2\u4e09\u811a\uff0c\u7b11\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u4e5f\u592a\u7231\u7ba1\u95f2\u4e8b\u4e86\uff0c\u597d\u597d\u7761\u4e00\u89c9\u5427\uff0c\u8fc7\u4e86\u5341\u4e8c \u4e2a\u65f6\u8fb0\uff0c\u4f60\u7684\u7a74\u9053\u81ea\u89e3\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u4e2d\u51b7\u7b11\uff1a\u201c\u4f60\u4eec\u4e5f\u672a\u514d\u81ea\u89c6\u8fc7\u9ad8\u4e86\uff0c\u4ee5\u4e3a\u70b9\u4e2d\u4e86\u6211\u4e09\u5904\u7a74\u9053\uff0c\u6211\u5c31\u53ef\u4ee5\u4efb\u51ed\u4f60 \u4eec\u6446\u5e03\uff1f\u54fc\uff0c\u7b49\u4f1a\u89c1\u518d\u548c\u4f60\u4eec\u7b97\u8fd9\u4e00\u7b14\u5e10\u201d \u3000\u3000\u5411\u5929\u51b2\u548c\u5e08\u4efb\u4e00\u8d70\uff0c\u6a80\u7fbd\u51b2\u5c31\u81ea\u884c\u8fd0\u6c14\u51b2\u5173\uff0c\u628a\u4e09\u5904\u88ab\u5c01\u7684\u7a74\u9053\u90fd\u89e3\u5f00\u4e86\u3002\u4ed6\u8fdb\u5165\u6811 \u6797\uff0c\u521a\u597d\u542c\u5f97\u5411\u5929\u51b2\u9053\uff1a\u201c\u5509\uff0c\u4f60\u771f\u662f\u975e\u5f97\u8ddf\u6211\u591a\u5386\u7ec3\u624d\u884c\u3002\u90a3\u5c11\u5e74\u662f\u5973\u626e\u7537\u88c5\uff0c\u4f60\u770b\u4e0d\u51fa \u6765\u4e48\uff1f\u201d \u3000\u3000\u94c1\u4e00\u7b14\u9053\u4e86\u4e00\u58f0\u201c\u6e10\u6127\u201d\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4f60\u89c1\u8fc7\u7389\u9762\u5996\u72d0\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u867d\u6ca1\u89c1\u8fc7\uff0c\u4e5f\u542c\u4eba\u5bb6\u8bf4\u8fc7\u5979\u7684\u5bb9\u8c8c\u3002\u800c\u4e14\u6211\u5df2\u7ecf\u6253\u542c\u6e05\u695a\uff0c\u7389\u9762\u5996\u72d0\u7684\u771f \u5b9e\u59d3\u540d\uff0c\u4e43\u662f\u590d\u59d3\u8d6b\u8fde\uff0c\u53cc\u540d\u6e05\u6ce2\uff0c\u90a3\u4e2a\u5047\u626e\u7537\u88c5\u7684\u5973\u5b50\u81ea\u79f0\u59d3\u8fde\uff0c\u5c11\u4e86\u4e00\u4e2a\u201c\u8d6b\u201d\u5b57\uff0c\u53ea \u662f\u628a\u590d\u59d3\u6539\u4e3a\u5355\u59d3\u800c\u5df2\u3002\u5979\u7684\u5bb9\u8c8c\u53c8\u548c\u753b\u56fe\u76f8\u4f3c\uff0c\u4e0d\u662f\u7389\u9762\u5996\u72d0\u8fd8\u80fd\u662f\u8c01\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff1a\u201c\u6211\u5f53\u521d\u4e5f\u662f\u8fd9\u6837\u60f3\u7684\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u4eac\u5e08\u7b2c\u4e00\u540d\u6355\u540c\u6837\u770b\u9519\u4e86 \u4eba\u3002\u201d \u3000\u3000\u94c1\u4e00\u7b14\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5e08\u53d4\u7684\u5224\u65ad\u6599\u60f3\u662f\u4e0d\u4f1a\u9519\u7684\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u90a3\u4e2a\u519b\u5b98\u53c8\u662f\u4ec0\u4e48\u6765 \u5386\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u8fd9\u4e2a\u519b\u5b98\uff0c\u6211\u662f\u5728\u5b8c\u989c\u738b\u7237\u7684\u5e9c\u4e2d\u89c1\u8fc7\u7684\u3002\u4ed6\u590d\u6027\u8036\u5f8b\uff0c\u53c8\u540d\u5b8c\u5b9c\u3002\u201d \u3000\u3000\u690d\u7fbd\u51b2\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u8fc7\u77bf\u7ad9\u4e00\u7701\uff0c\u5fc3\u9053\uff1a\u201c\u8036\u5f8b\u5b8c\u5b9c\uff1f\u4ed6\u662f\u548c\u6211\u7684\u5e08\u7236\u540c\u59d3\u7684\uff1f\u59d3\u8036\u5f8b \u7684\u4eba\u6781\u5c11\uff0c\u83ab\u975e\u4ed6\u662f\u8fbd\u56fd\u7687\u65cf\u4e2d\u4eba\uff0c\u5728\u8f88\u4efd\u4e0a\u5c5e\u4e8e\u6211\u5e08\u7236\u7684\u4f84\u513f\u4e00\u8f88\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672c\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u94c1\u4e00\u7b14\u8bf4\u9053\uff1a\u201c\u4ed6\u590d\u59d3\u8036\u5f8b\uff0c\u8fd9\u4e0d\u662f\u8fbd\u56fd\u7684\u56fd\u59d3\u5417\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fbd\u56fd\u6700\u540e\u4e00\u4e2a\u7687\u5e1d\u662f\u8036\u5f8b\u5ef6\u79a7\uff0c\u4ed6\u6709\u516d\u4e2a\u513f\u5b50\uff0c\u4e09\u5341\u591a\u4e2a\u4f84\u513f\uff0c\u56fd \u4ea1\u4e4b\u540e\uff0c\u6709\u4e09\u56db\u4e2a\u5b59\u513f\u4e0b\u843d\u4e0d\u660e\uff0c\u8fd9\u4e2a\u8036\u5f8b\u5b8c\u5b9c\u53ef\u80fd\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u94c1\u4e00\u7b14\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u771f\u7684\u662f\u8fbd\u56fd\u738b\u5b59\u8eab\u4efd\uff0c\u5b8c\u989c\u738b\u7237\u600e\u7684\u5374\u8ba9\u4ed6\u5f53\u4e0a\u54b1\u4eec\u91d1\u56fd\u7684\u519b \u5b98\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u8036\u5f8b\u5ef6\u79a7\u5f53\u5e74\u56fd\u4ea1\u88ab\u4fd8\uff0c\u4f7f\u5373\u6295\u8bda\u3002\u5148\u5e1d\u6cd5\u5916\u65bd\u4ec1\uff0c\u5c01\u4ed6\u4e3a\u897f\u660f\u5019\uff0c\u5bf9\u4ed6 \u7684\u5b50\u5b59\u4e5f\u6ca1\u6ee5\u52a0\u8bdb\u6740\uff0c\u4e0d\u8fc7\u662f\u6d3e\u4eba\u76d1\u89c6\u4ed6\u8fd9\u4e00\u5bb6\uff0c\u90a3\u662f\u514d\u4e0d\u4e86\u7684\u2026\u2026\u201d \u3000\u3000\u94c1\u4e00\u7b14\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e5f\u4e0d\u8fc7\u662f\u6b7b\u5211\u6539\u4e3a\u65e0\u671f\u5f92\u5211\u800c\u5df2\u3002\u201d\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u73b0\u5728\u8036\u5f8b\u5ef6\u79a7\u7684\u90a3 \u4e9b\u5b59\u513f\uff0c\u4e5f\u5dee\u4e0d\u591a\u6b7b\u4e86\u5341\u4e4b\u4e03\u516b\u4e86\uff1f\u201d \u3000\u3000\u5411\u5929\u51b2\u9053\uff1a\u201c\u4ea1\u56fd\u738b\u5b59\uff0c\u5f53\u7136\u662f\u96be\u514d\u53d7\u70b9\u6298\u78e8\u4e86\u3002\u4ed6\u7684\u5b50\u5b59\u6709\u4e9b\u53ef\u80fd\u662f\u56e0\u4e3a\u770b\u4e0d\u5f00\u81ea\u6740 \u7684\uff0c\u6709\u4e9b\u5219\u53ef\u80fd\u5fe7\u90c1\u4f24\u8eab\uff0c\u77ed\u547d\u6b7b\u7684\u3002\u4f46\u54b1\u4eec\u91d1\u56fd\u603b\u662f\u4f18\u5f85\u964d\u4eba\u4e86\u3002\u201d \u3000\u3000\u94c1\u4e00\u7b14\u9053\uff1a\u201c\u82e5\u7136\u738b\u7237\u77e5\u9053\u8036\u5f8b\u5b8c\u5b9c\u662f\u8fbd\u56fd\u738b\u5b59\u8eab\u4efd\uff0c\u8fd8\u6562\u7528\u4ed6\uff0c\u90a3\u5c31\u66f4\u52a0\u662f\u5bbd\u5b8f\u5927\u91cf \u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8eb2\u5728\u4e00\u5757\u5927\u77f3\u540e\u9762\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u6697\u81ea\u60f3\u5230\uff1a\u201c\u8fd9\u4e2a\u8036\u5f8b\u5b8c\u5b9c\u82e5\u7136\u662f\u4e2a\u8d2a\u56fe\u5bcc\u8d35\u7684 \u4eba\uff0c\u5b8c\u989c\u957f\u4e4b\u5012\u662f\u4e0d\u59a8\u7528\u4ed6\u6765\u7b3c\u7edc\u8fbd\u56fd\u7684\u4eba\u5fc3\u7684\u3002\u55ef\uff0c\u6740\u964d\u4e0d\u5982\u62db\u964d\uff0c\u6000\u67d4\u80dc\u4e8e\u9ad8\u538b\u3002\u8fd9\u662f \u5e08\u7236\u8bae\u8bba\u5386\u671d\u5f97\u5931\u65f6\u8bf4\u8fc7\u7684\u4e24\u53e5\u8bdd\u3002 \u3000\u3000\u201d\u53c8\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u5e08\u7236\u628a\u5b8c\u989c\u957f\u4e4b\u89c6\u4e3a\u5e73\u751f\u5927\u654c\uff0c\u770b\u6765\u6050\u6015\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u56e0\u4e3a\u5b8c\u989c\u957f\u4e4b\u7684 \u6b66\u529f\u6bd4\u5f97\u4e0a\u4ed6\u5462\u3002\u201d \u3000\u3000\u5411\u5929\u51b2\u5374\u4e0d\u613f\u548c\u5e08\u4f84\u591a\u52a0\u89e3\u91ca\u3002\u8bf4\u9053\uff1a\u201c\u738b\u7237\u7684\u8fd0\u7528\u4e4b\u5999\uff0c \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u66f2\u7ec8\u4eba\u6563 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u4e00\u56de\u3000\u66f2\u7ec8\u4eba\u6563 \u3000\u3000\u5317\u4eac\u5174\u5316\u95e8\u5916\u6709\u4e2a\u5730\u65b9\u53eb\u9493\u9c7c\u53f0\uff0c\u636e\u8bf4\u91d1\u521d\u6709\u4e2a\u8bd7\u4eba\u540d\u53eb\u738b\u90c1\uff0c\u66fe\u9690\u4e8e\u6b64\uff0c\u4ee5\u9493\u9c7c\u4e3a \u4e1a\uff0c\u56e0\u800c\u5f97\u540d\uff0c\u5176\u540e\u91d1\u592a\u5b97\u5b8c\u989c\u665f\u5728\u8fd9\u91cc\u5efa\u4e86\u4e00\u5ea7\u884c\u5bab\uff0c\u5e76\u5c06\u738b\u90c1\u9493\u9c7c\u7684\u6f6d\u758f\u6d5a\u6253\u5927\u6210\u6e56\uff0c \u4e8e\u662f\u6e10\u6e10\u6210\u4e3a\u516c\u5b50\u738b\u5b59\u7684\u6e38\u4e50\u4e4b\u6240\uff0c\u5728\u9669\u6e56\u90a3\u5ea7\u5c71\u5c97\u4e0a\u5efa\u4e86\u8bb8\u591a\u522b\u5885\u3002\u5176\u4e2d\u4e00\u5ea7\u5c31\u662f\u5b8c\u989c\u5bb6 \u7684\u3002\u5982\u4eca\u662f\u5546\u5dde\u8282\u5ea6\u4f7f\u5b8c\u989c\u9274\u592b\u4eba\u7684\u4f4f\u6240\u3002\u6b64\u5904\u6b63\u6709\u4eba\u5728\u95e8\u524d\u5356\u82b1\uff0c\u8fd9\u4e2a\u4eba\u662f\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u201c\u5356\u82b1\uff0c\u5356\u82b1\uff01\u91d1\u76cf\uff0c\u7ee3\u7403\uff0c\u5927\u7ea2\u83ca\uff0c\u59da\u9ec4\uff0c\u767d\u7389\uff0c\u9ed1\u7261\u4e39\uff0c\u8c01\u5bb6\u8981\u4e70\u8d81\u65e9\u4e70\uff01\u201c\u4ed6\u5927 \u58f0\u53eb\u5356\uff0c\u90a3\u5bb6\u4eba\u5bb6\u7684\u95e8\u5374\u4e0d\u6253\u5f00\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u63d0\u4e00\u53e3\u6c14\uff0c\u53c8\u518d\u53eb\u5356\uff1a\u201c\u6781\u54c1\u9ed1\u7261\u4e39\uff0c\u9752\u9f99\u5367\u58a8\u6c60\u3002\u540d\u82b1\u5356\u8bc6\u4e3b\uff0c\u8bc6\u8005\u83ab\u8fdf \u7591\uff01\u201d\u8fd9\u6b21\u7528\u4e0a\u4e86\u4f20\u97f3\u5165\u5bc6\u7684\u5185\u529f\uff0c\u58f0\u97f3\u5bb9\u8fc7\u91cd\u95e8\u4fdd\u6237\uff0c\u4f30\u91cf\u5b8c\u989c\u5927\u4eba\u5373\u4f7f\u5728\u6700\u91cc\u7684\u4e00\u9053\u3002 \u4e5f\u5f53\u542c\u5f97\u89c1\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u5bb6\u4eba\u5bb6\u7684\u95e8\u679c\u7136\u5f00\u4e86\u3002 \u3000\u3000\u51fa\u6765\u7684\u662f\u4e2a\u5973\u4e2b\u3002\u6a80\u7fbd\u51b2\u4e0d\u89c9\u6709\u70b9\u5931\u671b\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u6562\u5e0c\u671b\u5b8c\u989c\u592b\u4eba\u4eb2\u81ea\u51fa\u6765\uff0c\u4ed6\u7684\u5931\u671b\u662f\u56e0\u4e3a\u4e0d\u89c1\u4ed6\u7684\u59b9\u59b9\u3002\u4e00\u822c\u8bf4\u6765\uff0c\u5c0f\u5b69\u5b50 \u591a\u662f\u559c\u6b22\u65b0\u5947\u7684\u4e8b\u7269\u7684\uff0c\u95e8\u5916\u6709\u4eba\u5356\u82b1\uff0c\u800c\u4e14\u53eb\u5356\u7684\u662f\u6781\u54c1\u9ed1\u7261\u4e39\uff0c\u4ed6\u7684\u59b9\u59b9\u4e3a\u4f55\u4e0d\u8ddf\u968f\u5973 \u4e2b\u51fa\u6765\u5462\uff1f\u90a3\u5973\u4e2b\u4e5f\u4f3c\u4e4e\u6709\u70b9\u8be7\u5f02\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u771f\u6709\u9752\u9f99\u5367\u58a8\u6c60\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u4fe1\u4f60\u770b\uff01\u201c\u5728\u7bee\u4e2d\u68c0\u51fa\u9ed1\u7261\u4e39\uff0c\u7ed9\u90a3\u5973\u4ec6\u3002\u5973\u4ec6\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e0d\u61c2\u7684\uff0c \u8981\u7ed9\u592b\u4eba\u770b\u624d\u77e5\u771f\u5047\u3002\u4f60\u8ddf\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8ddf\u968f\u90a3\u5973\u4e2b\u8fdb\u53bb\uff0c\u4e0d\u8fc7\uff0c\u53ea\u662f\u767b\u5802\uff0c\u672a\u80fd\u5165\u5ba4\u3002\u5973\u4e2b\u53eb\u4ed6\u5728\u5ba2\u5385\u5750\u4e0b\uff0c\u63a5\u8fc7\u4ed6\u624b \u4e2d\u7684\u82b1\u7bee\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u62ff\u53bb\u7ed9\u592b\u4eba\uff0c\u4f60\u5728\u8fd9\u91cc\u5f85\u4e0a\u4e00\u4f1a\u513f\u3002\u201d\u8ba9\u4e00\u4e2a\u5356\u82b1\u7684\u5c0f\u53ae\u5728\u534e\u4e3d\u7684\u5ba2 \u5385\u5750\u5019\uff0c\u5bf9\u4ed6\u4e5f\u53ef\u7b97\u5f97\u4f18\u793c\u6709\u52a0\u4e86\u3002\u4f46\u6a80\u7fbd\u51b2\u7684\u5931\u671b\u66f4\u52a0\u6df1\u4e86\u3002\u56e0\u4e3a\u8fd8\u662f\u672a\u89c1\u4ed6\u7684\u59b9\u59b9\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u5973\u4e2b\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u8bf4\uff0c\u8fd9\u9ed1\u7261\u4e39\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u5374\u4e0d\u662f\u9752\u9f99\u5367\u58a8\u6c60\u3002\u4e0d \u8fc7\u4f60\u77e5\u9053\u8fd9\u4e2a\u82b1\u540d\u5df2\u7ecf\u7b97\u662f\u4e0d\u6613\uff0c\u592b\u4eba\u8bf4\u4e0d\u80fd\u53eb\u4f60\u81ea\u6765\u4e00\u8d9f\uff0c\u8fd9\u5341\u4e24\u94f6\u5b50\u662f\u8d4f\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u5341\u4e24\u94f6\u5b50\u4e70\u4e00\u6735\u771f\u7684\u201c\u9752\u9f99\u5367\u58a8\u6c60\u201c\u4e5f\u8db3\u591f\u4e86\u3002\u4e0d\u8fc7\uff0c\u6a80\u7fbd\u51b2\u5f53\u7136\u4e0d\u4f1a\u8981\u5979\u7684\u3002 \u3000\u3000\u4ed6\u6545\u610f\u88c5\u6a21\u4f5c\u6837\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u529f\u592b\u5b66\u4e0d\u5230\u5ac1\uff0c\u771f\u662f\u4e0d\u597d\u610f\u601d\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5bb6\u6bcd\u662f\u7ed9\u4eba\u5bb6\u79cd\u82b1\u7684\uff0c\u800c\u4e14\u79cd\u7684\u90fd\u662f\u7261\u4e39\u3002\u6211\u81ea\u5c0f\u5728\u7261\u4e39\u56fd\u4e2d \u957f\u5927\uff0c\u4ec0\u4e48\u540d\u79cd\u58ee\u4e39\u90fd\u66fe\u89c1\u8fc7\u3002\u6211\u4ee5\u4e3a\u8fd9\u662f\u9752\u9f99\u5367\u58a8\u6c60\uff0c\u8c01\u77e5\u8fd8\u662f\u770b\u5dee\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u4e2b\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u591a\u7b49\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u8fd9\u6b21\u5979\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5bf9\u6a80\u7fbd\u51b2\u66f4\u52a0\u5ba2\u6c14\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u60f3\u95ee\u4f60\u51e0\u8bdd\uff0c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6697\u6697\u6b22\u559c\uff0c\u53ea\u9053\u8fd9\u6b21\u4e00\u5b9a\u53ef\u4ee5\u89c1\u5f97\u7740\u5b8c\u989c\u592b\u4eba\u4e86\u3002\u90a3\u77e5\u9053\u8fd8\u662f\u89c1\u4e0d\u7740\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u6b21\u4ed6\u4e0d\u4ec5\u53ea\u662f\u767b\u5802\uff0c\u800c\u662f\u5165\u5ba4\u4e86\u3002\u4ed6\u88ab\u8bf7\u8fdb\u4e86\u592b\u4eba\u7684\u5367\u5ba4\u3002 \u3000\u3000\u4f46\u5b8c\u989c\u592b\u4eba\u7684\u5367\u5ba4\u662f\u4e00\u95f4\u5957\u623f\uff0c\u4ed6\u5728\u5916\u95f4\uff0c\u8fd8\u662f\u6709\u4e00\u677f\u4e4b\u9694\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u4f60\u7684\u6bcd\u4eb2\u7ed9\u4eba\u5bb6\u79cd\u82b1\uff0c\u90a3\u5bb6\u4eba\u5bb6\u662f\u4e2a\u4e48\u4eba\u5bb6\uff1f\u201c\u5b8c\u989c\u592b\u4eba\u9694\u7740\u677f\u95ee\u4ed6\u3002\u8bf4\u8bdd\u7684\u58f0 \u97f3\u4f3c\u4e4e\u6709\u70b9\u6c14\u5598\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5b8c\u592b\u4eba\u662f\u4f1a\u6b66\u529f\u7684\uff0c\u600e\u7684\u8bf4\u51e0\u8bdd\u4e5f\u4f1a\u6c14\u5598\uff0c\u7ed9\u9053\u5979\u662f\u751f \u75c5\u4e86\u4e48\uff1f\u201d\u4ed6\u7684\u542c\u89c9\u751a\u4e3a\u7075\u654f\uff0c\u542c\u5f97\u51fa\u623f\u95f4\u91cc\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u59b9\u59b9\u5982\u679c\u5728\u5bb6\u7684\u8bdd\uff0e\u6309\u8bf4\u662f\u5e94 \u8be5\u7559\u5728\u623f\u95f4\u4e2d\u966a\u4f34\u5b8c\u989c\u592b\u4eba\u7684\uff0c\u6b64\u65f6\u4ed6\u53ea\u80fd\u76fc\u671b\u4ed6\u7684\u59b9\u59b9\u80fd\u591f\u53ca\u9644\u56de\u6765\u4e86\u3002 \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u4eba\u5bb6\uff0c\u53ea\u8bb0\u5f97\u90a3\u5bb6\u4eba\u5bb6\u6709\u8bb8\u591a\u6b66\u58eb\uff0c\u4e3b\u4eba\u597d\u50cf\u662f\u4e2a\u5c06\u519b\u3001\u201d\u6a80\u7fbd\u51b2\u7b54 \u9053\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5fc3\u5934\u4e00\u8df3\uff0c\u63a5\u7740\u518d\u95ee\uff1a\u201c\u4ee4\u5802\u672c\u6765\u5c31\u4f1a\u79cd\u82b1\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u5bb6\u6bcd\u662f\u5230\u4e86\u90a3\u5bb6\uff0c\u624d\u8ddf\u90a3\u5bb6\u4eba\u5bb6\u7684\u82b1\u738b\u5b66\u4f1a\u79cd\u82b1\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u4f60\u81ea\u5c0f\u5728\u7261\u4e39\u7684\u56ed\u4e2d\u957f\u5927\uff0c\u96be\u9053\u90a3\u5bb6\u4eba\u5bb6\u7684\u82b1\u56ed\u91cc\u5c31\u53ea\u79cd\u7261\u4e39\uff1f\u201d \u3000\u3000\u201c\u90a3\u5bb6\u4eba\u5bb6\u6709\u4e24\u4e2a\u82b1\u56ed\uff0c\u5927\u82b1\u56ed\u91cc\u4ec0\u4e48\u82b1\u90fd\u6709\uff0c\u5c0f\u82b1\u56ed\u91cc\u53ea\u79cd\u7261\u4e39\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u53ea\u79cd\u7261\u4e39\uff1f\u201c\u5b8c\u989c\u592b\u4eba\u5598\u7740\u6c14\u8bf4\u8bdd\uff0c\u8fde\u5979\u7684\u5973\u4ec6\u90fd\u542c\u5f97\u89c1\u4e86\u3002 \u3000\u3000\u201c\u592b\u4eba\uff0c\u4f60\u7701\u70b9\u6c14\u529b\u8bf4\uff0c\u8ba9\u5974\u5a62\u66ff\u4f60\u4f20\u8bdd\u597d\u5417\uff1f\u201d\u90a3\u5973\u4ec6\u8d76\u5fd9\u8fdb\u5165\u5185\u5ba4\uff0c\u670d\u5f85\u4e3b\u4eba\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u5bb6\u4eba\u5bb6\u7684\u4e3b\u6bcd\u53ea\u7231\u7261\u4e39\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd8\u8bb0\u5f97\u90a3\u5bb6\u4eba\u5bb6\u7684\u4e3b\u6bcd\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\u5417\uff1f\u201d\u5b8c\u989c\u592b\u4eba\u4f4e\u58f0\u5411\u5973\u4ec6\u8bf4\uff0c\u518d\u8ba9\u5973\u4ec6\u66ff \u5979\u4f20\u8bdd\uff0c\u5176\u5b9e\u6a80\u7fbd\u51b2\u662f\u542c\u5f97\u6e05\u695a\u5979\u8bf4\u4ec0\u4e48\u7684\uff0c\u4e0d\u8fc7\u4ed6\u5374\u5e76\u4e0d\u8bf4\u7834\u3002 \u3000\u3000\u201c\u90a3\u4f4d\u592b\u4eba\u53c8\u7f8e\u4e3d\uff0c\u53c8\u9ad8\u8d35\uff0c\u800c\u4e14\u5fc3\u5730\u53c8\u5f88\u6148\u7965\u3002\u201d\u6a80\u7fbd\u51b2\u9053\u3002 \u3000\u3000\u8fd9\u6b21\u5b8c\u989c\u592b\u4eba\u548c\u90a3\u5973\u4ec6\u8bf4\u8bdd\u7684\u58f0\u97f3\u66f4\u5c0f\u4e86\uff0c\u6a80\u7fbd\u51b2\u4e5f\u542c\u4e0d\u5b8c\u5168\u3002 \u3000\u3000\u5973\u4ec6\u4f20\u8bdd\uff1a\u201c\u592b\u4eba\u4e0d\u60f3\u542c\u7a7a\u6cdb\u7684\u9882\u8bcd\uff0c\u592b\u4eba\u60f3\u8981\u77e5\u9053\u7684\u662f\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8ba9\u6211\u60f3\u60f3\u3002\u201d\u88c5\u6a21\u4f5c\u6837\uff0c\u60f3\u4e86\u7247\u523b\uff0c\u5ffd\u5730\u95ee\u90a3\u5973\u4ec6\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u4f1a\u5439\u7bab \u5417\uff1f\u201d \u3000\u3000\u95ee\u9898\u6765\u5f97\u592a\u8fc7\u7a81\u5140\uff0c\u90a3\u5973\u4ec6\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u95ee\u6211\u4e0d\u4f1a\u5439\u8427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u5bb6\u4eba\u5bb6\u7684\u4e3b\u6bcd\u6709\u4e2a\u4e2b\u73af\uff0c\u548c\u4f60\u4e00\u822c\u5e74\u7eaa\u3002\u5f88\u4f1a\u5439\u8427\uff0c\u4e0d\u8fc7\u5439\u6765\u5439\u53bb\uff0c\u8001 \u662f\u4e00\u4e2a\u66f2\u8c03\u3002\u201d \u3000\u3000\u90a3\u5973\u4ec6\u9053\uff1a\u201c\u592b\u4eba\u60f3\u8981\u77e5\u9053\u90a3\u5bb6\u4eba\u5bb6\u7684\u4e3b\u6bcd\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff0c\u4f60\u8bf4\u8fd9\u4e9b\u4e0d\u76f8\u5e72\u7684\u8bdd\u5e72 \u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\u3002\u201c\u4e2b\u73af\u5439\u7684\u90a3\u4e2a\u66f2\u5b50\uff0c\u5c31\u662f\u5979\u7684\u4e3b\u6bcd\u6559\u4f1a\u5979\u7684\u3002\u5979\u5df2\u7ecf\u5439\u5f97\u5f88\u597d\u542c\uff0c\u4f46\u636e\u5979 \u8bf4\uff0c\u5979\u7684\u4e3b\u6bcd\u5439\u5f97\u6bd4\u5979\u66f4\u52a0\u597d\u542c\u3002\u4f46\u53ea\u6559\u4e00\u652f\u66f2\u5b50\uff0c\u4e0d\u662f\u6709\u70b9\u7279\u522b\u5417\uff1f\u4e0d\u8fc7\uff0c\u90a3\u652f\u66f2\u5b50\u4e5f\u771f \u662f\u8033\u542c\u4e0d\u538c\uff0c\u6211\u542c\u5f97\u591a\u4e86\uff0c\u4e5f\u4f1a\u5439\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u8d8a\u53d1\u5403\u60ca\uff0c\u4e0d\u8981\u5973\u4ec6\u4f20\u8bdd\uff0c\u4fbf\u5373\u63d0\u9ad8\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e5f\u4f1a\u5439\uff1f\u5509\uff0c\u53ef\u60dc\u6211 \u90a3\u652f\u7389\u8427\u5931\u4e86\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6070\u5de7\u6211\u4e5f\u6709\u4e00\u652f\u7389\u8427\uff0c\u592b\u4eba\uff0c\u4f60\u82e5\u662f\u4e0d\u5acc\u6c61\u8033\u7684\u8bdd\uff0c\u6211\u5439\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u7389\u8427\u4e00\u4eae\u3002\u5973\u4ec6\u7981\u4e0d\u4f4f\u5931\u58f0\u60ca\u547c\uff1a\u201c\u592b\u4eba\uff0c\u4ed6\u8fd9\u652f\u7389\u7b56\u597d\u50cf\u6bd4\u4f60\u4ee5\u524d\u90a3\u652f\u7389\u8427\u8fd8\u597d\u5f97 \u591a\uff01\u201d\u4e00\u4e2a\u5356\u82b1\u90ce\u5c45\u7136\u80fd\u6709\u4e00\u652f\u582a\u79f0\u7a00\u4e16\u4e4b\u73cd\u7684\u7389\u8427\uff0c\u5b9e\u662f\u4e0d\u53ef\u601d\u4e49\u7684\u4e8b\uff1b\u4f46\u5b8c\u989c\u592b\u4eba\u5df2\u662f \u65e0\u6687\u601d\u7591\uff0c\u56e0\u4e3a\u6a80\u7fbd\u51b2\u5df2\u7ecf\u5f00\u59cb\u5439\u8427\uff0c\u8427\u58f0\u628a\u5979\u5e26\u8fdb\u5165\u4e86\u4e00\u4e2a\u5982\u5e7b\u5982\u68a6\u7684\u5883\u754c\uff01 \u3000\u3000\u5979\u597d\u50cf\u770b\u89c1\u4e86\u5979\u5c11\u5e74\u65f6\u4ee3\u7684\u60c5\u4eba\uff0c\u6b63\u5728\u624b\u6301\u7389\u7bab\uff0c\u542b\u7b11\u5411\u5979\u8d70\u6765\u3002 \u3000\u3000\u8fd9\u662f\u8036\u5f8b\u7384\u5143\u548c\u5979\u7b2c\u4e00\u6b21\u76f8\u4f1a\u4e4b\u65f6\uff0c\u5439\u7ed9\u5979\u542c\u7684\u4e00\u652f\u66f2\u5b50\u3002\u4e5f\u662f\u548c\u5979\u5206\u624b\u4e4b\u65f6\uff0c\u5439\u7ed9\u5979 \u542c\u7684\u90a3\u652f\u66f2\u5b50\u3002 \u3000\u3000\u5979\u832b\u7136\u82e5\u68a6\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8ddf\u7740\u8282\u540e\uff0c\u54fc\u51fa\u6b4c\u8bcd\u3002 \u3000\u3000\u201c\u4e07\u4e07\u82b1\u4e2d\u7b2c\u4e00\u6d41\uff0c\u6b8b\u971e\u8f7b\u67d3\u5ae9\u94f6\u74ef\u3002\u80fd\u72c2\u7d2b\u9677\u5343\u91d1\u5b50\uff0c\u4e5f\u611f\u6731\u95e8\u4e07\u6237\u4faf\u3002\u671d\u65e5\u7167\u5f00\u643a \u9152\u770b\uff0c\u66ae\u98ce\u5439\u843d\u7ed5\u680f\u6536\u3002\u8bd7\u4e66\u6ee1\u67b6\u5c18\u57c3\u6251\uff0c\u5c3d\u65e5\u65e0\u4eba\u7565\u4e3e\u5934\u3002\u201d \u3000\u3000\u8427\u58f0\u6b62\u4e86\uff0c\u5b8c\u989c\u592b\u4eba\u5374\u597d\u4f3c\u8fd8\u5728\u68a6\u4e2d\u3002\u6006\u7136\u8bf4\u9053\uff1a\u201c\u7384\u5143\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6765\uff1f\u4e8c\u5341\u591a\u5e74 \u4e86\uff0c\u4f60\u8fd8\u4e0d\u80af\u653e\u8fc7\u6211\u4e48\uff1f\u201d \u3000\u3000\u5973\u4ec6\u5931\u58f0\u60ca\u53eb\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4ed6\u4e0d\u8fc7\u662f\u4e2a\u82b1\u5e97\u5c0f\u53ae\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5ffd\u5730\u5750\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u5bf9\u3002\u4ed6\u4e0d\u662f\u82b1\u5e97\u5c0f\u53ae\uff0c\u5feb\u53eb\u4ed6\u8fdb\u6765\u3002\u201d\u4e0d\u5f85\u90a3\u5973\u4ec6 \u4f20\u547c\uff0c\u6a80\u7fbd\u51b2\u5df2\u7ecf\u8e0f\u8fdb\u5979\u7684\u5367\u623f\u4e86\u3002 \u3000\u3000\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u201d\u5b8c\u989c\u592b\u4eba\u98a4\u58f0\u95ee\u4ed6\u3002 \u3000\u3000\u201c\u6211\u662f\u5170\u59d1\u7684\u513f\u5b50\uff0c\u62dc\u89c1\u592b\u4eba\uff01\u201d\u6a80\u7fbd\u51b2\u8dea\u4e0b\u53bb\u7ed9\u5979\u884c\u793c\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u84e6\u5730\u8d77\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8be5\u60f3\u5230\u4f60\u662f\u5170\u59d1\u7684\u513f\u5b50\u4e86\uff0c\u6211\u600e\u80fd\u53d7\u4f60 \u7684\u5927\u793c\uff0c\u5feb\u5feb\u8d77\u6765\uff01\u201d \u3000\u3000\u5979\u65e0\u529b\u62c9\u8d77\u6a80\u7fbd\u51b2\uff0c\u7adf\u7136\u4e5f\u8dea\u4e0b\u53bb\u7ed9\u4ed6\u8fd8\u793c\u3002\u5973\u4ec6\u8fd9\u60ca\u975e\u540c\u5c0f\u53ef\u3002\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u3001 \u4f60\uff01\u201d\u53ea\u9053\u4e3b\u4eba\u75af\u4e86\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u5b8c\u989c\u592b\u4eba\u9053\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u4ec6\u662f\u5979\u56de\u5230\u91d1\u4eac\u4e4b\u540e\u624d\u8ddf\u5979\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u5170\u59d1\u662f\u4f60\u4ece\u524d\u5fc3\u7231\u7684\u4f8d\u5a62\uff0c\u4f46\u5979 \u7684\u513f\u5b50\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u4ed6\u662f\u5c0f\u8d1d\u52d2\u7684\u8eab\u4efd\uff1b\u4ed6\u7684\u6bcd\u4eb2\u4e5f\u4e0d\u662f\u5bfb\u5e38\u4eba\uff0c\u5979\u662f\u5357\u5b8b\u540d \u5c06\u5cb3\u98de\u5916\u5b59\u5973\u513f\uff01\u4ed6\u7684\u8eab\u4efd\u6bd4\u6211\u9ad8\u8d35\u5f97\u591a\uff01\u201d \u3000\u3000\u90a3\u4e2a\u5973\u4ec6\u767b\u65f6\u5446\u82e5\u6728\u9e21\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5c06\u5b8c\u989c\u592b\u4eba\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u8bf7\u4f60\u4e0d\u8981\u8fd9\u6837\u8bf4\uff0c\u4ec0\u4e48\u8d1d\u52d2\u7684\u8eab\u4efd\u4e0e\u6211\u65e0\u5173\uff0c \u6211\u53ea\u662f\u7528\u5170\u59d1\u7684\u513f\u5b50\u7684\u8eab\u4efd\u6765\u89c1\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u4ece\u524d\u6211\u4e0d\u77e5\u9053\u4f60\u4eec\u6bcd\u5b50\u7684\u8eab\u4efd\uff0c\u5b9e\u5728\u59d4\u5c48\u4e86\u4f60\u4eec\uff0c\u8bf7\u4f60\u539f\u8c05\u3002\u201d\u5b8c\u989c\u592b\u4eba\u9053\u3002\u6a80\u7fbd\u51b2 \u9053\uff1a\u201c\u6211\u4eec\u6bcd\u5b50\u60a3\u96be\u4e2d\u5f97\u4f60\u5e87\u62a4\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u6c34\u96be\u8a00\u62a5\u3002\u6211\u662f\u4e3a\u4e86\u6b7b\u53bb\u7684\u6bcd\u4eb2\u5411\u4f60\u78d5\u5934 \u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u554a\uff0c\u4ee4\u5802\u5979\uff0c\u5979\u4ed9\u901d\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c31\u662f\u5728\u592b\u4eba\u51fa\u8d70\u90a3\u5929\uff0c\u5bb6\u6bcd\u4e0d\u5e78\u5728\u7261\u4e39\u56ed\u91cc\uff0c\u4e2d\u7bad\u8eab\u4ea1\u7684\u3002\u201d \u3000\u3000\u7528\u4e0d\u7740\u4ed6\u591a\u8bf4\uff0c\u5b8c\u989c\u592b\u4eba\u5df2\u7ecf\u77e5\u9053\u4ed6\u7684\u6bcd\u4eb2\u662f\u7ed9\u81ea\u5df1\u7684\u4e08\u592b\u53eb\u624b\u4e0b\u5c04\u6740\u7684\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5fcd\u7740\u773c\u6cea\uff0c\u95ee\u9053\uff1a\u201c\u98d8\u9999\u5462\uff1f\u201d\u98d8\u9999\u5c31\u662f\u5979\u51fa\u8d70\u90a3\u5929\uff0c\u7279\u5730\u7559\u4e0b\uff0c\u53eb\u5979\u53bb\u963b\u6b62 \u8036\u5f8b\u7384\u5143\u5411\u5979\u4e08\u592b\u5bfb\u4ec7\u7684\u5f85\u5973\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\u3002\u201c\u98d8\u9999\u4e5f\u662f\u7ed9\u5e9c\u4e2d\u7684\u6b66\u58eb\u5c04\u6740\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u90a3\u652f\u7389\u8083\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5979\u8eab\u4ea1\u4e4b\u540e\u3002\u60f3\u5fc5\u662f\u843d\u5728\u4f60\u4e08\u592b\u624b\u4e2d\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6b32\u54ed\u65e0\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u5bb3\u6b7b\u4e86\u4f60\u7684\u6bcd\u4eb2\uff0c\u53c8\u5bb3\u6b7b\u4e86\u98d8\u9999\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u592b\u4eba\uff0c\u8fd9\u4e0d\u5173\u4f60\u7684\u4e8b\uff0c\u6211\u7684\u6bcd\u4eb2\u867d\u7136\u6b7b\u4e86\uff0c\u4e5f\u8fd8\u5728\u611f\u6fc0\u4f60\u7684\u3002\u592b\u4eba\uff0c\u4f60\u7684 \u9762\u8272\u597d\u50cf\u6709\u70b9\u4e0d\u5bf9\uff0c\u4e0d\u662f\u751f\u75c5\u628a\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u8001\u6bdb\u75c5\uff0c\u4e0d\u8981\u7d27\u7684\u3002\u5bf9\u5566\uff0c\u4f60\u7684\u7389\u8427\u53ef\u4ee5\u8ba9\u6211\u770b\u770b\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u63a5\u8fc7\u7389\u8427\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u611f\u4f24\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u652f\u4e94\u8427\uff0c\u4f60\u3001\u4f60\u662f\u600e\u6837\u5f97\u6765 \u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u662f\u6069\u5e08\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u554a\uff0c\u4ed6\u5df2\u7ecf\u6536\u4f60\u505a\u5f1f\u5b50\u4e86\u3002\u4ed6\u3001\u4ed6\u597d\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f88\u597d\u3002\u53ea\u662f\uff0c\u53ea\u662f\u2014\u2014\u201d\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u53ea\u662f\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e2d\u662f\u6302\u5ff5\u592b\u4eba\u3002\u592b\u4eba\uff0c\u6709\u51e0\u53e5\u8bdd\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u9493\u9c7c\u53f0\u6050\u975e\u9690\u5c45\u4e4b\u5730\uff0c\u592b\u4eba\uff0c\u4f60\u82e5\u51b3\u5fc3\u653e\u5f03\u5bcc\u8d35\u8363\u534e\uff0c\u4e0d\u5982\uff0c\u4e0d\u5982\u2026\u2026\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9661\u5730\u559d\u6b62\u4ed6\uff1a\u201c\u4f60\uff0c\u4f60\u4e0d\u8981\u8bf4\u4e0b\u53bb\u4e86\uff01\u5df2\u7ecf\u592a\u8fdf\u4e86\uff0c\u6211\uff0c\u6211\u4e0d\u80fd\u8fd9\u6837\u505a\u4e86\uff01\u201d \u3000\u3000\u5973\u4ec6\u5446\u7acb\u4e00\u65c1\uff0c\u4e0d\u77e5\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\u3002\u53ea\u89c1\u5b8c\u989c\u592b\u4eba\u5df2\u662f\u9893\u7136\u5012\u5367\uff0e\u9762\u8272\u66f4\u52a0\u96be\u770b\u3002 \u3000\u3000\u201c\u592b\u4eba\uff0c\u4f60\u3001\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5973\u4ec6\u7ed9\u5413\u614c\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u522b\u614c\uff0c\u8ba9\u6211\u770b\u770b\u3001\u201d\u8036\u5f8b\u7384\u5143\u6742\u5b66\u751a\u5e7f\uff0c\u533b\u672f\u661f\u76f8\u65e0\u6240\u4e0d\u80fd\uff0c\u6a80\u7fbd\u51b2\u5728\u4ed6 \u95e8\u4e0b\u51e0\u5e74\uff0c\u7c97\u901a\u533b\u672f\u3002\u4ed6\u7ed9\u5b8c\u989c\u592b\u4eba\u628a\u4e86\u628a\u8109\u3002\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8fd9\u597d\u50cf\u5fc3\u6c14\u75db\u7684\u6bdb\u75c5\uff0c\u53ea \u8981\u5fc3\u5883\u5bbd\u8212\uff0c\u81ea\u7136\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u6562\u8ba9\u5b8c\u989c\u592b\u4eba\u518d\u53d7\u523a\u6fc0\uff0c\u8f6c\u8fc7\u8bdd\u9898\u95ee\u9053\uff1a\u201c\u6211\u90a3\u59b9\u5b50\u4e3a\u4f55\u4e0d\u5728\u4f60\u7684\u773c\u524d\u670d \u4f8d\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u6211\u65e9\u5c31\u5e94\u8be5\u5bf9\u4f60\u8bf4\u4e86\uff0c\u4f60\u7684\u59b9\u5b50\uff0c\u5979\u3001\u5979\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u9762\u66ff\u5979\u63a8\u8840\u8fc7\u5bab\uff0c\u4e00\u9762\u95ee\u9053\uff1a\u201c\u5979\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6c14\u606f\u8c03\u5300\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u60ca\u7591\uff0c\u5979\u53ea\u662f\u4e0d\u5728\u8fd9\u91cc\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5979\u5230\u54ea\u91cc\u53bb \u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6b63\u60f3\u56de\u7b54\uff0c\u5ffd\u5730\u542c\u5f97\u6709\u4eba\u6572\u95e8\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u76b1\u8d77\u7709\u5934\uff0c\u5bf9\u5973\u4ec6\u9053\uff1a\u201c\u4f60\u53bb\u770b\u662f\u8c01\uff1f\u82e5\u662f\u90a3\u4e9b\u65e0\u4e8b\u6765\u732e\u6bb7\u52e4\u7684\u592b\u4eba\u5c0f\u59d0\uff0c\u4f60 \u7ed9\u6211\u6321\u9a7e\uff01\u201d \u3000\u3000\u201c\u5f00\u95e8\uff0c\u5f00\u95e8\uff01\u201d\u6765\u5ba2\u4f3c\u4e4e\u7b49\u5f97\u4e0d\u5927\u8010\u70e6\uff0c\u4ece\u6572\u95e8\u6210\u62cd\u95e8\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u89c9\u5f97\u58f0\u97f3\u597d\u4f3c\u719f\u4eba\uff0c\u4e00\u65f6\u95f4\u5374\u5019\u4e0d\u60f3\u6765\u662f\u8c01\uff0c\u76b1\u7709\u9053\uff1a\u201c\u600e\u7684\u8fd9\u6837\u6ca1\u6709\u793c \u8c8c\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5c0f\u58f0\u9053\uff1a\u201c\u6765\u7684\u4e00\u5171\u662f\u4e09\u4e2a\u4eba\uff0c\u597d\u50cf\u662f\u4e00\u4e3b\u4e8c\u4ec6\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fde\u8eab\u4efd\u4e5f\u542c\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53eb\u5f00\u95e8\u7684\u662f\u4e24\u4e2a\u4eba\uff0c\u53e6\u4e00\u4e2a\u4eba\u4e0d\u51fa\u58f0\u3002\u8fd9\u4e0d\u51fa\u58f0\u7684\u60f3\u5fc5\u662f\u4e3b\u4eba\u7684\u8eab\u4efd\uff0c\u800c \u4e14\u8eab\u4efd\u975e\u540c\u5c0f\u53ef\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4ed6\u4eec\u6562\u5728\u4f60\u4eec\u7684\u95e8\u524d\u5927\u547c\u5c0f\u53eb\uff0c\u5f53\u7136\u662f\u501a\u4ed7\u4e3b\u4eba\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u5982\u6b64\u65e0\u793c\uff0c\u7ba1\u4ed6\u662f\u8c01\uff0c\u6211\u90fd\u4e0d\u89c1\uff01\u201d\u4f46\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u58f0\u97f3\u5df2 \u662f\u6709\u70b9\u53d1\u98a4\uff0c\u800c\u4e14\u597d\u50cf\u6015\u7ed9\u5916\u9762\u7684\u4eba\u542c\u89c1\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u6bd4\u6a80\u7fbd\u51b2\u66f4\u8f7b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u7684\u53e3\u97f3\u4e00\u6837\uff0c\u54a6\uff0c\u4e0d\u5bf9\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fd8\u672a\u6765\u5f97\u53ca\u56de\u7b54\u3002\u53ea\u542c\u5f97\u90a3\u5973\u4ec6\u201c\u554a\u5440\uff01\u201d\u4e00\u58f0\uff0e\u63a5\u7740\u5c31\u628a\u5927\u95e8\u6253\u5f00\u4e86\u3002 \u3000\u3000\u8fd9\u5973\u4ec6\u6ca1\u6709\u6765\u901a\u62a5\uff0c\u5c31\u628a\u5927\u95e8\u6253\u5f00\uff0c\u7adf\u662f\u628a\u4e3b\u6bcd\u7684\u5429\u5490\u90fd\u7f6e\u4e4b\u8111\u540e\u3002\u8fd9\u4e00\u201c\u53cd\u5e38\u201d\u7684\u60c5 \u5f62\u51fa\u73b0\uff0c\u5b8c\u989c\u592b\u4eba\u4ea6\u5df2\u77e5\u9053\u201c\u4e0d\u5bf9\u201d\u4e86\u3002 \u3000\u3000\u201c\u6709\u5ba2\u4eba\u5417\uff1f\u201d\u4e00\u76f4\u6ca1\u6709\u4f5c\u58f0\u7684\u53e6\u5916\u4e00\u4eba\u53d1\u95ee\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u58f0\u97f3\u662f\u66f4\u52a0\u719f\u6089\u4e86\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5b8c\u989c\u592b\u4eba\u548c\u6a80\u7fbd\u51b2\u90fd\u662f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5e76\u975e\u522b\u4eba\uff0c\u6b63\u662f\u5979\u7684\u4e08\u592b\uff0c\u5546\u5dde\u8282\u5ea6\u4f7f\u5b8c\u989c\u9274\u3002 \u3000\u3000\u8ddf\u4ed6\u6765\u7684\u90a3\u4e24\u4e2a\u968f\u4ece\u662f\u7941\u8fde\u4e8c\u8001\u5e05\u514b\u5546\u548c\u5e05\u514b\u6bb7\u3002 \u3000\u3000\u7941\u8fde\u4e8c\u8001\u662f\u5ba2\u537f\u4e0e\u4efd\uff0c\u5b8c\u989d\u9274\u7684\u624b\u4e0b\uff0c\u4ee5\u4ed6\u4eec\u4e8c\u4eba\u6b66\u529f\u6700\u9ad8\u3002 \u3000\u3000\u5b8c\u989c\u9274\u662f\u8e0f\u8fdb\u5ba2\u5385\u4e4b\u65f6\u53d1\u95ee\u7684\uff0c\u5ba2\u5385\u548c\u5b8c\u989c\u592b\u4eba\u7684\u5367\u5ba4\u8fd8\u9694\u7740\u597d\u51e0\u91cd\u95e8\u6237\u3002 \u3000\u3000\u201c\u5947\u602a\uff0c\u4ed6\u600e\u7684\u7591\u5fc3\u5c4b\u5b50\u91cc\u6709\u5916\u4eba\uff1f\u201d\u8fde\u5fd9\u793a\u7adf\u53eb\u6a80\u7fbd\u51b2\u8eb2\u8fdb\u5979\u7684\u8863\u6a71\u3002 \u3000\u3000\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u5440\uff1f\u201d\u5973\u4ec6\u56de\u7b54\u3002 \u3000\u3000\u539f\u6765\u5b8c\u989c\u9274\u662f\u770b\u89c1\u5ba2\u5385\u7684\u5730\u6bef\u4e0a\u6709\u51e0\u7247\u6ce5\u5c51\u800c\u5f15\u8d77\u7591\u5fc3\u7684\u3002 \u3000\u3000\u5b8c\u989c\u9274\u89c1\u90a3\u5973\u4ec6\u9762\u4e0a\u4f3c\u6709\u60ca\u60f6\u795e\u8272\uff0c\u66f4\u52a0\u8d77\u7591\u3002\u95ee\u9053\uff1a\u201c\u592b\u4eba\u5462\uff1f\u201d \u3000\u3000\u5973\u4ec6\u9053\uff1a\u201c\u592b\u4eba\u8eab\u4f53\u4e0d\u9002\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u597d\u3002\u90a3\u4f60\u4e0d\u5fc5\u60ca\u52a8\u5979\uff0c\u6211\u81ea\u5df1\u8fdb\u53bb\u3002\u5e05\u5927\u5148\u751f\uff0c\u8bf7\u4f60\u8ddf\u6211\u8fdb\u53bb\u3002\u5e05\u4e8c\u5148 \u751f\uff0c\u8bf7\u4f60\u5728\u8fd9\u91cc\u66ff\u6211\u62db\u547c\u5ba2\u4eba\u3002\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u4e0d\u901f\u4e4b\u5ba2\u5230\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5927\u4e3a\u607c\u6012\u3002\u201c\u4ed6\u600e\u80fd\u5e26\u4e2a\u4eba\u95ef\u8fdb\u6211\u7684\u623f\u95f4\uff1f\u201d\u597d\u5728\u53ea\u662f\u5b8c\u989c\u9274\u4e00\u4e2a\u4eba\u8fdb\u6765\uff0c\u5e05 \u8001\u5927\u7559\u5728\u5979\u5367\u5ba4\u5916\u9762\u7684\u4e00\u4e2a\u5c0f\u9662\u5b50\u91cc\u3002 \u3000\u3000\u201c\u592b\u4eba\uff0c\u592b\u4eba\uff0c\u4f60\u770b\u770b\u662f\u8c01\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u672c\u6765\u662f\u60f3\u5047\u88c5\u719f\u7761\u7684\uff0e\u4f46\u6015\u4ed6\u5728\u623f\u95f4\u65f6\u641c\u7d22\uff0c\u53ea\u597d\u88c5\u4f5c\u7ed9\u4ed6\u5435\u9192\uff0c\u7acb\u5373\u5f20\u5f00\u773c \u775b\u3002 \u3000\u3000\u201c\u6211\u521a\u521a\u60f3\u7761\u5348\u89c9\uff0c\u4f60\u6765\u505a\u4ec0\u4e48\uff01\u201d \u3000\u3000\u201c\u5bf9\u4e0d\u4f4f\uff0c\u5435\u9192\u4f60\u4e86\uff0c\u4f60\u4e0d\u9ad8\u5174\u6211\u6765\u770b\u4f60\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e00\u4e2a\u4eba\u8fc7\u60ef\u4e86\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u770b\u6211\uff01\u201d \u3000\u3000\u201c\u592b\u4eba\uff0c\u8fd9\u6b21\u6211\u662f\u4eb2\u81ea\u6765\u63a5\u4f60\u56de\u53bb\u7684\uff01\u201d \u3000\u3000\u201c\u5728\u5546\u5dde\u4f60\u8fd8\u5c11\u5f97\u4e86\u59ec\u59be\u670d\u4f8d\u4f60\u5417\uff1f\u4f60\u82e5\u5acc\u6211\u4e0d\u5b88\u5987\u9053\uff0c\u5c3d\u53ef\u628a\u6211\u4f11\u4e86\u3002\u201d \u3000\u3000\u201c\u592b\u4eba\uff0c\u6211\u81ea\u95ee\u5e76\u6ca1\u6709\u5bf9\u4e0d\u4f4f\u4f60\u5440\uff01\u4f60\u4f55\u5fc5\u8bf4\u8fd9\u6837\u6c14\u8bdd\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u7b49\u4e8e\u662f\u6211\u5bf9\u4e0d\u4f4f\u4f60\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u592b\u4eba\uff0c\u8fc7\u53bb\u7684\u4e8b\u4e0d\u8981\u518d\u63d0\uff0c\u6211\u77e5\u9053\u4f60\u90a3\u6b21\u662f\u4e3a\u4e86\u907f\u5f00\u8036\u5f8b\u7384\u5143\u624d\u8dd1\u6765\u4eac\u5e08\u7684\u3002\u6211\u4e0d\u602a \u4f60\uff0c\u6211\u771f\u7684\u662f\u76fc\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u7d22\u6027\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1b\u201c\u5bf9\u5566\uff0c\u542c\u90a3\u4e2b\u5934\u8bf4\uff0c\u4f60\u4f3c\u4e4e\u6709\u70b9\u8eab\u4f53\u4e0d\u9002\uff0c\u4e0d\u662f\u4ec0\u4e48\u5927\u75c5\u5427\uff1f\u6211\u53bb\u8bf7\u4e2a\u5fa1 \u533b\u6765\u7ed9\u4f60\u770b\u75c5\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\u3002\u201c\u7528\u4e0d\u7740\u3002\u6211\u662f\u8001\u6bdb\u75c5\u5fc3\u6c14\u75db\u3002\u6700\u6015\u548c\u4ee4\u6211\u8ba8\u538c\u7684\u4eba\u5e94\u916c\uff0c\u4f60\u8ba9\u6211\u4e00\u4e2a\u4eba \u9759\u517b\u5427\u3002\u201d \u3000\u3000\u201c\u592b\u4eba\uff0c\u600e\u4e48\u4e0d\u89c1\u5170\u59d1\u90a3\u4e2a\u5973\u513f\uff1f\u201d\u4ed6\u8f6c\u8fc7\u8bdd\u9898\u95ee\u9053\u3002\u201c\u6211\u65e9\u5df2\u628a\u5979\u9001\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u9001\u5f80\u54ea\u513f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u7b54\u6848\u8fde\u8eb2\u5728\u8863\u6a71\u65f6\u91cc\u5077\u542c\u7684\u6a80\u7fbd\u51b2\u90fd\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u592b\u4eba\u8bf4\u7b11\u4e86\uff0c\u662f\u4f60\u628a\u5979\u9001\u8d70\uff0c\u53c8\u600e\u80fd\u4e0d\u77e5\u9053\u662f\u9001\u5f80\u4f55\u65b9\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u5170\u59d1\u662f\u94a6\u72af\u7684\u59bb\u5b50\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u662f\u6a80\u8001\u8d1d\u52d2\u7684\u513f\u5ab3\u5987\u3002\u6a80\u8001\u8d1d\u52d2\u662f\u56e0\u5f97\u7f6a\u5148\u5e1d\u800c\u5f03\u804c\u6f5c\u9003\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u5170\u59d1\u672c\u4eba\u7684\u8eab\u4efd\u4e5f\u662f\u975e\u540c\u5c0f\u53ef\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u7684\u3002\u5979\u662f\u5357\u5b8b\u540d\u5c06\u5cb3\u98de\u7684\u5916\u5b59\u5973\u513f\u3002\u5170\u59d1\u5f53\u7136\u53ea\u662f\u5979\u7684\u5316\u540d\u3002\u53ef\u60dc\u5979\u7684 \u8eab\u4efd\u4e00\u76f4\u5230\u4e86\u5979\u6b7b\u7684\u90a3\u5929\uff0c\u6211\u65b9\u624d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5426\u5219\uff0c\u4f60\u65e9\u5c31\u53ef\u4ee5\u62ff\u5979\u5411\u4f60\u7684\u4f2f\u7236\u5927\u4eba\u9886\u529f\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u7b54\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u5e72\u561b\uff1f\u6211\u60f3\u8981\u77e5\u9053\u5979\u7684\u5973\u513f\u4e0b\u843d\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u5979\u7684\u5973\u513f\u662f\u94a6\u72af\u540e\u4ee3\uff0c\u6211\u6015\u53d7\u5979\u8fde\u7d2f\uff0c\u56e0\u6b64\u6211\u6765\u5230\u4eac\u5e08\uff0c\u5c31\u628a\u5979\u9001\u7ed9\u4e00 \u4e2a\u4e0d\u76f8\u8bc6\u7684\u8fc7\u8def\u4eba\u4e86\u3002\u6211\u600e\u77e5\u5979\u73b0\u4eca\u5728\u4f55\u65b9\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5509\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u8f7b\u6613\u5c06\u5979\u9001\u7ed9\u522b\u4eba\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u662f\u820d\u4e0d\u5f97\u5979\uff0c\u4f46\u6211\u82e5\u7559\u5979\u5728\u6211\u8eab\u8fb9\uff0c\u7ec8\u7a76\u662f\u5bb3\u5979\u6027\u547d\u3002\u6211\u65e2 \u6015\u53d7\u5979\u8fde\u7d2f\uff0c\u53c8\u4e0d\u5fcd\u5bb3\u5979\u6027\u547d\uff0c\u9664\u4e86\u9001\u7ed9\u522b\u4eba\uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u4f60\u8981\u8d23\u602a\uff0c\u5c31\u8d23\u602a\u6211\u5427\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u77e5\u5979\u8bf4\u7684\u662f\u771f\u662f\u5047\uff0c\u552f\u6709\u6447\u5934\u53f9\u606f\u7684\u4efd\u513f\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6765\u4eac\u5e08\u7684\u76ee\u7684\uff0c\u73b0\u5728\u6211\u624d\u5b8c\u5168\u660e\u767d\u3002\u597d\u4e86\uff0c\u4f60\u5e72\u4f60\u7684\u6b63\u7ecf\u4e8b\u53bb \u5427\uff0c\u6211\u8fd8\u8981\u597d\u597d\u7761\u4e00\u89c9\u5462\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u522b\u80e1\u731c\u3002\u6211\u5e76\u975e\u5982\u4f60\u6240\u60f3\u7684\u90a3\u6837\u72e0\u5fc3\u7684\u4eba\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u4e2a\u5927\u5927\u7684\u597d\u4eba\uff0c\u4e0d\u597d\u7684\u662f\u6211\u3002\u591f\u4e86\u5427\uff01\u8c13\u4f60\u8ba9\u6211\u5b89\u9759\u4e00\u4f1a\u597d\u4e0d \u597d\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u518d\u8bf4\u4e00\u53e5\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u80cc\u8fc7\u8eab\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u672c\u6765\u4e0d\u7406\u4ed6\uff0c\u5ffd\u7136\u542c\u5f97\u60a6\u8033\u7684\u8427\u58f0\u3002 \u3000\u3000\u5979\u56de\u8fc7\u8eab\u4e00\u770b\uff0c\u53ea\u89c1\u5b8c\u989c\u9274\u624b\u4e2d\u62ff\u7684\u90a3\u652f\u7389\u7bab\uff0c\u6b63\u662f\u8036\u5f8b\u7384\u5143\u5f53\u5e74\u7ed9\u5979\u7684\u90a3\u652f\u6696\u7389\u7bab\u7684 \u4eff\u5236\u54c1\u3002\u4e5f\u6b63\u662f\u5979\u5728\u51fa\u8d70\u90a3\u5929\uff0c\u7559\u7ed9\u5979\u7684\u4f8d\u5973\u98d8\u9999\u7684\u90a3\u652f\u7389\u7bab\u3002 \u3000\u3000\u201c\u8fd9\u672c\u6765\u662f\u4f60\u7684\u4e1c\u897f\uff0c\u6211\u7ed9\u4f60\u9001\u56de\u6765\u4e86\u3002\u4f60\u559c\u6b22\u5417\uff1f\u201d\u5b8c\u989c\u9274\u9053\u3002 \u3000\u3000\u7779\u7269\u601d\u4eba\uff0c\u5b8c\u989c\u592b\u4eba\u7981\u4e0d\u4f4f\u6fc0\u52a8\u8d77\u6765\uff0c\u63a8\u5f00\u4e08\u592b\u9012\u7ed9\u5979\u7684\u7389\u7bab\uff0c\u8bf4\u9053\uff1a\u201c\u4e1c\u897f\u4f60\u7ed9\u6211\u9001 \u56de\u6765\u4e86\uff0c\u4eba\u5462\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u98d8\u9999\u5427\uff1f\u8fd9\u5c0f\u4e2b\u5934\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u628a\u8fd9\u652f\u7389\u7bab\u62ff\u8d70\u3002\u4f60\u4e5f\u7ed9\u6211\u8d70\uff01\u201d\u5b8c\u989c\u592b\u4eba\u677f\u8d77\u8138\u5b54\uff0c\u4e0d\u5ba2\u6c14\u5730\u7ed9\u4e08\u592b\u4e0b\u4e86\u9010\u5ba2\u4ee4\u3002 \u3000\u3000\u5b8c\u989c\u9274\u966a\u7b11\u9053\uff1a\u201c\u98d8\u9999\u4e0d\u8fc7\u662f\u4e2a\u666e\u901a\u4e2b\u5934\uff0c\u4f60\u4f55\u5fc5\u4e3a\u8fd9\u70b9\u5c0f\u4e8b\u6c14\u607c\uff1f\u201d \u3000\u3000\u201c\u5c0f\u4e8b\uff1f\u201d\u5b8c\u989c\u592b\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u5728\u4f60\u6765\u8bf4\uff0c\u8fd9\u662f\u5bf9\u7684\u3002\u4f60\u662f\u4e2a\u5927\u5c06 \u519b\uff0c\u662f\u4e60\u60ef\u4e86\u628a\u4eba\u547d\u89c6\u540c\u8349\u82a5\u7684\u3002\u54fc\uff0c\u90a3\u4f60\u4e0d\u5982\u7d22\u6027\u5c06\u6211\u4e5f\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u592b\u4eba\uff0c\u4f60\u626f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4f60\u4e00\u5411\u559c\u6b22\u8fd9\u652f\u7389\u7bab\u7684\uff0c\u6536\u4e0b\u5b83\u5427\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u8981\u8fd9\u67d3\u8fc7\u8840\u7684\u7389\u7bab\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u4f6f\u4f5c\u4e0d\u61c2\uff0c\u5b09\u76ae\u7b11\u8138\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u652f\u7389\u7bab\u5f88\u5e72\u51c0\u5440\uff0c\u5e76\u672a\u6cbe\u8fc7\u8840\u7684\uff0c\u6211\u5e76\u4e0d\u9a97 \u4f60\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1b\u201c\u7389\u7bab\u5e72\u51c0\uff0c\u4f60\u7684\u624b\u4e0d\u5e72\u51c0\u3002\u201d\u8bf4\u7f62\u8f6c\u8fc7\u4e86\u8eab\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u628a\u7389\u7bab\u7559\u4e0b\uff0c\u5f85\u4f60\u6c14\u5e73\u4e86\uff0c\u54b1\u4eec\u518d\u8c08\u3002\u54a6\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u521a\u624d\u6a80\u7fbd\u51b2\u8eb2\u5f97\u5306\u5fd9\uff0c\u5fd8\u8bb0\u4e86\u5411\u5b8c\u989c\u592b\u4eba\u8981\u56de\u90a3\u652f\u7389\u8427\u3002\u5b8c\u989c\u592b\u4eba\u5728\u4e08\u592b\u5165\u623f\u7684\u65f6 \u5019\uff0c\u5c06\u5b83\u538b\u5728\u6795\u5934\u4e0b\u9762\u3002\u6b64\u523b\uff0c\u5b8c\u989c\u9274\u628a\u8fd9\u652f\u4eff\u5236\u7684\u7389\u8427\u653e\u5728\u5979\u7684\u6795\u5934\u65c1\u8fb9\uff0c\u53d1\u73b0\u4e86\u90a3\u652f\u9732 \u51fa\u5c11\u8bb8\u7684\u6696\u7389\u7bab\u4e86\u3002 \u3000\u3000\u6696\u7389\u7bab\u4e4b\u6240\u4ee5\u4f1a\u9732\u51fa\u5c11\u8bb8\uff0c\u662f\u56e0\u4e3a\u5b8c\u989c\u592b\u4eba\u5728\u6fc0\u52a8\u4e4b\u4e2d\uff0c\u4e0d\u5c0f\u5fc3\u79fb\u52a8\u4e86\u6795\u5934\u3002 \u3000\u3000\u201c\u54e6\uff0c\u539f\u6765\u4f60\u53e6\u5916\u6709\u4e86\u4e00\u652f\u7389\u7bab\uff0c\u602a\u4e0d\u5f97\u4f60\u4e0d\u60f3\u8981\u539f\u6765\u7684\u7389\u7bab\u4e86\u3002\u4f60\u8fd9\u652f\u7389\u7bab\u7ed9\u6211\u770b \u770b\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u788d\u7740\u59bb\u5b50\u538b\u7740\u6795\u5934\uff0c\u60f3\u62ff\u7389\u8427\uff0c\u53c8\u4e0d\u6562\u63a8\u5f00\u59bb\u5b50\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u8fd9\u4e00\u60ca\u5374\u975e\u540c\u5c0f\u53ef\uff0c\u5979\u751f\u6015\u4e08\u592b\u6765\u62a2\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u5c31\u628a\u7389\u7bab\u7262\u7262\u6293\u4f4f\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u662f\u6211\u53eb\u5de7\u5320\u4eba\u6309\u7167\u539f\u6765\u90a3\u652f\u7389\u7bab\u6a21\u6837\u6253\u9020\u7684\uff0c\u4e24\u652f\u7389\u7bab\u4e00\u6a21\u4e00\u6837\uff0c\u4f60\u4e0d\u7528\u770b\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u8d8a\u53d1\u8d77\u4e86\u7591\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6709\u90a3\u6837\u5de7\u624b\u7684\u5320\u4eba\uff0c\u90a3\u6211\u66f4\u662f\u975e\u770b\u4e0d\u53ef\u4e86\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6012\u9053\uff1a\u201c\u7ed9\u4f60\u770b\u672c\u4e0d\u6253\u7d27\uff0c\u4f46\u6211\u7d20\u6765\u662f\u4e0d\u559c\u6b22\u7ed9\u4eba\u5f3a\u903c\u7684\uff0c\u73b0\u5728\u6211\u8981\u7761\u89c9\uff0c\u4f60 \u7ed9\u6211\u8d70\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u5012\u4e5f\u4e0d\u6562\u8fc7\u5206\u903c\u4ed6\u59bb\u5b50\uff0c\u4f46\u4ed6\u867d\u7136\u4e0d\u6562\u5f3a\u62a2\u7389\u7bab\uff0c\u6307\u5934\u5374\u5df2\u89e6\u53ca\u3002\u90a3\u6e29\u6da6\u5f02\u4e4e\u5bfb \u5e38\u7389\u77f3\u7684\u611f\u89c9\uff0c\u4ee4\u4ed6\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u662f\u77e5\u9053\u8036\u5f8b\u7384\u5143\u6709\u4e00\u652f\u66a7\u7389\u7bab\u7684\uff0c\u201c\u8be5\u4e0d\u4f1a\u8fd9\u6837\u5de7\u5427\uff1f\u96be\u9053\u4ed6\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u5fc3\u6709\u987e\u5fcc\uff0c\u6b63\u81ea\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u4e4b\u9645\u3001\u5ffd\u7136\u542c\u5f97\u5e05\u514b\u6bb7\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6709\u5ba2\u5230\uff01\u201d\u4ed6 \u7684\u58f0\u97f3\u4ece\u5ba2\u5385\u4f20\u6765\uff0c\u5982\u540c\u5bf9\u5750\u4ea4\u8c08\u4e00\u6837\uff0c\u5185\u529b\u4e4b\u6df1\uff0c\u5b8c\u989c\u592b\u4eba\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5fc3\u5934\u4e00\u51db\u3002 \u3000\u3000\u5b8c\u989c\u9274\u63d0\u9ad8\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u8d35\u5ba2\uff1f\u201d \u3000\u3000\u5e05\u514b\u6bb7\u9053\uff1a\u201c\u662f\u91d1\u526f\u7edf\u9886\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u554a\uff0c\u90a3\u53ef\u662f\u8d35\u5ba2\u767b\u95e8\u4e86\uff0c\u8bf7\u91d1\u5927\u4eba\u7a0d\u5019\uff0c\u6211\u5c31\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4f4d\u91d1\u526f\u7edf\u9886\uff0c\u4e43\u662f\u804c\u53f8\u9f99\u9a91\u519b\u526f\u7edf\u9886\u7684\u91d1\u8d85\u5cb3\u3002 \u3000\u3000\u9f99\u9a91\u519b\u662f\u7687\u5e1d\u7684\u4eb2\u5175\uff0c\u548c\u5fa1\u6797\u519b\u7684\u5206\u522b\u662f\uff0c\u5b83\u662f\u4e13\u95e8\u5b88\u536b\u7d2b\u7981\u57ce\u3002\u5fa1\u6797\u519b\u7531\u662f\u62f1\u536b\u4eac\uff0c \u7ba1\u8f96\u7684\u8303\u56f4\u8f83\u3002\u4f46\u82e5\u8bba\u8d77\u548c\u7687\u5e1d\u79c1\u4eba\u7684\u5173\u7cfb\uff0c\u9f99\u9a91\u519b\u66f4\u8fd1\u4e00\u5c42\u3002 \u3000\u3000\u91d1\u8d85\u8d8a\u7684\u804c\u4f4d\u5c31\u662f\u54c8\u6bd4\u56fe\u4ee5\u524d\u4f5c\u7684\u90a3\u4e2a\u804c\u4f4d\uff0c\u4f46\u91d1\u8d85\u8d8a\u7684\u6b66\u529f\uff0c\u636e\u5b8c\u989c\u9274\u6240\u77e5\uff0c\u66f4\u5728\u54c8 \u5fc5\u56fe\u4e4b\u4e0a\u3002\u5f97\u5230\u7687\u4e0a\u7684\u5ba0\u4fe1\uff0c\u5219\u4e0d\u5728\u4ee5\u524d\u7684\u54c8\u5fc5\u56fe\u4e4b\u4e0b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u5728\u5b8c\u989c\u9274\u76ee\u4e2d\u7684\u201c\u8d35\u5ba2\u201d\uff0c\u5728\u5b8c\u989c\u592b\u4eba\u7684\u773c\u4e2d\u5219\u662f\u6076\u5ba2\u3002\u5979\u5c24\u5176\u8ba8\u538c\u91d1\u8d85\u5cb3 \u7684\u59bb\u5b50\uff0c\u8fd9\u4e2a\u5973\u4eba\u662f\u4e2a\u5341\u5206\u52bf\u5229\u7684\u957f\u820c\u5987\uff0c\u6709\u4e8b\u65e0\u4e8b\uff0c\u90fd\u559c\u6b22\u5230\u5979\u8ba4\u4e3a\u662f\u8eab\u4efd\u53ef\u4ee5\u548c\u5979\u76f8\u7b49 \u7684\u4eba\u5bb6\u4e32\u95e8\u3002 \u3000\u3000\u4f46\u4e5f\u5e78\u4e8f\u6709\u8fd9\u4e2a\u6076\u5ba2\u6765\u8bbf\uff0c\u5b8c\u989c\u9274\u4e0d\u6562\u6020\u6162\u7687\u5e1d\u8ddf\u524d\u7684\u7ea2\u4eba\uff0c\u8fd9\u624d\u4e0d\u518d\u548c\u59bb\u5b50\u7ea0\u7f20\u4e0b \u53bb\u3002 \u3000\u3000\u4ed6\u6574\u597d\u8863\u51a0\uff0c\u51fa\u5230\u5ba2\u5385\u4e4b\u65f6\uff0c\u5e05\u514b\u6bb7\u5df2\u7ecf\u628a\u5ba2\u4eba\u8fce\u63a5\u8fdb\u6765\u3002 \u3000\u3000\u4e0d\u4f46\u662f\u91d1\u8d85\u5cb3\u81ea\u5df1\u6765\uff0c\u4ed6\u7684\u59bb\u5b50\u4e5f\u6765\u4e86\u3002\u91d1\u8d85\u5cb3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4f60\u5230\u4eac\u5e08\uff0c\u7279\u5730\u4e0e \u5185\u4eba\u524d\u6765\u62dc\u5019\uff0c\u4f60\u4e0d\u5acc\u6211\u4eec\u6253\u6270\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u5fc3\u91cc\u53c8\u662f\u5f97\u610f\uff0c\u53c8\u662f\u6709\u70b9\u731c\u7591\u3002\u201c\u96be\u9053\u6211\u4ea6\u5df2\u5728\u4ed6\u76d1 \u89c6\u4e4b\u5217\uff1f\u201d \u3000\u3000\u8981\u77e5\u9f99\u9a91\u519b\u526f\u7edf\u9886\u7684\u5b98\u9636\u867d\u7136\u6bd4\u4e0d\u4e0a\u8282\u5ea6\u4f7f\uff0c\u4f46\u4ed6\u662f\u7687\u4e0a\u773c\u524d\u5f97\u5ba0\u7684\u4eba\uff0c\u8981\u662f\u6ca1\u6709\u522b\u7684 \u539f\u56e0\uff0c\u6309\u8bf4\u4ed6\u4e0d\u4f1a\u5148\u6765\u201c\u767b\u95e8\u62dc\u8bbf\u201d\u7684\u3002 \u3000\u3000\u8bdd\u8bf4\u5230\u8fd9\u91cc\uff0c\u90a3\u4e2a\u5973\u4ec6\u6367\u51fa\u8336\u6765\u656c\u5ba2\u3002 \u3000\u3000\u91d1\u592b\u4eba\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u773c\u775b\u671b\u7740\u5b8c\u989c\u9274\uff0c\u8bf4\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u4f60\u4e0d\u602a\u6211\u4e0d\u8bc6\u8da3\uff0c\u8ddf\u6211\u5f53\u5bb6 \u7684\u6765\u4e48\uff1f\u6211\u77e5\u9053\u4f60\u4eec\u8fd9\u4e9b\u6709\u4e00\u5b98\u534a\u804c\u7684\u7537\u4eba\u89c1\u4e86\u9762\uff0c\u5c11\u4e0d\u514d\u8981\u8c08\u53ca\u516c\u4e8b\u3002\u6709\u6211\u4eec\u5987\u9053\u4eba\u5bb6\u5927 \u573a\u2026\u2026\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u90a3\u91cc\u8bdd\u6765\uff0c\u6211\u4eec\u662f\u901a\u5bb6\u4e4b\u597d\uff0c\u5c31\u50cf\u81ea\u5df1\u4eba\u4e00\u6837\u3002\u6211\u548c\u91d1\u5927\u54e5\u8bf4\u5f97\u7684 \u8bdd\uff0c\u8fd8\u6015\u5ac2\u5b50\u4f60\u542c\u4e0d\u5f97\u5417\uff1f\u6211\u4eec\u5176\u5b9e\u6ca1\u6709\u4ec0\u4e48\u516c\u4e8b\u8981\u8c08\u3002\u201d\u4ed6\u6545\u610f\u628a\u5173\u7cfb\u62c9\u8fd1\u4e00\u5c42\uff0c\u5c06\u201c\u91d1 \u5927\u4eba\u201d\u7684\u79f0\u53f7\u4e3a\u201c\u91d1\u5927\u54e5\u201d\u4e86\u3002 \u3000\u3000\u91d1\u592b\u4eba\u4f3c\u7b11\u975e\u7b11\u9053\u3002\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u4f60\u522b\u602a\u6211\u8bf4\u76f4\u6d3b\uff0c\u6211\u4e0d\u662f\u6765\u7ed9\u4f60\u63a5\u98ce\u7684\uff0c\u6211\u662f\u7279\u5730\u6765 \u63a2\u671b\u5c0a\u592b\u4eba\u7684\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u8336\u676f\u653e\u4e0b\u3002 \u3000\u3000\u5f26\u5916\u4e4b\u97f3\uff0c\u597d\u50cf\u662f\u4e0d\u6ee1\u5973\u4e3b\u4eba\u6ca1\u6709\u51fa\u6765\u62db\u5f85\uff0c\u53ea\u53eb\u4e2b\u73af\u5949\u8336\u3002 \u3000\u3000\u5b8c\u989c\u9274\u966a\u7b11\u9053\uff1a\u201c\u5185\u5b50\u8eab\u4f53\u6709\u70b9\u4e0d\u9002\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u8fd9\u662f\u771f\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\u3002\u201c\u4ec0\u4e48\u771f\u7684\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u524d\u4e24\u5929\u6211\u5c31\u542c\u5f97\u8bf4\u5c0a\u592b\u4eba\u7389\u4f53\u8fdd\u548c\uff0c\u4f46\u53c8\u4e0d\u89c1\u6709\u5fa1\u533b\u6765\u8fc7\u9493\u9c7c\u53f0\uff0c\u662f\u4ee5\u6211 \u60f3\u6765\u63a2\u75c5\uff0c\u4e5f\u4e0d\u6562\u5192\u6627\uff0c\u8c01\u77e5\u9053\u7adf\u662f\u771f\u7684\u3002\u5b8c\u989c\u5927\u4eba\uff0c\u731c\u60f3\u6211\u6043\u719f\u4e70\u719f\uff0c\u4f60\u4e0d\u7528\u966a\u6211\uff0c\u4f60\u4eec \u5728\u8fd9\u91cc\u8bf4\u8bdd\uff0c\u6211\u81ea\u5df1\u8fc7\u53bb\u95ee\u5019\u5c0a\u592b\u4eba\u3002\u201d \u3000\u3000\u63a2\u75c5\u662f\u4e0d\u7528\u8fd9\u6837\u7d27\u5f20\u7684\uff0c\u800c\u4e14\u5979\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u4e5f\u5f15\u8d77\u5b8c\u989c\u9274\u7684\u7591\u60d1\uff1a\u201c\u4ec0\u4e48\u771f\u7684\u5047\u7684\uff0c \u83ab\u975e\u5979\u662f\u7591\u5fc3\u6211\u7684\u59bb\u5b50\u88c5\u75c5\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e5f\u662f\u6709\u7740\u8fd9\u6837\u7591\u5fc3\uff0c\u751a\u81f3\u4ed6\u7684\u7591\u5fc3\u8fd8\u91cd\u4e00\u4e9b\uff0c\u5728\u4ed6\u53d1\u73b0\u4e86\u90a3\u652f\u7389\u7bab\u4e4b\u540e\uff0c\u4f46\u4e5f\u6b63 \u56e0\u4e3a\u4ed6\u7684\u7591\u5fc3\u66f4\u91cd\uff0c\u4ed6\u5c31\u66f4\u52a0\u4e0d\u613f\u610f\u8fd9\u4e2a\u7231\u7ba1\u95f2\u4e8b\u3001\u7231\u8bf4\u95f2\u8bdd\u7684\u957f\u820c\u5987\u4eba\u8fdb\u5165\u4ed6\u59bb\u5b50\u7684\u5367 \u623f\u3002 \u3000\u3000\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u62d9\u8346\u6ca1\u6709\u4ec0\u4e48\u5927\u75c5\uff0c\u4e0d\u8fc7\u5bfb\u5e38\u7684\u5fc3\u6c14\u75db\u800c\u5df2\u3002\u5979\u521a\u521a\u719f\u7761\uff0c\u4e0d\u6562 \u6709\u52b3\u5ac2\u592b\u4eba\u53bb\u770b\u5979\u4e86\u3002\u5f85\u5979\u9192\u4e86\uff0c\u6211\u518d\u53eb\u5979\u8e35\u5e9c\u7b54\u8c22\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u554a\uff0c\u5fc3\u6c14\u75db\u53ef\u4e0d\u662f\u5c0f\u6bdb\u75c5\u554a\uff01\u4fd7\u8bed\u8bf4\uff0c\u5fc3\u75c5\u662f\u6700\u96be\u533b\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u677e\u4e86\u53e3\u6c14\uff0c\u4e0e\u91d1\u592b\u4eba\u4e00\u540c\u5750\u4e0b\u3002\u90a3\u5973\u4ec6\u5219\u6536\u62fe\u8336\u5177\uff0c\u6b63\u60f3\u8d70\u5f00\u3002 \u3000\u3000\u91d1\u592b\u4eba\u5374\u5ffd\u5730\u53eb\u5979\u56de\u6765\u3002 \u3000\u3000\u90a3\u5973\u4ec6\u9053\uff1a\u201c\u91d1\u592b\u4eba\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u4f60\u7684\u4e3b\u5b50\uff0c\u600e\u6562\u5429\u5490\u4f60\uff1f\u4e0d\u8fc7\uff0c\u53ea\u662f\u60f3\u8bf7\u4f60\u6682\u4e14\u7559\u4e0b\uff0c\u8bf4\u4e0d\u5b9a\u4f60 \u7684\u4e3b\u4eba\u6709\u8bdd\u95ee\u4f60\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u66f4\u53e4\u602a\u4e86\uff0c\u5b8c\u989c\u9274\u6682\u4e14\u4e0d\u4f5c\u58f0\uff0c\u770b\u91d1\u592b\u4eba\u600e\u6837\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u91d1\u592b\u4eba\u628a\u676f\u4e2d\u5269\u4e0b\u7684\u8336\u559d\u5e72\u51c0\uff0c\u6e05\u6e05\u5589\u5499\u3002\u8bf4\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u4f60\u522b\u602a\u6211\u591a\u7ba1\u95f2\u4e8b\u3002\u4f60 \u7684\u5e72\u5973\u513f\u5462\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e00\u6014\u9053\uff1b\u201c\u6211\u54ea\u91cc\u6765\u7684\u5e72\u5973\u513f\uff1f\u201d\u968f\u5373\u7701\u609f\uff0c\u201c\u6562\u60c5\u4f60\u8bf4\u7684\u662f\u8d31\u5185\u4ece\u5546\u5dde\u5e26\u6765\u7684 \u90a3\u4e2a\u5c0f\u4e2b\u5934\u5427\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5979\u662f\u4e2b\u5934\u4e48\uff1f\u6211\u89c1\u592b\u4eba\u90a3\u6837\u75bc\u5979\uff0c\u7b80\u76f4\u5c31\u50cf\u4eb2\u751f\u5973\u513f\u4e00\u6837\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5979\u662f\u4e2a\u5b64\u5973\uff0c\u4e94\u5c81\u5c31\u5931\u4e86\u6bcd\u4eb2\uff0c\u7531\u5185\u5b50\u6536\u517b\u5979\u7684\u3002\u5185\u5b50\u5e76\u65e0\u6240\u51fa\uff0c\u5bf9\u5979\u5ba0 \u7231\u786e\u662f\u8fc7\u4efd\u4e86\u4e9b\u3002\u91d1\u592b\u4eba\uff0c\u600e\u7684\u4f60\u5bf9\u6211\u5bb6\u7684\u4e2b\u5934\u4e5f\u8fd9\u6837\u5173\u5fc3\u3002\u201d\u91d1\u592b\u4eba\u4f3c\u7b11\u975e\u7b11\u5730\u8bf4\u9053\uff1a \u201c\u5c0a\u592b\u4eba\u5ba0\u7231\u7684\u4e2b\u5934\u6211\u600e\u80fd\u4e0d\u5173\u5fc3\uff0c\u4e0d\u8fc7\uff0c\u6700\u5173\u5fc3\u5979\u7684\u4eba\u5374\u8fd8\u4e0d\u662f\u6211\u5462\u3002\u201d\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f \u8c01\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u60f3\u5fc5\u4f60\u77e5\u9053\u793c\u90e8\u7684\u53f2\u4f8d\u90ce\u5427\uff0c\u4ed6\u4e5f\u662f\u4f4f\u5728\u9493\u9c7c\u53f0\u7684\uff0c\u4ed6\u6709\u4e2a\u513f\u5b50\uff0c\u4e73\u540d\u5b9d \u5b98\uff0c\u4eca\u5e74\u4e0d\u8fc7\u5341\u4e09\u5c81\u5427\uff0c\u8bfb\u4e66\u662f\u806a\u660e\u5f97\u5f88\uff0c\u542c\u8bf4\u5df2\u53ef\u541f\u8bd7\u4f5c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u662f\u5417\uff1f\u6211\u89c1\u4e86\u53f2\u4f8d\u90ce\uff0c\u5012\u8981\u606d\u8d3a\u4ed6\u6709\u6b64\u4f73\u513f\u4e86\u3002\u4f46\u4ed6\u7684\u513f\u5b50\u8bfb\u4e66\u806a\u660e\uff0c\u5374 \u53c8\u4e0e\u6211\u5bb6\u4f55\u5e72\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u6700\u5173\u5fc3\u90a3\u4e2b\u5934\u7684\u4eba\uff0c\u5c31\u662f\u8fd9\u4e2a\u5b9d\u5b98\u3002\u4ed6\u4eec\u5e38\u5e38\u5728\u4e00\u8d77\u8bfb\u4e66\uff0c\u4e00\u8d77\u73a9\u800d \u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u4e0d\u77e5\u5c0a\u5351\uff0c\u662f\u5185\u5b50\u5ba0\u574f\u5979\u3002\u201d \u3000\u3000\u201c\u4f46\u5947\u602a\u7684\u662f\uff0c\u8fd9\u51e0\u5929\u5b9d\u5b98\u53bb\u627e\u90a3\u4e2b\u5934\uff0c\u5374\u4e0d\u89c1\u5979\u4e86\u3002\u4f60\u5bb6\u7684\u4ec6\u4eba\u53ea\u662f\u56de\u8bf4\u90a3\u4e2b\u5934\u4e0d\u5728 \u8fd9\u91cc\uff0c\u8fde\u95e8\u4e5f\u6ca1\u5f00\u3002\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u662f\u53f2\u4f8d\u90ce\u7684\u592b\u4eba\u548c\u6211\u8bf4\u7684\uff0c\u5979\u8bf4\u7684\u65f6\u5019\u8fd8\u6709\u70b9\u751f\u6c14\u5462\uff01\u5979\u8bf4 \u6211\u5bb6\u5b9d\u5b98\u662f\u5e38\u5e38\u5230\u5979\u5bb6\u91cc\u73a9\u800d\u7684\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u53bb\u627e\u4e00\u4e2a\u4e2b\u5934\uff0c\u4e5f\u906d\u95ed\u95e8\u4e0d\u7eb3\u3002\u201d\u8bf4\u8bdd\u4e4b\u9645\uff0c \u773c\u775b\u671b\u7740\u90a3\u4e2a\u5973\u4ec6\u3002\u610f\u601d\u663e\u7136\u662f\u8981\u5b8c\u989c\u7bad\u5bf9\u5979\u67e5\u95ee\u3002\u90a3\u5973\u4ec6\u53ea\u9053\uff1a\u201c\u592b\u4eba\u6709\u75c5\uff0c\u6ca1\u5de5\u592b\u7406\u5c0f \u5b69\u5b50\u7684\u4e8b\u60c5\u3002\u662f\u5979\u5429\u5490\u6211\u8fd9\u6837\u56de\u590d\u5b9d\u5b98\u7684\u3002\u201d\u4f46\u5979\u5374\u6ca1\u6709\u8bf4\u90a3\u4e2b\u5934\u5230\u5e95\u5728\u4e0d\u5728\u5bb6\u3002 \u3000\u3000\u5b8c\u989c\u9274\u53ea\u597d\u66ff\u59bb\u5b50\u5b8c\u8c0e\uff1a\u201c\u8fd9\u5c0f\u4e2b\u5934\u5185\u5b50\u5df2\u7ecf\u5c06\u5979\u9001\u7ed9\u4eba\u4e86\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u8be7\u9053\uff1b\u201c\u5c0a\u592b\u4eba\u5f53\u8fd9\u5c0f\u4e2b\u5934\u5982\u73cd\u4f3c\u5b9d\uff0c\u4f55\u4ee5\u53c8\u820d\u5f97\u9001\u4eba\u5462\uff1f\u9001\u4e86\u7ed9\u8c01\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u521a\u521a\u56de\u5bb6\uff0c\u8fd8\u6ca1\u5de5\u592b\u95ee\u53ca\u8fd9\u4e9b\u5c0f\u4e8b\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5df2\u662f\u6709\u70b9\u4e0d\u6ee1\u91d1\u592b\u4eba \u7684\u7f57\u55e6\u3002 \u3000\u3000\u504f\u504f\u91d1\u592b\u4eba\u4e0d\u8bc6\u8da3\uff0c\u4ecd\u7136\u4e0d\u80af\u653e\u5f03\u539f\u6765\u7684\u8bdd\u9898\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u771f\u7684\u5417\uff1f\u6211\u8fd8\u4ee5\u4e3a\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u9274\u5927\u6012\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u4f60\u8fd9\u6837\u8bf4\u5012\u662f\u628a\u6211\u5f53\u4f5c\u5916\u4eba\u4e86\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u662f\u6709\u70b9\u5947\u602a\uff0c\u6216\u8bb8\u662f\u6211\u4eec\u778e\u7591\u5fc3\uff0c\u4e0d\u8fc7\uff0c\u8bf4\u9519\u4e86\u4f60\u4e5f \u4e0d\u4f1a\u602a\u6211\uff0c\u6211\u5c31\u8bf4\u4e86\u5427\u3002\u4e94\u5929\u524d\uff0c\u4f60\u4eec\u5bb6\u91cc\u6765\u4e86\u4e00\u4e2a\u5947\u602a\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u51e0\u4e4e\u542c\u5f97\u89c1\u81ea\u5df1\u7684\u5fc3\u8df3\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u4e00\u4e2a\u751f\u9762\u7684\u9b41\u68a7\u6c49\u5b50\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u7a0d\u5b89\u5fc3\uff0c\u8036\u5f8b\u7384\u5143\u5916\u8c8c\u662f\u4e2a\u4fca\u96c5\u4e66\u751f\uff0c\u6b66\u529f\u867d\u7136\u5353\u7edd\u8eab\u88c1\u5374\u662f\u79f0\u4e0d\u4e0a\u201c\u9b41\u68a7\u201d \u7684\u3002 \u3000\u3000\u201c\u4ed6\u600e\u6837\u5947\u602a\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u9493\u9c7c\u53f0\u662f\u5f88\u5c11\u751f\u9762\u4eba\u6765\u7684\uff0c\u800c\u4e14\u5c0a\u592b\u4eba\u5728\u8fd9\u91cc\u4f4f\u4e86\u4e03\u516b\u5e74\uff0c\u6211\u4eec\u4ece\u672a\u89c1\u8fc7 \u5979\u6709\u5ba2\u4eba\u6765\u8bbf\uff0c\u5c31\u51ed\u8fd9\u4e24\u70b9\uff0c\u4e0d\u5c31\u662f\u5df2\u7ecf\u6709\u70b9\u5947\u602a\u5417\uff1f\u201d\u4f46\u770b\u5979\u7684\u795e\u6c14\uff0c\u201c\u5947\u602a\u201d\u4e4b\u5904\uff0c\u663e \u7136\u4e0d\u6b62\u8fd9\u4e24\u70b9\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u80fd\u4e0d\u95ee\u90a3\u5973\u4ec6\u4e86\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\uff0c\u4ed6\u6765\u6211\u5bb6\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u4ec6\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u8fd9\u6837\u7684\uff0c\u540e\u56ed\u6709\u4e2a\u82b1\u67b6\u584c\u4e86\u3001\u9ad8\u5927\u53d4\u5e74\u8001\u4f53\u5f31\uff0c\u53eb\u4ed6\u4e00\u4e2a\u540c\u4e61\u6765\u5e2e \u5fd9\u91cd\u4fee\u82b1\u67b6\u3002\u201d\u5973\u4ec6\u53e3\u4e2d\u7684\u201c\u9ad8\u5927\u53d4\u201d\u4e43\u662f\u5b8c\u989c\u592b\u4eba\u552f\u4e00\u7684\u7537\u4ec6\u4eba\u3002 \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u90a3\u9ad8\u8001\u5934\u597d\u50cf\u4e5f\u8d70\u4e86\u5427\uff1f\u201d \u3000\u3000\u90a3\u5973\u4ec6\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9ad8\u5927\u53d4\u5e74\u8001\u601d\u5bb6\uff0c\u592b\u4eba\u7ed9\u4ed6\u4e00\u4e2a\u6708\u5047\u671f\uff0c\u8ba9\u4ed6\u56de\u4e61\u63a2\u4eb2\u3002\u4fee\u82b1\u526a\u8349 \u7684\u4e8b\u60c5\u4e0d\u7528\u591a\u5927\u6c14\u529b\uff0c\u6211\u53ef\u4ee5\u517c\u987e\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u5de7\u554a\u3002\u90a3\u964c\u751f\u5ba2\u4eba\u521a\u521a\u6765\u8fc7\uff0c\u9ad8\u8001\u5934\u5c31\u8981\u56de\u4e61\u63a2\u4eb2\u4e86\u3002\u201d\u5973\u4ec6\u4eba\u5df2 \u7ecf\u8bf4\u660e\u90a3\u4eba\u662f\u8bf7\u6765\u505a\u201c\u6563\u5de5\u201d\u7684\uff0c\u5979\u8fd8\u662f\u79f0\u4e3a\u201c\u5ba2\u4eba\u201d\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e0d\u7981\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u3001\u5927\u5ac2\u4f60\u4eec\u5bf9\u90a3\u4eba\u6709\u751a\u6000\u7591\u4e5f\u4e0d\u59a8\u5bf9\u6211\u76f4\u8bf4\uff01\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1b\u201c\u90a3\u4e2a\u9ad8\u8001\u5934\u662f\u4ec0\u4e48\u5730\u65b9\u7684\u4eba\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\u2014\u2014\u201d\u628a\u773c\u775b\u671b\u5411\u90a3\u4e2a\u5973\u4ec6\u3002 \u3000\u3000\u90a3\u4e2a\u5973\u4ec6\u9053\uff1a\u201c\u9ad8\u5927\u53d4\u662f\u5c71\u4e1c\u8377\u6cfd\u4eba\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6709\u70b9\u5947\u602a\u4e86\uff0c\u4f60\u4e0d\u662f\u8bf4\u90a3\u4e2a\u4eba\u662f\u9ad8\u8001\u5934\u7684\u540c\u4e61\u5417\uff1f\u4f46\u90a3\u4e2a\u4eba\u5374\u597d\u50cf \u662f\u6c5f\u5357\u4eba\u6c0f\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u8be7\u9053\uff1a\u201c\u5ac2\u592b\u4eba\uff0c\u4f60\u53c8\u600e\u77e5\u9053\u4ed6\u662f\u6c5f\u5357\u4eba\u6c0f\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u8d85\u5cb3\uff0c\u8fd8\u662f\u4f60\u6765\u8bf4\u5427\u3002\u4f60\u77e5\u9053\u5f97\u6bd4\u6211\u591a\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u5982\u679c\u8001\u5362\u6ca1\u6709\u770b\u9519\u7684\u8bdd\uff0c\u90a3\u4e2a\u4eba\u8fd8\u662f\u4e2a\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u5462\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8001\u5362\uff0c\u90a3\u4e2a\u8001\u5362\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u4ee5\u524d\u66fe\u7ecf\u5728\u4ee4\u4f2f\u624b\u4e0b\u5f53\u8fc7\u5dee\u7684\u5362\u5fd7\u9ad8\uff0c\u4ed6\u73b0\u5728\u5df2\u7ecf\u662f\u5927\u5185\u4f8d\u536b\uff0c \u5e76\u4e14\u662f\u5f97\u5230\u7687\u4e0a\u601d\u8d4f\u4e8c\u7b49\u5df4\u56fe\u9c81\u5934\u8854\u7684\u4e86\u3002\u4ed6\u4e5f\u662f\u4f4f\u5728\u9493\u9c7c\u53f0\u7684\uff0c\u90a3\u5929\u4ed6\u6070\u597d\u4f11\u5047\u5728\u5bb6\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5362\u5fd7\u9ad8\u8ba4\u8bc6\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u5362\u5fd7\u9ad8\u672c\u662f\u6c5f\u5357\u6c49\u4eba\uff0c\u4e0d\u8fc7\u4ed6\u7684\u6765\u5386\u5927\u6982\u4f60\u8fd8\u4e0d\u5f88\u6e05\u695a\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u4ed6\u662f\u6c5f\u5357\u9ed1\u9053\u4e0a\u51fa\u8eab\u7684\uff0c\u540e\u6765\u5728\u6c5f\u5357\u7ad9\u4e0d\u4f4f\u811a\uff0c\u624d\u8dd1\u5230\u5230\u54b1\u8fd9\u8fb9\u6765\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u6697\u6697\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u548c\u4ed6\u7684\u6765\u5386\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u5f53\u7136\u6709\u6765\u5386\uff0c\u5c31\u56e0\u4e3a\u4ed6\u662f\u6c5f\u5357\u9ed1\u9053\u7684\u51fa\u8eab\uff0c\u6240\u4ee5\u4ed6\u624d\u8ba4\u5f97\u90a3\u4e2a\u5ba2\u4eba\u3002\u5b8c\u989c \u5927\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\u6c5f\u5357\u6709\u4e2a\u738b\u5b87\u5ead\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u592a\u6e56\u4e03\u5341\u4e8c\u5bb6\u6c34\u5bc7\u603b\u98d8\u628a\u5b50\u7684\u90a3\u4e2a\u738b\u5b87\u5ead\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u662f\u5440\uff0c\u5c31\u662f\u8fd9\u4e2a\u738b\u5b87\u5ead\u3002\u8fd9\u4e2a\u738b\u5b87\u5ead\u4e0d\u4f46\u662f\u548c\u5357\u5b8b\u5b98\u5bb6\u4f5c\u5bf9\u7684\u592a\u6e56\u76d7\u9b41\uff0c \u4ed6\u4e5f\u66fe\u548c\u54b1\u4eec\u5927\u91d1\u7684\u5b98\u5175\u6253\u8fc7\u4ed7\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5362\u5fd7\u9ad8\u8ba4\u5f97\u679c\u7136\u662f\u4ed6\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1b\u201c\u4f46\u613f\u4ed6\u662f\u8ba4\u9519\u4e86\u4eba\u3002\u4e0d\u8fc7\u738b\u5b87\u5ead\u751f\u5357\u4eba\u5317\u76f8\uff0c\u76f8\u8c8c\u662f\u6bd4\u8f83\u6709\u70b9\u7279\u522b\u7684\uff0c\u5362 \u5fd7\u9ad8\u66fe\u7ecf\u548c\u4ed6\u559d\u8fc7\u8840\u9152\uff0c\u4f3c\u4e4e\u4e0d\u81f3\u4e8e\u8ba4\u9519\u4eba\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u8bf4\u4e0d\u51fa\u8bdd\u4e86\u3002 \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6837\u5947\u602a\u7684\u662f\uff0c\u90a3\u5929\u662f\u90a3\u5c0f\u4e2b\u5934\u9001\u2018\u5ba2\u2019\u51fa\u95e8\u7684\u3002\u5047\u5982\u90a3\u4eba\u771f\u7684\u53ea\u662f \u9ad8\u8001\u5934\u8bf7\u6765\u7684\u6563\u5de5\uff0c\u4f3c\u4e4e\u7528\u4e0d\u7740\u592b\u4eba\u7684\u5b9d\u8d1d\u4e2b\u5934\u6765\u9001\u4ed6\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9762\u4e0a\u53d8\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5ac2\u592b\u4eba\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u5fc3\u6709\u6240\u7591\u4f46\u201c\u83ab\u975e\u4f60\u662f\u6000\u7591 \u5185\u5b50\u548c\u738b\u5b87\u5ead\u6709\u751a\u5173\u7cfb\u201d\uff0c\u8fd9\u53e5\u8bdd\u5374\u662f\u4e0d\u6562\u95ee\u51fa\u6765\u3002 \u3000\u3000\u91d1\u592b\u4eba\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u6211\u53ea\u662f\u89c9\u5f97\u6709\u70b9\u5947\u602a\u800c\u5df2\u3002\u738b\u5b87\u5ead\u6765\u8fc7\u4e4b\u540e\uff0c\u90a3\u4e2a\u4e2b \u5934\u5c31\u4e0d\u89c1\u4e86\u3002\u6211\u8fd8\u4ee5\u4e3a\u90a3\u5c0f\u4e2b\u5934\u662f\u8ddf\u738b\u5b88\u5ead\u8d70\u4e86\u5462\u3002\u73b0\u5728\u624d\u77e5\u9053\uff0c\u539f\u7c73\u662f\u5c0a\u592b\u4eba\u5c06\u5979\u9001\u7ed9\u522b \u4eba\uff0c\u6211\u8fd8\u80fd\u6709\u4ec0\u4e48\u6000\u7591\u5462\uff1f\u201c\u5979\u8fd9\u6837\u7b49\u4e8e\u662f\u660e\u767d\u544a\u8bc9\u5b8c\u989c\u9274\uff0c\u5979\u5b9e\u5728\u662f\u5df2\u6709\u6000\u7591\u3002 \u3000\u3000\u5b8c\u989c\u9274\u53ea\u597d\u88c5\u5446\uff0c\u54fc\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u6211\u662f\u4e00\u5b9a\u8981\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u7684\uff0c\u5f85\u9ad8\u8001\u5934\u56de\u6765\uff0c \u6211\u4ed4\u7ec6\u5ba1\u95ee\u4ed6\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c31\u53ea\u6015\u4ed6\u4e0d\u4f1a\u56de\u6765\u4e86\u3002\u55ef\uff0c\u4e0d\u8be5\u8d70\u7684\u8d70\u4e86\uff0c\u4e0d\u8be5\u6765\u7684\u5374\u6765\u4e86\u3002\u8fd9\u53ef \u771f\u662f\u65e0\u72ec\u6709\u5076\u201d\uff0c\u518d\u7b28\u7684\u4eba\u4ea6\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\uff0c\u5979\u662f\u8bdd\u4e2d\u6709\u8bdd\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9762\u8272\u66f4\u52a0\u96be\u770b\uff0c\u8bf4\u9053\uff1b\u201c\u54e6\uff0c\u65e0\u72ec\u6709\u5076\uff1f\u201d\u91d1\u592b\u4eba\u9053\uff1a\u201c\u662f\u5440\u3002\u9ad8\u8001\u5934\u548c\u90a3\u5c0f\u4e2b \u73af\u8fd8\u4e0d\u90fd\u662f\u4e0d\u8be5\u8d70\u800c\u8d70\u7684\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u8be5\u6765\u800c\u7684\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u738b\u5b87\u5ead\u662f\u4e00\u4e2a\u2026\u2026\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u6545\u610f\u987f\u4e86\u4e00\u987f\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u5ac2\u592b\u4eba\uff0c\u4f60\u8fd9\u6837\u8bf4\uff0c\u90a3\u5c31\u662f\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u3001\u7b2c\u4e09\u4e2a\u4e86\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u662f\u5426\u6709\u7b2c\u4e09\u4e2a\u6211\u4e0d\u77e5\uff0c\u4e0d\u8fc7\u8fd1\u65e5\u6765\u5230\u4f60\u5bb6\u7684\u964c\u751f\u5ba2\u4eba\uff0c\u9664\u4e86\u738b\u5b87\u5ead\u4e4b\u5916\uff0c \u6700\u5c11\u6211\u77e5\u9053\u8fd8\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u7684\u5fc3\u53c8\u662f\u4e00\u8df3\uff0c\u6da9\u58f0\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u5374\u56de\u8fc7\u5934\u95ee\u90a3\u5973\u4ec6\uff1a\u201c\u90a3\u4e2a\u81ea\u79f0\u662f\u6765\u9001\u82b1\u7684\u5c0f\u53ae\u5462\uff1f\u5927\u6982\u4ed6\u8fd8\u5728\u8fd9\u91cc\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\u9001\u82b1\u7684\u5c0f\u53ae\uff1f\u201d \u3000\u3000\u90a3\u5973\u4ec6\u9053\uff1a\u201c\u521a\u624d\u662f\u6709\u4e2a\u5356\u82b1\u7684\u5c0f\u53ae\u6765\u8fc7\uff0c\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u5230\u5e95\u662f\u6765\u5356\u82b1\u8fd8\u662f\u6765\u9001\u82b1\uff0c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u8bf4\u548c\u6e05\u695a\u4e00\u70b9\uff1f\u90a3\u5973\u4ec6\u5fc3\u614c\u610f\u4e71\uff0c \u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u7f16\u9020\u8c0e\u8bdd\uff0c\u66ff\u4e3b\u6bcd\u5e94\u4ed8\u8fd9\u4e2a\u957f\u820c\u5987\u4eba\u3002 \u3000\u3000\u91d1\u592b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u6211\u662f\u65e0\u6743\u76d8\u95ee\u4f60\u7684\u4e2b\u5934\u7684\uff0c\u8fd8\u662f\u4f60\u6765\u95ee\u5979\u5427\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u6211\u521a\u521a\u56de\u6765\uff0c\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u3002\u5ac2\u592b\u4eba\uff0c\u9ebb\u70e6\u4f60\u66ff\u6211\u5ba1\u95ee \u8fd9\u4e2a\u4e2b\u5934\u3002\u201d \u3000\u3000\u63a5\u7740\u559d\u90a3\u4e2b\u5934\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u8fd9\u6837\u4e0d\u61c2\u793c\u8c8c\uff0c\u597d\u597d\u5730\u56de\u7b54\u91d1\u592b\u4eba\u3002\u201c\u5973\u4e2b\u5fcd\u4e0d\u4f4f\u773c\u6cea\uff0c\u8bf4 \u9053\uff1a\u201c\u662f\uff0c\u6211\u77e5\u9053\u7684\u5b9a\u5f53\u5b9e\u8bf4\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u597d\u3002\u90a3\u4e48\u6211\u6765\u95ee\u4f60\uff0c\u8fd9\u4e24\u5929\u4f60\u51fa\u8fc7\u95e8\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5973\u4e2b\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u4f60\u5373\u7136\u6ca1\u6709\u51fa\u8fc7\u95e8\uff0c\u90a3\u4e48\u662f\u8c01\u5230\u82b1\u5e97\u5b9a\u82b1\uff1f\u5f53\u7136\u4e0d\u4f1a\u662f\u4f60\u5bb6\u592b\u4eba\u5427\uff1f\u201d \u3000\u3000\u5973\u4e2b\u9053\uff1a\u201c\u90a3\u5c0f\u53ae\u662f\u4e0a\u95e8\u53eb\u5356\u7684\uff0c\u5e76\u4e0d\u662f\u6211\u4eec\u53eb\u4ed6\u9001\u6765\u7684\u5440\uff01\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u5927\u5ac2\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\u3002\u201c\u5c31\u5728\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\u4e4b\u524d\u3002\u53f2\u4f8d\u6717\u7684\u5b9d\u5b98\u548c\u5c0f\u5973\u4e00\u8d77\u73a9\u800d\uff0c\u6070\u597d\u78b0\u4e0a\u90a3\u4e2a\u82b1 \u5e97\u7684\u5c0f\u53ae\uff0c\u5b9d\u5bab\u60f3\u548c\u4ed6\u4e70\u4e00\u652f\u9ed1\u7261\u4e39\u9001\u7ed9\u4f60\u5bb6\u7684\u5c0f\u4e2b\u5934\uff0c\u5c0f\u53ae\u4e0d\u5356\uff0c\u8bf4\u662f\u4f60\u5bb6\u592b\u4eba\u5df2\u7ecf\u5b9a \u4e0b\uff0c\u4ed6\u662f\u66ff\u82b1\u5e97\u6765\u9001\u82b1\u7684\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u76b1\u7709\u9053\u3002\u201c\u5982\u6b64\u8bf4\uff0c\u662f\u90a3\u5c0f\u53ae\u8bf4\u8c0e\u4e86\u3002\u4e3a\u4ec0\u4e48\u4ed6\u8981\u8bf4\u8c0e\u5462\uff1f\u662f\u7ed9\u522b\u4eba\u9001\u4fe1\u8fd8\u662f\u4ed6 \u81ea\u5df1\u6709\u76ee\u7684\u800c\u6765\uff1f\u201d\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u7591\u5fc3\u5927\u8d77\uff0c\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u5374\u5df1\u653e\u4e0b\uff0c\u82b1\u5e97\u7684\u5c0f\u53ae\u5f53\u7136\u4e5f \u4e0d\u4f1a\u662f\u771f\u6b63\u7684\u82b1\u5e97\u5c0f\u65af\u4e86\u3002\u201c\u4ed6\u8fd8\u6709\u4e00\u6837\u5947\u602a\u7684\u5730\u65b9\u5462\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u602a\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u5374\u53cd\u95ee\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u542c\u8bf4\u4f60\u7684\u5546\u5dde\u7684\u82b1\u56ed\u79cd\u6709\u8bb8\u591a\u540d\u79cd\u7684\u7261\u4e39\uff0c\u4f60\u542c\u8fc7\u6709\u4e00 \u79cd\u7261\u4e39\u53eb\u505a\u9752\u9f99\u5367\u58a8\u6c60\u7684\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u7684\u82b1\u56ed\u91cc\u5c31\u6709\u4e00\u682a\uff01\u8fd9\u662f\u6700\u540d\u8d35\u7261\u4e39\u54c1\u79cd\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\uff1a\u201c\u6211\u5bf9\u7261\u4e39\u54c1\u79cd\u77e5\u9053\u5f88\u5c11\uff0c\u54b1\u4eec\u4eac\u5e08\u91cc\u662f\u6ca1\u6709\u9752\u9f99\u5367\u58a8\u6c60\u7684\u5427\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u8fd9\u662f\u5c71\u4e1c\u83cf\u6cfd\u7684\u54c1\u79cd\uff0c\u636e\u6211\u6240\u77e5\u3002\u5fa1\u82b1\u56ed\u7684\u82b1\u5320\u4e5f\u79cd\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u559d\u95ee\u5973\u4eba\uff1a\u201c\u592b\u4eba\u4e70\u4e86\u82b1\u6ca1\u6709\uff0c\u62ff\u51fa\u6765\u7ed9\u6211\u770b\uff01\u201c\u91d1\u592b\u4eba\u5728\u4e00\u65c1\u51b7\u8a00\u51b7\u8bed\uff1a\u201c\u8981 \u662f\u771f\u7684\u9752\u9f99\u5367\u58a8\u6c60\uff0c\u6211\u5230\u60f3\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d \u3000\u3000\u90a3\u5973\u4e2b\u5934\u521a\u624d\u5728\u4e3b\u4eba\u56de\u6765\u7684\u65f6\u5019\uff0c\u662f\u628a\u82b1\u7bee\u653e\u5728\u5979\u7684\u623f\u95f4\u7684\u3002 \u3000\u3000\u6b64\u65f6\u5979\u5fc3\u614c\u610f\u4e71\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u5c31\u8dd1\u56de\u623f\u95f4\u53bb\u628a\u6574\u4e2a\u82b1\u7bee\u62ff\u51fa\u6765\u3002 \u3000\u3000\u91d1\u592b\u4eba\u7adf\u7136\u4e0d\u987e\u8eab\u4efd\uff0c\u8ddf\u7740\u90a3\u5973\u4eba\u4e00\u540c\u8fdb\u51fa\u3002 \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u4f60\u731c\u6d4b\u90a3\u7bee\u82b1\u653e\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u4f60\u60f3\u4e0d\u5230\u5427\uff0c\u662f\u653e\u5728\u5979\u7684\u5e8a\u4e0a \u7684\uff0c\u800c\u4e14\u8fd8\u662f\u7528\u68c9\u88ab\u76d6\u4f4f\u7684\u5462\u3002\u5b8c\u989c\u5927\u4eba\uff0c\u6211\u5bf9\u5404\u79cd\u7261\u4e39\u5e94\u8be5\u5982\u4f55\u4fdd\u517b\u662f\u5b8c\u5168\u4e0d\u61c2\u7684\uff0c\u8fd9\u5230 \u8981\u8bf7\u6559\u4f60\u4e86\uff0c\u9752\u9f99\u5367\u58a8\u6c60\u662f\u5fc5\u987b\u906e\u76d6\u5f97\u5bc6\u4e0d\u900f\u98ce\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u7ed9\u5979\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u53ea\u80fd\u88c5\u8154\u4f5c\u52bf\u4f5c\u542c\u4e0d\u61c2\u5979\u8bdd\u4e2d\u542b\u4e49\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1b\u201c\u8fd9 \u4e0d\u662f\u9752\u9f99\u5367\u58a8\u6c60\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u679c\u7136\u662f\u90a3\u5c0f\u53ae\u80e1\u8a00\u4e71\u8bed\u7684\u3002\u4f46\u4ed6\u80fd\u591f\u77e5\u9053\u6709\u9752\u9f99\u5367\u58a8\u6c60\u8fd9\u79cd\u73cd\u54c1\u7261 \u4e39\uff0c\u4e5f\u662f\u5341\u5206\u96be\u5f97\u4e86\u3002\u5947\u602a\uff0c\u8fd9\u79cd\u7261\u4e39\u5728\u5fa1\u82b1\u56ed\u90fd\u6ca1\u6709\u7684\uff0c\u4ed6\u5374\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u7684\u5462\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u5fc3\u4e2d\u4e00\u52a8\uff0c\u559d\u95ee\u5973\u4e2b\uff1a\u201c\u9001\u82b1\u6765\u7684\u90a3\u5c0f\u53ae\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5feb\u5feb\u4ece\u5b9e\u62db\u6765\uff1f\u201d \u3000\u3000\u90a3\u5973\u4e2b\u9053\uff1a\u201c\u8001\u7237\uff0c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u3002\u90a3\u5c0f\u53ae\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u5c0f\u5973\u662f\u770b\u7740\u90a3\u5c0f\u53ae\u8e0f\u5165\u8d35\u5e9c\u7684\uff0c\u6211\u4eec\u8ddf\u968f\u7740\u5c31\u6765\u4e86\uff0c\u4f46\u4e00\u8def\u4e0a\u5374\u6ca1\u78b0\u89c1\u90a3 \u5c0f\u53ae\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u542c\u5f97\u9762\u8272\u94c1\u9752\uff0c\u7a81\u7136\u4e00\u638c\u6253\u7ffb\u90a3\u4e2a\u5973\u4e2b\uff0c\u7acb\u5373\u56de\u5230\u59bb\u5b50\u7684\u5367\u623f\u3002 \u3000\u3000\u201c\u90a3\u82b1\u5e97\u7684\u5c0f\u53ae\u5462\uff1f\u4f60\u628a\u4ed6\u85cf\u5728\u54ea\u91cc\uff1f\u2018\u5b8c\u989c\u9274\u77aa\u7740\u773c\u775b\uff0c\u6c89\u58f0\u95ee\u4ed6\u59bb\u5b50\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6c14\u5f97\u58f0\u97f3\u53d1\u98a4\uff1a\u201c\u4f60\u80e1\u8bf4\u4ec0\u4e48\uff1f\u7ed9\u6211\u51fa\u53bb\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u628a\u90a3\u5c0f\u53ae\u4ea4\u51fa\u6765\uff0c\u662f\u4e0d\u662f\u628a\u90a3\u5c0f\u53ae\u770b\u5f97\u6bd4\u4e08\u592b\u8fd8\u8981\u7d27\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u786c\u7740\u5934\u76ae\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u628a\u4e00\u4e2a\u5c0f\u53ae\u85cf\u8d77\u6765\u4f5c\u4ec0\u4e48\uff1f\u4f60\u4e3a\u4ec0\u4e48\u8bec\u8d56\u6211\u7792\u7740\u4f60\u5077 \u6c49\u5b50\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u6ca1\u6000\u7591\u4f60\u5077\u6c49\u5b50\uff0c\u4f46\u6211\u53ef\u6000\u7591\u90a3\u5c0f\u53ae\u5e76\u4e0d\u662f\u6765\u9001\u82b1\u7684\uff01\u201c\u5b8c\u989c\u592b\u4eba\u9053\uff1a \u201c\u4f60\u6000\u7591\u4ed6\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u6211\u6000\u7591\u4ed6\u662f\u66ff\u4ec0\u4e48\u4eba\u9001\u4e1c\u897f\u7ed9\u4f60\u7684\u3002\u6211\u529d\u4f60\u8fd8\u662f\u81ea\u5df1\u8bf4\u51fa\u6765\u7684\u597d\uff0c\u4f60\u4e0d\u8bf4 \u51fa\u6765\uff0c\u53ef\u4f5c\u602a\u4e3a\u592b\u7684\u4e0d\u5ba2\u6c14\u4e86\uff0c\u6211\u81ea\u5df1\u4f1a\u641c\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u8981\u641c\u4e5f\u4e0d\u96be\uff0c\u5199\u5f20\u4f11\u4e66\u7ed9\u6211\uff0c\u6211\u4efb\u51ed\u4f60\u641c\uff01\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1b\u201c\u592b\u4eba\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u5373\u662f\u5982\u6b64\u4e4b\u4e0d\u4fe1\u4efb\u3002\u505a\u592b\u59bb\u4e0b\u53bb\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u641c\u4e5f\u884c\uff0c\u4f60\u628a\u90a3\u652f\u7389\u8427\u7ed9\u6211\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u7ed9\u4f60\u7389\u8427\u3002\u201d\u6a80\u7fbd\u51b2\u81ea\u8863\u6a71\u8dc3\u51fa\uff0c\u4e00\u628a\u6293\u7740\u4e86\u5b8c\u989c\u9274\uff0c\u60f3\u8d77\u6bcd\u4eb2\u7684\u60e8\u6b7b\uff0c\u6ee1\u8154\u60b2 \u6124\uff0c\u4e3e\u8d77\u6696\u7389\u8427\uff0c\u5c31\u8981\u53d6\u4ed6\u6027\u547d\u3002 \u3000\u3000\u6696\u7389\u8427\u575a\u903e\u91d1\u94c1\uff0c\u773c\u770b\u5c31\u8981\u628a\u5b8c\u989c\u9274\u7684\u5929\u7075\u76d6\u6253\u5f97\u7c89\u788e\uff0c\u5b8c\u989c\u592b\u4eba\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4f4f \u624b\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u628a\u738b\u8427\u505c\u5728\u5b8c\u989c\u9274\u7684\u5934\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u90a3\u6837\u72e0\u5fc3\u5bf9\u4f60\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u81ea\u5df1\u547d\u82e6\uff0c\u6211\u65e9\u5df2\u8ba4\u547d\u4e86\uff0c\u4ed6\u5bf9\u6211\u600e\u6837\u4e0d\u597d\uff0c\u603b\u8fd8\u662f\u6211\u7684 \u4e08\u592b\u3002\u6211\u4e0d\u80fd\u8ba9\u4ed6\u6740\u4f60\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u6740\u4ed6\uff0c\u8bf7\u4f60\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u9976\u4ed6\u4e00\u547d\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u628a\u7389\u8427\u4ece\u5b8c\u989c\u9274\u7684\u5934\u9876\u79fb\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u5bf9\u6211\u4eec\u6bcd\u5b50\u601d\u91cd\u5982\u5c71\uff0c\u6211\u65e0\u4ee5\u4e3a \u62a5\uff0c\u8fd9\u5c31\u7b97\u662f\u62a5\u7b54\u4f60\u7684\u6069\u60c5\u5427\uff01\u4f46\u6211\u53ef\u5f97\u6709\u8a00\u5728\u5148\uff0c\u6211\u53ea\u80fd\u9976\u4ed6\u4e00\u6b21\uff01\u201d\u8bf4\u7f62\uff0c\u632f\u81c2\u4e00\u6325\uff0c \u559d\u9053\uff1a\u201c\u5b8c\u989c\u9274\uff0c\u4f60\u597d\u81ea\u4e3a\u4e4b\uff0c\u5426\u5219\uff0c\u6211\u4e0d\u6740\u4f60\uff0c\u4e5f\u4f1a\u6709\u4eba\u6740\u4f60\uff01\u201d\u4e00\u4e2a\u4f7f\u52b2\uff0c\u5c06\u5b8c\u989c\u9274\u629b \u51fa\u3002 \u3000\u3000\u78b0\u7684\u4e00\u58f0\uff0c\u623f\u95e8\u7ed9\u4eba\u649e\u5f00\uff0c\u5b88\u5728\u95e8\u5916\u7684\u5e05\u8001\u5927\u8d76\u5fd9\u5c06\u5b8c\u989c\u9274\u63a5\u4e0b\u3002 \u3000\u3000\u5b8c\u989c\u9274\u53cc\u773c\u706b\u7ea2\uff0c\u559d\u9053\uff1a\u201c\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u5c0f\u5b50\u8dd1\u6389\uff01\u201d \u3000\u3000\u5e05\u8001\u5927\u89c1\u5b8c\u989c\u9274\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6709\u70b9\u5403\u60ca\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u8c01\uff1f\u201c\u5b8c \u989c\u9274\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u5170\u59d1\u7684\u513f\u5b50\u3002\u5170\u59d1\u7684\u513f\u5b50\u662f\u4ec0\u4e48\u4eba\uff0c\u60f3\u5fc5\u4f60\u4e5f\u77e5\u9053\u4e86\u5427\uff1f\u201d \u3000\u3000\u5e05\u8001\u5927\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u62ff\u4ed6\uff01\u201d\u53e3\u91cc\u8fd9\u4e48\u8bf4\uff0c\u53ef\u8fd8\u4e0d\u6562\u4fbf\u5373\u51b2\u8fdb\u592b\u4eba \u7684\u623f\u95f4\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4f60\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e05\u8001\u5927\u9053\uff1a\u201c\u53ea\u6015\u592b\u4eba\u2014\u2014\u201d\u987f\u4e86\u4e00\u987f\uff0c\u559d\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u7ed9\u6211\u6eda\u51fa\u6765\u3002\u4f60\u4ee5\u4e3a\u9760\u592b\u4eba \u7684\u5e87\u62a4\u3002\u4f60\u5c31\u53ef\u4ee5\u6c38\u8fdc\u505a\u7f29\u5934\u4e4c\u9f9f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u54ac\u7259\u559d\u9053\uff1a\u201c\u4e0d\u5fc5\u7406\u4f1a\u592b\u4eba\uff0c\u6d3b\u7684\u62ff\u4e0d\u5230\uff0c\u6b7b\u7684\u4e5f\u8981\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u98a4\u6296\u7684\u58f0\u97f3\u4ece\u623f\u95f4\u91cc\u4f20\u51fa\u6765\uff1a\u201c\u5b8c\u989c\u9274\u4f60\u600e\u4e48\u53ef\u4ee5\u8fd9\u6837\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u7b54\u5e94\u4f60\u6211\u4e0d\u4f1a\u4eb2\u624b\u6740\u4ed6\uff0c\u4f46\u65c1\u4eba\u6740\u4ed6\uff0c\u6211\u53ef\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u8fd9\u56de\u662f\u771f\u7684\u6c14\u6655\u8fc7\u53bb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4ed6\u4e00\u9762\u5439\u8427\uff0c\u4e00\u9762\u7f13\u7f13\u8d70\u51fa\u623f\u95f4\u3002 \u3000\u3000\u5e05\u8001\u5927\u77e5\u9053\u4ed6\u662f\u8036\u5f8b\u7384\u5143\u7684\u5f1f\u5b50\uff0c\u5bf9\u4ed6\u672c\u662f\u6709\u51e0\u5206\u60e7\u6015\u7684\uff0c\u6b64\u65f6\u89c1\u4ed6\u5439\u8427\u540c\u6765\uff0c\u4e0d\u7981\u53c8 \u6c14\u53c8\u607c\uff0c\u53c8\u662f\u6b22\u559c\u4e86\u3002 \u3000\u3000\u8981\u77e5\u9ad8\u624b\u6bd4\u62fc\uff0c\u6700\u5fcc\u8f7b\u654c\uff0c\u6545\u6b64\u5e05\u8001\u5927\u867d\u7136\u607c\u6012\u4ed6\u7684\u65e0\u793c\uff0c\u4f46\u4ed6\u7684\u8f7b\u654c\u5374\u7ed9\u5e05\u8001\u5927\u4e00\u4e2a \u6700\u597d\u7684\u53d1\u52a8\u653b\u51fb\u7684\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u201c\u597d\u4e2a\u72c2\u5c0f\u5b50\uff0c\u80c6\u6562\u5728\u6211\u9762\u524d\uff0c\u5982\u6b64\u50b2\u6162\uff0c\u8fd9\u662f\u4f60\u81ea\u5df1\u627e\u6b7b\uff01\u201d\u5e05\u8001\u5927\u53e3\u4e2d\u559d\u9a82\uff0c\u53cc\u638c \u5df2\u662f\u671d\u7740\u6a80\u7fbd\u51b2\u5288\u6253\uff01 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u4e00\u62db\u5373\u4f7f\u4f24\u4e0d\u4e86\u6a80\u7fbd\u51b2\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u628a\u4ed6\u7684\u7389\u8427\u593a\u8fc7\u6765\uff0c\u4ed6\u662f\u65bd\u5c55\u7a7a\u624b\u5165\u767d\u5203 \u7684\u624b\u6cd5\u8f85\u4ee5\u96c4\u6d51\u65e0\u6bd4\u7684\u5c0f\u5929\u661f\u638c\u529b\u7684\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u7684\u624b\u6307\u8fd8\u672a\u78b0\u7740\u7389\u8427\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u5370\u638c\u5fc3\u707c\u70ed\uff0c\u6a80\u7fbd\u51b2\u5df1\u662f\u4ece\u7389\u8427\u4e2d\u5439\u51fa\u4e00\u80a1 \u6c14\u3002 \u3000\u3000\u53ef\u60dc\u6a80\u7fbd\u51b2\u7684\u5185\u529f\u8fd8\u672a\u7ec3\u5230\u5e08\u7236\u90a3\u822c\u5883\u754c\uff0c\u5426\u5219\u8fd9\u4e00\u80a1\u6c14\u5c31\u53ef\u4ee5\u5c01\u95ed\u5e05\u8001\u5927\u638c\u5fc3\u7684\u201c\u52b3 \u5bab\u7a74\u201c\u4f4d\u4e8e\u624b\u5c11\u9633\u7ecf\u8109\u7684\u7ec8\u70b9\uff0c\u4e00\u88ab\u5c01\u95ed\uff0c\u591a\u5f3a\u7684\u5185\u529b\u4e5f\u4f7f\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5e05\u8001\u5927\u89c9\u638c\u5fc3\u4e00\u9635\u9178\u9ebb\uff0c\u53f3\u81c2\u5df2\u662f\u8f6f\u7ef5\u7ef5\u7684\u4f7f\u4e0d\u51fa\u529b\u9053\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u4e14\u770b\u662f\u8c01\u627e\u6b7b\uff01\u51b7\u7b11\u58f0\u4e2d\uff0c\u7389\u8427\u79bb\u624b\uff0c\u75be\u70b9\u5e05\u8001\u5927\u4e09\u5904\u5927\u7a74\u3002 \u3000\u3000\u5e05\u8001\u5927\u5de6\u81c2\u8fd8\u80fd\u4f7f\u7528\uff0c\u4e00\u4e2a\u201c\u56de\u907f\u626b\u67f3\u201c\uff0c\u638c\u98ce\u628a\u7389\u8427\u7684\u843d\u70b9\u8361\u6b6a\u3002\u4f59\u529b\u672a\u8870\uff0c\u628a\u9662\u5b50 \u91cc\u4e00\u68f5\u6811\u9707\u5f97\u6280\u6447\u53f6\u843d\u3002 \u3000\u3000\u773c\u770b\u5e05\u8001\u5927\u5c31\u8981\u4f24\u5728\u4ed6\u7684\u7389\u8427\u4e4b\u4e0b\uff0c\u4e00\u65c1\u89c2\u6218\u7684\u8001\u4e8c\u5df2\u662f\u4e0d\u7981\u5931\u58f0\u60ca\u547c\uff01 \u3000\u3000\u201c\u5c0f\u8d3c\u4f11\u5f97\u901e\u5f3a\uff01\uff01\u4e00\u4e2a\u5288\u7a7a\u638c\u5c31\u628a\u6a80\u7fbd\u51b2\u7684\u7389\u8427\u8361\u5f00\u4e86\u3002\u4ed6\u7684\u638c\u529b\u4f7f\u5f97\u6070\u5230\u597d\u5904\uff0c\u53ea \u662f\u8361\u5f00\u7389\u8427\uff0c\u5bf9\u5e05\u8001\u5927\u5374\u6ca1\u4e1d\u5f71\u54cd\u3002\u4ed6\u4eec\u4e24\u4eba\u5982\u540c\u4e00\u4f53\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\u3002 \u3000\u3000\u8036\u5f8b\u7384\u5143\u5f53\u5e74\u5927\u95f9\u5546\u5dde\uff0c\u6740\u51fa\u8282\u5ea6\u5e9c\uff0c\u5c31\u56e0\u4e3a\u53d7\u963b\u4e8e\u201c\u8fde\u8001\u513f\uff0c\u5bf9\u6a80\u7fbd\u51b2\u7684\u6bcd\u4eb2\u4e0d\u80fd\u517c \u987e\uff0c\u4ee5\u81f3\u5979\u88ab\u4e71\u7bad\u6740\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u60f3\u8d77\u6b64\u4e8b\uff0c\u5f53\u771f\u662f\u4ec7\u4eba\u89c1\u9762\u4efd\u5916\u80a1\u7ea2\uff0c\u4ed6\u672c\u5df2\u662f\u90c1\u95f7\u586b\u80f8\uff0c\u6b64\u65f6\u51b3\u610f\u4e3a\u6bcd\u4eb2\u62a5 \u4ec7\u3002\u4e00\u8154\u6012\u6c14\u5c3d\u90fd\u53d1\u6cc4\u5728\u201d\u7941\u8fde\u8001\u513f\u201d\u8eab\u4e0a\uff0c\u4ed6\u7684\u7389\u8427\uff0c\u53ef\u4ee5\u5f53\u4f5c\u4e09\u79cd\u4e0d\u540c\u7684\u5175\u5668\u4f7f\u7528\uff0c\u53ef \u4ee5\u70b9\u7a74\uff0c\u53ef\u4ee5\u4f7f\u51fa\u5251\u6cd5\uff0c\u8fd8\u53ef\u4ee5\u5f53\u4f5c\u68cd\u68d2\u4f7f\u7528\u3002\u7389\u8427\u970d\u970d\u5c55\u5f00\uff0c\u78a7\u5f71\u5343\u91cd\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357 \u6253\u5317\uff0c\u9976\u662f\u7941\u8fde\u8001\u513f\u8054\u624b\uff0c\u4e5f\u7ed9\u4ed6\u6740\u5f97\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002\u6b64\u8098\u91d1\u8d85\u5cb3\u5df2\u662f\u5230\u573a\u89c2\u6218\uff0c\u4ed6\u7684\u6b66 \u529f\u662f\u8fdc\u80dc\u4e8e\u5b8c\u989c\u9274\u7684\u3002\u4f46\u4e0d\u53ea\u5b8c\u989c\u9274\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u8fde\u4ed6\u770b\u4e86\u4e5f\u662f\u5403\u60ca\u4e0d\u5df2\u3002 \u3000\u3000\u201c\u8fd9\u82b1\u5e97\u91cc\u7684\u5c0f\u53ae\u600e\u5f97\u5982\u6b64\u4e86\u5f97\uff0c\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d\u91d1\u8d85\u5cb3\u5077\u5077\u7684\u95ee\u5b8c\u989c\u9274\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4ed6\u54ea\u91cc\u662f\u4ec0\u4e48\u5c0f\u53ae\uff1f\u55ef\uff01\u8bf4\u8d77\u6765\u4ed6\u8fd8\u662f\u5c0f\u8d1d\u52d2\u8eab\u4efd\u5462\uff1f\u201c\u91d1\u8d85\u5cb3\u5403\u4e86\u4e00\u60ca \u9053\uff1a\u201c\u5c0f\u8d1d\u52d2\uff1f\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5c31\u662f\u6211\u5bb6\u738b\u7237\u6240\u8981\u6349\u62ff\u7684\u90a3\u4e2a\u6a80\u7fbd\u51b2\u201d\u4ed6\u7956\u7236\u662f\u5f53\u5e74\u505a\u8fc7\u5175\u9a6c\u5927 \u5143\u5e05\u7684\u6d4e\u738b\u6a80\u516c\u76f4\uff0c\u4ed6\u4e0d\u662f\u5c0f\u8d1d\u52d2\u7684\u8eab\u4efd\u5417\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4ed6\u662f\u6a80\u8001\u8d1d\u52d2\u7684\u5b59\u513f\uff0c\u8036\u5f8b\u7384\u5143\u7684\u5f1f\u5b50\uff0c\u602a\u4e0d\u5f97\u8fd9\u4e48\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u4f60\u662f\u5927\u884c\u5bb6\uff0c\u4f60\u770b\u8001\u4e8c\u53ef\u5bf9\u4ed8\u5f97\u4e86\u8fd9\u5c0f\u5b50\u5417\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u96be\u8bf4\u5f97\u5f88\uff0e\u96be\u8bf4\u5f97\u5f88\u3002\u5514\uff0c\u5f85\u6211\u518d\u770b\u4e00\u4f1a\u513f\uff0c\u518d\u770b\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u63ed\u7834\u6a80\u7fbd\u51b2\u7684\u8eab\u4efd\uff0c\u672c\u662f\u60f3\u8981\u91d1\u8d85\u5cb3\u4e0a\u53bb\u5e2e\u5fd9\u7941\u8fde\u4e8c\u8001\u5c06\u6a80\u7fbd\u51b2\u62ff\u4e0b\u6765\u7684\uff0c\u4e0d\u6599 \u91d1\u8d85\u5cb3\u652f\u543e\u4ee5\u5bf9\u3002\u597d\u50cf\u4e0d\u61c2\u4ed6\u7684\u610f\u601d\uff0c\u53ea\u662f\u5728\u65c1\u89c2\u6218\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u91d1\u8d85\u5cb3\u4e5f\u6709\u91d1\u8d85\u5cb3\u7684\u7b97\u76d8\uff0c\u4e00\u662f\u4ed6\u4e0d\u613f\u81ea\u8d2c\u8eab\u4efd\uff0c\u5408\u201c\u7941\u8fde\u4e8c\u8001\u201d\u4e4b\u529b\u6765\u5bf9\u4ed8 \u4e00\u4e2a\u540e\u751f\u5c0f\u5b50\uff1b\u4e8c\u662f\u4ed6\u662f\u60f3\u770b\u6a80\u7fbd\u51b2\u5f97\u81ea\u8036\u5f8b\u7384\u5143\u6240\u4f20\u7684\u6b66\u529f\u7a76\u7adf\u6709\u591a\u795e\u5999\uff1b\u4e09\u6765\u4ed6\u662f\u6709\u5fc3 \u5750\u5fc3\u6e14\u4eba\u4e4b\u5229\uff0c\u6700\u597d\u662f\u5728\u6a80\u7fbd\u51b2\u4e0e\u7941\u8fde\u4e8c\u8001\u6597\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u4ed6\u65b9\u59cb\u51fa\u6765\u6536\u62fe\u6b8b\u5c40\uff0c\u8fd9\u6837\u5c82\u975e \u53ef\u4ee5\u72ec\u5360\u529f\u52b3\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u8bf4\u7684\u201c\u96be\u8bf4\u5f97\u5f88\u201d\u5374\u4e5f\u5e76\u975e\u6577\u884d\u4e4b\u8f9e\uff0c\u6a80\u7fbd\u51b2\u4e0e\u7941\u8fde\u4e8c\u8001\u7684\u8fd9\u573a\u5927 \u6218\uff0c\u7684\u786e\u662f\u65d7\u9f13\u76f8\u5f53\uff0c\u80dc\u8d1f\u6b8a\u96be\u9884\u6599\u7684\u3002\u6a80\u7fbd\u51b2\u5f3a\u653b\u731b\u6253\uff0c\u5360\u4e86\u516b\u6210\u653b\u52bf\uff0c\u4f46\u7941\u8fde\u4e8c\u8001\u5b88\u5f97 \u6781\u7a33\uff0c\u8fc7\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u4ed6\u8fd8\u662f\u653b\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u8d8a\u6597\u8d8a\u7d27\uff0c\u53ea\u89c1\u5343\u91cd\u78a7\u5f71\uff0c\u88f9\u4f4f\u7941\u8fde\u4e8c\u8001\u7684\u8eab\u5f62\u3002\u7941\u8fde\u4e8c\u8001\u6c89\u7a33\u51fa\u638c\uff0c\u9690\u9690\u631f\u7740\u98ce \u96f7\u4e4b\u58f0\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9661\u7136\u95f4\u5ffd\u89c1\u78a7\u5f71\u88ab\u51b2\u5f00\u4e00\u89d2\uff0c\u7941\u8fde\u4e8c\u8001\u9f50\u58f0\u559d\u9053\uff1b\u201c\u5177\u5c0f\u5b50\uff0c\u53eb\u4f60\u77e5\u9053 \u6211\u4eec\u7684\u5389\u5bb3\uff01\u201d\u5927\u559d\u58f0\u4e2d\uff0c\u4ed6\u4eec\u5df2\u662f\u8f6c\u5b88\u4e3a\u653b\uff01 \u3000\u3000\u91d1\u8d85\u5cb3\u6697\u6697\u540e\u6094\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u521a\u624d\u6211\u5c06\u4ed6\u4eec\u66ff\u4e0b\uff0c\u8fd8\u53ef\u4ee5\u505a\u4e2a\u4eba\u60c5\u3002\u201d \u3000\u3000\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6a80\u7fbd\u51b2\u5934\u88ab\u5e05\u8001\u5927\u6253\u4e86\u4e00\u638c\uff0c\u5267\u75db\u4e4b\u4e0b\uff0c\u53cd\u800c\u6e05\u9192\u8fc7\u6765\u3002\u60f3\u8d77\u4e86\u6bcd\u4eb2\u751f \u524d\u6559\u4ed6\u7684\u4e00\u4e2a\u201c\u5fcd\u201d\u5b57\uff0c\u5ffd\u7136\u609f\u5230\u8fd9\u4e2a\u201c\u5fcd\u201d\u5b57\uff0c\u4e0d\u4ec5\u53ef\u4ee5\u7528\u5728\u505a\u4eba\u7684\u9053\u7406\u4e0a\uff0c\u4e5f\u53ef\u4ee5\u7528\u5728 \u6b66\u5b66\u4e0a\u3002\u201c\u6211\u521a\u624d\u90a3\u6837\u5f3a\u653b\u731b\u6253\uff0c\u7684\u786e\u662f\u6c89\u4e0d\u4f4f\u6c14\u3002\u5403\u4e8f\u8fd9\u662f\u6d3b\u8be5\uff01\u201d \u3000\u3000\u4ed6\u4e00\u7701\u543e\u8fd9\u4e2a\u9053\u7406\uff0c\u7acb\u5373\u628a\u6025\u8e81\u7684\u5fc3\u60c5\u6291\u5236\u4e0b\u53bb\u3002\u8427\u6cd5\u4e00\u53d8\uff0c\u968f\u610f\u4e4b\u6240\uff0c\u6709\u5982\u6d41\u6c34\u884c \u4e91\uff0c\u95f2\u5ead\u4fe1\u6b65\u3002\u5fc3\u4e2d\u4e00\u7247\u7a7a\u660e\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8fbe\u5230\u4e86\u76ee\u4e2d\u6709\u654c\uff0c\u5fc3\u4e2d\u65e0\u654c\u7684\u5883\u754c\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u53ea\u6015\u4ed6\u4eec\u662f\u6709\u70b9\u4e0d\u5999\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u89c1\u7941\u8fde\u4e8c\u8001\u8fd8\u5360\u4e86\u4e00\u534a\u4ee5\u4e0a\u7684\u653b\u52bf\uff0c\u5fc3\u91cc\u6709\u70b9\u534a\u4fe1\u534a\u7591\u3002\u5ffd\u5730\u542c\u5f97\u6a80\u7fbd\u51b2\u6717\u58f0\u541f \u9053\uff1a\u201c\u671d\u8f9e\u767d\u5e1d\u5f69\u4e91\u95f4\uff0c\u5343\u91cc\u6c5f\u9675\u4e00\u65e5\u8fd8\u3002\u201d\u7389\u8427\u51fa\u62db\u914d\u5408\u8bd7\u610f\uff0c\u82e5\u5373\u82e5\u79bb\uff0c\u4e00\u6cbe\u5373\u9000\uff0c\u5feb \u5f97\u8fde\u5b8c\u989c\u9274\u90fd\u770b\u4e0d\u6e05\u695a\u3002\u201c\u4e24\u5cb8\u733f\u58f0\u557c\u4e0d\u4f4f\uff0c\u8f7b\u821f\u5df2\u8fc7\u4e07\u91cd\u5c71\uff01\u201d\u8427\u6cd5\u8d8a\u53d1\u8f7b\u7075\uff0c\u8d8a\u53d1\u8fc5 \u6377\uff01\u5b8c\u989c\u9274\u521a\u542c\u89c1\u4ed6\u5ff5\u51fa\u201c\u8f7b\u821f\u201d\u4e8c\u5b57\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u7941\u8fde\u4e8c\u8001\u4e0d\u7ea6\u800c\u540c\u7684\u5012\u7eb5\u51fa\u53bb\uff0c\u201c\u556a \u54d2\u201d\u4e00\u58f0\u54cd\uff0c\u540c\u65f6\u8dcc\u5012\u5730\u4e0a\u3002\u5bf9\u6a80\u7fbd\u51b2\u6765\u8bf4\uff0c\u4ed6\u7684\u786e\u662f\u201c\u8f7b\u821f\u5df2\u8fc7\u4e07\u91cd\u5c71\u201d\u4e86\uff01\u91d1\u8d85\u5cb3\u76ae\u7b11 \u8089\u4e0d\u7b11\u7684\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u6696\u7389\u8427\u679c\u7136\u662f\u4ef6\u5b9d\u8d1d\uff0c\u62ff\u6765\u8ba9\u6211\u77a7\u77a7\u3001\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6709\u672c\u9886\u7684\u81ea\u5df1\u6765\u62ff\uff01\u201c\u628a\u7389\u7bab\u5bf9\u51c6\u4ed6\u7684\u638c\u5fc3\u4e00\u53e3\u7f61\u6c14\u5439\u51fa\u53bb\u3002\u638c\u5fc3\u7684\u6b63\u4e2d \u662f\u52b3\u5bab\u7a74\uff0c\u5e05\u8001\u5927\u521a\u624d\u5c31\u662f\u56e0\u4e3a\u52b3\u5bab\u7a74\u88ab\u7f61\u6c14\u6240\u4f24\uff0c\u4ee5\u81f3\u5403\u4e86\u5927\u4e8f\u7684\u3002\u6709\u9053\u524d\u8f66\u4e4b\u9274\uff0c\u6a80\u7fbd \u51b2\u53ea\u9053\uff1a\u201c\u7eb5\u7136\u4f24\u4e0d\u4e86\u4ed6\uff0c\u4ed6\u4e5f\u975e\u5f97\u7f29\u638c\u4e0d\u53ef\u3002\u4e3b\u5ba2\u4e4b\u52bf\u4e00\u6613\uff0c\u6a80\u7fbd\u51b2\u9a6c\u4e0a\u5c31\u53ef\u593a\u5f97\u5148\u624b\u3002 \u90a3\u77e5\u91d1\u8d85\u5cb3\u7adf\u4e0d\u95ea\u907f\uff0c\u53cd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53eb\u6211\u62ff\uff0c\u90a3\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u4e00\u638c\u62cd\u51fa\uff0c\u8fc5 \u5373\u5c31\u5411\u8427\u6293\u6765\u3002 \u3000\u3000\u7f61\u6c14\u4e0e\u638c\u98ce\u4e92\u76f8\u6fc0\u8361\uff0c\u6a80\u7fbd\u51b2\u53ea\u89c9\u5947\u5bd2\u5f7b\u9aa8\uff0c\u7981\u4e0d\u4f4f\u673a\u4f36\u4f36\u7684\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u4e5f\u4e0d\u597d\u53d7\uff0c\u53ea\u89c9\u638c\u5fc3\u597d\u4f3c\u88ab\u9999\u706b\u707c\u4e86\u4e00\u4e0b\uff0c\u867d\u7136\u52b3\u5bab\u7a74\u4e0d\u81f3\u4e8e\u7ed9\u4ed6\u7684\u7f61\u6c14\u5c01\u95ed\uff0c \u8eab\u5f62\u4e5f\u662f\u4e0d\u7981\u6643\u4e86\u4e00\u6643\u3002\u91d1\u8d85\u5cb3\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u597d\u5728\u8fd9\u5c0f\u5b50\u7684\u5185\u529f\u8fd8\u672a\u7ec3\u5230\u4ed6\u5e08\u7236\u90a3\u822c\u5883\u754c\uff0c \u5426\u5219\u4ed6\u8f85\u4ee5\u8fd9\u652f\u66a7\u7389\u8427\uff0c\u6211\u662f\u6050\u6015\u975e\u8d25\u4e0d\u53ef\u7684\u4e86\u3002\u201d \u3000\u3000\u4ed6\u89c1\u8fd9\u652f\u6696\u7389\u8427\u5982\u6b64\u795e\u5947\uff0c\u800c\u4e14\u8fd8\u521a\u597d\u53ef\u4ee5\u514b\u5236\u4ed6\u6240\u7ec3\u7684\u4e00\u95e8\u529f\u592b\uff0c\u8d8a\u53d1\u60f3\u8981\u628a\u5b83\u593a\u5230 \u624b\u4e86\u3002\u4ed6\u4e00\u6643\u5373\u4e0a\uff0c\u5de6\u624b\u53c8\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u8bf4\u4e86\u5947\u602a\uff0c\u4ed6\u521a\u7528\u53f3\u638c\u6253\u6765\u7684\u65f6\u5019\uff0c\u638c\u98ce\u597d\u50cf\u4ece\u51b0\u7a9f\u5439\u6765\uff0c\u5947\u5bd2\u5f7b\u9aa8\uff0c\u5982\u4eca\u7528\u5de6\u638c\u6253 \u6765\uff0c\u638c\u98ce\u5374\u50cf\u4ece\u9f13\u98ce\u7089\u4e2d\u5439\u51fa\uff0c\u70ed\u547c\u547c\u7684\u89e6\u4f53\u5982\u70eb\u3002 \u3000\u3000\u5bd2\u70ed\u5939\u653b\u4e4b\u4e0b\uff0c\u6a80\u7fbd\u51b2\u4e5f\u96be\u7981\u53d7\uff0c\u8eab\u4f3c\u9640\u87ba\u4e00\u8f6c\uff0c\u63a5\u8fde\u6253\u4e86\u4e24\u4e2a\u5708\u5708\uff0c\u51e0\u4e4e\u7ad9\u4e0d\u4f4f\u811a\u3002 \u3000\u3000\u539f\u6765\u91d1\u8d85\u5cb3\u8fd9\u4e00\u51b7\u4e00\u70ed\u7684\u5947\u529f\u3002\u540d\u4e3a\u201c\u9634\u9633\u4e94\u884c\u638c\u201d\uff0c\u4e43\u662f\u5c06\u4e24\u95e8\u6700\u5389\u5bb3\u7684\u90aa\u6d3e\u529f\u592b\uff0c \u5408\u800c\u4e3a\u4e00\uff0c\u82e6\u7ec3\u4e86\u4e09\u5341\u5e74\uff0c\u8fd9\u624d\u7ec3\u6210\u529f\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u5730\u54fc\u7740\u66f2\u8c03\uff0c\u91d1\u8d85\u5cb3\u4e0d\u77e5\u4ed6\u54fc\u7684\u662f\u4ec0\u4e48\uff0c\u53ea\u89c9\u5f97\u4e00\u7247\u67d4\u548c\uff0c\u4ee4\u4eba\u6709\u5982\u4e91\u6de1\u98ce\u8f7b \u7684\u611f\u89c9\u3002\u4ed6\u7684\u7389\u8427\u4e5f\u6e10\u6e10\u7f13\u6162\u4e0b\u6765\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u5212\uff0c\u597d\u50cf\u6f2b\u4e0d\u7ecf\u610f\uff0c\u4fe1\u624b\u51fa\u62db\u3002\u4f46\u8bf4\u4e5f\u5947 \u602a\uff0c\u4ed6\u5374\u53cd\u800c\u4ece\u5bb9\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u9662\u5b50\u91cc\u6709\u4e2a\u8d2e\u6c34\u7684\u9752\u94dc\u6c34\u7f38\uff0c\u5b8c\u989c\u9274\u7a81\u7136\u62cd\u6253\u6c34\u7f38\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5411\u674e\u767d\u6c42\u52a9\uff0c\u4f46\u53ef \u60dc\u674e\u767d\u53ea\u662f\u8bd7\u4ed9\uff0c\u4e0d\u662f\u5251\u4ed9\uff0c\u4ed6\u7684\u8bd7\u662f\u6551\u4e0d\u4e86\u4f60\u7684\uff01\u201d \u3000\u3000\u539f\u6765\u6a80\u7fbd\u51b2\u54fc\u7684\u4e43\u662f\u674e\u767d\u7684\u4e00\u9996\u4e94\u8a00\u7edd\u53e5\uff1a\u201c\u4f17\u9e1f\u9ad8\u98de\u5c3d\uff0c\u5b64\u4e91\u72ec\u53bb\u95f2\u3002\u76f8\u770b\u4e24\u4e0d\u538c\uff0c \u53ea\u6709\u656c\u4ead\u5c71\u3002\u201d\u8bd7\u5883\u98d8\u7136\u51fa\u5c18\uff0c\u4ed6\u7684\u7389\u8427\u6309\u8282\u62cd\u51fa\u62db\uff0c\u548c\u8bd7\u5883\u9690\u9690\u548c\u5408\u3002\u5fc3\u65e0\u6742\u5ff5\uff0c\u5f97\u5931\u5df2 \u662f\u65e0\u6240\u7d0a\u6000\u3002\u5982\u6b64\u4e00\u6765\uff0c\u53cd\u800c\u8fbe\u5230\u4e86\u6b66\u5b66\u7684\u4e0a\u4e58\u5883\u754c\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u9887\u901a\u97f3\u5f8b\uff0c\u4ed6\u62cd\u6253\u6c34\u7f38\uff0c\u53d1\u51fa\u566a\u97f3\uff0c\u7528\u610f\u5c31\u662f\u60f3\u8981\u6253\u4e71\u6a80\u7fbd\u51b2\u7684\u8282\u62cd\u3002\u4e0d\u8fc7\uff0c\u4ed6 \u7684\u529f\u529b\u5c1a\u4e0d\u5982\u6a80\u7fbd\u51b2\uff0c\u867d\u7136\u609f\u51fa\u8fd9\u4e2a\u7834\u89e3\u4e4b\u6cd5\uff0c\u8fd8\u662f\u5e2e\u4e0d\u4e86\u91d1\u8d85\u5cb3\u7684\u5927\u5fd9\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u4e0d\u61c2\u8bd7\uff0c\u4f46\u5374\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\u3002\u4e00\u70b9\u5373\u900f\u3002\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u8fd9 \u5c0f\u5b50\u9003\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\u7684\u3002\u5012\u662f\u7941\u8fde\u4e8c\u8001\uff0c\u4e0d\u77e5\u7ed9\u8fd9\u5c0f\u5b50\u4f24\u5f97\u5982\u4f55\uff0c\u4f60\u8fd8\u662f\u5148\u53bb\u6551\u6cbb\u4ed6\u4eec \u5427\u3002\u201d \u3000\u3000\u4ed6\u7eb5\u58f0\u5927\u7b11\uff0c\u7b11\u58f0\u54ed\u94ff\u94ff\u9535\u9535\uff0c\u5b9b\u5982\u91d1\u5c5e\u4ea4\u51fb\uff0c\u4ee4\u4eba\u4e00\u542c\uff0c\u5c31\u89c9\u5f97\u5fc3\u91cc\u538c\u70e6\u3002\u8fd9\u662f\u4ed6\u4ee5 \u4e0a\u4e58\u5185\u529f\u53d1\u51fa\u7684\u7b11\u58f0\uff0c\u53ef\u4ee5\u5927\u6536\u6270\u4e71\u5bf9\u624b\u5fc3\u795e\u7684\u529f\u6548\u3002\u62cd\u6253\u6c34\u7f38\u7684\u58f0\u97f3\u548c\u5b83\u81ea\u662f\u4e0d\u80fd\u76f8\u63d0\u5e76 \u8bba\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5df2\u7ecf\u54fc\u4e0d\u51fa\u66f2\u8c03\uff0c\u5fc3\u4e2d\u6240\u54fc\u7684\u8282\u62cd\uff0c\u4ea6\u5df2\u7ed9\u8fd9\u5435\u8033\u7684\u7b11\u58f0\u6253\u4e71\u3002\u5916\u754c\u7684\u611f\u5e94\uff0c\u767b \u65f6\u5c31\u5728\u4ed6\u8eab\u4e0a\u53d1\u751f\u4e86\u5f71\u54cd\u3002\u91d1\u8d85\u5cb3\u5de6\u4e00\u638c\u3001\u53f3\u4e00\u638c\uff0c\u4e00\u9635\u51b7\uff0c\u4e00\u9635\u70ed\uff0c\u800c\u4e14\u662f\u51b7\u5219\u6781\u51b7\uff0c\u70ed \u5219\u6781\u70ed\u3002\u6a80\u7fbd\u51b2\u7684\u5185\u529f\u7eb5\u7136\u4e0d\u5f31\uff0c\u6e10\u6e10\u4ea6\u96be\u62b5\u53d7\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u6a80\u7fbd\u51b2\u53ea\u89c9\u4f53\u5185\u5bd2\u51b7\u96be\u7981\uff0c\u76ae\u80a4\u5374\u53c8\u662f\u5982\u53d7\u706b\u70eb\u3002\u4ed6\u7259\u5173\u6253\u6218\uff0c\u540c\u65f6\u53c8\u662f\u5927 \u6c57\u6dcb\u6f13\u3002 \u3000\u3000\u5b8c\u989c\u9274\u653e\u4e0b\u4e86\u5fc3\uff0c\u8d70\u8fc7\u53bb\u5bdf\u770b\u7941\u8fde\u4e8c\u8001\u7684\u4f24\u52bf\u3002 \u3000\u3000\u91d1\u592b\u4eba\u4ece\u5ba2\u5385\u91cc\u8d70\u51fa\u6765\uff0c\u7528\u624b\u6307\u5835\u7740\u8033\u6735\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u600e\u4e48\u7b11\u5f97\u8fd9\u6837\u96be\u542c\uff0c\u5e72\u8106\u628a\u8fd9 \u5c0f\u5b50\u6740\u4e86\u5427\uff0c\u4f55\u5fc5\u50cf\u732b\u6349\u8001\u9f20\u7684\u620f\u5f04\u4ed6\u5462\uff1f\u201d\u91d1\u592b\u4eba\u53ea\u662f\u7565\u61c2\u6b66\u529f\uff0c\u4e0d\u8fc7\u4ea6\u5df2\u770b\u5f97\u51fa\u6765\uff0c\u5979 \u7684\u4e08\u592b\u662f\u5360\u4e86\u7edd\u5bf9\u7684\u4f18\u52bf\u4e86\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u6536\u4e86\u7b11\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u548c\u4ed6\u7684\u7389\u8427\u4e00\u6837\uff0c\u90fd\u662f\u5b9d\u7269\u3002\u6740\u4ed6\u4e0d\u96be\uff0c\u4f46\u8fd8\u662f\u6d3b\u6349 \u7684\u597d\u3002\u201d\u8fd9\u8bdd\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f46\u5374\u5938\u5927\u4e86\u4e9b\uff0c\u4ed6\u662f\u6709\u6740\u6a80\u7fbd\u51b2\u4e4b\u80fd\uff0c\u4e0d\u8fc7\u4e5f\u5e76\u975e\u7acb\u65f6\u5c31\u505a\u5f97\u5230 \u4e86\uff0c\u6050\u6015\u8fd8\u5f97\u8fc7\u4e86\u4e94\u5341\u62db\u624d\u884c\u3002 \u3000\u3000\u7941\u8fde\u4e8c\u8001\u521a\u624d\u7ed9\u6a80\u7fbd\u51b2\u70b9\u7740\u7a74\u9053\uff0c\u5e78\u597d\u4e0d\u662f\u6b7b\u7a74\u3002\u5b8c\u989c\u9274\u522b\u7684\u6b66\u529f\u4e0d\u592a\u9ad8\u660e\uff0c\u70b9\u7a74\u89e3\u7a74 \u7684\u529f\u592b\u5374\u662f\u7b2c\u4e00\u6d41\u7684\uff0c\u5f88\u5feb\u5c31\u7ed9\u4ed6\u89e3\u5f00\u4e86\u7a74\u9053\u3002 \u3000\u3000\u4f46\u867d\u7136\u4e0d\u662f\u6b7b\u7a74\uff0c\u5374\u56e0\u5ef6\u8bef\u4e86\u89e3\u7a74\u7684\u65f6\u95f4\uff0c\u7941\u8fde\u4e8c\u8001\u5728\u7a74\u9053\u89e3\u5f00\u4e4b\u540e\uff0c\u8fd8\u662f\u56db\u80a2\u65e0\u529b\u3002 \u800c\u4e14\u4ed6\u4eec\u88ab\u6a80\u7fbd\u51b2\u7684\u7f61\u6c14\u635f\u53ca\u5185\u529f\uff0c\u4e00\u573a\u6fc0\u6218\u8fc7\u540e\uff0c\u5143\u6c14\u4ea6\u5df2\u5927\u4f24\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u77e5\u9053\u4ed6\u4eec\u8981\u8c03\u5300\u6c14\u606f\uff0c\u56e0\u6b64\u4e5f\u5c31\u4e0d\u548c\u4ed6\u4eec\u8bf4\u8bdd\u3002\u91d1\u8d85\u5cb3\u4e5f\u7528\u4e0d\u7740\u4ed6\u7684\u64cd\u5fc3\uff0c\u6b64\u65f6 \u4ed6\u653e\u5fc3\u4e0d\u4e0b\u7684\u5c31\u662f\u59bb\u5b50\u3002 \u3000\u3000\u5c3d\u7ba1\u4ed6\u5bf9\u59bb\u5b50\u6781\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u6700\u5c11\u4e3a\u4e86\u7ef4\u6301\u4f53\u9762\uff0c\u4ed6\u8fd8\u662f\u5e0c\u671b\u80fd\u591f\u548c\u59bb\u5b50\u8a00\u5f52\u4e8e\u597d\u7684\u3002 \u201c\u8fd9\u8bb8\u4e45\u6ca1\u542c\u89c1\u5979\u4f5c\u58f0\uff0c\u5979\u662f\u6655\u5012\u4e86\u5462\uff1f\u8fd8\u662f\u751f\u6211\u7684\u6c14\uff0c\u7d22\u6027\u4ec0\u4e48\u90fd\u4e0d\u7406\u776c\u4e86\u5462\uff1f\u4f46\u8981\u662f\u6211 \u8ffd\u589e\u5411\u5979\u966a\u7f6a\uff0c\u53ea\u6015\u8fd8\u662f\u8981\u7ed9\u5979\u8f70\u4e86\u51fa\u6765\u3002\u6211\u5802\u5802\u4e00\u4e2a\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u4e5f\u4e0d\u80fd\u5982\u6b64\u81ea\u6298\u5a01 \u98ce\uff0c\u7ed9\u5916\u4eba\u7b11\u8bdd\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u4f3c\u4e4e\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c\u8d70\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u7b11\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u91d1\u8d85\u5cb3\u5e94\u8be5\u662f\u5bf9\u4ed8\u4ecb\u4e86 \u8fd9\u5c0f\u5b50\u5427\u3002\u201d\u5b8c\u989c\u9274\u5450\u5450\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u4e00\u5b9a\u9003\u4e0d\u51fa\u5c0a\u592b\u638c\u5fc3\u7684\uff0c\u4e0d\u8fc7\u8fd9\u5c0f\u5b50\u4e43\u662f\u94a6\u72af\uff0c \u6211\u603b\u5f97\u89c1\u5230\u4ed6\u675f\u624b\u5c31\u64d2\uff0c\u65b9\u53ef\u653e\u5fc3\uff0c\u62d9\u8346\u3001\u62d9\u8346\u3001\u6211\u53ea\u80fd\u6682\u4e0d\u7406\u4f1a\u5979\u4e86\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u7b11\u9053\uff1a\u201c\u5b8c\u989c\u5927\u4eba\uff0c\u4f60\u662f\u4ee5\u516c\u4e8b\u4e3a\u91cd\uff0c\u4f69\u670d\u3001\u4f69\u670d\u3002\u6211\u66ff\u4f60\u53bb\u770b\u770b\u5979\u5427\u3002\u201d \u3000\u3000\u5b8c\u989c\u9274\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u9ebb\u70e6\u4f60\u4e5f\u66ff\u6211\u529d\u4e00\u529d\u5979\u3002\u201d\u91d1\u592b\u4eba\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u4f1a\u7684\u4e86\u3002\u201d\u8bf4 \u7f62\uff0e\u4fbf\u8d70\u8fdb\u5367\u623f\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u521a\u521a\u9192\u8f6c\uff0c\u795e\u667a\u8fd8\u6765\u600e\u4e48\u6e05\u9192\u3002\u6726\u80e7\u4e2d\u4f3c\u4e4e\u542c\u5f97\u6709\u4eba\u8fdb\u6765\uff0c\u53ea\u9053\u6765\u7684\u662f\u5973\u4ec6\uff0c \u4fbf\u5373\u95ee\u9053\uff1a\u201c\u4ed6\u3001\u4ed6\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u6328\u7740\u5979\u5750\u4e0b\uff0c\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u7741\u5f00\u773c\u775b\uff0c\u770b\u89c1\u662f\u5979\uff0c\u5c31\u597d\u50cf\u5728\u98df\u7269\u91cc\u7a81\u7136\u53d1\u73b0\u4e00\u53ea\u82cd\u8747\u4f3c\u7684\uff0c\u53ea\u60f3\u4f5c\u5455\u3002 \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u4f60\u662f\u6302\u5ff5\u4e08\u592b\u628a\uff1f\u4e0d\u7528\u62c5\u5fc3\uff0c\u4ed6\u4e00\u70b9\u4e8b\u4e5f\u6ca1\u6709\u3002\u4e0d\u8fc7\uff0c\u4ed6\u76ee\u524d\u4e0d\u8bb2\u6765\u5b89\u5c09 \u4f60\u3002\u56e0\u4e3a\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u677f\u8d77\u8138\u5b54\u9053\uff1a\u201c\u6211\u4e0d\u8981\u542c\uff0c\u8bf7\u4f60\u51fa\u53bb\u3002\u201d \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd9\u4eba\u771f\u70b9\u602a\uff0c\u4f60\u4e0d\u662f\u8981\u6253\u542c\u4ed6\u5417\uff1f\u600e\u4e48\u53c8\u4e0d\u8981\u542c\u4e86\uff1f\u54e6\uff0c\u6211\u660e\u767d \u4e86\uff0c\u6562\u60c5\u4f60\u8bf4\u7684\u8fd9\u4e2a\u4ed6\u4e0d\u662f\u4f60\u7684\u4e08\u592b\uff0c\u662f\u90a3\u4e2a\u5c0f\u53ae\uff0c\u4ed6\u662f\u6a80\u5c0f\u8d1d\u52d2\uff01 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u4e0b\u5b50\u6e05\u9192\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5df1\u7ecf\u77e5\u9053\u4e86\u4ed6\u7684\u6765\u5386\uff0c\u4f60\u4eec\u8981\u5c06\u4ed6 \u600e\u6837\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u600e\u6837\uff0c\u4e0d\u8fc7\u662f\u8981\u628a\u4ed6\u62ff\u53bb\u732e\u7ed9\u4f60\u4eec\u7684\u738b\u7237\u7f62\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u660e\u77e5\u6c42\u5979\u6ca1\u7528\uff0c\u4f46\u5728\u6fc0\u6124\u4e4b\u4e2d\uff0c\u5df2\u662f\u5931\u53bb\u4e86\u7406\u667a\uff0c\u7981\u4e0d\u4f4f\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u80fd\u8fd9 \u6837\uff01\u201d \u3000\u3000\u91d1\u592b\u4eba\u6545\u4f5c\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8fd9\u6837\uff1f\u8fd9\u53ef\u662f\u4f60\u4e08\u592b\u7684\u610f\u601d\u554a\uff01\u4f60\u6ca1\u6709\u542c\u89c1\u4ed6\u521a \u624d\u600e\u6837\u5429\u5490\u6211\u90a3\u5f53\u5bb6\u7684\uff0c\u4ed6\u8bf4\u7684\u662f\uff1a\u6d3b\u7684\u6293\u4e0d\u5230\uff0c\u6b7b\u7684\u4e5f\u8981\uff01\u4f46\u6211\u90a3\u5f53\u5bb6\u7684\u813e\u6c14\uff0c\u60f3\u5fc5\u4f60\u4e5f \u77e5\u9053\u3002\u8981\u662f\u8fd9\u5a03\u6a80\u7684\u5c0f\u5b50\u987d\u6297\u5230\u5e95\uff0c\u8bf4\u4e0d\u5b9a\u771f\u4f1a\u628a\u4ed6\u6253\u6b7b\u7684\u3002\u6240\u4ee5\u4f60\u6700\u597d\u53bb\u529d\u529d\u90a3\u5c0f\u5b50\u6295 \u964d\u3002\u201d\u5b8c\u989c\u592b\u4eba\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u4f4f\u54b3\u55fd\u3002 \u3000\u3000\u91d1\u592b\u4eba\u9053\uff1a\u201c\u5509\uff0c\u53ef\u501f\u4f60\u90a3\u8d34\u8eab\u4e2b\u5934\u8d70\u4e86\u3002\u6ca1\u4eba\u670d\u5f85\u4f60\uff0c\u6211\u66ff\u4f60\u6376\u6376\u80cc\u5427\u3002\u201d\u5b8c\u989c\u592b\u4eba \u63a8\u5f00\u4e86\u5979\u3002\u65a5\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u5047\u732e\u6bb7\u52e4\uff01\u201d\u91d1\u592b\u4eba\u78b0\u4e86\u4e00\u9f3b\u5b50\u7070\uff0c\u5495\u54dd\u9053\uff1a\u201c\u771f\u662f\u72d7\u54ac\u5415\u6d1e \u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\u3002\u201d\u4f46\u968f\u5373\u53c8\u96be\u8d77\u7b11\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u60c5\u4e0d\u597d\uff0c\u6211\u4e0d\u602a\u4f60\u3002\u201d \u3000\u3000\u5979\u53c8\u6328\u7740\u5b8c\u989c\u592b\u4eba\u5750\u4e0b\u4e86\u3002\u5b8c\u989c\u592b\u4eba\u5fc3\u91cc\u5728\u76d8\u7b97\u600e\u6837\u624d\u80fd\u6551\u6a80\u7fbd\u51b2\uff0c\u6839\u672c\u6ca1\u6709\u5fc3\u60c5\u7406\u4f1a \u5730\uff0c\u53ea\u597d\u8ba9\u5979\u5728\u8033\u8fb9\u8052\u7d6e\u3002 \u3000\u3000\u201c\u542c\u8bf4\u4f60\u4ece\u524d\u5728\u5546\u5dde\u7684\u65f6\u5019\uff0c\u6709\u4e2a\u4ec6\u4eba\u53eb\u505a\u5170\u59d1\uff0c\u5c31\u662f\u8fd9\u4f4d\u6a80\u5c0f\u8d1d\u52d2\u7684\u6bcd\u4eb2\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u89c1\u4ed6\u4e0d\u776c\uff0c\u53ea\u597d\u81ea\u8bf4\u81ea\u8bdd\uff1a\u201c\u5018\u82e5\u4ed6\u8fd8\u662f\u8d1d\u52d2\u8eab\u4efd\uff0c\u4f60\u7ef4\u62a4\u4ed6\u8fd8\u6709\u9053\u7406\uff0c\u4f46\u4ed6\u65e9 \u5df2\u5c31\u6210\u4e86\u94a6\u72af\u4e86\uff0c\u54c8\u5fc5\u56fe\u5c31\u662f\u4ed6\u6253\u6b7b\u7684\u3002\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5f53\u7136\u8fd8\u662f\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u91d1\u592b\u4eba\u518d\u95ee\uff1a\u201c\u5728\u5546\u5dde\u7684\u65f6\u5019\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u5170\u59d1\u6bcd\u5b50\u7684\u8eab\u4e16\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u5fc3\u91cc\u538c\u70e6\uff0c\u5b9e\u5728\u662f\u6309\u637a\u4e0d\u4f4f\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u591f\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u966a\u7b11\u9053\uff1b\u201c\u4f60\u83ab\u602a\u6211\u591a\u95ee\uff0c\u5179\u4e8b\u4f53\u5927\uff0c\u6211\u8fd9\u662f\u5173\u5fc3\u4f60\u3002\u4e0d\u8fc7\uff0c\u6211\u60f3\u2014\u2014\u4f60\u90a3\u65f6\u5f53 \u7136\u8fd8\u672a\u77e5\u9053\u4ed6\u4eec\u6bcd\u5b50\u7684\u8eab\u4e16\uff0c\u5426\u5219\u4f60\u4e5f\u4e0d\u4f1a\u6536\u5bb9\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u4f60\u559c\u6b22\u600e\u6837\u731c\u60f3\u5c31\u600e\u6837\u731c\u60f3\uff0c\u6211\u4e5f\u4e0d\u6015\u4f60\u53bb\u544a\u5bc6\u3002\u4f60\u8bf4\u591f\u4e86\u6ca1\u6709\uff1f\u8bf7\u4f60 \u51fa\u53bb\uff01\u201d \u3000\u3000\u91d1\u592b\u4eba\u5bf9\u7740\u5979\u51cc\u5389\u7684\u76ee\u5149\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u3001\u4f46\u5979\u4e00\u5411\u662f\u53d7\u4eba\u5949\u627f\u60ef\u7684\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6709 \u6c14\u3002\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f60\u4e0d\u7ed9\u6211\u9762\u5b50\uff0c\u6211\u504f\u8981\u6c14\u4e00\u6c14\u4f60\uff0c\u4f60\u75c5\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u8c05\u4f60\u4e5f\u5948\u4f55\u4e0d\u4e86 \u6211\u3002\u201d \u3000\u3000\u201c\u5509\uff0c\u4f60\u600e\u80fd\u8fd9\u6837\u8bf4\u8bdd\uff1f\u4ee5\u6211\u4eec\u4e24\u5bb6\u7684\u4ea4\u60c5\uff0c\u4f60\u5c31\u662f\u505a\u4e86\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u4f1a\u66ff\u4f60 \u63a9\u9970\u7684\uff0c\u600e\u4f1a\u544a\u4f60\u7684\u5bc6\uff1f\u6211\u53ea\u89c9\u5f97\u5947\u602a\uff0c\u4e0d\u7ba1\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u5c0f\u53ae\u7684\u8eab\u4e16\uff0c\u6309\u5e38\u7406\u8bf4\uff0c\u65e0\u8bba\u5982 \u4f55\u4f60\u90fd\u4e0d\u5e94\u8be5\u628a\u4ed6\u770b\u5f97\u6bd4\u4f60\u7684\u4e08\u592b\u66f4\u91cd\u8981\u7684\u3002\u6664\uff0c\u83ab\u975e\u90a3\u4ef6\u4e8b\u60c5\uff0c\u7adf\u7136\u4e0d\u662f\u8c23\u8a00\uff1f\u201d \u3000\u3000\u5979\u76ef\u7740\u91d1\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u8c23\u8a00\u4e0d\u8c23\u8a00\u7684\uff1f\u201d \u3000\u3000\u91d1\u592b\u4eba\u6328\u8fd1\u5979\uff0c\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u662f\u597d\u59ca\u59b9\uff0c\u4f60\u83ab\u602a\u6211\u76f4\u8a00\u529d\u4f60\u3002\u6211\u77e5\u9053\u6a80\u7fbd \u51b2\u662f\u8036\u5f8b\u7384\u5143\u7684\u5f1f\u5b50\uff0c\u4f60\u4e00\u5b9a\u662f\u4e3a\u4e86\u8036\u5f8b\u7384\u5143\u7684\u7f18\u6545\uff0c\u624d\u8981\u7ef4\u62a4\u8fd9\u5c0f\u5b50\u7684\u3002\u4f46\u6211\u5018\u82e5\u662f\u4f60\uff0c \u6211\u4e00\u5b9a\u4e0d\u4f1a\u62e6\u963b\u4e08\u592b\u62ff\u8fd9\u5c0f\u5b50\uff0c\u76f8\u53cd\uff0c\u6211\u8fd8\u8981\u5e2e\u4e08\u592b\u62ff\u8fd9\u5c0f\u5b50\u3002\u514d\u5f97\u4ed6\u6000\u7591\u4f60\u5bf9\u65e7\u65e5\u60c5\u90ce\u8fd8 \u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u4ee5\u81f3\u7231\u5c4b\u53ca\u4e4c\uff0c\u8fde\u65e7\u60c5\u4eba\u7684\u5f1f\u5b50\u4f60\u4e5f\u89c6\u540c\u5df2\u51fa\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u95f4\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5b8c\u989c\u592b\u4eba\u6253\u4e86\u91d1\u592b\u4eba\u4e00\u8bb0\u8033\u5149\uff0c\u559d\u9053\uff1a\u201c\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u4e00\u638c\u6253\u843d\u4e86\u5979\u7684\u4e24\u9f7f\u95e8\u7259\u3002 \u3000\u3000\u91d1\u592b\u4eba\u5927\u53eb\uff1a\u201c\u5b8c\u989c\u9274\uff0c\u4f60\u8001\u5a46\u53d1\u4e86\u75af\uff0c\u4f60\u8fd8\u4e0d\u8fc7\u6765\u2014\u2014\u201d\u5979\u6ee1\u9762\u9c9c\u8840\uff0c\u51b2\u5411\u5b8c\u989c\u592b \u4eba\uff0c\u53ef\u662f\u6d3b\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u7ed9\u5b8c\u989c\u592b\u4eba\u6263\u7740\u8109\u95e8\u62d6\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u5b8c\u989c\u9274\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5f53\u771f\u53d1\u75af\u4e86\u5427\uff01\u4f60\u600e\u4e48\u53ef\u4ee5\u8fd9\u6837\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5bb3\u6b7b\u4e86\u5170\u59d1\uff0c\u903c\u8d70\u4e86\u5979\u7684\u5973\u513f\uff0c\u5982\u4eca\u53c8\u8981\u6349\u5979\u7684\u513f\u5b50\uff0c\u4f60\u4eec \u4e3a\u4ec0\u4e48\u53c8\u53ef\u4ee5\u8fd9\u6837\uff1f\u54c8\u54c8\uff0c\u6211\u4e0d\u8fc7\u662f\u8ddf\u4f60\u4eec\u5b66\u7f62\u4e86\uff0c\u8ddf\u4f60\u4eec\u5b66\u7f62\u4e86\uff01\u201d \u3000\u3000\u201c\u5b8c\u989c\u592b\u4eba\uff0c\u653e\u5f00\u62d9\u8346\uff0c\u5426\u5219\u53ef\u4f11\u602a\u6211\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u91d1\u8d85\u5cb3\u559d\u9053\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5df1\u6240\u4e0d\u6b32\uff0c\u52ff\u65bd\u4e8e\u4eba\uff01\u4f60\u4e56\u4e56\u7684\u7ed9\u6211\u6eda\u51fa\u53bb\uff0c\u6211\u5c31\u628a\u4f60\u7684\u8001\u5a46\u4ea4 \u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u865a\u6643\u4e00\u638c\u3002\u907f\u5f00\u6a80\u7fbd\u51b2\u7684\u7389\u8427\u70b9\u7a74\uff0c\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5c31\u5230\u5b8c\u989c\u592b\u4eba\u9762\u524d\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u4f24\u4e86\u4f60\u7684\u8001\u5a46\uff0c\u4f60\u5c31\u2026\u2026\u201d \u3000\u3000\u5979\u4ee5\u4e3a\u91d1\u8d85\u5cb3\u4e0d\u6562\u6253\u5979\uff0c\u90a3\u77e5\u5979\u6d3b\u72b9\u672a\u4e86\uff0c\u91d1\u8d85\u5cb3\u7adf\u662f\u4e00\u638c\u6253\u4e0b\uff01 \u3000\u3000\u8fd9\u4e00\u638c\u5f53\u7136\u6253\u4e0d\u7740\u5b8c\u989c\u592b\u4eba\uff0c\u800c\u662f\u6253\u5728\u4ed6\u81ea\u5df1\u59bb\u5b50\u8eab\u4e0a\u3002 \u3000\u3000\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u65f6\u5019\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6a80\u7fbd\u51b2\u91cd\u638c\u51fa\u51fb\uff0c\u8fd9\u4e00\u62f3\u5df2\u6253\u4e2d\u4e86\u91d1\u8d85\u5cb3\u7684 \u540e\u5fc3\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u8ddf\u8ddf\u8dc4\u8dc4\uff0c\u659c\u7a9c\u4e09\u6b65\uff0c\u4f46\u5b8c\u989c\u592b\u4eba\u5374\u5df2\u662f\u201c\u54c7\u201d\u7684\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u539f\u6765\u91d1\u8d85\u5cb3\u6253\u5728\u4ed6\u59bb\u5b50\u8eab\u4e0a\u7684\u90a3\u4e00\u638c\uff0c\u7528\u7684\u4e43\u662f\u9694\u7269\u4f20\u529f\u3002\u867d\u7136\u6253\u5728\u59bb\u5b50\u8eab\u4e0a\uff0c\u53d7\u5230\u638c \u529b\u9707\u64bc\u7684\u5374\u662f\u5b8c\u989c\u592b\u4eba\u3002 \u3000\u3000\u5e78\u4e8f\u6a80\u7fbd\u51b2\u4e5f\u521a\u597d\u53ca\u65f6\u6253\u4e2d\u4e86\u91d1\u8d85\u5cb3\uff0c\u662f\u6b63\u5f53\u7740\u91d1\u8d85\u5cb3\u53d1\u529b\u4e4b\u9645\u6253\u4e2d\u4ed6\u7684\u540e\u5fc3\uff0c\u8981\u5bb3 \u7684\uff0c\u91d1\u8d85\u5cb3\u90a3\u4e00\u638c\u529b\u5927\u6253\u6298\u6263\uff0c\u5b8c\u989c\u592b\u4eba\u8fd9\u624d\u80fd\u52c9\u5f3a\u652f\u6301\u3002 \u3000\u3000\u5b8c\u989c\u9274\u4e00\u89c1\u91d1\u8d85\u5cb3\u53d7\u4f24\uff0c\u6a80\u7fbd\u51b2\u6b63\u5411\u4ed6\u6012\u76ee\u800c\u89c6\uff0c\u4ed6\u54ea\u91cc\u8fd8\u6562\u5411\u524d\uff1f\u5b8c\u989c\u592b\u4eba\u7a81\u7136\u632f\u81c2 \u4e00\u629b\uff0c\u628a\u91d1\u592b\u4eba\u629b\u51fa\uff0c\u559d\u9053\uff1a\u201c\u628a\u4f60\u7684\u59bb\u5b50\u5e26\u8d70\uff01\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u53d7\u7684\u4f24\u6216\u8bb8\u6ca1\u6709\u5b8c\u989c\u592b\u4eba\u4e4b\u91cd\uff0c\u4f46\u5df2\u81ea\u77e5\u662f\u7edd\u5bf9\u6253\u4e0d\u8fc7\u6a80\u7fbd\u51b2\u7684\u4e86\u3002\u4ed6\u63a5\u8fc7\u59bb \u5b50\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u7f62\u4e86\uff01\u201d\u751f\u6015\u6a80\u7fbd\u51b2\u4e58\u673a\u653b\u51fb\uff0c\u62b1\u7740\u59bb\u5b50\uff0c\u6025\u6025\u5fd9\u5fd9\u5c31\u8dd1\u51fa\u53bb\u3002 \u3000\u3000\u5b8c\u989c\u9274\u548c\u7941\u8fde\u4e8c\u8001\u90fd\u9003\u8dd1\u4e86\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u592b\u4eba\uff0c\u591a\u8c22\u4f60\u53c8\u4e00\u6b21\u6551\u4e86\u6211\uff0c\u4f60\uff0c\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u6b64\u65f6\u4ed6\u65b9\u59cb\u53d1\u89c9\u5b8c\u989c\u592b\u4eba\u8138\u4e0a\u6ca1\u6709\u534a\u70b9\u8840\u8272\uff0c\u82cd\u767d\u5982\u7eb8\u4e00\u822c\u3002 \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u6211\u5e2e\u5fd9\u7684\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53ea\u9053\u5979\u662f\u7981\u53d7\u4e0d\u8d77\u523a\u6fc0\u624d\u5f04\u8fd9\u6837\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u6211\u53d7\u4f60\u7684\u6069\u60e0\u592a\u591a\u4e86\u3002\u6211\u90a3\u59b9 \u5b50\uff0c\u5979\uff0c\u5979\u2026\u2026\u201d\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u521a\u624d\u4f60\u5927\u6982\u5df2\u7ecf\u542c\u89c1\u91d1\u8d85\u5cb3\u592b\u59bb\u8bf4\u7684\u90a3\u4e9b\u8bdd\u4e86\uff1f\u201d\u6a80\u7fbd\u51b2 \u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u6211\u7684\u59b9\u5b50\u88ab\u4e00\u4e2a\u4ec0\u4e48\u6c5f\u5357\u5927\u76d7\u738b\u5b87\u5ead\u5e26\u8d70\uff0c\u662f\uff0c\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u738b\u5b87\u5ead\u662f\u592a\u6e56\u4e03\u5341\u4e8c\u5bb6\u5be8\u4e3b\u7684\u603b\u5934\u9886\uff0c\u4ed6\u7684\u603b\u8235\u5728\u592a\u6e56\u897f\u6d1e\u5ead \u5c71\uff0c\u4ed6\u4e5f\u662f\u4f60\u5e08\u7236\u7684\u670b\u53cb\uff0c\u6211\u628a\u4ee4\u59b9\u4ea4\u7ed9\u4ed6\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u3002\u201d\u5979\u8bf4\u8bdd\u4e4b\u9645\uff1b\u8fde\u8fde\u54b3\u55fd\uff0c\u663e\u7136 \u662f\u6ca1\u6709\u6c14\u529b\u7ec6\u9053\u5176\u8be6\u4e86\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u5f53\u771f\u6ca1\u4e8b\uff1f\u8ba9\u6211\u66ff\u4f60\u628a\u4e00\u628a\u8109\u3002\u201d\u6307\u5934\u4e00\u89e6 \u5979\u7684\u8109\u95e8\uff0c\u6a80\u7fbd\u51b2\u7684\u4e00\u9897\u5fc3\u5c31\u5413\u5f97\u51e0\u4e4e\u4ece\u8154\u5b50\u91cc\u8df3\u51fa\u6765\u3002\u4ece\u8109\u640f\u4e2d\uff0c\u6a80\u7fbd\u51b2\u4e0d\u4f46\u77e5\u9053\u5979\u7684\u5185 \u4f24\u751a\u91cd\uff0c\u800c\u4e14\u4f3c\u4e4e\u6709\u4e2d\u6bd2\u7684\u8ff9\u8c61\uff0c\u8109\u606f\u51cc\u4e71\u3001\u5fae\u5f31\uff0c\u8fd9\u79cd\u60c5\u5f62\u5fc3\u810f\u968f\u65f6\u90fd\u6709\u505c\u6b62\u8df3\u52a8\u7684\u53ef \u80fd\u3002\u5b8c\u989c\u592b\u4eba\u5e73\u8c08\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6789\u8d39\u6c14\u529b\uff0c\u6211\u5728\u88ab\u91d1\u8d85\u5cb3\u6253\u4f24\u4e4b\u524d\uff0c\u5df2\u7ecf\u670d\u4e86\u6bd2\uff0c\u8fd9\u79cd\u6bd2 \u4ee4\u6211\u6b7b\u5f97\u6bd4\u8f83\u8212\u670d\u7684\u3002\u201d\u6a80\u7fbd\u51b2\u5927\u53eb\uff1a\u201c\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u6de1\u7136\u4e00\u7b11\uff1a\u6211\u4e0d\u8fd9\u6837\uff0c\u53c8\u80fd\u600e\u6837\u3002\u8bf4\u9053\uff1a\u201c\u6211\u7ecf\u8fc7\u4e86\u4eca\u65e5\u4e4b\u4e8b\uff0c\u8fd8\u80fd\u591f\u548c\u5b8c\u989c \u9274\u8fc7\u4e00\u8f88\u5b50\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fde\u5fd9\u6309\u7740\u5979\u7684\u540e\u5fc3\uff0c\u628a\u771f\u6c14\u8f93\u9001\u53bb\uff0c\u8ba9\u5979\u53ef\u4ee5\u591a\u6d3b\u7247\u523b\u3002\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u6709\u4ec0 \u4e48\u672a\u4e86\u4e4b\u4e8b\uff0c\u5feb\u548c\u6211\u8bf4\u3002\u201d \u3000\u3000\u5b8c\u989c\u592b\u4eba\u90a3\u672c\u5df2\u662f\u7ec6\u5982\u868a\u53eb\u7684\u58f0\u97f3\u5927\u4e86\u4e00\u70b9\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u60f3\u542c\u4f60 \u7684\u5e08\u7236\u5439\u4e00\u6b21\u8427\u3002\u542c\u4e0d\u5230\u4e5f\u65e0\u6240\u8c13\u4e86\u3002\u55ef\uff0c\u4ed6\u5439\u7684\u8427\u771f\u597d\u542c\u2026\u2026\u201d\u795e\u667a\u9010\u6e10\u6a21\u7cca\uff0c\u50cf\u662f\u5df2\u7ecf \u6c89\u6d78\u5728\u8fc7\u53bb\u7684\u56de\u5fc6\u4e2d\uff0c\u4f46\u8138\u4e0a\u663e\u7136\u6709\u9057\u61be\u7684\u795e\u60c5\u3002 \u3000\u3000\u90a3\u5973\u4ec6\u5fcd\u7740\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u6a80\u516c\u5b50\uff0c\u4f60\u5feb\u8d70\u5427\u3002\u592b\u4eba\u540e\u4e8b\uff0c\u6709\u5a62\u5b50\u6599\u7406\u3002\u4ed6\u4eec\u6050\u6015\u8fd8\u4f1a\u56de \u6765\u7684\uff0c\u518d\u8fdf\uff0c\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6ca1\u6709\u8d70\uff0c\u4ed6\u4e00\u8a00\u4e0d\u53d1\uff0c\u5374\u5439\u8d77\u7389\u8427\u3002 \u3000\u3000\u8427\u58f0\u5982\u51fa\u8c37\u9ec4\u83ba\uff0c\u5973\u4ec6\u542c\u4e0d\u61c2\uff0c\u5b8c\u989c\u592b\u4eba\u5374\u8ddf\u7740\u8282\u62cd\uff0c\u5728\u5fc3\u91cc\u9ed8\u5ff5\u90a3\u7f8e\u5999\u7684\u6b4c\u8f9e\u3002 \u3000\u3000\u5ead\u524d\u828d\u836f\u5996\u65e0\u683c\uff0c\u6c60\u4e0a\u8299\u84c9\u51c0\u5c11\u60c5\u3002 \u3000\u3000\u60df\u6709\u7261\u4e39\u771f\u56fd\u8272\uff0c\u82b1\u5f00\u65f6\u8282\u52a8\u4eac\u57ce\u3002 \u3000\u3000\u8fd9\u662f\u8036\u5f8b\u7384\u5143\u548c\u5979\u521d\u76f8\u8bc6\u7684\u90a3\u5929\uff0c\u7b2c\u4e00\u6b21\u5439\u7ed9\u5979\u542c\u7684\u90a3\u652f\u66f2\u5b50\u3002\u662f\u8d5e\u7f8e\u90a3\u682a\u540d\u79cd\u7261\u4e39 \u201c\u9752\u9f99\u5367\u58a8\u6c60\u201d\u7684\u3002\u5f53\u7136\uff0c\u5176\u5b9e\u5219\u662f\u501f\u82b1\u8d5e\u4eba\u3002 \u3000\u3000\u5979\u5411\u5973\u4ec6\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u773c\u775b\u671b\u5411\u6a80\u7fbd\u51b2\u8fdb\u6765\u90a3\u4e2a\u82b1\u84dd\u3002 \u3000\u3000\u8fd9\u6b21\u5973\u4ec6\u5012\u662f\u61c2\u5f97\u5979\u7684\u610f\u601d\u4e86\u3002\u628a\u90a3\u6735\u9ed1\u7261\u4e39\u62ff\u6765\u3002\u653e\u5728\u5979\u773c\u524d\u3002 \u3000\u3000\u5979\u6df1\u60c5\u7684\u671b\u7740\u8fd9\u6735\u9ed1\u7261\u4e39\uff0c\u597d\u50cf\u628a\u5b83\u5f53\u4f5c\u4e86\u771f\u7684\u201c\u9752\u9f99\u5367\u58a8\u6c60\u201d\u3002\u7261\u4e39\u5728\u5979\u7684\u773c\u524d\u6643\u5440 \u6643\u5440\uff0c\u6447\u6447\u6643\u6643\uff0c\u5e7b\u51fa\u4e86\u8036\u5f8b\u7384\u5143\u7684\u5f71\u5b50\uff0c\u4e5f\u5e7b\u51fa\u5979\u81ea\u5df1\u5c11\u5973\u65f6\u5019\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8427\u58f0\u4e00\u53d8\uff0c\u6109\u5feb\u7684\u8282\u62cd\u4e2d\u7565\u5e26\u51e0\u5206\u82cd\u51c9\u3002 \u3000\u3000\u201c\u4e07\u4e07\u82b1\u4e2d\u7b2c\u4e00\u6d41\uff0c\u6b8b\u971e\u8f7b\u67d3\u5ae9\u94f6\u74ef\u3002 \u3000\u3000\u80fd\u72c2\u7d2b\u964c\u5343\u91d1\u5b50\uff0c\u4e5f\u611f\u6731\u95e8\u4e07\u6237\u4faf\u3002 \u3000\u3000\u671d\u65e5\u7167\u5f00\u6447\u9152\u770b\uff0c\u66ae\u98ce\u5439\u843d\u7ed5\u680f\u6536\u3002\u2026\u2026\u201d \u3000\u3000\u8fd9\u662f\u8036\u5f8b\u7384\u5143\u548c\u5979\u5206\u624b\u4e4b\u65f6\u5439\u7684\u66f2\u5b50\u3002 \u3000\u3000\u4e00\u66f2\u672a\u7ec8\uff0c\u5b8c\u989c\u592b\u4eba\u7684\u773c\u775b\u5df1\u662f\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u5979\u7684\u8138\u4e0a\u8fd8\u7efd\u7740\u7b11\u5bb9\uff0c\u5979\u7684\u786e\u662f\u6ee1\u6000\u559c\u60a6\uff0c\u5e26\u7740\u521d\u604b\u7684\u5fc3\u60c5\u79bb\u5f00\u8fd9\u4e2a\u4eba\u95f4\u7684\u3002 \u3000\u3000----------- \u3000\u3000\u7fbd\u751f\u5802 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u897f\u6e56\u98ce\u6ce2 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u897f\u6e56\u98ce\u6ce2 \u3000\u3000\u6ce5\u4e0a\u5076\u7136\u7559\u6307\u722a\uff0c\u9e3f\u98de\u90a3\u590d\u8ba1\u4e1c\u897f\u3002\u6a80\u7fbd\u51b2\u7ec8\u4e8e\u6765\u5230\u4e86\u4e34\u5b89\uff0c\u5018\u4f6f\u4e8e\u897f\u5b50\u6e56\u8fb9\u4e86\u3002 \u3000\u3000\u201c\u6e56\u5149\u6f4b\u8273\u6674\u65b9\u597d\uff0c\u5c71\u8272\u7a7a\u8499\u96e8\u4ea6\u5947\uff0c\u82e5\u628a\u897f\u6e56\u6bd4\u897f\u5b50\uff0c\u6de1\u5986\u6d53\u62b9\u603b\u76f8\u5b9c\u3002\u201d\u8fd9\u662f\u82cf\u4e1c \u5761\u8d5e\u7f8e\u897f\u6e56\u7684\u53e5\u5b50\u3002 \u3000\u3000\u201c\u6c5f\u5357\u597d\uff0c\u98ce\u666f\u65e7\u66fe\u8c19\uff1a\u65e5\u51fa\u6c5f\u82b1\u7ea2\u4f3c\u706b\uff0c\u6625\u6765\u6c5f\u6c34\u7eff\u5982\u84dd\uff0c\u80fd\u4e0d\u5fc6\u6c5f\u5357\uff1f\u201d\u8fd9\u662f\u767d\u5c45 \u6613\u5378\u5b98\u4e4b\u540e\uff0c\u56e0\u5bf9\u676d\u5dde\u7684\u601d\u5ff5\u800c\u586b\u7684\u4e09\u9996\u300a\u5fc6\u6c5f\u5357\u300b\u4e2d\u7684\u4e00\u9996\u3002\u540c\u6837\uff0c\u4e5f\u8868\u8fbe\u4e86\u5bf9\u897f\u6e56\u7684\u8d5e \u7f8e\u3002\u897f\u6e56\uff0c\u5343\u767e\u5e74\u6765\uff0c\u66fe\u53d7\u8fc7\u591a\u5c11\u8bd7\u4eba\u8bcd\u5ba2\u7684\u6b4c\u548f\uff0c\u8d5e\u53f9\uff01\u6a80\u7fbd\u51b2\u6765\u5230\u7684\u65f6\u8282\uff0c\u6b63\u662f\u6625\u6696\u82b1 \u5f00\u7684\u65e9\u6625\u4e8c\u6708\u2014\u2014\u897f\u6e56\u6700\u7f8e\u4e3d\u7684\u5b63\u8282\u3002\u4f46\u4ed6\u5728\u5fc3\u8ff7\u76ee\u9189\u4e8e\u897f\u6e56\u7f8e\u666f\u4e4b\u4f59\uff0c\u5374\u4e5f\u4e0d\u7981\u53e6\u6709\u4e00\u756a \u611f\u6168\u3002\u897f\u6e56\u4e24\u8fb9\u7684\u82cf\u5824\u767d\u5824\u90fd\u6ee1\u662f\u6e38\u4eba\uff0c\u4ed6\u5018\u4f6f\u6e56\u7554\uff0c\u653e\u773c\u56db\u987e\uff0c\u6e56\u4e0a\u662f\u753b\u8239\u8f7d\u9152\uff0c\u7a33\u6cdb\u5e73 \u6ce2\uff1b\u5824\u4e0a\u662f\u6cb9\u58c1\u9999\u8f66\uff0c\u5206\u82b1\u62c2\u67f3\u3002\u6e56\u4e0a\u5cb8\u4e0a\u90fd\u662f\u9690\u9690\u7afa\u6b4c\u5904\u5904\u968f\u3002\u90a3\u91cc\u770b\u5f97\u51fa\u4e00\u70b9\u5907\u6218\u6c14 \u6c1b\uff1f\u4ed6\u60f3\u8d77\u4ece\u91d1\u56fd\u7684\u5357\u6765\u9014\u4e2d\uff0c\u4e00\u8def\u6240\u89c1\u7684\u8f66\u8f9a\u8f9a\u3001\u9a6c\u8427\u8427\u7684\u666f\u50cf\uff0c\u5b9e\u662f\u4e0d\u7981\u4e3a\u8fd9\u4f5c\u4e3a\u5357\u5b8b \u201c\u6218\u65f6\u9996\u90fd\u201d\u7684\u4e34\u5b89\u53f9\u606f\u4e86\u3002\u201c\u8d75\u5b8b\u5357\u6e21\uff0c\u628a\u676d\u5dde\u6539\u540d\u4e34\u5b89\uff0c\u4e34\u5b89\u5176\u5b9e\u5373\u662f\u82db\u5b89\uff0c\u770b\u6765\u4ed6\u4eec \u662f\u60f3\u5728\u4e34\u5b89\u4ee5\u56fe\u82df\u5b89\u7684\u4e86\u3002\u201d\u4ed6\u60f3\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\uff0c\u4ed6\u5df2\u8d70\u5230\u4e86\u897f\u6e56\u8fb9\u6700\u8d1f\u76db\u540d\u90a3\u5bb6\u9152\u697c\u2014\u2014\u697c\u5916\u697c\u7684\u95e8\u524d\u4e86\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u7684\u4e0d\u662f\u8d5e\u7f8e\u897f\u6e56\u7684\u8bd7\u8bcd\uff0c\u5374\u662f\u548c\u697c\u5916\u697c\u6709\u5173\u7684\u4e00\u9996\u8bd7\uff0c\u4e00\u9996\u8bbd\u523a\u610f\u5473\u5f88\u6d53\u7684\u8bd7\u3002 \u3000\u3000\u201c\u5c71\u5916\u9752\u5c71\u697c\u5916\u697c\u3002 \u3000\u3000\u897f\u6e56\u6b4c\u821e\u51e0\u65f6\u4f11\uff1f\u6696\u98ce\u718f\u5f97\u6e38\u4eba\u9189\uff0c\u76f4\u628a\u676d\u5dde\u4f5c\u6c74\u5dde\u3002\u201d \u3000\u3000\u4ed6\u6447\u5934\u82e6\u7b11\uff0c\u8d70\u4e0a\u697c\u5916\u697c\uff0c\u4ed6\u9009\u4e86\u4e00\u4e2a\u4e34\u7a97\u7684\u5ea7\u4f4d\uff0c\u70b9\u4e86\u697c\u5916\u697c\u7684\u540d\u83dc\u201c\u918b\u6e9c\u9c7c\u201d\u548c \u201c\u871c\u65b9\u201d\uff08\u6700\u597d\u7684\u871c\u6c41\u706b\u817f\uff09\uff0c\u8981\u4e86\u4e00\u58f6\u201c\u52a0\u996d\u201d\uff08\u4e0a\u597d\u7ecd\u9152\uff09\uff0c\u6682\u4e14\u628a\u80f8\u4e2d\u7684\u6291\u90c1\u653e\u5f00\uff0c \u4f4e\u659f\u6d45\u914c\uff0c\u6b23\u8d4f\u897f\u6e56\u98ce\u666f\u3002 \u3000\u3000\u4e00\u6761\u753b\u8239\u5728\u7a97\u5916\u7684\u6e56\u9762\u7ecf\u8fc7\uff0c\u8239\u4e2d\u7684\u6b4c\u5973\u6b63\u5728\u5531\u4e00\u9996\u65b0\u8bcd\u3002 \u3000\u3000\u5531\u7684\u662f\u5f20\u4e8e\u6e56\u7684\u300a\u897f\u6c5f\u6708\u300b\uff1a\u201c\u95ee\u90a3\u6e56\u8fb9\u67f3\u8272\uff0c\u91cd\u6765\u53c8\u662f\u4e09\u5e74\u3002 \u3000\u3000\u6765\u98ce\u5439\u8fc7\u6211\u6e56\u8239\uff0c\u6768\u67f3\u4e1d\u4e1d\u62c2\u9762\u3002 \u3000\u3000\u4e16\u8def\u5982\u4eca\u5df2\u60ef\uff0c\u6b64\u5fc3\u5230\u5904\u60a0\u7136\u3002 \u3000\u3000\u5bd2\u5149\u4ead\u4e0b\u6c34\u8fde\u5929\uff0c\u98de\u8d77\u6c99\u9e25\u4e00\u7247\u3002\u201d \u3000\u3000\u90bb\u5ea7\u7684\u4e24\u4e2a\u5b98\u5458\u540c\u8d5e\uff1a\u201c\u597d\u8bcd\uff01\u201d\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u679c\u7136\u4e0d\u6127\u662f\u72b6\u5143\u4e4b\u624d\u3002\u201d\uff08\u6309\uff1a\u5f20\u5b5d \u7965\uff0c\u53f7\u4e8e\u6e56\uff0c\u662f\u7ecd\u5174\u4e8c\u5341\u56db\u5e74\u72b6\u5143\uff09\u4e00\u4e2a\u6447\u5934\u6643\u8111\u8bf4\u9053\uff1a\u201c\u4e16\u8def\u5982\u4eca\u5df2\u60ef\uff0c\u6b64\u5fc3\u5230\u5904\u60a0\u7136\u3002 \u771f\u662f\u80fd\u591f\u770b\u7834\u4e16\u60c5\uff0c\u5fc3\u5883\u5e73\u548c\uff0c\u80fd\u628a\u95f9\u5e02\u5f53\u4f5c\u5c71\u6797\u96bd\u8bed\u3002\u542c\u4eba\u6b4c\u6b64\u8bcd\uff0c\u6211\u4e5f\u60f3\u5728\u6e56\u5c71\u7ec8\u8001 \u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5ea7\u5934\u7684\u5ba2\u4eba\uff0c\u5934\u6234\u65b9\u5dfe\uff0c\u8eab\u7a7f\u84dd\u5e03\u957f\u886b\uff0c\u867d\u7136\u4e0d\u662f\u8863\u88f3\u7834\u65e7\uff0c\u8d28\u6599\u5374\u5f88\u666e\u904d\u3002\u770b \u6765\u50cf\u662f\u843d\u9b44\u79c0\u624d\u3002\u4ed6\u5374\u5ffd\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f20\u4e8e\u6e56\u7684\u8bcd\u6709\u51fa\u4e16\u7684\u4e00\u9762\uff0c\u4e5f\u6709\u5165\u4e16\u7684\u4e00\u9762\u3002\u4ed6\u6700 \u597d\u7684\u8bcd\uff0c\u53ef\u4e0d\u662f\u8fd9\u4e00\u9996\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5b98\u5458\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4f9d\u4f60\u770b\u6765\u662f\u54ea\u4e00\u9996\uff1f\u201d \u3000\u3000\u90a3\u7a77\u79c0\u624d\u6a21\u6837\u7684\u4e2d\u5e74\u4eba\uff0c\u659f\u4e86\u6ee1\u6ee1\u4e00\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u9ad8\u58f0\u541f\u5531\u8d77\u6765\uff1a\u201c\u957f\u6dee\u671b\u65ad\uff0c\u5173\u585e \u83bd\u7136\u5e73\u3002 \u3000\u3000\u5f81\u5c18\u6697\uff0c\u971c\u98ce\u52b2\uff0c\u6084\u8fb9\u58f0\uff0c\u9eef\u9500\u51dd\u3002 \u3000\u3000\u8ffd\u60f3\u5f53\u5e74\u4e8b\uff0c\u6b86\u5929\u6570\uff0c\u975e\u4eba\u529b\uff1b\u8bdb\u6cd7\u4e0a\uff0c\u5f26\u6b4c\u5730\uff0c\u4ea6\u81bb\u8165\u3002 \u3000\u3000\u9694\u6c34\u6be1\u4e61\uff0c\u843d\u65e5\u725b\u7f8a\u4e0b\u3002\u533a\u8131\u7eb5\u6a2a\u3002 \u3000\u3000\u770b\u540d\u738b\u5bb5\u63aa\uff0c\u9a91\u706b\u4e00\u5ddd\u660e\u3002 \u3000\u3000\u7b33\u9f13\u60b2\u9e23\uff0c\u9063\u4eba\u60ca\u3002 \u3000\u3000\u5ff5\u8170\u95f4\u7bad\uff0c\u5323\u4e2d\u5251\uff0c\u7a7a\u57c3\u8822\uff0c\u7adf\u4f55\u6210\uff01 \u3000\u3000\u65f6\u6613\u5931\uff0c\u5fc3\u5f92\u58ee\uff0c\u5c81\u5c06\u96f6\u3002 \u3000\u3000\u6e3a\u795e\u4eac\uff0c\u5e72\u7fbd\u65b9\u6000\u8fdc\uff0c\u9759\u950b\u71e7\uff0c\u4e14\u4f53\u5175\u3002 \u3000\u3000\u51a0\u76d6\u4f9d\uff0c\u7eb7\u9a70\u9a9b\uff0c\u82e5\u4e3a\u60c5\u3002 \u3000\u3000\u95fb\u9053\u4e2d\u539f\u9057\u8001\uff0c\u5e38\u5357\u671b\u7fe0\u8446\u9713\u65cc\u3002 \u3000\u3000\u4f7f\u884c\u4eba\u5230\u6b64\uff0c\u5fe0\u6124\u6c14\u586b\u81ba\uff0c\u6709\u6cea\u5982\u503e\uff01\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u8c03\u5bc4\u300a\u516d\u5dde\u6b4c\u5934\u300b\uff0c\u662f\u5f20\u4e8e\u6e56\u611f\u6000\u56fd\u4e8b\u4e4b\u4f5c\u3002\u5c24\u5176\u6700\u540e\u4e24\u53e5\uff0c\u5199\u4e2d\u539f\u9057\u8001\uff0c\u76fc\u671b \u5357\u5b8b\u6536\u590d\u6545\u571f\u7684\u5fc3\u60c5\uff0c\u542b\u6709\u65e0\u9650\u60b2\u6124\u3002\u6a80\u7fbd\u51b2\u60c5\u4e0d\u81ea\u7981\u7684\u8d5e\u9053\uff1a\u201c\u597d\u8bcd\uff0c\u597d\u8bcd\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u5b98\u5458\u90fd\u662f\u4e0d\u7ea6\u800c\u540c\u7684\u76b1\u7709\u9053\uff1a\u201c\u72c2\u751f\uff01\u72c2\u751f\uff01\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53c8\u6765\u4e24\u4e2a\u5ba2\u4eba\u3002\u4e00\u4e2a\u5e74\u7ea6\u56db\u65ec\uff0c\u9762\u767d\u65e0\u987b\uff0c\u5934\u6234\u4e4c\u6c99\uff0c\u8eab\u7a7f\u5b98\u670d\u3001\u53e6\u4e00\u4e2a\u4e0d \u8fc7\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u8863\u670d\u534e\u4e3d\uff0c\u770b\u6765\u4e5f\u662f\u5bcc\u8d35\u4eba\u5bb6\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u8fdb\u6765\uff0c\u9152\u697c\u4e0a\u5012\u6709\u4e00\u534a\u5ba2\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u4e89\u7740\u548c\u4ed6\u4eec\u6253\u62db\u547c\u3002\u6a80\u7fbd\u51b2\u90bb\u5ea7\u90a3\u4e24\u4e2a \u5b98\u513f\uff0c\u66f4\u662f\u8d8b\u524d\u8fce\u63a5\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u53f2\u5927\u4eba\uff0c\u600e\u7684\u4eca\u65e5\u8fd9\u6837\u597d\u5174\u81f4\u6765\u559d\u9152\uff1f\u201d\u4e00\u4e2a\u95ee\u9053\uff1a \u201c\u8fd9\u4f4d\u516c\u4e8e\u662f\u2014\u2014\u770b\u6765\u8fd9\u4e2a\u59d3\u53f2\u7684\u4e2d\u5e74\u5b98\u5458\uff0c\u5b98\u9636\u5f88\u662f\u4e0d\u5c0f\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5374\u4e0d\u7406\u4f1a\u8fd9\u4e2a\u53f2\u5927\u4eba\u662f\u4ec0\u4e48\u4eba\uff0c\u5012\u662f\u90a3\u4e2a\u5c11\u5e74\u4ee4\u4ed6\u5403\u4e00\u60ca\u3002\u4ed6\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u5c11 \u5e74\uff0c\u600e\u597d\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u90a3\u201c\u53f2\u5927\u4eba\u201d\u9053\uff1a\u201c\u8fd9\u4f4d\u8c2d\u516c\u5b50\u662f\u6211\u7684\u4e16\u4ea4\uff0c\u4ed6\u521a\u4ece\u5916\u5730\u5230\uff0c\u6545\u6b64\u6211\u8bf7\u4ed6\u6765\u697c\u5916\u697c\u89c2\u8d4f \u897f\u6e56\u3002\u201d \u3000\u3000\u65c1\u4eba\u542c\u8bf4\u8fd9\u5c11\u5e74\u662f\u4ed6\u7684\u4e16\u4ea4\uff0c\u5f53\u7136\u90fd\u4e0d\u7981\u5bf9\u4ed6\u53e6\u773c\u76f8\u770b\u4e86\u3002\u6a80\u7fbd\u51b2\u90bb\u5ea7\u90a3\u4e24\u4e2a\u5b98\u513f\u4fbf \u9053\uff1a\u201c\u96be\u5f97\u8c2d\u516c\u5b50\u8fd0\u9053\u800c\u6765\uff0c\u8bf7\u8ba9\u6211\u4eec\u4e3a\u516c\u6d17\u5c18\u3002\u201d \u3000\u3000\u90a3\u201c\u53f2\u5927\u4eba\u201d\u9053\uff1a\u201d\u600e\u80fd\u8ba9\u4f60\u4eec\u7834\u8d39\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u5b98\u513f\u9053\uff1a\u201c\u8fd9\u662f\u8bf7\u90fd\u8bf7\u4e0d\u5230\u7684\uff0c\u4f55\u51b5\u6211\u8fd8\u60f3\u5411\u53f2\u5927\u4eba\u8ba8\u6559\u5462\u3002\u201d \u3000\u3000\u90a3\u201c\u53f2\u5927\u4eba\u201d\u63a8\u8f9e\u4e0d\u6389\uff0c\u4fbf\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u8fd9\u4e16\u4f84\u521d\u6765\u4e4d\u5230\uff0c\u5c31\u8ba9\u4ed6\u591a\u4ea4\u4e24\u4f4d\u670b\u53cb\u5427\u3002 \u8fd9\u4eec\u4f4d\u662f\u84dd\u7f16\u4fee\uff0c\u8fd9\u4f4d\u662f\u9ec4\u7f16\u4fee\uff0c\u4ed6\u4eec\u90fd\u662f\u5728\u7ff0\u6797\u9662\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u542c\u5f97\u8fd9\u5c11\u5e74\u81ea\u79f0\u59d3\u201c\u8c2d\u201d\uff0c\u201c\u6a80\u201d\u201c\u8c2d\u201d\u97f3\u8fd1\uff0c\u4ed6\u81ea\u5df1\u4e5f\u66fe\u6539\u59d3\u201c\u8c2d\u201d\u7684\uff0c\u5fc3\u4e2d \u4e00\u52a8\uff0c\u96be\u9053\u4ed6\u4e5f\u662f\u2014\u2014\u201d \u3000\u3000\u90a3\u201c\u53f2\u5927\u4eba\u201d\u5750\u4e0b\u6765\u9053\uff1a\u201c\u521a\u624d\u6211\u597d\u50cf\u542c\u89c1\u6709\u4eba\u5728\u5531\u5f20\u4e8e\u6e56\u7684\u8bcd\uff1f\u201d \u3000\u3000\u90a3\u59d3\u84dd\u7684\u5b98\u513f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6e56\u4e0a\u7684\u753b\u8239\u6709\u4e2a\u6b4c\u5973\u5531\u4e86\u5f20\u4e8e\u6e56\u90a3\u9996\u897f\u6c5f\u6708\uff0c\u8fd9\u9152\u697c\u4e0a\u4e5f\u6709 \u4eba\u5531\u4e86\u4ed6\u90a3\u9996\u516d\u5dde\u6b4c\u5934\u3002\u201d \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u9053\uff1a\u201c\u6211\u90fd\u542c\u89c1\u4e86\u3002\u201d \u3000\u3000\u90a3\u59d3\u9ec4\u7684\u5b98\u513f\u9053\uff1a\u201c\u6211\u6b63\u60f3\u8bf7\u6559\u5927\u4eba\uff0c\u8fd9\u4e24\u9996\u8bcd\u7a76\u7adf\u54ea\u4e00\u9996\u597d\uff1f\u201d \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u90fd\u662f\u7ff0\u6797\u9662\u5b66\u58eb\uff0c\u662f\u8be5\u6211\u5411\u4f60\u4eec\u8bf7\u6559\u624d\u5bf9\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5b98\u513f\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u79e6\u76f8\u7237\u751f\u524d\u90fd\u5938\u8d5e\u8fc7\u5927\u4eba\u7684\u6587\u624d\u7684\uff0c\u6211\u4eec\u8fd9\u70b9\u5b66\u95ee\uff0c\u600e\u80fd\u548c\u5927\u4eba \u6bd4\u8f83\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u7684\u79e6\u76f8\u7237\u60f3\u5fc5\u5c31\u662f\u79e6\u6867\uff0c\u539f\u6765\u8fd9\u4e2a\u53f2\u5927\u4eba\u662f\u79e6\u6867\u63d0\u62d4\u7684\u3002\u201d \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u9053\uff1a\u201c\u4e24\u9996\u8bcd\u98ce\u683c\u4e0d\u540c\uff0c\u5404\u6709\u5404\u7684\u597d\u5904\u3002\u4e0d\u8fc7\u6211\u559c\u6b22\u90a3\u9996\u897f\u6c5f\u6708\u66f4\u591a\u4e00\u4e9b\u3002 \u6b64\u5fc3\u5230\u5904\u60a0\u7136\uff0c\u771f\u6709\u51e0\u5206\u6e0a\u660e\u8bd7\u7684\u5473\u9053\u3002\u201d \u3000\u3000\u90a3\u59d3\u84dd\u7684\u5b98\u513f\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4eec\u4e5f\u662f\u8fd9\u6837\u60f3\u7684\u3002\u8fd9\u6b63\u662f\u4e00\u4e00\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\uff1a\u201c\u8fd9\u6b63\u662f\u82f1 \u96c4\u6240\u89c1\u7565\u540c\u201d\u7684\uff0c\u4f46\u60f3\u82e5\u8fd9\u6837\u8bf4\uff0c\u5c82\u975e\u628a\u81ea\u5df1\u7684\u8eab\u4efd\u63d0\u9ad8\u5230\u548c\u201c\u53f2\u5927\u4eba\u201d\u4e00\u6837\uff0c\u6025\u5fd9\u4f4f\u53e3\u3002 \u3000\u3000\u90a3\u843d\u9b44\u79c0\u624d\u6a21\u6837\u7684\u4eba\u6b63\u5728\u559d\u9152\uff0c\u5ffd\u5730\u5657\u55e4\u4e00\u7b11\uff0c\u9152\u90fd\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u59d3\u84dd\u7684\u5b98\u513f\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u79c0\u624d\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u7b11\u5417\uff1f\u201d\u8fd9\u4e24\u53e5\u597d\u50cf\u4e5f\u662f\u9676\u6e0a\u660e\u7684\u8bd7\u3002\u5f26\u5916\u4e4b\u97f3\uff0c\u6e0a\u660e\u8bd7\u548c\u4e8e\u6e56\u8bcd \u4e00\u6837\uff0c\u90fd\u662f\u6709\u4e24\u9762\u7684\u3002 \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u4f3c\u4e4e\u4e0d\u5c51\u548c\u8fd9\u4e2a\u7a77\u79c0\u624d\u8ba1\u8f83\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4e24\u4f4d\u8bf4\u6545\u4e8b\uff0c\u524d\u51e0\u5929\u6709\u4e2a\u59d3 \u4fde\u7684\u5b66\u58eb\u5728\u4e00\u95f4\u9152\u9986\u7684\u58c1\u4e0a\u9898\u4e86\u4e00\u9996\u8bcd\uff0c\u6700\u540e\u4e24\u53e5\u662f\uff1a\u660e\u65e5\u91cd\u6392\u6b8b\u9152\uff0c\u6765\u5bfb\u964c\u4e0a\u82b1\u94bf\u3002\u7ed9\u5f53 \u4eca\u5723\u4e0a\u77e5\u9053\uff0c\u7b11\u9053\u2018\u7a77\u79c0\u624d\u5bd2\u9178\u6c14\u592a\u751a\u4e86\u2019\uff0c\u5fa1\u7b14\u4e00\u6539\uff0c\u6539\u4e86\u4e24\u5b57\uff0c\u643a\u5b57\u6539\u4e3a\u6276\u5b57\uff0c\u9152\u5b57\u4e3a \u9189\u5b57\uff0c\u4f60\u4eec\u5ff5\u5ff5\uff01\u201d \u3000\u3000\u4e24\u4e2a\u5b98\u513f\u58f0\u5ff5\u9053\uff1a\u201c\u660e\u65e5\u91cd\u6276\u6b8a\u9189\uff0c\u6765\u5bfb\u9633\u4e0a\u82b1\u94a2\u3002\u679c\u7136\u662f\u5929\u5b50\u6c14\u8c61\u2014\u2014\u201d \u3000\u3000\u90a3\u201c\u53f2\u5927\u4eba\u201d\u9053\uff1a\u201c\u4e0d\uff0c\u5fa1\u7b14\u6539\u8bd7\uff0c\u8fd8\u662f\u8981\u7528\u539f\u6765\u90a3\u4eba\u7684\u53e3\u543b\u7684\uff0c\u4e0d\u8fc7\u522b\u5fd8\u4e86\u90a3\u4eba\u4e5f\u662f \u4e2a\u5b98\u3002\u201d\u4e24\u4e2a\u5b98\u513f\u53c8\u540c\u58f0\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u662f\u5bcc\u8d35\u6c14\u8c61\uff0c\u4e00\u6d17\u539f\u4f5c\u7684\u5bd2\u9178\u6c14\u4e86\uff01\u201d \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u9053\uff1a\u201c\u4ece\u8fd9\u4e2a\u6545\u4e8b\uff0c\u4f60\u4eec\u4e5f\u53ef\u5f97\u77e5\u5723\u5929\u5b50\u4e5f\u662f\u613f\u610f\u89c1\u5230\u996e\u9152\u8d4b\u8bd7\u7684\u5347\u5e73\u6c14\u8c61 \u4e86\u5427\uff1f\u201d\u4e24\u4e2a\u5b98\u513f\u4f1a\u610f\uff0c\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8981\u5ff5\u5ff5\u4e0d\u5fd8\u4e8e\u5211\u5929\u821e\u5e72\u621a\uff0c\u731b\u5fd7\u56de\u5e38\u5728\uff0c\u90a3\u8fd8 \u6709\u4ec0\u4e48\u5347\u5e73\u6c14\u8c61\u53ef\u8a00\uff1f\u201d \u3000\u3000\u90a3\u7a77\u79c0\u624d\u5ffd\u7136\u53c8\u51b7\u7b11\u4e86\u3002 \u3000\u3000\u59d3\u84dd\u90a3\u5b98\u513f\u6309\u637a\u4e0d\u4f4f\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u4f60\u4e00\u518d\u51b7\u7b11\uff0c\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7a77\u79c0\u624d\u8d8a\u53d1\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u89c9\u5f97\u597d\u7b11\u5c31\u7b11\uff0c\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u59d3\u9ec4\u90a3\u5b98\u513f\u8d8b\u5949\u4e0d\u7518\u4eba\u540e\uff0c\u8ddf\u7740\u4e5f\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u6211\u53d1\u73b0\u4f60\u4e24\u6b21\u51b7\u7b11\uff0c\u90fd\u662f\u5728\u53f2\u5927\u4eba\u8bf4\u8bdd \u4e4b\u540e\u3002\u201d \u3000\u3000\u7a77\u79c0\u624d\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d\u4ed6\u4e0d\u5206\u8fa8\uff0c\u663e\u7136\u662f\u76f4\u8ba4\u4e0d\u8bb3\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u5b98\u513f\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u53f2\u5927\u4eba\u7684\u9ad8\u8bba\uff0c\u4f60\u6562\u4e0d\u670d\u6c14\u4e48\uff1f\u201d \u3000\u3000\u7a77\u79c0\u624d\u9053\uff1a\u201c\u4ed6\u6709\u4ed6\u7684\u9ad8\u8bba\uff0c\u6211\u6709\u6211\u7684\u4f4e\u8bba\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u670d\u4ed6\uff01\u201d \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u53d8\u4e86\u9762\u8272\uff0c\u90a3\u5c11\u5e74\u5374\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u6c5f\u5357\u8bcd\u98ce\u6700\u76db\uff0c\u5356\u5531\u7684\u591a\u5531\u4e00\u4e9b\uff0c\u8457\u540d\u8bcd \u4eba\u6240\u586b\u7684\u8bcd\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u53ef\u60dc\u6211\u521a\u624d\u53ea\u542c\u4e86\u534a\u9615\uff0c\u5531\u5f97\u4e5f\u4e0d\u600e\u4e48\u597d\u3002\u201d\u90a3\u6761\u753b\u8239\u5df2\u53bb\u5f97\u8fdc \u4e86\u3002\u4f46\u697c\u4e0b\u5374\u6b63\u6709\u4e00\u4e2a\u624b\u62c9\u4e09\u5f26\u7684\u8001\u8005\u548c\u4e00\u4e2a\u5c11\u5973\u7ecf\u8fc7\uff0c\u770b\u6765\u50cf\u662f\u7956\u5b59\u3002 \u3000\u3000\u201c\u53f2\u5927\u4eba\u201d\u5fd9\u9053\uff1a\u201c\u516c\u5b50\u82e5\u6709\u96c5\u5174\uff0c\u5c31\u53eb\u5979\u4e0a\u6765\u5531\u5531\u5427\u3002\u8fd9\u59d1\u5a18\u957f\u5f97\u9887\u4e3a\u79c0\u4e3d\uff0c\u60f3\u5fc5\u4e5f \u4f1a\u5531\u5f97\u4e0d\u9519\u3002\u201d\u90a3\u5c11\u5e74\u70b9\u4e86\u70b9\u5934\u3002\u201c\u597d\uff0c\u5c31\u53eb\u5979\u8fc7\u6765\u5531\u4e2a\u66f2\u5b50\u7ed9\u6211\u542c\u3002\u201d\u6a80\u7fbd\u51b2\u542c\u4e86\u4ed6\u7684\u8bf4 \u8bdd\uff0c\u66f4\u4e3a\u8be7\u5f02\uff0c\u539f\u6765\u4ed6\u8bf4\u7684\u662f\u6c5f\u5357\u6d41\u884c\u7684\u5b98\u8bdd\uff0c\u4f46\u5374\u662f\u5317\u65b9\u7684\u53e3\u97f3\uff0c\u800c\u4e14\u8fd8\u597d\u50cf\u662f\u91d1\u4eac\u4eba\u58eb \u53e3\u97f3\u3002 \u3000\u3000\u90a3\u8001\u8005\u643a\u4e86\u5b59\u5973\u8fc7\u6765\uff0c\u6253\u4e86\u4e2a\u624b\u52bf\u9006\uff1a\u201c\u516c\u5b50\u70b9\u4ec0\u4e48\u66f2\u5b50\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u968f\u4f60\u4eec\u7684\u4fbf\uff0c\u53ea\u8981\u597d\u542c\u5c31\u884c\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6211\u4eec\u7ed9\u4f60\u5f39\u5531\u4e00\u66f2\u67f3\u6c38\u7684\u671b\u6d77\u6f6e\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u516c\u5b50\u7684\u795e\u60c5\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\uff0c\u6123\u4e86\u4e00\u6123\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u67f3\u3001\u67f3\u6c38\u7684\u90a3\u9996\u65b0\u8bcd\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u966a\u7b11\u9053\uff1a\u201c\u662f\u3002\u516c\u5b50\uff0c\u4f60\u82e5\u662f\u4e0d\u5408\u610f\u7684\u8bdd\u2014\u2014\u201d\u90a3\u516c\u5b50\u4e0d\u5f85\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4fbf\u5373\u8bf4 \u9053\uff1a\u201c\u67f3\u6c38\u7684\u8bcd\uff0c\u597d\uff0c\u5f88\u597d\uff01\u5c31\u8fd9\u4e00\u9996\u5427\uff0c\u4f60\u5f39\u3002\u201d \u3000\u3000\u67f3\u6c38\u7684\u8bcd\u5f53\u65f6\u6700\u4e3a\u6d41\u884c\uff0c\u540d\u95fb\u4e2d\u5916\uff0c\u6709\u4e2a\u897f\u590f\u5b98\u5458\u51fa\u4f7f\u5b8b\u56fd\u56de\u6765\u8a00\u9053\uff1a\u201c\u51e1\u6709\u4e95\u6c34\u5904\uff0c \u7686\u80fd\u6b4c\u67f3\u8bcd\u3002\u201d\u53ef\u77e5\u4ed6\u7684\u8bcd\u6d41\u4f20\u4e4b\u5e7f\u3002\u201c\u5373\u4f7f\u4ed6\u662f\u91d1\u4eba\uff0c\u77e5\u9053\u6709\u4e2a\u67f3\u6c38\uff0c\u4e5f\u4e0d\u7a00\u5947\u3002\u201d\u6a80\u7fbd \u51b2\u6697\u81ea\u60f3\u9053\u3002 \u3000\u3000\u90a3\u8001\u8005\u629a\u8d77\u4e09\u5f26\uff0c\u5c0f\u59d1\u5a18\u4fbf\u5373\u5531\u51fa\u67f3\u6c38\u90a3\u9996\u300a\u671b\u6d77\u6f6e\u300b\uff01 \u3000\u3000\u4e1c\u5357\u5f62\u80dc\uff0c\u6c5f\u5434\u90fd\u4f1a\uff0c\u94b1\u5858\u81ea\u53e4\u7e41\u534e\u3002 \u3000\u3000\u70df\u67f3\u753b\u6865\uff0c\u98ce\u5e18\u7fe0\u5e55\uff0c\u53c2\u5dee\u5341\u4e07\u4eba\u5bb6\u3002 \u3000\u3000\u4e91\u6811\u7ed5\u5824\u6c99\u3002 \u3000\u3000\u6012\u6d9b\u5377\u971c\u96ea\uff0c\u5929\u5811\u65e0\u6daf\u3002 \u3000\u3000\u5e02\u5217\u73e0\u7391\uff0c\u6237\u76c8\u7f57\u7eee\uff0c\u7ade\u8c6a\u5962\u3002 \u3000\u3000\u91cd\u6e56\u7239\u53e0\u5dd8\u6e05\u5609\uff0c\u6709\u4e09\u79cb\u6842\u5b50\uff0c\u5341\u91cc\u8377\u82b1\u3002 \u3000\u3000\u7f8c\u7ba1\u5f04\u6674\uff0c\u83f1\u6b4c\u6cdb\u591c\uff0c\u5b09\u5b09\u9493\u53df\u83b2\u5a03\u3002 \u3000\u3000\u5343\u9a91\u62e5\u9ad8\u7259\u3002 \u3000\u3000\u4e58\u9189\u542c\u8427\u9f13\uff0c\u541f\u8d4f\u70df\u971e\u3002 \u3000\u3000\u5f02\u65e5\u56fe\u5c06\u597d\u666f\uff0c\u5f52\u53bb\u51e4\u6c60\u5938\u3002 \u3000\u3000\u90a3\u516c\u5b50\u95ed\u76ee\u8f7b\u6253\u8282\u62cd\uff0c\u5982\u6709\u6240\u601d\u3002\u5c0f\u59d1\u5a18\u5531\u5b8c\u4e86\u90a3\u9996\u8bcd\uff0c\u4ed6\u8fd8\u6ca1\u6709\u5f20\u5f00\u773c\u775b\u3002 \u3000\u3000\u8001\u8005\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u732e\u62d9\u4e86\uff0c\u4e0d\u77e5\u53ef\u4e2d\u516c\u5b50\u4e4b\u542c\uff1f\u201d \u3000\u3000\u90a3\u516c\u5b50\u5982\u68a6\u521d\u9192\uff0c\u65b9\u77e5\u9f13\u638c\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u4e09\u79cb\u6842\u5b50\uff0c\u5343\u91cc\u8377\u82b1\uff0c\u628a\u6c5f\u5357\u7684\u7f8e\u666f\u3001\u7e41 \u534e\uff0c\u90fd\u5199\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u602a\u4e0d\u5f97\uff0c\u602a\u4e0d\u5f97\u2014\u2014\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u516c\u5b50\u60f3\u4e86\u4e00\u60f3\u3002\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4eba\u4eba\u5c3d\u8bf4\u6c5f\u5357\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u4eba\u4eba\u5c3d\u8bf4\u6c5f\u5357\u597d\u201d\u662f\u97e6\u5e84\u300a\u83e9\u8428\u86ee\u300b\u8bcd\u4e2d\u7684\u4e00\u53e5\uff0c\u4ed6\u7528\u4e00\u53e5\u51fa\u540d\u7684\u8bcd\u53e5\u6765\u4f5c\u7b54\u590d\uff0c\u53ef \u77e5\u4ed6\u4e5f\u662f\u8bfb\u8fc7\u4e0d\u5c11\u8bd7\u8bcd\u7684\u3002 \u3000\u3000\u4f46\u542c\u4ed6\u8bed\u6c14\uff0c\u770b\u4ed6\u795e\u60c5\uff0c\u90a3\u8001\u8005\u548c\u6a80\u7fbd\u51b2\u90fd\u53ef\u4ee5\u731c\u5f97\u5230\uff0c\u4ed6\u539f\u6765\u60f3\u8bf4\u7684\u201c\u4e0b\u6587\u201d\u5fc5\u5b9a\u4e0d \u662f\u8fd9\u6837\u3002 \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u8fd9\u9996\u8bcd\u662f\u5929\u4e0b\u95fb\u540d\u7684\uff0c\u8bf4\u8d77\u6765\u8fd8\u6709\u4e00\u4e2a\u548c\u5b83\u6709\u5173\u7684\u6545\u4e8b\u5462\u3002\u201d \u3000\u3000\u90a3\u516c\u5b50\u9053\uff1a\u201c\u662f\u5417\uff1f\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u542c\u8bf4\u67f3\u6c38\u8fd9\u9996\u300a\u671b\u6d77\u6f6e\u300b\u4f20\u5230\u91d1\u56fd\uff0c\u91d1\u56fd\u7684\u7687\u5e1d\u8bfb\u4e86\u5927\u4e3a\u8d5e\u8d4f\uff0c\u56e0\u800c\u4e5f\u5199 \u4e86\u4e00\u9996\u8bd7\uff0c\u8868\u8fbe\u4ed6\u5bf9\u4e0d\u8d30\u7684\u5c71\u5ddd\u79c0\u7f8e\u3001\u4eba\u7269\u98ce\u6d41\u7684\u503e\u6155\u3002\u91d1\u56fd\u7684\u7687\u5e1d\u5c45\u7136\u4f1a\u5199\u6c49\u8bd7\uff0c\u4f60\u60f3\u4e0d \u5230\u5427\uff1f\u201d \u3000\u3000\u90a3\u516c\u5b50\u9053\uff1a\u201c\u8fd9\u9996\u8bd7\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u662f\u542c\u4eba\u8bf4\u7684\u3002\u5927\u6982\u8fd9\u9996\u8bd7\u5199\u5f97\u4e0d\u600e\u4e48\u9ad8\u660e\uff0c\u6240\u4ee5\u5e76\u6ca1\u4f20\u6284\u3002\u201d \u3000\u3000\u516c\u5b50\u541f\u541f\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53ef\u771f\u662f\u9053\u542c\u9014\u8bf4\u4e86\uff01\u201d \u3000\u3000\u8001\u8005\u9053\uff1a\u201c\u54e6\uff0c\u6839\u672c\u6ca1\u6709\u8fd9\u56de\u4e8b\u5417\uff1f\u201d \u3000\u3000\u516c\u5b50\u9053\uff1a\u201c\u6709\u662f\u6709\u7684\u3002\u4e0d\u8fc7\u51e0\u4e4e\u90fd\u7ed9\u4f60\u8bf4\u9519\u4e86\u3002\u7b2c\u4e00\uff0c\u91d1\u4e3b\u5199\u7684\u8fd9\u9996\u8bd7\uff0c\u662f\u56e0\u67f3\u6c38\u7684\u8bcd \u800c\u6fc0\u53d1\u8d77\u4ed6\u7684\u96c4\u5fc3\u58ee\u5fd7\u7684\uff0c\u662f\u81ea\u8ff0\u62b1\u8d1f\u4e4b\u4f5c\u3002\u8bf4\u4ed6\u60f3\u5f80\u6c5f\u5357\u7684\u79c0\u4e3d\u5c71\u5ddd\uff0c\u8fd8\u52c9\u5f3a\u53ef\u4ee5\uff0c\u4ec0\u4e48 \u4ef0\u6155\u6c5f\u5357\u7684\u4eba\u7269\u98ce\u6d41\u7b49\u7b49\uff0c\u90a3\u5c31\u7b80\u76f4\u662f\u80e1\u8bf4\u4e00\u901a\u4e86\u3002\u7b2c\u4e8c\uff0c\u4ed6\u8fd9\u9996\u8bd7\u53ef\u79f0\u7edd\u5999\u597d\u8bd7\uff0c\u674e\u767d\u675c \u752b\u6050\u6015\u90fd\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u600e\u80fd\u8bf4\u4ed6\u5199\u5f97\u4e0d\u9ad8\u660e\uff1f\u201d\u90a3\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u6211\u53ef\u4e0d\u80fd\u76f8\u4fe1\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u9996\u8bd7\u6211\u5012\u8fd8\u8bb0\u5f97\uff0c\u4f60\u4e0d\u4fe1\uff0c\u6211\u5ff5\u7ed9\u4f60\u542c\u3002\u201d\u5ff5\u9053\uff1a\u6df7\u4e00\u8f66\u4e66\u56db\u6d77\u540c\uff0c\u6c5f \u5357\u5c82\u6709\u522b\u7586\u5c01\u3002 \u3000\u3000\u63d0\u5175\u767e\u4e07\u897f\u6e56\u4e0a\uff0c\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\uff01 \u3000\u3000\u539f\u6765\u6b63\u5982\u6a80\u7fbd\u51b2\u6240\u6599\uff0c\u8fd9\u4e2a\u8d35\u516c\u5b50\u6a21\u6837\u7684\u5c11\u5e74\uff0c\u4e0d\u4f46\u662f\u91d1\u56fd\u7684\u8d35\u65cf\u3002\u4ed6\u521a\u624d\u60f3\u8bf4\u7684\u201c\u4e0b \u6587\u201d\u5176\u5b9e\u6b63\u662f\u8fd9\u4e2a\u6545\u4e8b\uff0c\u53ea\u56e0\u6015\u7ed9\u522b\u4eba\u8d77\u7591\uff0c\u6545\u800c\u6ca1\u8bf4\u51fa\u6765\u7684\u3002\u4f46\u73b0\u5728\u90a3\u8001\u8005\u5148\u63d0\u8d77\u6b64\u4e8b\uff0c \u5bf9\u91d1\u56fd\u7684\u7687\u5e1d\u53c8\u9887\u6709\u201c\u4e0d\u656c\u201d\u7684\u8bdd\u8bed\uff0c\u4ed6\u5c31\u5fcd\u4e0d\u4f4f\u8981\u8bf4\u4e86\u3002 \u3000\u3000\u4ed6\u7b49\u5f85\u90a3\u8001\u8005\u7684\u8d5e\u597d\uff0c\uff08\u4ed6\u662f\u51fa\u94b1\u70b9\u5531\u7684\u5927\u7237\uff0c\u8001\u8005\u7a0d\u4e3a\u61c2\u5f97\u4e16\u6545\u7684\u8bdd\uff0c\u4e00\u542c\u4ed6\u5ff5\u5b8c\u8fd9 \u9996\u8bd7\uff0c\u5c31\u8be5\u8d5e\u597d\u7684\u3002\uff09\u4e0d\u6599\u8001\u8005\u7adf\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u5374\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4ec0\u4e48\u8bd7\u8bcd\u6b4c\u8d4b\uff0c\u4e5f\u4e0d\u77e5\u9053\u8c01\u662f\u674e\u767d\u675c\u752b\uff0c\u4f46\u4f9d\u6211\u770b\u6765\uff0c \u8fd9\u9996\u8bd7\u53ea\u662f\u6df7\u8d26\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u8001\u8005\u559d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u522b\u4e71\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u53d8\u4e86\u9762\u8272\uff0c\u4f46\u4e00\u60f3\u81ea\u5df1\u662f\u5728\u5b8b\u56fd\uff0c\u5012\u4e5f\u4e0d\u4fbf\u53d1\u4f5c\u3002\u53ea\u80fd\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u522b\u62e6\u963b\u5979\uff0c \u6211\u5012\u60f3\u542c\u5979\u7684\u9ad8\u89c1\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u91d1\u56fd\u7684\u8d3c\u7687\u5e1d\u60f3\u6765\u897f\u6e56\u8000\u6b66\u626c\u5a01\uff0c\u53eb\u4ed6\u6765\u4e16\u4e5f\u522b\u60f3\uff0c\u4ed6\u8981\u662f\u6562\u6765\u6b3a\u4fae\u54b1 \u4eec\u5927\u5bb6\u7684\u8bdd\uff0c\u522b\u8bf4\u7acb\u9a6c\u5434\u5c71\uff0c\u672a\u8fc7\u957f\u6c5f\uff0c\u6050\u6015\u4ed6\u5df2\u662f\u8981\u846c\u8eab\u9c7c\u8179\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u54fc\u4e86\u4e00\u58f0\uff0c\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u6211\u8bf4\u5f97\u4e0d\u5bf9\u5417\uff1f\u201d\u90a3\u5c11\u5e74\u4e0d\u6562\u66b4\u9732\u8eab\u4efd\uff0c\u5f53\u7136\u4e5f\u5c31\u4e0d\u6562 \u8bf4\u8fd9\u5c0f\u59d1\u5a18\u957f\u5927\u5b8b\u7684\u5fd7\u6c14\uff0c\u706d\u91d1\u56fd\u7684\u5a01\u98ce\u4e43\u662f\u4e0d\u5bf9\u3002\u4f46\u8fd9\u53e3\u6c14\u54bd\u4e0d\u4e0b\uff0c\u4ed6\u770b\u90a3\u5c0f\u59d1\u5a18\u4e00\u773c\uff0c \u6076\u5ff5\u9661\u751f\uff0c\u659f\u4e86\u4e00\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5012\u4e5f\u77e5\u7231\u56fd\uff0c\u8d4f\u4f60\u4e00\u676f\u9152\u559d\u3002\u201d \u3000\u3000\u4ed6\u628a\u9152\u676f\u9012\u7ed9\u90a3\u5c0f\u59d1\u5a18\uff0c\u6697\u4e2d\u5df2\u662f\u8fd0\u4e0a\u5185\u529b\uff0c\u53ea\u8981\u90a3\u5c0f\u59d1\u5a18\u2014\u63a5\uff0c\u5c31\u8981\u53d7\u5185\u4f24\uff0c\u4f46\u8fd9\u5185 \u4f24\u662f\u8fc7\u540e\u65b9\u59cb\u53d1\u4f5c\u7684\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u559d\u9152\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u559d\u4e00\u676f\u4e0d\u788d\u4e8b\u7684\uff0c\u4f60\u4e0d\u559d\u5c31\u662f\u4e0d\u7ed9\u6211\u9762\u5b50\uff01\u201d \u624b\u81c2\u4e00\u632f\uff0c\u9152\u676f\u5df2\u662f\u8d34\u8fd1\u90a3\u5c0f\u59d1\u5a18\u7684\u8138\u5b54\u4e86\u3002\u770b\u6765\u90a3\u5c0f\u59d1\u5a18\u4ecd\u7136\u4e0d\u80af\u559d\u7684\u8bdd\uff0c\u4ed6\u5c31\u8981\u5f3a\u884c\u704c \u9152\u3002 \u3000\u3000\u8001\u8005\u4e00\u770b\u4e0d\u5999\uff0c\u5fd9\u9053\uff1a\u201c\u5979\u771f\u7684\u4e00\u676f\u9152\u90fd\u4e0d\u80fd\u559d\u7684\uff0c\u6211\u66ff\u5979\u559d\uff01\u201d \u3000\u3000\u201c\u5f53\u537f\u201d\u4e00\u58f0\uff0c\u9152\u676f\u6389\u843d\u5730\u4e0a\uff0c\u788e\u6210\u7247\u7247\u3002 \u3000\u3000\u90a3\u8001\u8005\u63a5\u8fde\u9000\u4e86\u4e09\u6b65\uff0c\u604d\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u62a2\u5148\u4e0a\u53bb\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u5c11\u5e74\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u60f3\u8bf7\u516c\u5b50\u522b\u518d\u96be\u4e3a\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7d20\u4e0d\u76f8\u8bc6\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8fc7\u8def\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4e5f\u592a\u7231\u591a\u7ba1\u95f2\u4e8b\u4e86\uff01\u201d\u7a81\u7136\u5c31\u5411\u6a80\u7fbd\u51b2\u53d1\u51fa\u4e00\u638c\u3002\u8fd9\u4e00\u638c\u662f\u5728\u5341\u6b65\u8ddd\u79bb \u4e4b\u5916\u53d1\u51fa\uff0c\u4f46\u8fd9\u5288\u7a7a\u638c\u529b\uff0c\u5df1\u662f\u628a\u6a80\u7fbd\u51b2\u90a3\u5f20\u684c\u5b50\u9707\u52a8\u8d77\u6765\uff0c\u9152\u676f\u548c\u996d\u7897\u78b0\u649e\u4e52\u4e53\u4e52\u4e53\u54cd\u4e2a \u4e0d\u505c\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u53ea\u5f53\u4e0d\u77e5\uff0c\u5408\u638c\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u82e5\u5acc\u6211\u591a\u4e8b\uff0c\u6211\u5728\u8fd9\u53a2\u966a\u793c\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4f4d \u5c0f\u59d1\u5a18\uff0c\u6211\u4ecd\u662f\u5e0c\u671b\u516c\u5b50\u4f60\u522b\u8981\u5c06\u5979\u96be\u4e3a\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u63d6\uff0c\u4e1d\u6beb\u4e0d\u5e26\u98ce\u58f0\uff0c\u8868\u9762\u770b\u6765\uff0c\u6bd4\u90a3\u5c11\u5e74\u7684\u5288\u7a7a\u62f3\u5dee\u5f97\u8fdc\u4e86\u3002\u4f46\u4ed6\u8fd9\u4e00 \u63d6\u7684\u5185\u529b\u5374\u662f\u6709\u5982\u6697\u6d41\u6c79\u6d8c\uff0c\u4e0d\u4f46\u628a\u5288\u638c\u529b\u62b5\u6d88\u3001\u800c\u4e14\u53cd\u9707\u56de\u53bb\uff0c\u638c\u529b\u6fc0\u52a8\uff0c\u53d1\u51fa\u66f4\u5f3a\u7684\u52b2 \u98ce\uff0c\u4e0d\u8fc7\u8fd9\u80a1\u52b2\u98ce\u662f\u53cd\u5377\u56de\u53bb\u7684\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u53cc\u638c\u5728\u80f8\u524d\u4e00\u6321\uff0c\u4f46\u4e0a\u8863\u8fd8\u662f\u7ed9\u98ce\u5439\u5f97\u98d8\u626c\uff0c\u9732\u51fa\u4e86\u4ed6\u8d34\u8eab\u7684\u80cc\u5fc3\u3002\u80cc\u5fc3\u4e0a\u7ee3\u6709 \u4e00\u6761\u91d1\u9f99\u5728\u6d77\u4e2d\u9f13\u6d6a\uff0c\u7a7a\u4e2d\u5374\u6709\u4e00\u5934\u5927\u9e4f\uff0c\u4f5c\u52bf\u6251\u5411\u8fd9\u6761\u91d1\u9f99\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5446\u4e86\u4e00\u5446\u3002\u539f\u6765\u8fd9\u201c\u5927\u9e4f\u6597\u91d1\u9f99\u201d\u7684\u56fe\u6848\uff0c\u6b63\u662f\u6a80\u5bb6\u7684\u201c\u5bb6\u5fbd\u201d\u3002\u4f46\u4e5f\u5e76\u4e0d\u662f\u6a80 \u5bb6\u7684\u6bcf\u4e2a\u4eba\u90fd\u53ef\u4ee5\u7a7f\u8fd9\u4ef6\u7ee3\u6709\u201c\u5bb6\u5fbd\u201d\u7684\u8863\u88f3\uff0c\u5fc5\u987b\u662f\u7ee7\u627f\u7235\u4f4d\u7684\u4e3b\u4eba\u624d\u53ef\u4ee5\u7a7f\u3002\u4ea6\u5373\u8bf4\uff0c \u7a7f\u8fd9\u4ef6\u8863\u88f3\u7684\u4eba\uff0c\u4e0d\u662f\u8d1d\u52d2\uff08\u4eb2\u738b\uff09\u5c31\u662f\u8d1d\u5b50\uff08\u5c0f\u738b\u7237\uff09\u3002 \u3000\u3000\u90a3\u516c\u5b50\u53ef\u4e0d\u77e5\u9053\u6a80\u7fbd\u51b2\u7684\u8eab\u4efd\uff0c\u4ed6\u89c1\u81ea\u5df1\u7684\u5185\u529f\u6bd4\u4e0d\u8fc7\u6a80\u7fbd\u51b2\uff0c\u767b\u65f6\u5c31\u8981\u62d4\u5251\u3002 \u3000\u3000\u4e0d\u6599\u4ed6\u521a\u8981\u62d4\u5251\uff0c\u6a80\u7fbd\u51b2\u5ffd\u7136\u5c31\u5230\u4e86\u4ed6\u7684\u9762\u524d\u3002 \u3000\u3000\u201c\u516c\u5b50\u4f55\u5fc5\u52a8\u6012\uff0c\u6709\u8bdd\u597d\u597d\u7684\u8bf4\u3002\u8bf7\u5750\u4e0b\u6765\u5427\u3002\u201d\u6a80\u7fbd\u51b2\u4f38\u51fa\u624b\u6765\uff0c\u5728\u4ed6\u80a9\u5934\u4e0a\u8f7b\u8f7b\u4e00 \u6309\uff0c\u8bf4\u9053\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\u6b8a\u975e\u6cdb\u6cdb\uff0c\u4ed6\u5df2\u7ecf\u770b\u89c1\u4e86\u6a80\u7fbd\u51b2\u795e\u624b\u5411\u4ed6\u6309\u4e0b\uff0c\u4ecd\u7136\u95ea\u907f\u4e0d\u5f00\uff0c\u4e0d\u89c9\u5413\u51fa \u4e00\u8eab\u51b7\u6c57\u3002\u8981\u77e5\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u5bf9\u7ec3\u6b66\u7684\u4eba\u6765\u8bf4\u6700\u5173\u7d27\u8981\uff0c\u7435\u7436\u9aa8\u5018\u88ab\u634f\u788e\uff0c\u591a\u597d\u7684\u6b66\u529f\u4e5f\u8981 \u53d8\u4f5c\u5e9f\u4eba\u3002\u6a80\u7fbd\u51b2\u6240\u6309\u7684\u90e8\u4f4d\uff0c\u6b63\u662f\u4ed6\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u503c\u7fbd\u51b2\u4e1d\u6beb\u4e5f\u6ca1\u7528\u529b\uff0c\u90a3\u5c11\u5e74\u4e00\u5750\u4e0b\u6765\uff0c\u4ed6\u7684\u624b\u4e5f\u677e\u5f00\u4e86\u3002 \u3000\u3000\u201c\u5947\u602a\u201d\uff0c\u8fd9\u5c0f\u5b50\u600e\u4e48\u5bf9\u6211\u624b\u4e0b\u7559\u60c5\uff1f\u6599\u60f3\u4ed6\u4e0d\u4f1a\u77e5\u9053\u4e86\u6211\u7684\u8eab\u4efd\u5427\uff1f\u6664\uff0c\u5bf9\u4e86\uff0c\u4ed6\u867d \u7136\u4e0d\u77e5\u9053\u6211\u771f\u6b63\u7684\u8eab\u4efd\uff0c\u5374\u4e00\u4e0b\u4ee5\u4e3a\u6211\u662f\u4e34\u5b89\u7684\u8d35\u4eba\uff0c\u6240\u4ee5\u4e0d\u6562\u505a\u5f97\u592a\u7edd\u3002\u201d \u3000\u3000\u4ed6\u90a3\u77e5\u9053\uff0c\u6a80\u7fbd\u51b2\u4e0d\u662f\u4e0d\u6562\uff0c\u800c\u6b63\u662f\u56e0\u4e3a\u77e5\u9053\u4e86\u4ed6\u7684\u8eab\u4efd\uff0c\u65b9\u59cb\u624b\u4e0b\u7559\u60c5\u7684\u3002\u5018\u82e5\u6a80\u7fbd \u51b2\u4e0d\u662f\u521a\u521a\u770b\u89c1\u4e86\u4ed6\u7684\u5bb6\u65cf\u5fbd\u8bb0\uff0c\u65e9\u5df2\u628a\u4ed6\u7684\u7435\u7436\u9aa8\u634f\u788e\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u5949\u529d\u516c\u5b50\u4e24\u53e5\uff0c\u542c\u4e0d\u542c\u968f\u4f60\u3002\u5230\u4e86\u4eba\u5bb6\u7684\u5730\u65b9\uff0c\u5c31\u8be5\u5c0a\u91cd\u4eba \u5bb6\uff0c\u5207\u83ab\u505a\u60f9\u4eba\u8ba8\u538c\u7684\u6076\u5ba2\u3002\u201d \u3000\u3000\u90a3\u516c\u5b50\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u542c\u8fd9\u53e3\u6c14\uff0c\u96be\u9053\u4ed6\u7adf\u5df2\u77e5\u9053\u6211\u7684\u8eab\u4efd\uff1f\u201d\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9 \u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u6211\u4ee5\u4e3a\u5df2\u7ecf\u8bf4\u5f97\u591f\u660e\u767d\u4e86\uff0c\u4f60\u8fd8\u4e0d\u61c2\u5417\uff1f\u201d \u3000\u3000\u7a81\u7136\u6539\u7528\u91d1\u4eac\u7684\u53e3\u97f3\u8bf4\u9053\uff1a\u201c\u5b8c\u989c\u4eae\u60f3\u8981\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\u662f\u505a\u4e0d\u5230\u7684\uff0c\u6211\u5e0c\u671b\u4f60\u53ea\u662f\u4ee5 \u666e\u901a\u6e38\u5ba2\u7684\u8eab\u4efd\u6765\u6c5f\u5357\uff0c\u4f60\u61c2\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u53f2\u5927\u5165\u201d\u62cd\u6848\u559d\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff0c\u628a\u4ed6\u62ff\u4e0b\u3002\u201d\u4e0e\u4ed6\u76f8\u90bb\u7684\u4e24\u5f20\u684c\u5b50\uff0c\u5750\u7684\u90fd\u662f\u519b \u5b98\u3002 \u3000\u3000\u4e00\u4e2a\u519b\u5b98\u5954\u5411\u6a80\u7fbd\u51b2\uff0c\u7ed9\u690d\u7fbd\u51b2\u6325\u8896\u4e00\u62c2\uff0c\u6251\u901a\u8dcc\u5012\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u519b\u5b98\u89c1\u8bc6\u8f83\u9ad8\uff0c\u65e9\u5c31\u770b\u51fa\u6a80\u7fbd\u51b2\u6b66\u529f\u4e0d\u51e1\uff0c\u559d\u9053\uff1a\u201c\u90a3\u9178\u79c0\u624d\u4e5f\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u4e00 \u5e76\u62ff\u4e0b\uff01\u201d\u4ed6\u4e00\u6765\u60f3\u8ba8\u597d\u90a3\u88ab\u9178\u79c0\u624d\u5f97\u7f6a\u8fc7\u7684\u201c\u53f2\u5927\u4eba\u201d\uff0c\u4e8c\u6765\u53c8\u6015\u8fd9\u9178\u79c0\u624d\u4e5f\u4f1a\u6b66\u529f\uff0c\u51b2 \u4e0a\u524d\u53bb\uff0c\u7acb\u5373\u91cd\u62f3\u51fb\u51fa\uff0c\u60f3\u628a\u4ed6\u4e00\u62f3\u51fb\u6655\uff0c\u7136\u540e\u6293\u4ed6\u3002\u4ed6\u7ec3\u7684\u662f\u731b\u864e\u62f3\uff0c\u8fd9\u62f3\u8db3\u6709\u4e09\u767e\u65a4\u6c14 \u529b\u3002\u6a80\u7fbd\u51b2\u60f3\u6551\u4e5f\u6765\u4e0d\u53ca\uff0c\u6697\u53eb\u201c\u800d\u8bc8\u201d\u3002\u4e0d\u6599\u53ea\u542c\u5f97\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff1b\u4e00\u4e2a\u4eba\u4ef0\u516b\u53c9\u7684\u5012\u5728 \u5730\u4e0a\uff0c\u4f46\u5374\u4e0d\u662f\u90a3\u4e2a\u79c0\u624d\uff0c\u53cd\u800c\u662f\u6253\u4ed6\u7684\u90a3\u4e2a\u519b\u5b98\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8fd9\u624d\u77e5\u9053\uff0c\u8fd9\u4e2a\u8c8c\u4e0d\u60ca\u4eba\u7684\u7a77\u79c0\u624d\uff0c\u7adf\u662f\u4e2a\u6b66\u6797\u9ad8\u624b\u3002\u4ed6\u5fc3\u91cc\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed \u6127\u201d\uff0c\u8fd9\u201c\u79c0\u624d\u7684\u6cbe\u519c\u5341\u516b\u8dcc\u529f\u592b\uff0c\u7eb5\u7136\u4e0d\u5728\u6211\u4e4b\u4e0a\uff0c\u4e5f\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53e6\u5916\u8fd8\u6709\u51e0\u4e2a\u519b\u5b98\uff0c\u672c\u662f\u60f3\u6765\u52a9\u9635\u7684\uff0c\u4e00\u89c1\u8fd9\u79c0\u624d\u5982\u6b64\u5389\u5bb3\uff0c\u5413\u5f97\u6025\u5fd9\u62d4\u51fa\u8170\u5200\uff0c\u56f4\u7740 \u201c\u53f2\u5927\u4eba\u201d\u5750\u7684\u90a3\u5f20\u684c\u5b50\uff0c\u4f46\u5374\u4e0d\u6562\u4e0a\u524d\u60f9\u90a3\u79c0\u624d\u4e86\u3002\u8fd9\u4e00\u4e0b\u9152\u697c\u4e0a\u66f4\u4e71\u4e86\u3002\u90a3\u79c0\u624d\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u6015\u4ec0\u4e48\uff1b\u6211\u53c8\u4e0d\u4f1a\u6253\u4eba\uff0c\u52a8\u624b\u6253\u4eba\u7684\u662f\u4f60\u4eec\u8fd9\u4e9b\u5927\u5c0f\u5b98\u513f\u3002\u597d\uff0c\u7b97\u6211\u6015\u4e86\u4f60\u4eec\uff0c \u6211\u4eec\u8d70\uff01\u201d\u628a\u94f6\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u5728\u5927\u7b11\u58f0\u4e2d\u626c\u957f\u800c\u53bb\u3002\u6a80\u7fbd\u51b2\u8ddf\u7740\u7ed3\u8d26\u4e5f\u8d70\u3002\u90a3\u79c0\u624d\u597d\u50cf\u4e0d\u77e5 \u6a80\u7fbd\u51b2\u8ddf\u5728\u4ed6\u540e\u9762\u4f3c\u7684\uff0c\u697c\u5916\u697c\u5728\u5b64\u5c71\u811a\u4e0b\uff0c\u4ed6\u51fa\u4e86\u697c\u5916\u697c\uff0c\u4fbf\u8d70\u4e0a\u5b64\u5c71\u3002\u6a80\u7fbd\u51b2\u4e5f\u4e0d\u6562\u5728 \u4eba\u591a\u7684\u5730\u65b9\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4e0d\u5373\u4e0d\u79bb\u7684\u8ddf\u4ed6\u8d70\u4e0a\u5b64\u5c71\u3002\u8d70\u5230\u4e00\u4e2a\u6ca1\u4eba\u7684\u5730\u65b9\uff1b\u4e24\u4eba\u4e0d\u7ecf\u800c\u540c\u7684\u505c \u4e0b\u811a\u6b65\u3002\u90a3\u79c0\u624d\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u6211\u505a\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u6211\u8fd8\u6b20\u4f60\u4e00\u58f0\u591a\u8c22\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u9002\u624d\u665a\u8f88\u4e0d\u81ea\u91cf\u529b\uff0c\u6559\u524d\u8f88\u89c1\u7b11\u4e86\u3002\u6562\u8bf7\u95ee\u524d\u8f88\u9ad8\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u90a3\u79c0\u624d\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u50cf\u60f3\u6765\u548c\u6211\u7ed3\u4ea4\u7684\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u53ef\u8bf4\u5f97\u6709\u70b9\u4e0d\u5927\u5ba2\u6c14\uff0c\u800c\u4e14\u4ed6\u8138\u4e0a\u7684\u795e\u8272\uff0c\u4e5f\u663e\u5f97\u6709\u51e0\u5206\u51b7\u610f\u5632\u7b11\u7684\u610f\u5473\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u6ee1\u8154\u70ed\u60c5\u597d\u50cf\u7ed9\u6cfc\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u5fc3\u91cc\u4e0d\u7981\u4e5f\u662f\u6709\u70b9\u4e0d\u5927\u8212\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u7ed3\u4ea4\u4e8c \u5b57\uff0c\u665a\u8f88\u81ea\u77e5\u9ad8\u6500\u4e0d\u8d77\uff0c\u53ea\u76fc\u524d\u8f88\u6307\u6559\u3002\u201d \u3000\u3000\u90a3\u79c0\u624d\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53ea\u5c31\u521a\u624d\u5728\u9152\u697c\u4e0a\u7684\u4e00\u4e8b\u800c\u8bba\uff0c\u665a\u8f88\u5df2\u77e5\u524d\u8f88\u4e43\u662f\u6177\u6168\u60b2\u4ea2\u7684\u8c6a\u4fa0\u4e4b \u58eb\uff01\u201d \u3000\u3000\u90a3\u79c0\u624d\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u4e71\u6234\u9ad8\u5e3d\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u3002\u6240\u4ee5\u665a\u8f88\u624d\u8981\u6765\u2014\u2014\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\uff0c\u6b63\u56e0\u4e0d\u77e5\uff0c\u6240\u4ee5\u624d\u5411\u4f60 \u8bf7\u6559\u7684\u3002\u90a3\u77e5\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u7a77\u79c0\u624d\u5df2\u662f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6839\u672c\u4e0d\u77e5\u9053\u6211\u662f\u8c01\u5c31\u8981\u548c\u6211\u7ed3\u4ea4\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u70ed\u5fc3\u51b0\u51b7\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u524d\u8f88\u82e5\u662f\u4e0d\u5c51\u6298\u8282\u4e0b\u4ea4\uff0c\u665a\u8f88\u544a\u8f9e\uff01\u201d \u3000\u3000\u79c0\u624d\u9661\u5730\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6b62\u6b65\u9053\uff1a\u201c\u524d\u8f88\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u90a3\u79c0\u624d\u9053\uff1a\u201c\u4f60\u95ee\u4e86\u6211\uff0c\u6211\u8fd8\u6ca1\u6709\u95ee\u4f60\u5462\uff0c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u8eab\u4efd\u672c\u6765\u5c31\u662f\u4e0d\u4fbf\u548c\u4eba\u8bf4\u7684\uff0c\u4f55\u51b5\u8fd9\u79c0\u624d\u5bf9\u4ed6\u7684\u6001\u5ea6\u53c8\u662f\u5982\u6b64\u51b7\uff0c\u4fbf\u4e0d\u613f\u610f\u548c \u4ed6\u5b9e\u8bf4\u4e86\u3002 \u3000\u3000\u4fbf\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e2a\u6765\u6e38\u897f\u6e56\u7684\u8fc7\u8def\u5ba2\u3002\u201d \u3000\u3000\u90a3\u79c0\u624d\u9053\uff1a\u201c\u6211\u4e0d\u662f\u95ee\u4f60\u8fd9\u4e2a\uff0c\u6211\u662f\u95ee\u4f60\u7684\u59d3\u540d\u3001\u6765\u5386\uff01\u201d\u90a3\u53e3\u6c14\u66f4\u50cf\u5ba1\u95ee\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u867d\u7136\u201c\u76f8\u4fe1\u201d\u4ed6\u662f\u4fa0\u4e49\u4e2d\u4eba\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e00\u89c1\u9762\u5c31\u503e\u5410\u5e73\u751f\u7684\uff0c\u4f55\u51b5\u53c8\u662f\u5728\u8fd9\u6837\u4e00 \u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4fbf\u4e0d\u80fd\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u524d\u8f88\u65e2\u662f\u4e0d\u613f\u4e0b\u4ea4\uff0c\u90a3\u53c8\u4f55\u5fc5\u591a\u95ee\uff1f\u597d\uff0c\u7b97\u662f\u6211\u6765\u5f97\u5192\u6627\uff0c\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u90a3\u79c0\u624d\u51b7\u7b11\u9053\uff1a\u201c\u7ed9\u6211\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u690d\u7fbd\u51b2\u9053\uff1a\u201c\u524d\u8f88\u4e0d\u5c51\u4e0e\u6211\u7ed3\u4ea4\uff0c\u53c8\u4e0d\u653e\u6211\u8d70\uff0c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u90a3\u79c0\u624d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u51ed\u7740\u4f60\u521a\u624d\u5728\u697c\u5916\u7684\u4e00\u756a\u505a\u4f5c\uff0c\u5c31\u53ef\u4ee5\u9a97\u6211\u4e0a\u5f53\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u6115\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u90a3\u79c0\u624d\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff0c\u6211\u53ef\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4f60\u662f\u91d1\u56fd\u6d3e\u6765\u7684\u5978\u7ec6\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u524d\u8f88\uff0c\u4f60\u8bef\u4f1a\u4e86\u2014\u2014\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u79c0\u624d\u5df2\u7ecf\u51fa\u624b\uff0c\u4e00\u51fa\u624b\u5c31\u6293\u4ed6\u7684\u745f\u745f\u9aa8\uff0c\u6a80\u7fbd\u51b2\u54ea\u91cc\u8fd8\u80fd\u89e3\u91ca\uff0c\u53ea\u597d\u63a5 \u62db\u3002 \u3000\u3000\u90a3\u79c0\u624d\u75be\u653b\uff0c\u5728\u7b2c\u4e03\u62db\u6a80\u7fbd\u51b2\u95ea\u8eb2\u907f\u4e0d\u5f00\uff0c\u5316\u89e3\u4e5f\u96be\u5316\u89e3\uff0c\u53ea\u597d\u786c\u63a5\u3002\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c \u53cc\u638c\u76f8\u4ea4\uff0c\u79c0\u624d\u6643\u4e86\u4e24\u6643\uff0c\u6a80\u7fbd\u51b2\u9000\u540e\u4e09\u6b65\uff0c\u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u8981\u8bf4\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u79c0\u624d\u88ab\u4ed6\u7684\u638c\u529b\u6240\u9707\uff0c\u51e0\u4e4e\u7ad9\u7acb\u4e0d\u7a33\uff0c\u4e5f\u662f\u5403\u60ca\u4e0d\u5c0f\u3002\u970d\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u628a\u85cf\u5728\u8863\u886b\u5185 \u7684\u90a3\u652f\u5224\u5b98\u7b14\u62ff\u4e86\u51fa\u6765\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u6211\u5012\u8981\u770b\u4f60\u80fd\u591f\u63a5\u6211\u51e0\u62db\uff01\u201d \u3000\u3000\u4ed6\u7684\u94c1\u7b14\u70b9\u7a74\u53e6\u6709\u4e00\u529f\uff0c\u597d\u50cf\u5199\u5b57\u4e00\u6837\uff0c\u6700\u5148\u5199\u7684\u662f\u201c\u8349\u4e66\u201d\uff0c\u7b14\u8d70\u9f99\u86c7\uff0c\u6765\u5f97\u6709\u5982\u72c2 \u98ce\u66b4\u96e8\uff0c\u6a80\u7fbd\u51b2\u8fde\u63a5\u9669\u62db\uff0c\u6697\u6697\u540e\u6094\uff0c\u6ca1\u6709\u62ff\u51fa\u6696\u7389\u7bab\uff0c\u90a3\u79c0\u624d\u731b\u5730\u559d\u58f0\u201c\u7740\u201d\u4ed6\u5df2\u7ecf\u4f7f\u51fa \u4e86\u201c\u72c2\u8349\u201d\u7684\u6700\u540e\u4e00\u7b14\uff0c\u7b14\u5c16\u6233\u5411\u6a80\u7fbd\u51b2\u80f8\u819b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8feb\u4e8e\u65e0\u5948\uff0c\u53ea\u597d\u4f7f\u51fa\u5e08\u95e8\u7edd\u6280\u2014\u2014\u5f39\u6307\u795e\u901a\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u628a\u4ed6\u7684\u5224\u5b98\u7b14\u5f39\u5f00\uff0c\u4e0d \u7531\u81ea\u5df1\u7684\u53c8\u518d\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u79c0\u624d\u4f7f\u4e86\u4e00\u5957\u201c\u72c2\u8349\u201d\u7b14\u6cd5\uff0c\u603b\u7b97\u5df2\u4e0d\u4e0b\u767e\u62db\uff0c\u4ecd\u7136\u672a\u80fd\u70b9\u7740\u6a80\u7fbd\u51b2\u7684\u7a74\u9053\uff0c\u89c1\u6a80\u7fbd\u51b2 \u5634\u89d2\u6302\u7740\u51b7\u7b11\uff0c\u4ed6\u4e0d\u7981\u4e5f\u662f\u8138\u4e0a\u53d1\u70ed\u4e86\u3002\u672c\u6765\u6b64\u65f6\u4ed6\u82e5\u662f\u7acb\u5373\u8ffd\u51fb\uff0c\u6a80\u7fbd\u51b2\u6700\u591a\u53ea\u80fd\u62b5\u6321\u4e09 \u62db\uff0c\u4f46\u4ed6\u662f\u6c5f\u5357\u6b66\u6797\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u5374\u53c8\u600e\u597d\u610f\u601d\u5728\u5bf9\u65b9\u53ea\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u63a5\u4e86\u4ed6\u4e00\u767e\u62db \u4e4b\u540e\uff0c\u7eed\u65bd\u6740\u624b\uff0c\u4f55\u51b5\u5bf9\u65b9\u53ea\u662f\u4e2a\u4e8c\u5341\u5c81\u90fd\u6050\u6015\u672b\u5230\u7684\u5c11\u5e74\u3002 \u3000\u3000\u4ed6\u505c\u4e0b\u811a\u6b65\uff0c\u559d\u9053\uff1a\u201c\u73b0\u5728\u6211\u6740\u4e86\u4f60\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u5fc3\u670d\uff0c\u4eae\u51fa\u4f60\u7684\u5175\u5203\u5427\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6709\u5e08\u95e8\u79d8\u4f20\u7684\u4e0a\u4e58\u5185\u529f\u5fc3\u6cd5\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u6c14\u8840\u5df2\u662f\u7545\u901a\uff0c\u672c\u6765\u4ed6\u6b64\u65f6\u662f\u53ef\u4ee5\u5f00\u53e3 \u8bf4\u8bdd\u4e86\uff0c\u4f46\u4ed6\u4e5f\u662f\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u7684\u4eba\uff0c\u51e0\u4e4e\u88ab\u90a3\u79c0\u624d\u70b9\u7740\u7a74\u9053\uff0c\u4ea6\u6709\u70b9\u607c\u6012\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u82e5 \u4e0d\u8fd8\u4f60\u4e00\u70b9\u989c\u8272\uff0c\u5012\u6559\u4f60\u5c0f\u89d1\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u5c31\u7528\u8fd9\u7ba1\u7389\u8427\u8bf7\u524d\u8f88\u518d\u6307\u6559\u51e0\u62db\uff0c\u51e0\u5341\u62db\uff0c\u6216\u51e0\u767e\u62db\uff01\u201d\u7389\u7bab\u4e00\u4e2a\u201c\u6a2a\u626b\u516d \u5408\u201d\uff0c\u628a\u79c0\u624d\u7684\u94c1\u7b14\u8361\u5f00\u3002 \u3000\u3000\u79c0\u624d\u542c\u4ed6\u8bf4\u8bdd\u9887\u6709\u5632\u8bbd\u610f\u5473\uff0c\u5fc3\u4e2d\u4e5f\u662f\u607c\u6012\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u6709\u70b9\u5403\u60ca\u4e86\uff0c\u4ed6\u8fd9\u652f\u7389\u7bab\u597d\u50cf \u4f20\u8bf4\u7684\u4e00\u4ef6\u5f02\u5b9d\uff0c\u96be\u9053\u4ed6\u5c31\u662f\u90a3\u4e2a\u5f02\u4eba\u7684\u5f1f\u5b50\uff01\u8fd9\u79c0\u624d\u548c\u6a80\u7fbd\u51b2\u7684\u5e08\u7236\u8036\u5f8b\u7384\u5143\u5e76\u4e0d\u76f8\u8bc6\uff0c \u4e0d\u8fc7\u5374\u4e5f\u662f\u5f7c\u6b64\u95fb\u540d\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6709\u7389\u7bab\u5728\u624b\uff0c\u5f62\u52bf\u5927\u53d8\uff0c\u4e0d\u4f46\u6273\u6210\u5e73\u624b\uff0c\u800c\u4e14\u6e10\u6e10\u5360\u4e86\u4e00\u70b9\u4e0a\u98ce\u4e86\u3002\u4f46\u90a3\u7a77\u79c0\u624d \u7684\u7b14\u6cd5\u4e5f\u662f\u8ddf\u7740\u518d\u53d8\u3002\u4ece\u201c\u72c2\u8349\u201d\u53d8\u4e3a\u201c\u6977\u4e66\u201d\uff0c\u4e00\u70b9\u4e00\u753b\u3001\u4e00\u6487\u4e00\u637a\uff0c\u6beb\u4e0d\u82df\u4e14\uff0c\u90a3\u662f\u5de5 \u7b14\u6977\u4e66\u7684\u7b14\u6cd5\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6253\u8d77\u7cbe\u795e\u5e94\u4ed8\uff0c\u7389\u7bab\u5ffd\u800c\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\u3002\u4ed6\u7684\u70b9\u7a74\u624b\u6cd5\u548c\u5b8c\u989c\u5bb6\u7684\u60ca\u795e\u7b14\u6cd5\u5927\u540c \u5c0f\u5f02\uff0c\u867d\u7136\u706b\u5019\u672a\u591f\uff0c\u8fdc\u4e0d\u53ca\u5b8c\u989c\u957f\u4e4b\u795e\u5999\uff0c\u4f46\u4ea6\u5df2\u8db3\u4ee5\u4ee4\u5f97\u90a3\u79c0\u624d\u5927\u4e3a\u60ca\u5f02\u3002\u539f\u6765\u8fd9\u79c0\u624d \u4ecd\u662f\u6c5f\u5357\u7b2c\u4e00\u70b9\u7a74\u540d\u5bb6\uff0c\u6781\u4e3a\u81ea\u8d1f\uff0c\u4eba\u5bb6\u8bf4\u4ed6\u662f\u6c5f\u5357\u7b2c\u4e00\uff0c\u4ed6\u8fd8\u662f\u4e0d\u6ee1\u8db3\u7684\uff0c\u6b64\u65f6\u89c1\u4e86\u6a80\u7fbd \u51b2\u7684\u7b14\u6cd5\uff0c\u8fd9\u624d\u77e5\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u7b14\u6cd5\u4f3c\u4e4e\u8fd8\u672a\u7ec3\u5230\u6d41\u8f6c\u81ea\u5982 \u7684\u8d85\u51e1\u8131\u4fd7\u5883\u754c\uff0c\u7b14\u610f\u7a0d\u5acc\u6da9\u6ede\uff0c\u770b\u6765\u4ed6\u4e0d\u662f\u4e13\u653b\u70b9\u7a74\u8fd9\u4e00\u95e8\u7684\u529f\u592b\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ee5\u4ed6\u7b14 \u6cd5\u7684\u672c\u8eab\u800c\u8bba\uff0c\u5374\u53ea\u6709\u5728\u6211\u4e4b\u4e0a\uff0c\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\u4e86\u3002\u201d\u4ed6\u89c1\u201c\u5de5\u7b14\u6977\u4e66\u201d\u4e0d\u80fd\u53d6\u80dc\uff0c\u53c8\u518d\u53d8 \u4e3a\u523b\u201c\u77f3\u9f13\u6587\u201d\u7684\u7b14\u6cd5\uff0c\u6977\u4e66\u662f\u7528\u4e09\u4e2a\u6307\u5934\u62ff\u7b14\u7684\uff0c\u523b\u77f3\u9f13\u6587\u5219\u662f\u4e94\u6307\u9f50\u4f38\uff0c\u7528\u624b\u6765\u201c\u63e1\u201d \u7b14\u4e86\u3002\u8fd9\u5957\u7b14\u6cd5\u4f7f\u5f00\uff0c\u5f53\u771f\u5c31\u50cf\u77f3\u5320\u523b\u5b57\u4e00\u6837\uff0c\u70b9\u3001\u6487\u3001\u637a\u3001\u7ad6\uff0c\u90fd\u662f\u51ff\u4e0b\u53bb\u7684\u3002\u6c89\u91cd\u6709 \u529b\uff0c\u767b\u65f6\u538b\u5f97\u6a80\u7fbd\u51b2\u597d\u50cf\u80cc\u4e0a\u4e86\u5343\u65a4\u91cd\u62c5\uff01 \u3000\u3000\u5e78\u597d\u6a80\u7fbd\u51b2\u7684\u6696\u7389\u7bab\u662f\u4ef6\u5b9d\u7269\uff0c\u8fd8\u80fd\u52c9\u5f3a\u62db\u67b6\u3002\u4f46\u8fd9\u4e48\u4e00\u6765\uff0c\u5df2\u7ecf\u662f\u53d8\u6210\u4e86\u5185\u529b\u7684\u8f83\u91cf \u4e86\uff0c\u5728\u8fd9\u65b9\u9762\uff0c\u6a80\u7fbd\u51b2\u5374\u662f\u7a0d\u900a\u4e00\u7b79\u7684\u3002 \u3000\u3000\u79c0\u624d\u521a\u624d\u90a3\u5957\u201c\u72c2\u8349\u201d\u5feb\u5230\u6781\u70b9\uff0c\u6b64\u9645\u8fd9\u4e00\u5957\u77f3\u9f13\u6587\u7684\u7b14\u6cd5\u5219\u521a\u597d\u76f8\u53cd\uff0c\u6162\u5230\u6781\u70b9\u3002\u6a80 \u7fbd\u51b2\u989d\u5934\u89c1\u6c57\uff0c\u8d8a\u6765\u8d8a\u89c9\u5403\u529b\uff0c\u53ea\u597d\u62ff\u51fa\u6700\u540e\u4e00\u95e8\u7edd\u6280\uff0c\u6697\u8fd0\u7384\u529f\uff0c\u8d81\u4ed6\u7b14\u6cd5\u6162\u541e\u541e\u7684\u5c06\u51ff \u800c\u672a\u51ff\u4e0b\u4e4b\u65f6\uff0c\u7389\u7bab\u51d1\u5230\u5507\u8fb9\uff0c\u545c\u7684\u4e00\u53e3\u7f61\u6c14\u5439\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u79c0\u624d\u521d\u65f6\u4ee5\u4e3a\u4ed6\u653e\u6697\u5668\uff0c\u8981\u77e5\u7389\u7bab\u4e2d\u7a7a\uff0c\u5982\u679c\u7528\u6885\u82b1\u9488\u4e4b\u7c7b\u7684\u6697\u5668\uff0c\u662f\u53ef\u4ee5\u4ece\u7bab\u7ba1\u91cc\u5439 \u51fa\u6765\u7684\u3001\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u9a82\u9053\uff1a\u201c\u4e0b\u4e09\u6ee5\u2014\u2014\u201d\u9a82\u58f0\u521a\u51fa\uff0c\u53ea\u8bf4\u5f97\u4e09\u4e2a\u5b57\uff0c\u9661\u7136\u53ea\u89c9\u8109\u95e8\u4e00 \u9707\uff0c\u6a80\u7fbd\u51b2\u7684\u7389\u7bab\u6a2a\u626b\u8fc7\u6765\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u628a\u4ed6\u7684\u94c1\u7b14\u8361\u5f00\uff0c\u8981\u4e0d\u662f\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u94c1\u7b14\u90fd\u51e0\u4e4e \u638c\u63e1\u4e0d\u7262\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4e0d\u63a5\u8fde\u9000\u4e86\u56db\u6b65\uff0c\u6bd4\u521a\u624d\u6a80\u7fbd\u51b2\u63a5\u4e0d\u7740\u4ed6\u7684\u201c\u72c2\u8349\u201d\u4e4b\u65f6\uff0c \u8fd8\u591a\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u8fd9\u79c0\u624d\u89c1\u591a\u8bc6\u5e7f\uff0c\u6b64\u65f6\u5f53\u7136\u77e5\u9053\u6a80\u7fbd\u51b2\u662f\u5229\u7528\u6696\u7389\u7bab\u8fd9\u4ef6\u6b66\u6797\u5f02\u5b9d\u5439\u51fa\u6765\u7684\u7f61\u6c14\u4e86\uff0c\u4ed6 \u6b63\u8981\u53d8\u6362\u7b14\u6cd5\uff0c\u4e0a\u524d\u62a2\u73ab\u3002\u5ffd\u542c\u5f97\u4e09\u5f26\u62e8\u52a8\u7684\u58f0\u97f3\u81ea\u8fdc\u800c\u8fd1\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u521a\u624d\u5728\u9152\u697c\u62c9\u4e09\u5f26 \u90a3\u4e2a\u8001\u8005\u5df2\u662f\u548c\u4ed6\u7684\u5b59\u5973\u6765\u5230\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u94c1\u7b14\u4e66\u751f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u7b14\u8d70\u9f99\u86c7\uff0c\u4ee4\u6211\u5927\u5f00 \u773c\u754c\uff0c\u4f46\u4f60\u5374\u8bef\u4f1a\u597d\u4eba\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u60ca\u9053\uff1a\u201c\u524d\u8f88\u6562\u60c5\u662f\u6587\u5927\u4fa0\uff1f\u201d\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u5018\u82e5\u6211\u65e9\u77e5\u9053\u4ed6\u662f\u94c1\u7b14\u4e66\u751f\u6587\u9038 \u51e1\uff0c\u53ea\u6015\u5728\u767e\u62db\u4e4b\u5185\uff0c\u6211\u5df2\u662f\u975e\u5f97\u843d\u8d25\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u539f\u6765\u5728\u6a80\u7fbd\u51b2\u827a\u6210\u51fa\u5e08\u4e4b\u65e5\uff0c\u4ed6\u7684\u5e08\u7236\u66fe\u4e0e\u4ed6\u8c08\u53ca\u6c5f\u5357\u7684\u6b66\u6797\u4eba\u7269\uff0c\u51c6\u5907\u4ed6\u6709\u4e00\u5929\u524d\u5f80 \u6c5f\u5357\uff0c\u4e0d\u81f3\u4e8e\u5168\u65e0\u6240\u77e5\uff0c\u8c08\u53ca\u6c5f\u5357\u7684\u6b66\u6797\u4eba\u7269\uff0c\u5f53\u7136\u662f\u5c11\u4e0d\u514d\u8981\u63d0\u53ca\u6c5f\u5357\u7684\u7b2c\u4e00\u70b9\u7a74\u540d\u5bb6\u2014 \u2014\u94c1\u7b14\u4e66\u751f\u6587\u9038\u51e1\u4e86\u3002 \u3000\u3000\u6587\u9038\u51e1\u6ca1\u6709\u7406\u776c\u6a80\u7fbd\u51b2\uff0c\u8ff3\u81ea\u95ee\u90a3\u8001\u8005\uff1a\u201c\u949f\u8001\u4e09\uff0c\u4f60\u77e5\u9053\u4ed6\u7684\u59d3\u540d\u6765\u5386\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u4e0d\u77e5\uff01\u201d \u3000\u3000\u6587\u9038\u51e1\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4f60\u600e\u77e5\u4ed6\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u662f\u90a3\u5c0f\u59d1\u5a18\u62a2\u7740\u8bf4\uff1a\u201c\u4f84\u5973\u8bfb\u4e66\u5f88\u5c11\uff0c\u4f46\u8bb0\u5f97\u4e0d\u77e5\u54ea\u4e2a\u53e4\u4eba\uff0c\u597d\u50cf\u8bf4\u8fc7\u8fd9\u4e48\u4e00\u53e5 \u8bdd\uff1a\u767d\u5934\u5982\u65b0\uff0c\u503e\u76d6\u5982\u6545\u3002\u4e0d\u77e5\u8be5\u5f53\u5982\u4f55\u89e3\u91ca\uff0c\u8bf7\u6587\u53d4\u53d4\u6307\u6559\u3002\u201d \u3000\u3000\u201c\u767d\u5934\u5982\u65b0\u201d\u7684\u610f\u601d\uff0c\u662f\u6307\u6709\u4eba\u76f8\u4ea4\u4e00\u8f88\u5b50\uff0c\u5230\u4e86\u5934\u53d1\u767d\u7684\u65f6\u5019\uff0c\u5f7c\u6b64\u8fd8\u662f\u4e0d\u4e86\u89e3\u5bf9 \u65b9\uff0c\u597d\u50cf\u65b0\u670b\u53cb\u4e00\u6837\u3002\u4f46\u6709\u7684\u4eba\u4e58\u8f66\u5728\u8def\u4e0a\u76f8\u9022\uff0c\u505c\u8f66\u4ea4\u8c08\u4e00\u4f1a\uff0c\u5c31\u597d\u50cf\u8001\u670b\u53cb\u4e00\u6837\u3002\u201c\u503e \u76d6\u201d\u8bf4\u7684\u5373\u662f\u505c\u8f66\u4e4b\u65f6\uff0c\u8f66\u76d6\u503e\u659c\u3002\u8fd9\u53e5\u8bdd\u662f\u51fa\u4e8e\u90b9\u9633\uff08\u6218\u56fd\u65f6\u4ee3\u4eba\uff09\u7684\u300a\u72f1\u4e2d\u4e0a\u6881\u738b\u4e66\u300b \u7684\u3002 \u3000\u3000\u6587\u9038\u51e1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u949f\u8001\u5934\uff0c\u4f60\u8fd9\u5b59\u5973\u771f\u662f\u80fd\u8a00\u5584\u8fa9\uff0c\u8fde\u6211\u90fd\u6709\u51e0\u5206\u4f69\u670d\u5979\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u6587\u53d4\u53d4\uff0c\u4f60\u522b\u2018\u635f\u2019\u6211\u597d\u4e0d\u597d\uff0c\u6211\u662f\u8bda\u5fc3\u5411\u4f60\u8bf7\u6559\u3002\u201d \u3000\u3000\u6587\u9038\u51e1\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u503e\u76d6\u5982\u654c\uff0c\u8fd8\u662f\u591a\u5c11\u4f1a\u77e5\u9053\u90a3\u4e2a\u4eba\u7684\u4e3a\u4eba\u7684\uff0c\u6216\u8005\u6070\u597d\u78b0\u89c1\u4ed6\u505a\u67d0 \u4e00\u4ef6\u4e8b\uff0c\u662f\u503c\u5f88\u94a6\u4f69\u7684\u3002\u90a3\u624d\u4f1a\u7ed3\u4e3a\u77e5\u5df1\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u7237\u7237\u548c\u6211\u88ab\u4eba\u6b3a\u8d1f\uff0c\u4ed6\u66ff\u6211\u4eec\u6253\u62b1\u4e0d\u5e73\uff0c\u8981\u4e0d\u662f\u4ed6\uff0c\u6211\u4eec\u53ea\u6015\u4e0d\u6b7b\u4e5f\u53d7 \u91cd\u4f24\u3002\u4ed6\u662f\u6211\u4eec\u7684\u6069\u4eba\uff0c\u600e\u80fd\u4e0d\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u6587\u9038\u51e1\u9053\uff1a\u201c\u4f60\u4eec\u662f\u53ea\u77e5\u5c0f\u4e8b\uff0c\u4e0d\u77e5\u5927\u4e8b\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u6551\u547d\u4e4b\u6069\uff0c\u600e\u80fd\u8bf4\u662f\u5c0f\u4e8b\u3002\u201d\u90a3\u8001\u8005\u5219\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5927\u4e8b\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6587\u9038\u51e1\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4e5f\u662f\u521a\u5f97\u6765\u7684\u6d88\u606f\uff0c\u6709\u4eba\u544a\u8bc9\u6211\u8bf4\uff0c\u4ed6\u3001\u4ed6\u2014\u2014\u201d\u4ed6\u548c\u90a3\u8001\u8005\u7684\u4ea4 \u60c5\u8fd8\u672a\u5230\u63a8\u5fc3\u7f6e\u8179\u7684\u7a0b\u5ea6\uff0c\u6b63\u81ea\u601d\u5fd6\uff0c\u8981\u4e0d\u8981\u628a\u79d8\u5bc6\u544a\u8bc9\u4ed6\uff0c\u8001\u8005\u5df1\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u662f\u542c \u4eba\u8bf4\u7684\uff0c\u5c0f\u4e8b\u7eb5\u7136\u4e0d\u8db3\u4e3a\u51ed\uff0c\u4e5f\u514d\u4e8e\u8f7b\u4fe1\u4eba\u8a00\u3002\u201d \u3000\u3000\u6587\u9038\u51e1\u5446\u4e86\u4e00\u5446\uff0c\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u597d\uff0c\u4eca\u65e5\u6211\u5356\u7ed9\u4f60\u4e00\u4e2a\u4eba\u60c5\uff0c\u8981\u662f\u2014\u2014\u201d\u4ed6\u6ce8\u89c6\u7740\u690d\u7fbd \u51b2\u7684\u6696\u7389\u7bab\uff0c\u201c\u8981\u662f\u201d\u600e\u6837\uff0c\u6ca1\u8bf4\u51fa\u6765\uff0c\u5ffd\u7136\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u6587\u9038\u51e1\u8d70\u540e\uff0c\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u949f\u8001\u7237\u5b50\uff0c\u591a\u8c22\u4f60\u7ed9\u6211\u89e3\u56f4\uff0c\u6211\u8fd8\u672a\u8bf7\u6559\u4f60\u8001\u5927\u540d\u5462\uff1f\u201d \u3000\u3000\u949f\u8001\u5934\u9053\uff1a\u201c\u6211\u53eb\u4e0d\u9e23\u3002\u6211\u8fd9\u5b59\u5973\u513f\u53eb\u7075\u79c0\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u7237\u7237\u7684\u540d\u5b57\u662f\u2018\u4e0d\u5e73\u5219\u9e23\u2019\u7684\u7b80\u7701\u3002\u4ed6\u59d3\u540d\u53eb\u949f\u4e0d\u9e23\uff0c\u5176\u5b9e\u4ed6\u8fd9\u53e3\u949f\u5374 \u662f\u7ecf\u5e38\u5927\u9e23\u7279\u9e23\u7684\uff0c\u662f\u4e3a\u4e0d\u5e73\u800c\u9e23\u7684\u3002\u76f8\u516c\uff0c\u4f60\u8d35\u59d3\uff1f\u55ef\uff0c\u6211\u77e5\u9053\u4f60\u662f\u53d7\u4eba\u8bef\u4f1a\u7684\uff0c\u4f9d\u6211\u770b \u6765\uff0c\u90a3\u4e2a\u6b3a\u8d1f\u6211\u7684\u5c0f\u5b50\u624d\u662f\u5978\u7ec6\u5462\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u7684\u540d\u5b57\u8d77\u5f88\u597d\u3002\u5c0f\u59b9\u5b50\uff0c\u4f60\u771f\u662f\u540d\u5982\u5176\u4eba\u3002\u6211\u6bd4\u4f60\u5927\u51e0\u5c81\uff0c\u4f60\u5c31\u53eb\u6211 \u4e00\u58f0\u8c2d\u5927\u54e5\u5427\uff0c\u522b\u79f0\u4ec0\u4e48\u76f8\u516c\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8c2d\u5927\u54e5\uff0c\u6211\u966a\u4f60\u6e38\u6e56\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5f88\u559c\u6b22\u8fd9\u5c0f\u59d1\u5a18\uff0c\u4e0d\u8fc7\u8981\u662f\u548c\u4ed6\u4eec\u7956\u5b59\u4e00\u540c\u6e38\u6e56\uff0c\u5374\u662f\u6709\u70b9\u4e0d\u4fbf\uff0c\u56e0\u6b64\u8e0c\u8e87\u672a \u7b54\u3002 \u3000\u3000\u949f\u8001\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u771f\u4e0d\u61c2\u4e8b\uff0c\u54b1\u4eec\u600e\u80fd\u548c\u8c2d\u76f8\u516c\u4e00\u540c\u6e38\u6e56\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u54b1\u4eec\u8eab\u4efd\u4e0d\u914d\u4e48\uff1f\u6211\u76f8\u4fe1\u8c2d\u5927\u54e5\u4e0d\u4f1a\u2014\u2014\u201d \u3000\u3000\u949f\u8001\u5934\u9053\uff1a\u201c\u8c2d\u76f8\u516c\u5f53\u7136\u4e0d\u4f1a\u770b\u8f7b\u54b1\u4eec\uff0c\u4f46\u5374\u4f1a\u5f15\u8d77\u522b\u4eba\u6ce8\u610f\u3002\u4e07\u4e00\u53c8\u518d\u78b0\u4e0a\u90a3\u4e2a\u5978\u7ec6 \u7684\u8bdd\uff0c\u5c31\u66f4\u7cdf\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u6b63\u60f3\u95ee\u4f60\u4eec\uff0c\u4f60\u4eec\u600e\u77e5\u9053\u90a3\u5c0f\u5b50\u662f\u5978\u7ec6\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u662f\u548c\u90a3\u4e2a\u4ec0\u4e48\u53f2\u5927\u4eba\u540c\u5728\u4e00\u8d77\uff0c\u8bf4\u7684\u53c8\u662f\u5916\u8def\u53e3\u97f3\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u4e2a\u2018\u53f2\u5927\u4eba\u2019\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u949f\u4e0d\u9e23\u9053\uff1a\u201c\u6b64\u4eba\u540d\u53eb\u53f2\u6d69\uff0c\u662f\u79e6\u6867\u95e8\u751f\uff0c\u73b0\u4efb\u540f\u90e8\u4f8d\u90ce\u3002\u201d \u3000\u3000\u63a5\u7740\u53f9\u9053\uff1a\u201c\u5f53\u4eca\u7687\u4e0a\u867d\u7136\u4e0b\u8bcf\u8ffd\u590d\u5c11\u4fdd\uff08\u5cb3\u98de\uff09\u539f\u5b98\uff0c\u4f46\u6cf0\u6867\u7684\u513f\u5b50\u548c\u95e8\u751f\u8fd8\u662f\u4f4d\u5c45 \u8981\u6d25\u3002\u4ee4\u4eba\u6d69\u53f9\u3002\u5cb3\u5c11\u4fdd\u7684\u6c89\u51a4\u4e5f\u8fd8\u672a\u80fd\u8bf4\u662f\u5df2\u7ecf\u662d\u96ea\u5462\u3002\u6a80\u7fbd\u51b2\u542c\u4e86\u4ed6\u4eec\u7684\u8c08\u8bba\uff0c\u65b9\u77e5\u79e6 \u6867\u7684\u513f\u5b50\u79e6\u71b9\uff0c\u4e5f\u662f\u4e00\u4e2a\u4e09\u54c1\u5b98\uff0c\u800c\u4e14\u9887\u5f97\u91cd\u7528\uff0c\u516c\u5e03\u671d\u5ef7\u653f\u4ee4\u7684\u671d\u62a5\u5c31\u662f\u7531\u4ed6\u4e3b\u7f16\u7684\u3002 \u3000\u3000\u949f\u4e0d\u9e23\u9053\uff1a\u201c\u90a3\u4e2a\u91d1\u56fd\u5978\u7ec6\u7684\u540e\u53f0\uff0c\u6050\u6015\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u4f4d\u5c45\u4f8d\u90ce\u7684\u53f2\u6d69\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u949f\u4e0d\u9e23\u9053\uff1a\u201c\u67a2\u5bc6\u4f7f\u6c64\u601d\u9000\uff01\u201d\u67a2\u5bc6\u4f7f\u662f\u519b\u4e8b\u5927\u81e3\uff0c\u5cb3\u98de\u751f\u524d\uff0c\u5b9e\u804c\u4e5f\u53ea\u662f\u505a\u5230\u67a2\u5bc6\u526f \u4f7f\u800c\u5df2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u949f\u4e0d\u9e23\u9053\uff1a\u201c\u4f60\u8d70\u4e86\u4e4b\u540e\uff0c\u6211\u542c\u5f97\u4e24\u4e2a\u5b98\u513f\u8c08\u8bba\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u6c64\u601d\u9000\u95e8\u5ba2\uff0c\u4ed6\u8bf4\uff1a\u4f60\u4ee5 \u4e3a\u90a3\u4f4d\u8c2d\u516c\u5b50\u4ec5\u4ec5\u662f\u53f2\u6d69\u7684\u4e16\u4f84\u5417\uff1f\u4ed6\u5176\u5b9e\u4e5f\u662f\u4f4f\u5728\u6c64\u5927\u4eba\u5bb6\u91cc\u7684\uff0c\u53f2\u6d69\u4e0d\u8fc7\u662f\u5949\u966a\u8fd9\u4f4d\u8c2d \u516c\u5b50\u51fa\u6e38\u800c\u5df2\u3002\u53ef\u80fd\u4ed6\u8bf4\u548c\u8fd9\u4f4d\u8c2d\u516c\u4e8e\u662f\u4e16\u4ea4\u4e5f\u662f\u5047\u7684\u3002\u4e0d\u8fc7\uff0c\u8fd9\u662f\u4e00\u4e2a\u79d8\u5bc6\uff0c\u4f60\u53ef\u5207\u83ab\u4e71 \u5bf9\u4eba\u8bf4\uff0c\u6211\u548c\u90a3\u4e24\u5b98\u513f\u90fd\u662f\u4ece\u697c\u5916\u697c\u8dd1\u51fa\u6765\u7684\uff0c\u4ed6\u4eec\u5c0f\u58f0\u8bf4\u8bdd\uff0c\u6211\u5728\u4ed6\u4eec\u7684\u80cc\u540e\uff0c\u8ddd\u79bb\u9887 \u8fdc\uff0c\u4ed6\u4eec\u5f53\u7136\u4e0d\u4f1a\u6ce8\u610f\u6211\u8fd9\u4e48\u4e00\u4e2a\u5356\u827a\u4eba\uff0c\u4ee5\u4e3a\u6ca1\u4eba\u542c\u89c1\uff0c\u8c01\u77e5\u5374\u7ed9\u6211\u542c\u89c1\u4e86\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u4ed6\u60f3\u4e86\u8d77\u6765\uff0c\u95ee\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5728\u697c\u5916\u697c\uff0c\u90a3\u5978\u7ec6\u6ca1\u8ba4\u51fa\u6765\u4f60\u5417\uff1f\u201d\u6a80\u7fbd\u51b2 \u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u6216\u8bb8\u4ed6\u867d\u7136\u8ba4\u51fa\uff0c\u5374\u6015\u6211\u63ed\u7834\u4ed6\u662f\u91d1\u56fd\u4eba\u7684\u8eab\u4efd\uff0c\u6545\u800c\u4e0d\u6562\u751f\u4e8b\u3002\u201d \u3000\u3000\u949f\u4e0d\u9e23\u5374\u4e0d\u80fd\u4e0d\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u591a\u7684\u5730\u65b9\u4ed6\u4e0d\u6562\u751f\u4e8b\uff0c\u4f46\u4f60\u53ef\u5fc5\u987b\u63d0\u9632\u4ed6\u7684\u6697 \u7b97\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u662f\uff0c\u6211\u4f1a\u5c0f\u5fc3\u7684\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5728\u6e56\u6ee8\u627e\u4e86\u4e00\u95f4\u5c0f\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u4ed6\u51c6\u5907\u505a\u7684\u7b2c\u4e00\u4ef6\u4e8b\u60c5\u662f\u7ed9\u4ed6\u7684\u5916\u66fe\u7956\u5cb3\u98de\u796d\u575f\u3002 \u3000\u3000\u5176\u5b9e\u79e6\u6867\u7684\u515a\u7fbd\u867d\u7136\u5c1a\u672a\u94f2\u9664\uff0c\u79e6\u6867\u7684\u515a\u7fbd\u751a\u81f3\u5728\u671d\u5ef7\u8fd8\u9887\u4e3a\u5f97\u52bf\uff0c\u4f46\u56e0\u4e3a\u767e\u59d3\u666f\u4ef0\u5cb3 \u98de\uff0c\u5cb3\u575f\u4e00\u5efa\uff0c\u6bcf\u5929\u90fd\u51e0\u4e4e\u6709\u5ddd\u6d41\u4e0d\u606f\u7684\u4eba\u7fa4\uff0c\u5230\u4ed6\u7684\u575f\u524d\u540a\u796d\u3002\u56e0\u6b64\uff0c\u6a80\u7fbd\u51b2\u5f88\u5bb9\u6613\u6253\u542c \u5230\u5cb3\u575f\u7684\u6240\u5728\uff0c\u800c\u4e14\u5e76\u6ca1\u5f15\u522b\u4eba\u5bf9\u4ed6\u7279\u522b\u6ce8\u610f\u3002 \u3000\u3000\u539f\u6765\u5cb3\u575f\u5c31\u5728\u6816\u971e\u5cad\u4e0b\uff0c\u548c\u4ed6\u6240\u4f4f\u7684\u8fd9\u95f4\u5ba2\u5e97\uff0c\u8ddd\u79bb\u751a\u8fd1\uff0c\u8d70\u8def\u6700\u591a\u4e5f\u4e0d\u8fc7\u662f\u8d70\u4e00\u652f\u9999 \u65f6\u95f4\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u4fbf\u767d\u5929\u4e0a\u575f\uff0e\u4e8e\u662f\u9884\u5148\u4e70\u597d\u9999\u70db\uff0c\u4e09\u66f4\u8fc7\u540e\u3002\u624d\u53bb\u591c\u796d\u3002 \u3000\u3000\u90a3\u65f6\u5cb3\u575f\u521d\u5efa\uff0c\u5f53\u7136\u8fd8\u6ca1\u6709\u540e\u6765\u7684\u201c\u98ce\u5149\u201d\u3002\u65e2\u672a\u7acb\u7960\uff0c\u4e5f\u672a\u94f8\u6709\u5978\u81e3\u7684\u8dea\u50cf\u3002\u90a3\u526f\u8457 \u540d\u7684\u5bf9\u8054\u201c\u9752\u5c71\u6709\u5e78\u7406\u5fe0\u9aa8\uff0c\u767d\u94c1\u65e0\u8f9c\u94f8\u4f5e\u81e3\u201d\uff0c\u5f53\u7136\u4e5f\u8fd8\u662f\u672a\u6709\u7684\u3002\u6709\u8fc7\u4eba\u5230\u575f\u524d\u75db\u9a82\u5978 \u81e3\uff0c\u6709\u8054\u6ca1\u8054\uff0c\u90fd\u662f\u4e00\u6837\u3002 \u3000\u3000\u5cb3\u98de\u662f\u6a80\u7fbd\u51b2\u6bcd\u4eb2\u7684\u5916\u516c\uff0c\u4ed6\u7684\u611f\u89e6\u5c31\u66f4\u6df1\u4e86\u3002\u4ed6\u70b9\u8d77\u9999\u70db\uff0c\u8dea\u5728\u575f\u524d\uff0c\u60f3\u8d77\u7237\u7237\u60e8 \u6b7b\uff0c\u7236\u6bcd\u53cc\u4ea1\uff0c\u548c\u5893\u4e2d\u7684\u8fd9\u4f4d\u4e00\u4ee3\u540d\u5c06\u90fd\u6709\u5173\u7cfb\uff0c\u4f46\u5982\u4eca\uff0c\u91d1\u5b8b\u4e24\u56fd\u8fd8\u662f\u5728\u5175\u8fde\u7978\u7ed3\uff0c\u672a\u606f \u5e72\u6208\uff0c\u4e0d\u7981\u70ed\u6cea\u76c8\u7736\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5fcd\u4f4f\u6ca1\u54ed\u51fa\u58f0\u3002 \u3000\u3000\u5cb3\u575f\u540e\u9762\u6709\u5757\u77f3\u7891\uff0c\u6a80\u7fbd\u51b2\u540a\u796d\u8fc7\u540e\uff0c\u8d70\u53bb\u770b\u90a3\u77f3\u7891\u4e0a\u523b\u7684\u5b57\uff0c\u4e00\u770b\u53c8\u7981\u4e0d\u4f4f\u70ed\u6cea\u76c8 \u7736\uff0c\u6ee1\u6000\u60b2\u6124\uff0c\u90a3\u77f3\u7891\u4e0a\u523b\u7684\u6b63\u662f\u5cb3\u98de\u5199\u7684\u90a3\u9996\u300a\u6ee1\u6c5f\u7ea2\u300b\uff0c\u800c\u4e14\u662f\u6a21\u4eff\u5cb3\u98de\u7684\u4e66\u6cd5\u523b\u7684\u3002 \uff08\u6309\uff1a\u5cb3\u98de\u8fd9\u9996\u6ee1\u6c5f\u7ea2\u7684\u771f\u5047\u95ee\u9898\uff0c\u662f\u5b66\u672f\u754c\u4e89\u8bba\u95ee\u9898\u4e4b\u4e00\u3002\u6709\u4eba\u8ba4\u4e3a\u6b64\u8bcd\u975e\u5cb3\u98de\u4e0d\u80fd\u5199\uff0c \u4f46\u4e5f\u4eba\u8bf4\u662f\u540e\u4eba\u4f2a\u9020\u7684\u3002\u4e0d\u8fc7\uff0c\u5c0f\u8bf4\u867d\u7136\u4e0d\u80fd\u8fdd\u80cc\u5386\u53f2\uff0c\u4f46\u5e76\u4e0d\u8fc7\u5168\u7b49\u4e8e\u5386\u53f2\u3002\u8bf7\u6055\u6211\u4e0d\u53bb \u8003\u8bc1\u771f\u4f2a\u95ee\u9898\uff0c\u5728\u5c0f\u8bf4\u4e2d\u5f53\u6210\u662f\u5cb3\u98de\u7684\u771f\u4f5c\u4e86\u3002\uff09\u5cb3\u98de\u624b\u5199\u7684\u300a\u6ee1\u6c5f\u7ea2\u300b\u771f\u8ff9\uff0c\u6a80\u7fbd\u51b2\u8fd8\u85cf \u5728\u8eab\u4e0a\uff0c\u8fd9\u662f\u4ed6\u7684\u201c\u516c\u516c\u201d\u5f20\u708e\u5b81\u820d\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u4fdd\u5b58\u7684\u201c\u5b9d\u7269\u201d\uff0c\u201c\u516c\u516c\u201d\u4e34\u7ec8\u4e4b\u9645\uff0c\u624d \u4ea4\u7ed9\u4ed6\u7684\u3002\u4ed6\u60f3\u8d77\u8fd9\u4f4d\u820d\u8eab\u4e3a\u4e3b\u7684\u6bcd\u4eb2\u7684\u4e49\u7236\uff0c\u81ea\u5df1\u4e00\u76f4\u628a\u4ed6\u5f53\u5916\u516c\u7684\u201c\u516c\u516c\u201d\uff0c\u66f4\u52a0\u5fcd\u4e0d \u4f4f\u6cea\u6d8c\u5fc3\u4f24\u4e86\u3002 \u3000\u3000\u4ed6\u867d\u7136\u4e0d\u6562\u72c2\u6b4c\u5f53\u54ed\uff0c\u5374\u4e5f\u7981\u4e0d\u4f4f\u4f4e\u58f0\u5ff5\u8d77\u8fd9\u9996\u8bcd\u6765\u3002\u201c\u6012\u53d1\u51b2\u51a0\uff0c\u51ed\u680f\u5904\uff0c\u6f47\u6f47\u96e8 \u6b47\u3002\u62cd\u671b\u773c\uff0c\u4ef0\u5929\u957f\u5578\uff0c\u58ee\u6000\u6fc0\u70c8\u2026\u2026\u201d\u4e00\u76f4\u5ff5\u5230\u201c\u58ee\u5fd7\u9965\u9910\u80e1\u864f\u8089\uff0c\u7b11\u8c08\u6e34\u996e\u5308\u5974\u8840\u3002\u5f85 \u4ece\u5934\uff0c\u6536\u62fe\u65e7\u5c71\u6cb3\uff0c\u671d\u5929\u9619\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u80e1\u864f\uff1f\u5308\u5974\uff1f\u4f60\u597d\u50cf\u5fd8\u8bb0\u81ea\u5df1\u662f\u54ea\u4e00\u56fd\u7684\u4eba\u4e86\uff01\u201d\u6a80\u7fbd \u51b2\u62ac\u8d77\u5934\u6765\uff0c\u4e00\u4e2a\u4eba\u5df2\u7ecf\u51fa\u73b0\u5728\u4ed6\u7684\u9762\u524d\uff0c\u6b63\u662f\u90a3\u4e2a\u76f8\u8c8c\u548c\u4ed6\u6709\u70b9\u76f8\u4f3c\u7684\u5c11\u5e74\uff0c\u4ea6\u5373\u662f\u5dee\u4e0d \u591a\u5df2\u7ecf\u88ab\u8bc1\u5b9e\u4e86\u662f\u91d1\u56fd\u6d3e\u6765\u7684\u5978\u7ec6\u7684\u90a3\u4e2a\u5c11\u5e74\uff01 \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5230\u4e34\u5b89\uff0c\u5fc5\u5b9a\u4f1a\u6765\u8fd9\u91cc\uff0c\u679c\u7136\u6211\u6ca1\u6599\u9519\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6599\u9519\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u6ca1\u6599\u9519\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77e5\u9053\u4f60\u662f\u8c01\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u77e5\u9053\u5c31\u597d\u3002\u201d\u8fb9\u8bf4\u8fb9\u89e3\u5f00\u5916\u8863\uff0c\u9732\u51fa\u90a3\u4e2a\u7ee3\u6709\u6a80\u5bb6\u5fbd\u8bb0\u7684\u9526\u888d\uff0c\u8bf4\u9053\uff1a \u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u7684\u8eab\u4efd\u4e5f\u4e0d\u7528\u7792\u6211\u4e86\u3002\u8fd9\u4ef6\u9526\u888d\u672c\u6765\u662f\u5e94\u8be5\u7a7f\u5728\u4f60\u7684\u8eab\u4e0a\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7a00\u7f55\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4e0d\u7a00\u7f55\u662f\u4f60\u7684\u4e8b\u3002\u6211\u8fd8\u662f\u8981\u591a\u8c22\u4f60\u770b\u5728\u8fd9\u4ef6\u9526\u888d\u7684\u4efd\u4e0a\uff0c\u5bf9\u6211\u624b\u4e0b\u7559 \u60c5\u3002\u201d\u539f\u6765\u6b63\u56e0\u4e3a\u6b64\u4e8b\u731c\u5230\u6a80\u7fbd\u51b2\u7684\u8eab\u4efd\u7684\uff0c\u6b64\u4e0d\u8fc7\u662f\u6c42\u8bc1\u800c\u5df2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u6765\u6b64\u5730\uff0c\u4e0d\u53ea\u662f\u7279\u5730\u4e3a\u4e86\u5411\u6211\u9053\u8c22\u5427\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u95ee\u5f97\u597d\uff0c\u6211\u5f53\u7136\u4e0d\u53ea\u662f\u4e3a\u4e86\u9053\u8c22\u6765\u7684\u3002\u54b1\u4eec\u73b0\u5728\u5df2\u7528\u4e0d\u7740\u9690 \u7792\u8eab\u4efd\uff0c\u662f\u5e94\u8be5\u53ef\u4ee5\u6253\u5f00\u5929\u7a97\u6765\u8bf4\u4eae\u8bdd\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4eec\u7684\u8eab\u4efd\u65e9\u5df2\u4e0d\u540c\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u597d\u8c08\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u4f60\u968f\u65f6\u90fd\u53ef\u4ee5\u6062\u590d\u539f\u6765\u8eab\u4efd\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u521a\u521a\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4f60\u90fd\u597d\u50cf\u5fd8\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u5426\u613f\u610f\uff0c\u54b1\u4eec\u8fd8\u662f\u4e00\u5bb6\u4eba\u662f\u4e0d\u662f\uff1f\u4f60\u5927\u6168\u8fd8\u672a\u77e5\u9053\u6211\u7684\u540d\u5b57\u5427\uff0c \u6211\u53eb\u6a80\u4e16\u82f1\uff0c\u6211\u548c\u4f60\u662f\u540c\u4e00\u4e2a\u66fe\u7956\u7236\u7684\u5144\u5f1f\u3002\u201d \u3000\u3000\u539f\u6765\u81ea\u4ece\u6a80\u7fbd\u51b2\u7684\u7956\u7236\u6a80\u516c\u76f4\u9003\u4ea1\u4e4b\u540e\uff0c\u4ed6\u7684\u4eb2\u738b\u7235\u4f4d\u5373\u6539\u7531\u4ed6\u7684\u540c\u80de\u5144\u5f1f\u6a80\u516c\u4e49\u4e16 \u88ad\uff0c\u6a80\u516c\u4e49\u53bb\u4e16\uff0c\u7235\u4f4d\u4f20\u7ed9\u957f\u5b50\u6a80\u9053\u9686\uff0c\u6a80\u9053\u9686\u662f\u91d1\u56fd\u7684\u5175\u9a6c\u526f\u5143\u5e05\uff0c\u6743\u52bf\u4e4b\u5927\uff0c\u4ec5\u6b21\u4e8e\u7687 \u53d4\u5b8c\u989c\u957f\u4e4b\u3002\u6a80\u4e16\u82f1\u5219\u662f\u6a80\u9053\u9686\u7684\u72ec\u751f\u513f\u5b50\u3002\u6a80\u5bb6\u7684\u7235\u4f4d\uff0c\u5c06\u6765\u5b9a\u7531\u4ed6\u627f\u7ee7\u7684\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u540c\u662f\u4e00\u5bb6\u4eba\uff0c\u4f46\u4e5f\u6709\u4e0d\u540c\u4e4b\u5904\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u540c\u4e4b\u5904\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u521a\u624d\u4f60\u95ee\u6211\u662f\u90a3\u4e00\u56fd\u4eba\uff0c\u73b0\u5728\u6211\u53ef\u4ee5\u7b54\u590d\u4f60\uff0c\u6211\u662f\u91d1\u56fd\u4eba\uff0c\u4e5f\u662f\u5b8b\u56fd \u4eba\uff01\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u6bcd\u4eb2\u662f\u5cb3\u98de\u7684\u5916\u5b59\u5973\u513f\uff0c\u4f46\u4e00\u4e2a\u4eba\u603b\u662f\u4e0d\u80fd\u811a\u8e0f\u4e24\u6761\u8239\uff0c\u8981\u561b \u4f60\u5c31\u505a\u91d1\u56fd\u4eba\uff0c\u8981\u561b\u4f60\u5c31\u505a\u5b8b\u56fd\u4eba\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5bf9\u6211\u6765\u8bf4\uff0c\u7236\u6bcd\u4e4b\u90a6\u90fd\u662f\u4e00\u6837\u3002\u91d1\u4eba\u662f\u4eba\uff0c\u5b8b\u4eba\u4e5f\u662f\u4eba\u3002\u5e76\u975e\u4e00\u751f\u4e0b\u6765\uff0c \u5c31\u975e\u654c\u5bf9\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u4f46\u4e8b\u5b9e\u4e0a\u4e24\u56fd\u662f\u5728\u5f00\u6218\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53ea\u8981\u5316\u5e72\u6208\u800c\u4e3a\u7389\u5e1b\uff0c\u4e24\u56fd\u5c31\u53ef\u4eb2\u5982\u4e00\u5bb6\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u6beb\u65e0\u8868\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u62b1\u8d1f\u5012\u662f\u4e0d\u5c0f\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u7684\u7237\u7237\u5f53\u5e74\u5c31\u8fd9\u6837 \u505a\uff0c\u6211\u5fc5\u987b\u7ee7\u627f\u4ed6\u7684\u9057\u5fd7\uff0c\u800c\u4e14\u6211\u5e0c\u671b\u4f60\u4e5f\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u519b\u56fd\u5927\u4e8b\uff0c\u53ea\u80fd\u7531\u7687\u4e0a\u5723\u88c1\u3002\u4f46\u4f60\u65e2\u7136\u6709\u8fd9\u6837\u4e3b\u5f20\uff0c\u4e0d\u59a8\u548c\u6211\u540c\u56de\u71d5 \u4eac\uff0c\u5411\u7687\u4e0a\u9762\u9648\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u7687\u4e0a\u4f1a\u542c\u4ece\u6211\u7684\u4e3b\u5f20\uff1f\u6211\u7684\u7237\u7237\u5f53\u5e74\u66fe\u8fd9\u6837\u505a\u8fc7\uff0c \u7ed3\u679c\u8fd8\u4e0d\u662f\u843d\u5f97\u4e2a\u94a6\u72af\u7684\u7f6a\u540d\uff1f\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u5f53\u4eca\u7687\u4e0a\u548c\u5148\u5e1d\u5e76\u4e0d\u4e00\u6837\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u6b64 \u6b21\u5357\u6765\uff0c\u5c31\u662f\u5949\u4e86\u7687\u5e1d\u4e4b\u547d\uff0c\u6765\u8bd5\u63a2\u5b8b\u56fd\u662f\u5426\u6709\u8c0b\u548c\u8bda\u610f\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u4eec\u5e0c\u671b\u8fbe\u6210\u600e\u6837\u7684\u548c\u8bae\uff1f\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u56fd\u5bb6\u673a\u5bc6\uff0c\u6055\u6211\u4e0d\u80fd\u5949\u544a\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u82e5\u5df2\u7ecf\u6062\u590d\u8d1d\u5b50\u8eab\u4efd\uff0c\u90a3\u53c8\u53e6 \u5f53\u522b\u8bba\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u597d\u50cf\u662f\u66ff\u8c01\u505a\u8bf4\u5ba2\u4f3c\u7684\uff0c\u6211\u56de\u53bb\u505a\u8d1d\u5b50\uff0c\u5bf9\u4f60\u6709\u4ec0\u4e48\u597d \u5904\uff1f\u201d\u6a80\u4e16\u82f1\u7b11\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\u3002\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u4f60\u5230\u8fc7\u4eac\u57ce\uff0c\u6b64\u4e8b\u7687\u4e0a\u4ea6\u5df2\u77e5\u9053\u4e86\u3002\u4f60\u548c \u5b8c\u989c\u738b\u7237\u4f5c\u5bf9\uff0c\u7687\u4e0a\u5e76\u4e0d\u751f\u6c14\uff0c\u8fd8\u8ba4\u4e3a\u4f60\u662f\u4e2a\u4eba\u6750\u5462\u3002\u56e0\u6b64\uff0c\u4ed6\u5dee\u6211\u5357\u6765\uff0c\u987a\u4fbf\u627e\u4f60\u56de\u53bb\u3002 \u7687\u4e0a\u8bf4\u53ef\u4ee5\u8ba9\u6211\u4eec\u6a80\u5bb6\u6709\u4e24\u4e2a\u4eb2\u738b\u7684\u7235\u4f4d\uff0c\u4f60\u6709\u597d\u5904\uff0c\u6211\u4e5f\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u4e2a\u597d\u5904\uff0c\u6211\u4e0d\u60f3\u8981\u3002\u6211\u53ea\u76fc\u671b\u91d1\u5b8b\u4e24\u56fd\u7684\u767e\u59d3\uff0c\u90fd\u5f97\u5230\u597d\u5904\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u7687\u4e0a\u4e0d\u6b63\u662f\u60f3\u8981\u548c\u5b8b\u56fd\u8bae\u548c\u4e48\uff1f\u6240\u4ee5\u4f60\u5373\u4f7f\u4e0d\u60f3\u5c01\u738b\uff0c\u4e5f\u5e94\u5f53\u548c\u6211\u56de\u53bb\uff0c \u8bba\u4eb2\u8c0a\uff0c\u7687\u4e0a\u4e5f\u662f\u54b1\u4eec\u7684\u8868\u5144\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u7b49\u5f85\u7687\u4e0a\u64a4\u5175\uff0c\u4ee5\u53ca\u628a\u4fb5\u5360\u5b8b\u56fd\u7684\u5730\u65b9\u90fd\u5f52\u8fd8\u4e4b\u540e\uff0c\u6211\u5c31\u56de \u53bb\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u6837\u70ed\u5fc3\u5e2e\u5fd9\u5b8b\u56fd\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u7687\u4e0a\u8981\u548c\u5b8b\u56fd\u8bb2\u548c\u5417\uff1f\u4e0d\u64a4\u5175\uff0c\u4e0d\u8fd8\u5730\uff0c\u600e\u80fd\u7b97\u5f97\u662f\u548c\uff1f\u201d \u3000\u3000\u6a80\u4e16\u82f1\u4f3c\u4e4e\u6709\u70b9\u4e0d\u8010\u70e6\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u548c\u4f60\u8c08\u4ec0\u4e48\u5927\u9053\u7406\u3002\u53ea\u60f3\u529d\u4f60\u4e3a\u81ea\u5df1\u60f3\u60f3\u3002 \u5cb3\u98de\u5728\u5b8b\u56fd\uff0c\u4ed6\u7684\u5b98\u4e5f\u53ea\u4e0d\u8fc7\u592a\u5b50\u5c11\u4fdd\uff0c\u6bd4\u8d77\u54b1\u4eec\u6a80\u5bb6\u7684\u4eb2\u738b\u7235\u4f4d\u8fd8\u5dee\u5f97\u8fdc\u5462\uff01\u4f60\u96be\u9053\u8fd8\u8981 \u50cf\u4f60\u7684\u7237\u7237\u90a3\u6837\u505a\u50bb\u5b50\uff1f\u505a\u50bb\u5b50\u7684\u4e0b\u573a\u4f60\u5e94\u8be5\u6bd4\u6211\u66f4\u52a0\u6e05\u695a\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6ee1\u6000\u60b2\u6124\uff0c\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u4f46\u5373\u4f7f\u662f\u5bb6\u7834\u4eba\u4ea1\uff0c\u50cf\u6211\u7237\u7237\u90a3 \u6837\uff0c\u6211\u4e5f\u8fd8\u662f\u8981\u505a\u50bb\u5b50\uff01\u201d \u3000\u3000\u6a80\u4e16\u82f1\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u662f\u8bf7\u4e0d\u52a8\u4f60\u4e86\u3002\u4f60\u4e0d\u542c\u826f\u8a00\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u671b\u4f60\u597d\u81ea\u4e3a\u4e4b\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u671b\u4f60\u597d\u81ea\u4e3a\u4e4b\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff0c\u6a80\u8d1d\u5b50\u4e5f\u8bf7\u4f60\u4e0d\u52a8\uff0c\u4f46\u4f60\u83ab\u4ee5\u4e3a\u5c31\u6ca1\u4eba\u80fd\u8bf7\u5f97\u52a8\u4f60 \u7684\u5927\u9a7e\u4e86\u3002\u201d \u3000\u3000\u5cb3\u575f\u540e\u9762\uff0c\u7a81\u7136\u8d70\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u4e00\u9ad8\u4e00\u77ee\u3002\u8bf4\u8bdd\u7684\u662f\u90a3\u4e2a\u77ee\u5b50\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u4e24\u4f4d\u4e5f\u662f\u6765\u8bf7\u5ba2\u7684\u4e48\uff1f\u201d\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u5bb6\u4e3b\u4eba\u6709\u6e05\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u67a2\u5bc6\u4f7f\u6c64\u5927\u4eba\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6c64\u601d\u9000\u5dee\u9063\u4f60\u4eec\u6765\u7684\u3002\u770b\u6765\u6211\u7684\u9762\u5b50\u5012\u662f\u4e0d\u5c0f\uff0c\u4e00\u5230 \u6c5f\u5357\uff0c\u5c31\u63a5\u8fde\u6709\u4eba\u8bf7\u5ba2\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6c64\u5927\u4eba\u7ed9\u4f60\u7684\u9762\u5b50\u5c31\u597d\uff0c\u90a3\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u53ef\u501f\u4f60\u5bb6\u6c64\u5927\u4eba\u7684\u9762\u5b50\u4e0d\u591f\uff01\u201d \u3000\u3000\u90a3\u4e24\u4eba\u6012\u9053\uff1a\u201c\u4f60\u6562\u5c0f\u770b\u6211\u5bb6\u4e3b\u4eba\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5207\u65ad\u4ed6\u4eec\u7684\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u6c64\u601d\u9000\u5927\u4eba\u4e0d\u8fc7\u662f\u4e00\u4e2a\u67a2\u5bc6\u4f7f\u800c\u5df2\uff0c\u91d1\u56fd\u7684\u7687\u5e1d\u90fd\u8bf7\u4e0d \u52a8\u6211\uff0c\u6c64\u601d\u9000\u7684\u9762\u5b50\u96be\u9053\u8fd8\u80fd\u5927\u5f97\u8fc7\u91d1\u56fd\u7684\u7687\u5e1d\u5417\uff1f\u201d \u3000\u3000\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u4fd7\u8bdd\u8bf4\u5f97\u597d\uff0c\u5c71\u9ad8\u7687\u5e1d\u8fdc\uff0c\u4e0d\u6015\u5b83\uff0c\u53ea\u6015\u7ba1\uff0c\u4e34\u5b89\u662f\u5728\u6211\u4eec\u6c64\u5927\u4eba\u7ba1\u8f96 \u4e4b\u4e0b\uff0c\u91d1\u56fd\u7684\u7687\u5e1d\u7ba1\u4e0d\u5230\u4f60\uff0c\u6c64\u5927\u4eba\u53ef\u7ba1\u5f97\u5230\u4f60\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u6211\u529d\u4f60\u8fd8\u662f\u8bc6\u8da3\u7684\u597d\uff0c\u83ab\u8981\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u4eba\u5c31\u662f\u6700\u4e0d\u8bc6\u8da3\uff0c\u656c\u9152\u7f5a\u9152\u6211\u90fd\u4e0d\u559d\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u90a3\u77ee\u5b50\u7acb\u5373\u5c31\u6251\u4e0a\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u559d\u4e5f\u8981\u559d\uff01\u201d\u4e00\u62db\u201c\u6076\u864e\u638f\u5fc3\u201d\uff0c \u5de6\u638c\u6a2a\u80f8\uff0c\u53f3\u638c\u731b\u6363\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u5916\u5bb6\u529f\u592b\u5012\u662f\u7ec3\u5f97\u4e0d\u9519\uff01\u201d\u4f7f\u4e86\u4e2a\u201c\u5378\u201d\u5b57\u8bc0\uff0c\u8f7b\u8f7b\u4e00\u62e8\uff0c\u5c06\u4ed6 \u7684\u62f3\u5934\u6280\u5f00\u3002\u90a3\u4eba\u8eab\u5f62\u4e00\u8f6c\uff0c\u6539\u7528\u201c\u9e70\u722a\u624b\u201d\uff0c\u5411\u4ed6\u7684\u745f\u745f\u9aa8\u6293\u4e0b\uff0c\u6a80\u7fbd\u51b2\u559d\u9053\uff1a\u201c\u53bb\uff01\u201d \u970d\u5730\u4e00\u4e2a\u51e4\u70b9\u5934\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u638c\u529b\u4e00\u5410\uff0c\u628a\u90a3\u77ee\u5b50\u903c\u5f97\u5012\u9000\u4e86\u516d\u4e03\u6b65\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u8fd9\u4e00\u638c\u662f\u5df2\u7ecf\u7528\u4e0a\u4e86\u5185\u5bb6\u771f\u529b\u7684\uff0c\u8fd9\u77ee\u5b50\u5c45\u7136\u6ca1\u6709\u5982\u4ed6\u6599\u7684\u8dcc\u4e2a\u56db\u811a\u671d\u5929\uff0c\u5012\u662f \u4ee4\u4ed6\u4e0d\u7981\u6709\u70b9\u8be7\u5f02\u3002 \u3000\u3000\u90a3\u9ad8\u4e2a\u5b50\u89c1\u4f19\u4f34\u62b5\u654c\u4e0d\u4f4f\uff0c\u4f7f\u5373\u4e0a\u524d\u5939\u653b\u3002\u4ed6\u7528\u7684\u662f\u4e00\u628a\u5f2f\u5200\uff0c\u76f4\u780d\u4e09\u5200\uff0c\u5200\u6cd5\u9887\u4e3a\u5947 \u7279\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8bc6\u5f97\u662f\u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d\uff0c\u4e0d\u89c9\u53c8\u662f\u6697\u6697\u5947\u602a\uff0c\u987b\u77e5\u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d\u4e43\u662f\u4fdd\u5b9a \u5e9c\u7530\u5bb6\u7684\u72ec\u95e8\u5200\u6cd5\uff0c\u5728\u5317\u65b9\u5df2\u7ecf\u7f55\u89c1\uff0c\u60f3\u4e0d\u5230\u5374\u5728\u6c5f\u5357\u78b0\u4e0a\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u5317\u65b9\u6765\u7684\uff0c \u800c\u4e14\u4ed6\u4eec\u672c\u6765\u662f\u5b8c\u989c\u957f\u4e4b\u7684\u95e8\u5ba2\uff0c\u7531\u5b8c\u989c\u957f\u4e4b\u201c\u8350\u201d\u7ed9\u6c64\u601d\u9000\u7684\u3002\u77ee\u7684\u90a3\u4e2a\u662f\u72ec\u811a\u5927\u76d7\u51fa \u8eab\uff0c\u590d\u59d3\u5357\u5bab\uff0c\u5355\u540d\u4e00\u4e2a\u201c\u9020\u201d\u5b57\u3002\u6c5f\u6e56\u4e0a\u4eba\u79f0\u5357\u5c71\u864e\u3002\u90a3\u9ad8\u4e2a\u5b50\u5219\u662f\u590d\u59d3\u201c\u6fee\u9633\u201d\u5355\u540d\u4e00 \u4e2a\u201c\u521a\u201d\u5b57\uff0c\u4ed6\u7684\u54e5\u54e5\u6fee\u9633\u575a\u662f\u91d1\u56fd\u5927\u5185\u536b\u58eb\uff0c\u4ed6\u5012\u662f\u201c\u6b63\u9014\u201d\u51fa\u8eab\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u5200\u5f71\u7eb5\u6a2a\uff0c\u638c\u98ce\u864e\u864e\uff0c\u5360\u4e86\u4e03\u6210\u653b\u52bf\u3002 \u3000\u3000\u6a80\u4e16\u82f1\u54b3\u55fd\u4e00\u58f0\uff0c\u6e05\u7406\u5589\u5499\uff0c\u6b63\u60f3\u51fa\u8a00\uff0c\u518d\u884c\u8bf1\u903c\uff0c\u4e0d\u6599\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u4e00\u7247\u78a7\u7eff\u5149 \u534e\uff0c\u628a\u6fee\u9633\u521a\u7684\u5200\u5149\u538b\u4e86\u4e0b\u53bb\uff0c\u539f\u6765\u6a80\u7fbd\u51b2\u5df2\u7ecf\u62ff\u51fa\u4e86\u6696\u7389\u7bab\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u6fee\u9633\u521a\u7684\u5f2f\u5200\u7ed9\u7389\u7bab\u8361\u5f00\uff0c\u53ea\u89c9\u80a9\u4e95\u7a74\u4e00\u9ebb\uff0c\u7a74\u9053\u7ed9\u70b9\u4e2a\u6b63\u7740\u3002\u6fee\u9633\u521a \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5012\u7eb5\u51fa\u53bb\u3002\u5357\u5bab\u9020\u8d76\u5fd9\u6536\u638c\uff0c\u548c\u6fee\u9633\u521a\u5e76\u80a9\u7ad9\u5728\u4e00\u8d77\uff0c\u4ed6\u4eec\u90fd\u662f\u9762\u5411\u6a80\u7fbd\u51b2 \u6012\u76ee\u800c\u89c6\uff0c\u4f46\u5df2\u662f\u4e0d\u6562\u5411\u524d\u4e86\u3002\u6a80\u7fbd\u51b2\u4e0d\u7981\u4e5f\u662f\u6709\u70b9\u5403\u60ca\uff0c\u80a9\u4e95\u7a74\u662f\u4e2a\u611f\u89c9\u6700\u7075\u654f\u7684\u9ebb\u7a74\uff0c \u6fee\u9633\u521a\u7ed9\u70b9\u4e2d\u201c\u80a9\u5e76\u7a74\u201d\uff0c\u201c\u5e94\u8be5\u201d\u4e0d\u80fd\u52a8\u5f39\u7684\uff0c\u800c\u4ed6\u5c45\u7136\u8fd8\u662f\u4ee4\u5f97\u6a80\u7fbd\u51b2\u6709\u70b9\u201c\u83ab\u6d4b\u9ad8 \u6df1\u201d\u4e86\u3002\u201c\u96be\u9053\u4ed6\u4f1a\u632a\u79fb\u7a74\u9053\u7684\u529f\u592b\uff1f\u201d \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u6a80\u7fbd\u51b2\u628a\u654c\u4eba\u4f30\u8ba1\u5f97\u8fc7\u9ad8\u4e86\uff0c\u6fee\u9633\u521a\u7684\u5185\u529f\u662f\u4e0d\u9519\uff0c\u4f46\u6bd4\u8d77\u6a80\u7fbd\u51b2\u8fd8 \u662f\u9887\u6709\u4e0d\u5982\u7684\u3002\u4ed6\u5e76\u4e0d\u4f1a\u201c\u632a\u79fb\u7a74\u9053\u201d\uff0c\u53ea\u662f\u7a0d\u5fae\u61c2\u5f97\u201c\u95ed\u7a74\u201d\u7684\u529f\u592b\u3002\u4ed6\u88ab\u7389\u7bab\u70b9\uff0c\u7acb\u5373 \u81ea\u884c\u201c\u95ed\u7a74\u201d\uff0c\u6545\u800c\u5728\u90a3\u77ac\u95f4\u8fd8\u80fd\u7eb5\u8dc3\u3002\u6b64\u523b\u4ed6\u6b63\u5728\u8c03\u5300\u6c14\u606f\uff0c\u89e3\u6d88\u7a74\u9053\u6240\u53d7\u7684\u5916\u529b\u51b2\u51fb\u3002 \u6240\u4ee5\u4ed6\u53ea\u80fd\u5bf9\u6a80\u7fbd\u51b2\u6012\u76ee\u800c\u89c6\uff0c\u8fde\u5f00\u53e3\u8bf4\u8bdd\u90fd\u4e0d\u80fd\u591f\uff0c\u5047\u5982\u6a80\u7fbd\u51b2\u65e9\u5df1\u6478\u7740\u4ed6\u7684\u6df1\u6d45\uff0c\u6b64\u65f6 \u53ea\u8981\u4e0a\u53bb\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5c31\u80fd\u628a\u4ed6\u63a8\u5012\u3002 \u3000\u3000\u6a80\u4e16\u82f1\u54b3\u55fd\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u90fd\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u522b\u518d\u6253\u4e86\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u603b\u7b97\u6ca1\u6709\u51fa\u624b\uff0c\u53ea\u662f\u51fa\u53e3\u3002\u5f53\u7136\uff0c\u5047\u5982\u4ed6\u51fa\u624b\u7684\u8bdd\uff0c\u4e5f\u672a\u5fc5\u5c31\u80dc\u5f97\u4e86\u6853\u7fbd\u51b2\uff0c\u4f46 \u6a80\u7fbd\u51b2\u4ee5\u4e00\u654c\u4e09\uff0c\u603b\u662f\u8f83\u96be\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6ca1\u6709\u5e2e\u4ed6\u4eec\u903c\u6211\u559d\u8fd9\u676f\u7f5a\u9152\u3002\u201d\u8fd9\u8bdd\u662f\u8fd8\u6709\u5632\u8bbd\u5473\u9053\uff0c\u4f46\u4e5f \u5e76\u975e\u5b8c\u5168\u662f\u201c\u53cd\u8bdd\u201d\u3002\u4e0d\u8fc7\uff0c\u4ed6\u8fd9\u201c\u591a\u8c22\u201d\u4e8c\u5b57\u8fd8\u662f\u8bf4\u5f97\u592a\u65e9\u4e86\u3002 \u3000\u3000\u6a80\u4e16\u82f1\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\u54b1\u4eec\u90fd\u662f\u5144\u5f1f\uff0c\u5927\u54e5\uff0c\u521a\u624d\u6211\u548c\u4f60\u8bf4\u8fc7\u7684\u90a3\u4e9b\u8bdd\uff0c\u5e0c\u671b\u4f60\u56de \u53bb\u60f3\u4e00\u60f3\u3002\u201d \u3000\u3000\u5357\u5bab\u9020\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u8ba9\u4f60\u591a\u60f3\u4e24\u5929\uff0c\u4f60\u53ef\u522b\u6253\u9003\u8dd1\u7684\u4e3b\u610f\u3002\u201d\u8bf4\u7f62\uff0c\u7a81\u7136\u6296\u5f00 \u4e00\u5e45\u4e66\u56fe\uff0c\u56fe\u4e2d\u4eba\u50cf\uff0c\u6b63\u662f\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u201c\u4e34\u5b89\u57ce\u5916\u5404\u5904\u5173\u5361\uff0c\u90fd\u5df2\u6709\u8fd9\u5e45\u4e66\u56fe\uff0c\u4f60\u8981\u8dd1\u662f\u8dd1\u4e0d\u6389\u7684\u3002\u770b\u5728\u6a80\u8d1d\u5b50\u7684\u4efd\u4e0a\uff0c\u8fd9\u4e24 \u5929\u6211\u4eec\u4e0d\u6253\u6270\u4f60\u7684\u6e38\u5174\u3002\u7b49\u4f60\u6e38\u7f62\u897f\u6e56\uff0c\u6211\u4eec\u518d\u6765\u8ba8\u4f60\u56de\u97f3\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4ed6\u548c\u6fee\u9633\u521a\u5c31\u8ddf\u7740\u6a80\u4e16\u82f1\u8d70\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u56de\u5230\u90a3\u5bb6\u5ba2\u5e97\uff0c\u8def\u4e0a\u5012\u662f\u5e76\u6ca1\u6709\u53d1\u89c9\u6709\u4eba\u8ddf\u8e2a\u3002 \u3000\u3000\u4ed6\u76d8\u819d\u6253\u5750\uff0c\u517b\u4e86\u4e00\u4f1a\u795e\uff0c\u4e0d\u4e45\u5c31\u5929\u4eae\u4e86\u3002 \u3000\u3000\u4ed6\u521a\u6253\u5f00\u623f\u95e8\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u4f19\u8ba1\u7ad9\u5728\u95e8\u524d\uff0c\u6b63\u662f\u6628\u5929\u62db\u547c\u4ed6\u8fdb\u623f\u7684\u90a3\u4e2a\u4f19\u8ba1\u3002 \u3000\u3000\u4ed6\u4e5f\u65e9\u5df2\u51c6\u5907\u6709\u8fd9\u6837\u7684\u4e8b\u53d1\u751f\uff0c\u628a\u4e00\u952d\u5143\u5b9d\u585e\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5bb3\u6015\uff0c\u6211\u4e0d\u662f\u5f3a \u76d7\uff0c\u4e5f\u4e0d\u662f\u574f\u4eba\uff0c\u53ea\u662f\u60f3\u548c\u670b\u53cb\u5f00\u5f00\u73a9\u7b11\uff0c\u8fd9\u70b9\u8336\u94b1\uff0c\u4f60\u6536\u4e0b\u5427\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u5e94\u8be5\u61c2\u5f97\u600e \u6837\u505a\u7684\u3002\u201d \u3000\u3000\u623f\u996d\u94b1\u4ed6\u662f\u6628\u665a\u5c31\u5df2\u4ed8\u7684\uff0c\u4f46\u201c\u8fd9\u70b9\u8336\u94b1\u201d\u5374\u6bd4\u623f\u996d\u94b1\u591a\u4e86\u5341\u500d\u4e0d\u6b62\u3002\u4ed6\u518d\u7ed9\u90a3\u4f19\u8ba1\u7684 \u65f6\u5019\uff0c\u6697\u8fd0\u6307\u529b\uff0c\u634f\u4e86\u4e00\u9053\u6307\u75d5\u3002 \u3000\u3000\u6839\u636e\u8fd9\u4e00\u5e74\u6765\u4ed6\u8d70\u6c5f\u6e56\u7684\u7ecf\u9a8c\uff0c\u8fd9\u79cd\u5a01\u8feb\u5229\u8bf1\uff0c\u53cc\u7ba1\u9f50\u4e0b\u7684\u529e\u6cd5\uff0c\u901a\u5e38\u90fd\u662f\u5f88\u6709\u6548\u7684\u3002 \u3000\u3000\u679c\u7136\u90a3\u4f19\u8ba1\u5c31\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u5bf9\u4eba\u8bf4\u7684\u3002\u201d\u4ed6\u8bf4\u7684\u8fd9\u4e9b\u8bdd\u662f\u65e9\u5728\u6a80\u7fbd\u51b2\u610f\u6599 \u4e4b\u4e2d\uff0c\u4f46\u4ed6\u7684\u9762\u4e0a\u5374\u5e76\u6ca1\u60ca\u614c\u795e\u8272\uff0c\u5374\u662f\u7a0d\u5fae\u51fa\u4e4e\u6a80\u7fbd\u51b2\u610f\u6599\u4e4b\u5916\u3002\u597d\u5728\u4ed6\u53d1\u89c9\u8fd9\u4e00\u70b9\uff0c\u7a81 \u7136\u4ed6\u53c8\u53d1\u73b0\u53e6\u5916\u4e00\u70b9\u66f4\u5927\u7684\u53ef\u7591\u4e4b\u5904\u4e86\u3002 \u3000\u3000----------- \u3000\u3000\u7fbd\u751f\u5802 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u542b\u51a4\u83ab\u767d \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u4e09\u56de\u3000\u542b\u51a4\u83ab\u767d \u3000\u3000\u6628\u5929\u62db\u547c\u4ed6\u7684\u90a3\u4e2a\u4f19\u8ba1\uff0c\u6307\u7532\u80ae\u810f\uff0c\u800c\u4e14\u53ef\u80fd\u662f\u56e0\u4e3a\u5e38\u505a\u7c97\u91cd\u7684\u5de5\u4f5c\uff0c\u98df\u6307\u62c7\u6307\u4fa7\u8fb9\uff0c \u662f\u8d77\u4e86\u4e00\u5c42\u8327\u7684\u3002\u4f46\u8fd9\u4e2a\u4f19\u8ba1\uff0c\u6307\u7532\u5374\u662f\u4fee\u526a\u5f97\u751a\u4e3a\u6574\u6d01\uff0c\u6307\u5934\u4e5f\u662f\u5149\u6ed1\u5e73\u51c0\uff0c\u4e00\u70b9\u4e0d\u50cf\u5e72 \u6d3b\u7684\u201c\u7c97\u4eba\u201d\u3002\u6613\u5bb9\u672f\u6700\u6ce8\u91cd\u9762\u90e8\u7684\u5316\u88c5\uff0c\u6307\u5934\u7684\u7279\u70b9\u662f\u8f83\u5c11\u6ce8\u610f\u7684\u3002\u5c24\u5176\u5728\u6ca1\u6709\u5145\u88d5\u7684\u65f6 \u95f4\u7ed9\u4ed6\u5316\u88c5\u7684\u60c5\u5f62\u5e95\u4e0b\uff0c\u66f4\u5bb9\u6613\u5ffd\u7565\u8fd9\u4e9b\u7ec6\u8282\u3002\u6a80\u7fbd\u51b2\u4e0d\u652f\u58f0\u97f3\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5916\u9762\u8fd8\u6709\u4f60 \u7684\u51e0\u4e2a\u4f19\u4f34\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff0c\u5ba2\u5b98\uff0c\u4f60\u522b\u591a\u7591\uff01\u201d \u3000\u3000\u679c\u7136\u4e00\u8bd5\u5c31\u9732\u51fa\u9a6c\u811a\u6765\u4e86\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u8bf4\u4f60\u662f\u5949\u547d\u76d1\u89c6\u6211\u7684\uff0c\u4f60\u600e\u77e5\u6211\u662f\u591a\u7591\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u65b9\u77e5\u4e2d\u8ba1\uff0c\u62bd\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u523a\u5411\u6a80\u7fbd\u51b2\u3002\u624b\u6cd5\u4e0d\u5f31\uff0c\u4f46\u5374\u600e\u523a\u5f97\u7740\u6a80\u7fbd\u51b2\uff1f\u7ed9\u6a80 \u7fbd\u51b2\u4e00\u4e0b\u5b50\u5c31\u70b9\u7740\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u628a\u8fd9\u4f19\u8ba1\u63a8\u5165\u623f\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u559c\u6b22\u5192\u5145\u65c1\u4eba\uff0c\u6211\u5c31\u8ba9\u4f60\u5145\u6211\u5427\u3002\u201d\u5c06\u4ed6\u653e\u5728\u5e8a \u4e0a\uff0c\u7528\u88ab\u8499\u4f4f\u3002\u6a80\u7fbd\u51b2\u7528\u7684\u662f\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u8981\u8fc7\u4e8c\u5341\u56db\u4e2a\u65f6\u8fb0\uff0c\u65b9\u80fd\u81ea\u89e3\u3002 \u3000\u3000\u4ed6\u607b\u6253\u5f00\u623f\u95e8\uff0c\u53ea\u89c1\u90a3\u4e2a\u80d6\u80d6\u7684\u5ba2\u5e97\u638c\u67dc\uff0c\u5df2\u662f\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5fd9\u9053\uff1a\u201c\u5d14\u8235\u4e3b\uff0c\u6211\u662f\u949f\u4e0d\u9e23\u7684\u670b\u53cb\u3002\u8fd9\u4ef6\u4e8b\u662f\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u638c\u67dc\uff0c\u59d3\u5d14\u540d\u6d69\uff0c\u662f\u4e10\u5e2e\u5728\u4e34\u5b89\u5206\u8235\u7684\u526f\u8235\u4e3b\u3002\u6b63\u56e0\u4e3a\u4ed6\u6709\u8fd9\u79cd\u8eab\u4efd\uff0c\u6240\u4ee5\u949f \u4e0d\u9e23\u6307\u5f15\u6a80\u7fbd\u51b2\u5230\u8fd9\u95f4\u5ba2\u5e97\u6295\u5bbf\u3002\u4ee5\u4fbf\u6a80\u7fbd\u51b2\u5728\u5fc5\u8981\u4e4b\u65f6\u53ef\u4ee5\u5411\u4ed6\u6c42\u52a9\u3002 \u3000\u3000\u5d14\u6d69\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u65e9\u5df2\u5728\u6211\u610f\u6599\u4e4b\u4e2d\uff0c\u6211\u4eec\u7684\u5211\u5802\u9999\u4e3b\u6b63\u8981\u627e\u4f60\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e00\u60ca\u9053\uff1a\u201c\u8d35\u5e2e\u7684\u5211\u5802\u9999\u4e3b\u627e\u6211\uff1f\u2014\u2014\u201d\u5fc3\u60f3\uff1a\u201c\u4e10\u5e2e\u7684\u5211\u5802\u9999\u4e3b\uff0c\u59d3\u751a\u540d \u8c01\uff0c\u6211\u90fd\u4e0d\u77e5\uff0c\u600e\u7684\u4ed6\u8981\u627e\u6211\uff1f\u5fc3\u5ff5\u672a\u5df2\uff0c\u8fd9\u4e2a\u4e10\u5e2e\u7684\u5211\u5802\u9999\u4e3b\u4ea6\u5df2\u8d70\u51fa\u6765\u4e86\uff0c\u662f\u4e2a\u5e74\u7ea6\u4e09 \u5341\u5de6\u53f3\uff0c\u795e\u60c5\u5a01\u731b\uff0c\u6ee1\u8138\u866c\u9aef\u7684\u5927\u6c49\u3002 \u3000\u3000\u866c\u9aef\u6c49\u5b50\u53cc\u7738\u70af\u70af\uff0c\u903c\u89c6\u7740\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4ece\u603b\u8235\u524d\u6765\u3002\u4e3a\u7684\u5c31\u6b63\u662f\u627e\u4f60\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd8\u6ca1\u8bf7\u6559\u9999\u4e3b\u59d3\u540d\uff1f\u201d \u3000\u3000\u866c\u9aef\u6c49\u5b50\u7684\u76ee\u5149\u6709\u5982\u5bd2\u51b0\u5229\u526a\uff0c\u76ef\u7740\u4ed6\u4e00\u4e2a\u4e2a\u5b57\u7684\u8bf4\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u4f60\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff0c \u6211\u53ef\u77e5\u9053\u4f60\u662f\u8c01\uff01\u6211\u4e0d\u4f46\u77e5\u9053\u4f60\u662f\u6a80\u8d1d\u5b50\uff0c\u66f4\u77e5\u9053\u4f60\u662f\u91d1\u56fd\u6d3e\u6765\u7684\u5978\u7ec6\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u5df2\u662f\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u4e00\u6293\u5c31\u5411\u4ed6\u6293\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u7adf\u7136\u4e00\u51fa\u624b\u5c31\u60f3\u628a\u6a80\u7fbd\u51b2\u7684\u6b66\u529f\u5e9f\u6389\uff0c\u6293\u7684\u7adf\u662f\u6a80\u7fbd\u51b2\u7684\u7435\u7436\u9aa8\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u600e\u80fd\u8ba9\u4ed6\u634f\u788e\u7435\u7436\u9aa8\uff0c\u53ea\u597d\u5728\u638c\u6cd5\u4e2d\u65bd\u5c55\u64d2\u62ff\u5316\u89e3\u4e4b\u6280\uff0c\u53cd\u626d\u4ed6\u7684\u81c2\u5f2f\u5173\u8282\u3002\u8fd9 \u4e00\u4e0b\u82e5\u662f\u7ed9\u626d\u4e2a\u6b63\u7740\uff0c\u975e\u5f97\u8131\u81fc\u4e0d\u53ef\u3002\u866c\u9aef\u6c49\u5b50\u8bc6\u5f97\u5389\u5bb3\uff0c\u659c\u9000\u4e24\u6b65\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8981 \u62da\u547d\u5417\uff1f\u90a3\u5c31\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d\u58f0\u51fa\u638c\u53d1\uff0c\u52bf\u5982\u5954\u96f7\uff0c\u638c\u98ce\u9707\u5f97\u9644\u8fd1\u7684\u4e24\u5f20\u684c\u5b50\u90fd\u7ffb\u5012 \u4e86\u3002\u53cc\u638c\u76f8\u4ea4\uff0c\u866c\u9aef\u6c49\u5b50\u662f\u8eab\u5f71\u4e00\u6643\uff0c\u6a80\u7fbd\u51b2\u5374\u5df2\u63a5\u8fde\u9000\u51fa\u4e09\u56db\u6b65\uff01\u6a80\u7fbd\u51b2\u4e00\u63a5\u6b64\u4eba\u638c\u529b\uff0c \u4fbf\u77e5\u4ed6\u7528\u7684\u662f\u6df7\u5143\u4e00\u65e0\u529f\uff0c\u4e0d\u7981\u6697\u6697\u8d77\u7591\uff0c\u539f\u6765\u4ed6\u521a\u624d\u672c\u6765\u53ef\u4ee5\u53cd\u5c06\u90a3\u866c\u9aef\u6c49\u5b50\u7684\u5173\u8282\u626d\u65ad \u7684\uff0c\u4f46\u90a3\u6c49\u5b50\u5374\u53cd\u800c\u8d23\u4ed6\u662f\u201c\u8981\u62fc\u547d\u201d\uff0c\u597d\u50cf\u6839\u672c\u4e0d\u77e5\u4ed6\u4e1a\u5df2\u624b\u4e0b\u7559\u60c5\u3002\u800c\u4e14\u866c\u9aef\u6c49\u5b50\u7684\u7b2c \u4e00\u62db\u5c31\u662f\u8981\u634f\u788e\u6a80\u7fbd\u51b2\u7684\u7435\u7436\u9aa8\uff0c\u65e9\u5df2\u662f\u201c\u624b\u4e0b\u65e0\u60c5\u201d\u7684\u4e86\u3002\u4f46\u4ed6\u5374\u76f4\u5230\u53d1\u51fa\u4e86\u6df7\u5143\u4e00\u65e0 \u529f\uff0c\u624d\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u8bf4\u6210\u4e86\u597d\u50cf\u662f\u56e0\u4e3a\u6a80\u7fbd\u51b2\u8981\u548c\u4ed6\u62fc\u547d\uff0c\u624d\u903c\u4e86\u4f7f\u6740\u624b\u7684\u3002\u201c\u96be\u9053\u4ed6\u7adf\u662f \u60f3\u6740\u4eba\u706d\u53e3\uff1f\u201d\u4f46\u6b64\u9645\u4ed6\u5df2\u662f\u65e0\u6687\u591a\u60f3\u4e86\uff0c\u53ea\u597d\u8d76\u5feb\u8bf4\u9053\uff1a\u201c\u9999\u4e3b\uff0c\u4f60\u4e00\u5b9a\u662f\u8bef\u4f1a\u4e86\uff0c\u6211\u4e0d \u662f\u8d1d\u5b50\uff0c\u4e5f\u4e0d\u662f\u5978\u7ec6\uff0c\u6628\u5929\u6211\u5df2\u7ecf\u548c\u949f\u8001\u524d\u8f88\u8bf4\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u9a97\u5f97\u4e86\u949f\u4e0d\u9e23\uff0c\u9a97\u4e0d\u4e86\u6211\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u6765\u7684\u6b63\u662f\u94c1\u7b14\u4e66\u751f\u6587 \u9038\u51e1\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e0d\u9a97\u4f60\uff0c\u5978\u7ec6\u53e6\u6709\u5176\u4eba\uff0c\u662f\u6628\u5929\u548c\u53f2\u6d69\u5728\u4e00\u8d77\u7684\u90a3\u4e2a\u5c11\u5e74\u3002\u201d \u3000\u3000\u6587\u9038\u51e1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u4eba\u662f\u5978\u7ec6\uff0c\u6211\u4e5f\u76f8\u4fe1\u3002\u4f46\u636e\u6211\u6240\u77e5\uff0c\u6628\u5929\u665a\u4e0a\uff0c\u4f60\u548c\u90a3\u4e2a\u5978\u7ec6 \u6b63\u662f\u5728\u5cb3\u575f\u79c1\u4f1a\uff01\u201d \u3000\u3000\u8679\u9aef\u6c49\u5b50\u653b\u5f97\u6b63\u6025\uff0c\u6a80\u7fbd\u51b2\u597d\u4e0d\u5bb9\u6613\u624d\u5316\u89e3\u4e86\u4ed6\u7684\u8fde\u73af\u4e09\u62db\u653b\u52bf\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u6211 \u548c\u90a3\u4eba\u76f8\u4f1a\uff0c\u90a3\u4f60\u4e5f\u8be5\u77e5\u9053\u6211\u66fe\u7ecf\u548c\u90a3\u4e24\u4e2a\u9e70\u722a\u5b59\u6253\u4e00\u67b6\u3002\u201d \u3000\u3000\u5d14\u6d69\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u628a\u5b8b\u56fd\u7684\u516c\u5dee\u8bf4\u6210\u9e70\u722a\u5b59\uff0c\u8fd8\u4e0d\u662f\u5978\u7ec6\uff0c\u54fc\uff0c\u96be\u9053\u6211\u4eec\u7684\u98ce\u9999\u4e3b\u8fd8\u4f1a \u51a4\u6789\u4f60\u3002\u201d \u3000\u3000\u5d14\u6cbb\u53e3\u4e2d\u8bf4\u51fa\u4e86\u4e00\u4e2a\u201c\u98ce\u201d\u5b57\uff0c\u6a80\u7fbd\u51b2\u53ef\u5c31\u60f3\u8d77\u6765\u4e86\uff1a\u201c\u83ab\u975e\u8fd9\u866c\u9aef\u6c49\u5b50\u5c31\u662f\u98ce\u706b\u9f99\uff01 \u3000\u3000\u98ce\u706b\u9f99\u662f\u4e10\u5e2e\u5e2e\u4e3b\u5c1a\u6606\u9633\u7684\u5927\u5f1f\u5b50\uff0c\u5c1a\u6606\u9633\u5e74\u7eaa\u5df2\u8001\uff0c\u8fd1\u5e74\u4e10\u5e2e\u4e2d\u7684\u4e8b\u52a1\u8005\u662f\u7531\u4ed6\u4ee3\u7ba1 \u7684\u3002\u4e10\u5e2e\u53e6\u6709\u4e09\u4e2a\u4eba\u7ec3\u6210\u6df7\u5143\u4e00\u65e0\u529f\uff0c\u4e00\u4e2a\u662f\u5e2e\u4e3b\u5c1a\u6606\u9633\uff0c\u4e00\u4e2a\u662f\u957f\u8001\u6731\u4e39\u9e64\uff0c\u8fd8\u6709\u4e00\u4e2a\u5c31 \u662f\u4ed6\u4e86\u3002\u4ed6\u4e0d\u4f46\u5728\u6b66\u529f\u65b9\u9762\u5f97\u5230\u5e08\u7236\u7684\u8863\u94b5\u771f\u4f20\uff0c\u54c1\u683c\u65b9\u9762\uff0c\u4ed6\u4e5f\u662f\u548c\u4ed6\u5e08\u7236\u4e00\u6837\uff0c\u53d7\u5230\u6c5f \u6e56\u540c\u9053\u5c0a\u656c\u7684\u3002\u6545\u800c\u672a\u6ee1\u4e09\u5341\u5c81\uff0c\u5c31\u6323\u6765\u4e86\u201c\u5927\u4fa0\u201d\u7684\u540d\u5934\uff0c\u5e2e\u5185\u5e2e\u5916\uff0c\u8c01\u90fd\u8ba4\u5b9a\u4e86\u4ed6\u5fc5\u5b9a \u662f\u7ee7\u4efb\u7684\u4e10\u5e2e\u7684\u5e2e\u4e3b\u65e0\u7591\u3002\u6a80\u7fbd\u51b2\u5728\u91d1\u4eac\u7684\u65f6\u5019\uff0c\u6ca1\u6709\u89c1\u8fc7\u98ce\u706b\u9f99\uff0c\u4f46\u98ce\u706b\u9f99\u7684\u540d\u58f0\u4ed6\u5f53\u7136 \u662f\u65e9\u5df2\u542c\u4eba\u8bf4\u8fc7\u7684\u4e86\u3002\u4ed6\u4e00\u76f4\u4e5f\u662f\u628a\u98ce\u706b\u9f99\u5f53\u4f5c\u201c\u4fa0\u4e49\u53ef\u98ce\u201d\u7684\u4eba\u7269\u7684\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u6000\u7591\u98ce\u706b\u9f99\u548c\u6731\u4e39\u9e64\u540c\u8c0b\uff0c\u4f46\u73b0\u5728\u8fd9\u4f4d\u201c\u98ce\u5927\u4fa0\u201d\u5374\u6b63\u662f\u62db\u62db\u72e0\u8fa3\uff0c\u5484\u5484\u903c\u4eba\uff0c \u8981\u53d6\u4ed6\u7684\u6027\u547d\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u4ee5\u653b\u5bf9\u653b\uff0c\u628a\u98ce\u706b\u9f99\u903c\u9000\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u666e\u901a\u516c\u5dee\uff0c\u4ed6\u4eec\u662f\u6c64\u601d\u9000\u7684\u624b \u4e0b\u3002\u6c64\u601d\u9000\u662f\u4e3b\u548c\u7684\uff0c\u5c31\u548c\u5f53\u5e74\u7684\u79e6\u6867\u4e00\u822c\uff01\u201d \u3000\u3000\u6587\u9038\u51e1\u51b7\u7b11\u9053\uff1a\u201c\u4eba\u521a\u5230\u4e34\u5b89\uff0c\u77e5\u9053\u7684\u4e8b\u60c5\u5012\u662f\u4e0d\u5c11\u5440\uff01\u201d\u563f\u563f\uff0c\u5f53\u5e74\u7684\u79e6\u6867\u5728\u5978\u8c0b\u672a \u66fe\u5927\u767d\u4e8e\u5929\u4e0b\u4e4b\u524d\uff0c\u4e5f\u66fe\u7ecf\u626e\u8fc7\u6b63\u4eba\u541b\u5b50\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\u3002\u5f53\u7136\u662f\u8ba4\u5b9a\u4ed6\u81f3\u5c11\u4e86\u662f\u6709\u5978\u7ec6\u7684\u5acc \u7591\u7684\u4e86\u3002 \u3000\u3000\u5d14\u6d69\u7684\u6b66\u529f\u672a\u5230\u4e00\u6d41\uff0c\u773c\u529b\u5374\u662f\u4e00\u6d41\uff0c\u98ce\u706b\u9f99\u653b\u52bf\u867d\u76db\uff0c\u4ed6\u5df2\u662f\u770b\u5f97\u51fa\u96be\u4ee5\u4e3a\u7ee7\u4e86\uff0c\u6025 \u5fd9\u53eb\u9053\uff1a\u201c\u6587\u5927\u4fa0\uff0c\u6349\u62ff\u5978\u7ec6\u65e0\u987b\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff0c\u6211\u770b\u8fd8\u662f\u65e9\u70b9\u4e86\u7ed3\u6b64\u4e8b\u5427\uff01\u201d\u6a80\u7fbd\u51b2\u4e00 \u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u975e\u9ed1\u767d\uff0c\u5c06\u6765\u603b\u4f1a\u6e05\u695a\u7684\u3002\u5bf9\u4e0d\u4f4f\uff0c\u6055\u6211\u4e0d\u5949\u966a\u4e86\uff01\u201d\u957f\u7b11\u58f0\u4e2d\uff0c\u53f3\u638c \u4e00\u62db\u201c\u94c1\u9501\u6a2a\u6c5f\u201d\uff0c\u6321\u4f4f\u98ce\u706b\u9f99\u7684\u653f\u52bf\uff0c\u5de6\u624b\u9a88\u6307\u5982\u621f\uff0c\u500f\u7684\u5c31\u70b9\u4e86\u98ce\u706b\u9f99\u7684\u7a74\u9053\u3002 \u3000\u3000\u98ce\u706b\u9f99\u8fd9\u4e00\u62db\u662f\u53cc\u638c\u9f50\u53d1\uff0c\u800c\u4e14\u662f\u5df2\u7ecf\u7528\u4e0a\u4e86\u6df7\u5143\u4e00\u529f\u7684\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u60f3\u5230\u6a80\u7fbd\u51b2\u5355\u51ed \u4e00\u638c\u4e4b\u529b\u5c31\u53ef\u4ee5\u62b5\u6321\u4ed6\u7684\u6df7\u5143\u4e00\u529f\uff0c\u6240\u4ee5\u4e1d\u6beb\u4e0d\u52a0\u9632\u5907\uff0c\u9661\u7136\u95f4\uff0c\u53ea\u89c9\u80f8\u53e3\u4e00\u9ebb\uff0c\u795e\u7167\u7a74\u5df1 \u662f\u7ed9\u70b9\u4e2a\u6b63\u7740\u3002\u795e\u7167\u7a74\u662f\u5c11\u9633\u5c11\u9634\u4e24\u5927\u7ecf\u8109\u4ea4\u4f1a\u4e4b\u5904\u7684\u4e00\u4e2a\u9ebb\u7a74\uff0c\u6362\u4e86\u522b\u4eba\uff0c\u4e00\u88ab\u70b9\u4e2d\uff0c\u65e9 \u5df2\u4e0d\u80fd\u52a8\u5f39\uff0c\u98ce\u706b\u9f99\u7ed5\u662f\u529f\u529b\u6df1\u539a\uff0c\u4ea6\u5df2\u56db\u80a2\u9178\u9ebb\uff0c\u6447\u6447\u6b32\u5760\uff0c\u6587\u9038\u51e1\u5927\u5403\u4e00\u60ca\uff0c\u8d76\u5fd9\u4e0a\u524d \u5e2e\u4ed6\u3002\u98ce\u706b\u9f99\u53eb\u9053\uff1a\u201c\u522b\u7ba1\u6211\uff0c\u8ffd\u5978\u7ec6\uff01\u201d\u5d14\u6d69\u8ffd\u51fa\u95e8\u5916\u3002\u5ffd\u7136\u88ab\u4e2a\u77ee\u5b50\u4e00\u628a\u63ea\u4f4f\u3002\u8fd9\u77ee\u5b50 \u662f\u5357\u5bab\u9020\uff0c\u4ed6\u662f\u4e00\u65e9\u5c31\u6765\u7aa5\u89c6\u4e86\u3002\u4ed6\u77e5\u9053\u5d14\u6d69\u7684\u8eab\u4efd\uff0c\u4e0d\u8fc7\u4ed6\u8fd8\u672a\u77e5\u9053\u5d14\u6d69\u4e0e\u6a80\u7fbd\u51b2\u662f\u53cb\u662f \u654c\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u672c\u6765\u5df2\u7ecf\u5728\u524d\u5934\uff0c\u6b64\u9645\u5ffd\u7136\u56de\u8fc7\u8eab\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8981\u7684\u662f\u6211\uff0c\u5c06\u4ed6\u653e\u4e0b\uff01\u201d \u3000\u3000\u5357\u5bab\u9020\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u679c\u7136\u662f\u4e00\u4f19\u3002\u201d\u63ea\u7740\u5d14\u6d69\uff0c\u8fce\u4e0a\u8dd1\u51fa\u6765\u7684\u6a80\u7fbd\u51b2\uff0c\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u82e5\u4e0d\u987e\u4f60\u7684\u670b\u53cb\uff0c\u90a3\u5c31\u6253\u5427\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u4e3a\u6a80\u7fbd\u51b2\u4e0d\u6562\u6253\u7684\uff0c\u90a3\u77e5\u6a80\u7fbd\u51b2\u8bf4\u6253\u5c31\u6253\u3002\u78b0\u7684\u4e00\u62f3\uff0c\u6253\u5728\u5d14\u6d01\u8eab\u4e0a\u3002 \u3000\u3000\u5947\u602a\u7684\u4e8b\u60c5\u53d1\u751f\u4e86\uff0c\u62f3\u5934\u662f\u6253\u5728\u5d14\u6cbb\u7684\u8eab\u4e0a\uff0c\u5012\u4e0b\u53bb\u7684\u5374\u662f\u5357\u5bab\u9020\u3002 \u3000\u3000\u5d14\u6d69\u7ed9\u90a3\u80a1\u529b\u9053\u649e\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u843d\u5728\u4e09\u4e08\u5f00\u5916\uff0c\u80cc\u5fc3\u6709\u70ed\u8fa3\u8fa3\u7684\u611f\u89c9\uff0c\u4f46\u5e76\u4e0d\u75bc\u75db\uff0c\u811a \u5c16\u4e00\u7740\u5730\u5c31\u7ad9\u7a33\u4e86\uff0c\u4ed6\u9690\u9690\u542c\u5f97\u597d\u50cf\u6709\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\uff0c\u4ed6\u7ed9\u5413\u50bb\u4e86\uff0c\u4e0d\u81ea\u89c9\u5f97\u53cd\u624b\u6478\u4e00\u6a21 \u81ea\u5df1\u7684\u80cc\u810a\uff0c\u53d1\u89c9\u81ea\u5df1\u5e76\u6ca1\u53d7\u4f24\u3002\u8fd9\u624d\u4e0d\u7981\u54d1\u7136\u5927\u5956\u201c\u788e\u7684\u5f53\u7136\u4e0d\u662f\u6211\u9aa8\u5934\uff0c\u5426\u5219\u6211\u5982\u4f55\u8fd8 \u80fd\u633a\u76f4\u8170\u677f\u3002\u201d \u3000\u3000\u5357\u5bab\u9020\u7ed9\u51fb\u5012\u5730\u4e0a\uff0c\u5634\u89d2\u8fd8\u5728\u6dcc\u7740\u9c9c\u8840\uff0c\u4e00\u6839\u808b\u9aa8\u4e5f\u7ed9\u6253\u65ad\u4e86\u3002 \u3000\u3000\u5d14\u6d69\u83ab\u540d\u5176\u5999\uff0c\u201d\u600e\u7684\u4ed6\u4e5f\u9020\u53cd\u800c\u5e2e\u6211\uff1f\u201d \u3000\u3000\u6587\u9038\u51e1\u8ffd\u4e86\u51fa\u6765\uff0c\u89c1\u5d14\u6d69\u6ca1\u4e8b\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u7406\u8fd9\u53ae\uff0c\u5148\u8ffd\u5978\u7ec6\uff01\u201d\u5357\u5bab\u9020\u542c \u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u521a\u624d\u7684\u731c\u60f3\u5b8c\u5168\u9519\u4e86\u3002\u4ed6\u867d\u7136\u8fd8\u6709\u70b9\u7591\u60d1\uff0c\u4f46\u5df2\u662f\u5927\u559c\u8fc7\u671b\uff0c \u201c\u4e0d\u7ba1\u4ed6\u4eec\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6709\u6587\u9038\u51e1\u4f5c\u5e2e\u624b\uff0c\u8fd8\u6015\u90a3\u5c0f\u5b50\u98de\u5f97\u4e0a\u5929\uff1f\u201d\u4ed6\u4e5f\u771f\u662f\u987d\u56fa\uff0c\u54ac\u7740\u7259 \u6839\uff0c\u8ddf\u7740\u53bb\u8ffd\u3002 \u3000\u3000\u8857\u4e0a\u8fd8\u6ca1\u6709\u884c\u4eba\uff0c\u6a80\u7fbd\u51b2\u4ee5\u201c\u9694\u7269\u4f20\u529f\u201d\u51fb\u5012\u4e86\u5357\u5bab\u9020\uff0c\u6025\u5fd9\u5c31\u8dd1\u3002 \u3000\u3000\u6709\u7406\u8bf4\u4e0d\u6e05\u3002\u4ed6\u7684\u5fc3\u91cc\u5f53\u7136\u751a\u4e3a\u82e6\u607c\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u770b\u6765\u6211\u53ea\u6709\u53bb\u627e\u738b\u5b87\u5ead\uff0c\u5411\u4ed6\u7533 \u8fa9\uff0c\u6c42\u4ed6\u66ff\u6211\u6d17\u8131\u7f6a\u540d\u4e86\u3002\u4ed6\u548c\u5e08\u7236\u4ea4\u60c5\u5f88\u6df1\uff0c\u6211\u7684\u59b9\u59b9\u53c8\u5728\u4ed6\u90a3\u91cc\uff0c\u6599\u60f3\u4ed6\u662f\u5e94\u8be5\u76f8\u4fe1\u6211 \u7684\u3002\u4f46\u600e\u6837\u624d\u80fd\u8d70\u51fa\u4e34\u5b89\u59ae\uff1f\u201d \u3000\u3000\u4ed6\u60d8\u60d8\u524d\u884c\uff0c\u7a7f\u8fc7\u4e86\u4e00\u6761\u5c0f\u8857\uff0c\u8857\u8fb9\u6709\u4e2a\u5efa\u7b51\u5de5\u5730\uff0c\u5de5\u5730\u4e0a\u6709\u5806\u6728\u6599\u3002\u6728\u6599\u540e\u9762\u5ffd\u7136\u8dd1 \u51fa\u4e00\u4e2a\u5c0f\u59d1\u5a18\uff0c\u7b11\u563b\u563b\u7684\u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u8c2d\u5927\u54e5\uff1b\u6211\u8eb2\u5728\u8fd9\u91cc\u770b\u4f60\u6253\u67b6\u5462\uff0c\u4f60\u6253\u5f97\u771f\u68d2\uff01\u201d\u8fd9 \u5c0f\u59d1\u5a18\u4e0d\u7528\u8bf4\u5f53\u7136\u662f\u949f\u7075\u79c0\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u6dd8\u6c14\uff0c\u8fd8\u4e0d\u8d76\u7d27\u56de\u5bb6\u53bb\uff0c\u4f60\u7684\u7237\u7237\u5728\u7b49\u7740\u4f60\u5462\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u7237\u7237\u6b63\u662f\u56e0\u4e3a\u653e\u5fc3\u4e0d\u4e0b\u4f60\uff0c\u624d\u53eb\u6211\u6765\u5e2e\u4f60\u7684\u3002\u201d \u3000\u3000\u6853\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u4e8b\u4e0d\u5fc5\u4f60\u7ba1\uff0c\u4f60\u4e5f\u7ba1\u4e0d\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u5e2e\u4f60\u6253\u67b6\u7684\u672c\u4e8b\u6211\u6ca1\u6709\uff0c\u4f46\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u5e2e\u4f60\u9003\u8d70\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u9003\u8d70\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8c2d\u5927\u54e5\uff0c\u4f60\u522b\u7792\u6211\uff0c\u6211\u77e5\u9053\u7684\u5df1\u7ecf\u6700\u5c11\u6709\u4e24\u5e2e\u4eba\u8981\u548c\u4f60\u4e3a\u96be\u4e86\uff0c\u662f\u4e0d \u662f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u6211\u60f3\u4e0d\u5230\u5176\u4e2d\u7684\u4e00\u5e2e\u7adf\u662f\u4e10\u5e2e\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u770b\u5440\uff0c\u4f60\u548c\u5b98\u5e9c\u4f5c\u5bf9\uff0c\u4e10\u5e2e\u53c8\u8981\u62ff\u4f60\uff0c\u5728\u4e34\u5b89\u4f60\u8eb2\u4e5f\u8eb2\u4e0d\u8fc7\u7684\u3002\u5feb\u8bf4\uff0c\u4f60 \u8981\u4e0a\u54ea\u513f\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u5f53\u771f\u6709\u529e\u6cd5\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u968f\u6211\u6765\uff01\u201d\u5979\u5df2\u7ecf\u5f00\u59cb\u8d70\u5728\u524d\u5934\u4e86\uff0c\u6a80\u7fbd\u51b2\u53ea\u597d\u8ddf\u7740\u5979\u8d70\u3002 \u3000\u3000\u5979\u5e26\u7740\u6a80\u7fbd\u51b2\u6284\u5c0f\u8def\u8d70\u51fa\u90ca\u533a\uff0c\u6cbf\u7740\u6816\u971e\u5cad\u7684\u5c71\u8fb9\u5f80\u5317\u8d70\uff0c\u4e0d\u4e45\u5c31\u770b\u89c1\u53e6\u4e00\u5ea7\u5c71\u3002 \u3000\u3000\u201c\u8c2d\u5927\u54e5\uff0c\u4f60\u770b\u8fd9\u5ea7\u5c71\u50cf\u4e0d\u50cf\u4e00\u53ea\u51e4\u51f0\uff1f\u201d \u3000\u3000\u8fd9\u5ea7\u5c71\u5317\u63a5\u4e07\u677e\u5cad\uff0c\u4e1c\u9760\u5357\u5c4f\u5c71\uff0c\u4e24\u8fb9\u7684\u5c71\u9e93\u5de6\u8fbe\u897f\u5b50\u6e56\u8fb9\uff0c\u53f3\u8fbe\u94b1\u5858\u6c5f\u5cb8\uff0c\u6a80\u7fbd\u51b2\u4e3e \u5934\u56db\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u5f88\u8c61\u4e00\u5934\u98de\u5728\u6c5f\u6e56\u4e4b\u95f4\u7684\u51e4\u51f0\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u5ea7\u5c71\u5c31\u53eb\u505a\u51e4\u51f0\u5c71\uff0c\u4f60\u770b\u5c71\u4e0a\u9690\u73b0\u7684\u4ead\u53f0\u697c\u9601\u4e48\uff1f\u201d\u6a80\u7fbd\u51b2\u968f\u53e3\u95ee\u9053\uff1a \u201c\u662f\u54ea\u5bb6\u5bcc\u8d35\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u662f\u7687\u5bab\u5462\uff01\u7687\u5e1d\u8001\u5b50\u5c31\u4f4f\u5728\u90a3\u91cc\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e00\u60ca\u9053\uff1a\u201c\u662f\u7687\u5bab\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5bb3\u6015\uff0c\u7687\u5bab\u7684\u5b88\u536b\u90fd\u5728\u5c71\u4e0a\uff0c\u5728\u5c71\u4e0b\u5f80\u6765\u7684\u90fd\u662f\u9644\u8fd1\u7684\u6e14\u6c11\uff0c\u4ed6\u4eec \u4e0d\u4f1a\u8d70\u4e0a\u5c71\u53bb\uff0c\u5c71\u4e0a\u7684\u5b88\u536b\u4e5f\u4e0d\u4f1a\u7279\u5730\u4e0b\u6765\u76d8\u95ee\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u604d\u7136\u5927\u609f\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u61c2\u5f97\u5175\u6cd5\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5657\u55e4\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u771f\u662f\u8bf4\u5f97\u5947\u602a\u4e86\uff0c\u6211\u61c2\u5f97\u4ec0\u4e48\u5175\u6cd5\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5175\u6cd5\u6709\u4e91\uff0c\u5b9e\u8005\u865a\u4e4b\uff0c\u865a\u8005\u5b9e\u4e4b\uff0c\u6c64\u601d\u9000\u7edd\u60f3\u4e0d\u5230\u6211\u6562\u4e8e\u4ece\u5929\u5b50\u811a\u4e0b\u8d70\u51fa \u4e34\u5b89\uff0c\u6240\u4ee5\u4ed6\u4e5f\u4e0d\u4f1a\u5728\u8fd9\u91cc\u8bbe\u7acb\u54e8\u5c97\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u5929\u5b50\u811a\u4e0b\u8fd8\u8981\u4ec0\u4e48\u5730\u65b9\u5b98\u5e9c\u7acb\u54e8\u5c97\uff1f\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u522b\u6211\u4e71\u6234\u9ad8\u5e3d\uff0c\u6211\u6839\u672c \u6ca1\u6709\u60f3\u5230\u4f60\u8bf4\u7684\u8fd9\u2014\u5c42\uff0c\u6211\u53ea\u662f\u56e0\u4e3a\u4ece\u8fd9\u91cc\u53ef\u4ee5\u8dd1\u5f80\u94b1\u5858\u6c5f\uff0c\u94b1\u5858\u6c5f\u4e0a\u6709\u6211\u7684\u4e00\u6761\u5c0f\u8239\u3002\u5927 \u54e5\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u7239\u672c\u6765\u662f\u3001\u662f\u4e2a\u8239\u592b\uff0c\u4ed6\u6b7b\u4e86\uff0c\u7237\u7237\u7779\u7269\u4f24\u5fc3\uff0c\u624d\u8981\u6211\u8ddf\u4ed6\u4e0a\u5cb8\uff0c\u6539\u884c\u5356\u5531 \u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53bb\u94b1\u5858\u6c5f\u4f5c\u751a\uff1f\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u7237\u7237\u8bf4\uff0c\u4f60\u82e5\u65e0\u6cd5\u53ef\u60f3\uff0c\u90a3\u5c31\u552f\u6709\u53bb\u6c42\u738b \u5b87\u5ead\u4e86\u3002\u738b\u5b87\u5ead\u4f60\u77e5\u9053\u5417\uff1f\u201d\u6a80\u7fbd\u51b2\u559c\u9053\uff1a\u201c\u77e5\u9053\uff0c\u539f\u6765\u4f60\u7237\u7237\u4e5f\u662f\u8fd9\u6837\u60f3\u3002\u90a3\u5c31\u53bb\u5427\u3002\u201d \u3000\u3000\u518d\u8d70\u4e00\u7a0b\uff1b\u5df2\u7ecf\u53ef\u4ee5\u770b\u5230\u77d7\u7acb\u94b1\u5858\u6c5f\u53e3\u7684\u767d\u5854\u4e86\u3002 \u3000\u3000\u767d\u5854\u7684\u5317\u8fb9\uff0c\u8fd8\u6709\u4e00\u5ea7\u5b9d\u5854\u548c\u4ed6\u9065\u9065\u76f8\u5bf9\uff0c\u90a3\u5c31\u662f\u8457\u540d\u7684\u516d\u5408\u5854\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u516d\u548c\u5854\u6211\u77e5\u9053\uff0c\u6211\u5ff5\u8fc7\u4e00\u9996\u516d\u548c\u5854\u7684\u8bd7\uff0c\u6c5f\u5206\u5434\u8d8a\u7eff\u6f2b\u6f2b\uff0c\u95f2\u5411\u6d6e\u5c60\u7edd\u9876 \u770b\u3002\u76ee\u89c8\u94b1\u5858\u6b8a\u89c9\u5c0f\uff0c\u8eab\u6e38\u7389\u5b87\u4e0d\u77e5\u5bd2\u3002\u8fd9\u5ea7\u767d\u5854\u5927\u6982\u6ca1\u516d\u548c\u5854\u90a3\u4e48\u9ad8\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u867d\u7136\u6ca1\u516d\u548c\u5854\u90a3\u4e48\u51fa\u540d\uff0c\u4f46\u542c\u8bf4\u5b83\u662f\u5728\u4e09\u767e\u5e74\u524d\u5efa\u9020\u7684\uff0c\u6bd4\u516d\u548c\u5854\u66f4\u53e4 \u8001\u3002\u767d\u5854\u4e5f\u6709\u4e00\u9996\u8bd7\uff0c\u662f\u4eca\u4eba\u5199\u7684\u3002\u6216\u8005\u6ca1\u6709\u4f60\u5ff5\u7684\u90a3\u9996\u9898\u516d\u548c\u5854\u8bd7\u51fa\u540d\uff0c\u4f46\u5728\u4e34\u5b89\uff0c\u5374\u4e5f \u5dee\u4e0d\u591a\u662f\u5bb6\u55bb\u6237\u6653\u7684\u3002\u6211\u5728\u897f\u6e56\u5356\u5531\uff0c\u6709\u4e00\u6b21\u5c31\u56e0\u4e3a\u5531\u8fd9\u9996\u8bd7\u5012\u4e86\u9709\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u5531\u4e00\u9996\u6d41\u884c\u6c11\u95f4\u7684\u8bd7\u4e5f\u4f1a\u5012\u9709\uff0c\u90a3\u6211\u5012\u60f3\u542c\u542c\u8fd9\u9996\u8bd7\u662f\u600e\u6837\u5199\u7684 \u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5ff5\u9053\uff1a\u201c\u767d\u642d\u6865\u8fb9\u5356\u5730\u7ecf\uff0c\u957f\u4ead\u77ed\u9a7f\u751a\u5206\u660e\uff0c\u5982\u4f55\u53ea\u8bf4\u4e34\u5b89\u8def\uff0c\u4e0d\u8bf4\u4e2d\u539f\u6709\u51e0 \u7a0b\uff1f\u201d \u3000\u3000\u201c\u5730\u7ecf\u201d\u662f\u4e00\u79cd\u6807\u660e\u6709\u91cc\u7a0b\u7684\u5730\u56fe\uff0c\u767d\u5854\u6865\u8fb9\u5e38\u6709\u5404\u5730\u8239\u53ea\u6765\u5f80\uff0c\u5546\u4eba\u5728\u90a3\u91cc\u51fa\u552e\u7684 \u201c\u5730\u7ecf\u201d\uff0c\u628a\u4ece\u5404\u5730\u524d\u5f80\u4e34\u5b89\u7684\u201c\u957f\u4ead\u201d\u201c\u77ed\u9a7f\u201d\u90fd\u63cf\u7ed8\u5f97\u5f88\u8be6\u7ec6\uff0c\u53ef\u662f\u5e7f\u5927\u7684\u4e2d\u539f\u5374\u6ca1\u6709 \u753b\u8fdb\u53bb\u3002\u201c\u5982\u4f55\u53ea\u8bf4\u4e34\u5b89\u8def\uff0c\u4e0d\u8bf4\u4e2d\u539f\u6709\u51e0\u7a0b\uff1f\u201d\u5b9e\u662f\u542b\u6709\u5bf9\u5357\u5b8b\u7518\u5fc3\u504f\u5b89\u5c40\u9762\u7684\u6124\u61d1\u548c\u8bbd \u523a\u7684\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u90a3\u6b21\u6211\u81ea\u4f5c\u4e3b\u5f20\u5531\u4e86\u8fd9\u9996\u8bd7\uff0c\u6709\u4e2a\u5b98\u513f\u9a82\u6211\uff0c\u6709\u591a\u5c11\u65b0\u8bd7\u65b0\u8bcd\u4f60\u4e0d\u5531\uff0c\u504f \u504f\u5531\u8fd9\u9996\u8bbd\u523a\u671d\u5ef7\u7684\u8bd7\uff0c\u82e5\u4e0d\u662f\u770b\u4f60\u5e74\u7eaa\u5c0f\uff0c\u975e\u628a\u4f60\u9001\u5b98\u7a76\u529e\u4e0d\u53ef\u3002\u7ed3\u679c\u6211\u4e00\u6587\u94b1\u5f97\u4e0d\u5230\uff0c \u5e73\u767d\u7ed9\u4ed6\u9a82\u4e86\u4e00\u987f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9ed8\u7136\u65e0\u8bed\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u91d1\u56fd\u4fb5\u5360\u4e86\u4e2d\u539f\u4e00\u5927\u7247\u571f\u5730\uff0c\u4e5f\u96be\u602a\u5b8b\u56fd\u767e\u59d3\u6124\u6168\uff0c\u8fde \u5e26\u5bf9\u4ed6\u4eec\u90a3\u4e2a\u4e0d\u60dc\u5c48\u8fb1\u6c42\u548c\u7684\u7687\u5e1d\u4e5f\u4e0d\u6ee1\u4e86\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u4e00\u534a\u662f\u91d1\u4eba\uff0c\u4e00\u534a\u662f\u5b8b\u4eba\uff0c\u5fc3\u60c5\u6b8a \u4e3a\u90c1\u90c1\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8c2d\u5927\u54e5\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u6211\u5374\u9a97\u4e86\u4f60\u3002\u6211\u5b9e\u662f\u59d3\u6a80\uff0c\u4e0d\u662f\u59d3\u8c2d\u3002\u6211\u8bf4\u6211\u662f \u6c49\u4eba\uff0c\u90a3\u4e5f\u53ea\u6709\u4e00\u534a\u662f\u771f\u7684\u3002\u6211\u7684\u5a18\u4eb2\u662f\u5b8b\u56fd\u4eba\uff0c\u6211\u7684\u7239\u7239\u5374\u662f\u91d1\u4eba\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u59d3\u8c2d\u59d3\u6a80\u90a3\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u53ea\u8981\u4f60\u662f\u597d\u4eba\u5c31\u884c\u4e86\uff0c\u91d1\u56fd\u4e5f\u6709\u597d\u4eba\u3002\u4f60\u662f\u6765\u5e2e \u6211\u4eec\u7684\uff0c\u4e0d\u662f\u6765\u548c\u6211\u4eec\u6253\u4ed7\u7684\u3002\u7eb5\u7136\u4f60\u7684\u5a18\u4eb2\u4e5f\u662f\u91d1\u4eba\uff0c\u6211\u4e00\u6837\u4f1a\u5bf9\u4f60\u597d\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a \u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u5012\u5f88\u660e\u767d\u4e8b\u7406\u3002\u201d\u5ffd\u547c\u5f97\u6f6e\u58f0\u5927\u4f5c\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6765\u5230\u6c5f\u8fb9\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u6211\u9a7e\u821f\u7684\u672c\u9886\uff0c\u5176\u5b9e\u6bd4\u6211\u5531\u66f2\u7684\u672c\u9886\u8981\u597d\u5f97\u591a\u3002\u94b1\u5858\u6c5f\u7684\u6d6a\u6f6e\u867d\u7136\u5389 \u5bb3\uff0c\u4f46\u73b0\u5728\u8fd8\u662f\u65e9\u6f6e\uff0c\u65e9\u6f6e\u6700\u5f31\uff0c\u4f60\u5927\u53ef\u653e\u5fc3\uff0c\u8bf7\u4e0a\u8239\u5427\u3002\u201d \u3000\u3000----------- \u3000\u3000\u7fbd\u751f\u5802 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u592a\u6e56\u6ce2\u6d9b \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u56db\u56de\u3000\u592a\u6e56\u6ce2\u6d9b \u3000\u3000\u592a\u6e56\u897f\u6d1e\u5c71\u4e0a\u738b\u5b87\u5ead\u7684\u5c71\u5be8\u91cc\uff0c\u8d3a\u5ba2\u4e91\u96c6\u3002\u738b\u5b87\u5ead\u662f\u4e03\u5341\u4e8c\u5bb6\u6c34\u5be8\u7684\u603b\u5be8\u4e3b\uff0c\u6c34\u9646\u4e24\u8def \u7684\u9ed1\u9053\u597d\u6c49\u52a0\u4e0a\u6c5f\u5357\u4fa0\u4e49\u7684\u8c6a\u6770\uff0c\u5dee\u4e0d\u591a\u5168\u90fd\u6765\u4e86\u3002\u800c\u738b\u5b87\u5374\u8fd8\u672a\u89c1\u5728\u5bff\u5802\u9732\u9762\u3002 \u3000\u3000\u6709\u4eba\u7a83\u7a83\u79c1\u8bae\uff1a\u201c\u5df2\u662f\u5348\u65f6\u4e86\uff0c\u738b\u603b\u5be8\u4e3b\u4e3a\u4f55\u8fd8\u4e0d\u89c1\u51fa\u6765\u63a5\u53d7\u795d\u8d3a\uff1f\u4f60\u77a7\uff0c\u9ed1\u77f3\u5e84\u7684\u77f3 \u5e84\u4e3b\u548c\u5e38\u5dde\u7684\u91d1\u5200\u5218\u4e09\u7237\u90fd\u5df2\u5230\u4e86\u3002\u201d\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u6c5f\u5357\u6b66\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u4eba\u7269\u3002\u5f26\u5916\u4e4b\u97f3\uff0c \u51ed\u7740\u8fd9\u4e24\u4e2a\u4eba\u7684\u8eab\u4efd\uff0c\u738b\u5b87\u5ead\u867d\u7136\u662f\u4e03\u5341\u4e8c\u5bb6\u6c34\u5be8\u7684\u603b\u5be8\u4e3b\uff0c\u4f3c\u4e4e\u4e5f\u8be5\u4eb2\u81ea\u51fa\u6765\u62db\u547c\u624d\u5bf9\u3002 \u53e6\u5916\u4e00\u4e2a\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8981\u62db\u547c\u53e6\u4e00\u4e2a\u6765\u5934\u66f4\u5927\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u94c1\u7b14\u4e66\u751f\u6587\u9038\u51e1\uff0c\u542c\u8bf4\u738b\u5be8\u4e3b\u662f\u51c6\u5907\u63a8\u4e3e\u4ed6\u505a\u6c5f\u5357\u7684\u6b66\u6797\u76df\u4e3b\u7684\u3001\u6b64\u523b\u738b\u5be8\u4e3b\u6b63\u5728\u966a \u4ed6\u5728\u5bc6\u5ba4\u5546\u8c08\u3002\u201d \u3000\u3000\u201c\u54e6\uff0c\u539f\u6765\u662f\u6587\u5927\u4fa0\u4ea6\u5df2\u6765\u4e86\u4e48\uff1f\u4f46\u6211\u5374\u5728\u70b9\u5947\u602a\u2014\u2014\u201d \u3000\u3000\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u5947\u602a\u738b\u5be8\u4e3b\u4e3a\u4e86\u62db\u5f85\u4ed6\u7684\u7f18\u6545\u800c\u51b7\u843d\u522b\u7684\u5bbe\u5ba2\uff0c\u53ea\u662f\u5947\u602a\u6587\u5927\u4fa0\u8fd9\u6b21\u6765\u5f97\u597d \u50cf\uff0c\u597d\u50cf\u2026\u2026\u6709\u70b9\uff0c\u6709\u70b9\u2026\u201d \u3000\u3000\u8fd9\u4eba\u541e\u541e\u5410\u5410\uff0c\u597d\u50cf\u6709\u8bdd\u4e0d\u6562\u76f4\u8bf4\u3002\u4ed6\u7684\u670b\u53cb\u4ea6\u5df2\u4f1a\u610f\u4e86\uff0c\u51e0\u4e4e\u662f\u548c\u4ed6\u54ac\u7740\u8033\u6735\u7684\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6587\u5927\u4fa0\u8fd9\u6b21\u6765\u5f97\u597d\u50cf\u6709\u70b9\u9b3c\u9b3c\u795f\u795f\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\u8bf4\u4ed6\u9b3c\u5d07\uff0c\u201d\u90a3\u4eba\u4e5f\u538b\u4f4e\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f46\u6587\u5927\u4fa0\u7684\u4e3a\u4eba\u4f60\u4e5f\u77e5\u9053\uff0c\u4ed6\u867d\u7136\u662f\u5927 \u4fa0\u8eab\u4efd\uff0c\u4f46\u4ece\u6765\u4e0d\u6446\u67b6\u5b50\uff0c\u548c\u4ec0\u4e48\u4eba\u90fd\u6709\u8bf4\u8bf4\u6709\u7b11\u3002\u50cf\u4eca\u5929\u8fd9\u4e2a\u573a\u5408\uff0c\u4ed6\u4e00\u5230\u5fc5\u5b9a\u662f\u5230\u5904\u627e \u76f8\u719f\u7684\u670b\u53cb\u503e\u8c08\uff0c\u4f46\u8fd9\u6b21\u5374\u662f\u6084\u6084\u7684\u6765\uff0c\u4e00\u6765\u5c31\u53ea\u53bb\u89c1\u738b\u5be8\u4e3b\uff0c\u548c\u4ed6\u5e73\u65e5\u7684\u4f5c\u98ce\u597d\u50cf\u6709\u70b9\u4e0d \u5927\u76f8\u4f3c\u3002\u96be\u9053\u2014\u2014\u201d\u201c\u4f60\u4e0d\u8981\u80e1\u731c\u3002\u4ee5\u6587\u5927\u4fa0\u7684\u4e3a\u4eba\u3002\u4ed6\u5f53\u7136\u4e0d\u4f1a\u70ed\u8877\u4e8e\u505a\u6b66\u6797\u76df\u4e3b\uff0c\u4e3a\u4e86 \u8981\u505a\u76df\u4e3b\u800c\u60a3\u5f97\u60a3\u5931\u3002\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u4e0d\u6562\u8fd9\u6837\u80e1\u731c\uff0c\u6240\u4ee5\u6211\u624d\u89c9\u5f97\u5947\u602a\uff0c\u4ed6\u6709\u4ec0\u4e48\u5927\u4e8b\u8981\u4ee4\u738b\u5be8\u4e3b\u4e5f\u966a\u4ed6\u51b7\u843d\u5bbe\u5ba2 \u5462\u3002\u201d \u3000\u3000\u7b2c\u4e09\u4e2a\u4eba\u52a0\u5165\u4ed6\u4eec\u7684\u5708\u5b50\uff0c\u8fd9\u4eba\u662f\u738b\u5b87\u5ead\u7684\u4eb2\u4fe1\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6837\u66f4\u5947\u602a\u7684\u4e8b \u5462\uff0c\u6587\u5927\u4fa0\u662f\u66ff\u4eba\u9012\u62dc\u5e16\u6765\u7684\u3002\u6211\u521a\u521a\u624d\u77e5\u9053\u3002\u201d \u3000\u3000\u8001\u670b\u53cb\u6765\u795d\u5bff\u4e5f\u65e0\u987b\u9012\u62dc\u8d34\u7684\uff0c\u50cf\u8fd9\u6837\u7684\u573a\u5408\uff0c\u53ea\u6709\u540c\u7b49\u8eab\u4efd\u7684\u4eba\uff0c\u800c\u4e14\u662f\u7b2c\u4e00\u6b21\u76f8\u4f1a \u7684\u4eba\uff0c\u624d\u4f1a\u8fd9\u6837\u90d1\u800c\u91cd\u4e4b\u6258\u53e6\u4e00\u4e2a\u4e5f\u662f\u5927\u6709\u8eab\u4efd\u7684\u4eba\u6765\u9012\u62dc\u8d34\u3002 \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u5148\u5934\u90a3\u4e24\u4e2a\u4eba\u90fd\u662f\u5403\u4e00\u60ca\u3002 \u3000\u3000\u4e00\u4e2a\u76b1\u7740\u7709\u5934\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u6587\u5927\u4fa0\u7684\u8eab\u4efd\u548c\u4f60\u4eec\u8d5b\u4e3b\u7684\u8eab\u4efd\u76f8\u5f53\uff0c\u90a3\u4e2a\u4eba\u5c45\u7136\u6562\u53eb\u6587\u5927 \u4fa0\u66ff\u4ed6\u6765\u9001\u62dc\u8d34\uff0c\u96be\u9053\u4ed6\u7684\u8eab\u4efd\u66f4\u9ad8\uff1f\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u53e6\u4e00\u4e2a\u4eba\u5219\u662f\u4e00\u8138\u5b54\u4e0d\u4ee5\u4e3a\u7136\u7684\u795e\u6c14\u8bf4 \u9053\uff1a\u201c\u5373\u4f7f\u4ed6\u7684\u8eab\u4efd\u66f4\u9ad8\uff0c\u4f46\u4fd7\u8bed\u6709\u4e91\u5ba2\u4e0d\u50ed\u4e3b\uff0c\u4ed6\u5230\u4e86\u5357\u6c5f\uff0c\u4e5f\u8be5\u4eb2\u81ea\u6765\u9012\u62dc\u8d34\u624d\u5bf9\u3002\u201d \u3000\u3000\u8981\u77e5\u82e5\u8bba\u6c5f\u5357\u6b66\u6797\u4eba\u7269\u7684\u8eab\u4efd\uff0c\u662f\u6ca1\u6709\u4eba\u80fd\u591f\u6bd4\u6587\u9038\u51e1\u548c\u738b\u5b87\u5ead\u8981\u9ad8\u7684\u4e86\u3002\u56e0\u6b64\u4ed6\u624d\u6562 \u65ad\u5b9a\u90a3\u4e2a\u6258\u6587\u9038\u51e1\u6765\u9012\u62dc\u8d34\u7684\u4eba\u662f\u5916\u5730\u6765\u7684\u3002 \u3000\u3000\u738b\u5b87\u5ead\u7684\u4eb2\u4fe1\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u90a3\u4eba\u662f\u6709\u4ec0\u4e48\u8bdd\uff0c\u4e0d\u4fbf\u76f4\u63a5\u548c\u738b\u5be8\u4e3b\u8bf4\u5462\u3002\u4ed6\u6258\u6587\u5927\u4fa0\u66ff\u4ed6 \u628a\u8bdd\u8bf4\u5728\u524d\u5934\uff0c\u90a3\u662f\u2018\u4ee3\u4e3a\u5148\u5bb9\u2019\u7684\u610f\u601d\u3002\u201d\u4e5f\u53ea\u6709\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u6258\u4eba\u6765\u9012\u62dc\u8d34\u8005\u66f4\u4e3a \u5408\u4e4e\u793c\u8282\u7684\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u3002\u4e0d\u8fc7\u966a\u4ed6\u6765\u7684\u54ea\u4e2a\u4eba\u6211\u5012\u8ba4\u5f97\u3002\u201d \u3000\u3000\u201c\u54e6\uff0c\u9664\u4e86\u6587\u5927\u4fa0\u4e4b\u5916\uff0c\u8fd8\u6709\u4eba\u966a\u4ed6\u6765\u7684\u4e48\uff1f\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e10\u5e2e\u5728\u4e34\u5b89\u5206\u8235\u4e3b\u9a6c\u5929\u884c\uff0c\u6587\u5927\u4fa0\u6765\u66ff\u4ed6\u9001\u62dc\u8d34\uff0e\u9a6c\u5929\u884c\u5219\u966a\u4ed6\u7559\u5728\u8fce\u5ba2\u4ead\u7b49\u5019\u738b\u5be8 \u4e3b\u51fa\u53bb\u8fce\u63a5\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u770b\u4e86\u62dc\u8d34\uff0c\u4e0d\u89c9\u4e5f\u662f\u6709\u70b9\u60ca\u8be7\u3002\u62dc\u8d34\u4e0a\u5177\u540d\u7684\u662f\u4e10\u5e2e\u7684\u5211\u5802\u9999\u4e3b\u98ce\u706b\u9f99\u3002 \u3000\u3000\u98ce\u706b\u9f99\u76ee\u524d\u7684\u8eab\u4efd\u672a\u5fc5\u6bd4\u4ed6\u9ad8\uff0c\u4f46\u4e10\u5e2e\u5e2e\u4e3b\u7ee7\u627f\u4eba\u7684\u8eab\u4efd\u5219\u975e\u540c\u5c0f\u53ef\u3002\u4e0d\u8fc7\uff0c\u4ee4\u5f97\u738b\u5b87 \u5ead\u60ca\u8be7\u7684\u5012\u8fd8\u4e0d\u662f\u98ce\u706b\u9f99\u7684\u201c\u672a\u6765\u8eab\u4efd\u201d\u3002\u800c\u662f\u53e6\u6709\u522b\u60c5\uff0c\u201c\u4ed6\u5012\u662f\u6765\u5f97\u5feb\uff0c\u4ed6\u6b64\u6765\u83ab\u975e\u771f \u7684\u5c31\u662f\u4e3a\u4e86\u5c1a\u6606\u9633\u7684\u90a3\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6587\u9038\u51e1\u679c\u7136\u8bf4\u9053\uff1a\u201c\u98ce\u706b\u9f99\u5230\u4e86\u6c5f\u5357\u624d\u77e5\u9053\u4f60\u4eca\u5929\u505a\u4e94\u5341\u5927\u5bff\u7684\u3002\u4ed6\u6b64\u6765\u56fa\u7136\u662f\u4e3a\u4e86\u66ff \u4f60\u62dc\u5bff\uff0c\u4f46\u5374\u8fd8\u6709\u53e6\u5916\u4e00\u4ef6\u66f4\u8981\u7d27\u7684\u4e8b\u60c5\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\uff0c\u201c\u8bf7\u4f60\u5e2e\u5fd9\u5730\u6349\u62ff\u91d1\u56fd\u7684\u5978\u7ec6\uff01\u201d \u3000\u3000\u201c\u6349\u62ff\u91d1\u56fd\u5978\u7ec6\u662f\u5e94\u8be5\u7684\u3002\u4f46\u4e0d\u77e5\u90a3\u5978\u7ec6\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u96be\u9053\u6709\u4eba\u548c\u6c5f\u5357\u4e10\u5e2e\u90fd\u8fd8\u5bf9\u4ed8\u4e0d \u4e86\u5417\uff1f\u201d\u738b\u5b87\u5ead\u95ee\u9053\u3002 \u3000\u3000\u6587\u9038\u51e1\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u53ea\u77e5\u4ed6\u59d3\u6a80\uff0c\u5e74\u7eaa\u6050\u6015\u4e0d\u5230\u4e8c\u5341\uff0c\u6b66\u529f\u5374\u662f\u5341\u5206 \u9ad8\u5f3a\u3002\u201d\u5f53\u4e0b\u628a\u5728\u4e34\u5b89\u78b0\u4e0a\u6a80\u7fbd\u51b2\u7684\u7ecf\u8fc7\uff0c\u8bf4\u7ed9\u738b\u5b87\u5ead\u77e5\u9053\u3002 \u3000\u3000\u738b\u5b87\u5ead\u542c\u7f62\uff0c\u795e\u8272\u66f4\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u7528\u7684\u662f\u4e00\u652f\u7389\u7bab\uff0c\u4ed6\u7684\u7389\u7bab\u5c45\u7136\u80fd\u591f \u62b5\u6321\u4f60\u7684\u94c1\u7b14\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u800c\u4e14\u6211\u8fd8\u662f\u7528\u523b\u77f3\u9f13\u6587\u7684\u7b14\u6cd5\uff01\u201d\u523b\u77f3\u9f13\u6587\u7b14\u529b\u662f\u6700\u4e3a\u9053\u52b2\u7684\u3002 \u3000\u3000\u201c\u4e10\u5e2e\u600e\u4e48\u77e5\u9053\u4ed6\u662f\u91d1\u56fd\u5978\u7ec6\uff1f\u98ce\u706b\u9f99\u53ef\u66fe\u548c\u4eba\u8bf4\u8fc7\uff1f\u201d\u738b\u5b87\u5ead\u95ee\u9053\u3002 \u3000\u3000\u201c\u4ed6\u8bf4\u662f\u4ed6\u4eec\u7684\u6731\u957f\u8001\u67e5\u63a2\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u54e6\u3002\u662f\u6731\u4e39\u9e64\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u5728\u4e10\u5e2e\u56db\u5927\u957f\u8001\u4e2d\u6392\u540d\u7b2c\u4e8c\u7684\u6731\u4e39\u9e64\u3002\u96be\u9053\u4f60\u5bf9\u4ed6\u2014\u2014\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\u6000\u7591\u6731\u4e39\u9e64\uff0c\u4e5f\u4e0d\u6562\u6000\u7591\u98ce\u706b\u9f99\u7684\u4f20\u8bdd\u4e0d\u771f\u5b9e\u3002\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u600e\u6837\uff1f\u201d\u6587\u9038\u51e1\u8fde\u5fd9\u95ee\u4ed6\u3002\u539f\u6765\u6587\u9038\u51e1\u7684\u5fc3\u91cc\u5176\u5b9e\u65e9\u5c31\u6709\u70b9\u6000\u7591\uff0c\u6000\u7591\u53e6\u6709\u5185 \u60c5\uff0c\u6a80\u7fbd\u51b2\u672a\u5fc5\u5f53\u771f\u5c31\u662f\u91d1\u56fd\u7684\u5978\u7ec6\u4e86\u3002\u4ed6\u4ee5\u6c5f\u5357\u5927\u4fa0\u7684\u8eab\u4efd\uff0c\u66ff\u98ce\u706b\u9f99\u6765\u9012\u62dc\u8d34\uff0c\u56fa\u7136\u662f \u51fa\u4e8e\u5bf9\u4e10\u5e2e\u7684\u5c0a\u91cd\uff0c\u4f46\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u3002\u662f\u60f3\u5728\u98ce\u706b\u9f99\u4e0e\u738b\u5b87\u5ead\u4f1a\u9762\u4e4b\u524d\uff0c\u5148\u548c\u738b\u5b87\u5ead\u4ea4 \u6362\u610f\u89c1\u7684\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5c11\u5e74\u53ef\u80fd\u662f\u6211\u4e00\u4f4d\u597d\u670b\u53cb\u7684\u5f92\u5f1f\u3002\u4e0d\u8fc7\uff0c\u6211\u8981\u89c1\u4e86\u7389\u7bab \u624d\u80fd\u65ad\u5b9a\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u771f\u7684\u662f\u4f60\u7684\u90a3\u4f4d\u670b\u53cb\u7684\u5f92\u5f1f\uff0c\u4f60\u5c31\u76f8\u4fe1\u4ed6\u4e0d\u4f1a\u662f\u91d1\u56fd\u7684\u5978\u7ec6\u4e48\uff1f\u201d \u3000\u3000\u6587\u9038\u51e1\u8fd9\u4e00\u95ee\uff0c\u5012\u662f\u95ee\u5f97\u738b\u5b87\u5ead\u6709\u70b9\u96be\u4ee5\u4f5c\u7b54\u4e86\u3002\u4ed6\u518d\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u80fd\u8fd9 \u6837\u8bf4\u3002\u9f99\u751f\u4e5d\u79cd\uff0c\u5404\u6709\u4e0d\u540c\u3002\u4e16\u95f4\u4e0d\u8096\u7684\u513f\u5b50\u90fd\u591a\u7740\u5462\uff0c\u4f55\u51b5\u5e08\u5f92\uff1f\u4e0d\u8fc7\uff0c\u6b64\u4e8b\u53ea\u6015\u8fd8\u662f\u6709 \u70b9\u8e4a\u8df7\u7684\u3002\u201d \u3000\u3000\u201c\u56e0\u4f55\u4f60\u6709\u8fd9\u4e2a\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6628\u5929\u6211\u4e5f\u63a5\u5230\u4e00\u4f4d\u4e10\u5e2e\u4eba\u7269\u4f20\u8bdd\uff0c\u8bf4\u7684\u8bdd\u53ef\u662f\u548c\u98ce\u706b\u9f99\u4e24\u6837\u3002\u201d \u3000\u3000\u6587\u9038\u51e1\u5927\u5403\u4e00\u60ca\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\u4f60\u5b81\u53ef\u76f8\u4fe1\u4ed6\uff0c\u4e0d\u76f8\u4fe1\u98ce\u706b\u9f99\uff0c\u96be\u9053\u2014\u2014\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u5730\u4f4d\u6bd4\u98ce\u706b\u9f99\u66f4\u9ad8\u3002\u201d\u6b63\u5728\u8003\u8651\u8981\u4e0d\u8981\u628a\u5b9e\u60c5\u544a\u8bc9\u6587\u9038\u51e1\uff0c\u5ffd \u542c\u5f97\u6709\u6572\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6572\u95e8\u7684\u662f\u5c71\u5be8\u6267\u638c\u94b1\u6599\u7684\u5934\u76ee\uff0c\u540d\u5524\u4e01\u5146\u3002\u4ed6\u5728\u5c71\u5be8\u7684\u5730\u4f4d\u867d\u7136\u4e0d\u7b97\u9ad8\uff0c\u4f46\u5374\u662f\u738b\u5b87\u5ead \u7684\u4eb2\u4fe1\u3002 \u3000\u3000\u738b\u5b87\u5ead\u7709\u5934\u4e00\u76b1\uff0c\u6253\u5f00\u623f\u95e8\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e01\u5146\u8fdb\u4e86\u623f\u95f4\uff0c\u8feb\u4e0d\u6025\u5f85\u7684\uff0c\u4e00\u9762\u884c\u793c\uff0c\u4e00\u9762\u4fbf\u5373\u7980\u62a5\uff1a\u201c\u6709\u4e2a\u5c11\u5e74\u6c42\u89c1\u5be8\u4e3b\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u662f\u5e38\u4e94\u5e26\u4ed6\u4e0a\u5c71\u7684\u3002\u201d\u5e38\u4e94\u4e0d\u8fc7\u662f\u4e2a\u65e0\u8db3\u8f7b\u91cd\u7684\u5c0f\u5934\u76ee\u3002 \u3000\u3000\u738b\u5b87\u5ead\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5c31\u8981\u9a82\u4ed6\uff0c\u4f46\u4e00\u60f3\u4e01\u5146\u4e3a\u4eba\u7d20\u6765\u8c28\u614e\uff0c\u5176\u4e2d\u5fc5\u6709\u9053\u7406\uff0c\u4fbf\u518d\u95ee\u9053\uff1a \u201c\u54e6\uff0c\u4e0d\u77e5\u6765\u5386\uff1f\u90a3\u4e48\uff0c\u4ed6\u603b\u6709\u4e2a\u540d\u5b57\u5427\uff1f\u201d \u3000\u3000\u4e01\u5146\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u4ed6\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4ed6\u4e0d\u80af\u8bf4\uff1f\u201d \u3000\u3000\u4e01\u5146\u9053\uff1a\u201c\u4ed6\u6709\u4e00\u652f\u7389\u7bab\uff0c\u751a\u4e3a\u5947\u602a\u3001\u4ed6\u53eb\u6211\u62ff\u8fd9\u652f\u7389\u7bab\u7ed9\u4f60\u770b\u3002\u4ed6\u8bf4\u4f60\u89c1\u4e86\u8fd9\u652f\u7389 \u7bab\uff0c\u5c31\u4f1a\u77e5\u9053\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u63a5\u8fc7\u8fd9\u652f\u7389\u7bab\uff0c\u7acb\u5373\u5c31\u61c2\u5f97\u4e86\u5146\u6240\u8bf4\u7684\u201c\u5947\u602a\u201d\u8db3\u4ec0\u4e48\u610f\u601d\u4e86\u3002\u4ed6\u7684\u624b\u6307\u4e00\u63a5\u89e6 \u8fd9\u652f\u7389\u7bab\uff0c\u5c31\u6709\u6e29\u6696\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u662f\u5929\u4e0b\u72ec\u4e00\u65e0\u4e8c\u7684\u201c\u6696\u7389\u7bab\u201d\uff0c\u738b\u5b87\u5ead\u662f\u66fe\u7ecf\u5728\u8036\u5f8b\u7384\u5143\u624b\u4e2d\u89c1\u8fc7\u8fd9\u4ea4\u7389\u7bab\u7684\u3002 \u3000\u3000\u6587\u9038\u51e1\u548c\u4ed6\u4e0d\u7ea6\u800c\u540c\u7684\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u8fd9\u652f\u7389\u7bab\uff01\u201d \u3000\u3000\u4e01\u5146\u5403\u60ca\u5730\u770b\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u738b\u5b87\u5ead\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5446\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u5feb\u7ed9\u6211\u8bf7\u90a3\u5c11\u5e74\u8fdb\u6765\u3002\u201d \u3000\u3000\u4e01\u5146\u9053\uff1a\u201c\u73b0\u5728\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u7fdf\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u4e0d\u80fd\u8ba9\u98ce\u706b\u9f99\u4e45\u5019\uff0c\u8fd9\u6837\u5427\uff0c\u4f60\u628a\u90a3\u5c11\u5e74\u5e26\u6765\u8fd9 \u91cc\u3002\u6211\u51fa\u53bb\u8fce\u63a5\u98ce\u706b\u9f99\uff0c\u6211\u518d\u8ddf\u4ed6\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53e6\u4e00\u4e2a\u804c\u53f8\u201c\u77e5\u5ba2\u201d\u7684\u5934\u76ee\u4e5f\u8fdb\u6765\u50ac\u4ed6\u4e86\uff1a\u201c\u7980\u5be8\u4e3b\uff0c\u4e09\u5f53\u5bb6\u5df2\u5230\u8fce\u5ba2\u4ead\u966a \u90a3\u4f4d\u4e10\u5e2e\u6765\u7684\u8d35\u5ba2\u4e86\uff0c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u7528\u4e0d\u7740\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u738b\u5b87\u5ead\u4ea6\u5df2\u61c2\u5f97\u4ed6\u7684\u610f\u601d\u4e86\uff0c\u4ed6\u8bf4\u7684\u201c\u4e09\u5f53\u5bb6\u201d\u4e43\u662f\u5728\u5c71\u5be8\u91cc\u5750\u7b2c\u4e09 \u628a\u4ea4\u6905\u7684\u7126\u633a\uff0c\u7126\u633a\u867d\u540d\u5df2\u7ecf\u53ef\u4ee5\u7b97\u5f97\u662f\u5c71\u5be8\u7684\u9996\u8111\u4eba\u7269\uff0c\u4f46\u8fd8\u591f\u8d44\u683c\u4ee3\u8868\u738b\u5b87\u5ead\u51fa\u8fce\u7684\u3002 \u4ed6\u53ea\u662f\u6015\u5931\u5229\u4e8e\u8d35\u5bbe\uff0c\u6545\u800c\u5148\u5230\u8fce\u5ba2\u4ead\u62db\u547c\u5ba2\u4eba\u800c\u5df2\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4e86\u3002\u6211\u9a6c\u4e0a\u5c31\u548c\u6587\u5927\u4fa0\u51fa\u53bb\u3002\u201d\u738b\u5b87\u5ead\u8bf4\u7f62\uff0c\u8ddf\u7740\u5bf9\u6587\u9038\u51e1\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u6869\u4e8b \u60c5\uff0c\u770b\u6765\u4e5f\u53ea\u6709\u62bc\u540e\u624d\u80fd\u548c\u4f60\u8bf4\u4e86\u3002\u201d\u54ea\u91cc\u77e5\u9053\uff0c\u8fd8\u6709\u4e00\u4ef6\u4ed6\u6240\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u4e1a\u5df2\u53d1\u751f\uff01 \u3000\u3000\u4ed6\u521a\u521a\u8d70\u51fa\u5385\u5802\uff0c\u53ea\u89c1\u5916\u9762\u5df2\u662f\u50cf\u70e7\u6cb8\u4e86\u4e00\u9505\u6c34\u4f3c\u7684\uff0c\u5608\u5608\u6742\u6742\uff0c\u8dd1\u8fdb\u8dd1\u51fa\uff0c\u4e71\u54c4\u54c4\u95f9 \u6210\u4e00\u7247\u3002 \u3000\u3000\u201c\u7980\u5be8\u4e3b\uff0c\u4e09\u5f53\u5bb6\u7ed9\u4e00\u4e2a\u65e0\u540d\u5c0f\u5b50\u6253\u4f24\u4e86\uff01\u201d \u3000\u3000\u201c\u77f3\u5be8\u4e3b\u597d\u50cf\u4e5f\u6253\u4e0d\u8fc7\u90a3\u4e2a\u5c0f\u8d3c\uff01\u201d \u3000\u3000\u201c\u5218\u5927\u4fa0\u5df2\u7ecf\u52a8\u5200\u4e86\uff0c\u90a3\u5c0f\u8d3c\u8fd8\u662f\u8d64\u624b\u7a7a\u62f3\uff01\u201d \u3000\u3000\u201c\u77f3\u5e84\u4e3b\u548c\u8bf4\u5927\u5bb6\u8054\u624b\uff0c\u4f3c\u4e4e\u90fd\u62e6\u622a\u4e0d\u4e86\uff0c\u90a3\u4e2a\u5c0f\u8d3c\u5df2\u7ecf\u95ef\u8fdb\u5c71\u5be8\u6765\u4e86\uff01\u201d \u3000\u3000\u738b\u5b87\u5ead\u7684\u624b\u4e0b\u4e03\u5634\u516b\u820c\u5730\u5411\u4ed6\u62a5\u544a\uff0c\u738b\u5b87\u5ead\u559d\u9053\uff1a\u201c\u522b\u5435\uff0c\u5f85\u6211\u51fa\u53bb\u4f1a\u4ed6\u3002\u4f60\u4eec\u53ef\u4e0d\u8bb8 \u4e0d\u95ee\u60c5\u7531\u5c31\u4e00\u7a9d\u5cf0\u7684\u4e0a\u53bb\uff0c\u53eb\u4eba\u7b11\u8bdd\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u529b\u4fdd\u9547\u5b9a\uff0c\u53ef\u4e5f\u7740\u5b9e\u6709\u70b9\u5fc3\u70e6\u610f\u4e71\u4e86\u3002\u4e0d\u9519\uff0c\u4ed6\u53ef\u4ee5\u201c\u7ea6\u675f\u201d\u624b\u4e0b\uff0c\u4f46\u9ed1\u77f3\u5e84\u4e3b \u77f3\u96f7\u5df2\u7ecf\u624b\uff0c\u8bba\u6b66\u6797\u7684\u8f88\u4efd\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7684\u8f88\u4efd\u662f\u6bd4\u4ed6\u8fd8\u9ad8\u7684\uff0c\u4ed6\u53c8\u600e\u80fd\u7ea6\u675f\u4ed6\u4eec\u5462\uff1f\u4f55\u51b5\u8fd8 \u6709\u4e00\u4e2a\u4e10\u5e2e\u7684\u4f7f\u8005\u98ce\u706b\u9f99\uff0c\u66f4\u4e0d\u662f\u4ed6\u6240\u80fd\u7ea6\u675f\u7684\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u4f1a\u95f9\u51fa\u8fd9\u6837\u5927\u7684\u4e8b\u60c5\uff0c\u8fd9\u4ef6\u4e8b\u6211\u4e5f\u4e0d\u77e5\u5982\u4f55\u6536\u62fe\u4e86\uff01\u201d\u4ed6\u5fc3\u4e71\u5982\u9ebb\uff0c\u53ea\u597d\u89c1\u4e00 \u6b65\u8d70\u4e00\u6b65\u4e86\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u662f\u600e\u6837\u95f9\u51fa\u6765\u7684\u5462\uff1f\u7126\u633a\uff08\u4e09\u5f53\u5bb6\uff09\u5728\u8fce\u5ba2\u4ead\u62db\u547c\u5ba2\u4eba\uff0c\u8ddf\u7740\u9ed1\u77f3\u5e84\u7684\u5e84\u4e3b\u548c\u5e38 \u5dde\u5927\u4fa0\u5218\u5929\u5316\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u77f3\u5218\u4e8c\u4eba\u5728\u5ba2\u4eba\u4e2d\u5730\u4f4d\u6700\u5c0a\uff0c\u4ed6\u4eec\u662f\u4ee3\u8868\u6c5f\u5357\u7684\u6b66\u6797\u540c\u9053\uff0c\u5148\u5230\u8fce\u5ba2\u4ead\u6765\uff0c\u5bf9\u98ce\u706b\u9f99\u8868 \u793a\u6b22\u8fce\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u90fd\u731c\u60f3\u5f97\u5230\uff0c\u4e10\u5e2e\u7684\u4f7f\u8005\u524d\u6765\uff0c\u5f53\u7136\u4e0d\u4f1a\u53ea\u662f\u4e3a\u4e86\u7ed9\u738b\u5b87\u5ead\u795d\u5bff\u8fd9\u6837\u7b80\u5355\uff0c\u81ea\u662f \u4e0d\u514d\u95ee\u53ca\u4ed6\u7684\u6765\u610f\u3002 \u3000\u3000\u98ce\u706b\u9f99\u8bf4\u51fa\u4e86\u8981\u6349\u62ff\u91d1\u56fd\u5978\u7ec6\u4e4b\u4e8b\uff0c\u542c\u5f97\u4ed6\u4eec\u90fd\u662f\u4e0d\u7981\u76f8\u987e\u9a87\u7136\u3002 \u3000\u3000\u201c\u98ce\u9999\u4e3b\uff0c\u4f60\u653e\u5fc3\uff0c\u91d1\u56fd\u7684\u5978\u7ec6\u80c6\u6562\u6f5c\u5165\u6c5f\u5357\uff0c\u6211\u4eec\u6c5f\u5357\u7684\u4fa0\u9053\u4e5f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002\u201d \u9ed1\u77f3\u5e84\u7684\u5e84\u4e3b\u77f3\u96f7\u8bf4\u9053\u3002 \u3000\u3000\u5e38\u5dde\u7684\u91d1\u5200\u5927\u4fa0\u5218\u5929\u5316\u9053\uff1a\u201c\u738b\u5be8\u4e3b\u5ac9\u6076\u5982\u4ec7\uff0c\u8fd9\u4ef6\u4e8b\u7531\u4ed6\u4e3b\u6301\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u4ed6\u662f \u4e03\u5341\u4e8c\u5be8\u7684\u603b\u8235\u4e3b\uff0c\u624b\u4e0b\u4eba\u9a6c\u4f17\u591a\uff0c\u4e00\u5b9a\u53ef\u4ee5\u5c06\u5978\u7ec6\u6355\u83b7\u3002\u201d \u3000\u3000\u77f3\u96f7\u6027\u5b50\u6700\u6025\uff0c\u76b1\u7709\u9053\uff1a\u201c\u600e\u7684\u8fd8\u4e0d\u89c1\u738b\u5be8\u4e3b\u51fa\u6765\uff1f\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u4eec\u53d1\u73b0\u6709\u4e2a\u4eba\u8d70\u6765\u4e86\uff0c\u4f46\u5374\u4e0d\u662f\u738b\u5b87\u5ead\uff0c\u662f\u4e2a\u4e30\u795e\u4fca\u79c0\u5c11\u5e74\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u4e54\u88c5\u6253\u626e\u7792\u4e0d\u8fc7\u98ce\u706b\u9f99\u7684\u773c\u775b\uff0c\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u9661\u7136\u559d\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u5c0f\u5b50\uff01\u201d \u4ed6\u867d\u7136\u7701\u6389\u4e86\u201c\u91d1\u56fd\u5978\u7ec6\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u4f46\u77f3\u3001\u5218\u3001\u7126\u7b49\u4eba\u5df2\u662f\u4e00\u542c\u5c31\u660e\u767d\u4e86\u3002 \u3000\u3000\u7126\u633a\u6027\u6025\u5982\u706b\uff0c\u53eb\u9053\uff1a\u201c\u8ba9\u6211\u6765\uff01\u201d\u4e00\u58f0\u5927\u543c\uff0c\u62a2\u5148\u5c31\u8dd1\u51fa\u53bb\u3002 \u3000\u3000\u77f3\u96f7\u8bf4\u9053\uff1a\u201c\u98ce\u9999\u4e3b\uff0c\u8bf7\u4f60\u5750\u4e0b\u6765\u5427\u3001\u7126\u8001\u4e09\u53f7\u79f0\u795e\u62f3\u65e0\u654c\uff0c\u8fd9\u5c0f\u5b50\u78b0\u4e0a\u4e86\u4ed6\uff0c\u662f\u6d3b\u8be5 \u5012\u9709\u7684\u4e86\u3002\u4f60\u82e5\u8fd8\u4e0d\u653e\u5fc3\u7684\u8bdd\uff0c\u6211\u53bb\u7ed9\u7126\u8001\u4e09\u62bc\u9635\u3002\u201d\u98ce\u706b\u9f99\u5403\u8fc7\u6a80\u7fbd\u51b2\u7684\u4e8f\uff0c\u4e50\u5f97\u8896\u624b\u65c1 \u89c2\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u3002\u7126\u633a\u5df1\u62e6\u4f4f\u6a80\u7fbd\u51b2\u7684\u53bb\u8def\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u5403\u4e86\u8001\u864e\u7684\u5fc3\u8fd8\u662f\u5403\u4e86 \u8c79\u5b50\u80c6\uff0c\u80c6\u6562\u8dd1\u5230\u8fd9\u91cc\u6492\u91ce\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8c08\u8c08\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6765\u6492\u91ce\u7684\uff0c\u6211\u662f\u6765\u6c42\u89c1\u5be8\u4e3b\u7684\u3002\u201d\u7126\u633a\u54ea\u6709\u529f\u592b\u542c\u4ed6\u5206\u8fa8\uff0c \u54fc\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4f60\u89c1\u9b3c\u53bb\u5427\uff01\u201d\u63d0\u8d77\u7897\u53e3\u5927\u7684\u62f3\u5934\uff0c\u4e00\u62f3\u5c31\u6253\u8fc7\u53bb\uff01\u5218\u5929\u5316\u53eb\u9053\uff1a\u201c\u5582\uff0c \u4f60\u522b\u4e00\u62f3\u5c31\u6253\u6b7b\u4e86\u4ed6\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff0c\u7126\u633a\u8fd9\u4e00\u62f3\u5df2\u662f\u6253\u5728\u64c5\u7fbd\u51b2\u7684\u8eab \u4e0a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u542c\u8bf4\u6b64\u4eba\u53f7\u79f0\u201c\u795e\u62f3\u65e0\u654c\u201d\uff0c\u6709\u5fc3\u8bd5\u8bd5\u4ed6\u7684\u62f3\u529b\u3002\u4ed6\u4f7f\u51fa\u4e86\u6cbe\u8877\u5341\u516b\u8dcc\u7684\u529f\u592b\uff0c \u809a\u76ae\u4e00\u633a\uff0c\u786c\u63a5\u4ed6\u7684\u94c1\u62f3\u3002 \u3000\u3000\u53ea\u542c\u5f97\u7126\u633a\u201c\u566b\u201d\u7684\u4e00\u58f0\uff0c\u5c9b\u5f62\u6643\u4e86\u4e24\u6643\uff0c\uff3f\u4e0d\u8fc7\u4ed6\u5e76\u6ca1\u6709\u8dcc\u5012\u3002\u5012\u662f\u6a80\u7fbd\u51b2\u7ed9\u4ed6\u6253\u5f97 \u5f2f\u4e0b\u8170\u4e86\u3002 \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u4f3c\u4e4e\u6709\u70b9\u672c\u9886\u3002\u4f46\u6bd5\u7adf\u8fd8\u662f\u6371\u4e0d\u8d77\u7126\u8001\u4e09\u7684\u4e00\u62f3\uff01\u201d\u5fc3\u91cc\u8fd8\u5728\u597d \u7b11\uff1a\u201c\u98ce\u706b\u9f99\u4e5f\u4e0d\u662f\u6ca1\u6709\u89c1\u8fc7\u5927\u9635\u4ed7\u7684\u4eba\uff0c\u8981\u5982\u6b64\u90d1\u91cd\u5176\u4e8b\u7684\u5174\u5e08\u52a8\u4f17\uff01\u5514\uff0c\u770b\u6765\u6050\u6015\u98ce\u706b \u9f99\u90fd\u662f\u6d6a\u5f97\u865a\u540d\u4e86\uff01\u201d \u3000\u3000\u90a3\u77e5\u5fc3\u5ff5\u672a\u5df1\uff0c\u4e8b\u60c5\u5df2\u662f\u6709\u4e86\u5927\u5927\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u7684\u53d8\u5316\u3002 \u3000\u3000\u53ea\u89c1\u6a80\u7fbd\u51b2\u5df2\u633a\u76f4\u8170\u677f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u62f3\u7684\u786e\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u4f46\u53f7\u79f0\u65e0\u654c\uff0c\u5374\u6015\u672b \u5fc5\uff01\u201d\u539f\u6765\u4ed6\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u867d\u7136\u4e0d\u80fd\u4ee4\u7126\u633a\u8dcc\u5012\uff0c\u4f46\u4ed6\u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u88ab\u7126\u633a\u6253\u4f24\u4e86\u3002\u4ed6\u5f2f \u4e0b\u4e86\u8170\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u6d88\u89e3\u90a3\u80a1\u5343\u65a4\u5de8\u529b\u800c\u5df2\u3002 \u3000\u3000\u7126\u633a\u660e\u77e5\u78b0\u4e0a\u9ad8\u624b\uff0c\u4f46\u4ed6\u662f\u706b\u7206\u813e\u6c14\uff0c\u600e\u4e5f\u4e0d\u80af\u8ba4\u8f93\u7684\u3002\u7acb\u5373\u53c8\u662f\u4e00\u62f3\u6253\u51fa\uff0c\u8fd9\u4e00\u62f3\u5df1 \u662f\u7528\u4e0a\u6d51\u8eab\u6c14\u529b\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8bd5\u8fc7\u4ed6\u7684\u529f\u592b\uff0c\u4e0d\u6562\u786c\u63a5\u4ed6\u8fd9\u4e00\u62f3\u3002\u4f7f\u51fa\u56db\u4e24\u62e8\u5343\u65a4\u7684\u624b\u600e\u8f7b\u8f7b\u4e00\u62e8\uff0c\u501f\u529b\u6253 \u529b\u3001\u628a\u7126\u633a\u7684\u8eab\u5f62\u5e26\u52a8\u3002\u7126\u633a\u7528\u529b\u5927\u9530\uff0c\u8eab\u4f53\u5931\u4e86\u91cd\u5fc3\uff0c\u5411\u524d\u503e\u5012\uff0c\u6a80\u7fbd\u51b2\u4e00\u628a\u6293\u7740\u4ed6\u7684\u624b \u8155\uff0c\u559d\u58f0\uff1a\u201c\u8d77\uff01\u201d\u767b\u65f6\u5c06\u4ed6\u4e3e\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u629b\u4e86\u51fa\u53bb\u3002\u7126\u633a\u4ece\u534a\u7a7a\u4e2d\u8dcc\u4e86\u4e0b \u6765\uff0c\u9976\u662f\u4ed6\u80c6\u5927\uff0c\u4e5f\u4e0d\u7981\u5413\u5f97\u9b42\u98de\u9b44\u6563\u3002\u53ea\u9053\u8fd9\u4e00\u4e0b\u5b50\u53ea\u6015\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\uff0c\u6b7b\u4e86\u8fd8\u4e0d\u6253\u7d27\uff0c \u6700\u6015\u6454\u4e2a\u534a\u6b7b\u4e0d\u6d3b\uff0c\u53d8\u6210\u7ec8\u8eab\u6b8b\u5e9f\uff0c\u90a3\u53ef\u7cdf\u4e86\uff0c\u52a8\u5ff5\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u5c41\u80a1\u5df2\u7ecf \u7740\u5730\u3002\u5947\u602a\uff0c\u5012\u5e76\u4e0d\u89c9\u5f97\u5982\u4f55\u75bc\u75db\uff0c\u539f\u6765\u6a80\u7fbd\u51b2\u7528\u7684\u4e00\u80a1\u5de7\u52b2\uff0c\u529b\u5ea6\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u5c31\u597d\u50cf \u6709\u4e00\u53ea\u65e0\u5f62\u7684\u624b\u5c06\u4ed6\u8f7b\u8f7b\u63d0\u8d77\u4e86\u5c06\u4ed6\u8f7b\u8f7b\u653e\u4e0b\u4e00\u822c\uff0c\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u53d7\u4f24\u3002\u5047\u5982\u4ed6\u957f\u4e8e\u8f7b\u529f\u7684 \u8bdd\uff0c\u4e00\u7740\u5730\u4fbf\u53ef\u5f39\u8d77\uff0c\u504f\u504f\u4ed6\u6700\u5f31\u7684\u4e00\u95e8\u5c31\u662f\u8f7b\u529f\uff0c\u5fc3\u4e2d\u53c8\u5148\u81ea\u53d1\u614c\uff0c\u8fd9\u624d\u95f9\u4e86\u4e2a\u5f53\u573a\u51fa \u4e11\u3002 \u3000\u3000\u7126\u633a\u8111\u7f9e\u6210\u6012\uff0c\u8df3\u8d77\u6765\u5471\u5471\u5927\u53eb\u3002\u5c0f\u5934\u76ee\u53ea\u9053\u4ed6\u53d7\u4e86\u4f24\uff0c\u8d76\u5fd9\u8dd1\u56de\u5927\u5be8\u7980\u62a5\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ea\u60f3\u89c1\u4f60\u4eec\u7684\u6848\u4e3b\u3001\u53ef\u4e0d\u60f3\u53bb\u89c1\u960e\u7f57\uff0c\u53ea\u597d\u8bf7\u4f60\u8ba9\u4e00\u8ba9\u8def \u4e86\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\u3002\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u6709\u4eba\u5728\u4ed6\u80cc\u540e\u5077\u88ad\u3002\u6a80\u7fbd\u51b2\u53cd\u624b\u4e00\u638c\uff0c\u53cd\u5207\u90a3\u4eba\u864e\u53e3\u3002\u90a3\u4eba \u624b\u6cd5\u53c8\u5feb\u53c8\u72e0\uff0c\u6a80\u7fbd\u51b2\u8fd9\u4e00\u638c\u6ca1\u6253\u7740\u4ed6\uff0c\u4ed6\u5df2\u662f\u500f\u7684\u8f6c\u8fc7\u65b9\u5411\uff0c\u5411\u7740\u6a80\u7fbd\u51b2\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u6765 \u4e86\u3002\u6a80\u7fbd\u51b2\u4e00\u4e2a\u6c89\u80a9\u7f29\u8098\uff0c\u4e00\u8098\u649e\u51fa\uff0c\u653b\u5b88\u517c\u65bd\uff0c\u5728\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u521a\u597d\u5316\u89e3\u4e86\u4ed6\u4e00\u62db\u5206\u7b4b \u9519\u9aa8\u624b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5077\u88ad\u4ed6\u7684\u8fd9\u4e2a\u4eba\u5374\u539f\u6765\u662f\u4e10\u5e2e\u5728\u4e34\u5b89\u5206\u8235\u7684\u8235\u4e3b\u9a6c\u5929\u884c\u3002\u6a80\u7fbd\u51b2 \u7b11\u9053\uff1a\u201c\u4f60\u7684\u5206\u7b4b\u9519\u9aa8\u6cd5\u6bd4\u5d14\u6d69\u9ad8\u660e\u3002\u4f69\u670d\uff0c\u4f69\u670d\uff0c\u54b1\u4eec\u4e0d\u5fc5\u518d\u6bd4\u4e86\u5427\uff1f\u201d \u3000\u3000\u9a6c\u5929\u884c\u662f\u6015\u4ed6\u4f24\u5bb3\u7126\u633a\uff0c\u624d\u8d76\u6765\u51fa\u624b\u7684\uff0c\u5012\u4e0d\u662f\u6709\u610f\u5077\u88ad\u7684\u3002\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u89c9\u6ee1\u9762 \u901a\u7ea2\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u6765\u66ff\u5d14\u6d69\u62a5\u4e00\u638c\u4e4b\u4ec7\uff01\u4ed6\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u6b64\u65f6\u4ea6\u77e5\u9053\u7126 \u633a\u5e76\u6ca1\u53d7\u4f24\u4e86\uff0c\u53ea\u597d\u7ba1\u81ea\u5df1\u7684\u5077\u88ad\u53e6\u5916\u627e\u4e2a\u501f\u53e3\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u90a3\u4e00\u62f3\u6211\u867d\u7136\u6253\u5728\u5d14\u6d69\u8eab\u4e0a\uff0c\u5176\u5b9e\u6253\u7684\u5374\u662f\u5357\u5c71\u864e\u3002\u5d14\u6d69\u6ca1\u6709 \u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5e2e\u5d14\u6d69\u6253\u9000\u5357\u5c71\u864e\u4e00\u4e8b\u9a6c\u5929\u884c\u662f\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u4f46\u6b64\u65f6\u5982\u4f55\u80fd\u591f\u9000\u7f29\uff0c\u559d\u9053\u201c\u4f60\u662f \u5168\u56fd\u5978\u7ec6\uff0c\u4f60\u4ee5\u4e3a\u4f60\u5356\u7ed9\u5d14\u6d69\u4e00\u4e2a\u5c0f\u4eba\u60c5\uff0c\u6211\u5c31\u53ef\u4ee5\u653e\u8fc7\u4f60\u5417\uff1f\u201d \u3000\u3000\u4ed6\u89c1\u8bc6\u8fc7\u6a80\u7fbd\u51b2\u7684\u672c\u9886\u518d\u6b21\u51fa\u62db\uff0c\u53c8\u72e0\u53c8\u7a33\uff0c\u5148\u662f\u4e00\u62db\u201c\u767d\u733f\u63a2\u8def\u201d\uff0c\u671d\u7740\u6a80\u7fbd\u51b2\u7684\u5929 \u7075\u76d6\u5288\u4e0b\uff0c\u770b\u4ed6\u5982\u4f55\u5e94\u4ed8\u3002\u6a80\u7fbd\u51b2\u659c\u8eab\u4e0a\u6b65\uff0c\u53f3\u638c\u6a2a\u6321\uff0c\u5de6\u638c\u753b\u5f27\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u5982\u5c01\u4f3c \u95ed\u201d\u3002\u4f46\u95ed\u5f97\u4e0d\u591f\u4e25\u5bc6\uff0c\u80c1\u4e0b\u5fae\u9732\u7a7a\u95e8\u3002 \u3000\u3000\u9a6c\u5929\u884c\u8fd9\u4e00\u5957\u638c\u6cd5\u5b9e\u4e2d\u6709\u865a\uff0c\u865a\u4e2d\u6709\u5b9e\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u8981\u65e8\u4e0d\u5916\u5728\u4ee5\u653b\u52bf\u903c\u4f7f\u5bf9\u65b9\u9732\u51fa\u7834 \u7efd\u3002\u4e00\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u4e94\u6307\u4e00\u753b\uff0c\u5de6\u638c\u5982\u5f39\u7435\u7436\uff0c\u5207\u6a80\u7fbd\u51b2\u7684\u8109\u95e8\uff0c\u53f3\u638c\u9a88\u6307\u5982 \u621f\uff0c\u70b9\u5411\u6a80\u7fbd\u51b2\u7684\u8170\u80c1\u8f6f\u9aa8\u3002 \u3000\u3000\u5218\u5929\u5316\u966a\u98ce\u706b\u9f99\u5728\u4ead\u4e2d\u89c2\u6218\uff0c\u770b\u5230\u6b64\u5904\uff0c\u637b\u987b\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e10\u5e2e\u4eba\u6750\u9f0e\u76db\u786e\u662f\u4e0d\u6127\u5929 \u4e0b\u7b2c\u4e00\u5927\u5e2e\u3002\u9a6c\u822a\u4e3b\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u4e5f\u6709\u521a\u67d4\u5e76\u7528\u4e4b\u5999\uff0c\u4ee4\u6211\u5927\u5f00\u773c\u754c\u3002\u201d\u9a6c\u5929\u884c\u5de6\u638c\u90a3 \u4e00\u753b\u7528\u7eaf\u521a\u7684\u653f\u52bf\uff0c\u4e5f\u662f\u6b63\u5b97\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\u3001\u5de6\u521a\u53f3\u67d4\u3002\u4ee5\u201c\u6b63\u201d\u8f85\u201c\u5947\u201d\uff0c\u521a\u67d4\u517c\u6d4e\uff0c \u662f\u6b66\u529f\u4e2d\u6700\u96be\u7ec3\u5230\u7684\u4e00\u79cd\u5883\u754c\u3002 \u3000\u3000\u98ce\u706b\u9f99\u5374\u662f\u8d77\u4e86\u7591\u5fc3\uff0c\u201c\u4ee5\u6a80\u7fbd\u51b2\u7684\u672c\u9886\uff0c\u7edd\u4e0d\u80fd\u5728\u7b2c\u4e00\u62db\u5c31\u9732\u51fa\u7834\u7efd\uff01\u201d\u521a\u8981\u63d0\u9192\u9a6c \u5929\u884c\uff0c\u9a6c\u5929\u884c\u5df2\u662f\u7740\u4e86\u9053\u513f\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u7a81\u7136\u4ead\u91cc\u4ead\u5916\uff1b\u4e09\u4e2a\u4eba\u90fd\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u539f\u6765\u6a80\u7fbd\u51b2\u6025\u4e8e\u53bb\u89c1\u738b\u5b87\u5ead\uff0c\u4e0d\u8010\u4e45\u6218\uff0c\u4eba\u6025\u8ba1\u751f\uff0c\u4ed6\u7528\u7684\u662f\u8bf1\u654c\u4e4b\u8ba1\uff0c\u6545\u610f\u5728\u7b2c\u4e00\u62db \u5c31\u9732\u51fa\u7834\u7efd\u7684\uff0c\u4ed6\u8d81\u52bf\u524d\u6251\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u4e00\u4e0b\u5b50\u5c31\u70b9\u7740\u4e86\u9a6c\u5927\u884c\u80c1\u4e0b\u7684\u6108\u6c14\u7a74\u3002\u8fd9\u4e5f\u6b63\u662f\u9a6c \u5929\u884c\u60f3\u70b9\u4ed6\u7684\u90a3\u4e2a\u7a74\u9053\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5fae\u7b11\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u9a6c\u8235\u4e3b\uff0c\u8bf7\u4f60\u6b47\u6b47\u7f62\uff01\u201d \u3000\u3000\u9a6c\u5929\u884c\u4ecd\u7136\u4fdd\u6301\u653b\u51fb\u6001\u52bf\uff0c\u4f46\u5df2\u662f\u5446\u82e5\u6728\u9e21\u4e86\u3002\u4ead\u5b50\u91cc\u7684\u5218\u5929\u5316\u6ca1\u60f3\u5230\u6709\u6b64\u53d8\u5316\uff0c\u4e5f\u662f \u4e0d\u7981\u5446\u4e86\u3002 \u3000\u3000\u81ea\u544a\u594b\u52c7\u7ed9\u9a6c\u5929\u884c\u201c\u538b\u9635\u201d\u7684\u9ed1\u77f3\u5e84\u5e84\u4e3b\u77f3\u96f7\u4e5f\u662f\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u8fc5\u5373\u5c31\u6251\u4e0a\u53bb\u3002\u66ff\u4ee3\u4e86 \u521a\u624d\u7684\u9a6c\u5929\u884c\uff0c\u62e6\u4f4f\u4e86\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u77f3\u96f7\u5927\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u4f11\u5f97\u7316\u72c2\uff0c\u83ab\u9053\u6c5f\u5357\u65e0\u4eba\uff01\u201d\u58f0\u5982\u9739\u9707\uff01\u638c\u4f3c\u5954\u96f7\uff0c\u6a80\u7fbd\u51b2\u8eab\u5f62\u4e00 \u4fa7\uff0c\u6a2a\u638c\u6a2a\u524a\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6a80\u7fbd\u51b2\u7adf\u7136\u7ed9\u9707\u5f97\u9000\u4e86\u4e09\u6b65\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff1a \u201c\u8fd9\u4eba\u7684\u638c\u529b\u6216\u8bb8\u8fd8\u6bd4\u4e0d\u4e0a\u98ce\u706b\u9f99\uff0c\u4f46\u5df2\u662f\u8fdc\u5728\u7126\u633a\u4e4b\u4e0a\u4e86\u3002\u201d\u77f3\u96f7\u6bcf\u53d1\u4e00\u638c\uff0c\u5c31\u559d\u4e00\u58f0\uff0c \u5a01\u52bf\u9a87\u4eba\uff0c\u95ea\u7535\u8fde\u653b\u4e03\u62db\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u5df1\u5f97\u8036\u5f8b\u7384\u5143\u4e0a\u4e58\u5185\u529f\u5fc3\u6cd5\u771f\u4f20\uff0c\u8bba\u5185\u529f\u7684\u6df1\u539a\uff0c\u5176\u5b9e\u4ed6\u662f\u4e0d\u5728\u9ed1\u77f3\u5e84\u5e84\u4e3b\u77f3\u96f7 \u4e4b\u4e0b\u7684\u3002\u53ea\u56e0\u4ed6\u4e00\u6765\u4e0d\u613f\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u4e8c\u6765\u4ed6\u5df2\u7ecf\u6253\u4e86\u4e24\u573a\uff0c\u82e5\u7136\u53ea\u4ee5\u5185\u529b\u8f83\u91cf\uff0c\u4ed6\u81ea\u662f \u96be\u514d\u6709\u70b9\u76f8\u5f62\u89c1\u62d9\u4e86\u3002 \u3000\u3000\u77f3\u96f7\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u8d8a\u653b\u8d8a\u731b\uff0c\u8fc5\u82e5\u6012\u72ee\u3002\u4ed6\u8fd9\u5957\u638c\u6cd5\u79f0\u4e3a\u201c\u9739\u9707\u638c\u201d\uff0c\u4ee5\u53f1\u559d\u6765\u52a9\u638c \u52bf\uff0c\u5f53\u771f\u6709\u82e5\u884c\u96f7\u95ea\u7535\uff0c\u6151\u4eba\u5fc3\u9b44\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u907f\u91cd\u5c31\u8f7b\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u5728\u5bf9\u65b9\u7684\u638c\u98ce\u6fc0\u8361\u4e4b\u4e0b\uff0c\u4fe8\u4f3c\u7a7f\u82b1\u8774\u8776\u3002\u77f3\u96f7\u547c\u7684\u4e00\u638c\u6a2a \u626b\u8fc7\u6765\uff0c\u6a80\u7fbd\u51b2\u7a81\u7136\u5e73\u5730\u62d4\u8d77\uff0c\u53ea\u5dee\u534a\u5bf8\uff0c\u9669\u4e9b\u5c31\u8981\u7ed9\u77f3\u96f7\u6253\u65ad\u811a\u9aa8\u3002\u4f46\u77f3\u96f7\u6bd5\u7adf\u6ca1\u6709\u6253\u7740 \u4ed6\uff0c\u4ed6\u5df2\u7ecf\u98de\u9e1f\u4f3c\u7684\u4ece\u77f3\u96f7\u5934\u9876\u63a0\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u77f3\u96f7\u5fae\u89c9\u5934\u9876\u4e00\u7247\u6c81\u51c9\uff0c\u9976\u4ed6\u80c6\u6c14\u7c97\u8c6a\uff0c\u4e5f\u4e0d\u7981\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff01\u5047\u5982\u6a80\u7fbd\u51b2\u4e0d\u662f\u4ece\u4ed6\u5934 \u9876\u63a0\u8fc7\u53bb\uff0c\u800c\u662f\u4ece\u4ed6\u7684\u5934\u9876\u4e00\u811a\u8e29\u4e0b\u6765\u7684\u8bdd\uff0c\u4ed6\u7684\u65e0\u7075\u76d6\u53ea\u6015\u5df2\u7ecf\u5f00\u4e86\u4e00\u4e2a\u5927\u7a9f\u7abf\uff01 \u3000\u3000\u77f3\u96f7\u662f\u6b66\u6797\u4e2d\u6210\u540d\u4eba\u7269\uff0c\u6309\u7167\u6b66\u6797\u89c4\u77e9\uff0c\u4ed6\u8f93\u4e86\u8fd9\u4e00\u62db\uff0c\u5373\u4f7f\u4e0d\u8ba4\u8f93\uff0c\u4e5f\u8be5\u7f62\u624b\u7684\u3002\u4f46 \u5bf9\u65b9\u4e43\u662f\u201c\u91d1\u56fd\u7684\u5978\u7ec6\u201d\uff0c\u5bf9\u4ed8\u201c\u5978\u7ec6\u201d\uff0c\u662f\u4e0d\u662f\u4e5f\u8981\u8bb2\u6c5f\u6e56\u89c4\u77e9\u5462\uff1f\u77f3\u96f7\u5446\u4e86\u5446\u4e4b\u540e\uff0c\u7ec8 \u4e8e\u8fd8\u662f\u53c8\u8ffd\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5728\u77f3\u96f7\u4e4b\u524d\uff0c\u5218\u5929\u5316\u5df2\u7ecf\u622a\u4f4f\u4e86\u6a80\u7fbd\u51b2\u4e86\u3002\u4ed6\u4e00\u89c1\u77f3\u96f7\u9047\u9669\uff0c\u5c31\u4f17\u8fce\u5ba2\u4ead\u91cc\u98de\u4e5f\u4f3c\u7684\u8dd1 \u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u628a\u91d1\u5200\u4e00\u6446\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8fd8\u6709\u6211\u5462\uff01\u4f60\u4eae\u5175\u5203\u9886\u6b7b\u5427\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u67c4\u5200\u91cd\u8fbe\u56db\u5341\u516b\u65a4\uff0c\u662f\u7eaf\u91d1\u6253\u6210\u7684\u3002\u56db\u5341\u516b\u65a4\u9ec4\u91d1\u94f8\u9020\u7684\u5175\u5668\uff0c\u5f53\u771f\u53ef\u8bf4\u5f97\u662f\u6700 \u201c\u8d35\u91cd\u201d\u7684\u5175\u5668\u4e86\u3002\u6a80\u7fbd\u51b2\u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\u4e86\u3002 \u3000\u3000\u4ee4\u4ed6\u5403\u60ca\u4e0d\u662f\u8fd9\u67c4\u91d1\u5200\u7684\u201c\u540d\u8d35\u201d\uff0c\u800c\u662f\u4ed6\u7684\u91cd\u91cf\u3002\u5218\u5929\u5316\u821e\u8fd9\u67c4\u56db\u5341\u516b\u65a4\u91cd\u7684\u91d1\u5200\uff0c \u5c31\u50cf\u5c0f\u5b69\u5b50\u821e\u5f04\u4e00\u6761\u7af9\u68d2\uff0c\u7528\u6765\u73a9\u800d\u4f3c\u7684\uff0c\u4e00\u70b9\u4e5f\u4e0d\u7740\u529b\u3002\u5200\u91cd\u5200\u6c89\uff0c\u62db\u6570\u53c8\u5feb\uff0c\u6a80\u7fbd\u51b2\u82e5 \u6709\u6696\u7389\u7bab\u5728\u624b\uff0c\u5f53\u7136\u4e0d\u6015\uff0c\u5982\u4eca\u4ed6\u8d64\u624b\u7a7a\u62f3\uff0c\u5982\u4f55\u80fd\u591f\u529b\u654c\uff1f\u65e0\u53ef\u5948\u4f55\uff0c\u6a80\u7fbd\u51b2\u53ea\u6709\u65bd\u5c55 \u817e\u3001\u632a\u3001\u95ea\u3001\u5c55\u7684\u5c0f\u5de7\u529f\u592b\uff0c\u4e0e\u4ed6\u6e38\u6597\uff0c\u4f3a\u673a\u8131\u8eab\u3002\u4f46\u5218\u5929\u5316\u628a\u91d1\u5200\u4f7f\u7528\uff0c\u5a01\u529b\u53ef\u6bd4\u77f3\u96f7\u7684 \u638c\u529b\u66f4\u52a0\u5389\u5bb3\uff0c\u4e5f\u66f4\u80fd\u53ca\u8fdc\u3002\u77f3\u96f7\u4f38\u957f\u624b\u81c2\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u80fd\u5230\u4e09\u5c3a\u5f00\u5916\uff0c\u6a80\u7fbd\u51b2\u5c31\u662f\u7ed9\u4ed6\u6253 \u7740\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u53d7\u70b9\u8f7b\u4f24\uff0c\u4f46\u82e5\u7ed9\u5218\u5929\u5316\u7684\u91d1\u5200\u5288\u4e2d\uff0c\u7109\u80fd\u8fd8\u6709\u547d\u5728\uff1f\u4e0d\u8fc7\u7247\u523b\uff0c\u6a80\u7fbd\u51b2\u5df2 \u5728\u4e00\u5e62\u91d1\u5149\u7684\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u60f3\u8981\u8131\u8eab\u4ea6\u5df2\u96be\u4e86\uff01 \u3000\u3000\u5267\u6597\u4e2d\u5218\u5929\u5316\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u91d1\u5200\u7adf\u7136\u671d\u5929\u7075\u76d6\u5288\u4e0b\u3002\u6a80\u7fbd\u51b2\u7a81\u4f7f\u9669\u62db\uff0c\u4e2d\u6307\u5f39 \u51fa\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u628a\u4ed6\u7684\u91d1\u5200\u5f39\u5f00\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u6709\u4e0d\u5c11\u5ba2\u4eba\u51fa\u6765\u89c2\u6218\uff0c\u5176\u4e2d\u4e0d\u4e4f\u8bc6\u8d27\u7684\u4eba\uff0c\u4e00\u89c1\u6a80\u7fbd\u51b2\u4f7f\u51fa\u6b64\u62db\uff0c\u4e0d\u7981\u90fd\u662f\u5927\u5403 \u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5f39\u6307\u795e\u901a\uff01\u201d\u5f39\u6307\u795e\u901a\u662f\u4e00\u79cd\u6781\u4e3a\u96be\u7ec3\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u6a80\u7fbd\u51b2\u770b\u6765\u8fd8\u4e0d\u5230 \u4e8c\u5341\u5c81\uff0c\u4ed6\u4eec\u600e\u4e5f\u60f3\u4e0d\u5230\u8fd9\u6837\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u4f19\u5b50\uff0c\u5df2\u7136\u7ec3\u6210\u4e86\u8fd9\u79cd\u4e0a\u4e58\u529f\u592b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u521a\u521a\u8131\u51fa\u5200\u5149\u7684\u7b3c\u7f69\uff0c\u77f3\u96f7\u5df2\u5728\u7b49\u5f85\u7740\u4ed6\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u591a\u627f\u4f60\u8ba9\u4e86\u4e00\u62db\uff0c\u8bba\u7406 \u6211\u672c\u8be5\u7f62\u624b\u7684\uff0c\u4f46\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u6bd4\u5bfb\u5e38\u6bd4\u6b66\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ef\u4e0d\u80fd\u548c\u4f60\u8bb2\u4ec0\u4e48\u89c4\u77e9\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u66fe\u53c2\u6740\u4eba\uff0c\u767e\u8f9e\u83ab\u8fa9\u3002\u4f60\u8981\u7fa4\u6bb4\uff0c\u90a3\u5c31\u4e0a\u5427\uff0c\u4e5f\u4e0d\u5fc5\u591a\u8a00\u4e86\u3002\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5218\u5929\u5316\u7684\u91d1\u5200\u53c8\u5288\u5230\uff0c\u77f3\u96f7\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u4e0e\u5218\u5927\u5316\u4e00\u5de6\u4e00 \u53f3\uff0c\u5939\u653b\u6a80\u7fbd\u51b2\uff0c\u8fd9\u4e00\u4e0b\u6a80\u7fbd\u51b2\u7684\u5f62\u52bf\u66f4\u9669\u4e86\u3002 \u3000\u3000\u597d\u5728\u77f3\u96f7\u5fc3\u4e2d\u6709\u6127\uff0c\u9739\u96f7\u638c\u7684\u5a01\u529b\uff0c\u6253\u4e86\u4e00\u70b9\u6298\u6263\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u4ecd\u662f\u8131\u56f0\u4e3a\u96c9\uff0c\u53ea \u6709\u4ed7\u7740\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u5728\u5200\u5149\u638c\u5f71\u4e2d\u7a7f\u6765\u63d2\u53bb\u3002\u597d\u51e0\u6b21\u773c\u770b\u7740\u5218\u5929\u5316\u7684\u91d1\u5200\u5c31\u8981\u780d\u5230\u4ed6\u7684\u8eab \u4e0a\uff0c\u4f46\u8fd8\u662f\u7ed9\u4ed6\u907f\u8fc7\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u95ea\u8fc7\u5218\u5929\u5316\u5288\u6765\u7684\u4e00\u5200\uff0c\u8fce\u4e0a\u77f3\u96f7\u7684\u94c1\u638c\u3002\u4ed6\u811a\u6b65\u8e09\u8dc4\uff0c\u773c\u770b\u8fd9\u4e00\u638c\u5df2\u662f\u65e0\u6cd5\u907f \u5f00\u3002\u7a81\u7136\u50cf\u9189\u6c49\u4e00\u6837\u8dcc\u5230\u77f3\u96f7\u8eab\u524d\uff0c\u8f7b\u8f7b\u4e00\u6258\u77f3\u96f7\u8098\u5c16\uff0c\u5239\u65f6\u95f4\uff0c\u4e24\u4eba\u6240\u7ad9\u7684\u4f4d\u7f6e\u5df2\u662f\u6362 \u8f6c\uff0c\u5218\u5316\u5929\u8ddf\u7740\u5288\u6765\u7684\u90a3\u4e00\u5200\uff0c\u5c31\u7adf\u7136\u662f\u671d\u7740\u77f3\u96f7\u5288\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5e78\u800c\u5218\u5929\u5316\u7684\u6b66\u529f\u4ea6\u5df2\u5230\u4e86\u80fd\u53d1\u80fd\u6536\u4e4b\u5883\uff0c\u4f17\u4eba\u60ca\u547c\u58f0\u4e2d\uff0c\u4ed6\u5200\u5cf0\u4e00\u504f\uff0c\u4ece\u77f3\u96f7\u5934\u9876\u4e0a \u65b9\u63a0\u8fc7\uff0c\u4e24\u4eba\u4e5f\u6ca1\u78b0\u4e0a\u3002\u4f46\u6a80\u7fbd\u51b2\u53c8\u5df2\u7a9c\u51fa\u53bb\u4e86\u3002\u6a80\u7fbd\u51b2\u5927\u53eb\u9053\uff1a\u201c\u6211\u662f\u6765\u6c42\u89c1\u738b\u5be8\u4e3b\u7684\uff0c \u4f60\u4eec\u8bb2\u4e0d\u8bb2\u7406\uff1f\u201d \u3000\u3000\u7126\u633a\u5598\u606f\u5df2\u5b9a\uff0c\u6251\u4e0a\u53bb\u559d\u9053\uff1a\u201c\u548c\u4f60\u8fd9\u4e2a\u91d1\u56fd\u5978\u7ec6\uff0c\u4f55\u987b\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u201d\u6a80\u7fbd\u51b2\u5fc3 \u5934\u706b\u8d77\uff0c\u91cd\u65bd\u6545\u6280\uff0c\u4f7f\u51fa\u5927\u6454\u7891\u624b\u7684\u529f\u592b\uff0c\u60f3\u628a\u4ed6\u7529\u51fa\u53bb\u3002\u9661\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u8ba9\u6211 \u6765\uff01\u201d\u52b2\u98ce\u98d2\u7136\uff0c\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u5f20\u5f00\u4e00\u628a\u6298\u94c1\u6247\uff0c\u5df2\u662f\u62a2\u5728\u7126\u633a\u524d\u9762\u6321\u4f4f\u4e86\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u6765\u7684\u662f\u738b\u5b87\u5ead\u7684\u526f\u624b\uff0c\u897f\u6d1e\u5ead\u5c71\u7684\u201c\u4e8c\u5f53\u5bb6\u201d\u5b5f\u5b8f\u3002\u4ed6\u7684\u6b66\u529f\u53ef\u6bd4\u7126\u633a\u5f3a\u7684\u591a\u4e86\u3002\u6298\u6247 \u4e00\u5f00\u4e00\u5408\uff0c\u6247\u8fb9\u950b\u5229\uff0c\u5f20\u5f00\u6765\u53ef\u5f53\u4e94\u884c\u5200\uff0c\u5408\u8d77\u6765\u53ef\u5f53\u4f5c\u5224\u5b98\u7b14\u3002\u6a80\u7fbd\u51b2\u5403\u4e8f\u5728\u6ca1\u6709\u7389\u7bab\u5728 \u624b\uff0c\u867d\u53ef\u4ee5\u62b5\u6321\uff0c\u8981\u80dc\u4ed6\u53ef\u4e5f\u4e0d\u6613\u3002\u7126\u633a\u5e76\u6ca1\u6709\u9000\u4e0b\uff0c\u4ecd\u7136\u5728\u65c1\u52a9\u653b\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u91d1\u5200\u5218\u5149 \u5316\u548c\u9ed1\u77f3\u5e84\u5e84\u4e3b\u77f3\u96f7\u4ea6\u5df2\u8d76\u5230\uff0c\u56db\u65b9\u5408\u56f4\uff0c\u6a80\u7fbd\u51b2\u672c\u9886\u518d\u9ad8\uff0c\u4e5f\u662f\u63d2\u7ffc\u96be\u9003\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8863\u8896\u4e00\u62c2\uff0c\u62c2\u5f00\u4e86\u5b5f\u5b8f\u7684\u6298\u94c1\u6247\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8ba9\u6211\u89c1\u4e86\u738b\u5be8\u4e3b\uff0c\u6211\u6b7b\u4e86\u7518 \u5fc3\uff01\u201d\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u5218\u5929\u5316\u91d1\u5200\u524a\u8fc7\uff0c\u524a\u4e86\u4ed6\u4e00\u5e45\u8863\u8896\u3002 \u3000\u3000\u5b5f\u5b8f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6349\u4e86\u4f60\u81ea\u7136\u8981\u62ff\u53bb\u732e\u7ed9\u5be8\u4e3b\u7684\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u4f60\u82e6\u5fc3\u6025\u8981\u65e9\u70b9\u89c1\u5230 \u5be8\u4e3b\uff0c\u90a3\u5c31\u4e56\u4e56\u6295\u964d\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53ef\u4e0d\u80af\u6295\u964d\u3002\u5b5f\u5b8f\u559d\u9053\uff1a\u201c\u4f60\u65e2\u8981\u987d\u6297\u5230\u5e95\uff0c\u90a3\u5c31\u4f11\u602a\u6211\u4eec\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u7126\u633a\u9053\uff1a\u201c\u662f\u554a\uff0c\u5bf9\u5ba2\u4eba\u6211\u4eec\u5f53\u7136\u8981\u8bb2\u793c\u8282\uff0c\u4f46\u8fd9\u5c0f\u5b50\u662f\u5978\u7ec6\uff0c\u4e0d\u662f\u5ba2\u4eba\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u738b\u5b87\u5ead\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u5b5f\u8001\u4e8c\uff0c\u7126\u8001\u4e09\uff0c\u4f60\u4eec\u4f4f\u624b\uff01\u8ba9\u6765\u4eba\u89c1\u6211\uff01\u201d\u738b\u5b87\u5ead\u6c89\u58f0\u559d\u9053\u3002\u4ed6\u867d\u7136\u53ea\u662f\u547d\u4ee4\u5b5f\u7126 \u4e8c\u4eba\uff0c\u4f46\u4ed6\u65e2\u7136\u8bf4\u51fa\u8981\u89c1\u6b64\u4eba\uff0c\u5f53\u7136\u4e5f\u5305\u542b\u6709\u8bf7\u6c42\u77f3\u3001\u5218\u4e8c\u4eba\u4f4f\u624b\u7684\u610f\u601d\u5728\u5185\u4e86\u3002 \u3000\u3000\u7126\u633a\u53eb\u9053\uff1a\u201c\u7980\u5be8\u4e3b\uff0c\u8fd9\u5c0f\u5b50\u662f\u2014\u2014\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u4ed6\u65e2\u6765\u6c42\u89c1\uff0c\u6211\u5c31\u5f97\u5148\u95ee\u4e2a\u6e05\u695a\uff01\u201d \u3000\u3000\u5b5f\u5b8f\u3001\u7126\u633a\u4e0d\u6562\u8fdd\u4ed6\u7684\u547d\u4ee4\uff0c\u53cc\u53cc\u9000\u4e0b\u3002\u4f46\u77f3\u3001\u5218\u4e8c\u4eba\u4f9d\u7136\u4e0d\u80af\u7f62\u4f11\u3002 \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6b66\u529f\u5f88\u5f3a\uff0c\u987b\u5f97\u9632\u4ed6\u4f7f\u8bc8\u3002\u5f85\u6211\u4eec\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u738b\u5be8\u4e3b\u3002\u4f60\u518d\u5ba1 \u95ee\u4ed6\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u98ce\u706b\u9f99\u4e0a\u524d\u548c\u738b\u5b87\u5ead\u89c1\u8fc7\u793c\uff0c\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\u5df2\u7ecf\u67e5\u5f97\u786e\u5b9e\uff0c\u8fd9\u5c0f\u5b50\u786e\u662f\u91d1\u56fd\u6d3e\u6765\u7684 \u5978\u7ec6\uff0c\u5c06\u4ed6\u62ff\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u8bba\u7ed3\u679c\u5982\u4f55\uff0c\u6211\u90fd\u4f1a\u5411\u4ee4\u5e08\u4ea4\u5f85\u3002\u98ce\u9999\u4e3b\uff0c\u4f60\u4fe1\u5f97\u8fc7\u6211\u548c\u4ee4\u5e08\u8fd8 \u6709\u8fd9\u4e2a\u4ea4\u60c5\u5427\uff1f\u201d\u98ce\u706b\u9f99\u5ffd\u7136\u5982\u6709\u6240\u89c9\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u600e\u6562\u4e0d\u76f8\u4fe1\u5be8\u4e3b\u3002\u597d\uff0c\u8fd9\u5c0f\u5b50\u5c31\u4ea4 \u7ed9\u5be8\u4e3b\u5904\u7f6e\u7f62\u3002\u201d \u3000\u3000\u77f3\u96f7\u3001\u5218\u5929\u5316\u7b49\u90fd\u662f\u6709\u70b9\u5947\u602a\uff0c\u4e0d\u89e3\u6765\u52bf\u6c79\u6c79\u7684\u98ce\u706b\u9f99\uff0c\u5ffd\u7136\u4f1a\u201c\u8f6f\u5316\u201d\u4e0b\u6765\u3002\u4ed6\u4eec\u4e0d \u77e5\uff0c\u539f\u6765\u98ce\u706b\u9f99\u5728\u548c\u738b\u5b87\u5ead\u8bf4\u8bdd\u65f6\uff0c\u5ffd\u7136\u53d1\u73b0\u738b\u5b87\u5ead\u7684\u624b\u4e0a\u4e00\u4e2a\u6307\uff0c\u90a3\u662f\u4ed6\u5e08\u7236\u72ec\u6709\u7684\u9668\u77f3 \u6307\uff0c\u662f\u5728\u6709\u975e\u5e38\u7684\u4e8b\u4ef6\u53d1\u751f\u4e4b\u65f6\u7528\u4f5c\u4fe1\u7269\u7684\u3002\u738b\u5b87\u5ead\u4eae\u51fa\u8fd9\u4e2a\u4fe1\u7269\u4e0e\u4ed6\u8bf4\u8bdd\uff0c\u7b49\u4e8e\u662f\u4ee3\u4f20\u5e08 \u547d\u3002 \u3000\u3000\u98ce\u706b\u9f99\u4e0d\u6562\u591a\u8a00\uff0c\u5218\u5929\u5316\u7b49\u4eba\u4e00\u5411\u662f\u5c0a\u656c\u738b\u5b87\u5ead\u7684\uff0c\u800c\u4e14\u6b64\u4e8b\u7531\u4e3b\u4eba\u5904\u7406\u4ea6\u662f\u6b63\u7406\uff0c\u81ea \u7136\u4ea6\u662f\u518d\u65e0\u5f02\u8bae\u4e86\u3002 \u3000\u3000\u738b\u5b87\u5ead\u628a\u6a80\u7fbd\u51b2\u5e26\u5165\u5bc6\u5ba4\uff0c\u5173\u4e0a\u95e8\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u6765\u5386\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u4ee4\u5e08\u597d \u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\u3002\u738b\u5be8\u4e3b\uff0c\u6211\u6709\u5978\u7ec6\u5acc\u7591\uff0c\u591a\u8c22\u4f60\u4e0d\u907f\u5acc\u7591\uff0c\u8fd8\u80af\u89c1\u6211\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u4ee4\u4f60\u53d7\u5c3d\u59d4\u5c48\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u559c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4fe1\u5f97\u8fc7\u6211\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u6211\u662f\u8bf4\uff1a\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u5978\u7ec6\u3002\u5e76\u975e\u8bf4\uff1a\u6211\u76f8\u4fe1\u4f60\u4e0d\u662f\u7ea4\u7ec6\u3002\u4f60\u547c\u542c\u5f97\u51fa\u5176 \u4e2d\u5206\u522b\u5417\uff1f\u201d\u4ed6\u7279\u522b\u5f3a\u8c03\u201c\u77e5\u9053\u201d\u4e8c\u5b57\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u61c2\u3002\u4fd7\u8bed\u867d\u7136\u6709\u4e91\uff1a\u6709\u5176\u7236\u5fc5\u6709\u5176\u5b50\uff0c\u6709\u5176\u5e08\u5fc5\u6709\u5176\u5f92\u3002 \u4f46\u8fd9\u79cd\u8bdd\u5176\u5b9e\u662f\u672a\u5fc5\u5c3d\u7136\u7684\u3002\u4f60\u5f53\u7136\u4e0d\u80fd\u56e0\u4e3a\u6211\u662f\u4f60\u7684\u670b\u53cb\u7684\u5f92\u5f1f\uff0c\u5c31\u6beb\u65e0\u4fdd\u7559\u7684\u76f8\u4fe1\u6211\u3002 \u4f46\u4e0d\u77e5\u4f60\u662f\u600e\u6837\u77e5\u9053\u6211\u4e0d\u662f\u5978\u7ec6\u7684\u5462\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u56e0\u4e3a\u6709\u4eba\u5df2\u7ecf\u5728\u6697\u4e2d\u67e5\u6e05\u695a\u4e86\uff0c\u77e5\u9053\u4f60\u4e0d\u662f\u5978\u7ec6\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4e10\u5e2e\u5e2e\u4e3b\u5c1a\u6606\u9633\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u98ce\u706b\u9f99\u53c8\u8bf4\u662f\u5949\u5e2e\u4e3b\u4e4b\u547d\u6349\u6211\uff0c\u96be\u9053\u4ed6\u5728\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u7b97\u8bf4\u8c0e\uff0c\u90a3\u9053\u547d\u4ee4\u662f\u5c1a\u5e2e\u4e3b\u7684\u5e08\u5f1f\uff0c\u4e10\u5e2e\u957f\u8001\u6731\u4e39\u9e64\u4ee3\u4f20\u7684\u3002\u5c1a\u6606 \u9633\u8fd8\u4e0d\u4fbf\u628a\u5b9e\u6e05\u544a\u8bc9\u5f92\u5f1f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7591\u60d1\u6781\u4e86\uff0c\u201c\u5c1a\u5e2e\u751f\u4f55\u4ee5\u77e5\u9053\u6211\u53d7\u51a4\u6789\uff0c\u77e5\u9053\u4e86\u53c8\u4e3a\u4f55\u4e0d\u4fbf\u8bf4\u660e\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u5176\u4e2d\u539f\u56e0\uff0c\u4f60\u4e0d\u5fc5\u95ee\u3002\u6709\u4e00\u5929\u4f60\u4f1a\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u4e00\u5929\u8981\u7b49\u5230\u51e0\u65f6\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u516b\u5e74\u4e0d\u4e00\u5b9a\uff0c\u5341\u5e74\u4e0d\u5b9a\u3002\u5509\uff0c\u6216\u8bb8\u3001\u6216\u8bb8\u2026\u2026\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6216\u8bb8\u5728\u6211\u4e1a\u5df2\u542b\u51a4\u800c\u6b7b\u7684\u65f6\u5019\uff0c\u8fd9\u4e00\u5929\u8fd8\u672a\u5230\u6765\u4e5f\u8bf4\u4e0d\u5b9a\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u7792\u4f60\uff0c\u5b9e\u60c5\u786e\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6124\u7136\u9053\uff1a\u201c\u4e10\u5e2e\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\uff0c\u5c1a\u5e2e\u4e3b\u7684\u4fa0\u4e49\u66f4\u662f\u5929\u4e0b\u95fb\u540d\uff0c\u4ed6\u77e5\u9053\u6211\u7684\u51a4 \u6789\uff0c\u4e3a\u4f55\u4e0d\u6765\u66ff\u6211\u8fa9\u8bec\uff1f\u8fd8\u8ba9\u4ed6\u7684\u5f92\u5f1f\u5047\u4ed6\u7684\u4e4b\u540d\u5bb3\u6211\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4ed6\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u624d\u505a\u8fd9\u573a\u620f\u7684\u3002\u5509\uff0c\u4e14\u83ab\u8bf4\u4ed6\uff0c\u5c31\u8bf4\u6211\u5427\uff0c\u6211\u660e\u77e5\u4f60\u662f\u51a4 \u6789\u4f46\u6211\u4e5f\u4e0d\u80fd\u66ff\u4f60\u4f38\u51a4\uff01\u5bf9\u4e0d\u4f4f\uff0c\u6211\u6240\u80fd\u544a\u8bc9\u53ea\u8fd9\u4e48\u591a\u4e86\uff0c\u4f60\u8981\u602a\u602a\u6211\u5427\uff01\u201d\u6a80\u7fbd\u51b2\u9ed8\u7136\u4e0d \u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u731c\u60f3\u5f97\u5230\uff0c\u5176\u4e2d\u5b9a\u6709\u7275\u8fde\u751a\u5927\u7684\u9690\u60c5\u5728 \u5185\uff0c\u4f60\u4e0d\u907f\u5acc\u7591\uff0c\u6562\u4e8e\u89c1\u6211\uff0c\u6211\u5df2\u7ecf\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u5978\u7ec6\u7684\u5acc\u7591\uff0c\u6211\u552f\u6709\u8ba4\u547d\u7f62\u5566\u3002\u4f46\u6211\u8fd8\u6709 \u4e00\u4e8b\u76f8\u6c42\uff0c\u76fc\u4f60\u4fef\u5141\u3002\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u542c\u8bf4\u820d\u59b9\u5df2\u8499\u738b\u5be8\u4e3b\u7167\u62c2\uff0c\u5c06\u5979\u643a\u56de\u6c5f\u5357\uff0c\u4e0d\u77e5\u53ef\u5426\u8ba9\u6211\u4eec\u5144\u59b9\u4e00\u89c1\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u820d\u59b9\u4e0d\u5728\u8fd9\u91cc\uff0c\u6211\u662f\u72ec\u81ea\u56de\u6c5f\u5357\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5b8c\u989c\u592b\u4eba\u9a97\u6211\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u5b8c\u989c\u592b\u4eba\u7684\u66fe\u6258\u6211\u628a\u4ee4\u59b9\u5e26\u56de\u6c5f\u5357\uff0c\u4f46\u6211\u56e0\u8def\u9014\u4e0d\u4fbf\uff0c\u6211\u53c8\u4e0d\u5584\u7167\u6599\u5c0f \u5b69\uff0c\u6545\u6b64\u6211\u628a\u5979\u8f6c\u6258\u7ed9\u522b\u4eba\u7167\u6599\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u90a3\u4e2a\u4eba\u662f\u4f60\u7684\u5e08\u7236\u4e5f\u8ba4\u8bc6\u7684\u5fc3\u5982\u795e\u5c3c\u3002\u5979\u7684\u9053\u89c2\u5728\u5c71\u897f\u6052 \u5c71\u3002\u201d \u3000\u3000\u690d\u7fbd\u51b2\u4e5f\u66fe\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\u8fd9\u4f4d\u5e08\u5c3c\uff0c\u8fd9\u4f4d\u5e08\u5c3c\u7684\u8f88\u5206\u6bd4\u4ed6\u5e08\u7236\u8fd8\u9ad8\uff0c\u6b66\u529f\u4e5f\u4e0d\u5728\u5e08\u7236\u4e4b \u4e0b\uff0c\u53ea\u662f\u6027\u60c5\u6709\u70b9\u602a\u50fb\u3002\u59b9\u59b9\u8ddf\u5979\uff0c\u81ea\u662f\u653e\u5fc3\u5f97\u4e0b\u3002 \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u8d70\u4e86\uff0c\u6211\u53eb\u4eba\u5e26\u4f60\u4ece\u540e\u5c71\u51fa\u53bb\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8d70\u4e86\uff0c\u4f60\u600e\u6837\u5411\u90a3\u4e9b\u4eba\u4ea4\u5f85\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u7684\u4e8b\u4e86\u3002\u4f60\u653e\u5fc3\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u8d77\u7591\u6211\u4e5f\u662f\u5978\u7ec6\u7684\u3002 \u4f60\u5feb\u8d70\uff0c\u4f60\u7684\u670b\u53cb\u8fd8\u5728\u7b49\u7740\u4f60\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6014\u4e86\u4e00\u6014\uff1a\u201c\u6211\u7684\u670b\u53cd\uff1f\u54e6\uff0c\u6562\u60c5\u4f60\u8bf4\u7684\u662f\u90a3\u4f4d\u949f\u59d1\u5a18\uff1f\u201d \u3000\u3000\u738b\u5b87\u5ead\u9053\uff1a\u201c\u4e0d\u9519\u3002\u949f\u4e0d\u9e23\u7684\u5b59\u5973\u513f\u5e74\u7eaa\u867d\u5c0f\uff0c\u4eba\u5374\u975e\u5e38\u80fd\u5e72\uff0c\u524d\u5e74\u5979\u66fe\u548c\u7237\u7237\u6765\u8fc7\uff0c \u9a7e\u8239\u7684\u672c\u9886\u6bd4\u5f97\u4e0a\u6211\u4eec\u5c71\u5be8\u6700\u597d\u7684\u6c34\u624b\u3002\u5979\u8fd9\u6b21\u4e0d\u6562\u6765\u89c1\u6211\uff0c\u6211\u53ef\u662f\u77e5\u9053\u5979\u6765\u4e86\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u679c\u7136\u8fd8\u5728\u6e56\u8fb9\u7b49\uff0c\u4e00\u89c1\u5c31\u95ee\uff1a\u201c\u600e\u4e48\u6837\uff0c\u738b\u5be8\u4e3b\u66ff\u4f60\u5316\u89e3\u4e86\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u6211\u53d7\u4e86\u51a4\u6789\uff0c\u4f46\u4ed6\u4e0d\u80fd\u66ff\u6211\u4f38\u51a4\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4ed6\u80af\u653e\u4f60\u8d70\uff0c\u5df2\u7b97\u662f\u96be\u5f97\u4e86\u3002\u5927\u54e5\uff0c\u4f60\u6253\u7b97\u600e\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6c5f\u5357\u4e0d\u80fd\u7acb\u8db3\uff0c\u53ea\u80fd\u56de\u6c5f\u5317\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u9001\u4f60\u8fc7\u6c5f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u6c34\u6027\u597d\uff0c\u4e0d\u8fc7\u521a\u624d\u4e0b\u6c34\u65f6\u5019\uff0c\u542c\u5f97\u62a4\u9001\u6211\u7684\u90a3\u4e2a\u5934\u76ee\u8bf4\uff0c\u91d1\u56fd \u5373\u5c06\u5357\u4fb5\uff0c\u957f\u6c5f\u4ee5\u5317\uff0c\u5df2\u88ab\u91d1\u56fd\u7684\u6c34\u5e08\u5c01\u9501\uff0c\u6c34\u8def\u6050\u6015\u662f\u4e0d\u884c\u4e86\u3002\u738b\u5be8\u4e3b\u4e5f\u4ee5\u4e3a\u4f60\u53ea\u662f\u9001\u6211 \u51fa\u592a\u6e56\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8d70\u9646\u8def\uff0c\u4f60\u66f4\u9700\u8981\u6211\u5e2e\u4f60\u4e86\u3002\u5927\u54e5\uff0c\u4f60\u53ef\u522b\u7b11\u6211\u5938\u53e3\uff0c\u6211\u603b\u6bd4\u4f60\u719f\u6089\u70b9\u6c5f \u6e56\u8def\u9053\u3002\u5982\u4eca\u5b98\u5e9c\u7684\u4eba\u8981\u6349\u4f60\uff0c\u9ed1\u9053\u7684\u4eba\u4e5f\u548c\u4f60\u7ed3\u6028\uff0c\u6211\u7ed9\u4f60\u5e26\u8def\uff0c\u6700\u5c11\u53ef\u5e2e\u4f60\u8d8b\u5409\u907f \u51f6\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\uff1a\u201c\u4f46\u4f60\u7237\u7237\u4e00\u4e2a\u4eba\u5728\u5bb6\u4e61\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u7237\u7237\u4e5f\u65e9\u5df2\u6599\u5230\u4f60\u7ec8\u987b\u8981\u56de\u53bb\uff0c\u4ed6\u5e76\u6559\u4e86\u6211\u8bb8\u591a\u8d8b\u5409\u907f\u51f6\u7684\u6cd5\u95e8\u5462\u3002\u4f55\u51b5 \u6211\u53c8\u4e0d\u80fd\u7ec8\u8eab\u8ddf\u4f60\uff0c\u9001\u4f60\u8fc7\u4e86\u6c5f\u754c\uff0c\u6211\u8fd8\u662f\u8981\u56de\u5bb6\u7684\uff0c\u4e5f\u7528\u4e0d\u4e86\u591a\u5c11\u65f6\u65e5\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd \u5730\u7b11\u9053\uff1a\u201c\u9664\u975e\u4f60\u80af\u6536\u7559\u6211\u505a\u4e2b\u5934\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u522b\u8bf4\u7b11\u4e86\uff0c\u4f60\u5bb6\u7684\u5927\u6069\uff0c\u6211\u771f\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u53c8\u6765\u4e86\uff0c\u4f60\u53eb\u5f97\u6211\u505a\u59b9\u5b50\uff0c\u5144\u59b9\u4e4b\u95f4\uff0c\u4e5f\u8981\u8bb2\u62a5\u7b54\u7684\u5417\uff1f\u4e0a\u8239\u5427\u3002\u201d \u3000\u3000\u8fc7\u4e86\u592a\u6e56\uff0c\u6539\u8d70\u65f1\u8def\uff0c\u949f\u7075\u79c0\u679c\u7136\u662f\u4e2a\u975e\u5e38\u597d\u7684\u5e26\u8def\u4eba\uff0c\u5e2e\u6a80\u7fbd\u51b2\u907f\u8fc7\u8bb8\u591a\u98ce\u9669\u3002\u4f46\u521a \u521a\u8e0f\u5165\u8fb9\u754c\u7684\u8303\u56f4\uff0c\u5c31\u53d1\u751f\u4e00\u4ef6\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u659c\u9633\u5982\u8840\uff0c\u4e00\u6279\u9a91\u5175\uff0c\u7ea6\u6709\u767e\u4eba\uff0c\u5e26\u5934\u7684\u519b\u5b98\u6b63\u662f\u90a3\u4e2a\u7ef0\u53f7\u201c\u5357\u5c71\u864e\u201d\u7684\u5357 \u5bab\u9020\u3002\u53e6\u4e00\u4e2a\u519b\u5b98\u8eab\u62ab\u6597\u7bf7\uff0c\u62c9\u5f97\u5f88\u4f4e\uff0c\u4f46\u8fd8\u662f\u770b\u5f97\u89c1\u4ed6\u7684\u9762\u5b54\uff0c\u7adf\u7136\u662f\u6a80\u4e16\u82f1\u3002\u539f\u6765\u5357\u5bab \u9020\u662f\u5949\u6c64\u601d\u9000\u4e4b\u547d\uff0c\u7528\u8fd9\u4e2a\u6cd5\u5b50\uff0c\u62a4\u9001\u6765\u4e34\u5b89\u7684\u91d1\u56fd\u5bc6\u4f7f\u6a80\u4e16\u82f1\u51fa\u5883\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5927\u5403\u4e00\u60ca\uff0c\u770b\u60c5\u5f62\u662f\u907f\u4e0d\u5f00\u4e86\uff0c\u4ed6\u6025\u5fd9\u70b9\u4e86\u949f\u7075\u79c0\u7684\u6655\u7761\u7a74\uff0c\u5c06\u5979\u85cf\u4eba\u4e71\u8349\u4e1b \u4e2d\u3002 \u3000\u3000\u8fdc\u8fdc\u8fd8\u6709\u4e00\u6279\u4eba\u9a6c\uff0c\u4f3c\u662f\u5378\u5c3e\u8ffd\u6765\uff0c\u5148\u5934\u7684\u9996\u9886\u58f0\u82e5\u6d2a\u949f\uff0c\u559d\u9053\uff1a\u201c\u5357\u5c71\u864e\uff0c\u6536\u961f\u505c\u5728 \u539f\u5730\uff0c\u542c\u89c1\u6ca1\u6709\uff1f\u201d\u5357\u5c71\u864e\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5b98\u5175\u4e0d\u67e5\u95ee\u4f60\u4eec\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4f60\u4eec\u53cd\u6765\u67e5\u5b98 \u5175\uff01\u201d \u3000\u3000\u201c\u8c01\u53eb\u4f60\u8ba9\u5978\u7ec6\u6df7\u5728\u519b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u90a3\u4e2a\u662f\u5978\u7ec6\uff1f\u201d \u3000\u3000\u201c\u5728\u4f60\u540e\u9762\u7684\u90a3\u4e2a\u519b\u5b98\uff0c\u4f60\u53eb\u4ed6\u4e0b\u9a6c\u6765\uff0c\u7ed9\u6211\u4eec\u770b\u4e2a\u6e05\u695a\uff0c\u4e0d\u662f\u5978\u7ec6\uff0c\u6211\u4eec\u5c31\u653e\u8fc7 \u4ed6\u3002\u201d \u3000\u3000\u4e3a\u9996\u7684\u90a3\u4e2a\u6765\u5f97\u8fd1\u4e86\uff0c\u6a80\u7fbd\u51b2\u4e5f\u770b\u5f97\u751a\u4e3a\u6e05\u695a\u4e86\uff0c\u662f\u4e34\u5b89\u4e10\u5e2e\u5206\u8235\u7684\u8235\u4e3b\u9a6c\u5929\u884c\uff0c\u53e6\u5916 \u7ea6\u6709\u4e00\u5c0f\u534a\u4eba\u662f\u5728\u897f\u6d1e\u5ead\u5c71\u4e0a\u89c1\u8fc7\u7684\uff0c\u4f46\u5374\u4e0d\u77e5\u4ed6\u4eec\u7684\u540d\u5b57\u3002\u8fd9\u6279\u4eba\u9a6c\u4e0d\u8fc7\u4e8c\u4e09\u5341\u4eba\uff0c\u4f46\u56e0 \u90fd\u662f\u6c5f\u6e56\u597d\u6c49\uff0c\u5b98\u5175\u8fd8\u771f\u4e0d\u6562\u548c\u4ed6\u4eec\u4f5c\u5bf9\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u65b9\u81ea\u601d\u7591\uff0c\u201c\u4ed6\u4eec\u771f\u7684\u77e5\u9053\u4e86\u4e16\u82f1\u662f\u6211\u7684\u5802\u5144\u5f1f\uff0c\u76f8\u8c8c\u672c\u6765\u548c\u6211\u6709\u70b9\u76f8\u4f3c\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u8fdb\uff0c\u6a80\u4e16\u82f1\u4ea6\u5df2\u53d1\u73b0\u5c71\u5761\u4e0a\u7684\u6a80\u7fbd\u51b2\u4e86\u3002 \u3000\u3000\u6a80\u4e16\u82f1\u5fc3\u4e2d\u5927\u559c\uff0c\u201c\u8fd9\u6b63\u662f\u5929\u52a9\u6211\u4e5f\uff01\u201d\u62e8\u8f6c\u9a6c\u5934\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u770b\u6e05\u695a\uff0c\u5978\u7ec6\u5728\u90a3\u513f \u5462\uff1f\u201d\u5357\u5c71\u864e\u63a5\u7740\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u6b63\u662f\u4e3a\u8ffd\u6355\u5978\u7ec6\u624d\u8ffd\u5230\u8fb9\u754c\u6765\u7684\uff0c\u8c01\u8981\u662f\u5c06\u4ed6\u6d3b\u6349\uff0c \u8d4f\u94f6\u4e07\u4e24\uff0c\u6349\u4e0d\u5230\u6d3b\u7684\uff0c\u53ea\u8981\u5f97\u5230\u4ed6\u7684\u8111\u888b\uff0c\u4e5f\u8d4f\u94f6\u4e94\u5343\u4e24\uff01\u201d\u8fd9\u756a\u8bf4\u6cd5\u8868\u9762\u662f\u5bf9\u519b\u5b98\u8bb8 \u613f\uff1b\u5176\u5b9e\u4e5f\u662f\u8bf4\u7ed9\u90a3\u73ed\u6c5f\u6e56\u597d\u6c49\u542c\u7684\u3002 \u3000\u3000\u6709\u51e0\u4e2a\u4e0d\u8ba4\u8bc6\u6a80\u7fbd\u51b2\u7684\u6c5f\u6e56\u597d\u6c49\u4e0d\u5f85\u5357\u5c71\u864e\u628a\u8bdd\u8bf4\u5b8c\uff0c\u5c31\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u9a6c\u8235\u4e3b\uff0c\u4f60\u662f\u89c1 \u8fc7\u5978\u7ec6\u7684\uff0c\u8c01\u771f\u8c01\u5047\uff1f\u201d \u3000\u3000\u9a6c\u5929\u884c\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u8fd9\u5c0f\u5b50\u6b63\u662f\u4e0a\u4e2a\u6708\u7684\u5077\u5165\u4e34\u5b89\u7684\u5978\u7ec6\uff0c\u6211\u8fd8\u548c\u4ed6\u6253\u8fc7\u4e00\u67b6 \u5462\uff01\u201d \u3000\u3000\u90a3\u73ed\u6c5f\u6e56\u597d\u6c49\u867d\u7136\u4e0d\u7a00\u7f55\u671d\u5ef7\u7684\u8d4f\u94f6\uff0c\u4f46\u542c\u5f97\u8fd9\u5c0f\u5b50\u201c\u679c\u7136\u771f\u662f\u201d\u5978\u7ec6\uff0c\u81ea\u7136\u4e89\u5148\u6050\u540e \u7684\u8dd1\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e00\u4e2a\u5927\u6c49\u5df2\u662f\u5411\u6a80\u7fbd\u51b2\u6251\u6765\uff0c\u6a80\u7fbd\u51b2\u4f9d\u7a00\u8ba4\u5f97\u662f\u5728\u897f\u6d1e\u5ead\u5c71\u4e0a\u89c1\u8fc7 \u7684\uff0c\u5f53\u4e0b\u4e00\u4e2a\u79fb\u5f62\u6362\u4f4d\uff0c\u95ea\u5f00\u4ed6\u7684\u4e00\u6251\uff0c\u53cd\u624b\u6293\u7740\u4ed6\u80cc\u90e8\u7684\u8170\u5e26\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f24\u4f60\uff0c\u4f60\u56de \u53bb\u5427\uff01\u201d \u3000\u3000\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u5c06\u4ed6\u63b7\u51fa\u3001\u521a\u597d\u628a\u4e00\u4e2a\u4e5f\u5728\u5411\u4ed6\u51b2\u6765\u7684\u9a91\u7740\u9a6c\u7684\u519b\u5b98\u649e\u843d\u9a6c\u80cc\uff0c\u90a3\u4e2a\u5927 \u6c49\u5374\u7aef\u7aef\u6b63\u6b63\u7684\u5750\u5728\u9a6c\u4e0a\uff0c\u5c31\u597d\u50cf\u662f\u7ed9\u4e00\u53ea\u65e0\u5f62\u5de8\u624b\uff0c\u5c06\u4ed6\u8f7b\u8f7b\u63d0\u8d77\uff0c\u653e\u5728\u9a6c\u4e0a\u4e00\u822c\u3002\u6a80\u7fbd \u51b2\u7528\u7684\u529b\u5ea6\u4e4b\u5de7\uff0c\u771f\u662f\u532a\u5937\u6240\u601d\u3002\u90a3\u6c49\u5b50\u5446\u4e86\u4e00\u5446\u3002\u62e8\u8f6c\u9a6c\u5934\u8dd1\u56de\u53bb\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u9a91\u7740\u9a6c\u7684\u519b\u5b98\u53c8\u51b2\u8fc7\u6765\u4e86\u3002\u6a80\u7fbd\u51b2\u98de\u51fa\u4e00\u5757\u77f3\u5934\uff0c\u6253\u7740\u9a6c\u7684\u8111\u888b\uff0c\u519b\u5b98\u8fde\u4eba\u5e26\u9a6c \u6eda\u4e0b\u5c71\u5761\uff0c\u4e5f\u4e0d\u77e5\u662f\u6b7b\u662f\u6d3b\u3002 \u3000\u3000\u8ddf\u7740\u53c8\u662f\u4e24\u6761\u5927\u6c49\u5411\u4ed6\u6251\u6765\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u6211\u548c\u4f60\u62fc\u4e86\uff01\u201d\u8fd9\u4e24\u4eba\u7528\u7684\u90fd\u662f\u91cd\u5175\u5668\uff0c\u4e00 \u4e2a\u662f\u4f7f\u5ba3\u82b1\u5927\u65a7\uff0c\u4e00\u4e2a\u662f\u4f7f\u539a\u80cc\u780d\u5c71\u5200\uff0c\u6c14\u529f\u6df1\u96c4\uff0c\u6b66\u529b\u4e5f\u5f53\u771f\u4e0d\u5f31\u3002\u6a80\u7fbd\u51b2\u65e0\u6cd5\u50cf\u5bf9\u4ed8\u7b2c \u4e00\u4e2a\u6c49\u5b50\u90a3\u6837\uff0c\u7528\u5de7\u52b2\u5c06\u4ed6\u4eec\u540c\u65f6\u6293\u4f4f\u3001\u63b7\u5f00\uff0c\u53ea\u597d\u4f7f\u51fa\u56db\u4e24\u62e8\u5343\u65a4\u7684\u529f\u592b\uff0c\u6258\u7740\u4f7f\u5200\u6c49\u5b50 \u7684\u624b\u638c\uff0c\u5c06\u4ed6\u7684\u5927\u5200\u8f7b\u8f7b\u4e00\u62e8\uff0c\u8ba9\u5927\u5200\u548c\u5ba3\u82b1\u65a7\u78b0\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6c49\u5b50\u7684\u6c14\u529b\u6b63\u597d\u534a\u65a4\u516b\u4e24\uff0c\u4e24\u4ef6\u5175\u5668\u78b0\u5728\u4e00\u8d77\uff0c\u540c\u65f6\u88ab\u9707\u5f97\u864e\u53e3\u6d41\u8840\uff0c\u5012\u5728\u5730 \u4e0a\u3002\u7528\u5200\u7684\u88ab\u65a7\u5934\u5288\u5f00\u5934\u9885\uff0c\u7528\u65a7\u7684\u88ab\u5927\u529b\u780d\u65ad\u7684\u8116\u5b50\uff0c\u4e24\u4eba\u90fd\u662f\u4e0d\u80fd\u6d3b\u4e86\u3002 \u3000\u3000\u9a6c\u5929\u884c\u5df2\u7ecf\u4e0a\u4e86\u5c71\u88ab\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u89c1\u690d\u7fbd\u51b2\u653e\u4e86\u4ed6\u4eec\u4e00\u4e2a\u4eba\u56de\u6765\uff0c\u800c\u4e14\u6240\u7528\u7684\u624b\u6cd5\u4e4b \u5de7\uff0c\u4ee4\u5f97\u90a3\u4eba\u6beb\u53d1\u65e0\u4f24\uff0c\u7684\u786e\u662f\u6709\u5fc3\u4fdd\u90a3\u4eba\u6027\u547d\u3002\u9a6c\u5929\u884c\u4e5f\u4e0d\u7981\u6709\u70b9\u601d\u7591\u4e0d\u5b9a\u4e86\u3002\u4e0d\u9519\uff0c\u6a80 \u7fbd\u51b2\u4e5f\u201c\u6740\u201d\u4e86\u4ed6\u4eec\u7684\u4e24\u4e2a\u4eba\uff0c\u4f46\u9a6c\u5929\u884c\u770b\u5f97\u6e05\u695a\uff0c\u5728\u5f53\u65f6\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u6a80\u7fbd\u51b2\u82e5\u7136\u4e0d\u662f\u7528 \u90a3\u4e00\u62db\u5de7\u5999\u7684\u624b\u6cd5\uff0c\u5c06\u90a3\u4e24\u4eba\u7684\u91cd\u5175\u5668\u62e8\u5f00\uff0c\u4ed6\u81ea\u5df1\u5148\u5c31\u8981\u6b7b\u5728\u5200\u65a7\u4e4b\u4e0b\u3002\u8bbe\u8eab\u5904\u5730\uff0c\u9a6c\u5929 \u884c\u4e5f\u4e0d\u80fd\u8981\u6c42\u4ed6\u5f15\u9888\u5c31\u622e\u7684\u3002 \u3000\u3000\u9a6c\u5929\u884c\u6697\u81ea\u601d\u7591\uff1a\u201c\u5728\u897f\u6d1e\u5ead\u5c71\u65f6\uff0c\u738b\u5be8\u4e3b\u5728\u4e1a\u5df2\u77e5\u9053\u6b64\u4eba\u662f\u91d1\u56fd\u5978\u7ec6\u4e4b\u540e\uff0c\u4ecd\u7136\u5c06\u4ed6 \u653e\u8d70\uff0c\u867d\u8bf4\u6c5f\u6e56\u4e0a\u6709\u4e0d\u80fd\u4e3a\u96be\u5ba2\u4eba\u7684\u89c4\u77e9\uff0c\u4f46\u7a76\u7adf\u4e0d\u4f3c\u738b\u5be8\u4e3b\u7684\u5bfb\u5e38\u884c\u4e8b\uff0c\u83ab\u975e\u53e6\u6709\u9690 \u60c5\uff1f\u201d\u518d\u770b\u773c\u524d\u4e4b\u4e8b\uff0c\u8fd9\u201c\u5978\u7ec6\u201d\u4f3c\u4e4e\u4e5f\u5e76\u4e0d\u592a\u574f\uff0c\u201c\u5426\u5219\u201d\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8d76\u5c3d\u6740\u7edd\uff1f\u201d \u3000\u3000\u4e0d\u8fc7\u3002\u9a6c\u5929\u884c\u867d\u7136\u5f00\u59cb\u8d77\u4e86\u6000\u7591\u3002\u4f46\u4ed6\u8eab\u4e3a\u4e10\u5e2e\u7684\u4e00\u4e2a\u5206\u8235\u7684\u8235\u4e3b\uff0c\u800c\u4e14\u662f\u6c5f\u5357\u6700\u91cd\u8981 \u7684\u5206\u8235\u8235\u4e3b\uff0c\u4ed6\u53c8\u600e\u6562\u8fdd\u6297\u98ce\u706b\u9f99\u6240\u4f20\u7684\u547d\u4ee4\uff0c\u8fde\u6000\u7591\u603b\u8235\u4e3b\u53ef\u80fd\u662f\u51a4\u6789\u4e86\u597d\u4eba\u7684\u60f3\u6cd5\uff0c\u4ed6 \u4e5f\u89c9\u5f97\u4e0d\u8be5\uff0c\u4ed6\u54ac\u4e86\u54ac\u7259\uff0c\u201c\u5b81\u53ef\u6740\u9519\uff0c\u4e0d\u53ef\u653e\u9519\uff01\u201d\u4fbf\u5373\u7387\u4f17\u4e0a\u5c71\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u9707\u8033\u6b32\u804b\u7684\u91d1\u9f13\u58f0\uff01 \u3000\u3000\u6765\u7684\u662f\u91d1\u56fd\u8fb9\u5173\u603b\u5175\u8428\u62c9\u6c57\u5e26\u9886\u7684\u4e00\u652f\u5175\u9a6c\u3002 \u3000\u3000\u8428\u62c9\u6c57\u8000\u6b66\u626c\u5a01\u559d\u9053\uff1a\u201c\u4f60\u4eec\u64c5\u51fa\u9632\u533a\uff0c\u662f\u5426\u60f3\u6765\u6311\u8845\uff1f\u201d \u3000\u3000\u91d1\u5b8b\u4e24\u56fd\u63a5\u58e4\u4e4b\u5904\uff0c\u4ece\u5b8b\u56fd\u8fb9\u754c\u5c0f\u9547\u77e2\u96c6\u7b97\u8d77\u5230\u91d1\u56fd\u6240\u8bbe\u7684\u8fb9\u5173\u4e0a\uff0c\u7ea6\u6709\u4e09\u5341\u91cc\u65e0\u4eba\u5730 \u5e26\uff0c\u5728\u519b\u4e8b\u4e0a\u79f0\u4e3a\u201c\u7f13\u51b2\u533a\u201d\u3002\u6487\u5f00\u8fd9\u4e9b\u5730\u65b9\u672c\u6765\u662f\u5b8b\u56fd\u7684\u7586\u571f\u4e0d\u8c08\uff0c\u5373\u4f9d\u7167\u5b8b\u5ba4\u5357\u6e21\u4e4b\u540e \u7684\u201c\u65e2\u6210\u4e8b\u5b9e\u201d\uff0c\u8fd9\u4e2a\u5730\u65b9\u4e5f\u662f\u53cc\u65b9\u90fd\u7ba1\u4e0d\u7740\u7684\u201c\u7f13\u51b2\u533a\u201d\u3002\u800c\u5b8b\u5175\u6b64\u523b\u6240\u5728\u4e4b\u5904\uff0c\u53ea\u4e0d\u8fc7 \u79bb\u5f00\u77e2\u96c6\u4e94\u91cc\u4e4b\u5730\uff0c\u5927\u5bb6\u90fd\u8fdb\u5165\u201c\u7f13\u51b2\u533a\u201d\uff0c\u4e5f\u8fd8\u662f\u91d1\u5175\u6df1\u5165\u7684\u3002\u8428\u62c9\u6c57\u8bf4\u7684\u5f53\u7136\u53ea\u662f\u4e2a\u501f \u53e3\u3002 \u3000\u3000\u5357\u5c71\u864e\u88c5\u4f5c\u4e0d\u77e5\u6240\u63aa\u7684\u795e\u60c5\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ea\u662f\u6765\u88ad\u532a\u7684\uff0c\u8d35\u56fd\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u6a80\u4e16\u82f1\u5219\u88c5\u4f5c\u6fc0\u6602\u6177\u6168\u7684\u6837\u5b50\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8bb2\u4e0d\u8bb2\u7406\uff0c\u8fd9\u662f\u6211\u4eec\u5b8b\u56fd\u7684\u5730\u65b9\uff01\u201d \u3000\u3000\u8428\u62c9\u6c57\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u548c\u4f60\u8bb2\u7406\uff01\u201d\u4e00\u4f38\u624b\u5c06\u4ed6\u63ea\u4e0b\u9a6c\uff0c\u559d\u4ee4\u624b\u4e0b\uff0c\u201c\u5c06\u4ed6\u7ed1\u4e86\u56de \u53bb\uff01\u201d\u7f1a\u4ed6\u65f6\u5374\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8d1d\u5b50\u6055\u7f6a\uff0c\u54b1\u4eec\u53ea\u80fd\u5982\u6b64\u505a\u620f\u3002\u201d \u3000\u3000\u5b98\u5175\u5c3d\u90fd\u8dd1\u4e86\uff0c\u90a3\u73ed\u6c5f\u6e56\u597d\u6c49\u5219\u8fd8\u5728\u5c71\u5761\u4e0a\u3002\u9a6c\u5929\u884c\u5f53\u673a\u7acb\u65ad\uff0c\u559d\u9053\uff1a\u201c\u5feb\uff0c\u64d2\u4e0b\u4ed6\u4eec \u7684\u8d1d\u5b50\uff01\u201d\u4ed6\u662f\u610f\u6b32\u628a\u6a80\u7fbd\u51b2\u4e3a\u4eba\u8d28\u65b9\u80fd\u7a81\u56f4\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u60f3\u548c\u4ed6\u4eec\u52a8\u624b\uff0c\u53c8\u4e0d\u7518\u88ab\u64d2\uff0c\u53ea\u597d\u8dd1\u5f80\u9ad8\u5904\uff0c\u6682\u907f\u4e00\u65f6\u3002 \u3000\u3000\u91d1\u519b\u961f\u4f0d\u4e2d\uff0c\u7a81\u7136\u98de\u51fa\u4e00\u56e2\u7ea2\u5f71\uff0c\u8fd9\u4e2a\u4eba\u6b63\u662f\u7389\u9762\u5996\u72d0\u8d6b\u8fde\u6e05\u6ce2\uff0c\u4eba\u672a\u5230\uff0c\u6697\u5668\u5148\u53d1\uff0c \u662f\u5979\u7684\u72ec\u95e8\u6697\u5668\u201c\u6bd2\u9738\u91d1\u9488\u70c8\u7130\u5f39\u201d\uff0c\u201c\u84ec\u201d\u7684\u70b8\u5f00\u3002\u70df\u96fe\u5f25\u6f2b\uff0c\u91d1\u9488\u56db\u5c04\u3002\u6c5f\u6e56\u597d\u6c49\u6709\u4e2d \u6bd2\u660f\u8ff7\u7684\uff0c\u6709\u53d7\u4e86\u6885\u82b1\u9488\u5c04\u4f24\u7684\uff0c\u5012\u4e0b\u4e86\u4e00\u534a\u3001\u5269\u4e0b\u7684\u5341\u591a\u4e2a\u4eba\uff0c\u600e\u80fd\u62b5\u6321\u6f6e\u6c34\u822c\u6d8c\u6765\u7684\u91d1 \u56fd\u9a91\u5175\uff0c\u53ea\u597d\u843d\u8352\u800c\u9003\u3002\u90a3\u4e9b\u53d7\u4f24\u7684\u597d\u6c49\uff0c\u4e5f\u90fd\u5728\u94c1\u9a91\u8df5\u8e0f\u4e4b\u4e0b\u4e27\u751f\u3002\u91d1\u5175\u5168\u64a4\u8d70\u4e86\u3002\u53ea\u7559 \u4e0b\u4e00\u4e2a\u8d6b\u8fde\u6e05\u6ce2\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u627e\u7740\u6a80\u7fbd\u51b2\u67d4\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u662f\u6211\u8fde\u7d2f\u4f60\u4e86\u3002\u6211\u77e5\u9053\u4f60\u60f3\u548c\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u5316 \u654c\u4e3a\u53cb\uff0c\u4f46\u53ef\u60dc\u7ecf\u8fc7\u4eca\u65e5\u8fd9\u4e8b\uff0c\u4ed6\u4eec\u6050\u6015\u662f\u66f4\u96be\u8c05\u89e3\u4f60\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u53eb\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5bf9\u4f60\u8d54\u4e0d\u662f\u4e86\uff0c\u4f60\u8fd8\u8981\u600e\u6837\uff1f\u518d\u8bf4\uff0c\u4f60\u4e5f\u6740\u4e86\u51e0\u540d\u6c5f\u5357\u597d\u6c49\uff0c \u80fd\u591f\u5168\u90e8\u602a\u6211\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u50cf\u4e00\u652f\u5229\u7bad\u5c04\u4f24\u4e86\u4ed6\u7684\u5fc3\uff0c\u6a80\u7fbd\u51b2\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u8001\u662f\u7f20\u7740\u6211\u5e72\u5417\uff1f\u6211\u4e0d\u60f3\u518d\u89c1 \u4f60\uff0c\u4f60\u8d70\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u5728\u6c5f\u5357\u7684\u4e8b\u60c5\uff0c\u6211\u5168\u90fd\u77e5\u9053\u3002\u82e5\u4e0d\u56de\u5fc3\u8f6c\u610f\uff0c\u53ea\u6015\u5929\u5730\u518d\u5927\uff0c\u4e5f\u96be\u6709 \u4f60\u5bb9\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u7b97\u662f\u6211\u6b20\u4e86\u4f60\u7684\u60c5\uff0c\u4f60\u628a\u6211\u7684\u8111\u888b\u5e26\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u600e\u7684\u5982\u6b64\u4e0d\u77e5\u597d\u6b79\uff0c\u6211\u662f\u4e3a\u4e86\u4f60\u7740\u60f3\uff0c\u4e0d\u5982\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8981\u561b\u4f60\u5272\u4e0b\u6211\u7684\u8111\u888b\uff0c\u5426\u5219\u2014\u2014\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5426\u5219\u600e\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8d70\u4f60\u7684\u9633\u5173\u9053\uff0c\u6211\u8d70\u6211\u7684\u72ec\u6728\u6865\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u53f9\u4e86\u53e3\u6c14\uff0c\u53ea\u80fd\u8d70\u4e86\u3002 \u3000\u3000----------- \u3000\u3000\u7fbd\u751f\u5802 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u7ea2\u989c\u8584\u547d \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u4e94\u56de\u3000\u7ea2\u989c\u8584\u547d \u3000\u3000\u949f\u7075\u79c0\u4e00\u5f20\u773c\u775b\uff0c\u5c31\u6293\u7262\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5927\u54e5\uff0c\u522b\u629b\u5f00\u6211\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u597d\u597d\u5728\u4f60\u8eab\u8fb9\u5417\uff1f\u521a\u624d\u6211\u70b9\u4f60\u7684\u7a74\u9053\uff0c\u53ea\u662f\u56e0\u4e3a\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u77e5\u662f\u6015\u8fde\u7d2f\u4e86\u6211\uff0c\u53ef\u662f\u6211\u53ea\u662f\u60f3\u4e0e\u4f60\u540c\u751f\u5171\u6b7b\uff0c\u4e0d\u613f\u6709\u96be\u53ea\u662f\u7531\u4f60\u62c5 \u5f53\u3001\u55ef\uff0c\u4e0d\u8fc7\u73b0\u5728\u4e00\u60f3\uff0c\u8fd8\u662f\u4f60\u505a\u5f97\u5bf9\uff0c\u521a\u624d\u8981\u662f\u6709\u6211\u5728\u65c1\uff0c\u53cd\u800c\u8981\u7d2f\u4f60\u5206\u795e\u7167\u987e\u6211\u4e86\u3002\u90a3 \u4e9b\u5b98\u5175\u5462\uff1f\u54a6\uff0c\u54b1\u4eec\u597d\u50cf\u4e0d\u662f\u5728\u539f\u6765\u7684\u5730\u65b9\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u4e9b\u5b98\u5175\u6ca1\u6709\u53d1\u73b0\u6211\u4eec\uff0c\u4e0d\u8fc7 \u539f\u6765\u7684\u5730\u65b9\u4e0d\u662f\u85cf\u8eab\u7684\u597d\u5904\u6240\uff0c\u6240\u4ee5\u5b98\u5175\u4e00\u8fc7\uff0c\u6211\u5c31\u628a\u4f60\u8f6c\u5230\u6797\u4e2d\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u597d\u50cf\u8fd8\u6709\u53e6\u4e00\u6279\u4eba\u9a6c\uff0c\u90a3\u53c8\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u4ed6\u4eec\u662f\u8ddf\u5728\u5b98\u519b\u540e\u9762\u7684\uff0c\u6211\u5728\u5b98\u519b\u8d70\u540e\u4ed6\u4eec\u5c1a\u672a\u6765\u5230\u4e4b\u524d\uff0c\u5c31\u548c \u4f60\u8d70\u4e86\u7684\u3002\u201d \u3000\u3000\u4ed6\u662f\u5bb3\u6015\u949f\u7075\u79c0\u4e3a\u4ed6\u62c5\u5fe7\uff0c\u662f\u4ee5\u53ea\u80fd\u9690\u7792\u4e8b\u5b9e\uff0c\u7f16\u9020\u8c0e\u8a00\u3002 \u3000\u3000\u4e0d\u8fc7\u949f\u7075\u79c0\u53c8\u600e\u80fd\u4e0d\u62c5\u5fe7\u5462\uff0c\u5c3d\u7ba1\u5979\u5e76\u4e0d\u7591\u5fc3\u6a80\u7fbd\u51b2\u8bf4\u8c0e\u3002 \u3000\u3000\u201c\u6211\u770b\u90a3\u4e9b\u4eba\u6050\u6015\u5c31\u662f\u4ece\u4e34\u5b89\u6765\u641c\u7d22\u4f60\u7684\u9ed1\u9053\u4e2d\u4eba\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4e0e\u4e0d\u662f\uff0c\u53ea\u8981\u6211\u73b0\u5728\u8fd8\u662f\u597d\u597d\u7684\u6d3b\u7740\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f46\u73b0\u5728\u521a\u8e0f\u5165\u8fb9\u754c\uff0c\u5c31\u53d1\u73b0\u8fd9\u4e24\u6279\u4eba\u9a6c\uff0c\u6211\u53ea\u6015\u4eca\u540e\u66f4\u52a0\u5bf8\u6b65\u96be\u884c\uff01\u770b\u6765 \u53ea\u6709\u53bb\u6c42\u5343\u67f3\u5e84\u7684\u5e84\u4e3b\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u5e84\u4e3b\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u201d \u3000\u3000\u4ef2\u7075\u79c0\u9053\uff1a\u201c\u5343\u67f3\u5e84\u6b63\u662f\u5728\u91d1\u5b8b\u4e24\u56fd\u4ea4\u754c\u4e4b\u5904\uff0c\u5e84\u4e3b\u53eb\u67f3\u5143\u7532\uff0c\u4e0d\u4f46\u548c\u9ed1\u767d\u4e24\u90fd\u6709\u4ea4 \u60c5\uff0c\u751a\u81f3\u91d1\u5b8b\u4e24\u56fd\u7684\u8fb9\u5173\u5c06\u58eb\uff0c\u4ed6\u4e5f\u6709\u6765\u4f4f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u90a3\u6b7b\u53bb\u7684\u7239\u7239\u548c\u4ed6\u6709\u70b9\u4ea4\u60c5\uff0c\u6211\u5c0f\u65f6\u5019\u6216\u8005\u89c1\u8fc7\u4ed6\uff0c\u4f46\u4ed6\u4e00\u5b9a\u8bb0\u4e0d\u8d77 \u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u4e48\u6211\u65b9\u4fbf\u53bb\u89c1\u4ed6\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6709\u8fc7\u4e0a\u6b21\u6c42\u738b\u5b87\u5ead\u7684\u7ecf\u9a8c\uff0c\u5fc3\u60f3\u5373\u4f7f\u6240\u6c42\u4e0d\u9042\uff0c\u4ea6\u65e0\u5bb3\u5904\uff0c\u5c31\u7167\u5979\u7684\u8ba1\u5212\u884c\u4e8b\u3002 \u3000\u3000\u949f\u7075\u79c0\u8ddf\u4e00\u4e2a\u59d3\u4e01\u7684\u95e8\u5ba2\u8fdb\u5165\u5185\u5802\uff0c\u67f3\u5143\u7532\u679c\u7136\u4eb2\u81ea\u63a5\u89c1\u5979\u3002 \u3000\u3000\u201c\u4e01\u5148\u751f\uff0c\u6ca1\u4f60\u7684\u4e8b\u4e86\uff0c\u9ebb\u70e6\u4f60\u51fa\u53bb\u544a\u8bc9\u7ba1\u5bb6\uff0c\u6ca1\u6211\u7684\u5429\u5490\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u8fdb\u6765\u3002\u201d\u67f3\u5143\u7532 \u9063\u8d70\u95e8\u5ba2\u4e4b\u540e\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u949f\u6210\u5668\u7684\u5973\u513f\uff0c\u90fd\u8fd9\u4e48\u5927\u4e86\u3002\u8bb0\u5f97\u4f60\u7239\u51fa\u4e8b\u90a3\u4e00\u5e74\uff0c\u4f60\u624d\u4e0d\u8fc7 \u516d\u3001\u4e03\u5c81\u5427\uff1f\u8f6c\u773c\u5c31\u662f\u5341\u5e74\u4e86\u3002\u4f60\u5988\u597d\u5417\uff1f\u201d\u201c\u5988\u4e5f\u65e9\u5df2\u53bb\u4e16\u4e86\u3002\u6211\u5982\u4eca\u662f\u548c\u7237\u7237\u76f8\u4f9d\u4e3a \u547d\u3002\u201d \u3000\u3000\u201c\u5bf9\u5566\uff0c\u542c\u8bf4\u4f60\u7237\u7237\u5927\u9690\u4e8e\u5e02\uff0c\u5df2\u4e0d\u5c51\u548c\u6211\u4eec\u8fd9\u4e9b\u4eba\u6765\u5f80\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u662f\u8fd9\u6837\u8bf4\uff0c\u7237\u7237\u56e0\u4e3a\u5e74\u7eaa\u8001\u8fc8\uff0c\u5f88\u5c11\u51fa\u95e8\uff0c\u6240\u4ee5\u8fd9\u4e9b\u5e74\u6ca1\u6765\u62dc\u671b\u4f60\u8001\u3002\u201d \u3000\u3000\u201c\u597d\u8bf4\u3001\u597d\u8bf4\u3002\u90a3\u4e48\u4f84\u5973\uff0c\u4f60\u8fd9\u6b21\u662f\u8def\u8fc7\u5462\uff1f\u8fd8\u662f\u4f60\u7237\u7237\u6709\u4e8b\u8981\u4f60\u627e\u6211\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u786e\u662f\u65e0\u4e8b\u4e0d\u767b\u4e09\u5b9d\u6bbf\u3002\u4e0d\u8fc7\u4e0d\u662f\u7237\u7237\u7684\u4e8b\u60c5\u3002\u5c0f\u4e8b\u6211\u662f\u4e0d\u6562 \u6765\u9ebb\u70e6\u4f60\u7684\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u5417\uff0c\u6050\u6015\u53ea\u6709\u4f60\u8001\u4eba\u5bb6\u624d\u5e2e\u5f97\u4e86\u5fd9\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u95ef\u4e86\u4ec0\u4e48\u5927\u7978\uff0c\u8981\u6211\u5e2e\u5fd9\uff1f\u201d\u8a00\u4e0b\u9887\u6709\u8baa\u7b11\u610f\u5473\u2014\u2014\u8c05\u4f60\u8fd9\u5c0f\u5c0f \u5e74\u7eaa\u4e5f\u95ef\u4e0d\u4e86\u4ec0\u4e48\u5927\u7978\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u662f\u6211\u95ef\u7684\u7978\uff0c\u6211\u662f\u8bf7\u4f60\u5e2e\u6211\u4e00\u4e2a\u670b\u53cb\u7684\u5fd9\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u7978\u4e5f\u4e0d\u662f\u4ed6\u81ea\u5df1\u95ef \u7684\u2014\u2014\u201d\u67f3\u5143\u7532\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9\u6211\uff0c\u4f60\u8fd9\u670b\u53cb\u6027\u751a\u540d\u8c01\u5462\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4ed6\u6027\u6a80\u540d\u7fbd\u51b2\u3002\u201d\u59d3\u540d\u8bf4\u51fa\u6765\uff0c\u67f3\u5143\u7532\u767b\u65f6\u7cbe\u795e\u4e00\u632f\uff0c\u6001\u5ea6\u8f6c\u4e3a\u5e84\u91cd\uff0c\u8fde \u5fd9\u95ee\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff1f\u4ed6\u662f\u91d1\u56fd\u4eba\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u4ed6\u5176\u5b9e\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u597d\u574f\u7684\u6807\u51c6\u662f\u5f88\u96be\u8bf4\u7684\u3002\u6211\u8981\u7684\u53ea\u662f\u4e8b\u5b9e\uff0c\u542c\u8bf4\u4ed6\u662f\u91d1\u56fd\u7684\u8d1d\u5b50\u5462\uff0c\u4f60\u77e5 \u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u522b\u4eba\u662f\u8fd9\u6837\u8bf4\u4ed6\uff0c\u4f46\u4ed6\u81ea\u5df1\u5374\u8bf4\u4ed6\u5e76\u4e0d\u662f\u4ec0\u4e48\u8d1d\u5b50\u3002\u67f3\u5e84\u4e3b\uff0c\u4f60\u8fd9\u6837\u95ee\uff0c \u60f3\u5fc5\u4f60\u5df2\u542c\u5230\u4e86\u4e00\u4e9b\u6709\u5173\u4ed6\u7684\u6d88\u606f\u4e86\u5427\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6765\uff0c\u6211\u6bcf\u5929\u90fd\u542c\u5230\u6709\u5173\u4ed6\u7684\u6d88\u606f\u3002\u6bd4\u5982\u8bf4\u6628\u5929\u5427\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u5c31 \u66fe\u5f3a\u9886\u91d1\u56fd\u8fb9\u5173\u7684\u5b88\u5175\uff0c\u548c\u5b8b\u56fd\u5b98\u519b\u4ee5\u53ca\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u5927\u6253\u4e86\u4e00\u573a\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u6697\u6697\u5403\u60ca\uff0c\u5634\u91cc\u5374\u9053\uff1a\u201c\u91d1\u5175\u4e5f\u4e0d\u662f\u4ed6\u5e26\u6765\u7684\u3002\u6211\u4eec\u5728\u8def\u4e0a\u4e5f\u66fe\u6253\u542c\u8fc7\u8fd9\u4ef6\u4e8b\uff0c \u542c\u8bf4\u662f\u5076\u7136\u78b0\u4e0a\u7684\u3002\u201d \u3000\u3000\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u597d\u5728\u6211\u8fd8\u6ca1\u544a\u8bc9\u5e84\u4e3b\u6211\u662f\u548c\u5927\u54e5\u540c\u6765\uff0c\u4f46\u5927\u54e5\u4e3a\u4ec0\u4e48\u8981\u9a97\u6211\u5462\uff1f\u54e6\uff0c\u662f \u4e86\uff0c\u4ed6\u4e00\u5b9a\u662f\u6015\u6211\u62c5\u5fe7\uff0c\u6240\u4ee5\u4e0d\u6562\u9053\u51fa\u5b9e\u60c5\u3002\u4e0d\u8fc7\uff0c\u5b9e\u60c5\u5f53\u7136\u4e5f\u4e0d\u4f1a\u662f\u4ed6\u4eec\u52fe\u7ed3\u91d1\u5175\uff0c\u90a3\u4e9b \u91d1\u5175\u4e00\u5b9a\u4e5f\u662f\u6765\u6349\u62ff\u4ed6\u7684\uff01\u201d\u4ed6\u7684\u786e\u4e0d\u6127\u662f\u6a80\u7fbd\u51b2\u7684\u7ea2\u989c\u77e5\u5df2\uff0c\u731c\u60f3\u7684\u4e8b\u867d\u4e0d\u4e2d\u4e5f\u4e0d\u8fdc\u77e3\u3002 \u67f3\u5143\u7532\u9053\uff1a\u201c\u6211\u8fd8\u542c\u8bf4\u4ed6\u548c\u4e10\u5e2e\u4e5f\u7ed3\u4e86\u4ec7\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u90a3\u662f\u98ce\u706b\u9f99\u65e0\u4e8b\u751f\u975e\uff0c\u53ea\u56e0\u4ed6\u662f\u91d1\u4eba\uff0c\u5c31\u8ba4\u5b9a\u4ed6\u662f\u5978\u7ec6\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u4f46\u4e5f\u66fe\u4eb2\u624b\u6253\u6b7b\u4e86\u4e24\u4e2a\u4fa0\u4e49\u9053\u4e2d\u7684\u4eba\u7269\uff0c\u5176\u4e2d\u4e00\u4e2a\u5c31\u662f\u4e34\u5b89\u4e10\u5e2e\u5206\u8235\u8235\u4e3b \u9a6c\u5929\u884c\u7684\u7ed3\u62dc\u5144\u5f1f\uff0c\u8fd9\u4e8b\u4e0d\u5047\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u662f\u66fe\u7ecf\u542c\u4eba\u8bf4\u8fc7\u3002\u4f46\u5373\u4f7f\u5982\u6b64\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u662f\u8feb\u4e8e\u65e0\u5948\u7684\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u5012\u597d\u50cf\u5f88\u504f\u8892\u4ed6\u5462\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7237\u7237\u7684\u670b\u53cb\u4e5f\u662f\u6211\u7684\u5927\u54e5\u54e5\u3002\u4e0d\u8fc7\uff0c\u6211\u4e0d\u662f\u504f\u8892\u4ed6\uff0c\u6211\u77e5\u9053\u4ed6\u662f\u597d \u4eba\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u548c\u4f60\u8bae\u8bba\u4ed6\u662f\u5426\u597d\u4eba\uff0c\u6211\u53ea\u60f3\u95ee\u4f60\uff0c\u4f60\u8981\u6211\u600e\u6837\u5e2e\u4ed6\u7684\u5fd9\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5f53\u52a1\u4e4b\u6025\u662f\u5e2e\u4ed6\u8fc7\u5173\uff0c\u5f80\u540e\u7684\u4e8b\uff0c\u662f\u5e2e\u4ed6\u548c\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u89e3\u5f00\u6881\u5b50\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u4f60\u4e5f\u77e5\u9053\u6b64\u4e8b\u662f\u4f1a\u5f15\u8d77\u5acc\u7591\u7684\u3002\u5f04\u5f97\u4e0d\u597d\uff0c \u751a\u81f3\u8fde\u6211\u4e5f\u5377\u8fdb\u6f29\u6da1\u3002\u4f60\u4e0d\u89c9\u5f97\uff0c\u4f60\u6c42\u6211\u7684\u4e8b\u60c5\uff0c\u662f\u8fc7\u4efd\u4e86\u4e00\u70b9\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u662f\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u4f46\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u80fd\u591f\u5e2e\u8fd9\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u5012\u60f3\u77e5\u9053\u4f60\u7684\u60f3\u6cd5\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u5e2e\u4f60\u7684\u5fd9\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u8981\u6211\u76f4\u8bf4\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u7237\u7237\u544a\u8bc9\u6211\uff0c\u6211\u7239\u662f\u4e3a\u4f60\u800c\u6b7b\u7684\u3002\u6211\u7239\u51fa\u6ba1\u90a3\u5929\uff0c\u4f60\u66fe\u8bb8\u4e0b\u8a93\u613f\uff0c\u5982\u679c\u6211 \u4eec\u513f\u5973\u6709\u6c42\u4e8e\u4f60\u7684\u60c5\uff0c\u4f60\u65e0\u6709\u4e0d\u5e94\u3002\u201d\u5979\u867d\u7136\u80fd\u5e72\uff0c\u5230\u5e95\u5e74\u8f7b\uff0c\u4e0d\u77e5\u5982\u6b64\u76f4\u8a00\uff0c\u5df2\u662f\u72af\u4e86\u67f3 \u5143\u7532\u4e4b\u5fcc\u3002 \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u7237\u7237\u8fd8\u544a\u8bc9\u4f60\u4ec0\u4e48\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u7237\u7237\u8bf4\uff0c\u6211\u7239\u662f\u5f3a\u76d7\uff0c\u4f60\u3001\u4f60\u2014\u2014\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u6211\u662f\u5728\u4ed6\u80cc\u540e\u7684\uff0c\u4ece\u4e0d\u9732\u9762\u7684\u5f3a\u76d7\u5934\u5b50\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6709\u4e00\u6b21\u4ed6\u548c\u5b59\u53d4\u53d4\u5949\u4eba\u4e4b\u547d\u53bb\u52a8\u4e00\u4e2a\u9556\u5c40\u4fdd\u7684\u7ea2\u8d27\uff0c\u540c\u53bb\u7684\u8fd8\u6709\u5341\u591a\u4e2a \u4eba\uff0c\u7ed9\u679c\u53ea\u6709\u5b59\u53d4\u53d4\u4e00\u4eba\u53ea\u662f\u5931\u53bb\u4e86\u53cc\u773c\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u5931\u53bb\u7684\u6027\u547d\u3002\u201d\u67f3\u5143\u7532\u53f9\u9053\uff1a\u201c\u4ed6\u4eec \u4e3a\u6211\u4e27\u4e86\u6027\u547d\uff0c\u6211\u4e5f\u5f88\u662f\u8fc7\u610f\u4e0d\u53bb\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u6562\u6765\u6c42\u4f60\uff0c\u67f3\u5e84\u4e3b\u8bf7\u4f60\u8bf4\u4e00\u53e5 \u5427\uff0c\u4f60\u80af\u4e0d\u80af\u5e2e\u8fd9\u4e2a\u670b\u53cb\u7684\u5fd9\uff1f\u201d\u201c\u5230\u5e95\u5e2e\u4e0d\u5e2e\uff1f\u201d\u67f3\u5143\u7532\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\u2014\u2014\u201d\u521a\u8bf4\u5230\u8fd9 \u91cc\uff0c\u5ffd\u542c\u5f97\u201c\u535c\u535c\u201d\u4e24\u4e0b\u95e8\u58f0\u3002 \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u8c01\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6211\uff01\u201d\u63a8\u95e8\u800c\u5165\uff0c\u539f\u6765\u5c31\u662f\u521a\u624d\u5e26\u9886\u949f\u7075\u79c0\u8fdb\u6765\u7684\u90a3\u4e2a \u95e8\u5ba2\u3002 \u3000\u3000\u67f3\u5143\u7532\u66fe\u5429\u5490\u8fc7\u4e0d\u8bb8\u522b\u4eba\u6765\u6253\u6270\u4ed6\u7684\uff0c\u8fd9\u59d3\u4e01\u7684\u5374\u4e0d\u5f85\u4ed6\u8bf4\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u5c31\u8fdb\u6765\u4e86\u3002\u67f3 \u5143\u7532\u6014\u4e86\u4e00\u6014\u3002\u4f46\u968f\u5373\u60f3\u5230\uff0c\u6ca1\u6709\u6025\u4e8b\uff0c\u8c05\u4ed6\u4e5f\u4e0d\u6562\u83bd\u649e\u3002\u4fbf\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u51e0\u4e4e\u5fd8\u4e86\u4f60\u548c \u6211\u7ea6\u597d\u7684\u4e8b\u4e86\u3002\u949f\u59d1\u5a18\uff0c\u4f60\u7a0d\u5750\u4e00\u4f1a\uff0c\u6211\u53bb\u4ea4\u4ee3\u51e0\u53e5\u8bdd\uff0c\u6599\u7406\u4e86\u90a3\u4ef6\u4e8b\u5c31\u56de\u6765\u3002\u201d \u3000\u3000\u51fa\u4e86\u5bc6\u5ba4\uff0c\u90a3\u59d3\u4e01\u7684\u95e8\u5ba2\u624d\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u5ba2\u4eba\u8981\u89c1\u4f60\u3002\u201d\u67f3\u5143\u7532\u9053\uff1a\u201c\u4ec0\u4e48\u5ba2\u4eba\uff1f\u201d \u59d3\u4e01\u7684\u9053\uff1a\u201c\u662f\u4f60\u975e\u89c1\u4e0d\u53ef\u7684\u5ba2\u4eba\uff01\u201d \u3000\u3000\u67f3\u5143\u7532\u6599\u5230\u51e0\u5206\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u662f\u738b\u7237\u90a3\u8fb9\u6765\u7684\uff1f\u201d\u90a3\u59d3\u4e01\u7684\u95e8\u5ba2\u70b9\u4e86\u70b9\u5934\u3002\u67f3\u5143\u7532 \u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u4f1a\u5ba2\uff0c\u4f60\u66ff\u591a\u770b\u7740\u90a3\u4e24\u4e2a\u5a03\u513f\u3002\u201d \u3000\u3000\u4ed6\u8d70\u8fdb\u53e6\u4e00\u4e2a\u5bc6\u5ba4\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u5c11\u5973\u5750\u5728\u5f53\u4e2d\uff0c\u4e0d\u89c9\u60ca\u559c\u4ea4\u96c6\uff0c\u8bf4\u9053\uff1a\u201c\u683c\u683c\uff0c\u4ec0\u4e48 \u98ce\u628a\u4f60\u5439\u6765\u7684\uff1f\u201d\u4ed6\u65e9\u5df2\u6599\u5230\u5ba2\u4eba\u662f\u4ece\u5b8c\u989c\u957f\u4e4b\u7684\u738b\u5e9c\u6765\u7684\uff0c\u4f46\u5374\u8fd8\u60f3\u4e0d\u5230\u7adf\u662f\u738b\u5e9c\u7684\u683c \u683c\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u9ed1\u8863\u5c11\u5973\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u8d6b\u8fde\u6e05\u6ce2\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7ad9\u8d77\u6765\u8fd8\u793c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u6765\u62dc\u5019\u4f60\u7684\u3002\u67f3\u5e84\u4e3b\uff0c\u4f60\u53ef\u771f\u4f1a\u4ea8\u5029\u798f\u554a\uff01\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u67f3\u67d0\u5f97\u6709\u4eca\u5929\u7684\u65e5\u5b50\uff0c\u8fd8\u4e0d\u662f\u6cbe\u4e86\u738b\u7237\u548c\u683c\u683c\u7684\u5149\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u600e\u4e48\u548c\u6211\u5ba2\u6c14\u8d77\u6765\u4e86\u3002\u6211\u6b64\u6765\u53ea\u6015\u662f\u8981\u5e26\u7ed9\u4f60\u4e00\u70b9\u9ebb\u70e6\u7684\u5462\uff01 \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u4f46\u51ed\u5dee\u9063\uff0c\u8bf7\u95ee\u662f\u516c\u4e8b\u79c1\u4e8b\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7b11\u9053\uff1a\u201c\u5018\u82e5\u662f\u79c1\u4e8b\uff0c\u4f60\u5c31\u4e0d\u5356\u529b\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u4e0d\uff0c\u82e5\u662f\u683c\u683c\u7684\u79c1\u4e8b\uff0c\u6211\u5f53\u7136\u66f4\u52a0\u5356\u529b\u4e86\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u6b64\u6765\u65e2\u4e3a\u516c\uff0c\u4e5f\u662f\u4e3a\u79c1\u3002\u8fd9\u4e24\u5929\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\u7684\u5927\u4e8b\uff0c\u60f3 \u4f60\u4e0d\u4f1a\u4e0d\u77e5\u3002\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u683c\u683c\u662f\u4e3a\u90a3\u4f4d\uff0c\u90a3\u4f4d\u6a80\u8d1d\u5b50\u800c\u6765\u7684\u5417\uff1f\u201d\u8d6b\u8fde\u6e05\u6ce2\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u662f\u94a6\u72af\u3002\u4f46\u6211\u53c8\u542c\u8bf4\u4ed6\u597d\u50cf\u548c\u683c\u683c\u4f60\u4e00\u540c\u8d70\u8fc7\u6c5f\u6e56\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4f3c\u7b11\u975e\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5728\u6c5f\u6e56\u4e0a\u662f\u2018\u7389\u9762\u5996\u72d0\u2019\u4e0d\u662f\u738b\u5e9c\u683c\u683c\u3002\u5996\u72d0\u5229\u94a6\u72af\u8d70\u5728 \u4e00\u8d77\uff0c\u90a3\u5c31\u4e0d\u80fd\u7b97\u662f\u5947\u602a\u7684\u4e8b\u4e86\u3002\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u683c\u683c\uff0c\u4f60\u522b\u8bef\u4f1a\u3002\u5bf9\u8fd9\u4ef6\u4e8b\u6211\u5e76\u65e0\u975e\u8bae\u4e4b\u610f\uff0c\u6211\u53ea\u60f3\u77e5\u9053\u4ed6\u662f\u4e0d\u662f\u4f60\u7684\u670b \u53cb\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u8fd9\u5c42\u4f60\u53ef\u4ee5\u4e0d\u5fc5\u7406\u4f1a\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u5982\u679c\u4ed6\u6765\u5230\u5e9c\u4e0a\uff0c\u4f60\u6253\u7b97\u5982\u4f55\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff01\u6211\u4e0e\u4ed6\u7d20\u4e0d\u76f8\u8bc6\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f46\u6211\u542c\u8bf4\u4ed6\u5728\u4e34\u5b89\u7ed3\u8bc6\u4e86\u4e00\u4e2a\u5356\u5531\u5c0f\u59d1\u5a18\uff0c\u662f\u949f\u4e0d\u9e23\u7684\u5b59\u5973\u513f\uff0c\u548c\u4f60\u4f3c \u6709\u591a\u5c11\u5173\u7cfb\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6765\u6c42\u4f60\u3002\u201d \u3000\u3000\u5979\u53ef\u4e0d\u77e5\uff0c\u4e0d\u4f46\u90a3\u201c\u5c0f\u59d1\u5a18\u201d\u6765\u4e86\uff0c\u6a80\u7fbd\u51b2\u4e5f\u5df2\u6765\u4e86\uff0c\u800c\u4e14\u5c31\u5728\u7a97\u5916\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u662f\u5728\u95e8\u623f\u770b\u89c1\u5979\u8fdb\u6765\u7684\u3002\u5979\u65e0\u987b\u7ecf\u8fc7\u95e8\u623f\u901a\u62a5\uff0c\u600e\u77e5\u95e8\u623f\u5c31\u8eb2\u6709\u5979\u8981\u627e\u7684\u4eba\u3002\u8fc7 \u540e\u6a80\u7fbd\u51b2\u6258\u8f9e\u89e3\u624b\uff0c\u6697\u5730\u8ddf\u8e2a\uff0c\u4ed6\u662f\u949f\u7075\u79c0\u5e26\u8fdb\u6765\u7684\u3002\u95e8\u623f\u662f\u4ed6\u7236\u4eb2\u7684\u65e7\u4ea4\uff0c\u4e14\u53c8\u66fe\u5f97\u4ed6\u7684 \u597d\u5904\uff0c\u81ea\u662f\u4e0d\u4f1a\u7591\u5fc3\u4ed6\uff0c\u4f1a\u5728\u8fd9\u91cc\u201c\u6363\u4e71\u201d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u67f3\u5143\u7532\u9053\uff1a\u201c\u4ed6\u662f\u7687\u4e0a\u6240\u8981\u7684\u94a6\u72af\uff0c\u4f46\u4e5f\u662f\u738b\u7237\u548c\u683c\u683c\u6240\u8981\u7684\u4eba\u3002\u5982\u679c\u4ed6\u771f\u7684\u6765 \u5230\u6b64\u5730\uff0c\u6211\u6253\u7b97\u5c06\u4ed6\u64d2\u4e86\uff0c\u732e\u7ed9\u732e\u7ed9\u2014\u2014\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5f53\u7136\u732e\u7ed9\u7687\u4e0a\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u6253\u7b97\u732e\u7ed9\u738b\u7237\u3002\u6211\u7684\u79d8\u5bc6\u53ea\u6709\u738b\u7237\u77e5\u9053\uff0c\u6211\u53ef\u4e0d\u60f3\u8ba9\u7687\u4e0a \u7684\u4eba\u4e5f\u77e5\u9053\u6211\u7684\u8eab\u4efd\u5462\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u662f\u6253\u7b97\u732e\u7ed9\u6211\u7684\u5e72\u7239\uff0c\u90a3\u5c31\u76f4\u63a5\u4ea4\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u8fd9\u4e2a\u2014\u2014\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u5148\u628a\u4ed6\u7684\u6b66\u529f\u5e9f\u6389\uff0c\u7136\u540e\u624d\u4ea4\u7ed9\u6211\uff0c\u90a3\u5c31\u4e0d\u7528\u62c5\u5fc3\u6211\u770b\u5b88\u4e0d\u4f4f\u5730 \u4e86\u3002\u201d\u8bdd\u4e2d\u6709\u8bdd\uff0c\u771f\u7684\u542b\u610f\uff0c\u5176\u5b9e\u662f\u8981\u4f7f\u5f97\u67f3\u5143\u7532\u653e\u5fc3\uff0c\u4ea6\u5373\u8868\u7528\u4e86\u81ea\u5df1\u662f\u4e0d\u4f1a\u628a\u6a80\u7fbd\u51b2\u79c1 \u4e0b\u653e\u8d70\u7684\u3002\u5426\u5219\u5979\u5c31\u4e0d\u4f1a\u51c6\u8bb8\u67f3\u5143\u7532\u5e9f\u6389\u6a80\u7fbd\u51b2\u6b66\u529f\u4e86\u3002\u67f3\u5143\u7532\u662f\u8001\u72d0\u72f8\uff0c\u4e00\u542c\u5c31\u4f1a\u610f\u3002\u4e24 \u4eba\u90fd\u662f\u5fc3\u7167\u4e0d\u5ba3\u3002\u53ea\u6709\u8eb2\u5728\u5916\u9762\u6697\u4e2d\u5077\u542c\u7684\u6a80\u7fbd\u51b2\uff0c\u5374\u662f\u4e0d\u7981\u8d8a\u542c\u8d8a\u60ca\uff0c\u201c\u539f\u6765\u5979\u771f\u662f\u60f3\u6349 \u6211\u56de\u53bb\u7684\uff0c\u4ed6\u7684\u624b\u6bb5\u4e5f\u771f\u662f\u591f\u6bd2\u4e86\uff01\u5509\uff0c\u5979\u600e\u7684\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\u5462\uff1f\u8fd8\u662f\u5979\u672c\u6765\u5c31\u662f\u8fd9\u4e2a\u6837\u5b50 \u5462\uff1f\u602a\u4e0d\u5f97\u4eba\u5bb6\u53eb\u5979\u7389\u9762\u5996\u72d0\uff0c\u597d\u5728\u6211\u6ca1\u6709\u7ed9\u5979\u751c\u8a00\u871c\u8bed\u6240\u9a97\u3002\u201d\u4ed6\u65e0\u6687\u591a\u60f3\uff0c\u7acb\u5373\u5c31\u8dd1\u51fa \u53bb\u3002 \u3000\u3000\u949f\u7075\u79c0\u8fd8\u5728\u90a3\u5bc6\u5ba4\u4e4b\u4e2d\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u51b2\u8fdb\u53bb\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff01\u5feb\u8d70\uff01\u201d\u84e6\u5730\u6709\u4eba\u98de\u6251\u8fdb\u6765\u3002 \u3000\u3000\u6251\u8fdb\u6765\u7684\u662f\u90a3\u4e2a\u59d3\u4e01\u7684\u95e8\u5ba2\uff0c\u6a80\u7fbd\u51b2\u542c\u5f97\u80cc\u540e\u52b2\u98ce\u547c\u547c\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u638c\u3002\u4ed6\u8fd9\u4e00\u638c\u4e0d\u5e26 \u98ce\u58f0\uff0c\u4f46\u53cc\u638c\u4e00\u4ea4\uff0c\u90a3\u59d3\u4e01\u7684\u95e8\u5ba2\u5df2\u662f\u7ed9\u4ed6\u8feb\u5f97\u659c\u9000\u4e09\u6b65\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8fd9\u4e00\u638c\u662f\u7528\u4e0a\u4e03\u6210\u5185\u529b\u7684\u3002\u8fd9\u95e8\u5ba2\u5c45\u7136\u6ca1\u6709\u5012\u4e0b\uff0c\u4ed6\u4ea6\u6709\u70b9\u60ca\u8be7\uff0c\u6b63\u60f3\u4ece\u7bab\u4e2d\u5439 \u51fa\u7f61\u6c14\uff0c\u53ea\u89c1\u949f\u7075\u79c0\u53cc\u638c\u4e00\u63a8\uff0c\u5374\u5df2\u628a\u90a3\u59d3\u4e01\u7684\u95e8\u5ba2\u63a8\u5728\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u90a3\u95e8\u5ba2\u5012\u5728\u5730\u4e0a\uff0c\u7f29\u4f5c\u4e00\u56e2\uff0c\u7a81\u7136\u597d\u50cf\u5728\u4ed6\u7684\u8eab\u4e0a\u53d1\u51fa\u4e00\u4e32\u7206\u8c46\u7684\u58f0\u97f3\uff0c\u53e3\u4e2d\u6dcc\u8840\uff0c\u52a8 \u4e5f\u4e0d\u80fd\u52a8\u4e86\u3002\u6a80\u7fbd\u51b2\u8fd9\u624d\u660e\u767d\u5176\u4e2d\u9053\u7406\uff0c\u539f\u6765\u8fd9\u59d3\u4e01\u7684\u95e8\u5ba2\u672c\u6765\u662f\u62b5\u6321\u4e0d\u4f4f\u4ed6\u8fd9\u4e00\u638c\u7684\uff0c\u4ed6 \u901e\u5f3a\u786c\u63a5\uff0c\u5168\u8eab\u9aa8\u8282\uff0c\u90fd\u5df2\u6563\u5f00\u3002\u949f\u7075\u79c0\u90a3\u4e00\u63a8\uff0c\u53ea\u4e0d\u8fc7\u662f\u6b63\u8d76\u4e0a\u4ed6\u201c\u5d29\u6e83\u201d\u7684\u65f6\u5019\u800c\u5df2\u3002 \u6a80\u7fbd\u51b2\u53d1\u89c9\u81ea\u5df1\u529f\u592b\u53c8\u5df2\u6709\u8fdb\u5883\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u6b22\u559c\u3002 \u3000\u3000\u949f\u7075\u79c0\u6251\u5165\u4ed6\u7684\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u522b\u95ee\u8fd9\u4e48\u591a\uff0c\u8fd9\u59d3\u67f3\u7684\u4e0d\u662f\u597d\u4eba\uff0c\u5feb\u8ddf\u6211\u8d70\uff01\u201d \u3000\u3000\u201c\u8fd8\u60f3\u8dd1\u5417\uff1f\u201d\u67f3\u5143\u7532\u8ddf\u8e2a\u6765\u5230\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5927\u6012\u9053\uff1a\u201c\u597d\u8d3c\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u628a\u6696\u7389\u7bab\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\uff0c\u75be\u70b9\u67f3\u5143\u7532\u7684\u201c\u98ce\u773c \u7a74\u201d\u3002\u67f3\u5143\u7532\u7b11\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u6211\u53ef\u8fd8\u4e0d\u60f3\u4f60\u6b7b\u5728\u655d\u5e84\u5462\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u9a88\u6307\u5982\u621f\uff0c\u4e5f\u7528\u7a74 \u9053\u94dc\u4eba\u7684\u70b9\u7a74\u624b\u6cd5\u8fd8\u4e86\u4e00\u62db\uff0c\u6a80\u7fbd\u51b2\u7684\u7389\u7bab\u4fe8\u5982\u70b9\u6c34\u873b\u8713\uff0c\u987a\u6d41\u800c\u4e0b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u70b9\u4e86\u5341\u4e03 \u516b\u4e0b\uff0c\u4ece\u5bf9\u65b9\u7684\u80a9\u53f0\u7a74\u70b9\u5230\u4e86\u864e\u53e3\u7684\u5173\u767d\u7a74\u3001\u4f46\u67f3\u5143\u7532\u7684\u53cc\u6307\u70b9\u7a74\uff0c\u5374\u662f\u66f4\u52a0\u51cc\u5389\uff0c\u5728\u8fd9\u7247 \u523b\u4e4b\u95f4\uff0c\u4e5f\u662f\u904d\u88ad\u4e86\u5bf9\u65b9\u7684\u5341\u516b\u5904\u7a74\u9053\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u662f\u4e00\u6cbe\u5373\u9000\uff0c\u8c01\u4e5f\u6ca1\u6709\u7ed9\u5bf9\u65b9\u771f\u4e2a\u70b9\u7740\u7a74\u9053\uff0c\u4f46\u67f3\u5143\u7532\u5f39\u6307\u53d1\u51fa\u52b2\u98ce\uff0c\u5df2\u662f\u9707\u5f97 \u6a80\u7fbd\u51b2\u7684\u82e5\u5e72\u7a74\u9053\u9690\u9690\u53d1\u9ebb\u3002\u4e0d\u8fc7\u6a80\u7fbd\u51b2\u6696\u7389\u7bab\u4e2d\u5439\u51fa\u7684\u7f61\u6c14\uff0c\u4e5f\u4ee4\u5f97\u67f3\u5143\u7532\u7684\u82e5\u5e72\u7a74\u9053\u9690 \u9690\u4f5c\u75db\u3002 \u3000\u3000\u8bba\u529f\u529b\u67f3\u5143\u7532\u662f\u5728\u6a80\u7fbd\u51b2\u4e4b\u4e0a\uff0c\u8bba\u70b9\u7a74\u7684\u624b\u6cd5\uff0c\u4e5f\u4e0d\u5728\u6a80\u7fbd\u51b2\u4e4b\u4e0b\u3002\u4f46\u597d\u5728\u7bab\u957f\u6307\u77ed\uff0c \u4fd7\u8bed\u8bf4\u4e00\u5bf8\u957f\u4e00\u5bf8\u5f3a\uff0c\u4e00\u5bf8\u77ed\u4e00\u5bf8\u9669\uff0c\u5728\u8fd1\u8eab\u640f\u6597\u4e4b\u4e2d\u67f3\u5143\u7532\u7684\u624b\u6307\u672a\u70b9\u5230\u5bf9\u65b9\u7684\u8eab\u4e0a\uff0c\u6a80 \u7fbd\u51b2\u7684\u7389\u7bab\u5df1\u662f\u6307\u5230\u4ed6\u7684\u8981\u5bb3\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6a80\u7fbd\u51b2\u4e00\u4e0b\u79fb\u5f62\u6362\u4f4d\uff0c\u7528\u7389\u7bab\u4f7f\u51fa\u523a\u7a74\u7684\u5251\u6cd5\uff0c\u523a\u5411\u67f3\u5143\u7532\u8170\u80cc\u7684\u7cbe\u4fc3\u7a74\uff0c\u67f3\u5143 \u7532\u95ea\u5f97\u7a0d\u8fdf\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e0a\u8863\u7ed9\u7389\u7bab\u6233\u7a7f\u5c0f\u5b54\u3002\u67f3\u5143\u7532\u559d\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u4f60\u5fc3\u91cc\u4e5f\u8be5\u660e \u767d\uff0c\u8bba\u70b9\u7a74\u624b\u6cd5\uff0c\u4f60\u662f\u80dc\u4e0d\u8fc7\u6211\u7684\uff0c\u4f60\u83ab\u4ee5\u4e3a\u4ed7\u7740\u6696\u7389\u7bab\u5c31\u53ef\u4ee5\u53d6\u80dc\uff0c\u6211\u529d\u4f60\u83ab\u8981\u903c\u6211\u4f7f\u51fa \u6740\u624b\uff01\u201d\u6a80\u7fbd\u51b2\u559d\u9053\uff1a\u201c\u5e9f\u8bdd\u4f55\u5fc5\u591a\u8bf4\uff0c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d \u3000\u3000\u6b63\u5728\u6253\u5f97\u5403\u7d27\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u55a7\u95f9\u4e4b\u58f0\u3002\u6709\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u80c6\u6562\u4e71\u95ef\uff01\u201d \u3000\u3000\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6211\u662f\u6765\u6c42\u89c1\u8d35\u5e84\u4e3b\u7684\uff0c\u8fd9\u662f\u6211\u7684\u62dc\u5e16\u3002 \u3000\u3000\u201c\u949f\u4e0d\u9e23\uff1f\u201d\u63a5\u8fd1\u62dc\u5e16\u7684\u90a3\u95e8\u5b50\u4e00\u770b\u4e0a\u9762\u7684\u540d\u5b57\uff0c\u5c31\u54fc\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u6211\u4ece\u6ca1\u89c1 \u8fc7\uff0c\u4f60\u662f\u5e84\u4e3b\u7684\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u8eb2\u5728\u6a80\u7fbd\u51b2\u80cc\u540e\uff0c\u6a80\u7fbd\u51b2\u6b63\u5728\u594b\u529b\u62b5\u5fa1\u67f3\u5143\u7532\u7684\u5f3a\u653b\uff0c\u5979\u5904\u5728\u4e24\u5927\u9ad8\u624b\u62fc\u6597\u4e4b \u4e2d\uff0c\u6709\u5982\u5c0f\u821f\u4e4b\u5728\u6ce2\u6d9b\u6c79\u6d8c\u7684\u6d77\u6d0b\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u54ea\u91cc\u8fd8\u6709\u5fc3\u795e\u53bb\u4ed4\u7ec6\u7ec6\u542c\u5916\u8fb9\u7684\u5435\u95f9\uff1f\u4f46 \u201c\u949f\u4e0d\u9e23\u201d\u8fd9\u4e09\u4e2a\u5b57\u5979\u662f\u592a\u719f\u6089\u4e86\uff0c\u90a3\u4e2a\u4eba\u53c8\u9ad8\u58f0\u5ff5\u51fa\u6765\u7684\uff0c\u5979\u867d\u7136\u6ca1\u6709\u7559\u5fc3\u53bb\u542c\uff0c\u8fd9\u4e09\u4e2a \u5b57\u4ea6\u5df2\u542c\u8fdb\u5979\u7684\u8033\u6735\uff0c\u4ee4\u5f97\u5979\u5fc3\u5934\u9661\u7136\u4e00\u9707\u4e86\uff01 \u3000\u3000\u201c\u7237\u7237\u4f60\u83ab\u8fc7\u6765\uff01\u201d\u4f46\u5979\u7684\u58f0\u97f3\u600e\u80fd\u4f20\u5230\u7237\u7237\u7684\u8033\u6735\u3002 \u3000\u3000\u949f\u4e0d\u9e23\u5012\u662f\u542c\u89c1\u4e86\u5c4b\u5b50\u91cc\u9762\u7684\u53ae\u6740\u58f0\u4e86\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u63a8\u5f00\u90a3\u4e2a\u95e8\u5b50\uff0c\u5c31\u5f80\u91cc\u95ef\u3002 \u3000\u3000\u4ed6\u4e00\u8e0f\u5165\u9662\u5b50\uff0c\u6709\u4e2a\u6253\u624b\u5c31\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u8fdb\u53bb\u5427\uff01\u6211\u8981\u8bf7\u8fdb\u9b3c\u95e8\u5173\u53bb\uff01\u201d \u3000\u3000\u201c\u79c0\u513f\uff01\u201d\u4e00\u58f0\u60e8\u53eb\uff0c\u949f\u4e0d\u9e23\u88ab\u90a3\u6253\u624b\u5728\u80cc\u540e\u5077\u88ad\uff0c\u767b\u65f6\u5012\u5730\uff01 \u3000\u3000\u949f\u7075\u79c0\u672c\u6765\u662f\u4e2a\u806a\u660e\u61c2\u4e8b\u7684\u5c0f\u59d1\u5a18\uff0c\u4f46\u5979\u4e00\u51fa\u751f\u4fbf\u4e0e\u7237\u7237\u76f8\u4f9d\u4e3a\u547d\uff0c\u5ffd\u7136\u542c\u5230\u4e86\u7237\u7237\u5bf9 \u5979\u547c\u5524\uff0c\u90a3\u6700\u540e\u4e00\u58f0\u7684\u60e8\u5389\u547c\u5524\uff0c\u4f60\u53eb\u5979\u5982\u4f55\u8fd8\u80fd\u4fdd\u6301\u5fc3\u667a\u7075\u660e\uff1f\u8fd9\u4e00\u58f0\u60e8\u5389\u7684\u547c\u5524\uff0c\u767b\u65f6 \u5c31\u597d\u50cf\u7206\u70b8\u5f00\u4e86\u5979\u7684\u8111\u888b\uff0c\u4ee4\u5979\u6d88\u5931\u4e86\u7406\u667a\u4e86\uff0c\u5979\u5c16\u53eb\uff1a\u201c\u7237\u7237\uff01\u4e0d\u987e\u4e00\u5207\uff0c\u51b2\u51fa\u5c4b\u5b50\u3002 \u3000\u3000\u5979\u811a\u6b65\u4e00\u8e0f\u51fa\u95e8\u5916\uff0c\u767b\u65f6\u5c31\u6709\u51e0\u4e2a\u4eba\u8dd1\u4e0a\u6765\u6349\u5979\u3002\u949f\u7075\u79c0\u706b\u7ea2\u7684\u773c\u775b\uff0c\u5530\u5530\u5530\u8fde\u73af\u75be \u523a\uff0c\u90a3\u51e0\u4e2a\u4eba\u4e5f\u662f\u6599\u4e0d\u5230\u8fd9\u5c0f\u59d1\u5a18\u7adf\u6709\u5982\u6b64\u672c\u9886\uff0c\u5927\u610f\u8f7b\u654c\uff0c\u7a7a\u624b\u6349\u5979\u7ed9\u5979\u523a\u4f24\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u4e00\u4e2a\u95e8\u5ba2\u9053\uff1a\u201c\u5e84\u4e3b\u53ea\u8bf4\u8981\u6349\u6d3b\u7684\uff0c\u53ef\u6ca1\u8bf4\u4e0d\u8bb8\u4f24\u8fd9\u4e2b\u5934\uff01\u201d\u62d4\u51fa\u8170\u5200\uff0c\u4e00\u62db\u201d\u987a\u6c34\u63a8 \u821f\u201c\u76ee\u949f\u7075\u79c0\u7684\u53f3\u80a9\u524a\u4e0b\u3002\u8fd9\u4e00\u5200\u82e5\u7136\u7ed9\u4ed6\u780d\u4e2d\uff0c\u949f\u7075\u79c0\u7684\u4e00\u6761\u53f3\u81c2\u53ea\u6015\u5c31\u8981\u4fdd\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u8eab\u8eaf\u4e00\u77ee\uff0c\u8fd9\u4e00\u5200\u53d8\u6210\u4e86\u4ece\u5979\u5934\u9876\u4e0a\u65b9\u524a\u8fc7\u3002\u949f\u7075\u79c0\u611f\u89c9\u5934\u76ae\u4e00\u9635\u6c81\u51c9\uff0c\u4e0d\u7406\u6b7b \u6d3b\uff0c\u4e00\u5251\u5c31\u65a9\u8fc7\u53bb\u3002\u8fd9\u4e00\u5251\u6b63\u4e2d\u90a3\u4eba\u7684\u819d\u76d6\uff0c\u90a3\u4eba\u6ca1\u780d\u6389\u949f\u7075\u79c0\u7684\u624b\u81c2\uff0c\u534a\u6761\u817f\u53cd\u800c\u548c\u8eab\u4f53 \u5206\u5bb6\u3002 \u3000\u3000\u4f46\u662f\u949f\u7075\u79c0\u8fd8\u672a\u80fd\u633a\u8d77\u8eab\u6765\uff0c\u5df2\u662f\u7ed9\u53e6\u4e00\u4e2a\u6293\u7740\u3002\u8fd9\u4eba\u7528\u7684\u662f\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u6293\u7740\u5979\u559d \u9053\uff1a\u201c\u597d\u72e0\u7684\u5c0f\u4e2b\u5934\uff0c\u6211\u4e0d\u6740\u4f60\uff0c\u4e5f\u5f97\u62c9\u65ad\u4f60\u4e00\u6761\u624b\u81c2\uff01\u201d\u6b63\u5728\u65bd\u5c55\u5206\u7b4b\u9519\u9aa8\u624b\u5fd7\u5ffd\u5730\u6709\u4e2a \u201c\u98de\u4eba\u201d\u5411\u4ed6\u649e\u6765\uff0c\u539f\u6765\u6a80\u7fbd\u51b2\u4ea6\u5df2\u51b2\u51fa\u6765\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u201c\u98de\u4eba\u201d\u5374\u4e0d\u662f\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u613f\u591a\u4f24\u6027\u547d\uff0c\u6551\u90a3\u4e9b\u4e00\u7a9d\u5cf0\u56f4\u62e5\u4e0a\u6765\u7684\u5e84\u4e01\u95e8\u5ba2\u3002\u4ed6\u7528\u7684\u4e5f\u662f\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4e0d \u8fc7\u4ed6\u4e00\u6293\u7740\u5c31\u7529\u51fa\u53bb\u3002\u8f6c\u773c\u95f4\u7ed9\u4ed6\u7529\u51fa\u53bb\u516d\u4e03\u4e2a\u3002\u201c\u98de\u4eba\u201d\u649e\u7740\u540c\u4f34\uff0c\u8fde\u73af\u78b0\u649e\u767b\u65f6\u5012\u4e0b\u4e86 \u5341\u4e03\u516b\u4e2a\u4e4b\u591a\uff01\u7ed9\u4ed6\u6740\u5f00\u4e86\u4e00\u6761\u8def\u4e86\u3002\u6293\u7740\u949f\u7075\u79c0\u7684\u90a3\u4e2a\u95e8\u5ba2\uff0c\u5c31\u6b63\u662f\u7ed9\u4eba\u649e\u7ffb\u7684\u3002\u949f\u7075\u79c0 \u8131\u51fa\u638c\u63e1\uff0c\u4ecd\u7136\u5411\u524d\u98de\u8dd1\uff0c\u8fb9\u8dd1\u8fb9\u53eb\uff1a\u201c\u7237\u7237\uff01\u7237\u7237\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53eb\u9053\uff1a\u201c\u79c0\u59b9\uff0c\u4f60\u9192\u9192\uff0c\u4e0d\u53ef\u4e71\u2014\u2014\u201d\u4ed6\u7684\u8bdd\u672a\u8bf4\u5f97\u5b8c\u5168\uff0c\u4e00\u80a1\u52b2\u98ce\u5df2\u662f\u4ece\u4ed6\u80cc \u540e\u88ad\u6765\u3002\u67f3\u5143\u7532\u8ffd\u4e86\u51fa\u6765\u3002\u8fd9\u80a1\u52b2\u98ce\u4e43\u662f\u4ed6\u7684\u58c1\u7a7a\u638c\u6240\u53d1\u3002 \u3000\u3000\u949f\u7075\u79c0\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u600e\u4e48\u4e86\uff0c\u4f60\u5e94\u6211\u5440\uff0c\u4f60\u5e94\u6211\u5440\uff01\u201d\u5979\u5df2\u7ecf\u53d1\u73b0\u7237\u7237\u8eba\u5728\u8840\u6cca\u4e2d \u4e86\u3002 \u3000\u3000\u90a3\u4e2a\u88ab\u5979\u523a\u4f24\u7684\u95e8\u5ba2\uff0c\u4e3e\u8d77\u94c1\u62d0\uff0c\u72de\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u4f60\u7684\u7237\u7237\uff0c\u6211\u5c31\u9001\u4f60\u4f5c\u4ed6\u76f8\u4f1a \u5427\uff01\u201d\u72de\u7b11\u58f0\u4e2d\uff0c\u731b\u7684\u4e00\u62d0\u5c31\u5411\u949f\u7075\u79c0\u5f53\u5934\u6253\u4e0b\uff01 \u3000\u3000\u67f3\u5143\u7532\u51b7\u7b11\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u6211\u8fd9\u5e72\u67f3\u5e84\u53ef\u4e0d\u80fd\u4efb\u51ed\u4f60\u8981\u6765\u4fbf\u6765\uff0c\u8981\u53bb\u4fbf\u53bb\uff01\u4f60\u4e0d\u5403\u656c \u9152\uff0c\u90a3\u5c31\u53ea\u80fd\u5403\u7f5a\u9152\u4e86\uff01\u201d\u638c\u631f\u52b2\u98ce\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u63a5\u8fde\u53d1\u51fa\u4e86\u4e24\u8bb0\u5288\u7a7a\u638c\u3002 \u3000\u3000\u4e24\u4eba\u529f\u529b\u76f8\u5dee\u6709\u9650\uff0c\u6a80\u7fbd\u51b2\u82e5\u662f\u548c\u4ed6\u5bf9\u638c\uff0c\u7edd\u8ba1\u4e0d\u4f1a\u53d7\u4f24\uff0c\u4f46\u6b64\u65f6\u4ed6\u5df2\u770b\u89c1\u90a3\u4e2a\u95e8\u5ba2\u6b63 \u5728\u4e3e\u8d77\u94c1\u62d0\uff0c\u94c1\u62d0\u5c31\u8981\u6253\u5230\u949f\u7075\u79c0\u7684\u5934\u4e0a\u4e86\uff0c\u4ed6\u5982\u4f55\u8fd8\u80fd\u53ea\u987e\u81ea\u8eab\uff1f\u4ed6\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u4eba\u672a \u5230\uff0c\u638c\u5148\u53d1\uff0c\u4e5f\u662f\u4e00\u8bb0\u5288\u7a7a\u638c\u5411\u90a3\u95e8\u5ba2\u6253\u53bb\uff01 \u3000\u3000\u8fd9\u80a1\u638c\u5288\u5f97\u6b63\u662f\u5408\u65f6\uff0c\u7528\u5f97\u4e5f\u662f\u6070\u5230\u597d\u5904\uff0c\u90a3\u4eba\u7684\u94c1\u62d0\u6253\u4e2d\u81ea\u5df1\u7684\u8111\u888b\uff01\u8fd9\u4eba\u7684\u8111\u888b\u5f00 \u82b1\uff0c\u5bb3\u4eba\u4e0d\u6210\uff0c\u53cd\u800c\u5bb3\u4e86\u81ea\u5df1\u3002\u949f\u7075\u79c0\u53ea\u89c9\u5f97\u52b2\u98ce\u98d2\u7136\uff0c\u4ece\u4ed6\u5934\u9876\u5439\u8fc7\u3001\u5439\u6563\u4e86\u5979\u7684\u5934\u53d1\uff0c \u5979\u5374\u6ca1\u6709\u53d7\u5230\u534a\u70b9\u4f24\u635f\u3002\u4e0d\u8fc7\u5979\u770b\u89c1\u90a3\u4e2a\u4eba\u8111\u888b\u5f00\u82b1\uff0c\u5728\u5979\u9762\u524d\u5012\u4e86\u4e0b\u53bb\uff0c\u5374\u662f\u542c\u5f97\u5979\u53cc\u817f \u8f6f\u4e86\u3002 \u3000\u3000\u4e0e\u6b64\u540c\u65f6\uff0c\u67f3\u5143\u7532\u7684\u5288\u7a7a\u638c\u529b\u4ea6\u5df2\u5230\u8fbe\uff0c\u6a80\u7fbd\u51b2\u7684\u80cc\u5fc3\u5982\u53d7\u94c1\u731b\u51fb\uff0c\u9976\u662f\u4ed6\u5185\u529f\u7cbe\u7eaf\uff0c \u8fd9\u5239\u90a3\u95f4\uff0c\u4e94\u810f\u516d\u8151\u4e5f\u597d\u50cf\u7ed9\u7ffb\u8f6c\u4e86\u4e00\u822c\uff0c\u4e0d\u8fc7\u67f3\u5143\u7532\u7684\u5288\u7a7a\u638c\u5374\u662f\u63a7\u5236\u5f97\u4e0d\u53ca\u6a80\u7fbd\u51b2\u4e4b \u5999\uff0c\u4ed6\u7684\u76ee\u6807\u662f\u6a80\u7fbd\u51b2\uff0c\u5728\u6a80\u7fbd\u51b2\uff0c\u5728\u6a80\u7fbd\u51b2\u65c1\u8fb9\u7684\u4eba\uff0c\u5374\u4e5f\u7ed9\u4ed6\u7684\u638c\u529b\u6ce2\u53ca\u4e86\u3002\u53ea\u542c\u5f97 \u201c\u6251\u901a\u3001\u6251\u901a\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u6a80\u7fbd\u51b2\u5012\u6ca1\u6709\u5012\u4e0b\u3002\u53cd\u800c\u662f\u5343\u67f3\u5e84\u7684\u5e84\u4e01\u548c\u95e8\u5ba2\u5012\u4e0b\u4e86\u516d\u4e03 \u4e2a\u3002 \u3000\u3000\u53ef\u662f\u5c31\u662f\u6b64\u65f6\uff0c\u4e00\u5927\u7fa4\u6c5f\u6e56\u4eba\u7269\u6d8c\u4e86\u8fdb\u6765\u3002\u4e3a\u9996\u7684\u7adf\u7136\u662f\u6c5f\u5357\u5927\u4fa0\u94c1\u7b14\u4e66\u751f\u6587\u9038\u51e1\u3002 \u3000\u3000\u6587\u9038\u51e1\u7b2c\u4e00\u773c\u5c31\u770b\u89c1\u6a80\u7fbd\u51b2\u548c\u949f\u7075\u79c0\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u626c\u58f0\u95ee\u9053\uff1a\u201c\u963f\u79c0\uff0c\u4f60\u7684\u7237\u7237\u5462\uff1f\u4ed6 \u662f\u4e0d\u662f\u4e5f\u5df2\u6765\u4e86\uff1f\u6a80\u7fbd\u51b2\uff0c\u4f60\u53c8\u5c06\u5979\u62b1\u4f4f\u505a\u4ec0\u4e48\uff0c\u5feb\u5c06\u5979\u653e\u4e0b\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u5636\u54d1\u7740\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u6587\u53d4\u53d4\uff0c\u6211\u7684\u7237\u7237\u7ed9\u4ed6\u4eec\u6740\u6b7b\u4e86\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u82e5\u5c06\u5979\u653e\u4e0b\uff0c\u5343\u67f3\u5e84\u7684\u4eba\u5c31\u8981\u628a\u5979\u6349\u53bb\u4e86\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\u2014\u2014\u54a6\uff0c\u79c0\u59b9\uff0c \u4f60\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u56e0\u53d7\u4e0d\u8d77\u8fd9\u4e48\u5927\u7684\u523a\u6fc0\uff0c\u65e9\u5df2\u5fc3\u529b\u4ea4\u7601\u4e86\u3002\u5979\u672c\u6765\u8981\u628a\u771f\u76f8\u544a\u8bc9\u6587\u9038\u51e1\uff0c\u4f46\u4e5f\u53ea \u80fd\u8bf4\u51fa\u4e00\u53e5\u8bdd\uff0c\u5c31\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u6587\u9038\u51e1\u559d\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u6587\u5927\u4fa0\uff0c\u4f60\u662f\u4e3a\u4e86\u6349\u62ff\u91d1\u56fd\u5978\u7ec6\u800c\u6765\u7684\u5427\uff1f\u54ce\uff0c\u8fd9\u5c0f\u59d1\u5a18\u4e0d\u8bc6\u597d\u6b79\uff0c\u5374\u628a \u91d1\u56fd\u7684\u5978\u7ec6\u4f5c\u54e5\u54e5\u3002\u949f\u4e0d\u9e23\u8fd9\u8001\u513f\u4e5f\u4e0d\u660e\u4e8b\u7406\uff0c\u4e3a\u4e86\u5b59\u5973\u513f\uff0c\u786c\u8981\u8892\u62a4\u5978\u7ec6\u3002\u4ed6\u548c\u6211\u7684\u95e8\u5ba2 \u6597\u5f97\u4e24\u8d25\u4ff1\u4ea1\uff0c\u53ef\u4e5f\u602a\u4e0d\u5f97\u6211\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u63a2\u949f\u7075\u79c0\u7684\u9f3b\u606f\uff0c\u77e5\u9053\u5979\u4e0d\u8fc7\u662f\u4e00\u65f6\u6655\u5012\uff0c\u7a0d\u7a0d\u653e\u5fc3\uff0c\u559d\u9053\uff1a\u201c\u65e0\u803b\u8001\u8d3c\uff0c\u4f60 \u624d\u662f\u91d1\u56fd\u5978\u7ec6\uff01\u201d \u3000\u3000\u67f3\u5143\u7532\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u6587\u5927\u4fa0\uff0c\u4f60\u76f8\u4fe1\u8c01\uff0c\u524d\u5929\u6740\u5bb3\u4e86\u90a3\u8bb8\u591a\u6c5f\u5357\u4fa0\u4e49\u9053\u7684\u4eba\u53ef\u4e0d\u662f \u6211\uff01\u201d \u3000\u3000\u738b\u5b87\u5ead\u867d\u7136\u66fe\u900f\u9732\u8fc7\u4e00\u70b9\u6d88\u606f\u7ed9\u6587\u9038\u51e1\u3002\u4f46\u90a3\u4e5f\u53ea\u662f\u201c\u4e10\u5e2e\u4e00\u4e2a\u91cd\u8981\u4eba\u7269\u201d\u5bf9\u6a80\u7fbd\u51b2\u7684 \u770b\u6cd5\u800c\u5df2\u3002\u738b\u5b87\u5ead\u5e76\u672b\u5c06\u6240\u77e5\u7684\u5168\u90e8\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u6587\u9038\u51e1\u601d\u7591\u4e0d\u5b9a\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u67f3\u5143\u7532\u8bf4\u7684\u603b\u662f\u4e8b\u5b9e\u3002\u4ed6\u201c\u5f53\u673a\u7acb\u65ad\u201d\uff0c\u559d\u9053\uff1a\u201c\u6a80\u7fbd \u51b2\uff0c\u4f60\u7684\u8eab\u4efd\u6211\u5df2\u77e5\u968f\u4e86\u3002\u4f60\u624b\u4e0a\u67d3\u4e86\u6211\u7684\u670b\u53cb\u7684\u8840\uff0c\u4f60\u8981\u8fd8\u662f\u4e2a\u7537\u5b50\u6c49\u7684\u8bdd\uff0c\u5feb\u628a\u8fd9\u5c0f\u59d1 \u5a18\u653e\u4e0b\uff01\u201d\u8bed\u6c14\u51cc\u5389\uff0c\u7adf\u7136\u662f\u8ba4\u5b9a\u64c5\u7fbd\u51b2\u8981\u628a\u949f\u7075\u79c0\u631f\u4e3a\u4eba\u8d28\u4e86\u3002\u6a80\u7fbd\u51b2\u4ea6\u662f\u6ee1\u809a\u76ae\u95f7\u6c14\u65e0 \u53ef\u53d1\u6cc4\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6587\u9038\u51e1\uff0c\u4f60\u8fd9\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff01\u597d\u5427\uff0c\u4f60\u8981\u6740\u6211\uff0c\u90a3\u5c31\u6765 \u5427\uff01\u201d \u3000\u3000\u6587\u9038\u51e1\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u631f\u6301\u4eba\u8d28\u6211\u5c31\u5948\u4f55\u4e0d\u4e86\u4f60\u5417\uff1f\u201d\u53cc\u7b14\u659c\u98de\uff0c\u4f7f\u51fa\u4e86\u5f20\u65ed\u8349\u4e66\u7684\u7b14 \u6cd5\uff0c\u75be\u5982\u98ce\u96e8\u822c\u7684\u5411\u6a80\u7fbd\u51b2\u70b9\u6765\uff0c\u4ed6\u7b14\u8d70\u9f99\u86c7\uff0c\u6bcf\u4e00\u7b14\u90fd\u662f\u70b9\u5411\u6a80\u7fbd\u51b2\u7684\u8981\u5bb3\u7a74\u9053\u3002\u4f46\u7b14\u4e0a \u4e5f\u50cf\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u6ca1\u78b0\u4e0a\u949f\u7075\u79c0\u5206\u6beb\u3002\u6a80\u7fbd\u51b2\u6012\u6c14\u52c3\u53d1\uff0c\u4e5f\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\uff0c\u7389\u7bab\u72c2\u6325\uff0c \u7d22\u6027\u5c31\u4e0e\u6587\u9038\u51e1\u62fc\u547d\u3002 \u3000\u3000\u6696\u7389\u7bab\u662f\u4ef6\u6b66\u6797\u5f02\u5b9d\uff0c\u6a80\u7fbd\u51b2\u5728\u5175\u5668\u4e0a\u5148\u4e0d\u5403\u4e8f\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u628a\u6587\u9038\u51e1\u53cc\u7b14\u67b6\u5f00\uff0c\u7389\u7bab \u8fde\u6307\uff0c\u5b9b\u5982\u70b9\u6c34\u6674\u8712\uff0c\u4e00\u63a0\u5373\u8fc7\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4ece\u6587\u9038\u51e1\u7684\u80a9\u4e95\u7a74\u70b9\u81f3\u624b\u638c\u80cc\u7684\u7ae0\u95e8\u7a74\uff0c\u867d\u7136 \u6ca1\u6709\u70b9\u5b9e\uff0c\u4f46\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff1b\u6587\u9038\u51e1\u624b\u5c11\u9633\u7ecf\u8109\u7684\u4e8c\u5341\u4e03\u4e2a\u7a74\u9053\uff0c\u90fd\u5df2\u53d7\u5230\u4ed6\u7684\u653b\u51fb\u3002 \u3000\u3000\u4e24\u5927\u70b9\u7a74\u9ad8\u624b\u5404\u663e\u795e\u901a\uff0c\u53cc\u65b9\u90fd\u662f\u4e00\u6cbe\u5373\u9000\uff0c\u4e00\u70b9\u5373\u6536\uff0c\u79fb\u6b65\u6362\u5f62\uff0c\u77ac\u95f4\u767e\u53d8\uff0c\u6587\u9038\u51e1 \u7684\u4e00\u5957\u201c\u8349\u4e66\u7b14\u6cd5\u201d\u4f7f\u5b8c\uff0c\u4e1d\u8c6a\u4e5f\u5360\u4e0d\u5230\u4fbf\u5b9c\uff0c\u864e\u53e3\u5df2\u662f\u9690\u9690\u53d1\u9ebb\u3002\u6587\u9038\u51e1\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u539f\u6765\u90a3\u6b21\u5728\u897f\u6e56\u7684\u8f83\u91cf\uff0c\u6562\u60c5\u4ed6\u8fd8\u662f\u672a\u5c3d\u5168\u529b\u7684\uff1f\u201d\u6a80\u7fbd\u51b2\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u800c\u4e14\u8fd8 \u62b1\u7740\u4e2a\u4eba\uff0c\u6587\u9038\u51e1\u6218\u4ed6\u4e0d\u4e0b\uff0c\u4e0d\u7531\u5f97\u9762\u9732\u60ed\u8272\u81ea\u6127\u4e0d\u5982\u3002 \u3000\u3000\u67f3\u5143\u7532\u9053\uff1a\u201c\u5bf9\u4ed8\u91d1\u56fd\u7684\u5978\u7ec6\uff0c\u53ef\u65e0\u987b\u8ddf\u4ed6\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff01\u201d\u4e00\u638c\u62a4\u80f8\uff0c\u9a88\u6307\u5982\u621f\uff0c \u63c9\u8eab\u800c\u4e0a\uff0c\u52a0\u5165\u6218\u56e2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u5927\u4fa0\uff0c\u4f60\u8fd8\u6709\u6ca1\u6709\u6b66\u529f\u9ad8\u5f3a\u7684\u670b\u53cb\uff0c\u53eb\u4ed6\u4eec\u4e00\u8d77\u6765\u5427\uff01\u53cd \u6b63\u4eca\u65e5\u6211\u662f\u6b7b\u4e86\uff01\u4e0d\u5982\u8ba9\u6211\u591a\u4f1a\u51e0\u4f4d\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\uff0c\u6211\u4ea6\u53ef\u6b7b\u800c\u65e0\u61be\uff01\u201d \u3000\u3000\u6587\u9038\u51e1\u9762\u4e0a\u4e00\u7ea2\uff0c\u4fbf\u60f3\u9000\u51fa\u5708\u5b50\uff0c\u67f3\u5143\u7532\u9053\uff1a\u201c\u9022\u5c27\u821c\uff0c\u8bb2\u63d6\u8ba9\uff0c\u9047\u6840\u7ea3\uff0c\u52a8\u5200\u5175\u3002\u6587 \u5927\u4fa0\uff0c\u4f60\u56e0\u4f55\u4e8b\u800c\u6765\uff0c\u96be\u9053\u8981\u653e\u8fc7\u8fd9\u91d1\u56fd\u5978\u7ec6\u4e48\uff1f\u201d\u6587\u9038\u51e1\u4e00\u60f3\u4e0d\u9519\uff0c\u4e8e\u662f\u9000\u800c\u590d\u4e0a\uff0c\u7ee7\u7eed \u548c\u67f3\u5143\u7532\u8054\u624b\uff0c\u5408\u6597\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u628a\u6696\u7389\u7bab\u821e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u8f6c\u773c\u5316\u4f5c\u4e00\u56e2\u7eff\u8272\u7684\u5149\u534e\uff0c\u5c45\u7136\u5728\u4e24 \u5927\u9ad8\u624b\u56f4\u653b\u4e4b\u4e0b\u3002\u6709\u653b\u6709\u5b88\uff0c\u67f3\u5143\u7532\u521a\u624d\u4e0e\u4ed6\u5355\u6253\u72ec\u6597\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u7a0d\u5360\u70b9\u4e0a\u98ce\uff0c\u73b0\u5728\u4e0e\u6587\u9038 \u51e1\u8054\u624b\u6597\u4ed6\uff0c\u53cd\u800c\u7ed9\u6a80\u7fbd\u51b2\u5360\u4e86\u4f18\u52bf\u3002\u4e0d\u7531\u5f97\u597d\u751f\u8be7\u5f02\uff1a\u201c\u96be\u9053\u4ed6\u521a\u624d\u662f\u6545\u610f\u9690\u85cf\u5b9e\u529b\uff1f\u201d \u60f3\u6cd5\u8ddf\u6587\u9038\u51e1\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u8fd9\u731c\u6d4b\uff0c\u53ea\u80fd\u8bf4\u662f\u5bf9\u4e86\u56db\u5206\u4e4b\u4e00\u3002\u6a80\u7fbd\u51b2\u4e0e\u6587\u9038\u51e1\u5728\u897f\u6e56\u90a3\u4e00\u6218\uff0c\u7684\u786e\u662f\u672a\u5c3d\u5168 \u529b\u7684\uff0c\u4f46\u5f53\u65f6\u6587\u9038\u51e1\u4e5f\u672a\u5c3d\u5168\u529b\u3002\u5018\u82e5\u53cc\u65b9\u90fd\u5c3d\u5168\u529b\u7684\u8bdd\uff0c\u6a80\u7fbd\u51b2\u4e5f\u53ea\u4ee5\u80fd\u7a0d\u80dc\u4e00\u7b79\u800c\u5df2\uff0c \u51b3\u8ba1\u62b5\u5fa1\u4e0d\u4e86\u6587\u9038\u51e1\u8fd9\u6837\u7684\u4e24\u4e2a\u6b66\u529f\u9ad8\u624b\u3002\u81f3\u4e8e\u521a\u624d\u5bc6\u5ba4\u4e2d\u548c\u67f3\u5143\u7532\u7684\u4ea4\u624b\uff0c\u5219\u6a80\u7fbd\u51b2\u65e9\u5df2 \u7ecf\u662f\u4f7f\u4e86\u5168\u529b\u7684\u3002\u90a3\u4e48\u4ed6\u600e\u7684\u53c8\u80fd\u4ee5\u4e00\u654c\u4e8c\u4e86\u3002\u8fd9\u662f\u56e0\u4e3a\u4e00\u4e2a\u4eba\u5230\u4e86\u5371\u6025\u7684\u5173\u5934\uff0c\u8eab\u4f53\u7684\u6f5c \u80fd\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\u4e4b\u6545\u3002\u4e0d\u8fc7\u201c\u6f5c\u80fd\u201d\u4e5f\u4e0d\u662f\u201c\u65e0\u9650\u201d\u7684\uff0c\u53d1\u6325\u5230\u4e86\u6781\u70b9\uff0c \u867d\u53ef\u8fdc\u80dc\u5e73\u65f6\uff0c\u5374\u4e0d\u80fd\u626d\u8f6c\u6839\u672c\u5f62\u52bf\u3002\u8fc7\u4e86\u6570\u5341\u62db\uff0c\u6a80\u7fbd\u51b2\u6e10\u611f\u4e0d\u652f\uff0c\u4ed6\u62b1\u7740\u7684\u949f\u79c0\u7075\u5ffd\u7136 \u53d1\u51fa\u547b\u541f\uff0c\u597d\u50cf\u68a6\u5453\u4e00\u822c\u5583\u5583\u81ea\u8bed\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u522b\u7406\u6211\uff0c\u8ba9\u6211\u53bb\u89c1\u7237\u7237\uff0c\u53bb\u89c1\u7237 \u7237\uff01\u201d\u663e\u7136\u5979\u662f\u5728\u638c\u98ce\u6fc0\u8361\u4e4b\u4e2d\uff0c\u88ab\u60ca\u9192\u4e86\u7684\uff0c\u6587\u9038\u51e1\u7684\u7b14\u6cd5\u795e\u4fca\u975e\u51e1\uff0c\u5c3d\u7ba1\u4ed6\u6bcf\u4e00\u7b14\u90fd\u662f \u5411\u7740\u6a80\u7fbd\u51b2\u7684\u8981\u5bb3\u201c\u62db\u547c\u201d\uff0c\u7b14\u5c16\u5374\u957f\u7740\u773c\u775b\uff0c\u603b\u662f\u6070\u5230\u597d\u5904\u7684\u907f\u514d\u89e6\u53ca\u949f\u7075\u79c0\uff0c\u4f46\u67f3\u5143\u7532 \u5374\u662f\u6beb\u65e0\u987e\u5fcc\u7684\uff0c\u6b64\u65f6\u4ed6\u638c\u53d8\u6307\uff0c\u6307\u6cd5\u56fa\u7136\u662f\u5728\u5bfb\u7455\u627e\u9699\uff0c\u638c\u529b\u4e5f\u52a0\u5f3a\u5230\u4e86\u516b\u4e5d\u5206\u4e86\uff0c\u4ed6\u7684 \u5288\u7a7a\u638c\u4e09\u4e08\u4e4b\u5916\u4fbf\u53ef\u4f24\u4eba\uff0c\u4f55\u51b5\u662f\u8fd1\u8eab\u640f\u6597\uff1f\u949f\u7075\u79c0\u4e4b\u6240\u4ee5\u6ca1\u6709\u53d7\u4f24\uff0c\u90a3\u662f\u5168\u9760\u6a80\u7fbd\u51b2\u4e3a\u5979 \u63a9\u62a4\u5f97\u7acb\u4e4b\u6545\uff0c\u6a80\u7fbd\u51b2\u7684\u6f5c\u529b\u7684\u53d1\u6325\u5230\u4e86\u6781\u70b9\uff0c\u662f\u53ef\u62b5\u6d88\u67f3\u5143\u7532\u7684\u5288\u7a7a\u638c\u529b\u3002\u4f46\u6b64\u65f6\u4ed6\u6e10\u611f \u4e0d\u652f\uff0c\u5374\u662f\u6ca1\u6709\u628a\u63e1\u4ee4\u949f\u7075\u79c0\u4e0d\u88ab\u6ce2\u53ca\u4e86\u3002\u4ed6\u542c\u5f97\u949f\u7075\u79c0\u7684\u547b\u541f\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u601d \u91cf\uff0c\u5979\u7684\u7237\u7237\u90fd\u5df2\u53d7\u6211\u8fde\u7d2f\u6b7b\u4e86\uff0c\u6211\u8fd8\u80fd\u591f\u8ba9\u5979\u4e5f\u966a\u6211\u6b7b\u4e48\u3002\u4ed6\u5fc3\u91cc\u660e\u767d\uff0c\u53ea\u8981\u65f6\u95f4\u7a0d\u957f\uff0c \u4ed6\u548c\u949f\u7075\u79c0\u6050\u6015\u540c\u5f52\u4e8e\u5c3d\u4e86\u3002 \u3000\u3000\u6587\u9038\u51e1\u4f3c\u4e4e\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u8fd8\u4e0d\u6295\u964d\u5417\uff1f\u4f60\u6b7b\u4e86\u4e0d\u6253\u7d27\uff0c\u8fde \u7d2f\u4e86\u8fd9\u5c0f\u59d1\u5a18\uff0c\u4f60\u4e8e\u5fc3\u4f55\u5b89\u3002\u201d\u4e5f\u4e0d\u77e5\u9053\u949f\u7075\u79c0\u662f\u5426\u5df2\u7ecf\u6e05\u9192\u8fc7\u6765\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5927\u54e5\u54e5\u4e0d \u8981\u6295\u964d\uff0c\u8fd9\u662f\u7237\u7237\u8bf4\u7684\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u50b2\u6c14\u4e0e\u90c1\u6c14\u5e76\u53d1\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u77e5\u6211\u8005\u8c13\u6211\u5fc3\u5fe7\u4e0d\u77e5\u6211\u8005\u8c13\u6211\u4f55\u6c42\uff0c\u60a0\u60a0\u82cd \u5929\uff0c\u5f7c\u4f55\u4eba\u54c9\uff01\u201d\u7389\u7bab\u6a2a\u6325\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u7eff\u5149\u66b4\u6da8\uff0c\u628a\u67f3\u5143\u7532\u548c\u6587\u9038\u513f\u90fd\u903c\u5f00\u4e86\u3002\u4ed6\u5fc3 \u5934\u6fc0\u6124\u4ea6\u5df2\u5230\u6781\u70b9\uff0c\u628a\u6b8b\u4f59\u7684\u6f5c\u529b\u90fd\u903c\u4e86\u51fa\u6765\uff01\u5267\u6597\u4e2d\u6a80\u7fbd\u51b2\u5ffd\u89c9\u5589\u5499\u95f4\u53c8\u6709\u80a1\u751c\u610f\uff0c\u9c9c\u8840 \u5192\u4e0a\u5589\u5934\uff0c\u867d\u7136\u4ed6\u7acb\u628a\u53e3\u9c9c\u8840\u54bd\u4e86\u4e0b\u53bb\uff0c\u53ef\u5634\u89d2\u5df2\u662f\u6c81\u51fa\u8840\u4e1d\u4e86\uff0c\u6587\u9038\u51e1\u559d\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c \u4f60\u8fd8\u4e0d\u6295\u964d\uff0c\u5f53\u771f\u8981\u548c\u8fd9\u5c0f\u59d1\u5a18\u4e00\u8d77\u6b7b\u4e48\uff1f\u201d\u5c31\u5728\u6b64\u9645\uff0c\u5ffd\u542c\u5f97\u94f6\u94c3\u4f3c\u7684\u5a9a\u7b11\u58f0\uff0c\u7389\u9762\u5996\u72d0 \u8d6b\u8fde\u6e05\u6ce2\u8d70\u4e86\u51fa\u6765\u4e86\u3002 \u3000\u3000\u67f3\u5143\u7532\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u683c\u5443\u5443\uff0c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u4ed6\u4e00\u65f6\u60c5\u6025\u3002\u51e0\u4e4e\u628a\u201c\u683c\u683c\u201d \u4e24\u4e2a\u5b57\u8bf4\u4e86\u51fa\u6765\uff0c\u84e6\u5730\u4e00\u7701\uff0c\u6709\u6587\u9038\u51e1\u5728\u4ed6\u65c1\u8fb9\uff0c\u5982\u4f55\u53ef\u4ee5\u66b4\u9732\u8d6b\u8fde\u6e05\u6ce2\u7684\u8eab\u4efd\uff0c\u53ea\u597d\u7528\u542b \u7cca\u4e0d\u6e05\u7684\u5589\u95f4\uff0c\u628a\u201c\u683c\u683c\u201d\u5ff5\u6210\u201c\u5443\u5443\u201d\u3002\u201c\u89c1\u90ae\u201d\u662f\u597d\u50cf\u201c\u54b3\u54b3\u201d\u3001\u5509\u5509\u201c\u4e00\u7c7b\u6709\u6765\u52a0\u5f3a \u8bed\u6c14\u7684\u58f0\u97f3\uff0c\u8bb8\u591a\u4eba\u5728\u8bf4\u9053\u6b63\u6587\u4e4b\u524d\uff0c\u4e60\u60ef\u7528\u8fd9\u7c7b\u201c\u52a9\u8bed\u8bcd\u201d\u7684\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u67f3\u5e84\u4e3b\uff0c\u6211\u8981\u4f60\u4eec\u6d3b\u64d2\u4ed6\u7684\uff0c\u600e\u4e48\u4f60\u7adf\u662f\u8981\u6740\u4ed6\u5462\uff1f\u597d\uff0c\u4f60\u6ca1\u672c\u9886\u62ff \u4ed6\uff0c\u6211\u53ea\u597d\u81ea\u5df1\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u201c\u51fa\u624b\u201d\u4e8c\u5b57\uff0c\u7acb\u5373\u628a\u624b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9897\u5f39\u4e38\u5728\u7a7a\u4e2d\u7206\u70b8\uff0c\u5f39\u4e38 \u867d\u5c0f\uff0c\u70df\u96fe\u5374\u5feb\u901f\u5f25\u6f2b\uff0c\u8f6c\u773c\u95f4\u5728\u8fd9\u56ed\u5b50\u91cc\u5df2\u662f\u53ea\u80fd\u770b\u89c1\u5e62\u5e62\u7684\u9ed1\u5f71\u4e86\uff0c\u8fd9\u70df\u96fe\u8fd8\u6709\u4e00\u6837\u53e4 \u602a\uff0c\u5b83\u662f\u5e26\u7740\u6de1\u6de1\u7684\u5e7d\u9999\u7684\uff0c\u95fb\u5230\u9999\u5473\u7684\u4eba\u3002\u7ec3\u6709\u5185\u529f\u7684\u52c9\u5f3a\u53ef\u4ee5\u652f\u6301\uff0c\u672a\u7ec3\u8fc7\u5185\u529f\u7684\u5219\u662f \u5728\u7247\u523b\u8fd9\u4e4b\u95f4\uff0c\u4fbf\u90fd\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u6015\u9999\u96fe\u5f39\uff0c\u53ea\u6015\u949f\u7075\u79c0\u4e2d\u6bd2\uff0c\u597d\u5728\u4ed6\u8fd8\u6709\u4e00\u9897\u5929\u5c71\u96ea\u83b2\u6ce1\u5236\u7684\u201c\u78a7\u7075\u4e39\u201d\uff0c\u8d76 \u5fd9\u628a\u8fd9\u9897\u78a7\u7075\u4e39\u7eb3\u5165\u949f\u7075\u79c0\u53e3\u4e2d\u3002\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u4e00\u5927\u534a\u90fd\u949f\u4e86\u6bd2\u70df\u3002\u67f3\u5143\u7532\u6bd4\u8f83\u597d\u4e9b\uff0c\u4f46\u4ed6 \u5f00\u53e3\u8bf4\u8bdd\u3002\u5438\u8fdb\u4e0d\u5c11\u8ff7\u9999\uff0c\u4e5f\u662f\u4e0d\u5927\u597d\u53d7\uff0c\u4ed6\u6697\u81ea\u601d\u91cf\uff1a\u201c\u7389\u9762\u5996\u6293\u6551\u6a80\u7fbd\u51b2\uff0c\u6211\u867d\u7136\u53ef\u4ee5 \u5411\u5b8c\u989c\u738b\u7237\u544a\u5979\u7684\u4f0f\uff0c\u5979\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5e72\u683c\u683c\u3002\u4e0d\u6015\u6597\u4e0d\u8fc7\u4e5f\uff0c\u4f46\u4e8b\u60c5\u603b\u662f\u9884\u7559\u9000\u6b65\uff0c\u76ee\u524d\u738b \u7237\u8fd8\u662f\u8981\u5229\u7528\u5979\u7684\uff0c\u6211\u82e5\u628a\u4e8b\u60c5\u505a\u5f97\u592a\u7edd\uff0c\u5bf9\u6211\u4e5f\u672a\u5fc5\u771f\u6709\u597d\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u4ed6\u4e5f\u6545\u610f\u88c5\u4f5c \u4e2d\u4e86\u6bd2\u7684\u6a21\u6837\uff0c\u653e\u5f03\u8ffd\u8e2a\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u662f\u5343\u67f3\u5e84\u7684\u5e38\u5ba2\uff0c\u719f\u6089\u9053\u8def\uff0c\u6a80\u7fbd\u51b2\u8ddf\u7740\u5979\u8d70\uff0c\u4e0d\u4e45\uff0c\u5c31\u51fa\u4e86\u56ed\u95e8\u3002 \u3000\u3000\u5e38\u5dde\u8001\u6b66\u5e08\u5b59\u4ef2\u662f\u5934\u5934\u4e4b\u4e00\uff0c\u559d\u9053\uff1a\u201c\u5927\u5bb6\u51c6\u5907\u6697\u5668\uff0c\u2018\u62db\u547c\u2019\u5ba2\u4eba\uff0c\u6211\u6570\u5230\u4e09\u58f0\uff0c\u5927 \u4f19\u513f\u5c31\u53d1\u6697\u5668\u5427\uff01\u201d \u3000\u3000\u6709\u4eba\u95ee\u9053\uff1a\u201c\u949f\u4e0d\u9e23\u7684\u5b59\u5973\u5728\u90a3\u5978\u7ec6\u8eab\u8fb9\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5b59\u949f\u9053\uff1a\u201c\u5979\u81ea\u7518\u5815\u843d\uff0c\u82e5\u4e0d\u79bb\u5f00\u5730\u90a3\u4e2a\u5978\u7ec6\uff0c\u4e00\u9f50\u5c04\u6740\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u770b\u89c1\u4e34\u5b89\u4e10\u5e2e\u7684\u526f\u8235\u4e3b\u5185\u5d14\u6d69\u6c11\u5728\u8fd9\u73ed\u4eba\u4e2d\u95f4\uff0c\u53eb\u9053\uff1a\u201c\u5d14\u5927\u54e5\uff0c\u8bf7\u4f60\u4eec\u542c\u6211\u8bf4 \u660e\u771f\u76f8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5d14\u6d69\u90a3\u6b21\u9669\u4f24\u5728\u5357\u5c71\u864e\u624b\u4e0b\uff0c\u5e78\u4e8f\u5f97\u5230\u6a80\u7fbd\u51b2\u6551\u4ed6\u6027\u547d\uff0c\u4fbf\u9053\uff1a\u201c\u5b59\u8001\u524d\u8f88\uff0c\u6587\u5927\u4fa0\u8fd8 \u6ca1\u6709\u51fa\u6765\uff0c\u4e0d\u5982\u7b49\u4ed6\u51fa\u6765\uff0c\u54b1\u4eec\u518d\u884c\u8bba\u5904\u4e0d\u8fdf\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5feb\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u56ed\u95e8\u5916\u6709\u8f86\u9a6c\u8f66\uff0c\u5230\u4e86\u8fd9\u4e2a\u5730\u6b65\uff0c\u6a80\u7fbd\u51b2\u53ea\u597d\u7531\u5979\u6446\u5e03\uff0c\u62b1\u7740\u949f\u7075\u79c0\u8ddf\u5979\u4e0a\u4e86\u9a6c\u8f66\u3002 \u3000\u3000\u5b59\u4ef2\u5e26\u9886\u5341\u591a\u4eba\u5185\u529f\u8f83\u9ad8\uff0c\u4e2d\u6bd2\u8f83\u8f7b\u7684\u4fa0\u4e49\u9053\u8ffd\u4e86\u51fa\u4e1a\uff0c\u6697\u5668\u7eb7\u98de\uff0c\u4e0d\u8fc7\u53ea\u6709\u51e0\u679d\u5f3a\u5f13 \u5c04\u7bad\u63d2\u5165\u9a6c\u8f66\u8f66\u53a2\u5916\u9762\u7684\u677f\u58c1\u3002 \u3000\u3000\u672c\u6765\u6697\u5668\u662f\u8ffd\u4e0d\u4e0a\u9a6c\u8f66\u7684\uff0c\u4f46\u8d6b\u8fde\u6e05\u6ce2\u8fd8\u662f\u8fa3\u624b\u53cd\u51fb\u3002 \u3000\u3000\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u706b\u5149\u8000\u95ea\uff0c\u70df\u96fe\u8ff7\u6f2b\uff0c\u70df\u96fe\u4e4b\u4e2d\u8fd8\u6709\u8bb8\u591a\u91d1\u8272\u7684\u5149\u8292\u95ea\u70c1\u3002\u539f\u6765\u5979\u8fd9\u6b21 \u53d1\u51fa\u7684\u6697\u5668\u540d\u4e3a\u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u201d\uff0c\u6bd4\u201c\u9999\u96fe\u5f39\u201d\u66f4\u52a0\u5389\u5bb3\uff0c\u90a3\u4e9b\u91d1\u8272\u5149\u8292\u4e43\u662f\u7ec6\u5982\u725b\u6bdb \u7684\u6885\u82b1\u9488\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u3001\u201c\u535c\u901a\u201d\u7684\u5012\u5730\u58f0\u4e0e\u201c\u54ce\u54df\u201d\u201c\u54ce\u54df\u201d\u7684\u5c16\u53eb\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u6709\u7684\u4e2d\u6bd2 \u660f\u8ff7\uff0c\u6709\u7684\u88ab\u6885\u82b1\u9488\u523a\u4f24\uff0c\u5341\u591a\u4e2a\u6c5f\u5357\u597d\u6c49\uff0c\u5168\u90fd\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u867d\u7136\u5df2\u7ecf\u8131\u9669\uff0c\u5fc3\u5934\u53ef\u662f\u4e00\u70b9\u4e5f\u4e0d\u8f7b\u677e\u3002\u4ed6\u7684\u8033\u6735\u542c\u5230\u90a3\u4e9b\u597d\u6c49\u7684\u547c\u53eb\u58f0\uff0c\u5fc3 \u9053\uff1a\u201c\u8fd9\u6b21\u4f24\u7684\u6bd4\u4e0a\u6b21\u66f4\u591a\uff0c\u6211\u8fd9\u4e2a\u91d1\u56fd\u5978\u7ec6\u7684\u5acc\u7591\u6050\u6015\u66f4\u662f\u8df3\u5230\u9ec4\u6cb3\u4e5f\u6d17\u4e0d\u6e05\u4e86\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4f3c\u662f\u770b\u900f\u4ed6\u7684\u5fc3\u601d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53c8\u5728\u5acc\u6211\u7684\u624b\u6bb5\u592a\u8fc7\u6bd2\u8fa3\u662f\u4e0d\u662f\uff1f\u563f\u563f\uff0c \u82e5\u4e0d\u662f\u627e\u7528\u8fd9\u7b49\u6bd2\u8fa3\u7684\u624b\u6bb5\uff0c\u4f60\u548c\u4f60\u6000\u91cc\u8fd9\u5c0f\u59d1\u5a18\u6050\u6015\u90fd\u8981\u53d8\u6210\u523a\u8c03\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u8fd9\u9a6c\u8f66\u8dd1\u7684\u98de\u5feb\uff0c\u8d6b\u8fde\u6e05\u6ce2\u6c89\u9ed8\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u548c\u9053\u4f60\u5fc3\u91cc\u4e0d\u75db\u5feb\uff0c\u4f60\u8981\u9a82\u6211 \u5c31\u5c3d\u7ba1\u9a82\u5427\uff0c\u6211\u8ba9\u4f60\u9a82\u4e2a\u75db\u5feb\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u9053\uff1a\u201c\u4f60\u522b\u8bf4\u4e86\uff0c\u6211\u628a\u6211\u8fd9\u6761\u6027\u547d\u8fd8\u7ed9\u4f60\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8fd9\u6761\u6027\u547d\u662f\u4f60\u66ff\u6211\u6361\u56de\u6765\u7684\uff0c\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u6211\u662f\u5e94\u8be5\u4efb\u7531\u4f60\u6765\u5904\u7f6e \u4e86\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u613f\u610f\u8ddf\u6211\u56de\u4eac\u4e86\uff1f\u56e0\u4e3a\u6211\u5e76\u4e0d\u662f\u60f3\u662f\u4f60\u7684\u6027\u547d\u3002\u201d\u4ed6\u76ee\u5149 \u5c04\u5230\u6a80\u7fbd\u51b2\u9762\u4e0a\uff0c\u4f46\u89c1\u6a80\u7fbd\u51b2\u7684\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6027\u547d\u7684\u90fd\u662f\u4f60\u7684\uff0c\u4f60\u8981\u600e\u4e48\u5c31\u600e\u4e48\u6837\uff0c\u4f55\u987b\u95ee\u6211\u613f\u4e0d\u613f\u610f\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5176\u5b9e\u6211\u8fd9\u6837\u505a\u4e86\u662f\u4e3a\u4e86\u4f60\u7684\u597d\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u548c\u67f3\u5143\u7532\u8bf4\u8bdd\u7684\u6211\u90fd\u542c\u89c1\u4e86\uff1f\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u5e94 \u8be5\u77e5\u9053\u6211\u5e76\u4e0d\u662f\u5b58\u5fc3\u5bb3\u4f60\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u662f\u4e0d\u8bb8\u67f3\u5143\u7532\u5bb3\u6211\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u8981\u4ed6\u5e9f\u6389\u6211\u7684\u6b66\u529f\u3002\u4f60\u73b0\u5728\u4e0d\u662f \u8981\u6211\u81ea\u884c\u5e9f\u6389\u6b66\u529f\uff0c\u4f60\u624d\u80fd\u653e\u5fc3\u6536\u6211\u505a\u4f60\u7684\u6251\u4eba\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u82b1\u5bb9\u5931\u8272\uff0c\u534a\u664c\uff0c\u9893\u7136\u9053\uff1a\u201c\u6211\u672c\u6765\u53ef\u4ee5\u548c\u4f60\u89e3\u91ca\u7684\uff0c\u4f46\u4e0d\u60f3\u5230\u4f60\u5bf9\u6211\u7684\u8bef\u4f1a \u7adf\u662f\u5982\u6b64\u4e4b\u6df1\uff0c\u591a\u8bf4\u4e5f\u65e0\u76ca\u4e86\u3002\u597d\uff0c\u4f60\u8bf4\uff0c\u6211\u60f3\u600e\u6837\uff0c\u6211\u90fd\u4f9d\u673a\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8fd8\u662f\u597d\u53e5\u8001\u8bdd\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u8001\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u8d70\u4f60\u7684\u9633\u5173\u9053\uff0c\u6211\u8d70\u6211\u7684\u72ec\u6728\u6865\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8d70\u7684\u672a\u5fc5\u662f\u9633\u5173\u9053\uff0c\u4e0d\u8fc7\u73b0\u5728\u4e5f\u4e0d\u662f\u4f60\u8d70\u72ec\u6728\u6865\u7684\u65f6\u5019\u3002\u201c\u5979 \u79fb\u5f00\u76ee\u5149\uff0c\u671b\u5411\u8f66\u7a97\u5916\u3002\u6709\u4e00\u961f\u7684\u91d1\u56fd\u58eb\u5175\uff0c\u6b63\u4ece\u524d\u9762\u8d70\u6765\uff0c\u539f\u6765\u8d6b\u8fde\u6e05\u6ce2\u5df2\u7ecf\u7ed5\u8fc7\u8fb9\u5173\uff0c \u8e0f\u5165\u91d1\u56fd\u7684\u8f96\u533a\u4e86\u3002 \u3000\u3000\u5979\u7684\u9a6c\u8f66\u5df2\u7ecf\u7ad6\u8d77\u5b8c\u989c\u738b\u5e9c\u7684\u65d7\u53f7\uff0c\u58eb\u5175\u961f\u957f\u4e5f\u662f\u89c1\u8fc7\u5979\uff0c\u7684\u614c\u4e0d\u8fed\u53eb\u5175\u58eb\u8eb2\u8fc7\u4e24\u65c1\uff0c \u7ed9\u5979\u8ba9\u8def\uff0c\u8d6b\u8fde\u6e05\u6ce2\u7406\u4e5f\u4e0d\u7406\u90a3\u4e2a\u961f\u7684\u201c\u95ee\u5b89\u201d\u53ea\u662f\u6446\u4e00\u6446\u624b\uff0c\u5c31\u98de\u8f66\u76f4\u8fc7\u3002 \u3000\u3000\u949f\u7075\u79c0\u4ecd\u7136\u660f\u8ff7\u5728\u6a80\u7fbd\u51b2\u7684\u6000\u4e2d\uff0c\u6a80\u7fbd\u51b2\u5bf9\u5916\u95f4\u7684\u4e00\u5207\uff0c\u66f4\u662f\u89c6\u800c\u4e0d\u89c9\uff0c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u8def\u4e0a\u78b0\u89c1\u7684\u91d1\u5175\u8d8a\u6765\u8d8a\u5c11\uff0c\u7ec8\u4e8e\u89c1\u4e0d\u5230\u4e86\u3002\u4ed6\u4eec\u5df2\u7ecf\u8fdb\u5165\u201c\u65e0\u4eba\u5730\u5e26\u201d\u7684\u5c71\u533a\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u505c\u4e0b\u9a6c\u8f66\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u628a\u8fd9\u8f86\u9a6c\u7559\u7ed9\u4f60\uff0c\u4f60\u53ef\u4ee5\u548c\u4f60\u8fd9\u4f4d\u59d1\u5a18\u8d70\u4f60\u7684\u9633\u5173\u9053 \u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7528\u4e0d\u7740\uff0c\u6211\u8fd8\u80fd\u8d70\u8def\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u966a\u4ed6\u8d70\u4e0b\u8f66\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u8fde\u6211\u7684\u4e00\u70b9\u70b9\u5fc3\u610f\uff0c\u90fd\u4e0d\u613f\u9886\u53d7\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5927\u4e08\u592b\u6069\u6028\u5206\u660e\uff0c\u6211\u5f97\u628a\u8bdd\u8bf4\u4e2a\u660e\u767d\uff0c\u4f60\u4eca\u65e5\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u4f1a\u62a5\u7b54\u4f60 \u7684\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u8ba9\u4f60\u5229\u7528\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u7684\u62a5\u7b54\uff0c\u4f60\u4e5f\u65e0\u987b\u4f7f\u62a5\u7b54\u3002\u53bb\u5e74\u5728\u5f52\u4e91\u5e84\uff0c\u4f60\u4e5f\u66fe\u7ecf\u6570\u6551\u6211\u4e00 \u6761\u6027\u547d\uff0c\u5982\u4eca\u6211\u53ea\u4e0d\u8fc7\u662f\u8fd8\u4e86\u8fd9\u7b14\u5e10\u800c\u5df2\u3002\u201d\u8f6c\u8eab\u56de\u9a6c\u8f66\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5446\u4e86\u4e00\u5446\uff0c\u76ee\u9001\u5979\u7684\u80cc\u5f71\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u7adf\u6709\u4e00\u70b9\u60c6\u6005\u4e4b\u611f\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5ffd\u7136\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u60c5\uff0c\u6211\u77e5\u9053\u4f60\u6709\u78a7\u7075\u4e39\uff0c\u53ef\u4ee5\u4fdd \u5168\u8fd9\u5c0f\u59d1\u5a18\u7684\u6027\u547d\uff0c\u4f46\u6709\u4e86\u6211\u8fd9\u679a\u89e3\u836f\uff0c\u529f\u6548\u53ef\u4ee5\u66f4\u597d\u4e00\u4e9b\uff0c\u800c\u4e14\u53ef\u4ee5\u6c38\u7edd\u540e\u60a3\u3002\u201d\u8bf4\u7f62\u3002 \u62ff\u4e86\u4e00\u679a\u89e3\u836f\u7ed9\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u56db\u76ee\u76f8\u4ea4\uff0c\u4e24\u4eba\u90fd\u4e0d\u7981\u9887\u591a\u611f\u89e6\u3002\u6a80\u7fbd\u51b2\u907f\u5f00\u5979\u7684\u76ee\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d\u8d6b \u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u7684\u9762\u8272\u597d\u50cf\u6709\u70b9\u4e0d\u5bf9\uff0c\u662f\u53d7\u4f24\u4e86\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u591a\u8c22\u4f60\u7684\u5173\u5fc3\uff0c\u6211\u4f1a\u7167\u987e\u81ea\u5df1\u7684\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u6563\u7684\u7b75\u5e2d\uff0c\u8fd9\u6837\u6563\u4e86\u4e5f\u597d\u3002\u4f60\u81ea\u5df1\u591a\u4fdd\u91cd \u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u76ee\u9001\u9a6c\u8f66\u8fdc\u53bb\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u4e5f\u8be5\u8d70\u4e86\uff0c\u4f46\u5929\u5730\u867d\u5927\uff0c\u4f55\u5904\u662f\u6211\u5bb9\u8eab\u4e4b \u5730\uff1f\u201d\u4e0d\u9519\uff0c\u8d6b\u8fde\u6e05\u6ce2\u5982\u4eca\u5df2\u662f\u7ad9\u5728\u548c\u4ed6\u654c\u5bf9\u7684\u5730\u4f4d\uff0c\u4f46\u4ed6\u4eec\u6bd5\u7adf\u66fe\u7ecf\u662f\u670b\u53cb\uff0c\u4ed6\u521d\u61c2\u4eba \u4e8b\uff0c\u5c31\u5931\u53bb\u4e86\u6240\u6709\u7684\u4eb2\u4eba\uff0c\u5982\u4eca\u662f\u8fde\u6700\u540e\u4e00\u4e2a\u201c\u670b\u53cb\u201d\u4e5f\u5931\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u6765\u5230\u6c5f\u5357\uff0c\u672c\u6765\u662f\u60f3\u7ed3\u4ea4\u670b\u53cb\u7684\uff0c\u54ea\u60f3\u5f97\u5230\u4f1a\u5f04\u6210\u8fd9\u4e2a\u5c40\u9762\uff0c\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u4e0d\u5f53\u4ed6\u5f53 \u4f5c\u670b\u53cb\uff0c\u800c\u662f\u628a\u4ed6\u5f53\u4f5c\u654c\u4eba\u4e86\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u6bcd\u4eb2\u7684\u9057\u613f\uff0c\u771f\u662f\u6b32\u54ed\u65e0\u6cea\u3002\u201c\u5a18\u4eb2\u4e00\u751f\u7684\u5fc3\u613f\uff0c\u5c31\u662f\u76fc\u671b\u5b8b\u91d1\u4e24\u56fd\u4fee\u597d\uff0c\u6c38 \u7edd\u5e72\u6208\u3002\u4f46\u5728\u6211\u4eca\u5929\u7684\u5904\u5883\uff0c\u53c8\u600e\u80fd\u5b8c\u6210\u5a18\u4eb2\u7684\u5fc3\u613f\u5462\uff1f\u201d \u3000\u3000\u8ff7\u832b\u4e2d\u4ed6\u7684\u8033\u8fb9\u54cd\u8d77\u4e86\u6bcd\u4eb2\u4e34\u7ec8\u7684\u5429\u5490\uff1a\u201c\u513f\u554a\uff0c\u4f60\u8981\u8bb0\u7740\uff0c\u4f60\u7684\u7239\u7239\u662f\u91d1\u56fd\u4eba\uff0c\u4f60\u7684 \u5a18\u4eb2\u7684\u5b8b\u56fd\u4eba\uff0c\u4f60\u8981\u505a\u4e86\u4e00\u756a\u4e8b\u4e1a\uff0c\u8ba9\u91d1\u5b8b\u4e24\u56fd\u7684\u767e\u59d3\u5982\u540c\u4e00\u5bb6\u3002\u201d\u8ff7\u832b\u4e2d\u4ed6\u597d\u50cf\u770b\u89c1\u6587\u9038 \u51e1\u6307\u7740\u9a82\u4ed6\uff1a\u201c\u5978\u7ec6\uff0c\u5978\u7ec6\uff0c\u4f60\u8fd9\u4e2a\u91d1\u56fd\u5978\u7ec6\uff01\u201d\u597d\u50cf\u770b\u89c1\u4e86\u4f24\u5728\u4ed6\u624b\u4e0b\u7684\u6c5f\u5357\u4fa0\u4e49\u9053\u5bf9\u4ed6 \u6012\u76ee\u800c\u89c6\u3002 \u3000\u3000\u8ff7\u832b\u4e2d\uff0c\u4ed6\u542c\u89c1\u4e86\u949f\u7075\u79c0\u53d1\u51fa\u4e00\u58f0\u547b\u541f\uff0c\u8fd9\u624d\u7fdf\u7136\u4e00\u7701\uff0c\u4ed6\u5931\u53bb\u4e86\u6240\u6709\u7684\u4eb2\u4eba\uff0c\u8fd9\u4e2a\u4e49 \u59b9\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u518d\u5931\u53bb\u4e86\uff0c\u949f\u7075\u79c0\u8fd8\u6ca1\u6709\u9192\u6765\u3002\u4ed6\u7ed9\u5979\u628a\u8109\uff0c\u8109\u640f\u6b63\u5e38\uff0c\u4ed6\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684 \u4e00\u5757\u77f3\u5934\uff0c\u5f53\u4e0b\u628a\u8d6b\u8fde\u6e05\u6ce2\u4ea4\u7ed9\u4ed6\u7684\u90a3\u9897\u89e3\u836f\u7eb3\u5165\u949f\u7075\u79c0\u53e3\u4e2d\uff0c\u5fc3\u4e2d\u82e6\u7b11\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\u3002\u6050 \u6015\u4e5f\u53ea\u6709\u8fd9\u4e2a\u4e49\u59b9\u966a\u4f34\u6211\u4e86\u3002\u4f46\u6211\u8fd8\u80fd\u591f\u8fde\u7d2f\u5979\u5417\uff1f\u4ed6\u7740\u949f\u7075\u79c0\u7ee7\u7eed\u524d\u884c\uff0c\u80f8\u53e3\u90c1\u95f7\u8d8a\u6765\u8d8a \u751a\uff0c\u4ed6\u662f\u5728\u5c71\u4e0a\u671d\u5317\u8d70\u7684\uff0c\u5c71\u8def\u5d0e\u5c96\uff0c\u4ed6\u62b1\u7740\u4ed6\uff0c\u5f88\u611f\u5403\u529b\uff0c\u6709\u6b21\u8fd8\u9669\u4e9b\u6454\u5012\u3002\u4ed6\u4e0d\u7981\u5fc3\u5934 \u4e00\u51db\uff1a\u201c\u6211\u600e\u7684\u8fd9\u4e48\u4e0d\u6d4e\u4e8b\uff1f\u201d\u8bd5\u4e00\u8fd0\u6c14\uff0c\u53ea\u89c9\u4e39\u7530\u9690\u9690\u4f5c\u75db\uff0c\u4ed6\u660e\u767d\u4e86\uff0c\u4ed6\u662f\u53d7\u5230\u4e25\u91cd\u7684 \u5185\u4f24\u3002\u5982\u4eca\u5df1\u662f\u7b4b\u75b2\u529b\u7aed\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5728\u5343\u67f3\u5e84\u6251\u6551\u949f\u7075\u79c0\u4e4b\u65f6\uff0c\u540e\u5fc3\u53d7\u4e86\u67f3\u5143\u7532\u5288\u7a7a\u638c\u529b\u6240\u4f24\uff0c\u8ddf\u7740\u53c8\u4ee5\u5be1\u654c\u4f17\uff0c\u5f53 \u65f6\u5f3a\u8fd0\u7384\u52a8\u62b5\u5fa1\uff0c\u5185\u4f24\u4eca\u59cb\u53d1\u4f5c\u3002 \u3000\u3000\u4ed6\u62b1\u7740\u949f\u7075\u79c0\uff0c\u8d70\u4e0a\u524d\u9762\u5c71\u5934\uff0c\u60f3\u8981\u627e\u4e2a\u5730\u65b9\u6b47\u606f\uff0c\u8fd0\u6c14\u81ea\u7597\uff0c\u5ffd\u5730\u542c\u5f97\u6811\u6797\u4e2d\u6709\u4eba\u5927 \u58f0\u5406\u559d\u3002\u9690\u9690\u8fd8\u542c\u5f97\u5175\u5668\u76f8\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5c06\u949f\u7075\u79c0\u85cf\u597d\uff0c\u6084\u6084\u8d70\u5165\u6811\u6797\u5077\u770b\u3002 \u3000\u3000\u53ea\u89c1\u6811\u6797\u91cc\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u90fd\u662f\u4ed6\u8ba4\u8bc6\u7684\u3002\u4e00\u4e2a\u662f\u9ed1\u77f3\u5e84\u7684\u5e84\u4e3b\u77f3\u96f7\uff0c\u4e00\u4e2a\u662f\u5e38\u5dde\u5927\u4fa0\u91d1 \u5200\u5218\u5929\u5316\uff0c\u4e00\u4e2a\u662f\u738b\u5b87\u5ead\u7684\u4e09\u5be8\u4e3b\u7126\u633a\uff0c\u6a80\u7fbd\u51b2\u4e0a\u897f\u6d1e\u5ead\u5c71\u62dc\u4f1a\u738b\u5b87\u5ead\u90a3\u5929\uff0c\u8fd9\u4e09\u4e2a\u4eba\u66fe\u7ecf \u8054\u624b\u4e0e\u4ed6\u4e3a\u96be\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u89c9\u5927\u4e3a\u5947\u602a\uff01 \u3000\u3000\u53ea\u89c1\u5218\u5929\u6b63\u5728\u6325\u821e\u4ed6\u90a3\u628a\u91cd\u8fbe\u4e09\u5341\u516d\u65a4\u7684\u91d1\u5200\uff0c\u8ffd\u65a9\u77f3\u7126\u8d77\u6765\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5927\u4e3a\u5947\u602a\uff0c\u4ed6\u4eec\u672c\u6765\u662f\u597d\u670b\u53cb\u7684\u5440\uff0c\u600e\u4e48\u7684\u81ea\u76f8\u6b8b\u6740\u8d77\u6765\u7684\u3002 \u3000\u3000\u7126\u633a\u53eb\u9053\uff1a\u201c\u5218\u5927\u4fa0\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f\u201d \u3000\u3000\u5218\u5929\u5316\u559d\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0c\u4f60\u53d8\u4e86\u7070\u6211\u4e5f\u8ba4\u5f97\u4f60\uff01\u4f60\u8fd9\u5c0f\u5996\u5973\uff0c\u5bb3\u5f97\u6211\u597d\u82e6\uff0c\u4eca\u65e5\u4e0d\u662f \u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d \u3000\u3000\u7126\u633a\u662f\u4e2a\u866c\u9aef\u5927\u6c49\uff0c\u7adf\u7136\u88ab\u53eb\u505a\u201c\u5c0f\u5996\u5973\u201d\uff0c\u5728\u65c1\u8fb9\u5077\u770b\u7684\u6a80\u7fbd\u51b2\u90fd\u5fcd\u4fca\u4e0d\u7981\uff0c\u7126\u633a\u672c \u4eba\u5f53\u7136\u66f4\u662f\u7ed9\u4ed6\u5f04\u5f97\u557c\u7b11\u7686\u975e\u4e86\u3002 \u3000\u3000\u4f46\u8c01\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002\u56de\u4e3a\u5218\u5929\u5316\u7684\u8bdd\u867d\u7136\u597d\u7b11\uff0c\u52a8\u4f5c\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u597d\u7b11\uff0c\u4ed6\u771f\u662f\u4e00\u5200\u5411\u7126 \u633a\u5288\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u7126\u633a\u7684\u72fc\u7259\u68d2\u4e5f\u662f\u91cd\u5175\u5668\uff0c\u4f46\u6c14\u529b\u4e0d\u53ca\u5218\u5929\u5316\uff0c\u5200\u68d2\u76f8\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u72fc\u7259\u68d2\u6b6a\u8fc7\u4e00 \u8fb9\uff0c\u9669\u4e9b\u8131\u624b\uff0c\u7126\u633a\u864e\u53e3\u5df2\u7ed9\u9707\u88c2\u3002 \u3000\u3000\u77f3\u96f7\u53eb\u9053\uff1a\u201c\u5218\u5927\u54e5\u3002\u4f60\u9192\u9192\uff01\u6211\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u548c\u5218\u5929\u5316\u662f\u7ed3\u4e49\u5144\u5f1f\uff0c\u6309\u8bf4\u5218\u5929\u5316\u5373\u4f7f\u600e\u6837\u795e\u667a\u4e0d\u6e05\uff0c\u4e5f\u8be5\u8ba4\u5f97\u4ed6\u7684\uff0c\u90a3\u77e5\u8fd8\u672a\u8bf4\u4e86 \u59d3\u540d\uff0c\u5218\u5929\u5316\u5df2\u5728\u559d\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u8fd9\u5c0f\u767d\u8138\uff0c\u5154\u5d3d\u5b50\uff0c\u6211\u6653\u5f97\u4f60\u662f\u5996\u72d0\u7684\u5e2e\u51f6\uff0c\u5982\u4eca\u5374 \u60f3\u6765\u54c4\u6211\u4e0a\u5f53\u4e48\uff0c\u6211\u4e00\u5200\u5288\u4e86\u4f60\uff01\u201d \u3000\u3000\u5f53\u4ed6\u53eb\u51fa\u201c\u6a80\u7fbd\u51b2\u201d\u59d3\u540d\u7684\u65f6\u5019\uff0c\u8eb2\u5728\u4e00\u65c1\u5077\u770b\u7684\u6a80\u7fbd\u51b2\u8fd8\u4ee5\u4e3a\u662f\u88ab\u53d1\u73b0\u4e86\u3002\u542c\u4e0b\u53bb\u77e5 \u9053\u4ed6\u662f\u628a\u77f3\u96f7\u5f53\u4f5c\u662f\u201c\u6a80\u7fbd\u51b2\u3002\u201d \u3000\u3000\u77f3\u96f7\u9762\u5982\u9505\u5e95\uff0c\u8eab\u9ad8\u516d\u5c3a\uff0c\u548c\u6a80\u7fbd\u51b2\u6ca1\u6709\u534a\u70b9\u76f8\u4f3c\u4e4b\u5904\uff0c\u7adf\u7136\u7ed9\u9a82\u4e3a\u201c\u5c0f\u767d\u7751\u201d\u201c\u5154\u5d3d \u5b50\u201d\uff0c\u4e0d\u7981\u6447\u5934\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u54e5\uff0c\u8bf7\u4f60\u4ed4\u7ec6\u770b\u6e05\u695a\u3002\u6211\u8fd9\u5f20\u8138\u662f\u7384\u575b\u8138\u4e0d\u662f\u5c0f\u767d \u8138\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u559d\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6539\u5bb9\u6613\u8c8c\uff0c\u7384\u575b\u7751\u4e5f\u597d\uff0c\u5c0f\u767d\u8138\u4e5f\u597d\uff0c\u603b\u4e4b\u4f60\u662f\u90a3\u6df7\u5e10\u5c0f\u5b50\u6a80 \u7fbd\u51b2\uff0c\u6709\u80c6\u7684\u522b\u8d70\uff0c\u5403\u6211\u4e00\u5200\uff01\u201d\u58f0\u51fa\u62db\u53d1\uff0c\u4e0d\u4ec5\u7b2c\u4e00\u5200\uff0c\u7b2c\u4e8c\u5200\uff0c\u7b2c\u4e09\u5200\u90fd\u5411\u77f3\u96f7\u65a9\u4e0b\u6765 \u4e86\uff0c\u4e00\u9762\u8ffd\u65a9\uff0c\u4e00\u9762\u5927\u9a82\u201c\u5996\u72d0\u201d\u4e0e\u201c\u5c0f\u767d\u8138\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6ca1\u548c\u4ed6\u4ea4\u624b\uff0c\u4f46\u4ed6\u8fd9\u6837\u660e\u9a82\u4e00\u901a\uff0c\u4e0d\u89c9\u4e5f\u662f\u557c\u7b11\u7686\u975e\u3002\u201c\u602a\u4e0d\u5f97\u5728\u5343\u67f3\u5e84\u6ca1\u6709\u770b \u89c1\u4ed6\u4eec\uff0c\u60f3\u5fc5\u4ed6\u4eec\u4ee5\u4e3a\u6211\u5df2\u7ecf\u8fc7\u4e86\u8fb9\u754c\uff0c\u6240\u4ee5\u8ffd\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d\u4f46\u5218\u5929\u5316\u600e\u7684\u4f1a\u53d1\u4e86\u75af \u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u731c\u5f97\u6ca1\u9519\uff0c\u8ffd\u5175\u662f\u5206\u6210\u51e0\u8def\u7684\uff0c\u8fd9\u4e09\u4e2a\u4eba\u6b66\u529f\u8f83\u9ad8\uff0c\u662f\u4ee5\u4ed6\u4eec\u81ea\u613f\u5192\u9669\u6df1\u5165\u91d1\u56fd \u8fd9\u65b9\u7684\u8fb9\u5883\u3001\u5c71\u533a\uff0c\u641c\u67e5\u6a80\u7fbd\u51b2\u7684\u8e2a\u8ff9\uff0c\u5374\u4e0d\u6599\u78b0\u4e0a\u8d6b\u8fde\u6e05\u6ce2\u3002\u800c\u8d6b\u8fde\u6e05\u6ce2\u4e5f\u6b63\u662f\u56e0\u4e3a\u78b0\u4e0a \u4ed6\u4eec\uff0c\u77e5\u9053\u6a80\u7fbd\u51b2\u8eab\u5904\u9669\u5883\uff0c\u8fd9\u624d\u7279\u5730\u8d76\u6765\u5343\u67f3\u5e84\u7684\u3002 \u3000\u3000\u4e8b\u60c5\u95f9\u5f97\u66f4\u52a0\u4e0d\u53ef\u6536\u62fe\u4e86\uff0c\u7126\u633a\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u65e9\u4e0d\u53d1\u4f5c\u8fdf\u4e0d\u53d1\u4f5c\uff0c\u504f\u504f\u5728\u8fd9\u4e2a\u65f6\u5019\u60a3\u8d77 \u5931\u5fc3\u75af\u6765\uff0c\u8fd9\u91cc\u5df2\u7ecf\u662f\u91d1\u56fd\u7684\u5730\u754c\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u77f3\u96f7\u907f\u5f00\u5218\u5929\u5316\u7684\u8fde\u73af\u4e09\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u60ca\u52a8\u4e86\u8fb9\u5173\u4e0a\u7684\u58eb\u5175\u53ef\u4e0d\u662f\u597d\u73a9\u7684\u3002\u53ea\u597d\u5c06 \u4ed6\u5236\u670d\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u77f3\u96f7\u6b63\u5f53\u76db\u5e74\uff0c\u8bba\u6b66\u529f\u4e5f\u4e0d\u5728\u5218\u5929\u5316\u4e4b\u4e0b\uff0c\u518d\u52a0\u4e0a\u7126\u633a\u6309\u8bf4\u662f\u8db3\u4ee5\u5236\u670d\u5218\u5929\u5316\u6709\u4f59\u7684\uff0c \u4f46\u5218\u5929\u5316\u53d1\u4e86\u72c2\uff0c\u529b\u5927\u5982\u725b\uff0c\u77f3\u7126\u4e8c\u4eba\u53c8\u6015\u5931\u624b\u4f24\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u53cd\u800c\u7ed9\u4ed6\u7684\u91d1\u5200\u4e71\u5288\u903c\u5f97\u624b \u811a\u4e71\uff0c\u72fc\u72c8\u975e\u5e38\u3002 \u3000\u3000\u7126\u633a\u53f9\u633a\u9053\uff1a\u201c\u4ed6\u5b9e\u5728\u75af\u5f97\u5386\u5bb3\uff0c\u54b1\u4eec\u53c8\u4e0d\u80fd\u4f24\u4ed6\uff0c\u8fd9\u6837\u95f9\u4e0b\u53bb\uff0c\u54b1\u4eec\u5373\u4f7f\u4e0d\u88ab\u4ed6\u6240 \u4f24\uff0c\u8fdf\u65e9\u4e5f\u4f1a\u7ed9\u91d1\u5175\u53d1\u73b0\u3002\u90a3\u65f6\u54b1\u4eec\u53ef\u5c31\u6ce5\u83e9\u8428\u8fc7\u6c5f\uff0c\u81ea\u8eab\u96be\u4fdd\u4e86\uff01\u201d \u3000\u3000\u77f3\u96f7\u9053\uff1a\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u4f46\u54b1\u4eec\u603b\u4e0d\u80fd\u629b\u5f00\u5218\u5927\u54e5\u4e0d\u7406\uff01\u201d\u4ed6\u7a81\u7136\u6293\u8d77\u4e00\u628a\u6ce5\u6c99\uff0c\u5411\u5218\u5929 \u5316\u6d12\u53bb\uff0c\u634f\u7740\u55d3\u5b50\uff0c\u626e\u5973\u58f0\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u7ed9\u6211\u4e56\u4e56\u6eda\u56de\u53bb\uff01\u4f60\u82e5\u662f\u518d\u50cf\u730e\u72d7\u4e00\u6837\u8ffd\u8e2a\u6a80 \u7fbd\u51b2\uff0c\u5f53\u5fc3\u6211\u53d6\u4f60\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u821e\u5200\u9632\u8eab\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u522b\u4eba\u6015\u4f60\u7684\u6bd2\u9999\uff0c\u6211\u4e0d\u6015\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u77f3 \u96f7\u8d81\u4ed6\u773c\u775b\u672a\u6562\u7741\u5f00\u4e4b\u9645\uff0c\u4e00\u638c\u51fb\u4e2d\u4ed6\u7684\u5c0f\u8179\uff0c\u5218\u5929\u5316\u5927\u559d\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u4f60\u6562\u6253\u6211\uff0c\u6211\u548c\u4f60 \u62fc\u4e86\uff01\u201d \u3000\u3000\u4f46\u4ed6\u7740\u4e86\u8fd9\u4e00\u638c\uff0c\u5374\u597d\u50cf\u6253\u6389\u9510\u6c14\u4f3c\u7684\uff0c\u9510\u6c14\u4e00\u6cc4\uff0c\u811a\u6b65\u8e22\u8dc4\uff0c\u767b\u65f6\u51fa\u73b0\u4e0d\u652f\u4e4b\u8c61\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u53ea\u89c1\u4ed6\u53e3\u5410\u767d\u6cab\uff0c\u91d1\u5200\u5288\u51fa\uff0c\u5200\u9053\u5927\u51cf\uff0c\u7126\u633a\u7684\u72fc\u7259\u68d2\u731b\u5730\u4e00\u78d5\uff0c\u628a\u4ed6\u7684\u91d1 \u5200\u6253\u843d\u3002\u77f3\u96f7\u6251\u4e0a\u524d\u53bb\uff0c\u5c06\u4ed6\u62b1\u4f4f\u3002 \u3000\u3000\u7126\u633a\u5378\u4e0b\u8170\u5e26\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u54e5\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u8981\u80cc\u4f60\u56de\u53bb\uff0c\u53ea\u597d\u8bf7\u4f60\u53d7\u70b9\u59d4\u5c48\u3002\u201d \u3000\u3000\u4ed6\u7528\u8170\u5e26\u6765\u7f1a\u5218\u5929\u5316\u7684\u53cc\u624b\uff0c\u5218\u5929\u5316\u672c\u6765\u662f\u597d\u50cf\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u8f6f\u8f6f\u7684\u9760\u7740\u77f3\u96f7\u7684\u6b64\u65f6 \u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u53cd\u800c\u4e00\u4e2a\u8098\u9524\u649e\u5411\u7126\u633a\uff0c\u77f3\u96f7\u521a\u521a\u677e\u624b\u8ba9\u7126\u633a\u7f1a\u3002\u6ca1\u6599\u4ed6\u7adf\u201c\u6b7b\u7070\u5f97\u71c3\u201d\uff0c \u8981\u6551\u7126\u633a\u5df2\u6765\u4e0d\u53ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5218\u5929\u5316\u649e\u7ffb\u7126\u633a\u3002\u7acb\u5373\u9a91\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u627c\u7740\u4ed6\u7684\u5589 \u5499\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5996\u5973\u5df1\u7ecf\u7ed9\u6211\u6349\u4f4f\u4e86\uff0c\u8c01\u6562\u8fc7\u6765\uff0c\u6211\u5c31\u627c\u6b7b\u8fd9\u5c0f\u5996\u5973\uff01\u201d \u3000\u3000\u77f3\u96f7\u5fd9\u9053\uff1a\u201c\u4ed6\u662f\u5e2e\u4f60\u7684\uff0c\u4f60\u82e5\u6740\u4e86\u4ed6\uff0c\u90a3\u5c0f\u5996\u5973\u8ffd\u5230\uff0c\u8c01\u4eba\u5e2e\u4f60\u62b5\u6321\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u4f3c\u4e4e\u7a0d\u5fae\u6e05\u9192\u4e86\u4e9b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6293\u4f4f\u7684\u4e0d\u662f\u5c0f\u5996\u5973\u5417\uff1f\u201d \u3000\u3000\u77f3\u96f7\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u3002\u5c0f\u5996\u5973\u662f\u6709\u957f\u5934\u53d1\u7684\uff0c\u4f60\u6478\u6478\u4ed6\u7684\u5934\u770b\uff0c\u4ed6\u53ef\u662f\u5149\u5934\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u7528\u4e0d\u4f38\u624b\u53bb\u6478\u5149\u5934\uff0c\u773c\u775b\u4e5f\u770b\u5f97\u89c1\u7684\u3002\u4f46\u4ed6\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u5c0f\u5996\u5973\u662f\u5996\u7cbe\uff0c\u5996\u7cbe\u4f1a \u4e03\u5341\u4e8c\u53d8\u3002\u201d \u3000\u3000\u77f3\u96f7\u9053\uff1a\u201c\u5218\u5927\u54e5\uff0c\u4f60\u603b\u8be5\u8bb0\u5f97\u592a\u6e56\u4e03\u5341\u4e8c\u5bb6\u5be8\u4e3b\u738b\u5b87\u5ead\u5427\uff1f\u4ed6\u662f\u4f60\u6700\u656c\u91cd\u91cf\u7684\u4eba \u5440\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u4e5f\u4e0d\u77e5\u662f\u5426\u8bb0\u5f97\uff0c\u4ed6\u7728\u7728\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u77f3\u96f7\u9053\uff1a\u201c\u4f60\u6293\u7684\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u662f\u738b\u5be8\u4e3b\u624b\u4e0b\u7684\u4e09\u5f53\u5bb6\u7126\u633a\u5440\uff01\u4f60\u4e0d\u4e70\u6211\u7684\u5e10\uff0c\u4e5f\u8be5\u4e70\u738b \u5be8\u4e3b\u7684\u5e10\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u559d\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u5728\u80e1\u8bf4\u4ec0\u4e48\uff0c\u5929\u738b\u8001\u5b50\u7684\u5e10\u6211\u4e5f\u4e0d\u4e70\uff01\u201d \u3000\u3000\u4ed6\u7684\u547c\u5438\u6c14\u606f\u8d8a\u6765\u8d8a\u91cd\uff0c\u8138\u90e8\u9752\u7b4b\u66b4\u8d77\uff0c\u795e\u60c5\u6781\u4e3a\u6050\u6016\u3002\u77f3\u96f7\u867d\u7136\u4e0d\u662f\u4f7f\u6bd2\u7684\u884c\u5bb6\uff0c\u4e5f \u77e5\u9053\u8fd9\u662f\u6bd2\u6027\u5c31\u8981\u5927\u53d1\u4f5c\u7684\u5148\u5146\u3002\u751f\u6015\u4ed6\u63a7\u5236\u4e0d\u4f4f\uff0c\u771f\u7684\u4e00\u4e0b\u5c31\u626d\u65ad\u7126\u633a\u7684\u8116\u5b50\u3002 \u3000\u3000\u5ffd\u5730\u9690\u9690\u542c\u5f97\u8fdc\u8fdc\u5904\u6709\u53f7\u89d2\u58f0\u4f20\u6765\uff0c\u8fb9\u5883\u7684\u91d1\u5175\u4f3c\u4e4e\u662f\u5df2\u5728\u51fa\u52a8\u4e86\u3002 \u3000\u3000\u7126\u633a\u8bf4\u9053\uff1a\u201c\u77f3\u5e84\u4e3b\uff0c\u91d1\u5175\u6050\u6015\u5c31\u8981\u6765\u4e86\u3002\u522b\u7406\u6211\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u77f3\u96f7\u6da9\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u4e09\u4e2a\u4eba\u4e00\u8d77\u51fa\u6765\uff0c\u53ea\u6211\u4e00\u4e2a\u4eba\u56de\u53bb\uff0c\u6d3b\u7740\u4e5f\u662f\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u7126\u633a\u9053\uff1a\u201c\u5218\u5927\u54e5\u4e2d\u4e86\u90a3\u5996\u5973\u7684\u6bd2\u9999\uff0c\u5df2\u662f\u8ff7\u4e86\u672c\u6027\uff0c\u800c\u4e14\u90a3\u6bd2\u9999\u8fd8\u4e0d\u662f\u666e\u901a\u7684\u8ff7\u9b42 \u9999\uff0c\u5373\u4f7f\u4ed6\u80fd\u591f\u6682\u65f6\u6e05\u9192\u8fc7\u6765\uff0c\u4f46\u5f97\u4e0d\u5230\u89e3\u836f\uff0c\u8fd8\u662f\u6d3b\u4e0d\u6210\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u542c\u5230\u6b64\u5904\uff0c\u5fc3\u4e2d\u767b\u65f6\u660e\u767d\uff1a\u201c\u539f\u6765\u5218\u5929\u5316\u662f\u4e2d\u4e86\u6e05\u6ce2\u7684\u9999\u96fe\u5f39\u4e4b\u6bd2\uff01\u201d \u3000\u3000\u800c\u9999\u96fe\u5f39\u6709\u4e24\u79cd\uff0c\u4e00\u79cd\u53ea\u6709\u8ff7\u9999\u6548\u80fd\uff0c\u4e00\u79cd\u662f\u52a0\u4e0a\u5176\u4ed6\u6bd2\u836f\u914d\u5236\uff0c\u836f\u529b\u4e5f\u7279\u5f3a\uff0c\u4e0d\u8fc7\u4e5f \u6709\u7f3a\u70b9\uff0c\u6bd2\u529b\u4e0d\u53ca\u8fdc\uff0c\u654c\u4eba\u82e5\u5728\u767e\u6b65\u4e4b\u5916\uff0c\u5c31\u53ef\u907f\u514d\u4e2d\u6bd2\u3002\u5218\u5929\u5316\u4e2d\u7684\u9999\u96fe\u4e4b\u6bd2\uff0c\u663e\u7136\u662f\u8fd9 \u4e00\u79cd\u3002\u5b83\u7684\u6bd2\u6027\uff0c\u7b2c\u4e00\u6b65\u80fd\u4f7f\u4eba\u53d8\u6210\u75af\u72c2\uff0c\u6b64\u65f6\u5018\u82e5\u5f97\u5230\u89e3\u836f\uff0c\u8fd8\u53ef\u4ee5\u4fdd\u5168\u6027\u547d\u3002\u5018\u82e5\u5f97\u4e0d \u5230\u89e3\u836f\u3002\u7b2c\u4e8c\u6b21\u53d1\u4f5c\uff0c\u90a3\u5c31\u662f\u5fc5\u6b7b\u65e0\u7591\u4e86\u3002 \u3000\u3000\u77f3\u96f7\u663e\u7136\u4ea6\u5df2\u65e0\u6cd5\u633d\u6551\u5218\u5929\u5316\u7684\u6027\u547d\u3002\u6ceb\u7136\u6b32\u6ce3\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u54e5\u4f60\u83ab\u602a\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c \u8fd9\u662f\u4e3a\u4e86\u4f60\u7684\u597d\uff0c\u4f60\u4e00\u4e16\u82f1\u96c4\uff0c\u4e0e\u5176\u547d\u4e27\u91d1\u5bc7\u4e4b\u624b\u3002\u4e0d\u5982\u6211\u6210\u5168\u4f60\u5427\uff01\u201d\u4e3e\u8d77\u624b\u638c\uff0c\u5c31\u60f3\u4e00 \u638c\u628a\u5218\u5929\u5316\u6253\u6b7b\u3002 \u3000\u3000\u8981\u77e5\u6b64\u65f6\u82e5\u4e0d\u662f\u628a\u5218\u5929\u5316\u6253\u6b7b\uff0c\u91d1\u5175\u4e00\u5230\uff0c\u8fde\u7126\u633a\u4e5f\u6d3b\u4e0d\u6210\uff0c\u4e0d\u662f\u7ed9\u91d1\u5175\u4e71\u7bad\u5c04\u6740\uff0c\u4e5f \u4f1a\u7ed9\u5218\u5929\u5316\u627c\u6b7b\u7684\uff0c\u65e2\u7136\u5218\u5929\u5316\u53cd\u6b63\u4e5f\u514d\u4e0d\u4e86\u4e00\u6b7b\uff0c\u90a3\u5c31\u4e0d\u5982\u6740\u4e00\u4e2a\u6551\u4e00\u4e2a\u5427\u3002\u8fd9\u662f\u77f3\u96f7\u7684 \u60f3\u6cd5\u3002\u5218\u5929\u6b64\u65f6\u6c14\u529b\u5df1\u8870\uff0c\u77f3\u96f7\u81ea\u4fe1\u5df2\u662f\u53ef\u4ee5\u53d6\u4ed6\u6027\u547d\u3002 \u3000\u3000\u77f3\u96f7\u54ac\u4e00\u54ac\u7259\uff0c\u72e0\u8d77\u5fc3\u80a0\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u6b63\u8981\u6251\u8fc7\u53bb\u4e00\u638c\u6253\u6b7b\u5218\u5929\u5316\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a \u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u77f3\u96f7\u5927\u5403\u4e00\u60ca\uff0c\u7741\u5f00\u773c\u775b\uff0c\u770b\u89c1\u4e00\u4e2a\u5c11\u91cc\u62ff\u7740\u4e00\u652f\u7389\u7bab\uff0c\u5df2\u4ece\u6811\u6797\u91cc\u8d70\u51fa\u6765\uff0c\u8fd9\u4e00\u60ca\u66f4 \u662f\u628a\u4ed6\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\uff0c\u53ef\u4e0d\u6b63\u662f\u4ed6\u4eec\u6240\u8981\u8ffd\u6740\u7684\u201c\u91d1\u56fd\u5978\u7ec6\u201d\u4e48\uff1f\u77f3\u96f7\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u4e00 \u7728\u773c\uff0c\u6a80\u7fbd\u51b2\u5df1\u662f\u4ece\u4ed6\u7684\u8eab\u8fb9\u8d70\u8fc7\u53bb\u4e86\uff0c\u6a80\u7fbd\u51b2\u559d\u9053\uff1a\u201d\u5218\u5929\u5316\uff0c\u4f60\u770b\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u5218\u5929\u5316\u867d\u795e\u667a\u672b\u6e05\uff0c\u4f46\u771f\u7684\u6a80\u7fbd\u51b2\u51fa\u73b0\uff0c\u4ed6\u6bd5\u7adf\u8fd8\u662f\u8ba4\u5f97\u7684\u3002\u4ed6\u559d\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0c \u54fc\uff0c\u4f60\u8fd9\u5c0f\u8d3c\uff0c\u6211\u6b63\u8981\u6740\u4f60\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u8fc7\u6765\u6740\u6211\u5427\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u7684\u6ce8\u610f\u529b\u88ab\u6a80\u7fbd\u51b2\u7684\u51fa\u73b0\u5438\u5f15\u8fc7\u6765\uff0c\u4ed6\u627c\u7740\u7126\u633a\u5589\u5499\u7684\u90a3\u53ea\u624b\u4e0d\u89c9\u5c31\u653e\u677e\u4e86\u4e00 \u4e9b\uff0c\u6a80\u7fbd\u51b2\u8d81\u8fd9\u4e2a\u65f6\u673a\uff0c\u4e00\u53e3\u7f61\u6c14\u4ece\u7389\u7bab\u4e2d\u5439\u8fc7\u53bb\uff0c\u5218\u5929\u5316\u6253\u4e86\u4e2a\u98a4\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e0e \u6b64\u540c\u65f6\uff0c\u7126\u633a\u5df2\u662f\u6323\u8131\u4ed6\u7684\u638c\u63e1\uff0c\u4ed6\u6b7b\u91cc\u6c42\u751f\uff0c\u7528\u7684\u6c14\u529b\u4e0d\u4f1a\u5c0f\u3002\u5218\u5929\u5316\u4e5f\u4e0d\u77e5\u662f\u7981\u53d7\u4e0d\u8d77 \u4ed6\u8fd9\u80a1\u731b\u529b\uff0c\u8fd8\u662f\u7981\u53d7\u4e0d\u8d77\u6a80\u7fbd\u51b2\u4ece\u6696\u7389\u7bab\u4e2d\u5439\u51fa\u7684\u90a3\u4e2d\u7f61\u6c14\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u5c31\u50cf\u4e00\u6839\u6728\u5934\u822c \u5012\u4e0b\u53bb\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u5c06\u4ed6\u6293\u4f4f\uff0c\u53ea\u89c1\u4ed6\u5df2\u7ecf\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u77f3\u96f7\u5446\u4e86\u4e00\u5446\uff0c\u559d\u9053\uff1a\u201c\u653e\u5f00\u6211\u7684\u5218\u5927\u54e5\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\u3002\u201d\u6162\u6761\u65af\u7406\u7684\u5750\u4e0b\u6765\uff0c\u5218\u5929\u5316\u7684\u5934\u6795\u7740\u4ed6\u7684\u5927\u817f\u3002 \u3000\u3000\u7126\u633a\u9003\u51fa\u751f\u5929\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u62fe\u8d77\u72fc\u7259\u68d2\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u4eec\u662f\u60f3\u8981\u4ed6\u6b7b\uff0c\u8fd8\u662f\u60f3\u8981\u4ed6\u6d3b\uff1f\u201d \u3000\u3000\u77f3\u96f7\u9762\u4e0a\u4e00\u7ea2\uff0c\u559d\u9053\uff1a\u201c\u6211\u4eec\u7eb5\u7136\u4e0d\u80fd\u5c06\u4ed6\u6551\u6d3b\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u6b7b\u5728\u4f60\u7684\u624b\u4e0a\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u60f3\u8981\u6740\u4ed6\uff0c\u65e9\u5c31\u53ef\u4ee5\u5c06\u4ed6\u6740\u4e86\u3002\u201d \u3000\u3000\u7126\u633a\u559d\u9053\uff1a\u201c\u8c01\u77e5\u9053\u4f60\u5b89\u7740\u4ec0\u4e48\u574f\u5fc3\u80a0\uff1f\u201d\u4e3e\u8d77\u72fc\u7259\u68d2\u51b2\u8fc7\u53bb\u5c31\u6253\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4ecd\u7136\u76d8\u819d\u800c\u5750\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u628a\u72fc\u7259\u68d2\u62c2\u8fc7\u4e00\u8fb9\u3002\u7126\u633a\u6c14\u529b\u53ea\u6062\u590d\u51e0\u5206\uff0c\u7981\u4e0d\u8d77\u8fd9 \u80a1\u7275\u5f15\u4e4b\u529b\uff0c\u9669\u4e9b\u53c8\u8981\u8dcc\u5012\u3002 \u3000\u3000\u7126\u633a\u53eb\u9053\uff1a\u201c\u77f3\u5e84\u4e3b\uff0c\u4f60\u2026\u2026\u201d\u5e95\u4e0b\u7684\u8bdd\u867d\u7136\u6ca1\u8bf4\u51fa\u6765\u3002\u77f3\u96f7\u4e5f\u542c\u5f97\u61c2\u662f\u8d23\u5907\u4ed6\u4e3a\u4f55\u4e0d \u6765\u5e2e\u624b\u4e4b\u610f\u3002 \u3000\u3000\u77f3\u96f7\u76f8\u8c8c\u76f8\u8c6a\uff0c\u4f46\u53ef\u6ca1\u6709\u7126\u633a\u8fd9\u4e48\u9c81\u83bd\uff0c\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u54b1\u4eec\u4e5f\u4e0d\u60f3\u6d3b\u7740\u56de\u53bb\u4e86\uff0c\u95ee\u6e05\u695a \u4ed6\u7684\u6765\u610f\u518d\u4f5c\u6253\u7b97\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u7126\u633a\u6012\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u91d1\u56fd\u5978\u7ec6\uff0c\u4ed6\u8fd8\u80fd\u5b89\u7740\u4ec0\u4e48\u597d\u5fc3\uff1f\u54b1\u4eec\u6253\u4e0d\u8fc7\u4ed6\u4e5f\u8981\u6253\uff01\u201d \u3000\u3000\u4ed6\u518d\u6b21\u51b2\u4e0a\u53bb\uff0c\u77f3\u96f7\u53ea\u597d\u98de\u638c\u76f8\u52a9\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u53f3\u624b\u6309\u7740\u5218\u5149\u5316\u7684\u80cc\u5fc3\uff0c\u53ea\u6709\u4e00\u53ea\u5de6\u624b\uff0c\u5750\u7740\u4e0d\u52a8\uff0c\u5c31\u5316\u89e3\u4e86\u4ed6\u4eec\u4e24\u4eba\u7684\u653b\u52bf\u3002 \u3000\u3000\u201c\u77f3\u5e84\u4e3b\u8bf4\u5f97\u4e0d\u9519\uff0c\u7126\u5be8\u4e3b\uff0c\u8bf7\u4f60\u4e5f\u5c11\u5b89\u6bcb\u8e81\u5427\u3002\u4f60\u4eec\u8981\u6253\u67b6\u3002\u5f85\u5f85\u6211\u628a\u5218\u8001\u524d\u8f88\u6551\u6d3b \u4e86\u4e5f\u8fd8\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u77f3\u96f7\u505c\u4e0b\u624b\u9053\uff1a\u201c\u4f60\u6709\u89e3\u836f\uff1f\u201d \u3000\u3000\u7126\u633a\u9053\uff1a\u201c\u77f3\u5e84\u4e3b\uff0c\u4f60\u600e\u53ef\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff01\u201d\u53ef\u662f\u77f3\u96f7\u5df2\u7ecf\u505c\u624b\uff0c\u4ed6\u521a\u521a\u6559\u8fc7\u6a80\u7fbd\u51b2\u7684\u5389 \u5bb3\uff0c\u864e\u53e3\u4ea6\u5df1\u9178\u9ebb\uff0c\u60f3\u6253\u4e5f\u4e0d\u6562\u8fc7\u6765\uff0c\u53ea\u597d\u7ad9\u5728\u77f3\u96f7\u8eab\u65c1\uff0c\u5bf9\u6a80\u7fbd\u51b2\u6012\u76ee\u800c\u89c6\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\u201c\u6211\u867d\u7136\u6ca1\u6709\u9999\u96fe\u5f39\u7684\u89e3\u836f\uff0c\u4f46\u6211\u8fd9\u78a7\u7075\u4e39\u6599\u60f3\u5b59\u53ef\u4fdd\u5168\u4ed6\u7684\u6027\u547d\u3002\u201d \u5f53\u4e0b\u628a\u5218\u5929\u5316\u7684\u4e0b\u5df4\u4e00\u634f\uff0c\u5218\u5929\u5316\u5634\u5df4\u5f20\u5f00\uff0c\u4ed6\u4fbf\u5373\u628a\u4e00\u9897\u78a7\u7eff\u8272\u7684\u836f\u4e38\u585e\u5165\u5218\u5929\u5316\u53e3\u4e2d\u3002 \u3000\u3000\u7126\u633a\u7741\u7740\u773c\u775b\uff0c\u601d\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4f3c\u4e4e\u7740\u900f\u4ed6\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u89e3\u836f\u8fd8\u662f\u6bd2\u836f\uff0c\u5f85\u4f1a\u513f\u4f60\u5c31\u77e5\u9053\uff0c\u6b64\u523b\u4e0d\u5fc5\u80e1 \u731c\uff01\u201d \u3000\u3000\u78a7\u7075\u4e39\u662f\u7528\u5929\u5c71\u96ea\u83b2\u6ce1\u5236\u7684\uff0c\u80fd\u795b\u767e\u6bd2\uff0c\u90a3\u65e5\u4faf\u6606\u4e2d\u4e86\u9999\u96fe\u4e4b\u6bd2\uff0c\u5c31\u662f\u5f97\u5230\u6a80\u7fbd\u51b2\u8d60\u4e39 \u89e3\u6551\u7684\uff0c\u4e0d\u8fc7\u5218\u5929\u5316\u5982\u4eca\u6240\u4e2d\u7684\u6bd2\uff0c\u8981\u6bd4\u5019\u6606\u90a3\u65e5\u4e2d\u7684\u6bd2\u6df1\u5f97\u591a\uff0c\u5374\u662f\u5fc5\u987b\u6a80\u7fbd\u51b2\u591a\u8017\u4e00\u4e9b \u529f\u529b\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u638c\u8d34\u7740\u5218\u5929\u5316\u80cc\u5fc3\uff0c\u5c06\u672c\u8eab\u771f\u6c14\u8f93\u5165\u4ed6\u7684\u4f53\u5185\uff0c\u4e00\u6765\u66ff\u4ed6\u63a8\u8840\u8fc7\u5b98\uff0c\u4e8c\u6765\u52a0\u901f\u836f \u529b\u8fd0\u884c\uff0c\u7126\u633a\u770b\u89c1\u5218\u5929\u5316\u5934\u9876\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u77e5\u9053\u8fd9\u662f\u6bd2\u8d28\u968f\u7740\u6c57\u6c34\u6325\u53d1\u7684\u89c6\u8c61\uff0c\u65b9\u59cb \u653e\u4e0b\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u3002 \u3000\u3000\u8fc7\u4e86\u7ea6\u83ab\u4e00\u679d\u9999\u7684\u65f6\u523b\uff0c\u6a80\u7fbd\u51b2\u628a\u5218\u5929\u5316\u653e\u5728\u5730\u4e0a\uff0c\u80cc\u8f6c\u8eab\u5b50\u3002 \u3000\u3000\u5218\u5929\u5316\u597d\u50cf\u4ece\u68a6\u4e2d\u9192\u6765\u3002\u4e00\u8dc3\u800c\u8d77\uff0c\u832b\u7136\u95ee\u9053\uff1a\u201c\u77f3\u5144\uff0c\u7126\u5144\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u77f3\u7126\u4e8c\u4eba\u5927\u559c\u9053\uff1a\u201c\u5218\u5927\u54e5\uff0c\u4f60\u679c\u7136\u597d\u4e86\uff01\u201d\u77f3\u96f7\u60f3\u8d77\u521a\u624d\u81ea\u5df1\u51e0\u4e4e\u6740\u4e86\u5218\u5929\u5316\u7684\u4e8b\uff0c \u5fc3\u4e2d\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u5bf9\u5218\u5929\u5316\u60ed\u6127\uff0c\u5bf9\u6a80\u7fbd\u51b2\u611f\u6fc0\u3002 \u3000\u3000\u7126\u633a\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5218\u5927\u54e5\uff0c\u4f60\u4e2d\u4e86\u90a3\u5996\u72d0\u4e4b\u6bd2\uff0c\u662f\u3001\u662f\u8fd9\u3001\u8fd9\u4eba\u66ff\u4f60\u89e3\u7684\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u56de\u8fc7\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u4fa0\uff0c\u54b1\u4eec\u662f\u5728\u897f\u6d1e\u5ead\u5c71\u89c1\u8fc7\u9762\u7684\uff0c\u4f60\u8fd8\u8bb0\u5f97\u6211\u5417\uff1f\u201d \u3000\u3000\u5218\u5929\u5316\u77aa\u7740\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u6551\u6211\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u4e3a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4eec\u662f\u6765\u8ffd\u6355\u4f60\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u65e9\u5df2\u77e5\u9053\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u559d\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u8981\u6551\u6211\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4eba\u547d\u5173\u5929\uff0c\u5373\u4f7f\u662f\u4e0d\u76f8\u8bc6\u7684\u8def\u4eba\uff0c\u5018\u82e5\u6211\u6709\u529e\u6cd5\u6551\u4ed6\uff0c\u6211\u4e5f\u4e0d\u80fd\u5750\u89c6\uff0c\u4f55 \u51b5\u4f60\u7684\u4e2d\u6bd2\u662f\u56e0\u6211\u800c\u8d77\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u5446\u4e86\u7247\u8be5\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u76f8\u4fe1\u4f60\u6709\u8fd9\u7b49\u4ec1\u4e49\u5fc3\u80a0\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6124\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4f60\u4eec\u773c\u4e2d\u3002\u6211\u662f\u5973\u771f\u9791\u5b50\uff0c\u600e\u80fd\u548c\u4f60\u4eec\u6c49\u4eba\u7684\u4fa0\u4e49\u9053\u76f8 \u6bd4\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u666e\u901a\u7684\u91d1\u4eba\uff0c\u4f60\u662f\u91d1\u56fd\u6d3e\u6765\u7684\u5978\u7ec6\uff0c\u4f60\u83ab\u4ee5\u4e3a\u6551\u4e86\u6211\u7684\u6027 \u547d\uff0c\u6211\u4eec\u662f\u611f\u6069\u56fe\u62a5\uff0c\u4e0d\u518d\u628a\u4f60\u5f53\u4f5c\u654c\u4eba\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u6ca1\u6709\u60f3\u5230\u8981\u4f60\u7684\u62a5\u7b54\uff0c\u4f60\u4ecd\u7136\u53ef\u4ee5\u628a\u6211\u5f53\u4f5c\u654c\u4eba\u3002\u201d \u3000\u3000\u77f3\u96f7\u529d\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u522b\u2026\u2026\u201d\u5218\u5929\u5316\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u56e0\u79c1\u4eba\u7684\u6069\u60e0\u5c31\u5fd8\u4e86\u516c\u4e49\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65bd\u6069\uff0c\u4e0d\u8fc7\u4f60\u6bd2\u4f24\u521d\u6108\uff0c\u4eca\u5929\u4f60\u4eec\u662f\u4e0d\u5b9c\u548c\u6211\u4ea4\u624b\u7684\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u9762\u8272\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u53cd\u800c\u4ed6\u5fc3\u60c5\u6709\u6df7\u4e71\uff0c\u4ed6\u76ef\u7740\u6a80\u7fbd\u51b2\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u540e\u6094\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u540e\u6094\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u653e\u4e86\u6211\u3002\u4ed6\u65e5\u6211\u82e5\u9047\u4e0a\u4e86\u4f60\uff0c\u8fd8\u662f\u8981\u548c\u4f60\u62fc\u547d\u7684\uff01\u4f60\u82e5\u4e0d\u4ee5\u4e86\u90a3\u662f \u518d\u6765\u9a82\u6211\u5fd8\u6069\u8d1f\u4e49\uff0c\u4e0d\u5982\u4eca\u65e5\u628a\u6211\u6740\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u65e9\u5df2\u77e5\u9053\u4f60\u4f1a\u8fd9\u6837\u505a\uff0c\u53c8\u4f55\u540e\u6094\u98df\u8a00\uff1f\u518d\u8bf4\uff0c\u4f60\u662f\u4e3a\u4e86\u56fd\u4ec7\u5927\u4e49\u3002\u90a3\u4e5f \u4e0d\u7b97\u5fd8\u6069\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7adf\u7136\u628a\u4ed6\u7684\u5fc3\u601d\u66ff\u4ed6\u8bf4\u4e86\u51fa\u6765\uff0c\u5218\u5929\u5316\u770b\u7740\u4ed6\uff0c\u597d\u50cf\u770b\u7740\u4e00\u4e2a\u201c\u602a\u7269\u201d\u4f3c\u7684\uff0c\u534a \u664c\uff0c\u6447\u4e86\u6447\u5934\u3002\u8bf4\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u5728\u6c49\u4eba\u4e2d\u4e5f\u662f\u5c11\u89c1\u3002\u597d\u3002\u90a3\u6211\u5c31\u628a\u8bdd\u5148\u8bf4\u5728\u524d\u5934\u3002 \u4ed6\u65e5\u4f60\u82e5\u78b0\u4e0a\u4e86\u6211\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u624b\u4e0b\u7559\u60c5\uff0c\u4f60\u6740\u4e86\u6211\uff0c\u6211\u6b7b\u800c\u65e0\u6028\uff0c\u4f46\u5018\u82e5\u662f\u6211\u6740\u4e86\u4f60\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u53ea\u597d\u8ba4\u547d\uff01\u201d \u3000\u3000\u77f3\u96f7\u5583\u5583\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u5978\u7ec6\u8fd8\u662f\u4fa0\u58eb\uff0c\u771f\u662f\u8ba9\u4eba\u731c\u4e0d\u900f\u3002\u201d \u3000\u3000\u5218\u5929\u5316\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4f60\u8ba4\u547d\uff0c\u6211\u4e5f\u8ba4\u547d\u4e86\uff0c\u4ed6\u65e5\u5c1a\u82e5\u662f\u6211\u6740\u4e86\u4f60\uff0c\u6211\u5fc5\u5f53\u81ea\u520e\u4ee5\u62a5\u3002\u201d \u3000\u3000\u7126\u633a\u6ee1\u8154\u773c\u6cea\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4f46\u4ed6\u671b\u5411\u6a80\u7fbd\u51b2\u7684\u773c\u775b\u5374\u662f\u5145\u6ee1\u611f\u6fc0\u4e4b\u60c5\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u90fd\u8d70\u4e86\uff0c\u6a80\u7fbd\u51b2\u5374\u662f\u6d51\u8eab\u4e4f\u529b\uff0c\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u3002\u8fd9\u6b21\u4e3a\u4e86\u6551\u6d3b\u5218\u5929\u5316\uff0c\u4ed6\u8feb\u5f97\u9006\u8fd0 \u771f\u6c14\uff0c\u628a\u4f53\u529b\u7684\u6f5c\u529b\u90fd\u201c\u538b\u6324\u201d\u51fa\u6765\uff0c\u5982\u4eca\u5df2\u662f\u5230\u6cb9\u5c3d\u706f\u67af\u7684\u5730\u6b65\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u6bd5\u7adf\u8fd8\u662f\u7ad9\u8d77\u6765\u4e86\u3002 \u3000\u3000\u201c\u6211\u4e0d\u80fd\u5012\u4e0b\u53bb\uff01\u201d\u6a80\u7fbd\u51b2\u5728\u5fc3\u91cc\u81ea\u5df1\u5bf9\u81ea\u5df1\u8bf4\u9053\uff1a\u201c\u6211\u5012\u4e0b\u53bb\u4e0d\u6253\u7d27\uff0c\u79c0\u59b9\u53ef\u6ca1\u4eba\u7167 \u987e\u4e86\u3002\u201d \u3000\u3000\u4ed6\u62b1\u8d77\u949f\u7075\u79c0\u7ee7\u7eed\u524d\u884c\uff0c\u5979\u90a3\u7626\u5c0f\u7684\u8eab\u8eaf\uff0c\u9876\u591a\u4e5f\u4e0d\u8fc7\u662f\u4e03\u516b\u5341\u65a4\u5427\uff0c\u6b64\u65f6\u7adf\u597d\u53d8\u6210\u4e86 \u5343\u65a4\u5de8\u77f3\uff0c\u8fd9\u201c\u6c89\u91cd\u201d\u7684\u8d1f\u8377\uff0c\u4ee4\u5f97\u6a80\u7fbd\u51b2\u4e3e\u6b65\u8270\u96be\uff0c\u5ffd\u5730\u53cc\u817f\u4e00\u8f6f\uff0c\u4ed6\u4e0d\u7531\u81ea\u4e3b\u7684\u5c41\u80a1\u7740 \u5730\uff0c\u8fd9\u8fd8\u662f\u4ed6\u6050\u9632\u6454\u574f\u4e86\u949f\u7075\u79c0\uff0c\u7aed\u529b\u652f\u6491\uff0c\u8fd9\u624d\u80fd\u591f\u7ef4\u6301\u201c\u5750\u201d\u7684\u59ff\u6001\uff0c\u4e0d\u81f3\u4e8e\u53d8\u4f5c\u6eda\u5730 \u846b\u82a6\u7684\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u5426\u56e0\u4e3a\u9707\u8361\uff0c\u8fd8\u662f\u56e0\u4e3a\u836f\u5df2\u7ecf\u89c1\u6548\u7684\u7f18\u6545\uff0c\u949f\u7075\u79c0\u201c\u5624\u201d\u7684\u4e00\u58f0\uff0c\u9192\u8fc7\u6765\u7684\u3002 \u3000\u3000\u5979\u597d\u50cf\u662f\u4ece\u6076\u68a6\u4e2d\u9192\u8fc7\u6765\uff0c\u5f20\u5f00\u773c\u775b\uff1b\u4e00\u6d3e\u832b\u7136\u7684\u671b\u7740\u6a80\u7fbd\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u6211\u662f \u5728\u505a\u68a6\u5427\uff1f\u6211\u8fd9\u4e2a\u68a6\u597d\u53ef\u6015\u5440\uff01\u90a3\u4e48\u591a\u7684\u6b7b\u4eba\uff0c\u90a3\u4e48\u591a\u7684\u8840\uff01\u54a6\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u4e5f\u662f\u6ee1\u8eab \u8840\u6c61\uff1f\u7237\u7237\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8179\u5982\u5200\u7ede\u3002\u5fcd\u7740\u60b2\u75db\u8bf4\u9053\uff1a\u201c\u79c0\u59b9\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u8fd9\u4e0d\u662f\u68a6\uff0c\u8fd9\u662f\u4e8b\u5b9e\uff0c\u7237\u7237\u6b7b \u4e86\uff0c\u4f60\u8981\u54ed\u5c31\u54ed\u5427\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5446\u4f4f\u4e86\uff0c\u4f46\u5979\u4ea6\u5df2\u4ece\u201c\u68a6\u201d\u4e2d\u9192\u8fc7\u6765\u4e86\uff0c\u5728\u5343\u67f3\u5e84\u63a5\u4e8c\u8fde\u4e09\u53d1\u751f\u7684\u90a3\u4e9b\u60e8\u5267\uff0c\u4e00 \u5e55\u5e55\u60ca\u5fc3\u52a8\u9b44\u7684\u573a\u666f\uff0c\u4e00\u4e0b\u5b50\u5168\u90fd\u6d8c\u73b0\u5979\u7684\u8111\u6d77\u4e86\u3002 \u3000\u3000\u5979\u5446\u5446\u7684\u770b\u7740\u6a80\u7fbd\u51b2\uff0c\u54ed\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u79c0\u59b9\uff0c\u6211\u6bd4\u4f60\u66f4\u5c0f\u7684\u5e74\u7eaa\uff0c\u5c31\u5931\u6389\u4e86\u6240\u6709\u7684\u4eb2\u4eba\u7684\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u7684\u96be\u8fc7 \u4e86\uff0c\u5509\uff0c\u8fd9\u90fd\u602a\u6211\u4e0d\u597d\uff0c\u662f\u6211\u8fde\u7d2f\u4e86\u4f60\uff01\u201d\u949f\u7075\u79c0\u5ffd\u5730\u6251\u5728\u6a80\u7fbd\u51b2\u8eab\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u5927\u54e5 \u54e5\uff0c\u522b\u8fd9\u6837\u8bf4\uff01\u662f\u6211\u4e0d\u597d\uff01\u662f\u6211\u8fde\u7d2f\u4e86\u4f60\uff01\u201d\u5979\u7ec8\u4e8e\u54ed\u51fa\u6765\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8f7b\u8f7b\u5730\u629a\u6478\u5979\u9053\uff1a\u201c\u79c0\u59b9\uff0c\u4f60\u75db\u75db\u5feb\u5feb\u54ed\u4e00\u573a\u5427\uff0c\u4f46\u6211\u8981\u4f60\u575a\u5f3a\u7684\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u54ed\u7740\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4e0d\u4f1a\u62c5\u5fc3\u6211\uff0c\u4f60\u7684\u906d\u9047\u6bd4\u6211\u60e8\uff0c\u4f46\u4f60\u4e5f\u5014\u5f3a\u7684\u6d3b\u4e0b\u6765 \u4e86\uff0c\u6211\u4f1a\u62ff\u4f60\u5f53\u4f5c\u508d\u6837\u7684\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u7684\u4f24\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u50cf\u54c4\u5b69\u5b50\u4e00\u6837\u5bf9\u5979\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4f24\u4e0d\u6253\u7d27\uff0c\u79c0\u59b9\uff0c\u4f60\u6536\u4e86\u773c\u6cea\uff0c\u8bd5\u4e00\u8bd5\u80fd\u4e0d\u80fd\u591f \u8d70\u8def\uff0c\u4f46\u6211\u53ea\u6015\u4e0d\u80fd\u966a\u4f60\u56de\u4e34\u5b89\u53bb\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u8981\u6211\u56de\u4e34\u5b89\u505a\u4ec0\u4e48\uff1f\u6211\u548c\u4f60\u4e00\u6837\uff0c\u4e5f\u662f\u5df2\u7ecf\u6ca1\u6709\u5bb6\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f46\u4e34\u5b89\u8fd8\u6709\u6587\u5927\u4fa0\uff0c\u8fd8\u6709\u4e10\u5e2e\u7684\u5d14\u6d69\uff0c\u4ed6\u4eec\u90fd\u662f\u4f60\u7237\u7237\u7684\u670b\u53cb\uff0c\u5bf9\u5566\uff0c\u6211 \u8fd8\u8bb0\u5f97\uff0c\u4f60\u4e0d\u662f\u4f60\u53eb\u4ed6\u4eec\u505a\u53d4\u53d4\u7684\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u518d\u53eb\u4ed6\u4eec\u53d4\u53d4\uff0c\u4ed6\u4eec\u90fd\u662f\u8981\u5bb3\u4f60\u7684\u4eba\uff0c\u90a3\u4e2a\u6587\u5927\u4fa0\u773c\u770b\u6211\u7684\u7237\u7237 \u60e8\u6b7b\uff0c\u4ed6\u8fd8\u8981\u8ddf\u7237\u7237\u7684\u4ec7\u4eba\u8054\u624b\u6765\u6740\u4f60\uff0c\u4ed6\u4eec\u4e5f\u90fd\u4e0d\u662f\u6211\u7237\u7237\u7684\u670b\u53cb\u4e86\uff01\u5509\uff01\u5927\u54e5\u54e5\uff0c\u54b1\u4eec \u90fd\u662f\u522b\u65e0\u4eb2\u4eba\u7684\uff0c\u4f60\u600e\u4e48\u5fcd\u8fd8\u53eb\u6211\u8ddf\u4f60\u5206\u5f00\uff1f\u201d \u3000\u3000----------- \u3000\u3000\u7fbd\u751f\u5802 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u53d8\u751f\u5e7d\u8c37 \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u516d\u56de\u3000\u53d8\u751f\u5e7d\u8c37 \u3000\u3000\u6a80\u7fbd\u51b2\u542c\u5979\u8bf4\u7684\u771f\u631a\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60b2\u4f24\uff0c\u4ed6\u4e0d\u5fcd\u8bf4\u81ea\u5df1\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u53ea \u6015\u4e0d\u6d3b\u4e45\u957f\u7684\u4e8b\u544a\u8bc9\u949f\u7075\u79c0\uff0c\u5f53\u4e0b\u5fcd\u7740\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u65e2\u7136\u613f\u610f\u8ddf\u6211\u3002\u90a3\u5c31\u8d70\u5427\uff01\u8d70 \u5230\u90a3\u513f\u7b97\u597d\u513f\uff01\u201d \u3000\u3000\u4ed6\u60f3\u8d77\u5a18\u4eb2\u7684\u5fc3\u613f\u81ea\u5df1\u5df2\u65e0\u6cd5\u66ff\u5979\u5b8c\u6210\uff0c\u81ea\u5df1\u60f3\u8981\u7ed3\u4ea4\u7684\u6c5f\u5357\u4fa0\u4e49\u9053\u90fd\u5df2\u662f\u201c\u4ec7\u4eba\u201d \u4e86\uff0c\u6b63\u5982\u949f\u7075\u79c0\u8bf4\u7684\u90a3\u6837\uff0c\u5982\u4eca\u4ed6\u53ea\u6709\u4e00\u4e2a\u5c0f\u59d1\u5a18\u613f\u610f\u966a\u4ed6\u4e86\u3002\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u60b2\u4ece\u8877\u6765\uff0c \u96be\u4ee5\u65ad\u7edd\uff0c\u653e\u58f0\u6b4c\u9053\uff1b \u3000\u3000\u201c\u524d\u4e0d\u89c1\u53e4\u4eba\u3002\u540e\u4e0d\u89c1\u6765\u8005\u3002 \u3000\u3000\u5ff5\u5927\u5730\u4e4b\u60a0\u60a0\uff0c\u72ec\u6006\u7136\u800c\u6d95\u4e0b\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u53e3\u9c9c\u8840\u5410\u4e86\u73b0\u672a\uff0c\u6a80\u7fbd\u51b2\u5df1\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u62b1\u7740\u6a80\u7fbd\u51b2\u7684\u8eab\u5b50\u6447\u4e86\u6447\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u522b\u5413\u6211\uff0c\u4f60\u9192 \u9192\u4f60\u9192\u9192\u5440\uff01\u201d \u3000\u3000\u6a80\u7fbd\u4ef2\u6ca1\u6709\u7ed9\u5979\u6447\u9192\uff0c\u4ed6\u7684\u773c\u775b\u4e5f\u95ed\u4e0a\u4e86\uff0c\u4e0d\u8fc7\u5fc3\u810f\u8fd8\u6ca1\u6709\u505c\u6b62\u8df3\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u867d\u7136\u5c1a\u672a\u6c14\u7edd\uff0c\u949f\u7075\u79c0\u5374\u5df2\u662f\u675f\u624b\u65e0\u7b56\u4e86\uff0c\u5979\u4e0d\u8fc7\u662f\u4e2a\u5341\u516d\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u672c\u6765\u8fd8\u662f \u9700\u8981\u522b\u4eba\u7167\u987e\u7684\uff0c\u6709\u4ec0\u4e48\u529e\u6cd5\u6551\u6d3b\u6a80\u7fbd\u51b2\u5462\uff1f\u96be\u9053\u773c\u7741\u7741\u7684\u5c31\u770b\u7740\u4ed6\u6b7b\u4ea1\uff01 \u3000\u3000\u5979\u62b1\u7740\u6a80\u7fbd\u51b2\u54ed\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u53ef\u4e0d\u80fd\u629b\u4e0b\u6211\uff0c\u4f60\u6b7b\u4e86\uff0c\u6211\u4e5f\u6d3b\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u5ffd\u5730\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u98de\u5feb\u8dd1\u6765\uff0c\u8f6c\u77ac\u5230\u4e86\u5979\u7684\u524d\u9762\u3002 \u3000\u3000\u6765\u7684\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u7389\u9762\u5996\u72d0\u8d6b\u8fde\u6e05\u6ce2\u3002 \u3000\u3000\u539f\u6765\u5979\u65e9\u5df2\u770b\u51fa\u6a80\u7fbd\u51b2\u53d7\u4e86\u91cd\u4f24\uff0c\u6b63\u56e0\u4e3a\u5979\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8fd9\u624d\u53c8\u53bb\u800c\u590d\u56de\u3002 \u3000\u3000\u201c\u4f60\u7684\u5927\u54e5\u8fd8\u6ca1\u6b7b\uff0c\u4f60\u8d70\u5f00\uff0c\u8ba9\u6211\u77a7\u77a7\u4ed6\u4f24\u5f97\u600e\u6837\uff1f\u8d6b\u8fde\u6e05\u6ce2\u8bf4\u9053\u3002 \u3000\u3000\u949f\u7075\u79c0\u62d4\u51fa\u77ed\u5251\uff0c\u62e6\u5728\u6a80\u7fbd\u51b2\u524d\u9762\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u62a2\u8d70\u6211\u7684\u5927\u54e5\u54e5\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u5bf9\u4f60\u7684\u5927\u54e5\u54e5\u5012\u662f\u5fe0\u5fc3\u5f97\u5f88\u5440\uff01\u4e0d\u8fc7\uff0c\u6211\u4e0d\u662f\u6765\u5bb3\u4f60\u7684 \u5927\u54e5\u54e5\uff0c\u6211\u662f\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0c\u4f60\u662f\u7389\u9762\u72d0\u72f8\uff0c\u4f60\u8bf4\u4ec0\u4e48\u6211\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u4f60\u5bb3\u6211\u5927\u54e5\u54e5\u5bb3\u5f97\u8fd8 \u4e0d\u591f\u60e8\u5417\uff1f\u4e8f\u4f60\u8fd8\u6709\u8138\u76ae\u8bf4\u662f\u4ed6\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9eef\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u4ed6\u7684\u786e\u662f\u5df2\u7ecf\u548c\u6211\u7edd\u4ea4\uff0c\u4e0d\u518d\u628a\u6211\u5f53\u4f5c\u670b\u53cb\u4e86\u3002\u6211\u4e0d \u602a\u4f60\u9a82\u6211\uff0c\u4f46\u4f60\u4fdd\u5f97\u4f4f\u4f60\u5927\u54e5\u54e5\u6027\u547d\u5417\uff1f\u201d\u8d6b\u8fde\u6e05\u6ce2\u51b7\u51b7\u7684\u95ee\u949f\u7075\u79c0\u3002 \u3000\u3000\u949f\u7075\u79c0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53cc\u773c\u671b\u7740\u5979\u9053\uff1a\u201c\u4f60\u80fd\u591f\u6551\u6d3b\u4ed6\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u6ca1\u6709\u628a\u63e1\uff0c\u4e0d\u8fc7\uff0c\u6700\u5c11\u6211\u8981\u6bd4\u4f60\u591a\u4e00\u70b9\u628a\u63e1\u3002\u5c0f\u59d1\u5a18\uff0c\u4f60\u5df1\u7ecf\u4e3a\u4f60\u7684 \u5927\u54e5\u54e5\u5c3d\u4e86\u5fc3\u529b\u4e86\uff0c\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u63e1\u7d27\u624b\u4e2d\u77ed\u5251\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6eda\u5f00\uff0c\u6211\u624d\u4e0d\u76f8\u4fe1\u4f60\u7684\u82b1\u8a00\u5de7\u8bed\u5462\uff0c\u4f60\u4e0d\u8fc7\u662f\u60f3 \u62a2\u8d70\u6211\u7684\u5927\u54e5\u54e5\u7f62\u4e86\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u5b81\u613f\u548c\u6211\u7684\u5927\u54e5\u4e00\u8d77\u6b7b\u6389\uff0c\u4e5f\u4e0d\u613f\u610f\u4ed6\u4e0d\u6b7b\u4e0d\u6d3b\u7684\u843d\u5728 \u4ed6\u7684\u4ec7\u4eba\u7684\u624b\u91cc\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u89c1\u5979\u90a3\u526f\u575a\u51b3\u7684\u795e\u6c14\uff0c\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u504f\u4e0d\u6eda\u5f00\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u4f60\u4fdd\u62a4\u5f97 \u4e86\u4f60\u7684\u5927\u54e5\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u77e5\u9053\u6253\u4e0d\u8fc7\u4f60\uff0c\u4f46\u6709\u6211\u6709\u4ed6\u8eab\u8fb9\uff0c\u4f60\u53ef\u4f11\u60f3\u78b0\u4ed6\u4e00\u4e0b\uff0c\u9664\u975e\u4f60\u5148\u628a\u6211\u6740 \u6389\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u6211\u4e5f\u4e0d\u8981\u62a2\u8d70\u4f60\u7684\u5927\u54e5\u54e5\uff0c\u6211\u4f46\u4e0d\u8981\u4f60\u7684\u4e1c\u897f\uff0c\u6211\u8fd8\u6709\u4e1c\u897f \u8981\u9001\u7ed9\u4f60\u5462\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u559d\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7684\u4e1c\u897f\uff0c\u4f60\u7ed9\u6211\u2026\u2026\u201d\u4e00\u4e2a\u201c\u6eda\u201d\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u8d6b\u8fde\u6e05\u6ce2\u5df2\u662f\u4e0a \u6765\u593a\u5979\u7684\u5251\u4e86\u3002\u949f\u7075\u79c0\u201c\u5530\u201d\u7684\u4e00\u5251\u523a\u51fa\uff0c\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u7684\u5251\u6cd5\u5012\u662f\u4e0d\u5dee\uff0c\u4e0d\u8fc7\uff0c \u53ea\u51ed\u4f60\u8fd9\u70b9\u672c\u9886\uff0c\u53ef\u8fd8\u4fdd\u62a4\u4e0d\u4e86\u4f60\u7684\u5927\u54e5\u54e5\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e00\u4e2a\u7a7a\u5200\u8fdb\u638c\uff0c \u4f7f\u51fa\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\u5c31\u593a\u4e86\u949f\u7075\u79c0\u7684\u77ed\u5251\uff0c\u968f\u5373\u70b9\u4e86\u5979\u7684\u7a74\u9053\u3002\u8d6b\u8fde\u6e05\u6ce2\u6254\u4e0b\u77ed\u5251\uff0c\u8d70\u8fc7 \u53bb\u5750\u5728\u6a80\u7fbd\u51b2\u8eab\u8fb9\uff0c\u628a\u8eba\u5728\u5730\u4e0a\u7684\u6a80\u7fbd\u51b2\u7684\u4e0a\u534a\u8eab\u6273\u8d77\u6765\uff0c\u8ba9\u4ed6\u7684\u5934\u6795\u7740\u81ea\u5df1\u819d\u76d6\uff0c\u4e00\u9762\u628a \u8109\uff0c\u4e00\u9762\u4ed4\u7ec6\u5bdf\u770b\u4ed6\u7684\u4f24\u52bf\uff0c\u949f\u7075\u79c0\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u8eab\u5b50\u4e0d\u80fd\u52a8\u5f39\uff0c\u53e3\u4e5f\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u53ea\u80fd\u53cc\u773c \u6ee1\u542b\u6012\u610f\u7684\u76ef\u7740\u8d6b\u8fde\u6e05\u6ce2\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u628a\u4e00\u9897\u836f\u4e38\u7eb3\u5165\u6a80\u7fbd\u51b2\u53e3\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u54e5\u54e5\u6240\u53d7\u7684\u5185\u4f24\u6bd4\u6211\u60f3\u50cf\u7684 \u8fd8\u8981\u4e25\u91cd\u7684\u591a\uff0c\u73b0\u5728\u6211\u7ed9\u4ed6\u670d\u4e0b\u7684\u662f\u4e00\u9897\u5927\u5185\u73cd\u85cf\u7684\u5c0f\u8fd8\u4e39\uff0c\u8fd9\u4e39\u836f\u6709\u53bb\u7600\u751f\u65b0\uff0c\u57f9\u5143\u56fa\u672c \u4e4b\u6548\uff0c\u5728\u6cbb\u5185\u4f24\u65b9\u9762\uff0c\u548c\u5c11\u6797\u5bfa\u79d8\u5236\u7684\u5c0f\u8fd8\u4e39\u662f\u4e0d\u76f8\u4e0a\u4e0b\u7684\uff0c\u4f46\u662f\u5426\u80fd\u591f\u4fdd\u5168\u4f60\u5927\u54e5\u54e5\u7684\u6027 \u547d\uff0c\u53ef\u8fd8\u8981\u770b\u4ed6\u7684\u8fd0\u6c14\u3002\u7b2c\u4e00\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u610f\u6c14\u6d88\u6c89\uff0c\u7b2c\u4e8c\uff0c\u8fd8\u5f97\u6709\u4e2a\u4eba\u6089\u5fc3\u8c03\u62a4\u4ed6\uff0c\u4e24\u8005\u4ff1 \u5907\uff0c\u6216\u8005\u53ef\u4ee5\u4ee4\u4ed6\u6e10\u6e10\u597d\u8d77\u6765\uff0c\u5426\u5219\uff0c\u53ea\u662f\u80fd\u591f\u8ba9\u4ed6\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\u7f62\u4e86\u3002\u5c0f\u59d1\u5a18\uff0c\u6211\u8bf4\u7684 \u8bdd\uff0c\u4f60\u5e94\u8be5\u542c\u5f97\u61c2\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u5f53\u7136\u662f\u542c\u5f97\u61c2\u7684\uff0c\u8fd9\u756a\u8bdd\u7684\u610f\u601d\u65e0\u975e\u662f\u8bf4\u6a80\u7fbd\u51b2\u9700\u8981\u4e00\u4e2a\u771f\u6b63\u7231\u4ed6\u7684\u4eba\uff0c\u5b88\u5728\u4ed6 \u7684\u8eab\u8fb9\uff0c\u7ed9\u4ed6\u9f13\u52b1\uff0e\u4e3a\u4ed6\u62a4\u7406\u800c\u5df2\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u7528\u8bf4\u5c31\u662f\u8d6b\u8fde\u6e05\u6ce2\u81ea\u5df1\u4e86\uff0c\u949f\u7075\u79c0\u53e3\u91cc\u8bf4\u4e0d\u51fa \u8bdd\uff0c\u5fc3\u91cc\u5df1\u662f\u5728\u9a82\uff1a\u201c\u8bf4\u6765\u8bf4\u53bb\uff0c\u4e0d\u8fc7\u662f\u8981\u62a2\u8d70\u6211\u7684\u5927\u54e5\u54e5\u7f62\u4e86\uff0c\u771f\u4e0d\u8981\u8138\uff0c\u8fd9\u5996\u72d0\u628a\u6211\u7684 \u5927\u54e5\u54e5\u5bb3\u6210\u8fd9\u6837\uff0c\u5c45\u7136\u8fd8\u6562\u4ee5\u4ed6\u7684\u7ea2\u989c\u77e5\u5df1\u81ea\u5c45\u3002\u54fc\uff0c\u6211\u7684\u6027\u547d\u5df2\u7ecf\u64cd\u5728\u4f60\u7684\u624b\u4e0a\u3002\u4f60\u4f55\u4e0d \u628a\u6211\u4e00\u8d77\u6740\u4e86\u66f4\u4e3a\u5e72\u8106\uff1f\u201d \u3000\u3000\u662f\u554a\uff01\u5979\u662f\u5df2\u7ecf\u7ed9\u8d6b\u8fde\u6e05\u6ce2\u70b9\u4e86\u7a74\u9053\u7684\uff0c\u8d6b\u8fde\u6e05\u6ce2\u672c\u53ef\u4e3a\u6240\u6b32\u4e3a\uff0c\u4e3a\u4f55\u4e0d\u6740\u6389\u5979\u5462\uff1f\u4e3a \u4f55\u8fd8\u8981\u62d4\u5bfc\u501f\u53e3\u6765\u4e3a\u81ea\u5df1\u7684\u884c\u4e3a\u8fa9\u89e3\u5462\uff1f \u3000\u3000\u5979\u968f\u5373\u60f3\u5230\uff1a\u201c\u662f\u4e86\uff0c\u5979\u6015\u6740\u4e86\u6211\uff0c\u5373\u4f7f\u5979\u80fd\u591f\u6551\u5f97\u6d3b\u5927\u54e5\u54e5\uff0c\u5927\u54e5\u54e5\u4e5f\u51b3\u8ba1\u4e0d\u4f1a\u539f\u8c05 \u5979\u3002\u5979\u81ea\u5df1\u95ee\u5fc3\u6709\u6127\u5427\uff1f\u201d \u3000\u3000\u5979\u6b63\u5728\u5fc3\u91cc\u9a82\u8d6b\u8fde\u6e05\u6ce2\uff0c\u53ea\u89c1\u8d6b\u8fde\u6e05\u6ce2\u5df2\u7ecf\u628a\u6a80\u7fbd\u51b2\u8f7b\u8f7b\u653e\u4e0b\uff0c\u8d70\u5230\u81ea\u5df1\u9762\u524d\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u8d70\u5230\u5979\u7684\u9762\u524d\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u6253\u91cf\u5979\uff0c\u5979\u4e5f\u77aa\u7740\u53cc\u773c\u76ef\u7740\u8d6b\u8fde\u6e05\u6ce2\uff0c\u5979\u9a82\u4e0d\u51fa \u58f0\uff0c\u53ea\u80fd\u7528\u773c\u775b\u8868\u793a\u5979\u7684\u654c\u610f\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u59b9\uff0c\u4f60\u7684\u5fc3\u91cc\u662f\u5728\u607c\u6211\uff0c\u6068\u6211\u5bf9\u4e0d\u5bf9\uff1f\u563f\u563f\uff0c\u4f60 \u8d8a\u607c\u6211\uff0c\u6211\u8d8a\u9ad8\u5174\uff1f\u201d \u3000\u3000\u5979\u597d\u50cf\u8d8a\u8bf4\u8d8a\u9ad8\u5174\uff0c\u5ffd\u7136\u4f38\u51fa\u624b\u6765\uff0c\u5411\u949f\u7075\u79c0\u7684\u9762\u5e9e\u6162\u6162\u8d34\u8fd1\u3002\u949f\u7075\u79c0\u6c14\u5f97\u53cc\u773c\u7ffb\u767d\uff0c \u5fc3\u91cc\u53eb\u9053\uff1a\u201c\u6700\u597d\u4f60\u4e00\u638c\u6253\u6b7b\u6211\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9\u4f60\u4fae\u8fb1\uff01\u201d\u5979\u4ee5\u4e3a\u8fd9\u4e2a\u201c\u7389\u9762\u5996\u72d0\u201d\u6ca1\u6709\u4ec0\u4e48 \u201c\u597d\u4e8b\u201d\u505a\u51fa\u6765\uff0c\u6050\u6015\u6700\u5c11\u4e5f\u8981\u6253\u4e0a\u8033\u5149\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u8d6b\u8fde\u6e05\u6ce2\u53ea\u662f\u5728\u5979\u7684\u7c89\u8138\u4e0a\u8f7b\u8f7b\u634f\u4e86\u4e00\u4e0b\uff0c\u63a5\u7740\u53c8\u7b11\u9053\uff1a\u201c\u771f\u662f\u6211\u89c1\u72b9\u601c\uff0c\u6a80\u7fbd\u51b2 \u6709\u4f60\u8fd9\u6837\u4e00\u4e2a\u597d\u59b9\u59b9\u90a3\u4e5f\u662f\u4ed6\u7684\u798f\u6c14\u3002\u563f\u563f\uff0c\u6211\u77e5\u9053\u4f60\u607c\u6211\u6068\u6211\uff0c\u662f\u6015\u6211\u62a2\u8d70\u4e86\u4f60\u7684\u5927\u54e5 \u54e5\uff0c\u6211\u65e9\u5df2\u8bf4\u8fc7\u6211\u4e0d\u4f1a\u62a2\u4f60\u7684\u4efb\u4f55\u4e1c\u897f\u7684\uff0c\u4f60\u8fd9\u50bb\u59d1\u5a18\u600e\u4e48\u8fd8\u5403\u6211\u7684\u5e72\u918b\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u4f46\u81ea\u5df1\u4e5f\u611f\u89c9\u5f97\u5230\uff0c\u8138\u4e0a\u662f\u597d\u50cf\u6709\u70b9\u53d1\u70e7\u4e86\u3002\u5979\u5728\u9a82\u8d6b\u8fde\u6e05\u6ce2\u201c\u4e71\u56bc \u820c\u5934\u201d\uff0c\u53ea\u4e0d\u8fc7\u2014\u4e00\u5979\u81ea\u5df1\u4e5f\u5206\u8fa8\u4e0d\u51fa\uff0c\u5979\u8fd9\u6837\u607c\u6068\u201c\u7389\u9762\u5996\u72d0\u201d\u662f\u4e0d\u662f\u542b\u662f\u4e00\u70b9\u7089\u5fcc\u7684\u6210 \u5206\uff1f \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u53d7\u7684\u91cd\u4f24\uff0c\u6211\u672c\u6765\u662f\u653e\u5fc3\u4e0d\u4e0b\u7684\u3002\u4f46\u5982\u4eca\u6211\u5219\u662f\u653e\u5fc3\u628a\u4ed6\u4ea4 \u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u5012\u662f\u5927\u51fa\u949f\u7075\u79c0\u610f\u6599\u4e4b\u5916\u4e86\u3002 \u3000\u3000\u96be\u9053\u8fd9\u7389\u9762\u5996\u72d0\u5e76\u4e0d\u662f\u5982\u731c\u60f3\u90a3\u6837\uff1b\u4ee5\u6a80\u7fbd\u51b2\u7684\u7ea2\u989c\u77e5\u5df1\u81ea\u5c45\uff0c\u800c\u662f\u8ba4\u4e3a\u5979\u624d\u662f\u4e48\uff1f \u3000\u3000\u5979\u5fc3\u5ff5\u672b\u5df2\uff0c\u53ea\u542c\u5f97\u8d6b\u8fde\u6e05\u6ce2\u53c8\u5728\u7b11\u9053\uff1a\u201c\u4f60\u6015\u6211\u4e5f\u597d\u3002\u6068\u6211\u4e5f\u597d\uff0c\u8ba8\u538c\u6211\u4e5f\u597d\uff0c\u6211\u7b54 \u5e94\u4e86\u8981\u7ed9\u4f60\u7684\u4e1c\u897f\u8fd8\u662f\u8981\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u5979\u62ff\u51fa\u4e00\u4e2a\u9526\u76d2\uff0c\u653e\u5728\u949f\u7075\u79c0\u7684\u811a\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u76d2\u5b50\u91cc\u662f\u4e00\u652f\u5343\u5e74\u7684\u8001\u5c71\u4eba\u53c2\uff0c\u8981\u4e0d\u8981 \u968f\u4f60\u3002\u4e0d\u8fc7\uff0c\u4f60\u7684\u5927\u54e5\u54e5\u6050\u6015\u8981\u8fc7\u8bb8\u591a\u5929\u624d\u80fd\u591f\u81ea\u5df1\u5403\u4e1c\u897f\uff0c\u5018\u82e5\u6ca1\u6709\u8fd9\u652f\u4eba\u53c2\u5c31\u4fdd\u4e0d\u4e86\u4ed6 \u7684\u6027\u547d\u3002\u201d \u3000\u3000\u8ddf\u7740\u5979\u53c8\u62ff\u51fa\u4e00\u9762\u8170\u724c\uff0c\u653e\u5728\u9526\u76d2\u65c1\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9762\u8170\u724c\u4e5f\u662f\u7ed9\u4f60\u5927\u54e5\u54e5\u7684\uff0c\u7531\u4f60\u66ff \u4ed6\u4fdd\u7ba1\u3002\u8def\u4e0a\u5018\u82e5\u78b0\u4e0a\u516c\u5dee\u67e5\u95ee\uff0c\u4f60\u53ef\u4ee5\u628a\u8fd9\u9762\u8170\u724c\u62ff\u7ed9\u4ed6\u4eec\u770b\uff0c\u4ed6\u4eec\u5c31\u4e0d\u4f1a\u627e\u4f60\u7684\u9ebb\u70e6 \u4e86\u3002\u4f60\u82e5\u6709\u6240\u9700\uff0c\u4ed6\u4eec\u8fd8\u4f1a\u4f9b\u5e94\u4f60\u5462\uff0c\u56e0\u4e3a\u8fd9\u9762\u8170\u724c\u662f\u53ef\u4ee5\u8bc1\u660e\u4f60\u5927\u54e5\u54e5\u662f\u5728\u738b\u5e9c\u5f53\u5dee\u7684\u3002 \u738b\u5e9c\u7684\u51fa\u5dee\u4eba\u5458\u662f\u6709\u9650\u671f\u7684\uff0c\u4f60\u53ef\u4ee5\u8bf4\u4f60\u7684\u5927\u54e5\u54e5\u662f\u8bf7\u5047\u56de\u5bb6\u63a2\u4eb2\uff0c\u4e0d\u5e78\u5728\u5bb6\u4e2d\u751f\u4e86\u75c5\uff0c\u4e3a \u6015\u8bef\u4e86\u671f\u9650\uff0c\u4f60\u8fd9\u4e2a\u505a\u5c0f\u59b9\u59b9\u7684\u53ea\u80fd\u62a4\u9001\u4ed6\u56de\u4eac\u3002\u5f53\u7136\uff0c\u6211\u53ea\u662f\u4e3e\u4e2a\u4f8b\u800c\u5df2\uff0c\u4ee5\u4f60\u8fd9\u6837\u806a \u660e\uff0c\u600e\u6837\u7f16\u9020\u8bf4\u8f9e\uff0c\u672c\u6765\u662f\u7528\u4e0d\u7740\u6211\u6559\u4f60\u7684\u3002\u597d\u4e86\uff0c\u6211\u8981\u8bf4\u7684\u90fd\u5df2\u8bf4\u4e86\uff0c\u6211\u4e5f\u8981\u8d70\u4e86\u3002\u563f \u563f\uff0c\u5c0f\u59b9\u59b9\uff0c\u4f60\u8fd8\u5728\u607c\u6211\u4e0d\uff1f\u4f60\u607c\u6211\u4e5f\u4e0d\u6253\u7d27\uff0c\u53ea\u6c42\u4f60\u6089\u5fc3\u770b\u62a4\u4f60\u7684\u5927\u54e5\u54e5\u3002\u5176\u5b9e\uff0c\u8fd9\u4e5f\u4e0d \u7528\u6211\u5631\u5490\u4f60\u7684\u4e86\uff0c\u6211\u628a\u4ed6\u4ea4\u7ed9\u4f60\uff0c\u6211\u662f\u53ef\u4ee5\u5b8c\u5168\u653e\u5fc3\u8d70\u4e86\uff01\u201d\u5979\u5e26\u7740\u7b11\u66ff\u949f\u7075\u79c0\u89e3\u5f00\u7a74\u9053\uff0c \u8f6c\u8fc7\u8eab\uff0c\u98d8\u7136\u800c\u53bb\u3002\u5f88\u5feb\uff0c\u8fde\u5f71\u5b50\u4e5f\u4e0d\u89c1\u4e86\uff0c\u53ea\u6709\u7b11\u58f0\u8fd8\u5728\u8fdc\u5904\u9690\u9690\u4f20\u6765\uff0c\u5509\uff0c\u5979\u7684\u7b11\u58f0\u600e \u7684\u597d\u50cf\u5145\u6ee1\u7740\u65e0\u53ef\u5948\u4f55\u7684\u51c4\u51c9\u610f\u5473\u3002 \u3000\u3000\u7a74\u9053\u89e3\u5f00\uff0c\u949f\u7075\u79c0\u662f\u5df2\u7ecf\u53ef\u4ee5\u6d3b\u52a8\u4e86\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u8fd8\u5728\u53d1\u5446\u3002 \u3000\u3000\u521a\u624d\u5979\u8fd8\u662f\u6ee1\u809a\u76ae\u7684\u6c14\uff0c\u6068\u4e0d\u5f97\u628a\u7389\u9762\u5996\u72d0\u9a82\u5f97\u75db\u5feb\u6dcb\u6f13\u7684\uff0c\u73b0\u5728\u5979\u53ef\u4ee5\u9a82\u51fa\u58f0\u4e86\uff0c\u53ef \u662f\u5979\u53c8\u4e0d\u60f3\u9a82\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u5012\u662f\u597d\u50cf\u6709\u70b9\u540c\u60c5\u8d77\u201c\u7389\u9762\u5996\u72d0\u201d\u6765\u4e86\u3002 \u3000\u3000\u5979\u9996\u5148\u8d70\u8fc7\u53bb\u770b\u5979\u7684\u201c\u5927\u54e5\u54e5\u201d\uff0c\u6a80\u7fbd\u51b2\u4ecd\u5728\u660f\u8ff7\uff0c\u4e0d\u8fc7\u5fc3\u810f\u7684\u8df3\u52a8\u5df2\u662f\u4e0d\u50cf\u521a\u624d\u90a3\u6837 \u5fae\u5f31\u4e86\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u6a80\u7fbd\u51b2\u7684\u4f24\u52bf\u4e4b\u91cd\u4e5f\u8fd8\u662f\u4ee4\u5f97\u5979\u5fd0\u5fd1\u4e0d\u5b89\u7684\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u88ab\u90a3\u4e24\u53e5\u8bdd\u8fd8\u7559\u5728\u5979\u7684\u8033\u8fb9\uff1a\u201c\u4f60\u7684\u5927\u54e5\u54e5\u662f\u5426\u80fd\u4fdd\u5168\u6027\u547d\u8fd9\u8fd8\u8981\u770b\u4ed6\u7684\u8fd0 \u6c14\uff01\u201d \u3000\u3000\u5979\u5728\u4e00\u65e5\u4e4b\u95f4\uff0c\u5c3d\u5931\u4eb2\u4eba\uff0c\u672c\u6765\u662f\u6307\u671b\u201c\u4f9d\u9760\u201d\u201c\u5927\u54e5\u54e5\u201d\u7684\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u5374\u662f\u6613\u4f4d\u800c \u5904\uff0c\u5fc5\u987b\u7531\u5979\u6765\u7167\u987e\u201c\u5927\u54e5\u54e5\u201d\u4e86\u3002\u5979\u80fd\u591f\u6311\u5f97\u8d77\u8fd9\u526f\u62c5\u5b50\u5417\uff1f\u6709\u611f\u4e8e\u9020\u5316\u5f04\u4eba\uff0c\u5979\u4e0d\u7981\u5fc3 \u5934\u82e6\u7b11\u4e86\uff1a\u201c\u90a3\u7389\u9762\u5996\u72d0\u5012\u662f\u8bf4\u5f97\u4e0d\u9519\uff0c\u4eca\u540e\u6211\u53ea\u80fd\u6c42\u8001\u5929\u7237\u4fdd\u4f51\u6211\u7684\u8fd0\u6c14\u597d\u4e86\u3002\u201d \u3000\u3000\u4f46\u4ece\u53e6\u4e00\u65b9\u9762\u6765\u8bf4\uff1a\u4e5f\u201c\u5e78\u4e8f\u201d\u547d\u8fd0\u5b89\u6392\u5979\u62c5\u5f53\u8fd9\u4ef6\u5927\u4e8b\uff0c\u4ee4\u5979\u65e0\u6687\u53bb\u60b2\u75db\u4e86\u3002\u5426\u5219\u4ee5 \u5979\u5c0f\u5c0f\u7684\u5e74\u7eaa\uff0c\u53c8\u600e\u53d7\u5f97\u8d77\u8fd9\u7a81\u5982\u5176\u6765\u7684\uff0c\u4e00\u65e5\u4e4b\u95f4\u5c3d\u5931\u4eb2\u4eba\u7684\u5927\u6253\u51fb\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u5fc3\u810f\u8fd8\u5728\u8df3\u52a8\uff0c\u4f46\u4ecd\u662f\u6c14\u82e5\u6e38\u4e1d\uff0c\u5f53\u52a1\u4e4b\u6025\uff0c\u5fc5\u9700\u8ba9\u4ed6\u8fd9\u5fae\u5f31\u7684\u751f\u547d\u80fd\u591f\u5ef6\u7eed \u4e0b\u53bb\u3002 \u3000\u3000\u5979\u62fe\u8d77\u8d6b\u8fde\u6e05\u6ce2\u7559\u4e0b\u6765\u7684\u9526\u76d2\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u679c\u7136\u662f\u4e00\u652f\u7c97\u5982\u513f\u81c2\u7684\u4eba\u53c2\u3002 \u3000\u3000\u53ef\u4e0d\u53ef\u4ee5\u76f8\u4fe1\u8fd9\u4e2a\u201c\u5996\u72d0\u201d\u5462\uff1f\u72d0\u72f8\u662f\u4ee5\u72e1\u733e\u51fa\u540d\u7684\uff0c\u5979\u4f1a\u4e0d\u4f1a\u5728\u8fd9\u4eba\u53c2\u4e0a\u5f04\u4ec0\u4e48\u624b \u811a\uff1f \u3000\u3000\u5979\u4e0d\u61c2\u5f97\u5206\u8fa9\u4eba\u53c2\u7684\u771f\u5047\uff0c\u4f46\u6709\u4e00\u6837\u5979\u662f\u61c2\u5f97\u7684\uff0c\u5979\u662f\u5973\u4eba\uff0c\u7389\u9762\u5996\u72d0\u4e5f\u662f\u5973\u4eba\uff0c\u5979\u61c2 \u5f97\u5206\u8fa8\u53e6\u4e00\u4e2a\u5973\u4eba\u4e8b\u60c5\u7684\u771f\u5047\u3002 \u3000\u3000\u5979\u7684\u773c\u524d\u5e7b\u51fa\u7389\u9762\u5996\u72d0\u7684\u5f71\u5b50\uff0c\u7389\u9762\u5996\u72d0\u597d\u50cf\u8fd8\u5728\u6ce8\u89c6\u7740\u5979\uff0c\u5e26\u7740\u90a3\u526f\u65e0\u53ef\u5948\u4f55\u7684\u7b11 \u5bb9\uff0c\u5979\u7684\u7591\u60e7\u4e5f\u597d\u50cf\u7ed9\u8fd9\u7b11\u5bb9\u6eb6\u5316\u4e86\u3002 \u3000\u3000\u201c\u7389\u9762\u5996\u72d0\u201d\u6216\u8005\u662f\u4e2a\u73af\u5973\u4eba\uff0c\u4f46\u5979\u51b3\u8ba1\u4e0d\u4f1a\u5bb3\u6211\u7684\u5927\u54e5\u54e5\uff01\u5979\u7ec8\u4e8e\u76f8\u4fe1\u4e86\u7389\u9762\u5996\u72d0 \u4e86\u3002 \u3000\u3000\u4f46\u6a80\u7fbd\u51b2\u8138\u90e8\u7684\u808c\u8089\u90fd\u50f5\u786c\u4e86\uff0c\u4ed6\u6ca1\u6709\u77e5\u89c9\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5480\u56bc\uff0c\u4ed6\u600e\u4e48\u80fd\u591f\u5403\u4eba\u53c2\u5462\uff1f \u3000\u3000\u5979\u60f3\u5230\u4e00\u4e2a\u529e\u6cd5\uff0c\u4f46\u8fd9\u4e2a\u529e\u6cd5\uff0c\u53ef\u6709\u70b9\u4ee4\u5979\u96be\u4e3a\u60c5\u7684\u3002 \u3000\u3000\u4f46\u5979\u53ef\u4e0d\u80fd\u4e0d\u987e\u5927\u54e5\u54e5\u7684\u6027\u547d\u554a\uff0c\u5979\u5728\u5fc3\u91cc\u81ea\u5df1\u5bf9\u81ea\u5df1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u53eb\u4ed6\u505a\u5927\u54e5\u5417\uff1f \u6211\u53eb\u4ed6\u505a\u5927\u54e5\u54e5\uff0c\u5c31\u5e94\u8be5\u5f53\u4ed6\u662f\u4eb2\u54e5\u54e5\u4e00\u6837\u3002\u6211\u8fd8\u8981\u907f\u4ec0\u4e48\u8d5a\u5462\uff1f\u201d \u3000\u3000\u4e3a\u4e86\u4fdd\u5168\u5927\u54e5\u54e5\u7684\u6027\u547d\uff0c\u96be\u4e3a\u60c5\u7684\u4e8b\u4e5f\u8981\u505a\u4e86\u3002 \u3000\u3000\u5979\u7528\u77ed\u5251\u524a\u4e0b\u4e00\u6bb5\u4eba\u53c2\uff0c\u5148\u628a\u4eba\u53c2\u653e\u5728\u81ea\u5df1\u7684\u53e3\u4e2d\u56bc\u70c2\uff0c\u518d\u64ac\u5f00\u6a80\u7fbd\u51b2\u7684\u5634\u5df4\uff0c\u597d\u50cf\u6bcd \u4eb2\u628a\u56bc\u70c2\u7684\u996d\u56e2\u5582\u7ed9\u81ea\u5df1\u7684\u5b69\u5b50\u4e00\u6837\uff0c\u5582\u7ed9\u5979\u7684\u5927\u54e5\u54e5\u54bd\u4e0b\u3002 \u3000\u3000\u201c\u5047\u5982\u8fd9\u4e0d\u662f\u4eba\u53c2\uff0c\u662f\u6bd2\u836f\u7684\u8bdd\uff0c\u90a3\u5c31\u8ba9\u6211\u548c\u5927\u54e5\u54e5\u4e00\u8d77\u6b7b\u5427\uff01\u201d\u5979\u60f3\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u679d\u9999\u65f6\u523b\uff0c\u5979\u6ca1\u6709\u6b7b\uff0c\u7cbe\u795e\u53cd\u800c\u4f3c\u4e4e\u597d\u8d77\u6765\u4e86\u3002\u6a80\u7fbd\u51b2\u547c\u5438\u7684\u6c14\u606f\u4e5f\u597d\u50cf\u6bd4\u521a\u624d \u7c97\u58ee\u4e00\u4e9b\uff0c\u50cf\u662f\u5728\u9163\u7761\u4e4b\u4e2d\uff0c\u7761\u5f97\u66f4\u5b89\u7a33\u4e86\u3002 \u3000\u3000\u5979\u8bd5\u8bd5\u4f38\u62f3\u8e22\u817f\uff0c\u89c9\u5f97\u81ea\u5df1\u7684\u6c14\u529b\u867d\u7136\u672a\u80fd\u6062\u590d\u5982\u521d\uff0c\u4f46\u80cc\u4e2a\u4eba\u8d70\u8def\u5927\u6982\u662f\u53ef\u4ee5\u4e86\u3002 \u3000\u3000\u4e5f\u5e78\u4e8f\u5979\u5728\u628a\u4eba\u53c2\u56bc\u70c2\u5582\u6a80\u7fbd\u51b2\u5403\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u4e5f\u201c\u7565\u6709\u5f97\u76ca\u201d\uff0c\u8fd9\u624d\u6709\u7cbe\u795e\u53ef\u4ee5\u652f\u6491 \u5f97\u4f4f\u3002\u4f46\u5979\u6bd5\u7adf\u662f\u4e2a\u5341\u4e03\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u53c8\u662f\u5728\u4e00\u573a\u5267\u6218\u517c\u4e14\u53d7\u4e86\u6781\u5927\u7684\u523a\u6fc0\u4e4b\u540e\uff0c\u62b1\u7740\u4e00\u4e2a \u5927\u4eba\u8d70\u8def\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u6e10\u6e10\u4e5f\u89c9\u5f97\u75b2\u60eb\u4e0d\u582a\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5c0f\u59d1\u5a18\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5c71\u5773\u5904\u8f6c\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u7a7f\u7740\u7adf\u662f\u91d1\u56fd\u519b\u5b98\u7684\u670d\u9970\u3002 \u3000\u3000\u8fd9\u519b\u5b98\u8d70\u5230\u5979\u7684\u773c\u524d\uff0c\u7741\u5927\u773c\u775b\u770b\u5979\uff0c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u8fd8\u662f\u4e00\u4e2a\u6807\u81f4\u7684\u5927\u59d1\u5a18\u5462\uff01\u8fd9\u4eba\u662f \u8c01\uff0c\u4f60\u62b1\u7740\u4ed6\uff1f\u662f\u4f60\u7684\u60c5\u90ce\u8fd8\u662f\u4f60\u7684\u4e08\u592b\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u5fcd\u7740\u6c14\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4ed6\u662f\u6211\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u7b11\u9053\uff1a\u201c\u662f\u4f60\u7684\u54e5\u54e5\u5417\uff1f\u6211\u8fd8\u4e3a\u662f\u4f60\u7684\u4e08\u592b\u5462\uff1f\u8fd9\u4e48\u8bf4\uff0c\u4f60\u8fd8\u662f\u9ec4\u82b1\u95fa\u5973\u4e86\uff01\u201d \u54a7\u5f00\u6ee1\u5634\u9ec4\u7259\uff0c\u7b11\u563b\u563b\u7684\u7adf\u7136\u634f\u4e86\u5979\u7684\u8138\u988a\u4e00\u4e0b\u3002 \u3000\u3000\u949f\u7075\u79c0\u677f\u7740\u8138\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u7684\u54e5\u54e5\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u7b11\u9053\uff1a\u201c\u662f\u5929\u738b\u8001\u5b50\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u5929\u738b\u8001\u5b50\uff0c\u4e0d\u8fc7\uff0c\u6216\u8005\u4ed6\u7684\u5b98\u804c\u6bd4\u4f60\u9ad8\u4e9b\uff0c\u4f60\u770b\u8fd9\u9762\u8170\u724c\u3002\u201d \u3000\u3000\u4fd7\u8bdd\u8bf4\u201c\u5bb0\u76f8\u95e8\u524d\u4e03\u54c1\u5b98\u201d\uff0c\u4f55\u51b5\u662f\u738b\u5e9c\u4eba\u5458\u3002\u5b8c\u989c\u957f\u4e4b\u53ef\u662f\u91d1\u56fd\u6743\u52bf\u6700\u5927\u7684\u738b\u7237\uff01\u4ece \u5b8c\u989c\u738b\u5e9c\u51fa\u6765\u7684\u4eba\uff0c\u5373\u4f7f\u662f\u8fb9\u5173\u603b\u5175\u4e5f\u8981\u5949\u627f\u4ed6\u7684\u3002\u8fd9\u4e2a\u519b\u5b98\uff0c\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u5c0f\u7684\u201c\u4f50\u9886\u201d\uff0c \u6700\u5c0f\u8981\u8fde\u5347\u51e0\u7ea7\uff0c\u624d\u80fd\u8fbe\u5230\u603b\u5175\u7684\u5730\u4f4d\u3002 \u3000\u3000\u519b\u5b98\u770b\u4e86\u8170\u724c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u662f\u5728\u5b8c\u989c\u738b\u5e9c\u5f53\u5dee\u7684\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u8fd9\u9762\u8170\u724c\u662f\u5047\u7684\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u519b\u5b98\u662f\u4ece\u8fb9\u5173\u51fa\u5dee\u56de\u6765\u7684\uff0c\u4ed6\u5728\u8fb9\u5173\u66fe\u7ecf\u4e0d\u6b62\u4e00\u6b21\u89c1\u8fc7\u5b8c\u989c\u738b\u5e9c\u7684\u8170\u724c\uff0c\u5f53\u7136\u4e00\u770b \u5c31\u77e5\u9053\u662f\u771f\u7684\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5fc3\u91cc\u8fd8\u662f\u4e0d\u80fd\u65e0\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u542c\u4f60\u7684\u53e3\u97f3\uff0c\u4f3c\u4e4e\u662f\u6c5f\u5357\u4eba\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u5144\u59b9\u662f\u5bb6\u5728\u6c5f\u5357\u7684\uff0c\u4f46\u6c5f\u5357\u4eba\u6c0f\uff0c\u96be\u9053\u5c31\u4e0d \u80fd\u5230\u738b\u5e9c\u5f53\u5dee\u5417\uff1f\u201d\u5b8c\u989c\u957f\u4e4b\u7684\u624b\u4e0b\uff0c\u5947\u624d\u6700\u80fd\u4e4b\u58eb\u751a\u591a\uff0c\u6c49\u8499\u56de\u85cf\uff0c\u5404\u4e2a\u5730\u65b9\u7684\u4eba\u90fd\u6709 \u7684\u3002 \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4ee4\u5144\u597d\u50cf\u4e0d\u7701\u4eba\u4e8b\u7684\u6837\u5b50\uff0c\u4e3a\u4e86\u4f55\u56e0\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u4e4b\u98ce\u4e91\uff0c\u4eba\u6709\u5239\u65f6\u4e4b\u7978\u798f\uff0c\u6211\u4e5f\u60f3\u4e0d\u5230\u4ed6\u7a81\u7136\u5728\u9014\u4e2d\u60a3\u75c5\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4ee4\u5144\u8fd9\u6b21\u5357\u5f52\uff0c\u662f\u4e3a\u4e86\u516c\u4e8b\u8fd8\u662f\u4e3a\u4e86\u79c1\u4e8b\uff1f\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u5728\u5ba1\u95ee \u6211\u5417\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u4eec\u90fd\u662f\u4e3a\u4e86\u738b\u7237\u6548\u529b\u7684\uff0c\u6211\u53ea\u662f\u60f3\u5e2e\u4ee4\u5144\u7684\u5fd9\u800c\u5df2\u3002\u6bd4\u5982\u8bf4\uff0c\u4ed6 \u7684\u516c\u4e8b\u5047\u5982\u6ca1\u6709\u529e\u59a5\u7684\u8bdd\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u79c1\u4e8b\u5462\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\u540c\u6837\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u62ac\u5934\u6765\uff0c\u95ee\u9053\uff1a\u201c\u524d\u9762\u90a3\u5ea7\u5c71\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u53eb\u7fe0\u5c4f\u5c71\uff0c\u4f60\u77a7\u90a3\u56db\u65b9\u5f62\u7684\u5c71\u5cf0\uff0c\u662f\u4e0d\u662f\u50cf\u4e00\u9762\u5c4f\u98ce\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u4f5c\u51fa\u7fdf\u7136\u4e00\u7701\u7684\u6a21\u6837\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u662f\u7fe0\u5c4f\u5c71\uff0c\u8fd9\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u519b\u5b98\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u4e86\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u6709\u4e2a\u4e16\u4f2f\uff0c\u5c31\u662f\u5728\u8fd9\u5ea7\u7fe0\u5c4f\u5c71\u4e0a\u9690\u5c45\u7684\u3002\u201d \u3000\u3000\u519b\u5b98\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff1f\u201d\u671b\u5411\u949f\u7075\u79c0\u7684\u76ee\u5149\u4e0d\u89c9\u5e26\u7740\u51e0\u5206\u7591\u60d1\uff0c\u5fc3\u91cc\u5728\u60f3\uff1a \u201c\u65e2\u7136\u6709\u4eb2\u53cb\u4f4f\u5728\u8fd9\u91cc\uff0c\u4e3a\u4ec0\u4e48\u4f60\u73b0\u5728\u624d\u60f3\u8d77\u6765\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4ed6\u662f\u5148\u7236\u7684\u597d\u670b\u53cb\uff0c\u6211\u5c0f\u65f6\u5019\u4ed6\u6765\u8fc7\u6211\u5bb6\u91cc\u4e00\u6b21\uff0c\u4ee5\u540e\u5c31\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4e86\uff0c \u8981\u4e0d\u662f\u6211\u53d1\u89c9\u524d\u9762\u90a3\u5ea7\u5c71\u5cf0\u662f\u50cf\u4e00\u9053\u5c4f\u98ce\uff0c\u6211\u8fd8\u60f3\u4e0d\u8d77\u6765\u5462\uff1f\u542c\u5148\u7236\u8bf4\uff0c\u4ed6\u7684\u6b66\u529f\u867d\u7136\u4e0d \u9ad8\u3002\u533b\u9053\u5374\u662f\u76f8\u5f53\u9ad8\u660e\u7684\u3002\u201d\u4e0d\u7740\u75d5\u8ff9\u5730\u7b54\u590d\u4e86\u8fd9\u519b\u5b98\u7684\u7591\u95ee\u3002 \u3000\u3000\u90a3\u519b\u5b98\u53bb\u4e86\u7591\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6562\u60c5\u597d\uff0c\u90a3\u4e48\uff0c\u4f60\u7684\u610f\u601d\u662f\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5f53\u7136\u662f\u5c31\u8fd1\u6c42\u533b\u597d\u4e86\u3002\u4f60\u53ef\u4ee5\u5e2e\u5fd9\u9001\u6211\u7684\u54e5\u54e5\u4e0a\u5c71\u4e48\uff1f\u201d \u3000\u3000\u519b\u5b98\u770b\u524d\u9762\u90a3\u5ea7\u7fe0\u5c4f\u5c71\uff0c\u8ddd\u79bb\u867d\u7136\u4e0d\u8fdc\uff0c\u5c71\u5374\u751a\u9ad8\u3002\u5fc3\u60f3\uff1a\u201c\u8981\u722c\u4e0a\u8fd9\u5ea7\u5c71\u6050\u6015\u6700\u5c11\u4e5f \u5f97\u82b1\u6211\u534a\u5929\u5de5\u592b\uff0c\u6765\u56de\u5c31\u5f97\u803d\u6401\u4e00\u5929\u3002\u4e0d\u8fc7\uff0c\u603b\u80dc\u4e8e\u80cc\u5979\u7684\u54e5\u54e5\u8d70\u4e00\u767e\u591a\u91cc\u624d\u80fd\u56de\u5230\u8fb9\u5173\u3002 \u6211\u4e3a\u4e86\u5e2e\u5b8c\u989c\u738b\u7237\u7684\u4eba\u803d\u6401\u884c\u7a0b\uff0c\u6599\u60f3\u603b\u5175\u4e5f\u4e0d\u4f1a\u602a\u6211\u3002\u201d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u8d4f\u9762\uff0c\u6211\u81ea\u5f53 \u6548\u52b3\uff0c\u4f46\uff0c\u59d1\u5a18\u4f60\u8d70\u5f97\u52a8\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u53c8\u997f\u53c8\u7d2f\uff0c\u4e00\u54ac\u7259\u6839\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\u4e0d\u52a8\u4e5f\u5f97\u8d70\uff01\u201d \u3000\u3000\u90a3\u519b\u5b98\u8001\u4e8e\u7ecf\u9a8c\uff0c\u4e00\u77a7\u5c31\u77e5\u9053\u949f\u7075\u79c0\u662f\u997f\u5f97\u53d1\u614c\uff0c\u8bf4\u9053\uff1a\u201c\u5c71\u8fd9\u6837\u9ad8\uff0c\u6211\u53ef\u5f97\u5403\u70b9\u4e1c\u897f \u624d\u8d70\u5f97\u52a8\u5462\u3002\u59d1\u5a18\uff0c\u5982\u679c\u4f60\u4e0d\u5acc\u7c97\u7cd9\u7684\u8bdd\uff0c\u8bf7\u4f60\u4e5f\u5403\u4e00\u70b9\u5427\u3002\u201d \u3000\u3000\u4ed6\u7684\u5e72\u7cae\u5012\u662f\u5f88\u4e30\u5bcc\uff0c\u6709\u7092\u7c73\uff0c\u6709\u5e72\u679c\uff0c\u6709\u7cd5\u997c\uff0c\u8fd8\u6709\u8089\u812f\u3002\u949f\u7075\u79c0\u4e5f\u4e0d\u548c\u4ed6\u5ba2\u6c14\uff0c\u5f00 \u6000\u5927\u56bc\uff0c\u5403\u4e86\u4e2a\u9971\uff0c\u62b9\u62b9\u5634\u7b11\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eca\u5929\u8fde\u4e00\u676f\u6c34\u4e5f\u672a\u8fdb\u8fc7\u53e3\u5462\uff0c\u591a\u8c22\u4f60\u8fd9\u4e9b \u597d\u4e1c\u897f\u3002\u201d \u3000\u3000\u519b\u5b98\u6253\u5f00\u4e00\u4e2a\u846b\u82a6\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u59d1\u5a18\u559c\u6b22\uff0c\u8bf7\u5e38\u9762\u559d\u4e00\u70b9\u9152\u5427\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u9053\uff1a\u201c\u8fd9\u662f\u9a6c\u5976\u9152\uff0c\u4e0d\u4f1a\u559d\u9189\u7684\u3002\u4e0d\u8fc7\uff0c\u5b83\u5bf9\u6062\u590d\u6c14\u529b\uff0c\u5012\u662f\u5f88\u6709\u529f\u6548\u3002\u201d\u8fd9\u9a6c \u5976\u9152\u662f\u4ed6\u4ece\u5bb6\u4e61\u5e26\u56de\u6765\u7684\uff0c\u867d\u7136\u4e0d\u662f\u540d\u9152\uff0c\u4ed6\u5374\u6781\u5176\u73cd\u60dc\uff0c\u8981\u4e0d\u662f\u4e3a\u4e86\u5df4\u7ed3\u949f\u7075\u79c0\u7684\u7f18\u6545\uff0c \u4ed6\u8fd8\u820d\u4e0d\u5f97\u81ea\u5df1\u559d\u5462\u3002 \u3000\u3000\u949f\u7075\u79c0\u6599\u4ed6\u4e0d\u6562\u5728\u9152\u4e2d\u4e0b\u6bd2\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8ba9\u6211\u5c1d\u5c1d\u3002\u201d\u5979\u5176\u5b9e\u662f\u80fd\u559d\u9152\u7684\uff0c\u4e00\u559d \u5c31\u559d\u4e86\u534a\u846b\u82a6\uff0c\u9a6c\u5976\u6ecb\u8865\uff0c\u559d\u4e86\u8fd9\u534a\u846b\u82a6\u7684\u9a6c\u5976\u9152\uff0c\u679c\u7136\u6c14\u529b\u53c8\u6062\u590d\u4e86\u51e0\u5206\u3002 \u3000\u3000\u519b\u5b98\u80cc\u8d77\u6a80\u7fbd\u51b2\u5f80\u524d\u9762\u8d70\uff0c\u4ed6\u5728\u4ece\u519b\u524d\u672c\u6765\u662f\u4e2a\u730e\u4eba\uff0c\u767b\u5c71\u5982\u5c65\u5e73\u5730\u3002\u521d\u65f6\u4ed6\u6015\u5c0f\u59d1\u5a18 \u8ddf\u4e0d\u4e0a\u4ed6\uff0c\u540e\u6765\u4e00\u770b\uff0c\u949f\u7075\u79c0\u8d70\u5f97\u6bd4\u4ed6\u8fd8\u5feb\uff0c\u4ed6\u4e5f\u5c31\u8fc8\u5f00\u4e86\u5927\u6b65\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u7ec3\u8fc7\u4e00\u70b9\u5185\u529f\uff1b\u4e00\u9762\u8d70\u4e00\u9762\u8fd0\u7528\u201c\u884c\u529f\u201d\u6765\u8c03\u5300\u6c14\u606f\uff0c\u8d8a\u8d70\u8d8a\u89c9\u7cbe\u795e\uff0c\u8fc7\u4e86\u4e00\u4e2a \u65f6\u8fb0\uff0c\u5979\u5df2\u7ecf\u662f\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6062\u590d\u5982\u521d\u4e86\u3002 \u3000\u3000\u90a3\u519b\u5b98\u6ca1\u6709\u7ec3\u8fc7\u5185\u529f\uff0c\u4ed6\u80cc\u7740\u4e2a\u4eba\uff0c\u8fc8\u5f00\u5927\u6b65\uff0c\u521d\u65f6\u5065\u6b65\u5982\u98de\uff0c\u6e10\u6e10\u5c31\u6162\u4e0b\u6765\uff0c\u6765\u5230\u4e86 \u534a\u5c71\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u6c14\u5598\u5982\u725b\u3002 \u3000\u3000\u6b64\u65f6\u65e0\u8272\u5df2\u8fd1\u9ec4\u660f\uff0c\u4ed6\u662f\u4e2a\u6709\u7ecf\u9a8c\u7684\u730e\u4eba\u4e00\u770b\u5c71\u4e0a\u6ca1\u6709\u708a\u70df\u5347\u8d77\uff0c\u6cbf\u9014\u4e5f\u6ca1\u6709\u53d1\u73b0\u66fe\u7ecf \u6709\u4eba\u8d70\u8fc7\u7684\u8ff9\u8c61\uff0c\u4e0d\u89c9\u7591\u5fc3\u518d\u8d77\u4e86\u3002 \u3000\u3000\u201c\u5c71\u4e0a\u4f3c\u4e4e\u6ca1\u6709\u4eba\u5bb6\uff0c\u4f60\u5f53\u771f\u8bb0\u5f97\u4f60\u662f\u6709\u4e2a\u4e16\u4f2f\u4f4f\u5728\u8fd9\u5c71\u4e0a\u5417\uff1f\u201d\u519b\u5b98\u95ee\u9053\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u662f\u5148\u7236\u544a\u8bc9\u6211\u7684\uff0c\u6211\u600e\u4e48\u4f1a\u8bb0\u9519\u3002\u5230\u4e86\u5c71\u4e0a\uff0c\u6162\u6162\u627e\uff0c\u603b\u53ef\u4ee5\u627e\u5f97\u7740\u4ed6 \u7684\u3002\u201d \u3000\u3000\u519b\u5b98\u9053\uff1a\u201c\u6050\u6015\u8fd8\u8981\u8d70\u4e00\u4e2a\u65f6\u8fb0\u5462\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u8d70\u7d2f\u4e86\uff0c\u662f\u5427\uff1f\u597d\uff0c\u90a3\u5c31\u5148\u6b47\u4e00\u6b47\u518d\u8d70\u3002\u201d \u3000\u3000\u519b\u5b98\u628a\u6a80\u7fbd\u51b2\u653e\u4e86\u4e0b\u6765\uff0c\u6a80\u7fbd\u51b2\u4e0d\u77e5\u662f\u5426\u53d7\u4e86\u9707\u8361\u7684\u5173\u7cfb\uff0c\u867d\u7136\u672a\u9192\uff0c\u5374\u8bf4\u8d77\u8bdd\u6765\u4e86\u3002 \u539f\u6765\u5728\u68a6\u4e2d\u4ed6\u8fd8\u5728\u5343\u67f3\u5e84\u53ae\u6740\uff0c\u4ed6\u662f\u5728\u53d1\u68a6\u5453\u3002\u201d \u3000\u3000\u201c\u67f3\u8001\u8d3c\uff0c\u4f60\u597d\u72e0\u6bd2\uff01\u201d\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u5feb\u8d70\uff0c\u522b\u7406\u6211\uff01\u201d \u3000\u3000\u4ed6\u5728\u9a82\u201c\u67f3\u8001\u8d3c\u201d\uff0c\u90a3\u519b\u5b98\u53ef\u4e0d\u662f\u80e1\u6d82\u86cb\uff0c\u4e00\u542c\u5c31\u77e5\uff0c\u4ed6\u9a82\u7684\u8fd9\u4e2a\u201c\u67f3\u8001\u8d3c\u201d\uff0c\u4e0d\u662f\u67f3 \u5143\u7532\u8fd8\u80fd\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u4e00\u77e5\u4e0a\u5f53\uff0c\u7acb\u5373\u5c31\u6293\u6a80\u7fbd\u51b2\uff0c\u53ef\u662f\u4ed6\u60f3\u4e0d\u5230\u7684\u949f\u7075\u79c0\u4ea6\u662f\u65e9\u5df2\u60f3\u5230\u4e86\uff0c\u6a80\u7fbd\u51b2\u4e00\u53d1\u68a6 \u5453\u5979\u7acb\u77e5\u4e0d\u5999\uff0c\u62a2\u5feb\u4e00\u6b65\uff0c\u62e6\u4f4f\u90a3\u519b\u5b98\uff0c\u7b11\u9053\uff1a\u201c\u4e5f\u7528\u4e0d\u7740\u8fd9\u6837\u5c31\u8d70\u5440\uff0c\u4f60\u591a\u6b47\u4e00\u4f1a\u513f \u5427\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u559d\u9053\uff1a\u201c\u81ed\u4e2b\u5934\uff0c\u6562\u800d\u5f04\u6211\uff01\u201d\u5f20\u5f00\u5927\u624b\uff0c\u5411\u5979\u6293\u4e0b\u3002 \u3000\u3000\u949f\u7075\u79c0\u4e00\u95ea\u95ea\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u8feb\u6211\u548c\u4f60\u52a8\u624b\u4e48\uff1f\u6211\u529d\u4f60\u8fd8\u4e0d\u662f\u5feb\u5feb\u8d70\u4e86\u7684\u597d\uff0c\u6211 \u53ef\u4e0d\u60f3\u6740\u4f60\uff01\u201d \u3000\u3000\u519b\u5b98\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u4e2b\u5934\u4e5f\u80fd\u6740\u6211\uff1f\u201d\u957f\u638c\u6363\u51fa\uff0c\u547c\u547c\u631f\u98ce\u3002\u949f\u7075\u79c0\u4e00\u6765\u786e\u662f\u4e0d\u60f3\u6740 \u4ed6\uff0c\u4e8c\u6765\u6c14\u529b\u662f\u6bd4\u4ed6\u5f31\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u89c1\u4ed6\u6765\u7684\u51f6\u731b\uff0c\u53ea\u597d\u53c8\u518d\u9000\u540e\u51e0\u6b65\u3002 \u3000\u3000\u519b\u5b98\u559d\u9053\uff1a\u201c\u81ed\u4e2b\u5934\uff0c\u77e5\u9053\u5389\u5bb3\u4e86\u5427\uff1f\u82e5\u8981\u6211\u70e7\u4f60\u6027\u547d\uff0c\u5feb\u5feb\u4ece\u5b9e\u62db\u6765\uff0c\u8fd9\u4eba\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u5df2\u544a\u8bc9\u4e86\u4f60\u5417\uff0c\u4ed6\u662f\u6211\u7684\u5927\u54e5\u54e5\u3002\u201d \u3000\u3000\u519b\u5b98\u60f3\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u6211\u5148\u6740\u4e86\u4f60\u8fd9\u4e2a\u5047\u54e5\u54e5\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u6562\u6211\u7684\u54e5\u54e5\u4e00\u6839\u6beb\u6bdb\uff0c\u53ef\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u5979\u963b\u62e6\u90a3\u4e2a\u519b\u5b98\uff0c\u7528\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u89d1\u4e2a\u771f\u5207\uff0c\u9a88\u6307\u52a0\u621f\uff0c\u70b9\u90a3\u519b\u5b98\u80f8\u53e3\u7684 \u201c\u81bb\u4e2d\u7a74\u201d\uff0c\u70b9\u662f\u70b9\u4e2d\u4e86\uff0c\u53ef\u60dc\u4e0d\u662f\u5f88\u51c6\uff0c\u53ea\u70b9\u7740\u7a74\u9053\u65c1\u8fb9\uff0c\u90a3\u519b\u5b98\u53ea\u89c9\u80f8\u53e3\u4e00\u9ebb\uff0c\u4f46\u5374\u672a \u81f3\u4e8e\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u8fd9\u519b\u5b98\u662f\u8fb9\u5173\u603b\u5175\u7684\u536b\u58eb\u51fa\u8eab\uff0c\u4ed6\u867d\u7136\u4e0d\u61c2\u70b9\u7a74\uff0c\u4f46\u4e5f\u89c1\u8fc7\u8fd9\u95e8\u529f\u592b\u7684\u3002\u80f8\u53e3\u53d1\u9ebb\uff0c\u5927 \u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u4e0d\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u53ea\u6015\u5f53\u771f\u8981\u6b7b\u5728\u8fd9\u4e2b\u5934\u624b\u4e0a\u3002\u201d\u767b\u65f6\u62d4\u51fa\u8170\u5200\uff0c\u6076 \u72e0\u72e0\u7684\u5411\u949f\u7075\u79c0\u780d\u6765\u3002 \u3000\u3000\u949f\u7075\u79c0\u7a7a\u624b\u62b5\u6321\u4e0d\u4f4f\uff0c\u53ea\u597d\u4e5f\u62d4\u51fa\u77ed\u5251\u548c\u4ed6\u53ae\u6740\u3002\u90a3\u519b\u5b98\u780d\u4e0d\u7740\u949f\u7075\u79c0\uff0c\u5374\u7ed9\u949f\u7075\u79c0\u4e00 \u5251\u524a\u53bb\u4ed6\u7684\u534a\u5e45\u8863\u8896\u3002\u949f\u7075\u79c0\u559d\u9053\uff1a\u201c\u5ff5\u5728\u4f60\u9001\u6211\u5927\u54e5\u54e5\u4e0a\u5c71\u7684\u4efd\u4e0a\uff0c\u6211\u653e\u4f60\u8d70\uff0c\u4f60\u83ab\u8981\u656c \u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff01\u201d\u90a3\u519b\u5b98\u5df2\u7ecf\u803d\u6401\u4e86\u4e00\u5929\u884c\u7a0b\uff0c\u90a3\u80af\u8f7b\u6613\u7f62\u4f11\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u5b9a\u662f\u8981\u72af\uff0c\u6349 \u4ed6\u56de\u53bb\uff0c\u6211\u8fd8\u53ef\u4ee5\u5c06\u529f\u8865\u8fc7\u3002\u5426\u5219\u5982\u4f55\u5411\u603b\u5175\u4ea4\u5f85\uff1f\u201d\u4ed6\u60c5\u77e5\u6253\u4e0d\u8fc7\u8fd9\u4e2a\u201c\u4e2b\u5934\uff0c\u4e00\u4e2a\u8f6c \u8eab\uff0c\u8170\u5200\u5411\u6a80\u7fbd\u51b2\u780d\u4e0b\u3002\u559d\u9053\uff1a\u201c\u81ed\u4e2b\u5934\uff0c\u4f60\u8981\u4e0d\u8981\u4ed6\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u5200\u5288\u7740\u5730\u4e0a\u7684\u77f3\u5934\uff0c\u4ed6\u672c\u6765\u60f3\u5413\u4e00\u5413\u949f\u7075\u79c0\u7684\uff0c\u949f\u7075\u79c0\u5927\u60ca\u4e4b\u4e0b\uff0c \u77ed\u5251\u98de\u51fa\uff0c\u63d2\u5165\u4ed6\u7684\u540e\u5fc3\u3002\u519b\u5b98\u5927\u53eb\u4e00\u58f0\uff0c\u6251\u5230\u6a80\u7fbd\u51b2\u8eab\u4e0a\uff0c\u627c\u7740\u6a80\u7fbd\u51b2\u5589\u5499\u3002\u4f46\u4ed6\u88ab\u4e00\u5200 \u4f24\u7740\u8981\u5bb3\uff0c\u6c14\u529b\u98de\u5feb\u6d88\u5931\uff0c\u949f\u7075\u79c0\u8dd1\u8fc7\u6765\u4e00\u811a\u5c06\u4ed6\u8e22\u5f00\uff0c\u628a\u6a80\u7fbd\u51b2\u6276\u8d77\u6765\u63a2\u4e00\u68f5\u4ed6\u7684\u9f3b\u606f\uff0c \u89c1\u4ed6\u8fd8\u5728\u547c\u5438\uff0c\u60ca\u9b42\u65b9\u59cb\u7a0d\u5b9a\u3002\u56de\u5934\u770b\u65f6\u3002\u53ea\u89c1\u90a3\u519b\u5b98\u5df1\u5012\u5728\u8840\u6cca\u4e4b\u4e2d\uff0c\u6b7b\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u867d\u66fe\u5728\u4e8e\u67f3\u5e84\u4e2d\u7ecf\u8fc7\u4e00\u573a\u8840\u6218\uff0c\u4f46\u4eb2\u624b\u6740\u4eba\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\uff0c\u5979\u5185\u5fc3\u751a\u611f\u6b49\u759a\uff0c\u5bf9 \u90a3\u519b\u5b98\u7684\u5c38\u4f53\u62dc\u4e86\u4e00\u62dc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u83ab\u602a\u6211\u6069\u5c06\u4ec7\u62a5\uff0c\u6211\u4e0d\u6740\u4f60\uff0c\u6211\u5927\u54e5\u54e5\u7684\u6027\u547d\u53ef\u4e0d\u80fd\u4fdd \u5168\u3002\u201d\u53d6\u4e86\u90a3\u519b\u5b98\u7684\u5e72\u7cae\uff0c\u80cc\u8d77\u6a80\u7fbd\u51b2\u7ee7\u7eed\u767b\u5c71\u3002 \u3000\u3000\u5230\u4e86\u5c71\u9876\uff0c\u53ea\u89c1\u4e91\u5c01\u96fe\u7ed5\uff0c\u4e0d\u7981\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u6709\u70b9\u62c5\u5fe7\uff1a\u201c\u8fd9\u5730\u65b9\u53ef\u771f\u662f\u907f\u96be\u7684\u6700\u597d \u6240\u5728\uff0c\u5018\u82e5\u80fd\u591f\u548c\u5927\u54e5\u54e5\u5728\u6b64\u6e21\u8fc7\u4e00\u751f\uff0c\u6211\u4e5f\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u53ea\u662f\u8fd9\u70b9\u5e72\u7cae\uff0c\u8fc7\u51e0\u5929\u5c31\u4f1a\u5403 \u5b8c\uff0c\u600e\u4e48\u529e\u5462\uff1f\u968f\u5373\u60f3\u8d77\u6bcd\u4eb2\u7684\u8bdd\uff1a\u201c\u5a18\u4eb2\u5e38\u8bf4\u5728\u5c71\u9760\u5c71\uff0c\u5728\u6c34\u9760\u6c34\uff0c\u4ec0\u4e48\u5730\u65b9\u90fd\u53ef\u4ee5\u517b\u6d3b \u4eba\u7684\u3002\u6211\u6709\u4e24\u53ea\u624b\uff0c\u4e0d\u76f8\u4fe1\u5c31\u4f1a\u997f\u6b7b\u3002\u201d\u4f46\u60f3\u8d77\u6bcd\u4eb2\uff0c\u5374\u7981\u4e0d\u4f4f\u53c8\u662f\u4e00\u9635\u5fc3\u9178\u4e86\u3002\u5979\u7684\u6cea\u6c34 \u6ef4\u5728\u6a80\u7fbd\u51b2\u8138\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u5c31\u552f\u6709\u54b1\u4eec\u76f8\u4f9d\u4e3a\u547d\u4e86\u3002\u6211\u8fd9 \u4e2a\u5c0f\u59b9\u5b50\u8fd8\u662f\u9700\u8981\u4f60\u7684\u7167\u987e\u7684\uff0c\u4f60\u53ef\u5f97\u5feb\u70b9\u597d\u8d77\u6765\u554a\uff01\u201d \u3000\u3000xXX \u3000\u3000\u6a80\u7fbd\u51b2\u5f00\u59cb\u6709\u4e86\u77e5\u89c9\uff0c\u53ea\u89c9\u6709\u4e00\u56e2\u8f6f\u7ef5\u7ef5\u7684\u4e1c\u897f\u4f0f\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u6162\u6162\u5f20\u5f00\u773c\u775b\u3002 \u3000\u3000\u949f\u7075\u79c0\u6b63\u5728\u628a\u56bc\u788e\u7684\u4eba\u53c2\u5582\u7ed9\u4ed6\u5403\uff0c\u90a3\u652f\u7c97\u5982\u513f\u81c2\u7684\u4eba\u53c2\u53ea\u5269\u4e0b\u5c0f\u6307\u5934\u7c97\u7ec6\u7684\u4e00\u622a\u4e86\u3002 \u3000\u3000\u5979\u89c1\u6a80\u7fbd\u51b2\u5f20\u5f00\u773c\u775b\uff0c\u53c8\u7f9e\u53c8\u559c\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u597d\u5566\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u9192\u6765\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u611f\u89c9\u6709\u7518\u51c9\u7684\u6db2\u4f53\u6d41\u5165\u4ed6\u7684\u54bd\u5589\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u662f\u5728\u7fe0\u5c4f\u5c71\u4e0a\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7684\u8eab\u4f53\u4ecd\u7136\u50f5\u786c\uff0c\u53ea\u6709\u773c\u775b\u53ef\u4ee5\u8f6c\u52a8\uff0c\u770b\u7740\u7af9\u548c\u8305\u8349\u642d\u7684\u5c4b\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5bb6\u4eba \u5bb6\u662f\u4ec0\u4e48\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u662f\u522b\u4eba\u7684\uff0c\u662f\u54b1\u4eec\u81ea\u5df1\u7684\u5bb6\uff0c\u4f60\u770b\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u4f60\u642d\u8d77\u6765\u7684\uff0c\u6211\u6c89\u7761\u4e86\u51e0\u5929\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u6709\u4e03\u5929\u4e03\u591c\u4e0d\u7701\u4eba\u4e8b\u4e86\uff0c\u771f\u662f\u5413\u4eba\u3002\u5927\u54e5\u54e5\uff0c\u4f60\u997f\u4e0d\u997f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u771f\u7684\u5417\uff0c\u6211\u5df2\u7ecf\u660f\u8ff7\u4e86\u4e03\u5929\uff1f\u5c0f\u59b9\u5b50\uff0c\u771f\u662f\u8f9b\u82e6\u4f60\u4e86\u3002\u6211\u8fd8\u672a\u611f \u89c9\u997f\u5462\uff0c\u4f60\u7ed9\u6211\u5403\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u8138\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u56bc\u70c2\u7684\u4eba\u53c2\uff0c\u6211\u53ea\u80fd\u8fd9\u6837\u5582\u7ed9\u4f60\u5403\uff0c\u4f60\u4e0d\u5acc\u80ae\u810f\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u4ef2\u8eab\u4f53\u4e0d\u80fd\u8f6c\u52a8\uff0c\u4e24\u9897\u6cea\u73e0\u5374\u5df2\u593a\u7736\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u59b9\u5b50\uff0c\u6211\u672a\u80fd\u7167\u987e\u4f60\uff0c\u53cd\u800c \u7d2f\u4f60\u4e3a\u6211\u64cd\u52b3\u3002\u597d\u59b9\u5b50\uff0c\u4f60\u771f\u662f\u6bd4\u6211\u7684\u4eb2\u59b9\u5b50\u8fd8\u4eb2\u3002\u6211\uff0c\u6211\u4e0d\u77e5\u5e94\u8be5\u5982\u4f55\u62a5\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u63a9\u7740\u4ed6\u7684\u5634\uff0c\u4e0d\u8bb8\u4ed6\u628a\u201c\u62a5\u7b54\u201d\u4e8c\u5b57\u8bf4\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u65e2\u7136\u628a\u6211\u5f53\u4f5c \u4eb2\u59b9\u5b50\u770b\u5f85\uff0c\u90a3\u8fd8\u4f55\u987b\u8bf4\u4ec0\u4e48\u5ba2\u6c14\u8bdd\u5462\u3002\u8bf4\u5ba2\u6c14\u8bdd\uff0c\u5c31\u662f\u628a\u6211\u5f53\u4f5c\u5916\u4eba\u4e86\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u8981\u5b89 \u5fc3\u517b\u75c5\uff0c\u4e0d\u53ef\u80e1\u601d\u4e71\u60f3\u3002\u522b\u5fd8\u4e86\u4f60\u6709\u4e00\u4e2a\u59b9\u5b50\uff0c\u5979\u9700\u8981\u4f60\u7167\u987e\u7684\u65e5\u5b50\u8fd8\u957f\u7740\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u4e2d\u611f\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u7ecf\u8fc7\u8fd9\u573a\u60a3\u96be\uff0c\u4f60\u597d\u50cf\u4e00\u4e0b\u5b50\u5c31\u957f\u5927\u8bb8\u591a\u4e86\u3002\u597d\uff0c \u5927\u54e5\u54e5\u542c\u4f60\u7684\u8bdd\uff0c\u75c5\u597d\u4e86\u5c31\u5e26\u4f60\u53bb\u770b\u5317\u56fd\u98ce\u5149\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u521a\u521a\u9192\u6765\uff0c\u522b\u8bf4\u592a\u591a\u7684\u8bdd\uff0c\u4f60\u6b47\u4e00\u6b47\uff0c\u6211\u7ed9\u4f60\u51c6\u5907\u4eca\u665a\u7684\u665a\u9910\uff0c\u4e03\u5929\u6765 \u4f60\u6ef4\u6c34\u4e0d\u8fdb\uff0c\u4eca\u665a\u4e5f\u8be5\u5403\u70b9\u4e1c\u897f\uff0c\u53ef\u4e0d\u80fd\u51c0\u5582\u4f60\u5403\u4eba\u53c2\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u5f04\u665a\u9910\u53bb\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u4f60\u5c31\u522b\u7ba1\u4e86\u3002\u77a7\u6211\u7684\u672c\u4e8b\u5427\u3002\u201d \u3000\u3000\u5979\u51fa\u53bb\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\u5c31\u56de\u6765\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u5c3e\u9c9c\u9c7c\u3002 \u3000\u3000\u201c\u5927\u54e5\u54e5\uff0c\u4eca\u665a\u6211\u5f04\u9c7c\u7fb9\u7ed9\u4f60\u559d\u597d\u4e0d\u597d\uff1f\u201d\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u679c\u7136\u597d\u672c\u4e8b\uff0c\u54ea\u91cc\u5f04\u6765\u7684\u9c9c \u9c7c\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u6211\u662f\u6e14\u5bb6\u5973\u5417\uff1f\u6253\u9c7c\u662f\u6211\u62ff\u624b\u672c\u4e8b\u3002\u5c71\u4e0a\u6709\u4e2a\u78a7\u6c34\u6f6d\uff0c\u6f6d\u91cc\u7684\u9c7c \u53ef\u591a\u5462\uff0c\u6211\u4e0d\u7528\u7f51\u4e5f\u53ef\u968f\u624b\u62ff\u8d77\u6765\u3002\u201d \u3000\u3000\u5979\u5f04\u597d\u9c7c\u5960\uff0c\u7528\u4e00\u4e2a\u65e9\u5df2\u5236\u6210\u7684\u6728\u5319\uff0c\u628a\u9c7c\u7fb9\u5582\u7ed9\u6a80\u7fbd\u51b2\u5403\uff0c\u4ed6\u5df2\u7ecf\u80fd\u591f\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5403 \u4e00\u70b9\u6d41\u8d28\u7684\u4e1c\u897f\u662f\u5e94\u65e0\u56f0\u96be\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u4e9b\u7528\u5177\u90fd\u662f\u4f60\u81ea\u5df1\u5236\u9020\u7684\u5417\uff1f\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u5c71\u4e0a\u6709\u7684\u662f\u7af9\u6728\uff0c\u5c31\u5730\u53d6 \u6750\uff0c\u7528\u4e4b\u4e0d\u5c3d\u3002\u6211\u95f2\u7740\u6ca1\u4e8b\uff0c\u7528\u6728\u5934\u505a\u4e86\u676f\u76d8\u7897\u789f\uff0c\u7528\u7af9\u5b50\u505a\u4e86\u7b77\u5b50\u3001\u6905\u5b50\uff0c\u8fd8\u7f16\u4e86\u7af9\u5e2d\uff0c \u53ea\u662f\u7f3a\u6b20\u4e86\u4e00\u4e2a\u9505\uff0c\u53ea\u597d\u628a\u4e00\u4e2a\u6241\u5e73\u7684\u6c34\u58f6\uff0c\u628a\u58f6\u53e3\u5f04\u5bbd\u6765\u5145\u94c1\u9505\u716e\u7269\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u554a\uff0c\u4f60\u771f\u80fd\u5e72\uff0c\u90a3\u6c34\u58f6\u53c8\u662f\u54ea\u91cc\u5f04\u6765\u7684\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u522b\u53ea\u662f\u8d5e\u6211\uff0c\u8fd9\u9c7c\u7fb9\u597d\u4e0d\u597d\u5403\u3002\u4f60\u5403\u9971\u4e86\uff0c\u6211\u518d\u628a\u6c34\u58f6\u7684\u4e8b\u60c5\u6162\u6162\u544a\u8bc9 \u4f60\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8d5e\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u5f04\u7684\u9c7c\u7fb9\u771f\u597d\u5403\uff0c\u6bd4\u6211\u5728\u897f\u6e56\u697c\u5916\u697c\u5403\u8fc7\u7684\u8457\u540d\u5b8b\u5ac2\u9c7c\u7fb9\u8fd8 \u597d\u5403\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u7c89\u8138\u7efd\u51fa\u82b1\u6735\u4f3c\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u662f\u8ba8\u6211\u559c\u6b22\u7684\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\u9a97\u4f60\uff0c\u8fd9\u662f\u6211\u6709\u751f\u4ee5\u6765\u5403\u7684\u6700\u597d\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u90a3\u4e5f\u662f\u56e0\u4e3a\u4f60\u997f\u4e86\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u7136\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u90a3\u4f60\u56e0\u4f55\u53f9\u6c14\uff1f\u4e0d\u662f\u60f3\u5fc3\u4e8b\uff0c\u5c31\u662f\u5acc\u6211\u8fd9\u9c7c\u7fb9\u4e0d\u597d\u5403\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u9c7c\u7fb9\u7684\u786e\u6bd4\u897f\u6e56\u7684\u5b8b\u5ac2\u9c7c\u7fb9\u597d\u5403\uff0c\u6211\u53ea\u4e0d\u8fc7\u56e0\u5b83\u800c\u751f\u4e00\u70b9\u5c0f\u5c0f\u7684\u611f\u89e6\u7f62 \u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4ec0\u4e48\u611f\u89e6\uff1f\u96be\u9053\u4e0d\u53ef\u4ee5\u5bf9\u6211\u8bf4\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u897f\u6e56\u771f\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u53ea\u53ef\u60dc\u6211\u4eca\u751f\u662f\u4e0d\u80fd\u518d\u5230\u897f\u6e56\u4e86\u3002\u4f60\u672c\u6765\u5bb6\u4f4f\u897f\u6e56 \u8fb9\uff0c\u6211\u4e5f\u7d2f\u5f97\u4f60\u6709\u5bb6\u5f52\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u53ea\u8981\u4f60\u548c\u6211\u5728\u4e00\u8d77\uff0c\u8fd9\u4e2a\u8352\u5c71\u5c31\u80dc\u8fc7\u897f\u6e56\u3002\u4f46\u5927\u54e5\u54e5\uff0c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\u4f60 \u7684\u5fc3\u4e8b\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u56e0\u4e3a\u548c\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u95f9\u7ffb\uff0c\u5fc3\u4e2d\u8fd8\u5728\u6094\u6068\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u4e2d\u4e86\u6a80\u7fbd\u51b2\u7684\u5fc3\u4e8b\uff0c\u4ed6\u7981\u4e0d\u4f4f\u82e6\u7b11\u9053\uff1a\u201c\u5c82\u53ea\u95f9\u7ffb\uff0c\u6211\u8fd8\u6740\u4e86\u4ed6\u4eec\u7684\u4eba\u5462\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60\u7684\u96be\u8fc7\u7684\u3002\u56e0\u4e3a\u6211\u4e5f\u66fe\u88ab\u8feb\u6740\u4eba\u3002\u201d \u3000\u3000\u5979\u628a\u6740\u4e86\u90a3\u4e2a\u519b\u5b98\u7684\u4e8b\u60c5\uff0c\u544a\u8bc9\u6a80\u7fbd\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6241\u53e3\u5927\u6c34\u58f6\u5c31\u662f\u90a3\u4e2a\u519b\u5b98\u7684\uff0c\u6211 \u5229\u7528\u4ed6\u5e2e\u4e86\u6211\u7684\u5fd9\uff0c\u5403\u4e86\u4ed6\u7684\u5e72\u7cae\uff0c\u62ff\u4e86\u4ed6\u7684\u4e1c\u897f\uff0c\u7ed3\u679c\u6211\u8fd8\u662f\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u662f\u4e3a\u4e86\u4fdd\u5168\u627e\u7684\u7f18\u6545\u624d\u6740\u4ed6\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u7ba1\u8fd9\u7b14\u5e10\u600e\u4e48\u7b97\uff0c\u6211\u53ea\u662f\u60f3\u4f60\u660e\u767d\uff0c\u6709\u65f6\u771f\u662f\u4f1a\u88ab\u8feb\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9ed8\u7136\u4e0d\u8bed\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u522b\u4eba\u4e0d\u4f1a\u50cf\u4f60\u8fd9\u6837\uff0c\u8bbe\u8eab\u5904\u5730\uff0c\u4e3a\u6211\u7740\u60f3\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u54b1\u4eec\u4f46\u6c42\u95ee\u5fc3\u65e0\u6127\uff0c\u53c8\u4f55\u5fc5\u5b9a\u8981\u522b\u4eba\u8c05\u89e3\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u4e0d\u7406\u4f1a\u522b \u4eba\uff0c\u522b\u4eba\u53ef\u7406\u4f1a\u4f60\uff0c\u9664\u975e\u54b1\u4eec\u4ece\u6b64\u4e0d\u5728\u6c5f\u6e56\u9732\u9762\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u820d\u4e0d\u5f97\u5916\u9762\u7684\u7e41\u534e\u4e16\u754c\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u770b\u6211\u662f\u604b\u5e55\u7e41\u534e\u7684\u4eba\u4e48\uff1f\u5bcc\u8d35\u3001\u7e41\u534e\uff0c\u5728\u6211\u90fd\u4e0d\u8fc7\u5982\u4e91\u70df\u8fc7\u773c\u3002\u6211\u53ea\u662f \u6e10\u6127\u81ea\u5df1\u4e00\u4e8b\u65e0\u6210\uff0c\u8f9c\u8d1f\u4e86\u5a18\u4eb2\u548c\u5e08\u7236\u7684\u671f\u671b\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u6bd5\u7adf\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u672a\u80fd\u7406\u89e3\u4ed6\u7684\u80f8\u4e2d\u62b1\u8d1f\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u820d\u5f97\uff0c\u90a3\u4e0d\u5c31\u6210\u4e86 \u5417\uff1f\u54b1\u4eec\u5728\u8fd9\u5c71\u4e0a\u9690\u5c45\uff0c\u907f\u5f00\u90a3\u4e9b\u4eba\u4e5f\u5c31\u662f\u4e86\u3002\u5f85\u4f60\u517b\u597d\u4e86\u4f24\uff0c\u54b1\u4eec\u8fd8\u53ef\u4ee5\u9009\u4e00\u5904\u98ce\u666f\u6700\u597d \u7684\u5730\u65b9\u5efa\u4e00\u95f4\u77f3\u5c4b\uff0c\u4f60\u6253\u730e\uff0c\u6211\u6355\u9c7c\uff0c\u65e0\u5fe7\u65e0\u8651\u7684\u8fc7\u65e5\u5b50\u3002\u4f60\u8bf4\u53ef\u597d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u7070\u610f\u51b7\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u8fde\u6307\u5934\u90fd\u4e0d\u80fd\u52a8\u4e00\u6839\uff0c\u54ea\u91cc\u8fd8\u80fd\u884c\u8d70\u6c5f\u6e56\uff1f\u4f60\u8bf4 \u7684\u90a3\u79cd\u65e5\u5b50\u662f\u6211\u8fde\u60f3\u4e5f\u60f3\u4e0d\u5230\u7684\u3002\u5c31\u53ea\u6015\u4f60\u60f3\u5f97\u592a\u5982\u610f\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u96be\u4e8b\uff0c\u53ea\u6015\u6709\u5fc3\u4eba\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c31\u53ea\u6015\u6211\u6709\u5fc3\u65e0\u529b\u3002\u6211\u8fd9\u6761\u6027\u547d\u662f\u68c0\u56de\u6765\u7684\uff0c\u4e5f\u4e0d\u77e5\u80fd\u6d3b\u5230\u51e0\u65f6\uff1f\u80fd\u591f\u6d3b \u4e0b\u53bb\uff0c\u4e5f\u53ea\u6015\u8981\u53d8\u6210\u6b8b\u5e9f\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u6211\u6253\u730e\u3001\u4f60\u6355\u9c7c\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4f1a\u957f\u547d\u767e\u5c81\u7684\uff01\u201d \u3000\u3000\u6a80\u7fbd\u4f38\u7b11\u9053\uff1a\u201c\u6211\u957f\u547d\u767e\u5c81\uff0c\u90a3\u4f60\u7684\u9ebb\u70e6\u53ef\u5c31\u5927\u4e86\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u53d8\u6210\u6b8b\u5e9f\uff0c\u6211\u5c31\u670d\u4f8d\u4f60\u4e00\u751f\u3002\u6211\u4e0d\u6015\u9ebb\u70e6\uff0c\u4f60\u4e0d\u8981\u6211\u670d\u5f85\uff0c\u6211\u624d \u96be\u8fc7\u5462\uff0c\u5c31\u6015\u4f60\u5bf9\u7740\u6211\u8ba8\u538c\u3002\u201d \u3000\u3000\u60c5\u771f\u610f\u8bda\uff0c\u6a80\u7fbd\u51b2\u662f\u4e0d\u5fcd\u4f24\u5979\u7684\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u61c2\u5f97\uff0c\u53e4\u4eba\u628a\u806a\u660e\u4f36\u4fd0\u7684\u5973\u5b69\u5b50 \u6bd4\u4f5c\u89e3\u8bed\u82b1\uff0c\u90a3\u771f\u662f\u6709\u9053\u7406\u7684\uff0c\u5c0f\u59b9\u5b50\uff0c\u6709\u4eba\u966a\u7740\u6211\uff0c\u6211\u82e5\u8fd8\u611f\u5230\u5bc2\u5bde\uff0c\u90a3\u6211\u5c31\u662f\u6700\u4e0d\u77e5\u8db3 \u7684\u4eba\u4e86\uff0c\u4e0d\u8fc7\u4f60\u4e5f\u6709\u4e00\u70b9\u4e0d\u597d\u201d\uff01\u949f\u7075\u79c0\u4e00\u6014\u9053\uff1a\u201c\u54ea\u4e00\u70b9\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8650\u5f85\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u54e6\uff0c\u6211\u8650\u5f85\u8c01\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8650\u5f85\u6211\u7684\u5c0f\u59b9\u5b50\uff01\u4f60\u53ea\u77e5\u7167\u6599\u6211\uff0c\u5374\u4e0d\u987e\u81ea\u5df1\uff0c\u8fd9\u70b9\u6700\u4e0d\u597d\uff0c\u6211\u5df2\u7ecf\u5403\u4e86 \u9c7c\u7fb9\uff0c\u4f60\u8fd8\u6ca1\u6709\u5403\u4e1c\u897f\u5462\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u4f60\u6015\u6211\u6ca1\u4e1c\u897f\u5417\uff0c\u4f60\u5c11\u64cd\u5fc3\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u597d\u50cf\u53ea\u62ff\u4e86\u4e00\u6761\u9c9c\u9c7c\u56de\u6765\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u8fd8\u6709\u597d\u4e1c\u897f\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8eba\u7740\uff0c\u8eab\u5b50\u4e0d\u80fd\u52a8\u5f39\uff0c\u770b\u4e0d\u89c1\u5979\u7684\u52a8\u4f5c\uff0c\u53ea\u95fb\u5f97\u4e00\u80a1\u9999\u6c14\u3002 \u3000\u3000\u201c\u597d\u9999\uff0c\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u6a80\u7fbd\u51b2\u95ee\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u662f\u5c71\u828b\u3002\u8fd9\u5c71\u4e0a\u53ef\u5403\u7684\u4e1c\u897f\u591a\u5462\uff0c\u6709\u91ce\u751f\u7684\u679c\u5b50\uff0c\u6709\u4fef\u62fe\u5373\u662f\u5404\u79cd\u83cc\u7c7b\uff0c \u4f46\u662f\u80fd\u5145\u9965\u7684\u8fd8\u662f\u91ce\u751f\u7684\u5c71\u828b\u3002\u70e4\u719f\u4e86\uff0c\u9999\u55b7\u55b7\u7684\u6bd4\u767d\u7c73\u8fd8\u597d\u5403\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u771f\u7684\uff0c\u6211\u90fd\u7ed9\u4f60\u8bf4\u5f97\u5782\u6d8e\u4e86\uff0c\u53ea\u53ef\u60dc\u6211\u73b0\u5728\u8fd8\u5403\u4e0d\u52a8\u5b83\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u60f3\u5403\u4e1c\u897f\uff0c\u90a3\u5c31\u4f1a\u5f88\u5feb\u597d\u4e86\u3002\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5403\u7684\u5bb9\u6613\uff0c\u7a7f\u7684\u96be\uff0c\u6211\u968f\u8eab\u5e26\u7684\u5305\u88b1\uff0c\u5728\u5343\u67f3\u5e84\u4e22\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u7684\u80cc\u56ca\u5462\uff0c\u6211\u6740\u51fa\u5343\u67f3\u5e84\u7684\u65f6\u5019\uff0c\u597d\u50cf\u6ca1\u6709\u4e22\u7684\uff0c\u4e0d\u77e5\u53ef\u8fd8\u5728\u5426\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd8\u5728\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e09\u5957\u8863\u88f3\uff0c\u8eab\u4e0a\u7a7f\u7684\u4e00\u5957\uff0c\u80cc\u56ca\u8fd8\u6709\u4e24\u5957\u3002\u4f60\u53ef\u62ff\u53bb\u66ff\u6362\u3002\u867d\u7136\u4e0d \u79f0\u8eab\uff0c\u53cd\u6b63\u6ca1\u4eba\u77a7\u89c1\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e48\uff1f\u6211\u6bd4\u4f60\u7626\u5c0f\uff0c\u7a7f\u4e0a\u4f60\u7684\u8863\u88f3\uff0c\u90a3\u5f62\u72b6\u4e00\u5b9a\u6ed1\u7a3d\u53ef\u7b11\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u6211\u662f\u4f60\u7684\u5927\u54e5\u54e5\uff0c\u4f60\u7a7f\u4e0a\u4ec0\u4e48\u8863\u88f3\uff0c\u7537\u88c5\u4e5f\u597d\uff0c\u5973\u88c5\u4e5f\u597d\uff0c\u6211\u90fd\u89c9\u5f97\u597d \u770b\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5bf9\uff0c\u6211\u4e5f\u4e0d\u662f\u7a7f\u7ed9\u522b\u4eba\u770b\u7684\uff0c\u53ea\u8981\u4f60\u8bf4\u597d\u770b\u5c31\u6210\u3002\u201d\u5979\u559c\u5b5c\u5b5c\u7684\u7ee7\u7eed\u8bf4 \u9053\uff1a\u201c\u4f4f\u4e0b\u53bb\u518d\u60f3\u529e\u6cd5\uff0c\u6211\u4f1a\u7eba\u7eb1\u7ec7\u5e03\uff0c\u6211\u4e5f\u61c2\u5f97\u88c1\u8863\u88f3\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u771f\u662f\u6837\u6837\u7686\u80fd\u3002\u51ed\u7740\u4f60\u8fd9\u53cc\u624b\uff0c\u8981\u662f\u5728\u8fd9\u91cc\u4f4f\u4e0a\u5341\u5e74\u516b\u5e74\uff0c\u53ea \u6015\u8352\u5c71\u4e5f\u4f1a\u53d8\u6210\u4e50\u56ed\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u591a\u8c22\u5927\u54e5\u54e5\u5938\u5956\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u73b0\u5728\uff0c\u8fd9\u4e2a\u8352\u5c71\u5df2\u7ecf\u662f\u6211\u4eec\u7684\u4e50\u56ed\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f46\u8fd8\u4e0d\u4e00\u6837\uff0c\u4f60\u867d\u7136\u4e5f\u61c2\uff0c\u6211\u5374\u60f3\u8ba9\u4f60\u591a\u61c2\u4e00\u4e9b\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u662f\u54ea\u4e00\u6837\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u662f\u6b66\u529f\uff0c\u4f60\u5df2\u7ecf\u6740\u4e86\u4e00\u4e2a\u519b\u5b98\uff0c\u96be\u4fdd\u6ca1\u6709\u7b2c\u4e8c\u4eba\u6765\u7684\u201d \u3000\u3000\u949f\u7075\u79c0\u5168\u51ed\u673a\u667a\uff0c\u6740\u6389\u90a3\u4e2a\u519b\u5b98\uff0c\u60f3\u8d77\u6b64\u4e8b\uff0c\u5fc3\u4e2d\u72b9\u6709\u4f59\u60b8\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5b66\u597d\u6b66\u529f\uff0c \u5c31\u4e0d\u6015\u574f\u4eba\u6b3a\u4fae\u4e86\u3002\u5927\u54e5\u54e5\uff0c\u5f85\u4f60\u517b\u597d\u4f24\uff0c\u5c31\u6559\u7ed9\u6211\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u73b0\u5728\u53ef\u4ee5\u6559\u4f60\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u73b0\u5728\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u73b0\u5728\uff0c\u73b0\u5728\u6211\u7684\u8eab\u5b50\u867d\u7136\u4e0d\u80fd\u52a8\uff0c\u6211\u7684\u53e3\u8fd8\u80fd\u8bf4\u8bdd\uff0c\u6211\u53ef\u4ee5\u53e3\u6388\u6b66 \u529f\uff0c\u5148\u4f20\u4f60\u5185\u529f\u5fc3\u6cd5\uff0c\u5185\u529f\u5b66\u5f97\u597d\u4e86\uff0c\u4ee5\u540e\u5b66\u62db\u6570\u53ef\u4ee5\u4e8b\u534a\u529f\u500d\uff01\u201d \u3000\u3000\u4ece\u90a3\u5929\u8d77\uff0c\u6a80\u7fbd\u51b2\u5f00\u59cb\u53e3\u6388\u6b66\u529f\u3002\u949f\u7075\u79c0\u4eba\u6781\u806a\u660e\uff0c\u672c\u6765\u662f\u6df1\u5965\u590d\u6742\u7684\u4e0a\u4e58\u6b66\u529f\u5fc3\u6cd5\uff0c \u5979\u51e0\u4e4e\u4e5f\u80fd\u4e00\u70b9\u5373\u901a\u3002\u4e0d\u77e5\u4e0d\u89c9\u7684\u8fc7\u4e86\u4e09\u4e2a\u6708\u3002\u5979\u7684\u5185\u52a8\u5df2\u7ecf\u9887\u6709\u57fa\u7840\u4e86\u3002 \u3000\u3000\u4f46\u6a80\u7fbd\u51b2\u5374\u597d\u5f97\u5f88\u6162\u3002\u4ed6\u7684\u5185\u4f24\u5b9e\u5728\u592a\u91cd\uff0c\u7ecf\u8fc7\u4e09\u4e2a\u6708\u7684\u8c03\u6cbb\uff0c\u4e5f\u672a\u80fd\u4e0b\u5730\uff0c\u53ea\u4e0d\u8fc7\u53ef \u4ee5\u5750\u8d77\u6765\u800c\u5df2\u3002\u4ed6\u7684\u4e00\u53cc\u624b\u8fd8\u597d\u4e00\u4e9b\uff0c\u4e5f\u53ef\u4ee5\u5c48\u4f38\u4e86\uff0c\u4e00\u53ea\u811a\u5374\u662f\u4f9d\u7136\u50f5\u786c\uff0c\u52a8\u4e0d\u4e86\u5206\u6beb\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u8bf4\uff0c\u949f\u7075\u79c0\u4e5f\u53ef\u4ee5\u770b\u51fa\u4ed6\u5185\u5fc3\u7126\u6025\u548c\u90c1\u95f7\u3002\u949f\u7075\u79c0\u60f3\u5c3d\u529e\u6cd5\u9017\u4ed6\u9ad8\u5174\uff0c\u7ed9\u4ed6 \u5531\u6c5f\u5357\u5c0f\u8c03\uff0c\u8fd8\u62ff\u8d77\u4ed6\u7684\u7389\u7bab\u5439\u7ed9\u4ed6\u542c\u3002\u6a80\u7fbd\u51b2\u6700\u559c\u6b22\u5979\u5439\u7bab\uff0c\u4f46\u5728\u542c\u5f97\u5165\u795e\u7684\u65f6\u5019\uff0c\u4e5f\u5e38 \u5e38\u4f1a\u9732\u51fa\u832b\u7136\u82e5\u5931\u7684\u5fc3\u60c5\u3002\u949f\u7075\u79c0\u4e03\u7a8d\u73b2\u73d1\uff0c\u61c2\u5f97\u4ed6\u5fc3\u4e2d\u7684\u611f\u53d7\uff0c\u201c\u5927\u54e5\u54e5\u8981\u662f\u6709\u4e00\u5929\u80fd\u591f \u81ea\u5df1\u5439\u8427\uff0c\u90a3\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u679c\u7136\u5982\u5979\u6240\u613f\uff0c\u6709\u4e00\u5929\u5979\u542c\u89c1\u4e86\u6a80\u7fbd\u51b2\u7684\u7bab\u58f0\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u5979\u4ece\u6f6d\u8fb9\u6d17\u8863\u670d\u56de\u6765\uff0c\u8fdc\u8fdc\u7684\u5c31\u542c\u89c1\u4e86\u60a0\u626c\u7684\u7bab\u58f0\u3002\u5439\u7684\u662f\u4e00\u9996\u6b63\u5728\u6c5f\u5357\u6d41\u884c\u7684 \u5c0f\u66f2\uff0c\u662f\u7531\u8f9b\u5f03\u75be\u7684\u4e00\u9996\u65b0\u8bcd\u300a\u5357\u6b4c\u5b50\u300b\u8c31\u6210\u7684\u3002\u8fd9\u652f\u66f2\u5b50\uff0c\u4e5f\u662f\u949f\u7075\u79c0\u6628\u5929\u624d\u5439\u8fc7\u7ed9\u4ed6\u542c \u7684\u3002\u949f\u7075\u79c0\u5fc3\u9053\uff1a\u201c\u5927\u54e5\u54e5\u771f\u806a\u660e\uff0c\u4e00\u542c\u5c31\u4f1a\u3002\u201d\u8033\u542c\u7bab\u9ed8\u5ff5\u66f2\u8bcd \u3000\u3000\u201c\u4e16\u4e8b\u4ece\u5934\u51cf\uff0c\u79cb\u6000\u5f7b\u5e95\u6e05\u3002\u591c\u6df1\u72b9\u9001\u6795\u8fb9\u58f0\uff0c\u8bd5\u95ee\u6e05\u6eaa\u5e95\u4e2d\u672a\u80fd\u5e73\uff1f \u3000\u3000\u6708\u5230\u6101\u8fb9\u767d\uff0c\u9e21\u5148\u8fdc\u5904\u9e23\u3002\u662f\u4e2d\u65e0\u6709\u5229\u548c\u540d\uff0c\u56e0\u751a\u5c71\u524d\u672a\u6653\u6709\u4eba\u884c\uff1f\u201d \u3000\u3000\u6709\u4eba\u89e3\u91ca\u8fd9\u9996\u8bcd\uff1a\u201c\u591c\u6df1\u4eba\u9759\uff0c\u6795\u8fb9\u4f20\u6765\u5e7d\u54bd\u8dcc\u5b95\u7684\u6eaa\u6c34\u58f0\uff0c\u8fd9\u4eff\u4f5b\u5728\u4e3a\u4eba\u95f4\u503e\u8bc9\u4e0d \u5e73\u3002\u8fd9\u65f6\u65e9\u5df2\u6709\u4eba\u4fa7\u542c\u7740\u8fdc\u5904\u7684\u7b2c\u4e00\u58f0\u9e21\u53eb\uff0c\u6101\u770b\u7740\u811a\u4e0b\u82cd\u767d\u7684\u6708\u8272\uff0c\u5f00\u59cb\u5728\u574e\u5777\u4e0d\u5e73\u7684\u5c71 \u8def\u4e0a\u4e3a\u751f\u6d3b\u8f9b\u82e6\u5954\u5fd9\u4e86\u3002\u4ed6\u4eec\u5e76\u975e\u4e3a\u4e86\u8ffd\u540d\u9010\u5229\uff0c\u7adf\u4e5f\u96be\u5f97\u7247\u523b\u5b89\u95f2\uff0c\u8bd7\u4eba\u4ece\u6df1\u591c\u7684\u6eaa\u6d41\uff0c \u542c\u51fa\u4e86\u4eba\u95f4\u7684\u4e0d\u5e73\u4e4b\u9e23\uff0c\u7531\u5c71\u524d\u7684\u65e9\u884c\u4eba\uff0c\u53d1\u51fa\u4e86\u8010\u4eba\u5bfb\u601d\u7684\u8bd8\u95ee\uff01\u201d\uff08\u5f15\u81ea\u5218\u4e43\u660c\u7684\u300a\u8f9b \u5f03\u75be\u8bba\u4e1b\u300b\uff09 \u3000\u3000\u8f9b\u5f03\u75be\u7684\u8bcd\u6709\u96c4\u58ee\u7684\u4e00\u9762\uff0c\u4e5f\u6709\u606c\u9759\u7684\u4e00\u9762\uff0c\u8fd9\u9996\u201c\u5357\u6b4c\u5b50\u201d\u662f\u6bd4\u8f83\u5c5e\u4e8e\u201c\u606c\u9759\u7684\u201d\u3002 \u867d\u7136\u5728\u606c\u9759\u4e4b\u4e2d\u4e5f\u9690\u85cf\u7740\u5173\u60c5\u6c11\u95f4\u75be\u82e6\u7684\u4e0d\u5e73\u3002\u4f46\u53ef\u60dc\u4f5c\u66f2\u7684\u4eba\u5374\u672a\u80fd\u4f53\u4f1a\u8bcd\u4eba\u7684\u6df1\u610f\uff0c\u8fd9 \u652f\u66f2\u5b50\uff0c\u662f\u88ab\u5904\u7406\u6210\u5e7d\u96c5\u6292\u60c5\u7684\u5c0f\u8c03\u7684\u3002\u4e0d\u8fc7\u6a80\u7fbd\u51b2\u7684\u7bab\u58f0\u8fd8\u662f\u628a\u8bcd\u4e2d\u9690\u85cf\u7684\u90a3\u79cd\u5fe7\u90c1\u7684\u5fc3 \u60c5\u5439\u51fa\u6765\u4e86\u3002\u6216\u8005\u4ed6\u4e5f\u672a\u4f53\u4f1a\u5f97\u90a3\u6837\u6df1\uff0c\u4ed6\u53ea\u662f\u5439\u51fa\u4e86\u81ea\u5df1\u5fc3\u4e2d\u7684\u5fe7\u90c1\u3002 \u3000\u3000\u949f\u7075\u79c0\u5ffd\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u6709\u6ca1\u6709\u94f6\u5b50\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8981\u94f6\u5b50\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5c71\u5357\u5341\u91cc\u5916\u6709\u4e2a\u5c0f\u9547\uff0c\u6709\u4e86\u94f6\u5b50\uff0c\u5c31\u53ef\u4ee5\u6362\u4e9b\u4e1c\u897f\u56de\u6765\u3002\u4f60\u5929\u5929\u5403\u5c71\u828b\uff0c \u6211\u6015\u4f60\u5403\u538c\u4e86\uff0c\u4e70\u70b9\u7c73\u9762\u56de\u6765\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u505a\u5e74\u7cd5\u3001\u5305\u997a\u5b50\u3001\u505a\u5927\u997c\u8fd8\u53ef\u4ee5\u505a\u6cb9\u6761\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u73b0\u5728\u5927\u6982\u624d\u4e0d\u8fc7\u7acb\u79cb\u5427\uff0c\u4f60\u5c31\u60f3\u5403\u5e74\u7cd5\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u7684\u8863\u88f3\u4e5f\u7834\u65e7\u4e86\uff0c\u8be5\u6362\u4e00\u6362\u5566\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4f60\u6362\u4e0a\u65b0\u8863\uff0c\u4f46 \u4f60\u8fd8\u662f\u4e0d\u8981\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u9547\u505a\u7684\u90fd\u662f\u719f\u6089\u4eba\u4e70\u5356\uff0c\u4f60\u662f\u4e2a\u8138\u5b54\u964c\u751f\u7684\u5916\u5730\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u6f02\u4eae\u7684 \u5c0f\u59d1\u5a18\uff0c\u4f60\u4e00\u53bb\u4e70\u4e1c\u897f\uff0c\u9a6c\u4e0a\u5c31\u4f1a\u7ed9\u4eba\u6ce8\u610f\u3002\u201d\u949f\u7075\u79c0\u9053\u3002\u201d\u8c01\u8bf4\u6211\u8981\u4e70\u4e1c\u897f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e0d\u662f\u4f60\u521a\u624d\u81ea\u5df1\u8bf4\u7684\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u542c\u9519\u4e86\uff0c\u6211\u8bf4\u7684\u662f\u6362\uff0c\u4e0d\u662f\u4e70\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5206\u522b\u53ef\u5927\u5462\uff0c\u4e70\u4e1c\u897f\u5fc5\u987b\u9762\u5bf9\u9762\u7684\u8bb2\u4ef7\u94b1\uff0c\u6362\u4e1c\u897f\u5417\uff0c\u4e70\u5356\u53cc\u65b9\u4e0d\u89c1\u9762\u4e5f \u884c\u7684\u3002\u4ef7\u94b1\u4e5f\u6ca1\u4e2a\u8c31\u513f\u3002\u4e0d\u8fc7\uff0c\u5f53\u7136\u6211\u662f\u4e0d\u4f1a\u5c11\u7ed9\u4eba\u5bb6\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u554a\uff01\u539f\u6765\u8fd9\u6837\uff0c\u6211\u61c2\u4e86\u3002\u4f60\u8bf4\u7684\u2018\u6362\u2019\u662f\u4ecb\u4e4e\u4e70\u4e0e\u5077\u4e4b\u95f4\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u600e\u4e48\u8bf4\u662f\u5077\uff0c\u867d\u7136\u6211\u662f\u4e0d\u95ee\u800c\u53d6\uff0c\u90a3\u5bb6\u4eba\u5bb6\u505a\u7684\u53ef\u662f\u8d5a\u94b1\u751f\u610f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u628a\u94f6\u5b50\u653e\u4e0b\uff0c\u62ff\u8d70\u4e1c\u897f\uff0c\u7b2c\u4e8c\u5929\u4eba\u5bb6\u53d1\u73b0\u4e86\uff0c\u5c82\u4e0d\u662f\u66f4\u8981\u95f9\u5f97\u6cb8\u6cb8\u626c \u626c\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u90a3\u5c0f\u9547\u6211\u867d\u7136\u6ca1\u6709\u4f4f\u8fc7\uff0c\u4f46\u6211\u77e5\u9053\u8fd9\u4e00\u5e26\u7684\u98ce\u4fd7\u662f\u548c\u8fb9\u5173\u90a3\u8fb9\u7684\u6c49\u4eba\u98ce \u4fd7\u76f8\u540c\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u6765\u662f\u5b8b\u56fd\u7684\u5730\u65b9\uff0c\u4f4f\u7684\u53c8\u90fd\u662f\u6c49\u4eba\uff0c\u98ce\u4fd7\u5f53\u7136\u76f8\u540c\u4e86\u3001\u4f46\u61c2\u98ce\u4fd7\u548c \u4f60\u8981\u4e1c\u897f\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u98ce\u4fd7\u662f\u8ff7\u4fe1\u72d0\u4ed9\u7684\uff0c\u90a3\u5bb6\u4eba\u5bb6\u5f97\u5230\u4e86\u597d\u5904\uff0c\u591a\u534a\u4f1a\u4ee5\u4e3a\u662f\u72d0\u4ed9\u6240 \u8d50\u3001\u4e0d\u4f1a\u8bf4\u51fa\u6765\u7684\u3002\u800c\u4e14\u5373\u4f7f\u4e0d\u4fe1\u72d0\u4ed9\uff0c\u4ed6\u5f97\u4e86\u597d\u5904\uff0c\u6015\u4eba\u8ffd\u7a76\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u62db\u6765\u7978\u6b83\uff0c\u6599 \u60f3\u4ed6\u4e5f\u4f1a\u7792\u4f4f\u522b\u4eba\u3002\u201d\u6a80\u7fbd\u51b2\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7684\u4eba\u60c5\u4e16\u6545\u4e5f\u5c45\u7136\u6bd4\u6211\u8fd8\u61c2\u3002\u4f46\u53ef\u60dc\u2014\uff0d\u201d \u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u94f6\u5b50\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u53ea\u6709\u91d1\u4e8e\uff0c\u662f\u4e00\u9897\u9897\u7684\u5c0f\u91d1\u8c46\u3002\u201d\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u662f\u91d1\u5b50\u66f4\u597d\u4e86\uff0c\u4fa0\u76d7 \u51fa\u624b\u4e5f\u4e0d\u4f1a\u8fd9\u6837\u9614\u7ef0\u7684\uff0c\u4eba\u5bb6\u66f4\u4ee5\u4e3a\u662f\u72d0\u4ed9\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u53bb\u5c31\u53bb\uff0c\u53ef\u5f97\u5343\u4e07\u591a\u52a0\u5c0f\u5fc3\uff01\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8981\u4e0d\u662f\u6211\u8bd5\u51fa\u6211 \u7684\u8f7b\u529f\u5df2\u7ecf\u5927\u80dc\u4ece\u524d\uff0c\u8db3\u591f\u8d44\u683c\u505a\u98de\u8d3c\u7684\u8bdd\uff0c\u6211\u8fd8\u4e0d\u6562\u6253\u8fd9\u4e2a\u4e3b\u610f\u5462\u3002\u201d \u3000\u3000\u8fd9\u665a\u5979\u7a7f\u4e0a\u6a80\u7fbd\u51b2\u4e00\u5957\u9ed1\u8272\u7684\u8863\u88f3\u3002\u5f53\u4f5c\u591c\u884c\u8863\uff0c\u65bd\u5c55\u8f7b\u529f\u4e0b\u5c71\uff0c\u5929\u672a\u4eae\u5c31\u56de\u6765\u4e86\uff0c\u679c \u7136\u201c\u6362\u201d\u6765\u4e86\u8bb8\u591a\u4e1c\u897f\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u6ca1\u88ab\u4eba\u53d1\u73b0\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5bf9\u5f92\u5f1f\u8fd9\u6837\u6ca1 \u6709\u4fe1\u5fc3\uff1f\u201d\u6a80\u7fbd\u51b2\u518d\u95ee\uff1a\u201c\u5916\u95f4\u6709\u751a\u98ce\u58f0\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6362\u4e1c\u897f\u867d\u7136\u4e0d\u6bd4\u5077\u4e1c\u897f\u3002\u4f46\u4e5f\u662f\u5077\u5077\u6478\u6478\uff0c\u6211\u600e\u6562\u53bb\u6253\u542c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e24\u592b\u59bb\u8eb2\u5728\u623f\u95f4\u4e5f\u4f1a\u8c08\u8bba\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u53ef\u501f\u4f60\u7684\u5c0f\u59b9\u5b50\u80c6\u5b50\u5c0f\uff0c\u521d\u6b21\u51fa\u9053\uff0c\u53ea\u6015\u88ab\u4eba\u8bef\u4f1a\uff0c\u5f53\u4f5c\u5077\u513f\uff0c\u8981\u662f\u623f\u95f4 \u91cc\u8fd8\u542c\u5f97\u6709\u58f0\u97f3\u7684\u8bdd\uff0c\u6211\u5c31\u53ea\u80fd\u6e9c\u4e4b\u5927\u5409\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u9887\u4f3c\u6709\u6005\u7136\u4e4b\u611f\u3002 \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u597d\u50cf\u8fd8\u672a\u770b\u7834\u7ea2\u5c18\u5462\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u60f3\u8981\u7406\u4f1a\u5916\u95f4\u7684\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u95f7\u5f97\u53d1\u614c\uff0c\u542c\u542c\u5916\u95f4\u7684\u65b0\u9c9c\u4e8b\u513f\uff0c\u4e5f \u597d\u89e3\u95f7\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u6bcf\u5929\u5bf9\u7740\u6211\uff0c\u89c9\u5f97\u817b\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u8bf4\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6628\u665a\u4f60\u8d70\u4e86\u4e4b\u540e\uff0c\u6211\u8fd8\u6015\u4f60\u4e0d\u518d\u56de \u6765\u4e86\u5462\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u8ba8\u538c\u6211\uff0c\u6211\u5230\u6b7b\u7684\u90a3\u5929\u4e5f\u4e0d\u4f1a\u79bb\u5f00\u4f60\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53f9\u9053\uff1a\u201c\u6211\u5df2\u662f\u4e2a\u5e9f\u4eba\u4e86\uff0c\u4f60\u5e74\u7eaa\u8fd9\u6837\u8f7b\uff0c\u5018\u82e5\u771f\u7684\u8981\u4f60\u670d\u4f8d\u6211\u4e00\u751f\uff0c\u6211\u5012\u771f\u662f \u5b81\u613f\u65e9\u70b9\u6b7b\u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u8fd9\u6837\u8bf4\uff0c\u4f60\u73b0\u5728\u4e0d\u662f\u5df2\u7ecf\u4e00\u5929\u5929\u597d\u8d77\u6765\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u7684\u5947\u7ecf\u516b\u8109\u90fd\u53d7\u4e86\u4f24\uff0c\u5c24\u4ee5\u8db3\u5c11\u9633\u7ecf\u8109\u53d7\u4f24\u6700\u91cd\uff0c\u8981\u60f3\u6253\u901a \u7ecf\u8109\uff0c\u5148\u5f97\u4e00\u6b65\u6b65\u6062\u590d\u5185\u529f\uff0c\u8c08\u4f55\u5bb9\u6613\uff0c\u8fd9\u534a\u8eab\u4e0d\u9042\u4e4b\u75c7\uff0c\u6050\u6015\u662f\u6cbb\u4e0d\u597d\u7684\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u542c\u5f97\u4e00\u4e2a\u5927\u592b\u8bf4\u8fc7\uff0c\u75c5\u4eba\u8d8a\u4e0d\u628a\u81ea\u5df1\u7684\u75c5\u653e\u5728\u5fc3\u4e0a\uff0c\u4ed6\u5c31\u4f1a\u597d\u5f97\u8d8a\u5feb\uff0c \u8fd9\u53eb\u505a\u5b89\u5fc3\u517b\u75c5\u662f\u826f\u65b9\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4ece\u73b0\u5728\u8d77\uff0c\u6211\u5c31\u4e24\u8033\u4e0d\u95fb\u7a97\u5916\u4e8b\uff0c\u4e00\u5fc3\u4e00\u4e00\u55ef\uff0c\u6211\u53ef\u662f\u4e0d\u60f3\u4e13\u8bfb \u5723\u8d24\u4e66\u7684\uff0c\u90a3\u5c31\u4e00\u5fc3\u4e13\u7b49\u9c7c\u7fb9\u5427\u3002\u4f60\u7684\u9c7c\u7fb9\u6211\u662f\u767e\u5403\u4e0d\u538c\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4e24\u8033\u4e0d\u95fb\u7a97\u5916\u4e8b\uff0c\u7ec8\u6709\u4e00\u5929\u3001\u9c7c\u7fb9\u4e5f\u4f1a\u5403\u538c\u7684\uff0c\u521a\u624d\u6211\u548c\u4f60\u8bf4 \u7b11\u7684\uff0c\u8fc7\u4e24\u5929\u6211\u518d\u4e0b\u5c71\u66ff\u4f60\u6253\u542c\u6d88\u5427\u3002\u201d \u3000\u3000\u5176\u5b9e\u5979\u65e9\u5df2\u77e5\u9053\u4e86\u5916\u95f4\u7684\u4e00\u4e2a\u6d88\u606f\u7684\u4e86\uff0c\u5c31\u56e0\u4e3a\u5bb3\u6015\u6a80\u7fbd\u51b2\u672a\u80fd\u201c\u770b\u7834\u7ea2\u5c18\u201d\u624d\u4e0d\u6562\u544a \u8bc9\u4ed6\u3002 \u3000\u3000\u6b63\u5f53\u5979\u5c0f\u5fc3\u5955\u5955\u5730\u62ff\u8d77\u4e00\u628a\u526a\u5200\u653e\u5165\u5979\u7684\u767e\u5b9d\u888b\u7684\u65f6\u5019\u5ffd\u7136\u542c\u5f97\u5e97\u4e3b\u4eba\u5728\u5367\u623f\u91cc\u53f9\u6c14\u3002 \u8ddf\u7740\u5c31\u542c\u5230\u4e86\u4e00\u6bb5\u592b\u59bb\u5bf9\u8bdd\u3002\u5f00\u5934\u662f\u59bb\u5b50\u5728\u95ee\uff0c\u4e08\u592b\u5728\u7b54\u3002 \u3000\u3000\u201c\u4e09\u66f4\u534a\u591c\uff0c\u4f60\u4e0d\u7761\u89c9\uff0c\u5509\u58f0\u53f9\u6c14\uff0c\u5374\u4e3a\u4f55\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u7761\u5f97\u7740\u554a\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u53c8\u8981\u62bd\u58ee\u4e01\u4e86\u3002\u201d \u3000\u3000\u201c\u62bd\u58ee\u4e86\u4e5f\u4e0d\u5173\u54b1\u4eec\u7684\u4e8b\u5440\uff0c\u54b1\u4eec\u53ea\u6709\u4e00\u4e2a\u513f\u5b50\uff0c\u4e0d\u662f\u8bf4\u72ec\u5b50\u53ef\u514d\u7684\u5417\uff1f\u800c\u4e14\u54b1\u4eec\u7684\u5b69 \u5b50\u8fd8\u672a\u6210\u5e74\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u4eca\u5e74\u7684\u89c4\u4f8b\u6539\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u6539\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e09\u4e01\u62bd\u4e8c\u3001\u4e24\u4e01\u62bd\u4e00\u3002\u8fc7\u53bb\u4e8c\u5341\u5c81\u624d\u7b97\u6210\u5e74\uff0c\u73b0\u5728\u662f\u5341\u516b\u5c81\u5c31\u7b97\u6210\u5e74\u4e86\u3002\u201d \u3000\u3000\u201c\u54ce\u54df\uff0c\u54b1\u4eec\u7684\u5b69\u5b50\u4eca\u5e74\u53ef\u521a\u597d\u662f\u5341\u516b\u5c81\u3002\u4f46\u4f60\u4e0d\u662f\u5df2\u7ecf\u8d85\u8fc7\u4e86\u56db\u5341\u4e94\u5c81\u4e48\uff1f\u4ece\u56db\u5341\u4e94 \u5c81\u5230\u4e94\u5341\u4e94\u5c81\u7684\uff0c\u5373\u4f7f\u62bd\u4e2d\u4e86\uff0c\u8981\u670d\u52b3\u5f79\uff0c\u4e5f\u4e0d\u7528\u79bb\u5f00\u672c\u4e61\u571f\u7684\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4e0d\u540c\u4e86\uff0c\u4ece\u5341\u516b\u5c81\u5230\u4e94\u5341\u5c81\u90fd\u7b97\u58ee\u4e86\u3002\u6211\u4eca\u5e74\u662f\u56db\u5341\u516b\u5c81\uff0c\u8fd8\u5dee\u4e24\u5e74\u624d\u80fd\u514d \u5f79\u3002\u201d \u3000\u3000\u201c\u554a\u5440\uff0c\u90a3\u4e48\u4f60\u4eec\u7236\u5b50\u4e8c\u4eba\uff0c\u603b\u5f97\u6709\u4e00\u4e2a\u8981\u62bd\u53bb\u5f53\u5175\u6253\u4ed7\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f60\u603b\u7b97\u660e\u767d\u4e86\u3002\u4e0d\u8fc7\uff0e\u4e5f\u4e0d\u2014\u5b9a\u8981\u53bb\u6253\u4ed7\uff0c\u591a\u534a\u662f\u5f53\u6c11\u592b\u3002\u201d \u3000\u3000\u201c\u5f53\u6c11\u592b\u7684\u66f4\u60e8\uff0c\u88ab\u4eba\u50cf\u755c\u7272\u9a71\u8d76\u97ad\u6253\uff0c\u54b1\u4eec\u7684\u5b69\u5b50\u600e\u53d7\u5f97\u8fd9\u4e2a\u82e6\uff0c\u4e0a\u4e86\u6218\u573a\uff0e\u6c11\u592b\u6b7b \u7684\u4e00\u5b9a\u6bd4\u5175\u58eb\u66f4\u591a\uff01\u201d \u3000\u3000\u201c\u6211\u5012\u5b81\u613f\u5f53\u6c11\u592b\u4e0d\u613f\u5f53\u5175\uff0c\u7ed9\u91d1\u864f\u5f53\u5175\u662f\u8981\u6253\u6c49\u4eba\u7684\uff0c\u6c49\u4eba\u600e\u80fd\u53bb\u6740\u6c49\u4eba\uff1f\u201d \u3000\u3000\u201c\u597d\u5440\uff0c\u4f60\u559c\u6b22\u5f53\u6c11\u592b\u4f60\u5c31\u53bb\u5f53\u5427\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9\u5b69\u5b50\u8fed\u6b7b\uff01\u54fc\uff0c\u4f60\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u53ea\u6015\u4e5f \u71ac\u4e0d\u8d77\u3002\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6211\u559c\u6b22\u53bb\u5f53\uff0c\u6211\u53ea\u662f\u8bf4\u5018\u82e5\u4e0d\u8270\u907f\u514d\uff0c\u4e24\u8005\u4efb\u62e9\u5176\u4e00\uff0c\u90a3\u6211\u552f\u6709\u62fc\u7740\u591a\u53d7\u82e6\u695a\u53bb \u5f53\u6c11\u592b\uff0c\u6b7b\u4e86\u4e5f\u5bf9\u5f97\u8d77\u826f\u5fc3\u3002\u201d\u59bb\u5b50\u542c\u51fa\u4e00\u70b9\u201d\u82d7\u5934\u201d\uff0c\u5fd9\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8fd8\u6709\u529e\u6cd5\u53ef \u60f3\u3002\u201d\u4e08\u592b\u8bf4\u9053\uff1a\u201c\u529e\u6cd5\u4e0d\u662f\u6ca1\u6709\u3002\u505a\u5b98\u7684\u8c01\u4e0d\u7231\u94b1\uff0c\u54b1\u4eec\u53ea\u8981\u82b1\u5f97\u8d77\u94b1\uff0c\u5c31\u53ef\u4ee5\u8bf7\u4ed6\u4e70\u4eba \u9876\u66ff\uff0c\u4e0d\u8fc7\u6050\u6015\u8981\u5927\u5927\u7834\u8d22\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8bd5\u63a2\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u4ef7\u94b1\u4e5f\u5f00\u51fa\u6765\u4e86\u3002\u94f6\u5b50\u4e00\u5343\u4e24\uff01\u201d \u3000\u3000\u59bb\u5b50\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8d76\u5feb\u7b54\u5e94\u3002\u201d \u3000\u3000\u4e08\u592b\u53f9\u9053\uff1a\u201c\u4e00\u5343\u4e24\u94f6\u5b50\uff0c\u4f60\u5f53\u662f\u5bb9\u6613\u6323\u7684\u5417\uff1f\u54b1\u4eec\u8fd9\u95f4\u6742\u8d27\u5e97\u9876\u591a\u4e5f\u4e0d\u8fc7\u503c\u4e8c\u5343\u4e24 \u94f6\uff0c\u53bb\u4e86\u4e00\u534a\u4e86\uff01\u201d \u3000\u3000\u59bb\u5b50\u9053\uff1a\u201c\u94f6\u5b50\u8981\u7d27\uff0c\u8fd8\u662f\u6027\u547d\u8981\u7d27\uff1f\u83ab\u8bf4\u534a\u95f4\u3002\u5c31\u662f\u6574\u95f4\u6742\u8d27\u5e97\u9001\u6389\uff0c\u5018\u80fd\u4fdd\u5f97\u4f60\u4eec \u7236\u5b50\u5e73\u5b89\uff0c\u90a3\u5df2\u662f\u8981\u53e9\u8c22\u795e\u6069\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u542c\u4e86\u5e97\u592b\u59bb\u7684\u5bf9\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u6709\u529e\u6cd5\u53ef\u60f3\uff0c\u90a3\u4e9b\u62ff\u4e0d\u51fa\u94f6\u5b50\u7684\u7a77\u4eba\u5bb6 \u53ef\u662f\u9003\u4e0d\u8fc7\u9aa8\u8089\u5206\u79bb\u7684\u707e\u96be\u4e86\u3002\u5509\uff0c\u91d1\u864f\u62bd\u58ee\u4e86\u62bd\u5f97\u5982\u6b64\u7d27\u6025\uff0c\u6050\u6015\u5c31\u8981\u5357\u4fb5\u4e86\uff0c\u8fd9\u6d88\u606f\u53ef \u4e0d\u80fd\u8ba9\u5927\u54e5\u54e5\u77e5\u9053\uff01\u201d\u5979\u77e5\u9053\u6a80\u7fbd\u51b2\u6700\u62c5\u5fe7\u7684\u5c31\u662f\u8fd9\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u5979\u5728\u8fd9\u95f4\u6742\u8d27\u5e97\u62ff\u7684\u4e1c\u897f\u5927\u6982\u53ea\u503c\u516d\u4e03\u4e24\u94f6\u5b50\uff0c\u5374\u653e\u4e0b\u4e86\u4e94\u9897\u91d1\u8c46\uff0c\u4e94\u9897\u91d1\u8c46\u53ef\u4ee5\u6362\u4e94 \u5341\u4e24\u94f6\u5b50\u6709\u4f59\u3002 \u3000\u3000\u5979\u7b2c\u4e00\u6b21\u5bf9\u6a80\u7fbd\u51b2\u8bf4\u8c0e\uff0c\u867d\u7136\u63a9\u9970\u7684\u597d\uff0c\u795e\u6001\u4e5f\u8fd8\u6709\u70b9\u4e0d\u5927\u81ea\u7136\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u5927\u54e5\u54e5\uff0c\u6211\u53ea\u662f\u60f3\u544a\u8bc9\u4f60\uff0c\u6211\u505a\u4e86\u8680\u672c\u751f\u610f\uff0c\u4f60\u7684\u4e00\u5927\u628a\u91d1 \u8c46\uff0c\u6211\u90fd\u7ed9\u4f60\u82b1\u5149\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u91d1\u5b50\u4e0d\u80fd\u5f53\u996d\u5403\uff0c\u53c8\u4e0d\u80fd\u5f53\u8863\u88f3\u3002\u4f60\u6362\u6765\u7684\u4e1c\u897f\u90fd\u662f\u6211\u60f3\u8981\u7684\uff0c\u518d\u591a\u82b1 \u4e00\u70b9\u91d1\u5b50\uff0c\u6211\u4e5f\u8bf4\u503c\u5f97\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u5339\u7ef8\u7f0e\u597d\u4e0d\u597d\uff0c\u6211\u884c\u7ed9\u4f60\u7f1d\u4e24\u4ef6\u8863\u88f3\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5148\u7ed9\u4f60\u81ea\u5df1 \u7f1d\u5427\u3002\u6211\u4e5f\u4e0d\u7528\u7eeb\u7f57\u7ef8\u7f0e\uff0c\u53ea\u9700\u8981\u7c97\u5e03\u8863\u88f3\u5c31\u884c\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u62ff\u56de\u6765\u7684\u7ef8\uff0c\u4e5f\u8db3\u591f\u54b1\u4eec\u6bcf\u4eba\u7f1d\u4e24\u4e09\u5957\u5462\u3002\u201d\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u7a7f \u51fa\u53bb\u4f5c\u5ba2\u4eba\uff0c\u5728\u8fd9\u8352\u5c71\u91cc\u7a7f\u7ed9\u8c01\u770b\uff1f\u4f60\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u7a7f\u7ed9\u6211\u770b\uff0c\u6211\u4e5f\u7a7f\u7ed9\u4f60\u770b\u5440\u3002\u4f60\u4e0d\u559c \u6b22\u770b\u89c1\u6211\u7a7f\u5f97\u6f02\u4eae\u5417\uff1f\uff0c\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u559c\u6b22\uff0c\u5f53\u7136\u559c\u6b22\u3002\u201d\u8fd9\u53e5\u8bdd\u4ed6\u662f\u5e26\u7740\u7b11\u5bb9\u8bf4\u7684\uff0c\u4f46\u7b11\u5bb9\u5374\u4e5f\u63a9\u4e0d\u4f4f\u4ed6\u90a3\u9eef \u7136\u7684\u795e\u8272\u4e86\u3002 \u3000\u3000\u63a5\u4e0b\u6765\u7684\u4e00\u6bb5\u65e5\u5b50\uff0c\u949f\u7075\u79c0\u505a\u7cd5\u997c\u3001\u7f1d\u65b0\u8863\u3001\u5236\u5bb6\u5177\uff0c\u8fd8\u590d\u62bd\u51fa\u65f6\u95f4\u7ec3\u6b66\uff0c\u5fd9\u5f97\u5012\u662f\u633a \u6709\u610f\u601d\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e5f\u5728\u52e4\u7ec3\u5185\u529f\uff0c\u771f\u6c14\u6e10\u6e10\u80fd\u5728\u4e39\u7530\u51dd\u9aa4\u4e86\uff0c\u4f46\u8fd8\u662f\u672a\u80fd\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u53ea\u80fd\u5750 \u7acb\uff0c\u672a\u80fd\u5f97\u52a8\u3002 \u3000\u3000\u8fd9\u51e0\u5929\u949f\u7075\u79c0\u5728\u5c71\u6eaa\u6d17\u4e86\u8863\u88f3\u56de\u6765\uff0c\u770b\u89c1\u6a80\u7fbd\u51b2\u4f0f\u5728\u65b0\u5236\u684c\u5b50\u4e0a\u201c\u5199\u5b57\u201d\u3002\u6ca1\u6709\u7eb8\u7b14\uff0c \u4ed6\u662f\u7528\u624b\u6307\u5f53\u7b14\uff0c\u5199\u5728\u57f9\u5e72\u7684\u7af9\u7247\u4e0a\uff0c\u90a3\u4e9b\u7af9\u7247\u662f\u949f\u7075\u79c0\u51c6\u5907\u62ff\u6765\u505a\u4e00\u5f20\u8336\u51e0\u7684\u3002 \u3000\u3000\u8bf4\u662f\u5199\u5b57\uff0c\u5176\u5b9e\u662f\u523b\u5b57\u3002 \u3000\u3000\u949f\u7075\u79c0\u8d70\u8fd1\u53bb\u770b\uff0c\u53ea\u89c1\u4ed6\u5728\u7af9\u7247\u4e0a\u523b\u7684\u5b57\uff0c\u7b14\u753b\u6574\u9f50\u6df1\u6d45\u5982\u4e00\uff0c\u6bcf\u4e2a\u5b57\u90fd\u770b\u5f97\u6e05\u6e05\u695a \u695a\u3002 \u3000\u3000\u949f\u7075\u79c0\u559c\u9053\uff1a\u201c\u5927\u54e5\u54e5\u3002\u4f60\u7684\u529f\u529b\u6062\u590d\u4e86\uff01\u8fd9\u4e9b\u5b57\u4e5f\u5199\u5f97\u771f\u662f\u6f02\u4eae\u54e6\uff01\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5927 \u6168\u53ea\u6062\u590d\u4e09\u5206\u529f\u529b\u7f62\u4e86\uff0c\u8fd8\u5dee\u5f97\u8fdc\u5462\u3002\u5728\u7af9\u7247\u4e0a\u5199\u5b57\uff0c\u6709\u7684\u4eba\u5199\u5f97\u5f88\u597d\uff0c\u4f46\u6211\u5c1a\u672a\u4e60\u60ef\uff0c\u4e66 \u6cd5\u4e5f\u662f\u672a\u80fd\u8bb2\u7a76\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8ba9\u6211\u77a7\u77a7\u3002\u201d\u62ff\u8fc7\u6765\u770b\uff0c\u53ea\u89c1\u4ed6\u201c\u5199\u201d\u7684\u662f\u5357\u5510\u4e2d\u4e3b\u674e\u7304\u4f5c\u7684\u4e00\u9996\u8bcd\u3002 \u3000\u3000\u201c\u83e1\u840f\u9999\u9500\u7fe0\u53f6\u6b8b\uff0c\u897f\u98ce\u6101\u8d77\u7eff\u6ce2\u95f4\uff0c\u8fd8\u4e0e\u97f6\u5149\u5171\u6194\u60b4\uff0c\u4e0d\u582a\u770b\u3002\u7ec6\u96e8\u68a6\u56de\u9e21\u585e\u8fdc\uff0c\u5c0f \u697c\u5439\u5f7b\u7389\u771a\u5bd2\uff0c\u591a\u5c11\u6cea\u73e0\u65e0\u9650\u6068\uff0c\u501a\u9611\u6746\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u770b\u4e86\uff0c\u9ed8\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u77a7\u51fa\u6bdb\u75c5\u4e86\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u7eff\u6ce2\u5c31\u662f\u78a7\u6ce2\u5427\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u78a7\u6ce2\u4e5f\u5c31\u662f\u6e05\u6ce2 \u5427\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u7a76\u7adf\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u8fd8\u5728\u60f3\u5ff5\u90a3\u4f4d\u8d6b\u8fde\u59d1\u5a18\uff1f\u201d\u7389\u9762\u5996\u72d0\u662f\u590d\u59d3 \u8d6b\u8fde\uff0c\u53cc\u540d\u6e05\u6ce2\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5446\u4e86\u4e00\u5446\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u7684\u60f3\u50cf\u529b\u771f\u591f\u4e30\u5bcc\uff0c\u5c06\u6765\u5927\u6709\u5e0c\u671b\u505a\u4e2a\u8bd7\u4eba\u3002\u6211 \u53ea\u4e0d\u8fc7\u89c1\u4e00\u5e74\u4e00\u5ea6\u53c8\u79cb\u98ce\uff0c\u4e0d\u514d\u6709\u70b9\u611f\u89e6\uff0c\u501f\u5357\u5510\u4e2d\u4e3b\u8fd9\u9996\u300a\u644a\u7834\u6d63\u6eaa\u6c99\u300b\uff0c\u597d\u6bd4\u501f\u522b\u4eba\u7684 \u9152\u676f\uff0c\u4ee5\u6d47\u81ea\u5df1\u80f8\u4e2d\u7684\u5757\u5792\u800c\u5df2\u3002\u201d\u4e0d\u8fc7\u4ed6\u867d\u7136\u5426\u8ba4\u5e76\u975e\u56e0\u4e3a\u8bcd\u4e2d\u201c\u7eff\u6ce2\u201d\u4e8c\u5b57\uff0c\u8054\u60f3\u5230 \u201c\u6e05\u6ce2\u201d\uff0c\u624d\u5199\u8fd9\u9996\u8bcd\uff0c\u4f46\u5fc3\u5e95\u5374\u662f\u4e0d\u7981\u81ea\u95ee\uff1a\u201c\u6211\u771f\u7684\u5c31\u80fd\u5fd8\u8bb0\u4e86\u6e05\u6ce2\u4e48\uff1f\u201d \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4ed6\u662f\u6781\u529b\u5728\u6291\u5236\u81ea\u5df1\uff0c\u4e0d\u53bb\u518d\u60f3\u8d6b\u8fde\u6e05\u6ce2\uff0c\u4f46\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u8d6b\u8fde\u6ce2 \u7684\u5f71\u5b50\u8fd8\u662f\u7a81\u7136\u4f1a\u51fa\u73b0\u5728\u4ed6\u7684\u8111\u6d77\u4e4b\u4e2d\u7684\u3002\u4ed6\u4e0d\u60f3\u6b3a\u9a97\u81ea\u5df1\uff0c\u4f46\u4ed6\u4e0d\u60f3\u4f24\u4e86\u8fd9\u5c0f\u59b9\u5b50\u7684\u5fc3\uff0c \u5374\u662f\u4e0d\u4fbf\u76f4\u8a00\u65e0\u9690\u4e86\u3002\u949f\u7075\u79c0\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u5373\u4f7f\u4f60\u662f\u5728\u60f3\u5979\uff0c\u6211\u4e5f\u4e0d\u4f1a\u751f\u4f60\u7684 \u6c14\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5979\u662f\u738b\u5e9c\u7684\u5e72\u683c\u683c\uff0c\u67f3\u5143\u7532\u80cc\u540e\u9760\u5c71\uff0c\u4e5f\u6b63\u5c31\u662f\u5979\u7684\u5e72\u7239\uff0c\u96be\u9053\u4f60\u4e0d\u6068 \u5979\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u7684\u7237\u7237\u6b7b\u5728\u5343\u67f3\u5728\uff0c\u5979\u662f\u5343\u67f3\u5728\u7684\u534a\u4e2a\u4e3b\u5b50\uff0c\u6211\u5bf9\u5f53\u7136\u7edd\u65e0\u597d\u611f\uff0c\u4f46\u6211 \u8fd8\u662f\u4e0d\u80fd\u4e0d\u66ff\u5979\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u5979\u548c\u67f3\u5143\u7532\u6bd5\u7adf\u8fd8\u662f\u6709\u6240\u4e0d\u540c\u7684\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u60f3\u4e0d\u5230\u5979\u4f1a\u66ff\u8d6b\u8fde\u6e05\u6ce2\u8bf4\u597d\u8bdd\uff0c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f9d\u4f60\u770b\u4ed6\u4eec\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d\u949f\u7075 \u79c0\u671b\u7740\u4ed6\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u6709\u4e00\u4ef6\u4e8b\u60c5\u6211\u672c\u8be5\u65e9\u5c31\u544a\u8bc9\u4f60\u7684\uff0c\u5374\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9 \u4f60\uff0c\u90a3\u652f\u4eba\u53c2\uff0c\u4f60\u77e5\u9053\u662f\u8c01\u7ed9\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u662f\u5168\u9760\u90a3\u652f\u4eba\u53c2\u7eed\u547d\u7684\uff0c\u949f\u7075\u79c0\u600e\u4f1a\u6709\u90a3\u6837\u540d\u8d35\u7684\u4eba\u53c2\u5462\uff1f\u4ed6\u5f53\u7136\u65e9\u5c31\u60f3\u5230\u5b83\u7684 \u201c\u6765\u5386\u201d\u662f\u201c\u53ef\u7591\u201d\u7684\u4e86\uff0c\u6b63\u56e0\u4e3a\u4ed6\u65e9\u5df2\u9690\u9690\u731c\u5230\u51e0\u5206\uff0c\u8fd9\u624d\u6ca1\u6709\u5411\u949f\u7075\u79c0\u201c\u67e5\u6839\u95ee\u5e95\u201d\uff0c \u6b64\u65f6\u542c\u5f97\u949f\u7075\u79c0\u63d0\u8d77\uff0c\u53ea\u597d\u88c5\u4f5c\u65b9\u59cb\u7701\u8d77\u7684\u6a21\u6837\u8bf4\u9053\uff1a\u201c\u51fa\u4e86\u5343\u67f3\u5728\uff0c\u6211\u660f\u8ff7\u4e86\u90a3\u4e48\u591a\u5929\uff0c \u4f60\u4e0d\u8bf4\u6211\u90fd\u51e0\u4e4e\u5fd8\u4e86\u3002\u5bf9\u5566\uff0c\u90a3\u652f\u4eba\u53c2\u662f\u8c01\u7ed9\u4f60\u7684\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u662f\u7ed9\u6211\u7684\uff0c\u662f\u7ed9\u4f60 \u7684\u3002\u7ed9\u4f60\u9001\u8fd9\u4efd\u539a\u793c\u7684\u4eba\u5c31\u662f\u8d6b\u8fde\u6e05\u6ce2\uff01\u201d \u3000\u3000\u6a80\u7fbd\u4ef6\u867d\u7136\u65e9\u5c31\u6599\u5230\u662f\u8d6b\u8fde\u6e05\u6ce2\u6240\u4e3a\uff0c\u4f46\u4ece\u949f\u7075\u79c0\u53e3\u4e2d\u5f97\u5230\u8bc1\u5b9e\uff0c\u4ed6\u8fd8\u662f\u4e0d\u7981\u5446\u4e86\u4e00 \u5446\u3002 \u3000\u3000\u949f\u7075\u79c0\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u67f3\u5143\u7532\u662f\u6709\u5fc3\u5bb3\u4f60\uff0c\u4f46\u5979\u65e0\u5fc3\u5bb3\u4f60\u3002\u6216\u8005\u5979\u7684\u884c\u4e3a\u66fe\u7ecf\u4f24\u5bb3\u8fc7\u4f60\uff0c \u4f46\u5979\u4e5f\u66fe\u7ecf\u6551\u8fc7\u4f60\u7684\u3002\u4e0d\u9519\uff0c\u5979\u548c\u67f3\u5143\u7532\u662f\u5b8c\u989c\u738b\u5e9c\u7684\u4eba\uff0c\u4f46\u4f3c\u4e4e\u8fd8\u4e0d\u80fd\u8bf4\u4ed6\u4eec\u4e43\u662f\u4e00\u4e18\u4e4b \u8c89\u3002\u8fd9\u5c31\u662f\u4ed6\u4eec\u4e4b\u95f4\u7684\u4e0d\u540c\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5446\u4e86\u4e00\u4f1a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u771f\u662f\u8d8a\u6765\u8d8a\u61c2\u4e8b\u4e86\u3002\u5979\u4e0d\u4f46\u80fd\u5e72\uff0c\u800c\u4e14\u660e\u767d\u771f \u7406\uff0c\u8bb8\u591a\u5927\u4eba\u6050\u6015\u90fd\u4e0d\u5982\u5979\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u4eca\u5929\u7a7f\u7684\u662f\u4ef6\u65b0\u8863\uff0c\u88c1\u526a\u5408\u8eab\uff0c\u886c\u6258\u51fa\u4e00\u4e2a\u5c11\u5973\u73b2\u653f\u6d6e\u51f8\u7684\u4f53\u6001\uff0c\u6a80\u7fbd\u51b2\u7a81\u7136\u53d1 \u89c9\uff0c\u5979\u671d\u5915\u76f8\u5904\u4e86\u534a\u5e74\u6709\u591a\u7684\u201c\u5c0f\u5973\u5b69\u201d\u539f\u6765\u5df1\u662f\u4ed6\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u201c\u6210\u719f\u201d\u4e86\u3002\u4e0d\u4ec5\u4ec5\u662f \u201c\u61c2\u4e8b\u201d\u7684\u90a3\u79cd\u201c\u6210\u719f\u201d\uff0c\u800c\u4e14\u662f\u53ef\u4ee5\u5438\u5f15\u7537\u4eba\u6ce8\u610f\u7684\u90a3\u79cd\u6210\u719f\u4e86\u3002\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u9053\uff1a \u201c\u554a\uff0c\u6211\u53ef\u4e0d\u80fd\u518d\u628a\u5979\u5f53\u5b69\u5b50\u4e86\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4e0d\u8ba4\u8bc6\u6211\u5417\uff0c\u8fd9\u6837\u671b\u7740\u6211\uff1f\u201d \u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u771f\u7684\u6709\u70b9\u8fd9\u6837\u611f\u89c9\uff0c\u4f60\u597d\u50cf\u4e00\u5239\u90a3\u95f4\u5c31\u53d8\u6210\u5927\u4eba\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u561f\u7740\u5c0f\u5634\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u6211\u6700\u4e0d\u9ad8\u5174\u4f60\u8001\u662f\u628a\u6211\u5f53\u4f5c\u5b69\u5b50\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6628\u5929\u6211 \u5df2\u7ecf\u6ee1\u5341\u516b\u5c81\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8fc7\uff1a\u201c\u771f\u7684\u5417\uff0e\u90a3\u4e48\u6211\u53ef\u8981\u8865\u8d3a\u4f60\u7684\u751f\u65e5\u4e86\u3002\u201d\u949f\u7075\u79c0\u5fc3\u91cc\u751c\u6ecb\u6ecb\u7684\uff0c\u8bf4\u9053\uff1a \u201c\u54b1\u4eec\u521a\u624d\u8c08\u7684\u662f\u8d6b\u8fde\u59d1\u5a18\u3002\u4f60\u522b\u88c5\u4f5c\u5fd8\u4e86\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8981\u6211\u8bf4\u4ec0\u4e48\uff1f\u201d\u949f\u7075\u79c0\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u628a\u771f\u50cf\u544a\u8bc9\u4f60\u4e86\uff0c\u4f60\u7684\u6027\u547d\u6700\u5979\u6551\u7684\u3002\u6211\u4e5f\u8981\u4f60\u628a\u771f\u5fc3\u8bdd\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u8981\u89c1 \u5979\uff1f\u201d\u5979\u671b\u7740\u6a80\u7fbd\u51b2\uff0c\u597d\u50cf\u662f\u8981\u770b\u4ed6\u5fc3\u5e95\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e0e\u5979\u6069\u4ec7\u65e9\u5df2\u4e00\u7b14\u52fe\u9500\uff0c\u6211\u662f\u4e0d\u60f3\u518d\u89c1\u5979\u4e86\u3002\u201d\u949f\u7075\u79c0\u534a\u4fe1\u534a\u7591\uff0c\u5999\u76ee \u659c\u7768\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5979\u548c\u67f3\u5143\u7532\u7eb5\u7136\u4e0d\u80fd\u8bf4\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u548c\u54b1\u4eec\u603b\u4e0d\u662f\u4e00\u6761\u8def \u4e0a\u7684\u4eba\uff0c\u5373\u4f7f\u6211\u4e0d\u628a\u5979\u5f53\u4f5c\u4ec7\u4eba\uff0c\u4e5f\u53ea\u80fd\u628a\u5979\u5f53\u4f5c\u7ad9\u5728\u654c\u5bf9\u4e00\u65b9\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u542c\u5f97\u201c\u54b1\u4eec\u201d\u4e8c\u5b57\uff0c\u597d\u50cf\u5403\u4e86\u871c\u7cd6\u4e00\u6837\uff0c\u5fc3\u4e2d\u611f\u5230\u4e00\u80a1\u751c\u610f\uff0c\u7b11\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c \u4f60\u771f\u7684\u80fd\u591f\u72e0\u5f97\u4e0b\u5fc3\u80a0\uff0c\u628a\u5979\u5f53\u4f5c\u654c\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u662f\u4e0d\u60f3\u6740\u5979\u7684\u3002\u5c31\u56e0\u4e3a\u6211\u4e0d\u60f3\u6740\u5979\uff0c\u6240\u4ee5\u6211\u4e0d\u613f\u610f\u518d\u89c1\u5979 \u4e86\u3002\u4f60\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u671b\u7740\u4ed6\u7684\u773c\u775b\uff0c\u534a\u664c\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u6211\u76f8\u4fe1\u4f60\u8bf4\u7684\u662f\u771f\u8bdd\u4e86\uff0c\u4e0d \u8fc7\u2014\u2014\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u8fc7\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5c31\u53ea\u6709\u6211\u966a\u7740\u4f60\uff0c\u5e74\u590d\u4e00\u5e74\u7684\u5728\u8fd9\u5ea7\u8352\u5c71\u4e0a\u5f80\u4e0b\u53bb\u3002\u4f60\u4e0d\u4f1a\u611f\u5230\u5bc2\u5bde \u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u6709\u8fc7\u4e00\u6b21\u611f\u5230\u975e\u5e38\u5bc2\u5bde\u7684\u7ecf\u9a8c\uff0c\u554a\uff0c\u90a3\u4e2a\u5bc2\u5bde\u4e4b\u611f\u662f\u53ef\u6015\u6781\u4e86\uff01\u4f60\u60f3\u77e5 \u9053\u662f\u5728\u4f55\u65f6\u5417\uff1f\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u5f53\u7136\u60f3\u8981\u77e5\u9053\u5566\uff0c\u522b\u5356\u5173\u5b50\u4e86\uff0c\u5feb\u544a\u8bc9\u6211\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u662f\u5728\u5343\u67f3\u5728\u5927\u6218\u7684\u65f6\u5019\u3002\u66f4\u786e\u5207\u7684\u8bf4\uff0c\u662f\u5728\u6c5f\u5357\u5927\u4fa0\u94c1\u7b14\u4e66\u751f\u6587\u9038\u51e1\u548c\u67f3 \u5143\u7532\u8054\u624b\u5939\u653b\u6211\u7684\u65f6\u5019\uff01\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e2a\u65f6\u5019\uff0c\u5f53\u771f\u662f\u4f60\u6700\u5371\u9669\u7684\u65f6\u5019\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\uff0c\u90a3\u4e2a\u65f6\u5019\uff0c\u6211\u65e9\u5df2\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u6839\u672c\u5c31\u4e0d\u53bb\u7406\u4f1a\u4ec0\u4e48\u5371\u9669\u4e0d\u5371\u9669 \u4e86\u3002\u4f46\u662f\u6211\u53ef\u4ee5\u4e0d\u60f3\u5230\u5371\u9669\u5374\u4e0d\u80fd\u4e0d\u611f\u53d7\u5230\u90a3\u5f02\u6837\u7684\u5bc2\u5bde\uff01\u201d\u4ed6\u5598\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60 \u77e5\u9053\u6211\u6765\u6c5f\u5357\u7684\u76ee\u7684\u4e4b\u4e00\uff0c\u5c31\u662f\u60f3\u8981\u548c\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u7ed3\u4ea4\u7684\uff0c\u6587\u5927\u4fa0\u5c24\u5176\u662f\u6211\u60f3\u7ed3\u4ea4\u7684\u670b \u53cb\u3002\u5728\u4e34\u5b89\u7684\u90a3\u6bb5\u65e5\u5b50\uff0c\u4e00\u5ea6\u6211\u4eec\u4e5f\u66fe\u7ecf\u4ea4\u4e0a\u4e86\u670b\u53cb\u4e86\u3002\u67f3\u5143\u7532\u8981\u6740\u6211\uff0c\u65e9\u5df2\u5728\u6211\u610f\u6599\u4e4b \u4e2d\uff0c\u751a\u81f3\u8d6b\u8fde\u6e05\u6ce2\u8981\u548c\u4ed6\u4e32\u8c0b\u6765\u5bf9\u4ed8\u6211\uff0c\u867d\u7136\u662f\u6211\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u6211\u4e5f\u8fd8\u4e0d\u662f\u7279\u522b\u4f24\u5fc3\u3002\u4f46 \u6587\u9038\u51e1\u662f\u6211\u5c0a\u656c\u7684\u670b\u53cb\uff0c\u60f3\u4e0d\u5230\u4ed6\u5bf9\u6211\u7684\u8bef\u4f1a\u5982\u6b64\u4e4b\u6df1\uff0c\u7adf\u4e5f\u8981\u6765\u6740\u6211\uff0c\u800c\u4e14\u662f\u548c\u67f3\u5143\u7532\u8054 \u624b\u6740\u6211\u3002\u5f53\u6211\u770b\u89c1\u4ed6\u5e26\u9886\u7684\u90a3\u73ed\u6c5f\u5357\u4fa0\u4e49\u9053\u90fd\u5df2\u6765\u5230\u7684\u65f6\u5019\uff0c\u6211\u53ea\u89c9\u5f97\u8fd9\u4e2a\u4e16\u4e0a\u5df2\u662f\u6ca1\u4eba\u80fd \u591f\u8c05\u89e3\u6211\u4e86\uff0c\u5929\u5730\u4e4b\u5927\uff0c\u5df2\u662f\u65e0\u6211\u5bb9\u4e0e\u4e4b\u5730\uff01\u6211\u611f\u89c9\u5230\u6709\u751f\u4ee5\u6765\u4ece\u6240\u672a\u6709\u8fc7\u7684\u5bc2\u5bde\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u5a07\u8eaf\u5fae\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u65f6\u5019\uff0c\u6211\u5927\u6982\u5df2\u7ecf\u662f\u5728\u4f60\u7684\u6000\u4e2d\u660f\u8ff7\u8fc7\u53bb\u7684\uff0c\u4f46\u4f60\u5e94\u8be5 \u77e5\u9053\uff0c\u6700\u5c11\u8fd8\u4e0d\u4e00\u4e2a\u4eba\u76f8\u4fe1\u4f60\u662f\u597d\u4eba\uff0c\u6700\u5c11\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u5728\u5173\u5fc3\u4f60\u7684\u554a\uff01\u5373\u4f7f\u5979\u90a3\u65f6\u5019\u662f\u5df2 \u7ecf\u6ca1\u6709\u77e5\u89c9\uff0c\u5979\u4e5f\u8fd8\u662f\u5728\u5173\u5fc3\u4f60\u7684\u554a\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u4f60\u4e0d\u662f\u5728\u7b11\u8bdd\u6211\u8bf4\u7684\u8bdd\u4e0d\u5408\u7406 \u8def\u5427\uff1f\u201d \u3000\u3000\u6ca1\u6709\u77e5\u89c9\uff0c\u8fd8\u600e\u80fd\u201c\u5173\u5fc3\u201d\u522b\u4eba\u3002\u542c\u6765\u4f3c\u4e4e\u4e0d\u5408\u201c\u7406\u8def\u201d\uff0c\u4f46\u949f\u7075\u79c0\u5374\u662f\u51b2\u53e3\u800c\u51fa\uff0c\u8bf4 \u5f97\u6781\u4e3a\u81ea\u7136\uff0c\u6a80\u7fbd\u51b2\u4e5f\u5b8c\u5168\u660e\u767d\u5979\u7684\u5fc3\u610f\uff0c\u4e1d\u8c6a\u5730\u4e0d\u89c9\u5f97\u53ef\u7b11\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u61c2\uff0c\u6240\u4ee5\u5f53\u6211\u4e00\u5f20\u5f00\u773c\u775b\uff0c\u53d1\u73b0\u4f60\u5728\u6211\u8eab\u65c1\u7684\u65f6\u5019\uff0c\u6211\u5c31 \u89c9\u5f97\u81ea\u5df1\u4e0d\u662f\u5b64\u7acb\u65e0\u63f4\u7684\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u559c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5bc2\u5bde\u5728\u4e8e\u5fc3\u5883\uff0c\u5728\u5343\u67f3\u5e84\u7684\u65f6\u5019\u3002\u6ee1\u773c\u90fd\u662f\u4eba\uff0c\u6211\u5374\u5982\u540c\u7f6e\u8eab\u9b3c\u57df\uff01\u5728\u8fd9 \u91cc\u53ea\u6709\u4f60\u548c\u6211\uff0c\u4f46\u8352\u5c71\u5374\u597d\u50cf\u53d8\u6210\u4e86\u4e50\u56ed\u3002\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u949f\u7075\u79c0\u6101\u7709\u5c3d\u5c55\uff0c\u5c31\u50cf\u6362\u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u6ee1\u8138\u90fd\u662f\u6b22\u7b11\u3002\u201c\u5927\u54e5 \u54e5\uff0c\u542c\u5f97\u4f60\u8fd9\u6837\u8bf4\uff0c\u6211\u771f\u9ad8\u5174\uff01\u201d\u4e0d\u77e5\u5979\u662f\u5426\u9ad8\u5174\u5f97\u5fd8\u4e86\u5f62\uff0c\u7a81\u7136\u7eb5\u4f53\u5165\u6000\uff0c\u62b1\u7740\u6a80\u7fbd\u51b2\u5728 \u4ed6\u7684\u8138\u4e0a\u543b\u4e86\u4e00\u543b\u3002\u6e29\u6da6\u7684\u7ea2\u5507\u5370\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u4e00\u80a1\u9189\u4eba\u7684\u82b3\u9999\u900f\u5165\u4ed6\u7684\u5fc3\u623f\u3002\u8fd9\u4e00\u4e0b\u7a81\u5982 \u5176\u6765\u7684\u201c\u88ad\u51fb\u201d\uff0c\u4ee4\u5f97\u4ed6\u4e0d\u77e5\u6240\u63aa\uff0c\u4ed6\u6ca1\u6709\u6c14\u529b\u63a8\u5f00\u5979\uff08\u5c3d\u7ba1\u4ed6\u5df2\u7ecf\u6062\u590d\u4e86\u51e0\u5206\u529f\u529b\uff09\uff0c\u6216 \u8005\u66f4\u786e\u529f\u7684\u8bf4\uff0c\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u8981\u63a8\u5f00\u5979\u3002\u8fd9\u771f\u662f\u4e00\u79cd\u975e\u5e38\u5947\u5999\u7684\u611f\u89c9\uff0c\u5239\u90a3\u95f4\u5929\u5730\u4e07\u7269\u90fd \u597d\u50cf\u9759\u6b62\u4e86\uff0c\u4ed6\u53ea\u542c\u4e86\u89c1\u4e86\u81ea\u5df1\u7684\u5fc3\u8df3\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u79cd\u5947\u5999\u7684\u611f\u89c9\uff1f\u6740\u51fa\u5343\u67f3\u5e84\u7684\u65f6\u5019\uff0c\u4ed6\u66fe\u7ecf\u62b1\u7740\u5979\u8d70\u8fc7\u957f\u8def\uff0c\u5728\u4ed6\u660f\u8ff7\u7684 \u90a3\u4e03\u5929\u4e03\u591c\uff0c\u949f\u7075\u79c0\u4e5f\u66fe\u80cc\u7740\u4ed6\u8d70\u4e0a\u9ad8\u5c71\uff0c\u4e5f\u56bc\u5480\u70c2\u4eba\u53c2\u5582\u7ed9\u4ed6\u5403\uff0c\u6700\u540e\u90a3\u6b21\uff0c\u4ed6\u4e14\u662f\u5df2\u7ecf \u6709\u4e86\u77e5\u89c9\u7684\u3002\u4e00\u9635\u201c\u8ff7\u832b\u201d\u8fc7\u540e\uff0c\u4e24\u4eba\u90fd\u597d\u50cf\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u949f\u7075\u79c0\u7ad9\u4e86\u8d77\u6765\uff0c\u50cf\u662f\u5f00\u59cb\u611f \u89c9\u5230\u81ea\u5df1\u7684\u5931\u6001\u5fd8\u5f62\uff0c\u7f9e\u7ea2\u4e86\u8138\u3002\u6a80\u7fbd\u51b2\u6ca1\u6709\u955c\u5b50\uff0c\u770b\u4e0d\u89c1\u81ea\u5df1\u7684\u8138\u8272\uff0c\u4f46\u949f\u7075\u79c0\u7684\u7c89\u8138\u5c31 \u50cf\u4e00\u9762\u955c\u5b50\uff0c\u770b\u4e0d\u89c1\u81ea\u5df1\u7684\u8138\u8272\u4e00\u5b9a\u4e5f\u662f\u50cf\u5979\u4e00\u6837\u3002\u56e0\u4e3a\u4ed6\u4e5f\u611f\u89c9\u5230\u81ea\u5df1\u7684\u8138\u4e0a\u662f\u70ed\u8fa3\u8fa3\u7684 \u4e86\u3002 \u3000\u3000\u5947\u5999\u7684\u611f\u89c9\u662f\u4e92\u76f8\u611f\u67d3\u7684\uff0c\u7528\u4e0d\u7740\u8bf4\u8bdd\uff0c\u5fc3\u7075\u5df2\u76f8\u901a\u3002\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u6837\u5947\u5999\u7684\u611f\u89c9\uff0c\u4ed6 \u4eec\u4e5f\u90fd\u660e\u767d\u4e86\u3002\u56e0\u4e3a\u6b64\u523b\u7684\u949f\u7075\u79c0\u5728\u4ed6\u7684\u773c\u4e2d\uff0c\u5df2\u7ecf\u4e0d\u518d\u662f\u7a1a\u6c14\u672a\u6d88\u7684\u201c\u5c0f\u59b9\u59b9\u201d\u4e86\uff0c\u5979\u662f \u5df2\u7ecf\u61c2\u5f97\u9762\u7ea2\u7684\u5c11\u5973\u4e86\u3002\u800c\u4ed6\u5728\u949f\u7075\u79c0\u7684\u773c\u4e2d\uff0c\u6050\u6015\u4e5f\u4e0d\u4ec5\u53ea\u662f\u4e2a\u201c\u5927\u54e5\u54e5\u201d\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u4eec \u867d\u7136\u90fd\u6709\u611f\u89c9\u5230\u8fd9\u79cd\u611f\u60c5\u4e0a\u5fae\u5999\u7684\u53d8\u5316\uff0c\u5374\u662f\u8c01\u4e5f\u6ca1\u6709\u52c7\u6c14\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u6c89\u9ed8\u7247\u523b\uff0c\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u505a\u5927\u4e48\uff0c\u5bf9\u5927\u54e5\u54e5\u8fd8\u662f\u8fd9\u6837\u6492\u5a07\uff1f\u201d\u949f\u7075\u79c0\u4f6f\u55d4 \u9053\uff1a\u201c\u8c01\u53eb\u4f60\u4ecd\u7136\u628a\u6211\u5f53\u4f5c\u5b69\u5b50\uff0c\u4f60\u8d8a\u628a\u6211\u5f53\u4f5c\u5b69\u5b50\uff0c\u6211\u5c31\u8d8a\u53d1\u6dd8\u6c14\u3002\u201d\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u7b11 \u4e86\u8d77\u6765\uff0c\u7559\u4e0b\u7684\u90a3\u4e00\u70b9\u201c\u5c34\u5c2c\u201d\u4e5f\u5728\u7b11\u58f0\u4e2d\u5316\u4e3a\u4e4c\u6709\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8bf4\u6b63\u7ecf\u7684\uff0c\u6709\u4e00\u693f\u5927\u4e8b\u8fd8\u5f97\u5907\u529e\u5462\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u5c3d\u5f00\u73a9\u7b11\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u4e00\u6014\u9053\uff1a\u201c\u54e6\uff0c\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u7ed9\u949f\u5bb6\u5927\u5c0f\u59d0\u8865\u795d\u5979\u7684\u5341\u516b\u5c81\u751f\u8fb0\u5440\uff01\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u8bf4\u6b63\u7ecf\u8fd8\u662f\u4e0d\u6b63 \u7ecf\uff0c\u54fc\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u5c31\u77e5\u9053\u548c\u6211\u5f00\u73a9\u7b11\u7684\u3002\u201d\u5176\u8f9e\u82e5\u6709\u61be\u7109\uff0c\u5176\u5fc3\u5219\u5b9e\u559c\u4e4b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u6ee1\u4e86\u5341\u516b\u5c81\u5c31\u662f\u5927\u4eba\u4e48\uff0c\u8fd9\u8fd8\u4e0d\u662f\u5927\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u624d\u662f\u5927\u4e8b\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u63a9\u9970\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u559c\u6c14\uff0c\u8fd9\u624d\u5f00\u6000\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u771f\u597d\u3002\u591a\u8c22\u4f60\u8fd8\u8bb0 \u5f97\uff01\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u521a\u521a\u8bf4\u8fc7\u7684\u6211\u600e\u4e48\u80fd\u5c31\u5fd8\u8bb0\u5462\uff1f\u4f46\u53ef\u60dc\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53ef\u60dc\u6ca1\u6709\u7f8e\u9152\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4ece\u5979\u7684\u767e\u5b9d\u888b\u4e2d\u62ff\u51fa\u4e00 \u6a3d\u9152\u6765\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u5357\u7684\u540d\u9152\u2018\u5973\u513f\u7ea2\u2019\u5440\uff0c\u6211\u5728\u4e34\u5b89\u559d\u8fc7\u7684\u3002\u4f60\u600e\u4e48\u5f97\u6765\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u7528\u4f60\u7684\u4e00\u9897\u91d1\u8c46\u6362\u6765\u7684\u3002\u6211\u6765\u7ed9\u4f60\u914d\u51e0\u4e2a\u5c0f\u83dc\u9001\u9152\u3002\u6709\u65b0\u6458\u7684\u7af9\u7b0b\u548c\u5c71 \u9e21\uff0c\u8fd8\u6709\u7528\u53e6\u4e00\u9897\u91d1\u8c46\u6362\u6765\u7684\u814a\u8089\u548c\u9c7c\u5e72\uff0c\u4f60\u8bf4\u53ef\u597d\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u8fd9\u56de\u4f60\u53ef\u771f\u662f\u505a\u4e86\u8680\u672c\u751f\u610f\u4e86\u3002\u672c\u6765\u662f\u6211\u8981\u7ed9\u4f60\u505a\u5bff\u7684\u3002\u5982\u4eca \u6211\u53ea\u51fa\u4e00\u5f20\u5634\uff0c\u4e00\u5207\u8fd8\u662f\u8981\u52b3\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u9152\u83dc\u5f04\u597d\uff0c\u660e\u6708\u5df2\u6302\u677e\u68a2\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u559d\u4e86\u4e24\u676f\uff0c\u82e5\u6709\u6240\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u7684\u6708\u4eae\u53c8\u5927\u53c8\u5706\uff0c\u662f\u4e0d\u662f\u4e2d\u79cb\u5df2\u7ecf\u5230\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u7684\u751f\u65e5\u662f\u4e2d\u79cb\u524d\u4e09\u5929\uff0c\u5df2\u7ecf\u8fc7\u4e86\u4e24\u5929\uff0c\u4eca\u5929\u5e94\u8be5\u662f\u516b\u6708\u5341\u56db\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e5f\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u559c\u6b22\u4e2d\u79cb\u3002\u5c31\u5f53\u4eca\u665a\u662f\u4e2d\u79cb\u597d\u4e86\u3002\u5927\u54e5\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u56e0\u4e3a\u6bcf\u9022\u4f73\u8282\u500d\u601d \u4eb2\u800c\u751f\u611f\u89e6\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u7684\u4eb2\u4eba\u53ea\u6709\u4f60\u4e86\uff0c\u4f60\u5c31\u5728\u6211\u7684\u8eab\u8fb9\uff0c\u4f55\u7528\u601d\u5ff5\uff1f\u6211\u53ea\u662f\u60f3\u8d77\u82cf\u4e1c\u5761\u5199\u7684 \u4e00\u9996\u8bcd\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201d\u662f\u4e0d\u662f\u82cf\u4e1c\u5761\u5728\u4e2d\u79cb\u4e4b\u591c\u4f5c\u7684\u90a3\u9996\u300a\u6c34\u8c03\u6b4c\u5934\u300b\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u771f\u806a \u660e\uff0c\u4e00\u731c\u5c31\u4e2d\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u5728\u4e34\u5b89\u8ddf\u7237\u7237\u5356\u5531\u7684\u65f6\u5019\uff0c\u6bcf\u5e74\u4e2d\u79cb\uff0c\u90a3\u4e9b\u8fbe\u5b98\u8d35\u4eba\u6e38\u897f\u6e56\u8d4f\u6708\uff0c\u90fd\u559c \u6b22\u70b9\u5531\u8fd9\u9996\u8bcd\u5e94\u666f\uff0c\u6211\u5df2\u4e0d\u77e5\u5531\u8fc7\u591a\u5c11\u904d\u4e86\u3002\u201d \u3000\u3000\u503c\u7fbd\u51b2\u6015\u5979\u63d0\u8d77\u7237\u7237\u6613\u751f\u4f24\u611f\uff0c\u5c94\u5f00\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u5439\u8427\uff0c\u4f60\u6765\u5531\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5fc3\u5934\u4e00\u52a8\uff0c\u82e5\u6709\u6240\u609f\uff0c\u95ee\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4e3a\u4ec0\u4e48\u60f3\u8d77\u8fd9\u9996\u8bcd\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u82cf\u4e1c\u5761\u8fd9\u9996\u8bcd\u662f\u4e3a\u4e86\u6000\u5ff5\u4ed6\u7684\u5f1f\u5f1f\u800c\u4f5c\u7684\u3002\u4ed6\u81ea\u79f0\u2018\u4e19\u8fb0\u4e2d\u79cb\uff0c\u6b22\u996e\u8fbe \u65e6\uff0c\u5927\u9189\uff0c\u4f5c\u6b64\u7bc7\uff0c\u517c\u6000\u5b50\u7531\u3002\u2019\u5b50\u7531\u5c31\u662f\u4ed6\u7684\u5f1f\u5f1f\u82cf\u8f99\u3002\u6211\u6ca1\u6709\u4e1c\u897f\u7ed9\u4f60\u4f5c\u751f\u65e5\u793c\u7269\uff0c\u5c31 \u501f\u4ed6\u8fd9\u9996\u8bcd\u9001\u7ed9\u4f60\u5427\u3002\u4ed6\u662f\u72ec\u5bf9\u660e\u6708\uff0c\u5144\u5f1f\u5404\u5728\u4e00\u65b9\uff0c\u54b1\u4eec\u5374\u80fd\u540c\u4e00\u5904\u6b22\u996e\uff0c\u80dc\u4ed6\u591a\u4e86\u3002\u201d \u4ed6\u548c\u949f\u7075\u79c0\u5f02\u59d3\u5144\u59b9\uff0c\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5373\u662f\u628a\u5f02\u59d3\u5144\u59b9\u6bd4\u4f5c\u624b\u8db3\u4e4b\u4f59\u3002\u4f46\u53e6\u5916\u4e00\u5c42\u7684\u610f\u601d\uff0c\u4ea6\u5373 \u662f\u5144\u59b9\u5c31\u53ea\u80fd\u662f\u5144\u59b9\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u6bd5\u610f\u53ea\u662f\u4e2a\u60c5\u7aa6\u521d\u5f00\u7684\u5c0f\u59d1\u5a18\uff0c\u5979\u53ef\u4e0d\u4f1a\u8f6c\u4e2a\u5f2f\u53bb\u60f3\u90a3\u66f4\u6df1\u4e00\u5c42\u7684\u610f\u601d\uff0c\u767b\u65f6\u559c \u4e0a\u7709\u68a2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4efd\u751f\u65e5\u793c\u7269\u771f\u662f\u592a\u597d\u4e86\uff0c\u597d\uff0c\u54b1\u4eec\u5c31\u5f00\u59cb\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8c03\u5300\u6c14\u606f\uff0c\u6309\u62cd\u5439\u7bab\uff0c\u949f\u7075\u79c0\u66fc\u58f0\u4f4e\u5531\uff1a \u3000\u3000\u201c\u660e\u6708\u51e0\u65f6\u6709\uff0c\u628a\u9152\u95ee\u9752\u5929\u3002 \u3000\u3000\u4e0d\u77e5\u5929\u4e0a\u5bab\u9619\uff0c\u4eca\u5915\u662f\u4f55\u5e74\uff1f \u3000\u3000\u6211\u6b32\u4e58\u98ce\u5f52\u53bb\uff0c\u53c8\u6050\u743c\u697c\u7389\u5b87\uff0c\u9ad8\u5904\u4e0d\u80dc\u5bd2\u3002 \u3000\u3000\u8d77\u821e\u5f04\u6e05\u5f71\uff0c\u4f55\u4f3c\u5728\u4eba\u95f4\uff1f \u3000\u3000\u8f6c\u6731\u9601\uff0c\u4f4e\u7eee\u6237\uff0c\u7167\u65e0\u7720\u3002 \u3000\u3000\u4e0d\u5e94\u6709\u6068\uff0c\u4f55\u4e8b\u957f\u5411\u522b\u65f6\u5706\u3002 \u3000\u3000\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be\u5168\uff01 \u3000\u3000\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc\u5171\u5a75\u5a1f\u3002\u201d \u3000\u3000\u4e00\u66f2\u594f\u7f62\uff0c\u4f59\u97f3\u8885\u8885\u3002\u949f\u7075\u79c0\u7ec6\u5473\u540c\u4e2d\u7684\u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be \u5168\uff01\u201d\u7684\u60c5\u610f\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u6211\u5439\u5f97\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u5439\u5f97\u597d\u6781\u4e86\u3002\u771f\u7684\uff0c\u6211\u4e0d\u662f\u548c\u4f60\u8bf4\u5ba2\u6c14\u8bdd\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u89c1\u4f60\u4e0d\u8bf4\u8bdd\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5acc\u6211\u5439\u5f97\u4e0d\u597d\u5462\u3002\u90a3\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f \u3000\u3000\u949f\u7075\u79c0\u603b\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u606d\u559c\u4f60\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u6014\u9053\uff1a\u201c\u606d\u559c\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u652f\u66f2\u5b50\u662f\u5f88\u96be\u5439\u7684\uff0c\u4f60\u80fd\u591f\u4e00\u53e3\u6c14\u5439\u5230\u5e95\uff0c\u5706\u719f\u5982\u610f\uff0c\u5439\u5f97\u597d\u542c\u8fd8\u5176 \u6b21\uff0c\u82e5\u975e\u4e2d\u6c14\u5145\u6c9b\uff0c\u4f60\u4e5f\u5439\u4e0d\u51fa\u6765\uff0c\u8fd9\u624d\u662f\u6700\u53ef\u559c\u7684\u3002\u5927\u54e5\u54e5\uff0c\u5bf9\u4e8e\u6b66\u5b66\u6211\u867d\u7136\u61c2\u5f97\u4e0d\u591a\uff0c \u4f46\u4ece\u4f60\u5439\u7684\u8fd9\u652f\u66f2\u5b50\u4e5f\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\uff0c\u4f60\u8fd0\u7528\u4e39\u7530\u4e4b\u6c14\uff0c\u5df2\u662f\u5e76\u65e0\u963b\u6ede\u4e86\uff0c\u5bf9\u5417\uff1f\u6a80\u7fbd\u51b2\u7b11 \u9053\uff1a\u201c\u4f60\u679c\u7136\u662f\u77e5\u97f3\uff0c\u4e0d\u4ec5\u662f\u97f3\u4e50\u65b9\u9762\u7684\u77e5\u97f3\u800c\u5df2\u3002\u4e0d\u9519\uff0c\u6211\u8fd1\u6765\u5f97\u611f\u89c9\u4f3c\u4e4e\u6709\u70b9\u8fdb\u5883\uff0c\u4f46 \u8981\u60f3\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u90a3\u8fd8\u5dee\u5f97\u8fdc\u5462\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u6709\u8fdb\u5883\u5c31\u597d\uff0c\u4f60\u4f1a\u6162\u6162\u597d\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u5c31\u53ea\u6015\u6162\u5230\u54b1\u4eec\u7684\u5934\u53d1\u90fd\u767d\u4e86\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u8fd8\u662f\u8981\u4f60\u6276\u7740\u6211\u8d70\u8def\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u90a3\u4e5f\u5f88\u597d\u554a\uff0c\u4e0d\u6b63\u662f\u5c31\u5e94\u4e86\u767d\u5934\u5055\u8001\u8fd9\u53e5\u8bdd\u4e48\uff1f\u201d\u84e6\u5730\u7701\u8d77\uff0c\u8fd9\u53e5\u8bdd\u662f\u5f62 \u5bb9\u592b\u59bb\u6069\u7231\u7684\uff0c\u4e0d\u89c9\u9762\u7ea2\u8fc7\u8033\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u66ff\u4ed6\u89e3\u7a98\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4e48\u5230\u4e86\u660e\u5e74\u4eca\u665a\uff0c\u8fd8\u662f\u4f60\u6765\u5531\u66f2\uff0c\u6211\u6765\u5439\u8427\u3002 \u4ee5\u540e\u6bcf\u5e74\u4e2d\u79cb\uff0c\u90fd\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4eca\u5929\u662f\u516b\u6708\u5341\u56db\uff0c\u5e76\u975e\u4e2d\u79cb\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u54b1\u4eec\u53ef\u4ee5\u628a\u516b\u6708\u5341\u56db\u5f53\u4f5c \u4e2d\u79cb\uff0c\u5c31\u53ea\u662f\u54b1\u4eec\u4e24\u4e2a\u4eba\u7684\u4e2d\u79cb\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u6062\u590d\u5e38\u6001\uff0c\u6ee1\u5fc3\u6b22\u559c\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4e48\u6211\u7684\u751f\u65e5\u4ee5\u540e\u4e5f\u6539\u5230\u516b\u6708\u5341\u56db\u624d\u6765\u5e86 \u795d\uff0c\u4e00\u5207\u90fd\u50cf\u4eca\u665a\u4e00\u6837\uff0c\u90a3\u5c31\u66f4\u6709\u610f\u601d\u4e86\u3002\u4f46\u53ea\u6015\u2014\u2014\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d\u949f\u7075\u79c0 \u9053\uff1a\u201c\u5c31\u53ea\u6015\u4f60\u5728\u6211\u8eab\u8fb9\u5439\u7bab\uff0c\u60f3\u7684\u5374\u662f\u5343\u91cc\u4e4b\u5916\u7684\u5a75\u5a1f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5931\u7b11\u9053\uff1a\u201c\u5343\u91cc\u5171\u5a75\u5a1f\uff0c\u4e0d\u662f\u8fd9\u6837\u89e3\u7684\u3002\u8bcd\u4e2d\u7684\u2018\u5a75\u5a1f\u2019\u662f\u6307\u4e2d\u79cb\u7684\u660e\u6708\uff0c\u8fd9\u4e2a \u610f\u5ff5\u867d\u7136\u662f\u4ece\u2018\u6708\u4e2d\u4ed9\u5b50\u2019\u5f97\u6765\uff0c\u4f46\u5df2\u4e0d\u662f\u6307\u67d0\u4e00\u4e2a\u4f73\u4eba\u4e86\u3002\u66f4\u5e7f\u4e49\u7684\u8bf4\uff0c\u8bcd\u4e2d\u7684\u5a75\u5a1f\u53ef\u4ee5 \u4ee3\u8868\u4e00\u5207\u7f8e\u597d\u7684\u4e8b\u7269\u7684\u3002\u82cf\u4e1c\u5761\u56e0\u4e3a\u548c\u5f1f\u5f1f\u5206\u9694\u5343\u91cc\uff0c\u56e0\u6b64\u4ed6\u7684\u795d\u613f\u662f\uff0c\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc \u5171\u5a75\u5a1f\u3002\u7eb5\u7136\u76f8\u9694\u5343\u91cc\uff0c\u4e5f\u53ef\u4ee5\u540c\u4eab\u6708\u534e\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u8bf4\u63d0\u8bcd\u7684\u672c\u610f\u3002\u6211\u8bf4\u7684\u662f\u773c\u524d\u7684\u4e8b\u5b9e\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4f6f\u4f5c\u4e0d\u61c2\uff0c\u8bf4\u9053\uff1a\u201c\u773c\u524d\u7684\u4e8b\u5b9e\u5c31\u53ea\u6709\u6211\u548c\u4f60\uff0c\u54b1\u4eec\u5df2\u7ecf\u662f\u5728\u2018\u6b64\u65f6\u6b64\u5730\u5171\u5a75 \u5a1f\u2019\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5982\u679c\u54b1\u4eec\u6709\u4e00\u5929\u5206\u5f00\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u6211\u662f\u8d70\u4e0d\u52a8\u7684\uff0c\u9664\u975e\u662f\u4f60\u629b\u5f00\u6211\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u603b\u6709\u4e00\u5929\u53ef\u4ee5\u81ea\u5df1\u8d70\u7684\u3002\u5f53\u7136\u6211\u77e5\u9053\u4f60\u4e5f\u4e0d\u4f1a\u629b\u5f00\u6211\u7684\uff0c\u4f46\u53ea\u5f53\u4f5c\u5047\u8bbe \u5982\u4f55\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u82e5\u662f\u54b1\u4eec\u5206\u5f00\uff0c\u6211\u4e5f\u4f1a\u50cf\u82cf\u4e1c\u5761\u6000\u5ff5\u5f1f\u5f1f\u4e00\u6837\u6000\u5ff5\u4f60\u3002\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc \u5171\u5a75\u5a1f\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9765\u5982\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u591a\u8c22\u4f60\u5584\u9882\u5584\u7977\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u2014\u2014\u201d\u6a80\u7fbd\u51b2\u9053\uff1a \u201c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u6162\u58f0\u8bf4\u9053\uff1a\u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be\u5168\uff0c\u4e16\u4e8b\u54ea\u80fd\u6c38\u5982\u4eba\u610f\uff0c \u5982\u4eca\u6211\u4e5f\u60f3\u901a\u8fd9\u5c42\u9053\u7406\u4e86\u3002\u201d \u3000\u3000\u5979\u9762\u4e0a\u4ecd\u662f\u5e26\u7740\u7b11\u5bb9\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u798f\u6709\u53ef\u4eab\u5c3d\u3002\u5982\u4eca\u6211\u4e5f\u4e0d\u60f3\u5962\u6c42\u4e86\u3002\u4eca\u665a\u5f97\u4f60\u66ff\u6211 \u8865\u795d\u751f\u8fb0\uff0c\u4e0e\u6211\u5171\u4eab\u6708\u534e\uff0c\u6211\u5df2\u7ecf\u5fc3\u6ee1\u610f\u8db3\u3002\u201d \u3000\u3000\u4ed6\u5403\u60ca\u7684\u770b\u7740\u5979\uff0c\u201c\u8fd9\u5b69\u5b50\u2014\u2014\u554a\uff0c\u600e\u80fd\u8bf4\u5979\u8fd8\u662f\u5b69\u5b50\u5462\uff1f\u201d\u5979\u4e0d\u4f46\u6210\u719f\u7684\u50cf\u4e2a\u5927\u4eba\uff0c \u800c\u4e14\u50cf\u662f\u4e2a\u5386\u5c3d\u98ce\u971c\uff0c\u9971\u7ecf\u5fe7\u60a3\u7684\u5927\u4eba\u4e86\u3002 \u3000\u3000\u201c\u5927\u54e5\u54e5\uff0c\u591a\u8c22\u4f60\u3002\u54b1\u4eec\u5e72\u4e86\u8fd9\u676f\uff01\u201d \u3000\u3000\u4ed6\u60f3\u4e0d\u5230\u949f\u7075\u79c0\u5c45\u7136\u5f88\u80fd\u559d\u9152\uff0c\u949f\u7075\u79c0\u8fd8\u6ca1\u6709\u9189\uff0c\u4ed6\u5df2\u7ecf\u9189\u4e86\u3002\u53cb \u3000\u3000\u690d\u7fbd\u51b2\u9192\u6765\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u65e5\u4e0a\u4e09\u7aff\u3002 \u3000\u3000\u4ed6\u5f20\u5f00\u773c\u775b\uff0c\u5ffd\u7136\u770b\u89c1\u4e00\u4e2a\u957f\u5f97\u5f88\u79c0\u6c14\u7684\u5c11\u5e74\u7ad9\u5728\u5e8a\u524d\u3002\u4f3c\u7b11\u975e\u7b11\u7684\u770b\u7740\u4ed6\u3002 \u3000\u3000\u662f\u5bbf\u9152\u672a\u9192\uff0c\u9189\u773c\u770b\u82b1\uff1f\u4ed6\u63c9\u63c9\u773c\u775b\u3002\u770b\u6e05\u695a\u4e86\uff0c\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u54ea\u91cc\u6765\u7684\u4fca\u5c0f\u5b50 \u5462\uff0c\u539f\u6765\u662f\u4f60\u8fd9\u4e2a\u987d\u76ae\u4e2b\u5934\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u5957\u8863\u88f3\u662f\u6211\u7792\u7740\u4f60\u88c1\u7684\uff0c\u4f60\u77a7\u6211\u626e\u5f97\u50cf\u4e0d\u50cf\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5934\u53d1\u518d\u526a\u77ed\u4e00\u4e9b\uff0c\u55d3\u5b50\u518d\u7c97\u4e00\u4e9b\uff0c\u6211\u5c31\u53ef\u4ee5\u628a\u4f60\u5f53\u4f5c\u5c0f\u5144\u5f1f\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u653e\u5927\u55d3\u95e8\uff0c\u7c97\u58f0\u7c97\u6c14\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7684\u65e9\u9910\u548c\u5348\u9910\u6211\u90fd\u66ff\u4f60\u51c6\u5907\u597d\u4e86\u3002\u65e9\u9910\u662f \u5c71\u828b\uff0c\u52a0\u4e86\u7cd6\u53c8\u9999\u53c8\u751c\u3002\u5348\u9910\u662f\u4e00\u53ea\u70e4\u5c71\u9e21\uff0c\u5403\u4e0d\u5b8c\u8fd8\u53ef\u4ee5\u7559\u5230\u665a\u4e0a\u5403\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u5e94\u8be5\u6dfb\u70b9\u4e1c\u897f\u4e86\uff0c\u4eca\u5929\u662f\u2018\u5916\u9762\u4eba\u2019\u8bf4\u7684\u4e2d\u79cb\u8282\uff0c\u53c8\u662f\u90a3\u5c0f\u9547\u7684\u589f \u671f\uff0c\u6211\u60f3\u53bb\u51d1\u4e2a\u70ed\u95f9\uff0c\u8981\u6253\u542c\u6d88\u606f\u4e5f\u5bb9\u6613\u4e00\u70b9\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u53ea\u6015\u5269\u4e0b\u7684\u91d1\u8c46\u5df2\u7ecf\u4e0d\u591f\u4f60\u6362\u4e1c\u897f\u4e86\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u662f\u53bb\u4e70\uff0c\u4e0d\u662f\u53bb\u2018\u6362\u2019\uff0c\u4e0a\u4e00\u6b21\u6211\u5df2\u7ecf\u628a\u4e00\u9897\u91d1\u8c46\u6362\u4e86\u5341\u4e24\u94f6\u5b50\uff0c \u8db3\u591f\u6211\u4e70\u4e1c\u897f\u5566\u3002\u5927\u54e5\u54e5\uff0c\u4f60\u60f3\u4e0d\u60f3\u5403\u6708\u997c\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6708\u997c\u5403\u4e0d\u5403\u4e5f\u7f62\uff0c\u6211\u53ef\u6709\u70b9\u62c5 \u5fc3\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8fd9\u4e2a\u5730\u65b9\u662f\u4e0d\u4f1a\u6709\u4eba\u8ba4\u8bc6\u6211\u7684\uff0c\u800c\u4e14\u522b\u4eba\u90fd\u5728\u5fd9\u7740\u4e70\u4e1c\u897f\u8fc7\u8282\u65e5\uff0c\u4e5f\u6ca1\u4eba \u6709\u90a3\u95f2\u5fc3\u6765\u6ce8\u610f\u6211\u3002\u5e02\u96c6\u8d8a\u70ed\u95f9\uff0c\u5c31\u8d8a\u5bb9\u6613\u6df7\u5f97\u8fc7\u53bb\u3002\u201d\u63a5\u7740\u7b11\u9053\uff1a\u201c\u4e0a\u6b21\u6211\u53ea\u80fd\u5077\u5077\u6478\u6478 \u53bb\u6362\u4e1c\u897f\uff0c\u867d\u7136\u4e0d\u662f\u5c0f\u5077\uff0c\u4e5f\u50cf\u5c0f\u5077\u4e00\u6837\u63d0\u5fc3\u540a\u80c6\uff0c\u597d\u4e0d\u6c14\u95f7\u3002\u4eca\u513f\u6211\u53ef\u4ee5\u5927\u6447\u5927\u6446\u53bb\u8d81\u589f \u4e86\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u5c31\u8ba9\u6211\u53bb\u8212\u5c55\u4e00\u4e0b\u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u73b0\u5728\u4e5f\u61c2\u5f97\u5bc2\u5bde\u662f\u4ec0\u4e48\u6ecb\u5473\u4e86\u3002\u4e0e\u4e16\u9694\u7edd\uff0c\u90a3\u65e5\u5b50\u603b\u662f\u8fc7 \u4e0d\u60ef\u7684\u3002\u201d\u4ed6\u672c\u60f3\u6307\u7740\u5979\u8fc7\u53bb\u8bf4\u8fc7\u7684\u8bdd\u53d6\u7b11\u5979\u51e0\u53e5\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u6837\u82b1\u6837\u5e74\u534e\u7684\u5c0f\u59d1\u5a18\u966a \u4f34\u81ea\u5df1\u5fcd\u53d7\u8fd9\u7a7a\u5c71\u5bc2\u5bde\uff0c\u5374\u662f\u4e0d\u5fcd\u53d6\u7b11\u5979\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u8d70\u540e\uff0c\u6a80\u7fbd\u51b2\u56de\u5473\u6628\u665a\u6e05\u4e8b\uff0c\u5fc3\u7eea\u4e0d\u89c9\u6709\u70b9\u4e0d\u5b81\uff0c\u4e0d\u77e5\u4eca\u540e\u662f\u5426\u8fd8\u80fd\u4e0e\u5979\u5144\u59b9\u76f8 \u5904\u3002\u4f46\u60f3\u8d77\u5979\u521a\u624d\u8fd8\u662f\u90a3\u6837\u7eaf\u771f\u65e0\u90aa\u7684\u6001\u5ea6\uff0c\u53c8\u7a0d\u5fae\u5fc3\u5bbd\u4e00\u4e9b\uff1b\u5fc3\u9053\uff1a\u201c\u6216\u8005\u53ea\u662f\u6211\u7684\u591a\u7591 \u5427\uff1f\u201d \u3000\u3000\u4ed6\u672c\u6765\u6bcf\u5929\u4e00\u65e9\u5c31\u8981\u7ec3\u529f\u7684\uff0c\u4f46\u4eca\u5929\u5374\u6709\u70b9\u4e24\u6837\u3002\u65e9\u9910\u5403\u8fc7\u4e86\uff0c\u5348\u9910\u4e5f\u5403\u8fc7\u4e86\uff0c\u4ed6\u8fd8\u662f \u95f7\u5750\u7a97\u524d\uff0c\u6d6e\u60f3\u8fde\u7fe9\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u4e0a\u6b21\u949f\u7075\u79c0\u4e0b\u5c71\uff0c\u4ed6\u867d\u5fc3\u4e2d\u6302\u5ff5\uff0c\u4f46\u8fd9\u4e00\u6b21\u4ed6\u5374\u662f\u66f4\u52a0\u76fc \u671b\u5979\u80fd\u591f\u65e9\u70b9\u56de\u6765\u3002\u65e5\u5934\u521a\u521a\u8fc7\u5348\u4ed6\u5c31\u5728\u7a97\u524d\u9065\u671b\u4e86\u3002\u201c\u8fd9\u662f\u5426\u53ea\u5c5e\u4e8e\u5144\u59b9\u7684\u5173\u5fc3\u5462\uff1f\u201d\u4ed6 \u5ffd\u5730\u5728\u5fc3\u91cc\u81ea\u5df1\u95ee\u81ea\u5df1\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u611f\u5230\u6000\u7591\u4e86\u3002\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u662f\u6ce8\u5b9a\u8981\u53d1 \u751f\u7684\u4e8b\u60c5\uff0c\u9632\u6b62\u4e5f\u9632\u6b62\u4e0d\u6765\uff0c\u53ea\u80fd\u4e00\u5207\u90fd\u542c\u5176\u81ea\u7136\u5427\u3002\u201d \u3000\u3000\u6b63\u81ea\u60c5\u601d\u60d8\u60d8\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u597d\u50cf\u542c\u5230\u4eba\u58f0\u3002 \u3000\u3000\u201c\u79c0\u59b9\u4e0d\u4f1a\u8fd9\u6837\u5feb\u56de\u6765\u5427\uff1f\u201d\u4ed6\u51dd\u795e\u7ec6\u542c\uff0c\u58f0\u97f3\u4ece\u5c4b\u540e\u9762\u7684\u6811\u6797\u4f20\u6765\u7684\uff0c\u4e0d\u53ea\u4e00\u4e2a\u4eba\u3002 \u4ed6\u867d\u7136\u534a\u8eab\u4e0d\u9042\uff0c\u4f46\u5185\u529f\u5df2\u7ecf\u6062\u590d\u51e0\u5206\uff0c\u4f0f\u5730\u542c\u58f0\uff0c\u8fd8\u662f\u53ef\u4ee5\u6bd4\u5e38\u4eba\u542c\u5f97\u66f4\u8fdc\u3002\u6765\u7684\u662f\u4e09\u4e2a \u4eba\uff0c\u8fb9\u8d70\u8fb9\u8c08\uff1a\u201c\u90a3\u662f\u8c01\u7684\u5c38\u4f53\uff1f\u201d \u3000\u3000\u201c\u662f\u54b1\u4eec\u603b\u5175\u7684\u536b\u58eb\u3002\u53bb\u5e74\u603b\u5175\u5dee\u4ed6\u4e0a\u4eac\u529e\u4e00\u4ef6\u516c\u4e8b\uff0c\u4ed6\u987a\u4fbf\u544a\u5047\u8fd8\u4e61\uff0c\u5374\u4e86\u5e74\u591a\uff0c\u4e00 \u76f4\u4e0d\u89c1\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u4f1a\u8ba4\u9519\uff1f\u201d \u3000\u3000\u201c\u7edd\u4e0d\u4f1a\u9519\uff0c\u4ed6\u66fe\u5728\u4f5c\u6218\u4e2d\u53d7\u8fc7\u4f24\uff0c\u989d\u9aa8\u88ab\u780d\u4e86\u4e00\u5200\u7684\u3002\u5c38\u8eab\u867d\u7136\u8150\u70c2\uff0c\u989d\u9aa8\u7684\u4f24\u75d5\u8fd8 \u5728\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u6b66\u529f\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u5728\u6211\u4eec\u8fd9\u4e2a\u8fb9\u5173\uff0c\u4ed6\u53ef\u4ee5\u7b97\u5f97\u662f\u5341\u540d\u4e4b\u5185\u7684\u52c7\u58eb\u3002\u201d \u3000\u3000\u2018\u5982\u6b64\u8bf4\u6765\uff0c\u80fd\u591f\u6740\u5bb3\u4ed6\u7684\u4eba\u6599\u975e\u7b49\u95f2\u4e4b\u8f88\u4e86\u3002\u201d \u3000\u3000\u201d\u4f60\u4eec\u4e0d\u7528\u731c\u7591\u4e86\uff0c\u4f9d\u6211\u770b\u4e00\u5b9a\u662f\u90a3\u5c0f\u5b50\u6240\u4e3a\uff01\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\uff0c\u6a80\u7fbd\u51b2\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u79c0\u59b9\u53bb\u5e74\u6740\u7684\u90a3\u4e2a\u519b\u5b98\u7ed9\u4ed6\u4eec\u53d1\u73b0\u4e86\u3002\u201d\u53c8\u60f3\uff1a \u201c\u8fd9\u4e09\u4e2a\u4eba\u867d\u7136\u662f\u4e00\u4f19\u7684\uff0c\u4f46\u8eab\u4efd\u5374\u597d\u50cf\u5404\u81ea\u4e0d\u540c\u3002\u7b2c\u4e09\u4e2a\u4eba\u8bf4\u7684\u90a3\u4e2a\u2018\u5c0f\u5b50\u2019\uff0c\u6050\u6015\u5c31\u662f \u6307\u6211\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e09\u4e2a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6015\u4ed6\u662f\u8d1d\u5b50\uff0c\u6211\u53ef\u4e0d\u6015\u3002\u83ab\u8bf4\u4ed6\u7956\u7236\u90a3\u4ee3\u65e9\u5df2\u6210\u4e3a\u94a6\u72af\uff0c\u5373\u4f7f\u4ed6 \u8fd8\u662f\u4e16\u88ad\u7684\u8d1d\u5b50\u3002\u6211\u4e5f\u4e0d\u80fd\u4e70\u4ed6\u7684\u5e10\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u6015\u4ed6\uff0c\u4f46\u542c\u8bf4\u7687\u4e0a\u8fd8\u662f\u8981\u7528\u4ed6\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u5c11\u62c5\u5fc3\uff0c\u4ed6\u5f97\u7f6a\u4e86\u6211\u4eec\u738b\u7237\uff0c\u53c8\u505a\u51fa\u8fd9\u7b49\u53db\u56fd\u7684\u4e3a\uff0c\u7687\u4e0a\u4e5f\u5e87\u62a4\u4e0d\u4e86\u4ed6\u7684\u3002\u6709\u738b\u7237 \u6491\u8170\uff0c\u6211\u4eec\u53ea\u7ba1\u5148\u65a9\u540e\u594f\uff01\u201d\u6a80\u7fbd\u51b2\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u4eba\u662f\u5b8c\u989c\u738b\u5e9c\u7684\uff0c\u602a\u4e0d\u5f97\u4ed6\u6700\u7316 \u72c2\uff01\u201d \u3000\u3000\u7b2c\u4e8c\u4eba\u9053\uff1a\u201c\u4f46\u542c\u8bf4\u4f60\u4eec\u7684\u683c\u683c\u53ef\u662f\u4ed6\u7684\u8001\u76f8\u597d\u5462\uff1f\u201d \u3000\u3000\u201c\u683c\u683c\u8fd8\u80fd\u5927\u5f97\u8fc7\u738b\u7237\u4e48\uff1f\u4f55\u51b5\u5979\u8fd8\u53ea\u662f\u5e72\u683c\u683c\u5462\uff01\u738b\u7237\u8868\u9762\u5ba0\u7231\u5979\uff0c\u90a3\u662f\u56e0\u4e3a\u5979\u8fd8\u80fd \u66ff\u738b\u7237\u529e\u4e00\u70b9\u4e8b\uff0c\u4f46\u5176\u5b9e\u4ea6\u5df2\u6697\u6d3e\u4eba\u4e34\u89c6\u5979\u7684\u4e86\uff0c\u5979\u82e5\u662f\u6562\u66ff\u90a3\u5c0f\u5b50\u51fa\u5934\uff0c\u5979\u5148\u5c31\u81ea\u8eab\u96be \u4fdd\uff01\u4e0d\u8fc7\uff0c\u6a80\u8d1d\u5b50\u7684\u6b66\u529f\u662f\u975e\u540c\u5c0f\u53ef\u7684\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u4eba\u6253\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u8fd9\u5c42\u4f60\u4eec\u4e0d\u7528\u62c5\u5fc3\uff0c\u90a3\u65e5\u5343\u67f3\u5e84\u4e4b\u6218\uff0c\u4ed6\u88ab\u6211\u4eec\u5e84\u4e3b\u6253\u4e86\u4e00\u638c\uff0c \u636e\u6211\u4eec\u5e84\u4e3b\u8bf4\uff0c\u7eb5\u4f7f\u4fdd\u4f4f\u6027\u547d\uff0c\u53ea\u6015\u4e5f\u8981\u53d8\u6210\u4e00\u4e2a\u5e9f\u4eba\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6599\u60f3\u9003\u4e0d\u8fc7\uff0c\u7d22\u6027\u5750\u4e86\u8d77\u6765\uff0c\u8c8c\u4f53\u60a0\u95f2\u5730\u5439\u8d77\u7bab\u6765\u3002 \u3000\u3000\u7bab\u58f0\u4e00\u8d77\uff0c\u8fd9\u4e09\u4e2a\u4eba\u98de\u5feb\u7684\u5c31\u6765\u5230\u4e86\u3002\u4f46\u4ed6\u4eec\u542c\u89c1\u6a80\u7fbd\u51b2\u7684\u7bab\u58f0\u60a0\u7136\u81ea\u5f97\uff0c\u4e00\u65f6\u95f4\u5012\u4e5f \u4e0d\u6562\u9c81\u83bd\u4ece\u4e8b\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u731c\u4e0d\u900f\u6a80\u7fbd\u51b2\u6446\u7684\u662f\u4e0d\u662f\u7a7a\u57ce\u8ba1\u3002 \u3000\u3000\u90a3\u738b\u5e9c\u6b66\u58eb\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u8fd9\u4e2a\u5730\u65b9\u600e\u80fd\u662f\u4f60\u4eec\u8d35\u4eba\u4f4f\u5f97\u60ef\u7684\uff1f\u563f\u563f\uff0c\u5373\u4f7f\u4f60\u613f \u610f\uff0c\u6211\u4eec\u738b\u7237\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u53d7\u59d4\u5c48\u5440\uff01\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u662f\u5949\u4e86\u738b\u7237\u4e4b\u547d\u8bf7\u4f60\u4e0a\u4eac\u5171\u4eab\u8363\u534e\u7684\uff0c\u4f60 \u83ab\u8981\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff01\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u5949\u4e86\u738b\u7237\u4e4b\u547d\u6765\u8bf7\u6211\u7684\uff0c\u5f88\u597d\uff0c\u90a3\u4e48\u5c31 \u8bf7\u4f60\u628a\u4e00\u53e5\u8bdd\u7ed9\u6211\u5e26\u56de\u53bb\u3002\u201d \u3000\u3000\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u4f60\u8bf4\uff01\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8bf7\u4f60\u544a\u8bc9\u738b\u7237\uff0c\u5728\u6211\u773c\u4e2d\uff0c\u72d7\u7a9d\u4e5f\u8981\u6bd4\u4ed6\u738b\u5e9c\u597d \u4e9b\u3001\u201d\u5f26\u5916\u4e4b\u97f3\uff0c\u5373\u662f\u9a82\u90a3\u6b66\u58eb\u4e0d\u8fc7\u662f\u6761\u72d7\u7f62\u4e86\u3002 \u3000\u3000\u90a3\u6b66\u58eb\u53d8\u4e86\u9762\u8272\uff0c\u201c\u54fc\u201d\u7684\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u5f53\u771f\u656c\u9152\u4e0d\u5403\u3002\u8981\u5403\u7f5a\u9152\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u656c\u9152\u4e5f\u597d\uff0c\u7f5a\u9152\u4e5f\u597d\uff0c\u4f60\u90fd\u6050\u6015\u8fd8\u6ca1\u6709\u8d44\u683c\u53eb\u6211\u559d\u5427\uff01\u201d \u3000\u3000\u90a3\u6b66\u58eb\u6c14\u5f97\u53cc\u773c\u53d1\u767d\uff0c\u4f46\u4ed6\u987e\u5fcc\u6a80\u7fbd\u51b2\u7684\u6b66\u529f\u4e86\u5f97\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u6562\u5982\u6b64\u5014\u5f3a\uff0c\u53ea\u6015 \u6240\u53d7\u4f24\u672a\u5fc5\u6709\u5982\u67f3\u5143\u7532\u8bf4\u7684\u90a3\u6837\u4e25\u91cd\uff01\u201d\u6012\u5728\u5fc3\u5934\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u672a\u6562\u83bd\u649e\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u8bf4\u8bdd\u7684\u662f\u90a3\u4e2a\u5343\u67f3\u5e84\u7684\u95e8\u5ba2\uff0c\u4ed6\u7684\u989d\u89d2\u6709\u4f24\u75a4\uff0c\u5728\u4ed6\u9634\u6d4b\u6d4b\u53d1\u7740\u51b7\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c \u7275\u52a8\u4f24\u75a4\uff0c\u8d8a\u53d1\u663e\u5f97\u53ef\u6016\u3002 \u3000\u3000\u90a3\u95e8\u5ba2\u9634\u6d4b\u6d4b\u7684\u8bf4\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u6211\u4e5f\u8981\u591a\u8c22\u4f60\uff0c\u591a\u8c22\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u662f\u7ed9\u6211\u7559\u4e0b\u8fd9\u4e2a \u4f24\u75a4\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u95e8\u5ba2\u5c31\u662f\u90a3\u65e5\u5728\u5343\u67f3\u5e84\u4e4b\u6218\u4e2d\uff0c\u8d81\u7740\u6a80\u7fbd\u51b2\u548c\u67f3\u5143\u7532\u4ea4\u624b\uff0c\u65e0\u6687\u517c\u987e\u7684\u65f6\u5019\u5411\u949f\u7075 \u79c0\u7a81\u65bd\u5077\u88ad\u7684\u90a3\u4e09\u4e2a\u4eba\u4e2d\u7684\u4e00\u4e2a\u3002\u4ed6\u5934\u4e0a\u7684\u4f24\u75a4\uff0c\u662f\u6a80\u7fbd\u51b2\u7528\u4e00\u679a\u94dc\u94b1\u6253\u4f24\u7684\u3002\u4e0d\u8fc7\uff0c\u6bd4\u5176 \u4ed6\u4e8c\u4eba\uff0c\u4ed6\u786e\u5b9e\u662f\u5df2\u7ecf\u7b97\u5f97\u201c\u5e78\u8fd0\u201d\u4e86\u3002\u53e6\u5916\u90a3\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7ed9\u6a80\u7fbd\u51b2\u7528\u5927\u6454\u7891\u624b\u6454\u5f97\u534a\u6b7b \u4e0d\u6d3b\uff0c\u4e00\u4e2a\u5219\u4e1a\u5df2\u6b7b\u5728\u949f\u7075\u79c0\u7684\u624b\u4e0b\u3002\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\uff0c\u96be\u9053\u4f60\u8fd8\u8981\u6765\u8ba8\u8d4f\u94b1 \u4e48\uff1f\u201d \u3000\u3000\u90a3\u95e8\u5ba2\u559d\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u662f\u95e8\u7f1d\u91cc\u770b\u4eba\uff0c\u5fd2\u4e5f\u628a\u4eba\u770b\u5c0f\u4e86\uff01\u5927\u4e08\u592b\u5e10\u76ee\u5206\u660e\uff0c\u4f60\u90a3\u679a \u81ed\u94b1\uff0c\u8001\u5b50\u52a0\u500d\u8fd8\u4f60\uff01\u201d \u3000\u3000\u4e00\u6296\u624b\uff0c\u4e09\u679a\u94dc\u94b1\u5411\u6a80\u7fbd\u51b2\u63b7\u53bb\u3002\u6a80\u7fbd\u51b2\u4f3c\u4e4e\u614c\u4e86\u624b\u811a\uff0c\u7f29\u4f4e\u4e86\u5934\uff0c\u94ee\u3001\u94ee\u3001\u94ee\u4e09\u58f0\u8fde \u73e0\u54cd\u8fc7\uff0c\u90a3\u4e09\u679a\u94dc\u94b1\u843d\u5728\u684c\u4e0a\uff0c\u5d4c\u6210\u2014\u4e2a\u54c1\u5f62\u3002\u90a3\u95e8\u5ba2\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u5728\u5343\u67f3\u5e84\u7684 \u5a01\u98ce\u54ea\u91cc\u53bb\u4e86\uff0c\u600e\u7684\u7adf\u53d8\u4f5c\u4e86\u7f29\u5934\u4e4c\u9f9f\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u90a3\u4e2a\u738b\u5e9c\u6b66\u58eb\uff0c\u80c6\u5b50\u767b\u65f6\u58ee\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\u5982\u679c\u8fd8\u6709\u534a\u5206\u6b66\u529f\uff0c\u7109\u80fd \u5bb9\u5fcd\u5982\u6b64\u4fae\u8fb1\uff1f\u201d\u559d\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u8fd8\u8981\u88c5\u6a21\u4f5c\u6837\u5417\uff1f\u7ed9\u6211\u6eda\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u50ac\u5f97\u5982\u6b64\u6025\uff0c\u4f60\u542c\u6211\u5439\u5b8c\u8fd9\u652f\u66f2\u5b50\u518d\u8d70\u4e0d\u8fdf\uff01\u201d\u90a3\u6b66\u58eb\u9053\uff1a \u201c\u54fc\uff0c\u6211\u5012\u8981\u770b\u4f60\u8fd8\u4e0d\u4ec0\u4e48\u82b1\u62db\uff1f\u201d\u4ed6\u89c1\u6a80\u7fbd\u51b2\u597d\u6574\u4ee5\u6687\uff0c\u6bd5\u7adf\u8fd8\u662f\u6709\u4e9b\u987e\u5fcc\u3002\u90a3\u95e8\u5ba2\u5374\u662f \u62a5\u4ec7\u5fc3\u5207\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77e5\u9053\u4ed6\u662f\u88c5\u6a21\u6837\u6837\uff0c\u8fd8\u6015\u4ed6\u4f5c\u751a\uff01\u54fc\uff0c\u4f60\u6015\u4ed6\uff0c\u6211\u53ef\u4e0d\u6015 \u4ed6\uff01\u6a80\u7fbd\u51b2\uff0c\u4f60\u53d8\u6210\u7f29\u5934\u4e4c\u9f9f\uff0c\u8001\u5b50\u4e5f\u80fd\u628a\u4f60\u6293\u51fa\u6765\uff01\u201d\u4ed6\u7528\u7684\u5175\u5668\u662f\u4e00\u4e2a\u8fde\u7740\u94c1\u94fe\u7684\u94a2 \u722a\uff0c\u653e\u5c3d\u4e86\u53ef\u8fbe\u4e09\u4e08\u5f00\u5916\uff0c\u4e00 \u3000\u3000\u6296\u624b\uff0c\u94a2\u722a\u98de\u51fa\uff0c\u6a80\u7fbd\u51b2\u4e00\u4fa7\u5934\u3001\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u94a2\u722a\u6253\u7740\u4ed6\u5750\u7740\u7684\u90a3\u5f20\u6905\u80cc\u3002 \u3000\u3000\u8fd9\u4e00\u6293\u867d\u7136\u6ca1\u6709\u6293\u4f24\u6a80\u7fbd\u51b2\uff0c\u4f46\u5df2\u662f\u8feb\u4f7f\u4ed6\u201c\u9732\u4e86\u5e95\u201d\u4e86\uff0c\u90a3\u6b66\u58eb\u5fc3\u5934\u5927\u559c\uff1a\u201c\u539f\u6765\u4ed6 \u679c\u7136\u534a\u8eab\u4e0d\u9042\uff01\u201d\u4ed6\u7684\u529f\u592b\u672c\u6765\u5c31\u6bd4\u90a3\u95e8\u5ba2\u9ad8\u5f97\u591a\uff0c\u600e\u80fd\u7518\u53d7\u90a3\u95e8\u5ba2\u5632\u7b11\uff0c\u5f53\u4e0b\u4e00\u58f0\u5927\u559d\u3002 \u201c\u8fd9\u676f\u7f5a\u9152\uff0c\u4f60\u662f\u559d\u5b9a\u7684\u5566\uff01\u201d\u51b2\u8fdb\u8305\u5c4b\uff0c\u4e00\u5200\u5c31\u5411\u6a80\u7fbd\u51b2\u5288\u4e0b\u53bb\uff01 \u3000\u3000\u90a3\u519b\u5b98\u53eb\u9053\uff1a\u201c\u5200\u4e0b\u7559\u4eba\uff0c\u4e0d\u53ef\u80e1\u6765\uff01\u201d \u3000\u3000\u4f46\u5df2\u7ecf\u8fdf\u4e86\uff0c\u6b66\u58eb\u90a3\u4e00\u5200\u5df1\u7ecf\u5288\u4e0b\u53bb\u4e86\uff01\u4e0d\u8fc7\uff0c\u5200\u950b\u7a0d\u7a0d\u504f\u4e86\u4e00\u4e9b\uff0c\u4ed6\u4e0d\u662f\u780d\u6a80\u7fbd\u51b2\u7684 \u8111\u888b\uff0c\u800c\u662f\u5288\u4ed6\u53f3\u80a9\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u7435\u7436\u9aa8\u82e5\u7ed9\u7834\u788e\uff0c\u591a\u597d\u7684\u6b66\u529f\uff0c\u4e5f\u8981\u4f5c\u5e9f\u3002 \u3000\u3000\u5200\u51fa\u82e5\u98ce\uff0c\u52bf\u52b2\u529b\u731b\uff0c\u90a3\u519b\u5b98\u5927\u60ca\u5931\u8272\uff0c\u8981\u963b\u6b62\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u76fc\u671b\u8fd9\u4e00\u5200\u53ea\u662f\u6bc1\u4e86\u6a80\u7fbd\u51b2\u7684\u6b66\u529f\uff0c\u800c\u4e0d\u81f3\u4f24\u4e86\u4ed6\u7684\u6027\u547d\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u597d\u50cf\u7ed9\u5413\u50bb\u4e86\u4e00\u822c\uff0c\u8fd8\u5728\u5439\u7bab\uff0c\u4ed6\u907f\u5f97\u5f00\u8fd9\u4e00\u5200\u5417\uff1f \u3000\u3000\u65e5\u843d\u897f\u5c71\uff0c\u949f\u7075\u79c0\u8e0f\u7740\u665a\u971e\u56de\u6765\u3002\u5979\u7684\u79c0\u8138\u4e5f\u50cf\u665a\u971e\u4e00\u6837\u8273\u4e3d\u3002 \u3000\u3000\u8fd9\u5929\u5979\u5728\u90a3\u5c0f\u9547\u505a\u4e86\u4e00\u4ef6\u81ea\u9e23\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u60f3\u5230\u5f00\u5fc3\u4e4b\u5904\uff0c\u8fd8\u5fcd\u4e0d\u4f4f\u8981\u7b11\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4ea8\u4ea8\u537f\u537f\u4e4b\u58f0\uff0c\u4e0d\u50cf\u866b\u53eb\uff0c\u949f\u7075\u79c0\u6709\u70b9\u5947\u602a\uff0c\u62ac\u8d77\u5934\u671b\u53bb\u3002 \u3000\u3000\u5979\u521a\u62ac\u5934\u6765\uff0c\u9661\u5730\u5c31\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u5484\uff0c\u4ec0\u4e48\u4eba\uff0c\u7ed9\u6211\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u53ea\u89c1\u6709\u4e24\u4e2a\u4eba\u6b63\u597d\u5411\u5979\u8d70\u6765\uff0c\u4e00\u4e2a\u662f\u91d1\u56fd\u519b\u5b98\u7684\u670d\u9970\uff0c\u4e00\u4e2a\u662f\u989d\u89d2\u6709\u4f24\u75a4\u7684\u5927\u6c49\u3002 \u3000\u3000\u519b\u5b98\u5979\u6ca1\u89c1\u8fc7\uff0c\u90a3\u4e2a\u5343\u67f3\u5728\u95e8\u5ba2\u53ef\u662f\u548c\u5979\u4ea4\u8fc7\u624b\u7684\u3002\u5979\u7981\u4e0d\u4f4f\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u6562\u4f5c\u58f0\u4e86\u3002 \u3000\u3000\u5979\u5f97\u6a80\u7fbd\u51b2\u4f20\u6388\u6b66\u529f\uff0c\u5c06\u8fd1\u4e00\u5e74\uff0c\u65e9\u5df2\u662f\u4eca\u975e\u6614\u6bd4\uff0c\u5979\u7684\u5403\u60ca\uff0c\u5e76\u4e0d\u662f\u5bb3\u6015\u654c\u4e0d\u8fc7\u8fd9\u4e24 \u4e2a\u4eba\uff0c\u800c\u662f\u5bb3\u6015\u7ed9\u4ed6\u4eec\u8bc6\u7834\uff0c\u90a3\u5c31\u4f1a\u8fde\u7d2f\u4e86\u6a80\u7fbd\u51b2\u4e86\u3002\u90a3\u95e8\u5ba2\u7684\u8db3\u90e8\u597d\u50cf\u662f\u53d7\u4e86\u4f24\uff0c\u8d70\u8d77\u8def \u6765\u4e00\u8ddb\u4e00\u62d0\uff0c\u4f46\u8fd8\u662f\u8d70\u5f97\u5f88\u5feb\u3002\u4ed6\u8d70\u5230\u949f\u7075\u79c0\u8ddf\u524d\uff0c\u5b9a\u7740\u773c\u775b\u770b\u5979\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f \u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u662f\u54d1\u5df4\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u6b63\u81ea\u5fc3\u60f3\uff1a\u201c\u88c5\u54d1\u5df4\u5012\u662f\u4e2a\u597d\u751f\u610f\u3002\u201d\u5fc3\u5ff5\u672b\u5df2\uff0c\u53ea\u89c1\u90a3\u95e8\u5ba2\u5df2\u5728\u628a\u8170\u5200\u62d4\u4e86\u51fa \u6765\uff0c\u51b7\u51b7\u8bf4\u9053\u201c\u4f60\u60f3\u88c5\u54d1\u5df4\u9a97\u6211\uff0c\u597d\uff0c\u4e14\u5f85\u6211\u780d\u4f60\u4e00\u5200\u8bd5\u8bd5\uff0c\u770b\u4f60\u662f\u4e0d\u662f\u54d1\u5df4\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u4e0d\u77e5\u8fd9\u662f\u6c5f\u6e56\u4e0a\u5e38\u7528\u7684\u6050\u5413\u624b\u6cd5\uff0c\u5fc3\u60f3\u53ef\u662f\u4e0d\u80fd\u8ba9\u4ed6\u8bd5\u7684\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u662f\u751f\u5ba2\uff0c \u6211\u6ca1\u6709\u95ee\u4f60\uff0c\u4f60\u5012\u95ee\u8d77\u6211\u6765\u4e86\uff1f\u201d \u3000\u3000\u90a3\u95e8\u5ba2\u542c\u51fa\u5979\u662f\u634f\u7740\u55d3\u5b50\u8bf4\u8bdd\uff0c\u8d8a\u53d1\u7591\u4e5f\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4f4f\u5728\u8fd9\u5c71\u4e0a\u7684\u5417\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u5bb6\u4e09\u4ee3\u90fd\u662f\u5728\u8fd9\u5c71\u4e0a\u6253\u67f4\u7684\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u95e8\u5ba2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e0b\u5b50\u4f60\u53ef\u9732\u51fa\u9985\u513f\u4e86\uff0c\u8fd9\u5c71\u4e0a\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u4eba\u5bb6\uff1f\u4f60\u662f\u7ed9\u90a3\u59d3\u6a80 \u7684\u5c0f\u5b50\u6765\u9001\u98df\u7cae\u7684\u5427\uff1f\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u5426\u5219\u6211\u5bb0\u4e86\u4f60\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u5fc3\u5934\u53eb\u82e6\uff1a\u201c\u539f\u6765\u4ed6\u5df2\u7ecf\u77e5\u9053\u6211\u7684\u5927\u54e5\u54e5\u662f\u8eb2\u5728\u5c71\u4e0a\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u5012\u662f\u4e0d\u60f3\u591a\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u5bc6\u6797\u6df1\u5904\u662f\u8fd8\u53e6\u6709\u4eba\u5bb6\uff0c\u54b1\u4eec\u672a\u66fe\u53d1\u73b0\u3002\u201d\u90a3\u95e8 \u5ba2\u9053\uff1a\u201c\u4f60\u77a7\u5979\u8fd9\u6a21\u6837\u50cf\u4e2a\u6253\u67f4\u7684\u4e48\uff1f\u6211\u77a7\u5979\u5012\u662f\u50cf\u4e2a\u5927\u59d1\u5a18\uff01\u201d\u949f\u7075\u79c0\u5973\u626e\u7537\u88c5\uff0c\u867d\u7136\u4e1a \u5df2\u6539\u5bb9\u8c8c\uff0c\u4f46\u5341\u5206\u7ea4\u7ec6\uff0c\u4e00\u770b\u5c31\u77e5\u4e0d\u662f\u5e72\u7c97\u6d3b\u7684\u3002 \u3000\u3000\u949f\u7075\u79c0\u53d8\u4e86\u9762\u5305\uff0c\u5f3a\u4f5c\u9547\u5b9a\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u6ca1\u5de5\u592b\u548c\u4f60\u7ea0\u7f20\uff0c\u8ba9\u5f00\uff01\u201d\u5979\u7528\u5047 \u55d3\u5b50\u8bf4\u8bdd\uff0c\u4e00\u6025\uff0c\u88c5\u7537\u58f0\u66f4\u52a0\u4e0d\u50cf\u4e86\u3002\u90a3\u519b\u5b98\u4e5f\u662f\u4e0d\u89c9\u8d77\u7591\u548c\u90a3\u95e8\u5ba2\u4e00\u6837\u76ef\u7740\u5979\u770b\u4e86\u3002 \u3000\u3000\u90a3\u519b\u5b98\u4e5f\u770b\u51fa\u6765\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u773c\u529b\u4e0d\u9519\uff0c\u679c\u7136\u662f\u4e2a\u5973\u7684\u3002\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u95e8\u5ba2\u9053\uff1a\u201c\u5979\u5c31\u662f\u90a3\u65e5\u548c\u6a80\u7fbd\u51b2\u4e00\u8d77\u5728\u5343\u67f3\u5e84\u6740\u4e86\u6211\u7ed3\u62dc\u5144\u5f1f\u7684\u90a3\u4e2a\u81ed\u5a18\u4eec\uff01\u201d\u8bf4\u8bdd \u4e4b\u95f4\uff0c\u5df2\u662f\u79d1\u5f00\u8fde\u7740\u94c1\u94fe\u7684\u94a2\u722a\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5411\u7740\u949f\u7075\u79c0\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u3002 \u3000\u3000\u949f\u7075\u79c0\u4e00\u95ea\u95ea\u5f00\uff0c\u559d\u9053\uff1a\u201c\u90a3\u65e5\u6211\u7684\u5927\u54e5\u54e5\u5df2\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u7834\u4f60\u4e0d\u6b7b\uff0c\u4f60\u628a\u4ed6\u600e\u6837 \u4e86\uff01\u201d\u90a3\u95e8\u5ba2\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u60c5\u54e5\u54e5\u5df2\u7ecf\u7ed9\u6211\u6740\u6389\u5566\uff0c\u6ca1\u4eba\u4fdd\u62a4\u4f60\u4e86\uff0c\u4f60\u8981\u6d3b\u547d\uff0c\u5feb\u5feb\u6295 \u964d\uff01\u201d\u949f\u7075\u79c0\u4e0d\u77e5\u771f\u5047\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u6765\u5bb3\u6211\u7684\u5927\u54e5\u54e5\uff0c\u4f60\u6295\u964d\u6211\u4e5f\u4e0d\u9976\u4f60\uff01\u201d \u3000\u3000\u90a3\u95e8\u5ba2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u81ed\u5c0f\u5a18\uff0c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u6211\u5148\u5e9f\u4e86\u4f60\u7684\u6b66\u529f\uff01\u201d\u4ed6\u7684\u94a2\u94c1\u722a\uff0c\u8fde\u7740\u94c1 \u94fe\uff0c\u6296\u5f00\u6765\u53ef\u8fbe\u4e09\u4e08\u5f00\u5916\uff0c\u53c8\u5411\u949f\u7075\u79c0\u7684\u7435\u7436\u9aa8\u6293\u6765\u4e86\uff01 \u3000\u3000\u949f\u7075\u79c0\u8fd9\u56de\u53ef\u662f\u51fa\u624b\u4e0d\u7559\u60c5\u4e86\uff0c\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u7528\u4e86\u4e2a\u632a\u79fb\u624b\u6cd5\uff0c\u628a\u94a2\u722a\u8f7b\u8f7b\u4e00\u62e8\uff0c\u94a2 \u722a\u8f6c\u4e86\u65b9\u5411\uff0c\u98de\u56de\u6765\u53cd\u6293\u4e3b\u4eba\u3002\u90a3\u519b\u5b98\u8fde\u5fd9\u4e0a\u6765\u5e2e\u624b\u3002 \u3000\u3000\u90a3\u95e8\u5ba2\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5c0f\u59d1\u5a18\u7684\u6b66\u529f\u5df1\u662f\u4eca\u975e\u6614\u6bd4\uff0c\u6765\u4e0d\u53ca\u629b\u5f00\u94c1\u94fe\uff0c\u5df1\u7ed9\u94a2\u722a\u6293\u4f4f\uff0c \u75db\u5f7b\u5fc3\u80ba\u3002\u4ed6\u53f3\u817f\u672c\u5df1\u53d7\u4f24\uff0c\u7ad9\u7acb\u4e0d\u7a33\u60e8\u53eb\u4e00\u58f0\uff0c\u5e26\u7740\u94a2\u722a\uff0c\u9aa8\u788c\u788c\u7684\u5c31\u6eda\u6eda\u4e0b\u4e86\u5c71\u5761\u3002\u90a3 \u519b\u5b98\u62e8\u51fa\u8170\u5200\uff0c\u53cd\u8f6c\u5200\u80cc\uff0c\u5411\u949f\u7075\u79c0\u62cd\u4e0b\u3002\u4ed6\u8fd8\u662f\u53ea\u60f3\u628a\u949f\u7075\u79c0\u6253\u6655\u7684\u3002\u949f\u7075\u79c0\u4f7f\u4e86\u4e00\u62db\u7a7a \u624b\u5165\u767d\u5203\u7684\u624b\u6cd5\uff0c\u4e00\u6258\u4ed6\u7684\u8098\u90e8\uff0c\u53cd\u624b\u5c31\u593a\u4e86\u4ed6\u7684\u8170\u5200\u3002\u90a3\u519b\u5b98\u542c\u5f97\u540c\u4f34\u6eda\u4e0b\u5c71\u5761\u7684\u60e8\u53eb \u58f0\uff0c\u5413\u5f97\u614c\u4e86\uff0c\u5175\u5203\u88ab\u593a\uff0c\u8f6c\u8eab\u5c31\u8dd1\u3002 \u3000\u3000\u949f\u7075\u79c0\u559d\u9053\uff1a\u201c\u4f60\u4f3c\u4e4e\u6bd4\u4f60\u7684\u540c\u4f34\u597d\u4e9b\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u6d3b\u7740\u56de\u53bb\uff0c\u4f60\u8ba4\u547d\u7f62\uff01\u201d\u628a\u593a\u6765 \u7684\u8170\u5200\u98de\u51fa\uff0c\u63d2\u5165\u519b\u5b98\u7684\u540e\u5fc3\uff0c\u519b\u5b98\u4e5f\u5e26\u7740\u8170\u5200\u6eda\u4e0b\u5c71\u5761\u53bb\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u6000\u7740\u60f4\u60f4\u4e0d\u5b89\u7684\u5fc3\u60c5\uff0c\u8d76\u5fd9\u8dd1\u56de\u201c\u5bb6\u201d\u4e2d\u3002 \u3000\u3000\u5929\u8272\u5df2\u662f\u5165\u9ed1\u65f6\u5206\uff0c\u5979\u4e00\u56de\u5230\u5bb6\uff0c\u5c31\u95fb\u5230\u4e00\u80a1\u8840\u8165\u6c14\u5473\uff0c\u53ea\u89c1\u4e00\u5177\u5c38\u4f53\u5012\u5367\u5728\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u949f\u7075\u79c0\u5fc3\u5934\u535c\u535c\u7684\u8df3\uff0c\u65e0\u6687\u628a\u90a3\u5c38\u4f53\u7ffb\u8f6c\u6765\u770b\u662f\u4f55\u4eba\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff01\u201d\u8fd9\u4e00\u77ac \u95f4\uff0c\u5b9e\u662f\u6050\u60e7\u5230\u4e86\u6781\u70b9\uff0c\u597d\u50cf\u7b49\u5f85\u4e86\u4e00\u4e2a\u6f2b\u957f\u7684\u9ed1\u591c\uff0c\u201c\u5927\u54e5\u8fd8\u80fd\u56de\u7b54\u6211\u4e48\uff1f\u201d\u90a3\u5c38\u4f53\u5373\u4f7f \u4e0d\u662f\u5927\u54e5\u54e5\uff0c\u53ea\u6015\u4ed6\u4e5f\u53d7\u4e86\u4f24\u5427\uff1f\u201d \u3000\u3000\u8ff7\u5e95\u7acb\u5373\u63ed\u5f00\uff0c\u5979\u5fc3\u5ff5\u672a\u5df2\u3001\u53ea\u542c\u5f97\u4e00\u4e2a\u67d4\u548c\u7684\u58f0\u97f3\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u56de\u6765\u4e86 \u4e48\u3002\u5929\u5df2\u9ed1\u4e86\uff0c\u9ebb\u70e6\u4f60\u70b9\u4eae\u6cb9\u706f\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5fc3\u5934\u4e00\u5b9a\uff0c\u64e6\u71c3\u706b\u77f3\uff0c\u5927\u53e3\u5927\u53e3\u7684\u5598\u7740\u6c14\u53eb\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u5413\u6b7b\u6211\u4e86\uff0c\u4f60\u6ca1\u4e8b \u5427\uff1f\u201d\u53ea\u89c1\u6a80\u7fbd\u51b2\u5750\u5728\u9760\u7a97\u7684\u6905\u5b50\u4e0a\uff0c\u8863\u88f3\u6ee1\u662f\u8840\u6e0d\uff0c\u706b\u5149\u4e0b\u662f\u4e00\u7247\u6697\u8d6d\u7684\u989c\u8272\uff0c\u4ee4\u5f97\u949f\u7075 \u79c0\u7684\u4e00\u9897\u5fc3\u53c8\u5267\u8df3\u4e86\uff0c\u5979\u7684\u624b\u4e00\u98a4\uff0c\u706b\u5149\u7184\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u6211\u8981\u662f\u6709\u4e8b\uff0c\u8fd8\u80fd\u548c\u4f60\u8fd9\u6837\u8bf4\u8bdd\u4e48\uff1f\u5bf9\u4e0d\u4f4f\uff0c\u4f60\u7ed9\u6211\u4e70\u7684\u65b0\u8863\uff0c\u88ab\u522b\u4eba \u7684\u8840\u6c61\u4e86\u3002\u201d\u949f\u7075\u79c0\u559c\u6ce3\uff0c\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u6251\u5165\u4ed6\u7684\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u602a\u6211\u56de\u6765\u8fdf\u4e86\u3002\u5927\u54e5 \u54e5\uff0c\u4f60\u600e\u4e48\u80fd\u591f\u6740\u6389\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u8981\u77e5\u5979\u4eca\u65e9\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u6a80\u7fbd\u51b2\u8fd8\u662f\u53ea\u80fd\u6276\u7740\u5899\u58c1\uff0c\u8d70\u51e0\u6b65\u7684\uff0c\u5979\u4e0d\u5927\u80fd\u60f3\u50cf\u4e00\u4e2a\u534a\u8eab \u4e0d\u9042\u7684\u75c5\u4eba\uff0c\u5982\u4f55\u80fd\u591f\u6740\u654c\uff1f \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u5728\u9ed1\u6697\u4e2d\u8bf4\u8bdd\u6211\u53ef\u4e0d\u5927\u4e60\u60ef\uff0c\u4f60\u70b9\u4e86\u706f\uff0c\u6211\u518d\u544a\u8bc9\u4f60\u5427\u3002\u201d\u4ed6\u5c3d\u91cf\u8bf4\u5f97 \u5e73\u6de1\uff0c\u4f46\u5728\u949f\u7075\u79c0\u542c\u6765\uff0c\u53ef\u8fd8\u662f\u60ca\u5fc3\u52a8\u9b44\uff01 \u3000\u3000\u539f\u6765\u4ed6\u6b63\u662f\u56e0\u4e3a\u884c\u52a8\u4e0d\u4fbf\uff0c\u8fd9\u624d\u6545\u610f\u793a\u5f31\uff0c\u5f15\u8bf1\u654c\u4eba\u5165\u5c4b\u6355\u4ed6\u7684\u3002 \u3000\u3000\u90a3\u95e8\u5ba2\u7684\u94a2\u722a\u6293\u7740\u4ed6\u5750\u7684\u90a3\u5f20\u6905\u80cc\uff0c\u5b8c\u989c\u738b\u5e9c\u90a3\u4e2a\u6b66\u58eb\u51b2\u8fdb\u5c4b\u6765\uff0c\u4e00\u5200\u5411\u4ed6\u5288\u4e0b\u3002\u6a80\u7fbd \u51b2\u534a\u8eab\u4e0d\u9042\uff0c\u4f46\u5185\u529f\u5374\u5df2\u6062\u590d\u4e86\u4e94\u516d\u5206\uff0c\u4e00\u53e3\u7f61\u6c14\u4ece\u66a7\u7389\u7bab\u4e2d\u5439\u51fa\u3002\u8981\u662f\u90a3\u6b66\u58eb\u7ad9\u5728\u95e8\u5916\uff0c \u4ed6\u7684\u7f61\u6c14\u8fd8\u662f\u672a\u80fd\u4f24\u53ca\u4ed6\u7684\u3002\u6b64\u65f6\u7684\u8ddd\u79bb\u5df2\u5c45\u4e09\u5c3a\u4e4b\u5185\uff0c\u4ed6\u7684\u8fd9\u53e3\u7f61\u6c14\u53ef\u7acb\u5373\u89c1\u6548\u4e86\u3002\u6b66\u58eb \u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u94a2\u5200\u98de\u51fa\u53bb\uff0c\u4eba\u4e5f\u6454\u5012\u5728\u5730\u4e0a\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u6a80\u7fbd\u51b2\u4ea6\u5df2\u6eda\u8fc7\u4e00\u8fb9\uff0c\u90a3\u5f20\u6905\u5b50 \u7ed9\u94a2\u722a\u6293\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u95e8\u5ba2\u89c1\u6b66\u529f\u6bd4\u4ed6\u9ad8\u5f3a\u7684\u6b66\u58eb\u7a81\u7136\u5012\u5730\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u614c\u4e71\u4e2d\u6905\u5b50\u7838\u4e0b\u6765\uff0c\u7838\u788e\u4e86 \u90a3\u6b66\u58eb\u7684\u8111\u888b\u3002 \u3000\u3000\u201c\u6211\u7684\u8fd0\u6c14\u603b\u7b97\u4e0d\u9519\u3002\u201d\u6a80\u7fbd\u51b2\u5fae\u7b11\u9053\uff1a\u201c\u53ea\u5439\u4e86\u4e00\u53e3\u6c14\uff0c\u5c31\u6536\u4e86\u6740\u9e21\u8b66\u7334\u4e4b\u6548\uff0c\u628a\u53e6 \u5916\u4e24\u4e2a\u4e5f\u5413\u8dd1\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\u540e\u60a3\uff0c\u90a3\u4e24\u4e2a\u4eba\u4e5f\u90fd\u7ed9\u6740\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u6070\u597d\u78b0\u4e0a\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u4eec\u4e00\u89c1\u5230\u6211\uff0c\u5c31\u731c\u5230\u6211\u662f\u7ed9\u4f60\u9001\u7cae\u98df\u7684\u4eba\uff0c\u540e\u6765\u6211\u7684\u9762\u76ee\u4e5f\u7ed9\u90a3 \u4e2a\u5343\u67f3\u5e84\u7684\u95e8\u5ba2\u7740\u7834\u4e86\u3002\u53ef\u7b11\uff0c\u4ed6\u4eec\u8fd8\u4ee5\u4e3a\u6211\u662f\u4ece\u524d\u6837\u7684\u6b66\u529f\u4f4e\u5fae\u7684\u5c0f\u4e2b\u5934\uff0c\u5374\u4e0d\u77e5\u6211\u5df2\u7ecf \u8ddf\u4f60\u5b66\u4e86\u4e00\u5e74\u7684\u6b66\u529f\uff0c\u867d\u7136\u4e0d\u6562\u8bf4\u662f\u540d\u5e08\u51fa\u9ad8\u5f92\uff0c\u4e5f\u662f\u8db3\u4ee5\u514b\u5236\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u5e26\u4e86\u4ec0\u4e48\u4e1c\u897f\u56de\u6765\u5440\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u6211\u7ed9\u4f60\u4e70\u4e86\u6708\u997c\u56de\u6765\u4e86\u3002\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u600e\u4e48\u559c\u6b22\u5403\u6708\u997c\uff0c\u4f46\u8fd9 \u662f\u82cf\u5dde\u91c7\u4e4b\u9f50\u7684\u6708\u997c\uff0c\u98ce\u5473\u4e0e\u522b\u4e0d\u540c\uff0c\u4f60\u8bd5\u8bd5\u770b\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u5c71\u6751\u5c0f\u9547\uff0c\u4e5f\u6709\u91c7\u4e4b \u9f50\u7684\u6708\u997c\u5356\u4e48\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u662f\u4e70\u7684\uff0c\u662f\u522b\u4eba\u9001\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u8be7\u9053\uff1a\u201c\u8c01\u9001\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u662f\u91d1\u56fd\u7684\u519b\u5b98\u9001\u7ed9\u6211\u7684\uff0c\u4eca\u5929\u6211\u5e72\u4e86\u4e00\u4ef6\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u6b63\u8981\u8bf4\u7ed9\u4f60 \u542c\u2026\u2026\u201d \u3000\u3000\u539f\u6765\u5979\u5728\u90a3\u5c0f\u9547\u4e0a\u78b0\u4e0a\u4e00\u961f\u5317\u5f52\u7684\u91d1\u56fd\u519b\u5b98\u5175\uff0c\u90a3\u961f\u5b98\u5175\u7684\u961f\u957f\u89c1\u5979\u8ff9\u53ef\u7591\uff0c\u622a\u4f4f\u5979\u76d8 \u95ee\u3002 \u3000\u3000\u201c\u6211\u4e0d\u60f3\u5728\u9547\u4e0a\u751f\u4e8b\uff0c\u7ed3\u679c\u53ea\u597d\u53c8\u4eae\u51fa\u90a3\u8170\u724c\u505a\u62a4\u8eab\u7b26\u4e86\u3002\u90a3\u519b\u5b98\u4e5f\u50cf\u4e0a\u4e00\u6b21\u78b0\u4e0a\u7684\u90a3 \u519b\u5b98\u4e00\u6837\uff1b\u4ee5\u4e3a\u6211\u771f\u7684\u662f\u5b8c\u989c\u738b\u7237\u6d3e\u6765\u6c5f\u5357\u7684\u4eba\uff0c\u5bf9\u6211\u6bd5\u606d\u6bd5\u656c\u3002\u4e0d\u4f46\u9001\u6211\u6708\u997c\uff0c\u8fd8\u9001\u4e86\u6211 \u51e0\u5341\u4e24\u94f6\u5b50\u5462\u3002\u201d\u949f\u7075\u79c0\u7b11\u9053\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7b11\u9053\uff1a\u201c\u662f\u4f60\u52d2\u7d22\u4ed6\u7684\u5427\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u7b11\u9053\uff1a\u201c\u4f60\u7684\u91d1\u8c46\uff0c\u6211\u5df2\u7ecf\u5dee\u4e0d\u591a\u7ed9\u4f60\u82b1\u5149\u4e86\u3002\u4ed6\u95ee\u6211\u9700\u8981\u4ec0\u4e48\uff0c\u6211\u4e50\u5f97\u5411\u4ed6\u8ba8 \u70b9\u8def\u8d39\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u5e94\u8be5\u5411\u4ed6\u591a\u8981\u4e00\u4e9b\u3002\u53e3\u6c14\u592a\u5c0f\uff0c\u4ed6\u53cd\u4f1a\u7591\u5fc3\u4f60\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u7ed9\u4e86\u94f6\u5b50\uff0c\u8fd8\u597d\u50cf\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\u7684\u6837\u5b50\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u662f\u8fc7\u8def\u7684\u5b98 \u5175\uff0c\u60f9\u4e0d\u60f9\u4ed6\u7591\u5fc3\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u7406\u4f1a\u5b83\u4e86\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u6765\u641c\u6355\u6211\u7684\u90a3\u4e09\u4e2a\u4eba\u5931\u8e2a\u4e86\uff0c\u53c8\u53d1\u751f\u4f60\u5728\u5c0f\u9547\u78b0\u4e0a\u90a3\u961f \u5b98\u5175\u7684\u4e8b\u60c5\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u8ffd\u7a76\u7684\u3002\u53ea\u6015\u54b1\u4eec\u662f\u518d\u4e5f\u4e0d\u80fd\u5728\u8fd9\u91cc\u5b89\u5c45\u4e86\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u4e5f \u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u4f46\u603b\u8fd8\u6709\u4e00\u6bb5\u65f6\u95f4\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6bb5\u65f6\u95f4\u53c8\u600e\u6837\uff1f\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\u4f60\u5df2\u7ecf\u80fd\u591f\u8fd0\u7528\u7f61\u6c14\u4f24\u4eba\uff0c \u6599\u60f3\u4e0d\u4e45\u4ea6\u5f53\u53ef\u4ee5\u6062\u590d\u5982\u521d\u4e86\u5427\uff1f\u201d\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u529f\u529b\u662f\u5df2\u7ecf\u6062\u590d\u4e86\u4e00\u534a\uff0c \u4f46\u60f3\u8981\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u5374\u8fd8\u4e0d\u77e5\u4f55\u65e5\u65b9\u53ef\u5b8c\u6210\uff1f\u7ecf\u8109\u672a\u901a\uff0c\u6211\u4ecd\u662f\u534a\u8eab\u4e0d\u9042\u7684\u5e9f\u4eba\uff0c\u5982\u4f55\u53ef \u4ee5\u62b5\u5fa1\u5f3a\u654c\uff1f\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f9d\u4f60\u63a8\u6d4b\uff0c\u4ed6\u4eec\u7684\u4eba\uff0c\u6700\u5feb\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u6765\u5462\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u8bf4\u5f97\u51c6\uff0c\u6211\u53ea\u76fc\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u8ba9\u6211\u52a0\u7d27\u7ec3\u529f\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u535a\u5b83\u4e00\u535a\uff0c\u4ee5\u534a\u4e2a\u6708\u4e3a\u671f\uff0c\u5230\u671f\u9650\uff0c\u5982\u679c\u4f60\u8fd8\u672a\u6253\u901a\u7ecf\u8109\uff0c \u6211\u5c31\u548c\u4f60\u79fb\u8f6c\u5230\u522b\u7684\u5730\u65b9\u53bb\u3002\u201d\u6a80\u7fbd\u51b2\u82e6\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u53ef\u53bb\uff0c\u6211\u4e5f\u4e0d\u60f3\u8fde\u7d2f\u4f60\u4e00 \u751f\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u55d4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u54b1\u4eec\u4e0d\u662f\u65e9\u5df2\u8bf4\u597d\uff0c\u54b1\u4eec\u8fd9\u4e00\u751f\u662f\u53ea\u80fd\u76f8\u4f9d\u4e3a\u547d\u7684\u4e48\uff1f\u4f60\u5230\u73b0 \u5728\u8fd8\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u662f\u4e0d\u662f\u5df2\u7ecf\u4e0d\u628a\u6211\u5f53\u4f5c\u59b9\u59b9\u770b\u5f85\u4e86\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u522b\u7740\u607c\u3002\u6211\u53ea\u662f\u4e3a\u4f60\u770b\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u79bb\u5f00\u4f60\u6211\u8fd8\u80fd\u6d3b\u4e48\uff1f\u4f60\u4e3a\u6211\u7740\u60f3\uff0c\u5c31\u4e0d\u8bb8\u4f60\u8bf4\u518d\u5206\u5f00\u7684\u8bdd\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u4e2d\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u54b1\u4eec\u5c31\u8d4c\u4e00\u8d4c\u8fd0\u6c14\u5427\u3002\u90a3\u4e09\u4e2a\u4eba\u5931\u8e2a\u7684\u6d88\u606f\u4f20\u5230\u91d1 \u4eac\uff0c\u6700\u5c11\u4e5f\u5f97\u6709\u534a\u4e2a\u6708\u7684\u65f6\u95f4\u7684\uff0c\u6211\u4f9d\u4f60\u5c31\u662f\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5373\u4f7f\u6d88\u606f\u672a\u4f20\u5230\u91d1\u4eac\uff0c\u5b8c\u989c\u957f\u4e4b\u4e00\u6837\u4e5f\u53ef\u4ee5\u6d3e\u4eba\u6765\u5230\u8fb9\u5173\u67e5\u63a2\u7684\u3002\u4e0d\u8fc7\u6a80\u7fbd\u51b2\u5374 \u662f\u4e0d\u60f3\u628a\u8fd9\u5c42\u5fe7\u8651\u5bf9\u949f\u7075\u79c0\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u5929\u8fc7\u540e\uff0c\u6a80\u7fbd\u51b2\u548c\u949f\u7075\u79c0\u90fd\u52a0\u7d27\u7ec3\u529f\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5e73\u5b89\u5730\u8fc7\u4e86\u5341\u4e09\u5929\uff0c\u6a80\u7fbd\u51b2\u5df2\u591a\u6062 \u590d\u4e86\u4e24\u4e09\u5206\uff0c\u4f46\u5947\u7ecf\u516b\u8109\uff0c\u4ecd\u662f\u672a\u80fd\u6253\u901a\u3002 \u3000\u3000\u8fd9\u5929\u949f\u7075\u79c0\u5728\u5c71\u6eaa\u6d17\u8863\u88f3\uff0c\u542c\u677e\u98ce\u5982\u8bd7\uff0c\u60f3\u8d77\u53bb\u5e74\u4e0e\u6a80\u7fbd\u51b2\u5728\u94b1\u5858\u6c5f\u540c\u4e00\u6761\u8239\u9003\u51fa\u4e34 \u5b89\uff0c\u542c\u90a3\u60ca\u6d9b\u62cd\u5cb8\u7684\u60c5\u666f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u4e00\u5e74\u591a\uff0c\u4e0d\u89c9\u5fc3\u6f6e\u4e5f\u50cf\u6ce2\u6d9b\u8d77\u4f0f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6c99\u6c99\u58f0\u54cd\uff0c\u4f3c\u662f\u8e0f\u5728\u94fa\u6ee1\u843d\u53f6\u7684\u5730\u4e0a\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u949f\u7075\u79c0\u60ca\u9192\u793a\u6765\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u5bb9\u8c8c\u8273\u4e3d\u7684\u5c11\u5973\u5df2\u7ecf\u4ece\u6811\u6797\u91cc\u8d70\u51fa\u6765\u4e86\u3002\u949f\u7075\u79c0 \u5446\u4e86\u4e00\u5446\uff0c\u9661\u5730\u53d8\u4e86\u9762\u8272\uff0c\u8df3\u8d77\u6765\u5c31\u9a82\uff1a\u201c\u597d\u4e2a\u4e0d\u77e5\u7f9e\u803b\u7684\u5996\u5973\uff01\u201d \u3000\u3000\u90a3\u5973\u5b50\u6bd4\u5979\u5403\u60ca\u66f4\u751a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4f55\u4e00\u89c1\u6211\u5f00\u53e3\u5c31\u9a82\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u5341\u5206\u660e\u663e\uff0c\u5979\u662f\u8bf4\u5979\u548c\u949f\u7075\u79c0\u7d20\u4e0d\u76f8\u8bc6\uff0c\u56e0\u800c\u5bf9\u949f\u7075\u79c0\u7684\u201c\u5f00\u53e3\u5927\u9a82\u201d\uff0c \u611f\u5230\u5947\u602a\u7684\u3002\u5979\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u4e5f\u6b63\u662f\u8bf4\u660e\u4e86\u8fd9\u4e00\u70b9\u3002 \u3000\u3000\u4f46\u504f\u504f\u4efb\u4f55\u4eba\u90fd\u542c\u5f97\u61c2\u7684\u8bf4\u8bdd\uff0c\u949f\u7075\u79c0\u5374\u8bef\u89e3\u4e86\u3002\u539f\u6765\u8fd9\u4e2a\u7f8e\u8c8c\u5c11\u5973\uff0c\u4e43\u662f\u8d6b\u8fde\u6e05\u5bb6\u4e09 \u59b9\u59b9\u4e2d\u7684\u4e8c\u59ca\u8d6b\u8fde\u6e05\u4e91\uff0c\u949f\u7075\u79c0\u5374\u8bef\u8ba4\u4f5c\u4e09\u59ca\u59b9\u4e2d\u7684\u5927\u59ca\u8d6b\u8fde\u6e05\u6ce2\u3002\u5979\u53ea\u9a82\u201c\u5996\u5973\u201d\uff0c\u4e0d\u9a82 \u201c\u5996\u72d0\u201d\uff0c\u5df2\u7ecf\u662f\u5ff5\u5728\u8d6b\u8fde\u6e05\u6ce2\u5bf9\u6a80\u7fbd\u51b2\u66fe\u7ecf\u6709\u8fc7\u8d60\u53c2\u6d3b\u547d\u4e4b\u6069\uff0c\u9a82\u5f97\u6bd4\u8f83\u201c\u5ba2\u6c14\u201d\u7684\u4e86\u3002 \u3000\u3000\u5979\u53ea\u5f53\u8fd9\u201c\u7389\u9762\u5996\u72d0\u201d\u4e43\u662f\u53cd\u8fc7\u6765\u8ba5\u8bbd\u5979\u4e0d\u77e5\u7f9e\u803b\u3002 \u3000\u3000\u949f\u7075\u79c0\u51b7\u7b11\u9053\uff1a\u201c\u6211\u548c\u4ed6\u662f\u7ed3\u62dc\u5144\u59b9\u3002\u4f60\u5462\uff1f\u4f60\u5374\u8fd8\u6562\u539a\u7740\u8138\u76ae\uff0c\u81ea\u8ba4\u662f\u4ed6\u7684\u597d\u670b\u53cb \u5417\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u54e6\uff0c\u4ed6\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u949f\u7075\u79c0\u51b7\u7b11\u9053\uff1a\u201c\u522b\u88c5\u849c\uff0c\u4f60\u662f\u4e0d\u662f\u6765\u627e\u6211\u7684\u5927\u54e5\u54e5\u7684\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u731c\u5230\u51e0\u5206\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5927\u54e5\u54e5\u5c31\u662f\u6a80\u7fbd\u51b2\u5427\uff1f\u8fd9\u4e00\u5e74\u6765\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e00\u5e74\u6765\u6211\u5c31\u662f\u548c\u4ed6\u4f4f\u5728\u4e00\u8d77\u7684\u3002\u9664\u6211\u4e4b\u5916\u3002\u4ed6\u4e0d\u60f3\u89c1\u4efb\u4f55\u4eba\uff0c\u5c24 \u5176\u662f\u4f60\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u557c\u7b11\u7686\u975e\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u4ed6\u662f\u5982\u6b64\u618e\u6068\u6211\u5462\uff1f\u4f46\u5c31\u7b97\u662f\u6211\u6765\u627e \u4ed6\uff0c\u89c1\u4e0d\u89c1\u662f\u4ed6\u7684\u4e8b\uff0c\u90a3\u4e5f\u4e0d\u80fd\u8bf4\u6211\u662f\u4e0d\u77e5\u7f9e\u803b\u554a\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u8bf4\u8fc7\u7684\u8bdd\u90fd\u5fd8\u8bb0\u4e86\u4e48\uff1f\u5f53\u65f6\u4f60\u662f\u600e\u4e48\u8bf4\u7684\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u6211\u4e5f\u8bb0\u4e0d\u8d77\u6211\u662f\u66fe\u7ecf\u8bf4\u4ec0\u4e48\u4e86\uff0c\u4f60\u8bf4\u6765\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u6012\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u89c1\u8fc7\u50cf\u4f60\u8fd9\u6837\u539a\u8138\u76ae\u7684\u4eba\uff0c\u5927\u54e5\u54e5\u5df2\u7ecf\u548c\u4f60\u4e00\u5200\u4e24\u65ad\uff0c\u4f60\u4e5f\u66fe \u4eb2\u53e3\u7b54\u5e94\u8fc7\u6211\u3002\u4e0d\u518d\u6765\u627e\u6211\u4eec\u7684\u9ebb\u70e6\u7684\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u518d\u6765\uff1f\u4e16\u95f4\u591a\u5c11\u7537\u5b50\uff0c\u4f60\u627e\u522b\u4eba\u53bb \u5427\uff1f\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\uff0c\u8d6b\u8fde\u6e05\u4e91\u5fc3\u91cc\u5df2\u662f\u660e\u767d\u4e03\u516b\u5206\u4e86\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6562\u60c5\u5979\u662f\u628a\u6211\u8ba4\u9519\u8ba4\u4f5c\u5927\u59ca \u59ca\uff0c\u5979\u6015\u6211\u62a2\u8d70\u5979\u7684\u5927\u54e5\u54e5\uff0c\u4eba\u751f\u6700\u96be\u7684\u662f\u60a3\u96be\u4e2d\u7684\u77e5\u5df2\uff0c\u8fd9\u4e00\u5e74\u4e2d\u4ed6\u4eec\u8352\u5c71\u76f8\u5904\uff0c\u542c\u5979\u7684 \u53e3\u6c14\uff0c\u6050\u6015\u65e9\u5df2\u4e0d\u6b62\u4e8e\u5144\u59b9\u4e4b\u60c5\u4e86\u3002\u55ef\uff0c\u6a80\u7fbd\u51b2\u5012\u662f\u597d\u798f\u6c14\uff0c\u56e0\u7978\u5f97\u798f\uff0c\u5f97\u5230\u4e86\u8fd9\u6837\u4e00\u4e2a\u7eaf \u771f\u5c11\u5973\u7684\u7231\u60c5\uff0c\u6211\u4e5f\u7528\u4e0d\u7740\u62c5\u5fc3\u4ed6\u6ca1\u4eba\u7167\u6599\u4e86\u3002\u201d\u4f46\u4e0d\u77e5\u600e\u4e48\u7684\uff0c\u5728\u6b23\u6170\u4e2d\uff0c\u4ea6\u6709\u70b9\u201c\u9178\u6e9c \u6e9c\u201d\u7684\u611f\u89c9\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u5bdf\u89c9\u4e86\u3002\u5fc3\u4e2d\u7fdf\u7136\u4e00\u7701\uff0c\u4e0d\u7981\u9762\u7ea2\u8033\u8d64\u3002\u949f\u7075\u79c0\u51b7\u51b7\u7684\u6ce8\u89c6\u5979\uff0c\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u4f60\u61c2\u5f97\u7f9e\u803b\u5c31\u597d\uff0c\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u5c1a\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u5e2e\u4e3b\u6765\u4e86\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u5c1a\u5e2e\u4e3b\u53ea\u662f\u8bf7\u6211\u66ff\u4ed6\u5e26\u8fd9\u4e00\u53e5\u8bdd\u6765\u7ed9\u4f60\u7684\u5927\u54e5\u54e5\uff0c\u4ed6\u5927\u6982\u4e0d\u4f1a\u6765\u8fd9\u91cc \u7684\u3002\u4ed6\u73b0\u5728\u5c71\u4e1c\u83b1\u829c\uff0c\u4f60\u7684\u5927\u54e5\u54e5\u8eab\u4f53\u597d\u4e86\uff0c\u53ef\u4ee5\u5230\u83b1\u829c\u53bb\u89c1\u4ed6\u3002\u4f46\u6700\u597d\u5bb9\u8c8c\u6539\u4e00\u4e0b\uff1b\u5343\u4e07 \u4e0d\u53ef\u7ed9\u522b\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5446\u4e86\u7247\u523b\uff0c\u84e6\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u5e2e\u4e3b\u5373\u4f7f\u60f3\u89c1\u6211\u7684\u5927\u54e5\u54e5\uff0c\u6599\u60f3\u4e5f\u4e0d\u4f1a\u6258\u4f60\u8fd9 \u4e2a\u5996\u5973\u6765\u66ff\u4ed6\u4f20\u8bdd\u5427\uff1f\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u5012\u597d\u50cf\u662f\u5c1a\u5e2e\u4e3b\u7684\u5fc3\u8179\u4f3c\u7684\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u6b63\u5bb9\u9053\uff1a\u201c\u4fe1\u4e0d\u4fe1\u4efb\u4ece\u4f60\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u548c\u4f60\u7684\u5927\u54e5\u54e5\u5173\u7cfb\u91cd\u5927\uff0c\u52a1\u5fc5\u8bf7\u4f60\u8f6c\u8fbe\u3002 \u5373\u4f7f\u4f60\u5f53\u4f5c\u7b11\u8bdd\u8bf4\u7ed9\u4ed6\u542c\uff0c\u90a3\u4e5f\u65e0\u59a8\uff01\u201d \u3000\u3000\u5979\u7684\u795e\u6001\u975e\u5e38\u5e84\u91cd\uff0c\u949f\u7075\u79c0\u672c\u6765\u662f\u628a\u5979\u5f53\u4f5c\u201c\u7389\u9762\u5996\u72d0\u201d\u7684\uff0c\u6b64\u65f6\u5374\u5ffd\u7136\u6709\u7740\u5979\u597d\u50cf \u201c\u53d8\u4e86\u4e2a\u4eba\u201d\u7684\u611f\u89c9\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u5df2\u7ecf\u8d70\u4e86\uff0c\u949f\u7075\u79c0\u8fd8\u5728\u53d1\u5446\u3002 \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u5018\u82e5\u662f\u771f\u7684\u8bdd\uff0c\u90a3\u5c31\u4e00\u5b9a\u662f\u4e10\u5e2e\u7684\u5e2e\u4e3b\u5df2\u7ecf\u77e5\u9053\u5927\u54e5\u54e5\u6240\u53d7\u7684 \u51a4\u5c48\uff0c\u65b9\u59cb\u8981\u7ea6\u4f1a\u4ed6\u4e86\u3002\u4e10\u5e2e\u7684\u5c1a\u5e2e\u4e3b\u6599\u60f3\u662f\u4e0d\u4f1a\u7528\u8be1\u8ba1\u9a97\u5927\u54e5\u54e5\u4e0a\u5f53\u7684\uff0c\u6211\u8be5\u4e0d\u8be5\u544a\u8bc9\u4ed6 \u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5bf9\uff01\u4e0d\u5bf9\uff01\u5c1a\u5e2e\u4e3b\u4e0d\u4f1a\u9a97\u4eba\uff0c\u90a3\u5996\u5973\u53ef\u662f\u4f1a\u9a97\u4eba\u7684\u3002\u6211\u600e\u80fd\u4e0a\u5979\u7684\u5f53\uff0c\u5e2e\u5979\u9a97\u5927\u54e5 \u54e5\u4e0b\u5c71\uff01\u201d \u3000\u3000\u201c\u4f46\u5979\u8bf4\u5f97\u90a3\u4e48\u8bda\u6073\uff0c\u53ef\u53c8\u4e0d\u50cf\u9a97\u4eba\u7684\u6837\u5b50\u3002\u54a6\uff0c\u5947\u602a\uff0c\u600e\u7684\u5979\u597d\u50cf\u548c\u53bb\u5e74\u6211\u6240\u89c1\u7684\u90a3 \u4e2a\u5979\u6709\u70b9\u4e0d\u540c\uff1f\u4f46\u5230\u5e95\u662f\u4ec0\u4e48\u6837\u7684\u4e0d\u540c\uff0c\u6211\u53c8\u8bf4\u4e0d\u4e0a\u6765\u3002\u201d\u949f\u7075\u79c0\u5bf9\u8d6b\u8fde\u6e05\u4e91\u8bf4\u7684\u90a3\u756a\u8bdd\u534a \u4fe1\u534a\u7591\uff0c\u6b63\u81ea\u5fc3\u601d\u4e0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u771f\u806a\u660e\uff0c\u597d\u5728\u4f60\u6ca1\u6709\u4e0a\u4e86\u8fd9\u5996\u5973\u7684 \u5f53\u3002\u201d\u4e00\u4e2a\u8eab\u6750\u9b41\u68a7\u7684\u9ed1\u8863\u6c49\u5b50\u51fa\u73b0\u5728\u5979\u7684\u773c\u524d\uff0c\u4e0d\u77e5\u7528\u4ec0\u4e48\u65f6\u5019\u94bb\u51fa\u6765\uff0c\u949f\u7075\u79c0\u7adf\u4e1d\u6beb\u4e5f \u6ca1\u6709\u5bdf\u89c9\u3002\u201c\u522b\u5403\u60ca\uff0c\u6211\u662f\u4f60\u5927\u54e5\u54e5\u7684\u670b\u53cb\u3002\u201d\u90a3\u9ed1\u8863\u4eba\u8bf4\u9053\u3002 \u3000\u3000\u201c\u4f60\u521a\u624d\u5c31\u5728\u8fd9\u91cc\u7684\u5417\uff1f\u201d\u949f\u7075\u79c0\u95ee\u9053\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u4e00\u76f4\u662f\u8ddf\u7740\u90a3\u5996\u5973\u7684\u3002\u201d\u9ed1\u8863\u4eba\u56de\u7b54\u3002\u201c\u4f60\u65e2\u7136\u662f\u5927\u54e5\u54e5\u7684\u670b\u53cb\uff0c\u53c8\u77e5\u9053\u90a3 \u5996\u5973\u662f\u610f\u56fe\u5bf9\u5927\u54e5\u54e5\u4e0d\u5229\uff0c\u4e3a\u4f55\u4e0d\u5236\u6b62\u5979\u4f5c\u8fd9\u9a97\u4eba\u7684\u52fe\u5f53\u5462\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\uff0c\u4f60\u77e5\u9053\u8fd9\u4e2a\u5996\u5973\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u5979\u662f\u5b8c\u989c\u738b\u5e9c\u7684\u5e72\u683c\u683c\uff01\u201d \u3000\u3000\u201c\u5bf9\u5566\uff01\u90a3\u4f60\u60f3\u60f3\uff0c\u6211\u600e\u4e48\u610f\u5f97\u8d77\u738b\u5e9c\u7684\u5e72\u683c\u683c\u3002\u4f55\u51b5\uff0c\u6211\u4e5f\u672a\u5fc5\u6253\u5f97\u8fc7\u5979\u3002\u6240\u4ee5\uff0c\u6211 \u53ea\u80fd\u6697\u4e2d\u7aa5\u89c6\u4e86\u3002\u201d\u949f\u7075\u79c0\u542c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u4f46\u4ecd\u6709\u6240\u7591\uff0c\u4e8e\u662f\u53c8\u518d\u95ee\u9053\uff1a\u201c\u4f60\u65e2\u81ea\u77e5\u60f9\u4e0d\u8d77 \u5979\uff0c\u4e3a\u4f55\u53c8\u6562\u5927\u7740\u80c6\u5b50\u8ddf\u8e2a\u5979\u5462\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u4e3a\u670b\u53cb\u4e24\u52a9\u63d2\u5200\uff0c\u82e5\u662f\u5230\u4e86\u8ffd\u4e0d\u5f97\u5df2\u7684\u65f6\u5019\uff0c\u60f9\u4e0d\u8d77\u4e5f\u8981\u60f9\u4e86\u3002\u6bd4 \u5982\u8bf4\uff0c\u5047\u5982\u5979\u521a\u624d\u8981\u5bf9\u4f60\u4e0d\u5229\u7684\u65f6\u5019\uff0c\u90a3\u5f53\u7136\u5c31\u8981\u51fa\u624b\u5e2e\u4f60\u4e86\u3002\u201d\u949f\u7075\u79c0\u9053\uff1a\u201c\u591a\u8c22\u3002\u8bf7\u95ee \u4f60\u76ee\u4e0b\u610f\u6b32\u5982\u4f55\uff1f\u201d\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u7684\u884c\u85cf\u5df2\u7ecf\u7ed9\u8fd9\u5996\u5973\u53d1\u73b0\uff0c\u8fd9\u4e2a\u5730\u65b9\uff0c\u4f60\u4eec\u662f\u4f4f\u4e0d \u4e0b\u53bb\u7684\u4e86\u3002\u6211\u60f3\u5e2e\u5fd9\u4f60\u4eec\u9003\u5230\u53e6\u4e00\u4e2a\u5730\u65b9\u53bb\uff0c\u8bf7\u4f60\u5e2e\u6211\u53bb\u89c1\u4f60\u7684\u5927\u54e5\u54e5\u5427\u3002\u201d\u201c\u8bf7\u95ee\u8d35\u59d3\u5927 \u540d\uff1f\u201d\u90a3\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u7684\u5927\u54e5\u54e5\u89c1\u6211\u81ea\u7136\u5c31\u4f1a\u77e5\u9053\u3002\u5c0f\u59d1\u5a18\u4f60\u522b\u591a\u7591\uff0c\u8981\u662f\u6211\u60f3\u9a97\u4f60\u7684 \u8bdd\uff0c\u6211\u4e0d\u4e5f\u53ef\u4ee5\u968f\u4fbf\u634f\u4e2a\u5047\u540d\u5417\uff1f\u201d\u949f\u7075\u79c0\u70b9\u4e86\u70b9\u5934\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff01\u4f60\u672a\u66fe\u89c1\u5230\u6211\u7684 \u5927\u54e5\u54e5\uff0c\u81ea\u4e5f\u4e0d\u514d\u6709\u70b9\u987e\u5fcc\u7684\u3002\u6211\u76f8\u4fe1\u4f60\uff0c\u8bf7\u8ddf\u6211\u6765\u5427\u3002\u201d\u7a81\u7136\u53cd\u624b\u4e00\u626c\uff0c\u4e09\u679a\u94dc\u94b1\u95ea\u7535\u98de \u51fa\u3002\u4e09\u679a\u94dc\u94b1\u90fd\u6253\u4e2d\u4e86\u9ed1\u8863\u6c49\u5b50\u7684\u9ebb\u7a74\u3002 \u3000\u3000\u539f\u6765\u949f\u7075\u79c0\u662f\u5047\u88c5\u76f8\u4fe1\u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\u7684\u8bf4\u8bdd\uff0c\u5979\u51fa\u624b\u4e4b\u65f6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u54e5\u5018\u82e5\u6709\u8fd9 \u4e48\u4e00\u4e2a\u597d\u670b\u53cb\uff0c\u4e3a\u4f55\u4ece\u6765\u4e0d\u89c1\u4ed6\u548c\u6211\u63d0\u8d77\uff01\u597d\uff0c\u6211\u4e14\u62fc\u7740\u53d7\u5927\u54e5\u54e5\u8d23\u602a\uff0c\u5148\u70b9\u4e86\u4ed6\u7684\u9ebb\u7a74\u3002 \u5982\u679c\u4ed6\u771f\u7684\u662f\u5927\u54e5\u54e5\u7684\u597d\u670b\u53cb\uff0c\u6211\u518d\u5411\u4ed6\u966a\u7f6a\u4e0d\u8fdf\u3002\u201d\u4f46\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\u662f\uff0c\u8fd9\u4e09\u679a\u94dc\u94b1\uff0c \u867d\u7136\u90fd\u6253\u4e2d\u4e86\u90a3\u9ed1\u8863\u6c49\u5b50\u7684\u9ebb\u7a74\uff0c\u4f46\u53ea\u542c\u5f97\u94ee\u94ee\u58f0\u54cd\uff0c\u4e09\u679a\u94dc\u94b1\u5374\u53c8\u90fd\u7ed9\u53cd\u5f39\u56de\u6765\u4e86\u3002\u5e78\u800c \u949f\u7075\u79c0\u8f7b\u529f\u4e0d\u5f31\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u8fd9\u624d\u6ca1\u6709\u7ed9\u98de\u56de\u6765\u7684\u94b1\u9556\u6253\u4e2d\u3002\u90a3\u9ed1\u8863\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u9b3c \u4e2b\u5934\u5934\u5012\u4f1a\u4f7f\u8bc8\uff0c\u597d\u5440\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u80fd\u4e0d\u80fd\u9003\u51fa\u6211\u7684\u638c\u5fc3\uff1f\u201d \u3000\u3000----------- \u3000\u3000\u7fbd\u751f\u5802 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u6cea\u6d12\u957f\u6c5f \u6881\u7fbd\u751f\u300a\u6b66\u6797\u5929\u9a84\u300b \u7b2c\u5341\u4e03\u56de\u3000\u6cea\u6d12\u957f\u6c5f \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u53cc\u638c\u8fde\u6325\uff0c\u638c\u529b\u81ea\u56db\u9762\u516b\u65b9\u6324\u6765\uff0c\u949f\u7075\u79c0\u7684\u5251\u6cd5\u81ea\u65bd\u5c55\u4e0d\u5f00\uff0c\u9ed1\u8863\u6c49\u5b50\u51b7\u7b11\u559d \u9053\uff1a\u201c\u8bc6\u5f97\u5389\u5bb3\u4e86\u4e48\uff0c\u8fd8\u4e0d\u8d76\u5feb\u6295\u964d\uff01\u201d\u949f\u7075\u79c0\u65a5\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d\u54ac\u7d27\u7259\u6839\uff0c\u4f7f\u51fa\u5403\u5976\u7684\u6c14 \u529b\uff0c\u5530\u7684\u4e00\u5251\uff0c\u523a\u4ed6\u54bd\u5589\u3002\u9ed1\u8863\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u4e0d\u89c1\u68fa\u6750\u4e0d\u6d41 \u6cea\uff01\u201d\u53cc\u6307\u53ea\u662f\u8f7b\u8f7b\u4e00\u5f39\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5c31\u628a\u949f\u7075\u79c0\u7684\u77ed\u5251\u5f39\u5f97\u8131\u624b\uff0c\u98de\u4e0a\u7a7a\u4e2d\u3002 \u3000\u3000\u949f\u7075\u79c0\u7981\u53d7\u4e0d\u8d77\u8fd9\u80a1\u529b\u9053\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u7684\u8eab\u6cd5\uff0c\u5012\u7eb5\u51fa\u53bb\u3002\u4e0d\u8fc7\uff0c\u5979\u867d \u7136\u8131\u51fa\u4e86\u9ed1\u8863\u6c49\u5b50\u62ff\u529b\u6240\u53ca\u7684\u8303\u56f4\uff0c\u4f46\u6c14\u529b\u5374\u662f\u4e0d\u7ee7\u4e86\uff0c\u4e00\u4e2a\u65a4\u6597\u7ffb\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u811a\u8ddf\u7adf\u7136 \u4e0d\u80fd\u5e73\u7a33\u7740\u5730\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u4e24\u4e2a\u6eda\uff0c\u5df2\u662f\u672a\u80fd\u7ad9\u5f97\u8d77\u6765\u3002 \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u6b63\u8981\u4e0a\u524d\u62ff\u5979\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u91d1\u8d85\u5cb3\uff0c\u7ed9\u6211\u4f4f\u624b\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u6c49\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u91d1\u56fd\u7684\u7b2c\u4e00\u5927\u5185\u9ad8\u624b\u91d1\u8d85\u5cb3\u3002 \u3000\u3000\u949f\u7075\u79c0\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u628a\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u559d\u4ee4\u91d1\u8d85\u5cb3\u4f4f\u624b\u7684\u90a3\u4e2a\u4eba\uff0c\u5df1\u7ecf\u51fa\u73b0 \u5728\u5979\u7684\u773c\u524d\u4e86\uff0c\u53ef\u4e0d\u6b63\u662f\u521a\u624d\u90a3\u4e2a\u5973\u5b50\u662f\u8c01\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u7684\u540d\u5b57\u662f\u5979\u66fe\u7ecf\u542c\u5f97\u6a80\u7fbd\u51b2\u8bf4\u8fc7\u7684\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5c31\u662f\u91d1 \u56fd\u7b2c\u4e00\u9ad8\u624b\u91d1\u8d85\u5cb3\uff0c\u5927\u54e5\u54e5\u8bf4\u8fc7\uff0c\u91d1\u56fd\u6700\u5389\u5bb3\u7684\u4e24\u4e2a\uff0c\u4e00\u4e2a\u662f\u5b8c\u989c\u738b\u5e9c\u7684\u8fe6\u5e90\u4e0a\u4eba\uff0c\u53e6\u4e00\u4e2a \u5c31\u662f\u4ed6\u4e86\uff0c\u679c\u7136\u771f\u662f\u5389\u5bb3\u3002\u5927\u54e5\u54e5\u5373\u4f7f\u6ca1\u53d7\u4f24\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u6253\u5f97\u8fc7\u4ed6\u3002\u201d\u6b22\u559c\u7684\u5374\u662f\uff1a\u201c\u60f3 \u4e0d\u5230\u8fd9\u5996\u5973\u5bf9\u5927\u54e5\u54e5\u8fd8\u662f\u672a\u80fd\u5fd8\u60c5\uff0c\u5979\u53bb\u800c\u590d\u56de\uff0c\u56de\u6765\u53cd\u800c\u5e2e\u4e86\u6211\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u5403\u4e00\u60ca\u9053\uff1a\u201c\u683c\u683c\uff0c\u4f60\u77e5\u9053\u8fd9\u4e2b\u5934\u662f\u4ec0\u4e48\u4eba\u5417\uff0c\u4ed6\u662f\u6a80\u7fbd\u51b2\u7684\u4e49\u59b9\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u5979\u8c01\uff0c\u4f60\u8ddf\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u54b1\u4eec\u6b63\u8981\u7740\u843d\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u6349\u62ff\u94a6\u72af\uff0c\u600e\u80fd\u56de\u53bb\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u6349\u62ff\u94a6\u72af\u4e4b\u4e8b\u7f13\u529e\uff0c\u6211\u53eb\u4f60\u56de\u53bb\uff0c\u4f60\u5c31\u8981\u56de\u53bb\uff01\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u5fc3\u5934\u706b\u8d77\uff0c\u8138\u4e0a\u4ecd\u662f\u7b11\u563b\u563b\u7684\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5417\uff1f\u90a3\u5c31\u8bf7\u5e72\u683c\u683c\u628a\u5723\u65e8 \u62ff\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u4ec0\u4e48\u5723\u65e8\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u7687\u4e0a\u53ec\u6211\u56de\u53bb\u7684\u5723\u65e8\u554a\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u7687\u4e0a\u624d\u80fd\u53eb\u4f60\u56de\u53bb\u4e48\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u653e\u8d70\u94a6\u72af\u7684\u7f6a\u540d\u975e\u540c\u5c0f\u53ef\uff0c\u5018\u4f7f\u6ca1\u6709\u5723\u65e8\uff0c\u53ef\u62c5\u5f53\u4e0d\u8d77\u3002\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u683c\u683c\u6ca1\u6709\u5723\u65e8\uff0c\u60f3\u5fc5\u6709\u738b\u7237\u7684\u4eb2\u7b14\u624b\u8c15\u5427\uff1f\u6709\u738b\u7237\u7684\u624b\u8c15\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u8981\u77e5\u91d1\u8d85\u5cb3\u4e43\u662f\u91d1\u5bab\u4f8d\u536b\u7684\u5934\u5b50\uff0c\u6309\u4f53\u5236\u4ed6\u662f\u53ea\u80fd\u9075\u4ece\u7687\u5e1d\u7684\u547d\u4ee4\uff0c\u5982\u4eca\u4ed6\u80af\u542c\u5b8c\u989c\u738b \u7237\u7684\u547d\u4ee4\uff0c\u90a3\u5df2\u7ecf\u662f\u7ed9\u4e86\u201c\u5e72\u683c\u683c\u201d\u5929\u5927\u7684\u9762\u5b50\u4e86\u3002\u201c\u5192\u5145\u5e72\u683c\u683c\u201d\u7684\u8d6b\u8fde\u6e05\u4e91\u89c1\u5413\u4e0d\u5012 \u4ed6\uff0c\u4e0d\u89c9\u4e5f\u6709\u70b9\u5fc3\u865a\uff0c\u786c\u7740\u5934\u8bf4\u9053\uff1a\u201c\u7239\u7239\u53eb\u6211\u4f20\u6d3b\uff0c\u4e5f\u7528\u5f97\u7740\u4ed6\u4eb2\u7b14\u5199\u4e0b\u624b\u8c15\u5417\uff0c\u4f60\u8fd9\u6837 \u8bf4\uff0c\u90a3\u5206\u660e\u662f\u4e0d\u76f8\u4fe1\u6211\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u7591\u5fc3\u5927\u8d77\uff0c\u4f6f\u88c5\u60f6\u6050\uff0c\u4e00\u63d6\u8bf4\u9002\uff1a\u201c\u683c\u683c\u606f\u6012\uff0c\u6211\u600e\u6562\u4e0d\u4fe1\u683c\u683c\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\u6211\u2014\u2014\u201d \u3000\u3000\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u5ffd\u89c9\u4e00\u80a1\u529b\u9053\u5c31\u50cf\u6697\u6d41\u6c79\u6d8c\u7684\u5411\u5979\u88ad\u6765\u3002\u539f\u6765\u91d1\u8d85\u5907\u8fd9\u4e00\u63d6\u662f \u7528\u4e0a\u4e86\u5185\u5bb6\u771f\u529b\uff0c\u610f\u6b32\u8bd5\u5979\u6b66\u529f\u7684\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u90a3\u91cc\u8fd8\u5bb9\u8d6b\u8fde\u6e05\u4e91\u6709\u4f59\u6687\u601d\u7d22\uff1f\u51fa\u4e8e\u672c\u80fd\u5f53\u7136\u662f\u7acb\u5373\u62b5\u5fa1\u3002\u5979\u53cc\u638c\u9f50\u51fa\uff0c\u628a \u5bf9\u65b9\u8feb\u8fc7\u6765\u7684\u638c\u529b\u5316\u89e3\u4e86\u4e00\u534a\uff0c\u8eab\u5f62\u98d8\u95ea\uff0c\u95ea\u8fc7\u4e00\u65c1\u3002\u5927\u6012\u559d\u9053\uff1a\u201c\u91d1\u8d85\u5cb3\uff0c\u4f60\uff01\u201d \u3000\u3000\u5979\u8fd8\u6765\u4e0d\u53ca\u8d28\u95ee\u91d1\u8d85\u5cb3\uff0c\u91d1\u8d85\u5cb3\u5df1\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e2a\u80c6\u5927\u7684\u4e2b\u5934\uff0c\u7adf\u6562\u5192\u5145\u738b \u5e9c\u7684\u683c\u683c\uff0c\u563f\u563f\uff0c\u4f60\u626e\u5f97\u5012\u662f\u5f88\u50cf\u53ea\u53ef\u60dc\u7792\u4e0d\u4f4f\u6211\uff01\u201d\u539f\u6765\u8d6b\u8fde\u6e05\u4e91\u5b66\u7684\u662f\u6b63\u5b97\u5185\u529f\uff0c\u5979\u6240 \u53d1\u7684\u5185\u529b\u548c\u6240\u7528\u7684\u8eab\u6cd5\u90fd\u4e0e\u8d6b\u8fde\u6e05\u6ce2\u4e0d\u540c\uff0c\u91d1\u8d85\u5cb3\u4e00\u8bd5\u5c31\u8bd5\u51fa\u6765\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ed6\u5374\u5e76\u4e0d\u77e5\u9053\u8d6b \u8fde\u6e05\u4e91\u4e43\u662f\u8d6b\u8fde\u6e05\u6ce2\u7684\u540c\u80de\u59b9\u59b9\uff0c\u76f8\u8c8c\u672c\u6765\u5c31\u5341\u5206\u76f8\u4f3c\uff0c\u5e76\u975e\u626e\u7684\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u559d\u9053\uff1a\u201c\u6211\u624b\u4e0a\u5b9d\u5251\u5c31\u662f\u5723\u65e8\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5979\u5df2\u662f\u5b9d\u5251\u51fa\u9798\uff0c\u4e00\u62db \u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u5c31\u5411\u91d1\u8d85\u5cb3\u523a\u53bb\u3002\u8fd9\u4e00\u62db\u5e73\u6de1\u8f7b\u8212\uff0c\u770b\u4f3c\u6beb\u4e0d\u7740\u529b\uff0c\u4f46\u5251\u5c16\u5236\u51fa\uff0c\u5374\u55e4\u55e4\u6709 \u58f0\u3002 \u3000\u3000\u539f\u6765\u5979\u7528\u7684\u662f\u67d4\u4e91\u5251\u6cd5\uff0c\u5251\u6cd5\u67d4\u4e2d\u5bcc\u521a\uff0c\u8f7b\u7075\u7fd4\u52a8\uff0c\u5185\u4e2d\u8574\u85cf\u7740\u5f3a\u52b2\u7684\u771f\u529b\u3002\u90a3\u55e4\u55e4\u58f0 \u54cd\uff0c\u5c31\u662f\u5979\u7684\u5251\u5c16\u7a81\u7834\u5bf9\u65b9\u6240\u53d1\u7684\u9634\u9633\u638c\u529b\uff0c\u6c14\u6d41\u6fc0\u8361\uff0c\u53d1\u800c\u4e3a\u58f0\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u7684\u9634\u9633\u638c\u529b\u4ea6\u662f\u6b66\u5b66\u4e00\u7edd\uff0c\u4e00\u9634\u4e00\u9633\uff0c\u4e92\u76f8\u7275\u5f15\uff0c\u529f\u529b\u7a0d\u5f31\u7684\u7528\u4e0d\u7740\u7ed9\u4ed6\u6253\u4e2a\u6b63 \u7740\uff0c\u5df2\u662f\u6709\u5982\u8eab\u9677\u6fc0\u6d41\u4e4b\u4e2d\uff0c\u800c\u4e14\u4ed6\u5de6\u638c\u53d1\u51fa\u6765\u7684\u5374\u6709\u5982\u5728\u9f13\u98ce\u62a4\u4e2d\u5439\u51fa\u6765\u7684\u70ed\u98ce\uff0c\u53f3\u638c\u53d1 \u51fa\u6765\u7684\u6709\u5982\u5728\u51b0\u7a9f\u91cc\u5377\u8fc7\u6765\u7684\u5bd2\u6f6e\uff0c\u66f4\u662f\u4ee4\u4eba\u96be\u4ee5\u62b5\u53d7\u3002 \u3000\u3000\u9976\u662f\u8d6b\u8fde\u6e05\u4e91\u5b66\u7684\u662f\u6b63\u5b97\u5185\u529f\uff0c\u5728\u8fd9\u4e00\u51b7\u4e00\u70ed\u7684\u714e\u71ac\u4e4b\u4e0b\uff0c\u5251\u6cd5\u4e5f\u662f\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\u4e86\u3002 \u4e09\u5341\u62db\u8fc7\u540e\uff0c\u53ea\u89c1\u5979\u989d\u5934\u4e0a\u7684\u6c57\u73e0\uff0c\u6709\u5982\u9ec4\u8c46\u6781\u5927\u5c0f\uff0c\u5df2\u662f\u4e00\u9897\u9897\u7684\u6ee1\u4e86\u4e0b\u6765\u4e86\u3002\u4f46\u4e00\u9762\u6d41 \u6c57\uff0c\u4e00\u9762\u5374\u662f\u7259\u5173\u6253\u6218\u3002\u53ef\u77e5\u5979\u6240\u53d7\u7684\u714e\u71ac\u4e4b\u82e6\u3002\u91d1\u8d85\u5cb3\u9ed8\u8fd0\u7384\u529f\uff0c\u628a\u9634\u9633\u638c\u529b\u53d1\u6325\u5f97\u6dcb\u6f13 \u5c3d\u81f4\uff0c\u8d6b\u8fde\u6e05\u4e91\u7684\u5251\u5c16\u523a\u5230\u79bb\u8eab\u4e09\u5c3a\u4e4b\u5904\uff0c\u5c31\u7ed9\u90a3\u80a1\u53cd\u5f39\u4e4b\u529b\uff0c\u53cd\u5f39\u56de\u6765\u3002\u90a3\u55e4\u55e4\u58f0\u54cd\uff0c\u4f3c \u7092\u719f\u7684\u9ec4\u8c46\u4e00\u822c\uff0c\u8d8a\u6765\u8d8a\u54cd\u3002 \u3000\u3000\u949f\u7075\u79c0\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8df3\u8d77\u8eab\u6765\uff0c\u89c1\u8d6b\u8fde\u6e05\u4e91\u5f62\u52bf\u4e0d\u5999\uff0c\u62fe\u8d77\u77ed\u5251\uff0c\u66f4\u5373\u52a0\u5165\u6218\u56e2\u3002\u5979 \u8ddf\u6a80\u7fbd\u51b2\u5b66\u4e86\u4e00\u5e74\u5185\u529f\uff0c\u5df1\u662f\u6709\u70b9\u57fa\u7840\uff0c\u6b64\u65f6\u867d\u7136\u8fd8\u662f\u5598\u606f\u672a\u5b9a\uff0c\u5374\u4e5f\u53ef\u4ee5\u52c9\u5f3a\u4e00\u6218\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u5438\u4e86\u4e00\u53e3\u6c14\u3002\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\u3002\u6211\u7528\u4e0d\u7740\u4f60\u5e2e\u5fd9\uff0c \u4f60\u5feb\u8d70\u5427\u3002\u201d\u8bf4\u8bdd\u5206\u795e\uff0c\u51e0\u4e4e\u7ed9\u91d1\u8d85\u5cb3\u4e00\u62f3\u6253\u4e2d\uff0c\u5e78\u4e8f\u949f\u7075\u79c0\u7684\u5251\u6765\u5f97\u5feb\uff0c\u5251\u5c16\u95ea\u7535\u822c\u7684\u6307 \u5411\u4ed6\u7684\u638c\u5fc3\u7684\u52b3\u5bab\u7a74\uff0c\u8fd9\u624d\u66ff\u6e38\u8fde\u6e05\u4e91\u5316\u89e3\u4e86\u4e00\u62db\u3002\u5979\u5728\u9634\u9633\u638c\u529b\u6fc0\u8361\u4e4b\u4e0b\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u6253 \u4e86\u4e24\u4e2a\u5bd2\u5664\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5f00\u5934\u7684\u516d\u4e03\u62db\uff0c\u5c45\u7136\u8fd8\u662f\u4e1d\u6beb\u4e0d\u7f13\u3002\u91d1\u8d85\u5cb3\u89c1\u5979\u6709\u6b64\u529f\u529b\uff0c\u4e5f\u662f \u751a\u611f\u60ca\u5947\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u4f6f\u6012\u9053\uff1a\u201c\u6211\u662f\u5996\u5973\uff0c\u4f60\u966a\u6211\u9001\u547d\uff0c\u503c\u5f97\u4e48\uff1f\u4f60\u53bb\u6551\u4f60\u503c\u5f97\u6551\u7684\u4eba\u5427\uff01\u201d\u8fd9\u53e5 \u8bdd\u7684\u610f\u601d\u5f88\u660e\u663e\u5f97\u5f88\uff0c\u662f\u8981\u5979\u8d76\u56de\u53bb\u5e2e\u6a80\u7fbd\u51b2\u9003\u8d70\u3002 \u3000\u3000\u949f\u7075\u79c0\u4e5f\u77e5\u9053\u81ea\u5df1\u5e2e\u4e0d\u4e86\u5979\u7684\u5927\u5fd9\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u5979\u4e3a\u5927\u54e5\u54e5\u820d\u8eab\uff0c\u6211\u5c82\u80fd\u5f03\u5979\u800c \u53bb\uff1f\u4f55\u51b5\u5927\u54e5\u54e5\u534a\u8eab\u4e0d\u9042\uff0c\u5979\u82e5\u88ab\u64d2\uff0c\u6211\u548c\u5927\u54e5\u54e5\u4e5f\u7edd\u8ba8\u9003\u8dd1\u4e0d\u4e86\u3002\u4e0e\u5176\u88ab\u5927\u54e5\u54e5\u8d23\u9a82\u6211\u4e0d \u8bb2\u4e49\u6c14\uff0c\u4e0d\u5982\u548c\u8fd9\u4f4d\u59d1\u5a18\u8054\u624b\u4e00\u62fc\uff0c\u8981\u80fd\u591f\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u4fdd\u5168\u5927\u54e5\u54e5\u4e00\u6761\u6027 \u547d\u3002\u201d\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u6211\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u80af\u4e3a\u6211\u7684\u5927\u54e5\u54e5\u62fc\u547d\uff0c\u6211\u5c31\u7518\u5fc3 \u4e0e\u4f60\u540c\u6b7b\uff01\u201d\u91d1\u8d85\u5cb3\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u201d\u4e2b\u5934\uff0c\u6211\u504f\u4e0d\u8ba9\u4f60\u4eec\u6b7b\u5f97\u90a3\u4e48\u5bb9\u6613\uff0c \u6211\u8981\u4f60\u4eec\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u91d1\u5149\u8000\u773c\uff0c\u8d6b\u8fde\u6e05\u4e91\u5df1\u662f\u5530\u7684\u4e00\u58f0\u4e00\u5251\u5411\u4ed6\u523a\u6765\u3002\u8fd9\u4e00\u5251\u7adf\u7136 \u7a81\u7834\u4e86\u4ed6\u7684\u638c\u529b\u7684\u9632\u5fa1\u5708\uff0c\u51e0\u4e4e\u523a\u5230\u4e86\u4ed6\u7684\u9762\u95e8\u3002\u91d1\u8d85\u5cb3\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u52a0\u5f3a\u638c\u529b\u3002\u8fd9\u624d\u628a \u5979\u7684\u653b\u52bf\u538b\u4e86\u4e0b\u53bb\u3002\u539f\u6765\u8d6b\u8fde\u6e05\u4e91\u7ec3\u7684\u662f\u6b63\u5b97\u5185\u529f\uff0c\u529f\u529b\u867d\u7136\u6bd4\u4e0d\u4e0a\u91d1\u8d85\u5cb3\uff0c\u4f46\u5374\u6bd4\u4ed6\u6b63\u90aa \u5408\u4e00\u7684\u5185\u529f\u7cbe\u7eaf\u3002\u6709\u949f\u7075\u79c0\u66ff\u5979\u5206\u62c5\u4e86\u538b\u529b\uff0c\u5979\u8d81\u7740\u5bf9\u65b9\u8bf4\u8bdd\u5206\u5fc3\u4e4b\u9645\uff0c\u7cb9\u7136\u4e00\u51fb\uff0c\u4ee4\u5f97\u91d1 \u8d85\u5cb3\u4e5f\u9669\u4e9b\u7ed9\u5979\u6740\u4e86\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u8bdd\u5df1\u8bf4\u6ee1\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u9634\u9633\u638c\u529b\uff0c\u4ea4\u4e92\u4f7f\u7528\uff0c\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\u3002\u949f\u7075\u79c0\u6bd5\u7adf\u4fee\u4e3a \u5c1a\u6d45\uff0c\u5f00\u5934\u5341\u6570\u62db\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u62b5\u5fa1\uff0c\u4e8c\u5341\u62db\u4e00\u8fc7\uff0c\u5bd2\u70ed\u4ea4\u4f5c\uff0c\u5979\u5df1\u662f\u8fde\u547c\u5438\u4e5f\u611f\u4e0d\u8212\u4e86\u3002\u8d6b \u8fde\u6e05\u4e91\u4e00\u4e2a\u4eba\u63a5\u4e86\u5bf9\u65b9\u4e03\u516b\u6210\u653b\u52bf\u3002\u4e0d\u7981\u53c8\u662f\u6c57\u5982\u96e8\u4e0b\uff0c\u6bd4\u8d77\u521a\u624d\u949f\u7075\u79c0\u6ca1\u6709\u52a0\u5165\u6218\u56e2\u9500\u65f6 \u5019\uff0c\u66f4\u52a0\u5403\u529b\u3002\u5979\u81ea\u5df1\u77e5\u9053\uff0c\u662f\u7edd\u8ba1\u4e0d\u80fd\u518d\u62b5\u5fa1\u5341\u62db\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u5df2\u662f\u6447\u6447\u6b32\u5760\u4e86\u3002\u5ffd\u542c\u4e00\u7f15\u7bab\u58f0\u3002\u4fe8\u4f3c\u4ece\u5929\u800c\u964d\uff0c\u7bab\u58f0\u6e05\u4eae\uff0c\u5439\u7bab\u7684\u4eba\uff0c\u5185\u529f\u6df1 \u539a\uff0c\u884c\u5bb6\u4e00\u542c\u5c31\u77e5\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u67f3\u5143\u7532\u8bf4\u7684\u4e43\u662f\u5047\u8bdd\uff1f\u201d\u539f\u6765\u4ed6\u5230\u8fc7\u5343\u67f3\u5728\uff0c\u4ece\u67f3\u5143\u7532 \u53e3\u4e2d\u5f97\u77e5\u6a80\u7fbd\u51b2\u4e1a\u5df2\u91cd\u4f24\u6b8b\u5e9f\u7684\u6d88\u606f\uff0c\u8fd9\u624d\u6562\u8086\u65e0\u5fcc\u60ee\uff0c\u72ec\u81ea\u524d\u6765\u641c\u5c71\u7684\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u542c\u5f97\u6a80\u7fbd\u51b2\u7684\u58f0\u97f3\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u91d1\u8d85\u5cb3\uff0c\u597d\u6b79\u4f60\u4e5f\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u6b3a\u4fae \u4e24\u4e2a\u5c0f\u59d1\u5a18\uff0c\u4e0d\u6015\u5931\u6389\u4f60\u7684\u8eab\u4efd\u4e48\uff1f\u201d \u3000\u3000\u58f0\u97f3\u521d\u8d77\u4e4b\u65f6\uff0c\u8ddd\u79bb\u4f3c\u4e4e\u8fd8\u5728\u534a\u91cc\u4e4b\u5916\uff0c\u8bf4\u5230\u6700\u540e\u51e0\u4e2a\u5b57\uff0c\u6a80\u7fbd\u51b2\u7684\u8eab\u5f62\u5df2\u7ecf\u51fa\u73b0\u5728\u4ed6 \u4eec\u7684\u9762\u524d\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u559c\u51fa\u671b\u5916\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u597d\u4e86\uff01\u201d\u4e00\u8de4\u6454\u5012\u3002\u8d6b\u8fde\u6e05\u4e91\u8fde\u5fd9\u62c9\u8d77\u8dc3\u8fc7\u4e00 \u65c1\u3001\u597d\u5728\u6a80\u7fbd\u51b2\u5df2\u7ecf\u6765\u5230\uff0c\u91d1\u8d85\u5cb3\u751f\u6015\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u5df1\u662f\u4e0d\u6562\u53bb\u4f24\u5bb3\u5979\u4eec\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u51dd\u795e\u7ec6\u5ba1\u6a80\u7fbd\u51b2\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u4ed6\u7684\u6b66\u529f\u6062\u590d\u5f97 \u6ca1\u591a\u4e45\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u88c5\u8154\u4f5c\u52bf\u800c\u5df2\u3001\u54fc\uff0c\u5373\u4f7f\u4ed6\u6b66\u529f\u4f3c\u662f\u6062\u590d\uff0c\u53ea\u4e0d\u8fc7\u548c\u6211\u6253\u4e2a\u5e73\u624b\u800c\u5df2\uff0c \u6211\u6015\u4ed6\u4f55\u6765\uff1f\u201d\u4e8e\u662f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5728\u4eac\u57ce\u51e0\u6b21\u4ea4\u624b\uff0c\u672a\u5206\u80dc\u8d1f\uff0c\u4eca\u65e5\u5c31\u51b3\u4e00\u51b3\u96cc\u96c4 \u5427\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u51fa\u62db\u5427\uff01\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u82e5\u8f93\u4e86\u5982\u4f55\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u76b1\u7709\u9053\uff1a\u201c\u6027\u547d\u7ed9\u4f60\u5c31\u662f\uff0c\u4f55\u987b\u591a\u95ee\uff01\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u4f60\u662f\u7687\u4e0a\u6240\u8981\u7684\u4eba\uff0c\u6211\u53ef\u4e0d\u6562\u8981\u4f60\u6027\u547d\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u82e5\u8f93\u4e86\uff0c\u6211\u8ba9\u4f60\u5e26\u56de\u4eac\u57ce\u4ea4\u5dee\u5c31\u662f\u3002\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591a\u8c22\u8d1d\u5b50\u5141\u8bfa\uff0c\u5c31\u8fd9\u6837\u5427\uff01\u201d\u5f97\u610f\u4e4b\u72b6\uff0c\u597d\u50cf\u4ed6\u5df2\u662f\u5fc5\u80dc\u65e0\u7591\u3002\u539f \u6765\u4ed6\u5df2\u770b\u51fa\u6a80\u7fbd\u51b2\u662f\u5927\u75c5\u521d\u6108\uff0c\u5143\u6c14\u5c1a\u672a\u5145\u6c9b\uff0c\u662f\u4ee5\u60f3\u6fc0\u6a80\u7fbd\u51b2\u52a8\u6012\uff0c\u8fd9\u5c31\u66f4\u6709\u628a\u63e1\u53d6\u80dc\u3002 \u949f\u7075\u79c0\u5598\u606f\u672a\u5b9a\u3002\u9760\u5728\u8d6b\u8fde\u6e05\u4e91\u7684\u8eab\u4e0a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u7b11\u5f97\u592a\u65e9\uff0c\u4f60\u82e5\u8f93\u4e86\u5982\u4f55\uff0c\u53ef\u8fd8\u6ca1 \u6709\u8bf4\u5462\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u9053\uff1a\u201c\u8bf7\u6a80\u8d1d\u5b50\u5212\u51fa\u9053\u513f\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8981\u4f60\u7684\u6027\u547d\uff0c\u53ea\u8981\u4f60\u7ed9\u6211\u8fd9\u5c0f\u59b9\u5b50\u78d5\u5934\u8d54\u793c\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u6781\u4e86\u3002\u591a\u8bb2\u5927\u54e5\u54e5\u7ed9\u6211\u4e89\u8fd9\u4e2a\u9762\u5b50\u3002\u6211\u6454\u4e86\u4e00\u8de4\uff0c\u5f97\u56de\u4e00\u4e2a\u54cd \u5934\uff0c\u9a6c\u9a6c\u864e\u864e\uff0c\u4e5f\u7b97\u626f\u5e73\u5566\u3002\u5582\uff0c\u59d3\u91d1\u7684\uff0c\u6211\u5927\u54e5\u54e5\u5212\u51fa\u4e86\u9053\u513f\uff0c\u4f60\u662f\u4f9d\u4e0d\u4f9d\uff1f\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u7eb5\u58f0\u5927\u7b11\uff1a\u201c\u53ea\u6015\u4f60\u65e0\u798f\u6d88\u53d7\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u559d\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\u3002\u51fa\u62db\uff01\u201d\u4f46\u91d1\u8d85\u5cb3\u4ecd\u672a\u51fa\u62db\uff0c\u53ea\u89c1\u4ed6\u7ad9\u4e86\u4e2a\u5f0f \u4e8e\uff0c\u53cc\u638c\u7f13\u7f13\u4e3e\u8d77\uff0c\u638c\u5fc3\u5411\u5916\uff0c\u53cc\u76ee\u76f4\u89c6\uff0c\u72b6\u4f3c\u6597\u9e21\u3002\u6a80\u7fbd\u51b2\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u7389\u7bab\u62ff\u5728\u624b\u4e2d\uff0c \u4e25\u9635\u4f3c\u5f85\u3002 \u3000\u3000\u949f\u7075\u79c0\u9760\u5728\u8d6b\u8fde\u6e05\u4e91\u7684\u8eab\u4e0a\uff0c\u542c\u89c1\u5979\u7684\u5fc3\u535c\u535c\u7684\u8df3\u3002\u5979\u672c\u6765\u60f3\u8bf4\u51e0\u53e5\u8c03\u4f83\u91d1\u8d85\u5cb3\u7684\u8bdd\uff0c \u4e5f\u5413\u5f97\u4e0d\u6562\u8bf4\u4e86\u3002 \u3000\u3000\u5c71\u96e8\u6b32\u6765\u98ce\u6ee1\u697c\uff0c\u5979\u867d\u7136\u7ecf\u9a8c\u4e0d\u4e30\uff0c\u770b\u5230\u4e86\u8fd9\u4e00\u53cc\u5f15\u6e2f\u5f85\u53d1\u7684\u60c5\u666f\uff0c\u4ea6\u5df2\u77e5\u9053\u6b64\u6218\u975e\u540c \u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u9661\u7136\u95f4\u53ea\u542c\u5f97\u91d1\u8d85\u5cb3\u4e00\u58f0\u5927\u559d\uff0c\u5de6\u638c\u5212\u4e86\u5f27\u5f62\uff0c\u53f3\u638c\u8ddf\u7740\u53d1\u51fa\u3002\u5148\u662f\u70ed\u98ce\u547c\u547c\uff0c\u8ddf\u7740\u5bd2 \u98d9\u98d9\u5377\u5730\u3002\u949f\u7075\u79c0\u5728\u767e\u6b65\u5f00\u5916\uff0c\u4e5f\u611f\u5230\u5bd2\u70ed\u4ea4\u4fb5\u3001\u5979\u7684\u4e00\u9897\u5fc3\u4e0d\u7531\u5f97\u4e5f\u7830\u7830\u5730\u8df3\uff1a\u201c\u5927\u54e5\u54e5 \u521a\u521a\u6062\u590d\u5982\u5e38\uff0c\u4ed6\u62b5\u6321\u5f97\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u614c\u4e0d\u5fd9\uff0c\u628a\u6696\u7389\u7bab\u51d1\u5230\u5507\u8fb9\uff0c\u5439\u51fa\u4e00\u53e3\u7f61\u6c14\uff0c\u70ed\u98ce\u4e0e\u5bd2\u98d9\u597d\u50cf\u4f1a\u5408\u5230\u4e00\u8d77\uff0c\u7a81 \u7136\u201c\u4e2d\u548c\u201d\u4e86\u3002\u91d1\u8d85\u5cb3\u4e5f\u611f\u5230\u6696\u6d0b\u6d0b\u7684\u597d\u4e0d\u8212\u670d\u3002\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u5927\u75c5\u4e00 \u573a\uff0c\u8fd8\u662f\u548c\u6211\u6253\u6210\u5e73\u624b\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u632b\u4e86\u4ed6\u7684\u9510\u6c14\uff0c\u7acb\u5373\u62a2\u73ab\uff0c\u6696\u7389\u7bab\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u767b\u65f6\u62a2\u4e86\u4e03\u6210\u653b\u52bf\u3002\u949f \u7075\u79c0\u770b\u5f97\u7709\u98de\u8272\u821e\uff0c\u8bf4\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u4f60\u770b\uff01\u5927\u54e5\u54e5\u6253\u5f97\u591a\u597d\uff0c\u591a\u5999\uff01\u770b\u4ed6\u4e0d\u4f46\u6062\u590d\u4e86\u6b66\u529f\uff0c \u597d\u50cf\u66f4\u80dc\u4e8e\u4ece\u524d\u4e86\u3002\u201d\u8d6b\u8fde\u6e05\u4e91\u6ca1\u6709\u56de\u7b54\uff0c\u4e00\u53cc\u773c\u775b\u3002\u53ea\u662f\u6ce8\u89c6\u7740\u6a80\u7fbd\u51b2\u90a3\u679a\u6325\u821e\u7684\u7389\u7bab\uff0c \u7709\u5934\u6e10\u6e10\u76b1\u8d77\u6765\u4e86\u3002\u949f\u7075\u79c0\u9760\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u53c8\u542c\u89c1\u5979\u5fc3\u8df3\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6a80\u7fbd\u51b2\u4e0d\u77e5\u600e\u4e48\u7684\uff0c\u65e0\u7aef\u9000\u4e86\u4e24\u6b65\u3002\u91d1\u8d85\u5cb3\u62a2\u8fc7\u653b\u52bf\uff0c\u6a80\u7fbd\u51b2\u628a\u6696\u7389\u7bab\u7684\u4e00\u7aef\u6307 \u4ed6\u7684\u638c\u5fc3\uff0c\u53e6\u4e00\u7aef\u51d1\u5230\u5507\u8fb9\uff0c\u5439\u51fa\u7b2c\u4e8c\u53e3\u7f61\u6c14\u3001\u91d1\u8d85\u5cb3\u6253\u4e86\u4e2a\u98a4\uff0c\u949f\u7075\u79c0\u6b63\u81ea\u5fc3\u60f3\uff1a\u201c\u539f\u6765 \u5927\u54e5\u54e5\u662f\u8bf1\u654c\u4e4b\u8ba1\u3002\u201d\u4f46\u770b\u4e0b\u53bb\u53c8\u4f3c\u4e4e\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002\u53ea\u89c1\u91d1\u8d85\u5cb3\u867d\u7136\u6253\u4e86\u4e2a\u98a4\uff0c\u4f46\u8138\u4e0a\u5df2\u9732 \u51fa\u4e86\u7b11\u5bb9\uff0c\u624b\u5e95\u4e0b\u4e5f\u662f\u4e1d\u8c6a\u4e0d\u7f13\u3002 \u3000\u3000\u539f\u6765\u6a80\u7fbd\u51b2\u7b2c\u4e8c\u6b21\u4ece\u6696\u7389\u7bab\u4e2d\u5439\u51fa\u6765\u7684\u7f61\u6c14\uff0c\u867d\u7136\u66f4\u4e3a\u731b\u70c8\uff0c\u4f46\u6548\u679c\u5374\u53cd\u800c\u6bd4\u4e0d\u4e0a\u7b2c\u4e00 \u6b21\u5439\u51fa\u6765\u7684\u3002 \u3000\u3000\u90a3\u79cd\u61d2\u6d0b\u6d0b\u7684\u611f\u89c9\uff0c\u4e0d\u5f85\u91d1\u8d85\u5cb3\u8fd0\u529f\u9a71\u9664\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u5373\u81ea\u884c\u6d88\u5931\u3002\u91d1\u8d85\u5cb3\u5fc3\u5934\u5927 \u559c\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u8d70\u4e86\u773c\u5462\uff0c\u539f\u6765\u5e76\u6ca1\u770b\u9519\uff0c\u4ed6\u679c\u7136\u662f\u4e2d\u6c14\u4e0d\u8db3\uff0c\u96be\u4ee5\u4e3a\u7ee7\u4e86\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u770b\u5f97\u83ab\u540d\u5176\u5999\uff0c\u6084\u6084\u95ee\u9053\uff1a\u201c\u59b9\u59ca\u4f9d\u4f60\u770b\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u91d1\u8d85\u5cb3\u5df1\u662f\u559d \u9053\uff1a\u201c\u6a80\u7fbd\u51b2\uff0c\u4f60\u4e0d\u81ea\u91cf\u529b\uff0c\u5927\u75c5\u521d\u6108\uff0c\u4f60\u5373\u5f3a\u8fd0\u7384\u529f\uff0c\u5bf9\u4f60\u53ea\u6709\u4f24\u5bb3\uff0c\u4f60\u662f\u7edd\u8bb8\u80dc\u4e0d\u4e86\u6211 \u7684\uff0c\u4f60\u8fd8\u662f\u6b7b\u4e86\u8fd9\u6761\u5fc3\u5427\uff0c\u6211\u4e0d\u60f3\u8981\u4f60\u7684\u6027\u547d\uff0c\u5feb\u5feb\u8ba4\u8f93\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u54ac\u7740\u7259\u6839\uff0c\u4f9d\u7136\u594b\u6218\u3002\u91d1\u8d85\u5cb3\u51b7\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u662f\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\u4e86\u3002\u597d\uff0c\u6211\u5012 \u8981\u770b\u4f60\u8fd8\u80fd\u652f\u6301\u591a\u5c11\u65f6\u5019\u3002\u201d\u52a0\u5f3a\u653b\u52bf\uff0c\u628a\u9634\u9633\u4e94\u884c\u638c\u7684\u5999\u7528\u5c3d\u6570\u53d1\u6325\uff0c\u5de6\u638c\u62cd\u51fa\u7684\u662f\u7b2c\u4e03 \u91cd\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u7684\u638c\u529b\uff0c\u53f3\u638c\u5219\u4ee5\u201c\u96f7\u795e\u638c\u201d\u53d1\u51fa\u7684\u70ed\u98ce\uff0c\u5411\u6a80\u7fbd\u51b2\u731b\u653b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u8d8a\u6765\u8d8a\u611f\u89c9\u70bd\u70ed\u96be\u5f53\u4e86\uff0c\u80f8\u53e3\u597d\u50cf\u585e\u4e86\u4e00\u56e2\u4e1c\u897f\u4f3c\u7684\uff0c\u4ee4\u4ed6\u7a92\u606f\u5f97\u51e0\u4e4e\u60f3\u8981\u7206 \u70b8\u3002 \u3000\u3000\u539f\u6765\u4ed6\u82e5\u662f\u5faa\u5e8f\u6e10\u4e5f\u6700\u5c11\u8fd8\u5f97\u4e00\u4e2a\u6708\u7684\u5de5\u592b\uff0c\u65b9\u80fd\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u4ee4\u81ea\u5df1\u884c\u52a8\u5982\u5e38\u3002\u53ea \u56e0\u542c\u5f97\u91d1\u8d85\u5cb3\u5728\u5916\u9762\u6b3a\u4fae\u4ed6\u7684\u4e49\u59b9\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u6f5c\u529b\u7a81\u7136\u53d1\u6325\uff0c\u4e00\u4e0b\u5b50\u5c31\u628a\u7ecf\u8109\u6253\u901a\u3002\u53ef\u662f \u57fa\u7840\u6bd5\u7adf\u8fd8\u662f\u672a\u66fe\u5de9\u56fa\u7684\u3002\u521d\u65f6\u56e0\u4e3a\u91d1\u8d85\u5cb3\u5148\u6253\u4e86\u4e00\u573a\uff0c\u4ed6\u8fd8\u53ef\u4ee5\u6253\u6210\u4e86\u5e73\u624b\uff0c\u65f6\u95f4\u4e00\u4e45\u3002 \u771f\u529b\u5f7c\u6b64\u90fd\u6709\u6d88\u8017\uff0c\u4ed6\u5374\u662f\u4e0d\u5982\u91d1\u8d85\u5cb3\u4e4b\u80fd\u6301\u4e45\u4e86\u3002\u949f\u7075\u79c0\u6b64\u65f6\u4e0d\u89c9\u5df2\u662f\u7ad9\u4e86\u8d77\u6765\u3002\u5168\u795e\u89c2 \u6218\u3002\u5979\u89c1\u6a80\u7fbd\u51b2\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u9762\u7ea2\u5982\u8840\u3002\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\u3002 \u3000\u3000\u90a3\u77e5\u4ee4\u5979\u66f4\u52a0\u5403\u60ca\u7684\u8fd8\u5728\u540e\u5934\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u80f8\u53e3\u6c14\u95f7\uff0c\u70bd\u70ed\u96be\u5f53\uff0c\u6574\u4e2a\u4eba\u5c31\u50cf\u8981\u201c\u7206\u70b8\u201d\u4f3c\u7684\u3002\u4e0d\u4f46\u9762\u7ea2\u5982\u8840\uff0c\u5ffd\u5730\u201c\u54c7\u201d \u7684\u4e00\u58f0\uff0c\u53e3\u4e2d\u5410\u51fa\u4e86\u9c9c\u8840\uff01 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u5fd9\u5728\u795e\u7075\u79c0\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u9547\u5b9a\u4e00\u4e9b\uff0c\u4ed6\u4e0d\u89c1\u5f97\u5c31\u4f1a\u8f93\u7684\u3002\u4f60\u82e5\u614c\u4e71\uff0c\u53cd\u800c\u4f1a\u5f71 \u54cd\u4ed6\uff01\u201d \u3000\u3000\u201c\u5927\u54e5\u54e5\u5230\u4e86\u8fd9\u6837\u7530\u5730\uff0c\u8fd8\u80fd\u591f\u6253\u4e0b\u53bb\u4e48\uff1f\u201d\u949f\u7075\u79c0\u534a\u4fe1\u534a\u7591\uff0c\u5fc3\u91cc\u60f3\u9053\u3002\u4f46\u5979\u81ea\u5df1\u65e9 \u5df2\u662f\u529b\u7aed\u7b4b\u75b2\uff0c\u5373\u4f7f\u4e0d\u987e\u6027\u547d\uff0c\u81ea\u77e5\u4e5f\u662f\u65e0\u6cd5\u5e2e\u5f97\u4e86\u5927\u54e5\u54e5\u7684\u5fd9\u4e86\u3002\u9664\u4e86\u542c\u4ece\u8d6b\u8fde\u6e05\u4e91\u7684\u529d \u544a\uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f \u3000\u3000\u91d1\u8d85\u5cb3\u559d\u9053\uff1a\u201d\u6a80\u7fbd\u51b2\uff0c\u4f60\u8fd8\u4e0d\u8ba4\u8f93\uff0c\u5f53\u771f\u662f\u8981\u627e\u6b7b\u5417\uff1f \u3000\u3000\u559d\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6a80\u7fbd\u51b2\u6717\u58f0\u541f\u9047\uff1a\u201c\u77e5\u6211\u8005\uff0c\u8c13\u6211\u5fc3\u5fe7\uff0c\u4e0d\u77e5\u6211\u8005\uff0c\u8c13\u6211\u4f55\u6c42\uff1f\u60a0\u60a0 \u82cd\u5929\uff0c\u5f7c\u4f55\u4eba\u54c9\uff01\u563f\u563f\uff0c\u5927\u5730\u832b\u832b\u96be\u7acb\u8db3\uff0c\u4f46\u51ed\u4e00\u5251\u51b3\u6069\u4ec7\uff01\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6\u4e00\u53e3\u9c9c\u8840\u5410\u4e86\u51fa\u6765\uff0c\u7cbe\u795e\u7adf\u4f3c\u9661\u7136\u91cd\u632f\u4e86\u3002\u4ed6\u6717\u58f0\u9ad8\u541f\uff0c\u597d\u50cf\u8981\u628a\u79ef\u538b\u80f8 \u4e2d\u7684\u90c1\u95f7\u5168\u90e8\u53d1\u6cc4\u51fa\u6765\uff01\u624b\u4e2d\u7684\u7389\u7bab\u76d8\u65cb\u98de\u821e\uff0c\u5982\u5251\u5982\u7b14\uff0c\u6325\u6d12\u81ea\u5982\uff0c\u70b9\u3001\u6253\u3001\u538b\u3001\u6233\uff0c\u65e0 \u4e00\u4e0d\u662f\u7edd\u5999\u7684\u62db\u6570\u3002\u62db\u62db\u6307\u5411\u91d1\u8d85\u5cb3\u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u949f\u7075\u79c0\u66fe\u7ecf\u8ddf\u6a80\u7fbd\u51b2\u5b66\u5439\u7bab\uff0c\u6b64\u65f6\u5979\u628a\u7528\u5c71\u4e2d\u7af9\u5b50\u81ea\u5236\u7684\u4e00\u652f\u7bab\u62ff\u51fa\u6765\uff0c\u6a80\u7fbd\u51b2\u6717\u541f\uff0c \u5979\u8ddf\u7740\u8282\u62cd\u5439\u7bab\u76f8\u548c\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u90c1\u95f7\u51fa\u6765\uff0c\u4e0d\u4f46\u80f8\u4e2d\u8212\u626c\uff0c\u6253\u5f97\u4e5f\u662f\u8d8a\u6765\u8d8a\u7545\u987a\u4e86\u3002\u7389\u7bab\u968f\u7740\u7bab\u58f0\u7684\u987f\u632b\u6291\u626c\uff0c \u7aef\u7684\u662f\u6709\u5982\u4e91\u6d41\u6c34\u4e4b\u5999\uff01 \u3000\u3000\u91d1\u8d85\u5cb3\u906e\u62e6\u4e0d\u4f4f\uff0c\u6b63\u60f3\u4f5c\u4e24\u8d25\u4ff1\u4f24\u7684\u4e00\u62fc\uff0c\u5ffd\u89c9\u80cc\u5fc3\u4e00\u5e95\u6a80\u7fbd\u51b2\u7684\u7389\u7bab\u5df2\u7ecf\u70b9\u7740\u810a\u690e\u7684 \u5929\u5e9c\u7a74\u3002\u4f46\u6a80\u7fbd\u51b2\u7684\u7389\u7bab\u53ea\u662f\u8d34\u7d27\u4e86\u4ed6\uff0c\u5e76\u672a\u53d1\u529b\u3002 \u3000\u3000\u201c\u5929\u5e9c\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u7684\u6b7b\u7a74\u4e4b\u4e00\uff0c\u91d1\u8d85\u5cb3\u54ea\u91cc\u8fd8\u6562\u52a8\u5f39\uff01 \u3000\u3000\u6a80\u7fbd\u51b2\u559d\u9053\uff1a\u201c\u5411\u6211\u7684\u5c0f\u59b9\u5b50\u966a\u793c\uff01\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u91d1\u67d0\u65e0\u793c\uff0c\u5192\u72af\u59d1\u5a18\uff0c\u4e07\u671b\u6055\u7f6a\u3002\u201d \u3000\u3000\u6a80\u7fbd\u4ef6\u62ff\u5f00\u7389\u8427\uff0c\u91d1\u8d85\u5cb3\u98de\u5feb\u5c31\u8dd1\u3002\u949f\u7075\u79c0\u53eb\u9053\uff1a\u201c\u5582\u3001\u5582\uff0c\u4f60\u8fd8\u672a\u66fe\u5411\u6211\u78d5\u5934\u5462\uff01\u4f60 \u4e0d\u78d5\u5934\uff0c\u5c31\u60f3\u6211\u9976\u6055\u4f60\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u503c\u3002\u201c\u5c0f\u59b9\u5b50\uff0c\u7531\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u91d1\u8d85\u5cb3\u8dd1\u5f97\u98de\u5feb\uff0c\u8f6c\u773c\u6ca1\u4e86\u8e2a\u8ff9\u3002\u949f\u7075\u79c0\u987f\u8db3\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u7559\u4e0b\u540e\u60a3\u4e48\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9002\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u6211\u66ff\u4f60\u51fa\u4e86\u53e3\u6c14\uff0c\u4f60\u8fd8\u4e0d\u6ee1\u610f\u5417\uff1f\u55ef\uff0c\u4f60\u8ddf\u7740\u6211\uff0c\u8fd9\u4e00\u751f\u5c31\u6ce8\u5b9a \u662f\u8981\u591a\u707e\u591a\u96be\u7684\u4e86\uff0c\u53ea\u8981\u54b1\u4eec\u90fd\u8fd8\u6d3b\u7740\uff0c\u53c8\u4f55\u5fc5\u7406\u4f1a\u90a3\u8bb8\u591a\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bf4\u8bdd\u628a\u949f\u7075\u79c0\u542c\u5f97\u5fc3\u91cc\u751c\u751c\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u3002\u4f60\u7ed9\u75c5\u9b54\u56f0\u4e86\u4e00\u5e74\uff0c\u4eca\u65e5\u65b9\u80fd\u8131\u56f0\u3002 \u54b1\u4eec\u662f\u8be5\u6b22\u559c\u624d\u5bf9\u3002\u5c31\u7b97\u4fbf\u5b9c\u4e86\u90a3\u53ae\u5427\u3002\u201d\u5979\u5fc3\u91cc\u751c\u4e1d\u4e1d\u7684\uff0c\u5374\u4e0d\u77e5\u6a80\u7fbd\u51b2\u6b63\u5728\u5fc3\u5934\u82e6\u7b11\u3002 \u3000\u3000\u539f\u6765\u4ed6\u662f\u5168\u51ed\u4e00\u80a1\u6c14\u65b9\u80fd\u652f\u6491\u5230\u6700\u540e\u80dc\u5229\u7684\uff0c\u8fd9\u80a1\u6c14\u4e00\u53d1\u6cc4\u51fa\u6765\uff0c\u4ed6\u4ea6\u5df2\u662f\u5982\u6cc4\u4e86\u6c14\u7684 \u76ae\u7403\u4e86\u3002\u4ed6\u7684\u7389\u7bab\u8d34\u7740\u91d1\u8d85\u5cb3\u80cc\u5fc3\u7684\u7a74\u9053\u4e4b\u65f6\uff0c\u4ed6\u7684\u529f\u529b\u5176\u5b9e\u5df2\u662f\u6240\u4f59\u65e0\u51e0\u3002\u91d1\u8d85\u5cb3\u867d\u7136\u4e5f \u662f\u5143\u6c14\u5927\u4f24\uff0c\u4f46\u6bd4\u8d77\u4ed6\u6765\uff0c\u8fd8\u662f\u597d\u5f97\u591a\u7684\u3001\u6a80\u7fbd\u51b2\u81ea\u77e5\uff0c\u5373\u4f7f\u91d1\u8d85\u5cb3\u88ab\u5730\u70b9\u7740\u6b7b\u7a74\uff0c\u4f46\u4ed6\u7684 \u5185\u529f\u4e0d\u80fd\u6df1\u900f\u7a74\u9053\uff0c\u4ee5\u91d1\u8d85\u5cb3\u7684\u5185\u529f\u9020\u8be3\uff0c\u4ed6\u4e5f\u672a\u5fc5\u80fd\u5236\u91d1\u8d85\u5cb3\u7684\u6b7b\u547d\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4e0d\u60f3\u949f\u7075 \u79c0\u4e3a\u4ed6\u62c5\u5fe7\uff0c\u5374\u662f\u4e0d\u4fbf\u5bf9\u949f\u7075\u79c0\u76f4\u8bf4\u4e86\u3002\u8d6b\u8fde\u6e05\u4e91\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u5fc3\u4e2d\u5374\u662f\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb \u5473\u3002\u597d\u50cf\u751c\u9178\u82e6\u8fa3\uff0c\u517c\u800c\u6709\u4e4b\u3002\u6a80\u7fbd\u51b2\u6b63\u8981\u548c\u5979\u8bf4\u8bdd\uff0c\u5979\u5df2\u662f\u7ad9\u4e86\u8d77\u6765\uff0c\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u770b\u6765 \u6211\u662f\u591a\u6b64\u4e00\u4e3e\u4e86\u3002\u55ef\uff0c\u8fd9\u4e2a\u5730\u65b9\u5373\u4f7f\u4f60\u4eec\u4e0d\u80fd\u518d\u5f85\u4e0b\u53bb\uff0c\u4e5f\u53ef\u4ee5\u627e\u5230\u7b2c\u4e8c\u4e2a\u4e16\u5916\u6843\u6e90\uff0c\u6211\u53c8 \u4f55\u5fc5\u91c7\u6270\u4e71\u4f60\u4eec\u7684\u5b89\u9759\u3002\u201d \u3000\u3000\u5979\u4e00\u8bf4\u5b8c\uff0c\u9a6c\u4e0a\u5c31\u8d70\u3002\u6a80\u7fbd\u51b2\u83ab\u540d\u5176\u5999\uff0c\u53eb\u9053\uff1a\u201c\u6e05\u4e91\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8d6b\u8fde\u6e05\u4e91\u7684 \u5f71\u5b50\u65e9\u5df2\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u662f\u4e2a\u806a\u660e\u7684\u4eba\uff0c\u5979\u77e5\u9053\u8d6b\u8fde\u6e05\u4e91\u60f3\u8981\u8bf4\u7684\u610f\u601d\u90a3\u610f\u601d\u662f\u8bf4\u613f\u4ed6\u4eec\u767d\u5934\u5055\u8001\u7684\u3002\u53ea \u8981\u4ed6\u4eec\u80fd\u627e\u5230\u53e6\u4e00\u4e2a\u201c\u4e16\u5916\u6843\u6e90\u201d\uff0c\u5e73\u5b89\u5ea6\u8fc7\u4e00\u751f\uff0c\u53d7\u70b9\u59d4\u5c48\u8fd8\u7b97\u4ec0\u4e48\uff0c\u4f55\u987b\u8fa9\u767d\uff1f \u3000\u3000\u201c\u8fd9\u4f4d\u59d1\u5a18\u5012\u662f\u6211\u7684\u77e5\u5fc3\uff01\u201d\u949f\u7075\u79c0\u60f3\u9053\uff1a\u201c\u5979\u628a\u6211\u5fc3\u91cc\u7684\u8bdd\u90fd\u8bf4\u51fa\u6765\u4e86\u3002\u4e00\u70b9\u4e0d\u9519\uff0c \u6211\u4f46\u6c42\u80fd\u4e0e\u5927\u54e5\u54e5\u5b89\u9759\u8fc7\u8fd9\u4e00\u751f\uff0c\u8fd8\u6709\u4ec0\u4e48\u6bd4\u8fd9\u66f4\u8981\u7d27\u7684\uff1f\u201d\u82e5\u662f\u8ba9\u4ed6\u53bb\u8d74\u4e10\u5e2e\u5e2e\u4e3b\u7684\u7ea6 \u4f1a\uff0c\u90a3\u5c31\u6050\u6015\u66f4\u52a0\u4e0d\u5f97\u5b89\u5b81\u4e86\u3002\u201d\u6a80\u7fbd\u51b2\u5446\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u6562\u60c5\u4f60\u662f\u628a\u5979\u5f53\u4f5c\u7389 \u9762\u8dc3\u72d0\u5427\uff1f\u201c\u949f\u7075\u79c0\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77e5\u9053\u5979\u4e0d\u662f\u4e86\u3002\u4f46\u5947\u602a\uff0c\u5979\u4eec\u7684\u76f8\u8c8c\u5374\u662f\u5982\u6b64\u76f8\u4f3c\u3002\u5927\u54e5 \u54e5\uff0c\u4f60\u662f\u600e\u6837\u548c\u5979\u4ea4\u4e0a\u670b\u53cb\u7684\uff1f\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u5979\u548c\u8d6b\u8fde\u6e05\u6ce2\u672c\u662f\u59b9\u59b9\uff0c\u4f46\u59ca\u59b9\u4e8c\u4eba\u5374\u662f\u76f8\u8c8c\u76f8\u540c\uff0c\u5fc3\u6027\u4e0d\u540c\u7684\u3002\u55ef\uff0c\u8bf4 \u8d77\u6211\u600e\u4e48\u548c\u5979\u76f8\u8bc6\uff0c\u5012\u662f\u8bf4\u6765\u8bdd\u957f\u2014\u2014\u201d \u3000\u3000\u4ed6\u5fc3\u529b\u4ea4\u75b2\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u58f0\u97f3\u5df2\u662f\u5636\u54d1\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8981\u5206\u51e0\u6b21\u8bf4\u4e86\u3002 \u3000\u3000\u949f\u7075\u79c0\u5403\u4e00\u60ca\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u7684\u9762\u8272\u600e\u7684\u8fd9\u4e48\u96be\u770b\uff01\u65e2\u662f\u8bf4\u6765\u8bdd\u957f\uff0c\u4f60\u6b47\u6b47\u518d\u8bf4 \u5427\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5b9e\u5728\u652f\u6301\u4e0d\u4f4f\uff0c\u5f53\u4e0b\u4fbf\u5373\u76d8\u819d\u800c\u5750\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u4e5f\u6b47\u6b47\u5427\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u5750\u5728\u4ed6\u7684\u65c1\u8fb9\uff0c\u542c\u4ed6\u7684\u547c\u5438\u6e10\u89c1\u5747\u5300\uff0c\u8138\u8272\u4e5f\u6e10\u6e10\u6062\u590d\u7ea2\u6da6\uff0c\u77e5\u9053\u4ed6\u6b63\u9ed8\u8fd0\u7384 \u529f\uff0c\u5c06\u771f\u6c14\u5bfc\u5165\u4e39\u7530\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u54e5\u54e5\u5e38\u8bf4\u7684\u95ed\u5173\u7ec3\u529f\uff0c\u5230\u4e86\u7269\u6211\u4e24\u5fd8\u7684\u5883\u754c\uff0c\u662f\u89c6\u800c \u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u7684\u3002\u8fd9\u4e2a\u65f6\u523b\uff0c\u5fc5\u987b\u6709\u4eba\u9632\u62a4\uff0c\u6211\u53ef\u4e0d\u80fd\u5927\u610f\u7761\u7740\u4e86\u3002 \u3000\u3000\u4e00\u65b9\u9762\u662f\u4e3a\u4e86\u8981\u62a4\u536b\u6a80\u7fbd\u51b2\uff0c\u4e00\u65b9\u9762\u5979\u81ea\u5df1\u4e5f\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u662f\u4ee5\u867d\u7136\u75b2\u7d2f\u4e0d\u582a\uff0c\u4f46\u5374\u9759 \u4e0d\u4e0b\u6765\u3002 \u3000\u3000\u7ea2\u65e5\u897f\u6c89\uff0c\u6708\u4eae\u5f00\u59cb\u5347\u8d77\u6765\u4e86\u3002\u8352\u6797\u5bc2\u5bde\uff0c\u9759\u5f97\u4ee4\u4eba\u5fc3\u8df3\u3002\u949f\u7075\u79c0\u770b\u7740\u5728\u6708\u5149\u4e0b\u95ed\u76ee\u9759 \u5750\u7684\u6a80\u7fbd\u51b2\uff0c\u89c9\u65e2\u6709\u5e78\u798f\u7684\u611f\u89c9\uff0c\u53c8\u6709\u5bf9\u672a\u6765\u7684\u5fe7\u8651\u3002\u201c\u5927\u54e5\u54e5\u4e3a\u4e86\u6211\uff0c\u53d7\u7684\u82e6\u4e5f\u53d7\u591f\u4e86\uff0c \u8fd9\u4e00\u5e74\u6765\u4ed6\u56f0\u5904\u8352\u5c71\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u8bf4\u3002\u6211\u4e5f\u77e5\u9053\u4ed6\u96be\u53d7\u7684\u3002\u5982\u4eca\u4ed6\u6b66\u529f\u5df2\u7ecf\u6062\u590d\uff0c\u6211\u8fd8\u5e94\u8be5 \u675f\u7f1a\u4ed6\u5417\uff1f\u201d \u3000\u3000\u201c\u77e5\u6211\u8005\uff0c\u8c13\u6211\u5fc3\u5fe7\u3002\u4e0d\u77e5\u6211\u8005\uff0c\u8c13\u6211\u95ee\u6c42\u3002\u60a0\u60a0\u82cd\u5929\uff0c\u5f7c\u4f55\u4eba\u54c9\uff1f\u201d\u8fd9\u662f\u6a80\u7fbd\u51b2\u521a\u624d \u548c\u91d1\u8d85\u5cb3\u6fc0\u6597\u4e4b\u65f6\uff0c\u4e3a\u4e86\u53d1\u6cc4\u80f8\u4e2d\u7684\u90c1\u79ef\uff0c\u72c2\u541f\u7684\u8bd7\u6587\u3002\u6b64\u65f6\u949f\u7075\u79c0\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5728 \u5fc3\u4e2d\u9ed8\u5ff5\u8fd9\u51e0\u53e5\u53e4\u8bd7\u3002 \u3000\u3000\u5979\u77e5\u9053\uff0c\u5c3d\u7ba1\u6a80\u7fbd\u51b2\u8bf4\u662f\u201c\u770b\u7834\u7ea2\u5c18\u201d\uff0c\u4f46\u4ed6\u6240\u53d7\u7684\u59d4\u5c48\uff0c\u8fd8\u662f\u5728\u4ed6\u5fc3\u5e95\u76d8\u7ed3\u7684\u3002 \u201c\u554a\uff01\u6211\u600e\u80fd\u8fd9\u8981\u81ea\u79c1\uff0c\u90a3\u4f4d\u8d6b\u8fde\u59d1\u5a18\u7ed9\u4ed6\u5e26\u6765\u7684\u6d88\u606f\uff0c\u5373\u4f7f\u5bf9\u6211\u4e0d\u5229\uff0c\u6211\u4e5f\u5e94\u8be5\u544a\u8bc9\u4ed6 \u7684\u3002\u201d \u3000\u3000\u6b63\u5728\u5979\u5fc3\u4e71\u5982\u9ebb\u4e4b\u9645\uff0c\u5ffd\u547c\u5f97\u6797\u5b50\u91cc\u4f3c\u6709\u6c99\u6c99\u58f0\u54cd\uff0c\u4e00\u62ac\u5934\uff0c\u5ffd\u7136\u5c31\u770b\u89c1\u4e00\u4e2a\u4eba\u6251\u8fc7\u6765 \u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u7adf\u7136\u662f\u91d1\u8d85\u5cb3\u3002\u539f\u6765\u4ed6\u8f93\u5f97\u5f88\u4e0d\u670d\u6c14\uff0c\u6545\u6b64\u57cb\u4f0f\u6797\u95f4\uff0c\u770b\u89c1\u8d6b\u8fde\u6e05\u4e91\u8d70\u4e86\u4e4b\u540e\uff0c\u4fbf \u5373\u56de\u6765\u5077\u88ad\u3002\u4ed6\u76f8\u4fe1\u81ea\u5df1\u7684\u5224\u65ad\uff1a\u6a80\u7fbd\u51b2\u7684\u6b66\u529f\u7eb5\u7136\u6062\u590d\u3002\u4e5f\u7edd\u4e0d\u662f\u5728\u6b63\u5e38\u7684\u72b6\u6001\u4e0b\u6062\u590d \u7684\u3002\u53ea\u8981\u6a80\u7fbd\u51b2\u5c11\u4e86\u4e00\u4e2a\u8d6b\u8fde\u6e05\u4e91\u4f5c\u5e2e\u624b\uff0c\u4ed6\u5c31\u6709\u4fe1\u5fc3\u518d\u640f\u4e00\u6b21\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5927\u5468\u5929\u5410\u7eb3\u6cd5\uff0c\u628a\u771f\u6c14\u7f13\u7f13\u5bfc\u5165\u4e39\u7530\uff0c\u6b64\u65f6\u521a\u597d\u5230\u4e86\u5173\u952e\u65f6\u523b\u3002\u5728\u8fd9\u5173\u952e\u65f6\u523b\uff0c \u83ab\u8bf4\u4ed6\u662f\u95ed\u76ee\u6253\u5750\uff0c\u5373\u4f7f\u4ed6\u662f\u5f20\u5f00\u773c\u775b\uff0c\u6050\u6015\u4e5f\u662f\u89c6\u800c\u4e0d\u89c1\u3002\u542c\u800c\u4e0d\u95fb\u7684\u4e86\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u6697\u4e2d\u7aa5\u4f3a\uff0c\u4e00\u89c1\u65f6\u673a\u5df2\u5230\uff0c\u7acb\u5373\u5c31\u6251\u51fa\u6765\u3002 \u3000\u3000\u4e8b\u60c5\u6765\u5f97\u592a\u7a81\u7136\uff0c\u949f\u7075\u79c0\u65e0\u6687\u601d\u7d22\uff0c\u51e0\u4e4e\u50cf\u662f\u4e00\u79cd\u51fa\u672c\u80fd\u7684\u53cd\u5e94\uff0c\u7acb\u5373\u5148\u6251\u5728\u6a80\u7fbd\u51b2\u7684 \u8eab\u4e0a\uff0c\u7528\u81ea\u5df1\u7684\u8eab\u4f53\uff0c\u63a9\u62a4\u6a80\u7fbd\u51b2\u3002 \u3000\u3000\u5979\u6839\u672c\u6ca1\u6709\u60f3\u5230\u540e\u679c\uff0c\u4e0d\u8fc7\uff0c\u5373\u4f7f\u5979\u60f3\u5230\u4e86\u540e\u679c\uff0c\u5979\u4e5f\u4f1a\u8fd9\u6837\u505a\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u662f\u91d1\u56fd\u7687\u5e1d\u6240\u8981\u7684\u4eba\uff0c\u91d1\u8d85\u5cb3\u672c\u6765\u4e0d\u6562\u53d6\u4ed6\u6027\u547d\uff0c\u53ea\u662f\u60f3\u5236\u4f4f\u4ed6\u7684\u7a74\u9053\uff0c\u5c06\u4ed6\u8bdd \u64d2\u7684\u3002\u4f46\u949f\u7075\u79c0\u6251\u5728\u4ed6\u8eab\u4e0a\uff0c\u8fd9\u5c31\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u91d1\u8d85\u5cb3\u521a\u624d\u505a\u8feb\u5411\u5979\u8d54\u793c\uff0c\u5fc3\u4e2d\u4f59\u6124\u672a\u6d88\uff0c\u5982\u4f55\u8fd8\u4e0d\u4e58\u673a\u62a5\u590d\uff1f\u5f53\u4e0b\u7acb\u826f\u6539\u6293\u4e3a\u5288\uff0c\u4e00 \u638c\u5411\u5979\u5288\u4e0b\uff0c\u8fd9\u4e00\u638c\u800c\u4e14\u7528\u7684\u662f\u91cd\u624b\uff01 \u3000\u3000\u949f\u7075\u79c0\u62b1\u7740\u6a80\u7fbd\u7075\u51b2\u6eda\u8fc7\u4e00\u8fb9\uff0c\u8fde\u6700\u540e\u4e00\u5206\u6c14\u529b\u90fd\u6d88\u5931\u4e86\u3002\u5979\u8f6f\u7ef5\u7ef5\u7684\u677e\u5f00\u53cc\u624b\uff0c\u5012\u5728 \u5730\u4e0a\u3002 \u3000\u3000\u5979\u4fdd\u4f4f\u4e86\u6a80\u7fbd\u51b2\u514d\u4e8e\u53d7\u8fb1\u3002\u4f46\u5979\u4ed8\u51fa\u7684\u4ee3\u4ef7\u5374\u662f\u81ea\u5df1\u7684\u6027\u547d\uff01 \u3000\u3000\u8fd9\u4e00\u638c\u7684\u529b\u9053\u5979\u627f\u53d7\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u5269\u4e0b\u7684\u90a3\u4e24\u4e09\u5206\u529b\u9053\u5df2\u662f\u4e0d\u8db3\u4f24\u5bb3\u6a80\u7fbd\u51b2\u4e86\u3002\u53ea\u80fd\u4ee4 \u6a80\u7fbd\u51b2\u60ca\u9192\u8fc7\u6765\uff0c\u5979\u7ed9\u6a80\u7fbd\u51b2\u4e89\u53d6\u4e86\u7247\u523b\u7684\u65f6\u95f4\uff0c\u800c\u8fd9\u7247\u523b\u7684\u65f6\u95f4\uff0c\u6b63\u597d\u8fc7\u4e86\u6a80\u7fbd\u51b2\u9ed8\u8fd0\u7384 \u529f\u7684\u5173\u952e\u65f6\u523b\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u4e00\u8dc3\u800c\u8d77\uff0c\u6325\u62f3\u6253\u51fa\u3002\u4e24\u80a1\u638c\u98ce\u78b0\u5728\u4e00\u8d77\uff0c\u91d1\u8d85\u5cb3\u8017\u635f\u7684\u771f\u6c14\u8fd8\u672a\u8865\u8db3\u3001\u6b64\u6d88\u5f7c \u957f\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u654c\u4e0d\u8fc7\u64c5\u7fbd\u51b2\u4e86\u3002\u6a80\u7fbd\u51b2\u538b\u4e0b\u4ed6\u7684\u638c\u98ce\uff0c\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\u822c\u6d8c\u6765\uff0c\u91d1\u8d85\u5cb3 \u7684\u808b\u9aa8\u767b\u65f6\u7ed9\u6253\u65ad\u4e24\u6839\uff0c\u4ed6\u8fd9\u624d\u77e5\u9053\u662f\u771f\u7684\u6253\u4e0d\u8fc7\u6a80\u7fbd\u51b2\u4e86\u3002\u6697\u7b97\u4e0d\u6210\uff0c\u53e3\u55b7\u9c9c\u8840\uff0c\u53ea\u597d\u5954 \u9003\u3002 \u3000\u3000\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u9192\u9192\uff0c\u9192\u9192\u5440\uff01\u201d\u6a80\u7fbd\u51b2\u62b1\u8d77\u949f\u7075\u79c0\uff0c\u8ba9\u5979\u9760\u7740\u81ea\u5df1\u7684\u80a9\u818a\uff0c \u638c\u5fc3\u8d34\u7740\u5979\u7684\u80cc\u5fc3\uff0c\u771f\u6c14\u8f93\u5165\u5979\u7684\u4f53\u5185\u3002 \u3000\u3000\u949f\u7075\u79c0\u7f13\u7f13\u5f20\u5f00\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u5fc5\u987b\u544a\u8bc9\u4f60\u2026\u2026\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u522b\u5fd9\u8bf4\u8bdd\uff01\u201d\u4f46\u949f\u7075\u79c0\u8fd8\u662f\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff1a\u201c\u4e10\u5e2e\u7684\u5c1a\u5e2e\u4e3b\u5df2\u7ecf\u77e5\u9053\u4f60\u53d7\u7684 \u51a4\u5c48\uff0c\u4ed6\u60f3\u8981\u89c1\u4f60\uff0c\u4ed6\u3001\u4ed6\u73b0\u5728\u6850\u67cf\u5c71\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u771f\u6c14\u8f93\u5165\u5979\u7684\u4f53\u5185\uff0c\u5df2\u7ecf\u53d1\u89c9\u5979\u53d7\u4f24\u4e4b\u91cd\u8fdc\u8fdc\u8d85\u51fa\u81ea\u5df1\u7684\u4f30\u8ba1\u3002\u6b64\u8d77\u5979\u4e0a\u6b21\u5728\u5343 \u4e91\u5e84\u6240\u53d7\u7684\u4f24\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\uff0c\u4e0a\u4e00\u6b21\u4ed6\u662f\u6551\u5f97\u4e86\u5979\uff0c\u4f46\u8fd9\u4e00\u6b21\u3001\u8fd9\u4e00\u6b21\u2014\u2014\u4ed6\u4e0d\u6562\u60f3\u4e0b\u53bb\uff0c \u53ea\u80fd\u5b58\u4e2a\u4e07\u4e00\u7684\u5e0c\u671b\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u53ea\u597d\u67d4\u58f0\u54c4\u5979\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u54b1\u4eec\u8bf4\u597d\u4e86\u7ec8\u8001\u6b64\u5c71\u7684\u3002\u6211\u4e0d\u60f3\u4e0b\u5c71\uff0c\u6211\u4e5f\u4e0d\u8981\u53bb\u89c1 \u4ec0\u4e48\u4e10\u5e2e\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u554a\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5f53\u521d\u662f\u54c4\u6211\u7684\u5462\uff0c\u539f\u6765\u4f60\u662f\u5f53\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u8bf4\u8fc7\u5047\u8bdd\u3002\u201d\u5176\u5b9e\u4ed6\u662f\u5e26\u7740\u6b49\u759a\u7684\u5fc3\u60c5\u8bf4\u8fd9\u53e5\u8bdd\u7684\u3002\u8981\u77e5\u5f53\u521d \u4ed6\u8bf4\u90a3\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u867d\u7136\u4e0d\u662f\u5b58\u5fc3\u54c4\u9a97\uff0c\u4fbf\u5374\u662f\u5728\u62b1\u7740\u81ea\u66b4\u81ea\u5f03\u7684\u5fc3\u60c5\u4e0b\u8bf4\u7684\u3002\u90a3\u65f6\u4ed6\u6839\u672c \u4e0d\u60f3\u5230\u81ea\u5df1\u8fd8\u80fd\u6062\u590d\u6b66\u529f\uff0c\u5f53\u7136\u662f\u4e50\u5f97\u7b54\u5e94\u548c\u949f\u7075\u79c0\u201c\u7ec8\u8001\u6b64\u5c71\u201d\u4e86\u3002 \u3000\u3000\u4ed6\u6000\u7740\u6b49\u759a\u7684\u5fc3\u60c5\uff0c\u671b\u7740\u5944\u5944\u4e00\u606f\u7684\u949f\u7075\u79c0\u3002\u5979\u7684\u751f\u547d\u6b63\u5728\u6e10\u6e10\u6d88\u901d\uff0c\u4f46\u8138\u4e0a\u5374\u53cd\u800c\u6700 \u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u542c\u89c1\u4ed6\u7684\u90a3\u53e5\u8bdd\u800c\u8868\u73b0\u51fa\u6765\u7684\u6b23\u60a6\u3002\u5c31\u50cf\u67af\u840e\u7684\u82b1\u6735\u5f97\u5230\u6700\u540e\u4e00 \u6ef4\u9732\u6c34\u6ecb\u6da6\u4f3c\u7684\u3002 \u3000\u3000\u949f\u7075\u79c0\u9762\u4e0a\u73b0\u51fa\u7b11\u5bb9\uff0c\u58f0\u97f3\u5374\u662f\u66f4\u52a0\u5fae\u5f31\u4e86\uff1a\u201c\u5373\u4f7f\u4f60\u662f\u5f53\u771f\uff0c\u8fd9\u4e2a\u5730\u65b9\uff0c\u4f60\u4e5f\u662f\u4f4f\u4e0d \u4e0b\u53bb\u7684\u4e86\u3002\u5927\u54e5\u54e5\uff0c\u4f60\u542c\u6211\u2014\u2014\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u542c\u6211\u8bf4\u3001\u8fd9\u91cc\u4f4f\u4e0d\u4e0b\u53bb\uff0c\u54b1\u4eec\u8fd8\u53ef\u4ee5\u5230\u522b\u7684\u5730\u65b9\u3002\u91cd\u8981\u7684\u662f\u4eba\uff0c\u4e0d \u662f\u5730\u65b9\u3002\u8fd8\u8bb0\u5f97\u5417\uff0c\u2018\u54b1\u4eec\u6ce8\u5b9a\u4e86\u662f\u76f8\u4f9d\u4e3a\u547d\u7684\u2019\uff0c\u8fd9\u53e5\u8bdd\u4f60\u8bf4\u8fc7\uff0c\u6211\u4e5f\u8bf4\u8fc7\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u80fd\u548c\u4f60\u4f5c\u4f34\u4e86\uff0c\u5927\u54e5\u54e5\u54e5\u6211\u8981\u8d70\u5566\uff01\u201d\u6a80\u7fbd\u51b2\u5fd9\u628a\u4e00\u80a1\u771f\u6c14\u8f93\u5165 \u5979\u7684\u80cc\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u7b54\u5e94\u8fc7\u6211\uff0c\u4f60\u8981\u7167\u6599\u6211\u4e00\u751f\u7684\uff01\u4f60\u600e\u80fd\u8d70\uff1f\u4f60\u4e0d\u80fd\u8d70\uff01\u201d\u949f \u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u662f\u6ca1\u6cd5\u7167\u6599\u4f60\u4e86\u3002\u4f46\u6211\u60f3\u4f1a\u6709\u6bd4\u6211\u66f4\u597d\u7684\u4eba\u7167\u6599\u4f60\u7684\u3002\u201d\u6a80 \u7fbd\u51b2\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u522b\u80e1\u601d\u4e71\u60f3\uff0c\u5728\u6211\u7684\u5fc3\u76ee\u4e2d\uff0c\u4efb\u4f55\u4eba\u90fd\u66ff\u4ee3\u4e0d\u4e86\u4f60\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u522b\u50bb\u6c14\u3002\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u4f60\u4e0d\u662f\u4e5f\u66fe\u8bf4\u8fc7\u2018\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c \u6708\u6709\u9634\u6674\u5706\u7f3a\u2019\u7684\u5417\uff1f\u8fd9\u4e00\u5e74\u6765\uff0c\u6211\u548c\u4f60\u5728\u4e00\u8d77\uff0c\u8fd9\u662f\u6211\u4e4e\u751f\u4ece\u6ca1\u6709\u8fc7\u7684\u5feb\u4e50\u65e5\u5b50\uff0c\u4e0a\u5929\u7ed9 \u6211\u7684\u5df2\u7ecf\u592a\u591a\u4e86\u3002\u201d\u6a80\u7fbd\u51b2\u6cea\u76c8\u4e8e\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4f60\u771f\u597d\u3002\u53ef\u60dc\u6211\u5bf9\u4f60\u4e0d\u591f\u597d\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u5bf9\u6211\u6837\u6837\u90fd\u597d\uff0c\u5c31\u53ea\u6709\u4e00\u6837\u2014\u2014\u201d \u3000\u3000\u6853\u7fbd\u51b2\u9053\uff1a\u201c\u554a\uff0c\u4f60\u5feb\u8bf4\uff0e\u662f\u54ea\u4e00\u6837\uff1f\u201d\u4ed6\u662f\u62b1\u7740\u201c\u8865\u8fc7\u201d\u7684\u5fc3\u60f0\uff0c\u53ea\u8981\u949f\u7075\u79c0\u8bf4\u5f97\u51fa \u6765\uff0c\u4ed6\u5c31\u7518\u613f\u4e0d\u60dc\u4e00\u5207\u5b8c\u6210\u5979\u7684\u5fc3\u613f\u3002\u949f\u7075\u79c0\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u53eb\u4f60\u5927\u54e5\u54e5\uff0c\u4f46\u6211\u5374\u4e0d\u559c\u6b22\u4f60\u53eb \u6211\u5c0f\u59b9\u5b50\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u66a8\u7136\u4e00\u7701\uff0c\u5fc3\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u8fd9\u53e5\u8bdd\u6211\u662f\u5e94\u8be5\u65e9\u5c31\u5bf9\u5979\u8bf4\u4e86\u3002\u201d\u4ed6\u4f4e\u4e0b\u4e86\u5934\uff0c\u5728\u949f \u7075\u79c0\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\u2014\u2014\u201d \u3000\u3000\u949f\u7075\u79c0\u4ecd\u7709\u5934\u6253\u7ed3\uff0c\u5fc3\u9053\uff1a\u201c\u53c8\u662f\u53eb\u6211\u5c0f\u59b9\u5b50\uff01\u201d\u4e0d\u8fc7\uff0c\u5979\u8fd8\u6765\u4e0d\u53ca\u6297\u8bae\uff0c\u53ea\u542c\u6a80\u7fbd\u51b2 \u90a3\u6e29\u67d4\u7684\u58f0\u97f3\u5df2\u5728\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\uff0c\u4eca\u540e\u6211\u4e0d\u4f1a\u518d\u53eb\u4f60\u5c0f\u59b9\u5b50\u4e86\uff0c\u4f60\u613f\u610f\u505a\u6211\u7684\u59bb\u5b50 \u4e48\uff1f\u201d \u3000\u3000\u8e59\u7709\u5f00\u5c55\uff0c\u7070\u6697\u7684\u773c\u73e0\u653e\u51fa\u5149\u4eae\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u4e5f\u6062\u590d\u4e86\u7b11\u5bb9\uff0c\u949f\u7075\u79c0\u559c\u6781\u800c\u6ce3\uff1a\u201c\u6211\u613f \u610f\uff01\u5927\u54e5\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u7b49\u5f85\u4f60\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5df2\u7ecf\u7b49\u5f85\u8bb8\u4e45\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f46\u4ee5\u524d\u7684\u6211\u662f\u4e2a\u50bb\u74dc\uff0c\u5b9e\u5728\u592a\u8fc7\u8f9c\u8d1f\u4e86\u4f60\u7684\u60c5\u610f\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u4e3a\u65f6\u672b\u665a\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u73b0\u5728\u4e5f\u8fd8\u4e3a\u65f6\u672b\u665a\uff0c\u6211\u7684\u5c0f\u3001\u5c0f\u59bb\u5b50\uff0c\u4f60\u8981\u575a\u5f3a\u5730\u6d3b\u4e0b\u53bb\uff0c\u54b1\u4eec\u4eca \u540e\u662f\u518d\u4e5f\u4e0d\u5206\u5f00\u4e86\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u597d\u54e5\u54e5\uff0c\u4f60\u522b\u592a\u50bb\uff0c\u5929\u4e0b\u662f\u6ca1\u6709\u4e0d\u6563\u7684\u7b75\u5e2d\u7684\u3002\u4e0d\u9519\uff0c\u6211\u662f\u6c38\u8fdc\u4e0d\u4f1a\u79bb\u5f00 \u4f60\u7684\uff0c\u4f46\u6211\u7684\u8eaf\u58f3\u662f\u4e0d\u80fd\u7559\u5728\u4e16\u4e0a\u966a\u4f34\u4f60\u4e86\u3002\u597d\u54e5\u54e5\uff0c\u8bf7\u4f60\u542c\u6211\u6700\u540e\u4e00\u53e5\u8bdd\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u542c\uff01\u201d\u62b1\u8d77\u5979\u6df1\u6df1\u7684\u543b\u4e86\u53bb\u3002\u949f\u7075\u79c0\u597d\u50cf\u89e6\u7535\u4f3c\u7684\u4ed6\u7684\u6000\u4e2d\u6296\u98a4\uff0c \u6a80\u7fbd\u51b2\u4ece\u5979\u7684\u5507\u611f\u89c9\u5f97\u5230\u5979\u7684\u5fc3\u623f\u8df3\u52a8\uff0c\u554a\uff01\u90a3\u5f3a\u70c8\u7684\u53cd\u5e94\uff0c\u4e0d\u5c31\u6b63\u662f\u5fc3\u623f\u8d2e\u6ee1\u4e86\u66f4\u6e05\u6240\u53d1 \u51fa\u7684\u51b2\u51fb\u4e48\uff1f\u5509\uff0c\u4f46\u4e0d\u5bf9\u5440\uff0c\u4e0d\u5bf9\uff01\u4ed6\u5ffd\u7136\u611f\u89c9\u5230\u90a3\u4e24\u7247\u7ea2\u5507\u6e10\u6e10\u51b0\u51b7\u4e86\u3002 \u3000\u3000\u795e\u8bdd\u4e2d\u6709\u738b\u5b50\u7684\u4e00\u543b\u53ef\u4ee5\u4ee4\u4e2d\u4e86\u9b54\u6cd5\u7684\u516c\u4e3b\u8d77\u6b7b\u56de\u751f\uff0c\u4f46\u53ef\u60dc\u8fd9\u79cd\u7f8e\u4e3d\u7684\u6545\u4e8b\u53ea\u80fd\u89c1\u4e8e \u795e\u8bdd\u3002\u6a80\u7fbd\u51b2\u8fd9\u6df1\u60c5\u4e00\u543b\uff0c\u5374\u5e76\u4e0d\u80fd\u4ee4\u5782\u5371\u7684\u949f\u7075\u79c0\u6062\u590d\u751f\u673a\u3002\u6a80\u7fbd\u51b2\u611f\u89c9\u5f97\u5230\u5979\u7684\u5634\u5507\u5f00 \u9616\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\u3002\u53ea\u597d\u628a\u81ea\u5df1\u7684\u8033\u6735\u66ff\u4ee3\u5634\u5507\uff1b\u8d34\u7740\u5979\u7684\u5634\u5507 \u3000\u3000\u949f\u7075\u79c0\u7684\u58f0\u97f3\u6709\u6c14\u8bbe\u529b\uff0c\u4f46\u8fd8\u662f\u542c\u5f97\u6e05\u695a\uff1a\u201c\u597d\u54e5\u54e5\uff0c\u4f60\u627f\u8ba4\u6211\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u5c31\u8be5\u542c\u6211 \u7684\u8fd9\u53e5\u8bdd\uff0c\u4f60\uff0c\u4f60\u662f\u5e94\u8be5\u53bb\u8d74\u4e10\u5e2e\u5e2e\u4e3b\u7684\u7ea6\u4f1a\u7684\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u8981\u7559\u4e0b\u6765\u966a\u4f60\u3002\u8981\u4e48\uff0c\u9664\u975e\u662f\u54b1\u4eec\u4e00\u540c\u53bb\uff0c\u6211\u4e0d\u4f1a\u5355\u72ec\u53bb\u7684\u3002\u201d \u3000\u3000\u949f\u7075\u79c0\u9053\uff1a\u201c\u8bf7\u6055\u6211\u4e0d\u80fd\u966a\u4f60\u53bb\u4e86\u3002\u4f60\u5df2\u7ecf\u966a\u4e86\u6211\u4e00\u5e74\uff0c\u6211\u771f\u7684\u662f\u5fc3\u6ee1\u610f\u8db3\u4e86\uff0c\u5e76\u65e0\u9057 \u61be\u4e86\u3002\u597d\u54e5\u54e5\uff0c\u4f60\u518d\u53eb\u6211\u4e00\u58f0\u597d\u59bb\u5b50\u5427\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u542b\u7740\u773c\u6cea\uff0c\u5fcd\u7740\u60b2\u75db\uff0c\u67d4\u58f0\u53eb\u9053\uff1a\u201c\u597d\u59bb\u5b50\uff01\u201d \u3000\u3000\u82cd\u767d\u7684\u8138\u4e0a\u7efd\u5f00\u9c9c\u82b1\uff0c\u949f\u7075\u79c0\u7684\u58f0\u97f3\u50cf\u662f\u4ece\u82b1\u4e1b\u4e2d\u5439\u8fc7\u6765\u7684\u6625\u65e5\u5fae\u98ce\u3002\u201c\u597d\u54e5\u54e5\uff0c\u554a\uff0c \u6211\u597d\u5feb\u4e50\uff01\u771f\u7684\uff0c\u6211\u597d\u5feb\u4e50\uff0c\u597d\u5feb\u4e50\uff0c\u5feb\u4e50\u2026\u201d \u3000\u3000\u5fae\u98ce\u6d88\u901d\uff0c\u949f\u7075\u79c0\u7684\u751f\u547d\u4ea6\u5df2\u968f\u98ce\u800c\u901d\u3002 \u3000\u3000\u201c\u6211\u7684\u597d\u59bb\u5b50\uff01\u597d\u59bb\u5b50\uff01\u597d\u59bb\u5b50\uff01\u201d\u6a80\u7fbd\u51b2\u518d\u4e09\u547c\u5524\uff0c\u5df2\u662f\u542c\u4e0d\u5230\u5979\u7684\u56de\u7b54\u4e86\u3002 \u3000\u3000\u201c\u9999\u6d88\u7389\u6b92\uff0c\u9057\u72b9\u6e29\u3002\u201d\u6a80\u7fbd\u51b2\u62b1\u7740\u8fd9\u4e2a\u66fe\u7ecf\u4e0e\u4ed6\u671d\u5915\u76f8\u5904\u7684\u201c\u5c0f\u59b9\u5b50\u201d\uff0c\u4f46\u611f\u5230\u5929\u8f6c \u65cb\uff0c\u6b32\u54ed\u65e0\u6cea\u3002 \u3000\u3000\u5929\u8fb9\u6302\u7740\u4e00\u5f2f\u7709\u6708\uff0c\u5374\u88ab\u72c2\u98ce\u5439\u6765\u7684\u4e00\u7247\u4e4c\u4e91\u63a9\u76d6\u4e86\u3002\u4e4c\u4e91\u672a\u6563\uff0c\u5ffd\u5730\u53c8\u6709\u4e86\u8000\u76ee\u7684\u5149 \u8292\u3002\u8fd9\u662f\u5929\u8fb9\u95ea\u8fc7\u7684\u4e00\u9897\u6d41\u661f\uff0c\u554a\uff0c\u8fd9\u662f\u591a\u4e48\u8000\u773c\u7684\u6d41\u661f\uff0c\u4f46\u53ef\u60dc\u4e5f\u662f\u4e00\u95ea\u5373\u901d\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u5fc3\u5934\u7ede\u75db\uff0c\u4f4e\u4e0b\u5934\u8f7b\u543b\u949f\u7075\u79c0\u90a3\u5df2\u7ecf\u51b0\u51b7\u7684\u7ea2\u5507\u3002 \u3000\u3000\u554a\uff0c\u5979\u8fd8\u53ea\u4e0d\u8fc7\u662f\u5341\u516b\u5c81\u7684\u5c11\u5973\u54ea\uff0c\u4e3a\u4ec0\u4e48\u751f\u547d\u5c31\u50cf\u6d41\u661f\u4e00\u6837\u77ed\u4fc3\uff1f \u3000\u3000\u6708\u4eae\u4ece\u4e4c\u4e91\u4e2d\u94bb\u51fa\u6765\u4e86\uff0c\u4f46\u53ef\u60dc\u5df2\u7ecf\u4e0d\u662f\u4e2d\u79cb\u524d\u90a3\u4e00\u665a\u7684\u90a3\u4e2a\u53c8\u5927\u53c8\u5706\u7684\u660e\u6708\u4e86\u3002 \u3000\u3000\u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be\u5168\uff01\u201d\u6a80\u7fbd\u51b2\u653e\u58f0\u72c2\u541f\uff0c\u773c\u6cea\u7ec8\u4e8e\u6dcc\u4e0b\u6765 \u4e86\uff01 \u3000\u3000\u4ed6\u6b63\u5728\u54ed\u5f97\u4f24\u5fc3\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u53ef\u7b11\u5440\u53ef\u7b11\uff01\u201d\u4e00\u4e2a\u719f\u6089\u7684\u5c11\u5973\u7684\u8138\u5b54\uff0c\u7a81\u7136\u51fa \u73b0\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4f3c\u7b11\u975e\u7b11\u7684\u6ce8\u89c6\u7740\u4ed6\u3002 \u3000\u3000\u662f\u8d6b\u8fde\u6e05\u6ce2\u8fd8\u662f\u8d6b\u8fde\u6e05\u4e91\uff1f \u3000\u3000\u6362\u4e86\u522b\u7684\u4eba\u4e5f\u8bb8\u96be\u4e8e\u5206\u8fa8\uff0c\u4f46\u4ed6\u5374\u662f\u7528\u4e0d\u7740\u770b\u5979\u7684\u8138\uff0c\u4e00\u542c\u5c31\u542c\u51fa\u6765\u4e86\u3002 \u3000\u3000\u7edd\u5bf9\u662f\u8d6b\u8fde\u6e05\u6ce2\uff0c\u53ea\u6709\u8d6b\u8fde\u6e05\u6ce2\u624d\u4f1a\u7528\u8fd9\u79cd\u53e3\u543b\u8bf4\u8bdd\u3001\u5728\u4ed6\u6700\u4f24\u5fc3\u7684\u65f6\u5019\u6765\u5632\u7b11\u4ed6\u3002 \u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u7b11\uff1f\u201d\u6a80\u7fbd\u51b2\u987e\u4e0d\u5f97\u62b9\u4e8e\u773c\u6cea\u3002\u8df3\u8d77\u6765\u5c31\u9a82\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4e0d\u614c\u5fd9\u4e0d\u5fd9\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f2a\u541b\u5b50\uff0c\u5047\u6148\u60b2\uff0c\u8fd9\u8fd8\u4e0d\u53ef\u7b11\u3002\u201d \u3000\u3000\u6709\u4ec0\u4e48\u4fae\u8fb1\u6bd4\u611f\u60c5\u53d7\u5230\u635f\u5bb3\u66f4\u52a0\u4e25\u91cd\uff1f\u6a80\u7fbd\u51b2\u6012\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u597d\u59bb\u5b50\uff0c\u4f60\u6562\u8bf4\u6211\u4e3a\u5979 \u6d41\u6cea\u90fd\u662f\u5047\u7684\u5417\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u53ea\u6015\u662f\u6cea\u771f\u60c5\u4e0d\u771f\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5bf9\u5979\u6ca1\u6709\u771f\u60c5\uff0c\u5bf9\u4f60\u6709\u771f\u60c5\u5417\uff1f\u4f60\u771f\u662f\u4e0d\u8981\u8138\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u522b\u5984 \u60f3\u6211\u4f1a\u7231\u4f60\uff0c\u6211\u7231\u7684\u53ea\u662f\u5979\uff01\u54fc\uff0c\u4f60\u53ef\u4ee5\u6b7b\u5fc3\u4e86\u5427\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u54ac\u7740\u5634\u5507\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u9a82\uff0c\u6211\u4e5f\u8981\u544a\u8bc9\u4f60\uff0c\u6211\u4e0d\u662f\u6765\u4e5e\u6c42\u4f60\u7684\u7231\u601c \u7684\uff01\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u90a3\u4f60\u6765\u4f5c\u4ec0\u4e48\uff1f\u96be\u9053\u662f\u4e3a\u4e86\u544a\u8bc9\u6211\u8fd9\u53e5\u8bdd\u53ef\u7b11\u7684\u8bdd\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u53ef\u7b11\uff01\u6211\u8fd8\u8981\u544a\u8bc9\u4f60\uff0c\u4f60\u662f\u81ea\u5df1\u5728\u9a97\u81ea\u5df1\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u54e6\uff0c\u6211\u600e\u6837\u5728\u9a97\u81ea\u5df1\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u949f\u7075\u79c0\u6b7b\u4e86\uff0c\u4f60\u4e3a\u5979\u75db\u54ed\uff0c\u4f60\u4ee5\u4e3a\u8fd9\u5c31\u662f\u8868\u793a\u4f60\u7231\u5730\u5417\uff1f\u8fd9\u53ea\u4e0d\u8fc7\u662f\u63a9 \u9970\u4f60\u826f\u5fc3\u7684\u4e0d\u5b89\u7f62\u4e86\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u4e0d\u7231\u5979\uff0c\u7231\u8c01\uff1f\u6211\u660e\u767d\u544a\u8bc9\u4f60\uff0c\u6211\u5bf9\u5979\u662f\u4e00\u7247\u771f\u60c5\uff0c\u5e76\u975e \u5982\u4f60\u8bf4\u7684\u53ea\u662f\u56e0\u4e3a\u5bf9\u4e0d\u4f4f\u5979\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4f60\u7231\u7684\u8c01\uff0c\u6216\u8bb8\u4f60\u8fd8\u672a\u66fe\u627e\u5230\u4f60\u771f\u6b63\u8981\u7231\u7684\u4eba\u3002\u6211\u4e5f \u76f8\u4fe1\u5979\u662f\u771f\u7684\u7231\u4f60\uff0c\u4f46\u7edd\u4e0d\u76f8\u4fe1\u4f60\u66fe\u7ecf\u4e3a\u5979\u8fd9\u6837\u4e00\u4e2a\u5c0f\u5973\u5b69\u52a8\u8fc7\u771f\u60c5\uff01\u4f60\u662f\u5728\u9a97\u5979\u3002\u4e5f\u662f\u5728 \u9a97\u81ea\u5df1\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u63a7\u5236\u4e0d\u4e86\u81ea\u5df1\uff0c\u4e00\u5df4\u638c\u5c31\u6253\u8fc7\u53bb\u3002\u6253\u4e86\u8d6b\u8fde\u6e05\u6ce2\u4e00\u8bb0\u6e05\u8106\u54cd\u4eae\u7684 \u8033\u5149\u3002 \u3000\u3000\u201c\u8c01\u8981\u4f60\u76f8\u4fe1\uff0c\u4f60\u7ed9\u6211\u6eda\uff01\u201d\u6a80\u7fbd\u51b2\u559d\u9053\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u6e05\u9192\u7684\u5f88\uff0c\u563f\u563f\uff0c\u4f60\u82e5\u4e0d\u662f\u7ed9\u6211\u8bf4\u4e2d\u5fc3\u75c5\uff0c\u4f55\u987b\u8fd9\u6837\u52a8\u6012\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9762\u8272\u94c1\u9752\uff0c\u559d\u9053\uff1a\u201c\u95ed\u5634\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7b11\u5f97\u66f4\u5a07\u5a9a\u4e86\uff1a\u201c\u4f60\u82e5\u662f\u4e00\u4e2a\u8c6a\u4e0d\u76f8\u5e72\u7684\u4eba\uff0c\u4f60\u7ba1\u4ed6\u80e1\u8bf4\u4ec0\u4e48\uff0c\u4f60\u90fd\u53ef\u4ee5\u4e00\u7b11 \u7f6e\u4e4b\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u6240\u4ee5\u4f60\u6253\u6211\u9a82\u6211\uff0c\u6211\u4e5f\u8fd8\u662f\u53ef\u4ee5\u539f\u8c05\u4f60\u7684\u3002\u201d \u3000\u3000\u64c5\u7fbd\u51b2\u7ed9\u5979\u6c14\u5f97\u557c\u7b11\u7686\u975e\uff0c\u559d\u9053\uff1a\u201c\u6ca1\u89c1\u8fc7\u4f60\u8fd9\u6837\u539a\u7751\u76ae\u7684\u4eba\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u8d76\u4f60\u4f60\u624d \u8d70\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u624d\u662f\u539a\u7751\u76ae\u5462\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201d\u6211\u600e\u6837\u539a\u8138\u76ae\u4e86\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u81ea\u4f5c\u591a\u60c5\uff0c\u8fd8\u4e0d\u662f\u539a\u8138\u76ae\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u7981\u4e0d\u4f4f\u53c8\u7ed9\u5979\u6c14\u5f97\u8df3\u5f97\u4e86\u8d77\u6765\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u662f\u6211\u81ea\u4f5c\u591a\u60c5\u8fd8\u662f\u4f60\u81ea\u4f5c\u591a\u60c5\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u81ea\u4f5c\u591a\u60c5\uff0c\u90a3\u5c31\u6b63\u662f\u4f60\u81ea\u4f5c\u591a\u60c5\uff01\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u6211\u662f\u56e0\u4f55\u800c \u6765\u7684\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u60f3\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u4e0d\u5bf9\u5427\uff1f\u6211\u770b\u4f60\u5fc3\u91cc\u60f3\u5f97\u5f88\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6012\u9053\uff1a\u201c\u4f60\u559c\u6b22\u8bf4\u5c31\u8bf4\uff0c\u4e0d\u559c\u6b22\u8bf4\u5c31\u8d70\u3002\u6211\u6ca1\u5de5\u592b\u8ddf\u4f60\u95f2\u78d5\u7259\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u9053\uff1a\u201c\u54df\u201c\u751f\u6c14\u5566\uff1f\u597d\uff0c\u90a3\u6211\u5c31\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\u3002\u67f3\u5143\u7532\u5df2\u7ecf\u77e5\u9053\u4f60\u662f\u8eb2\u5728\u8fd9\u91cc \u7684\u3002\u4ed6\u7ea6\u6211\u8054\u624b\u6765\u5bf9\u4ed8\u4f60\uff0c\u6211\u7279\u5730\u5148\u6765\u4e00\u6b65\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u6253\u4e86\u9ed1\u5403\u9ed1\u7684\u4e3b\u610f\u3002\u5982\u679c\u4f60\u662f\u5f53\u771f \u5982\u4ed6\u6240\u8bf4\u90a3\u6837\uff0c\u6b66\u529f\u5c1a\u672a\u6062\u590d\u7684\u8bdd\uff0c\u6211\u5c31\u628a\u4f60\u5148\u62a2\u4e86\u53bb\u3002\u4f46\u4f60\u522b\u8bef\u4f1a\uff0c\u6211\u662f\u8981\u628a\u6211\u4f60\u6349\u53bb\u9886 \u529f\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u906d\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u5766\u767d\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4ee5\u524d\u66fe\u7ecf\u4f5c\u8fc7\u670b\u53cb\uff0c\u5bf9\u670b\u53cb\u6211\u4e00\u5411\u4e0d\u8bf4\u5047\u8bdd\u3002\u73b0\u5728\u6211\u6253\u4e0d\u8fc7\u4f60\uff0c \u6240\u4ee5\u4f60\u4e0d\u8d76\u6211\uff0c\u6211\u4e5f\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u5979\u679c\u7136\u8bf4\u8d70\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u62b1\u7740\u949f\u7075\u79c0\u7684\u5c38\u4f53\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5979\u5f53\u771f\u662f\u4e3a\u4e86\u7ed9\u6211\u901a\u98ce\u62a5\u8baf\u624d\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u7684\u8bdd\u58f0\u4ece\u5c71\u5761\u4e0b\u9762\u4f20\u6765\uff1a\u201c\u4f60\u559c\u6b22\u626e\u6f14\u5927\u60c5\u4eba\u7684\u89d2\u8272\uff0c\u90a3\u4e5f\u5c3d\u53ef\u4ee5\u626e\u6f14\u4e0b\u53bb\u3002 \u4f46\u6211\u529d\u4f60\u8fd8\u4e0d\u4e0d\u8981\u81ea\u5df1\u6b3a\u9a97\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u4e3a\u4e86\u949f\u7075\u79c0\u4e4b\u6b7b\uff0c\u6a80\u7fbd\u51b2\u672c\u6765\u662f\u60b2\u75db\u4e4b\u6781\uff0c\u751a\u81f3\u51e0\u4e4e\u9677\u5165\u75af\u72c2\u72b6\u6001\u7684\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u7ecf\u8fc7\u8d6b\u8fde\u6e05\u6ce2\u8fd9\u4e48\u4e00\u95f9\uff0c\u8d1f\u8d1f\u76f8\u4e58\uff0c\u4ed6\u7684\u5fc3\u60c5\u53cd\u800c\u6062\u590d\u4e00\u4e9b\u51b7\u9759\u4e86\u3002 \u3000\u3000\u5047\u5982\u8d6b\u8fde\u6e05\u6ce2\u662f\u8dd1\u6765\u5b89\u6170\u4ed6\u7684\u8bdd\uff0c\u4e00\u5b9a\u6536\u4e0d\u5230\u8fd9\u6837\u597d\u7684\u6548\u679c\u3002\u4f46\u8d6b\u8fde\u6e05\u6ce2\u7684\u51b7\u5632\u70ed\u8bbd\uff0c \u5bf9\u4ed6\u6765\u8bf4\uff0c\u5374\u6709\u5982\u201c\u5f53\u5934\u68d2\u559d\u201d\u4e00\u822c\u3002 \u3000\u3000\u4ed6\u51b7\u9759\u4e0b\u6765\uff0c\u5fc3\u4e2d\u81ea\u95ee\uff1a\u201c\u6211\u662f\u4e0d\u662f\u5728\u6b3a\u9a97\u81ea\u5df1\uff1f\u6211\u7684\u4f24\u5fc3\u75db\u54ed\uff0c\u96be\u9053\u771f\u7684\u53ea\u662f\u4e3a\u4e86\u63a9 \u9970\u81ea\u5df1\u826f\u5fc3\u7684\u4e0d\u5b89\u5417\uff1f\u201d \u3000\u3000\u521a\u624d\u4e3a\u4e86\u8fd9\u4e24\u53e5\u201c\u4e0d\u4e2d\u542c\u201d\u7684\u8bf4\u8bdd\uff0c\u66fe\u7ecf\u6c14\u5f97\u8981\u6253\u8d6b\u8fde\u6e05\u6ce2\u7684\u8033\u5149\uff0c\u4f46\u73b0\u5728\u53cd\u8eac\u81ea\u95ee\uff0c \u4ed6\u7684\u5fc3\u5934\u5374\u662f\u4e0d\u89c9\u4e00\u7247\u832b\u7136\u4e86\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u4ed6\u5bf9\u949f\u7075\u79c0\u7684\u201c\u60c5\u201d\u662f\u771f\u7684\uff0c\u5e76\u4e0d\u662f\u505a\u7ed9\u522b\u4eba\u770b\u7684\uff0c\u4e5f\u4e0d\u662f\u7ed9\u81ea\u5df1\u770b\u7684\u3002\u4f46\u8fd9\u4e2a \u201c\u60c5\u201d\u662f\u592b\u59bb\u4e4b\u60c5\u8fd8\u662f\u5144\u59b9\u4e4b\u60c5\uff1f\u6216\u8005\u5373\u4f7f\u591a\u5c11\u63ba\u4e86\u4e00\u70b9\u5f02\u6027\u4e4b\u95f4\u7684\u90a3\u79cd\u7231\u6155\u4e4b\u60c5\uff0c\u4f46\u6050\u6015 \u4e5f\u8fd8\u672a\u66fe\u8fbe\u5230\u751f\u6b7b\u4e0d\u6e1d\u7684\u90a3\u79cd\u60c5\u5883\u754c\u5427\uff1f\u611f\u60c5\u4e0a\u7684\u4e8b\u6700\u96be\u5206\u6790\u7684\uff0c \u4f55\u51b5\u5f53\u5c40\u8005\u8ff7\uff0c\u81ea\u5df1\u53c8\u600e\u80fd\u6e05\u695a\u51c6\u786e\u5730\u7406\u89e3\u81ea\u5df1\u7684\u611f\u60c5\uff1f\u56e0\u4e4b\u4ed6\u66f4\u662f\u4e00\u7247\u832b\u7136\u4e86\u3002\u4e0d\u8fc7\uff0c\u6309 \u201c\u5c42\u6b21\u201d\u6765\u5206\uff0c\u201c\u832b\u7136\u201d\u5df2\u7ecf\u662f\u6bd4\u201c\u56fa\u6267\u201d\u6e05\u9192\u4e00\u70b9\u4e86\u3002 \u3000\u3000\u201c\u6e05\u6ce2\u5f53\u771f\u8981\u548c\u67f3\u5143\u7532\u8054\u624b\u6765\u5bf9\u4f8d\u6211\u7684\u5417\uff1f\u54fc\uff0c\u5979\u8bf4\u5047\u8bdd\u7684\u672c\u4e8b\u5012\u662f\u4e0d\u9519\uff01\u201d\u4ed6\u5e76\u4e0d\u76f8 \u4fe1\u8d6b\u8fde\u6e05\u6ce2\uff0c\u4ed6\u4e5f\u5e76\u4e0d\u8ba4\u4e3a\u4ed6\u4eec\u4e4b\u95f4\u53ef\u80fd\u4ea7\u751f\u4ec0\u4e48\u771f\u6b63\u7684\u53cb\u8c0a\u3002\u4f46\u6709\u4e00\u70b9\u4ed6\u662f\u76f8\u4fe1\u7684\uff0c\u8d6b\u8fde \u6e05\u6ce2\u4e0d\u4f1a\u4e58\u4ed6\u4e4b\u5371\u6765\u5bb3\u4ed6\u7684\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u7ee7\u7eed\u60f3\u9053\uff1a\u201c\u67f3\u5143\u7532\u5df2\u7ecf\u77e5\u9053\u6211\u7684\u884c\u85cf\uff0c\u4ed6\u8981\u6765\u8fd9\u91cc\u5bf9\u4ed8\u6211\uff0c\u8fd9\u624d\u6050\u6015\u771f\u7684 \u4e86\u3002\u201d\u4ed6\u7684\u8033\u8fb9\u597d\u50cf\u54cd\u8d77\u4e86\u8d6b\u8fde\u6e05\u6ce2\u7684\u5632\u8bbd\uff1a\u201c\u4f60\u8981\u8fd9\u91cc\u53d1\u75af\u5427\uff0c\u67f3\u5143\u7532\u53ef\u4e0d\u4f1a\u8ddf\u4f60\u53d1 \u75af\uff01\u201d \u3000\u3000\u949f\u7075\u79c0\u4e00\u6b7b\uff0c\u4ed6\u672c\u6765\u89c9\u5f97\u4e00\u5207\u90fd\u662f\u65e0\u5173\u7d27\u8981\u7684\u4e86\u3002\u4f46\u73b0\u5728\u9010\u6e10\u6062\u590d\u4e86\u6e05\u9192\uff0c\u4ed6\u5374\u4e0d\u7981\u832b \u7136\u81ea\u601d\uff1a\u201c\u5929\u5730\u4e4b\u5927\uff0c\u6211\u5c06\u4f55\u4e4b\u201d\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u4e5f\u5728\u60d8\u60d8\u524d\u884c\u3002\u5979\u5e76\u6ca1\u6709\u9047\u4e0a\u5979\u7684\u59ca\u59ca\u3002\u540e\u6765\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5979\u5b8c\u5168\u4e0d\u77e5\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u79bb\u5f00\u4e86\u6a80\u7fbd\u51b2\uff0c\u4f46\u773c\u524d\u8fd8\u51fa\u73b0\u7740\u6a80\u7fbd\u51b2\u548c\u949f\u7075\u79c0\u76f8\u4f9d\u76f8\u504e\u7684\u60c5\u666f\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u559c\u6b22\uff0c\u53c8\u662f\u6709\u70b9\u6005\u7136\u3002\u5509\u3001\u5979\u5fc3\u91cc\u5728\u60f3\u9053\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u5fc3\u91cc\u53c8\u662f\u559c\u6b22\uff0c\u53c8\u662f\u4f24\u611f\uff0c\u201c\u90a3\u4f4d\u59d1\u5a18\u5929\u771f\u65e0\u90aa\uff0c\u662f\u6bd4\u6211\u59d0\u59d0\u597d\u5f97\u591a\u4e86\u3002\u55ef\uff0c\u4e00\u4e2a\u4eba \u7684\u5e78\u798f\u4e0e\u5426\uff0c\u662f\u4f1a\u770b\u4ed6\u7684\u5fc3\u5883\u7684\uff0c\u6a80\u5927\u54e5\u6709\u949f\u59d1\u5a18\u4f5c\u4f34\uff0c\u53ea\u8981\u4ed6\u81ea\u5df1\u89c9\u5f97\u5e78\u798f\uff0c\u8eab\u5916\u7684\u8363\u8fb1 \u4e5f\u90fd\u662f\u65e0\u5173\u7d27\u8981\u7684\u4e86\u3002\u602a\u4e0d\u5f97\u90a3\u4f4d\u949f\u59d1\u5a18\u4ec7\u89c6\u6211\uff0c\u6211\u867d\u7136\u4e0d\u662f\u8981\u6765\u62a2\u593a\u5979\u7684\u60c5\u90ce\uff0c\u6211\u4e5f\u662f\u5fd2 \u5acc\u591a\u4e8b\u4e86\u3002\u201d\u5979\u5f53\u7136\u65e9\u5df2\u660e\u767d\u949f\u7075\u79c0\u9519\u628a\u5979\u5f53\u4f5c\u4e86\u5979\u7684\u59ca\u59ca\uff0c\u4f46\u5979\u7684\u4f24\u611f\u53c8\u5c82\u53ea\u4e3a\u4e86\u59d0\u59d0\u3002 \u3000\u3000\u5979\u53ef\u4e0d\u77e5\u5979\u7684\u59ca\u59b9\u4e5f\u6b63\u662f\u72ec\u884c\uff0c\u6bd4\u5979\u8fd8\u66f4\u4f24\u5fc3\u3001\u53ea\u4e0d\u8fc7\u5979\u4eec\u59d0\u59b9\u8d70\u7684\u662f\u4e0d\u540c\u7684\u65b9\u5411\u800c \u5df2\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4ece\u5317\u9762\u4e0b\u5c71\uff0c\u770b\u7740\u5c71\u4e0a\u6302\u4e0b\u6765\u7684\u7011\u5e03\uff0c\u5ffd\u7136\u72c2\u7b11\u8d77\u6765\uff0c\u201c\u771f\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d \u8bc6\u597d\u4eba\u5fc3\uff01\u54fc\uff0c\u4ed6\u6253\u6211\u9a82\u6211\uff0c\u5c82\u77e5\u6211\u662f\u672c\u6765\u8981\u5e2e\u4ed6\u7684\u3002\u201d \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u7684\u4f24\u5fc3\u5374\u53c8\u548c\u5979\u7684\u59b9\u59b9\u4e0d\u540c\uff0c\u5979\u770b\u7740\u5c71\u4e0a\u5012\u6302\u4e0b\u6765\u7684\u7011\u5e03\uff0c\u4e0d\u89c9\u634f\u7740\u62f3\u5934\u60f3 \u9053\uff1a\u201c\u6211\u53ef\u4ee5\u539f\u8c05\u4ed6\uff0c\u4f46\u8fd9\u8bb0\u8033\u5149\u6211\u662f\u4e0d\u80fd\u8ba9\u4ed6\u767d\u6253\u7684\u3002\u4ed6\u5bf9\u522b\u7684\u5973\u4eba\uff0c\u770b\u5f97\u6bd4\u6211\u66f4\u52a0\u91cd \u8981\uff0c\u6211\u4e5f\u7edd\u4e0d\u80fd\u5fcd\u53d7\u3002\u6211\u4e0d\u4e00\u5b9a\u8981\u5f97\u5230\u4ed6\u3002\u4f46\u6211\u4e00\u5b9a\u8981\u62a5\u590d\u4ed6\u5bf9\u6211\u7684\u9119\u89c6\u3002\u7011\u5e03\u4e3a\u8bc1\uff0c\u6211\u8981 \u50cf\u7011\u5e03\u4e00\u6837\uff0c\u628a\u963b\u62e6\u6211\u7684\uff0c\u5168\u90e8\u51b2\u6389\uff01\u201d \u3000\u3000\u5c71\u7684\u90a3\u8fb9\u4e5f\u6709\u7011\u5e03\uff0c\u8fd8\u6709\u4e00\u4e2a\u6c60\u6f6d\u3002\u7011\u5e03\u5954\u817e\uff0c\u6c60\u6f6d\u5374\u662f\u6c34\u5e73\u5982\u955c\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u662f\u548c\u4e09\u59b9\u6e05\u971e\u4e00\u8d77\u957f\u5927\u7684\uff0c\u5982\u4eca\u5df2\u7ecf\u540d\u9707\u6c5f\u6e56\u7684\u7b11\u6fc0\u4e7e\u5764\u534e\u8c37\u6db5\uff0c\u4ee5\u524d\u662f\u5979\u5bb6 \u4e2d\u7684\u5e38\u5ba2\u3002\u59b9\u59b9\u548c\u5979\u7684\u6027\u683c\u4e0d\u540c\uff0c\u5979\u662f\u4e2a\u6587\u9759\u7684\u59d1\u5a18\uff0c\u6709\u4e8b\u603b\u662f\u85cf\u5728\u5fc3\u91cc\uff0c\u4e0d\u8f7b\u6613\u5bf9\u5916\u4eba \u8bf4\u3002\u59b9\u59b9\u5374\u662f\u4e2a\u597d\u52a8\u7684\u5c0f\u6dd8\u6c14\uff0c\u559c\u6b22\u65b0\u5947\uff0c\u523a\u6fc0\uff0c\u987d\u76ae\u7684\u82b1\u6837\u767e\u51fa\u3002\u5979\u8bb0\u5f97\u534e\u8c37\u6db5\u66fe\u4f5c\u8fc7\u4e00 \u4e2a\u6bd4\u55bb\uff0c\u628a\u5979\u6bd4\u4f5c\u5e73\u9759\u7684\u6e56\u6c34\uff0c\u628a\u59b9\u59b9\u6bd4\u4f5c\u5954\u817e\u4e0d\u80fd\u81ea\u4f11\u7684\u7011\u5e03\u3002 \u3000\u3000\u4ece\u59b9\u59b9\u7684\u8eab\u4e0a\u5979\u5ffd\u7136\u60f3\u5230\u4e86\u59d0\u59d0\u7684\u8eab\u4e0a\u4e86\u3002 \u3000\u3000\u5979\u867d\u7136\u662f\u53ea\u662f\u548c\u59d0\u59d0\u89c1\u4e86\u4e00\u6b21\u9762\uff0c\u4f46\u5df2\u6df1\u523b\u7684\u611f\u89c9\u5f97\u5230\u5979\u4eec\u59ca\u59b9\u4e4b\u95f4\u6027\u683c\u7684\u4e0d\u540c\u3002\u201c\u770b \u6765\u5012\u662f\u4e09\u59b9\u548c\u5927\u7ec4\u6bd4\u8f83\u76f8\u4f3c\uff0c\u5176\u5b9e\u534e\u5927\u54e5\u5e94\u8be5\u628a\u5927\u59d0\u6bd4\u7011\u5e03\u66f4\u52a0\u9002\u5408\u3002\u5373\u4f7f\u540c\u6837\u662f\u7011\u5e03\u5427\uff0c \u5728\u843d\u5230\u5730\u9762\u4e4b\u65f6\uff0c\u4e5f\u6709\u56e0\u4e3a\u6d41\u7ecf\u7684\u5730\u8d28\u4e0d\u540c\uff0c\u6709\u7684\u6df7\u6742\u4e86\u592a\u591a\u7684\u6ce5\u6c99\uff0c\u6709\u7684\u53ea\u662f\u5feb\u5e26\u7740\u5c11\u8bb8 \u6c99\u77f3\u7684\u6e05\u6d41\u6d4a\u8d28\u4e4b\u5206\u3001\u5927\u59d0\u548c\u4e09\u59b9\uff0c\u672c\u6765\u5c31\u662f\u751f\u957f\u5728\u4e0d\u540c\u7684\u73af\u5883\u554a\uff01\u201d \u3000\u3000\u5979\u53c8\u628a\u534e\u8c37\u6db5\u62ff\u6765\u548c\u6a80\u7fbd\u51b2\u76f8\u6bd4\uff0c\u89c9\u5f97\u8fd9\u4e24\u4e2a\u4eba\u7684\u6027\u683c\u4e5f\u9887\u6709\u76f8\u4f3c\u7684\u5730\u65b9\u3002\u534e\u8c37\u6db5\u7684\u662f \u51e0\u5206\u72c2\uff0c\u6a80\u7fbd\u51b2\u591a\u7684\u662f\u51e0\u5206\u50b2\u3002 \u3000\u3000\u5979\u53c8\u518d\u60f3\u9053\uff1a\u201c\u90a3\u4f4d\u59d1\u5a18\u7684\u6027\u683c\u5012\u4f3c\u662f\u5728\u6211\u548c\u4e09\u59b9\u4e4b\u95f4\u3002\u5979\u662f\u6e05\u6f88\u53ef\u4ee5\u89c1\u5e95\u7684\u6eaa\u6d41\uff0c\u6a80 \u7fbd\u51b2\u771f\u7684\u4f1a\u8ddf\u5979\u5f7c\u6b64\u503e\u5fc3\u76f8\u7231\u4e48\uff1f\u201d \u3000\u3000\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u53c8\u5ffd\u7136\u60f3\u8d77\u4e86\u5c0f\u65f6\u5019\u81ea\u8272\u7684\u201c\u53ef\u7b11\u5e7c\u7a1a\u201d\uff0c\u5341\u4e8c\u3001\u4e09\u5c81\u65f6\uff0c\u5979\u4e5f\u66fe\u7ecf\u4ee5\u4e3a \u81ea\u5df1\u662f\u201c\u6697\u604b\u201d\u4e0a\u534e\u8c37\u6db5\u7684\uff0c\u540e\u6765\u5979\u65b9\u59cb\u61c2\u5f97\u8fd9\u4e0d\u8fc7\u662f\u201c\u5c0f\u59b9\u59b9\u201d\u5bf9\u201c\u5927\u54e5\u54e5\u201d\u7684\u503e\u6155\u800c \u5df2\uff0c\u201c\u503e\u6155\u201d\u548c\u201c\u503e\u5fc3\u201d\u4e0d\u540c\u3002\u5979\u60f3\u5230\u201c\u90a3\u4f4d\u59d1\u5a18\u201d\u4e5f\u662f\u5728\u53eb\u6a80\u7fbd\u51b2\u505a\u201c\u5927\u54e5\u54e5\u201d\u4e0d\u89c9\u597d\u7b11 \u8d77\u6765\u4e86\u3002 \u3000\u3000\u4f46\u5979\u5728\u7b11\u8fc7\u4e4b\u540e\uff0c\u4e0d\u89c9\u53c8\u662f\u51b7\u7136\u81ea\u7701\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u597d\u50cf\u5df4\u671b\u4ed6\u4eec\u53ea\u662f\u5144\u59b9\u4e4b\u60c5\u5462\uff1f\u5e84\u5b50 \u8bf4\u5b50\u975e\u9c7c\u7109\u77e5\u9c7c\u4e4b\u4e50\uff0c\u6211\u4e0d\u662f\u90a3\u4f4d\u949f\u59d1\u5a18\uff0c\u4e5f\u4e0d\u662f\u6a80\u7fbd\u51b2\uff0c\u53c8\u600e\u77e5\u4ed6\u4eec\u4e4b\u95f4\u6ca1\u6709\u5df2\u7ecf\u662f\u53ef\u4ee5 \u767d\u5934\u76f8\u8bb8\u7684\u771f\u60c5\uff1f\u201d\u60f3\u8d77\u81ea\u5df1\u201c\u4e00\u53a2\u60c5\u613f\u201d\u7684\u60f3\u6cd5\uff0c\u5373\u4f7f\u4e0d\u662f\u201c\u5e78\u707e\u4e50\u7978\u201d\u591a\u5c11\u4e5f\u662f\u6709\u70b9\u59a8 \u5fcc\u90a3\u4f4d\u949f\u59d1\u5a18\u5427\uff1f\u201c\u771f\u662f\u5439\u76b1\u4e00\u6c60\u6625\u6c34\u5e73\u537f\u5e95\u4e8b\uff1f\u4ed6\u4eec\u662f\u5144\u59b9\u4e4b\u60c5\u4e5f\u597d\uff0c\u662f\u7537\u5973\u4e4b\u60c5\u4e5f\u597d\uff0c \u6211\u53c8\u4f55\u5fc5\u53bb\u7ba1\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u7136\u51fa\u73b0\u5728\u5979\u7684\u9762\u524d\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u5e76\u4e0d\u60ca\u8be7\uff0c\u5e73\u9759\u5982\u540c\u6f6d\u6c34\u3002 \u3000\u3000\u949f\u7075\u79c0\u4e00\u6b7b\uff0c\u4ed6\u672c\u6765\u89c9\u5f97\u4e00\u5207\u90fd\u662f\u65e0\u5173\u7d27\u8981\u7684\u4e86\u3002\u4f46\u73b0\u5728\u9010\u6e10\u6062\u590d\u4e86\u6e05\u9192\uff0c\u4ed6\u5374\u4e0d\u7981\u832b \u7136\u81ea\u601d\uff1a\u201c\u5929\u5730\u4e4b\u5927\uff0c\u6211\u5c06\u4f55\u4e4b\u201d\u4e86\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u5c71\u5728\u60d8\u60d8\u524d\u884c\u3002\u5979\u5e76\u6ca1\u6709\u9047\u4e0a\u5979\u7684\u59ca\u59ca\u3002\u540e\u6765\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5979\u5b8c\u5168\u4e0d\u77e5\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u79bb\u5f00\u4e86\u6a80\u7fbd\u51b2\uff0c\u4f46\u773c\u524d\u8fd8\u51fa\u73b0\u7740\u6a80\u7fbd\u51b2\u548c\u949f\u7075\u79c0\u76f8\u4f9d\u76f8\u504e\u7684\u60c5\u666f\u3002 \u3000\u3000\u5979\u53c8\u662f\u559c\u6b22\uff0c\u53c8\u662f\u6709\u70b9\u6005\u7136\u3002\u5509\uff0c\u5979\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u5fc3\u91cc\u53c8\u662f\u559c\u6b22\uff0c\u53c8\u662f\u4f24\u611f\uff1a\u201c\u90a3\u4f4d\u949f\u59d1\u5a18\u5929\u771f\u65e0\u90aa\uff0c\u662f\u6bd4\u6211\u59d0\u59d0\u597d\u5f97\u591a\u4e86\u3002\u55ef\uff0c\u4e00\u4e2a \u4eba\u7684\u5e78\u798f\u4e0e\u5426\uff0c\u662f\u4f1a\u770b\u4ed6\u7684\u5fc3\u5883\u7684\uff0c\u6a80\u5927\u54e5\u6709\u949f\u59d1\u5a18\u4f5c\u4f34\uff0c\u53ea\u8981\u4ed6\u81ea\u5df1\u89c9\u5f97\u5e78\u798f\uff0c\u8eab\u5916\u7684\u8363 \u8fb1\u4e5f\u90fd\u662f\u65e0\u5173\u7d27\u8981\u7684\u4e86\u3002\u602a\u4e0d\u5f97\u90a3\u4f4d\u949f\u59d1\u5a18\u4ec7\u89c6\u6211\uff0c\u6211\u867d\u7136\u4e0d\u662f\u8981\u6765\u62a2\u593a\u5979\u7684\u60c5\u90ce\uff0c\u6211\u4e5f\u662f \u5fd2\u5acc\u591a\u4e8b\u4e86\u3002\u201d\u5979\u5f53\u7136\u65e9\u5df2\u660e\u767d\u949f\u7075\u79c0\u9519\u628a\u6211\u5f53\u4f5c\u4e86\u5979\u7684\u59ca\u59ca\uff0c\u4f46\u5979\u7684\u4f24\u611f\u53c8\u5c82\u53ea\u4e3a\u4e86\u59d0 \u59d0\u3002 \u3000\u3000\u5979\u53ef\u4e0d\u77e5\u5979\u7684\u59ca\u59ca\u4e5f\u6b63\u662f\u72ec\u884c\uff0c\u6bd4\u5979\u8fd8\u66f4\u4f24\u5fc3\u3002\u53ea\u4e0d\u8fc7\u5979\u4eec\u59d0\u59d0\u8d70\u7684\u662f\u4e0d\u540c\u7684\u65b9\u5411\u800c \u5df2\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u6ce2\u4ece\u5317\u9762\u4e0b\u5c71\uff0c\u770b\u7740\u5c71\u4e0a\u6302\u4e0b\u6765\u7684\u7011\u5e03\uff0c\u5ffd\u7136\u72c2\u7b11\u8d77\u6765\uff1a\u201c\u771f\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d \u8bc6\u597d\u4eba\u5fc3\uff01\u54fc\uff0c\u4ed6\u6253\u6211\u9a82\u6211\uff0c\u5c82\u77e5\u6211\u662f\u672c\u6765\u8981\u5e2e\u4ed6\u7684\u3002\u201d \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u7684\u4f24\u5fc3\u5374\u53c8\u548c\u5979\u7684\u59b9\u59b9\u4e0d\u540c\uff0c\u5979\u770b\u7740\u5c71\u4e0a\u5012\u6302\u4e0b\u6765\u7684\u7011\u5e03\uff0c\u4e0d\u89c9\u634f\u7740\u62f3\u5934\u60f3 \u9053\uff1a\u201c\u6211\u53ef\u4ee5\u539f\u8c05\u4ed6\uff0c\u4f46\u8fd9\u8bb0\u8033\u5149\u6211\u662f\u4e0d\u80fd\u8ba9\u4ed6\u81ea\u6253\u7684\u3002\u4ed6\u5bf9\u522b\u7684\u5973\u4eba\uff0c\u770b\u5f97\u6bd4\u6211\u66f4\u52a0\u91cd \u8981\uff0c\u6211\u4e5f\u7edd\u4e0d\u80fd\u5fcd\u53d7\u3002\u6211\u4e0d\u4e00\u5b9a\u8981\u5f97\u5230\u4ed6\uff0c\u4f46\u6211\u4e00\u5b9a\u8981\u62a5\u590d\u4ed6\u5bf9\u6211\u7684\u9119\u89c6\u3002\u7011\u5e03\u4e3a\u8bc1\uff0c\u6211\u8981 \u50cf\u7011\u5e03\u4e00\u6837\uff0c\u628a\u963b\u62e6\u6211\u7684\uff0c\u5168\u90e8\u51b2\u6389\uff01\u201d \u3000\u3000\u5c71\u7684\u90a3\u8fb9\u4e5f\u6709\u7011\u5e03\uff0c\u8fd8\u6709\u4e00\u4e2a\u6c60\u6f6d\u3002\u7011\u5e03\u5954\u817e\uff0c\u6c60\u6f6d\u5374\u662f\u6c34\u5e73\u5982\u955c\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u662f\u548c\u4e09\u59b9\u6e05\u971e\u4e00\u8d77\u957f\u5927\u7684\uff0c\u5982\u4eca\u5df2\u7ecf\u540d\u9707\u6c5f\u6e56\u7684\u7b11\u50b2\u4e7e\u5764\u534e\u8c37\u6db5\uff0c\u4ee5\u524d\u662f\u5979\u5bb6 \u4e2d\u7684\u5e38\u5ba2\u3002\u59b9\u59b9\u548c\u5979\u7684\u6027\u683c\u4e0d\u540c\uff0c\u5979\u662f\u4e2a\u6587\u9759\u7684\u59d1\u5a18\uff0c\u6709\u4e8b\u603b\u662f\u85cf\u5728\u5fc3\u91cc\uff0c\u4e0d\u8f7b\u6613\u5bf9\u5916\u4eba \u8bf4\u3002\u59b9\u59b9\u5374\u662f\u4e2a\u597d\u52a8\u7684\u5c0f\u6dd8\u6c14\uff0c\u559c\u6b22\u65b0\u5947\uff0c\u523a\u6fc0\uff0c\u987d\u76ae\u7684\u82b1\u6837\u767e\u51fa\u3002\u5979\u8bb0\u5f97\u534e\u8c37\u6db5\u66fe\u7ecf\u4f5c\u8fc7 \u4e00\u4e2a\u6bd4\u55bb\uff0c\u628a\u5979\u6bd4\u4f5c\u5e73\u9759\u7684\u6e56\u6c34\uff0c\u628a\u59b9\u59b9\u6bd4\u4f5c\u5954\u817e\u4e0d\u80fd\u81ea\u4f11\u7684\u7011\u5e03\u3002 \u3000\u3000\u4ece\u59b9\u59b9\u7684\u8eab\u4e0a\u5979\u5ffd\u7136\u60f3\u5230\u4e86\u59d0\u59d0\u7684\u8eab\u4e0a\u4e86\u3002 \u3000\u3000\u5979\u867d\u7136\u53ea\u662f\u548c\u59d0\u59d0\u89c1\u8fc7\u4e00\u6b21\u9762\uff0c\u4f46\u5df2\u6df1\u523b\u7684\u611f\u89c9\u5f97\u5230\u5979\u4eec\u59ca\u59b9\u4e4b\u95f4\u6027\u683c\u7684\u4e0d\u540c\u3002\u201c\u770b\u6765 \u5012\u662f\u4e8c\u59b9\u548c\u5927\u59d0\u6bd4\u8f83\u76f8\u4f3c\uff0c\u5176\u5b9e\u534e\u5927\u54e5\u5e94\u8be5\u628a\u5927\u59d0\u6bd4\u4f5c\u7011\u5e03\u66f4\u52a0\u9002\u5408\u3002\u5373\u4f7f\u540c\u6837\u662f\u7011\u5e03\u5427\uff0c \u5728\u843d\u5230\u5730\u9762\u4e4b\u65f6\uff0c\u4e5f\u6709\u56e0\u4e3a\u6d41\u7ecf\u7684\u5730\u8d28\u4e0d\u540c\uff0c\u6709\u7684\u6df7\u6742\u4e86\u592a\u591a\u7684\u6ce5\u6c99\uff0c\u6709\u7684\u53ea\u662f\u631f\u5e26\u7740\u5c11\u8bb8 \u6c99\u77f3\u7684\u6e05\u6d41\u6c99\u8d28\u4e4b\u5206\u3002\u5927\u59d0\u548c\u4e09\u59b9\uff0c\u672c\u6765\u5c31\u662f\u751f\u957f\u5728\u4e0d\u540c\u7684\u73af\u5883\u554a\uff01\u201d \u3000\u3000\u5979\u53c8\u628a\u534e\u8c37\u6db5\u62ff\u6765\u548c\u6a80\u7fbd\u51b2\u76f8\u6bd4\uff0c\u89c9\u5f97\u8fd9\u4e24\u4e2a\u4eba\u7684\u6027\u683c\u4e5f\u9887\u6709\u76f8\u4f3c\u7684\u5730\u65b9\u3002\u534e\u8c37\u6db5\u591a\u7684 \u662f\u51e0\u5206\u72c2\uff0c\u6a80\u7fbd\u51b2\u591a\u7684\u662f\u51e0\u5206\u50b2\u3002 \u3000\u3000\u5979\u53c8\u518d\u60f3\u9053\uff1a\u201c\u90a3\u4f4d\u949f\u59d1\u5a18\u7684\u6027\u683c\u5012\u4f3c\u662f\u5728\u6211\u548c\u4e09\u59b9\u4e4b\u95f4\u3002\u5979\u662f\u6e05\u6f88\u53ef\u4ee5\u89c1\u5e95\u7684\u6eaa\u6d41\uff0c \u6a80\u7fbd\u51b2\u771f\u7684\u4f1a\u8ddf\u5979\u5f7c\u6b64\u503e\u5fc3\u76f8\u7231\u4e48\uff1f\u201d \u3000\u3000\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u53c8\u5ffd\u7136\u60f3\u8d77\u4e86\u5c0f\u65f6\u5019\u81ea\u5df1\u7684\u201c\u53ef\u7b11\u5e7c\u7a1a\u201d\uff0c\u5341\u4e8c\u3001\u4e09\u5c81\u65f6\uff0c\u5979\u4e5f\u66fe\u7ecf\u4ee5\u4e3a \u81ea\u5df1\u662f\u201c\u6697\u604b\u201d\u4e0a\u534e\u8c37\u6db5\u7684\uff0c\u540e\u6765\u5979\u65b9\u59cb\u61c2\u5f97\u8fd9\u4e0d\u8fc7\u662f\u201c\u5c0f\u59b9\u59b9\u201d\u5bf9\u201c\u5927\u54e5\u54e5\u201d\u7684\u503e\u6155\u800c \u5df2\uff0c\u201c\u503e\u6155\u201d\u548c\u201c\u503e\u5fc3\u201d\u4e0d\u540c\u3002\u5979\u60f3\u5230\u201c\u90a3\u4f4d\u949f\u59d1\u5a18\u201d\u4e5f\u662f\u5728\u53eb\u6a80\u7fbd\u51b2\u505a\u201c\u5927\u54e5\u54e5\u201d\uff0c\u4e0d\u89c9 \u597d\u7b11\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4f46\u5979\u5728\u7b11\u8fc7\u4e4b\u540e\uff0c\u4e0d\u89c9\u53c8\u662f\u51b7\u7136\u81ea\u7701\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u597d\u50cf\u5df4\u671b\u4ed6\u4eec\u53ea\u662f\u5144\u59b9\u4e4b\u60c5\u5462\uff1f\u5e84\u5b50 \u8bf4\u5b50\u975e\u9c7c\u7109\u77e5\u9c7c\u4e4b\u4e50\uff0c\u6211\u4e0d\u662f\u90a3\u4f4d\u949f\u59d1\u5a18\uff0c\u4e5f\u4e0d\u662f\u6a80\u7fbd\u51b2\uff0c\u53c8\u600e\u77e5\u4ed6\u4eec\u4e4b\u95f4\u6ca1\u6709\u5df2\u7ecf\u662f\u53ef\u4ee5 \u767d\u5934\u76f8\u8bb8\u7684\u771f\u60c5\uff1f\u201d\u60f3\u8d77\u81ea\u5df1\u201c\u4e00\u53a2\u60c5\u613f\u201d\u7684\u60f3\u6cd5\uff0c\u5373\u4f7f\u4e0d\u662f\u201c\u5e78\u707e\u4e50\u7978\u201d\u591a\u5c11\u4e5f\u662f\u6709\u70b9\u5992 \u5fcc\u90a3\u4f4d\u949f\u59d1\u5a18\u5427\uff1f\u201c\u771f\u662f\u5439\u76b1\u4e00\u6c60\u6625\u6c34\u5e72\u537f\u5e95\u4e8b\uff1f\u4ed6\u4eec\u662f\u5144\u59b9\u4e4b\u60c5\u4e5f\u597d\uff0c\u662f\u7537\u5973\u4e4b\u60c5\u4e5f\u597d\uff0c \u6211\u53c8\u4f55\u5fc5\u53bb\u7ba1\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5ffd\u7136\u51fa\u73b0\u5728\u5979\u7684\u9762\u524d\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u5e76\u4e0d\u60ca\u8be7\uff0c\u5e73\u9759\u5982\u540c\u6f6d\u6c34\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4f1a\u51fa\u5c71\u7684\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u59b9\u5b50\u4e34\u7ec8\u5bf9\u6211\u7684\u671f\u671b\uff0c\u4f60\u53ef\u4ee5\u6307\u5f15\u6211\u53bb\u89c1\u5c1a\u5e2e\u4e3b\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u89c1\u5c1a\u5e2e\u4e3b\u987b\u5f85\u4e00\u5e74\uff0c\u56e0\u4e3a\u4ed6\u6ca1\u60f3\u5230\u4f60\u4f1a\u597d\u5f97\u8fd9\u6837\u5feb\uff0c\u4ed6\u662f\u51c6\u5907\u660e\u5e74\u624d\u5230\u83b1\u829c\u7b49\u4f60 \u7684\u3002\u4f46\u4f60\u53ef\u4ee5\u5148\u5230\u4e34\u5b89\uff0c\u89c1\u4e00\u89c1\u6c5f\u5357\u5927\u4fa0\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u94c1\u7b14\u4e66\u751f\u6587\u9038\u51e1\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u914d\u4e0d\u4e0a\u5927\u4fa0\u7684\u79f0\u53f7\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u6211\u53ea\u60f3\u77e5\u9053\u4e3a\u4f55\u4ed6\u8981\u89c1\u6211\uff1f\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u56e0\u4e3a\u5c1a\u5e2e\u4e3b\u4e5f\u6709\u4f20\u8bdd\u7ed9\u4ed6\u3002\u5176\u5b9e\u2014\u2014\u6bcb\u9700\u5c1a\u5e2e\u4e3b\u7684\u4f20\u8bf4\uff0c\u4ed6\u4ea6\u5df2\u77e5\u9053\u4f60 \u662f\u548c\u4ed6\u4e00\u6837\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5929\u662f\u516b\u6708\u521d\u4e09\uff0c\u8ddd\u79bb\u94b1\u5858\u6c5f\u6f6e\u795e\u7684\u751f\u65e5\u8fd8\u6709\u4e94\u5929\uff0c\u4f46\u98ce\u6d6a\u4e4b\u5927\uff0c\u5df2\u662f\u5f02\u4e4e\u5bfb\u5e38\u4e86\u3002\u4e00 \u6761\u5c0f\u821f\uff0c\u6b64\u65f6\u8d81\u7740\u65e9\u6f6e\u5df2\u8fc7\uff0c\u5348\u6f6e\u672a\u5230\u7684\u65f6\u5019\uff0c\u52a0\u901f\u524d\u8fdb\u3002\u8239\u4e0a\u6709\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u6b63 \u662f\u6a80\u7fbd\u51b2\u548c\u8d6b\u8fde\u6e05\u4e91\u3002\u4ed6\u4eec\u662f\u51c6\u5907\u5230\u4e34\u5b89\u53bb\u7684\u3002\u90a3\u6761\u5c0f\u8239\u662f\u4ed6\u4eec\u7528\u52a0\u500d\u7684\u94b1\u79df\u6765\u7684\uff0c\u4f46\u821f\u5b50 \u7684\u672c\u4e8b\u5374\u662f\u5bfb\u5e38\uff0c\u8fd8\u672a\u671b\u5230\u5cb8\uff0c\u5348\u6f6e\u5df2\u662f\u5f00\u59cb\u53d1\u52a8\u4e86\u3002\u821f\u5b50\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5ba2\u5b98\u5750\u7a33\uff0c\u6f6e\u5934\u5c31 \u8981\u6765\u4e86\u3002\u201d\u8d6b\u8fde\u6d88\u4e91\u5374\u4e0d\u80af\u6765\u5750\u8231\u4e2d\uff0c\u7ad9\u8d77\u6765\u770b\uff0c\u53ea\u89c1\u90a3\u6f6e\u6c34\u597d\u4f3c\u5339\u7ec3\u6a2a\u6c5f\uff0c\u6d8c\u5165\u94b1\u5858\u6c5f\u7684 \u5165\u53e3\u5904\uff0c\u8d5e\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4eba\u5bb6\u8bf4\u94b1\u5858\u6c5f\u89c2\u6f6e\u4e43\u662f\u4e00\u5927\u5947\u666f\uff0c\u679c\u662f\u58ee\u89c2\uff01\u201d\u6a80\u7fbd\u51b2\u84e6\u5730\u60f3\u8d77\u4e86 \u4e0e\u949f\u7075\u79c0\u540c\u6e21\u94b1\u5858\u6c5f\u7684\u5f80\u4e8b\uff0c\u90a3\u6b21\u662f\u949f\u7075\u79c0\u66ff\u4ed6\u628a\u8235\u7684\uff0c\u4e0d\u7531\u5f97\u4ed6\u7136\u795e\u4f24\u3002 \u3000\u3000\u8d6b\u8fde\u6d88\u4e91\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u6211\u5ff5\u4e00\u9996\u8bd7\u7ed9\u4f60\u542c\u3002\u201d\u8d6b\u8fde\u6e05\u4e91\u7b11\u9053\uff1a\u201c\u96be\u5f97\u4f60\u8fd8\u6709\u5174\u8da3\u5ff5\u8bd7\u3002\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8fd9\u9996\u8bd7\u662f\u548f\u6f6e\u795e\u751f\u65e5\u90a3\u5929\u7684\u94b1\u5858\u6f6e\u7684\u3002\u201d\u62ab\u895f\u8fce\u98ce\uff0c\u6717\u58f0\u5ff5\u9053\uff1a\u201c\u4e00\u75d5\u521d \u89c1\u6d77\u4e0a\u751f\uff0c\u9877\u523b\u957f\u9a71\u4f5c\u6012\u58f0\u3002\u4e07\u9a6c\u7a81\u56f4\u5929\u9f13\u788e\uff0c\u5929\u9ccc\u7ffb\u89c1\u4e91\u5c71\u503e\uff01\u201d\u541f\u58f0\u6fc0\u8d8a\uff0c\u6f8e\u6e43\u7684\u6d9b\u58f0 \u7adf\u4e5f\u63a9\u4e0d\u4f4f\u3002 \u3000\u3000\u541f\u7f62\uff0c\u6a80\u7fbd\u51b2\u6006\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u9996\u8bd7\u662f\u6211\u4e0a\u6b21\u6e21\u6c5f\u4e4b\u65f6\uff0c\u4e00\u4f4d\u670b\u53cb\u5728\u6211\u7684\u8033\u8fb9\u5ff5\u7ed9\u6211\u542c \u7684\uff0c\u53ef\u60dc\u5979\u5df2\u662f\u968f\u6c5f\u6f6e\u800c\u53bb\uff0c\u6c38\u4e0d\u56de\u5934\u4e86\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u77e5\u9053\u4ed6\u8bf4\u7684\u662f\u8c01\uff0c\u65e0\u8a00\u53ef\u4ee5\u6170\u89e3\uff0c\u552f\u6709\u7d27\u63e1\u4ed6\u7684\u624b\u4e86\u3002 \u3000\u3000\u821f\u5b50\u5ffd\u7136\u60ca\u547c\uff1a\u201c\u5feb\u5feb\u4f0f\u4e0b\uff0c\u8981\u649e\u8239\u4e86\uff01\u8001\u5929\u7237\u4fdd\u4f51\uff0c\u53ef\u522b\u8ba9\u5b83\u649e\u4e0a\uff01\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u522b\u6015\u3002\u201d\u63a5\u624b\u66ff\u4ed6\u638c\u8235\u3002\u6a80\u7fbd\u51b2\u9887\u611f\u610f\u5916\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u4e5f\u4f1a\u64cd\u821f\uff0c\u867d\u7136 \u6ca1\u6709\u5c0f\u59b9\u5b50\u90a3\u4e48\u7075\u6d3b\uff0c\u5374\u4f3c\u4e4e\u66f4\u7a33\u3002\u201d\u4ed6\u4e5f\u4f7f\u51fa\u4e86\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\uff0c\u52a9\u4e86\u8d6b\u8fde\u6e05\u4e91\u4e00\u81c2\u4e4b \u529b\u3002 \u3000\u3000\u201c\u9669\u6ee9\u5df2\u7ecf\u8fc7\u4e86\uff01\u201d\u8d6b\u8fde\u6e05\u4e91\u5fae\u7b11\u8bf4\u9053\u3002 \u3000\u3000\u679c\u7136\u4e0d\u8fc7\u4e00\u4f1a\u513f\uff0c\u8239\u5df2\u9760\u5cb8\u3002 \u3000\u3000\u5cb8\u4e0a\u4e00\u5927\u7fa4\u4eba\uff0c\u6709\u4ee5\u6587\u9038\u51e1\u4e3a\u9996\u7684\u6c5f\u5357\u4fa0\u4e49\u9053\uff0c\u4e5f\u6709\u4e10\u5e2e\u7684\u5211\u5802\u9999\u4e3b\u98ce\u706b\u9f99\u3002\u751a\u81f3\u8fd8\u6709 \u5f53\u5b98\u7684\u5357\u5bab\u9020\u548c\u6fee\u9633\u575a\u3002\u4e0d\u8fc7\u4ed6\u4eec\u662f\u4ee5\u6b66\u6797\u4e2d\u4eba\u7684\u8eab\u4efd\u4e0e\u4f1a\u7684\uff0c \u3000\u3000\u98ce\u706b\u9f99\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u597d\u7ec6\uff0c\u7adf\u6562\u91cd\u5230\u4e34\u5b89\uff0c\u6211\u662f\u7279\u5730\u8d76\u6765\u4f1a\u4f60\u7684\uff01\u201d\u4ed6\u5df2\u6253\u542c\u5230\u6587\u9009\u51e1 \u6709\u2018\u5bbd\u6055\u2019\u6a80\u7fbd\u51b2\u4e4b\u610f\uff0c\u662f\u4ee5\u9996\u5148\u53d1\u96be\uff0c\u7ed9\u4ed6\u6765\u4e2a\u4e0b\u9a6c\u5a01\u3002 \u3000\u3000\u201c\u4ed6\u662f\u5b8b\u56fd\u5fe0\u826f\u4e4b\u540e\uff0c\u4e0d\u662f\u91d1\u56fd\u5978\u5219\uff01\u201d\u4e0d\u77e5\u662f\u8c01\uff0c\u5728\u4eba\u4e1b\u4e2d\u53eb\u8d77\u6765\u3002 \u3000\u3000\u5357\u5bab\u9020\u51b7\u7b11\u9053\uff1a\u201c\u6a80\u8d1d\u5b50\uff0c\u4f60\u597d\u5440\uff01\u2026\u2026\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8d1d\u5b50\uff0c\u6211\u7684\u5802\u5144\u5f1f\u6a80\u4e16\u82f1\u624d\u662f\u8d1d\u5b50\uff0c\u4ed6\u6258\u6211\u95ee\u4f60\u95ee\u597d\uff01\u201d \u3000\u3000\u5357\u5bab\u9020\u6015\u4ed6\u6296\u51fa\u81ea\u5df1\u4e0e\u6a80\u4e16\u82f1\u540c\u8c0b\u4e4b\u4e8b\uff0c\u201c\u4e0b\u6587\u201d\u767b\u65f6\u88ab\u5207\u65ad\u4e86\u3002 \u3000\u3000\u6fee\u9633\u575a\u9053\uff1a\u201c\u6211\u4eec\u53ea\u77e5\u4ed6\u662f\u5168\u56fd\u8d1d\u5b50\uff0c\u8bf4\u4ed6\u662f\u5b8b\u56fd\u5fe0\u826f\u4e4b\u540e\u6709\u4f55\u51ed\u8bc1\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u8001\u6c49\u5ffd\u5730\u8d70\u4e0a\u6765\u9053\uff1a\u201c\u6a80\u5c11\u5e74\uff0c\u4f60\u7684\u5bb6\u4f20\u4e4b\u5b9d\u8fd8\u5728\u5417\uff1f\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u54ea\u6709\u4ec0\u4e48\u4f20\u5bb6\u4e4b\u5b9d\uff1f\u201d\u90a3\u8001\u6c49\u76ee\u5149\u70af\u70af\u7684\u671b\u7740\u4ed6\u9053\uff1a\u201c\u4f60 \u8fd8\u8bb0\u5f97\u4f60\u7684\u5f20\u7237\u7237\u5417\uff1f\u201d\u6a80\u7fbd\u51b2\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u6211\u5a18\u4eb2\u7684\u4e49\u7236\uff0c\u6211\u628a\u4ed6\u5f53\u6210\u4eb2\u5916\u516c \u4e00\u6837\uff0c\u600e\u80fd\u5fd8\u8bb0\uff1f\u201d\u90a3\u8001\u6c49\u9053\uff1a\u201c\u96be\u9053\u90a3\u4ef6\u5b9d\u8d1d\u4ed6\u6ca1\u6709\u4ea4\u7ed9\u4f60\u7684\u5a18\u4eb2\uff1f\u201d\u6a80\u7fbd\u51b2\u604d\u7136\u5927\u609f\uff0c \u6253\u5f00\u4e00\u4e2a\u9526\u5323\uff0c\u4ece\u9526\u5323\u4e2d\u62ff\u51fa\u4e00\u5f20\u8272\u6cfd\u5df2\u7ecf\u53d8\u9ec4\u7684\u7eb8\u5f20\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u662f\u8fd9\u4e2a\u5417\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e07\u5728\u8be7\u5f02\uff0c\u4e00\u5f20\u53d1\u4e86\u9709\u7684\u7eb8\u600e\u7684\u7adf\u662f\u4f20\u5bb6\u4e4b\u5b9d\uff1f\u53ea\u89c1\u90a3\u8001\u6c49\u5df2\u662f\u559c\u5f62\u4e8e\u8272\uff0c\u8bf4\u9053\uff1a \u201c\u6b63\u662f\u8fd9\u4e2a\uff0c\u8fd9\u662f\u5cb3\u5c11\u4fdd\u4eb2\u7b14\u5199\u7684\u6ee1\u6c5f\u7ea2\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u8001\u4f19\uff0c\u4f60\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u90a3\u8001\u6c49\u9053\uff1a\u201c\u5cb3\u5c11\u4fdd\u6709\u4e24\u540d\u5bb6\u5c06\uff0c\u9a6c\u524d\u5f20\u4fdd\uff0c\u9a6c\u540e\u738b\u6a2a\u3002\u4f60\u5988\u5988\u7684\u4e49\u7236\u5f20\u708e\u662f\u5f20\u4fdd\u4e4b \u5b50\uff0c\u6211\u7684\u5148\u7236\u6b63\u662f\u738b\u6a2a\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u626c\u8d77\u90a3\u5f20\u5cb3\u98de\u7684\u58a8\u5b9d\uff0c\u800c\u5bf9\u7fa4\u96c4\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d \u6a80\u5c11\u4fa0\u7684\u6bcd\u4eb2\u4e43\u662f\u5cb3\u5c11\u4fdd\u7684\u5916\u5b59\u5973\u513f\uff01\u201d\u7fa4\u96c4\u8c01\u4e0d\u5c0a\u656c\u5cb3\u98de\uff0c\u767b\u65f6\u90fd\u52b1\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u98ce\u706b\u9f99\u5ffd\u9053\uff1a\u201c\u8c01\u77e5\u9053\u662f\u771f\u662f\u5047\uff1f\u201d\u9a6c\u5927\u884c\u6295\u524d\u8bf4\u9053\uff1a\u201c\u5373\u4fbf\u662f\u771f\uff0c\u90a3\u53c8\u600e\u6837\uff1f\u5fe0\u826f\u4e4b \u540e\uff0c\u96be\u9053\u5c31\u6ca1\u574f\u4eba\uff1f\u201d\u4ed6\u662f\u4e10\u5e2e\u4e34\u5b89\u5206\u8236\u7684\u8235\u4e3b\uff0c\u98ce\u706b\u9f99\u6b63\u662f\u4ed6\u7684\u9760\u5c71\uff0c\u4ed6\u53c8\u66fe\u8d25\u4e8e\u6a80\u7fbd\u51b2 \u7684\u624b\u4e0b\uff0c\u9057\u6068\u672a\u6d88\u3002 \u3000\u3000\u6587\u9038\u51e1\u53f7\u79f0\u94c1\u7b14\u4e66\u751f\uff0c\u6700\u559c\u6b22\u6536\u96c6\u540d\u4eba\u4e66\u6cd5\uff0c\u4ed6\u4ece\u90a3\u8001\u6c49\u624b\u4e2d\u63a5\u8fc7\u90a3\u5f20\u8bcd\u7b3a\uff0c\u4e00\u770b\u5c31 \u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u6b63\u662f\u5cb3\u5c11\u4fdd\u7684\u771f\u8ff9\uff01\u201d\u4e0d\u89c9\u5c31\u624b\u811a\u8db3\u8e48\u6717\u5464\u8d77\u6765\uff1a\u201c\u6012\u53d1\u51b2\u51a0\uff0c\u51ed\u680f\u5904\uff0c \u6f47\u6f47\u96e8\u6b47\u2026\u2026\u201d\u4ed6\u89c1\u4e86\u4ed6\u6700\u5d07\u62dc\u7684\u540d\u5c06\u624b\u4e66\uff0c\u4e00\u65f6\u95ee\u5927\u559c\u82e5\u72c2\uff0c\u7adf\u987e\u4e0d\u5f97\u4e0e\u7fa4\u96c4\u8bf4\u201c\u6b63\u4e8b\u201d \u4e86\u3002 \u3000\u3000\u9a6c\u5929\u884c\u7684\u8bdd\u521a\u8bf4\u5b8c\uff0c\u6709\u4e09\u4e2a\u4eba\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u9519\u4e86\uff0c\u4ed6\u4e0d\u662f\u574f\u4eba\uff0c\u4ed6\u662f\u6211\u4eec\u7684\u670b \u53cb\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u58f0\u82e5\u6d2a\u949f\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u6a80\u5c11\u4fa0\u662f\u6211\u7684\u5927\u6069\u4eba\uff0c\u82e5\u4e0d\u662f\u4ed6\u820d\u8eab\u76f8\u6551\uff0c\u83ab\u8bf4\u6211\u7684\u91d1 \u5200\u63d0\u4e0d\u8d77\u6765\uff0c\u6211\u6050\u6015\u5df2\u7ecf\u53d8\u6210\u75af\u5b50\uff0c\u8fd9\u4e00\u751f\u90fd\u6bc1\u4e86\u3002\u201d \u3000\u3000\u5728\u4ed6\u8bf4\u5b8c\u672c\u8eab\u906d\u9047\u4e4b\u540e\uff0c\u5d14\u6d69\u3001\u77f3\u96f7\u548c\u7126\u633a\u7b49\u4eba\uff0c\u4e5f\u90fd\u8bf4\u51fa\u4ed6\u4eec\u53d7\u6a80\u7fbd\u51b2\u7684\u6069\u60e0\u3002 \u3000\u3000\u6587\u9038\u51e1\u9053\uff1a\u201c\u73b0\u5728\u5927\u5bb6\u53ef\u4ee5\u6e05\u695a\u4e86\u5427\uff0c\u6a80\u7fbd\u51b2\u867d\u7136\u662f\u534a\u4e2a\u91d1\u56fd\u4eba\uff0c\u5982\u679c\u4ed6\u613f\u610f\u7684\u8bdd\uff0c\u4ed6 \u8fd8\u53ef\u4ee5\u505a\u91d1\u56fd\u8d1d\u5b50\uff0c\u4f46\u4ed6\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u90fd\u662f\u54b1\u4eec\u7684\u540c\u9053\u3002\u201d \u3000\u3000\u98ce\u706b\u9f99\u9053\uff1a\u201c\u6a80\u7fbd\u51b2\u6551\u8fc7\u4f60\u4eec\u4fa0\u4e49\u9053\u4e2d\u7684\u4eba\u7269\uff0c\u4f46\u4ed6\u7684\u53cc\u624b\u4e5f\u66fe\u6cbe\u8fc7\u4f60\u4eec\u4fa0\u4e49\u9053\u7684\u9c9c \u8840\uff0c\u8fd9\u53c8\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u6c5f\u5357\u4fa0\u4e49\u9053\u7684\u76df\u4e3b\u6587\u5927\u4fa0\u5728\u6b64\uff0c\u7528\u4e0d\u7740\u4f60\u66ff\u4ed6\u7ba1\uff01\u201d \u3000\u3000\u98ce\u706b\u9f99\u9053\uff1a\u201c\u597d\uff0c\u4fa0\u4e49\u9053\u7684\u4e8b\u6211\u4e0d\u7ba1\uff0c\u4e10\u5e2e\u7684\u4e8b\u6211\u53ef\u4ee5\u7ba1\u5427\uff1f\u4ed6\u7ed3\u4ea4\u672c\u5e2e\u53db\u5f92\uff0c\u672c\u5e2e\u7684 \u6731\u957f\u8001\u67e5\u5f97\u5206\u660e\uff01\u201d \u3000\u3000\u8fdc\u5904\u5ffd\u5730\u6709\u4e2a\u58f0\u97f3\u4f20\u6765\uff1a\u201c\u4e10\u5e2e\u7684\u4e8b\u7531\u6211\u6765\u7ba1\uff01\u201d \u3000\u3000\u58f0\u5230\u4eba\u5230\uff0c\u6765\u7684\u662f\u65b0\u8fd1\u5347\u4efb\u4e10\u5e2e\u9996\u5e2d\u957f\u8001\u7684\u590f\u6e05\u5e73\u3002 \u3000\u3000\u590f\u6e05\u5e73\u9053\uff1a\u201c\u6731\u4e39\u9e64\u8bef\u4fe1\u8c23\u8a00\uff0c\u8d8a\u6743\u4f20\u4ee4\uff0c\u5c1a\u5e2e\u4e3b\u5df2\u7ecf\u67e5\u5f97\u6e05\u695a\uff0c\u6240\u4ee5\u624d\u8981\u6211\u66ff\u4ee3\u4ed6\u505a \u4e10\u5e2e\u7684\u9996\u5e2d\u957f\u8001\u3002\u201d\u5176\u5b9e\u6731\u4e39\u9e64\u4e4b\u7f6a\u4e0d\u6b62\u4e8e\u6b64\uff0c\u4e0d\u8fc7\u8fd8\u672a\u5230\u63ed\u53d1\u7684\u65f6\u5019\u7f62\u4e86\u3002\u4e0d\u8fc7\uff0c\u98ce\u706b\u9f99 \u542c\u5f97\u66f4\u6e05\u5e73\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e5f\u53ea\u80fd\u7070\u6e9c\u6e9c\u7684\u8d70\u4e86\u3002 \u3000\u3000\u98ce\u706b\u9f99\u8d70\u4e86\u4e4b\u540e\uff0c\u5b9c\u5174\u6b66\u5e08\u9093\u5927\u9b41\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4fa0\u4e49\u9053\u8bb2\u7a76\u7684\u662f\u6069\u6028\u5206\u660e\uff0c\u98ce\u706b\u9f99\u8bf4\u7684 \u90a3\u756a\u8bdd\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\u3002\u8bf7\u95ee\u6587\u5927\u4fa0\uff0c\u6a80\u7fbd\u51b2\u624b\u4e0a\u6240\u6cbe\u7684\u9c9c\u8840\uff0c\u662f\u5426\u5c31\u6b64\u4f5c\u7f62\uff1f\u201d\u539f\u6765\u4ed6\u6700 \u5fc3\u7231\u7684\u4e00\u4e2a\u5f92\u5f1f\u662f\u6b7b\u5728\u6a80\u7fbd\u51b2\u624b\u4e0b\u7684\u3002\u5218\u5929\u5316\u9053\uff1a\u201c\u51e1\u662f\u5e94\u4ece\u5927\u5904\u7740\u60f3\uff0c\u9093\u8001\u5927\uff0c\u4f60\u4e00\u5b9a\u8981 \u7b97\u5e10\u7684\u8bdd\uff0c\u6211\u66ff\u6a80\u7fbd\u51b2\u507f\u4ee4\u5f92\u6027\u547d\uff01\u201d\u9093\u5927\u9b41\u9053\uff1a\u201c\u5218\u5927\u4fa0\u6b64\u8a00\u5dee\u77e3\uff01\u6c5f\u6e56\u89c4\u77e9\u8bb2\u7a76\u7684\u4e00\u4eba \u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u4ed6\u6b20\u4e0b\u7684\u8840\u503a\u600e\u80fd\u7531\u4f60\u4ee3\u507f\uff1f\u201d\u6587\u9038\u51e1\u9053\uff1a\u201c\u597d\uff0c\u6211\u6765\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u5f53\u65e5\u628a\u6a80 \u7fbd\u51b2\u8bef\u5f53\u5978\u7ec6\uff0c\u662f\u7531\u6211\u9886\u5934\u8ffd\u6355\u4ed6\u7684\uff0c\u5728\u90a3\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4ed6\u4f24\u4e86\u54b1\u4eec\u513f\u4e2a\u4eba\uff0c\u4e5f\u662f\u60c5\u6709\u53ef \u539f\u2026\u2026\u201d \u3000\u3000\u9093\u5927\u9b41\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u6b62\u51e0\u4e2a\u5427\uff1f\u201d \u3000\u3000\u66fe\u53c2\u4e0e\u8ffd\u6355\u6a80\u7fbd\u51b2\u7684\u4fa0\u4e49\u9053\uff0c\u51e0\u4e4e\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u66fe\u6709\u8bb8\u591a\u4eba\u53d7\u4f24\uff0c\u4f46\u90a3\u662f\u7389\u9762\u957f \u72d0\u6240\u4e3a\uff0c\u4e0d\u5173\u6a80\u7fbd\u51b2\u7684\u4e8b\u3002\u201d\u8fde\u9a6c\u5929\u884c\u90fd\u968f\u58f0\u9644\u548c\u3002 \u3000\u3000\u9093\u5927\u9b41\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u662f\u53d7\u4f24\u3002\u6211\u7684\u5f92\u5f1f\u5374\u662f\u6a80\u7fbd\u51b2\uff4c\u4eb2\u624b\u6240\u6740\uff01\u201d \u3000\u3000\u5218\u5929\u5316\u9053\uff1a\u201c\u90a3\u4f60\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u9093\u5927\u9b41\u9053\uff1a\u201c\u6211\u8981\u4ed6\u507f\u8fd8\u8840\u503a\uff01\u201d \u3000\u3000\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u4ee5\u8840\u8fd8\u8840\uff01\u201d\u8892\u9732\u80f8\u819b\uff0c\u7ad9\u5728\u573a\u5fc3\u3002 \u3000\u3000\u9093\u5927\u9b41\u62d4\u51fa\u5c16\u5200\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u5f92\u5f1f\uff0c\u5403\u6211\u4e00\u5200\uff01\u201d\u660e\u6643\u6643\u7684\u5200\u5c16\u671d\u7740\u6a80\u7fbd\u51b2\u7684\u80f8 \u819b\u523a\u53bb\uff0c\u4e0d\u4f46\u8d6b\u8fde\u6e05\u4e91\u5403\u60ca\uff0c\u6587\u9038\u51e1\u7684\u9762\u8272\u4e5f\u53d8\u4e86\u3002\u773c\u770b\u5c16\u5200\u523a\u5230\u80f8\u819b\uff0c\u5374\u5ffd\u5730\u5f80\u65c1\u8fb9\u4e00 \u6ed1\uff0c\u53ea\u662f\u5728\u6a80\u7fbd\u51b2\u7684\u53f3\u80a9\u5212\u5f00\u4e86\u4e00\u9053\u4e09\u5bf8\u957f\u7684\u53e3\u5b50\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4f24\u7740\u9aa8\u5934\u3002\u9093\u5927\u9b41\u9053\uff1a \u201c\u597d\uff0c\u6211\u7684\u4ec7\u5df2\u7ecf\u62a5\u4e86\uff0c\u54ea\u4f4d\u8bf7\u4e0a\u3002\u201d\u8bf4\u7f62\uff0c\u4fbf\u5373\u9000\u4e0b\u3002\u539f\u6765\u6c5f\u6e56\u89c4\u77e9\u7684\u6240\u8c13\u201c\u4ee5\u8840\u8fd8 \u8840\u201d\uff0c\u662f\u53ea\u987b\u89c1\u8840\u4fbf\u53ef\u7684\u3002\u4e00\u822c\u800c\u8a00\uff0c\u4e0d\u4f1a\u4f24\u5bf9\u65b9\u6027\u547d\uff0c\u4e0d\u8fc7\u82e5\u662f\u4ec7\u51a4\u592a\u6df1\uff0c\u91cd\u4f24\u5bf9\u4e07\u4e5f\u4e0d \u7b97\u72af\u4f8b\u3002\u9093\u5927\u9b41\u53ea\u662f\u8981\u4e89\u4e00\u53e3\u6c14\uff0c\u5200\u5934\u67d3\u4e86\u6a80\u7fbd\u51b2\u7684\u9c9c\u8840\uff0c\u6c14\u4e5f\u5e73\u4e86\u3002 \u3000\u3000\u6587\u9009\u51e0\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u8981\u6a80\u7fbd\u51b2\u4ee5\u8840\u8fd8\u8840\uff1f\u201d\u6ca1\u6709\u4eba\u58f0\uff0c\u4e8b\u60c5\u5c31\u7ed3\u675f\u4e86\u3002 \u3000\u3000\u6a80\u7fbd\u51b2\u6cea\u76c8\u4e8e\u773c\uff0c\u4f5c\u4e86\u4e2a\u7f57\u5708\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u67d0\u53ea\u4e0d\u8fc7\u6d12\u4e86\u51e0\u6ef4\u8840\uff0c\u5c31\u4ea4\u4e86\u8bb8\u591a\u597d\u670b \u53cb\uff0c\u5e73\u751f\u5feb\u610f\u4e4b\u4e8b\uff0c\u5f53\u771f\u662f\u83ab\u8fc7\u4e8e\u6b64\u4e86\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u54b1\u4eec\u4e0a\u54ea\u513f\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u76d8\u9f99\u5c71\u6211\u662f\u4e0d\u60f3\u56de\u53bb\u4e86\uff0c\u54b1\u4eec\u56de\u9526\u5c4f\u5c71 \u5427\u3002\u201d \u3000\u3000\u9526\u5c4f\u5c71\u662f\u4ed6\u4ee5\u524d\u548c\u949f\u7075\u79c0\u907f\u96be\u4e4b\u6240\uff0c\u5c71\u5357\u662f\u5b8b\u56fd\u7684\u7586\u571f\uff0c\u5c71\u5317\u662f\u91d1\u56fd\u7684\u8f96\u533a\u3002\u6a80\u7fbd\u51b2\u8ba4 \u4e3a\u81ea\u5df1\u662f\u534a\u4e2a\u91d1\u4eba\uff0c\u534a\u4e2a\u5b8b\u4eba\uff0c\u662f\u4ee5\u9009\u62e9\u6b64\u5c71\u4e0e\u8d6b\u8fde\u6e05\u4e91\u5055\u9690\u3002\u53e6\u5916\u4e00\u4e2a\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6765\u7684\u539f \u56e0\u5219\u662f\uff0c\u5728\u949f\u7075\u79c0\u751f\u524d\uff0c\u4ed6\u66fe\u7ecf\u7b54\u5e94\u8fc7\u5979\uff0c\u5728\u6b64\u5c71\u4e2d\u4e0e\u5979\u957f\u76f8\u53ae\u5b88\u7684\u3002\u8fd9\u5c71\u4e0a\u6709\u4ed6\u4eb2\u624b\u6240\u7b51 \u7684\u949f\u7075\u79c0\u7684\u575f\u5893\u3002\u6765\u5230\u9526\u5c4f\u5c71\u7684\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u4ed6\u5c31\u4e0e\u8d6b\u8fde\u6e05\u4e91\u5230\u949f\u7075\u79c0\u7684\u5893\u524d\u7977\u544a\u3002 \u3000\u3000\u6708\u4e0a\u68a2\u5934\uff0c\u8352\u5c71\u5df2\u662f\u53ea\u95fb\u733f\u5578\u4e86\uff0c\u6a80\u7fbd\u51b2\u5140\u662f\u5750\u5728\u575f\u524d\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002 \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u9053\uff1a\u201c\u591c\u5df2\u6df1\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4eca\u5915\u4f55\u5915\uff1f\u201d\u8d6b\u8fde\u6e05\u4e91 \u9053\uff1a\u201c\u662f\u516b\u6708\u5341\u56db\uff0c\u554a\uff0c\u65e5\u5b50\u8fc7\u5f97\u771f\u5feb\uff0c\u660e\u5929\u5c31\u662f\u4e2d\u79cb\u4e86\u3002\u201d\u6a80\u51fa\u51b2\u9053\uff1a\u201c\u4e0d\uff0c\u5bf9\u6211\u6765\u8bf4\uff0c \u4eca\u665a\u5c31\u662f\u4e2d\u79cb\u3002\u201d\u6e05\u4e91\u8be7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6a80\u7fbd\u51b2\u9053\uff1a\u201c\u56e0\u4e3a\u4eca\u5929\u662f\u7075\u79c0\u7684\u751f\u65e5\uff0c\u4e24\u5e74\u524d\u7684 \u4eca\u665a\uff0c\u6211\u7684\u4f24\u521a\u597d\uff0c\u4e0e\u5979\u5728\u6b64\u8d4f\u6708\uff0c\u6211\u7b54\u5e94\u5979\uff0c\u4ee5\u540e\u6bcf\u5e74\u6b64\u665a\uff0c\u90fd\u4e0e\u5979\u5f53\u4f5c\u4e2d\u79cb\u6765\u8fc7\u3002\u5509\uff0c \u771f\u662f\u4e00\u8bed\u6210\u8c36\uff0c\u6ca1\u60f3\u5230\u7b2c\u4e8c\u5929\u5c31\u51fa\u4e8b\u3002\u6211\u8fd9\u4e00\u751f\u6b20\u5979\u6700\u591a\uff0c\u4f60\u4e0d\u602a\u6211\u6000\u5ff5\u5979\u5427\uff1f\u201d\u8d6b\u8fde\u6e05\u4e91 \u5f3a\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u6b22\u559c\u4f60\u6709\u8fd9\u4efd\u771f\u60c5\u3002\u201d \u3000\u3000\u8d6b\u8fde\u6e05\u4e91\u63a5\u8fc7\u4ed6\u7684\u7389\u8427\uff0c\u5439\u51fa\u90a3\u9996\u300a\u6c34\u8c03\u6b4c\u5934\u300b\u7684\u4e0b\u534a\u8131\uff1a\u201c\u8f6c\u6731\u9601\uff0c\u4f4e\u7eee\u6237\uff0c\u7167\u65e0 \u7720\u3002\u4e0d\u5e94\u6709\u6068\uff0c\u4f55\u4e8b\u957f\u5411\u522b\u65f6\u5706\u3002\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be\u5168\u3002\u4f46\u613f\u4eba\u957f \u4e45\uff0c\u5343\u91cc\u5171\u5a75\u5a1f\u3002\u201d\u6b4c\u58f0\u594f\u51fa\u5fc3\u52a0\uff0c\u6a80\u7fbd\u51b2\u7684\u613f\u671b\uff0c\u4e5f\u53ea\u80fd\u5982\u6b64\u4e86\u3002"}),showOnHomepage:!0}]}}]);