"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[19394],{53928:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u90ed\u5927\u8def\u4e0e\u738b\u52a8",content:(0,s.jsx)(s.Fragment,{children:"(\u4e00) \u3000\u3000\u90ed\u5927\u8def\u4eba\u5982\u5176\u540d,\u7684\u786e\u662f\u4e2a\u5f88\u5927\u8def\u7684\u4eba\u3002\u201c\u5927\u8def\u201d\u7684\u610f\u601d\u5c31\u662f\u5f88\u5927\u65b9\u3001\u5f88\u9a6c\u864e,\u751a\u81f3\u6709\u70b9 \u7cca\u6d82,\u65e0\u8bba\u5bf9\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u738b\u52a8\u5374\u4e0d\u52a8\u3002 (\u4e8c) \u3000\u3000\u5927\u8def\u7684\u4eba\u901a\u5e38\u90fd\u5f88\u7a77\u3002\u90ed\u5927\u8def\u5c24\u5176\u7a77,\u7a77\u7684\u7279\u522b,\u7a77\u5f97\u79bb\u4e86\u8c31\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u8be5\u8fd9\u4e48\u7a77\u7684\u3002 \u3000\u3000\u4ed6\u672c\u6765\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u4e2a\u5f88\u6709\u94b1\u7684\u4eba\u3002\u4e00\u4e2a\u6709\u94b1\u7684\u4eba\u5982\u679c\u7a81\u7136\u53d8\u7a77\u4e86\uff0c\u53ea\u6709\u4e24\u79cd\u539f\u56e0\uff1a\u7b2c \u4e00\u662f\u56e0\u4e3a\u4ed6\u7b28\uff0c\u7b2c\u4e8c\u662f\u56e0\u4e3a\u4ed6\u61d2\u3002 \u3000\u3000\u90ed\u5927\u8def\u5e76\u4e0d\u7b28\uff0c\u4ed6\u4f1a\u505a\u7684\u4e8b\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u591a\uff0c\u800c\u4e14\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u505a\u5f97\u597d\u3002\u8b6c\u5982\u8bf4\u2014\u2014\u9a91 \u9a6c\uff0c\u4ed6\u80fd\u9a91\u6700\u5feb\u7684\u9a6c\uff0c\u4e5f\u80fd\u9a91\u6700\u70c8\u7684\u9a6c\u3002 \u3000\u3000\u51fb\u5251\uff0c\u4ed6\u4e00\u5251\u80fd\u523a\u7a7f\u5927\u5c06\u8eab\u4e0a\u7684\u94c1\u7532\uff0c\u4e5f\u80fd\u523a\u7a7f\u6625\u98ce\u4e2d\u7684\u67f3\u7d6e\u3002 \u3000\u3000\u4f60\u82e5\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u9047\u7740\u4ed6\u5fc3\u60c5\u7279\u522b\u597d\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u8bb8\u4f1a\u8d64\u624b\u7a7a\u62f3\u8dc3\u5165\u9ec4\u6cb3\u6349\u4e24\u5c3e\u9ca4\u9c7c\uff0c \u5728\u4ece\u6c34\u91cc\u8dc3\u51fa\u6293\u4e24\u53ea\u79cb\u96c1\uff0c\u4e3a\u4f60\u505a\u4e00\u5473\u6e05\u84b8\u9c7c\u3001\u70e7\u91ce\u9e2d\uff0c\u8ba9\u4f60\u5927\u5feb\u6735\u9890\uff1b\u4f60\u5403\u4e86\u4ed6\u7684\u83dc\u4fdd\u8bc1 \u4e0d\u4f1a\u5931\u671b\u3002 \u3000\u3000\u4ed6\u505a\u83dc\u7684\u624b\u827a\u7edd\u4e0d\u5728\u4eac\u57ce\u4efb\u4f55\u4e00\u4f4d\u540d\u53a8\u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u80fd\u7528\u94c1\u677f\u94dc\u7435\u5531\u82cf\u8f7c\u7684\u201c\u5927\u6c5f\u4e1c\u53bb\u201d\uff0c\u4e5f\u53ef\u4ee5\u5f04\u53c1\u5f26\u5531\u67f3\u6c38\u7684\u201c\u6768\u67f3\u5cb8\uff0c\u6653\u98ce\u6b8b \u6708\u201d\uff0c\u8ba9\u4f60\u4ee5\u4e3a\u4ed6\u7ec8\u751f\u90fd\u662f\u5728\u5356\u5531\u7684\u3002 \u3000\u3000\u6709\u4eba\u751a\u81f3\u8ba4\u4e3a\u4ed6\u9664\u4e86\u751f\u5b69\u5b50\u5916\uff0c\u4ec0\u4e48\u90fd\u4f1a\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u61d2\uff0c\u975e\u4f46\u4e0d\u61d2\uff0c\u800c\u4e14\u65f6\u65f6\u523b\u523b\u90fd\u60f3\u627e\u4e8b\u505a\uff0c\u505a\u8fc7\u7684\u4e8b\u8fd8\u771f\u4e0d\u5c11\u3002\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u600e \u4e48\u4f1a\u7a77\u5462\uff1f \u3000\u3000\u4ed6\u7b2c\u4e00\u6b21\u505a\u7684\u4e8b\uff0c\u662f\u9556\u5e08\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u521a\u51fa\u9053\uff0c\u521a\u5b88\u8fc7\u7236\u6bcd\u7684\u4e27\uff0c\u5c06\u5bb6\u5b85\u7684\u7530\u56ed\u5356\u7684\u5356\uff0c\u9001\u7684\u9001\uff0c\u60f3\u51ed\u4e00\u8eab\u672c\u4e8b\uff0c\u5230\u6c5f \u6e56\u4e2d\u6765\u95ef\u4e00\u95ef\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u662f\u4e2a\u5f88\u7cbe\u660e\u7684\u751f\u610f\u4eba\uff0c\u4e5f\u6839\u672c\u4e0d\u60f3\u505a\u4e2a\u5f88\u7cbe\u660e\u7684\u751f\u610f\u4eba\uff0c\u6240\u4ee5\u672c\u6765\u503c\u53c1\u767e\u4e24 \u4e00\u4ea9\u7684\u7530\uff0c\u4ed6\u53ea\u5356\u4e86\u4e00\u767e\u4e03\uff0c\u518d\u52a0\u4e0a\u9001\u7ed9\u7a77\u4eb2\u621a\u670b\u53cb\u7684\uff0c\u5269\u4e0b\u7684\u4e5f\u5c31\u4e0d\u592a\u591a\u4e86\u3002 \u3000\u3000\u4f46\u90a3\u4e5f\u8db3\u591f\u8ba9\u4ed6\u4e70\u4e00\u5339\u597d\u9a6c\uff0c\u94f8\u4e00\u67c4\u5feb\u5251\uff0c\u5236\u51e0\u8eab\u98ce\u5149\u7684\u884c\u5934\uff0c\u4f4f\u6700\u597d\u7684\u5ba2\u6808\uff0c\u5403\u6700\u597d \u7684\u9986\u5b50\u3002 \u3000\u3000\u90a3\u65f6\u6b63\u662f\u6625\u5929\uff0c\u4e00\u5e74\u4e4b\u8ba1\u5728\u4e8e\u6625\u3002\u6625\u5929\u9002\u4e8e\u505a\u5f88\u591a\u4e8b\uff0c\u4e5f\u662f\u9556\u5c40\u751f\u610f\u6700\u597d\u7684\u65f6\u5019\u3002 \u3000\u3000\u9556\u5c40\u7684\u751f\u610f\u6700\u597d\u7684\u65f6\u5019\uff0c\u6b63\u4e5f\u5c31\u662f\u5f3a\u76d7\u751f\u610f\u6700\u597d\u7684\u65f6\u5019\u3002 \u3000\u3000\u201c\u4e2d\u539f\u9556\u5c40\u201d\u7684\u603b\u9556\u5934\u7f57\u632f\u7ffc\uff0c\u4eba\u867d\u672a\u8001\uff0c\u6c5f\u6e56\u5df2\u8001\uff0c\u5f53\u7136\u4e5f\u5f88\u660e\u767d\u8fd9\u9053\u7406\u3002\u6240\u4ee5\u8d70\u5728 \u9053\u4e0a\uff0c\u603b\u662f\u7279\u522b\u5c0f\u5fc3\u3002\u4f55\u51b5\uff0c\u73b0\u5728\u6b63\u662f\u6625\u5929\uff0c\u4ed6\u8fd9\u6b21\u4fdd\u7684\u9556\u53c8\u4e0d\u8f7b\u3002 \u3000\u3000\u53ef\u662f\u4fdd\u9556\u53ea\u9760\u5c0f\u5fc3\u662f\u7edd\u4e0d\u591f\u7684\uff0c\u8fd8\u5f97\u6b66\u529f\u786c\uff0c\u8fd0\u6c14\u597d\u3002 \u3000\u3000\u7f57\u632f\u7ffc\u6b66\u529f\u5e76\u4e0d\u5f31\uff0c\u4f46\u8fd9\u6b21\u7684\u8fd0\u6c14\u5374\u5b9e\u5728\u4e0d\u597d\uff0c\u7adf\u504f\u504f\u9047\u4e0a\u4e86\u4e24\u6cb3\u9ed1\u9053\u4e0a\u6700\u96be\u60f9\u7684\u6b27\u9633 \u5144\u5f1f\u3002 \u3000\u3000\u6b27\u9633\u5144\u5f1f\u4e0d\u662f\u4e24\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u662f\u53c1\u4e2a\u4eba\u3001\u56db\u4e2a\u4eba\u2026\u2026\u6b27\u9633\u5144\u5f1f\u5c31\u662f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u867d\u7136\u53ea\u662f\u4e00\u4e2a\u4eba\uff0c\u5374\u7b80\u76f4\u6bd4\u56db\u5341\u4e2a\u4eba\u8fd8\u96be\u6597\u3002\u4ed6\u5de6\u624b\u4f7f\u77ed\u5200\uff0c\u53f3\u624b\u4f7f\u957f\u5200\uff0c\u8fd8\u53ef\u4ee5\u540c \u65f6\u53d1\u51fa\u4e03\u516b\u79cd\u4e0d\u540c\u7684\u6697\u5668\uff0c\u5f88\u5c11\u4eba\u80fd\u770b\u51fa\u4ed6\u6697\u5668\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u7f57\u632f\u7ffc\u4e5f\u770b\u4e0d\u51fa\u3002\u4ed6\u521a\u8eb2\u8fc7\u53c1\u679d\u201c\u9526\u80cc\u4f4e\u5934\u82b1\u88c5\u5f29\u201d\u3001\u4e00\u7b52\u201c\u6d41\u661f\u8d76\u6708\u8896\u4e2d\u7bad\u201d\uff0c\u8c01\u77e5 \u6b27\u9633\u5144\u5f1f\u5200\u80cc\u4e00\u7ffb\uff0c\u53c8\u5c04\u51fa\u4e00\u53cc\u5b50\u6bcd\u5bd2\u9488\u3002 \u3000\u3000\u8981\u547d\u7684\u9488\uff0c\u4ece\u522b\u4eba\u8981\u547d\u4e5f\u731c\u4e0d\u51fa\u7684\u5730\u65b9\u5c04\u51fa\u6765\u3002 \u3000\u3000\u7f57\u632f\u7ffc\u53f3\u80a9\u4e0a\u6328\u4e86\u4e24\u9488\uff0c\u867d\u8fd8\u4e0d\u81f4\u7acb\u5373\u8981\u547d\uff0c\u4f46\u4e5f\u53ea\u6709\u7b49\u7740\u6b27\u9633\u5144\u5f1f\u6765\u8981\u4ed6\u7684\u547d\u3002 \u3000\u3000\u6b27\u9633\u5144\u5f1f\u5c31\u7b97\u4e0d\u60f3\u8981\u4ed6\u7684\u547d\uff0c\u4ed6\u8fd9\u8d9f\u9556\u4e22\u4e86\uff0c\u4e5f\u53ea\u6709\u81ea\u5df1\u53bb\u4e0a\u540a\u8df3\u6cb3\u62b9\u8116\u5b50\uff0c\u81ea\u5df1\u8981\u81ea \u5df1\u7684\u547d\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u7136\u4e00\u9a91\u5feb\u9a6c\u9a70\u6765\uff0c\u9a6c\u5feb\u4eba\u66f4\u5feb\uff0c\u9a6c\u8fd8\u672a\u5230\uff0c\u9a6c\u4e0a\u7684\u4eba\u5df2\u5230\u3002\u6b27\u9633\u5144\u5f1f\u53ea\u770b \u5230\u4e00\u4e2a\u4eba\u4ece\u534a\u7a7a\u4e2d\u843d\u4e0b\u6765\uff0c\u4e03\u516b\u79cd\u6697\u5668\u8fde\u4e00\u79cd\u90fd\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u51fa\u624b\uff0c\u5de6\u53f3\u8109\u95e8\u5df2\u540c\u65f6\u6371\u4e86\u4eba \u5bb6\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u534a\u7a7a\u843d\u4e0b\u6765\u7684\u6551\u661f\u81ea\u7136\u5c31\u662f\u90ed\u5927\u8def\u3002 \u3000\u3000\u7f57\u632f\u7ffc\u5bf9\u8fd9\u4f4d\u6551\u661f\u81ea\u7136\u4e0d\u4f46\u611f\u6fc0\uff0c\u800c\u4e14\u4f69\u670d\uff1b\u4e0d\u4f46\u4f69\u670d\uff0c\u800c\u4e14\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u3002\u5c06\u8fd9\u8d9f \u9556\u9001\u5230\u5730\u5934\u540e\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u8bf7\u4ed6\u4e00\u8d77\u56de\u9556\u5c40\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u53bb\u4e86\uff0c\u4ed6\u53cd\u6b63\u6ca1\u4ec0\u4e48\u522b\u7684\u8981\u7d27\u4e8b\u3002 \u3000\u3000\u4ed6\u5c31\u7b97\u6709\u522b\u7684\u4e8b\uff0c\u4e5f\u4f1a\u53bb\u7684\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u51fa\u624b\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u975e\u4f46\u6b66\u529f\u4e0d\u9519\uff0c\u4eba\u7f18\u4e5f\u4e0d\u9519\u3002 \u3000\u3000\u4e8e\u662f\u7f57\u632f\u7ffc\u5c31\u89c9\u5f97\u5947\u602a\uff0c\u5c31\u95ee\uff1a\u201c\u50cf\u90ed\u5144\u5982\u6b64\u9ad8\u7684\u8eab\u624b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u505a\u9556\u5934\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u6ca1\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u6b66\u529f\u9ad8\u7684\u4eba\u8981\u53bb\u4fdd\u9556\uff1f\u201d \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u505a\u9556\u5934\u4e5f\u86ee\u5a01\u98ce\uff0c\u86ee\u6709\u8da3\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u521d\u5165\u6c5f\u6e56\u5c31\u505a\u4e86\u526f\u603b\u9556\u5934\uff0c\u7684\u786e\u591f\u5a01\u98ce\uff0c\u591f\u795e\u6c14\uff01 \u3000\u3000\u552f\u4e00\u4ee4\u90ed\u5927\u8def\u89c9\u5f97\u9057\u61be\u7684\u662f\uff0c\u201c\u4e2d\u539f\u9556\u5c40\u201d\u5e76\u4e0d\u662f\u4e2d\u539f\u6700\u5927\u7684\u9556\u5c40\uff0c\u751a\u81f3\u8fde\u7b2c\u4e00\u6d41\u7684\u9556 \u5c40\u4e5f\u7b97\u4e0d\u4e0a\u3002 \u3000\u3000\u4ed6\u7b49\u4e86\u597d\u51e0\u5929\uff0c\u624d\u63a5\u5230\u7b2c\u4e00\u7b14\u751f\u610f\uff0c\u800c\u4e14\u8fd8\u4e0d\u662f\u5927\u751f\u610f\uff0c\u53ea\u4e0d\u8fc7\u662f\u66ff\u4eba\u4ece\u5f00\u5c01\u62bc\u51e0\u5343\u4e24 \u94f6\u5b50\u56de\u6d1b\u9633\u3002 \u3000\u3000\u8def\u4e0d\u8fdc\uff0c\u9556\u4e0d\u91cd\uff0c\u53c8\u6709\u8fd9\u4e48\u6837\u4e00\u4f4d\u526f\u603b\u9556\u5934\uff0c\u603b\u9556\u5934\u81ea\u7136\u4e50\u5f97\u5b89\u5b89\u5fc3\u5fc3\u3001\u8212\u8212\u670d\u670d\u7684\u5728 \u5bb6\u91cc\u517b\u4f24\u4e86\u3002 \u3000\u3000\u8fd8\u662f\u6625\u5929\uff0c\u65e9\u4e0a\uff0c\u9556\u8f66\u542f\u884c\u3002 \u3000\u3000\u4e00\u5e74\u4e4b\u8ba1\u5728\u4e8e\u6625\uff0c\u4e00\u65e5\u4e4b\u8ba1\u5728\u4e8e\u6668\uff0c\u8fd9\u5f00\u59cb\u53ef\u771f\u4e0d\u9519\u3002 \u3000\u3000\u9556\u65d7\u8fce\u98ce\u62db\u5c55\uff0c\u8d9f\u5b50\u624b\u7684\u558a\u9556\u58f0\u5639\u4eae\u5165\u4e91\uff0c\u90ed\u5927\u8def\u7a7f\u7740\u7d2b\u7f57\u886b\uff0c\u4f69\u7740\u4e4c\u9798\u5251\uff0c\u5750\u5728\u5927\u767d \u9a6c\u4e0a\uff0c\u6625\u5929\u7684\u592a\u9633\u521a\u5347\u8d77\uff0c\u7167\u5f97\u4ed6\u8eab\u4e0a\u6696\u6696\u548c\u548c\u7684\uff0c\u8fdc\u5904\u7684\u6625\u5c71\u4e00\u78a7\u5982\u6d17\uff0c\u71d5\u5b50\u6b63\u5728\u6811\u4e0a\u8854 \u6ce5\u505a\u5de2\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5b9e\u5728\u89c9\u5f97\u6109\u5feb\u6781\u4e86\u3001\u5f97\u610f\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u80fd\u5728\u8def\u4e0a\u9047\u89c1\u51e0\u4e2a\u6c5f\u6d0b\u5927\u76d7\u3001\u7eff\u6797\u597d\u6c49\uff0c\u90a3\u5012\u5e76\u4e0d\u5b8c\u5168\u662f\u4e3a\u4e86\u4ed6\u60f3\u9732\u9732\u672c\u4e8b\u3001 \u663e\u663e\u5a01\u98ce\uff0c\u800c\u662f\u4e3a\u4e86\u60f3\u591a\u4ea4\u51e0\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u670b\u53cb\u8d8a\u591a\u8d8a\u597d\u3002\u4ed6\u559c\u6b22\u670b\u53cb\uff0c\u80fd\u548c\u8fd9\u79cd\u4eba\u4ea4\u4e0a\u670b\u53cb\uff0c\u5c82\u975e\u4e5f\u5f88\u523a\u6fc0\u3001\u5f88\u6709\u8da3\uff0c\u82e5\u518d\u80fd\u611f \u5316\u4ed6\u4eec\u6539\u90aa\u5f52\u6b63\uff0c\u5c82\u975e\u66f4\u5999\u4e0d\u53ef\u8a00\u3002 \u3000\u3000\u4ed6\u679c\u7136\u9047\u5230\u4e86\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u9047\u5230\u7684\uff0c\u5e76\u4e0d\u662f\u4ed6\u60f3\u8c61\u4e2d\u90a3\u79cd\u5927\u79e4\u5206\u91d1\u3001\u5c0f\u79e4\u5206\u94f6\uff0c\u5927\u5757\u5403\u8089\uff0c\u5927\u7897\u559d\u9152\u7684\u6c5f \u6d0b\u5927\u76d7\uff1b\u4e5f\u4e0d\u662f\u90a3\u79cd\u4e00\u8bfa\u5343\u91d1\uff0c\u8c6a\u6c14\u5e72\u4e91\uff0c\u968f\u65f6\u80af\u4e3a\u670b\u53cb\u4e24\u808b\u63d2\u5200\u7684\u7eff\u6797\u597d\u6c49\u3002\u4ed6\u9047\u89c1\u7684\u7adf \u53ea\u4e0d\u8fc7\u662f\u4e00\u4f19\u5c0f\u6bdb\u8d3c\uff0c\u4e00\u4e2a\u4e2a\u9762\u6709\u83dc\u8272\uff0c\u597d\u8c61\u997f\u4e86\u53c1\u5929\uff0c\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u5230\u5904\u662f\u8865\u4e01\uff0c\u8fde\u5200\u90fd \u751f\u4e86\u9508\u3002 \u3000\u3000\u90ed\u5927\u8def\u867d\u7136\u5931\u671b\uff0c\u4f46\u65e2\u7136\u9047\u89c1\u4e86\uff0c\u4e5f\u6ca1\u6cd5\u5b50\uff0c\u53ea\u597d\u5148\u9732\u4e24\u624b\u6b66\u529f\uff0c\u5c06\u4ed6\u4eec\u5148\u9707\u4f4f\uff0c\u518d\u5faa \u5faa\u5584\u8bf1\uff0c\u5e0c\u671b\u4ed6\u4eec\u4ece\u6b64\u6d17\u5fc3\u9769\u9762\uff0c\u6539\u8fc7\u5411\u5584\uff0c\u505a\u4e2a\u5b89\u5206\u5b88\u5df1\u3001\u81ea\u98df\u5176\u529b\u7684\u826f\u6c11\uff0c\u83ab\u8981\u8fb1\u6ca1\u4e86 \u7956\u5b97\u3002 \u3000\u3000\u5927\u5bb6\u5148\u88ab\u4ed6\u7684\u6b66\u529f\u5413\u5f97\u5446\u82e5\u6728\u9e21\uff0c\u7ee7\u800c\u53c8\u88ab\u4ed6\u7684\u826f\u8a00\u611f\u52a8\u5f97\u75db\u54ed\u6d41\u6d95\uff0c\u4e00\u4e2a\u4e2a\u90fd\u8868\u793a\u51b3 \u5fc3\u8981\u91cd\u65b0\u505a\u4eba\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u5374\u8eab\u65e0\u4e00\u6280\u4e4b\u957f\uff0c\u53eb\u6211\u4eec\u53bb\u505a\u4ec0\u4e48\u5462\uff1f\u4e0d\u505a\u5f3a\u76d7\uff0c\u53ea\u6015\u4e00\u5bb6\u4eba\u90fd\u5f97\u997f\u6b7b\u3002\u201d \u3000\u3000\u201c\u505a\u505a\u5c0f\u751f\u610f\u4e5f\u597d\u5440\uff0c\u5c31\u7b97\u5356\u9992\u5934\uff0c\u4e5f\u603b\u6bd4\u505a\u5f3a\u76d7\u597d\u3002\u201d \u3000\u3000\u201c\u8fde\u4e00\u6587\u672c\u94b1\u90fd\u6ca1\u6709\uff0c\u80fd\u505a\u4ec0\u4e48\u751f\u610f\uff1f\u4e0d\u5982\u73b0\u5728\u5c31\u6b7b\u4e86\u7b97\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u4e00\u628a\u773c\u6cea\uff0c\u4e00\u628a\u9f3b\u6d95\uff0c\u7684\u786e\u662f\u5929\u826f\u53d1\u73b0\u7684\u6837\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u4e5f\u88ab\u611f\u52a8\u5f97\u6d41\u6cea\u4e86\u3002 \u3000\u3000\u201c\u6ca1\u6709\u672c\u94b1\uff0c\u8fd9\u5bb9\u6613\uff0c\u6211\u6709\u3002\u201d \u3000\u3000\u9556\u8f66\u91cc\u5c82\u975e\u6709\u7684\u662f\u94f6\u5b50\u5417\uff1f \u3000\u3000\u672c\u94b1\u5c11\u4e86\uff0c\u4e5f\u505a\u4e0d\u6210\u751f\u610f\uff0c\u90ed\u5927\u8def\u51fa\u624b\u4e00\u5411\u5927\u65b9\u5f97\u5f88\u3002 \u3000\u3000\u201c\u6bcf\u4eba\u4e00\u767e\u4e24\u3002\u201d \u3000\u3000\u5927\u5bb6\u5343\u6069\u4e07\u8c22\uff0c\u7136\u540e\uff0c\u5ffd\u7136\u95f4\u5c31\u5168\u90e8\u547c\u5578\u800c\u53bb\uff0c\u8fdc\u8fdc\u90fd\u53ef\u4ee5\u542c\u89c1\u4ed6\u4eec\u5728\u8bf4\uff1a\u201c\u8fd9\u4f4d\u6069\u516c \u4e0d\u4f46\u662f\u5927\u82f1\u96c4\u3001\u5927\u8c6a\u6770\uff0c\u800c\u4e14\u7b80\u76f4\u662f\u4e2a\u6d3b\u83e9\u8428\u3001\u5927\u5723\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u4e5f\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u611f\u6168\u4e0d\u5df2\uff1a\u201c\u4eba\u4e4b\u521d\uff0c\u6027\u672c\u5584\uff0c\u82e5\u975e\u88ab\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u53c8\u6709 \u8c01\u613f\u610f\u505a\u5f3a\u76d7\u5462\uff1f\u201d \u3000\u3000\u7b49\u4ed6\u7684\u611f\u60c5\u6e10\u6e10\u5e73\u9759\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u5ffd\u7136\u53d1\u73b0\u4e86\u4e24\u4ef6\u4e8b\uff1a\u7b2c\u4e00\uff0c\u9556\u8f66\u91cc\u7684\u94f6\u5b50\u5df2\u88ab\u5206\u6389\u4e00 \u5927\u534a\u3002 \u3000\u3000\u7b2c\u4e8c\uff0c\u8fd9\u94f6\u5b50\u5e76\u4e0d\u662f\u4ed6\u7684\u3002 \u3000\u3000\u8ddf\u7740\u4ed6\u7684\u9556\u4f19\u4eec\u4e00\u4e2a\u4e2a\u90fd\u5f20\u5927\u4e86\u5634\uff0c\u773c\u7741\u7741\u5730\u77a7\u7740\u4ed6\uff0c\u8c01\u4e5f\u5206\u4e0d\u6e05\u4ed6\u4eec\u8fd9\u79cd\u773c\u8272\u662f\u5c06\u4ed6 \u770b\u6210\u4ec0\u4e48\uff1f \u3000\u3000\u662f\u5927\u82f1\u96c4\uff1f\u5927\u5723\u4eba\uff1f\u8fd8\u662f\u4e2a\u5927\u5446\u5b50\uff1f \u3000\u3000\u9556\u94f6\u5c11\u4e86\u4e00\u5927\u534a\uff0c\u9556\u5934\u5f53\u7136\u662f\u8981\u8d54\u3002 \u3000\u3000\u90ed\u5927\u8def\u56de\u9556\u5c40\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u867d\u6709\u4e9b\u4e0d\u5b89\uff0c\u5374\u8fd8\u4e0d\u592a\u96be\u53d7\u3002 \u3000\u3000\u4ed6\u6709\u628a\u63e1\u8d54\u8fd9\u9556\u94f6\uff0c\u6709\u672c\u4e8b\u7684\u4eba\u90fd\u6709\u8fd9\u79cd\u628a\u63e1\u3002 \u3000\u3000\u201c\u6211\u8fd9\u5339\u9a6c\u662f\u4e8c\u767e\u516b\u5341\u4e24\u4e70\u6765\u7684\uff0c\u8eab\u4e0a\u8fd8\u5269\u4e0b\u4e03\u767e\u591a\u4e24\u94f6\u5b50\uff0c\u52a0\u8d77\u6765\u4e5f\u6709\u4e00\u5343\u591a\u4e24\u4e86\u3002 \u5148\u8d54\u4ed6\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u5269\u4e0b\u7684\u5462\uff1f \u3000\u3000\u201c\u5269\u4e0b\u7684\u9556\u5c40\u5148\u57ab\u4e0a\uff0c\u6211\u7528\u526f\u603b\u9556\u5934\u7684\u85aa\u9977\u6162\u6162\u6765\u8fd8\u3002\u201d \u3000\u3000\u4e2d\u539f\u9556\u5c40\u80fd\u8bf7\u5230\u4ed6\u8fd9\u6837\u7684\u526f\u603b\u9556\u5934\uff0c\u4ee5\u540e\u540d\u6c14\u81ea\u7136\u4f1a\u8d8a\u6765\u8d8a\u5927\uff0c\u751f\u610f\u81ea\u7136\u4f1a\u8d8a\u6765\u8d8a\u597d\uff0c \u4ed6\u7684\u85aa\u9977\u5f53\u7136\u51b3\u4e0d\u4f1a\u5c11\uff0c\u5f88\u5feb\u5c31\u80fd\u8fd8\u6e05\u7684\u3002 \u3000\u3000\u7f57\u632f\u7ffc\u4e00\u76f4\u5728\u542c\u7740\uff0c\u542c\u5f97\u76ee\u5b9a\u53e3\u5446\uff0c\u542c\u5f97\u50cf\u662f\u5df2\u51fa\u4e86\u795e\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd8\u662f\u5f88\u6709\u628a\u63e1\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u81ea\u5df1\u63d0\u51fa\u7684\u8fd9\u65b9\u6cd5\u5b9e\u5728\u592a\u5408\u7406\u4e86\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u7f57\u632f\u7ffc\u4f1a\u7a81\u7136\u8dea\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7f57\u632f\u7ffc\u8dea\u4e0b\u6765\u5e76\u4e0d\u662f\u8981\u6c42\u4ed6\u7559\u4e0b\u6765\uff0c\u6216\u662f\u53e9\u8c22\u4ed6\u7684\u6551\u547d\u4e4b\u6069\uff0c\u800c\u662f\u6c42\u4ed6\u5feb\u8d70\uff0c\u8d70\u5f97\u8d8a\u5feb \u8d8a\u597d\uff0c\u8d8a\u8fdc\u8d8a\u597d\u3002 \u3000\u3000\u201c\u4f60\u6551\u8fc7\u6211\uff0c\u6211\u66ff\u4f60\u8d54\u9556\u94f6\uff0c\u5c31\u7b97\u8fd8\u4e86\u503a\u3002\u50cf\u90ed\u5927\u7237\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u6211\u4ee5\u524d\u5b9e\u5728\u6ca1\u6709\u89c1\u5230 \u8fc7\uff0c\u53ea\u6c42\u4ee5\u540e\u4e5f\u83ab\u8981\u9047\u89c1\u624d\u597d\u3002\u201d \u3000\u3000\u6240\u4ee5\u90ed\u5927\u8def\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u4f46\u8d70\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u73b0\u5728\uff0c\u4ed6\u8eab\u4e0a\u867d\u7136\u8fd8\u4f69\u7740\u5251\uff0c\u8863\u670d\u867d\u7136\u8fd8\u662f\u5f88\u5149\u9c9c\uff0c\u4f46\u5927\u767d\u9a6c\u5df2\u6ca1\u6709 \u4e86\uff0c\u5269\u4e0b\u7684\u51e0\u4e24\u94f6\u5b50\uff0c\u975e\u4f46\u4e0d\u80fd\u8ba9\u4ed6\u518d\u4f4f\u6700\u597d\u7684\u5ba2\u6808\uff0c\u4e0a\u6700\u597d\u7684\u9986\u5b50\uff0c\u5c31\u7b97\u5403\u9992\u5934\uff0c\u7761\u5927 \u7095\uff0c\u4e5f\u7ef4\u6301\u4e0d\u4e86\u51e0\u5929\u3002 \u3000\u3000\u90ed\u5927\u8def\u662f\u4e0d\u662f\u4e5f\u4f1a\u89c9\u5f97\u6709\u4e9b\u6050\u614c\uff0c\u6709\u70b9\u96be\u53d7\uff1f \u3000\u3000\u4e0d\u662f\uff0c\u4ed6\u5b8c\u5168\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u50cf\u4ed6\u8fd9\u4e48\u6837\u6709\u672c\u4e8b\u7684\u4eba\uff0c\u8fd8\u6015\u6ca1\u996d\u5403\u5417\uff0c\u90a3\u5c82\u975e\u7b11\u8bdd\uff1f \u3000\u3000\u8fd8\u662f\u627e\u4e86\u5bb6\u6700\u5927\u7684\u9986\u5b50\uff0c\u597d\u9152\u597d\u83dc\uff0c\u75db\u75db\u5feb\u5feb\u7684\u5403\u4e86\u4e00\u987f\u3002 \u3000\u3000\u6bcf\u4e2a\u7537\u4eba\u5403\u4e86\u987f\u597d\u996d\u540e\uff0c\u5fc3\u60c5\u603b\u662f\u7279\u522b\u597d\u7684\uff0c\u4f55\u51b5\u8fd8\u5e26\u7740\u516d\u4e03\u5206\u9152\u610f\uff0c\u5c31\u7b97\u6700\u8ba8\u538c\u7684 \u4eba\uff0c\u5728\u4ed6\u773c\u4e2d\u770b\u6765\u90fd\u4f1a\u53d8\u5f97\u53ef\u7231\u591a\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u5c06\u5269\u4e0b\u6765\u7684\u94f6\u5b50\u5168\u90fd\u7ed9\u4e86\u90a3\u5f88\u53ef\u7231\u7684\u5e97\u5c0f\u4e8c\uff0c\u6240\u4ee5\u8d70\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u53e3\u888b\u5c31 \u53d8\u5f97\u548c\u521a\u6d17\u8fc7\u4e00\u6837\uff0c\u6d17\u5f97\u53c8\u5e72\u51c0\u3001\u53c8\u5f7b\u5e95\u3002 \u3000\u3000\u4e0b\u987f\u996d\u5728\u54ea\u91cc\uff1f\u7b80\u76f4\u8fde\u4e00\u70b9\u5f71\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u8fd9\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u8239\u5230\u6865\u5934\u81ea\u7136\u76f4\uff0c\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u73b0\u5728\u552f\u4e00\u91cd\u8981\u7684\u4e8b\u662f\u627e\u4e2a\u5730\u65b9\u8212 \u8212\u670d\u670d\u7761\u4e00\u89c9\u3002 \u3000\u3000\u201c\u660e\u5929\uff0c\u53c8\u662f\u53e6\u5916\u4e00\u5929\u4e86\u3002\u201d\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u5230\u4e86\u660e\u5929\uff0c\u603b\u4f1a\u6709\u529e\u6cd5\u7684\uff0c\u4eca\u5929\u665a\u4e0a\u82e5\u5c31\u4e3a \u660e\u5929\u7684\u4e8b\u62c5\u5fc3\uff0c\u5c82\u975e\u5212\u4e0d\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u5927\u6a21\u5927\u6837\u7684\u8d70\u8fdb\u4e86\u57ce\u91cc\u6700\u597d\u7684\u5ba2\u6808\u3002 \u3000\u3000\u4ed6\u53ea\u5fd8\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u5ba2\u6808\u7684\u95e8\u867d\u7136\u6c38\u8fdc\u662f\u5f00\u7740\u7684\uff0c\u8d70\u8fdb\u53bb\u7684\u65f6\u5019\u867d\u7136\u5f88\u5bb9\u6613\uff0c\u8d70\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5c31\u56f0\u96be\u591a\u4e86\u3002 \u4f60\u888b\u53ea\u5b50\u82e5\u6ca1\u94b1\uff0c\u4eba\u5bb6\u5c31\u4e0d\u4f1a\u8ba9\u4f60\u518d\u5927\u6a21\u5927\u6837\u5730\u8d70\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u4e0d\u4f1a\u6e9c\u6389\uff0c\u4e5f\u4e0d\u4f1a\u6492\u8d56\uff0c\u90a3\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u624d\u6709\u70b9\u7740\u6025\u4e86\uff0c\u5728\u9662\u5b50\u91cc\u515c\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u5ffd\u7136\u53d1\u89c9\u5899\u4e0a\u8d34\u7740\u5f20\u7ea2\u7eb8\u6761\uff0c \u4e0a\u9762\u5199\u7740\uff1a\u201c\u6025\u5f81\u53a8\u5e08\u3002\u201d \u3000\u3000\u4e8e\u662f\u90ed\u5927\u8def\u5c31\u505a\u4e86\u53a8\u5b50\u3002 \u3000\u3000\u505a\u9556\u5934\uff0c\u8fde\u5934\u5e26\u5c3e\uff0c\u4ed6\u603b\u7b97\u8fd8\u5e72\u4e86\u534a\u4e2a\u591a\u6708\u3002 \u3000\u3000\u53a8\u5b50\u4ed6\u53ea\u5e72\u4e86\u53c1\u5929\u3002 \u3000\u3000\u53c1\u5929\u91cc\uff0c\u4ed6\u591a\u7528\u4e86\u4e8c\u5341\u591a\u65a4\u6cb9\uff0c\u6454\u574f\u4e86\u53c1\u5341\u591a\u4e2a\u7897\uff0c\u56db\u5341\u591a\u4e2a\u789f\u5b50\u3002 \u3000\u3000\u522b\u4eba\u5c45\u7136\u5fcd\u8010\u4e0b\u6765\u4e86\uff0c\u56e0\u4e3a\u90ed\u5927\u8def\u70e7\u51fa\u6765\u7684\u51e0\u6837\u83dc\u7684\u786e\u4e0d\u9519\uff0c\u6709\u65f6\u5019\u627e\u4e2a\u597d\u53a8\u5b50\u751a\u81f3\u6bd4 \u627e\u4e2a\u597d\u592a\u592a\u8fd8\u56f0\u96be\u5f97\u591a\uff0c\u76f4\u5230\u90ed\u5927\u8def\u5c06\u4e00\u76d8\u521a\u51fa\u9505\u7684\u7cd6\u918b\u9c7c\u6454\u5230\u5ba2\u4eba\u8138\u4e0a\u53bb\u7684\u65f6\u5019\uff0c\u522b\u4eba\u624d \u771f\u7684\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u90a3\u5ba2\u4eba\u4e5f\u53ea\u4e0d\u8fc7\u5acc\u4ed6\u9c7c\u505a\u7684\u592a\u6de1\uff0c\u8981\u52a0\u70b9\u76d0\u800c\u5df2\uff0c\u90ed\u5927\u8def\u5c31\u5df2\u706b\u5192\u53c1\u4e08\u9ad8\uff0c\u6307\u7740\u4eba\u5bb6\u7684 \u9f3b\u5b50\u5927\u9a82\uff1a\u201c\u4f60\u5403\u8fc7\u7cd6\u918b\u9c7c\u6ca1\u6709\uff1f\u4f60\u5403\u8fc7\u9c7c\u6ca1\u6709\uff1f\u7cd6\u918b\u9c7c\u672c\u6765\u5c31\u4e0d\u80fd\u505a\u5f97\u592a\u54b8\u7684\uff0c\u4f60\u77e5\u4e0d\u77e5 \u9053\uff1f\u201d \u3000\u3000\u5929\u4e0b\u7684\u53a8\u5b50\u82e5\u90fd\u8c61\u4f60\u8fd9\u4e48\u51f6\uff0c\u54ea\u8fd8\u6709\u4eba\u6562\u4e0a\u9986\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u522b\u4eba\u5c31\u7b97\u8fd8\u6562\u7559\u4ed6\uff0c\u4ed6\u81ea\u5df1\u4e5f\u803d\u4e0d\u4e0b\u53bb\u4e86\u3002\u5e72\u4e86\u53c1\u5929\u53a8\u5b50\uff0c\u552f\u4e00\u7684\u6536\u83b7 \u5c31\u662f\u8eab\u4e0a\u591a\u4e86\u5c42\u6cb9\u70df\uff0c\u53e3\u888b\u8fd8\u662f\u7a7a\u7684\u3002 \u3000\u3000\u4f46\u662f\uff0c\u201c\u6b64\u5904\u4e0d\u7559\u4eba\uff0c\u81ea\u6709\u7559\u4eba\u5904\u201d\u3002\u6015\u4ec0\u4e48\uff1f \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u8fd8\u662f\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff0c\u4ed6\u4ec0\u4e48\u4e8b\u90fd\u4f1a\u505a\uff0c\u4ec0\u4e48\u4e8b\u90fd\u80fd\u5e72\uff0c\u4e3a\u4ec0\u4e48\u8981\u5728\u4e4e\uff1f \u3000\u3000\u95ee\u9898\u662f\uff0c\u5e72\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u5f00\u59cb\u60f3\uff0c\u60f3\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u4f1a\u505a\u7684\u4e8b\uff0c\u5927\u591a\u6570\u90fd\u662f\u82b1\u94b1\u7684\u4e8b\u2014\u2014\u9a91\u9a6c\u3001\u559d \u9152\u3001\u8d4f\u82b1\u3001\u884c\u4ee4\uff0c\u8fd9\u79cd\u4e8b\u80fd\u8d5a\u5f97\u5230\u534a\u6587\u94b1\u4e48\uff1f \u3000\u3000\u5e78\u597d\u8fd8\u6709\u4e00\u4e24\u6837\u80fd\u8d5a\u94b1\u7684\uff0c\u8b6c\u5982\u8bf4\uff0c\u5356\u5531\u3002 \u3000\u3000\u4ee5\u524d\u4ed6\u5531\u66f2\u7684\u65f6\u5019\uff0c\u522b\u4eba\u5e38\u5e38\u4f1a\u62cd\u70c2\u5df4\u638c\uff0c\u542c\u51fa\u8033\u6cb9\uff0c\u8fd8\u6709\u4eba\u95ee\u4ed6\uff1a\u662f\u4e0d\u662f\u5728\u5a18\u80ce\u91cc\u5c31 \u5df2\u5b66\u4f1a\u5531\u4e86\uff1f \u3000\u3000\u4e5f\u6709\u4eba\u8bf4\uff1a\u51ed\u4ed6\u7684\u55d3\u5b50\uff0c\u51ed\u4ed6\u5bf9\u4e50\u66f2\u7684\u4fee\u517b\uff0c\u82e5\u662f\u771f\u7684\u53bb\u5356\u5531\uff0c\u522b\u7684\u90a3\u4e9b\u5356\u5531\u7684\u4eba\u4e00\u5b9a \u6ca1\u6709\u996d\u5403\u3002 \u3000\u3000\u90ed\u5927\u8def\u867d\u4e0d\u613f\u62a2\u522b\u4eba\u7684\u996d\u7897\uff0c\u600e\u5948\u809a\u5b50\u5374\u5df2\u5f00\u59cb\u5728\u5531\u4e86\u2014\u2014\u5531\u7a7a\u57ce\u8ba1\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u627e\u4e86\u5bb6\u81ea\u5df1\u4ece\u672a\u4e0a\u53bb\u8fc7\u7684\u9152\u697c\uff0c\u51c6\u5907\u5356\u5531\u3002 \u3000\u3000\u4e00\u4e0a\u697c\uff0c\u5e97\u5c0f\u4e8c\u4eec\u5c31\u7acb\u523b\u56f4\u4e86\u4e0a\u6765\uff0c\u5012\u8336\u7684\u5012\u8336\uff0c\u9001\u6bdb\u5dfe\u7684\u9001\u6bdb\u5dfe\uff0c\u966a\u7740\u7b11\uff0c\u54c8\u7740\u8170\uff0c \u95ee\u4ed6\uff1a\u201c\u5927\u7237\u4eca\u5929\u60f3\u5403\u70b9\u4ec0\u4e48\uff1f\u559d\u70b9\u4ec0\u4e48\uff1f\u4eca\u5929\u5c0f\u5e97\u7684\u9c7c\u662f\u7279\u5730\u4ece\u6c5f\u5357\u5feb\u9a6c\u634e\u6765\u7684\uff0c\u8981\u4e0d\u8981 \u6d3b\u6740\u4e00\u6761\u6765\u914d\u53c1\u5341\u5e74\u9648\u7684\u7ecd\u5174\u9152\uff1f\u201d \u3000\u3000\u50cf\u90ed\u5927\u8def\u8fd9\u4e48\u6837\u7684\u6c14\u6d3e\u7684\u4eba\uff0c\u5e97\u5c0f\u4e8c\u4e0d\u53bb\u5df4\u7ed3\u4ed6\u53bb\u5df4\u7ed3\u8c01\uff1f \u3000\u3000\u90ed\u5927\u8def\u7684\u8138\u5374\u5df2\u7ea2\u7684\u8c61\u662f\u559d\u8fc7\u53c1\u5341\u65a4\u7ecd\u5174\u9152\u4e86\uff0c\u201c\u6211\u662f\u6765\u5356\u5531\u7684\u201d\uff0c\u8fd9\u53e5\u8bdd\u4ed6\u600e\u4e48\u8fd8\u80fd \u8bf4\u5f97\u51fa\u53e3\uff1f \u3000\u3000\u8fc7\u4e86\u5927\u534a\u5929\uff0c\u4ed6\u624d\u7ed3\u5df4\u7684\u8bf4\u4e86\u53e5\uff1a\u201c\u6211\u662f\u627e\u4eba\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u8c61\u88ab\u4eba\u7528\u97ad\u5b50\u8d76\u7740 \u4f3c\u7684\u4e0b\u4e86\u697c\uff0c\u593a\u95e8\u800c\u51fa\u3002 \u3000\u3000\u90a3\u5f53\u7136\u4e0d\u80fd\u602a\u90a3\u4e9b\u5e97\u5c0f\u4e8c\uff0c\u53ea\u602a\u4ed6\u81ea\u5df1\u65e0\u8bba\u600e\u4e48\u770b\u4e5f\u4e0d\u8c61\u662f\u4e2a\u5356\u5531\u7684\u3002 \u3000\u3000\u201c\u5509\uff0c\u539f\u6765\u4e00\u4e2a\u4eba\u8c8c\u76f8\u957f\u5f97\u592a\u597d\uff0c\u6709\u65f6\u4e5f\u5f88\u5403\u4e8f\u7684\uff0c\u4e5f\u8bb8\u6211\u957f\u7684\u4e11\u4e9b\u53cd\u800c\u597d\u4e9b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u867d\u7136\u662f\u5728\u53f9\u7740\u6c14\uff0c\u5374\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7acb\u523b\u8981\u53bb\u7167\u7167\u955c\u5b50\u3002 \u3000\u3000\u5356\u5531\u4e5f\u5356\u4e0d\u6210\uff0c\u5e72\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u201c\u8001\u5929\u7ed9\u4e86\u6211\u8fd9\u4e48\u6837\u4e00\u53cc\u7075\u5de7\u7684\u624b\uff0c\u6211\u603b\u6709\u4e8b\u53ef\u505a\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5bf9\u81ea\u5df1\u7684\u624b\u4e00\u5411\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u4ed6\u770b\u7740\u81ea\u5df1\u7ec6\u957f\u800c\u6709\u529b\u7684\u624b\u6307\uff0c\u5fc3\u91cc\u5ffd\u7136\u60f3\u8d77\u4e86\u4e00\u4e9b\u5df2\u5728\u6c5f\u6e56\u4e2d\u6d41\u4f20\u4e86\u5f88\u4e45\u7684\u6545\u4e8b\u3002 \u3000\u3000\u201c\u5bf9\uff0c\u5356\u827a\uff0c\u5c31\u5728\u8857\u5934\u5356\u827a\uff0c\u51ed\u6211\u8fd9\u8eab\u6b66\u529f\uff0c\u8fd8\u6015\u6ca1\u6709\u4eba\u8d4f\u8bc6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5f00\u5fc3\u5f97\u8fde\u809a\u5b50\u997f\u90fd\u5fd8\u4e86\uff0c\u53ea\u602a\u81ea\u5df1\u524d\u4e24\u5929\u4e3a\u4ec0\u4e48\u6ca1\u6709\u60f3\u51fa\u8fd9\u597d\u4e3b\u610f\u3002 \u3000\u3000\u5929\u867d\u5df2\u9ed1\uff0c\u8857\u4e0a\u8fd8\u662f\u5f88\u70ed\u95f9\u3002 \u3000\u3000\u90ed\u5927\u8def\u9009\u4e86\u4e2a\u6700\u70ed\u95f9\u7684\u8857\u89d2\uff0c\u51c6\u5907\u5f00\u59cb\u5356\u827a\u4e86\u3002 \u3000\u3000\u4f46\u662f\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u597d\u8c61\u8fd8\u5f97\u5148\u8bf4\u4e0a\u4e00\u6bb5\u5f00\u573a\u767d\u3002 \u3000\u3000\u8bf4\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u7684\u53e3\u624d\u5e76\u4e0d\u5dee\uff0c\u4e0d\u8be5\u8bf4\u7684\u8bdd\uff0c\u4ed6\u5e38\u5e38\u8bf4\u5f97\u53c8\u673a\u7075\uff0c\u53c8\u4fcf\u76ae\uff0c\u53ea\u4e0d\u8fc7\u7b49\u5230\u8be5\u4ed6\u8bf4\u8bdd \u7684\u65f6\u5019\uff0c\u4ed6\u53cd\u800c\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u8bf4\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u53cd\u6b63\u522b\u4eba\u662f\u6765\u770b\u672c\u4e8b\u7684\uff0c\u4e0d\u662f\u6765\u542c\u6211\u8bf4\u8bdd\u7684\uff1b\u53ea\u8981\u6211\u672c\u4e8b\u4e00\u62ff\u51fa \u6765\uff0c\u8fd8\u6015\u4eba\u4e0d\u56f4\u8fc7\u6765\u770b\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u662f\u90ed\u5927\u8def\u633d\u4e86\u633d\u8896\u5b50\uff0c\u6396\u4e86\u6396\u8863\u89d2\uff0c\u5c31\u5728\u8fd9\u8857\u89d2\u4e0a\u5c06\u4ed6\u751f\u5e73\u6700\u5f97\u610f\u7684\u4e00\u5957\u62f3\u6cd5\u7ec3\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u62f3\u8d77\u65f6\u5982\u731b\u864e\u51fa\u6797\uff0c\u811a\u8e22\u65f6\u5982\u86df\u9f99\u5165\u6d77\uff0c\u62f3\u5f71\u7ffb\u98de\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u5f53\u7136\u662f\u6bcf\u4e00\u62db\u90fd \u6709\u771f\u529f\u592b\u3002 \u3000\u3000\u4f46\u522b\u4eba\u975e\u4f46\u6ca1\u6709\u56f4\u8fc7\u6765\uff0c\u53cd\u800c\u90fd\u8fdc\u8fdc\u7684\u907f\u5f00\u4e86\uff0c\u5c31\u7b97\u6709\u51e0\u4e2a\u80c6\u5b50\u5927\u7684\uff0c\u4e5f\u53ea\u6562\u7ad9\u5728\u5c4b\u89d2 \u5077\u5077\u7684\u77a7\u3002 \u3000\u3000\u201c\u8fd9\u4eba\u5ffd\u7136\u5728\u8857\u4e0a\u6253\u8d77\u62f3\u6765\uff0c\u83ab\u975e\u6709\u4e86\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u7ec3\u5f97\u8fd8\u86ee\u5f97\u610f\uff0c\u540e\u6765\u624d\u6e10\u6e10\u53d1\u73b0\u6709\u70b9\u4e0d\u5bf9\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u7acb\u5373\u604d\u7136\u5927\u609f\u3002 \u3000\u3000\u201c\u6211\u7ec3\u7684\u662f\u771f\u529f\u592b\uff0c\u4e00\u70b9\u82b1\u62f3\u7ee3\u811a\u90fd\u6ca1\u6709\uff0c\u8fd9\u4e9b\u51e1\u592b\u4fd7\u5b50\u5f53\u7136\u770b\u4e0d\u51fa\u597d\u5904\u6765\u3002\u597d\uff0c\u6211\u5c31 \u518d\u7ec3\u70b9\u60ca\u4eba\u7684\u7ed9\u4ed6\u4eec\u77a7\u77a7\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u90ed\u5927\u8def\u7a81\u7136\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u201c\u7830\u201d\u7684\u4e00\u62f3\u5c06\u540e\u9762\u7684\u5899\u6253\u7834\u4e86\u4e2a\u5927\u6d1e\uff0c\u201c\u547c\u201d \u7684\u4e00\u811a\u5c06\u8857\u89d2\u7cfb\u9a6c\u7684\u77f3\u67f1\u5b50\u8fde\u6839\u8e22\u5012\u2014\u2014\u4ed6\u81ea\u5df1\u7684\u88e4\u5b50\u5f53\u7136\u4e5f\u88ab\u8e22\u7834\u4e86\u3002 \u3000\u3000\u53ea\u542c\u4e00\u7247\u60ca\u547c\uff0c\u6ee1\u8857\u7684\u4eba\u7a81\u7136\u5168\u90e8\u843d\u8352\u800c\u9003\uff0c\u6709\u51e0\u5bb6\u5e97\u751a\u81f3\u5c06\u5927\u95e8\u90fd\u4e0a\u4e86\u8d77\u6765\uff0c\u53ea\u56e0\u8857 \u4e0a\u6765\u4e86\u4e2a\u5403\u9519\u836f\u7684\u75af\u5b50\u3002\u8fd9\u5c31\u662f\u90ed\u5927\u8def\u5356\u827a\u7684\u7ecf\u8fc7\uff0c\u4ed6\u7ec3\u4e86\u4e00\u8d9f\u62f3\uff0c\u8fd8\u52a0\u4e00\u62db\u5f00\u5c71\u529f\uff0c\u4e00\u62db \u626b\u5802\u817f\uff0c\u6362\u6765\u7684\u53ea\u4e0d\u8fc7\u662f\u6761\u7834\u88e4\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u6545\u4e8b\u4e3a\u4ec0\u4e48\u4e0d\u8c61\u522b\u7684\u843d\u9b44\u82f1\u96c4\u90a3\u4e48\u597d\u542c\u5462\uff1f \u3000\u3000\u8fd9\u5b9e\u5728\u6ca1\u6cd5\u5b50\uff0c\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u542c\u6765\u5f88\u7f8e\uff0c\u505a\u6765\u5c31\u4e0d\u7f8e\u4e86\u3002 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\uff0c\u90ed\u5927\u8def\u53ea\u6709\u997f\u7740\u809a\u5b50\uff0c\u5728\u7834\u5e99\u7684\u4f9b\u684c\u4e0a\u7761\u4e86\u4e00\u89c9\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u8fd8\u53ef\u4ee5\u4e0a\u6700\u597d\u7684\u9986\u5b50\u5148\u5403\u4e86\u518d\u8bf4\uff0c\u4e0a\u6700\u597d\u7684\u5ba2\u6808\u7761\u4e0b\u518d\u8bf4\uff0c\u4f46\u6211\u4eec\u7684\u82f1\u96c4\u867d\u7136\u6709 \u4e9b\u7cca\u6d82\uff0c\u5374\u51b3\u4e0d\u8d56\u76ae\u3002\u4e22\u4eba\u7684\u4e8b\uff0c\u6b7b\u4e5f\u4e0d\u80af\u505a\u7684\u3002 \u3000\u3000\u201c\u5c31\u7b97\u8981\u505a\u8d3c\uff0c\u4e5f\u5f97\u505a\u5927\u5f3a\u76d7\uff0c\u51b3\u4e0d\u80fd\u505a\u5077\u9e21\u6478\u72d7\u7684\u5c0f\u5077\u3002\u201d \u3000\u3000\u5230\u4e86\u7b2c\u4e8c\u5929\u4e0b\u5348\uff0c\u90ed\u5927\u8def\u5ffd\u7136\u60f3\u5230\u505a\u8d3c\u3002 \u3000\u3000\u8fd9\u5ff5\u5934\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u662f\u4ece\u54ea\u91cc\u6765\u7684\u2014\u2014\u5927\u6982\u662f\u4ece\u4ed6\u90a3\u5df2\u5feb\u88ab\u78e8\u7a7f\u4e86\u7684\u809a\u5b50\u91cc\u6765\u7684\u3002 \u3000\u3000\u201c\u505a\u8d3c\u4e5f\u5e76\u4e0d\u592a\u574f\uff0c\u6709\u5f88\u591a\u52ab\u5bcc\u6d4e\u8d2b\u7684\u4e49\u76d7\uff0c\u4ed6\u4eec\u7684\u6545\u4e8b\u5c82\u975e\u4e5f\u4e00\u6837\u5728\u6c5f\u6e56\u4e2d\u6d41\u82b3\u5343\u53e4 \u4e48\uff1f\u201d \u3000\u3000\u4e8e\u662f\u90ed\u5927\u8def\u51b3\u5b9a\u505a\u5f3a\u76d7\uff0c\u5f53\u7136\u662f\u505a\u4e2a\u4e49\u76d7\u3001\u5927\u76d7\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u51b3\u5b9a\u53ea\u8bb8\u6210\u529f\uff0c\u4e0d\u8bb8\u5931\u8d25\u3002 \u3000\u3000\u201c\u8981\u505a\u597d\u4e00\u4ef6\u4e8b\uff0c\u8fd8\u672a\u5f00\u59cb\u65f6\uff0c\u5c31\u4e00\u5b9a\u5148\u5f97\u8ba1\u5212\u5468\u5bc6\u3002\u201d \u3000\u3000\u8981\u505a\u4e2a\u8d3c\uff0c\u8be5\u8ba1\u5212\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u7b2c\u4e00\uff0c\u5f53\u7136\u662f\u8981\u627e\u4e2a\u5408\u9002\u7684\u5bf9\u8c61\u4e0b\u624b\uff0c\u8fd9\u4eba\u4e00\u5b9a\u8981\u5f88\u6709\u94b1\uff0c\u800c\u4e14\u4e3a\u5bcc\u4e0d\u4ec1\uff0c\u5982\u679c\u662f\u8d2a\u5b98 \u6c61\u540f\u66f4\u597d\u3002 \u3000\u3000\u4f60\u62a2\u4e86\u8fd9\u79cd\u4eba\u7684\u94b1\uff0c\u522b\u4eba\u975e\u4f46\u4e0d\u4f1a\u602a\u4f60\uff0c\u53cd\u800c\u4f1a\u62cd\u624b\u79f0\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u6253\u8d77\u7cbe\u795e\uff0c\u5f00\u59cb\u56db\u4e0b\u627e\uff0c\u627e\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u627e\u5230\u5bf9\u8c61\u3002 \u3000\u3000\u90a3\u662f\u4e00\u680b\u5750\u843d\u5728\u5c71\u8170\u4e0a\u7684\u623f\u5b50\uff0c\u623f\u5b50\u5f88\u5927\uff0c\u5efa\u7b51\u5f97\u5f88\u5802\u7687\u3002 \u3000\u3000\u90a3\u8868\u793a\u623f\u4e3b\u4e00\u5b9a\u5f88\u6709\u94b1\u3002 \u3000\u3000\u623f\u5b50\u8ddd\u79bb\u5e02\u533a\u5f88\u8fdc\uff0c\u5f88\u504f\u50fb\uff0c\u9644\u8fd1\u7b80\u76f4\u53ef\u8bf4\u662f\u8352\u65e0\u4eba\u70df\uff0c\u8ddd\u79bb\u8fd9\u623f\u5b50\u6700\u8fd1\u7684\u5730\u65b9\uff0c\u5c31\u662f \u575f\u573a\u3002 \u3000\u3000\u8fd9\u8868\u793a\u623f\u4e3b\u4e00\u5b9a\u4e0d\u662f\u5149\u660e\u6b63\u5927\u7684\u4eba\uff0c\u5149\u660e\u6b63\u5927\u7684\u4eba\u51b3\u4e0d\u4f1a\u4f4f\u5728\u8fd9\u79cd\u5730\u65b9\u3002 \u3000\u3000\u6240\u6709\u7684\u6761\u4ef6\u90fd\u5f88\u9002\u5408\uff0c\u73b0\u5728\u53ea\u7b49\u5230\u5408\u9002\u7684\u65f6\u5019\uff0c\u5c31\u53bb\u4e0b\u624b\u3002 \u3000\u3000\u6700\u9002\u5408\u7684\u65f6\u5019\u81ea\u7136\u662f\u665a\u4e0a\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u7b49\u4e0d\u53ca\u4e86\uff0c\u9ec4\u660f\u65f6\u5c31\u95ef\u8fdb\u4e86\u8fd9\u623f\u5b50\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u773c\u770b\u5230\u7684\u4e1c\u897f\uff0c\u662f\u5f20\u5e8a\u3002 \u3000\u3000\u4e00\u5f20\u5f88\u5927\u5f88\u5927\u3001\u5f88\u8212\u670d\u5f88\u8212\u670d\u7684\u5e8a\u3002 \u3000\u3000\u5e8a\u4e0a\u8eba\u7740\u4e2a\u4eba\u3002 \u3000\u3000\u9664\u6b64\u5916\uff0c\u4ed6\u518d\u4e5f\u6ca1\u770b\u5230\u522b\u7684\u3002 \u3000\u3000\u8fd9\u623f\u5b50\u5f88\u5927\uff0c\u5efa\u7b51\u5f88\u5802\u7687\uff0c\u524d\u524d\u540e\u540e\uff0c\u81f3\u5c11\u4e5f\u6709\u53c1\u5341\u95f4\u623f\uff0c\u6700\u5927\u7684\u4e00\u95f4\u623f\u5927\u7684\u53ef\u4ee5\u540c\u65f6 \u6446\u4e0b\u5341\u51e0\u684c\u9152\u3002 \u3000\u3000\u4f46\u524d\u524d\u540e\u540e\u51e0\u5341\u95f4\u5c4b\u5b50\u5168\u662f\u7a7a\u7684\u3002\u8fde\u53a8\u623f\u90fd\u662f\u7a7a\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8eba\u5728\u5e8a\u4e0a\u7684\u90a3\u4e2a\u4eba\u5e76\u6ca1\u6709\u7761\u7740\uff0c\u773c\u775b\u4e00\u76f4\u7741\u5f97\u5f88\u5927\uff0c\u53ef\u662f\u5c3d\u7ba1\u4ed6\u524d\u524d\u540e\u540e\u7684\u8dd1\uff0c\u524d\u524d\u540e \u540e\u7684\u627e\uff0c\u8fd9\u4eba\u59cb\u7ec8\u6ca1\u6709\u7406\u4ed6\u3002 \u3000\u3000\u5230\u540e\u6765\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u51b2\u5230\u8fd9\u4eba\u5e8a\u524d\uff0c\u60f3\u95ee\u95ee\u4ed6\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u8fd9\u4eba\u53cd\u800c\u5148\u95ee\uff1a\u201c\u627e\u5230\u4ec0\u4e48\u503c\u94b1\u7684\u4e1c\u897f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u6447\u6447\u5934\u3002 \u3000\u3000\u8fd9\u4eba\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u627e\u4e0d\u5230\u7684\uff0c\u6211\u5df2\u7ecf\u627e\u4e86\u53c1\u5929\uff0c\u8fde\u6700\u540e\u4e00\u4e2a\u7834\u94c1\u9505 \u90fd\u88ab\u6211\u62ff\u53bb\u6362\u70e7\u997c\u4e86\u3002\u4f60\u82e5\u8fd8\u80fd\u627e\u5230\u522b\u7684\uff0c\u90a3\u672c\u4e8b\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u4ed6\u957f\u5f97\u672c\u4e0d\u7b97\u96be\u770b\uff0c\u53ea\u4e0d\u8fc7\u663e\u5f97\u9762\u9ec4\u808c\u7626\uff0c\u8fde\u8bf4\u8bdd\u90fd\u662f\u6709\u6c14\u65e0\u529b\u7684\u6837\u5b50\uff0c\u7684\u786e\u8c61\u662f\u5df2\u997f \u4e86\u597d\u51e0\u5929\u3002 \u3000\u3000\u4f46\u4ed6\u7761\u7684\u8fd9\u5f20\u5e8a\uff0c\u5374\u4e0d\u6298\u4e0d\u6263\u662f\u5f20\u597d\u5e8a\u3002 \u3000\u3000\u8fd9\u7a7a\u5c4b\u5b50\u91cc\u600e\u4e48\u8fd8\u4f1a\u6709\u8fd9\u4e48\u6837\u7684\u4e00\u5f20\u597d\u5e8a\uff1f\u8fd9\u4eba\u7761\u5728\u5e8a\u4e0a\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u8bf4\u8d77\u8fd9\u5e84\u4e3b\uff0c\u53ef\u771f\u662f\u5927\u5927\u6709\u540d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\u540d\uff1f\u6709\u4ec0\u4e48\u540d\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u542c\u89c1\u8fc7\u5bcc\u8d35\u5c71\u5e84\u8fd9\u540d\u5b57\u6ca1\u6709\uff1f\u8fd9\u91cc\u5c31\u662f\u5bcc\u8d35\u5c71\u5e84\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5bcc\u8d35\u5c71\u5e84\uff1f\u8fd9\u89c1\u9b3c\u7684\u5730\u65b9\u5c45\u7136\u53eb\u5bcc\u8d35\u5c71\u5e84\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u80d6\u5b50\u65e2\u7136\u53ef\u80fd\u53d8\u5f97\u5f88\u7626\uff0c\u5bcc\u8d35\u5c71\u5e84\u4e5f\u53ef\u80fd\u53d8\u5f97\u5f88\u7a77\uff0c\u8fd9\u53c8\u6709\u4ec0 \u4e48\u597d\u5e0c\u5947\u7684\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u53c8\u662f\u4f55\u8bb8\u4eba\u4e5f\uff1f\u803d\u5728\u8fd9\u79cd\u9b3c\u5730\u65b9\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u6e05\u4e86\u6e05\u5589\u5499\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u803d\u5728\u8fd9\u91cc\u803d\u5728\u54ea\u91cc\uff1f\u6211\u5c31\u662f\u5bcc\u8d35\u5c71\u5e84\u7b2c\u4e03\u4ee3\u7684\u5e84\u4e3b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u773c\u775b\u4e00\u76f4\u76ef\u7740\u4ed6\u624b\u91cc\u7684\u5251\uff0c\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5251\u770b\u6765\u5012\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u672c\u6765\u5c31\u4e0d\u9519\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u770b\u6765\u603b\u8fd8\u503c\u597d\u51e0\u4e24\u94f6\u5b50\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u53eb\u4e86\u8d77\u6765\u9053\uff1a\u201c\u597d\u51e0\u4e24\uff1f\u4f60\u8bc6\u8d27\u4e0d\u8bc6\u8d27\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u53f9\u51fa\u53e3\u6c14\uff0c\u4f60\u53eb\u6211\u53bb\u628a\u81ea\u5df1\u7684\u5251\u5f53\u4e86\uff0c\u4e70\u9152\u8089\u56de\u6765\u9001\u7ed9\u4f60\u5403\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u603b\u7b97\u542c\u61c2\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u60f3\u6765\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u8fd9\u5f53\u7136\u77e5\u9053\uff0c\u4f60\u662f\u60f3\u6765\u62a2\u94b1\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u773c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\u6211\u662f\u5f3a\u76d7\uff0c\u8fd8\u60f3\u5728\u6211\u8eab\u4e0a\u6253\u4e3b\u610f\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u867d\u662f\u5f3a\u76d7\uff0c\u6211\u5374\u662f\u7a77\u9b3c\uff0c\u5f3a\u76d7\u9047\u89c1\u7a77\u9b3c\uff0c\u4e5f\u53ea\u6709\u81ea\u8ba4\u6666\u6c14\u3002\u201d\u90ed\u5927\u8def\u77a7 \u7740\u4ed6\uff0c\u5ffd\u7136\u53d1\u89c9\u8fd9\u4eba\u7b11\u5f97\u5f88\u53ef\u7231\uff0c\u751a\u81f3\u5f88\u59a9\u5a9a\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u60f3\u5728\u6211\u8eab\u4e0a\u6253\u4e3b\u610f\uff0c\u81f3\u5c11\u4e5f\u8be5\u81ea\u5df1\u628a\u6211\u8fd9\u67c4\u5251\u62ff\u53bb \u5f53\uff0c\u81ea\u5df1\u53bb\u4e70\u9152\u56de\u6765\u7ed9\u6211\u5403\u624d\u5bf9\u5440\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u8981\u505a\u597d\u4eba\u5c31\u505a\u5230\u5e95\uff0c\u8fd8\u662f\u4f60\u8d70\u4e00\u8d9f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u8fde\u52a8\u90fd\u61d2\u5f97\u52a8\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u60f3\uff0c\u6211\u82e5\u662f\u4e0d\u61d2\uff0c\u53c8\u600e\u4e48\u4f1a\u7a77\u6210\u8fd9\u6837\u5b50\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b2c\u53c1\u6b21\u6014\u4f4f\u4e86\u3002\u4ed6\u4ee5\u524d\u5b9e\u5728\u4e5f\u6ca1\u89c1\u8fc7\u8fd9\u6837\u7684\u4eba\uff0c\u4ed6\u5b9e\u5728\u4e5f\u62ff\u8fd9\u4eba\u6ca1\u6cd5\u5b50\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u771f\u7684\u5c06\u5251\u6362\u4e86\u9152\u8089\u56de\u6765\u3002 \u3000\u3000\u4e00\u6761\u9e2d\u817f\u3001\u534a\u65a4\u9152\u4e0b\u4e86\u809a\uff0c\u8fd9\u4eba\u624d\u4ece\u5e8a\u4e0a\u5750\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5403\u4e86\u4f60\u7684\u9152\uff0c\u5374\u8fde\u4f60\u7684 \u540d\u5b57\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u53eb\u90ed\u5927\u8def\uff0c\u5927\u65b9\u7684\u5927\uff0c\u4e0a\u8def\u7684\u8def\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u5927\u8def\u2014\u2014\u4f60\u8fd9\u4eba\u5012\u771f\u7684\u540d\u526f\u5176\u5b9e\uff0c\u771f\u7684\u5f88\u5927\u8def\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u53eb\u738b\u52a8\uff0c\u5e1d\u738b\u7684\u738b\uff0c\u52a8\u5982\u8131\u5154\u7684\u52a8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u5f88\u4e45\uff0c\u7a81\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u4f60\u5b9e\u5728\u5e94\u8be5\u53eb\u738b\u4e0d\u52a8\u3002\u201d (\u4e09) \u3000\u3000\u53ea\u6709\u6b7b\u4eba\u624d\u5b8c\u5168\u4e0d\u52a8\u3002 \u3000\u3000\u738b\u52a8\u867d\u4e0d\u662f\u6b7b\u4eba\uff0c\u4f46\u52a8\u7684\u6bd4\u6b7b\u4eba\u4e5f\u591a\u4e0d\u4e86\u591a\u5c11\u3002 \u3000\u3000\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u7684\u65f6\u5019\uff0c\u4ed6\u51b3\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u52a8\u7684\u65f6\u5019\uff0c\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u8981\u4ed6\u52a8\u3002 \u3000\u3000\u6cb9\u74f6\u5b50\u82e5\u5728\u9762\u524d\u5012\u4e86\uff0c\u4efb\u4f55\u4eba\u90fd\u4f1a\u4f38\u624b\u53bb\u6276\u8d77\u6765\u7684\uff0c\u738b\u52a8\u5374\u4e0d\u52a8\u3002\u5929\u4e0a\u82e5\u7a81\u7136\u6389\u4e0b\u4e2a\u5927 \u5143\u5b9d\uff0c\u65e0\u8bba\u8c01\u90fd\u4e00\u5b9a\u4f1a\u6361\u8d77\u6765\u7684\uff0c\u738b\u52a8\u4e5f\u4e0d\u52a8\uff0c\u751a\u81f3\u8fde\u4e16\u4e0a\u6700\u7f8e\u7684\u5973\u4eba\u8131\u5f97\u5149\u5149\u7684\u5750\u5728\u4ed6\u6000 \u91cc\uff0c\u4ed6\u8fd8\u662f\u4e0d\u4f1a\u52a8\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u6709\u52a8\u7684\u65f6\u5019\uff0c\u800c\u4e14\u4e0d\u52a8\u5219\u5df2\uff0c\u4e00\u52a8\u5c31\u5f88\u60ca\u4eba\u3002\u6709\u4e00\u6b21\u4ed6\u5728\u7247\u523b\u5185\u4e0d\u505c\u5730\u7ffb\u4e86\u53c1\u767e \u516b\u5341\u4e8c\u4e2a\u8ddf\u6597\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u4e8b\u60f3\u8ba9\u4e00\u4e2a\u521a\u6b7b\u4e86\u6bcd\u4eb2\u7684\u5c0f\u5b69\u5b50\u7b11\u4e00\u7b11\u3002 \u3000\u3000\u6709\u4e00\u6b21\u4ed6\u5728\u4e24\u5929\u4e24\u591c\u95f4\u8d76\u4e86\u4e00\u5343\u56db\u767e\u4e94\u5341\u91cc\u8def\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u53bb\u89c1\u4e00\u4e2a\u670b\u53cb\u7684\u6700\u540e\u4e00 \u9762\u3002 \u3000\u3000\u4ed6\u90a3\u670b\u53cb\u65e9\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u6709\u4e00\u6b21\u4ed6\u5728\u53c1\u5929\u53c1\u591c\u4e2d\uff0c\u8e0f\u5e73\u4e86\u56db\u5ea7\u5c71\u5be8\uff0c\u548c\u4e24\u767e\u4e03\u5341\u56db\u4e2a\u4eba\u4ea4\u8fc7\u624b\uff0c\u6740\u4e86\u5176\u4e2d\u4e00\u767e\u96f6 \u53c1\u4e2a\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u90a3\u4f19\u5f3a\u76d7\u6740\u4e86\u8d75\u5bb6\u6751\u7684\u8d75\u8001\u5148\u751f\u8001\u4e24\u53e3\u5b50\uff0c\u8fd8\u62a2\u8d70\u4e86\u4ed6\u4eec\u7684\u53c1\u4e2a\u5973\u513f\u3002 \u3000\u3000\u8d75\u8001\u5148\u751f\u548c\u90a3\u53c1\u4f4d\u59d1\u5a18\u4ed6\u6839\u672c\u5168\u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u82e5\u6709\u4eba\u6b3a\u8d1f\u4e86\u4ed6\uff0c\u751a\u81f3\u5410\u53e3\u75f0\u5728\u4ed6\u8138\u4e0a\uff0c\u4ed6\u90fd\u51b3\u4e0d\u4f1a\u52a8\u3002\u4f60\u8bf4\u4ed6\u5947\u602a\uff0c\u4ed6\u7684\u786e\u6709\u70b9\u5947 \u602a\u3002 \u3000\u3000\u4f60\u8bf4\u4ed6\u61d2\uff0c\u4ed6\u7684\u786e\u61d2\u5f97\u51fa\u5947\uff0c\u61d2\u5f97\u79bb\u8c31\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u5c45\u7136\u548c\u90ed\u5927\u8def\u4ea4\u4e0a\u4e86\u670b\u53cb\u3002\u8c61\u4ed6\u4eec\u8fd9\u4e48\u6837\u4e24\u4e2a\u4eba\u51d1\u5230\u4e00\u8d77\uff0c\u4ed6\u4eec\u82e5\u4e0d\u7a77\uff0c\u4f60\u8bf4 \u8c01\u7a77\uff1f \u3000\u3000\u4ed6\u4eec\u867d\u7136\u7a77\uff0c\u5374\u7a77\u5f97\u5feb\u4e50\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u65e2\u6ca1\u6709\u5bf9\u4e0d\u8d77\u522b\u4eba\uff0c\u4e5f\u6ca1\u6709\u5bf9\u4e0d\u8d77\u81ea\u5df1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u65e2\u4e0d\u6028\u5929\uff0c\u4e5f\u4e0d\u5fe7\u4eba\uff0c\u65e0\u8bba\u4ed6\u4eec\u9047\u5230\u591a\u4e48\u5927\u7684\u56f0\u96be\uff0c\u591a\u4e48\u5927\u7684\u632b\u6298\uff0c\u90fd\u4e0d\u4f1a\u4ee4 \u4ed6\u4eec\u4e27\u5931\u52c7\u6c14\u3002\u4ed6\u4eec\u4e0d\u6015\u514b\u670d\u56f0\u96be\u65f6\u6240\u7ecf\u5386\u7684\u8270\u82e6\uff0c\u5374\u61c2\u5f97\u4eab\u53d7\u514b\u670d\u56f0\u96be\u540e\u90a3\u79cd\u6210\u529f\u7684\u6b22 \u6109\u3002 \u3000\u3000\u5c31\u7b97\u5931\u8d25\u4e86\uff0c\u4ed6\u4eec\u4e5f\u51b3\u4e0d\u6c14\u9981\uff0c\u66f4\u4e0d\u7070\u5fc3\u3002 \u3000\u3000\u4ed6\u4eec\u61c2\u5f97\u751f\u547d\u662f\u53ef\u8d35\u7684\uff0c\u4e5f\u61c2\u5f97\u5982\u4f55\u53bb\u4eab\u53d7\u751f\u547d\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u7684\u751f\u547d\u6c38\u8fdc\u662f\u591a\u5f69\u591a\u59ff\u3002\u8fd9\u4e00\u751f\u4e2d\uff0c\u4ed6\u4eec\u505a\u4e86\u8bb8\u591a\u51fa\u4eba\u610f\u5916\u3001\u4ee4\u4eba\u7edd\u5012\u7684\u4e8b\uff0c \u4f60\u4e5f\u8bb8\u4f1a\u8ba4\u4e3a\u4ed6\u4eec\u505a\u7684\u4e8b\u5f88\u611a\u8822\u3001\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u4f46\u4f60\u5374\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u4ed6\u4eec\u505a\u7684\u4e8b\u522b\u4eba\u90fd\u505a\u4e0d\u5230\u3002 \u3000\u3000\u4f60\u4e5f\u505a\u4e0d\u5230\u3002\u6240\u4ee5\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u559c\u6b22\u542c\u4ed6\u4eec\u7684\u6545\u4e8b\u3002 \u71d5\u4e03\u4e0e\u8682\u8681>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u71d5\u4e03\u4e0e\u8682\u8681 (\u4e00) \u3000\u3000\u6709\u90ed\u5927\u8def\u548c\u738b\u52a8\u8fd9\u4e48\u6837\u4e24\u4e2a\u4eba\uff0c\u505a\u51fa\u6765\u7684\u4e8b\u5df2\u7ecf\u591f\u53eb\u4eba\u77a7\u8001\u534a\u5929\u7684\u4e86\uff0c\u600e\u4e48\u80fd\u518d\u52a0\u4e0a\u4e2a \u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u4e00\u4e2a\u4eba\u505a\u51fa\u6765\u7684\u4e8b\uff0c\u5df2\u7ecf\u6bd4\u522b\u4eba\u53c1\u767e\u4e2a\u52a0\u8d77\u6765\u90fd\u8981\u7cbe\u5f69\uff0c\u600e\u4e48\u80fd\u518d\u52a0\u4e0a\u90ed\u5927\u8def\uff1f\u518d \u52a0\u4e0a\u738b\u52a8\uff1f \u3000\u3000\u4f46\u8001\u5929\u504f\u504f\u8981\u53eb\u4ed6\u4eec\u53c1\u4e2a\u4eba\u51d1\u5728\u4e00\u8d77\uff0c\u4f60\u8bf4\u8fd9\u600e\u4e48\u5f97\u4e86\u3002 (\u4e8c) \u3000\u3000\u90ed\u5927\u8def\u548c\u738b\u52a8\u5e76\u4e0d\u662f\u5929\u5929\u90fd\u7a77\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u7a77\u7684\uff0c\u5076\u5c14\u4ed6\u4eec\u4e5f\u4f1a\u6709\u4e0d\u7a77\u7684\u65f6\u5019\uff0c\u53ea\u4e0d\u8fc7 \u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u4ec0\u4e48\u65f6\u5019\u4f1a\u4e0d\u7a77\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6\u4eec\u94b1\u662f\u4ece\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u94b1\u603b\u662f\u6765\u5f97\u51fa\u4e4e\u610f\u5916\uff0c\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u6709\u70b9\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u4eec\u82b1\u94b1\u66f4\u82b1\u7684\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u5df2\u7ecf\u5feb\u79cb\u5929\u4e86\uff0c\u201c\u5bcc\u8d35\u5c71\u5e84\u201d\u540e\u9662\u91cc\u7684\u6811\u4e0a\uff0c\u5ffd\u7136\u7ed3\u51fa\u4e86\u6ee1\u6811\u53c8\u751c\u53c8\u5927\u7684\u68a8\u5b50\uff0c\u6458\u4e0b\u6765 \u8db3\u8db3\u53ef\u4ee5\u88c5\u51e0\u5341\u7bd3\uff0c\u5356\u51fa\u53bb\u5c45\u7136\u5356\u4e86\u4e8c\u53c1\u5341\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u68a8\u662f\u81ea\u5df1\u4ece\u6811\u4e0a\u957f\u51fa\u6765\u540e\uff0c\u5c31\u6709\u4eba\u6765\u95ee\u4ef7\u94b1\uff0c\u81ea\u5df1\u4ece\u6811\u4e0a\u6458\u8d70\uff0c\u4ece\u5934\u5230\u5c3e\u90fd\u4e0d\u7528\u4ed6\u4eec\u51fa \u4e00\u5206\u529b\uff0c\u5e2e\u4e00\u5206\u5fd9\u3002 \u3000\u3000\u8fd9\u94b1\u7b80\u76f4\u5c31\u597d\u8c61\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\u7684\uff0c\u5f53\u7136\u4e00\u5b9a\u8981\u5e86\u795d\u5e86\u795d\u3002 \u3000\u3000\u8981\u5e86\u795d\uff0c\u5f53\u7136\u4e0d\u80fd\u6ca1\u6709\u9152\uff0c\u6709\u4e86\u9152\uff0c\u5f53\u7136\u66f4\u4e0d\u80fd\u6ca1\u6709\u8089\u3002 \u3000\u3000\u201c\u7a7f\u5a01\u98ce\uff0c\u8d4c\u5bf9\u51b2\uff0c\u5ad6\u6210\u7a7a\u201d\uff0c\u53ea\u6709\u201c\u5403\u201d\u6700\u5b9e\u60e0\uff0c\u8fd9\u662f\u738b\u52a8\u7684\u539f\u5219\uff0c\u4e5f\u662f\u4ed6\u6700\u5927\u7684\u4eab \u53d7\u3002 \u3000\u3000\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u4ed6\u603b\u662f\u8eba\u7740\u5403\u3001\u7761\u7740\u5403\u3001\u5403\u5f97\u9ad8\u5174\u7684\u65f6\u5019\uff0c\u624d\u5750\u8d77\u6765\uff0c\u4f46\u4e00\u5403\u7d2f\u4e86\uff0c\u5c31\u53c8 \u8981\u8eba\u4e0b\u53bb\u3002\u8eba\u4e0b\u53bb\u518d\u5403\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u90a3\u5f20\u5e8a\u7b80\u76f4\u6bd4\u53a8\u623f\u91cc\u7684\u684c\u5b50\u8fd8\u6cb9\u817b\uff0c\u4f60\u65e0\u8bba\u5f80\u4ec0\u4e48\u5730\u65b9\u53bb\u968f\u624b\u4e00\u6478\uff0c\u603b\u4f1a\u6478\u51fa\u4e00 \u4e24\u5757\u5403\u5269\u7684\u8089\uff0c\u53c1\u56db\u6839\u8fd8\u6ca1\u5543\u5b8c\u7684\u8089\u9aa8\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u867d\u4e0d\u662f\u5f88\u7231\u5e72\u51c0\u7684\u4eba\uff0c\u4f46\u5b81\u53ef\u7761\u5730\u94fa\uff0c\u4e5f\u4e0d\u6562\u8eba\u5728\u4ed6\u5e8a\u4e0a\u3002 \u3000\u3000\u738b\u52a8\u5c31\u4e50\u5f97\u809a\u5b50\u4eab\u53d7\u4e00\u5f20\u5e8a\uff0c\u8fd9\u5f20\u5e8a\u4e0d\u4f46\u662f\u4ed6\u7761\u89c9\u7684\u5730\u65b9\uff0c\u4e5f\u662f\u4ed6\u7684\u5ba2\u5385\u3001\u4ed6\u7684\u82b1\u56ed\u3001 \u4ed6\u7684\u996d\u684c\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u4ed6\u8fd8\u80fd\u8eba\u5728\u5e8a\u4e0a\u559d\u9152\uff0c\u5148\u628a\u9152\u74f6\u5b50\u5bf9\u7740\u5634\uff0c\u7136\u540e\u201c\u5495\u561f\u5495\u561f\u201d\u4e00\u53e3\u6c14\u559d\u4e0b \u53bb\uff0c\u51b3\u4e0d\u4f1a\u6709\u534a\u6ef4\u9152\u6f0f\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u5bf9\u4ed6\u8fd9\u624b\u53ef\u4f69\u670d\u6781\u4e86\uff0c\u81ea\u5df1\u4e5f\u60f3\u5b66\u5b66\uff0c\u53c8\u6709\u70b9\u72b9\u7591\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8eba\u7740\u559d\u9152\u4e5f \u80fd\u559d\u5f97\u4e0b\u53bb\u4e48\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u5f53\u7136\u559d\u5f97\u4e0b\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u4ece\u9f3b\u5b50\u91cc\u55b7\u51fa\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u51b3\u4e0d\u4f1a\uff0c\u5c31\u7b97\u5934\u4e0b\u811a\u4e0a\u540a\u7740\u559d\uff0c\u4e5f\u4e0d\u4f1a\u4ece\u9f3b\u5b50\u91cc\u55b7\u51fa\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u8bd5\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u8fde\u5750\u90fd\u61d2\u5f97\u5750\uff0c\u600e\u4e48\u80af\u628a\u81ea\u5df1\u540a\u8d77\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u6240\u4ee5\u90ed\u5927\u8def\u5c31\u628a\u81ea\u5df1\u540a\u4e86\u8d77\u6765\uff0c\u7136\u540e\u518d\u5c06\u9152\u74f6\u5bf9\u7740\u5634\uff0c\u6162\u6162\u5730\u4e00\u53e3\u4e00\u53e3\u5f80\u809a\u91cc\u559d\uff0c\u521a\u559d \u4e86\u4e24\u53e3\uff0c\u9152\u5df2\u4ece\u9f3b\u5b50\u91cc\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u770b\u5230\u4e86\u71d5\u4e03\u2014\u2014\u5148\u770b\u5230\u4e86\u71d5\u4e03\u7684\u4e00\u53cc\u811a\u3002 \u3000\u3000\u71d5\u4e03\u7684\u811a\u4e5f\u8bb8\u548c\u522b\u4eba\u6ca1\u4ec0\u4e48\u4e24\u6837\uff0c\u4f46\u7a7f\u7684\u4e00\u53cc\u9774\u5b50\u5374\u7279\u522b\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u7a7f\u7684\u9774\u5b50\u662f\u7528\u5c0f\u725b\u76ae\u505a\u7684\uff0c\u624b\u5de5\u6781\u7cbe\u81f4\uff0c\u4e0a\u9762\u8fd8\u5e26\u7740\u82b1\u7eb9\uff0c\u6bd4\u8d77\u585e\u5916\u56de\u56de\u5927\u738b\u7237\u811a\u4e0a \u7a7f\u7684\u9774\uff0c\u4e5f\u6beb\u65e0\u900a\u8272\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u5947\u602a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u8fd9\u53cc\u9774\u5b50\u4ec0\u4e48\u90fd\u6709\uff0c\u5c31\u662f\u6ca1\u6709\u9774\u5e95\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u672c\u6765\u4e5f\u5f88\u534e\u4e3d\uff0c\u800c\u4e14\u5f88\u5408\u8eab\uff0c\u4f46\u73b0\u5728\u5374\u5df2\u88ab\u6495\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u7b80\u76f4\u6ca1\u6709\u4e00 \u5757\u5b8c\u6574\u7684\u5730\u65b9\u3002 \u3000\u3000\u53ea\u6709\u4ed6\u5934\u4e0a\u6234\u7684\u5e3d\u5b50\uff0c\u5012\u4e0d\u6298\u4e0d\u6263\u662f\u9876\u5f88\u6f02\u4eae\u7684\u5e3d\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5e76\u4e0d\u592a\u9ad8\uff0c\u4f46\u624b\u811a\u5374\u5f88\u957f\u3002 \u3000\u3000\u4ed6\u7684\u8138\u5f88\u79c0\u6c14\uff0c\u751a\u81f3\u6709\u70b9\u50cf\u5c0f\u59d1\u5a18\u8138\uff0c\u5927\u5927\u7684\u773c\u775b\uff0c\u5c0f\u5c0f\u7684\u5634\uff0c\u7b11\u8d77\u6765\u7684\u65f6\u5019\u8fd8\u6709\u4e24\u4e2a \u9152\u7a9d\uff1b\u4f46\u4e0d\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u8138\u7acb\u5373\u5c31\u53d8\u5f97\u51b7\u51b0\u51b0\uff0c\u8138\u8272\u4e5f\u767d\u7684\u53d1\u9752\uff0c\u51e0\u4e4e\u4ee4\u4eba\u6709\u70b9\u4e0d\u6562\u4eb2 \u8fd1\u3002 \u3000\u3000\u4ed6\u8863\u670d\u672c\u6765\u597d\u8c61\u662f\u6de1\u9752\u8272\u7684\uff0c\u73b0\u5728\u5374\u662f\u4e00\u5757\u7ea2\uff0c\u4e00\u5757\u9ec4\u3002 \u3000\u3000\u9ec4\u7684\u81ea\u7136\u662f\u6ce5\uff0c\u7ea2\u7684\u662f\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u96be\u9053\u662f\u8840\uff1f \u3000\u3000\u4e24\u4e2a\u4eba\u597d\u597d\u7684\u5728\u5bb6\u91cc\u559d\u9152\uff0c\u7a81\u7136\u770b\u5230\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u95ef\u4e86\u8fdb\u6765\uff0c\u65e0\u8bba\u8c01\u90fd\u96be\u514d\u8981\u5413\u4e0a\u4e00 \u8df3\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u548c\u738b\u52a8\u8fd8\u662f\u4e00\u4e2a\u7761\u7740\u3001\u4e00\u4e2a\u540a\u7740\uff0c\u597d\u8c61\u6839\u672c\u6ca1\u6709\u770b\u8fd9\u4e2a\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u4f60\u8d70\u8fdb\u4e00\u95f4\u5c4b\u5b50\uff0c\u82e5\u662f\u770b\u5230\u4e00\u4e2a\u4eba\u7761\u5728\u5e8a\u4e0a\u559d\u9152\uff0c\u4e00\u4e2a\u4eba\u5012\u540a\u7740\u559d\u9152\uff0c\u53ea\u6015\u4f1a\u4ee5\u4e3a\u81ea\u5df1 \u8d70\u8fdb\u4e86\u75af\u4eba\u9662\uff0c\u7eb5\u7136\u6ca1\u6709\u88ab\u5413\u5f97\u593a\u95e8\u800c\u9003\uff0c\u4e5f\u96be\u514d\u5934\u76ae\u53d1\u6bdb\u3002 \u3000\u3000\u4f46\u8fd9\u4eba\u5374\u50cf\u662f\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u60ca\u5947\uff0c\u5c31\u597d\u8c61\u540a\u7740\u559d\u9152\u672c\u6765\u5c31\u662f\u5f88\u6b63\u5e38\u7684\u65b9\u5f0f\uff0c\u5750\u7740\u559d\u9152\u624d \u5e94\u8be5\u5947\u602a\u3002 \u3000\u3000\u8fd9\u4eba\u5c31\u662f\u71d5\u4e03\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u811a\u5012\u6302\u5728\u5c4b\u6881\u4e0a\u3002 \u3000\u3000\u71d5\u4e03\u7a81\u7136\u51cc\u7a7a\u7ffb\u4e86\u4e2a\u8ddf\u6597\uff0c\u628a\u4e00\u53cc\u811a\u4e5f\u5012\u6302\u4e0a\u5c4b\u6881\uff0c\u8138\u5bf9\u7740\u90ed\u5927\u8def\u7684\u8138\uff0c\u8c61\u662f\u89c9\u5f97\u8fd9\u6837 \u5b50\u624d\u597d\u8bf4\u8bdd\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u5f00\u59cb\u89c9\u5f97\u8fd9\u4eba\u6709\u8da3\u4e86\uff0c\u7a81\u7136\u6324\u4e86\u6324\u773c\uff0c\u505a\u4e86\u4e2a\u9b3c\u8138\u3002 \u3000\u3000\u71d5\u4e03\u4e5f\u6324\u4e86\u6324\u773c\uff0c\u505a\u4e86\u4e2a\u9b3c\u8138\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u597d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u559d\u53e3\u9152\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7acb\u5373\u5c06\u9152\u74f6\u9012\u4e86\u8fc7\u53bb\uff0c\u4ed6\u5b58\u5fc3\u60f3\u770b\u770b\u9152\u4ece\u8fd9\u4eba\u7684\u9f3b\u5b50\u91cc\u5f80\u5916\u5192\u7684\u6a21\u6837\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u4eba\u7684\u6280\u672f\u6bd4\u4ed6\u5f3a\u591a\u4e86\uff0c\u201c\u5495\u561f\u5495\u561f\u201d\uff0c\u4e00\u53e3\u6c14\u5c06\u5927\u534a\u74f6\u9152\u5168\u90fd\u559d\u4e86\u4e0b\u53bb\uff0c\u5c45\u7136\u8fde \u4e00\u6ef4\u90fd\u6ca1\u6709\u6f0f\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u773c\u775b\u5df2\u770b\u5f97\u53d1\u76f4\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u5c31\u8fd9\u6837\u559d\u8fc7\u9152\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u559d\u8fc7\u51e0\u6b21\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u60f3\u8bd5\u8bd5\u8fd9\u4e48\u6837\u559d\u9152\u662f\u4e0d\u662f\u80fd\u559d\u5f97\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u8fde\u8fd9\u79cd\u4e8b\u90fd\u8bd5\u8fc7\uff0c\u4ed6\u6ca1\u6709\u505a\u7684\u4e8b\u53ea\u6015\u5c31\u5f88\u5c11\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u8bd5\u8fc7\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u80fd\u8bf4\u5f97\u51fa\u6765\u7684\u4e8b\uff0c\u5927\u6982\u6211\u5168\u8bd5\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u5927\u6982\u5f88\u5c11\u518d\u6709\u522b\u7684\u4e8b\u6bd4\u5012\u540a\u7740\u559d\u9152\u66f4\u96be\u53d7\u7684\u5427\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8fd8\u6709\u51e0\u6837\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u6709\uff1f\u90a3\u4e48\u6700\u96be\u53d7\u7684\u4e8b\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6700\u96be\u53d7\u7684\u4e8b\u5c31\u662f\u88ab\u4eba\u9489\u5728\u68fa\u6750\u91cc\uff0c\u57cb\u5728\u5730\u4e0b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u77aa\u5f97\u66f4\u5927\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u4f60\u4e5f\u8bd5\u8fc7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8bd5\u8fc7\u7684\u6b21\u6570\u4e5f\u4e0d\u592a\u591a\uff0c\u53ea\u4e0d\u8fc7\u624d\u4e24\u6b21\u800c\u5df2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u4e00\u4e2a\u8ddf\u6597\u4ece\u534a\u7a7a\u4e2d\u8df3\u4e0b\u6765\uff0c\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u71d5\u4e03\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u90ed\u5927\u8def\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u82e5\u4e0d\u662f\u5439\u725b\u5927\u738b\uff0c\u5c31\u4e00\u5b9a\u662f\u4e2a\u602a\u7269\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\uff1a\u201c\u4ed6\u662f\u602a\u7269\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5f7c\u6b64\u5f7c\u6b64\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u629a\u638c\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u4e0d\u9519\uff0c\u5927\u5bb6\u90fd\u662f\u602a\u7269\uff0c\u5426\u5219\u4e5f\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u63a5\u9053\uff1a\u201c\u6211\u7b2c\u4e00\u6b21\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u4e3a\u4e86\u60f3\u505a\u5f3a\u76d7\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u5374\u4e0d\u60f3\u505a\u5f3a\u76d7\uff0c\u56e0\u4e3a\uff0c\u6211\u65e9\u5c31\u662f\u5f3a\u76d7\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\uff0c\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u5f3a\u76d7\uff0c\u4e00\u5b9a\u662f\u7b28\u5f3a\u76d7\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u662f\u7b28\uff0c\u53ea\u4e0d\u8fc7\u8d70\u4e86\u9709\u8fd0\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8d70\u4e86\u9709\u8fd0\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u8d70\u9709\u8fd0\uff0c\u600e\u4e48\u4f1a\u95ef\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u7a76\u7adf\u662f\u60f3\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ec0\u4e48\u90fd\u4e0d\u60f3\u5e72\uff0c\u53ea\u4e0d\u8fc7\u60f3\u627e\u4e2a\u5730\u65b9\u8eb2\u4e00\u8eb2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u8eb2\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u56e0\u4e3a\u53c8\u6709\u4eba\u60f3\u628a\u6211\u9489\u5728\u68fa\u6750\u91cc\uff0c\u57cb\u5230\u5730\u4e0b\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u6b21\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8682\u8681\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5f20\u5927\u4e86\u5634\uff0c\u51e0\u4e4e\u8fde\u4e0b\u5df4\u90fd\u6389\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u8bf4\u8682\u8681\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8682\u8681\uff1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u5f2f\u4e86\u8170\uff0c\u5598\u7740\u6c14\u9053\uff1a\u201c\u4f60\u82e5\u8fde\u8682\u8681\u90fd\u6015\uff0c\u80c6\u5b50\u53ef\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u71d5\u4e03\u5374\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u770b\u6765\u4f60\u7b80\u76f4\u6ca1\u6709\u5728\u6c5f\u6e56\u4e2d\u6df7\u8fc7\uff0c\u5c45\u7136\u8fde\u2018\u8682\u8681\u2019\u662f\u4ec0\u4e48 \u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5728\u6211\u53c1\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u77e5\u9053\u8682\u8681\u662f\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u662f\u4e00\u79cd\u5f88\u5c0f\u5f88\u5c0f\u7684\uff0c\u5728\u5730\u4e0a\u722c\u6765\u722c\u53bb\u7684\u866b\u3002\u738b\u52a8\u7684\u5e8a\u4e0a\u5c31\u6709\u4e0d\u5c11\uff0c\u6211\u968f\u65f6 \u53ef\u4ee5\u6349\u51e0\u53ea\u6765\u7ed9\u4f60\u77a7\u77a7\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u8fd9\u79cd\u8682\u8681\uff0c\u662f\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u5f81\uff0c\u9053\uff1a\u201c\u4eba\uff1f\u8682\u8681\u662f\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u662f\u56db\u4e2a\u4eba\uff0c\u8fd9\u56db\u4e2a\u4eba\u662f\u8682\u8681\u738b\uff0c\u624b\u4e0b\u8fd8\u6709\u5f88\u591a\u5c0f\u8682\u8681\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u4eba\u4e00\u4e2a\u53eb\u91d1\u8682\u8681\uff0c\u4e00\u4e2a\u53eb\u94f6\u8682\u8681\uff0c\u4e00\u4e2a\u53eb\u7ea2\u8682\u8681\uff0c\u4e00\u4e2a\u53eb\u767d\u8682\u8681\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u6709\u7ea2\u8682\u8681\uff0c\u767d\u8682\u8681\uff0c\u5c31\u5e94\u8be5\u6709\u9ed1\u8682\u8681\u624d\u5bf9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u672c\u6765\u7684\u5374\u6709\u4e00\u4e2a\uff0c\u73b0\u5728\u5374\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u65e2\u7136\u660e\u660e\u662f\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u53eb\u8682\u8681\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5f88\u591a\u4eba\u90fd\u6709\u5916\u53f7\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8981\u53d6\u5916\u53f7\uff0c\u81f3\u5c11\u4e5f\u8be5\u53d6\u4e2a\u5a01\u98ce\u5802\u7687\u70b9\u7684\u540d\u5b57\uff0c\u8b6c\u5982\u53eb\u4ec0\u4e48\u2018\u63d2\u7fc5\u864e\u2019\u55bd\uff0c \u2018\u91d1\u6bdb\u72ee\u2019\u55bd\uff0c\u4ec0\u4e48\u5916\u53f7\u90fd\u597d\u53d6\uff0c\u4e3a\u4ec0\u4e48\u8981\u53eb\u5c0f\u8682\u8681\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u957f\u5f97\u5f88\u5c0f\uff0c\u90fd\u662f\u4f8f\u5112\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8d8a\u542c\u8d8a\u4e0d\u8c61\u8bdd\u4e86\uff0c\u8fd8\u662f\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u4f8f\u5112\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u4f8f\u5112\u975e\u4f46\u53ef\u6015\uff0c\u800c\u4e14\u53ef\u6015\u6781\u4e86\uff0c\u4e16\u4e0a\u6bd4\u4ed6\u4eec\u66f4\u53ef\u6015\u7684\u4eba\u53ea\u6015\u5df2\u6ca1\u6709\u51e0 \u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u54e6\uff1f\u83ab\u975e\u4ed6\u4eec\u7684\u672c\u4e8b\u5f88\u5927\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u6709\u79cd\u5f88\u7279\u522b\u7684\u529f\u592b\uff0c\u8fde\u5ce8\u5d4b\u6d3e\u7684\u7b2c\u4e00\u9ad8\u624b\u90fd\u5df2\u6b7b\u5728\u4ed6\u4eec\u624b \u4e0b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\u53ef\u6015\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u53bb\u60f9\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u6700\u8fd1\u95f9\u7a77\uff0c\u53c8\u8d70\u9709\u8fd0\uff0c\u534a\u4e2a\u6708\u91cc\u8fde\u8f93\u4e86\u5341\u4e94\u573a\uff0c\u8fde\u9774\u5e95\u90fd \u5356\u4e86\uff0c\u62ff\u53bb\u8fd8\u8d4c\u503a\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u4f60\u5c06\u9774\u5e95\u5356\u4e86\u8fd8\u8d4c\u5e10\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u6b20\u4e86\u591a\u5c11\u8d4c\u5e10\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5927\u6982\u4e03\u516b\u5343\u4e24\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u9774\u5e95\u5356\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e24\u53ea\u9774\u5e95\u4e00\u5171\u5356\u4e86\u4e00\u5343\u53c1\u767e\u4e24\u3002\u201d \u3000\u3000\u4ed6\u8d8a\u8bf4\u8d8a\u4e0d\u8c61\u8bdd\u4e86\uff0c\u90ed\u5927\u8def\u7d22\u6027\u5c31\u60f3\u518d\u542c\u542c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u9b3c\u8bdd\u53ef\u8bf4\uff0c\u62da\u547d\u5fcd\u4f4f\u7b11\u9053\uff1a\u90a3\u5c31 \u6b64\u975e\u8fd8\u5dee\u516d\u5343\u4e03\u767e\u4e24\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6b63\u56e0\u5982\u6b64\uff0c\u6240\u4ee5\u6211\u624d\u8981\u6253\u522b\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u5f3a\u76d7\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u62a2\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6b63\u8272\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u8fd9\u4e2a\u5f3a\u76d7\u662f\u4ec0\u4e48\u4eba\u90fd\u62a2\u7684\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8fd8\u6311\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u4f46\u6311\uff0c\u800c\u4e14\u6311\u5f97\u5f88\u5389\u5bb3\uff0c\u4e0d\u662f\u8d2a\u5b98\u6211\u4e0d\u62a2\uff0c\u4e0d\u662f\u5978\u5546\u4e5f\u4e0d\u62a2\uff0c\u4e0d\u662f\u5f3a\u76d7\u66f4 \u4e0d\u62a2\uff0c\u4eba\u4e0d\u5bf9\u4e0d\u62a2\uff0c\u5730\u65b9\u4e0d\u5bf9\u4e5f\u4e0d\u62a2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd9\u5f3a\u76d7\u8fd8\u62a2\u5f3a\u76d7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u53eb\u9ed1\u5403\u9ed1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u4e3b\u610f\u5c31\u6253\u5230\u90a3\u4e9b\u8682\u8681\u5934\u4e0a\u53bb\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u78b0\u5de7\u77e5\u9053\u90a3\u51e0\u5929\u4ed6\u4eec\u505a\u4e86\u7968\u5927\u4e70\u5356\uff0c\u6240\u4ee5\u5c31\u53bb\u95ee\u4ed6\u4eec\u501f\u4e00\u4e07\u4e24\u94f6 \u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u4eec\u7b54\u5e94\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u7b54\u5e94\u662f\u7b54\u5e94\u4e86\uff0c\u5374\u6709\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u6211\u7761\u5728\u68fa\u6750\u91cc\uff0c\u518d\u57cb\u5230\u5730\u4e0b\u53bb\u803d\u4e24\u5929\uff0c\u770b\u770b\u6211\u7a76\u7adf\u6b7b\u4e0d\u6b7b\u5f97\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4e8b\u4f60\u5c82\u975e\u65e9\u5c31\u5e72\u8fc7\u4e86\u4e48\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u867d\u7136\u5e72\u8fc7\uff0c\u4f46\u90a3\u6ecb\u5473\u786e\u5b9e\u518d\u4e0d\u597d\u53d7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u6ca1\u6709\u7b54\u5e94\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u7b54\u5e94\u4e86\uff0c\u56e0\u4e3a\u4ec0\u4e48\u503a\u90fd\u53ef\u4ee5\u6b20\uff0c\u53ea\u6709\u8d4c\u503a\u662f\u6b20\u4e0d\u5f97\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60 \u7b54\u5e94\u4e86\u4ed6\u4eec\u5374\u4e0d\u80af\u8ba4\u5e10\uff0c\u6240\u4ee5\u4ed6\u4eec\u624d\u6765\u8ffd\u4f60\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u71d5\u4e03\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u516d\u4e2a\u54e5\u54e5\u59d0\u59d0\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u662f\u6392\u884c\u7b2c\u4e03\uff0c\u4e3a\u4ec0\u4e48\u8981\u53eb\u71d5\u4e03\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u6b7b\u8fc7\u4e03\u6b21\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u82e5\u662f\u518d\u6b7b\u4e00\u6b21\uff0c\u4f60\u5c82\u975e\u5c31\u8981\u53eb\u505a\u71d5\u516b\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u82e6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u71d5\u4e03\u8fd9\u540d\u5b57\u86ee\u597d\uff0c\u6211\u4e0d\u60f3\u518d\u6539\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u5f2f\u4e0b\u8170\uff0c\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u5f97\u773c\u6cea\u9f3b\u6d95\u90fd\u6d41\u4e86\u51fa\u6765\uff0c\u6307\u7740\u4ed6\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u602a \u7269\uff0c\u4f60\u4e0d\u6298\u4e0d\u6263\u662f\u4e2a\u5439\u725b\u5927\u738b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u4fe1\uff0c\u4f60\u8bf4\u7684\u8bdd\u7b80\u76f4\u8fde\u53c1\u5c81\u5927\u7684\u5c0f\u5b69\u5b50\u90fd\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u6253\u7b97\u8bf4\u771f\u8bdd\u7684\uff0c\u56e0\u4e3a\u6211\u65e9\u5c31\u77e5\u9053\u8c0e\u8bdd\u6bd4\u771f\u8bdd\u66f4\u5bb9\u6613\u4ee4 \u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u82e5\u662f\u771f\u8bdd\uff0c\u6211\u60c5\u613f\u5728\u5730\u4e0a\u722c\u2026\u2026\u201d \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u722c\u5427\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u53c8\u5c16\u53c8\u7ec6\uff0c\u58f0\u97f3\u867d\u4e0d\u5927\uff0c\u5374\u523a\u5f97\u4eba\u7684\u8033\u6735\u53d1\u9ebb\u3002 \u3000\u3000\u90ed\u5927\u8def\u62ac\u8d77\u5934\uff0c\u5c31\u770b\u5230\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u5c31\u7ad9\u5728\u7a97\u53f0\u4e0a\uff0c\u5374\u8fd8\u6ca1\u6709\u7a97\u5b50\u9ad8\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7740\u4ef6\u91d1\u5149\u95ea\u95ea\u7684\u8863\u670d\uff0c\u82e5\u4e0d\u662f\u8138\u4e0a\u751f\u7740\u80e1\u987b\uff0c\u773c\u89d2\u6709\u4e86\u76b1\u7eb9\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u5c06\u4ed6 \u770b\u6210\u4e2a\u4e94\u516d\u5c81\u7684\u5c0f\u5b69\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u664c\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u91d1\u8682\u8681\uff1f\u201d \u3000\u3000\u91d1\u8682\u8681\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\u6211\u53ef\u4ee5\u4fdd\u8bc1\u4ed6\u8bf4\u7684\u5168\u90fd\u662f\u771f\u8bdd\uff0c\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u5047\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u5410\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u91d1\u8682\u8681\u65e2\u7136\u6765\u4e86\uff0c\u94f6\u8682\u8681\u5462\uff1f\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a97\u5b50\u4e0a\u5c31\u53c8\u51fa\u73b0\u4e86\u4e2a\u4eba\u3002\u8fd9\u4eba\u603b\u7b97\u6bd4\u91d1\u8682\u8681\u9ad8\u4e9b\uff0c\u4f46\uff0c\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u9ad8\u4e24\u53c1 \u5bf8\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7740\u4ef6\u94f6\u5149\u95ea\u95ea\u7684\u8863\u670d\uff0c\u8138\u4e0a\u8fd8\u5e26\u7740\u4e2a\u94f6\u9762\u5177\uff0c\u770b\u6765\u5c31\u8c61\u662f\u4e2a\u7528\u767d\u94f6\u94f8\u6210\u7684\u5c0f\u5996 \u602a\uff0c\u5b9e\u5728\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u8fde\u90ed\u5927\u8def\u90fd\u89c9\u5f97\u6709\u70b9\u6bdb\u9aa8\u609a\u7136\uff0c\u5583\u5583\u9053\uff1a\u201c\u770b\u6765\u7ea2\u8682\u8681\u7a7f\u7684\u4e00\u5b9a\u662f\u7ea2\u8863\u670d\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u7b11\u58f0\u53c8\u6e05\u8106\uff0c\u53c8\u5a07\u5a9a\uff0c\u8fd9\u4e48\u597d\u542c\u7684\u7b11\u58f0\u65e0\u8bba\u8c01\u90fd\u5f88\u5c11\u80fd\u542c\u5230\u3002\u53ea\u8981\u542c\u5230\u8fd9\u79cd\u7b11\u58f0\uff0c\u5c31\u53ef \u4ee5\u60f3\u8c61\u5230\u7b11\u7684\u4eba\u4e00\u5b9a\u5f88\u7f8e\u3002 \u3000\u3000\u7ea2\u8682\u8681\u7684\u786e\u5f88\u7f8e\u3002 \u3000\u3000\u4f8f\u5112\u7684\u8eab\u6750\u672c\u6765\u4e00\u5b9a\u4e0d\u4f1a\u957f\u5f97\u5f88\u5300\u79f0\uff0c\u4f46\u5979\u5374\u662f\u4f8b\u5916\u3002 \u3000\u3000\u5979\u7a7f\u7740\u4ef6\u7d27\u8eab\u7684\u7ea2\u8863\u670d\uff0c\u8be5\u7ec6\u7684\u5730\u65b9\u7edd\u4e0d\u7c97\uff0c\u8be5\u80d6\u7684\u5730\u65b9\u7edd\u4e0d\u7626\uff0c\u4e00\u5f20\u7aef\u7aef\u6b63\u6b63\u7684\u74dc\u5b50 \u8138\uff0c\u7709\u4f3c\u8fdc\u5c71\uff0c\u76ee\u5982\u6625\u6c34\uff0c\u7b11\u9762\u751c\u751c\u7684\uff0c\u66f4\u6d53\u7684\u5316\u4e0d\u5f00\uff0c\u53ea\u8981\u5c06\u5979\u518d\u653e\u5927\u4e00\u500d\uff0c\u5c31\u662f\u4e2a\u7edd\u8272 \u7684\u7f8e\u4eba\u3002 \u3000\u3000\u82e5\u662f\u771f\u7684\u5c06\u5979\u653e\u5927\u4e86\u4e00\u500d\uff0c\u751a\u81f3\u8fde\u90ed\u5927\u8def\u8fd9\u79cd\u7537\u4eba\u4e5f\u8bb8\u90fd\u4e0d\u60dc\u4e3a\u5979\u72af\u7f6a\u3002 \u3000\u3000\u7eb5\u8fd8\u6ca1\u6709\u653e\u5927\u4e00\u500d\uff0c\u90ed\u5927\u8def\u7684\u773c\u775b\u4e5f\u4e0d\u7981\u77a7\u7684\u53d1\u76f4\u4e86\u3002 \u3000\u3000\u5979\u90a3\u53cc\u6625\u6c34\u822c\u7684\u773c\u6ce2\u4e5f\u6b63\u5728\u779f\u7740\u90ed\u5927\u8def\uff0c\u5a9a\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u7684\u773c\u4e0d\u8001\u5b9e\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u4e2a\u8001\u5b9e\u4eba\uff0c\u4ece\u5934\u81f3\u811a\u90fd\u6ca1\u6709\u4e00\u4e2a\u5730\u65b9\u8001\u5b9e\u7684\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u54af\u54af\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u662f\u4e2a\u8272\u9b3c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u867d\u7136\u4e0d\u5b8c\u5168\u662f\uff0c\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u7ea2\u8682\u8681\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u4e0d\u89c1\u4e86\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4eba\u4e0d\u80fd\u7f29\u5c0f\uff0c\u5426\u5219\u6211\u5012\u4e5f\u60f3\u53d8\u6210\u4e2a\u9ec4\u8682\u8681\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u54ac\u7740\u5634\u5507\uff0c\u5634\u89d2\u53c8\u9732\u51fa\u4e86\u751c\u751c\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u6562\u8c03\u620f\u6211\uff0c\u80c6\u5b50\u5012\u771f\u4e0d\u5c0f\uff0c\u96be\u9053 \u5c31\u4e0d\u6015\u6211\u7684\u8001\u516c\u5403\u918b\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8001\u516c\u662f\u8c01\uff1f\u767d\u8682\u8681\uff1f\u2026\u2026\u542c\u8bf4\u767d\u8682\u8681\u4f1a\u98de\u7684\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u5a07\u7b11\u7740\uff0c\u9053\uff1a\u201c\u4f60\u53c8\u731c\u5bf9\u4e86\uff0c\u771f\u662f\u4e2a\u5929\u624d\u513f\u7ae5\u3002\u201d \u3000\u3000\u94f6\u73b2\u822c\u7684\u7b11\u58f0\u4e2d\uff0c\u7a97\u5916\u5ffd\u7136\u6709\u6837\u4e1c\u897f\u98de\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u6837\u4e1c\u897f\u65e0\u8bba\u600e\u4e48\u770b\u90fd\u4e0d\u8c61\u662f\u4e2a\u4eba\uff0c\u8f7b\u98d8\u98d8\u7684\uff0c\u5c31\u8c61\u662f\u7247\u6de1\u6de1\u7684\u4e91\uff0c\u53c8\u8c61\u7247\u767d\u767d\u7684\u96ea\uff0c \u8f7b\u98d8\u98d8\u7684\u98de\u4e86\u8fdb\u6765\uff0c\u7a81\u7136\u201c\u547c\u201d\u7684\u4ece\u90ed\u5927\u8def\u5934\u9876\u4e0a\u98de\u8fc7\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u89c9\u5934\u9876\u4e00\u51c9\uff0c\u82e5\u4e0d\u662f\u8eb2\u5f97\u5feb\uff0c\u8111\u888b\u8bf4\u4e0d\u5b9a\u5df2\u642c\u4e86\u5bb6\u3002 \u3000\u3000\u53ea\u542c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u7247\u4e1c\u897f\u53c8\u98de\u4e86\u56de\u6765\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u4e0d\u662f\u4eba\uff0c\u4eba\u51b3\u4e0d\u4f1a\u6709\u8fd9\u4e48\u53ef\u6015\u7684\u8f7b\u529f\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u504f\u504f\u662f\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7a7f\u7740\u96ea\u767d\u8863\u88f3\u7684\u4eba\uff0c\u8896\u5b50\u53c8\u5bbd\u53c8\u957f\uff0c\u5c31\u8c61\u662f\u4e24\u53ea\u7fc5\u8180\uff0c\u4eba\u5374\u53c8 \u7626\u53c8\u5c0f\uff0c\u957f\u4e0d\u6ee1\u53c1\u5c3a\u534a\uff0c\u5bbd\u4e0d\u53ca\u4e00\u5c3a\uff0c\u82e5\u662f\u653e\u5728\u79e4\u4e0a\u79e4\u4e00\u79e4\uff0c\u51b3\u4e0d\u4f1a\u6bd4\u4e00\u53ea\u5154\u5b50\u91cd\u591a\u5c11\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u53c8\u600e\u4e48\u4f1a\u7ec3\u5f97\u6210\u8fd9\u4e48\u6837\u7684\u8f7b\u529f\uff1f \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u767d\u8682\u8681\u679c\u7136\u662f\u4f1a\u98de\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u767d\u8682\u8681\u8f7b\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u7ea2\u8682\u8681\u5168\u8eab\u90fd\u662f\u6697\u5668\uff0c\u91d1\u8682\u8681\u62f3\u5251\u53cc\u7edd\uff0c\u94f6\u8682\u8681\u5200\u67aa \u4e0d\u5165\u3002\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u90fd\u6709\u79cd\u5f88\u7279\u522b\u7684\u5de5\u592b\uff0c\u73b0\u5728\uff0c\u4f60\u603b\u8be5\u76f8\u4fe1\u4e86\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6211\u73b0\u5728\u5c31\u722c\uff0c\u8fd8\u662f\u7b49\u7b49\u518d\u722c\uff1f\u201d \u3000\u3000\u767d\u8682\u8681\u51b7\u51b7\u9053\uff1a\u201c\u6700\u597d\u73b0\u5728\u5c31\u722c\uff0c\u722c\u51fa\u53bb\uff0c\u514d\u5f97\u88ab\u4eba\u62ac\u51fa\u53bb\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u8bf4\u4ed6\u4f1a\u5403\u918b\u7684\uff0c\u73b0\u5728\u4f60\u603b\u4e5f\u8be5\u76f8\u4fe1\u4e86\u5427\u3002\u201d \u3000\u3000\u91d1\u8682\u8681\u9053\uff1a\u201c\u6211\u4eec\u7684\u662f\u4e0e\u4f60\u4eec\u65e0\u5173\uff0c\u4f60\u4eec\u7684\u786e\u8fd8\u662f\u722c\u51fa\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u722c\uff0c\u4f60\u6700\u597d\u5148\u6559\u6559\u6211\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u53ea\u5e26\u4e00\u53e3\u68fa\u6750\u6765\u7684\u786e\u592a\u5c11\uff0c\u5e94\u8be5\u5e26\u53c1\u53e3\u6765\u624d\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u68fa\u6750\u90fd\u5e26\u6765\u4e86\uff1f\u771f\u7684\u8981\u628a\u4ed6\u9489\u5165\u68fa\u6750\uff1f\u201d \u3000\u3000\u91d1\u8682\u8681\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4ed6\u8bf4\u7684\u8bdd\uff0c\u6bcf\u4e2a\u5b57\u90fd\u4e0d\u5047\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u62cd\u4e86\u62cd\u90ed\u5927\u8def\u7684\u80a9\u8180\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u60f9\u7684\u9ebb\u70e6\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u901e\u82f1\u96c4\u3001\u7ba1\u95f2 \u4e8b\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u53cd\u6b63\u4f60\u5df2\u6b7b\u8fc7\u4e03\u6b21\uff0c\u518d\u591a\u6b7b\u4e00\u6b21\u53c8\u4f55\u59a8\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u662f\u4eba\u5bb6\u7684\u5730\u65b9\uff0c\u6211\u8981\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u767d\u8682\u8681\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u51fa\u53bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u62cd\u4e86\u62cd\u8863\u670d\uff0c\u7b11\u9053\uff1a\u201c\u51fa\u53bb\u5c31\u51fa\u53bb\u2026\u2026\u4e24\u4f4d\uff0c\u8fd9\u6b21\u6211\u82e5\u8fd8\u6b7b\u4e0d\u4e86\uff0c\u4e00\u5b9a\u8fd8\u4f1a\u56de\u6765\u627e \u4f60\u4eec\u559d\u9152\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u4e00\u76f4\u7761\u5728\u5e8a\u4e0a\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u91d1\u8682\u8681\u9053\uff1a\u201c\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u7ea2\u8682\u8681\u5403\u5403\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u662f\u4f60\u7684\u732a\u7a9d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u91cc\u82e5\u662f\u732a\u7a9d\uff0c\u6211\u5c31\u662f\u732a\u5927\u738b\uff0c\u65e0\u8bba\u8c01\u5230\u4e86\u8fd9\u91cc\uff0c\u90fd\u5f97\u542c\u6211\u7684\u3002\u201d \u3000\u3000\u91d1\u8682\u8681\u6012\u9053\uff1a\u201c\u4f60\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u8981\u71d5\u4e03\u7559\u4e0b\u6765\u966a\u6211\u559d\u9152\uff0c\u8981\u60f3\u518d\u627e\u4e2a\u80fd\u5012\u540a\u7740\u966a\u6211\u559d\u7684\u4eba\u5e76\u4e0d\u5bb9\u6613\uff0c\u6211\u600e \u4e48\u80af\u8ba9\u4ed6\u7761\u5230\u68fa\u6750\u91cc\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u52a8\u4e86\u4e48\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u4e9b\u8682\u8681\u4f1a\u54ac\u4eba\uff0c\u6211\u60f3\u4e0d\u52a8\u4e5f\u4e0d\u884c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u600e\u4e48\u52a8\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u7ea2\u8682\u8681\u662f\u6211\u7684\uff0c\u767d\u8682\u8681\u5f52\u4f60\u3002\u201d \u3000\u3000\u738b\u52a8\u4e0d\u52a8\uff0c\u4e00\u52a8\u8d77\u6765\u5c31\u52a8\u5f97\u5389\u5bb3\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4ed6\u7684\u4eba\u5df2\u5ffd\u7136\u4ece\u5e8a\u4e0a\u5f39\u8d77\uff0c\u6251\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e0d\u4f46\u4eba\u6251\u4e86\u51fa\u53bb\uff0c\u4ed6\u8eab\u4e0a\u76d6\u7740\u7684\u90a3\u5e8a\u88ab\u4e5f\u8ddf\u7740\u6251\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8ba4\u51c6\u4e86\u7ea2\u8682\u8681\u3002 \u3000\u3000\u7ea2\u8682\u8681\u5374\u6839\u672c\u770b\u4e0d\u5230\u4ed6\u7684\u4eba\uff0c\u53ea\u770b\u5230\u4e00\u5e8a\u9ed1\u9edd\u9edd\u7684\u68c9\u88ab\u5411\u81ea\u5df1\u5377\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u8eab\u5b50\u4e00\u8f6c\uff0c\u5df2\u6709\u53c1\u56db\u4ef6\u4e94\u989c\u516d\u8272\u3001\u5404\u5f0f\u5404\u6837\u7684\u6697\u5668\u98de\u4e86\u51fa\u6765\uff0c\u6709\u7684\u53c8\u5feb\u53c8\u6025\uff0c\u6709\u7684\u4e92 \u76f8\u649e\u51fb\uff0c\u6709\u7684\u5728\u7a7a\u4e2d\u6253\u7740\u8f6c\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u4eba\u5c0f\uff0c\u6240\u4ee5\u6697\u5668\u4e5f\u7279\u522b\u5c0f\u3002 \u3000\u3000\u56e0\u4e3a\u6697\u5668\u7279\u522b\u5c0f\uff0c\u6240\u4ee5\u7834\u98ce\u4e4b\u529b\u7279\u522b\u5f3a\uff0c\u522b\u4eba\u4e5f\u7279\u522b\u96be\u8eb2\u3002 \u3000\u3000\u4f46\u5979\u5374\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u68c9\u88ab\u4e0d\u662f\u4eba\u3002 \u3000\u3000\u68c9\u88ab\u662f\u6253\u4e0d\u6b7b\u7684\u3002 \u3000\u3000\u5979\u7684\u6697\u5668\u867d\u7136\u5947\u5de7\uff0c\u624b\u6cd5\u867d\u7136\u9ad8\u660e\uff0c\u4e5f\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ea\u542c\u201c\u5657\u3001\u5657\u3001\u5657\u201d\u4e00\u8fde\u4e32\u58f0\u54cd\uff0c\u53c1\u56db\u5341\u4ef6\u6697\u5668\uff0c\u5168\u90fd\u6253\u5728\u68c9\u88ab\u4e0a\uff0c\u68c9\u88ab\u4e0a\u6709\u732a\u6cb9\u3001\u6709 \u9e2d\u6cb9\u3001\u6709\u9e21\u6cb9\u3001\u8fd8\u6709\u9ebb\u6cb9\u3002 \u3000\u3000\u8fd9\u5e8a\u68c9\u88ab\u7b80\u76f4\u5c31\u8c61\u662f\u7528\u6cb9\u6ce1\u8fc7\u7684\uff0c\u6ce1\u5f97\u53c8\u6ed1\u53c8\u97e7\uff0c\u5c31\u7b97\u662f\u5f3a\u5f13\u786c\u5f29\uff0c\u4e5f\u672a\u5fc5\u80fd\u5c04\u5f97\u7a7f\uff0c \u4f55\u51b5\u662f\u8fd9\u4e48\u5c0f\u7684\u6697\u5668\uff1f \u3000\u3000\u7b49\u5230\u7ea2\u8682\u8681\u53d1\u89c9\u4e0a\u5f53\u4e86\uff0c\u8eab\u5f62\u5411\u540e\u5012\u63a0\u800c\u51fa\uff0c\u68c9\u88ab\u5df2\u4e4c\u4e91\u822c\u5377\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u738b\u52a8\u4e0d\u52a8\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4e00\u52a8\u8d77\u6765\u7adf\u8fd9\u4e48\u5feb\u3002 \u3000\u3000\u7ea2\u8682\u8681\u521a\u55c5\u5230\u4e00\u79cd\u5947\u5947\u602a\u602a\u5f97\u6cb9\u817b\u5473\u9053\uff0c\u6574\u4e2a\u4eba\u5df2\u88ab\u68c9\u88ab\u5305\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u7684\u4eba\u82e5\u662f\u957f\u7684\u9ad8\u5927\u4e9b\uff0c\u738b\u52a8\u4e5f\u672a\u5fc5\u80fd\u7528\u5e8a\u68c9\u88ab\u5c06\u5979\u5305\u4f4f\uff0c\u600e\u5948\u5979\u7684\u4eba\u5b9e\u5728\u592a\u5c0f\u4e86\uff0c\u738b \u52a8\u4e24\u53ea\u624b\u4e00\u56f4\uff0c\u5979\u6574\u4e2a\u4eba\u5df2\u8c61\u662f\u88f9\u7cbd\u5b50\u4f3c\u7684\u88ab\u5305\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u738b\u52a8\u7684\u8eab\u5b50\u5374\u8fd8\u662f\u6ca1\u6709\u505c\uff0c\u53ea\u542c\u8eab\u540e\u98ce\u58f0\u54cd\u52a8\uff0c\u767d\u8682\u8681\u5df2\u98de\u63a0\u4e86\u8fc7\u6765\uff0c\u738b\u52a8\u518d\u5feb\uff0c\u4e5f\u6ca1 \u6709\u8fd9\u53ea\u4f1a\u98de\u7684\u767d\u8682\u8681\u5feb\u3002 \u3000\u3000\u7728\u773c\u95f4\u767d\u8682\u8681\u5c31\u5df2\u8ffd\u4e0a\u4e86\u4ed6\u3002 \u3000\u3000\u738b\u52a8\u5c31\u662f\u8981\u767d\u8682\u8681\u8ffd\u4e0a\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u7edd\u5bf9\u8ffd\u4e0d\u4e0a\u767d\u8682\u8681\u3002 \u3000\u3000\u7b49\u767d\u8682\u8681\u8ffd\u8fc7\u6765\u4e86\uff0c\u4ed6\u8eab\u5b50\u9aa4\u7136\u4e00\u505c\uff0c\u4e00\u8f6c\uff0c\u5c06\u624b\u91cc\u7684\u4e00\u5377\u68c9\u88ab\u5377\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u68c9\u88ab\u91cc\u5377\u7740\u7684\u662f\u81ea\u5df1\u7684\u8001\u5a46\uff0c\u767d\u8682\u8681\u5f53\u7136\u4e0d\u80fd\u4e0d\u63a5\u4f4f\u3002 \u3000\u3000\u8fd9\u5377\u68c9\u88ab\u6bd4\u4ed6\u7684\u4eba\u5927\u4e00\u500d\uff0c\u91cd\u4e24\u500d\uff0c\u4ed6\u4e00\u4f38\u624b\u63a5\u4f4f\uff0c\u8eab\u5b50\u5c31\u7acb\u5373\u5f80\u4e0b\u6389\u3002\u738b\u52a8\u5374\u5df2\u7ed5\u5230 \u4ed6\u80cc\u540e\uff0c\u8f7b\u8f7b\u677e\u677e\u5c31\u62cd\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u767d\u8682\u8681\u5c0f\u5c0f\u7684\u8138\u4e0a\u9752\u7b4b\u66b4\u9732\uff0c\u77aa\u7740\u4ed6\uff0c\u8fde\u773c\u73e0\u5b50\u90fd\u597d\u8c61\u8981\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u738b\u52a8\u5374\u53c8\u4e0d\u52a8\u4e86\uff0c\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4f60\u8d25\u5f97\u4e0d\u7518\u5fc3\u662f\u4e0d\u662f\uff1f\u56e0\u4e3a\u6211\u7528\u7684\u4e0d\u662f\u771f\u529f\u592b\u3002\u544a\u8bc9\u4f60 \u82e5\u7528\u771f\u529f\u592b\u5c31\u4e0d\u7b97\u672c\u4e8b\u4e86\u3002\u6211\u6253\u67b6\u4ece\u6765\u4e5f\u4e0d\u7528\u771f\u529f\u592b\u7684\u3002\u201d \u3000\u3000\u767d\u8682\u8681\u6c14\u5f97\u7b80\u76f4\u8981\u5410\u8840\u3002 \u3000\u3000\u738b\u52a8\u7684\u786e\u597d\u8c61\u8fde\u4e00\u70b9\u771f\u529f\u592b\u4e5f\u6ca1\u6709\uff0c\u5b8c\u5168\u662f\u6295\u673a\u53d6\u5de7\u3002 \u3000\u3000\u4f46\u82e5\u6ca1\u6709\u4e00\u7b49\u4e00\u7684\u771f\u529f\u592b\uff0c\u53c8\u600e\u80fd\u8fd9\u4e48\u6837\u6295\u673a\u53d6\u5de7\uff1f\u65f6\u95f4\u53c8\u600e\u80fd\u62ff\u5f97\u8fd9\u4e48\u51c6\uff1f\u51fa\u624b\u53c8\u600e \u4f1a\u8fd9\u4e48\u7a33\uff1f \u3000\u3000\u8fd9\u4e0d\u4f46\u624b\u811a\u4e0a\u8981\u6709\u771f\u529f\u592b\uff0c\u8111\u888b\u91cc\u66f4\u8981\u6709\u771f\u529f\u592b\u3002 \u3000\u3000\u738b\u52a8\u4e0d\u52a8\uff0c\u4e00\u52a8\u8d77\u6765\u53ef\u771f\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u518d\u770b\u90a3\u8fb9\u7684\u91d1\u8682\u8681\uff0c\u5df2\u88ab\u90ed\u5927\u8def\u7684\u62f3\u98ce\u8feb\u7684\u8fde\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u71d5\u4e03\u5374\u5728\u56f4\u7740\u94f6\u8682\u8681\u6253\u8f6c\u3002 \u3000\u3000\u94f6\u8682\u8681\u4e2a\u5b50\u867d\u8f83\u5927\uff0c\u5374\u662f\u4e00\u8eab\u7684\u786c\u529f\u592b\uff0c\u529f\u592b\u4e00\u786c\uff0c\u624b\u811a\u5c31\u6162\u3002 \u3000\u3000\u71d5\u4e03\u8f6c\u5f97\u8d8a\u6025\uff0c\u4ed6\u8d8a\u6162\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u71d5\u4e03\u6458\u4e0b\u5934\u4e0a\u7684\u5e3d\u5b50\uff0c\u5f80\u4ed6\u7684\u5934\u4e0a\u4e00\u6263\uff0c\u5e3d\u5b50\u5927\uff0c\u5934\u5c0f\uff0c\u4ed6\u6574\u4e2a\u5934\u90fd\u88ab\u8499\u4f4f\uff0c \u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u4f38\u811a\u4e00\u7eca\uff0c\u4ed6\u5c31\u8dcc\u5012\uff0c\u53ea\u542c\u201c\u54d7\u5566\u5566\u201d\u4e00\u58f0\uff0c\u539f\u6765\u4ed6\u8eab\u4e0a\u7a7f\u7684\u7adf\u662f\u94f6\u7532\uff0c\u4e00\u8dcc\u5012\u518d \u60f3\u722c\u8d77\u6765\uff0c\u5c31\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4ed6\u60f3\u53bb\u6293\u5934\u4e0a\u7684\u5e3d\u5b50\uff0c\u4f46\u4eba\u5df2\u88ab\u4e00\u6837\u5f88\u79cd\u5f88\u91cd\u7684\u4e1c\u897f\u538b\u4f4f\u3002\u539f\u6765\u71d5\u4e03\u5df2\u4e00\u5c41\u80a1\u5750\u5728\u4ed6\u8eab \u4e0a\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u8fd9\u51f3\u5b50\u5012\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u592a\u5c0f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u91d1\u8682\u8681\u5462\uff1f\u4ed6\u672c\u5c31\u8fde\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\u4e86\uff0c\u6b64\u523b\u4e00\u53d1\u6025\uff0c\u4e00\u53e3\u6c14\u5c31\u88ab\u618b\u5728\u809a\u5b50\u91cc\uff0c\u7528\u4e0d\u7740\u90ed \u5927\u8def\u52a8\u624b\uff0c\u4ed6\u81ea\u5df1\u5c31\u6655\u4e86\u8fc7\u53bb\uff0c\u5634\u89d2\u5410\u51fa\u4e86\u767d\u6cab\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u6709\u7f8a\u766b\u75af\uff0c\u770b\u6765\u6211\u627e\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u672c\u6765\u8bf4\u767d\u8682\u8681\u5f52\u4f60\uff0c\u4f60\u6ca1\u542c\u89c1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u7684\uff0c\u6211\u627e\u6211\u7684\uff0c\u767d\u8682\u8681\u6211\u8ffd\u4e0d\u4e0a\u4ed6\uff0c\u4ed6\u5374\u4e00\u5b9a\u4f1a\u53bb\u8ffd\u4f60\uff0c\u6240\u4ee5\u6211 \u5c31\u6311\u4e86\u8fd9\u91d1\u8682\u8681\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u5757\u5934\u603b\u6bd4\u4ed6\u5927\u4e9b\uff0c\u529b\u6c14\u81ea\u7136\u4e5f\u4e0d\u4f1a\u6bd4\u4ed6\u5c0f\uff0c\u5c31\u51ed\u529b\u6c14\u6211\u5c31\u5df2 \u5403\u5b9a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u4eba\u5c45\u7136\u4e5f\u4f1a\u6361\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u4f60\u8fd9\u5e8a\u68c9\u88ab\u5c45\u7136\u8fd8\u6709\u8fd9\u4e48\u5927\u7528\u5904\uff0c\u4ee5\u540e\u82e5\u6709\u4eba\u8981\u5b66\u63a5\u6697\u5668\uff0c\u6211 \u4e00\u5b9a\u8981\u529d\u4ed6\u5728\u5e8a\u4e0a\u5403\u6cb9\u9e21\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u9e21\u6cb9\u592a\u5c11\uff0c\u8fd8\u662f\u5403\u70e7\u9e2d\u597d\u3002\u201d \u3000\u3000\u71d5\u4e03\u7a81\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u5c45\u7136\u4f1a\u9047\u89c1\u4f60\u4eec\u8fd9\u4e48\u6837\u4e24\u4e2a\u4eba\uff0c\u5927\u6982 \u662f\u6211\u7684\u9709\u8fd0\u5df2\u8d70\u5f97\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u56e0\u4e3a\u4f60\u771f\u7684\u662f\u602a\u7269\uff0c\u4e0d\u662f\u5439\u725b\u5927\u738b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u80af\u5e2e\u6211\u7684\u5fd9\uff0c\u5c31\u56e0\u4e3a\u6211\u8bf4\u7684\u662f\u8001\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e5f\u56e0\u4e3a\u4f60\u80fd\u5012\u540a\u7740\u559d\u9152\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u770b\u5230\u4f60\u5012\u540a\u7740\u559d\u9152\uff0c\u6211\u53c8\u600e\u4e48\u4f1a\u8bf4\u90a3\u79cd\u8bdd\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u8fd8\u6709\u53e5\u8bdd\u8981\u8bf4\u7684\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u5e94\u8be5\u8bf4\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u8c22\u8c22\u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4e8b\uff0c\u6211\u5b9e\u5728\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u8c22\u6cd5\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u82e5\u771f\u8981\u8c22\u6211\uff0c\u5012\u6709\u4ef6\u4e8b\u53ef\u4ee5\u505a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u628a\u6211\u62ac\u56de\u5e8a\u4e0a\u53bb\uff0c\u6211\u53c8\u61d2\u5f97\u52a8\u4e86\u3002\u201d (\u4e09) \u3000\u3000\u201c\u5bcc\u8d35\u5c71\u5e84\u201d\u65e0\u8bba\u5728\u4efb\u4f55\u4eba\u773c\u4e2d\u770b\u6765\uff0c\u90fd\u4e0d\u4f1a\u662f\u4e00\u4e2a\u5f88\u6709\u8da3\u7684\u5730\u65b9\uff0c\u7b80\u76f4\u8fde\u4e00\u6837\u53ef\u4ee5\u4f7f \u4eba\u7559\u604b\u7684\u4e1c\u897f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u71d5\u4e03\u5c45\u7136\u4e5f\u548c\u90ed\u5927\u8def\u4e00\u6837\uff0c\u4ee5\u6765\u4e86\u5c31\u518d\u4e5f\u820d\u4e0d\u5f97\u8d70\u3002 \u3000\u3000\u8fd9\u5012\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4eec\u5df2\u6ca1\u6709\u522b\u7684\u5730\u65b9\u53ef\u53bb\uff0c\u800c\u662f\u56e0\u4e3a\u2026\u2026 \u3000\u3000\u56e0\u4e3a\u4ec0\u4e48\u5462\uff1f\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u5f7c\u6b64\u4e4b\u95f4\uff0c\u4eff\u4f5b\u6709\u79cd\u5f88\u5947\u602a\u7684\u5438\u5f15\u529b\uff0c\u6b63\u5982\u94c1\u548c\u78c1\u77f3\u4e00\u6837\uff0c\u5f7c\u6b64\u53ea\u8981\u4e00\u9047\u7740\uff0c\u5c31 \u4f1a\u88ab\u5bf9\u65b9\u7262\u7262\u7684\u5438\u4f4f\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u53ea\u8981\u5f7c\u6b64\u80fd\u5728\u4e00\u8d77\u5c31\u4f1a\u89c9\u5f97\u5f88\u5f00\u5fc3\uff0c\u7761\u5730\u94fa\u4e5f\u6ca1\u5173\u7cfb\uff0c\u997f\u4e24\u987f\u4e5f\u6ca1\u5173\u7cfb\uff0c\u751a\u81f3\u8fde \u5929\u584c\u4e0b\u6765\u4ed6\u4eec\u90fd\u4e0d\u4f1a\u5728\u4e4e\u3002 \u3000\u3000\u4e16\u4e0a\u53ea\u6709\u5f88\u5c11\u51e0\u4ef6\u4e8b\u80fd\u4ee4\u4ed6\u4eec\u53d7\u4e0d\u4e86\uff0c\u5176\u4e2d\u6709\u4e00\u6837\u5c31\u662f\u773c\u6cea\u3002 \u3000\u3000\u5973\u4eba\u7684\u773c\u6cea\uff0c\u5c24\u5176\u662f\u4e00\u4e2a\u8fd8\u4e0d\u6ee1\u56db\u5c3a\u7684\u5c0f\u5973\u4eba\u7684\u773c\u6cea\u3002 \u3000\u3000\u7ea2\u8682\u8681\u7684\u4eba\u867d\u5c0f\uff0c\u4f46\u773c\u6cea\u5374\u771f\u4e0d\u5c11\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u53d1\u89c9\u4e00\u4e2a\u5973\u4eba\u7684\u773c\u6cea\u7684\u591a\u5c11\uff0c\u548c\u5979\u8eab\u6750\u7684\u5927\u5c0f\u8fde\u4e00\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\uff0c\u8d8a\u7626\u5c0f\u7684 \u5973\u4eba\uff0c\u773c\u6cea\u5f80\u5f80\u53cd\u800c\u8d8a\u591a\u3002 \u3000\u3000\u5973\u4eba\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u8d8a\u80d6\u7684\u4eba\u5403\u5f97\u8d8a\u5c11\uff0c\u8d8a\u4e11\u7684\u4eba\u82b1\u6837\u8d8a\u591a\uff0c\u8d8a\u8001\u7684\u4eba\u7c89\u25a1\u5f97\u8d8a\u539a\uff0c\u8863\u670d\u8d8a\u591a\u7684\u4eba\u7a7f\u5f97\u8d8a \u8584\u3002 \u3000\u3000\u201c\u5509\uff0c\u5973\u4eba\u771f\u662f\u79cd\u5947\u602a\u7684\u52a8\u7269\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u7ea2\u8682\u8681\u4e00\u76f4\u4e0d\u505c\u7684\u54ed\uff0c\u5df2\u54ed\u5f97\u4ed6\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u8d70\u3002 \u3000\u3000\u71d5\u4e03\u5374\u4e0d\u8ba9\u4ed6\u8d70\u3002 \u3000\u3000\u738b\u52a8\u65e9\u5df2\u53c8\u8eba\u4e86\u4e0b\u53bb\uff0c\u8499\u5934\u5927\u7761\uff0c\u4ed6\u53ea\u8981\u4e00\u7761\u7740\uff0c\u5c31\u662f\u6b7b\u4e86\u4eba\u4e5f\u4e0d\u7ba1\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u62c9\u4f4f\u90ed\u5927\u8def\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u518d\u8d70\uff0c\u6211\u62ff\u8fd9\u56db\u4e2a\u4eba\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u672c\u5c31\u662f\u4f60\u7684\u9ebb\u70e6\uff0c\u4e0d\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f46\u82e5\u4e0d\u662f\u4f60\u4eec\u5e2e\u6211\uff0c\u6211\u600e\u4e48\u80fd\u5c06\u4ed6\u4eec\u6293\u4f4f\uff0c\u4ed6\u4eec\u82e5\u6ca1\u6709\u88ab\u6211\u6293\u4f4f\uff0c\u6211\u600e\u4e48\u4f1a \u6709\u8fd9\u79cd\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u8fd8\u6015\u81ea\u5df1\u8bf4\u5f97\u4e0d\u591f\u660e\u767d\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u4e0d\u5e2e\u6211\uff0c\u6211\u5c31\u4f1a\u88ab\u4ed6\u4eec\u6293\u4f4f\uff0c\u6700\u591a\u518d\u6b7b\u4e00 \u6b21\uff0c\u8fde\u4e00\u70b9\u9ebb\u70e6\u90fd\u6ca1\u6709\u3002\u4f46\u73b0\u5728\u6211\u65e2\u4e0d\u80fd\u6740\u4ed6\u4eec\uff0c\u53c8\u4e0d\u80fd\u653e\u4ed6\u4eec\uff0c\u4f60\u8bf4\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u8d8a\u660e\u767d\uff0c\u90ed\u5927\u8def\u8d8a\u7cca\u6d82\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u4ece\u88ab\u91cc\u4f38\u51fa\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5012\u6709\u4e2a\u597d\u6cd5\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u60f3\u6740\u4ed6\u4eec\uff0c\u53c8\u4e0d\u60f3\u653e\u4ed6\u4eec\uff0c\u4e0d\u5982\u5c31\u5c06\u4ed6\u4eec\u7559\u5728\u8fd9\u91cc\uff0c\u517b\u4ed6\u4eec\u4e00\u8f88 \u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7acb\u5373\u62cd\u624b\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7684\u786e\u662f\u597d\u4e3b\u610f\uff0c\u53cd\u6b63\u4ed6\u4eec\u4eba\u957f\u5f97\u8fd9\u4e48\u5c0f\uff0c\u5403\u7684\u51b3\u4e0d\u4f1a \u591a\u3002\u201d \u3000\u3000\u7ea2\u8682\u8681\u4e5f\u7acb\u5373\u4e0d\u54ed\u4e86\uff0c\u9053\uff1a\u201c\u6211\u6bcf\u5929\u53ea\u8981\u5403\u4e24\u5c0f\u7897\u73cd\u73e0\u7c89\u62cc\u996d\uff0c\u518d\u52a0\u4e0a\u4e00\u70b9\u6d77\u9c9c\uff0c\u51e0\u7247 \u6c34\u871c\u6843\u5c31\u591f\u4e86\uff1b\u6ca1\u6709\u6c34\u871c\u6843\uff0c\u54c8\u5bc6\u74dc\u4e5f\u884c\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u8fde\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u5583\u5583\u9053\uff1a\u201c\u73cd\u73e0\u7c89\u62cc\u996d\uff1f\u6d77\u9c9c\uff1f\u6c34\u871c\u6843\uff1f\u2026\u2026 \u8fd9\u5012\u4e5f\u4e0d\u96be\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u8f6c\u8fc7\u8eab\uff0c\u6389\u5934\u5c31\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u627e\u90a3\u53e3\u68fa\u6750\uff0c\u8eba\u4e0b\u53bb\uff0c\u518d\u627e\u4e2a\u4eba\u57cb\u8d77\u6765\uff0c\u8fd9\u81f3\u5c11\u603b\u6bd4\u6bcf\u5929\u627e\u73cd\u73e0\u7c89\u6c34\u871c\u6843\u5bb9 \u6613\u591a\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u770b\u6765\uff0c\u4e3a\u4e86\u8981\u6551\u4f60\uff0c\u5c31\u53ea\u597d\u628a\u4ed6\u4eec\u653e\u8d70\u4e86\uff0c\u8fd9\u81f3\u5c11\u4e5f\u6bd4 \u518d\u627e\u4e2a\u80fd\u540a\u8d77\u6765\u559d\u9152\u7684\u4eba\u5bb9\u6613\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u624b\u91cc\u5df2\u89e3\u5f00\u4e86\u8682\u8681\u4eec\u7684\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u6765\u5f97\u5feb\uff0c\u8d70\u7684\u4e5f\u4e0d\u6162\u3002 \u3000\u3000\u53c1\u4e2a\u4eba\u773c\u770b\u7740\u4ed6\u4eec\u8d70\u51fa\u53bb\uff0c\u7136\u540e\u5ffd\u7136\u4e00\u8d77\u8f6c\u8fc7\u5934\uff0c\u6211\u770b\u7740\u4f60\uff0c\u4f60\u770b\u7740\u6211\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u60f3\u653e\u4ed6\u4eec\u8d70\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u53c8\u4e0d\u597d\u610f\u601d\u660e\u8bf4\uff0c\u56e0\u4e3a\u6211\u4eec\u4e5f\u51fa\u4e86\u529b\uff0c\u82e5\u5c31\u8fd9\u6837\u653e\u4ed6\u4eec\u8d70\u4e86\uff0c\u4f60 \u6015\u6211\u4eec\u4e0d\u7518\u5fc3\uff0c\u5176\u5b9e\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u4e5f\u65e9\u5c31\u60f3\u653e\u4ed6\u4eec\u8d70\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u53c1\u4e2a\u4eba\u4f60\u770b\u7740\u6211\uff0c\u6211\u770b\u7740\u4f60\uff0c\u7a81\u7136\uff0c\u4e00\u9f50\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u770b\u6765\u653e\u4eba\u4e0d\u4f46\u6bd4\u6740\u4eba\u5bb9\u6613\uff0c\u800c\u4e14\u6109\u5feb\u7684\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u6211\u4eec\u82e5\u6740\u4e86\u4ed6\u4eec\uff0c\u73b0\u5728\u51b3\u4e0d\u4f1a\u8fd9\u4e48\u5f00\u5fc3\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f46\u6211\u4eec\u653e\u4e86\u4ed6\u4eec\u540e\uff0c\u4ed6\u4eec\u82e5\u518d\u53bb\u5bb3\u522b\u4eba\uff0c\u90a3\u5c31\u4e0d\u6109\u5feb\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u6447\u5934\uff0c\u5927\u58f0\u62a2\u7740\u9053\uff1a\u201c\u51b3\u4e0d\u4f1a\uff0c\u6211\u770b\u4ed6\u4eec\u5e76\u4e0d\u662f\u5341\u5206\u574f\u7684\u4eba\u3002\u5c31\u7b97\u4ee5\u524d\u505a\u8fc7\u4e0d \u592a\u597d\u7684\u4e8b\uff0c\u6b64\u540e\u4e5f\u4e00\u5b9a\u4f1a\u6539\u8fc7\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u6324\u4e86\u6324\u773c\uff0c\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u4eec\u771f\u7684\u5f88\u574f\uff0c\u542c\u5230\u4e86\u6211\u8fd9\u53e5\u8bdd\u540e\uff0c\u4e5f\u4e00\u5b9a\u4e0d \u597d\u610f\u601d\u518d\u53bb\u505a\u574f\u4e8b\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u60f3\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u542c\u5230\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u5f53\u7136\u542c\u5f97\u5230\uff0c\u8fd9\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u8fde\u5341\u91cc\u5916\u7684\u804b\u5b50\u90fd\u80fd\u542c\u5f97\u5230\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u55d3\u5b50\u4e00\u5411\u4e0d\u9519\uff0c\u4ee5\u524d\u8fd8\u6709\u5f88\u591a\u4eba\u8bf4\u6211\u662f\u5929\u751f\u7684\u91d1\u55d3\u5b50\uff0c\u7b49\u6211\u5fc3 \u60c5\u597d\u7684\u65f6\u5019\u6211\u5531\u4e24\u6bb5\u7ed9\u4f60\u4eec\u542c\u542c\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e00\u5b9a\u8981\u5531\uff0c\u6700\u597d\u7b49\u6211\u7761\u7740\u4e86\u518d\u5531\u3002\u201d \u3000\u3000\u4ed6\u5c06\u5934\u53c8\u8499\u8fdb\u88ab\u91cc\uff0c\u9053\uff1a\u201c\u53ea\u8981\u6211\u5df2\u7761\u7740\uff0c\u4f60\u5c31\u7b97\u8e29\u5230\u9e21\u8116\u5b50\uff0c\u6211\u90fd\u4e0d\u4f1a\u9192\u7684\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5c31\u662f\u8fd9\u4e48\u6837\u7684\u4eba\uff0c\u4ed6\u4eec\u505a\u4e8b\u7684\u6cd5\u5b50\u7684\u786e\u7279\u522b\u5f97\u5f88\u3002 \u3000\u3000\u4ed6\u4eec\u53c8\u662f\u505a\u5f97\u5f88\u5bf9\uff0c\u6709\u65f6\u4e5f\u4f1a\u505a\u9519\u3002 \u3000\u3000\u4f46\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u4eec\u505a\u4e8b\uff0c\u603b\u4e0d\u4f1a\u505a\u7684\u8840\u6dcb\u6797\u7684\uff0c\u4ee4\u4eba\u89c9\u5f97\u5f88\u6076\u5fc3\u3002 \u3000\u3000\u4ed6\u4eec\u505a\u7684\u4e8b\uff0c\u4e0d\u4f46\u80fd\u4ee4\u81ea\u5df1\u6109\u5feb\uff0c\u4e5f\u80fd\u591f\u4ee4\u522b\u4eba\u6b22\u4e50\u3002 \u6797\u592a\u5e73>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u6797\u592a\u5e73 (\u4e00) \u3000\u3000\u6bcf\u4e2a\u6708\u91cc\uff0c\u71d5\u4e03\u90fd\u4f1a\u4e00\u4e2a\u4eba\u6e9c\u51fa\u53bb\u4e24\u53c1\u6b21\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6 \u53bb\u5e72\u4ec0\u4e48\u3002 \u3000\u3000\u6bcf\u6b21\u4ed6\u56de\u6765\u7684\u65f6\u5019\uff0c\u603b\u4f1a\u5e26\u4e00\u4e24\u6837\u5947\u5947\u602a\u602a\u7684\u4e1c\u897f\u56de\u6765\u3002 \u3000\u3000\u4ed6\u5e26\u56de\u6765\u7684\u8bf4\u4e0d\u5b9a\u662f\u53cc\u65b0\u889c\u5b50\u3001\u662f\u5757\u7ee3\u82b1\u624b\u5e15\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u662f\u9505\u7ea2\u70e7\u8089\u3001\u662f\u4e00\u6574\u575b\u5bb6\u917f\u7684 \u7cef\u7c73\u9152\u3002 \u3000\u3000\u6709\u65f6\u4ed6\u751a\u81f3\u4f1a\u5e26\u53ea\u82b1\u732b\u3001\u5e26\u53ea\u91d1\u4e1d\u96c0\u3001\u5e26\u51e0\u6761\u6d3b\u9c7c\u56de\u6765\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u662f\u4ec0\u4e48\uff0c\u90fd\u6ca1\u6709\u4ed6\u8fd9\u6b21\u5e26\u56de\u6765\u7684\u4e1c\u897f\u5947\u602a\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u5c45\u7136\u5e26\u4e86\u4e2a\u4eba\u56de\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u53eb\u6797\u592a\u5e73\uff0c\u4f46\u81ea\u4ece\u4ed6\u6765\u4e86\u540e\uff0c\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u7684\u65e5\u5b50\u80fd\u8fc7\u7684\u592a\u5e73\u3002 (\u4e8c) \u3000\u3000\u6709\u4e9b\u4eba\u5f88\u559c\u6b22\u51ac\u5929\uff0c\u56e0\u4e3a\u51ac\u5929\u53ef\u4ee5\u8d4f\u96ea\u3001\u8d4f\u6885\uff0c\u53ef\u4ee5\u5403\u70ed\u70d8\u70d8\u7684\u706b\u9505\uff0c\u53ef\u4ee5\u8eb2\u5728\u70ed\u70d8\u70d8 \u7684\u88ab\u7a9d\u91cc\u8bfb\u7981\u4e66\u3001\u7761\u5927\u89c9\u3002 \u3000\u3000\u8fd9\u4e9b\u4e50\u8da3\u90fd\u662f\u522b\u7684\u5b63\u8282\u53d7\u4e0d\u5230\u7684\u3002 \u3000\u3000\u559c\u6b22\u51ac\u5929\u7684\u4eba\u5f53\u7136\u51b3\u4e0d\u4f1a\u662f\u7a77\u4eba\uff0c\u51ac\u5929\u662f\u7a77\u4eba\u6700\u8981\u547d\u7684\u65e5\u5b50\uff0c\u7a77\u4eba\u4eec\u90fd\u5e0c\u671b\u51ac\u5929\u80fd\u6765\u7684 \u8fdf\u4e9b\uff0c\u6700\u597d\u6c38\u8fdc\u83ab\u8981\u6765\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u7a77\u4eba\u7684\u51ac\u5929\u603b\u662f\u504f\u504f\u6765\u5f97\u7279\u522b\u65e9\u3002 \u3000\u3000\u73b0\u5728\u5df2\u7ecf\u662f\u51ac\u5929\u4e86\u3002 \u3000\u3000\u5bcc\u8d35\u5c71\u5e84\u9662\u5b50\u91cc\u7684\u96ea\u4e5f\u548c\u522b\u7684\u5730\u65b9\u4e00\u6837\u767d\uff0c\u800c\u4e14\u4e5f\u6709\u51e0\u682a\u6885\u82b1\u3002\u4f46\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\u7a7f\u7684\u82e5 \u8fd8\u662f\u6625\u5929\u7684\u8584\u8863\u670d\uff0c\u809a\u5b50\u91cc\u88c5\u7684\u82e5\u8fd8\u662f\u6628\u5929\u5403\u7684\u9633\u6625\u9762\uff0c\u4ed6\u552f\u4e00\u8fd8\u6709\u5fc3\u60c5\u6b23\u8d4f\u7684\u4e1c\u897f\u5c31\u662f\u53ef \u4ee5\u5f80\u5634\u91cc\u541e\u4e0b\u53bb\u3001\u585e\u9971\u809a\u5b50\u7684\uff0c\u51b3\u4e0d\u4f1a\u662f\u767d\u96ea\u6885\u82b1\u3002 \u3000\u3000\u90ed\u5927\u8def\u671b\u7740\u9662\u5b50\u91cc\u7684\u767d\u96ea\u6885\u82b1\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u6885\u82b1\u82e5\u662f\u8fa3\u6912\u591a\u597d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u6ee1\u5730\u7684\u96ea\u5c82\u975e\u6b63\u50cf\u662f\u9762\u7c89\uff0c\u914d\u4e0a\u51e0\u6839\u7ea2\u8fa3\u6912\uff0c\u5c82\u975e\u6b63\u597d\u505a\u4e00\u7897\u8fa3 \u4e4e\u4e4e\u7684\u70ed\u6c64\u9762\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u4fd7\uff0c\u6797\u900b\u82e5\u542c\u5230\u4f60\u7684\u8bdd\uff0c\u4e00\u5b9a\u4f1a\u6d3b\u6d3b\u6c14\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6797\u900b\u662f\u8c01\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8fde\u6797\u900b\u4f60\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u542c\u8bf4\u8fc7\u8089\u812f\uff0c\u65e0\u8bba\u662f\u732a\u8089\u812f\u3001\u725b\u8089\u812f\u3001\u9e7f\u8089\u812f\uff0c\u7528\u6765\u4e0b\u9152\u90fd\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6797\u900b\u5c31\u662f\u6797\u541b\u590d\uff0c\u4e5f\u5c31\u662f\u6797\u548c\u9756\uff0c\u662f\u5b8b\u671d\u7684\u4e00\u4f4d\u5927\u9690\u58eb\uff0c\u9690\u5c45\u5728\u897f\u6e56\u5b64\u5c71\uff0c \u636e\u8bf4\u6709\u4e8c\u5341\u5e74\u6ca1\u6709\u4e0b\u5c71\u4e00\u6b65\uff0c\u9664\u4e86\u79cd\u6885\u517b\u9e64\u5916\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u505a\uff0c\u4e16\u79f0\u2018\u6885\u59bb\u9e64\u5b50\u2019\uff1b\u505a\u7684\u548f \u6885\u8bd7\u6709\u4e24\u53e5\u662f\u2018\u758f\u5f71\u6a2a\u659c\u6c34\u6e05\u6d45\uff0c\u6697\u9999\u6d6e\u52a8\u6708\u9ec4\u660f\u2019\u66f4\u662f\u4f20\u8bf5\u5343\u53e4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60a0\u60a0\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u8fd9\u4f4d\u6797\u5148\u751f\u5012\u7684\u786e\u662f\u4f4d\u9ad8\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u9ad8\u6781\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f46\u4ed6\u7684\u809a\u5b50\u82e5\u997f\u5f97\u548c\u6211\u4e00\u6837\u5389\u5bb3\uff0c\u8fd8\u4f1a\u4e0d\u4f1a\u8fd9\u4e48\u9ad8\uff1f\u201d \u3000\u3000\u738b\u52a8\u60f3\u4e86\u60f3\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5230\u4e86\u4f60\u8fd9\u79cd\u65f6\u5019\uff0c\u6211\u60f3\u4ed6\u8bf4\u4e0d\u5b9a\u6bd4\u4f60\u8fd8\u4fd7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u4e00\u4e2a\u4eba\u65e0\u8bba\u591a\u51b7\u591a\u997f\uff0c\u4e00\u7b11\u8d77\u6765\u603b\u4f1a\u89c9\u5f97\u8212\u670d\u7684\u591a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u738b\u52a8\u5ffd\u7136\u4ece\u5e8a\u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u60f3\u8d77\u6797\u548c\u9756\uff0c\u6211\u5012\u60f3\u8d77\u6837\u4e8b\u6765\u4e86\u3002\u201d \u3000\u3000\u80fd\u53eb\u738b\u52a8\u4ece\u5e8a\u4e0a\u8df3\u8d77\u6765\u7684\u4e8b\uff0c\u90a3\u771f\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u8d77\u4e86\u4ec0\u4e48\uff1f\u96be\u9053\u4e5f\u60f3\u628a\u6885\u82b1\u4f5c\u8001\u5a46\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u8fd9\u6885\u82b1\u6bd4\u8001\u5a46\u8fd8\u597d\uff0c\u662f\u9152\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u4e0b\u5df4\u7acb\u5373\u597d\u8c61\u8981\u6389\u4e0b\u6765\u4e86\uff0c\u5583\u5583\u9053\uff1a\u201c\u9152\uff1f\u54ea\u91cc\u6765\u7684\u9152\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u5c31\u5728\u6885\u82b1\u4e0b\u9762\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\uff1a\u201c\u628a\u6885\u82b1\u5f53\u8001\u5a46\u5df2\u7ecf\u591f\u75af\u5f97\u4e86\uff0c\u60f3\u4e0d\u5230\u8fd9\u4eba\u5c45\u7136\u66f4\u75af\u3002\u201d \u3000\u3000\u4f46\u6885\u6811\u4e0b\u7684\u7684\u786e\u786e\u57cb\u7740\u4e00\u575b\u9152\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u5c31\u8fd8\u662f\u6211\u5341\u51e0\u5e74\u524d\u57cb\u4e0b\u53bb\u7684\uff0c\u90a3\u5e74\u6211\u521a\u542c\u5230\u6797\u548c\u9756\u7684\u6545\u4e8b\uff0c\u4e5f\u7231\u4e0a\u4e86\u6885 \u82b1\uff0c\u6240\u4ee5\u5c31\u5f04\u4e86\u575b\u9152\u57cb\u5728\u6885\u6811\u4e0b\uff0c\u60f3\u6cbe\u6cbe\u6885\u82b1\u7684\u9999\u6c14\u3002\u201d \u3000\u3000\u4f60\u65e0\u8bba\u5c06\u4e00\u575b\u9152\u57cb\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u82e5\u5df2\u57cb\u4e86\u5341\u51e0\u5e74\uff0c\u8fd9\u9152\u90fd\u4e00\u5b9a\u4f1a\u9999\u5f97\u5f88\u3002 \u3000\u3000\u90ed\u5927\u8def\u62cd\u788e\u5c01\u575b\u7684\u6ce5\u76d6\uff0c\u95ed\u7740\u773c\u775b\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u53f9\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u9999\u6c14\uff0c\u7b80\u76f4\u662f\u4ed9 \u6c14\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u603b\u8be5\u611f\u6fc0\u6797\u5148\u751f\u4e86\u5427\uff0c\u82e5\u4e0d\u662f\u4ed6\uff0c\u6211\u5c31\u4e0d\u4f1a\u57cb\u8d77\u8fd9\u575b\u9152\uff1b\u82e5\u4e0d\u662f \u4ed6\uff0c\u6211\u4e5f\u4e0d\u4f1a\u60f3\u8d77\u6709\u8fd9\u575b\u9152\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5df2\u7ecf\u6ca1\u5de5\u592b\u8bf4\u8bdd\u4e86\uff0c\u6709\u9152\u559d\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u5634\u51b3\u4e0d\u505a\u522b\u7684\u4e8b\u3002 \u3000\u3000\u738b\u52a8\u5374\u62c9\u4f4f\u4e86\u4ed6\uff0c\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u71d5\u4e03\u5df2\u7ecf\u51fa\u53bb\u4e86\u4e24\u5929\uff0c\u7b97\u65f6\u95f4\u5df2\u7ecf\u5feb\u56de\u6765\u4e86\uff0c\u6211\u4eec\u81f3\u5c11\u8be5\u7b49\u7b49\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u7b49\u591a\u4e45\uff1f\u4ed6\u56de\u6765\u7684\u65f6\u5019\u6211\u4eec\u8bf4\u4e0d\u5b9a\u5df2\u51bb\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7528\u4e0d\u7740\u7b49\u8fd9\u4e48\u4e45\u3002 \u3000\u3000\u71d5\u4e03\u7684\u58f0\u97f3\u5df2\u5728\u5899\u5916\u54cd\uff0c\u9053:\u201c\u4f60\u4eec\u6b7b\u4e86\u6700\u597d\uff0c\u8fd9\u575b\u9152\u6211\u4e50\u5f97\u4e00\u4e2a\u4eba\u4eab\u53d7\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u4f46\u8033\u6735\u957f\uff0c\u9f3b\u5b50\u4e5f\u957f\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4ed6\u4e00\u55c5\u5230\u9152\u9999\u5c31\u4f1a\u8d76\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u8fd9\u957f\u9f3b\u5b50\u5e26\u4e86\u4ec0\u4e48\u4e1c\u897f\u56de\u6765\u7ed9\u6211\u4eec\u4e0b\u9152\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0b\u9152\u7684\u8fd9\u6b21\u6211\u5012\u6ca1\u5e26\u56de\u6765\uff0c\u53ea\u5e26\u56de\u6765\u4e2a\u559d\u9152\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u786e\u662f\u4e2a\u80fd\u559d\u9152\u7684\u3002\u4efb\u4f55\u4eba\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\uff0c\u90fd\u51b3\u4e0d\u4f1a\u76f8\u4fe1\u4ed6\u80fd\u559d\u90a3\u4e48\u591a\u9152\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\u7684\u65f6\u5019\u5c24\u5176\u4e0d\u4fe1\u3002 \u3000\u3000\u6797\u592a\u5e73\u662f\u4e2a\u5f88\u79c0\u6c14\u3001\u5f88\u7ea4\u5f31\uff0c\u800c\u4e14\u975e\u5e38\u6f02\u4eae\u7684\u4eba\u3002\u82e5\u8bf4\u71d5\u4e03\u957f\u7684\u6709\u70b9\u50cf\u5973\u5b69\u5b50\uff0c\u90a3\u4e48\u4ed6 \u7b80\u76f4\u5c31\u8c61\u662f\u4e2a\u5973\u5b69\u5b50\u5316\u88c5\u7684\u3002 \u3000\u3000\u4ed6\u7684\u5634\u5f88\u5c0f\uff0c\u5c31\u7b97\u7528\u201c\u6a31\u6843\u5c0f\u5634\u201d\u6765\u5f62\u5bb9\u4ed6\u4e5f\u51b3\u4e0d\u8fc7\u5206\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u5634\u95ed\u5f97\u5f88\u7d27\uff0c\u5634\u5507\u7684\u989c\u8272\u53d1\u9752\uff0c\u8981\u7528\u5f88\u5927\u7684\u529b\u6c14\u624d\u80fd \u6273\u5f97\u5f00\u4ed6\u7684\u7259\u9f7f\u704c\u4e0b\u9152\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u88ab\u51bb\u5f97\u534a\u6b7b\uff0c\u997f\u5f97\u53ea\u5269\u4e0b\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u60f3\u4e0d\u5230\u4e16\u4e0a\u8fd8\u6709\u6bd4\u4ed6\u66f4\u51b7\u66f4\u997f\u7684\u4eba\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u4f60\u662f\u4ece\u54ea\u91cc\u5e26\u6765\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8def\u4e0a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u6b21\u4f60\u4ece\u8def\u4e0a\u5e26\u4e86\u6761\u732b\u56de\u6765\uff0c\u7b2c\u4e8c\u6b21\u5e26\u56de\u6765\u6761\u72d7\uff0c\u73b0\u5728\u5c45\u7136 \u6361\u5230\u4e2a\u4eba\u4e86\u3002\u7167\u8fd9\u6837\u5b50\u4e0b\u53bb\uff0c\u4f60\u4e0b\u6b21\u5c82\u975e\u8981\u4ece\u8def\u4e0a\u5e26\u4e2a\u5927\u7329\u7329\u56de\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\uff1a\u201c\u6700\u597d\u662f\u6bcd\u7329\u7329\uff0c\u521a\u597d\u53ef\u4ee5\u8ddf\u4f60\u914d\u6210\u4e00\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u4e0d\u751f\u6c14\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u82e5\u662f\u6bcd\u7334\u5b50\u5c31\u7cdf\u4e86\uff0c\u6211\u5c82\u975e\u8fd8\u7684\u53eb\u5979\u4e00\u58f0\u738b\u5927\u5ac2\uff1f\u201d \u3000\u3000\u4ed6\u8eab\u6750\u5f88\u9ad8\u5927\uff0c\u6bd4\u738b\u52a8\u81f3\u5c11\u8981\u9ad8\u4e00\u4e2a\u5934\uff0c\u8fd9\u4e00\u5411\u662f\u4ed6\u6700\u81ea\u50b2\u7684\u4e8b\u3002\u82e5\u6709\u4eba\u7528\u8fd9\u4ef6\u4e8b\u6765\u7b11 \u4ed6\uff0c\u4ed6\u975e\u4f46\u4e0d\u751f\u6c14\uff0c\u800c\u4e14\u8fd8\u5f88\u5f97\u610f\u3002 \u3000\u3000\u4ed6\u603b\u8ba4\u4e3a\u8fd9\u6837\u624d\u50cf\u4e2a\u7537\u5b50\u6c49\u5927\u4e08\u592b\u7684\u6837\u5b50\u3002 \u3000\u3000\u71d5\u4e03\u5df2\u627e\u4e86\u4e2a\u7834\u7897\u52fa\u4e86\u534a\u7897\u9152\uff0c\u7528\u529b\u6273\u5f00\u6797\u592a\u5e73\u7684\u5634\u704c\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u559d\u5230\u7b2c\u4e8c\u7897\u7684\u65f6\u5019\uff0c\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\u624d\u6e10\u6e10\u6709\u4e86\u4e9b\u8840\u8272\uff0c\u4f46\u773c\u775b\u8fd8\u662f\u95ed\u7740\u7684\uff0c\u5c06\u5634\u91cc\u5269\u4e0b \u7684\u534a\u53e3\u9152\u6162\u6162\u7684\u54bd\u4e0b\u53bb\uff0c\u4ed6\u8bf4\u4e86\u53e5\u8bdd\uff1a\u201c\u8fd9\u662f\u53c1\u5341\u5e74\u9648\u7684\u7af9\u53f6\u9752\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u6797\u592a\u5e73\u8bf4\u7684\u7b2c\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u738b\u52a8\u7b11\u4e86\uff0c\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\uff0c\u5c31\u51ed\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u4eec\u5c31\u5df2\u5c06\u6797\u592a\u5e73\u5f53\u6210\u670b\u53cb\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u670b\u53cb\u5012\u662f\u4e2a\u559d\u9152\u7684\u884c\u5bb6\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6162\u6162\u7684\u5f20\u5f00\u773c\u775b\uff0c\u77a7\u89c1\u71d5\u4e03\u624b\u91cc\u7684\u7834\u7897\uff0c\u7acb\u5373\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u7528 \u8fd9\u79cd\u7897\u6765\u559d\u9152\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\u5c31\u597d\u8c61\u770b\u5230\u6709\u4eba\u7528\u9f3b\u5b50\u5403\u996d\u3001\u7528\u811a\u62ff\u7b77\u5b50\u4e00\u6837\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0d\u7528\u8fd9\u79cd\u7897\u559d\u7528\u4ec0\u4e48\u559d\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u559d\u7af9\u53f6\u9752\u5c31\u8be5\u7528\u7fe1\u7fe0\u78a7\u7389\u76cf\uff0c\u7528\u8fd9\u79cd\u7897\u559d\u7b80\u76f4\u7cdf\u8e0f\u4e86\u597d\u9152\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u8fd8\u662f\u5c06\u5c31\u70b9\u5427\uff0c\u53ea\u8981\u95ed\u8d77\u773c\u775b\uff0c\u7834\u7897\u548c\u78a7\u7389\u76cf\u4e5f\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u60f3\u4e86\u60f3\uff0c\u5230\uff1a\u201c\u8fd9\u8bdd\u5012\u4e5f\u4e0d\u9519\uff0c\u4f46\u6211\u8fd8\u662f\u5b81\u53ef\u7528\u575b\u5b50\u559d\u3002\u201d \u3000\u3000\u9152\u575b\u5c31\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u5c45\u7136\u771f\u7684\u6367\u4e86\u8d77\u6765\uff0c\u4ef0\u8d77\u5934\u5f80\u5634\u91cc\u704c\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u65c1\u8fb9\u5e72\u770b\u7740\uff0c\u770b\u5f97\u773c\u775b\u90fd\u53d1\u4e86\u76f4\u3002 \u3000\u3000\u76f4\u7b49\u534a\u575b\u5c31\u4e0b\u4e86\u809a\uff0c\u6797\u592a\u5e73\u624d\u62b9\u4e86\u62b9\u5634\uff0c\u9053\uff1a\u201c\u597d\u9152\uff0c\u4e0b\u9152\u7684\u83dc\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0b\u9152\u83dc\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f60\u4eec\u559d\u9152\u96be\u9053\u4e0d\u7528\u4e0b\u9152\u83dc\u7684\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u4f60\u5c31\u4e0d\u61c2\u4e86\uff0c\u771f\u6b63\u559d\u9152\u7684\u4eba\uff0c\u559d\u9152\u90fd\u4e0d\u7528\u83dc\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u53c8\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u4e5f\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u4ed6\u53c8\u4ef0\u8d77\u5934\uff0c\u5c45\u7136\u5c06\u5269\u4e0b\u7684\u534a\u575b\u9152\u53c8\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u575b\u9152\u82e5\u5df2\u57cb\u85cf\u4e86\u5341\u51e0\u5e74\uff0c\u9152\u5df2\u6d53\u7f29\uff0c\u5269\u4e0b\u7684\u672c\u5c31\u53ea\u4e0d\u8fc7\u6709\u534a\u575b\u5b50\u800c\u5df2\uff0c\u4f46\u9152\u529b\u5374\u6bd4\u666e \u901a\u7684\u4e24\u575b\u5b50\u8fd8\u5927\u3002 \u3000\u3000\u6797\u592a\u5e73\u5c45\u7136\u8fd8\u662f\u9762\u4e0d\u6539\u8272\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u7684\u9152\u8fd8\u6709\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u62b1\u6b49\u5f97\u5f88\uff0c\u8fd9\u575b\u9152\u975e\u4f46\u662f\u6211\u4eec\u53c1\u4e2a\u4eba\u4eca\u5929\u7684\u4f19\u98df\uff0c\u4e5f\u662f\u6211\u4eec\u7684 \u5168\u90e8\u8d22\u4ea7\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5e73\u5e38\u5149\u559d\u9152\uff0c\u4ece\u6765\u4e0d\u5403\u996d\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5f88\u5c11\u5403\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u4eec\u771f\u662f\u9152\u9b3c\uff0c\u8981\u77e5\u9053\u5149\u559d\u9152\u6700\u4f24\u80c3\uff0c\u5076\u5c14\u4e5f\u8be5\u5403\u70b9\u996d \u7684\u3002\u201d \u3000\u3000\u4ed6\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u56db\u4e0b\u77a7\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5e73\u65f6\u5c31\u7761\u5728\u8fd9\u5f20\u5e8a\u4e0a\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5e8a\u4e5f\u80fd\u7761\u4eba\u4e48\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u81f3\u5c11\u603b\u6bd4\u7761\u5728\u8def\u4e0a\u597d\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u53c8\u60f3\u4e86\u534a\u5929\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u4e5f\u6709\u7406\uff0c\u4f60\u4eec\u8bf4\u7684\u8bdd\u597d\u8c61\u90fd\u86ee\u6709\u7406\uff0c\u770b\u6765\u6211\u5012\u53ef\u4ee5\u8ddf \u4f60\u4eec\u4ea4\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u591a\u8c22\u591a\u8c22\uff0c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f46\u73b0\u5728\u6211\u5374\u8981\u7761\u4e86\uff0c\u6211\u7761\u89c9\u7684\u65f6\u5019\uff0c\u4e0d\u559c\u6b22\u6709\u4eba\u6765\u5435\u6211\uff0c\u4f60\u4eec\u6700\u597d\u51fa\u53bb\u901b \u901b\u3002\u201d \u3000\u3000\u4ed6\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u8eba\u5728\u5e8a\u4e0a\uff0c\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5c45\u7136\u7acb\u5373\u5c31\u7761\u7740\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u77a7\u7740\u738b\u52a8\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4e0d\u4f46\u9152\u91cf\u6bd4\u4f60\u597d\uff0c\u7761\u89c9\u7684\u672c\u4e8b\u4e5f\u4e0d\u6bd4\u4f60\u5dee\u3002\u201d \u3000\u3000\u71d5\u4e03\u77a7\u7740\u90a3\u7a7a\u575b\u5b50\uff0c\u53d1\u4e86\u534a\u5929\u6014\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u5e26\u56de\u6765\u7684\u7a76\u7adf\u662f\u4e2a\u4eba\uff1f\u8fd8\u662f\u5339\u9a6c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u9a6c\u4e5f\u559d\u4e0d\u4e86\u8fd9\u4e48\u591a\u9152\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8981\u4ed6\u5c11\u559d\u4e9b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u5c31\u7b97\u7a77\uff0c\u81f3\u5c11\u603b\u4e0d\u662f\u4e2a\u5c0f\u6c14\u9b3c\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\uff1a\u201c\u6211\u5012\u89c9\u5f97\u8fd9\u4eba\u5f88\u6709\u8da3\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6709\u8da3\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u8fd9\u6761\u547d\u662f\u4f60\u6551\u56de\u6765\u7684\uff0c\u53c8\u559d\u5149\u4e86\u6211\u4eec\u53c1\u4e2a\u4eba\u4eca\u5929\u7684\u7cae\u98df\uff0c\u5360\u636e\u4e86\u8fd9\u5c4b\u5b50\u91cc \u552f\u4e00\u7684\u4e00\u5f20\u5e8a\u3002\u53ef\u662f\u4ed6\u975e\u4f46\u6ca1\u6709\u8bf4\u4e00\u53e5\u611f\u6fc0\u7684\u8bdd\uff0c\u800c\u4e14\u8fd8\u6311\u53c1\u6311\u6b7b\uff0c\u8fd8\u89c9\u5f97\u8ddf\u6211\u4eec\u4ea4\u670b\u53cb\uff0c \u662f\u5f88\u7ed9\u6211\u4eec\u9762\u5b50\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4eba\uff0c\u4f60\u8bf4\u5230\u54ea\u91cc\u624d\u627e\u5f97\u5230\u7b2c\u4e8c\u4eba\uff1f\u201d \u3000\u3000\u6240\u4ee5\u6797\u592a\u5e73\u4e5f\u7559\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u5728\u6c5f\u6e56\u4e2d\u4f60\u82e5\u8bf4\u8d77\u201c\u5bcc\u8d35\u5c71\u5e84\u201d\uff0c\u90a3\u610f\u601d\u5e76\u4e0d\u4ec5\u662f\u8bf4\u4e00\u680b\u9760\u8fd1\u575f\u573a\u3001\u70df\u7b52\u91cc\u6c38\u8fdc\u6ca1 \u6709\u70df\uff0c\u6709\u65f6\u751a\u81f3\u8fde\u706f\u5149\u90fd\u6ca1\u6709\u7684\u7a7a\u623f\u5b50\u3002 \u3000\u3000\u4f60\u53ea\u6709\u8bf4\u8d77\u5bcc\u8d35\u5c71\u5e84\uff0c\u6c5f\u6e56\u4e2d\u4eba\u5c31\u660e\u767d\u4f60\u8bf4\u7684\u662f\u4e00\u4e2a\u5f88\u5947\u5999\u7684\u56e2\u4f53\u2014\u2014\u4e00\u680b\u7a7a\u623f\u5b50\u548c\u56db \u4e2a\u4eba\uff0c\u4ed6\u4eec\u4e4b\u95f4\u6240\u4ea7\u751f\u7684\u90a3\u79cd\u4eb2\u5207\u3001\u5feb\u4e50\u548c\u535a\u7231\u7684\u6545\u4e8b\uff0c\u8fd8\u6709\u4ed6\u4eec\u56db\u4e2a\u4eba\u90a3\u79cd\u4f1f\u5927\u800c\u5947\u5999\u7684 \u53cb\u60c5\u3002 (\u4e09) \u3000\u3000\u8fd9\u4e9b\u670b\u53cb\u4e4b\u95f4\u4eff\u4f5b\u6709\u79cd\u5f88\u5947\u602a\u7684\u9ed8\u5951\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u4ece\u4e0d\u95ee\u522b\u4eba\u7684\u5f80\u4e8b\uff0c\u4e5f\u4ece\u4e0d\u5c06\u81ea\u5df1\u7684 \u5f80\u4e8b\u5bf9\u522b\u4eba\u8bf4\u8d77\u3002 \u3000\u3000\u53ef\u662f\u5728\u71d5\u4e03\u5c06\u6797\u592a\u5e73\u5e26\u56de\u6765\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u90ed\u5927\u8def\u5374\u7834\u574f\u4e86\u8fd9\u89c4\u77e9\u3002 \u3000\u3000\u90a3\u5929\u665a\u4e0a\uff0c\u96ea\u5df2\u5f00\u59cb\u6eb6\u5316\u3002 \u3000\u3000\u6797\u592a\u5e73\u8fd8\u5728\u547c\u547c\u5927\u7761\uff0c\u738b\u52a8\u5f53\u7136\u4e5f\u4e0d\u7518\u793a\u5f31\uff0c\u90ed\u5927\u8def\u53ea\u6709\u62c9\u7740\u71d5\u4e03\u5230\u5c71\u4e0b\u53bb\u201c\u6253\u730e\u201d\u3002 \u3000\u3000\u6253\u730e\u7684\u610f\u601d\u5c31\u662f\u53bb\u627e\u627e\u770b\u6709\u6ca1\u6709\u8d5a\u94b1\u7684\u673a\u4f1a\u3002 \u3000\u3000\u6ca1\u6709\u3002 \u3000\u3000\u96ea\u6eb6\u7684\u65f6\u5019\uff0c\u6bd4\u4e0b\u96ea\u7684\u65f6\u5019\u66f4\u51b7\uff0c\u5403\u9971\u4e86\u5c31\u4e0a\u5e8a\uff0c\u6b63\u662f\u5bf9\u4ed8\u5bd2\u51b7\u6700\u806a\u660e\u7684\u6cd5\u5b50\uff0c\u8857\u9053\u4e0a \u51e0\u4e4e\u8fde\u4e2a\u4eba\u5f71\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u5c31\u8c61\u662f\u4e24\u4e2a\u5b64\u9b42\u91ce\u9b3c\uff0c\u9ad8\u4e00\u811a\u4f4e\u4e00\u811a\u8d70\u5728\u6ce5\u6cde\u91cc\uff0c\u90ed\u5927\u8def\u4e00\u76f4\u5728\u77a7\u7740\u71d5\u4e03 \u7684\u9774\u5b50\u3002 \u3000\u3000\u5230\u540e\u6765\u4ed6\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u53cc\u9774\u5b50\u53c8\u88c5\u4e0a\u5e95\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u95ee\u8fc7\u4f60\u4ece\u524d\u90a3\u53cc\u9774\u5e95\u600e\u4f1a\u503c\u4e0a\u5343\u4e24\u94f6\u5b50\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u95ee\u8fc7\u4f60\u600e\u4e48\u4f1a\u6b7b\u8fc7\u4e03\u6b21\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u7684\u786e\u6ca1\u6709\u95ee\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u91cc\u6ee1\u6000\u5e0c\u671b\uff0c\u9053\uff1a\u201c\u6211\u82e5\u95ee\u5462\uff1f\u4f60\u80af\u4e0d\u80af\u8bf4\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e5f\u8bb8\u80af\u2026\u2026\u4f46\u6211\u77e5\u9053\u4f60\u51b3\u4e0d\u4f1a\u95ee\u7684\uff0c\u56e0\u4e3a\u6211\u4e5f\u4ece\u6765\u6ca1\u6709\u95ee\u8fc7\u4f60\u4ec0\u4e48\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u677f\u8d77\u8138\uff0c\u7528\u529b\u54ac\u7740\u7259\u9f7f\u3002 \u3000\u3000\u71d5\u4e03\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u770b\u6797\u592a\u5e73\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u677f\u7740\u8138\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e5f\u4e0d\u60f3\u95ee\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5f53\u7136\u4e0d\u4f1a\u95ee\u4ed6\uff0c\u4f46\u81ea\u5df1\u731c\u731c\u603b\u6ca1\u5173\u7cfb\u5427\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u4e5f\u8bb8\u662f\u4e3a\u4e86\u4ef6\u4e8b\uff0c\u6240\u4ee5\u4ece\u5bb6\u91cc\u6e9c\u4e86\u51fa\u6765\u3002\u4ed6\u7a7f\u7684\u8863\u670d\u5f88\u5355\u8584\uff0c\u90a3\u8868\u793a\u4ed6\u4e00 \u5b9a\u662f\u4ece\u5f88\u6696\u548c\u7684\u5730\u65b9\u51fa\u6765\u7684\u3002\u4ed6\u8eab\u4e0a\u4ec0\u4e48\u4e1c\u897f\u90fd\u6ca1\u6709\u5e26\uff0c\u90a3\u8868\u793a\u4ed6\u51fa\u6765\u7684\u65f6\u5019\u4e00\u5b9a\u5f88\u5306\u5fd9\u3002 \u8bf4\u4e0d\u5b9a\u662f\u9003\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5012\u5f88\u7ec6\u5fc3\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5728\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\u91cc\u6328\u51b7\u53d7\u997f\uff0c\u4e00\u5b9a\u652f\u6301\u4e0d\u4e86\u591a\u4e45\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6700\u591a\uff0c\u4e5f\u4e0d\u8fc7\u80fd\u652f\u6301\u53c1\u4e24\u5929\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u82e5\u53ea\u80fd\u652f\u6301\u53c1\u5929\uff0c\u4ed6\u6700\u591a\u5c31\u53ea\u80fd\u652f\u6301\u4e00\u5929\u534a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5df2\u7ecf\u4e60\u60ef\u4e86\uff0c\u4ed6\u5374\u662f\u4e2a\u517b\u5c0a\u5904\u4f18\u7684\u5927\u5c11\u7237\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5728\u8fd9\u79cd\u5929\u6c14\uff0c\u4e00\u5929\u534a\u4e4b\u5185\uff0c\u65e0\u8bba\u8c01\u4e5f\u8d70\u4e0d\u4e86\u591a\u8fdc\u8def\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\uff0c\u4ed6\u7684\u5bb6\u5c31\u5728\u9644\u8fd1\u4e0d\u8fdc\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u9644\u8fd1\u6709\u4ec0\u4e48\u8c6a\u5bcc\u4eba\u5bb6\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6ca1\u6709\u51e0\u5bb6\uff0c\u6b66\u6797\u4e16\u5bb6\u66f4\u5c11\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6b66\u6797\u4e16\u5bb6\uff1f\u96be\u9053\u4ed6\u90a3\u4e48\u6587\u8d28\u5f6c\u5f6c\u7684\u4eba\u4e5f\u4f1a\u6b66\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u975e\u4f46\u4f1a\u6b66\uff0c\u800c\u4e14\u6b66\u529f\u8fd8\u4e0d\u5f31\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u600e\u4e48\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u5c31\u662f\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u90ed\u5927\u8def\u518d\u95ee\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u9644\u8fd1\u7684\u6b66\u6797\u4e16\u5bb6\u53ea\u6709\u4e24\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\u54ea\u5bb6\u662f\u59d3\u6797\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e24\u5bb6\u90fd\u4e0d\u59d3\u6797\uff0c\u6797\u592a\u5e73\u672c\u5c31\u4e0d\u4e00\u5b9a\u59d3\u6797\uff0c\u4ed6\u65e2\u7136\u662f\u9003\u51fa\u6765\u7684\uff0c\u600e\u4e48\u4f1a\u544a\u8bc9\u522b \u4eba\u4ed6\u7684\u771f\u540d\u5b9e\u59d3\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u662f\u90a3\u4e24\u5bb6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e00\u5bb6\u59d3\u718a\uff0c\u5e84\u4e3b\u53eb\u2018\u6843\u674e\u6ee1\u5929\u4e0b\u2019\u718a\u6a71\u4eba\uff0c\u662f\u5bb6\u5927\u6b66\u573a\u7684\u4e3b\u4eba\uff0c\u867d\u7136\u6843\u674e\u6ee1 \u5929\u4e0b\uff0c\u81ea\u5df1\u5374\u662f\u4e2a\u72ec\u8eab\u6c49\uff0c\u975e\u4f46\u6ca1\u6709\u513f\u5973\uff0c\u4e5f\u6ca1\u6709\u8001\u5a46\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5bb6\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5bb6\u59d3\u6885\uff0c\u867d\u7136\u6709\u4e00\u513f\u4e00\u5973\uff0c\u4f46\u513f\u5b50\u2018\u77f3\u4eba\u2019\u6885\u6c5d\u7532\u518d\u6c5f\u6e56\u4e2d\u6210\u540d\u5df2 \u4e45\uff0c\u5e74\u7eaa\u4e00\u5b9a\u6bd4\u6797\u592a\u5e73\u5927\u7684\u591a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8d77\u4e2a\u540d\u5b57\u53eb\u77f3\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u636e\u8bf4\u8fd9\u4e00\u5bb6\u7684\u6b66\u529f\u5f88\u5947\u7279\uff0c\u6240\u7528\u7684\u5175\u5203\u548c\u6697\u5668\u90fd\u662f\u77f3\u5934\u505a\u7684\uff0c\u6240\u4ee5\u4ed6\u7236\u4eb2\u53eb \u2018\u77f3\u795e\u2019\uff0c\u4ed6\u5c31\u53eb\u2018\u77f3\u4eba\u2019\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u4ee5\u540e\u751f\u7684\u513f\u5b50\u53eb\u4ec0\u4e48\u540d\uff1f\u4f1a\u4e0d\u4f1a\u53eb\u77f3\u72d7\uff1f\u201d \u3000\u3000\u8fd9\u662f\u5ea7\u5f88\u5b81\u9759\u7684\u5c71\u57ce\uff0c\u8857\u9053\u90fd\u5f88\u7a84\uff0c\u800c\u4e14\u6709\u70b9\u9661\u659c\u3002 \u3000\u3000\u4e24\u65c1\u623f\u5c4b\u7684\u6784\u9020\u4e5f\u5f88\u5e73\u51e1\u3002\u73b0\u5728\u867d\u7136\u8fd8\u6ca1\u6709\u8d77\u66f4\uff0c\u4f46\u5927\u591a\u6570\u4eba\u5bb6\u7684\u706f\u706b\u90fd\u5df2\u7184\u4e86\uff0c\u505a\u751f \u610f\u4e70\u5356\u7684\u4e5f\u5927\u591a\u90fd\u4e0a\u8d77\u4e86\u95e8\uff0c\u5c31\u7b97\u6709\u7684\u7a97\u6237\u91cc\u8fd8\u6709\u706f\u5149\u900f\u51fa\uff0c\u706f\u5149\u4e5f\u5f88\u6697\u6de1\u3002\u5f88\u5c11\u6709\u4eba\u4f1a\u5728 \u4e00\u95f4\u5c4b\u5b50\u91cc\u71c3\u4e24\u76cf\u706f\uff0c\u7528\u8721\u70db\u7684\u66f4\u5c11\uff0c\u56e0\u4e3a\u706f\u6cb9\u603b\u6bd4\u8721\u70db\u4fbf\u5b9c\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5b9e\u5728\u662f\u4e2a\u7a77\u5730\u65b9\uff0c\u4eba\u5728\u8fd9\u91cc\u803d\u5f97\u4e45\uff0c\u4e0d\u4f46\u4f1a\u8d8a\u6765\u8d8a\u7a77\uff0c\u800c\u4e14 \u4f1a\u8d8a\u6765\u8d8a\u61d2\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u6211\u5c31\u5f88\u559c\u6b22\u8fd9\u5730\u65b9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u65e0\u8bba\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u4f1a\u89c9\u5f97\u5f88\u7d27\u5f20\uff0c\u4e5f\u53ea\u6709\u8fd9\u91cc\uff0c\u624d\u4f1a\u89c9\u5f97\u662f\u81ea\u7531\u81ea\u5728\uff0c \u65e0\u62d8\u65e0\u675f\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u5730\u65b9\u7684\u4eba\u90fd\u7a77\u7684\u8fde\u81ea\u5df1\u90fd\u7167\u987e\u4e0d\u4e86\uff0c\u6240\u4ee5\u7edd\u6ca1\u6709\u529f\u592b\u53bb\u7ba1\u522b\u4eba\u7684\u95f2 \u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u53c8\u9519\u4e86\uff0c\u8fd9\u5730\u65b9\u4e00\u70b9\u90fd\u4e0d\u7a77\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6bd4\u8d77\u6211\u4eec\u6765\u5f53\u7136\u90fd\u4e0d\u7a77\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u770b\u7740\u8fd9\u5730\u65b9\u7684\u4eba\u7a77\uff0c\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u4ed6\u4eec\u90fd\u4e0d\u613f\u70ab\u8000\u800c\u5df2\u3002 \u8b6c\u5982\u8bf4\uff0c\u738b\u52a8\u8ba4\u5f97\u7684\u90a3\u5f53\u94fa\u8001\u677f\uff0c\u4ed6\u975e\u4f46\u4e0d\u7a77\uff0c\u800c\u4e14\u8fd8\u5fc5\u5b9a\u662f\u4e2a\u5f88\u6709\u6765\u5934\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u6765\u5934\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ee5\u6211\u770b\uff0c\u8fd9\u4eba\u4ee5\u524d\u7eb5\u7136\u4e0d\u662f\u4e2a\u6c5f\u6d0b\u5927\u76d7\uff0c\u4e5f\u5fc5\u5b9a\u662f\u4e2a\u5f88\u6709\u540d\u7684\u6b66\u6797\u4eba\u7269\u3002\u4e5f \u4e0d\u77e5\u662f\u56e0\u4e3a\u907f\u4ec7\u907f\u7978\uff0c\u8fd8\u662f\u56e0\u4e3a\u538c\u5026\u4e86\u6c5f\u6e56\uff0c\u6240\u4ee5\u624d\u8eb2\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5411\u4ed6\u8fd9\u6837\u7684\u4eba\uff0c\u5728\u8fd9\u91cc\u8fd8\u6709\u4e0d\u5c11\uff0c\u5c06\u6765\u6211\u82e5\u8981\u9000\u4f11\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u4f4f \u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u4e48\u6837\u8bf4\uff0c\u8fd9\u91cc\u5c82\u975e\u662f\u4e2a\u5367\u864e\u85cf\u9f99\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u600e\u4e48\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u6b7b\u8fc7\u4e03\u6b21\uff0c\u770b\u7684\u5c31\u81ea\u7136\u6bd4\u522b\u4eba\u591a\u4e9b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f46\u4f60\u8fd8\u662f\u6ca1\u770b\u51fa\u6797\u592a\u5e73\u7684\u6765\u5386\uff0c\u4ed6\u65e2\u7136\u4e0d\u4f1a\u662f\u6885\u5bb6\u7684\u513f\u5b50\uff0c\u4e5f\u4e0d\u4f1a\u662f\u718a\u5bb6 \u7684\u540e\u4ee3\uff0c\u8bf4\u4e86\u534a\u5929\uff0c\u8fd8\u4e0d\u662f\u7b49\u4e8e\u767d\u8bf4\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u542c\u8bf4\u8fc7\u2018\u9646\u4e0a\u9f99\u738b\u2019\u8fd9\u540d\u5b57\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u53ea\u6709\u804b\u5b50\u624d\u6ca1\u6709\u542c\u8bf4\u8fc7\uff0c\u6211\u5c31\u7b97\u5b64\u964b\u5be1\u95fb\uff0c\u81f3\u5c11\u603b\u4e0d\u662f\u804b\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u542c\u8bf4\u9646\u4e0a\u9f99\u738b\u4e5f\u6709\u5ea7\u522b\u5885\u5728\u9644\u8fd1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u96be\u9053\u6000\u7591\u6797\u592a\u5e73\u662f\u4ed6\u7684\u513f\u5b50\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6709\u53ef\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6ca1\u6709\u53ef\u80fd\uff0c\u7edd\u6ca1\u6709\u53ef\u80fd\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u9646\u4e0a\u9f99\u738b\u662f\u4e2a\u6602\u85cf\u4e03\u5c3a\u7684\u7537\u5b50\u6c49\uff0c\u600e\u4e48\u4f1a\u751f\u51fa\u4e2a\u50cf\u5c0f \u59d1\u5a18\u4f3c\u7684\u513f\u5b50\u6765\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u662f\u4e0d\u662f\u7537\u5b50\u6c49\uff0c\u5e76\u4e0d\u662f\u4ece\u4ed6\u5916\u8868\u6765\u51b3\u5b9a\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u4e0d\u5bf9\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ed\u4e0a\u4e86\u5634\uff0c\u6574\u4e2a\u4eba\u90fd\u50cf\u662f\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8857\u4e0a\u672c\u5df2\u6ca1\u6709\u884c\u4eba\uff0c\u8fd9\u662f\u5374\u6709\u4e2a\u4eba\u8885\u8885\u5a77\u5a77\u7684\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e00\u770b\u5230\u8fd9\u4eba\uff0c\u773c\u775b\u5c31\u53d1\u4e86\u76f4\u3002 \u3000\u3000\u80fd\u4ee4\u90ed\u5927\u8def\u773c\u775b\u53d1\u76f4\u7684\uff0c\u5f53\u7136\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u975e\u4f46\u6f02\u4eae\uff0c\u800c\u4e14\u6f02\u4eae\u6781\u4e86\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u7a7f\u7684\u867d\u7136\u662f\u4ef6\u7c97\u5e03\u8863\u670d\uff0c\u4f46\u65e0\u8bba\u4ec0\u4e48\u8863\u670d\u7a7f\u5728\u5979\u8eab\u4e0a\uff0c\u90fd\u4f1a\u53d8\u5f97\u5f88\u597d\u770b\uff0c\u90ed\u5927\u8def \u51e0\u4e4e\u4ece\u6765\u4e5f\u6ca1\u89c1\u8fc7\u8eab\u6750\u8fd9\u4e48\u597d\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u624b\u91cc\u63d0\u7740\u4e24\u4e2a\u5927\u7bee\u5b50\uff0c\u65e0\u8bba\u8c01\u624b\u91cc\u63d0\u7740\u4e24\u4e2a\u8fd9\u4e48\u5927\u7684\u7bee\u5b50\uff0c\u8d70\u8d77\u8def\u6765\u90fd\u4e00\u5b9a\u4f1a\u8c61\u662f\u53ea \u8783\u87f9\u3002 \u3000\u3000\u4f46\u5979\u8d70\u8def\u7684\u98ce\u59ff\u5374\u8fd8\u662f\u90a3\u4e48\u7f8e\uff0c\u8db3\u4ee5\u4ee4\u4eba\u770b\u5f97\u773c\u775b\u53d1\u76f4\uff1b\u5979\u624b\u91cc\u82e5\u6ca1\u6709\u63d0\u7bee\u5b50\uff0c\u90ed\u5927\u8def \u8bf4\u4e0d\u5b9a\u4f1a\u770b\u7684\u8fde\u773c\u73e0\u5b50\u90fd\u6389\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u672c\u6765\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u4eec\uff0c\u5ffd\u7136\u779f\u89c1\u90ed\u5927\u8def\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u62bf\u4e86\u62bf\u5634\uff0c \u5ae3\u7136\u4e00\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u4e00\u9897\u5fc3\u7acb\u5373\u5c31\u50cf\u9f13\u69cc\u822c\u201c\u5657\u25a1\u5657\u25a1\u201d\u7684\u8df3\u4e86\u8d77\u6765\uff0c\u76f4\u7b49\u8fd9\u5973\u5b69\u5b50\u5df2\u8f6c\u8fc7\u8857\u89d2\uff0c \u4ed6\u8fd8\u75f4\u75f4\u7684\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5730\u65b9\u679c\u7136\u662f\u5367\u864e\u85cf\u9f99\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6050\u6015\u4e0d\u662f\u85cf\u9f99\uff0c\u662f\u85cf\u51e4\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff0c\u5bf9\u6781\u4e86\uff0c\u53e4\u4eba\u8bf4\uff0c\u5341\u6b65\u4e4b\u5185\uff0c\u5fc5\u6709\u82b3\u8349\uff0c\u8fd9\u53e5\u8bdd\u679c\u7136\u4e00\u70b9\u4e0d \u5dee\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u633a\u8d77\u80f8\uff0c\u9053\uff1a\u201c\u4f60\u770b\u6211\u957f\u7684\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u71d5\u4e03\u4e0a\u4e0a\u4e0b\u4e0b\u7684\uff0c\u770b\u4e86\u4ed6\u51e0\u773c\uff0c\u7b54\u9053\uff1a\u201c\u8fd8\u4e0d\u9519\uff0c\u9ad8\u9ad8\u7684\u4e2a\u5b50\uff0c\u5927\u5927\u7684\u773c\u73e0\uff0c\u7b11\u8d77\u6765\u4e5f \u86ee\u6709\u4eba\u7f18\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u82e5\u662f\u5973\u5b69\u5b50\uff0c\u4f1a\u4e0d\u4f1a\u770b\u4e0a\u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u89c1\u4ed6\u7b11\u7684\u4e0d\u4f46\u5f88\u59a9\u5a9a\uff0c\u800c\u4e14\u4e5f\u5f88\u50cf\u5973\u5b69\u5b50\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u662f\u5973\u5b69 \u5b50\uff0c\u4e16\u4e0a\u53ea\u6015\u6ca1\u6709\u4e00\u4e2a\u7537\u4eba\u53d7\u5f97\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u677f\u8d77\u4e86\u8138\uff0c\u9053\uff1a\u201c\u80fd\u53d7\u5f97\u4e86\u4f60\u7684\u5973\u4eba\u53ea\u6015\u4e5f\u6ca1\u51e0\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u521a\u624d\u4e0d\u662f\u8fd8\u8bf4\u6211\u957f\u7684\u6ee1\u597d\u770b\u7684\u4e48\uff1f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u53ef\u662f\u4f60\u53c8\u810f\u3001\u53c8\u61d2\u3001\u53c8\u9760\u4e0d\u4f4f\uff0c\u5973\u4eba\u559c\u6b22\u7684\u51b3\u4e0d\u4f1a\u662f\u8fd9\u79cd\u7537\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u4f60\u4e0d\u662f\u5973\u4eba\uff0c\u5176\u5b9e\u5973\u4eba\u5c31\u559c\u6b22\u6211\u8fd9\u6837\u5b50\uff0c\u8fd9\u6837\u5b50\u624d\u662f\u7537\u513f\u672c \u8272\u3002\u201d \u3000\u3000\u71d5\u4e03\u770b\u6765\u597d\u8c61\u8981\u5410\u4e86\uff0c\u82e6\u7740\u8138\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u521a\u624d\u90a3\u5973\u5b69\u5b50\u770b\u4e0a\u4e86\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5426\u5219\u5979\u4e3a\u4ec0\u4e48\u5bf9\u6211\u7b11\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4f4f\u7b11\uff0c\u9053\uff1a\u201c\u5973\u5b69\u5b50\u7684\u7b11\u6709\u5f88\u591a\u79cd\uff0c\u5979\u4eec\u770b\u89c1\u4e00\u4e2a\u5446\u5934\u5446\u8111\u7684\u6837\u5b50\u5c31\u4f1a\u7b11\uff0c\u770b\u5230 \u765e\u86e4\u87c6\u3001\u732a\u516b\u6212\u65f6\u4e5f\u4f1a\u7b11\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u706b\u5927\u4e86\uff0c\u51e0\u4e4e\u8981\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u8ba4\u4e3a\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u95ed\u4e0a\u4e86\u5634\uff0c\u56e0\u4e3a\u521a\u624d\u90a3\u5973\u5b69\u5b50\u8fd9\u662f\u53c8\u4ece\u8857\u89d2\u8f6c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u624b\u91cc\u63d0\u7740\u7684\u7bee\u5b50\u672c\u662f\u7a7a\u7684\uff0c\u73b0\u5728\u5374\u88c5\u6ee1\u4e86\u4e1c\u897f\uff0c\u6240\u4ee5\u5979\u663e\u5f97\u5f88\u5403\u529b\uff0c\u5730\u4e0a\u53c8\u6ee1\u662f\u6ce5 \u6cde\uff0c\u5979\u811a\u4e0b\u7a81\u7136\u4e00\u6ed1\uff0c\u6574\u4e2a\u4eba\u5411\u524d\u6251\u5012\uff0c\u624b\u91cc\u7684\u7bee\u5b50\u4e5f\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5e78\u597d\u5979\u9047\u89c1\u4e86\u90ed\u5927\u8def\u548c\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u7684\u53cd\u5e94\u4e00\u5411\u5f88\u5feb\uff0c\u90ed\u5927\u8def\u7684\u53cd\u5e94\u4e5f\u4e0d\u6162\uff0c\u5979\u811a\u4e0b\u521a\u4e00\u6ed1\uff0c\u4ed6\u4eec\u7684\u4eba\u5df2\u8c61\u7bad\u4e00\u822c\u7a9c\u4e86 \u51fa\u53bb\u3002 \u3000\u3000\u7bee\u5b50\u8fd8\u6ca1\u6709\u6389\u5230\u5730\u4e0a\uff0c\u71d5\u4e03\u5df2\u4f38\u624b\u63a5\u7740\uff1b\u8fd9\u5973\u5b69\u5b50\u8fd8\u6ca1\u6709\u8dcc\u5012\uff0c\u90ed\u5927\u8def\u5df2\u4f38\u624b\u5c06\u5979\u6276 \u4f4f\u3002 \u3000\u3000\u5979\u5598\u4e86\u534a\u5929\u6c14\uff0c\u624d\u5b9a\u8fc7\u795e\u6765\uff0c\u5ffd\u7136\u53d1\u73b0\u4e00\u4e2a\u964c\u751f\u7537\u4eba\u7684\u624b\u8fd8\u6276\u7740\u81ea\u5df1\uff0c\u8138\u4e0a\u7acb\u5373\u98de\u7ea2\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u5fc3\u4e5f\u5728\u8df3\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u6ca1\u6709\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u7ea2\u7740\u8138\uff0c\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u771f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u6837\u8c22\u4f60\u4eec\u3002\u201d \u3000\u3000\u71d5\u4e03\u5df2\u53d1\u73b0\u7bee\u5b50\u91cc\u88c5\u7684\u5168\u662f\u5403\u7684\u4e1c\u897f\uff0c\u6709\u718f\u9e21\u3001\u6709\u725b\u8089\u3001\u8fd8\u6709\u4e00\u5f20\u5f20\u70d9\u5f97\u4e24\u9762\u53d1\u9ec4\u7684\u6cb9 \u997c\u3002 \u3000\u3000\u4ed6\u771f\u60f3\u8bf4\uff1a\u201c\u4f60\u8981\u8c22\u6211\u4eec\u5bb9\u6613\u6781\u4e86\uff0c\u53ea\u8981\u4e00\u53ea\u9e21\u3001\u4e24\u5f20\u997c\u3002\u201d \u3000\u3000\u4f46\u770b\u5230\u90ed\u5927\u8def\u5bf9\u4eba\u5bb6\u90a3\u79cd\u6df1\u60c5\u6b3e\u6b3e\u7684\u6837\u5b50\uff0c\u4ed6\u600e\u4e48\u80fd\u4e22\u81ea\u5df1\u670b\u53cb\u7684\u4eba\uff1f \u3000\u3000\u4f55\u51b5\uff0c\u90ed\u5927\u8def\u65e9\u5df2\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u4e8b\uff0c\u6ca1\u5173\u7cfb\uff0c\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u5c11\u5973\u5ffd\u7136\u62ac\u5934\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u867d\u662f\u201c\u4f60\u4eec\u201d\uff0c\u4f46\u773c\u775b\u5374\u53ea\u76ef\u7740\u90ed\u5927\u8def\u4e00\u4eba\u3002\u90ed\u5927\u8def\u5fc3\u4e5f\u9165\u4e86\uff0c\u4eba\u4e5f\u9165\u4e86\uff0c\u5403\u5403 \u7684\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u7528\u2026\u2026\u7528\u4e0d\u7740\u5ba2\u2026\u2026\u5ba2\u6c14\u3002\u201d \u3000\u3000\u5c11\u5973\u5df2\u63a5\u8fc7\u7bee\u5b50\uff0c\u5ffd\u53c8\u56de\u5934\u5ae3\u7136\u4e00\u7b11\uff0c\u624d\u4f4e\u4e0b\u5934\u5f80\u524d\u8d70\u3002 \u3000\u3000\u82e5\u8bf4\u90ed\u5927\u8def\u7684\u9b42\u8fd8\u5728\uff0c\u8fd9\u4e00\u7b11\u53ef\u771f\u628a\u4ed6\u7684\u9b42\u4e5f\u7b11\u98de\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u867d\u7136\u50cf\u9489\u5b50\u822c\u9489\u5728\u90a3\u91cc\uff0c\u4f46\u4ed6\u7684\u9b42\u5374\u4f3c\u5df2\u88ab\u4eba\u88c5\u5728\u7bee\u5b50\u91cc\u5e26\u8d70\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u5feb\u8ffd\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ee5\u4e3a\u6211\u771f\u662f\u4e2a\u8272\u9b3c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6de1\u6de1\u9053\uff1a\u201c\u5c31\u7b97\u4e0d\u662f\uff0c\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u672c\u8d70\u51fa\u5f88\u8fdc\uff0c\u6b64\u523b\u5ffd\u53c8\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u6211\u4e70\u4e86\u5f88\u591a\u83dc\uff0c\u4e24\u4f4d\u80af\u4e0d\u80af\u8d4f \u8138\u8ddf\u6211\u56de\u53bb\u559d\u4e00\u676f\uff1f\u201d \u3000\u3000\u8fd9\u79cd\u8981\u6c42\u4ece\u4e00\u4e2a\u7f8e\u5973\u5634\u91cc\u8bf4\u51fa\u6765\uff0c\u542c\u5728\u4e24\u4e2a\u53c8\u51b7\u53c8\u997f\u7684\u4eba\u8033\u6735\u91cc\uff0c\u53ea\u6015\u6bd4\u4e16\u4e0a\u6700\u597d\u542c\u7684 \u97f3\u4e50\u90fd\u8981\u597d\u542c\u5341\u500d\u3002 \u3000\u3000\u82e5\u6709\u4eba\u62d2\u7edd\u8fd9\u79cd\u8981\u6c42\uff0c\u4e0d\u662f\u5446\u5b50\u624d\u602a\u3002 \u3000\u3000\u71d5\u4e03\u4e0d\u662f\u5446\u5b50\uff0c\u90ed\u5927\u8def\u66f4\u4e0d\u662f\u3002 \u3000\u3000\u4ed6\u5634\u91cc\u867d\u7136\u8fd8\u5728\u8bf4\uff1a\u201c\u8fd9\u600e\u4e48\u597d\u610f\u601d\u5462\uff1f\u201d\u4f46\u4ed6\u7684\u4e00\u53cc\u811a\u5374\u65e9\u5df2\u8fc8\u5f00\u6b65\u5b50\uff0c\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5509\uff0c\u4e3a\u4ec0\u4e48\u82f1\u96c4\u603b\u662f\u96be\u8fc7\u7f8e\u4eba\u5173\u5462\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u90ed\u5927\u8def\u4e5f\u4e0d\u95ee\u95ee\u8fd9\u5973\u5b69\u5b50\u8981\u5c06\u4ed6\u4eec\u5e26\u5230\u54ea\u91cc\u53bb\uff1f \u3000\u3000\u770b\u6765\u5c31\u7b97\u5979\u8981\u5c06\u4ed6\u4eec\u5e26\u53bb\u5356\u4e86\uff0c\u90ed\u5927\u8def\u4e5f\u4f1a\u8ddf\u7740\u53bb\u7684\u3002 \u7389\u73b2\u6591>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7389\u73b2\u6591 \u3000\u3000\u5979\u7adf\u7136\u5c31\u662f\u7389\u73b2\u6591\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u4e8b\u6ca1\u6709\u4eba\u613f\u610f\u76f8\u4fe1\u3002 \u3000\u3000\u8fd9\u6e29\u67d4\u5584\u826f\u7eaf\u771f\u7684\u5c0f\u7b4b\u5a18\u771f\u5c31\u662f\u90a3\u51f6\u72e0\u6cfc\u8fa3\u9a84\u6a2a\u7684\u652f\u715e\u661f \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u76ee\u5149\u90fd\u76ef\u5728\u5979\u8138\u4e0a\u3002 \u3000\u3000\u5979\u5782\u5934\uff0c\u53d1\u5df2\u51cc\u4e71\u5fc3\u4e5f\u4f3c\u5df3\u788e\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u7a81\u4e5f\u4e0d\u7981\u6709\u4e86\u601c\u60af\u4e4b\u610f\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u4f60\u662f\u4ed6\u6bcd\u4eb2\u9009\u4e2d\u7684\u5ab3\u5987\xb7\u5374 \u662f\u4ed6\u7236\u4eb2\u7684\u4ec7\u4eba\uff1f\u4e16\u4e0a\u7530\u6709\u8fd9\u9ebd\u590d\u6742\u7684\u5173\u7cfb\uff1f\u4f60\u2026\u2026\u4f60\u4e00\u5b9a\u662f\u5728\u5f00\u73a9\u7b11\u3002\u201d \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u77e5\u9053\u8fd9\u7edd\u4e0d\u662f\u73a9\u7b11\uff0c\u4f46\u5374\u5b81\u613f\u76f8\u4fe1\u8fd9\u4e0d\u662f\u771f\u7684\u3002 \u3000\u3000\u7389\u73b2\u6591\u7b11\u5f97\u66f4\u51c4\u51c9\u8def\u7136\u9053\u201c\u6211\u660e\u767d\u4f60\u7684\u597d\u610f\u53ea\u53ef\u60dc\u4e16\u4e0a\u6709\u4e9b\u4e8b\u5c31\u5014\u504f\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u671f\u8fd8\u662f\u4e0d\u500d\u3002\u201d \u3000\u3000\u4e94\u73b2\u6591\u5782\u5934\uff0c\u9053\u201c\u9646\u4e0a\u9f99\u738b\u548c\u6211\u4eec\u7389\u5bb6\u7684\u4ec7\u6068\xb7\u5df2\u79ef\u4e86\u5f88\u591a\u5e74\uff0c\u4e8c\u5341\u5e74\u524d\u5c31\u53d1\u8fc7\u8a93\u5b9a\u8981 \u4eb2\u9650\u770b\u5230\u7389\u5bb6\u7684\u4eba\u5168\u90fd\u6b7b\u5c3d\u6b7b\u7edd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5931\u58f0\u9053\u201c\u4f60\u7236\u4eb2\u662f\u4e0d\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0d\u6562\u95ee\u51fa\u6765\u56e0\u4e3a\u5982\u679c\u7389\u73b2\u6591\u7684\u7236\u4eb2\u771f\u662f\u6b7b\u5728\u9646\u4e0a\u9f99\u738b\u7684\u624b\u91cc\uff0c\u6740\u7236\u7684\u4ec7\u63e1\uff0c\u5c31\u6ca1\u6709\u522b \u7684\u4eba\u80fd\u591f\u89e3\u5f97\u5f00\u4e86\u3002 \u3000\u3000\u7389\u73b2\u6591\u5374\u6447\u4e86\u6447\u5934\xb7\u9053\u201c\u6211\u7236\u4eb2\u5012\u4e0d\u662f\u6b7b\u5728\u4ed6\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u5979\u76ee\u4e2d\u53c8\u9732\u51fa\u4e86\u6028\u6068\u4e4b\u8272\xb7\u51b7\u51b7\u63a5\u9053\u201c\u56e0\u4e3a\u4ed6\u5c31\u7b97\u6709\u5929\u5927\u7684\u672c\u4e8b\u4e5f\u8bbe\u6cd5\u5b50\u518d\u6740\u4e00\u4e2a\u5df2\u7ecf \u6b7b\u4e86\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u677e\u4e01\u53e3\u6c14\u53c8\u5fcd\u4e0d\u4f4f\u76b1\u4e86\u76b1\u7709\xb7\u9053\u201c\u4f60\u6bcd\u4eb2\u2026\u3002\u201c \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u6211\u6bcd\u4eb2\u4e0d\u59d3\u4e94\u59d3\u536b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u59d3\u536b\uff1f\u96be\u9053\u662f\u6797\u592b\u4eba\u7684\u59d0\u59b9\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u70b9\u70b9\u5934\u8c0a\u201c\u5c31\u56e0\u4e3a\u8fd9\u5173\u7cfb\u6240\u4ee5\u4ed6\u624d\u653e\u8fc7\u4e86\u6211\u6bcd\u4eb2\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u90a3\u65f6\u6211\u6bcd\u4eb2\u8179\u4e2d\u5df2 \u6709\u6211\uff0c\u6211\u8fd8\u662f\u59d3\u4e94\u7684\u3002\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u5f8c\u6765\u4ed6\u5f53\u7136\u5df3\u77e5\u9053\u6709\u4f60\u8fd9\u9ebd\u6837\u4e00\u4e2a\u4eba\u4e86\uff0c\u201d \u3000\u3000\u4e94\u73b2\u620f\u9053\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u90fd\u5728\u8eb2\u4ed6\uff0c\u4ed6\u5728\u5317\u8fb9\uff0c\u6211\u5c31\u4e0d\u5230\u5317\u8fb9\u6765\u4ed6\u5728\u5357\u65b9\uff0c\u6211\u5c31\u4e0d\u5230\u5357\u8fb9 \u53bb\u4ed6\u7684\u540d\u6c14\u6bd4\u6211\u5927\xb7\u6211\u8eb2\u4ed6\xb7\u603b\u6bd4\u4ed6\u94b1\u6211\u5bb9\u6613\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u556a\u54fa\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\xb7\u4e00\u4e2a\u4eba\u592a\u6709\u540d\u4e5f\u4e0d\u662f\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u4e5f\u5e76\u4e0d\u592a\u574f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5176\u5b9e\uff0c\u4f60\u5f62\u4eb2\u672c\u4e0d\u8be5\u8ba9\u4f60\u6210\u540d\u7684\uff0c\u4f60\u82e5\u679c\u771f\u7684\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u5c0f\u9f13\u5a18\u4ed6\u4e5f\u8bb8\u5c31 \u6c38\u8fdc\u627e\u4e0d\u5230\u4f60\u4e86\u3002\u2019 \u3000\u3000\u4e94\u73b2\u6591\u54ac\u7259\u9053\u201c\u90a3\u9ebd\u6837\u7684\u6d3b\xb7\u548c\u6b7b\u53c8\u6709\u4ec0\u9ebd\u5206\u522b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e16\u4e0a\u6709\u5f88\u591a\u4eba\u90fd\u662f\u90a3\u9ebd\u6837\u6d3b\uff0c\u800c\u4e14\u6d3b\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u7389\u73b2\u7470\u9053\u201c\u4f46\u6211\u4eec\u706d\u5bb6\u4ece\u6765\u6ca1\u6709\u90a3\u6837\u7684\u4eba\xb7\u7389\u5bb6\u7684\u58f0\u540d\u4e5f\u4e0d\u80fd\u4ece\u6211\u8fd9\u4ee3\u65ad\u7edd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u4f60\u6bcd\u4eb2\u5462\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u9ed8\u7136\u9053\u201c\u5df2\u7ecf\u5728\u53bb\u5e74\u53bb\u4e16\u4e86\u3002\u201d \u3000\u3000\u5979\u54ac\u5634\u5507\xb7\u9053\u201c\u5979\u4e34\u6b7b\u7684\u65f6\u5019\u8fd8\u4f2f\u9646\u4e0a\u9f99\u738b\u653e\u4e0d\u8fc7\u6211\xb7\u6240\u4ee5\u7279\u5730\u53bb\u627e\u5979\u7684\u59b9\u59b9\u3002\u2026\u2606 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u5979\u53bb\u627e\u6797\u592b\u4eba\u7684\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u70b9\u4e86\u70b9\u5934\u9053\u201c\u5979\u5e0c\u671b\u6797\u592b\u4eba\u80fd\u591f\u5316\u89e3\u5f00\u6211\u4eec\u4e24\u5bb6\u7684\u51a4\u4ec7\xb7\u53ea\u53ef\u60dc\u6797\u592b\u4eba\u81ea\u5df1\u4e5f\u65e0 \u80fd\u4e3a\u529b\uff0c\u6240\u4ee5\u201d\u4e00\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u5979\u5201\u2019\u5c06\u4f60\u8bb8\u914d\u7ed9\u5979\u7684\u72ec\u751f\u4e88\uff0c\u5e0c\u671b\u4f60\u4eec\u4e24\u5bb6\u7684\u6028\u4ec7\u80fd\u4ece\u8fd9\u5a5a\u4e8b\u4e2d\u5316 \u89e3\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u6211\u60f3\u5979\u2500\u5b9a\u662f\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7528\u773c\u89d2\u819c\u6797\u592a\u5e73\u957f\u53f9\u9053\u201c\u53ea\u53ef\u60dc\u5979\u7684\u513f\u5b50\u5374\u4e0d\u660e\u767d\u6bcd\u4eb2\u7684\u597d\u610f\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u51c4\u7136\u7b11\u9053\u201c\u4e0b\u4e00\u4ee3\u7684\u4eba\u603b\u662f\u4e0d\u80fd\u5382\u89e3\u4e0a\u4ee3\u7684\u597d\u610f\uff0c\u5c31\u8fde\u6211\u4e5f\u6837\xb7\u6211\u672c\u6765\u4e5f\u6837\u4e0d\u613f \u505a\u4ed6\u4eec\u6797\u5bb6\u7684\u5ab3\u5987\u3002\u201d \u3000\u3000\u5979\u4e0d\u6562\u53bb\u770b\u6797\u592a\u5e73\uff0c\u4f46\u5979\u7684\u773c\u6ce2\u8fd8\u662f\u60c5\u4e0d\u81ea\u7981\uff0c\u5411\u6797\u592a\u5e73\u8198\u4e86 \u3000\u3000\u6797\u592a\u5e73\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u51b0\u51b7\u50f5\u786c\u5ffd\u7136\u9053\u201c\u90a3\u672a\u4f60\u4e3a\u4ec0\u9ebd\u8981\u5230\u8fd9\u91cc\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u7b11\u5f97\u66f4\u51c4\u51c9\xb7\u5e7d\u5e7d\u9053\u201c\u4f60\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u5927\u58f0\u9053\u201c\u6211\u5f53\u7136\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u54ac\u5634\u5507\uff0c\u52c9\u5f3a\u5fcd\u7530\u4e0d\u8ba9\u773c\u6cea\u6d41\u4e0b\uff0c\u53c8\u95ee\u4e86\u65ec \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e0d\u660e\u767d\u201d \u3000\u3000\u4e94\u73b2\u620f\u8eab\u5b50\u7a81\u7136\u98a4\u6296\u5636\u58f0\u9053\u201c\u597d\u6211\u544a\u8bc9\u4f60\u6211\u8fd9\u9ebd\u505a\xb7\u53ea\u4e3a\u4e86\u6211\u8ddf\u4f60\u8bf4\u8fc7\u603b\u6709\u5929\u8981\u8ba9\u4f60\u6c42 \u6211\u5ac1\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u80f8\u53e3\u8c61\u662f\u5ffd\u7136\u88ab\u4eba\u91cd\u91cd\u4e00\u51fb\xb7\u8fde\u7ad9\u90fd\u5df2\u65e0\u6cd5\u7ad9\u7a33\u3002\u7389\u73b2\u6591\u81ea\u5df1\u4e5f\u8c61\u662f\u8981\u5012\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u4e5f\u5c0f\u77e5\u8fc7\u4e86\u591a\u4e45\u6797\u592a\u5e73\u624d\u54ac\u7259\u5b57\u5b57\u9053\u201c\u73b0\u5728\u6211\u5df2\u660e\u767d\u4e86\u2026\u2606\u2500\u603b\u7b97\u660e\u767d\u4e86\u4e25 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u522b\u7684\u8bdd\uff0c\u5ffd\u7136\u8f6c\u8eab\u51b2\u8fdb\u5382\u81ea\u5df1\u7684\u623f\u95e8\u91cc\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\u95e8\u5173\u8d77\uff0c \u3000\u3000\u5929\u73b2\u73b0\u4e5f\u5f00\u6ca1\u6709\u518d\u770b\u4ed6\xb7\u4f46\u773c\u6cea\u5374\u5df4\u6084\u6084\u7684\u6d41\u4e86\u4e0b\u6765\u2500\u2026\xb7 \u3000\u3000\u4eba\u5c31\u662f\u4eba \u3000\u3000\u4e3a\u4ec0\u9ebd\u66b4\u98ce\u96e8\u6765\u4e34\u524d\u603b\u662f\u51fa\u5947\u7684\u6c89\u95f7\u5e73\u9759\uff1f \u3000\u3000\u6674\u7a7a\u5982\u6d17\u78a7\u4e07\u91cc\u3002 \u3000\u3000\u6ca1\u6709\u66b4\u98ce\u96e8\u3002 \u3000\u3000\u66b4\u98ce\u96e8\u5728\u4eba\u4eec\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u53ea\u6709\u8fd9\u79cd\u66b4\u98ce\u96e8\u5f15\u7684\u707e\u7978\xb7\u624d\u662f\u6700\u53ef\u4f2f\u7684\u3002 \u3000\u3000\u8d70\u5ed3\u4e0b\u9759\u5f97\u53ef\u4ee5\u542c\u89c1\u738b\u52a8\u5728\u5c4b\u4e8e\u91cc\u7684\u547c\u5438\u58f0\u3002 \u3000\u3000\u4ed6\u7684\u547c\u5438\u58f0\u5f88\u6c89\u91cd\u7ade\u4f3c\u5df2\u7761\u4e86\u80fd\u5728\u8fd9\u79cd\u65f6\u5019\u7761\u7684\u4eba\u771f\u6709\u672c\u4e8b\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u4e5f\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u65b0\u5a5a\u592b\u59bb\u7684\u884c\u52a8\xb7\u5728\u522b\u4eba\u773c\u4e2d\u770b\u6765\u603b\u597d\u50cf\u6709\u70b9\u795e\u79d8\u3002 \u3000\u3000\u53ea\u6709\u7ea2\u5a18\u5b50\u966a\u7389\u73b2\u6591\u4e24\u4e2a\u5bc2\u5bde\u7684\u4eba\xb7\u4e24\u9897\u7834\u788e\u7684\u5fc3\u3002 \u3000\u3000\u7389\u73b2\u6591\u75f4\u75f4\u7684\u671b\u8fdc\u65b9\u8fdc\u65b9\u4ec0\u9ebd\u90fd\u6ca1\u6709\xb7\u5979\u773c\u775b\u4e5f\u4ec0\u9ebd\u90fd\u6ca1 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u53d8\u6210\u7a7a\u7684\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5ffd\u7136\u957f\u957f\u53f9\u606f\u4e86\u58f0\uff0c\u9053\u201c\u6211\u77e5\u9053\u4f60\u521a\u5728\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u832b\u7136\u9053\u201c\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u8fd9\u6b21\u6765\u627e\u4ed6\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u62a5\u590d\xb7\u5e76\u4e0d\u662f\u4e3a\u5382\u8981\u4ed6\u8dea\u6c42\u4f60\u3002\u201d \u3000\u3000\u7389\u73b2\u806a\u9053\u201c\u6211\u4e0d\u662f\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4ee5\u524d\u4f60\u4e5f\u8bb8\u4e0d\u613f\u505a\u6797\u5bb6\u7684\u5ab3\u5987\u4f46\u73b0\u5728\u5374\u5df2\u613f\u610f\u505a\u6797\u592a\u5e73\u7684\u59bb\u5b50\xb7\u6211\u770b\u5f97 \u51fa\u3002\u201d\u5979\u957f\u957f\u53f9\u606f\u9053\u201c\u4f97\u6211\u5374\u4e0d\u61c2\xb7\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u544a\u8bc9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u7389\u73b2\u9b4f\u54ac\u7792\u5507\uff0c\u9053\u201c\u4f60\u65e2\u7136\u770b\u5f97\u51fa\uff0c\u4ed6\u4e5f\u5e94\u8be5\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53f9\u9053\u201c\u4f60\u8fd8\u4e0d\u4e86\u89e3\u7537\u4eba\uff0c\u5c24\u5176\u662f\u4ed6\u8fd9\u79cd\u7537\u4eba\u4ed6\u770b\u6765\u867d\u67d4\u5f31\u5176\u5b9e\u5374\u6bd4\u8c01\u90fd\u521a\u5f3a\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u6700\u521a\u5f3a\u7684\u4eba\u6709\u65f6\u4e5f\u5f80\u5f80\u662f\u6700\u8106\u5f31\u7684\u4eba\xb7\u522b\u4eba\u53ea\u8981\u6709\u70b9\u70b9\u5730\u65b9\u4f24\u5bb3\u5230\u4ed6\u4ed6\u7684 \u5fc3\u5c31\u4f1a\u788e\u4e86\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u4f60\u8ba4\u4e3a\u6211\u4f24\u5bb3\u4e86\u4ed6\u201d \u3000\u3000\u7ea2\u5a18\u4e88\u9053\u201c\u4f60\u4e0d\u8be5\u5bf9\u4ed6\u90a3\u6837\u8bf4\u7684\xb7\u4f60\u5e94\u8be5\u8001\u5b9e\u544a\u8bc9\u4ed6\uff0c\u73b0\u5728\u4f60\u5bf9\u4ed6\u7684\u60c5\u610f\u8ba9\u4ed6\u77e5\u9053\u4f60\u7684 \u771f\u5fc3\u4ed6\u624d\u4f1a\u4ee5\u771f\u5fc3\u5f85\u4f60\u3002\u201d \u3000\u3000\u4e94\u73b2\u6591\u51c4\u7136\u7b11\u9053\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u6211\u672c\u6765\u4e5f\u60f3\u8fd9\u9ebd\u6837\u505a\u7684\u53ef\u662f\u2026\u3002\u201c \u3000\u3000\u5979\u5782\u4e0b\u5934\u5782\u5f97\u72e0\u4f4e\u8f7b\u8f7b\u7684\u63a5\u9053\u201c\u73b0\u5728\u65e0\u8bba\u600e\u9ebd\u6837\u505a\uff0c\u90fd\u5df2\u592a\u8fdf\u5382\u201d\u2500\u3002\u201d \u3000\u3000\u7ea2\u65d7\u5b50\u770b\u5979\u76ee\u4e2d\u5145\u6ee1\u4e86\u62ce\u501f\u7684\u540c\u60c5\xb7\u4eff\u4f5b\u5df2\u4ece\u8fd9\u5014\u5f3a\u5b64\u72ec\u7684\u5c11\u5973\u8eab\u4e0a\xb7\u770b\u5230\u4e86\u5979\u81ea\u5df1\u7684 \u5f71\u5b50\u3002 \u3000\u3000\u4e0d\u9519\u73b0\u5728\u5df2\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u673a\u4f1a\u4e00\u9519\u8fc7\u662f\u6c38\u4e0d\u4f1a\u518d\u6765\u7684\u3002 \u3000\u3000\u7ea2\u6388\u4e88\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u4e5f\u8bb8\u73b0\u5728\u8fd8\u6765\u5f97\u53ca\xb7\u4e5f\u8bb8\u4f60\u5e94\u8be5\u5bf9\u4ed6\u7528\u70b9\u624b\u6bb5\u5bf9\u4ed8\u7537\u4eba\u6709\u65f6\u662f \u8981\u7528\u4e9b\u624b\u6bb5\u7684\xb7\u53ea\u8981\u4ed6\u5a36\u4e86\u4f60\u2606\u4f60\u5c31\u662f\u6797\u5bb6\u7684\u5ab3\u5987\u9646\u4e0a\u9f99\u738b\u60f3\u5fc5\u4e5f\u4e0d\u4f1a\u2026\u2500\u5c0f \u3000\u3000\u7389\u73b2\u6591\u7a81\u7136\u59cb\u8d77\u5934\u6253\u65ad\u4e86\u5979\u7684\u8bdd\u9053\u201c\u4f60\u4e0d\u5fc5\u518d\u8bf4\u4e86\u6211\u5df2\u6709\u6211\u7684\u6253\u7b97\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u9646\u4e0a\u9f99 \u738b\u4e5f\u662f\u4e2a\u4eba\u6211\u4e3a\u4ec0\u9ebd\u5b9a\u8981\u4f2f\u4ed6\uff1f\u201d \u3000\u3000\u5979\u795e\u60c5\u867d\u7136\u4ecd\u5f88\u60b2\u4f24\u4f46\u76ee\u4e2d\u5df2\u5145\u6ee1\u4e86\u5014\u5f3a\u81ea\u50b2\u7684\u8868\u60c5\u3002 \u3000\u3000\u5979\u672c\u5c31\u662f\u4e2a\u4e0d\u80af\u4f4e\u5934\u7684\u4eba\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5782\u4e0b\u5934\xb7\u77e5\u9053\u81ea\u5df1\u7684\u786e\u5df2\u4e0d\u5fc5\u518d\u8bf4\u5382\uff0c\u4e5f\u4e0d\u80fd\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u7389\u73b2\u6591\u5ffd\u53c8\u63d0\u8d77\u5979\u7684\u624b\xb7\u67d4\u58f0\u9053\u201c\u65e0\u8bba\u600e\u9ebd\u8bf4\u6211\u8fd8\u662f\u4e00\u6837\u611f\u6fc0\u4f60\u7684\u597d\u610f\u3002\u201d \u3000\u3000\u7ea2\u94f6\u5b50\u9053\u201c\u6211\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u7389\u73b2\u73ed\u9053\u201c\u4f46\u4f60\u5374\u6709\u4ef6\u4e8b\u4e0d\u6a2a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff0c\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u7389\u73b2\u745a\u671b\u738b\u52a8\u7684\u7a97\u53e3\u8f7b\u8f7b\u5730\u95ee\u9053\u201c\u4f60\u7684\u786e\u5f88\u80fd\u4e86\u89e3\u522b\u4eba\u4f46\u5374\u4e3a\u4ec0\u9ebd\u597d\u50cf\u504f\u5021\u4e0d\u80fd\u4e86\u89e3\u4ed6 \u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u4e86\u7b11\u4e5f\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5e7d\u5e7d\u7684\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u672c\u6765\u5c31 \u4e0d\u662f\u4e2a\u4eba\u5426\u5219\u73b0\u5728\u53c8\u600e\u9ebd\u7761\u5f97\u5462\uff1f\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u738b\u52a8\u771f\u7684\u7761\u4e86\u9ebd \u3000\u3000\u5c5e\u65bc\u91cc\u4e3a\u4ec0\u9ebd\u5ffd\u7136\u6ca1\u6709\u4e86\u4ed6\u7684\u547c\u5438\u58f0\uff1f \u3000\u3000\u9646\u4e0a\u9f99\u738b\u659c\u501a\u5728\u4ed6\u7684\u864e\u76ae\u8f6f\u6444\u4e0a\u76ef\u738b\u52a8\u5c31\u8c61\u8981\u5728\u4ed6\u8138\u4e0a\u9489\u51fa\u4e24\u4e2a\u6d1e\u6765\u3002 \u3000\u3000\u8fde\u738b\u52a8\u81ea\u5df1\u90fd\u89c9\u5f97\u8138\u4e0a\u4f3c\u5df2\u88ab\u9489\u51fa\u4e24\u4e2a\u6d1e\u6765\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u770b\u89c1\u8fc7\u8fd9\u9ebd\u6837\u7684\u773c\u775b\u4ece\u6765\u672a\u770b\u89c1\u8fc7\u8fd9\u9ebd\u6837\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u60f3\u8c61\u4e2d\u7684\u9646\u4e0a\u9f99\u738b\u4e5f\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u5e94\u8be5\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\u5462 \u3000\u3000\u5f53\u7136\u5b9a\u5f88\u9ad8\u5927\u3001\u5f88\u5a01\u6b66\u5f88\u96c4\u58ee\u7d2b\u9762\u7728\u9732\u72ee\u9f3b\u6d77\u53e3\u4e5f\u8bb8\u5df2\u6ee1\u80a1\u767d\u53d1\u4f46\u662f\u8170\u5e72\u8fd8\u662f\u633a\u5f97\u7b14\u76f4 \u5c31\u597d\u50cf\u4f60\u5728\u56fe\u753b\u4e2d\u770b\u5230\u7684\u5929\u795e\u6837\u3002 \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u5b9a\u8c61\u662f\u6d2a\u949f\u5de8\u9f13\u53ef\u4ee5\u9707\u5f97\u4f60\u8033\u6735\u53d1\u9ebb\u7b49\u5230\u4ed6\u6012\u6c14\u53d1\u4f5c\u65f6\uff0c\u4f60\u5458\u597d\u7684\u6cd5\u5b50 \u5c31\u662f\u8fdc\u8fdc\u79bb\u5f00\u4ed6\u3002 \u3000\u3000\u738b\u52a8\u751a\u81f3\u5df2\u51c6\u5907\u597d\u6765\u542c\u4ed6\u53d1\u6012\u65f6\u7684\u543c\u58f0\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u60f3\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u770b\u5230\u9646\u4e0a\u9f99\u738b\xb7\u5c31\u77e5\u9053\u65e0\u8bba\u8c01\u60f3\u6fc0\u8d77\u4ed6\u7684\u6012\u706b\u90fd\u5f88\u4e0d\u5bb9 \u3000\u3000\u53ea\u6709\u4ece\u4e0d\u53d1\u6012\u7684\u4eba\u624d\u771f\u6b63\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u8138\u8272\u662f\u82cd\u767d\u7684\u5934\u53d1\u72e0\u7a00\u80e1\u5b50\u4e5f\u627f\u957f\u987b\u53d1\u90fd\u4fee\u9970\u5f97\u5149\u6d01\u800c\u6574\u9f50\u53cc\u624b\u4e5f\u4fdd\u517b\u5f97\u5f88\u597d\xb7\u4ee4\u4eba \u5f88\u96be\u76f8\u4fe1\u8fd9\u53cc\u624b\u662f\u6740\u8fc7\u4eba\u7684\u3002 \u3000\u3000\u4ed6\u7a7f\u5f88\u7b80\u5355\u56e0\u4e3a\u4ed6\u77e5\u9053\u5df2\u4e0d\u5fc5\u518d\u7528\u534e\u4e3d\u7684\u8863\u7740\u548c\u73cd\u8d35\u7684\u73e0\u5b9d\u6765\u7194\u8000\u81ea\u5df1\u7684\u8eab\u4efd\u548c\u8d22\u5bcc\u3002 \u3000\u3000\u738b\u52a8\u8fdb\u6765\u7684\u65f6\u5019\xb7\u4ed6\u5e76\u6ca1\u6709\u7ad9\u8d77\u6765\u65e0\u8bba\u8c01\u8fdb\u6765\u4ed6\u90fd\u4e0d\u4f1a\u7ad9\u8d77\u91c7\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u4e0d\u4f1a\u602a\u4ed6\u5931\u793c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u53ea\u6709\u6761\u817f \u3000\u3000\u8fd9\u7eb5\u6a2a\u5929\u5382\u50b2\u89c6\u6b66\u6797\u7684\u5f53\u4e16\u4e4b\u96c4\xb7\u7adf\u662f\u4e2a\u53ea\u6709\u2018\u6761\u817f\u7684\u6b8b\u5e9f\u3002 \u3000\u3000\u5de8\u5927\u7684\u5e10\u7bf7\u91cc\uff0c\u9759\u5bc2\u65e0\u58f0\xb7\u9664\u4e86\u4ed6\u4eec\u4e24\u4e2a\u4eba\u5916\u4e5f\u6ca1\u6709\u522b\u7684\u4eba\u3002 \u3000\u3000\u738b\u52a8\u5df2\u8fdb\u6765\u5f88\u4e45\u53ea\u8bf4\u4e86\u56db\u4e2a\u5b87\u201c\u5728\u4e0b\u738b\u52a8\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u8fde\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u82e5\u662f\u6362\u4e86\u522b\u4eba\xb7\u2500\u5b9a\u4f1a\u8ba4\u4e3a\u4ed6\u6839\u672c\u6ca1\u6709\u542c\u89c1\u81ea\u5df1\u7684\u8bdd\u3002 \u3000\u3000\u4f46\u738b\u52a8\u5e76\u6ca1\u6709\u8fd9\u9ebd\u60f3\uff0c \u3000\u3000\u738b\u52a8\u77e5\u9053\u4ed6\u5fc5\u5b9a\u662f\u8981\u62ff\u5b9a\u4e3b\u610f\u5f8c\u624d\u5f00\u53e3\u3002 \u3000\u3000\u6709\u79cd\u4eba\u662f\u4ece\u6765\u4e0d\u4f1a\u8bf4\u9519\u53e5\u8bdd\uff0c\u4ed6\u663e\u7136\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u8fd9\u79cd\u4eba\u504f\u504f\u901a\u5e38\u662f\u8bf4\u9519\u4e00\u4e07\u53e5\u8bdd\u4e5f\u6ca1\u5173\u7cfb\u7684\u3002 \u3000\u3000\u738b\u52a8\u5728\u7b49\u7ad9\u5728\u7b49\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u7ec8\u4e8e\u4f38\u51fa\u624b\uff0c\u6307\u4e86\u6307\u5bf9\u9762\u7684\u4e00\u5f20\u72fc\u76ae\u57ab\xb7\u9053\u201c\u5750\u3002\u201d \u3000\u3000\u738b\u52a8\u5c31\u5750\u4e0b\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u5b50\u53c8\u6307\u4e86\u6307\u76ae\u57ab\u65c1\u7684\u5c0f\u51e0\u4e0a\u7684\u91d1\u6a2a\uff0c\u9053\u201c\u9152\u3002\u201d \u3000\u3000\u738b\u52a8\u6447\u6447\u5934\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u76ee\u5149\u707c\u707c\u9053\u201c\u4f60\u53ea\u548c\u670b\u53cb\u559d\u9152\u3002\u201d\u624b\u52a8\u9053\u201c\u6709\u65f6\u4e5f\u4f8b\u5916\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u738b\u52a8\u7f13\u7f13\u9053\u201c\u60f3\u6577\u884d\u522b\u4eba\u7684\u65f6\u5019\u4f46\u6211\u5e76\u4e0d\u60f3\u6577\u884d\u4f53\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u5929\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u6211\u4ece\u4e0d\u6577\u884d\u503c\u5f97\u6211\u5c0a\u656c\u7684\u4eba\u3002\u201d \u3000\u3000\u9646\u4ea1\u9f99\u738b\u76ef\u4ed6\u53c8\u8fc7\u4e86\u5f88\u4e45\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u6765\u65e9\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u672c\u4e0d\u662f\u6765\u559d\u9152\u7684\uff0c\u201d \u3000\u3000\u9646\u4e0a\u9f99\u5e72\u6162\u9992\u7684\u70b9\u4e86\u70b9\u5934\xb7\u9053\u201c\u4f60\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u4ed6\u7aef\u8d77\u9762\u524d\u7684\u4e94\u676f\u7f13\u7f13\u9178\u4e86\u66f0\u76ee\u5149\u7a81\u53c8\u5200\u950b\u822c\u8f6c\u5411\u5de5\u52a8\u9053\u201c\u4f60\u5728\u770b\u6211\u7684\u817f\uff1f\u201d\u738b\u52a8\u9053 \u201c\u662f\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u5b9a\u5728\u5947\u602a\uff0c\u6709\u8c01\u80fd\u591f\u780d\u65ad\u6211\u7684\u817f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u8db3\u8c01\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff0c\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u65e0\u8bba\u4ed6\u662f\u8c01\xb7\u73b0\u5728\u60f3\u5fc5\u90fd\u65e9\u5df2\u7ecf\u6b7b\u5382\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u5ffd\u53c8\u7b11\u7b11\xb7\u9053\u201c\u770b\u6765\u4f60\u5e76\u4e0d\u662f\u591a\u8bdd\u7684\u4eba\u3002\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e0d\u662f\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u559c\u6b22\u8bf4\u8bdd\u5c11\u7684\u4eba\u8fd9\u79cd\u4eba\u8bf4\u51fa\u7684\u8bdd\u901a\u5e38\u6bd4\u8f83\u53ef\u9760\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u901a\u5e38\u90fd\u662f\u7684\u3002\u201d\u9646\u4e0a\u9f99\u738b\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u4f60\u4e0d\u59a8\u8bf4\u51fa\u4f60\u662f\u60f3\u6765\u5e72\u4ecd\u9ebd\u7684\u5382\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u738b\u52a8\u5f00\u53e3\uff0c\u7a81\u53c8\u51b7\u51b7\u9053\u201c\u6700\u597d\u53ea\u7528\u4e00\u53e5\u8bdd\u8bf4\u51fa\u6765\u3002\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4e0d\u80fd\u6740\u7389\u73b2\u73ed\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6c89\u4e0b\u4e86\u8138\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u82e5\u60f3\u53eb\u6797\u592a\u5e73\u6d3b\u4e0b\u53bb\xb7\u5c31\u4e0d\u80fd\u591f\u6740\u7389\u73b2\u6591\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u82e5\u6740\u4e86\u7389\u73b2\u6591\uff0c\u6797\u592a\u5e73\u5c31\u4f1a\u4e3a\u5979\u6b7b\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u4f60\u4fe1\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u82e5\u4e0d\u4fe1\u5c31\u4e0d\u4f1a\u6765\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u76f8\u4fe1\u4e16\u4e0a\u6709\u80af\u4e3a\u522b\u4eba\u6b7b\u7684\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u4f46\u6709\u800c\u4e14\u5f88\u591a\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u8bf4\u4e24\u4e2a\u7ed9\u6211\u542c\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6797\u592a\u5e73\xb7\u6211\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u7b11\u4e86\uff0c \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u4fe1\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4e0d\u59a8\u548c\u6211\u6253\u8d4c\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u8d4c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7528\u6211\u7684\u6761\u547d\u8d4c\u4e94\u73b2\u6591\u7684\u4e00\u6761\u547d\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u600e\u9ebd\u778e\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6797\u592a\u5e73\u82e5\u4e0d\u613f\u4e3a\u7389\u73b2\u6591\u6b7b\uff0c\u4f60\u968f\u65f6\u53ef\u4ee5\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u5426\u5219\u5462\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u6240\u4ee5\u65e0\u8bba\u8f93\u8d62\u4f60\u90fd\u6beb\u65e0\u635f\u5931\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u51b7\u7b11\u9053\u201c\u6beb\u65e0\u635f\u5931\uff1f\u201d\u4e00\u201d\u8fd9\u9ebd\u60f3\u7684\u4eba\u5b9a\u8fd8\u6709\u4e24\u6761\u817f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5c31\u662f\u88ab\u4eba\u780d\u65ad\u4e00\u6761\u817f\xb7\u4e5f\u53ea\u4f1a\u53bb\u627e\u4ed6\u4e0d\u4f1a\u53bb\u627e\u4ed6\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u76ee\u5149\u66f4\u950b\u5229\u53c8\u770b\u4e86\u4ed6\u5f88\u4e45\u624d\u7f13\u7f13\u9053\u201c\u4f60\u80fd\u8bc1\u660e\u6797\u592a\u5e73\u80af\u4e3a\u5979\u6b7b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u6211\u4e0d\u80fd\u4f60\u80fd\u3002\u201d \u3000\u3000\u4ed6\u9992\u9992\u7684\u63a5\u9053\u201c\u53ef\u662f\u6211\u76f8\u4fe1\u4ed6\u4e00\u5b9a\u5f88\u5feb\u5c31\u4f1a\u5230\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u679c\u7136\u53c8\u6709\u4eba\u6765\u4e86\u3002\u6765\u7684\u4e0d\u662f\u6797\u592a\u5e73\xb7\u662f\u7ea2\u5a18\u5b50\u3001\u90ed\u5927\u8def\u548c\u9893\u4e03\u3002 \u3000\u3000\u4ed6\u4eec\u8fdb\u6765\u7684\u65f6\u5019\u738b\u52a8\u5df2\u672c\u5728\u8fd9\u5e10\u7bf7\u91cc\u3002 \u3000\u3000\u770b\u4ed6\u4eec\u8138\u4e0a\u7684\u8868\u60c5\xb7\u663e\u7136\u548c\u738b\u52a8\u521a\u540c\u6837\u60ca\u5f02\u65e0\u8bba\u8c01\u4e5f\u60f3\u4e0d\u5230\u9646\u4e0a\u9f99\u738b\u4f1a\u662f\u8fd9\u9ebd\u6837\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u6765\u7684\u76ee\u7684\u4e5f\u548c\u738b\u52a8\u6837\xb7\u56e0\u4e3a\u4ed6\u597d\u5bf9\u670b\u53cb\u4e5f\u540c\u6837\u6709\u60c5\u611f\u548c\u4fe1\u5fc3\u3002 \u3000\u3000\u201c\u4fe1\u5fc3\u201d\u786e\u5b9e\u662f\u6837\u5f88\u795e\u5947\u7684\u4e1c\u897f\xb7\u597d\u50cf\u6c38\u8fdc\u90fd\u4e0d\u820d\u4ee4\u4eba\u5931\u671b\u7684 \u3000\u3000\u53cb\u60c5\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u6797\u592a\u5e73\u5e76\u6ca1\u6709\u4ee4\u4ed6\u4eec\u5931\u671b\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u659c\u501a\u5728\u864e\u76ae\u8f6f\u635f\u4e0a\xb7\u770b\u6797\u592a\u5e73\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u4eb2\u751f\u7684\u513f\u5b50\uff0c\u4ed6\u7684\u72ec\u751f\u5b50\xb7\u4ed6\u5df2\u5c06\u8fd1\u6709\u5341\u4e94\u5e74\u672a\u66fe\u89c1\u8fc7\u5730\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5728\u770b\u4ed6\u7684\u65f6\u5019\u5c31\u597d\u50cf\u548c\u770b\u738b\u52a8\u65f6\u5e76\u6ca1\u6709\u4ec0\u9ebd\u4e24\u6837\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\xb7\u4ed6\u624d\u4f38\u51fa\u624b\u6307\u5382\u6307\u738b\u52a8\u521a\u5750\u8fc7\u7684\u72e0\u76ae\u57ab\u9053 \u3000\u3000\u6797\u592a\u5e73\u6ca1\u6709\u5165\u6a0a\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5e7f\u5df2\u50f5\u786c\u51b7\u800c\u50f5\u786c\xb7\u4f46\u4ed6\u7684\u773c\u775b\u5374\u4eff\u4f5b\u662f\u6f6e\u6e7f\u7684\u3002 \u3000\u3000\u4ed6\u9762\u5bf9\u7684\uff0c\u662f\u4ed6\u7684\u7236\u4eb2\u5341\u4e94\u5e74\u672a\u66fe\u89c1\u8fc7\u4e00\u9762\u7684\u7236\u4eb2\u3002 \u3000\u3000\u4ed6\u773c\u6cc5\u8fd8\u672a\u843d\u5c38\u5df2\u72e0\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u80a1\u535c\u8fd8\u662f\u5168\u65e0\u8868\u60c5\u4f46\u773c\u89d2\u5374\u4f3c\u5ffd\u7136\u591a\u4e86\u51e0\u6761\u76b1\u7eb9\u7ec8\u8f7b\u8f7b\u53e3\u606f\u4e86\u58f0\u9053\u201c\u4f60\u5927\u4e86\uff0c \u503e\u770b\u6765\u6709\u81ea\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u5634\u8fd8\u662f\u95ed\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u82e5\u4e0d\u613f\u8bf4\u8bdd\u4e3a\u4f55\u8981\u6765\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u53c8\u6c89\u9ed8\u4e86\u534a\u54cd\u624d\u7f13\u7f13\u9053\u201c\u6211\u77e5\u9053\u4f60\u4ece\u6765\u4e0d\u613f\u542c\u5e9f\u8bdd\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u662f\u4e0d\u662f\u5b9a\u8981\u4e94\u5bb6\u7684\u4eba\u5168\u90fd\u6b7b\u5c3d\u6b7b\u7edd\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u73b0\u5728\u7389\u5bb6\u5df2\u53ea\u5269\u4e0b\u4e2a\u4eba\u3002\u201d\u9646\u4e0a\u9f99\u738b\u9053\u5824\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u624b\u4e5f\u5df2\u63e1\u7d27\u5b87\u5b57\u9053\u201c\u4f60\u82e5\u6740\u4e86\u5979\u6211\u4e5f\u5b9a\u8981\u6740\u2018\u4e2a\u6797\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6c89\u4e0b\u4e86\u8138\u9053\u201c\u4f60\u8981\u6740\u8c01\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u9646\u4ea1\u9f99\u738b\u76ef\u4ed6\u773c\u89d2\u7684\u76b1\u7eb9\u66f4\u6df1\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u513f\u4e88\u4ed6\u9aa8\u4e2d\u7684\u9aa8\u8840\u4e2d\u7684\u8840\xb7\u8fd9\u5c11\u5e74\u8eab\u4f53\u91cc\u6d3b\u7684\u8840\u4e5f\u548c\u4ed6\u662f\u6837\u7684\xb7\u6837\u7684\u5014\u5f3a\xb7\u6837 \u7684\u9a84\u50b2\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u80fd\u6539\u53d8\u8fd9\u4e8b\u5b9e\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u80fd\u3002\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u957f\u957f\u53f9\u606f\u4e86\u58f0\u9053\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6797\u5bb6\u4eba\u8bf4\u51fa\u7684\u8bdd\u662f\u6c38\u65e0\u66f4\u6539\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u77e5\u9053\u6240\u4ee5\u6211\u624d\u8fd9\u9ebd\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u63a5\u9053\u201c\u6211\u4e5f\u77e5\u9053\u5979\u548c\u4f60\u5e76\u6ca1\u6709\u4ec7\u6068\u751a\u81f3\u4ece\u6765\u6ca1\u89c1\u8fc7\u4f60\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u5979\u53c8\u662f\u4f60\u7684\u4ee3\u9ebd\u4eba\uff1f\u4f60\u4e3a\u4ec0\u9ebd\u5b9a\u8981\u5979\u6d3b\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u6797\u5927\u5e73\u9053\u201c\u56e0\u4e3a\u5979\u6d3b\u4e0b\u53bb\xb7\u6211\u624d\u80fd\u6d3b\u5c38\u53bb\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u4eec\u7684\u60c5\u611f\u5df2\u5982\u6b64\u6df1\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u54ac\u5507\uff0c\u9053\u201c\u672c\u6765\u6211\u4e5f\u4e0d\u77e5\u9053\u7684\u2026\u2026\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u95ee\u9053\u201c\u4f60\u4ec0\u9ebd\u65f6\u5019\u624d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u8981\u6740\u5979\u7684\u65f6\u5019\u4f60\u6740\u4e86\u5979\u4f60\u771f\u7684\u4f1a\u5f88\u6109\u5feb\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6c89\u9ed8\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u81fc\u5df1\u4e5f\u4e0d\u80fd\u786e\u5b9a\u662f\u4e0d\u662f\uff1f\u4f46\u6211\u5374\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u4f60\u6740\u4e86\u5979\u4e4b\u5f8c\u5b9a\u6bd4\u4e0d\u6740\u5979\u65f6\u66f4 \u96be\u53d7\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6c89\u8138\u9053\u201c\u4f60\u771f\u7684\u7518\u5fc3\u4e3a\u5979\u6b7b\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6b7b\u5e76\u4e0d\u5bb9\u6613\u4f46\u4e5f\u4e0d\u662f\u4ec0\u9ebd\u592a\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\uff1a\u201c\u5979\u5462\u5979\u662f\u4e0d\u662f\u4e5f\u80af\u4e3a\u4f60\u505a\u540c\u6837\u7684\u4e8b\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u6c89\u9ed8\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u4e5f\u4e0d\u80fd\u786e\u5b9a\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7f13\u7f00\u9053\u201c\u90a3\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u4eec\u5bb6\u7684\u4eba\xb7\u5e76\u6ca1\u6709\u8981\u6740\u6211\u5e76\u6ca1\u6709\u5c06\u4f60\u4eec\u4e0a\u4e00\u4ee3\u7684\u6267\u6839\u7b97\u5728 \u6211\u4eec\u4e0b\u2606\u4ee3\u4eba\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u76ee\u5149\u95ea\u52a8\u7a81\u7136\u9053\u201c\u597d\u6211\u7b54\u5e94\u4f60\uff0c\u53ef\u662f\u6211\u6709\u6761\u4ef6\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4ec0\u9ebd\u6761\u4ef6\uff1f\u201d\u9646\u4ea1\u9f99\u738b\u9053\u201c\u5979\u82e5\u4e5f\u80af\u4e3a\u4f60\u987f\u7272\u81ea\u5df1\uff0c\u90a3\u5c31\u8bc1\u660e\u4f60\u4eec\u7684\u60c5\u611f\u5df2 \u8db3\u591f\u6df1\u539a\u6211\u5c31\u8ba9\u5979\u8d70\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5426\u5219\u5462\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u51b7\u51b7\u9053\u201c\u5426\u5219\u4f60\u5c31\u8be5\u660e\u767d\uff0c\u5979\u6839\u672c\u4e0d\u503c\u5f97\u4f60\u4e3a\u5979\u6b7b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u624b\u63e1\u788d\u66f4\u7d27\u3001\u9053\uff0c\u201c\u4f60\u96be\u9053\u662f\u5728\u963b\u6211\u8d4c\uff1f\u7528\u5979\u7684\u547d\u6765\u8d4c\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u8fd9\u81f3\u5c11\u8d4c\u5f97\u5f88\u516c\u5e73\xb7\u56e0\u4e3a\u65e0\u8bba\u80dc\u8d1f\u90fd\u7531\u5979\u81ea\u5df1\u6765\u51b3\u5b9a\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u600e\u77e5\u662f\u5426\u516c\u5e73\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u4fdd\u8bc1\u4f60\u4e00\u5b9a\u53ef\u4ee5\u770b\u5230\u7684\u4f46\u4f60\u4e5f\u5b9a\u8981\u7b54\u5e94\u6211\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5728\u542c\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u672a\u5230\u80dc\u8d1f\u4e4b\u524d\u4f60\u7edd\u4e0d\u80fd\u63d2\u624b\u65e0\u8bba\u8c01\u90fd\u4e0d\u80fd\u63d2\u624b\u3002\u201d\u4ed6\u76ee\u5149\u5982\u5200\u950b\uff0c\u5b87\u5b57\u63a5 \u9053\u201c\u5426\u5219\u8fd9\u573a\u8d4c\u5c31\u7b97\u4f60\u4eec\u8f93\u4e86\u3002\u201d \u3000\u3000\u5e10\u7bf7\u5f8c\u5782\u91cd\u6697\u5f97\u5f88\xb7\u4ece\u5916\u9762\u6839\u672c\u65e0\u6cd5\u770b\u5230\u91cc\u9762\u6765\u3002 \u3000\u3000\u4f46\u5185\u7684\u4eba\u5374\u53ef\u4ee5\u770b\u5f97\u89c1\u524d\u9762\u53d1\u751f\u7684\u4e8b\u3002 \u3000\u3000\u5de5\u52a8\u3001\u7ea2\u5a18\u5b50\u3001\u90ed\u5927\u8def\u3001\u71d5\u4e03\u90fd\u5df2\u5728\u8fd9\u91cc\u4e5f\u5df2\u542c\u5230\u6797\u592a\u5e73\u6240\u8bf4\u7684\u6bcf\u53e5\u8bdd\u6bcf\u4e2a\u5b87\u3002 \u3000\u3000\u4ed6\u8ba2\u89c9\u5f97\u5f88\u5b89\u6170\uff0c\u56e0\u4e3a\u6797\u592a\u5e73\u5e76\u6ca1\u6709\u4ee4\u4ed6\u4eec\u5931\u671b\u3002 \u3000\u3000\u53ef\u662f\u4e94\u73b2\u62e2\u5462\uff1f \u3000\u3000\u73b0\u5728\u4e0d\u4f46\u5979\u81ea\u5df1\u7684\u6027\u547d\uff0c\u5df2\u88ab\u5979\u81ea\u634f\u8fde\u6797\u592a\u5e73\u7684\u6027\u547d\u90fd\u5df2\u88ab\u5979\u634f\u5728\u65bc\u91cc\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u6797\u592a\u5e73\u81ea\u5df1\u4e0b\u7684\u51b3\u5b9a\u663e\u7136\u4ed6\u5bf9\u5979\u4e5f\u540c\u6837\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u5979\u4f1a\u4e0d\u4f1a\u4eca\u4ed6\u5931\u671b\uff1f \u3000\u3000\u4ed6\u4eec\u542c\u5230\u9646\u4e0a\u9f99\u738b\u4e49\u5728\u95ee\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u4ee5\u524d\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684 \u3000\u3000\u6797\u592a\u5e73\u7684\u95ee\u7b54\u6839\u7b80\u5355\u201c\u90a3\u5df2\u662f\u4ee5\u524d\u7684\u4e8b\u6211\u5c31\u7b97\u77e5\u9053\xb7\u4e5f\u5df2\u8fb0 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u5979\u7528\u4e86\u4ec0\u9ebd\u624b\u6bb5\u4f7f\u4f60\u80fd\u5982\u6b64\u4fe1\u4efb\u5979\u201d\u6797\u592a\u5e73\u9053\u201c\u5979\u7528\u95e8\u53e3\u591a\u79cd\u624b\u6bb5\u4ec5\u6709\u6548 \u7684\u5374\u53ea\u6709\u4e00\u79cd\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u54ea\u79cd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5979\u8bf4\u4e86\u771f\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u5b57\u7f00\u7f13\u63a5\u9053\u201c\u5979\u672c\u4e0d\u5fc5\u8bf4\u7684\u4e5f\u6ca1\u6709\u4eba\u901a\u5979\u53ef\u662f\u5979\u8bf4\u4e86\u771f\u8bdd\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u542c\u4e86\u8fd9\u53e5\u8bdd\u7ea2\u5a18\u5b50\u7684\u5934\u5ffd\u7136\u4e58\u535c\u3002 \u3000\u3000\u7136\u5f8c\u6797\u592a\u5e73\u4e5f\u8d70\u4e86\u8fdb\u6765\u770b\u4ed6\u4eec\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u611f\u6fc0\u3002 \u3000\u3000\u4ed6\u7684\u670b\u53cb\u4e5f\u6ca1\u6709\u4ee4\u4ed6\u5931\u671b\u3002 \u3000\u3000\u516b\u4e2a\u4eba\u9759\u9759\u7684\u7ad9\u5728\u5f20\u7bf7\u524d\u51b7\u9759\u5f97\u5c31\u8c61\u662f\u516b\u4e2a\u77f3\u5934\u4eba\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u9646\u4e0a\u9f99\u738b\u5ea7\u95ed\u7684\u5929\u9f99\u516b\u5c06\u5176\u4e2d\u65e0\u8bba\u4efb\u4f55\u4e2a\u4eba\uff0c\u90fd\u8db3\u4ee5\u5a01\u9707\u2026\u65b9\u3002 \u3000\u3000\u4f46\u7389\u73b2\u6591\u7684\u773c\u775b\u91cc\u5374\u597d\u50cf\u6839\u672c\u6ca1\u6709\u770b\u89c1\u4ed6\u94b3\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u7a7f\u7684\u8fd8\u662f\u90a3\u4ef6\u5356\u82b1\u5973\u7684\u9752\u5e03\u8863\u88f3\xb7\u6602\u5934\xb7\u4ece\u4ed6\u8ba2\u4e4b\u95f4\u8d70\u8fc7\u53bb\u8d70\u5165\u5e10\u7bf7\u3002\u6b22\u4e50\u82f1\u7ef4 \u3000\u3000\u5979\u8138\u8272\u5f88\u5e73\u9759\u3001\u4f46\u76ee\u4e2d\u5374\u5145\u6ee1\u4e86\u51b3\u5fc3\u3002 \u3000\u3000\u7136\u5f8c\u5979\u5c31\u770b\u89c1\u4e86\u9646\u4e0a\u9f99\u738b\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u5e76\u6ca1\u6709\u8ba9\u5979\u5750\u4f46\u770b\u5979\u7684\u65f6\u5019\u76ee\u5149\u5374\u6781\u950b\u5229\u3002 \u3000\u3000\u7389\u73b2\u6591\u4e5f\u6ca1\u6709\u7b49\u4ed6\u5f00\u53e3\u3002\u5c31\u5927\u58f0\u9053\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e94\u73b2\u9738\u9053\u201c\u6211\u5df2\u662f\u7389\u5bb6\u6700\u5f8c\u7684\u4e2a\u4eba\xb7\u4f60\u53ea\u8981\u6740\u4e86\u6211\xb7\u5c31\u53ef\u4ee5\u8fbe\u6210\u4f60\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u7f13\u7f13\u9053\u201c\u90a3\u5e76\u4e0d\u662f\u6211\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u4e94\u73b2\u6591\u9053\uff1a\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6de1\u8c08\u9053\u201c\u90a3\u4e0d\u8fc7\u662f\u6211\u8bf4\u8fc7\u7684\u53e5\u8bdd\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u4f60\u8bf4\u7684\u6bcf\u53e5\u8bdd\u90fd\u5df2\u505a\u5230\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u8fd8\u672a\u505a\u6210\u7684\u53ea\u6709\u8fd9\u65ec\u3002\u201d \u3000\u3000\u4e94\u73b2\u6591\u9053\u201c\u4f60\u73b0\u5728\u4e5f\u8bb8\u5f88\u5feb\u5c31\u4f1a\u505a\u5230\u4e86\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4e5f\u8bb8\u201d \u3000\u3000\u4e94\u73b2\u9b4f\u9053\u201c\u4e5f\u8bb8\u7684\u610f\u601d\u5c31\u662f\u8bf4\u4e0d\u5b9a\u3002\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u96be\u9053\u8fd8\u6562\u548c\u6211\u4ea4\u624b\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u51b7\u7b11\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u6562\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u81ea\u5df1\u771f\u7684\u5f88\u5382\u4e0d\u8d77\uff1f\u201d \u3000\u3000\u5979\u4e0d\u8ba9\u9646\u4e0a\u9f99\u738b\u5f00\u53e3\u4eba\u6c11\u5feb\u7684\u63a5\u53c8\u9053\uff1a\u2606\u4e2a\u4eba\u82e5\u8fde\u81ea\u5df1\u7684\u59bb\u5b50\u548c\u513f\u5b50\u90fd\u65e0\u6cd5\u7167\u987e\xb7\u518d\u4e86 \u4e0d\u8d77\u4e5f\u6709\u9650\u5f97\u6839\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u5c45\u7136\u5e76\u6ca1\u6709\u88ab\u6fc0\u6012\u6de1\u6de1\u9053\u201c\u4ed6\u4eec\u80fd\u7167\u987e\u81ea\u5df1\u3002\u201d \u3000\u3000\u7389\u73b2\u8e6c\u51b7\u7b11\u9053\u201c\u90a3\u662f\u4ed6\u4eec\u7684\u4e8b\u4f60\u5462\u4f60\u6709\u6ca1\u6709\u5c3d\u5230\u4f60\u7684\u8d23\u4efb\uff1f\u4e16\u4e0a\u505a\u7236\u4eb2\u548c\u4e08\u592b\u7684\u4eba\xb7\u82e5 \u90fd\u8ddf\u4f60\u4e00\u6837\uff0c\u5973\u4eba\u548c\u5b69\u5b50\u53ea\u4f2f\u5c31\u5df2\u5feb\u6b7b\u5149\u4e86\u3002\u4e25 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u7684\u8138\u7ec8\u4e8e\u6c89\u4e86\u4e0b\u53bb\u6c89\u58f0\u9053\u201c\u4f60\u6765\u5c31\u662f\u4e3a\u4e86\u8bf4\u8fd9\u4e9b\u8bdd\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u6211\u53ea\u662f\u63d0\u9192\u4f60\u4f60\u8fd8\u6709\u4e2a\u59bb\u5b50\u548c\u513f\u5b50\uff0c\u4f60\u6700\u597d\u83ab\u8981\u5fd8\u8bb0\u4ed6\u4eec\u56e0\u4e3a\u4ed6\u4eec\u4e5f\u5e76\u6ca1\u6709 \u5fd8\u8bb0\u4f60\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u51b7\u51b7\u9041\u201c\u73b0\u5728\u4f60\u5df2\u7ecf\u63d0\u9192\u8fc7\u4e86\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u957f\u957f\u5410\u51fa\u53e3\u6c14\u9053\u201c\u4e0d\u9519\u8be5\u8bf4\u7684\u8bdd\u6211\u4e5f\u5168\u90fd\u8bf4\u5b8c\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u633a\u8d77\u80f8\uff0c\u53cc\u624b\u62b1\u62f3\xb7\u9053\u201c\u8bf7\u3002\u201d \u3000\u3000\u5979\u660e\u77e5\u81ea\u5df1\u9762\u5bf9\u7684\u662f\u5929\u4e0b\u65e0\u6fc0\u7684\u9646\u4e0a\u9f99\u738b\u660e\u77e5\u6062\u5916\u8fd8\u6709\u5a01\u9707\u516b\u65b9\u7684\u5929\u9f99\u516b\u5c06\u5728\u7b49\xb7\u53ef\u662f \u5979\u795e\u60c5\u5378\u4e1d\u6beb\u6ca1\u6709\u754f\u60e7\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u867d\u7136\u7ea4\u5f31\u82d7\u6761\u4f46\u5374\u5145\u6ee1\u4e86\u51b3\u5fc3\u548c\u52c7\u6c14\u6b64\u523b\u8fd9\u4e00\u633a\u80f8 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u5ffd\u7136\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4f53\u4eca\u5e74\u5df2\u7ecf\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u7389\u73b2\u5a01\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4ec0\u9ebd\u5ffd\u7136\u95ee\u51fa\u8fd9\u53e5\u8bdd\xb7\u8fd8\u662f\u56de\u7b54\u9053\uff1a\u201c\u5341\u4e03\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u4ece\u51e0\u5c81\u5f00\u59cb\u7ec3\u6b66\u7684\uff1f\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u56db\u5c81\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u51b7\u7b11\u9053\u201c\u4f60\u53ea\u4e0d\u8fc7\u7ec3\u4e86\u5341\u53c1\u5e74\u6b66\u529f\xb7\u5c31\u5df2\u6562\u6765\u4e0e\u6211\u4ea4 \u3000\u3000\u7389\u73b2\u6591\u4e5f\u51b7\u7b11\u9053\u201c\u6211\u5c31\u7b97\u53ea\u7ec3\u8fc7\u4e00\u5929\u6b66\u529f\xb7\u4e5f\u4e00\u6837\u662f\u8981\u6765\u8ddf\u4f60\u4e00\u8f83\u9ad8\u4f4e\u6211\u4eec\u7389\u5bb6\u7684\u4eba\u65e0 \u8bba\u6b66\u529f\u6bd4\u5f97\u4e0a\u4f60\u9aa8\u5934\u603b\u662f\u786c\u7684\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u7a81\u7136\u7ea7\u58f0\u957f\u7b11\uff0c\u9053\u201c\u597d\uff0c\u597d\u786c\u7684\u9aa8\u5934\xb7\u597d\u5927\u7684\u80c6\u5b50\u3002\u201d \u3000\u3000\u957f\u7b11\u58f0\u4e2d\u4ed6\u8eab\u5b50\u5ffd\u7136\u4ece\u659c\u635f\u4e0a\u817e\u7a7a\u800c\u8d77\uff0c\u5c31\u8c61\u662f\u4e0b\u9762\u6709\u53cc\u770b\u4e0d\u89c1\u7684\u624b\u5728\u6258\u4ed6\u4f3c\u7684\u3002 \u3000\u3000\u7389\u73b2\u620f\u60c5\u4e0d\u81ea\u7981\uff0c\u5f8c\u9000\u4e86\u534a\u6b65\u3002 \u3000\u3000\u5979\u8ba4\u5f97\u51fa\u8fd9\u62cd\u6b63\u662f\u4f20\u8bf4\u4e2d\u201c\u5929\u9f99\u516b\u5f0f\u201d\u91cc\u7684\u7b2c\u5f0f\u201c\u6f5c\u9f99\u5347 \u3000\u3000\u4f46\u5979\u5374\u4ece\u672a\u60f3\u5230\u4e16\u4e0a\u771f\u7684\u6709\u4eba\u80fd\u5c06\u8f7b\u529f\u7ec3\u5230\u8fd9\u6837\u7684\u706b\u5019\u3002 \u3000\u3000\u8c01\u77e5\u9646\u4e0a\u9f99\u738b\u8eab\u4e88\u817e\u7a7a\u5c45\u7136\u8fd8\u80fd\u5f00\u53e3\u8bf4\u8bdd\u6c89\u58f0\u9053\u201c\u5c0f\u5fc3\u4f60\u7684\u5de6\u6709\u9752\u7075\u7a74\u3002\u201d \u3000\u3000\u8fd9\u201c\u9752\u7075\u7a74\u201d\u5728\u4e24\u8111\u5185\u4fa7\u4e4b\u4e0b\xb7\u7ea6\u53c1\u5206\u4e4b\u5904\u82e5\u88ab\u70b9\u4e2d\u80a9\u8180\u4e0d\u4e3e\u4e0d\u80fd\u5e26\u8863\u3002 \u3000\u3000\u4f46\u4f60\u82e5\u4e2d\u5c06\u53cc\u81c2\u4e3e\u8d77\u522b\u4eba\u4e5f\u6839\u672c\u65e0\u6cd5\u70b9\u4e2d\u4f60\u8fd9\u4e24\u5904\u7a74\u9053\u3002 \u3000\u3000\u4e94\u73b2\u6591\u51b7\u7b11\u5728\u5fc3\u91cc\u60f3\u201c\u6211\u5c31\u7b97\u4e0d\u662f\u4f60\u7684\u654c\u624b\u4f46\u4f60\u82e5\u8981\u70b9\u4e2d\u6211\u7684\u9752\u7075\u7a74\uff0c\u6015\u8fd8\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5979\u4e0b\u5b9a\u51b3\u5fc3\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\xb7\u90fd\u7edd\u4e0d\u5c06\u53cc\u81c2\u4e3e\u8d77\u3002 \u3000\u3000\u4ee5\u9646\u4e0a\u9f99\u738b\u7684\u8eab\u4efd\u5730\u4f4d\u65e2\u7136\u5df2\u8bf4\u660e\u8981\u70b9\u5979\u7684\u9752\u7075\u7a74\xb7\u81ea\u7136\u7edd\u4e0d\u4f1a\u518d\u5411\u522b\u5904\u4e0b\u624b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u9646\u4e0a\u9f99\u738b\u7684\u4eba\u5ffd\u7136\u95f4\u5df2\u5230\u4e86\u5979\u9762\u524d\u670d\u5f3a\u52b2\u7684\u98ce\u58f0\xb7\u9707\u5f97\u5979\u8863\u895f\u98d8\u98d8\u98de\u8d77\u3002 \u3000\u3000\u5979\u8eab\u5b50\u8f6c\xb7\u521a\u60f3\u501f\u52bf\u5c06\u8fd9\u80a1\u529b\u91cf\u5316\u5f00\u53ea\u542c\u201c\u556a\u556a\u201d\u4e24\u54cd\uff0c\u5de6\u53f3\u80a9\u4e95\u7a74\u5df2\u88ab\u62cd\u4f4f\uff0c\u4e24\u6761\u624b \u81c2\u518d\u4e5f\u4e3e\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u518d\u770b\u9646\u4e0a\u9f99\u738b\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u53c8\u8eba\u5728\u90a3\u8f6f\u94f6\u4e0a\xb7\u795e\u6001\u8fd8\u662f\u90a3\u9ebd\u60a0\u95f2\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u521a\u66fe\u7ecf \u51fa\u8fc7\u624b\u7684\u3002 \u3000\u3000\u7389\u73b2\u6591\u6025\u5f97\u8138\u90fd\u7ea2\u4e86\xb7\u5927\u58f0\u9053\u201c\u4f60\u70b9\u7684\u662f\u6211\u7684\u80a9\u5e76\u7a74\u4e0d\u662f\u9752\u6b22\u4e50\u8363\u8000\u7075\u707e\u3002 \u3000\u3000\u9646\u4ea1\u9f99\u738b\u6de1\u8c08\u9053\u201c\u8fd9\u5012\u7528\u4e0d\u4f60\u63d0\u9192\uff0c\u80a9\u4e95\u7a74\u548c\u9752\u7075\u7a74\uff0c\u6211\u5012\u8fd8\u5206\u5f97\u51fa\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u9ebd\u5927\u4e2a\u4eba\u8bf4\u51fa\u6765\u7684\u8bdd\u4e5f\u4e0d\u7b97\u6570\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u51e0\u65f6\u8bf4\u8fc7\u8981\u70b9\u4f60\u7684\u9752\u7075\u7a74\uff1f\u201d \u3000\u3000\u4e94\u73b2\u6591\u9053\u201c\u4f60\u521a\u660e\u660e\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u8981\u4f60\u7559\u610f\u9762\u5df2\u548c\u4eba\u4ea4\u624b\u65f6\xb7\u8eab\u4e0a\u6bcf\u5904\u7a74\u9053\u90fd\u8be5\u7559\u610f\u7684\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u63a5\u9053\u201c\u4f55\u51b5\u6b66\u529f\u9053\uff0c\u672c\u4ee5\u4e34\u654c\u531d\u53d8\u3001\u673a\u667a\u5706\u901a\u4e3a\u8981\u6211\u70b9\u4e0d\u4e2d\u4f60\u7684\u9752\u7075\u4f17\xb7\u81ea\u7136\u5c31 \u53ea\u597d\u70b9\u4f60\u7684\u80a9\u4e95\u7a74\u53cd\u6b63\u4f60\u4e24\u6761\u624b\u81c2\u8fd8\u662f\u6837\u65e0\u6cd5\u4e3e\u8d77\u6211\u53c8\u4f55\u82e6\u8981\u70b9\u4f60\u9752\u7075\u7a74\uff1f\u4f60\u82e5\u8fde\u9053\u7406\u90fd\u4e0d \u61c2\xb7\u5c31\u7b97\u518d\u7ec3\u767e\u53c1\u5341\u5e74\u4e5f\u6837\u65e0\u6cd5\u6210\u4e3a\u9ad8\u624b\u7684\u3002\u201d \u3000\u3000\u4ed6\u59ae\u636e\u8bf4\u6765\u5c31\u597d\u50cf\u5e08\u535a\u5728\u6559\u8bfa\u5f92\u5f1f\u7236\u53d4\u5728\u6559\u5bfc\u4e88\u4f84\u3002 \u3000\u3000\u4e94\u73b2\u5c48\u6c14\u5f97\u5f20\u8138\u53c8\u7531\u7ea2\u53d8\u767d\u54ac\u7259\u9053\u201c\u597d\u4f60\u6740\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u2606\u4f60\u4e0d\u670d\u6c14\u3002\u201d \u3000\u3000\u7389\u73cd\u620f\u9053\u201c\u6b7b\u4e5f\u4e0d\u670d\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u597d\u3002\u201d \u3000\u3000\u597d\u5b57\u51fa\u58f0\u53ea\u542c\u201c\u78b3\u201d\u7684\u58f0\u4e5f\u4e0d\u77e5\u662f\u4ec0\u9ebd\u4e1c\u897f\u4ece\u4ed6\u624b\u4e2d\u53d1\u51fa\u6253\u5728\u5979\u795e\u5c01\u707e\u4e0a\u3002\uff0c \u3000\u3000\u4e94\u73b2\u6591\u53ea\u89c9\u2018\u80a1\u529b\u91cf\u81ea\u80f8\u95e8\u5e03\u8fbe\u56db\u80a2\u4e24\u6761\u624b\u81c2\u7acb\u523b\u53ef\u4ee5\u52a8\u4e86\u3002 \u3000\u3000\u9694\u7a7a\u6253\u7a74\xb7\u5df2\u662f\u6c5f\u6e56\u4e2d\u6781\u5c11\u89c1\u7684\u7edd\u9876\u6b66\u529f\xb7\u60f3\u4e0d\u5230\u8fd9\u9646\u4e0a\u9f99\u738b\u7adf\u80fd\u9694\u7a7a\u89e3\u7a74\u3002 \u3000\u3000\u7389\u73b2\u7470\u54ac\u54ac\u7259\uff0c\u867d\u7136\u5df2\u660e\u7ec6\u5bf9\u65b9\u6b66\u529f\u6df1\u4e0d\u53ef\u8c03\u4e5f\u5df2\u51c6\u5907\u62fc \u3000\u3000\u8c01\u77e5\u5979\u8eab\u5b50\u521a\u63a0\u8d77\u2019\u5242\u8fd8\u672a\u4f7f\u51fa\u5ffd\u7136\u89c9\u5f97\u2018\u9635\u6696\u98ce\u5439\u8fc7\u5de6\u53f3\u9752\u7075\u7a74\u4e0a\u9ebb\u4e86\u9ebb\xb7\u4e2a\u4eba\u53c8\u843d \u5728\u5730\u4e0a\uff0c\u4e24\u6761\u624b\u81c2\u53c8\u65e0\u6cd5\u4e3e\u8d77\u3002 \u3000\u3000\u518d\u770b\u9646\u535c\u9f99\u738b\u5df2\u53c8\u8eba\u56de\u8f6f\u9521\xb7\u795e\u60c5\u8fd8\u662f\u90a3\u9ebd\u60a0\u95f2\uff0c\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u7389\u73b2\u7470\u9762\u5982\u6b7b\u7070\u3002 \u3000\u3000\u5979\u5c31\u7b97\u518d\u9a84\u50b2\u73b0\u5728\u4e5f\u5df2\u770b\u51fa\u9646\u4e0a\u9f99\u738b\u82e5\u8981\u53d6\u5979\u7684\u6027\u547d\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\u3002 \u3000\u3000\u5979\u90a3\u2018\u8eab\u4e5f\u66fe\u9707\u8c05\u8fc7\u5f88\u591a\u4eba\u7684\u6b66\u529f\uff0c\u5230\u4e86\u9646\u4e0a\u9f99\u738b\u9762\u524d\u7ade\u53d8\u5f97\u8fde\u51fa\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u9646\u4e03\u9f99\u738b\u770b\u5979\u8c08\u6de1\u9053\u201c\u73b0\u5728\u4f60\u670d\u4e0d\u670d\uff1f\u201d \u3000\u3000\u5979\u7a81\u53c8\u51b7\u7b11\uff0c\u5f88\u5feb\u7684\u63a5\u9053\u201c\u4f46\u6211\u670d\u7684\u53ea\u662f\u4f60\u7684\u6b66\u529f\u4e0d\u662f\u4f60\u7684\u4eba\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u7389\u73b2\u7470\u901a\u201c\u4f60\u7684\u6b66\u529f\u7ea7\u7136\u5929\u4e0b\u65e0\u654c\uff0c\u4f46\u4f60\u7684\u4eba\u5378\u662f\u4e2a\u6c14\u91cf\u504f\u72ed\u7684\u5c0f\u4eba\xb7\u4f60\u5c31\u7b97\u628a\u6211\u4eec\u7389 \u5bb6\u7684\u4eba\u5168\u90fd\u632b\u9aa8\u626c\u7070\u4e5f\u6ca1\u6709\u4eba\u4f1a\u670d\u4f60\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6c89\u4e0b\u4e86\u8138\u9053\u201c\u5c0f\u59a8\u5a18\u597d\u5229\u7684\u56fe\u7adf\u6562\u5728\u6211\u9762\u524d\u5982\u6b64\u653e\u8086\u3002\u201d \u3000\u3000\u7389\u73b2\u6591\u51b7\u7b11\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u6562\uff1f\u8fde\u6b7b\u6211\u90fd\u4e0d\u6015\uff0c\u8fd8\u6709\u4ec0\u9ebd\u597d\u4f2f\u7684\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u76ee\u5149\u95ea\u52a8\u8e44\u54fa\u9053\u201c\u4e0d\u9519\uff0c\u4e2a\u4eba\u82e5\u5df3\u660e\u77e5\u81ea\u5df1\u5fc5\u5979\u65e0\u7591\u8fd8\u6709\u4ec0\u9ebd\u4e8b\u4e0d\u6562\u505a\u4ec0\u9ebd\u8bdd \u4e0d\u6562\u8bf4\u7684\uff1f\u201d \u3000\u3000\u4ed6\u5634\u89d2\u5ffd\u53c8\u9732\u51fa\u4e00\u4e1d\u5947\u7279\u7684\u7b11\uff0c\u63a5\u9053\u201c\u4f46\u6211\u82e5\u7b54\u5e94\u4e0d\u6740\u4f60\u4e49\u5982\u4f55\u201d \u3000\u3000\u4e94\u73b2\u57df\u6014\u4e86\u6014\u9053\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u975e\u4f46\u4e0d\u6740\u4f24\u800c\u4e14\u7edd\u4e0d\u4f24\u4f60\u6beb\u53d1\u4f60\u6211\u4e24\u5bb6\u7684\u601d\u6028\u4e5f\u4ece\u6b64\u2026\u7b14\u52fe\u9500\u3002\u201d\u7389\u73b2 \u7470\u9053\u201c\u771f\u2026\u2026\u771f\u7684\uff1f\u201d \u3000\u3000\u9646\u706b\u9f99\u4e8e\u9053\u201c\u6211\u8bf4\u7684\u8bdd\xb7\u51e0\u65f6\u6709\u8fc7\u4e0d\u7b97\u6570\u7684\uff1f\u201d \u3000\u3000\u7389\u73b2\u73b0\u5ffd\u7136\u89c9\u5f97\u8eab\u5b50\u53d1\u8f6f\u51e0\u4e4e\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u521a\u9762\u5bf9\u7a7a\u524d\u672a\u6709\u7684\u5f3a\u654c\uff0c\u660e\u77e5\u5fc5\u6b7b\uff0c\u5374\u8fd8\u662f\u5458\u7136\u65e0\u60e7\u3002 \u3000\u3000\u4f46\u73b0\u5728\u522b\u4eba\u5df2\u7b54\u5e94\u4e0d\u6740\u5979\uff0c\u5979\u4e24\u6761\u817f\u53cd\u800c\u8f6f\u4e86\u76f4\u5230\u8fd9\u65f6\u5979\u5200\u53d1\u73b0\u5979\u672c\u6765\u662f\u4e0d\u60f3\u6b7b\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u8981\u8fd8\u80fd\u6d3b\u5f97\u4e0b\u53bb\u53c8\u6709\u8c01\u8fd8\u771f\u7684\u60f3\u6b7b\u5462\uff1f \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9510\u5229\u7684\u76ee\u5149\xb7\u4f3c\u5df2\u770b\u900f\u4e86\u5979\u7684\u5fc3\u6162\u6162\u7684\u63a5\u9053\u201c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\u4ef6\u4e8b\uff0c\u6211\u7acb\u523b\u5c31\u8ba9 \u4f60\u8d70\u4ece\u6b64\u7edd\u4e0d\u518d\u627e\u4f60\u3002\u201d \u3000\u3000\u7389\u73b2\u806a\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u53ea\u8981\u4f60\u4ece\u6b64\u4e0d\u63d0\u4f60\u548c\u6211\u513f\u5b50\u8ba2\u4e0b\u7684\u90a3\u95e8\u4eb2\u4e8b\u4ece\u6b64\u4e0d\u518d\u89c1\u4ed6\u3002\u201d \u3000\u3000\u4e94\u73b2\u7ebf\u7684\u8138\u8272\u53c8\u53d8\u4e86\u7530\u58f0\u9053\u201c\u4f60\u201d\u4e00\u2606\u4f60\u8981\u6211\u4ece\u6b64\u4e0d\u518d\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\uff0c\u201c\u4ece\u4eca\u4ee5\u5f8c\uff0c\u4f60\u53ea\u5f53\u4e16\u4e0a\u6839\u672c\u6ca1\u6709\u4ed6\u8fd9\u9ebd\u6837\u4e2a\u4eba\u53ea\u5f53\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u4f60 \u6837\u8fd8\u662f\u80fd\u6d3b\u5f97\u5f88\u597d\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\u8c08\u6de1\u9053\u201c\u4e16\u4e0a\u7684\u7537\u4eba\u5f88\u591a\u4f60\u8bf4\u4e0d\u5b9a\u5f88\u5feb\u5c31\u4f1a\u5fd8\u4e86\u4ed6\u3002\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u4e94\u73b2\u6591\u8138\u8272\u82cd\u767d\u8eab\u5b50\u53c8\u5f00\u59cb\u9891\u6296\u8c0a\u201c\u6211\u82e5\u4e0d\u7b54\u5e94\u5462\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u60a0\u7136\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u7b54\u5e94\uff1f\u4f60\u6b7b\u4e86\u4e4b\u5f8c\u5c82\u975e\u8fd8\u662f\u6837\u89c1\u4e0d\u5230\u4ed6\uff1f\u201d \u3000\u3000\u7389\u73b2\u6210\u5021\u9992\u7684\u6447\u4e86\u6447\u5934\u54fa\u54fa\u9053\u201c\u4e0d\u6837\u2026\u2500\uff0e\u7edd\u4e0d\u4e00\u6837\u3002\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6709\u4ec0\u9ebd\u4e0d\u4e00\u6837\uff1f\u201d \u3000\u3000\u4e94\u73b2\u6591\u51c4\u7136\u4e00\u7b11\xb7\u9053\u201c\u4f60\u4e0d\u4f1a\u5e62\u7684\xb7\u4f60\u8fd9\u79cd\u4eba\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u61c2\u7684\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u867d\u51c4\u51c9\u4f46\u76ee\u4e2d\u5374\u53c8\u4eff\u4f5b\u5145\u6ee1\u4e86\u79cd\u795e\u79d8\u7684\u5e78\u798f\u4e4b\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5df4\u7231\u8fc7\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u65e2\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u4ee3\u66ff\u4e5f\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u593a\u8d70\u3002 \u3000\u3000\u65e0\u8bba\u5979\u7231\u7684\u662f\u82eb\u662f\u751c\u81f3\u5c11\u5df2\u6bd4\u90a3\u4e9b\u4ece\u672a\u7231\u8fc7\u7684\u4eba\u5e78\u798f\u5f97\u591a\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u770b\u5230\u5979\u9762\u4e0a\u7684\u8868\u60c5\u81ea\u5df1\u7684\u8111\u8272\u4f3c\u5df2\u53d8\u4e86\u5ffd\u7136\u4ece\u91d1\u699c\u65c1\u7684\u53ea\u78a7\u7389\u58f6\u4e2d\xb7\u5012\u51fa\u4e86\u676f \u60e8\u78a7\u8272\u7684\u9152\u6c89\u58f0\u9053\u201c\u4f60\u82e5\u771f\u7684\u4e0d\u7b54\u5e94\uff0c\u5c31\u5c06\u8fd9\u676f\u9152\u559d\u4e0b\u53bb\u4ece\u6b64\u4e5f\u4e0d\u518d\u6709\u70e6\u607c\u3002\u201d \u3000\u3000\u5929\u73b2\u6591\u76ef\u8fd9\u676f\u6bd2\u9152\u5b57\u5b87\u9053\u201c\u6211\u53ea\u80fd\u7b54\u9877\u4f60\u4ef6\u4e8b\u3002\u201d\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4ec0\u9ebd\u4e8b\u201d \u3000\u3000\u7389\u73b2\u620f\u76ee\u5149\u51dd\u89c6\u5230\u8fdc\u65b9\u9053\u201c\u6211\u7edd\u4e0d\u80fd\u5fd8\u8bb0\u4ed6\xb7\u4e5f\u7edd\u4e0d\u4f1a\u5fd8\u8bb0\u4ed6\uff0c\u6211\u65e0\u8bba\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u5fc3 \u91cc\u603b\u6709\u4ed6\u65e0\u8bba\u4f24\u6709\u591a\u5927\u7684\u672c\u4e8b\u3002\u4e5f\u62ff\u6211\u6ca1\u529e\u6cd5\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u51b2\u51fa\u5c06\u90a3\u676f\u6bd2\u9152\u559d\u4e0b\u3002 \u3000\u3000\u7136\u5f8c\u5979\u7684\u4eba\u4e5f\u7acb\u523b\u5012\u4e0b\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u7530\u89d2\xb7\u5374\u8fd8\u662f\u5e26\u90a3\u79cd\u795e\u79d8\u7684\u3001\u5e78\u798f\u7684\u5fbd\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\uff0c\u6b64\u5f8c\u65e0\u8bba\u662f\u5929\u4e0a\u5730\u4e0b\uff0c\u90fd\u6ca1\u6709\u4eba\u518d\u80fd\u8981\u5979\u5fd8\u8bb0\u4ed6 \u3000\u3000\u56db \u3000\u3000\u9646\u4e0a\u9f99\u738b\u4f3c\u5df2\u6014\u4efb\u3002 \u3000\u3000\u4e16\u4e0a\u5c45\u7136\u771f\u6709\u8fd9\u79cd\u4eba\u8fd9\u79cd\u60c5\u611f\u8fd9\u7684\u786e\u662f\u4ed6\u6c38\u8fdc\u4e0d\u80fd\u4e86\u89e3\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u5df2\u51b2\u4e86\u8fc7\u53bb\u6251\u5012\u5728\u7389\u73b2\u6591\u8eab\u4e0a\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6ca1\u6709\u53bb\u770b\u4ed6\u5df2\u4e0d\u5fcd\u518d\u53bb\u770b\u4ed6\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u6797\u592a\u5e73\u624d\u7ad9\u8d77\u6765\uff0c\u8138\u4e0a\u6beb\u65e0\u8840\u8272\xb7\u773c\u775b\u91cc\u5374\u6ee1\u662f\u8840\u4e1d\u77aa\u4ed6\uff0c\u56db\u58f0\u9053\u201c\u4f60 \u7b54\u5e94\u8fc7\u6211\u7684\u3002\u2026\u201c \u3000\u3000\u9646\u4e0a\u9f99\u738b\u53ea\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u9ebd\u4e86\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5f25\u7b54\u5e94\u8fc7\u6211\u5b9a\u4f1a\u505a\u5f97\u5f88\u516c\u5e73\uff0c\u4f46\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u6253\u65ad\u5382\u4ed6\u7684\u8bdd\uff0c\u9053\u201c\u6211\u77e5\u9053\u8fd9\u5e76\u4e0d\u516c\u4e4e\xb7\u4f46\u4e16\u4e0a\u4e0d\u516c\u5e73\u7684\u4e8b\u672c\u5c31\u5f88\u591a\u2018\u4e2a\u4eba\u82e5 \u60f3\u6d3b\u4e0b\u5de6\xb7\u5c31\u5e94\u8be5\u5b66\u4f1a\u5fcd\u53d7\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u5b66\u4e0d\u4f1a\u6c38\u8fdc\u90fd\u5b66\u4e0d\u4f1a\u2026\u3002\u201c \u3000\u3000\u4ed6\u80a1\u4e0a\u7684\u8868\u60c5\u5ffd\u7136\u4e5f\u53d8\u5f97\u5f88\u795e\u79d8\u5f88\u5947\u7279\u5634\u91cc\u751a\u81f3\u4e5f\u9732\u51fa\u4e1d\u548c\u7389\u73b2\u57df\u540c\u6837\u7684\u5fae\u7b11\xb7\u6162\u6162\u7684 \u63a5\u9053\u201c\u6211\u53ea\u77e5\u9053\u4e16\u4e0a\u7edd\u6ca1\u6709\u4eba\u80fd\u8981\u5979\u5fd8\u8bb0\u6211\u4e5f\u7edd\u6ca1\u6709\u4eba\u80fd\u8981\u6211\u5fd8\u8bb0\u5979\u2026\u2026\u201d \u3000\u3000\u542c\u5230\u8fd9\u53e5\u8bdd\u770b\u5230\u4ed6\u9762\u7684\u8868\u60c5\u7ff1\u5927\u8def\u7684\u70ed\u62b1\u5df2\u5fcd\u4e0d\u4f4f\u6cc9\u6c34\u822c\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u8fd9\u79cd\u4eba\u4e86\u89e3\u8fd9\u79cd\u60c5\u611f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6797\u592a\u5e73\u4e5f\u4e0d\u60f3\u6d3b\u4e86\u5fcd\u4e0d\u4f4f\u8df3\u8d77\u6765\u5c31\u8981\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\u738b\u52a8\u5374\u62c9\u4f4f\u4e86\u4ed6\u6c89\u58f0\u9053\u201c\u518d\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u560e\u58f0\u8fe2\u201c\u73b0\u5728\u8fd8\u7b49\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u7684\u773c\u775b\u91cc\u53d1\u5149\xb7\u9053\u201c\u518d\u7b49\u7b49\u4f60\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\u6797\u592a\u5e73\u5df2\u5c06\u684c\u535c\u7684\u90a3\u58f6\u6bd2\u9152\u5168\u90fd\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u6211\u4e5f\u7b54\u5e94\u8fc7\u4f60\u4f60\u82e5\u6740\u5382\u5979\xb7\u6211\u4e5f\u5b9a\u8981\u6740\u4e2a\u6797\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u6740\u5382\u4ed6\u65e5\u5df1\u3002 \u3000\u3000\u4ed6\u4e5f\u5012\u4e86\u4e0b\u53bb\u5012\u5728\u7389\u73b2\u57df\u8eab\u4e0a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7684\u5634\u89d2\xb7\u90fd\u5e26\u540c\u6837\u7684\u5fae\u7b11\u7b11\u5f97\u5e78\u798f\u800c\u795e\u79d8\u2500\u4e00\uff0c \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u90fd\u7ea2\u4e86\xb7\u6b63\u60f3\u628a\u63ea\u4f4f\u4e8e\u52a8\xb7\u95ee\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u4ed6\u7b49\uff1f \u3000\u3000\u606b\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5ffd\u7136\u542c\u5230\u4e2a\u795e\u79d8\u800c\u52a8\u4eba\u7684\u58f0\u97f3\u201c\u4f60\u8f99\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u5ffd\u7136\u5df2\u51fa\u73b0\u5728\u5e10\u5e55\u91cc\uff0c\u957f\u8eab\u7389\u7acb\u98ce\u534e\u7edd\u4ee3\u8d6b\u7136\u7adf\u662f\u6797\u592a\u5e73\u7684\u6bcd\u4eb2\u201c\u536b\u72ac\u4eba\u201d\u3002 \u3000\u3000\u5979\u56f4\u89d2\u7adf\u4e5f\u5e26\u540c\u6837\u795e\u79d8\u7684\u5fae\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4f50\u3002 \u3000\u3000\u5979\u770b\u81ea\u5df1\u7684\u513f\u5b50\u6b64\u5728\u9762\u524d\u600e\u9ebd\u8fd8\u7b11\u5f97\u51fa\uff1f \u3000\u3000\u9646\u4e0a\u9f99\u738b\u8138\u535c\u7684\u8868\u60c5\u4e5f\u5f88\u5947\u7279\u4e5f\u4e0d\u77e5\u662f\u6109\u5feb\uff1f\u8fd8\u662f\u75db\u82e6\uff1f\u662f\u5f97\u610f\uff1f\u8fd8\u6700\u5931\u671b\uff1f \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4ed6\u624d\u9992\u6162\u7684\u70b9\u4e86\u70b9\u5934\u957f\u53f9\u9053\u201c\u4e0d\u9519\u6211\u8f93\u4e86\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u73b0\u5728\u4f60\u603b\u8be5\u76f8\u4fe1\u5e76\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u548c\u4f60\u4e00\u6837\u90c1\u662f\u4e3a\u81ea\u5df1\u6d3b\u7684\xb7\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053 \u4e16\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u6bd4\u751f\u547d\u66f4\u91cd\u8981\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u5782\u4e0b\u5934\u5ffd\u53c8\u7b11\u4e86\u7b11\u9053\u201c\u603b\u7b97\u6211\u77e5\u9053\u5f97\u8fd8\u4e0d\u592a\u8fdf\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u51dd\u89c6\u4ed6\u67d4\u58f0\u9053\u201c\u8fd8\u4e0d\u592a\u8fdf\uff1f\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u4e5f\u59cb\u8d77\u5934\u51dd\u89c6\u5979\xb7\u9053\u201c\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u76ee\u5149\u4e2d\u5ffd\u7136\u90fd\u6d8c\u51fa\u4e00\u79cd\u795e\u79d8\u7684\u60c5\u611f\uff0c\u5ffd\u7136\u76f8\u89c6\u7b11\u3002\u6b22\u4e50\u82f1\u7f00 \u3000\u3000\u4ed6\u4eec\u591a\u5e74\u7684\u8bef\u4f1a\u548c\u6069\u6028\xb7\u5c31\u4eff\u4f5b\u90fd\u5df2\u5728\u8fd9\u7b11\u4e4b\u4e2d\uff0c\u5316\u4f5c\u4e86\u6625\u98ce\u3002 \u3000\u3000\u672c\u5c31\u662f\u523b\u9aa8\u96be\u5fd8\u7684\u4eba\u5979\u5bf9\u4ed6\u8fd8\u6709\u4ec0\u9ebd\u4e0d\u80fd\u539f\u8c05\xb7\u4e0d\u80fd\u4e86\u89e3\u7684\u4e8b\u5462\uff1f \u3000\u3000\u53ef\u662f\u5979\u7684\u513f\u5b50\u2026\u2026 \u3000\u3000\u9646\u4ea1\u9f99\u738b\u773c\u775b\u8fd8\u5728\u51dd\u89c6\u5979\u5fae\u7b11\u9053\u201c\u4ed6\u5df2\u559d\u4e0b\u4e86\u4ed6\u4eec\u751f\u4e2d\u5458\u82eb\u7684\u676f\u9152\u73b0\u5728\u4f60\u5df2\u4e0d\u59a8\u7ed9\u4ed6\u4eec \u559d\u4e9b\u751c\u7684\u3002\u201d\u536b\u592b\u4eba\u67d4\u58f0\u9053\u201c\u5927\u5bb6\u90fd\u5e94\u8be5\u8d50\u4e9b\u751c\u7684\u4e86\u2026\u201d\u201c \u3000\u3000\u5979\u5ffd\u7136\u56de\u5934\u5411\u5782\u4e2d\u7684\u90ed\u5927\u8def\u4ed6\u4eec\u7b11\uff0c\u9053\u201c\u73b0\u5728\u4f60\u4eec\u603b\u8be5\u5df4\u660e\u767d\u662f\u600e\u9ebd\u56de\u4e8b\u5382\uff0c\u4e3a\u4ec0\u9ebd\u8fd8 \u4e0d\u51fa\u6765\u559d\u2018\u676f\u751c\u9152\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u8fd8\u4e0d\u660e\u767d\uff0c\u71d5\u4e03\u5374\u5df2\u660e\u767d\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b2c\u4e2a\u94f6\u9646\u4e0a\u9f99\u738b\u8d4c\u7684\u5e76\u4e0d\u662f\u738b\u5df7\u5927\u662f\u536b\u592b\u4eba\u3002\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u4e3a\u4e86\u5979\u513f\u5b50\u2500\u751f\u7684\u5e78\u798f\u6240\u4ee5\u5979\u624d\u4e0d\u501f\u53bb\u627e\u9646\u4e0a\u9f99\u738b\u8d4c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u7684\u8d4c\u6cd5\u4e5f\u8ddf\u6211\u4eec\u4e00\u6837\u5979\u77e5\u9053\u4e16\u4e0a\u6709\u5f88\u591a\u4eba\u90fd\u53ef\u4ee5\u4e3a\u522b\u4eba\u727a\u7272\u4ed6\u81ea\u5df1\u7684\u6240\u4ee5\u5979 \u8d62\u4e86\u3002\u201d \u3000\u3000\u5979\u51dd\u89c6\u90ed\u5927\u8def\u76ee\u4e2d\u4e5f\u5145\u6ee1\u5382\u6e29\u67d4\u4e4b\u7a9f\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f7b\u8f7b\u63e1\u4f4f\u5979\u7684\u624b\u67d4\u58f0\u9053\u201c\u4e0d\u9519\u660e\u767d\u8fd9\u9053\u7406\u7684\u4eba\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u8f93\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u9646\u4e0a\u9f99\u738b\u7ed9\u4ed6\u4eec\u559d\u7684\u90a3\u676f\u9152\u5f53\u7136\u7edd\u4e0d\u662f\u771f\u7684\u6bd2\u9152\u3002\u201d \u3000\u3000\u5f53\u7136\u4e0d\u662f\u3002 \u3000\u3000\u56e0\u4e3a\u6797\u592a\u5e73\u548c\u7389\u73b2\u6210\u73b0\u5728\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u6b63\u7d27\u7d27\u7684\u62e5\u62b1\u5728\u8d77\u3002 \u3000\u3000\u73b0\u5728\u4e16\u6b20\u5df2\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u518d\u80fd\u62c6\u6563\u4ed6\u4ecd\u4e86\u56e0\u4e3a\u4ed6\u4eec\u6709\u52c7\u6c14\u559d\u4e0b\u4ed6\u4eec\u751f\u547d\u4e2d\u6700\u82eb\u7684\u90a3\u676f \u9152\u3002 \u3000\u3000\u662f\u82e6\u9152\u4f46\u5374\u4e0d\u662f\u6bd2\u9152\u3002 \u3000\u3000\u4f60\u77e5\u4e0d\u77e5\u9053\u4e16\u535c\u6709\u79cd\u795e\u79d8\u7684\u6cb9\u80fd\u8ba9\u4f60\u9003\u907f\u8fd9\u5c18\u4e16\u7247\u523b\u7136\u5f8c\u518d\u590d\u6d3b\uff1f \u3000\u3000\u4f60\u7ec6\u4e0d\u77e5\u9053\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u795e\u79d8\u7684\u4e8b\uff0c\u662f\u7279\u5730\u4e3a\u4e86\u771f\u5fc3\u76f8\u7231\u7684\u4eba\u4e3d\u5b58\u5728\u7684 \u3000\u3000\u90ed\u5927\u8def\u8f6c\u5411\u738b\u52a8\u3002\u9053\u201c\u4f60\u521a\u4e60\u2019\u62c9\u4f50\u6211\uff0c\u96be\u9053\u4f60\u65e9\u5df3\u77e5\u9053\u90a3\u4e0d\u662f\u6bd2\u9152\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e0d\u77e5\u9053\u4f46\u6211\u5374\u77e5\u9053\uff0c\u6ca1\u6709\u4e2a\u505a\u7236\u4eb2\u7684\u4eba\u80fd\u5fcd\u5fc3\u6bd2\u6b7b\u81ea\u5df1\u7684\u513f\u4e2d\uff0c\u6211\u76f8\u4fe1\u53ea\u8981 \u662f\u4eba\u5c31\u5b9a\u6709\u4eba\u6027\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6709\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u8fd9\u5c31\u96be\u602a\u4f60\u4e5f\u4f17\u8fdc\u4e0d\u4f1a\u8f93\u4e86\u3002\u201d \u3000\u3000\u5782\u5f8c\u5df2\u53ea\u5269\u4e0b\u7ea2\u5a18\u5b50\u548c\u738b\u52a8\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5782\u5934\u9053\u201c\u4ed6\u4eec\u90fd\u5728\u5916\u9762\u7b49\u4f60\uff0c\u4f60\u8fd8\u4e0d\u51fa\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u2026\u2026\u6211\u4e0d\u914d\u8ddf\u4f60\u4eec\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u914d\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u76ee\u4e2d\u5df2\u6709\u5382\u6cea\u5149\u5782\u5934\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u8dea\u9646\u4e0a\u9f99\u738b\u4e00\u6837\u3001\u4ece\u6765\u4e0d\u77e5\u9053\u771f\u6b63\u7684\u60c5\u611f\u662f \u7528\u4e0d\u7528\u4efb\u4f55\u624b\u6bb5\u7684\u4f60\u82e5\u8981\u5f97\u5230\u522b\u4eba\u7684\u771f\u60c5\xb7\u53ea\u6709\u7528\u81ea\u5df1\u7684\u771f\u60c5\u53bb\u6362\u53d6\u7edd\u6ca1\u6709\u7b2c\u4e8c\u79cd\u6cd5\u4e8e\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f46\u73b0\u5728\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u70b9\u70b9\u5934\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u73b0\u5728\u77e5\u9053\u603b\u7b97\u8fd8\u4e0d\u592a\u8fdf\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u970d\u7136\u62ac\u8d77\u5934\u51dd\u89c6\u4ed6\u65e5\u4e2d\u5145\u6ee1\u4e86\u5e0c\u671b\u9053\u201c\u73b0\u5728\u8fd8\u4e0d\u592a\u8fdf\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e5f\u5728\u51dd\u89c6\u5979\u58f0\u97f3\u4e5f\u53d8\u5f97\u975e\u5e38\u6e29\u67d4\u67d4\u58f0\u9053\u201c\u4e0d\u8fdf\u53ea\u8981\u4f60\u771f\u7684\u80fd\u660e\u767d\u8fd9\u9053\u7406\u6c38\u8fdc\u90fd\u4e0d\u4f1a \u592a\u8fdf\u7684\u3002\u201d \u3000\u3000\u4ed6\u4f38\u51fa\u4e86\u624b\u63e1\u4f4f\u4e86\u5979\u7684\u624b\u67d4\u58f0\u9053\u201c\u6240\u4ee5\u73b0\u5728\u6211\u4eec\u4e5f\u5e94\u8be5\u8ddf\u4ed6\u4eec\u8d77\u53bb\u559d\u676f\u751c\u9152\uff0c\u6211\u8ba2\u7684\u82e6 \u9152\u4e5f\u5df2\u559d\u5f97\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u4e94 \u3000\u3000\u9152\u662f\u751c\u7684\u751c\u800c\u7f8e\u53ea\u6709\u7981\u5f97\u4f50\u8003\u9a8c\u53d7\u5f97\u4f50\u6253\u51fb\u7684\u4eba\u624d\u80fd\u559d\u5230\u8fd9\u79cd\u9152\u3002 \u3000\u3000\u4e5f\u53ea\u6709\u4ed6\u4eec\u624d\u914d\u559d\u3002 \u3000\u3000\u9646\u4e0a\u9f99\u738b\u91d1\u643a\u5728\u624b\xb7\u770b\u4ed6\u7684\u513f\u5b50\u548c\u5ab3\u5987\xb7\u9053\u201c\u6211\u4e8f\u5f85\u4e86\u4f60\u6253\u6211\u5e94\u8be5\u54c6\u507f\xb7\u968f\u4fbf\u4f60\u4eec\u8981\u4ec0 \u9ebd\uff0c\u6211\u90fd\u53ef\u4ee5\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u4eec\u4e0d\u8981\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u8981\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u8981\u7684\u6ca1\u6709\u4eba\u80fd\u7ed9\u6211\u4eec\u4f60\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u6211\u4e5f\u4e0d\u80fd\u7ed9\u4f60\u4eec\uff1f\u8c01\u80fd\u7ed9\u4f60\u4eec\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9650\u775b\u91cc\u53d1\u5149\xb7\u9053\u201c\u6211\u4eec\u81ea\u5df1\u53ea\u6709\u6211\u4eec\u81ea\u5df1\u3002\u201d \u3000\u3000\u9646\u4e0a\u9f99\u738b\u9053\u201c\u4f60\u4eec\u7a76\u7adf\u8981\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u4eec\u800d\u7684\u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u6709\u4e86\u3002\u201d\u6b22\u4e50\u82f1\u8000 \u3000\u3000\u4ed6\u63e1\u4f4f\u4ed6\u59bb\u5b50\u7684\u624b\u5145\u6ee1\u4e86\u5e78\u798f\u548c\u6ee1\u8db3\u3002\u56e0\u4e3a\u4ed6\u8981\u7684\u662f\u81ea\u7531\u3001\u7231\u60c5\u548c\u5feb\u4e50\u73b0\u5728\u4ed6\u5168\u90fd\u5f97 \u5230\u3002 \u3000\u3000\u8fd9\u7edd\u4e0d\u662f\u522b\u4eba\u8d50\u7ed9\u4ed6\u4ecd\u7684\u4e5f\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u7ed9\u4ed6\u4eec\u3002 \u3000\u3000\u4f60\u82e5\u4e5f\u60f3\u8981\u81ea\u7531\u3001\u7231\u60c5\u548c\u5feb\u4e50\u5c31\u53ea\u6709\u7528\u4f60\u7684\u4fe1\u5fc3\u3001\u51b3\u5fc3\u548c\u7231\u5fc3\u53bb\u6362\u53d6\u2606\u9664\u6b64\u4e4b\u5916\u7edd\u5bf9\u6ca1 \u6709\u522b\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u7edd\u5bf9\u6ca1\u6709\u3002\u5c31\u56e0\u4e3a\u4ed6\u4eec\u660e\u767d\u8fd9\u9053\u7406\xb7\u6240\u4ee5\u4ed6\u4ecd\u624d\u80fd\u5f97\u5230\u3002\u6240\u4ee5\uff01 \u3000\u3000\u4ed6\u4eec\u6c38\u8fdc\u90fd\u5f88\u5feb\u4e50\u3002 \u3000\u3000\u8c01\u8bf4\u82f1\u96c4\u5bc2\u5bde\uff1f \u3000\u3000\u6211\u4eec\u7684\u82f1\u96c4\u5c31\u662f\u6b22\u4e50\u7684 \u7b2c\u4e00\u7ae0\u3000\u5973\u4eba\u548c\u94b1>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e00\u7ae0\u3000\u5973\u4eba\u548c\u94b1 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u5973\u4eba\u662f\u7978\u6c34\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u6ca1\u6709\u5973\u4eba\uff0c\u51b7\u51b7\u6e05\u6e05\uff1b\u6709\u4e86\u5973\u4eba\uff0c\u9e21\u72ac\u4e0d\u5b81\u3002 \u3000\u3000\u8fd9\u4e9b\u8bdd\u81ea\u7136\u662f\u7537\u4eba\u8bf4\u7684\u3002\u4f46\u65e0\u8bba\u7537\u4eba\u4eec\u600e\u4e48\u8bf4\uff0c\u5973\u4eba\u603b\u662f\u8fd9\u4e16\u754c\u4e0a\u6240\u4e0d\u80fd\u7f3a\u5c11\u7684\u3002\u4e00\u4e07 \u4e2a\u7537\u4eba\u4e2d\u81f3\u5c11\u6709\u4e5d\u5343\u4e5d\u767e\u4e5d\u5341\u4e2a\u5b81\u613f\u5c11\u6d3b\u5341\u5e74\u4e5f\u4e0d\u80fd\u6ca1\u6709\u5973\u4eba\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u94b1\u53ef\u901a\u795e\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u91d1\u94b1\u4e07\u6076\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u94b1\u4e5f\u662f\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u7f3a\u5c11\u7684\u3002\u4e00\u4e2a\u4eba\u82e5\u662f\u6ca1\u6709\u94b1\uff0c\u5c31\u597d\u8c61\u4e00\u53e3\u7a7a\u9ebb\u888b\uff0c \u6c38\u8fdc\u90fd\u6ca1\u6cd5\u5b50\u7ad9\u5f97\u76f4\u3002 \u3000\u3000\u8fd9\u4e24\u6837\u4e1c\u897f\u4e0d\u4f46\u53ef\u4ee5\u4ee4\u6700\u806a\u660e\u7684\u4eba\u53d8\u6210\u5446\u5b50\uff0c\u4e5f\u53ef\u4ee5\u4ee4\u6700\u8981\u597d\u7684\u670b\u53cb\u53d8\u6210\u51a4\u5bb6\u3002 \u3000\u3000\u56db\u4e2a\u5149\u68cd\u7684\u7537\u4eba\u4e2d\u82e5\u662f\u5ffd\u7136\u591a\u4e86\u4e2a\u5973\u4eba\uff0c\u90a3\u60c5\u51b5\u7b80\u76f4\u5c31\u50cf\u4e00\u53ea\u7b77\u5b50\u5ffd\u7136\u4f38\u5230\u88c5\u7740\u56db\u4e2a\u751f \u9e21\u86cb\u7684\u7897\u91cc\u53bb\uff0c\u60f3\u4e0d\u6405\u5f97\u4e00\u584c\u7cca\u6d82\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u738b\u52a8\u3001\u90ed\u8fbe\u8def\u3001\u71d5\u4e03\u3001\u6797\u592a\u5e73\uff0c\u8fd9\u56db\u4e2a\u4eba\u8fc7\u7684\u672c\u6765\u7684\u786e\u662f\u81ea\u7531\u81ea\u5728\u3001\u65e0\u62d8\u65e0\u675f\u7684\u65e5\u5b50\uff0c \u56e0\u4e3a\u4ed6\u4eec\u65e2\u6ca1\u6709\u94b1\uff0c\u4e5f\u6ca1\u6709\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u6bcf\u5929\u65e9\u4e0a\u8d77\u6765\u7684\u65f6\u5019\u90fd\u89c9\u5f97\u5feb\u4e50\uff0c\u56e0\u4e3a\u90a3\u5012\u9709\u7684\u201c\u6628\u5929\u201d\u603b\u7b97\u5df2\u8fc7\u53bb\uff0c\u4eca\u5929\u53c8\u5145\u6ee1 \u4e86\u5e0c\u671b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5ffd\u7136\u95f4\uff0c\u8fd9\u4e24\u6837\u4e1c\u897f\u90fd\u6765\u4e86\uff0c\u4f60\u8bf4\u8981\u547d\u4e0d\u8981\u547d\uff1f \u7b2c\u4e8c\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u7ae0 \u3000\u3000\u738b\u52a8\u4e5f\u8bb8\u5df2\u9192\u4e86\u5f88\u4e45\uff0c\u5374\u8fd8\u662f\u8eba\u5728\u5730\u4e0a\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u5148\u628a\u4e00\u5e8a\u7834\u68c9\u88ab\u5377\u6210\u5706\u7b52\uff0c\u7136\u540e\u518d\u4e00\u70b9\u4e00\u70b9\u4f38\u8fdb\u53bb\uff0c\u628a\u6574\u4e2a\u4eba\u90fd\u4f38\u8fdb\u8fd9\u4e2a\u7b52\u91cc\uff0c\u56db\u9762 \u90fd\u5bc6\u4e0d\u900f\u98ce\u3002 \u3000\u3000\u8001\u9f20\u5c31\u5728\u4ed6\u8eab\u65c1\u8dd1\u6765\u8dd1\u53bb\uff0c\u672c\u6765\u8fd8\u6709\u70b9\u987e\u5fcc\uff0c\u4e0d\u6562\u5728\u4ed6\u8eab\u4e0a\u722c\uff0c\u53ef\u662f\u540e\u6765\u6e10\u6e10\u5c31\u5c06\u4ed6\u770b \u6210\u4e2a\u6b7b\u4eba\uff0c\u51e0\u4e4e\u90fd\u722c\u4e0a\u4e86\u4ed6\u7684\u5934\u3002 \u3000\u3000\u738b\u52a8\u8fd8\u662f\u4e0d\u52a8\u3002 \u3000\u3000\u6797\u592a\u5e73\u5df2\u6ce8\u610f\u4ed6\u5f88\u4e45\uff0c\u5230\u540e\u6765\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u6084\u6084\u8d70\u8fc7\u53bb\uff0c\u4f38\u51fa\u624b\uff0c\u4f38\u5230\u4ed6\u9f3b\u5b50\u524d\u9762\uff0c \u60f3\u8bd5\u63a2\u4ed6\u662f\u4e0d\u662f\u8fd8\u6709\u547c\u5438\u3002 \u3000\u3000\u738b\u52a8\u7a81\u7136\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u7d27\u7f29\u56de\u624b\uff0c\u9053\uff1a\u201c\u8001\u9f20\u5728\u4f60\u8eab\u4e0a\u722c\uff0c\u4f60\u4e5f\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e0d\u8ddf\u8001\u9f20\u6253\u4ea4\u9053\uff0c\u4e5f\u4e0d\u8ddf\u4ed6\u4eec\u4e00\u822c\u89c1\u8bc6\u2014\u2014\u53ea\u6709\u732b\u624d\u4f1a\u8ddf\u8001\u9f20\u6597 \u6c14\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u786e\u5e94\u8be5\u517b\u6761\u732b\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u6765\u6709\u6761\u732b\uff0c\u662f\u71d5\u4e03\u5e26\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u732b\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8ddf\u5c71\u4e0b\u7684\u516c\u732b\u79c1\u5954\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u77aa\u5927\u4e86\u773c\u775b\uff0c\u770b\u7740\u4ed6\uff0c\u770b\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u96ea\u5df2\u4f4f\uff0c\u661f\u6708\u5347\u8d77\u3002 \u3000\u3000\u6708\u5149\u4ece\u7a97\u5916\u7167\u8fdb\u6765\uff0c\u7167\u5728\u4ed6\u8138\u4e0a\u3002\u4ed6\u8138\u4e0a\u8f6e\u5ed3\u6781\u5206\u660e\uff0c\u989d\u89d2\u5bbd\u9614\uff0c\u9f3b\u5b50\u9ad8\u800c\u633a\uff0c\u7eb5\u7136\u4e0d \u662f\u4e2a\u5f88\u82f1\u4fca\u7684\u7537\u4eba\uff0c\u81f3\u5c11\u5f88\u6709\u6027\u683c\u3002 \u3000\u3000\u201c\u8fd9\u4eba\u770b\u6765\u5373\u4e0d\u8c61\u75af\u5b50\uff0c\u4e5f\u4e0d\u8c61\u767d\u75f4\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u6709\u70b9\u75af\u75c5\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u56db\u4e0b\u77a7\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u90a3\u4e24\u4e2a\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u627e\u4e2a\u4e0d\u662f\u75af\u5b50\u7684\u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e0b\u5c71\u6253\u730e\u53bb\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u6253\u730e\uff1f\u8fd9\u79cd\u5929\u6c14\u53bb\u6253\u730e\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u8bf4\u4e0d\u51fa\u8bdd\u4e86\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u6761\u5b9a\u7406\uff1a \u3000\u3000\u75af\u5b50\u7684\u670b\u53cb\u4e00\u5b9a\u4e5f\u662f\u75af\u5b50\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u4f20\u51fa\u201c\u5495\u565c\u201d\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u662f\u201c\u5495\u565c\u201d\u4e00\u58f0\u3002 \u3000\u3000\u738b\u52a8\u5583\u5583\u9053\uff1a\u201c\u5947\u602a\uff01\u4eca\u5929\u600e\u4e48\u8fde\u8001\u9f20\u7684\u53eb\u58f0\u90fd\u548c\u5e73\u65f6\u4e0d\u4e00\u6837\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u8138\u7ea2\u4e86\uff0c\u5450\u5450\u9053\uff1a\u201c\u4e0d\u662f\u8001\u9f20\uff0c\u662f\u2026\u2026\u662f\u2026\u2026\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u662f\u6211\u7684\u809a\u5b50\u5728\u53eb\uff0c\u4f60\u4eec\u96be\u9053\u4ece\u6765\u4e0d\u5403\u996d\u7684\u4e48\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6709\u996d\u5403\u7684\u65f6\u5019\u5f53\u7136\u8981\u5403\u7684\uff0c\u6ca1\u996d\u5403\u7684\u65f6\u5019\u4e5f\u53ea\u597d\u542c\u7740\u809a\u5b50\u53eb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u53c8\u6014\u4f4f\u4e86\uff0c\u4ed6\u5b9e\u5728\u4e0d\u61c2\uff0c\u4e00\u4e2a\u4eba\u8fde\u996d\u90fd\u6ca1\u5f97\u5403\uff0c\u600e\u4e48\u8fd8\u80fd\u8fd9\u4e48\u5f00\u5fc3\uff1f \u3000\u3000\u738b\u52a8\u5ffd\u53c8\u9053\uff1a\u201c\u4eca\u5929\u4f60\u8fd0\u6c14\u603b\u7b97\u4e0d\u9519\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u82e6\u7b11\u9053\uff1a\u201c\u6211\uff1f\u8fd0\u6c14\u4e0d\u9519\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4eca\u5929\u6211\u6709\u79cd\u9884\u611f\uff0c\u4ed6\u4eec\u6253\u730e\u7684\u6536\u83b7\u4e00\u5b9a\u4e0d\u9519\uff0c\u5e26\u56de\u6765\u7684\u4e1c\u897f\u8bf4\u4e0d\u5b9a\u4f1a\u8ba9\u4f60\u5927 \u5403\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u6765\u60f3\u8bf4\u201c\u5927\u5403\u4e00\u987f\u201d\uff0c\u4f46\u8fd9\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u4ed6\u81ea\u5df1\u5374\u201c\u5927\u5403\u4e00\u60ca\u201d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u7ecf\u56de\u6765\u4e86\uff0c\u8d70\u4e86\u8fdb\u95e8\uff0c\u800c\u4e14\u679c\u7136\u5e26\u4e86\u6837\u4e1c\u897f\u56de\u6765\uff0c\u662f\u4e2a\u4f1a\u8dd1\u4f1a\u8df3\u4f1a\u722c\u6811\uff0c\u8fd8\u4f1a \u201c\u5431\u5431\u201d\u4e71\u53eb\u7684\u4e1c\u897f\u3002 \u3000\u3000\u662f\u4e2a\u7334\u5b50\u3002 \u3000\u3000\u5047\u5982\u8bf4\u738b\u52a8\u4e5f\u6709\u8138\u8272\u53d1\u767d\u7684\u65f6\u5019\uff0c\u90a3\u4e48\u5c31\u662f\u73b0\u5728\u3002 \u3000\u3000\u770b\u5230\u738b\u52a8\u7684\u8868\u60c5\uff0c\u90ed\u5927\u8def\u51e0\u4e4e\u7b11\u65ad\u4e86\u80a0\u5b50\uff0c\u5598\u7740\u6c14\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u5bb3\u6015\uff0c\u8fd9\u662f\u4e2a\u516c\u7334 \u5b50\uff0c\u4e0d\u662f\u6bcd\u7684\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u5728\u4ed6\u8eab\u540e\u54cd\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u7684\u670b\u53cb\u6015\u6bcd\u7334\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u5f97\u66f4\u5389\u5bb3\uff0c\u9053\uff1a\u201c\u7684\u786e\u6709\u70b9\u6015\uff0c\u4e0d\u6015\u8001\u5a46\u7684\u4eba\u8fd9\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u677f\u7740\u8138\uff0c\u9053\uff1a\u201c\u597d\u7b11\u597d\u7b11\uff0c\u597d\u7b11\u6781\u4e86\uff0c\u4e16\u4e0a\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u98ce\u8da3\u7684\u4eba\uff0c\u5012\u771f\u662f\u602a \u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5373\u4e0d\u77e5\u9053\u4ec0\u4e48\u4e8b\u5982\u6b64\u597d\u7b11\uff0c\u4e5f\u4e0d\u60f3\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u9ed1\u9edd\u9edd\u7684\u5c4b\u5b50\u91cc\u597d\u8c61\u5ffd\u7136\u71c3\u8d77\u4e86\u51e0\u5343\u51e0\u767e\u76cf\u706f\u3002 \u3000\u3000\u6240\u6709\u7684\u5149\u4eae\u90fd\u662f\u4ece\u4e00\u4e2a\u4eba\u8eab\u4e0a\u53d1\u51fa\u6765\u7684\u3002\u8fd9\u4eba\u7a7f\u7740\u4ef6\u7c97\u5e03\u8863\u670d\uff0c\u624b\u91cc\u63d0\u7740\u4e24\u4e2a\u7bee\u5b50\uff0c\u5df2 \u7ecf\u8ddf\u7740\u90ed\u5927\u8def\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8ddf\u5728\u5979\u540e\u9762\u7684\u8fd8\u6709\u53c1\u4e2a\u4eba\uff1a\u4e00\u4e2a\u5927\u4eba\uff0c\u4e24\u4e2a\u5b69\u5b50\u3002\u5b69\u5b50\u4eec\u90fd\u7a7f\u5f97\u5f88\u6574\u9f50\uff0c\u5927\u4eba\u7684\u8eab\u4e0a\u5374 \u53ea\u56f4\u7740\u5f20\u8c79\u76ae\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5df2\u7ecf\u591f\u77a7\u8001\u534a\u5929\u4e86\uff0c\u5374\u8fd8\u4e0d\u662f\u5168\u90e8\u3002\u9664\u4e86\u4ed6\u4eec\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u6761\u72d7\u3001\u4e00\u5927\u6346\u5200\u67aa\u3001 \u53c1\u56db\u9762\u9523\u3001\u4e94\u516d\u6839\u7af9\u7aff\u3002 \u3000\u3000\u738b\u52a8\u5583\u5583\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4e00\u76f4\u8c61\u548c\u71d5\u4e03\u6bd4\u6bd4\u770b\u8c01\u7684\u672c\u4e8b\u5927\uff0c\u8c01\u5e26\u56de\u6765\u7684\u4e1c\u897f\u591a\uff0c\u53ef\u662f\u81f3 \u5c11\u4e5f\u8be5\u7ed9\u4ed6\u7559\u70b9\u9762\u5b50\uff0c\u7528\u4e0d\u7740\u8ba9\u4ed6\u8f93\u5f97\u8fd9\u4e48\u60e8\u5440\u3002\u201d \u3000\u3000\u71d5\u4e03\u501a\u7740\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u867d\u7136\u8f93\u5f97\u5f88\u60e8\uff0c\u5374\u8f93\u5f97\u53e3\u670d\u5fc3\u670d\uff0c\u6211\u51fa\u53bb\u4e8c\u5341\u6b21\uff0c\u5e26\u56de\u6765\u7684\u4e1c\u897f \u4e5f\u6ca1\u6709\u4ed6\u4e00\u6b21\u591a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e9b\u670b\u53cb\u7684\u5634\u5df4\u867d\u7136\u574f\uff0c\u4eba\u5012\u5e76\u4e0d\u592a\u574f\u3002\u6765\uff0c\u6211\u5148\u66ff\u4f60\u4eec\u5f15\u89c1\u5f15\u89c1\uff0c \u8fd9\u4f4d\u59d1\u5a18\u662f\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u8ba9\u6211\u81ea\u5df1\u8bf4\u5427\u3002\u6211\u53eb\u9178\u6885\u6c64\uff0c\u8fd9\u662f\u6211\u7684\u5802\u54e5\u2018\u98de\u8c79\u5b50\u2019\uff0c\u8fd8\u6709\u6211\u4e24 \u4e2a\u5c0f\u8868\u5f1f\uff0c\u4e00\u4e2a\u53eb\u2018\u5c0f\u73b2\u73d1\u2019\uff0c\u4e00\u4e2a\u53eb\u2018\u5c0f\u91d1\u521a\u2019\u3002\u201d \u3000\u3000\u201c\u98de\u8c79\u5b50\u201d\u662f\u8c01\uff1f\u5176\u5b9e\u6839\u672c\u7528\u4e0d\u7740\u4ecb\u7ecd\uff0c\u522b\u4eba\u4e00\u770b\u5c31\u660e\u767d\u3002 \u3000\u3000\u4f46\u90a3\u4e24\u4e2a\u5b69\u5b50\u5374\u51e0\u4e4e\u957f\u7684\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\uff0c\u4e24\u4eba\u90fd\u662f\u5927\u5927\u7684\u773c\u73e0\uff0c\u90fd\u68b3\u7740\u671d\u5929\u8fab\u5b50\uff0c\u7b11\u8d77 \u6765\u90fd\u6709\u4e2a\u9152\u7a9d\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u4eec\u7684\u9152\u7a9d\u5e76\u4e0d\u662f\u4e00\u4e2a\u5728\u5de6\uff0c\u4e00\u4e2a\u5728\u53f3\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7684\u9152\u7a9d\u90fd\u5728\u53f3\u8fb9\u3002 \u3000\u3000\u738b\u52a8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8c01\u662f\u5c0f\u73b2\u73d1\uff1f\u8c01\u662f\u5c0f\u91d1\u521a\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u5b69\u5b50\u4e00\u9f50\u9053\uff1a\u201c\u4f60\u731c\u731c\u770b\u3002\u201d \u3000\u3000\u738b\u52a8\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u91d1\u521a\u65c1\u8fb9\u7684\u662f\u5c0f\u73b2\u73d1\uff0c\u5c0f\u73b2\u73d1\u65c1\u8fb9\u7684\u662f\u5c0f\u91d1\u521a\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u5b69\u5b50\uff0c\u4e00\u9f50\u7b11\u4e86\uff0c\u5176\u4e2d\u4e00\u4e2a\u5ffd\u7136\u8dd1\u8fc7\u6765\uff0c\u51d1\u5230\u738b\u52a8\u8033\u65c1\uff0c\u6084\u6084\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u53c8\u7b11 \u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u7684\u79d8\u5bc6\uff0c\u4f60\u53ef\u4e0d\u80fd\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u62c9\u8d77\u4e86\u53e6\u4e00\u4e2a\u5b69\u5b50\u7684\u624b\uff0c\u9053\uff1a\u201c\u5c0f\u73b2\u73d1\u662f\u4f60\u7684\u59d0\u59d0\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u8fd9\u7537\u5b69\u5b50\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u5979\u662f\u6211\u59b9\u59b9\u3002\u201d \u3000\u3000\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u5c0f\u73b2\u73d1\u5df2\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u7b28\u86cb\uff01\u6211\u65e9\u5c31\u77e5\u9053\u7537\u5b69\u5b50\u90fd\u662f\u7b28\u86cb\uff0c\u88ab\u4eba\u4e00\u9a97 \u5c31\u9a97\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u5c0f\u91d1\u521a\u6da8\u7ea2\u4e86\u8138\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u7b28\uff0c\u4f60\u806a\u660e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6253\u626e\u7684\u548c\u7537\u5b69\u5b50\u4e00\u6837\uff1f\u201d \u3000\u3000\u8fd9\u5b69\u5b50\u7684\u8bdd\u5012\u771f\u662f\u4e00\u9488\u89c1\u8840\u2014\u2014\u5973\u4eba\u90fd\u77a7\u4e0d\u8d77\u7537\u4eba\uff0c\u8ba4\u4e3a\u7537\u4eba\u662f\u7b28\u86cb\uff0c\u4f46\u5374\u53c8\u504f\u504f\u5e0c\u671b \u81ea\u5df1\u662f\u4e2a\u7537\u4eba\uff0c\u8fd9\u5c31\u662f\u5973\u4eba\u6700\u5927\u7684\u6bdb\u75c5\u3002 \u3000\u3000\u6797\u592a\u5e73\u4e00\u76f4\u773c\u7741\u7741\u77a7\u7740\u9178\u6885\u6c64\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u4e9b\u5f53\u7136\u4e0d\u662f\u4f60\u4eec\u7684\u771f\u540d\u5b57\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u8c61\u6211\u4eec\u8fd9\u4e9b\u8d70\u6c5f\u6e56\u5356\u827a\u7684\uff0c\u8fde\u7956\u5b97\u7684\u4eba\u90fd\u4e22\u5149\u4e86\uff0c\u90a3\u91cc\u8fd8 \u6709\u4ec0\u4e48\u771f\u540d\u5b57\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d70\u6c5f\u6e56\u5356\u827a\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u6709\u4e9b\u4eba\u60f3\u53bb\u8d70\u6c5f\u6e56\u8fd8\u4e0d\u884c\u5462\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u53c8\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u597d\u8c61\u6709\u5f88\u591a\u5fc3\u4e8b\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u672c\u6765\u5c31\u8c61\u4e2a\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u8138\u8272\u5df2\u6709\u70b9\u53d8\u4e86\u3002 \u3000\u3000\u9178\u6885\u6c64\u62a2\u7740\u7b11\u9053\uff1a\u201c\u96be\u9053\u53ea\u6709\u5973\u5b69\u5b50\u624d\u80fd\u6709\u5fc3\u4e8b\uff1f\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u7537\u4eba\u5c82\u975e\u771f\u7684\u53d8\u6210\u6ca1\u5fc3 \u6ca1\u80ba\u7684\u50bb\u86cb\u4e86\u5417\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u77a7\u7740\u5979\uff0c\u76ee\u5149\u5145\u6ee1\u4e86\u611f\u6fc0\u3002 \u3000\u3000\u90ed\u5927\u8def\u8038\u4e86\u8038\u80a9\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u7537\u4eba\u5168\u90fd\u6ca1\u5fc3\u6ca1\u80ba\uff0c\u81f3\u5c11\u90fd\u6709\u809a\u5b50\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u5012\u5dee\u70b9\u5fd8\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u653e\u4e0b\u7bee\u5b50\uff0c\u6380\u8d77\u76d6\u5728\u4e0a\u9762\u7684\u7eb8\uff0c\u81ea\u5df1\u5148\u6495\u4e0b\u6761\u9e21\u817f\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u5973\u4eba\u7684\u809a\u5b50\u4e5f\u4e0d \u6bd4\u7537\u4eba\u5c0f\u591a\u5c11\uff0c\u53ea\u4e0d\u8fc7\u6709\u65f6\u4e0d\u597d\u610f\u601d\u5403\u5f97\u592a\u591a\u800c\u5df2\u3002\u201d \u3000\u3000\u5c0f\u91d1\u521a\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u4ece\u6765\u4e5f\u6ca1\u6709\u89c9\u5f97\u4e0d\u597d\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u9178\u6885\u6c64\u7528\u9e21\u817f\u53bb\u6572\u4ed6\u7684\u5934\uff0c\u5c0f\u91d1\u521a\u62a2\u4e86\u534a\u53ea\u9e21\u5c31\u8dd1\uff0c\u7334\u5b50\u5728\u5730\u4e0a\u4e0d\u505c\u7684\u8df3\uff0c\u4e24\u6761\u72d7\u201c\u6c6a \u6c6a\u201d\u7684\u53eb\u3002 \u3000\u3000\u738b\u52a8\u6447\u7740\u5934\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u5df2\u6709\u5341\u51e0\u5e74\u6ca1\u8fd9\u4e48\u70ed\u95f9\u8fc7\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8fd9\u91cc\u8fd8\u6709\u597d\u51e0\u5929\u70ed\u95f9\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u51e0\u5929\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u671b\u7740\u9178\u6885\u6c64\u7a88\u7a95\u7684\u80cc\u5f71\uff0c\u9053\uff1a\u201c\u5f88\u591a\u5929\u2026\u2026\u6211\u542c\u8bf4\u4ed6\u4eec\u8981\u627e\u5c4b\u5b50\u4f4f\u4e0b\u6765\uff0c\u6240\u4ee5\u5df2 \u7ecf\u628a\u540e\u9762\u90a3\u4e00\u6392\u4e94\u95f4\u5c4b\u5b50\u79df\u7ed9\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u51e0\u4e4e\u628a\u521a\u559d\u4e0b\u6765\u7684\u4e00\u53e3\u9152\u545b\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u79df\u91d1\u591a\u5c11\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u8d77\u4e86\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u5c0f\u6c14\u9b3c\u4e48\uff1f\u4f1a\u5411\u4eba\u5bb6\u8981\u79df\u91d1\uff1f\u82e5\u4e0d\u662f\u6211\uff0c \u8fd9\u6837\u7684\u5ba2\u4eba\u8fde\u8bf7\u90fd\u8bf7\u4e0d\u5230\u3002\u201d \u3000\u3000\u738b\u52a8\u770b\u7740\u4ed6\uff0c\u770b\u4ed6\u5f88\u4e45\uff0c\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u5df2\u8d8a\u6765\u8d8a\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u623f\u5b50\u7a76\u7adf\u662f\u4f60\u7684\uff1f\u8fd8\u662f\u6211\u7684\uff1f\u201d \u3000\u3000\u82e5\u8bf4\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4e8b\u80fd\u4ee4\u4e00\u4e2a\u53c8\u810f\u53c8\u61d2\u5f97\u7537\u4eba\u53d8\u5f97\u52e4\u5feb\u8d77\u6765\uff0c\u90a3\u5c31\u662f\u5973\u4eba\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u738b\u52a8\u8fd8\u8eba\u5728\u201c\u7b52\u201d\u91cc\uff0c\u90ed\u5927\u8def\u5df2\u7ecf\u53bb\u63d0\u6c34\u4e86\uff0c\u6797\u592a\u5e73\u5374\u5728\u5c4b\u5b50\u91cc\u627e\u6765\u627e \u53bb\u3002 \u3000\u3000\u738b\u52a8\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u627e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u6d17\u8138\u76c6\u3001\u6d17\u8138\u5e03\uff0c\u8fd8\u6709\u6f31\u53e3\u676f\u5b50\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u6211\u975e\u4f46\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u770b\u5230\uff0c\u8fde\u542c\u90fd\u6ca1\u6709\u542c\u8fc7\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5c31\u597d\u8c61\u5ffd\u7136\u88ab\u4eba\u62bd\u4e86\u4e00\u97ad\u5b50\uff0c\u5f20\u5927\u4e86\u5634\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4eec\u96be\u9053\u8fde\u8138\u90fd\u4e0d \u6d17\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u5f53\u7136\u6d17\uff0c\u53ea\u4e0d\u8fc7\u662f\u53c1\u65e5\u4e00\u5c0f\u6d17\uff0c\u4e94\u65e5\u4e00\u5927\u6d17\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u5c0f\u6d17\u662f\u600e\u4e48\u6d17\uff1f\u5927\u6d17\u662f\u600e\u4e48\u6d17\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u71d5\u4e03\uff0c\u4f60\u6d17\u7ed9\u4ed6\u770b\u770b\u3002\u201d \u3000\u3000\u71d5\u4e03\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u9053\uff1a\u201c\u6211\u6628\u5929\u521a\u6d17\u8fc7\uff0c\u4eca\u5929\u8be5\u8f6e\u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u81f3\u5c11\u603b\u8be5\u628a\u6d17\u8138\u7684\u5bb6\u4f19\u62ff\u8fc7\u6765\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u521a\u597d\u63d0\u4e86\u4e24\u6876\u6c34\u8fdb\u6765\uff0c\u71d5\u4e03\u5c31\u7528\u90a3\u4e2a\u7834\u7897\u8200\u4e86\u5927\u534a\u7897\u6c34\uff0c\u53c8\u4ece\u5899\u4e0a\u62ff\u4e0b\u5757\u53c8\u9ec4\u53c8 \u9ed1\u3001\u672c\u6765\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u989c\u8272\u5f97\u5e03\u3002 \u3000\u3000\u738b\u52a8\u8fd9\u624d\u52c9\u5f3a\u5750\u4e86\u8d77\u6765\uff0c\u5148\u559d\u4e86\u53e3\u6c34\uff0c\u542b\u5728\u5634\u91cc\uff0c\u7528\u624b\u644a\u5f00\u6bdb\u5dfe\uff0c\u7528\u529b\u6f31\u4e86\u6f31\u53e3\uff0c\u7136\u540e \u5c31\u5c06\u4e00\u53e3\u6c34\u201c\u5657\u201d\u7684\u55b7\u5728\u624b\u91cc\u7684\u5e03\u4e0a\uff0c\u968f\u4fbf\u5728\u8138\u4e0a\u4e00\u62b9\uff0c\u677e\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u6d17\u5b8c\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5c31\u597d\u8c61\u770b\u5230\u9b3c\u4f3c\u7684\uff0c\u5413\u5f97\u8138\u8272\u53d1\u9752\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u5c31\u7b97\u662f\u5c0f\u6d17\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u662f\u5c0f\u6d17\uff0c\u662f\u5927\u6d17\uff0c\u5c0f\u6d17\u82e5\u8fd9\u4e48\u9ebb\u70e6\u90a3\u8fd8\u5f97\u4e86\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u8fde\u5634\u5507\u90fd\u6709\u70b9\u53d1\u9752\uff0c\u770b\u6837\u5b50\u597d\u8c61\u7acb\u5373\u5c31\u8981\u6655\u8fc7\u53bb\uff0c\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u82e5\u6709\u8c01\u8fd8\u80fd\u627e\u5230\u6bd4\u4f60\u4eec\u66f4\u810f\u7684\u4eba\uff0c\u6211\u60c5\u613f\u8ddf\u4ed6\u78d5\u5934\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u78d5\u5427\uff0c\u6bd4\u6211\u4eec\u810f\u7684\u4eba\u6ee1\u8857\u90fd\u662f\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u62da\u547d\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u738b\u52a8\u6de1\u6de1\u9053\uff1a\u201c\u6211\u4eec\u7684\u4eba\u867d\u810f\uff0c\u5fc3\u5374\u4e0d\u810f\uff0c\u975e\u4f46\u4e0d\u810f\uff0c\u800c\u4e14\u5e72\u51c0\u5f97\u5f88\u3002\u4e00\u4e2a\u4eba\u7684\u5fc3\u82e5\u662f \u810f\uff0c\u4ed6\u5c31\u7b97\u6bcf\u5929\u7528\u80a5\u7682\u716e\u5341\u6b21\uff0c\u4e5f\u4e0d\u7b97\u5e72\u51c0\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6b6a\u7740\u5934\uff0c\u60f3\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u4e00\u62cd\u5df4\u638c\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u5f88\u6709\u9053\u7406\u3002\u4e00\u4e2a\u4eba\u82e5\u662f\u6d3b\u5f97 \u5feb\u5feb\u4e50\u4e50\uff0c\u95ee\u5fc3\u65e0\u6127\uff0c\u5403\u4e0d\u5403\u996d\u90fd\u6ca1\u5173\u7cfb\uff0c\u6d17\u4e0d\u6d17\u8138\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u9762\u5927\u7b11\u4e86\u53c1\u58f0\uff0c\u8dd1\u5230\u9662\u5b50\u91cc\uff0c\u5728\u5730\u4e0b\u6253\u4e86\u4e2a\u6eda\uff0c\u5927\u7b11\u9053\uff1a\u201c\u6211\u60f3\u901a\u4e86\uff0c\u6211\u60f3\u901a \u4e86\u2026\u2026\u6211\u4ee5\u524d\u4e3a\u4ec0\u4e48\u4e00\u76f4\u60f3\u4e0d\u901a\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u548c\u71d5\u4e03\u542b\u7b11\u77a7\u7740\u4ed6\uff0c\u8c61\u662f\u4e5f\u90fd\u5728\u66ff\u4ed6\u9ad8\u5174\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e5f\u90fd\u5df2\u770b\u51fa\u4ed6\u672c\u6765\u7684\u786e\u6709\u4ef6\u5f88 \u91cd\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4e00\u76f4\u4e0d\u77e5\u9053\u81ea\u5df1\u505a\u5f97\u5bf9\u4e0d\u5bf9\uff1f\u73b0\u5728\u624d\u77e5\u9053\u5e76\u6ca1\u6709\u505a\u9519\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6d3b\u7740\uff0c\u5c31\u8981\u6d3b\u5f97\u95ee\u5fc3\u65e0\u6127\uff0c\u8fd9\u624d\u662f\u6700\u91cd\u8981\u7684\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u5728\u6d17\u8138\uff0c\u5634\u91cc\u8fd8\u5583\u5583\u9053\uff1a\u201c\u4e0d\u6d17\u8138\u6ca1\u5173\u7cfb\uff0c\u6d17\u8138\u4e5f\u6ca1\u5173\u7cfb\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ed6\u6d17\u5b8c\u4e86\u8138\uff0c\u53c8\u7528\u5e03\u64e6\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u64e6\u9774\u5b50\u3002 \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u8131\u4e0b\u9774\u5b50\u6d17\u6d17\u811a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211\u6b63\u6709\u8fd9\u610f\u601d\uff0c\u53ea\u53ef\u60dc\u65f6\u95f4\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u51b2\u51fa\u95e8\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u5b9a\u4e5f\u9192\u4e86\uff0c\u6211\u5230\u540e\u9762\u77a7\u77a7\u53bb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u540c\u65f6\u51b2\u4e86\u51fa\u53bb\uff0c\u5c31\u597d\u8c61\u8d76\u7740\u53bb\u6551\u706b\u4f3c\u7684\u3002 \u3000\u3000\u738b\u52a8\u779f\u4e86\u71d5\u4e03\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u7a88\u7a95\u6dd1\u5973\uff0c\u541b\u5b50\u597d\u9011\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6c89\u7740\u8138\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u541b\u5b50\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u597d\u8c61\u4e00\u70b9\u4e5f\u4e0d\u559c\u6b22\u90a3\u9178\u6885\u6c64\u59d1\u5a18\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u4eec\u7a76\u7adf\u662f\u5e72\u4ec0\u4e48\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u8d70\u6c5f\u6e56\u5356\u827a\u7684\u4e48\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u4e5f\u62ff\u4ed6\u4eec\u5f53\u4f5c\u8d70\u6c5f\u6e56\u5356\u827a\u7684\uff0c\u4f60\u5c31\u4e5f\u662f\u4e2a\u5446\u5b50\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u96be\u9053\u770b\u4e0d\u51fa\u90a3\u53ea\u7334\u5b50\u548c\u90a3\u6761\u72d7\u4e00\u70b9\u4e5f\u4e0d\u542c\u4ed6\u4eec\u7684\u8bdd\uff0c\u663e\u7136\u662f\u4e34\u65f6\u627e\u6765\u88c5\u4f6f \u7684\u3002\u8fd8\u6709\u90a3\u98de\u8c79\u5b50\uff0c\u6545\u610f\u5947\u88c5\u5f02\u670d\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u5f88\u89c4\u77e9\u7684\u4eba\uff0c\u8fde\u8bdd\u90fd\u4e0d\u6562\u591a\u8bf4\uff0c\u4e00\u53cc\u624b\u66f4\u662f \u53c8\u767d\u53c8\u7ec6\uff0c\u90a3\u91cc\u8c61\u662f\u4e2a\u6574\u5929\u63d0\u7bb1\u5b50\u7275\u72d7\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9759\u9759\u7684\u542c\u7740\uff0c\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd9\u4e48\u7ec6\u5fc3\u3002\u4f46\u4ed6\u4eec\u82e5\u4e0d\u662f\u8d70\u6c5f\u6e56 \u5356\u827a\u7684\uff0c\u662f\u5e72\u4ec0\u4e48\u7684\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8c01\u77e5\u9053\uff0c\u4e5f\u8bb8\u662f\u5f3a\u76d7\u90fd\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u771f\u7684\u662f\u5f3a\u76d7\u5c31\u4e0d\u4f1a\u6765\u4e86\uff0c\u8fd9\u5730\u65b9\u53c8\u6709\u4ec0\u4e48\u4e1c\u897f\u597d\u8ba9\u4ed6\u4eec\u6253\u4e3b\u610f \u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5c31\u542c\u5230\u540e\u9762\u4f20\u6765\u4e00\u58f0\u60ca\u547c\u3002 \u3000\u3000\u662f\u90ed\u5927\u8def\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8c61\u90ed\u5927\u8def\u8fd9\u79cd\u4eba\uff0c\u5c31\u7b97\u770b\u5230\u9b3c\u4e5f\u4e0d\u4f1a\u5403\u60ca\u5f97\u53eb\u8d77\u6765\u7684\u3002 \u3000\u3000\u4e16\u4e0a\u53ea\u6015\u5f88\u5c11\u6709\u4e8b\u80fd\u4ee4\u4ed6\u53eb\u8d77\u6765\u3002 \u3000\u3000\u71d5\u4e03\u7b2c\u4e00\u4e2a\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u738b\u52a8\u4e5f\u52a8\u4e86\u3002 \u3000\u3000\u540e\u9762\u7684\u9662\u6bd4\u524d\u9762\u7684\u5c0f\u4e9b\uff0c\u9662\u5b50\u79cd\u6ee1\u4e86\u7af9\u3002\u4ee5\u524d\u6bcf\u5f53\u98ce\u6e05\u6708\u767d\u7684\u590f\u591c\uff0c\u4e3b\u4eba\u5c31\u4f1a\u8eba\u5230\u8fd9 \u91cc\uff0c\u542c\u90a3\u6d77\u6d6a\u822c\u7684\u7af9\u6d9b\u58f0\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u91cc\u4e5f\u548c\u5176\u4ed6\u8bb8\u591a\u79cd\u4e86\u7af9\u5b50\u7684\u9662\u5b50\u4e00\u6837\uff0c\u53eb\u505a\u201c\u542c\u7af9\u5c0f\u9662\u201d\uff0c\u90a3\u4e00\u6392\u4e94\u95f4\u5c4b\u5b50\uff0c\u5c31 \u53eb\u505a\u201c\u542c\u7af9\u8f69\u201d\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u738b\u52a8\u4f5c\u4e3b\u4eba\u7684\u65f6\u5019\uff0c\u5c31\u66ff\u5b83\u6539\u4e86\u4e2a\u540d\u5b57\uff0c\u53eb\u201c\u6709\u7af9\u65e0\u8089\u8f69\u201d\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u201c\u542c \u7af9\u201d\u8fd9\u540d\u5b57\u672c\u6765\u867d\u5f88\u96c5\uff0c\u73b0\u5728\u5374\u5df2\u53d8\u5f97\u5f88\u4fd7\u3002 \u3000\u3000\u4ed6\u8ba4\u4e3a\u7b2c\u4e00\u4e2a\u7528\u201c\u542c\u7af9\u201d\u4f5c\u8f69\u540d\u7684\u4eba\u867d\u7136\u662f\u4e2a\u5f88\u98ce\u96c5\u7684\u806a\u660e\u4eba\uff0c\u4f46\u7b2c\u516b\u5341\u4e2a\u7528\u201c\u542c\u7af9\u201d \u4f5c\u8f69\u540d\u7684\u4eba\u5c31\u662f\u4fd7\u4e0d\u53ef\u8010\u7684\u7b28\u86cb\u4e86\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u9662\u5b50\u91cc\u975e\u4f46\u201c\u65e0\u8089\u201d\uff0c\u8fde\u7af9\u5b50\u90fd\u51e0\u4e4e\u88ab\u780d\u5149\u3002 \u3000\u3000\u7af9\u5b50\u53ef\u4ee5\u505a\u6652\u8863\u670d\u7684\u7af9\u7aff\uff0c\u4e5f\u53ef\u4ee5\u7528\u6765\u642d\u51c9\u68da\uff0c\u6240\u4ee5\u738b\u52a8\u5e38\u5e38\u62ff\u7af9\u5b50\u53bb\u6362\u8089\u3002\u4e00\u4e2a\u4eba\u809a \u5b50\u5f88\u997f\u7684\u65f6\u5019\uff0c\u5c31\u5e38\u5e38\u4f1a\u5fd8\u8bb0\u98ce\u96c5\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u9178\u6885\u6c64\u3001\u98de\u8c79\u5b50\u4ed6\u4eec\u6628\u5929\u665a\u4e0a\u5c31\u4f4f\u5728\u8fd9\u91cc\uff0c\u4f46\u73b0\u5728\u8fde\u4eba\u5e26\u72d7\u5e26\u7334\u5b50\u3002\u5df2\u5168\u90fd\u8d70\u5f97\u5e72\u5e72\u51c0 \u51c0\uff0c\u53ea\u5269\u4e0b\u90ed\u5927\u8def\u548c\u6797\u592a\u5e73\u7ad9\u5728\u90a3\u91cc\u53d1\u6014\u3002 \u3000\u3000\u4ed6\u4eec\u811a\u65c1\u8fd8\u6446\u7740\u51e0\u53e3\u7bb1\u5b50\uff0c\u5d2d\u65b0\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4f60\u7684\u5ba2\u4eba\u5df2\u4e0d\u544a\u800c\u522b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\uff1a\u201c\u8d70\u4e86\u5c31\u8d70\u4e86\uff0c\u8fd9\u4e5f\u7528\u4e0d\u7740\u5927\u547c\u5c0f\u53eb\uff0c\u5927\u60ca\u5c0f\u602a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u5374\u5c06\u624b\u91cc\u7684\u4e00\u5f20\u7eb8\u6761\u9012\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u7eb8\u6761\u4e0a\u7528\u6728\u70ad\u5199\u4e86\u51e0\u4e2a\u5b57\uff1a\u201c\u4e94\u53e3\u7bb1\u5b50\uff0c\u804a\u5145\u623f\u79df\uff0c\u656c\u8bf7\u6536\u4e0b\uff0c\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f4f\u623f\u5b50\u672c\u6765\u5c31\u8981\u4ed8\u623f\u79df\uff0c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u597d\u7a00\u5947\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7a00\u5947\u867d\u4e0d\u7a00\u5947\uff0c\u53ea\u4e0d\u8fc7\u4ed8\u5f97\u592a\u591a\u4e86\u4e9b\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u7bb1\u5b50\u91cc\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\u522b\u7684\uff0c\u53ea\u4e0d\u8fc7\u51e0\u7bb1\u94dc\u81ed\u7269\u800c\u5df2\u3002\u201d \u3000\u3000\u82e5\u8bf4\u94b1\u6709\u94dc\u81ed\u6c14\uff0c\u90a3\u4e48\u8fd9\u4e94\u7bb1\u4e1c\u897f\u5c31\u8db3\u8db3\u53ef\u4ee5\u5c06\u53c1\u4e07\u516b\u5343\u4e2a\u4eba\u5168\u90e8\u81ed\u6b7b\u3002 \u3000\u3000\u5176\u4e2d\u56db\u53e3\u7bb1\u5b50\u91cc\u4ec0\u4e48\u522b\u7684\u90fd\u6ca1\u6709\uff0c\u5c31\u53ea\u6709\u5143\u5b9d\u3002\u5927\u5927\u5c0f\u5c0f\uff0c\u5404\u79cd\u5404\u6837\u7684\u5143\u5b9d\uff0c\u6700\u5c11\u7684\u4e5f \u6709\u5341\u4e24\u91cd\uff0c\u5c31\u7b97\u81ed\u4e0d\u6b7b\u4eba\uff0c\u4e5f\u538b\u5f97\u6b7b\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u53e3\u7bb1\u5b50\u91cc\u5168\u662f\u73e0\u5b9d\uff0c\u5404\u5f0f\u5404\u6837\u7684\u73e0\u5b9d\uff0c\u6709\u73cd\u73e0\u3001\u6709\u7fe1\u7fe0\u3001\u6709\u739b\u7459\uff0c\u8fd8\u6709\u4e03\u4e03\u516b\u516b \u4e00\u4e9b\u8fde\u540d\u5b57\u90fd\u53eb\u4e0d\u51fa\u7684\u5b9d\u77f3\u3002 \u3000\u3000\u5176\u4e2d\u65e0\u8bba\u54ea\u53e3\u7bb1\u5b50\uff0c\u90fd\u53ef\u4ee5\u628a\u5bcc\u8d35\u5c71\u5e84\u5168\u4e70\u4e0b\u6765\u3002 \u3000\u3000\u738b\u52a8\u548c\u71d5\u4e03\u4e5f\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u71d5\u4e03\u624d\u5410\u51fa\u53e3\u6c14\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u4ed6\u4eec\u6765\u7684\u65f6\u5019\uff0c\u5e76\u6ca1\u6709\u5e26\u8fd9\u4e94\u53e3\u7bb1\u5b50\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u90a3\u4e48\u7bb1\u5b50\u662f\u54ea\u91cc\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u62a2\u6765\uff0c\u5c31\u662f\u5077\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u4e9b\u5143\u5b9d\u540e\u9762\u7684\u6233\u8bb0\u90fd\u4e0d\u540c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u540c\uff0c\u8c01\u5bb6\u91cc\u90fd\u4e0d\u4f1a\u653e\u7740\u8fd9\u4e48\u591a\u5143\u5b9d\uff0c\u4ed6\u4eec\u4e00\u5b9a\u662f\u4ece\u5f88\u591a\u4e0d\u540c\u7684\u4eba\u5bb6\u5077 \u6765\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u9053\uff1a\u201c\u80fd\u5728\u4e00\u5929\u665a\u4e0a\u5077\u8fd9\u4e48\u591a\u4eba\u5bb6\uff0c\u672c\u4e8b\u5012\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u7a00\u5947\uff0c\u9ad8\u660e\u7684\u8d3c\u672c\u5c31\u80fd\u65e5\u8d70\u5343\u5bb6\uff0c\u591c\u76d7\u767e\u6237\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u4eec\u8f9b\u8f9b\u82e6\u82e6\u5077\u6765\u7684\u4e1c\u897f\uff0c\u5374\u9001\u7ed9\u4e86\u6211\u4eec\uff0c\u8fd9\u6837\u7684\u8d3c\u5012\u4e5f\u5929\u4e0b\u5c11\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4eec\u662f\u60f3\u683d\u8d43\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u683d\u8d43\uff1f\u4e3a\u4ec0\u4e48\u8981\u683d\u8d43\uff1f\u6211\u4eec\u8ddf\u5979\u53c8\u6ca1\u6709\u4ec7\u3002\u201d \u3000\u3000\u71d5\u4e03\u60a0\u60a0\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ee5\u4e3a\u5979\u771f\u770b\u4e0a\u4e86\u4f60\uff0c\u7279\u5730\u9001\u8fd9\u4e94\u53e3\u7bb1\u5b50\u6765\u505a\u5ac1\u5986\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u4e9b\u5168\u4e0d\u53bb\u7ba1\u5b83\uff0c\u95ee\u9898\u662f\u6211\u4eec\u73b0\u5728\u62ff\u8fd9\u4e94\u53e3\u7bb1\u5b50\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u4eba\u5bb6\u65e2\u7136\u9001\u6765\u4e86\uff0c\u6211\u4eec\u5f53\u7136\u5c31\u6536\u4e0b\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u6709\u4e2a\u6700\u5927\u7684\u672c\u4e8b\uff0c\u65e0\u8bba\u591a\u590d\u6742\u7684\u4e8b\uff0c\u88ab\u4ed6\u4e00\u8bf4\uff0c\u9a6c\u4e0a\u5c31\u53d8\u5f97\u7b80\u5355\u8d77 \u6765\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u4e8b\u672c\u6765\u5c31\u7b80\u5355\u5f97\u5f88\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u7b80\u5355\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u7b80\u5355\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u9001\u6211\u4eec\u8fd9\u4e48\u591a\u8d22\u5b9d\uff0c\u4e00\u5b9a\u53e6\u6709\u76ee\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u8fd9\u4e9b\u4e1c\u897f\u65e2\u7136\u662f\u5077\u6765\u7684\uff0c\u6211\u4eec\u82e5\u6536\u4e0b\u6765\uff0c\u5c82\u975e\u4e5f\u53d8\u6210\u4e86\u8d3c\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u90fd\u80fd\u505a\uff0c\u53ea\u6709\u8d3c\u662f\u4e07\u4e07\u505a\u4e0d\u5f97\u7684\u3002\u4f60\u53ea\u8981\u505a\u4e86\u4e00\u6b21\u8d3c\uff0c\u5c1d\u7740\u4e86\u751c\u5934\uff0c \u4ee5\u540e\u522b\u7684\u4e8b\u5c31\u4f1a\u90fd\u4e0d\u60f3\u505a\u4e86\uff0c\u4e00\u8f88\u5b50\u90fd\u5f97\u505a\u8d3c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u800c\u4e14\u4ee5\u540e\u751f\u51fa\u6765\u7684\u513f\u5b50\u4e5f\u662f\u8d3c\uff0c\u8001\u8d3c\u751f\u5927\u8d3c\uff0c\u5927\u8d3c\u751f\u5c0f\u8d3c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u81ed\u6211\uff0c\u6211\u867d\u4e5f\u505a\u8fc7\u4e00\u6b21\u8d3c\uff0c\u53ef\u662f\u975e\u4f46\u6ca1\u5c1d\u751c\u5934\uff0c\u53cd\u800c\u628a\u6700\u540e\u7684 \u4e00\u628a\u5251\u4e5f\u8d54\u4e86\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u505a\u8d3c\u4e5f\u6709\u5b66\u95ee\uff0c\u672c\u6765\u5c31\u4e0d\u662f\u4eba\u4eba\u90fd\u4f1a\u505a\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u770b\u6211\u4eec\u6700\u597d\u5c06\u8fd9\u4e9b\u4e1c\u897f\u62ff\u53bb\u8fd8\u7ed9\u522b\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u7ed9\u8c01\uff1f\u8c01\u77e5\u9053\u8fd9\u4e9b\u4e1c\u897f\u662f\u4ece\u8c01\u5bb6\u5077\u6765\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u53ef\u4ee5\u6253\u542c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\u6253\u542c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5c71\u4e0b\u3002\u8fd9\u4e9b\u4e1c\u897f\u65e2\u7136\u5168\u662f\u4ed6\u4eec\u5728\u6628\u5929\u665a\u4e0a\u4e00\u591c\u4e2d\u5077\u6765\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u5728\u5c71\u4e0b\u5077 \u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77a7\u7740\u90a3\u6574\u7bb1\u7684\u5143\u5b9d\uff0c\u53f9\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u5730\u65b9\u7684\u786e\u4e0d\u662f\u4e2a\u7a77\u5730\u65b9\u3002\u2026\u2026\u65e0\u8bba \u4ec0\u4e48\u5730\u65b9\u6709\u8fd9\u4e48\u591a\u91d1\u5b50\u5c31\u4e0d\u662f\u7a77\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u5bcc\u8d35\u5c71\u5e84\u81f3\u5c11\u5728\u4eca\u5929\u771f\u7684\u662f\u540d\u526f\u5176\u5b9e\u7684\u5bcc\u8d35\u5c71\u5e84\u3002\u201d \u3000\u3000\u5bcc\u8d35\u5c71\u5e84\u540d\u526f\u5176\u5b9e\u7684\u65f6\u5019\u867d\u7136\u5e76\u4e0d\u957f\uff0c\u4f46\u4ed6\u4eec\u5374\u8fd8\u662f\u5feb\u4e50\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u505a\u4e86\u4e2a\u6700\u806a\u660e\u7684\u9009\u62e9\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u5c31\u662f\u5bcc\u8d35\u79bb\u4ed6\u4eec\u6700\u8fd1\u7684\u65f6\u5019\uff0c\u4f46\u4ed6\u4eec\u5e76\u4e0d\u8d2a\u56fe\u5bcc\u8d35\uff0c\u4e5f\u4e0d\u8981\u4ee5\u8d2a\u5a6a\u3001\u5351\u9119\u3001\u6b3a\u8bc8 \u7684\u65b9\u6cd5\u53bb\u652b\u53d6\u5bcc\u8d35\uff0c\u6240\u4ee5\u4ed6\u4eec\u6c38\u8fdc\u5feb\u4e50\uff0c\u5c31\u8c61\u6c90\u6d74\u5728\u6625\u65e5\u9633\u5149\u4e2d\u7684\u82b1\u8349\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u5feb\u4e50\u8fdc\u6bd4\u8d22\u5bcc\u53ef\u7231\u7684\u591a\u3002 \u7b2c\u4e09\u7ae0\u3000\u9ea6\u8001\u5e7f>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e09\u7ae0\u3000\u9ea6\u8001\u5e7f \u3000\u3000\u9ea6\u8001\u5e7f\u662f\u4e2a\u5c0f\u996d\u94fa\u7684\u540d\u5b57\uff0c\u4e5f\u662f\u4e2a\u4eba\u7684\u540d\u5b57\u3002\u201c\u9ea6\u8001\u5e7f\u201d\u7684\u70e7\u814a\u9999\u5f97\u636e\u8bf4\u53ef\u4ee5\u5c06\u9644\u8fd1\u5341 \u91cc\u4e4b\u5185\u7684\u4eba\u548c\u72d7\u5168\u90fd\u5f15\u5230\u95e8\u53e3\u6765\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u4e5f\u5c31\u662f\u8fd9\u5c0f\u996d\u94fa\u7684\u8001\u677f\u3001\u5927\u5e08\u5085\u517c\u8dd1\u5802\u3002 \u3000\u3000\u9664\u4e86\u70e7\u814a\u5916\uff0c\u9ea6\u8001\u5e7f\u53ea\u5356\u767d\u996d\u548c\u7ca5\u3002 \u3000\u3000\u82e5\u60f3\u559d\u9152\uff0c\u5c31\u5f97\u5230\u9694\u58c1\u51e0\u5bb6\u7684\u201c\u8a00\u8302\u6e90\u9152\u94fa\u201d\u53bb\u5356\uff0c\u6216\u8005\u662f\u4e70\u4e86\u70e7\u814a\u5230\u8a00\u8302\u6e90\u53bb\u559d\u3002 \u3000\u3000\u6709\u4eba\u529d\u9ea6\u8001\u5e7f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u7740\u5356\u9152\u5462\uff0c\u5c82\u975e\u53ef\u4ee5\u591a\u8d5a\u70b9\u94b1\uff1f\u4f46\u9ea6\u8001\u5e7f\u662f\u4e2a\u56fa\u6267\u7684\u4eba\uff0c \u201c\u8001\u5e7f\u201d\u5927\u591a\u662f\u5f88\u56fa\u6267\u7684\u4eba\uff0c\u6240\u4ee5\u8981\u559d\u9152\uff0c\u8fd8\u5f97\u81ea\u5df1\u53bb\u4e70\uff0c\u4f60\u82e5\u5bf9\u8fd9\u5730\u65b9\u4e0d\u6ee1\u610f\uff0c\u4e5f\u6ca1\u5730\u65b9 \u597d\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u9ea6\u8001\u5e7f\u7684\u70e7\u814a\u4e0d\u4f46\u6700\u597d\uff0c\u4e5f\u662f\u8fd9\u9644\u8fd1\u552f\u4e00\u7684\u4e00\u5bb6\u3002 \u3000\u3000\u5c71\u57ce\u91cc\u7684\u4eba\u8fde\u6cb9\u706f\u90fd\u820d\u4e0d\u5f97\u70b9\uff0c\u600e\u4e48\u820d\u5f97\u82b1\u94b1\u5230\u5916\u9762\u5403\u996d\u3002\u6240\u4ee5\u5c31\u7b97\u6709\u4eba\u60f3\u62a2\u8001\u5e7f\u7684\u751f \u610f\uff0c\u8fc7\u51e0\u5929\u4e5f\u5c31\u4f1a\u81ea\u52a8\u5173\u95e8\u5927\u5409\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u5bf9\u738b\u52a8\u548c\u90ed\u5927\u8def\u4ed6\u4eec\u4e00\u5411\u6ca1\u6709\u6076\u611f\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e9b\u4eba\u867d\u7136\u7a77\uff0c\u5374\u4ece\u4e0d\u8d4a\u5e10\u3002 \u3000\u3000\u4ed6\u4eec\u6bcf\u6b21\u6765\u7684\u65f6\u5019\uff0c\u8eab\u4e0a\u603b\u6709\u4e24\u628a\u94f6\u5b50\uff0c\u800c\u4e14\u6bcf\u6b21\u90fd\u5403\u5f97\u5f88\u591a\u3002\u65e0\u8bba\u54ea\u4e2a\u996d\u94fa\u8001\u677f\u90fd\u4e0d \u4f1a\u5bf9\u5403\u5f88\u591a\u7684\u5ba2\u4eba\u6709\u6076\u611f\u7684\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u7684\u659c\u5bf9\u9762\uff0c\u5c31\u662f\u738b\u52a8\u4ed6\u4eec\u7684\u201c\u5a18\u8205\u5bb6\u201d\u3002 \u3000\u3000\u5a18\u8205\u5bb6\u7684\u65c1\u8fb9\u5c31\u662f\u5f53\u94fa\u3002 \u3000\u3000\u4ed6\u4eec\u6bcf\u6b21\u6765\u7684\u65f6\u5019\uff0c\u90fd\u4f1a\u5148\u5230\u5a18\u8205\u5bb6\u53bb\u8f6c\u4e00\u8f6c\uff0c\u51fa\u6765\u7684\u65f6\u5019\u4e00\u5b9a\u6bd4\u8fdb\u53bb\u7684\u65f6\u5019\u795e\u6c14\u5f97 \u591a\u3002 \u3000\u3000\u4f46\u4eca\u5929\u5374\u5f88\u4f8b\u5916\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u8fc7\u5a18\u8205\u5bb6\u7684\u65f6\u5019\uff0c\u5c45\u7136\u8fde\u505c\u90fd\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u800c\u4e14\u80f8\u633a\u5f97\u5f88\u9ad8\u3002\u770b\u4ed6\u4eec\u8d70\u8def\u7684\u6837 \u5b50\uff0c\u5c31\u77e5\u9053\u53e3\u888b\u51b3\u4e0d\u4f1a\u662f\u7a7a\u7684\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u53c8\u653e\u5fc3\uff0c\u53c8\u5947\u602a\uff1a\u201c\u5514\u901a\u5462\u73ed\u5951\u5f1f\u6539\u884c\u505a\u8d3c\uff1f\u70b9\u89e3\u7a81\u7136\u6709\u8fd9\u4e48\u591a\u94b1\uff1f\u201d\u5951\u5f1f\u5e76\u4e0d \u5b8c\u5168\u662f\u9a82\u4eba\u7684\u610f\u601d\uff0c\u6709\u65f6\u5b8c\u5168\u662f\u4e3a\u4e86\u8868\u793a\u4eb2\u70ed\u3002 \u3000\u3000\u8fd9\u6b21\u7684\u6709\u56db\u4e2a\u4eba\uff0c\u8fd8\u6ca1\u8fdb\u95e8\uff0c\u9ea6\u8001\u5e7f\u5c31\u8fce\u4e86\u4e0a\u53bb\uff0c\u7528\u4ed6\u90a3\u534a\u751f\u4e0d\u719f\u7684\u5e7f\u4e1c\u5b98\u8bdd\u6253\u62db\u547c\uff0c \u9053\uff1a\u201c\u4f60\u4eec\u4eca\u65e5\u70b9\u89e3\u8fd9\u4e48\u65e9\uff1f\u201d\u5929\u4e0d\u6015\uff0c\u5730\u4e0d\u6015\uff0c\u5c31\u6015\u5e7f\u4e1c\u4eba\u8bf4\u5b98\u8bdd\u3002 \u3000\u3000\u597d\u5728\u90ed\u5927\u8def\u5df2\u542c\u60ef\u4e86\uff0c\u5c31\u7b97\u542c\u4e0d\u61c2\uff0c\u4e5f\u731c\u5f97\u51fa\u3002\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u4eba\u6765\u5f97\u65e9\uff0c\u662f\u94b1\u6765\u5f97\u65e9\uff0c \u5148\u7ed9\u6211\u4eec\u5207\u4e24\u53ea\u70e7\u9e45\uff0c\u4e94\u65a4\u8106\u76ae\u8089\uff0c\u518d\u6765\u4e2a\u6cb9\u9e21\u3002\u201d\u9ea6\u8001\u5e7f\u7728\u7728\u773c\u9053\uff1a\u201c\u5514\u996d\u9152\uff1f\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u5f53\u7136\u8981\uff0c\u4f60\u5148\u53bb\u62ff\u51e0\u65a4\u6765\uff0c\u7b49\u7b49\u4e00\u9f50\u7b97\u7ed9\u4f60\u3002\u201d\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u54cd\u4e86\uff0c\u56e0\u4e3a\u4ed6\u8eab\u4e0a\u6709 \u952d\u8db3\u8db3\u5341\u4e24\u91cd\u7684\u91d1\u5b50\u3002 \u3000\u3000\u65e2\u7136\u662f\u4e3a\u4e86\u8981\u6253\u542c\u8c01\u5bb6\u88ab\u5077\u7684\u6d88\u606f\uff0c\u82b1\u4ed6\u4eec\u5341\u6765\u4e24\u91d1\u5b50\u53c8\u4f55\u59a8\uff0c\u809a\u5b50\u997f\u7684\u65f6\u5019\u8fde\u8bdd\u90fd\u61d2 \u5f97\u8bf4\uff0c\u600e\u4e48\u80fd\u6253\u542c\u6d88\u606f\uff1f\u6240\u4ee5\u4ed6\u4eec\u7684\u826f\u5fc3\u4e0a\u8fde\u4e00\u70b9\u8d1f\u62c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u9152\u6e10\u6e10\u5728\u74f6\u5b50\u91cc\u4e0b\u964d\u7684\u65f6\u5019\uff0c\u8d23\u4efb\u5fc3\u5c31\u5728\u4ed6\u4eec\u5fc3\u91cc\u4e0a\u5347\u8d77\u6765\u3002 \u3000\u3000\u559d\u4e86\u4eba\u5bb6\u7684\u9152\uff0c\u5c31\u8be5\u66ff\u4eba\u5bb6\u505a\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u7edd\u4e0d\u662f\u767d\u5403\u7684\u4eba\u3002 \u3000\u3000\u4e8e\u662f\u90ed\u5927\u8def\u5c31\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u4f60\u53ef\u542c\u5230\u4ec0\u4e48\u6d88\u606f\u6ca1\u6709\uff1f\u201d\u6ca1\u6709\u3002 \u3000\u3000\u57ce\u91cc\u6700\u8038\u52a8\u7684\u6d88\u606f\uff0c\u5c31\u662f\u5f00\u6742\u8d27\u5e97\u7684\u738b\u5927\u5a18\u751f\u4e86\u4e2a\u53cc\u80de\u80ce\u3002 \u3000\u3000\u5927\u5bb6\u5f00\u59cb\u5947\u602a\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4eec\u4e0d\u662f\u5728\u8fd9\u91cc\u5077\u7684\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u4e48\u8fd9 \u5730\u65b9\u4e3a\u4ec0\u4e48\u6ca1\u6709\u88ab\u5077\u7684\u4eba\uff1f\u4e00\u591c\u95f4\u5077\u4e86\u8fd9\u4e48\u591a\u4eba\u5bb6\uff0c\u662f\u5927\u4e8b\uff0c\u57ce\u91cc\u65e9\u8be5\u95f9\u7ffb\u5929\u4e86\u3002\u201d\u71d5\u4e03 \u9053\uff1a\u201c\u4e0d\u662f\u6ca1\u6709\uff0c\u800c\u662f\u4e0d\u8bf4\uff0c\u4e0d\u6562\u8bf4\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u88ab\u5077\u53c8\u4e0d\u662f\u4ef6\u4e22\u4eba\u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562 \u8bf4\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u94b1\u8d22\u82e5\u662f\u6765\u8def\u4e0d\u6b63\uff0c\u88ab\u4eba\u5077\u4e86\u4e5f\u53ea\u597d\u54d1\u5df4\u5403\u9ec4\u8fde\uff0c\u82e6\u5728\u5fc3\u91cc\u3002\u201d \u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u53ef\u5c31\u4e0d\u5173\u6211\u4eec\u7684\u4e8b\uff0c\u6211\u4eec\u53cd\u6b63\u5df2\u5c3d\u4e86\u529b\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u8fd9\u65f6\u9152\u5df2 \u5dee\u4e0d\u591a\u5168\u5230\u4e86\u4ed6\u7684\u809a\u5b50\u91cc\uff0c\u5df2\u5feb\u5c06\u4ed6\u7684\u8d23\u4efb\u5fc3\u5b8c\u5168\u6324\u4e86\u51fa\u6765\u3002\u4ed6\u5ffd\u7136\u89c9\u5f97\u8f7b\u677e\u5f97\u5f88\uff0c\u5927\u58f0 \u9053\uff1a\u201c\u518d\u53bb\u66ff\u6211\u4eec\u62ff\u51e0\u65a4\u9152\u6765\u3002\u201d\u9ea6\u8001\u5e7f\u8fd8\u6ca1\u6709\u8d70\u51fa\u95e8\uff0c\u95e8\u5916\u5ffd\u7136\u8d70\u8fdb\u6765\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u7b2c\u4e00\u4eba\u5f88\u9ad8\uff0c\u7a7f\u7684\u8863\u670d\u91d1\u5149\u95ea\u95ea\uff0c\u597d\u8c61\u5f88\u534e\u4e3d\uff1b\u7b2c\u4e8c\u4eba\u66f4\u9ad8\uff0c\u7626\u5f97\u51fa\u5947\u3002\u4f46\u8fd9\u4e24\u4eba\u957f\u7684 \u7a76\u7adf\u662f\u4ec0\u4e48\u6a21\u6837\uff0c\u522b\u4eba\u5e76\u6ca1\u6709\u770b\u6e05\u3002 \u3000\u3000\u56e0\u4e3a\u6240\u6709\u7684\u76ee\u5149\u90fd\u5df2\u88ab\u7b2c\u4e09\u4e2a\u4eba\u5438\u5f15\u3002 \u3000\u3000\u8fd9\u4eba\u5168\u8eab\u90fd\u662f\u9ed1\u7684\uff0c\u9ed1\u8863\u3001\u9ed1\u88e4\u3001\u9ed1\u9774\u5b50\uff0c\u624b\u4e0a\u5e26\u7740\u9ed1\u624b\u5957\uff0c\u5934\u4e0a\u4e5f\u5e26\u7740\u9ed1\u8272\u7684\u6be1\u7b20\uff0c \u7d27\u7d27\u538b\u5728\u989d\u4e0a\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5c31\u7b97\u4e0d\u5e26\u8fd9\u9876\u6be1\u7b20\u4e5f\u6ca1\u6709\u4eba\u80fd\u770b\u5230\u4ed6\u7684\u8138\uff0c\u4ed6\u8fde\u5934\u5e26\u8138\u90fd\u7528\u4e00\u4e2a\u9ed1\u5e03\u7684\u5957\u5b50\u5957\u4e86 \u8d77\u6765\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u5200\u4e00\u822c\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u65f6\u591c\u884c\u4eba\u7684\u6253\u626e\uff0c\u53ea\u9002\u5408\u534a\u591c\u4e09\u66f4\u53bb\u505a\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\u65f6\u7a7f\u7740\uff0c\u4f46\u4ed6\u5374\u5149\u660e\u6b63\u5927\u7684\u7a7f\u5230 \u8857\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u957f\u7684\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u7a76\u7adf\u662f\u4e2a\u600e\u4e48\u6837\u5b50\uff1f\u8c01\u4e5f\u770b\u4e0d\u89c1\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u5168\u8eab\u4e0a\u4e0b\u6839\u672c\u6ca1 \u6709\u4e00\u5bf8\u53ef\u4ee5\u8ba9\u4eba\u5bb6\u770b\u89c1\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u6bcf\u4e2a\u4eba\u90fd\u89c9\u5f97\u4ed6\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u5bf8\u5730\u65b9\u90fd\u5145\u6ee1\u4e86\u5371\u9669\u3002 \u3000\u3000\u6700\u5371\u9669\u7684\u5f53\u7136\u8fd8\u662f\u4ed6\u80cc\u540e\u80cc\u7740\u7684\u90a3\u67c4\u5251\u3002 \u3000\u3000\u4e00\u67c4\u56db\u5c3a\u4e03\u5bf8\u957f\u7684\u4e4c\u9798\u5251\u3002 \u3000\u3000\u5f88\u5c11\u4eba\u7528\u8fd9\u79cd\u5251\uff0c\u56e0\u4e3a\u8981\u5c06\u8fd9\u4e48\u957f\u4e00\u67c4\u5251\uff0c\u4ece\u5251\u9798\u4e2d\u62d4\u51fa\u6765\u5c31\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u90a3\u5fc5\u987b\u6709 \u5f88\u7279\u522b\u7684\u624b\u6cd5\uff0c\u5f88\u7279\u522b\u7684\u6280\u5de7\u3002 \u3000\u3000\u80fd\u7528\u8fd9\u79cd\u5251\u7684\u4eba\uff0c\u5c31\u7edd\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u3002\u65e2\u7136\u5df2\u5f88\u56f0\u96be\u5730\u5c06\u5251\u62d4\u51fa\u6765\uff0c\u5c31\u51b3\u4e0d\u4f1a\u8f7b\u8f7b\u6613 \u6613\u653e\u56de\u53bb\u3002 \u3000\u3000\u5251\u56de\u9798\u7684\u65f6\u5019\u901a\u5e38\u5df2\u67d3\u4e0a\u4e86\u8840\u3002 \u3000\u3000\u522b\u4eba\u7684\u8840\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u8d70\u8fdb\u6765\u540e\uff0c\u5c31\u5360\u636e\u4e86\u6700\u91cc\u9762\u89d2\u843d\u7684\u4e00\u5f20\u684c\u5b50\uff0c\u663e\u7136\u4e0d\u613f\u610f\u6253\u6270\u522b\u4eba\uff0c\u66f4\u4e0d\u613f\u610f \u88ab\u522b\u4eba\u6253\u6270\u3002 \u3000\u3000\u4ed6\u4eec\u8981\u7684\u4e1c\u897f\u662f\uff1a\u201c\u968f\u4fbf\u3002\u201d\u90a3\u8868\u793a\u4ed6\u4eec\u65e2\u4e0d\u662f\u4e3a\u4e86\u201c\u5403\u201d\u800c\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u4e5f\u4e0d\u8bb2\u7a76 \u5403\u3002 \u3000\u3000\u4e0d\u8bb2\u7a76\u5403\u5f97\u4eba\u82e5\u4e0d\u662f\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u5c31\u4e00\u5b9a\u662f\u5728\u60f3\u522b\u7684\u4e8b\u3002\u65e0\u8bba\u4ed6\u4eec\u60f3\u7684\u662f\u4ec0\u4e48\uff0c\u90fd\u4e00\u5b9a\u4e0d \u4f1a\u662f\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u6797\u592a\u5e73\u4e00\u76f4\u5728\u77a7\u7740\u9ed1\u8863\u4eba\u7684\u5251\uff0c\u5583\u5583\u9053\uff1a\u201c\u5251\u672a\u51fa\u9798\uff0c\u5c31\u5df2\u5e26\u7740\u6740\u6c14\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e0d \u662f\u5251\u7684\u6740\u6c14\uff0c\u662f\u4eba\u7684\u6740\u6c14\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u77e5\u9053\u6211\u5c31\u7b97\u5df2\u559d\u5f97\u9169\u914a \u5927\u9189\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u627e\u8fd9\u4eba\u6253\u67b6\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u9053\uff1a\u201c\u53e6\u5916\u4e24\u4eba\u6211\u5012\u8ba4\u5f97\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u4eec\u5374 \u4e0d\u8ba4\u5f97\u4f60\u3002\u201d\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff0c\u8c61\u4ed6\u4eec\u8fd9\u4e48\u6709\u540d\u6c14\u7684\u4eba\u600e\u4e48\u8ba4\u5f97\u6211\uff1f\u201d\u90ed \u5927\u8def\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u6709\u540d\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5750\u5728\u6700\u5916\u9762\u90a3\u4e2a\u53c8\u7626\u53c8\u9ad8\u7684\u4eba\uff0c\u53eb\u4f5c\u5939\u68cd\uff0c\u53c8\u53eb\u505a\u68cd \u5b50\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u68cd\u5b50\uff0c\u5012\u4e5f\u8c61\uff0c\u5939\u68cd\u8fd9\u540d\u5b57\u5c31\u6709\u70b9\u7279\u522b\u4e86\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5939\u68cd\u662f\u79cd\u5211 \u5177\uff0c\u65e0\u8bba\u591a\u5201\u591a\u6ed1\u7684\u8d3c\uff0c\u4e00\u4e0a\u4e86\u5939\u68cd\uff0c\u4f60\u8981\u4ed6\u8bf4\u4ec0\u4e48\u4ed6\u5c31\u8bf4\u4ec0\u4e48\uff0c\u8981\u4ed6\u53eb\u4f60\u7956\u5b97\u4ed6\u90fd\u4e0d\u6562\u4e0d \u53eb\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u4e5f\u6709\u8fd9\u79cd\u672c\u4e8b\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u636e\u8bf4\u65e0\u8bba\u8c01\u9047\u7740\u4ed6\u90fd\u6ca1\u6cd5\u5b50\u4e0d\u8bf4\u5b9e\u8bdd\uff0c \u5c31\u7b97\u662f\u4e2a\u6b7b\u4eba\uff0c\u4ed6\u4e5f\u6709\u672c\u4e8b\u95ee\u5f97\u51fa\u53e3\u4f9b\u6765\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u624b\u6bb5\u4e00\u5b9a\u5f88\u8fa3\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u4ed6\u8fd8\u6709\u4e2a\u5916\u53f7\u53eb\u68cd\u5b50\uff0c\u90a3\u610f\u601d\u5c31\u662f\u2018\u89c1\u4eba\u5c31\u6253\u2019\u3002\u65e0\u8bba\u8c01\u843d\u5230\u4ed6\u7684\u624b\u91cc\uff0c\u90fd\u514d\u4e0d\u4e86\u8981\u5148\u88ab \u4ed6\u6253\u7684\u9f3b\u9752\u773c\u80bf\u518d\u8bf4\u3002\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\u4e00\u9047\u89c1\u4ed6\uff0c\u7b80\u76f4\u5c31\u597d\u8c61\u9047\u89c1\u4e86\u8981\u547d\u9b3c\u3001\u6d3b\u960e\u738b\u3002\u201d\u738b\u52a8 \u9053\uff1a\u201c\u4ed6\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6e05\u6cb3\u53bf\u7684\u6355\u5934\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u6e05\u6cb3\u53bf\u5e76\u4e0d\u662f\u4e2a\u5927\u5730\u65b9\uff0c \u5c82\u975e\u57cb\u6ca1\u4e86\u4eba\u624d\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u7684\u624b\u6bb5\u592a\u8fa3\uff0c\u6240\u4ee5\u4e00\u76f4\u5347\u4e0d\u4e0a\u53bb\u3002\u4f46\u65e0\u8bba\u4ec0\u4e48\u5730\u65b9 \u6709\u4e86\u529e\u4e0d\u4e86\u7684\u5927\u6848\u5b50\uff0c\u90fd\u514d\u4e0d\u4e86\u8981\u5230\u6e05\u6cb3\u53bf\u53bb\u501f\u4ed6\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u4f4d\u91d1\u5149\u95ea\u95ea\u7684\u4ec1 \u5144\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u59d3\u91d1\uff0c\u53c8\u559c\u6b22\u91d1\u5b50\uff0c\u6240\u4ee5\u53eb\u2018\u91d1\u72ee\u2019\uff0c\u4f46\u522b\u4eba\u5728\u80cc\u5730\u91cc\u5374\u90fd\u53eb\u4ed6\u91d1\u6bdb\u72ee \u5b50\u72d7\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u51ed\u826f\u5fc3\u8bb2\uff0c\u8fd9\u4eba\u5012\u4e00\u70b9\u4e0d\u8c61\u72ee\u5b50\u72d7\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u770b\u8fc7\u72ee\u5b50\u72d7\u6ca1 \u6709\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5404\u79cd\u72d7\u6211\u90fd\u770b\u8fc7\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u72ee\u5b50\u72d7\u8138\u4e0a\u4ec0\u4e48\u4e1c\u897f\u6700\u5927\uff1f\u201d\u6797\u592a\u5e73\u62a2 \u7740\u9053\uff1a\u201c\u9f3b\u5b50\u6700\u5927\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\u6700\u5c0f\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5634\u3002\u201d\u4ed6\u7b11\u4e86\u7b11\uff0c\u53c8\u89e3\u91ca \u7740\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u517b\u8fc7\u597d\u51e0\u6761\u72ee\u5b50\u72d7\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4eec\u518d\u770b\u770b\u90a3\u4eba\u7684\u8138\u3002\u201d\u4ece\u8fd9\u8fb9\u770b\u8fc7 \u53bb\uff0c\u521a\u597d\u53ef\u4ee5\u770b\u5230\u90a3\u201c\u91d1\u6bdb\u72ee\u5b50\u72d7\u201d\u7684\u8138\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u4ed6\u7684\u8138\uff0c\u90fd\u65e0\u6cd5\u4e0d\u770b\u5230\u4ed6\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u9f3b\u5b50\u5c31\u5df2\u5360\u636e\u4e86\u6574\u4e2a\u4e00\u5f20\u8138\u7684\u4e09\u5206\u4e4b\u4e00\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u7684\u5634\u90fd\u6bd4\u9f3b\u5b50\u5bbd\uff0c\u4f46\u4ed6\u7684\u9f3b\u5b50\u5374\u6bd4\u5634\u5bbd\uff1b\u82e5\u662f\u4ece\u4ed6\u5934\u4e0a\u671b\u4e0b\u53bb\uff0c\u4e00\u5b9a\u770b\u4e0d\u5230\u4ed6\u7684 \u5634\uff0c\u56e0\u4e3a\u5634\u5df4\u5df2\u88ab\u9f3b\u5b50\u6321\u4f4f\u3002 \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u7b11\u51fa\u58f0\u6765\uff0c\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4e2a\u7279\u5927\u53f7\u7684\u9f3b\u5b50\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4ed6\u7684\u773c\u775b\u4e00 \u5b9a\u4e0d\u592a\u7075\u3002\u201d\u90ed\u5927\u8def\u5947\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u773c\u5df2\u88ab\u4e2d\u95f4\u7684\u9f3b\u5b50\u9694\u5f00\u4e86\uff0c \u6240\u4ee5\u5de6\u8fb9\u7684\u773c\u775b\u53ea\u80fd\u770b\u5230\u5de6\u8fb9\u7684\u4e1c\u897f\uff0c\u53f3\u8fb9\u7684\u773c\u775b\u53ea\u80fd\u770b\u5230\u53f3\u8fb9\u3002\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8fde\u71d5\u4e03\u90fd \u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u53ef\u662f\u5230\u73b0\u5728\u6211\u8fd8\u6ca1\u6709\u627e\u5230\u4ed6\u7684\u5634\u3002\u201d\u71d5\u4e03\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u9f3b\u5b50\u4e0b\u9762\u7684\u90a3 \u4e2a\u6d1e\uff0c\u5c31\u662f\u5634\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u662f\u5634\u4e48\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u9f3b\u5b54\u5462\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u9f3b\u5b54\u4e0a\u600e \u4e48\u4f1a\u957f\u80e1\u5b50\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u90a3\u662f\u9f3b\u6bdb\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u5403\u4e1c\u897f\u7684\u65f6\u5019\uff0c\u522b\u4eba\u5f80 \u5f80\u4e0d\u77e5\u9053\u4e1c\u897f\u662f\u4ece\u54ea\u91cc\u5403\u4e0b\u53bb\u7684\u3002\u201d\u4ed6\u4eec\u867d\u7136\u5728\u62da\u547d\u5fcd\u4f4f\u7b11\uff0c\u4f46\u8fd9\u662f\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u5f97\u51e0\u4e4e\u6ed1\u5230\u684c\u5b50\u5e95\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u91d1\u6bdb\u72ee\u5b50\u72d7\u5ffd\u7136\u56de\u8fc7\u5934\uff0c\u77a7\u4e86\u4ed6\u4eec\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u4e00\u773c\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u5df2\u611f\u89c9\u5230\u4ed6\u773c\u775b\u91cc\u90a3\u79cd\u903c\u4eba\u7684\u950b\u8292\uff0c\u7adf\u771f\u7684\u6709\u70b9\u50cf\u662f\u96c4\u5e08\u7684\u773c\u775b\uff0c\u8fde\u773c\u73e0\u5b50\u90fd \u9ec4\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\u672c\u6765\u5c31\u5f88\u4f4e\uff0c\u73b0\u5728\u66f4\u4f4e\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u4eba\u53c8\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e5f\u662f\u6355\u5934\uff0c\u4e24\u5e74\u524d\u8fd8\u662f\u4eac\u57ce\u7684\u6355\u5934\uff0c\u6700 \u8fd1\u542c\u8bf4\u5df2\u5347\u5230\u5317\u4e5d\u7701\u7684\u603b\u6355\u5934\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u770b\u4ed6\u7a7f\u7684\u5c31\u8c61\u662f\u4e2a\u82b1\u82b1\u516c\u5b50\uff0c\u5b9e\u5728\u4e0d\u8c61\u662f\u4f4d \u540d\u6355\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u8c61\u7a77\u5149\u86cb\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4ed6\u7684\u672c\u4e8b\u53c8\u5728\u54ea\u91cc\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5728 \u9f3b\u5b50\u4e0a\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u9f3b\u5b50\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u7684\u9f3b\u5b50\u867d\u5927\uff0c\u5374\u4e0d\u662f\u5927\u800c\u65e0\u5f53\u3002\u636e\u8bf4\u4ed6\u7684\u9f3b \u5b50\u6bd4\u72d7\u8fd8\u7075\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u88ab\u4ed6\u55c5\u8fc7\u5473\u9053\uff0c\u65e0\u8bba\u600e\u4e48\u6539\u626e\uff0c\u90fd\u9003\u4e0d\u4e86\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u672c\u4e8b \u9053\u7684\u786e\u4e0d\u5c0f\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u53ef\u8bf4\u5168\u90fd\u662f\u516d\u6247\u95e8\u91cc\u4e00\u7b49\u4e00\u7684\u9876\u5c16\u9ad8\u624b\uff0c\u82e5\u4e0d\u662f\u4ec0\u4e48\u5927\u6848 \u5b50\uff0c\u7edd\u5bf9\u52a8\u4e0d\u4e86\u4ed6\u4eec\uff0c\u6240\u4ee5\u2026\u2026\u201d\u738b\u52a8\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5947\u602a\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u5ffd\u7136\u5230\u4e86\u8fd9\u79cd\u5730\u65b9 \u6765\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6211\u7684\u786e\u5947\u602a\u7684\u5f88\uff0c\u82e5\u8bf4\u4ed6\u4eec\u662f\u4e3a\u4e86\u6628\u5929\u665a\u4e0a\u7684\u6848\u5b50\u6765\u7684\uff0c\u4ed6\u4eec\u7684\u6d88\u606f\u600e\u4f1a \u8fd9\u4e48\u5feb\uff1f\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u8857\u4e0a\u5ffd\u7136\u4f20\u6765\u4e86\u4e00\u58f0\u5973\u4eba\u7684\u5c16\u53eb\u58f0\uff0c\u5c31\u597d\u8c61\u6709\u4eba\u8e29\u5230\u4e86\u9e21\u8116\u5b50\u4f3c\u7684\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4eec\u5c31\u770b\u5230\u4e00\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u5973\u4eba\u4ece\u5bf9\u9762\u4e00\u5bb6\u623f\u5b50\u91cc\u51b2\u51fa\u6765\uff0c\u4e00\u4e2a\u77ee\u77ee\u80d6\u80d6\u7684\u7537\u4eba \u62da\u547d\u62c9\u4e5f\u62c9\u4e0d\u4f4f\u3002 \u3000\u3000\u5230\u540e\u6765\u8fd9\u5973\u4eba\u7d22\u6027\u8d56\u5230\u5730\u4e0a\uff0c\u53f7\u5555\u5927\u54ed\uff0c\u8fb9\u54ed\u8fb9\u53eb\uff0c\u9053\uff1a\u201c\u6211\u8fde\u68fa\u6750\u672c\u90fd\u88ab\u4eba\u5077\u53bb\u4e86\uff0c \u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8bf4\uff1f\u2026\u2026\u6211\u504f\u8981\u8bf4\u3002\u201d\u5979\u8d8a\u8bf4\u8d8a\u4f24\u5fc3\uff0c\u7d22\u6027\u7528\u5934\u53bb\u649e\u5730\uff0c\u5927\u54ed\u9053\uff1a\u201c\u5929\u5440\uff0c\u5929\u6740 \u7684\u5f3a\u76d7\u5440\uff0c\u4f60\u597d\u72e0\u7684\u5fc3\u5440\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7559\u70b9\u7ed9\u6211\uff1f\u2026\u2026\u6574\u6574\u7684\u4e09\u5343\u4e24\u91d1\u5b50\uff0c\u8fd8\u6709\u6211\u7684\u9996\u9970\uff0c \u82e5\u662f\u90a3\u4f4d\u597d\u5fc3\u7684\u4eba\u66ff\u6211\u627e\u56de\u6765\uff0c\u6211\u60c5\u613f\u5206\u7ed9\u4ed6\u4e00\u591a\u534a\u3002\u201d\u90a3\u7537\u4eba\u8138\u4e0a\u7ea2\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u7528\u51fa \u5403\u5976\u7684\u529b\u6c14\uff0c\u603b\u7b97\u628a\u5979\u6b7b\u62d6\u4e86\u56de\u53bb\uff0c\u62bd\u7a7a\u8fd8\u626d\u8f6c\u5934\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90a3\u6709\u4e09\u5343\u4e24\u91d1\u5b50\u7ed9\u4eba \u5bb6\u5077\uff1f\u201d\u90ed\u5927\u8def\u548c\u71d5\u4e03\u4ea4\u6362\u4e86\u773c\u8272\uff0c\u6b63\u60f3\u95ee\u9ea6\u8001\u5e7f\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u4f46\u90a3\u5939\u68cd\u5374\u6bd4\u4ed6\u4eec\u95ee\u5f97 \u66f4\u5feb\u3002 \u3000\u3000\u4ed6\u58f0\u97f3\u5f88\u6c89\uff0c\u8bf4\u8bdd\u5f88\u6162\uff0c\u6bcf\u4e2a\u5b57\u8bf4\u51fa\u6765\u90fd\u597d\u8c61\u5f88\u8d39\u529b\u3002\u90a3\u7ed9\u4eba\u4e00\u79cd\u611f\u89c9\uff0c\u4ed6\u8bf4\u7684\u6bcf\u4e2a\u5b57 \u4f60\u6700\u597d\u90fd\u7559\u795e\u53bb\u542c\u7740\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u8fd9\u592b\u59bb\u4fe9\u4eba\u542c\u8bf4\u662f\u4ece\u5f00\u5c01\u6765\u7684\uff0c\u672c\u6765\u505a\u7684\u662f\u68c9\u5e03\u751f\u610f\uff0c\u79ef\u4e86\u5343\u591a\u4e24\u94f6\u5b50\uff0c \u51c6\u5907\u5230\u8fd9\u91cc\u8282\u8282\u7701\u7701\u7684\u8fc7\u4e0b\u534a\u8f88\u5b50\u3002\u4ed6\u4eec\u5bb6\u91cc\u82e5\u771f\u6709\u4e09\u5343\u4e24\u91d1\u5b50\u88ab\u4eba\u5077\u4e86\uff0c\u90a3\u624d\u771f\u662f\u602a \u4e8b\u3002\u201d\u4ed6\u672c\u4e0d\u662f\u4e2a\u591a\u5634\u7684\u4eba\uff0c\u4f46\u73b0\u5728\u5634\u4e0a\u5374\u597d\u8c61\u62b9\u4e86\u6cb9\uff0c\u8fde\u5b98\u8bdd\u90fd\u7a81\u7136\u8bf4\u7684\u6bd4\u5e73\u65f6\u6807\u51c6\u591a \u4e86\u3002 \u3000\u3000\u5939\u68cd\u5728\u542c\u7740\u3002 \u3000\u3000\u4ed6\u8bf4\u5f97\u6162\uff0c\u542c\u5f97\u66f4\u4ed4\u7ec6\uff0c\u8c61\u662f\u8981\u628a\u4f60\u8bf4\u7684\u6bcf\u4e2a\u5b57\u90fd\u5148\u56bc\u70c2\uff0c\u518d\u541e\u5230\u809a\u5b50\u91cc\u53bb\uff0c\u800c\u4e14\u5df2\u541e \u4e0b\u53bb\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u5410\u51fa\u6765\u3002 \u3000\u3000\u7b49\u9ea6\u8001\u5e7f\u8bf4\u5b8c\uff0c\u4ed6\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u59d3\u4ec0\u4e48\uff1f\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u7537\u7684\u59d3\u9ad8\uff0c\u5973\u7684\u5a18\u5bb6\u597d\u8c61\u662f \u59d3\u7f57\u3002\u201d\u5939\u68cd\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u4ece\u5934\u5230\u5c3e\u90fd\u6ca1\u6709\u8bf4\u4e00\u4e2a\u5b57\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u201c\u5348\u65f6\u5230\u4e86\u6ca1\u6709\uff1f\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u521a \u8fc7\u5348\u65f6\u3002\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u62ff\u6765\u3002\u201d\u91d1\u72ee\u5b50\u8fdf\u7591\u7740\uff0c\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u65b9\u4fbf\u5417\uff1f\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u65b9 \u4fbf\u3002\u201d\u91d1\u72ee\u5b50\u597d\u8c61\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u952d\u7ea6\u6709\u4e8c\u5341\u4e24\u91cd\u7684\u91d1\u5b50\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8f7b\u8f7b\u5730\u63a8\u4e86 \u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6536\u4e0b\u91d1\u5b50\uff0c\u518d\u4e5f\u4e0d\u8bf4\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u91d1\u72ee\u5b50\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u671b\u7740\u7a97\u5916\u7684\u5929\u8272\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e00\u5929\u8fc7\u7684\u597d\u5feb\u3002\u201d\u53ef\u662f\u5728\u6709\u4e9b\u4eba\u770b \u6765\uff0c\u8fd9\u4e00\u5929\u5c31\u597d\u8c61\u6c38\u8fdc\u4e5f\u71ac\u4e0d\u8fc7\u53bb\u4f3c\u7684\u3002 \u3000\u3000\u5251\u548c\u68cd\u5b50\u4e00\u68cd\u5b50\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u559c\u6b22\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4f46\u68cd\u5b50\u5374\u5f88\u6709\u7528\u3002 \u3000\u3000\u68cd\u5b50\u4e5f\u6bd4\u5251\u52bf\u5229\uff0c\u4ed6\u4e00\u68cd\u6253\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u5f80\u5f80\u4f1a\u5148\u770b\u770b\u8981\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u5251\u82e5\u51fa\u9798\uff0c\u5c31\u53ea\u627e\u4eba\u81f4\u547d\u7684\u5f31\u70b9\u3002 \u3000\u3000\u5c24\u5176\u662f\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u62d4\u51fa\u6765\u7684\u65f6\u5019\u8981\u6709\u4ee3\u4ef7\uff0c\u63d2\u56de\u53bb\u7684\u65f6\u5019\u4e5f\u8981\u6709\u4ee3\u4ef7\u3002 \u3000\u3000\u62d4\u51fa\u6765\u7684\u4ee3\u4ef7\u662f\u94b1\uff0c\u63d2\u56de\u53bb\u7684\u4ee3\u4ef7\u662f\u8840\u3002 \u3000\u3000\u4e8c\u4e00\u4e2a\u591a\u65f6\u8fb0\u5df2\u8fc7\u53bb\u4e86\uff0c\u91d1\u72ee\u5b50\u548c\u9ed1\u8863\u4eba\u8fd8\u5750\u5728\u90a3\u91cc\uff0c\u90ed\u5927\u8def\u4ed6\u4eec\u4e5f\u8fd8\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u820d\u4e0d\u5f97\u8d70\uff0c\u4e5f\u4e0d\u80fd\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e5\u662f\u638f\u51fa\u90a3\u952d\u91d1\u5b50\u6765\u4ed8\u5e10\uff0c\u5c82\u975e\u7b49\u4e8e\u544a\u8bc9\u522b\u4eba\u81ea\u5df1\u5c31\u662f\u8d3c\u3002 \u3000\u3000\u5939\u68cd\u7ec8\u4e8e\u56de\u6765\u4e86\uff0c\u90ed\u5927\u8def\u8fd9\u624d\u770b\u6e05\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4ed6\u7684\u8138\u5c31\u597d\u8c61\u53ea\u6709\u76ae\u5305\u7740\u9aa8\u5934\uff0c\u65e2\u6ca1\u6709\u8868\u60c5\uff0c\u4e5f\u6ca1\u6709\u8089\u3002 \u3000\u3000\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u5939\u68cd\u9053\uff1a\u201c\u90a3\u4eba\u4e0d\u59d3\u9ad8\uff0c\u59d3\u5b8b\uff0c\u672c\u6765\u662f\u5f20\u5bb6\u53e3\u2018\u8fbd\u4e1c\u725b\u7f8a\u53f7\u2019 \u7684\u5e10\u623f\uff0c\u62d0\u4e86\u8001\u677f\u4e00\u7b14\u5e10\uff0c\u9003\u5230\u8fd9\u91cc\u6765\uff0c\u6240\u4ee5\u91d1\u5b50\u4e22\u4e86\u4e5f\u4e0d\u6562\u5f20\u626c\u3002\u201d\u91d1\u72ee\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u770b \u6765\u8fd9\u5012\u6b63\u662f\u4ed6\u5e38\u7528\u7684\u624b\u6bb5\uff0c\u5148\u6293\u4f4f\u522b\u4eba\u7684\u628a\u67c4\u518d\u4e0b\u624b\u3002\u201d\u5939\u68cd\u9053\uff1a\u201c\u800c\u4e14\u4f5c\u6848\u7684\u624b\u6cd5\u4e5f\u4e00 \u6837\uff0c\u505a\u7684\u53c8\u5e72\u51c0\u53c8\u6f02\u4eae\uff0c\u95e8\u7a97\u4e0d\u52a8\uff0c\u91d1\u5b50\u5df2\u4e22\u4e86\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u4e22\u7684\uff1f\u201d\u5939\u68cd \u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u4ed6\u53ea\u6709\u4e00\u51fa\u624b\uff0c\u81f3\u5c11\u5c31\u662f\u5341\u4e09\u4ef6\u5927\u6848\uff0c\u8fd9\u662f\u4ed6\u7684\u8001\u89c4 \u77e9\u3002\u201d\u5939\u68cd\u9053\uff1a\u201c\u9664\u4e86\u90a3\u59d3\u5b8b\u7684\u5916\uff0c\u6211\u53c8\u67e5\u51fa\u4e86\u4e94\u5bb6\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u8fd9\u4e94\u5bb6\u4eba\u8eab\u4e0a\u662f\u4e0d\u662f \u4e5f\u90fd\u80cc\u7740\u6848\u5b50\u7684\uff1f\u201d\u5939\u68cd\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5176\u4e2d\u5c45\u7136\u8fd8\u6709\u5bb6\u662f\u4ee5\u524d\u9646\u4e0a\u9f99\u738b\u8fd8\u672a\u6d17\u624b\u65f6\u7684\u5c0f\u5934 \u76ee\uff0c\u73b0\u5728\u5df2\u5a36\u4e86\u8001\u5a46\uff0c\u751f\u4e86\u5b69\u5b50\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u9047\u89c1\u4ed6\uff0c\u603b\u7b97\u4e5f\u5012\u4e86\u9709\uff0c\u5c31\u653e\u4ed6\u4eec\u4e00 \u9a6c\u5427\u3002\u201d\u5939\u68cd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u770b\u7740\u81ea\u5df1\u7684\u624b\u51b7\u7b11\u3002 \u3000\u3000\u91d1\u72ee\u5b50\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u80af\u677e\u4e00\u677e\u624b\u7684\uff0c\u53ea\u8981\u548c\u9646\u4e0a\u9f99\u738b\u6cbe\u7740\u8fb9\u7684 \u4eba\uff0c\u9047\u7740\u4f60\u5c31\u5012\u9709\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4f60\u4e5f\u5f97\u5c0f\u5fc3\u4e9b\uff0c\u771f\u8981\u9047\u7740\u9646\u4e0a\u9f99\u738b\u548c\u90a3\u6761\u6bd2\u86c7\uff0c\u90a3\u65f6\u5012\u9709\u7684\u53ef\u5c31\u662f\u4f60\u4e86\u3002\u201d\u5939\u68cd\u8fd8 \u662f\u5728\u51b7\u7b11\u7740\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u770b\u6765\u6211\u4eec\u5f97\u5230\u7684\u6d88\u606f\u5e76\u6ca1\u6709\u9519\uff0c\u8fd9\u4e9b\u5e74\u4ed6\u7684\u786e\u4e00\u76f4\u7a9d\u5728\u8fd9 \u91cc\u3002\u201d\u5939\u68cd\u9053\uff1a\u201c\u544a\u8bc9\u6211\u8fd9\u6d88\u606f\u7684\u4eba\u672c\u6765\u5c31\u4e0d\u4f1a\u9760\u4e0d\u4f4f\uff0c\u5426\u5219\u6211\u600e\u4f1a\u8981\u4f60\u4ed8\u4e00\u4e07\u4e24\uff1f\u201d\u91d1\u72ee \u5b50\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u65e2\u7136\u5df2\u5728\u8fd9\u91cc\u7a9d\u4e86\u4e03\u516b\u5e74\uff0c\u4e3a\u4ec0\u4e48\u5ffd\u7136\u53c8\u51fa\u4e86\u624b\u5462\uff1f\u201d\u5939\u68cd\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u624b \u75d2\u3002\u201d\u4ed6\u4eec\u8bf4\u8bdd\u5b8c\u5168\u4e0d\u6015\u88ab\u522b\u4eba\u542c\u89c1\uff0c\u90ed\u5927\u8def\u5f53\u7136\u6bcf\u53e5\u8bdd\u90fd\u4e0d\u4f1a\u4e0d\u542c\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6cd5\u5b50\u4e0d\u627f\u8ba4\u8fd9\u5939\u68cd\u679c\u7136\u6709\u4e24\u4e0b\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5634\u91cc\u8bf4\u7684\u201c\u4ed6\u201d\u53c8\u662f\u8c01\u5462\uff1f\u5939\u68cd\u5ffd\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u6628\u5929\u665a\u4e0a\u5728\u8fd9\u91cc\u505a\u4e86\u6848\uff0c \u5c31\u4e00\u5b9a\u8fd8\u7a9d\u5728\u8fd9\u57ce\u91cc\u3002\u4eca\u5929\u65e9\u4e0a\u51fa\u57ce\u7684\u4eba\u6211\u90fd\u89c1\u8fc7\uff0c\u51fa\u4e86\u4e00\u4f19\u5356\u827a\u7684\u7a0d\u5fae\u624e\u773c\u5916\uff0c\u522b\u7684\u5168\u662f \u89c4\u77e9\u4eba\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u4ed6\u4f1a\u4e0d\u4f1a\u5c06\u8d3c\u8d43\u53eb\u90a3\u4f19\u5356\u827a\u7684\u4eba\u5939\u5e26\u51fa\u57ce\uff1f\u201d\u5939\u68cd\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u811a \u5e95\u5e26\u8d77\u7684\u5c18\u571f\uff0c\u8eab\u4e0a\u5e26\u7684\u7edd\u4e0d\u8d85\u8fc7\u5341\u4e24\u94f6\u5b50\u3002\u201d\u91d1\u72ee\u5b50\u5634\u89d2\u5ffd\u7136\u9732\u51fa\u4e86\u4e00\u4e1d\u4e0d\u6000\u597d\u610f\u7684\u72de \u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u4ed6\u4e00\u5b9a\u8fd8\u5728\u57ce\u91cc\u4e86\u3002\u201d\u542c\u5230\u8fd9\u91cc\uff0c\u90ed\u5927\u8def\u771f\u5fcd\u4e0d\u4f4f\u60f3\u95ee\u4ed6\u4eec\uff1a\u201c\u4f60 \u600e\u4e48\u77e5\u9053\u4ed6\u6ca1\u6709\u4ece\u5c0f\u8def\u6e9c\u8d70\uff1f\u53c8\u600e\u4e48\u77e5\u9053\u4ed6\u73b0\u5728\u4e0d\u4f1a\u6e9c\u8d70\uff1f\u201d\u90ed\u5927\u8def\u5f53\u7136\u4e0d\u80fd\u95ee\u3002 \u3000\u3000\u5e78\u597d\u7528\u4e0d\u7740\u4ed6\u95ee\uff0c\u5939\u68cd\u81ea\u5df1\u5df2\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u4ed6\u8981\u4e00\u51fa\u624b\u81f3\u5c11\u5c31\u662f\u4e0a\u4e07\u4e24\u7684\u91d1\u5b50\uff0c\u6211\u5df2\u5728\u56db\u9762\u90fd\u5e03\u4e0b\u6697\u5361\uff0c\u65e0\u8bba\u8c01\u4e5f\u4f11\u60f3\u5e26\u7740\u4e0a\u4e07 \u4e24\u7684\u91d1\u5b50\u6e9c\u8d70\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u4e5f\u51b3\u4e0d\u80af\u628a\u5403\u4e0b\u53bb\u7684\u518d\u5410\u51fa\u6765\u3002\u8fd9\u4eba\u89c6\u94b1\u5982\u547d\uff0c\u6709\u540d \u7684\u8fde\u76ae\u5e26\u9aa8\u4e00\u53e3\u541e\uff0c\u541e\u4e0b\u53bb\u5c31\u6b7b\u4e5f\u5410\u4e0d\u51fa\u4e86\u3002\u201d\u5939\u68cd\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u7684\u8001\u6bdb\u75c5\uff0c\u6211\u65e9\u5c31\u77e5 \u9053\u8fd9\u6bdb\u75c5\u603b\u6709\u4e00\u5929\u4f1a\u8981\u4ed6\u7684\u540d\uff01\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u4f46\u8fd9\u4eba\u5b9e\u5728\u592a\u72e1\u733e\uff0c\u6613\u5bb9\u672f\u53c8\u7cbe\uff0c\u8fde\u8eab\u6750\u9ad8 \u77ee\u90fd\u80fd\u6539\u53d8\u3002\u201d\u4f46\u90ed\u5927\u8def\u8fd8\u662f\u7b11\u563b\u563b\u7684\u9762\u4e0d\u6539\u8272\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u4ec0\u4e48\u90fd\u4e0d\u5728\u4e4e\uff0c\u4f55\u51b5\u73b0\u5728\u809a\u5b50\u91cc\u53c8\u88c5\u6ee1\u4e86\u8a00\u8302\u6e90\u7684\u9648\u5e74\u7af9\u53f6\u9752\u3002 \u3000\u3000\u5939\u68cd\u8138\u4e0a\u4e5f\u8fde\u534a\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u773c\u775b\u4e00\u76f4\u76ef\u7740\u90ed\u5927\u8def\u7684\u773c\u775b\uff0c\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u7684 \u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8138\u8272\u53d1\u9752\uff0c\u773c\u775b\u9634\u68ee\u68ee\u7684\uff0c\u80c6\u5c0f\u7684\u4eba\u5728\u665a\u4e0a\u89c1\u7740\u4ed6\uff0c\u975e\u4f46\u5b9e\u8bdd\u8981\u88ab\u4ed6\u903c\u51fa\u6765\uff0c\u4e5f\u8bb8\u8fde \u5c41\u90fd\u8981\u88ab\u5413\u51fa\u6765\u3002\u201c\u8fd9\u4eba\u4e0d\u8be5\u53eb\u5939\u68cd\uff0c\u5e94\u8be5\u53eb\u50f5\u5c38\u624d\u5bf9\u3002\u201d\u8fd9\u53e5\u8bdd\u51e0\u4e4e\u5df2\u5230\u4e86\u90ed\u5927\u8def\u7684\u5634 \u8fb9\uff0c\u5dee\u70b9\u5c31\u51fa\u4e86\u53e3\u2014\u2014\u4f60\u5343\u4e07\u83ab\u8981\u4ee5\u4e3a\u4ed6\u4e0d\u6562\u8bf4\uff0c\u53ea\u6709\u9152\u4e00\u5230\u4e86\u4ed6\u809a\u5b50\u91cc\uff0c\u201c\u4e0d\u6562\u201d\u8fd9\u4e24\u4e2a \u5b57\u5c31\u65e9\u5df2\u79bb\u5f00\u4ed6\u5341\u4e07\u516b\u5343\u91cc\u4e86\u3002 \u3000\u3000\u738b\u52a8\u4ed6\u4eec\u5012\u4e5f\u65e0\u6240\u8c13\uff1a\u201c\u4f60\u53ea\u8981\u4ea4\u4e0a\u90ed\u5927\u8def\u8fd9\u670b\u53cb\uff0c\u5c31\u5f97\u968f\u65f6\u51c6\u5907\u4e3a\u4ed6\u6253\u67b6\u3002\u201d\u6253\u67b6\u5728 \u4ed6\u4eec\u8bf4\u6765\uff0c\u4e5f\u65e9\u5c31\u662f\u5bb6\u5e38\u4fbf\u996d\u3002 \u3000\u3000\u5c31\u8fde\u6797\u592a\u5e73\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u5939\u68cd\u7684\u773c\u775b\u867d\u6ca1\u6709\u77aa\u7740\u4ed6\uff0c\u4ed6\u7684\u773c\u775b\u5374\u5728\u72e0\u72e0\u7684\u77aa\u7740\u5939\u68cd\u3002 \u3000\u3000\u770b\u6837\u5b50\u65e0\u8bba\u662f\u90ed\u5927\u8def\u8bf4\u9519\u4e00\u53e5\u8bdd\u4e5f\u597d\uff0c\u662f\u5939\u68cd\u95ee\u9519\u4e00\u53e5\u8bdd\u4e5f\u597d\uff0c\u8fd9\u573a\u67b6\u968f\u65f6\u90fd\u4f1a\u6253\u8d77 \u6765\u3002 \u3000\u3000\u8c01\u77e5\u91d1\u72ee\u5b50\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u4eba\u7528\u4e0d\u7740\u95ee\u3002\u201d\u5939\u68cd\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u91d1\u72ee\u5b50\u7b11\u4e86\u7b11\uff0c \u9053\uff1a\u201c\u4ed6\u4eec\u809a\u5b50\u91cc\u82e5\u6709\u9b3c\uff0c\u600e\u4e48\u4f1a\u8c08\u8bba\u6211\u7684\u9f3b\u5b50\uff1f\u201d\u539f\u6765\u8fd9\u4eba\u4e0d\u4f46\u9f3b\u5b50\u7075\uff0c\u8033\u6735\u4e5f\u5f88\u5c16\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u5168\u542c\u5230\u4e86\uff1f\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u5e72\u6211\u4eec\u8fd9\u884c\u7684\uff0c\u4e0d\u4f46\u8981\u773c\u89c2\u56db\u8def\uff0c \u800c\u4e14\u8981\u8033\u542c\u516b\u65b9\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u4e0d\u751f\u6c14\uff1f\u201d\u91d1\u72ee\u5b50\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u751f\u6c14\uff1f\u9f3b\u5b50\u5927\u5c31\u7b97 \u5f88\u96be\u770b\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u4e22\u4eba\u3002\u201d\u90ed\u5927\u8def\u5bf9\u8fd9\u4eba\u7684\u5370\u8c61\u7acb\u5373\u597d\u8d77\u6765\u4e86\uff0c\u9053\uff1a\u201c\u975e\u4f46\u4e0d\u4e22\u4eba\uff0c\u4e5f\u4e0d \u96be\u770b\u3002\u7537\u4eba\u5c31\u8981\u9f3b\u5b50\u5927\uff0c\u8d8a\u5927\u8d8a\u597d\uff0c\u61c2\u4e8b\u7684\u5973\u4eba\u5c31\u559c\u6b22\u5927\u9f3b\u5b50\u7684\u7537\u4eba\u3002\u201d\u91d1\u72ee\u5b50\u5927\u7b11\u9053\uff1a \u201c\u4f60\u9f3b\u5b50\u4e5f\u4e0d\u5c0f\u3002\u201d\u90ed\u5927\u8def\u6478\u4e86\u6478\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u9a6c\u9a6c\u864e\u864e\uff0c\u8fd8\u8fc7\u5f97\u53bb\u3002\u201d\u91d1\u72ee\u5b50 \u9053\uff1a\u201c\u4f60\u4eec\u5c31\u4f4f\u5728\u8fd9\u57ce\u91cc\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0d\u5728\u57ce\u91cc\uff0c\u5728\u5c71\u4e0a\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u5c71\u4e0a\u4e5f\u4f4f\u7740 \u5f88\u591a\u4eba\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6d3b\u4eba\u5c31\u53ea\u6709\u6211\u4eec\u56db\u4e2a\uff0c\u6b7b\u4eba\u5374\u5012\u6709\u4e0d\u5c11\u3002\u201d\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u6b7b\u4eba\uff1f\u201d \u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4eec\u4f4f\u7684\u5730\u65b9\u5c31\u5728\u575f\u573a\u65c1\u8fb9\uff0c\u53eb\u5bcc\u8d35\u5c71\u5e84\uff0c\u6709\u7a7a\u4e0d\u59a8\u8fc7\u6765\u559d\u4e24\u676f\u3002\u201d\u91d1\u72ee\u5b50 \u9053\uff1a\u201c\u4e00\u5b9a\u53bb\u62dc\u8bbf\u3002\u201d\u4ed6\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u638c\u67dc\u7684\uff0c\u7b97\u5e10\uff0c\u8fd9\u51e0\u4f4d\u7684\u5e10\u6211\u4eec\u4e00\u9f50\u4ed8 \u4e86\u3002\u201d\u90ed\u5927\u8def\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff0c\u6211\u4eec\u662f\u5730\u4e3b\uff0c\u4f60\u4e00\u5b9a\u8981\u8ba9\u6211\u4eec\u5c3d\u4e00\u5c3d\u5730\u4e3b\u4e4b \u8c0a\u3002\u201d\u4ed6\u4e0d\u4f46\u559c\u6b22\u4ea4\u670b\u53cb\uff0c\u66f4\u559c\u6b22\u8bf7\u5ba2\u3002 \u3000\u3000\u670b\u53cb\u8c01\u90fd\u6ca1\u6709\u4ed6\u4ea4\u5f97\u5feb\uff0c\u5e10\u4e5f\u8c01\u90fd\u6ca1\u6709\u4ed6\u4ed8\u5f97\u5feb\u3002\u53ef\u662f\u8fd9\u6b21\u4ed6\u7684\u624b\u4f38\u8fdb\u53e3\u888b\uff0c\u5374\u638f\u4e0d\u51fa \u6765\u4e86\u3002 \u3000\u3000\u4ed6\u603b\u4e0d\u80fd\u5f53\u7740\u4eba\u5bb6\u628a\u90a3\u952d\u91d1\u5b50\u638f\u51fa\u6765\u3002 \u3000\u3000\u8c01\u77e5\u91d1\u72ee\u5b50\u4e5f\u5e76\u4e0d\u518d\u62a2\u7740\u4ed8\u5e10\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\uff0c\u591a\u8c22\u591a\u8c22\u3002\u201d \u5939\u68cd\u5ffd\u7136\u62cd\u4e86\u62cd\u90ed\u5927\u8def\u7684\u80a9\u5934\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u57ce\u91cc\u4e00\u5b9a\u5f88\u4e71\uff0c\u6ca1\u4e8b\u8fd8\u662f\u803d\u5728\u5bb6\u91cc\u7684\u597d\uff0c \u514d\u5f97\u51fa\u6765\u60f9\u9ebb\u70e6\u3002\u201d\u4ed6\u4e0d\u8ba9\u90ed\u5927\u8def\u8bf4\u8bdd\uff0c\u624b\u7528\u529b\u5728\u80a9\u4e0a\u4e00\u6309\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u52b3\u76f8\u9001\uff0c\u8bf7\u5750\u3002\u201d \u90ed\u5927\u8def\u7b11\u563b\u563b\u9053\uff1a\u201c\u6211\u5750\u7d2f\u4e86\uff0c\u5c31\u60f3\u7ad9\u7ad9\u3002\u201d\u5939\u68cd\u7528\u4e86\u516b\u6210\u529b\uff0c\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\uff0c\u4e0a\u4e0a\u4e0b \u4e0b\u77a7\u4e86\u90ed\u5927\u8def\u51e0\u773c\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7a81\u542c\u91d1\u72ee\u5b50\u9053\uff1a\u201c\u5bf9\u9762\u90a3\u4eba\u5404\u4f4d\u53ef\u8ba4\u5f97\u4e48\uff1f\u201d\u4e00\u4e2a\u8eab\u5f71\u53e5\u507b\uff0c\u767d\u53d1\u82cd\u82cd\u7684\u8001\u5934\u5b50\u624b\u91cc\u63d0 \u7740\u6876\u810f\u6c34\uff0c\u6b63\u4ece\u5bf9\u9762\u7684\u95e8\u91cc\u8d70\u51fa\u6765\uff0c\u201c\u54d7\u5566\u5566\u201d\u5c06\u4e00\u6876\u6c34\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u5f53\u7136\u8ba4\u5f97\uff0c\u4ed6\u5c31\u662f\u5229\u6e90\u5f53\u94fa\u7684\u8001\u671d\u5949\uff0c\u6211\u4eec\u90fd\u53eb\u4ed6\u6d3b\u5265\u76ae\u3002\u201d\u91d1\u72ee\u5b50\u76ee \u5149\u707c\u707c\uff0c\u4e0d\u4f4f\u76ef\u7740\u90a3\u8001\u4eba\uff0c\u76f4\u5230\u8001\u4eba\u53c8\u8f6c\u8eab\u8d70\u4e86\u8fdb\u53bb\uff0c\u4ed6\u624d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5404\u4f4d\u6709\u9050\uff0c\u6211\u4eec \u5148\u544a\u8f9e\u4e86\u3002\u201d\u4ed6\u8d76\u4e0a\u5939\u68cd\uff0c\u4e24\u4eba\u8f7b\u8f7b\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u4e00\u9f50\u5f80\u5f53\u94fa\u90a3\u8fb9\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fd9\u65f6\u624d\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u7684\u8d70\u8fc7\u90ed\u5927\u8def\u4ed6\u4eec\u9762\u524d\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u4f4e\u7740\u5934\u559d\u9152\uff0c\u8c01\u4e5f\u6ca1\u6709\u77a7\u4ed6\u3002\u56e0\u4e3a\u6bcf\u6b21\u770b\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u90fd\u597d\u8c61\u770b\u5230\u6761\u6bd2\u86c7\u4e00\u6837\uff0c \u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u4e0d\u8212\u670d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u811a\u6b65\u5e76\u6ca1\u6709\u505c\uff0c\u5374\u5ffd\u7136\u5524\u9053\uff1a\u201c\u9ec4\u7389\u548c\uff0c\u4f60\u597d\u3002\u201d\u5927\u5bb6\u90fd\u5f81\u4e86\u5f81\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6 \u5728\u8ddf\u4ec0\u4e48\u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u8fd9\u65f6\u9ed1\u8863\u4eba\u5374\u5df2\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u6447\u4e86\u6447\u5934\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u4eba\u83ab\u975e\u6709\u6bdb\u75c5\uff1f\u201d\u6797\u592a\u5e73\u53c8\u5728\u76ef\u7740\u9ed1\u8863\u4eba\u80cc\u540e\u7684\u957f\u5251\uff0c \u9053\uff1a\u201c\u8fd9\u67c4\u5251\u81f3\u5c11\u6709\u56db\u5c3a\u4e03\u5bf8\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u773c\u529b\u4e0d\u9519\uff0c\u60f3\u5fc5\u4e5f\u662f\u4f7f\u5251\u7684\uff1f\u201d\u6797\u592a\u5e73\u597d\u8c61 \u6ca1\u542c\u89c1\u8fd9\u53e5\u8bdd\uff0c\u53c8\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u6b66\u6797\u4e2d\u80fd\u4f7f\u8fd9\u6837\u957f\u5251\u7684\u53ea\u6709\u4e09\u4e2a\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u54e6\uff0c\u54ea\u4e09\u4e2a\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4e00\u4e2a\u53eb\u4e01\u9038\u90ce\uff0c\u636e\u8bf4\u662f\u6276\u6851\u6d6a\u4eba\u2018\u8d64\u6728\u4e09\u592a\u90ce\u2019\u548c\u9ec4\u5c71\u5973\u5251 \u5ba2\u4e01\u4e3d\u7684\u79c1\u751f\u5b50\uff1b\u8d64\u6728\u4e09\u592a\u90ce\u662f\u6276\u6851\u2018\u62ab\u98ce\u4e00\u5200\u6d41\u2019\u7684\u5251\u5ba2\uff0c\u6240\u4ee5\u4e01\u9038\u90ce\u7684\u5251\u6cd5\uff0c\u4e5f\u878d\u5408\u4e86 \u6276\u6851\u548c\u9ec4\u5c71\u4e24\u79cd\u5251\u6cd5\u4e4b\u957f\u5904\u3002\u201d\u71d5\u4e03\u51dd\u89c6\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u77e5\u9053\u7684\u6b66\u6797\u79d8\u4e8b\u6bd4\u6211\u8fd8 \u591a\u3002\u201d\u6797\u592a\u5e73\u8fdf\u7591\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u662f\u542c\u522b\u4eba\u8bf4\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u4e2a\u5462\uff1f\u201d\u6797\u592a \u5e73\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u662f\u5bab\u957f\u8679\u5251\u6cd5\u552f\u4e00\u7684\u4f20\u4eba\uff0c\u53eb\u5bab\u7ea2\u7c89\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5bab\u7ea2\u7c89\uff1f\u8fd9\u7b80\u76f4\u662f\u4e2a \u5973\u4eba\u7684\u540d\u5b57\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5979\u672c\u6765\u5c31\u662f\u5973\u4eba\uff0c\u4f60\u96be\u9053\u8ba4\u4e3a\u5973\u4eba\u5c31\u4e0d\u80fd\u7528\u8fd9\u4e48\u957f\u7684\u5251\uff1f\u201d\u90ed\u5927 \u8def\u7b11\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u90a3\u9ed1\u8863\u4eba\u7edd\u4e0d\u53ef\u80fd\u662f\u5973\u4eba\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u542c\u8bf4\u4e01\u9038\u90ce\u6700\u8fd1\u5df2\u8fdc\u6e21\u6276 \u6851\uff0c\u53bb\u627e\u4ed6\u4eb2\u751f\u7684\u7236\u4eb2\u53bb\u4e86\uff0c\u6240\u4ee5\uff0c\u8fd9\u9ed1\u8863\u4eba\u4e5f\u7edd\u4e0d\u53ef\u80fd\u662f\u4ed6\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u7b2c\u4e09\u4e2a \u5462\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u4eba\u53eb\u2018\u5251\u5e95\u6e38\u9b42\u2019\u5357\u5bab\u4e11\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5251\u5e95\u6e38\u9b42\uff1f\u8fd9\u5c82\u975e\u4e00\u53e5\u9a82 \u4eba\u7684\u8bdd\uff0c\u4ed6\u600e\u4e48\u4f1a\u53d6\u4e86\u4e2a\u8fd9\u4e48\u6837\u7684\u540d\u5b57\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5f88\u591a\u5e74\u524d\uff0c\u6c5f\u6e56\u4e2d\u51fa\u4e86\u4e2a\u602a\u4eba\uff0c\u53eb \u2018\u75af\u72c2\u5341\u5b57\u5251\u2019\uff0c\u9047\u7740\u4ed6\u7684\u4eba\u6ca1\u6709\u4e00\u4e2a\u80fd\u9003\u5f97\u8fc7\u4ed6\u7684\u5251\u4e0b\uff0c\u5c31\u8fde\u5f53\u65f6\u5f88\u8d1f\u76db\u540d\u7684\u2018\u897f\u5c71\u4e09 \u53cb\u2019\u548c\u2018\u6c5f\u5357\u7b2c\u4e00\u5251\u2019\u90fd\u88ab\u4ed6\u6740\u4e86\uff0c\u53ea\u6709\u5357\u5bab\u4e11\uff0c\u5c45\u7136\u4ece\u4ed6\u5251\u4e0b\u9003\u4e86\u51fa\u6765\uff0c\u6240\u4ee5\u5357\u5bab\u4e11\u81ea\u5df1 \u4e5f\u89c9\u5f97\u5f88\u5f97\u610f\uff0c\u5c31\u66ff\u81ea\u5df1\u53d6\u4e86\u4e2a\u5916\u53f7\u53eb\u5251\u5e95\u6e38\u9b42\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8d25\u5728\u4eba\u5bb6\u5251\u4e0b\u5c45\u7136\u8fd8\u5f97 \u610f\uff0c\u8fd9\u4eba\u5012\u6709\u8da3\u5f97\u5f88\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u4eba\u975e\u4f46\u65e0\u8da3\uff0c\u800c\u4e14\u65e0\u8da3\u6781\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4eba\u6700\u559c\u6b22\u6740\u4eba\uff0c\u6709\u65f6\u56fa\u7136\u662f\u4e3a\u4e86\u4ed6\u81ea\u5df1\u9ad8\u5174\u800c\u6740\u4eba\uff0c\u6709\u65f6\u4e5f\u4f1a\u4e3a \u4e86\u94b1\u800c\u6740\u4eba\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u867d\u7136\u4fa5\u5e78\u81ea\u5341\u5b57\u5251\u4e0b\u9003\u4e86\u6027\u547d\uff0c\u4f46\u8138\u4e0a\u8fd8\u662f\u88ab\u5212\u4e86\u4e2a\u5927\u5341\u5b57\uff0c\u6240\u4ee5\u4ece\u6765\u4e0d\u613f\u610f\u771f \u9762\u76ee\u89c1\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u8fd9\u9ed1\u8863\u4eba\u4e00\u5b9a\u5c31\u662f\u4ed6\u4e86\u3002\u201d\u738b\u52a8\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u5012\u4e5f \u672a\u5fc5\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u672a\u5fc5\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u77e5\u9053\u4ed6\u4e0d\u662f\u4e2a\u5973\u4eba\uff0c\u4e0d\u662f\u5bab\u7ea2\u7c89\uff1f\u201d\u90ed \u5927\u8def\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\u662f\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u770b\u5230\u4ed6\u7684\u8138\uff0c\u770b\u8fc7\u4ed6\u7684\u624b\uff1f\u770b\u8fc7\u4ed6\u7684 \u811a\uff1f\u2026\u2026\u4ed6\u8fde\u4e00\u5bf8\u5730\u65b9\u90fd\u6ca1\u6709\u8ba9\u4f60\u770b\u5230\uff0c\u4f60\u80fd\u770b\u5230\u7684\u53ea\u4e0d\u8fc7\u4ed6\u90a3\u8eab\u9ed1\u8863\u670d\u800c\u5df2\uff0c\u7537\u4eba\u53ef\u4ee5\u7a7f \u8fd9\u6837\u7684\u8863\u670d\uff0c\u5973\u4eba\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u53ef\u4ee5\uff1f\u201d\u90ed\u5927\u8def\u6014\u4f4f\u4e86\uff0c\u5f81\u4e86\u534a\u664c\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u5973\u4eba\uff0c \u90a3\u5012\u6709\u8da3\u5f97\u5f88\uff0c\u6211\u5012\u771f\u60f3\u770b\u770b\u5979\u957f\u7684\u662f\u4ec0\u4e48\u6837\u5b50\u3002\u201d\u71d5\u4e03\u60a0\u60a0\u9053\uff1a\u201c\u53ea\u8981\u662f\u5973\u4eba\uff0c\u4f60\u5c31\u89c9\u5f97 \u6709\u8da3\u4e48\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u5927\u591a\u6570\u5973\u4eba\u7684\u786e\u90fd\u6bd4\u7537\u4eba\u6709\u8da3\u4e9b\uff0c\u592a\u4e11\u592a\u8001\u7684\u81ea\u7136\u662f\u4f8b\u5916\u3002\u201d\u71d5 \u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u5c45\u7136\u8fd8\u6562\u8bf4\u4ed6\u4e0d\u662f\u8272\u9b3c\uff0c\u4ed6\u4e0d\u662f\u8c01\u662f\uff1f\u201d\u738b\u52a8\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u9053\uff1a \u201c\u6211\u81f3\u5c11\u4e5f\u6709\u4e00\u70b9\u662f\u548c\u8272\u9b3c\u76f8\u540c\u7684\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u968f\u65f6\u968f\u5730\u6211\u90fd\u4f1a\u60f3 \u5230\u5e8a\u3002\u201d\u5e8a\u3002 \u3000\u3000\u4e94\u7bb1\u91d1\u73e0\u5c31\u5728\u5e8a\u5e95\u4e0b\u3002 \u3000\u3000\u7eb5\u7136\u662f\u5929\u4e0b\u6700\u8c6a\u5bcc\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u5c06\u8fd9\u4e94\u53e3\u4ef7\u503c\u4ebf\u4e07\u7684\u7bb1\u5b50\u968f\u968f\u4fbf\u4fbf\u5f80\u5e8a\u4e0b\u4e00\u585e\uff0c\u8fde\u95e8\u90fd \u4e0d\u9501\u5c31\u8dd1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u786c\u662f\u8fd9\u4e48\u6837\u505a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u9664\u4e86\u4ed6\u4eec\u81ea\u5df1\u4e4b\u5916\uff0c\u522b\u4eba\u8fde\u505a\u68a6\u90fd\u4e0d\u4f1a\u60f3\u5230\u8fd9\u5f20\u7834\u5e8a\u5e95\u4e0b\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u5b9d\u85cf\uff0c\u800c\u4e14 \u8fd9\u5c4b\u5b50\u91cc\u6839\u672c\u7a7a\u7a7a\u5982\u4e5f\uff0c\u9664\u4e86\u5e8a\u5e95\u4e0b\u5916\uff0c\u4e5f\u6ca1\u6709\u80fd\u653e\u5f97\u4e0b\u8fd9\u4e94\u53e3\u7bb1\u5b50\u7684\u5730\u65b9\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u4e70\u5728\u5730\u4e0b\uff1f\u201d\u71d5\u4e03\u4e5f\u66fe\u7ecf\u8fd9\u4e48\u6837\u63d0\u8bae\u8fc7\uff0c\u4f46\u738b\u52a8\u7b2c\u4e00\u4e2a\u5c31\u575a\u51b3\u53cd\u5bf9\u3002 \u3000\u3000\u201c\u73b0\u5728\u6211\u4eec\u82e5\u8f9b\u8f9b\u82e6\u82e6\u5f97\u57cb\u4e0b\u53bb\uff0c\u8fc7\u4e0d\u4e86\u4e24\u5929\u53c8\u5f97\u8f9b\u8f9b\u82e6\u82e6\u7684\u6316\u51fa\u6765\uff0c\u65e2\u7136\u603b\u7684\u8981\u6316\u51fa \u6765\uff0c\u73b0\u5728\u53c8\u4f55\u5fc5\u57cb\u4e0b\u53bb\uff1f\u201d\u61d2\u4eba\u6c38\u8fdc\u6709\u5f88\u5145\u5206\u7684\u7406\u7531\u62d2\u7edd\u505a\u4e8b\u5f97\u3002 \u3000\u3000\u738b\u52a8\u7684\u7406\u7531\u5f53\u7136\u6700\u5145\u8db3\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5f53\u7136\u5df2\u7ecf\u53c8\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b63\u5728\u82e6\u7ec3\u5012\u540a\u7740\u559d\u9152\uff0c\u4ed6\u542c\u8bf4\u559d\u9152\u6709\u56da\u996e\uff0c\u751a\u81f3\u8fd8\u6709\u5c38\u996e\uff0c\u6240\u4ee5\u5df2\u51b3\u5fc3\u8981\u628a\u8fd9\u540a \u996e\u7ec3\u6210\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u82e5\u662f\u6709\u4eba\u80fd\u7528\u773c\u775b\u559d\u9152\uff0c\u5c31\u7b97\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4e5f\u51b3\u4e0d\u4f1a\u670d\u8f93\u5f97\uff0c\u597d\u6b79\u4e5f\u8981\u7ec3\u5f97\u548c \u90a3\u4eba\u4e00\u6837\u624d\u505c\u6b62\u3002 \u3000\u3000\u6797\u592a\u5e73\u5750\u5728\u95e8\u53e3\u7684\u77f3\u9636\u4e0a\uff0c\u7528\u624b\u62b1\u7740\u5934\uff0c\u4e5f\u4e0d\u77e5\u662f\u5728\u53d1\u6014\uff1f\u8fd8\u662f\u5728\u60f3\u5fc3\u4e8b\uff1f\u4ed6\u5e74\u7eaa\u770b\u6765 \u6bd4\u8c01\u90fd\u8f7b\uff0c\u4f46\u5fc3\u4e8b\u5374\u6bd4\u8c01\u90fd\u91cd\u3002 \u3000\u3000\u71d5\u4e03\u53c8\u4e0d\u77e5\u6e9c\u54ea\u91cc\u53bb\u4e86\u3002\u8fd9\u4eba\u7684\u884c\u52a8\u597d\u8c61\u603b\u662f\u6709\u70b9\u795e\u79d8\u516e\u516e\uff0c\u5e38\u5e38\u4f1a\u4e00\u4e2a\u6e9c\u51fa\u53bb\u8eb2\u8d77 \u6765\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u53bb\u5e72\u4ec0\u4e48\u3002 \u3000\u3000\u591c\u4f3c\u5df2\u5f88\u6df1\uff0c\u53c8\u4f3c\u4e4e\u8fd8\u5f88\u65e9\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u201c\u65f6\u95f4\u662f\u4e07\u7269\u7684\u4e3b\u5bb0\uff0c\u53ea\u6709\u65f6\u95f4\u624d\u662f\u6c38\u6052\u7684\u3002\u201d\u8fd9\u53e5\u8bdd\u5728\u8fd9\u91cc\u5374\u597d\u8c61\u5e76\u4e0d\u5341\u5206 \u6b63\u786e\u3002 \u3000\u3000\u5728\u8fd9\u91cc\u7684\u4eba\u867d\u7136\u4e0d\u4f1a\u5229\u7528\u65f6\u95f4\uff0c\u5374\u4e5f\u51b3\u4e0d\u505a\u65f6\u95f4\u7684\u5974\u96b6\u3002 \u3000\u3000\u90ed\u5927\u8def\u559d\u5b8c\u4e86\u7b2c\u4e09\u7897\u9152\u7684\u65f6\u5019\uff0c\u6797\u592a\u5e73\u7a81\u7136\u4ece\u77f3\u9636\u4e0a\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8868\u60c5\u5f88\u5174\u594b\uff0c\u4e5f\u5f88\u4e25\u8083\uff0c\u5c31\u597d\u8c61\u51b3\u80dc\u5343\u91cc\u7684\u5927\u5c06\u8981\u5bf9\u4ed6\u7684\u5c5e\u4e0b\uff0c\u5ba3\u5e03\u4e00\u9879\u6781\u91cd\u8981\u7684 \u6218\u7b56\u65f6\u7684\u8868\u60c5\u4e00\u6837\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u65e0\u8bba\u8868\u60c5\u591a\u4e25\u8083\u7684\u4eba\uff0c\u5047\u5982\u4f60\u5012\u7740\u53bb\u770b\uff0c\u4ed6\u90a3\u6837\u5b50\u4e5f\u4f1a\u53d8\u5f97\u5f88\u6ed1\u7a3d\u7684\uff0c\u90ed\u5927\u8def\u521a \u559d\u4e0b\u53bb\u7684\u4e00\u53e3\u9152\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u6709\u8bdd\u8981\u8bf4\u3002\u201d\u90ed\u5927\u8def\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u6765\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u57ce\u91cc \u6709\u4e2a\u4eba\uff0c\u4e0d\u4f46\u6b66\u529f\u5f88\u9ad8\uff0c\u800c\u4e14\u8fd8\u4f1a\u6613\u5bb9\u672f\u3001\u7f29\u9aa8\u6cd5\uff0c\u66fe\u7ecf\u505a\u8fc7\u5f88\u591a\u5b97\u4ee4\u5b98\u5e9c\u5934\u75bc\u7684\u6848\u5b50\u3002\u201d \u90ed\u5927\u8def\u7728\u7728\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u597d\u8c61\u5e76\u4e0d\u53ea\u4f60\u4e00\u4e2a\u4eba\u77e5\u9053\uff0c\u6211\u597d\u8c61\u4e5f\u542c\u8bf4\u8fc7\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a \u201c\u4e0d\u4f46\u4f60\u77e5\u9053\uff0c\u9178\u6885\u6c64\u4e5f\u77e5\u9053\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u54e6\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5979\u4e0d\u4f46\u77e5\u9053\uff0c\u800c\u4e14\u8fd8\u4e00 \u5b9a\u8ddf\u8fd9\u4e2a\u4eba\u6709\u4ec7\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\u4ec7\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4e0d\u8fc7\u5979\u4e5f\u8ddf\u6211\u4eec\u4e00\u6837\uff0c\u53ea\u77e5\u9053\u8fd9\u4e2a \u4eba\u85cf\u5728\u57ce\u91cc\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u85cf\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u7528\u4ec0\u4e48\u8eab\u4efd\u4f5c\u63a9\u62a4\uff1f\u5979\u867d\u7136\u60f3\u627e\u4ed6\u62a5\u4ec7\uff0c\u5374\u627e\u4e0d \u7740\uff0c\u6240\u4ee5\u2026\u2026\u201d\u90ed\u5927\u8def\u5ffd\u7136\u89c9\u5f97\u4ed6\u4e0d\u8c61\u521a\u624d\u90a3\u4e48\u53ef\u7231\u4e86\uff0c\u4e00\u4e2a\u8ddf\u6597\u7ffb\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u600e\u4e48 \u6837\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6240\u4ee5\u5979\u5c31\u60f3\u6cd5\u5b50\u8981\u522b\u4eba\u4ee3\u5979\u628a\u8fd9\u4eba\u627e\u51fa\u6765\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5979\u5f53\u7136\u77e5\u9053 \u5929\u4e0b\u6700\u4f1a\u627e\u4eba\u7684\u5c31\u662f\u68cd\u5b50\u548c\u91d1\u6bdb\u72ee\u5b50\u72d7\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5979\u8fd8\u77e5\u9053\u4ed6\u4eec\u90fd\u5df2\u5230\u4e86\u9644\u8fd1\uff0c\u6240\u4ee5 \u5c31\u5148\u60f3\u6cd5\u5b50\u53bb\u901a\u98ce\u62a5\u4fe1\uff0c\u8ba9\u4ed6\u4eec\u77e5\u9053\uff1a\u8fd9\u4e3a\u540d\u8d3c\u5c31\u85cf\u5728\u57ce\u91cc\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u7136\u540e\u5979\u81ea\u5df1\u518d \u5230\u8fd9\u57ce\u91cc\u6765\uff0c\u4e00\u591c\u95f4\u505a\u4e0b\u5341\u4e03\u516b\u4ef6\u65e0\u5934\u6848\uff0c\u800c\u4e14\u8fd8\u6545\u610f\u6a21\u4eff\u90a3\u540d\u8d3c\u4f5c\u6848\u7684\u624b\u6cd5\uff0c\u8ba9\u68cd\u5b50\u548c\u91d1 \u6bdb\u72ee\u5b50\u72d7\u8ba4\u5b9a\u8fd9\u4e9b\u6848\u5b50\u90fd\u662f\u4ed6\u505a\u7684\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u662f\u6700\u91cd\u8981\u7684\u4e00\u70b9\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u6700\u91cd\u8981\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5979\u8fd9\u4e48\u6837\u4e00\u505a\uff0c\u68cd\u5b50\u548c\u91d1\u6bdb\u72ee\u5b50\u72d7\u624d\u80fd\u786e\u5b9a\u8fd9\u4f4d\u540d\u8d3c\u7684 \u786e\u662f\u5728\u57ce\u91cc\uff0c\u624d\u4f1a\u8ba4\u771f\u627e\u3002 \u3000\u3000\u8c61\u4ed6\u4eec\u8fd9\u79cd\u8eab\u4efd\u7684\u4eba\uff0c\u81ea\u7136\u51b3\u4e0d\u4f1a\u4e3a\u4e86\u4e00\u70b9\u6355\u98ce\u6349\u5f71\u7684\u6d88\u606f\u5c31\u5356\u529b\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u4f46\u5979\u8fd8\u6709\u4e2a\u95ee\u9898\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5979\u7684\u95ee\u9898\u5c31\u662f\u5f97\u624b\u7684\u8d43\u7269\u4e00\u65f6\u65e2\u4e0d\u80fd\u8131\u624b\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u8fd0 \u51fa\u53bb\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u68cd\u5b50\u548c\u72ee\u5b50\u72d7\u5df2\u7ecf\u6765\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u79cd\u53c8\u60f9\u773c\u3001\u53c8\u70eb\u624b\u7684\u4e1c \u897f\uff0c\u5c31\u7b97\u8981\u85cf\u8d77\u6765\u90fd\u4e0d\u5bb9\u6613\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u975e\u4f46\u4e0d\u5bb9\u6613\uff0c\u800c\u4e14\u8fd8\u7684\u9887\u8d39\u529f\u592b\uff0c\u6240\u4ee5\u2026\u2026\u201d \u90ed\u5927\u8def\u82e6\u7b11\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5979\u5c31\u8981\u627e\u4e2a\u4eba\u4ee3\u5979\u85cf\u8fd9\u4e9b\u4e1c\u897f\uff0c\u53ef\u662f\u5979\u4e3a\u4ec0\u4e48\u8c01\u90fd\u4e0d\u53bb\u627e\uff0c\u504f\u504f\u627e \u4e0a\u4e86\u6211\u5462\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5979\u5f53\u7136\u77e5\u9053\u4f60\u5c31\u4f4f\u5728\u8fd9\u91cc\uff0c\u4e5f\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\u8fde\u9b3c\u90fd\u4e0d\u60f3\u6765\u7684\uff0c\u628a \u8d3c\u8d43\u85cf\u5728\u8fd9\u91cc\uff0c\u5c31\u597d\u8c61\u2026\u2026\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5c31\u597d\u8c61\u628a\u9152\u85cf\u5728\u809a\u5b50\u91cc\u4e00\u6837\u7684\u5b89\u5168\u53ef\u9760\u3002\u201d\u738b\u52a8 \u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u662f\u6700\u91cd\u8981\u7684\u539f\u56e0\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u54e6\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u5979\u627e\u6765 \u505a\u8fd9\u79cd\u4e8b\u7684\u4eba\uff0c\u4e00\u5b9a\u8981\u662f\u4e2a\u505a\u4e8b\u9a6c\u9a6c\u864e\u864e\uff0c\u770b\u5230\u963f\u732b\u963f\u72d7\u90fd\u4f1a\u53bb\u4ea4\u670b\u53cb\u7684\u7cca\u6d82\u866b\u3002\u201d\u738b\u52a8\u975e \u4f46\u4e0d\u52a8\uff0c\u4e5f\u5f88\u5c11\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u8bdd\u5f80\u5f80\u5c31\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u4e0b\u7ed3\u8bba\u7684\u4eba\u5374\u4e0d\u662f\u4ed6\uff0c\u662f\u90ed\u5927\u8def\u81ea\u5df1\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u5230\u963f\u732b\u963f\u72d7\u90fd\u53bb\u4ea4\u670b\u53cb\u5012\u6ca1\u5173\u7cfb\uff0c\u4e00\u770b\u5230\u6f02\u4eae\u7684\u5973\u4eba\u5c31 \u8d70\u4e0d\u52a8\u4e86\u7684\u4eba\u624d\u771f\u7684\u6df7\u5e10\u52a0\u516b\u7ea7\u3002\u201d\u6797\u592a\u5e73\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d\u90ed\u5927\u8def\u6307\u7740\u81ea \u5df1\u7684\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u7684\u5c31\u662f\u6211\u3002\u201d\u5176\u5b9e\u90ed\u5927\u8def\u5012\u4e5f\u4e0d\u662f\u771f\u7684\u7cca\u6d82\uff0c\u53ea\u4e0d\u8fc7\u5f88\u591a\u4e8b\u4ed6\u6839\u672c\u61d2 \u5f97\u8ba4\u771f\u53bb\u60f3\uff0c\u53ea\u8981\u4ed6\u53bb\u60f3\uff0c\u4ed6\u6bd4\u8c01\u90fd\u660e\u767d\u3002 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u8fd8\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u90ed\u5148\u751f\u505a\u9519\u4e8b\u4e0d\u7a00\u5947\uff0c\u505a\u5bf9\u4e86 \u624d\u662f\u5947\u95fb\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u8be5\u7528\u90a3\u952d\u91d1\u5b50\u53bb\u4ed8\u5e10\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4e0d\u7528\u90a3\u952d\u91d1\u5b50 \u4ed8\u5e10\uff0c\u96be\u9053\u7528\u6211\u81ea\u5df1\u7684\u624b\u6307\u5934\u53bb\u4ed8\uff1f\u83ab\u5fd8\u4e86\u4f60\u521a\u624d\u559d\u7684\u4e5f\u5e76\u4e0d\u6bd4\u6211\u5c11\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u68cd\u5b50 \u548c\u91d1\u6bdb\u72ee\u5b50\u72d7\u82e5\u77e5\u9053\u6211\u4eec\u4f7f\u7528\u91d1\u5b50\u4ed8\u7684\u5e10\uff0c\u4e00\u5b9a\u4f1a\u5947\u602a\u8fd9\u4e9b\u7a77\u9b3c\u7684\u91d1\u5b50\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u90a3\u65f6 \u6211\u4eec\u7684\u9ebb\u70e6\u4e5f\u5c31\u6765\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4e5f\u544a\u8bc9\u4f60\u51e0\u4ef6\u4e8b\u597d\u4e0d\u597d\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u597d\u3002\u201d\u90ed \u5927\u8def\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u68cd\u5b50\u548c\u72ee\u5b50\u72d7\u6839\u672c\u5c31\u4e0d\u4f1a\u77e5\u9053\uff0c\u56e0\u4e3a\u9ea6\u8001\u5e7f\u7edd\u4e0d\u662f\u4e2a\u591a\u5634\u7684\u4eba\u3002\u201d\u6797\u592a\u5e73 \u9053\uff1a\u201c\u6709\u4e86\u7b2c\u4e00\uff0c\u5f53\u7136\u8fd8\u6709\u7b2c\u4e8c\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u7b2c\u4e8c\uff0c\u90ed\u5148\u751f\u8eab\u4e0a\u6709\u51e0\u952d\u91d1\u5b50\uff0c\u4e5f\u5e76\u4e0d\u662f \u7a7a\u524d\u7edd\u540e\u7684\u4e8b\uff0c\u5e76\u4e0d\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u3002\u4f55\u51b5\uff0c\u90a3\u952d\u91d1\u5b50\u4e0a\u8fde\u4e00\u70b9\u6807\u8bb0\u90fd\u6ca1\u6709\uff0c\u6211\u65e9\u5c31\u68c0\u67e5\u8fc7 \u4e86\uff0c\u8c01\u6562\u8bf4\u90a3\u662f\u5077\u6765\u7684\uff0c\u6211\u5c31\u5148\u7ed9\u4ed6\u51e0\u4e2a\u5927\u5634\u5df4\u5b50\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8fd8\u6709\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u8fd8\u6709\uff0c\u6bcf\u4e2a\u4eba\u90fd\u8981\u5403\u996d\u7684\uff0c\u6211\u4eec\u82e5\u8981\u5403\u996d\uff0c\u5c31\u975e\u7528\u90a3\u952d\u91d1\u5b50\u4ed8\u5e10\u4e0d\u53ef\u3002\u201d\u53ea\u542c\u4e00\u4eba \u9053\uff1a\u201c\u8fd9\u70b9\u624d\u6700\u91cd\u8981\uff0c\u9178\u6885\u6c64\u627e\u7684\u4eba\u4e0d\u4f46\u8981\u662f\u4e2a\u597d\u8272\u7684\u7cca\u6d82\u866b\uff0c\u800c\u4e14\u8fd8\u8981\u662f\u4e2a\u7a77\u75af\u4e86\u3001\u997f\u75af \u4e86\u7684\u7cca\u6d82\u866b\u3002\u201d\u8fd9\u4e5f\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u8fd9\u6b21\u4e0b\u7ed3\u8bba\u7684\u4e5f\u4e0d\u662f\u738b\u52a8\uff0c\u662f\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u6bcf\u6b21\u51fa\u73b0\u7684\u65f6\u5019\uff0c\u4e5f\u548c\u4ed6\u5931\u8e2a\u7684\u65f6\u5019\uff0c\u4e00\u6837\u98d8\u5ffd\u3002 \u3000\u3000\u90ed\u5927\u8def\u6447\u4e86\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u65e0\u8bba\u8ddf\u8c01\u8bf4\u8bdd\u90fd\u86ee\u8c61\u4eba\u7684\uff0c\u5374\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u603b\u662f\u504f \u504f\u559c\u6b22\u81ed\u6211\u3002\u201d\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u60f3\u8ba9\u6211\u81ed\u4f60\u90fd\u56f0\u96be\u5f97\u5f88\u3002\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u738b\u52a8\u4e5f\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u81ed\u81ed\u4ed6\uff1f\u201d\u738b\u52a8\u7b11\u9053\uff1a\u201c\u80fd\u81ed\u6211\u7684\u8bdd\u5df2\u7ecf\u88ab\u4f60\u8bf4 \u5149\uff0c\u8fd8\u7528\u5f97\u7740\u522b\u4eba\u5f00\u53e3\u4e48\uff1f\u201d\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\uff0c\u8d70\u8fc7\u53bb\u62cd\u4e86\u62cd\u71d5\u4e03\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u53c8\u6e9c \u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u51fa\u53bb\u901b\u4e86\u901b\u3002\u201d\u4ed6\u597d\u8c61\u5f88\u4e0d\u559c\u6b22\u522b\u4eba\u78b0\u5230\u4ed6\uff0c\u6bcf\u6b21\u90ed\u5927 \u8def\u78b0\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u90fd\u597d\u8c61\u89c9\u5f97\u5f88\u4e0d\u4e60\u60ef\uff0c\u8fd9\u4e5f\u8bb8\u56e0\u4e3a\u9664\u90ed\u5927\u8def\u5916\u4e5f\u5f88\u5c11\u6709\u4eba\u53bb\u78b0\u4ed6\u3002 \u3000\u3000\u53ea\u8981\u770b\u5230\u4ed6\u90a3\u8eab\u8863\u670d\uff0c\u522b\u4eba\u5df2\u7ecf\u8fde\u9694\u591c\u996d\u90fd\u8981\u5455\u51fa\u751f\u7c7d\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u901b\u53bb\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5c71\u4e0b\uff0c\u57ce\u91cc\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u5730\u65b9\u6709\u4ec0 \u4e48\u597d\u901b\u7684\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8c01\u8bf4\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u4f60\u5c82\u975e\u5c31\u770b \u5230\u4e2a\u63d0\u7740\u4e24\u4e2a\u7bee\u5b50\u7684\u5927\u7f8e\u4eba\u4e48\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u4f60\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6740 \u4eba\u3002\u201d\u90ed\u5927\u8def\u609a\u7136\u9053\uff1a\u201c\u6740\u4eba\uff1f\u8c01\u6740\u4eba\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u68cd\u5b50\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u68cd\u5b50\u6740\u4eba\uff1f\u6740 \u7684\u662f\u8c01\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6709\u5acc\u7591\u7684\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8c01\u662f\u6709\u5acc\u7591\u7684\u4eba\uff1f\u6709\u4ec0\u4e48\u5acc\u7591\uff1f\u201d\u71d5\u4e03 \u9053\uff1a\u201c\u68cd\u5b50\u8981\u627e\u7684\u4eba\u662f\u4e2a\u4e94\u5341\u591a\u5c81\u7684\u7537\u4eba\uff0c\u662f\u5341\u5e74\u524d\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u6240\u4ee5\u51e1\u662f\u5341\u5e74\u524d\u624d\u642c\u5230\u8fd9 \u91cc\u6765\u7684\u7537\u4eba\u90fd\u6709\u5acc\u7591\uff0c\u90fd\u53ef\u80fd\u662f\u51e4\u6816\u68a7\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u51e4\u6816\u68a7\u662f\u8c01\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u51e4\u6816\u68a7 \u5c31\u662f\u68cd\u5b50\u8981\u627e\u7684\u4eba\u3002\u201d\u6797\u592a\u5e73\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u98ce\u6816\u68a7\uff0c\u662f\u4e0d\u662f\u2018\u9e21\u72ac\u4e0d\u7559\u2019\u51e4\u6816\u68a7\uff1f\u201d\u71d5 \u4e03\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u540d\u5b57\u5982\u6b64\u98ce\u96c5\u7684\u4eba\uff0c\u600e\u4e48\u8d77\u4e86\u4e2a\u5982\u6b64\u96be\u542c\u7684\u5916\u53f7\uff1f\u201d\u71d5 \u4e03\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e00\u4e0b\u624b\u5c31\u975e\u628a\u4eba\u5bb6\u5077\u5f97\u7cbe\u5149\u4e0d\u53ef\uff0c\u6709\u65f6\u8fde\u4e00\u6587\u94b1\u90fd\u4e0d\u66ff\u4eba\u5bb6\u7559\u4e0b\uff0c\u6709\u7684\u4eba\u88ab \u4ed6\u5077\u7684\u503e\u5bb6\u8361\u4ea7\uff0c\u53ea\u6709\u81ea\u5df1\u4e0a\u540a\u62b9\u8116\u5b50\uff0c\u6240\u4ee5\u4ed6\u867d\u7136\u6ca1\u6709\u6740\u8fc7\u4eba\uff0c\u4f46\u88ab\u4ed6\u903c\u6b7b\u7684\u4eba\u5374\u4e0d \u5c11\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4eba\u4e0d\u4f46\u5fc3\u9ed1\u624b\u8fa3\uff0c\u800c\u4e14\u89c6\u94b1\u5982\u547d\uff0c\u5077\u6765\u7684\u94b1\u81ea\u5df1\u4e5f\u820d\u4e0d\u5f97\u82b1\u3002\u201d \u90ed\u5927\u8def\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u5c06\u5077\u6765\u7684\u94b1\u5168\u90fd\u6551\u6d4e\u4e86\u522b\u4eba\uff0c\u505a\u4e86\u597d\u4e8b\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u4eba\u5e73\u751f\u4ec0\u4e48\u4e8b \u90fd\u505a\u8fc7\uff0c\u5c31\u662f\u6ca1\u505a\u8fc7\u597d\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u7684\u94b1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8c01\u90fd\u4e0d\u77e5 \u9053\u3002\u201d\u90ed\u5927\u8def\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u57ce\u91cc\u6709\u8fd9\u79cd\u5acc\u7591\u7684\u4eba\u4e00\u5171\u6709\u591a\u5c11\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u672c\u6765\u5c31\u4e0d \u591a\uff0c\u73b0\u5728\u5c31\u66f4\u5c11\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u68cd\u5b50\u5df2\u6740\u4e86\u51e0\u4e2a\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e94\u516d\u4e2a\u3001\u516d\u4e03\u4e2a\u3002\u201d\u90ed\u5927 \u8def\u77aa\u773c\u9053\uff1a\u201c\u4ed6\u6740\u4eba\uff0c\u4f60\u5c31\u5728\u65c1\u8fb9\u770b\u7740\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u73b0\u5728\u6211\u8fde\u770b\u90fd\u61d2\u5f97\u770b\u4e86\u3002\u201d\u90ed\u5927\u8def\u77aa \u7740\u4ed6\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u81ea\u4ece\u8ba4\u5f97\u4ed6\u4e4b\u540e\uff0c\u6211\u603b\u662f\u975e\u52a8\u4e0d\u53ef\u5462\uff1f\u201d\u90ed\u5927\u8def\u867d\u7136 \u4e0d\u7cca\u6d82\uff0c\u5374\u5f88\u51b2\u52a8\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5e94\u8be5\u5148\u95ee\u95ee\u71d5\u4e03\uff1a\u201c\u68cd\u5b50\u6740\u7684\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d\u4ed6\u6ca1\u6709\u95ee\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u68cd\u5b50\u6740 \u7684\u4e5f\u51b3\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\u3002 \u3000\u3000\u4ed6\u5f88\u660e\u767d\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u51b2\u52a8\u3002\u8fd9\u867d\u7136\u5e76\u4e0d\u662f\u79cd\u597d\u4e60\u60ef\uff0c\u4f46\u81f3\u5c11\u4e5f\u6bd4\u90a3\u4e9b\u5fc3\u80a0\u51b7\u9177\u3001 \u9ebb\u6728\u4e0d\u4ec1\u7684\u4eba\u597d\u5f97\u591a\u3002 \u3000\u3000\u4e09\u9ed1\u8863\u4eba\u4e5f\u6709\u79cd\u4e60\u60ef\u2014\u2014\u4ed6\u6c38\u8fdc\u4e0d\u613f\u8d70\u5728\u4efb\u4f55\u4eba\u7684\u524d\u9762\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u4e0d\u662f\u56e0\u4e3a\u4ed6\u8c26\u865a\u591a\u793c\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u5b81\u53ef\u7528\u773c\u775b\u5bf9\u7740\u4eba\u800c\u4e0d\u613f\u7528\u80cc\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5c31\u8d70\u5728\u68cd\u5b50\u548c\u91d1\u72ee\u5b50\u8eab\u540e\u3002 \u3000\u3000\u4ed6\u4eec\u5bf9\u4ed6\u5012\u653e\u5fc3\u7684\u5f88\uff0c\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u4ed6\u7684\u5251\u662f\u51b3\u4e0d\u4f1a\u4ece\u4eba\u80cc\u540e\u523a\u8fc7\u6765\u7684\u3002 \u3000\u3000\u4ed6\u867d\u7136\u7528\u9ed1\u5dfe\u8499\u4f4f\u4e86\u8138\uff0c\u4f46\u5374\u6bd4\u5f88\u591a\u4eba\u90fd\u8981\u9762\u5b50\u7684\u591a\u3002 \u3000\u3000\u957f\u8857\u5f88\u9759\uff0c\u53ea\u6709\u4e09\u4e24\u5bb6\u7684\u7a97\u6237\u91cc\uff0c\u8fd8\u71c3\u7740\u6697\u6de1\u7684\u706f\u706b\u3002 \u3000\u3000\u8d70\u5230\u8857\u5de6\u8fb9\u7684\u7b2c\u56db\u5bb6\uff0c\u4ed6\u4eec\u5c31\u505c\u4f4f\u4e86\u811a\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u4e5f\u548c\u57ce\u91cc\u522b\u7684\u4eba\u5bb6\u4e00\u6837\uff0c\u5efa\u7b51\u5f97\u6734\u5b9e\u800c\u7b80\u964b\uff0c\u7a84\u800c\u539a\u7684\u95e8\uff0c\u5c0f\u800c\u9ad8\u7684\u7a97\u5b50\uff0c\u660f\u9ec4 \u7684\u7a97\u7eb8\uff0c\u660f\u9ec4\u7684\u706f\u5149\u3002 \u3000\u3000\u95e8\u7a97\u90fd\u662f\u7d27\u7d27\u5173\u7740\u7684\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u6c89\u58f0\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e00\u5bb6\uff1f\u201d\u68cd\u5b50\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u7a81\u7136\u98de\u63a0\u800c\u8d77\u3002\u4ed6\u8eab\u6750\u867d\u9b41\u4f1f\uff0c\u884c\u52a8\u5374\u6781\u7075\u4fbf\uff0c\u8f7b\u529f\u4e5f\u4e0d\u5f31\uff0c\u811a\u5c16\u5728\u5c4b\u6a90\u4e0a\u8f7b\u8f7b \u4e00\u70b9\uff0c\u4fbf\u5df2\u63a0\u8fc7\u5c4b\u810a\uff0c\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u68cd\u5b50\u56de\u5934\u77a7\u4e86\u90a3\u9ed1\u8863\u4eba\u4e00\u773c\uff0c\u624d\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u516c\u5bb6\u529e\u6848\uff0c\u5c45\u6c11\u95ed\u6237\u83ab\u51fa\uff0c\u5426\u5219\u683c\u6740\u52ff \u8bba\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c4b\u5b50\u91cc\u7684\u706f\u5df2\u7184\u706d\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u663e\u7136\u6709\u4eba\u649e\u7834\u4e86\u540e\u9762\u7684\u7a97\u5b50\uff0c\u5411\u593a\u7a97\u800c\u9003\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u91d1\u6bdb\u72ee\u65e9\u5df2\u9632\u5230\u4e86\u8fd9\u4e00\u7740\u3002 \u3000\u3000\u53ea\u662f\u4e00\u9635\u60ca\u547c\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u4f4e\u5412\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u53bb\u3002\u201d\u63a5\u7740\u5c31\u770b\u5230\u4e00\u6761\u4eba\u5f71\u4e0a\u4e86\u5c4b\u810a\uff0c\u8f7b\u529f\u867d\u4e0d\u5728\u91d1\u6bdb\u72ee\u4e4b \u4e0b\uff0c\u8eab\u6750\u5374\u7626\u5c0f\u7684\u591a\uff0c\u56db\u4e0b\u7565\u4e00\u9021\u5de1\uff0c\u5c31\u5411\u4e1c\u5357\u65b9\u98de\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u68cd\u5b50\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4f46\u662f\u5ffd\u7136\u95f4\uff0c\u4ed6\u5df2\u7ecf\u4e0a\u4e86\u5c4b\u810a\uff0c\u6321\u4f4f\u4e86\u90a3\u4eba\u5f71\u7684\u53bb\u8def\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u4e00\u60ca\uff0c\u53cc\u62f3\u9f50\u51fa\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4f3c\u4e4e\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u4f46\u5ffd\u7136\u95f4\uff0c\u51fa\u624b\u6253\u7684\u4eba\u5df2\u4ece\u5c4b\u810a\u4e0a\u6eda\u4e86\u4e0b\u6765\uff0c\u8dcc\u5012\u8857\u5fc3\u3002 \u3000\u3000\u68cd\u5b50\u8fd9\u624d\u6162\u6162\u7684\u8d70\u4e86\u8fc7\u53bb\u3002\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u4f4e\u5934\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u5bd2\u98ce\u51c4\u5389\uff0c\u5929\u5730\u8083\u6740\u3002 \u3000\u3000\u4ed6\u4e00\u53cc\u773c\u775b\u5728\u51b7\u591c\u4e2d\u770b\u6765\u8c61\u4e24\u628a\u9525\u5b50\u3002 \u3000\u3000\u7ed3\u4e86\u51b0\u7684\u9525\u5b50\u3002 \u3000\u3000\u9001\u4e0d\u8d70\u7684\u761f\u795e\u90ed\u5927\u8def\u5df2\u7ecf\u5728\u8857\u89d2\u91cc\u770b\u4e86\u5f88\u4e45\uff0c\u4ed6\u672c\u6765\u65e9\u5c31\u60f3\u51b2\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u53ef\u662f\u51b2\u8fc7\u53bb\u5e72\u4ec0\u4e48\u5462\uff1f\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u8981\u5e72\u4ec0\u4e48\uff1f\u68cd\u5b50\u6293\u7684\u82e5\u771f\u662f\u4e2a\u5fc3\u9ed1\u624b\u8fa3\u7684\u5f3a\u76d7\uff0c \u4ed6\u96be\u9053\u8fd8\u80fd\u5e2e\u5f3a\u76d7\u62d2\u6355\u4e48\uff1f\u4ece\u5c71\u4e0a\u4e00\u8def\u8dd1\u4e0b\u6765\uff0c\u51b7\u98ce\u6251\u9762\uff0c\u4ed6\u7684\u706b\u5df2\u7ecf\u5c0f\u4e86\u5f88\u591a\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u8fd8\u662f\u5728\u8857\u89d2\u91cc\u7b49\u7740\u3002 \u3000\u3000\u8dcc\u5230\u8857\u5fc3\u4e0a\u7684\u90a3\u4e2a\u4eba\u8737\u66f2\u5728\u54ea\u91cc\uff0c\u5c31\u8c61\u662f\u4e00\u6ee9\u6ce5\uff0c\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u68cd\u5b50\u7a81\u7136\u4e00\u628a\u5c06\u4ed6\u62c9\u4e86\u8d77\u6765\uff0c\u7528\u4e24\u53ea\u624b\u6978\u7740\u4ed6\u7684\u8863\u895f\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u770b\u7740\u6211\u3002\u201d\u8fd9\u4eba\u7684 \u8eab\u5b50\u867d\u5df2\u7ad9\u8d77\uff0c\u5934\u8fd8\u662f\u8f6f\u8f6f\u7684\u5782\u7740\u3002 \u3000\u3000\u68cd\u5b50\u7684\u53f3\u624b\u4e00\u677e\uff0c\u6b63\u6b63\u53cd\u53cd\u63b4\u4e86\u4ed6\u5341\u51e0\u4e2a\u8033\u522e\u5b50\u3002 \u3000\u3000\u8840\u5f00\u59cb\u4ece\u4ed6\u5634\u89d2\u5f80\u5916\u6d41\uff0c\u4f46\u4ed6\u8fd8\u662f\u54ac\u7740\u7259\uff0c\u8fde\u54fc\u90fd\u6ca1\u6709\u54fc\u4e00\u58f0\u3002 \u3000\u3000\u68cd\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6709\u79cd\u3002\u201d\u4ed6\u7684\u819d\u76d6\u7a81\u7136\u62ac\u8d77\uff0c\u7528\u529b\u4e00\u649e\u3002 \u3000\u3000\u8fd9\u4eba\u75db\u5f97\u8fde\u8138\u90fd\u53d8\u4e86\u5f62\uff0c\u60f3\u5f2f\u8170\uff0c\u5374\u5f2f\u4e0d\u4e0b\u53bb\u3002\u53ea\u6709\u5c06\u4e0b\u8eab\u5f80\u4e0a\u7f29\uff0c\u6574\u4e2a\u4eba\u90fd\u7f29\u6210\u4e86\u4e00 \u56e2\uff0c\u60ac\u7a7a\u540a\u5728\u68cd\u5b50\u624b\u4e0a\uff0c\u6296\u5f97\u5168\u8eab\u7684\u9aa8\u5934\u90fd\u4f3c\u5df2\u5c06\u677e\u6563\u3002 \u3000\u3000\u68cd\u5b50\u9053\uff1a\u201c\u5bf9\u4ed8\u4e0d\u542c\u8bdd\u7684\u4eba\uff0c\u6211\u6709\u5f88\u591a\u6cd5\u5b50\uff0c\u8fd9\u662f\u5176\u4e2d\u6700\u7b80\u5355\u7684\u4e00\u79cd\uff0c\u4f60\u60f3\u4e0d\u60f3\u518d\u8bd5\u7b2c \u4e8c\u79cd\uff1f\u201d\u8fd9\u4eba\u7ec8\u4e8e\u62ac\u8d77\u5934\uff0c\u77a7\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u4ec7\u6068\u7684\u6012\u706b\u3002 \u3000\u3000\u68cd\u5b50\u7684\u795e\u60c5\u5374\u5ffd\u7136\u53d8\u4e86\uff0c\u53d8\u5f97\u548c\u6c14\u4e86\u4e9b\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u51e4\u6816\u68a7\uff1f\u201d\u8fd9\u4eba\u7259\u9f7f\u683c\u683c\u6253 \u865e\uff0c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u660e\u77e5\u9053\u6211\u4e0d\u662f\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u8fd9\u4e48\u6837\u5bf9\u4ed8\u6211\uff1f\u201d\u68cd\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u4e0d\u80fd\u786e \u5b9a\uff0c\u9664\u975e\u4f60\u544a\u8bc9\u6211\u4f60\u662f\u8c01\uff0c\u6211\u624d\u80fd\u8bc1\u5b9e\u4f60\u4e0d\u662f\u51e4\u6816\u68a7\u3002\u201d\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u8c01\u90fd\u4e0d\u662f\uff0c\u53ea\u4e0d\u8fc7\u662f \u8fd9\u57ce\u91cc\u4e00\u4e2a\u5356\u6742\u8d27\u7684\u5c0f\u5546\u4eba\u3002\u201d\u68cd\u5b50\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u522b\u7684\u4eba\uff0c\u6211\u53ea\u6709\u628a\u4f60\u5f53 \u4f5c\u51e4\u6816\u68a7\u3002\u201d\u8fd9\u4eba\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u6015\u6293\u9519\u4e86\u4eba\uff0c\u6015\u4e0a\u5934\u602a\u4f60\uff0c\u6240\u4ee5\u4f60\u660e\u77e5\u6211\u4e0d\u662f\u51e4\u6816\u68a7\uff0c\u4e5f\u4e0d \u80af\u653e\u8fc7\u6211\u3002\u4f60\u8fd9\u79cd\u4eba\u7684\u624b\u6bb5\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u3002\u201d\u68cd\u5b50\u7684\u8138\u8272\u53c8\u548c\u7f13\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u6211\u627e \u7684\u53ea\u662f\u51e4\u6816\u68a7\u4e00\u4e2a\u4eba\uff0c\u548c\u522b\u4eba\u5168\u6ca1\u5173\u7cfb\uff0c\u53ea\u8981\u4f60\u80af\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\u6765\u5386\uff0c\u6211\u7acb\u5373\u5c31\u653e\u4e86 \u4f60\u3002\u201d\u8fd9\u4eba\u9053\uff1a\u201c\u653e\u4e86\u6211\uff1f\u4f60\u4f1a\u653e\u4e86\u6211\uff1f\u201d\u68cd\u5b50\u5c45\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u4e0d\u4f1a\u653e\u4f60\uff1f\u5c31 \u7b97\u4f60\u5728\u522b\u7684\u5730\u65b9\u6709\u6848\uff0c\u548c\u6211\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u6211\u4f55\u5fc5\u72d7\u62ff\u8017\u5b50\uff0c\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d\u8fd9\u4eba\u60f3\u4e86\u5f88\u4e45\uff0c\u624d \u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u6211\u59d3\u97e9\uff0c\u53eb\u4e00\u9635\u98ce\u3002\u201d\u68cd\u5b50\u9053\uff1a\u201c\u4e00\u9635\u98ce\uff0c\u90a3\u5e74\u6625\u5929\uff0c\u5728\u5f20\u5bb6\u53e3\u6740\u4e86\u9ec4\u5458\u5916 \u4e00\u5bb6\u7684\u662f\u4e0d\u662f\u4f60\uff1f\u201d\u4e00\u9635\u98ce\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\uff0c\u53ea\u8981\u6211\u4e0d\u662f\u51e4\u6816\u68a7\u522b\u7684\u4e8b\u4f60\u90fd\u4e0d\u7ba1\u3002\u201d\u68cd\u5b50\u9053\uff1a \u201c\u6211\u5f53\u7136\u4e0d\u7ba1\u3002\u4f46\u6211\u53c8\u600e\u77e5\u4f60\u5c31\u662f\u4e00\u9635\u98ce\uff0c\u4e0d\u662f\u51e4\u6816\u68a7\uff1f\u201d\u4e00\u9635\u98ce\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u523a\u7740 \u82b1\u2026\u2026\u201d\u201c\u54e7\u201d\u7684\uff0c\u8863\u895f\u88ab\u6495\u5f00\uff0c\u80f8\u819b\u4e0a\u679c\u7136\u523a\u7740\u9f99\u5377\u98ce\u7684\u5f62\u72b6\u3002 \u3000\u3000\u8fd9\u7684\u786e\u662f\u4e00\u9635\u98ce\u7684\u6807\u5fd7\u3002 \u3000\u3000\u68cd\u5b50\u6de1\u6de1\u9053\uff1a\u201c\u4e00\u9635\u98ce\u4e0d\u4f1a\u5192\u5145\u51e4\u6816\u68a7\uff0c\u51e4\u6816\u68a7\u5374\u53ef\u80fd\u5192\u5145\u4e00\u9635\u98ce\u7684\u3002\u201d\u68cd\u5b50\u6c89\u541f\u7740\uff0c \u9053\uff1a\u201c\u542c\u8bf4\uff0c\u9ec4\u5458\u5916\u662f\u88ab\u4eba\u4e00\u5251\u523a\u6b7b\u7684\u3002\u201d\u4e00\u9635\u98ce\u9053:\u201c\u4e0d\u662f\uff0c\u6211\u4ece\u6765\u4e0d\u4f7f\u5251\u3002\u201d\u68cd\u5b50\u9053\uff1a \u201c\u4ed6\u662f\u600e\u4e48\u6b7b\u7684\u5462\uff1f\u201d\u4e00\u9635\u98ce\u9053\uff1a\u201c\u6211\u7528\u836f\u5148\u6bd2\u6b7b\u4e86\u4ed6\uff0c\u518d\u5c06\u4ed6\u629b\u5230\u4e95\u91cc\u53bb\u3002\u201d\u68cd\u5b50\u53c8\u7b11\u4e86 \u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u7684\u786e\u662f\u4e00\u9635\u98ce\u4e86\u3002\u201d\u4e00\u9635\u98ce\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u3002\u201d\u68cd\u5b50\u9053\uff1a\u201c\u597d\uff0c \u5f88\u597d\u2026\u2026\u201d\u4ed6\u7a81\u7136\u51fa\u624b\uff0c\u53cd\u624b\u5728\u4e00\u9635\u98ce\u8116\u5b50\u4e0a\u4e00\u5207\u3002 \u3000\u3000\u4e00\u9635\u98ce\u7acb\u5373\u53c8\u53d8\u6210\u4e86\u4e00\u6ee9\u6ce5\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u867d\u5df2\u6b7b\uff0c\u72e0\u72e0\u5730\u76ef\u7740\u68cd\u5b50\uff0c\u773c\u73e0\u6162\u6162\u5730\u5411\u5916\u7a81\u51fa\uff0c\u5145\u6ee1\u4e86\u6124\u6012\u4e0e\u6028\u6bd2\uff0c\u8c61\u662f\u5728 \u95ee\uff1a\u201c\u4f60\u7b54\u5e94\u8fc7\u653e\u4e86\u6211\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0b\u6bd2\u624b\uff1f\u201d\u68cd\u5b50\u7684\u5634\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u773c\u775b\u5374\u4f3c\u5728\u66ff\u4ed6\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u5f97\u610f\u4e4b\u8272\uff0c\u4eff\u4f5b\u5728\u8bf4\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u7684\u624b\u6bb5\uff0c\u6211\u65e2\u7136\u4e0d\u4fe1\u4efb\u4f60\uff0c\u4f60\u4e3a\u4f55\u53c8 \u8981\u4fe1\u4efb\u6211\u5462\uff1f\u201d\u90ed\u5927\u8def\u7684\u773c\u775b\u91cc\u4e5f\u5728\u5192\u706b\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u53ea\u6709\u77a7\u7740\uff0c\u56e0\u4e3a\u4e00\u9635\u98ce\u7684\u786e\u8be5\u6b7b\u3002 \u3000\u3000\u5b98\u5dee\u6740\u8d3c\uff0c\u672c\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u9053\uff1a\u201c\u539f\u6765\u4ed6\u6740\u4eba\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u53ea\u4e0d\u8fc7\u5728\u65c1\u8fb9\u77a7\u7740\u7684\u3002\u201d\u90ed\u5927\u8def\u7528\u4e0d\u7740\u56de\u5934\uff0c \u4e5f\u77e5\u9053\u8bf4\u8bdd\u7684\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u8981\u770b\u4e0b\u53bb\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u559c\u6b22\u770b\u4ed6\u6740\u4eba\uff1f\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u6211\u8981\u7b49\u7740\u770b\u4ed6\u6740\u9519\u4e00\u4e2a\u4eba\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u65f6\u6211\u624d\u6709\u7406\u7531\u6740 \u4ed6\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u60f3\u6740\u4ed6\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e00\u9635\u98ce\u867d\u8be5\u6b7b\uff0c\u4f46\u4ed6\u5374\u66f4\u8be5\u6b7b\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u4f60\u8ba4\u4e3a\u4ed6\u505a\u9519\u4e86\u4e8b\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u505a\u7684\u4e8b\u4e5f\u4e0d\u80fd\u8bf4\u4e0d\u5bf9\uff0c\u4f46\u7528\u7684\u624b\u6bb5\u5374\u592a\u5351\u9119\u3001\u592a\u53ef \u6076\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u82e5\u6c38\u8fdc\u4e0d\u6740\u9519\u4eba\u5462\uff1f\u201d\u90ed\u5927\u8def\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u6709\u4e9b\u4e8b\u672c\u5c31\u662f\u4efb\u4f55\u4eba\u90fd\u6ca1\u6cd5\u5b50\u53bb\u7ba1\u7684\u3002\u4f55\u51b5\u68cd\u5b50\u867d\u53ef\u6076\uff0c\u5374 \u5f88\u6709\u7528\uff0c\u6709\u4e9b\u4eba\u7684\u786e\u5c31\u8981\u4ed6\u8fd9\u79cd\u4eba\u53bb\u5bf9\u4ed8\u3002\u201d\u90ed\u5927\u8def\u5ffd\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u8fd9\u79cd\u4eba\u5c31 \u6ca1\u6709\u4eba\u80fd\u5bf9\u4ed8\u5f97\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8c01\u80fd\u5bf9\u4ed8\u4ed6\uff1f\u4f60\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u6211\uff0c\u4e5f\u8bb8\u662f\u522b \u4eba\uff0c\u65e0\u8bba\u662f\u8c01\u90fd\u6ca1\u5173\u7cfb\uff0c\u6211\u53ea\u77e5\u9053\u5929\u7406\u5faa\u73af\uff0c\u62a5\u5e94\u4e0d\u723d\uff0c\u8fdf\u65e9\u603b\u6709\u4eba\u53bb\u5bf9\u4ed8\u4ed6\u7684\u3002\u201d\u8fd9\u5c31\u662f \u90ed\u5927\u8def\u4e4b\u6240\u4ee5\u4e3a\u90ed\u5927\u8def\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u5bf9\u4eba\u751f\u5145\u6ee1\u4e86\u70ed\u7231\uff0c\u800c\u4e14\u5145\u6ee1\u4e86\u4fe1\u5fc3\u3002 \u3000\u3000\u4ed6\u786e\u4fe1\u771f\u7406\u6c38\u8fdc\u4e0d\u53d8\uff0c\u516c\u9053\u6c38\u8fdc\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u786e\u4fe1\u6b63\u4e49\u5fc5\u5b9a\u6218\u80dc\u90aa\u6076\uff0c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u6253\u51fb\u90fd\u4e0d\u4f1a\u8ba9\u4ed6\u5931\u53bb\u8fd9\u79cd\u4fe1\u5fc3\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u6b63\u62cd\u7740\u68cd\u5b50\u7684\u80a9\uff0c\u7b11\u9053\uff1a\u201c\u606d\u559c\u606d\u559c\uff0c\u6709\u4e00\u4ef6\u5927\u6848\u88ab\u4f60\u7834\u4e86\uff1b\u4e00\u665a\u4e0a\u8fde\u7834\u4e03\u6848\uff0c \u9664\u4e86\u4f60\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u672c\u4e8b\uff1f\u201d\u68cd\u5b50\u9053\uff1a\u201c\u4f60\u3002\u201d\u91d1\u6bdb\u72ee\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u884c\uff0c\u6211\u7684\u5fc3\u4e0d\u591f \u72e0\uff0c\u8fd9\u7897\u996d\u5df2\u6e10\u6e10\u5403\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d\u68cd\u5b50\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u53c8\u5fcd\u4f4f\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u4e0b\u4e00\u5bb6\u662f\u8c01\uff1f\u201d\u68cd\u5b50\u62ac\u8d77\u5934\uff0c\u773c\u775b\u77aa\u7740\u5bf9\u9762\u7684\u4e00\u5757\u62db\u724c\u3002 \u3000\u3000\u9ed1\u5e95\u7684\u62db\u724c\uff0c\u91d1\u5b57\uff1a\u201c\u5229\u6e90\u5f53\u94fa\u201d\u3002 \u3000\u3000\u5229\u6e90\u5f53\u94fa\u7684\u8001\u677f\u867d\u7136\u5265\u76ae\uff0c\u5374\u4e0d\u5543\u9aa8\u5934\uff0c\u800c\u4e14\u5e38\u5e38\u8fd8\u4f1a\u5728\u9aa8\u5934\u4e0a\u7559\u70b9\u8089\u5206\u7ed9\u522b\u4eba\u5403\u3002 \u3000\u3000\u90ed\u5927\u8def\u5bf9\u8fd9\u4eba\u7684\u5370\u8c61\u4e00\u5411\u4e0d\u9519\uff0c\u770b\u5230\u68cd\u5b50\u548c\u91d1\u6bdb\u72ee\u5411\u5f53\u94fa\u8d70\u8fc7\u53bb\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u4e5f\u60f3\u8d76\u8fc7 \u53bb\u3002 \u3000\u3000\u738b\u52a8\u4e00\u76f4\u7ad9\u5728\u540e\u9762\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u80fd\u52a8\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u738b \u52a8\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u52a8\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u73b0\u5728\u82e5\u52a8\uff0c\u4e00\u52a8\u5c31\u6709\u9ebb\u70e6\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u6015\u8fc7\u9ebb \u70e6\u4e86\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u5c31\u662f\u73b0\u5728\uff0c\u800c\u4e14\u6015\u7684\u5c31\u662f\u8fd9\u79cd\u9ebb\u70e6\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u83ab\u5fd8\u4e86\uff0c\u4ed6\u662f\u6211\u4eec \u7684\u5927\u5a18\u8205\uff0c\u6211\u4eec\u968f\u65f6\u90fd\u53ef\u80fd\u53bb\u5e2e\u4ed6\u7684\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u6ca1\u6709\u5a18\u8205\u65e0\u59a8\uff0c\u6ca1\u6709\u7956\u5b97\u624d\u9ebb\u70e6\u3002\u201d\u90ed \u5927\u8def\u6014\u4e86\u6014\u9053\uff1a\u201c\u6ca1\u6709\u7956\u5b97\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u5a18\u8205\u82e5\u771f\u662f\u6709\u6848\u5e95\u7684\u8d3c\uff0c\u6211\u53bb\u52a9\u4ed6\uff0c\u5c82\u975e\u8fde\u6211\u7956 \u5b97\u7684\u4eba\u90fd\u4e22\u5149\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u53bb\uff0c\u6211\u53bb\uff01\u201d\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u82e5\u80fd\u8ba9\u4f60 \u4e00\u4e2a\u4eba\u53bb\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u4e0d\u8eba\u5728\u5e8a\u4e0a\u7761\u89c9\uff1f\u201d\u90ed\u5927\u8def\u77a7\u7740\u4ed6\u51b7\u51b0\u51b0\u7684\u773c\u775b\uff0c\u51b7\u51b0\u51b0\u7684\u8138\uff0c\u5fc3\u91cc \u5ffd\u7136\u6d8c\u8d77\u4e86\u4e00\u9635\u53cb\u60c5\u7684\u6e29\u6696\u3002 \u3000\u3000\u4ed6\u82e5\u60f3\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u62e6\u5f97\u4f4f\u3002 \u3000\u3000\u80fd\u62e6\u5f97\u4f4f\u4ed6\u7684\u53ea\u6709\u670b\u53cb\u3002 \u3000\u3000\u8fd9\u65f6\u91d1\u72ee\u5b50\u548c\u68cd\u5b50\u5df2\u8d70\u5230\u5f53\u94fa\u95e8\u53e3\u3002 \u3000\u3000\u95e8\u672c\u6765\u4e5f\u662f\u5173\u7740\u7684\uff0c\u4f46\u4ed6\u4eec\u8fd8\u6ca1\u6709\u62cd\u95e8\uff0c\u95e8\u5ffd\u7136\u5f00\u4e86\u3002 \u3000\u3000\u5265\u76ae\u8001\u677f\u4ece\u95e8\u91cc\u63a2\u51fa\u5934\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4e09\u4f4d\u8fd8\u4f1a\u518d\u6765\u7684\uff0c\u8bf7\u8fdb\u8bf7\u8fdb\u3002\u201d\u91d1\u72ee\u5b50\u548c\u68cd \u5b50\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u628a\u4f4f\u4e86\u95e8\u3002 \u3000\u3000\u90ed\u5927\u8def\u54ac\u7740\u7259\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u68cd\u5b50\u8981\u7528\u4ec0\u4e48\u624b\u6bb5\u5bf9\u4ed8\u4ed6\uff0c\u770b\u6765\u6211\u8fd8\u662f\u8be5\u53bb\u77a7\u77a7\u3002\u201d \u4ed6\u7528\u4e0d\u7740\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u65f6\u91d1\u6bdb\u72ee\u548c\u68cd\u5b50\u5df2\u7ecf\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u542c\u5265\u76ae\u8001\u677f\u7684\u58f0\u97f3\u5728\u95e8\u91cc\u9762\u9053\uff1a\u201c\u4e09\u4f4d\u8981\u8d70\u4e86\u4e48\uff0c\u4e0d\u9001\u4e0d\u9001\u3002\u201d\u91d1\u6bdb\u72ee\u542b\u7b11\u62b1\u62f3\uff0c \u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u8bf7\u7559\u6b65\u3002\u201d\u90ed\u5927\u8def\u770b\u7684\u5446\u4f4f\u4e86\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u8fd9\u4e24\u4eba\u600e\u4e48\u5ffd \u7136\u53d8\u5f97\u5ba2\u6c14\u8d77\u6765\u4e86\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u68cd\u5b50\u8981\u6253\u4eba\u7684\u65f6\u5019\uff0c\u5e76\u4e0d\u662f\u968f\u968f\u4fbf\u4fbf\u5c31\u6253\u4e0b\u53bb\u7684\u3002\u5426\u5219\u68cd \u5b50\u65e9\u5c31\u6253\u65ad\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u5265\u76ae\u8001\u677f\u53c8\u662f\u8c01\uff1f\u51ed\u4ec0\u4e48\u80fd\u4ee4\u4ed6\u4eec\u5982\u6b64\u5ba2\u6c14\uff1f\u201d\u738b\u52a8\u6c89\u601d \u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u8c01\u90fd\u4e0d\u662f\uff0c\u6240\u4ee5\u4eba\u5bb6\u624d\u4f1a\u5bf9\u4ed6\u5ba2\u6c14\u3002\u201d\u90ed\u5927\u8def\u60f3\u4e86\u60f3\uff0c\u4e5f\u4e0d\u77e5\u662f\u5426\u76f8\u901a \u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u5df2\u6ca1\u7a7a\u518d\u60f3\uff0c\u91d1\u6bdb\u72ee\u548c\u68cd\u5b50\u4e0b\u4e00\u4e2a\u76ee\u6807\u7adf\u662f\u9ea6\u8001\u5e7f\u70e7\u814a\u94fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u76b1\u7709\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u4eec\u8fde\u9ea6\u8001\u5e7f\u8fd9\u79cd\u4eba\u4e5f\u6000\u7591\uff0c\u7591\u5fc3\u75c5\u5012\u771f\u4e0d\u5c0f\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u8fd9\u6b21\u4f60\u5012\u7528\u4e0d\u7740\u62c5\u5fc3\uff0c\u9ea6\u8001\u5e7f\u51b3\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6bdb\u75c5\u88ab\u4ed6\u4eec\u627e\u51fa\u6765\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d \u62c5\u5fc3\uff0c\u4f46\u5374\u4e0d\u662f\u4e3a\u4e86\u4f60\u8fd9\u539f\u56e0\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u662f\u4eba\uff0c \u4e5f\u5f97\u5403\u996d\uff0c\u82e5\u6ca1\u6709\u9ea6\u8001\u5e7f\uff0c\u4ed6\u4eec\u660e\u5929\u5403\u4ec0\u4e48\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u5403\u5c41\u3002\u201d\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u4f46\u7b11\u5bb9\u521a \u9732\u51fa\uff0c\u7acb\u5373\u5c31\u53c8\u6d88\u5931\u3002 \u3000\u3000\u70e7\u814a\u5e97\u91cc\u7adf\u5ffd\u7136\u4f20\u51fa\u4e00\u58f0\u60ca\u547c\uff0c\u6b63\u662f\u9ea6\u8001\u5e7f\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u53c8\u542c\u5230\u68cd\u5b50\u7684\u58f0\u97f3\u5728\u95ee\uff1a\u201c\u8fd9\u952d\u91d1\u5b50\u662f\u54ea\u91cc\u6765\u7684\uff1f\u8bf4\uff01\u201d\u542c\u5230\u201c\u91d1\u5b50\u201d\u4e24\u4e2a\u5b57\uff0c\u90ed\u5927\u8def \u7684\u4eba\u5df2\u7bad\u4e00\u822c\u4e32\u4e86\u51fa\u53bb\u8fd9\u6b21\u8fde\u738b\u52a8\u90fd\u6ca1\u6709\u518d\u62e6\u4ed6\u3002 \u3000\u3000\u53ea\u89c1\u68cd\u5b50\u62ce\u7740\u9ea6\u8001\u5e7f\uff0c\u5c31\u597d\u8c61\u9ea6\u8001\u5e7f\u62ce\u7740\u6cb9\u9e21\u4f3c\u7684\u3002 \u3000\u3000\u6cb9\u9e21\u5f53\u7136\u6709\u6cb9\uff0c\u9ea6\u8001\u5e7f\u8138\u4e0a\u7684\u6c57\u4e5f\u8c61\u662f\u6cb9\uff0c\u5728\u706f\u4e0b\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u4ed6\u4e0d\u505c\u5730\u6296\u6296\u7684\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u68cd\u5b50\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u91d1\u5b50\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d\u8fd9\u6b21\u5df2\u7528\u4e0d\u7740\u9ea6\u8001\u5e7f\u81ea\u5df1\u8bf4\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u51b2\u4e86\u8fdb\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u91d1\u5b50\u662f\u6211\u7ed9\u4ed6\u7684\uff0c\u4e00\u5171\u5356\u4e86\u4ed6\u4e09\u5341\u65a4\u8089\u3001\u56db\u5341\u65a4\u9152\uff0c\u5916 \u52a0\u4e03\u53ea\u9e45\u3001\u516b\u53ea\u9e21\uff0c\u8c01\u4e5f\u6ca1\u505a\u8680\u672c\u751f\u610f\u3002\u201d\u68cd\u5b50\u6162\u6162\u7684\u653e\u4e0b\u9ea6\u8001\u5e7f\uff0c\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u77aa\u7740\u90ed \u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u540a\u513f\u90ce\u5f53\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u7684\u786e\u4e0d\u8c61\u662f\u4e2a\u80fd\u7528\u91d1\u5b50\u4ed8\u5e10\u7684\u4eba\u3002 \u3000\u3000\u68cd\u5b50\u9053\uff1a\u201c\u91d1\u5b50\u662f\u4f60\u7684\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u662f\u3002\u201d\u68cd\u5b50\u9053\uff1a\u201c\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u4e00\u4e2a\u4eba\u6709\u91d1\u5b50\u82e5\u4e5f\u72af\u6cd5\u7684\u8bdd\uff0c\u90a3\u4e48\u5929\u4e0b\u72af\u6cd5\u7684\u4eba\u53ef\u5c31\u592a\u591a\u4e86\uff0c\u53ea\u6015\u4e24\u4f4d\u4e5f\u4e0d\u4f8b\u5916\u5427\uff1f\u201d\u68cd \u5b50\u7684\u8138\u4e0a\u867d\u7136\u6ca1\u6709\u8868\u60c5\uff0c\u77b3\u5b54\u5374\u5df2\u6e10\u6e10\u5f00\u59cb\u5728\u6536\u7f29\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4ed6\u7684\u624b\u5df2\u4f38\u51fa\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u6bd4\u522b\u4eba\u9ad8\uff0c\u624b\u4e5f\u6bd4\u522b\u4eba\u957f\uff0c\u5341\u6839\u53c8\u5e72\u53c8\u7626\u7684\u624b\u6307\uff0c\u5c31\u8c61\u662f\u4e00\u53cc\u88c5\u5728\u68cd\u5b50\u4e0a\u7684\u94c1 \u722a\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u504f\u504f\u5c31\u8981\u78b0\u78b0\u8fd9\u53cc\u94c1\u722a\u3002 \u3000\u3000\u4ed6\u65e2\u6ca1\u6709\u95ea\u907f\uff0c\u4e5f\u6ca1\u6709\u62db\u67b6\uff0c\u201c\u547c\u201d\u7684\uff0c\u53cc\u62f3\u9f50\u51fa\uff0c\u786c\u78b0\u786c\u5c31\u5f80\u8fd9\u53cc\u94c1\u722a\u53cd\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u51fb\u51fa\uff0c\u975e\u4f46\u68cd\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u91d1\u6bdb\u72ee\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5931\u8272\u3002 \u3000\u3000\u68cd\u5b50\u8fd9\u4e00\u53cc\u94c1\u722a\u4e0a\u663e\u7136\u7ec3\u7740\u6709\u9e70\u722a\u529f\u7684\u5de5\u592b\uff0c\u5c31\u7b97\u662f\u778e\u5b50\u4e5f\u80fd\u611f\u89c9\u5f97\u5230\uff0c\u5bf9\u65b9\u624b\u4e0a\u82e5\u6ca1 \u6709\u60ca\u4eba\u7684\u5185\u529f\uff0c\u600e\u4e48\u6562\u4e00\u51fa\u624b\u5c31\u4f7f\u51fa\u8fd9\u79cd\u786c\u78b0\u786c\u7684\u62db\u5f0f\uff1f\u5176\u5b9e\u90ed\u5927\u8def\u7684\u5185\u529b\u5e76\u4e0d\u5982\u4ed6\u4eec\u60f3\u8c61 \u4e2d\u90a3\u4e48\u53ef\u6015\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u5929\u751f\u662f\u4e2a\u5927\u8def\u7684\u4eba\uff0c\u4e0d\u4f46\u82b1\u94b1\u5927\u8def\uff0c\u505a\u4e8b\u5927\u8def\uff0c\u6b66\u529f\u4e5f\u5927\u8def\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u51fb\u51fa\uff0c\u4f7f\u4ed6\u7684\u62f3\u5934\u51fb\u65ad\u5bf9\u65b9\u7684\u9e70\u722a\uff1f\u8fd8\u662f\u5bf9\u65b9\u7684\u9e70\u722a\u6d1e\u7a7f\u4ed6\u7684\u62f3\u5934\uff1f\u4ed6\u6839\u672c\u8fde\u60f3 \u90fd\u6ca1\u6709\u53bb\u60f3\u3002 \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u9ad8\u5174\uff0c\u4ec0\u4e48\u6837\u7684\u62db\u5f0f\u90fd\u80fd\u4f7f\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4f46\u522b\u4eba\u53ef\u6ca1\u6709\u8fd9\u4e48\u6837\u5927\u8def\uff0c\u4f55\u51b5\u6b66\u529f\u8bb2\u7a76\u7684\u672c\u662f\u62db\u5f0f\u7684\u53d8\u5316\u548c\u6280\u5de7\uff0c\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c \u8c01\u80af\u548c\u5bf9\u65b9\u786c\u62c6\u786c\u78b0\uff1f\u90ed\u5927\u8def\u4e00\u62f3\u51fb\u51fa\uff0c\u68cd\u5b50\u7684\u62db\u5f0f\u5df2\u53d8\uff0c\u8098\u4e00\u6c89\uff0c\u722a\u4e0a\u7ffb\uff0c\u5341\u6307\u5982\u94a9\uff0c\u5982 \u6293\u4f3c\u9501\uff0c\u51fb\u5411\u90ed\u5927\u8def\u7684\u8155\u90e8\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b80\u76f4\u8fde\u77a7\u90fd\u6ca1\u6709\u77a7\u89c1\uff0c\u62db\u5f0f\u8fde\u4e00\u70b9\u90fd\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u201c\u4e0d\u53d8\u5c31\u662f\u53d8\uff0c\u4ee5\u4e0d\u53d8\u5e94\u4e07\u53d8\u3002\u201d\u8fd9\u4e00\u7740\u6b63\u53c8\u662f\u6b66\u529f\u4e2d\u6700\u9ad8\u5999\u7684\u539f\u5219\u3002 \u3000\u3000\u68cd\u5b50\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u51e0\u4e4e\u5c31\u649e\u5230\u5899\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b80\u76f4\u53ef\u8bf4\u662f\u8fde\u4e00\u62db\u90fd\u6ca1\u6709\u5b8c\u5168\u4f7f\u51fa\uff0c\u5c31\u5df2\u5c06\u8fd9\u516d\u6247\u95e8\u91cc\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\u51fb\u9000\u4e86\u3002 \u3000\u3000\u4ed6\u5bf9\u81ea\u5df1\u5f88\u6ee1\u610f\uff0c\u4e5f\u6ca1\u6709\u8ffd\u51fb\u3002 \u3000\u3000\u201c\u4e58\u80dc\u8ffd\u51fb\u201d\u8fd9\u53e5\u8bdd\u4ed6\u5e76\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\u522b\u4eba\u65e2\u5df2\u793a\u5f31\u8ba4\u8f93\uff0c\u65e2\u7136\u5df2\u9000\u4e86\u4e0b\u53bb\uff0c\u53c8\u4f55 \u5fc5\u518d\u8ffd\u5462\uff1f\u8d76\u5c3d\u6740\u7edd\u8fd9\u79cd\u4e8b\u90ed\u5927\u8def\u662f\u4ece\u6765\u4e0d\u4f1a\u505a\u7684\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u5e72\u54b3\u4e24\u58f0\uff0c\u8fce\u4e86\u4e0a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f55\u5fc5\u751f\u8fd9\u4e48\u5927\u7684\u706b\u6c14\uff1f\u201d\u90ed \u5927\u8def\u9053\uff1a\u201c\u662f\u4ed6\u7684\u706b\u6c14\u5927\uff0c\u662f\u4ed6\u60f3\u6765\u63cd\u6211\uff0c\u6211\u54ea\u6709\u751a\u4e48\u706b\u6c14\uff1f\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u8bef\u4f1a\u8bef\u4f1a\uff0c\u5927 \u5bb6\u5168\u662f\u8bef\u4f1a\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f46\u4ed6\u95ee\u4e86\u6211\u534a\u5929\uff0c\u6211\u5012\u4e5f\u60f3\u95ee\u4ed6\u4e00\u53e5\u8bdd\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u8bf7 \u95ee\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7528\u91d1\u5b50\u6765\u4e70\u9152\u4e70\u8089\uff0c\u662f\u4e0d\u662f\u72af\u6cd5\uff1f\u201d\u91d1\u6bdb\u72ee\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u72af \u6cd5\uff0c\u6211\u4e5f\u5e38\u5e38\u7528\u91d1\u5b50\u6765\u4ed8\u5e10\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u72af\u6cd5\uff0c\u5c31\u8bf7\u4f60\u4eec\u653e\u8fc7\u9ea6\u8001\u5e7f\uff0c\u4e5f\u653e\u8fc7 \u6211\u5427\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u5f53\u7136\u5f53\u7136\u3002\u201d\u4ed6\u779f\u4e86\u95e8\u5916\u7684\u738b\u52a8\u3001\u71d5\u4e03\u3001\u548c\u6797\u592a\u5e73\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4eca\u5929 \u4e0b\u5348\u6211\u4eec\u5df2\u53e8\u6270\u4e86\u5404\u4f4d\u4e00\u987f\uff0c\u665a\u4e0a\u5c31\u7531\u6211\u6765\u505a\u4e1c\uff0c\u559d\u51e0\u676f\u5982\u4f55\uff1f\u201d\u90ed\u5927\u8def\u8fd8\u5728\u6c89\u541f\uff0c\u610f\u601d\u5df2 \u6709\u70b9\u6d3b\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u5012\u5e76\u4e0d\u662f\u559c\u6b22\u767d\u5403\uff0c\u53ea\u4e0d\u8fc7\u62d2\u7edd\u522b\u4eba\u7684\u8bdd\uff0c\u4ed6\u5b9e\u5728\u8bf4\u4e0d\u51fa\u53e3\u6765\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u73b0\u5728\u6211\u4ec0\u4e48\u90fd\u4e0d\u60f3\uff0c\u53ea\u60f3\u65e9\u70b9\u4e0a\u5e8a\u3002\u201d\u91d1\u6bdb\u72ee\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff01\u53cd\u6b63\u6211\u4eec\u65e9 \u5c31\u60f3\u5230\u5e9c\u4e0a\u62dc\u8bbf\u4e86\uff0c\u4e0d\u5982\u5c31\u4e58\u4eca\u591c\u4e4b\u4fbf\uff0c\u5230\u5e9c\u4e0a\u53bb\u505a\u4e00\u957f\u591c\u4e4b\u996e\uff0c\u56db\u4f4d\u7684\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u8fd9\u4e48 \u6837\u4e00\u8bf4\uff0c\u738b\u52a8\u4e5f\u6ca1\u6cd5\u5b50\u62d2\u7edd\u4e86\u2014\u2014\u516d\u6247\u95e8\u4e2d\u7684\u4eba\u8981\u5230\u4f60\u5bb6\u91cc\u53bb\u201c\u62dc\u8bbf\u201d\uff0c\u4f60\u80fd\u6709\u6cd5\u5b50\u62d2\u7edd \u4e48\uff1f\u4f55\u51b5\uff0c\u4ed6\u4eec\u82e5\u5230\u4e86\u5bcc\u8d35\u5c71\u5e84\u5c31\u4e0d\u80fd\u591f\u5728\u8fd9\u91cc\u6740\u4eba\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u5230\u4e86\u5bcc\u8d35\u5c71\u5e84\u3002 \u3000\u3000\u5e8a\u5e95\u4e0b\u7684\u79d8\u5bc6\u65e0\u8bba\u8c01\u5148\u542c\u5230\u201c\u5bcc\u8d35\u5c71\u5e84\u201d\u7684\u540d\u5b57\uff0c\u518d\u5230\u90a3\u91cc\u53bb\u514d\u4e0d\u4e86\u90fd\u8981\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u9ebd\u6837\u201c\u5bcc\u8d35\u201d\u7684\u5c71\u5e84\u5012\u4e5f\u7684\u786e\u5c11\u89c1\u7684\u5f88\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u6765\u975e\u4f46\u6ca1\u6709\u706f\u4e5f\u6ca1\u6709\u6cb9\u5e78\u597d\u6211\u4eca\u5929\u4ece\u5c71\u4e0b\u5e26\u4e86\u4e9b\u8721\u707f\u56de\u6765\uff0c\u5426\u5219 \u5927\u5bb6\u5c31\u53ea\u597d\u9ed1\u5403\u4e86\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u5176\u5b9e\u9ed1\u5403\u9ed1\u4e5f\u86ee\u6709\u8da3\u6015\u53ea\u4f2f\u5403\u5230\u9f3b\u5343\u91cc\u53bb\u3002\u201d\u4ed6\u672c\u6765\u56de\u5230 \u5bb6\u7b2c\u4ef6\u4e8b\u5c31\u662f\u8131\u6388\u4e88\u4e0a\u5e8a\uff0c\u4f46\u4eca\u5929\u5374\u8fde\u8d70\u90fd\u6ca1\u6709\u8d70\u8fc7\u53bb\u8fdc\u8fdc\u5c31\u5750\u4e86\u4e0b\u6765\u53c8\u9053\uff1a\u201c\u5404\u4f4d\u82e5\u4e0d\u5acc \u8d43\u5c31\u8bf7\u5750\u5230\u5730\u4e0a\u3002\u201d\u91d1\u6bdb\u72ee\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u53e4\u98ce\u6211\u4eec\u7684\u8001\u7956\u5b97\u672c\u5c31\u662f\u5750\u5728\u5730\u4e0a\u7684\u3002\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u6211\u4eec\u590d\u53e4\u7684\u7cbe\u795e\u6bd4\u8c01\u90fd\u5f7b\u5e95\xb7\u8fde\u7761\u90fd\u7761\u5728\u5730\u4e0a\u3002\u201d\u91d1\u6bdb\u6ca5\u9053\uff1a\u201c\u90a3\u5f20\u5e8a\u5462\uff1f\u201d\u8c01\u90fd\u4e0d \u613f\u610f\u4ed6\u4eec\u6ce8\u610f\u5230\u90a3\u5f20\u5e8a\uff0c\u53ef\u662f\u65e0\u8bba\u8c01\u8d70\u8fdb\u6765\u90fd\u6ca1\u6cd5\u5b50\u4e0d\u6ce8\u610f\u90a3\u5f20\u5e8a\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u5e8a\u662f\u6211\u4e2a\u4eba\u7761\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u56e0\u4e0d\u662f\u4ed6\u505a\u4e3b\u4eba\u7684\u5c0f\u6c14\u800c\u662f\u6211\u4eec\u7530 \u810f\u3002\u201d\u5c4b\u5b50\u91cc\u53ea\u6709\u4ed6\u4eec\u53c1\u4e2a\u4eba\u8bf4\u8bdd\uff0c\u6797\u592a\u5e73\u3001\u71d5\u4e03\u3001\u68cd\u5b50\u90fd\u6ca1\u6709\u5f00\u8fc7\u53e3\u90a3\u9ed1\u8863\u4eba\u66f4\u8fde\u95e8\u90fd\u6ca1 \u6709\u8fdb\u6765\u80cc\u8d1f\u7740\u624b\u7ad9\u5728\u9662\u5b50\u91cc\u3002\u4eff\u4f5b\u5df3\u548c\u8fd9\u9634\u68ee\u68ee\u7684\u9662\u5b50\u3001\u9634\u68ee\u68ee\u7684\u591c\u8272\u6eb6\u6210\u4e86\u2606\u7ec3\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u8fd9\u9ebd\u5546\u7684\u6b66\u529f\uff0c\u4e0d\u77e5\u662f\u90a3\u4e00\u95e8\u7684\u5546\u4eba\u4f20\u6388\u7684\uff1f\u201d\u4ed6\u81ea\u52a8\u5c06\u8bdd\u9898\u4ece \u201c\u5e8a\u201d\u4e0a\u79fb\u5f00\xb7\u522b\u4eba\u5f53\u7136\u66f4\u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u5012\u6709\u4e0d\u5c11\u6559\u51fa\u6765\u7684\u5f92\u5f1f\u5374\u53ea\u6709\u6211\u2606\u4e2a\u6c50\u91d1\u6bdb\u7530\u901a\uff1a\u201c\u4e0d\u77e5\u662f\u90a3\u51e0 \u4f4d\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u542f\u8499\u7684\u601d\u5e08\u662f\u2018\u795e\u62f3\u6cf0\u6597\u2019\u5218\u864e\u5218\u8005\u7237\u4e88\uff0c\u7136\u5f8c\u662f\u2018\u65e0\u654c\u5200\u2019\u6768\u658c\u6768\u4e8c \u7237\u4e88\u3001\u2018\u67aa\u523a\u4e5d\u9f99\u2019\u8d75\u5e7f\u8d75\u8001\u5e08\u3001\u795e\u5200\u94c1\u80f3\u81c2\u2019\u80e1\u5f97\u6768\u80e1\u5927\u7237\u2026\u3002\u201d\u91d1\u6bdb\u72ee\u77aa\u5927\u4e86\u773c\u775b\u5728\u542c \u7740\xb7\u4ed6\u540d\u5b57\u8bf4\u5f97\u8d8a\u591a\u91d1\u6bdb\u72ee\u7684\u773c\u775b\u77aa\u5f97\u8d8a\u5927\uff0c\u4eff\u4f5b\u5df2\u6014\u4efb\u3002 \u3000\u3000\u8fd9\u4e9b\u540d\u5b57\u4ed6\u5b9e\u5728\u8fde\u4e2a\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u6709\u6837\u72e0\u5999\u7684\u4e8b\u90a3\u5c31\u662f\u5916\u53f7\u8d77\u5f97\u8d85\u5413\u552c\u4eba\u7684\u6b66\u529f\u5f80\u5f80\u8d8a\u7a00\u677e\u5e73\u5e38\uff0c\u5c24\u5176\u662f\u201c\u67aa\u523a\u4e5d \u9f99\u201d\u3001\u201c\u795e\u5200\u94c1\u80f3\u7ba1\u201d\u8fd9\u4e00\u7c7b\u7684\u540d\u5b57\u66f4\u50cf\u662f\u5b9a\u6c5f\u6e56\u5356\u628a\u5f0f\u7684\u771f\u6b63\u7684\u540d\u5bb6\u5b97\u4e3b\u82e5\u662f\u8d77\u4e86\u4e2a\u8fd9\u9ebd \u6837\u7684\u540d\u5b57\uff0c\u5c82\u975e\u8981\u53eb\u4eba\u7b11\u6389\u5927\u7259\u3002 \u3000\u3000\u90ed\u5927\u8def\u597d\u4e0d\u5bb9\u6613\u624d\u628a\u8fd9\u4e9b\u54cd\u5f53\u5f53\u7684\u540d\u5b57\u8bf4\u5b8c\u4e86\u7b11\u9053\uff1a\u201c\u5bb6\u5e08\u4eec\u7684\u540d\u5b57\xb7\u4f60\u53ef\u542c\u8bf4\u8fc7\uff1f\u201d \u91d1\u6bdb\u72ee\u54b3\u55fd\u4e24\u58f0\uff1a\u201c\u4e45\u4ef0\u7684\u5f88\xb7\u54b3\u54b3\u4e45\u4ef0\u5f97\u5f88\u3002\u201d\u4ed6\u5ffd\u7136\u4e00\u59cb\u811a\u4eba\u5df2\u7a9c\u4e86\u8fc7\u53bb\u7a9c\u5230\u5e8a\u8fb9\u6293\u7740 \u5e8a\u6cbf\uff0c\u4eba\u8dc3\u8d77\u4e58\u52bf\u5c06\u5e8a\u4e5f\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u3001\u738b\u52a8\u3001\u71d5\u4e03\u3001\u6797\u592a\u5e73\uff0c\u56db\u4e2a\u4eba\u7684\u5fc3\u4f3c\u4e5f\u88ab\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5e8a\u4e0b\u7684\u4e94\u53e3\u7bb1\u5b50\u82e5\u662f\u88ab\u4eba\u53d1\u73b0\u4eca\u5929\u4ed6\u4eec\u5c31\u7b97\u80fd\u6321\u4f4f\u91d1\u6bdb\u72ee\u7684\u5200\u3001\u68cd\u5b50\u7684\u722a\u3001\u9ed1\u8863\u4eba\u7684\u957f \u5228\uff0c\u8fd9\u505a\u8d3c\u7684\u6c61\u540d\u6015\u662f\u518d\u4e5f\u6d17\u4e0d\u6389\u7684\u4ed6\u4eec\u7684\u5e74\u7eaa\u8fd8\u8f7b\u82e5\u662f\u80cc\u4e0a\u4e86\u505a\u8d3c\u7684\u9ed1\u94a5\uff0c\u5230\u51e0\u65f6\u624d\u80fd\u59cb \u5f97\u8d77\u5934\u6765\uff1f\u8c01\u77e5\u5e8a\u4e0b\u8fde\u4e00\u53e3\u7bb1\u5b50\u90fd\u6ca1\u6709\uff0c\u4ec0\u9ebd\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u9080\u4e0d\u4f4f\u8981\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u4f3c\u4e5f\u6014\u4e86\u6014\uff0c\u6162\u6162\u7684\u653e\u4e0b\u5e8a\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u521a\u660e\u660e\u770b\u5230\u5e8a\u5e95\u4e0b\u6709\u53ea\u8001\u9f20 \u7684\xb7\u600e\u9ebd\u5ffd\u7136\u5c31\u4e0d\u89c1\u4e86\u3002\u201d\u738b\u52a8\u51b7\u51b7\u9053\uff1a\u201c\u662f\u767d\u8001\u9f20\u8fd8\u662f\u9ed1\u8001\u9f20\uff1f\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u8fd9\u2026\u6211\u56e0 \u6ca1\u770b\u6e05\u695a\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u767d\u8001\u9f20\u5c31\u662f\u8d22\uff0c\u85cf\u91d1\u7684\u5730\u65b9\u5f80\u5f80\u4f1a\u6709\u767d\u8001\u9f20\u51fa\u73b0\xb7\u660e\u5929\u6211\u4ecd\u8981\u6316\u6316 \u770b\u3001\u8bf4\u4e0d\u5b9a\u8fd9\u4e0b\u4e3d\u57cb\u7740\u597d\u51e0\u7bb1\u91d1\u5b50\u4e5f\u672a\u53ef\u77e5\u3002\u201d\u4ed6\u8138\u4e0a\u8fd8\u662f\u51b7\u51b0\u51b0\u7684\uff0c\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u8198\u4e86\u4ed6\u773c\u9053\u201c\u91d1\u5144\u82e5\u80af\u7559\u4e0b\u6765\u8bf4\u4e0d\u5b9a\u4e5f\u53ef\u4ee5\u53d1\u4e2a\u5c0f\u8d22\u7684\u3002\u201d\u91d1\u6bdb\u72ee\u52c9\u5f3a\u7b11\u300f\u7f6e\uff1a \u201c\u4e0d\u5fc5\u4e86\uff0c\u6211\u8fd9\u4eba\u5929\u751f\u6ca1\u6709\u6a2a\u8d22\u8fd0\u3002\u201d\u8fd9\u5c4b\u4e8e\u73b0\u5728\u867d\u7956\u65e7\u6c34\u6765\u7684\u5efa\u5374\u8bb2\u7a76\u5f97\u5f88\xb7\u5730\u6291\u54fa\u7740\u6574 \u5757\u7684\u80b2\u77f3\u677f\u77f3\u677f\u7f1d\u4e2d\u90fd\u957f\u4e2d\u4e86\u8e7f\uff1f\u2606\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u80fd\u770b\u51fa\u8fd9\u4e9b\u77f3\u677f\u81f3\u5c11\u5df2\u6709\u5341\u5e74\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u68cd\u5b50\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u9189\u4e86\u544a\u8f9e\u4e86\u3002\u201d\u4ed6\u660e\u660e\u8fde\u4e00\u6ef4\u9152\u90fd\u6ca1\u6709\u559d\uff0c\u660e\u660e\u662f\u7741\u7740\u773c\u5728 \u8bf4\u778e\u8bdd\xb7\u4f46\u8c01\u4e5f\u4e0d\u60f3\u63ed\u7a7f\u4ed6\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u89c9\u5f97\u8fd9\u6062\u8bdd\u8bf4\u5f97\u5f88\u662f\u65f6\u5019\u3002 \u3000\u3000\u68cd\u5b50\u548c\u91d1\u6bdb\u72ee\u8d70\u4e86\u5f88\u4e45\uff0c\u90ed\u5927\u8def\u624d\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u6211\u4eec\u7684\u738b\u8001\u5927\u9ad8\u660e\u82e5\u4e0d \u662f\u4ed6\u628a\u7bb1\u5b50\u642c\u8d70\u6211\u4eec\u4eca\u5929\u5c31\u8981\u5f53\u5802\u51fa\u6765\u738b\u52a8\u9053\uff1a\u201c\u738b\u8001\u5927\u662f\u8c01\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5f53\u7136\u662f \u4f60\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u4f1a\u4e00\u4e2a\u4eba\u628a\u8fd9\u4e94\u53e3\u7bb1\u5b50\u642c\u8d70\uff0c\u518d\u85cf\u8d77\u6765\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u82e5\u662f\u738b\u52a8\u642c\u7bb1\u5b50\xb7\u5012\u4e0d\u5982\u8981\u7bb1\u5b50\u642c\u738b\u52a8\u4e5f\u8bb8\u53cd\u5012\u5bb9\u6613\u3002\u201d\u90ed\u5927\u8def\u6293\u7740\u5934\u76ae\uff0c\u9053\uff1a\u201c\u82e5\u4e0d \u662f\u4f60\xb7\u662f\u8c01\uff1f\u201d\u4ed6\u8f6c\u8fc7\u5934\uff0c\u5c31\u770b\u5230\u4e86\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u770b\u6211\uff0c\u6211\u4e5f\u672a\u5fc5\u6bd4\u738b\u8001\u5927\u52e4\u5feb\u591a\u5c11\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u4e00\u8f88\u5b50\u6ca1\u642c\u8fc7 \u7bb1\u5b50\u3002\u201d\u53cc\u624b\u53c8\u767d\u53c8\u7ec6\uff0c\u7b80\u76f4\u6bd4\u5c0f\u59d1\u5a18\u7684\u8138\u8fd8\u5ae9\u3002 \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u628a\u5934\u76ae\u90fd\u6293\u7834\u4e01\u5403\u6628\u9041\u201c\u4f60\u4eec\u65e2\u7136\u90fd\u6c72\u6709\u642c\u7bb1\u4e86\xb7\u90a3\u4e94\u95e8\u7bb1\u5b50\u96be\u906d\u662f\u81ea\u5df1\u957f \u817f\u8dd1\u8d70\u7684\u9ebd\uff1f\u738b\u52a8\u9053\uff1a\u201c\u7bb1\u5b50\u867d\u7136\u6ca1\u6709\u817f\u9178\u4fae\u6c64\u5374\u6709\u817f\u800c\u4e14\u4e00\u5b9a\u662f\u53cc\u5f88\u597d\u770b\u7684\u811a\u3002\u201d\u738b\u52a8\u8bf4 \u7684\u8bdd\u5f80\u5f80\u5c31\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u9664\u4e86\u9178\u6885\u6c64\u4e4b\u5916\xb7\u4ed6\u4eec\u5b9e\u5728\u60f3\u4e2d\u51fa\u8fd8\u80fd\u6709\u8c01\u77e5\u9053\u5e8a\u5e95\u4e0b\u6709\u4e94\u53e3\u7bb1\u5b50\xb7\u66f4\u6ca1\u6709\u522b\u4eba\u4f1a\u5c06\u7bb1 \u5b50\u642c\u8d70\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u73b0\u5728\u5979\u76ee\u7684\u5df2\u8fbe\u5230\u4e86\xb7\u81ea\u7136\u4e0d\u5fc5\u628a\u4e94\u5927\u7bb1\u5b50\u767d\u767d\u7559\u7ed9\u6211\u4eec\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a \u201c\u6240\u4ee5\u5979\u4e00\u770b\u5230\u6211\u4eec\u4e0b\u5c71\u5c31\u4e58\u673a\u628a\u7bb1\u5b50\u642c\u8d70\u3002\u201d\u738b\u52a8\u4f38\u4e86\u4e2a\u998f\u53e3\uff0c\u9053\uff1a\u201c\u642c\u8d70\u4e86\u53cd\u9762\u597d\uff0c\u5426 \u5219\u6211\u7684\u5e8a\u4e0a\u5207\u9053\u4e5f\u4e0d\u8212\u670d\u3002\u201d\u6797\u592a\u5e73\u8fdb\uff1a\u201c\u6211\u53ea\u5947\u602a\u5343\u4ef6\u4e8b\uff0c\u6211\u4eec\u660e\u660e\u8c01\u90fd\u6ca1\u6709\u5f80\u5e8a\u8fd9\u8fb9\u7530 \u8fc7\u773c\uff0c\u91d1\u6bdb\u72ee\u600e\u9ebd\u4f1a\u6000\u7591\u5230\u5e8a\u5e95\u4e0b\u6709\u6bdb\u75c5\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u6211\u4eec\u8c01\u90fd\u6ca1\u6709\u5f80\u5e8a\u8fd9\u8fb9 \u77a7\u8fc7\u4e00\u773c\u6240\u4ee5\u4ed6\u624d\u4f1a\u6000\u7591\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u7ed3\u8bba\u4e00\u4f53\u8d8a\u662f\u6545\u610f\u88c5\u7740\u5bf9\u4e00\u4ef6\u4e8b\u5168\u4e0d\u5173\u5fc3\u53cd\u800c\u663e\u5f97\u4f60\u5bf9\u5b83\u7279\u522b\u5173\u5fc3\u3002 \u3000\u3000\u5c24\u5176\u662f\u5973\u5b69\u5b50\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\u82e5\u662f\u5bf9\u522b\u4eba\u5168\u90fd\u5f88\u548c\u6c14\uff0c\u53ea\u6709\u5bf9\u4f60\u4e0d\u7406\u4e0d\u71e5\uff0c\u90a3\u4e5f\u8bb8\u5c31\u662f\u8bf4\u5979\u5fc3\u91cc\u6ca1\u6709\u522b \u4eba\uff0c\u53ea\u6709\u4f60\u3002 \u3000\u3000\u6797\u592a\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u72ee\u5b50\u72d7\u5012\u771f\u662f\u4e2a\u5389\u5bb3\u4eba\u7269\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u4eba\u8001\u5978\u5de8\u6ed1 \u7b11\u91cc\u85cf\u5200\u5b9e\u5728\u6bd4\u68cd\u5b50\u8fd8\u5389\u5bb3\u5f97\u591a\u3002\u201d\u90ed\u5927\u8def\u5df2\u6709\u5f88\u4e45\u6ca1\u8bf4\u8bdd\u4e86\uff0c\u6b64\u6e56\u5ffd\u7136\u9053\uff1a\u201c\u7bb1\u5b50\u7edd\u4e0d\u662f \u9178\u6885\u6c64\u642c\u8d70\u7684\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u662f\u5979\u662f\u8c01\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5979\u82e5\u8981\u5c06\u7bb1\u5b50\u642c\u8d70\xb7\u6628\u5929\u5c31\u6839\u672c \u4e0d\u4f1a\u7559\u4e0b\u6765\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8981\u628a\u90a3\u4e94\u53e3\u7bb1\u5b50\u642c\u51fa\u57ce\xb7\u4eca\u5929\u6bd4\u6628\u5929\u8fd8 \u56fa\u96be\u5f97\u591a\u5979\u4e3a\u4ec0\u9ebd\u6628\u5929\u4e0d\u642c\u4eca\u5929\u642c\uff1f\u5979\u96be\u9053\u4f1a\u662f\u5446\u5b50\uff1f\u201d\u71d5\u4e03\u51b7\u7b11\u9053\uff1a\u201c\u5979\u5f53\u7136\u4e0d\u662f\u5446\u5b50\u6211 \u624d\u662f\u6211\u5c31\u662f\u60f3\u4e0d\u51fa\u8fd8\u6709\u522b\u4eba\u4f1a\u6765\u642c\u7bb1\u5b50\u3002 \u3000\u3000\u201d\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u6211\u4e00\u63d0\u8d77\u9178\u6885\u6c64\u4f60\u5c31\u751f\u6c14\u96be\u9053\u4f60\u4e5f\u5077\u5077\u7684\u770b\u4e0a\u5979\u4e86\uff1f\u6211 \u628a\u5979\u8ba9\u7ed9\u4f60\u597d\u4e0d\u597d\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u8981\u4f60\u8ba9\uff1f\u5979\u96be\u9053\u662f\u4f60\u7684\uff1f\u201d\u535c\u529f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60 \u9178\u6885\u6c64\u8fd8\u6ca1\u6709\u5403\u5230\u840c\u9177\u5df3\u559d\u4e86\u51e0\u5927\u7897\xb7\u4ece\u82eb\u5462\uff1f\u201d\u4f3c\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u5f97\u5f88\u7279\u522b\u4e5f\u5f88\u597d\u770b\u3002 \u3000\u3000\u2606\u961f\u5f00\u59cb\u7b11\u7684\u65f6\u5019\u6709\u7684\u662f\u773c\u775b\u5148\u7b11\u6709\u7684\u8db3\u7792\u5148\u7b11\u6c60\u4ec0\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u5374\u662f\u9f3b\u5b50\u5148\u7b11\u9f3b\u5b50\u5149 \u8f7b\u8f7b\u7684\u76b1\u8d75\u70b9\u70b9\xb7\u7136\u5f8c\u9762\u671f\u516b\u518d\u9992\u9992\u7684\u73b0\u51fa\u4e24\u4e2a\u5f88\u6df1\u5f88\u6df1\u7684\u6d12\u7a9d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u8146\u7740\u4ed6\uff0c\u7483\u8d2d\u8c0a\uff1a\u201c\u5047\u5982\u8fd9\u5c0f\u5b50\u4e0d\u662f\u4e2a\u8fd9\u9ebd\u6837\u7684\u4eba\u6211\u8d70\u4f1a\u8ba4\u4e3a\u4ed6\u662f\u4e2a\u5973\u7684\u3002\u201d \u71d5\u4e03\u8ddf\u53c8\u77aa\u4e86\u8d77\u6765\u9053\uff1a\u201c\u6211\u82e5\u662f\u5973\u7684\xb7\u4f60\u5c31\u662f\u4e2a\u7530\u7530\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u77e5\u9053\u4f60\u7edd \u4e0d\u4f1a\u662f\u5973\u7684\u53ef\u662f\u4f60\u90a3\u7b11\u90a3\u9152\u7a9d\u2026\u2026\u201d\u71d5\u4e03\u9053\uff1a\u201c\u9152\u7a9d\u600e\u9ebd\u6837\uff1f\u9152\u7a9d\u7684\u610f\u601d\u53ea\u4e0d\u8fc7\u8868\u793a\u4f1a\u559d\u9152 \u4f60\u503c\u4e0d\u7530\uff1f\u201d\u90ed\u5927\u8def\u5ffd\u7136\u62c9\u8d77\u4e86\u4ed6\u7684\u624b\u9053\uff1a\u201c\u8d70\uff0c\u54b1\u4eec\u559d\u9152\u53bb\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u7530\u91cc\u559d\u9152 \u53bb\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0b\u5c71\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u9152\u8fd8\u6ca1\u6709\u559d\u5b8c\u4e3a\u4ec0\u9ebd\u8981\u5230\u5c71\u4e0b\u559d\uff1f\u201d\u90ed\u5927\u8def \u7728\u4e86\u7728\u963b\xb7\u9053\uff1a\u201c\u542c\u8bf4\u9ea6\u8001\u5e7f\u7684\u70e7\u70e4\u90fd\u662f\u4e2d\u591c\u505a\u7684\u6211\u60f3\u53bb\u5403\u4ed6\u65b0\u51fa\u7089\u7684\u70e7\u9e2d\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u6211\u6ca1\u6709\u4f60\u8fd9\u9ebd\u998b\u4f60\u4e00\u4e2a\u4eba\u53bb\u5427\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u4ece\u6765\u4e0d\u4e00\u4e2a\u4eba\u559d\u9152\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u8981\u4e0d\u7136\u4f60\u627e\u738b\u8001\u5927\u966a\u4f60\u53bb\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5c31\u62ff\u5200\u67b6\u5728\u4ed6\u8116\u5b50\u95e8\u4ed6\u4e5f\u4e0d\u4f1a\u4e0b\u5e8a \u4e86\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u4e0d\u53bb\u6211\u4e5f\u4e0d\u53bb\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u4e2a\u5927\u59d1\u5a18\u8ddf\u6211\u4e00\u9053\u53bb\u96be\u9053\u8fd8 \u4e0d\u653e\u5fc3\uff1f\u201d\u71d5\u4e03\u7684\u8138\u4eff\u4f5b\u7ea2\u4e86\u7ea2\u9053\u201c\u8bf4\u4e0d\u53bb\u5c31\u4e0d\u53bb\u4f60\u6b7b\u62c9\u4f4f\u6211\u5e72\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211 \u504f\u8981\u4f60\u53bb\u4e0d\u7ba1\u4f60\u662f\u7537\u662f\u5973\u6211\u90fd\u627e\u5b9a\u4f60\u4e86\u3002\u201d\u738b\u52a8\u53f9\u9053\u55e1\u770b\xb7\u4f60\u8fd8\u662f\u8ddf\u4ed6\u53bb\u5427\u9047\u89c1\u4e86\u4ed6\u8fd9\u79cd\u4eba \u53ea\u602a\u4f60\u4ea4\u53cb\u4e0d\u614e\u4f60\u82e5\u4e0d\u53bb\u7684\u8bdd\u8fde\u6211\u4e5f\u7761\u4e0d\u6210\u89c9\u3002\u201d\u71d5\u4e03\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5e78\u597d\u6211\u662f\u7537\u4eba\uff0c\u82e5 \u662f\u4e2a\u5973\u7684\u90a3\u624d\u771f\u53d7\u4e0d\u4e86\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u662f\u5973\u4eba\uff0c\u53d7\u4e0d\u4e86\u7684\u6050\u6015\u662f\u6211\u3002\u201d\u9047\u89c1\u90ed \u5927\u8def\u8fd9\u79cd\u4eba\uff0c\u7684\u786e\u8c01\u4e5f\u6ca1\u6cd5\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u6bd5\u7adf\u8fd8\u662f\u88ab\u4ed6\u62c9\u4e86\u51fa\u53bb\u521a\u8d70\u51fa\u5927\u95e8\u4e24\u4eba\u5c31\u6014\u4f4f\u3002 \u3000\u3000\u6b64\u523b\u5df2\u662f\u6df1\u591c\u8fd9\u5c71\u57ce\u4e2d\u7684\u4eba\u672c\u8be5\u90fd\u5df2\u7761\u4e86\u597d\u51e0\u89c9\u6709\u7684\u751a\u81f3\u5df2\u5feb\u8d77\u5e8a\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u5c71\u4e0b\u73b0\u5728\u5374\u8fd8\u662f\u706f\u706b\u901a\u660e\u90ed\u5927\u8def\u5230\u8fd9\u91cc\u5df4\u6709\u53c1\u4e2a\u6708\u4ece\u6765\u4e5f\u6ca1\u770b\u89c1\u5c71\u57ce\u91cc\u706f\u706b\u8d1d\u6b64\u660e \u4eae\u8fc7\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4eca\u5929\u96be\u9053\u5df2\u8fc7\u5e74\u4e86\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u597d\u50cf\u8fd8\u6ca1\u6709\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0d\u662f\u8fc7 \u5e74\u4e3a\u4ec0\u9ebd\u5982\u6b64\u70ed\u95f9\uff1f\u201d\u71d5\u4e03\u8d2d\u95fb\u9053\uff1a\u201c\u8fc7\u5e74\u7684\u65f6\u5019\u8fd9\u91cc\u6015\u4e5f\u6ca1\u6709\u5982\u6b64\u70ed\u95f9\u3002\u201d\u90ed\u5927\u8def\u53c8\u62c9\u8d77 \u4ed6\u7684\u624b\u9053\uff1a\u201c\u8d70\u6211\u4eec\u5feb\u53bb\u51d1\u70ed\u95f9\u53bb\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4f1a\u8d70\u8def\xb7\u4f60\u4e3a\u4ec0\u9ebd\u603b\u662f\u8981\u62c9\u4f4f\u6211\u7684 \u624b\uff1f\u201d\u90ed\u5927\u8def\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u613f\u610f\u6211\u62c9\u4f60\u7684\u624b\u4f60\u5c31\u62c9\u4f4f\u6211\u7684\u597d\u71d5\u4e03\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u770b \u6765\u6211\u53c8\u5f97\u6539\u540d\u5b57\u4e86\u53eb\u71d5\u516b\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u9047\u5230\u4f60\u8fd9\u79cd\u4eba\u6211\u975e\u518d\u6b7b\u4e00 \u6b21\u4e0d\u53ef\u3002\u201d\u9ea6\u8001\u5e7f\u548c\u4ed6\u7684\u70e7\u9e2d\u5b50\u5c71\u57ce\u91cc\u53ea\u6709\u53c1\u767e\u591a\u6237\u4eba\u5bb6\u73b0\u5728\u6bcf\u5bb6\u4eba\u90fd\u71c3\u8d77\u4e86\u706f\u800c\u4e14\u8fd8\u655e\u5f00 \u7740\u95e8\uff0c\u8c61\u662f\u5728\u8fce\u8d22\u795e\u7684\u6837\u5b50\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u4eec\u8fce\u63a5\u7684\u4e0d\u662f\u8d22\u795e\u800c\u662f\u761f\u795e\u3002 \u3000\u3000\u51e0\u5341\u4e2a\u6234\u7740\u7ea2\u7f28\u5e3d\u7a7f\u7740\u7682\u670d\u7684\u4eba\uff0c\u8170\u91cc\u4f69\u7740\u5200\u624b\u91cc\u4e3e\u7740\u706b\u628a\u6328\u5bb6\u6328\u6237\u7684\u641c\u67e5\u3002 \u3000\u3000\u71d5\u4e03\u548c\u90ed\u5927\u8def\u4e00\u4e0b\u5c71\u5c31\u9047\u89c1\u4e86\u91d1\u6bdb\u72ee\xb7\u8d1f\u624b\u7eb3\u5728\u8857\u5934\xb7\u547c\u6765\u558a\u53bb\u4f0a\u7136\u5c31\u50cf\u662f\u4e00\u4f4d\u5728\u6c99\u573a \u91cf\u6307\u63a2\u82e5\u5b9a\u7684\u5927\u5c06\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fce\u4e86\u4e0a\u53bb\u7b11\u9053\uff1a\u201c\u91d1\u5c06\u519b\u51c6\u5907\u5c06\u8fd9\u91cc\u8f9f\u4e3a\u6218\u573a\u9ebd\uff1f\u201d\u91d1\u6bdb\u72ee\u7684\u8138\u4e0a\u672c\u6765\u4eff\u4f5b\u5e26\u7740 \u5c42\u5bd2\u971c\uff0c\u770b\u5230\u4ed6\u6765\u4e86\uff0c\u624d\u6709\u4e86\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4e07\u4e0d\u5f97\u5df2\u5426\u5219\u6211\u4eec\u7edd\u4e0d\u6562\u60ca\u6270\u826f\u6c11\u7684\u3002\u201d \u71d5\u4e03\u9053\uff1a\u201c\u65e2\u7136\u660e\u77e5\u662f\u826f\u6c11\u53c8\u4f55\u5fc5\u60ca\u6270\uff1f\u201d\u91d1\u6bdb\u72ee\u53f9\u9053\uff1a\u201c\u6211\u4eec\u53ea\u77e5\u9053\u90a3\u6279\u8d43\u7269\u8fd8\u7559\u5728\u9547\u4e0a \u6ca1\u6709\u8fd0\u8d70\uff0c\u5374\u4e0d\u77e5\u662f\u85cf\u5728\u54ea\u5bb6\uff1f\u6240\u4ee5\u53ea\u597d\u5c06\u9644\u8fd1\u5341\u516b\u53bf\u7684\u5dee\u5f79\u6355\u5feb\u5168\u90fd\u8c03\u5230\u8fd9\u91cc\u6765\u6328\u6237\u8c03 \u67e5\u3002\u201d\u4ed6\u53c8\u7b11\u4e86\u7b11\u63a5\u7740\u9053\uff1a\u201c\u53ea\u8981\u80fd\u67e5\u51fa\u90a3\u6279\u8d43\u7269\u5728\u5a1c\u91cc\uff0c\u98ce\u6816\u68a7\u8fd9\u6b21\u5c31\u518d\u4e5f\u4f11\u60f3\u8dd1\u5f97 \u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\u9547\u4e0a\u6211\u8ba2\u4e5f\u8fdb\u4e0d\u53bb\u4e86\uff1f\u201d\u91d1\u6bdb\u72ee\u76ee\u5149\u95ea\u52a8\u9053\uff1a\u201c\u5982\u6b64\u6df1\u591c\u4e24\u4f4d \u8fd8\u8981\u9547\u4e0a\u53bb\u5e72\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u559d\u9152\u4e2d\u91d1\u6bdb\u7530\u9053\uff1a\u201c\u5230\u7231\u8005\u5e7f\u5e97\u91cc\u559d\u9152\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u54bd\xb7\u5c71\u4ea1\u7684\u9152\u5df2\u8d50\u5b8c\u4e86\uff0c\u6211\u4eec\u7684\u9152\u762a\u8fd8\u6ca1\u6709\u8fc7\u8db3\u3002\u201d\u91d1\u6bdb\u72ee\u7b11\u9053\u5207\u53e3\u5730\u65b9\u6211\u4eec\u4e0a\u534a\u591c\u5df2\u7ecf \u641c\u67e5\u8fc7\u4e86\uff0c\u53ea\u652b\u51fa\u4e86\u4e00\u952d\u91d1\u5b50\uff0c\u67c4\u4f4d\u89c4\u5728\u53ea\u7ba1\u53bb\u65e0\u59a8\uff0c\u8bf7\uff01\u201d\u4ed6\u5411\u8857\u4e0a\u5de1\u4e03\u7684\u6355\u5feb\u6253\u4e86\u4e2a\u5e73 \u5f0f\u81ea\u5df1\u4e5f\u8ba9\u5f00\u4e86\u8def\u3002 \u3000\u3000\u8d70\u8fc7\u53bb\u4e00\u6bb5\u8def\xb7\u71d5\u4e03\u624d\u7b11\u9053\uff1a\u201c\u770b\u6837\u5b50\u4ed6\u5bf9\u4f60\u56fe\u62a5\u5356\u5e10\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u6211 \u7684\u5e95\u7ec6\uff0c\u4ed6\u8fde\u4e00\u70b9\u4e5f\u6a21\u4e0d\u900f\u3002\u201d\u71d5\u4e03\u4e5f\u7b11\u4e86\xb7\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u90a3\u4e9b\u540d\u5b57\uff0c\u771f\u7684\u5168\u90fd\u662f\u4f60\u5e08\u5085\u201d \u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u4e9b\u4e00\u70b9\u4e5f\u4e0d\u5047\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u6b66\u529f\u867d\u4e5f\u4e0d\u592a\u600e\u9ebd\u6837\uff0c\u4f46\u4ed6\u4eec\u8fd8\u6559\u4e0d\u51fa\u4f60\u8fd9 \u6837\u7684\u5f92\u5f1f\u6765\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u5b66\u7684\u5e76\u4e0d\u662f\u4ed6\u4eec\u6b66\u529f\u7684\u957f\u5904\uff0c\u800c\u662f\u4ed6\u4eec\u6b66\u529f\u7684\u77ed\u5904\u3002\u201d\u71d5\u4e03 \u76b1\u7709\u9053\uff1a\u201c\u77ed\u5904\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u770b\u5230\u4ed6\u4eec\u6b66\u529f\u6709\u4ec0\u9ebd\u7834\u7efd\u7f3a\u70b9\u81ea\u5df1\u5c31\u5c3d\u91cf\u60f3\u6cd5\u5b50\u907f\u514d\u3002 \u8fd9\u5c31\u53eb\u53c1\u4eba\u884c\uff0c\u5fc5\u6709\u6211\u5e08\u65e0\u8bba\u613f\u4ec0\u9ebd\u4eba\u4f60\u90fd\u80fd\u5b66\u5f97\u70b9\u4e1c\u897f\u7684\u3002\u201d\u71d5\u4e03\u77aa\u4e86\u4ed6\u4e00\u773c\u9053\uff1a\u201c\u770b\u4e0d \u51fa\u4f60\u5012\u6709\u70b9\u5b66\u95ee\u3002\u201d\u90ed\u5927\u8def\u6b63\u8272\u9053\uff1a\u201c\u5728\u4f60\u9762\u524d\uff0c\u6211\u4e5f\u7528\u4e0d\u7740\u8c26\u865a\xb7\u6211\u7684\u5b66\u95ee\u672c\u6765\u5c31\u5927\u5f97 \u5f88\u3002\u201d\u71d5\u4e03\u53c8\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u957f\u5904\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u95ee\u8fc7 \u4f60\u9774\u5e95\u7684\u4e8b\u6ca1\u6709\uff1f\u95ee\u8fc7\u4f60\u600e\u9ebd\u6b7b\u4e86\u4e03\u6b21\u7684\u4e8b\u6ca1\u6709\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u4f60 \u4e3a\u4ec0\u9ebd\u8981\u95ee\u6211\uff1f\u201d\u9ea6\u8001\u5e7f\u662f\u4e2a\u8001\u5149\u68cd\uff0c\u5e97\u91cc\u5927\u5927\u5c0f\u5c0f\uff0c\u4e00\u5171\u53ea\u56db\u95f4\u623f\u3002 \u3000\u3000\u9609\u5c31\u662f\u524d\u9762\u7684\u5e97\u9988\uff0c\u95ee\u662f\u53a8\u623f\xb7\u4e00\u95f4\u662f\u4ed6\u7761\u89c9\u7684\u5730\u65b9\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u4e00\u95f4\u5728\u5458\u5f8c\u9762\u662f\u4ed6\u7684\u70e7\u70e4\u623f\u3002 \u3000\u3000\u8fd9\u95f4\u623f\u95e8\u603b\u662f\u5173\u7740\u7684\uff0c\u56e0\u4e3a\u9ea6\u8001\u5e7f\u7684\u70e7\u9988\u5364\u5473\u4e5f\u662f\u201c\u72ec\u95e8\u79d8\u65b9\u201d\u82e5\u662f\u88ab\u522b\u4eba\u5077\u5077\u5b66\u53bb \u4e86\xb7\u4ed6\u7684\u996d\u7897\u4e5f\u5c31\u5df4\u7834\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u4ed6\u4eec\u6765\u7684\u65f6\u5019\u9ea6\u8001\u5e7f\u6b63\u5728\u70e7\u70e4\u623f\uff0c\u623f\u95e8\u867d\u662f\u5173\u7740\u7684\u4f46\u4e00\u9635\u9635\u6251\u9f3b\u7684\u9999\u6c14\u5df2\u7ecf\u4ece\u95e8\u7f1d \u91cc\u8fdb\u51fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u54bd\u4e86\u53e3\u53e3\u6c34\u5927\u58f0\u9053\u201c\u8001\u5e7f\u751f\u610f\u4e0a\u95e8\u4e86\u8fd8\u4e0d\u5feb\u51fa\u6765\uff1f\u201d\u8fc7\u4e86\u534a\u8d2d\xb7\u9ea6\u8001\u5e7f\u624d\u8d70\u4e86\u51fa \u6765\uff0c\u6d51\u8eab\u90fd\u662f\u6cb9\xb7\u5c31\u597d\u50cf\u521a\u5728\u732a\u6cb9\u5806\u91cc\u76ef\u8fc7\u6e21\u3002 \u3000\u3000\u770b\u5230\u6717\u5927\u8def\uff0c\u4ed6\u4e0d\u9762\u987f\u7684\u8def\u4e0a\u624d\u6709\u4e86\u7b11\u5bb9\uff0c\u9053\u5c71\u4e89\u9647\u5927\u5bb6\u90fd\u7761\u4e0d\u6210\u5929\u5149\u65f6\u751f\u610f\u5b9a\u597d\u6240\u4ee5 \u6211\u7279\u5730\u591a\u70e4\u4e86\u51e0\u5341\u53ea\u9e2d\uff0c\u624d\u4f1a\u6bd4\u5e73\u65f6\u5fd9\u70b9\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8001\u5e7f\u4f11\u6ca1\u6709\u513f\u5b50\xb7\u53c8\u6ca1\u6709\u8001\u5a46 \u81ea\u5df1\u66f4\u662f\u7701\u5403\u4ed6\u7528\u8fde\u65b0\u8863\u670d\u90fd\u820d\u4e0d\u5f97\u6df1\u4e00\u4ef6\uff0c\u8d61\u8fd9\u9ebd\u591a\u94b1\u4e8e\u4ec0\u9ebd\uff1f\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u6211\u5730\u5462\u7684 \u6574\u65e5\u7cfb\u6cb9\u91cc\u6253\u6eda\u56db\u4eba\u8981\u65b0\u6749\u505a\u5305\u91ce\uff1f\u800c\u4e14\uff0c\u94b1\u7cfb\u4e0d\u6015\u591a\u56db\uff0c\u8d8a\u591a\u5c31\u8d8a\u66f4\u597d\u3002\u201d\u71d5\u4e03\u4e5f\u7b11 \u4e86\xb7\u8c0a\uff1a\u201c\u4ed6\u8bf4\u7684\u8fd9\u56fe\u662f\u8001\u5b9e\u8bdd\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u8001\u5b9e\u4eba\u5f53\u7136\u8bf4\u8001\u5b9e\u8bdd\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u9ea6\u8001 \u5e7f\u5012\u771f\u662f\u4e2a\u8001\u5b9e\u4eba\uff0c\u542c\u8bf4\u4ed6\u6765\u4e86\u5341\u51e0\u5e74\u8fde\u8d75\u5be1\u5987\u8d1e\u8282\u7891\u574a\u5f8c\u7684\u77f3\u5934\u5df7\u90fd\u6ca1\u6709\u53bb\u8fc7\u4e00\u6b21\u3002\u201d\u71d5 \u4e03\u9053\uff1a\u201c\u77f3\u5934\u5df7\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u77f3\u5934\u5df7\u662f\u4e2a\u597d\u5730\u65b9\u4e0d\u4f46\u7f8e\u5973\u5982\u4e91\uff0c\u800c\u4e14\u6e29\u67d4 \u4f53\u8d34\u3002\u201d\u71d5\u4e03\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u53bb\u8fc7\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u56e0\u5e76\u4e0d\u662f\u4e0d\u60f3\u53bb\uff0c\u53ea\u4e0d\u8fc7\u6bcf\u6b21 \u8d50\u9189\u4e86\u65f6\u5019\xb7\u5374\u90fd\u71d5\u4e03\u9053\uff1a\u201c\u6d88\u533b\u7684\u65f6\u5019\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u51fb\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6ed1\u919b\u7684\u65f6\u5019\u6211\u4e0d\u6562 \u53bb\u3002\u201d\u71d5\u4e03\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4f1a\u4e0d\u6562\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u53ea\u4f2f\u90a3\u4e9b\u7f8e\u5973\u89c1\u4e86\u6211\u8fd9\u6837\u7684\u7f8e\u7537\u5b50\u5c31\u518d \u4e5f\u4e0d\u80af\u653e\u6211\u8d70\u4e86\u3002\u201d\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u90a3\u79cd\u5730\u65b9\u504f\u504f\u8981\u8bbe\u5728\u4eba\u5bb6\u7684\u8d1e\u8282\u724c\u574a\u5f8c\u9762\u4f60 \u8bf4\u662f\u4e0d\u662f\u8981\u53eb\u4eba\u6d3b\u6d3b\u6c14\u6b7b\uff1f\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u8fd9\u9ebd\u6df1\u591c\u4e86\uff0c\u4e24\u4f4d\u8fd8\u8981\u94a6\u9152\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6 \u60f3\u6765\u5403\u4f60\u521a\u51fa\u7089\u7684\u6320\u9e2d\u3002\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u597d\u6211\u53bb\u62e3\u53ea\u80a5\u56db\u6765\u3002\u201d\u4ed6\u8f6c\u8eab\u8d70\u4e86\u8fdb\u53bb\xb7\u90ed\u5927\u8def\u5c45 \u7136\u4e5f\u5728\u5f8c\u9762\u6bb5\u7740\u9053\uff1a\u201c\u6211\u4e5f\u5230\u5f8c\u9762\u53bb\u7530\u77a7\u3002\u201d\u9ea6\u8001\u5e7f\u505c\u4f4f\u811a\u901a\uff1a\u201c\u5f8c\u9762\u53e3\u5708\u9080\u9047\u6709\u5305\u597d\u77a7\u201d \u90ed\u5927\u8def\u7406\uff1a\u201c\u6211\u4e0d\u6015\u810f\xb7\u53cd\u6b63\u6211\u5df2\u7ecf\u591f\u810f\u4e86\u3002\u201d\u71d5\u4e03\u53f9\u9053\uff0c\u201c\u4ed6\u82e5\u4e00\u5b9a\u8981\u53bb\xb7\u4f60\u6700\u70c1\u8fd8\u662f\u8ba9 \u4ed6\u53bb\u5427\uff0c\u5426\u5219\u4ed6\u5c31\u7b97\u56f4\u5230\u5f8c\u5929\u5927\u5929\u4eae\u4e5f\u662f\u975e\u53bb\u4e0d\u53ef\u7684\u3002\u201d\u9ea6\u8001\u5e7f\u4e5f\u7b11\u4e86\xb7\u9053\uff1a\u201c\u5f8c\u9762\u9ed1\u8ff7 \u817f\xb7\u4f60\u884c\u8def\u8981\u5c0f\u5fc3\u4e9b\u5440\u3002\u201d\u5f8c\u9762\u7684\u9662\u5b50\u679c\u7136\u5f88\u6444\u3002 \u3000\u3000\u70e7\u70e4\u623f\u5c31\u5728\u9662\u5b50\u7684\u5c3d\u5934\uff0c\u4e5f\u662f\u4e2a\u9ed1\u6487\u8d50\u7684\u5c61\u5b50\uff1b\u9ea6\u8001\u5e7f\u6b65\u5c65\u9633\u7530\xb7\u8d70\u5f97\u5f88\u6162\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8d70\u8def\u7684\u6837\u5b50\xb7\u597d\u50cf\u4e5f\u559d\u8fc7\u9152\u4f3c\u7684\u3002\u201d\u9ea6\u8001\u5e7f\u9053\uff1a\u201c\u4eca\u665a\u5929\u65f6\u51bb\xb7\u6211 \u53ea\u996e\u4e86\u4e24\u676f\u5df2\u7ecf\u597d\u4f3c\u6709\u70b9\u9189\u9189\u5730\u4ed6\u811a\u4e0b\u5ffd\u7136\u4e00\u4e2a\u8e6c\u8def\u8c61\u662f\u8981\u8dcc\u5012\u3002 \u3000\u3000\u90ed\u5927\u8def\u521a\u60f3\u4f38\u624b\u53bb\u6276\xb7\u8c01\u77e5\u9ea6\u8001\u5e7f\u5ffd\u7136\u4e00\u8f6c\u8eab\u5982\u868a\u9f99\u51fa\u6d77\u3001\u5982\u9e25\u4e8e\u56e0\u8eab\xb7\u5176\u77eb\u5065\u8f7b\u6377\uff0c \u7b80\u76f4\u65e0\u6cd5\u7528\u8a00\u8bed\u5f62\u5bb9\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u624b\u521a\u4f38\u51fa\uff0c\u5df2\u88ab\u4ed6\u6263\u4f4f\u4e86\u8109\u95e8\u3002 \u3000\u3000\u71d5\u4e03\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5e73\u65f6\u8fde\u8d70\u8def\u90fd\u4f3c\u8981\u8dcc\u5012\u7684\u7cdf\u8001\u5934\u5b50\u5ffd\u7136\u95f4\u53d8\u5f97\u5982\u6b64\u53ef\u6015\u5927\u60ca\u4e4b \u4e0b\xb7\u60f3\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u5df2\u6c89\u58f0\u665a\u9053\uff1a\u201c\u7ad9\u4f4f\u5426\u5219\u8981\u4ed6\u7684\u547d\u3002\u201d\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\u7adf\u662f\u6807\u51c6\u7684\u5317\u65b9\u53e3\u97f3\u8fde\u4e00\u70b9 \u5e7f\u4e1c\u5473\u90fd\u6ca1\u6709\u3002\u201d\u71d5\u4e03\u5446\u4f4f\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5c31\u662f\u2026\u2026\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u98ce\u6816\u68a7 \u5c31\u662f\u628a\u7bb1\u5b50\u4ece\u6211\u4eec\u5e8a\u5e95\u4e0b\u642c\u8d70\u7684\u4eba\u4f60\u96be\u9053\u8fd8\u60f3\u4e0d\u5230\uff1f\u201d\u4ed6\u4eba\u5df2\u88ab\u5236\u547d\u5728\u65e6\u5915\u5c45\u7136\u8fd8\u662f\u7b11\u7b11\u563b \u778e\u7684\u70b9\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u9ea6\u8001\u5e7f\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u98ce\u6816\u68a7\xb7\u4f60\u600e\u9ebd\u77e5\u9053\u7684\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u53ea\u4e0d\u8fc7 \u662f\u80e1\u4e71\u731c\u731c\uff0c\u56e0\u4e3a\u9664\u4e86\u68cd\u5b50\u3001\u91d1\u6bdb\u72ee\u3001\u9ed1\u8863\u4eba\u548c\u6211\u4eec\u56db\u4e2a\u4eba\u4e4b\u5916\u8fd9\u5730\u65b9\u5c31\u53ea\u6709\u4f60\u77e5\u9053\u6211\u4eec\u85cf \u6709\u91d1\u5b50\uff0c\u53ea\u6709\u4f60\u6709\u673a\u4f1a\u4e58\u6211\u4eec\u6162\u6162\u8d70\u4e0a\u5c71\u7684\u65f6\u5019\xb7\u5148\u8d76\u53bb\u5c06\u7bb1\u5b50\u642c\u8d70\u3002\u201d\u98ce\u6816\u68a7\u51b7\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u6709\u4f60\u65e2\u5df2\u88ab\u68cd\u5b50\u4ed6\u4eec\u2018\u51a4\u72c2\u2019\u8fc7\u4ed6\u4eec\u73b0\u5728\u5f53\u7136\u4e0d\u4f1a\u518d\u6000\u7591\u4f60\u4f55\u51b5\u4f60\u90a3\u9976 \u8f89\u623f\u8c01\u90fd\u4e0d\u80fd\u8fdb\u53bb\u7bb1\u5b50\u85cf\u5728\u90a3\u91cc\u771f\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u98ce\u6816\u68a7\u9053\uff1a\u201c\u8fd8\u6709\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u91d1\u6bdb\u72ee\u7684\u9f3b\u4e8e\u6700\u7075\u4ed6\u65e2\u5df4\u89c1\u8fc7\u4f60\xb7\u4f60\u8eab\u5e7f\u7684\u5473\u9053\u5c31\u7792\u4e0d\u8fc7\u4ed6\u7684\u9f3b\u5b50\u6240\u4ee5\u4f60\u624d\u6545\u610f\u6765\u4f5c \u8fd9\u884c\u751f\u610f\u3002\u201d\u4ed6\u8038\u9f3b\u5b50\u957f\u957f\u5438\u4e86\u53e3\u6c14\u624d\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u65e0\u8bba\u4efb\u4f55\u4eba\u8eab\u571f\u7684\u5473\u9053\xb7\u90fd\u7edd\u4e0d\u4f1a\u6709 \u7130\u7801\u90a3\u9ebd\u6d53\u7684\u5c31\u7b97\u6709\u72d0\u81ed\u7684\u5973\u4eba\u90fd\u4e0d\u4f8b\u5916\xb7\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u8fd8\u6709\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd8\u6709 \u6211\u542c\u8bf4\u98ce\u6816\u609f\u662f\u4e2a\u4e00\u6bdb\u4e0d\u62d4\u7684\u5c0f\u6c14\u9b3c\uff0c\u5c31\u7b97\u662f\u60c5\u6765\u7684\u94f6\u5b50\u90fd\u820d\u4e0d\u5f97\u82b1\u751a\u81f3\u8fde\u8005\u5a46\u90fd\u820d\u4e0d\u5f97\u5a36 \u4e00\u4e2a\u800c\u6211\u8fd9\u9635\u5b50\u89c1\u5230\u7684\u4eba\u518d\u4e5f\u6ca1\u6709\u6bd4\u4f60\u66f4\u5c0f\u6c14\u7684\u4e86\u3002\u653e\u7740\u65b0\u5e73\u539f\u9152\u8089\u820d\u4e0d\u5f97\u5403\u5374\u4e13\u95e8\u5403\u6211\u4eec \u5269\u4e0b\u7684\u5269\u83dc\u51b7\u996d\u3002\u201d\u4ed6\u5ffd\u7136\u7b11\u4e86\u63a5\u7740\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u53d1\u73b0\u4f60\u7684\u98ce\u6816\u609f\u7684\u540d\u5b57\u53d6\u5f97\u771f\u662f\u5999\u6781\u4e86 \u4eba\u5bb6\u6797\u901a\u662f\u6885\u59bb\u9e64\u5b50\uff0c\u4f60\u7684\u59bb\u4e88\u5c31\u662f\u4f60\u81ea\u5df1\uff0c\u6240\u4ee5\u53eb\u505a\u59bb\u543e\u2019\u3002\u201d\u4ed6\u4f3c\u4e4e\u5bf9\u81ea\u5df1\u7684\u5e7d\u9ed8\u611f\u6b23 \u8d4f\u6781\u4e86\u81ea\u5df1\u7b11\u788d\u773c\u6cea\u90fd\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u522b\u4eba\u90fd\u6ca1\u6709\u7b11\u4e5f\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u98ce\u6816\u609f\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\u7b49\u4ed6\u7b11\u5b8c\u624d\u51b7\u51b7\u9053\uff1a\u201c\u8fd8\u6709\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u8fd9\u4e9b\u5df2\u7ecf \u591f\u4e86\xb7\u53c1\u6837\u4e8b\u52a0\u8d77\u6765\xb7\u6240\u4ee5\u98ce\u987f\u609f\u5c31\u662f\u9ea6\u8001\u5e7f\u9ea6\u8005\u5e7f\u5c31\u662f\u98ce\u6816\u609f\u3002\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60 \u8fd9\u6837\u7684\u6df7\u5c0f\u65bc\u4e5f\u6709\u806a\u660e\u7684\u65f6\u5019\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6700\u7b28\u7684\u4eba\uff0c\u751f\u4e2d\u4e5f\u4f1a\u806a\u660e\u4e00\u4e24\u6b21\u7684\u4f55 \u51b5\u6211\u672c\u6765\u5c31\u662f\u4e2a\u5929\u624d\u53ea\u4e0d\u8fc7\u5076\u800c\u4f1a\u88c5\u88c5\u80e1\u6d82\u800c\u5df2\u3002\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u4f60\u60f3\u5230\u5c4b\u6211\u7684\u70e7\u70e4\u623f\u53bb\u662f \u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u672c\u6765\u662f\u60f3\u7684\u3002\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u597d\u8fdb\u53bb\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u672c\u6765\u867d\u60f3\uff0c\u73b0\u5728 \u5374\u4e0d\u60f3\u4e86\u3002\u56e0\u4e3a\u6211\u4e0d\u60f3\u88ab\u4eba\u5f53\u4f5c\u9e2d\u5b50\u540a\u5728\u67b6\u4e0a\u70e4\u3002\u201d\u98ce\u6816\u609f\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u73b0\u5728\u53bb\u4e0d\u53bb \u5df2\u7531\u4e0d\u5f97\u4f60\u6b79\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\u4e5f\u6ca1\u6709\u7528\xb7\u8fd8\u6709\u6211\u6211\u8fd8\u662f\u53ef\u4ee5\u628a\u4f60\u7684\u79d8\u5bc6\u4f20\u51fa\u53bb\u3002\u201d \u98ce\u6816\u609f\u9053\uff1a\u201c\u4ed6\u8fdb\u53bb\u4e86\u4f60\u81ea\u7136\u4e5f\u4f1a\u8ddf\u7740\u8fdb\u53bb\u7684\u56e0\u4e3a\u4f60\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6551\u4f60\u670b\u53cb\u7684\u673a\u4f1a\u6211\u6d3b\u4e86\u4e94\u516d \u5341\u5c81\u8fd9\u4e00\u70b9\u81f3\u5c11\u8fd8\u80fd\u770b\u5f97\u51fa\u3002\u201d\u71d5\u4e03\u54ac\u7740\u7259\u8fde\u773c\u775b\u90fd\u7ea2\u4e86\xb7\u83ab\u8bf4\u662f\u4e94\u516d\u5341\u5c81\u7684\u8001\u6c5f\u6e56\u5c31\u7b97\u662f \u4e8c\u5c81\u5927\u7684\u5b69\u5b50\u4e5f\u80fd\u770b\u5f97\u51fa\u4ed6\u5bf9\u90ed\u5927\u8def\u662f\u591a\u9ebd\u5173\u5fc3\u3002 \u3000\u3000\u90ed\u5927\u8def\u655e\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4eba\u751f\u5f97\u4e00\u77e5\u5df2\u6b7b\u800c\u65e0\u582a\u6709\u4f60\u8fd9\u6837\u7684\u597d\u670b\u53cb\u6b7b\u6d3b\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\u53ea\u4e0d \u8fc7\u2026\u2026\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u600e\u6837\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u6740\u6211\u4eec\u7684\u3002\u201d\u98ce\u6816\u609f \u9053\uff1a\u201c\u54e6\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u5c31\u7b97\u628a\u6211\u4eec\u4fe9\u4e2a\u5168\u6740\u4e86\u4e5f\u6ca1\u6709\u7528\u3002\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0d\u4f46\u738b\u8001\u5927\u77e5\u9053\u6211\u4eec\u8981\u5230\u4f60\u8fd9\u91cc\u6765\uff0c\u91d1\u6bdb\u72ee\u4e5f\u77e5\u9053\u6211\u4eec\u82e5\u662f\u7a81\u7136\u5931\u8e2a\u4e86\u4ed6\u4eec\u600e \u9ebd\u4f1a\u4e0d\u6000\u7591\uff1f\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u90a3\u662f\u4ee5\u5f8c\u7684\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u5728\u4e4e\u73b0\u5728\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d \u52a8\u624b\u6740\u6211\uff1f\u201d\u98ce\u6816\u609f\u9053\uff1a\u201c\u8fd9\u91cc\u53cd\u6b63\u4e0d\u4f1a\u6709\u4eba\u6765\u6211\u7528\u4e0d\u7740\u90a3\u9ebd\u6025\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709 \u52a8\u624b\u53ea\u56e0\u4f60\u8fd8\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5411\u662f\u4e2a\u5f88\u5c0f\u5fc3\u7684\u4eba\uff0c\u4e0d\u662f\u5341\u62ff\u4e5d\u7a33\u7684\u4e8b\uff0c\u4f60\u7edd\u4e0d\u80af \u505a\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u9053\uff1a\u201c\u53ea\u8981\u4f60\u653e\u4e86\u4ed6\xb7\u6211\u4eec\u4e5f\u8bb8\u53ef\u4ee5\u66ff\u4f60\u4fdd\u5b88\u79d8\u5bc6\u3002\u201d\u98ce\u6816\u609f\u76ee\u5149\u95ea\u52a8\u770b\u6765 \u5c31\u50cf\u662f\u4e00\u53ea\u8001\u72d0\u72f8\u3002\u201d\u8001\u72d0\u72f8\u7684\u6bdb\u75c5\u5c31\u662f\u592a\u7591\u4e0d\u4f46\u6000\u7591\u522b\u4eba\u4e5f\u6000\u7591\u81ea\u5df2\u3002 \u3000\u3000\u90ed\u5927\u8def\u60a0\u60a0\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5bf9\u65bc\u6293\u8d3c\u5e76\u6ca1\u6709\u5174\u8da3\u53ea\u4e0d\u8fc7\u4e0d\u559c\u6b22\u88ab\u4eba\u9a97\u800c\u5df2\u3002 \u3000\u3000\u201d\u53ea\u542c\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u8c01\u90fd\u4e0d\u559c\u6b22\u88ab\u4eba\u9a97\u7684\u3002\u201d\u8fd9\u662f\u91d1\u6bdb\u72ee\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8bed\u58f0\u4e2d\xb7\u91d1\u6bdb\u72ee\u3001\u68cd\u4e8e\u3001\u9ed1\u8863\u4eba\u5df2\u6162\u6162\u7684\u8d70\u8fdb\u4e86\u9662\u5b50\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\u56db\u9762\u7530\u5934\u706b\u628a\u9ad8\u4e3e\u51e0\u5341\u4e2a\u6355\u5feb\u5f13\u4e0a\u5f26\u5200\u51fa\u671f\xb7\u5df2\u5c06\u8fd9\u5c0f\u5c0f\u7684\u9662\u5b50\u56e2 \u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u98ce\u6816\u609f\u6ee1\u8138\u53d1\u5149\u4e5f\u4e0d\u77e5\u662f\u6cb9\uff1f\u662f\u6c57\uff1f\u7a81\u7136\u53cd\u624b\u4e00\u62a1\u3002 \u3000\u3000\u90ed\u5927\u8def\u767e\u628a\u65a4\u91cd\u7684\u8eab\u5b50\u5df3\u88ab\u4ed6\u62a1\u4e86\u51fa\u53bb\uff0c\u51b2\u5411\u91d1\u6bdb\u72ee\u548c\u90a3\u9ed1\u8863\u98ce\u6816\u609f\u7684\u4eba\u5c31\u50cf\u662f\u5df2\u53d8\u6210 \u4e86\u4e00\u6839\u7b4b\uff0c\u201c\u54e9\u201d\u7684\u5c04\u51fa\uff0c\u7728\u773c\u5df2\u63a0\u4e0a\u623f\u810a\u987a\u624b\u593a\u8fc7\u4e24\u628a\u5200\uff0c\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\u3002 \u3000\u3000\u5200\u5149\u4e00\u95ea\u95f4\xb7\u5df2\u6709\u4e24\u540d\u6355\u5feb\u81ea\u623f\u4e0a\u8dcc\u4e0b\xb7\u518d\u4e00\u95ea\u98ce\u6816\u609f\u8eab\u5f62\u5df2\u8fdc\u5728\u53c1\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8fd9\u95ef\u4e86\u51e0\u5341\u5e74\u6c5f\u6e56\xb7\u505a\u8fc7\u65e0\u6570\u4ef6\u5927\u6848\u7684\u5de8\u76d7\uff0c\u679c\u7136\u6709\u975e\u4eba\u80fd\u53ca\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u8eab\u6cd5\u5feb\uff0c\u51fa\u624b\u5feb\u800c\u4e14\u5584\u65bc\u628a\u63e1\u673a\u4f1a\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7b2c\u4e00\u4e2a\u673a\u4f1a\u4e5f\u662f\u4ed6\u6700\u5f8c\u4e00\u4e2a\u673a\u4f1a\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u91d1\u6bdb\u643a\u7684\u8f7b\u529f\u5c31\u7b97\u6bd4\u4ed6\u5f3a\xb7\u88ab\u51b2\u8fc7\u6765\u7684\u90ed\u4eba\u8def\u6321\u4e86\u6321\u4e5f\u662f\u4e07\u4e07\u8ffd\u4e0d\u4e0a\u4ed6\u7684\u3002 \u3000\u3000\u7a81\u542c\u4e00\u58f0\u4f4e\u9640\uff1a\u201c\u4e0b\u53bb\u3002\u201d\u623f\u810a\u5f8c\u7a81\u7136\u51fa\u73b0\u4e86\u4e24\u4e2a\u4eba\xb7\u6321\u4f4f\u4e86\u98ce\u6816\u609f\u7684\u53bb\u8def\u3002 \u3000\u3000\u5176\u4e2d\u6709\u4e2a\u4eba\u597d\u50cf\u53ea\u6325\u4e86\u6325\u624b\u51e4\u727a\u68a7\u5c31\u88ab\u9707\u51fa\uff0c\u5728\u623f\u810a\u56e0\u8dea\u5012\u9000\u539f\u8def\u9000\u56de\uff1a\u201c\u7830\u201d\u7684\u8dcc\u4e0b \u9662\u5b50\u521a\u597d\u8dcc\u5728\u90a3\u4e24\u540d\u6355\u5feb\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u623f\u810a\u5f8c\u7684\u4e24\u4e2a\u4eba\u8f7b\u8f7b\u4e00\u63a0\xb7\u4e5f\u5df2\u843d\u4eba\u9662\u4e2d\uff0c\u4e00\u4e2a\u9762\u5bb9\u51b7\u6478\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\u4e00\u4e2a\u65af\u65af\u6587\u6587\u79c0 \u6c14\u5f97\u5982\u5c11\u4e8e\u3002 \u3000\u3000\u738b\u52a8\u548c\u6797\u592a\u5e73\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u521a\u59a8\u7a33\u5c31\u62cd\u624b\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7684\u738b\u8001\u5927\u679c\u7136\u6709\u4e24\u4e0b\u5b50\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u662f\u6211\u3002\u201d\u4e0d \u662f\u4ed6\u81ea\u7136\u5c31\u662f\u6797\u592a\u5e73\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u4f3c\u7684\u4eba\u7adf\u6709\u8fd9\u9ebd\u5927\u7684\u672c\u4e8b\uff1f\u201d\u8c01\u4e5f\u770b\u4e0d\u51fa\u5374\u53c8\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u8fd9\u65f6\u98ce\u6816\u609f\u5df2\u88ab\u4eba\u8c61\u88f9\u7efc\u4e8e\u4f3c\u7684\u7ed1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u4ef0\u5929\u5410\u51fa\u53e3\u6c14\xb7\u7b11\u9053\uff1a\u201c\u8ffd\u8e2a\u4e86\u53c1\u5341\u5e74\xb7\u4eca\u5929\u603b\u7b97\u624d\u5c06\u8fd9\u6761\u8001\u72d0\u72f8\u6293\u4f4f\u3002\u201d\u90ed\u5927 \u8def\u9053\uff1a\u201c\u8d43\u7269\u4e00\u5b9a\u5c31\u5728\u70e7\u70e4\u623f\u91cc\xb7\u968f\u65f6\u53ef\u4ee5\u642c\u51fa\u6765\u3002\u201d\u91d1\u6bdb\u72ee\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u4eba\u8d43\u5e76\u83b7\u5f53 \u771f\u662f\u529f\u5fb7\u5706\u6ee1\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u7740\u8c22\u6211\u82e5\u662f\u4e00\u5b9a\u8981\u8c22\u5c31\u8c22\u8c22\u4ed6\u5427\u3002\u201d\u4ed6\u6307\u7740\u6797\u592a \u5e73\xb7\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u670b\u53cb\u957f\u5f97\u867d\u7136\u79c0\u91cc\u79c0\u6c14\u559d\u8d77\u9152\u6765\u90e8\u8c61\u662f\u4e2a\u5927\u6c34\u7f38\u3002\u201d\u91d1\u6bdb\u72ee\u773c\u775b\u8198\u7740\u68cd \u5b50\u9053\uff1a\u201c\u6211\u4eec\u53ef\u771f\u8be5\u8c22\u8c22\u4ed6\u4eec\u624d\u662f\u4f60\u8bf4\u8be5\u600e\u9ebd\u8c22\u5462\uff1f\u201d\u68cd\u5b50\u6c89\u7740\u8138\uff0c\u9053\uff1a\u201c\u62ff\u4e0b\u6765\u7edf\u7edf\u62ff\u4e0b \u6765\u3002\u201d\u90ed\u5927\u8def\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u9ebd\uff1f\u201d\u68cd\u5b50\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u56db\u4eba\u7a9d\u8d43\u6536\u8d43\u7eb5\u4e0d\u662f\u98ce\u6816 \u609f\u7684\u540c\u515a\u4e5f\u662f\u6c5f\u6d0b\u5927\u76d7\u7edf\u7edf\u7ed9\u6211\u4e94\u82b1\u5927\u7ed1\u5e26\u56de\u53bb\u4e25\u5211\u64ad\u95ee\u4e0d\u6015\u4ed6\u4e0d\u62db\u3002\u201d\u90ed\u5927\u8def\u7b80\u76f4\u809a\u5b50\u90fd \u8981\u6c14\u7834\u6c14\u6781\u4e86\u53cd\u800c\u7b11\u4e86\u9053\uff1a\u201c\u6211\u5012\u8981\u770b\u770b\u8c01\u6562\u6765\u52a8\u6211\u3002\u201d\u68cd\u5b50\u5389\u58f0\u9053\uff1a\u201c\u4f60\u6562\u62d2\u6355\u3002\u201d\u738b\u52a8 \u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d\u68cd\u5b50\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u6562\u8fd8\u4e0d\u67ec\u624b\u5c31\u7f1a\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u6211\u4eec\u867d\u4e0d\u6562\u62d2\u6355\u53ea\u53ef \u60dc\u4f60\u4e0d\u662f\u6355\u4fa0\xb7\u800c\u662f\u5f3a\u76d7\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6bd4\u5f3a\u76d7\u8fd8\u51f6\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f60\u4eec\u82e6\u82e6\u8ffd\u8e2a\u98ce\u6816 \u609f\xb7\u6839\u672c\u4e0d\u662f\u4e3a\u4e86\u4ed6\u7684\u4eba\xb7\u800c\u662f\u4e3a\u4e86\u4ed6\u7684\u94b1\u3002 \u3000\u3000\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4e2a\u6355\u5934\u6bcf\u6708\u7684\u65b0\u50a3\u6709\u591a\u5c11\uff1f\u80fd\u517b\u5f97\u8d77\u4f60\u4eec\uff1f\u5c31\u51ed\u91d1\u5927\u7237\u8eab\u4e0a\u7684\u8fd9\u5957\u8863\u670d\u53ea \u4f2f\u8fde\u5c06\u519b\u90fd\u7a7f\u4e0d\u8d77\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f55\u51b5\u8981\u96c7\u8fd9\u4f4d\u53ea\u4ec1\u5144\u8fd9\u6837\u7684\u804c\u4e1a\u6740\u624b\u82b1\u8d39\u4e5f\u4e0d\u5728\u5c11\uff0c\u5b98\u5bb6 \u81ea\u7136\u662f\u4e0d\u4f1a\u51fa\u8fd9\u79cd\u94b1\u7684\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f46\u8d3c\u8d43\u5374\u591a\u5f97\u6839\u5929\u4e0b\u5230\u5904\u6709\u8d3c\u6240\u4ee5\u8d3c\u8d43\u4e5f\u53d6\u4e4b\u4e0d\u5c3d\u7528 \u4e4b\u4e0d\u7aed\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u5c0f\u8d3c\u4e0d\u59a8\u62ff\u56de\u53bb\u9080\u529f\u9886\u8d4f\uff0c\u50cf\u51e4\u6816\u609f\u8fd9\u6837\u7684\u5927\u8d3c\xb7\u4e0d\u5982\u5c31\u7d22\u6027\u81ea\u5df1\u7559 \u4e0b\u4e86\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u50cf\u8fd9\u6837\u7684\u8d3c\u6293\u4e00\u4e2a\u81f3\u5c11\u53ef\u4ee5\u5403\u4e0a\u4e2a\u4e24\u53c1\u5e74\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f46\u7559\u7740\u6211\u4eec\u603b \u6709\u6cc4\u9732\u98ce\u58f0\u7684\u5929\u6240\u4ee5\u4e0d\u5982\u4e5f\u7d22\u6027\u6740\u4eba\u706d\u53e3\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u4eec\u505a\u7684\u4e8b\u867d\u7136\u6bd4\u5f3a\u76d7\u51f6\xb7\u4f46\u5374\u4e0d\u72af \u6cd5\u8fd9\u771f\u5999\u6781\u4e86\u3002\u201d\u738b\u52a8\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u9ed1\u5403\u9ed1\u53cd\u800c\u6709\u8da3\uff0c\u5c31\u6015\u5403\u5230\u9f3b\u5b50\u91cc\u53bb\u3002\u201d\u4e24\u4eba\u4e00\u642d\u4e00 \u6863\u8fde\u90ed\u5927\u8def\u548c\u6797\u592a\u5e73\u90fd\u542c\u5f97\u6014\u4f50\u4e86\u6c5f\u6e56\u5c0f\u8fd9\u79cd\u89c1\u4e0d\u5f97\u4eba\u7684\u52fe\u5f53\u4ed6\u61c2\u5f97\u5b9e\u5728\u6ca1\u6709\u71d5\u4e03\u4ed6\u4eec\u591a\u3002 \u3000\u3000\u68cd\u5b50\u51e0\u4e4e\u60f3\u53d1\u4f5c\u5374\u90fd\u88ab\u91d1\u6bdb\u72ee\u62e6\u4f4f\u3002 \u3000\u3000\u7b49\u4ed6\u4eec\u8bdd\u8bf4\u5b8c\u91d1\u6bdb\u72ee\u624d\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u7684\u4e00\u70b9\u4e5f\u4e0d\u9519\u6211\u5168\u90e8\u627f\u8ba4\u3002\u201d\u4ed6\u6307\u7740\u68cd\u5b50\u7b11\u9053\uff1a \u201c\u8fd9\u4eba\u5728\u5f00\u5c01\u3001\u6d1b\u9633\u6d4e\u5357\u3001\u5929\u6d25\u6bcf\u4e2a\u57ce\u91cc\u90fd\u6709\u4e2a\u5bb6\u6bcf\u4e2a\u5bb6\u91cc\u90fd\u6709\u8001\u5a46\u5355\u51ed\u4efd\u6355\u5934\u7684\u85aa\u6a1f\xb7\u80fd \u517b\u5f97\u8d77\u9ebd\u3002\u201d\u68cd\u5b50\u677f\u7740\u8138\u9053\uff1a\u201c\u4f60\u7684\u8001\u5a46\u4e5f\u5c0f\u6bd4\u6211\u5c11\u3002\u201d\u90ed\u5927\u8def\u6012\u9053\uff1a\u201c\u53ea\u53ef\u63aa\u4f60\u4eec\u8fd9\u4e9b\u8001 \u5a46\u773c\u770b\u90fd\u8981\u505a\u5be1\u5987\u4e86\u3002\u201d\u91d1\u6bdb\u72ee\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ee\u7ec6\u9053\u6211\u4e3a\u4ec0\u9ebd\u8981\u5c06\u8fd9\u4e9b\u4e8b\u8bf4\u7ed9\u4f60\u4eec\u542c\u3002\u201d\u4ed6 \u6307\u7740\u5899\u5934\u9053\uff1a\u201c\u8fd9\u5750\u6709\u3008\u5341\u5f20\u5f3a\u5f13\u56db\u5341\u628a\u5feb\u5200\xb7\u8fd9\u4e9b\u4eba\u90fd\u662f\u6211\u8fc7\u547d\u7684\u5144\u5f1f\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u653e\u4f60\u4eec \u8d70\uff1f\u201d\u68cd\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u4e71\u7bad\u7a7f\u5fc3\u800c\u6b7b\uff0c\u90a3\u6ecb\u6627\u53ef\u4e0d\u592a\u597d\u53d7\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u4f55\u51b5\u8fd8\u6709\u8fd9\u4f4d\u6211 \u4e0d\u60dc\u91cd\u8d44\u8bf7\u6765\u7684\u9ed1\u4ec1\u5144\u3002\u201d\u4ed6\u7b11\u4e86\u7b11\u63a5\u770b\u4e5f\uff1a\u201c\u4f60\u4eec\u5f53\u7136\u4e5f\u77e5\u8feb\u4ed6\u4e0d\u59d3\u9ed1\u4ed6\u90a3\u67c4\u5251\u4e2d\u5c11\u5c31\u53ef \u4ee5\u5bf9\u4ed8\u4f60\u4eec\u7f51\u4e8c\u4e2a\u6240\u4ee5\u6211\u770b\u4f60\u4eec\u4e0d\u5982\u8fd8\u662f\u542c\u8bdd\u4e9b\u597d\u81f3\u5c11\u6b64\u4e5f\u6b7b\u5f97\u75db\u5feb\u70b9\u3002\u201d\u90ed\u5927\u8def\u6012\u9053\uff1a \u201c\u653e\u4f60\u5988\u7684\u5c41\u3002\u201d\u91d1\u6bdb\u72ee\u53d8\u8272\u9053\uff1a\u201c\u5148\u6740\u4e86\u4ed6\u4ee5\u656c\u6548\u5c24\u3002\u201d\u9ed1\u8863\u4eba\u5458\u624b\u7ad9\u5728\u65c1\u8fb9\u6b64\u523b\u5ffd\u7136 \u9053\uff1a\u201c\u4f60\u8981\u8c01\u9489\u75ae\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u5f53\u7136\u662f\u4f60\u3002\u201d\u68cd\u5b50\u9053\u4e50\u2500\u4e2a\u591a\u52a0\u9ec4\u91d1\u53c1\u767d\u4e24\u3002\u201d\u9ed1\u8863\u4eba \u9053\uff1a\u201c\u597d\u201d\u4ed6\u5ffd\u7136\u53cd\u624b\u62d4\u5228\u5251\u4e00\u95ea\u5df2\u523a\u5165\u300f\u91d1\u4e03\u72ee\u7684\u80a9\u5934\u3002 \u3000\u3000\u4e0d\u662f\u957f\u5251\u662f\u77ed\u5251\u56db\u5c3a\u68c0\u66f2\u5251\u9798\u4e2d\u88c5\u7740\u7684\u7adf\u53ea\u4e2a\u8fc7\u662f\u67c4\u5c3a\u4e03\u5bf8\u957f\u7684\u77ed\u5251\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u672c\u6765\u4e5f\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u89d2\u8272\u4ed6\u4e2d\u4f46\u65e2\u60f3\u4e0d\u5230\u9ed1\u8863\u4eba\u4f1a\u5411\u4ed6\u51fa\u624b\uff0c\u66f4\u60f3\u4e0d\u5230\u6709\u8fd9\u9ebd \u77ed\u7684\u4e00\u67c4\u5251\u3002 \u3000\u3000\u68cd\u5b50\u5927\u60ca\u4e4b\u535c\u559d\u9053\uff1a\u201c\u5c04\u201d\u559d\u58f0\u4e2d\u4ed6\u8eab\u5f62\u5df2\u63a0\u8d77\u3002 \u3000\u3000\u4f46\u522b\u4eba\u600e\u9ebd\u4f1a\u653e\u4ed6\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u71d5\u4e03\uff0c\u4e24\u4e2a\u5f80\u4e0a\u4e00\u5939\xb7\u68cd\u5b50\u659c\u659c\u51b2\u51fa\u3002 \u3000\u3000\u738b\u52a8\u672c\u6765\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u73b0\u5728\u5ffd\u7136\u52a8\u4e86\u53ea\u52a8\u4e86\u4e00\u52a8\u3002 \u3000\u3000\u8fd9\u4e00\u52a8\u4e4b\u51c6\u4e4b\u5feb\xb7\u4e5f\u7b80\u76f4\u53eb\u4eba\u6ca1\u6cd5\u5b50\u5f62\u5bb9\u3002 \u3000\u3000\u68cd\u5b50\u53ea\u89c9\u773c\u524d\u4e00\u82b1\xb7\u81ea\u5df1\u7684\u624b\u4e0a\u5c31\u597d\u50cf\u5ffd\u7136\u591a\u4e86\u4ed8\u624b\u62f7\u3002 \u3000\u3000\u5899\u5934\u4e0a\u7684\u4eba\u547c\u5578\u4e00\u58f0\u629b\u5f13\u7684\u629b\u5f13\u4e22\u5200\u7684\u4e22\u5200\u7728\u773c\u95f4\u5c31\u9003\u5f97\u4e00\u4e2a\u4e0d\u5269\xb7\u4ed6\u4eec\u5f97\u5230\u7684\u597d\u5904\u8fd8 \u4e0d\u503c\u5f97\u4ed6\u4eec\u62fc\u547d\u3002 \u3000\u3000\u7136\u5f8c\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u77aa\u7740\u90a3\u9ed1\u8863\u4eba\u8c01\u4e5f\u5c0f\u77e5\u9053\u8fd9\u4eba\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\u3002 \u3000\u3000\u91d1\u6bdb\u72ee\u7684\u76ee\u4e2d\u66f4\u4f3c\u5df2\u8981\u5192\u51fa\u706b\u6765\u54ac\u7740\u7259\u9053\u201c\u4f60\u62ff\u4e86\u6211\u7684\u91d1\u5b50\u5374\u53cd\u8fc7\u6765\u54ac\u6211\u4e00\u53e3\u4f60\u8fd9\u79cd\u4eba \u7b80\u76f4\u8fde\u72d7\u90fd\u4e0d\u5982\u3002\u201d\u9ed1\u8863\u4eba\u6de1\u8c08\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u72d7\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u4e45\u95fb\u2018\u5251\u9540\u6e38\u9b42\u2019 \u5357\u5bab\u4e11\u662f\u6761\u597d\u6c49\uff0c\u8bf4\u4e00\u4e0d\u4e0b\u6240\u4ee5\u6211\u4eec\u624d\u4e0d\u60dc\u91cd\u91d1\u8bf7\u4f60\u6765\uff0c\u600e\u77e5\u7ec8\u65e5\u6253\u96c1\u7684\u4eba\u4eca\u65e5\u5012\u88ab\u96c1\u5544\u4e86 \u773c\u3002\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u672c\u6765\u5c31\u778e\u4e86\u773c\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u2026\u2026\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60 \u4ee5\u4e3a\u6211\u771f\u662f\u5357\u5bab\u4e11\uff1f\u201d\u91d1\u6bdb\u80f8\u9053\uff1a\u201c\u4f60\u4e0d\u8db3\u5357\u5bab\u4e11\u662f\u8c01\uff1f\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e5f\u662f\u4e2a\u4e13\u627e\u4eba\u9ebb\u70e6 \u7684\u4eba\u53ea\u4e0d\u8fc7\u8fd9\u6b21\u662f\u7279\u5730\u6765\u627e\u4f60\u4eec\u9ebb\u70e6\u7684\u3002\u201d\u91d1\u6bdb\u72ee\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\uff1f\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u7684 \u5934\u9876\u571f\u53f8\u63d0\u7763\u8001\u7237\u65e9\u5df3\u77e5\u9053\u4f60\u4eec\u6709\u6bdb\u75c5\u4e86\u6240\u4ee5\u7279\u5730\u8bf7\u6211\u6765\u8c03\u67e5\u8c03\u67e5\u4f60\u4eec\u7a76\u7adf\u662f\u4ec0\u9ebd\u82b1\u6837\u3002\u201d \u4ed6\u53d1\u51fa\u58f0\u77ed\u4fc3\u95f9\u5c16\u9510\u7684\u51b7\u7b11\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u4f60\u81ea\u5df1\u4f9b\u51fa\u4e86\u81ea\u5df1\u7684\u7f6a\u72b6\u771f\u51ed\u5b9e\u636e\u5168\u90fd\u6709\u4e86\u8fd9\u662f \u4e0d\u662f\u4e5f\u53eb\u505a\u4eba\u8d43\u5e76\u83b7\u3001\u529f\u5fb7\u5706\u6ee1\u201d\u91d1\u6bdb\u72ee\u77aa\u7740\u4ed6\uff0c\u518d\u4e5f\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u4e2a\u4eba\u8fd9\u624d\u5411\u4e0a\u52a8\u4ed6\u4eec\u62a4\u4e86\u62f1\u624b\u7b11\u9053\u201c\u65e0\u8bba\u90a3\u884c\u91cc\u90fd\u6709\u8d25\u7c7b\u540c\u95e8\u91cc\u4e0d\u4f8b\u5916\u4eec\u4e2d\u56db\u4f4d\u4e0b\u6b21\u89c1 \u5230\u6355\u5feb\u65f6\u83ab\u800d\u4ee5\u4e3a\u4eba\u4eba\u90fd\u548c\u4ed6\u4eec\u4e00\u6837\u3002\u201d\u90ed\u5927\u8def\u542b\u7b11\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\u6211\u4e5f\u51e0\u4e4e\u5c31\u505a\u8fc7\u6355 \u5feb\u3002\u201d\u56db\u8863\u4eba\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u5168\u4ed7\u7740\u56db\u4f9d\u4ed7\u4e49\u63f4\u624b\u8fd9\u53c1\u4e2a\u4eba\u6211\u89c2\u5b58\u5c31\u60f3\u5e26\u56de\u53bb\u4ea4\u5dee\u4e86\u3002\u201d\u71d5 \u4e03\u9053\uff1a\u201c\u8bf7\u4fbf\u3002\u201d\u90ed\u5927\u8def\u5ffd\u7136\u62cd\u4e86\u62cd\u98ce\u6816\u609f\u7684\u80a9\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u8fdb\u4e86\u76d1\u7262\u53cd\u800c\u4f1a\u66f4\u8212\u670d\u4e9b\u90a3\u4e9b \u5305\u7ba1\u4e00\u6587\u94b1\u90fd\u7528\u4e0d\u7740\u82b1\u3002\u201d\u98ce\u6816\u609f\u7ffb\u4e86\u7ffb\u767d\u670d\uff0c\u9664\u4e86\u7ffb\u767d\u773c\u5916\u4ed6\u8fd8\u80fd\u505a\u4ec0\u9ebd\u522b\u7684\uff1f\u201d\u9ed1\u8863\u4eba \u9053\uff1a\u201c\u81f3\u65bc\u8fd9\u8d3c\u8d43\u2026\u2026\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8d3c\u8d43\u81ea\u7136\u8be5\u5165\u5e93\u5145\u516c\u3002\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u4ef6\u6848\u5b50 \u672c\u8be5\u7b97\u56db\u4f4d\u7834\u7684\u5728\u60c5\u5728\u7406\uff0c\u90fd\u8be5\u4ece\u8d3c\u8d43\u91cc\u63d0\u51fa\u53c1\u6210\u6765\u4f5c\u4e3a\u5404\u4f4d\u7684\u916c\u52b3\u53ea\u8981\u56db\u4f4d\u80af\u968f\u6211\u5230\u5e9c\u57ce \u91cc\u53bb\u8d70\u8000\u2026\u3002\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\xb7\u738b\u52a8\u5df2\u62a2\u7740\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d\u4e3a\u4e86\u91d1\u5b50\u5c31\u8981\u4ed6\u8d70\u4e00\u8d9f\u56de\u8def\u6740\u4e86 \u4ed6\u7684\u5934\u4ed6\u4e5f\u4e0d\u5e72\u3002\u90ed\u5927\u8def\u3001\u71d5\u4e03\u3001\u6797\u592a\u5e73\u4e5f\u4e0d\u5e72\u3002\u5728\u4ed6\u4eec\u773c\u4e2d\u770b\u6765\uff0c\u4e16\u4e0a\u8fd8\u6709\u5f88\u591a\u4e8b\u90fd\u6bd4\u94b1 \u8d22\u91cd\u8981\u5f97\u591a\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u9664\u4e86\u5e26\u7ed9\u6211\u4eec\u4e0d\u5c11\u9ebb\u70e6\u5916\xb7\u522b\u7684\u4ec0\u9ebd\u7528\u90fd\u6ca1\u6709\uff0c\u9601\u4e0b\u53ea\u8981\u80af\u5c06 \u8fd9\u70e7\u70e4\u623f\u91cc\u7684\u9e2d\u5b50\u62e8\u7ed9\u6211\u4eec\u4f5c\u916c\u52b3\xb7\u6211\u4eec\u5df2\u9886\u60c5\u5f97\u5f88\u4e86\u3002\u201d\u9ece\u660e\u3002\u57ce\u91cc\u53c8\u6062\u590d\u5bc2\u9759\u98ce\u8fd8\u662f\u90a3 \u9ebd\u5439\uff0c\u96ea\u8fd8\u662f\u90a3\u9ebd\u843d\u3002\u4e16\u4e0a\u6709\u4e9b\u4e1c\u897f\u672c\u5c31\u4e0d\u662f\u5176\u4ed6\u4efb\u4f55\u4e8b\u6240\u80fd\u6539\u53d8\u8111\u3002\u6709\u4e9b\u4eba\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u9e2d\u5b50\u70e4\u5230\u73b0\u5728\uff0c\u6b63\u662f\u65f6\u5019\u3002\u90ed\u5927\u8def\u6495\u5f00\u53ea\u9e2d\u5b50\uff0c\u6b63\u5f85\u7834\u574f\u5927\u56bc\u3002\u5ffd\u7136\u95f4\u4e03\u516b\u5757\u6307\u5934\u822c\u5927 \u5c0f\u7684\u8e48\u7fe0\u4ece\u9e2d\u809a\u5b50\u91cc\u6389\u4e86\u4e0b\u6765\u3002\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5706\u4e86\u3002\u518d\u6495\u5f00\u9e2d\u5b50\u809a\u5b50\u91cc\u88c5\u7684\u662f\u739b\u8e48\u3002\u53c1\u56db \u5341\u53ea\u9e2d\u5b50\u5012\u6709\u535c\u6765\u53ea\u809a\u5b50\u91cc\u662f\u88c5\u7740\u4e1c\u897f\u7684\u3002 \u3000\u3000\u71d5\u4e03\u7728\u7740\u773c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u90ed\u5927\u8def\u8c0a\uff1a\u201c\u4f60\u660e\u767d\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u98ce\u6816\u609f\u672c \u6765\u662f\u60f3\u5c06\u503c\u94b1\u7684\u73e0\u5b9d\u85cf\u5728\u9e2d\u809a\u91cc\u8fd0\u8d70\u597d\u7792\u8fc7\u522b\u4eba\u7684\u8033\u76ee\uff0c\u8c01\u77e5\u5374\u88ab\u6211\u4eec\u95ef\u4e86\u53bb\uff0c\u6240\u4ee5\u4ed6\u53ea\u585e \u4e86\u4e00\u5c0f\u534a\u3002\u201d\u90ed\u5927\u8def\u9053\uff0c\u201c\u6709\u9053\u7406\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u90a3\u4f4d\u9ed1\u4ec1\u5144\u4e5f\u4e0d\u77e5\u9053\u8d3c\u8d43\u6709\u591a\u5c11\u5c31\u7b97\u6e05 \u70b9\xb7\u4e5f\u70b9\u4e0d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6709\u9053\u7406\u3002\u201d\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u88c5\u4ec0\u9ebd\u7cca\u5f90\u8fd9\u9053\u7406\u4f60\u65e9\u5c31\u77e5\u9053\u4e86\u3002\u201d \u90ed\u5927\u8def\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u77e5\u9053\u4e3a\u4ec0\u9ebd\u8981\u4eba\u5bb6\u628a\u9e2d\u5b50\u7559\u7ed9\u4f60\uff1f\u201d \u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u82e5\u4e00\u5b9a\u8981\u8fd9\u9ebd\u6837\u60f3\uff0c\u6211\u4e5f\u6ca1\u6cd5\u5b50\u3002\u201d\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53cd\u6b63\u5728 \u60c5\u5728\u7406\uff0c\u4ed6\u90fd\u5e94\u8be5\u63d0\u51fa\u53c1\u6210\u6765\u4f5c\u6211\u4eec\u916c\u52b3\u7684\u8fd9\u79cd\u94b1\u53d6\u4e0d\u4f24\u5ec9\u6211\u6253\u300f\u4e0d\u82b1\u4e5f\u662f\u767d\u4e0d\u82b1\u3002\u201d\u71d5\u4e03 \u76ef\u7740\u4ed6\u6447\u7740\u5934\u9053\uff1a\u201c\u6709\u65f6\u6211\u771f\u731c\u4e0d\u8fdc\u4f60\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u54e6\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6211\u5b9e\u5728\u731c\u4e0d\u51fa\u4f60 \u7a76\u7adf\u662f\u771f\u806a\u660e\uff1f\u8fd8\u662f\u771f\u7cca\u6d82\uff1f\u201d\u738b\u52a8\u60a0\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u7cca\u5f90\u65f6\u4ed6\u6015\u504f\u806a\u660e\u5f97\u5f88\uff0c\u4f60\u8bf4\u4ed6\u806a\u660e \u65f6\u4ed6\u53cd\u800c\u7cca\u5f90\u4e86\u3002\u201d\u8fd9\u4e5f\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u83e9\u8428\u548c\u81ed\u94b1\u662f\u7537\u4eba\u4e0d\u53ef\u7f3a\u5c11\u7684\u5973\u4eba\u4e5f\u662f\u3002 \u3000\u3000\u94b1\u80fd\u8471\u7978\u5973\u4eba\u9f99\u7684\u7978\u66f4\u591a\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u94b1\u8fd8\u6709\u4e00\u6837\u548c\u5973\u4eba\u76f8\u540c\u7684\u5730\u65b9\u6765\u5f97\u5bb9\u6613\u53bb\u5f97\u4e00\u5b9a\u4e5f\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5411\u8ba4\u4e3a\u81ea\u5df1\u662f\u4e2a\u5f88\u6709\u539f\u5219\u7684\u4eba\xb7\u65e0\u8bba\u505a\u4ec0\u9ebd\u4e8b\u90fd\u6709\u539f\u5219\u3002 \u3000\u3000\u4ed6\u5403\u9e2d\u5b50\u7684\u539f\u5219\u662f\uff1a\u201c\u6709\u8089\u7684\u65f6\u5019\u7edd\u4e0d\u5543\u9aa8\u5934\u6709\u76ae\u7684\u65f6\u5019\u7edd\u4e2a\u5403\u8089\u3002\u201d\u73b0\u5728\u9e2d\u5343\u7684\u76ae\u90fd \u5df3\u88ab\u5265\u5149\u4e86\u5265\u4e86\u76ae\u7684\u9e2d\u5b50\u770b\u6765\u5c31\u8c61\u662f\u4e2a\u4e94\u5c81\u7684\u5973\u4eba\u88ab\u5265\u5149\u4e86\u8863\u670d\u5ffd\u7136\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u81c3\u80bf\u53ef \u7b11\u3002 \u3000\u3000\u9e2d\u5b50\u5374\u8c61\u662f\u2606\u79fd\u7684\u5973\u4eba\u76ae\u5265\u5f97\u8d8a\u4e7e\u51c0\u5c31\u8d8a\u597d\u770b\u3002 \u3000\u3000\u5f88\u5c11\u4eba\u80fd\u4ece\u9e2d\u5b50\u8eab\u4e0a\u8054\u60f3\u5230\u5973\u4eba\u90ed\u5927\u8def\u80fd\u3002 \u3000\u3000\u9152\u5df2\u559d\u4e0b\u4ed6\u809a\u6237\u94b1\u5df2\u88c5\u8fdb\u4ed6\u888b\u7684\u65f6\u5019\u65e0\u8bba\u4ece\u4efb\u4f55\u4e1c\u897f\u4e0a\u4ed6\u90fd\u80fd\u7acb\u523b\u8054\u60f3\u5230\u5973\u4eba\u3002 \u3000\u3000\u73b0\u5728\u9152\u5df2\u559d\u5b8c\u73e0\u7acb\u4e5f\u5df2\u5206\u6210\u56db\u4efd\u3002 \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u2026\u2026\u6709\u4ec0\u9ebd\u6253\u7b97\uff1f\u201d\u4ec0\u9ebd\u6253\u7b97\uff1f\u8c01\u4e5f\u6ca1\u6709\u6253\u7b97\u3002 \u3000\u3000\u71d5\u4e03\u77aa\u7740\u4ed6\u9053\uff1a\u201c\u83ab\u975e\u4f60\u6709\u4ec0\u9ebd\u6253\u7b97\uff1f\u201d\u90ed\u5927\u8def\u773c\u775b\u76ef\u7740\u53ea\u5265\u4e86\u76ae\u7684\u9e2d\u5b50\u9053\uff1a\u201c\u5927\u5bb6\u90fd \u5df2\u7ecf\u95f2\u4e86\u5f88\u4e45\u4eca\u5929\u5f53\u7136\u90fd\u5e94\u8be5\u5de6\u6d3b\u52a8\u6d3b\u52a8\u5426\u5219\u9aa8\u5934\u53ea\u4f2f\u90fd\u8981\u751f\u9508\u4e86\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6211\u4eec\u7684\u9aa8 \u5934\u4e0d\u50cf\u4f60\u6709\u4e86\u51e0\u4e2a\u94b1\u5c31\u4f1a\u53d1\u75d2\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u53c8\u7b11\u4e86\u9053\uff1a\u201c\u5c31\u7b97\u6211\u662f\u8d31\u9aa8\u5934\u53cd\u6b63\u6211\u60f3\u53bb \u6d3b\u52a8\u6d3b\u52a8\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u5355\u72ec\u6d3b\u52a8\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u662f\u3002\u201d\u71d5\u4e03\u51b7\u7b11\xb7\u9053\uff1a\u201c\u6211 \u5c31\u77e5\u9053\u6709\u4e9b\u4eba\u53ea\u6709\u7a77\u7684\u65f6\u5019\u624d\u8981\u670b\u53cb\xb7\u6709\u4e86\u94b1\u82b1\u6837\u5c31\u6765\u4e86\u3002\u201d\u90ed\u5927\u8def\u77aa\u773c\u9053\uff1a\u201c\u4f60\u96be\u9053\u6ca1\u6709 \u5355\u72ec\u6d3b\u52a8\u8fc7\uff1f\u201d\u71d5\u4e03\u626d\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8981\u8d70\uff0c\u5c31\u8d70\u5427\u53c8\u6ca1\u6709\u4eba\u62c9\u4f50\u4f60\u3002\u201d\u90ed\u5927\u8def\u7ad9\u8d77\u6765\u53c8\u5750 \u4e0b\u7b11\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u5355\u72ec\u6d3b\u52a8\u4e2a\u4e00\u5929\u534a\u5929\u660e\u5929\u665a\u4e0a\u6211\u4eec\u518d\u89c1\u9762\u3002\u201d\u6ca1\u6709\u4eba\u7406\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u6413\u7740\u624b\u53c8\u9053\uff1a\u201c\u9ea6\u8001\u5e7f\u65e2\u5df2\u88ab\u6293\u53bb\u8fd9\u91cc\u5c31\u8fde\u5bb6\u597d\u9986\u5b50\u90fd\u6ca1\u6709\u4e86\xb7\u6211\u77e5\u9053\u53bf\u57ce\u91cc\u6709 \u5bb6\u594e\u5143\u9986\u9152\u83dc\u90fd\u4e0d\u9519\u597d\u5728\u53bf\u57ce\u4e5f\u4e0d\u8fdc\u660e\u5929\u6211\u4eec\u5c31\u5728\u90a3\u91cc\u89c1\u9762\u5982\u4f55\u2026\u2026\u6211\u8bf7\u5ba2\u3002\u201d\u8fd8\u662f\u6ca1\u6709\u4eba \u7406\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u6025\u4e86\xb7\u9053\uff1a\u201c\u96be\u9053\u6211\u8fde\u5355\u72ec\u6d3b\u52a8\u4e00\u5929\u90fd\u4e0d\u884c\u5417\uff1f\u201d\u738b\u52a8\u8fd9\u624d\u7ffb\u4e86\u4e2a\u81ea\u773c\u9053\uff1a\u201c\u8c01 \u8bf4\u4e0d\u884c\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u9ebd\u660e\u5929\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f60\u96be\u9053\u5c31\u4e0d\u80fd\u628a\u9152\u83dc\u4ece\u594e\u5143\u9986\u4e70 \u56de\u6765\u8bf7\u6211\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6c42\u6c42\u4f60\u4e0d\u8981\u8fd9\u9ebd\u61d2\u884c\u4e0d\u884c\uff1f\u4f60\u4e5f\u8be5\u53bb\u4e70\u51e0\u4ef6\u8863\u670d\u6362\u6362\u4e86\uff0c\u8fd9\u5957 \u8863\u670d\u518d\u7a7f\u4e0b\u53bb\u8fde\u4f60\u7684\u4eba\u90fd\u8981\u53d1\u9709\u3002\u201d\u738b\u52a8\u5ffd\u7136\u7ad9\u8d77\u6765\xb7\u6162\u6162\u7684\u5f80\u5916\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8981\u54ea\u91cc\u53bb\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u5230\u9ea6\u8001\u5e7f\u7684\u5e8a\u4e0a\u53bb\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u53bb\u5e72\u4ec0 \u9ebd\uff1f\u201d\u738b\u52a8\u53f9\u4e86\u95e8\u2018\u9053\uff1a\u201c\u5230\u5e8a\u4e0a\u53bb\u8fd8\u80fd\u5e72\u4ec0\u9ebd\uff1f\u5f53\u7136\u662f\u53bb\u7761\u89c9\u4f60\u5230\u5e8a\u4e0a\u53bb\u96be\u9053\u662f\u5e72\u522b\u7684\u4e8b \u9ebd\uff1f\u201d\u90ed\u5927\u8def\u7b11\u4e86\u4ed6\u7684\u786e\u662f\u60f3\u5e72\u522b\u7684\u4e8b\u53bb\u800c\u4e14\u7684\u786e\u662f\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u4ed6\u7ad9\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7761\u89c9\u4e5f\u597d\u53cd\u6b63\u660e\u5929\u8981\u5230\u53bf\u57ce\u53bb\u4e5f\u7701\u5f97\u518d\u56de\u5bb6\u8fd8\u8981\u6765\u56de\u7684 \u8dd1\u3002\u80fd\u5c11\u8d70\u4e00\u6bb5\u8def\u4e5f\u662f\u597d\u7684\u3002\u201d\u90ed\u5927\u8def\u8198\u4e0b\u71d5\u4e03\u4e00\u773c\u9053\uff1a\u201c\u4f60\u660e\u5929\u662f\u4e0d\u662f\u4e5f\u613f\u738b\u8001\u5927\u4e00\u8d77 \u53bb\uff1f\u201d\u6797\u592a\u5e73\u70b9\u70b9\u5934\u71d5\u4e03\u5374\u8c08\u6de1\u9053\uff1a\u201c\u6211\u4eca\u5929\u5c31\u8ddf\u4f60\u4e00\u8d77\u53bb\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u53ef \u662f\u2026\u2026\u6211\u2026\u2026\u201d\u71d5\u4e03\u4e5f\u77aa\u8d77\u4e86\u773c\xb7\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u6837\uff1f\u96be\u9053\u4e00\u6709\u4e86\u94b1\u5c31\u771f\u7684\u8fde\u670b\u53cb\u90fd\u4e0d\u8981 \u4e86\uff1f\u201d\u90ed\u5927\u8def\u4e00\u8def\u8d70\u4e00\u8def\u53f9\u7740\u6c14\u3002 \u3000\u3000\u71d5\u4e03\u7528\u773c\u89d2\u8198\u7740\u4ed6\xb7\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u56de\u4e8b\uff1f\u6709\u4ec0\u9ebd\u5730\u65b9\u4e0d\u8212\u670d\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e6\u7740\u8138\u9053\uff1a\u201c\u597d\u50cf\u5403\u574f\u4e86\xb7\u809a\u5b50\u6709\u70b9\u4e0d\u8212\u670d\u3002\u201d\u71d5\u4e03\u51b7\u51b7\u9053\uff1a\u201c\u6211\u770b\u4f60\u96be\u8fc7\u7684\u5730 \u65b9\u6050\u6015\u4e0d\u662f\u809a\u5b50\u5427\u3002\u201d\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u4ec0\u9ebd\u5730\u65b9\u96be\u8fc7\uff0c\u6211\u65e9\u5c31\u6e05\u695a\u5f97\u6839\u3002\u201d\u90ed\u5927 \u8def\u9053\uff1a\u201c\u4f60\u6e05\u695a\uff1f\u201d\u71d5\u4e03\u773c\u73e0\u5b50\u8f6c\u52a8\u9053\uff1a\u201c\u6709\u7ecf\u9a8c\u7684\u90fd\u77e5\u9053\u53e5\u8bdd\u53eb\u5355\u6f02\u53cc\u8d4c\u2019\u6211\u600e\u9ebd\u4f1a\u4e0d\u6e05 \u695a\u3002 \u3000\u3000\u201d\u90ed\u5927\u8def\u6014\u4e86\u534a\u5929\u53ea\u6709\u7b11\u4e86\u7b11\xb7\u82e6\u7b11\u7740\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u6487\u5f00\u4f60\u4eec\uff0c\u662f\u60f3\u4e00\u4e2a\u4eba\u6e9c\u53bb\u627e\u5973 \u4eba\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u96be\u9053\u6ca1\u6709\u8fd9\u610f\u601d\uff1f\u201d\u90ed\u5927\u8def\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\u613f\u7740\u4f60\xb7\u5c31\u56e0\u4e3a\u8981\u4f60\u5e26\u6211\u53bb\uff0c\u6211\u77e5\u9053\u4f60\u5728\u8fd9\u65b9\u9762\u4e00\u5b9a\u5f88\u6709\u7ecf\u9a8c\u662f\u4e0d \u662f\uff1f\u201d\u90ed\u5927\u8def\u201c\u5631\u201d\u4e86\u4e00\u58f0\u5ffd\u7136\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u53c8\u98ce\u6d41\u3001\u53c8\u8427\u6d12\u7684\u82b1\u82b1\u516c\u5b50\u5f53\u7136\u4e00\u5b9a\u77e5\u9053\u5728\u4ec0\u9ebd\u5730\u65b9\u80fd\u627e\u5230\u6700\u597d\u7684 \u5973\u4eba\u3002\u201d\u4ed6\u7528\u773c\u89d2\u8198\u7740\u90ed\u5927\u8def\uff0c\u53c8\u9053\uff1a\u201c\u5927\u5bb6\u65e2\u7136\u662f\u670b\u53cb\u4f60\u603b\u4e0d\u80fd\u4e0d\u6307\u70b9\u6211\u4e00\u6761\u660e\u8def\u5427\u3002\u201d \u90ed\u5927\u8def\u7684\u8138\u597d\u50cf\u5df2\u6709\u70b9\u53d1\u7ea2\xb7\u7530\u56ed\u9053\uff1a\u201c\u5f53\u7136\u5f53\u7136\u2026\u2026\u201d\u71d5\u4e03\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u4eec\u73b0\u5728\u8be5\u600e\u9ebd\u8d70 \u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5f53\u7136\u662f\u2026\u2026\u5148\u5230\u57ce\u91cc\u53bb\u518d\u8bf4\u3002\u201d\u71d5\u4e03\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u672c\u8be5\u628a\u738b\u8001 \u5927\u4ed6\u4eec\u4e5f\u4e00\u8d77\u627e\u6765\u7684\uff0c\u8ba9\u4ed6\u4eec\u4e5f\u597d\u5f00\u5f00\u9650\u754c\uff0c\u6211\u771f\u4e0d\u61c2\u4f60\u4e3a\u4ec0\u9ebd\u8981\u7792\u7740\u4ed6\u4eec\u3002\u201d\u90ed\u5927\u8def\u4e00\u70b9 \u4e5f\u4e0d\u7792\u522b\u4eba\uff0c\u4ed6\u672c\u89c9\u5f97\u627e\u5973\u4eba\u5e76\u4e0d\u662f\u4ec0\u9ebd\u4e22\u4eba\u7684\u4e8b\u3002 \u3000\u3000\u627e\u4e0d\u5230\u5973\u4eba\u624d\u4e22\u4eba\u3002 \u3000\u3000\u4ed6\u7792\u7740\u522b\u4eba\uff0c\u53ea\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u77e5\u9075\u5728\u4ec0\u9ebd\u5730\u65b9\u627e\u5f97\u5230\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u6839\u672c\u8fd8\u6ca1\u6709\u94b1\u8fc7\xb7\u5c31\u56e0\u4e3a\u8fd8\u6ca1\u627e\u8fc7\uff0c\u6240\u4ee5\u624d\u60f3\u7a33\uff0c\u6240\u4ee5\u624d\u60f3\u5f97\u8fd9\u9ebd\u5389\u5bb3\u3002 \u3000\u3000\u53bf\u57ce\u597d\u50cf\u5f88\u5feb\u5c31\u5230\u4e86\u3002 \u3000\u3000\u4e00\u8fdb\u57ce\uff0c\u71d5\u4e03\u5c31\u95ee\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u8be5\u600e\u9ebd\u8d70\u5462\u5f80\u54ea\u6761\u8def\u8d70\uff1f\u201d\u5341\u6b65\u4e4b\u5185\u5fc5\u6709\u82b3\u8349\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e8e\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5f80\u54ea\u6761\u8def\u4e0a\u8d70\u90fd\u4e00\u6837\u3002\u201d\u71d5\u4e03\u901a\uff1a\u201c\u90fd\u4e00\u6837\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u56db\u6761\u8def\u4e0a\u90fd\u6709\u5973\u4eba\u3002\u201d\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u6bcf\u6761\u8def\u4e0a\u90fd\u6709\u5973\u4eba\u4f46\u5973\u4eba\u5374\u6709\u5f88\u591a\u79cd\u95ee\u9898\u662f \u56db\u6761\u8def\u4e0a\u624d\u6709\u4f60\u8981\u627e\u7684\u90a3\u79cd\u5973\u4eba\uff1f\u201d\u90ed\u5927\u8def\u6309\u4e86\u6444\u6c57\u6307\u7740\u65c1\u8fb9\u4e00\u5bb6\u8336\u9986\u9053\uff1a\u201c\u4f60\u5148\u5230\u90a3\u91cc\u53bb \u7b49\u7740\xb7\u6211\u53bb\u66ff\u4f60\u627e\u6765\u3002\u201d\u71d5\u4e03\u7728\u7740\u773c\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u5728\u8fd9\u91cc\u7b49\xb7\u96be\u9053\u4e0d\u80fd\u6211\u4eec\u4e00\u8d77\u53bb\u90ed\u5927 \u8def\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u4f60\u5c31\u4e0d\u6a80\u4e86\u8fd9\u79cd\u5730\u65b9\u90fd\u5f88\u79d8\u5bc6\xb7\u8d8a\u79d8\u5bc6\u7684\u5730\u65b9\u8d8a\u7cbe\u91c7\u4f46\u82e5\u770b\u5230\u964c\u751f\u4eba\xb7\u5979\u4eec \u5c31\u4e0d\u80af\u6b79\u3002\u201d\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\xb7\u9053\uff1a\u201c\u597d\u5427\uff0c\u53cd\u6b63\u4f60\u662f\u8bc6\u9014\u8001\u9a6c\uff0c\u6211\u4ec0\u9ebd\u90fd\u5f97\u542c\u4f60\u7684\u3002\u201d\u770b\u7740 \u71d5\u4e03\u8d70\u8fdb\u8336\u9986\u90ed\u5927\u8def\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8c01\u77e5\u71d5\u4e03\u53c8\u56de\u8fc7\u5934\u5927\u58f0\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u4f60\u53ef\u4e0d\u80fd\u6e9c\u5440\u3002\u201d\u90ed\u5927\u8def\u4e5f\u5927\u58f0\u9053\uff1a\u201c\u6211 \u5f53\u7136\u4e0d\u4f1a\u6e9c\u7684\u3002\u201d\u4ed6\u7684\u786e\u4e0d\u60f3\u6e9c\uff0c\u53ea\u4e0d\u8fc7\u60f3\u5148\u5c06\u884c\u60c5\u6253\u542c\u6e05\u695a\u597d\u53eb\u71d5\u4e03\u4f69\u670d\u4ed6\u3002 \u3000\u3000\uff1a\u201c\u50cf\u6211\u8fd9\u6837\u53c8\u98ce\u6d41\u3001\u53c8\u8427\u6d12\u7684\u82b1\u82b1\u516c\u4e8e\u82e5\u8fde\u8fd9\u79cd\u5730\u65b9\u90fd\u627e\u4e0d\u5230\xb7\u5c82\u975e\u8981\u53eb\u71d5\u4e03\u7b11\u6389\u5927 \u7259\u800c\u4e14\u81f3\u5c11\u8981\u7b11\u4e0a\u4e2a\u53c1\u4e94\u5e74\u3002\u201d\xb7\u4ed6\u7528\u6700\u5feb\u7684\u901f\u5ea6\u8f6c\u8fc7\u8fd9\u6761\u8857\xb7\u524d\u9762\u7684\u6761\u8857\u597d\u50cf\u8fd8\u662f\u548c\u90a3\u6761 \u4e00\u6a21\u4e00\u6837\u6709\u8336\u9986\u3001\u6709\u5e97\u94fa\u3001\u6709\u7537\u4eba\uff0c\u5f53\u7136\u4e5f\u6709\u5973\u4eba\u3002 \u3000\u3000\uff1a\u201c\u4f46\u54ea\u4e2a\u624d\u662f\u6211\u8981\u627e\u7684\u90a3\u79cd\u5973\u4eba\u5462\uff1f\u201d\u4ed6\u770b\u6765\u770b\u53bb\xb7\u54ea\u4e2a\u90fd\u4e0d\u8c61\xb7\u6bcf\u4e2a\u5973\u4eba\u597d\u50cf\u90fd\u5f88 \u6b63\u7ecf\u3002 \u3000\u3000\uff1a\u201c\u5e72\u8fd9\u79cd\u4e8b\u7684\u4eba\u8138\u4e0a\u53c8\u4e0d\u4f1a\u63a5\u7740\u62db\u724c\u7684\u3002\u201d\u90ed\u5927\u8def\u7ad9\u5728\u8def\u65c1\xb7\u53d1\u4e86\u534a\u5929\u6014\xb7\u81ea\u5df1\u9f13\u52b1 \u81ea\u5df1\xb7\u5b89\u6170\u81ea\u5df1\uff1a\u201c\u53ea\u8981\u6709\u94b1\xb7\u8fd8\u4f2f\u627e\u4e0d\u5230\u5973\u4eba\uff1f\u201d\u4ed6\u51c6\u5907\u5148\u53bb\u4e70\u5957\u8863\u56e0\u518d\u8bf4\u3002\u201c\u4eba\u8981\u8863 \u88c5\uff0c\u4ecd\u8981\u91d1\u88c5\u201d\u7a7f\u5f97\u98ce\u5149\u4e9b\uff0c\u81f3\u5c11\u5148\u5360\u4e86\u53c1\u5206\u4fbf\u5b9c\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4e70\u8863\u670d\u7684\u94fa\u4e88\u597d\u50cf\u4e5f\u4e0d\u592a\u5bb9\u6613\u627e\u3002 \u3000\u3000\u4ed6\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u4e00\u5bb6\uff0c\u5ffd\u7136\u770b\u5230\u6709\u4e2a\u4eba\u5728\u91cc\u9762\u9009\u8863\u670d\u7adf\u662f\u71d5\u4e03\uff1a\u201c\u8fd9\u5c0f\u65bc\u5c45\u7136\u6ca1\u6709\u5728 \u8336\u9986\u91cc\u7b49\u6211\u3002\u201d\u53ea\u542c\u71d5\u4e03\u5728\u91cc\u9762\u7b11\u7740\u9053\u201c\u8700\u6700\u597d\u770b\u7684\u8863\u670d\u4ef7\u94b1\u8d35\u70b9\u6ca1\u6709\u5173\u7cfb\u4eca\u5929\u6211\u4e0e\u4f73\u4eba\u6709 \u7684\xb7\u8981\u7a7f\u5f97\u6c14\u6d3e\u4e9b\u3002\u201d\u90ed\u5927\u8def\u76b1\u8d77\u4e86\u7709\u5934\uff1a\u201c\u96be\u9053\u8fd9\u5c0f\u65bc\u53cd\u800c\u5148\u72ec\u5230\u8def\u4e86\u9ebd\uff1f\u201d\u770b\u5230\u71d5\u4e03\u6ee1 \u8138\u6625\u98ce\u7684\u6837\u5b50\u90ed\u5927\u8def\u4e0d\u7981\u53c8\u597d\u6c14\u53c8\u597d\u7b11\u3002 \u3000\u3000\uff1a\u201c\u65e2\u7136\u4f60\u4e0d\u4ec1\uff0c\u6211\u53c8\u4f55\u59a8\u4e0d\u4e49\uff0c\u73b0\u5728\u4f60\u603b\u4e0d\u80fd\u8bf4\u6211\u6e9c\u4e86\u5427\u3002\u201d\u4ed6\u51b3\u5b9a\u8fde\u8863\u670d\u90fd\u4e0d \u6362\xb7\u51b3\u5b9a\u6487\u5f00\u71d5\u4e03\u4e86\u3002 \u3000\u3000\uff1a\u201c\u59d0\u513f\u7231\u7684\u662f\u4fcf\xb7\u94a8\u513f\u7231\u7684\u662f\u949e\xb7\u6211\u65e2\u4fcf\u53c8\u6709\u949e\uff0c\u6362\u4e0d\u6362\u8863\u670d\u53c8\u4f55\u59a8\uff1f\u201d\u8fd9\u6761\u8857\u4e0a\u4e5f \u6709\u8336\u9986\u4e00\u4e2a\u4eba\u624b\u63d0\u7740\u9e1f\u7b3c\u65bd\u65bd\u7136\u4ece\u8336\u9986\u91cc\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u5e74\u7eaa\u5e76\u4e0d\u5927\u4f46\u4e24\u670d\u65e0\u5149\uff0c\u8138\u8272\u53d1\u80b2\xb7\u8138\u75b2\u52b3\u8fc7\u5ea6\u7684\u6837\u5b50\u800c\u4e14\u4efb\u4f55\u4eba\u90fd\u80fd\u770b\u5f97\u51fa\u4ed6\u662f \u4e8e\u4ec0\u9ebd\u75b2\u52b3\u8fc7\u5ea6\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u8d70\u8fc7\u53bb\u62b1\u62b1\u62f3\xb7\u7b11\u9053\uff1a\u201c\u6211\u59d3\u90ed\u6211\u77e5\u9053\u4f60\u4e0d\u8ba4\u5f97\u6211\u6211\u4e5f\u4e0d\u8ba4\u5f97\u4f60\xb7\u4f46\u73b0\u5728\u6211 \u4eec\u5df2\u7ecf\u8ba4\u5f97\u4e86\u3002\u201d\u4ed6\u505a\u4e8b\u559c\u6b22\u7528\u76f4\u63a5\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u4eba\u4e5f\u662f\u5728\u5916\u9762\u6e29\u8fc7\u7684\u6014\u4e86\u6014\u4e4b\u5f8c\xb7\u4e5f\u7b11\u4e86\u9053\uff1a\u201c\u90ed\u670b\u53cb\u6709\u4f55\u89c1\u6559\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u4eba\u4e0d\u98ce\u6d41\u6789\u5c11\u5e74\u8fd9\u53e5\u8bdd\u4f60\u60f3\u5fc5\u4e5f\u6709\u540c\u611f\u3002\u201d\u8fd9\u4eba\u9053\uff1a\u201c\u539f\u6765\u90ed\u5144\u662f\u60f3\u98ce\u6d41\u98ce\u6d41\u3002\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u6b63\u6709\u6b64\u610f\u53ea\u6068\u627e\u4e0d\u7740\u5165\u5929\u53f0\u7684\u8def\u800c\u5df2\u3002\u201d\uff01\u8fd9\u4eba\u7b11\u9053\uff1a\u201c\u90ed\u5144\u627e\u5230\u6211\u53ef\u771f\u662f\u627e\u5bf9\u4eba \u4e86\u3002\u4f46\u8981\u98ce\u6d41\u5c31\u5f97\u6709\u94b1\uff0c\u6ca1\u6709\u94b1\u662f\u8981\u88ab\u4eba\u6253\u51fa\u6765\u7684\u3002\u201d\u90ed\u5927\u8def\u88ab\u4eba\u6253\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u59d0\u513f\u5e76\u4e0d\u7231\u6084\u3002 \u3000\u3000\u59d0\u513f\u7231\u7684\u4e5f\u662f\u94b1\u3002 \u3000\u3000\u90ed\u5927\u8def\u5e76\u4e0d\u662f\u4e2a\u597d\u6b3a\u8d1f\u7684\u4eba\u7edd\u4e0d\u80af\u968f\u968f\u4fbf\u4fbf\u6328\u4eba\u6253\u7684\u3002\u53ef\u662f\u4ed6\u53c8\u600e\u9ebd\u80fd\u613f\u8fd9\u79cd\u5973\u4eba\u5bf9\u6253 \u5462\uff1f\u4ed6\u8180\u5b50\u4e0a\u88ab\u4eba\u54ac\u4e86\u4e24\u53e3\u5934\u4e0a\u4e5f\u88ab\u6253\u51fa\u4e86\u4e2a\u5305\u73b0\u5728\u4ed6\u4e00\u53ea\u624b\u6512\u7740\u5934\u4e0a\u7684\u8fd9\u4e2a\u5305\u4e00\u53ea\u624b\u8fd8\u5728 \u6478\u7740\u53e3\u888b\u3002 \u3000\u3000\u53e3\u888b\u662f\u7a7a\u7684\u6bd4\u4ed6\u7684\u809a\u5b50\u8fd8\u7a7a\u3002\u4ed6\u660e\u660e\u5c06\u90a3\u4efd\u73e0\u5b9d\u653e\u5728\u8fd9\u53e3\u888b\u91cc\u7684\u73b0\u5728\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u65e9\u4e0a\u5403\u7684\u9e2d\u76ae\u73b0\u5728\u90fd\u5df2\u6d88\u5316\u5f97\u5e72\u4e7e\u51c0\u51c0\xb7\u9152\u4e5f\u65e9\u5c31\u53d8\u6210\u4e86\u6c57\u3002 \u3000\u3000\u7b49\u5230\u5929\u9ed1\u65f6\u6c57\u90fd\u6d41\u5e72\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u627e\u4e86\u4e2a\u7834\u5e99\u5750\u5728\u795e\u6848\u524d\u671b\u7740\u90a3\u6ce5\u83e9\u8428\u53d1\u5f81\u3002\u6ce5\u83e9\u8428\u597d\u50cf\u4e5f\u6b63\u671b\u7740\u4ed6\u53d1\u5f81\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5df2\u8ba1\u5212\u5f97\u5f88\u597d\u51c6\u5907\u5148\u8212\u8212\u670d\u670d\u7684\u5403\u4e00\u987f\u518d\u8212\u8212\u670d\u670d\u7684\u6d17\u4e2a\u6fa1\u4ed6\u751a\u81f3\u5df2\u60f3\u8c61\u5230\u53cc\u4e94 \u624b\u66ff\u4ed6\u64e6\u80cc\u65f6\u7684\u8d61\u65bd\u98ce\u5149\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u5462\uff1f\u73b0\u5728\u66ff\u4ed6\u64e6\u80cc\u7684\u662f\u53ea\u81ed\u4e5f\u8bb8\u8fd8\u4e0d\u6b62\u4e00\u53ea\u4ed6\u5750\u7740\u7684\u84b2\u56e2\u5c31\u597d\u50cf\u662f\u81ed\u7684\u5927\u672c\u8425 \u597d\u50cf\u5168\u4e16\u754c\u7684\u5965\u65e9\u98a7\u5df2\u96c6\u4e2d\u5230\u8fd9\u91cc\xb7\u6b63\u4e00\u961f\u4e00\u961f\u7684\u94c0\u4eba\u4ed6\u7684\u8863\u670d\u51c6\u5907\u5728\u4ed6\u80cc\u4e0a\u5f00\u996d\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e00\u5df4\u638c\u6253\u4e0b\u53bb\u53ea\u6068\u4e0d\u5f97\u4e00\u5df4\u638c\u5c06\u81ea\u5df1\u6253\u6b7b\u7b97\u4e86\u3002\uff1a\u201c\u6211\u8fd9\u5165\u96be\u9053\u662f\u5929\u751f\u7684\u7a77\u547d\uff1f \u5c31\u4e0d\u80fd\u6709\u5929\u4e0d\u6328\u997f\u7684\uff1f\u201d\u4ed6\u5ffd\u7136\u53c8\u60f3\u5230\u4e86\u670b\u53cb\u7684\u597d\u5904\u3002\uff1a\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u4e00\u4e2a\u4eba\u5355\u72ec\u884c\u52a8\uff1f\u4e3a \u4ec0\u9ebd\u8981\u50b2\u5f00\u71d5\u4e03\u5462\u201d\u60f3\u5230\u4ed6\u4eec\u73b0\u5728\u4e00\u5b9a\u5728\u5927\u5403\u5927\u559d\u4ed6\u66f4\u997f\u5f97\u51e0\u4e4e\u8fde\u81ed\u90fd\u541e\u5f97\u4e0b\uff1a\u201c\u4e2a\u4eba\u7684\u786e \u5c0f\u8be5\u76df\u5f00\u4ed6\u7684\u670b\u53cb\u7684\xb7\u65e0\u8bba\u60f3\u5e72\u4ec0\u9ebd\u4e5f\u5f97\u8ddf\u670b\u53cb\u5728\u4e00\u8d77\uff0c\u9664\u4e86\u670b\u53cb\u5916\u4e16\u4e0a\u8fd8\u6709\u4ec0\u9ebd\u503c\u5f97\u73cd\u501f \u7684\u5462\uff1f\u201d\u90ed\u5927\u8def\u5ffd\u7136\u53d8\u5f97\u53c8\u73cd\u60dc\u53cb\u60c5\xb7\u53c8\u591a\u6101\u5584\u611f\u8d77\u6765\u65e0\u8bba\u8c01\u53c8\u7a77\u53c8\u997f\u7684\u65f6\u5019\xb7\u4ed6\u90fd\u4f1a\u53d8\u6210 \u8fd9\u6837\u4e88\u7684\u3002 \u3000\u3000\u5e78\u597d\u660e\u5929\u53c8\u8981\u548c\u4ed6\u4eec\u89c1\u9762\u4e86\uff0c\u4f46\u4ed6\u53ea\u5e0c\u671b\u65f6\u95ee\u8fc7\u7684\u8d8a\u5feb\u8d8a\u597d\u3002\uff1a\u201c\u6211\u8fd9\u9ebd\u6837\u60f3\u4ed6\u4eec\u4ed6\u4eec \u8bf4\u4e0d\u5b9a\u65e9\u5df3\u5fd8\u4e86\u6211\u738b\u52a8\u4e00\u5b9a\u65e9\u5df2\u547c\u547c\u5927\u7761\u71d5\u4e03\u8bf4\u4e0d\u5b9a\u6b63\u5728\u8ddf\u4ed6\u7684\u4f73\u4eba\u6253\u60c5\u9a82\u68a2\u3002\u201d\u60f3\u5230\u8fd9 \u91cc\xb7\u90ed\u5927\u8def\u53c8\u4e0d\u7981\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u5b9e\u5728\u662f\u4e2a\u5f88\u91cd\u53cb\u60c5\u7684\u4eba\u89c9\u5f97\u81ea\u5df1\u5bf9\u670b\u53cb\uff0c\u603b\u6bd4 \u670b\u53cb\u5bf9\u4ed6\u597d\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u53c8\u89c9\u5f97\u5b89\u6170\u5b89\u6170\u4e2d\u53c8\u5e26\u7740\u70b9\u4f24\u611f\u3002 \u3000\u3000\u8fd9\u79cd\u5fc3\u60c5\u4f7f\u4ed6\u6682\u65f6\u5fd8\u8bb0\u4e86\u522b\u7684\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u8ff7\u8ff7\u7cca\u6eaf\u7684\u7761\u7740\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\u90ed\u5927\u8def\u9192\u6765\u5c31\u51b3\u5b9a\u5148\u5230\u7ae5\u5143\u9986\u53bb\u7b49\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u5148\u5927\u5403\u4e00\u987f\xb7\u7b49\u4ed6\u7684\u670b\u53cb\u6765\u8ba8\u949e\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u9009\u5458\u597d\u7684\u5403\u6765\u8865\u507f\u8865\u507f\u8fd9\u591c\u53d7\u7684\u7f6a\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u6bcf\u4e00\u4e2a\u4eba\u90fd\u5e94\u8be5\u597d\u597d\u4e8b\u95ee\u5c1d\u8865\u507f\u56e0\u4e3a\u4ed6\u51e0\u4e4e\u5df2\u5fd8\u4e86\u81ea\u5df2\u662f\u4e3a\u4ec0\u9ebd\u53d7\u7684\u7f6a\uff0c\u4e3a\u4ec0 \u9ebd\u5403\u7684\u82e6\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u7684\u5934\u5df2\u997f\u5f97\u53d1\u6655\u660f\u660f\u8ff7\u8ff7\u4e2d\uff0c\u4ed6\u597d\u50cf\u89c9\u5f97\u81ea\u5df1\u8fd9\u4e00\u5207\u90fd\u662f\u4e3a\u4e86\u670b\u53cb\u800c\u727a\u7272 \u7684\u3002 \u3000\u3000\u4ed6\u5f88\u540c\u60c5\u81ea\u5df1\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u594e\u5143\u9986\u7684\u8001\u677f\u5e76\u4e0d\u8fd9\u6837\u60f3\u3002\u975e\u4f46\u6ca1\u6709\u5f00\u95e8\xb7\u8fde\u7a97\u5b50\u90fd\u6ca1\u6709\u5f00\uff0c\u90ed\u5927\u8def\u5f53\u7136\u4e0d\u4f1a\u602a \u81ea\u5df2\u6765\u5f97\u592a\u65e9\xb7\u53ea\u602a\u8fd9\u4e9b\u4eba\u5927\u998f\u4e3a\u4ec0\u9ebd\u5230\u73b0\u5728\u8fd8\u4e0d\u5f00\u95e8\u96be\u9053\u6210\u5fc3\u8ddf\u4ed6\u8fc7\u4e0d\u53bb\uff1f\u4e00\u4e2a\u997f\u5f97\u53d1\u6655 \u7684\u4eba\u901a\u5e38\u90fd\u4e0d\u592a\u8bb2\u7406\u7684\u4ed6\u6b63\u60f3\u53bb\u6572\u95e8\xb7\u5f8c\u9762\u5ffd\u7136\u6709\u4e2a\u4eba\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\u9053\u53fc\u65e9\xb7\u201d\u71d5\u4e03\u7a7f\u7740\u8eab \u5d2d\u65b0\u7684\u8863\u670d\u6ee1\u9762\u6625\u98ce\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u4e00\u526f\u5403\u5f97\u9971\u3001\u7761\u5f97\u8db3\u7684\u6837\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e00\u809a\u5b50\u6ca1\u597d\u6c14\u7530\u7740\u56e2\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u65e9\uff1f\u592a\u9633\u90fd\u6652\u5230\u5c41\u80a1\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u6625\u5bb5\u4e00\u523b\u503c \u5343\u91d1\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8eba\u5728\u7f8e\u4eba\u539f\u4e0a\u591a\u7530\u56ed\u592a\u9633\u5462\uff1f\u2019\u90ed\u5927\u8def\u9053\uff1a\u201c\u90a3\u91cc\u81ed\u592a\u591a\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u81ed\uff1f\u7f8e\u4eba\u7a9d\u91cc\u600e\u9ebd\u4f1a\u6709\u81ed\uff1f\u201d\u90ed\u5927\u8def\u4e5f\u53d1\u89c9\u81ea\u5df2\u8bf4\u56ed\u7530\u4e86\uff0c\u54b3\u55fd\u4e86\u4e24\u58f0\xb7\u7530\u66f0\u7b11\u9053\uff1a\u201c\u5e76 \u4e0d\u662f\u771f\u7684\u81ed\uff0c\u53ea\u4e0d\u8fc7\u5979\u90a3\u53cc\u624b\u8001\u662f\u5728\u6211\u8eab\u4e0a\u722c\u6765\u722c\u53bb\uff0c\u6bd4\u81ed\u8fd8\u8ba8\u538c\u3002\u201d\u71d5\u4e03\u7728\u4e86\u7728\u773c\u6447\u5934\u53f9 \u606f\u9053\uff1a\u201c\u6700\u96be\u6d88\u53d7\u7f8e\u4eba\u601d\uff0c\u4f60\u771f\u662f\u6709\u798f\u4e0d\u4f1a\u4eab\u6211\u60f3\u627e\u4e2a\u81ed\u5728\u6211\u8eab\u4e0a\u722c\u722c\u8fd8\u627e\u4e0d\u5230\u54e9\u3002\u201d\u90ed\u5927 \u8def\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u4ed6\u4e5f\u60f3\u7b11\u5f97\u5f00\u5fc3\u4e9b\xb7\u4f46\u58f0\u97f3\u5374\u504f\u4f2f\u8c61\u662f\u4ece\u9a74\u8116\u5b50\u91cc\u53d1\u51fa\u6765\u7684\uff0c\u597d\u8c61\u5b58\u53ea\u811a\u7740\u4e86\u9a74\u8116\u4e88\u3002 \u3000\u3000\u71d5\u4e03\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u77a7\u7740\u4ed6\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u809a\u5b50\u53c8\u4e0d\u597d\u816e\u4e86\uff1f\u4e00\u5b9a\u4e49\u5403\u5f97\u592a\u9971\u3002 \u3000\u3000\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u54bd\u3002\u201d\u71d5\u4e03\u5403\u5403\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u59d1\u5a18\u65e2\u7136\u5bf9\u4f60\u8fd9\u6837\u597d\uff0c\u4e00\u5b9a\u4eb2\u81ea\u4e0b\u7530\u5c45\u7279 \u522b\u505a\u4e86\u4e0d\u5c11\u597d\u4e1c\u897f\u7ed9\u4f60\u5403\u597d\u8ba9\u4f60\u8865\u8865\u5143\u6c14\u3002\u201d\u90ed\u5927\u8def\u51b7\u51b7\u7530\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\u4e2d\u7530\u4e0d\u5230\u4f60\u5ffd\u7136\u4e5f \u53d8\u5f97\u5f88\u6709\u7ecf\u9a8c\u71d5\u4e03\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u600e\u9ebd\u6709\u4f24\u8fd9\u9ebd\u597d\u7684\u798f\u6c14\u5462\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u6628\u5929 \u665a\u4e0a\u5230\u7530\u91cc\u53bb\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u8fd8\u597d\u610f\u601d\u95ee\u6211\uff0c\u6211\u5728\u8336\u9986\u91cc\u7b49\u5f97\u53d1\u660f\xb7\u8fde\u4f60\u7684\u9b3c\u5f71\u5b50\u787c\u6ca1 \u7b49\u7740\u53ea\u597d\u4e00\u4e2a\u4eba\u5b64\u9b42\u91ce\u9b3c\u80f6\u5230\u5904\u4e71\u901b\uff0c\u5dee\u70b9\u8fde\u7761\u89c9\u7684\u5730\u65b9\u90fd\u627e\u4e0d\u5230\u3002\u201d\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u65bc\u4e5f\u4f1a \u88c5\u849c\u3002\u201d\u90ed\u5927\u8def\u6068\u5f97\u7259\u75d2\u75d2\u7684\u504f\u504f\u53c8\u4e0d\u80fd\u62c6\u7a7f\u4ed6\u7684\u628a\u620f\uff0c\u53ea\u597d\u563f\u563f\u7684\u7b11\u9053\u201c\u8c01\u53eb\u4f60\u6c72\u8010\u5fc3\u591a \u7b49\u7b49\u7684\u5bb3\u5f97\u6211\u4e2a\u4eba\u8981\u5e94\u8ba8\u597d\u51e0\u4e2a\u5927\u59a8\u59d0\u7b80\u76f4\u70e6\u5f97\u6211\u8981\u547d\u3002\u201d\u71d5\u4e03\u6447\u7740\u5934\u4e0d\u505c\u7684\u54ce\u58f0\u53f9\u6c14\xb7\u597d \u50cf\u5f8c\u6094\u5f97\u8981\u547d\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u6709\u70b9\u5f00\u5fc3\u4e86\u63a5\u7740\uff1a\u201c\u5176\u5b9e\u4f60\u4e5f\u7528\u4e0d\u7740\u96be\u53d7\xb7\u4e0b\u6b21\u603b\u8fd8\u6709\u673a\u4f1a\u7684\u3002\u5c24\u5176\u5176\u4e2d\u6709\u4e2a \u5c0f\u59d1\u5a18\uff0c\u4e0d\u4f46\u957f\u5f97\u6f02\u4eae\xb7\u5bf9\u4eba\u66f4\u6e29\u67d4\u4f53\u8d34\u4f60\u5fc3\u91cc\u60f3\u8981\u4ec0\u9ebd\u7528\u4e0d\u7740\u5f00\u53e3\u5979\u5df2\u7ecf\u66ff\u4f60\u96be\u5907\u5f97\u597d\u597d \u7684\u3002\u201d\u85cf\u4e03\u542c\u5f97\u773c\u775b\u53d1\u76f4\uff0c\u9053\uff1a\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u5979\u7b80\u76f4\u662f\u4f4d\u6551\u82e6\u6551\u96be\u7684\u6ce5\u83e9\u8428\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86 \u6014\u9053\u6ce5\u83e9\u8428\uff1f\u54ea\u91cc\u6765\u7684\u6ce5\u83e9\u8428\uff1f\u201d\u4ed6\u5ffd\u7136\u60f3\u8d77\u6628\u5929\u5e99\u91cc\u7684\u90a3\u6ce5\u83e9\u8428\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u5973\u83e9\u8428\u4e13\u95e8\u6551\u7537\u4eba\u7684\u5973\u83e9\u8428\xb7\u201d\u90ed\u5927\u8def\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\u505a\u8fc7\u8d3c\u7684 \u4eba\u5fc3\u603b\u662f\u6bd4\u8f83\u865a\u7684\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4eca\u5929\u65e9\u535c\u90a3\u5973\u83e9\u8428\u66ff\u4f60\u505a\u4e86\u4e9b\u4ec0\u9ebd\u597d\u4e1c\u897f\u5403\uff1f\u201d\u90ed\u5927\u8def\u54bd\u4e86\u66f0\u53e3\u6c34\u6de1\u6de1\u9053\uff0c \u201c\u4e5f\u6c72\u4ec0\u9ebd\u597d\u5403\u7684\u53ea\u4e0d\u8fc7\u662f\u4e9b\u85cf\u7a9d\u3001\u9e21\u6c64\u3001\u9762\u3001\u5305\u5b50\u3001\u706b\u817f\u3001\u86cb\u201d\u4e00\u3002\u201d\u4ed6\u7b80\u76f4\u5f88\u4e0d\u5f97\u628a\u66f0 \u5df1\u5fc3\u91cc\u60f3\u5403\u7684\u4e1c\u897f\u5168\u8bf4\u51fa\u6765\u867d\u7136\u6ca1\u5403\u5230\u81f3\u5c11\u4e5f\u89e3\u89e3\u998b\u3002 \u3000\u3000\u53ea\u53ef\u63aa\u4ed6\u5b9e\u5728\u8bf4\u4e0d\u4e0b\u53bb\u4e86\xb7\u56e0\u4e3a\u518d\u8bf4\u4e0b\u53bb\uff0c\u4ed6\u53e3\u6c34\u7acb\u523b\u5c31\u8981\u6d41\u4e2d\u6765\u3002 \u3000\u3000\u71d5\u4e03\u53f9\u9053\uff1a\u201c\u770b\u6765\u4f60\u975e\u4f46\u8273\u798f\u9f50\u5929\u53e3\u798f\u4e5f\u771f\u4e0d\u9519\u6211\u5374\u5df2\u7ecf\u5feb\u997f\u6b7b\u4e86\uff0c\u975e\u8981\u627e\u4e2a\u5730\u65b9\u5403\u4e1c \u897f\u53bb\u4e0d\u53ef\u201c\u4e00\u3002\u201d\u4ed6\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u90ed\u5927\u8def\u5df2\u62a2\u7740\u9053\uff1a\u201c\u5230\u9668\u91cc\u53bb\u5403\uff1f\u6211\u968b\u4f60\u53bb\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u4e0d\u5fc5\u4e86\u4f60\u65e2\u7136\u5df2\u5403\u9971\u6211\u600e\u9ebd\u597d\u610f\u601d\u53eb\u4f60\u968b\u6211\uff1f\u201d\u90ed\u5927\u8def\u53c8\u6025\u53c8\u6c14\xb7\u5df2\u7ecf\u5fcd\u4e0d\u4f4f\u5feb\u5c06\u8001\u5b9e\u8bdd \u8bf4\u51fa\u6765\u4e86\u5e78\u597d\u5c31\u5728\u8fd9\u65f6\u594e\u5143\u9986\u7684\u95e8\u5ffd\u7136\u5f00\u4e86\u4e00\u7ebf\uff0c\u4e00\u4e2a\u4eba\u4ece\u91cc\u9762\u63a2\u51fa\u5934\u6765\uff0c\u773c\u5703\u534a\u95ed\uff0c\u4eff\u4f5b \u7ec8\u5e74\u90fd\u7761\u4e0d\u9192\u4e00\u8138\u736d\u6d0b\u6d0b\u7684\u6837\u5b50\u659c\u9650\u7784\u7740\u4ed6\u8d38\u6de1\u6de1\u9053\uff1a\u201c\u5c0f\u5e97\u5c31\u6709\u4e1c\u897f\u5403\u5ba2\u5b98\u4e3a\u4ec0\u9ebd\u8981\u820d\u8fd1 \u6c42\u8fdc\uff1f\u201d\u71d5\u4e03\u548c\u90ed\u5927\u8def\u5168\u90fd\u7b11\u4e86\u3002 \u3000\u3000\u738b\u52a8\u90ed\u5927\u8def\u5931\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u505a\u4e8b\u5012\u771f\u662f\u795e\u51fa\u9b3c\u8bbe\xb7\u7a76\u7adf\u662f\u4ec0\u9ebd\u65f6\u5019\u6765\u7684\uff1f\u4ec0\u9ebd\u65f6\u5019\u505a \u4e86\u594e\u5143\u9986\u7684\u5925\u8ba1\uff1f\u201d\u738b\u52a8\u8c08\u8c08\u9053\uff1a\u201c\u96be\u5f97\u88ab\u90ed\u5927\u5c11\u8bf7\u6b21\u5ba2\uff0c\u82e5\u662f\u7761\u8fc7\u4e86\u5934\uff0c\u9519\u8fc7\u673a\u4f1a\u5c82\u975e\u51a4 \u6789\u5f97\u5f88\uff1f\u5012\u4e0d\u5982\u7d22\u6027\u5934\u5929\u665a\u4e0a\u5c31\u8d76\u6765\u7761\u5728\u8fd9\u91cc\u7b49\u4e5f\u514d\u5f97\u8d70\u8def\u3002\u201d\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u597d\u4e3b\u610f\xb7\u738b\u8005 \u5927\u505a\u4e8b\u679c\u7136\u662f\u5341\u62ff\u4e5d\u7a33\u80fd\u8bf7\u5230\u8fd9\u9ebd\u8bda\u5fc3\u8bda\u610f\u7684\u5ba2\u4eba\u505a\u4e3b\u4eba\u7684\u4e5f\u4e00\u5b9a\u611f\u52a8\u5f97\u5f88\u3002\u201d\u90ed\u5927\u8def\u6ee1\u809a \u5b50\u82e6\u6c34\u5410\u4e5f\u5410\u4e0d\u51fa\xb7\u53ea\u6709\u563f\u563f\u7684\u4e7e\u7b11\uff0c\u8d3f\u54fa\u9053\uff1a\u201c\u6211\u5b9e\u5728\u611f\u52a8\u5f97\u5f88\uff0c\u7b80\u76f4\u4ed6\u5988\u7684\u611f\u52a8\u6781 \u4e86\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u6ca1\u5230\u4f60\u611f\u52a8\u7684\u65f6\u5019\uff0c\u7b49\u6211\u4eec\u5403\u8d77\u6765\u90a3\u624d\u771f\u8981\u4f60\u611f\u52a8\u56db\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u4e0d\u9519\u975e\u4ed6\u5988\u7684\u8981\u4ed6\u611f\u52a8\u5f97\u9650\u6cea\u76f4\u6d41\u4e0d\u53ef\u3002\u201d\u594e\u5143\u9986\u5730\u65b9\u4e0d\u5c0f\u6709\u697c\u4e0a\u697c\u4e0b\u4e24\u5c42\u697c\u4e0b\u4e5f\u6709\u5341\u4e03 \u516b\u5f20\u684c\u5b50\u3002 \u3000\u3000\u665a\u4e0a\u684c\u4e8e\u5c31\u90fd\u62fc\u5728\u4e00\u8d77\xb7\u5e97\u91cc\u7684\u5925\u8ba1\u5c31\u5728\u684c\u5b50\u4e0a\u6253\u94fa\u3002 \u3000\u3000\u5e97\u91cc\u5171\u6709\u4e03\u4e2a\u5925\u8ba1\xb7\u73b0\u5728\u6b63\u4e00\u4e2a\u4e2a\u7761\u773c\u634f\u677e\u7684\u722c\u8d77\u6765\uff0c\u7eb7\u7eb7\u62db\u547c\u7740\u738b\u52a8\uff0c\u663e\u5f97\u65e2\u53c8\u4eb2 \u5207\u3002\uff1a\u201c\u738b\u5927\u54e5\u7b49\u7684\u4eba\u5df2\u7ecf\u6765\u4e86\u9ebd\uff1f\u201d\uff1a\u201c\u8fd8\u4e0d\u5feb\u8d77\u6765\u62db\u547c\u738b\u5927\u54e5\u7684\u5ba2\u4eba\u201d\u90ed\u5927\u8def\u773c\u775b\u53d1\u76f4 \u771f\u60f3\u95ee\u95ee\u738b\u52a8\u4ec0\u9ebd\u65f6\u5019\u53c8\u505a\u4e86\u8fd9\u4e9b\u4eba\u7684\u5927\u54e5\uff1f\u4ed6\u5ffd\u7136\u53d1\u89c9\u738b\u52a8\u8fd9\u4eba\u505a\u4e8b\u4e0d\u4f46\u795e\u51fa\u9b3c\u6ca1\u800c\u4e14\u4ea4 \u670b\u53cb\u4e5f\u6709\u4e24\u624b\u4ed6\u81ea\u5df1\u5c31\u6c38\u8fdc\u6c72\u6cd5\u5b50\u963b\u996d\u94fa\u7684\u5925\u8ba1\u4ea4\u4e0a\u670b\u53cb\u3002 \u3000\u3000\u71d5\u4e03\u5df2\u5fcd\u4e0d\u4f4f\u95ee\u9053\u8fd9\u5730\u65b9\u4f60\u4ee5\u524d\u5e38\u6765\u9ebd\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002\u201d\u71d5\u4e03\u7684\u773c\u775b\u4e5f \u76f4\u4e86\u5fc3\u91cc\u4e5f\u5b9e\u5728\u4f69\u670d\u5f97\u5f88\uff0c\u4e00\u5929\u665a\u4e0a\u5c31\u80fd\u591f\u5c06\u996d\u94fa\u91cc\u7684\u5925\u8ba1\u5f04\u5f97\u8fd9\u9ebd\u670d\u8d34\u53ef\u771f\u4e0d\u662f\u4ef6\u5bb9\u6613 \u4e8b\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f60\u4eec\u8981\u5403\u4ec0\u9ebd\uff0c\u8bf4\u5427\xb7\u6211\u8fd9\u5c31\u53eb\u4ed6\u4eec\u53bb\u8d77\u706b\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u7ed9\u6211\u6765\u7897\u5a49\u9e21 \u9762\xb7\u716e\u53c1\u4e2a\u86cb\u4e0b\u53bb\uff0c\u518d\u714e\u4e24\u4e2a\u6392\u9aa8\u6709\u66f0\u9c7c\u548c\u770b\u8089\u4e5f\u6765\u4e24\u5757\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u6211\u4e5f\u7167\u6837\u6765\u4e00\u4efd\u597d \u4e86\u90ed\u5927\u5c11\u5462\uff1f\u201d\u90ed\u5927\u8def\u53c8\u5703\u4e86\u53e3\u53e3\u6c34\u9053\uff1a\u201c\u6211\u2026\u2026\u201d\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u51fa\u53e3\u71d5\u4e03\u5df2\u62a2\u7740\u9053\uff1a \u201c\u4ed6\u4e0d\u8981\xb7\u4ed6\u5df2\u7ecf\u5403\u5f97\u5feb\u670d\u6b7b\u4e86\u3002\u201d\u90ed\u5927\u8def\u53c8\u6025\u53c8\u6c14\u53c8\u6068\uff0c\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u624b\u4e5f\u75d2\u75d2\u7684\u6068\u4e0d \u5f97\u6301\u62f3\u5934\u585e\u5230\u8fd9\u591a\u7406\u87f9\u7684\u919a\u91cc\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u773c\u73e0\u5b50\u76f4\u8f6c\u597d\u50cf\u5728\u5077\u5077\u7b11\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u6797\u592a\u5e73\u5462\uff1f\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e5f\u6765 \u4e86\u8fd8\u5728\u697c\u4e0a\u7761\u5927\u89c9\u3002\u201d\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u4ed6\u7761\u89c9\u7684\u672c\u4e8b\u4f8b\u4e5f\u4e0d\u5c0f\u3002\u201d\u697c\u4e0a\u975e\u4f46\u6ca1\u6709\u4eba\u8fde\u4e2a \u9b3c\u5f71\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c4b\u89d2\u91cc\u6709\u51e0\u5f20\u684c\u5b50\u62fc\u5728\u8d77\uff0c\u684c\u4e0a\u7684\u786e\u94fa\u7740\u88ab\uff0c\u4f46\u88ab\u7a9d\u5374\u662f\u7a7a\u7684\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u7684\u4eba\u5462\uff1f\u201d\u738b\u52a8\u4e5f\u5728\u53d1\u6014\u9053\uff1a\u201c\u6211\u521a\u521a\u4e0b\u697c\u7684\u65f6\u5019\u4ed6\u660e\u660e\u8fd8\u7761\u5728\u8fd9\u91cc \u7684\xb7\u600e\u9ebd\u4e00\u4e0b\u5b50\u4eba\u5c31\u4e0d\u89c1\u4e86\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u6ca1\u770b\u5230\u4ed6\u5382\u697c\uff1f\u201d\u738b\u52a8\u6447\u6447\u5934\uff0c\u773c\u775b\u8035\u7740\u6247\u7a97 \u4e8e\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4eba\u505a\u4e8b\u4e5f\u6709\u70b9\u795e\u51fa\u9b3c\u6ca1\u53c8\u4e0d\u8981\u4ed6\u4ed8\u5e10\uff0c\u4ed6\u6e9c\u4ec0\u9ebd\uff1f\u201d\u4ed6\u773c\u775b\u4e5f\u968f\u7740 \u738b\u52a8\u5411\u90a3\u6247\u7a97\u5b50\u770b\u8fc7\u53bb\u3002 \u3000\u3000\u697c\u4e0a\u5171\u6709\u516b\u6247\u7a97\u4e8e\uff0c\u53ea\u6709\u8fd9\u6247\u7a97\u65bc\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u71d5\u4e03\u53c8\u9053\uff1a\u201c\u521a\u8fd9\u6247\u7a97\u65bc\u662f\u4e0d\u662f\u5f00\u7740\u7684\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u6ca1\u6709\u6211\u4e0d\u559c\u6b22\u5f00\u7740\u7a97\u4e8e\u7761\u89c9\u6211\u6015 \u7740\u51c9\u3002\u201d\u4ed6\u6084\u6084\u7684\u8d70\u5411\u7a97\u53e3\u3002 \u3000\u3000\u7a97\u4e0b\u5c31\u662f\u594e\u5143\u9986\u7684\u5f8c\u95e8\xb7\u5f8c\u95e8\u5bf9\u7740\u6761\u5c0f\u6cb3\xb7\u6cb3\u4e0a\u6709\u6761\u5c0f\u6865\u3002 \u3000\u3000\u6cb3\u6c34\u867d\u7136\u53c8\u810f\u53c8\u81ed\u5c0f\u6865\u867d\u7136\u53c8\u7834\u53c8\u65e7\u4f46\u73b0\u5728\u592a\u9633\u521a\u5347\u8d77\xb7\u6de1\u6de1\u7684\u9633\u5149\u7167\u7740\u6cb3\u6c34\u6cb3\u6c34\u4e0a\u7684 \u6668\u7f57\u8fd8\u672a\u6d88\u6563\u5fbd\u5fbd\u7684\u98ce\u5439\u7740\u6cb3\u9189\u7684\u5782\u67f3\xb7\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u9e21\u54fa\u770b\u6765\u5012\u771f\u8fd8\u6709\u51e0\u5206\u8bd7\u60c5\u753b\u610f\u3002 \u3000\u3000\u6740\u98ce\u666f\u7684\u662f\uff0c\u6865\u5bf9\u9762\u6b63\u6709\u4e2a\u80cc\u7740\u5b69\u5b50\u7684\u5987\u4eba\u8e72\u5728\u6cb3\u8845\u6d17\u9a6c\u6876\u3002 \u3000\u3000\u71d5\u4e03\u76b1\u4e86\u76b1\u7709\u53c8\u76b1\u4e86\u76b1\u9f3b\u5b50\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u5ac2\u521a\u6709\u4e2a\u4eba\u4ece\u8fd9\u6247\u7a97\u6237\u91cc\u4e0b\u53bb\u4f60\u77a7\u89c1\u4e86\u6ca1 \u6709\uff1f\u201d\u5987\u4eba\u62fe\u8d77\u5934\u77aa\u4e86\u4ed6\u773c\u53c8\u4f4e\u4e0b\u5934\u7483\u54fa\u9053\uff1a\u201c\u5927\u6e05\u65e9\u7684\uff0c\u8fd9\u4eba\u83ab\u975e\u64c5\u89c1\u9b3c\u4e86\u9ebd\uff1f\u201d\u71d5\u4e03\u78b0 \u5b50\u4e00\u9f3b\u5b50\u7070\xb7\u53ea\u6709\u82e6\u7b11\u7740\u54fa\u95fb\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u83ab\u975e\u6389\u5728\u6cb3\u91cc\u6df9\u6b7b\u4e86\u9ebd\uff1f\u201d\u90ed\u5927\u8def \u809a\u4e8e\u8d8a\u6765\u8d8a\u7a7a\u865a\u706b\u4e0a\u5347\u6b63\u60f3\u627e\u4e2a\u4eba\u51fa\u51fa\u6c14\uff0c\u677f\u7740\u8138\u9053\uff1a\u201c\u6df9\u6b7b\u4e00\u4e2a\u5c11\u4e00\u4e2a\u5c31\u6015\u4ed6\u6df9\u4e0d\u6b7b\u3002\u201d \u738b\u52a8\u773c\u89d2\u56ed\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u4eca\u5929\u65e9\u4e0a\u600e\u9ebd\u8fd9\u9ebd\u5927\u7684\u706b\u6c14\uff0c\u96be\u9053\u6628\u5929\u665a\u4e0a\u8fd8\u6ca1\u6709\u628a\u706b\u6c14\u653e\u51fa \u53bb\uff1f\u201d\u71d5\u4e03\u5403\u5403\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u6628\u5929\u665a\u4e0a\u53c8\u6709\u81ed\u53c8\u6709\u5973\u83e9\u8428\uff0c\u5c31\u7b97\u6709\u5929\u5927\u7684\u706b\xb7\u4e5f\u8be5\u51fa\u5f97\u5e72\u4e7e \u51c0\u51c0\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u5973\u83e9\u8428\uff1f\u81ed\uff1f\u96be\u9053\u6628\u5929\u665a\u4e0a\u4ed6\u7761\u5728\u7834\u5e99\u91cc\u7684\uff1f\u90a3\u5c31\u4e0d\u5982\u5230\u8fd9\u91cc\u6765\u7761\u684c\u4e8e \u4e86\u3002\u201d\u90ed\u5927\u8def\u7684\u8138\u4e00\u4e0b\u5b50\u5c31\u6da8\u5f97\u901a\u7ea2\u5e78\u597d\u8fd9\u65f6\u5925\u8ba1\u5df2\u7aef\u7740\u4e24\u7897\u9762\u4e0a\u697c\uff0c\u597d\u5927\u7684\u4e24\u7897\u9762\uff0c\u8fd8\u5916 \u5e26\u4e24\u5927\u789f\u56ed\u9c7c\u6392\u9aa8\u3002\u9635\u9635\u9999\u5473\u968f\u7740\u70ed\u6c14\u5f80\u90ed\u5927\u8def\u9f3b\u5b50\u91cc\u94bb\u4f60\u53eb\u90ed\u5927\u8def\u600e\u9ebd\u8fd8\u53d7\u5f97\u4e86\uff1f\u90ed\u5927\u8def \u5ffd\u7136\u96c6\u4e2d\u7cbe\u529b\u5168\u5fc3\u5168\u610f\u7684\u76ef\u7740\u684c\u5b50\u4e0b\u9762\xb7\u5c31\u597d\u50cf\u684c\u5b50\u4e0b\u9762\u6b63\u6709\u51e0\u4e2a\u5c0f\u5996\u602a\u5728\u6f14\u620f\u3002 \u3000\u3000\u71d5\u4e03\u548c\u738b\u52a8\u5634\u91cc\u867d\u5728\u5403\u7740\u9762\u773c\u4e5f\u4e0d\u7531\u81ea\u4e3b\u968f\u7740\u4ed6\u5411\u684c\u5b50\u4e0b\u77a7\u4e86\u8fc7\u53bb\uff0c\u90ed\u5927\u8def\u5c31\u8d81\u7740\u8fd9\u673a \u4f1a\u98de\u5feb\u7684\u4f38\u51fa\u624b\xb7\u5f80\u6700\u5927\u7684\u5757\u6392\u9aa8\u4e0a\u838e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u7684\u624b\u521a\u6478\u5230\u6392\u9aa8\u53cc\u7b77\u4e86\u7a81\u7136\u4e4e\u7a7a\u98de\u8fc7\u53bb\uff0c\u201c\u5f7c\u201d\u7684\uff0c\u5728\u4ed6\u624b\u80cc\u4e0a\u91cd\u91cd\u7684\u6572\u4e86\u4e0b\u3002 \u3000\u3000\u71d5\u4e03\u6b63\u5728\u659c\u773c\u56ed\u7740\u4ed6\u5e26\u7740\u7b11\u9053\u201c\u521a\u5403\u4e86\u5341\u4e03\u516b\u6837\u4e1c\u897f\uff1b\u8fd8\u60f3\u5077\u4eba\u5bb6\u7684\u8089\xb7\u96be\u9053\u771f\u662f\u997f\u6b7b \u9b3c\u6295\u80ce\uff1f\u201d\u8fd9\u5c0f\u5b50\u5f53\u771f\u662f\u5929\u751f\u7684\u4e00\u53cc\u8d3c\u773c\u3002 \u3000\u3000\u90ed\u5927\u8def\u6da8\u7ea2\u7740\u8138\u6c55\u6c55\u7684\u7f29\u56de\u4e86\u624b\u7483\u95fb\u7740\uff1a\u201c\u4e0d\u77e5\u597d\u6b79\u597d\u5fc3\u66ff\u4ed6\u8d76\u82cd\u8747\xb7\u4ed6\u53cd\u800c\u8981\u54ac\u6211 \u53e3\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u9ebd\u51b7\u7684\u5929\xb7\u54ea\u6765\u7684\u82cd\u8747\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u82cd\u8747\u867d\u6ca1\u6709\xb7\u81f3\u5c11\u81ed\u603b\u6709\u51e0 \u4e2a\u3002\u201d\u8fd9\u4e24\u4eba\u4eca\u5929\u4e5f\u4e0d\u77e5\u72af\u4e86\u4ec0\u9ebd\u6bdb\u75c5\u65f6\u65f6\u523b\u523b\u90fd\u5728\u627e\u90ed\u5927\u8def\u7684\u9ebb\u70e6\u968f\u65f6\u968f\u5730\u90fd\u5728\u8ddf\u4ed6\u4f5c \u5bf9\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u4e0d\u7406\u4e0d\u776c\u4e2a\u4eba\u53d1\u4e86\u534a\u5929\u6014\xb7\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u6211\u5728\u60f3\u4ec0\u9ebd\uff1f\u201d\u6ca1\u6709 \u4eba\u8bf4\u8bdd\uff0c\u56e0\u4e3a\u5634\u91cc\u90fd\u585e\u6ee1\u4e86\u8089\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u81ea\u5df1\u63a5\u7740\u9053\uff1a\u201c\u6211\u5728\u60f3\u8fd9\u7897\u9762\u7684\u5473\u9053\u4e00\u5b9a\u4e0d\u9519\u3002\u201d\u71d5\u4e03\u559d\u53e3\u9762\u6c64\u628a\u7531\u9001\u4e0b \u809a\xb7\u624d\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u6211\u4eec\u771f\u8fd8\u5f88\u5c11\u5403\u5230\u8fd9\u9ebd\u597d\u5403\u7684\u9762\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u7897\u9762\u4e3a \u4ec0\u9ebd\u7279\u522b\u5473\u9053\u4e0d\u540c\uff1f\u201d\u71d5\u4e03\u7728\u7728\u773c\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u60a0\u7136\u9053\u201c\u56e0\u4e3a\u8fd9\u7897\u9762\u662f\u7528\u6cb3\u91cc\u7684 \u6c34\u716e\u7684\xb7\u6d17\u9a6c\u6876\u7684\u6c34\u9165\u901a\u5f53\u7136\u7279\u4e0d\u540c\u4e86\u3002\u201d\u71d5\u4e03\u5c45\u7136\u4e0d\u52a8\u58f0\u8272\u53cd\u800c\u563b\u563b\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6d17\u811a\u6c34 \u716e\u7684\u9762\uff0c\u4e5f\u6bd4\u997f\u7740\u809a\u5b50\u6c72\u6709\u9762\u5403\u597d\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86\u534a\u664c\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u5f20\u5f00\u53cc\u624b\uff0c\u5927\u53eb\u9053\uff1a \u201c\u6211\u4e5f\u8981\u5403\uff0c\u975e\u5403\u4e0d\u53ef\u8c01\u518d\u4e0d\u8ba9\u6211\u5403\uff0c\u6211\u5c31\u8981\u62fc\u547d\u4e86\u3002\u201d\u6797\u592a\u5e73\u5750\u7740\u5728\u53d1\u6014\u3002 \u3000\u3000\u4ed6\u5df2\u56de\u6765\u4e86\u5f88\u4e45\u53d1\u4e86\u4e2d\u5929\u6014\u597d\u50cf\u5728\u7b49\u7740\u522b\u4eba\u95ee\u4ed6\uff1a\u201c\u600e\u9ebd\u4f1a\u5ffd\u7136\u5931\u8e2a\uff1f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4e8e \u4ec0\u9ebd\u53bb\u4e86\uff1f\uff1f\u5021\u504f\u6ca1\u6709\u4eba\u95ee\u4ed6\u5c31\u597d\u50cf\u4ed6\u6839\u672c\u6ca1\u6709\u79bb\u5f00\u8fc7\u4f3c\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u53ea\u6709\u81ea\u5df1\u8bf4\u51fa\u6765\uff0c\u4ed6\u5148\u770b\u4e86\u90ed\u5927\u8def\u4e00\u773c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u521a\u770b\u5230\u4e86\u4e00\u4e2a\u4eba\u4f60\u4eec\u6c38\u8fdc \u90fd\u60f3\u4e0d\u5230\u662f\u8c01\u3002\u201d\u90ed\u5927\u8def\u679c\u7136\u6c89\u4e0d\u4f4f\u6c14\u4e86\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u6211\u961f\u4e0d\u8ba4\u5f97\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5c31\u7b97 \u4e0d\u8ba4\u5f97\u81f3\u5c11\u603b\u89c1\u8fc7\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u7a76\u7adf\u662f\u8c01\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u56e0\u4e3a\u6211\u4e5f \u4e0d\u8ba4\u5f97\u4ed6\u3002\u201d\u90ed\u5927\u8def\u53c8\u6014\u4f4f\u4e86\u82e6\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u4eba\u8bf4\u7684\u7a76\u7adf\u662f\u54ea\u4e00\u56fd\u7684\u8bdd\uff1f\u4f60\u4eec\u8c01\u80fd\u542c\u5f97\u61c2\u4ed6 \u5728\u8bf4\u4ec0\u9ebd\uff1f\u201d\u6797\u592a\u5e73\u4e5f\u4e0d\u7406\u4ed6\uff0c\u63a5\u7740\u4e49\u9053\uff1a\u201c\u6211\u867d\u4e0d\u8ba4\u5f97\u4ed6\u7684\u4eba\u5374\u8ba4\u5f97\u4ed6\u90a3\u8eab\u8863\u670d\u3002\u201d\u90ed\u5927 \u8def\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u8863\u670d\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u9ed1\u8863\u670d\u3002\u201d\u90ed\u5927\u8def\u7b11\u4e86\u9053\uff1a\u201c\u7a7f\u9ed1\u8863\u670d\u7684\u4eba \u6ee1\u8857\u90fd\u662f\uff0c\u4f60\u968f\u4fbf\u4ece\u9aa1\u91cc\u90fd\u80fd\u627e\u5230\u51e0\u5341\u4e2a\u3002 \u3000\u3000\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u7684\u8863\u670d\u5916\u6211\u8fd8\u8ba4\u5f97\u90a3\u4eba\u7684\u90a3\u67c4\u5251\u3002\u201d\u90ed\u5927\u8def\u8fd9\u624d\u542c\u51fa\u70b9\u540d\u5802\u6765\u4e86 \u4e92\u523b\u8ffd\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u6837\u7684\u5251\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u5c3a\u4e03\u5bf8\u957f\u7684\u5251\u5374\u914d\u7740\u56db\u5c3a\u957f\u7684\u5251\u5708\u3002\u201d\u90ed\u5927\u8def \u5410\u51fa\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4ec0\u9ebd\u65f6\u5019\u770b\u5230\u4ed6\u7684\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f60\u4eec\u6765\u7684\u65f6\u5019\u3002\u201d\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86 \u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u4ef6\u4e8b\u5f88\u5947\u602a\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4e0d\u5947\u602a\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u672c\u6765\u5c31\u662f\u8981 \u5230\u53bf\u57ce\u91cc\u6765\u4ea4\u5dee\u7684\u4f60\u82e5\u6ca1\u6709\u5728\u8fd9\u91cc\u89c1\u5230\u4ed6\u90a3\u624d\u5947\u602a\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4ed6\u672c\u6765\u5e94\u8be5\u5c06\u91d1\u72ee\u3001\u68cd \u5b50\u3001\u98ce\u6816\u68a7\u548c\u90a3\u6279\u8d3c\u8d43\u90fd\u4ea4\u5230\u8859\u95e8\u91cc\u53bb\u662f\u4e0d\u662f\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u662f\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f46\u8859\u95e8 \u91cc\u52ab\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u8fd9\u4e24\u5929\u6839\u672c\u6ca1\u6709\u4eba\u62bc\u72af\u4eba\u6765\u3002 \u3000\u3000\u201d\u90ed\u5927\u8def\u8fd9\u624d\u89c9\u5f97\u6709\u70b9\u5403\u60ca\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u7684\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5230\u8859\u95e8\u91cc\u53bb\u6253 \u542c\u8fc7\u4e86\u3002\u201d\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u51c6\u5907\u5c06\u72af\u4eba\u62bc\u5230\u522b\u7684\u5730\u65b9\u53bb\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6ca1\u6709\u72af \u4eba\u3002\u201d\u90ed\u5927\u8def\u76b1\u7709\u9053\uff1a\u201c\u6ca1\u6709\u72af\u4eba\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6ca1\u6709\u72af\u4eba\u7684\u610f\u601d\u5c31\u662f\u91d1\u6ca5 \u5b50\u3001\u68cd\u5b50\u3001\u98ce\u6816\u68a7\xb7\u5df2\u7ecf\u5168\u4e0d\u89c1\u4e86\uff0c\u90a3\u6279\u8d3c\u8d43\u4e5f\u4e0d\u89c1\u4e86\xb7\u6211\u4e00\u76f4\u8feb\u8e2a\u5230\u4ed6\u843d\u811a\u7684\u5730\u65b9\xb7\u90a3\u5730 \u65b9\u53ea\u6709\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d\u90ed\u5927\u8def\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u548c\u738b\u52a8\u4e5f\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u6797\u592a\u5e73\u5c06\u90ed\u5927\u8def\u9762\u524d\u7684\u9152\u4e00\u996e\u800c\u5c3d\xb7\u6de1\u8c08\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8ba4\u4e3a\u8fd9\u4ef6\u4e8b\u5947\u602a\u4e0d\u5947\u602a\uff1f\u201d\u90ed\u5927 \u8def\u9053\uff1a\u201c\u5947\u602a\u3002\u201d\u6740\u4eba\u4e0e\u88ab\u6740\u684c\u5b50\u5df2\u62c9\u5f00\xb7\u68c9\u88ab\u5df2\u6536\u5b9a\u3002 \u3000\u3000\u81f3\u5143\u9986\u5ba2\u4eba\u4e0a\u5ea7\u7684\u65f6\u5019\u5df2\u7ecf\u5feb\u5230\u4e86\u3002\u4f46\u73b0\u5728\u697c\u4e0a\u5378\u8fd8\u662f\u53ea\u6709\u4ed6\u4eec\u56db\u4e2a\u4eba\u3002\u56db\u4e2a\u4eba\u52a8\u4e5f\u4e0d \u52a8\u7684\u5750\u5728\u90a3\u91cc\u5c31\u8c61\u662f\u56db\u4e2a\u6728\u5934\u4eba\u3002 \u3000\u3000\u4f1a\u559d\u9152\u7684\u6728\u5934\u4eba\u3002 \u3000\u3000\u58f6\u91cc\u7684\u9152\u5c31\u8c61\u662f\u9000\u6f6e\u822c\u6d88\u5931\u4e86\u4e0b\u53bb\uff0c\u5927\u5bb6\u4f60\u676f\uff0c\u6211\u4e00\u676f\xb7\u81ea\u5df2\u5012\uff0c\u81ea\u5df2\u559d\u8c01\u4e5f\u4e0d\u53bb\u62db\u547c \u522b\u4eba\u3002 \u3000\u3000\u7136\u5f8c\u71d5\u4e03\u3001\u738b\u52a8\u3001\u90ed\u5927\u8def\u5c31\u8c61\u662f\u7ea6\u597d\u4e86\u4f3c\u7684\uff0c\u540c\u65f6\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u5c31\u7b97\u662f\u767d\u75f4\uff0c\u73b0\u5728\u4e5f\u77e5\u8c0a\u8fd9\u6b21\u53c8\u4e0a\u4e86\u522b\u4eba\u4e2a\u5927\u5f53\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u6839\u672c\u5c31\u4e0d\u662f\u5b98\u5dee\u4e5f\u4e0d\u662f\u4ec0\u9ebd\u63d0\u7763\u8001\u7237\u6d3e\u6765\u8c03\u67e5\u91d1\u72ee\u5b50\u548c\u68cd\u5b50\u7684\u5bc6\u63a2\xb7\u4ed6\u4e5f\u662f\u9ed1 \u5403\u9ed1\u3002 \u3000\u3000\u88ab\u4eba\u9a97\u5f97\u8fd9\u9ebd\u60e8\u672c\u662f\u5f88\u607c\u706b\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u8ba4\u4e3a\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u71d5\u4e03\u6307\u7740\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u738b\u8001\u5927\u8bf4\u7684\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u8be5\u806a\u660e\u7684\u65f6\u5019\u4f60\u53cd\u9762\u7cca\u6d82\u4e86\uff0c\u4e0d\u4f46\u6a84 \u6dfb\u800c\u4e14\u7b28\u4e0d\u4f46\u7b28\u800c\u4e14\u82ef\u5f97\u8981\u547d\u3002\u201d\u90ed\u5927\u8def\u4e5f\u6307\u7740\u4ed6\u7b11\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u4e5f\u5e76\u4e0d\u6bd4\u6211\u806a\u660e\u591a \u5c11\u3002\u201d\u6797\u592a\u5e73\u4e00\u76f4\u5728\u65c1\u8fb9\u8def\u8d39\u7684\u770b\u7740\u4ed6\u4eec\u7b49\u4ed6\u4eec\u7b11\u58f0\u505c\u4e0b\u6765\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7b11\u5b8c\u4e86\u6ca1\u6709\u201d \u7ff1\u5927\u8def\u9685\u7740\u6c14\uff0c\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u7b11\u5b8c\uff0c\u53ea\u4e0d\u8fc7\u5df2\u6ca1\u6709\u529b\u6c14\u518d\u7b11\u3002\u201d\u6797\u592a\u5e73\u8c0a\uff1a\u201c\u4f60\u4eec\u8ba4\u4e3a\u8fd9\u4ef6 \u4e8b\u72e0\u53ef\u7b11\uff1f\u201d\u738b\u52a8\u5ffd\u7136\u7530\u4e86\u7ffb\u81ea\u773c\u9053\uff1a\u201c\u4e0d\u7b11\u600e\u9ebd\u529e\uff1f\u54ed\u9ebd\uff1f\u201d\u8fd9\u5c31\u662f\u4ed6\u4eec\u505a\u4eba\u7684\u54f2\u5b66\u4ed6\u8861 \u4f1a\u7b11\u6562\u7b11\uff0c\u4e5f\u61c2\u5f97\u7b11\u3002 \u3000\u3000\u7b11\u4e0d\u4f46\u53ef\u4ee5\u4ee4\u4eba\u6b22\u5077\u4e5f\u53ef\u4ee5\u589e\u52a0\u4f60\u5bf9\u4eba\u751f\u7684\u60c5\u5fc3\u548c\u52c7\u6c14\u3002\uff1a\u201c\u7b11\u7684\u4eba\u6709\u798f\u4e86\u56e0\u4e3a\u751f\u547d\u662f \u7528\u4e8e\u4ed6\u5546\u7684\u3002\u201d\u6797\u592a\u5e73\u770b\u6765\u5374\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8ddf\u6211\u76ef\u6837\u7b11\uff1f\u201d\u6797\u592a\u5e73\u8c0a\uff1a\u201c\u82e5\u662f\u7b11\u5c31\u80fd\u89e3\u51b3\u95ee\u9898\xb7\u6211\u5b9a\u6bd4\u4f60 \u4eec\u7b11\u5f97\u8fd8\u5389\u5bb3\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u7b11\u5c31\u7b97\u4e0d\u80fd\u89e3\u51b3\u95ee\u9898\u81f3\u5c11\u603b\u4e0d\u4f1a\u589e\u52a0\u70e6\u607c\u3002\u201d\u4ed6\u53c8\u7b11\u4e86\u7b11\u63a5 \u7740\u9053\uff1a\u201c\u4f55\u51b5\u4f60\u82e5\u5b66\u4f1a\u4e86\u7528\u7b11\u53bb\u9762\u5bf9\u4eba\u751f\uff0c\u6e10\u6e10\u5c31\u4f1a\u53d1\u89c9\u4eba\u751f\u672c\u6ca1\u6709\u4ec0\u9ebd\u771f\u6b63\u4e0d\u80fd\u89e3\u51b3\u7684\u95ee \u9898\u3002\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u7b11\u5f97\u591a\u5f00\u5fc3\u8fd8\u662f\u6837\u88ab\u4eba\u9a97\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u4e0d\u7b11\u8fd8\u662f\u6837\u88ab\u4eba\u9a97\u4e86 \u65e2\u7136\u5df2\u88ab\u9a97\u4e3a\u4ec0\u9ebd\u4e0d\u7ecd\uff1f\u201d\u6797\u592a\u5e73\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u6709\u4ec0\u9ebd\u95ee\u9898\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u5bf9\u8fd9\u4ef6\u4e8b\u5982\u6b64\u5173\u5fc3\u201d\u6797\u592a\u5e73\u6c89 \u9ed8\u4e86\u534a\u8d2d\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u4eba\u5c31\u662f\u771f\u7684\u5357\u5bcc\u4e11\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6211\u5c31 \u662f\u77e5\u9053\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5357\u5bcc\u4e11\u548c\u4f60\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u6ca1\u6709\u5173\u7cfb\u5c31\u56e0\u4e3a\u6ca1\u6709\u5173\u7cfb \u6240\u4ee5\u6211\u624d\u8981\u2026\u2026\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8981\u600e\u9ebd\u6837\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u8981\u6740\u4e86\u4ed6\u3002\u201d\u90ed\u5927\u8def\u770b\u770b\u71d5\u4e03\u53c8 \u770b\u770b\u738b\u52a8\u9053\uff1a\u201c\u4f60\u4eec\u542c\u89c1\u4ed6\u8bf4\u7684\u8bdd\u6ca1\u6709\uff1f\u201d\u738b\u52a8\u52a8\u4e5f\u4e2d\u52a8\u3002 \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u8bf4\u4ed6\u8981\u6740\u4eba\u3002\u201d\u738b\u52a8\u8fd8\u662f\u4e0d\u52a8\u3002 \u3000\u3000\u71d5\u4e03\u53c8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9992\u998f\u7684\u56de\u8fc7\u5934\u770b\u7740\u6797\u592a\u5e73\u3002 \u3000\u3000\u6797\u592a\u5e73\u8138\u4e0a\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u521a\u5df3\u770b\u89c1\u4ed6\uff1f\u201d\u6797\u592a\u5e73\u906d\uff1a\u201c\u662f\u3002\u201d\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u521a \u4e3a\u4ec0\u9ebd\u4e0d\u6740\u4e86\u4ed6\uff1f\u201d\u6797\u592a\u5e73\u8138\u4e0a\u8fd8\u662f\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u4ed6\u8138\u4e0a\u5c31\u8c61\u662f\u98a4\u4e0a\u4e86\u4e2a\u9762\u5177\u3002 \u3000\u3000\u94c1\u80b2\u8272\u7684\u9762\u5177\uff0c\u770b\u6765\u4f3c\u4e4e\u5df2\u6709\u70b9\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u4e00\u5b57\u5b87\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u6740\u4e86\u4ed6\u3002\u201d\u58f6\u91cc\u53c8\u6dfb\u6ee1\u4e86\u9152\uff0c\u56e0\u4e3a\u738b\u52a8\u5429\u5490\u8fc7\uff1a\u201c\u770b\u5230\u6211\u4eec\u7684\u9152 \u58f6\u7a7a\u4e86\u5c31\u6765\u52a0\u6ee1\u3002\u201d\u81f3\u5143\u9986\u91cc\u7684\u5925\u8ba1\u5bf9\u738b\u52a8\u5f88\u670d\u8d34\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u77aa\u5927\u4e86\u773c\u775b\u671b\u7740\u9152\u58f6\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9152\u4e0d\u662f\u7528\u773c\u559d\u7684\u3002\u201d\u9884\u4e03\u9053\uff1a\u201c\u6211\u7684\u7530\u72e0\u5fd9\u3002\u201d\u7ff1\u5927\u8def\u8c0a\uff1a \u201c\u5fd9\u4ec0\u9ebd\uff1f\u201d\u852c\u4e03\u9053\uff1a\u201c\u5fd9\u7740\u628a\u60f3\u8bf4\u7684\u8bdd\u541e\u56de\u809a\u4e86\u91cc\u53bb\u3002\u201d\u5ba2\u4eba\u5df2\u6e10\u6e10\u6765\u4e86\u8fd9\u91cc\u5df2\u4e0d\u662f\u8bf4\u8bdd \u7684\u5730\u65b9\u3002 \u3000\u3000\u90ed\u5927\u8def\u7aef\u8d77\u9152\u676f\u53c8\u653e\u4e0b\u9053\uff1a\u201c\u90ed\u5927\u5c11\u96be\u5f97\u8bf7\u6b21\u7a96\u201d\u2026\u201c\u71d5\u4e03\u9053\uff1a\u201c\u8fd9\u6b21\u4fbf\u5b9c\u4e86\u4f60\xb7\u6211\u4eec \u8d70\u5427\u3002\u201d\u6797\u592a\u5e73\u7b2c\u4e2a\u7ad9\u4e86\u8d77\u6765\u571f\u52a8\u5c45\u7136\u4e5f\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u624b\u5df2\u4f38\u5230\u4ed6\u9762\u524d\u3002 \u3000\u3000\u738b\u52a8\u770b\u770b\u4ed6\xb7\u9053\uff1a\u201c\u4f60\u60f3\u5e72\u4ec0\u9ebd\uff1f\u60f3\u8981\u6211\u66ff\u4f60\u770b\u624b\u76f8\uff1f\u201d\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0d \u5fc5\u770b\u4e86\u6211\u662f\u5929\u751f\u7684\u592f\u547d\u6700\u8981\u547d\u7684\u662f\u53ea\u8981\u6211\u60f3\u8bf7\u5ba2\u888b\u5b50\u91cc\u5c31\u7b97\u6709\u94b1\u4e5f\u4f1a\u98de\u8d70\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f60 \u60f3\u95ee\u6211\u501f\u94b1\u4ed8\u5e10\uff1f\u201d\u90ed\u5927\u8def\u4e7e\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff0c\u6211\u6628\u5929\u665a\u4e0a\u5e72\u7684\u662f\u4ef6\u5f88\u8d39\u94b1\u7684\u4e8b\u3002\u201d \u738b\u52a8\u672c\u6765\u60f3\u7b11\u4e86\uff0c\u4f46\u770b\u4e86\u6797\u592a\u5e73\u773c\xb7\u5374\u53f9\u4e86\u53e3\u6c14\xb7\u9053\uff1a\u201c\u4f60\u627e\u9519\u4ed6\u3002\u201d\u90ed\u5927\u8def\u618e\u7136\u9053\uff1a\u201c\u4f60 \u7684\u94b1\u4e5f\u82b1\u5149\u4e86\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u662d\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u2026\u201d\u4f60\u600e\u9ebd\u82b1\u7684\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u6211\u6628\u5929 \u665a\u4e0a\u5343\u7684\u4e5f\u662f\u4ef6\u5f88\u8d39\u94b1\u7684\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u5728\u65bc\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e16\u4e0a\u53ea\u6709\u4ef6\u4e8b\u6bd4\u627e \u5973\u4eba\u66f4\u8d39\u94b1\u90a3\u5c31\u662f\u8d4c\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8f93\u5149\u4e86\uff1f\u8f93\u7ed8\u4e86\u8c01\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u8fd9\u996d\u94fa\u91cc\u7684\u5925 \u8ba1\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86\u534a\u664c\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u9053\uff1a\u201c\u96be\u602a\u4ed6\u4eec\u5bf9\u4f60\u8fd9\u9ebd\u670d\u8d34\u8d29\u94fa\u91cc\u7684\u5925\u8ba1\u5bf9\u51a4\u5927\u5934\u603b\u662f \u7279\u522b\u670d\u8d34\u7684\xb7\u4f55\u51b5\u4f60\u82e5\u628a\u94b1\u8f93\u7ed9\u6211\u6211\u4e5f\u4e00\u6837\u670d\u8d34\u4f60\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u51a4\u5927\u5934\u4e0d\u6b62\u6211\u4e2a\u3002\u201d\u90ed\u5927 \u8def\u9053\uff1a\u201c\u8fd8\u6709\u8c01\uff1f\u201d\u738b\u52a8\u770b\u770b\u6797\u592a\u5e73\xb7\u53c8\u770b\u770b\u71d5\u4e03\u3002 \u3000\u3000\u90ed\u5927\u8def\u8e22\u8d77\u6765\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u706f\u7684\u94b1\u90fd\u8f93\u5149\u4e86\uff1f\u201d\u6ca1\u6709\u4eba\u51fa\u58f0\uff0c\u6c89\u9ed8\u5c31\u662f\u7b54\u590d\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u4e00\u5c41\u80a1\u5750\u4e86\u4e0b\u53bb\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4e9b\u5925\u8ba1\u5c82\u975e\u5168\u53d1\u4e86\u8d22\uff1f\u201d\u738b\u52a8\u9053\uff1a \u201c\u4ed6\u4eec\u4e5f\u53d1\u4e0d\u4e86\u8d22\u4ed6\u4eec\u8fdf\u65e9\u4e5f\u4f1a\u7ff0\u7ed9\u522b\u4eba\u7684\u3002\u201d\u90ed\u5927\u8def\u998f\u4fc3\u7684\u70b9\u7740\u5934\uff0c\u95fb\u8d2d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6765 \u5f97\u5bb9\u6613\u53bb\u5f97\u4fa0\u600e\u9ebd\u6765\u7684\u600e\u9ebd\u53bb\u3002 \u3000\u3000\u201d\u738b\u52a8\u9053\uff1a\u201c\u4f46\u6211\u4eec\u5bf9\u4eba\u7c7b\u603b\u7b97\u4e5f\u6709\u70b9\u8d21\u732e\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ec0\u9ebd\u8d21\u732e\uff1f\u201d\u738b\u52a8\u9053\uff1a \u201c\u94b1\u6d41\u901a\u5f97\u8d8a\u5feb\uff0c\u5e02\u9762\u8d8a\u7e41\u8363\uff0c\u4eba\u7c7b\u5c31\u662f\u8fd9\u6837\u8fdb\u6b65\u7684\u3002\u201d\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd \u597d\u50cf\u603b\u6709\u70b9\u9053\u7406\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e5f\u4e0d\u5fc5\u96be\u53d7\u3002\u201d\u7ff1\u5927\u8def\u9053\uff1a\u201c\u6211\u96be\u53d7\u4ec0\u9ebd\uff1f\u6211\u53c8\u6ca1\u6709 \u7ff0\u2026\u3002\u201c\u738b\u52a8\u9053\uff1a\u201c\u62b1\u6b49\u7684\u662f\u6211\u4eec\u628a\u4f60\u7684\u94b1\u4e5f\u4e00\u9f50\u8f93\u4e86\u3002\u201d\u90ed\u5927\u8def\u6014\u4f50\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u7834\u5e99\u91cc\u7684\u6ce5\u83e9\u8428\u5c31\u7b97\u966a\u4eba\u7761\u89c9\xb7\u4e5f\u4e0d\u4f1a\u6536\u94b1\u7684\u3002\u201d\u90ed\u5927\u8def\u7684\u773c\u775b\u9992\u4fb5\u7684\u53d8\u5706 \u4e86\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\uff1f\u201d\u4e00\u201d\u4f60\u4eec\u65e9\u5c31\u4e32\u901a\u597d\u4e86\u7684\u2026\u2026\u5077\u6211\u7684\u5c0f\u4f2f\u5c31\u662f\u201c\u2026\u201c\u4ed6\u624b\u6307\u5ffd\u7136\u76f4 \u6233\u5230\u71d5\u4e03\u7684\u9f3b\u5b50\u4e0a\u5927\u53eb\u9053\uff1a\u201c\u5c31\u662f\u4f60\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u7b54\u5bf9\u4e86\u3002\u201d\u90ed\u5927\u8def\u628a\u63ea\u4f50\u4ed6\u8863\u895f\u54ac\u7740\u7259 \u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u505a\u9009\u79cd\u4e8b\uff1f\u201d\u71d5\u4e03\u4e0d\u8bf4\u8bdd\u8138\u5374\u4f3c\u6709\u70b9\u53d1\u7ea2\u3002 \u3000\u3000\u738b\u52a8\u8c08\u6de1\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u4e3a\u4f60\u597d\uff0c\u4ed6\u4e0d\u60f3\u670b\u53cb\u5f97\u82b1\u67f3\u75c5\u3002\u201d\u90ed\u5927\u8def\u7684\u624b\u9992\u9992\u653e\u5f00\u5c41\u80a1\u53c8\u5750 \u5728\u6905\u5b50\u4e0a\u624b\u6478\u7740\u5934\u54fa\u54fa\u9053\uff1a\u201c\u5929\u5440\u2026\u5929\u5440\uff0c\u4f60\u600e\u9ebd\u4f1a\u8ba9\u6211\u4ea4\u5230\u8fd9\u79cd\u597d\u670b\u53cb\u7684\uff1f\u201d\u4ed6\u5ffd\u53c8\u8e22\u8d77 \u6765\uff0c\u54ac\u7740\u7259\u9053\u201c\u4f60\u76ef\u65e2\u7136\u77e5\u9053\u56db\u4e2a\u4eba\u90fd\u5df2\u56ca\u7a7a\u5982\u6d17\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u5728\u8fd9\u91cc\u5927\u5403\u5927\u559d\uff1f\u201d\u738b\u52a8 \u9053\uff1a\u201c\u4e3a\u4e86\u8981\u8ba9\u4f60\u9ad8\u5174\u3002\u201d\u90ed\u5927\u8def\u5fcd\u4e0d\u4f73\u53eb\u4e86\u8d77\u6765\u9053\uff1a\u201c\u8ba9\u6211\u9ad8\u5174\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e2a\u4eba\u8bf7\u5ba2 \u7684\u65f6\u5019\xb7\u603b\u662f\u7279\u522b\u9ad8\u5174\u7684\u662f\u4e0d\u662f\uff1f\u201d\u90ed\u5927\u8def\u53cc\u624b\u62b1\u5934\u9053\uff1a\u201c\u662f\u662f\u662f\u6211\u771f\u9ad8\u5174\u771f\u4ed6\u5988\u7684\u9ad8\u5174\u5f97 \u4e0d\u5982\u6b7b\u7b97\u4e86\u3002\u201d\u4e2a\u5925\u8ba1\u5ffd\u7136\u8d70\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u738b\u5927\u54e5\u4e0d\u5fc5\u4e3a\u4ed8\u5e10\u7684\u4e8b\u53d1\u6101\u8fd9\u91cc\u7684\u5e10\u5df2\u7b97\u6ed1\u4e86\u3002 \u3000\u3000\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u91cc\u603b\u7b97\u6709\u4e2a\u826f\u5fc3\u597d\u7684\u4eba\u3002\u201d\u8fd9\u5925\u8ba1\u8138\u7ea2\u4e86\u7ea2\uff0c\u7b11 \u9053\uff1a\u201c\u6211\u672c\u6765\u7684\u786e\u60f3\u66ff\u738b\u5927\u54e5\u7ed3\u5e10\uff0c\u53ea\u53ef\u4fd7\u6709\u4eba\u62a2\u7740\u5148\u628a\u5e10\u4f1a\u4e86\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u662f\u8c01\u201d\u8fd9\u5925 \u8ba1\u9053\uff1a\u201c\u5c31\u662f\u5750\u5728\u90a3\u8fb9\u89d2\u4e0a\u7684\u90a3\u4f4d\u5ba2\u4eba\u3002\u201d\u4ed6\u56de\u8fc7\u8eab\uff0c\u60f3\u6307\u7ed9\u4ed6\u4eec\u77f3\u53c8\u5f81\u4f0e\u3002 \u3000\u3000\u90a3\u8fb9\u89d2\u4e0a\u7684\u684c\u5b50\u4e0a\u8fd8\u6446\u7740\u9152\u83b1\uff0c\u4eba\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u8d70\u5728\u6700\u5f8c\u9762\uff0c\u8d70\u4e86\u51e0\u6b65\u53c8\u56de\u8fc7\u5934\u62cd\u4e86\u62cd\u90a3\u9001\u5ba2\u4e0b\u697c\u7684\u5925\u8ba1\u80a9\u8180\uff0c\u9053\uff1a\u201c\u6211\u6709\u4ef6\u4e8b \u60f3\u95ee\u95ee\u4f60\u3002\u201d\u8fd9\u5925\u8ba1\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8d62\u4e86\u8fd9\u9ebd\u591a\u94b1\u51c6\u5907\u600e\u9ebd\u82b1\u5462\uff1f\u201d\u8fd9\u5925\u8ba1 \u9053\uff1a\u201c\u6211\u4e0d\u51c6\u5907\u82b1\u5b83\u3002\u201d\u90ed\u5927\u8def\u77aa\u7740\u4ed6\u5c31\u597d\u50cf\u5ffd\u7136\u770b\u5230\u4e2a\u5723\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u5925\u8ba1\u5ffd\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u51c6\u5907\u7528\u5b83\u4f5c\u672c\u94b1\u518d\u53bb\u8d62\u591a\u4e9b\u6700\u8fd1\u6211\u624b\u6c14\u4e0d\u9519\u3002\u201d\u90ed\u5927\u8def\u8fd8\u5728 \u77aa\u7740\u4ed6\u5ffd\u7136\u5927\u7b11\uff0c\u7b11\u5f97\u5f2f\u4e0b\u8170\u5dee\u70b9\u4ece\u697c\u4e0a\u6eda\u4e0b\u4ed6\u5927\u7b11\u7740\u62cd\u8fd9\u5925\u8ba1\u7684\u80a9\u9053\uff1a\u201c\u597d\u4e3b\u610f\u597d\u4e3b\u610f\u5c31 \u8981\u8fd9\u6837\u4eba\u7c7b\u624d\u4f1a\u8fdb\u6b65\u6211\u4ee3\u8868\u5929\u535c\u7684\u4eba\u611f\u6fc0\u4f60\u3002\u201d\u8fd9\u5925\u8ba1\u8fd8\u60f3\u95ee\uff1a\u201c\u611f\u6fc0\u6211\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u5374 \u5df2\u8d70\u4e0b\u4e86\u697c\u3002 \u3000\u3000\u8fd9\u5925\u8ba1\u53f9\u4e86\u53e3\u6c14\uff0c\u64c2\u7740\u5934\u54fa\u54fa\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e9b\u4eba\u4e0d\u4f46\u662f\u51a4\u5927\u5934\u800c\u662f\u75af\u5b50\u3002\u201d\u4ee5\u524d\u6709\u4e2a\u5f88 \u806a\u660e\u7684\u4eba\u8bf4\u8fc7\u53e5\u5f88\u806a\u660e\u7684\u8bdd\u201c\u88ab\u4eba\u5f53\u505a\u51a4\u5927\u5934\u548c\u75af\u5b50\u5176\u5b9e\u4e5f\u662f\u4ef6\u72e0\u6709\u8da3\u7684\u4e8b\u8511\u81f3\u6bd4\u88ab\u4eba\u5f53\u505a \u82f1\u65c5\u5723\u8d24\u66f4\u6709\u8da3\uff0c\u201d\u90a3\u5925\u8ba1\u5e76\u4e0d\u662f\u806a\u660e\u4eba\uff0c\u5f53\u7136\u6ca1\u542c\u8fc7\u8fd9\u53e5\u8bdd\xb7\u5c31\u7b97\u542c\u8fc7\u4e5f\u4e0d\u4f1a\u61c2\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u5176\u4e2d\u7684\u9053\u7406\uff0c\u672c\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u542c\u5f97\u6a2a\u7684\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u4e24\u79cd\u4eba\u3002 \u3000\u3000\u79cd\u4eba\u505a\u7684\u4e8b\u6c38\u8fdc\u662f\u89c4\u89c4\u77e9\u77e9\u3001\u987a\u7406\u6210\u7ae0\u4ed6\u4eec\u505a\u7684\u4e8b\u65e0\u8bba\u8c01\u90fd\u80fd\u731c\u5f97\u51fa\u90fd\u80fd\u60f3\u5f97\u901a\u3002 \u3000\u3000\u53e6\u2018\u79cd\u4eba\u505a\u4e8b\u5374\u4e0d\u540c\u4e86\xb7\u4ed6\u851a\u4e13\u559c\u6b22\u505a\u4e9b\u795e\u51fa\u9b3c\u6ca1\u7684\u4e8b\uff0c\u975e\u4f46\u522b\u4eba\u60f3\u4e0d\u901a\u4ed6\u4eec\u5728\u505a\u4ec0 \u9ebd\uff0c\u4e5f\u8bb8\u8fde\u4ed6\u4ecd\u81ea\u5df1\u90fd\u60f3\u4e0d\u901a\uff0c\u738b\u52a8\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u6797\u592a\u5e73\u4e5f\u662f\u3002 \u3000\u3000\u4f46\u4e16\u4eba\u5374\u8fd8\u6709\u6837\u4e1c\u897f\u6bd4\u8fd9\u79cd\u4eba\u66f4\u795e\u51fa\u9b3c\u6ca1\u3002 \u3000\u3000\u90a3\u5c31\u662f\u94b1\u3002 \u3000\u3000\u4f60\u4e0d\u60f3\u8981\u94b1\u7684\u65f6\u5019\u5b83\u5f80\u5f80\u4f1a\u65e0\u7f18\u65e0\u6545\u3001\u83ab\u540d\u5176\u5999\u7684\u6765\u4e86\uff0c\u4f60\u5458\u9700\u8981\u5b83\u7684\u65f6\u5019\u5374\u5f80\u5f80\u8fde\u5b83 \u7684\u5f71\u5b50\u90fd\u770b\u4e0d\u5230\u3002 \u3000\u3000\u6740\u4eba\u662f\u4ec0\u9ebd\u6ecb\u5473\uff1f\u5f88\u5c11\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4e07\u4e2a\u4eba\u4e2d\u4e5f\u8bb8\u53ea\u6709\u4e2a\u662f\u6740\u8fc7\u4eba\u7684\u3002 \u3000\u3000\u6709\u4eba\u8bf4\uff1a\u201c\u4e2d\u7ba1\u6740\u4eba\u662f\u4ec0\u9ebd\u6ecb\u5473\u81f3\u5c11\u603b\u6bd4\u88ab\u4eba\u6740\u597d\u3002\u201d\u8bf4\u8fd9\u79cd\u8bdd\u7684\u4eba\u4ed6\u81ea\u5df1\u5b9a\u6ca1\u6709\u6740\u8fc7 \u4eba\u3002\u4e5f\u6709\u4eba\u8bf4\u201c\u6740\u4eba\u7684\u6fc0\u6627\u6bd4\u6b7b\u8fd8\u53ef\u6015\u3002\u201d\u8bf4\u8fd9\u79cd\u8bdd\u7684\u4eba\u5c31\u7b97\u81ea\u5df1\u6ca1\u6709\u6740\u8fc7\u4eba\xb7\u81f3\u5c11\u5df2\u7ecf\u5f88 \u63a5\u8fd1\u4e86\uff0c\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u6740\u8fc7\u4eba\uff1f\u201d\uff1a\u201c\u4f60\u600e\u9ebd\u6740\u4ed6\u7684\uff1f\u201d\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u6740\u4ed6\uff1f\u201d\u6797\u592a\u5e73\u76f4 \u5728\u7b49\u7740\u4ed6\u4eec\u95ee\u4ed6\u8fd9\u4e8c\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u5e72\u52a8\u3001\u71d5\u4e03\u3001\u90ed\u5927\u8def\u7acb\u4e2a\u4eba\u53c8\u597d\u50cf\u7ea6\u597d\u4e86\u8fde\u53e5\u8bdd\u90fd\u6ca1\u6709\u8def\u4e8c\u4eba\u6839\u672c\u6ca1\u6709\u5f00\u8fc7\u53e3\u3002 \u3000\u3000\u53bf\u57ce\u8ddd\u79bb\u90a3\u5c71\u57ce\u5e76\u4e0d\u8fdc\u4f46\u662f\u4e0d\u8bf4\u8bdd\u7684\u65f6\u5019\u5c31\u663e\u5f97\u5f88\u8fdc\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u7792\u9192\u5546\u642d\u6ca1\u642d\u7684\u54fc\u7740\u5c0f\u8c03\u66f2\u8c03\u4e5f\u8bb8\u5df2\u6d41\u4f20\u5f88\u4e45\u6b4c\u8bcd\u5212\u5b9a\u662f\u4ed6\u81ea\u7f16\u7684\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4e4b\u5916\u6ca1\u6709\u4eba\u80fd\u7f16\u5f97\u51fa\u8fd9\u79cd\u6b4c\u8bcd\u6765\u3002 \u3000\u3000\u50da\u7684\u65f6\u5019\u6210\u98ce\u53bb\u7684\u65f6\u5019\u7a00\u677e\u3002\u6765\u7684\u65f6\u5019\u4fee\u8f66\u53bb\u7684\u65f6\u5019\u4e58\u98ce\u3002\u7c73\u521a\u65f6\u5019\u4e2d\u5f53\u54cd\u53bb\u7684\u65f6\u5019\u2606 \u6210\u4eba\u4e2a\u4f53\u961f\u9650\u2026\u2500\u201d\u71d5\u4e03\u5ffd\u7136\u903c\uff1a\u201c\u4f60\u5728\u54bd\u4eec\u9ebd\u201c\u90ed\u5927\u8def\u9053\uff1a\u201c\u8fd9\u53eb\u2018\u6765\u53bb\u6b4c\u2019\xb7\u6765\u6765\u53bb \u53bb\uff0c\u6765\u53bb\u53bb\u7684\u4e0d\u6765\u6765\u7684\u4e0d\u53bb\u3002\u201d\u71d5\u4e03\u5ffd\u4e5f\u6444\u7740\u4ed6\u7684\u8c03\u5b50\u5531\u9053\u201c\u653e\u7684\u4e54\u901a\u901a\u7684\u4e0d\u653e\u653e\u653e\u901a\u901a\u901a \u4e00\u653e\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u653e\u4ec0\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u72d7\u5c41\u3002\u8fd9\u53eb\u653e\u72d7\u5c41\u3002\u201d\u90ed\u5927\u8def\u677f\u7740\u8138\u9053\uff1a\u201c\u4f60\u4eec \u7528\u4e0d\u7740\u81ed\u6211\u4ee5\u524d\u6709\u4eba\u6c42\u6211\u5531\u6211\u8fd8\u61d2\u5f97\u5531\u56db\u3002\u201d\u738b\u52a8\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u90a3\u4e9b\u662f\u4ec0\u9ebd\u4eba\u3002\u201d\u71d5 \u4e03\u7728\u7728\u773c\u9053\uff1a\u201c\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d\u5de5\u52a8\u9053\uff1a\u201c\u804b\u5b50\u3002\u201d\u90ed\u5927\u8def\u60f3\u677f\u8d77\u8138\u81ea\u5df1\u5374\u5fcd\u4e0d\u4f4f\u7b11\u3002 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u804b\u5b50\u81f3\u5c11\u6bd4\u90a3\u4e9b\u88c5\u804b\u4f5c\u54d1\u7684\u4eba\u597d\u3002\u201d\u90ed\u5927\u8def\u7728\u7728\u773c\xb7\u9053\uff1a\u201c\u8c01\u88c5 \u804b\u4f5c\u54d1\uff1f\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4f60\xb7\u4f60\u4f60\u3002\u201d\u4ed6\u7528\u624b\u5f80\u4ed6\u4eec\u4e8c\u4e2a\u4eba\u80a1\u4e0a\u4e2a\u4e2a\u70b9\u8fc7\u53bb\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec \u5fc3\u91cc\u660e\u660e\u6709\u8bdd\u8981\u95ee\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u95ee\u51fa\u6765\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u95ee\u662f\u4e0d\u5fc5\u95ee\uff1f\u201d\u6797\u592a\u5e73\u9053\uff0c \u201c\u4e3a\u4ec0\u9ebd\u4e0d\u5fc5\u95ee\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u90a3\u79cd\u4eba\u6d3b\u7740\u4e0d\u6a21\u591a\xb7\u6b7b\u4e86\u4e5f\u4e0d\u5acc\u5c11\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u5bf9\u5bf9\u90a3 \u79cd\u4eba\u6b7b\u4e2a\u5c11\u4e2a\xb7\u8d8a\u5c11\u8d8a\u597d\u3002\u201d\u4ed6\u62cd\u4e86\u62cd\u6797\u592a\u5e73\u7684\u80a9\u7b11\u7740\u9053\u201c\u4f60\u65e2\u7136\u6ca1\u6709\u6740\u9519\u4eba\uff0c\u6211\u3002\u53c8\u4f55\u5fc5 \u95ee\u5462\uff1f\u2606\u6797\u592a\u5e73\u54ac\u7740\u7259\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u6740\u8fc7\u4eba\u6ca1\u6709\uff1f\uff1f\u90ed\u5927\u8def\u770b\u770b\u58ec\u52a8\u738b\u52a8\u770b\u770b\u71d5\u4e03\u3002\u85d0\u4e03 \u82e6\u7b11\u9053\u201c\u53ea\u88ab\u4eba\u6740\u8fc7\u3002\u201d\u6797\u592a\u5e73\u5ffd\u7136\u7eb5\u8eab\u5411\u8def\u65c1\u63a0\u4e86\u8fc7\u53bb\u521a\u843d\u5230\u6811\u5f8c\u54ed\u58f0\u5df1\u4f20\u5de5\u51fa\u6765\u3002 \u3000\u3000\u71d5\u4e03\u770b\u770b\u90ed\u5927\u8def\u90ed\u5927\u8def\u770b\u770b\u738b\u52a8\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4ed6\u4ee5\u524d\u6ca1\u6709\u6740\u8fc7\u4eba\u3002\u201d\u7ff1\u5927\u8def\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u6740\u4eba\u3002\u201d\u85cf\u4e03\u53f9\u4e86 \u53e3\u6c14\xb7\u9053\uff1a\u201c\u539f\u6765\u6740\u4eba\u7684\u6ecb\u6627\u6bd4\u88ab\u6740\u8fd8\u96be\u53d7\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u5357\u5bcc\u4e11\u53d1\u73b0\u4ed6\u5728\u5f8c\u9762\u8ddf\u8e2a\u5b9a\u4ee5\u4e3a \u4ed6\u5df3\u53d1\u73b0\u4e86\u9ed1\u5403\u9ed1\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u5c31\u5148\u5411\u4ed6\u51fa\u624b\u60f3\u6740\u4e86\u4ed6\u706d\u53e3\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8c01\u77e5\u60f3\u6740\u4eba \u7684\uff0c\u53cd\u800c\u88ab\u6740\u4e86\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u6797\u592a\u5e73\u7684\u6b66\u529f\u597d\u50cf\u6bd4\u6211\u4eec\u5f3a\u5f97\u591a\xb7\u6bd4\u5357\u5bcc\u4e11\u8fd8\u5f3a\u5f97\u90ed\u5927\u8def\u53f9 \u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u505a\u4eba\u4e0d\u53ef\u8c8c\u76f8\u6d77\u6c34\u4e0d\u53ef\u6597\u91cf\xb7\u6211\u521a\u770b\u5230\u4ed6\u7684\u65f6\u5019\xb7\u8fd8\u4ee5\u4e3a\u4ed6\u8fde\u652f\u9e21\u90fd\u6293\u4e0d \u4f4f\u3002\u201d\u54ed\u58f0\u8fd8\u6ca1\u6709\u505c\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u60f3\u6740\u4eba\u7684\u672a\u5fc5\u6740\u5f97\u4e86\u4eba\xb7\u4ed6\u867d\u7136\u6740\u4e86\u4eba\u5374\u4e0d\u60f3\u6740\u4eba\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4eec \u53bb\u529d\u529d\u4ed6\u597d\u4e0d\u597d\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u597d\u3002\u201d\u2606\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u54ed\u867d\u7136\u6ca1\u6709 \u7b11\u597d\uff0c\u4f46\u4e2a\u4eba\u5076\u800c\u80fd\u5927\u54ed\u4e00\u573a\u4e5f\u4e0d\u9519\u3002\u201d\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u6211\u8fd8\u662f\u5b81\u53ef\u7b11\u4e2a\u4eba\u800d\u7b11\u7684\u65f6\u5019\u81f3\u5c11 \u7528\u4e0d\u7740\u8eb2\u5728\u6811\u5f8c\u5934\u3002\u201d\u71d5\u4e03\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u800c\u4e14\u4f60\u65e0\u8bba\u600e\u9ebd\u7b11\u90fd\u4e0d\u5fc5\u4f2f\u4eba\u5bb6\u6765\u770b\u70ed\u95f9\u3002\u201d \u4f60\u8d8a\u6015\u522b\u4eba\u770b\u70ed\u95f9\u8d8a\u6709\u4eba\u6765\u770b\u70ed\u95f9\u3002 \u3000\u3000\u73b0\u5728\u8fd8\u6ca1\u6709\u5927\u9ed1\u8def\u4e0a\u7684\u4eba\u4e0d\u5f88\u591a\u6709\u7684\u4eba\u5df2\u505c\u4e0b\u811a\uff0c\u76f4\u7740\u8116\u5b50\u5f80\u8fd9\u8fb9\u77a7\u6709\u7684\u4eba\u751a\u81f3\u5df2\u8d70\u4e86 \u8fc7\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u64e6\u4e86\u64e6\u6c57\u82e6\u7b11\u7740\u6084\u6084\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u522b\u4eba\u83ab\u8981\u6000\u7591\u4ed6\u662f\u88ab\u6211\u4eec\u6b3a\u8d1f\u54ed\u7684\u3002\u201d\u6ca1\u6709 \u4eba\u201c\u676f\u7591\u201d\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7b80\u76f4\u90fd\u5df2\u786e\u5b9a\u4e86\u3002 \u3000\u3000\u770b\u5230\u8fd9\u4e9b\u4eba\u7684\u773c\u8272\u71d5\u4e03\u4e5f\u4e0d\u7981\u64e6\u4e86\u64e6\u6c57\uff0c\u9053\uff1a\u201c\u4f60\u6f84\u5feb\u60f3\u6cd5\u5b50\u628a\u4ed6\u529d\u8d70\u597d\u4e0d\u597d\uff1f\u201d\u90ed\u5927 \u8def\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u90a3\u9ebd\u5927\u672c\u4e8b\u6211\u6700\u591a\u4e5f\u4e0d\u8fc7\u53ea\u80fd\u6316\u4e2a\u6d1e\u3002\u201d\u9893\u4e03\u9053\uff1a\u201c\u6316\u4e2a\u6d1e\u5e72\u4ec0\u9ebd\uff1f\u201d\u90ed \u5927\u8def\u9053\uff1a\u201c\u597d\u94bb\u5230\u6d1e\u91cc\u53bb\u4e5f\u514d\u5f97\u88ab\u4eba\u5bb6\u8fd9\u9ebd\u6837\u6b7b\u76ef\u7740\u3002 \u3000\u3000\u71d5\u4e03\u53f9\u9053\uff1a\u201c\u4f60\u6700\u597d\u6316\u4e2a\u5927\u70b9\u7684\u3002\u201d\u90ed\u5927\u8def\u72e0\u72e0\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u662f\u5c11\u8f93\u4e9b\uff0c\u6216\u662f\u6ca1\u6709\u8f93\u5149 \u6211\u4eec\u81f3\u5c11\u8fd8\u80fd\u96c7\u8f86\u8f66\u8ba9\u4ed6\u5750\u5728\u8f66\u91cc\u53bb\u54ed\u4e2a\u75db\u5feb\u3002\u201d\u8fd9\u53e5\u8bdd\u4e00\u8bf4\u5c45\u7136\u771f\u7684\u5c31\u6709\u8f86\u72e0\u6f02\u4eae\u7684\u9a6c\u8f66 \u9a76\u4e86\u8fc7\u6765\uff0c\u800c\u4e14\u5c31\u505c\u5728\u4ed6\u4eec\u9762\u524d\u3002 \u3000\u3000\u71d5\u4e03\u819c\u4e86\u738b\u52a8\u773c\uff0c\u6084\u6084\u9053\uff1a\u201c\u6211\u4eec\u6700\u5f8c\u90a3\u628a\u7684\u786e\u4e0d\u8be5\u8d4c\u7684\u65e2\u7136\u5df2\u8f93\u5b9a\u4e86\u5c31\u4e0d\u8be5\u60f3\u7ffb \u672c\u3002\u201d\u738b\u52a8\u6de1\u8c08\u9053\u201c\u8d4c\u94b1\u7684\u4eba\u82e5\u4e0d\u60f3\u7ffb\u672c\u9760\u8d4c\u5403\u996d\u7684\u4eba\u65e9\u5c31\u5168\u90fd\u997f\u6b7b\xb7\u4f60\u603b\u4e0d\u81f3\u65bc\u60f3\u770b\u4eba\u997f \u6b7b\u5427\u3002\u201d\u90a3\u9a6c\u8f66\u7684\u8f66\u592b\u5ffd\u7136\u8df3\u4e0b\u8f66\uff0c\u8d70\u5230\u4ed6\u4eec\u9762\u524d\uff0c\u966a\u7740\u7b11\u9053\u201c\u90a3\u4f7c\u662f\u90ed\u5927\u7237\uff1f\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u8c01\u627e\u6211\uff1f\u627e\u6211\u5e72\u4ec0\u9ebd\uff1f\u201d\u8f66\u592b\u8eac\u8eab\u9053\uff1a\u201c\u8bf7\u90ed\u5927\u7237\u4e0a\u8f66\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u5750 \u8f66\uff0c\u6211\u559c\u6b22\u8d70\u8def\u3002\u201d\u8f66\u592b\u8d54\u7b11\u9053\uff1a\u201c\u8fd9\u8f86\u8f66\u662f\u90ed\u5927\u7237\u7684\u670b\u53cb\u7279\u5730\u96c7\u6765\u7684\u8f66\u94b1\u65e9\u5df2\u4ed8\u90ed\u5927\u8def\u6014 \u4e86\u5f81\u9053\uff1a\u201c\u8c01\u96c7\u7684\uff1f\u201d\u8f66\u592b\u7b11\u9053\uff1a\u201c\u90a3\u662f\u90ed\u5927\u7237\u7684\u670b\u53cb\u90ed\u5927\u7237\u4e0d\u8ba4\u5f97\u5c0f\u4eba\u600e\u9ebd\u4f1a\u8ba4\u5f97\u201d\u90ed\u5927 \u8def\u60f3\u4e86\u60f3\xb7\u5ffd\u7136\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u60f3\u8d77\u4ed6\u662f\u8c01\u4ed6\u662f\u6211\u7684\u5e72\u513f\u5b50\u3002\u201d\u5750\u4e0a\u8f66\u6797\u592a\u5e73\u5c31\u4e0d\u54ed\u4e86\xb7\u53ea \u662f\u5750\u5728\u90a3\u91cc\u5446\u5446\u7684\u53d1\u6014\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u5728\u53d1\u5f81\u3002 \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u771f\u6709\u4e7e\u513f\u5b50\uff1f\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u6709\u4e2a\u89c1\u9b3c\u7684\u4e7e\u513f\u5b50\u3002\u6211\u5c31\u7b97\u60f3 \u505a\u4eba\u5bb6\u7684\u4e7e\u513f\u5b50\uff0c\u4eba\u5bb6\u4e5f\u5acc\u6211\u592a\u7a77\uff0c\u54ea\u6709\u4eba\u80af\u505a\u6211\u7684\u4e7e\u513f\u5b50\uff1f\u201d\u71d5\u4e03\u76b1\u7709\u9053\uff1a\u201c\u90a3\u9ebd\u96c7\u8f66\u7684 \u4eba\u662f\u8c01\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u516b\u6210\u5c31\u662f\u90a3\u4e2a\u5728\u594e\u5143\u9986\u66ff\u6211\u4eec\u4f1a\u5e10\u7684\u4eba\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u77a7\u89c1\u90a3 \u4eba\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u90a3\u65f6\u522b\u4eba\u4e0d\u770b\u6211\uff0c\u5df2\u7ecf\u8c22\u5929\u8c22\u5730\u4e86\xb7\u6211\u600e\u9ebd\u8fd8\u6562\u53bb\u770b\u522b\u4eba\uff1f\u201d\u4e2a \u4eba\u8981\u4ed8\u5e10\xb7\u53e3\u888b\u91cc\u5374\u6ca1\u94b1\u7684\u65f6\u5019\u7684\u786e\u8fde\u5934\u90fd\u62cd\u4e0d\u8d77\u6765\u7684\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u5462\u201d\u4ed6\u6ca1\u6709\u95ee\u6797\u592a\u5e73\uff0c\u95ee\u7684\u662f\u738b\u52a8\u3002 \u3000\u3000\u6797\u592a\u5e73\u90a3\u65f6\u5f53\u7136\u4e5f\u6ca1\u6709\u5fc3\u60c5\u53bb\u6ce8\u610f\u522b\u4eba\u3002 \u3000\u3000\u738b\u52a8\u7b11\u4e86\u7b11\uff0c\u9053\u54ea\u65f6\u6211\u53ea\u987e\u7740\u770b\u90ed\u5927\u5c11\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u8fc7\u4ed6\u90a3\u9ebd\u53ef\u7231\u3002\u201d \u65bc\u662f\u71d5\u4e03\u4e5f\u5f00\u59cb\u53d1\u6014\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u770b\u89c1\u66ff\u4ed6\u4eec\u4ed8\u5e10\u7684\u662f\u8c01\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u90a3\u8f66\u592b\u627e\u7684\u662f\u90ed\u5927\u5c11\u90a3\u4eba\u5b9a\u662f\u90ed\u5927\u7237\u7684\u670b\u53cb\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u53ef \u6ca1\u6709\u90a3\u9ebd\u56ed\u7684\u670b\u53cb\u6211\u7684\u670b\u53cb\u4e2d\u6700\u7530\u7684\u5c31\u662f\u4f60\u3002\u3002 \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u5f88\u9614\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u8fd8\u6709\u680b\u623f\u5b50\xb7\u867d\u7136\u662f\u4eba\u538c\u9b3c\u4e0d\u7231\u7684\u623f\u5b50\u4f46\u623f \u5b50\u603b\u5f52\u662f\u623f\u5b50\u3002\u201d\u738b\u52a8\u8c08\u8c08\u9053\uff1a\u201c\u4f60\u82e5\u559c\u6b22\xb7\u6211\u5c31\u9001\u7ed9\u4f60\u5427\u3002\u201d\u7ff1\u5927\u8def\u9053\uff1a\u201c\u6211\u4e0d\u8981\u3002\u201d\u738b \u52a8\u8c0a\uff1a\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u8981\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u8eab\u65e0\u957f\u7269\u56ca\u7a7a\u5982\u6d17\u4e50\u5f97\u65e0\u7275\u6302\u4e0d\u8c61\u4f60\u4eec\u8fd8\u8981 \u4e3a\u522b\u7684\u4e8b\u62c5\u5fc3\u3002\u201d\u85cf\u4e03\u9053\uff1a\u201c\u738b\u8001\u5927\u8fd8\u6709\u62e3\u623f\u5b50\u53ef\u4ee5\u62c5\u5fc3\u6211\u6709\u4ec0\u9ebd\u597d\u62c5\u5fc3\u7684\u201d\u90ed\u5927\u8def\u4e0a\u4e0a\u4e0b \u4e0b\u6f02\u4e86\u4ed6\u773c\xb7\u7b11\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u8fd8\u6709\u8eab\u65b0\u8863\u907f\u505a\u4e8b\u7684\u65f6\u5019\u5c31\u514d\u4e0d\u4e86\u800d\u62c5\u5fc3\u4f1a\u4e0d\u4f1a\u8863\u670d\u5f04\u810f\u5750\u4e0b \u6765\u7684\u65f6\u5019\u514d\u4e0d\u4e86\u8981\u770b\u770b\u5730\u4e0a\u6709\u6ca1\u6709\u6ce5\u5df4\xb7\u600e\u53ca\u5f97\u6211\u8fd9\u6837\u81ea\u7531\u81ea\u5728\u3002\u201d\u71d5\u4e03\u51dd\u89c6\u7740\u4ed6\u9053\uff1a\u201c\u8fd9 \u4e16\u4e0a\u771f\u7684\u6ca1\u6709\u4e00\u4e2a\u4f60\u5173\u5fc3\u7684\u4eba\uff1f\u6ca1\u6709\u6837\u4f60\u5173\u5fc3\u7684\u4e8b\uff1f\u201d\u90ed\u5927\u8def\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\u76ee\u5149\u7adf\u4f3c\u4e4e\u9732\u51fa \u4e1d\u60b2\u4f24\u4e4b\u8272\u3002 \u3000\u3000\u85cf\u4e03\u5ffd\u7136\u53d1\u73b0\u8fd9\u4eba\u4e5f\u8bb8\u5e76\u4e0d\u8c61\u8868\u9762\u770b\u6765\u90a3\u9ebd\u5f00\u5fc3\xb7\u8bf4\u4e0d\u5b9a\u4e5f\u6709\u4e9b\u4f24\u5fc3\u4e8b\u53ea\u4e0d\u8fc7\u4ed6\u76f4\u9690\u85cf \u5f97\u5f88\u597d\u4ece\u4e0d\u5404\u522b\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u53ea\u8ba9\u522b\u4eba\u7ec6\u9053\u4ed6\u7684\u5feb\u4e50\uff0c\u5206\u4eab\u4ed6\u7684\u5feb\u4e50\u3002\u4ece\u4e0d\u613f\u522b\u4eba\u6765\u5206\u4ed6\u7684\u75db\u82e6\u548c\u5fe7\u90c1\u3002 \u3000\u3000\u71d5\u4e03\u770b\u7740\u4ed6\uff0c\u53cc\u7741\u5b50\u5ffd\u7136\u53d8\u5f97\u4efd\u5916\u660e\u4eae\u3002 \u3000\u3000\u4ed6\u548c\u90ed\u5927\u8def\u76f8\u5904\u5f97\u8d8a\u4e45\uff0c\u8d8a\u89c9\u5f97\u90ed\u5927\u8def\u786e\u5b9e\u6700\u4e2a\u5f88\u53ef\u7231\u7684\u5165\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u738b\u52a8\u5ffd\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5feb\u5230\u4e86\u5feb\u5230\u5bb6\u4e86\u3002\u201d\u4ed6\u53f9\u606f\u4e2d\u5145\u6ee1\u4e86\u6b22 \u9971\u6ee1\u8db3\u4e4b\u610f\u3002 \u3000\u3000\u5f80\u7a97\u5916\u671b\u51fa\u53bb\u5df2\u53ef\u770b\u5230\u90a3\u5c0f\u5c0f\u7684\u5c71\u5761\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7235\u4e0d\u4f4f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u770b\u6765\u65e0\u8bba\u662f\u91d1\u7a9d\u94f6\u7a9d\u4e5f\u6bd4\u4e0d\u4e0a\u4f60\u7684\u90a3\u72d7\u7a9d\u3002\u201d\u738b\u52a8 \u77aa\u963b\u9053\uff1a\u201c\u6211\u7684\u72d7\u7a9d\uff1f\u201d\u90ed\u5927\u8def\u7b11\u4e86\u9053\uff1a\u201c\u6211\u4eec\u7684\u72d7\u7a9d\u3002\u201d\u9ec4\u660f\u3002 \u3000\u3000\u5915\u9633\u6ee1\u5c71\u3002 \u3000\u3000\u534a\u67af\u7684\u79cb\u8349\u5728\u5915\u9633\u4e0b\u770b\u6765\u5b9b\u5982\u9ec4\u91d1\uff0c\u904d\u5730\u7684\u9ec4\u91d1\u77f3\u677f\u780c\u6210\u7684\u5c0f\u5f84\u659c\u5411\u524d\u65b9\u4f38\u5c55\uff0c\u5b9b\u5982\u9ec4 \u91d1\u5806\u4e2d\u7684\u4e00\u4e32\u767d\u7389\u3002 \u3000\u3000\u98ce\u5728\u5439\uff0c\u9e1f\u5728\u557c\u79cb\u5728\u4f4e\u8bed\u6df7\u5408\u6210\u79cd\u6bd4\u97f3\u4e50\u8fd8\u7f8e\u5999\u7684\u58f0\u97f3\u5b83\u7f8e\u5999\u5f97\u5b9b\u5982\u60c5\u4eba\u5728\u8033\u7554\u4f4e\u8bed\u3002 \u3000\u3000\u6ee1\u5c71\u5f25\u6f2b\u7740\u82b1\u7684\u9999\u6c14\u3001\u8349\u7684\u9999\u6c14\u3001\u98ce\u7684\u9999\u6c14\u3002\u751a\u81f3\u8fde\u5915\u9633\u90fd\u4eff\u4f5b\u88ab\u67d3\u4e0a\u4e86\u82ac\u52b3\u82ac\u82b3\u5f97\u5b9b \u5982\u60c5\u4eba\u7525\u8fb9\u7684\u67d4\u53d1\u3002 \u3000\u3000\u4eba\u751f\u539f\u6765\u7adf\u5982\u6b64\u82ac\u82b3\u5982\u6b64\u7f8e\u5999\u3002 \u3000\u3000\u90ed\u5927\u8def\u957f\u957f\u5438\u4e86\u53e3\u6c14\u5927\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u7a77\u539f\u6765\u4e5f\u662f\u4ef6\u5f88\u5f00\u5fc3\u7684\u4e8b\u3002\u201d\u71d5\u4e03\u9053\uff1a \u201c\u5f00\u5fc3\uff1f\u201d\u90ed\u5927\u8def\u8bf4\u9053\uff1a\u201c\u6709\u94b1\u4eba\u6709\u51e0\u4e2a\u80fd\u4eab\u53d7\u5230\u8fd9\u6837\u7684\u7f8e\u666f\uff1f\u80fd\u547c\u5438\u5230\u8fd9\u6837\u7684\u9999\u6c14\uff1f\u4ed6\u4eec \u53ea\u80fd\u95fb\u5f97\u5230\u94dc\u81ed\u6c14\u3002\u201d\u71d5\u4e03\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u53d1\u89c9\u4ed6\u7684\u7b11\u5bb9\u5982\u5915\u9633\u822c\u707f\u70c2\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u53d1\u73b0\u4f60\u4e00\u70b9\u4e5f\u4e0d\u4e11\u53ea\u4e0d \u8fc7\u6709\u65f6\u7684\u786e\u592a\u810f\u4e86\u4e9b\u3002\u201d\u71d5\u4e03\u8fd9\u6b21\u5c45\u7136\u6ca1\u6709\u53cd\u5507\u76f8\u8ba5\u53cd\u800c\u5782\u4e0b\u4e86\u5934\u3002\u4ed6\u672c\u6765\u5e76\u4e0d\u662f\u8fd9\u9ebd\u597d\u6b3a \u8d1f\u7684\u4eba\u662f\u4ec0\u9ebd\u4ee4\u4ed6\u6539\u53d8\u4e86\u7684\uff1f\u662f\u8fd9\u5915\u9633\uff1f\u662f\u8fd9\u67d4\u98ce\uff1f\u8fd8\u662f\u90ed\u5927\u8def\u8fd9\u660e\u6717\u7684\u7b11\u8138\uff1f\u738b\u52a8\u5ffd\u7136 \u9053\uff1a\u201c\u6709\u94b1\u4e5f\u5e76\u4e0d\u662f\u574f\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u7a77\u5462\uff1f\u201d\u738b\u52a8\u9053\u201c\u7a77\u4e5f\u4e0d\u574f\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ec0 \u9ebd\u624d\u574f\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4ec0\u9ebd\u90fd\u4e0d\u574f\uff0c\u574f\u4e0d\u53ea\u770b\u4f60\u8fd9\u4e2a\u4eba\u61c2\u4e0d\u61c2\u5f97\u4eab\u53d7\u4eba\u751f\u3002\u201d\u90ed\u5927\u8def\u4ed4\u7ec6\u5480 \u56bc\u7740\u4ed6\u8fd9\u53e5\u8bdd\u5fc3\u4e2d\u5ffd\u7136\u5145\u6ee1\u4e86\u6e29\u6696\u5e78\u798f\u548c\u6ee1\u8db3\u3002 \u3000\u3000\u4ed6\u6ee1\u8db3\xb7\u53ea\u56e0\u4ed6\u80fd\u6d3b\u7740\u3002 \u3000\u3000\u4ed6\u6d3b\u7740\uff0c\u5c31\u80fd\u4eab\u53d7\u4eba\u751f\u5982\u6b64\u7f8e\u5999\u7684\u4eba\u751f\u3002 \u3000\u3000\u6240\u4ee5\xb7\u670b\u53cb\u4ecd\u4f60\u7edd\u4e0d\u8981\u4e3a\u6709\u94b1\u800c\u70e6\u607c\u66f4\u4e0d\u8981\u4e3a\u7a77\u800c\u70e6\u607c\u3002 \u3000\u3000\u53ea\u8981\u4f60\u53e3\u5f97\u4eab\u53d7\u4eba\u751f\u4f60\u5c31\u7b97\u6ca1\u6709\u767d\u6d3b\u3002 \u3000\u3000\u90a3\u9ebd\u6709\u5929\u4f60\u5c31\u7b97\u6b7b\u4e86\uff0c\u4e5f\u4f1a\u6b7b\u5f97\u5f88\u5f00\u5fc3\u3002 \u3000\u3000\u56e0\u4e3a\u4f60\u6d3b\u5f97\u4e5f\u6bd4\u522b\u4eba\u5f00\u5fc3\u3002 \u3000\u3000\u9a6c\u8f66\u4e0d\u80fd\u5c71\uff0c\u4ed6\u4eec\u5c31\u8d70\u4e0a\u5c71\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u5f97\u5f88\u6162\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u65e0\u8bba\u8d70\u5f97\u591a\u6162\uff0c\u603b\u8fd8\u662f\u4f1a\u8d70\u5230\u7684\u3002 \u3000\u3000\u5929\u5df2\u6e10\u6e10\u9ed1\u4e86\u3002 \u3000\u3000\u4ed6\u851a\u4e5f\u7edd\u4e0d\u62c5\u5fc3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u5929\u5f88\u5feb\u8fd8\u4f1a\u4eae\u7684\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u5fc3\u4e2d\u5145\u6ee1\u4e86\u6b22\u5077\xb7\u5c31\u8fde\u6797\u592a\u5e73\u773c\u775b\u90fd\u660e\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u7ec8\u4e8e\u770b\u5230\u4e86\u738b\u52a8\u90a3\u680b\u623f\u4e8e\u867d\u7136\u662f\u680b\u53c8\u65e7\u53c8\u7834\u7684\u623f\u5b50\u4f46\u5728\u8fd9\u5915\u9633\u7530\u8106\u7684\u9ec4\u660f\u65f6\u770b\u6765\u4e5f \u7f8e\u4e3d\u7684\u4f3c\u5bab\u6bbf\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u5ea7\u5bab\u6bbf\u4ed6\u7684\u5bab\u6bbf\u5c31\u5728\u4ed6\u5fc3\u91cc\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u6709\u4e9b\u4eba\u5374\u5014\u504f\u627e\u4e0d\u5230\u3002 \u3000\u3000\u738b\u52a8\u5c16\u9510\u66f2\u9762\u5bb9\u4e5f\u53d8\u5f97\u67d4\u548c\u8d77\u6765\u5ffd\u7136\u7b11\u4e86\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u731c\u731c\u6211\u56de\u53bb\u5f8c\xb7\u7b2c\u4ef6\u4e8b\u60f3\u4e8e\u4ec0 \u9ebd\uff1f\u201d\u90ed\u5927\u8def\u548c\u71d5\u4e03\u540c\u65f6\u62a2\u7740\u9053\uff1a\u201c\u4e0a\u5e8a\u7761\u89c9\u3002\u201d\u738b\u52a8\u9053\uff1a\u201c\u7b54\u5bf9\u4e86\u3002\u201d\u4f46\u4eba\u751f\u4e2d\u65f6\u5e38\u4e5f\u4f1a \u53d1\u751f\u610f\u5916\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u6ca1\u6709\u8d70\u5230\u90a3\u680b\u5c4b\u5b50\xb7\u5ffd\u7136\u770b\u5230\u7a97\u5343\u91cc\u4eae\u8d77\u4e86\u706f\u5149\u3002 \u3000\u3000\u5f00\u59cb\u65f6\u662f\u5bf9\u7740\u95e8\u7684\u90a3\u6247\u5708\u5b50\u3002 \u3000\u3000\u7136\u5f8c\u6bcf\u6247\u7a97\u5b50\u90fd\u63a5\u7740\u6709\u706f\u5149\u4eae\u8d77\u3002 \u3000\u3000\u706f\u5149\u660e\u4eae\u4ed6\u4eec\u53c8\u6014\u4f50\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5c4b\u5343\u91cc\u6709\u4eba\uff1f\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6709\u670b\u53cb\u6765\u770b\u4f60\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u672c\u6765\u662f\u6709 \u7684\u81ea\u4ece\u6211\u5c06\u6700\u5f8c\u4e00\u5f20\u6905\u5b50\u5356\u6389\u4e86\u5f8c\u670b\u53cb\u5c31\u5ffd\u7136\u5168\u90fd\u4e0d\u89c1\u4e86\u3002\u201d\u4ed6\u6de1\u8c08\u7684\u7b11\u4e86\u7b11\xb7\u63a5\u7740\u9053\uff1a \u201c\u4ed6\u4eec\u4e5f\u8bb8\u5168\u90fd\u548c\u6211\u4e00\u6837\u61d2\u4f2f\u6765\u4e86\u4e4b\u5f8c\u6ca1\u5730\u65b9\u5750\u3002\u201d\u8fd9\u6de1\u8c08\u7684\u7b11\u5bb9\xb7\u6b63\u8c61\u5f81\u7740\u4ed6\u5bf9\u4eba\u751f\u4e86\u89e3 \u5f97\u591a\u9ebd\u6df1\u523b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5bf9\u4efb\u4f55\u4eba\u90fd\u6ca1\u6709\u5f88\u5927\u7684\u8981\u6c42\u3002 \u3000\u3000\u4ed6\u7ed9\u7684\u65f6\u5019\u4ece\u6ca1\u6709\u60f3\u5230\u8981\u6536\u56de\u6765\u8fd9\u4e5f\u8bb8\u5c31\u662f\u4ed6\u4e3a\u4ec0\u9ebd\u6d3b\u5f97\u6bd4\u522b\u4eba\u5feb\u4e50\u7684\u539f\u56e0\u4e4b\u3002 \u3000\u3000\u71d5\u4e03\u76b1\u7709\u9053\uff1a\u201c\u90a3\u9ebd\u662f\u8c01\u70b9\u7684\u706f\u5462\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4f55\u5fc5\u731c\uff1f\u53ea\u8981\u8fdb\u53bb\u770b\u770b\u5c82\u975e \u5c31\u77e5\u9053\u4e86\uff1f\u201d\u8fd9\u672c\u6765\u4e5f\u662f\u79cd\u5f88\u6b63\u786e\u7684\u6001\u5ea6\u4f46\u8fd9\u6b21\u5374\u9519\u4e86\u3002\u4ed6\u4eec\u8fdb\u53bb\u770b\u4e86\uff0c\u8fd8\u662f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6765\u8def\u4e0d\u660e\u7684\u4e66\u751f\u9675\u2606\u5750\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u706f\u5c31\u8c61\u662f\u81ea\u5df1\u71c3\u7740\u7684\u3002 \u3000\u3000\u5d2d\u65b0\u7684\u94dc\u706f\u4eae\u5f97\u8c61\u9ec4\u91d1\u3002 \u3000\u3000\u5d2d\u65b0\u7684\u94dc\u706f\u6446\u5728\u5d2d\u65b0\u7684\u68a8\u82b1\u6728\u684c\u535c\u5d2d\u65b0\u7684\u684c\u4e88\u6446\u5728\u5d2d\u65b0\u7684\u6ce2\u63ea\u5730\u6bef\u4e0a\u94dc\u706f\u65c1\u8fb9\u8fd8\u6709\u9c9c\u82b1 \u4ec0\u9ebd\u90fd\u6709\u3002 \u3000\u3000\u53ea\u8981\u662f\u4f60\u80fd\u5728\u4e00\u95f4\u5c4b\u5b50\u91cc\u770b\u5230\u7684\u4e1c\u897f\u8fd9\u5c4b\u5b50\u91cc\u5c31\u6837\u6837\u4ff1\u5168\u3002 \u3000\u3000\u8fd9\u91cc\u5c31\u50cf\u662f\u51fa\u73b0\u4e86\u5947\u8ff9\u3002 \u3000\u3000\u552f\u4e00\u8fd8\u6ca1\u6709\u6539\u53d8\u7684\u5c31\u662f\u738b\u52a8\u7684\u90a3\u5f20\u5927\u5e8a\u3002 \u3000\u3000\u4f46\u51b0\u4e0a\u4e5f\u6362\u4e86\u5d2d\u65b0\u7684\u88ab\u8925\u88ab\u4e0a\u8fd8\u653e\u7740\u82b1\u6735\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ad9\u5728\u95e8\u53e3\uff0c\u770b\u5f97\u773c\u73e0\u4e8e\u90fd\u5feb\u6389\u4e86\u535c\u6765\u3002\u54fa\u54fa\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e2a\u662f\u4f01\u9519\u4e86\u5730\u65b9\uff1f\u201d\u71d5 \u4e03\u82eb\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u8d70\u9519\u522b\u7684\u5730\u65b9\u7edd\u6ca1\u6709\u8fd9\u9ebd\u5927\u7684\u5e8a\u3002\u201d\u90ed\u5927\u8def\u53f9\u9053\u201c\u770b\u6765\u8fd9\u5730\u6253\u771f\u8c61\u662f\u6709\u795e \u4ed9\u6765\u7167\u987e\u8fc7\u4e86\u4e0d\u77e5\u9053\u661f\u4e2a\u662f\u5b89\u795e\u4ed9\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u770b\u6765\u91cf\u8001\u5927\u4e0b\u4e5f\u548c\u853c\u6c38\u6837\u662f\u4e2a\u8003\u6237\u611f\u52a8\u4e86 \u5929\u535c\u7684\u4ed9\u5b50\u3002\u201d\u90ed\u4eba\u8def\u9053\u201c\u4ed9\u5b50\u8bf4\u4e0d\u5b9a\u662f\u6765\u627e\u6211\u7684\u6211\u4e5f\u8db3\u4e2a\u5b5d\u5b50\u201c\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u50bb \u5b50\u3002\u201d\u4ed6\u4eec\u6687\u91cc\u867d\u8fd9\u9ebd\u6837\u8bf4\u5fc3\u91cc\u5374\u90fd\u5df2\u660e\u767d\u5b9a\u6709\u4e2a\u4eba\u5c06\u8fd9\u4e9b\u4e1c\u897f\u9001\u6765\u8fd9\u4eba\u4e5f\u8bb8\u5c31\u662f\u90a3\u5728\u594e\u4e5d \u9986\u66ff\u4ed6\u4eec\u8ba8\u5e10\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u9ebd\u8bf4\u53ea\u4e0d\u8fc7\u662f\u5728\u63a9\u9970\u5fc3\u91cc\u7684\u60ca\u7591\u548c\u4e0d\u5b89\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u731c\u4e0d\u51fa\u8fd9\u4eba\u662f\u6dee\u66f4\u731c\u4e0d\u51fa\u8fd9\u4eba\u4e3a\u4ec0\u9ebd\u8981\u505a\u8fd9\u4e9b\u4e8b\u3002 \u3000\u3000\u4e0a\u52a8\u6162\u6162\u7684\u8d70\u5230\u5e8a\u8fb9\u6162\u6162\u7684\u8131\u4e0b\u978b\u5b50\u5f88\u5feb\u7684\u8eba\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u65e0\u8bba\u505a\u4ec0\u9ebd\u4e8b\u65f6\u90fd\u6162\u6761\u65af\u7406\u70b9\u4e5f\u4e0d\u7740\u6025\u53ea\u6709\u9500\u4e0b\u53bb\u65f6\u7eab\u5feb\u5f97\u5f88\xb7\u5feb\u5f97\u8981\u547d\u3002 \u3000\u3000\u90ed\u5927\u8def\u76b1\u7709\u9053\uff1a\u201c\u4f60\u5c31\u8fd9\u6837\u7761\u4e86\u9ebd\uff1f\u201d\u738b\u52a8\u6253\u4e86\u4e2a\u5475\u6b20\u5475\u6b20\u5c31\u7b97\u4ed6\u7684\u56de\u7b54\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4e1c\u897f\u662f\u63a8\u9001\u6765\u7684\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u6211\u53ea\u77e5\u9053\u7d2f\u4e86\u5c31 \u8981\u7761\u89c9\u3002\u201d\u8fd9\u4e9b\u4e1c\u897f\u662f\u4ed9\u4ea5\u9001\u6765\u7684\u4e5f\u597d\uff0c\u662f\u6076\u9b3c\u9001\u6765\u7684\u4e5f\u597d\u4ed6\u90fd\u4e0d\u7ba1\u3002\u5c31\u7b97\u5929\u4e0b\u6240\u6709\u7684\u4ed9\u5973 \u548c\u6076\u9b3c\u5168\u90fd\u6765\u4e86\u4e5f\u4e0d\u80fd\u53eb\u4ed6\u4e0d\u7761\u89c9\u3002 \u3000\u3000\u4ed6\u53ea\u8981\u95ed\u4e0a\u773c\u775b\u597d\u50cf\u5c31\u7acb\u523b\u80fd\u7761\u5f97\u7740\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5012\u8fd8\u771f\u4f69\u670d\u4ed6\u3002\u201d\u71d5\u4e03\u54ac\u7740\u916e\u5507\u9053\uff1a\u201c\u6211\u5230\u5f8c\u9762\u7684\u9662\u5b50\u53bb\u770b\u770b \u4e5f\u8bb8\u4eba\u5728\u90a3\u91cc\u3002\u201d\u5f8c\u9762\u7684\u9662\u4e25\u91cc\u8fd8\u6709\u6392\u5c5e\u65bc\u5c31\u662f\u90a3\u5929\u9178\u6885\u6c64\u4ed6\u4eec\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u524d\u9762\u8fd9\u6392\u5c4b\u4e8e\u9664\u4e86\u6b63\u5385\u548c\u82b1\u5385\u5916\xb7\u8fd8\u6709\u4e03\u516b\u95f4\u7684\u623f\u9664\u4e86\u4e0a\u52a8\u7761\u7684\u8fd9\u95f4\u5916\u8fd8\u6709\u4e86\u95f4\u5c4b\u4e8e\u91cc \u4e5f\u6446\u7740\u5f88\u8212\u670d\u7684\u5e8a\u3002 \u3000\u3000\u90ed\u5927\u8def\u7792\u54fa\u9053\uff1a\u201c\u4ed6\u5c45\u7136\u8fd8\u77e5\u9053\u6211\u4eec\u6709\u56db\u4e2a\u4eba\u4f4f\u5728\u8fd9\u91cc\u60f3\u5f97\u771f\u5468\u5230\u3002\u201d\u7a81\u542c\u71d5\u4e03\u5728\u5f8c\u9762 \u9662\u5b50\u91cc\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u6765\u770b\u770b\uff0c\u8fd9\u65f6\u6709\u4e2a\u2026\u2026\u6709\u4e2a\u2026\u2026\u201d\u6709\u4e2a\u4ec0\u9ebd\u4e1c\u897f\u4ed6\u7adf\u597d\u50cf\u8bf4\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b2c\u4e2a\u51b2\u51fa\u4f17\u6797\u592a\u5e73\u4e5f\u5728\u5f8c\u9762\u8ddf\u7740\u3002 \u3000\u3000\u9662\u5b50\u91cc\u5df2\u6253\u626b\u5f97\u5f88\u4e7e\u51c0\xb7\u5c45\u7136\u8fd8\u4ece\u54ea\u91cc\u79fb\u6765\u300f\u7aff\u4fee\u7af9\u4e1b\u83ca\u4f26\u71d5\u4e03\u5c0f\u7ad9\u5728\u83ca\u82b1\u4e1b\u4e2d\u770b\u7740\u6837 \u4e1c\u897f\u53d1\u5446\u3002 \u3000\u3000\u4ed6\u770b\u7740\u7684\u8d6b\u7136\u662f\u53e3\u68fa\u6750\u3002 \u3000\u3000\u5d2d\u65b0\u7684\u68fa\u6750\u3002 \u3000\u3000\u68fa\u5934\u4e0a\u4eff\u4f5b\u523b\u7740\u884c\u5b57\u4ed4\u7ec6\u770b\u4e0a\u9762\u523b\u7684\u8d6b\u7136\u7adf\u662f\u201c\u5357\u5bcc\u300f\u4e8c\u62a0\u201d\u3002 \u3000\u3000\u6797\u592a\u5e73\u7a81\u7136\u7535\u8eab\u51b0\u51b7\u8fde\u5676\u5507\u4e0a\u7684\u8840\u8272\u90fd\u892a\u5f97\u5e72\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u4e5f\u6709\u70b9\u53d1\u6bdb\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u5728\u4ec0\u9ebd\u5730\u65b9\u6740\u4ed6\u7684\uff1f\u201d\uff01\u6797\u592a\u5e73\u9053\u201c\u5c31\u2026\u2026\u5c31 \u5728\u5916\u9762\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ec0\u9ebd\u5730\u65b9\u5916\u9762\u3002\u201d\u6797\u592a\u5e73\u9053\uff1a\u201c\u4ed6\u4f4d\u7684\u5c4b\u6237\u5916\u9762\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a \u201c\u4f60\u6740\u4e86\u4ed6\u5f8c\u6709\u6ca1\u6709\u628a\u4ed6\u7684\u4f53\u57cb\u8d77\u6765\uff1f\u201d\u6797\u592a\u4e4e\u54ac\u7740\u5634\u5507\u6447\u6447\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u4f60\u5012\u5177\u662f\u7ba1\u6740\u4e0d\u7ba1\u57cb\u3002\u201d\u6797\u592a\u5e73\u7684\u6837\u5b50\u5c31\u597d\u50cf\u4e49\u8981\u54ed\u51fa\u6765\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u7b2c\u4e00\u6b21\u6740\u4eba\u7684\u65f6\u5019\u90fd\u96be\u514d\u5fc3\u6382\u610f\u4e71\u6740\u4eba\u4e4b\u5f8c\u6015\u8fde\u770b\u90fd\u4e0d\u6562\u518d\u770b\u4e00\u773c\u54ea \u91cc\u987e\u5f97\u4e86\u522b\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8fd9\u5012\u597d\u50cf\u662f\u7ecf\u9a8c\u4e4b\u8c08\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u83ab\u5fd8\u4e86\uff0c\u6211\u867d\u7136\u6ca1 \u6709\u6740\u8fc7\u4eba\xb7\u81f3\u5c11\u88ab\u4eba\u6740\u8fc7\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u6740\u4ed6\u7684\u65f6\u5019\uff0c\u65c1\u8fb9\u8fd8\u6ca1\u6709\u522b\u7684 \u4eba\uff1f\u201d\u6797\u592a\u5e73\u53c8\u6447\u6447\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u82e5\u6ca1\u6709\u522b\u4eba\uff0c\u662f\u8c01\u628a\u4ed6\u5c38\u8eab\u88c5\u8fdb\u68fa\u6750\u91cc\uff1f\u8fd9\u68fa\u6750\u53c8\u662f\u8c01\u9001\u6765\u7684\uff1f\u201d\u4ed6\u5ffd\u7136 \u7b11\u4e86\u7b11\u53c8\u9053\uff1a\u201c\u603b\u4e0d\u4f1a\u662f\u4ed6\u81ea\u5df1\u808b\u8fdb\u68fa\u6750\u518d\u5c06\u68fa\u6750\u9001\u6765\u7684\u5427\u3002\u201d\u90ed\u5927\u8def\u6709\u4e2a\u6bdb\u75c5\u65e0\u8bba\u4ec0\u9ebd\u65f6 \u5019\u90fd\u5fcd\u4e0d\u4f4f\u8981\u5f00\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u8fd9\u73a9\u7b11\u5f00\u5f97\u5e76\u4e0d\u5999\u3002 \u3000\u3000\u6797\u592a\u5e73\u7684\u8138\u8272\u53d8\u5f97\u66f4\u60e8\xb7\u54ac\u7740\u5634\u5507\uff0c\u5450\u5450\u9053\uff1a\u201c\u6211\uff0e\u2026\u6211\u672c\u4e0d\u662f\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u68fa\u6750 \u91cc\u5ffd\u7136\u201c\u7565\u201d\u7684\u54cd\u3002 \u3000\u3000\u63a5\u7740\u53c8\u662f\u201c\u7565\u201d\u7684\u54cd\u3002 \u3000\u3000\u71d5\u4e03\u548c\u90ed\u5927\u8def\u7684\u8138\u8272\u4e5f\u4e0d\u7981\u53d8\u4e86\u3002\uff1a\u201c\u83ab\u975e\u68fa\u6750\u91cc\u7684\u6b7b\u4eba\u5df2\u8fd8\u9b42\u201d\u90ed\u5927\u8def\u62cd\u4e86\u62cd\u6797\u592a\u5e73 \u7684\u80a9\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5bb3\u6015\u4ed6\u6d3b\u7740\u65f6\u6211\u4eec\u90fd\u4e0d\u6015\u6b7b\u4e86\u6015\u4ec0\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u65e2\u7136\u4e0d \u6015\xb7\u5c31\u7d22\u6027\u6253\u5f00\u68fa\u6750\xb7\u8ba9\u4ed6\u51fa\u6765\u5427\u3002\u201d\u4ed6\u597d\u50cf\u771f\u7684\u8981\u53bb\u5c06\u68fa\u6750\u6253\u5f00\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u7b49\u7b49\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e0d\u6015\u7684\u5417\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u6015\uff0c \u53ea\u4e0d\u8fc7\u2026\u2026\u53ea\u4e0d\u8fc7\u2026\u2500\u2606\uff1a\u201c\u964d\xb7\u964d\u7565\u201d\u8fd9\u6b21\u68fa\u6750\u91cc\u7adf\u4e00\u8fde\u4e32\u7684\u54cd\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u58f0\u97f3\u6bd4\u521a\u624d \u66f4\u5927\u771f\u7684\u597d\u50cf\u6b7b\u4eba\u6025\u7740\u8981\u51fa\u6765\u3002 \u3000\u3000\u80c6\u5b50\u5c0f\u7684\u4eba\uff0c\u6b64\u523b\u6015\u65e9\u5df2\u88ab\u5413\u5f97\u843d\u8352\u800c\u9003\u4e86\u3002 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u9053\uff1a\u201c\u8ba9\u6211\u6765\u5f00\u8fd9\u53e3\u68fa\u6750\u4ed6\u53cd\u6b63\u662f\u6765\u627e\u6211\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u53bb\uff0c\u8fd8 \u662f\u8ba9\u6211\u6765\u3002\u201d\u4ed6\u53e3\u91cc\u8bf4\u7740\u8bdd\xb7\u4eba\u5df2\u8df3\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5fc3\u91cc\u4e5f\u5f88\u6015\xb7\u4e5f\u8bb8\u6bd4\u522b\u4eba\u8fd8\u4f2f\u5f97\u5389\u5bb3\uff0c\u8fd9\u82e5\u662f\u4ed6\u81ea\u5df1\u7684\u4e8b\xb7\u8bf4\u4e0d\u5b9a\u5df2\u6e9c\u4e4b\u5927\u5409\u3002 \u3000\u3000\u4f46\u6797\u592a\u5e73\u662f\u4ed6\u7684\u670b\u53cb\xb7\u53ea\u8981\u662f\u670b\u53cb\u7684\u4e8b\uff0c\u4ed6\u5c31\u7b97\u4f2f\u5f97\u8981\u547d\u4e5f\u4f1a\u786c\u7740\u5934\u76ae\u633a\u4e0a\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u77a7\u7740\u4ed6\u76ee\u5149\u53c8\u53d8\u5f97\u72e0\u6e29\u67d4\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u88ab\u9b3c\u6293\u53bb\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4e0d\u6015 \u7684\uff1f\u201d\u4ed6\u7530\u91cc\u5728\u8bf4\u201c\u6015\u201d\uff0c\u624b\u5df2\u5c06\u68fa\u6750\u76d6\u8d77\u3002\uff1a\u201c\u7530\u201d\u7684\u4e00\u6837\u6d3b\u751f\u751f\u7684\u4e1c\u897f\u4ece\u68fa\u6750\u91cc\u7a9c\u4e86\u51fa \u6765\u3002 \u3000\u3000\u4ece\u68fa\u6750\u91cc\u8df3\u51fa\u6765\u7684\u8fd9\u6837\u4e1c\u897f\u4e5f\u5728\u53eb\xb7\u201c\u6c6a\u6c6a\u6c6a\u201d\u7684\u53eb\u3002 \u3000\u3000\u662f\u6761\u72d7\u9ed1\u72d7\uff0c\u6d3b\u751f\u751f\u7684\u9ed1\u72d7\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u5728\u90a3\u91cc\uff0c\u63a5\u7740\u6c57\xb7\u60f3\u7b11\u5374\u7b11\u4e0d\u51fa\u53e3\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\u82e6\u7b11\u7740\u9053\uff1a\u201c\u8fd9 \u73a9\u7b11\u5b9e\u5728\u5f00\u5f97\u4e0d\u9ad8\u660e\xb7\u53ea\u6709\u767d\u75f4\u624d\u4f1a\u5f00\u8fd9\u79cd\u73a9\u7b11\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4ed6\u7edd\u4e0d\u662f\u767d\u75f4\xb7\u4e5f\u7edd\u4e0d\u662f\u5728 \u5f00\u73a9\u7b11\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e0d\u662f\u5f00\u73a9\u7b11\u662f\u4ec0\u9ebd\uff1f\u201d\u85cf\u4e03\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u4f46\u77e5\u9053\u6797\u592a\u5e73\u6740\u4e86\u5357\u5bab \u4e11\xb7\u800c\u4e14\u8fd8\u77e5\u9053\u6797\u592a\u5e73\u4f4f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u201d\u90ed\u5927\u8def\u53f9\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u7684\u4e8b\u786e\u5b9e\u4e0d\u5c11\u53ef\u662f\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\u7684\uff1f\u201d\u71d5\u4e03\u4e5f\u53f9\u4e86\u53e3\u6c14 \u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u53e6\u6709\u7528\u610f\u4e5f\u8bb8\u4ed6\u53ea\u4e0d\u8fc7\u5403\u9971\u4e86\u996d\u6ca1\u4e8b\u505a\u4e3d\u5df1\u4e0d\u7ba1\u662f\u4e3a\u4e86\u4ec0\u9ebd\xb7\u4ed6\u65e2\u7136\u5df2\u505a\u4e86\u5c31 \u7edd\u4e0d\u4f1a\u505c\u6b62\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u5b9a\u8fd8\u8981\u505a\u4e9b\u522b\u7684\u4e8b\uff1f\u201d\u71d5\u4e03\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u53ea \u8981\u80fd\u6c89\u4efb\u6c14\uff0c\u5c31\u4e00\u5b9a\u4f1a\u7b49\u5f97\u5230\u4ed6\u7684\u3002\u201d\u4ed6\u4e5f\u62cd\u4e86\u62cd\u6797\u592a\u5e73\u7684\u80a9\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u73b0\u5728\u8fd8\u662f\u53bb \u7761\u5427\u653e\u7740\u90a3\u9ebd\u597d\u670d\u7684\u5e8a\u4e0d\u7761\u624d\u771f\u7684\u662f\u767d\u75f4\u3002\u201d\u53ea\u542c\u738b\u52a8\u7684\u58f0\u97f3\u8fdc\u8fdc\u4ece\u5c4b\u5b50\u91cc\u4f20\u51fa\u6765\u9053\uff1a\u201c\u7b54 \u5bf9\u4e86\u3002\u201d\u7b2c\u4e8c\u5929\u65e9\u4e0a\u90ed\u5927\u8def\u662f\u88ab\u4e00\u4e32\u94c3\u58f0\u5435\u9192\u7684\u3002 \u3000\u3000\u4ed6\u9192\u7684\u65f6\u5019\u94c3\u58f0\u8fd8\u5728\u201c\u53ee\u53ee\u5f53\u5f53\u201d\u7684\u54cd\u597d\u50cf\u662f\u4ece\u82b1\u5385\u90a3\u8fb9\u4f20\u8fc7\u6765\u7684\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u8d77\u5e8a\u65f6\u706b\u6c14\u603b\u6bd4\u5e73\u65f6\u5927\u4e9b\u5c24\u5176\u662f\u88ab\u4eba\u5435\u9192\u7684\u65f6\u5019\u3002 \u3000\u3000\u8fd9\u5c31\u53eb\u505a\u201c\u4e0b\u5e8a\u6c14\u201d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u543c\u4e86\u8d77\u6765\u9053\uff1a\u201c\u662f\u8c01\u5728\u7a77\u6447\u90a3\u9b3c\u94c3\u9500\uff1f\u624b\u75d2\u9ebd\u201d\u4ed6\u53eb\u7684\u65f6\u5019\u597d\u50cf\u542c\u5230\u738b\u52a8 \u4e5f\u5728\u53eb\u3002 \u3000\u3000\u94c3\u58f0\u5374\u8fd8\u662f\u4e0d\u505c\u3002 \u3000\u3000\u90ed\u5927\u8def\u8df3\u8d77\u6765\u8d64\u7740\u811a\u51b2\u51fa\u53bb\u54fa\u9676\u5730\u9053\uff1a\u201c\u5b9a\u662f\u71d5\u4e03\u90a3\u5c0f\u4e88\u4ed6\u7684\u624b\u597d\u50cf\u968f\u65f6\u968f\u5730\u90fd\u4f1a \u75d2\u3002\u201d\u53ea\u542c\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u6211\u7684\u624b\u75d2\u65f6\u60f3\u6253\u4eba\u5374\u7edd\u4e0d\u6447\u94c3\u3002\u201d\u71d5\u4e03\u4e5f\u51fa\u6765\u4e86\u8eab\u4e0a\u7684\u8863\u670d\u5c45\u7136\u5df2 \u7a7f\u5f97\u6574\u6574\u9f50\u9f50\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u597d\u50cf\u6bcf\u5929\u90fd\u662f\u7a7f\u7740\u8863\u670d\u7761\u89c9\u7684\u3002 \u3000\u3000\u7ff1\u5927\u8def\u63c9\u4e86\u63c9\u773c\u775b\u4f5c\u4e86\u4e2a\u82e6\u7b11\u53c8\u76b1\u7740\u7709\u8bf4\u9053\uff1a\u201c\u603b\u4e0d\u4f1a\u662f\u6797\u592a\u5e73\u5427\u9664\u975e\u4ed6\u771f\u7684\u662f\u88ab\u9b3c\u8ff7 \u4f4f\u4e86\u3002\u201d\u94c3\u58f0\u8fd8\u5728\u54cd\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u542c\u5f97\u5f88\u6e05\u695a\u7684\u786e\u662f\u4ece\u82b1\u5385\u91cc\u4f20\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\u540c\u65f6\u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6797\u592a\u5e73\u7684\u786e\u5728\u82b1\u5385\u91cc\u4f46\u6447\u94c3\u7684\u5374\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u7ad9\u5728\u90a3\u91cc\u53d1\u6014\u6447\u94c3\u7684\u662f\u6761\u732b\u3002 \u3000\u3000\u9ed1\u732b\u3002 \u3000\u3000\u4e2a\u94c3\u6321\u7528\u7ef3\u5b50\u540a\u5728\u82b1\u67b6\u535c\u7ef3\u4e88\u7684\u53e6\u5934\u5c31\u7ed1\u5728\u8fd9\u9ed1\u7784\u7684\u811a\u9ed1\u732b\u4e0d\u505c\u7684\u8df3\u94c3\u9500\u4e0d\u505c\u7684\u54cd\u3002 \u3000\u3000\u82b1\u300f\u2019\u4e2d\u7684\u684c\u5b50\u535c\u6446\u7740\u5927\u684c\u7684\u4e1c\u897f\u90fd\u662f\u5403\u7684\u4e1c\u897f\u6709\u9e21\u3001\u6709\u9e2d\u3001\u6709\u5305\u5b50\u3001\u6709\u9992\u5934\u3001\u8fd8\u6709\u5927 \u9152\u3002 \u3000\u3000\u9ed1\u732b\u64ad\u94c3\xb7\u539f\u6765\u662f\u53eb\u4ed6\u4eec\u6765\u5403\u65e9\u996d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u53c8\u63c9\u63c9\u773c\u775b\u9053\uff1a\u201c\u6211\u7684\u773c\u775b\u6709\u6bdb\u75c5\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u7684\u773c\u775b\u53ea\u6709\u5728\u770b\u5230 \u5973\u4eba\u65f6\u624d\u4f1a\u6709\u6bdb\u75c5\u3002\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u4e5f\u8bb8\u8fd9\u662f\u6761\u4ea5\u9ed1\u732b\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u662f\u516c\u7684\u3002\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\uff1f\u71d5\u4e03\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u770b\u6765\u5e76\u4e0d\u559c\u6b22\u4f60\u3002\u201d\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6bcd \u7684\u4e5f\u4e0d\u4f1a\u559c\u6b22\u6211\u559c\u6b22\u7684\u5b9a\u662f\u738b\u8001\u5927\u3002\u201d\u8fd9\u6b21\u8f6e\u5230\u71d5\u4e03\u4e0d\u61c2\u4e86\xb7\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d\u90ed\u5927\u8def \u9053\uff1a\u201c\u6bcd\u732b\u90fd\u559c\u6b22\u61d2\u732b\u3002\u201d\u7a81\u542c\u738b\u52a8\u7684\u58f0\u97f3\u5728\u5f8c\u9762\u9053\uff1a\u201c\u6211\u770b\u8fd9\u6761\u730e\u5b9a\u662f\u5973\u7684\u3002\u201d\u8fd9\u6b21\u90ed\u5927 \u8def\u548c\u71d5\u4e03\u90fd\u4e0d\u906d\u4e86\u51e0\u4e4e\u540c\u65f6\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u56e0\u4e3a\u5b83\u4f1a\u505a\u996d\u3002\u201d\u732b\u5f53\u7136\u4e0d\u4f1a\u505a \u996d\u3002 \u3000\u3000\u90ed\u5927\u8def\u6495\u4e0b\u6761\u9e21\u56e0\u585e\u8fdb\u5634\u91cc\u53c8\u62ff\u51fa\u6765\xb7\u9053\uff1a\u201c\u9e21\u8fd8\u662f\u70ed\u7684\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u5305\u5b50\u4e5f\u662f\u70ed \u7684\u3002\u201d\u90ed\u5927\u8def\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e9b\u4e1c\u897f\u9001\u6765\u8fd8\u4e0d\u4e45\u3002\u201d\u71d5\u4e03\u9053\uff1a\u201c\u7b54\u5bf9\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u662f\u8c01\u9001 \u6765\u7684\u5462\uff1f\u96be\u9053\u4e5f\u662f\u90a3\u4e2a\u5728\u594e\u5143\u9986\u66ff\u6211\u4eec\u4ed8\u94b1\u7684\u4eba\u201d\u71d5\u4e03\u9053\uff1a\u201c\u53c8\u7b54\u5bf9\u4e86\u3002\u201d\u90ed\u5927\u8def\u8c0a\uff1a\u201c\u4ed6 \u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u62cd\u6211\u4eec\u7684\u9a6c\u5c41\uff0c\u96be\u9053\u771f\u662f\u6211\u4e7e\u513f\u9893\u4e03\u9053\uff1a\u201c\u772f\u9699\u201d\u2026\u6627\u772f\u2026\u2026\u201d\u7ff1\u5927\u8def\u9053\uff1a \u201c\u4f60\u51e0\u4e4e\u53d8\u6210\u4e00\u6761\u732b\u4e86\uff0c\u6211\u53ef\u542c\u4e0d\u61c2\u6eaf\u8bf4\u7684\u8bdd\u3002\u201d\u9893\u4e03\u201c\u7abf\u9645\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u5728\u8ddf\u4f60\u7684 \u4e8e\u513f\u5b50\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u5c06\u6bcf\u6837\u4e1c\u897f\u90fd\u6495\u4e86\u70b9\u653e\u5728\u76d8\u5b50\u4e0a\u90a3\u9ed1\u732b\u5df2\u8df3\u4e86\u8fc7\u6765\u71d5\u4e03\u8f7b\u8f7b\u629a\u7740\u5b83\u8116\u5b50\u4e0a\u7684\u6bdb\u9053\uff1a \u201c\u8fd9\u4e9b\u4e1c\u897f\u90fd\u662f\u4f60\u9001\u6765\u7684\xb7\u4f60\u81ea\u5df1\u5148\u5c1d\u70b9\u5427\u3002\u201d\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u5b5d\u987a\uff0c\u770b\u6765\u5012\u597d \u50cf\u662f\u8fd9\u6761\u732b\u7684\u4e7e\u513f\u5176\u5b9e\u4ed6\u5f53\u7136\u4e5f\u77e5\u9053\u75c8\u4e03\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u8981\u8bd5\u8bd5\u8fd9\u4e9b\u4e1c\u897f\u91cc\u6709\u6ca1\u6709\u56a3\u3002 \u3000\u3000\u71d5\u4e03\u505a\u4e8b\u597d\u50cf\u603b\u662f\u7279\u522b\u7ec6\u5fc3\u770b\u6765\u5374\u504f\u5014\u53c8\u4e0d\u8c61\u662f\u4e2a\u7ec6\u5fc3\u7684\u7ec6\u5fc3\u7684\u4eba\u6ca1\u6709\u90a3\u9ebd\u810f\u7684\u4ed6\u7b80\u76f4 \u5c31\u4ece\u6765\u4e0d\u6e89\u6fa1\u3002 \u3000\u3000\u98df\u7269\u4e2d\u6ca1\u6709\u6bd2\u90ed\u5927\u8def\u7684\u9e21\u817f\u5df2\u4e0b\u4e86\u809a\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u770b\u6765\u8fd9\u4eba\u5bf9\u6211\u4eec\u5012\u6ca1\u6709\u4ec0\u9ebd\u6076\u610f\u53ea\u4e0d\u8fc7\u6709\u70b9\u6bdb\u75c5\u4e3d\u7ff1\u5927\u8def\u9053\u201c\u4e0d\u4f46\u6709\u70b9\u6bdb\u75c5\uff0c \u662f\u6709\u5f88\u591a\u6bdb\u75c5\xb7\u6bdb\u75c5\u4e0d\u5927\u7684\u4eba\u600e\u9ebd\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d\u4ed6\u541e\u4e0b\u4e2a\u5305\u5b50\uff0c\u5ffd\u53c8\u9053\u201c\u8fd9\u4eba\u4e00\u5b9a\u662f\u4e2a\u4ea5 \u7684\u3002\u201d\u82d1\u4e03\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d\u7530\u5927\u8def\u7406\u201c\u53ea\u6709\u5973\u4eba\u624d\u820d\u505a\u8fd9\u75af\u75af\u6444\u7530\u7684\u4e8b\u3002\u201d\u82d1\u4e03\u54ac\u7740\u7530 \u66f0\u5c45\u7136\u4e5f\u70b9\u4e86\u70b9\u5934\uff0c\u624d\u8bf4\u9053\u201c\u5979\u8fd9\u9ebd\u6837\u505a\u8bf4\u4e0d\u8d70\u662f\u56e0\u4e3a\u770b\u4e0a\u4e86\u4f60\uff0c\u8981\u8ba8\u597d\u4f60\xb7\u56e0\u4e3a\u201d\u4e00\u3002\u201d \u7ff1\u5927\u8def\u7b11\u4e86\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u56e0\u4e3a\u4ec0\u9ebd\uff1f\u56e0\u4e3a\u6211\u5f88\u6709\u7537\u5b50\u6c14\uff1f\u8fd8\u662f\u56e0\u4e3a\u6211\u957f\u5f97\u4fca\uff1f\u201d\u71d5\u4e03\u9053\u201c\u90fd \u4e0d\u662f\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u662f\u56e0\u4e3a\u4ec0\u9ebd\u5462\uff1f\u201d\u71d5\u4e03\u8c08\u8c08\u9053\u201c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5979\u662f\u4e2a\u75c5\u75af\u7530\u7530\u7684\u5973\u4eba\uff0c\u4e5f \u53ea\u6709\u75af\u75af\u7530\u56ed\u7684\u5973\u4eba\u624d\u4f1a\u7231\u4e0a\u4f60\u3002\u201d\u90ed\u5927\u8def\u60f3\u677f\u8d77\u8138\xb7\u5374\u53c8\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u9053\u201c\u75af\u5973\u4eba\u81f3\u5c11\u603b \u6bd4\u6ca1\u6709\u5973\u4eba\u597d\u3002\u201d\u7a97\u5916\u9633\u5149\u666e\u7167\u5927\u5730\uff0c\u5728\u8fd9\u79cd\u5929\u6c14\u91cc\u522b\u4eba\u65e0\u8bba\u8bf4\u4ec0\u9ebd\u4ed6\u90fd\u4e0d\u4f1a\u4e2d\u6c14\u5c24\u5176\u4e0d\u4f1a \u5bf9\u71d5\u4e03\u751f\u6c14\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u71d5\u4e03\u3002 \u3000\u3000\u4ed6\u6e10\u6e10\u89c9\u5f97\u81ea\u5df1\u5728\u8fd9\u5806\u670b\u53cb\u4e2d\u6700\u559c\u6b22\u7684\u5c31\u662f\u71d5\u4e03\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u85cf\u4e03\u5374\u4f97\u504f\u597d\u50cf\u5904\u5904\u90fd\u8981\u613f\u4ed6\u4f5c\u5bf9\uff0c\u968f\u65f6\u968f\u5730\u90fd\u8981\u673a\u4f1a\u81ed\u81ed\u4ed6\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\xb7\u71d5\u4e03\u8d8a\u81ed\u4ed6\u4ed6\u8d8a\u559c\u6b22\u71d5\u4e03\u3002 \u3000\u3000\u738b\u52a8\u603b\u662f\u5728\u65c1\u8fb9\u770b\u7740\u4ed6\u4eec\u81ed\u6765\u81ed\u53bb\u4ed6\u770b\u7740\u4ed6\u4eec\u7684\u65f6\u5019\uff0c\u773c\u77aa\u91cc\u603b\u662f\u6709\u79cd\u5f88\u7279\u522b\u7684\u7b11\u610f\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u624b\u521a\u5c06\u5305\u5b50\u9001\u5230\u5207\u91cc\u53bb\u5c31\u53bb\u62ff\u9152\u676f\u3002 \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u4e0b\u773c\u9053\u201c\u9152\u9b3c\u4f60\u96be\u9053\u5c31\u4e0d\u80fd\u7b49\u5230\u5929\u9ed1\u518d\u559d\u9152\u5417\u201d\u90ed\u5927\u8def\u7b11\u4e86\u7b11\u5c45\u7136\u5c06\u9152\u676f\u653e \u4e0b\u6765\u54fa\u54fa\u5730\u9053\u201c\u8c01\u8bf4\u6211\u8981\u559d\u9152\xb7\u6211\u53ea\u4e0d\u8fc7\u662f\u60f3\u7528\u9152\u6765\u6e90\u6f31\u53e3\u800c\u5df2\u3002\u201d\u5c31\u5728\u8fd9\u65f6\u4ed6\u4eec\u5ffd\u7136\u542c\u5230 \u5916\u9762\u6709\u4eba\u5728\u9992\u58f0\u957f\u541f\u201c\u8fdc\u4e0a\u5bd2\u5c71\u77f3\u5f84\u659c\xb7\u767d\u4e91\u6df1\u5904\u6709\u5bb6\u505c\u8f66\u5750\u770b\u67ab\u6797\u665a\u971c\u53f6\u7ea2\u4e8e\u4e8c\u6708 \u82b1\u2026\xb7\u201d\u597d\u7247\u98ce\u5149\u5440\xb7\u597d\u4e00\u5904\u6240\u5728\u3002\u201d\u90ed\u5927\u8def\u53c8\u7b11\u7b11\uff0c\u9053\u201c\u6765\u4e86\u4e2a\u9178\u4e01\u3002\u201d\u738b\u52a8\u9053\u201c\u4e0d\u662f\u4e00 \u4e2a\uff0c\u662f\u53c1\u4e2a\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u7ec6\u9053\uff1f\u201d\u738b\u52a8\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5916\u753b\u679c\u7136\u6709\u53e6\u4e00\u4eba\u7684\u58f0\u97f3\u9053 \u201c\u516c\u4e8e\u65e2\u7136\u559c\u6b22\u8fd9\u91cc\xb7\u54b1\u4eec\u4e0d\u5982\u5c31\u5728\u8fd9\u91cc\u6b47\u4e0b\u5427\u6211\u8d70\u5f97\u817f\u90fd\u9178\u4e86\u3002\u201d\u53c8\u6709\u4eba\u9053\u201c\u4e0d\u77e5\u9053\u8fd9\u5bb6 \u7684\u4e3b\u4eba\u662f\u8c01\uff1f\u80af\u4e0d\u80af\u8ba9\u6211\u4eec\u8fdb\u53bb\u5750\u8fd9\u4e24\u4eba\u7684\u58f0\u97f3\u542c\u6765\u8fd8\u662f\u5b69\u5b50\uff0c\uff0c\u4f46\u5b69\u5b50\u4e5f\u662f\u4eba\uff0c\u6765\u7684\u679c\u7136 \u662f\u53c1\u4e2a\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u597d\u7075\u7684\u8033\u6735\xb7\u867d\u7136\u53ea\u4e0d\u8fc7\u662f\u6761\u6a84\u730e\xb7\u8033\u6735\u8fd8\u662f\u6bd4\u4eba\u7075\u3002\u201d \u201c\u9699\u201d\u7684\u4e00\u58f0\u90a3\u9ed1\u732b\u5df2\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u732b\u7684\u8033\u6735\u679c\u7136\u7279\u522b\u7075\xb7\u8fde\u738b\u52a8\u81ea\u5df1\u90fd\u4e0d\u7981\u7b11\u4e86\u3002 \u3000\u3000\u53ea\u542c\u90a3\u4f4d\u516c\u4e8e\u9053\u201c\u9ad8\u95e8\u63a9\u800c\u4e0d\u95ed\u7075\u5974\u5df2\u6765\u8fce\u5ba2\u770b\u6765\u8fd9\u5bb6\u4e3b\u4eba\u4e0d\u4f46\u597d\u5ba2\u800c\u4e14\uff0c\u8fd8\u5fc5\u5b9a\u98ce\u96c5 \u5f97\u5f88\u201d\u4e00\u3002\u98ce\u96c5\u5f97\u72e0\u3002\u201d\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u9053\u201c\u98ce\u96c5\u867d\u672a\u5fc5\u597d\u5ba2\u5374\u5012\u662f\u771f\u7684\u3002\u201d\u4ed6\u7b2c\u4e2a\u8fce\u4e86\u51fa \u53bb\u3002\u65ed\u65e5\u65b0\u9c9c\u5f97\u8c61\u521a\u51fa\u62a4\u7684\u9992\u5934\uff0c\u4ee4\u4eba\u770b\u7740\u4e0d\u7531\u81ea\u4e3b\u4ece\u5fc3\u5e95\u5347\u51fa\u4e00\u79cd\u6e29\u6687\u4e4b\u610f\u3002 \u3000\u3000\u5728\u8fd9\u9ebd\u597d\u7684\u5929\u6c14\u91cc\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u53d8\u5f97\u4efd\u5916\u53cb\u5584\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u8138\u4e0a\u5e26\u7740\u53cb\u5584\u7684\u5fae\u7b11\u671b\u7740\u95e8\u5916\u7684\u53c1\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u5782\u56fe\u7ae5\u4e88\u4e00\u4e2a\u80cc\u7740\u4e2a\u4e66\u7bb1\u4e2a\u94e3\u7740\u62c5\u5b50\u7ad9\u5728\u4ed6\u4eec\u4e3b\u4eba\u8eab\u5f8c\u4e24\u5f20\u5c0f\u8138\u88ab\u6652\u5f97\u597d\u50cf\u662f\u4e2a\u719f \u900f\u4e86\u7684\u82f9\u679c\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u4e3b\u4eba\u662f\u4e2a\u6587\u8d28\u5f6c\u5f6c\u7684\u4e66\u751f\xb7\u5e74\u7eaa\u5e76\u4e0d\u592a\u5927\uff0c\u957f\u5f97\u975e\u5e38\u82f1\u4fca\xb7\u800c\u4e14\u98ce\u5ea6\u7530\u7530\xb7\u6e29\u6587 \u6709\u672d\u3002 \u3000\u3000\u8fd9\u9ebd\u6837\u53c1\u4e2a\u4eba\xb7\u65e0\u8bba\u8c01\u770b\u5230\u90fd\u4e0d\u4f1a\u8ba8\u538c\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u4eec\u662f\u6e38\u5c71\u6765\u7684\uff1f\u5012\u771f\u662f\u9009\u5bf9\u4e86\u5929\u6c14\u3002\u201d\u4e66\u751f\u957f\u63d6\xb7\u9053\u201c\u5c0f\u53ef\u65e0\u7aef\u5192\u6627\uff0c \u6253\u6270\u4e86\u4e3b\u4eba\u60c5\u8da3\xb7\u6028\u7f6a\u6028\u7f6a\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e5f\u4e0d\u662f\u4e3b\u4eba\xb7\u662f\u5ba2\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u77e5\u9053\u8fd9\u91cc\u7684\u4e3b\u4eba \u597d\u5ba2\u3002\u201d\u4e66\u751f\u7b11\u9053\u201c\u5374\u4e0d\u77e5\u4e3b\u4eba\u5728\u4f55\u5904\uff1f\u662f\u5426\u80fd\u5bb9\u5c0f\u53ef\u4e00\u89c1\u201d\u90ed\u5927\u8def\u9053\u201c\u8fd9\u91cc\u7684\u4e3b\u4eba\u867d\u597d\u5ba2 \u5374\u6709\u70b9\u75c5\u3002\u201d\u4e66\u751f\u7406\u201c\u4e0d\u77e5\u4e3b\u4eba\u6709\u4f55\u75c5\u5c0f\u53ef\u5bf9\u6b67\u9ec4\u4e4b\u9053\u5012\u7565\u77e5\u4e8c\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u4ed6\u7684\u75c5\u4f2f \u662f\u6cbb\u4e0d\u597d\u7684\u4ed6\u5f97\u7684\u662f\u61d2\u75c5\u3002\u4f60\u82e5\u60f3\u89c1\u4ed6\uff0c\u53ea\u597d\u81ea\u5df1\u8fdb\u53bb\u3002\u201d\u4e66\u751f\u5fae\u7b11\u9053\u201c\u65e2\u7136\u5982\u6b64\u5c31\u606d\u656c\u4e0d \u5982\u4ece\u547d\u4e86\u3002\u201d\u4ed6\u8d70\u8def\u4e5f\u5f88\u65af\u6587\u7b80\u76f4\u6709\u70b9\u5f31\u4e0d\u7981\u98ce\u7684\u6837\u5b50\xb7\u4f46\u90a3\u4e24\u4e2a\u5782\u7537\u7ae5\u4e88\u8eab\u4e0a\u80cc\u7684\u4e66\u7bb1\u548c \u62c5\u5b50\u5374\u597d\u50cf\u4e0d\u592a\u8f7b\u3002 \u3000\u3000\u8f7b\u8f7b\u64cd\u7fc5\u5b50\u7684\u4e00\u4e2a\u8d70\u5728\u6700\u5f8c\u9762\u4e00\u8def\u8d70\xb7\u62c5\u5b50\u91cc\u4e00\u8def\u53ee\u53ee\u7684\u54cd\u3002 \u3000\u3000\u7ff1\u5927\u8def\u6478\u4e86\u6478\u4ed6\u7684\u5934\uff0c\u9053\u201c\u4f60\u8fd9\u62c5\u5b50\u91cc\u88c5\u7684\u662f\u4ec0\u9ebd\u5440\uff1f\u91cd\u4e0d\u91cd\uff1f\u201d\u8fd9\u5b69\u5b50\u5927\u9650\u775b\u7728 \u7728\xb7\u9053\u201c\u4e0d\u592a\u91cd\u53ea\u4e0d\u8fc7\u662f\u4e9b\u6db5\u74f6\u5b50\xb7\u8305\u53f0\u6d12\u90fd\u662f\u7528\u74f6\u5b50\u88c5\u7684\uff1a\u6211\u4eec\u516c\u4e8e\u6700\u7231\u559d\u9152\uff0c\u8fd8\u559c\u6b22\u4f5c \u8bd7\u6211\u4e0d\u4f1a\u4f5c\u8bd7\u6211\u53ea\u4f1a\u559d\u9152\u3002\u201d\u90ed\u5927\u8def\u7b11\u4e86\xb7\u95ee\u9053\u201c\u4f60\u4e5f\u4f1a\u559d\u9152\uff1f\u4f60\u591a\u5927\u4e2d\u7eaa\u4e86\u5440\uff1f\u201d\u8fd9\u5b69\u5b50 \u9053\u201c\u5341\u56db\u4e86\u660e\u5929\u5c31\u5341\u4e94\u3002\u6211\u53eb\u9493\u8bd7\uff0c\u4ed6\u53eb\u626b\u4fd7\u6211\u4eec\u5bb6\u516c\u5b50\u59d3\u4f55\u4eba\u53ef\u4f55\u6211\u4eec\u662f\u4ece\u5927\u540d\u5e9c\u6765\u7684\u3002 \u56e0\u4e3a\u6211\u4eec\u7684\u4e3b\u4eba\u559c\u6b22\u6e38\u5c71\u73a9\u6c34\uff0c\u6240\u4ee5\u6211\u4eec\u6210\u5e74\u96be\u5f97\u5728\u5bb6\u91cc\u3002\u201d\u90ed\u706b\u8def\u6bcf\u95ee\u4e00\u53e5\u8bdd\xb7\u8fd9\u5b69\u5b50\u81f3 \u5c11\u8981\u56de\u7b54\u4e03\u516b\u53e5\u3002 \u3000\u3000\u90ed\u5927\u8def\u8d8a\u770b\u8d8a\u89c9\u5f97\u8fd9\u5b69\u5b50\u6709\u8da3\xb7\u6545\u610f\u9017\u7740\u4ed6\u53c8\u95ee\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u53eb\u9493\u8bd7\uff1f\u8bd7\u53c8\u4e0d\u662f\u9c7c\uff0c\u600e \u9ebd\u80fd\u9493\u5f97\u8d77\u6765\u3002\u201d\u9493\u8bd7\u6487\u4e86\u6487\u5634\u597d\u50cf\u6709\u70b9\u770b\u4e0d\u8d77\u4ed6\u9053\u201c\u8fd9\u5178\u6545\u4f60\u90fd\u4e0d\u61c2\u5417\uff1f\u56e0\u4e3a\u9152\u7684\u522b\u540d\u53c8 \u53eb\u505a\u201c\u9493\u8bd7\u94a9\u201d\u6211\u603b\u662f\u66ff\u516c\u4e8e\u80cc\u9152\u6240\u4ee5\u53eb\u9493\u8bd7\uff0c\u56e0\u4e3a\u8bfb\u4e66\u80fd\u626b\u6389\u4eba\u809a\u5b50\u91cc\u7684\u4fd7\u6c14\u6240\u4ee5\u4ed6\u505a\u626b \u4fd7\u3002\u201d\u4ed6\u4e0a\u4e0a\u4e0b\u4e0b\u77a7\u4e86\u7ff1\u5927\u8def\u51e0\u773c\xb7\u53c8\u9053\u201c\u4f60\u5927\u6982\u6ca1\u6709\u5ff5\u8fc7\u4ec0\u9ebd\u4e66\u90ed\u5927\u8def\u5927\u7b11\u9053\u201c\u597d\u5b69\u5b50\uff0c \u679c\u7136\u662f\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\xb7\u4e0d\u4f46\u80fd\u559d\u9152\u8fd8\u5f88\u6709\u5b66\u95ee\u3002 \u3000\u3000\u201d\u4ed6\u5927\u7b11\u7740\u53c8\u9053\u201c\u6211\u4e66\u867d\u5ff5\u5f97\u4e0d\u591a\u9152\u5374\u559d\u5f97\u4e0d\u5c11\u4f60\u60f3\u4e0d\u60f3\u8ddf\u6211\u559d\u51e0\u676f\uff1f\u201d\u9493\u8bd7\u9053\u201c\u4f60\u9152 \u91cf\u82e5\u771f\u7684\u597d\u4e3a\u4ec0\u9ebd\u4e0d\u6562\u613f\u6211\u4eec\u516c\u5b50\u559d\u9152\u53bb\uff1f\u201d\u90ed\u5927\u8def\u8fd9\u624d\u53d1\u73b0\u90a3\u4f55\u516c\u4e8e\u65e9\u5df1\u8fdb\u4e86\u82b1\u5385\u5df2\u5f00\u59cb \u548c\u738b\u52a8\u4ed6\u4eec\u5bd2\u77aa\u8d77\u6765\u4ece\u7a97\u4e88\u770b\u8fdb\u53bb\u53ef\u4ee5\u770b\u5230\u738b\u52a8\u548c\u6797\u592a\u5e73\u5bf9\u4ed6\u4e5f\u5f88\u6709\u597d\u611f\u3002 \u3000\u3000\u71d5\u4e03\u5374\u6709\u70b9\u5fc3\u4e0d\u5728\u7109\u7684\u6837\u5b50\u4e0d\u65f6\u626d\u8fc7\u5934\u5f80\u7a97\u5b50\u5916\u9762\u770b\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u5230\u4ed6\u4ed6\u5c31\u7ad9\u4e86\u8d77\u6765\u9762\u80cc\u5bf9\u7740\u522b\u4eba\u5411\u90ed\u5927\u8def\u6084\u6084\u6253\u4e86\u4e2a\u624b\u5f0f\u4e00\u9762\u5f80\u5916\u8fb9\u8d70\u3002 \u3000\u3000\u4ed6\u8d70\u51fa\u82b1\u5385\u65f6\u90ed\u5927\u8def\u5df2\u8fce\u4e86\u4e0a\u53bb\xb7\u9053\u201c\u4f60\u627e\u6211\u6709\u4e8b\uff1f\u201d\u71d5\u4e03\u767d\u4e86\u4ed6\u773c\uff0c\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u597d \u50cf\u603b\u662f\u957f\u4e0d\u5927\u4f3c\u7684\uff1f\u8ddf\u5b69\u5b50\u804a\u5f97\u53cd\u800c\u7279\u522b\u8d77\u52b2\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u90a3\u5b69\u5b50\u7684\u4e00\u5f20\u5634\u6bd4\u5927\u4eba\u8fd8\u80fd \u8bf4\u4f1a\u9053\xb7\u6709\u65f6\u4f60\u82e5\u56e0\u5b69\u5b50\u4eec\u804a\u804a\xb7\u5c31\u4f1a\u53d1\u73b0\u81ea\u5df1\u4e5f\u597d\u50cf\u53d8\u5f97\u5e74\u8f7b\u8d77\u6765\u3002\u201d\u71d5\u4e03\u6ca1\u6709\u8bf4\u8bdd\u5374\u6cbf \u7740\u957f\u5eca\xb7\u6162\u4fb5\u7684\u5411\u5f8c\u9662\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u53ea\u597d\u8ddf\u7740\u4ed6\u8d70\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u6709\u8bdd\u8981\u8ddf\u6211\u8bf4\uff1f\u201d\u71d5\u4e03\u53c8\u8d70\u4e86\u6bb5\u8def\uff0c\u624d\u5ffd\u7136\u56de \u5934\u9053\u201c\u4f60\u770b\u8fd9\u4f4d\u4f55\u516c\u4e8e\u600e\u9ebd\u6837\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u770b\u6765\u4ed6\u56e0\u662f\u4e2a\u72e0\u98ce\u96c5\u7684\u4eba\uff0c\u800c\u4e14\u636e\u8bf4\u8fd8\u5f88\u80fd\u559d \u9152\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u4ed6\u4f1a\u4e0d\u4f1a\u5c31\u662f\u90a3\u2026\u2026\u201d\u90ed\u5927\u8def\u773c\u775b\u4eae\uff0c\u62a2\u7740\u9053\u201c\u5c31\u662f\u90a3\u5728\u594e\u5143\u9986\u66ff\u6211\u4eec \u4ed8\u5e10\u7684\u4eba\uff1f\u201d\u71d5\u4e03\u70b9\u70b9\u5934\xb7\u9053\u201c\u4f60\u60f3\u53ef\u4e0d\u53ef\u80fd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u9685\u6211\u672c\u6765\u6ca1\u6709\u60f3\u5230\u8fd9\u70b9\u73b0\u5728\u8d8a \u60f3\u8d8a\u6709\u53ef\u80fd\u3002\u201d\u71d5\u4e03\u9053\u201c\u8fd9\u5730\u65b9\u53c8\u6ca1\u6709\u4ec0\u9ebd\u540d\u80dc\u98ce\u666f\uff0c\u6e38\u5c71\u7684\u4eba\u600e\u9ebd\u4f1a\u6e38\u5230\u8fd9\u91cc\u6765\uff1f\u800c\u4e14\u8fdf \u4e0d\u6765\uff0c\u65e9\u4e0d\u6765\xb7\u6070\u5de7\u5728\u4eca\u5929\u65e9\u4e0a\u6765\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e16\u4e0a\u51d1\u5de7\u7684\u4e8b\u672c\u6765\u5f88\u591a\u4f46\u8fd9\u4ef6\u4e8b\u7684\u786e\u592a\u5de7 \u4e9b\u4e86\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u4ee5\u524d\u6709\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u518d\u60f3\u60f3\u3002\u201d\u90ed\u5927 \u8def\u9053\u201c\u7528\u4e0d\u7740\u518d\u60f3\u8fd9\u6837\u7684\u4eba\u6211\u82e5\u89c1\u8fc7\uff0c\u5b9a\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d\u85cf\u4e03\u54ac\u7740\u7530\u5507\u9053\u201c\u770b\u738b\u8001\u5927\u548c\u6797\u592a\u5e73 \u7684\u6837\u5b50\u597d\u50cf\u4e5f\u4e0d\u8ba4\u5f97\u4ed6\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u201d\u71d5\u4e03\u8bf4\u9053\u201c\u4ed6\u81ea\u5df1\u8bf4\u4ed6\u53eb\u4f55\u96c5\u98ce\u4f46 \u4e5f\u53ef\u80fd\u662f\u5047\u540d\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u7528\u5047\u540d\u5b57\uff1f\u96be\u9053\u4f60\u8ba4\u4e3a\u4ed6\u5bf9\u6211\u4eec\u6709\u6076\u610f\uff1f\u201d\u71d5\u4e03\u9053\u201c\u5230\u76ee\u524d\u4e3a \u6b62\xb7\u5012\u770b\u4e0d\u51fa\u6709\u4ec0\u9ebd\u6076\u610f\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u975e\u4f46\u6ca1\u6709\u6076\u610f\xb7\u7b80\u76f4\u53ef\u4ee5\u8bf4\u5bf9\u6211\u4eec\u592a\u597d\u4e86\uff0c\u597d\u5f97\u5df2 \u4e0d\u50cf\u8bdd\u3002\u201d\u7897\u4e03\u9053\u5c31\u56fa\u4ed6\u5bf9\u6211\u4eec\u592a\u597d\uff0c\u6240\u4ee5\u6211\u624d\u66f4\u89c9\u5f97\u6000\u7591\u4e2a\u4eba\u82e5\u662f\u5bf9\u522b\u4eba\u597d\u53ec\u8fc7\u4e86\u4efd\u591a\u5c11 \u603b\u6709\u4e9b\u76ee\u7684\u3002\u201d\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u5728\u60f3\u4e00\u4e2a\u4eba\u2018\u505a\u4eba\u2019\u5b9e\u5728\u72e0\u96be\u4f60\u82e5\u8d22\u522b\u4eba\u592a\u597d\u522b\u4eba \u4f1a\u6000\u51dd\u4f60\u6709\u76ee\u7684\u4f60\u82e5\u5bf9\u522b\u4eba\u592a\u574f\xb7\u522b\u4eba\u53c8\u4f1a\u8bf4\u4f60\u662f\u6df7\u86cb\u3002\u201d\u82d1\u4e03\u77aa\u4e86\u4ed6\u4e00\u773c\xb7\u9053\u201c\u6211\u5c31\u77e5\u9053 \u4f60\u4e00\u5b9a\u4f1a\u5e2e\u7740\u4ed6\u8bf4\u8bdd\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u82d1\u4e03\u9053\u201c\u56e0\u4e3a\u4ed6\u4e5f\u80fd\u559d\u9152\u9152\u9b3c\u603b\u8ba4\u4e3a\u4e2a\u4eba \u53ea\u8981\u80fd\u559d\u9152\u5c31\u7edd\u4e0d\u4f1a\u662f\u574f\u4eba\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u5012\u662f\u6b63\u8bdd\xb7\u559d\u9152\u75db\u5feb\u7684\u4eba\u5fc3\u5730\u603b\u6bd4\u8f83\u76f4\u723d\u4e9b \u4f60\u7edd\u4e0d\u4f1a\u770b\u5230\u4e2a\u559d\u9189\u4e86\u9152\u7684\u4eba\uff0c\u8fd8\u5728\u6253\u4e3b\u6020\u5bb3\u4eba\u7684\u3002\u201d\u85cf\u4e03\u9053\u201c\u4f60\u5e76\u6ca1\u6709\u9189\u3002\u201d\u7ff1\u5927\u8def\u9053\uff1a \u201c\u5feb\u9189\u4e86\u6211\u73b0\u5728\u5c31\u6253\u7b97\u8fdb\u53bb\u628a\u4ed6\u64c5\u9189\u3002\u201d\u4ed6\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\u201c\u53ea\u8981\u4ed6\u4e00\u559d\u9189\uff0c\u5c31\u4e0d\u6015\u4ed6\u4e0d\u8bf4\u5b9e \u8bdd\u3002\u201d\u85cf\u4e03\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\u201c\u6211\u5728\u60f3\xb7\u4f60\u8fd9\u4eba\u81f3\u5c11\u8fd8\u6709\u6837\u522b\u4eba\u6bd4\u4e0d\u4e0a\u7684\u957f\u5904\u3002\u201d\u90ed \u5927\u8def\u9053\u201c\u6211\u7684\u957f\u5904\u81f3\u5c11\u6709\u53c1\u767e\u591a\u79cd\u5374\u4e0d\u77e5\u4f60\u8bf4\u7684\u662f\u7530\u79cd\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4f60\u968f\u65f6\u968f\u5730\u90fd\u80fd\u628a\u63e1\u4f4f \u673a\u4f1a\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u673a\u4f1a\u201d\u85cf\u4e03\u9053\u201c\u559d\u9152\u7684\u673a\u4f1a\u201d\u90ed\u5927\u8def\u5f04\u9519\u4e86\u4ef6\u4e8b\u4eba\u6e05\u9192\u65f6\u6709\u5f88\u591a\u79cd \u6240\u4ee5\u559d\u9189\u4e86\u65f6\u4e5f\u5e76\u4e0d\u5b8c\u5168\u6837\uff0c\u5e76\u4e0d\u662f\u90fd\u50cf\u4ed6\u81ea\u5df1\u90a3\u6837\xb7\u53ea\u8981\u77a9\u9189\u5c31\u628a\u5fc3\u91cc\u7684\u8bdd\u5168\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u6709\u7684\u4eba\u559d\u4e86\u559c\u6b22\u5439\u725b\u559c\u6b22\u80e1\u8bf4\u516b\u9053\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u5728\u8bf4\u4ec0\u9ebd\u7b49\u5230\u6e05\u919b\u65f6\u65e9\u5df3\u5fd8\u5f97\u5e72 \u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u8fd8\u6709\u7684\u4eba\u559d\u9189\u4e86\u6839\u672c\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u55a7\u79cd\u4eba\u559d\u9189\u4e86\u4e5f\u8bb8\u4f1a\u75db\u54ed\u6d41\u6d95\u4e5f\u8bb8\u4f1a\u54c8\u54c8\u5927\u7b11\u4e5f\u8bb8\u4f1a\u5012\u5934\u5927\u7761\u4f46\u5374\u7edd\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u4eec\u54ed\u7684\u65f6\u5019\u5982\u4e27\u8003\u5979\u800c\u4e14\u8d8a\u54ed\u8d8a\u4f24\u5fc3\u54ed\u5230\u5f8c\u6765\xb7\u5c31\u597d\u50cf\u4e16\u4e0a\u53ea\u5269\u4e0b\u4e86\u4ed6\u8fd9\u9ebd\u6837\u4e2a\u53ef\u601c \u4eba\u3002 \u3000\u3000\u4f60\u5c31\u7b97\u8dea\u4e0b\u6765\u6c42\u4ed6\u7acb\u523b\u7ed8\u4ed6\u4e24\u767e\u4e07\uff0c\u4ed6\u53cd\u800c\u4f1a\u54ed\u5f97\u66f4\u4f24\u5fc3\u3002 \u3000\u3000\u7b49\u4ed6\u6e05\u9192\u65f6\xb7\u4f60\u518d\u95ee\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u54ed\xb7\u4ed6\u81ea\u5df1\u5b9a\u4e5f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u4ed6\u4eec\u7b11\u7684\u65f6\u5019\xb7\u5c31\u597d\u50cf\u5929\u535c\u5ffd\u7136\u6389\u4e0b\u4e86\u6ee1\u5730\u7684\u91d1\u5143\u5b9d\u800c\u4e14\u9664\u4e86\u4ed6\u4e4b\u5916\xb7\u522b\u4eba\u90fd\u6361\u4e0d\u5230\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u7684\u5bb6\u5df2\u88ab\u4fa5\u5149\u4e86\u4ed6\u8fd8\u662f\u800d\u7b11\u3002\u4f60\u5c31\u7b97\u201c\u5288\u5288\u556a\u556a\u201d\u7ed9\u4ed6\u5341\u51e0\u4e2a\u8033\u5149\xb7\u4ed6\u4e5f\u8bb8\u7b11\u5f97 \u66f4\u8d77\u52b2\u3002 \u3000\u3000\u4ed6\u76ef\u53ea\u8981\u4e00\u7761\u7740\xb7\u90a3\u5c31\u66f4\u60e8\uff0c\u5c31\u7b97\u5168\u4e16\u754c\u7684\u4eba\u90fd\u6765\u8e22\u4ed6\u4e00\u811a\uff0c\u4e5f\u8e22\u4e0d\u9192\u5c31\u7b97\u628a\u4ed6\u4e22\u5230\u6cb3 \u91cc\uff0c\u4ed6\u8fd8\u662f\u7167\u7761\u4e0d\u8bef\u7684\u3002 \u3000\u3000\u4f55\u96c5\u98ce\u6070\u5de7\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u5f00\u59cb\u7684\u65f6\u5019\u4ed6\u597d\u50cf\u8fd8\u80fd\u559d\uff0c\u800c\u4e14\u559d\u5f97\u5f88\u5feb\u4e0d\u505c\u5730\u628a\u9152\u676f\u53c8\u4e00\u676f\u5f80\u919a\u91cc\u94a5\uff0c\u4f46\u5ffd\u7136\u95f4\u4f60\u521a \u7728\u4e86\u7728\u773c\u4ed6\u5df2\u7ecf\u7761\u7740\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u7761\u7740\uff0c\u90ed\u5927\u8def\u5c31\u7b11\u3002 \u3000\u3000\u71d5\u4e03\u6068\u5f88\u9053\u201c\u4f60\u4e5f\u559d\u9189\u5382\u9ebd\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u9189\uff1f\u4f60\u770b\u6211\u6709\u6ca1\u6709\u4e00\u70b9\u559d\u9189\u7684\u6837\u5b50\uff1f\u201d\u71d5\u4e03 \u9053\u201c\u6ca1\u6709\u4e00\u70b9\u6709\u516b\u4e5d\u70b9\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u9519\u4e86\uff0c\u6211\u73b0\u5728\u6e05\u919b\u5f97\u7b80\u76f4\u5c31\u50cf\u5b54\u592b\u5b50\u4e00\u6837\u3002\u201d\u71d5\u4e03 \u9053\u201c\u4f60\u7b11\u5f97\u5374\u50cf\u662f\u58eb\u72d7\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u7b11\u4ed6\uff0c\u8fd8\u6ca1\u8d3b\u59cb\uff0c\u4ed6\u5df2\u7ecf\u88ab\u6211\u5708\u9189\u4e86\u3002\u201d\u85d0 \u4e03\u9053\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u4e3a\u4ec0\u9ebd\u8981\u704c\u4ed6\u9152\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u8bb0\u5f97\u6211\u672c\u6765\u662f\u60f3\u8981\u53eb\u4ed6\u8bf4\u5b9e\u8bdd \u7684\u3002\u201d\u71d5\u4e03\u9053\u201c\u4ed6\u8bf4\u4e86\u5417\uff1f\u201d\u90ed\u5927\u8def\u9053\uff0c\u201c\u8bf4\u4e86\u3002\u201d\u71d5\u4e03\u9053\u201c\u8bf4\u4e86\uff1f\u8bf4\u4e86\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053 \u201c\u4ed6\u8bf4\u4ed6\u82e5\u5bf9\u6211\u4eec\u6709\u6076\u610f\xb7\u5c31\u4e0d\u4f1a\u559d\u9189\uff0c\u9189\u5f97\u50cf\u6b7b\u732a\u6837\u3002\u201d\u7897\u4e03\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u770b\u7740\u4ed6\u6447\u7740\u5934\u9053 \u201c\u6709\u65f6\u6211\u771f\u770b\u4e0d\u900f\u4f60\xb7\u7a76\u7adf\u662f\u559d\u9189\u4e86\uff1f\u8fd8\u662f\u5f88\u6e05\u9192\uff1f\u201d\u90ed\u5927\u8def\u563b\u563b\u7684\u7b11\uff0c\u770b\u7740\u738b\u52a8\u3002\u738b\u52a8\u9053 \u201c\u4f60\u770b\u6211\u5e72\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u5728\u7b49\u7740\u4f60\u8bf4\u8bdd\u73b0\u5728\u5c82\u975e\u5df2\u7ff0\u5230\u4f60\u8bf4\u8bdd\u7684\u65f6\u5019\u738b\u52a8\u9053\u201c\u4f60 \u8981\u6211\u8bf4\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u8bf4\u6211\u6e05\u9192\u7684\u65f6\u5019\u4e5f\u9189\uff0c\u9189\u7684\u65f6\u5019\u53cd\u800c\u6e05\u9192\u3002\u201d\u738b\u52a8\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86 \u8fd9\u7684\u786e\u662f\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u7b54\u5bf9\u4e86\u9ebd\uff1f\u201d\u738b\u52a8\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d\u5f8c\u9662\u90a3\u6392\u5c4b\u5b50\u91cc\xb7\u4e5f\u6446\u4e86\u4e24\u5f20\u5e8a\u3002 \u3000\u3000\u8fd9\u4e24\u5f20\u5e8a\u597d\u50cf\u5c31\u662f\u4e3a\u559d\u9189\u4e86\u7684\u5ba2\u4eba\u51c6\u5907\u7684\u3002 \u3000\u3000\u4f55\u96c5\u98ce\u5c31\u5bb6\u662f\u4e2a\u6b7b\u4eba\u822c\u88ab\u6307\u5230\u8fd9\u5f20\u5e8a\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4ed6\u4eca\u5929\u6765\u8fd8\u662f\u7b97\u6765\u5bf9\u4e86\u65f6\u5019\u82e5\u662f\u524d\u4e24\u5929\u6765\u5c31\u53ea\u597d\u7761\u5730\u677f\u3002\u201d\u738b\u52a8\u9053\u201c\u6211\u53ea \u671b\u4ed6\u8fd9\u89c9\u80fd\u7761\u5230\u660e\u5929\u5929\u4eae\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\u201c\u514d\u5f97\u6211\u8ba2\u51fb\u5f53\u4e1c\u897f\u3002\u201d\u90ed\u5927\u8def \u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u5f53\u4e1c\u897f\uff1f\u201d\u738b\u52a8\u9053\uff1a\u201c\u8bf7\u5ba2\u4eba\u5403\u665a\u996d\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e5f\u8bb8\u6211\u4eec\u7528\u4e0d\u7740\u5f53\u4e1c \u897f\uff0c\u53ea\u7b49\u7740\u732b\u513f\u6447\u94c3\u5c31\u884c\u71d5\u4e03\u9053\u4f60\u8ba4\u4e3a\u665a\u996d\u8fd8\u4f1a\u6709\u4eba\u9001\u6765\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u54bd\u3002\u201d\u85cf\u4e03\u5fcd\u4e0d\u4f4f \u7b11\u9053\uff0c\u201c\u4f60\u7b80\u76f4\u597d\u50cf\u5df2\u7ecf\u5403\u5b9a\u4ed6\u4e86\u3002\u201d\u90ed\u5927\u8def\u5927\u7b11\u9053\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u6211\u5df2\u7ecf\u6dee\u5907\u5403\u4ed6\u4e00\u8f88\u5b50\u8981 \u4ed6\u517b\u6211\u7684\u8001\u3002\u3002 \u3000\u3000\u4ed6\u58f0\u97f3\u8bf4\u5f97\u7279\u522b\u9ad8\u597d\u50cf\u6545\u610f\u8981\u8ba9\u90a3\u5165\u542c\u5230\u3002 \u3000\u3000\u90a3\u4eba\u662f\u4e0d\u662f\u4e00\u76f4\u8eb2\u5728\u6697\u4e2d\u5077\u770b\u7740\u4ed6\u4eec\uff1f\u90a3\u4eba\u662f\u4e0d\u662f\u4f55\u96c5\u98ce\u662f\u4e0d\u662f\u559d\u9189\u4e86\uff1f\u9189\u5f97\u5feb\u7684\u4eba\uff0c \u5f80\u5f80\u9192\u5f97\u4e5f\u5feb\u3002 \u3000\u3000\u8fd8\u6ca1\u5230\u9ec4\u660f\uff0c\u90a3\u4e24\u4e2a\u5b69\u5b50\u5ffd\u7136\u4ece\u5f8c\u9662\u8dd1\u5230\u524d\u9762\u6765\xb7\u606d\u606d\u656c\u656c\u7684\u7ad9\u5728\u4ed6\u4eec\u9762\u524d\u606d\u606d\u656c\u78b0\u7684 \u9001\u4e0a\u4e86\u4efd\u8bf7\u7c98\u3002 \u3000\u3000\u9493\u8bd7\u9053\u201c\u6211\u4eec\u5bb6\u516c\u4e88\u8bf4\u4eca\u6668\u56db\u6270\u4e86\u5404\u6062\uff0c\u665a\u4e0a\u5c31\u8be5\u4ed6\u56de\u8bdd\uff0c\u52a1\u5fc5\u8bf7\u5404\u4f4d\u8d4f\u5149\u3002\u201d\u90ed\u5927\u8def \u770b\u4e86\u738b\u52a8\u8ddf\u6324\u4e86\u6324\u773c\u775b\u3002 \u3000\u3000\u738b\u52a8\u54fa\u54fa\u9053\u201c\u770b\u6765\u7528\u4e0d\u7740\u732b\u6447\u94c3\u4e86\u3002\u201d\u9493\u8bd7\u6ca1\u542c\u89c1\u4ed6\u5728\u8bf4\u4ec0\u9ebd\u5c31\u7b97\u542c\u89c1\u4e5f\u542c\u4e0d\u7530\u5fcd\u4e0d\u4f4f \u95ee\u9053\u201c\u738b\u5927\u7237\u5728\u8bf4\u4ec0\u9ebd\u201d\u90ed\u5927\u8def\u4e0d\u7b49\u738b\u52a8\u5f00\u53e3\xb7\u5df2\u62a2\u7740\u9053\u2019\u4ed6\u8bf4\u6211\u4eec\u4e00\u5b9a\u8d27\u5149\u3002\u201d\u71d5\u4e03\u53f9\u4e86 \u53e3\u6c14\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8138\u76ae\u5012\u771f\u4e0d\u8584\u3002\u201d\u9493\u8bd7\u5ffd\u7136\u7728\u7728\u773c\u53c8\u95ee\u201c\u8fd9\u4f0e\u5927\u7237\u5728\u8bf4\u4ec0\u9ebd\u201d\u90ed \u5927\u8def\u53c8\u62a2\u7740\u9053\u201c\u4ed6\u8bf4\u6211\u4eec\u9a6c\u4e0a\u5c31\u53bb\u3002\u201d\u90ed\u5927\u8def\u7684\u62f3\u5934\u9493\u8bd7\u7b11\u9053\u201c\u65e2\u7136\u5982\u6b64\u6211\u4eec\u5c31\u5f97\u56de\u53bb\u51c6\u5907 \u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u5feb\u53bb\xb7\u8d8a\u5feb\u8d8a\u597d\u3002\u201d\u9493\u8bd7\u606d\u606d\u656c\u656c\u7684\u884c\u4e86\u4e2a\u793c\xb7\u5ffd\u7136\u4e5f\u5411\u626b\u4fd7\u6324\u4e86\u6324\u963b\u775b \u6084\u6084\u9053\u201c\u62ff\u6765\u3002\u201d\u626b\u4fd7\u77aa\u4e86\u4ed6\u4e00\u9650\u54fc\u9053\u201c\u4f60\u6025\u4ec0\u9ebd\xb7\u7b97\u4f60\u539f\u4e86\u5c31\u662f\u3002\u201d\u8fd9\u6b21\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee \u9053\u201c\u8c0b\u626b\u4fd7\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u54fc\u9053\u201c\u4f60\u6025\u4ec0\u9ebd\uff0c\u7b97\u4f60\u5f3a\u4e86\u5c31\u662f\u3002\u201d\u8fd9\u6b21\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u8bf4 \u4ec0\u9ebd\uff1f\u201d\u94a9\u8bd7\u62a2\u7740\u9053\u201c\u4ed6\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u8bf4\u3002\u201d\u4ed6\u62c9\u7740\u626b\u4fd7\u5c31\u60f3\u998f\uff0c\u626b\u4fd7\u770b\u6765\u5374\u6bd4\u8f83\u8001\u5b9e\u800c\u4e14\u597d \u50cf\u5f88\u7740\u6025\uff0c\u7ea2\u7740\u8138\u9053\u201c\u6211\u8ddf\u4ed6\u6253\u8d4c\u8f93\u7ed9\u4ed6\u540a\u94b1\uff0c\u4ed6\u903c\u7740\u95ee\u6211\u8981\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u600e\u9ebd\u8f93\u7684\uff1f\u201d \u626b\u4fd7\u201c\u6211\u751f\u6015\u5404\u4f4d\u4e0d\u80af\u8d4f\u5149\uff0c\u4ed6\u5374\u8bf4\u2026\u2026\u201d\u4ed6\u963b\u775b\u6687\u7740\u90ed\u5927\u8def\xb7\u5ffd\u7136\u64ad\u6447\u5934\u9053\u201c\u4ed6\u8bf4\u7684 \u8bdd\xb7\u6211\u4e0d\u6562\u8bf4\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u53ea\u7ba1\u653e\u5fc3\u8bf4\u7edd\u5bf9\u6ca1\u6709\u4eba\u602a\u4f60\u3002\u3002 \u3000\u3000\u626b\u4fd7\u773c\u73e0\u5b50\u76f4\u8f6c\u9053\u82e5\u662f\u6709\u4eba\u602a\u6211\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u90a3\u4e5f\u6c72\u5173\u7cfb\uff0c\u6211\u4fdd\u62a4\u4f60\u3002\u201d\u626b\u4fd7\u8fd9\u624d \u7b11\u9053\u201c\u4ed6\u8bf4\xb7\u5c31\u7b97\u522b\u4eba\u4e0d\u597d\u610f\u601d\xb7\u5927\u7237\u4f60\u4e5f\u5b9a\u4f1a\u53bb\u7684\u56e0\u4e3a\u8fd9\u4e9b\u4eba\u91cc\u9762\uff0c\u5c31\u7b97\u5927\u7237\u4f60\u7684\u8138\u76ae\u6700 \u539a\u3002\u201d\u4ed6\u8bdd\u5207\u8bf4\u5b8c\uff0c\u5df2\u62c9\u7740\u9493\u5f85\u6e9c\u4e4b\u5927\u5409\u3002\u8fc7\u4e86\u5f88\u4e45\xb7\u8fd8\u53ef\u4ee5\u542c\u5230\u4ed6\u4eec\u5728\u5403\u5403\u66f2\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\u8d2d\u7483\u9053\u201c\u539f\u6765\u8fd9\u5c0f\u9b3c\u4e5f\u4e0d\u8001\u5b9e\xb7\u5c45\u7136\u4f1a\u7ed5\u7740\u53e3\u5b50\u9a82\u4eba\u3002\u201d\u71d5\u4e03\u5fcd \u4e0d\u4f4f\u7b11\u9053\u201c\u5176\u5b9e\u4ed6\u8fd9\u4e5f\u4e0d\u80fd\u7b97\u9a82\u4eba\uff0c\u53ea\u4e0d\u8fc7\u5728\u8bf4\u5b9e\u8bdd\u800c\u738b\u52a8\u9053\u201c\u5176\u5b9e\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u7b97\u662f\u8138\u76ae\u539a \u53ea\u4e0d\u8fc7\u662f\u4eba\u7a77\u5fd7\u77ed\u71d5\u4e03\u63a5\u7740\u9053\u201c\u800c\u4e14\u662f\u997f\u6b7b\u9b3c\u6295\u80ce\u3002\u201d\u90ed\u5927\u8def\u4e5f\u4e0d\u751f\u6c14\u60a0\u7136\u9053\u201c\u597d\u6211\u53c8\u7a77\u53c8 \u4fc4\u53c8\u539a\u8138\u76ae\u4f60\u4eec\u90fd\u662f\u541b\u5b50\u3002\u201d\u4ed6\u5ffd\u7136\u51b7\u7b11\u4e86\u4e24\u58f0\u9053\u201c\u4f46\u82e5\u4e0d\u662f\u6211\u8fd9\u4e2a\u539a\u8138\u76ae\u4f60\u4eec\u8fd9\u4e9b\u4f2a\u541b\u5b50 \u4eca\u5929\u665a\u4e0a\u5c31\u8981\u4e0a\u5f53\u94fa\u3001\u51fa\u6d0b\u76f8\u3002\u201d\u71d5\u4e03\u9053\u201c\u6211\u4eec\u5230\u5e95\u662f\u5ba2\u4eba\u4f60\u600e\u9ebd\u597d\u610f\u601d\u53bb\u5403\u4eba\u5bb6\u7684\uff1f\u201d\u90ed \u5927\u8def\u73b2\u51b7\u9053\u201c\u4ed6\u5230\u5e95\u8fd8\u662f\u4e2a\u4eba\u5403\u4ed6\u7684\u81f3\u5c11\u603b\u6bd4\u5403\u732b\u7684\u597d\u4e2a\u4eba\u82e5\u8fde\u732b\u9001\u6765\u7684\u4e1c\u897f\u5403\u8925\u4e0d\u4ea6\u4e50 \u4e4e\uff0c\u8fd8\u6709\u4ec0\u9ebd\u8138\u6446\u67b6\u5b50\uff1f\u201d\u738b\u52a8\u9053\u201c\u8c01\u6446\u67b6\u5b50\uff1f\u6211\u53ea\u4e0d\u8fc7\u60f3\u8981\u4ed6\u628a\u9152\u83dc\u9001\u5230\u8fd9\u91cc\u6765\u800c\u5df2\u3002\u201d \u83dc\u4e0d\u591a\u9152\u5012\u771f\u4e0d\u5c11\u3002 \u3000\u3000\u83dc\u867d\u7136\u4e0d\u591a\uff0c\u5374\u5f88\u7cbe\u81f4\xb7\u6446\u5728\u4e00\u683c\u683c\u7684\u98df\u76d2\u91cc\u8fde\u98a7\u8272\u90fd\u914d\u5f97\u5f88\u597d\xb7\u5c31\u662f\u770b\u770b\u90fd\u4ee4\u4eba\u89c9\u5f97 \u5f88\u8212\u670d\u3002 \u3000\u3000\u4f55\u96c5\u98ce\u9053\u201c\u8fd9\u4e9b\u83b1\u867d\u662f\u6628\u591c\u5c31\u5df2\u505a\u597d\u4e86\u7684\uff0c\u4f46\u5c0f\u5f1f\u7ec8\u5e74\u5728\u5916\u8d70\u52a8\u5bf9\u4fdd\u5b58\u98df\u7269\u7684\u6cd5\u5b50\uff0c\u5012 \u53ef\u7b97\u662f\u7565\u6709\u5fc3\u5f97\u53ef\u4ee5\u4fdd\u8bc1\u7edd\u4e0d\u81f4\u53d8\u5473\u53ea\u4e0d\u8fc7\u4ee5\u8def\u83b1\u656c\u5ba2\u5b9e\u5acc\u592a\u7b80\u6162\u4e86\u4e9b\u3002\u201d\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u9053 \u201c\u4f60\u6628\u5929\u665a\u4e0a\u5c31\u51c6\u5907\u4e86\u8fd9\u9ebd\u591a\u83dc\u96be\u9053\u7b97\u51c6\u4e86\u4eca\u5929\u665a\u4e0a\u8981\u8bf7\u5ba2\uff1f\u201d\u9493\u8bd7\u6b63\u5728\u659f\u6d12\u62a2\u7740\u9053\u201c\u6211\u4eec \u5bb6\u516c\u5b50\u6700\u597d\u5ba2\u4e00\u8def\u4e0a\u65e0\u8bba\u9047\u7740\u4ec0\u9ebd\u4eba\u90fd\u4f1a\u62c9\u7740\u4ed6\u559d\u4e24\u676f\u6240\u4ee5\u65e0\u8bba\u5230\u54ea\u91cc\u9152\u83b1\u90fd\u51c6\u5907\u5f97\u72e0\u5145\u90ed \u5927\u8def\u5411\u4ed6\u6324\u4e86\u6324\u773c\u775b\u6084\u6084\u7b11\u9053\u201c\u8fd9\u9ebd\u6837\u770b\u6765\uff0c\u8138\u76ae\u539a\u7684\u4eba\u5e76\u4e0d\u662f\u53ea\u6709\u6211\u4e2a\u3002\u201d\u4f55\u96c5\u98ce\u906d\u201c\u90ed \u5144\u5728\u8bf4\u4ec0\u9ebd\uff1f\u201d\u7530\u5927\u8def\u9053\u201c\u6211\u5728\u8bf4\u4ed6\u2026\u2026\u201d\u9493\u8bd7\u5ffd\u7136\u5927\u58f0\u54b3\u55fd\u3002 \u3000\u3000\u7ff1\u5927\u8def\u7b11\u9053\u201c\u4ed6\u9152\u5012\u5f97\u592a\u6162\u4e86\u6211\u7b80\u76f4\u5df2\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u3002\u201d\u4ed6\u7b2c\u4e00\u4e2a\u4e3e\u8d77\u6d12\u676f\u55c5\u4e86\u55c5\xb7\u5927 \u7b11\u9053\u201c\u597d\u9152\u6211\u501f\u82b1\u732e\u4f5b\uff0c\u5148\u656c\u4e3b\u4eba\u4e00\u676f\u3002\u201d\u4ed6\u521a\u60f3\u8d50\u4f55\u96c5\u98ce\u5df2\u6309\u4f50\u4e86\u4ed6\u7684\u624b\uff0c\u7b11\u9053\u201c\u90ed\u5144\u5148 \u7b49\u7b49\xb7\u8fd9\u7b2c\u4e00\u676f\u9152\u5e94\u8be5\u6211\u656c\u56db\u4f4d\uff0c\u56db\u4f4d\u4e00\u9f50\u2026\u2026\u201d\u5ffd\u7136\u95f4\uff0c\u4e00\u6761\u9ed1\u72d7\u3001\u4e00\u53ea\u9ed1\u732b\u540c\u65f6\u4ece\u5916\u9762 \u7a9c\u4e86\u8fdb\u6765\u7a9c\u4e0a\u4e86\u684c\u4e88\u521a\u52d8\u6ee1\u7684\u51e0\u676f\u6d12\u5c31\u4e00\u9f50\u88ab\u649e\u7ffb\u3002 \u3000\u3000\u4f55\u96c5\u98ce\u8138\u8272\u53d8\u4e86\u53d8\xb7\u7a81\u7136\u51fa\u65bc\u3002 \u3000\u3000\u4ed6\u53cc\u624b\u770b\u6765\u53c8\u767d\u51c0\u3001\u53c8\u79c0\u6c14\u5c31\u597d\u50cf\u4e00\u8f88\u5b50\u6ca1\u6709\u78b0\u8fc7\u810f\u4e1c\u897f\u8fde\u9152\u74f6\u5b50\u5012\u4e86\u90fd\u4e0d\u4f1a\u53bb\u6276\u6276\u3002 \u3000\u3000\u8fd9\u53ea\u732b\u548c\u8fd9\u6761\u72d7\u5374\u597d\u50cf\u521a\u4ece\u6ce5\u91cc\u6253\u8fc7\u6eda\u51fa\u6765\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u51fa\u624b\u5c31\u6293\u4f73\u4e86\u5b83\u6253\u7684\u8116\u5b50\xb7\u4e00\u53ea\u624b\u4e00\u4e2a\u5c06\u5b83\u4eec\u62ce\u4e86\u8d77\u6765\xb7\u6b63\u51c6\u5907\u5f80\u5916\u9762\u7529\u3002 \u3000\u3000\u4ed6\u521a\u5f80\u5916\u7529\u5ffd\u7136\u53c8\u6709\u4e24\u53cc\u624b\u4f38\u8fc7\u6765\u8f7b\u8f7b\u7684\u63a5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u63a5\u4f50\u4e86\u90a3\u6761\u9ed1\u732b\xb7\u9891\u4e03\u63a5\u4f4f\u4e86\u9ed1\u72d7\u3002 \u3000\u3000\u90ed\u5927\u8def\u629a\u7740\u732b\u8116\u5b50\u7b11\u9053\u201c\u4f60\u6765\u5e72\u4ec0\u9ebd\uff1f\u83ab\u975e\u8981\u548c\u4f55\u516c\u5b50\u62a2\u7740\u505a\u4e3b\u4eba\u9ebd\uff1f\u201d\u71d5\u4e03\u62cd\u7740\u72d7\u5934 \u9053\u201c\u4f60\u6765\u5e72\u4ec0\u9ebd\uff1f\u83ab\u975e\u4e5f\u548c\u90ed\u5148\u751f\u6837\u6025\u7740\u8981\u559d\u9152\uff1f\u201d\u4f55\u96c5\u98ce\u9501\u7740\u7709\xb7\u52c9\u5f3a\u7b11\u9053\u201c\u8fd9\u9ebd\u810f\u7684\u5c0f \u755c\u7272\u4e24\u5783\u4e3a\u4f55\u8fd8\u62b1\u5728\u8eab\u4e0a\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u559c\u6b22\u732b\uff0c\u5c24\u5176\u662f\u597d\u8bf7\u5ba2\u7684\u732b\u3002\u201d\u71d5\u4e03\u7b11\u9053\u201c\u6211\u559c \u6b22\u72d7\uff0c\u5c24\u5176\u662f\u597d\u559d\u9152\u7684\u72d7\u3002\u201d\u9152\u5012\u7ffb\u5728\u684c\u4e8e\u4e0a\u7684\u65f6\u5019\xb7\u8fd9\u6761\u72d7\u7684\u786e\u4f38\u51fa\u820c\u5934\u6765\u8214\u4e86\u8214\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u53ea\u53ef\u501f\u8fd9\u4e0d\u662f\u91d1\u6bdb\u72ee\u5b50\u72d7\u3002\u201d\u6797\u592a\u5e73\u631f\u8d77\u5757\u6cb9\u9e21\u53c8\u653e\u4e0b\u9053\u201c\u53ea\u53ef\u60dc\u8fd9\u4e0d\u662f \u70e4\u9e2d\u3002\u201d\u4f55\u96c5\u98ce\u58f0\u8272\u4e0d\u52a8\u5fae\u7b11\u9053\u201c\u56db\u4f7c\u8bf4\u7684\u8bdd\u5c0f\u5f1f\u4e3a\u4f55\u603b\u662f\u542c\u4e0d\u64c5\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e5f\u8bb8\u6211 \u4eec\u90fd\u5728\u8bf4\u9189\u8bdd\u3002\u201d\u85cf\u4e03\u62b1\u7740\u7684\u72d7\u7a81\u7136\u60e8\u5420\u4e86\u58f0\uff0c\u4ece\u4ed6\u6000\u4e2d\u8df3\u8d77\u6765\u201c\u7830\u201d\u7684\u843d\u5728\u684c\u4e8e\u4e0a\u5c31\u8c61\u662f \u5ffd\u7136\u88ab\u4eba\u5272\u65ad\u4e86\u8116\u5b50\xb7\u8fde\u53eb\u90fd\u53eb\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u672c\u6765\u9c9c\u52a9\u6d3b\u8df3\u7684\u6761\u72d7\uff0c\u7a81\u7136\u5c31\u53d8\u6210\u4e86\u6761\u6b7b\u72d7\u3002 \u3000\u3000\u71d5\u4e03\u770b\u7740\u770b\u7740\u6b7b\u72d7\uff0c\u53c8\u62ac\u8d77\u5934\u770b\u770b\u90ed\u5927\u8def\u9053\u201c\u4f60\u77a7\u89c1\u4e86\u9ebd\u8fd9\u5c31\u662f\u6025\u7740\u8981\u559d\u9152\u7684\u699c\u6837\u3002\u201d \u90ed\u5927\u8def\u4e5f\u5728\u770b\u7740\u6b7b\u72d7\u53c8\u62fe\u8d77\u5934\u770b\u770b\u4f55\u96c5\u98ce\u9053\u201c\u6211\u4eec\u90fd\u4e0d\u662f\u5e7f\u4e1c\u4eba\xb7\u9601\u4e0b\u4e3a\u4f55\u8981\u8bf7\u6211\u4eec\u5403\u72d7 \u8089\uff1f\u201d\u738b\u52a8\u770b\u770b\u4f55\u96c5\u98ce\xb7\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u8c08\u8c08\u9053\u201c\u542c\u8bf4\u9ed1\u72d7\u7684\u8089\u6700\u6ecb\u8865\u3002\u201d\u6797\u592a\u5e73\u51b7\u7b11 \u9053\u201c\u4e5f\u8bb8\u8fd9\u5e76\u4e0d\u662f\u9ed1\u72d7\u53ea\u4e0d\u8fc7\u7a7f\u4e86\u8eab\u9ed1\u8863\u670d\u3002\u3002 \u3000\u3000\u4f55\u96c5\u98ce\u5c45\u7136\u8fd8\u662f\u58f0\u8272\u4e0d\u52a8\u6162\u6162\u7684\u7ad9\u8d77\u6765\uff0c\u62cd\u4e86\u62cd\u8eab\u4e0a\u7684\u9152\u9988\u9053\u201c\u5404\u4f4d\u5c11\u5750\uff0c\u5728\u4e0b\u53bb\u6362\u5957 \u8863\u670d\xb7\u53bb\u53bb\u5c31\u6765\u3002\u201d\u90ed\u5927\u8def\u770b\u770b\u738b\u52a8\uff0c\u9053\u201c\u4ed6\u8bf4\u4ed6\u53bb\u53bb\u5c31\u6765\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u542c\u89c1\u4e86\u3002\u201d\u90ed\u5927 \u8def\u9053\u201c\u4f60\u76f8\u4fe1\uff1f\u201d\u738b\u52a8\u9053\u201c\u76f8\u4fe1\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u5230\u522b\u5730\u65b9 \u53bb\uff0c\u4ed6\u5c31\u5728\u8fd9\u4e88\u5f8c\u6362\u8863\u670d\u3002\u201d\u4f55\u96c5\u98ce\u9759\u9759\u7684\u770b\u7740\u4ed6\u4ecd\u518d\u4e5f\u4e0d\u8bf4\u522b\u7684\u8bdd\u770b\u4e86\u5f88\u4e45\u7f13\u7f13\u8f6c\u8eab\xb7\u63d0 \u8d77\u4e86\u5f8c\u9762\u51e0\u4e0a\u7684\u7bb1\u5b50\uff0c\u8d70\u4eba\u5f8c\u3002 \u3000\u3000\u65bc\u662f\u88ab\u6bb5\u505a\u7684\u5c31\u6302\u5728\u8fd9\u5c0f\u5ba2\u5385\u4e2d\u95f4\u3002 \u3000\u3000\u522b\u7684\u4eba\u77aa\u7740\u5b50\u90ed\u5927\u8def\u5374\u770b\u7740\u9493\u8bd7\u3002 \u3000\u3000\u9493\u8bd7\u7684\u5c0f\u8138\u4e5f\u5df2\u53d1\u767d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u53c8\u5411\u4ed6\u6324\u4e86\u6324\u773c\u775b\xb7\u7b11\u9053\u201c\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u53bb\u6362\u8863\u670d\uff1f\u201d\u9493\u8bd7\u560e\u8815\u7740\u9053\u201c\u6211\u2026 \u201c\u6211\u6ca1\u6709\u5e26\u8863\u670d\u6765\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u91cc\u6ca1\u6709\u8863\u670d\u6362\u96be\u9053\u4e0d\u4f1a\u56de\u5bb6\u53bb\u6362\u201d\u9493\u8bd7\u7acb\u523b\u559c\u52a8\u989c\u8272 \u62c9\u8d77\u626b\u4fd7\u7684\u624b\uff0c\u62d4\u811a\u5c31\u8dd1\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\xb7\u9053\u201c\u770b\u6765\u8fd9\u4eba\u7684\u8138\u76ae\u867d\u539a\uff0c\u5fc3\u5012\u4e0d\u9ed1\u3002\u201d\u4ed6\u770b\u7740\u90ed\u5927\u8def\u65f6\u76ee\u4e2d\u5145\u6ee1\u4e86\u6e29\u67d4 \u4e4b\u610f\u4f46\u7b49\u4ed6\u56de\u8fc7\u5934\u65f6\u3002\u76ee\u5149\u4e94\u523b\u53d8\u5f97\u51b0\u51b7\xb7\u8138\u8272\u4e5f\u7acb\u523b\u53d8\u5f97\u51b0\u51b7\u3002 \u3000\u3000\u4f55\u96c5\u98ce\u5df2\u4ece\u5b50\u5f8c\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u679c\u7136\u6362\u4e86\u8eab\u8863\u670d\u3002 \u3000\u3000\u4e00\u8eab\u9ed1\u8863\u670d\u3002 \u3000\u3000\u9ed1\u8863\u670d\uff0c\u9ed1\u9774\u5b50\u8138\u4e0a\u8499\u7740\u9ed1\u5dfe\u8fde\u8eab\u5f8c\u80cc\u7740\u7684\u4e00\u67c4\u5251\uff0c\u5251\u7792\u90fd\u662f\u4e4c\u9ed1\u8272\u7684\u3002 \u3000\u3000\u67c4\u56db\u5c3a\u4e03\u5bf8\u7684\u5251\u3002 \u3000\u3000\u6797\u592a\u5e73\u53d8\u8272\u8c0a\u201c\u539f\u6765\u662f\u4f60\xb7\u4f60\u6ca1\u6709\u6b7b\u3002\u201d\u9ed1\u8863\u4eba\u51b7\u51b7\u9053\u201c\u53ea\u56e0\u4f60\u8fd8\u4e0d\u4f0d\u5f97\u6740\u4eba\xb7\u4e5f\u4e0d\u4f1a \u6740\u4eba\u3002\u201d\u6797\u592a\u5e73\u8138\u4ea1\u9635\u9752\u9635\u7ea2\u3002 \u3000\u3000\u4ed6\u7684\u786e\u8fd8\u4e0d\u4f1a\u6740\u4eba\u6740\u4e86\u4eba\u5f8c\u5c31\u5df2\u5fc3\u614c\u610f\u4e71\xb7\u4e5f\u4e0d\u53bb\u770b\u770b\u90a3\u4eba\u662f\u5426\u771f\u7684\u6b7b\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4f60\u82e5\u4f1a\u6740\u4eba\xb7\u5c31\u7b97\u6211\u771f\u7684\u6b7b\u4e86\xb7\u4f60\u4e5f\u8be5\u5728\u6211\u8eab\u4e0a\u591a\u7530\u51e0\u5200\u3002\u201d\u6797\u592a\u5e73\u54ac\u7740\u7259 \u9053\u201c\u6211\u5df2\u5b66\u4f1a\u4e86\u3002\u201d\u9ed1\u8863\u4eba\u9053\u201c\u5b66\u4e0d\u4f1a\u7684\u4e0d\u4f1a\u6740\u4eba\u7684\u4eba\u6c38\u8fdc\u90fd\u5b66\u4e0d\u4f1a\u7684\u3002\u6740\u4eba\u4e5f\u5f97\u8981\u6709\u5929 \u4efd\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\uff0c\u9601\u4e0b\u83ab\u975e\u5f88\u6709\u6740\u4eba\u7684\u5929\u4efd\uff1f\u201d\u9ed1\u8863\u4eba\u9053\u201c\u8fd8\u8fc7\u5f97\u53bb\u3002\u201d\u71d5 \u4e03\u7b11\u4e86\u7b11\uff0c\u8c08\u8c08\u9053\u201c\u9601\u4e0b\u82e5\u771f\u6709\u6740\u4eba\u7684\u5929\u4efd\u6211\u4eec\u73b0\u5728\u5c31\u5df2\u7ecf\u5168\u90fd\u6b7b\u4e86\u3002\u201d\u9ed1\u8863\u4eba\u6c89\u601d\u4e86\u534a\u664c \u9053\u201c\u4f60\u4eec\u8fd8\u6d3b\u7740\uff0c\u771f\u8be5\u8c22\u8c22\u90a3\u6761\u72d7\u3002\u201d\u71d5\u4e03\u770b\u7740\u90ed\u5927\u8def\uff0c\u9053\u4eca\u6211\u53d1\u73b0\u4e86\u4e00\u6837\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053 \u201c\u751a\u9ebd\u4e8b\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4ed6\u81f3\u5c11\u5f88\u6709\u6740\u72d7\u7684\u5929\u4efd\u56e0\u4e3a\u4ed6\u81f3\u5c11\u6740\u4e86\u6761\u72d7\u3002\u201d\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u6211 \u4e5f\u53d1\u73b0\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4e0d\u662f\u5357\u5bcc\u4e11\u3002\u201d\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4e11\u3002\u201d\u738b\u52a8\u5ffd\u7136\u9053\u201c\u540d\u5b57\u53eb\u5357\u5bcc\u4e11\xb7\u4eba\u5e76\u4e0d\u2018\u5b9a\u5c31\u4f1a\u5f88\u4e11\u3002\u201d\u90ed\u5927\u8def\u7b11 \u9053\u5e7f\u4e0d\u9519\xb7\u5c31\u597d\u50cf\u540d\u5b57\u53eb\u738b\u52a8\u7684\u4eba\uff0c\u5e76\u4e0d\u5b9a\u559c\u6b22\u52a8\u3002\u201d\u738b\u52a8\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f46\u4ed6 \u80fa\u4e0a\u4e5f\u6ca1\u6709\u5200\u75a4\u3002\u201d\u6c5f\u6e56\u4e2d\u5f88\u591a\u4eba\u90fd\u77e5\u9053\u5357\u5bab\u4e11\u867d\u4fa5\u5e78\u81ea\u75af\u72c2\u5341\u5b57\u5251\u4e0b\u9003\u4e86\u6027\u547d\u8138\u4e0a\u5374\u8fd8\u662f \u88ab\u5212\u4e86\u4e2a\u5927\u5341\u5b57\u6240\u4ee5\u4ece\u4e0d\u613f\u4ee5\u771f\u9762\u76ee\u89c1\u4eba\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u8c01\u770b\u8fc7\u5357\u5bab\u4e11\u8138\u5e7f\u6709\u5200\u75a4\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u81f3\u5c11\u6211\u6ca1\u6709\u770b\u89c1\u8fc7\u3002\u201d\u738b\u52a8\u9053\u201c\u4ed6\u65e2 \u7136\u4ece\u4e0d\u4ee5\u771f\u9762\u76ee\u89c1\u4eba\xb7\u8c01\u80fd\u770b\u5230\u4ed6\u7684\u8138\uff1f\u201d\u7ff1\u5927\u8def\u7b11\u9053\u201c\u4e0d\u9519\u4e5f\u8bb8\u4ed6\u5200\u75a4\u5728\u5c41\u80a1\u4e0a\u3002\u201d\u7199\u8863 \u4eba\u4e00\u76f4\u5728\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u851a\xb7\u6b64\u523b\u5ffd\u7136\u9053\u201c\u4f60\u4eec\u53ea\u8bf4\u5bf9\u4e86\u4ef6\u4e8b\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u54ea\u6837\uff1f\u201d\u9ed1\u8863\u4eba \u9053\u201c\u6211\u4e0d\u6740\u4eba\xb7\u53ea\u6740\u72d7\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u539f\u6765\u4f60\u4e5f\u72e0\u5766\u767d\u3002\u201d\u9ed1\u8863\u4eba\u9053\u201c\u6211\u521a\u6740\u4e86\u6761\u4f60\u662f\u7b2c \u4e8c\u6761\u3002\u201d\u591c\u72e0\u8def\uff0c\u6b63\u662f\u4e2a\u6807\u51c6\u7684\u6708\u9ed1\u98ce\u9ad8\u6740\u4eba\u591c\u201d\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4eec\u5916\xb7\u8fd9\u5c71\u4e0a\u6d3b\u4eba\u672c\u5c31\u4e0d\u591a\u4eca\u5929\u665a\u4e0a\u4e5f\u8bb8\u53c8\u8981\u5c11\u4e00\u4e5f\u8bb8\u5c11\u56db\u4e2a\u3002 \u3000\u3000\u9662\u91cc\u6709\u6811\xb7\u98ce\u5728\u5439\u6811\u5728\u52a8\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5374\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\u4eff\u4f5b\u5df2\u7ecf\u548c\u8fd9\u6740\u4eba\u4e4b\u591c\u6eb6\u4e3a\u4f53\u3002 \u3000\u3000\u65e0\u8bba\u51c6\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u7684\u786e\u662f\u4e2a\u201c\u6740\u4eba\u201d\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7684\u786e\u8c61\u662f\u5e26\u7740\u79cd\u6740\u6c14\u3002 \u3000\u3000\u5251\u8fd8\u672a\u51fa\u8def\xb7\u6740\u6c14\u5374\u5df2\u51fa\u6a2a\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd8\u5728\u5c4b\u91cc\u6162\u6162\u7684\u8131\u8863\u670d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5c31\u5728\u5916\u9762\u7b49\u7740\u4eff\u4f5b\u70b9\u4e5f\u4e0d\u7740\u6025\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u9053\u201c\u8fd9\u4eba\u5012\u5f88\u6709\u8010\u5fc3\u3002\u201d\u738b\u52a8\u9053\u201c\u8981\u6740\u4eba\u5c31\u8981\u6709\u8010\u5fc3\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u8010\u5fc3 \u6740\u4e0d\u4eba\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u6545\u610f\u60f3\u8981\u4ed6\u7740\u6025\u4ed6\u4e0d\u6025\u4f60\u5c31\u6025\u4e86\uff0c\u4f60\u6025\xb7\u4ed6\u5c31\u6709\u673a\u4f1a\u6740\u4f60\u201c\u90ed\u5927\u8def\u7b11 \u4e86\u7b11\u9053\u201c\u6240\u4ee5\u6211\u4e5f\u4e0d\u6025\u3002\u201d\u71d5\u4e03\u4e00\u76f4\u5728\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\u201c\u4f60\u975e\u4f46\u4e0d\u5fc5\u6025\uff0c\u4e5f\u4e0d\u5fc5\u4e00\u4e2a\u4eba\u51fa\u90ed \u5927\u8def\u9053\u201c\u6211\u867d\u7136\u662f\u539a\u8138\u76ae\xb7\u5374\u4e0d\u662f\u80c6\u5c0f\u9b3c\u3002\u201d\u71d5\u4e03\u9053\u201c\u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u6211\u4eec\u672c\u4e0d\u5fc5\u8bb2\u4ec0\u9ebd\u6c5f\u6e56 \u9053\u4e49\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u60f3\u56db\u4e2a\u6253\u4e2a\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u884c\uff1f\u201d\u90ed\u5927\u8def\u963b\u4e86\u53e3\u6c14\u9053\u201c\u6211\u5012\u4e5f \u5f88\u60f3\u90a3\u9ebd\u6837\u505a\u53ea\u53ef\u501f\u6211\u662f\u4e2a\u7537\u71d5\u4e03\u5782\u4e0b\u5934\uff0c\u9053\u201c\u53ef\u662f\u4f60\u2026\u2026\u4f60\u6709\u6ca1\u6709\u628a\u63e1\u5bf9\u4ed8\u4ed6\uff1f\u201d\u90ed\u5927\u8def \u9053\u201c\u6ca1\u6709\u3002\u201d\u71d5\u4e03\u9053\u201c\u90a3\u9ebd\u4f60\u2026\u2026\u201d\u90ed\u5927\u8def\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u7b11\u9053\u201c\u6709\u628a\u63e1\u8981\u53bb\uff0c\u6ca1\u6709\u628a\u63e1\u4e5f\u8981 \u53bb\xb7\u5c31\u7b49\u65bc\u6709\u94b1\u8981\u559d\u9152\u6ca1\u6709\u94b1\u4e5f\u8981\u559d\u9152\u3002\u201d\u738b\u52a8\u7b11\u7b11\u9053\u201c\u8fd9\u6bd4\u55bb\u867d\u7136\u72d7\u5c41\u4e0d\u901a\uff0c\u5374\u8bf4\u660e\u4e86\u4e00 \u4ef6\u4e8b\u3002\u201d\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d\u738b\u52a8\u9053\u201c\u6709\u4e9b\u4e8b\u672c\u5c31\u662f\u975e\u505a\u4e0d\u53ef\u7684\u3002\u201d\u6797\u592a\u5e73\u5ffd\u7136\u9053\u201c\u597d\u4f60 \u53bb\uff0c\u4ed6\u82e5\u6740\u4e86\u4f60\u6211\u66ff\u4f60\u62a5\u4ec7\u3002\u201d\u90ed\u5927\u8def\u7b11\u4e86\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u7b11\u9053\u201c\u4f60\u867d\u7136\u662f\u4e2a\u6df7\u86cb\xb7\u4f46\u81f3\u5c11\u591f \u4e49\u6c14\u3002\u201d\u9893\u4e03\u5ffd\u53c8\u62c9\u4f4f\u4ed6\u7684\u624b\u6084\u6084\u9053\u201c\u7ad9\u5f97\u79bb\u4ed6\u8fdc\u4e00\u4e9b\u4ed6\u7684\u5228\u5e76\u4e0d\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u653e\u5fc3\u6211\u4e0d \u4f1a\u4e0a\u5f53\u7684\u3002\u201d\u4ed6\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6211\u771f\u4e0d\u6a2a\uff0c\u6709\u4e9b\u4eba\u4e3a\u4ec0\u9ebd\u603b\u662f\u786c\u8981\u5145\u82f1\u6444\u3002\u201d\u738b\u52a8\u6de1\u6de1\u9053\u201c\u4e5f\u8bb8\u4ed6 \u672c\u6765\u5c31\u662f\u82f1\u96c4\u6709\u4e9b\u4eba\u5929\u751f\u5c31\u662f\u82f1\u96c4\u3002\u201d\u6797\u592a\u5e73\u53f9\u9053\u4e0d\u9519\uff0c\u65e0\u8bba\u4ed6\u662f\u9152\u9b3c\u4e5f\u597d\uff0c\u662f\u6df7\u86cb\u4e5f\u597d\u4f46 \u5374\u7684\u7684\u786e\u786e\u662f\u4e2a\u82f1\u96c4\u4e0d\u6298\u4e0d\u6263\u7684\u5927\u82f1\u96c4\u3002\u201d\u71d5\u4e03\u53f9\u606f\u7740\u54fa\u8d3f\u9053\u201c\u53ef\u60dc\u82f1\u96c4\u5927\u591a\u90fd\u6b7b\u5f97\u65e9\u3002\u201d \u90ed\u5927\u8def\u4e5f\u5df2\u7ad9\u5728\u9662\u5b50\u91cc\u679c\u7136\u7ad9\u5f97\u79bb\u9ed1\u8863\u4eba\u5f88\u8fdc\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4f60\u7684\u5251\u5462\uff1f\u201d\u90ed\u5927\u8def\u7b11\u7b11\u9053\u201c\u6211\u7684\u5251\u5df2\u9001\u8fdb\u5f53\u94fa\u4e86\u3002\u201d\u9ed1\u8863\u4eba\u51b7\u7b11\u9053\u201c\u4f60\u6562 \u4ee5\u7a7a\u5bf9\u6211\uff1f\u662f\u4e0d\u662f\u8fd8\u6015\u6b7b\u5f97\u4e0d\u591f\u5feb\uff1f\u201d\u90ed\u5927\u8def\u53c8\u7b11\u7b11\u9053\u201c\u65e2\u7136\u8981\u6b7b\u5c31\u4e0d\u5982\u6b7b\u5f97\u5feb\u4e9b\u4e5f\u514d\u5f97\u6d3b \u7740\u7a77\u53d7\u7f6a\u53d7\u7a77\u7f6a\u3002\u201d\u9ed1\u8863\u4eba\u9053\u201c\u597d\u6211\u6210\u5168\u4f60\u3002\u201d\u8bf4\u5230\u201c\u597d\u201d\u5b57\u4ed6\u5df2\u53cd\u624b\u62d4\u5251\u3002 \u3000\u3000\u4ed6\u7684\u624b\u521a\u9ad3\u53ca\u5251\u67c4\uff0c\u90ed\u5927\u8def\u5df2\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u7684\u5fc3\u51e0\u4e4e\u773a\u51fa\u4e86\u8154\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u96be\u9053\u771f\u7684\u60f3\u5feb\u70b9\u6b7b\uff1f\u660e\u77e5\u5bf9\u65b9\u7528\u7684\u662f\u77ed\u5251\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u9001\u4e0a\u95e8\u53bb\uff1f\u5251\u5149\u4e00\u95ea\uff0c\u5228 \u5df2\u51fa\u9798\u3002 \u3000\u3000\u4e0d\u662f\u77ed\u5251\u662f\u957f\u5251\u3002 \u3000\u3000\u5251\u5149\u5982\u6f2b\u5929\u98de\u8679\u4eae\u5f97\u4ee4\u4eba\u773c\u82b1\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u90ed\u5927\u8def\u5df2\u51b2\u4eba\u4ed6\u6000\u91cc\u5df2\u770b\u4e0d\u5230\u8fd9\u67c4\u5251\u770b\u4e0d\u5230\u8fd9\u5228\u5149\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u4e5f\u6ca1\u6709\u82b1\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u770b\u5230\u9ed1\u8863\u4eba\u7684\u5251\u5374\u770b\u5230\u4e86\u9ed1\u8863\u4eba\u7684\u5f31\u70b9\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\xb7\u9ed1\u8863\u4eba\u8eab\u5b50\u98de\u51fa\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u5411\u5f8c\u98de\u51fa\xb7\u5228\u5149\u5374\u5411\u524d\u98de\u51fa\u8eab\u5b50\u649e\u4e0a\u5f8c\u9762\u7684\u5899\xb7\u957f\u5251\u9489\u4eba\u4e86\u524d\u9762\u7684\u6811\u3002 \u3000\u3000\u4ed6\u5012\u4e0b\u53bb\u5c31\u4e0d\u518d\u52a8\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ad9\u5728\u90a3\u91cc\u770b\u7740\u81ea\u5df1\u7684\u62f3\u5934\xb7\u4eff\u4f5b\u89c9\u5f97\u72e0\u63a0\u8bb6\uff0c\u5f88\u5947\u602a\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4eff\u4f5b\u4e5f\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u62f3\u5c31\u80fd\u5c06\u5bf9\u65b9\u6253\u5012\u3002 \u3000\u3000\u522b\u4eba\u4e5f\u6ca1\u6709\u60f3\u5230\u3002 \u3000\u3000\u71d5\u4e03\u66f4\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u6014\u4e86\u4e2d\u5929\uff0c\u624d\u51b2\u51fa\u53bb\u53c8\u60ca\u5947\uff0c\u53c8\u559c\u6b22\uff0c\u53c8\u5e26\u7740\u51e0\u5206\u60f6\u6050\uff0c\u7b11\u7740\u9053 \u201c\u6211\u53eb\u4f60\u79bb\u4ed6\u8fdc\u4e9b\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u504f\u504f\u8981\u51b2\u8fc7\u53bb\uff1f\u201d\u90ed\u5927\u8def\u7b11\u4e86\u9053\u201c\u4e5f\u56e0\u4e3a\u6211\u662f\u4e2a\u50bb\u5b50\u3002\u201d\u4ed6\u7684 \u7b11\u770b\u6765\u771f\u6709\u70b9\u50bb\u4e2d\u4e2d\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5f53\u7136\u70b9\u4e5f\u4e0d\u50bb\u4f60\u8ba4\u4e3a\u4ed6\u50bb\u7684\u65f6\u5019\u4ed6\u5374\u504f\u504f\u4f1a\u53d8\u5f97\u72e0\u806a\u660e\u800c\u5c4b\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u806a\u660e\u5f97 \u591a\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u8c01\u8bf4\u4f60\u50bb\u4e86\uff0c\u53ea\u4e0d\u8fc7\u6211\u5b9e\u5728\u4e0d\u503c\u4f60\u600e\u9ebd\u770b\u51fa\u4ed6\u8fd9\u6b21\u7528\u7684\u4e0d\u662f\u77ed\u5251\uff1f\u201d\u90ed\u5927\u8def \u7b11\u7b11\u9053\u201c\u6211\u6839\u672c\u6ca1\u6709\u770b\u51fa\u6765\u6211\u662f\u731c\u51fa\u6765\u7684\u3002\u201d\u71d5\u4e03\u6014\u4e86\u6014\uff0c\u9053\u201c\u82e5\u662f\u731c\u9519\u4e86\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053 \u201c\u6211\u4e0d\u4f1a\u731c\u9519\u3002\u201d\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u7b11\u563b\u5580\u9053\u201c\u56e0\u4e3a\u6211\u7684\u8fd0\u6c14\u597d\u3002\u201d\u9893\u4e03\u6014\u4e86\u534a \u664c\uff0c\u5ffd\u4e5f\u7b11\u4e86\xb7\u5927\u7b11\u9053\u201c\u4f60\u867d\u7136\u4e0d\u50bb\u4f46\u5374\u4e5f\u4e0d\u8001\u5b9e\uff0c\u70b9\u90fd\u4e0d\u8001\u5b9e\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u786e\u4e0d\u8001\u5b9e\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4f1a\u88c5\u50bb\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u5df2\u770b\u51fa\u9ed1\u8863\u4eba\u8fd9\u6b21\u7528\u7684\u4e0d\u662f\u77ed\u5251\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u6b27\u9ed1\u8863\u4eba\u7684\u5251\u67c4\u5728\u5de6\u80a9\uff0c\u5374\u7528\u53f3\u624b\u53bb\u62d4\u5251\uff0c\u62d4\u5251\u7684\u65f6\u5019\xb7\u80f8\u8179\u5411\u5f8c\u6536\u7f29\u529b\u91cf\u90fd\u653e \u5728\u524d\u9762\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u80f8\u819b\u548c\u5c0f\u8179\u4e4b\u95f4\u5c31\u6709\u4e86\u5f31\u70b9\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u51fa\u4e86\u8fd9\u5f31\u70b9\u3002 \u3000\u3000\u4ed6\u62f3\u5c31\u6253\u5728\u8fd9\u5f31\u70b9\u4e0a\u3002 \u3000\u3000\u53ea\u8981\u80fd\u770b\u5f97\u51c6\u80fd\u5224\u65ad\u6b63\u786e\u62f3\u5c31\u591f\u4e86\uff0c\u7528\u4e0d\u7740\u7b2c\u4e8c\u62f3\u3002 \u3000\u3000\u5546\u624b\u76f8\u4e89\u6700\u6709\u6548\u7684\u5c31\u662f\u8fd9\u7b2c\u62f3\u3002 \u3000\u3000\u8fd9\u62f3\u4f60\u82b3\u4e0d\u80fd\u6253\u5012\u522b\u4eba\u81ea\u5df1\u4e5f\u8bb8\u5c31\u4f1a\u88ab\u4eba\u6253\u56fe\u3002 \u3000\u3000\u80dc\u4e0e\u8d1f\u7684\u5206\u522b\u5f80\u5f80\u53ea\u4e0d\u8fc7\u5728\u4e00\u5f20\u4e4b\u95f4\u4e5f\u5f80\u5f80\u53ea\u4e0d\u8fc7\u5728\u4e00\u5ff5\u4e4b\u95f4\u3002\u71d5\u4e03\u5ffd\u53c8\u9053\uff0c\u671f\u8fd8\u6709\u4ef6 \u4e8b\u4e0d\u61c2\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4ed6\u7684\u624b\u6bd4\u5251\u77ed\u5f97\u591a\u4e3a\u4ec0\u9ebd\u4e00\u4f38\u624b\u5c31\u80fd\u5c06\u5251\u62d4\u51fa\u6765\uff1f\u201d \u90ed\u5927\u8def\u60f3\u4e86\u60f3\xb7\u7b11\u9053\u201c\u6211\u4e5f\u4e0d\u503c\u3002\u201d\u738b\u52a8\u9053\u201c\u6211\u503c\u3002\u201d\u4ed6\u8d70\u8fc7\u6765\u2606\u624b\u91cc\u62ff\u7740\u7684\u5c31\u662f\u9ed1\u8863\u4eba\u7684 \u5251\u9798\u3002 \u3000\u3000\u71d5\u4e03\u63a5\u8fc7\u5251\u9505\xb7\u770b\u4e86\u770b\u7b11\u9053\u201c\u6211\u4e5f\u61c2\u4e86\u3002\u201d\u65e0\u8bba\u8c01\u53ea\u8981\u770b\u8fc7\u8fd9\u5251\u671f\u90fd\u4f1a\u6a2a\u7684\u3002\u5251\u9798\u91cc\u672c \u6709\u4e24\u67c4\u67c4\u957f\u2026\u67c4\u77ed\u3002\u8fd9\u70b9\u71d5\u4e03\u4e5f\u5df2\u60f3\u5230\u3002 \u3000\u3000\u4ed6\u5374\u672a\u60f3\u5230\u8fd9\u5251\u8d3f\u6839\u672c\u4e0d\u662f\u771f\u6b63\u7684\u5251\u671f\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5939\u4e88\u3002 \u3000\u3000\u5251\u5e76\u4e0d\u662f\u4ece\u4e0a\u9762\u201c\u62d4\u201d\u51fa\u6765\u7684\u800c\u662f\u4ece\u65c1\u8fb9\u201c\u63a2\u201d\u51fa\u6765\u7684\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u8fd9\u5c31\u597d\u50cf\u9e21\u86cb\u6837\u3002\u201d\u90ed\u5927\u8def\u8bc1\u5382\u6014\u9053\u201c\u8c61\u9e21\u86cb\u201d\u71d5\u4e03\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8981\u7528\u4ec0 \u9ebd\u6cd5\u5b50\u624d\u80fd\u628a\u9e21\u86cb\u7ad9\u5728\u684c\u5b50\u535c\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d\u71d5\u4e03\u7b11\u9053\u201c\u5446\u5b50\u4f60\u53ea\u8981\u628a\u9e21\u86cb\u5927\u7684 \u90a3\u5934\u6572\u7834\uff0c\u8fd9\u9e21\u86cb\u5c82\u975e\u5c31\u80fd\u5730\u4f4d\u4e86\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u771f\u662f\u4e2a\u5929\u624d\u8fd9\u6cd5\u5b50\u771f\u4e8f\u4f60\u600e\u9ebd\u60f3\u5f97\u5230 \u7684\u3002\u201d\u4e16\u4e0a\u6709\u4e9b\u4e8b\u7684\u786e\u5c31\u8c61\u9e21\u86cb\u6837\u3002 \u3000\u3000\u4f60\u8ba4\u4e3a\u5f88\u590d\u6742\u7684\u4e8b\u5176\u5b9e\u5374\u5f80\u5f80\u5f88\u7b80\u5355\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u4e5f\u548c\u9e21\u86cb\u6837\u3002 \u3000\u3000\u65e0\u8bba\u591a\u6ca1\u7528\u7684\u4eba\u4f60\u53ea\u8981\u6572\u7834\u4ed6\u7684\u5934\u4ed6\u5c31\u80fd\u81ea\u5df1\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u9662\u5b50\u91cc\u591a\u5382\u4e2a\u575f\u3002 \u3000\u3000\u72d7\u575f\u3002 \u3000\u3000\u71d5\u4e03\u4eb2\u624b\u5c06\u90a3\u9ed1\u72d7\u88c5\u4eba\u68fa\u6750\u8def\u7136\u53f9\u606f\u7740\u9053\u201c\u4f60\u4ece\u68fa\u6750\u91cc\u6765\u73b0\u5728\u53c8\u5f80\u68fa\u6750\u91cc\u53bb\u4e86\uff0c\u65e9\u77e5\u5982 \u6b64\u4f60\u53c8\u4f55\u5fc5\u6765\u3002\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u4ed6\u82e5\u4e0d\u6765\xb7\u6211\u4eec\u5c31\u8981\u5f80\u68fa\u6750\u91cc\u53bb\u4e86\u3002\u201d\u6797\u592a\u5e73\u53f9\u9053\u201c\u5b83\u6765 \u7684\u65f6\u5019\u3002\u6211\u8fd8\u8e22\u4e86\u5b83\u4e00\u811a\xb7\u8c01\u77e5\u5b83\u5374\u6551\u4e86\u6211\u4eec\u7684\u547d\u3002\u201d\u738b\u52a8\u9053\u201c\u72d7\u4e0d\u50cf\u4eba\xb7\u72d7\u4e0d\u8bb0\u4ec7\uff0c\u53ea\u8bb0 \u5f97\u4f50\u522b\u4eba\u7684\u601d\u60e0\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u9519\u4f60\u53ea\u8981\u7ed9\u72d7\u5403\u8fc7\u5757\u9aa8\u5934\u5b83\u4e0b\u6b21\u89c1\u4e86\u4f60\u5b9a\u4f1a\u6447\u5c3e\u5df4\u4f46\u6709\u4e9b \u5fd8\u601d\u8d1f\u4e49\u7684\u4eba\u4f53\u65e0\u8bba\u7ed8\u8fc7\u4ed6\u591a\u5c11\u597d\u5904\u4ed6\u56de\u8fc7\u5934\u6765\u53cd\u800c\u4f1a\u54ac\u4f60\u53e3\u6240\u4ee5\u2026\u2026\u201d\u6797\u592a\u5e73\u63a5\u7740\u9053\u201c\u6240 \u4ee5\u72d7\u6bd4\u4eba\u8bb2\u4e49\u6c14\uff0c\u81f3\u5c11\u6bd4\u67d0\u4e9b\u4eba\u8bb2\u4e49\u6c14\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u6211\u4eec\u5e94\u8be5\u66ff\u5b83\u7acb\u4e2a\u7891\u3002\u201d\u6797\u592a\u5e73 \u9053\u201c\u7891\u4e0a\u5199\u4ec0\u9ebd\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e49\u72ac\u4e4b\u5893\u3002\u201d\u71d5\u4e03\u6447\u6447\u5934\xb7\u9053\u201c\u4e49\u72ac\u4e24\u4e2a\u5b87\u8fd8\u4e0d\u591f\u4f60\u83ab\u5fd8 \u8bb0\uff0c\u5b83\u4e5f\u662f\u6211\u4eec\u7684\u6551\u547d\u6069\u4eba\u2026\u2026\u201d\u738b\u52a8\u9053\u7891\u4e0d\u59a8\u5f8c\u53cb\u796d\u6587\u5374\u4e0d\u53ef\u4e0d\u5148\u9010\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u4f1a \u4f5c\u796d\u6587\uff1f\u201d\u90ed\u5927\u8def\u70b9\u70b9\u95ee\u5ffd\u7136\u7ad9\u8d77\u6765\xb7\u6717\u58f0\u9053\u201c\u68fa\u4e2d\u72d7\u601d\u670b\u4e49\u53cb\xb7\u4f60\u82e5\u4e0d\u6765\u6211\u4eec\u5df2\u8d70\uff0c\u521d\u5341 \u8fd1\u9999\u82b1\u5960\u9152\u545c\u547c\u54c0\u54c9\u2026\u2026\u3002\u201d\u732a\u4e0d\u80fd\u592a\u80a5\u4eba\u4e2d\u80fd\u592a\u806a\u660e\u3002 \u3000\u3000\u80a5\u732a\u603b\u662f\u5148\u6328\u5bb0\u4eba\u82e5\u8981\u6d3b\u5f97\u6109\u5feb\u4e9b\uff0c\u4e5f\u5f97\u5e26\u51e0\u5206\u50bb\u6c14\u505a\u51e0\u4ef6\u50bb\u4e8b\u3002 \u3000\u3000\u90a3\u5e76\u4e0d\u8868\u5c14\u4ed6\u4eec\u5c31\u662f\u50bb\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u77e5\u9053\u732b\u81ea\u5df1\u4e0d\u4f1a\u505a\u996d\u72d7\u4e5f\u4e0d\u4f1a\u66f0\u5df1\u5c06\u81ea\u5df1\u88c5\u8fdb\u68fa\u6750\u8fd9\u53ea\u732b\u548c\u8fd9\u6761\u72d7\u5b9a\u6709\u4e2a\u6587 \u4eba\u3002\u8fd9\u4eba\u8db3\u6dee\u5462\uff1f\u71d5\u4e03\u9053\u201c\u8fd9\u4eba\u5c06\u68fa\u6750\u9001\u6765\u7684\u65f6\u5019\u4e00\u5b9a\u5df2\u77e5\u9053\u5357\u5bab\u4e11\u5e76\u6ca1\u6709\u6b7b\u3002\u201d\u90ed\u5927\u8def\u9053 \u201c\u4e0d\u9519\u4ed6\u9001\u8fd9\u53e3\u68fa\u6750\u6765\u5c31\u662f\u8981\u544a\u8bc9\u6211\u4eec\u5357\u5bab\u4e11\u6ca1\u6709\u6b7b\u3002\u201d\u71d5\u4e03\u70b9\u70b9\u5934\u9053\u201c\u4ed6\u65e9\u5df3\u77e5\u9053\u4e86\u5357\u5bab \u536b\u7684\u9634\u8c0b\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u4ed6\u4e3a\u4ec0\u9ebd\u4e0d\u5bf9\u6211\u4eec\u8bf4\u660e\u767d\u5462\uff1f\u201d\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u4ed6\u4e0d\u60f3\u8ddf\u6211\u4eec\u89c1 \u9762\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u4ed6\u65e2\u7136\u6ca1\u6709\u6028\u610f\u505a\u4e8b\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u9b3c\u9b3c\u90ed\u5927\u8def\u9053\u201c\u6211\u770b\u8fd9\u4eba\u4e00\u5b9a \u662f\u4e2a\u5973\u4eba\u3002\u201d\u71d5\u4e03\u9053\u201c\u600e\u89c1\u5f97\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u53ea\u6709\u4ea5\u4eba\u624d\u4f1a\u505a\u8fd9\u4e9b\u9b3c\u9b3c\u795f\u795f\u3001\u83ab\u540d\u5176\u5999\u7684 \u4e8b\u3002\u201d\u71d5\u4e03\u677f\u7740\u8138\u9053\u201c\u5973\u4eba\u5c31\u7b97\u505a\u8fd9\u79cd\u4e8b\uff0c\u90a3\u4e5f\u53ea\u56e0\u4e3a\u7537\u4eba\u66f4\u83ab\u540d\u5176\u5999\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u83ab \u5fd8\u8bb0\u4f60\u4e5f\u662f\u7537\u4eba\u3002\u201d\u71d5\u4e03\u9053\u201c\u83ab\u5fd8\u8bb0\u4f60\u4e5f\u662f\u4ea5\u4eba\u751f\u51fa\u6765\u7684\u3002\u201d\u738b\u52a8\u770b\u7740\u71d5\u4e03\xb7\u5ffd\u7136\u9053\u201c\u7537\u4eba \u5929\u751f\u5c31\u770b\u4e0d\u8d77\u5973\u4eba\u5973\u4eba\u4e5f\u5929\u751f\u5c31\u770b\u4e0d\u8d77\u7537\u4eba\u8fd9\u672c\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u51e0\u5343\u767e\u5e74\u524d\u5982\u6b64\uff0c\u51e0\u5343\u767e\u5e74 \u5f8c\u5b9a\u8fd8\u662f\u8fd9\u6837\u6240\u4ee5\u2026\u4e00\u201d\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u600e\u9ebd\u6837\uff1f\u201d\u738b\u52a8\u9053\u201c\u6240\u4ee5\u8fd9\u79cd\u4e8b\u672c\u6ca1\u6709\u4ec0\u9ebd\u597d\u4e89\u8fa9\u7684 \u6211\u4e0d\u61c2\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u603b\u662f\u5bf9\u8fd9\u95ee\u9898\u7279\u522b\u6709\u5174\u8da3\u3002\u201d\u4ed6\u53f9\u4e86\u53e3\u6c14\u63a5\u7740\u9053\u201c\u6211\u7684\u95ee\u9898\u672c\u6765\u5df2\u591f\u591a \u4e86\xb7\u73b0\u5728\u4e49\u591a\u4e86\u90ed\u5927\u8def\u9053\u201c\u591a\u4e86\u4e2a\u4ec0\u9ebd\u95ee\u9898\u201d\u738b\u52a8\u9053\u201c\u5357\u5bab\u536b\u3002\u201d\u5357\u5bcc\u4e11\u5e76\u6ca1\u6709\u6b7b\u56e0\u4e3a\u6ca1\u6709 \u4eba\u613f\u610f\u6740\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u8c01\u90fd\u4e0d\u613f\u610f\u6740\u4eba\u5c24\u5176\u4e0d\u613f\u6740\u4e2a\u5df2\u88ab\u6253\u5012\u7684\u4eba\u3002\u5357\u5bcc\u53cc\u81f3\u5c11\u6709\u4ef6\u4e8b\u6ca1\u6709\u8bf4\u9519\u201c\u6709\u4e9b\u4eba \u5929\u751f\u5c31\u4e0d\u4f1a\u6740\u4eba\u800c\u4e14\u6c38\u8fdc\u90fd\u5b66\u4e0d\u4f1a\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u9519\u4ed6\u7684\u786e\u662f\u4e2a\u95ee\u9898\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u4ed6 \u4e0d\u662f\u5df2\u7ecf\u88ab\u6211\u4eec\u5173\u8d77\u6765\u4e86\u5f13\uff1f\u201d\u90ed\u5927\u8def\u9053\u662f\u7684\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u4f60\u6015\u4ed6\u4f1a\u9003\u8d70\uff1f\u201d\u90ed\u5927\u8def\u9053 \u201c\u4ed6\u9003\u4e0d\u4e86\u3002\u201d\u4e2a\u4eba\u82e5\u5df2\u88ab\u7ed1\u5f97\u8c61\u53ea\u8e2a\u5343\u8c01\u90fd\u4f11\u60f3\u80fd\u9003\u5f97\u5382\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u65e2\u7136\u9003\u4e0d\u4e86\uff0c\u8fd8\u6709\u4ec0\u9ebd\u95ee\u9898\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\u4ed6\u65e2\u7136\u9003\u4e0d\u4e86\u6211\u4eec \u5c31\u5f97\u770b\u7740\u4ed6\u662f\u4e0d\u662f\uff1f\u201d\u6797\u592a\u5e73\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u4eec\u8fde\u65e5\u90fd\u5feb\u517b\u4e0d\u6d3b\uff01\u600e\u5973\u517b\u5f97\u8d77\u522b\uff1f\u201d\uff01\u6797\u592a\u5e73\u7ec8\u4e8e\u660e\u767d\u4e86\u76b1\u7709\u9053 \u201c\u6211\u4eec\u4e0d\u5982\u653e\u4e86\u4ed6\u5427\u3002\u201d\u90ed\u5927\u8def\u8c0a\u201c\u8fd9\u79cd\u4eba\u4e5f\u653e\u4e0d\u5f97\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u90a3\u9ebd\u6211\u4eec\u96be\u9053\u8981\u517b\u4ed6\u4e00 \u8f88\u5b50\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u8fd9\u624d\u662f\u95ee\u9898\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u6211\u4eec\u53ef\u4ee5\u8981\u4ed6\u81ea\u5df1\u517b\u81ea\u5df1\u3002\u201d\u90ed\u5927\u8def \u963b\u775b\u7acb\u523b\u5c31\u4eae\u4e86\u8d77\u6765\u9053\u201c\u4e0d\u9519\u4ed6\u6bd4\u6211\u4eec\u6709\u94b1\u5f97\u591a\u3002\u201d\u71d5\u4e03\u9053\u201c\u81f3\u5c11\u4ed6\u521a\u4ece\u98ce\u6816\u68a7\u8eab\u4e0a\u635e\u4e86\u4e00 \u7968\u3002\u201d\u90ed\u5927\u8def\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\u201c\u6211\u8fd9\u5c31\u53bb\u95ee\u4ed6\xb7\u5c06\u90a3\u4e9b\u73e0\u5b9d\u85cf\u5728\u4ec0\u9ebd\u5730\u65b9\u4e86\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4f60\u95ee \u5f97\u51fa\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u867d\u4e0d\u662f\u5939\u68cd\uff0c\u4f46\u4e5f\u6709\u6211\u7684\u6cd5\u4e88\u3002\u201d\u71d5\u4e03\u5931\u7b11\u9053\u201c\u770b\u6765\u8fd9\u4e2a\u4eba\u5df3\u4ece\u592e \u68cd\u90a3\u91cc\u5b66\u4f1a\u51e0\u5957\u3002\u201d\u7537\u4eba\u548c\u732b\u5f8c\u56ed\u6709\u95f4\u67f4\u623f\u3002 \u3000\u3000\u67f4\u623f\u597d\u50cf\u5e76\u4e0d\u662f\u5806\u67f4\u7684\u800c\u662f\u5173\u4eba\u7684\u65e0\u8bba\u787c\u5bb6\u4eba\u6293\u4f4f\u4e86\u5f3a\u76d7\uff0c\u90fd\u4f1a\u5c06\u4ed6\u5173\u5728\u8363\u623f\u91cc\u3002 \u3000\u3000\u8fd9\u67f4\u623f\u91cc\u6709\u8718\u8eb2\u6709\u8001\u9f20\u6709\u72d7\u5c4e\u732b\u5c3f\xb7\u6709\u6c72\u9505\u7834\u7897\uff0c\u6709\u7528\u5269\u4e0b\u7684\u7164\u5c51\u3002\u2026\u51e0\u4e4e\u4ec0\u9ebd\u90fd\u6709\u5c31 \u662f\u6ca1\u6709\u67f4\u8fde\u6839\u67f4\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e5f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u88ab\u7ed1\u5f97\u8ddf\u8e2a\u5b50\u6837\u7684\u5357\u5bab\u4e11\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5730\u4e0a\u53ea\u4e58\u4e0b\u5806\u7ef3\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u53d1\u4e86\u534a\u5929\u6014\u62fe\u8d77\u6839\u65ad\u7ef3\u5b50\u770b\u4e86\u770b\u9053\u201c\u8fd9\u662f\u88ab\u5200\u5272\u65ad\u7684\u3002\u201d\u71d5\u4e03\u9053\u201c\u800c\u4e14\u662f\u628a\u5feb \u5200\u3002\u201d\u53ea\u6709\u5feb\u5200\u5272\u65ad\u7684\u7ef3\u5b50\xb7\u5207\u53e3\u624d\u4f1a\u5982\u6b64\u6574\u9f50\u3002 \u3000\u3000\u6797\u592a\u5e73\u76b1\u7709\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u4ed6\u5e76\u4e0d\u662f\u81ea\u5df1\u9009\u8d70\u7684\xb7\u4e00\u5b9a\u6709\u4eba\u6765\u6551\u4ed6\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211 \u5b9e\u5728\u60f3\u4e0d\u5230\u8fde\u8fd9\u79cd\u5165\u4e5f\u4f1a\u6709\u670b\u53cb\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f1a\u4e0d\u4f1a\u662f\u90a3\u4e24\u4e2a\u5c0f\u9b3c\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u4f1a\xb7\u4ed6 \u4eec\u65e2\u6ca1\u6709\u8fd9\u9ebd\u4eba\u4e2d\u4e8b\u4e14\u6ca1\u6709\u8fd9\u2606\u5927\u80c6\u6237\u800c\u4e14\u2026\u2026\u201d\u4ed6\u5ffd\u53c8\u7b11\u7b11\uff0c\u9053\u201c\u5c0f\u5b69\u5b50\u6709\u7684\u5730\u65b9\xb7\u5c31\u8ddf \u5973\u4eba\u6837\u2019\u85cf\u4e03\u9053\u201c\u54ea\u70b9\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u5c0f\u5b69\u5b50\u90fd\u4e0d\u4f1a\u5f88\u8bb2\u4e49\u6c14\u2026\u2500\u4ed6\u4eec\u6839\u672c\u4e2a\u5eb7\uff0c\u2026\u71d5\u4e03\u77aa \u4e86\u4ed6\u4e00\u773c\uff0c\u6797\u592a\u5e73\u5df2\u62a2\u7740\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u91d1\u6bdb\u72ee\u5b50\u72d7\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u60f3\u8d77\u4ed6\u7684\u201d\uff1f\u6797 \u592a\u5e73\u9053\u201c\u6211\u90a3\u5929\u5e76\u6ca1\u6709\u770b\u5230\u91d1\u6bdb\u72ee\u5b50\u72d7\xb7\u4e5f\u8bb8\u5357\u5bcc\u4e11\u5df2\u5c06\u4ed6\u653e\u4e86\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u6839\u672c\u5c31\u662f\u4e32\u901a\u597d \u4e86\u7684\u3002\u201d\u90ed\u5927\u8def\u64ad\u64ad\u5934\u9053\u201c\u5357\u5bab\u4e11\u8fd9\u79cd\u4eba\u5c31\u7b97\u4ec0\u9ebd\u4e8b\u90fd\u505a\u5f97\u51fa\u4f46\u81f3\u5c11\u6709\u4ef6\u4e8b\u662f\u7edd\u4e0d\u4f1a\u505a \u7684\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u7530\u4ef6\u4e8b\uff1f\uff1f\u90ed\u5927\u8def\u9053\u201c\u4ed6\u7edd\u4e0d\u4f1a\u7559\u7740\u522b\u4eba\u613f\u4ed6\u5206\u8d43\u3002\u201d\u4ed6\u7b11\u4e86\u7b11\uff0c\u53c8\u89e3\u91ca \u7740\u9053\u201c\u684c\u4e8e\u4e0a\u82e5\u6709\u53c1\u7897\u996d\u4ed6\u5c31\u7b97\u5c79\u4e0d\u4e0b\uff0c\u4e5f\u4e0d\u4f1a\u7559\u4e0b\u7897\u5206\u7ed9\u522b\u4eba\u4ed6\u5c31\u7b97\u80c0\u6b7b\u4e5f\u5168\u90fd\u8981\u5403\u4e0b \u53bb\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u4f60\u8ba4\u4e3a\u68cd\u5b50\u548c\u91d1\u6bdb\u72ee\u5b50\u72d7\u90fd\u5df2\u88ab\u4ed6\u6740\u4e86\uff1f\u201d\u90ed\u5927\u8def\u70b9\u70b9\u5934\u9053\u201c\u6211\u997f\u4e86\u3002\u201d \u8fd9\u53e5\u8bdd\u4e0e\u4ed6\u4eec\u73b0\u5728\u8c08\u8bba\u7740\u7684\u4e8b\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u8fde\u70b9\u5173\u7cfb\u90fd\u6ca1\u4f60\u7b80\u76f4\u65e0\u6cd5\u60f3\u8c61\u4e2a\u4eba\u4f1a\u5728\u8fd9\u79cd\u65f6\u5019 \u5ffd\u7136\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u6797\u592a\u5e73\u770b\u7740\u4ed6\u773c\u775b\u5f20\u5f97\u5f88\u5927\u3002\u5de5\u52a8\u548c\u71d5\u4e03\u4e5f\u5728\u770b\u7740\u4ed6\u597d\u50cf\u90fd\u60f3\u7814\u7a76\u8fd9\u4e2a\u4eba\u6784\u9020\u662f\u4e0d\u662f\u548c \u522b\u4eba\u4e0d\u540c\uff1f\u90ed\u5927\u8def\u7b11\u7b11\u53c8\u9053\u201c\u6211\u8bf4\u5230\u53c1\u7897\u996d\u7684\u65f6\u5019\u5c31\u5df2\u53d1\u89c9\u997f\u4e86\u8bf4\u5230\u5403\u7684\u65f6\u5019\u5c31\u5df2\u60f3\u5230\u4f60\u4eec \u81f3\u5c11\u5df4\u6709\u5927\u534a\u5927\u6ca1\u5403\u4e1c\u897f\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u8bf4\u5230\u4ec0\u9ebd\u7684\u65f6\u5019\u5c31\u4f1a\u60f3\u5230\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u597d\u50cf \u662f\u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u8bf4\u5230\u72d7\u5c4e\u7684\u65f6\u5019\u96be\u9053\u5c31\u4f1a\u60f3\u5230\u2500\u2026\u201d\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u90ed\u5927\u8def\u5ffd\u7136\u8f6c\u8eab \u8dd1\u4e01\u5c71\u5360\u3002 \u3000\u3000\u5f80\u5395\u6240\u90a3\u8fb9\u8dd1\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u738b\u52a8\u770b\u7740\u770b\u5f97\u773c\u964c\u53d1\u76f4\u597d\u50cf\u5df2\u770b\u5446\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u53c8\u5fcd\u4e0d\u4f4f\u7b11\u9053\u9f99\u4eba\u5b9e\u5728\u662f\u4e2a\u5929\u624d\u3002\u201d\u6797\u592a\u5e73\u7b11\u9053\u201c\u8fd9\u6837\u7684\u5929\u624d\u4e16\u4e0a\u4e5f \u8bb8\u8fd8\u4e0d\u591a\u3002\u201d\u71d5\u4e03\u9053\u201c\u975e\u4f46\u4e0d\u591a\u6050\u6015\u53ea\u6709\u8fd9\u9ebd\u6837\u4e2a\u3002\u201d\u571f\u52a8\u7ec8\u4e8e\u4e5f\u56db\u201c\u53e3\u6c14\u9053\u201c\u5e78\u597d\u53ea\u6709 \u4e2a\u3002\u201d\u8fd9\u4ea6\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u8c61\u90ed\u5927\u8def\u8fd9\u79cd\u4eba\u82e5\u662f\u591a\u6709\u51e0\u4e2a\u8fd9\u4e16\u754c\u4e5f\u8bb8\u5c31\u4f1a\u53d8\u5f97\u66f4\u5feb\u4e50\u3002\u201d\u52a8\u7269\u4e2d\u548c\u4eba\u6700\u4eb2\u8fd1\u7684\u4e5f\u8bb8 \u5c31\u662f\u732b\u548c\u72d7\u3002\u6709\u4e9b\u4eba\u559c\u6b22\u517b\u72d7\u6709\u4e9b\u4eba\u8ba4\u4e3a\u517b\u732b\u548c\u517b\u72d7\u5e76\u6ca1\u6709\u4ec0\u9ebd\u5206\u522b\u3002 \u3000\u3000\u5176\u5b9e\u5b83\u4eec\u5f88\u6709\u5206\u522b\u3002 \u3000\u3000\u732b\u4e0d\u8c61\u72d7\u6837\u4e0d\u559c\u6b22\u51fa\u53bb\xb7\u4e0d\u559c\u6b22\u5728\u5916\u9762\u4e71\u8dd1\u3002 \u3000\u3000\u732b\u559c\u6b22\u803d\u5728\u5bb6\u91cc\uff0c\u6700\u591a\u662f\u803d\u5728\u706b\u7089\u65c1\u3002 \u3000\u3000\u730e\u559c\u6b22\u5403\u9c7c\u5c24\u5176\u559c\u6b22\u5403\u9c7c\u5934\u3002 \u3000\u3000\u732b\u4e5f\u559c\u6b22\u989d\u5728\u4eba\u7684\u6000\u91cc\uff0c\u559c\u6b22\u4eba\u8f7b\u8f7b\u6478\u5b83\u7684\u8116\u5b50\u548c\u8033\u6735\u3002 \u3000\u3000\u4f60\u6bcf\u5929\u82e5\u662f\u6309\u65f6\u6687\u5b83\xb7\u5e38\u5e38\u5c06\u5b83\u62b1\u5728\u6000\u91cc\u8f7b\u8f7b\u7684\u629a\u6478\u5b83\u5b83\u5b9a\u5c31\u4f1a\u62a5\u559c\u6b22\u4f60\xb7\u4f5c\u4f60\u7684\u597d\u670b \u53cb\u3002 \u3000\u3000\u4f46\u4f60\u5343\u4e07\u83ab\u8981\u4ee5\u4e3a\u5b83\u53ea\u559c\u6b22\u4f60\u4e2a\u4eba\xb7\u53ea\u5c5e\u65bc\u4f60\u4e2a\u4eba\u3002 \u3000\u3000\u72ec\u7edd\u4e0d\u8c61\u72d7\u90a3\u9ebd\u5fe0\u5b9e\u4f60\u76d8\u4e8e\u91cc\u82e5\u6ca1\u6709\u8272\u7684\u65f6\u5019\xb7\u5b83\u5f80\u5f80\u5c31\u4f1a\u6e9c\u5230\u522b\u4eba\u5bb6\u91cc\u53bb\u800c\u4e14\u5f88\u5feb\u5c31 \u4f1a\u53d8\u6210\u90a3\u4e2a\u4eba\u7684\u670b\u53cb\u3002 \u3000\u3000\u4f60\u4e0b\u6b21\u89c1\u7740\u5b83\u7684\u65f6\u5019\u5b83\u4e5f\u8bb8\u5df2\u4e0d\u8ba4\u5f97\u4f60\uff0c\u5df2\u5c06\u4f60\u5fd8\u4e86\u3002 \u3000\u3000\u732b\u770b\u6765\u5f53\u7136\u6ca1\u6709\u72d7\u90a3\u9ebd\u51f6\u5374\u6bd4\u72d7\u6b8b\u5fcd\u5f97\u591a\u5b83\u6349\u4f50\u53ea\u8001\u9f20\u7684\u65f6\u5019\u5c31\u7b97\u809a\u5b50\u5f88\u997f\u4e5f\u7edd\u4e0d\u4f1a\u5c06 \u8fd9\u8001\u9f20\u53e3\u541e\u4e0b\u53bb\u3002 \u3000\u3000\u5b83\u5b9a\u8981\u5148\u5c06\u8fd9\u8001\u9f20\u800d\u5f97\u6655\u5934\u8f6c\u5411\xb7\u624d\u9992\u9992\u4eab\u53d7\u3002 \u3000\u3000\u732b\u7684\u201c\u624b\u811a\u201d\u5f88\u8f6f\xb7\u5b9a\u8d77\u8def\u6765\u70b9\u58f0\u97f3\u4e5f\u6ca1\u6709\xb7\u4f46\u4f60\u82e5\u60f9\u4e86\u5b83\uff0c\u5b83\u90a3\u8f6f\u8f6f\u7684\u201c\u624b\u201d\u91cc\u5c31\u4f1a \u7a81\u7136\u9732\u51fa\u5c16\u9510\u7684\u722a\u5b50\u6765\u6293\u5f97\u4f60\u5934\u7834\u8840\u6d41\u3002 \u3000\u3000\u730e\u82e5\u4e0d\u8c61\u72d7\u8c61\u4ec0\u9ebd\u5462\uff1f\u4f60\u6709\u6ca1\u6709\u770b\u8fc7\u5973\u4eba\uff1f\u6709\u6ca1\u6709\u770b\u8fc7\u5973\u4eba\u5403\u9c7c\uff1f\u6709\u6ca1\u6709\u770b\u8fc7\u5973\u4eba\u8eba\u5728 \u4e08\u592b\u548c\u60c5\u4eba\u6000\u91cc\u7684\u65f6\u5019\u4f60\u77e5\u4e0d\u77e5\u9053\u6709\u5f88\u591a\u7537\u4eba\u7684\u8138\u4e0a\u662f\u88ab\u8c01\u6293\u7834\u7684\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u6709\u4e9b\u7537\u4eba\u4e3a \u4ec0\u9ebd\u4f1a\u81ea\u6740\uff1f\u4f1a\u53d1\u75af\u4f60\u82e5\u8bf4\u732b\u8c61\u5973\u4eba\u4f60\u5c31\u9519\u4e86\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u732b\u5e76\u4e0d\u8c61\u5973\u4eba\u53ea\u4e0d\u8fc7\u6709\u5f88\u591a\u4ea5\u4eba\u7684\u786e\u72e0\u8c61\u732b\u3002 \u3000\u3000\u8fd9\u53ea\u732b\u662f\u9ed1\u7684\uff0c\u6cb9\u5149\u6c34\u6ed1\u9ed1\u5f97\u53d1\u4eae\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b63\u5728\u4ed4\u7ec6\u7814\u7a76\u7740\u8fd9\u53ea\u732b\u3002 \u3000\u3000\u4e00\u4e2a\u997f\u5f97\u53d1\u660f\u7684\u4eba\u662f\u7edd\u6ca1\u6709\u5174\u8da3\u7814\u7a76\u732b\u7684\u3002\u4e00\u4e2a\u997f\u5f97\u53d1\u660f\u7684\u4eba\u6839\u672c\u5c31\u6ca1\u6709\u5174\u8da3\u7814\u7a76\u4efb\u4f55 \u4e1c\u897f\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u5df2\u5403\u9971\u4e86\u3002\u5c31\u8c61\u6628\u5929\u65e9\u4e0a\u4e00\u6837\u996d\u83dc\u53c8\u6446\u5728\u684c\u5b50\u4e0a\u7684\u65f6\u5019\u4ed6\u4eec\u5c31\u542c\u89c1\u8fd9\u53ea\u732b\u5728 \u6447\u94c3\u3002\u90ed\u5927\u8def\u5ffd\u7136\u9053\u201c\u8fd9\u53ea\u732b\u5403\u5f97\u5f88\u9971\u3002\u800c\u4e14\u76f4\u90fd\u5403\u5f97\u5f88\u9971\xb7\u5e38\u5e38\u6328\u997f\u7684\u732b\u7edd\u4e0d\u4f1a\u957f\u5f97\u8c61\u8fd9 \u4e2a\u6837\u5b50\u3002\u201d\u71d5\u4e03\u7b11\u4e86\u95ee\u9053\u201c\u4f60\u7814\u7a76\u4e86\u534a\u5929\xb7\u5c31\u5728\u7814\u7a76\u8fd9\u4ef6\u4e8b\uff1f\u201d\u90ed\u5927\u8def\u7406\u4e5f\u4e0d\u7406\u4ed6\xb7\u53c8\u9053 \u201c\u5047\u5982\u8bf4\u8fd9\u4e9b\u5bb6\u5177\uff0c\u8fd9\u4e9b\u9152\u83b1\u548c\u90a3\u53e3\u68fa\u6750\u90fd\u662f\u4e2a\u53eb\u597d\u597d\u5148\u751f\u7684\u4eba\u9001\u6765\u7684\u6708\u53e3\u9ebd\u8fd9\u53ea\u732b\u5b9a\u4e5f\u662f \u4ed6\u517b\u7684\u6240\u4ee5\u2026\u2026\u201d\u85cf\u4e03\u9053\u6240\u4ee5\u600e\u9ebd\u6837\u201d\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u90a3\u597d\u597d\u5148\u751f\u5bb6\u91cc\u4e00\u5b9a\u72e0\u8212\u670d\u3001\u5f88\u9614 \u6c14\uff0c\u5426\u5219\u8fd9\u53ea\u732b\u5c31\u7edd\u4e0d\u4f1a\u88ab\u517b\u5f97\u8fd9\u9ebd\u80a5\u3001\u8fd9\u9ebd\u7261\u3002\u201d\u846b\u4e03\u7728\u7728\u773c\u9053\u201c\u90a3\u53c8\u600e\u9ebd\u6837\u5462\uff1f\u201d\u90ed\u5927 \u8def\u8bf4\u201c\u6211\u82e5\u662f\u732b\xb7\u6709\u4e2a\u8fd9\u9ebd\u9614\u6c14\u7684\u4e3b\u4eba\xb7\u5c31\u7edd\u5bf9\u4e0d\u80af\u613f\u522b\u4eba\u8d70\u7684\u3002\u201d\u9893\u4e03\u9053\u201c\u6240\u4ee5\u2026\u3002\u201c\u90ed \u5927\u8def\u9053\u201c\u6240\u4ee5\u6211\u4eec\u82b3\u5c06\u8fd9\u53ea\u732b\u653e\u4e86\uff0c\u5b83\u4e00\u5b9a\u5f88\u5feb\u5c31\u4f1a\u56de\u5230\u4e3b\u4eba\u90a3\u91cc\u53bb\u3002\u201d\u71d5\u4e03\u9650\u775b\u4eae\u4e86\xb7\u9053 \u201c\u90a3\u9ebd\u4f60\u8fd8\u62b1\u7740\u5b83\u5e72\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u62cd\u4e86\u62cd\u732b\u7684\u8116\u5b50\uff0c\u7b11\u9053\u201c\u732b\u5144\u6eaf\u5144\u4f60\u82e5\u80fd\u5e26\u6211\u4eec\u627e\u5230\u4f60 \u7684\u4e3b\u4eba\uff0c\u6211\u4e00\u5b9a\u5929\u5929\u8bf7\u4f60\u5403\u9c7c\u5934\u3002\u201d\u4ed6\u6548\u5f00\u624b\u628a\u732b\u9001\u51fa\u95e8\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u53ea\u732b\u201c\u772f\u545c\u201d\u58f0\u53c8\u8df3\u5230\u4ed6\u8eab\u4e0a\u6765\u4e86\u800c\u4e14\u4f38\u51fa\u820c\u5934\u8f7b\u8f7b\u8214\u4ed6\u7684\u624b\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u770b\u6765\u8fd9\u6761\u732b\u5b9a\u662f\u6bcd\u7684\u800c\u4e14\u5df2\u7ecf\u770b\u4e0a\u4e01\u4f60\u3002\u201d\u90ed\u5927\u8def\u62ce\u8d77\u732b\u7684\u8116\u5b50\u653e\u4e0b\u3002 \u3000\u3000\u732b\u8fd8\u662f\u56f4\u7740\u4ed6\u6253\u8f6c\u3002 \u3000\u3000\u90ed\u5927\u8def\u76b1\u7709\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8d70\uff1f\u96be\u9053\u4e0d\u60f3\u4f60\u7684\u4e3b\u4eba\uff1f\u4ed6\u5bf9\u4f60\u5411\u4e0d\u9519\u5440\u3002\u201d\u738b\u52a8\u5ffd\u7136\u7b11\u4e86 \u7b11\xb7\u9053\u201c\u732b\u7684\u8bb0\u5fc6\u867d\u7136\u4e0d\u597d\u8111\u7b4b\u5374\u5f88\u6e05\u695a\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u8111\u7b4b\u6e05\u695a\uff1f\u201d\u738b\u52a8\u9053\u201c\u5b83\u65e2\u7136\u77e5\u9053 \u8fd9\u91cc\u6709\u8272\u5403\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u8dd1\u5230\u522b\u7684\u5730\u65b9\u90ed\u5927\u8def\u9053\u201c\u4f46\u6211\u53c8\u4e0d\u662f\u5b83\u7684\u4e3b\u4eba\uff0c\u5b83\u4e3a\u4ec0\u9ebd\u8981\u7f20\u4f50 \u6211\uff1f\u201d\u738b\u52a8\u9053\u201c\u4f60\u521a\u5582\u5b83\u5403\u8fc7\u4e00\u6761\u9c7c\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u8c01\u5582\u5b83\u5403\u9c7c\u6dee\u5c31\u662f\u5b83\u7684\u4e3b\u4eba\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u95fb\u7530\u5730\u8c0a\u201c\u770b\u6765\u8fd9\u7684\u786e\u662f\u6761 \u6bcd\u732b\u3002\u201d\u6797\u592a\u5e73\u5ffd\u7136\u9053\u201c\u8fd9\u91cc\u82e5\u6ca1\u6709\u9c7c\u5403\u5462\uff1f\u201d\u738b\u52a8\u9053\u201c\u90a3\u9ebd\u5b83\u4e5f\u625b\u5c31\u4f1a\u56de\u53bb\u4e86\u3002\u201d\u6797\u592a\u5e73 \u7b11\u9053\u201c\u6211\u53ea\u5e0c\u671b\u8fd9\u6761\u7784\u4e5f\u8ba4\u5f97\u8def\u7684\u3002\u201d\u732b\u7684\u786e\u8ba4\u5f97\u8def\u3002 \u3000\u3000\u5b83\u82e5\u5728\u5916\u9762\u627e\u4e0d\u5230\u4e1c\u897f\u5403\xb7\u65e0\u8bba\u5b83\u5728\u7530\u91cc\uff0c\u90fd\u5b9a\u5f88\u6289\u5c31\u80fd\u627e\u5f97\u5230\u8def\u56de\u5bb6\u3002 \u3000\u3000\u4ece\u65e9\u4e0a\u5230\u4e0b\u5348\u90fd\u6ca1\u6709\u4e1c\u897f\u5403\uff0c\u65e0\u8bba\u662f\u4eba\u662f\u732b\xb7\u90fd\u4f1a\u997f\u5f97\u53d7\u4e0d\u4e86\u7f51\uff0c\u73b0\u5728\u90ed\u5927\u8def\u5c31\u7b97\u8fd8\u60f3 \u62b1\u7740\u8fd9\u6761\u732b\uff0c\u7784\u4e5f\u4e0d\u80af\u8ba9\u4ed6\u62b1\u4e86\u3002 \u3000\u3000\u5b83\u4e00\u6e9c\u59fb\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u5f8c\u9762\u8ddf\u7740\u3002 \u3000\u3000\u71d5\u4e03\u8ddf\u7740\u90ed\u5927\u8def\u6797\u592a\u5e73\u8ddf\u7740\u71d5\u4e03\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4eec\u6700\u597d\u4e0d\u8981\u8ddf\u5f97\u592a\u8fd1\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u4f60\u5462\u201d\uff1f\u738b\u52a8\u6ca1\u6709\u8bf4\u8bdd\u53ea\u53f9\u4e86\u53e3\u6c14\u4eff\u4f5b \u89c9\u5f97\u6797\u592a\u5e73\u8fd9\u53e5\u8bdd\u95ee\u5f97\u5f88\u611a\u56f4\u3002 \u3000\u3000\u4ed6\u8eba\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c71\u5761\u7684\u5de6\u9762\u662f\u5927\u7247\u8352\u575f\u5c31\u7b97\u5728\u6e05\u660e\u65f6\u8282\u8fd9\u91cc\u4e5f\u5f88\u5c11\u6709\u4eba\u6765\u626b\u5893\u7684\u57cb\u846c\u5728\u8fd9\u91cc\u7684\u4eba\u6d3b\u7740\u65f6 \u5c31\u5e76\u4e0d\u53d7\u4eba\u6ce8\u610f\xb7\u6b7b\u4e86\u5f8c\u66f4\u5f88\u5feb\u5c31\u88ab\u4eba\u9057\u5fd8\u3002 \u3000\u3000\u7a77\u4eba\u7684\u4eb2\u621a\u670b\u53cb\u672c\u4e0d\u4f1a\u591a\u4f55\u51b5\u662f\u4e2a\u6b7b\u4e86\u7684\u7a77\u4eba\u201d\u90ed\u5927\u8def\u65f6\u5e38\u89c9\u5f97\u5f88\u611f\u6168\uff0c\u6bcf\u6b21\u5230\u8fd9\u91cc\u6765 \u90fd\u4f1a\u89c9\u5f97\u6709\u5f88\u591a\u611f\u6168\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5374\u6ca1\u6709\u65f6\u95f4\u8ba9\u4ed6\u611f\u6168\u3002 \u3000\u3000\u90a3\u6761\u732b\u8dd1\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u5b83\u5f88\u5feb\u7684\u7a9c\u4eba\u575f\u573a\u53c8\u7a9c\u51fa\u53bb\u8fdc\u8fdc\u770b\u6765\u5c31\u8c61\u662f\u4e00\u80a1\u9ed1\u59fb\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u8981\u8ffd\u4e0a\u6761\u732b\uff0c\u90fd\u4e0d\u662f\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u4f60\u9664\u5382\u4e13\u5fc3\u53bb\u8ffd\u5b83\u4e4b\u5916\uff0c\u6839\u672c\u5c31\u6ca1\u5de5\u592b\u53bb\u60f3\u522b \u7684\u4e8b\u3002 \u3000\u3000\u8ffd\u5973\u4eba\u7684\u65f6\u5019\u4e5f\u6837\u3002 \u3000\u3000\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u6ca1\u5de5\u592b\u53bb\u60f3\uff0c\u6240\u4ee5\u624d\u4f1a\u53bb\u8ffd\u3002 \u3000\u3000\u82e5\u662f\u4ed4\u7ec6\u60f3\u60f3\u4e5f\u8bb8\u5c31\u4f1a\u7acb\u523b\u56de\u5934\u4e86\u3002 \u3000\u3000\u575f\u573a\u65c1\u8fb9\xb7\u6709\u7247\u6811\u6797\u3002 \u3000\u3000\u6811\u6797\u91cc\u6709\u95f4\u5c0f\u6728\u5c4b\u3002 \u3000\u3000\u8fd9\u662f\u5bc6\u6797\u6728\u5c4b\u5c31\u662f\u7528\u67a3\u6728\u677f\u642d\u6210\u7684\u90ed\u5927\u8def\u4ee5\u524d\u4e5f\u66fe\u5230\u8fd9\u67a3\u6797\u91cc\u6765\u901b\u8fc7\u5374\u6ca1\u6709\u770b\u5230\u8fc7\u8fd9\u5c0f \u6728\u5c4b\u3002 \u3000\u3000\u6728\u5c4b\u597d\u50cf\u662f\u8fd9\u4e24\u5929\u624d\u642d\u6210\u7684\u3002 \u3000\u3000\u732b\u7a9c\u4eba\u6811\u6797\xb7\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002\u5374\u6709\u9635\u9635\u9999\u6c14\u4ece\u6728\u5c4b\u91cc\u4f20\u51fa\u6765\u3002 \u3000\u3000\u662f\u7ea2\u6320\u8089\u7684\u5473\u9053\u3002 \u3000\u3000\u90ed\u5927\u8def\u8038\u4e86\u8038\u9f3b\u5b50\uff0c\u8138\u4e0a\u9732\u51fa\u5fae\u7b11\u3002 \u3000\u3000\u6728\u5c4b\u91cc\u5347\u7740\u706b\uff0c\u706b\u4e0a\u5a49\u7740\u8089\u3002 \u3000\u3000\u4e2a\u8001\u5934\u5b50\u8e72\u5728\u5730\u4e0a\u6247\u706b\uff0c\u4e2a\u8005\u592a\u5a46\u6b63\u5f80\u9505\u91cc\u5012\u9171\u3002 \u3000\u3000\u8fd8\u6709\u4e2a\u5934\u53d1\u957f\u957f\u7684\u5973\u4eba\u4e00\u76f4\u8e72\u5728\u65c1\u8fb9\u4e0d\u505c\u7684\u50ac\u4ed6\u4eec\u3002 \u3000\u3000\u8fd9\u53ea\u732b\u5ba1\u8fdb\u5c4b\u5b50\u5c31\u7a9c\u5165\u5979\u6000\u91cc\u3002 \u3000\u3000\u5979\u663e\u7136\u5c31\u662f\u8fd9\u53ea\u732b\u7684\u4e3b\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u627e\u5230\u4e86\u4ed6\u8981\u627e\u7684\u4eba\u3002\u4ed6\u8ffd\u5230\u95e8\u53e3\u7684\u65f6\u5019\u5979\u521a\u597d\u56de\u8fc7\u5934\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u76ee\u5149\u76f8\u9047\u90fd\u545b\u5382\u60ca\u3002 \u3000\u3000\u7136\u5f8c\u90ed\u5927\u8def\u5c31\u53eb\u4e86\u8d77\u6765\u201c\u9178\u6885\u6c64\u539f\u6765\u662f\u4f60\uff1f\u201d \u7b2c\u56db\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u56db\u7ae0 \u3000\u3000\u7ea2\u70e7\u8089\u5a49\u70c2\u5207\u5f97\u56db\u56db\u65b9\u65b9\u7684\u6bcf\u5757\u81f3\u5c11\u6709\u56db\u4e24\u3002 \u3000\u3000\u90ed\u5927\u8def\u6070\u597d\u80fd\u53e3\u5403\u5757\u3002 \u3000\u3000\u732b\u4f0f\u5728\u9178\u6885\u6c64\u811a\u767e\u61d2\u6d0b\u6d0b\u7684\u8fd9\u662f\u6761\u5f88\u968f\u548c\u7684\u732b\xb7\u5e76\u4e0d\u5b9a\u8981\u5403\u9c7c\xb7\u5e76\u4e0d\u53cd\u5bf9\u7ea2\u70e7\u8089\u3002 \u3000\u3000\u65e0\u8bba\u662f\u4eba\u662f\u732b\xb7\u809a\u5b50\u997f\u7684\u65f6\u5019\uff0c\u90fd\u4e0d\u4f1a\u4e0d\u5403\u5bf9\u7ea2\u70e7\u8089\u7684\u3002 \u3000\u3000\u5403\u4e0b\u4e03\u516b\u5757\u8089\u90ed\u5927\u8def\u624d\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u7b80\u76f4\u8fde\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u4f60\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u635f\u7740\u5634\u7b11\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u505a\u4e8b\u603b\u662f\u8fd9\u9ebd\u6837\u795e\u79d8\u4e2d\u4eca\u7684\u9ebd\uff1f\u201d \u3000\u3000\u9178\u6885\u6ca5\u5782\u4e0b\u5934\xb7\u7b11\u9053\u201c\u6211\u672c\u6765\u662f\u60f3\u81ea\u5df1\u9001\u53bb\u7684\xb7\u53ef\u662f\u6211\u4f2f\u4f60\u4eec\u4e0d\u80af\u6536\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u51b7\u51b7\u9053\u201c\u4f60\u6839\u672c\u4e0d\u5fc5\u9001\u8fd9\u4e9b\u4e1c\u897f\u6765\u7684\u3002\u201d\u9178\u6885\u6c64\u9053\u201c\u4f60\u4eec\u5e2e\u4e86\u6211\u5f88\u591a\u5fd9\u6211\u603b\u4e0d\u80fd \u4e0d\u8868\u793a\u70b9\u5fc3\u610f\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u3008\u8fd9\u4e9b\u4e1c\u897f\u6211\u4eec\u8fd8\u662f\u4e0d\u80fd\u6536\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u4f60\u662f\u5973\u4eba\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u9053\u201c\u5973\u4eba\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8198\u4e86\u71d5\u4e03\u773c\xb7\u7b11\u9053\u201c\u5979\u8bf4\u8bdd\u7684\u53e3\u6c14\u5012\u6444\u4f60\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u677f\u7740\u8138\u9053\u201c\u7537\u4eba\u9001\u8fd9\u9ebd\u591a\u4e1c\u897f\u6765\uff0c\u6211\u4eec\u4e5f\u6837\u4e0d\u80fd\u6536\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u63a5\u7740\u9053\u201c\u4f55\u51b5\u6211\u4eec\u5df2\u5403\u4e86\u4f60\u597d\u51e0\u987f\u5df2\u7ecf\u4e0d\u592a\u597d\u610f\u601d \u3000\u3000\u9178\u6885\u6c64\u7728\u7728\u773c\uff0c\u9053\u201c\u90a3\u9ebd\u5c31\u7b97\u6211\u628a\u8fd9\u4e9b\u4e1c\u897f\u5b58\u5728\u4f60\u4eec\u8fd9\u91cc\u597d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u5c31\u8981\u79df\u91d1\u3002\u201d \u3000\u3000\u9178\u6885\u6ca5\u9053\u201c\u6211\u6751\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd8\u8981\u4fdd\u7ba1\u8d39\u3002\u201d \u3000\u3000\u9178\u6885\u6ca5\u9053\u201c\u6211\u4e5f\u4ed8\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6bcf\u5929\u5341\u4e24\u94f6\u5b50\u3002\u201d\u9178\u6885\u6c64\u9053\u201c\u597d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8981\u5148\u4ed8\u4e0d\u80fd\u6b20\u5e10\u3002\u201d\u9178\u6885\u6c64\u7b11\u9053\u201c\u6211\u5148\u4ed8\u5341\u5929\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u5979\u771f\u7684\u62ff\u51fa\u4e86\u2606\u81ea\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u738b\u52a8\u6ca1\u6709\u52a8\u53ea\u662f\u7740\u8fd9\u5927\u952d\u94f6\u4e8e\u770b\uff0c\u597d\u50cf\u770b\u5f97\u51fa\u4e86\u795e\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ed6\u4eec\u5374\u5728\u76ef\u7740\u738b\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u5ffd\u7136\u5f00\u59cb\u89c9\u5f97\u5e72\u52a8\u8fd9\u4eba\u72e0\u83ab\u540d\u5176\u5999\u5f88\u5c82\u6709\u6b64\u7406\u3002 \u3000\u3000\u522b\u4eba\u597d\u5fc3\u597d\u610f\u7684\u9001\u9152\u7ed9\u4ed6\u559d\u9001\u996d\u7ed9\u4ed6\u5403\u9001\u6905\u5b50\u7ed9\u4ed6\u5750\u9001\u5e8a\u7ed9\u4ed6\u7761\u8fd8\u628a\u4ed6\u7684\u7834\u5c4b\u4e8e\u4fee\u9970 \u65b0\u3002 \u3000\u3000\u4ed6\u5374\u8981\u6536\u4eba\u5bb6\u7684\u79df\u91d1\u800c\u4e14\u8fd8\u8981\u5148\u4ed8\u3002 \u3000\u3000\u201c\u8fd9\u4eba\u4ed6\u5988\u7684\u7b80\u76f4\u662f\u4e2a\u6d3b\u6df7\u86cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u7740\u4ed6\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u9a82\u4e86\u51fa\u6765\u3002 \u3000\u3000\u738b\u52a8\u7684\u773c\u775b\u5df2\u7ecf\u4ece\u94f6\u5b50\u4e0a\u79fb\u5f00\u77aa\u7740\u9178\u6885\u6c64\u5ffd\u7136\u9053\u201c\u4f60\u6709\u75c5\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u6014\u4e01\u6014\u9053\u201c\u6709\u75c5\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u4f46\u6709\u75c5\u800c\u4e14\u75c5\u5f88\u91cd\u3002\u201d\u9178\u6885\u6c64\u7b11\u9053\u201c\u6211\u5403\u53c8\u5403\u5f97\u4e0b\u7761\u53c8\u7761\u5f97\u7740\u600e\u9ebd\u4f1a\u6709\u75c5 \u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e5f\u8bb8\u4f60\u8fd9\u75c5\u5c31\u662f\u5403\u591a\u4e86\u80c0\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u6beb\u65e0\u8868\u60c5\u53c8\u9053\u201c\u4f60\u82b1\u94b1\u4e70\u4e86\u8fd9\u9ebd\u591a\u4e1c\u897f\u53c8\u8d39\u4e86\u5f88\u591a\u4e8b\u9001\u5230\u8fd9\u91cc\u6765\xb7\u5374\u8fd8\u5fc3\u7518\u60c5\u613f \u7684\u4ed8\u6211\u79df\u91d1\uff0c\u4e2a\u4eba\u82e5\u662f\u6ca1\u6709\u75c5\u600e\u9ebd\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d\u90ed\u5927\u8def\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u89c9\u5f97\u9178\u6885\u6ca5\u7684\u786e\u6709\u75c5\uff0c\u800c\u4e14\u8fd8\u7684\u786e\u75c5\u5f97\u5f88\u91cd\u3002 \u3000\u3000\u9178\u6885\u6c64\u773c\u73e0\u4e8e\u5728\u6253\u8f6c\u9053\u201c\u6211\u82e5\u8bf4\u8fd9\u9ebd\u6837\u505a\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u89c9\u5f97\u6b20\u4e86\u4f60\u4eec\u7684\u60c5\uff0c\u4f60\u4eec\u4fe1\u4e0d \u4fe1\uff1f\u201d \u3000\u3000\u738b\u52a8\u770b\u4e86\u770b\u90ed\u5927\u8def\u9053\u201c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u82e5\u8fde\u4ed6\u90fd\u4e0d\u4fe1\u53ea\u4f2f\u5929\u4e0b\u5c31\u6ca1\u6709\u522b\u7684\u4eba\u4f1a\u4fe1\u4e86\u3002\u201d \u3000\u3000\u9178\u6885\u6ca5\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6240\u4ee5\u6211\u4e5f\u6ca1\u6709\u8fd9\u9ebd\u6837\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f53\u51c6\u5907\u600e\u9ebd\u6837\u8bf4\uff1f\u201d \u3000\u3000\u9178\u6885\u6c64\u963b\u73e0\u4e8e\u4e0d\u505c\u7684\u8f6c\u54ac\u7740\u7525\u5507\u9053\u201c\u4e00\u4e2a\u7537\u4eba\u82e5\u662f\u770b\u4e0a\u4e86\u4e2a\u5973\u4eba\xb7\u60f3\u8981\u5a36\u5979\u662f\u4e0d\u662f\u5c31\u4f1a \u505a\u51fa\u5f88\u591a\u83ab\u540d\u5176\u5999\u7684\u4e8b\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7537\u4eba\u4e3a\u4e86\u4e2a\u4ed6\u5df2\u7231\u4e0a\u4e86\u7684\u5973\u4eba\u7b80\u76f4\u4ec0\u9ebd\u4e8b\u90fd\u505a\u5f97\u51fa\u7684\u3002 \u3000\u3000\u9178\u6885\u6c64\u9053\u201c\u5973\u4eba\u4e5f\u6837\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u9053\u201c\u4e2a\u5973\u4eba\uff0c\u82e5\u662f\u770b\u4e86\u4e2a\u7537\u4eba\u60f3\u8981\u5ac1\u7ed9\u4ed6\u4e5f\u6837\u4f1a\u505a\u51fa\u5f88\u591a\u83ab\u540d\u5176\u5999\u7684\u4e8b\u6765\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u8138\u5ffd\u7136\u7ea2\u4e86\u5782\u7740\u5934\u9053\u201c\u6211\u2026\u201d\u6211\u4eca\u5e74\u5df2\u7ecf\u5341\u516b\u4e86\u3002\u201d \u3000\u3000\u5341\u516b\u5c81\u7684\u5973\u5b69\u5b50\uff0c\u901a\u5e38\u90fd\u4f1a\u60f3\u5230\u4ef6\u4e8b\u3002 \u3000\u3000\u5ac2\u4eba\u3002 \u3000\u3000\u5341\u516b\u5c81\u7684\u5973\u5b69\u5b50\u6709\u54ea\u4e2a\u4e0d\u6000\u6625\uff1f \u3000\u3000\u8fd9\u672c\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u7b11\u5382\u9053\u201c\u4f60\u6ca1\u6709\u75c5\xb7\u7537\u4eba\u5f53\u5a5a\u4ea5\u5927\u5f53\u5ac1\uff0c\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u4f60\u6709\u75c5\u3002\u201d \u3000\u3000\u4ed6\u633a\u4e86\u633a\u80f8\u53c8\u9053\u201c\u5374\u4e0d\u77e5\u4f60\u770b\u4e0a\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8e6c\u4e86\u4ed6\u773c\u51b7\u51b7\u9053\u201c\u5f53\u7136\u662f\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u90a3\u5012\u4e0d\u2500\u5b9a\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u8bf4\u201c\u4e0d\u5b9a\u201d\u8138\u4e0a\u7684\u8868\u60c5\u5374\u5df2\u662f\u5341\u62ff\u4e5d\u7a33\u4e86\u3002 \u3000\u3000\u50cf\u4ed6\u8fd9\u6837\u7684\u7537\u4eba\u5c31\u7b97\u6253\u9523\u90fd\u627e\u4e0d\u5230\u7684\u3002 \u3000\u3000\u9178\u6994\u6c64\u82e5\u6ca1\u6709\u770b\u4e0a\u4ed6\xb7\u8fd8\u80fd\u770b\u4e0a\u8c01\uff1f \u3000\u3000\u9178\u6885\u6ca5\u7684\u786e\u6b63\u5728\u770b\u7740\u4ed6\xb7\u4f46\u5374\u6447\u4e86\u6447\u5934\uff0c\u63a9\u7740\u5634\u7b11\u9053\u201c\u4e5f\u8bb8\u662f\u9645\u4e5f\u8bb8\u6c34\u8db3\u4f60\u6211\u73b0\u5728\u8fd8\u4e0d \u80fd\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9178\u6885\u6c64\u9053\u201c\u56e0\u4e3a\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u65f6\u5019\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65f6\u624d\u5230\u65f6\u5019\uff1f\u201d \u3000\u3000\u9178\u6885\u6c64\u773c\u6ce2\u6d41\u52a8\u53c8\u4f4e\u7740\u5934\xb7\u9053\u201c\u6211\u603b\u8981\u5148\u770b\u770b\u4ed6\u662f\u4e0d\u662f\u771f\u7684\u6c72\u597d\u8fd9\u662f\u4f60\u7684\u7ec8\u751f\u5927\u4e8b\u6211\u603b \u4e0d\u80fd\u4e0d\u7279\u522b\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u73b0\u5728\u8fd8\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u9178\u6885\u6c64\u9053\u201c\u6211\u2500\u2026\u6211\u8fd8\u60f3\u518d\u7b49\u7b49\uff0c\u518d\u770b\u770b\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u6211\u770b\u4f60\u8fd8\u662f\u5feb\u70b9\u770b\u5427\xb7\u6709\u4eba\u5df2\u7ecf\u5feb\u6025\u6b7b\u4e86\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u966a\u7b11\u9053\u201c\u6ca1\u5173\u7cfb\u4f60\u6162\u6162\u7684\u770b\u597d\u4eba\u603b\u662f\u597d\u4eba\u8d8a\u770b\u8d8a\u597d\u770b\u3002\u201d \u3000\u3000\u9178\u6885\u6c64\u5a9a\u7136\u9053\u201c\u6211\u770b\u51fa\u6765\u4e4b\u5f8c\uff0c\u5b9a\u7b2c\u4e2a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u62a5\u5934\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8d70\u5462\uff1f\u5927\u5bb6\u9f50\u804a\u804a\u5929\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6709\u4ec0\u9ebd\u597d\u804a\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u96be\u9053\u6ca1\u6709\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8c0a\u201c\u6211\u53ea\u6709\u53e5\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u5934\u4e5f\u4e0d\u56de\xb7\u51b7\u51b7\u7684\u63a5\u901a\u201c\u73b0\u5728\u7684\u5973\u5b69\u5b50\u8138\u76ae\u7684\u786e\u8d8a\u6765\u8d8a\u539a\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u70ed\u4e03\u8d70\u51fa\u53bb\u624d\u6447\u4e86\u6447\u5934\u7b11\u9053\u201c\u8fd9\u4eba\u7684\u813e\u6c14\u867d\u7136\u6709\u70b9\u602a\u4ee3\u95e8\u662f\u4e2a\u5904\u4eba\u77aa\u8d34\u5a18\u4f60\u5343 \u4e07\u4e0d\u80fd\u751f\u4ed6\u7684\u6c14\u3002\u201d \u3000\u3000\u9178\u5fa1\u6c64\u8e1e\u7136\u9053\u201c\u627e\u4e2a\u59d3\u9178\u6211\u59d3\u6885\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u6885\u82b1\u7684\u6094\uff1f\u201d \u3000\u3000\u9178\u6885\u6c64\u70b9\u4ece\u5934\xb7\u9053\u201c\u6211\u53eb\u6094\u6c5d\u7537\u3002\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u53c8\u662f\u6885\u82b1\uff0c\u53c8\u662f\u5170\u82b1\u7b80\u76f4\u53ef\u4ee5\u5f00\u82b1\u5e97\u4e86\u3002\u201d \u3000\u3000\u9178\u6885\u6ca5\u7b11\u9053\u201c\u4e0d\u662f\u2606\u82b1\u7684\u7aff\u662f\u7537\u4eba\u7684\u7537\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6885\u6c5d\u7537\u8fd9\u540d\u5b57\u5012\u6709\u70b9\u602a\u3002\u201d \u3000\u3000\u641e\u6c5d\u7537\u9053\u201c\u5149\u7236\u66ff\u6211\u53d6\u8fd9\u540d\u5b57\u7684\u610f\u601d\u5c31\u662f\u544a\u8bc9\u6211\u4f60\u8981\u8c61\u4e2a\u7537\u4eba\u4e0d\u80fd\u62a5\u62a5\u634f\u634f\u7684\u60f3\u505a\u4ec0\u9ebd \u4e8b\u5c31\u53bb\u505a\u60f3\u8bf4\u4ec0\u9ebd\u5c31\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u4eca\u7b79\u4e5d\u6cc9\u4e4b\u4e0b\u6709\u7075\u5b9a\u4f1a\u89c9\u5f97\u5f88\u9ad8\u5174\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u529b\u4f60\u7684\u786e\u6ca1\u54cd\u8f9c\u8d1f\u4ed6\u7684\u671f\u671b\u3002\u201d \u3000\u3000\u6094\u6c5d\u7537\u7684\u8138\u7ea2\u4e86\u9053\u201c\u4f60\u2026\u2500\u4f60\u8ba4\u4e3a\u6211\u505a\u4e8b\u771f\u7684\u5f88\u50cf\u7537\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8db3\u5973\u4eba\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u5fcd\u4e2a\u4efb\u7b11\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u9053\u201c\u4f60\u505a\u4e8b\u7684\u786e\u6bd4\u5f88\u591a\u7537\u4eba\u8fd8\u8c61\u7537\u4eba\u8b6c\u5982\u8bf4 \u3000\u3000\u4ed6\u5c06\u58f0\u97f3\u538b\u5f97\u5f88\u4f4e\u5f88\u4f4e\u6084\u6084\u9053\u201c\u6211\u8000\u90a3\u670b\u53cb\u71d5\u4e03\u6709\u65f6\u5c31\u5f88\u8c61\u5973\u4eba\u4e0d\u4f97\u6709\u70b9\u5a18\u5a18\u80f6\xb7\u800c\u4e14 \u5e38\u5e38\u4f1a\u65e0\u7f18\u65e0\u6545\u7684\u53d1\u813e\u6c14\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4f60\u961f\u4e3a\u5973\u4eba\u5e38\u4f1a\u65e0\u7f18\u65e0\u6545\u7684\u751f\u6c14\uff1f \u3000\u3000\u90ed\u5927\u8def\u53ea\u7b11\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5973\u2606\u4e5f\u8ddf\u7537\u4eba\u4e2d\u82e5\u751f\u6c14\u5b9a\u6709\u7f18\u6545\u7684\u53ea\u4e0d\u8fc7\u7537\u4eba\u4e0d\u5982\u9053\u800c\u5df2\u3002 \u3000\u3000\u5979\u7b11\u4e86\u7b11\u63a5\u9053\u201c\u5176\u5b9e\u7537\u4eba\u5e76\u4e0d\u5982\u4ed6\u4eec\u81ea\u5df1\u60f3\u5f97\u90a3\u9ebd\u806a\u660e\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u8bf4\u8bdd\u5374\u53c8\u5fcd\u4f4f\u3002 \u3000\u3000\u4ed6\u51b3\u5fc3\u4e0d\u8ddf\u5979\u4e89\u8fa9\u8981\u4e89\u8fa9\u4e5f\u7b49\u5979\u8bf4\u51fa\u5979\u770b\u4e0a\u7684\u90a3\u4e2a\u4eba\u4e4b\u5f8c\u518d\u4e89\u8fa9\u3002 \u3000\u3000\u5230\u90a3\u65f6\u4ed6\u5c31\u4f1a\u544a\u8bc9\u5979\u7537\u4eba\u81f3\u5c11\u603b\u6bd4\u5979\u60f3\u8c61\u4e2d\u806a\u660e\u5f97\u591a\u3002 \u3000\u3000\u5230\u90a3\u65f6\u5979\u2026\u5b9a\u5c31\u4f1a\u76f8\u4fe1\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9762\u4e0a\u9732\u51fa\u4e86\u7b11\u5bb9\u597d\u50cf\u5df2\u60f3\u8c61\u5230\u90a3\u65f6\u5019\u7684\u7897\u80a0\u98ce\u5149\u9178\u62c7\u6c64\u6b63\u9547\u5728\u4ed6\u7684\u6000\u91cc\u544a\u8bc9\u4ed6 \u201c\u90a3\u4e2a\u4eba\u201d\u5c31\u662f\u4ed6\u3002 \u3000\u3000\u201c\u90a3\u65f6\u4eba\u5c31\u4f1a\u77e5\u9053\u7a76\u7adf\u662f\u8c01\u806a\u660e\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u5f97\u51e0\u4e4e\u8fde\u5634\u90fd\u5408\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u6797\u592a\u5e73\u4e5f\u5728\u7b11\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u4e5f\u5728\u60f3\u540c\u6837\u7684\u4e8b\u5462\uff1f \u3000\u3000\u4e2d\u4eba\u82e5\u4e0d\u4f1a\u81ea\u6211\u9676\u9189\uff0c\u4e5f\u5c31\u4e0d\u80fd\u7b97\u662f\u4e2a\u771f\u6b63\u7684\u7537\u4eba\u3002 \u3000\u3000\u4e5f\u8bb8\u6839\u672c\u4e0d\u7b97\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u4eba\u4e4b\u6240\u4ee5\u6bd4\u755c\u7272\u5f3a\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4eba\u4f1a\u81ea\u6211\u9633\u9189\u755c\u7272\u4e0d\u4f1a\xb7 \u3000\u3000\u6885\u6c5d\u7537\u5ffd\u53c8\u9053\u201c\u5176\u5b9e\u4e2a\u7537\u4eba\u80fd\u6709\u70b9\u7ad9\u5a18\u8154\u4e5f\u4e0d\u9519\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u90a3\u79cd\u4eba\u81f3\u5c11\u4e0d\u4f1a\u72e0\u91ce\u86ee\u516b\u7c97\u9c81\uff0c\u800c\u4e14\u4e00\u5b9a\u6bd4\u8f83\u6e29\u67d4\u4f53\u8d34\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u6218\u4e86\u8d77\u6765\u4e00\u626d\u626d\u7684\u8d70\u51fa\u53bb\u5ffd\u53c8\u56de\u5934\xb7\u95ee\u738b\u52a8\u9053\u201c\u4f60\u770b\u6211\u662f\u4e0d\u662f\u4e5f\u6709\u70b9\u59a8\u5a18\u8154 \u5462\uff1f\u201d\u738b\u52a8\u9053\u201c\u4f60\u662f\u7537\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5927\u7b11\u9053\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u662f\u7684\u73b0\u5728\u8fde\u81ea\u5df1\u4e5f\u6709\u70b9\u5f04\u4e0d\u6e05\u695a\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e94\u7ae0",content:(0,s.jsx)(s.Fragment,{children:"\u6708\u4eae\u3002\u6708\u4eae\u5f88\u4eae\u3002 \u3000\u3000\u5706\u5706\u7684\u6708\u4eae\u6302\u5728\u6811\u68a2\u3002 \u3000\u3000\u71d5\u4e03\u4e2a\u4eba\u5750\u5728\u6811\u4e0b\uff0c\u75f4\u75f4\u7684\u53d1\u6014\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u4e5f\u8d70\u8fc7\u6765\u5750\u5728\u4ed6\u65c1\u8fb9\u3002 \u3000\u3000\u71d5\u4e03\u76b1\u4e86\u76b1\u7709\u77aa\u8d77\u4e86\u773c\u9053\u201c\u4f60\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6765\u804a\u804a\u3002\u201d \u3000\u3000\u71d5\u4e03\u677f\u8138\u9053\u201c\u4f60\u8ddf\u6211\u6709\u4ec0\u9ebd\u597d\u804a\u7684\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u53bb\u627e\u90a3\u6885\u59cb\u5a18\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6478\u6478\u4e0b\u5df4\uff0c\u9053\u201c\u4f60\u597d\u50cf\u4e0d\u592a\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u559c\u6b22\u5979\u7684\u4eba\u5df2\u7ecf\u591f\u591a\u4e86\uff0c\u7528\u4e0d\u6211\u518d\u53bb\u51d1\u6570\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u71d5\u4e03\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\u201c\u4eca\u5929\u4e0b\u5348\uff0c\u4f60\u4eec\u597d\u50cf\u804a\u5f97\u5f88\u5f00\u5fc3\u561b\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u662d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u65e2\u7136\u804a\u5f97\u90a3\u9ebd\u5f00\u5fc3\u4f55\u5fc5\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u9053\u201c\u4f60\u5728\u5403\u918b\u3002\u201d \u3000\u3000\u85cf\u4e03\u7684\u8138\u597d\u50cf\u7ea2\u4e86\u7ea2\u9053\u201c\u5403\u918b\uff1f\u6211\u5403\u8c01\u7684\u9177\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u77e5\u9053\u5979\u559c\u6b22\u7684\u4eba\u4e00\u5b9a\u662f\u6211\u4f60\u5374\u5f88\u559c\u6b22\u5979\u6240\u4ee5\u201c\u4e00\u201d\u201d \u3000\u3000\u71d5\u4e03\u4e0d\u7b49\u4ed6\u7684\u8bdd\u8bf4\u5b8c\uff0c\u7ad9\u8d77\u6765\u5c31\u8981\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u62c9\u4f50\u4ed6\u7684\u624b\u4ed6\u7528\u529b\u7529\u5f00\xb7\u90ed\u5927\u8def\u53c8\u62c9\u4f4f\u9053\u201c\u6211\u662f\u6765\u627e\u4f60\u8c08\u6b63\u7ecf\u4e8b\u7684\u3002\u201d \u3000\u3000\u9893\u4e03\u76b1\u7709\u9053\u201c\u6b63\u7ecf\u4e8b\uff1f\u4f60\u5634\u91cc\u8fd8\u8bf4\u5f97\u51fa\u4ec0\u9ebd\u6b63\u7ecf\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u4f60\u597d\u50cf\u8bf4\u8fc7\uff0c\u8fd9\u9644\u8fd1\u6709\u4e2a\u59d3\u6885\u7684\u4eba\u5bb6\xb7\u6709\u4e2a\u5927\u5c11\u7237\u53eb\u4e07\u4eba\u2019\u6885\u6c5d\u7532\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5357\u5bab\u4e11\u7684\u79d8\u5bc6 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u60f3\u6885\u6c5d\u7537\u4f1a\u4e0d\u4f1a\u662f\u6885\u6c5d\u7532\u7684\u59b9\u59b9\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4e0d\u662f\u90fd\u548c\u6211\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6885\u5bb6\u662f\u4e0d\u662f\u548c\u98ce\u6816\u68a7\u6709\u4ec7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u60f3\u5b9a\u662f\u7684\u6240\u4ee5\u6885\u6c5d\u7537\u624d\u4f1a\u7528\u8ba1\u9664\u6389\u98ce\u6816\u68a7\u53ef\u662f\u5979\u548c\u5357\u5bcc\u4e11\u662f\u4e0d\u662f\u4e5f\u6709\u4ec7\uff1f \u5357\u5bcc\u53ca\u662f\u4e0d\u662f\u5979\u6551\u8d70\u7684\uff1f\u5979\u5c06\u5357\u5bab\u4e11\u6551\u8d70\uff1f\u662f\u4e0d\u662f\u4e3a\u90a3\u6279\u73e0\u5b9d\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\u5979 \u5df1\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u5979\u81ea\u5df1\u65e2\u7136\u6ca1\u6709\u8bf4\uff0c\u6211\u95ee\u4e5f\u95ee\u4e0d\u51fa\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u7b11\u9053\u201c\u6211\u770b\u4f60\u662f\u6c34\u6562\u95ee\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u653f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4f2f\u5f97\u7f6a\u5979\u4eec\u5979\u751f\u6c14\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ed\u5634\u817f\u62c9\u5f97\u8001\u957f\u3002 \u3000\u3000\u90ed\u5927\u8def\u56de\u8fc7\u5934\u5c31\u770b\u5230\u6885\u6c5d\u7537\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u5979\u8138\u4e0a\u5e26\u751c\u7b11\u773c\u775b\u53c8\u5927\u53c8\u4eae\u7b11\u9053\u201c\u90a3\u4e9b\u4e8b\u4f60\u4eec\u672c\u6765\u5c31\u8be5\u95ee\u6211\u7684\u6211\u600e\u9ebd\u4f1a\u751f\u6c14\u3002\u201d \u3000\u3000\u75c8\u4e03\u677f\u8138\u51b7\u51b7\u9053\u201c\u6211\u4eec\u521a\u8bf4\u7684\u8bdd\u4f60\u4f1a\u542c\u89c1\u4e86\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u4f4e\u4e0b\u5934\u9053\u201c\u6211\u4e0d\u662f\u6545\u610f\u60f3\u6765\u5077\u542c\u7684\uff0c\u6211\u662f\u6765\u544a\u8bc9\u4f60\u4eec\u665a\u996d\u5df2\u6e2f\u5907\u597d\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6765\u5f97\u5012\u771f\u5de7\u3002\u201d \u3000\u3000\u4ed6\u672c\u5df2\u7ad9\u4e86\u8d77\u6765\u73b0\u5728\u5df2\u62a5\u5934\u5c31\u8d70\u6885\u6c5d\u7537\u770b\u4ed6\u8d70\u8fdc\u5201\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u6211\u53c8\u6ca1\u6709\u5f97\u7f6a\u4ed6\u4ed6 \u4e3a\u4ec0\u9ebd\u770b\u89c1\u6211\u5c31\u8d70\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u559c\u6b22\u4f60\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u7728\u4e86\u7728\u773c\xb7\u9053\u201c\u80b2\u6b22\u6211\uff1f\u4e3a\u4ec0\u9ebd\u53cd\u800c\u8eb2\u6211\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e5f\u8bb8\u5c31\u7f51\u4e3a\u4ed6\u5df3\u770b\u51fa\u4f60\u559c\u6b22\u7684\u4eba\u4e0d\u662f\u4ed6\u3002\u201d \u3000\u3000\u6885\u72ec\u7537\u4f4e\u5934\u8fc7\u4e86\u5f88\u4e45\u5ffd\u7136\u7b11\u4e86\u3002\u90ed\u5927\u8def\u9053\u201c\u4f60\u7b11\u4ec0\u9ebd\u201d \u3000\u3000\u6885\u72ec\u7537\u62bf\u5634\u7b11\u9053\u201c\u6211\u7b11\u4f60\u8ba2\u7537\u4eba\uff0c\u603b\u662f\u8be5\u95ee\u7684\u8bdd\u4e0d\u95ee\uff0c\u8be5\u8bf4\u7684\u8bdd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u60f3\u95ee\u4f60\u7684\u90a3\u4e9b\u4e8b\uff0c\u4f60\u201d\u4e00\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u62c9\u8d77\u4ed6\u7684\u624b\xb7\u7b11\u9053\u201c\u8d70\u6211\u4eec\u5403\u996d\u53bb\u90a3\u4e9b\u4e8b\u5403\u5b8c\u996d\u6211\u518d\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u4e3a\u4ec0\u9ebd\u4e0d\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6211\u4f2f\u4f60\u542c\u5382\u5403\u4e0d\u4e0b\u996d\u53bb\u3002 \u3000\u3000\u5979\u6309\u7ff1\u5927\u8def\u8d70\u8fdb\u5c4b\u4e8e\uff0c\u62c9\u5f97\u5f88\u7d27\uff0c\u5750\u4e0b\u6765\u5f8c\u597d\u50cf\u8fd8\u820d\u4e0d\u5f97\u653e \u3000\u3000\u738b\u52a8\u5728\u5979\u7684\u4e88\u6797\u592a\u5e73\u4e5f\u5728\u76ef\u5979\u7684\u624b\xb7\u71d5\u4e03\u60f3\u6545\u610f\u88c5\u505a\u770b\u4e0d\u89c1\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5077\u5077\u8198\u4e86\u773c\u3002 \u3000\u3000\u90ed\u4eba\u8def\u5fc3\u91cc\u771f\u662f\u8bf4\u4e2d\u51fa\u7684\u8212\u670d\xb7\u6240\u4ee5\u8fd9\u987f\u996d\u5403\u5f97\u7279\u522b\u591a\u3002 \u3000\u3000\u4ed6\u62b9\u5634\u7684\u65f6\u5019\xb7\u6885\u6c5d\u7537\u5ffd\u7136\u9053\u201c\u4f60\u97f6\u731c\u7684\u90fd\u6ca1\u6709\u9519\u6211\u662f\u6885\u6c5d\u7532\u7684\u59b9\u59b9\u6211\u4eec\u5bb6\u7684\u786e\u8ddf\u98ce\u6816 \u68a7\u6709\u4ec7\u53ea\u53ef\u60dc\u76f4\u627e\u4e0d\u4ed6\u6240\u4ee5\u624d\u60f3\u51fa\u8fd9\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u63a5\u9053\u201c\u6211\u4eec\u65e9\u5df3\u7b97\u51c6\u68cd\u4e88\u548c\u91d1\u7530\u5b50\u4e00\u5b9a\u80fd\u5c06\u98ce\u6816\u68a7\u4ece\u7a9d\u91cc\u638f\u51fa\u6765\u4ed6\u4eec\u662f\u5b98 \u5dee\uff0c\u627e\u4eba\u81ea\u7136\u6bd4\u6211\u4eec\u65b9\u4fbf\u5f97\u591a\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u5979\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u624d\u63a5\u9053\u201c\u76f4\u5230\u8fd9\u91cc\u4e3a\u6b62\u4f60\u4eec\u90fd\u8fd8\u6ca1\u6709\u731c\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ee5\u5f8c\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4ee5\u5f8c\u7684\u4e8b\xb7\u4f60\u4eec\u5c31\u5168\u90fd\u731c\u9519\u4e86\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86\u6014\u9053\u201c\u6211\u4eec\u731c\u9519\u4e86\u56db\u4e9b\u4e8b\uff1f\u201d \u6885\u6c5d\u7537\u9053\u201c\u7b2c\xb7\u90a3\u9ed1\u8863\u4eba\u5e76\u4e0d\u662f\u5357\u5bcc\u4e11\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6c34\u662f\u5357\u5bab\u4e11\u662f\u8c01\uff1f\uff1f \u3000\u3000\u6885\u72ec\u7537\u54ac\u5631\u5507\u8fc7\u4e86\u5f88\u4e45\u624d\u8428\u5b9a\u51b3\u5fc3\xb7\u9053\u201c\u662f\u6211\u54e5\u54e5\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\xb7\u5927\u5bb6\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u90ed\u5927\u8def\u7b80\u76f4\u5fcd\u4e0d\u4f4f\u8981\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6797\u592a\u5e73\u4e5f\u4e0d\u7981\u5931\u58f0\u9053\u201c\u4f60\u54e5\u54e5\uff1f\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u505a\u90a3\u79cd\u4e8b\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u5782\u4e0b\u5934\u8c0a\u201c\u6c5f\u6e56\u4e2d\u4eba\u90fd\u4ee5\u4e3a\u6211\u4eec\u62d6\u5bb6\u662f\u6b66\u6797\u4e16\u5bb6\u4e00\u5b9a\u662f\u5bb6\u8d2d\u4e07\u8d2f\u56e0\u4e3a\u6211\u4eec\u5bb6\u7684\u6392 \u573a\u4e00\u5411\u90fd\u5f88\u5927\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u53ea\u8981\u627e\u5230\u6211\u4eec\xb7\u6211\u4eec\u4ece\u6ca1\u6709\u8ba9\u4ed6\u4eec\u5931\u671b\u8fc7\u3002\u201d \u3000\u3000\u5979\u795e\u60c5\u53d8\u5f97\u5f88\u51c4\u51c9\u954a\u7136\u9053\u201c\u5176\u5b9e\u81ea\u4ece\u5148\u7236\u53bb\u4e16\u4e4b\u5f8c\u6211\u8ba2\u5bb6\u65e9\u5df2\u53d8\u5f97\u5916\u5f3a\u4e2d\u5e72\uff0c\u975e\u4f46\u6ca1\u6cd5 \u5b50\u63a5\u6d4e\u522b\u4eba\u8fde\u81ea\u5df1\u7684\u65e5\u5b50\u90fd\u8fc7\u5f97\u5f88\u8270\u82e6\uff0c\u6240\u4ee5\u2026\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u4f60\u4eec\u4e0d\u4f46\u60f3\u8981\u98ce\u6816\u68a7\u7684\u547d\u8fd8\u60f3\u8981\u4ed6\u7684\u94b1\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u70b9\u70b9\u5934\xb7\u9053\u201c\u4e0d\u9519\u6211\u4eec\u8ba1\u5212\u672c\u662f\u53cc\u7ba1\u9f50\u4e0b\xb7\u6211\u5230\u8fd9\u91cc\u6765\u4f5c\u6848\u7684\u65f6\u5019\u6211\u54e5\u54e5\u65e9\u5df3\u627e \u5230\u68cd\u5b50\u548c\u91d1\u72ee\u5b50\xb7\u800c\u4e14\u505a\u4e86\u4ed6\u4eec\u7684\u4fdd\u954d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c61\u68cd\u5b50\u548c\u91d1\u643a\u5b50\u90a3\u9ebd\u7cbe\u660e\u7684\u4eba\u600e\u9ebd\u4f1a\u968f\u968f\u4fbf\u4fbf\u76f8\u4fe1\u4ed6\u5c31\u662f\u5357\u5bab\u4e11\uff1f\u600e\u9ebd\u4f1a\u968f \u968f\u4fbf\u4fbf\u5c31\u7528\u4ed6\u505a\u4fdd\u954a\u5462\uff1f\u201d \u3000\u3000\u638f\u6c5d\u7537\u9053\u201c\u7b2c\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e5f\u6ca1\u89c1\u8fc7\u5357\u5bab\u4e11\xb7\u7b2c\u4e8c\uff0c\u56e0\u4e3a\u6211\u54e5\u54e5\u8eab\u4e0a\u5e26\u6837\u5357\u5bab\u4e11\u7684\u4fe1 \u7269\uff0c\u7b2c\u53c1\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u60f3\u4e0d\u5230\u4f1a\u6709\u4eba\u5192\u5145\u752d\u5bcc\u4e11\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7b2c\u56db\xb7\u56e0\u4e3a\u4f60\u4eec\u7684\u8fd0\u6c14\u4e0d\u9519\u3002\u4f46\u662f\u4f60\u54e5\u54e5\u8eab\u4e0a\u600e\u9ebd\u4f1a\u6709\u5357\u5bab\u4e11\u7684\u4fe1\u7269\uff1f\u201d\u6885 \u6c5d\u7537\u9053\u201c\u56e0\u4e3a\u4ed6\u662f\u6211\u54e5\u54e5\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u82e6\u7b11\u9053\u201c\u770b\u6765\u4f60\u54e5\u5012\u4e5f\u662f\u4e2a\u5929\u624d\u5c45\u7136\u80fd\u4ea4\u5230\u8fd9\u79cd\u670b\u53cb\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u7684\u8138\u7ea2\u4e86\u7ea2\u3001\u9053\u201c\u4ed6\u672c\u6765\u5c31\u559c\u6b22\u4ea4\u670b\u53cb\uff0c\u800c\u4e14\u559c\u6b22\u5e2e\u4eba\u5bb6\u7684\u5fd9\xb7\u6c5f\u6e56\u4e2d\u5f97\u8fc7\u4ed6\u597d \u5904\u7684\u4eba\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u3002\u5c31\u56e0\u4e3a\u4ed6\u670b\u53cb\u592a\u591a\u3001\u592a\u6177\u6168\uff0c\u6240\u4ee5\u6211\u4eec\u5bb6\u624d\u4f1a\u4e00\u5929\u6bd4\u4e00\u5929\u7a77\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e0d\u9519\u5b88\u8d22\u5974\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u7f3a\u94b1\u7528\u65e9\u77e5\u4ed6\u662f\u8fd9\u9ebd\u6837\u7684\u4e00\u4e2a\u4eba\u6211\u90a3\u62f3\u5c31\u8be5\u6253\u5f97\u8f7b \u70b9\u7684\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u7684\u8138\u6c89\u4e86\u4e0b\u6765\xb7\u7f13\u7f13\u9053\u201c\u6211\u8fd8\u8981\u544a\u8bc9\u4f60\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u7b2c\u4e00\u6211\u4e0d\u559c\u6b22\u522b\u4eba\u5728\u6211\u9762\u524d\u4fae\u8fb1\u6211\u54e5\u54e5\u7b2c\u4e8c\u82e5\u975e\u4ed6\u7528\u7684\u5175\u5668\u4e0d\u987e\u624b\u6328\u63cd\u7684\u4e0d \u662f\u4ed6\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u77f3\u4eba\u201d\u6885\u6c5d\u7532\u7528\u7684\u5175\u5203\u662f\u77f3\u5668\u8fd9\u70b9\u90ed\u5927\u8def\u4e5f\u542c\u8bf4\u8fc7\u3002\u90ed\u5927\u8def\u53ea\u597d\u7b11\u7b11\uff0c\u9053\u201c\u5374\u4e0d\u77e5\u90a3 \u771f\u7684\u5357\u5bcc\u4e11\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u62c7\u72e1\u7537\u8c08\u8c08\u9053\u201c\u4f60\u9047\u89c1\u7684\u82e5\u771f\u662f\u5357\u5bcc\u4e11\u73b0\u5728\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u5750\u5728\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u5750\u5728\u8fd9\u91cc\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6885\u72ec\u7537\u9053\u201c\u8eba\xb7\u5c31\u7b97\u6ca1\u6709\u8eba\u5728\u68fa\u6750\u91cc\uff0c\u81f3\u5c11\u4e5f\u7ed5\u5728\u5e8a\u4e0a\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u5927\u7b11\xb7\u53ea\u4e0d\u8fc7\u7b11\u5f97\u591a\u5c11\u5df2\u6709\u70b9\u4e0d\u81ea\u7136\u4e86\u3002 \u3000\u3000\u5e78\u597d\u6885\u6c5d\u7537\u5df2\u63a5\u9053\u201c\u6211\u4eec\u7684\u8ba1\u5212\u4ece\u5934\u5230\u5c3e\u90fd\u8fdb\u884c\u5f97\u5f88\u987e\u5229\u76f4\u5230\u2026\u2026\u201d \u3000\u3000\u5979\u770b\u4e86\u6797\u592a\u5e73\u4e00\u773c\uff0c\u6797\u592a\u5e73\u9053\u201c\u76f4\u5230\u6211\u65e0\u610f\u4e2d\u770b\u5230\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u771f\u5e0c\u671b\u90a3\u5929\u4f60\u4eec\u6ca1\u6709\u5230\u57ce\u91cc\u53bb\xb7\u6ca1\u6709\u770b\u5230\u4ed6\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4ed6\u751f\u4f2f\u6211\u4eec\u8fd8\u8981\u8ffd\u67e5\u4ed6\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u60f3\u6765\u628a\u6211\u4eec\u6740\u5382\u706d\u53e3\u3002\u201d \u3000\u3000\u6094\u6c5d\u7537\u51c4\u7136\u9053\u201c\u4ed6\u662f\u6211\u4eec\u6885\u5bb6\u7684\u72ec\u751f\u4e8e\u7edd\u4e0d\u80fd\u8ba9\u6211\u4eec\u6885\u5bb6\u51e0\u767e\u5e74\u7684\u58f0\u540d\u6bc1\u5728\u4ed6\u624b\u4e0a\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u9053\u201c\u6240\u4ee5\u4ed6\u5b81\u53ef\u627f\u8ba4\u81ea\u5df2\u662f\u5357\u5bab\u4e11\u4e5f\u4e0d\u80af\u8bf4\u51fa\u81ea\u5df1\u7684\u771f\u5b9e\u7684\u8eab\u4efd\u6765\u4ed6\u5b81\u53ef\u6b7b\uff0c\u4e5f \u4e0d\u80fd\u4e22\u4eba\u662f\u9ebd\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u70b9\u70b9\u5934\xb7\u773c\u5708\u513f\u5df2\u7ea2\u4e86\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u505a\u4e2a\u6b66\u6797\u4e16\u5bb6\u7684\u72ec\u751f\u5b50\uff0c\u7684\u786e\u6709\u5f88\u591a\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u7684\u75db \u82d7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e16\u4e0a\u4e5f\u8bb8\u53ea\u6709\u79cd\u4eba\u6bd4\u4ed6\u66f4\u75db\u82e6\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56db\u79cd\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u7684\u5a49\u59b9\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u8198\u4e86\u4ed6\u773c\xb7\u4f3c\u7b11\u975e\u7b11\u4f3c\u6028\u975e\u6028\u770b\u6765\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u52a8\u4eba\u3002 \u3000\u3000\u6797\u592a\u5e73\u75f4\u75f4\u7684\u770b\u5979\u5ffd\u7136\u9053\u201c\u90a3\u53e3\u68fa\u6750\u662f\u5f25\u9001\u6765\u7684\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u662d\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e3a\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u53f9\u9053\u201c\u6211\u7ec6\u9053\u4f60\u6740\u4e86\u4eba\u4e4b\u76fe\u5fc3\u91cc\u5b9a\u72e0\u96be\u53d7\u9001\u90a3\u53e3\u7a7a\u68fa\u6750\u6765\u4e3a\u7684\u5c31\u662f\u544a\u8bc9\u4f60\u4f60\u6740\u7684 \u4eba\u5e76\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u6837\u5b50\u66f4\u75f4\u4e86\xb7\u54fa\u95fb\u9053\u201c\u65e0\u8bba\u5982\u4f55\u6211\u603b\u8be5\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u770b\u4ed6\u53c8\u770b\u4e86\u770b\u6885\u6c5d\u7537\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u771f\u8be5\u8c22\u8c22\u4ed6\uff0c\u4ed6\u5bf9\u4f60\u771f\u4e0d\u9519\u3002\u201d \u3000\u3000\u71d5\u4e03\u76f4\u6ca1\u6709\u5f00\u53e3\u5ffd\u7136\u51b7\u51b7\u9053\u201c\u4f46\u68fa\u6750\u4e0a\u8fd8\u662f\u5199\u5357\u5bcc\u4e11\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u65e0\u8bba\u5982\u4f55\u6211\u603b\u4e0d\u80fd\u51fa\u5356\u6211\u54e5\u54e5\u3002\u201d \u3000\u3000\u5979\u773c\u5708\u513f\u66f4\u7ea2\u4e86\u63a5\u9053\u201c\u6211\u867d\u7136\u77e5\u9053\u4ed6\u505a\u7684\u4e0d\u5bf9\u4f46\u4e5f\u53ea\u80fd\u5728\u6697\u4e2d\u963b\u6b62\u3002\u4e00\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u4f60\u4e00\u76f4\u4e0d\u6562\u9732\u9762\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u8def\u7136\u9053\u201c\u6211\u4e0d\u6562\u9732\u9762\u2018\u4e5f\u4e0d\u80fd\u9732\u9762\u3002\u4f46\u6211\u8fd8\u662f\u5c3d\u6211\u6240\u6709\u7684\u529b\u91cf\u6765\u8ba8\u597d\u4f60\u4eec\u53ea\u5e0c\u671b \u4f60\u4eec\u80fd\u770b\u5728\u6211\u7684\u9762\u4e0a\u539f\u8c05\u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4f60\u628a\u4ed6\u6551\u8d70\u7684\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5f53\u7136\u662f\u6211\uff0c\u4ed6\u662f\u6211\u8d61\u4eb2\u7684\u54e5\u54e5\u6211\u603b\u4e0d\u80fd\u770b\u4ed6\u53d7\u82e6\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u59cb\u8d77\u5934\uff0c\u9053\u201c\u5047\u5982\u4f60\u4eec\u8fd8\u4e0d\u80af\u539f\u8c05\u4ed6\xb7\u4e5f\u4e0d\u5fc5\u518d\u53bb\u627e\u4ed6\xb7\u53ef\u4ee5\u6765\u627e\u6211\uff0c\u6211\u613f\u610f\u627f \u5f53\u5207\u8fc7\u9519\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\u5927\u58f0\u9053\u201c\u65e0\u8bba\u522b\u4eba\u600e\u9ebd\u8bf4\u6211\u603b\u8ba4\u4e3a\u4f60\u6ca1\u6709\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\u8bf4\u5979\u9519\u4e86\u8c01\u5c31\u662f\u6df7\u86cb\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u53ea\u80fd\u8bf4\u5979\u7b80\u76f4\u4e0d\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7acb\u523b\u7ea2\u4e86\u8138\u8fde\u8116\u5b50\u90fd\u7c97\u4e86\uff0c\u77aa\u773c\u9053\u201c\u4f60\u8bf4\u5979\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u53f9\u9053\u201c\u5979\u7684\u786e\u4e0d\u8db3\u4eba\u56e0\u4e3a\u50cf\u5979\u8fd9\u9ebd\u6837\u6709\u52c7\u6c14\u7684\u4eba\u6211\u8fd8\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u62cd\u624b\u9053\u201c\u70b9\u4e5f\u4e0d\u9519\u8fd9\u4e9b\u8bdd\u5979\u672c\u6765\u6839\u672c\u4e0d\u5fc5\u544a\u8bc9\u6211\u4eec\u7684\xb7\u4f46\u5979\u5374\u70b9\u4e5f\u6ca1\u6709\u9690\u7792\u8fd9\u79cd \u52c7\u6c14\u8c01\u80fd\u6bd4\u5f97\u4e0a\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e5f\u6bd4\u4e0d\u4e0a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u82e5\u6362\u4e86\u6211\xb7\u6211\u5012\u771f\u672a\u5fc5\u6562\u5c06\u8fd9\u79cd\u4e8b\u5f53\u9762\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u73b0\u5728\u603b\u8be5\u77e5\u9053\uff0c\u5973\u4eba\u5e76\u6ca1\u6709\u4f60\u60f3\u8c61\u4e2d\u90a3\u9ebd\u5dee\u52b2\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u975e\u4f46\u4e0d\u5dee\u52b2\u7b80\u76f4\u4f1f\u5927\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u773c\u5708\u53c8\u7ea2\u4e86\u9053\u201c\u4f60\u4eec\u2018\xb7\u2026\u4f60\u4eec\u771f\u7684\u90fd\u4e0d\u602a\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u602a\u4f60\uff1f\u8c01\u6562\u602a\u4f60\uff1f\u6211\u4eec\u7b80\u76f4\u5e94\u8be5\u8dea\u5382\u6765\u8ddf\u4f60\u78d5\u5934\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u82e5\u4e0d\u662f\u4f60\xb7\u6211\u4eec\u5c31\u7b97\u6ca1\u6709\u88ab\u6bd2\u6b7b\u4e5f\u997f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u5782\u4e0b\u5934\u9053\u201c\u5176\u5b9e\u6211\u54e5\u54e5\u4e5f\u5e76\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u201c\u4f60\u4e5f\u7528\u4e0d\u4e3a\u4ed6\u89e3\u91ca\uff0c\u6211\u4eec\u4e5f\u4e0d\u602a\u4ed6\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u771f\u7684\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u662f\u4ed6\u8bf4\u4e0d\u5b9a\u4e5f\u4f1a\u8fd9\u9ebd\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u505a\u5f97\u4e5f\u8bb8\u6bd4\u4ed6\u66f4\u51f6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u62c5\u5fc3\u4f60\u54e5\u54e5\xb7\u4ed6\u4ee5\u5f8c\u82e5\u77e5\u9053\u4f60\u8ddf\u4ed6\u6363\u86cb\u5b9a\u4f1a\u6c14\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u82e6\u7b11\u9053\u201c\u4ed6\u73b0\u5728\u5c31\u5df2\u77e5\u9053\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86\u5f81\u9053\u201c\u4ed6\u77e5\u9053\u5f8c\u600e\u9ebd\u6837\uff1f\uff1f \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6c14\u5f97\u8981\u547d\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u529e\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6211\u5c31\u6e9c\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u76b1\u7709\u9053\u201c\u4f46\u4f60\u8fdf\u65e9\u603b\u8981\u56de\u53bb\u7684\xb7\u90a3\u662f\u4f60\u7684\u5bb6\u3002\u201d \u3000\u3000\u6768\u6c5d\u7537\u53c8\u5782\u4e0b\u5934\uff0c\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u5979\u82e5\u56de\u53bb\xb7\u5f53\u7136\u4e00\u5b9a\u8981\u53d7\u7f6a\uff0c\u4f46\u662f\u5979\u5374\u53ef\u4ee5\u4e0d\u56de\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u5fae\u7b11\u9053\u201c\u4e2a\u5973\u5b69\u5b50\u5ac1\u4e86\u4eba\u4e4b\u5f8c\u5c31\u53ef\u4ee5\u4e0d\u5fc5\u56de\u5a18\u5bb6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u604d\u7136\u5931\u7b11\u9053\u201c\u4e0d\u9519\u5979\u82e5\u51fa\u4e86\u5ac1\uff0c\u5c31\u4e0d\u662f\u6885\u5bb6\u7684\u4eba\u4e86\uff0c\u5979\u54e5\u54e5\u5c31\u518d\u4e5f\u7ba1\u4e0d\u5979\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u5979\u5c31\u4e0d\u80fd\u4e0d\u8d76\u5feb\u51fa\u5ac1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5ac1\u7ed9\u8c01\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u60a0\u7136\u9053\u201c\u5f53\u7136\u662f\u5ac1\u7ed9\u5979\u559c\u6b22\u7684\u4eba\uff0c\u4e5f\u8bb8\u662f\u4f60\uff0c\u4e5f\u8bb8\u662f\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u6014\u4f4f\u5382\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u6885\u6c5d\u7537\u5728\u5077\u5077\u7684\u7b11\u3002 \u3000\u3000\u6885\u6c5d\u7537\u76f4\u5782\u5934\u7ea2\u8138\uff0c\u9759\u9759\u7684\u5750\u5728\u90a3\u91cc\xb7\u597d\u50cf\u5f88\u96be\u53d7\u3001\u5f88\u4f24\u5fc3\u7684\u6837\u5b50\xb7\u4f46\u7530\u89d2\u5374\u5df2\u60c5\u4e0d\u81ea \u7981\u9732\u51fa\u4e86\u5fae\u7b11\u3002\u5979\u7b11\u5f97\u5c31\u8c61\u662f\u53ea\u521a\u5077\u6765\u4e86\u516b\u53ea\u9e21\u7684\u5c0f\u72d0\u72f8\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u56db\u4e2a\u5927\u7537\u4eba\u5168\u90fd\u4e0a\u4e86\u5979\u7684\u5f53\u4e86\u3002\u5728\u8fd9\u79cd\u4e0b\u65e0\u8bba\u5979\u559c\u6b22\u7684\u4eba \u662f\u8c01\u770b\u6765\u90fd\u5df2\u975e\u5a36\u5979\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u5c0f\u72d0\u72f8\u5df2\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5c06\u4ed6\u4eec\u5168\u90fd\u5957\u4efb\u5957\u4f73\u4e86\u4ed6\u4eec\u7684\u8116\u5b50\xb7\u73b0\u5728\u53ea\u8981\u5979\u7684\u624b\u4e00\u63d0\uff0c\u5c31 \u6709\u4e2a\u4eba\u88ab\u5979\u540a\u8d77\u6765\u540a\u8f88\u5b50\u3002 \u3000\u3000\u201c\u770b\u6765\u4ea5\u4eba\u7684\u786e\u8981\u6bd4\u7537\u4eba\u60f3\u8c61\u4e2d\u806a\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u53ea\u4e0d\u8fc7\u5979\u60f3\u540a\u7684\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f \u3000\u3000\u738b\u52a8\u8fd8\u5728\u7b11\u7b11\u5f97\u4e5f\u8c61\u662f\u53ea\u72d0\u72f8\u8001\u72d0\u72f8\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u5df2\u77e5\u9053\u81ea\u5df1\u7edd\u4e0d\u4f1a\u88ab\u540a\u8d77\u6765\u7684\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u8fd8\u77e5\u9053\u4e00\u4e9b\u90ed\u5927\u8def\u4e0d\u77e5\u9053\u7684\u4e8b\u5ffd\u53c8\u7b11\u4e86\u7b11\xb7\u9053\u201c\u6211\u4eec\u8fd9\u4e9b\u4eba\u867d\u7136\u5e76\u4e0d\u662f\u4ec0\u9ebd\u5927\u82f1 \u65cf\u3001\u5927\u8c6a\u6770\uff0c\u4f46\u4e5f\u7edd\u4e0d\u662f\u5fd8\u601d\u8d1f\u4e49\u7684\u80c6\u5c0f\u9b3c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u6885\u59a8\u5a18\u82e5\u662f\u6709\u4ec0\u9ebd\u56f0\u96be\uff0c\u6211\u76ef\u5c31\u5b9a\u8981\u60f3\u6cd5\u5b50\u66ff\u5979\u89e3\u51b3\xb7\u5bf9\u4e0d\u5bf9\uff1f\u201d\u6797\u592a\u5e73 \u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u4ed6\u53c8\u662f\u7b2c\u4e2a\u62a2\u8bf4\u8bdd\u7684 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u201c\u5230\u5e95\u662f\u5e74\u9752\u4eba\u968f\u65f6\u968f\u5730\u90fd\u4f1a\u70ed\u60c5\u8fc7\u5ea6\uff0c\u522b\u4eba\u521a\u51c6\u5907\u597d\u7ef3\u5b50\u4ed6 \u5c31\u62a2\u5f80\u81ea\u5df1\u5934\u4e0a\u5957\u3002\u201d\u4ed6\u8fd9\u53e3\u6c14\u8fd8\u6ca1\u6709\u5b8c\u5168\u53f9\u51fa\u6765\uff0c\u5c31\u53d1\u89c9\u738b\u52a8\u5728\u77aa\u4ed6\u4eba\u9053\u201c\u4f60\u5462\uff1f\u4f60\u8bf4\u5bf9 \u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u8bf4\u4e0d\u5bf9\u4e5f\u4e0d\u884c\u53ea\u6068\u4e0d\u5f97\u627e\u4e2a\u9e21\u86cb\u585e\u5230\u738b\u52a8\u5634\u91cc\u53bb\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u4f60\u6839\u672c\u5c31\u4e0d\u5fc5\u95ee \u4e86\u82e5\u8bba\u8d77\u601c\u9999\u501f\u4e94\u3001\u89c1\u4e49\u52c7\u4e3a\u8fd9\u79cd\u79cd\u4e8b\uff0c\u5929\u4e0b\u8fd8\u6709\u8c01\u6bd4\u5f97\u4e0a\u7ff1\u5148\u751f\uff1f\u201d \u3000\u3000\u738b\u52a8\u70b9\u70b9\u5934\xb7\u597d\u50cf\u88ab\u71d5\u4e03\u8bf4\u5230\u5fc3\u91cc\u53bb\u4e86\uff0c\u6b63\u8272\u9053\u201c\u8fd9\u8bdd\u5012\u771f\u7684\u70b9\u4e5f\u4e0d\u5047\u4f46\u662f\u4f60\u5462\uff1f\uff1f \u3000\u3000\u71d5\u4e03\u7b11\u7b11\u8c08\u6de1\u9053\u201c\u53ea\u8981\u738b\u8001\u5927\u53e5\u8bdd\uff0c\u6211\u8fd8\u6709\u4ec0\u9ebd\u95ee\u9898\u201d \u3000\u3000\u738b\u52a8\u957f\u957f\u5410\u51fa\u53e3\u6c14\u5c55\u989c\u7b11\u9053\u201c\u6885\u59d1\u5a18\u6211\u4eec\u7684\u8bf4\u8bdd\uff0c\u4f60\u5168\u542c\u5230\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u4f4e\u5934\u4ece\u9f3b\u5b50\u91cc\u201c\u54bd\u201d\u4e86\u4e00\u58f0\uff0c\u8f7b\u5f97\u5c31\u597d\u50cf\u868a\u5b50\u53eb\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u9ebd\u4f60\u82e5\u6709\u4ec0\u9ebd\u56f0\u96be\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u5934\u5782\u5f97\u66f4\u4f4e\u2019\u526f\u53ef\u601c\u5df4\u5df4\u7684\u6837\u5b50\u8f7b\u8f7b\u9053\u201c\u6211\u4e0d\u597d\u610f\u601d\u8bf4\u201c\u738b\u52a8\u9053\u201c\u4f60\u53ea\u7ba1\u8bf4\u3002\u201d \u3000\u3000\u6885\u6b49\u7537\u80a0\u4e5f\u7ea2\u4e86\u663e\u5f97\u53c8\u53ef\u601c\xb7\u4e49\u96be\u4e3a\u60c5\u7684\u6837\u5b50\xb7\u8d39\u4e86\u534a\u5929\u52b2\u624d\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\u201c\u6211\u54e5\u54e5 \u53d1\u73b0\u6211\u8fd9\u9ebd\u505a\u7684\u65f6\u5019\xb7\u7b80\u76f4\u6c14\u5f97\u8981\u53d1\u75af\uff0c\u76f4\u903c\u6211\u95ee\u6211\u4e3a\u4ec0\u9ebd\u8981\u505a\u8fd9\u79cd\u4e8b\u4e3a\u4ec0\u9ebd\u5e2e\u5916\u4eba\u5bb3\u81ea\u5df2 \u7684\u54e5\u54e5\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u600e\u9ebd\u8bf4\uff1f\uff1f\u6885\u6c5d\u7537\u7684\u8138\u66f4\u7ea2\uff0c\u9053\u54e6\u60f3\u4e0d\u51fa\u522b\u7684\u8bdd\u8bf4\u53ea\u597d\u8bf4\u2026\u2026\u53ea\u597d\u8bf4\u2026\u2026\u53ea \u597d\u8bf4\u2026\u2026\u201d \u3000\u3000\u5979\u597d\u50cf\u5ffd\u7136\u62bd\u4e86\u7b4b\u8bf4\u6765\u8bf4\u53bb\u90fd\u53ea\u6709\u8fd9\u53c1\u4e2a\u5b87\u3002 \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u53d7\u4e0d\u4e86\uff0c\u5fcd\u6c34\u4f4d\u9053\u201c\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u7528\u529b\u54ac\u4e86\u54ac\u5634\u5507\xb7\u8c61\u662f\u4e0b\u5382\u5f88\u5927\u7684\u51b3\u5fc3\xb7\u7ea2\u8138\u9053\u201c\u6211\u53ea\u597d\u8bf4\u6211\u5e2e\u7684\u4e5f\u4e0d\u662f\u5916\u4eba\u3002 \u4ed6\u5c31\u95ee\xb7\u4e0d\u662f\u5916\u4eba\u662f\u4ec0\u9ebd\u4eba\u6211\u5c31\u53ea\u597d\u8bf4\u662f\u2500\u2500\u2500\u662f\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6211\u53ea\u597d\u8bf4\u662f\u4ed6\u7684\u59b9\u592b\u56e0\u4e3a\u6211\u5df2\u548c\u8fd9\u4eba\u8ba2\u4eb2\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\u5979\u597d\u50cf\u5168\u8eab\u90fd\u8f6f\u4e86\uff0c\u5dee\u70b9\u8dcc\u5230\u684c\u5b50\u5e95\u4e0b\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u5dee\u70b9\u6389\u5230\u684c\u5b50\u5e95\u4e0b\u53bb\u3002\u738b\u52a8\u7728\u773c\u9053\u201c\u4f60\u54e5\u54e5\u542c\u201c\u4f60\u8fd9\u8bdd\u53c8\u600e\u9ebd\u8bf4\u5462\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4ed6\u542c\u4e86\u8fd9\u8bdd\u6c14\u624d\u7b97\u5e73\u4e86\u4e9b\u4f46\u5374\u53c8\u8b66\u544a\u6211\u5047\u5982 \u3000\u3000\u738b\u52a8\u9053\u201c\u5e26\u4ec0\u9ebd\u56de\u53bb\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u54ac\u5634\u5507\u9053\u201c\u5e26\u4eba\u2026\u2500\u2606 \u3000\u3000\u738b\u52a8\u9053\u201c\u5e26\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u59b9\u2500\u2026\u59b9\u592b\u2026\u2026\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8c01\u7684\u59b9\u592b\uff1f\uff1f \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6211\u201d\u2026\u6211\u54e5\u54e5\u7684\u59b9\u592b\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u597d\u50cf\u6574\u4e2a\u4eba\u53c8\u5168\u90fd\u8f6f\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u4eba\u4e5f\u8f6f\u4e86\u3002 \u3000\u3000\u738b\u52a8\u53c8\u957f\u957f\u5410\u51fa\u53e3\u6c14\xb7\u597d\u50cf\u5230\u73b0\u5728\u624d\u603b\u7b97\u5f04\u6e05\u695a\u5979\u7684\u610f\u601d\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u800d\u5f04\u6e05\u695a\u4e00\u4e2a\u5973\u5b69\u5b50\u8bf4\u7684\u8bdd\u4e5f\u7684\u786e\u4e0d\u592a\u5bb9\u6613\u3002 \u3000\u3000\u738b\u52a8\u7b11\u9053\u201c\u770b\u6765\u73b0\u5728\u5df2\u53ea\u5269\u4e0b\u4e2a\u95ee\u9898\u5382\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4ec0\u9ebd\u95ee\u9898\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u8fd9\u56db\u4e2a\u4eba\u6e2f\u662f\u6885\u59cb\u5a18\u54e5\u54e5\u7684\u59b9\u592b\u5462\uff1f\u4ed6\u662f\u4e0d\u662f\u80af\u613f\u6885\u8e48\u59fb\u56de\u53bb\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8c01\u4f1a\u4e0d\u80af\uff1f\u96be\u9053\u4ed6\u5fcd\u5fc3\u770b\u6885\u59a8\u59d0\u88ab\u5979\u54e5\u54e5\u6d3b\u6d3b\u8ba2\u6b7b\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e07\u4e00\u6709\u4eba\u4e0d\u80af\u5462\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u90a3\u9ebd\u4ed6\u7b80\u76f4\u5c31\u4e0d\u80fd\u7b97\u662f\u6211\u201d\u7684\u670b\u53cb\u5bf9\u8fd9\u79cd\u4e0d\u662f\u670b\u53cb\u7684\u670b\u53cb\u6211\u4efd\u5c31\u7528\u4e0d\u5ba2\u6c14 \u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u629a\u638c\u9053\uff0c\u201c\u4e0d\u9519\u5c31\u7b97\u6709\u4eba\u4e0d\u80af\u53bb\u53e6\u5916\u7684\u53c1\u4e2a\u4eba\u4e5f\u5f97\u903c\u4ed6\u53bb\xb7\u4ed6\u4eec\u8d5e\u6210\u4e0d\u8d5e\u6210\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u2018\u201c\u8d5e\u6210\u3002\u201d \u3000\u3000\u738b\u52a8\u773c\u89d2\u8198\u90ed\u5927\u8def\u9053\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u53c8\u51b7\u51b7\u9053\u201c\u8fd9\u53e5\u8bdd\u4f60\u4e5f\u4e0d\u8be5\u95ee\u7684\u4f60\u96be\u9053\u5c06\u90ed\u5148\u751f\u770b\u6210\u4e86\u5fd8\u601d\u8d1f\u4e49\u7684\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\u201c\u90a3\u5c31\u597d\u6781\u4e86\uff0c\u73b0\u5728\u6240\u6709\u7684\u95ee\u9898\u90fd\u5df2\u89e3\u51b3\u4fae\u59a8\u5a18\u4f60\u8fd8\u7b49\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u5374\u504f\u504f\u8fd8\u8981\u8ba9\u4ed6\u4eec\u518d\u7b49\u7b49\u3002\u4ea5\u4eba\u597d\u50cf\u5929\u751f\u5c31\u559c\u6b22\u8ba9\u7537\u4eba\u6025\u3002 \u3000\u3000\u5979\u773c\u6b8a\u5b50\u4e0d\u505c\u7684\u8f6c\uff0c\u5728\u8fd9\u56db\u4e2a\u4eba\u8138\u8f6c\u6765\u8f6c\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u771f\u5e0c\u671b\u8fd9\u53cc\u773c\u6b8a\u4e0d\u8981\u505c\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u70b9\u4e5f\u4e0d\u8ba8\u538c\u8fd9\u4f0e\u201c\u9178\u6885\u6c64\uff3c\u4eca\u5929\u65e9\u4e0a\u5979\u6765\u7684\u65f6\u5019\uff0c\u82e5\u8bf4\u5979\u559c\u6b22\u7684\u662f\u522b\u4eba\u4e0d\u662f\u4ed6\uff0c \u4ed6\u5b9a\u4f1a\u6c14\u5f97\u8981\u547d\u3002 \u3000\u3000\u4f46\u559c\u6b22\u662f\u56de\u4e8b\xb7\u5a36\u5979\u505a\u8001\u5a46\u53c8\u662f\u53e6\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u88ab\u9000\u5a36\u5979\u505a\u8001\u5a46\xb7\u66f4\u662f\u4ef6\u5b8c\u5168\u4e0d\u540c\u7684\u4e8b\u5c31\u597d\u50cf\u4ed6\u867d\u7136\u559c\u6b22\u559d\u9152\uff0c\u4f46\u4e5f\u4e0d\u613f\u88ab\u4eba\u634f\u9f3b \u5b50\xb7\u62ff\u6d12\u5f80\u4ed6\u7530\u91cc\u64c5\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u671b\u8fd9\u4f4d\u9178\u6885\u6ca5\u7684\u773c\u775b\u6709\u6bdb\u75c5\u770b\u4e0a\u7684\u4e0d\u662f\u4ed6\u662f\u522b\u4eba\u3002 \u3000\u3000\u9178\u6885\u6c64\u7684\u5374\u504f\u504f\u8fde\u70b9\u6bdb\u75c5\u4e5f\u6ca1\u6709\uff0c\u800c\u4e14\u6b63\u5728\u4ed6\u3002 \u3000\u3000\u4e0d\u4f46\u5728\u76ef\u4ed6\u800c\u4e14\u8fd8\u5728\u7b11\u7b11\u5f97\u5f88\u751c\u72e0\u8ff7\u4eba\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u77e5\u9053\u81ea\u5df1\u5df2\u9493\u4e0a\u6761\u5927\u9c7c\u7684\u65f6\u5019\uff0c\u90fd\u4f1a\u7b11\u5f97\u5f88\u751c\u7684\u3002\u90ed\u5927\u8def\u4e5f\u60f3\u5bf9\u5979\u7b11\u7b11\u5374\u5b9e\u5728\u7b11 \u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5728\u53f9\u6c14\u201c\u7b97\u6211\u5012\u9709\uff0c\u8c01\u53eb\u6211\u957f\u5f97\u6bd4\u522b\u4eba\u5e05\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u5ffd\u7136\u9053\u201c\u6211\u7b54\u5e94\u8fc7\u6211\u51b3\u5b9a\u7684\u65f6\u5019\u5b9a\u7b2c\u4e00\u4e2a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8d2d\u95fb\u9053\u201c\u5176\u5b9e\u4f60\u4e5f\u7528\u4e0d\u5bf9\u6211\u592a\u5b88\u4fe1\xb7\u4ea5\u5b69\u5b50\u7b54\u5e94\u7684\u4e8b\u5e38\u5e38\u90fd\u4f1a\u5fd8\u8bb0\u7684\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u5ae1\u7136\u9053\u201c\u6211\u6ca1\u6709\u5fd8\u8bb0\u4f60\u8ddf\u6211\u51fa\u6765\u6211\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u7ad9\u8d77\u6765\u8d70\u51fa\u53bb\u811a\u6b65\u8f7b\u76c8\u5f97\u5c31\u8c61\u662f\u71d5\u5b50\u3002 \u3000\u3000\u53ea\u521a\u6349\u4f4f\u4e03\u516b\u6761\u5927\u6bdb\u7684\u71d5\u5b50\u3002 \u3000\u3000\u82e6\u5dee \u3000\u3000\u5979\u8d70\u5230\u95e8\u53e3\u8fd8\u8f6c\u56de\u5934\u5411\u90ed\u5927\u8def\u62db\u4e86\u62bd\u624b\u3002\u5979\u7684\u624b\u53c8\u767d\u53c8\u5ae9\u3002 \u3000\u3000\u4f60\u7684\u8116\u5b50\u5047\u5982\u5df2\u88ab\u53cc\u624b\u627c\u4f4f\u65e0\u8bba\u8fd9\u53cc\u624b\u591a\u9ebd\u767d\u591a\u9ebd\u5ae9\u90a3\u6ecb\u5473\u4e5f\u662f\u6837\u4e0d\u592a\u597d\u53d7\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u7ad9\u8d77\u6765\u770b\u770b\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u770b\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u5de5\u52a8\u3002 \u3000\u3000\u738b\u52a8\u5728\u559d\u9152\xb7\u9152\u676f\u6321\u4f4f\u4e86\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u7ff1\u5927\u8def\u770b\u770b\u6797\u592a\u5e73\u3002 \u3000\u3000\u6797\u592a\u5e73\u5728\u53d1\u6014\u3002 \u3000\u3000\u90ed\u5927\u8def\u54ac\u54ac\u7259\uff0c\u6068\u5f88\u9053\u201c\u6211\u7956\u5b97\u5b9a\u79ef\u4e86\u5fb7\xb7\u5426\u5219\u600e\u4f1a\u4ea4\u5230\u4f60\u4eec\u8fd9\u79cd\u597d\u670b\u53cb\u5462\u201d \u3000\u3000\u53ea\u542c\u6885\u6c5d\u7537\u5728\u95e8\u5916\u9053\u201c\u4f60\u5728\u8bf4\u4ec0\u9ebd\uff1f\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u9053\u201c\u6211\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u8bf4\u6211\u5728\u653e\u5c41\u3002\u201d \u3000\u3000\u4ed6\u603b\u7b97\u8d70\u4e86\u51fa\u53bb\u3002\u770b\u4ed6\u90a3\u6101\u7709\u82e6\u8138\u3001\u5782\u5934\u4e27\u6c14\u7684\u6837\u5b50\u5c31\u597d\u50cf\u88ab\u4eba\u62bc\u4e0a\u6cd5\u573a\u4f3c\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u5929\u6797\u592a\u5e73\u5ffd\u7136\u53f9\u4e86\u95e8\u6c14\u54fa\u7483\u9053\u201c\u60f3\u4e0d\u5230\u8fd9\u4eba\u539f\u6765\u4e5f\u4f1a\u88c5\u849c\u7684\uff0c\u5fc3\u91cc\u660e\u660e\u559c\u6b22\u5f97\u8981 \u547d\xb7\u5374\u504f\u504f\u8981\u88c5\u51fa\u8fd9\u79cd\u6101\u7709\u82e6\u80c6\u7684\u8981\u4e8e\u53eb\u4eba\u770b\u751f\u6c14\u3002\u201d \u3000\u3000\u4ed6\u53e3\u6c14\u597d\u50cf\u6709\u70b9\u9178\u6db8\u6e9c\u7684\uff0c\u809a\u5b50\u91cc\u7684\u9152\u597d\u50cf\u5168\u90fd\u53d8\u6210\u4e86\u9178\u3002 \u3000\u3000\u738b\u52a8\u7b11\u4e86\u9053\u201c\u4f60\u5f04\u9519\u4e86\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u5fc3\u91cc\u5e76\u4e0d\u559c\u6b22\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u4e0d\u559c\u6b22\uff1f\u6885\u59cb\u5a18\u96be\u9053\u8fd8\u914d\u4e0d\u4e0a\u4ed6\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u914d\u4e0d\u914d\u5f97\u4e0a\u662f\u56de\u4e8b\xb7\u559c\u4e0d\u559c\u6b22\u53c8\u662f\u53e6\u5916\u56de\u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u4ed6\u4e0d\u559c\u6b22\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4ed6\u8fd8\u6ca1\u6709\u53d8\u6210\u679c\u5b50\u4e5f\u6ca1\u6709\u53d8\u6210\u54d1\u5df4\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7728\u7728\u773c\u4ed6\u542c\u4e0d\u5e62\u3002 \u3000\u3000\u738b\u52a8\u4e5f\u77e5\u9053\u4ed6\u542c\u4e0d\u61c2\uff0c\u6240\u4ee5\u53c8\u89e3\u91ca\u9053\u201c\u6709\u4e2a\u5f88\u806a\u660e\u7684\u4eba\u8bf4\u8fc7\u53e5\u5f88\u6709\u9053\u7406\u7684\u8bdd\xb7\u4ed6\u8bf4\xb7\u65e0 \u8bba\u591a\u806a\u660e\u7684\u4eba\u82e5\u662f\u771f\u7684\u559c\u6b22\u4e03\u4e00\u4e2a\u5973\u4eba\u4ed6\u5728\u5979\u9762\u524d\u4e5f\u2018\u5b9a\u4f1a\u53d8\u5f97\u5446\u5934\u5446\u8111\u7684\u751a\u81f3\u8fde\u8bdd\u90fd\u8bf4\u4e0d \u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u6709\u610f\u65e0\u610f\u95ee\u5411\u71d5\u4e03\u770b\u4e86\u770b\u7b11\u9053\u201c\u4f46\u4ed6\u5728\u6885\u59cb\u5a18\u9762\u524d\u8bf4\u7684\u8bdd\u8fd8\u662f\u6bd4\u522b\u4eba\u591a\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u6253\u65ad\u5382\u4ed6\u7684\u8bdd\u51b7\u51b7\u9053\u201c\u8fd9\u53ea\u56e0\u6709\u7684\u4eba\u5929\u751f\u5c31\u662f\u591a\u5634\u5a46\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u7b11\uff0c\u4e0d\u8bf4\u8bdd\u5382\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u613f\u610f\u505a\u591a\u5634\u5a46\u5e73\u65f6\u4e5f\u6ca1\u6709\u4eba\u4f1a\u8ba4\u4e3a\u4ed6\u662f\u591a\u7530\u5a46\u4f46\u4eca\u5929\u4ed6\u5374\u597d\u50cf\u6709\u70b9\u53d8\u4e86\u8bf4\u7684\u8bdd\u81f3 \u5c11\u6bd4\u5e73\u65f6\u591a\u597d\u51e0\u500d\u3002 \u3000\u3000\u6797\u592a\u4e4e\u672c\u5c31\u5728\u5947\u602a\u201c\u8fd9\u5165\u4eca\u5929\u4e3a\u4ec0\u9ebd\u53d8\u5f97\u5982\u6b64\u591a\u5634\uff1f\u8fd9\u4e9b\u8bdd\u7a76\u7adf\u662f\u8bf4\u7ed9\u8c01\u542c\u7684\uff1f \u3000\u3000\u6797\u592a\u5e73\u53ea\u77e5\u9053\u4ef6\u4e8b\u82e5\u6ca1\u6709\u7279\u522b\u7684\u539f\u56e0\u738b\u52a8\u8fde\u5634\u90fd\u61d2\u5f97\u52a8\u3002 \u3000\u3000\u6708\u5149\u5f88\u7f8e\u3002 \u3000\u3000\u4e5f\u8bb8\u5f88\u5c11\u6709\u4eba\u4f1a\u6ce8\u610f\u5230\xb7\u4f46\u51ac\u5929\u7684\u6708\u5149\u5e76\u4e0d\u5b9a\u5c31\u4e0d\u5982\u6625\u5929\u7684\u6708\u5149\u90a3\u9ebd\u52a8\u4eba\uff0c\u51b7\u5929\u7684\u6708\u5149 \u4e5f\u6837\u80fd\u6253\u52a8\u5c11\u5973\u7684\u5fc3\u3002 \u3000\u3000\u5706\u5706\u7684\u6708\u4eae\u6302\u5728\u6811\u68a2\uff0c\u6885\u6c5d\u7537\u5c31\u7ad9\u591c\u6811\u4e0b\u3002\u6708\u5149\u7167\u5979\u7684\u8138\uff0c\u5979\u7684\u773c\u775b\u3002 \u3000\u3000\u5979\u7684\u773c\u9677\u6bd4\u6708\u5149\u66f4\u7f8e\u3002 \u3000\u3000\u5c31\u8fde\u90ed\u5927\u8def\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u5979\u7684\u786e\u662f\u4e2a\u5f88\u597d\u770b\u7684\u5973\u5b69\u5b50\xb7\u5c24\u5176\u662f\u5979\u7684\u8eab\u6750\xb7\u90ed\u5927\u8def\u51e0\u4e4e\u4ece \u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u8eab\u6750\u8fd9\u9ebd\u597d\u7684\u5973\u5b50\u3002\u5979\u597d\u50cf\u6bd4\u90ed\u5927\u8def\u7b2c\u6b21\u770b\u5230\u5979\u7684\u65f6\u5019\u66f4\u6f02\u4eae\u4e86\u8fd9\u4e5f\u8bb8\u662f\u56e0\u5979 \u7684\u8863\u670d\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5979\u7684\u7b11\u3002 \u3000\u3000\u5979\u4eca\u5929\u7a7f\u7684\u4e0d\u518d\u662f\u60f3\u5e03\u8863\u670d\u7a84\u7a84\u7684\u8170\u8eab\xb7\u957f\u957f\u7684\u88d9\u5b50\u886c\u5f97\u5979\u7684\u80fa\u66f4\u7ec6\u98ce\u59ff\u66f4\u8ff7\u4eba\u3002 \u3000\u3000\u5979\u53c8\u5728\u770b\u90ed\u5927\u8def\u7b11\xb7\u7b11\u5f97\u66f4\u751c\u3002\u2606 \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u6700\u6b23\u8d4f\u5979\u7684\u7b11\u73b0\u5728\u5374\u51e0\u4e4e\u8fde\u770b\u90fd\u4e0d\u6562\u53bb\u770b\u4e00\u773c\u3002 \u3000\u3000\u4ea5\u5b69\u4e8e\u7684\u7b11\u5c31\u8c61\u662f\u5979\u7684\u8863\u670d\u9996\u9970\u3001\u59fb\u8102\u82b1\u7c89\u6837\u5168\u90fd\u662f\u5979\u4eec\u7528\u6765\u8bf1\u7537\u5b50\u91cf\u94a9\u7684\u9975\u3002\u806a\u660e\u7684 \u7537\u4eba\u5458\u597d\u8fde\u770b\u90fd\u4e0d\u8981\u770b\u3002 \u3000\u3000\u90ed\u5927\u8def\u90a3\u5929\u82e5\u5df2\u5e62\u5f97\u8fd9\u9053\u7406\u4eca\u5929\u53c8\u6028\u4f1a\u8574\u4e0a\u8fd9\u9ebd\u591a\u9ebb\u9885 \u3000\u3000\u4ed6\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u4fb5\u541e\u541e\u7684\u8d70\u8fc7\u53bb\u5ffd\u7136\u9053\u201c\u4f60\u54e5\u54e5\u771f\u7684\u9152\u91cf \u3000\u3000\u6885\u6c5d\u7537\u7b11\u9053\u201c\u5047\u7684\uff0c\u4ed6\u5e73\u5e38\u6839\u672c\u5f88\u5c11\u559d\u9152\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u90a3\u5c31\u66f4\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6709\u4ec0\u9ebd\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u672c\u6765\u8fd8\u60f3\u89c1\u9762\u5c31\u5148\u60f3\u6cd5\u5b50\u628a\u4ed6\u7a46\u9189\u7684\u514d\u5f97\u4ed6\u60f3\u8d77\u6628\u5929\u7684\u4e8b\u6545\u610f\u627e\u6211\u7684\u9ebb \u70e6\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u8d50\u7136\u9053\u201c\u4f60\u82e5\u6015\u4ed6\u627e\u4f60\u9ebb\u70e6\u4e0d\u59a8\u8eb2\u4ed6\u4e9b\xb7\u7b49\u8fc7\u51e0\u5929\u4ed6\u7684\u6c14\u5e73\u4e86\u5f8c\uff0c\u518d\u53bb\u89c1 \u4ed6\u3002\u201d\uff01 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e0d\u662f\u6025\u8981\u6211\u56de\u53bb\u89c1\u4ed6\u5417\uff1f\uff1f \u3000\u3000\u6885\u6c5d\u7537\u9650\u775b\u5ffd\u7136\u77aa\u5f97\u5f88\u5927\u77aa\u4ed6\xb7\u9053\u201c\u4f60\u4ee5\u4e3a\u201c\u2026\u2019\u4f60\u4ee5\u4e3a \u3000\u3000\u5979\u5ffd\u7136\u7b11\u4e86\u7b11\u5f97\u5f13\u4e86\u8170\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u4f4f\xb7\u9650\u775b\u4e5f\u5df2\u53d1\u76f4\u4e5f\u5728\u77aa\u5979\u5450\u5632\u9053\u201c\u4e0d\u662f\u6211 \u3000\u3000\u6885\u6c5d\u7537\u7b11\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\uff0c\u53ea\u80fd\u4e0d\u505c\u7684\u6447\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u4e0d\u662f\u6211\u662f\u8c01\u201d \u3000\u3000\u6885\u6c5d\u7537\u597d\u4e0d\u5bb9\u6613\u505c\u4f4f\u7b11\u5598\u53e3\u6c14\u9053\u201c\u662f\u9884\u4e03\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53eb\u4e86\u8d77\u6765\u9053\u201c\u71d5\u4e03\uff1f\u2019\u4e00\u201d\u4f60\u770b\u4e0a\u7684\u4eba\u662f\u71d5\u4e03\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u771f\u7684\u5f81\u4f50\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u6839\u672c\u5c31\u4e0d\u60f3\u8e6c\u6885\u6c5d\u7537\u6210\u4eb2\u6839\u672c\u5c31\u4e0d\u60f3\u8ddf\u4efb\u4f55\u4eba\u6210\u4eb2\u3002 \u3000\u3000\u6885\u6c5d\u7537\u770b\u4e0a\u7684\u65e2\u7136\u4e0d\u662f\u4ed6\u4ed6\u672c\u8be5\u5927\u5927\u7684\u677e\u53e3\u6c14\u89c9\u5f97\u5f88\u5f00\u5fc3\u7259\u5bf9\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\u73b0\u5728\u4ed6\u5ffd\u7136\u53c8\u89c9\u5f97\u5f88\u96be\u53d7\u3001\u5f88\u5931\u671b\xb7\u751a\u81f3\u6709\u70b9\u9178\u6e9c\u6e9c\u3002\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d \u5c06\u95e8\u9178\u6c14\u5410\u51fa\u6765\u6447\u5934\u95fb\u95fb\u9053\u201c\u6211\u5b9e\u5728\u4e0d\u4f0d\u4f60\u600e\u9ebd\u4f1a\u770b\u4e0a\u4ed6\u7684\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u773c\u6ce2\u6d41\u52a8\u7b11\u9053\u201c\u6211\u89c9\u5f97\u4ed6\u5f88\u597d\u6837\u6837\u90fd\u597d\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u8fde\u4e0d\u6d17\u6fa1\u90a3\u6837\u4e5f\u597d\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6709\u4e2a\u6027\u7684\u7537\u4eba\uff0c\u5728\u6ca1\u6709\u6210\u4eb2\u7684\u65f6\u5019\u5e38\u5e38\u90fd\u4e0d\u4fee\u8fb9\u9609\u7684\uff0c\u4f46\u7b49\u5230\u6709\u4e86\u4e2a\u59bb\u5b50\u7167 \u987e\u4ed6\u7684\u65f6\u95f4\u4ed6\u5c31\u4f1a\u53d8\u4e86\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u53d1\u5149\u5c31\u8c61\u505a\u68a6\u4f3c\u7684\xb7\u75f4\u75f4\u7684\u7b11\u9053\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4ece\u5c0f\u5c31\u559c\u6b22\u8fd9\u79cd\u4e0d\u62d8\u5c0f\u8282\u7684\u7537\u4eba\u8fd9 \u79cd\u4eba\u624d\u771f\u7684\u6709\u7537\u5b50\u6c14\u3002\u90a3\u79cd\u6210\u5929\u6253\u626e\u5f97\u6cb9\u5934\u7c89\u8138\u7684\u7537\u4eba\u6211\u770b\u5c31\u8981\u5410\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\u7684\u773c\u775b\u5ffd\u7136\u89c9\u5f97\u8fd9\u53cc\u773c\u775b\u7b80\u76f4\u70b9\u4e5f\u4e0d\u7f8e\u7b80\u76f4\u5c31\u597d\u50cf\u5708\u5b50\u7684\u773c\u775b\u4e00\u6837\u3002 \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6211\u4e5f\u77e5\u9053\u4ed6\u603b\u662f\u5728\u8eb2\u6211\uff0c\u597d\u50cf\u5f88\u8ba8\u538c\u6211\xb7\u5176\u5b9e\u771f\u6b63\u6709\u6027\u683c\u7684\u7537\u4eba\u90fd\u662f\u8fd9\u6837\u5b50 \u7684\u3002\u90a3\u79cd\u4e00\u89c1\u4e86\u5973\u4eba\u5c31\u8c61\u82cd\u7ef3\u89c1\u4e86\u8840\u7684\u7537\u4eba\xb7\u6211\u66f4\u8ba8\u538c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u8138\u597d\u50cf\u6709\u70b9\u53d1\u70ed\xb7\u4e7e\u54b3\u4e86\u51e0\u58f0\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u4f60\u662f\u771f\u7684\u62a5\u559c\u6b22\u4ed6\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4f60\u8fde\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u6211\u53ea\u89c9\u5f97\u4f60\u597d\u50cf\u8ddf\u6211\u7279\u522b\u4eb2\u70ed\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u5a9a\u7136\u9053\u201c\u90a3\u4e0d\u8fc7\u662f\u6211\u6545\u610f\u9017\u4ed6\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u65e2\u7136\u559c\u6b22\u4ed6\uff0c\u4e3a\u4ec0\u9ebd\u53cd\u800c\u8981\u9017\u4ed6\u751f\u6c14\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5c31\u56e0\u4e3a\u6211\u559c\u6b22\u4ed6\u6240\u4ee5\u624d\u8981\u9017\u4ed6\u751f\u6c14\xb7\u8fd9\u9053\u7406\u4f60\u4e5f\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u8fd9\u9ebd\u6837\u770b\u6765\u4e2a\u7537\u4eba\u8fd8\u662f\u83ab\u8981\u88ab\u5973\u4eba\u770b\u4e0a\u7684\u597d\uff0c\u82e5\u662f\u6c38\u8fdc\u90fd\u6ca1\u6709\u5973\u4eba\u770b\u4e0a \u4ed6\uff0c\u4ed6\u6d3b\u5f97\u53cd\u800c\u5f00\u5fc3\u4e9b\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u7728\u773c\uff0c\u9053\u201c\u4f60\u73b0\u5728\u5f88\u5f00\u5fc3\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u5f88\u5f00\u5fc3\u7b80\u76f4\u5f00\u5fc3\u6781\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8d70\u8fdb\u6765\u7684\u65f6\u5019\u5c31\u7b97\u778e\u4e8e\u4e5f\u80fd\u770b\u5f97\u51fa\u4ed6\u70b9\u4e5f\u4e0d\u5f00\u5fc3\u3002 \u3000\u3000\u5047\u5982\u4ed6\u51fa\u53bb\u7684\u65f6\u5019\u770b\u6765\u8c61\u662f\u4e2a\u88ab\u62bc\u300f\u2606\u6cd5\u573a\u7684\u56da\u72af\u90a3\u9ebd\u4ed6\u73b0\u5728\u8fd9\u6837\u5b50\u770b\u6765\u7b80\u76f4\u5c31\u8c61\u662f\u4e2a \u6b7b\u4eba\u3002\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u6bd4\u6b7b\u4eba\u591a\u53e3\u6c14\u800c\u5df2\u3002 \u3000\u3000\u4e00\u5927\u66f0\u53c8\u9178\u53c8\u82e6\u7684\u51a4\u6c14\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u60c5\u51b5\u51e0\u4e4e\u8fd8\u662f\u548c\u4ed6\u521a\u79bb\u5f00\u5b8c\u5168\u4e00\u6837\u738b\u52a8\u8fd8\u662f\u5728\u559d\u9152\u6797\u592a\u5e73\u8fd8\u662f\u76f4\u53d1\u6014\u71d5\u4e03\u8fd8\u662f \u6545\u610f\u88c5\u4f5c\u770b\u4e0d\u89c1\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u628a\u738b\u52a8\u624b\u91cc\u7684\u9152\u676f\u62a2\u4e86\u8fc7\u6765\u5927\u58f0\u9053\u201c\u4f60\u4eca\u5929\u600e\u9ebd\u56de\u4e8b\uff1f\u53d8\u6210\u4e86\u4e2a\u9152\u5b50\u5417\uff1f\u201d \u3000\u3000\u571f\u52a8\u7b11\u7b11\u9053\u201c\u597d\u670b\u53cb\u7684\u559c\u9152\u5f53\u7136\u8981\u591a\u559d\u300f\u676f\u5728\u4f60\u96be\u9053\u820d\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u4e5f\u60f3\u7b11\u7b11\u7684\u5374\u7b11\u4e0d\u51fa\u6765\uff0c\u7528\u670d\u89d2\u819c\u71d5\u4e03\uff0c\u9053\uff0c\u201c\u8fd9\u91cc\u5012\u7684\u786e\u6709\u4e2a\u65b0\u90ce\u4f70\uff0c\u4f46 \u5374\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u738b\u52a8\u597d\u50cf\u5e76\u4e0d\u89c9\u5f97\u610f\u5916\u53ea\u8c08\u8c08\u7684\u95ee\u9053\u201c\u4e0d\u662f\u4f60\u662f\u8c01\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u5df2\u8f6c\u8fc7\u8eab\u77aa\u5927\u4e86\u773c\u775b\xb7\u770b\u71d5\u4e03\u3002\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u770b\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u770b\u4f60\u3002\u201d \u3000\u3000\u9893\u4e03\u51b7\u7b11\u8c0a\u201c\u6211\u6709\u4ec0\u9ebd\u597d\u770b\u7684\u4f60\u53ea\u4f2f\u770b\u9519\u4e86\u4eba\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6211\u6b63\u662f\u60f3\u627e\u51fa\u4f60\u8fd9\u4eba\u7a76\u7adf\u6709\u751a\u9ebd\u597d\u770b\u7684\u5730\u65b9\uff0c\u4f1a\u6709\u4eba\u770b\u4e0a\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u5374\u76b1\u7709\u9053\u201c\u8c01\u770b\u4e0a\u4e86\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8c0a\u201c\u65b0\u5a18\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u5f00\u59cb\u6709\u70b9\u5403\u60ca\u4e86\u8c0a\u201c\u65b0\u5a18\u5b50\u8ddf\u6211\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u603b\u7b97\u7b11\u4e86\u7b11\u9053\u201c\u65b0\u5a18\u5b50\u82e5\u662f\u8ddf\u65b0\u90ce\u68fa\u6ca1\u6709\u5173\u7cfb\u8dea\u8c01\u6709\u5173\u7cfb\uff1f\u201d \u3000\u3000\u85cf\u4e03\u7684\u773c\u775b\u4e5f\u77aa\u4e86\u8d77\u6765\u9053\u201c\u8c01\u662f\u65b0\u90ce\u68fa\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u3002\u201d \u3000\u3000\u85d0\u4e03\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u5f00\u59cb\u65f6\u4ed6\u663e\u5f97\u5f88\u5403\u60ca\u5f8c\u8d77\u5ffd\u7136\u53d8\u5f97\u5f88\u6b22\u559c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u51fa\u6765\xb7\u5c31\u597d\u50cf\u9762\u524d\u5ffd\u7136\u6389\u4e0b\u4e2a \u5927\u5143\u5b9d\u4f3c\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\xb7\u9053\u201c\u539f\u6765\u4f60\u4e5f\u5f88\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e0d\u8bf4\u8bdd\xb7\u76f4\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u82e5\u662f\u4e0d\u559c\u6b22\u5979\xb7\u4e3a\u4ec0\u9ebd\u7b11\u5f97\u8fd9\u9ebd\u5f00\u5fc3\uff1f\u201d \u3000\u3000\u71d5\u4e03\u4e0d\u56de\u7b54\u53cd\u95ee\u9053\u201c\u5979\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6de1\u8c08\u9053\u201c\u6b63\u5728\u9662\u5b50\u91cc\u7b49\u65b0\u5373\u4fe1\u4f60\u6700\u597d\u4e0d\u8981\u520a\u2606\u5979\u7b49\u5f97\u592a\u6025\u3002\u201d \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u8ba9\u5979\u7b49\u90ed\u5927\u8def\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u4ed6\u5df2\u8155\u8d77\u6765\u51b2\u5382\u51fa\u4e91\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\xb7\u6162\u6162\u7684\u6447\u5934\xb7\u95fb\u54fa\u9053\u201c\u770b\u6765\u65b0\u90ce\u60f0\u6bd4\u65b0\u8155\u5b50\u8fd8\u6025\u201c\u7389\u52a8\u5ffd\u7136\u7b11\u9053\u201c\u4f60\u662f\u4e0d \u662f\u5f88\u4e0d\u670d\u6c14\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u5382\u4ed6\u773c\u51b7\u51b7\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u6709\u4ec0\u9ebd\u597d\u5947\u602a\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u5947\u602a\u4e3a\u751a\u9ebd\u6bcf\u4e2a\u5973\u4eba\u7684\u9650\u9677\u90fd\u6bdb\u75c5\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8ba4\u4e3a\u8fd9\u59a8\u5a18\u5c0f\u773c\u770b\u4e0a\u71d5\u4e03\u7684\uff1f\u4f60\u8ba4\u4e3a\u4ed6\u5f88\u4e11\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\uff0c\u9053\u201c\u5176\u5b9e\u4ed6\u4e5f\u4e0d\u80fd\u53eb\u505a\u592a\u4e11\xb7\u81f3\u5c11\u4ed6\u7684\u773c\u775b\u5e76\u4e0d\u4e11\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\u71d5\u4e03\u7684\u773c\u775b\u975e\u4f46\u4e0d\u4e11\u800c\u4e14\u5f88\u597d\u770b\u5c24\u5176\u662f\u5728\u773c\u775b\u5e26\u7b11\u610f\u7684\u65f6\u5019\xb7\u770b\u6765\u5c31\u8c61\u662f\u6625\u98ce\u4e2d \u6e05\u6f88\u7684\u6e56\u6c34\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u9f3b\u5b50\u5f88\u4e11\u5417\u201d\uff1f \u3000\u3000\u90ed\u5927\u8def\u53c8\u60f3\u4e86\u60f3\u9053\u201c\u4e5f\u4e0d\u7b97\u662f\u5f88\u4e11\xb7\u53ea\u4e0d\u8fc7\u7b11\u8d77\u6765\u7684\u65f6\u5019\u5c31\u8c61\u4e2a\u8089\u5305\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u7684\u65f6\u5019\u9f3b\u5b50\u603b\u662f\u8981\u5148\u8f7b\u8f7b\u7684\u76b1\u8d77\u6765\xb7\u4f46\u90a3\u975e\u4f46\u4e0d\u8c61\u4e2a\u5305\u5b50\uff0c\u800c\u4e14\u53cd\u663e\u5f97\u5f88\u4fcf\u76ae\u5f88 \u597d\u770b\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u7684\u7530\u72e0\u4e11\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u9053\u201c\u6211\u5f88\u5c11\u770b\u5230\u4ed6\u7684\u8d61\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4ed6\u7684\u5207\u597d\u50cf\u6bd4\u91d1\u6bdb\u72ee\u5b50\u72d7\u7684\u5634\u8fd8\u8981\u5c0f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5c0f\u5634\u5f88\u96be\u770b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u6414\u6414\u5934\xb7\u56e0\u4e3a\u4ed6\u5e76\u4e0d\u662f\u4e2a\u4f1a\u6627\u826f\u5fc3\u8bf4\u8bdd\u7684\u4eba\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u4ec0\u9ebd\u5730\u65b9\u96be\u770b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u53d1\u89c9\u71d5\u4e03\u4ece\u5934\u5230\u811a\u5b9e\u5728\u90fd\u957f\u5f97\u5f88\u597d\u3002 \u3000\u3000\u5c31\u8fde\u4ed6\u90a3\u53cc\u810f\u4e2d\u4e2d\u7684\u624b\uff0c\u90fd\u6bd4\u522b\u4eba\u957f\u5f97\u79c0\u6c14\u4e9b\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4ed6\u82e5\u662f\u5e38\u6d17\u6d17\u64cd\xb7\u4e5f\u8bb8\u5e76\u4e0d\u662f\u4e2a\u72e0\u96be\u770b\u7684\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u53c8\u7b11\u4e86\u9053\u201c\u82e5\u771f\u7684\u4e0d\u6d17\u4e2a\u6fa1\u4f60\u4e5f\u8bb8\u4f1a\u5413\u7792\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\uff0c\u9053\u201c\u6211\u5012\u771f\u5e0c\u671b\u4ed6\u4ec0\u9ebd\u65f6\u5019\u80fd\u8ba9\u6211\u5413\u4e00\u8df3\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u65e2\u7136\u4e5f\u89c9\u5f97\u4ed6\u4e0d\u9519\xb7\u90a3\u9ebd\u6885\u59d1\u5a18\u770b\u4e0a\u4ed6\xb7\u53c8\u6709\u4ec0\u9ebd\u4e0d\u5bf9\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u5bf9\u5bf9\u6781\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u542c\u5230\u9662\u5b50\u91cc\u53d1\u51fa\u4e00\u58f0\u5c16\u53eb\u3002 \u3000\u3000\u662f\u62d6\u6c5d\u7537\u5728\u53eb\u53eb\u5f97\u5c31\u8c61\u4e00\u4e2a\u88ab\u4eba\u8e29\u5230\u5c3e\u5df4\u7684\u732b\u3002 \u3000\u3000\u90ed\u5927\u8def\u7eb3\u8d77\u6765\uff0c\u8c61\u662f\u60f3\u51fa\u53bb\u770b\u770b\u5374\u53c8\u5750\u4e0b\u64ad\u5934\u7b11\u9053\u201c\u6211\u77e5\u9053\u65b0\u90ce\u68fa\u90fd\u5f88\u6025\u5374\u8fd8\u662f\u6ca1\u60f3\u5230 \u71d5\u4e03\u4f1a\u6025\u5f97\u8fd9\u9ebd\u5389\u5bb3\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\u5c31\u770b\u5230\u71d5\u4e03\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65b0\u5a18\u5b50\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6ca1\u6709\u65b0\u5a18\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u65b0\u90ce\u68fa\uff0c\u5c31\u6709\u65b0\u5a18\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e5f\u6ca1\u6709\u65b0\u90ce\u9986\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\xb7\u5ffd\u53c8\u7b11\u4e86\u9053\u201c\u65b0\u5a18\u65bc\u662f\u4e0d\u662f\u5df2\u7ecf\u88ab\u65b0\u90ce\u68fa\u5413\u9898\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u71d5\u4e03\u8138\u4e0a\u6709\u53c1\u6761\u957f\u957f\u7684\u6307\u7532\u5370\uff0c\u5c31\u597d\u50cf\u662f\u88ab\u732b\u6293\u7684\u3002 \u3000\u3000\u71d5\u4e03\u5374\u70b9\u4e5f\u4e0d\u5728\u610f\u53cd\u9762\u597d\u50cf\u72e0\u4f2f\u5feb\uff0c\u7728\u9650\xb7\u7b11\u9053\u201c\u5979\u7684\u786e\u5df2\u7ecf\u8d70\u4e86\u4f46\u5374\u4e0d\u662f\u88ab\u6211\u5413\u8d70 \u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\uff1f\u4f60\u6ca1\u6709\u52a8\u624b\u52a8\u811a\u5979\u4e3a\u4ec0\u9ebd\u4f1a\u53eb\uff1f\u201d \u3000\u3000\u62e5\u4e03\u7b11\u7b11\xb7\u9053\u201c\u6211\u82e5\u771f\u7684\u52a8\u624b\u52a8\u811a\uff0c\u5979\u8fd8\u4f1a\u8d70\u5417\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u627f\u8ba4\u201c\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u77e5\u9053\u4e2a\u5973\u4eba\u82e5\u662f\u559c\u6b22\u4e86\u4e00\u4e2a\u7537\u4eba\u65f6\uff0c\u5c31\u4f2f\u4ed6\u4e0d\u52a8\u624b\u52a8\u5706\u3001 \u3000\u3000\u201c\u53ef\u662f\u5979\u4e3a\u4ec0\u9ebd\u8981\u8d70\u5462\uff1f\u201d \u3000\u3000\u85fb\u4e03\u9053\u201c\u56e0\u4e3a\u5979\u5ffd\u7136\u6539\u53d8\u4e86\u4e3b\u610f\u4e0d\u60f3\u613f\u7ed9\u6211\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u609f\u7136\u9053\u201c\u5979\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u600e\u9ebd\u4f1a\u7684\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u2026\u3002\uff0e\u56e0\u4e3a\u6211\u5bf9\u5979\u8bf4\u4e86\u53e5\u8bdd\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u6447\u5934\u9053\u201c\u6211\u4e0d\u60c5\u4e2a\u5973\u4eba\u82e5\u5df2\u6253\u5b9a\u4e86\u4e3b\u610f\u8981\u5ac1\u7ed9\u4f60\u4f60\u5c31\u7b97\u8bf4\u53c1\u5e72\u516d\u767e\u53e5\u8bdd\uff0c\u5979\u4e5f\u4e0d \u4f1a\u6539\u53d8\u4e3b\u610f\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u9053\u201c\u4f60\u51e0\u65f6\u770b\u8fc7\u6709\u4eba\u80af\u8ba9\u5df4\u9493\u4e0a\u624b\u7684\u9c7c\u6c8f\u8d70\u7684\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u4e5f\u8bb8\u5979\u5ffd\u7136\u53d1\u73b0\u8fd9\u6761\u9c7c\u523a\u592a\u591a\uff0c\u4e5f\u8bb8\u5979\u6839\u672c\u4e0d\u559c\u6b22\u4e34\u5371\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u5929\u5382\u6ca1\u6709\u4e2a\u559c\u6b22\u5403\u9c7c\u7684\u732b\u3002\u201d\u71d5\u4e03\u9053\u5979\u4e0d\u662f\u732b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\u7684\u9669\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u732b\u600e\u9ebd\u4f1a\u6293\u4eba\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u77e5\u9053\u5973\u4eba\u4e0d\u4f46\u4e5f\u4f1a\u6293\u4eba\uff0c\u800c\u4e14\u6293\u8d77\u4eba\u6765\u6bd4\u732b\u8fd8\u51f6\u3002 \u3000\u3000\u732b\u6293\u4eba\u603b\u8fd8\u6709\u4e2a\u7406\u7531\xb7\u5973\u4eba\u5374\u4e2d\u540c\u3002 \u3000\u3000\u5979\u9ad8\u5174\u6293\u4f60\u5c31\u6293\u4f60\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u4ef6\u4e8b\u60f3\u4e0d\u901a\u201c\u4f60\u7a76\u7adf\u662f\u7528\u4ec0\u9ebd\u6cd5\u5b50\u8ba9\u5979\u6539\u53d8\u4e3b\u610f\u7684\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4ec0\u9ebd\u6cd5\u4e8e\u4e5f\u6ca1\u6709\u7528\uff0c\u53ea\u4e0d\u8fc7\u8bf4\u4e86\u53e5\u8bdd\u800c\u5df1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8bf4\u7684\u4ec0\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u662f\u6211\u7684\u4e8b\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u5b9a\u8981\u95ee\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4e5f\u60f3\u5b66\u5b66\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u5b66\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u53ea\u8981\u662f\u7537\u4eba\u8c01\u4e0d\u60f3\u5b66\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u6211\u66f4\u4e0d\u80fd\u544a\u8bc9\u4f60\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u56e0\u4e3a\u90a3\u662f\u6211\u7684\u79d8\u5bc6\u82e5\u88ab\u4f60\u5b66\u4f1a\u6211\u8fd8\u6709\u4ec0\u9ebd\u620f\u559d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u54fa\u9676\u9053\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u662f\u6211\u670b\u53cb\u54e9\u8c01\u77e5\u59cb\u8fde \u3000\u3000\u738b\u52a8\u5ffd\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\u201c\u670b\u53cb\u4e4b\u95f4\u96be\u9053\u5c31\u4e0d\u80fd\u6709\u79d8\u5bc6\uff1f\uff1f \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u4e5f\u8981\u770b\u662f\u4ec0\u9ebd\u6837\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u79d8\u5bc6\u5c31\u662f\u79d8\u5bc6\u6240\u6709\u7684\u79d8\u5bc6\u90fd\u6837\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u4f60\u4e5f\u6709\u79d8\u5bc6\uff1f\uff1f \u3000\u3000\u738b\u52a8\u70b9\u70b9\u5934\u9053\u201c\u4f60\u5462\uff1f\u4f60\u96be\u9053\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\xb7\u7ec8\u4e8e\u52c9\u5f3a\u70b9\u5382\u70b9\u5934\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u522b\u4eba\u82e5\u8981\u95ee\u4f60\u7684\u79d8\u5bc6\xb7\u4f60\u80af\u4e0d\u80af\u8bf4\u201d\uff1f \u3000\u3000\u90ed\u5927\u8def\u53c8\u60f3\u4e86\u60f3\u7ec8\u4e8e\u52c9\u5f3a\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u9ebd\u4f60\u5c31\u4e5f\u4e0d\u80fd\u95ee\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u4ed6\u8eba\u5382\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8eba\u535c\u53bb\u7684\u65f6\u5019\u5c31\u8868\u793a\u8c08\u8bdd\u5df4\u7ed3\u675f\u3002 \u3000\u3000\u53ea\u6709\u6b63\u786e\u7684\u7ed3\u8bba\u624d\u80fd\u4f7f\u8c08\u8bdd\u7ed3\u675f\u3002 \u3000\u3000\u738b\u52a8\u7684\u7ed3\u8bba\u901a\u5e38\u90fd\u5f88\u6b63\u786e\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u79d8\u5bc6\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u6743\u4fdd\u7559\u81ea\u5df1\u7684\u79d8\u5bc6\u3001\u8fd9\u662f\u4ed6\u7684\u81ea\u7531\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u79d8\u5bc6 \u3000\u3000\u79d8\u5bc6\u662f\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u79d8\u5bc6\u5c31\u662f\u4f60\u552f\u53ef\u4ee5\u72ec\u81ea\u4eab\u53d7\u7684\u4e1c\u897f\u3002 \u3000\u3000\u5b83\u4e5f\u8bb8\u80fd\u4ee4\u4f60\u5feb\u4e50\u4e5f\u8bb8\u4ee4\u4f60\u75db\u82e6\u5b83\u65e0\u8bba\u662f\u4ec0\u9ebd\uff0c\u90fd\u662f\u5b8c\u5168\u5c5e\u6269\u4f60\u7684\u3002 \u3000\u3000\u5b83\u82e5\u662f\u75db\u82eb\xb7\u4f60\u53ea\u6709\u72ec\u81ea\u627f\u53d7\uff0c\u82e5\u662f\u6289\u4e50\u4f60\u4e5f\u4e0d\u80fd\u8ba9\u4eba\u5206\u4eab\u3002 \u3000\u3000\u8fde\u6700\u597d\u7684\u670b\u53cb\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u56e0\u4e3a\u5047\u5982\u6709\u7b2c\u4e8c\u4e2a\u4eba\u77e5\u9053\u8fd9\u79d8\u5bc6\xb7\u90a3\u5c31\u4e0d\u80fd\u7b97\u662f\u79d8\u5bc6\u5382\u3002 \u3000\u3000\u6709\u4e9b\u79d8\u5bc6\u7684\u786e\u662f\u79cd\u4eab\u53d7\u3002 \u3000\u3000\u5f53\u4f60\u521a\u5403\u4e86\u987f\u597d\u996d\xb7\u7edf\u4e86\u4e2a\u70ed\u6c34\u6fa1\xb7\u8eab\u4e0a\u7a7f\u4ef6\u5bbd\u5927\u7684\u65e7\u8863\u670d\u4e2a\u4eba\u5750\u5728\u8212\u670d\u7684\u6905\u5b50\u4e0a\u9762\u5bf9 \u7a97\u5916\u6ee1\u5929\u5915\u9633\u7684\u65f6\u5019\u4f60\u5ffd\u7136\u60f3\u8d77\u79d8\u5bc6\u5fc3\u91cc\u5c31\u4f1a\u4e0d\u7531\u81ea\u4e3b\u6cdb\u8d77\u79cd\u6e29\u6696\u4e4b\u610f\u2026\u2026 \u3000\u3000\u4f60\u7684\u79d8\u5bc6\u5047\u5982\u662f\u8fd9\u79cd\xb7\u5c31\u4e0d\u59a8\u6c38\u8fdc\u4fdd\u7559\u5b83\xb7\u5426\u5219\u5c31\u4e0d\u5982\u5feb\u4e9b\u8bf4\u51fa\u6765\u5427\u3002 \u3000\u3000\u90ed\u5927\u8def\u5750\u5728\u636e\u4e0b\u5df2\u7ecf\u5382\u5f88\u4e45\u3002 \u3000\u3000\u53ea\u8981\u8fd8\u6709\u6837\u522b\u7684\u4e8b\u53ef\u505a\u4ed6\u5c31\u4e0d\u4f1a\u5750\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u6709\u7684\u4eba\u5b81\u53ef\u5230\u5904\u4e71\u901b\u770b\u522b\u4eba\u4f0d\u8def\u4e0a\u8d70\u6765\u8d70\u53bb\u770b\u91ce\u72d7\u5728\u5899\u89d2\u6253\u67b6\u4e5f\u4e0d\u80af\u5173\u5728\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u552f\u80fd\u505a\u7684\u4e8b\u5c31\u662f\u5750\u5728\u8fd9\u91cc\u53d1\u6014\u3002 \u3000\u3000\u642a\u4e0b\u7ed3\u6839\u6839\u7684\u51b0\u67f1\xb7\u6709\u957f\u6709\u77ed\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u6839\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u77e5\u9053\u5171\u6709\u516d\u5341\u53c1\u6839\uff0c\u4e8c\u5341\u516d\u6839\u6bd4\u8f83\u957f\uff0c\u53c1\u5341\u4e03\u6839\u6bd4\u8f83\u77ed\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u6570\u8fc7\u5341\u4e03\u6b21\u3002 \u3000\u3000\u5929\u6c14\u5b9e\u5728\u592a\u51b7\u8857\u4e0a\u975e\u4f46\u770b\u4e0d\u5230\u4eba\u8fde\u91ce\u72d7\u90fd\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb \u3000\u3000\u4ed6\u6d3b\u4e86\u4e8c\u5341\u591a\u5e74\xb7\u8fc7\u4e86\u4e8c\u5341\u591a\u4e2a\u51ac\u5929\u4f46\u5374\u60f3\u4e0d\u8d77\u6765\u54ea\u5929\u6bd4\u8fd9\u51e0\u5929\u66f4\u51b7\u3002 \u3000\u3000\u4e2a\u4eba\u771f\u6b63\u5012\u9709\u7684\u65f6\u5019\u597d\u50cf\u8fde\u5929\u6c14\u90fd\u7279\u522b\u8981\u7f00\u4ed6\u4f5c\u5bf9\u3002 \u3000\u3000\u4ed6\u5e38\u5e38\u90fd\u5f88\u998f\u9709\xb7\u4f46\u5374\u4e5f\u4ece\u6765\u6ca1\u6709\u8c61\u73b0\u5728\u8fd9\u6837\u5012\u9709\u8fc7\u3002 \u3000\u3000\u9605\u9709\u5c31\u8c61\u662f\u79cd\u4f20\u67d3\u75c5\u4e2a\u4eba\u771f\u7684\u5012\u9709\xb7\u8ddf\u4ed6\u5728\u8d77\u7684\u4eba\u4e5f\u7edd\u4e0d\u4f1a\u8d70\u8fd0\u7684\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5e76\u4e0d\u662f\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u71d5\u4e03\u3001\u738b\u52a8\u3001\u6797\u4f17\u4e4e\u4e5f\u90fd\u5750\u5728\u8fd9\u91cc\u4e5f\u90fd\u6b63\u5728\u53d1\u6014\u3002 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u95ee\u9053\u201c\u4f60\u4eec\u731c\u8fd9\u91cc\u5171\u6709\u591a\u5c11\u6839\u51b0\u67f1\u5b50\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u516d\u5341\u53c1\u6839\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u2606\u5341\u516d\u6839\u957f\u53c1\u5341\u4e03\u6839\u77ed\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u9053\u201c\u539f\u6765\u4f60\u8861\u4e5f\u6570\u8fc7\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u5df2\u6570\u8fc7\u56db\u5341\u6e29\u3002 \u3000\u3000\u738b\u52a8\u8c0a\u201c\u6211\u53ea\u6570\u8fc7\u53c1\u904d\xb7\u56e0\u4e3a\u6211\u820d\u4e0d\u5f97\u591a\u6570\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u820d\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u6211\u8981\u7559\u6162\u9992\u7684\u6570\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u7b11\xb7\u5374\u5df2\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u8bdd\u867d\u7136\u72e0\u53ef\u7b11\u4f46\u5374\u53c8\u591a\u9ebd\u53ef\u601c\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u8f6c\u8fc7\u8eab\uff0c\u770b\u5c4b\u5b50\u4e2d\u592e\u7684\u5f20\u684c\u5b50\u3002 \u3000\u3000\u7d2b\u64c5\u6728\u7684\u684c\u5b50\xb7\u9576\u6574\u5757\u7684\u5927\u7406\u77f3\u3002 \u3000\u3000\u90ed\u5927\u8def\u54fa\u54fa\u9053\u201c\u4e0d\u77e5\u9053\u6211\u73b0\u5728\u8fd8\u6709\u6ca1\u6709\u529b\u6c14\u5c06\u8fd9\u684c\u5b50\u62fe\u5230\u5a18\u8205\u5bb6\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u8981\u4e0d\u8981\u6211\u6765\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u6839\u672c\u4e0d\u5fc5\u8bd5\u2606\u201d \u3000\u3000\u90ed\u5927\u8def\u8fe2\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u5f53\u7136\u80fd\u62fe\u5f97\u8d77\u4e00\u5f20\u7a7a\u684c\u4e8e\uff0c\u4f46\u684c\u4e0a\u82e5\u538b\u5f88\u91cd\u7684\u4e1c\u897f\u90a3\u5c31\u4e0d\u540c\u4e86\u2606\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u684c\u4e0a\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u5440\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u9762\u5b50\u800c\u4e14\u4e0d\u662f\u6211\u4e2a\u4eba\u7684\u9762\u5b50\u662f\u6211\u4eec\u5927\u5bb6\u7684\u9762\u5b50\u3002\u201d \u3000\u3000\u4ed6\u8c08\u6de1\u7684\u63a5\u9053\u201c\u6211\u4eec\u4e0d\u4f46\u6536\u4e86\u4eba\u5bb6\u7684\u79df\u91d1\u8fd8\u6536\u4e86\u4eba\u5bb6\u7684\u4fdd\u7ba1\u8d39\u73b0\u5728\u82e5\u5c06\u4eba\u5bb6\u7684\u4e1c\u897f\u62ff\u53bb \u5f53\u4e86\u4ee5\u5f8c\u8fd8\u6709\u8138\u89c1\u4eba\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u4e0d\u9519\u8fd9\u684c\u5b50\u6211\u7684\u786e\u6307\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4e16\u4e0a\u6700\u91cd\u7684\u4e1c\u897f\u5c31\u662f\u9762\u5b50\u6240\u4ee5\u8fd9\u5f20\u684c\u5b50\u53ea\u6709\u4e00\u79cd\u4eba\u80fd\u62cd\u5f97\u8d77\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u8981\u80a1\u7684\u4eba\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u90a3\u79cd\u4eba\u901a\u5e38\u90fd\u662f\u5403\u5f97\u72e0\u9971\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u732a\u901a\u5e38\u4e5f\u90fd\u5403\u5f97\u5f88\u9971\u7684\u201c\u6797\u592a\u5e73\u7b11\u4e86\u9053\u201c\u6240\u4ee5\u4e2a\u4eba\u82e5\u8981\u987e\u5168\u81ea\u5df1\u7684\u9762\u5b50\xb7\u6709\u65f6 \u4e0d\u5f97\u672c\u4e8f\u5f85\u81ea\u5df1\u7684\u809a\u5b50\u9762\u5b50\u6bd5\u7adf\u6bd4\u809a\u4e86\u96f7\u8981\u5f97\u591a\u3002\u201d \u3000\u3000\u988a\u4e03\u9053\u201c\u56e0\u4e3a\u4eba\u4e0d\u662f\u732a\u53ea\u6709\u732a\u624d\u4f1a\u8ba4\u4e3a\u809a\u5b50\u6bd4\u9762\u5b50\u91cd\u8981\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u6240\u4ee5\u6709\u4eba\u5b81\u53ef\u997f \u6b7b\u4e5f\u4e0d\u613f\u505a\u4e22\u4eba\u7684\u4e8b\u3002\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u4f46\u6211\u4eec\u5e76\u6ca1\u6709\u997f\u6b7b\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u867d\u7136\u5df2\u6709\u597d\u51e0\u5929\u90fd\u6ca1\u6709\u5403\u9971\u4f97\u603b\u7b97\u5df2\u6444\u5230\u73b0 \u3000\u3000\u90ed\u5927\u8def\u62ef\u80f8\uff0c\u9053\u201c\u8c01\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\xb7\u6211\u4eec\u7684\u9aa8\u5934\u786e\u6bd4\u5927\u6570\u4eba\u90fd\u786c\u4e9b\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53ea\u8981\u6211\u4eec\u80af\u63d0\u4e0b\u53bb\uff0c\u603b\u6709\u5929\u80fd\u63e1\u5230\u8f6c\u673a\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5c55\u98a7\u7b11\u9053\u201c\u4e0d\u9519\u51ac\u5929\u65e2\u5df2\u6765\u4e86\u6625\u5929\u8fd8\u4f1a\u8fdc\u5417\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53ea\u8981\u6211\u4eec\u80fd\u636e\u5230\u90a3\u5929\u6211\u4eec\u8fd8\u662f\u6837\u53ef\u4ee5\u62ac\u8d77\u5934\u6765\u89c1\u4eba\uff0c\u56e0\u4e3a\u6211\u4eec\u65e2\u6ca1\u6709\u5bf9\u4e0d\u8d77\u522b \u4eba\u4e5f\u6ca1\u6709\u5bf9\u4e0d\u8d77\u81ea\u5df1\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u8fdf\u7591\xb7\u7ec8\u2606\u5fcd\u4e0d\u4f4f\u9053\u201c\u6211\u4eec\u80fd\u6362\u5f97\u8fc7\u53bb\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u201c\u5f53\u7136\u80fd\u3002\u201d \u3000\u3000\u4ed6\u8d70\u8fc7\u53bb\u63fd\u4f4f\u6797\u592a\u5e73\u7684\u80a9\xb7\u7b11\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u867d\u7136\u4ec0\u9ebd\u90fd\u6ca1\u6709\u4e86\uff0c\u4f46\u81f3\u5c11\u8fd8\u6709\u670b\u53cb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u770b\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6cdb\u8d77\u4e00\u9635\u6e29\u6696\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u5df2\u6709\u8db3\u591f\u7684\u52c7\u6c14\u3002 \u3000\u3000\u65e0\u8bba\u591a\u9ebd\u5927\u7684\u56f0\u96be\u65e0\u8bba\u591a\u9ebd\u51b7\u7684\u5929\u6c14\u4ed6\u90fd\u5df2\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u8dd1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u76f4\u5230\u665a\u4e0a\u4ed6\u624d\u56de\u6765\u624b\u91cc\u591a\u4e86\u4e2a\u7eb8\u5305\u3002 \u3000\u3000\u4ed6\u4e3e\u8d77\u8fd9\u7eb8\u5305\uff0c\u7b11\u9053\u201c\u4f60\u9704\u731c\uff0c\u6211\u5e26\u4e86\u4ec0\u9ebd\u4e1c\u897f\u56de\u6765\uff1f\u201d\u7834\u4e50\u82f1\u8757 \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u9192\uff0c\u9053\uff1a\u201c\u96be\u9053\u662f\u7530\u5934\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u7eb8\u5305\u91cc\u679c\u7136\u662f\u9992\u5934\u3002 \u3000\u3000\u56db\u4e2a\u5927\u6e29\u5934\uff0c\u6bcf\u4e2a\u9992\u5934\u91cc\u5c45\u7136\u8fd8\u5939\u5757\u5927\u80a5\u8089\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b22\u547c\u9053\u201c\u6797\u592a\u5e73\u4e07\u5c81\u201d \u3000\u3000\u4ed6\u62ff\u8d77\u4e2a\u9992\u5934\u53c8\u7b11\u9053\u201c\u6211\u5b9e\u5728\u4f69\u670d\uff0c\u73b0\u5728\u5c31\u7b97\u6740\u4e86\u6211\u6211\u4e5f\u53d8\u4e0d\u51fa\u534a\u4e2a\u9992\u5934\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u76ef\u6797\u592a\u5e73\u9053\u201c\u8fd9\u4e9b\u9992\u5934\u5f53\u7136\u4e0d\u662f\u53d8\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u62ff\u4e86\u4e2a\u9992\u5934\u7ed9\u738b\u52a8\u3002 \u3000\u3000\u738b\u52a8\u6447\u6447\u5934\u9053\u201c\u6211\u4e0d\u5403\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u56e0\u4e3a\u6211\u4e0d\u5fcd\u5403\u4f60\u7684\u519c\u670d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u521a\u54ac\u4e86\u53e3\u9992\u5934\uff0c\u5df2\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u53d1\u73b0\u6797\u592a\u5e73\u8eab\u4e0a\u7684\u8863\u670d\u5df3\u5c11\u4e8e\u4ef6\u5458\u539a\u7684\u4ef6\u3002 \u3000\u3000\u6797\u592a\u5e73\u7a7f\u7684\u8863\u670d\u672c\u5c31\u4e0d\u591a\u3002\u73b0\u5728\u4ed6\u5634\u5507\u5df4\u51bb\u5f97\u53d1\u81ea\uff0c\u4f46\u5634\u89d2\u5374\u5e26\u5f88\u6015\u5feb\u7684\u7b11\u5bb9\uff0c\u9053\u201c\u4e0d \u9519\u6211\u7684\u786e\u5c06\u8863\u670d\u5f53\u5382\uff0c\u6362\u4e86\u8fd9\u56db\u4e2a\u9992\u5934\uff0c\u56e0\u4e3a\u6211\u5f88\u997f\xb7\u4e2a\u4eba\u5f88\u997f\u7684\u65f6\u5019\uff0c\u5c06\u76ee\u5df2\u7684\u8863\u670d\u62ff\u53bb \u5f53\u603b\u6ca1\u6709\u4eba\u80fd\u8bf4\u4ed6\u4e0d\u5bf9\u5427\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u9ebd\u4f60\u5c31\u8be5\u5403\u5b8c\u4e86\u518d\u56de\u6765\u4e5f\u514d\u5f97\u6211\u4eec\u201d\u2026\u201c \u3000\u3000\u6797\u592a\u5e73\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\u201c\u6211\u6ca1\u6709\u4e2a\u4eba\u8eb2\u5077\u5077\u7684\u5403\xb7\u53ea\u56e0\u4e3a\u6211\u5f88\u81ea\u79c1\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u81ea\u79c1\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u56e0\u4e3a\u6211\u89c9\u5f97\u56db\u4e2a\u4eba\u5728\u8d77\u5403\xb7\u6bd4\u6211\u4e2a\u8eb2\u5403\u5347\u5fc3\u5f97\u591a\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u670b\u53cb\u3002\u4ed6\u6709\u798f\u80fd\u540c\u4eab\u6709\u96be\u4e5f\u80fd\u540c\u5f53\u3002 \u3000\u3000\u4e2a\u4eba\u82e5\u6709\u4e86\u8fd9\u79cd\u670b\u53cb\uff0c\u7a77\u70b9\u7b97\u5f97\u4e86\u4ec0\u9ebd\xb7\u51b7\u70b9\u53c8\u7b97\u5f97\u4e86\u4ec0\u9ebd\uff1f \u3000\u3000\u90ed\u5927\u8def\u6162\u6162\u7684\u56bc\u9992\u5934\xb7\u5ffd\u7136\u7b11\u9053\u201c\u8001\u5b9e\u8bf4\u6211\u8fd9\u8f88\u5b50\u4ece\u6765\u4e5f\u6ca1\u5403\u8fc7\u8fd9\u9ebd\u597d\u5403\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u4f60\u8bf4\u7684\u8bdd\u4e0d\u8001\u5b9e\xb7\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4e2a\u51b7\u9992\u5934\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u867d\u7136\u662f\u4e2a\u51b7\u9992\u5934\uff0c\u4f46\u5c31\u7b97\u6709\u4eba\u8981\u7528\u5168\u4e16\u754c\u7684\u5927\u8272\u5927 \u3000\u3000\u53e4\u529b\u4f5c\u5458\u5de2\u8089\u6765\u6362\u6211\u8fd9\u51b7\u9992\u5934\u6211\u4e5f\u4e0d\u80af\u6362\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u773c\u5708\u5ffd\u7136\u597d\u50cf\u6709\u4e9b\u7ea2\u4e86\xb7\u6293\u4f4f\u90ed\u5927\u8def\u7684\u624b\u9053\u201c\u542c\u4e86\u4f60\u8fd9\u53e5\u8bdd\uff0c\u6211\u4e5f\u89c9\u5f97\u8fd9\u9992\u5934 \u597d\u5403\u591a\u4e86\u3002\u201d \u3000\u3000\u6709\u4e9b\u8bdd\u7684\u786e\u5c31\u8c61\u662f\u79cd\u795e\u5947\u7684\u7b26\u7eaa\u4e0d\u4f46\u80fd\u4ee4\u51b7\u9992\u5934\u53d8\u6210\u7f8e\u6627\xb7\u4ee4\u51ac\u5929\u53d8\u5f97\u6e29\u77aa\uff0c\u4e5f\u80fd\u4ee4\u67af \u71e5\u7684\u4eba\u751f\u53d8\u5f97\u591a\u59ff\u591a\u91c7\u3002 \u3000\u3000\u4f60\u82e5\u4e5f\u60f3\u5b66\u4f1a\u8bf4\u8fd9\u79cd\u8bdd\u5c31\u8981\u5148\u5b66\u4f1a\u7528\u771f\u8bda\u5bf9\u5f85\u4f60\u7684\u670b\u53cb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u53ea\u53ef\u501f\u6211\u8fd9\u4ef6\u8863\u670d\u592a\u7834\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u7834\u8863\u670d\u5e76\u4e0d\u4e22\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u53ea\u53ef\u60dc\u90a3\u6d3b\u5265\u76ae\u7edd\u4e0d\u4f1a\u8fd9\u9ebd\u60f3\u5426\u5219\u201c\u4e00\u2500\u201d \u3000\u3000\u71d5\u4e03\u7b11\u7b11\u9053\u201c\u5426\u5219\u4f60\u65e9\u5c31\u8131\u4e0b\u6765\u53bb\u6362\u9152\u4e86\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u7ad9\u8d77\u6765\u5f80\u5916\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7528\u4e0d\u53bb\u8bd5\u4f60\u7684\u8863\u670d\u6bd4\u6211\u8fd8\u7834\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e0d\u7406\u4eba\u5f88\u5feb\u7684\u8d70\u51fa\u53bb\u53c8\u72e0\u5feb\u5c31\u56de\u6765\u4e86\u3002 \u3000\u3000\u56de\u6765\u7684\u65f6\u5019\uff0c\u63d0\u58f6\u6c34\u3002 \u3000\u3000\u85cf\u4e03\u9053\u201c\u5bd2\u591c\u5ba2\u6765\u8336\u5f53\u9152\xb7\u8336\u65e2\u7136\u53ef\u4ee5\u5f53\u6d12\u6c34\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5931\u7b11\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u5012\u5f88\u98ce\u96c5\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u4e2a\u4eba\u7a77\u5f97\u8981\u547d\u7684\u65f6\u5019\xb7\u60f3\u4e0d\u98ce\u96c5\u4e5f\u4e0d\u884c\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u4eec\u5bf9\u4eba\u751f\u7684\u6001\u5ea6\u3002 \u3000\u3000\u6709\u9152\u7684\u65f6\u5019\u4ed6\u4eec\u559d\u5f97\u6bd4\u8c01\u90fd\u591a\u6ca1\u6709\u9152\u7684\u65f6\u5019\u4ed6\u4eec\u6c34\u4e5f\u6837\u559d\u3002 \u3000\u3000\u4ed6\u4eec\u559d\u9152\u7684\u65f6\u5019\u5f88\u5f00\u5fc3\u559d\u6c34\u4e5f\u6837\u5f00\u5fc3\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u6d3b\u5f97\u6bd4\u522b\u4eba\u5feb\u4e50\u3002 \u3000\u3000\u4f46\u559d\u9152\u548c\u559d\u6c34\u81f3\u5c11\u603b\u6709\u79cd\u5206\u522b\u3002 \u3000\u3000\u9152\u8d8a\u874e\u8d8a\u70ed\u6c34\u8d8a\u559d\u8d8a\u51b7\u3002 \u3000\u3000\u5c24\u5176\u662f\u5728\u8fd9\u79cd\u5929\u6c14\u91cc\u559d\u51b7\u6c34\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7ad9\u8d77\u6765\u5f00\u59cb\u7ffb\u8ddf\u6597\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u4f60\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u6709\u7ecf\u9a8c\uff0c\u52a8\u52a8\u5c31\u4f1a\u70ed\u8d77\u6765\u7684\uff0c\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u5b66\u5b66\u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6447\u6447\u5934\uff0c\u9053\u201c\u56e0\u4e3a\u6211\u4e5f\u6709\u7ecf\u9a8c\u52a8\u5f97\u5feb\uff0c\u997f\u5f97\u4e5f\u5feb\u3002\u201d\u2019\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u60f3\u5f97\u592a\u591a\u4e86 \u53ea\u8981\u73b0\u5728\u4e0d\u51b7\u53c8\u4f55\u5fc5\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u6ca1\u6709\u8bf4\u5b8c\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u770b\u5230\u6709\u6837\u4e1c\u897f\u4ece\u4ed6\u9762\u524d\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u91d1\u5b50\u5e76\u4e0d\u662f\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\u7684\xb7\u800c\u662f\u4ece\u90ed\u5927\u8def\u597d\u91cc\u6389\u4e0b\u6765\u7684\uff0c \u3000\u3000\u4ed6\u6b63\u5f00\u59cb\u736d\u7b2c\u516d\u4e2a\u56e0\u6597\u6b63\u5728\u5934\u671d\u4e0b\uff0c\u811a\u671d\u4e0a\u7684\u65f6\u5019\u8fd9\u91d1\u5b50\u5c31\u4ece\u4ed6\u6000\u91cc\u6389\u4e86\u4e0b\u6765\u3002\u201c\u5f53\u201d \u7684\uff0c\u6389\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u91d1\u5b50\u6389\u5728\u5730\u4e0a\uff0c\u4f1a\u53d1\u51fa\u201c\u5f53\u201d\u7684\u58f0\u5c31\u8868\u793a\u8fd9\u91d1\u5b50\u5f88\u91cd\u3002 \u3000\u3000\u8fd9\u7684\u786e\u662f\u6839\u5f88\u7c97\u66f2\u91d1\u94fe\u5b50\u4e0a\u9762\u8fd8\u6709\u4e2a\u91d1\u9e21\u5fc3\u3002 \u3000\u3000\u8fd9\u91d1\u9e21\u5fc3\u81f3\u5c11\u6bd4\u771f\u7684\u9e21\u5fc3\u5927\u500d\u3002 \u3000\u3000\u4e2a\u7a77\u5f97\u597d\u51e0\u5929\u6ca1\u5403\u996d\u7684\u4eba\xb7\u8eab\u4e0a\u5c45\u7136\u4f1a\u6389\u51fa\u8fd9\u9ebd\u591a\u91d1\u5b50\u6765\u7b80\u76f4\u662f\u4ef6\u4ee4\u4eba\u65e0\u6cd5\u76f8\u4fe1\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u738b\u52a8\u4ed6\u4eec\u5374\u65e0\u6cd5\u4e0d\u76f8\u4fe1\uff0c\u56e0\u4e3a\u4ed6\u4eec\u53c1\u4e2a\u90fd\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u5e0c\u671b\u65e5\u5df1\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u4ed6\u4eec\u5b9e\u51b3\u4e0d\u613f\u610f\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u8fde\u81ea\u5df2\u7684\u8863\u88f3\u90fd\u62ff\u53bb\u5f53\u4e86\u90ed\u5927\u8def\u8eab\u4e0a\u5374\u8fd8\u85cf\u6761\u8fd9\u9ebd\u7c97\u7684\u91d1\u954a\u5b50\u3002 \u3000\u3000\u4e2a\u8eab\u4e0a\u85cf\u91d1\u94fe\u5b50\u7684\u4eba\u5c45\u7136\u8fd8\u5728\u670b\u53cb\u9762\u524d\u88c5\u7a77\u5c45\u7136\u8fd8\u88c5\u5f97\u90a3\u9ebd\u8c61\u3002 \u3000\u3000\u8fd9\u7b97\u662f\u4ec0\u9ebd\u670b\u53cb\uff1f \u3000\u3000\u4ed6\u4eec\u5b9e\u5728\u4e0d\u613f\u76f8\u4fe1\u90ed\u5927\u8def\u4f1a\u662f\u8fd9\u6837\u7684\u670b\u53cb\u3002 \u3000\u3000\u738b\u52a8\u7a81\u7136\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u54fa\u54fa\u9053\u201c\u4e2a\u4eba\u5403\u9971\u4e86\xb7\u4e3a\u4ec0\u9ebd\u603b\u8db3\u60f3\u7761\u89c9\u5462\uff1f\u201d \u3000\u3000\u4ed6\u53bb\u7761\u4e86\u4ece\u90ed\u5927\u8def\u9762\u524d\u8d70\u8fc7\u53bb\u597d\u50cf\u65e2\u6ca1\u6709\u770b\u89c1\u8fd9\u6761\u91d1\u94fe\u5b50\u4e5f\u6ca1\u6709\u770b\u89c1\u90ed\u5927\u8def\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u6797\u592a\u5e73\u6253\u4e86\u4e2a\u7f51\u6b20\uff0c\u54fa\u7a3f\u9053\u8fd9\u9ebd\u51b7\u7684\u5929\u6c14\xb7\u8fd8\u6709\u4ec0\u9ebd\u5730\u4e07\u6bd4\u7834\u7a9d\u91cc\u597d\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u53bb\u7761\u5382\xb7\u4e5f\u597d\u50cf\u4ec0\u9ebd\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u53ea\u6709\u9893\u4e03\u8fd8\u5750\u5728\u90a3\u91cc\u5750\u5728\u90a3\u91cc\u53d1\u6014\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\xb7\u90ed\u5927\u8def\u7684\u811a\u624d\u9992\u9992\u7684\u4ece\u4e0a\u9762\u843d\u4e0b\u6765\u9992\u6162\u7684\u628a\u8eab\u5b50\u7ad9\u76f4\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u597d\u50cf\u5df2\u96be\u518d\u7ad9\u5f97\u76f4\u3002 \u3000\u3000\u6ca1\u6709\u661f\uff0c\u6ca1\u6709\u6708\uff0c\u53ea\u6709\u4e00\u76cf\u706f\u3002 \u3000\u3000\u76cf\u5f88\u5c0f\u7684\u706f\u56e0\u4e3a\u5269\u4e0b\u7684\u5973\u300f\u6cb9\u4e5f\u5df2\u4e0d\u591a\u3002 \u3000\u3000\u4f46\u8fd9\u6761\u91d1\u94fe\u5b50\u5728\u706f\u4e0b\u770b\u6765\u8fd8\u662f\u4eae\u5f97\u5f88\u3002 \u3000\u3000\u90ed\u5927\u8def\u4f4e\u5934\u770b\u8fd9\u6761\u91d1\u94fe\u5b50\uff0c\u95fb\u54e8\u9053\u201c\u5947\u602a\u4e3a\u4ec0\u9ebd\u91d1\u4e86 \u3000\u3000\u71d5\u4e03\u6de1\u6de1\u9053\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u91d1\u5b50\u7684\u597d\u5904\u5426\u5219\u4e3a\u4ec0\u9ebd\u4f1a\u6709\u90a3\u9ebd\u591a\u4eba\u5c06\u91d1\u5b50\u770b\u5f97\u6bd4\u670b\u53cb\u8fd8 \u91cd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4e86\u534a\u5929\u5ffd\u7136\u59cb\u8d77\u5934\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u4ea5\u7761\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u8fd8\u5728\u7b49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7b49\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u7b49\u542c\u4f60\u8bf4\u2026\u3002\u201c \u3000\u3000\u90ed\u5927\u8def\u5927\u58f0\u9053\u201c\u6211\u6ca1\u6709\u4ec0\u9ebd\u597d\u8bf4\u7684\xb7\u4f60\u4eec\u82e5\u628a\u6211\u770b\u6210\u8fd9\u79cd\u4eba\uff0c\u6211\u5c31\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u51dd\u89c6\u4ed6\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\u624d\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\xb7\u6162\u9992\u7684\u8d70\u51fa\u4e91\u3002 \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u770b\u4ed6\u3002 \u3000\u3000\u5916\u9762\u7684\u98ce\u597d\u5927\xb7\u597d\u51b7\u3002 \u3000\u3000\u706f\u5df2\u5c06\u6697\uff0c\u5ffd\u7136\u95f4\xb7\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u5377\u51fa\u4e86\u9635\u51b7\u98ce\u5439\u7184\u706f\u3002 \u3000\u3000\u4f46\u91d1\u954a\u5b50\u8fd8\u5728\u53d1\u5149\u3002 \u3000\u3000\u90ed\u5927\u8def\u5782\u5934\uff0c\u770b\u8fd9\u6761\u91d1\u6444\u4e88\uff0c\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u4ed6\u624d\u9992\u5021\u7684\u5f2f\u4e0b\u8170\xb7\u62fe\u8d77\u4e86\u8fd9\u91d1\u94fe\u5b50\u3002 \u3000\u3000\u4ed6\u6367\u8fd9\u91d1\u94fe\u5b50\xb7\u6367\u5728\u638c\u5fc3\u3002\u2606 \u3000\u3000\u4ed6\u9650\u6cea\u7a81\u7136\u6cc9\u6d8c\u800c\u51fa\uff0c\u4e00\u8d85\u7c92\u6ef4\u5728\u638c\u5fc3\u3002 \u3000\u3000\u51b0\u51b7\u7684\u91d1\u94fe\u5b50\u706b\u70ed\u7684\u9650\u6cea\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u8dea\u4e0b\u53bb\u7ec8\u4e8e\u54ed\u5382\u8d77\u6765\u5c3d\u91cf\u4e0d\u8ba9\u81ea\u5df1\u54ed\u51fa\u58f0\u97f3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u613f\u522b\u4eba\u542c\u5230\u4ed6\u7684\u54ed\u58f0\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u79d8\u5bc6\u4e5f\u662f\u4ed6\u751f\u4e2d\u5458\u5927\u7684\u75db\u82e6\u4ed6\u4e0d\u613f\u522b\u4eba\u77e5\u9053\u8fd9\u79d8\u5bc6\uff0c\u4e5f\u4e0d\u613f\u522b\u4eba\u5206\u62c5\u4ed6\u7684\u75db \u82e6\u3002 \u3000\u3000\u6240\u4ee5\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u75db\u82e6\u5f97\u591a\u9ebd\u6df1\u591a\u9ebd\u6df1\u523b\u3002 \u3000\u3000\u90a3\u867d\u7136\u5df2\u7ecf\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\u3002\u4f46\u73b0\u5728\u4ed6\u53ea\u8981\u60f3\u5230\u8fd8\u662f\u4f1a\u5fc3\u788e\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u7ec8\u751f\u8981\u80cc\u8d1f\u75db\u82e6\uff0c\u81f3\u6b7b\u90fd\u65e0\u6cd5\u89e3\u8131\u3002 \u3000\u3000\u521a\u7684\u4e8b\u4e5f\u4ee4\u4ed6\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5b81\u6b7b\u4e5f\u4e0d\u613f\u5931\u53bb\u8fd9\u4e9b\u670b\u53cb\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u6709\u89e3\u91ca\xb7\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ed6\u4eec\u4e0d\u4f1a\u539f\u8c05\u4ed6\uff0c\u56e0\u4e3a\u8fde\u4ed6\u81ea\u5df1\u90fd\u65e0\u6cd5\u539f\u8c05\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u4e5f\u8bb8\u4e16\u4e0a\u6709\u79cd\u771f\u6b63\u7684\u75c5\u82e6\u90a3\u5c31\u662f\u4e0d\u80fd\u5411\u522b\u4eba\u8bf4\u7684\u75db\u82e6\u3002 \u3000\u3000\u201c\u4e0d\u80fd\u8bf4\u201c\u2500\u201c\u6211\u600e\u9ebd\u80fd\u8bf4\uff1f\u2026\u3002 \u3000\u3000\u201c\u6211\u600e\u9ebd\u8fd8\u6709\u8138\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5916\u9762\u7684\u98ce\u66f4\u5927\u66f4\u51b7\u3002 \u3000\u3000\u4ed6\u54ac\u7d27\u7259\u6084\u6084\u64e6\u5e72\u773c\u6cea\xb7\u7ad9\u8d77\u6765\u5916\u9762\u7684\u4e16\u754c\u65e0\u8bba\u591a\u51b7\u9177\u65e0\u60c5\u4ed6\u90fd\u5df2\u51c6\u5907\u72ec\u81ea\u53bb\u627f\u53d7\u3002 \u3000\u3000\u4ed6\u505a\u9519\u5382\u4e8b\uff0c\u5c31\u81ea\u5df1\u627f\u5f53\u3001\u65e2\u4e0d\u80af\u89e3\u91ca\xb7\u4e5f\u4e0d\u80af\u544a\u9976\u3002 \u3000\u3000\u5c31\u7b97\u5728\u670b\u53cb\u9762\u524d\u4e5f\u4e0d\u80af\u3002 \u3000\u3000\u53ef\u662f\u4e0a\u5929\u77e5\u9053\u4ed6\u5b9e\u5728\u5c06\u670b\u53cb\u770b\u5f97\u6bd4\u81ea\u5df1\u751f\u547d\u8fd8\u8981\u91cd\u3002 \u3000\u3000\u201c\u670b\u53cb\u4ecd\uff0c\u518d\u89c1\u5427\xb7\u603b\u6709\u5929\uff0c\u4f60\u4eec\u4f1a\u4e86\u89e3\u6211\u7684\u3002\u5230\u90a3\u5929\u6211\u4eec\u8fd8\u662f\u670b\u53cb\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4ed6\u773c\u6cea\u53c8\u5728\u5f80\u4e0b\u6d41\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4f38\u624b\u53bb\u64e6\u773c\u6cc5\u7684\u65f6\u5019\u770b\u5230\u201c\u71d5\u4e03\u3002 \u3000\u3000\u4e0d\u4f46\u770b\u5230\u4e86\u71d5\u4e03\u4e5f\u770b\u5230\u4e86\u738b\u52a8\u548c\u6797\u592a\u5e73\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u77e5\u4ec0\u9ebd\u65f6\u5019\u53c8\u8d70\u8fdb\u4e86\u8fd9\u5c4b\u5b50\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u9759\u9759\u7684\u770b\u4ed6\u3002 \u3000\u3000\u4ed6\u770b\u4e0d\u89c1\u4ed6\u8ba2\u8138\u535c\u7684\u8868\u60c5\u53ea\u770b\u5230\u4ed6\u4eec\u53c1\u53cc\u53d1\u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4e5f\u5e0c\u671b\u4ed6\u4eec\u83ab\u8981\u770b\u5230\u4ed6\u7684\u8138\uff0c\u4ed6\u8138\u4e0a\u7684\u6cc5\u75d5\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u54b3\u55fd\u4e86\u51e0\u58f0\u9053\u201c\u4f60\u4eec\u4e0d\u662f\u5df1\u532a\u4e86\u5417\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u4eec\u7761\u4e0d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u7761\u4e0d\u4e5f\u8be5\u8eba\u5728\u88ab\u7a9d\u91cc\u5728\u8fd9\u79cd\u5929\u6c14\u4e16\u4e0a\u8fd8\u6709\u4ec0\u9ebd\u5730\u65b9\u6bd4\u88ab\u7a9d\u91cc\u66f4 \u597d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u3002\u201d\u71d5\u4e03\u9053\u8fd9\u91cc\u5c31\u6bd4\u88ab\u7a9d\u91cc\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u91cc\u6709\u7530\u70b9\u597d\uff1f\uff1f \u3000\u3000\u738b\u52a8\u9053\u201c\u53ea\u6709\u70b9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u91cc\u7684\u670b\u53cb\xb7\u88ab\u7a9d\u91cc\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u89c9\u5f97\u9635\u70ed\u610f\u4ece\u5fc3\u91cc\u51b2\u4e0a\u6765\uff0c\u4f3c\u5df2\u5c06\u5589\u5934\u585e\u4f4f\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4ed6\u624d\u80fd\u8bf4\u5f97\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4ed6\u5782\u4e0b\u5934\u9053\u201c\u8fd9\u91cc\u4e5f\u6ca1\u6709\u670b\u53cb\u6211\u5df2\u4e0d\u914d\u505a\u4f60\u4eec\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4e5f\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u60f3\u8bf4\u2018\u53e5\u8bdd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u63e1\u7d27\u4e86\u62f3\u9053\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u4e86\u89e3\u4f60\u4e5f\u76f8\u4fe1\u4f60\uff0c\u6240\u4ee5\u65e0\u8bba\u53d1\u4e2d\u5382\u4ec0\u9ebd\u4e8b\u4f60 \u3000\u3000\u8fd9\u5c31\u662f\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u4eec\u80fd\u5206\u4eab\u4f60\u7684\u5feb\u4e50\xb7\u4e5f\u80fd\u5206\u62c5\u4f60\u7684\u75db\u82e6\u3002 \u3000\u3000\u4f60\u82e5\u6709\u56f0\u96be\u4ed6\u4eec\u613f\u610f\u5e2e\u52a9\u3002 \u3000\u3000\u4f60\u82e5\u6709\u5371\u9669\u4ed6\u4eec\u613f\u610f\u4e3a\u4f60\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u5c31\u7b97\u4f60\u771f\u7684\u505a\u9519\u5382\u4ec0\u9ebd\u4e8b\u4ed6\u4eec\u4e5f\u80fd\u6e90\u89e3\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u670b\u53cb\u9762\u524d\xb7\u4f60\u8fd8\u6709\u4ec0\u9ebd\u79d8\u5bc6\u4e0d\u80fd\u8bf4\u7684\uff1f \u3000\u3000\u56db \u3000\u3000\u5916\u9762\u7684\u98ce\u8fd8\u662f\u72e0\u51b7\u5f88\u5927\u3002 \u3000\u3000\u5c4b\u4e86\u91cc\u8fd8\u662f\u5f88\u9ed1\u6697\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u6b64\u523b\u4ed6\u4eec\u6240\u80fd\u611f\u53d7\u5230\u7684\u5374\u53ea\u6709\u6e29\u6696\u548c\u5149\u660e\u3002\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u81ea\u5df2\u6709\u670b\u53cb\uff0c\u6709\u4e86\u771f\u5fc3 \u7684\u670b\u53cb\u3002 \u3000\u3000\u6709\u670b\u53cb\u7684\u5730\u65b9\u5c31\u6709\u6e29\u6696\u5c31\u6709\u5149\u660e\u3002 \u3000\u3000\u201c\u65e0\u8bba\u53d1\u751f\u5382\u4ec0\u9ebd\u4e8b\uff0c\u4f60\u90fd\u662f\u6211\u4eec\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u89c2\u5728\u6cb8\u817e\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5b81\u6b7b\u4e5f\u4e0d\u613f\u5728\u522b\u4eba\u9762\u524d\u6d41\u6cc5\u4f46\u73b0\u5728\u773c\u6cea\u5df2\u53c8\u6d41\u51fa\u3002\u4ed6\u672c\u6765\u5b81\u6b7b\u4e5f\u4e0d\u613f\u8bf4\u51fa\u81ea\u5df1\u5fc3 \u91cc\u7684\u75c8\u82eb\u548c\u79d8\u5bc6\u4f46\u73b0\u5728\u5374\u613f\u8bf4 \u3000\u3000\u6ca1\u6709\u522b\u7684\u4eba\u80fd\u4ee4\u4ed6\u8fd9\u9ebd\u505a\xb7\u53ea\u6709\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u8bf4\u51fa\u4e86\u4ed6\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u5bb6\u4e61\u6709\u5f88\u591a\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u6700\u7f8e\u7684\u4e2a\u53eb\u6731\u73e0\u3002 \u3000\u3000\u4ed6\u7231\u4e0a\u4e86\u6731\u73e0\uff0c\u6731\u73e0\u4e5f\u7231\u4ed6\u3002 \u3000\u3000\u4ed6\u5168\u5fc3\u5168\u610f\u7684\u5bf9\u5f85\u6731\u73e0\u4ed6\u5bf9\u5979\u8bf4\u613f\u610f\u5c06\u81ea\u5df1\u7684\u751f\u547d\u548c\u5207\u90fd\u732e\u7ed9\u5979\u3002 \u3000\u3000\u4ed6\u4e0d\u8c61\u522b\u7684\u7537\u4eba\u53ea\u662f\u8bf4\u8bf4\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u4ed6\u771f\u7684\u8fd9\u9ebd\u6837\u505a\u3002 \u3000\u3000\u672a\u73e0\u72e0\u7a77\uff0c\u7b49\u5230\u90ed\u5927\u8def\u7684\u53cc\u4eb2\u53bb\u4e16\u65f6\u5979\u5c31\u4e0d\u7a77\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u5979\u662f\u5c5e\u65bc\u4ed6\u7684\u5979\u4e5f\u8bf4\u8fc7\uff0c\u5979\u6574\u4e2a\u4eba\u90fd\u5c5e\u65bc\u4ed6\u7684\u3002 \u3000\u3000\u4e3a\u4e86\u8ba9\u5979\u4fe1\u4efb\u4ed6\xb7\u4e3a\u4e86\u8ba9\u5979\u5feb\u4e50\u4ed6\u613f\u610f\u505a\u4edf\u4f55\u7684\u4e8b\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u53d1\u73b0\u4e86\u6837\u4e8b\u3002 \u3000\u3000\u6731\u73e0\u5e76\u4e0d\u7231\u4ed6\u3002\u5c31\u8c61\u5f88\u591a\u522b\u7684\u4ea5\u4eba\u6837\xb7\u5979\u8bf4\u7684\u8bdd\uff0c\u53ea\u4e0d\u8fc7\u8bf4\u8bf4\u800c\u5df2\u3002 \u3000\u3000\u5979\u7b54\u5e94\u5ac1\u7ed9\u4ed6\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\u8c01\u90fd\u4e0d\u5ac1\u3002 \u3000\u3000\u4ed6\u4eec\u751a\u81f3\u5df2\u51b3\u5b9a\u4e86\u5a5a\u671f\u3002 \u3000\u3000\u53ef\u662f\u5728\u4ed6\u4eec\u5a5a\u671f\u7684\u524d\u5929\u5979\u5df2\u5148\u5ac1\u4e86\xb7\u5ac1\u7ed9\u4e86\u522b\u4eba\u3002 \u3000\u3000\u5979\u51fa\u5356\u4e86\u90ed\u5927\u8def\u6240\u7ed9\u5979\u7684\u4e00\u5207\uff0c\u7f00\u90a3\u4eba\u79c1\u5954\u4e86\u3002 \u3000\u3000\u8fd9\u6761\u91d1\u94fe\u5b50\u5c31\u662f\u5979\u7ed9\u4ed6\u7684\u8ba2\u60c5\u4e4b\u7269\u3002 \u3000\u3000\u4e5f\u662f\u5979\u7ed9\u4ed6\u7684\u552f\u4e00\u7684\u6837\u7684\u4e1c\u897f\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u5f00\u53e3\xb7\u8c01\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u8fd8\u662f\u90ed\u5927\u8def\u81ea\u5df1\u5148\u6253\u7834\u4e86\u6c89\u9ed8\u3002\u4ed6\u5ffd\u7136\u7b11\u7b11\uff0c\u9053\u201c\u4f60\u3002\u6c38\u8fdc\u731c\u4e0d\u81f3\u5979\u662f\u8ddf\u8c01\u8dd1\u4e86\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u7684\u9a6c\u592b\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\uff0c\u63a5\u9053\u201c\u6211\u5c06\u5979\u5f53\u505a\u5929\u4e0b\u6700\u9ad8\u8d35\u7684\u4eba\uff0c\u7b80\u76f4\u5c06\u5979\u5f53\u505a\u4ed9\u5973\uff0c\u4f46\u5979\u5374\u8ddf\u6211\u6700\u770b\u4e0d\u8d77 \u7684\u9a6c\u592b\u79c1\u5954\u4e86\uff0c\u4f60\u4eec\u8bf4\xb7\u8fd9\u53ef\u7b11\u4e0d\u53ef\u7b11\uff1f\u201d \u3000\u3000\u4e0d\u53ef\u7b11\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u89c9\u5f97\u8fd9\u79cd\u4e8b\u53ef\u7b11\u3002 \u3000\u3000\u53ea\u6709\u90ed\u5927\u8def\u4e2a\u4eba\u76f4\u4e0d\u505c\u66f2\u7b11\xb7\u56e0\u4e3a\u4ed6\u751f\u4f2f\u81ea\u5df1\u4e0d\u7b11\u5c31\u4f1a \u3000\u3000\u4ed6\u2019\u76f4\u4e0d\u505c\u7684\u7b11\u4e86\u5f88\u4e45\u5ffd\u7136\u53c8\u9053\u201c\u8fd9\u4ef6\u4e8b\u7684\u786e\u7ed9\u4e86\u6211\u4e2a\u5f88\u597d\u7684\u6559\u8bad\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4ec0\u9ebd\u6559\u8bad\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u95ee\u3001\u53ea\u672f\u8fc7\u5ffd\u7136\u89c9\u5f97\u4e0d\u5e94\u8be5\u8ba9\u90ed\u5927\u8def\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u76ee\u5df1\u5e94\u8be5\u8868\u793a\u51fa\u81ea\u5df1\u975e\u5e38\u5173\u5fc3\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u6559\u8bad\u5c31\u662f\u7537\u4eba\u7edd\u4e0d\u80fd\u592a\u5c0a\u91cd\u5973\u4eba\xb7\u4f60\u82e5\u592a\u5c0a\u91cd\u5979\u5979\u5c31\u4f1a\u8ba4\u4e3a\u4f60\u662f\u5446\u5b50\u8ba4\u4e3a \u4f60\u4e0d\u503c\u4e00\u6587\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u4f60\u9519\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\u8bf4\u6211\u9519\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u8fd9\u9ebd\u6837\u505a\u5e76\u4e0d\u662f\u56e0\u4e3a\u4f60\u5c0a\u656c\u5979\u4e00\u4e2a\u5973\u4eba\u82e5\u80fd\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\u53ea\u6709\u4e00\u4e2a\u539f\u56e0\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u539f\u56e0\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u53ea\u56e0\u5979\u5929\u751f\u662f\u4e2a\u574f\u5973\u4eba\u3002 \u3000\u3000\u71d5\u4e03\u6c89\u9ed8\u4e86\u5f88\u4e45\u7ec8\u4e8e\u9992\u6162\u7684\u70b9\u4e86\u70b9\u5934\xb7\u82e6\u7b11\u9053\u201c\u6240\u4ee5\u6211\u5e76\u4e0d\u602a\u5979\uff0c\u53ea\u602a\u81ea\u5df1\uff0c\u53ea\u602a\u6211\u81ea \u5df1\u4e3a\u4ec0\u9ebd\u770b\u9519\u4e86\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u8fd9\u79cd\u60f3\u6cd5\u4e5f\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4e00\u76f4\u4e3a\u8fd9\u4ef6\u4e8b\u96be\u53d7\xb7\u53ea\u56e0\u4f60\u4e00\u76f4\u5728\u5f80\u5458\u574f\u7684\u5730\u65b9\u53bb\u60f3\u603b\u89c9\u5f97\u5979\u662f\u5728\u6b3a\u9a97 \u4f60\xb7\u603b\u89c9\u5f97\u81ea\u5df1\u88ab\u4eba\u5bb6\u7529\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u672c\u6765\u96be\u9053\u4e0d\u662f\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u4e8e\u52a8\u9053\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u5f80\u522b\u7684\u5730\u65b9\u60f3\u60f3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5e94\u8be5\u600e\u9ebd\u60f3\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u60f3\u60f3\u597d\u7684\u90a3\u4e00\u9762\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u60f3\u4e0d\u51fa\u3002\u201d \u3000\u3000\u738b\u52a8\u8fe2\u201c\u4f60\u6709\u6ca1\u6709\u4eb2\u773c\u770b\u5230\u5979\u548c\u90a3\u4e2a\u9a6c\u592b\u505a\u51fa\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u9ebd\u4f60\u53c8\u600e\u9ebd\u80fd\u65ad\u5b9a\u5979\u662f\u548c\u90a3\u9a6c\u592b\u79c1\u5954\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\xb7\u9053\u201c\u6211\u3002\u2500\u201c\u5e76\u5c0f\u662f\u6211\u4e2a\u4eba\u8fd9\u9ebd\u60f3\u6bcf\u4e2a\u4eba\u90fd\u8fd9\u9ebd\u60f3\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u522b\u4eba\u600e\u9ebd\u60f3\u4f60\u5c31\u600e\u9ebd\u60f3\uff1f\u522b\u4eba\u82e5\u8ba4\u4e3a\u4f60\u5e94\u8be5\u53bb\u5403\u5c4e\xb7\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8bf4\u4e0d\u51fa\u8bdd\u4e86\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u504f\u89c1\u3002\u90a3\u4e9b\u4eba\u6839\u672c\u5c31\u4e0d\u5382\u89e3\u5979\xb7\u5bf9\u5979\u7684\u770b\u6cd5\u600e\u9ebd\u4f1a\u6b63\u786e\uff1f\u4f55\u51b5\uff0c\u5c31 \u7b97\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u6709\u65f6\u4e5f\u5e38\u5e38\u4f1a\u53d1\u751f\u8bef\u4f1a\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5382\u7b11\u9992\u6162\u5730\u63a5\u9053\u201c\u8b6c\u5982\u8bf4\u521a\u90a3\u4ef6\u4e8b\u6211\u4eec\u5c31\u5f88\u53ef\u80fd\u8bef\u4f1a\u4f60\u2019\u8ba4\u4e3a\u4f60\u662f\u4e2a\u5c0f\u6c14\u9b3c\u8ba4\u4e3a\u4f60 \u4e0d\u591f\u670b\u53cb\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f46\u5979\u7684\u786e\u662f\u548c\u90a3\u9a6c\u592b\u5728\u540c\u4e00\u5929\u7a81\u7136\u5931\u8e2a\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u662f\u5de7\u5408\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5929\u4e0b\u54ea\u6709\u8fd9\u9ebd\u5de7\u7684\u4e8b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u3002\u4e0d\u4f46\u6709\uff0c\u800c\u4e14\u5e38\u5e38\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u4ed6\u4eec\u4e3a\u4ec0\u9ebd\u8981\u7a81\u7136\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u9a6c\u592b\u4e5f\u8bb8\u56e0\u4e3a\u89c9\u5f97\u505a\u8fd9\u79cd\u4e8b\u6ca1\u51fa\u606f\u6240\u4ee5\u60f3\u5230\u522b\u5730\u65b9\u53bb\u53e6\u8c0b\u53d1\u5c55\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6731\u73e0\u5462\uff1f\u5979\u53c8\u6709\u4ec0\u9ebd\u7406\u7531\u8981\u8d70\uff1f\u6211\u5176\u81f3\u8fde\u82b1\u8f7f\u90fd\u5df2\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u600e\u9ebd\u4e0d\u53ef\u80fd\u6709\u522b\u7684\u7406\u7531\uff1f\u90a3\u5929\u665a\u4e0a\uff0c\u4e5f\u8bb8\u7a81\u7136\u53d1\u751f\u4e86\u4ec0\u9ebd\u4f60\u4e0d\u77e5\u9053\u7684\u53d8\u5316\u901a\u5f97 \u5979\u975e\u8d70\u4e0d\u53ef\u4e5f\u8bb8\u5979\u6839\u672c\u8eab\u4e0d\u7531\u4e3b\xb7\u662f\u88ab\u4eba\u7ed1\u67b6\u8d70\u7684\u3002\u201d\u6539\u4e50\u82f1\u87f9 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u9053\u201c\u4e5f\u8bb8\u5979\u4e00\u76f4\u90fd\u5f88\u60f3\u5411\u4f60\u89e3\u91ca\uff0c\u5374\u76f4\u6ca1\u6709\u673a \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4e16\u4e0a\u6781\u75db\u82e6\u7684\u4e8b\u4e5f\u8bb8\u5c31\u662f\u660e\u77e5\u9053\u522b\u4eba\u5bf9\u767d\u5df1\u6709\u4e86\u8bef\u4f1a\u81ea\u5df2\u660e\u660e\u53d7\u4e86\u51a4 \u6789\u5374\u65e0\u6cd5\u89e3\u91ca\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u66f4\u75db\u82e6\u7684\u662f\u522b\u4eba\u6839\u672c\u5c31\u4e0d\u7ed9\u4ed6\u673a\u4f1a\u89e3\u91ca\u3002\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u6700\u75db\u82eb\u7684\u662f\u6709\u4e9b\u4e8b\u6839\u672c\u5c31\u4e0d\u80fd\u5bf9\u522b\u4eba\u89e3\u91ca\u7684\u8b6c\u5982\u8bf4\u2026\u2026\u201d \u3000\u3000\u7ff1\u5927\u8def\u957f\u53f9\u9053\u201c\u8b6c\u5982\u8bf4\u521a\u90a3\u4ef6\u4e8b\uff0c\u6211\u672c\u6765\u5c31\u4e0d\u613f\u89e3\u91ca\u7684\u521a\u624d\u4f60\u4eec\u6765\u7684\u65f6\u5019\u6211\u82e5\u5df2\u8d70\u4e86\u4f60 \u4eec\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u5bf9\u6211\u76f4\u8bef\u4f1a\u4e0b\u9ebd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u9519\xb7\u73b0\u5728\u4f60\u5df2\u60f3\u901a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ef6\u4e8b\u5f80\u5f80\u6709\u5f88\u591a\u9762\u4f60\u82e5\u80af\u5f80\u597d\u7684\u90a3\u9762\u53bb\u60f3\u624d\u80fd\u6d3b\u5f97\u5feb\u4e50\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ea\u53ef\u60dc\u6709\u7684\u4eba\u9985\u504f\u4e0d\u80af\u504f\u504f\u8981\u5f80\u6700\u597d\u7684\u5730\u65b9\u53bb\u60f3\u504f\u504f\u8981\u94bb\u725b\u89d2\u5c16\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u79cd\u4eba\u975e\u4f46\u611a\u9e93\u4e3d\u6c11\u7b80\u76f4\u662f\u81ea\u5df1\u5728\u627e\u81ea\u5df2\u7684\u9ebb\u70e6\u81ea\u5df1\u5728\u8650\u5f85\u81ea\u5df1\u3002\u6211\u60f3\u5f25\u603b\u4e0d \u4f1a\u662f\u8fd9\u79cd\u4eba\u5427\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u4e86\u5927\u58f0\u9053\u201c\u51c6\u8bf4\u6211\u662f\u8fd9\u79cd\u4eba\u6211\u6253\u6241\u4ed6\u7684\u9f3b\u5b50\u3002\u201d \u3000\u3000\u6240\u4ee5\u4f53\u5fc3\u91cc\u8981\u6709\u4ec0\u9ebd\u4ee4\u4f60\u75db\u82eb\u7684\u79d8\u5bc6\u5458\u597d\u80fd\u5728\u670b\u53cb\u9762\u524d\u8bf4\u51fa\u91c7\u3002 \u3000\u3000\u56e0\u4e3a\u771f\u6b63\u7684\u670b\u53cb\u975e\u4f46\u80fd\u5206\u4eab\u4f60\u7684\u5feb\u4e50\xb7\u4e5f\u80fd\u5316\u89e3\u4f60\u7684\u75db\u82e6\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u89c9\u5f97\u8212\u670d\u591a\u4e86\xb7\u6109\u5feb\u591a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u6ca1\u6709\u79d8\u5bc6\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u80fd\u770b\u5230\u4e8b\u60c5\u5149\u660e\u7684\u9762\u3002 \u3000\u3000\u591c\u6df1\u68a6\u7f51\u65f6\u4ed6\u5c31\u7b97\u518d\u60f3\u5230\u8fd9\u79cd\u4e8b\u4e5f\u4e0d\u518d\u75db\u82e6\u6700\u591a\u53ea\u4e0d\u8fc7\u4f1a\u6709\u79cd\u6de1\u8c08\u7684\u5fe7\u90c1\u3002\u8c08\u6de1\u7684\u5fe7\u90c1 \u6709\u65f6\u751a\u81f3\u662f\u79cd\u4eab\u53d7\u3002 \u3000\u3000\u4e94 \u3000\u3000\u201c\u4f60\u4eec\u867d\u7136\u5206\u522b\u4e86\u8bf4\u4e0d\u5b9a\u53cd\u800c\u80fd\u6d3b\u5f97\u66f4\u5feb\u4e50\u4e9b\u3002\u201d \u3000\u3000\u201c\u5979\u8bf4\u4e0d\u5b9a\u4e5f\u627e\u5230\u5f88\u597d\u7684\u5f52\u5bbf\u81f3\u65bc\u4f60\u201d\u2026\u82e5\u6ca1\u6709\u53d1\u751f\u8fd9\u53d8\u5316\u4f60\u73b0\u5728\u8bf4\u4e0d\u5b9a\u6bcf\u5929\u90fd\u5728\u62b1\u7b4f \u5b50\u3001\u6362\u5c3f\u5e03\u800c\u4e14\u8bf4\u4e0d\u8d70\u6bcf\u5929\u4e3a\u4e86\u67f4\u7c73\u6cb9\u76d0\u5435\u67b6\uff0c\u201d \u3000\u3000\u201c\u4f46\u73b0\u5728\u4f60\u4ecd\u90fd\u53ef\u4ee5\u4e92\u76f8\u6000\u5ff5\u6000\u5ff5\u90a3\u4e9b\u751c\u871c\u7684\u5f80\u4e8b\u6000\u5ff5\u5bf9\u65b9\u7684\u597d\u5904\u4ee5\u5f8c\u82e5\u80fd\u518d\u76f8\u89c1\u5c31\u4f1a \u89c9\u5f97\u66f4\u5feb\u4e50\u3002\u201d \u3000\u3000\u201c\u4ee5\u5f8c\u5c31\u7b97\u4e0d\u80fd\u76f8\u89c1\u4e5f\u65e0\u59a8\xb7\u56e0\u4e3a\u4f60\u81f3\u5c11\u5df2\u6709\u4e86\u6bb5\u6e29\u591f\u7684\u56de\u5fc6\uff0c\u8ba9\u4f60\u5750\u5728\u7089\u8fb9\u70e4\u706b\u65f6\u80fd \u6709\u4ef6\u4ee4\u4f60\u6e29\u6696\u7684\u4e8b\u60f3\u60f3\u3002\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u76ee\u5df2\u7684\u547d\u8fd0\u4f60\u65e2\u4e0d\u80fd\u52c9\u5f3a\u4e5f\u4e0d\u5fc5\u52c9\u5f3a\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u6839\u672c\u6ca1\u6709\u4ec0\u9ebd\u4e8b\u597d\u75db\u82e6\u7684\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u738b\u52a8\u4ed6\u4eec\u5bf9\u8fd9\u4ef6\u4e8b\u5458\u5f8c\u7684\u7ed3\u8bba\u3002 \u3000\u3000\u4ece\u6b64\u4ee5\u5f8c\u4ed6\u4eec\u8c01\u4e5f\u6ca1\u6709\u518d\u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u6ca1\u6709\u518d\u63d0\u8d77\u90a3\u91d1\u94fe \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e86\u89e3\u90ed\u5927\u8def\u7684\u611f\u60c5\u4e86\u89e3\u8fd9\u91d1\u954a\u5b50\u5728\u4ed6\u5fc3\u91cc\u7684\u4ef7\u503c\u3002 \u3000\u3000\u6709\u4e9b\u4e1c\u897f\u7684\u4ef7\u503c\u5f80\u5f80\u662f\u522b\u4eba\u65e0\u6cd5\u8861\u91cf\u7684\u3002 \u3000\u3000\u738b\u52a8\u8fd8\u671f\u5728\u5e8a\u535c\uff0c\u5ffd\u7136\u542c\u5230\u90ed\u5927\u8def\u5728\u5916\u9762\u558a\u201c\u5a18\u8205\u6765\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u5a18\u8205\u3002 \u3000\u3000\u201c\u5a18\u8205\u201d\u7684\u610f\u601d\u5c31\u662f\u90a3\u5f53\u94fa\u7684\u8001\u677f\u201c\u6d3b\u5265\u76ae\u201d\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u5f53\u7136\u5e76\u4e0d\u59d3\u6d3b\u4e8b\u5b9e\u4e0a\u4e5f\u4e0d\u592a\u5265\u76ae\uff0c\u4ed6\u6700\u591a\u4e5f\u4e0d\u8fc7\u522e\u522e\u4f60\u8eab\u4e0a\u7684\u6cb9\u6c34\u800c\u5df2\u5f53\u7136\u522e\u5f97 \u76f8\u5f53\u5f7b\u5e95\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u8d8a\u60f3\u522e\u4eba\u6cb9\u6c34\u7684\u4eba\u8d8a\u957f\u4e0d\u80d6\u3002 \u3000\u3000\u4ed6\u770b\u6765\u5c31\u8c61\u662f\u53ea\u98ce\u5e72\u4e86\u7684\u91ce\u5154\u5b50\xb7\u603b\u662f\u9a7c\u80cc\u772f\u773c\u775b\u8bf4\u8bdd\u7684\u65f6\u5019\u603b\u662f\u7528\u773c\u89d2\u770b\u4f60\u597d\u50cf\u968f\u65f6 \u968f\u5730\u90fd\u5728\u6253\u91cf\u4f60\u8eab\u4e0a\u7684\u4e1c\u897f\u53ef\u4ee5\u503c\u591a\u5c11\u94f6\u5b50\u3002 \u3000\u3000\u738b\u52a8\u4ed6\u4eec\u867d\u7136\u5e38\u5e38\u9ebd\u62dc\u8bbf\u4ed6\u4f46\u4ed6\u8fd8\u662f\u7b2c\u6b21\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u6240\u4ee5\u738b\u52a8\u603b\u7b97\u4e5f\u52c9\u5f3a\u8d77\u4e86\u5e8a\u3002 \u3000\u3000\u8c61\u6d3b\u5265\u76ae\u8fd9\u79cd\u4eba\u82e5\u80af\u722c\u534a\u4e2a\u591a\u65f6\u8fb0\u7684\u5c71\xb7\u53bb\u201c\u62a8\u8bbf\u201d\u4e2a\u4eba\u7684\u65f6\u5019\u901a\u5e38\u90fd\u53ea\u6709\u4e00\u79cd\u7406\u7531\u3002 \u3000\u3000\u90a3\u7406\u7531\u901a\u5e38\u90fd\u548c\u9ec4\u9f20\u72e0\u53bb\u62dc\u8bbf\u9e21\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u571f\u52a8\u8d70\u8fdb\u5ba2\u5385\u7684\u65f6\u5019\u90ed\u5927\u8def\u6b63\u5728\u7b11\u95ee\u201c\u662f\u7530\u9635\u98ce\u628a\u6062\u5439\u6765\u7684\u96be\u9053\u4f60\u60f3\u6765\u4e70\u738b\u52a8\u7684\u8fd9\u680b\u623f \u5b50\u201d \u3000\u3000\u4ed6\u77e5\u9053\u738b\u52a8\u81f3\u5c11\u6709\u4e8c\u5341\u51e0\u79cd\u6cd5\u5b50\u60f3\u5c06\u8fd9\u623f\u5b50\u5356\u51fa\u53bb\uff0c\u53ea\u53ef\u60dc\u770b\u6765\u4ed6\u5c31\u7b97\u767d\u9001\u7ed9\u522b\u4eba\u522b\u4eba \u90fd\u4e0d\u8981\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u7684\u5934\u6447\u5f97\u5c31\u8c61\u968f\u65f6\u90fd\u4f1a\u4ece\u8116\u5b50\u4e0a\u6389\u4e0b\u6765\u4e8e\u7b11\u9053\u201c\u8fd9\u9ebd\u5927\u7684\u5c45\u5b50\xb7\u6211\u600e\u9ebd\u4e70\u5f97\u8d77\uff1f \u81ea\u4ece\u9047\u89c1\u4f60\u8d22\u300f\u4e4b\u5f8c\u6211\u7b80\u76f4\u8fde\u8001\u672c\u90fd\u5feb\u8d54\u5149\u4e86\xb7\u4e0d\u5356\u623f\u5343\u5df2\u7ecf\u5f88\u8fd0\u6c14\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5047\u5982\u4ed6\u80af\u4fbf\u76f4\u5356\u5462\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6211\u4e70\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u4f9d\u6240\u4ee5\u518d\u8f6c\u8ba9\u7ed9\u522b\u4eba\uff0c\u4e5f\u53ef\u4ee5\u81ea\u5df1\u4efb\u8fdb\u6765\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u7b11\u9053\u201c\u6ca1\u6709\u6bdb\u75c5\u7684\u4eba\xb7\u8c01\u80af\u4f4f\u8fdb\u8fd9\u79cd\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8fd8\u60f3\u518d\u515c\u515c\u751f\u610f\uff0c\u6d3b\u5265\u76ae\u5ffd\u53c8\u9053\u201c\u4f60\u4eec\u73b0\u5728\u662f\u4e0d\u662f\u72e0\u7f3a\u94b1\u7528\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\u201c\u6211\u4eec\u54ea\u5929\u4e0d\u7f3a\u94b1\u7528\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u90a3\u4f60\u4eec\u60f3\u4e0d\u60f3\u5e73\u81ea\u65e2\u4e94\u767e\u4e24\u94f6\u5b50\u201d \u3000\u3000\u201c\u5f53\u7136\u60f3\u3002\u201d \u3000\u3000\u4f46\u65e0\u8bba\u8c01\u90fd\u77e5\u9053\u6d3b\u5265\u76ae\u7684\u94f6\u5b50\u7edd\u4e0d\u4f1a\u662f\u5bb9\u6613\u5acc\u7684\uff0c\u4ece\u8001\u864e\u5934\u4e0a\u62d4\u6839\u6bdb\u4e5f\u8bb8\u53cd\u5012\u5bb9\u6613\u4e9b\u3002 \u96cc\u516c\u9e21\u8eab\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u6bdb\u53ef\u62d4\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4e94\u767e\u4e24\u94f6\u5b50\u7684\u8bf1\u60d1\u5b9e\u5728\u592a\u5927\u3002 \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u4f60\u8bf4\u7684\u662f\u4e94\u767e\u4e24\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6574\u6574\u4e94\u767e\u4e24\u3002\u201d\u90ed\u5927\u8def\u4e0a\u4e0a\u4e0b\u6263\u91cf\u4ed6\u51e0\u773c\uff0c\u9053\u201c\u4f60\u662f\u4e0d\u662f\u559d\u9189\u4e86\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6211\u6e05\u9192\u5f97\u5f88\u53ea\u8981\u4f60\u4eec\u7b54\u5e94\xb7\u6211\u73b0\u5728\u5c31\u53ef\u4ee5\u5148\u4ed8\u534a\u5b9a\u91d1\u3002\u201d\u4ed6\u2018\u5411\u62a5\u4fe1\u4efb\u8fd9\u4e9b \u4eba\xb7\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e9b\u4eba\u867d\u7136\u6587\u4e0d\u540d\u4f46\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u91cd\u903e\u5e72\u91d1\u3002 \u3000\u3000\u90ed\u5927\u8def\u5438\u4e86\u53f9\u6c14\u9053\u201c\u8fd9\u94f6\u5b50\u8981\u600e\u9ebd\u6837\u624d\u80fd\u5acc\u5f97\u5230\u5462\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u5f88\u5bb9\u6613\uff0c\u53ea\u8981\u4f60\u4eec\u8ddf\u6211\u5230\u53bf\u57ce\u91cc\u53bb\u8d70\u8d85\uff0c\u94f6\u5b50\u5c31\u5230\u624b\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8d70\u8d9f\uff1f\u600e\u9ebd\u8d70\u6cd5\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u5f53\u7136\u662f\u7528\u4e24\u6761\u817f\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8d70\u4e86\u4e24\u6b65\xb7\u9053\u201c\u5c31\u8fd9\u9ebd\u6837\u8d70\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u662d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u7136\u5f8c\u4f60\u4eec\u5c31\u53ef\u4ee5\u5e26\u4e94\u767e\u4e24\u94f6\u5b50\u8d70\u56de\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u522b\u7684\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u738b\u52a8\u7b11\u9053\u201c\u8d70\u9500\u5c31\u80fd\u7530\u4e94\u767e\u4e24\u94f6\u4e8e\u8fd9\u79cd\u4e8b\u4f60\u542c\u8bf4\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u3002\u201d\u6d3b\u5265\u76ae\u9053\u201c\u6709\u5f88\u591a\u4e8b\u4f53\u4eec\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\uff0c\u4f46\u5374\u5e76\u4e0d\u662f\u5047\u66f2\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8d54\u672c\u4e5f\u4e0d\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6700\u8fd1\u751f\u610f\u7684\u786e\u8d8a\u6765\u8d8a\u96be\u505a\u4e86\uff0c\u5f53\u7684\u4eba\u591a\u8d4e\u7684\u4eba\u5c11\u65ad\u4e86\u5f53\u7684\u4e1c\u897f\u53c8 \u5356\u4e0d\u51fa\u53bb\xb7\u6211\u8981\u7684\u5229\u94b1\u53c8\u5c11\u3002\u201d \u3000\u3000\u738b\u52a8\u70b9\u70b9\u5934\u663e\u5f97\u5f88\u540c\u60c5\u7684\u6837\u4e88\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u65e2\u662f\u8d54\u672c\u7684\u751f\u610f\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u505a\u5462\u201d \u3000\u3000\u6d3b\u5265\u76ae\u53f9\u9053\u201c\u90a3\u4e5f\u662f\u6ca1\u6cd5\u5b50\u5509\u8c01\u53eb\u6211\u5f53\u521d\u9009\u4e86\u8fd9\u884c\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u90a3\u4e94\u767e\u4e24\u94f6\u5b50\u4f60\u8fd8\u662f\u7559\u81ea\u5df1\u6162\u6162\u7528\u5427\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u62a2\u9053\u201c\u90a3\u4e0d\u540c\u90a3\u662f\u6211\u81ea\u5df1\u613f\u610f\u8ba9\u4f60\u4eec\u5acc\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u6de1\u6de1\u7684\u9053\u201c\u4f60\u7684\u94b1\u6765\u5f97\u5e76\u4e0d\u5bb9\u6613\u6211\u4ecd\u53ea\u8d70\u4e00\u8d9f\uff0c\u5c31\u8981\u4f60\u4e94\u767e\u4e24\u8fd9\u79cd\u4e8b\u6211\u7fc1\u600e\u9ebd\u597d\u610f \u601d\u505a\u5462\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u82cd\u767d\u7684\u8138\u597d\u50cf\u6709\u70b9\u53d1\u7ea2\u3002\u4e7e\u54b3\u9053\u90a3\u6709\u751a\u9ebd\u4e0d\u597d\u610f\u601d\uff1f\u4f55\u51b5\xb7\u6211\u8981\u4f60\u4eec\u966a\u6211\u8d70\u8fd9 \u8d9f\xb7\u5f53\u7136\u4e5f\u6709\u7530\u8471\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ec0\u9ebd\u7528\u610f\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u53c8\u4e7e\u54b3\u4e86\u51e0\u58f0\uff0c\u52c9\u5f3a\u7684\u7b11\u9053\u201c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u53cd\u6b63\u4e0d\u4f1a\u8981\u4f60\u4eec\u53bb\u5f53\u5f3a\u76d7\u4e5f\u4e0d\u4f1a\u8981\u4f60 \u4eec\u53bb\u6740\u4eba\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u618e\u7136\u9053\u201c\u4e94\u767e\u4e24\u94f6\u4e88\u4f60\u4e0d\u60f3\u8981\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u6014\u5382\u534a\u664c\xb7\u5ffd\u53c8\u7b11\u9053\u201c\u4f60\u4e00\u4e2a\u4eba\u4e0d\u53bb\u4e5f\u6ca1\u5173\u7cfb\u6211\u8fd8\u662f \u3000\u3000\u852c\u4e03\u5ffd\u7136\u9053\u201c\u4ed6\u4e0d\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u4f60\u4e5f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4e5f\u4e0d\u5b8f\u800c\u4e14\u4e5f\u6ca1\u6709\u539f\u56e0\u4e0d\u53bb\u5c31\u662f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u6211\u672c\u6765\u8fd8\u4ee5\u4e3a\u53ea\u6709\u6211\u4e2a\u4eba\u4e0d\u80af\u53bb\uff0c\u8c01\u77e5\u5927\u5bb6\u90fd\u6837\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u6025\u4e86\u5927\u58f0\u9053\u201c\u6211\u7684\u94f6\u5b50\u96be\u9053\u4e0d\u597d\uff1f\u4f60\u4eec\u96be\u9053\u6ca1\u62ff\u8fc7\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u6de1\u9053\u54e6\u4eec\u82e5\u8981\u4f60\u7684\u94f6\u5b50\uff0c\u81ea\u7136\u4f1a\u62ff\u4e1c\u897f\u53bb\u5f53\u7684\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6211\u4e0d\u8981\u4f60\u8ba2\u7684\u4e1c\u897f\xb7\u53ea\u8981\u4f60\u851a\u300f\u8ddf\u6211\u8d70\u8d9f\xb7\u5c31\u7ed8\u4f60\u4eec\u4e94\u767e\u4e24\u94f6\u5b50\u4f60\u4ec0\u53cd\u800c\u4e0d \u80af\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u597d\u50cf\u8981\u8df3\u4e86\u8d77\u6765\u5927\u58f0\u9053\u201c\u4f60\u4eec\u7a76\u7adf\u6709\u4ec0\u9ebd\u6bdb\u75c5\uff1f\u2026\u2026\u6211\u770b\u4f60\u4eec\u8fdf\u65e9\u603b\u6709\u5929\u4f1a\u8981\u997f \u6b7b\u7684\u3002\u4e00\u3002\u50cf\u4f60\u4eec\u8fd9\u79cd\u4eba\u82e5\u662f\u4e0d\u7a77\u90a3\u624d\u771f\u662f\u602a\u4e8b\u3002 \u3000\u3000\u738b\u52a8\u4ed6\u8861\u7684\u786e\u6709\u70b9\u6bdb\u75c5\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u786e\u5b81\u53ef\u7a77\u6b7b\u3001\u997f\u6b7b\u4f46\u6765\u8def\u4e0d\u660e\u7684\u94b1\u4ed6\u4eec\u662f\u7edd\u4e0d\u80af\u8981\u5207\u3002 \u3000\u3000\u62ff\u4e1c\u897f\u53bb\u5f53\u5e76\u4e0d\u4e22\u4eba\uff0c\u4ed6\u4eec\u51e0\u4e4e\u4ec0\u9ebd\u4e1c\u897f\u90fd\u5f53\u8fc7\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u53ea\u5f53\u4e1c\u897f\u4e0d\u5f53\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u5b81\u53ef\u5c06\u81ea\u5df1\u7684\u88e4\u5b50\u90fd\u62ff\u53bb\u5f53\u4f46\u5374\u5b9a\u8981\u4fdd\u4f50\u81ea\u5df1\u7684\u5c0a\u4e25\u826f\u5fc3\u3002 \u3000\u3000\u4ed6\u8ba2\u53ea\u505a\u81ea\u5df1\u613f\u610f\u505a\u800c\u4e14\u89c9\u5f97\u5e94\u8be5\u505a\u7684\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u8981\u4e0a\u5395\u6240\u7684\u800c\u4e14\u6bcf\u5929\u81f3\u5c11\u8981\u53bb\u4e03\u516b\u6b21\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u65e2\u4e0d\u810f\u4e5f\u804c\u6ed1\u7a3d\xb7\u53ea\u4e0d\u8fc7\u662f\u4ef6\u5f88\u6b63\u5e38\u3001\u5f88\u666e\u901a\u800c\u4e14\u975e\u505a\u4e0d\u53ef\u7684\u4e8b\u6240\u4ee5\u6839\u672c\u5df2\u4e0d\u503c \u5f97\u5728\u6211\u851a\u7684\u6545\u4e8b\u4e2d\u63d0\u8d77\u3002 \u3000\u3000\u5047\u5982\u6709\u4eba\u8981\u5c06\u8fd9\u79cd\u4e8b\u5199\u51fa\u6765\xb7\u90a3\u9ebd\u4e2a\u5341\u4e07\u5b57\u7684\u6545\u4e8b\u81f3\u5c11\u53ef\u4ee5\u5199\u6210\u4e8c\u5341\u4e07\u5b87\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u4e8b\u6709\u65f6\u5374\u53c8\u4e0d\u80fd\u4e0d\u63d0\u662f\u4e0a\u5395\u6240\u3002 \u3000\u3000\u4ed6\u56de\u5230\u5ba2\u300f\u2019\u91cc\u7684\u65f6\u5019\u53d1\u73b0\u71d5\u4e03\u548c\u6797\u592a\u5e73\u7684\u795e\u60c5\u597d\u50cf\u90fd\u6709\u70b9\u7279\u522b\xb7\u597d\u50cf\u5fc3\u91cc\u90fd\u6709\u8bdd\u8981\u8bf4 \u5374\u53c8\u4e0d\u60f3\u8bf4\u3002 \u3000\u3000\u6240\u4ee5\u738b\u52a8\u4e5f\u4e0d\u95ee\u4ed6\u5411\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u800c\u4e14\u77e5\u9053\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4f60\u5982\u679c\u60f3\u95ee\xb7\u5c31\u4e0d\u5982\u7b49\u4ed6\u4eec\u81ea \u5df1\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u71d5\u4e03\u679c\u7136\u6c89\u4e0d\u4f4f\u6c14\xb7\u5ffd\u7136\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u95ee\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u6ca1\u6709\u770b\u5230\u8fd9\u91cc\u5c11\u4e86\u4e2a\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u70b9\u70b9\u5934\xb7\u9053\u201c\u597d\u50cf\u662f\u5c11\u4e86\u4e2a\u3002\u201d \u3000\u3000\u5c11\u4e86\u7684\u4e2a\u4eba\u662f\u90ed\u5927\u8def\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u7b11\u9053\u201c\u4ed6\u5230\u54ea\u5766\u514b\u53bb\u90fd\u6ca1\u5173\u7cfb\uff0c\u4f46\u4f60\u5982\u679c\u5b9a\u8981\u6211\u95ee\u6211\u95ee\u95ee\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u6162\u9992\u7684\u5750\u4e0b\u6765\xb7\u56db\u9762\u770b\u4e86\u770b\xb7\u624d\u95ee\u9053\u201c\u5c0f\u90ed\u5230\u54ea\u91cc\u53bb\u4e2b\uff1f\u201d\u71d5\u4e03\u5ffd\u7136\u51b7\u7b11\u4e86\u58f0\u9053\u201c\u4f60 \u6c38\u8fdc\u731c\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u5c31\u56e0\u4e3a\u731c\u4e0d\u5230\xb7\u6240\u4ee5\u624d\u8981\u95ee\u3002\u201d \u3000\u3000\u71d5\u4e03\u54ac\u77aa\u5507\uff0c\u9053\u201c\u53bb\u8ffd\u6d3b\u5265\u76ae\u6d3b\u5265\u76ae\u8d70\u4ed6\u5c31\u8ffd\u4e86\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u52a8\u8fd9\u624d\u6709\u70b9\u5947\u602a\u76b1\u76b1\u7709\u9053\u201c\u53bb\u8ffd\u6d3b\u5265\u76ae\u4e8e\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u95ed\u5634\u8138\u8272\u6709\u70b9\u53d1\u9752\u3002\u738b\u52a8\u770b\u4ed6\uff0c\u7483\u54fa\u9053\u201c\u96be\u9053\u4ed6\u4e3a\u4e94\u767e\u4e24\u94f6\u5b50\uff0c\u5c31\u80af\u53bb\u505a\u6d3b\u5265\u76ae\u7684 \u8ddf\u73ed\uff1f\u201d \u3000\u3000\u4ed6\u6447\u4e86\u6447\u5934\u9053\u201c\u8fd9\u79cd\u4e8b\u6211\u7edd\u4e0d\u4fe1\u5c0f\u90ed\u7edd\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u8fd9\u79cd\u4e8b\u6211\u4e5f\u4e0d\u613f\u610f\u76f8\u4fe1\u4f46\u5374\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8bef\u4f1a\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u6211\u4eb2\u773c\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u770b\u5230\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u770b\u5230\u4ed6\u8ddf\u6d3b\u5265\u76ae\u6652\u5495\u4e86\u534a\u5929\uff0c\u6d3b\u5265\u76ae\u62ff\u51fa\u5382\u952d\u94f6\u5b50\u7ed9\u4ed6\u4ed6\u5c31\u8ddf\u6797\u592a\u5e73\u5b9a\u5382\u3002\u201d \u3000\u3000\u5e72\u52a8\u6014\u5382\u6014\u9053\u201c\u4f60\u6ca1\u6709\u8ffd\u8fc7\u53bb\u95ee\uff1f\u201d\u71d5\u4e03\u51b7\u7b11\u9053\u201c\u6211\u8ffd\u53bb\u5e72\u4ec0\u9ebd\uff1f\u6211\u53c8\u4e0d\u60f3\u505a\u6d3b\u5265\u76ae\u7684 \u8ddf\u73ed\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u53f9\u5382\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u5047\u5982\u53ea\u4e0d\u8fc7\u662f\u505a\u7f00\u73ed\u8ddf\u4ed6\u5230\u57ce\uff01 \u3000\u3000\u56db\u6708\u65e5\u70b9\u5206\uff01\u91cc\u53bb\u8d70\u8d9f\u5012\u4e5f\u6ca1\u4ec0\u9ebd\u5173\u7cfb\u4f46\u6211\u770b\u8fd9\u4ef6\u4e8b\u7edd\u4e0d\u4f1a\u5982\u6b64\u7b80\u5355\u3002\u201d \u3000\u3000\u5f53\u7136\u4e0d\u4f1a\u5982\u6b64\u7b80\u5355\u3002 \u3000\u3000\u5047\u5982\u6d3b\u5265\u76ae\u771f\u7684\u53ea\u4e0d\u8fc7\u60f3\u627e\u4e2a\u613f\u73ed\xb7\u4e3a\u4e86\u4e94\u94b1\u94f6\u5b50\u5c31\u80af\u505a\u4ed6\u8ddf\u73ed\u7684\u4eba\u6ee1\u8857\u90fd\u662f\u4ed6\u53c8\u4f55\u5fc5 \u5b9a\u5230\u8fd9\u91cc\u6765\u627e\u4ed6\u884c\uff1f \u3000\u3000\u6797\u592a\u5e73\u63a5\u9053\u201c\u6d3b\u5265\u76ae\u81ea\u5df1\u4e5f\u8bf4\u8fc7\uff0c\u4ed6\u8fd9\u6837\u505a\u5fc5\u5b9a\u53e6\u6709\u7528\u610f\u6211\u770b\u4ed6\u7edd\u4e0d\u4f1a\u5e72\u4ec0\u9ebd\u597d\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u80fd\u8ba9\u6d3b\u5265\u76ae\u8fd9\u79cd\u4eba\u5fc3\u7518\u60c5\u613f\u62ff\u51fa\u4e94\u767e\u4e24\u94f6\u5b50\u6765\u7684\u53ea\u6709\u79cd\u4e8b\u3002\u201d \u3000\u3000\u6797\u5927\u4e4e\u9053\u201c\u54ea\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u65e2\u4e94\u5343\u4e24\u94f6\u5b50\u7684\u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e0d\u9519\u82e5\u975e\u4e00\u672c\u4e07\u5229\u7684\u4e8b\xb7\u4ed6\u7edd\u4e0d\u80af\u638f\u8170\u5305\u62ff\u51fa\u4e94\u767e\u4e24\u94f6\u4e8e\u6765\u4e2d \u3000\u3000\u71d5\u4e03\u9053\u201c\u771f\u6b63\u80fd\u672c\u4e07\u5229\u7684\u4e5f\u53ea\u6709\u79cd\u4e8b\u3002\u201d \u3000\u3000\u852c\u4e03\u9053\u201c\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e0d\u9519\xb7\u6211\u770b\u4ed6\u4e0d\u662f\u53bb\u5077\u5c31\u662f\u53bb\u9a97\uff0c\u4e49\u751f\u4f2f\u522b\u4eba\u53d1\u89c9\u5f8c\u5bf9\u4ed6\u4e0d\u5ba2\u6c14\u6240\u4ee5\u624d\u6765\u627e \u6211\u4eec\u505a\u4ed6\u7684\u4fdd\u954a\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\u63a5\u9053\u201c\u8fd9\u9053\u7406\u90ed\u5927\u8def\u96be\u9053\u60f3\u4e0d\u5230\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u7b11\u9053\u201c\u8fde\u4f60\u90fd\u80fd\u60f3\u5f97\u5230\u4ed6\u600e\u9ebd\u4f1a\u60f3\u4e0d\u5230\u4ed6\u53c8\u4e0d\u6bd4\u522b\u4eba\u7b28\u3002\u201d \u3000\u3000\u738b\u52a8\u76f4\u5728\u6ce8\u610f\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u6b64\u523b\u5ffd\u7136\u9053\u201c\u4f53\u82e5\u8ba4\u4e3a\u4ed6\u4e0d\u8be5\u53bb\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u62e6\u4ed6\uff1f\u201d \u3000\u3000\u84b8\u4e03\u51b7\u51b7\u9053\u201c\u4e2a\u4eba\u82e5\u662f\u81ea\u5df2\u60f3\u5f80\u6ce5\u5751\u8df3\uff0c\u522b\u4eba\u5c31\u7b97\u60f3\u62c9\u4e5f\u62c9\u4e0d\u4f4f\u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u6240\u4ee5\u4f60 \u5c31\u773c\u770b\u4ed6\u8df3\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5634\u5507\u9053\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\u51b2\u4e86\u51fa\u53bb\u773c\u775b\u5c16\u7684\u4eba\u5c31\u80fd\u770b\u5230\u4ed6\u51b2\u51fa\u53bb\u7684\u65f6\u5019\u5df2\u7ecf\u6cea\u6c6a\u6c6a\xb7\u597d\u50cf\u6c14\u5f97\u5feb\u54ed\u51fa \u6765\u4e86\u3002 \u3000\u3000\u738b\u52a8\u7684\u773c\u775b\u5f88\u707e\u3002 \u3000\u3000\u4ed6\u4e2a\u4eba\u5750\u5728\u90a3\u91cc\u53d1\u5382\u4e2d\u5929\u6014\u5ffd\u4e5f\u53f9\u4e86\u53e3\u6c14\xb7\u54fa\u95fb\u9053\u201c\u7231\u4e4b\u6df1\u8d23\u4e4b\u5207\xb7\u770b\u6765\u8fd9\u53e5\u8bdd\u5012\u771f\u662f \u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u5728\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u7b11\uff0c\u9053\u201c\u6211\u5728\u8bf4\xb7\u5230\u73b0\u5728\u6211\u8fd8\u662f\u4e0d\u4fe1\u5c0f\u90ed\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff0c\u4f60\u5462\u201d \u3000\u3000\u6797\u592a\u5e73\u8fdf\u7591\u9053\u201c\u6211\u2026\u201d\u6211\u4e5f\u4e0d\u592a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u81f3\u5c11\u603b\u8fd8\u6709\u70b9\u6000\u7591\xb7\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f46\u71d5\u4e03\u5374\u70b9\u4e5f\u4e0d\u6000\u7591\u5df2\u8ba4\u5b9a\u4e86\u5c0f\u90ed\u4f1a\u505a\u90a3\u79cd\u4e8b\uff0c\u4f60\u53ef\u77e5\u9053\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u60f3\u5382\u60f3\uff0c\u9053\u201c\u6211\u4e5f\u6709\u70b9\u5947\u602a\xb7\u4ed6\u548c\u5c0f\u90ed\u7684\u4ea4\u60c5\u672c\u6765\u597d\u50cf\u7279\u522b\u597d\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u5c31\u56e0\u4e3a\u4ea4\u60c5\u7279\u522b\u597d\u6240\u4ee5\u624d\u76f8\u4fe1\u4e9b\u3002\u201d \u3000\u3000\u6797\u4eba\u5e73\u53c8\u60f3\u4e86\u60f3\xb7\u9053\u201c\u4e3a\u4ec0\u9ebd\u5462\uff1f\u6211\u4e0d\u503c\u3002\u201d\u5de5\u52a8\u9053\u201c\u6731\u73e0\u5ffd\u7136\u5931\u8e2a\xb7\u6211\u3002\u90fd\u60f3\u5230\u53ef\u80fd \u6709\u522b\u7684\u539f\u56e0\xb7\u4f46\u5c0f\u90ed\u5374\u60f3\u4e0d\u5230\xb7\u6240\u4ee5\u5c31\u5f80\u6700\u574f\u7684\u5730\u65b9\u53bb\u60f3\uff0c\u90a3\u53c8\u662f\u4e3a\u4e86\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u56e0\u4e3a\u4ed6\u5bf9\u6731\u73e0\u7528\u60c5\u592a\u6df1\u6240\u4ee5\u201c\u2026\u2606\u738b\u52a8\u9053\u201c\u6240\u4ee5\u8111\u7b4b\u5c31\u4e0d\u6e05\u695a\u4e01\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u7231\u60c5\u53ef\u4ee5\u4ee4\u4eba\u76f2\u76ee\u8fd9\u9053\u7406\u5927\u591a\u6570\u4eba\u90e1\u77e5\u9053\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8001\u5bf9\u4e00\u4e2a\u4eba\u7528\u60c5\u5f88\u6df1\xb7\u90a3\u9ebd\u4f60\u5bf9\u4ed6\u7684\u5224\u65ad\u5c31\u4e0d\u4f1a\u6b63\u786e\u56e0\u4e3a\u4f53\u5e73\u65f6\u53ea\u80fd\u770b\u5230\u4ed6 \u7684\u597d\u5904\u4f46\u53ea\u8981\u6709\u4e86\u4e2a\u5c0f\u5c0f\u7684\u53d8\u5316\u548c\u6253\u51fb\u4f24\u5c31\u7acb\u523b\u4f1a\u81ea\u8d23\u81ea\u6028\uff0c\u7528\u5f97\u60a3\u5931\uff0c\u6240\u4ee5\u5c31\u5fcd\u4e0d\u4f4f\u8981\u5f80 \u6700\u574f \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u7684\u610f\u601d\u6211\u906d\xb7\u53ea\u4e0d\u8fc7\u8fd9\u6bd4\u665a\u5374\u597d\u50cf\u4e0d\u592a\u6070\u5f53\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5ce8\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u4f60\u600e\u9ebd\u80fd\u62ff\u6731\u6b8a\u548c\u5c0f\u90ed\u7684\u4e8b\u6765\u6bd4\uff1f\u5c0f\u7ff1\u5bf9\u6731\u73e0\u7684\u60c5\u611f\u600e\u4f1a\u8ddf\u71d5\u4e03\u5bf9\u5c0f\u90ed\u7684 \u60c5\u611f\u6837\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u5df2\u53d1\u89c9\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u53c8\u597d\u50cf\u89c9\u5f97\u81ea\u5df1\u8bdd\u8bf4\u5f97\u592a\u591a\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u8fd8\u5728\u7b11\u800c\u4e14\u7b11\u5f97\u5f88\u7279\u522b\u3002 \u3000\u3000\u76f4\u7b49\u770b\u5230\u85cf\u4e03\u4ece\u9662\u5b50\u91cc\u5f80\u5916\u8d70\u7684\u65f6\u5019\xb7\u4ed6\u624d\u5f00\u53e3\u9053\u201c\u4f60\u60f3\u51fa \u3000\u3000\u71d5\u4e03\u773c\u775b\u8fd8\u662f\u7ea2\u7ea2\u7684\u52c9\u5f3a\u7b11\u9053\u201c\u4eca\u5929\u5929\u6c14\u597d\u4e86\u4e9b\uff0c\u6211\u60f3\u51fa\u53bb\u6253\u6253\u730e\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7ad9\u8d77\u6765\xb7\u7b11\u9053\u201c\u6211\u4e5f\u53bb\u4eca\u5929\u518d\u4e0d\u51fa\u53bb\u6253\u730e\u53ea\u4f2f\u5c31\u771f\u7684\u8981\u997f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u7b11\u3002\u9053\u201c\u5c0f\u90ed\u8eab\u4e0a\u65e2\u7136\u6709\u4e86\u94f6\u5b50\u3001\u5c31\u7edd\u4e0d\u4f1a\u8ba9\u6211\u4eec\u997f\u6b7b\xb7\u4f11\u4e3a\u4ec0\u9ebd\u4e0d\u7b49\u4ed6\u56de \u6765\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7acb\u523b\u6c89\u4e0b\u4e86\u80dc\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u7b49\u4ed6\u56de\u6765\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5c31\u7b97\u4e3a\u4e86\u6211\uff0c\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u4f4e\u4e0b\u5934\u7ad9\u5728\u9662\u5b50\u91cc\u3002 \u3000\u3000\u5929\u867d\u5df2\u653e\u6674\xb7\u98ce\u5374\u8fd8\u662f\u51b7\u5f97\u523a\u9aa8\u3002 \u3000\u3000\u738b\u52a8\u5374\u4eff\u6cb8\u70b9\u4e5f\u4e0d\u89c9\u5f97\u51b7\xb7\u7ad9\u5728\u90a3\u91cc\u5446\u4e86\u5f88\u4e45\xb7\u624d\u51b7\u51b7\u9053\u201c\u4ed6\u82e6\u4e0d\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u53c8\u7b11\u7b11\u9053\u201c\u4ed6\u82e5\u4e0d\u56de\u6765\u6211\u5c31\u8bf7\u4f60\u4eec\u5403\u72d7\u8089\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5fcd\u4e0d\u4f4f\u9053\u201c\u8fd9\u79cd\u5929\u6c14\u5230\u804c\u91cc\u627e\u72d7\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7528\u4e0d\u627e\uff0c\u8fd9\u91cc\u5c31\u6709\u6761\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u72d7\u5728\u7530\u91cc\uff1f\u201d \u3000\u3000\u738b\u52a8\u6307\u81ea\u5df1\u7684\u9f3b\u5b50\u9053\u201c\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7728\u7728\u773c\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u662f\u72d7\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u72d7\u3001\u800c\u4e14\u662f\u6761\u571f\u72d7\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u4e0d\u7b11\u8c08\u6de1\u7684\u63a5\u9053\u201c\u4e2a\u4eba\u82e5\u8fde\u81ea\u5df1\u7684\u670b\u53cb\u662f\u56db\u79cd\u4eba\u90fd\u5206\u4e0d\u51fa\u4e0d\u662f\u571f\u72d7\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e0d\u662f\u571f\u72d7\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f88\u5feb\u5c31\u56de\u6765\u4e86\u800c\u4e14\u5927\u5305\u5c0f\u5305\u7684\u5e26\u4e86\u5927\u5806\u4e1c\u897f\u56de\u6765\u3002 \u3000\u3000\u5c0f\u5305\u91cc\u662f\u8089\xb7\u5927\u5305\u91cc\u662f\u9992\u5934\u6700\u5c0f\u5305\u91cc\u662f\u82b1\u751f\u7c73\u3002 \u3000\u3000\u65e2\u7136\u6709\u82b1\u751f\u7c73\uff0c\u5f53\u7136\u4e0d\u4f1a\u6ca1\u6709\u9152\u3002 \u3000\u3000\u6ca1\u6709\u82b1\u751f\u7c73\u4e5f\u4e0d\u80fd\u6ca1\u6709\u9152\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u73b0\u5728\u5df2\u5f00\u59cb\u6709\u70b9\u6000\u5ff5\u9ea6\u8001\u5e7f\u4e86\u81ea\u4ece\u4ed6\u8d70\u8fd9\u91cc\u5c31\u597d\u50cf\u518d\u4e5f\u627e\u4e0d\u51fa\u4e00\u4e2a\u5364\u83b1 \u505a\u5f97\u597d\u7684\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u81f3\u5c11\u8fd8\u5bb6\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u5047\u5982\u4f60\u5f00\u5bb6\u996d\u9986\u5b50\u751f\u610f\u4e00\u5b9a\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u5012\u662f\u597d\u4e3b\u610f\u53ea\u65f6\u63cf\u8fd8\u6709\u6837\u4e0d\u5bf9\u2026\u2026\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54ea\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9000\uff0c\u201c\u6211\u90a3\u996d\u9505\u751f\u610f\u518d\u597d\uff0c\u5f00\u4e0d\u4e86\u4e8c\u5929\u4e5f\u5f97\u5173\u95e8\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u5c31\u7b97\u6211\u8089\u5df1\u6ca1\u6709\u628a\u81ea\u5df1\u5403\u57ae\uff0c\u4f60\u4eec\u4e5f\u4f1a\u6765\u628a\u6211\u5403\u57ae\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7a81\u7136\u51b7\u7b11\u9053\u201c\u653e\u5fc3\xb7\u6211\u7edd\u4e0d\u4f1a\u53bb\u5c79\u4f60\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u8fd8\u5728\u7b11\u4f46\u770b\u5230\u4ed6\u51b7\u51b0\u51b0\u7684\u8138\u8272\u4e0d\u7981\u6014\u4e86\u6014\u9053\u201c\u4f60\u5728\u751f\u6c14\uff0c\u6211\u53c8\u6709\u4ec0\u9ebd\u5730\u65b9\u5f97 \u7f6a\u4e86\u4f9d\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4f60\u5df1\u5fc3\u91cc\u660e\u767d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u660e\u767d\u4ec0\u9ebd\uff1f\u6211\u70b9\u4e5f\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u4e0d\u7406\u4ed6\u5ffd\u7136\u8d70\u5230\u738b\u52a8\u9762\u524d\u9053\u201c\u4f60\u867d\u7136\u4e0d\u662f\u571f\u72d7\uff0c\u4f46\u8fd9\u91cc\u5374\u6709\u6761\u8d70\u72d7\u571f\u72d7\u8fd8\u6ca1\u5173\u7cfb \u4f01\u72d7\u6211\u5374\u53d7\u4e0d\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u5927\u4e86\u773c\u775b\uff0c\u9053\u201c\u8c01\u662f\u8d70\u72d7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8fd8\u662f\u4e0d\u7406\u4ed6\xb7\u51b7\u7b11\u5f80\u5916\u8d70\u3002 \u3000\u3000\u7ff1\u5927\u8def\u773c\u73e0\u4e88\u8f6c\uff0c\u597d\u50cf\u5ffd\u7136\u660e\u767d\u4e86\u8d85\u8fc7\u53bb\u62e6\u4f4f\u4e86\u4ed6\uff0c\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u505a\u4e86\u6d3b\u5265\u76ae\u7684\u8d70\u72d7\u50cf \u4ee5\u4e3a\u8fd9\u4e9b\u4e1c\u897f\u662f\u6211\u7528\u4ed6\u7ed9\u6211\u7684\u5b9a\u91d1\u4e70\u6765\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u8fd9\u4e9b\u4e1c\u897f\u96be\u9053\u662f\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u3001\u5730\u4e0a\u957f\u51fa\u6765\u7684\u4e0d\u6210\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\xb7\u8fc7\u4eec\u826f\u4e45\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u8d2d\u95fb\u7684\u9053\u201c\u597d\xb7\u597d\u2026\u3002\u4f60\u8bf4\u6211\u662f\u8d70\u72d7\xb7\u6211\u5c31\u662f \u8d70\u72d7\u2026\u201c\u4f60\u53d7\u4e0d\u4e86\u6211\xb7\u6211\u8d70\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u8d70\u51fa\u53bb\u8d70\u8fc7\u738b\u52a8\u9762\u524d\u3002 \u3000\u3000\u738b\u52a8\u7ad9\u8d77\u6765\u8c61\u662f\u60f3\u62e6\u4f4f\u4ed6\xb7\u5374\u53c8\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u8d70\u5230\u9662\u5b50\u91cc\u62ac\u8d77\u5934\u6811\u4e0a\u7684\u79ef\u96ea\u4e00\u7247\u7247\u88ab\u98ce\u5439\u4e0b\u6765\u6d12\u5f97\u4ed6\u6ee1\u8eab\u90fd\u662f\u3002 \u3000\u3000\u4ed6\u7ad9\u4e0d\u52a8 \u3000\u3000\u96ea\u5728\u4ed6\u8138\u4e0a\u6eb6\u5316\u6cbf\u4ed6\u9762\u988a\u6d41\u4e0b\u3002\u4ed6\u7ad9\u4e0d\u52a8\u3002\u4ed6\u672c\u6765\u662f\u60f3\u8d70\u8fdc\u4e9b\u7684\xb7\u4f46\u5ffd\u7136\u95f4\u8d70\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u5f80\u9662\u5b50\u91cc\u770b\u4ed6\u4e5f\u8bb8\u4ec0\u9ebd\u90fd\u5df2\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u4ed6\u7684\u773c\u968b\u53c8\u7ea2\u4e86\u7a81\u7136\u8dfa\u4e86\u8dfa\u811a\u5f80\u53e6\u6247\u95e8\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u4e8e\u52a8\u7684\u624b\u5374\u5df2\u4f38\u8fc7\u6765\u62e6\u4f4f\u5382\u4ed6\u9053\u201c\u4f53\u5148\u770b\u770b\u8fd9\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u624b\u4e0a\u6709\u6837\u4e1c\u897f\u662f\u5f20\u82b1\u82b1\u7eff\u7eff\u7684\u7eb8\u3002 \u3000\u3000\u71d5\u4e03\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u4ec0\u9ebd\u8fd9\u6837\u7684\u7eb8\u4ed6\u8eab\u4e0a\u4e5f\u6709\u597d\u51e0\u5f20\u3002 \u3000\u3000\u201c\u8fd9\u662f\u5f53\u7968\u3002\u201d \u3000\u3000\u5361\u52a8\u9053\u201c\u4f60\u518d\u770b\u6e05\u8d85\u5428\u5f53\u7684\u662f\u4ec0\u9ebd\u201d \u3000\u3000\u5f53\u7968\u4e0a\u7684\u5b87\u5c31\u548c\u533b\u751f\u5f00\u7684\u836f\u65b9\u6837\xb7\u7b80\u76f4\u5c31\u8c61\u662f\u9b3c\u753b\u7b26\u82e5\u975e\u5f88\u6709\u7ecf\u9a8c\u7684\u4eba\u8fde\u4e2a\u5b87\u90fd\u4f11\u60f3\u8ba4 \u5f97\u51fa\u3002 \u3000\u3000\u71d5\u4e03\u5f88\u6709\u7ecf\u9a8c\xb7\u6d3b\u5265\u76ae\u7684\u5f53\u7968\u4ed6\u5df2\u770b\u8fc7\u5f88\u591a\u3002 \u3000\u3000\u201c\u7834\u65e7\u91d1\u94fe\u5b50\u6761\u7834\u65e7\u91d1\u9e21\u5fc3\u679a\u5171\u91cd\u4e03\u4e24\u4e5d\u94b1\u3002\u62bc\u7eb9\u94f6\u4e94\u5341\u4e24\u3002 \u3000\u3000\u660e\u660e\u662f\u5168\u65b0\u7684\u4e1c\u897f\u5230\u4e86\u5f53\u94fa\u91cc\xb7\u4e5f\u4f1a\u53d8\u5f97\u53c8\u7834\u53c8\u65e7\u3002 \u3000\u3000\u5929\u4e0b\u7684\u5f53\u94fa\u90fd\u662f\u8fd9\u89c4\u77ed\xb7\u5927\u5bb6\u4e5f\u89c1\u602a\u4e0d\u602a\u4f46\u91d1\u94fe\u5b50\u5c45\u7136\u4e5f\u6709\u201c\u7834\u65e7\u201d\u7684\u5c31\u672a\u514d\u6709\u70b9\u592a\u8bf4 \u4e0d\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u51e0\u4e4e\u60f3\u7b11\uff0c\u53ea\u53ef\u63aa\u5b9e\u5728\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u5c31\u597d\u50cf\u88ab\u4eba\u6253\u4e86\u8033\u5149\u6574\u4e2a\u4eba\u90fd\u6014\u4f4f\u3002 \u3000\u3000\u738b\u52a8\u6de1\u6de1\u7b11\u9053\u201c\u4e2d\u7968\u662f\u6211\u521a\u4ece\u5c0f\u90ed\u8eab\u4e0a\u6478\u51fa\u6765\u7684\u6211\u65e9\u5c31\u544a\u8bc9\u8fc7\u4f60\u4eec\u6211\u82e5\u662f\u6539\u884c\u505a\u5c0f \u5077\xb7\u73b0\u5728\u65e9\u5c31\u53d1\u8d22\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\xb7\u54fa\u54fa\u9053\u201c\u53ea\u53ef\u60dc\u6211\u5b9e\u5728\u61d2\u5f97\u52a8\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u6ca1\u6709\u52a8\u4f46\u773c\u6cea\u5374\u5df2\u6162\u6162\u7684\u4ece\u9762\u988a\u4e0a\u6d41\u5382\u4e0b\u6765\u2500\u2026\u2019 \u3000\u3000\u201c\u5c31\u7b97\u662f\u6700\u597d\u7684\u670b\u53cb\uff0c\u6709\u65f6\u4e5f\u4f1a\u53d1\u751f\u8bef\u4f1a\u7684\u3002\u201d\u6240\u4ee5\u4f60\u5047\u5982\u8ddf\u4f60\u7684\u670b\u53cb\u6709\u4e86\u8bef\u4f1a\u5b9a\u8981\u7ed9 \u4e2a\u673a\u4f1a\u8ba9\u4ed6\u89e3\u91ca\u3002 \u3000\u3000\u201c\u4e00\u4ef6\u4e8b\u5f80\u5f80\u6709\u5f88\u591a\u9762\xb7\u4f60\u82e5\u603b\u662f\u5f80\u574f\u7684\u90a3\u9762\u53bb\u60f3\u5c31\u662f\u81ea\u5df1\u5728\u8650\u5f85\u81ea\u5df1\u3002\u201d\u6240\u4ee5\u4f60\u5c31\u7b97 \u9047\u6253\u51fb\u4e5f\u8be5\u770b\u5f00\u4e9b\xb7\u60f3\u6cd5\u5b50\u53bb\u627e\u90a3\u5149\u660e\u7684\u5706 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u60f3\u8650\u5f85\u522b\u4eba\u4e5f\u4e0d\u8be5\u8650\u5f85\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u738b\u52a8\u7684\u7ed3\u8bba\u3002 \u3000\u3000\u738b\u52a8\u7684\u7ed3\u8bba\u901a\u5e38\u90fd\u5f88\u6b63\u786e\u3002 \u3000\u3000\u6b63\u786e\u7684\u7ed3\u8bba\u6bcf\u4e2a\u4eba\u6700\u597d\u8bb0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u4e16\u4e0a\u672c\u6ca1\u6709\u7edd\u5bf9\u597d\u7684\u4e8b\u4e5f\u6ca1\u6709\u7edd\u5bf9\u574f\u7684\u3002 \u3000\u3000\u5931\u8d25\u867d\u4e0d\u597d\u4f46\u201c\u5931\u8d25\u4e3a\u6210\u529f\u4e4b\u6bcd\u201d\u3002 \u3000\u3000\u6210\u529f\u867d\u597d\xb7\u4f46\u5f80\u5f80\u5374\u4f1a\u4ee4\u4eba\u53d8\u5f97\u9a84\u50b2\u3001\u81ea\u5927\u90a3\u9ebd\u5931\u8d25\u53c8\u4f1a\u613f\u6765\u4e86\uff0c \u3000\u3000\u4f60\u4ea4\u4e2a\u670b\u53cb\u5f53\u7136\u5e0c\u671b\u6444\u4ed6\u6210\u4e3a\u5f88\u4eb2\u8fd1\u7684\u670b\u53cb\u3002 \u3000\u3000\u670b\u53cb\u80fd\u4eb2\u8fd1\u5f53\u7136\u5f88\u597d\u4f46\u592a\u4eb2\u8fd1\u4e86\uff0c\u5c31\u5bb9\u6613\u4e92\u76f8\u8f7b\u89c6\u4e5f\u5f53\u7136\u53d1\u751f\u8bef\u4f1a\u3002 \u3000\u3000\u8bef\u4f1a\u867d\u4e0d\u597d\u4f46\u82e5\u80fd\u89e3\u91ca\u5f97\u6e05\u695a\u5f7c\u6b64\u95f4\u5c31\u53cd\u800c\u4f1a\u4e86\u89e3\u5f97\u66f4\u591a\uff0c\u60c5\u611f\u4e5f\u4f1a\u53d8\u5f97\u66f4\u6df1\u4e00\u5c42\u3002 \u3000\u3000\u56db \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u88ab\u4eba\u51a4\u6789\u7684\u6ecb\u6627\u603b\u662f\u4e0d\u592a\u597d\u53d7\u7684\u3002 \u3000\u3000\u5047\u5982\u8bf4\u4e16\u4e0a\u8fd8\u6709\u6bd4\u5f7c\u4eba\u51a4\u6789\u4e86\u6b21\u66f4\u96be\u53d7\u7684\u4e8b\xb7\u90a3\u5c31\u662f\u8fde\u88ab\u4eba\u51a4\u68f1\u4e86\u4e24\u6b21\u3002 \u3000\u3000\u71d5\u4e03\u4e5f\u88ab\u4eba\u51a4\u6444\u8fc7\uff0c\u4ed6\u5f88\u660e\u767d\u90ed\u5927\u8def\u6b64\u523b\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5fc3\u91cc\u6bd4\u90ed\u5927\u8def\u66f4\u96be\u53d7\u3002 \u3000\u3000\u9664\u4e86\u96be\u53d7\u5916\u8fd8\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u6627\u9664\u4e86\u4ed6\u81ea\u5df1\u5916\u8c01\u4e5f\u4e0d\u77e5\u9053\u6700\u4ec0\u9ebd\u6ecb\u5473\u60f3\u597d\u597d\u7684\u53bb\u5927\u54ed \u573a\u3002 \u3000\u3000\u4ed6\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u597d\u597d\u7684\u54ed\u8fc7\uff0c\u56e0\u4e3a\u4e00\u4e2a\u7537\u5b50\u6c49\u662f\u672f\u5e94\u8be5\u90a3\u9ebd\u54ed\u7684\u3002 \u3000\u3000\u5509\u8981\u505a\u4e2a\u7537\u5b50\u6c49\uff0c\u53ef\u5b9e\u5728\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\u73b0\u5728\u5e94\u8be5\u53bb\u627e\u7ff1\u5927\u8def\xb7\u4f46\u53bb\u4e86\u4e4b\u5f8c\u8bf4\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u6709\u4e9b\u8bdd\u4ed6\u4e2d\u613f\u8bf4\u6709\u4e9b\u8bdd\u4ed6\u4e0d\u80fd\u8bf4\xb7\u6709\u4e9b\u8bdd\u4ed6\u751a\u81f3\u4e0d\u6562\u8bf4\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u6b63\u4e71\u7cdf\u7cdf\u7684\xb7\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u662f\u597d\u5ffd\u7136\u770b\u5230\u53ea\u624b\u4f38\u51fa\u6765\u624b\u4e0a\u62ff\u676f\u9152\u3002 \u3000\u3000\u4ed6\u542c\u5230\u6709\u4eba\u5728\u5bf9\u4ed6\u8bf4\u201c\u4f60\u559d\u4e0b\u8fd9\u676f\u9152\u53bb\uff0c\u6211\u4eec\u5c31\u8bb2\u548c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4ed6\u7684\u5fc3\u8df3\xb7\u62ac\u8d77\u5934\u5c31\u770b\u5230\u4e86\u90ed\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u8138\u4e0a\u5e76\u6ca1\u6709\u751f\u6c14\u7684\u8868\u60c5\u4e5f\u6ca1\u6709\u75db\u82e6\u4e4b\u8272\u8fd8\u662f\u8c61\u5f80\u5e38\u4e00\u6837\uff0c\u7b11\u563b\u563b\u7684\u770b\u4ed6\u3002 \u3000\u3000\u8fd9\u526f\u563b\u76ae\u7b11\u8138\uff0c\u540a\u513f\u90ce\u5f53\u7684\u6837\u5b50\u71d5\u4e03\u5e73\u65f6\u672c\u6765\u6709\u70b9\u770b\u4e0d\u60ef\u3002 \u3000\u3000\u53e4\u86f0\u4f5c\u6606\u58a8 \u3000\u3000\u4ed6\u603b\u89c9\u662f\u4e2a\u4eba\u6709\u65f6\u5e94\u8be5\u6b63\u7ecf\u4e9b\u3001\u89c4\u77e9\u4e9b\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u6837\u5b50\u975e\u4f46\u70b9\u4e5f\u4e0d\u8ba8\u538c\uff0c\u800c\u4e14\u53ef\u7231\u6781\u5382\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5e0c\u671b\u90ed\u5927\u8def\u6c38\u8fdc\u90fd\u662f\u8fd9\u6837\u5b50\uff0c\u6c38\u8fdc\u4e0d\u8981\u677f\u8d77\u8138\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u624d\u662f\u4ed6\u771f\u6b63\u559c\u6b22\u7684\u90ed\u5927\u8def\xb7\u6c38\u8fdc\u65e0\u8651\u5f00\u5f00\u5fc3\u5fc3\u7684\xb7\u522b\u4eba\u5c31\u7b97\u5f97\u7f6a\u4e86 \u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u80af\u4e0d\u80af\u8bb2\u548c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u4f4e\u4e0b\u5934\xb7\u9053\uff1a\u201c\u4f24\u201d\u4e00\u201c\u4f60\u4e0d\u751f\u6c14\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u672c\u6765\u662f\u5f88\u751f\u6c14\u7684\xb7\u4f46\u5f8c\u6765\u60f3\u4e86\u60f3\u70b9\u3009\u4f46\u4e0d\u751f\u6c14\u53cd\u800c\u5f00\u5fc3\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5f00\u5fc3\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u82e5\u4e0d\u6280\u5fc3\u6211\uff0c\u6211\u5c31\u7b97\u505a\u4e86\u4e4c\u9f9f\u5343\u516b\u86cb\u548c\u4f60\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\u4f60\u4e5f\u7528\u4e0d\u751f\u6c14\u7684\u3002 \u5c31\u56e0\u4e3a\u4f60\u662f\u6211\u7684\u597d\u670b\u53cb\xb7\u6240\u4ee5\u624d\u4f1a\u5bf9\u6211\u53d1\u813e\u6c14\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ef\u662f\u201c\u2026\u6211\u672c\u4e0d\u8be5\u51a4\u6789\u4f60\u7684\u6211\u672c\u6765\u5e94\u8be5\u4fe1\u5f97\u8fc7\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u51a4\u6789\u6211\u4e5f\u6ca1\u5173\u7cfb\uff0c\u63cd\u6211\u4e24\u62f3\u4e5f\u6ca1\u5173\u7cfb\u53ea\u8981\u662f\u6211\u7684\u597d\u670b\u53cb\u3001\u968f\u4fbf\u65bc\u4ec0\u9ebd\u90fd \u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u7684\u65f6\u5019\xb7\u9f3b\u5b50\u5148\u8f7b\u8f7b\u76b1\u4e86\u8d77\u6765\u773c\u775b\u91cc\u5148\u6709\u4e86\u7b11\u610f\u3002 \u3000\u3000\u4ed6\u8106\u4e0a\u8fd8\u5e26\u6cea\u75d5\xb7\u672c\u6765\u53c8\u9ed1\u53c8\u810f\u7684\u5f20\u80dc\uff0c\u773c\u6cea\u6d41\u8fc7\u7684\u5730\u65b9\u5c31\u51fa\u73b0\u4e86\u51e0\u6761\u96ea\u767d\u7684\u6cea\u75d5\uff0c\u5c31 \u8c61\u662f\u6ee1\u5929\u4e4c\u4e91\u4e2d\u7684\u9633\u5149\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\xb7\u4eff\u4f5b\u770b\u5446\u4e86\u3002 \u3000\u3000\u75c8\u4e03\u53c8\u5782\u8428\u5934\u9053\u201c\u4f60\u6b7b\u6211\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u7b11\xb7\u53c8\u5480\u4e86\u53e3\u6c14\u9053\u201c\u6211\u5728\u60f3\xb7\u9178\u6885\u6ca5\u7684\u773c\u5149\u771f\u4e0d\u9519\u4f60\u82e5\u80af\u6d17\u6d17\u8138\u5b9a\u662f\u4e2a\u5f88\u6f02 \u4eae\u7684\u5c0f\u4f19\u5b50\u4e5f\u8bb8\u6bd4\u6211\u8fd8\u539f\u4eae\u5f97 \u3000\u3000\u71d5\u4e03\u60f3\u677f\u8d77\u8138\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u201c\u553e\u9648\u201d\u7b11\u63a5\u8fc7\u4e86\u9152\u676f \u3000\u3000\u738b\u52a8\u770b\u6797\u592a\u5e73\u6797\u592a\u5e73\u770b\u738b\u52a8\u4e24\u4e2a\u4eba\u4e5f\u5168\u90fd\u7b11\u3002 \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u6211\u65e9\u571f\u672c\u6765\u4e0d\u559c\u6b22\u559d\u9152\uff0c\u606b\u4eca\u5929\u5374\u771f\u60f3\u559d\u4e2a\u5927\u9189\u3002\u201d \u3000\u3000\u4eba\u751f\u96be\u5f97\u51e0\u56de\u9189\u3002 \u3000\u3000\u9047\u8fd9\u79cd\u4e8b\uff0c\u82e5\u8fd8\u4e0d\u9189\u8981\u7b49\u5230\u4ec0\u9ebd\u65f6\u5019\u624d\u9189\uff1f \u3000\u3000\u90ed\u5927\u8def\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u53ea\u53ef\u63aa\u4eca\u5929\u6211\u4e0d\u80fd\u9677\u4f60\u9189\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\uff0c\u4eca\u5929\u6211\u8fd8\u6709\u4e8b\xb7\u8fd8\u5f97\u4e0b\u5c71\u53bb\u4e00\u8d85\u3002\u201d \u3000\u3000\u8fd9\u5c0f\u65bc\u8eab\u4e0a\u6709\u4e86\u94b1\u5c31\u5728\u8fd9\u6709\u91cc\u803d\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u9053\u201c\u4e0b\u5c71\u53bb\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u9650\uff0c\u9053\u201c\u6211\u8ddf\u4e2a\u4eba\u6709\u7ea6\u4f1a\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u8138\u8272\u597d\u50cf\u53d8\u4e86\u53d8\u6084\u6084\u522b\u8fc7\u8111\u9053\u201c\u8ddf\u8c01\u6709\u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6d3b\u5265\u76ae\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u773c\u775b\u7acb\u523b\u53c8\u4eae\u4e86\uff0c\u5374\u6545\u610f\u677f\u8138\u9053\u201c\u4f60\u613f\u4ed6\u7ea6\u597d\u4e86\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4ed6\u6ca1\u6709\u7ea6\u6211\u6211\u5374\u8981\u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u627e\u4ed6\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u80af\u51fa\u4e94\u767e\u4e24\u94f6\u5b50\uff0c\u2500\u5b9a\u6ca1\u6709\u4ec0\u9ebd\u597d\u4e3b\u610f\u6240\u4ee5\u6211\u8981\u53bb\u770b\u770b\u4ed6\u7a76\u7adf\u60f3\u8981\u5265\u96c4\u7684 \u76ae\uff1f\u201d \u3000\u3000\u4e94 \u3000\u3000\u96ea\u5f00\u59cb\u6eb6\u5316\xb7\u79ef\u96f7\u7684\u5c71\u8def\u4e0a\u6ee1\u662f\u6ce5\u7597\u3002 \u3000\u3000\u4f46\u71d5\u4e03\u70b9\u4e5f\u4e0d\u5728\u4e4e\u4ed6\u7684\u811a\u63a2\u5728\u6ce5\u6cde\u4e2d\u5c31\u597d\u50cf\u8e29\u5728\u4e91\u7aef\u4e0a\u3002 \u3000\u3000\u56e0\u4e3a\u90ed\u5927\u8def\u5c31\u8d70\u5728\u4ed6\u8eab\u65c1\xb7\u4ed6\u751a\u81f3\u53ef\u4ee5\u611f\u89c9\u5230\u7ff1\u5927\u8def\u7684\u547c\u5438\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4eca\u5929\u6211\u53c8\u53d1\u73b0\u4e86\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54e6\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53d1\u73b0\u4e0a\u8001\u5927\u5b9e\u5728\u5382\u89e3\u6211\u5929\u4e0b\u6015\u518d\u4e5f\u627e\u4e0d\u51fa\u7b2c\u4e0b\u4e2a\u4eba\u80fd\u8fd9\u9ebd\u4e86\u89e3\u6211\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u5e7d\u5e7d\u9053\u201c\u4ed6\u7684\u786e\u6700\u80fd\u4e86\u89e3\u522b\u4eba\u4e2d\u4f46\u662f\u4f60\u6240\u6709\u7684\u4eba\u4ed6\u90fd\u4e86\u89e3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u6700\u540c\u60c5\u6211\u7684\u4eba\u5374\u662f\u6797\u592a\u5e73\u6211\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u8fdf\u7591\u7ec8\u5341\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u6211\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u65e2\u4e0d\u4e86\u6211\u4e5f\u4e0d\u540c\u60c5\u6211\u4f60\u4e0d\u4f46\u5bf9\u6211\u6700\u51f6\u800c\u6c11\u597d\u50cf\u968f\u65f6\u968f\u5730\u90fd\u5728\u8ddf\u6211\u6597\u5634\u6597 \u6c14\u3002\u2026\u201c \u3000\u3000\u71d5\u4e03\u5782\u535c\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u53c8\u7b11\u4e86\u7b11\u63a5\u9053\u201c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\u6211\u8fd8\u662f\u89c9\u5f97\u5bf9\u6211\u6700\u597d\u7684\u4e5f\u662f\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u5a7f\u7136\u7b11\u8138\u5df2\u4eff\u4f5b\u6709\u70b9\u53d1\u7ea2\u4e49\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u9053\uff0c\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u65f6\u6211\u5bf9\u4f60\u7b80\u76f4\u6c14\u5f97\u8981\u547d\u8b6c\u5982\u8bf4\u4eca\u5929\u738b\u8001\u5927\u82e5\u90a3\u6837\u5bf9\u6211\xb7\u6211\u4e5f\u8bb8\u53cd\u4e3d\u672c\u4f1a\u90a3 \u9ebd\u6837\u751f\u6c14\uff0c\u4e5f\u8bb8\u7acb\u523b\u5c31\u4f1a\u5bf9\u4ed6\u89e3\u91ca\uff0c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u53ea\u5bf9\u6211\u751f\u6c14\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u90a3\u4e5f\u53ea\u56fd\u4e3a\u6211\u5bf9\u4f60\u7279\u522b\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6c89\u541f\uff0c\u9053\u201c\u7a76\u7adf\u6709\u591a\u597d\u8fde\u6211\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8bf4\u4e0d\u51fa\u6765\u5c31\u662f\u5047\u7684\u3002\u6c14\u90ed\u5927\u8def\u9053\u201c\u4f46\u6211\u5374\u53ef\u80fd\u6253\u4e2a\u6bd4\u665a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u6bd4\u55bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u5382\u738b\u8001\u5927\u6211\u4f1a\u5c06\u6240\u6709\u7684\u519c\u670d\u90fd\u5f53\u5149\u53ea\u7a7f\u6761\u5e95\u88e4\u7f51\u6765\u201c \u3000\u3000\u4ed6\u7b11\u7b11\u63a5\u9053\u201c\u4f46\u4e3a\u5382\u4f60\xb7\u6211\u53ee\u4ee5\u5c06\u8fd9\u6761\u5e95\u88e4\u90fd\u62ff\u4f01\u5f53\u5382\u3002 \u3000\u3000\u71d5\u4e03\u8d61\u7136\u7b11\u9053\u201c\u8c01\u8981\u4f60\u90a3\u6761\u7834\u5e95\u88e4\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u5382\u8fd9\u53e5\u8bdd\u4ed6\u7684\u8138\u7236\u7ea2\u5382\u7ff1\u5927\u8def\u7684\u5e95\u88e4\u7834\u4e0d\u7834\uff0c\u4ed6\u600e\u9ebd\u94e1\u9053\uff1f \u3000\u3000\u5e78\u597d\u4ed6\u7684\u8138\u53c8\u810f\u4e49\u9ed1\u5c31\u7b97\u8138\u7ea2\u65f6\u4e5f\u770b\u5c0f\u51fa\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u90a3\u79cd\u8868\u60c5\u90a3\u79cd\u6f2b\u67d4\u751c\u7f8e\u7684\u7b11\u610f\u5e26\u4e9b\u7f9e\u6f84\u53d1\u5a07\u7684\u7b11\u610f\u82e5\u6709\u4eba\u8fd8\u770b\u4e0d\u51fa\u90a3\u4eba\u4e0d\u4f46\u662f\u5446 \u5b50\u7b80\u76f4\u5c31\u662f\u4e2a\u778e\u4e86\u773c\u7684\u7684\u679c \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\u7684\u773c\u775b\u5ffd\u53c8\u7b11\u9053\u201c\u6211\u8fd8\u6709\u4e2a\u6bd4\u55bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u867d\u5df2\u53d1\u8a93\u4e0d\u6210\u4eb2\u4eba\u53e3\u4f60\u82e5\u662f\u4ea5\u7684\u6211\u5b9a\u8981\u53d6\u4f60\u505a\u8001\u5a46\u201c \u3000\u3000\u70ed\u4e03\u9053\u201c\u51c6\u505a\u4f60\u7684\u8001\u5a46\u90a3\u624d\u662f\u5012\u5382\u516b\u8f88\u5b50\u7a77\u9709\u4e86\u3002\u201d \u3000\u3000\u4ed6\u58f0\u755c\u597d\u50cf\u5df2\u6709\u70b9\u4e0d\u5927\u5bf9\u5ffd\u7136\u52a0\u5feb\u811a\u6b65\u8d70\u5230\u524d\u9762\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5e76\u6ca1\u6709\u8ffd\u4e0a\u53bb\u53ea\u662f\u770b\u4ed6\u7684\u80cc\u5f71\u4eff\u4f5b\u5df2\u770b\u5f97\u51fa\u795e\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8272\u5ffd\u7136\u5f00\u6717\xb7\u7ebf\u91d1\u9ec4\u8272\u7684\u9633\u5149\u7834\u4e91\u76f4\u7167\u4e86\u4e0b\u6765\u7167\u5927\u5730\u7167\u71d5\u4e03\u4e5f\u7167\u90ed\u5927\u8def\u3002 \u3000\u3000\u8fd9\u9633\u5149\u5c31\u8c61\u662f\u7279\u5730\u4e3a\u4ed6\u8ba2\u7167\u5c04\u7684\u3002 \u3000\u3000\u5265\u8c01\u7684\u76ae\uff1f \u3000\u3000\u6d3b\u5265\u76ae\u7684\u5f53\u9505\u53eb\u201c\u5229\u6e90\u5f53\u94fa\u201d\u3002 \u3000\u3000\u5229\u6e90\u5f53\u94fa\u5c31\u591c\u9ea6\u8001\u5e7f\u70e7\u814a\u5e97\u5bf9\u56de\u3002 \u3000\u3000\u73b0\u5728\u9ea6\u8001\u5e7f\u7684\u62cd\u724c\u5df3\u5378\u4e86\u535c\u6765\uff0c\u6709\u51e0\u4e2a\u4eba\u6b63\u5408\u7c89\u5237\u5e97\u9762\u3002 \u3000\u3000\u6028\u5230\u9ea6\u8001\u5e7f\u90ed\u5927\u8def\u548c\u71d5\u4e03\u5fc3\u91cc\u4e0d\u7981\u6709\u5f88\u591a\u611f\u6168\u3002 \u3000\u3000\u4ed6\u4eec\u6bd5\u7adf\u5728\u8fd9\u91cc\u6709\u8bb8\u591a\u5feb\u4e50\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u4e0d\u662f\u591a\u6101\u5584\u611f\u7684\u4eba\u5374\u5e38\u5e38\u5bb9\u6613\u88ab\u5f88\u591a\u4e8b\u6240\u611f\u52a8\u3002 \u3000\u3000\u5229\u6e90\u5f53\u94fa\u95e8\u95e8\u505c\u8f86\u9a6c\u8f66\u3002 \u3000\u3000\u5f53\u94fa\u7684\u95e8\u8fd8\u6ca1\u6709\u5f00\uff0c\u4eca\u5929\u597d\u50cf\u4e2d\u51c6\u5907\u505a\u751f\u610f\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u6b7b\u4ea1\u4ea4\u6362\u5382\u4e2a\u773c\u8272\xb7\u521a\u8d70\u8fc7\u65c1\u8fb9\u7684\u5c0f\u5df7\u91cc\u5c31\u770b\u5230\u6d3b\u5265\u76ae\u7f29\u8111\u888b\u4ece\u5c0f\u300f\u91cc\u5b9a\u51fa\u6765 \u773c\u775b\u9b3c\u9b3c\u795f\u795f\u7684\u56db\u4e0b\u6c40\u91cf\u6000\u91cc\u7d27\u7d27\u62b1\u4e2a\u5305\u88b1\u3002\u770b\u5230\u56db\u4e0b\u6ca1\u4eba\u5c31\u7acb\u523b\u8df3\u4e0a\u4e86\u9a6c\u8f66\u3002 \u3000\u3000\u9a6c\u8f66\u7684\u95e8\u7acb\u523b\u5173\u7d27\u8fde\u8f66\u7a97\u7684\u5b50\u90dd\u653e\u4e86\u5e7f\u6765\u3002 \u3000\u3000\u5f53\u94fa\u91cc\u4e49\u6162\u541e\u541e\u7684\u8d70\u51fa\u4e86\u4e2a\u8001\u592a\u5a46\xb7\u5343\u91cc\u63d0\u6876\u5783\u6781\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u8ba4\u5f97\u8fd9\u8001\u592a\u5a46\u5979\u5e76\u4e0d\u662f\u6d3b\u5265\u76ae\u7684\u8001\u5a46\u53ea\u5c0f\u8fc7\u662f\u66ff\u4ed6\u70e7\u996d\u6253\u6742\u7684\u51fa\u4e3a\u5e74\u7eaa\u592a\u540d \u6240\u4ee5\u9664\u4e86\u5403\u996d\u5916\u6d3b\u5267\u76ae\u8fde\u6587\u5de5\u94b1\u90fd\u5c0f\u7ed9\u5979\u4ecd\u8981\u5979\u505a\u4e8b\u7684\u65f6\u5019\u5374\u53c8\u62ff\u5979\u5f53\u4e2a\u5c0f\u5feb\u5382\u3002 \u3000\u3000\u90ed\u5927\u8def\u5e38\u5e38\u89c9\u5f97\u5947\u602a\u8fd9\u8001\u592a\u5a46\u600e\u9ebd\u65e8\u66ff\u6d3b\u5265\u76ae\u505a\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u66ff\u6d3b\u5265\u76ae\u8fd9\u79cd\u4eba\u505a\u4e8b\u82e5\u662f\u4e07\u6709\u4e2a\u4e4b\u957f\u4e24\u77ed\u4e5f\u8bb8\u8fde\u53e3\u68fa\u6750\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ea\u542c\u6d3b\u5265\u76ae\u5728\u8f66\u91cc\u5927\u58f0\u901a\u628a\u95e8\u5173\u4e0a\u5343\u4e07\u4e0d\u8981\u653e\u4efb\u4f55\u4eba\u8fdb\u53bb\u6211\u660e\u5929\u65e9\u4e0a\u624d\u56de\u6765\u3002\u201d \u3000\u3000\u65bc\u662f\u8d76\u8f66\u7684\u626c\u97ad\u5b50\xb7\u9a6c\u8f66\u5c31\u8d70\u5954\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u7a81\u7136\u4ece\u5f04\u5802\u91cc\u51b2\u51fa\u6765\u4e00\u8fb9\u4e2a\u8df3\u4e0a\u4e86\u8f66\u8f95\u3002 \u3000\u3000\u7a97\u4e8e\u5e02\u5230\u5f00\u4e86\u6d3b\u5265\u76ae\u63a2\u51fa\u5382\u5934\u663e\u5f97\u5f88\u5403\u60ca\u7684\u6837\u4e8e\u7b49\u770b\u5230\u4ed6 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9041\u201c\u6ca1\u4ec0\u9ebd\uff0c\u53ea\u4e0d\u8fc7\u60f3\u642d\u4f60\u7684\u4fbf\u8f66\u5230\u57ce\u91cc\u53bb\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u7acb\u523b\u62ec\u5934\xb7\u9053\u201c\u4e0d\u884c\u6211\u8fd9\u8f86\u8f66\u8bf4\u597d\u4e86\u4e0d\u642d\u4eba\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u563b\u563b\u9053\u201c\u4e0d\u884c\u4e5f\u5f97\u884c\u6211\u4eec\u65e2\u7136\u5df2\u4e0a\u4e86\u8f66\u4f60\u96be\u9053\u8fd8\u80fd\u628a\u6211\u4eec\u63a8\u535c\u53bb\uff1f\u2019\u71d5\u4e03\u4e5f\u7b11 \u9053\u201c\u53cd\u6bd4\u4f60\u672c\u6765\u5c31\u60f3\u8bf7\u6211\u4eec\u8d54\u4f60\u53bb\u8d70\u6bef\u7684\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6211\u627e\u7684\u4e0d\u662f\u4f60\u4eec\u2026\u3002\u2606 \u3000\u3000\u4ed6\u597d\u50cf\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\u7acb\u523b\u95ed\u535c\u4e86\u5634\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u662f\u6211\u4eec\uff1f\u4f60\u96be\u9053\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u8138\u8272\u5df2\u6709\u70b9\u53d1\u767d\u5ffd\u53c8\u7b11\u9053\u201c\u4f60\u4eec\u8981\u642d\u4e2d\u4e5f\u884c\u53ea\u4e0d\u8fc7\u8981\u51fa\u8f66\u94b1\u3002\u8f66\u94b1\u5171\u662f\u53c1\u94b1\u94f6\u5b50 \u521a\u597d\u4e00\u4eba\u51fa\u4e00\u94b1\u3002\u201d \u3000\u3000\u4ed6\u5de6\u624b\u62ff\u5230\u94f6\u5b50\u53f3\u624b\u7acb\u523b\u5f00\u4e86\u8f66\u95e8\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u8fd9\u6837\u7684\u4eba\u4e5f\u6709\u79cd\u597d\u5904\u4f60\u53ea\u8981\u6709\u94b1\u7ed9\u4ed6\u4ed6\u603b\u80fd\u8ba9\u4f60\u89c9\u5f97\u6bcf\u5206\u94b1\u90fd\u82b1\u5f97\u4e0d\u51a4\u6746\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5c06\u6bd4\u8f83\u597d\u7684\u4e24\u4e2a\u4f4d\u5b50\u8ba9\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u65e2\u5df2\u4e0a\u4e86\u8f66\xb7\u5c31\u5f00\u59cb\u6253\u53e6\u5916\u7684\u4e3b\u610f\u4e86\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u624b\u91cc\u8fd8\u662f\u7d27\u7d27\u6402\u90a3\u5305\u88b1\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u9053\u201c\u71d5\u4e03\xb7\u6211\u4eec\u6253\u4e2a\u8d4c\u597d\u4e0d\u597d\uff1f\u201d\u71d5\u4e03\u9053\u201c\u597d\u8d4c\u4ec0\u9ebd\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8d4c\u4ed6\u8fd9\u5305\u88b1\u91cc\u9762\u6709\u4e2a\u8001\u9f20\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u6211\u8e66\u4f60\u8d4c\u5341\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u5ffd\u53c8\u7b11\u4e86\u9053\u201c\u4f60\u7fc1\u4e0d\u5fc5\u8d4c\u4e86\u6211\u77e5\u9053\u4f60\u4eec\u53ea\u4e0d\u8fc7\u60f3\u770b\u770b\u6211\u8fd9\u5305\u88b1\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u50cf\u662f\u54cd\u70b9\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u8981\u770b\u4e5f\u884c\u770b\u770b\u4e2d\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5012\u771f\u5c48\u672f\u5230\u4ed6\u7b54\u5e94\u5f97\u8fd9\u9ebd\u5bb9\u6613\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u8fd9\u5305\u62a5\u91cc\u5b9a\u6709\u4ec0\u9ebd\u89c1\u4e0d\u5f97\u4eba\u7684\u4e1c\u897f\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u5de6\u624b\u62ff\u5230\u94f6\u5b50\u53f3\u624b\u7acb\u523b\u5c31\u89e3\u5f00\u4e86\u5305\u88b1\u3002 \u3000\u3000\u5305\u88b1\u91cc\u53ea\u4e0d\u8fc7\u662f\u51e0\u4ef6\u65e7\u8863\u670d\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u9893\u4e03\uff0c\u71d5\u4e03\u770b\u770b\u90ed\u5927\u8def\uff0c\u4e24\u4e2a\u4eba\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u7b11\u9053\u201c\u4f60\u4eec\u73b0\u5728\u5df4\u89c9\u5f97\u8fd9\u5341\u4e24\u94f6\u5b50\u82b1\u5f97\u592a\u51a4\u4e86\u662d\uff1f\u53ea\u53ef\u60dc\u73b0\u5728\u5df2\u6536\u4e0d\u56de\u53bb\u4e86\u201c \u3000\u3000\u4ed6\u8138\u4e0a\u5e26\u5f97\u610f\u6c88\u7b11\u5bb9\u6b63\u60f3\u5c06\u5305\u88b1\u624e\u4e0a\u3002 \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u8fd9\u5305\u88b1\u91cc\u6709\u4ef6\u8863\u670d\u597d\u50cf\u662f\u6797\u592a\u5e73\u7684\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u4e8e\u54b3\u4e86\u4e24\u58f0\u9053\u201c\u597d\u50cf\u662f\u5427\u4ed6\u53cd\u6b63\u5df2\u5f53\u7ed9\u4e86\u6211\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5f53\u7968\u8fd8\u6ca1\u6709\u8fc7\u671f\xb7\u4ed6\u968f\u65f6\u90fd\u53ef\u4ee5\u53bb\u8d4e\u56de\u6765\uff0c\u4f60\u600e\u9ebd\u80fd\u5e26\u8d70\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u6e10\u6e10\u5df2\u6709\u70b9\u7b11\u4e0d\u51fa\u4e86\u9053\u201c\u4ed6\u8981\u8d4e\u7684\u65f6\u5019\xb7\u6211\u81ea\u7136\u6709\u8863\u670d\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u4ef6\u8863\u670d\u4ed6\u5f53\u5382\u591a\u5c11\u94f6\u5b50\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u4e24\u4e94\u94b1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u6211\u73b0\u5728\u5c31\u66ff\u4ed6\u8d4e\u51fa\u6765\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u94b1\u4e5f\u4e0d\u884c\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u6709\u94b1\u8fd8\u5f97\u6709\u5f53\u7968\xb7\u8fd9\u662f\u5f00\u5f53\u94fa\u7684\u89c4\u77e9\u4f60\u6709\u6ca1\u6709\u5e26\u5f53\u7968\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u770b\u770b\u71d5\u4e03\u8d2d\u4eba\u90fd\u4e0d\u8bf4\u8bdd\u6269\u4f46\u5fc3\u91cc\u5374\u66f4\u5947\u602a\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u5c06\u6797\u592a\u5e73\u7684\u8863\u670d\u5e26\u5230\u57ce\u91cc\u53bb\u5341\u4ec0\u9ebd\uff1f \u3000\u3000\u8fd9\u4ef6\u8863\u670d\u8d28\u6599\u867d\u4e0d\u9519\uff0c\u5374\u5df2\u72e0\u65e7\u5382\uff0c\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u7d27\u7d27\u7684\u62b1\u5c31\u597d\u50cf\u5c06\u5b83\u5f53\u5b9d\u8d1d\u4f3c\u52a9\u3002 \u3000\u3000\u9a6c\u4e2d\u8fdb\u57ce\u6d3b\u5265\u76ae\u5c31\u9053\u201c\u5730\u5934\u5df2\u5230\u5382\u4f60\u4eec\u4e0b\u8f66\u5427\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e0d\u8981\u6211\u4eec\u966a\u4f60\u901b\u901b\u5417\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u73b0\u5728\u5df2\u7528\u4e0d\u4e01\u4eb2\u751f\u5b50\u4e2d\u5982\u624b\u8fb9\u94b1\u80fd\u7701\u4e2a\u603b\u662f\u7701\u4e2a\u7684\u597d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4eec\u5047\u5982\u80af\u514d\u8d39\u5462\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u7b11\u9053\u201c\u514d\u8d39\u66f4\u4e2a\u884c\u4e86\u53ea\u6709\u73b0\u91d1\u4ea4\u6613\u7684\u751f\u610f\xb7\u624d\u662f\u9760\u5f97\u4f4f\u7684\u751f\u610f\uff0c\u514d\u8d39\u7684\u4e8b\u603b\u662f\u6709 \u70b9\u9ebb\u70e6\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u5480\u4e86\u53e3\u6c14\xb7\u9053\u201c\u90a3\u9ebd\u6211\u97f6\u4e0b\u8f66\u5427\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u9053\u201c\u4e0d\u9001\u4e0d\u9001\u3002\u201d \u3000\u3000\u71d5\u4e03\u4ed6\u4eec\u521a\u4e0b\u8f66\u4ed6\u5c31\u707e\u523b\u201c\u7830\u201d\u7684\u5173\u4e0a\u8f66\u95e8\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u9a6c\u8f66\u5f80\u524d\u8d70\u4e5f\u53f9\u5382\u53e3\u6c14\u9053\u8fd9\u4eba\u771f\u662f\u8001\u5978\u5de8\u6ed1\uff0c\u6211\u5b9e\u5728\u770b\u4e0d\u51fa\u4ed6\u5728\u6253\u4e9b\u4ec0\u9ebd\u9b3c\u4e3b \u610f\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u541f\u9053\u201c\u4ed6\u521a\u8bf4\u6f0f\u4e86\u5634\u8bf4\u8981\u627e\u7684\u4e0d\u662f\u6211\u8ba2\u4f60\u542c\u89c1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u96be\u9053\u4ed6\u8981\u627e\u7684\u53ea\u662f\u6797\u592a\u5e73\u4e2a\u4eba\xb7\u6211\u4eec\u90fd\u53ea\u4e0d\u8fc7\u662f\u8d54 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u627e\u6797\u592a\u5e73\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u603b\u89c9\u5f97\u6797\u592a\u5e73\u8fd9\u4eba\u597d\u50cf\u4e5f\u6709\u79d8\u5bc6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6c89\u541f\u4e86\u534a\u7897\u5ffd\u7136\u9053\u201c\u4f60\u770b\u4ed6\u4f1a\u4e0d\u4f1a\u5973\u626e\u7537\u88c5\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u773c\u9053\u201c\u6211\u770b\u4f60\u8fd9\u4eba\u6015\u662f\u542c\u8bf4\u4e66\u542c\u5f97\u592a\u591a\u4e86\uff0c\u5929\u4e0b\u90a3\u6709\u8fd9\u9ebd\u65b0\u9c9c\u7684\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u76f4\u5230\u9a6c\u8f66\u8f6c\u8fc7\u8857\xb7\u4e24\u4eba\u7a81\u7136\u540c\u65f6\u52a0\u5feb\u811a\u6b65\u8ffd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u5230\u5e95\u8fd8\u662f\u4e0d\u80af\u6b7b\u5fc3\u3002 \u3000\u3000\u9a6c\u8f66\u5728\u5bb6\u5f88\u5927\u7684\u5ba2\u6808\u95e8\u53e3\u505c\u535c\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u8fd9\u79cd\u4eba\u5c45\u7136\u820d\u5f97\u4f4f\u8fd9\u79cd\u5ba2\u6808\u5c82\u975e\u53c8\u662f\u4ef6\u602a\u4e8b\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u65f6\u5929\u8272\u5df2\u6697\u4e86\u4e0b\u6765\u3002\u51ac\u5929\u7684\u665a\u535c\u603b\u662f\u6765\u5f97\u7279\u522b\u65e9\u3002 \u3000\u3000\u4ed6\u4eec\u7ed5\u5230\u8fd9\u5bb6\u5ba2\u6808\u5f8c\u9762\uff0c\u7ffb\u5899\u6838\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u4e0d\u4f1a\u6c38\u8fdc\u5012\u9709\u7684\u8fd9\u6b21\u7684\u8fd0\u6c14\u5c31\u7279\u522b\u597d\uff0c\u521a\u843d\u5728\u6811\u68a2\u5c31\u770b\u5230\u6d3b\u5265\u76ae\u8d70\u4eba\u5f8c\u9762\u8de8\u9662 \u91cc\u7684\u6392\u53a2\u623f\u91cc\u3002 \u3000\u3000\u8fd8\u662f\u51b7\u5f97\u5f88\u9662\u4e88\u91cc\u770b\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u4ecd\u4ece\u6811\u68a2\u63a0\u8fc7\u53bb\xb7\u53ea\u53c1\u4e94\u4e2a\u8d77\u843d\uff0c\u5c31\u5df2\u63a0\u4e0a\u4e86\u90a3\u6392\u53a2\u623f\u7684\u5c4b\u76bf\u3002 \u3000\u3000\u4e24\u4eba\u5ffd\u7136\u90fd\u53d1\u89c9\u5bf9\u65b9\u7684\u8f7b\u529f\u90fd\u4e0d\u9519\xb7\u5c31\u597d\u50cf\u5929\u751f\u662f\u505a\u8fd9\u79cd\u4e8b\u7684\u6750\u6599\u3002 \u3000\u3000\u4e24\u4eba\u5fc3\u91cc\u90fd\u6253\u5b9a\u4e3b\u610f\u4ee5\u5f8c\u5b9a\u8981\u60f3\u6cd5\u5b50\u95ee\u95ee\u5bf9\u65b9\uff0c\u8fd9\u4efd\u8f7b\u529f\u662f\u600e\u9ebd\u7ec3\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4fe9\u597d\u50cf\u90fd\u5ffd\u7136\u53d8\u5f97\u5f88\u60f3\u77e5\u9053\u5bf9\u65b9\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5c4b\u6410\u4e0a\u4e5f\u7ed3\u51b0\u6280\uff0c\u7a97\u4e8e\u81ea\u7136\u5173\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u5e78\u597d\u5c4b\u4e88\u91cc\u751f\u706b\xb7\u6240\u4ee5\u5c31\u5f97\u5c06\u4e0a\u9762\u7684\u5c0f\u7a97\u4e88\u6253\u5f00\u900f\u900f\u6c14\u3002 \u3000\u3000\u4ece\u8fd9\u5c0f\u7a97\u5b50\u91cc\u671b\u8fdb\u53bb\u6b63\u597d\u5c06\u5c4b\u4e8e\u91cc\u7684\u60c5\u51b5\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u9664\u4e86\u6d3b\u5265\u76ae\u5916\uff0c\u53e6\u5916\u8fd8\u6709\u4e24\u4e2a\u7a7f\u5f88\u534e\u4e3d\u3001\u6e90\u5934\u5f88\u5927\u7684\u4eba\xb7\u8138\u8272\u9634\u9634\u6c89\u6c89\u7684\uff0c\u5c31\u597d \u50cf\u5168\u4e16\u754c\u7684\u5165\u90fd\u6b20\u4e86\u4ed6\u76ef\u7684\u94b1\u6ca1\u8fd8\u3002 \u3000\u3000\u71d5\u4e03\u773c\u5c31\u770b\u51fa\u8fd9\u4e24\u4eba\u975e\u4f46\u6b66\u529f\u4e0d\u5f31\uff0c\u800c\u4e14\u4e00\u5b9a\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u5176\u4e2d\u6709\u4e2a\u4eba\u8138\u4e0a\u8fd8\u5e26\u6761\u957f\u957f \u7684\u5200\u62d6\uff0c\u4f7f\u5f97\u4ed6\u770b\u6765\u66f4\u53ef\u4f2f\u3002 \u3000\u3000\u53e6\u4e2a\u4eba\u8138\u4e0a\u867d\u6ca1\u6709\u5200\u75a4\xb7\u4f46\u624b\u81c2\u5374\u65ad\u4e86\u4e00\u6761\u53ea\u7a7a\u7a7a\u7684\u8896\u5b50\u624e\u5728\u8170\u5e26\u4e0a\u8170\u5e26\u8fd8\u659c\u63d2\u4e00\u67c4\u5f2f \u5200\u3002 \u3000\u3000\u8fd9\u6837\u5b50\u7684\u5f2f\u5200\u6c5f\u6e56\u4e2d\u5e76\u4e0d\u591a\u89c1\xb7\u53ea\u4e58\u4e0b\u6761\u624b\u81c2\u7684\u4eba\u8fd8\u80fd\u7528\u8fd9\u79cd\u5f2f\u5200\u624b\u5e95\u4e0b\u663e\u7136\u72e0\u6709\u4e24\u4e0b \u4e88\u2606 \u3000\u3000\u800c\u4e14\uff0c\u82e5\u4e0d\u662f\u7ecf\u5e38\u51fa\u751f\u4eba\u6b7b\u7684\u4eba\u8eab\u4e0a\u4e5f\u4e0d\u4f1a\u5e26\u8fd9\u9ebd\u91cd\u7684\u4f24\u3002 \u3000\u3000\u7ecf\u5e38\u51fa\u751f\u4eba\u6b7b\u7684\u4eba\u8fd8\u80fd\u6d3b\u5230\u73b0\u5728\xb7\u6d3e\u5934\u8fd8\u80fd\u8fd9\u9ebd\u5927\u5c31\u4e00\u5b9a\u4e0d\u662f\u597d\u60f9\u7684\u90ed\u5927\u8def\u60f3\u4e0d\u901a\u6d3b\u5265 \u76ae\u600e\u4f1a\u548c\u8fd9\u79cd\u4eba\u6709\u4ea4\u6613\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u5df2\u5c06\u5305\u88b1\u89e3\u5f00\u5c06\u6797\u592a\u5e73\u90a3\u4ef6\u8863\u670d\u59da\u4e86\u51fa\u6765\u9001\u5230\u8fd9\u4e24\u4eba\u9762\u524d\u7684\u684c\u4e8e\u4e0a\u8138\u4e0a\u5e26\u5f97\u610f\u7684 \u8868\u60c5\uff0c\u5c31\u597d\u50cf\u5728\u732e\u5b9d\u4f3c\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u8fd9\u4ef6\u7834\u8863\u670d\u7a76\u7adf\u662f\u4ec0\u9ebd\u5b9d\u8d1d\uff1f \u3000\u3000\u5200\u75a4\u5927\u6c49\u62ff\u8d77\u8863\u670d\u6765\u4ed4\u4ed4\u7ec6\u7ec6\u770b\u4e86\u4e00\u904d\u53c8\u4ea4\u7ed9\u90a3\u72ec\u81c0\u4eba\u3002 \u3000\u3000\u4ed6\u5728\u7ffb\u8863\u670d\u7684\u65f6\u5019\xb7\u90ed\u5927\u8def\u4e5f\u770b\u5230\u8863\u89d2\u7684\u886c\u91cc\u4e0a\u597d\u50cf\u7ee3\u6837\u6c34\u897f\u5374\u770b\u4e0d\u6e05\u695a\u7ee3\u7684\u662f\u5b57\u8fd8\u662f \u82b1\uff1f \u3000\u3000\u72ec\u81c2\u4eba\u4e5f\u5c06\u8fd9\u8863\u89d2\u7ffb\u5f00\u770b\u4e86\u770b\xb7\u5021\u6162\u7684\u70b9\u70b9\u5934\u9053\u201c\u4e0d\u9519\u662f\u4ed6\u7684\u8863\u670d\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u7b11\u9053\u201c\u5f53\u7136\u4e0d\u4f1a\u9519\u7684\u5728\u4e0b\u505a\u751f\u610f\u5411\u53ef\u9760\u3002\u201d \u3000\u3000\u72ec\u81c2\u4eba\u9053\u201c\u4ed6\u7684\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u6ca1\u6709\u8bf4\u8bdd\u5374\u4f38\u51fa\u4e86\u624b\u3002\u72ec\u81c2\u4eba\u9053\u201c\u4f60\u73b0\u5728\u5c31\u8981\uff1f\u201d \u3000\u3000\u6d3b\u5265\u76ae\u7b11\u9053\u201c\u5f00\u5f53\u94a5\u7684\u4eba\u90fd\u662f\u73b0\u8d27\u4ea4\u6613\u4e24\u9971\u60f3\u5fc5\u4e5f\u77e5\u9053\u7684\uff0c\u201d \u3000\u3000\u72ec\u81c2\u4eba\u51b7\u51b7\u9053\u201c\u597d\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u5200\u75a4\u5927\u6c49\u7acb\u523b\u4ece\u4e0b\u9762\u63d0\u8d77\u4e2a\u5305\u88b1\uff0c\u653e\u5728\u684c\u4e0a\u65f6\u201c\u7830\u201d\u7684\u4e00\u54cd\u3002 \u3000\u3000\u597d\u91cd\u7684\u5305\u88b1\u3002 \u3000\u3000\u201c\u80fd\u4ee4\u6d3b\u5265\u76ae\u5148\u8d34\u51fa\u4e94\u767e\u4e24\u94f6\u5b50\u7684\u53ea\u6709\u4e00\u4ef6\u4e8b\u5c31\u662f\u7164\u65e0\u4e25\u4e24\u94f6\u5b50\u7684\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u8bdd\u663e\u7136\u6ca1\u6709\u8bf4\u9519\u5305\u88b1\u91cc\u7684\u94f6\u5b50\u81f3\u5c11\u4e5f\u6709\u4e94\u5343\u4e24\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u71d5\u4e03\u773c\u5fc3\u91cc\u603b\u7b97\u660e\u767d\u5382\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5b9a\u5728\u627e\u6797\u592a\u5e73\u800c\u4e14\u627e\u5f97\u5f88\u6025\uff0c\u7adf\u4e0d\u501f\u51fa\u4e94\u5343\u4e24\u94f6\u5b50\u60ac\u8d4f\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u65e9\u5df2\u77e5\u9053\u8fd9\u4ef6\u4e8b\u4f46\u76f4\u7b49\u770b\u5230\u6797\u592a\u5e73\u7684\u8863\u670d\u65f6\uff0c\u624d\u53d1\u73b0\u6797\u592a\u5e73\u662f\u4ed6\u554a\u8981\u627e\u7684\u4eba\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u8981\u6797\u592a\u5e73\u8d54\u4ed6\u5230\u57ce\u91cc\u6765\u8d70\u7586\u597d\u5c06\u6797\u592a\u5e73\u5f53\u9762\u4ea4\u7ed9\u8fd9\u4e24\u4e2a\u4eba\u80fd\u4eb2\u81ea\u5c06\u4eba\u9001\u6765\uff0c\u8d4f \u94f6\u81ea\u7136\u66f4\u591a\u4e86\u3002 \u3000\u3000\u4f46\u6797\u592a\u5e73\u7a76\u7adf\u505a\u4e86\u4ec0\u9ebd\u4e8b\u503c\u5f97\u522b\u4eba\u82b1\u8fd9\u9ebd\u5927\u529f\u4ef7\u94b1\u6765\u627e\u4ed6\u5462\uff1f \u3000\u3000\u4e00\u770b\u5230\u94f6\u5b50\u6d3b\u5265\u76ae\u5ffd\u7136\u53d8\u5f97\u53ef\u7231\u6781\u4e86\uff0c\u7b11\u5f97\u8fde\u773c\u775b\u90fd\u5df2\u770b\u4e2d\u5200\u6d12\u9f20\u6c72\u9053\u201c\u4ed6\u5f84\u7530\u519b\u4f53\u73b0 \u5728\u603b\u5bf9\u4ee5\u8bf4\u4e0b\u2103\u2606\uff1f\u201d \u3000\u3000\u65e0\u8bba\u6797\u592a\u5e73\u505a\u5382\u4ec0\u9ebd\u4e8b\u4ed6\u65e2\u7136\u8981\u8eb2\u8fd9\u4e24\u4eba\u5c31\u4e0d\u80fd\u8ba9\u8fd9\u66f2\u4eba\u627e\u5230\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u51c6\u5907\u4ece\u7a97\u5b50\u91cc\u51b2\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\xb7\u6d3b\u5265\u76ae\u7684\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u998f\u4f50\u3002 \u3000\u3000\u4ed6\u773c\u775b\u76f4\u52fe\u52fe\u7684\u77aa\u95e8\u53e3\u5f20\u5927\u4e86\u7530\xb7\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\xb7\u90a3\u8868\u60c5\u5c31\u597d\u50cf\u7a81\u7136\u88ab\u4eba\u585e\u4e86\u6ee1\u5634\u6ce5 \u5df4\u3002 \u3000\u3000\u7ff1\u5927\u8def\u987a\u4ed6\u76ee\u5149\u770b\u8fc7\u53bb\uff0c\u4e5f\u7acb\u523b\u5403\u4e86\u60ca\u3002 \u3000\u3000\u95e8\u95e8\u4e5f\u4e0d\u77e5\u4f55\u65f6\u8d70\u8fdb\u4e86\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5f88\u7ecf\u901a\u7684\u8001\u592a\u5a46\u5e76\u6ca1\u6709\u751a\u9ebd\u4ee4\u4eba\u5403\u60ca\u7684\u5730\u65b9\uff0c\u76ef\u5564\u53e3\u5927\u8def\u5374\u505a\u68a6\u4e5f\u60f3\u4e0d \u5230\u4f1a\u5728\u6b64\u65f6\u6b64\u5730\u770b\u5230\u5979\u3002 \u3000\u3000\u4ed6\u521a\u660e\u660e\u8fd8\u770b\u5230\u5979\u63d0\u6876\u5783\u573e\u7ad9\u5728\u5229\u6e90\u5f53\u7b4b\u95e8\u53e3\u7684\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u97f6\u5c31\u5750\u5495\u8f66\u5230\u8fd9\u91cc\u6765\uff0c\u8def\u4e0a\u5e76\u6ca1\u6709\u505c\u7559\uff0c\u8fd9\u8001\u592a\u5a46\u662f\u600e\u9ebd\u6765\u7684\uff0c\u96be\u9053\u662f\u98de\u6765\u7684 \u5417\uff1f\u6d3b\u5265\u76ae\u66f4\u8c61\u662f\u89c1\u4e86\u9b3c\u4f3c\u7684\u560e\u58f0\u9053\u201c\u4f60\u2026\u2500\u54cd\u9648\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8001\u592a\u5a46\u624b\u91cc\u63ba\u76cf\u7897\uff0c\u6162\u541e\u541e\u7684\u4f01\u8fdb\u6765\u6447\u5934\u53f9\u6c14\u9053\u6784\uff1a\u5403\u836f\u7684\u65f6\u5019\u5df2\u5230\u4e86\u4e3a\u4ec0\u9ebd\u603b\u662f\u5fd8\u8bb0 \u5462\uff1f\u6211\u7279\u5730\u66ff\u4f60\u9001\u6765\u4fa0\u559d\u53bb\u5427\u3002\u201d\u6d3b\u5265\u76ae\u63a5\u8fc7\u76ca\u7897\u53ea\u542c\u5f97\u76d6\u4e8e\u5728\u7897\u4e0a\u8def\u54af\u201d\u7684\u4f5c\u54cd\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u624b\u5728\u53d1\u6296\u8fde\u51b7\u6c57\u90fd\u6d41\u4e86\u51fa\u6765\u3002\u72ec\u81c2\u4eba\u548c\u5200\u75a4\u5927\u6c49\u8138\u4e0a\u8fd8\u662f\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u76f4\u51b7\u51b7\u7684 \u770b\u8fd9\u8001\u592a\u5a46\u6b64\u523b\u7a81\u7136\u540c\u65f6\u51fa\u4e4e\u4e24\u9053\u4e4c\u5149\u5411\u8fd9\u76d6\u7897\u4e0a\u968f\u5c04\u4e3d\u51fa\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u51fa\u624b\u90fd\u77f3\u6162\u3002 \u3000\u3000\u8bf4\u77e5\u4e4c\u5149\u521a\u89c8\u5230\u8001\u592a\u5a46\u9762\u524d\u5c31\u5ffd\u7136\u4e0d\u89c1\u4e8e\u3002 \u3000\u3000\u8fd9\u8001\u592a\u5a46\u660e\u660e\u8fde\u52a8\u90fd\u6ca1\u52a8\u3002\u5200\u75a4\u5927\u6c49\u8138\u8272\u4e5f\u6709\u70b9\u53d8\u4e86\u3002 \u3000\u3000\u72ec\u81c2\u4eba\u5374\u8fd8\u662f\u9762\u65e0\u8868\u60c5\uff0c\u51b7\u7b11\u9053\u201c\u60f3\u4e0d\u5230\u9601\u4e0b\u539f\u6765\u662f\u4f0e\u9ad8\u4eba\u597d\u869c\u6781\u4e86\u3002\u201d \u3000\u3000\u8005\u592a\u5a46\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u4e0d\u597d\u70b9\u4e5f\u4e0d\u597d\u3002\u201d \u3000\u3000\u72ec\u81c2\u4eba\u9053\u201c\u6709\u4ec0\u9ebd\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8001\u592a\u5a46\u9053\u201c\u6709\u4ec0\u9ebd\u597d\uff1f\u4f60\u4eec\u9047\u89c1\u6211\u5c31\u8981\u59fb\u9709\u4e86\u8fd8\u6709\u4ec0\u9ebd\u597d\uff1f\u201d \u3000\u3000\u72ec\u81c2\u4eba\u8200\u7136\u957f\u8eab\u800c\u8d77\u5389\u58f0\u9053\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\uff1f\u6562\u6765\u7ba1\u6211\u4eec\u7684\u95f9\u4e8b\uff1f\u201d\u8001\u592a\u5a46\u9053\u201c\u8c01\u7ba1 \u4f60\u4eec\u7684\u4e8b\uff1f\u6807\u4eec\u7684\u4e8b\u8fd8\u4e0d\u914d\u6211\u6765\u7ba1\uff0c\u8bf7\u6211\u7ba1\u6211\u5de1\u4e0d\u7ba1\u8dea\u4e0a\u6765\u6c42\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7ba1\u201c \u3000\u3000\u8001\u592a\u5a46\u8bf4\u8bdd\u603b\u6709\u70b9\u7792\u919a\u56db\u56db\u7684\u3002\u72ec\u81c2\u4eba\u9053\u201c\u90a3\u9ebd\u4f60\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d\u8001\u592a\u5a46\u9053\u201c\u6211\u6765\u8981\u4ed6\u5403 \u836f\u3002\u5feb\u5403\xb7\u5403\u5b8c\u4e86\u836f\u5c31\u8be5\u7761\u89c9\u4e86\u3002\u201d \u3000\u3000\u6d3b\u5265\u76ae\u6101\u7709\u82e6\u8138\u634f\u9f3b\u5b50\u5c06\u836f\u5403\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8001\u592a\u5a46\u9053\u201c\u597d\u56de\u53bb\u7761\u89c9\u5427\u3002\u201d \u3000\u3000\u5979\u5c31\u8c61\u62c9\u513f\u6237\u4f3c\u7684\u62c9\u6d3b\u5265\u76ae\u5c31\u6280\u5916\u8d70\u3002 \u3000\u3000\u7a81\u7136\u95ee\u5200\u5149\u95ea\u72ec\u81c2\u4eba\u5df2\u51cc\u7a7a\u98de\u8d77\u67c4\u96ea\u4eae\u7684\u5f2f\u5200\u5f53\u5934\u5288\u5382\u7c73\u3002 \u3000\u3000\u6562\u51cc\u7a7a\u51fa\u4e52\u7684\u4eba\u5200\u6cd5\u81ea\u7136\u4e0d\u5f31\u3002 \u3000\u3000\u4f46\u201c\u300f\u5149\u95ea\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u67c4\u518d\u4eae\u7684\u5f2f\u5200\u5ffd\u7136\u65ad\u6210\u4e01\u4e24\u622a\u201c\u5f53\u201d\u7684\u6389\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6885\u519c\u72ec\u81c2\u4eba\u8eab\u8fb9\u3002 \u3000\u3000\u72ec\u81c2\u4eba\u4e0d\u77e5\u4e3a\u5382\u4ec0\u9ebd\u5df2\u8dea\u5728\u5730\u4e0a\u8dea\u5728\u8fd9\u8001\u5a46\u9762\u524d\xb7\u6ee1\u5934\u5927\u6c57\u4eff\u4f5b\u7528\u529b\u60f3\u7ad9\u6765\u606b\u7528\u5c3d\u5168\u8eab \u529b\u6c14\u8fd8\u662f\u7ad9\u5c0f\u8d77\u6765\u3002 \u3000\u3000\u8001\u592a\u5a46\u53f9\u4e86\u95e8\u6c14\u8d2d\u54fa\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u4f60\u4eec\u7684\u4e8b\u5c31\u7b97\u8dea\u4e0b\u6765\u6c42\u6211\u6211\u4e5f\u4e0d\u7ba1\u7684\u8fd9\u4eba\u5c45\u7136\u6c72\u542c \u89c1\xb7\u6e2f\u9053\u4ece\u6735\u6bd4\u6211\u8fd8\u804b\u9ebd\uff1f\u201d \u3000\u3000\u5979\u7792\u7792\u56db\u56db\u7684\u8bf4\u8bdd\uff0c\u8e4b\u6805\u8d70\u5382\u51fa\u53bb\u3002 \u3000\u3000\u6d3b\u5265\u76ae\u4e56\u4e56\u6784\u8ddf\u5728\u5f8c\u9762\u8fde\u5927\u6c14\u90fd\u4e0d\u6562\u5c71\u3002\u5200\u75a4\u5927\u6c49\u4e5f\u5df1\u6ee1\u5934\u5927\u8ba1\u5ffd\u7136\u9053\u201c\u524d\u8f88\u8bf7\u7b49 \u7b49\u3002\u201d \u3000\u3000\u8001\u592a\u5a46\u9053\u201c\u8fd8\u7b49\u4ec0\u9ebd\uff1f\u96be\u9053\u4f60\u4e5f\u60f3\u6765\u8ddf\u6211\u78b4\u4e2a\u5934\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5200\u75a4\u5927\u6c49\u9053\u201c\u524d\u8f88\u65e2\u7136\u5df1\u4f38\u624b\u6765\u7ba1\u8fd9\u4ef6\u4e8b\u5728\u4ea7\u4e5f\u6ca1\u4ec0\u9ebd\u8bdd\u597d\u8bf4\uff01\u76fc\u524d\u8f88\u80fd\u9500\u5382\u4e2a\u540d\u53f7\u5728 \u5c38\u7b49\u56de\u53bb\u4e5f\u597d\u5411\u4e2d\u4eba\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u8001\u592a\u5a46\u9053\u201c\u4f60\u60f3\u95ee\u6211\u7684\u540d\u5b57\uff1f\u201d\u5200\u75a4\u5927\u6c49\u9053\u201c\u6c47\u60f3\u8bf7\u6559\u3002\u201d\u8001\u592a\u5a46\u9053\u201c\u4f60\u8fd8\u4e0d\u914d\u77e5\u9053\u6211 \u7684\u540d\u5b57\u6211\u8bf4\u4f60\u4e5f\u4e0d\u4f1a\u77e5\u901a\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u63a5\u9053\u201c\u606b\u4f60\u5374\u53ef\u4ee5\u56de\u53bb\u544a\u8bc9\u4f60\u90a3\u4e3b\u4eba\u5c31\u8bf4\u6709\u4e2a\u8001\u670b\u53cb\u529d\u4ed6\uff0c\u5c0f\u5b69\u5b50\u602a\u53ef\u601c\u7684\u6700\u597d \u83ab\u8981\u903c\u5f97\u592a\u7d27\u5426\u5219\u8fde\u522b\u4eba\u90fd\u4f1a\u770b\u4e0d\u60ef\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u8d70\u51fa\u3002 \u7b2c\u516d\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u516d\u7ae0 \u516d \u3000\u3000\u5200\u75a4\u5927\u6c49\u5145\u523b\u8ffd\u51fa\u6765\xb7\u8ffd\u5230\u95e8\u53e3\xb7\u4f3c\u4e4e\u8fd8\u60f3\u95ee\u5979\u4ec0\u9ebd\u3002 \u3000\u3000\u4f46\u95e8\u5916\u8fde\u4e2a\u4eba\u5f71\u90fd\u6ca1\u6709\u8fd9\u8001\u592a\u548c\u6d3b\u5265\u76ae\u90fd\u5df2\u5ffd\u7136\u4e0d\u89c1\u5382\u3002\u201d \u3000\u3000\u8fd9\u70e7\u996d\u7684\u8001\u592a\u5a46\u539f\u6765\u662f\u4f4d\u7edd\u9876\u7684\u9ad8\u624b\u6b66\u529f\u5df4\u9ad8\u5f97\u522b\u4eba\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u96be\u602a\u90a3\u5929\u91d1\u72ee\u548c\u68cd\u5b50\u5230\u5f53\u94fa\u91cc\u53bb\u641c\u67e5\xb7\u56de\u6765\u65f6\u6001\u5ea6\u90a3\u9ebd\u606d\u656c\u4ed6\u4eec\u82e5\u4e0d\u662f\u5403\u5382\u8fd9\u8001\u592a\u5a46\u7684 \u54d1\u5df4\u4e8f\xb7\u5c31\u662f\u5df3\u770b\u51fa\u5979\u662f\u51c6\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u73b0\u5728\u603b\u7b97\u5df3\u660e\u767d \u3000\u3000\u67ab\u4ed6\u4eec\u7eab\u6709\u4ef6\u4e8b\u66f4\u60f3\u4e0d\u901a\xb7\u4e24\u4eba\u5bf9\u671b\u4e86\u773c\u540c\u65f6\u5411\u5f8c\u68d5\u51fa\u3002 \u3000\u3000\u5f8c\u9762\u6709\u88f8\u6811\u5927\u6811\u3002\u6811\u4e0a\u6ca1\u6709\u53f6\u5b50\u53ea\u6709\u79ef\u96ea\uff0c \u3000\u3000\u71d5\u4e03\u53ea\u597d\u8e6d\u5728\u6811\u67f3\u4e0a\u90ed\u5927\u8def\u5374\u5e87\u80a1\u8d2c\u4e86\u535c\u5de6\u7136\u5f8c\u5c31\u8c61\u8db3\u6328\u5382\u5200\u4f3c\u7684\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u96ea\u51b7\u5f97\u8c61\u5200\u3002 \u3000\u3000\u71d5\u4e03\u53f9\u77ff\u95e8\u6c14\u6447\u64ad\u5934\u9053\u201c\u4f60\u5750\u5de6\u7684\u65f6\u5019\u96be\u9053\u4ece\u6765\u4e5f\u4e0d\u770b\u770b\u5c41\u80a1\u535c\u9762\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u6211\u6c72\u6ce8\u610f\u6211\u5728\u5c48\u5fc3\u534a\u3002\u201d\u6811\u679d\u5f88\u7cca\u4ed6\u4e5f\u5728\u71d5\u4e03\u8eab\u65c1\u8e72\u4e2b\u4e0b\u6765\u9053\u201c\u6211\u5728\u60f3\u90a3 \u8001\u592a\u5a46\u5979\u660e\u660e\u70c3\u503c\u5f88\u4e86\u4e2d\u8d77\u7684\u6b66\u6797\u9ad8\u624b\u4e3a\u4ec0\u9ebd\u8981\u5728\u6d3b\u5265\u76ae\u7684\u5f53\u94fa\u5f53\u8001\u5988 \u3000\u3000\u71d5\u4e03\u6c89\u541f\u9053\u201c\u4e5f\u8bb8\u5979\u4e5f\u548c\u98ce\u6816\u68a7\u6837\u5728\u8eb2\u907f\u522b\u4eba\u7684\u8ffd\u8e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u7406\u7531\u4e2d\u542c\u597d\u50cf\u5f88\u5145\u8db3\u4ed4\u7ec6\u60f3\u5374\u6709\u5f88\u591a\u5730\u65b9\u8bf4\u4e0d\u901a\u201c \u3000\u3000\u71d5\u4e03\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e16\u754c\u8fd9\u9ebd\u5927\u6709\u5f88\u591a\u5730\u65b9\u90fd\u53ee\u4ee5\u8eb2\u907f\u522b\u4eba\u7684\u9000\u8e2a\u5c24\u83ab\u662f\u50cf\u5979\u8fd9\u6837\u7684\u9ad8\u624b\u4e3a\u4ec0\u9ebd \u8981\u53bb\u505a\u522b\u4eba\u7684\u8005\u5988\u5b50\u542c\u522b\u4eba\u7684\u6307\u70e4\u53d7\u522b\u4eba\u7684\u6c14\u201d \u3000\u3000\u4ed6\u9762\u6447\u5934\u53c8\u63a5\u9053\u201c\u5c31\u7b97\u5979\u8981\u505a\u4eba\u5bb6\u7684\u8001\u5988\u5b50\xb7\u4e5f\u5e9c\u8be5\u627e\u4e2a\u50cf\u6837\u70b9\u7684\u5165\u627e\u4e2a\u50cf\u6837\u70b9\u7684\u5730\u65b9 \u4e3a\u4ec0\u9ebd\u504f\u504f\u9009\u4e0a\u6d3b\u5265\u76ae\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff0c\u201c\u4f60\u62a5\u4e0d\u901a\uff1f\u201d\u7ff1\u5927\u8def\u9053\u201c\u5b9e\u5728\u60f3\u5c0f\u901a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u4e0d\u901a\u7684\u4e8b\u522b\u4eba\u5f53\u7136\u4e5f\u5b9a\u60f3\u4e0d\u901a\u4e01\u3002\u201d\u90ed\u5927\u8def\u7b11\u7b11\xb7\u9053\u201c\u82e5\u8fde\u6211\u4e5f\u60f3\u4e0d\u5230\uff0c \u80fd\u60f3\u901a\u7684\u4eba\u6015\u5f88\u5c11\u3002\u201d\u71d5\u4e03\u9053\u201c\u4e5f\u8bb8\u5979\u5c31\u662f\u8981\u4eba\u5bb6\u60f3\u4e0d\u901a\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u60f3\u4e0d\u901a\u7684\u4e8b\u8fd8\u6709\u5f88\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u770b\u5979\u7684\u6b66\u529f\u5929\u4e0b\u6015\u5f88\u5c11\u6709\u4eba\u80fd\u662f\u5979\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u5979\u6b66\u529f\u7684\u786e\u5f88\u9ad8\u6211\u975e\u4f46\u6ca1\u6709\u770b\u8fc7\u6b66\u529f\u8fd9\u9ebd\u9ad8\u7684\u5165\u7b80\u76f4\u8fde\u542c\u90fd\u6ca1\u6709\u542c \u8bf4\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u6211\u8ba4\u4e3a\u5979\u6839\u672c\u5c31\u7528\u4e0d\u6015\u522b\u4eba\xb7\u6839\u672c\u5c31\u7528\u4e0d\u8eb2\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u83ab\u5fd8\u8bb0\u5f3a\u4e2d\u66f4\u6709\u5f3a\u4e2d\u624b\u4e00\u5c71\u8fd8\u6709\u5c71\u9ad8\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u53e5\u5df4\u8005\u6389\u7259\u7684 \u951a\u8bdd\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8001\u6389\u7259\u7684\u8bdd\u5f80\u5f80\u662f\u6700\u6709\u9053\u7406\u7684\u8bdd\u8d8a\u8005\u8d8a\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u79d8\u5bc6 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5047\u5982\u5979\u771f\u7684\u5728\u8eb2\u907f\u522b\u4eba\u7684\u8feb\u8e2a\xb7\u884c\u52a8\u81f3\u5c11\u5e94\u8be5\u79d8\u5bc6\u4e9b\u5047\u6211\u4eec\u6bcf\u6b21\u53bb\u5f53\u94fa\u7684\u65f6 \u5019\u90fd\u770b\u5230\u5979\u91cc\u91cc\u5916\u5916\u7684\u4f01\u8fdb\u8d70\u51fa\u70b9\u77b3\u6ca1\u6709\u77f3\u6562\u89c1\u4eba\u7684\u6837\u4e8e\u3002\u201d \u3000\u3000\u67b7\u4e5f\u9053\u201c\u90a3\u65f6\u4f60\u770b\u4e0d\u770b\u5f97\u51fa\u5979\u662f\u4e2a\u600e\u6837\u7684\u4eba\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u522b\u4eba\u65e2\u7136\u770b\u672c\u51fa\u5979\u662f\u8c01\u5979\u4e3a \u4ec0\u9ebd\u4e0d\u6562\u89c1\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8ba4\u4e3a\u5979\u4e5f\u548c\u98ce\u6816\u68a7\u6837\u6613\u5bb9\u6539\u4efd\u8fc7\uff1f\u201d \u3000\u3000\u75c8\u4e03\u9053\u201c\u6c5f\u6e56\u4e2d\u4f1a\u6613\u5bb9\u6539\u626e\u7684\u4eba\uff0c\u5e76\u4e0d\u6b62\u98ce\u6346\u68a7\u4e00\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u91d1\u643a\u548c\u68cd\u5b50\u4e3a\u4ec0\u9ebd\u773c\u5c31\u770b\u51fa\u5979\u662f\u8c01\u4e86\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u4ed6\u4eec\u770b\u51fa\u6765\u4e86\u201d\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4eec\u82e5\u6ca1\u6709\u770b\u51fa\u6765\u5bf9\u6d3b\u5265\u76ae\u4e3a\u4ec0\u9ebd\u524d\u636e \u5f8c\u606d\u51b2 \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\xb7\u9053\u201c\u90a3\u9ebd\u4f9d\u4f60\u770b\u6765\u8fd9\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f9d\u6211\u770b\u5979\u548c\u6d3b\u5265\u76ae\u5b9a\u6709\u70b9\u7279\u522b\u7684\u5173\u7cfb\uff0c\u4e5f\u8bb8\u662f\u6d3b\u5265\u76ae\u7684\u8001\u670b\u53cb\uff0c\u4e5f\u8bb8\u662f\u6d3b\u5265 \u76ae\u7684\u4eb2\u621a\xb7\u4f53\u8bf4\u6709\u6ca1\u6709\u9053\u7406\uff1f\u201d\u85cf\u4e03\u9053\u201c\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4e5f\u627f\u8ba4\u6211\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u4e5f\u7b11\u4e86\u9053\u201c\u56e0\u4e3a\u6211\u7684\u770b\u6cd5\u672c\u6765\u4e5f\u662f\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\u9053\u201c\u4f60\u7684\u770b\u6cd5\u65e2\u7136\u65e9\u5c31\u8ddf\u6211\u4e00\u6837\u521a\u4e3a\u4ec0\u9ebd\u8981\u8ddf\u6211\u64d2\u6760\uff1f\u201d \u3000\u3000\u852c\u4e03\u9053\u201c\u56e0\u4e3a\u6211\u5929\u751f\u5c31\u559c\u6b22\u8ddf\u4f60\u62a2\u6760\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u77aa\u773c\u770b\u4e86\u4ed6\u534a\u5929\uff0c\u9053\u201c\u5047\u5982\u6211\u8bf4\u8fd9\u96f7\u662f\u767d\u7684\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u6211\u5c31\u8bf4\u662f\u9ed1\u7684\u3002\u201d \u3000\u3000\u65e0\u8bba\u4f53\u591a\u806a\u660e\xb7\u591a\u80fd\u5e72\u4f46\u6709\u65f6\u8fd8\u662f\u4f1a\u7a81\u7136\u9047\u89c1\u4e2a\u514b\u661f\u65e0\u8bba\u4f60\u6709\u591a\u5927\u7684\u672c\u4e8b\uff0c\u4e00\u9047\u89c1\u4ed6\u5c31 \u5b8c\u5168\u4f7f\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u597d\u50cf\u5c31\u662f\u90ed\u5927\u8def\u7684\u514b\u661f\u3002 \u3000\u3000\u90ed\u5927\u8def\u786c\u662f\u5bf9\u4ed6\u6ca1\u6cd5\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\u4ed6\u5ffd\u53c8\u7b11\u5382\u7b11\xb7\u9053\u201c\u81f3\u5c11\u6709\u4ef6\u4e8b\u4f60\u603b\u4e2d\u80fd\u4e2d\u627f\u8ba4\u7684\u3002\u201d\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u4e8b\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6d3b\u5265\u76ae\u8fd9\u6b21\u8fde\u4e2a\u4eba\u7684\u76ae\u90fd\u6ca1\u6709\u5265\u5230\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e49\u9519\u4e86\u3002\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u4e49\u9519\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6d3b\u5265\u76ae\u8fd9\u6b21\u603b\u7b97\u5265\u4e86\u4e00\u4e2a\u4eba\u7684\u76ae\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u5265\u4e86\u51c6\u7684\u76ae\uff1f\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u4ed6\u65e5\u5df1\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\uff1f \u3000\u3000\u4e3a\u4ec0\u9ebd\u6709\u4eba\u80af\u82b1\u597d\u51e0\u5343\u4e24\u94f6\u4e8e\u6765\u627e\u4ed6\uff1f \u3000\u3000\u627e\u4ed6\u5e72\u4ec0\u9ebd\uff1f\u90ed\u5927\u8def\u9053\u201c\u4f60\u770b\u8fd9\u4e9b\u4eba\u4e3a\u4ec0\u9ebd\u8981\u627e\u6797\u592a\u5e73\u5462\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u4ed6\u597d\u50cf\u5df2\u5b66\u4e56\u4e86\u767d\u5df1\u5c45\u7136\u6ca1\u6709\u53d1\u8868\u610f\u89c1\u3002 \u3000\u3000\u71d5\u4e03\u6c89\u541f\u9053\u201c\u4f60\u80af\u82b1\u4e92\u516d\u5e72\u4e24\u94f6\u5b50\u53bb\u627e\u4e2a\u4eba\u4e3a\u7684\u4f1a\u662f\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u6839\u672c\u5c31\u4e2d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u8198\u4e86\u4ed6\u773c\u9053\u201c\u5047\u5982\u6211\u5ffd\u7136\u5931\u8e2a\u4e86\uff0c\u82e5\u8981\u4f60\u82b1\u4e94\u5343\u9676\u94f6\u5b50\u6765\u627e\u6211\u4f60\u80af\u4e0d\u80af\uff1f\u201d\u90ed\u5927\u8def \u60f3\u4e5f\u4e0d\u60f3\u7acb\u523b\u9053\u201c\u5f53\u7136\u80af\u3002\u4e3a\u4e86\u4f60\u5c31\u7b97\u53eb\u6211\u62ff\u8111\u888b\u53bb\u5f53\u90fd\u6ca1\u5173\u7cfb\u201c \u3000\u3000\u71d5\u4e03\u7684\u773c\u775b\u4eae\u4e86\u3002 \u3000\u3000\u4e2a\u4eba\u7684\u773c\u775b\u53ea\u6709\u5728\u975e\u5e38\u5feb\u4e50\u3001\u975e\u5e38\u5f97\u610f\u65f6\u624d\u4f1a\u4eae\u8d77\u6765\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4ecd\u662f\u597d\u670b\u53cb\u6240\u4ee5\u6211\u624d\u80af\u3002\u4f46\u6797\u592a\u4e2d\u5374\u7edd\u4e0d\u4f1a\u662f\u90a3\u4e24\u4eba\u7684\u597d\u670b\u53cb\xb7\u4ed6\u6839 \u672c\u5c31\u4e0d\u4f1a\u4ea4\u8fd9\u79cd\u670b\u53cb\u3002\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\xb7\u9053\u201c\u5047\u5982\u6709\u4eba\u6740\u4e86\u6211\u4f60\u662f\u4e0d\u662f\u4e5f\u80af\u82b1\u4e94\u5343\u4e24\u94f6\u5b50\u627e\u4ed6\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u80af\u6211\u5c31\u7b97\u62fc\u4e86\u8001\u547d\u4e5f\u8981\u627e\u5230\u90a3\u4eba\u66ff\u4f60\u62a5\u4ec7\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u6447\u5934\u9053\u201c\u4f46\u6797\u5927\u5e73\u5374\u7edd\u6ca1\u6709\u6740\u8fc7\u4eba\u4ed6\u4ee5\u4e3a\u81fc\u5df1\u6740\u4e86\u5357\u5bcc\u4e11\u4e4b\u5f8c\u90a3\u79cd\u75db\u82e6\u7684\u6837\u5b50\u7edd \u4e0d\u662f\u88c5\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5047\u5982\u6709\u4eba\u67aa\u4e86\u4f60\u4e94\u4e07\u4e24\u94f6\u5b50\u8981\u4f60\u82b1\u4e94\u5343\u94f6\u5b50\u627e\u4ed6 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4f46\u6797\u592a\u5e73\u6765\u7684\u65f6\u5019\u8eab\u4e0a\u8fde\u5206\u94f6\u4e88\u8c03\u6ca1\u6709\u4f55\u51b5\u4ed6\u6839\u672c\u4e5f\u4e0d\u8c61\u90a3\u79cd\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u73b0\u5728\u4e0d\u662f\u6211\u627e\u4f60\u64d2\u6760\u662f\u4f60\u5728\u627e\u6211\u59cb\u6789\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\u9053\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u4e5f\u5b9a\u4e0d\u4f1a\u771f\u7684\u8fd9\u9ebd\u60f3\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u540d\u5b9e\u8bf4\u6211\u6839\u672c\u5c31\u60f3\u4e0d\u51fa\u4ed6\u4eec\u627e\u6797\u592a\u5e73\u4e3a\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u867d\u7136\u60f3\u4e0d\u51fa\u5374\u95ee\u5f97\u51fa\u7684\u83ab\u5fd8\u8bb0\u6211\u5df2\u4ece\u68cd\u5b50\u90a3\u91cc\u5b66\u4f1a\u4e86\u5f88\u591a\u79cd\u95ee\u8bdd\u7684\u6cd5 \u5b50\u3002\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u706f\u8fd8\u4eae\u65e2\u6ca1\u6709\u770b\u5230\u6709\u4eba\u8fdb\u53bb\u4e5f\u6ca1\u6709\u770b\u5230\u6709\u4eba\u51fa\u805a\u3002 \u3000\u3000\u4ed6\u4eec\u6b63\u60f3\u53bb\u95ee\u4e2a\u660e\u767d\uff0c\u7a97\u4e8e\u5ffd\u7136\u5f00\u5382\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6b62\u7ad9\u5728\u7a97\u53e3\u62db\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u6b63\u5f04\u4e0d\u6e05\u8fd9\u4eba\u662f\u5728\u5411\u8c01\u62db\u624b\u7684\u65f6\u5019\xb7\u8fd9\u4eba\u5df2\u7b11\u9053\u201c\u6811\u535c\u5b9a\u5f88\u51b7\uff0c\u4e24\u4f0e\u4e3a\u4ec0\u9ebd\u4e0d\u8fdb\u6765 \u70e4\u70e4\u706b\u5462\uff1f\u201d \u3000\u3000\u706b\u5f88\u65fa\u3002 \u3000\u3000\u5750\u5728\u706b\u65c1\u786e\u6bd4\u7530\u5728\u6811\u535c\u8212\u670d\u591a\u4e86\u3002 \u3000\u3000\u521a\u5728\u7a97\u53e3\u5411\u4ed6\u4ecd\u62db\u624b\u7684\u4eba\u73b0\u5728\u4e5f\u5df2\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u65e2\u4e0d\u662f\u90a3\u80a1\u4e0a\u6709\u5200\u75a4\u7684\u5927\u6c49\xb7\u4e5f\u4e0d\u662f\u90a3\u770b\u6765\u5f88\u51f6\u6076\u7684\u72ec\u81c2 \u3000\u3000\u8fd9\u4eba\u521a\u6839\u672c\u5c31\u4e2d\u6789\u8fd9\u5c4b\u5343\u91cc\u3002 \u3000\u3000\u521a\u5728\u8fd9\u5c4b\u4e8e\u91cc\u7684\u4eba\uff0c\u73b0\u5728\u5df2\u4e0d\u77e5\u5230\u4ec0\u9ebd\u5730\u65b9\u53bb\u4e86\u3002\u90ed\u5927\u8def\u65e2\u6ca1\u6709\u770b\u89c1\u4ed6\u4eec\u8d70\u51fa\u6765\u4e5f\u6ca1\u6709 \u770b\u89c1\u8fd9\u4e2a\u4eba\u8d70\u8fdb\u53bb\u3002 \u3000\u3000\u7ef7\u5927\u8def\u53ea\u6709\u4e00\u70b9\u503c\u5f97\u5b89\u6170\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fd9\u4eba\u4ece\u5934\u5230\u811a\u65e0\u8bba\u4ece\u90a3\u91cc\u770b\u90fd\u6bd4\u521a\u4e24\u4e2a\u4eba\u987a\u773c\u788d\u5bb9\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u662f\uff0c\u8fd9\u4eba\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u81ea\u5df1\u5957\u72ec\u7279\u7684\u6cd5\u5b50\u6765\u5c06\u5973\u4eba\u5206\u6210\u597d\u51e0\u7b49\u597d\u51e0\u7c7b\u3002 \u3000\u3000\u65e0\u8bba\u4f60\u7528\u54ea\u79cd\u6cd5\u5b50\u6765\u5206\u5979\u90fd\u53ef\u4ee5\u7b97\u662f\u7b2c\u4e00\u7b49\u7684\u4ea5\u4eba\u3002 \u3000\u3000\u5979\u867d\u7136\u5df2\u4e0d\u592a\u5e74\u9752\uff0c\u4f46\u770b\u6765\u8fd8\u662f\u72e0\u7f8e\uff0c\u5f88\u6709\u98ce\u97f5\u3002 \u3000\u3000\u4e16\u4e0a\u7684\u786e\u6709\u79cd\u5973\u4eba\u53ef\u4ee5\u4ee4\u4f60\u6839\u672c\u5c31\u4e0d\u4f1a\u6ce8\u610f\u5979\u7684\u5e74\u7eaa\u3002 \u3000\u3000\u5979\u5c31\u662f\u8fd9\u79cd\u5973\u4eba\u3002 \u3000\u3000\u7f8e\u4e3d\u7684\u5973\u4eba\u5927\u591a\u90fd\u5f88\u9ad8\u50b2\u5f88\u4e0d\u8bb2\u7406\u53ea\u6709\u5f88\u5c11\u6570\u662f\u4f8b\u5916\u3002 \u3000\u3000\u5979\u5c31\u662f\u4f8b\u5916\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8c61\u8fd9\u9ebd\u6837\u4e2a\u5973\u4eba\u600e\u9ebd\u4f1a\u5ffd\u7136\u5728\u8fd9\u5c4b\u4e88\u51fa\u73b0\u5462\uff1f \u3000\u3000\u5979\u548c\u521a\u90a3\u4e24\u4e2a\u4eba\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u548c\u8fd9\u4ef6\u4e8b\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f \u3000\u3000\u90ed\u5927\u8def\u5f53\u7136\u60f3\u95ee\xb7\u5374\u76f4\u6ca1\u6709\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u6bcf\u6b21\u8981\u95ee\u7684\u65f6\u5019\u90fd\u53d1\u73b0\u81ea\u5df1\u5148\u5df2\u88ab\u4eba\u95ee\u8fd9\u9ebd\u6837\u4e2a\u5973\u4eba\u5728\u95ee\u4f60\u8bdd\u66f2\u65f6\u5019\xb7\u4f60\u5f53\u7136\u53ea\u6709\u5148 \u56f4\u7b54\u3002\u201c\u6211\u59d3\u536b\u3002\u201d\u5979\u5fae\u7b11\u9053\u201c\u4f60\u4eec\u4e24\u4f4d\u5462\uff1f\u201d \u3000\u3000\u5979\u7684\u7b11\u5bb9\u8ba9\u4eba\u6839\u672c\u6ca1\u6cd5\u5b50\u62d2\u7edd\u56de\u7b54\u5979\u7684\u8bdd\u3002 \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u201c\u6211\u59d3\u90ed\u3002\u4ed6\u59d3\u71d5\u71d5\u4e8e\u7684\u71d5\u3002\u201d \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u773c\xb7\u536b\u592b\u4eba\u5df2\u7b11\u9053\u201c\u6797\u592a\u5e73\u7684\u670b\u53cb\u6211\u90fd\u8ba4\u5f97\u600e\u9ebd\u80b2\u6ca1\u6709\u89c1\u8fc7\u4f60\u4eec\u4e24\u4f9d\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u53c8\u60f3\u62a2\u95ee\u7b54\u5ffd\u7136\u53d1\u73b0\u71d5\u4e03\u7684\u773c\u775b\u6b63\u5728\u77aa\u4ed6\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u4f4e\u4e0b\u5934\u53bb\u54b3\u55fd\u3002 \u3000\u3000\u71d5\u4e03\u7684\u773c\u775b\u8fd9\u624d\u8f6c\u8fc7\u6765\u770b\u536b\u592b\u4eba\uff0c\u6de1\u6de1\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u6211\u4eec\u662f\u6797\u4eba\u5e73\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4e24\u4f0e\u5192\u98ce\u96ea\u4ece\u5f53\u94fa\u5730\u65b9\u8d76\u5230\u8fd9\u91cc\u6765\xb7\u53c8\u5192\u98ce\u96ea\u5728\u5916\u9762\u7b49\u4e86\u90a3\u9ebd\u4e45\xb7\u5f53\u7136\u672f\u4f1a \u662f\u4e3a\u4e86\u90a3\u5f53\u7b4b\u8001\u677f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u4f1a\uff1f\u201d \u3000\u3000\u7889\u592b\u4eba\u8d61\u7136\u9053\u201c\u9f99\u4ea4\u9f99\u98ce\u4ea4\u98ce\u8017\u5b50\u7684\u670b\u53cb\u4f1a\u6253\u6d1e\u4ec0\u9ebd\u4eba\u4ea4\u4ec0\u9ebd\u6837\u7684\u670b\u53cb\u8fd9\u70b9\u6211\u81f3\u5c11\u8fd8\u80fd \u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u4f60\u5f53\u7136\u4e5f\u8ba4\u5f97\u6797\u592a\u5e73\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u70b9\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\xb7\u9053\u201c\u5176\u5b9e\u8fd9\u53e5\u8bdd\u6211\u6839\u672c\u5c31\u4e2d\u8be5\u95ee\u7684\u4f60\u8fde\u4ed6\u7684\u670b\u53cb\u90fd\u5b8c\u5168\u8ba4\u5f97\u5f53\u7136\u4e5f\u8ddf\u4ed6\u5f88 \u719f\u4e86\u3002\u201d\u536b\u592b\u4eba\u5fae\u7b11\u9053\u201c\u7684\u786e\u53ef\u4ee5\u7b97\u5f88\u719f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u2606\u535c\u6b21\u4f60\u89c1\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u4e0d\u59a8\u66ff\u6211\u4eec\u95ee\u58f0\u597d\uff0c\u5c31\u8bf4\u6211\u4eec\u5f88\u60f3\u5ff5\u4ed6\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u6211\u4e5f\u5f88\u60f3\u89c1\u4ed6\u9762\u6240\u4ee5\u7279\u5730\u6765\u8bf7\u6559\u4f60\u4eec\u4e24\u4f4d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8bf7\u6559\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u6211\u60f3\u8bf7\u4e24\u4f4d\u544a\u8bc9\u6211\xb7\u4ed6\u8fd9\u4e24\u5929\u5728\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d\u71d5\u4e03\u597d\u50cf\u5f88\u60ca\u8bb6\u9053\u201c\u4f60\u963b\u4ed6\u6bd4 \u6211\u300a\u719f\u5f97\u591a\u600e\u9ebd\u4f1a\u4e0d\u77e5\u9053\u4ed6\u5728\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u65e0\u8bba\u591a\u719f\u7684\u670b\u53cb\u4e5f\u5e38\u5e38\u4f1a\u5f88\u4e45\u4e0d\u89c1\u9762\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u8fd8\u60f3\u8bf7\u4f60\u5e26\u6211\u851a\u53bb\u770b\u770b\u4ed6\u54e9\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u4eec\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u82e5\u8fde\u4f60\u90fd\u4e0d\u77e5\u9053\xb7\u6211\u4eec\u600e\u9ebd\u4f1a\u77e5\u9053\uff1f\u4ed6\u7684\u670b\u53cb\u6211\u4eec\u8fde\u4e2a\u90fd\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\u4f9b\u62f1\u624b\uff0c\u9053\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u4eec\u4e5f\u8be5\u544a\u8f9e\u4e01\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u6de1\u6de1\u7b11\u9053\u201c\u4e24\u6062\u8981\u8d70\u4e86\u9ebd\xb7\u4e0d\u9001\u4e0d\u9001\u3002\u201d \u3000\u3000\u5979\u5c45\u7136\u4e5f\u70b9\u963b\u62e6\u7684\u610f\u601d\u90fd\u6ca1\u6709\xb7\u5c31\u8fd9\u6837\u770b\u4ed6\u4eec\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u521a\u8d70\u51fa\u8fd9\u5ba2\u6808\u3001\u90ed\u5927\u8def\u5c31\u5fcd\u4e0d\u4f4f\u9053\u201c\u6211\u771f\u4f69\u670d\u4f60\u4f60\u771f\u6709\u4e00\u624b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54ea\u624b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u8d77\u5047\u8bdd\u6765\uff0c\u7b80\u76f4\u5c31\u8170\u771f\u7684\u5b8c\u5168\u6837\u3002\u201d \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u670d\u9053\u201c\u6211\u4e5f\u5f88\u4f69\u670d\u4f60\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4f69\u670d\u6211\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u73b2\u9053\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\u5012\u4e5f\u5f88\u5c11\u6709\u53ea\u8981\u89c1\u5230\u597d\u770b\u7684\u5973\u4eba\u4f60\u7acb\u523b\u5c31\u5c06\u4e2d\u8fb0\u516b\u5b57\u90fd\u5fd8\u4e86 \u7b80\u76f4\u6068\u4e0d\u5f97\u628a\u5bb6\u8c31\u90fd\u80cc\u51fa\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u9053\u201c\u90a3\u53ea\u56e0\u4e3a\u6211\u770b\u5979\u5e76\u4e0d\u8c61\u662f\u4e2a\u574f\u4eba\u561b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u7b11\u9053\u201c\u574f\u4eba\u80a1\u4e0a\u96be\u9053\u8fd8\u63a5\u62db\u724c\u9ebd\uff1f\u201d\uff01 \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u5979\u82e5\u771f\u7684\u6709\u6076\u610f\uff0c\u600e\u4f1a\u968f\u968f\u4fbf\u4fbf\u5c31\u8ba9\u6211\u4eec\u8d70\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u7b11\u9053\u201c\u4e0d\u8ba9\u6211\u4eec\u8d70\u4e49\u80fd\u600e\u9ebd\u6837\uff1f\u96be\u9053\u5979\u8fd8\u6709\u672c\u4e8b\u628a\u6211\u4eec\u7559\u535c\u6765\uff1f\u201d\u90ed\u5927\u8def\u56db\u5382\u53e3 \u6c14\u9053\u201c\u4f60\u82e5\u4ee5\u4e3a\u5979\u662f\u4e2a\u666e\u901a\u6587\u4eba\uff0c\u4f60\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u7684\u4e3e\u4e00\u52a8\xb7\u5979\u597d\u50cf\u90fd\u77e5\u9053\u5f97\u6e05\u6d88\u695a\u695a\u5c31\u51ed\u8fd9\u70b9\u6211\u5c31\u6562\u65ad\u5b9a\u5979\u7edd\u4e0d\u662f\u4e2a\u666e \u901a\u4ea5\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u77e5\u9053\u4e9b\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u5979\u77e5\u9053\u662f\u4ece\u5916\u5730\u6765\u7684\u77e5\u9053\u6211\u8eb2\u5728\u6811\u4e0a\u2026\u2026\u201d\u4ed6\u58f0 \u755c\u7a81\u7136\u505c\u4f4f\u6084\u6084\u9053\u201c\u4f60\u770b\u770b\u5f8c\u9762\u90a3\u836f\u5e97\u95e8\u53e3\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u7528\u4e0d\u770b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u5df2\u53d1\u73b0\u6709\u4eba\u5728\u76ef\u6211\u6790\u7684\u68a2\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u7b11\u70b9\u70b9\u5973\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u8f6c\u4eba\u6761\u6bd4\u8f83\u504f\u50fb\u7684\u8857\u9053\xb7\u8fd9\u6761\u8857\u6b20\u7684\u5e97\u671f\u5173\u95e8\u6bd4\u8f83\u65e9\u672c\u5df2\u6ca1\u4ec0\u9ebd\u4eba\u884c\u8d70\u3002 \u3000\u3000\u836f\u5e97\u4e5f\u65e9\u5c31\u6253\u8be6\u4e86\u5374\u6709\u4e2a\u8eab\u6750\u5f88\u77ee\u5c0f\u7684\u9ed1\u8863\u4eba\u7ad9\u5728\u95e8\u53e3\u914c\u6954\u5b50\u5f8c\u9762\xb7\u8fd8\u4e0d\u65f6\u4f38\u51fa\u534a\u8fb9\u8138 \u5411\u4ed6\u4eec\u50a8\u770b\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u4eba\u662f\u4e0d\u662f\u76f4\u5728\u5f8c\u9762\u8ddf\u6211\u4eec\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8d70\u51fa\u90a3\u5ba2\u6808\u2018\u6211\u5c31\u5df2\u53d1\u73b0\u4ed6\u4e86\u3002\u6240\u4ee5\u6211\u624d\u6545\u610f\u8f6c\u5230\u8fd9\u6761\u8857\u4e0a\u6765\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u63a5\u9053\u201c\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u90a3\u4f4d\u536b\u592b\u4eba\u4e3a\u4ec0\u9ebd\u968f\u968f\u4fbf\u4fbf\u5c31\u8ba9\u6211\u4eec\u4f01\u4e86\u5427\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u5979\u65e9\u5df3\u77e5\u9053\u6211\u4eec\u8ddf\u6797\u592a\u5e73\u4f4f\u5728\u8d77\u6240\u4ee5\u624d\u6545\u610f\u8ba9\u6211\u4eec\u8d70\u518d\u53eb\u4eba\u5728\u5916\u9762\u8ddf \u8e2a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\u9053\u201c\u5979\u7b97\u76d8\u6253\u5f97\u5012\u5c0f\u9519\uff0c\u53ea\u53ef\u501f\u672a\u514d\u5c06\u6211\u4eec\u4f30\u8ba1\u5f97\u592a\u4f4e\u4e86\u4e9b\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u5730\u901a\u201c\u96be\u9053\u4f60\u8fd8\u4ee5\u4e3a\u5979\u628a\u4f60\u770b\u5f97\u5f88\u4e86\u4e0d\u8d77\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u867d\u7136\u6ca1\u6709\u4ec0\u9ebd\u4e86\u4e0d\u8d77\uff0c\u4f46\u522b\u4eba\u8981\u60f3\u76ef\u6211\u7684\u68a2\u5012\u8fd8\u4e0d\u592a\u5bb9\u6613\u4e2d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u7b11\u9053\u201c\u60f3\u76ef\u6211\u68a2\u7684\u4eba\uff0c\u81f3\u5c11\u4e5f\u5f97\u5148\u559d\u559d\u897f\u5317\u98ce\u3002\u201d \u3000\u3000\u8857\u4e0a\u53ea\u6709\u5bb6\u5e97\u8fd8\u6ca1\u6709\u6253\u4f6f\u3002 \u3000\u3000\u65e0\u8bba\u54ea\u6761\u8857\u4e0a\u6253\u4f6f\u6700\u665a\u7684\xb7\u2026\u5b9a\u662f\u996d\u94fa\u9152\u9986\u3002 \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f24\u7b11\u9053\u201c\u6211\u770b\u4f60\u6050\u6015\u5e76\u4e0d\u662f\u60f3\u8bf7\u522b\u4eba\u559d\u897f\u5317\u98ce\xb7\u53ea\u4e0d\u8fc7\u662f\u81ea\u5df1\u60f3\u559d\u9152\u5382\u5427\u3002\u201d \u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u559d\u9152\xb7\u4ed6\u559d\u897f\u5317\u98ce\xb7\u53cd\u6b63\u5927\u5bb6\u90fd\u6709\u5f97\u559d\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u559d\u9152\u6709\u4e2a\u6bdb\u75c5\u3002 \u3000\u3000\u4e0d\u559d\u5f97\u70c2\u9189\u5982\u6ce5\xb7\u4ed6\u7edd\u4e0d\u8d70\u4e0d\u559d\u5f97\u56ca\u7a7a\u5982\u6d17\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u8d70\u3002 \u3000\u3000\u5927\u4e0b\u5047\u5982\u53ea\u6709\u4e2a\u4eba\u80fd\u6cbb\u4ed6\u8fd9\u79cd\u75c5\u90a3\u4eba\u5c31\u662f\u71d5\u4e03\u3002 \u3000\u3000\u91d1\u94fe\u5b50\u5f53\u5382\u4e94\u535c\u4e24\uff0c\u5206\u4e01\u4e2d\u7ed9\u738b\u52a8\u90ed\u5927\u8def\u8fd9\u6b21\u5c45\u7136\u6ca1\u6709\u5c06\u5269\u535c\u6765\u7684\u534a\u5b8c\u5168\u559d\u5149\u3002\u800c\u4e14\u4ed6 \u8d70\u51fa\u5c0f\u9152\u94fa\u7684\u65f6\u5019\u5c45\u7136\u8fd8\u76f8\u5f53\u6e05\u9192\u8fd8\u80fd\u770b\u5f97\u89c1\u4eba\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u679c\u7136\u8fd8\u5728\u90a3\u836f\u94fa\u95e8\u53e3\u7684\u67f1\u4e8e\u5f8c\u9762\u559d\u897f\u5317\u98ce\u3002 \u3000\u3000\u90ed\u5927\u8def\u5480\u4e86\u53e3\u6c14\u9053\u201c\u6211\u5e94\u8be5\u8ba9\u4ed6\u591a\u559d\u70b9\u7684\xb7\u4ed6\u597d\u50cf\u8fd8\u6ca1\u6709\u559d\u591f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f46\u4f60\u5374\u5df2\u559d\u591f\u3002\u518d\u6846\u4e0a\u53bb\u5c31\u8fde\u53c1\u5c81\u5c0f\u5b69\u90fd\u80fd\u76ef\u5f97\u4f50\u4f60\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u773c\u9053\u201c\u8c01\u8bf4\u7684\u6211\u5c31\u7b97\u7528\u6761\u817f\u8dd1\uff0c\u4ed6\u4e5f\u4f11\u60f3\u8ffd\u5f97\u4e0a\u6211\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u53ea\u76f8\u4fe1\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u54ea\u6837\u4e8b\uff1f\u201d \u3000\u3000\u4e24\u4e03\u9053\u201c\u5730\u5c31\u7b97\u80fd\u591f\u8feb\u5f97\u4e0a\u4f53\uff0c\u4f53\u4e5f\u53ef\u4ee5\u5c06\u4ed6\u6b27\u8d70\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u5439\u8d70\uff1f\u600e\u9ebd\u6837\u5439 \u6cd5\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u50cf\u4f60\u5439\u725b\u90a3\u6837\u6b27\u6cd5\u3002\u201d\u7ff1\u5927\u8def\u4ec0\u9ebd\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u5ffd\u7136\u6367\u8d77\u5382\u6761\u817f\u5f80\u524d\u9762\u2019\u8df3\u3002 \u3000\u3000\u8fd9\u8df3\u5c45\u7136\u8df3\u51fa\u4e86\u4e24\u4e08\u3002 \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u4ee5\u6c14\u63a2\u5934\u54fa\u54fa\u9053\u201c\u8fd9\u4eba\u4e3a\u4ec0\u9ebd\u603b\u8c61\u662f\u6c38\u8fdc\u90c1\u957f\u5c0f\u4eba\u7684\u3002\u201d \u3000\u3000\u5929\u662f\u9ed1\u7684\u8def\u662f\u767d\u7684\u3002 \u3000\u3000\u8def\u82f1\u5b9e\u5e76\u4e0d\u767d\u767d\u7684\u662f\u79ef\u6c14\u3002 \u3000\u3000\u7ff1\u5927\u8def\u770b\u770b\u8d2d\u65c1\u79ef\u8b66\u67aa\u6811\u98de\u822c\u5f80\u5f8c\u9762\u8dd1\u3002 \u3000\u3000\u6811\u5176\u5b9e\u5e76\u6ca1\u6709\u8dd1\xb7\u662f\u4ed6\u5728\u8dd1\uff0c\u7528\u4e24\u6761\u817f\u8dd1\u3002\u4ed6\u5e76\u4e0d\u662f\u4f2f\u7529\u4e2a\u8131\u5f8c\u9762\u90a3\u4e01\u68a2\u7684\u56db\u6b21\u4eba\u800c\u8db3 \u4f2f\u81ea\u8d76\u4e0d\u4e0a\u70ed\u4e03\u3002 \u3000\u3000\u70ed\u4ea1\u65bd\u5c55\u8d75\u8f7b\u529f\u7684\u65f6\u5019\u771f\u8c61\u662f\u53d8\u6210\u4e86\u53ea\u71d5\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u5f00\u59cb\u5728\u5598\u6c14\u3002 \u3000\u3000\u853d\u4e03\u8fd9\u624d\u6e10\u6e10\u6162\u5382\u4e0b\u6765\u7528\u773c\u89d2\u8198\u4ed6\u7b11\u9053\u201c\u4f60\u4e0d\u884c\u4e86\u9a6c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53cd\u6c0f\u5410\u6bd4\u95e8\u6c14\u9f7f\u7b11\u9053\u201c\u6211\u5403\u7684\u6bd4\u4f60\u591a\u5757\u5934\u6bd4\u4f60\u5927\u5f53\u7136\u5730\u4e0d\u8fc7\u4f60\u201c \u3000\u3000\u853d\u4e03\u5403\u8d77\u519c\u4e5f\u5f88\u51f6\u5757\u5934\u4e5f\u5f88\u5927\u4f46\u8dd1\u8d77\u6765\u8fd8\u662f\u5feb\u5f97\u5f88\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u6211\u4e0d\u662f\u94a7\u6211\u53ea\u6709\u4e24\u6761\u817f\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u4f60\u4e0d\u8db3\u8bf4\u5c31\u7b97\u7528\u6761\u817f\u8dd1\u522b\u4eba\u4e5f\u4f11\u60f3\u8ffd\u5f97\u4e0a\u4f60 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u4f60\u3002\u201d\u71d5\u4e03\u76ee\u5149\u95ea\u52a8\u9053\u201c\u4f60\u4ee5\u4e3a\u522b\u4eba\u5c31\u4e0d\u884c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u988a\u4e03\u5ffd\u7136\u56db\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\u5934\u53bb\u770b\u770b\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u56de\u5934\u5c31\u6014\u4f4d\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u8def\u535c\u6709\u4e2a\u4eba\u3002\u201d \u3000\u3000\u8def\u662f\u767d\u7684\u4eba\u662f\u9ed1\u7684\u3002 \u3000\u3000\u521a\u5201\u8eb2\u5728\u836f\u724c\u95e8\u53e3\u67e0\u4e2d\u5f8c\u9762\u7684\u90a3\u9ed1\u8863\u4eba\u89c2\u4e2d\u5c45\u7136\u53c8\u8feb\u5230\u8fd9\u91cc\u6765\u4e01\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u6014\u5382\u534a\u664c\u9053\u201c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u65bc\u5c45\u7136\u4e5f\u8dd1\u5f97\u5f88\u5feb\u3002\u201d\u71d5\u4e03\u9053\u6a21\u8bf4\u4f60\u53ea\u6709\u6761\u817f\u770b\u6765\u5c31\u7b97 \u7528\u53c1\u6761\u817f\u8dd1\u4ed6\u4e5f\u7167\u6837\u80fd\u8ffd\u5f97\u4e0a\u4f60\u4f60\u4fe1\u4e0d\u500d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u50a8\u3002\u201d \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u65e5\u4e2d\u5145\u6ee1\u4e86\u7b11\u610f\u3002 \u3000\u3000\u7684\u786e\u4ed6\u662f\u4e2d\u5f88\u53ef\u7231\u7684\u4eba\u5458\u53ef\u7231\u5730\u65b9\u5c31\u662f\u4ed6\u80af\u627f\u8ba4\u81ea\u5df1\u7684\u6bdb\u75c5\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u65e0\u8bba\u6709\u591a\u5c11\u6bdb\u75c5\xb7\u90fd\u8fd8\u662f\u4e2a\u5f88\u53ef\u7231\u7684\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u65e2\u7136\u7529\u4e0d\u6389\u4ed6\u5c31\u4e0d\u80fd\u56de\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5a25\u4e03\u9053\u201c\u4e0d\u56de\u53bb\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6ca1\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u4ed6\u7728\u5382\u7728\u773c\u5ffd\u53c8\u7b11\u9053\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u4f60\u81ea\u5df1\u521a\u8bf4\u7684\u4ec0\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u8bf4\u4e01\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u4ed6\u5c31\u771f\u80fd\u8fe2\u4e0a\u6211\u6211\u4e5f\u53ef\u4ee5\u628a\u4ed6\u5439\u8d70\u3002\u201d \u3000\u3000\u82d1\u4e03\u7b11\u9053\u201c\u4f60\u771f\u6709\u8fd9\u9ebd\u5927\u7684\u672c\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u7728\u4e86\u7728\u773c\xb7\u9053\u201c\u4f60\u60f3\u7528\u4ec0\u9ebd\u5439\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u4f63\u62f3\u5934\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\u5411\u90a3\u9ed1\u8863\u4eba\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u519c\u4eba\u7ad9\u5728\u8def\u4e2d\u592e\u3001\u770b\u4ed6\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u65bc\u5012\u6c89\u5f97\u4f4f\u6c14\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u6c89\u4f50\u4e86\u6c14\u6162\u9992\u7684\u8d70\u8fc7\u53bb\u5fc3\u91cc\u6b63\u5728\u76d8\u7b97\u662f\u5141\u7eab\u5634\u5df4\u8fd8\u662f\u5148\u52a8\u62f3\u5934\uff1f \u3000\u3000\u8c01\u77e5\u90a3\u9ed1\u8863\u4eba\u5ffd\u7136\u6c89\u4e0d\u4f4f\u6c14\u4e86\u62db\u5934\u5c31\u8dd1\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7acb\u523b\u6c89\u4e0d\u4f4f\u6c14\u5382\u3002\u62d4\u811a\u5c31\u9000\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u9ed1\u8863\u4eba\u7684\u8f7b\u529f\u7edd\u4e0d\u5728\u7897\u4e03\u4e4b\u4e0b\u4ed6\u5c31\u7b97\u6c0f\u2606\u6761\u817f\u4e5f\u9000\u4e0d\u53ea\u6709\u5927\u53eb\u9053\u201c\u670b\u53cb\u4f60 \u7b49\u7b49\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5ffd\u7136\u56de\u5934\u7b11\u4e86\u7b11\u9053\u201c\u4e0d\u9519\u6211\u804b\u5f97\u5f88\u5389\u5bb3\u5f25\u8bf4\u7684\u8bdd\u6211\u8fde\u4e2a\u5b57\u90fd\u542c\u4e0d\u89c1\u3002\u201d \u3000\u3000\u4ed6\u597d\u50cf\u5b58\u5fc3\u8981\u6c14\u2019\u90ed\u5927\u8def\u3002 \u3000\u3000\u65e0\u8bba\u96be\u5b58\u5fc3\u8981\u8ba9\u7ff1\u5927\u8def\u751f\u6c14\u90fd\u5f88\u5bb9\u6613\u4ed6\u672c\u6765\u5c31\u5bb9\u6613\u751f\u6c14\u3002 \u3000\u3000\u751f\u6c14\u5c31\u975e\u8ffd\u5e7f\u4e0d\u53ef\u3002 \u3000\u3000\u672c\u6765\u662f\u8fd9\u9ed1\u591c\u4eba\u5728\u76ef\u4ed6\u7684\u68a2\u73b0\u5728\u53cd\u800c\u4ed6\u5728\u76ef\u8fd9\u9ed1\u8863\u4eba\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u4e5f\u53ea\u6709\u9677\u4ed6\u8ffd\u3002 \u3000\u3000\u8def\u65c1\u6709\u7247\u79ef\u96ea\u7684\u67af\u6797\xb7\u67af\u6797\u91cc\u5c45\u7136\u8fd8\u6709\u706f\u5149\u3002 \u3000\u3000\u9ed1\u591c\u4eba\u8eab\u5f62\u5728\u6811\u6797\u91cc\u95ea\xb7\u5ffd\u7136\u4e0d\u89c1\u5382\u3002 \u3000\u3000\u706f\u5149\u8fd8\u4eae\u3002 \u3000\u3000\u706f\u5149\u662f\u4ece\u680b\u5c4b\u5b50\u91cc\u7167\u51fa\u6765\u7684\u9ed1\u8863\u4eba\u60f3\u5fc5\u5df2\u8fdb\u5165\u4e86\u8fd9\u5c4b\u5b50\u3002 \u3000\u3000\u7ff1\u5927\u8def\u54ac\u7259\u6068\u6839\u9053\u201c\u4f60\u5728\u5916\u9762\u7b49\uff0c\u6211\u8fdb\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u8bf4\u8bdd\u4e5f\u6ca1\u6709\u62c9\u4f0e\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e5\u662f\u771f\u7684\u60f3\u505a\u2018\u4ef6\u4e8b\uff0c\u90a3\u5c31\u6839\u672c\u6ca1\u6709\u4eba\u80fd\u62c9\u5f97\u4f4f\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u8981\u53bb\u8df3\u6cb3\u71d5\u4e03\u4e5f\u53ea\u6709\u966a\u4ed6\u8df3\u3002 \u3000\u3000\u4eae\u706f\u7684\u90a3\u95f4\u5c4b\u5b50\u95e8\u5c45\u7136\u662f\u5f00\u7684\u706f\u5149\u4ece\u95e8\u91cc\u7167\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u51b2\u8fc7\u53bb\u521a\u51b2\u5230\u4e86\u66f0\u53c8\u6014\u4f4d\u3002 \u3000\u3000\u5c4b\u4e8e\u91cc\u751f\u76c6\u706b\u706b\u76c6\u65c1\u5750\u4e2a\u4eba\u3002 \u3000\u3000\u706b\u70e7\u5f97\u72e0\u77aa\u4eba\u957f\u8925\u771f\u7f8e\u3002 \u3000\u3000\u536b\u592b\u4eba\u3002 \u3000\u3000\u5979\u770b\u5230\u90ed\u5927\u8def\u8fde\u70b9\u60ca\u5947\u7684\u6837\u4e8e\u90fd\u6ca1\u6709\u5fae\u7b11\xb7\u9053\u201c\u5916\u9762\u5b9a\u5f88\u51b7\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u8fdb\u6765\u70e4\u59cb \u706b\uff1f\u201d \u3000\u3000\u5979\u597d\u50cf\u76f4\u5728\u7b49\u4ed6\u8ba2\u4f3c\u7684\u3002 \u3000\u3000\u56db \u3000\u3000\u9664\u5382\u5979\u4e4b\u5916\u5c4b\u5b50\u91cc\u8fd8\u6709\u4e2a\u4eba\u3002 \u3000\u3000\u4e2a\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u89c1\u8fd9\u9ed1\u519c\u4eba\xb7\u706b\u6c14\u53c8\u4e0a\u6765\u4e86\u9080\u4e0d\u4f4f\u51b2\u4e86\u8fc7\u53bb\xb7\u5927\u58f0\u53eb\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u2018\u76f4\u4efb\u5f8c\u9762 \u76ef\u6211\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7728\u4e86\u7728\u773c\uff0c\u9053\u201c\u662f\u6211\u5728\u76ef\u4f60\uff1f\u8fd8\u662f\u4f60\u5728\u76ef\u6211\uff1f\u201d \u3000\u3000\u4ed6\u7684\u773c\u775b\u5c45\u7136\u5f88\u4eae\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u662f\u4f60\u5728\u76ef\u6211\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u90a3\u9ebd\u627e\u5409\u8bc9\u4f60\u8fd9\u662f\u6211\u7684\u5bb6\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u7684\u5bb6\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\uff0c\u201c\u82e5\u662f\u6211\u5728\u76ef\u4f60\uff0c\u600e\u9ebd\u4f1a\u76ef\u5230\u6211\u81ea\u5df1\u7684\u5bb6\u91cc\u6765\u4e01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u9ed1\u8863\u4eba\u4e0d\u4f46\u773c\u775b\u72e0\u4eae\xb7\u800c\u4e14\u7b11\u5f97\u4e5f\u5f88\u751c\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u4eba\u539f\u6765\u662f\u4e2a\u7a7f\u9ed1\u8863\u670d\u7684\u5973\u4eba\xb7\u800c\u4e14\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u535c\u516d\u4e03\u5c81\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u7b97\u6709\u5f88\u591a\u9053\u7406\uff0c\u4e5f\u5168\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002\u536b\u592b\u4eba\u7b11\u9053\u201c\u4e24\u4f4d\u65e2\u7136\u6765\u201c\u8bf7\u5750\u8bf7\u5750\u3002\u201d \u3000\u3000\u706b\u76c6\u65c1\u8fd8\u6709\u4e24\u5f20\u6905\u5b50\u3002 \u3000\u3000\u71d5\u4e03\u5750\u4e0b\u6765\xb7\u5ffd\u7136\u7b11\u9053\u201c\u4f60\u597d\u50cf\u65e9\u5c31\u77e5\u9053\u6211\u4eec\u8981\u6765\u65e9\u5c31\u5728\u7b49\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u5fae\u7b11\u9053\u201c\u4f60\u4eec\u8981\u8d70\u6211\u62c9\u4e0d\u4f4f\u4f60\u4eec\u8981\u6765\xb7\u6211\u4e5f\u6321\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4efd\u73b0\u5728\u82e5\u53c8\u8981\u8d70\u5382\u5462\uff1f\u201d\u536b\u592b\u4eba\u9053\u201c\u6211\u8fd8\u662f\u53ea\u6709\u53e5\u8bdd\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u8bdd\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4e0d\u9001\u4e0d\u9001\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f97\u4f60\u8fd8\u662f\u4f1a\u8981\u8fd9\u4f4d\u5c0f\u59b9\u59b9\u5728\u5f8c\u9762\u76ef\u6211\u8ba2\u7684\u68a2\u3002\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u77aa\u773c\u9053\u201c\u8c01\u8981\u76ef\u4f60\u4eec\u7684\u68a2\u90a3\u6761\u8def\u4f60\u4eec\u80fd\u8d70\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u8d70\uff1f\u4f60\u4eec\u968f\u968f\u4fbf\u4fbf\u5c31\u53ef \u4ee5\u5f80\u6211\u5bb6\u91cc\u95ef\u6211\u96be\u9053\u5c31\u4e0d\u80fd\u8ddf\u4f60\u4eec\u8d70\u6761\u8def\uff1f\u201d\u71d5\u4e03\u51b7\u7b11\u9053\u201c\u539f\u6765\u4f60\u53ea\u4e0d\u5b88\u51d1\u5de7\u6bbf\u6211\u4eec\u540c \u8def\u3002\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5973\u9053\u201c\u70b9\u4e5f\u6c34\u9519\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u5012\u771f\u7684\u5f88\u5de7\u201d \u3000\u3000\u536b\u592b\u4eba\u6de1\u8c08\u7b11\u9053\u201c\u7b49\u4f60\u5e74\u7eaa\u518d\u5927\u4e9b\u65f6\xb7\u5c31\u4f1a\u53d1\u73b0\u5929\u5c38\u51d1\u9a79\u7684\u4e8b\u672c\u6765\u5c31\u5f88\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u9ebd\u6837\u770b\u6765\u4f60\u5df3\u6253\u5b9a\u4e3b\u610f\u8981\u4ece\u6211\u4eec\u8eab\u4e0a\u627e\u5230\u6797\u4eba \u3000\u3000\u536b\u592b\u4eba\u7b11\u9053\u201c\u90a3\u5c31\u5f97\u770b\u4f60\u4eec\u662f\u4e0d\u662f\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u906d\u201c\u6211\u4eec\u82e5\u662f\u94e1\u9053\u5462\uff1f\u201d \u3000\u3000\u53ca\u592b\u4eba\u5fae\u7b11\u9053\u201c\u53ea\u8981\u4f60\u4eec\u77e5\u9053\u6211\u8fdf\u65e9\u4e5f\u4f1a\u77e5\u9053\u7684\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u5411\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u4e2a\u4eba \u7684\u817f\u82e5\u662f\u88ab\u7ef3\u5b50\u6346\u4f0e\u8fd8\u80fd\u4e2d\u80fd\u76ef\u68a2\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u50cf\u4e0d\u80fd\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u7b54\u5bf9\u5382\u3002\u201d \u3000\u3000\u4ed6\u8896\u4e2d\u5ffd\u7136\u591a\u51fa\u6761\u7ef3\u5b50\uff0c\u5411\u9ed1\u8863\u5c11\u5973\u7684\u817f\u4e0a\u7f20\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u6761\u7ef3\u5b50\u5c31\u8c61\u86c7\u6837\u4e49\u4fa0\u4e49\u51c6\u800c\u53ea\u8fd8\u597d\u50cf\u957f\u773c\u775b\u4f3c\u7684\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u7ef3\u5b50\u51fa\u624b\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u8eb2\u5f97\u5f00\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5973\u6839\u672c\u6ca1\u6709\u8eb2\xb7\u56e0\u4e3a\u7ef3\u4e86\u5df2\u5230\u4e86\u536b\u592b\u4eba\u7684\u624b\u91cc\u3002 \u3000\u3000\u5979\u7684\u624b\u6162\u9992\u7684\u4f38\u4e86\u51fa\u6765\u7ef3\u5b50\u7684\u53bb\u52bf\u867d\u5f88\u5feb\u4f46\u4e0d\u77e5\u4e3a\u4e86\u4ecd\u9ebd\u7ef3\u5b50\u5374\u5df2\u5230\u4e86\u5979\u624b\u91cc\u3002 \u3000\u3000\u71d5\u4e03\u7684\u8138\u8272\u53d8\u4e86\xb7\u53ea\u6709\u4ed6\u529b\u77e5\u9053\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\u4ed6\u53ea\u89c9\u7ef3\u5b50\u535c \u3000\u3000\u4ed6\u4ece\u6765\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u771f\u6709\u8fd9\u9ebd\u53ef\u6015\u7684\u5185\u529f\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u76f8\u4fe1\u4e86\u3002 \u3000\u3000\u536b\u592b\u4eba\u5fbd\u7b11\u9053\u201c\u5176\u5b9e\u4f60\u5c31\u7b97\u771f\u5c06\u5979\u4e24\u6761\u817f\u90fd\u6346\u8d77\u6765\xb7\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u9ed8\u4e86\u534a\u80f8\u957f\u957f\u53f9\u4e86\u66f0\u6c14\u9053\u201c\u7684\u786e\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u81f3\u5c11\u5e94\u8be5\u5148\u6346\u4e0a\u6211\u7684\u817f\u3002\u201d\u71d5\u4e03\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u7b11\u9053\u201c\u4f46\u6211\u53ef\u4e5f\u4fdd\u8bc1\u4e16\u4e0a\u7edd\u6ca1\u6709\u2026\u4e2a\u4eba\u80fd\u6346\u4f50\u6211\u7684\u817f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u7edd\u5bf9\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4f46\xb7\u6211\u4e5f\u53ef\u4ee5\u5411\u4f60\u4fdd\u8bc1\u4ef6\u4e8b\u3002\u201d\u536b\u592b\u4eba\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u867d\u7136\u6346\u4e0d\u4f4f\u4f60\u4eec\u7684\u817f\u90e8\u53ef\u4ee5\u62e5\u4f4f\u53e6\u5916\u4e2a\u4eba\u7684\u817f\u627e\u53ea\u8981\u62e5\u4f4f\u8fd9\u4eba\u7684\u817f\u4f60\u4eec\u5c31\u7b97 \u6709\u5927\u5927\u7684\u672c\u4e8b\u4e5f\u4f11\u60f3\u8ffd\u51fa\u6797\u592a\u5e73\u7684\u4e0b\u843d\u201c\u536b\u592b\u4eba\u7b11\u9053\u201c\u4f60\u65ec\u7b97\u6346\u4efb\u7ee7\u7684\u817f\u5462\u201d\u71d5\u4e03\u9053\u201c\u6211\u81ea \u7684\u3002\u201d \u3000\u3000\u65e0\u8bba\u591a\u6ca1\u7528\u7684\u4eba\u4e2d\u5c11\u90fd\u80fd\u5c06\u81ea\u5df1\u7684\u817f\u6346\u4efb\u8fd9\u4e5f\u662f\u4ef6\u6beb\u65e0\u7591\u95ee\u7684\u4e8b\u3002 \u3000\u3000\u71d5\u4e03\u62e5\u4efb\u4e86\u81ea\u5df2\u7684\u817f\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7684\u7ef3\u5b50\u8fd8\u771f\u4e0d\u5c11\u3002\u4ed6\u597d\u50cf\u5f88\u559c\u6b22\u7528\u7ef3\u5b50\u4f5c\u6b66\u5668\u3002 \u3000\u3000\u536b\u592b\u4eba\u4e5f\u6014\u4f4f\u5382\uff0c\u6014\u4e86\u534a\u624d\u5c55\u989c\u7b11\u9053\u201c\u4e0d\u9519\u8fd9\u5012\u7684\u786e\u662f\u4e2a\u597d\u4ea4\u6020\uff0c\u8fde\u6211\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9 \u662f\u4e2a\u597d\u4e3b\u610f\u3002\u201d\u71d5\u4e03\u9053\u201c\u8fc7\u5956\u8fc7\u5956\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u82e5\u5c06\u65e5\u5df1\u6346\u5728\u8fd9\u91cc\u6211\u7684\u786e\u6ca1\u6cd5\u5b50\u9000\u51fa\u6797\u592a\u5e73\u7684\u4e0b\u843d\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u7528\u4e0d\u6346\u81ea\u5df1\u7684\u817f\u4ed6\u7684\u817f\u5c31\u8ddf\u6211\u7684\u817f\u6837\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u8fd9\u9ebd\u6837\u770b\u6765\u4f60\u4e5f\u51b3\u5fc3\u4e0d\u8d70\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u6211\u672c\u6765\u4e5f\u5df2\u51c6\u5907\u5c06\u4f60\u8861\u7528\u7ef3\u5b50\u6346\u8d77\u6765\u901a\u4f60\u4eec\u8bf4\u51fa\u6797\u592a\u5e73\u7684\u535c\u843d\u4f60\u4eec\u4e0d\u8bf4\u5c31\u4e0d \u653e\u4f60\u4eec\u8d70\u7684\u3002\u201d \u3000\u3000\u5979\u5c45\u7136\u4e5f\u53f9\u5382\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u8c01\u77e5\u4f9d\u4eec\u7adf\u81ea\u5df1\u6346\u8d77\u5382\u81ea\u5df1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u5c31\u53eb\u5148\u4e0b\u624b\u4e3a\u5f3a\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u53ea\u53ef\u60dc\u5f8c\u4e0b\u624b\u7684\u4e5f\u672a\u5fc5\u906d\u6b83\u7530\u6b83\u7684\u4e5f\u8fd8\u662f\u4f60\u4eec\u81ea \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u4eec\u603b\u4e0d\u80fd\u5728\u8fd9\u91cc\u5446\u8f88\u5b50\u5427\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u90a3\u5012\u4e5f\u8bf4\u4e0d\u8d70\u3002\u201d \u3000\u3000\u4ed6\u56db\u5468\u770b\u5382\u770b\u53c8\u7b11\u9053\u201c\u8fd9\u91cc\u53c8\u6696\u548c\u53c8\u8212\u670d\u81f3\u5c11\u6bd4\u6211\u4eec\u4efb\u7684\u90a3\u7834\u5c4b\u4e8e\u8212\u670d\u591a\u4e86\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u76ee\u5149\u95ea\u52a8\u9053\uff1a\u201c\u79f0\u4eec\u4efb\u7684\u662f\u4e2a\u7834\u5c4b\u4e8e\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u7528\u4e0d\u63a2\u53e3\u98ce\u5929\u5382\u7684\u7834\u5c5e\u65bc\u5f88\u591a\u4f60\u82e5\u60f3\u95f4\u95ee\u7684\u53bb\u627e\xb7\u627e\u5230\u4f60\u8fdb\u68fa\u6750\u91cc\u4e5f\u627e\u4e0d \u5bbd\u7684\u3002\u2606 \u3000\u3000\u536b\u592b\u4eba\u53c8\u53f9\u4e86\u53e3\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u6709\u70b9\u5947\u602a\u800c\u5df1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u5947\u602a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u6797\u592a\u5e73\u4ece\u5c0f\u5c31\u5a07\u751f\u60ef\u517b\uff0c\u600e\u9ebd\u4f1a\u5728\u4e00\u95f4\u7834\u5c4b\u5b50\u8fc7\u5f97\u535c\u53bb\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u90a3\u7834\u5c4b\u4e8e\u91cc\u6709\u6837\u4e1c\u897f\u662f\u522b\u7684\u5730\u65b9\u627e\u4e0d\u5230 \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u4eec\u90a3\u91cc\u6709\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u670b\u53cb\u3002\u201d \u3000\u3000\u53ea\u8981\u6709\u670b\u53cb\u518d\u7a77\u4e24\u7834\u7684\u5c4b\u4e8e\u90fd\u6ca1\u5173\u7cfb\u3002 \u3000\u3000\u56e0\u4e3a\u53ea\u8981\u6709\u670b\u53cb\u7684\u5730\u65b9\xb7\u5c31\u6709\u6e29\u77aa\u5c31\u6709\u5feb\u4e50\u3002 \u3000\u3000\u6ca1\u6709\u670b\u53cb\u7684\u5730\u65b9\u5c31\u7b97\u904d\u5730\u90fd\u5806\u6ee1\u4e86\u9ec4\u91d1\u5728\u4ed6\u4eec\u670d\u4e2d\u770b\u6765\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5750\u7528\u9ec4\u91d1\u5efa\u6210\u7684\u7262 \u72f1\u3002 \u3000\u3000\u53e3\u592b\u4eba\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u53c8\u8f7b\u8f7b\u53f9\u606f\u4e86\u58f0\u9053\u201c\u770b\u6765\u4f60\u4eec\u867d\u7136\u884c\u70b9\u513f\u5947\u602a\u5012\u90fd\u662f\u5f88\u591f\u670b\u53cb\u7684 \u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u81f3\u5c11\u603b\u4e0d\u4f1a\u51fa\u5356\u670b\u53cb\u3002\u201d\u536b\u592b\u4eba\u95ee\u9053\u201c\u65e0\u8bba\u7b49\u5230\u4ec0\u9ebd\u65f6\u5019\xb7\u90fd\u4e0d\u4f1a\u51fa\u5356 \u670b\u53cb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u592b\u4eba\u53c8\u7b11\u4e86\u60a0\u7136\u9053\u201c\u597d\u6211\u5012\u8981\u770b\u770b\u4f60\u4eec\u80fd\u7b49\u5230\u51e0\u65f6\uff1f\u201d \u3000\u3000\u9ed1\u6697\u7684\u5730\u72f1 \u3000\u3000\u5929\u4eae\u5382\u3002 \u3000\u3000\u684c\u4e0a\u6446\u6ee1\u4e86\u5f88\u591a\u70b9\u5fc3\u6bcf\u79cd\u90fd\u5f88\u597d\u5403\u3002 \u3000\u3000\u5403\u4e0d\u4f46\u662f\u79cd\u4eab\u53d7\u4e5f\u662f\u79cd\u827a\u672f\u3002 \u3000\u3000\u5de5\u72ac\u4eba\u5f88\u61c2\u5f97\u8fd9\u79cd\u4eab\u53d7\uff0c\u4e5f\u5f88\u61c2\u5f97\u8fd9\u79cd\u827a\u672f\u3002 \u3000\u3000\u5979\u5403\u5f97\u5f88\u6162\u4e5f\u5486\u5f97\u5f88\u7f8e\u3002 \u3000\u3000\u65e0\u8bba\u5979\u5728\u5403\u4ec0\u9ebd\u7684\u65f6\u5019\xb7\u90fd\u4f1a\u4ee4\u4eba\u89c9\u5f97\u5979\u5403\u7684\u4e1c\u897f\u975e\u5e38\u7f8e\u5473\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u4e9b\u70b9\u5fc3\u6c34\u6765\u5c31\u5168\u90fd\u662f\u7f8e\u5473\u3002 \u3000\u3000\u5403\u6765\u662f\u7f8e\u5473\uff0c\u55c5\u8d77\u6765\u4e5f\u5b9a\u5f88\u9999\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u5fcd\u4e0d\u4f4f\u5f00\u59cb\u5728\u6084\u6084\u7684\u54bd\u53e3\u6c34\u3002\u9152\u610f\u6765\xb7\u809a\u5b50\u5c31\u597d\u50cf\u997f\u5f97\u7279\u522b\u5feb \u3000\u3000\u997f\u809a\u5b50\u770b\u522b\u4eba\u5927\u5403\u5927\u559d\u8fd9\u79cd\u6ecb\u5473\u6709\u65f6\u7b80\u76f4\u6bd4\u4ec0\u9ebd\u5211\u7f5a\u90fd\u96be \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u5927\u58f0\u9053\u201c\u4e3b\u4eba\u72ec\u4e2a\u513f\u5927\u5403\u5927\u559d\u5374\u8ba9\u5ba2\u4eba\u997f\u5b50\u5728\u65c1\u8fb9\u770b\u8fd9\u597d\u50cf\u4e0d\u662f\u5f85\u5ba2\u4e4b \u9053\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u70b9\u70b9\u5934\u9053\u201c\u8fd9\u7684\u786e\u4e0d\u662f\u5f85\u5ba2\u4e4b\u9053\xb7\u4f46\u4f60\u4eec\u662f\u6211\u7684\u5ba2\u4eba \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u53f9\u606f\u82e6\u7b11\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u4eec\u60f3\u4e0d\u613f\u505a\u6211\u7684\u5ba2\u4eba\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8ddf\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u4e3a\u4e86\u6797\u592a\u5e73\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u957f\u957f\u53f9\u5382\u53e3\u6c14\u9053\u201c\u8c01\u53eb\u4ed6\u662f\u6211\u4f55\u7684\u670b\u53cb\u5462\u3002\u201d\u536b\u592b\u4eba\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4f60\u4eec\u867d\u7136\u5f88 \u591f\u670b\u53cb\uff0c\u5374\u4e5f\u591f\u7b28\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\uff01\u592b\u4eba\u9053\u201c\u76f4\u5230\u73b0\u5728\u4f60\u4eec\u8fd8\u6ca1\u6709\u95ee\u6211\u4e3a\u4ec0\u9ebd\u8981\u627e\u6797\u592a\u5e73\u3002\u201d\u6b21\u4e50\u82f1\u6444 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u6839\u672c\u4e0d\u5fc5\u95ee\u3002\u201d \u3000\u3000\u98de\u592b\u4eba\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u5fc5\u95ee\uff1f\u4ecd\u4eec\u600e\u77e5\u9053\u6211\u627e\u4ed6\u662f\u597d\u610f\u8fd8\u662f\u6076\u610f\uff1f\u4e5f\u8bb8\u6211\u627e\u4ed6\u53ea\u6c34\u8fc7\u662f\u4e3a \u4e86\u8981\u9001\u70b9\u4e1c\u897f\u7ed9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u77e5\u9053\u4ef6\u4e8b\u4ed6\u82e5\u4e0d\u60f3\u89c1\u4f60\u6211\u4eec\u5c31\u4e0d\u80fd\u8ba9\u4f60\u627e\u5230\u4ed6\u65e0\u8bba\u4f60\u662f\u597d\u610f\u8fd8\u662f\u6076 \u610f\xb7\u90fd\u662f\u6837\u7684\u3002\u201d \u3000\u3000\u592b\u4eba\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u4ed6\u4e0d\u613f\u89c1\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4f60\u627e\u4ed6\u627e\u5f97\u592a\u6025\u6d88\u826f\u8c61\u4e0d\u6000\u597d\u610f\u7684\u6837\u5b50\u5426\u5219\u4f60\u5c31\u8be5\u8ba9\u6211\u8ba2\u95ee\u53bb\u53f0\u8bc9 \u4ed6\xb7\u518d\u53eb\u4ed6\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u536b\u5927\u4eba\u7b11\u9053\u201c\u770b\u6765\u4f60\u4eec\u8fd8\u5c0f\u592a\u8000\u53ea\u4e0d\u8fc7\u6709\u4e00\u70b9\u7b28\u800c\u5df2\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u300f\u592b\u4eba\u9053\u201c\u4f60\u4eec\u5c31\u7b97\u6015\u6211\u4e5f\u6697\u7837\u8ffd\u8e2a\u4e0d\u95ee\u53bb\u4e5f\u5c31\u662f\u5382\u8fd8\u8db3\u53ef\u4ee5\u5230\u522b\u7684\u5730\u65b9\u53bb\u7684\u4e49\u4f97\u5fc5\u81ea \u5df1\u628a\u81ea\u5df1\u6346\u5728\u8fd9\u91cc\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u770b\u770b\u71d5\u4e03\u9053\u201c\u5979\u8bf4\u7684\u8bdd\u597d\u50cf\u6709\u70b9\u9053\u7406\u6211\u4eec\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u8d70\u5462\uff1f\u201d \u3000\u3000\u95e8\u592b\u4eba\u9053\u201c\u9602\u4e3a\u6211\u73b0\u5728\u5df4\u4e2d\u8ba9\u4f60\u8861\u8d70\u4e01\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u81ea\u5df1\u8bf4\u8fc7\u6211\u4eec\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70 \u7684\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u6211\u73b0\u5728\u5df3\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\xb7\u63a5\u9053\u201c\u4f60\u77e5\u9053\u5973\u4eba\u603b\u662f\u968f\u65f6\u90fd\u4f1a\u6539\u53d8\u4e3b\u610f\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u4f60\u770b\u4e0d\u662f\u5973\u4eba\u5c31\u597d\u4e86\u3002\u201d\u536b\u707e\u4eba\u9053\u201c\u6709\u4ec0\u9ebd\u597d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u76ef\u5979\u9762\u524d\u7684\u70e7\u5356\u548c\u84b8\u997a\uff0c\u9053\u201c\u4f60\u82e5\u662f\u7537\u4eba\xb7\u6211\u81f3\u5c11\u53ef\u4ee5\u539a\u8138\u76ae\u67aa\u4f60\u7684\u4e1c\u897f\u5403\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u5fae\u7b11\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u628a\u6211\u5f53\u505a\u7537\u4eba\u6765\u8bd5\u8bd5\u770b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u71d5\u4e03\u71d5\u4e03\u7728\u4e86\u7728\u773c\u3002 \u3000\u3000\u536b\u592b\u4eba\u53c8\u9053\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\u4e0d\u59a8\u8d77\u8fc7\u6765\u67aa\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\u9053\u201c\u6211\u7684\u8138\u76ae\u6ca1\u6709\u4ed6\u539a\u8fd8\u662f\u8ba9\u4ed6\u4e2a\u4eba\u52a8\u624b\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u963b\u4e86\u53e3\u6c14\u901a\u201c\u2018\u4e2a\u4eba\u997f\u5f97\u8981\u547d\u7684\u65f6\u5019\u8138\u76ae\u60f3\u4e0d\u539a\u4e9b\u4e5f\u4e0d\u884c\u5382\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u707e\u7136\u63a0\u8d77\u5411\u90a3\u5f20\u6446\u6ee1\u4e86\u70b9\u5fc3\u7684\u684c\u5b50\u6251\u4e86\u8fc7\u53bb\u3002\u5341\u6307\u7b95\u5f20\u5f2f\u66f2\u5982\u9e70\u722a\u7528\u7684\u5c45\u7136\u662f\u9e70 \u722a\u529f\u4e2d\u62db\u6781\u5389\u5bb3\u7684\u201c\u98de\u9e70\u640f\u514d\u201d\u3002 \u3000\u3000\u7528\u201c\u9002\u9e70\u613f\u5154\u201d\u8fd9\u79cd\u62db\u5f0f\u6765\u62a2\u84b8\u997a\u672a\u514d\u662f\u4ef6\u5f88\u53ef\u7b11\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u2019\u4e2a\u4eba\u82e5\u662f\u997f\u6781\u4e86\u518d\u53ef\u7b11\u7684\u4e8b\u4e5f\u6837\u80fd\u505a\u5f97\u51fa\u6765\u7684\u3002 \u3000\u3000\u536b\u592b\u4eba\u7b11\u9053\u201c\u4f60\u7684\u9e70\u722a\u529f\u5012\u4e0d\u9519\u3002\u201d \u3000\u3000\u5979\u5634\u91cc\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u8bdd\u624b\u91cc\u7684\u7b77\u5b50\u5ffd\u7136\u8f7b\u8f7b\u5f80\u524d\u987a\u70b9\u3002 \u3000\u3000\u5979\u7528\u7684\u662f\u53cc\u8361\u7fe0\u9576\u7684\u7b77\u5b50\u8fd9\u79cd\u7b77\u5b50\u5f80\u5f80\u78b0\u78b0\u5c31\u4f1a\u65ad \u3000\u3000\u7b77\u5b50\u5728\u90ed\u5927\u8def\u53f3\u624b\u4e2d\u6307\u4e0a\u8f7b\u8f7b\u4e00\u70b9\u3002 \u3000\u3000\u7b77\u5b50\u6ca1\u6709\u65ad\u3002 \u3000\u3000\u90aa\u5927\u8def\u7684\u4eba\u5374\u50cf\u662f\u65ad\u4e86\uff0c\u7a81\u7136\u4ece\u534a\u7a7a\u4e2d\u843d\u4e86\u4e0b\u6765\u773c\u770b\u5c31\u8981\u8dcc\u4ec0\u6446\u6ee1\u4e86\u70b9\u5fc3\u7684\u684c\u5b50\u4e0a\u3002 \u3000\u3000\u9000\u592b\u4eba\u624b\u91cc\u7684\u7b77\u5b50\u5ffd\u7136\u5939\u4f4f\u4e86\u4ed6\u7684\u8170\u5e26\u4ed6\u6574\u4e2a\u4eba\u7684\u91cd\u91cf\u90fd\u5df2\u4ef7\u516c\u8fd9\u53cc\u78b0\u5c31\u65ad\u7684\u7b77\u5b50\u4e0a\u3002 \u3000\u3000\u7b77\u5343\u8fd8\u662f\u6ca1\u6709\u65ad\u3002 \u3000\u3000\u7fbd\u592b\u4eba\u7684\u624b\u60ac\u5728\u7a7a\u5c0f\uff0c\u7528\u7b77\u5b50\u592e\u4ed6\u5c31\u8c61\u662f\u5939\u4e2a\u867e\u7c73\u4f3c\u7684\u3002 \u3000\u3000\u71d5\u4e03\u770b\u5446\u5915\u3002 \u3000\u3000\u536b\u592b\u4eba\u5fae\u7b11\u9053\u201c\u8fd9\u9ebd\u5927\u4e2a\u997a\u5b50\u591f\u4f60\u545b\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672f\u8bf4\u5b8c\u90ed\u706b\u8def\u7684\u4eba\u5df3\u5411\u70ed\u4ea1\u5df4\u5382\u8fc7\u706b\u3002 \u3000\u3000\u71d5\u4ec1\u60f3\u53bb\u63a5\xb7\u6ca1\u6709\u63a5\u4f5c\u4e24\u4e2a\u4eba\u649e\xb7\u5168\u90fd\u8dcc\u4ecb\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e86\u5f88\u4e45\u90ed\u5927\u8def\u8fd8\u6ca1\u6709\u722c\u8d77\u6765\u53ea\u662f\u773c\u7741\u7741\u7684\u770b\u536b\u592b\u4eba\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u4e5f\u770b\u5446\u4e86\u3002\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u7528\u7684\u8fd9\u62db\u53eb\u4ec0\u9ebd\u529f\u592b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u6447\u5934\u8d66\u4e03\u9053\u201c\u4f60\u65e2\u7136\u4f1a\u9e70\u722a\u529f\xb7\u5c31\u5e94\u8be5\u77e5\u9053\u5176\u3008\u6709\u62db\u53eb\u8001\u9e70\u6293 \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u5979\u8fd9\u62db\u5c31\u662f\u4ece\u2018\u8001\u9e70\u6293\u9e21\u4e2d\u53d8\u5316\u6765\u7684\u53eb\u505a\u2018\u7b77\u5b50\u631f\u9e21\u3002\u201d\u90ed\u5927\u8def\u5480\u4e86\u53e3\u6c14\u6652 \u54fa\u9053\u201c\u6211\u7a76\u7adf\u662f\u9e21\u8fd8\u662f\u997a\u5b50\u5462\uff1f\u201d\u71d5\u4e03\u9053\u201c\u662f\u9e21\u8089\u9526\u5b50\u997a\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\xb7\u9053\uff0c\u201c\u60f3\u4e0d\u5230\u4f60\u61c2\u5f97\u7684\u4e8b\u5012\u8fd8\u771f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u4ed6\u8eab\u4ea7\u7a81\u7136\u53c8\u7bad\u822c\u7a9c\u4e86\u8fc7\u706b\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u6ca1\u6709\u95ee\u684c\u4e8e\u4e0a\u9762\u4f38\u624b\u80e1\u7a9c\u4eba\u4e86\u684c\u4e88\u5e95\u535c\u3002 \u3000\u3000\u6b63\u592b\u4eba\u6b63\u5fae\u7b11\u5728\u542c\u4ed6\u4eec\u8bf4\u8bdd\u597d\u50cf\u6c47\u542c\u5f97\u5f88\u6709\u8da3\u7684\u6837\u5b50\u3002 \u3000\u3000\u5979\u65e2\u6ca1\u6709\u60f3\u5230\u90ed\u5927\u8def\u8bf4\u8bf4\u4f1a\u5ffd\u7136\u53c8\u5ba1\u4e86\u8fc7\u6765\u66f4\u6ca1\u6709\u60f3\u5230\u8fd9\u4eba\u4f1a\u5f80\u684c\u5b50\u5e95\u5c38\u7a9c\u3002 \u3000\u3000\u684c\u4e8e\u5e95\u4e0b\u53c8\u6ca1\u6709\u70b9\u5fc3\u8fd9\u4eba\u5230\u5c38\u9762\u53bb\u5341\u4ec0\u9ebd\u5462\uff1f\u60f3\u68c0\u9aa8\u5934\u9ebd\uff1f \u3000\u3000\u997a\u5b50\u53c8\u6ca1\u6709\u9aa8\u5934\u5440\u3002 \u3000\u3000\u536b\u592b\u4eba\u4e5f\u4e0d\u7981\u89c9\u5f97\u6709\u70b9\u5947\u602a\u5c31\u5728\u8fd9\u65f6\u684c\u4e0a\u7684\u70b9\u5fc3\u7a81\u7136\u51ed\u7a7a\u8df3\u4e01\u8d77\u6765\u3002 \u3000\u3000\u7ff1\u5927\u8def\u7684\u624b\u5728\u684c\u4e8e\u5e95\u4e0b\u62cd\u684c\u4e0a\u7684\u70b9\u5fc3\u5c31\u8df3\u8d77\u4e86\u4e03\u516b\u5c3a\u9ad8\uff0c \u3000\u3000\u71d5\u4e03\u7684\u6b79\u6325\u672c\u6765\u6346\u5728\u4ed6\u817f\u4e0a\u7684\u7ef3\u5b50\u7a81\u4e49\u957f\u8679\u822c\u98de\u51fa\xb7\u957f\u86c7\u822c\u5377\u5c31\u6709\u4e03\u516b\u6837\u70b9\u5fc3\u88ab\u4ed6\u5377\u4e86 \u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u5df2\u4ece\u684c\u4e8e\u5e95\u4e0b\u7a9c\u51fa\u3002 \u3000\u3000\u71d5\u4e03\u2018\u677e\u624b\u70b9\u5fc3\u6389\u4e0b\u6765\u53c1\u56db\u4e2a\u90ed\u5927\u8def\u4f38\u624b\u63a5\u4e86\u4e24\u53c1\u4e2a\u6708\u53f8\u65f6\u5f20\u5927\u4e86\u7792\u4e2a\u8f6f\u8f6f\u7684\u5112\u7c73\u70e7\u5356 \u6b63\u597d\u4e0d\u504f\u4e0d\u4f24\u6389\u5728\u4ed6\u5634\u91cc\u3002 \u3000\u3000\u8fd9\u4e0b\u867d\u7136\u5e76\u4e0d\u662f\u4ec0\u9ebd\u5382\u4e2a\u8d77\u7684\u6b66\u529f\u4f46\u5374\u914d\u5408\u5f97\u53c8\u7d27\u51d1\uff0c\u4e49\u707c\u5999\u3001\u7b80\u76f4\u4ee4\u4eba\u53f9\u4e3a\u89c2\u6b62\u3002 \u3000\u3000\u536b\u592b\u4eba\u5c45\u7136\u4e5f\u53f9\u606f\u4e86\u58f0\uff0c\u8bf4\u9053\u201c\u770b\u4e86\u4f60\u4eec\u8fd9\u4e24\u624b\u529f\u592b\uff0c\u6211\u5c31\u7b97\u8ba9\u4f60\u4eec\u665a\u70b9\u4e1c\u66f2\u4e5f\u7b97\u503c\u5f97 \u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c1\u53e3\u4e24\u53e3\u5c31\u5c06\u70e7\u835a\u541e\u535c\u53bb\u7b11\u9053\u201c\u8fd9\u4eba\u5012\u603b\u7b97\u8fd8\u6709\u70b9\u6c11\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u5f00\u59cb\u5403\u7b2c\u4e8c\u4e2a\u70e7\u5356\u7684\u65f6\u5019\u71d5\u4e03\u4e5f\u5df2\u541e\u4e0b\u5382\u4e2a\u5305\u4ea7\u3002 \u3000\u3000\u80fd\u5403\u5f97\u8fd9\u5305\u5b50\u53ef\u771f\u4e0d\u5bb9\u6613\u6240\u4ee5\u56bc\u5728\u5634\u91cc\u7684\u6ecb\u6627\u4e5f\u8c61\u662f\u7279\u522b\u597d\u71d5\u4e03\u7b11\u9053\u201c\u8fd9\u5305\u5b50\u771f\u597d\u5403\u5374 \u4e0d\u77e5\u662f\u7528\u4ec0\u9ebd\u505a\u9985\u7684\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u5fae\u7b11\u9053\u201c\u5305\u5b50\u548c\u70e7\u5356\u90fd\u6709\u4e24\u79cd\u8e48\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u4e24\u79cd\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\uff1a\u201c\u4e00\u79cd\u662f\u867e\u4ec1\u9c9c\u8089\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u8fd8\u6709\u79cd\u4ed8\u9ebd\u8089\uff1a\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u8001\u9f20\u8089\u6bd2\u8001\u9f20\u3002\u201d \u3000\u3000\u8001\u9f20\u4e2d\u6765\u662f\u53ef\u4ee5\u5403\u7684\u4f46\u6bd2\u8001\u9f20\u5403\u4e0b\u53bb\u5374\u80fd\u8981\u4eba\u7684\u547d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5403\u4e0b\u5de6\u7684\u70e7\u4f01\xb7\u597d\u50cf\u5df2\u505c\u5728\u55d3\u5b50\u670d\u4e0a\xb7\u518d\u4e5f\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8fd8\u60f3\u95ee\u95ee\xb7\u4ed6\u5403\u7684\u4fa5\u8981\u662f\u8036\u79cd\u9985\u505a\u73b0\u5728\u5374\u5df2\u7528\u4e0d\u95ee\u4ed6\u5ffd\u7136\u89c9\u5f97\u56db\u80a2\u53d1\u8f6f\u8111\u888b\u53d1 \u7eff\u3002 \u3000\u3000\u518d\u770b\u9893\u4e03\u5f20\u8138\u7adf\u5df4\u53d8\u6210\u6b7b\u7070\u8272\u800c\u4e14\u6e10\u6fc0\u53d1\u9ed1\u3002 \u3000\u3000\u536b\u592b\u4eba\u8fd8\u5728\u5fae\u7b11\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b63\u60f3\u8fc7\u53bb\xb7\u5ffd\u7136\u89c9\u5f97\u5979\u8c61\u5df2\u662f\u5df2\u5230\u4e86\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u5f20\u80a1\u6e10\u6e10\u53d8\u5f97\u6a21\u7cca\u4e0d\u6e05\xb7\u6e10 \u6e10\u7a7a\u770b\u90a3\u770b\u4e0d\u89c1\u5382\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u71d5\u4e03\u5df2\u51b2\u8fc7\u6765\u70ae\u4f4d\u5382\u4ed6\u591c\u4ed6\u4e4c\u65c1\u9053\u201c\u4e34\u6b7b\u4e4b\u9157\u6211\u6709\u4e2a\u79d8\u5bc6\u8981\u98df\u8bc9\u4f11\u3002\u90ed\u5927\u8def\u6b96 \u201c\u4ec0\u2026\u2026\u53f6\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u201c\u2500\u2500\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u51fa\u81ea\u5df1\u7684\u79d8\u5bc6\xb7\u5c31\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u8bf4\u51fa\uff0c\u90ed\u5927\u8def\u4e5f\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4eba\u4e3a\u8d22\u6b7b\u9e1f\u4e3a\u98df\u4ea1\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u5e76\u4e0d\u592a\u5bf9\u3002 \u3000\u3000\u6709\u7684\u4eba\u5e76\u4e0d\u592a\u5728\u4e4e\u8d22\u5b9d\u7edd\u4e0d\u4f1a\u4e3a\u4e86\u94b1\u62fc\u547d\u5374\u5f80\u5f80\u4f1a\u4e3a\u5382\u597d\u5403\u800c\u6b7b\u3002 \u3000\u3000\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u8fd9\u79cd\u6b7b\u6cd5\u5f88\u51a4\u6897\uff1f \u3000\u3000\u7b49\u4f60\u4fc4\u5f97\u53d1\u6655\u65f6\xb7\u8bf4\u4e0d\u5b9a\u4e5f\u4f1a\u89c9\u5f97\u4e0d\u5982\u6b7b\u4e86\u7b97\u5382\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4e3a\u4ec0\u9ebd\u4f1a\u6328\u997f\u5462\uff1f \u3000\u3000\u670b\u53cb\u5f53\u7136\u662f\u4e3a\u4e86\u670b\u53cb\u3002 \u3000\u3000\u201c\u4e3a\u670b\u53cb\u4e3d\u6b7b\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u4e0b\u5730\u72f1\u7684\u3002\u201d \u3000\u3000\u4f46\u670b\u53cb\u82e5\u90fd\u5728\u5730\u72f1\u91cc\u4ed6\u4eec\u4e5f\u8bb8\u5b81\u53ef\u4e0b\u5730\u72f1\uff0c\u4e5f\u4e0d\u613f\u4e0a\u5929\u5802\u3002 \u3000\u3000\u81ea\u5360\u8270\u96be\u5589\u6b7b\u3002 \u3000\u3000\u6b7b\u7684\u786e\u53ef\u4ee5\u7b97\u662f\u6700\u53ef\u4f2f\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u90a3\u610f\u601d\u5c31\u662f\u4f60\u5df2\u5b8c\u4e86\u5df2\u5b8c\u5168\u6d88\u51cf\u4e86\u4ece\u6b64\u4e0d\u518d\u6709\u5e0c\u671b\u4f60\u7684\u8089\u4f53\u5f88\u5feb\u5c31\u4f1a\u8150\u70c2\u4f60\u7684\u59d3\u540d\u4e5f\u5f88 \u5feb\u5c31\u4f1a\u88ab\u4eba\u6de1\u5fd8\u3002 \u3000\u3000\u810f\u571f\u8fd8\u6709\u4ec0\u9ebd\u6bd4\u6b7b\u66f4\u53ef\u6015\u7684\u5462\uff1f \u3000\u3000\u6b7b\u4e86\u82e5\u8fd8\u5f97\u4e0b\u5730\u72f1\u90a3\u5f53\u7136\u66f4\u53ef\u6015\u3002 \u3000\u3000\u4f46\u5730\u72f1\u7a76\u7adf\u662f\u4ec0\u9ebd\u6837\u5b50\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u90a3\u5730\u65b9\u60f3\u5fc5\u5f88\u9ed1\u6697\uff0c\u975e\u5e38\u9ed1\u6697\u2026\u2026 \u3000\u3000\u9ed1\u6697\u3002 \u3000\u3000\u9ed1\u6697\u5f97\u8ba9\u4f60\u975e\u4f46\u770b\u4e0d\u89c1\u522b\u4eba\xb7\u4e5f\u770b\u4e0d\u89c1\u81ea\u5df1\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fde\u81ea\u5df1\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u4ed6\u53ea\u611f\u89c9\u5230\u81ea\u5df1\u7684\u773c\u775b\u5df2\u7741\u5f00\u4e86\u3002 \u3000\u3000\u4f46\u81ea\u5df1\u7a76\u7adf\u5728\u4ec0\u9ebd\u5730\u65b9\uff1f\u7a76\u7adf\u662f\u4e0d\u662f\u5b58\u5728\uff1f\u4ed6\u5374\u5b8c\u5168\u672c\u77e5\u9053\u3002 \u3000\u3000\u201c\u4e0d\u77e5\u9053\u201d\u7684\u672c\u8eab\u5c31\u662f\u79cd\u6050\u60e7\u4e5f\u8bb8\u5c31\u662f\u4eba\u7c7b\u6700\u5927\u7684\u6050\u60e7\u3002 \u3000\u3000\u4eba\u4eec\u6050\u60e7\u6b7b\u4ea1\xb7\u5c82\u975e\u4e5f\u6b63\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u6b7b\u5170\u7a76\u7adf\u662f\u4ec0\u9ebd\u6837\u4e88\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u4e0d\u80fd\u4e0d\u601d\u60e7\u51e0\u4e4e\u5df2\u6050\u60e7\u5f97\u8fde\u52a8\u90fd\u4e0d\u80fd\u591f\u52a8 \u3000\u3000\u6050\u60e7\u672c\u6559\u662f\u4eba\u7c7b\u6c38\u8fdc\u65e0\u6cd5\u514b\u670d\u5450\u9ad8\u6208 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u90ed\u5927\u8def\u624d\u542c\u5230\u81ea\u5df1\u8eab\u65c1\u4eff\u4f5b\u6709\u4e2a\u4eba\u5728\u547c\u5438\u3002 \u3000\u3000\u4f46\u90a3\u7a76\u7adf\u662f\u4e0d\u662f\u4eba\u7684\u547c\u5438\u58f0\u4ed6\u8fd8\u662f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5728\u5982\u6b64\u9ed1\u6697\u4e2d\u4efb\u4f55\u4eba\u90fd\u5df2\u65e0\u6cd5\u518d\u5bf9\u81ea\u5df2\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u8fd8\u80fd\u76f8\u4fe1\u2019\u4ef6\u4e8b\u852c\u4e03\u6d3b\u65f6\u65e2\u7136\u8ddf\u4ed6\u5728\u8d77\u5c31\u7b97\u6b7b\u5382\u4e5f\u2018\u5b9a\u8fd8\u662f\u4f1a\u8ddf\u4ed6\u8d77\u3002 \u3000\u3000\u6709\u4e9b\u670b\u53cb\u597d\u50cf\u6c38\u8fdc\u90fd\u5206\u4e0d\u5f00\u7684\u65e0\u8bba\u6b7b\u6d3b\u90fd\u5206\u4e0d\u5f00\u3002 \u3000\u3000\u6240\u4ee5\u90ed\u5927\u8def\u58ee\u8d77\u80c6\u5b50\u9053\u201c\u9893\u4e03\u2026\u3002\u2019\u662f\u4e0d\u662f\u5f25\uff1f\u201d \u3000\u3000\u4e49\u8fc7\u534a\u664c\u9ed1\u6697\u4e2d\u529b\u54cd\u8d77\u4e2a\u5f88\u865a\u5f31\u7684\u58f0\u54cd\u201c\u662f\u5c0f\u90ed\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u603b\u7b97\u677e\u4e01\u53e3\u6c14\u3002 \u3000\u3000\u53ea\u8981\u53f0\u808b\u53cb\u8ddf\u4ed6\u5728\u8d77\u65e0\u8bba\u6b7b\u6d3b\u90fd\u6ca1\u5173\u7cfb\u4e86\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u5f00\u59cb\u5f80\u90a3\u8fb9\u79fb\u52a8\u7ec8\u6237\u6478\u5230\u4e86\u53ea\u624b\xb7\u53ea\u51b0\u51b7\u7684\u624b\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u662f\u4e2a\u662f\u4f60\u7684\u4e88\uff1f\u201d \u3000\u3000\u624b\u52a8\u4e86\u52a8\u7acb\u523b\u5c06\u90ed\u5927\u8def\u7684\u624b\u63e1\u7d27\u3002 \u3000\u3000\u7136\u5f8c\u542c\u5230\u71d5\u4e03\u865a\u5f31\u7684\u58f0\u97f3\u9053\u201c\u8fd9\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4eec\u662f\u4e0d\u662f\u8fd8\u6d3b\uff1f\u201d \u3000\u3000\u90ed\u4eba\u8def\u53f9\u4e86\u95e8\u6c14\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u770b\u6765\u4f60\u6d3b\u65f6\u662f\u4e2a\u7cca\u5f90\u4eba\u6b7b\u4e86\u4e5f\u662f\u4e2a\u7cca\u6d82\u9b3c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5374\u7b11\u4e86\u7b11\u9053\u201c\u770b\u6765\u4f60\u6d3b\u65f6\u8981\u81ed\u6211\u6b7b\u4e86\u4e5f\u8981\u81ed\u6211\u3002\u201d \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u8bf4\u8bdd\u5374\u5c06\u90ed\u5927\u8def\u7684\u624b\u63e1\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u4ed6\u5e73\u65f6\u672c\u662f\u4e2a\u5f88\u575a\u5f3a\u7684\u4eba\u4f46\u73b0\u5728\u5374\u8c61\u662f\u8981\u501a\u8d56\u90ed\u5927\u8def\u4e86\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u672c\u5c31\u5728\u501a\u8d56\u90ed\u5927\u8def\u4e86\u3001\u53ea\u4e0d\u8fc7\u5e73\u65f6\u76f4\u5728\u5c3d\u529b\u63a7\u5236\u81fc\u5df1\u4e2a\u4eba\u53ea\u6709\u5230\u4e86\u771f\u6b63\u6050\u60e7\u7684\u65f6 \u5019\u624d\u4f1a\u5c06\u81ea\u5df1\u771f\u6b63\u7684\u60c5\u611f\u6d41\u9732\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u6c89\u9ed8\u5382\u534a\u664c\u5ffd\u53c8\u95ee\u9053\u201c\u4f60\u731c\u6211\u73b0\u5728\u6700\u60f3\u77e5\u9053\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u60f3\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u5bf9\u3002\u201d\u71d5\u4e03\u9053\u201c\u6444\u77e5\u9053\u6211\u4eec\u7a76\u7adf\u662f\u4e0d\u662f\u8fd8\u6d3b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e5f\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u9053\u201c\u6211\u73b0\u5728\u6ca1\u6709\u5fc3\u60c5\u731c\u4f60\u7684\u5fc3\u4e8b\u4f60\u81ea\u5df1\u8bf4\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u5343\u53e4\u8270\u96be\u552f\u2606\u6b7b \u3000\u3000\u90ed\u5927\u8def\u8bf4\u201c\u6211\u5458\u60f3\u77e5\u9053\u4f60\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u82d1\u4e03\u9053\u201c\u6211\uff1f\u2026\u2026\u6211\u6709\u4ec0\u9ebd\u79d8\u5bc6\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e34\u6b7b\u7684\u8981\u544a\u8bc9\u6211\u7684\u90a3\u4ef6\u79d8\u5bc6\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u624b\u5ffd\u7136\u7f29\u4e86\u56de\u53bb\u6c89\u9ed8\u5382\u5f88\u4e45\u624d\u5e26\u7b11\u9053\u201c\u5230\u73b0\u5728\u4f60\u8fd8\u6ca1\u6709\u5fd8\u8bb0\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u65e0\u8bba\u6b7b\u6d3b\u90fd\u6c34\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\xb7\u624d\u7f13\u7f13\u9053\u201c\u53ef\u662f\u73b0\u5728\u6211\u5df2\u4e0d\u60f3\u628a\u90a3\u4ef6\u4e8b\u544a\u8bc9\u4f60\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e5f\u6ca1\u6709\u4e3a\u4ec0\u9ebd\u53ea\u4e0d\u8fc7\u2026\u2026\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5145\xb7\u524d\u9762\u90a3\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u4e2a\u5ffd\u7136\u4eae\u8d75\u4e86\u70b9\u9634\u68ee\u68ee\u3001\u78a7\u9cde\u9cde\u7684\u706b\u5149\u3002 \u3000\u3000\u9b3c\u706b\u60e8\u78a7\u8272\u7684\u706b\u5149\u4e0b\u4eff\u4f5b\u6709\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u4e5f\u8bb8\u4e0d\u662f\u4eba\u5f71\u662f\u9b3c\u5f71\u3002 \u3000\u3000\u4ed6\u770b\u6765\u98d8\u98d8\u8361\u8361\u7684\u7ad9\u5728\u90a3\u91cc\xb7\u597d\u50cf\u4e0a\u4e0d\u5929\u4e0b\u4e0d\u5730\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4d\u874e\u9053\u201c\u4f60\u662f\u4eba\uff1f\u8fd8\u662f\u9b3c\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u8fd9\u4e5f\u4e0d\u77e5\u662f\u4eba\u8fd8\u662f\u9b3c\u7684\u5f71\u5b50\xb7\u5ffd\u7136\u53c8\u5411\u524d\u98d8\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u662f\u4eba\u4e5f\u597d\u662f\u9b3c\u4e5f\u597d\u603b\u662f\u8fd9\u65e0\u8fb9\u9ed1\u6697\u4e2d\u552f\u7684\u70b9\u4eae \u3000\u3000\u53ea\u8981\u6709\u2018\u70b9\u5149\u5c31\u6bd4\u9ed1\u6697\u597d\u3002 \u3000\u3000\u90ed\u5927\u8def\u6c89\u58f0\u9053\u201c\u4f60\u8fd8\u80fd\u4e0d\u80fd\u8d70\uff1f\u201d\u71d5\u4e03\u9053\u201c\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u8ffd\u8fc7\u53bb\u597d\u4e2d\u597d\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u9053\u201c\u65e0\u8bba\u5982\u4f55\xb7\u6211\u60f3\u603b\u4e0d\u4f1a\u6bd4\u73b0\u5728\u8fd9\u60c5\u51b5\u66f4\u574f\u7684\u4e86\u3002\u201d \u3000\u3000\u9b3c\u706b\u5728\u524d\u9762\u98d8\u8361\u597d\u50cf\u6545\u610f\u5728\u7b49\u4ed6\u4eec\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u627e\u4e86\u71d5\u4e03\u7684\u624b\u518d\u63e1\u7d27\u9053\u201c\u4f60\u62c9\u6211\u5382\u4e07\u83ab\u8981\u653e\u677e\u65e0\u8bba\u597d\u6b79\u6211\u4eec\u90fd\u8981\u5728\u8d77\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7684\u529b\u6c14\u8fd8\u6ca1\u6709\u6062\u590d\u8eab\u5b50\u8fd8\u6709\u70b9\u9ebb\u75f9\u3002 \u3000\u3000\u4f69\u65e0\u8bba\u5982\u4f55\u4ed6\u4eec\u603b\u7b97\u5df2\u7ad9\u4e86\u8d77\u6765\xb7\u8ddf\u90a3\u70b9\u9b3c\u706b\u5f80\u524d\u8d70\u3002 \u3000\u3000\u524d\u9762\u662f\u4ec0\u9ebd\uff1f \u3000\u3000\u662f\u5929\u5802\uff1f\u8fd8\u662f\u5730\u72f1\uff1f \u3000\u3000\u4ed6\u4eec\u65e2\u4e0d\u77e5\u9053\u4e5f\u4e0d\u5728\u4e4e\u56e0\u4e3a\u4ed6\u8ba2\u603b\u7b97\u8fd8\u80fd\u624b\u62c9\u624b\u5f80\u524d\u8d70\u3002 \u3000\u3000\u7b49\u4ed6\u4eec\u6e10\u6e10\u53ef\u4ee5\u8d70\u5f97\u5feb\u70b9\u7684\u65f6\u5019\uff0c\u524d\u9762\u90a3\u9b3c\u706b\u901f\u5ea6\u4e5f\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u9b3c\u706b\u7a81\u7136\u5982\u6d41\u661f\u822c\u95ea\uff0c\u5ffd\u7136\u6d88\u5931\u3002 \u3000\u3000\u56db\u9762\u53c8\u53d8\u5f97\u5b8c\u5168\u9ed1\u6697\u3002 \u3000\u3000\u6ca1\u6709\u5149\u6ca1\u6709\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u80fd\u542c\u5f97\u5230\u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\u5fc3\u8df3\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5df2\u611f\u89c9\u5230\u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\xb7\u5fc3\u8df3\u5f97\u5feb\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5df4\u611f\u89c9\u51fa\u5bf9\u65b9\u7684\u624b\u5fc3\u91cc\u5728\u5192\u51b7\u6c57\u3002\u90ed\u5927\u8def\u9053\u201c\u4f60\u7528\u4e0d\u5bb3\u6015\uff0c\u5047\u5982\u6211\u4eec\u771f\u7684\u5df2\u6b7b \u4e86\uff0c\u8fd8\u6709\u4ec0\u9ebd\u597d\u5bb3\u6015\u7684\uff1f\u5047\u5982\u6211\u4eec\u8fd8\u6ca1\u6709\u6b7b\u5c31\u66f4\u4e0d\u5fc5\u5bb3\u6015\u4e86\u3002\u201d \u3000\u3000\u4e2a\u4eba\u53eb\u522b\u4eba\u83ab\u8981\u5bb3\u6015\u7684\u65f6\u5019\u4ed6\u81ea\u5df1\u5fc3\u91cc\u5b9a\u5728\u5bb3\u6015\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4eec\u662f\u7ee7\u7eed\u5f80\u524d\u8d70\uff1f\u8fd8\u662f\u9000\u56de\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u662f\u5f80\u5f8c\u9000\u7684\u4eba\u9ebd\u201d\u71d5\u4e03\u9053\u201c\u597d\xb7\u4e0d\u7ba1\u597d\u6b79\xb7\u6211\u4eec\u5148\u5f80\u524d\u9762\u95ef\u95ef\u518d\u8bf4\u201d \u3000\u3000\u4e24\u4eba\u7684\u624b\u63e1\u5f97\u66f4\u7d27\u5927\u6b65\u5411\u524d\u51b2\u51fa\u3002 \u3000\u3000\u7a81\u542c\u58f0\u5927\u559d\xb7\u9053\u201c\u7ad9\u4f50\u5382 \u3000\u3000\u559d\u58f0\u54cd\u8d77\u9ed1\u6697\u4e2d\u7a81\u53c8\u95ea\u8d77\u5382\u4e03\u516b\u70b9\u9b3c\u706b\u3002 \u3000\u3000\u9634\u68ee\u68ee\u7684\u706b\u5149\u98d8\u98d8\u8361\u8361\u7684\u60ac\u5728\u4e2d\u7a7a\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u53ef\u4ee5\u770b\u5230\u524d\u9762\u6709\u5f20\u5f88\u5927\u5f88\u5927\u7684\u516c\u6848\u3002 \u3000\u3000\u6848\u4e0a\u6709\u4e2a\u7b14\u7b52\u8fd8\u5854\u5f88\u591a\u4e2a\u672c\u5b50\u4e5f\u4e0d\u77e5\u662f\u4e66\uff1f\u8fd8\u662f\u5e10\u7c3f\uff1f \u3000\u3000\u4e2a\u4eba\u6b63\u5750\u5728\u6848\u5f8c\u7ffb\u672c\u5e10\u6ca5\u3002 \u3000\u3000\u4ed6\u8d38\u8fd8\u662f\u770b\u4e0d\u6e05\u8fd9\u4eba\u7684\u9762\u76ee\u4f9d\u7a00\u53ea\u770b\u51fa\u8fd9\u4eba\u597d\u50cf\u957f\u5f88\u957f\u7684\u80e1\u4e88\uff0c\u5934\u4e0a\u8fd8\u98a4\u9876\u5360\u4ee3\u7684\u7687 \u51a0\u3002 \u3000\u3000\u521a\u90a3\u9b3c\u5f71\u4e5f\u5728\u516c\u6848\u65c1\uff0c\u8fd8\u662f\u4e0a\u4e0d\u5929\uff0c\u4e0b\u4e0d\u5730\u7684\u540a\u5728\u90a3\u91cc\u624b\u91cc\u597d\u50cf\u62ff\u5757\u5f88\u5927\u7684\u6728\u724c\u3002 \u3000\u3000\u96be\u9053\u8fd9\u5c31\u662f\u62d8\u9b42\u724c\uff1f \u3000\u3000\u96be\u9053\u8fd9\u5730\u65b9\u5c31\u662f\u68ee\u7f57\u6bbf\uff1f \u3000\u3000\u4e0a\u9762\u5750\u7684\u5c31\u662f\u960e\u5929\uff1f \u3000\u3000\u4ed6\u4ecd\u4e0d\u77e5\u9053\u8c01\u4e5f\u6ca1\u6709\u5230\u8fc7\u68ee\u7f57\u6bbf\u8c01\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u960e\u738b\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u5df3\u611f\u89c9\u5230\u79cd\u9634\u68ee\u68ee\u7684\u9b3c\u6c14\u4ee4\u4eba\u6bdb\u9aa8\u60ca\u7136\u3002 \u3000\u3000\u4e0a\u9762\u5750\u7684\u960e\u738b\u5c45\u7136\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u90a3\u58f0\u97f3\u4e5f\u9634\u68ee\u68ee\u7684\u5e26\u9b3c\u6c14\xb7\u901a\u201c\u8fd9\u4e24\u4eba\u9633\u5bff\u672a\u5c3d\u4e3a\u4f55\u6765\u6b64\uff1f\u201d \u3000\u3000\u90a3\u9b3c\u5f71\u5b50\u9053\u201c\u56e0\u4e3a\u4ed6\u4eec\u72af\u4e86\u7f6a\u3002\u201d \u3000\u3000\u960e\u738b\u9053\u201c\u72af\u7684\u662f\u4f55\u7f6a\uff1f\u201d \u3000\u3000\u9b3c\u5f71\u5b50\u9053\u201c\u8d2a\u5486\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u960e\u738b\u9053\u7f6a\u5728\u513f\u7b49\u201d \u3000\u3000\u9b3c\u5f71\u5b50\u9053\u201c\u7537\u4eba\u597d\u5403\u5fc5\u5b9a\u4e3a\u54a8\u5973\u4eba\u597d\u5403\u5fc5\u5b9a\u4e3a\u5a1f\u6b64\u7f6a\u5217\u4e3a\u7b2c\u4e03\u5c42\u5730\u72f1\u6c38\u4e16\u4e0d\u5f97\u5403\u9971\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u5927\u58f0\u9053\u201c\u8bf4\u8c0e\u7684\u7f6a\u66f4\u5927\u5e86\u8be5\u6253\u5165\u62d4\u820c\u5730\u72f1\u2026\u2026\u201d\u960e\u6b63\u62cd\u684c\u4e86\u559d\u9053\u201c\u5927\u80c6\u5b58\u8fd9 \u53d6\u4e5f\u6562\u5982\u6b64\u653e\u8086\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65e0\u8bba\u4f60\u662f\u4eba\u4e5f\u597d\u662f\u9b3c\u4e5f\u597d\u53ea\u8981\u5154\u6821\u4e86\u6211\u6211\u90fd\u975e\u653e\u8086\u4e0d\u53ef\u3002\u201d\u960e\u738b\u9053\u201c\u51a4\u6746\u4e86 \u4f60\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5927\u5546\u8bf4\u9053\u201c\u4f11\u82e5\u771f\u7684\u662f\u960e\u738b\uff0c\u81ea\u5df1\u5c31\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u4e5f\u5927\u58f0\u9053\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u77e5\u9053\u4ef6\u4e8b\u3002\u201d\u960e\u738b\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u65e0\u8bba\u4f60\u662f\u771f\u960e\u738b\u4e5f\u597d\xb7\u5047\u960e\u738b\u4e5f\u597d\u90fd\u4f11\u60f3\u80fd\u4ece\u6211\u4eec\u5634\u91cc\u6253\u542c\u51fa\u6797\u592a\u5e73\u7684\u4e0b \u843d\u3002\u201d \u3000\u3000\u8fd9\u5207\u8bdd\u8bf4\u51fa\u6765\u960e\u738b\u597d\u50cf\u53cd\u5012\u6709\u70b9\u5403\u60ca\u8fc7\u4e86\u534a\u9633\uff0c\u624d\u9634\u60af\u6d4b\u9053\u201c\u5c31\u7b97\u6211\u662f\u4e2a\u5047\u960e\u738b\u4f46\u4f60\u4eec \u7eab\u5df2\u771f\u6b7b\u3002\u201d\u71d5\u4e03\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u960e\u738b\u51b7\u7b11\u9053\u201c\u65e2\u5df3\u5230\u8fd9\u91cc\u4f60\u4eec\u96be\u9053\u8fd8\u60f3\u6d3b\u56de\u53bb\uff1f\u201d\u71d5\u4e03\u9053\u201c\u60f3\u4e0d\u60f3\u6d3b\u662f\u95ee\u4e8b\u8bf4\u4e0d\u8bf4\u53c8\u662f \u53e6\u5916\u56de\u4e8b\u4e86\u3002\u201d\u960e\u738b\u5386\u58f0\u9053\u201c\u4f60\u4eec\u96be\u9053\u4e86\u6b7b\u4e5f\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u8bf4\u5c31\u662f\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u960e\u738b\u51b7\u7b11\u9053\u201c\u597d\u201c \u3000\u3000\u8fd9\u4e2a\u5b57\u8bf4\u51fa\u53e3\u6240\u6709\u7684\u706b\u5149\u7a81\u53c8\u6d88\u5931\xb7\u53c8\u53d8\u4e3a\u7247\u9ed1\u6697\u3002 \u3000\u3000\u90ed\u5927\u8def\u62c9\u71d5\u4e03\u5c31\u5f80\u524d\u51b2\u3002 \u3000\u3000\u4ed6\u4eec\u540c\u65f6\u51b2\u8fc7\u53bb\u540c\u65f6\u8dcc\u5012\u5728\u5730\u3002 \u3000\u3000\u524d\u9762\u7684\u516c\u6848\u5df2\u6ca1\u6709\u5382\uff0c\u960e\u738b\u4e5f\u6ca1\u6709\u4e86\u5c0f\u9b3c\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u9664\u4e86\u9ed1\u6697\u5916\xb7\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u53ea\u6709\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e0d\u662f\u592a\u806a\u660e\u5c31\u662f\u592a\u7b28\u3002 \u3000\u3000\u5de6\u9762\u662f\u77f3\u58c1\uff0c\u77f3\u9762\u4e5f\u662f\u77f3\u58c1\u524d\u9149\u662f\u77f3\u58c1\u5f8c\u9762\u4e5f\u662f\u77f3\u58c1\u3002 \u3000\u3000\u6bd4\u94c1\u8fd8\u786c\u7684\u77f3\u58c1\u3002 \u3000\u3000\u4ed6\u4eec\u7ec8\u4e8e\u53d1\u89c9\u8fd9\u5730\u65b9\u5df2\u53d8\u6210\u4e2a\u77f3\u6876\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u7d22\u6027\u5750\u5382\u4e0b\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u7ff1\u5927\u8def\u5c45\u7136\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u4e5f\u53d1\u73b0\u90a3\u960e\u738b\u8db3\u5047\u7684\u5382\uff1f\u201d\u71d5\u4e03\u9053\u201c\u90a3\u960e\u5e72\u5fff\u5c31\u8db3\u536b \u592b\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f46\u536b\u592b\u4eba\u6ca1\u6709\u80e1\u4e88\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u80e1\u5b50\u4e5f\u662f\u5047\u7684\u4ec0\u9ebd\u90fd\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u5ffd\u7136\u5927\u7b11\u9053\u201c\u8fd9\u4eba\u5012\u4e5f\u6ed1\u7a3d\u5c45\u7136\u60f3\u5f97\u51fa\u8fd9\u79cd\u7b28\u6c88\u4ea7\u6765\u60f3\u8981\u6211\u4eec\u4e0a\u5f53\u3002\u201d\u71d5\u4e03\u4e5f\u7b11 \u9053\u201c\u7b80\u76f4\u6ed1\u7a3d\u5f97\u8981\u9970\u3002\u201d \u3000\u3000\u4ed6\u4eec\u867d\u7136\u6789\u7b11\u4f46\u7b11\u7684\u58f0\u97f3\u5374\u96be\u542c\u5f97\u5f88\xb7\u633d\u624b\u6bd4\u54ed\u90fd\u96be\u9636\u3002 \u3000\u3000\u5185\u4e3a\u8fd9\u4ef6\u4e8b\u5e76\u4e0d\u6ed1\u7a3d\u70b9\u4e5f\u4e0d\u6ed1\u7a3d\u3002 \u3000\u3000\u8fd9\u6cd5\u5b50\u4e5f\u4e0d\u7b28\u3002 \u3000\u3000\u4f60\u82e5\u556e\u4e86\u4e2a\u6709\u6f33\u7684\u5305\u5b50\u5ffd\u7136\u89c9\u5f97\u56db\u80a2\u65e0\u529b\u53c8\u770b\u5230\u4f60\u670b\u53cb\u7684\u8138\u5df2\u53d1\u9ed1\u7136\u5f8c\u6655\u6b7b\u4e86\u8fc7\u53bb\u7b49\u4f60 \u9192\u6765\u7684\u65f6\u5019\u5c31\u53d1\u73b0\u81ea\u5df1\u5728\u8fd9\u9ebd\u6837\u7684\u4e2a\u5730\u65b9\uff0c\u770b\u5230\u5382\u4e2a\u98d8\u5728\u534a\u7a7a\u7684\u9b3c\u5f71\u5b50\u8fd8\u770b\u5230\u5382\u4f0e\u6234\u7687\u51a0\u3001 \u957f\u80e1\u5b50\u7684\u960e\u4e0a\u4f60\u4f1a\u4e0d\u4f1a\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u6ed1\u7a3d\uff1f \u3000\u3000\u90ed\u5927\u8def\u5df2\u7b11\u4e0d\u51fa\u4e86\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u5979\u505a\u7684\u4e8b\u867d\u6ed1\u7a3d\u8bf4\u7684\u8bdd\u5374\u4e0d\u6ed1\u7a3d\u3002\u201d\u71d5\u4e03\u901a\u201c\u4ec0\u9ebd \u8bdd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u960e\u738b\u867d\u662f\u5047\u7684\u6211\u4eec\u5374\u5df2\u7b49\u65bc\u771f\u7684\u6b7b\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u6015\u6b7b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u7684\u786e\u6709\u70b9\u6015\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\u706b\u5149\u95ea\u7167\u4eae\u5382\u5927\u5806\u9ec4\u6f84\u6f84\u95ea\u91d1\u5149\u7684\u4e1c\u897f\u3002 \u3000\u3000\u91d1\u5b50\u3002 \u3000\u3000\u4e16\u4e0a\u5f88\u5c11\u6709\u4eba\u80fd\u770b\u5230\u8fd9\u9ebd\u591a\u91d1\u5b50\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u53c8\u54cd\u8d77\u5382\u90a3\u9634\u4fa7\u4f8b\u7684\u58f0\u97f3\u201c\u53ea\u8981\u4f60\u4eec\u8bf4\u51fa\u6765\u6211\u4e0d\u505a\u7acb\u523b\u5c31\u653e\u4f53\u4eec\u8d70\u8fd9\u4e9b\u91d1\u5b50\u4e5f\u5168 \u90fd\u662f\u4f53\u4eec\u7684\u4e86\u3002\u201d\u90ed\u5927\u8def\u7a81\u7136\u773a\u4e86\u8d77\u6765\u5927\u58f0\u53eb\u9041\u201c\u4e0d\u8bf4\u4e0d\u8bf4\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u53d1\u51fa\u5382\u2026\u58f0\u53f9\u606f\u7136\u5f8c\u5c31\u53c8\u4ec0\u9ebd\u90fd\u770b\u4e0d\u89c1\u4f8b\u9ebd\u90fd\u542c\u5c0f \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u539f\u6765\u4f60\u4e5f\u4e0d\u6015\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u6015\u662f\u4e0d\u592a\u4f2f\u53ea\u4e0d\u8fc7\u2026\u2026\u6211\u4eec\u867d\u7136\u662f\u4e3a\u6797\u592a\u5e73\u6b7b\u7684\u4ed6\u5374\u6839\u672c\u4e0d\u77e5\u9053\u4e5f\u8bb8\u6c38\u8fdc \u90fd\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u65e0\u8bba\u4e3a\u670b\u53cb\u505a\u4e86\u4ec0\u9ebd\u90fd\u662f\u4f60\u81ea\u5df1\u7684\u4e8b\uff0c\u6839\u672c\u5c31\u4e0d\u5fc5\u60f3\u8981\u670b\u53cb\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u9053\u201c\u6211\u672c\u6765\u8fd8\u4f2f\u4f60\u89c9\u5f97\u6b7b\u5f97\u592a\u51a4\u6789\u60f3\u4e0d\u5230\u4f60\u6bd4\u6211\u66f4\u591f\u670b\u53cb\u3002\u201d \u3000\u3000\u9886\u4e03\u6c89\u9ed8\u5382\u534a\u664c\u53cd\u800c\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4e5f\u8bb8\u6211\u5e76\u4e0d\u662f\u591f\u670b\u53cb\u53ea\u4e0d\u8fc7\u60f3\u5f97\u591f\u660e\u767d\u800c\u5df3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4e86\u8981\u627e\u6797\u592a\u5e73\uff0c\u5979\u597d\u50cf\u5df2\u4e0d\u9519\u727a\u7272\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u82e5\u975e\u8ddf\u6797\u4eba\u5e73\u6709\u5f88\u6df1\u7684\u4ec7\u6068\u600e\u9ebd\u65e8\u5982\u6b64\u727a\u7272\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u5947\u602a\uff0c\u6797 \u592a\u5e73\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u600e\u9ebd\u4f1a\u8ddf\u5979\u8fd9\u79cd\u4eba\u7ed3\u4e0b\u6df1\u4ec7\u5927\u6068\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u60f3\u5fc5\u662f\u4ed6\u4e0a\u4ee3\u7ed3\u535c\u7684\u4ec7\u6028\u5979\u4e3a\u4e86\u8981\u65a9\u8349\u9664\u6839\u6240\u4ee5\u624d\u975e\u6740\u6797\u592a\u5e73\u4e0d\u53ef\u3002\u201d\u90ed\u5927\u8def \u9053\u201c\u6709\u7406\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u65e2\u7136\u77e5\u9053\u6211\u5546\u662f\u6797\u592a\u5e73\u7684\u670b\u53cb\u5f53\u7136\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211\u4ecd\u300f\u6240\u4ee5\u6211\u4eec\u5c31\u7b97\u8bf4\u51fa\u4e86\u6797 \u592a\u5e73\u7684\u4e0b\u843d\u4e5f\u662f\u6837\u8981\u6b7b\u4e5f\u8bb8\u6b7b\u5f97\u66f4 \u3000\u3000\u7ff1\u5927\u8def\u957f\u53f9\u201c\u53e3\uff0c\u82eb\u7b11\u9053\uff0c\u201c\u88ab\u4f60\u8fd9\u9ebd\u8bf4\uff0c\u6211\u597d\u50cf\u4e5f\u89c9\u5f97\u767d\u5df1\u5e76\u6ca1\u6709\u81ea\u5df1\u8bf4\u7684\u90a3\u9ebd\u591f\u808b \u53cb\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u82e5\u975e\u4f60\u63d0\u9192\u6211\u5c31\u5df2\u5fd8\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u600e\u9ebd\u5fd8\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ef6\u4e8b\u4f60\u82e5\u6545\u610f\u4e0d\u53bb\u60f3\u5b83\uff0c\u5c82\u975e\u5c31\u7b49\u65bc\u5fd8\u4e86\u6837\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u6545\u610f\u4e0d\u53bb\u60f3\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\xb7\u90a3\u6837\u6211\u5c31\u4f1a\u89c9\u5f97\u81ea\u5df1\u771f\u7684\u5f88\u591f\u670b\u53cb\u7b49\u6211\u6b7b\u7684\u65f6\u5019\xb7\u5c31\u4f1a\u89c9\u5f97\u81ea\u5df1\u6bd4\u8f83 \u4f1f\u5927\u70b9\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u4f46\u7b11\u58f0\u4e2d\u5374\u6709\u4e9b\u8f9b\u9178\u4e4b\u610f\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u624d\u7f00\u7f13\u9053\u201c\u5176\u5b9e\u4f60\u672c\u6765\u5c31\u6bd4\u522b\u4eba\u4f1f\u5927\u70b9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u597d\u50cf\u8981\u8df3\u5382\u8d77\u6765\u9053\u201c\u6211\u4f1f\u5927\uff1f\u4f60\u4e5f\u89c9\u5f97\u6211\u4f1f\u5927\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6ca1\u6709\u4eba\u5929\u751f\u5c31\u662f\u82f1\u96c4\u82f1\u96c4\u5f80\u5f80\u4e5f\u662f\u88ab\u903c\u51fa\u6765\u7684\u3002\u5927\u5bb6\u867d\u7136\u90fd\u660e\u767d\u8fd9\u9053\u7406\u5374\u8fd8\u8981 \u96be\u514d\u8981\u81ea\u5df2\u9a97\u9a97\u81ea\u5df1\u3002\u53ea\u6709\u4f60\u2500\u2026\u201c \u3000\u3000\u4ed6\u5480\u606f\u4e86\u58f0\xb7\u6162\u9992\u63a5\u9053\u201c\u4f60\u4e0d\u4f46\u6562\u627f\u8ba4\u800c\u4e14\u8fd8\u6562\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u201c\u4e00\u3002\u8fd9\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u8138\u76ae\u6bd4\u522b\u4eba\u539a\u3002\u201d \u3000\u3000\u6382\u4e03\u9053\u201c\u8fd9\u7edd\u4e0d\u662f\u8138\u539a\u662f\u201c\u3001\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u52c7\u6c14\u8fd9\u5c31\u662f\u52c7\u6c14\u5f88\u5c11\u4eba\u80fd\u6709\u8fd9\u79cd\u52c7\u6c14\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u6709\u5938\u5956\u6211\u7684\u65f6\u5019\xb7\u662f\u4e0d\u662f\u6545\u610f\u60f3\u5b89\u6170\u5b89\u6170\u6211\u8ba9\u6211\u89c9\u5f97\u8212\u670d \u4e9b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u56de\u7b54\xb7\u53ea\u662f\u7d27\u7d27\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u51b0\u51b7\u7684\u624b\u597d\u50cf\u5df4\u6e10\u6e10\u6e29\u6696\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\u90ed\u5927\u8def\u624d\u7f13\u7f13\u9053\u201c\u5176\u5b9e\u6211\u4eec\u8ba4\u8bc6\u5e76\u4e0d\u4e45\uff0c\u4f46\u6211\u603b\u89c9\u5f97\u4f60\u8db3\u6211\u5e73\u751f\u6700\u597d\u7684\u670b \u53cb\u3002\u5176\u5b9e\u738b\u52a8\u4e5f\u662f\u6211\u6700\u597d\u7684\u670b\u53cb\u4f46\u6211\u5bf9\u4f60\u8fd8\u662f\u548c\u5bf9\u4ed6\u4e0d\u540c\u3002\u201d \u3000\u3000\u71d5\u4e03\u8f7b\u8f7b\u7684\u95ee\u9053\u201c\u6709\u4ec0\u9ebd\u4e0d\u540c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u8bf4\u4e0d\u51fa\u6709\u4ec0\u9ebd\u4e0d\u540c\u53ea\u4e0d\u8fc7\u2026\u2026\u53ea\u4e0d\u8fc7\u738b\u52a8\u6709\u4ec0\u9ebd\u5bf9\u4e0d\u8d77\u6211\u7684\u5730\u65b9\u6211\u5b9a\u4f1a \u539f\u8c05\u4ed6\u4f46\u4f60\u82e5\u5bf9\u4e0d\u8d77\u6211\u6211\u53cd\u800c\u5f88\u751f\u6c14\uff0c\u6c14\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u8fd9\u79cd\u60c5\u611f\u7684\u786e\u5f88\u5fae\u5999\u4e5f\u96be\u602a\u4ed6\u89e3\u91ca\u4e0d\u51fa\u3002 \u3000\u3000\u71d5\u4e03\u7684\u6307\u5c16\u597d\u50cf\u5728\u53d1\u6296\uff0c\u5fc3\u91cc\u597d\u50cf\u5f88\u6fc0\u52a8\u53ea\u53ef\u60dc\u90ed\u5927\u8def\u770b\u4e0d\u51fa\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u6765\u5426\u5219\u4e5f\u8bb8 \u5c31\u4f1a\u660e\u767d\u5f88\u591a\u4e8b\u5382\u3002 \u3000\u3000\u5047\u672c\u660e\u65e5\u4e5f\u5f88\u597d\u3002 \u3000\u3000\u90a3\u79cd\u7ecd\u7ecd\u7ee7\u6e3a\u3001\u81c6\u613f\u8131\u80ce\u7684\u611f\u89c9\u53cd\u800c\u66f4\u7f8e\u3001\u66f4\u5947\u5999\u3002 \u3000\u3000\u53ea\u53ef\u501f\u4ed6\u4eec\u80fd\u4eab\u53d7\u8fd9\u79cd\u611f\u89c9\u7684\u65f6\u5019\u5df2\u4e0d\u591a\u4e86\u3002\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u6211\u8fd8\u60f3\u77e5\u9053\u4ef6\u4e8b\uff0c\u5374\u4e0d\u77e5\u8be5 \u4e0d\u8be5\u95ee\u51fa\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u3002\u65e0\u8bba\u4ec0\u9ebd\u8bdd\u4f60\u90fd\u53ef\u4ee5\u5bf9\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5047\u5982\u536b\u592b\u4eba\u771f\u7684\u80af\u653e\u8fc7\u6211\u4eec\u771f\u7684\u5c06\u90a3\u9ebd\u591a\u91d1\u4e8e\u9001\u7ed9\u6211\u4eec\u4f60\u662f\u4e0d\u8db3\u5c31\u4f1a\u5c06\u6797\u592a\u5e73 \u7684\u4e0b\u843d\u544a\u8bc9\u5979\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u53e5\u8bdd\xb7\u53ea\u662f\u7f13\u7f13\u9053\u201c\u6211\u53ea\u77e5\u9053\u91d1\u5b50\u2019\u5b9a\u6709\u7528\u5b8c\u7684\u65f6\u5019\u4eba\u4e5f\u5b9a\u6709\u6b7b \u7684\u65f6\u5019\xb7\u4f46\u53cb\u60c5\u548c\u9053\u4e49\u5374\u4f17\u8fdc\u90c1\u5b58\u5728\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u9053\u5c31\u56e0\u4e3a\u505a\u4e0a\u8fd8\u6709\u8fd9\u79cd\u4e1c\u897f\u5b58\u5728\xb7\u6240\u4ee5\u4eba\u624d\u548c\u755c\u7272\u4e0d\u540c\u3002\u201d \u3000\u3000\u71d5\u4e03\u957f\u957f\u53f9\u606f\u4e86\u58f0\uff0c\u9053\u201c\u6211\u597d\u50cf\u5f88\u5c11\u542c\u5230\u4f60\u8bf4\u8fd9\u79cd\u8bdd\u4f60 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u6709\u4e9b\u9053\u7406\u5e76\u4e0d\u662f\u8981\u4f60\u7528\u7792\u8bf4\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u82e5\u4e0d\u8bf4\xb7\u522b\u4eba\u600e\u9ebd\u77e5\u9053\u4f60\u7a76\u7adf\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u6839\u672c\u5c31\u7528\u4e0d\u522b\u4eba\u77e5\u9053\u53ea\u8981\u6211\u7684\u670b\u53cb\u77e5\u9053\u53ea\u8981\u4f60\u77e5\u9053\u90a3\u5c31\u5df2\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u9053\u201c\u4f46\u73b0\u5728\u6211\u4e5f\u5f88\u60f3\u77e5\u9053\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4e0d\u662f\u60f3\u77e5\u9053\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u7684\u90a3\u6837\u79d8\u5bc6\u4e25 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u2500\u2026\u4f60\u8fd8\u6ca1\u6709\u5fd8\u5df1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u65e0\u8bba\u6b7b\u6d3b\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u9893\u4e03\u6c89\u9ed8\u4e86\u5f88\u4e45\xb7\u5e7d\u5e7d\u9053\u201c\u5176\u5b9e\u6211\u5df2\u6709\u5f88\u591a\u6b21\u90fd\u60f3\u8981\u5c06\u8fd9\u4e2a\u79d8\u5bc6\u8bf4\u51fa\u6765\u4e86\xb7\u5374\u53c8\u4f2f\u8bf4\u51fa \u5f8c\u4f1a\u5f8c\u6094\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u5f8c\u6094\uff1f\u201d \u3000\u3000\u9884\u4e03\u9053\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u4f2f\u4f60\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5f8c\u5c31\u4e0d\u613f\u518d\u8ddf\u6211\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7528\u529b\u63e1\u4f4f\u5382\u4ed6\u7684\u624b\u9053\u201c\u6d82\u653e\u5fc3\xb7\u65e0\u8bba\u4f60\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u4eba\uff0c\u65e0\u8bba\u4f60\u4ee5\u524d\u505a\u8fc7\u4ec0\u9ebd\u4e8b \u6211\u90fd\u6c38\u8fdc\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5927\u58f0\u8bf4\u9053\u201c\u6211\u82e5\u6709\u534a\u53e5\u865a\u53f0\u5c31\u53eb\u6211\u4e0d\u5f97\u597d\u2026\u2026\u201d\u201c\u6b7b\u201d\u5b57\u8fd8\u6bb5\u6709\u8bf4\u51fa\u53e3\u71d5\u4e03\u5df2\u63a9 \u4f50\u4e86\u4ed6\u7684\u5634\xb7\u67d4\u58f0\u9053\u201c\u597d\xb7\u6211\u544a\u8bc9\u4f60\u6211\u672c\u662f\u4e2a\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u95f4\u9ed1\u6697\u4e2d\u53c8\u6709\u70b9\u706f\u5149\u4eae\u8d77\u7167\u6837\u5f88\u5947\u602a\u7684\u4e1c\u897f\u3002 \u3000\u3000\u770b\u6765\u8c61\u662f\u4e2a\u94c1\u7b80\u67b6\u5728\u6728\u67b6\u4e0a\u2606\u9ed1\u98a4\u52a9\u7684\u3002\u603b\u6709\u5927\u6d77\u7897\u822c\u7c97\u7ec6\u3002 \u3000\u3000\u63a5\u536b\u592b\u4eba\u7684\u58f0\u97f3\u53c8\u54cd\u8d77\u201c\u4f60\u4eec\u8ba4\u4e0d\u8ba4\u5f97\u8fd9\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e2a\u8ba4\u5f97\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u7b11\u9053\u201c\u770b\u6765\u4f60\u975e\u4f46\u98df\u53e4\u4e0d\u5316\uff0c\u4e3d\u4e14\u5b64\u9694\u5be1\u95fb\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\xb7\u90a3\u94c1\u7b52\u91cc\u5ffd\u7136\u53d1\u51fa\u5929\u5d29\u5730\u88c2\u822c\u58f0\u5927\u9732\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u5c3d\u6735\u90fd\u5feb\u88ab\u9707\u84d1\u4e01\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u5929\u624d\u80fd\u5f20\u5f97\u5916\u773c\u56ed\u53ea\u89c1\u56db\u9762\u70df\u785d\u8ff7\u6f2b\xb7\u94c1\u7b80\u8d22\u9762\u7684\u77f3\u58c1\u5df3\u88ab\u6253\u5f00\u4e86\u4e2a\u5927\u6f9c \u3000\u3000\u536b\u592b\u4eba\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053\u8fd9\u662f\u4ec0\u9ebd\u4e86\u5427\u201d \u3000\u3000\u90ed\u5927\u8def\u957f\u957f\u5410\u51fa\u4e86\u53e3\u6c14\u95ee\u9053\u201c\u8fd9\u96be\u9053\u5c31\u662f\u5927\u70ae\u9ebd\uff1f\u201d\u536b\u592b\u4eba\u7b11\u9053\u201c\u4f60\u603b\u7b97\u53d8\u5f97\u806a\u660e\u4e86 \u4e9b\u3002\u201d\u6b22\u4e50\u8363\u8000 \u3000\u3000\u70ae\u53e3\u5728\u79fb\u52a8\uff0c\u5df2\u5bf9\u51c6\u4e86\u71d5\u4e03\u548c\u7ff1\u5927\u8def\u3002 \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u60f3\u4e0d\u60f3\u5c1d\u5c1d\u8fd9\u706b\u70ae\u7684\u6ecb\u5473\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u90a3\u9ebd\u4f60\u5c31\u8d76\u5feb\u8bf4\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u60a0\u7136\u9053\u201c\u4e5f\u8bb8\u4f60\u8fd8\u4e0d\u77e5\u9053\u8fd9\u79cd\u5927\u59d0\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u77e5\u9053\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u542c\u8bf4\u82e5\u7528\u8fd9\u79cd\u70ae\u53bb\u653b\u57ce\uff0c\u65e0\u8bba\u591a\u575a\u56fa\u7684\u57ce\u5899\u90fd\u6321\u4e0d \u3000\u3000\u536b\u592b\u4eba\u7b11\u9053\u201c\u65e2\u7136\u57ce\u5899\u90fd\u6321\u4e0d\u4f73\u4f60\u96be\u9053\u8fd8\u80fd\u6321\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u5ffd\u7136\u5927\u7b11\u9053\u201c\u8fd9\u4f60\u5c31\u4e0d\u8c28\u4e86\u6211\u7684\u8138\u76ae\u672c\u6765\u5c31\u6bd4\u57ce\u5899\u8fd8\u539a\u3002\u201d\u536b\u592b\u4eba\u6012\u9053\u201c\u4f60\u771f\u7684 \u4e0d\u8bf4\u201d \u3000\u3000\u90ed\u5927\u8def\u597d\u50cf\u8fde\u8bdd\u90fd\u61d2\u5f97\u8bf4\u4e86\u53ea\u662f\u8f6c\u8fc7\u4e86\u5934\uff0c\u51dd\u89c6\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u7684\u65e5\u5149\u6e29\u67d4\u5982\u6c34\u4f46\u58f0\u97f3\u5378\u575a\u51b3\u5982\u94a2\u3002 \u3000\u3000\u4ed6\u65ad\u7136\u9053\u201c\u7b97\u4e0a\u6628\u5929\u90a3\u6b21\xb7\u6211\u5df2\u7ecf\u6b7b\u8fc7\u516b\u6b21\u4e86\u518d\u6b7b\u6b21\u53c8\u4f55\u5a49\uff1f\u201d \u3000\u3000\u201c\u6b7b\u201d\u672c\u662f\u4ef6\u5458\u8270\u96be\u3001\u6700\u53ef\u6015\u7684\u4e8b\u4f46\u5728\u4ed6\u4eec\u5634\u91cc\u8bf4\u51fa\u6765\u5374\u597d\u50cf\u8f7b\u677e\u5f97\u5f88\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u62c9\u71d5\u4e03\u7684\u4e54\u9053\u201c\u6211\u53ea\u6709\u4ef6\u9057\u61be\u7684\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u67d4\u58f0\u9053\u201c\u6211\u660e\u767d\u4f46\u90a3\u4ef6\u4e8b\u6211\u65e0\u8bba\u6b7b\u6d3b\u90fd\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5c55\u98a7\u7b11\u9053\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u8fd8\u6709\u4ec0\u9ebd\u653f\u4e0d\u4e0b\u7684\u5462\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u51b7\u51b7\u9053\u201c\u597d\uff0c\u90a3\u4f60\u4eec\u5c31\u6b7b\u5427\u3002\u201d \u3000\u3000\u70ae\u53e3\u6b63\u5bf9\u71d5\u4e03\u548c\u90ed\u5927\u8def\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\u53c8\u662f\u5929\u5d29\u5730\u88c2\u822c\u58f0\u5927\u9707\u3002 \u3000\u3000\u59fb\u785d\u8ff7\u6f2b\u4e2d\u53ef\u4ee5\u770b\u5230\u4ed6\u4eec\u7684\u4eba\u56e0\u4e86\u4e0b\u53bb\u5012\u5728\u4e00\u8d77\u2026\u2026 \u3000\u3000\u6709\u4eba\u8bf4\u6b7b\u5f88\u56f0\u96be\u6709\u4eba\u8bf4\u6b7b\u5f88\u5bb9\u6613\u3002 \u3000\u3000\u4f60\u8bf4\u5462 \u3000\u3000\u67f3\u6697\u82b1\u660e \u3000\u3000\u5bf9\u71d5\u4e03\u8bf4\u6765\xb7\u6b7b\u7684\u786e\u5f88\u5bb9\u6613\xb7\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u4e5d\u6b21\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5c45\u7136\u53c8\u6d3b\u4e86\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81fc\u5df1\u8eba\u5728\u5f20\u67d4\u8f6f\u987a\u8212\u670d\u7684\u5e8a\u4e0a\u773c\u775b\u91cc\u770b\u5230\u7684\u6bcf\u6837\u4e1c\u897f\u90fd\u5f88\u534e\u4e3d\u3001\u5f88\u7cbe\u81f4\xb7\u7b80\u76f4\u5df4 \u4e0d\u8c61\u662f\u4eba\u95f4\u6240\u6709\u7684\u3002 \u3000\u3000\u4ed6\u4e0a\u6b21\u9192\u6765\u7684\u5730\u65b9\u82e5\u662f\u5730\u72f1\u8fd9\u5730\u65b9\u5b9a\u5c31\u662f\u5929\u5802\u3002 \u3000\u3000\u4f46\u82e5\u6ca1\u6709\u90ed\u5927\u8def\u5728\u8d77\uff0c\u5929\u5802\u53c8\u6709\u4ec0\u9ebd\u610f\u601d\uff1f \u3000\u3000\u90ed\u5927\u8def\u5462\uff1f\u96be\u9053\u4e0b\u4e86\u5730\u72f1\uff1f \u3000\u3000\u85d0\u4e03\u6323\u624e\u722c\u8d77\u5c31\u770b\u5230\u4e86\u7ff1\u5927\u8def\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u5c5e\u91cc\u6709\u5f20\u684c\u4e88\xb7\u684c\u4e8e\u6446\u6ee1\u4e86\u9152\u98df\uff0c\u90ed\u5927\u8def\u6b63\u5750\u5728\u90a3\u91cc\u5927\u5403\u5927 \u559d\u3002 \u3000\u3000\u4ed6\u770b\u5230\u71d5\u4e03\u624d\u653e\u4e0b\u7b77\u4e8e\u7b11\u9053\u201c\u6211\u770b\u4f60\u7761\u5f97\u6b63\u597d\u4e0d\u60f3\u5435\u9192\u4f60\u6240\u4ee5\u5c31\u5148\u6765\u4eab\u53d7\u4e86\xb7\u597d\u5728\u8fd9\u91cc \u7684\u4e1c\u897f\u591a\u5f97\u5f88\u5341\u4e2a\u4eba\u4e5f\u5403\u4e0d\u5b8c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4f60\u5e26\u6211\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d\uff01 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u91cc\u662f\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u201c \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u963b\u6839\u6839\u9053\u201c\u4f60\u77e5\u9053\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u53ea\u77e5\u9053\u8fd9\u91cc\u7684\u53a8\u5b50\u4e0d\u9519\u9152\u4e5f\u4e0d\u9519\u4f60\u8fd8\u7b49\u4ec0 \u3000\u3000\u4ed6\u63a5\u53c8\u7b11\u9053\u201c\u4e0d\u5403\u767d\u4e0d\u5403\xb7\u8fd9\u53e5\u8bdd\u4f60\u8fd8\u6ca1\u6709\u5b66\u4f1a\u3002\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u5a7f\u7136\u7b11\u9053\u201c\u65e9\u5c31\u5b66\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5c4b\u91cc\u4e0d\u4f46\u6709\u95e8\u8fd8\u6709\u7a97\u4e8e\u3002 \u3000\u3000\u7a97\u5916\u4f20\u6765\u9635\u9635\u6885\u82b1\u7684\u9999\u6c14\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u6709\u6ca1\u6709\u51fa\u53bb\u8fc7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002 \u3000\u3000\u71d5\u4e03\u76b1\u7709\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u51fa\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u987e\u5f97\u4e86\u5634\u5c31\u987e\u4e0d\u5f97\u773c\u775b\u4e86\u8fd8\u662f\u5676\u6bd4\u773c\u775b\u91cd\u8981\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u5148\u627e\u5230\u8fd9\u91cc\u7684\u4e3b\u4eba\u624d\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u53cd\u6b63\u4f1a\u6765\u627e\u6211\u4eec\u7684\uff0c\u6211\u4eec\u4f55\u5fc5\u6025\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5916\u9762\u5c31\u54cd\u8d77\u4e86\u6572\u95e8\u58f0\u3002 \u3000\u3000\u4e2a\u767d\u8863\u5982\u96ea\u3001\u660e\u964d\u5de7\u7b11\u7684\u5c0f\u5993\u5a18\u624b\u91cc\u6258\u4e24\u58f6\u9152\uff0c\u76c8\u76c8\u5b9a\u4e86\u8fdb\u6765\u770b\u6765\u5012\u771f\u6709\u51e0\u5206\u8c61\u662f\u5929\u4ea1 \u7684\u4ed9\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u773c\u4e0a\u6709\u70b9\u53d1\u76f4\u4e86\uff0c\u71d5\u4e03\u77aa\u4e86\u4ed6\u773c\u4ed6\u624d\u4e7e\u54b3\u5382\u4e24\u58f0\u5c06\u8eab\u5b50\u5750\u6b63\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u7b11\u9053 \u201c\u6211\u6b63\u6101\u9152\u4e0d\u591f\xb7\u60f3\u4e0d\u5230\u9152\u5df2\u6765\u5382\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u62bf\u5634\u7b11\u9053\u201c\u4f60\u65e2\u5df2\u5230\u4e86\u8fd9\u91cc\u65e0\u8bba\u60f3\u8981\u4ec0\u9ebd\u5c31\u6709\u4ec0\u9ebd \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4eec\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\u201c\u5f53\u7136\u662f\u8fd9\u91cc\u7684\u4e3b\u4eba\u628a\u4f60\u4eec\u6551\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6062\u5c31\u662f\u8fd9\u91cc\u7684\u4e3b\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u7728\u5382\u7728\u773c\u9053\u201c\u4f60\u770b\u6211\u8c61\u4e0d\u8c61\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8c61\u3002\u201d\u767d\u8863\u5c11\u5973\u5a9a\u7136\u9053\u201c\u6211\u5df1\u770b\u4e5f\u4e0d\u8c61\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u8fd9\u91cc\u7684\u4e3b\u4eba\u662f\u8c01\u5462\uff1f\u6211\u4eec\u8ba4\u4e0d\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u9053\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u5b9a\u8ba4\u5f97\u4f60\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u7b11\u9053\u201c\u56e0\u4e3a\xb7\u4ed6\u8bf4\u4f60\u4e2a\u4eba\u5403\u7684\u6bd4\u51e0\u4e2a\u4eba\u90e1\u591a\xb7\u7279\u5730\u53eb\u6211\u591a\u51c6\u5907\u70b9\u9152\u83dc\u3002\u4ed6\u5e97\u4e0d \u8ba4\u5f97\u4f60\u600e\u9ebd\u4f1a\u5bf9\u4f60\u5982\u6b64\u4e86\u89e3\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4eba\u7b11\u9053\u201c\u8fd9\u9ebd\u6837\u770b\u6765\u4ed6\u4e0d\u4f46\u8ba4\u5f97\u6211\u8fd8\u5b9a\u662f\u6211\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u8d2c\u773c\u7b11\u9053\u201c\u8bf7\u4f60\u559d\u9152\u7684\uff0c\u90fd\u662f\u4f60\u7684\u597d\u670b\u53cb\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u4f46\u8138\u8272\u53c8\u53d8\u5f97\u5f88\u96be\u770b\u987a\u5df4\u8fde\u7b77\u5b50\u90fd\u653e\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u8198\u5382\u4ed6\u670d\u4e5f\u4e0d\u6562\u591a\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u767d\u8863\u5c11\u4ea5\u9053\u3001\u201c\u7b49\u4e24\u4f4d\u5486\u9971\u5382\u6211\u5c31\u5e26\u4e24\u4f4d\u53bb\u89c1\u8fd9\u91cc\u7684\u4e3b\u4eba\u4ed6\u76f4\u90fd\u5728\u7b49\u4e24\u4f9d\u3002\u201d \u3000\u3000\u71d5\u4e03\u970d\u7136\u7ad9\u8d77\u6765\u9053\u201c\u6211\u73b0\u5728\u5df2\u7ecf\u9971\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u773c\u6ce2\u6d41\u52a8\u5a7f\u7136\u9053\u201c\u4f60\u600e\u9ebd\u770b\u5230\u6211\u5c31\u9971\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6de1\u8c08\u9053\u201c\u56e0\u4e3a\u4f60\u957f\u5f97\u6bd4\u53ea\u8e44\u8180\u8fd8\u53ef\u7231\u3002\u201d \u3000\u3000\u6885\u82b1\u767d\u518d\u66f2\u5ed3\u96d5\u67f1\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u677f\u8138\u5728\u524d\u9762\u5e26\u8def\uff0c\u65e2\u4e0d\u8bf4\u8bdd\u4e5f\u4e0d\u7b11\u4e86\u3002 \u3000\u3000\u5979\u7684\u786e\u5f88\u751c\u3001\u5f88\u7f8e\u4f46\u7684\u786e\u7a0d\u5fae\u80d6\u5382\u70b9\u3002 \u3000\u3000\u201c\u71d5\u4e03\u5c45\u7136\u62ff\u5979\u6765\u6bd4\u8e44\u8180\u5012\u662f\u4e8f\u4ed6\u600e\u9ebd\u60f3\u5f97\u51fa\u6765\u7684\u3002\u201d\u90ed\u5927\u8def\u770b\u71d5\u4e03\xb7\u60f3\u7b11\u53c8\u4e0d\u6562\u7b11\u3002 \u3000\u3000\u9602\u4e3a\u71d5\u4e03\u7684\u8138\u5305\u8fd8\u662f\u4e0d\u592a\u597d\u770b\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\u4ed6\u597d\u50cf\u5f88\u8ba8\u538c\u4ea5\u4eba\uff0c\u5c24\u5176\u65f6\u538c\u6444\u7ff1\u5927\u8def\u5f00 \u73a9\u7b11\u7684\u5973\u4eba\u3002 \u3000\u3000\u201c\u4ed6\u4ee5\u524d\u5b9a\u4e5f\u5403\u8fc7\u5973\u4eba\u7684\u4e8f\xb7\u4e0a\u8fc7\u5973\u4eba\u7684\u5f53\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51b3\u5b9a\u4ee5\u5f8c\u5b9a\u8981\u8bbe\u6cd5\u5f00\u5bfc\u5f00\u5bfc\u4ed6\u544a\u8bc9\u4ed6\u5973\u4eba\u5e76\u4e0d\u662f\u6bcf\u4e2a\u90fd\u8ba8\u538c\u7684\u5176\u4e2d\u5076\u7838\u4e5f\u6709\u51e0\u4e2a \u6bd4\u5168\u90e8\u7537\u4eba\u90fd\u5fc3\u7231\u5f97\u591a\u3002 \u3000\u3000\u6c0f\u5ed3\u5df3\u8d70\u5c3d\u3002 \u3000\u3000\u5c3d\u5934\u5904\u73e0\u4f4e\u5782\xb7\u4ed6\u8861\u521a\u8d70\u8fc7\u53bb\u5c31\u542c\u5230\u5b50\u91cc\u6709\u4eba\u5728\u7b11\u9053\u201c\u4f60\u4eec\u53c8\u6765\u4e86\u9ebd\uff1f\u8bf7\u8fdb\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u8fd9\u8d6b\u7136\u53c8\u662f\u536b\u592b\u4eba\u7684\u58f0\u97f3 \u3000\u3000\u539f\u6765\u8fd9\u91cc\u7684\u4e3b\u4eba\u8fd8\u662f\u5979\u3002 \u3000\u3000\u5979\u4e0b\u6bd2\u3001\u626e\u9b3c\u751a\u81f3\u4e0d\u501f\u5c06\u653b\u57ce\u7684\u5927\u70ae\u90fd\u642c\u6765\u5bf9\u4ed8\u4ed6\u4eec\u53ef\u662f\u5979\u73b0\u5728\u53c8\u6551\u4e86\u4ed6\u4eec\u800c\u4e14\u8fd8\u62ff\u597d \u9152\u597d\u83dc\u6765\u62db\u5f85\u4ed6\u8ba2\u3002\u90ed\u5927\u8def\u548c\u71d5\u4e03\u800c\u9762\u7cca\u56db\u5b9e\u5728\u731c\u4e0d\u900f\u5979\u7a76\u7adf\u5728\u6253\u4ec0\u9ebd\u6b63\u610f\u3002 \u3000\u3000\u536b\u592b\u4eba\u7684\u7b11\u5bb9\u8fd8\u8db3\u90a3\u9ebd\u9ad8\u8d38\u90a3\u9ebd\u52a8\u4eba\u3002 \u3000\u3000\u5979\u770b\u90ed\u5927\u8def\u548c\u71d5\u4e03\u8fd8\u5e26\u5fae\u7b11\u9053\u201c\u4f60\u4efd\u4e5f\u4e0d\u5fc5\u95ee\u6211\u7a76\u7adf\u5728\u4e8e\u4ec0\u9ebd\u8d5b\u610f\u6211\u7684\u4e3b\u610f\u672c\u5c31\u4ece\u6ca1\u6709 \u522b\u4eba\u80fd\u731c\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u56db\u5382\u53e3\u6c14\u9053\u201c\u8fd9\u5207\u8bdd\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u8fd9\u6709\u4ef6\u4e8b\u4f60\u4e5f\u4e0d\u59a8\u76f8\u4fe1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u53c8\u9053\u201c\u4f60\u4eec\u73b0\u5728\u5df2\u53ef\u4ee5\u8d70\u4e86\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70\uff0c\u65e0\u8bba\u5230\u90a3\u91cc\u53bb\u6211\u90fd\u7edd\u4e0d\u4f1a\u6ce5\u4eba\u8ddf\u8e2a\u4f60 \u8ba2\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\u901a\u201c\u4f60\u4e2d\u60f3\u8981\u6211\u4eec\u7684\u547d\u4e86\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e5f\u4e0d\u60f3\u77e5\u9053\u6797\u592a\u5e73\u7684\u8428\u843d\u4e86\uff1f\u201d\u536b\u592b\u4eba\u9053\u201c\u81f3\u5c11\u76ee\u524d\u5df3\u6728\u60f3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8d39\u4e86\u90a3\u9ebd\u591a\u4e8b\u6765\u5bf9\u4ed8\u6211\u8ba2\uff0c\u73b0\u5728\u5374\u968f\u968f\u4fbf\u4fbf\u5c31\u8ba9\u6211\u4eec\u8d70\u5382\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u8fd9\u53e5\u8bdd\u6211\u5b9e\u5728\u4e0d\u80fd\u76f8\u4fe1\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u8fde\u6211\u7684\u8bdd\u4f60\u90fd\u4e0d\u60c5\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4f60\u77e5\u9053\u6211\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e2a\u5f88\u6709\u94b1\u3001\u5f88\u6709\u5730\u4f4d\u3001\u4e5f\u72e0\u6709\u672c\u4e8b\u7684\u4eba\u4f46\u8fd9\u79cd\u4eba\u8bf4\u7684\u8bdd\u901a\u5e38\u90fd\u672a \u5fc5\u53ef\u9760\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u51dd\u89c6\u4ed6\u5ffd\u7136\u7b11\u4e86\uff0c\u9053\u201c\u4f60\u4eec\u5b9a\u89c9\u5f97\u6211\u505a\u7684\u4e8b\u5f88\u5947\u602a\u4f46\u4f60\u4eec\u82e5\u771f\u6c47\u77e5\u9053\u6211\u662f\u4ec0\u9ebd\u4eba \u4e4b\u76fe\u5c31\u4e0d\u4f1a\u5947\u602a\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d\u536b\u592b\u4eba\u2606\u4e2a\u5b87\u4e2a\u5b57\u9053\u201c\u6211\u5c31\u662f\u6797\u4eba\u5e73\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\u90ed\u5927\u8def\u548c\u71d5\u4e03\u4e49\u5927\u5403\u4e86\u60ca\u3002 \u3000\u3000\u4ed6\u4eec\u5b9e\u5728\u4e0d\u6562\u76f8\u4fe1\u5374\u53c8\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u536b\u592b\u4eba\u8fd9\u751f\u4e2d\u5c31\u7b97\u4e5f\u4f1a\u8bf4\u8fc7\u8c0e\u73b0\u5728\u5374\u7edd\u4e0d\u8c61\u662f\u8bf4\u8c0e\u7684\u6837\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5c31\u76f8\u4fe1\u4f60\u771f\u662f\u6797\u592a\u5e73\u7684\u6bcd\u4eb2\u4f46\u96cc\u4eb2\u4e49\u600e\u4f1a\u4e2d\u77e5\u513f\u5b50\u7684\u4e0b\u843d\u5462\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u8f7b\u8f7b\u7684\u53f9\u606f\u4e86\u58f0\uff0c\u916a\u7136\u9053\u201c\u8fd9\u5c31\u662f\u505a\u6bcb\u4eb2\u7684\u60b2\u54c0\u513f\u4e8e\u957f\u5927\u4e86\u4e4b\u5f8c\uff0c\u505a\u7684\u4e8b\u5f80\u5f80\u5c31 \u4e0d\u662f\u6bcd\u4eb2\u6240\u80fd\u4e86\u89e3\u7684\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u7b11\u4e86\u7b11\u63a5\u9053\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u5df2\u6e10\u6e10\u53d8\u6210\u4e86\u4e2a\u7537 \u3000\u3000\u90ed\u5927\u8def\u6e29\u5c0f\u4f4f\u95ee\u9053\u201c\u4ed6\u7a76\u7adf\u505a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u53f9\u9053\u201c\u4ed6\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u505a\u53ea\u4e0d\u8fc7\u4ece\u5bb6\u91cc\u9003\u4e86\u51fa\u5de6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u9053\u201c\u4ece\u5bb6\u91cc\u9003\u4e86\u51fa\u53bb\uff1f\u4e3a\u4ec0\u9ebd\u8981\u9003\uff1f\u201d\u536b\u592b\u4eba\u9053\u201c\u4ed6\u8fdb\u5a5a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u618e\u7136\u9053\u201c\u9003\u5a5a\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u82eb\u7b11\u9053\u201c\u6211\u770b\u4ed6\u5e74\u7eaa\u6ca5\u6e10\u5927\u5382\u5c31\u66ff\u4ed6\u8ba2\u4e0b\u5382\u95e8\u4eb2\u4e8b\u8c01\u77e5\u9053\u4ed6\u7adf\u5728\u5a5a\u793c\u524d\u7684\u5929\u665a\u4e0a \u5077\u5077\u7684\u9003\u4e86\u51fa\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u9676\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u9053\u201c\u6211\u660e\u767d\u4e86\u4ed6\u5b9a\u4e0d\u518d\u6b22\u90a3\u4e2a\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u90a3\u4ea5\u63a5\u4e8e\u4ed6\u8fde\u89c1\u90fd\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u4e0d\u7981\u89c9\u5f97\u5947\u602a\u9053\u201c\u65e2\u7136\u6ca1\u6709\u89c1\u8fc7\uff0c\u4ed6\u600e\u9ebd\u77e5\u9053\u90a3\u5973\u88ab\u5b50\u597d\u4e0d\u597d\u5462\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u53f9\u9053\u201c\u53ea\u56e0\u90a3\u95e8\u4eb2\u4e8b\u662f\u6211\u66ff\u4ed6\u4e0b\u6765\u7684\u6240\u4ee5\u4ed6\u5c31\u4e0d\u559c\u6b22\u201c \u3000\u3000\u90ed\u5927\u8def\u53c8\u7b11\u4e86\xb7\u9053\u201c\u8005\u5a46\u662f\u767d\u5df1\u7684\uff0c\u672c\u5c31\u8be5\u81ea\u5df1\u6765\u9009\u624d\u5bf9\u3002\u4f60\u82e5\u65e8\u5148\u4ed6\u770b\u770b\u90a3\u5973\u5b69\u5b50\u4ed6 \u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u9003\u5382\u3002\u201d \u3000\u3000\u4ed6\u795e\u8272\u7a81\u7136\u53d8\u5f97\u5f88\u4e25\u8083\u53c8\u9053\u201c\u8fd9\u5e76\u4e0d\u662f\u8bf4\u4ed6\u4e0d\u5b5d\u987a\u4f60\u4f46\u4e2a\u7537\u4eba\u957f\u5927\u4e86\u4e4b\u5f8c\u591a\u591a\u5c11\u5c11\u603b\u8be5 \u6709\u70b9\u767d\u5df1\u7684\u4e3b\u6020\u5426\u5219\u4ed6\u53c8\u600e\u9ebd\u80fd\u7b97\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\u201c\u6211\u672c\u6765\u4e5f\u5f88\u751f\u6c14\u5c0f\u96a7\u5f8c\u6765\u60f3\u4e86\u60f3\u53cd\u5496\u89c9\u5f97\u6709\u70b9\u9ad8\u5174\u3002\u201d \u3000\u3000\u70ed\u4e03\u5ffd\u7136\u9053\u201c\u4f60\u7684\u780c\u5e94\u8be5\u9ad8\u5174\u56e0\u4e3a\u50cf\u4ed6\u8fd9\u9ebd\u6837\u6709\u4e3b\u89c1\u7684\u7537\u4eba\u4e16\u4e0a\u8fd8\u6728\u591a\u3002\u201d\u90ed\u5927\u8def\u9053 \u201c\u73b0\u5728\u663e\u7136\u4e2d\u591a\u4ecd\u4ee5\u5f8c\u5b9a\u4f1a\u6162\u6162\u591a\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u5927\u4eba\u5c55\u989d\u901a\u6240\u4ee5\u6211\u73b0\u5728\u5df2\u6539\u53d8\u4e3b\u610f\uff0c\u5e76\u4e2a\u51fa\u8981\u9065\u4ed6\u7684\u53bb\u6210\u4eb2\u4e86\u201c \u3000\u3000\u5979\u95e8\u5149\u51dd\u89c6\u8fdc\u529b\u6162\u6162\u7684\u63a5\u9053\u201c\u6211\u60f3\u4e2a\u7537\u6838\u5b50\u5728\u6210\u53cd\u7684\u65f6\u5019\u80fd\u4e2a\u4eba\u5728\u5916\u780c\u95ef\u8361\u95ef\u8361\u78e8\u7ec3\u78e8 \u7ec3\u81ea\u5df1\u5bf9\u4ed6\u8fd9\u751f\u603b\u662f\u6709\u597d\u5904\u7684\u201c\u90ed\u5927\u8def\u53f9\u201c\u95e8\u6c14\u7247\u7b11\u9053\u201c\u8fd9\u4e9b\u6d3b\u4f60\u5c45\u65e9\u70b9\u8bf4\u51fa\u6765\u591a\u597d\u3002\u201d \u3000\u3000\u536b\u592b\u4eba\u7b11\u9053\u201c\u6211\u4ee5\u524d\u6ca1\u6709\u8bf4\u5c71\u6765\u53ea\u56e0\u4e3a\u6211\u8fd8\u6709\u70b9\u4e0d\u653e\u5fc3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u653e\u5fc3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u9053\u201c\u4e0d\u653e\u5fc3\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u90a3\u9ebd\u6837\u505a\u53ea\u4e0d\u8fc7\u662f\u5728\u8bd5\u63a2\u6211\u4eec\uff1f\u201d\u536b\u592b\u4eba\u7b11\u9053\u201c\u4f60\u4eec\u65e2\u7136\u662f\u4ed6\u7684\u597d\u670b\u53cb\u60f3 \u5fc5\u4e5f\u4e0d\u4f1a\u602a\u6211\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u4f60\u653e\u5fc3\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u536b\u592b\u4eba\u68e0\u58f0\u9053\u201c\u73b0\u5728\u6211\u5df2\u77e5\u9053\u4ed6\u7684\u670b\u53cb\u975e\u4f46\u4e0d\u501f\u4e3a\u4ed6\u6218\u997f\u4e3a\u4ed6\u6b7b\u800c\u4e14\u8fd8\u80fd\u4e3a\u4ed6\u62d2\u7edd\u5404\u79cd \u8bf1\u60d1\u5728\u6211\u770b\u6765\xb7\u90a3\u6bd4\u6b7b\u8fd8\u5708\u96be\u5f97 \u3000\u3000\u5979\u53f9\u606f\uff0c\u4e49\u9053\u201c\u4ed6\u80fd\u4ea4\u5230\u8fd9\u79cd\u670b\u53cb\u771f\u662f\u4ed6\u7684\u8fd0\u6c14\uff0c\u6211\u8fd8\u6709\u4ec0\u9ebd\u4e0d\u653e\u5fc3\u8236\u3002\u201d \u3000\u3000\u201c\u6444\u5b50\u957f\u5927\u4e86\u867d\u5df2\u4e0d\u518d\u5c5e\u2606\u62c7\u4eb2\u4f46\u5f62\u4eb2\u603b\u5f52\u662f\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u5c24\u8bba\u5728\u54ea\u91cc\u6c38\u8fdc\u90fd\u662f\u4f60\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u505a\u6bcd\u4eb2\u7684\u82e5\u80fd\u61c2\u5f97\u8fd9\u9053\u7406\u5979\u7684\u60b2\u54c0\u5c31\u4f1a\u53d8\u4e3a\u6b22\u6109\u3002\u5c0f\u57ce\u8fd8\u662f\u90a3\u9ebd\u5b9e\u90a3\u9ebd\u5b81\u9759\uff0c\u6709\u4e9b\u5730\u65b9 \u662f\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u7684\xb7\u53ea\u6709\u4eba\u5728\u53d8\u4eba\u5fc3\u5728\u53d8\u3002 \u3000\u3000\u4f46\u6709\u4e9b\u4eba\u4e5f\u662f\u6c38\u8fdc\u4e0d\u4f1a\u53d8\u7684\u770b\u5230\u71d5\u4e03\u56de\u6765\u738b\u52a8\u8fd8\u662f\u7b4b\u5728\u5e8a\u4e0a\u8fd8\u662f\u8fde\u52a8\u90c1\u4e0d\u52a8\u2606 \u3000\u3000\u90ed\u5927\u8def\u5374\u5fcd\u4e2d\u4f50\u9053\u201c\u516d\u4e03\u5929\u4e0d\u89c1\u4f60\u96be\u9053\u4e5f\u6ca1\u6709\u53e5\u8bdd\u95ee\u6211\u4eec\uff1f\u201d\u738b\u52a8\u8fd9\u624d\u61d2\u6d0b\u6c57\u7684\u6253\u4e2a\u5475 \u6b20\u9053\u201c\u95f7\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u95ee\u95ee\u6211\u4eec\u8fd9\u51e0\u5929\u8fc7\u5f97\u597d\u4e0d\u597d\u3002\u201d \u3000\u3000\u4e03\u529f\u9053\u201c\u6211\u4e0d\u5fc5\u95ee\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u5fc5\u95ee\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4eec\u53ea\u800d\u8fd8\u80fd\u6d3b\u56de\u6765\u5c31\u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u70ed\u4e03\u7728\u7728\u773c\u9053\u201c\u53ef\u662f\u4f60\u81f3\u5c11\u603b\u5e94\u8be5\u95ee\u95ee\u6d3b\u5265\u76ae\u7a76\u7adf\u5265\u8bf4\u7684\u76ae\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e5f\u4e2a\u5fc5\u95ee\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u7b11\u4e86\u7b11\u6de1\u6de1\u9053\u201c\u50cf\u4ed6\u90a3\u79cd\u4eba\u9664\u4e86\u5265\u4ed6\u81ea\u5df1\u7684\u76ae\u4e4b\u5916\u8fd8\u80fd\u5265\u907f\u7684 \u76ae\u201d \u3000\u3000\u9664\u4e86\u51fa\u4e4e\u5bf9\u4ed8\u51e4\u6816\u68a7\u90a3\u6b21\u5916\u6797\u592a\u5e73\u65e0\u8bba\u505a\u4ec0\u9ebd\u4e8b\u90fd\u6bd4\u522b\u4eba\u6162\u4e2d\u62cd\u3002\u65e0\u8bba\u5403\u996d\u4e5f\u597d\u8bf4\u8bdd\u4e5f \u597d\u8d70\u8def\u4e5f\u597d\u4ed6\u603b\u662f\u541e\u541e\u7684\u3001\u4e0d\u614c\u4e0d\u5fd9\u7684\u6837\u5b50\u5c31\u7b97\u706b\u70e7\u5230\u7709\u6bdb\u4ed6\u597d\u50cf\u4e5f\u4e0d\u4f1a\u6025\u3002 \u3000\u3000\u90ed\u5927\u8def\u6709\u65f6\u751a\u81f3\u89c9\u5f97\u4ed6\u8c61\u662f\u4e2a\u8001\u5934\u5b50\u3002 \u3000\u3000\u4ed6\u4e0d\u8c61\u738b\u52a8\u4ed6\u5e76\u4e0d\u61d2\u4ed6\u5c31\u662f\u8fd9\u79cd\u6e29\u541e\u6c34\u813e\u6c14\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71ac\u4e03\u56de\u6765\u5df2\u6709\u8005\u534a\u5929\u4e86\xb7\u4ed6\u624d\u4fb5\u541e\u541e\u7684\u8d70\u4e86\u8fdb\u6765\u8863\u670d\u5df2\u7a7f\u5f97\u6574\u6574\u9f50\u9f50\u5934\u53d1\u5df2\u68b3 \u5f97\u6574\u6574\u9f50\u9f50\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u65f6\u5019\xb7\u4efb\u4f55\u5730\u65b9\u4ed6\u7684\u6837\u5b50\u770b\u6765\u603b\u8c61\u662f\u4e00\u6821\u521a\u5265\u5f00\u7684\u786c\u58f3\u679c\u4e49\u65b0\u9c9c\u53c8\u4e7e\u51c0\u3002 \u3000\u3000\u201c\u8fd9\u4eba\u968f\u65f6\u968f\u5730\u90fd\u597d\u50cf\u51c6\u5907\u88ab\u7687\u5e1d\u53ec\u89c1\u4f3c\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u548c\u85cf\u4e03\u5bf9\u671b\u4e86\u773c\u90fd\u4e0d\u7981\u7b11\u5382\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u53c8\u60f3\u5230\u4e86\u536b\u592b\u4eba\u4e5f\u53ea\u6709\u536b\u592b\u4eba\u90a3\u6837\u7684\u6bcb\u4eb2\u529b\u80fd\u4e2d\u5f97\u51fa\u6797\u592a\u5e73\u8fd9\u6837\u7684\u513f\u4e8e\u3002 \u3000\u3000\u201c\u597d\u6811\u4e0a\u662f\u7edd\u4e0d\u4f1a\u957f\u51fa\u70c2\u6843\u5b50\u6765\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u770b\u4ed6\u8ba2\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u7b11\u4ec0\u9ebd\u95fb\u8d61\u9053\u770b\u6837\u5b50\u4f60 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u5f00\u5fc3\u6781\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u4ec0\u77e5\u4e0d\u77e5\u9053\u6d3b\u5265\u76ae\u5df2\u5931\u8e2a\u4e86\u5229\u6e90\u5f53\u671f\u5df2\u6362\u4e86\u8001\u677f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8fde\u8fd9\u79cd\u5927\u4e8b\u90fd\u4e0d\u77e5\u9053\xb7\u8fd9\u4e24\u5929\u4f60\u4eec\u7a76\u7adf\u5e72\u4ec0\u9ebd\u53bb \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u53c8\u5bf9\u671b\u4e86\u773c\u53c8\u7b11\u4e86\u7b11\u4ed6\u4eec\u65e9\u5df2\u6709\u4e86\u51b3\u5b9a\u51b3\u5b9a\u4e2a\u5bf9\u4efb\u4f55\u4eba\u8bf4\u51fa\u4ed6\u4eec\u8fd9\u51e0\u5929\u6765 \u7684\u906d\u9047\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u89c9\u5f97\u6797\u592a\u5e73\u4e2a\u77e5\u9053\u8fd9\u4ef6\u5ba2\u53cd\u800c\u597d\u4ed6\u4eec\u5373\u4e0d\u613f\u5f71\u54cd\u6797\u4f17\u5e73\u7684\u51b3\u5b9a\u4e5f\u4e0d\u60f3\u6797\u592a\u5e73\u5bf9 \u4ed6\u4eec\u611f\u6fc0\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u5e0c\u671b\u6797\u592a\u5e73\u80fd\u81fc\u5dfe\u767d\u591c\u7684\u8ddf\u5927\u5bb6\u4e2d\u6d3b\u6bb5\u65f6\u5019\uff0c\u90a3\u4ed6\u5b9a\u5c31\u4f1a\u53d8\u5f97\u66f4\u575a\u5f3a\u66f4\u6210\u719f\u66f4\u806a \u660e\u3002 \u3000\u3000\u8fd9\u4e5f\u6b63\u662f\u536b\u592b\u4eba\u6240\u5e0c\u671b\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u4e24\u5929\u6211\u4eec\u4e5f\u6c72\u5e72\u4ec0\u9ebd\u3001\u53ea\u4e0d\u8fc7\u88ab\u4eba\u6bd2\u6b7b\u8fc7\u6b21\u89c1\u8fc7\u4f4e\u960e\u5e72\u53c8\u88ab\u5927\u70ae\u8f70\u8fc7\u6b21 \u6700\u5f8c\u8fd9\u4eba\u8bf7\u6211\u4eec\u5927\u5403\u5927\u874e\u5382\u987f\u6211\u4eec\u5c31\u56de\u6765\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u77aa\u4ed6\xb7\u77aa\u4e86\u5f88\u4e45\u5ffd\u7136\u5927\u7b11\u9053\u201c\u6211\u77e5\u9053\u4f60\u5f88\u4f1a\u5439\u725b\u4f46\u8fd9\u6b21\u5374\u672a\u514d\u5439\u5f97\u592a\u8fc7\u706b\u4e86\u4e9b\u6015 \u900f\u53c1\u5c81\u7684\u5c0f\u5b69\u5b50\u90c1\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8212\u8212\u670d\u670d\u7684\u8eba\u4e0b\u53bb\xb7\u95ed\u4e0a\u773c\u775b\u957f\u957f\u5410\u51fa\u6c14\u5fae\u7b11\u901a\u201c\u8fd9\u79cd\u4e8b\u6211\u5c31\u77e5\u9053\u7edd\u6ca1\u6709\u4eba\u4f1a\u76f8 \u4fe1\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u7684\u79d8\u5bc6 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u79d8\u5bc6\u3002 \u3000\u3000\u738b\u52a8\u662f\u4eba\u3002 \u3000\u3000\u6240\u4ee5\u5929\u52a8\u4e5f\u6709\u79d8\u5bc6\u3002 \u3000\u3000\u8c61\u738b\u52a8\u8fd9\u79cd\u4eba\u5c45\u7136\u4e5f\u4f1a\u6709\u79d8\u5bc6\u4e5f\u662f\u4ef6\u5f88\u96be\u4ee4\u4eba\u76f8\u4fe1\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u4ece\u6ca1\u6709\u5355\u72ec\u884c\u52a8\u8fc7\xb7\u751a\u81f3\u8fde\u4e0b\u5e8a\u7684\u65f6\u5019\u90fd\u5f88\u5c11\u3002 \u3000\u3000\u71d5\u4e03\u672c\u6765\u4e5f\u8fde\u505a\u68a6\u90fd\u4e0d\u4f1a\u60f3\u5230\u4ed6\u6709\u79d8\u5bc6\u3002 \u3000\u3000\u4f46\u7b2c\u4e00\u4e2a\u53d1\u73b0\u58eb\u52a8\u6709\u79d8\u5bc6\u7684\u4eba\u5c31\u662f\u71d5\u4e03\u3002 \u3000\u3000\u4ed6\u662f\u600e\u9ebd\u53d1\u73b0\u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u7b2c\u2500\u6b21\u53d1\u73b0\u8fd9\u79d8\u5bc6\u662f\u56e0\u4e3a\u4ed6\u770b\u5230\u4e86\u6837\u5f88\u5947\u602a\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u4e86\u53ea\u98ce\u7b2c\u3002 \u3000\u3000\u98ce\u7b2c\u5e76\u5c0f\u5947\u602a\u4f97\u4ece\u8fd9\u53ea\u98ce\u7b49\u4e0a\u5374\u5f15\u8d77\u4e86\u8bb8\u8bb8\u591a\u591a\u5f88\u5947\u602a\u3001\u5f88\u60ca\u4eba\u5177\u81f3\u53ef\u4ee5\u8bf4\u662f\u5f88\u65f6\u6015\u7684 \u4e8b\u3002 \u3000\u3000\u6309\u5b63\u8282\u6765\u8bf4\u73b0\u5728\u5e94\u8be5\u5df2\u7ecf\u662f\u6625\u5927\u4e86\u4f46\u968f\u4fbf\u4f60\u5de6\u770b\u53f3\u770b\u4e1c\u770b\u897f\u770b\u8fd8\u662f\u770b\u4e0d\u5230\u6709\u70b9\u6625\u5929\u7684\u5f71 \u6237\u3002 \u3000\u3000\u5929\u6c14\u8fd8\u662f\u72e0\u51b7\u98ce\u8fd8\u662f\u5f88\u5927\xb7\u5730\u4e0a\u7684\u79ef\u96ea\u8fd8\u6709\u4e03\u516b\u5bf8\u539a\u3002 \u3000\u3000\u8fd9\u5927\u96be\u5f97\u7adf\u6709\u592a\u9633\u3002 \u3000\u3000\u738b\u52a8\u3001\u71d5\u4e03\u3001\u90ed\u5927\u8def\u3001\u6797\u592a\u5e73\u90fd\u5728\u9662\u5b50\u91cc\u6652\u592a\u9633\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u8c61\u522b\u7684\u90a3\u4e9b\u7a77\u5149\u86cb\u6837\u4ece\u4e0d\u613f\u610f\u653e\u5f03\u6652\u592a\u9633\u7684\u59da\u53f0 \u3000\u3000\u5728\u5bd2\u51b7\u7684\u51ac\u5929\u91cc\u6652\u592a\u9633\u5df2\u53ef\u7b97\u662f\u7a77\u4eba\u4eec\u6709\u9650\u7684\u51e0\u79cd\u4eab\u53d7\u4e4b\u3002 \u3000\u3000\u5de5\u52a8\u627e\u4e86\u5f20\u6700\u8212\u670d\u7684\u6905\u5b50\xb7\u61d2\u6d0b\u6d0b\u7684\u534a\u7ef8\u5728\u5c4b\u7a3d\u535c\u9762\u3002 \u3000\u3000\u6797\u592a\u5e73\u5750\u5728\u65c1\u8fb9\u7684\u77f3\u9636\u535c\u624b\u6367\u5934\u773c\u775b\u53d1\u76f4\u4e0d\u77e5\u9053\u5728\u60f3\u4ec0\u9ebd\u5fc3\u4e8b\u3002 \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u4e00\u76f4\u90fd\u72e0\u5947\u602a\u4ed6\u5df2\u77e5\u9053\u6797\u592a\u5e73\u5728\u60f3\u4ec0\u9ebd\u3002 \u3000\u3000\u53ef\u662f\u71d5\u4e03\u7684\u79d8\u5bc6\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u53c8\u5c06\u71d5\u4e03\u6084\u6084\u62c9\u5230\u65c1\xb7\u9053\u201c\u4f60\u90a3\u79d8\u5bc6\u73b0\u5728\u603b\u53ef\u4ee5\u544a\u8bc9\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u81ea\u4ece\u56de\u6765\u4e4b\u5f8c\xb7\u8fd9\u5df2\u662f\u4ed6\u7b2c\u4e03\u5341\u516b\u6b21\u95ee\u71d5\u4e03\u8fd9\u53e5\u8bdd\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u7684\u56de\u7b54\u8fd8\u662f\u8ddf\u4ee5\u524d\u6837\u3002 \u3000\u3000\u201c\u7b49\u7b49\u201c \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8981\u6211\u7b49\u5230\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b49\u5230\u6211\u60f3\u8bf4\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6025\u9053\u201c\u4f60\u96be\u9053\u5b9a\u8981\u7b49\u5230\u6211\u5feb\u6b7b\u7684\u65f6\u5019\u624d\u80af\u8bf4\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8198\u4e86\u4ed6\u773c\u773c\u795e\u504f\u504f\u53d8\u5f97\u5947\u602a\u8fc7\u4e86\u5f88\u4e45\u624d\u5e7d\u5e7d\u9053\u201c\u4f60\u771f\u4e0d\u7ec6\u9053\u6211\u8981\u544a\u8bc9\u4f60\u7684\u79d8\u5bc6\u662f\u4ec0 \u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u77e5\u9053\xb7\u53c8\u4f55\u5fc5\u95ee\u4f60\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53c8\u770b\u5382\u4ed6\u5f88\u4e45\u5ffd\u7136\u6696\u9648\u7b11\xb7\u6447\u5934\u9053\u201c\u738b\u8001\u5927\u8bf4\u7684\u771f\u4e0d\u9519\xb7\u8fd9\u4eba\u8be5\u7cca\u6d82\u7684\u65f6\u5019\u806a\u660e\u8be5 \u806a\u660e\u7684\u65f6\u5019\u4ed6\u5374\u6bd4\u8c01\u90fd\u6eaf\u5f90\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53c8\u4e0d\u662f\u4f60\u809a\u91cc\u7684\u86d4\xb7\u600e\u77e5\u9053\u4f60\u7684\u79d8\u5bc6\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u53c8\u8f7b\u8f7b\u53f9\u606f\u5382\u58f0\xb7\u9053\u201c\u4e5f\u8bb8\u5f90\u4e0d\u77e5\u9053\u53cd\u800c\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u54ea\u70b9\u597d\uff1f\u201d \u3000\u3000\u85d0\u4e03\u9053\u201c\u6709\u54ea\u70b9\u4e0d\u597d\uff1f\u6211\u4ecd\u73b0\u5728\u8fd9\u6837\u5b50\u4e0d\u662f\u8fc7\u5f97\u5f88\u5f00\u5fc3\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u77e5\u9053\u5f8c\u96be\u9053\u5c31\u4f1a\u53d8\u5f97\u4e0d\u5f00\u5fc3\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8f7b\u8f7b\u53f9\u606f\u9053\u201c\u4e5f\u8bb8\u2500\u4e00\u201d\u4e5f\u8bb8\u6211\u4eec\u5c31\u4f1a\u53d8\u5f97\u5929\u5929\u8981\u5435\u5634\u5929\u5929\u8981\u5455\u6c14\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u4ed6\u91cd\u91cd\u8dfa\u4e86\u8dfa\u811a\xb7\u6068\u6068\u9053\u201c\u6211\u771f\u5f04\u4e0d\u61c2\u4f60\uff0c\u4f60\u660e\u54bd\u662f\u4e2a\u5f88\u75db\u5feb\u7684\u4eba\uff0c\u4f46\u6709\u65f6\u5374 \u7b80\u76f4\u6bd4\u5973\u4eba\u8fd8\u626d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u626d\u7684\u662f\u4f60\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u6709\u4ec0\u9ebd\u626d\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4eba\u5bb6\u4e0d\u613f\u610f\u505a\u7684\u4e8b\u4f53\u4e3a\u4ec0\u9ebd\u504f\u504f\u8981\u4eba\u5bb6\u505a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9762\u201c\u4eba\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4eba\u5bb6\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u7528\u624b\u62b1\u4f4f\u5934\xb7\u66f0\u8d2d\u9053\u201c\u660e\u660e\u662f\u4ed6\u4ed6\u80e1\u504f\u504f\u8981\u8bf4\u662f\u4eba\u5bb6\u3002\u8fd9\u4eba\u8fde\u8bf4\u8bdd \u7684\u80f6\u8c03\u90fd\u53d8\u5f97\u8d8a\u6765\u8d8a\u8c61\u5973\u4eba\u4e86\xb7\u4f60\u8bf4\u8fd9\u600e\u9ebd\u5f97\u4e86\u3002\u201d \u3000\u3000\u853d\u4e03\u5ffd\u53c8\u5a7f\u7136\u7b11\xb7\u6545\u610f\u6539\u53d8\u4e86\u8bdd\u9898\u9053\u201c\u4f11\u60f3\u6d3b\u5265\u76ae\u4e3a\u4ec0\u9ebd\u4f1a\u5ffd\u7136\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u4e0d\u60f3\u56de\u7b54\u8fd9\u53e5\u8bdd\u4f46\u618b\u4e86\u534a\u5929\u8fd8\u662f\u5fcd\u4e0d\u4f4f\xb7\u9053\u201c\u4e0d\u662f\u4ed6\u81ea\u5df1\u60f3\u8d70\uff0c\u662f\u90a3\u8001\u592a\u5a46 \u903c\u4ed6\u8d70\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u90a3\u8001\u592a\u5a46\u751f\u6015\u6211\u6253\u8ffd\u67e5\u5979\u7684\u8eab\u4efd\u6765\u5386\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u9ebd\u6837\u770b\u6765\u5979\u7684\u8eab\u4efd\u5b9a\u5f88\u79d8\u5bc6\xb7\u548c\u6d3b\u5265\u76ae\u4e4b\u95f4\u7684\u5173\u7cfb\u4e5f\u5b9a\u5f88\u7279\u522b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54bd\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u53bb\u6253\u542c\u6253\u542c\uff0c\u4ed6\u4eec\u8eb2\u5230\u54ea\u91cc\u53bb\u6269\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u6253\u542c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53bb\u53d1\u6398\u4ed6\u4eec\u7684\u79d8\u5bc6\u5440\u3002\u201d\u90ed\u5927\u8def\u9053\u54e6\u4e3a\u4ec0\u9ebd\u8981\u53bb\u53d1\u6398\u522b\u4eba\u7684\u79d8\u5bc6\uff1f\u6709\u4e9b\u79d8\u5bc6\u4f60 \u968f\u4fbf\u7528\u4ec0\u9ebd\u6cd5\u4e8e\u90fd\u53d1\u6398\u4e0d\u51fa\u7684\u4f97\u7b49\u5230\u4e86\u65f6\u5019\u4f60\u4e0d\u7528\u53d1\u6398\u4e5f\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e49\u7b11\u5382\u7b11\u9053\u201c\u4f60\u65e2\u7136\u660e\u767d\u8fd9\u9053\u7406\u4e3a\u4ec0\u9ebd\u8fd8\u603b\u662f\u903c\u6211\u8bf4\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u4ed6\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u56e0\u4e3a\u6211\u5173\u5fc3\u7684\u4e0d\u662f\u90a3\u8001\u592a\u5a46\xb7\u56e0\u4e3a\u6211\u53ea\u5173\u5fc3\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u6162\u6162\u7684\u8f6c\u8fc7\u5934\uff0c\u4eff\u62c2\u6545\u610f\u907f\u5f00\u90ed\u5927\u8def\u7684\u76ee\u5149\u3002 \u3000\u3000\u5979\u521a\u8f6c\u8fc7\u5934\uff0c\u5c31\u770b\u5230\u2018\u53ea\u98ce\u7b2c\u3002 \u3000\u3000\u53ea\u5927\u879f\u86c6\u98ce\u7b2c\xb7\u505a\u5f97\u53c8\u7cbe\u5de7\u3001\u4e49\u903c\u771f\u5728\u84dd\u5929\u65e5\u4e91\u95f4\u76d8\u65cb\u5f3a\u821e\uff0c\u770b\u6765\u7b80\u76f4\u5c31\u8c61\u662f\u6d3b\u7684\u3002 \u3000\u3000\u71d5\u4e03\u62cd\u624b\u7b11\u9053\u201c\u4f60\u770b\xb7\u90a3\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u770b\u89c1\u5382\u4e5f\u89c9\u5f97\u5f88\u6709\u8da3\u5374\u6545\u610f\u677f\u8138\u9053\u201c\u90a3\u53ea\u4e0d\u8fc7\u662f\u4e2a\u98ce\u7b49\u4e3d\u5df2\u6709\u4ec0\u9ebd\u597d\u7a00\u5947\u7684\u4f60 \u96be\u9053\u8fde\u98ce\u7b2c\u90fd\u6ca1\u89c1\u8fc7\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f69\u5728\u8fd9\u79cd\u65f6\u5019\u600e\u9ebd\u4f1a\u6709\u4eba\u653e\u98ce\u7b2c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8c08\u6de1\u9053\u201c\u53ea\u8981\u4eba\u5bb6\u9ad8\u5174\u968f\u4fbf\u4ec0\u9ebd\u65f6\u5019\u90fd\u53ef\u4ee5\u653e\u98ce\u7b2c\u7684\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u5f53\u7136\u4e5f\u77e5\u9053\xb7\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u653e\u98ce\u7b2c\u7684\u65f6\u5019\u5c31\u7b97\u6709\u4eba\u8981\u653e\u4e5f\u5b9a\u653e\u4e0d\u9ad8\u751a\u81f3\u6839\u672c\u653e\u4e0d \u8d77\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u53ea\u98ce\u7ba9\u5374\u653e\u5f97\u5f88\u9ad8\u3001\u5f88\u76f4\xb7\u653e\u98ce\u7b2c\u7684\u4eba\u663e\u7136\u662f\u6b64\u4e2d\u9ad8\u624b\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f11\u4f1a\u4e0d\u4f1a\u505a\u98ce\u7b2c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u4f1a\uff0c\u6211\u53ea\u4f1a\u5403\u996d\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u5382\u7728\u773c\uff0c\u7b11\u9053\u201c\u738b\u8001\u5927\u5b9a\u4f1a\u2500\u4e00\u3002\u738b\u8001\u5927\uff0c\u6211\u4eec\u4e5f\u505a\u4e2a\u98ce\u7b2c\u653e\u653e\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4ed6\u51b2\u5230\u738b\u52a8\u9762\u524d\xb7\u5ffd\u7136\u6014\u4f4f\u3002 \u3000\u3000\u738b\u52a8\u6839\u672c\u6ca1\u6709\u542c\u89c1\u4ed6\u5728\u8bf4\u4ec0\u9ebd\u53ea\u662f\u77aa\u5927\u4e86\u773c\u775b\u76f4\u52fe\u52fe\u7684\u770b\u90a3\u53ea\u98ce\u7b2c\xb7\u76ee\u4e2d\u7684\u795e\u8272\u975e\u5e38\u5947 \u7279\u597d\u50cf\u662f\u4ece\u6765\u6ca1\u770b\u89c1\u8fc7\u98ce\u7b2c\u4f3c\u7684\u3002 \u3000\u3000\u770b\u4ed6\u8138\u4e0a\u7684\u795e\u8272\xb7\u7b80\u76f4\u5c31\u597d\u50cf\u62ff\u8fd9\u98ce\u7b2c\u5f53\u505a\u4e2a\u771f\u7684\u5a31\u8681\u3002 \u3000\u3000\u4f1a\u5403\u4eba\u7684\u5927\u6bbf\u8231\u3002 \u3000\u3000\u85d0\u4e03\u4e5f\u6014\u4f4d\xb7\u5411\u4e3a\u4ed6\u77e5\u9053\u5e72\u52a8\u7edd\u4e0d\u662f\u4e2a\u5bb9\u6613\u88ab\u60ca\u5413\u7684\u4eba\u3002 \u3000\u3000\u5c31\u7b97\u771f\u7684\u770b\u5230\u4e03\u516b\u5341\u6761\u6d3b\u751f\u751f\u7684\u879f\u86c6\u5728\u9762\u524d\u722c\u6765\u722c\u53bb\uff0c\u738b\u52a8\u8138\u4e0a\u7684\u989c\u8272\u4e5f\u7edd\u4e0d\u4f1a\u6539\u53d8 \u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u7684\u80a1\u770b\u6765\u5374\u8c61\u662f\u5f20\u81ea\u7eb8\u3002 \u3000\u3000\u7a81\u7136\u95ee\u4ed6\u773c\u89d2\u7684\u808c\u8089\u8df3\u4e86\u2606\u5382\u5c31\u8c61\u662f\u88ab\u9488\u523a\u4f3c\u7684\u3002 \u3000\u3000\u71d5\u4e03\u62ac\u8d77\u5934\xb7\u5c31\u53d1\u89c9\u5929\u4e0a\u53c8\u591a\u4e86\u56db\u53ea\u98ce\u7ee9\u3002 \u3000\u3000\u53ea\u662f\u86c7\u53ea\u662f\u874e\u5b50\u53ea\u662f\u8001\u9e70\u3002 \u3000\u3000\u6700\u5927\u7684\u53ea\u98ce\u7b2c\u5374\u662f\u56db\u56db\u65b9\u5206\u7684\u9ec4\u8272\u7684\u98ce\u7b49\u4e0a\u7528\u6731\u7b14\u5f2f\u5f2f\u66f2\u66f2\u7684\u753b\u4e9b\u8c01\u4e5f\u770b\u4e0d\u61c2\u7684\u7b26\u5f55\u5c31 \u50cf\u662f\u9b3c\u753b\u7b26\u3002 \u3000\u3000\u738b\u52a8\u7a81\u7136\u7ad9\u8d77\u6765\u8ddf\u7f00\u8dea\u8def\u7684\u51b2\u4eba\u5c4b\u91cc\u53bb\u770b\u6765\u5c31\u8c61\u662f\u5df2\u652f\u6301\u4e0d\u4f4f\xb7\u968f\u65f6\u90fd\u4f1a\u6655\u5012\u7684\u6837\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u8d70\u8fc7\u6765\u4e86\u8138\u4e0a\u4e5f\u5e26\u8be7\u5f02\u4e4b\u8272\u9053\u201c\u738b\u8001\u5927\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u8c01\u77e5\u9053\u4ed6\u662f\u600e\u9ebd\u56de\u4e8b\u770b\u89c1\u8fd9\u4e9b\u98ce\u7b2c\xb7\u4ed6\u6574\u4e2a\u4eba\u5c31\u597d\u50cf\u5ffd\u7136\u53d8\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u66f4\u5947\u602a\uff0c\u9053\u2606\u770b\u89c1\u98ce\u7b2c\uff0c\u4ed6\u7684\u6837\u4e86\u5c31\u53d8\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5631\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u76b1\u76b1\u7709\u9053\u201c\u8fd9\u4e9b\u98ce\u7b2c\u96be\u9053\u6709\u4ec0\u9ebd\u7279\u522b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u4ed6\u59cb\u8d77\u5934\uff0c\u770b\u5929\u4e0a\u7684\u98ce\u7b2c\u4ed4\u7ec6\u7814\u7a76\u4e86\u5f88\u4e45\u8fd8\u662f\u8fde\u2018\u70b9\u7ed3\u679c\u90fd\u6ca1\u6709\u7814\u7a76\u51fa\u6765\u3002 \u3000\u3000\u8c01\u4e5f\u6c72\u6cd5\u5b50\u7814\u7a76\u51fa\u4ec0\u9ebd\u7ed3\u679c\u6765\u3002 \u3000\u3000\u98ce\u7b2c\u5c31\u662f\u98ce\u7b2c\u5e76\u6ca1\u6709\u4ec0\u9ebd\u4e0d\u540c\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u5982\u8fdb\u53bb\u95ee\u95ee\u738b\u8001\u5927\xb7\u95ee\u4ed6\u8fd9\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u6447\u6447\u5934\xb7\u53f9\u9053\u95ee\u4e86\u4e5f\u662f\u767d\u95ee\u4ed6\u7edd\u4e0d\u53ef\u80fd\u8bf4\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u8fd9\u4e9b\u98ce\u7b2c\u2026\u2500\u201c \u3000\u3000\u71d5\u4e03\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\xb7\u9053\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u95ee\u9898\u5e76\u4e0d\u5728\u8fd9\u4e9b\u98ce\u519b \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8ba4\u4e3a\u95ee\u9898\u51fa\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u653e\u98ce\u7b2c\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e00\u62cd\u5df4\u638c\u9053\u201c\u4e0d\u9519\u738b\u8001\u5927\u4e5f\u8bb8\u77e5\u9053\u662f\u8c01\u5728\u653e\u98ce\u7b49\u3002\u201d\u71d5\u4e03\u9053\u201c\u90a3\u4e9b\u4eba\u4e5f\u8bb8\u662f\u738b\u8001 \u5927\u4ee5\u524d\u7ed3\u4e0b\u7684\u51a4\u5bb6\u5bf9\u5934\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u76f4\u5728\u65c1\u8fb9\u542c\u5ffd\u7136\u9053\u201c\u6211\u53bb\u770b\u770b\u4f60\u4eec\u5728\u8fd9\u91cc\u7b49\u6211\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\u4ed6\u7684\u4eba\u5df2\u5ac1\u51fa\u5899\u5916\u3002 \u3000\u3000\u4ed6\u5e73\u65f6\u4e3e\u52a8\u867d\u90fd\u662f\u6162\u5f97\u541e\u7684\u4f46\u771f\u9047\u4e0a\u4e8b\uff0c\u4ed6\u7684\u52a8\u4f5c\u6bd4\u6dee\u90fd\u5feb\u3002\u90ed\u5927\u8def\u770b\u4e86\u770b\u71d5\u4e03\uff0c\u9053 \u201c\u6211\u4eec\u4e3a\u4ec0\u9ebd\u8981\u5728\u8fd9\u91cc\u7b49\u4ed6\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u4e0d\u7b49\u4ed6\u8fd9\u5207\u8bf4\u8bdd\u5b8c\u4e5f\u5df2\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e3a\u5382\u670b\u53cb\u7684\u4e8b\u4ed6\u4eec\u662f\u8c01\u4e5f\u4e0d\u80af\u843d\u5728\u522b\u4eba\u5f8c\u5934\u7684\u3002\u201d \u3000\u3000\u98ce\u7b49\u6548\u5f97\u5f88\u9ad8\u5f88\u76f4\u3002 \u3000\u3000\u71d5\u4e03\u6253\u5458\u529b\u5411\u9053\u201c\u770b\u4f6f\u5382\u8fd9\u4e9b\u98ce\u7b2c\u662f\u4ece\u575f\u573a\u91cc\u653e\u4e0a\u53bb\u7684\u3002\u6211\u5c0f\u65f6\u5019\u4e5f\u5e38\u5728\u575f\u573a\u91cc\u653e\u98ce \u7b2c\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u70b9\u70b9\u5934\u9053\u201c\u6211\u5c0f\u65f6\u5019\u4e5f\u5e38\u5728\u575f\u573a\u91cc\u653e\u98ce\u7b2c\u3002\u201d\u201c\u5bcc\u8d35\u5c71\u5e84\u201d\u538b\u79bb\u575f\u573a\u5e76\u4e0d\u592a\u8fdc\u4ed6 \u4eec\u5f88\u5feb\u5c31\u5df2\u8d76\u5230\u90a3\u91cc\u3002\u653b\u573a\u91cc\u552f\u521b\u4e2a\u4eba\u5c31\u662f\u6797\u592a\u5e73\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u770b\u89c1\u4e86\u4ec0\u9ebd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6ca1\u6709\u8fde\u4e2a\u9b3c\u5f71\u5b50\u90fd\u6ca1\u6709\u770b\u89c1\u3002\u201d \u3000\u3000\u98ce\u7b54\u662f\u8c01\u653e\u4e0a\u53bb\u7684\u5462\uff1f \u3000\u3000\u4e94\u4e2a\u7a3b\u8349\u4eba\u3002 \u3000\u3000\u4e94\u4e2a\u62ab\u9ebb\u5e26\u5b5d\u7684\u7528\u8349\u4eba\uff0c\u53ea\u624b\u8fd8\u63d0\u6839\u54ed\u4e27\u5f8b\u3002 \u3000\u3000\u98ce\u7b2c\u7684\u7ebf\xb7\u5c31\u7cfb\u5728\u6f9c\u8349\u4eba\u7684\u53e6\u53ea\u624b\u4e0a\u3002 \u3000\u3000\u7a3b\u8349\u4eba\u5f53\u7136\u4eba\u2606\u4f1a\u653e\u98ce\u7b49\u3002 \u3000\u3000\u7a3b\u8349\u4eba\u4e5f\u4ece\u4e2a\u88ab\u9ebb\u5e26\u5b5d\u7684\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u6545\u5f04\u7384\u865a\uff1f \u3000\u3000\u90ed\u5927\u8def\u4ed6\u4eec\u5bf9\u671b\u4e86\u2018\u773c\u5df2\u53d1\u89c9\u8fd9\u4ef6\u4e8b\u8d8a\u6765\u8d8a\u5c0f\u7b80\u5355\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u98ce\u7b2c\u521a\u653e\u535c\u53bb\u6ca1\u591a\u4e45\u4ed6\u4eec\u7684\u4eba\u4e5f\u8bb8\u8fd8\u6ca1\u6709\u8d70\u8fdc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u5bf9\u6211\u4eec\u5230\u56db\u9762\u53bb\u627e\u627e\u770b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u4eec\u60f3\u5fc5\u6709\u4e94\u4e2a\u4eba\u6211\u4eec\u5458\u597d\u4e5f\u4e0d\u8981\u843d\u5355\u3002\u201d \u3000\u3000\u4ed6\u4eec\u56f4\u575f\u573a\u7ed5\u4e86\u5708\xb7\u53c8\u770b\u5230\u5c71\u5761\u4e0b\u7684\u90a3\u95ee\u5c0f\u6728\u5c4b\u3002 \u3000\u3000\u4ed6\u4eec\u5c31\u662f\u5728\u8fd9\u5c0f\u6728\u5c4b\u91cc\u627e\u5230\u9178\u6885\u6c64\u7684\u3002 \u3000\u3000\u201c\u653e\u98ce\u7b2c\u7684\u90a3\u5428\u4eba\u4f1a\u4e0d\u4f1a\u8eb2\u5728\u8fd9\u5c0f\u6728\u5c4b\u91cc\uff1f\u201d \u3000\u3000\u53c1\u4e2a\u4eba\u5fc3\u91cc\u4e0d\u7ea6\u800c\u540c\u90fd\u5728\u8fd9\u9ebd\u60f3\xb7\u7ff1\u5927\u8def\u5df2\u7b2c\u4e2a\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u5931\u58f0\u9053\u201c\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u51fa\u95e8\u90ed\u5927\u8def\u5df2\u8e22\u5f00\u95e8\u95ef\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6728\u5c4b\u8fd8\u662f\u90a3\u6728\u5c4b\u606b\u6728\u5c5e\u91cc\u5374\u5df2\u5b8c\u5168\u53d8\u4e86\u6837\u5b50\u3002 \u3000\u3000\u9178\u6885\u6c64\u5728\u8fd9\u91cc\u70e7\u996d\u7528\u7684\u9505\u7076\u73b0\u5728\u5df2\u5168\u4e0d\u89c1\u4e86\u672c\u6765\u5f88\u810f\u4e71\u7684\u95ee\u5c0f\u6728\u5c4b\u73b0\u5728\u5c45\u7136\u5df2\u88ab\u6253\u5987\u5f97 \u5e72\u5341\u51c0\u51c0\u8fde\u4e00\u70b9\u7070\u5c18\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c4b\u4e88\u6b63\u4e2d\u6446\u5f20\u684c\u4e8e\u3002 \u3000\u3000\u684c\u5b50\u4e0a\u6446\u4e94\u53cc\u7b77\u5b50\u4e94\u53ea\u9152\u676f\uff0c\u8fd8\u6709\u4e94\u67c4\u7cbe\u5149\u8000\u670d\u7684\u5c0f\u5200\u3002 \u3000\u3000\u5200\u5203\u8584\u800c\u950b\u5229\u5200\u8eab\u5f2f\u66f2\u5f62\u72b6\u5f88\u594b\u7279\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\u5c4b\u6237\u91cc\u5c31\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u521a\u62ff\u8d75\u67c4\u5200\u5728\u770b\u71d5\u4e03\u5df2\u83e0\u5382\u8fdb\u6765\u8dfa\u811a\u9053\u201c\u4f60\u505a\u4e8b\u600e\u9ebd\u8fd8\u662f\u8fd9\u9ebd\u7c97\u5fc3\u5927\u610f\u968f\u968f\u4fbf\u4fbf \u5c31\u95ef\u4e86\u8fdb\u6765\u5c4b\u5b50\u91cc\u767e\u6709\u4eba\u5462\uff1f\u4f60\u96be\u9053\u5c31\u4e0d\u6015\u522b\u4eba\u6697\u7b97\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u4e0d\u6015\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e0d\u6015\u6211\u6015\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u51fa\u53e3\u4ed6\u81ea\u5df1\u7684\u8138\u5ffd\u7136\u7ea2\u4e86\xb7\u7ea2\u5f97\u5389\u5bb3\u3002 \u3000\u3000\u7387\u597d\u522b\u4eba\u90fd\u6ca1\u6709\u5207\u610f\u3002 \u3000\u3000\u6797\u592a\u5e73\u672c\u6765\u4e5f\u5728\u7814\u7a76\u684c\u4e0a\u7684\u5200\u6b64\u523b\u5ffd\u7136\u9053\u201c\u8fd9\u5200\u662f\u5272\u8089\u7528 \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u54e6\u89c1\u8fc7\u585e\u5916\u7684\u80e1\u4eba\u6700\u559c\u6b22\u7528\u8fd9\u79cd\u5200\u5272\u8089\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u4ed6\u4eec\u96be\u9053\u662f\u6765\u81ea\u585e\u5916\u7684\u80e1\u4eba\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u6c89\u541f\u9053\u201c\u4e5f\u6709\u65f6\u80fd\u53ea\u4e0d\u8fc7\u80e1\u4eba\u53ea\u7528\u5200\uff0c\u4e0d\u7528\u7b77\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u767d\u4e2d\u5ffd\u7136\u63a0\u8fc7\u9635\u60ca\u6050\u4e4b\u610f\u9053\u201c\u8fd9\u91cc\u53ea\u6709\u5200\u6ca1\u6709\u8089\u4ed6\u4eec\u51c6\u5907\u8346\u4ec0\u9ebd\u8089\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053 \u201c\u603b\u4e0d\u4f1a\u662f\u51c6\u5907\u5272\u738b\u52a8\u8235\u8089\u5427\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u5728\u7b11\u4f46\u7b11\u5f97\u5df2\u5f88\u4e0d\u81ea\u7136\u3002 \u3000\u3000\u71d5\u4e03\u597d\u50cf\u5fcd\u4e0d\u4f73\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u563f\uff0c\u9053\u201c\u6211\u4eec\u8fd8\u662f\u8d76\u5feb\u56de\u53bb\u5427\u53ea\u7559\u535c\u738b\u8001\u5927\u4e2a\u4eba\u5728\u5bb6\u91cc \u6211\u5b9e\u5728\u6709\u70b9\u5c0f\u653e\u5fc3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53d8\u8272\u9053\u201c\u5bf9\u6211\u4eec\u83ab\u8981\u4e2d\u5382\u522b\u4eba\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u613f\u5230\u8fd9\u91cc\uff0c\u53c1\u4e2a\u4eba\u540c\u65f6\u51b2\u5382\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u7528\u6700\u5feb\u7684\u901f\u5ea6\u63a0\u8fc7\u575f\u573a\uff0c\u71d5\u4e03\u7a81\u53c8\u505c\u4e0b\u6765\u5931\u58f0\u9053\u201c\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u4ec0\u9ebd\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8138\u8272\u53d1\u767d\u9053\u201c\u90a3\u4e94\u4e2a\u7a3b\u8349\u4eba\u521a\u597d\u50cf\u5c31\u5728\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u4e5f\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u9178\u3002 \u3000\u3000\u90a3\u8fc1\u4e2a\u7a3b\u8349\u4eba\u521a\u7684\u786e\u662f\u5728\u8fd9\u91cc\u7684\uff0c\u4f46\u73b0\u5728\u5df4\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u84dd\u5929\u767d\u4e91\u590f\u662f\u96be\u5f97\u7684\u597d\u5929\u6c14\u3002 \u3000\u3000\u4f46\u5929\u4e0a\u7684\u98ce\u7b49\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7528\u6700\u5feb\u7684\u901f\u5ea6\u8dd1\u56de\u53bb\u5230\u4e86\u95e8\u53e3\u53c8\u6014\u4f50\u3002 \u3000\u3000\u4e94\u4e2a\u7a3b\u8349\u4eba\u8d6b\u7136\u5728\u4ed6\u4eec\u95e8\u53e3\xb7\u8fd8\u662f\u62ab\u9ebb\uff0c\u6234\u5b5d\uff0c\u624b\u91cc\u8fd8\u662f\u63d0\u54ed\u4e27\u6387\xb7\u53ea\u4e0d\u8fc7\u80f8\u53e3\u5374\u591a\u5382 \u5f20\u7eb8\u6761\u5b50\u4e0a\u9890\u8fd8\u597d\u50cf\u5199\u4e2d\u3002 \u3000\u3000\u5f88\u5c0f\u7684\u5b57\u5f88\u96be\u770b\u7684\u6e05\u3002 \u3000\u3000\u98ce\u5439\u7eb8\u6761\u5b50\u5c31\u88ab\u5439\u5f97\u7b56\u7b4b\u76f4\u54cd\u53c8\u597d\u50cf\u662f\u7528\u9488\u7ebf\u7f1d\u5728\u7a20\u9769\u4eba\u7684\u9ebb\u8863\u4e0a\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u7b2c\u4e2a\u8d76\u5230\u4f38\u624b\u5c31\u53bb\u5904\u3002 \u3000\u3000\u7eb8\u6761\u5b50\u5c45\u7136\u7f1d\u5f97\u5f88\u4e2d\u4ed6\u7528\u4e86\u70b9\u529b\u624d\u603b\u7b97\u5c06\u5b83\u626f\u4e86\u535c\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u5239\u90a3\u95ee\xb7\u7a3b\u8349\u4eba\u624b\u91cc\u63d0\u7684\u54ed\u4e27\u68d2\u4e5f\u7a81\u7136\u8e72\u8d77\u5411\u6797\u592a\u5e73\u7684\u8179\u90e8\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7387\u597d\u6797\u592a\u5e73\u7ecf\u9a8c\u867d\u5dee\xb7\u53cd\u5e94\u5374\u4e0d\u9992\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\u5df4\u5c06\u54ed\u4e27\u68d2\u907f\u5f00\u3002 \u3000\u3000\u8c01\u77e5\u54ed\u4e27\u68d2\u5f39\u8d77\u6765\u65f6\u68d2\u5934\u4e0a\u8fd8\u6709\u2026\u70b9\u4e4c\u5149\u6253\u5382\u51fa\u6765\u3002 \u3000\u3000\u6797\u592a\u5e73\u53ea\u907f\u5f00\u5382\u54ed\u4fc3\u68d2\u5374\u597d\u50cf\u672a\u907f\u5f00\u54ed\u4e27\u68d2\u7684\u6697\u5668\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u53f3\u8fb9\u80ef\u9aa8\u4ea1\u9ebb\u597d\u50cf\u88ab\u868a\u5b50\u76ef\u4e86\u53e3\u4f3c\u7684\u3002 \u3000\u3000\u7b49\u4ed6\u843d\u5230\u5730\u4e0a\u65f6\u4eba\u7adf\u5df2\u7ad9\u4e0d\u4f4f\u4e2b\u3002 \u3000\u3000\u7728\u963b\u95f4\u6761\u53f3\u817f\u5df2\u53d8\u5f97\u5b8c\u5168\u9ebb\u6728\uff0c\u4ed6\u8eab\u5b50\u4e5f\u5012\u4e86\u4e0b\u53bb \u3000\u3000\u90ed\u5927\u8def\u53d8\u8272\u9053\u201c\u6bd2\u9488\u201d \u3000\u3000\u4ed6\u5171\u624d\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff0c\u8fd9\u4e24\u4e2a\u5b57\u8bf4\u5b8c\u71d5\u4e03\u5df2\u51fa\u624b\u5976\u98ce\xb7\u5c06\u6797\u592a\u5e73\u53f3\u8fb9\u80ef\u9aa8\u4e0a\u51f9\u9762\u7684\u7a74\u9053\u5168 \u90fd\u70b9\u4f4d\u53e6\u53ea\u624b\u5df2\u81fc\u9774\u7b80\u91cc\u6cb9\u51fa\u67c4\u4e59\u9996\u3002 \u3000\u3000\u5200\u5149\u95ea\uff0c\u6797\u592a\u5e73\u7684\u8863\u88f3\u5df2\u88ab\u5272\u5f00\uff0c\u518d\u95ea\uff0c\u5df2\u5c06\u6797\u592a\u4e2d\u4f24\u53ea\u7684\u90a3\u5757\u8089\u6316\u4e86\u51fa\u6765\xb7\u9c9c\u8840\u968f\u6e85 \u51fa\u3002 \u3000\u3000\u9ed1\u8272\u7684\u8840 \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u90fd\u770b\u76f4\u4e86\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u71d5\u4e03\u5e94\u53d8\u7adf\u5982\u6b64\u6289\xb7\u51fa\u624b\u66f4\u6289\u3002 \u3000\u3000\u201c\u6211\u5df2\u6b7b\u8fc7\u4e03\u6b21\u3002\u201d\xb7 \u3000\u3000\u76f4\u5230\u73b0\u5728\xb7\u90ed\u5927\u8def\u624d\u76f8\u4fe1\u71d5\u4e03\u8fd9\u53e5\u8bdd\u4e0d\u5047\u3002 \u3000\u3000\u53ea\u6709\u6b7b\u8fc7\u4e03\u6b21\u7684\u4eba\u624d\u80fd\u6709\u8fd9\u9ebd\u5feb\u7684\u5e94\u53d8\u529b\u8fd9\u9ebd\u4e30\u5bcc\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u6797\u592a\u5e73\u5df2\u75bc\u5f97\u51b7\u6c57\u90fd\u6d41\u4e86\u51fa\u6765\u2606\u4f46\u8fd8\u662f\u6ca1\u6709\u5fd8\u8bb0\u624b\u91cc\u7684\u90a3\u7eb8\u6761\u3002 \u3000\u3000\u4ed6\u54ac\u7d27\u7259\u6839\xb7\u5598\u606f\u9053\u201c\u770b\u8fd9\u7eb8\u6761\u4e0a\u5199\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7eb8\u6761\u4e0a\u5bc6\u5bc6\u7684\u5199\u4e86\u884c\u8747\u5934\u5c0f\u5b87\u201c\u4f60\u82e5\u4e0d\u662f\u738b\u52a8\xb7\u5c31\u662f\u4e2a\u66ff\u6b7b\u9b3c\u201d \u3000\u3000\u98ce\u591c\u5439\u3002 \u3000\u3000\u7a3b\u8349\u4eba\u88ab\u98ce\u5439\u5f97\u6447\u6447\u6643\u6643\u7684\u597d\u50cf\u5728\u5bf9\u4ed6\u4eec\u793a\u5a01\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u706b\u6c14\u5ffd\u7136\u4e0a\u6765\u4e86\u5ffd\u7136\u62f3\u5411\u90a3\u7a3b\u8349\u4eba\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7a3b\u8349\u4eba\u5f53\u7136\u4e0d\u4f1a\u8fd8\u624b\u4e5f\u4e0d\u4f1a\u95ea\u907f\u3002 \u3000\u3000\u7ff1\u5927\u8def\u62f3\u521a\u6253\u4e0a\u53bb\uff0c\u71d5\u4e03\u5df2\u62e6\u8170\u5c06\u4ed6\u62b1\u4f4f\xb7\u4ed6\u8fd9\u62f3\u867d\u7136\u6ca1\u6709\u5224\u5b9e\u8fd8\u662f\u6253\u4e86\u3002 \u3000\u3000\u4ed6\u62f3\u5934\u6253\u5728\u7a3b\u8349\u4eba\u80f8\u53e3\u4e0a\u65f6\xb7\u4e5f\u597d\u50cf\u88ab\u868a\u5b50\u53e3\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u62f3\u5934\u4e0a\u75d2\u75d2\u7684\u8fd8\u6709\u70b9\u53d1\u9ebb\u4e2d\u6307\u7684\u9aa8\u8282\u4e0a\u5df2\u591a\u4e86\u4e2a\u9ed1 \u3000\u3000\u71d5\u4e03\u7684\u5200\u5b9e\u5728\u8fd9\u9ed1\u70b9\u4e0a\u6311\xb7\u6d41\u51fa\u6765\u7684\u8840\u4e5f\u5df2\u53d8\u6210\u9ed1\u7684\u3002 \u3000\u3000\u6bd2\u8840\u8fd8\u5e26\u79cd\u8bf4\u4e0d\u51fa\u7684\u8165\u81ed\u4e4b\u6c14\u3002 \u3000\u3000\u4f46\u71d5\u4e03\u5374\u4e0d\u5acc\u81ed\u4e5f\u4e0d\u5acc\u810f\u7adf\u5fc3\u53e3\u7684\u5c06\u6bd2\u8840\u5168\u90fd\u542e\u5438\u4e86\u51fa \u3000\u3000\u90ed\u5927\u8def\u8fde\u773c\u6cea\u90c1\u51e0\u4e4e\u8000\u4e0d\u4f4f\u8981\u6d41\u5382\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u71d5\u4e03\u5bf9\u4ed6\u5df2\u5e76\u4e0d\u5b8c\u5168\u662f\u53cb\u60c5\xb7\u800c\u662f\u79cd\u6bd4\u53cb\u60c5\u66f4\u6df1\u6bd4\u53cb\u60c5\u66f4\u4eb2\u5bc6\u7684\u611f\u60c5\u3002 \u3000\u3000\u4f97\u4ed6\u4e5f\u8bf4\u4e0d\u51fa\u8fd9\u79cd\u611f\u60c5\u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u76f4\u5230\u71d5\u4e03\u7ad9\u8d77\u6765\uff0c\u4ed6\u8fd8\u662f\u6ca1\u6709\u8bf4\u8bdd\u8fde\u4e2a\u611f\u6fc0\u7684\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u7684\u611f\u6fc0\u4e5f\u6c34\u8db3\u4efb\u4f55\u5b57\u80fd\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u71d5\u4e03\u957f\u957f\u7812\u51fa\u53e3\u6c14\u8f7b\u8f7b\u9053\u201c\u4f60\u73b0\u5728\u89c9\u5f97\u600e\u9ebd\u6837\u4e86\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u53ea\u89c9\u5f97\u81ea\u5df2\u662f\u4e2a\u5446\u5b50\xb7\u4e0d\u6298\u4e0d\u6263\u7684\u5446\u5b50\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u76f4\u5728\u770b\u4ed6\u4ecd\xb7\u5ffd\u7136\u4e5f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u4f60\u7684\u786e\u662f\u4e2a\u5446\u5b50\u201c \u3000\u3000\u4ed6\u8138\u8272\u5df4\u6bd4\u521a\u597d\u770b\u591a\u4e86\u4f46\u4e00\u6761\u817f\u8fd8\u662f\u52a8\u4e5f\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u71d5\u4e03\u5e76\u6ca1\u6709\u66ff\u4ed6\u542e\u51fa\u4f24\u53e3\u91cc\u7684\u6bd2\u8840\u53ef\u662f\u4ed6\u70b9\u4e5f\u4e0d\u57cb\u6028\uff0c\u66f4\u6ca1\u6709\u8d23\u602a\u4e4b\u610f\xb7\u4eff\u4f5b\u4e5f\u89c9\u5f97\u8fd9 \u662f\u5e94\u8be5\u7684\u3002 \u3000\u3000\u96be\u9053\u4ed6\u5df2\u770b\u51fa\u4e86\u4ec0\u9ebd\uff1f\u770b\u51fa\u5382\u4e9b\u53ea\u6709\u90ed\u5927\u8def\u770b\u4e0d\u6e56\u7684\u79d8\u5bc6\uff1f \u3000\u3000\u71d5\u4e03\u7684\u80a1\u4f3c\u53c8\u7ea2\u4e86\uff0c\u5f88\u5feb\u7684\u8f6c\u8fc7\u8eab\u7528\u5200\u5b9e\u6311\u5f00\u4e86\u7a3b\u8349\u4eba\u8eab\u4e0a\u7684\u9ebb\u8863\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u770b\u5230\u7a3b\u8349\u4e0a\u63d2\u6ee1\u4e86\u5c16\u9488\uff0c\u9488\u5934\u5728\u9633\u5149\u4e0b\u53d1\u4e4c\u5149\uff0c\u5c31\u8fde\u679c\u5b50\u4e5f\u770b\u5f97\u51fa\u6bcf\u6839\u9488\u4e0a \u7684\u6bd2\u90fd\u8db3\u4ee5\u8981\u4eba\u7684\u547d\u3002 \u3000\u3000\u521a\u82e5\u4e0d\u662f\u71d5\u4e03\u62c9\u4f4f\u4ed6\u4ed6\u90a3\u4e00\u62f3\u82e5\u662f\u5b9e\u5b9e\u7684\u6253\u4e86\u4e0a\u53bb\xb7\u5c31\u7b97\u8fd8\u80fd\u4fdd\u4f4f\u6027\u547d\u8fd9\u53ea\u624b\u4e5f\u7b97\u62a5\u9500 \u4e86\u3002 \u3000\u3000\u6797\u592a\u5e73\u73b0\u5728\u5f53\u7136\u4e5f\u5df2\u60f3\u5230\u7eb8\u6761\u4e0a\u7684\u7ebf\u8fde\u54ed\u4e27\u68d2\u7684\u673a\u7c27\uff0c\u4ed6\u62c9\u7eb8\u6761\u5c31\u5c06\u673a\u7c27\u53d1\u52a8\u3002 \u3000\u3000\u8fd9\u7a3b\u8349\u4eba\u751f\u8eab\u4e0a\u4e0b\u4eff\u4f5b\u90fd\u57cb\u4f0f\u6740\u4eba\u7684\u6bd2\u9488\u3002 \u3000\u3000\u90ed\u5927\u8def\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9041\u201c\u4e2a\u7a3b\u8349\u4eba\u5c45\u7136\u80fd\u5c06\u6211\u4eec\u4e24\u4e2a\u5927\u6d3b\u4eba\u6253\u5012\uff0c\u8fd9\u79cd\u4e8b\u6211\u82e5\u975e\u81ea \u5df1\u9047\u89c1\xb7\u65e0\u8bba\u6dee\u8bf4\u6211\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\uff1a\u201c\u7a20\u8349\u4eba\u5df2\u7ecf\u8fd9\u9ebd\u5389\u5bb3\u4e86\uff0c\u505a\u8fd9\u7a3b\u8349\u4eba\u7684\u4eba\u5c82\u975e\u578b\u767e\u6015\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u4e0d\u662f\u5f88\u53ef\u6015\u571f\u8001\u5927\u53c8\u600e\u4f1a\u90a3\u9ebd\u5403\u60ca\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9762\u8272\u5df2\u53d1\u767d\uff0c\u9053\u201c\u73b0\u5728\u7a3b\u8349\u4eba\u5df4\u6765\u4e86\xb7\u4e0d\u77e5\u9053\u4ed6\u4eec\u81ea\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u5931\u58f0\u9053\u201c\u4f60\u4eec\u8fdb\u53bb\u770b\u770b\u738b\u8001\u5927\u7528\u4e0d\u7ba1\u6211\u6211\u7684\u4e8e\u8fd8\u80fd\u52a8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u53ea\u662f\u4f38\u624b\u5c06\u4ed6\u67b6\u4e86\u8d77\u6765\u3002\u71d5\u4e03\u5df2\u51b2\u4e2b\u8fdb\u53bb\u9ad8\u547c\u9053\u201c\u5de5\u8001\u5927\u2026\u2500\u738b \u52a8\u201d \u3000\u3000\u6ca1\u6709\u56de\u5e94\u6ca1\u6709\u58f0\u97f3\u3002 \u3000\u3000\u738b\u52a8\u5df2\u4e0d\u89c1\u5382\u3002 \u3000\u3000\u5e8a\u4e0a\u7684\u88ab\u8925\u51cc\u4e71\u738b\u52a8\u5374\u4e0d\u5728\u5e8a\u4e0a\u4e5f\u4e0d\u5728\u5c4b\u4e8e\u91cc\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ed6\u4eec\u524d\u524d\u5f8c\u5f8c\u90fd\u627e\u904d\u8fd8\u662f\u627e\u4e0d\u5230\u4ed6\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5f88\u89e3\u738b\u52a8\u3002 \u3000\u3000\u80fd\u53eb\u738b\u52a8\u4ece\u8d26\u4e0a\u722c\u8d77\u6765\u7684\u4e8b\u5df3\u4e0d\u591a\u80fd\u53eb\u4ed6\u4e2a\u4eba\u51fa\u53bb\u7684\u4e8b\u66f4 \u3000\u3000\u201c\u8fd9\u91cc\u83ab\u975e\u51fa\u53d1\u751f\u8fc7\u4ec0\u9ebd\u4e8b\uff1f\u738b\u52a8\u83ab\u975e\u5df2\u2026\u2500\u201c \u3000\u3000\u8036\u6728\u8def\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u6797\u592a\u5e73\u7eed\u5728\u738b\u52a8\u7684\u5e8a\u4e0a\u82cd\u767d\u7684\u8138\u53c8\u5df2\u6025\u5f97\u53d1\u7ea2\xb7\u5927\u58f0\u9053\u201c\u6211\u5df2\u544a\u8bc9\u8fc7\u4f60\u4eec\xb7\u7528\u4e0d\u7ba1 \u6211\xb7\u5feb\u53bb\u627e\u738b\u8001\u5927\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u53d1\u6025\u4e86\u5927\u58f0\u9053\u201c\u5f53\u7136\u8981\u53bb\u627e\uff0c\u4ecd\u4f60\u53eb\u6211\u4ece\u524d\u5230\u54ea\u91cc\u53cb\u627e\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u770b\u770b\u71d5\u4e03\uff0c\u71d5\u4e03\u4e5f\u5728\u53d1\u6014\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u6790\u5df2\u6709\u4e24\u4e2a\u4eba\u53d7\u4e86\u4f24\xb7\u4f46\u5374\u8fde\u5bf9\u65b9\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u5230\u73b0\u5728\u4e3a\u6b62\u8fd8\u662f\u8fde\u70b9\u5934\u7eea\u90fd\u6c72\u6709\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u53ea\u77e5\u9053\u70b9\u8fd9\u4e9b\u4eba\u7684\u786e\u548c\u738b\u52a8\u6709\u4ec7\u800c\u4e14\u4ec7\u5fc5\u5b9a\u6781\u6df1\u3002 \u3000\u3000\u4f46\u77e5\u9053\u8fd9\u70b9\u53c8\u6709\u4ec0\u9ebd\u7528\uff1f\u7b80\u76f4\u8ddf\u5b8c\u5168\u4e0d\u77e5\u9053\u6ca1\u6709\u4ec0\u9ebd\u4e24\u6837\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u8d70\u5ed3\u4e0a\u5ffd\u7136\u54cd\u8d77\u4e00\u9635\u811a\u6b65\u58f0\u3002 \u3000\u3000\u811a\u6b65\u58f0\u5f88\u8f7b\u5f88\u6162\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ed6\u4eec\u51e0\u4e4e\u8fde\u5fc3\u8df3\u90fd\u5df2\u505c\u6b62\u3002 \u3000\u3000\u6765\u7684\u7edd\u4e0d\u662f\u7a3b\u8349\u4eba\u3002 \u3000\u3000\u7a20\u5355\u4eba\u4e0d\u4f1a\u5ea6\u8def \u3000\u3000\u71d5\u4e03\u5411\u90ed\u5927\u8def\u6253\u5382\u4e2a\u773c\u8272\uff0c\u4e24\u4e2a\u4eba\u8eab\u5b50\u4e00\u95ea\u540c\u65f6\u8eb2\u5230\u95e8\u5f8c\u3002 \u3000\u3000\u5706\u6b65\u58f0\u8d8a\u6765\u8d8a\u8fd1\u7ec8\u4e8e\u505c\u5728\u95e8\u5916\u3002\u71d5\u4ea1\u4e4e\u91cc\u7684\u5df4\u9996\u5df2\u626c\u8d77 \u3000\u3000\u300f\u662f\u865a\u63a9\u7684\uff0c\u53ea\u624b\u5728\u63a8\u95e8\u3002 \u3000\u3000\u71d5\u4e03\u624b\u8155\u7ffb\xb7\u5df4\u9996\u95ea\u7535\u822c\u6325\u4e86\u51fa\u53bb\u5212\u5411\u8fd9\u53ea\u624b\u7684\u8109\u95e8\u3002 \u3000\u3000\u5e8a\u535c\u7684\u6797\u592a\u5e73\u5ffd\u7136\u5927\u559d\u9053\u201c\u4f50\u624b\u201d \u3000\u3000\u559d\u58f0\u8d77\u70ed\u4e03\u7684\u624b\u7acb\u523b\u786c\u4e2d\u751f\u505c\u4f4f\xb7\u5200\u950b\u8ddd\u79bb\u62a5\u95e8\u8fd9\u53ea\u624b\u7684\u8155\u8109\u8fd8\u4e0d\u53ca\u534a\u5c11\u3002 \u3000\u3000\u4f46\u8fd9\u53ea\u624b\u8fd8\u662f\u5f88\u7a33\u5b9a\uff0c\u8fd8\u662f\u6162\u4fb5\u7684\u628a\u95e8\u63a8\u5f00\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u4e0a\u2606\u7684\u795e\u7ecf\u5c31\u8c61\u662f\u94c1\u94f8\u7684\u3002 \u3000\u3000\u95e8\u63a8\u5f00\uff0c\u738b\u52a8\u5021\u6162\u7684\u8d70\u5382\u8fdb\u6765\u53e6\u53ea\u624b\u4e0a\u63d0\u9152\u3002 \u3000\u3000\u71d5\u4e03\u624b\u4e0a\u7684\u5200\u950b\u5728\u95ea\u5149\u3002 \u3000\u3000\u6797\u592a\u5e73\u8eba\u5728\u5e8a\u4e0a\u65e0\u8bba\u8c01\u90fd\u53ef\u770b\u6e56\u4ed6\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u4f46\u4e0a\u52a8\u5374\u597d\u50cf\u4ec0\u9ebd\u90fd\u6ca1\u770b\u89c1\u8138\u4e0a\u8fd8\u662f\u70b9\u8868\u503a\u4e5f\u6ca1\u6709\u3002\u8fd9\u4eba\u5168\u8eab\u4e0a\u4e0b\u7684\u6291\u7ecf\u597d\u50cf\u662f\u94c1\u94f8 \u7684\u3002 \u3000\u3000\u4ed6\u6388\u6162\u7684\u8d70\u4e86\u8fdb\u6765\xb7\u6162\u4fb5\u7684\u628a\u9152\u653e\u5728\u684c\u5b50\u4e0a\u3002 \u3000\u3000\u7b2c\u4e2a\u6c89\u4e0d\u4f4f\u6c14\u7684\u662f\u90ed\u5927\u8def\xb7\u5927\u58f0\u95ee\u9053\u201c\u4f60\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u6de1\u6de1\u7684\u9053\u201c\u4e70\u9152\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u56de\u7b54\u5f97\u90a3\u9ebd\u76ee\u7136\u597d\u50cf\u8fd9\u672c\u662f\u5929\u5382\u5458\u5408\u7406\u7684\u4e8b\u3002 \u3000\u3000\u201c\u4e70\u9152\u5de6\u5382\u3002\u201d\u8fd9\u79cd\u65f6\u5019\u4ed6\u5c45\u7136\u4e70\u9152\u53bb\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\uff0c\u7b80\u76f4\u6709\u70b9\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u738b\u52a8\u638c\u62cd\u5f00\u4e86\u9152\u4e0a\u7684\u5c01\u6ce5\u55c5\u4e86\u55c5\u4eff\u4f5b\u89c9\u5f97\u72e0\u6ee1\u610f\xb7\u56f4\u89d2\u8fd9\u624d\u7530\u51fa\u4e00\u4e1d\u7b11\u5bb9\u3001\u9053\uff1a\u8fd9\u9152\u8fd8 \u4e0d\u9519\uff0c\u6765\u5927\u5bb6\u90fd\u6765\u559d\u4e24\u676f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u73b0\u5728\u6211\u4e0d\u60f3\u559d\u9152\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u60f3\u8d50\u4e5f\u5f97\u559d\xb7\u975e\u4e0d\u53ef\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u835a\u52a8\u9053\uff0c\u201c\u56e0\u4e3a\u8fd9\u662f\u6211\u66ff\u4f60\u4eec\u94b1\u884c\u7684\u9152\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5931\u58f0\u9053\u201c\u94b1\u7b14\uff1f\u4e3a\u4ec0\u9ebd\u8981\u66ff\u6211\u4eec\u996f\u884c\uff1f\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u56e0\u4e3a\u4f60\u4eec\u9a6c\u4e0a\u5c31\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8df3\u4e86\u8d77\u6765\u9053\u201c\u8c01\u8bf4\u6211\u4eec\u8981\u8d70\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u62a2\u9053\u201c\u4f46\u6211\u4eec\u5e76\u4e0d\u60f3\u8d70\u3002\u201d \u3000\u3000\u738b\u52a8\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\u201c\u4e0d\u60f3\u4f01\u4e5f\u5f97\u8d70\u4f60\u4eec\u96be\u9053\u60f3\u5728\u6211\u8fd9\u91cc\u8d56\u8f88\u4e8e\u3002\u201d\u4e92\u52a8\u94c1\u9752\u8138\u9053 \u201c\u4f60\u4eec\u4f4f\u5728\u8fd9\u91cc\xb7\u4ed8\u8fc7\u623f\u94b1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u738b\u52a8\u51b7\u7b11\u9053\u201c\u65e2\u7136\u5982\u6b64\u4f60\u4eec\u51ed\u4ec0\u9ebd\u8d56\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u597d\u8d70\u5c31\u8d70\u3002\u201d \u3000\u3000\u4ed6\u771f\u7684\u8bf4\u8d70\u5c31\u8d70\u53ea\u4e0d\u8fc7\u8d70\u8fc7\u90ed\u5927\u8def\u9762\u524d\u7684\u65f6\u5019\uff0c\u5411\u7ff1\u5927\u8def\u6324\u6b79\u7b4b\u773c\u5708\u3002 \u3000\u3000\u7ff1\u5927\u8def\u773c\u73e0\u4e8e\u8f6c\xb7\u9053\u201c\u5bf9\u8d70\u5c31\u8d70\u6c72\u4ec0\u9ebd\u5382\u4e2a\u8d77\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u4e5f\u8bf4\u8d70\u5c31\u8d70\u597d\u50cf\u8fde\u7247\u523b\u90fd\u803d\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u6797\u592a\u5e73\u6014\u4e86\u6014\u9053\u201c\u4f60\u4eec\u8fde\u9152\u90fd\u4e0d\u559d\u5382\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65e2\u7136\u5df3\u88ab\u4eba\u8d76\u5382\u51fa\u53bb\u8fd8\u6709\u4ec0\u9ebd\u8138\u559d\u9152\u3002\u201d \u3000\u3000\u68ee\u592a\u5e73\u770b\u770b\u738b\u52a8\u3002 \u3000\u3000\u738b\u52a8\u8138\u4e0a\u8fd8\u662f\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\xb7\u51b7\u51b7\u9053\u201c\u4e0d\u8d50\u5c31\u4e0d\u559d\u9152\u653e\u5728\u8fd9\u91cc\u96be\u9053\u8fd8\u4f1a\u53d1\u9709\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9762\u201c\u6211\u7559\u4e0b\u6765\u597d\u4e0d\u597d\uff1f\u6211\u8d70\u4e0d\u52a8\u3002\u201d \u3000\u3000\u738b\u52a8\u677f\u8138\u9053\u201c\u8d70\u4e0d\u52a8\u5c31\u722c\u51fa\u53bb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6014\u4e86\u534a\u664c\xb7\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\u62d0\u62d0\u7684\u56e0\u4ed6\u4eec\u8d70\u4e86\u51fa \u3000\u3000\u738b\u52a8\u5730\u5728\u90a3\u91cc\uff0c\u51b7\u51b7\u5730\u770b\u4ed6\u4eec\u8d70\u51fa\u95e8\u8fde\u52a8\u90fd\u4e0d\u52a8\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u8d2d\xb7\u53ea\u542c\u201c\u7830\u201d\u7684\u58f0\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u5c06\u5916\u9762\u7684\u5927\u95e8\u91cd\u91cd\u7684\u5173\u4e86\u8d77\u6765\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u6367\u8d77\u684c\u4e0a\u7684\u9152\u5b50\u201c\u5495\u787c\u5495\u7530\u201d\u2500\u53e3\u6c14\u559d\u4e86\u4e03\u516b\u95e8\u624d\u505c\u4e0b\u6765\uff0c\u62b9\u4e86\u62b9\u5207\uff0c\u54fa\u7530 \u9053\uff1a\u201c\u597d\u9152\u8fd9\u9ebd\u6837\u7684\u597d\u9152\u5c45\u7136\u6709\u4eba\u4e0d\u559d \u3000\u3000\u4ed6\u671b\u624b\u91cc\u7684\u6d12\u5b50\u2018\u53cc\u51b7\u51b0\u51b0\u7684\u773c\u775b\u5ffd\u7136\u7ea2\u4e86\u5c31\u8c61\u662f\u968f\u65f6\u90fd\u53ef\u80fd\u6709\u773c\u7528\u8981\u6d41\u4e0b\u6765\u3002 \u3000\u3000\u71d5\u4e03\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u5230\u5927\u95e8\u5916\u5ffd\u7136\u505c\u4f4f\u3002\u90ed\u5927\u8def\u8d70\u5230\u4ed6\u8eab\u65c1\u4e5f\u5ffd\u7136\u505c\u4f4f\u3002 \u3000\u3000\u6797\u592a\u5e73\u8ddf\u51fa\u6765\uff0c\u201c\u7830\u201d\u7684\uff0c\u751f\u751f\u7684\u5173\u4e0a\u95e8\u77aa\u4ed6\u4eec\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u4eec\u771f\u7684\u8bf4\u8d70\u5c31\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u4ec0\u9ebd\u8bdd\u4e5f\u4e0d\u8bf4\u5374\u5728\u5927\u95e8\u5916\u7684\u77f3\u9636\u4e0a\u5750\u4e86\u535c\u6765\u9762\u5bf9\u7a3b\u8349\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u7acb\u523b\u4e5f\u8ddf\u5750\u4e86\u535c\u6765\u4e5f\u770b\u7a3b\u8349\u4eba\uff0c\u54fa\u54fa\u9053\u201c\u602a\u4e8b\u5e74\u5e74\u6709\u4eca\u5e74\u7279\u522b\u591a\u7cef\u8349\u4eba\u4e0d\u4f46\u4f1a\u653e \u98ce\u7b2c\xb7\u8fd8\u4f1a\u6740\u4eba\u4f60\u8bf4\u5947\u602a\u4e0d\u5947\u602a\uff1f\u201d\u6797\u592a\u5e73\u9053\u201c\u5947\u602a\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u5750\u5382\u4e0b\u6765\u53ea\u624b\u8fd8\u662f\u7d27\u7d27\u7684\u63a5\u4f24\u53e3\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u603b\u7b97\u4e5f\u660e\u767d\u90ed\u5927\u8def\u548c\u71d5\u4e03\u7684\u610f\u601d\u4e86\xb7\u6240\u4ee5\u4e5f\u4e0d\u518d\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u4e5f\u4e0d\u7ec6\u8fc7\u4e86\u591a\u4e45\u624d\u542c\u5230\u738b\u52a8\u7684\u811a\u6b65\u58f0\u6162\u6162\u7684\u8d70\u51fa\u6765\xb7\u7a7f\u8fc7\u9662\u5343\u5b9a\u5230\u4eba\u95e8\u53e3\u91cd\u91cd\u7684\u63d2\u4e0a\u4e86 \u95e8\u66f0\u3002 \u3000\u3000\u7a81\u7136\u95f4\u95e8\u95e8\u53c8\u62d4\u4e86\u51fa\u6765\uff0c\u5927\u95e8\u970d\u7136\u6253\u5f00\u3002 \u3000\u3000\u4e0a\u52a8\u7eb3\u5728\u95e8\u95e8\xb7\u5f20\u5927\u4e86\u8ddf\u775b\u77aa\u4ed6\u4eec\u3002 \u3000\u3000\u71d5\u4e03\u3001\u90ed\u5927\u8def\u3001\u6797\u592a\u5e73\u53c1\u4e2a\u4eba\u4e00\u6392\u5750\u5728\u95e8\u5916\uff0c\u8c01\u4e5f\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u738b\u52a8\u5fcd\u4e0d\u4f4f\u4eba\u58f0\u9053\u201c\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u8d70\uff1f\u5750\u5728\u8fd9\u91cc\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u53c1\u4e2a\u4eba\u8c01\u4e5f\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u71d5\u4e03\u53ea\u662f\u539f\u4e86\u90ed\u5927\u8def\u4e00\u773c\xb7\u9053\u201c\u6211\u4eec\u5750\u5728\u8fd9\u91cc\u72af\u4e0d\u72af\u6cd5\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4e0d\u72af\u6cd5\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8fde\u7a3b\u8349\u4eba\u90fd\u80fd\u5750\u5728\u8fd9\u91cc\xb7\u6211\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u738b\u52a8\u5389\u58f0\u9053\u201c\u8fd9\u91cc\u662f\u6211\u7684\u5927\u95e8\u53e3\xb7\u4f60\u4eec\u7ad9\u5728\u8fd9\u91cc\u5c31\u6321\u4f4f\u4e86\u6211\u7684\u8def\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u8198\u4e86\u90ed\u5927\u8def\u773c\u9053\u201c\u4eba\u5bb6\u8bf4\u6211\u4eec\u6321\u4f4f\u4e86\u4ed6\u7684\u8def\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u6211\u4eec\u5c31\u5750\u5f00\u4e9b\u3002\u201d \u3000\u3000\u4e8c\u4e2a\u4eba\u8d77\u7ad9\u4e86\u8d77\u6765\u8d70\u5230\u5bf9\u9762\u53c8\u6392\u5750\u4e86\u4e0b\u6765\u9762\u5bf9\u5927 \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4eec\u5904\u5728\u8fd9\u91cc\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u884c\u8fd9\u91cc\u65e2\u4e0d\u662f\u4eba\u5bb6\u7684\u5c4b\u4e8e\u4e5f\u4e0d\u6321\u8def\u3002\u201d \u3000\u3000\u6797\u592a\u4e2d\u9053\u201c\u4e3d\u4e14\u9ad8\u5174\u5750\u591a\u4e45\u5c31\u534e\u591a\u4e45\u3002\u201d \u3000\u3000\u5341\u52a8\u77aa\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u5de6\u987e\u6709\u76fc\u5c31\u662f\u4e0d\u53bb\u770b\u4e03\u52a8\u3002 \u3000\u3000\u6b63\u52a8\u4eba\u58f0\u9053\u201c\u4f60\u4eec\u5750\u5728\u8fd9\u91cc\u7a76\u7adf\u60f3\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ff1\u4eba\u8def\u9053\u201c\u4ec0\u9ebd\u4e5f\u4e2a\u5341\u53ea\u4e0d\u8fc7\u5750\u5750\u4e3d\u5df2\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4eec\u9ad8\u5174\u5750\u5728\u54ea\u91cc\u5c31\u5750\u5728\u54ea\u91cc\u8c01\u4e5f\u7ba1\u4e0d\u5382\u3002\u201d \u3000\u3000\u6797\u4eba\u4e2d\u901a\u201c\u8fd9\u91cc\u597d\u51c9\u5feb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e49\u51c9\u5feb\u4e49\u8212\u670d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u800c\u4e14\u7edd\u4e0d\u4f1a\u6709\u4eba\u6765\u627e\u6211\u4eec\u6536\u79df\u91d1\u3002\u201d \u3000\u3000\u5e72\u52a8\u7a81\u7136\u62a5\u5934\u5ea6\u4e86\u8fdb\u53bb\u201c\u80bf\u201d\u7684\u53c8\u5c06\u95e8\u91cd\u91cd\u7684\u5173\u4e86\u8d77\u6765\u3002 \u3000\u3000\u71d5\u4e03\u770b\u770b\u90ed\u5927\u8def\xb7\u7ff1\u5927\u8def\u770b\u770b\u6797\u592a\u5e73\uff0c\u53c1\u4e2a\u4eba\u8d75\u7b11\u4e86\u3002 \u3000\u3000\u867d\u7136\u7b11\u4e86\uff0c\u4f97\u7b11\u5bb9\u4e2d\u8fd8\u662f\u5e26\u4e9b\u4f18\u9157\u4e4b\u8272\u3002 \u3000\u3000\u592a\u9633\u5df2\u535c\u5382\u5c71\u3002 \u3000\u3000\u6625\u5929\u6bd5\u7adf\u8fd8\u6765\u5f97\u6ca1\u6709\u8fd9\u9ebd\u65e9\u767d\u5929\u8fd8\u662f\u5f88\u77ed\u3002 \u3000\u3000\u592a\u9633\u4e0b\u5c71\uff0c\u5929\u8272\u773c\u770b\u5c31\u8981\u6697\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5929\u8272\u2500\u6697\uff0c\u8fd9\u91cc\u5c31\u4f1a\u53d1\u751f\u4e9b\u4ec0\u9ebd\u4e8b\uff1f\u8c01\u90fd\u4e0d\u77e5\u9053\xb7\u751a\u81f3\u8fd8\u731c\u90fd\u4e0d\u6562\u731c\u3002 \u3000\u3000\u71d5\u4e03\u6084\u6084\u62c9\u8d77\u4e86\u90ed\u5927\u8def\u7684\u624b\u9053\u201c\u4f60\u7684\u4f24\u600e\u9ebd\u6837\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u59a8\u4e8b\uff0c\u670b\u6837\u8fd8\u662f\u53ef\u4ee5\u63cd\u4eba\u3002\u201d\u71d5\u4e03\u8fd9\u624d\u8f6c\u5411\u6797\u592a\u5e73\u9053\u201c\u4f60\u5462\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u7684\u4f24\u53e3\u5df2\u6e10\u6e10\u6709\u70b9\u53d1\u75db\u3002\u201d \u3000\u3000\u71d5\u4e03\u5410\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u90a3\u5c31\u4e0d\u59a8\u4e8b\u4e86\u3002 \u3000\u3000\u88ab\u6bd2\u836f\u77aa\u5668\u6253\u4e2d\u7684\u4f24\u53e3\u82e5\u5df4\u5728\u53d1\u75bc\u5c31\u8868\u793a\u6bd2\u5df2\u62d4\u5c3d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u8fd8\u662f\u6709\u70b9\u4e0d\u653e\u5fc3\uff0c\u6240\u4ee5\u53c8\u95ee\u9053\u201c\u75db\u5f97\u5389\u4e0d\u5389\u5bb3\uff1f\u201d\u6797\u592a\u5e73\u7b11\u4e86\u7b11\u9053\u201c\u8fd8\u597d\u867d\u7136 \u4e0d\u89c1\u5f97\u80fd\u8df3\u5899\u5378\u4e5f\u7167\u6837\u8fd8\u662f\u53ef\u4ee5\u63cd\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4eec\u997f\u4e0d\u997f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4fc4\u5f97\u60f3\u628a\u4f60\u541e\u4e0b\u53bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u7b11\u4e86\xb7\u9053\u201c\u4f46\u4f60\u809a\u5b50\u6151\u7684\u65f6\u5019\u4e5f\u7167\u6837\u53ef\u4ee5\u63cd\u4eba\u7684\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u5929\u8272\u679c\u7136\u6697\u5382\u4e0b\u6765\u3002 \u3000\u3000\u5343\u4e2a\u4eba\u795e\u60c5\u770b\u6765\u5df2\u6e10\u6e10\u6709\u70b9\u7d27\u5f20\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u4ecd\u5df2\u51c6\u5907\u51c6\u5907\u63cd\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u63e1\u7d27\u4e86\u62f3\u5934\u77aa\u5927\u4e86\u773c\u775b\uff0c\u9053\u201c\u73b0\u5728\u771f\u662f\u4e07\u4e8b\u60e7\u5907\u53ea\u6b20\u4e50\u98ce\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4e1c\u98ce\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u662f\u6328\u63cd\u7684\u4eba\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u4ed6\u5df2\u770b\u89c1\u4e86\u4e2a\u4eba\u3002 \u3000\u3000\u56db \u3000\u3000\u4e2a\u62b1\u9152\u5b50\u7684\u4eba\u3002 \u3000\u3000\u5927\u95e8\u5ffd\u7136\u53c8\u5f00\u4e86\u738b\u52a8\u62b1\u9152\u5b50\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u6ca1\u6709\u7406\u4ed6\u4eec\uff0c\u5374\u5728\u5927\u95e8\u53e3\u7684\u77f3\u9636\u4e0a\u5750\u4e0b\u6765\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u9762\u5bf9\u9762\u7684\u5750\u8c01\u4e5f\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u7b2c\u4e2a\u9ab8\u4e0d\u4f4f\u7684\u4eba\u5f53\u7136\u8fd8\u662f\u90ed\u5927\u8def\u3002 \u3000\u3000\u4ed6\u53f9\u5382\u53e3\u6c14\u54fa\u95fb\u9053\u201c\u6211\u8bb0\u5f97\u521a\u597d\u50cf\u6709\u4eba\u8981\u8bf7\u6211\u4eec\u559d\u9152\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u65e2\u4e0d\u7b54\u8154\xb7\u4e5f\u4e0d\u770b\u4ed6\u5ffd\u7136\u5c06\u9152\u5b50\u5411\u4ed6\u629b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f60\u65e0\u8bba\u5c06\u4ec0\u9ebd\u4e1c\u897f\u629b\u5411\u90ed\u5927\u8def\uff0c\u4ed6\u90fd\u53ef\u80fd\u63a5\u4e0d\u4f4f\uff0c\u4f46\u9152\u5b50 \u3000\u3000\u629b\u8fc7\u6765\u7684\u82e5\u662f\u4e2a\u9152\u5b50\xb7\u5c31\u7b97\u7761\u7684\u4ed6\u4e5f\u7167\u6837\u80fd\u591f\u63a5\u4f4f\u3002 \u3000\u3000\u4ed6\u53e3\u6c14\u704c\u4e0b\u4e86\u597d\u51e0\u53e3\uff0c\u624d\u9012\u7ed8\u71d5\u4e03\u71d5\u4e03\u559d\u4e86\u51e0\u53e3\u53c8\u4f20\u7ed9\u6797\u592a\u5e73\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u53d7\u4e86\u4f24\u7684\u4eba\u82e5\u8fd8\u60f3\u559d\u9152\xb7\u5b9a\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8c01\u8bf4\u6211\u53d7\u4e86\u4f24\u6211\u53ea\u4e0d\u8fc7\u88ab\u6761\u5c0f\u54ac\u4e86\u4e00\u53e3\u800c\u5df2\u3002\u201d \u3000\u3000\u738b\u52a8\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u51b2\u8fc7\u53bb\xb7\u5c06\u9152\u4e88\u62a2\u4e86\u8fc7\u6765\uff0c\u94c1\u9752\u8138\u9053\u201c\u4f60\u4eec\u7a76\u7adf\u60f3\u5728\u8fd9\u91cc\u5750\u5230\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u618b\u4e0d\u4f4f\u4e86\uff0c\u5927\u58f0\u9053\u201c\u5750\u5230\u6709\u4eba\u6765\u627e\u4f60\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6dee\u8bf4\u6709\u4eba\u8981\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u6055\u9ebd\u7ec6\u9053\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u3001\u5b9d\u7a3b\u8349\u4eba\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u4ed6\u7528\u7530\u89d2\u66f0\u738b\u52a8\xb7\u7b11\u9053\uff1a\u201c\u8fd9\u7a3b\u8349\u4eba\u4e0d\u4f46\u4f1a\u653e\u98ce\u7b49\uff0c\u8fd8\u4f1a\u8bf4\u8bdd\uff0c\u4f60\u8bf4\u5947\u602a\u4e0d\u5947\u602a\uff1f\u201d \u3000\u3000\u738b\u52a8\u8138\u8272\u7a81\u53c8\u53d8\u4e86\uff0c\u9992\u9992\u7684\u9000\u4e86\u56de\u53bb\u5750\u5230\u77f3\u9636\u4e0a\u3002 \u3000\u3000\u56db\u4e0b\u9759\u5f97\u72e0\uff0c\u53ea\u6709\u5b50\u91cc\u7684\u9152\u5728\u7483\u3002 \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u2019\u5b50\u91cc\u7684\u9152\u4e5f\u5728\u8bf4\u8bdd\u4f60\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5b83\u5728\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u8bf4\u6709\u4e2a\u4eba\u7684\u624b\u5728\u6296\u6296\u5f97\u5b83\u5934\u90fd\u53d1\u6655\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u970d\u7136\u7ad9\u8d77\u6765\u77aa\u4ed6\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u4e0d\u770b\u4e0a\u52a8\u3002 \u3000\u3000\u53c1\u4e2a\u4eba\u4e1c\u5f20\u897f\u671b\u4ec0\u9ebd\u5730\u65b9\u90fd\u53bb\u770b\u5c31\u662f\u4e0d\u770b\u738b\u52a8\u3002 \u3000\u3000\u7a81\u7136\u95f4\u70b9\u706b\u661f\u7535\u4e86\u8fc7\u6765\xb7\u5c04\u5728\u7b2c\u4e2a\u7a3b\u8349\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u84ec\u201d\u7684\u58f0\u7a3b\u8349\u4eba\u4e2d\u523b\u71c3\u70e7\u4e86\u8d77\u6765\u3002 \u3000\u3000\u706b\u5149\u662f\u60e8\u78a7\u8272\u7684\u8fd8\u5e26\u7f15\u7f15\u8f7b\u70df\u3002 \u3000\u3000\u738b\u52a8\u53d8\u8272\u9053\u201c\u5feb\u9000\uff0c\u9000\u56de\u5c4b\u91cc\u53bb\u3002\u201d \u3000\u3000\u4ed6\u6325\u624b\u5c06\u9152\u5b50\u629b\u7ed8\u4e86\u90ed\u5927\u8def\xb7\u8f6c\u8eab\u62b1\u8d77\u4e86\u6797\u592a\u5e73\xb7\u4eba\u5df2\u51b2\u8fdb\u5927\u95e8\u3002 \u3000\u3000\u738b\u52a8\u7ec8\u4e8e\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u52a8\u5219\u5df2\xb7\u52a8\u8d77\u6765\u5c31\u6bd4\u8c01\u90fd\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u52a8\u4e86\uff0c\u5148\u653e\u90a3\u9152\u518d\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5e76\u6ca1\u6709\u5411\u5c4b\u5b50\u91cc\u9000\xb7\u53cd\u800c\u5411\u706b\u661f\u5c04\u6765\u7684\u65b9\u5411\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u6251\u8fc7\u53bb\xb7\u71d5\u4e03\u81ea\u7136\u4e5f\u8ddf\u3002 \u3000\u3000\u5de5\u52a8\u5927\u559d\u9053\u201c\u5feb\u9000\u7f51\u6765\uff0c\u90a3\u8fb9\u53bb\u672f\u5f97\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u542c\u89c1\xb7\u5c31\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u804b\u5b50\u3002 \u3000\u3000\u4ed6\u542c\u4e0d\u89c1\uff0c\u853d\u4e03\u5c31\u4e5f\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u6797\u592a\u5e73\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u8fd9\u4eba\u5c31\u559c\u6b22\u5230\u53bb\u4e0d\u5f97\u7684\u5730\u65b9\u53bb\u4f60\u73b0\u5728\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7684\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u680b\u623f\u5b50\u5047\u5982\u88ab\u4eba\u79f0\u505a\u201c\u5c71\u75ca\uff3c\u6700\u4f4e\u9650\u5ea6\u4e5f\u5f97\u6709\u51e0\u6837\u5458\u8d77\u7801\u7684\u6761\u4ef6 \u3000\u3000\u8fd9\u623f\u5b50\u7edd\u4e0d\u4f1a\u592a\u5c0f\u3002 \u3000\u3000\u8fd9\u623f\u5b50\u5c31\u7b97\u6ca1\u6709\u76d6\u5728\u5c71\u4e0a\xb7\u81f3\u5c11\u4e5f\u5f97\u76d6\u5728\u5c71\u9e93\u4e0b\u3002 \u3000\u3000\u623f\u5b50\u7684\u5927\u95e8\u5916\u5927\u5927\u5c0f\u5c0f\u603b\u6709\u7247\u6811\u6797\u5b50\u3002 \u3000\u3000\u201c\u5bcc\u8d35\u5c71\u5e86\u201d\u867d\u7136\u2018\u70b9\u4e5f\u672f\u5bcc\u8d35\u81f3\u5c11\u603b\u8fd8\u662f\u4e2a\u201c\u5c71\u5e84\u201d\u3002\u6240\u4ee5\u95e8\u5916\u4e5f\u6709\u7247\u6811\u6797\u521a\u90a3\u70b9\u706b \u661f\u597d\u50cf\u5c31\u662f\u4ece\u6811\u6797\u91cc\u5c04\u51fa\u6765\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u6c89\u58f0\u9053\u201c\u90a3\u70b9\u706b\u661f\u662f\u4ece\u90a3\u68f5\u6811\u5f8c\u9762\u5c04\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u6ca1\u770b\u6e05\u695a\u4f60\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u6c72\u770b\u6e05\u3002\u3002 \u3000\u3000\u5929\u8272\u4e2d\u5df2\u5f88\u6697\u6811\u6797\u91cc\u5f53\u7136\u66f4\u6697\u770b\u4e0d\u89c1\u4eba\u5f71\xb7\u4e5f\u542c\u4e0d\u89c1\u58f0\u755c\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u770b\u6211\u4eec\u8fd8\u662f\u5148\u56de\u53bb\u8ddf\u738b\u8001\u5927\u5546\u91cf\u5546\u91cf\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4eba\u5bb6\u4e0d\u8e6c\u6211\u4eec\u5546\u91cf\u6211\u4eec\u81ea\u5df1\u5546\u91cf\u53c8\u6709\u4e2a\u5c41\u7528\u3002\u201d \u3000\u3000\u4ed6\u7530\u91cc\u8bf4\u51fa\u810f\u8bdd\u7684\u65f6\u5019\xb7\u5c31\u8868\u793a\u4ed6\u706b\u6c14\u771f\u7684\u5df2\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u9022\u6797\u83ab\u5165\xb7\u4f60\u96be\u9053\u8fde\u6c5f\u6e56\u4e2d\u7684\u89c4\u77e9\u90fd\u4e0d\u6a2a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e0d\u64c5\u3002\u6211\u672c\u5c31\u4e0d\u662f\u8005\u6c5f\u6e56\u6c5f\u6e56\u4e2d\u7684\u90a3\u4e9b\u7834\u89c4\u77e9\u6211\u6837\u4e5f\u4e0d\u6a2a\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u7a81\u7136\u5411\u524d\u4e00\u6251\u5df2\u51b2\u5165\u4e86\u6811\u6797\u3002 \u3000\u3000\u6697\u6797\u4e2d\u4eff\u4f5b\u6709\u5bd2\u5149\u95ea\u52a8\u3002\uff01 \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u8fd8\u6ca1\u6709\u770b\u6e05\xb7\u4eba\u5df2\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u770b\u89c1\u4e86\u628a\u5200\u3002 \u3000\u3000\u628a\u5f2f\u5200\u3002 \u3000\u3000\u4e00\u628a\u5272\u8089\u7684\u5200\u3002 \u3000\u3000\u5200\u9489\u5728\u6811\u6b62\uff0c\u9489\u5f20\u7eb8\u6761\u5b50\u3002 \u3000\u3000\u7eb8\u6761\u535c\u5f53\u7136\u6709\u5b57\u5f88\u5c0f\u7684\u5b57\uff0c\u5c31\u7b97\u5728\u81fc\u5929\u4e5f\u672a\u5fc5\u80fd\u591f\u770b\u5f97\u6e05\u3002 \u3000\u3000\u90ed\u5927\u8def\u521a\u60f3\u4f38\u624b\u62d4\u5200\u624b\u5df1\u88ab\u71d5\u4e03\u62c9\u4f4f\u3002\u71d5\u4e03\u7684\u8138\u8272\u82cd\u767d\xb7\u77aa\u773c\u9053\u201c\u4f60\u4e0a\u4e86\u4e00\u6b21\u5f53\u8fd8\u4e0d \u591f\uff1f\u8fd8\u8981\u4e0a\u7b2c\u4e8c\u6b21\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u6025\u53c8\u6c14\u90ed\u5927\u8def\u5374\u7b11\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u7b11\u4ec0\u9ebd\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u7b11\u4f60\u3002\u201d\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u7b11\u4e2a\u5c41\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u6709\u810f\u8bdd\u9a82\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5c31\u8868\u793a\u4ed6\u5b9e\u5728\u5df2\u6c14\u5f97\u8981\u547d\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e0d\u7b11\u4e86\xb7\u6b63\u8272\u9053\u201c\u4ed6\u4eec\u5c31\u7b97\u8fd8\u60f3\u8ba9\u6211\u4e0a\u5f53\u4e5f\u5e94\u8be5\u6362\u4e2a\u65b0\u9c9c\u70b9\u7684\u6cd5\u5b50\u600e\u9ebd\u4f1a\u7528\u90a3\u8001 \u5957\u96be\u9053\u771f\u62ff\u6211\u4eec\u5f53\u5446\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u677f\u8138\u9053\uff0c\u201c\u4f60\u4ee5\u4e3a\u4f60\u4e0d\u662f\u5446\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u597d\uff0c\u4f60\u53eb\u6211\u4e0d\u52a8\u624b\uff0c\u6211\u5c31\u4e0d\u52a8\u624b\u4f46\u8fc7\u53bb\u770b\u770b\u603b\u8fd8\u6ca1\u5173\u7cfb \u5427\u3002\u201d \u3000\u3000\u4ed6\u771f\u7684\u80cc\u8d1f\u53cc\u624b\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u624b\u4e0d\u52a8\xb7\u53ea\u7528\u9650\u775b\u770b\u770b\u7684\u786e\u597d\u50cf\u4e0d\u4f1a\u6709\u4ec0\u9ebd\u5173\u7cfb\u3002 \u3000\u3000\u4f46\u7eb8\u6761\u4e0a\u7684\u5b57\u5b9e\u5728\u592a\u5c0f\u4ed6\u4e0d\u80fd\u4e0d\u8d70\u5f97\u8fd1\u4e9b\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u5df2\u53ef\u9690\u7ea6\u770b\u51fa\u7eb8\u6761\u535c\u7684\u5b87\u4e86\u201c\u5c0f\u5fc3\u4f60\u7684\u811a\u201d\u4e00\u3002\u201d \u3000\u3000\u4ed6\u770b\u6e05\u8fd9\u4e94\u4e2a\u5b57\u7684\u65f6\u5019\xb7\u811a\u5382\u8f6f\u4eba\u5df2\u5f80\u4e0b\u9762\u6389\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5730\u4e0a\u6709\u4e2a\u9677\u65ad\u3002 \u3000\u3000\u71d5\u4e03\u5931\u58f0\u9053\u5c0f\u5fc3\u3002\u4e00\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\u2606\u4ed6\u4e5f\u5df2\u51b2\u8fc7\u53bb\xb7\u62c9\u4f24\u4e86\u90ed\u5927\u8def\u7684\u624b\u3002 \u3000\u3000\u90ed\u5927\u8def\u624b\u4e0a\u4f7f\u52b2\xb7\u4eba\u5df2\u4e58\u52bf\u8dc3\u8d77\u3002 \u3000\u3000\u4ed6\u8f7b\u529f\u4e0d\u5f31\xb7\u773a\u5f97\u5f88\u9ad8\u3002 \u3000\u3000\u53ea\u53ef\u501f\u8df3\u5f97\u8d85\u9ad8\uff0c\u5c31\u8d8a\u7cdf\u7cd5\u3002 \u3000\u3000\u53ea\u542c\u6811\u53f6\u201c\u54d7\u5566\u5566\u201d\u54cd\uff0c\u6811\u4e0a\u5ffd\u7136\u6709\u9762\u5927\u7f51\u7f69\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u597d\u5927\u7684\u9762\u7f51\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u7b97\u957f\u6709\u7fc5\u8180\u5c31\u7b97\u771f\u662f\u53ea\u9e1f\xb7\u4e5f\u96be\u514d\u8981\u88ab\u7a9c\u4f4f\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u8eab\u5b50\u5df2\u8dc3\u5728\u534a\u7a7a\u5c31\u597d\u50cf\u662f\u81ea\u5df2\u5f80\u8fd9\u7f51\u5b50\u91cc\u8df3\u4e00\u6837\u65e0\u8bba\u6324\u90a3\u8fb9\u9003\u90fd\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u975e\u4f46\u4ed6\u8eb2\u4e0d\u5f00\u71d5\u4e03\u4e5f\u8eb2\u4e0d\u5f00\u3002 \u3000\u3000\u773c\u89c1\u4e24\u4e2a\u4eba\u90fd\u8981\u88ab\u7f69\u5728\u7f51\u91cc\u5ffd\u7136\u95ee\u6761\u9ed1\u5f71\u98de\u4e86\u8fc7\u6765\u5c31\u597d\u50cf\u662f\u4e2a\u70ae\u5f39\u4f3c\u7684\u7b80\u76f4\u6289\u5f97\u65e0\u6cd5\u601d \u8bae\u3002\u9ed1\u5f71\u4ece\u4ed6\u4eec\u5934\u535c\u63a0\u8fc7\u4f38\u624b\uff0c\u5c31\u5df2\u5c06\u8fd9\u9762\u7f51\u635e\u4f50\u4e86\u3002 \u3000\u3000\u8fd9\u9ed1\u5f71\u5e76\u4e0d\u662f\u70ae\u5f39\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u662f\u6797\u592a\u5e73\u3002 \u3000\u3000\u6797\u592a\u5e73\u4f38\u624b\u635e\u4f50\u4e86\u8fd9\u9762\u7f51\u8eab\u5b50\u8fd8\u662f\u70ae\u5f39\u822c\u5f80\u524d\u98de\u53c8\u98de\u51fa\u4e86\u4e24\u5e72\u4e08\xb7\u53bb\u52bf\u624d\u7f13\u5382\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u90ed\u5927\u8def\u548c\u71d5\u4e03\u4e5f\u5df2\u9000\u4e86\u51fa\u5de6\u53ea\u89c1\u6797\u592a\u5e73\u4e00\u53ea\u624b\u6293\u6839\u6a2a\u679d\u53ea\u4e88\u6293\u4f50\u90a3\u9762\u5927\u7f51\xb7\u51ed\u7a7a\u540a \u5728\u90a3\u91cc\uff0c\u8fd8\u5728\u4e0d\u505c\u7684\u6643\u6765\u6643\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u5fc3\u4e5f\u8fd8\u5728\u8df3\xb7\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u5382\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u8fd9\u6b21\u82e5\u4e0d\u662f\u4f60\xb7\u6211\u53ea\u4f2f\u5c31\u771f\u7684\u5df4\u81ea \u6295\u7f57\u7f51\u5382\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u7528\u4e0d\u8c22\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8c22\u4f60\u8c22\u8c01\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8c22\u4f60\u80cc\u5f8c\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8f6c\u8fc7\u5934\u624d\u53d1\u73b0\u738b\u52a8\u94c1\u9752\u8138\u7ad9\u5728\u4ed6\u8eab\u5f8c\u3002 \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u6211\u5df2\u7ecf\u4e0d\u80fd\u8df3\u589e\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u4f60\u521a\u2026\u2026\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u521a\u662f\u738b\u8001\u5927\u7528\u529b\u628a\u6211\u63b7\u8fc7\u6765\u7684\u5426\u5219\u6211\u90a3\u6709\u8fd9\u9ebd\u5feb\uff1f\u201d \u3000\u3000\u4e16\u4e0a\u7684\u786e\u6ca1\u6709\u90a3\u9ebd\u5feb\u7684\u4eba\uff0c\u82e5\u4e0d\u662f\u501f\u4e86\u738b\u52a8\u63b7\u4e4b\u529b\xb7\u8c01\u90fd\u4e0d\u53ef\u4ee5\u6709\u8fd9\u9ebd\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u4f2f\u4f2f\u539f\u4e86\u738b\u52a8\u4e00\u773c\u8d54\u7b11\u9053\u201c\u770b\u6765\u738b\u8005\u5927\u7684\u529b\u6c14\u5012\u771f\u4e0d \u3000\u3000\u6797\u592a\u5e73\u9053\u4f46\u738b\u8001\u5927\u5374\u5f88\u4f69\u670d\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f69\u670d\u6211\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4ed6\u7684\u529b\u6c14\u867d\u5927\uff0c\u4f60\u7684\u80c6\u5b50\u66f4\u5927\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u4e86\u4ed6\u670d\u9053\u201c\u4f60\u96be\u9053\u5b9a\u8981\u8c61\u7334\u5b50\u6837\u540a\u5728\u6811\u4e0a\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u6211\u65e9\u5c31\u60f3\u4e0b\u53bb\u4e86\u53ea\u53ef\u501f\u6211\u7684\u817f\u4e0d\u542c\u8bdd\u3002\u201d \u3000\u3000\u738b\u52a8\u76f4\u6ca1\u6709\u5f00\u53e3\xb7\u71d5\u4e03\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5728\u77aa\u90ed\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u82e6\u7b11\u9053\u201c\u770b\u6765\u6211\u4eca\u5929\u975e\u4f46\u8fde\u4ef6\u4e8b\u90fd\u6ca1\u6709\u505a\u5bf9\uff0c\u8fde\u8bdd\u90fd\u6ca1\u6709\u8bf4\u5bf9\u8fc7\u53e5\u3002\u201d \u3000\u3000\u71d5\u4e03\u8fd9\u624d\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u8fd9\u53e5\u8bdd\u603b\u7b97\u8bf4\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4e94 \u3000\u3000\u5c4b\u4e88\u91cc\u5014\u8d77\u4e86\u706f\u3002 \u3000\u3000\u684c\u4e0a\u9664\u4e86\u706f\u4e4b\u5916\uff0c\u8fd8\u6709\u5f20\u7eb8\u6761\u3001\u4e00\u628a\u5200\u548c\u9152\u3002 \u3000\u3000\u56e0\u4e3a\u90ed\u5927\u8def\u5230\u6700\u5f8c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u5c06\u8fd9\u628a\u5200\u4ece\u6811\u4e0a\u62d4\u4e0b\u6765\u5f53\u7136\u66f4\u5fd8\u4e0d\u5382\u5c06\u90a3\u9152\u4e5f\u5e26\u56de\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u957f\u5f97\u867d\u4e0d\u8c61\u725b\u5374\u5b9e\u5728\u6709\u70b9\u725b\u813e\u6c14\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u8fd8\u5f88\u5f97\u610f\u7b11\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u62d4\u5200\u6ca1\u5173\u7cfb\u7684\uff0c\u65e9\u5c31\u7ec6\u9053\u4ed6\u4eec\u8fd9\u6b21\u8981\u6362\u4e2a\u65b0\u9c9c\u7684\u6cd5\u5b50 \u8fd9\u6cd5\u5b50\u662f\u4e0d\u662f\u65b0\u9c9c\u7684\u5f88\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u65b0\u9c9c\u6781\u4e86\u6bd4\u7f51\u91cc\u7684\u9c7c\u8fd8\u65b0\u9c9c\u3002\u201d \u3000\u3000\u4ed6\u62ff\u8d77\u4e86\u684c\u4e0a\u7684\u5200\uff0c\u63a5\u53c8\u9053\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u8fd9\u628a\u5200\u662f\u6e2f\u5907\u5272\u4ec0\u9ebd\u8089\u7684\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u662f\u4e0d\u662f\u5272\u9c7c\u8089\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u603b\u7b97\u53c8\u8bf4\u5bf9\u4e86\u53e5\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u6211\u4e0d\u5982\u7d22\u4ef6\u5c31\u505a\u6761\u9189\u9c7c\u5427\u3002\u201d \u3000\u3000\u4ed6\u6367\u8d77\u9152\u4e8e\u5634\u91cc\u8fd8\u54fa\u54fa\u9053\u201c\u9189\u867e\u65e2\u7136\u662f\u6c5f\u5357\u7684\u7f8e\u5473\xb7\u9189\u9c7c\u7684\u6ecb\u5473\u60f3\u5fc5\u4e5f\u6c34\u9519\u3002\u201d \u3000\u3000\u4f46\u4ed6\u7684\u9152\u8fd8\u6ca1\u6709\u559d\u5230\u5634\u738b\u52a8\u7a81\u7136\u53c8\u5c06\u9152\u5b50\u67aa\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\uff0c\u9053\u201c\u4f60\u51e0\u65f6\u4e5f\u53d8\u6210\u4e86\u4e2a\u548c\u6211\u6837\u7684\u9152\u9b3c\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u9152\u559d\u4e0d\u5f97\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u521a\u8fd8\u559d\u5f97\u73b0\u5728\u4e3a\u4ec0\u9ebd\u559d\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u521a\u662f\u521a\u73b0\u5728\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u71d5\u4e03\u9650\u6b8a\u4e8e\u8f6c\u4e86\u8f6c\u9053\u201c\u4f60\u521a\u5c06\u8fd9\u575b\u6d12\u6548\u5728\u90a3\u91cc\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u95e8\u53e3\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u521a\u6211\u4eec\u90fd\u5728\u6811\u6797\u91cc\u95e8\u53e3\u662f\u4e0d\u662f\u6ca1\u6709\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u8fd9\u9152\u73b0\u5728\u5df2\u559d\u4e0d\u5f97\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u5c31\u5728\u521a\u90a3\u4f1a\u513f\u5de5\u592b\u91cc\u5df2\u6709\u4eba\u5728\u8fd9\u9152\u91cc\u535c\u4e86\u6bd2\uff1f\u201d\u71d5\u4e03\u9053\u201c\u521a\u90a3\u4e00\u4f1a\u513f\u5de5 \u592b\xb7\u5df2\u8db3\u591f\u5728\u516b\u5341\u9152\u91cc\u4e0b\u6bd2\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5931\u7b11\u9053\u201c\u4f60\u4eec\u4e5f\u672a\u514d\u5c06\u90a3\u4e9b\u5165\u8bf4\u5f97\u592a\u53ef\u6015\u4e86\u96be\u9053\u4ed6\u4eec\u771f\u7684\u662f\u65e0\u5b54\u4e0d\u5165\u8fde\u70b9\u5bb3\u4eba\u7684 \u673a\u4f1a\u90fd\u4e0d\u4f1a\u9519\u8fc7\u9ebd\uff1f\u201d\u2026\u738b\u52a8\u4e5f\u4e0d\u8bf4\u8bdd\u5ffd\u7136\u8d70\u5230\u95e8\u5916\u5c06\u624b\u91cc\u7684\u9152\u91cd\u91cd\u5f80\u5730\u4e0a\u4e00\u987f\u3002 \u3000\u3000\u5b50\u7c89\u788e\xb7\u9152\u6d41\u5f97\u6ee1\u5730\u90fd\u662f\u3002\u4e00 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u95fb\u95fb\u9053\uff1a\u201c\u771f\u53ef\u60dc\u597d\u2019\u2026\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u5ffd\u7136\u505c\u987f\u4eba\u4e5f\u7a81\u7136\u6014\u4f4f\u3002 \u3000\u3000\u6761\u5f88\u5c0f\u5f88\u5c0f\u7684\u86c7\u6b63\u4ece\u788e\u88c2\u7684\u9152\u5b50\u91cc\u6162\u6162\u7684\u722c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u6761\u86c7\u5c0f\u5f97\u51fa\u5947\u4f46\u8d8a\u5c0f\u7684\u86c7\u8d8a\u6bd2\u3002 \u3000\u3000\u90ed\u5927\u8def\u8138\u8272\u4e5f\u53d8\u4e86\u5fcd\u4e0d\u4f4f\u53c8\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u8d2d\u7483\u9053\u201c\u770b\u6765\u8fd9\u4e9b\u4eba\u5012\u771f\u662f\u65e0\u5b54\u4e0d\u5165\u3002\u201d \u3000\u3000\u71d5\u4e03\u7a81\u7136\u5931\u58f0\u9053\u201c\u65e0\u5b54\u4e0d\u5165\u8d64\u7ec3\u86c7\u3002\u201d \u3000\u3000\u4ed6\u5403\u60ca\u7684\u770b\u738b\u52a8\uff0c\u53c8\u9053\u201c\u662f\u4e0d\u662f\u65e0\u8671\u4e0d\u5165\u8d64\u7ec3\u86c7\uff1f\u201d \u3000\u3000\u738b\u52a8\u94c1\u9752\u8138\xb7\u9992\u9992\u7684\u8f6c\u56de\u8eab\u8d70\u56de\u5c4b\u4e8e\u91cc\u5728\u706f\u9189\u5750\u4e0b\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u5c45\u7136\u6ca1\u6709\u8eba\u5230\u5e8a\u4e0a\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u53c8\u8ffd\u4e86\u8fc7\u6765\u8feb\u95ee\u9053\u201c\u662f\u4e0d\u662f\u4ed6\uff1f\u2026\u2026\u7a76\u7adf\u662f\u4e0d\u662f\u4ed6\u201d \u3000\u3000\u738b\u52a8\u53c8\u6c89\u9ed8\u5de5\u5f88\u4e45\u7ec8\u536b\u2606\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u957f\u957f\u5410\u51fa\u53e3\u6c14\u6b65\u6b65\u5f80\u5f8c\u9000\u5ffd\u7136\u95f4\u7f16\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u6b21\u662f\u4ed6\u8eba\u5230\u5e8a\u4e0a\u53bb\u5382 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u8ffd\u4e86\u8fc7\u6765\xb7\u8ffd\u95ee\u662f\u201c\u65e0\u5144\u4e0d\u5165\u8d64\u7ec3\u86c7\u662f\u4ec0\u9ebd\u73a9\u610f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u4f46\u4eba\u5df2\u8c61\u662f\u8f6f\u5382\xb7\u8fde\u8bf4\u8bdd\u90fd\u53d8\u5f97\u6709\u6c14\u65e0\u529b\u7684\u6837\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u82e6\u7b11\u9053\u201c\u6211\u82e5\u8ba4\u5f97\u4ed6\xb7\u8fd8\u80fd\u6d3b\u5230\u73b0\u5728\u624d\u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u8df3\u8d77\u51b2\u5230\u571f\u52a8\u9762\u524d\u9053\u201c\u53ef\u662f\u4f60\u4e00\u5b9a\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u738b\u52a8\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\xb7\u9053\u201c\u6211\u73b0\u5728\u8fd8\u6d3b\u3002\u201d \u3000\u3000\u71d5\u4e03\u56db\u9053\u201c\u8ba4\u5f97\u4ed6\u7684\u4eba\u5c45\u7136\u8fd8\u80fd\u6d3b\xb7\u53ef\u771f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u738b\u52a8\u8138\u5e7f\u7684\u7b11\u5bb9\u6e10\u6e10\u6d88\u5931\uff0c\u7ec8\u4e8e\u53f9\u4e86\u58f0\u201c\u7684\u786e\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u8981\u53eb\u4e86\u8d77\u6765\u9053\u201c\u4f60\u4eec\u8bf4\u7684\u7a76\u7adf\u662f\u4eba\uff1f\u8fd8\u662f\u86c7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4eba\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u4eba\u7684\u540d\u5b57\u53eb\u8d64\u7ec3\u86c7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u800c\u4e14\u65e0\u5b54\u4e0d\u5165\uff0c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\xb7\u4f60\u53ea\u8981\u6709\u4e00\u70b9\u70b9\u758f\u5ffd\u4ed6\u5c31\u80fd\u6bd2\u6b7b\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u2606\u70b9\u70b9\u758f\u5ffd\uff1f\u4efb\u4f55\u4eba\u90fd\u96be\u514d\u6709\u70b9\u70b9\u758f\u5ffd\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u5382\u66f0\u6c14\u9053\u201c\u6240\u4ee5\u4ed6\u82eb\u8981\u6bd2\u6b7b\u4f60\xb7\u4f60\u53ea\u6709\u6761\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u6761\u8def\uff1f\u201d\u71d5\u4e03\u9053\u201c\u88ab\u4ed6\u6bd2\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u6c34\u7981\u5012\u62bd\u5382\u53e3\u51c9\u6c14\u9053\u201c\u521a\u90a3\u4e9b\u5bb3\u4eba\u7684\u82b1\u6837\xb7\u5c31\u5168\u90fd\u662f\u4ed6\u73b0\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u4eba\u535c\u6bd2\u7684\u529f\u592b\u867d\u7136\u5df2\u53ef\u7b97\u662f\u5929\u4e0b\u7b2c\u4f46\u522b\u7684\u672c\u4e8b\u5374\u672c\u5927\u600e\u9ebd\u6837\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u677e\u4e86\u53e3\u6c14\u9053\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u591a\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ea\u5bf9\u501f\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd8\u6709\u8c01\uff1f\u201d\u71d5\u4e03\u9053\u201c\u5e72\u624b\u4e8e\u773c\u5a31\u86c6\u795e\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u5e72\u624b\u5e72\u773c\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\u8fd9\u4eba\u6536\u53d1\u6697\u5668\u65f6\uff0c\u5c31\u597d\u50cf\u6709\u2500\u5e72\u53ea\u624b\xb7\u4e8e\u53ea\u773c\u775b\u6837\xb7\u636e\u8bf4\u4ed6\u5168\u8eab \u535c\u535c\u90c1\u662f\u6697\u5668\u8fde\u9f3b\u4e8e\u90fd\u80fd\u53d1\u51fa\u662d\u5668 \u3000\u3000\u90ed\u5927\u8def\u8198\u5de8\u52a8\u773c\u5ffd\u7136\u7b11\u9053\u201c\u597d\u6781\u5382\uff0c\u6211\u53ea\u8981\u2018\u89c1\u5230\u8fd9\u4eba\u7684\u9762\u5c31\u5148\u6253\u6241\u4ed6\u7684\u9f3b\u5b50\u518d\u8bf4\u3002\u201d \u53a2\u4e03\u7728\u7728\u773c\u9053\u201c\u4f46\u4f60\u82e5\u89c1\u5230\u6551\u52b3\u6551\u96be\u7ea2\u5a18\u5b50\u53ea\u4f2f\u5c31\u820d\u4e0d\u5f97\u6253\u5382\u3002\u2606 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6551\u82e6\u6551\u96be\u7ea2\u5a18\u5b50\uff1f\u8fd9\u540d\u5b57\u542c\u8d77\u6765\u5012\u8c61\u662f\u4e2a\u5927\u597d\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u7684\u786e\u662f\u4e2a\u597d\u4eba\u77e5\u9053\u4e16\u4eba\u5927\u591a\u5728\u82d7\u96be\u4e2d\u6240\u4ee5\u5fc3\u60f3\u8981\u53eb\u4ed6\u851a\u65e9\u70b9\u8d85\u751f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u606f\u9053\u201c\u8fd9\u9ebd\u6837\u542c\u6765\xb7\u5979\u53c8\u4e0d\u8c61\u662f\u4e2a\u597d\u4eba\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u5c31\u7b97\u4ece\u516b\u5341\u4e07\u4e2a\u4eba\u91cc\u9762\uff0c\u4e5f\u6311\u4e0d\u51fa\u8fd9\u9ebd\u6837\u2018\u4e2a\u597d\u4eba\u6765\u3002\u201d\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5979\u53c8\u6709\u4ec0\u9ebd\u7279\u522b\u672c\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u677f\u80a1\xb7\u51b7\u51b7\u9053\uff1a\u201c\u5979\u7684\u672c\u4e8b\uff0c\u4f60\u6700\u597d\u4e0d\u8981\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u5979\u662f\u4e0d\u662f\u4e2a\u72e0\u6f02\u4eae\u7684\u5973\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u7b97\u662f\u73b0\u5728\u4e5f\u5df2\u662f\u4e2a\u8005\u592a\u5a46\u4e86\u5f88\u6444\u4eae\u7684\u8005\u592a\u5a46\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5979\u5df2\u6709\u4e03\u516b\u5341\u5c81\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u5012\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e94\u516d\u5341\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u597d\u50cf\u8fd8\u4e0d\u5230\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56db\u5341\u4e0a\u4e0b\uff1f\u2019 \u3000\u3000\u71d5\u4e03\u9053\u201c\u6015\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u90a3\u6b63\u662f\u72fc\u864e\u4e4b\u5e74\u600e\u9ebd\u80fd\u7b97\u8001\u592a\u5a46\u5462\u201d \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u773c\xb7\u9053\u201c\u5979\u5e74\u7eaa\u5927\u5c0f\u548c\u4f60\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u4f60\u53c8\u5fc3\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u51e0\u65f6 \u5173\u5fc3\u5382\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u5173\u5fc3\u4e3a\u4ec0\u9ebd\u7b11\u5f97\u5c31\u8c61\u662f\u6761\u571f\u72d7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u672c\u6765\u5c31\u662f\u6761\u4e0a\u72d7\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u77aa\u4e86\u4ed6\u773c\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u5382\u3002 \u3000\u3000\u90ed\u5927\u8def\u7acb\u523b\u53c8\u4e58\u673a\u95ee\u9053\u201c\u542c\u4f60\u8fd9\u9ebd\u8bf4\xb7\u5979\u7684\u672c\u4e8b\u5b9a\u662f\u4e13\u95e8\u7528\u6765\u5bf9\u4ed8\u7537\u4eba\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u677f\u8d77\u4e86\u8138\u9053\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u7a76\u7adf\u6709\u4ec0\u9ebd\u672c\u4e8b\u53ea\u77e5\u9053\u7537\u4eba\u6b7b\u5728\u5979\u624b\u4e0a\u7684\u53ef\u771f\u4e0d \u5c11\uff0c\u201d \u3000\u3000\u6797\u592a\u5e73\u2018\u76f4\u9760\u5728\u65c1\u8fb9\u7684\u6905\u5b50\u4e0a\u517b\u795e\xb7\u5ffd\u7136\u9053\u201c\u90a3\u5428\u7a3b\u8349\u4eba\u662f\u4e0d\u662f\u5979\u505a\u7684\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4e0d \u662f\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e0d\u662f\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u89c1\u9001\u7ec8\u50ac\u547d\u7b26\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u76b1\u4e86\u76b1\u7709\u9053\u201c\u507f\u547d\u7b26\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u4eba\u4e0d\u4f46\u6709\u809a\u5b50\u9b3c\u58eb\u610f\u6652\u8fd8\u6709\u53cc\u5de7\u624b\u6613\u5bb9\u6539\u626e\u3001\u6d88\u606f\u673a\u5173\u3001\u7a3b\u5de7\u662d\u5668\u3001\u5947\u95e8\u5175 \u5203\xb7\u53ef\u8bf4\u662f\u6837\u6837\u7cbe\u901a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u76ee\u5149\u95ea\u52a8\xb7\u54fa\u54fa\u9053\u4e00\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u2606\u6761\u86c7\u3001\u53ea\u5a31\u86c6\u3001\u4e00\u53ea\u874e\u5b50\xb7\u9053\u50ac\u547d\u7b26\u73b0\u5728\u53ea\u5dee\u53ea\u8005\u9e70\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u53c8\u9009\u6c11\u201c\u521a\u6211\u8ddf\u738b\u8001\u5927\u8fdb\u5165\u6811\u6797\u7684\u65f6\u5019\uff0c\u6b63\u597d\u770b\u5230\u6761\u4eba\u5f71\xb7\u4ece\u90a3\u6db5\u7f51\u85e9\u4e0b\u7684\u6811 \u68a2\u4e0a\u98de\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u6e14\u7f51\u672c\u5c31\u4e0d\u4f1a\u76ee\u5df1\u4ece\u6811\u4e0a\u843d\u4e0b\u6765\u7684\u6811\u4e0a\u5f53\u7136\u6709\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u4eba\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u82e6\u7b11\u9053\u201c\u90a3\u65f6\u6211\u5df2\u88ab\u738b\u8005\u5927\u7528\u529b\u63b7\u4e86\u51fa\u53bb\u600e\u9ebd\u8fd8\u987e\u5f97 \u3000\u3000\u71d5\u4e03\u9053\u201c\u5df4\u51b2\u5929\u9738\u738b\u9e70\u201d \u3000\u3000\u90ed\u5927\u8def\u4e00\u62cd\u5df4\u638c\u9053\u201c\u4e94\u4e2a\u98ce\u7b49\uff0c\u4e94\u4e2a\u4eba\u73b0\u5728\u603b\u7b97\u5168\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u4e94\u4e2a\u4eba\u4e2d\u4e0d\u4f46\u8f7b\u529f\u8981\u7b97\u9738\u738b\u9e70\u6700\u9ad8\xb7\u636e\u8bf4\u6b66\u529f\u4e5f\u662f\u5730\u6700\u9ad8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ee5\u6211\u770b\u8fd9\u4e94\u4eba\u4e2d\u6700\u96be\u5bf9\u4f55\u7684\u8fd8\u662f\u90a3\u6551\u82e6\u6551\u96be\u7684\u7ea2\u5a18\u5b50\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u90fd\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u7537\u4eba\u82e5\u4e0d\u597d\u8272\u4ed6\u4fbf\u6709\u5929\u5927\u7684\u672c\u4e8b\u4e5f\u4f7f\u4e0d\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u957f\u53f9\u9053\u201c\u4f46\u5929\u4e0b\u7684\u7537\u4eba\u53c8\u6709\u51e0\u4e2a\u590f\u4e0d\u597d\u8272\u5462\uff1f\u201d \u3000\u3000\u571f\u52a8\u76f4\u6c89\u8138\u5750\u5728\u90a3\u91cc\u9010\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u80fd\u4e0d\u52a8\u7684\u65f6\u5019\u4ed6\u7edd\u4e0d\u4f1a\u52a8\u7684\u3002 \u3000\u3000\u71d5\u4e03\u642c\u4e86\u5f20\u51f3\u5b50\uff0c\u5728\u4ed6\u5bf9\u9762\u5750\u65a4\u4e0b\u6765\u9053\u201c\u4f60\u770b\u5230\u5382\u90a3\u4e9b\u98ce\u7b2c\u4e5f\u5c31\u77e5\u9053\u4ed6\u4eec\u662f\u6765\u627e\u4f60\u9ebb\u70e6 \u7684\u5382\u3002\u201d\u90ed\u5927\u8def\u4e5f\u642c\u4e86\u5f20\u51f3\u5b50\u8fc7\u6765\uff0c\u9053\u6240\u4ee5\u4f60\u8981\u8d76\u6211\u4eec\u8d70\uff0c\u56e0\u4e3a\u4f60\u77e5\u9053\u8fd9\u4e94\u4e2a\u4eba\u65e0\u8bba\u5230\u4e86\u90a3 \u91cc\u90fd\u4f1a\u5c06\u90a3\u5730\u65b9\u641e\u5f97\u4e00\u573a\u7cca\u6d82\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e0d\u613f\u5c06\u6211\u4eec\u4e5f\u626f\u5165\u4e86\u90a3\u6dcc\u4e8e\u584c\u7cca\u6d82\u7684\u6d51\u6c34\u91cc\u4f17\uff0c\u6240\u4ee5\u624d\u8981\u8d76\u6211\u8ba2\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u4f60\u5374\u4e0d\u77e5\u9053\u6211\u8ba2\u65e9\u5df2\u5728\u90a3\u6dcc\u4e88\u6d51\u6c34\u91cc\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ece\u8ba4\u5f97\u4f60\u7684\u90a3\u5929\u5f00\u59cb\u6211\u4eec\u5df2\u7ecf\u5728\u91cc\u9762\u4e86\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u4f60\u65e0\u8bba\u5728\u4ec0\u9ebd\u5730\u65b9\u6211\u4eec\u4e5f\u5b9a\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u624d\u60f3\u8d76\u6211\u4eec\u8d70\u5df2\u7ecf\u592a\u8fdc\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u770b\u4ed6\u4eec\uff0c\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u73b0\u5728\u5df2\u7ecf\u7528\u4e0d\u518d\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u4ed6\u751f\u4f2f\u81ea\u5df1\u5f00\u53e3\u5c31\u4f1a\u6709\u70ed\u6cea\u593a\u7736\u800c\u51fa\u3002\u670b\u53cb \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u662f\u591a\u9ebd\u7b80\u5355\u5374\u4e49\u591a\u9ebd\u9ad8\u8d35\u3002 \u3000\u3000\u738b\u52a8\u634f\u7d2b\u53cc\u624b\uff0c\u4e00\u5b87\u5b87\u9053\u201c\u4f60\u4eec\u7684\u786e\u90fd\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u4f60\u53ea\u8981\u771f\u6b63\u61c2\u5f97\u8fd9\u53e5\u8bdd\u7684\u610f\u4e49\uff0c\u5c31\u5df2\u4ec0\u9ebd\u90fd\u4e0d\u5fc5\u518d\u8bf4\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u6797\u592a\u5e73\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u7ff1\u5927\u8def\u7d27\u7d27\u63e1\u8d77\u738b\u52a8\u7684\u624b\u3002\u4ed6\u851a\u53ea\u8981\u80fd\u542c\u5230\u8fd9\u53e5\u8bdd\xb7\u4e5f\u5df2\u8db3\u591f\u3002 \u3000\u3000\u4ed6\u851a\u65e2\u7136\u6ca1\u6709\u95ee\u8d77\u8fd9\u4e94\u4eba\u600e\u4f1a\u548c\u738b\u52a8\u7ed3\u7684\u4ec7\u4e5f\u6ca1\u95ee\u8fd9\u9ebb\u70e6\u662f\u4ece\u54ea\u91cc\u6765\u3002 \u3000\u3000\u738b\u52a8\u4e0d\u8bf4\u4ed6\u4eec\u5c31\u4e0d\u95ee\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8ba2\u552f\u7684\u95ee\u9898\u5c31\u662f\u201c\u600e\u9ebd\u6837\u5c06\u8fd9\u9ebb\u9885\u6253\u53d1\u8d70\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u770b\u5230\u90a3\u53ea\u98ce\u7b2c\u5c31\u77e5\u9053\u6709\u9ebb\u70e6\u6765\u5382\u3002\u201d \u3000\u3000\u4e07\u52a8\u9053\u201c\u90a3\u98ce\u7b2c\u672c\u662f\u79cd\u8b66\u544a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u4eec\u65e2\u7136\u8981\u627e\u4f60\u7684\u9ebb\u70e6\uff0c\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u8b66\u544a\u4f60\u8ba9\u4f60\u9632\u5907\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u60f3\u8981\u6211\u6b7b\u5f97\u592a\u5feb\u3002\u201d \u3000\u3000\u4ed6\u8138\u8272\u53d1\u80b2\u6162\u6162\u7684\u63a5\u9053\u201c\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u4e2a\u4eba\u5728\u7b49\u6b7b\u65f6\u7684\u90a3\u949f\u6050\u6016\u6bd4\u6b7b\u8fd8\u75db\u82e6\u5f97\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u95e8\u6c14\u9053\u201c\u770b\u6765\u8fd9\u9ebb\u70e6\u5f53\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u536b\u52a8\u9053\u201c\u7684\u786e\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u53ea\u63aa\u4ed6\u4eec\u8fd8\u662f\u7b97\u9519\u4e86\u70b9\u3002\u201d \u3000\u3000\u71d5\u4ea1\u901a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4eec\u867d\u7136\u6709\u4e94\u4e2a\u4eba\uff0c\u6211\u4eec\u4e5f\u6709\u56db\u4e2a\uff0c\u6211\u4eec\u4e3a\u4ec0\u9ebd\u8981\u6050\u6016\uff1f\u4e3a\u4ec0\u9ebd\u5728\u75db\u82e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f46\u4ed6\u6253\u81f3\u5c11\u603b\u6bd4\u6211\u4f55\u5360\u4e86\u70b9\u4f18\u52bf\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u660e\u67aa\u6613\u8eb2\u6697\u7bad\u96be\u9646\u8fd9\u53e5\u8bdd\u4f60\u96be\u9053\u4e0d\u6444\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u503c\u53ef\u662f\u6211\u4e0d\u6015\u3002\u201d\u71d5\u4e03\u77aa\u4ed6\u9053\u201c\u4f60\u4f2f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6015\u5f25\u3002\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u8d50\u7136\u2018\u7b11\uff0c\u5374\u53c8\u7acb\u523b\u677f\u8d77\u4e86\u8138\u626d\u8f6c\u5382\u5934\u3002\u5176\u5b9e\u4ed6\u5f53\u7136\u4e5f\u7530\u5f97\u90ed\u5927\u8def\u7684\u610f \u601d\xb7\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u6837\u3002\u50cf\u4ed6\u4eec\u8fd9\u79cd\u4eba\u5c31\u6015\u522b\u4eba\u5bf9\u4ed6\u8ba2\u597d\u53ea\u4f2f\u88ab\u522b\u4eba\u611f\u52a8\u3002 \u3000\u3000\u4f60\u82e5\u80fd\u771f\u7684\u611f\u52a8\u4ed6\u4eec\uff0c\u5c31\u7b97\u8981\u4ed6\u4eec\u5c06\u8111\u888b\u5207\u4e0b\u6765\u7ed9\u4f60\xb7\u4ed6\u8ba2\u4e5f\u4e0d\u4f1a\u76b1\u76b1\u7709\u5934\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5175\u6765\u5c06\u6321\uff0c\u6c34\u6765\u571f\u62d6\uff0c\u8fd9\u79cd\u4eba\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u4e86\u4e0d\u8d77\u9664\u4e86\u9b3c\u9b3c\u795f\u795f\u7684\u6697\u4e2d\u5bb3\u4eba\u5916 \u6211\u770b\u4ed6\u4eec\u7684\u771f\u529f\u592b\u4e5f\u6709\u9650\u7684\u5f88\u3002\u201d \u3000\u3000\u4ed6\u63a5\u53c8\u9053\u201c\u73b0\u5728\u7684\u95ee\u9898\u53ea\u4e0d\u8fc7\u662f\u4ed6\u4eec\u662f\u4ec0\u9ebd\u65f6\u5019\u5b8b\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u6211\u53ea\u77e5\u9053\u4ed6\u4eec\u82e5\u8fd8\u6ca1\u6709\u9001\u6211\u7684\u7ec8\u5c31\u7edd\u4e0d\u4f1a\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u73b0\u5728\u662f\u8c01\u9001\u8c01\u7684\u7ec8\u8fd8\u96be\u8bf4\u5f97\u5f88\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u90ed\u5927\u8def\u53ef\u7231\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u6c34\u8fdc\u90fd\u90a3\u9ebd\u81ea\u4fe1\u90a3\u9ebd\u4e50\u89c2\u3002 \u3000\u3000\u8fd9\u79cd\u4eba\u5c31\u7b97\u660e\u77e5\u5929\u8981\u584c\u4e0b\u6765\xb7\u4e5f\u4e0d\u4f1a\u53d1\u6101\u7684\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u4e2a\u4eba\u53ea\u8981\u6709\u4fe1\u5fc3\u65e0\u8bba\u4ec0\u9ebd\u56f0\u96be \u90fd\u53ef\u89e3\u51b3\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u81ea\u5df1\u6709\u4fe1\u5fc3\u540c\u65f6\u4e5f\u5c06\u8fd9\u4fe1\u5fc3\u7ed9\u5382\u522b\u4eba\u3002 \u3000\u3000\u5e72\u52a8\u7684\u8138\u8272\u4e5f\u6e10\u6e10\u5f00\u6717\u4e86\u8d77\u6765\u5ffd\u7136\u9053\u201c\u4ed6\u4eec\u867d\u7136\u5360\u4e86\u70b9\u4f18\u52bf\u4f46\u6211\u4e5f\u6709\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u90ed\u5927\u8def\u62a2\u95ee\u9053\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7761\u89c9\u3002\u201d\u90ed\u5927\u8def\u6014\u4e86\u6014\u5931\u7b11\u9053\u201c\u8fd9\u79cd\u6cd5\u5b50\u5927\u6982\u4e5f\u53ea\u6709\u4f60\u60f3\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u52a8\u53cd\u95ee\u9053\u201c\u8fd9\u6cd5\u5b50\u6709\u4ec0\u9ebd\u672f\u597d\uff1f\u8fd9\u5c31\u53eb\u4ee5\u9038\u5f85\u52b3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u67cf\u5c39\u9053\u201c\u5bf9\xb7\u8981\u7761\u73b0\u5728\u5c31\u7761\u517b\u8db3\u4e86\u7cbe\u795e\u597d\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f46\u8981\u7761\u4e5f\u5f97\u5206\u73ed \u7761\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u9519\xb7\u6211\u8ddf\u4f60\u9632\u5b88\u4e0a\u534a\u591c\xb7\u5230\u53c1\u66f4\u65f6\u518d\u53eb\u738b\u8001\u5927\u548c\u6797\u592a\u5e73\u8d77\u6765\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u9053\u201c\u8fd9\u6837\u5b50\u4e0d\u884c\u8fd8\u662f\u6211\u8ddf\u4f60\u4e00\u73ed\u7684\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u539f\u4e86\u85cf\u4e03\u4e00\u773c\u9053\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\u7684\u8bdd\u592a\u591a\xb7\u7530\u5f97\u5546\u5174\u8d77\u6765\xb7\u6015\u522b\u4eba\u8fdb\u4e86\u5c4b\u5b50\u90fd\u4e0d\u77e5 \u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u8d70\u4e86\u51fa\u53bb\u9611\u4e3a\u4ed6\u7684\u8138\u597d\u50cf\u5ffd\u7136\u53c8\u6709\u70b9\u53d1\u7ea2\u4e86\u3002 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u8fd8\u662f\u6211\u8ddf\u71d5\u4e03\u4e00\u73ed\u7684\u597d\u4e24\u4e2a\u4eba\u8c08\u8c08\u8bf4\u8bf4\uff0c\u624d\u4e0d\u4f1a\u56fa\u89c9\u3002\u201d \u3000\u3000\u4ed6\u7792\u91cc\u8bf4\u8bdd\u4eba\u5df4\u613f\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u65e0\u8bba\u522b\u4eba\u8bf4\u4ec0\u9ebd\uff0c\u4ed6\u8fd8\u662f\u975e\u8ddf\u71d5\u4e03\u73ed\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8eab\u4e0a\u5c31\u597d\u50cf\u6709\u6839\u7ebf\u8fde\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u770b\u4ed6\u4eec\u8d70\u51fa\u53bb\u5ffd\u7136\u7b11\u4e86\uff0c\u6717\u8d61\u9053\uff1a\u201c\u6211\u6709\u65f6\u771f\u5947\u602a\uff0c\u5c0f\u90ed\u4e3a\u4ec0\u9ebd\u4f1a\u8fd9\u9ebd\u7b28\u3002\u201d \u3000\u3000\u738b\u52a8\u4e5f\u5728\u7b11\u5fae\u7b11\u9053\u201c\u4f60\u653e\u5fc3\xb7\u4ed6\u7edd\u4e0d\u4f1a\u518d\u7b28\u5f88\u4e45\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5176\u5b9e\u6211\u56e0\u5e0c\u671b\u4ed6\u518d\u591a\u7b28\u4e9b\u65f6\u5019\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7b11\u9053\u201c\u56e0\u4e3a\u6211\u89c9\u5f97\u4ed6\u4eec\u8fd9\u6837\u5b50\u5b9e\u5728\u72e0\u6709\u610f\u601d\u3002\u201d \u7b2c\u4e03\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e03\u7ae0 \u3000\u3000\u5ba2\u5385\u91cc\u72e0\u6697\u3002 \u3000\u3000\u71d5\u4e03\u8d70\u8fdb\u5ba2\u5385\xb7\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u8d70\u8fdb\u5ba2\u5385\uff0c\u5750\u5382\u4e0b\u6765\u3002 \u3000\u3000\u661f\u5149\u7167\u8fdb\u7a97\u6237\u7167\u71d5\u4e03\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u597d\u4eae\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u65c1\u8fb9\u770b\u5ffd\u7136\u7b11\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u7684\u773c\u775b\u6709\u65f6\u770b\u6765\u8774\u8c61\u5973\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u677f\u8138\u9053\u201c\u6211\u8fd8\u6709\u4ec0\u9ebd\u5730\u65b9\u8c61\u4ea5\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u7b11\u8d77\u6765\u7684\u65f6\u5019\u4e5f\u6709\u70b9\u8c61\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u54e6\u65e2\u7136\u5f88\u50cf\u5973\u4eba\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u8001\u8ddf\u6211\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u82e5\u771f\u662f\u4e2a\u5973\u4eba\u6211\u5c31\u66f4\u8981\u8ddf\u4f60\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u8d85\u8fc7\u5934\xb7\u7ad9\u4e86\u8d77\u6765\u627e\u706b\u77f3\u70b9\u8d77\u4e2b\u684c\u4e0a\u7684\u706f\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u70b9\u4e0d\u6562\u548c\u90ed\u5927\u8def\u5355\u72ec\u5750\u5728\u9ed1\u6697\u91cc\u3002 \u3000\u3000\u706f\u513f\u4eae\u8d77\u5c06\u4ed6\u7684\u5f71\u5b50\u7167\u5728\u7a97\u6237\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u628a\u5c06\u4ed6\u62c9\u4e86\u8fc7\u6765\u597d\u50cf\u8981\u62b1\u4f50\u4ed6\u7684\u6837\u5b50\u3002\u71d5\u4ea1\u5931\u58f0\u9053\u201c\u4f60\u2026\u2026\u4f60\u5341\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u82e5\u7ad9\u5728\u90a3\u91cc\u5c82\u975e\u521a\u597d\u505a\u90a3\u4e0b\u624b\u4e8e\u773c\u5927\u879f\u8681\u7684\u6d3b\u9776\u5b50\uff1f\u201d \u3000\u3000\u4ed6\u773c\u73e0\u5e72\u8f6c\u773c\u775b\u5ffd\u7136\u4eae\u4e86\u8d77\u6765\uff0c\u54fa\u54fa\u9053\u201c\u8fd9\u5012\u4e5f\u662f\u4e2a\u597d\u4e2d\u610f\u3002\u201d \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u670d\u9053\u201c\u4f60\u8fd8\u4f1a\u6709\u4ec0\u9ebd\u597d\u4e3b\u610f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u5927\u5a31\u8d25\u65e2\u7136\u559c\u6b22\u7528\u6697\u5668\u4f24\u4eba\u6211\u4eec\u4e0d\u5982\u5c31\u7d22\u6027\u66ff\u4ed6\u627e\u51e0\u4e2a\u6d3b\u9776\u5b50\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u76b1\u7709\u9053\u201c\u4f60\u60f3\u627e\u6dee\u505a\u4ed6\u7684\u6d3b\u9776\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7a3b\u8349\u4eba\u3002\u201d \u3000\u3000\u4ed6\u63a5\u53c8\u9053\u201c\u6211\u4eec\u53bb\u628a\u90a3\u4e9b\u7a3b\u8349\u4eba\u642c\u8fdb\u6765\xb7\u5750\u5728\u8fd9\u91cc\uff0c\u4ece\u7a97\u6237\u5916\u9762\u770b\u6765\xb7\u53c8\u6709\u8c01\u80fd\u770b\u5f97\u51fa \u5b83\u4eec\u662f\u672c\u662f\u6d3b\u4eba\u201d \u3000\u3000\u71d5\u4e03\u76b1\u7684\u7709\u5934\u5c55\u5f00\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u5927\u5a31\u8681\u53ea\u8981\u770b\u5230\u7a97\u6237\u4e0a\u7684\u4eba\u5f71\u5c31\u4e00\u5b9a\u4f1a\u624b\u75d2\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7136\u5f8c\u5462\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u5728\u5916\u9762\u7b49\uff0c\u53ea\u5195\u4ed6\u7684\u624b\u75d2\u6211\u4eec\u5c31\u6709\u6cd5\u5b50\u5bf9\u8ba8\u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u541f\uff0c\u6de1\u6de1\u9053\u201c\u4f60\u4ee5\u4e3a\u8fd9\u4e3b\u610f\u5f88\u597d\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u7b97\u4e0d\u597d\u4e5f\u5f97\u8bd5\u8bd5\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u4e00\u76f4\u5728\u8fd9\u91cc\u7b49\u6b7b\uff0c\u603b\u5f97\u60f3\u6cd5\u5b50\u628a\u4ed6\u94b3\u5f15\u51fa \u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u83ab\u5f8c\u4e86\u90a3\u4e9b\u7a3b\u8349\u4eba\u4e5f\u4e00\u6837\u4f1a\u4f24\u4eba\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65e0\u8bba\u5982\u4f55\u7a3b\u8349\u4eba\u603b\u662f\u6b7b\u7684\u603b\u6bd4\u6d3b\u4eba\u597d\u5bf9\u4ed8\u4e9b\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u597d\u5427\u8fd9\u6b21\u6211\u5c31\u542c\u4f60\u7684\u770b\u770b\u4f60\u8fd9\u7b28\u4e3b\u610f\u884c\u4e0d\u884c\u5f97\u901a\u201c \u3000\u3000\u90ed\u5927\u8def\u7b11\u8c0a\u201c\u7b28\u4e3b\u610f\u5e01\u5c11\u603b\u6bd4\u6ca1\u6709\u4e3b\u610f\u597d\u4e9b\u3002\u201d \u3000\u3000\u7a20\u8349\u4eba\u7684\u5f71\u5b50\u6620\u5728\u7a97\u6237\u4e0a\u4ece\u5916\u9762\u770b\u6765\u7684\u786e\u548c\u771f\u4eba\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e9b\u7a3b\u8349\u4eba\u4e0d\u4f46\u7a7f\u8863\u670d\u8fd8\u6234\u5e3d\u5b50\u3002 \u3000\u3000\u4f7c\u5df2\u5f88\u6df1\u98ce\u5439\u5728\u8eab\u535c\u5c31\u597d\u50cf\u5200\u5272\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u867d\u7136\u8eb2\u5728\u5c4b\u4e8e\u4e0b\u907f\u98ce\u7684\u5730\u65b9\u8fd8\u662f\u51b7\u5f97\u53d1\u6296\u3002 \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u73b0\u5728\u8981\u662f\u6709\u70b9\u9152\u559d\uff0c\u5c31\u4e0d\u4f1a\u8fd9\u9ebd\u51b7\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u6709\u60f3\u559d\u9152\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u71d5\u4e03\u5480\u9053\u201c\u8fd9\u5c31\u53eb\u8fd1\u58a8\u8005\u9ed1\u4e2a\u4eba\u82e5\u662f\u5929\u5929\u8ddf\u9152\u9b3c\u5728\u8d77\uff0c\u8fdf\u65e9\u603b\u8981\u53d8\u6210\u4e2a\u9152\u9b3c\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6240\u4ee5\u4f60\u8fdf\u65e9\u4e5f\u603b\u4f1a\u6709\u4e0d\u8ba8\u538c\u5973\u4eba\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u53c8\u677f\u8d77\u8138\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u9890\uff0c\u90ed\u5927\u8def\u53c8\u9053\u201c\u6211\u603b\u60f3\u4e0d\u901a\xb7\u8c61\u738b\u8001\u5927\u8fd9\u79cd\u4eba\u600e\u9ebd\u4f1a\u548c\u90a3\u5927\u8715\u5438\u3001\u8d64\u7ec3\u86c7\u7ed3\u4e0b \u4ec7\u6765\u7684\uff1f\u800c\u4e14\u4ec7\u6068\u7adf\u5982\u6b64\u4e4b\u6df1\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u60f3\u4e0d\u901a\u5458\u597d\u5c31\u4e0d\u8981\u60f3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u96be\u9053\u4e0d\u89c9\u5f97\u5947\u602a\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u89c9\u5f97\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u6211\u4ece\u6765\u4e0d\u60f3\u63a2\u542c\u522b\u4eba\u7684\u79d8\u5bc6\xb7\u5c24\u5176\u662f\u670b\u53cb\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u597d\u4e0d\u4f5c\u58f0\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u7a81\u7136\u542c\u5230\u201c\u5495\u201d\u7684\u58f0\u3002\u71d5\u4e03\u52a8\u5bb9\u9053\u201c\u662f\u4ec0\u9ebd\u4e1c\u897f\u5728\u53eb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u662f\u6211\u7684\u809a\u5b50\u3002\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u997f\u5f97\u8981\u547d\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\u7a81\u7136\u53c8\u542c\u5230\u201c\u683c\u201d\u7684\u58f0\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u6b21\u53c8\u662f\u4ec0\u9ebd\u5728\u54cd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5634\u5507\uff0c\u9053\u201c\u662f\u6211\u7684\u7259\u9f7f\u3002\u201d \u3000\u3000\u4ed6\u5df2\u51b7\u5f97\u8fde\u7259\u9f7f\u90fd\u5728\u6253\u6218\u3002\u90ca\u5927\u8def\u9053\u201c\u4f60\u52a9\u7136\u4f2f\u51b7\u4e3a\u4ec0\u9ebd\u4e0d\u9760\u8fc7\u6765\u70b9\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5618\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u5c31\u662f\u53eb\u4f60\u83ab\u8981\u51fa\u58f0\u7684\u610f\u601d\u4f60\u7684\u56fe\u52b3\u8001\u662f\u4e0d\u505c\xb7\u90a3\u5927\u5a31\u8681\u600e\u4f1a\u73b0\u8eab\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u679c\u7136\u4e0d\u6562\u51fa\u58f0\u4e86\u3002 \u3000\u3000\u4ed6\u4ec0\u9ebd\u90fd\u4e0d\u4f2f\xb7\u4e5f\u4e0d\u6015\u90a3\u4e9b\u4eba\u6765\uff0c\u53ea\u4f2f\u4ed6\u6790\u4e0d\u6765\u3002 \u3000\u3000\u8fd9\u6837\u5b50\u7b49\u4e0b\u53bb\xb7\u5b9e\u5728\u53eb\u4eba\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u6700\u4ee4\u4eba\u53d7\u4e0d\u4e86\u7684\u662f\u8c01\u4e5f\u4e0d\u77e5\u90a3\u4e9b\u4eba\u4ec0\u9ebd\u65f6\u5019\u4f1a\u51fa\u73b0\u4e5f\u8bb8\u8981\u7b49\u4e0a\u597d\u51e0\u5929\xb7\u4e5f\u8bb8\u5c31\u5728\u8fd9\u5239\u90a3 \u95f4 \u3000\u3000\u90ed\u5927\u8def\u6b63\u60f3\u5c06\u624b\u91cc\u63d0\u7684\u6e14\u7f51\u76d6\u5230\u71d5\u4e03\u8eab\u535c\u53bb\u3002 \u3000\u3000\u8fd9\u6e14\u7f51\u53c8\u8f7b\u53c8\u8f6f\u4f46\u5374\u975e\u5e38\u7ed3\u5b9e\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u9ebd\u505a\u7684\u6797\u592a\u5e73\u5c06\u5b83\u5e26\u4e86\u56de\u6765\u90ed\u5927\u8def\u5c31\u51c6\u5907\u7528 \u5b83\u6765\u5bf9\u4ed8\u90a3\u5927\u5a31\u8239\u3002\u51c6\u5907\u4ee5\u7259\u8fd8\u7259\u4ee5\u773c\u8fd8\u773c\u3002 \u3000\u3000\u6e14\u7f51\u867d\u8f7b\xb7\u4f46\u71d5\u4e03\u5fc3\u91cc\u5374\u5145\u6ee1\u6e29\u6696\u4e4b\u610f\u3002\u7a81\u7136\u95f4\xb7\u6761\u4eba\u5f71\u7bad\u822c\u81ea\u5899\u5916\u7a9c\u4e86\u8fdb\u6765\u51cc\u7a7a\u4e2a\u7ffb \u8eab\u6ee1\u5929\u5bd2\u5149\u95ea\u52a8\uff0c\u5df2\u6709\u4e8c\u56db\u535c\u4ef6\u6697\u5668\u66b4\u96e8\u822c\u5c04\u4eba\u4e86\u7a97\u6237\u3002 \u3000\u3000\u8fd9\u4eba\u6765\u5f97\u597d\u5feb\u3002 \u3000\u3000\u6697\u5668\u66f4\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u7adf\u9511\u672a\u770b\u51fa\u4ed6\u8fd9\u4e9b\u6697\u5668\u662f\u600e\u9ebd\u5c04\u51fa\u6765\u7684\u3002 \u3000\u3000\u6697\u5668\u5c04\u51fa\uff0c\u8fd9\u4eba\u811a\u5c16\u70b9\u5730\xb7\u7acb\u523b\u53c8\u817e\u8eab\u800c\u8d77\u51c6\u5907\u7a9c\u4e0a\u5c4b\u810a\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u521a\u64e6\u8d77\u7a81\u7136\u53d1\u73b0\u9762\u5927\u7f51\u5df2\u5f53\u5934\u7f69\u4e86\u4e0a\u6765\uff0c\u4ed6\u7684\u4eba\u6b63\u5f80\u4e0a\u7a9c\xb7\u770b\u6765\u5c31\u597d\u50cf\u662f\u4ed6\u81ea\u5df1 \u5728\u81ea\u6295\u7f57\u7f51\u6837\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\xb7\u8fd8\u60f3\u6323\u8131\u4f46\u8fd9\u6e14\u7f51\u5df2\u8c61\u86db\u4e1d\u822c\u7ee9\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u9ad8\u5174\u5f97\u5fcd\u4e0d\u4f4f\u5927\u53eb\u8d77\u6765\uff0c\u53eb\u9053\u201c\u770b\u4f60\u8fd8\u80fd\u5f80\u90a3\u91cc\u8fdb\u3002\u201d\u85d0\u4e03\u5df2\u7a9c\u8fc7\u53bb\u6c14\u811a\u5f80\u8fd9\u4eba \u8170\u7554\u7684\u201c\u8840\u6d77\u201d\u7a74\u4e0a\u8e22\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u7f51\u4e2d\u53c8\u6709\u5341\u51e0\u70b9\u5bd2\u5149\u66b4\u96e8\u822c\u5c04\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u8f6e\u5230\u90ed\u5927\u8def\u548c\u71d5\u4e03\u5927\u5403\u4e00\u60ca\u4e86\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u540c\u5239\u90a3\u95f4\uff0c\u5899\u5916\u5ffd\u7136\u6709\u53ea\u94a9\u5b50\u98de\u8fdb\u6765\uff0c\u94a9\u4f4f\u4e86\u904d\u7f51\uff0c \u3000\u3000\u94a9\u5b50\u4e0a\u5f53\u7136\u8fd8\u5e26\u6761\u7ef3\u5b50\u3002 \u3000\u3000\u7ef3\u5b50\u5f53\u7136\u6709\u53ea\u624b\u62c9\u3002 \u3000\u3000\u624b\u62c9\u6e14\u7f51\u5c31\u88ab\u62c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6e14\u7f51\u88ab\u62c9\u8d77\u7684\u65f6\u5019\u90ed\u5927\u8def\u5df2\u5411\u71d5\u4e03\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u548c\u71d5\u4e03\u867d\u7136\u540c\u65f6\u5403\u4e86\u60ca\u4f46\u6697\u5668\u5374\u5e76\u4e0d\u662f\u540c\u65f6\u5c04\u5411\u4ed6\u8ba2\u4e24\u4e2a\u4eba\u7684\u3002 \u3000\u3000\u6240\u6709\u7684\u6697\u5668\u5168\u90fd\u5411\u85cf\u4e03\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6240\u4ee5\u90ed\u5927\u8def\u6bd4\u71d5\u4e03\u66f4\u60ca\u3001\u66f4\u6025\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u867d\u7136\u6ca1\u6709\u60f3\u5230\u8be5\u600e\u9ebd\u529e\xb7\u4eba\u5374\u5df2\u5411\u71d5\u4e03\u6251\u4e86\u8fc7\u53bb\u6251\u5728\u71d5\u4ea1\u8eab\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u8d77\u6eda\u5230\u5730\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u89c9\u5f97\u8eab\u4e0a\u9635\u6d4b\u75db\uff0c\u7a81\u7136\u95f4\xb7\u5168\u8eab\u90fd\u5df2\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u8fde\u77e5\u89c9\u90fd\u5df2\u9ebb\u6728\u3002 \u3000\u3000\u4ed6\u65e2\u672a\u770b\u5230\u6e14\u7f51\u88ab\u62c9\u8d77\u4e5f\u672a\u770b\u5230\u7f51\u4e2d\u7684\u4eba\u7ffb\u8eab\u8dc3\u8d77\u3002 \u3000\u3000\u660f\u8ff7\u4e2d\uff0c\u4ed6\u53ea\u542c\u89c1\u4e86\u4e24\u58f0\u547c\u53eb\u58f0\u60ca\u547c\xb7\u58f0\u60e8\u547c\u3002 \u3000\u3000\u4f46\u4ed6\u5df2\u5206\u4e0d\u6e05\u60ca\u547c\u662f\u8c01\u53d1\u51fa\u6765\u7684\xb7\u60e8\u547c\u53c8\u662f\u8c01\u53d1\u51fa\u6765\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u81ea\u5df1\u7edd\u6ca1\u6709\u53eb\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u7259\u54ac\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u6709\u7684\u4eba\u5e73\u65f6\u4e5f\u8bb8\u4f1a\u5927\u558a\u5927\u53eb\uff0c\u4f46\u5728\u771f\u6b63\u75db\u82e6\u65f6\xb7\u5374\u8fde\u54fc\u90fd\u4e0d\u4f1a\u54fc\u58f0\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u6709\u7684\u4eba\u770b\u5230\u670b\u53cb\u7684\u5371\u9669\u65f6\u5c31\u4f1a\u5fd8\u4e86\u81ea\u5df1\u7684\u5371\u9669\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u6b63\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u52a8\u8d77\u6765\uff0c\u4ed6\u5c31\u6839\u672c\u4e0d\u987e\u81ea\u5df1\u7684\u6b7b\u6d3b\u3002 \u7b2c\u516b\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u516b\u7ae0 \u3000\u3000\u60ca\u547c\u58f0\u4eff\u4f5b\u5df2\u6e10\u6e10\u9065\u8fdc\u6e10\u6ca5\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4ec0\u9ebd\u58f0\u97f3\u5462\uff1f \u3000\u3000\u662f\u4e0d\u662f\u6709\u4eba\u5728\u5207\u6ce3\uff1f \u3000\u3000\u90ed\u5927\u8def\u5f20\u5f00\u773c\u775b\u5c31\u770b\u5230\u71d5\u4e03\u8138\u4e0a\u7684\u6cea\u73e0\u3002 \u3000\u3000\u71d5\u4e03\u770b\u5230\u4ed6\u5f20\u5f00\u9650\u77aa\xb7\u5374\u53c8\u5fcd\u4e0d\u4f4f\u5931\u58f0\u800c\u547c\uff0c\u5927\u559c\u9053\u4ed6\u9192\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u65c1\u8fb9\u7acb\u671f\u6709\u4eba\u63a5\u9053\uff1a\u201c\u597d\u4eba\u4e0d\u957f\u547d\uff0c\u7978\u5bb3\u9057\u5343\u5e74\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4ed6\u5b9a\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u8fd9\u662f\u738b\u52a8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u58f0\u97f3\u672c\u603b\u662f\u51b7\u51b7\u6de1\u8c08\u7684\u4f46\u73b0\u5728\u5374\u597d\u50cf\u6709\u70b9\u53d1\u6296\u3002 \u3000\u3000\u7136\u5f8c\u90ed\u5927\u8def\u624d\u770b\u5230\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4ed6\u90a3\u5f20\u51b7\u51b7\u6de1\u6de1\u7684\uff0c\u73b0\u5728\u5c45\u7136\u4e5f\u5145\u6ee1\u4e86\u5174\u594b\u548c\u6fc0\u52a8\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u4eec\u96be\u9053\u4ee5\u4e3a\u6211\u5df2\u7ecf\u6b7b\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u7684\u786e\u662f\u5b58\u7b11\u4f46\u7b11\u7684\u6837\u5b50\u5374\u8c61\u662f\u5728\u54ed\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7b11\u5168\u8eab\u5c31\u53d1\u75bc\u3002\u71d5\u4e03\u6084\u6084\u64e6\u5e72\u670b\u826f\u6cea\xb7\u9053\u201c\u4f60\u597d\u597d\u7684\u8eba\uff0c\u4e0d\u786e\u5b9a\u4e5f\u4e0d\u51c6\u8bf4 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fde\u4e2a\u5b87\u90fd\u4e0d\u96be\u8bf4\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e5f\u4e0d\u51c6\u70b9\u5934\uff0c\u8fde\u52a8\u90fd\u672c\u51c6\u52a8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u679c\u7136\u52a8\u90fd\u4e0d\u52a8\u4e86\xb7\u773c\u775b\u8fd8\u662f\u5f20\u5f97\u5f88\u5927\uff0c\u51dd\u89c6\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u8f7b\u8f7b\u7684\u56db\u95e8\u6c14\u201c\u4f60\u8eab\u5c38\u4e2d\u4e86\u4e00\u6839\u4e27\u95e8\u9489\u3001\u6839\u8896\u7bad\u8fd8\u52a0\u4e0a\u4e24\u6839\u6bd2\u9488\u8fd9\u6761\u547d\u7b80\u76f4\u662f\u62a2\u56de \u6765\u7684\u6240\u4ee5\u4f60\u5c31\u8be5\u7279\u522b\u7231\u60dc\u624d\u8db3\u3002\u201d \u3000\u3000\u8bf4\u8bf4\u4ed6\u773c\u5708\u53c8\u7ea2\u4e86\u3002\u4e0a\u52a8\u4e5f\u53f9\u95e8\u6c14\u9053\u201c\u4f60\u4e0d\u51c6\u4ed6\u8bf4\u8bdd\u4ed6\u4e5f\u8bb8\u66f4\u96be\u53d7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u901a\u201c\u7b54\u5bf9\u5e7f\u3002\u201d\u71d5\u4e03\u77aa\u4e86\u4ed6\u773c\xb7\u9053\u201c\u770b\u6765\u6211\u771f\u8be5\u5c06\u8fd9\u4eba\u7684\u5634\u7f1d\u8d77\u6765\u624d\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e0d\u8bf4\u8bdd\u7684\u65f6\u5019\u5201\u4f1a\u89c9\u5f97\u75db\u3002\u201d\u71d5\u4e03\u9053\u201c\u6ca1\u6709\u8fd9\u56de\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6709\u3002\u201d \u3000\u3000\u4ed6\u60f3\u7b11\u53c8\u5fcd\u4f4f\u6162\u6162\u7684\u63a5\u9053\u201c\u56e0\u4e3a\u6211\u53ea\u8981\u8bf4\u8bdd\u5c31\u4ec0\u9ebd\u75db\u82eb\u90fd\u5fd8\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u90a3\u773c\u8272\u4e5f\u4e0d\u77e5\u662f\u601c\u60dc\uff1f\u662f\u57cb\u6028\uff1f\u8fd8\u662f\u53e6\u5916\u6709\u79cd\u8bf4\u4e5f\u8bf4\u4e2d\u51fa\u731c\u4e5f\u731c\u4e0d\u900f\u7684\u60c5\u611f\uff1f \u3000\u3000\u4ed6\u7684\u8138\u5374\u662f\u82cd\u767d\u5c31\u597d\u50cf\u7a97\u7eb8\u7684\u989c\u8272\u6837\u3002 \u3000\u3000\u7a97\u7eb8\u5df3\u767d\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u867d\u7136\u8fc7\u5f97\u5f88\u75db\u82e6\u4f46\u603b\u7b97\u5df2\u8fc7\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\u201c\u90a3\u5927\u7f0e\u5438\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u5df2\u53d8\u6210\u4e86\u6b7b\u879f\u5438\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u542c\u5230\u7684\u90a3\u58f0\u60e8\u53eb\u6b63\u662f\u4ed6\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u4f46\u767e\u8db3\u4e4b\uff0c\u6b7b\u9762\u4e0d\u50f5\uff0c\u6240\u4ee5\u90ed\u5927\u8def\u53c8\u8fe2\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u771f\u7684\u6b7b \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u56de\u7b54\uff0c\u56de\u7b54\u7684\u4eba\u662f\u6797\u592a\u5e73\u3002\u6797\u592a\u5e73\u9053\u201c\u4ed6\u6b7b\u5f97\u53c8\u4e7e\u51c0\u3001\u53c8\u5f7b\u5e95\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u4f60\u6740\u4e86\u4ed6\u7684\u201d\u6797\u592a\u5e73\u6447\u6447\u5934\u9053\u201c\u662f\u71d5\u4e03\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u4f60\u662f\u4e0d\u662f\u6ca1\u6709\u60f3\u5230\u4ed6\u5728\u90a3\u79cd\u60c5\u51b5\u4e0b\u8fd8\u80fd\u66ff\u4f60\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u786e\u60f3\u4e0d\u5230\u90a3\u65f6\u4ed6\u81ea\u5df1\u660e\u660e\u662f\u538b\u5728\u71d5\u4e03\u8eab\u4e0a\u7684\u3002\u4ed6\u60f3\u95ee\u71d5\u4e03\uff0c\u4f46\u71d5\u4e03\u5374\u5df2\u53c8\u626d\u8f6c \u4e86\u5934\u3002 \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u4e5f\u6ca1\u6709\u60f3\u5230\u4f46\u6211\u5374\u770b\u89c1\u90a3\u5927\u5a31\u86c6\u521a\u8df3\u8d77\u6765\xb7\u5c31\u6709\u628a\u5200\u523a\u5165\u4ed6\u7684\u54bd\u5589\u4e5f\u770b\u5230 \u4e86\u5730\u4e0a\u7684\u8840\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u5730\u4e0a\u53ea\u6709\u8840\u4ed6\u7684\u4eba\u5462\u6c50 \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u8d70\u4e86\u5e26\u5200\u8d70\u7684\u3002\u201d\u90ed\u5927\u8def\u901a\u201c\u6b7b\u4eba\u8fd8\u80fd\u8d70\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u56e0\u4e3a\u8fd9\u6b7b\u4eba\u8fd8\u5269\u4e0b\u53e3\u6c14\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u5269\u4e0b\u66f0\u6c14\u4f8b\u5df4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9ab8\u5728\u5fc3\u91cc\u53e3\u6c14\u4e5f\u5410\u51fa\u6765\u4e86\uff0c\u5c55\u989c\u9053\u201c\u770b\u6765\u6211\u4eec\u5012\u8fd8\u6ca1\u6709\u5403\u4e8f\u201c\u6797\u592a\u5e73\u9053\u201c\u4e0d\u9519\u73b0 \u5728\u6211\u4eec\u6b63\u597d\u662f\u56db\u4e2a\u5bf9\u4ed6\u4eec\u56db\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u53ea\u53ef\u501f\u6211\u6700\u591a\u5df2\u53ea\u80fd\u7b97\u534a\u4e2a\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u4ed6\u4eec\u4e5f\u53ea\u4e0d\u8fc7\u5269\u4e0b\u53c1\u4e2a\u6652\u5df2\u3002\u201d\u6797\u592a\u5e73\u9053\u201c\u7ea2\u5a18\u5b50\u3001\u8d64\u7ec3\u86c7\u3001\u50ac\u547d\u7b26\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u83ab\u5fd8\u4e86\u8fd8\u6709\u4e2a\u98de\u51b2\u5929\u9e70\u4e2d\u4e0a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5fd8\u4e0d\u4e86\u7684\u3002\u201d\u4ed6\u795e\u8272\u5ffd\u7136\u53d8\u5f97\u5f88\u5947\u602a\xb7\u76ee\u5149\u4f3c\u4e4e\u5728\u770b\u5f88\u9065\u8fdc\u7684\u5730\u65b9\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7ea2\u5a18\u5b50\u3001\u8d64\u7ec3\u86c7\u3001\u50ac\u547d\u7b26\u518d\u52a0\u4e0a\u9e70\u4e2d\u738b\u5c82\u975e\u6b63\u662f\u56db\u4e2a\uff1f\u201d \u3000\u3000\u738b\u52a8\u75bd\u201c\u53c1\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53c1\u4e2a\u52a0\u4e2a\u4e3a\u4ec0\u9ebd\u8fd8\u662f\u53c1\u4e2a\uff1f\u201d \u3000\u3000\u738b\u52a8\u773c\u775b\u91cc\u7a7a\u7a7a\u6d1e\u6d1e\u7684\u4e5f\u4e0d\u77e5\u5728\u770b\u4ec0\u9ebd\u8138\u4e0a\u604d\u604d\u998f\u998f\u7684\u4e5f\u4e0d\u77e5\u5728\u60f3\u4ec0\u9ebd\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\xb7\u4ed6\u624d\u5b57\u5b57\u7684\u7f13\u7f13\u9053\u201c\u56e0\u4e3a\u6211\u5c31\u662f\u4e00\u98de\u51b2\u5929\u9e70\u4e2d \u3000\u3000\u6ca1\u6709\u4eba\u95ee\u738b\u52a8\u7684\u8fc7\u53bb\u56e0\u4e3a\u4ed6\u4eec\u90fd\u5f88\u80fd\u5c0a\u91cd\u522b\u4eba\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u738b\u52a8\u4e0d\u8bf4\xb7\u4ed6\u4eec\u7edd\u6728\u95ee\u3002\u738b\u52a8\u7684\u79d8\u5bc6\u662f\u738b\u52a8\u81ea\u5df1\u8bf4\u51fa\u6765\u7684\u3002 \u7b2c\u4e5d\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e5d\u7ae0 \u3000\u3000\u738b\u52a8\u5e76\u4e0d\u662f\u5929\u751f\u5c31\u4e0d\u559c\u6b22\u52a8\u7684\u3002 \u3000\u3000\u4ed6\u5c0f\u65f6\u5019\u975e\u4f46\u559c\u6b22\u52a8\u800c\u4e14\u8fd8\u559c\u6b22\u7684\u8981\u547d\xb7\u52a8\u5f97\u5389\u5bb3\u3002 \u3000\u3000\u516d\u5c81\u7684\u65f6\u5019\u4ed6\u5c31\u4f1a\u722c\u6811\u3002 \u3000\u3000\u4ed6\u722c\u8fc7\u5404\u5f0f\u5404\u6837\u7684\u6811\u6240\u4ee5\u4e5f\u4ece\u5404\u5f0f\u5404\u6837\u7684\u6811\u4e0a\u6454\u4e0b\u6765\u8fc7\u3002 \u3000\u3000\u7528\u5404\u5f0f\u5404\u6837\u4e0d\u95ee\u7684\u59ff\u52bf\u69a8\u4e0b\u6765\u8fc7\u3002 \u3000\u3000\u6700\u60e8\u7684\u4e00\u6b21\xb7\u662f\u8111\u6a21\u5148\u5730\u90a3\u6b21\u4ed6\u4e2a\u8111\u888b\u51e0\u4e4e\u6454\u6210\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u5f00\u59cb\u53ef\u4ee5\u8c61\u7334\u4e88\u4f3c\u7684\u7528\u811a\u5c16\u540a\u5728\u6811\u4e0a\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u4e0d\u518d\u722c\u6811\u3002 \u3000\u3000\u56e0\u4e3a\u722c\u6811\u5df2\u53d8\u6210\u597d\u50cf\u7761\u5728\u88ab\u7a9d\u91cc\u6837\u5b89\u5168\uff0c\u5df2\u8fde\u4e00\u70b9\u523a\u6fc0\u90fd\u6ca1 \u3000\u3000\u4ece\u90a3\u65f6\u5019\u5f00\u59cb\u4ed6\u7236\u901a\u4fae\u5929\u90fd\u8981\u51fa\u52a8\u5168\u5bb6\u7684\u4f63\u4eba\u53bb\u627e\u4ed6\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u4eec\u5bb6\u9053\u867d\u5df2\u5c0f\u843d\xb7\u4f46\u4f63\u4eba\u8fd8\u662f\u6709\u597d\u51e0\u4e2a\u3002\u6bcf\u6b21\u4ed6\u4eec\u628a\u4ed6\u627e\u56de\u6765\u7684\u65f6\u5019\u90fd\u5df2\u7cbe\u75b2\u529b \u7aed\u597d\u50cf\u7528\u624b\u6307\u5934\u4e00\u70b9\u5c31\u4f1a\u5012\u4e0b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u9c9c\u8e66\u6d3b\u8df3\u7684\u6bd4\u521a\u51fa\u6c34\u7684\u5367\u5b50\u8fd8\u751f\u731b\u5f97\u591a\u3002 \u3000\u3000\u5230\u5f8c\u6765\u8c01\u4e5f\u4e0d\u613f\u610f\u5168\u627e\u4ed6\u4e86\u3002 \u3000\u3000\u5b81\u53ef\u780d\u516b\u767e\u65a4\u67f4\u4e5f\u4e0d\u613f\u5b8f\u627e\u4ed6\u3002 \u3000\u3000\u5b81\u53ef\u5377\u94fa\u76d6\u4e5f\u4e0d\u613f\u53bb\u627e\u4ed6\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u7684\u7236\u6bcd\u4e5f\u53ea\u6709\u653e\u5f03\u8fd9\u5ff5\u5934\uff0c\u968f\u4fbf\u4ed6\u9ad8\u5174\u5728\u5916\u9762\u73a9\u591a\u4e45\uff0c\u5c31\u73a9\u591a\u4e45\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u6bcf\u9694\u4e8c\u4e24\u5929\u603b\u8fd8\u56de\u6765\u2019\u6b21\u3002 \u3000\u3000\u56de\u6765\u6d17\u6fa1\u3001\u5403\u996d\u3001\u6362\u8863\u670d\u3002 \u3000\u3000\u56de\u6765\u8981\u96f6\u7528\u94b1\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u65f6\u4ed6\u8fd8\u53ea\u6709\u5341\u4e8c\u5dde\u5c81\u8fd8\u89c9\u5f97\u5411\u7236\u6bcd\u8981\u94b1\u662f\u4ef6\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u3002 \u3000\u3000\u7b49\u4ed6\u518d\u957f\u5927\u70b9\uff0c\u89c9\u5f97\u81ea\u5df1\u5df2\u5e94\u8be5\u72ec\u7acb\u7684\u65f6\u5019\uff0c\u4ed6\u7236\u6bcd\u5c31\u96be\u518d\u89c1\u5230\u4ed6\u7684\u5165\u5382\xb7\u8005\u5148\u751f\u548c\u8001 \u592a\u5927\u4e5f\u4e0d\u77e5\u5728\u6697\u4e2d\u53d1\u8fc7\u591a\u5c11\u8a93\u201c\u4e0b\u6b21\u7b49\u4ed6\u56de\u6765\xb7\u5c31\u7528\u6761\u94c1\u94fe\u5b50\u628a\u4ed6\u9501\u4f4d\xb7\u7528\u68cd\u5b50\u6253\u65ad\u4ed6\u7684\u4e24 \u6761\u817f\uff0c\u770b\u4ed6\u8fd8\u80fd\u4e0d\u80fd\u5230\u5916\u9762\u53bb\u91ce\u53bb\u3002\u201d \u3000\u3000\u4f46\u7b49\u4ed6\u4e0b\u6b21\u56de\u6765\u7684\u65f6\u5019\u770b\u5230\u4ed6\u53c8\u810f\u53c8\u997f\xb7\u9762\u9ec4\u808c\u7626\u7684\u6837\u5b50\u8001\u5148\u751f\u7684\u5fc3\u53c8\u8f6f\u4e86\xb7\u5458\u591a\u4e5f\u53ea \u4e0d\u8fc7\u628a\u4ed6\u53eb\u5230\u4e66\u623f\u91cc\u53bb\u8bad\u987f\u3002 \u3000\u3000\u8001\u592a\u592a\u66f4\u65e9\u5df2\u8d76\u4e0b\u53a8\u623f\u53bb\u58a9\u9e21\u6c64\u8001\u5148\u751f\u7684\u8bad\u8bdd\u8fd8\u6ca1\u6709\u7ed3\u675f\u9e21\u817f\u5df2\u7ecf\u585e\u5728\u513f\u5b50\u5634\u91cc\u4e86\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u8bb8\u53ea\u6709\u72ec\u751f\u4e88\u7684\u7236\u6bcd\u4eec\xb7\u624d\u80fd\u4e86\u89e3\u4ed6\u4ecd\u8fd9\u79cd\u5fc3\u60c5\u3002 \u3000\u3000\u505a\u513f\u4ea5\u7684\u4eba\u662f\u6c38\u8fdc\u4e0d\u4f1a\u987f\u7684\u3002 \u3000\u3000\u738b\u52a8\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4ed6\u53ea\u61c2\u5f97\xb7\u7537\u5b50\u6c49\u957f\u5927\u4e86\u4e4b\u5f8c\u5c31\u5e94\u8be5\u5230\u5916\u9762\u53bb\u95ef\u5929\u4e0b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u5f00\u59cb\u5230\u5916\u56de\u53bb\u95ef\u5929\u4e0b\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u624d\u5341\u4e03\u5c81\u3002 \u3000\u3000\u5c31\u548c\u5929\u4e0b\u5927\u591a\u6570\u5341\u4e03\u516b\u5c81\u7684\u5c11\u5e74\u6837\u5b50\u52a8\u521a\u79bb\u5f00\u5bb6\u7684\u65f6\u5019\u2606\u4e59\u91cc\u53ea\u6709\u5145\u6ee1\u4e86\u5174\u594b\u5145\u6ee1\u5927 \u5fd7\u3002 \u3000\u3000\u4f46\u7b49\u5230\u6328\u8fc7\u4e24\u5929\u997f\u4e4b\u5f8c\uff0c\u5c31\u6e10\u6e10\u4f1a\u5f00\u59cb\u60f3\u5bb6\u4e86\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u5fc3\u91cc\u5f88\u7a7a\u865a\uff0c\u5f88\u5bc2\u5bde\u3002 \u3000\u3000\u4ed6\u5c31\u4f1a\u62fc\u547d\u60f3\u5de6\u7ed3\u4ea4\u65b0\u7684\u670b\u53cb\u5f53\u7136\u6700\u597d\u662f\u4e2a\u7ea2\u7c89\u77e5\u5df2\u3002 \u3000\u3000\u6709\u54ea\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c0f\u4f19\u5b50\u5fc3\u91cc\u4e0d\u5728\u6e34\u671b\u7231\u60c5\u5e7b\u60f3\u7231\u60c5 \u3000\u3000\u8b66\u4ed6\u5bc2\u5bde\u5f97\u8981\u547d\u7684\u65f6\u5019\u90a3\u6551\u82e6\u6551\u96be\u7684\u7ea2\u5a18\u5b50\u5c31\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u5979\u4e86\u89e3\u4ed6\u7684\u96c4\u5fc3\u4e5f\u4e86\u89e3\u4ed6\u7684\u82e6\u95f7\u3002 \u3000\u3000\u5979\u5b89\u6170\u4ed6\u9f13\u52b1\u4ed6\u9f13\u52b1\u4ed6\u53bb\u505a\u5404\u79cd\u4e8b\u3002 \u3000\u3000\u201c\u7537\u5b50\u6c49\u82e5\u5728\u4e16\u4e0a\u4ec0\u9ebd\u4e8b\u90fd\u5e94\u8be5\u53bb\u5c1d\u8bd5\u5c1d\u8bd5\u3002\u201d \u3000\u3000\u5728\u4ed6\u8bf4\u6765\u5979\u8bf4\u7684\u8bdd\u5c31\u662f\u5723\u65e8\u3002 \u3000\u3000\u201c\u4e2a\u4eba\u6d3b\uff0c\u5c31\u8981\u6709\u94b1\u6709\u540d\xb7\u56e0\u4e3a\u4eba\u6d3b\u672c\u4e3a\u4e86\u4eab\u53d7\u3002\u201d \u3000\u3000\u90a3\u65f6\u4ed6\u8fd8\u4e0d\u77e5\u9053\u4eba\u751f\u4e2d\u9664\u4e86\u4eab\u53d7\u4e4b\u5916\xb7\u8fd8\u6709\u8bb8\u591a\u66f4\u6709\u610f\u4e49\u7684 \u3000\u3000\u6240\u4ee5\u4e3a\u5382\u6210\u540d\u4ed6\u4e0d\u501f\u505a\u5404\u79cd\u4e8b\u3002 \u3000\u3000\u4ed6\u6210\u540d\u5382\u3002 \u3000\u3000\u4ed6\u4e8c\u5341\u8fd8\u4e0d\u5230\u5c31\u5df2\u53d8\u6210\u4e86\u8d6b\u8d6b\u6709\u540d\u7684\u201c\u98de\u51b2\u5929\u9e70\u4e2d\u738b\u3002\u201d \u3000\u3000\u6210\u540d\u7684\u786e\u662f\u4ef6\u5f88\u607c\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u7cca\u91cc\u6eaf\u6d82\u7684\u505a\u4e86\u5f88\u591a\u4e8b\u6eaf\u91cc\u6eaf\u6d82\u7684\u6210\u5382\u540d\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7684\u662f\u6700\u534e\u8d35\u7684\u8863\u88f3\uff0c\u559d\u7684\u662f\u53c1\u4e24\u94f6\u5b50\u65a4\u7684\u9152\u3002 \u3000\u3000\u4ed6\u5df2\u906d\u5f97\u6311\u5254\u88c1\u7f1d\u7684\u624b\u5de5\u3002 \u3000\u3000\u9c7c\u7fc5\u82e5\u662f\u5ae9\u5f97\u8fd8\u5dee\u5206\u706b\u5019\u4ed6\u7acb\u523b\u5c31\u4f1a\u5f8b\u5230\u53a8\u5b50\u8138\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u5df2\u61c2\u5f97\u4eab\u53d7\u800c\u4e14\u4eab\u53d7\u5f97\u771f\u4e0d\u9519\u3002 \u3000\u3000\u4ed6\u672c\u5df2\u5e94\u8be5\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u4ed6\u5ffd\u7136\u53c8\u6709\u4e86\u75db\u82e6\uff0c\u6709\u4e86\u70e6\u607c\u800c\u4e14\u6bd4\u4ee5\u524d\u8fd8\u70e6\u607c\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u672c\u6765\u6cbe\u4e0a\u6795\u5934\u5c31\u7761\u5f97\u5f88\u751c\uff0c\u4f46\u73b0\u5728\u5374\u65f6\u5e38\u7761\u4e0d\u4e86\u3002 \u3000\u3000\u7761\u4e0d\u7684\u65f6\u5019\xb7\u4ed6\u5c31\u4f1a\u95ee\u81ea\u5df1\u201c\u6211\u505a\u7684\u8fd9\u4e9b\u4e8b\u662f\u4e0d\u662f\u5e94\u8be5\u505a\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4ea4\u7684\u8fd9\u4e9b\u670b\u53cb\u662f\u4e0d\u662f\u771f\u7684\u597d\u670b\u53cb\uff1f\u201d \u3000\u3000\u201c\u4e2a\u4eba\u9664\u4e86\u81ea\u5df1\u4eab\u53d7\u4e4b\u5916\u662f\u4e0d\u662f\u8fd8\u5e94\u8be5\u60f3\u60f3\u522b\u7684\u4e8b\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u5f00\u59cb\u94f6\u5bb6\u60f3\u4ed6\u7684\u7236\u6bcd\u3002 \u3000\u3000\u4e16\u4e0a\u624b\u827a\u6700\u597d\u7684\u53a8\u4e88\uff0c\u4e5f\u58a9\u4e0d\u51fa\u6bcd\u4eb2\u4eb2\u624b\u58a9\u7684\u90a3\u79cd\u9e21\u6c64\u3002 \u3000\u3000\u90a3\u79cd\u606d\u7ef4\u5949\u627f\u7684\u8bdd\u4e5f\u6e10\u6e10\u53d8\u5f97\u6ca1\u6709\u7236\u4eb2\u7684\u8bad\u8bdd\u597d\u542c\u4e86\u3002 \u3000\u3000\u5c31\u8fde\u7ea2\u5a18\u5b50\u7684\u751c\u8a00\u871c\u8bdd\u542c\u8d77\u6765\u4e5f\u6ca1\u6709\u4ee5\u524d\u90a3\u9ebd\u4ee4\u4ed6\u52a8\u5fc3\u3002 \u3000\u3000\u8fd9\u4e9b\u8fd8\u90fd\u4e0d\u7b97\u5f88\u91cd\u8981\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u662f\u4ed6\u5ffd\u7136\u60f3\u505a\u4e2a\u6b63\u6b63\u5f53\u5f53\u7684\u4eba\u3002 \u3000\u3000\u4e2a\u665a\u4e0a\u80fd\u591f\u5b89\u5b89\u5fc3\u5fc3\u7761\u89c9\u7684\u4eba\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5f00\u59cb\u8ba1\u5212\u8131\u79bb\u8fd9\u79cd\u751f\u6d3b\xb7\u8131\u79bb\u8fd9\u79cd\u670b\u53cb\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u653e\u4ed6\u8d70\u7684\u3002\u7b2c\xb7\u56e0\u4e3a\u4ed6\u4eec\u8fd8\u9700\u8981\u4ed6\u3002 \u3000\u3000\u7b2c\u4e8c\u56e0\u4e3a\u4ed6\u77e5\u9053\u7684\u79d8\u5bc6\u592a\u591a\u3002 \u3000\u3000\u552f\u5e78\u8fd0\u7684\u662f\u5728\u4ed6\u6253\u9762\u524d\uff0c\u4ed6\u59cb\u7ec8\u6ca1\u6709\u63d0\u8d77\u8fc7\u4ed6\u7684\u5bb6\u4ed6\u7684\u7236\u6bcd\u3002 \u3000\u3000\u8fd9\u4e5f\u4e0d\u77e5\u9053\u662f\u4ed6\u4f2f\u7236\u6bcd\u4e22\u4e86\u4ed6\u7684\u4eba\u8fd8\u662f\u4f2f\u4ed6\u81ea\u5df1\u4e22\u4e86\u7236\u6bcd\u7684 \u3000\u3000\u4ed6\u7684\u7236\u6bcd\u5e76\u4e0d\u662f\u4ec0\u9ebd\u4e86\u4e0d\u8d77\u7684\u5927\u4eba\u7269\u3002 \u3000\u3000\u4ed6\u7684\u670b\u53cb\u4eec\uff0c\u4e5f\u6ca1\u6709\u95ee\u8fc7\u4ed6\u7684\u5bb6\u5ead\u80cc\u666f\u53ea\u95ee\u8fc7\u4ed6\u201c\u4f60\u6b66\u529f\u662f\u600e\u9ebd\u7ec3\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u4ed6\u7684\u6b66\u529f\u662f\u4ed6\u5c0f\u65f6\u5019\u5728\u5916\u9762\u91ce\u7684\u65f6\u5019\u5b66\u6765\u7684\u4e2a\u5f88\u795e\u79d8\u7684\u8001\u4eba\u6bcf\u5929\u90fd\u5728\u6697\u6797\u4e2d\u7b49\u4ed6\u3001\u903c\u4ed6 \u82e6\u7ec3\u3002 \u3000\u3000\u4ed6\u59cb\u7ec8\u4e0d\u77e5\u9053\u8fd9\u8001\u4eba\u662f\u8c01\xb7\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4f20\u6388\u7684\u6b66\u529f\u7a76\u7adf\u6709\u591a\u9ad8\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u7b2c\u6b21\u6253\u67b6\u7684\u65f6\u5019\u624d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u5947\u9047\u4e49\u5947\u602a\u53c8\u795e\u79d8\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4ece\u672a\u5728\u522b\u4eba\u9762\u524d\u63d0\u8d77\u56e0\u4e3a\u8bf4\u51fa\u4e86\u4e5f\u6ca1\u6709\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u6709\u65f6\u9010\u4ed6\u81ea\u5df1\u90fd\u4e0d\u592a\u76f8\u4fe1\u3002 \u3000\u3000\u5fc3\u5973\u3002\u86c7\u874e\u7684\u7ea2\u5a18\u5b50 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u8fc7\u53bb\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u5728\u81ea\u5df1\u7684\u597d\u670b\u53cb\u9762\u524d\u8c08\u5230\u81ea\u5df1\u7684\u8fc7\u53bb\u3002 \u3000\u3000\u6709\u65f6\u90a3\u5c31\u597d\u50cf\u662f\u5728\u8bb2\u6545\u4e8b\u4f3c\u7684\u3002\u8fd9\u79cd\u6545\u4e8b\u5927\u591a\u90fd\u4e0d\u4f1a\u5f88\u5438\u5f15\u5165 \u3000\u3000\u542c\u522b\u4eba\u5439\u4e2d\u603b\u4e0d\u5982\u81ea\u5df1\u5439\u6709\u52b2\uff0c\u4f46\u65e0\u8bba\u4ec0\u9ebd\u4e8b\u90fd\u6709\u4f8b\u5916\u7684\u3002 \u3000\u3000\u738b\u52a8\u5728\u8bf4\u7684\u65f6\u5019\u6bcf\u4e2a\u4eba\u90fd\u77aa\u5927\u4e86\u773c\u77a9\u542c\uff0c\u8fde\u6253\u5c94\u7684\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u7b2c\u4e2a\u5f00\u53e3\u6253\u5c94\u7684\uff0c\u81ea\u7136\u8fd8\u662f\u90ed\u5927\u8def\u3002\u4e8b\u5b9e\u4e0a\u4ed6\u5df2\u61a8\u4e86\u5f88\u4e45\u542c\u5230\u8fd9\u91cc\u624d\u5b9e\u5728\u618b\u4e0d\u4f4f\u4e86\u5148\u957f \u6c0f\u5410\u51fa\u53e3\u6c14\u624d\u95ee\u9053\u201c\u90a3\u4f4d\u8001\u4eba\u5bb6\u90fd\u5728\u7b49\u4f60\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5c31\u5728\u575f\u573a\u5f8c\u9762\u90a3\u6811\u6797\u5b50\u91cc\u7b49\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6bcf\u5929\u90fd\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u65e0\u8bba\u522e\u98ce\u4e0b\u96e8\u6211\u6ca1\u6709\u5929\u4e0d\u53bb\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u5171\u53bb\u4e86\u591a\u5c11\u6b21\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53bb\u4e01\u4e8c\u5e74\u56db\u4e2a\u6708\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u5410\u51fa\u957f\u6c14\u9053\u201d\u90a3\u5c82\u975e\u6709\u5e72\u591a\u6b21\u201d \u3000\u3000\u738b\u52a8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u542c\u4f60\u8bf4\u4f60\u53ea\u8981\u5b66\u5f97\u6162\u70b9\u5c31\u8981\u6328\u63cd\uff0c\u63cd\u5f97\u8fd8\u4e0d\u8f7b\u3002\u201d\u738b\u52a8\u9053\u201c\u5f00\u59cb\u90a3\u4e2d\u6211\u51e0\u4e4e \u5f88\u5c11\u6709\u4e0d\u6328\u63cd\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65e2\u7136\u5929\u5929\u6328\u63cd\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u90a3\u65f6\u6211\u89c9\u5f97\u8fd9\u79cd\u4e8b\u4e0d\u4f46\u5f88\u795e\u79d8\u800c\u4e14\u53c8\u65b0\u9c9c\u3001\u53c8\u523a\u6fc0\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u7b11\u9053\u201c\u82e5\u6362\u4e86\u6211\u4e5f\u4f1a\u53bb\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u4e5f\u5fcd\u4e0d\u4f9d\u95ee\u9053\u2019\u4f60\u4ece\u6765\u6ca1\u6709\u95ee\u8fc7\u90a3\u4f4d\u8001\u4eba\u5bb6\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u95ee\u4e86\u51e0\u767e\u6b21\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u4ece\u4ec0\u9ebd\u5730\u65b9\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u6447\u6447\u5934\u9053\u201c\u6bcf\u6b21\u6211\u5230\u90a3\u91cc\u7684\u65f6\u5019\u4ed6\u90fd\u5df2\u5148\u5230\u4e86\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u65e9\u70b9\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e0\u8bba\u6211\u53bb\u5f97\u591a\u65e9\xb7\u4ed6\u90fd\u5df2\u5148\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u626c\u7709\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u613f\u8e2a\u4ed6\uff0c\u770b\u4ed6\u56de\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u7389\u52a8\u82e6\u7b11\u9053\u201c\u6211\u5f53\u7136\u8bd5\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u201c\u7ed3\u679c\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7ed3\u679c\u6bcf\u6b21\u90a3\u662f\u63a5\u987f\u81ed\u63cd\u4e56\u4e56\u7684\u4e2a\u4eba\u56de\u5bb6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u76b1\u8d77\u7709\u5934\u5428\u54fa\u5730\u9053\u201c\u4ed6\u6bcf\u5929\u90fd\u5728\u90a3\u91cc\u7b49\u4f60\u901a\u4f60\u53bb\u8bd5\u6b66\uff0c\u5374\u53c8\u4e0d\u80af\u8ba9\u4f60\u77e5\u9053\u4ed6\u662f \u8c01\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd8\u6709\u66f4\u5947\u602a\u7684\u4ed6\u4e5f\u4ece\u6765\u6ca1\u6709\u95ee\u8fc7\u6211\u662f\u6dee\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u8fd9\u6837\u7684\u602a\u4e8b\u5012\u771f\u662f\u5929\u4e0b\u5c11\u6709\u770b\u6765\u4e5f\u53ea\u6709\u4f60\u8fd9\u6837\u7684\u602a\u4eba\u624d\u4f1a\u9047\u89c1\u8fd9 \u79cd\u602a\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u4e5f\u95ee\u9053\u201c\u90a3\u65f6\u4f60\u6e2f\u5907\u8131\u79bb\u4ed6\u4eec\u7684\u65f6\u5019\u8fde\u7ea2\u5a18\u5b50\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u6211\u4ece\u6ca1\u6709\u5728\u4efb\u4f55\u4eba\u9762\u524d\u63d0\u8d77\u8fc7\u3002\u201d \u3000\u3000\u85cf\u4e03\u9053\u201c\u53ef\u662f\u90a3\u7ea2\u5a18\u5b50\u2019\xb7\u2026\u5979\u5bf9\u4f60\u5c82\u975e\u86ee\u4e0d\u9519\u7684\u5417\uff1f\u201d \u3000\u3000\u738b\u52a8\u7684\u8138\u8272\u66f4\u96be\u770b\u8fc7\u4e86\u5f88\u4e45\u624d\u51b7\u51b7\u9053\u201c\u5979\u5bf9\u5f88\u591a\u4eba\u90fd\u4e0d\u9519\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u53d1\u73b0\u65e5\u5df1\u95ee\u9519\u8bdd\u4e86\u4e94\u523b\u6539\u53d8\u8bdd\u9898\u9053\u201c\u5f8c\u6765\u4f60\u600e\u9ebd\u8d70\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u8c08\u9053\u201c\u6709\u6b21\u4ed6\u4ecd\u51c6\u5907\u4f01\u5077\u5c11\u6797\u5bfa\u7684\u85cf\u7ecf\u53eb\u6211\u5148\u53bb\u6253\u63a2\u52a8\u9759\u6211\u5c31\u4e58\u673a\u6e9c\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u5410\u51fa\u53e3\u957f\u6c14\uff0c\u9053\u201c\u8fd9\u4e9b\u4eba\u5c45\u7136\u6562\u53bb\u6253\u5c11\u6797\u5bfa\u7684\u4e3b\u610f\u80c6\u5b50\u5012\u590f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6e9c\u5382\u4e4b\u5f8c\uff0c\u4ed6\u4eec\u76f4\u6ca1\u6709\u627e\u5230\u4f60\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\xb7\u8d70\u5230\u7a97\u53e3\u3002\u591c\u5f88\u9ed1\u5f88\u51b7\u3002 \u3000\u3000\u4ed6\u6728\u7acb\u5728\u7a97\u53e3\uff0c\u75f4\u75f4\u66f2\u51fa\u4e86\u534a\u5929\u795e\xb7\u624d\u9992\u9992\u7684\u63a5\u9053\uff1a\u201c\u6211\u56db\u6765\u4e4b\u5f8c\uff0c\u5c31\u5f88\u5c11\u51fa\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u4e0d\u662f\u5ffd\u7136\u53d8\u5f97\u4e0d\u60f3\u52a8\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u6211\u7684\u786e\u53d8\u4e86\u53d8\u5f97\u5f88\u5feb\u53d8\u5f97\u5f88\u591a\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5636\u54d1\u800c\u60b2\u4f24\u63a5\u9053\u201c\u56e0\u4e3a\u6211\u56de\u6765\u4e4b\u5f8c\u624d\u77e5\u9053\u6211\u51fa\u53bb\u5f8c\u7b2c\u4e8c\u5e74\uff0c\u6211\u6bcd\u4eb2\u5c31\u4e00\u2026\u201c \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\xb7\u4ed6\u7d27\u63e1\u53cc\u62f3\u5168\u8eab\u53d1\u6296\u5df2\u8bf4\u4e0d\u4e0b\u53bb\u3002\u8fd9\u6b21\u8fde \u3000\u3000\u7b49\u5230\u4ed6\u56de\u6765\xb7\u60f3\u62a5\u7b54\u7236\u6bcd\u7684\u6069\u60c5\xb7\u60f3\u5c3d\u5c3d\u4eba\u5b50\u7684\u5b5d\u5fc3\u7684\u611f\u60c5\u5462\uff1f\u6797\u592a\u5e73\u5782\u4e0b\u5934\xb7\u76ee\u4e2d\u4f3c \u5df2\u6709\u6cea\u6ee1\u7736\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u4e5f\u89c9\u5f97\u9178\u9178\u7684\u773c\u76ee\u4e5f\u6709\u70b9\u53d1\u7ea2\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u77e5\u9053\uff0c\u4e3a\u4ec0\u9ebd\u738b\u52a8\u4f1a\u53d8\u5f97\u8fd9\u9ebd\u7a77\uff0c\u8fd9\u9ebd\u998f\u8fd9\u9ebd\u602a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u5145\u6ee1\u4e86\u60b2\u75db\u548c\u6094\u6068\uff0c\u4ed6\u5728\u60e9\u7f5a\u81ea\u5df1\u3002 \u3000\u3000\u5047\u5982\u4f60\u5b9a\u8981\u8bf4\u4ed6\u662f\u5728\u9003\u907f\u90a3\u9ebd\xb7\u4ed6\u9003\u907f\u7684\u7edd\u4e0d\u662f\u7ea2\u59d0\u4e8e\xb7\u4e5f\u4e0d\u662f\u8d64\u7ec3\u86c7\xb7\u66f4\u4e0d\u662f\u5176\u4ed6\u4efb \u4f55\u4eba\u3002 \u3000\u3000\u4ed6\u9003\u907f\u7684\u662f\u4ed6\u81ea\u5df1\u3002\u60f3\u5230\u7b2c\u4e00\u6b21\u770b\u89c1\u4ed6\u4e2a\u4eba\u8eba\u5728\u5e8a\u4e0a\u8eba\u5728\u9ed1\u6697\u4e2d\uff0c\u4efb\u51ed\u8001\u9f20\u5728\u81ea\u5df2\u8eab\u535c \u722c\u6765\u722c\u53bb\u7684\u60c5\u51b5\u7ff1\u5927\u8def\u53c8\u4e0d\u7981\u957f\u957f\u7684\u53f9\u5382\u53e3\u6c14\u3002 \u3000\u3000\u4e2a\u4eba\u82e5\u975e\u5df2\u5b8c\u5168\u4e27\u5931\u6597\u5fd7\u5c31\u7b97\u80fd\u5fcd\u53d7\u9965\u997f\u4e5f\u7edd\u4e0d\u80fd\u611f\u53d7\u8001\u9f20\u7684\u3002\u90a3\u5929\u665a\u4e0a\u82e5\u4e0d\u662f\u90ed\u5927\u8def \u6eaf\u91cc\u7cca\u6d82\u7684\u95ef\u8fdb\u6765\uff0c\u7cca\u91cc\u7cca\u6d82\u7684\u8ddf\u4ed6\u505a\u5382\u670b\u53cb\u4ed6\u662f\u4e0d\u662f\u8fd8\u4f1a\u6d3b\u5230\u4eca\u5929\u5462\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u90ed\u5927\u8def\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u738b\u52a8\u7ec8\u4e8e\u56de\u5934\u7f13\u7f13\u9053\u201c\u6211\u56de\u6765\u5df2\u7ecf\u5feb\u4e8c\u5e74\u4e86\uff0c\u8fd9\u53c1\u5e74\u6765\u4ed6\u8ba2\u5b9a\u4e0d\u505c\u7684\u5728\u627e\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4ed6\u76ef\u5f53\u7136\u5f88\u96be\u627e\u5f97\u5230\u4f60\u53c8\u6709\u8c01\u80fd\u60f3\u5f97\u5230\u4e00\u89c8\u51b2\u5929\u9e70\u4e2d\u738b\u4f1a\u5446\u5728 \u8fd9\u79cd\u5730\u65b9\u8fc7\u8fd9\u79cd\u65e5\u5b50\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f46\u6211\u5374\u65e9\u5c31\u77e5\u9053\u4ed6\u4eec\u8fdf\u65e9\u603b\u6709\u5929\u4f1a\u627e\u5230\u6211\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\u9053\u201c\u5df2\u7ecf\u8fc7\u4e86\u8fd9\u9ebd\u4e45\u4ed6\u4eec\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u80af\u653e\u624b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u81ea\u5df1\u7b97\u8fc7\u6ca1\u6709\uff1f\u662f\u4f60\u6b20\u4ed6\u4eec\u7684\uff1f\u8fd8\u662f\u4ed6\u4eec\u6b20\u4f60\uff1f\u201d \u3000\u3000\u738b\u52a8\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\xb7\u624d\u7f13\u7f13\u9053\u201c\u6709\u4e9b\u5e10\u672c\u5c31\u662f\u8c01\u4e5f\u7b97\u4e0d\u6e05\u52a9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u6709\u4ed6\u81ea\u5df1\u7684\u7b97\u6cd5\u6bcf\u4e2a\u4eba\u7684\u7b97\u6cd5\u90fd\u4e0d\u540c\u3002\u201d \u3000\u3000\u4ed6\u795e\u60c5\u66f4\u6c89\u91cd\xb7\u50f5\u6162\u7684\u63a5\u9053\u201c\u5728\u4ed6\u4eec\u8bf4\u6765\u8fd9\u7b14\u5e10\u53ea\u6709\u79cd\u7b97\u6cd5\u3002\u201d\u71d5\u4e03\u9053\u201c\u54ea\u79cd\uff1f\u201d\u738b\u52a8 \u9053\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u662f\u7530\u79cd\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e0d\u8bf4\u8bdd\u4e86\u4ed6\u7684\u786e\u77e5\u9053\xb7\u6709\u7684\u5e10\u4f60\u53ea\u6709\u7528\u8840\u53bb\u7b97\uff0c\u624d\u80fd\u7b97\u5f97\u6e05\u3002 \u3000\u3000\u70b9\u70b9\u8840\u8fd8\u4e0d\u591f\xb7\u8981\u5f88\u591a\u8840\xb7\u4f60\u4e2a\u4eba\u7684\u8840\u8fd8\u4e0d\u591f\xb7\u8981\u5f88\u591a\u4eba\u7684\u8840\u3002 \u3000\u3000\u9893\u4e03\u770b\u90ed\u5927\u8def\u8eab\u4e0a\u7684\u4f24\u53e3\u8fc7\u4e86\u5f88\u4e45\u624d\u53f9\u606f\u9053\u201c\u770b\u6765\u8fd9\u7b14\u5e10\u5df2\u8d8a\u6765\u8d8a\u96be\u7b97\u4e86\u4e0d\u77e5\u9053\u8981\u5230\u4ec0 \u9ebd\u65f6\u5019\u624d\u80fd\u7b97\u6e05\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u9053\u201c\u4f60\u653e\u5fc3\u90a3\u4e00\u5b9a\u7528\u4e0d\u7b49\u5f88\u4e45\u7684\u56e0\u4e3a\u2026\u3002\u201c \u3000\u3000\u4ed6\u5ffd\u7136\u95ed\u535c\u5634\u3002\u6bcf\u4e2a\u4eba\u90fd\u95ed\u4e0a\u4e86\u5634\u751a\u81f3\u8fde\u547c\u5438\u90fd\u505c\u987f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u542c\u5230\u4e86\u9635\u811a\u6b65\u58f0\u3002 \u3000\u3000\u811a\u6b65\u58f0\u5f88\u8f7b\uff0c\u6b63\u6162\u6162\u7684\u7a7f\u8fc7\u79ef\u96ea\u7684\u9662\u5b50\u3002 \u3000\u3000\u201c\u6765\u7684\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u73b0\u5728\u5c31\u5df2\u5230\u5382\u7b97\u8fd9\u7b14\u5e10\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u60f3\u6323\u624e\u722c\u8d77\u6765\u51b2\u51fa\u95e8\u53bb\u53c8\u5fcd\u4f50\u90ed\u5927\u8def\u5411\u7a97\u53e3\u6307\u4e86\u6307\u71d5\u4e03\u64ad\u6447\u5934\u3002 \u3000\u3000\u53ea\u6709\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\xb7\u8fd9\u4eba\u6b63\u6162\u6162\u7684\u8d70\u4e0a\u77f3\u9636\u8d70\u5230\u8fd9\u6247\u95e8\u5916\u3002 \u3000\u3000\u5916\u9762\u7a81\u7136\u6709\u4eba\u6572\u95e8\u8fd9\u4eba\u5c45\u7136\u6562\u5149\u660e\u5802\u7687\u7684\u6765\u6572\u95e8\xb7\u5012\u662f\u4ed6\u4eec\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u738b\u52a8\u7ec8\u4e8e\u95ee\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u5916\u9762\u6709\u4eba\u8f7b\u8f7b\u9053\u201c\u6211\u3002\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u4f60\u662f\u6dee\uff1f\u201d \u3000\u3000\u5916\u9762\u7684\u4eba\u7a81\u7136\u7b11\u4e86\xb7\u7b11\u58f0\u5982\u94f6\u94c3\u5374\u8fdc\u6bd4\u94c3\u58f0\u66f4\u6e05\u8106\u52a8\u4eba\u201c\u8fde\u6211\u7684\u58f0\u97f3\u4f60\u90fd\u542c\u4e0d\u51fa\u6765\u4e86\u9ebd \u771f\u662f\u4e2a\u5c0f\u6ca1\u826f\u5fc3\u7684\u3002\u201d \u3000\u3000\u6765\u7684\u8fd9\u4eba\u662f\u4e2a\u5973\u4eba\uff0c\u662f\u4e2a\u58f0\u97f3\u5f88\u597d\u542c\xb7\u597d\u50cf\u8fd8\u5f88\u5e74\u9752\u7684\u5973\u4eba\u3002\uff01 \u3000\u3000\u770b\u5230\u738b\u52a8\u7684\u8138\u8272\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5df2\u731c\u51fa\u8fd9\u5973\u4eba\u662f\u8c01\u4e86\u3001\u738b\u52a8\u7684\u8138\u8272\u5982\u767d\u7eb8\u3002 \u3000\u3000\u71d5\u4e03\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\uff0c\u5411\u95e8\u53e3\u6307\u4e86\u6307\u53c8\u5411\u5f8c\u9762\u6307\u4e86\u6307\u3002 \u3000\u3000\u90a3\u610f\u601d\u5c31\u662f\u8bf4\u201c\u4f60\u82e5\u4e0d\u613f\u89c1\u4ed6\uff0c\u53ef\u4ee5\u5230\u5f8c\u9762\u53bb\u907f\u907f\xb7\u6211\u53bb\u66ff\u4f60\u6321\u4e00\u6321\u3002\u201d \u3000\u3000\u4e0a\u52a8\u5f53\u7136\u61c2\u5f97\u4ed6\u7684\u610f\u601d\u5374\u6447\u5934\u3002 \u3000\u3000\u4ed6\u5bf9\u81ea\u5df1\u7684\u5904\u5883\xb7\u6bd4\u4efb\u4f8b\u522b\u7684\u4eba\u90fd\u660e\u767d\u7684\u591a\xb7\u4ed6\u5df2\u9000\u5230\u6700\u5f8c \u3000\u3000\u90a3\u610f\u601d\u5c31\u662f\u8bf4\u4ed6\u5df2\u65e0\u88ab\u518d\u9000\xb7\u800c\u4e14\u4e5f\u4e0d\u60f3\u518d\u9000\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u6765\u5f00\u95e8\uff1f\u201d \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u7ea2\u5a18\u5b50\u8fd9\u4e2a\u4eba\u4f46\u53ea\u8981\u542c\u5230\u8fd9\u79cd\u58f0\u97f3\xb7\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\u5979\u662f\u4e2a\u591a\u9ebd\u8ff7 \u4eba\u7684\u5973\u4eba\u3002\u201c\u662f\u4e0d\u662f\u4f60\u5c5e\u65bc\u91cc\u6709\u522b\u7684\u5973\u4eba\u4e0d\u6562\u8ba9\u6211\u770b\u89c1\uff1f\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u6211\u4e0d\u50cf\u4f60\u90a3\u9ebd\u5403 \u918b\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u5927\u6b65\u8d70\u8fc7\u53bb\uff0c\u4e49\u505c\u4e0b\u6c89\u58f0\u9053\uff1a\u201c\u95e8\u6ca1\u6709\u6813\u4e0a\u3002\u201d \u3000\u3000\u8f7b\u8f7b\u4e00\u63a8\u95e8\u5c31\u5f00\u4e86\uff0c\u4e00\u4e2a\u4eba\u6cb9\u5728\u95e8\u5916\u9762\u8fce\u6bdb\u4ece\u8fd9\u5c4b\u5b50\u91cc\u7167\u51fa\u53bb\u7684\u706f\u5149\u3002 \u3000\u3000\u6240\u6709\u7684\u706f\u5149\u597d\u50cf\u90fd\u5df2\u96c6\u4e2d\u5728\u5979\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u6240\u6709\u7684\u76ee\u5149\u5f53\u7136\u4e5f\u90fd\u96c6\u7578\u2019\u5728\u5979\u4e2a\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u597d\u50cf\u4e5f\u5728\u53d1\u5149\u79cd\u7ea2\u5f97\u8000\u773c\xb7\u7ea2\u5f97\u4ee4\u4eba\u5fc3\u8df3\u7684\u5149\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u8eab\u6c0f\u5f53\u7136\u5bb9\u7ea2\u8863\u670d\u4f46\u5149\u4e0d\u662f\u4ece\u5979\u8863\u670d\u4e0a\u53d1\u51fa\u6765\u7684\u4e8b\u5b9e\u4e0a\u9664\u4e86\u8863\u670d\u5916\uff0c\u5979\u8eab\u4e0a\u6bcf\u4e2a \u5730\u65b9\u597d\u50cf\u90fd\u5728\u53d1\u5149\u5c24\u5176\u662f\u5979\u7684\u773c\u775b\u5979\u7684\u7b11\u6e2f\u6bcf\u4e2a\u4eba\u90fd\u89c9\u5f97\u5979\u7684\u773c\u775b\u5728\u770b\u81ea\u5df1\u90fd\u89c9\u5f97\u5979\u5728\u5bf9\u81ea \u5df1\u7b11\u5047\u5982\u7b11\u5965\u6709\u503e\u57ce\u7684\u9b54\u529b\u5b9a\u5c31\u662f\u5979\u8fd9\u79cd\u7b11\u3002 \u3000\u3000\u71d5\u4e03\u7684\u8eab\u5b50\u79fb\u52a8\u201c\u4e0b\u6709\u610f\u65e0\u610f\u95f4\u6321\u4f4f\u90ed\u5927\u8def\u7684\u76ee\u5149\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u80fd\u4e0d\u8ba9\u81ea\u5df1\u7684\u670b\u53cb\u770b\u5230\u8fd9\u79cd\u5973\u4eba\u7684\u5a9a\u7b11\xb7\u8fd8\u662f\u4e0d\u8ba9\u4ed6\u770b\u89c1\u7684\u597d\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u5c82\u975e\u90fd\u5e94\u8be5\u8981\u81ea\u5df1\u7684\u670b\u53cb\u8fdc\u79bb\u7f6a\u6076\uff1f \u3000\u3000\u7ea2\u5a18\u5b50\u773c\u6ce2\u6d41\u52a8\u5ffd\u7136\u9053\u201c\u4f60\u4eec\u7537\u4eba\u4e3a\u4ec0\u9ebd\u603b\u4ed6\u5988\u7684\u662f\u8fd9\u79cd\u6837 \u3000\u3000\u8fd9\u5c31\u662f\u5979\u8bf4\u7684\u7b2c\u4e00\u53e5\u8bdd\uff0c\u8bf4\u5230\u8fd9\u91cc\u5979\u7a81\u7136\u505c\u987f\u4e86\u4e00\u535c\u597d\u50cf\u6218\u610f\u8981\u8ba9\u201c\u4ed6\u5988\u7684\u201d\u8fd9\u53c1\u4e2a\u5b57 \u5728\u8fd9\u4e9b\u7537\u5b50\u7684\u8111\u888b\u91cc\u7559\u4e86\u4e2a\u66f4\u6df1\u523b\u70b9\u7684\u5370\u8c61\uff0c\u597d\u50cf\u5979\u77e5\u9053\u8fd9\u5c4b\u4e8e\u91cc\u7684\u7537\u4eba\uff0c\u90fd\u62a5\u559c\u6b22\u8bf4\u8fd9\u53c1 \u4e2a\u5b87\u4e5f\u62a5\u559c\u6b22\u542c\u8fd9\u53c1\u4e2a\u5b57\u5728\u5979\u5634\u91cc\u8bf4\u51fa\u6765\u7684\u786e\u6709\u79cd\u7279\u522b\u7684\u5473\u9053\u3002 \u3000\u3000\u5c31\u5728\u5979\u505c\u987f\u8fd9\u4e0b\u4e8e\u7684\u65f6\u5019\u5df2\u6709\u4e2a\u4eba\u5fcd\u4e0d\u4f4f\u5728\u95ee\u5382\u201c\u6211\u4eec\u7537\u5b50\u90fd\u4ed6\u5988\u7684\u662f\u4ec0\u9ebd\u6837\u5b50\uff1f\u201d \u3000\u3000\u58f0\u97f3\u662f\u4ece\u71d5\u4e03\u80cc\u5f8c\u53d1\u6e56\u6765\u7684\xb7\u71d5\u4e03\u53ef\u4ee5\u6321\u4f4f\u90ed\u5927\u8def\u7684\u773c\u775b\u5374\u6321\u4e0d\u4ec6\u4ed6\u7684\u8033\u6735\uff0c\u4e5f\u585e\u4e0d\u4f4f \u4ed6\u7684\u5634\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u89c1\u5230\u597d\u770b\u7684\u5973\u4eba\u5c31\u597d\u50cf\u6d3b\u89c1\u4e86\u9b3c\u8fde\u4e2a\u5c41\u90fd\u653e\u4e0d\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u5979\u76b1\u8d77\u9f3b\u5b50\u8138\u4e0a\u53c8\u9732\u51fa\u4e86\u90a3\u79cd\u71d5\u4e03\u4e0d\u613f\u8ba9\u90ed\u5927\u8def\u770b\u89c1\u7684\u7b11\u5bb9\u7136\u5f8c\u624d\u8f7b\u8f7b\u63a5\u9053\u201c\u4f60\u4ecd\u4e4b\u4e2d \u81f3\u5c11\u4e5f\u8be5\u6709\u4e2a\u4eba\u5149\u8bf7\u6211\u8fdb\u53bb\u5440\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u7684\u65f6\u5019\uff0c\u5979\u7684\u4eba\u5df2\u7ecf\u5728\u5c4b\u4e8e\u91cc\u5382\u3002\u5c4b\u4e8e\u91cc\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\u5979\u662f\u8c01 \u4e5f\u90fd\u77e5\u9053\u5979\u662f\u6765\u5e72\u4ec0\u9ebd\u7684\u770b\u5230\u5979\u771f\u7684\u8d70\u4e86\u8fdb\u6765\xb7\u5927\u5bb6\u672c\u8be5\u89c9\u5f97\u5f88\u6124\u6012\u3001\u5f88\u7d27\u5f20\u3002 \u3000\u3000\u4f46\u71d5\u4e03\u5ffd\u7136\u53d1\u89c9\u90ed\u5927\u8def\u548c\u6797\u592a\u5e73\u770b\u5979\u7684\u65f6\u5019\u773c\u775b\u91cc\u975e\u4f46\u5b8c\u5168\u6ca1\u6709\u4ec7\u6068\u548c\u7d27\u5f20\u4e4b\u8272\u53cd\u800c\u5e26 \u7b11\u610f\uff0c\u5c31\u8fde\u71d5\u4e03\u81ea\u5df2\u90fd\u5df2\u7ecf\u5f00\u59cb\u6709\u70b9\u52a8\u6447\uff0c\u6709\u70b9\u6000\u7591\u3002 \u3000\u3000\u5728\u4ed6\u60f3\u8c61\u4e2d\xb7\u7ea2\u5a18\u5b50\u672c\u4e0d\u5e94\u8be5\u662f\u4e2a\u8fd9\u9ebd\u6837\u7684\u4eba\u81ea\u4ece\u5979\u8bf4\u51fa\u201c\u4ed6\u5988\u7684\u201d\u90a3\u53c1\u4e2a\u5b57\u5f8c\u5c4b\u5b50\u91cc \u7684\u6c14\u6c1b\u5c31\u597d\u50cf\u5b8c\u5168\u6539\u53d8\u4e86\u522b\u4eba\u5bf9\u5979\u7684\u5370\u8c61\u4e5f\u5b8c\u5168\u6539\u53d8\u4e86\u4e00\u4e2a\u6bd2\u5982\u86c7\u874e\u7684\u5996\u59ec\xb7\u8bf4\u8bdd\u4e2d\u4e0d\u8be5\u662f \u8fd9\u79cd\u8154\u8c03\u7684\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u65f6\uff0c\u71d5\u4e03\u300f\u53d1\u73b0\u5979\u624b\u91cc\u8fd8\u63d0\u4e2a\u5f88\u5927\u7684\u83dc\u7bee\u5b50\u3002 \u3000\u3000\u5979\u91cd\u91cd\u7684\u5c06\u7bee\u5b50\u5f80\u684c\u4e0a\u653e\u8f7b\u8f7b\u7684\u7529\u624b\u53f9\u6c14\u9053\u201c\u4e2a\u5973\u4eba\u5c31\u4e3a\u4e86\u66ff\u4f60\u4ecd\u9001\u4e1c\u897f\u6765\u63d0\u8fd9\u9ebd\u91cd\u7684 \u7bee\u5b50\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\u7d2f\u5f97\u624b\u90fd\u6289\u65ad\u4e86\uff0c\u4f60\u4eec\u5bf9\u5979\u96be\u9053\u8fde\u70b9\u611f\u6fc0\u7684\u610f\u601d\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u51b7\u51b7\u9053\u201c\u6ca1\u6709\u4eba\u8981\u4f60\u9001\u4e1c\u897f\u6765\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u8981\u4f60\u6765\u201c \u3000\u3000\u76f4\u5230\u8fd9\u65f6\uff0c\u7ea2\u5a18\u5b50\u624d\u7528\u773c\u89d2\u9690\u4e86\u4ed6\u4e00\u773c\u4f3c\u560e\u975e\u55c5\u4f3c\u7b11\u975e\u7b11\u54ac\u5634\u5507\u9053\u201c\u6211\u95ee\u4f60\u8fd9\u4e9b\u4eba\u662f\u4e0d \u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3\u6c14\xb7\u9053\u201c\u4f60\u53ef\u4ee5\u770b\u4f60\u670b\u53cb\u6328\u997f\u6211\u5374\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u6328\u997f\xb7\u90fd\u548c\u4f60\u70b9\u5173\u7cfb\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0\u9ebd\u6ca1\u6709\u5173\u7cfb\uff1f\u4f60\u7684\u670b\u53cb\u4e5f\u5c31\u662f\u6211\u7684\u670b\u53cb\u505a\u5927\u5ac2\u7684\u4eba\u600e\u9ebd\u80fd\u773c\u770b\u5f1f\u5144\u6328 \u997f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u9053\u201c\u6dee\u662f\u5927\u5ac2\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u4e86\u9053\u201c\u4f60\u4eec\u90fd\u662f\u738b\u8001\u5927\u7684\u597d\u670b\u53cb\xb7\u600e\u9ebd\u4f1a\u8fde\u738b\u5927\u5ac2\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5979\u6380\u8d77\u7bee\u5b50\u535c\u76d6\u7684\u5e03\u5a9a\u7136\u7684\u8bf4\u9053\u201c\u4eca\u5929\u662f\u5927\u5ac2\u8bf7\u5ba2\xb7\u4f60\u4eec\u8c01\u4e5f\u7528\u4e0d\u5ba2\u6c14\u4e0d\u5403\u4e5f\u662f\u767d\u4e0d \u5403\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u5403\u5382\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\uff1a\u201c\u5403\u5382\u4e5f\u662f\u767d\u5403\u3002\u201d \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u80a1\u4e0a\u7684\u8868\u60c5\u5c31\u597d\u50cf\u88ab\u4eba\u6398\u4e86\u8033\u5149\u4f3c\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\xb7\u5979\u624d\u8f6c\u8eab\u9762\u5bf9\u738b\u52a8\xb7\u9053\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u8ba4\u4e3a\u6211\u5e26\u6765\u7684\u4e1c\u897f\u6709\u6bd2\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e0d\u4f46\u8981\u6bd2\u6b7b\u522b\u7684\u4eba\u8fd8\u8981\u6bd2\u6b7b\u4f24\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u773c\u5708\u4f3c\u4e5f\u7ea2\u4e86\u7a81\u7136\u626d\u8f6c\u5934\u4ece\u7bee\u5b50\u91cc\u62ff\u51fa\u6761\u9e21\u817f\u560e\u58f0\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u9e21\u817f\u91cc\u9762\u5f53\u7136 \u4e5f\u6709\u6bd2\u4e86\uff1f\u201d \u3000\u3000\u7389\u52a8\u9053\u201c\u5f88\u53ef\u80fd\u3002\u201d\u7ea2\u65d7\u5b50\u9053\u201c\u597d\u597d\u2026\u2026\u201d \u3000\u3000\u5979\u5728\u9e21\u817f\u4e0a\u54ac\u4e86\u53e3\uff0c\u541e\u4e0b\u53bb\u53c8\u62ff\u51fa\u74f6\u9152\xb7\u9053\u201c\u9152\u91cc\u662f\u4e0d\u662f\u4e5f\u6709\u6bd2\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e5f\u6839\u53ef\u80fd\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u597d\u3002\u201d \u3000\u3000\u5979\u53c8\u559d\u4e86\u53e3\u9152 \u3000\u3000\u603b\u4e4b\u5979\u5c06\u7bee\u5b50\u91cc\u7684\u6bcf\u6837\u4e1c\u897f\u90fd\u5c1d\u4e86\u53e3\u624d\u59cb\u8d77\u5934\uff0c\u77aa\u738b\u52a8\u95ee\u9053\u201c\u73b0\u5728\u4f60\u8ba4\u4e3a\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u58eb\u52a8\u60f3\u4e5f\u4e0d\u60f3\u7acb\u523b\u56de\u7b54\u9053\u201c\u8fd8\u662f\u548c\u521a\u5b8c\u5168\u6837\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u8fd8\u8ba4\u4e3a\u6709\u6bd2\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7684\u773c\u6cea\u5df3\u7ecf\u5feb\u6d41\u4e0b\u6765\u5382\u53ef\u662f\u5979\u52c9\u5f3a\u5fcd\u4f4f\u8fc7\u95e8\u5458\u4e45\u624d\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\u7f00\u7136\u9053\u201c\u6211\u660e \u767d\u4f60\u7684\u60f3\u6cd5\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u65e9\u5c31\u8be5\u660e\u767d\u300f\u2019\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u8ba4\u4e3a\u6211\u65e9\u5c31\u5403\u4e86\u89e3\u836f\u624d\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u51c4\u7136\u9053\uff1a\u201c\u70bc\u59cb\u7ec8\u8ba4\u4e3a\u6211\u662f\u4e2a\u5fc3\u80a0\u6bd4\u86c7\u874e\u8fd8\u6bd2\u7684\u5973\u4eba\uff0c\u59cb\u7ec8\u8ba4\u4e3a\u6211\u5bf9\u4f60\u597d\u53ea\u4e0d\u8fc7 \u662f\u60f3\u5728\u5229\u7528\u4f60\u201d\u4e00\u2500\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u5979\u773c\u6cc5\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u6d41\u4e86\u5e7f\u6765\u3002 \u3000\u3000\u542c\u5230\u8feb\u91cc\xb7\u90ed\u5927\u8def\u548c\u6797\u592a\u5e73\u7684\u5fc3\u65e9\u5df2\u8f6f\u5382\u5634\u91cc\u867d\u6ca1\u6709\u8bf4\u4ec0\u9ebd\u5fc3\u91cc\u5df2\u4edf\u59cb\u89c9\u5f97\u738b\u52a8\u8fd9\u9ebd\u6837 \u5bf9\u5979\u5b9e\u5728\u672a\u514d\u8fc7\u4efd\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\u4ed6\u4eec\u4ee5\u524d\u603b\u7b97\u6709\u6bb5\u611f\u60c5\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u90ed\u5927\u8def\u73b0\u5728\u8bf4\u4e0d\u5b9a\u65e9\u5df2\u7ecf\u628a\u5979\u62b1\u5728\u949f\u91cc\u5382\u3002 \u3000\u3000\u4f46\u738b\u52a8\u8138\u535c\u5374\u8fd8\u662f\u8fde\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u8fd9\u4eba\u7684\u5fc3\u80a0\u7b80\u76f4\u5c31\u597d\u50cf\u662f\u94c1\u6253\u7684\u3002 \u3000\u3000\u53ea\u89c1\u7ea2\u5a18\u5b50\u5c06\u62ff\u51fa\u6765\u7684\u4e1c\u897f\u53c8\u6837\u6837\u9992\u6162\u7684\u653e\u56de\u7bee\u5b50\u91cc\u54ac\u8e1e\u5507\u9053\u201c\u597d\xb7\u4f60\u65e2\u7136\u8ba4\u4e3a\u6709\u6bd2\uff0c \u6211\u5c31\u5e26\u8d70\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u6700\u597d\u8d76\u5feb\u5e26\u8d70\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u8eab\u5b50\u5df2\u5728\u53d1\u6296\u98a4\u58f0\u9053\u201c\u4f60\u82e5\u662f\u8ba4\u4e3a\u6211\u5bf9\u4f60\u59cb\u7ec8\u6ca1\u5b89\u597d\u5fc3\uff0c\u6211\u4ee5\u5f8c\u4e5f\u53ef\u4ee5\u6c38\u8fdc\u4e0d\u6765 \u89c1\u4f60\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u672c\u5c31\u4e0d\u8be5\u6765\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u201c\u4e00\u201c\u6211\u60f3\u95ee\u4f60\u53e5\u8bdd\u2026\u2026\u201d \u3000\u3000\u5979\u7a81\u7136\u51b2\u5230\u738b\u52a8\u9762\u524d\uff0c\u5636\u58f0\u9053\u201c\u6211\u95ee\u4f60\u81ea\u4ece\u4f60\u8ba4\u5f97\u6211\u4e4b\u5f8c\u6211\u6709\u6ca1\u6709\u505a\u8fc7\u4ef6\u5bf9\u4e0d\u8d77\u4f60\u7684\u4e8b \u60c5\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u8bf4\u4e0d\u51fa\u8bdd\u4e86\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u634f\u7d27\u53cc\u62f3\u8fd8\u662f\u5fcd\u4e0d\u4f73\u5168\u8eab\u53d1\u6296\u5438\u58f0\u9053\u201c\u4e0d\u9519\u6211\u7684\u786e\u4e0d\u662f\u4e2a\u597d\u4eba\u7684\u786e\u5bb3\u8fc7\u4e0d\u5c11\u7537\u4eba \u53ef\u662f\u6211\u5bf9\u4f60\u2026\u2026\u6211\u51e0\u65f6\u5bb3\u8fc7\u4f60\u4f60\u8bf4\u4f60\u8bf4\u3002\u201d \u3000\u3000\u571f\u52a8\u51b7\u51b7\u9053\u201c\u73b0\u5728\u6211\u4eec\u5df2\u6ca1\u6709\u4ec0\u9ebd\u8bdd\u597d\u8bf4\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6014\u5382\u534a\u664c\u53c8\u9992\u6162\u7684\u70b9\u4e86\u70b9\u5934\u6f5e\u7136\u9053\u201c\u597d\u6211\u8d70\u6211\u8d70\u2026\u3002\u2018\u4f60\u653e\u5fc3\u8fd9\u6b21\u6211\u8d70\u4e86\u6c38\u8fdc\u518d \u4e5f\u4e0d\u4f1a\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u8f6c\u8fc7\u8eab\u63d0\u8d77\u7bee\u5b50\u6162\u6162\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\u53c8\u5b64\u72ec\u3001\u4e49\u7626\u5f31\u7684\u7559\u5f71\uff0c\u770b\u5979\u9992\u9992\u7684\u8d70\u5411\u53c8\u5bd2\u51b7\u3001\u53c8\u9ed1\u6697\u7684\u9662\u4e8e\u2026\u2026 \u3000\u3000\u9662\u5b50\u91cc\u7684\u98ce\u597d\u5927\u5c06\u6811\u4e0a\u7684\u79ef\u96ea\u7247\u7247\u5377\u4e86\u8d77\u6765\u7728\u773c\u5c31\u5439\u6563\u4e86\u5439\u5f97\u5e72\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u5979\u5c82\u975e\u4e5f\u4f1a\u50cf\u8fd9\u79ef\u96ea\u4e00\u6837\uff0c\u7728\u773c\u95f4\u5c31\u4f1a\u88ab\u6b27\u6563\xb7\u6b27\u5f97\u5e72\u4e7e\u51c0\u51c0\uff1f \u3000\u3000\u90ed\u5927\u8def\u53ea\u89c9\u5fc3\u91cc\u9178\u9178\u7684\u53ea\u5e0c\u671b\u738b\u52a8\u7684\u5fc3\u80fd\u8f6f\u8f6f\xb7\u80fd\u5c06\u8fd9\u65f6\u601c\u4e2d\u4e2d\u7684\u5973\u5b50\u7559\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u5de5\u52a8\u7684\u5fc3\u80a0\u786c\u7684\u8c61\u94c1\u6253\u7684\u5c31\u8fd9\u6837\u773c\u7741\u7741\u7684\u770b\u5979\u8d70\u5c71\u53bb\u8fde\u70b9\u8868\u793a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u773c\u770b\u7ea2\u5a18\u5b50\u5df2\u8de8\u51fa\u95e8\u64de\uff0c\u90ed\u5927\u8def\u51e0\u4e4e\u5df2\u5fcd\u4e2d\u4f4f\u8981\u66ff\u738b\u52a8\u628a\u5979\u7559\u4e0b\u6765\u5382\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7ea2\u5a18\u5b50\u7684\u8eab\u5b50\u9648\u62bd\u6410\xb7\u5c31\u597d\u50cf\u7a81\u7136\u6328\u4e86\u97ad\u5b50\u3002 \u3000\u3000\u7136\u8d28\u5979\u7684\u4eba\u5c31\u5012\u4e86\u535c\u53cb\u3002 \u3000\u3000\u5012\u5728\u5730\u4e0a\u56db\u80a2\u5df4\u62bd\u9677\u5728\u8d77\u5f20\u767d\u751f\u751f\u7684\u8138\u5df2\u53d8\u6210\u9ed1\u7d2b\u8272\u773c\u775b\u5f80\u4e0a\u7ffb\u55e1\u91cc\u4e0d\u505c\u7684\u5f80\u5916\u5192\u51fa\u767d \u6cab\u3002 \u3000\u3000\u767d\u6cab\u4e2d\u8fd8\u5e26\u8840\u4e1d\u3002 \u3000\u3000\u71d5\u4e03\u52a8\u5bb9\u9053\u201c\u5979\u5e26\u6765\u7684\u4e1c\u897f\u91cc\u679c\u7136\u6709\u6bd2\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u201c\u4f46\u5979\u81ea\u5df1\u5b9a\u4e0d\u77e5\u9053\uff0c\u5426\u5219\u5979\u81ea\u5df1\u600e\u9ebd\u4e2d\u6bd2\uff1f\u201d \u3000\u3000\u738b\u52a8\u5374\u8fd8\u662f\u77f3\u50cf\u822c\u7ad9\u5728\u90a3\u91cc\u8fde\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u770b\u5230\u8fd9\u56de\u4e8b\u3002 \u3000\u3000\u8fde\u9893\u4e03\u90fd\u6709\u70b9\u6025\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u9053\u201c\u738b\u8001\u5927\u65e0\u8bba\u600e\u9ebd\u6837\uff0c\u4f60\u4e5f\u8be5\u5148\u770b\u770b\u5979\u2026\u2026\u201d\u738b\u52a8\u9053\u770b\u4ec0 \u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u770b\u5979\u4e2d\u7684\u662f\u4ec0\u9ebd\u6bd2\uff1f\u8fd8\u6709\u6ca1\u6709\u6551\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u4ec0\u9ebd\u597d\u770b\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u53eb\u5382\u8d77\u6765\u9053\u201c\u4f60\u8fd9\u4eba\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u600e\u9ebd\u8fde\u70b9\u4eba\u6027\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u82e5\u4e0d\u662f\u71d5\u4e03\u5c06\u4ed6\u6309\u4f4f\xb7\u4ed6\u5df2\u7ecf\u6323\u624e\u722c\u8d77\u6765\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u7ea2\u5a18\u5b50\u4e0d\u505c\u7684\u75c9\u631b\u3001\u5598\u606f\uff0c\u8fd8\u5728\u4e0d\u505c\u7684\u665a\u9053\u201c\u738b\u52a8\u2026\u2026\u738b\u52a8\u2026\u2026\u201d \u3000\u3000\u738b\u52a8\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6323\u624e\u4f38\u51fa\u624b\u9053\u201c\u4f60\u201c\u2026\u2018\u4f60\u8fc7\u6765\u2026\u3002\u2019\u6c42\u6c42\u4f60\u2026\u2026\u201d \u3000\u3000\u738b\u52a8\u54ac\u201c\u54ac\u7259\u9053\u201c\u4f60\u82e5\u6709\u4ec0\u9ebd\u8bdd\u8981\u8bf4\uff0c\u6211\u90fd\u542c\u5f97\u89c1\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u4e0d\u77e5\u9053\u2026\u2026\u771f\u7684\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4e1c\u897f\u91cc\u6709\u6bd2\xb7\u6211\u771f\u7684\u7edd\u4e0d\u662f\u6765\u5bb3\u4f60\u7684\u4f60\u2026\u2026\u4f60 \u5e94\u8be5\u76f8\u4fe1\u6211\u3002\u201d \u3000\u3000\u5e72\u52a8\u8fd8\u6ca1\u6709\u8bf4\u8bdd\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\u201c\u6211\u76f8\u4fe1\u4f60\u6211\u4eec\u90fd\u76f8\u4fe1\u4f60\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u51c4\u7136\u7b11\u9053\u201c\u8d64\u7ec3\u86c7\u4ed6\u3002\u867d\u89c9\u5f97\u4f60\u5bf9\u4e0d\u8d77\u4ed6\u4eec\xb7\u867d\u7136\u662f\u60f3\u6765\u6740\u4f60\u7684\u53ef\u662f\u6211\u2026\u2026\u6211\u5e76 \u6ca1\u6709\u8fd9\u610f\u601d\u201d\u2026\u201c \u3000\u3000\u5979\u776b\u4f0f\u51b7\u6c57\u5df2\u6e7f\u900f\u91cd\u8863\u6323\u624e\u63a5\u9053\u201c\u6211\u867d\u7136\u4e0d\u662f\u4e2a\u597d\u5973\u4eba\u53ef\u662f\u6211\u5bf9\u4f60\u5374\u59cb\u7ec8\u662f\u771f\u5fc3\u771f\u610f \u7684\uff0c\u53ea\u8981\u4f60\u660e\u767d\u6211\u7684\u5fc3\u610f\u6211\u2026\u2026\u6211\u5c31\u7b97\u6b7b\u4e5f\u5fc3\u9669\u60c5\u613f\u5382\u2026\u3002\u201c \u3000\u3000\u8bf4\u5b8c\u5382\u8fd9\u53e5\u8bdd\u5979\u4f3c\u5df2\u7528\u5b8c\u4e01\u5168\u90e8\u529b\u6c14\u8fde\u6323\u624e\u90fd\u65e0\u529b\u72f0\u624e\u3002 \u3000\u3000\u7ff1\u5927\u8def\u53c8\u54ac\u7259\u9053\uff1a\u201c\u65e2\u7136\u89c1\u4e86\u4e3a\u4ec0\u9ebd\u8fd8\u57ce\u5728\u90a3\u91cc\u4e0d\u52a8\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5e94\u8be5\u600e\u9ebd\u52a8\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5979\u662f\u4e3a\u5382\u4f60\u624d\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\xb7\u4f60\u96be\u9053\u4e0d\u80fd\u60f3\u4e2a\u6cd5\u5b50\u6551\u6551\u5979\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u53eb\u6211\u600e\u9ebd\u6551\u5979\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u9053\u201c\u4f60\u65e2\u7136\u80fd\u89e3\u5c0f\u90ed\u4e2d\u7684\u6697\u5668\u4e4b\u6bd2\u5c31\u5e94\u8be5\u4e5f\u80fd\u89e3\u5979\u7684\u6bd2\u3002\u201d\u5e72\u52a8\u6447\u6447\u5934\u7f13\u7f13 \u9053\u201c\u90a3\u4e0d\u540c\u5b8c\u5168\u4e0d\u540c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u6709\u4ec0\u9ebd\u4e0d\u540c\uff1f\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u4e49\u4e0d\u8bf4\u8bdd\u4e2b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5728\u52c9\u5f3a\u63a7\u5236\u81ea\u5df1\u4f46\u76ee\u4e2d\u4f3c\u4e5f\u6cdb\u8d77\u5382\u6cea\u5149\xb7\u90a3\u4e0d\u4ec5\u662f\u60b2\u75db\u7684\u6cea\u800c\u4e14\u8fd8\u4eff\u4f5b\u5145\u6ee1\u5382\u6124 \u6012\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6307\u4e5f\u5728\u53d1\u6296\u3002 \u3000\u3000\u71d5\u4e03\u6c89\u541f\u9053\u201c\u5047\u5982\u8fde\u738b\u8001\u5927\u90fd\u4e0d\u80fd\u89e3\u5979\u7684\u6bd2\u4e16\u6b63\u53ea\u6709\u4e2a\u4eba\u80fd\u89e3\u5979\u7684\u6bd2\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8d64\u7ec3\u86c7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u9519\u6211\u4eec\u8be5\u5411\u8d64\u7ec3\u86c7\u8981\u89e3\u836f\u53bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u90a3\u53ea\u4f2f\u5f88\u96be\u3002\u201d \u3000\u3000\u5411\u8d64\u7ec3\u86c7\u53bb\u8981\u89e3\u836f\u90a3\u7b80\u76f4\u5c31\u597d\u50cf\u53bb\u8981\u8001\u864e\u5265\u5b83\u81ea\u5df1\u8eab\u4e0a\u7684\u76ae\u6837\u7684\u56f0\u96be\u3002 \u3000\u3000\u8fd9\u9053\u7406\u90ed\u5927\u8def\u81ea\u7136\u4e5f\u660e\u767d\u7684\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7684\u57d4\u606f\u58f0\u5df2\u6e10\u6e10\u5fae\u5f31\u5374\u8fd8\u5728\u4f4e\u547c\u738b\u52a8\u7684\u540d\u5b57\u201c\u738b\u52a8\u2500\u2500\u2500\u4e94\u52a8\u201c\u4e00\u3002\u201d \u3000\u3000\u547c\u5524\u58f0\u4e5f\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u7ff1\u5927\u8def\u542c\u5f97\u5fc3\u90fd\u788e\u4e86\xb7\u5fcd\u4e0d\u4f4f\u5927\u53eb\u9053\u201c\u4f60\u4eec\u65e2\u7136\u80fd\u6551\u5979\u53c8\u4e0d\u80af\u53bb \u5411\u8d64\u7ec3\u86c7\u8981\u89e3\u836f\u96be\u9053\u5c31\u8fd9\u6837\u773c\u770b\u5979\u6b7b\u5728\u4f60\u4eec\u9762\u524d\uff1f\u4f60\u4eec\u7a76\u7adf\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u7b8d\u4e03\u4e49\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u8ba4\u4e3a\u5e94\u8be5\u600e\u9ebd\u529e\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u7b97\u662f\u8d64\u7ec3\u86c7\xb7\u4e5f\u7edd\u4e0d\u4f1a\u773c\u770b\u5979\u88ab\u6bd2\u6b7b\u7684\uff0c\u4f60\u4eec \u3000\u3000\u6797\u592a\u4e2d\u76f4\u5750\u5728\u90a3\u91cc\u53d1\u6014\u6b64\u523b\u7a81\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u5927\u58f0\u9053\u201c\u5bf9\u8d64\u7ec3\u86c7\u4e5f\u7edd\u4e2d\u4f1a\u773c\u770b\u5979\u6b7b\uff0c\u6240 \u4ee5\u6211\u4eec\u5e94\u8be5\u9001\u5979\u56de\u53bb\u3002\u201d \u3000\u3000\u8fd9\u6cd5\u5b50\u867d\u4e0d\u597d\u4f46\u4e5f\u7b97\u6ca1\u6709\u6cd5\u5b50\u4e2d\u552f\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u71d5\u4e03\u76b1\u7709\u9053\u201c\u95ee\u9898\u662f\u8c01\u9001\u5979\u56de\u53bb\u5462\uff1f\u201d\u90ed\u5927\u8def\u901a\u201c\u54fc\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u4ec0\u9ebd\u90fd\u6ca1\u6709\u8bf4\u4f46\u773c\u89d2\u5374\u5728\u8198\u738b\u52a8\u3002 \u3000\u3000\u5f53\u7136\u662f\u738b\u52a8\u5e94\u8be5\u9001\u5979\u56de\u53bb\u3002\u53ea\u800d\u8fd9\u4eba\u8fd8\u6709\u70b9\u70b9\u826f\u5fc3\u5c31\u4e0d\u8be5\u773c\u770b\u5979\u6b7b\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u6ee5\u77e5\u4e0a\u52a8\u8fd8\u662f\u8fde\u70b9\u53cd\u5e94\u4e5f\u6ca1\u6709\uff0c\u5c31\u597d\u50cf\u6839\u672c\u542c\u5c0f\u61c2\u5c31\u597d\u50cf\u662f\u4e2a\u767d\u75f4\u3002 \u3000\u3000\u4e0a\u52a8\u5f53\u7136\u4e0d\u662f\u81fc\u75f4\u3002 \u3000\u3000\u4ed6\u662f\u5728\u88c5\u50bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u5fcd\u4e0d\u4f4f\u5927\u53eb\u8d77\u6765\u9053\u201c\u597d\u4f60\u4eec\u90fd\u4e0d\u9001\u5979\u56de\u53bb\u6211\u9001\u5979\u56de\u53bb\u201c \u3000\u3000\u4ed6\u7528\u5c3d\u5e73\u751f\u529b\u6c14\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u71d5\u4e03\u4e94\u523b\u7d27\u7d27\u62b1\u4f50\u4e86\u4ed6\u3002 \u3000\u3000\u738b\u52a8\u56de\u8fc7\u5934\u770b\u4ed6\u4eec\u65e5\u5149\u4e2d\u53c8\u662f\u60b2\u75db\u53c8\u662f\u601c\u60dc\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5fc3\u91cc\u7a76\u7adf\u5728\u60f3\u4ec0\u9ebd\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u7ec8\u4e88\u8dfa\u4e86\u8dfa\u811a\xb7\u9053\u201c\u597d\u6211\u9001\u5979\u56de\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\uff0c\u521a\u60f3\u62b1\u8d77\u7ea2\u5974\u5b50\u3002 \u3000\u3000\u6797\u592a\u5e73\u7a81\u7136\u7bad\u822c\u7a9c\u8fc7\u6765\u7528\u529b\u5c06\u4ed6\u6a80\u649e\u5f97\u9000\u51fa\u4e03\u516b\u5c3a\u7834\u8dcc\u5728\u5899\u89d2\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u6797\u592a\u5e73\u5df2\u7fd8\u8d77\u4e86\u5de5\u5a18\u5b50\u3002 \u3000\u3000\u738b\u52a8\u7a81\u7136\u53d8\u8272\u5927\u58f0\u9053\u201c\u4f60\u60f3\u5e72\u4ec0\u9ebd\u201d \u3000\u3000\u6797\u592a\u5e73\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\u201c\u53ea\u6709\u6211\u624d\u80fd\u9001\u5979\u56de\u53bb\u71d5\u4e03\u8981\u7167\u987e\u5c0f\u90ed\uff0c\u4f60\u662f\u4ed6\u7684\u773c\u4e2d\u9489\u4f60\u53bb\u4e86 \u4ed6\u851a\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u8bdd\u4eba\u5df2\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u706d\u52a8\u8df3\u8d77\u6765\u51b2\u8fc7\u53bb\u5927\u58f0\u559d\u9053\u201c\u5feb\u70b9\u653e\u4e0b\u5979\uff0c\u5feb\u2026\u2026\u201d \u3000\u3000\u559d\u58f0\u4e2d\xb7\u6797\u592a\u5e73\u7a81\u7136\u58f0\u60ca\u547c\u3002 \u3000\u3000\u90a3\u5944\u5944\u606f\u7684\u7ea2\u5a18\u5b50\u5df2\u6bd2\u86c7\u822c\u81ea\u4ed6\u6000\u4e2d\u5f39\u8d77\u51cc\u7a7a\u2026\u4e2a\u7ffb\u8eab\u62e3\u51fa\u5382\u4e8c\u6587\u7728\u773c\u95f4\u5c31\u6ca1\u4eba\u9ed1\u6697 \u4e2d\u3002 \u3000\u3000\u53ea\u542c\u5979\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u8fdc\u8fdc\u4f20\u6765\u9053\u201c\u80dc\u738b\u7684\u738b\u516b\u86cb\xb7\u4f60\u89c1\u6b7b\u4e0d\u6551\u4f60\u597d\u6ca1\u826f\u5fc3\uff0c\u4f60\u7b80\u76f4\u4e0d\u662f \u4e2a\u597d\u4e1c\u897f\u3002\u201d \u3000\u3000\u8bf4\u5230\u6700\u5f8c\u53e5\u8bdd\xb7\u4eba\u5df2\u8d70\u8fdc\u53ea\u5269\u4e0b\u90a3\u6bd4\u94f6\u94c3\u8fd8\u6e05\u8106\u60a6\u8033\u7684\u7b11\u5362\u98d8\u8361\u5728\u98ce\u91cc\u3002 \u3000\u3000\u597d\u51b7\u7684\u98ce\u3002 \u3000\u3000\u6444\u9b42\u7684\u94f6\u94c3\u3002 \u3000\u3000\u6797\u592a\u5e73\u5012\u5728\u96ea\u5730\u91cc\u524d\u80f8\u5df2\u591a\u4e86\u70b9\u4e4c\u9ed1\u7684\u8840\u8ff9\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u52a8\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u8fde\u5458\u5f8c\u4e1d\u751c\u7b11\u4e5f\u7ec8\u4e8e\u88ab\u51b7\u98ce\u5439\u6563\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u5382\u591a\u4e45\xb7\u738b\u52a8\u7ec8\u4e8e\u5021\u6162\u7684\u8d70\u51fa\u53bb\u5c06\u6797\u592a\u5e73\u62b1\u5382\u56de\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8272\u6bd4\u98ce\u8fd8\u51b7\u6bd4\u591c\u8272\u8fd8\u9634\u6697\u3002 \u3000\u3000\u90ed\u5927\u8def\u6cc5\u5df2\u6d41\u4e0b\u3002 \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u4e5f\u5df1\u6cea\u6d41\u6ee1\u9762\u67d4\u58f0\u9053\u201c\u4f60\u7528\u77f3\u96be\u53d7\xb7\u8fd9\u4e5f\u4e0d\u80fd\u602a\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8bf4\u8fd9\u53e5\u8bdd\u8fd8\u597d\u8bf4\u51fa\u6765\u90ed\u5927\u8def\u600e\u9ebd\u8fd8\u80fd\u5fcd\u5f97\u4f0e\u600e\u9ebd\u4e0d\u53d7\u5f97\u4e86\uff1f \u3000\u3000\u4ed6\u7a81\u7136\u8c61\u662f\u4e2a\u5b69\u5b50\u822c\xb7\u5931\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\xb7\u738b\u52a8\u624d\u4fb5\u6162\u7684\u62cd\u8d77\u5934\u9053\u201c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u60ca\u53c8\u559c\u5931\u58f0\u9053\u201c\u4ed6\u662f\u4e0d\u662f\u8fd8\u6709\u6551\uff1f\u201d\u707e\u4e50\u82f1\u96c4 \u3000\u3000\u4e03\u52a8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u8981\u600e\u9ebd\u624d\u80fd\u6551\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\u4ed6\u8138\u8272\u53c8\u53d8\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u60f3\u5230\u4e16\u4e0a\u4e5f\u53ea\u6709\u79cd\u6cd5\u5b50\u80fd\u6551\u5f97\u4e86\u6797\u592a\u5e73\u3002 \u3000\u3000\u6700\u53ef\u6015\u7684\u79cd\u6cd5\u5b50\u3002 \u3000\u3000\u4ed6\u770b\u738b\u52a8\u76ee\u4e2d\u5df3\u4e0d\u7981\u9732\u51fa\u6050\u60e7\u4e4b\u8272\u56e0\u4e3a\u4ed6\u77e5\u9053\u738b\u52a8\u5728\u60f3\u4ec0\u9ebd\u3002 \u3000\u3000\u738b\u52a8\u5f53\u7136\u4e5f\u77e5\u9053\u4ed6\u5728\u60f3\u4ec0\u9ebd\u8138\u4f3c\u53cd\u5012\u5f88\u5e73\u9759\u8c08\u8c08\u5730\u9053\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u8981\u600e\u9ebd\u6837\u624d\u80fd\u6551\u5f97\u4e86 \u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u7528\u529b\u6447\u5934\u9053\u201c\u8fd9\u6cd5\u5b50\u4e0d\u884c\u3002\u201d \u3000\u3000\u4e8e\u52a8\u9053\u201c\u884c\u3002\u201d \u3000\u3000\u71d5\u4e03\u5927\u58f0\u9053\u201c\u7edd\u5bf9\u4e0d\u884c\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u884c\u4e5f\u5f97\u884c\u56e0\u4e3a\u6211\u4eec\u5df2\u522b\u65e0\u9009\u62e9\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u71d5\u4e03\u7a81\u7136\u5012\u4e86\u4e0b\u53bb\u5012\u5728\u6905\u5b50\u4e0a\u4f3c\u4e4e\u518d\u4e5f\u652f\u6301\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b63\u77aa\u5927\u4e86\u773c\u775b\u5728\u770b\u4ed6\u4eec\u4ed6\u8138\u4ea1\u8fd8\u5e26\u6cea\u75d5\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u4eec\u8bf4\u7684\u7a76\u7adf\u662f\u4e2a\u4ec0\u9ebd\u6cd5 \u5b50\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u6ca1\u6709\u4eba\u5385\u66f0\u3002\u90ed\u5927\u8def\u6025\u9053\u201c\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u4e2d\u544a\u8bc9\u6211\u201d \u3000\u3000\u71d5\u4e03\u7ec8\u4ee4\u8f7b\u8f7b\u53f9\u5382\u53e3\u6c14\u9053\u201c\u4f60\u5c31\u7b97\u77e5\u9053\u4e86\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u6ca1\u6709\u7528\uff1f\u82e5\u4e0d\u662f\u6211\u4e71\u51fa\u535e\u610f\u6797\u592a\u5e73\u4e5f\u4e0d\u4f1a\u53d8\u6210\u8fd9\u6837\u5382\u6211\u6bd4\u8c01\u90fd\u96be\u53d7\uff0c \u6bd4\u8c01\u90fd\u6025\u60f3\u6551\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u52a8\u51b7\u51b7\u9053\u201c\u4f60\u73b0\u5728\u53ea\u80fd\u6551\u4e2a\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u71d5\u4e03\u67d4\u58f0\u9053\u201c\u4f60\u53d7\u7684\u4f24\u5f88\u4e0d\u8f7b\u82e5\u518d\u80e1\u601d\u4e71\u60f3\uff0c\u6015\u8fde\u4f60\u81ea\u5df1\u7684\u547d\u90fd\u5f88\u96be\u4fdd\u4f50\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u77aa\u4ed6\u4eec\uff0c\u5ffd\u7136\u9053\u201c\u6211\u4e2d\u7684\u6697\u5668\u662f\u4e0d\u662f\u4e5f\u6709\u6bd2\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u8c01\u6551\u5de5\u6211\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u738b\u8001\u5927\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u738b\u8001\u5927\u65e2\u7136\u80fd\u89e3\u5f97\u4e86\u6211\u7684\u4e2d\u6bd2\u4e3a\u4ec0\u9ebd\u5c31\u4e0d\u80fd\u89e3\u6797\u592a\u5e73\u7684\u6bd2\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53c8\u4e0d\u80af\u53e3\u5382\u3002\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4eec\u6697\u5668\u4e0a\u7684\u6bd2\u5e94\u8be5\u662f\u540c\u8def\u7684\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53c8\u6c89\u9ed8\u5382\u5f88\u4e45\u624d\u957f\u957f\u53f9\u606f\u4e86\u58f0\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u95ee\u5f97\u8fd9\u9ebd\u6e05\u695a\u201d \u3000\u3000\u90ed\u5927\u8def\u5927\u58f0\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u95ee\u6e05\u695a\uff1f\u4f60\u4eec\u82e5\u518d\u4e0d\u544a\u8bc9\u6211\u6211\u5c31\u2026\u2026\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u4ed6\u7528\u529b\u6376\u5e8a\u94fa\uff0c\u6c14\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u54ac\u5382\u54ac\u7259\u9053\u201c\u597d\u6211\u54a8\u8bc9\u4f60\u4f60\u4e2d\u7684\u6bd2\uff0c\u548c\u6797\u592a\u5e73\u5dfe\u7684\u85e9\u7684\u786e\u90fd\u662f\u8d64\u7ec3\u86c7\u7684\u72ec\u95e8\u6bd2 \u836f\xb7\u6240\u4ee5\u4e5f\u53ea\u6709\u4ed6\u7684\u72ec\u95e8\u89e3\u836f\u624d\u80fd\u6551\u5f97\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u738b\u8001\u5927\u2026\u2500\u2606 \u3000\u3000\u71d5\u4e03\u9053\u201c\u738b\u8005\u5927\u51c6\u5907\u8131\u79bb\u4ed6\u6790\u7684\u65f6\u5019\u4ed6\u5c31\u5df4\u7ecf\u5077\u5077\u5730\u85cf\u8d77\u4e86\u70b9\u8d64\u7ec3\u86c7\u7684\u72ec\u95e8\u89e3\u836f\u4ee5\u9632 \u4e07\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u89e3\u836f\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u4e2d\u5b57\u9053\u201c\u6551\u4f60\u7684\u65f6\u5019\u5df2\u7528\u5b8c\u5de5\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5148\u58f0\u901a\u201c\u5168\u90fd\u7528\u5b8c\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fde\u70b9\u90fd\u6ca1\u6709\u5269\u3002\u201d \u3000\u3000\u4ed6\u5478\u5634\u5c5e\uff0c\u7f13\u7f13\u9053\u201c\u90a3\u4e9b\u89e3\u836f\u672c\u662f\u51c6\u5907\u7528\u7c73\u6551\u4ed6\u81ea\u5df1\u7684\u4eba\u53e3\u5374\u5168\u90fd\u7528\u6765\u6551\u4e86\u4f60\u6211\u672c\u6765\u4ee5 \u4e3a\u4ed6\u8fd8\u7559\u2026\u70b9\xb7\u6df1\u77e5\u4ed6\u5374\u4e2d\u4f2f\u4f60\u4e2d\u7684\u6bd2\u592a\u6df1\u751f\u6015\u89e3\u836f\u7684\u4efd\u91cf\u5c0f\u591f\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u4ed6\u4e5f\u773c\u7736\u53d1\u7ea2\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u8fd9\u4ef6\u4e8b\u672c\u53ea\u6709\u4ed6\u77e5\u9053\xb7\u56e0\u4e3a\u90a3\u65f6\u6797\u592a\u5e73\u8fd8\u5728\u5916\u9762\u5b88 \u671b\u3002 \u3000\u3000\u90ed\u5927\u8def\u634f\u7d27\u53cc\u62f3\u9ec4\u8c46\u822c\u5927\u7684\u51b7\u6c57\u5df2\u6d41\u4e86\u80a1\xb7\u8fc7\u4e86\u5f88\u4e45\xb7\u624d\u8d2d\u54fa\u9053\u201c\u6797\u592a\u5e73\u8db3\u6211\u5bb3\u7684\u552f\u80fd \u6551\u4ed6\u7684\u89e3\u836f\u4e5f\u88ab\u6211\u7528\u5149\u6211\u771f\u6709\u529e\u6cd5\u771f\u4e86\u4e0d\u8d77\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u7ecd\u7136\u9053\u201c\u8fd9\u672c\u662f\u8c01\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\xb7\u4f60\u5e76\u6ca1\u6709\u8981\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u5636\u58f0\u9053\u201c\u4e0d\u9519\xb7\u6211\u5e76\u6ca1\u6709\u8981\u4f60\u4eec\u6551\u6211\u4f60\u4eec\u81ea\u5df1\u975e\u8fd9\u6837\u5b50\u505a\u4e0d\u5475\u4f46\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u60f3 \u60f3\u8fd9\u6837\u5b50\u53eb\u6211\u600e\u9ebd\u80fd\u5b89\u5fc3\u6d3b\u5f97\u5382\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u6c89\u8138\xb7\u9053\u201c\u4f60\u975e\u6d3b\u535c\u53bb\u4e0d\u53ef\u6211\u65e2\u5df2\u6551\u5382\u4f60\u4f60\u60f3\u6b7b\u4e5f\u4e0d\u884c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u6797\u592a\u5e73\u2500\u4e00\u3002\u201d \u3000\u3000\u4e0a\u52a8\u6c89\u58f0\u9053\u201c\u4f60\u7528\u4e0d\u62c5\u5fc3\u4ed6\u6211\u65e2\u80fd\u6551\u4f60\xb7\u5f53\u7136\u4e5f\u6709\u6cd5\u4ea7\u6551\u4ed6\u201c \u3000\u3000\u90ed\u5927\u8def\u54ac\u7259\u9053\u201c\u73b0\u5728\u6211\u603b\u7b97\u5df2\u77e5\u9053\u4f60\u8981\u7528\u4ec0\u9ebd\u6cd5\u5b50\u4e86\u3002\u201d \u3000\u3000\u4e8e\u52a8\u9053\u201c\u54e6\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u60f3\u95ee\u8d64\u7ec3\u86c7\u53bb\u8981\u89e3\u836f\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u54ac\u7259\u9053\u201c\u521a\u4f60\u4e0d\u80af\u53bb\xb7\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4f60\u592a\u4e86\u89e3\u7ea2\u5a18\u5b50\u5382\xb7\u4f46\u73b0\u5728\u4e3a\u4e86\u6797\u592a\u5e73\u5c31\u7b97\u8981\u7528 \u4f60\u7684\u547d\u53bb\u6362\u89e3\u836f\xb7\u4f60\u4e5f\u975e\u53bb\u4e0d\u53ef\u7684\u2606\u201d \u3000\u3000\u738b\u52a8\u8c08\u8c08\u7684\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4f60\u4ee5\u4e3a\u2018\u5df4\u51b2\u5929\u9e70\u4e2d\u571f\u662f\u4e2a\u8fd9\u9ebd\u597d\u7684 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e0d\u8ba4\u5f97\u4ec0\u9ebd\u9e70\u4e2d\u738b\u53ea\u8ba4\u5f97\u738b\u52a8\uff0c\u4e5f\u5f88\u4e86\u89e3\u738b\u52a8\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u4eba\u3002\u201d\u738b\u52a8\u9053 \u201c\u54e6\uff1f\u201d\u90ed\u5927\u8def\u76ee\u4e2d\u53c8\u6709\u6cea\u5149\u9053\u201c\u738b\u52a8\u8fd9\u4e2a\u4eba\u7684\u8138\u770b\u6765\u867d\u7136\u4e49\u51b7\u53c8\u786c\u5177\u5b9e\u4ed6\u7684\u5fc3\u80a0\u5374\u6bd4\u8c46\u8150 \u8fd8\u8f6f\u6bd4\u706b\u8fd8\u70ed\u3002\u201d \u3000\u3000\u738b\u52a8\u6c89\u9ed8\u7ec8\u4e8e\u7f13\u7f13\u5730\u9053\u201c\u4f60\u65e2\u7136\u5f88\u4e86\u89e3\u6211\xb7\u5c31\u5e94\u8be5\u77e5\u9053\u6211\u82d4\u60f3\u505a\u4ef6\u4e8b\u4fbf\u6dee\u4e5f\u62e6\u4e0d\u4f4f\u6211 \u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e5f\u5e94\u8be5\u5f88\u5382\u89e3\u6211\u6211\u82e5\u60f3\u505a\u4ef6\u4e8b\u65f6\u4e5f\u6ca1\u6709\u4eba\u80fd\u62e6\u5f97\u4f4d\u7684\u201c \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u60f3\u505a\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u53cb\u5411\u8d64\u7ec3\u86c7\u8981\u89e3\u836f\u3002\u201d \u3000\u3000\u71d5\u4e03\u52a8\u5bb9\u9053\u201c\u4f60\u600e\u9ebd\u80fd\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u4fc4\u975e\u53bb\u4e0d\u53ef\uff0c\u800c\u4e14\u4e5f\u53ea\u6709\u6211\u80fd\u53bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u606b\u4f60\u7684\u4f24\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u56e0\u4e3a\u6211\u53d7\u4e86\u4f24\uff0c\u6240\u4ee5\u4f60\u4eec\u66f4\u8981\u8ba9\u6211\u53bb\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u522b\u4eba\u8bf4\u8bdd\u63a5\u53c8\u9053\u201c\u73b0\u5728\u6211\u4eec\u53ea\u5269\u4e0b\u4e24\u4e2a\u4eba\u4e24\u4e2a\u4eba\u53bb\u5bf9\u4ed8\u4ed6\u4eec\u53c1\u4e2a\u5df1\u5f88\u5403\u529b\u6240\u4ee5\u4f60 \u4eec\u7edd\u4e0d\u80fd\u518d\u53d7\u4f24\u4e86\uff0c\u897f\u5219\u6211\u4eec\u5927\u5bb6\u90fd\u53ea\u6709\u6b7b\u8def\u6761\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u8bdd\u867d\u7136\u6709\u9053\u7406\u53ee\u662f\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\u201c\u53ef\u662f\u6211\u8ba2\u4e49\u7edd\u4e0d\u80fd\u770b\u6797\u592a\u5e73\u4e2d\u6bd2\u800c\u6b7b\u6240\u4ee5\u53ea\u6709\u8ba9\u6211\u53bb\u6211\u53cd\u6b63 \u5df2\u53d7\u4f24\u5df2\u51fa\u4e0d\u5382\u529b\uff0c\u4f55\u51b5 \u3000\u3000\u4ed6\u7b11\u5382\u7b11\uff0c\u63a5\u9053\u201c\u8d64\u7ec3\u86c7\u4ed6\u4eec\u81f3\u5c11\u4e5f\u7b97\u662f\u4e2a\u4eba\xb7\u603b\u672c\u4f1a\u5bf9\u4e2a\u5b8c\u5168\u65e0\u56de\u624b\u4e4b\u529b\u7684\u4eba\u535c\u6bd2\u624b \u5427\u3002\u201d \u3000\u3000\u738b\u52a8\u51b7\u7b11\u9053\u201c\u4f60\u4ee5\u4e3a\u4ed6\u4eec\u4e0d\u4f1a\u6740\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8c0a\u201c\u60f3\u5fc5\u4e0d\u4f1a\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u4f60\u4e86\u89e3\u4ed6\u4eec\uff1f\u8fd8\u662f\u6211\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u662f\u4f60\u3002\u201d\u5e72\u52a8\u9053\u201c\u90a3\u9ebd\u6211\u544a\u8bc9\u4f60\u4ed6\u4eec\u4e0d\u6740\u7684\u53ea\u6709\u4e00\u79cd\u4eba\u3002\u201d \u3000\u3000\u90e7\u6b20\u8def\u904f\u201c\u54ea\u79cd\u5165\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6b7b\u4eba\u3002\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u98ce\u4e2d\u53c8\u4f20\u6765\u4e00\u9635\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u3002 \u3000\u3000\u71d5\u4e03\u51b2\u51fa\xb7\u5c31\u770b\u5230\u4e00\u53ea\u6de1\u9ec4\u8272\u7684\u98ce\u7b49\u81ea\u591c\u7a7a\u4e2d\u6162\u6162\u98d8\u843d\u4e0b\u6765\u3002 \u3000\u3000\u98ce\u7b2c\u662f\u65b9\u7684\u4e0a\u9762\u8fd8\u7528\u6731\u7b14\u753b\u4e86\u5f2f\u5f2f\u66f2\u66f2\u7684\u82b1\u7eb9\u3002 \u3000\u3000\u73b0\u5728\u71d5\u4e03\u5df2\u77e5\u9053\u8fd9\u5e76\u4e0d\u662f\u98ce\u7b2c\uff0c\u4e3d\u662f\u9053\u89c1\u5c31\u9001\u7ec8\u7684\u50ac\u547d\u6905\u3002 \u3000\u3000\u50ac\u547d\u7b26\u4e0a\u5199\u7684\u662f\u4ec0\u9ebd\xb7\u8c01\u4e5f\u770b\u4e0d\u61c2\u3002 \u3000\u3000\u53ea\u6709\u5230\u8fc7\u5730\u72f1\u7684\u4eba\u624d\u770b\u5f97\u61c2\u3002 \u3000\u3000\u738b\u52a8\u770b\u5f97\u6a2a\u3002 \u3000\u3000\u6de1\u9ec4\u8272\u7684\u98ce\u7ba9\u4e0a\u753b\u6ee1\u4e86\u6731\u7ea2\u8272\u7684\u7b26\u5492\u7ea2\u5f97\u5c31\u8c61\u662f\u8840\u5c31\u8c61\u662f\u5730\u72f1\u4e2d\u7684\u706b\u3002 \u3000\u3000\u4e0a\u52a8\u51dd\u89c6\u51b7\u6de1\u7684\u76ee\u5149\u4e2d\u4e0d\u7981\u9732\u51fa\u4e86\u6050\u60e7\u4e4b\u610f\u3002 \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u770b\u8fd9\u98ce\u7b2c\uff0c\u53ea\u5728\u770b\u738b\u52a8\u7684\u773c\u775b\u4ed6\u867d\u7136\u770b\u4e0d\u61c2\u98ce\u7b2c\u4e0a\u7684\u7b26\u51b5\u5374\u770b\u5f97\u906d\u738b\u52a8\u773c\u775b\u91cc \u7684\u795e\u8272\uff1a \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u8fd9\u4e0a\u9762\u5199\u4e9b\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u6c89\u9ed8\u4e86\u5f88\u4e45\u8fd8\u662f\u6ca1\u6709\u56de\u7b54\u5374\u53c8\u63a8\u5f00\u7a97\u4e8e\xb7\u671b\u7a97\u5916\u7684\u5438\u8272\u3002 \u3000\u3000\u661f\u5df2\u6e10\u7a00\u591c\u5df2\u5c06\u5c3d\u3002 \u3000\u3000\u7070\u6aac\u6f58\u7684\u591c\u8272\u4e2d\u53c8\u6709\u53ea\u98ce\u7b2c\u6b63\u5189\u5189\u5347\u8d77\u3002\u5e72\u52a8\u8f7b\u8f7b\u53f9\u606f\u4e86\u58f0\u9053\u201c\u5929\u5feb\u4eae\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5929\u5b9a\u4f1a\u4eae\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e5f\u5b9a\u8981\u8d70\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u5931\u8272\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u56e0\u4e3a\u5929\u4eae\u4e4b\u524d\uff0c\u6211\u82e5\u8fd8\u6ca1\u6709\u8d76\u5230\u90a3\u98ce\u7b49\u4e0b\u9762\u6797\u592a\u5e73\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u5929\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u66d9\u8272\u5e26\u7ed9\u4eba\u8861\u7684\uff0c\u672c\u662f\u5149\u660e\u3001\u6b22\u4e50\u548c\u5e0c\u671b\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5e26\u7ed9\u738b\u52a8\u4ed6\u4eec\u7684\u5374\u53ea\u6709\u6b7b\u53c1\u3002 \u3000\u3000\u201c\u5929\u4eae\u4e4b\u524d\uff0c\u738b\u52a8\u82e5\u8fd8\u6ca1\u6709\u7ad9\u5728\u90a3\u98ce\u7b54\u4e0b\u7b49\u6797\u592a\u5e73\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u90a3\u8d38\u5492\u6b62\u5199\u7684\u610f\u601d\u3002 \u3000\u3000\u8fd9\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u738b\u52a8\u5df2\u975e\u53bb\u4e0d\u53ef\xb7\u975e\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u90ed\u5927\u8def\u5927\u58f0\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u53ea\u6709\u6211\u80fd\u53bb\uff0c\u8c01\u4e5f\u4f11\u60f3\u62e6\u4f4f\u6211\u3002\u201d \u3000\u3000\u738b\u52a8\u8c08\u6de1\u9053\u201c\u597d\u4f11\u53ef\u4ee5\u53bb\xb7\u4f46\u65e0\u8bba\u4f60\u53bb\u4e0d\u53bb\xb7\u6211\u8fd8\u662f\u975e\u53bb\u4e0d\u9762\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u65e2\u5df2\u53bb\u4e86\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4ed6\u4ecd\u8981\u7684\u662f\u6211\xb7\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u62a2\u9053\u201c\u4f60\u53bb\u4e86\u4ed6\u4eec\u4e5f\u672a\u5fc5\u4f1a\u5c06\u89e3\u836f\u7ed9\u4f60\u4f60\u5e94\u8be5\u6bd4\u6211\u66f4\u660e\u767d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u4e0d\u8fc7\u53ea\u662f\u4ed6\u4eec\u7684\u8bf1\u5175\u4e4b\u8ba1\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5708\u5957\u4ed6\u4eec\u5b9a\u65e9\u5df2\u5728\u90a3\u91cc\u5e03\u4e0b\u4e86\u57cb\u4f0f\xb7\u5c31 \u7b49\u4f60\u53bb\u4e0a\u94a9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u70b9\u6211\u4e5f\u6bd4\u4f60\u660e\u767d\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u3008\u6c11\u4f60\u8fd8\u662f\u8981\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8981\u6211\u770b\u6797\u592a\u5e73\u6b7b\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u547c\u5438\u5df2\u5fae\u5f31\u7259\u5173\u5df1\u54ac\u7d27\u8138\u4e0a\u5df2\u9732\u51fa\u5382\u6b7b\u8272\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u80fd\u770b\u5f97\u51fa\u4ed6\u5df3\u79bb\u6b7b\u4e0d\u8fdc\u3002 \u3000\u3000\u71d5\u4e03\u6444\u7136\u9053\u201c\u6211\u4eec\u4e0d\u80fd\u770b\u4ed6\u6b7b\u4eba\u6c11\u4e5f\u4e0d\u80fd\u773c\u770b\u4f60\u53bb\u9001\u6b7b\u201d\u738b\u52a8\u8c08\u6de1\u7b11\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u6211 \u5b9a\u662f\u53bb\u9001\u6b7b\uff1f\u8bf4\u4e0d\u5b9a\u6211\u5f88\u5feb\u5c31\u80fd\u5e26\u89e3\u836f\u95f4\u6765\u5462\uff1f\u201d\u71d5\u4e03\u77aa\u4ed6\xb7\u9053\u201c\u4f60\u8fd9\u662f\u5728\u9a97\u6211\u4eec\uff1f\u8fd8\u662f\u9a97 \u4f60\u5df1\uff1f\u201d \u3000\u3000\u738b\u52a8\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u4e5f\u77e5\u9053\u80fd\u56de\u6765\u7684\u5e0c\u671b\u4e0d\u5927\xb7\u4f46\u53ea\u800d\u6709\u5206\u5e0c\u671b\u6211\u5c31\u5f97\u5de6\u3002\u201d \u3000\u3000\u70ed\u4e03\u9053\u201c\u82d4\u8fde\u5206\u5e0c\u671b\u90fd\u6ca1\u6709\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u627e\u8fd8\u662f\u8981\u53bb\u3002\u201d \u3000\u3000\u8fd9\u5207\u8bdd\u4ed6\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\u5df2\u5168\u65e0\u8f6c\u5708\u7684\u4f59\u5730\u3002 \u3000\u3000\u71d5\u4e03\u7a81\u7136\u7ad9\u8d77\u6765\xb7\u5927\u58f0\u9053\u201c\u597d\uff0c\u4f60\u5360\u6211\u4e5f\u966a\u4f60\u53bb\u3002\u201d \u3000\u3000\u5e72\u52a8\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\xb7\u9053\u201c\u597d\u4f60\u4e5f\u5de6\u80fd\u53bb\u7684\u90fd\u53bb\u5c31\u8ba9\u4e0d\u80fd\u5de6\u7684\u7559\u5728\u8fd9\u91cc\u7b49\u522b\u4eba\u7c73\u5bb0\u5272 \u5427\u3002\u201d \u3000\u3000\u71d5\u4e03\u8bf4\u4e0d\u51fa\u8bdd\u7c73\u5382\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4ec6\u9053\u201c\u4f60\u7a76\u7adf\u8981\u6211\u4eec\u600e\u9ebd\u505a\uff1f\u4e3a\u4ec0\u9ebd\u4e0d\u4e7e\u8106\u8bf4\u6bd4\u6765\uff1f\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u6211\u4e2a\u4eba\u53bb\u4f60\u6790\u5e26\u6797\u592a\u5e73\u5230\u5c71\u4e0b\u53bb\u7b49\u6211\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u7136\u5f8c\u4f60\u4eec\u60f3\u6cd5\u5343\u53bb\u51c6\u5907\u8f86\u9a6c\u8f66\xb7\u65e0\u8bba\u53bb\u50a8\u53bb\u62a2\u90fd\u5b9a\u8981\u5e76\u5230\u201c \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7136\u5f8c\u4f60\u4eec\u5c31\u5750\u5728\u9a6c\u8f66\u91cc\u7b49\u592a\u9633\u4e0b\u5c71\u5f8c\xb7\u6211\u82d4\u8fd8\u6ca1 \u3000\u3000\u738b\u52a8\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5df2\u6709\u4e9b\u51c4\u51c9\u9053\u201c\u5929\u5730\u4e4b\u5927\u54ea\u91cc\u4f60\u4eec\u4e0d\u80fd \u3000\u3000\u90ed\u5927\u8def\u4e5f\u60e7\u6162\u7684\u70b9\u4e86\u70b9\u5934\xb7\u9053\u201c\u597d\u597d\u4e3b\u610f\u8fd9\u79cd\u4e3b\u610f\u771f\u4e8f\u4f60\u600e\u9ebd\u60f3\u5f97\u51fa\u6765\u7684\u201d \u3000\u3000\u738b\u52a8\u9053\uff0c\u201c\u8fd9\u867d\u7136\u4e0d\u80fd\u7b97\u662f\u597d\u4e3b\u610f\uff0c\u5374\u662f\u552f\u4e00\u7684\u4e3b\u6020\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f88\u597d\uff0c\u4f60\u4e3a\u4e86\u6797\u592a\u5e73\u53bb\u62fc\u547d\uff0c\u5374\u8ba9\u6211\u4eec\u8c61\u72d7\u6837\u592e\u5c3e\u5df4\u9003\u8d70\uff0c\u4f60\u662f\u4e2a\u597d\u670b \u53cb\xb7\u5374\u8981\u6211\u4eec\u505a\u755c\u7272\u3002\u201d\u738b\u52a8\u6c89\u80ce\u9053\u201c\u4f60\u96be\u9053\u8fd8\u6709\u522b\u7684\u4e3b\u610f\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u6211\u53ea\u6709\u4e2a\u4e3b\u610f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8981\u6d3b\uff0c\u6211\u8ba2\u5f00\u5f00\u5fc3\u5fc3\u7684\u6d3b\u5728\u8d77\u8981\u6b7b\uff0c\u6211\u6751\u4e5f\u8981\u75db\u75db\u5feb\u4f19\u7684\u6b7b\u5728\u8d75\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u5c31\u662f\u90ed\u5927\u8def\u65e2\u4e0d\u662f\u4e8e\u52a8\u4e5f\u4e2d\u662f\u71d5\u4e03\u3002 \u3000\u3000\u4ed6\u4e5f\u8bb8\u6ca1\u6709\u738b\u52a8\u9547\u5b9a\u51b7\u9759\u4e5f\u8bb8\u6ca1\u6709\u71d5\u4e03\u7684\u9492\u667a\u806a\u660e\u3002 \u3000\u3000\u4f46\u8fd9\u4eba\u52a0\u771f\u4ed6\u5988\u7684\u75db\u5feb\xb7\u771f\u4ed6\u5988\u7684\u6709\u79cd\u3002 \u3000\u3000\u56db \u3000\u3000\u98ce\u6b27\u8fc7\u7684\u65f6\u5019\u6b7b\u7070\u8272\u7684\u51b7\u96fe\u521a\u521a\u81ea\u8352\u5bb6\u95f4\u5347\u8d77\u3002 \u3000\u3000\u9b3c\u706b\u5df2\u6d88\u5931\u5728\u96fe\u91cc\u3002 \u3000\u3000\u8c01\u8bf4\u8fd9\u4e16\u4e0a\u6ca1\u6709\u9b3c\uff1f\u8c01\u8bf4\u7684\uff1f\u6b64\u523b\u5728\u8fd9\u96fe\u4e2d\u98d8\u8361\u7684\u5c82\u975e\u6b63\u662f\u4e2a\u8fde\u5730\u72f1\u90fd\u62d2\u7edd\u6536\u7559\u7684\u6e38 \u9b42\uff1f \u3000\u3000\u8c01\u4e5f\u770b\u4e0d\u6e05\u4ed6\u7684\u8138\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u8138\u8272\u662f\u6b7b\u7070\u8272\u7684\u4f3c\u5df2\u548c\u8fd9\u51c4\u8ff7\u7684\u51b7\u96fe\u878d\u4e3a\u4e00\u4f53\u9f3b\u5b50\u5df2\u878d\u5165\u96fe\u91cc\u5634\u4e5f\u878d\u4eba\u96fe\u91cc\u3002 \u3000\u3000\u53ea\u5269\u4e0b\u90a3\u53cc\u9b3c\u706b\u822c\u7684\u773c\u775b\u3002 \u3000\u3000\u773c\u775b\u91cc\u6ca1\u6709\u5149\u2018\u4e5f\u5206\u4e0d\u51fa\u9ed1\u767d\u4f46\u5374\u5145\u6ee1\u5382\u6076\u6bd2\u4e4b\u610f\u4eff\u4f5b\u6b63\u5728\u6cae\u4e16\u4e0a\u6240\u6709\u7684\u4e8b\u3001\u6240\u6709\u7684 \u4eba\u3002 \u3000\u3000\u65e0\u8bba\u8fd9\u53cc\u773c\u775b\u770b\u5230\u4ec0\u9ebd\u5730\u65b9\uff0c\u90a3\u5730\u65b9\u7acb\u523b\u4f1a\u6cbe\u4e0a\u4e0d\u6837\u7684\u7fc5\u8fd0\uff0c \u3000\u3000\u73b0\u5728\uff0c\u8fd9\u53cc\u773c\u775b\u6b63\u5728\u9992\u6162\u7684\u73af\u987e\u56db\u65b9\u6bcf\u5ea7\u8352\u5bb6\u6bcf\u7247\u79ef\u7f55\u4ed6\u90fd\u7edd\u4e0d\u80af\u9519\u8fc7\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u773c\u775b\u91cc\u624d\u9732\u51fa\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u8c61\u4e0d\u51fa\u8fd9\u79cd\u7b11\u610f\u6709\u591a\u9ebd\u6076\u6bd2\u3001\u591a\u9ebd\u53ef\u6015\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u8ff7\u96fe\u91cc\u53c8\u54cd\u8d77\u4e86\u4e00\u9635\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4e0d\u662f\u94f6\u94c3\u662f\u6444\u9b42\u7684\u94c3\u58f0\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5e7d\u7075\u822c\u51fa\u73b0\u5728\u8ff7\u96fe\u91cc\uff0c\u5e26\u7b11\u9053\u201c\u90fd\u51c6\u5907\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u8fd9\u6e38\u9b42\u6162\u63e1\u7684\u70b9\u4e86\u70b9\u5934\u9053\u201c\u9664\u975e\u4eba\u4e0d\u6765\u6765\u4e86\u5c31\u4f11\u60f3\u6d3b\u56de\u7ea2\u5a18\u5b50\u670d\u88ab\u6d41\u52a8\u9053\u201c\u4f60\u60f3\u4ed6\u4f1a\u4e0d \u4f1a\u6765\uff1f\u201d \u3000\u3000\u8fd9\u6e38\u9b42\u9053\uff0c\u201c\u4f60\u8bf4\u5462\u201d\u7ea2\u5a18\u5b50\u7728\u773c\uff0c\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u6211\u8bf4\uff1f\u201d\u6e38\u9b42\u9053\u201c\u4f60\u6bd4\u6211\u201c\u4e86\u89e3 \u4ed6\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u76c8\u76c8\u8d70\u8fc7\u6765\uff0c\u7528\u773c\u8272\u8198\u4ed6\u9053\u201c\u4f60\u73b0\u5728\u8fd8\u5403\u918b\uff1f\u201d \u3000\u3000\u6e38\u9b42\u9053\u201c\u54fc\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u771f\u7684\u5bf9\u4ed6\u6709\u610f\u601d\uff1f\u201d \u3000\u3000\u6e38\u9b42\u76ee\u4e2d\u7684\u6076\u6bd2\u4e4b\u610f\u66f4\u6df1\u9053\u201c\u4ed6\u5728\u7684\u65f6\u5019\xb7\u4f60\u4ece\u6765\u6ca1\u6709\u966a\u8fc7\u6211\u5929\u201c\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u96be\u9053\u5df4 \u5fd8\u662f\u8c01\u53eb\u6211\u90a3\u9ebd\u505a\u7684\uff1f\u201d \u3000\u3000\u6e38\u9b42\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u51b7\u7b11\u9053\u201c\u4f60\u4e3a\u4e86\u8981\u62c9\u62e2\u4ed6\uff0c\u53eb\u6211\u53bb\u8d54\u5979\u7761\u89c9\u73b0\u5728\u53cd\u6765\u602a\u6211\u4e86\u4f60\u6709\u826f\u5fc3\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6e38\u9b42\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53c8\u7b11\u4e86\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u5076\u5c14\u4e5f\u4f1a\u8bf4\u53e5\u8001\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u6e38\u9b42\u901a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u5728\u4f60\u9881\u524d\u8bf4\u7684\u53e5\u65ec\u90fd\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u6e38\u9b42\u9053\u201c\u6211\u7740\u4e0d\u53eb\u4f60\u53bb\u966a\u4ed6\u7761\u89c9\uff0c\u4f60\u96be\u9053\u4e0d\u4f1a\u53bb\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u8fd8\u662f\u6837\u4f1a\u4f17\u3002\u201d\u6e38\u9b42\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5a5a\u7136\u9053\u201c\u56e0\u4e3a\u6211\u559c\u6b22\u966a\u7537\u4eba\u7761\u89c9\u3002\u201d \u3000\u3000\u6e38\u9b42\u54ac\u7259\u9053\u201c\u9677\u4ec0\u9ebd\u6837\u7684\u7537\u4eba\u7761\u89c9\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u201c\u9664\u4e86\u4f60\u4e4b\u5916\u4ec0\u9ebd\u6837\u7684\u7537\u4eba\u90fd\u559c\u6b22\u3002\u201d \u3000\u3000\u6e38\u9b42\u76ee\u4e2d\u7684\u6076\u6bd2\u4e4b\u8272\u5df2\u53d8\u4e3a\u75db\u82e6\u4f46\u773c\u775b\u5374\u53cd\u800c\u4eae\u4e86\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u770b\u4ed6\u7684\u8ddf\u775b\u9053\u201c\u4f60\u7684\u8bdd\u95ee\u5b8c\u4e86\u5417\uff1f\u201d \u3000\u3000\u6e38\u9b42\u7a81\u7136\u4e00\u628a\u63ea\u4f73\u5979\u7684\u5934\u53d1\uff0c\u53cd\u624b\u91cd\u91cd\u636e\u5979\u7684\u80a1\uff0c\u560e\u58f0\u9053\u201c\u4f60\u8fd9\u9647\u4eba\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u65e2\u4e0d\u60ca\u60e7\u4e5f\u4e0d\u751f\u6c14\xb7\u53cd\u800c\u7b11\u5f97\u66f4\u751c\xb7\u9053\u201c\u6211\u672c\u5c31\u662f\u4e2a\u8d31\u4eba\u4f46\u4f60\u5374\u6bd4\u6211\u66f4\u8df5\u3002\u201d \u3000\u3000\u6e38\u9b42\u53c8\u91cd\u6398\u5979\u7684\u8138\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u8fd8\u5728\u7b11\uff0c\u9053\u201c\u4f60\u4e0d\u4f46\u559c\u6b22\u6211\u53bb\u966a\u522b\u7684\u7537\u4eba\u7761\u89c9\uff0c\u8fd8\u559c\u6b22\u95ee\u6211\uff0c\u5929\u5929\u95ee\u6211\u8fd9\u4e9b\u8bdd\u4f60 \u5df2\u4e0d\u77e5\u95ee\u8fc7\u591a\u5c11\u6b21\u4e86\u3002\u201d \u3000\u3000\u5979\u4e0d\u8ba9\u6e38\u9b42\u7684\uff0c\u63a5\u53c8\u9053\u201c\u56e0\u4e3a\u4f60\u559c\u6b22\u8fd9\u4e9b\u8bdd\xb7\u559c\u6b22\u88ab\u6211\u6298\u78e8\xb7\u53ea\u6709\u6211\u5728\u6298\u78e8\u4f60\u7684\u65f6 \u5019\xb7\u4f60\u624d\u662f\u4e2a\u4eba\u4f60\u624d\u4f1a\u5feb\u6d3b\u3002\u201d \u3000\u3000\u6e38\u9b42\u5589\u5499\u4f4e\u5636\u58f0\xb7\u7528\u529b\u5c06\u5979\u62c9\u4e86\u8fc7\u6765\u3002\u7ea2\u5a18\u5b50\u5403\u5403\u7684\u7b11\u9053\u201c\u4f60\u662f\u4e0d\u662f\u53c8\u60f3\u201d\u2500\u3002\u201d \u3000\u3000\u7a81\u542c\u4eba\u51b7\u51b7\u5730\u9053\u201c\u73b0\u5728\u4e0d\u662f\u4f60\u79c6\u6253\u60c5\u9a82\u4fcf\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u58f0\u97f3\u51b7\u5f97\u8c61\u51b0\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u58f0\u97f3\u672c\u5c31\u662f\u4ece\u79ef\u96ea\u4e0b\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u539f\u6765\u4ed6\u5df2\u7ecf\u5230\u79ef\u96ea\u91cc\u9762\u53bb\u4e86\u3002\u201d \u3000\u3000\u5f20\u8138\u4ea4\u7136\u4ece\u5730\u4e0a\u7684\u79ef\u7f55\u4e2d\u9732\u51fa\u6765\u3002 \u3000\u3000\u5f20\u6bd4\u6b7b\u4eba\u8fd8\u53ef\u6015\u7684\u8138\u3002 \u3000\u3000\u7ea2\u5a18\u4e8e\u9053\u201c\u535c\u9762\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u5f88\u51c9\u5feb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u505a\u6b62\u6bd4\u4f60\u90a3\u91cc\u66f4\u51c9\u5feb\u7684\u5730\u65b9\uff0c\u7684\u786e\u518d\u4e5f\u627e\u4e0d\u5230 \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u94bb\u8fdb\u6765\u966a\u6211\u7761\u89c9\uff1f\u201d\u7ea2\u5a18\u4e8e\u9053\u201c\u53ea\u8981\u4f60\u6709\u8010\u5fc3\u5728\u4e0b\u9762\u7b49\u6211\u8fdf\u4e2d \u603b\u4f1a\u8fdb\u53bb\u7684\u3002\u201d \u3000\u3000\u6e38\u9b42\u51b7\u7b11\u901a\u201c\u53ea\u53ef\u60dc\u4ed6\u5bf9\u4f60\u6ca1\u80c3\u53e3\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u670d\u770b\u5929\u7a81\u7136\u9053\u201c\u65f6\u5019\u5df2\u4e0d\u65e9\xb7\u4f60\u8fd8\u662f\u5feb\u53bb\u6b7b\u5427\u3002\u201d \u3000\u3000\u6e38\u9b42\u9053\u201c\u4f60\u60f3\u4ed6\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d\u7ea2\u5a18\u5b50\u9053\u201c\u4f1a\u7684\u3002\u201d \u3000\u3000\u6e38\u9b42\u62a2\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u56e0\u4e3a\u4ed6\u9664\u4e86\u4f60\u8ba2\u4e4b\u5916\uff0c\u5bf9\u522b\u7684\u670b\u53cb\u90fd\u4e0d\u9519\u3002\u201d \u3000\u3000\u6e38\u9b42\u4e5f\u4ef0\u5934\u770b\u4e86\u770b\u5929\u8272\u3002 \u3000\u3000\u66d9\u8272\u5df2\u81fc\u3002 \u3000\u3000\u4e16\u4e0a\u7684\u5b64\u9b42\u91ce\u9b3c\u90fd\u5df4\u5230\u5382\u5e94\u8be5\u56de\u53bb\u7684\u65f6\u5019\u3002 \u3000\u3000\u6e38\u9b42\u9053\u201c\u6211\u8981\u53bb\u6b7b\u5382\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u8d76\u5feb\u53bb\u6b7b\u5427\u3002\u201d \u3000\u3000\u6e38\u9b42\u6162\u9992\u7684\u8d70\u8fc7\u53bb\u8d70\u5230\u65c1\u8fb9\u5ea7\u8352\u575f\u524d\u65e5\u6000\u4e2d\u53d6\u51fa\u4e2a\u74f7\u74f6\u653e\u5728\u575f\u5934\u4e0a\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u7684\u4eba\u7a81\u7136\u6d88\u5931\u5728\u575f\u5893\u91cc\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u95ed\u95fb\u9053\u201c\u4ed6\u82e5\u6c38\u8fdc\u5728\u91cc\u9762\u4e0d\u51fa\u6765\u90a3\u6709\u591a\u597d\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u6709\u4ec0\u9ebd\u597d\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5782\u9996\u770b\u4ed6\u773c\u77aa\u6c34\u7433\u6dcb\u7684\u67d4\u58f0\u9053\u201c\u53ea\u5269\u4e0b\u6211\u4eec\u4e24\u4e2a\u4eba\u8fd8\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u51b7\u51b7\u9053\u201c\u90a3\u4e5f\u5f97\u7b49\u5929\u4e0b\u7684\u5973\u4eba\u90fd\u6b7b\u5149\u4e2b\u518d\u8bf4\u3002\u201d\u7ea2\u5a18\u4e8e\u51b2\u8fc7\u53bb\u53e3\u53e3\u553e\u5728\u4ed6\u80a1\u4e0a\u6839 \u6839\u9053\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9634\u4fa7\u4fa7\u4e00\u7b11\xb7\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\u8fd9\u5f20\u8138\u5df3\u9690\u6ca1\u5728\u79ef\u96ea\u91cc\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u53d1\u4e86\u534a\u5929\u6014\u597d\u50cf\u7a81\u7136\u6709\u4e86\u5f88\u591a\u5fc3\u4e8b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5979\u8eab\u5f62\u7a81\u53c8\u63a0\u8d77\u3002 \u3000\u3000\u5979\u7acb\u523b\u5c31\u6d88\u5931\u5728\u96fe\u91cc\u3002 \u3000\u3000\u98ce\u5439\u8fc7\u7684\u65f6\u5019\u6b7b\u7070\u8272\u7684\u8ff7\u96fe\u8ff7\u6f2b\u4e86\u5927\u5730\u3002 \u3000\u3000\u5929\u4e5f\u662f\u6b7b\u7070\u8272\u7684\u3002 \u3000\u3000\u8352\u5bb6\u51b7\u96ea\uff0c\u6ca1\u6709\u4eba\uff0c\u751a\u81f3\u8fde\u9b3c\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ea\u5269\u4e0b\u53ea\u98ce\u7b2c\u6b63\u6162\u6162\u7684\u843d\u4e0b\u3002 \u3000\u3000\u4e0d\u662f\u98ce\u7b2c\u662f\u50ac\u547d\u9b3c\u7684\u7b26\u89c8\u3002 \u3000\u3000\u98ce\u7b54\u51fa\u843d\u4e0b\u3002 \u3000\u3000\u82cd\u7a84\u7247\u7070\u767d\xb7\u4ec0\u9ebd\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u738b\u52a8\u5728\u8def\u4e0a\u6162\u6162\u7684\u8d70\u8138\u4e0a\u8fd8\u662f\u8fde\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u5c31\u7b97\u5fc3\u91cc\u6709\u6050\u60e7\u4e5f\u7edd\u4e0d\u4f1a\u843d\u5728\u80a1\u4e0a\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u82e5\u53d7\u8fc7\u6c60\u6240\u53d7\u7684\u75db\u82e6\u548c\u6298\u78e8\u90fd\u5df3\u8be5\u5b66\u4f1a\u5c06\u60c5\u611f\u9690\u85cf\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u5404\u79cd\u60c5\u611f\u90fd\u9690\u85cf\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u4eec\u60c5\u611f\u5374\u8c61\u9152\u6837\u3002 \u3000\u3000\u4f11\u85cf\u5f97\u8d8a\u6df1\u85cf\u5f97\u8d8a\u4e45\u53cd\u9163\u8d8a\u6d53\u8d8a\u70c8\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u670b\u53cb\u4eec\u5f53\u7136\u6ca1\u6709\u6765\u3002 \u3000\u3000\u662f\u4ed6\u4eec\u80cc\u5f03\u4e86\u4ed6\u8fd8\u662f\u4ed6\u8bf4\u670d\u5382\u4ed6\u8d22\uff1f \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8c01\u4e5f\u8bbe\u6cd5\u4e25\u4ece\u4ed6\u8138\u535c\u7684\u8868\u60c5\u770b\u51fa\u6765\u3002 \u3000\u3000\u4f46\u5927\u5bb6\u90fd\u77e5\u9053\xb7\u5929\u535c\u65e0\u4e0d\u6563\u7684\u7329\u5e2d\uff0c\u65e0\u8bba\u591a\u597d\u7684\u670b\u53cb\u8fdf\u65e9\u90fd\u6709\u5206\u624b\u7684\u65f6\u5019\u3002 \u3000\u3000\u4eba\u751f\u805a\u5408\u672c\u65e0\u5e38\u662f\u805a\u4e5f\u597d\uff0c\u662f\u6563\u4e5f\u597d\uff0c\u53c8\u4f55\u5fc5\u592a\u8ba4\u771f\uff1f \u3000\u3000\u5929\u8272\u7530\u8106\uff0c\u4f46\u603b\u7b97\u5df2\u6709\u4e86\u5149\u4eae\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u867d\u4fb5\u4f46\u603b\u7b97\u5df2\u8d70\u4e86\u5730\u5934\u3002 \u3000\u3000\u4eba\u751f\u672c\u5982\u6b64\u5f88\u591a\u4e8b\u90fd\u5982\u6b64\uff0c\u4f60\u53c8\u4f55\u5fc5\u592a\u5306\u5fd9\uff1f \u3000\u3000\u98ce\u8fd8\u662f\u5f88\u51b7\u51b7\u5f97\u8c61\u5200\u5200\u822c\u522e\u8fc7\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u7a7f\u8fc7\u8352\u575f\uff0c\u9ed8\u6570\u5757\u5757\u5893\u7891\u3002 \u3000\u3000\u5893\u7891\u6709\u7684\u5df2\u503e\u5012\u6709\u7684\u5df2\u88ab\u98ce\u96ea\u4fb5\u8680\u8fde\u5b57\u8ff9\u90fd\u5206\u8fa8\u4e0d\u6e05\u3002 \u3000\u3000\u575f\u5893\u91cc\u7684\u4eba\u662f\u8c01\uff1f\u5df2\u4e0d\u518d\u6709\u4eba\u5173\u5fc3\u5382\u3002 \u3000\u3000\u4ed6\u4eec\u6d3b\u7684\u65f6\u5019\u5c82\u4e0d\u4e5f\u6709\u4ed6\u4eec\u7684\u5149\u8363\u548c\u7f9e\u8fb1\u3001\u5feb\u4e50\u548c\u60b2\u4f24\uff1f \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u8ba2\u5df2\u65e0\u6240\u6709\u3002 \u3000\u3000\u90a3\u9ebd\u4f60\u53c8\u4f55\u5fc5\u5c06\u751f\u6b7b\u8363\u8fb1\u65f6\u65f6\u523b\u523b\u7684\u653e\u5728\u5fc3\u535c\uff1f \u3000\u3000\u738b\u52a8\u8f7b\u8f7b\u7684\u53f9\u606f\u4e86\u58f0\u7a81\u7136\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u542c\u5230\u7ea2\u5a18\u5b50\u7684\u7b11\u58f0\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u6b63\u94f6\u94c3\u822c\u7b11\u9053\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4f1a\u6765\u7684\xb7\u4f60\u679c\u7136\u6765\u4e86\u3002\u201d \u3000\u3000\u5de5\u52a8\u9053\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5df2\u770b\u89c1\u5979\uff0c\u7ad9\u5728\u79ef\u96ea\u7684\u67af\u6811\u4e0b\u8fd8\u662f\u7a7f\u90a3\u8eab\u9c9c\u7ea2\u7684\u8863\u88f3\u4eff\u4f5b\u8fd8\u6bbf\u4ed6\u7b2c\u6b27\u89c1\u5230\u5979\u65f6\u4e00\u6837\u3002 \u3000\u3000\u4f46\u901d\u53bb\u7684\u65f6\u5149\uff0c\u5df2\u7ecf\u4e0d\u518d\u6765\u901d\u53bb\u7684\u6b22\u4e50\u548c\u60b2\u4f24\xb7\u4e5f\u5df2\u5c06\u6de1\u5fd8\u3002 \u3000\u3000\u5c31\u7b97\u8fd8\u672a\u9057\u5fd8\uff0c\u8fdf\u65e9\u4e5f\u5fc5\u5c06\u6de1\u5fd8\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u4e5f\u7ad9\u5728\u90a3\u91cc\u770b\u4ed6\u76ee\u5149\u4e2d\u4e5f\u4e0d\u77e5\u662f\u55c5\u662f\u6028\uff1f\u662f\u7231\u662f\u6839\uff1f \u3000\u3000\u5979\u662f\u7231\u4e5f\u597d\xb7\u662f\u6839\u4e5f\u597d\uff0c\u90fd\u5df2\u65e0\u59a8\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7ec8\u4e8e\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u771f\u662f\u4e3a\u6797\u592a\u5e73\u62ff\u89e3\u836f\u6765\u7684\uff1f\u201d\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u4e8e\u54ac\u5634\u5507\u9053\u201c\u4e3a\u4e86\u6211\xb7\u4f60\u5c31\u4e0d\u80af\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u80af\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u5f97\u5f88\u51c4\u51c9\xb7\u9053\u201c\u4f60\u5bf9\u522b\u7684\u670b\u53cb\u4e3a\u4ec0\u9ebd\u603b\u6bd4\u5bf9\u6211\u597d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4f60\u4e0d\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff1f\u4f60\u96be\u9053\u5fd8\u4e86\u6211\u5546\u4ee5\u524d\u5728\u8d77\u65f6\u6709\u591a\u9ebd\u5f00\u5fc3\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5e97\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6447\u6447\u5934\u9053\u201c\u65e0\u8bba\u4f60\u5634\u4e0a\u8bf4\u5f97\u591a\u786c\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u7edd\u4e0d\u4f1a\u5fd8\u7684\u3002\u201d \u3000\u3000\u5979\u773c\u6ce2\u5982\u96fe\u5e7d\u5e7d\u7684\u63a5\u9053\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\xb7\u6709\u4e00\u5929\u6211\u4eec\u9896\u5728\u534e\u5c71\u4e4b\u5f3a\u7528\u767d\u4e91\u505a\u6211\u4ecd\u7684 \u88ab\xb7\u5927\u5730\u505a\u6211\u5546\u7684\u5e8a\u5929\u5730\u95f4\u4f24\u62c2\u53ea\u5269\u4e0b\u6211\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5979\u58f0\u97f3\u66f4\u4f4e\u8ff7\xb7\u66f4\u8f7b\u67d4\u53c8\u9053\u201c\u8fd8\u6709\u6b21\u6211\u4eec\u9500\u5728\u65e0\u8fb9\u65e0\u9645\u7684\u5927\u6c99\u6f20\u4e0a\uff0c\u6570\u5929\u4e0a\u7684\u661f\u661f\u76f4\u5230 \u6211\u4eec\u4e24\u4e2a\u4eba\u90fd\u5df2\u88ab\u57cb\u5728\u6284\u91cc\u201d\u2026\u8fd9\u4e9b\u4e8b\u4f60\u80fd\u5fd8\u5f97\u4e86\u5417\u201d \u3000\u3000\u738b\u52a8\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u7684\u786e\u662f\u8c01\u4e5f\u5fd8\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u4ed6\u771f\u7684\u80fd\u5fd8\u8bb0\uff1f \u3000\u3000\u9762\u5bf9\u4ed6\u751f\u5e73\u7b2c\u4e2a\u604b\u4eba\u4ed6\u7684\u5fc3\u771f\u80fd\u5982\u4ed6\u7684\u80a1\u6837\u51b7\u9759\uff1f \u3000\u3000\u7ea2\u5a18\u5b50\u51dd\u89c6\u4ed6\xb7\u76ee\u4e2d\u5df2\u6709\u6cea\u5149\xb7\u9053\u201c\u8fd9\u4e9b\u4e8b\u6211\u662f\u6c38\u8fdc\u4e5f\u5fd8\u4e0d\u5382\u7684\uff0c\u6240\u4ee5\u6211\u624d\u6839\u4f60\u6068\u4f60\u8d70 \u7684\u65f6\u5019\u8fde\u8bf4\u90fd\u4e0d\u8bf4\u58f0\u6068\u5f97\u60f3\u8981\u4f60\u6b7b\u53ef\u662f\u2026\u3002\u201c \u3000\u3000\u5979\u5782\u4e0b\u5934\u9053\u201c\u53ea\u8981\u4f60\u80af\u56de\u5fc3\u8f6c\u610f\u53ea\u8981\u4f60\u8096\u8bf4\u53e5\u8bdd\uff0c\u6211\u73b0\u5728\u5c31\u8ddf\u4f60\u8d70\u65e0\u8bba\u5929\u6daf\u6d77\u89d2\u6211\u90fd\u8ddf \u4f60\u8d70\u3002\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u5927\u58f0\u9053\u201c\u6211\u56db\u91cc\u90fd\u4e0d\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u58f0\u97f3\u90a3\u9ebd\u5927\u4f3c\u4e4e\u60f3\u5c06\u81ea\u5df1\u4ece\u68a6\u4e2d\u60ca\u9192\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u54ac\u4e86\u54ac\u7530\u5507\u9053\u201c\u4f60\u7530\u91cc\u90fd\u4e0d\u53bb\xb7\u53c8\u4e3a\u4ec0\u9ebd\u8981\u6765\u5462\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u51b7\u51b7\u9053\u201c\u9664\u6b64\u4e4b\u5916\u5c31\u6ca1\u6709\u522b\u7684\u539f\u56e0\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u4e2d\u60f3\u6765\u770b\u770b\u6211\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7684\u8138\u8272\u7a81\u7136\u53d1\u9752\xb7\u9752\u5f97\u5c31\u8c61\u662f\u4e00\u53ea\u9752\u874e\u5b50\u3002 \u3000\u3000\u5979\u76ee\u4e2d\u7684\u67d4\u60c5\u871c\u610f\u4e5f\u5df2\u4e0d\u89c1\u7528\u529b\u8dfa\u811a\u9053\u201c\u597d\xb7\u89e3\u836f\u5c31\u5728\u5f8c\u9762\u4f60\u81ea\u5df1\u9ebd\u62ff\u5427\u3002\u201d \u3000\u3000\u738b\u52a8\u56de\u8fc7\u5934\u5c31\u770b\u5230\u575f\u5934\u4e0a\u90a3\u74f7\u74f6\u3002\u7ea2\u5a18\u5b50\u9053\u9f99\u6b21\u6211\u4eec\u5c06\u89e3\u836f\u7ed8\u4f60\u53ea\u56e0\u4e3a\u6211\u4eec\u8fd8\u6700\u62ff\u4f60\u5f53 \u4f5c\u670b\u53cb\u4f60\u62ff\u4e86\u4e4b\u5f8c\u6700\u597d\u8d76\u5feb\u8d70\u3002\u201d \u3000\u3000\u738b\u52a8\u8138\u4e0a\u8fd8\u662f\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u65e0\u8bba\u5979\u8bf4\u4ec0\u9ebd\u4ed6\u8fde\u4e2a\u5b87\u90fd\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u4eec\u662f\u7edd\u4e0d\u4f1a\u5c06\u89e3\u836f\u5c31\u8fd9\u6837\u5bb9\u5bb9\u6613\u6613\u7684\u7ed9\u4ed6\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u8d70\u5382\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u975e\u62ff\u5230\u8fd9\u74f6\u89e3\u836f\u4e0d\u53ef \u3000\u3000\u8fd9\u74f6\u89e3\u836f\u82e5\u662f\u5728\u6c34\u91cc\u4ed6\u5c31\u773a\u5382\u6c34\u91cc\u53bb\u8fd9\u74f6\u89e3\u836f\u82e5\u662f\u5728\u70c8\u706b\u91cc\u4ed6\u5c31\u8df3\u8fdb\u706b\u91cc\u53bb\u3002 \u3000\u3000\u79ef\u96ea\u51b7\u800c\u67d4\u8f6f\u3002 \u3000\u3000\u738b\u52a8\u53ea\u8d70\u4e86\u516d\u4e03\u6b65\u5c31\u5df2\u53ef\u4f38\u624b\u62ff\u5230\u89e3\u836f\u3002 \u3000\u3000\u4ed6\u4f38\u51fa\u624b\u3002 \u3000\u3000\u74f7\u74f6\u5f88\u51b7\u51b7\u5f97\u8c61\u6b7b\u4eba\u7684\u624b\u3002 \u3000\u3000\u4ed6\u62ff\u8d77\u4e86\u74f7\u74f6\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6bd4\u74f7\u74f6\u8fd8\u51b7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u611f\u89c9\u5230\u6b7b\u7684\u6c14\u606f\u2606 \u3000\u3000\u53cc\u624b\u7a81\u7136\u4ece\u575f\u5893\u91cc\u4f38\u51fa\u6765\u70b9\u4e2d\u4e86\u4ed6\u6f06\u76d6\u4e0a\u7684\u201c\u73af\u8df3\u7a74\u3002 \u3000\u3000\u53e6\u53cc\u624b\u540c\u65f6\u4ece\u79ef\u96ea\u535c\u4f38\u51fa\u6765\xb7\u6325\u624b\u5c04\u51fa\u4e24\u9897\u5bd2\u5c3e\u5c04\u4eba\u4e01\u4ed6\u7684\u8db3\u8e29\u3002 \u3000\u3000\u4ed6\u8dea\u4e86\u4e0b\u53bb\u8dea\u5728\u575f\u5893\u524d\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u624d\u770b\u5230\u575f\u5893\u5c38\u5df2\u9732\u51fa\u6d1e\u7a74\u3002 \u3000\u3000\u8fd9\u575f\u5893\u539f\u6765\u662f\u5047\u7684\uff0c\u662f\u7a7a\u7684\u3002 \u3000\u3000\u7ea2\u5a18\u4e8e\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u53c8\u54cd\u8d77\u751c\u7b11\u9053\u201c\u4f60\u73b0\u5728\u771f\u7684\u54ea\u91cc\u90fd\u4e0d\u5fc5\u53bb\u201d \u7b2c\u5341\u7ae0>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u7ae0 \u3000\u3000\u738b\u52a8\u8dea\u5728\u575f\u5893\u524d\u80a1\u4e0a\u4e0d\u662f\u5168\u65e0\u8868\u60c5\uff0c\u4f46\u8138\u8272\u5374\u82cd\u767d\u5f97\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u5f88\u4e86\u89e3\u8fd9\u4e9b\u4eba\u3002\u72e0\u4e86\u89e3\u8fd9\u4e9b\u4eba\u7684\u624b\u6bb5\u3002 \u3000\u3000\u4ed6\u5728\u7b49\u7b49\u4ed6\u4eec\u4f7f\u51fa\u624b\u6bb5\u6765\u3002 \u3000\u3000\u575f\u5893\u4e2d\u7ec8\u6269\u53d1\u51fa\u4e86\u58f0\u97f3\u201c\u4f60\u8f93\u5382\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u662f\u50ac\u547d\u7b26\u7684\u58f0\u97f3\u3002\u50ac\u547d\u7b26\u65e0\u8bba\u5728\u4ec0\u9ebd\u5730\u65b9\u8bf4\u8bdd\xb7\u90fd\u8c61\u662f\u4ece\u575f\u5893\u91cc\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u201c\u6211\u8f93\u6b79\u3002\u201d \u3000\u3000\u4ed6\u53ea\u6709\u8ba4\u8f93\u3002 \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u8fd9\u6b21\u4f60\u5df4\u6ca1\u67ef\u7ffb\u672c\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u6211\u6ca1\u6709\u3002\u201d \u3000\u3000\u50ac\u4fde\u7b26\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8f93\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u53ea\u6709\u201c\u6761\u547d\u53ef\u8f93\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u8fd8\u6709\u522b\u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u8fd8\u8981\u4ed8\u9ebd\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u4ece\u68fa\u6750\u91cc\u4f38\u51fa\u624b\u6765\uff0c\u8981\u7684\u662f\u4ec0\u9ebd\uff1f\u2606 \u3000\u3000\u5de5\u52a8\u9053\u201c\u8981\u94b1\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4e0d\u9519\uff0c\u662f\u8981\u94b1\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u82e5\u662f\u8981\u94b1\u4f60\u5c31\u627e\u9519\u4e86\u4eba\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u6211\u4ece\u672a\u627e\u9519\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8981\u94b1\u7684\u672c\u8be5\u662f\u6211\u516c\u5f20\u91cc\u7684\u94b1\u672c\u8be5\u4e5f\u6709\u4efd\u3002\u201d \u3000\u3000\u50ac\u547d\u9053\uff0c\u201c\u4f60\u5f53\u7136\u6709\u4efd\u4f46\u5374\u4e0d\u8be5\u5c06\u56db\u4efd\u90fd\u72ec\u541e\u3002\u3002 \u3000\u3000\u738b\u52a8\u6ca1\u6709\u8bf4\u8bdd\xb7\u80a1\u4e0a\u7684\u8868\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u4f7f\u547d\u7b26\u9053\u201c\u90a3\u51e0\u5e74\u6211\u4eec\u7684\u6536\u5165\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u50ac\u547d\u9053\u201c\u662f\u4e0d\u662f\u53ea\u6709\u6211\u4eec\u4e94\u4e2a\u4eba\u77e5\u9053\u6211\u4eec\u7684\u6536\u5165\u7a76\u7adf\u6709\u591a \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u662f\u4e0d\u662f\u4e5f\u53ea\u6709\u6211\u4eec\u4e94\u4e2a\u4eba\u624d\u77e5\u9053\u6211\u4eec\u7a76\u7adf\u5b58\u4e0b\u4e86\u591a\u5c11\u3001\u5b58\u5728\u54ea\u91cc\uff1f\u201d\u738b\u52a8\u9053 \u201c\u662f\u3002\u201d\u50ac\u547d\u7b26\u9053\u201c\u6709\u6ca1\u6709\u7b2c\u516d\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u90a3\u7b14\u94b1\u65e0\u8bba\u6dee\u62ff\u53bb\u3002\u90fd\u8db3\u591f\u8212\u8212\u670d\u670d\u7684\u4eab\u53d7\u8f88 \u3000\u3000\u738b\u52a8\u9053\u201c\u5c31\u7b97\u6700\u6d6a\u8d39\u7684\u4eba\u4e5f\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f46\u7b49\u4f60\u8d70\u4e86\u5f8c\u6211\u4eec\u624d\u77e5\u9053\uff0c\u80fd\u4eab\u53d7\u90a3\u7b14\u94b1\u7684\u53ea\u6709\u4f60\u4e2a\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8ba4\u4e3a\u6211\u5df3\u5c06\u90a3\u7b14\u94b1\u5e26\u8d70\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\uff0c\u201c\u90a3\u7b14\u94b1\u5df1\u6587\u4e0d\u5269\xb7\u4f60\u8ba4\u4e3a\u662f\u8c01\u5e26\u8d70\u7684\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u957f\u957f\u65f6\u51fa\u53e3\u6c14\u9053\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u4f60\u4eec\u662f\u4e3a\u4ec0\u9ebd\u6765\u7684\u3002\u3002 \u3000\u3000\u50ac\u547d\u7b26\u51b7\u7b11\u9053\u201c\u6211\u4e2d\u5df2\u77e5\u9053\u4f60\u662f\u4e3a\u4ec0\u9ebd\u8d70\u7684\u4e86\uff0c\u90a3\u7b14\u94b1\u5df2\u8db3\u591f\u4ee4\u4efb\u4f55\u4eba\u51fa\u5356\u670b\u53cb\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u50ac\u547d\u7b26\u8bf4\u9053\u201c\u4f60\u8ba4\u4e3a\u6211\u4eec\u72e0\u53ef\u7b11\uff1f\u8ba4\u4e3a\u6211\u4eec\u662f\u7b28\u86cb\uff1f\u201d \u3000\u3000\u738b\u52a8\u201c\u6211\u624d\u662f\u7b28\u86cb\u65e0\u8bba\u8c01\u6709\u4e86\u90a3\u7b14\u94b1\u90fd\u4e0d\u4f1a\u8fc7\u6211\u8fd9\u79cd\u65e5\u5b50\u9664\u975e\u662f\u4e2a\u7b28\u86cb\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u8fc7\u7684\u662f\u4ec0\u9ebd\u65e5\u5b50\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7a77\u65e5\u5b50\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u7a77\u65e5\u5b50\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5ffd\u7136\u63a0\u8fc7\u6765\uff0c\u7f00\u94c3\u822c\u7b11\u9053\uff0c\u201c\u4f60\u6709\u591a\u7a77\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5f88\u7a77\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7728\u7728\u9650\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e2a\u4eba\u5728\u8fd9\u53bf\u57ce\u7684\u81f3\u5143\u9986\u91cc\uff0c\u4e00\u665a\u4e0a\u5c31\u690d\u4e86\u597d\u51e0\u4e07\u4e24\u94f6\u5b50\u8fd9\u4eba \u662f\u8c01\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u201c\u542c\u8bf4\u6709\u4e2a\u4eba\u5728\u5c71\u4e0b\u7684\u8a00\u8302\u6e90\u4e00\u4e2a\u6708\u5c31\u4e70\u4e86\u51e0\u767e\u4e24\u94f6\u5b50\u7684\u9152\u8fd9\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u8fd8\u6709\u4e2a\u4eba\u5bb6\u91cc\u6700\u8fd1\u521a\u6362\u4e86\u6279\u5bb6\u60e7\xb7\u8fde\u5f8c\u9662\u5c0f\u5c4b\u91cc\u7684\u6905\u5b50\u90fd\u662f\u6a80\u6728\u505a\u7684\u5458\u5c11\u4e5f \u503c\u4e03\u4e24\u94f6\u5b50\u8fd9\u4eba\u53c8\u662f\u8c01\u201d \u3000\u3000\u738b\u52a8\u9053\u4e0d\u80fd\u7b97\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u201c\u6211\u4eec\u5df2\u6253\u542c\u8fc7\uff0c\u8fd9\u91cc\u867d\u53eb\u5bcc\u8d35\u5c71\u5e86\u4f46\u4ece\u4e0a\u4e00\u4ee3\u5f00\u59cb\u9664\u4e86\u8fd9\u540d\u5b57\u5916\u5c31\u518d\u4e5f\u6ca1\u6709\u70b9 \u5bcc\u8d35\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u9519\u3002\u201d\u7ea2\u5a18\u5b50\u9053\u9f99\u9ebd\u4e9b\u5e74\u6765\u4f60\u4e5f\u6ca1\u6709\u518d\u51fa\u53bb\u505a\u8fc7\u751f\u610f\uff1f\u201d \u3000\u3000\u738b\u52a8\u8c08\u6de1\u9053\u201c\u4e2a\u4eba\u53ef\u4ee5\u5728\u5bb6\u91cc\u4eab\u798f\xb7\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u51fa\u53bb\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u94f6\u5b50\u662f\u7edd\u4e0d\u4f1a\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u4f46\u5374\u53ef\u4ee5\u4ece\u5730\u4e0b\u6316\u51fa\u6765\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5a7f\u7136\u9053\u201c\u60f3\u4e0d\u5230\u4f60\u627f\u8ba4\u5f97\u7b80\u5f88\u5feb\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e0d\u627f\u8ba4\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u4e0d\u884c\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e2\u7136\u4e0d\u884c\uff0c\u6211\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u627f\u8ba4\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u7b11\u5f97\u5f88\u52c9\u5f3a\uff0c\u53c8\u9053\u201c\u4f60\u4eec\u82e5\u8981\u8c03\u67e5\u4e00\u4e2a\u4eba\u7684\u5e95\u7ec6\u8fde\u4ed6\u7956\u5b97\u53c1\u4ee3\u90fd\u8981\u6316\u51fa\u6765\u82e5\u8981 \u4e2a\u4eba\u8bf4\u5b9e\u8bdd\u8fde\u54d1\u5df4\u90fd\u4e0d\u80fd\u4e0d\u5f00\u53e3\xb7\u8fd9\u70b9\u6211\u603b\u6bd4\u522b\u4eba\u77e5\u9053\u5f97\u6e05\u695a\u4e9b\u3002\u201d\u50ac\u547d\u7b26\u51b7\u51b7\u9053\u201c\u6240\u4ee5\u4f60 \u6839\u672c\u4e0d\u5fc5\u8d70\u7684\u201c \u3000\u3000\u738b\u52a8\u53f9\u9053\u201c\u53ea\u53ef\u60dc\u5f88\u591a\u4eba\u90fd\u5e38\u5e38\u4f1a\u505a\u4e0d\u8be5\u505a\u7684\u4e8b\u3002 \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u597d\xb7\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u5b9a\uff1f\u5230\u7530\u91cc\u53bb\uff1f\u201d\u50ac\u547d\u7b26\u9053\u201c\u53bb\u62ff\u56de\u6211\u4eec\u7684\u90a3\u53c1\u4efd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u597d\u4f60\u4eec\u53bb\u62ff\u5427\u3002\u201d\u50ac\u547d\u7b26\u9053\u201c\u5230\u54ea\u91cc\u53bb\u62ff\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4eec\u9ad8\u5174\u5230\u54ea\u91cc\u53bb\u62ff\u5c31\u5230\u9890\u91cc\u53bb\u62ff\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u8bf4\u6211\u4f01\u600e\u77e5\u9053\u94b1\u85cf\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u8bf4\uff1f\u6211\u4ec0\u9ebd\u90fd\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u5389\u58f0\u5230\u201c\u4f60\u8fd8\u4e0d\u627f\u8ba4\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6de1\u6de1\u51b7\u7b11\u9053\u201c\u4f60\u8981\u94b1\uff1f\u8fd8\u662f\u8981\u547d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u80fd\u6d3b\u4e0b\u53bb\u7684\u65f6\u5019\u5f53\u7136\u8981\u547d\u82e5\u5df2\u6d3b\u4e0d\u4e0b\u53bb\xb7\u5c31\u53ea\u597d\u8981\u94b1\u4e86\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u8981\u600e\u9ebd\u6837\u624d\u80af\u7b54\u531d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4eec\u80af\u7b54\u5e94\u8fd8\u6211\u7684\u547d\xb7\u6211\u5c31\u7b54\u5e94\u8fd8\u4f60\u4eec\u7684\u94b1\u3002\u201d\uff01 \u3000\u3000\u50ac\u547d\u7b26\u6c89\u9ed8\u4e86\u4e2d\u664c\u5ffd\u7136\u9053\u201c\u597d\xb7\u8fd8\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6761\u547d\u4efd\u94b1\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u6709\u51e0\u6761\u547d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e00\u6761\u3001\u90ed\u5927\u8def\u6761\u3001\u6797\u592a\u5e73\u6761\xb7\u8339\u4e03\u6761\uff0c\u56db\u6761\u547d\xb7\u56db\u4efd\u94b1\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u2606\u4e00\u6761\u547d\uff0c\u56db\u4efd\u94b1\u3002\u201d\u738b\u52a8\u9053\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4e0d\u884c\u4e5f\u5f97\u884c\u4f60\u662f\u6d3b\u7684\u94b1\u662f\u6b7b\u7684\u6211\u4eec\u65e2\u80fd\u627e\u5230\u4f60\uff0c\u8fd8\u4f2f\u627e\u4e0d\u5230\u94b1\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u7f13\u7f13\u9053\u201c\u597d\u5427\xb7\u5c31\u5148\u8fd8\u547d\u6765\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u576f\u8c01\u7684\u547d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8981\u8c01\u8fd8\u94b1\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53c8\u7b11\u4e86\u5403\u5403\u7b11\u9053\u201c\u6211\u65e9\u5c31\u77e5\u8c0a\u4ed6\u603b\u7b97\u8fd8\u662f\u4e2a\u806a\u660e\u4eba\u603b\u7b97\u8fd8\u77e5\u9053\u65e0\u8bba\u6dee\u7684\u547d\xb7\u90fd\u4e0d \u5982\u81ea\u5df1\u7684\u547d\u503c\u94b1\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u5148\u89e3\u4f60\u7684\u6bd2\xb7\u4e0d\u89e3\u7a74\u9053\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7a74\u9053\u82e5\u4e0d\u89e3\u4f60\u4eec\u968f\u65f6\u8fd8\u662f\u53ef\u4ee5\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u6211\u7b54\u5e94\u7559\u4e0b\u4f60\u5df2\u8be5\u77e5\u8db3\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u662f\u5440\u6d3b\u603b\u6bd4\u6b7b\u597d\u4f60\u8fd8\u662f\u60f3\u5f00\u4e9b\u5427\u3002\u201d\u738b\u52a8\u53c8\u6c89\u997f\u4e86\u5f88\u4e45\xb7\u7ec8\u4e8e\u957f\u957f\u53f9\u606f\u4e00 \u58f0\xb7\u9053\u201c\u770b\u6765\u6211\u5df2\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u51b7\u51b7\u9053\u201c\u4f60\u5e26\u8d70\u90a3\u7b14\u94b1\u7684\u65f6\u5019\u5c31\u5df2\u8d70\u4e0a\u5382\u7edd\u8def\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u73af\u773a\u7a74\u88ab\u70b9\u4f4f\u7684\u4eba\u4ec0\u9ebd\u8def\u90fd\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5a9a\u7b11\u9053\u201c\u4f60\u672c\u80fd\u8d70\u6211\u80cc\u4f60\u83ab\u5fd8\u4e86\u4ee5\u524d\u4f60\u603b\u662f\u538b\u6211\u7684\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u51b7\u51b7\u9053\u201c\u4f60\u8ddf\u6211\u8d70\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7728\u7728\u773c\u9053\u201c\u90a3\u9ebd\u8c01\u80cc\u4ed6\u5462\uff1f\u201d \u3000\u3000\u4e2a\u4eba\u5ffd\u7136\u4ece\u79ef\u96ea\u4e2d\u94bb\u51fa\u6765\u86c7\u775b\u822c\u94bb\u51fa\u6765\xb7\u9053\u201c\u6211\u3002\u201d \u3000\u3000\u738b\u52a8\u4f0f\u5728\u8d64\u7ec3\u86c7\u80cc\u4e0a\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u7684\u8eab\u5b50\u67d4\u8f6f\u3001\u6f6e\u6e7f\u3001\u51b0\u51b7\u3002 \u3000\u3000\u96fe\u5df2\u5c06\u6563\u3002 \u3000\u3000\u4f46\u5929\u8272\u4f9d\u65e7\u9634\u5195\u770b\u4e0d\u89c1\u592a\u9633\u4e5f\u770b\u4e0d\u89c1\u5149\u660e\u3002 \u3000\u3000\u6839\u672c\u5c31\u6ca1\u6709\u5149\u660e\u56e0\u4e3a\u5df2\u5168\u65e0\u5e0c\u671b\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u5ffd\u7136\u9053\u201c\u8fd9\u662f\u4f60\u56de\u5bb6\u7684\u8def\u3002\u201d \u3000\u3000\u571f\u52a8\u9053\u201c\u53ea\u5e0c\u671b\u4e0d\u662f\u56de\u8001\u5bb6\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u628a\u94b1\u5c31\u85cf\u5728\u5bb6\u91cc\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u82e5\u662f\u4f60\u4f60\u85cf\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u8c0a\u201c\u5f53\u7136\u662f\u53ef\u4ee5\u968f\u65f6\u6478\u5f97\u5230\u7684\u5730\u65b9\uff0c\u94b1\u5c31\u8c61\u4ea5\u4eba\u6837\u6700\u597d\u653e\u5728\u968f\u65f6\u53ef\u4ee5\u6478\u5f97\u5230\u7684\u5730 \u65b9\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u4e86\uff0c\u8c0a\u60f3\u4e0d\u5230\u4f60\u4e5f\u9524\u5973\u4eba\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u5c31\u56e0\u4e3a\u6211\u61c2\u6240\u4ee5\u624d\u4e0d\u8981\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u53ea\u8981\u94b1\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u201c\u94b1\u6bd4\u5973\u4eba\u597d\u94b1\u4e0d\u4f1a\u9a97\u4f60\u4e16\u4e0a\u7edd\u6ca1\u6709\u6bd4\u94b1\u66f4\u5fe0\u5b9e\u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u6240\u4ee5\uff0c\u94b1\u53ef\u4ee5\u653e \u5728\u5ba2\u5385\u91cc\u9762\uff0c\u5973\u4eba\u5374\u4e0d\u80fd\u4eba \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u94b1\u5c31\u5728\u5ba2\u5385\u91cc\u201d \u3000\u3000\u5e72\u52a8\u9053\uff1a\u201c\u4e2a\u4eba\u7684\u5bb6\u91cc\u3001\u8fd8\u6709\u4ec0\u9ebd\u5730\u65b9\u6bd4\u5ba2\u5385\u66f4\u5bbd\u655e\u3001\u66f4\u663e\u773c\uff1f\u201d\u8d64\u7ec3\u86c7\u70b9\u70b9\u5934\u9053\u201c\u4e0d \u9519\u8d8a\u663e\u773c\u7684\u5730\u65b9\u522b\u4eba\u53cd\u800c\u8d8a\u4e0d\u4f1a\u6ce8 \u3000\u3000\u50ac\u547d\u7b26\u4ece\u4e0d\u80af\u8d70\u5728\u4efb\u4f55\u4eba\u524d\u9762\u3002 \u3000\u3000\u4e16\u535c\u7684\u786e\u6709\u8fd9\u79cd\u4eba\u56e0\u4e3a\u4ed6\u5728\u80cc\u5f8c\u6697\u7b97\u522b\u4eba\u7684\u6b21\u6570\u592a\u591a\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6c38\u8fdc\u4e0d\u613f\u8ba9\u4efb\u4f55\u4eba\u8d70\u5728\u4ed6\u80cc\u5f8c\u3002 \u3000\u3000\u4ed6\u7d27\u7d27\u8d34\u7ea2\u5a18\u5b50\u5c31\u597d\u50cf\u662f\u6761\u5f71\u5b50\u3002 \u3000\u3000\u7ea2\u5a18\u5382\u751a\u81f3\u53ef\u4ee5\u611f\u89c9\u5230\u4ed6\u90a3\u51b0\u51b7\u7684\u547c\u5438\u5e26\u6b7b\u7684\u6c14\u5473\u7684\u547c\u5438\u3002 \u3000\u3000\u5979\u7684\u8138\u8272\u96be\u770b\u6781\u4e86\u3002 \u3000\u3000\u50ac\u547d\u7b26\u770b\u4e0d\u89c1\u5979\u7684\u8138\u53ea\u80fd\u770b\u89c1\u5979\u7684\u8116\u5b50\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u770b\u5979\u7684\u8116\u5b50\xb7\u8138\u4e0a\u5e26\u6b23\u8d4f\u7684\u8868\u60c5\uff0c\u56e0\u4e3a\u5979\u5149\u6ed1\u767d\u5ae9\u7684\u8116\u5b50\u5df2\u56e0\u4ed6\u7684\u547c\u5438\u4e3d\u8d77\u4e86\u4e00 \u7c92\u7c92\u6363\u76ae\u7599\u7629\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5374\u5728\u770b\u524d\u9762\u7684\u738b\u52a8\uff0c\u5ffd\u7136\u9053\u201c\u4f60\u8ba4\u4e3a\u4ed6\u771f\u7684\u4f1a\u5e26\u6211\u4eec\u53bb\u62ff\u94b1\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4ed6\u5df2\u522b\u65e0\u9009\u62e9\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u5374\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u90a3\u70b9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4ed6\u4e0d\u662f\u8fd9\u9ebd\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\u4e5f\u4e0d\u8be5\u8fd9\u9ebd\u6015\u6b7b\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u51b7\u7b11\u9053\u201c\u968f\u4fbf\u4ed6\u662f\u600e\u9ebd\u6837\u7684\u4eba\xb7\u73b0\u5728\u90fd\u5df2\u65e0\u59a8\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u56e0\u4e3a\u4ed6\u73b0\u5728\u5df2\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u771f\u4f1a\u7559\u4e0b\u4ed6\u7684\u547d\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u8e1e\u7136\u9053\u201c\u6211\u5f53\u7136\u77e5\u9053\u4f60\u4e0d\u4f1a\u4f46\u73b0\u5728\u4ed6\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u63a5\u9053\u201c\u867d\u7136\u8fd8\u6ca1\u6709\u5b8c\u5168\u6b7b\xb7\u4f46\u5df2\u6b7b\u4e86\u5927\u534a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4ed6\u8fd8\u6709\u670b\u53cb\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4e2a\u662f\u5feb\u6b7b\u7684\u670b\u53cb\u53e6\u5916\u4e24\u4e2a\u7b80\u76f4\u5df2\u7b49\u4e60\u2019\u6b7b\u4e86\u6211\u4eec\u53c1\u4e2a\u4eba\u65e0\u8bba\u8c01\u90fd\u5df4\u8db3\u591f\u5bf9\u4ed8 \u4ed6\u4eec\uff0c\u4f60\u8fd8\u62c5\u5fc3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u6211\u4e0d\u662f\u62c5\u5fc3\u53ea\u89c9\u5f97\u6709\u70b9\u53ef\u60dc\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u53ef\u60dc\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u60a0\u7136\u7b11\u9053\u201c\u53ef\u60dc\u6211\u8fd8\u6ca1\u6709\u8ddf\u90a3\u53c1\u4e2a\u5c0f\u4f19\u4e8e\u7761\u8fc7\u89c9\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u5ffd\u7136\u53e3\u54ac\u4f4f\u5979\u7684\u8116\u5b50\u3002 \u3000\u3000\u5c31\u597d\u50cf\u662f\u6761\u75af\u72d7\u54ac\u4f4f\u4e86\u6761\u6bcd\u72d7\u3002 \u3000\u3000\u5929\u8272\u9634\u6697\u6240\u4ee5\u5ba2\u5385\u91cc\u6697\u5f97\u72e0\u3002 \u3000\u3000\u7a97\u65bc\u662f\u5f00\u7684\u4ece\u5916\u9762\u53ef\u4ee5\u9690\u7ea6\u770b\u5230\u4e24\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4ec0\u9ebd\u4eba\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u738b\u52a8\u8c08\u6de1\u9053\u201c\u60f3\u4e2d\u5230\u4f60\u7684\u773c\u775b\u8fd1\u6765\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u7684\u773c\u775b\u672c\u6765\u5c31\u4e0d\u884c\u3002 \u3000\u3000\u4efb\u4f55\u6b62\u82e5\u662f\u751f\u94bb\u5728\u5404\u5f0f\u5404\u6837\u7684\u6bd2\u836f\u91cc\u773c\u529b\u90fd\u672f\u4f1a\u597d\u3002 \u3000\u3000\u4f46\u5c31\u7b97\u773c\u529b\u518d\u5dee\u7684\u4eba\xb7\u53ea\u8981\u591a\u770b\u51e0\u773c\uff0c\u4e5f\u80fd\u770b\u5f97\u51fa\u90a3\u53ea\u4e0d\u8fc7\u662f\u4e24\u4e2a\u7a3b\u8349\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u62ab\u9ebb\u5e26\u5b5d\u7684\u7a3b\u8349\u4eba\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u82e5\u8fd8\u6ca1\u6709\u770b\u6e05\u6211\u4e0d\u59a8\u544a\u8bc9\u4f60\u6211\u82e5\u6b7b\u5382\u4ed6\u4eec\u5c31\u662f\u6211\u7684\u5b5d\u5b50\xb7\u4f60\u82e5\u6b7b \u4e86\xb7\u6015\u4e5f\u53ea\u6709\u7528\u4ed6\u4eec\u6765\u505a\u5b5d\u5b50\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u8fd9\u6837\u7684\u5b5d\u4e86\u81f3\u5c11\u603b\u6bd4\u8d25\u5bb6\u4e88\u597d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u4f60\u5b81\u53ef\u7edd\u5b50\u7edd\u5b59\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u6700\u597d\u8fde\u670b\u53cb\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5ffd\u7136\u8d76\u4e0a\u6765\u9053\u201c\u4f60\u7684\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u5979\u95ee\u7684\u662f\u738b\u52a8\u56e0\u4e3a\u8fd9\u4e9b\u4eba\u91cc\u53ea\u6709\u738b\u52a8\u624d\u6709\u670b\u53cb\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u4eec\u5728\u5c71\u535c\u7b49\u6211\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u5230\u5c71\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u82e5\u662f\u4ed6\u4eec\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\uff0c\u4f1a\u5728\u90a3\u91cc\u7b49\u6211\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u5979\u6839\u672c\u5c31\u4e0d\u4f1a\u7b49\u4f60\u3002\u201d \u3000\u3000\u7a3b\u8349\u4eba\u7684\u79d8\u5bc6 \u3000\u3000\u7ea2\u5a18\u5b50\u7728\u4e86\u7728\u773c\u9053\u201c\u6211\u2026\u5411\u603b\u89c9\u5f97\u6700\u4e86\u89e3\u6211\u7684\u4eba\u5c31\u662f\u4f60\u4f60\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u56e0\u4e3a\u53ea\u6709\u5973\u4eba\u624d\u80fd\u5382\u89e3\u5973\u4eba\u8fd9\u9053\u7406\u8c01\u90fd\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u662f\u4ea5\u4eba\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u4ee5\u4e3a\u4ed6\u662f\u7537\u4eba\uff1f\u201d \u3000\u3000\u571f\u52a8\u9053\u201c\u770b\u8d77\u6765\u4ed6\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u5c31\u7b97\u4ed6\u672c\u6765\u662f\u4e2a\u7537\u4eba\xb7\u4f46\u5728\u6bd2\u836f\u91cc\u6ce1\u4e86\u51e0\u535c\u5e74\u4e5f\u65e9\u5c31\u53d8\u6210\u4e2a\u5973\u4eba\u4e86\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u7684\u8138\u5ffd\u7136\u50f5\u786c\u5c31\u597d\u50cf\u662f\u6761\u86c7\u5ffd\u7136\u88ab\u4eba\u634f\u4f4d\u5382\u4e03\u5bf8\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5403\u5403\u7b11\u9053\u201c\u8fd9\u662f\u4ed6\u6700\u5927\u7684\u79d8\u5bc6\u6211\u672c\u6765\u4e0d\u8be5\u8bf4\u51fa\u6765\u7684\u5e78\u597d\u4f60\u4e5f\u4e0d\u662f\u5916\u4eba\uff0c\u6240 \u4ee5\u2026\u2026\u201d \u3000\u3000\u5979\u6545\u610f\u538b\u4f4e\u8bed\u58f0\u6084\u6084\u9053\u201c\u6211\u8fd8\u53ef\u4ee5\u544a\u8bc9\u4f60\u4e2a\u79d8\u5bc6\u3002\u201d \u3000\u3000\u7389\u52a8\u9053\u201c\u4ec0\u9ebd\u79d8\u5bc6\uff1f\u201d\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u77e5\u9053\u4ed6\u548c\u5927\u9a73\u8239\u662f\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u5e72\u52a8\u53c8\u7b11\u9053\u201c\u4f60\u9519\u4e86\u4ed6\u8ba2\u4e0d\u662f\u670b\u53cb\u4ed6\u4eec\u5df2\u662f\u2026\u2026\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u76f4\u5728\u77aa\u5979\u51b7\u51b0\u51b0\u7684\u773c\u775b\u5df2\u53d8\u6210\u78a7\u7eff\u8272\xb7\u5ffd\u7136\u5bf9\u51c6\u5979\u7684\u8138\u5439\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u8f7b\u8f7b\u5439\u4e86\u53e3\u6c14\u4f46\u7ea2\u5a18\u5b50\u5374\u8c61\u662f\u5728\u95ea\u907f\u4e16\u4e0a\u6700\u6b79\u6bd2\u7684\u6697\u5668\u6837\u8fde\u8bdd\u90fd\u6765\u4e0d\u53ca\u8bf4 \u5b8c\xb7\u8eab\u5b50\u8dc3\u8d77\u51cc\u7a7a\u4e2a\u7ffb\u8eab\u5df2\u63a0\u5230\u5c4b\u80cc\u5f8c\xb7\u5979\u8eab\u5f8c\u7684\u50ac\u547d\u7b26\u5374\u65e9\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u5979\u8bf4\u7684\u8bdd\xb7\u6211\u672c\u6765\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u4fe1\u7684\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u672c\u6765\u4e0d\u7b28\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u5382\u7b11\u9053\u201c\u56e0\u4e3a\u5979\u8bf4\u7684\u82e5\u6c34\u662f\u771f\u7684\u4f60\u4f55\u5fc5\u8981\u5979\u7684\u547d\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u51b7\u51b7\u9053\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u53eb\u6211\u8981\u4f60\u7684\u4fde\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u8c08\u9053\u201c\u6211\u8fd9\u6761\u547d\u65e9\u5df3\u4e0d\u59d3\u738b\u4e2b\u8c01\u8981\u53bb\u90fd\u6ca1\u5173\u7cfb\uff0c\u4f46\u4f60 \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u6211\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u4eba\u4f24\u5fc3\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u6ca1\u6709\u4eba\u5f00\u5fc3\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u6709\u3002\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u4f60\u4e5f\u77e5\u9053\u5979\u6068\u4f60\uff1f\u201d\u8d64\u7ec3\u86c7\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5979\u4e3a\u4ec0\u9ebd\u76f4\u6ca1\u6709\u8981\u4f60\u7684\u547d\uff1f\u201d\u8d64\u7ec3\u86c7\u9053\u201c\u95ea\u4e3a\u5979\u7ec6\u9053\u6211\u6d3b\u6bd4\u6b7b\u300f\u2019\u5546\u7528\u3002\u201d\u738b \u529f\u9053\u201c\u4ee5\u53a2\u5462\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4ee5\u5f8c\uff1f\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u4ee5\u5f8c\u5206\u94b1\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u7684\u8138\u4e49\u5df2\u50f5\u786c\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u706b\u7f00\u8681\u6b7b\u4e01\u4ed6\u4eec\u662f\u4e0d\u662f\u4e5f\u5f88\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u6253\u4e3a\u4ec0\u9ebd\u4e0d\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u56e0\u4e3a\u53c1\u4e2a\u4eba\u5206\u94b1\u603b\u6bd4\u56db\u4e2a\u4eba\u5206\u5f97\u591a\u4e9b\u3002\u201d\u738b\u52a8\u9053\u201c\u82e5\u53ea\u6709\u4e24\u4e2a\u4eba\u5206\u94b1\u5462\uff1f\u201d \u8d64\u7ec3\u86c7\u56de\u8fc7\u5934\u76ef\u4ed6\u5b57\u5b57\u9053\u201c\u4f60\u7a76\u7adf\u60f3\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u60f3\u8bf4\u7684\u4e8b\u4f60\u672c\u8be5\u65e9\u5c31\u660e\u767d\u4e86\u7684\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u78a7\u7eff\u8272\u7684\u773c\u795e\u7a81\u53c8\u53d8\u6210\u6b7b\u7070\u8272\u51b7\u51b0\u51b0\u7684\u5168\u65e0\u8868\u60c5\u3002 \u3000\u3000\u624b\u52a8\u9053\u201c\u4e2a\u9992\u5934\u4e24\u4e2a\u4eba\u5403\u603b\u6bd4\u4e8c\u4e2a\u4eba\u5403\u597d\u4e9b\xb7\u8fd9\u9053\u7406\u672c\u5c31\u8c01\u90fd\u660e\u767d\u73b0\u5728\u7684\u95ee\u9898\u662f\uff0c\u662f\u90a3 \u4e24\u4e2a\u4eba\u80fd\u5403\u5230\u9992\u5934\u5462\uff1f\u201d\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u738b\u52a8\u7f13\u7f13\u9053\u201c\u6211\u77e5\u9053\u4f60\u7684\u529f\u592b\u4f60\u5f53\u7136\u4e0d\u6015\u7ea2\u5a18\u5b50\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f46\u5979\u548c\u5d14\u8001\u5927\u662f\u4ec0\u9ebd\u5173\u7cfb\uff1f\u4f60\u548c\u5d14\u8001\u5927\u53c8\u662f\u4ec0\u9ebd\u5173\u7cfb\uff1f\u4f60\u80fd\u4e0d\u80fd\u6bd4\u5f97\u4e0a\u5979\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u51b7\u7b11\u3002 \u3000\u3000\u5728\u67d0\u79cd\u60c5\u51b5\u4e0b\xb7\u4e2a\u4eba\u82e5\u662f\u51b7\u7b11\u53ea\u4e0d\u8fc7\u8868\u793a\u4ed6\u5df2\u65e0\u8bdd\u53ef\u8bf4\u8868\u793a\u4ed6\u5fc3\u91cc\u5df2\u4e0d\u5b89\u3002 \u3000\u3000\u4e2a\u5bf9\u6bcf\u4ef6\u4e8b\u90fd\u5b8c\u5168\u6709\u628a\u63e1\u7684\u4eba\u662f\u5f88\u5c11\u4f1a\u8fd9\u9ebd\u6837\u7684\u51b7\u7b11\u7684\u3002 \u3000\u3000\u6240\u4ee5\u738b\u52a8\u7acb\u523b\u53c8\u63a5\u9053\u201c\u6240\u4ee5\u4f60\u82e5\u60f3\u5403\u5230\u9992\u5934\xb7\u5c31\u6700\u597d\u8d76\u5feb\u53e6\u60f3\u6cd5\u5b50\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u8fdf\u7591\xb7\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53e6\u5916\u627e\u4e2a\u4eba\u6765\u5e2e\u4f60\u62a2\u90a3\u9992\u5934\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u53c8\u5728\u51b7\u7b11\u9053\u201c\u627e\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7b2c\u90a3\u4eba\u8981\u4e0d\u592a\u8d2a\u5fc3\u3002\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4e16\u4e0a\u6709\u8fd9\u79cd\u4eba\uff1f\u201d\u738b\u52a8\u9053\u201c\u6211\u5c31\u4e0d\u662f\u8d2a\u5fc3\u7684\u4eba\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ee5\u524d\u6211\u4e5f\u8bb8\u4e0d\u662f\uff0c\u4f46\u73b0\u5728\u6211\u5df2\u503a\u5f97\u4e24\u4e2a\u4eba\u5206\u7ec4\u5934\u9662\u603b\u6bd4\u6ca1\u6709\u9992\u5934\u5403\u597d\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u51dd\u89c6\u4ed6\u9053\u201c\u7b2c\u4e8c\u5462\uff1f\u201d\u738b\u52a8\u9053\u201c\u7b2c\u7236\u90a3\u4eba\u8981\u4e0d\u5982\u4f60\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u4e0d\u5982\u6211\uff1f\u201d \u3000\u3000\u738b\u52a8\uff1a\u201c\u56e0\u4e3a\u4ed6\u82e5\u4e0d\u5982\u4f60\uff0c\u5c31\u7edd\u4e0d\u6562\u5728\u4f60\u9762\u524d\u52a8\u6b6a\u8111\u7b4b\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u4e0d\u5982\u6211\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u5382\u7b11\u9053\u201c\u6211\u82e5\u6bd4\u4f60\u5f3a\u73b0\u5728\u600e\u4f1a\u8981\u4f9d\u80cc\u6211\u5462\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u6b7b\u7070\u8272\u7684\u773c\u775b\u91cc\u5ffd\u7136\u51fa\u73b0\u4e86\u2019\u70b9\u5149\uff0c\u9053\u201c\u4f60\u771f\u662f\u7ad9\u5728\u6211\u8fd9\u8fb9\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u53ea\u6709\u7ad9\u5728\u4f60\u8fd9\u8fb9\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4ed6\u4eec\u90a3\u8fb9\u7684\u4eba\u5df2\u592a\u6324\u4e86\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u7684\u773c\u775b\u53c8\u4eae\u4e86\u4e9b\uff0c\u9053\u201c\u4f60\u80fd\u591f\u66ff\u6211\u505a\u4e9b\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u8fd8\u6709\u624b\u3002\u201d\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u7684\u624b\u80fd\u505a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u81f3\u5c11\u8fd8\u80fd\u62c9\u4f0e\u4e2a\u4eba\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u4e0d\u518d\u51b7\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5bf9\u8fd9\u4ef6\u4e8b\u5df2\u6e10\u60ed\u5f00\u59cb\u89c9\u5f97\u6709\u4e86\u4e9b\u628a\u63e1\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u73b0\u5728\u53ea\u6709\u4e00\u4e2a\u95ee\u9898\u4e86\u3002\u201d\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u80fd\u4e0d\u80fd\u5bf9\u4ed8\u5d14\u8001\u5927\uff1f\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u201c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u82e5\u771f\u7684\u52a8\u624b\u6211\u4e0d\u77e5\u9053\u4f46\u82e5\u9aa4\u51fa\u4e0d\u610f\xb7\u653b\u5176\u65e0\u5907\xb7\u90a3\u5c31\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a81\u7136\u95ed\u4e0a\u4e86\u7792\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u4e5f\u95ed\u4e0a\u4e86\u5634\uff0c\u8fd9\u624d\u9992\u4fb5\u7684\u8d70\u8fdb\u4e86\u5c4b\u4e8e\u3002 \u3000\u3000\u50ac\u547d\u7b26\u548c\u7ea2\u5a18\u5b50\u5df2\u5728\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u5c4b\u4e88\u91cc\u5df2\u7ecf\u72e0\u4eae\u4e86\u3002 \u3000\u3000\u50ac\u547d\u7b26\u7684\u8138\u5728\u5929\u5149\u4e0b\u770b\u6765\u5c31\u8c61\u662f\u5f20\u767d\u7eb8\u3002 \u3000\u3000\u5f20\u53c8\u4e8e\u53c8\u76b1\u7684\u81ea\u7eb8\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u597d\u50cf\u90fd\u89c1\u4e0d\u5f97\u5929\u5149\u7684\xb7\u4ed6\u663e\u7136\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u5c06\u738b\u52a8\u653e\u5728\u6905\u5b50\u4e0a\u9053\u201c\u4f60\u8ba2\u5df2\u770b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u6bcf\u4e2a\u5730\u65b9\u90fd\u770b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u7ea2\u8d29\u5b50\u8d61\u7136\u9053\u201c\u8fde\u5395\u6240\u91cc\u90fd\u770b\u8fc7\u4e86\uff0c\u5947\u602a\u7684\u662f\u90a3\u91cc\u5c45\u7136\u4e0d\u592a\u81ed\u3002\u201d \u3000\u3000\u5979\u539f\u4e86\u738b\u52a8\u4e00\u773c\u53c8\u9053\u201c\u6240\u4ee5\u6211\u77e5\u9053\u4f60\u7684\u670b\u53cb\u5b9a\u6709\u4e2a\u5f88\u559c\u6b22\u4e7e\u51c0\u7684\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u51b7\u51b7\u9053\u201c\u4f60\u8fd8\u77e5\u9053\u751a\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u6211\u8fd8\u77e5\u9053\u90a3\u4eba\u4e00\u5b9a\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u8d64\u7ec3\u86c7\u9053\u4ed6\u7684\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u5168\u8d70\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53c8\u8198\u4e86\u738b\u52a8\u773c\xb7\u5a9a\u7b11\u9053\u201c\u770b\u6765\u4f60\u6700\u8fd1\u4ea4\u7684\u4e5f\u5e76\u4e0d\u662f\u4ec0\u9ebd\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u738b\u52a8\u6de1\u6de1\u9053\u201c\u5929\u4e0b\u672c\u6ca1\u6709\u771f\u80fd\u966a\u4f60\u6b7b\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u6b21\u5979\u773c\u6ce2\u8198\u7684\u662f\u8d64\u7ec3\u86c7\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u597d\u50cf\u6839\u672c\u6ca1\u542c\u89c1\u4e5f\u6ca1\u770b\u89c1\u9053\u201c\u8fd9\u5c4b\u4e8e\u91cc\u5df2\u6ca1\u6709\u522b\u7684 \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u53ea\u6709\u8fd9\u4e24\u4e2a\u7a3b\u8349\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7a3b\u8349\u4eba\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u7a81\u7136\u9634\u4fa7\u4fa7\u7684\u4e00\u7b11\uff0c\u9053\u201c\u83ab\u5fd8\u4e86\u7a3b\u8349\u4eba\u6709\u65f6\u4e5f\u80fd\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u7684\u8138\u8272\u597d\u50cf\u5ffd\u7136\u6709\u4e9b\u53d8\u4e86\u3002 \u3000\u3000\u50ac\u547d\u7b26\u76f4\u5728\u76ef\u4ed6\u7684\u8138\u5c31\u5728\u4ed6\u8138\u8272\u5fae\u53d8\u7684\u90a3\u4e00\u77ac\u95f4\u50ac\u547d\u7b26\u5df2\u51fa\u624b\u3002 \u3000\u3000\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u50ac\u547d\u7b26\u6740\u4eba\u7528\u7684\u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6740\u4eba\u662f\u771f\u6740\xb7\u4e00\u51fa\u624b\u5c31\u7edd\u4e0d\u4f1a\u518d\u8ba9\u5bf9\u65b9\u6709\u6d3b\u4e0b\u53bb\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5426\u5219\u4ed6\u5c31\u4e0d\u51fa\u624b\u3002 \u3000\u3000\u53ea\u6709\u770b\u8fc7\u4ed6\u6740\u4eba\u7684\u4eba\u624d\u77e5\u9053\u4ed6\u6740\u4eba\u7528\u7684\u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u53ea\u6709\u56db\u4e2a\u4eba\u770b\u8fc7\u4ed6\u6740\u4eba\u3002\u738b\u52a8\u770b\u8fc7\u3002 \u3000\u3000\u4ed6\u6740\u4eba\u7528\u7684\u662f\u4e24\u6839\u523a\u3002 \u3000\u3000\u4e24\u6839\u94a2\u4e1d\u822c\u7684\u523a\u53ef\u4ee5\u6e38\u9b42\u822c\u7f20\u4f60\u7ee9\u4f4f\u4f60\u7684\u5175\u5668\uff0c\u627c\u65ad\u4f60\u7684\u8116\u5b50\uff0c\u4e5f\u53ef\u4ee5\u4e0b\u4e8e\u5c31\u523a\u8fdb\u4f24\u5fc3 \u810f\u91cc\u3002 \u3000\u3000\u90a3\u5c31\u662f\u4ed6\u7684\u51fa\u624b\u53cc\u98de\u6e38\u9b42\u523a\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u6709\u5f88\u591a\u4eba\u662f\u4ee5\u4ed6\u6253\u7684\u72ec\u95e8\u5175\u5668\u800c\u6210\u540d\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u82e5\u6709\u79cd\u5947\u7279\u7684\u72ec\u95e8\u5175\u5668\uff0c\u5728\u548c\u4eba\u4ea4\u624b\u65f6\uff0c\u5c31\u5f80\u5f80\u4f1a\u5360\u5230\u5f88\u591a\u4fbf\u5b9c\u3002 \u3000\u3000\u5f88\u591a\u610f\u60f3\u4e0d\u5230\u7684\u4fbf\u5b9c\u3002\u6240\u4ee5\u4f60\u82e5\u80fd\u521b\u9020\u4ef6\u4ee4\u4eba\u610f\u60f3\u4e0d\u5230\u7684\u72ec\u95e8\u5175\u5668\u4f60\u5c31\u5b9a\u4f1a\u5728\u6c40\u6e56\u4e2d\u95ef \u51fa\u540d\u5934\u6765\u7528\u522b\u4eba\u7684\u8840\u5199\u51fa\u4f60\u7684\u540d\u5934\u6765 \u3000\u3000\u867d\u7136\u4f60\u4ee5\u5f8c\u4e5f\u4f1a\u6b7b\u5728\u53e6\u4ef6\u4ee4\u4f60\u610f\u60f3\u4e0d\u5230\u7684\u72ec\u95e8\u5175\u5668\u4e0b\u3002 \u3000\u3000\u7a3b\u8349\u4eba\u7684\u8eab\u5b50\u770b\u6765\u5f88\u81c3\u80bf\u3002 \u3000\u3000\u6bd4\u4ed6\u4eec\u5728\u653e\u98ce\u519b\u65f6\u81c3\u80bf\u5f97\u591a\u5382\u3002 \u3000\u3000\u8fd9\u70b9\u522b\u4eba\u4e5f\u8bb8\u770b\u4e0d\u51fa\u6765\u4f46\u50ac\u547d\u7b26\u5374\u7edd\u4e0d\u4f1a\u770b\u4e0d\u51fa\u6765\u3002\u56e0\u4e3a\u7a20\u8349\u4eba\u5c31\u662f\u4ed6\u4f5c\u7684\u3002 \u3000\u3000\u4ed6\u867d\u6709\u5f20\u7b28\u8138\u5374\u6709\u53cc\u5de7\u624b\u771f\u6b63\u806a\u660e\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u5c06\u806a\u660e\u6446\u5728\u8138\u535c\u7684\u3002 \u3000\u3000\u7a3b\u8349\u4eba\u672c\u5403\u80a5\u8089\uff0c\u4e5f\u4e0d\u559d\u9152\u4e3a\u4ec0\u9ebd\u4f1a\u5ffd\u7136\u591c\u4e4b\u95f4\u957f\u80d6\u4e86\u5462\uff1f \u3000\u3000\u662f\u4e0d\u662f\u6709\u4eba\u85cf\u5728\u6eba\u9769\u4eba\u91cc\u51c6\u5907\u7a81\u7136\u95f4\u8df3\u8d77\u6765\u51fa\u624b\uff1f\u8fd9\u5c31\u662f\u738b\u52a8\u548c\u71d5\u4e03\u4ed6\u4eec\u65e9\u5df2\u5546\u91cf\u597d\u7684 \u6700\u5f8c\u51fb \u3000\u3000\u4e0a\u52a8\u7684\u8138\u8272\u53d8\u5382\u3002 \u3000\u3000\u56fd\u4e3a\u8fd9\u65f6\u50ac\u547d\u7b26\u7684\u51fa\u624b\u53cc\u98de\u6e38\u89c2\u523a\u5df2\u95ea\u7535\u822c\u523a\u4eba\u7a3b\u8349\u4eba\u7684\u5fc3\u810f\u3002 \u3000\u3000\u523a\u5f97\u5f88\u6df1\u3002\u6df1\u6781\u4e86\u3002 \u3000\u3000\u6700\u5f8c\u4e00\u51fb\u4e16\u4e0a\u7684\u786e\u5f88\u5c11\u6709\u771f\u80fd\u548c\u4f60\u5171\u751f\u6b7b\u7684\u670b\u53cb\u3002\u8fde\u8fd9\u6837\u7684\u592b\u59bb\u90fd\u5f88\u5c11\u4f55\u51b5\u670b\u53cb\uff1f\u4f46\u8fd9 \u6837\u7684\u670b\u53cb\u5e76\u4e0d\u662f\u7edd\u5bf9\u6ca1\u6709\u3002\u81f3\u5c11\u7ff1\u5927\u8def\u4ed6\u4eec\u5c31\u662f\u8fd9\u6837\u7684\u670b\u53cb\u3002\u4ed6\u4eec\u77e5\u9053\u738b\u52a8\u5df2\u5728\u751f\u6b7b\u5173\u5934\u600e \u9ebd\u80af\u653e\u4e0b\u738b\u52a8\u4e2a\u4eba\u5728\u5371\u9669\u4e2d\uff1f\u4ed6\u4eec\u600e\u9ebd\u4f1a\u8d70\uff1f\u7a3b\u8349\u4eba\u957f\u80d6\u5382\u3002\u80d6\u4eba\u7684\u8840\u591a\u3002\u50ac\u547d\u7b26\u7684\u51fa\u624b\u53cc \u98de\u6e38\u9b42\u523a\u5df2\u523a\u4eba\u4e86\u4ed6\u8ba2\u7684\u5fc3\u810f\u3002\u4f46\u5374\u6ca1\u6709\u8840\u8fde\u6ef4\u8840\u90fd\u6ca1\u6709\u3002\u8fd9\u6b21\u8138\u8272\u6539\u53d8\u7684\u4e0d\u662f\u738b\u52a8\xb7\u662f\u50ac \u547d\u7b26\u3002\u5c31\u5728\u50ac\u547d\u7b26\u8138\u8272\u6539\u53d8\u7684\u8fd9\u77ac\u95f4\u8d64\u7ec3\u86c7\u7684\u773c\u775b\u91cc\u5df2\u53d1\u51fa\u4e86\u5149\u3002\u4e5f\u5c31\u5728\u8fd9\u540c\u77ac\u95f4\u738b\u52a8\u62c9\u4f50 \u4e86\u7ea2\u5a18\u5b50\u7684\u624b\u3002\u871c\u7525\u7684\u523a\u6709\u6bd2\u3002\u50ac\u547d\u7b26\u7684\u4f8b\u66f4\u6bd2\u3002\u7559\u9677\u7684\u523a\u82e5\u5df4\u523a\u8fc7\u4eba\u5c31\u6ca1\u6709\u6bd2\u4e86\u3002\u50ac\u547d\u7b26 \u7684\u523a\u73b0\u5728\u8fd8\u7559\u5728\u7a3b\u8349\u4eba\u7684\u5fc3\u810f\u91cc\u3002\u8fd9\u673a\u4f1a\u8d64\u7ec3\u86c7\u600e\u80af\u9519\u8fc7\u3002\u4ed6\u5ffd\u7136\u5bf9\u51c6\u50ac\u547d\u7b26\u7684\u8138\xb7\u7528\u529b\u5439 \u4e2b\u53e3\u6c14\u3002\u5929\u5149\u7167\u4eba\u7a97\u6237\uff0c\u53ef\u4ee5\u770b\u51fa\u4ed6\u5439\u51fa\u7684\u6c14\u662f\u6de1\u78a7\u8272\u7684\u3002\u50ac\u547d\u7b26\u597d\u50cf\u6b63\u5728\u53d1\u6014\u4f46\u5c31\u5728\u4ed6\u8fd9 \u53e3\u6c14\u5439\u51fa\u6765\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u50ac\u547d\u7b26\u7684\u957f\u8896\u7a81\u7136\u53d8\u6210\u4e2a\u5957\u5b50\xb7\u5957\u4f4f\u4e86\u8d64\u7ec3\u86c7\u7684\u5934\u3002 \u3000\u3000\u4e5f\u7f51\u4f4f\u4e86\u4ed6\u7684\u90a3\u53e3\u6c14\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u58f0\u60e8\u547c\u3002 \u3000\u3000\u547c\u58f0\u5f88\u5c16\u9510\xb7\u5f88\u77ed\u4fc3\u3002 \u3000\u3000\u50ac\u547d\u7b26\u7684\u8eab\u5b50\u5df2\u63a0\u8d77\u652f\u624b\u52fe\u4f4f\u4e86\u5927\u6881\u540a\u5728\u6881\u4e0a\xb7\u770b\u4ed6\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u7684\u9650\u775b\u90fd\u8c61\u662f\u5b8c\u5168\u776b\u4e86\uff0c\u4ec0\u9ebd\u90fd\u5df2\u770b\u4e0d\u89c1\u5c31\u8c61\u662f\u4e00\u6761\u778e\u4e86\u773c\u7684\u72d7\u8dea\u8e6c\u5411\u524d\u51b2\u51fa\u3002 \u3000\u3000\u4ed6\u51b2\u51fa\u2019\u6b65\u4e24\u6b65\u53c1\u6b65\u2026\u2500\u2019 \u3000\u3000\u4ed6\u7684\u8138\u5df2\u78a7\u7eff\u3002 \u3000\u3000\u4ed6\u624d\u51b2\u51fa\u5382\u4e24\u6b65\uff0c\u5c31\u5012\u4e0b\u3002 \u3000\u3000\u4e2d\u5382\u8d64\u7ec3\u86c7\u7684\u6bd2\u7edd\u6ca1\u6709\u4eba\u80fd\u8d70\u51fa\u4e03\u6b65\u3002 \u3000\u3000\u5c31\u8fde\u8d64\u7ec3\u86c7\u81ea\u5df2\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u738b\u52a8\u653e\u5f00\u4e86\u7ea2\u5a18\u5b50\u7684\u624b\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u8fd8\u662f\u8fde\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u4f46\u77b3\u5b54\u5374\u5df2\u5f00\u59cb\u5728\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u5df2\u6e10\u6e10\u660e\u767d\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff0c\u8fd9\u4ef6\u4e8b\u5e76\u4e0d\u592a\u6709\u8da3\u3002 \u3000\u3000\u4f46\u7ea2\u5a18\u5b50\u5374\u663e\u7136\u89c9\u5f97\u5f88\u6709\u8da3\xb7\u5979\u65e9\u5df2\u7b11\u4e86\xb7\u7b11\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u7b11\u58f0\u5982\u94f6\u94c3\u3002 \u3000\u3000\u738b\u52a8\u7b2c\u6b21\u770b\u5230\u5979\u7684\u65f6\u5019\uff0c\u5c31\u662f\u88ab\u5979\u8fd9\u79cd\u7b11\u58f0\u8ff7\u4f4f\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u770b\u8fc7\u5979\u51e0\u767e\u6b21\u4e4b\u5f8c\uff0c\u4ed6\u8fd8\u662f\u8ba4\u4e3a\u4e16\u4e0a\u7edd\u6ca1\u6709\u522b\u7684\u4eba\u80fd\u7b11\u5f97\u8fd9\u9ebd\u53ef\u7231\u8fd9\u9ebd\u597d\u542c\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5374\u53ea\u89c9\u5f97\u5455\u5410\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u8d64\u7ec3\u86c7\u603b\u662f\u8ddf\u5979\u5728\u8d77\u751f\u6d3b\u4e86\u8bb8\u591a\u5e74\u7684\u5925\u4f34\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u82e5\u80fd\u5728\u81ea\u5df1\u5925\u8ba1\u7684\u4f53\u65c1\u7b11\u5f97\u5982\u6b64\u5f00\u5fc3\u90fd\u4f1a\u4ee4\u522b\u4eba\u89c9\u5f97\u60f3\u5455\u5410\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u773c\u6ce2\u6d41\u52a8\u9053\u201c\u4f60\u662f\u4e0d\u662f\u5728\u5947\u602a\uff0c\u6211\u4e3a\u4ec0\u9ebd\u8981\u7b11\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u5947\u602a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u4fed\u6839\u672c\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u738b\u52a8\u7684\u7ed3\u8bba\u3002 \u3000\u3000\u50ac\u547d\u7b26\u8fd8\u5728\u51dd\u89c6\u8d64\u7ec3\u86c7\u7684\u5c38\u8eab\xb7\u5c31\u8c61\u662f\u751f\u4f2f\u8fd9\u4eba\u6b7b\u5f97\u8fd8\u4e0d\u591f\u5f7b\u5e95\u3002 \u3000\u3000\u8d64\u7ec3\u86c7\u6b7b\u5f97\u5f88\u5f7b\u5e95\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u6d3b\u65f6\u5c31\u5df2\u5f7b\u5e95\u4e3a\u6bd2\u836f\u8d21\u732e\u51fa\u81ea\u5df2\u7684\u5168\u90e8\u751f\u547d\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u522b\u7684\u670b\u53cb\xb7\u4ed6\u751a\u81f3\u53ef\u4ee5\u8bf4\u4ec0\u9ebd\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6bd2\u836f\u5c31\u662f\u4ed6\u7684\u751f\u547d\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u50ac\u547d\u7b26\u624d\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u7f13\u7f13\u9053\u201c\u8fd9\u662f\u4e2a\u5f88\u5fe0\u5b9e\u7684 \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u8bf4\u4ed6\u5fe0\u5b9e\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u70b9\u70b9\u5934\xb7\u9053\u201c\u4ed6\u81f3\u5c11\u5bf9\u81ea\u5df1\u505a\u7684\u4e8b\u72e0\u5fe0\u5b9e\xb7\u4ed6\u7684\u6bd2\u836f\u7684\u786e\u6ca1\u6709\u5931\u6548\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53c8\u7b11\u4e86\xb7\u9053\u201c\u6240\u4ee5\u4f60\u66f4\u5e94\u8be5\u611f\u6fc0\u6211\u82e5\u4e0d\u662f\u6211\u73b0\u5728\u6b7b\u7684\u5c31\u662f\u4f60\u3002\u201d\u50ac\u547d\u7b26\u6de1\u8c08\u9053 \u201c\u6211\u5012\u7684\u786e\u4ece\u672a\u60f3\u5230\u8fc7\u4ed6\u4e5f\u4f1a\u5356\u6211\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u4f60\u82e5\u4ece\u672a\u60f3\u8fc7\u600e\u9ebd\u4f1a\u65e9\u5df3\u51c6\u5907\u597d\u5bf9\u4ed8\u4ed6\u7684\u6cd5\u50ac\u547d\u7b26\u9053\u201c\u56e0\u4e3a\u6211\u4e5f\u662f\u4e2a\u5f88\u5fe0 \u5b9e\u7684\u4eba\u3002\u201d\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u5bf9\u4ec0\u9ebd\u5fe0\u5b9e\u201d\u50ac\u547d\u7b26\u9053\u8d22\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u7ea2\u5a76\u5b50\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u600e\u9ebd\u4ece\u6765\u4e0d\u8bf4\u6211\u4e5f\u5f88\u5fe0\u5b9e\u201c \u3000\u3000\u50ac\u547d\u7b26\u51b7\u51b7\u9053\u201c\u56e0\u4e3a\u4f60\u5bf9\u4f60\u81ea\u5df1\u4e5f\u4e0d\u5fe0\u5b9e\xb7\u4f60\u5e38\u5e38\u90fd\u5728\u51fa\u5356\u81ea\u5df1\u4f60\u81ea\u5df1\u51fa\u5356\u81ea\u5df1\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u6211\u5378\u4ece\u6765\u672b\u51fa\u5356\u8fc7\u4f60\u4e5f\u4ece\u6765\u6ca1\u6709\u9a97\u8fc7\u4f60\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u8fd8\u662f\u51b7\u51b7\u5730\u9053\u201c\u56e0\u4e3a\u4f11\u77e5\u9053\u6ca1\u6709\u4eba\u80fd\u9a97\u5f97\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u5411\u738b\u52a8\uff0c\u9053\u201c\u6240\u4ee5\u4f60\u5728\u6211\u9762\u524d\xb7\u4e5f\u662f\u4e2a\u8001\u5b9e\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u6ca1\u6709\u53cd\u5e94\u3002\u50ac\u547d\u7b26\u9053\u201c\u4f60\u8bf4\u4f60\u7684\u670b\u53cb\u90fd\u5df2\u8d70\u4e86\u4ed6\u4eec\u679c\u7136\u4e0d\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u738b\u52a8\u8fd8\u662f\u6ca1\u6709\u53cd\u5ea7\u3002 \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u73b0\u5728\u6211\u60f3\u77e5\u9053\u4f60\u662f\u5bf9\u94b1\u6bd4\u8f83\u5fe0\u5b9e\u8fd8\u662f\u5bf9\u6211\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u5f97\u770b\u60c5\u5f62\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u600e\u9ebd\u770b\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u8c08\u5730\u9053\uff0c\u201c\u901a\u5e38\u6211\u662f\u5bf9\u8def\u5fe0\u5b9e\u4e9b\uff0c\u4f46\u73b0\u5728\u662f\u5bf9\u4f60\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u5f88\u597d\uff0c\u62ff\u6765\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u62ff\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u4f60\u6709\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u72b9\u7591\uff0c\u7ec8\u4e8e\u4e0b\u4e86\u51b3\u5fc3\uff0c\u9053\u201c\u684c\u4e88\u4e0b\u9762\u6709\u51e0\u5757\u77f3\u6273\u662f\u677e\u7684\uff0c\u4e0b\u9762\u6709\u4e2a\u5730\u7a96\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u51b7\u7b11\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u770b\u4e0d\u51fa\u6765\u201d \u3000\u3000\u738b\u52a8\u901a\u201c\u4f60\u65e2\u5df2\u770b\u51fa\u6765\xb7\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u53bb\u62ff\u4e1c\u897f\u5c31\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u62a2\u9053\u201c\u6211\u53bb\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u50ac\u547d\u7b26\u9053\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u95ea\uff0c\u5df2\u63a0\u5230\u7ea2\u5a18\u5b50\u524d\u9762\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u5e73\u751f\u7b2c\u6b21\u8d70\u5728\u522b\u4eba\u524d\u9762\u4e5f\u662f\u6700\u5f8c\u6b21\u3002 \u3000\u3000\u4e00\u7ebf\u94f6\u5149\u6162\u6162\u7684\u81ea\u7ea2\u5a18\u5b50\u8896\u4e2d\u98de\u51fa\u6253\u5728\u4ed6\u8111\u5f8c\u7684\u7389\u6795\u5145\u4e0a\u3002 \u3000\u3000\u8fd9\u81f4\u547d\u7684\u51fb\u975e\u4f46\u4e0d\u5feb\u800c\u4e14\u5f88\u6162\uff0c\u4f69\u4ed6\u5374\u504f\u504f\u4e0d\u80fd\u95ea\u907f\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5c31\u5012\u4e86\u4e0b\u53bb\u3002\u6ca1\u6709\u62b5\u6297\u4e5f\u6ca1\u6709\u75db\u82e6\u3002 \u3000\u3000\u751a\u81f3\u8fde\u58f0\u755c\u90fd\u6ca1\u6709\u53d1\u51fa\u4e2a\u6d3b\u4eba\u5ffd\u7136\u95ee\u53d8\u6210\u4e86\u6b7b\u4eba\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u7adf\u6b7b\u5f97\u5982\u6b64\u5bb9\u6613\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5f53\u7136\u66f4\u60f3\u4e0d\u5230\u6740\u4ed6\u7684\u4eba\uff0c\u7adf\u662f\u7ea2\u5a18\u5b50\u3002 \u3000\u3000\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u53c8\u54cd\u8d77\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u8fd9\u6b21\u4f60\u603b\u8be5\u660e\u767d\u6211\u4e3a\u4ec0\u9ebd\u8981\u7b11\u4e86\u5427\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u662f\u7528\u4ec0\u9ebd\u6740\u4ed6\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u90a3\u5c31\u662f\u6211\u4ece\u4ed6\u90a3\u91cc\u5b66\u6765\u7684\u6e38\u9b42\u523a\u3002\u201d \u3000\u3000\u5979\u5403\u5403\u7684\u7b11\u63a5\u9053\u201c\u4ed6\u521a\u7528\u8d64\u7ec3\u86c7\u7684\u6bd2\u6bd2\u6b7b\u4e86\u8d64\u7ec3\u86c7\u6211\u7acb\u523b\u5c31\u7528\u4ed6\u7684\u523a\u523a\u6b7b\u4e86\u4ed6\uff0c\u8fd9\u9ebd\u6709 \u8da3\u7684\u4e8b\u6211\u60f3\u4e0d\u7b11\u90fd\u4e0d\u884c\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u53ea\u5947\u602a\u4ed6\u600e\u4f1a\u5c06\u8fd9\u6559\u7ed9\u4f60\u3002\u201d \u3000\u3000\u7ea2\u5a18\u4e8e\u9053\u6237\u56e0\u4e3a\u4ed6\u5e76\u6ca1\u6709\u5b8c\u5168\u5c06\u51b3\u7a8d\u6559\u7ed9\u6211\xb7\u77e5\u9053\u6211\u6c38\u8fdc\u5b66\u6c34\u597d\u7684\u3002\u2606 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u7684\u786e\u6ca1\u6709\u4ed6\u5feb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u90a3\u5dee\u5f97\u8fdc\u4e2b\u6240\u4ee5\u6211\u867d\u7136\u5b66\u4f1a\u5374\u8fd8\u662f\u6ca1\u6709\u7528\u6839\u672c\u4e0d\u80fd\u7528\u6765\u5bf9\u4ed8\u522b\u4eba\uff1b\u6e38\u9b42\u523a\u8fd8 \u662f\u4ed6\u7684\u72ec\u95e8\u5175\u5668\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e2\u7136\u6ca1\u6709\u7528\u4f53\u4f55\u5fc5\u5b66\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u5e76\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u7528\u53ea\u6709\u4e00\u79cd\u7528\u5904\u53ea\u80fd\u7528\u6765\u5bf9\u4ed8\u4e2a\u4eba\u3002\u3002 \u3000\u3000\u5929\u52a8\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4ed6\u81ea\u5df1\u3002\u201d \u3000\u3000\u738b\u52a8\u5947\u9053\u201c\u4f60\u4e0d\u80fd\u7528\u6765\u5bf9\u8ba8\u522b\u4eba\u5374\u80fd\u7528\u6765\u5bf9\u4ed8\u4ed6\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u5929\u4e0b\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u9ebd\u5947\u602a\u7684\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e0d\u6a2a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u54af\u54af\u7b11\u9053\u201c\u4f60\u4e0d\u6a2a\u7684\u4e8b\u8fd8\u591a\u54e9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u54e6\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u6545\u610f\u5355\u72ec\u7559\u4e0b\u4f60\u548c\u8d64\u7ec3\u86c7\u5728\u8d77\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4f60\u4eec\u6709\u673a\u4f1a\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u539f\u6765\u4f60\u662f\u6545\u610f\u8d70\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u6211\u5148\u6545\u610f\u8bf4\u51fa\u4ed6\u6700\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\u7136\u5f8c\u518d\u8d70\u5f00\u6545\u610f\u8981\u4ed6\u6c14\u5f97\u534a\u6b7b\u4f60\u770b\u5230\u90a3\u79cd\u673a\u4f1a \u5f53\u7136\u4e0d\u80af\u9519\u8fc7\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u77e5\u9053\u6211\u4f1a\u60f3\u6cd5\u5b50\u8bf4\u52a8\u4ed6\u8981\u4ed6\u51fa\u5356\u4f60\u4eec\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u5e76\u4e0d\u662f\u4f60\u8bf4\u52a8\u4ed6\u7684\xb7\u4ed6\u65e9\u5df2\u6709\u4e86\u8fd9\u610f\u601d\u53ea\u4e0d\u8fc7\u2019\u771f\u6ca1\u6709\u673a\u4f1a\u800c\u5df2\u3002\u201d\u738b\u52a8\u9053 \u201c\u4f60\u6545\u610f\u7ed9\u4ed6\u8fd9\u673a\u4f1a\u7136\u5f8c\u5c31\u53bb\u53eb\u5d14\u8001\u5927\u63d0\u9632\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u4e5f\u77e5\u9053\u5d14\u8001\u4eba\u65e9\u5df3\u67ef\u4e86\u5bf9\u4ed8\u4ed6\u7684\u6cd5\u4e8e\u4ed6\u53ea\u8981\u51fa\u624b\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u7b97\u5f97\u5f88\u6e2f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5a7f\u7136\u9053\u201c\u8fd9\u70b9\u6211\u5012\u4e5f\u4e0d\u5fc5\u592a\u8c26\u865a\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u5382\u53e3\u6c14\u4e45\u9053\u201c\u8fd9\u4ef6\u4e8b\u6211\u603b\u7b97\u660e\u767d\u4e86\xb7\u8fd8\u6709\u5462\uff1f\u201d\u7ea2\u5a18\u5b50\u7728\u7728\u773c\xb7\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053 \u5d14\u8001\u5927\u6700\u4eba\u7684\u79d8\u5bc6\u662f\u4ec0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7ea2\u5a18\u4e8e\u9053\u201c\u4ed6\u7684\u5c3d\u6735\u5e76\u4e0d\u7075\u7b80\u76f4\u8ddf\u804b\u5b50\u5dee\u4e0d\u8bb8\u591a\u3002\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u4f46\u6211\u8ddf\u4ed6\u8bf4\u8bdd\uff0c\u58f0\u97f3\u5e76\u4e0d\u592a\u5927\u4ed6\u5374\u90fd\u542c\u5f97\u89c1\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff0c\u201c\u90a3\u53ea\u56e0\u4e3a\u4ed6\u770b\u4f60\u7684\u5634\u5507\u52a8\u4f5c\u5c31\u80fd\u770b\u51fa\u70bc\u8bf4\u7684\u662f\u4ec0\u738b\u52a8\u5438\u9053\u201c\u8fd9\u7684\u786e\u662f\u4e2a\u79d8 \u5bc6\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u8fd9\u79d8\u5bc6\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u6ca1\u6709\u522b\u4eba\u77e5\u9053\u3002\u5c31\u56e0\u4e3a\u4ed6\u7684\u4e4c\u6735\u4e0d\u7075\xb7\u6240\u4ee5\u6c38\u8fdc\u4e0d\u80af\u8d70 \u5728\u4efb\u4f55\u4eba\u524d\u9762\u4ed6\u751f\u6015\u522b\u4eba\u4ece\u80cc\u5f8c\u6697\u7b97\u4ed6\u3002\u201d \u3000\u3000\u5979\u7b11\u5382\u7b11\u53c8\u9053\u201c\u8fd9\u5012\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u6bd4\u522b\u4eba\u5c0f\u5fc3\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u542c\u4e0d\u89c1\u6697\u5668\u7684\u98ce\u58f0\xb7\u82e5\u6709 \u4eba\u4ece\u80cc\u5f8c\u6697\u7b97\u4ed6\u4ed6\u6839\u672c\u6ca1\u6cd5\u5b50\u95ea\u907f\u3002\u201d \u3000\u3000\u571f\u52a8\u9053\u201c\u82e5\u662f\u98ce\u58f0\u5f88\u5c16\u9510\uff0c\u4ed6\u5f53\u7136\u8fd8\u662f\u542c\u5f97\u89c1\u7684\u4f46\u82e5\u6709\u4eba\u4ece\u80cc\u5f8c\u6162\u9992\u7684\u7ed9\u4ed6\u535c\u5b50\u90a3\u4ed6\u5c31 \u975e\u6b7b\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u2606\u4e00\u70b9\u4e5f\u4e0d\u9519\u6240\u4ee5\uff0c\u6211\u7528\u90a3\u6c38\u8fdc\u4e5f\u5b66\u4e0d\u597d\u7684\u6e38\u9b42\u523a\u6765\u5bf9\u4ed8\u4ed6\u53cd\u800c\u518d\u597d\u4e5f\u6ca1\u6709 \u4e86\u53fc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4e5f\u7b97\u51c6\u5382\u4ed6\u542c\u5230\u4e1c\u897f\u5728\u90a3\u91cc\u5c31\u5fcd\u4e0d\u4f53\u4f1a\u8d76\u5230\u524d\u9762\u53bb\u7684\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u82e5\u5728\u522b\u4eba\u9762\u524d\xb7\u4ed6\u4e5f\u8bb8\u8fd8\u80fd\u6c89\u5f97\u4f4f\u6c14\u8fd8\u4f1a\u63d0\u9632\u4f46\u613f\u6211\u5728\u8d77\u7684\u65f6\u5019\u4ed6\u603b\u662f\u4f1a\u6bd4 \u5e73\u65f6\u758f\u5ffd\u4e9b\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u56e0\u4e3a\u4ed6\u603b\u8ba4\u4e3a\u6211\u662f\u5728\u501a\u9760\u4ed6\uff0c\u603b\u8ba4\u4e3a\u4ed6\u82e5\u6b7b\u4e86\u6211\u4e5f\u6d3b\u4e0d\u5382\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u9053\u201c\u4ed6\u4e5f\u603b\u8ba4\u4e3a\u6ca1\u6709\u4eba\u80fd\u9a97\u8fc7\u4ed6\u2026\u2026\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u7684\u786e\u6ca1\u6709\u4eba\u80fd\u9a97\u8fc7\u4ed6\u4ed6\u81ea\u5df1\u80fd\u9a97\u8fc7\u81ea\u5df1\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u8bf4\u4ed6\u81ea\u5df1\u5728\u9a97\u81ea\u5df1\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5a9a\u7b11\u9053\u201c\u4e0d\u4f1a\u81ea\u6211\u9676\u9189\u7684\u7537\u4eba\uff0c\u5929\u5e95\u4e0b\u8fd8\u6ca1\u6709\u51e0\u4e2a\u7537\u4eba\u82e5\u662f\u4e0d\u81ea\u6211\u9676\u9189\uff0c\u5973\u4eba\u8fd8 \u80fd\u6df7\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u6c89\u9ed8\u4e86\u534a\u9610\u8c08\u8c08\u9053\u201c\u4f60\u7684\u786e\u7b97\u5f97\u5f88\u6e2f\xb7\u4e5f\u770b\u5f97\u5f88\u51c6\u3002\u201d\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u6211\u5374\u770b\u9519\u4e86 \u4f60\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53c8\u7b11\u9053\u201c\u6211\u59cb\u7ec8\u8ba4\u4e3a\u4f60\u662f\u4e0d\u4f1a\u8bf4\u8c0e\u7684\u60f3\u4e0d\u5230\u5f25\u82e5\u8bf4\u8d77\u8c0e\u8bdd\u6765\u7b80\u76f4\u53ef\u4ee5\u9a97\u6b7b\u4eba\u4e0d\u8d54 \u547d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u8bf4\u5382\u4ec0\u9ebd\u8c0e\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u8bf4\u4e1c\u897f\u5c31\u5728\u684c\u5b50\u4e0b\u9762\uff0c\u8fd9\u662f\u4e0d\u662f\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u4f46\u5374\u53ea\u6709\u6211\u4e2a\u4eba\u77e5\u9053\u4f60\u5728\u8bf4\u8c0e\u56e0\u4e3a\u4e16\u4e0a\u53ea\u6709\u6211\u624d\u77e5\u9053\u4e1c\u897f\u5230\u5e95\u85cf\u5728\u90a3 \u91cc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u4f60\u521a\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u4e1c\u897f\u662f\u6211\u62ff\u8d70\u7684\uff1f\u201d\u738b\u52a8\u9053\u201c\u6ca1 \u6709\u60f3\u5230\u3002\u201d \u3000\u3000\u4ed6\u6c89\u9ed8\u4e86\u4e2d\u664c\xb7\u53c8\u9053\u201c\u6211\u4ec0\u9ebd\u90fd\u6ca1\u6709\u60f3\u5230\uff0c\u4ec0\u9ebd\u90fd\u4e0d\u77e5\u9053\u6211\u53ea\u7ec6\u9053\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e00\u4e2a\u4eba\u4e0d\u80fd\u592a\u5f97\u610f\u65e0\u8bba\u8c01\u82e5\u89c9\u5f97\u6ca1\u6709\u4eba\u80fd\u9a97\u4ed6\uff0c\u4ed6\u5c31\u662f\u81ea\u5df1\u5728\u9a97\u81ea\u5df1\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7684\u751c\u7b11\u597d\u50cf\u6709\u70b9\u53d8\u5473\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u9053\u201c\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u8c08\u9053\u201c\u8fd9\u5308\u8bdd\u7684\u610f\u601d\u5c31\u662f\u8bf4\u4f60\u82e5\u80fd\u8bbe\u8ba1\u51fa\u4e2a\u5708\u5957\u6765\u5bb3\u522b\u4eba\u522b\u4eba\u5c31\u4e5f\u80fd\u8bbe\u8ba1\u51fa\u4e2a\u5708 \u5957\u6765\u5bb3\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u7ed3\u8bba\u901a\u5e38\u90fd\u5f88\u5c11\u4f1a\u9519\u7684\u3002 \u3000\u3000\u9551\u4e86\u7684\u901a\u5e38\u90c1\u4e0d\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u66f0\u5929\u3002 \u3000\u3000\u5973\u4eba\u5728\u767d\u5929\u770b\u6765\xb7\u603b\u663e\u5f97\u6bd4\u8f83\u82cd\u8001\u4e9b\u3001\u50a8\u4f53\u4e9b\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5df2\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u4f1a\u7b11\u7684\u5973\u4eba\u4e0d\u7b11\u7684\u65f6\u5019\xb7\u4e5f\u603b\u4f1a\u663e\u5f97\u82cd\u8001\u4e9b\u3001\u614c\u505c\u4e9b\u3002 \u3000\u3000\u6240\u4ee5\u7ea2\u5a18\u5b50\u73b0\u5728\u770b\u6765\uff0c\u51e0\u4e4e\u5df2\u63a5\u8fd1\u201c\u7ea2\u5a46\u5b50\u201d\u7684\u7a0b\u5ea6\u4e86\u3002 \u3000\u3000\u684c\u4e86\u4e0b\u6ca1\u6709\u5b9d\u85cf\u8fde\u4e2a\u7ef8\u677f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u5374\u6709\u4eba\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u738b\u52a8\u867d\u4e0d\u80fd\u52a8\uff0c\u4f46\u8fd9\u4e24\u4e2a\u4eba\u5374\u80fd\u52a8\u3002 \u3000\u3000\u4e2a\u52a8\u5f97\u6bd4\u8f83\u5feb\u4e2a\u52a8\u5f97\u6162\u4e9b\u3002 \u3000\u3000\u5feb\u7684\u662f\u9893\u4e03\u6162\u7684\u662f\u90ed\u5927\u8def\u3002 \u3000\u3000\u8c61\u90ed\u5927\u8def\u8fd9\u6837\u7684\u4eba\u5728\u670b\u53cb\u6709\u5371\u96be\u7684\u65f6\u5019\u4f60\u5c31\u7b97\u7528\u97ad\u5b50\u9010\u4ed6\u7528\u5200\u67b6\u5728\u4ed6\u8116\u5b50\u4e0a\u4ed6\u4e5f\u4e0d\u4f1a\u8d70 \u7684\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\uff0c\u7ea2\u5a18\u5b50\u624d\u53d1\u89c9\u81fc\u5df1\u6389\u4eba\u4e86\u5708\u5957\u3002 \u3000\u3000\u4f46\u662f\u600e\u9ebd\u6389\u5382\u53bb\u7684\u5462 \u3000\u3000\u5979\u5b8c\u5168\u4e0d\u77e5\u9053\xb7\u8fd9\u5708\u5957\u8fde\u70b9\u5f71\u5b50\u5979\u90fd\u6ca1\u6709\u770b\u5230\u3002 \u3000\u3000\u5c5e\u65bc\u91cc\u603b\u6709\u4e2a\u89d2\u843d\u5149\u7ebf\u6bd4\u8f83\u6697\u4e9b\uff0c\u8fd9\u89d2\u843d\u91cc\u901a\u5e38\u603b\u6709\u5f20\u6905\u4e8e\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u6162\u4fb5\u7684\u8d70\u8fc7\u53bb\u3001\u4fb5\u9992\u7684\u5750\u4e0b\u6765\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u62e6\u963b\u5979\u3002\u56e0\u4e3a\u5df2\u6ca1\u6709\u8fd9\u5fc5\u8981\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\u7ea2\u5a18\u4e8e\u5ffd\u7136\u9053\u201c\u738b\u52a8\xb7\u6211\u77e5\u9053\u4f60\u76f4\u662f\u4e2a\u5f88\u516c\u5e73\u7684 \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u201c\u4ed6\u672c\u6765\u5c31\u662f\u7684\u3002\u201d \u3000\u3000\u6709\u90ed\u5927\u8def\u5728\u7684\u65f6\u5019\u2018\u738b\u52a8\u8bf4\u8bdd\u7684\u673a\u4f1a\u516c\u5e73\u4e9b\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u8981\u600e\u9ebd\u6837\u516c\u5e73\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u521a\u6211\u5df2\u5c06\u6211\u7684\u5708\u5957\u8bf4\u4e86\u51fa\u6765\u73b0\u5728\u4f60\u5462\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u5bf9\u8c61\u662f\u738b\u52a8\uff0c\u9664\u4e86\u738b\u52a8\u5916\xb7\u5979\u6ca1\u6709\u770b\u8fc7\u522b\u4eba\u3002 \u3000\u3000\u71d5\u4e03\u7684\u773c\u775b\u5374\u5728\u77aa\u90ed\u5927\u8def\u3002 \u3000\u3000\u6240\u4ee5\u90ed\u5927\u8def\u7684\u7530\u4e5f\u53ea\u597d\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u738b\u52a8\u624d\u5f00\u53e3\u9053\u201c\u521a\u4f60\u662f\u4ece\u54ea\u91cc\u8bf4\u8d77\u7684\uff1f \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4ece\u6211\u7ed9\u4f60\u673a\u4f1a\u8ba9\u4f60\u5355\u72ec\u548c\u8d64\u7ec3\u86c7\u8bf4\u8bdd\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u9ebd\u8ddf\u4ed6\u8bf4\u90a3\u4e9b\u8bdd\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u4ece\u4f60\u4eec\u53c1\u4e2a\u4eba\u4e2d\xb7\u627e\u51fa\u662f\u8c01\u62ff\u8d70\u90a3\u4e9b\u4e1c\u897f\u7684\u4eba\u6765\u3002\u201d\u7ea2\u5a18\u5b50\u9053 \u201c\u4f60\u8ddf\u8d64\u7ec3\u86c7\u8bf4\u90a3\u4e9b\u8bdd\xb7\u4e3a\u7684\u5c31\u662f\u8981\u8bd5\u63a2\u4ed6\uff1f\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u4e0d\u9519\u4ed6\u82e5\u662f\u62ff\u8d70\u90a3\u4e9b\u4e1c\u897f\u7684\u4eba\u5c31\u7edd\u4e0d\u4f1a\u90a3\u9ebd\u505a\u4e01\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u90a3\u4eba\u4e0d\u662f\u5927\u6bbf\u8681\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u5047\u5982\u662f\u7684\u5c31\u4e0d\u4f1a\u90a3\u9ebd\u5192\u9669\u6709\u5382\u51e0\u5343\u4e07\u4e24\u8eab\u5bb6\u7684\u4eba\u5750\u5728\u5c4b\u69b4\u4e0b\u751f\u6015\u6709\u74e6\u4f1a\u6389\u4e0b \u6765\u6253\u7834\u4ed6\u7684\u5934\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u8bf4\u5f97\u7b80\u5355\u4e9b\uff1f\u2018\u5343\u91d1\u4e4b\u5b50\u5750\u4e0d\u4e56\u5802\u2019\u8fd9\u5207\u8bdd\u6211\u4e5f\u542c \u5f97\u61c2\u7684\u3002\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u77e5\u9053\u90a3\u5428\u4e1c\u897f\u85cf\u5904\u7684\u53ea\u6709\u4e94\u4e2a\u4eba\u9664\u6389\u4e8c\u4e2a\xb7\u5c31\u53ea\u5269\u4e0b\u4f60\u548c\u5d14\u8001\u4eba\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5343\u9053\u5224\u65e6\u4f60\u8fd8\u662f\u4e0d\u80fd\u786e\u5b9a\u6211\u548c\u5d14\u8001\u5927\u7a76\u7adf\u8c01\u624d\u662f\u771f\u6c47\u62ff\u8d70\u90a3\u4e9b\u4e1c\u897f\u7684\u4eba\u3002\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u90a3\u65f6\u6211\u8fd8\u4e0d\u80fd\u786e\u5b9a\u4f46\u6211\u5df2\u6709\u628a\u63e1\u8fdf\u65e9\u603b\u4f1a\u627e\u51fa\u5165\u7c73\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u771f\u6709\u628a\u63e1\uff1f\u201d\u738b\u52a8\u9053\u5634\u6211\u77e5\u9053\u8d64\u7ec3\u86c7\u7edd\u4e2d\u662f\u5d14\u8001\u5927\u7684\u654c\u624b\u53ea\u8981\u6709\u4e3e\u52a8\u5c31\u5fc5 \u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u5012\u4e5f\u770b\u5f97\u5f88\u51c6\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7b2c\u2606\uff0c\u6211\u77e5\u9053\u4f60\u548c\u5d14\u8001\u5927\u4e4b\u95ee\uff0c\u4e5f\u5fc5\u5b9a\u6709\u4e2a\u4eba\u8981\u6b7b\u7684\u3002\u201d\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0 \u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u65e0\u8bba\u8c01\u62ff\u8d70\u90a3\u4e9b\u4e1c\u897f\uff0c\u90fd\u7edd\u4e0d\u4f1a\u8ba9\u53e6\u4e2a\u4eba\u6d3b \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u4e94\u4e2a\u4eba\u4e4b\u4e2d\u53ea\u8981\u8fd8\u6709\u2500\u4e2a\u4eba\u6d3b\xb7\u4ed6\u5c31\u4e0d\u80fd\u5b89\u5fc3\u4e8b\u53d7\u90a3\u7b14\u8d22\u5bcc\u73b0\u5728\u4e94\u4e2a \u4eba\u7b49\u65bc\u53ea\u5269\u4e0b\u4e2a\u6b63\u662f\u4ed6\u6700\u597d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u4e8e\u53f9\u4e86\u53e3\u6c14\xb7\u9053\u201c\u8fd9\u673a\u4f1a\u7684\u786e\u592a\u597d\u5de5\u3002\u201d\u738b\u52a8\u9053\u201c\u4ed6\u5df3\u7b49\u95e8\u826f\u4e45\uff0c\u597d\u5bb9\u6613\u624d\u7b49\u5230\u8fd9 \u673a\u4f1a\uff0c\u5f53\u7136\u7edd\u4e0d\u80af\u8f7b\u6613\u9519\u8fc7\u201c \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u82e5\u6362\u5382\u4f60\uff0c\u4e5f\u5b9a\u820d\u4e0d\u5f97\u9519\u8fc7\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f55\u51b5\u4ee5\u524d\u4ed6\u8fd8\u53ef\u4ee5\u5c06\u8d23\u4efb\u63a8\u5728\u6211\u8eab\u4e0a\u73b0\u5728\u65e2\u5df2\u627e\u5230\u5382\u6211\u4ed6\u7684\u79d8\u5bc6\u5c31\u8fdf\u65e9\u8981\u88ab\u63ed \u7a7f\uff0c\u5c31\u7b97\u4ed6\u4e0d\u60f3\u6740\u522b\u4eba\xb7\u522b\u4eba\u4e5f\u5b9a \u3000\u3000\u7ea2\u5a18\u5b50\u7f13\u7f13\u9053\u201c\u6211\u672c\u6765\u7684\u786e\u4e0d\u613f\u4ed6\u851a\u627e\u5230\u4f60\xb7\u53ef\u662f\u2026\u3002\u201c \u3000\u3000\u5979\u7b11\u4e86\u7b11\u7b11\u5f97\u72e0\u51c4\u51c9\u8f7b\u8f7b\u7684\u63a5\u9053\u201c\u53ef\u662f\u6211\u5fc3\u91cc\u5374\u53c8\u5e0c\u671b\u4ed6\u4eec\u80fd\u627e\u5230\u4f60\u4e5f\u597d\u8ba9\u6211\u770b\u770b\xb7\u8fd9 \u51e0\u5929\u6765\u4f60\u5df2\u53d8\u6210\u4ec0\u9ebd\u6837\u5b50\u4e86\uff1f\u65e5\u5b50\u8fc7\u7684\u8fd8\u597d\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\u201c\u4ed6\u65e5\u5b50\u8fc7\u5f97\u5f88\u597d\u867d\u7136\u7a77\u70b9\u5374\u8fd8\u662f\u7167\u6837\u5f88\u5feb\u4e50\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6162\u6162\u7684\u70b9\u5382\u70b9\u5934\u8d2d\u8d3f\u9053\u201c\u4f60\u4eec\u7684\u786e\u90fd\u662f\u4ed6\u7684\u597d\u670b\u53cb\u7684\u786e\u662f\u6bd4\u4ed6\u4ee5\u524d\u90a3\u4e9b\u670b\u53cb\u597d\u5f97 \u591a\u3002\u201d \u3000\u3000\u5979\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u63a5\u9053\uff1a\u201c\u4f60\u7b97\u6765\u7b97\u53bb\u65e9\u5df3\u7b97\u51c6\u4e86\u6700\u5f8c\u5fc5\u5b9a\u53ea\u6709\u4e00\u4e2a\u4eba\u5269\u4e0b\u6765\xb7\u4e5f\u7b97\u51c6\u4e86 \u4ed6\u5c31\u662f\u62ff\u8d70\u90a3\u4e9b\u4e1c\u897f\u7684\u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u7b97\u6cd5\u672c\u6765\u5c31\u597d\u50cf\u52a0\u7b49\u65bc\u4e8c\u90a3\u9ebd\u7b80\u5355\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u96be\u9053\u4f60\u8d74\u7ea6\u53bb\u7684\u65f6\u5019\u5c31\u5df2\u7b97\u51c6\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u975e\u5982\u6b64\u6211\u4eec\u600e\u9ebd\u80fd\u653e\u5fc3\u8ba9\u4ed6\u53bb\u8d74\u7ea6\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53f9\u9053\u201c\u6211\u65e9\u5c31\u8be5\u60f3\u5230\u7684\u6211\u65e9\u5c31\u770b\u51fa\u4f60\u4eec\u4e2d\u662f\u90a3\u79cd\u770b\u89c1\u670b\u53cb\u6709\u5371\u9669\u5c31\u50a8\u5077\u6e9c\u8d70\u7684 \u4eba\u3002\u201d \u3000\u3000\u738b\u52a8\u201c\u4ed6\u4eec\u7684\u786e\u4e0d\u662f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u662f\u6211\u6709\u51e0\u70b9\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u53ef\u4ee5\u95ee\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u5dfe\u8ba1\u88ab\u64d2\uff0c\u96be\u9053\u4e5f\u662f\u6545\u610f\u7684\u201d \u3000\u3000\u738b\u52a8\u6de1\u6de1\u9053\u201c\u6211\u53ea\u77e5\u9053\u90a3\u5730\u65b9\u7edd\u4e0d\u4f1a\u7a81\u7136\u5192\u51fa\u4e2a\u8352\u575f\u6765\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u901a\u201c\u4f60\u6545\u610f\u88ab\u4ed6\u4eec\u6293\u4f50\u96be\u9053\u4e0d\u6015\u4ed6\u4eec\u5f53\u65f6\u5c31\u6740\u4e86\u4f60\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6015\u603b\u662f\u6709\u70b9\u4f2f\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u4f60\u8fd8\u662f\u7167\u6837\u8981\u53cb\u505a\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u6211\u5df2\u731c\u5230\u4f60\u4eec\u7edd\u4e0d\u4f1a\u5c31\u53ea\u4e3a\u5382\u8981\u6740\u6211\u800c\u6765\u5b9a\u8fd8\u53e6\u6709\u76ee\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u5df2\u731c\u51fa\u662f\u4ec0\u9ebd\u76ee\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u4f60\u5c31\u53eb\u4ed6\u4eec\u5728\u8fd9\u91cc\u7b49\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u6709\u628a\u63e1\u80fd\u8bf1\u6211\u4eec\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u738b\u52a8\u9053\u201c\u53ea\u6709\u70b9\u4e0d\u592a\u591a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u4f60\u8fd8\u662f\u8981\u8fd9\u9ebd\u6837\u505a\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e2a\u4eba\u82e5\u53ea\u80af\u505a\u7edd\u5bf9\u6709\u628a\u63e1\u7684\u4e8b\u90a3\u9ebd\u4ed6\u5c31\u8fde\u6837\u4e8b\u90fd\u505a\u4e0d\u6210\u3002\u201d\u519c\u4e50\u82f1\u96c4 \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u56e0\u4e3a\u4e16\u4e0a\u672c\u6ca1\u6709\u7edd\u5bf9\u6709\u628a\u63e1\u7684\u4e8b\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u8981\u4ed6\u4eec\u85cf\u5728\u8fd9\u91cc\uff0c\u96be\u9053\u5c31\u4e0d\u6015\u4e8b\u5148\u88ab\u6211\u4ecd\u53d1\u73b0\u9ebd\uff1f\u201d\u738b\u52a8\u9053\u201c\u8fd9\u79cd\u673a\u4f1a\u5f88 \u5c11\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u5f97\u5206\u51e0\u79cd\u60c5\u5f62\u6765\u8bf4\u3002\u201d \u3000\u3000\u7ea2\u59ec\u4e8e\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7b2c\u4e00\u79cd\u60c5\u51b5\u662f\u53c1\u4e2a\u4eba\u90fd\u540c\u5728\u8fd9\u91cc\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u201c\u54bd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u65f6\u53c1\u4e2a\u4eba\u4e4b\u4e2d\u81f3\u5c11\u6709\u4e24\u4e2a\u4eba\u4ee5\u4e3a\u85cf\u5b9d\u5c31\u5728\u684c\u4e0b\uff0c\u5f53\u7136\u7edd\u4e0d\u80af\u8ba9\u522b\u4eba\u5148\u5f97\u624b\u7684 \u5c31\u7b97\u6709\u4eba\u8981\u8fc7\u6765\u770b\u770b\u4e5f\u5fc5\u5b9a\u6709\u4eba\u4f1a\u963b\u6b62\u6240\u4ee5\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\xb7\u4ed6\u4eec\u5fc5\u662f\u5b89\u5168\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u7b2c\u4e8c\u79cd\u60c5\u51b5\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u65f6\u5df2\u53ea\u5269\u4e0b\u4e24\u4e2a\u4eba\u4e86\u5c31\u81c0\u5982\u8bf4\u662f\u4f60\u548c\u5d14\u8001\u5927\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e0d\u7528\u8b6c\u5982\xb7\u672c\u6765\u5c31\u662f\u6211\u4eec\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u5df2\u51b3\u5fc3\u4e0d\u8ba9\u5d14\u8005\u5927\u518d\u6d3b\uff0c\u4ed6\u5c31\u7b97\u60f3\u8981\u6765\u770b\u770b\xb7\u4f60\u4e5f\u5fc5\u5b9a\u4f1a\u5148\u4e0b\u624b\u6240\u4ee5 \u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4ed6\u4eec\u4e5f\u662f\u5b89\u5168\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u7b2c\u53c1\u79cd\u60c5\u51b5\u5f53\u7136\u662f\u5df2\u53ea\u5269\u4e0b\u6211\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u90a3\u70b9\u4f60\u7a74\u9053\u7684\u5730\u65b9\u8fd8\u662f\u88ab\u70b9\u4f4d\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u82e5\u5148\u53d1\u73b0\u4ed6\u4eec\u85cf\u5728\u90a3\u91cc\u5c82\u975e\u8fd8\u53ef\u4ee5\u5148\u628a\u4ed6\u4eec\u5c01\u6b7b\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\u201c\u53ef\u662f\u4f60\u660e\u77e5\u85cf\u5b9d\u4e0d\u5728\u90a3\u91cc\xb7\u600e\u9ebd\u4f1a\u8fc7\u53bb\u770b\uff1f\u4f60\u6839\u672c\u8fde\u6ce8\u610f\u90fd\u4e0d\u4f1a\u6ce8\u610f\u6240\u4ee5\u5728 \u8fd9\u79cd\u60c5\u51b5\u4e0b\xb7\u4ed6\u8ba2\u4e5f\u662f\u5b89\u5168\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u771f\u7684\u7b97\u5f97\u90a3\u9ebd\u7cbe\u90a3\u9ebd\u96be\uff1f\u201d\u738b\u52a8\u9053\u201c\u5047\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u63a5\u9053\u201c\u4eba\u7b97\u4e0d\u5982\u5929\u7b97\u8c01\u4e5f\u4e0d\u80fd\u5c06\u4ef6\u4e8b\u7b97\u5f97\u4e07\u65e0\u4e00\u5931\u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f46\u4f60\u8fd8\u662f\u5192\u8fd9\u4e2a\u9669\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u672c\u662f\u6211\u4ecd\u7684\u5b64\u6ce8\u4e00\u7ed1\u5458\u5f8c\u51fb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u4f60\u4eec\u7684\u80c6\u5b50\u4e5f\u672a\u514d\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u7684\u80c6\u4e8e\u5e76\u4e0d\u5927\u8ba1\u8c0b\u4e5f\u6ca1\u6709\u4f60\u4eec\u7cbe\u5bc6\u751a\u81f3\u8fde\u529b \u3000\u3000\u7ea2\u5a18\u4e88\u9053\u201c\u4f46\u5f90\u6292\u5374\u80a1\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u53ea\u56e0\u4e3a\u6211\u4eec\u6709\u6837\u4f60\u4eec\u6ca1\u6709\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u53cb\u60c5\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u63a5\u9053\u201c\u8fd9\u6837\u4e1c\u897f\u867d\u7136\u662f\u770b\u4e0d\u89c1\u6478\u4e0d\u4f46\u529b\u91cf\u4e4b\u5927\uff0c\u5374\u8db3\u4f60\u4eec\u6c38\u8fdc\u4e5f\u68a6\u60f3\u4e0d\u5230 \u7684\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5728\u542c\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u4e0d\u542c\u56e0\u4e3a\u8fd9\u4e9b\u8bdd\u90fd\u662f\u5979\u4ece\u6765\u6ca1\u542c\u89c1\u8fc7\u7684\u3002 \u3000\u3000\u5de5\u52a8\u9053\u6211\u4eec\u6562\u62fc\u547d\u6562\u5192\u9669\u4e5f\u56e0\u4e3a\u6211\u4eec\u77e5\u9053\u81ea\u5df1\u5e76\u4e0d\u662f\u5b64\u7acb\u65e0\u52a9\u7684\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u5411\u71d5\u4e03\u548c\u90ed\u5927\u8def\u63a5\u9053\u201c\u4e2a\u4eba\u82e5\u77e5\u9053\u81ea\u5df1\u65e0\u8bba\u5728\u5229\u9ebd\u60c5\u51b5\u767e\u90fd\u6709\u771f\u6b63\u7684\u670b\u53cb\u7ad9\u5728 \u4ed6\u4e00\u8fb9\u548c\u4ed6\u540c\u751f\u5171\u6b7b\u3001\u5171\u60a3\u96be\u4ed6\u7acb\u523b\u5c31\u4f1a\u53d8\u5f97\u6709\u4e86\u52c7\u6c14\uff0c\u6709\u4e86\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5782\u4e0b\u5934\u4eff\u6cb8\u53c8\u82cd\u8001\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u5de5\u52a8\u9053\u201c\u6211\u672c\u6765\u4e5f\u60f3\u8981\u4ed6\u4eec\u8d70\u4f46\u4ed6\u8d38\u53ea\u8bf4\u5382\u53e5\u8bdd\u5c31\u4ee4\u6211\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5fcd\u4e0d\u4f4f\u95ee\u201c\u4ed6\u4eec\u8bf4\u4e01\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u4ec0\u544a\u8bc9\u6211\u6211\u4eec\u8981\u6d3b\u5c31\u5feb\u5feb\u4e50\u4e50\u7684\u6d3b\u5728\u8d77\u8981\u6b64\uff0c\u4e5f\u75db\u75db\u5feb\u4fa0\u7684\u6b7b\u5728\u8d77\uff0c\u65e0\u8bba\u662f \u6b7b\u662f\u6d3b\uff0c\u90fd\u6ca1\u4ec0\u9ebd\u4e86\u4e0d\u8d77\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u662f\u7ea2\u5a18\u5b50\u4ece\u672a\u542c\u8bf4\u8fc7\u7684\u3002 \u3000\u3000\u5979\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u53ef\u662f\u73b0\u5728\u5979\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u5979\u770b\u9762\u524d\u7684\u53c1\u4e2a\u4eba \u3000\u3000\u4e2a\u6ee1\u8eab\u8d1f\u4f24\uff0c\u80fd\u7ad9\u5f97\u4f9d\u5df2\u5f88\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4e2a\u7ea4\u5f31\u7626\u5c0f\u663e\u5f97\u65e2\u9965\u997f\u3001\u53c8\u75b2\u5026\u3002 \u3000\u3000\u5c31\u8fde\u738b\u52a8\u4e5f\u6837\u3002 \u3000\u3000\u82e5\u8bf4\u53ea\u51ed\u8fd9\u53c1\u4e2a\u4eba\uff0c\u5c31\u80fd\u5c06\u8d64\u7ec3\u86c7\u3001\u50ac\u547d\u7b26\u548c\u7ea2\u5a18\u5b50\u7f6e\u4e8e\u6b7b\u5730\u8fd9\u79cd\u4e8b\u7b80\u76f4\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u4f46\u8fd9\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff0c\u73b0\u5728\u5374\u5df2\u6210\u4e3a\u4e8b\u5b9e\u3002 \u3000\u3000\u4ed6\u4eec\u51ed\u7684\u662f\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u7ea2\u5a18\u5b50\u5782\u4e0b\u5934\u7a81\u7136\u89c9\u5f97\u9635\u70ed\u8840\u4e00\u6d8c\u51e0\u4e4e\u5fcd\u4e0d\u3008\u5173\u8981\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u5979\u5df2\u4e0d\u77e5\u6709\u591a\u4e45\u672a\u66fe\u771f\u6b63\u6d41\u8fc7\u773c\u6cea\u51e0\u4e4e\u5df2\u5fd8\u4e86\u6d41\uff1f\u76ee\u662f\u4ec0\u9ebd\u611f \u3000\u3000\u71d5\u4e03\u76f4\u5728\u770b\u5979\uff0c\u76ee\u4e2d\u6e10\u6e10\u9732\u51fa\u540c\u60c5\u4e4b\u8272\u5ffd\u7136\u9053\u201c\u4f60\u4ece\u6765\u6ca1\u6709\u670b\u53cb\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6447\u6447\u5934\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u7edd\u4e0d\u662f\u56e0\u4e3a\u670b\u53cb\u4e0d\u8981\u4f60\u800c\u662f\u56e0\u4e3a\u4f60\u4e0d\u8981\u670b\u53cb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u53ef\u662f\u6211\u2026\u201d\u5c11 \u3000\u3000\u71d5\u4e03\u9053\u201c\u7528\u4f60\u81ea\u5df2\u7684\u771f\u5fc3\u8bda\u610f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u6253\u53c1\u4e2a\u4eba\u4e2d\u53ea\u8981\u6709\u534a\u5206\u771f\u5fc3\u8bda\u610f\uff0c\u4eca\u5929\u5c31\u5b9a\u8fd8\u5feb\u5feb\u4e50\u4e50\u7684\u6d3b\u3002\u201d \u3000\u3000\u90aa\u4e0d\u80dc\u6b63\u3002 \u3000\u3000\u6b63\u4e49\u5fc5\u5b9a\u6218\u80dc\u5f3a\u6743\u3002 \u3000\u3000\u4e3a\u9053\u4e49\u53cb\u60c5\u800c\u7ed3\u5408\u7684\u529b\u91cf\u5fc5\u5b9a\u6218\u80dc\u4e3a\u5229\u5bb3\u800c\u52fe\u7ed3\u7684\u66b4\u529b\u3002\u771f\u7406\u4e0e\u53cb\u60c5\u5fc5\u5b9a\u6c34\u8fdc\u5b58\u5728\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u53e3\u53f7\u3002\u7edd\u4e0d\u662f\u3002 \u3000\u3000\u4f60\u4eec\u82e5\u542c\u8bf4\u90ed\u5927\u8def\u548c\u738b\u52a8\u4ed6\u4eec\u7684\u4e8b\xb7\u5c31\u4f1a\u77e5\u9053\u8fd9\u7edd\u4e0d\u662f\u53e3\u53f7\uff0c\u5c31\u7b97\u4f60\u5546\u6ca1\u542c\u8bf4\u4e5f\u65e0\u59a8\u3002 \u3000\u3000\u56e0\u4e3a\u4e16\u4e0a\u8c61\u7ff1\u5927\u8def\u548c\u738b\u52a8\u8fd9\u6837\u7684\u4eba\u968f\u65f6\u968f\u5730\u90fd\u5b58\u5728\u7684\u53ea\u8981\u4f60\u80af\u7528\u4f60\u7684\u8bda\u5fc3\u8bda\u610f\u53bb\u5bfb\u627e\u5c31 \u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u8fd9\u6837\u7684\u670b\u53cb\u3002\u6625\u5230\u4eba\u95ee\u65e9\u4e0a\u3002 \u3000\u3000\u91d1\u9ec4\u8272\u7684\u9633\u5149\u7a7f\u7834\u4e91\u5c42\u7167\u4e0a\u7a97\u6237\u3002 \u3000\u3000\u98ce\u5439\u8fc7\u7a97\u6237\uff0c\u6d41\u52a8\u81ea\u8fdc\u5c71\u5e26\u6765\u7684\u6e05\u65b0\u82ac\u82b3\u3002 \u3000\u3000\u65e9\u4e0a\u6c38\u8fdc\u662f\u53ef\u7231\u7684\u6c38\u8fdc\u5145\u6ee1\u4e86\u5e0c\u671b\u3002 \u3000\u3000\u4f46\u4f60\u4e5f\u7528\u4e0d\u5492\u8bc5\u591c\u7684\u9ed1\u6697\u82e5\u6ca1\u6709\u9ed1\u6697\u7684\u4e11\u95fb\u53c8\u600e\u80fd\u663e\u5f97\u51fa\u5149\u660e\u7684\u53ef\u7231\uff1f \u3000\u3000\u9999\u5927\u3002 \u3000\u3000\u91d1\u9ec4\u8272\u7684\u9633\u5149\u7a7f\u7834\u4e91\u5c42\xb7\u7167\u535c\u6821\u5934\u3002 \u3000\u3000\u98ce\u5439\u8fc7\u67d4\u679d\u679d\u5934\u4e0a\u5df2\u62bd\u51fa\u4e86\u51e0\u82bd\u65b0\u7eff\u3002 \u3000\u3000\u6eb6\u5316\u7684\u79ef\u96ea\u4e2d\u5df2\u6d41\u52a8\u6625\u5929\u7684\u6e05\u65b0\u82ac\u52b3\u3002 \u3000\u3000\u6625\u5929\u6c38\u8fdc\u662f\u53ef\u7231\u66f2\u6c38\u8fdc\u5145\u6ee1\u4e86\u5e0c\u671b\u3002 \u3000\u3000\u4f46\u4f60\u4e5f\u7528\u4e0d\u5492\u56e0\u51ac\u7684\u4e25\u9177\u82e5\u6ca1\u6709\u4e25\u7a83\u7684\u5bd2\u51b7\uff0c\u53c8\u600e\u80fd\u663e\u5f97\u51fa\u6625\u5929\u7684\u6e29\u6696\uff1f \u3000\u3000\u6625\u5929\u7684\u65e9\u4e0a\u3002 \u3000\u3000\u6797\u592a\u5e73\u6b63\u671f\u5728\u7a97\u4e0b\uff0c\u7a97\u65bc\u662f\u5f00\u7684\xb7\u6709\u98ce\u5439\u8fc7\u7684\u65f6\u5019\u5c31\u53ef\u4ee5\u95fb\u5230\u98ce\u81ea\u8fdc\u5c71\u5e26\u6765\u7684\u82ac\u82b3\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u62ff\u5377\u4e66\uff0c\u773c\u775b\u5374\u5728\u51dd\u89c6\u7a97\u5916\u679d\u5934\u4e0a\u7684\u7eff\u82bd\u3002 \u3000\u3000\u5c31\u8eba\u5728\u8fd9\u91cc\u4ed6\u5df2\u736d\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4ed6\u53d7\u7684\u4f24\u5e76\u4e0d\u6bd4\u90ed\u5927\u8def\u91cd\u4e2d\u7684\u6bd2\u4e5f\u5e76\u4e0d\u6bd4\u90ed\u5927\u8def\u6df1\u3002 \u3000\u3000\u53ef\u662f\u90ed\u5927\u8def\u5df2\u53ef\u5230\u8857\u4e0a\u4e70\u9152\u7684\u65f6\u5019\u4ed6\u5374\u8fd8\u53ea\u80fd\u5728\u5e8a\u4e0a\u8eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u89e3\u836f\u6765\u5f97\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u6bd2\u5df2\u4fb5\u5165\u4e86\u4ed6\u7684\u5185\u810f\u4fb5\u8680\u4e86\u4ed6\u7684\u4f53\u529b\u3002 \u3000\u3000\u4eba\u751f\u672c\u5c31\u662f\u8fd9\u6837\u4e88\u7684\u6709\u5e78\u4e0e\u4e0d\u5e78\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u57cb\u6028\u3002 \u3000\u3000\u4ed6\u5df2\u80fd\u4e86\u89e3\xb7\u5e78\u4e0e\u4e0d\u5e78\u4e5f\u4e0d\u662f\u7edd\u5bf9\u7684\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5728\u75c5\u5374\u4e5f\u56e0\u6b64\u80fd\u4eab\u53d7\u5230\u75c5\u4e2d\u90a3\u2500\u4efd\u8c08\u8c08\u7684\u95f2\u9602\u7684\xb7\u5e26\u51e0\u5206\u6e05\u6101\u7684\u5e7d\u8da3\u3002 \u3000\u3000\u4f55\u51b5\u8fd8\u6709\u670b\u53cb\u4eec\u7167\u987e\u548c\u5173\u5fc3\u5462\u3002 \u3000\u3000\u4eba\u751f\u672c\u6709\u5f88\u591a\u79cd\u4e50\u8da3\uff0c\u662f\u5b9a\u8981\u4f60\u653e\u5f00\u80f8\u895f\u653e\u5f00\u773c\u754c\u5f8c\u624d\u80fd\u9884\u7565\u5230\u7684\u3002 \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u95e8\u8f7b\u8f7b\u7684\u88ab\u63a8\u5f00\u4e86\u4e2a\u4eba\u8f7b\u8f7b\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e2a\u5e03\u8863\u94a1\u88d9\u4e0d\u65bd\u8102\u7c89\xb7\u663e\u5f97\u5f88\u4e7e\u51c0\u3001\u5f88\u7d20\u7684\u5987\u4eba\u3002 \u3000\u3000\u5979\u624b\u91cc\u6258\u4e2a\u6728\u76d8\uff0c\u76d8\u4e0a\u6709\u7897\u70ed\u817e\u817e\u7684\u5f3a\u4e24\u789f\u6e05\u6de1\u7684\u5c0f\u83dc \u3000\u3000\u6797\u592a\u5e73\u4f3c\u4e4e\u5df2\u7761\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u7684\u8d70\u8fdb\u6765\xb7\u5c06\u6728\u76d8\u653e\u4e0b\xb7\u50cf\u662f\u751f\u4f2f\u60ca\u9192\u4e86\u6797\u592a\u5e73\u5145\u5230\u8f7b\u8f7b\u7684\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u60f3\u4e86\u60f3\u4e4b\u76fe\u5979\u4e49\u5b9a\u8fdb\u6765\u6258\u8d77\u6728\u76c8\u53ea\u9611\u5979\u751f\u4f2f\u7ca5\u51c9\u4e86\u5bf9\u6401\u4eba\u4e0d\u5b9c\u3002 \u3000\u3000\u8fd9\u5987\u4eba\u662f\u8c01\uff1f \u3000\u3000\u5979\u505a\u4e8b\u5b9e\u5728\u592a\u5468\u5230\u592a\u5c0f\u5fc3\u3002 \u3000\u3000\u79ef\u96ea\u6eb6\u5c3d\u5927\u5730\u5df2\u5728\u9633\u5149\u4e0b\u6e10\u6e10\u53d8\u5f97\u6e29\u6696\u4e7e\u71e5\u3002 \u3000\u3000\u9662\u5b50\u91cc\u7684\u5730\u4e0a\u6446\u53c1\u5f20\u85e4\u6905\u5c40\u95f2\u68cb\u3002 \u3000\u3000\u738b\u52a8\u548c\u71d5\u4e03\u6b63\u5728\u4e0b\u68cb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u65c1\u8fb9\u770b\u5ffd\u800c\u5f04\u4e54\u6905\u535c\u7684\u6563\u85e4\u5ffd\u6652\u7ad9\u8d77\u6765\u8d70\u51e0\u6b65\u5ffd\u800c\u4f38\u957f\u8116\u5b50\u53bb\u9647\u671b\u5899\u5916\u7684\u8fdc \u5c71\u3002 \u3000\u3000\u603b\u4e4b\u4ed6\u5c31\u662f\u5750\u4e0d\u4f4f\u3002 \u3000\u3000\u8981\u4ed6\u9759\u9759\u7684\u5750\u5728\u90a3\u91cc\u4e0b\u68cb\u9664\u975e\u780d\u65ad\u4ed6\u7684\u6761\u817f\uff0c\u8981\u4ed6\u9759\u9759\u7684\u5750\u5728\u65c1\u8fb9\u770b\u522b\u4eba\u4e0b\u68cb\uff0c\u7b80\u76f4\u8981 \u4ed6\u7684\u547d\u3002 \u3000\u3000\u73b0\u5728\u738b\u52a8\u7684\u767d\u5e72\u5df2\u5c06\u9ed1\u6a21\u5c01\u6b7b\u71d5\u4e03\u624b\u91cc\u62c8\u679a\u9ed1\u5b50\u6b63\u5728\u5927\u4f24\u8111\u989d\uff0c\u6b63\u4e0d\u77e5\u8be5\u600e\u9ebd\u6837\u505a\u4e24\u4e2a \u773c\u5c06\u8fd9\u76d8\u68cb\u6551\u6d3b\u3002 \u3000\u3000\u90ed\u5927\u8def\u76f4\u5728\u4ed6\u65c1\u8fb9\u6643\u6765\u6643\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u77aa\u4e86\u4ed6\u773c\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u80fd\u4e0d\u80fd\u5750\u4e0b\u6765\u5b89\u9759\u4e0b\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u71d5\u4e03\u6068\u6839\u9053\u201c\u4f60\u4e0d\u505c\u7684\u5728\u8fd9\u91cc\u5435\u5435\u5f97\u4eba\u5fc3\u70e6\u610f\u4e71\xb7\u600e\u9ebd\u80fd\u4e0b\u68cb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8fde\u8bdd\u90fd\u6ca1\u8bf4\u4e00\u53e5\u51e0\u65f6\u5435\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8fd9\u6837\u8fd8\u4e0d\u7b97\u5435\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u6837\u5b50\u5c31\u7b97\u5435\uff1f\u738b\u8001\u5927\u600e\u9ebd\u6ca1\u6709\u602a\u6211\u5435\u4ed6\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u6de1\u9053\u201c\u56e0\u4e3a\u8fd9\u76d8\u68cb\u6211\u5df2\u5feb\u8d62\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u6253\u52ab\u8fd8\u6ca1\u6709\u6253\u5b8c\xb7\u8c01\u4f0a\u548b\u8d62\u8fd8\u662f\u4e0d\u5b9a\u54e9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5b9a\u3002\u201d \u3000\u3000\u71d5\u4e03\u77aa\u773c\u9053\u2018\u201c\u4f60\u61c2\u5f97\u4ec0\u9ebd\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u867d\u7136\u4e0d\u6a2a\u4e0b\u68cb\u4f97\u5374\u61c2\u5f97\u8f93\u4e86\u68cb\u7684\u4eba\xb7\u6bdb\u75c5\u603b\u662f\u7279\u522b\u591a\u4e9b\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8c01\u7684\u6bdb\u75c5\u591a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6240\u4ee5\u8f93\u68cb\u7684\u4eba\u5b9a\u662f\u4f60\u3002\u201d \u3000\u3000\u738b\u52a8\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5bb9\u521a\u9732\u51fa\u6765\xb7\u7a81\u53c8\u50f5\u4f4f\u3002 \u3000\u3000\u90a3\u9752\u8863\u5987\u4eba\u6b63\u7a7f\u788e\u77f3\u5c0f\u8def\u5b9a\u6765\u624b\u6258\u7684\u6728\u76d8\u4e0a\u6709\u53c1\u7897\u70ed\u6761\u3002 \u3000\u3000\u738b\u52a8\u626d\u8fc7\u5382\u5934\u4e0d\u53bb\u770b\u5979\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u7b2c\u76cf\u83dc\u5c31\u9001\u5230\u4ed6\u9762\u524d\u67d4\u58f0\u9053\u201c\u8fd9\u662f\u4f60\u6700\u559c\u6b22\u559d\u7684\u9999\u7247\u521a\u6ce1\u597d\u7684\u3002 \u3000\u3000\u738b\u52a8\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u9053\u201c\u4f60\u82e5\u60f3\u559d\u9f99\u4e95\u6211\u8fd8\u53ef\u4ee5\u518d\u53bb\u6ce1\u3001\u58f6\u3002\u201d \u3000\u3000\u738b\u52a8\u8fd8\u662f\u6295\u542c\u89c1\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u5c06\u76cf\u8336\u8f7b\u8f7b\u653e\u5230\u4ed6\u9762\u524d\u9053\u201d\u4eca\u5929\u4e2d\u5348\u4f60\u60f3\u5403\u70b9\u4ec0\u9ebd\uff1f\u5305\u997a\u5b50\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u7ad9\u8d77\u6765\u8fdc\u8fdc\u7684\u8d70\u5f00\u4e86\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u770b\u4e86\u4ed6\u7684\u80cc\u5f71\uff0c\u53d1\u4e86\u534a\u5929\u6014\u4eff\u4f5b\u5e26\u6ee1\u6000\u59d4\u66f2\uff0c\u6ee1\u80f6\u5e7d\u6028\u3002 \u3000\u3000\u90ed\u5927\u8def\u6e29\u4e0d\u4f4f\u9053\uff1a\u201c\u5305\u997a\u5b50\u597d\u6781\u4e86\uff0c\u6015\u592a\u9ebb\u70e6\u4e86\u4e9b\uff0c\u201d \u3000\u3000\u9752\u8863\u5987\u4eba\u8fd9\u624d\u56de\u8fc7\u5934\u6765\u4fb5\u6162\u7684\u8d70\u56de\u53bb\u8d70\u4e86\u4e24\u6b65\u53c8\u5fcd\u4e0d\u4f4f\u56de\u8fc7\u5934\u770b\u4e86\u738b\u52a8\u773c\u3002 \u3000\u3000\u738b\u52a8\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u6709\u611f\u89c9\u5230\u5979\u8fd9\u4eba\u5b58\u5728\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u5782\u535c\u5934\xb7\u7ec8\u4e8e\u8d70\u4e86\u867d\u7136\u663e\u5f97\u5f88\u96be\u53d7\uff0c\u5374\u70b9\u4e5f\u6ca1\u6709\u57cb\u6028\u8d23\u602a\u4e4b\u610f\u3002 \u3000\u3000\u738b\u52a8\u65e0\u8bba\u600e\u9ebd\u6837\u5bf9\u5979\u5979\u90fd\u53ef\u4ee5\u9006\u6765\u987a\u53d7 \u3000\u3000\u8fd9\u53c8\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f \u3000\u3000\u90ed\u5927\u8def\u76ee\u9001\u5979\u8d70\u4eba\u5c4b\u5b50\u5f8c\xb7\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u8fd9\u4e2a\u4eba\u53d8\u5f97\u771f\u5feb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u77a9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u522b\u4eba\u8bf4\uff0c\u6c5f\u5c71\u6613\u6539\xb7\u672c\u6027\u96be\u79fb\uff0c\u6211\u770b\u8fd9\u53e5\u8bdd\u5e76\u4e0d\u592a\u6b63\u786e\xb7\u5979\u8fd9\u4e2a\u4eba\u5c82\u975e\u5c31\u5f7b \u5f7b\u5e95\u5e95\u7684\u5b8c\u5168\u53d8\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u5979\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ea5\u4eba\u4e5f\u662f\u4eba\u8fd9\u53e5\u8bdd\u5c82\u975e\u662f\u4f60\u5e38\u5e38\u8bf4\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f46\u5973\u4eba\u5230\u5e95\u8fd8\u8ddf\u7537\u4eba\u4e0d\u540c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5ce8\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5973\u4eba\u4e3a\u4e86\u4e2a\u5979\u6240\u559c\u6b22\u7684\u7537\u4eba\uff0c\u662f\u53ef\u4ee5\u5b8c\u5168\u5c06\u81ea\u5df1\u6539\u53d8\u7684\uff0c\u7537\u4eba\u4e3a\u5382\u559c\u6b22\u7684\u5973\u4eba \u5c31\u7b97\u80fd\u6539\u53d8\u2500\u6bb5\u65f6\u5019\u6539\u53d8\u7684\u4e5f\u662f\u8868\u9762\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u5382\u60f3\xb7\u9053\u201c\u8fd9\u8bdd\u542c\u6765\u597d\u50cf\u4e5f\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5f53\u7136\u6709\u9053\u7406\u6211\u8bf4\u7684\u8bdd\uff0c\u53e5\u65ec\u90fd\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u5e7f\u3002 \u3000\u3000\u71d5\u4e03\u77aa\u773c\u9053\u201c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u4f60\u4e0d\u627f\u8ba4\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u627f\u8ba4\u65e0\u6ca6\u4f60\u8bf4\u4ec0\u9ebd\u6211\u90fd\u6ca1\u6709\u4e0d\u540c\u610f\u7684\u3002\u201d \u3000\u3000\u8fd9\u5c31\u53eb\u7269\u964d\u7269\u9752\u83dc\u914d\u8c46\u8150\u3002 \u3000\u3000\u90ed\u5927\u8def\u5929\u4e0d\u6015\xb7\u5730\u4e0d\u6015\u4f46\u4e00\u89c1\u5230\u85cf\u4e03\xb7\u4ed6\u5c31\u6ca1\u6cd5\u5b50\u4e86\u3002\uff01"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e00\u7ae0",content:(0,s.jsx)(s.Fragment,{children:"\u8fd9\u65f6\u4e0a\u52a8\u624d\u8d70\u56de\u6765\u5750\u5382\u8fd8\u662f\u8138\u8272\u94c1\u9752\u2606 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4eba\u5bb6\u597d\u5fc3\u9001\u8336\u6765\u7ed9\u4f60\u4f60\u80fd\u4e0d\u80fd\u5bf9\u5979\u597d\u4e00\u70b9\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u80fd\u3002\u201d\u90ed\u5927\u8def\u9053\u62a5\u5bfc\u4f60\u771f\u7684\u770b\u89c1\u5979\u5c31\u751f\u6c14\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u7b97\u7ea2\u8d29\u4e88\u4ee5\u524d\u4e0d\u592a\u597d\u4f46\u73b0\u5728\u5979\u5df2\u7ecf\u4e0d\u662f\u7ea2\u5a18\u5b50\u5382\u4f60\u96be\u9053\u770b\u4e0d\u51fa\u5979\u5df2\u5b8c\u5168\u53d8 \u4e2b\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7acb\u523b\u5e2e\u8154\u9053\u201c\u662f\u5440\xb7\u73b0\u5728\u770b\u89c1\u5979\u7684\u4eba\u6709\u8c01\u80fd\u60f3\u5f97\u5230\u5979\u5c31\u662f\u90a3\u6551\u8425\u6551\u96be\u7684\u7ea2\u5a18\u5b50\uff1f\u201d \u3000\u3000\u7684\u786e\u6ca1\u6709\u4eba\u80fd\u60f3\u5230\u3002 \u3000\u3000\u90a3\u53c8\u5c0f\u5fc3\u3001\u53c8\u5468\u5230\u3001\u53c8\u6e29\u67d4\u3001\u53c8\u80fd\u5fcd\u53d7\u7684\u9752\u8863\u5987\u4eba\xb7\u5c45\u7136\u5c31\u662f \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u8c01\u80fd\u591f\u60f3\u5f97\u5230\uff0c\u6211\u60c5\u613f\u5728\u5730\u4e0a\u722c\u5708\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4e5f\u722c\u3002\u201d \u3000\u3000\u738b\u52a8\u677f\u80a1\xb7\u51b7\u51b7\u9053\u201c\u4f60\u4eec\u82e5\u8981\u6ee1\u5730\u4e71\u722c\xb7\u4e5f\u662f\u4f60\u4eec\u66f2\u4e8b\u6211\u7ba1\u4e0d\u3002\u201d \u3000\u3000\u71d5\u4e03\u901a\u201c\u53ef\u662f\u4f60\u2026\u3002\u201c\u738b\u52a8\u8c0a\u7b3c\u5c40\u68cb\u4f60\u8ba4\u8f93\u4e86\u6ca1\u6709\uff1f\u201d\u9893\u4e03\u9053\u201c\u5f53\u7136\u4e0d\u8ba4\u8f93\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u597d\u90a3\u9ebd\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u5feb\u4e0b\u8c0b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u664c\u8d3f\u9053\u201c\u770b\u6765\u8fd9\u4eba\u7684\u6bdb\u75c5\u6bd4\u71d5\u4e03\u8fd8\u5927\u8fd9\u76d8\u68cb\u4ed6\u4e0d\u8f93\u624d\u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u8fd9\u5c40\u68cb\u679c\u7136\u662f\u738b\u52a8\u8f93\u4e86\u3002 \u3000\u3000\u4ed6\u4e2d\u6765\u660e\u660e\u5df2\u5c06\u71d5\u4e03\u7684\u68cb\u5c01\u6b7b\uff0c\u4f46\u4e0d\u77e5\u600e\u9ebd\u6765\uff0c\u4ed6\u7ade\u83ab\u540d\u5176\u5999\u7684\u8f93\u4e86\u3002 \u3000\u3000\u8f93\u5382\u4e03\u9897\u4e8e\u3002 \u3000\u3000\u571f\u52a8\u770b\u68cb\u6444\u53d1\u4e86\u534a\u5929\u6014\u5ffd\u7136\u9053\u201c\u6765\uff0c\u518d\u4e0b\u5c40\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u6765\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u975e\u6765\u4e0d\u53ef\uff0c\u4e00\u5c40\u68cb\u600e\u9ebd\u80fd\u5b9a\u8f93\u8d62\uff1f\u201d \u3000\u3000\u852c\u4e03\u9053\u201c\u518d\u5382\u5341\u5c40\uff0c\u4f60\u8fd8\u662f\u8981\u8f93\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8c01\u8bf4\u7684\u201d \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u201c\u6211\u8bf4\u7684\u56e0\u4e3a\u4f60\u4e0d\u4f46\u6709\u6bdb\u75c5\uff0c\u800c\u4e14\u6bdb\u75c5\u8fd8\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u738b\u52a8\u7ad9\u8d77\u6765\u5c31\u8981\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u62c9\u4f73\u4e86\u4ed6\u5927\u58f0\u9053\u201c\u4e3a\u4ec0\u9ebd\u6211\u4ecd\u4e00\u63d0\u8d77\u8fd9\u4ef6\u4e8b\xb7\u4f60\u5c31\u8981\u843d\u8352\u800c\u9003\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u9003\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u5c31\u5f97\u95ee\u4f60\u81fc\u5df1\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u60a0\u7136\u9053\u201c\u662f\u5440\u4e2a\u4eba\u5fc3\u91cc\u82e5\u6ca1\u6709\u4e8f\u5fc3\u7684\u5730\u65b9\u522b\u4eba\u65e0\u8bba\u8bf4\u4ec0\u9ebd\u4ed6\u90fd\u7528\u4e0d\u9003\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u77aa\u4ed6\u8861\xb7\u5ffd\u7136\u7528\u529b\u5750\u4e0b\u53bb\u9053\u201c\u597d\u4f60\u4eec\u8981\u8bf4\u3002\u5927\u5bb6\u5c31\u8bf4\u4e2a\u6e05\u695a\u6211\u5fc3\u91cc\u6709\u4ec0\u9ebd\u4e8f\u5fc3\u7684\u5730 \u65b9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5148\u95ee\u4f60\uff0c\u662f\u8c01\u8981\u5979\u7559\u4e0b\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u7ba1\u662f\u8c01\u53cd\u6b63\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8bf4\u9053\u201c\u5f53\u7136\u4e0d\u662f\u4f60\uff0c\u4e5f\u4e0d\u662f\u6211\xb7\u66f4\u4e0d\u662f\u9893\u4e03\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u8981\u7ea2\u5a18\u5b50\u7559\u4e0b\u6765\u662f\u5979\u81ea\u5df1\u613f\u610f\u7559\u4e0b\u6765\u7684\u3002 \u3000\u3000\u5979\u672c\u6765\u53ef\u4ee5\u8d70\u3002 \u3000\u3000\u82e5\u6362\u4e86\u522b\u4eba\xb7\u5728\u90a3\u79cd\u60c5\u51b5\u4e0b\xb7\u5b9a\u4f1a\u5148\u903c\u5979\u8bf4\u51fa\u90a3\u6279\u85cf\u5b9d\u7684\u4e0b\u843d\uff0c\u7136\u5f8c\u5f88\u53ef\u80fd\u5c31\u6740\u4e86\u5979\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u4ed6\u6c40\u4e0d\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u7edd\u4e0d\u80af\u6740\u4e2a\u5df2\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\u7684\u4eba\uff0c\u66f4\u4e0d\u613f\u6740\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u5c24\u5176\u4e0d\u4f1a\u6740\u4e2a\u4e0d\u4f46\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\xb7\u66f4\u6709\u6094\u7f6a\u4e4b\u5fc3\u7684\u5973\u4eba\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u770b\u5f97\u51fa\u7ea2\u5a18\u5b50\u5df2\u88ab\u611f\u52a8\u4e86\u88ab\u4ed6\u8861\u90a3\u79cd\u4f1f\u5927\u7684\u53cb\u8c0a\u611f\u52a8\u5382\u3002 \u3000\u3000\u5979\u5df2\u660e\u767d\u4e16\u4e0a\u6700\u75db\u82e6\u7684\u4e8b\u5e76\u4e0d\u662f\u6ca1\u6709\u94b1\u800c\u662f\u6ca1\u6709\u670b\u53cb\u3002 \u3000\u3000\u5979\u5ffd\u7136\u89c9\u5f97\u4ee5\u524d\u6240\u505a\u7684\u90a3\u4e9b\u4e8b\uff0c\u6240\u5f97\u7684\u552f\u4ee3\u4ef7\u5c31\u662f\u5b64\u72ec\u548c\u5bc2 \u3000\u3000\u56fd\u4e3a\u5979\u5df2\u662f\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u5df2\u80fd\u4e86\u89e3\u5b64\u72ec\u548c\u5bc2\u5bde\u662f\u591a\u9ebd\u53ef\u65e7\u7684\u4e8b\u3002 \u3000\u3000\u5979\u4e5f\u5df4\u4e86\u89e3\u4e16\u4e0a\u6240\u6709\u7684\u8d22\u5bcc\u4e5f\u586b\u4e0d\u6ee1\u4e2a\u4eba\u5fc3\u91cc\u7684\u7a7a\u865a\u3002 \u3000\u3000\u90a3\u7edd\u4e0d\u662f\u4e2a\u5341\u516b\u4e5d\u5c81\u7684\u5973\u5b69\u5b50\u6240\u80fd\u4e86\u89e3\u7684\u3002 \u3000\u3000\u6240\u4ee5\u7ea2\u5a18\u5b50\u6ca1\u6709\u8d70\u3002\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u8fc7\xb7\u4f60\u4eec\u90a3\u51e0\u5e74\u6536\u83b7\u4e0d\u5c11\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5631\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e5f\u8bf4\u8fc7\u65e0\u8bba\u6e2f\u6709\u5382\u90a3\u7b14\u8d26\u5bcc\u90fd\u53ef\u4ee5\u8c61\u7687\u5e1d\u822c\u4ea8\u53d7\u8f88\u5b50\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u5979\u5374\u5b81\u53ef\u653e\u5f03\u90a3\u79cd\u5e1d\u738b\u822c\u7684\u751f\u6d3b\u5b81\u53ef\u5230\u8fd9\u91cc\u6765\u670d\u4f8d\u4f60\uff0c\u5979\u75af\u4e86\u5417\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u5f53\u7136\u6ca1\u6709\u75af\u4f55\u51b5\u5c31\u7b97\u662f\u75af\u5b50\u4e5f\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u5c31\u7b97\u662f\u5446\u5b50\u4e5f\u5e94\u8be5\u660e\u767d\u5979\u7684\u610f\u601d\u4e5f\u5e94\u8be5\u5bf9\u5979\u597d\u4e9b\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5e76\u4e0d\u662f\u6ca1\u6709\u8d70\u51fa\u8fd9\u5c4b\u4e8e\u8fc7\u3002 \u3000\u3000\u5979\u51fa\u53bb\u8fc7\u4e94\u516d\u5929\u3002 \u3000\u3000\u56de\u6765\u65f6\uff0c\u5e26\u56de\u6765\u4e2a\u5c0f\u5c0f\u7684\u5305\u88b1\uff0c\u5305\u88b1\u91cc\u6709\u51e0\u4ef6\u9752\u5e03\u8863\u670d\uff0c\u51e0\u6837\u96f6\u661f\u7684\u4e1c\u897f\u3002 \u3000\u3000\u90a3\u5c31\u662f\u5979\u5269\u4e0b\u7684\u6240\u6709\u8d22\u4ea7\u4e86\u3002 \u3000\u3000\u5176\u4ed6\u7684\u5462\uff1f \u3000\u3000\u5979\u5c45\u7136\u5df2\u5c06\u90a3\u7b14\u5192\u4e86\u751f\u547d\u5371\u9669\u5f97\u6765\u7684\u8d22\u5bcc\uff0c\u5168\u90fd\u6350\u7ed9\u4e86\u9ec4\u6cb3\u6cbf\u5cb8\xb7\u6b63\u5728\u95f9\u6c34\u707e\u7684\u51e0\u7701\u5584 \u5802\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u7b80\u76f4\u4ee4\u4eba\u65e0\u6cd5\u76f8\u4fe1\u3002 \u3000\u3000\u738b\u52a8\u7684\u8138\u8272\u8fd8\u662f\u94c1\u9752\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u73b0\u5728\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u5979\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u884c\u751a\u81f3\u5df1\u7279\u5730\u53bb\u4e3a\u4f60\u597d\u542c\u8fc7\uff0c\u96be\u9053\u6211\u4eec\u4e5f\u4f1a\u5e2e\u5979\u9a97\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u73b0\u5728\u4f60\u8fd8\u770b\u4e0d\u51fa\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u5f53\u7136\u662f\u8d4e\u7f6a\u3002\u4f46\u6700\u91cd\u8981\u7684\u8fd8\u662f\u56e0\u4e3a\u5979\u60f3\u611f\u52a8\u4f60\u8ba9\u4f60\u56de\u5df4\u8f6c\u610f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5047\u5982\u6709\u4eba\u8fd9\u6837\u5bf9\u6211\u65e0\u8bba\u5979\u4ee5\u524d\u505a\u8fc7\u4ec0\u9ebd\u4e8b\u6211\u90fd\u4f1a\u539f\u8c05\u5979\u7684\u3002\u201d \u3000\u3000\u5e72\u52a8\u6c89\u9ed8\u2019\u76f4\u6ca1\u6709\u8bf4\u8bdd\u3002\u8fc7\u4e86\u5f88\u4e45\u4ed6\u624d\u6307\u8d77\u5934\u9053\u201c\u4f60\u4eec\u8bf4\u5b8c\u4e86\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8be5\u8bf4\u7684\u90fd\u5df3\u8bf4\u5b8c\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u751a\u81f3\u8fde\u4e0d\u8be5\u8bf4\u7684\u90fd\u8bf4\u4e86\u73b0\u5728\u53ea\u770b\u4f60\u600e\u9ebd\u505a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4eec\u8981\u6211\u600e\u9ebd\u6837\u505a\u8dea\u8428\u6765\u6c42\u5979\u5ac1\u7ed9\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u5012\u4e5f\u4e0d\u5fc5\u53ea\u4e0d\u8fc7\u2026\u201c\u2019\u53ea\u4e0d\u8fc7\u2500\u4e00\u2500\u201d \u3000\u3000\u71d5\u4e03\u66ff\u4ed6\u63a5\u4e86\u4e0b\u53bb\uff0c\u9053\u201c\u53ea\u4e0d\u8fc7\u8981\u4f60\u5bf9\u5979\u7a0d\u5fae\u597d\u4e00\u70b9\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u770b\u770b\u90ed\u5927\u8def\u53c8\u770b\u770b\u71d5\u4e03\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u4eec\u5f88\u597d\u90fd\u5f88\u597d\u2500\u2026\u201c \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u4ed6\u5c31\u7ad9\u8d77\u6765\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u8d70\u5f97\u5f88\u6162\uff0c\u4f46\u90ed\u5927\u8def\u53cd\u800c\u6ca1\u6709\u62c9\u4ed6\u56e0\u4e3a\u738b\u52a8\u5411\u5f88\u5c11\u53f9\u6c14\u3002 \u3000\u3000\u592a\u9633\u6e10\u6e10\u5347\u9ad8\uff0c\u5c06\u4ed6\u7684\u5f71\u5b50\u957f\u957f\u7684\u62d6\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u80cc\u597d\u50cf\u6709\u70b9\u5f2f\xb7\u80cc\u4e0a\u597d\u50cf\u538b\u5f88\u91cd\u7684\u62c5\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u4ece\u672a\u770b\u89c1\u8fc7\u4ed6\u7684\u6837\u5b50\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u7684\u5fc3\u60c5\u4e5f\u6c89\u91cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u4ecd\u53c8\u542c\u89c1\u9635\u5f88\u8f7b\u7684\u811a\u6b65\u58f0\u59cb\u8d77\u5934\u5c31\u770b\u5230\u7ea2\u5a18\u5b50\u5df2\u7ad9\u5728\u4ed6\u8ba2\u9762\u524d\u3002 \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u5382\u7b11\u9053\u201c\u5750\u8bf7\u5750\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5c31\u5750\u4e86\u5c38\u6765\xb7\u7aef\u8d77\u5979\u521a\u5012\u7ed9\u738b\u52a8\u7684\u8336\u559d\u4e86\u53e3\xb7\u53c8\u6162\u6162\u7684\u653e\u4e0b\xb7\u5ffd\u7136\u9053\u201c\u4f60\u4eec\u521a\u8bf4 \u7684\uff0c\u6211\u5168\u90fd\u542c\u89c1\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\u3002\u201d \u3000\u3000\u9664\u4e86\u8fd9\u4e2a\u201c\u54e6\u201d\u5b87\u5916\uff0c\u4ed6\u5b9e\u5728\u6444\u4e0d\u51fa\u5e94\u8be5\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u8f7b\u8f7b\u9053\u201c\u4f60\u4eec\u5bf9\u6211\u7684\u597d\u610f\u6211\u5f88\u611f\u6fc0\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u4e03\u5728\u7b49\u5979\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u7ea2\u5a18\u4e88\u624d\u6162\u6162\u7684\u63a5\u9053\u201c\u53ef\u662f\u6211\u8ddf\u4ed6\u4e4b\u95f4\u7684\u4e8b\u4f60\u4eec\u8fd8\u4e0d\u592a\u5382\u89e3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u548c\u71d5\u5df3\u8c01\u4e5f\u6ca1\u6709\u8868\u793a\u610f\u89c1\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u4e0d\u80fd\u8bf4\u81ea\u5df1\u5bf9\u522b\u4eba\u7684\u4e8b\u72e0\u4e86\u89e3\u8c01\u4e5f\u4e0d\u80fd\u8fd9\u9ebd\u8bf4\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5782\u4e0b\u5934\u9053\u201c\u6211\u4eec\u4ee5\u524d\u672c\u6765\u2026\u201d\u672c\u6765\u975e\u5e38\u8981\u597d\u2026\u201c\u4e2d\u25a0\u5e38\u71ce\u2026\u2026\u201d \u3000\u3000\u5979\u58f0\u97f3\u4f3c\u5df2\u6709\u4e9b\u9877\u54bd\u957f\u957f\u5410\u51fa\u53e3\u6c14\u624d\u63a5\u9053\u201c\u8fd9\u6b21\u6211\u7559\u4e0b\u6765\u6b63\u5982\u4f60\u4eec\u6240\u8bf4\uff0c\u662f\u5e0c\u671b\u80fd\u4f7f\u4ed6 \u56de\u5fc3\u8f6c\u610f\u91cd\u65b0\u8fc7\u8c61\u4ee5\u524d\u90a3\u6837\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u90ca\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u5bf9\u4ee5\u524d\u90a3\u6bb5\u65e5\u5b50\xb7\u771f\u7684\u8fd8\u72e0\u6000\u5ff5\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u70b9\u70b9\u5934\u6f5e\u7136\u9053\u201c\u53ef\u662f\u73b0\u5728\u6211\u624d\u77e5\u9053\xb7\u8fc7\u53bb\u7684\u4e8b\u5c31\u5df2\u8fc7\u53bb\u5c31\u50cf\u662f\u2018\u4e2a\u4eba\u7684\u9752\u6625\u6837\u53bb \u4e86\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u518d\u56de\u5934\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u5979\u773c\u6cea\u4f3c\u5df2\u5fcd\u4e0d\u4f4f\u8981\u6d41\u8428\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u5ffd\u7136\u4e5f\u89c9\u5f97\u9635\u9178\u695a\u60f3\u8bf4\u8bdd\u5374\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u9ebd\u3002\u4ed6\u5426\u65cf\u4e03\u71d5\u4e03\u7684\u773c\u5708\u513f\u4f3c\u4e5f\u6709\u4e9b \u53d1\u7ea2\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u4ee5\u524d\u867d\u7136\u4f24\u5bb3\u8fc7\u4ed6\u4eec\u6697\u7b97\u8fc7\u4ed6\u5546\u4f46\u73b0\u5728\u4ed6\u4eec\u65e9\u5df2\u5fd8\u5382\u53ea\u8bb0\u5f97\u7ea2\u5a18\u5b50\u662f\u4e2a\u5fc3\u60f3\u51f9\u5934 \u7684\u53ef\u601c\u7684\u5973\u4eba\u4ed6\u4eec\u5fc3\u91cc\u53ea\u6709\u540c\u60c5\u7edd\u6ca1\u6709\u4ec7\u6068\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u6bd4\u90ed\u5927\u8def\u4ed6\u4eec\u66f4\u5bb9\u6613\u5fd8\u8ba1\u5bf9\u522b\u4eba\u7684\u4ec7\u6068\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\u7ea2\u5a18\u5b50\u624d\u603b\u7b97\u52c9\u5f3a\u5c06\u773c\u6cea\u5ff5\u4f50\xb7\u8f7b\u8f7b\u9053\u201c\u4f46\u4f60\u4eec\u82e6\u4ee5\u4e3a\u4ed6\u771f\u662f\u4e2a\u94c1\u77f3\u5fc3\u80a0\u7684 \u4eba\u4f60\u4eec\u5c31\u9519\u4e86\u4ed6\u8d8a\u8fd9\u6837\u5bf9\u6211\u5c31\u8d8a\u53cb\u793a\u4ed6\u6ca1\u6709\u5fd8\u8bb0\u6211\u4eec\u4ee5\u524d\u7684\u60c5\u611f\u3002 \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u70b9\u70b9\u5934\uff0c\u9053\u201c\u6211\u4e86\u89e3\u3002\u201d \u3000\u3000\u4ed6\u771f\u7684\u4e86\u89e3\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\u5f80\u5f80\u5f88\u5fae\u5999\u3002 \u3000\u3000\u4eba\u4eec\u4e92\u76f8\u4f24\u5bb3\u5f97\u8d8a\u6df1\u5f80\u5f80\u53ea\u56e0\u4ed6\u4eec\u76f8\u7231\u5f97\u66f4\u6df1\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u8f7b\u8f7b\u7684\u63a5\u53c8\u9053\u201c\u6c60\u5bf9\u6211\u82e5\u662f\u5f88\u597d\u72e0\u5ba2\u6c14\xb7\u6211\u5fc3\u91cc\u53cd\u77f3\u66f4\u96be\u53d7\u3002\u201d \u3000\u3000\u9884\u4e03\u67d4\u58f0\u9053\u6211\u4e86\u89e3\u3002\u201d \u3000\u3000\u5de5\u6388\u4e88\u901a\u201c\u5c31\u56e0\u4e3a\u4ed6\u4ee5\u524d\u5bf9\u6211\u592a\u597d\u3001\u592a\u771f\u6240\u4ee5\u624d\u4f1a\u89c9\u5f97\u88ab\u6211\u8d1f\u5bb3\u5f97\u79df\u7406\u5386\u4ee5\u73b0\u5728\u4ed6\u624d\u4f1a \u8fd9\u9ebd\u6837\u6068\u6211\u3002\u201d \u3000\u3000\u90fd\u5927\u8def\u9053\u2606\u5730\u575b\u9ebd\u4f1a\u6068\u4f60\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6f5e\u7136\u7b11\u9053\u201c\u4ed6\u6068\u6211\xb7\u6211\u53cd\u800c\u9ad8\u5174\u56e0\u4e3a\u4ed6\u4ee5\u524d\u82e5\u4e0d\u662f\u771f\u7684\u5bf9\u6211\u597d\u73b0\u5728\u53c8\u60f3\u9ebd\u4f1a\u635f \u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\u9053\u201c\u6211\u6a2a\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4f60\u82e5\u5728\u4e00\u4e2a\u4eba\u8138\u4e0a\u523a\u4e86\u5200\u523a\u5f97\u5f88\u6df1\uff0c\u90a3\u9ebd\u4ed6\u8138\u4e0a\u5fc5\u5b9a\u4f1a\u7559\u4e0b\u6761\u5f88\u6df1\u7684\u5200\u75a4\u6c38 \u8fdc\u4e5f\u4e0d\u4f1a\u5e73\u590d\u3002\u201d \u3000\u3000\u5979\u8180\u7136\u63a5\u9053\u201c\u5fc3\u6bd4\u7684\u5200\u75d5\u4e5f\u6837\u6240\u4ee5\u6211\u77e5\u9053\u6211\u8861\u662f\u6c38\u8fdc\u65e0\u6cd5\u6062\u590d\u5230\u4ee5\u524d\u90a3\u6837\u5b50\u4e86\uff0c\u5c31\u7b97\u8fd8 \u80fd\u52c9\u5f3a\u76f8\u805a\u5728\u8d77\u516c\u91cc\u4e5f\u5fc5\u5b9a\u4f1a\u6709\u5c42\u9694\u819c\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u2026\u2026\u4f60\u4eec\u81f3\u5c11\u8fd8\u53ef\u4ee5\u505a\u4e2a\u670b \u53cb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u670b\u53cb\uff1f\u2026\u3002\u201c \u3000\u3000\u5979\u7b11\u5f97\u66f4\u51c4\u51c9\u9053\u201c\u4efb\u4f55\u4e24\u4e2a\u4eba\u90fd\u53ef\u80fd\u6210\u4e3a\u670b\u53cb\uff0c\u4f46\u4ed6\u4eec\u4ee5\u524d\u82e5\u662f\u76f8\u7231\u8fc7\u5c31\u6c38\u8fdc\u5766\u65e0\u6cd5\u6210 \u4e3a\u670b\u53cb\u4e86\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d\u90ed\u5927\u8def\u53ea\u5b58\u627f\u8ba4\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5ffd\u7136\u7ad9\u8d77\u6765\u9053\u201c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u201c\u90fd\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5fd8\u4e86\u4f60\u6253\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u770b\u89c1\u5979\u624b\u91cc\u63d0\u4e2a\u5c0f\u5c0f\u7684\u5305\u88b1\xb7\u52a8\u5bb9\u9053\u201c\u4f60\u60f3\u8d70\uff1f\u201d \u3000\u3000\u7ea2\u5ac2\u5b50\u51c4\u7136\u9053\u54e6\u82e5\u52c9\u5f3a\u7559\u4e0b\u6765\u4e0d\u4f46\u4ed6\u5fc3\u91cc\u96be\u53d7\xb7\u6211\u4e5f\u96be\u53d7\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u624d\u51b3\u5b9a\u8fd8\u4e0d\u5982\u8d70 \u5382\u597d\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u4f60\u2026\u2500\u4f60\u6709\u6ca1\u6709\u6253\u7b97\u6e2f\u5907\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d\u7ea2\u5a18\u5b50\u9053\u201c\u6ca1\u6709\u6253\u7b97\u201d \u3000\u3000\u5979\u4e0d\u8ba9\u522b\u4eba\u8bf4\u8bdd\u4eba\u5458\u5feb\u7684\u63a5\u53c8\u9053\u201c\u4f46\u4f60\u4eec\u53ef\u4e3a\u653e\u5fc3\xb7\u50cf\u6211\u8fd9\u6837\u7684\u4eba\u6709\u5f88\u591a\u5730\u65b9\u90fd\u53ef\u4ee5\u53bb \u7684\u6240\u4ee5\u4f60\u4eec\u4e3a\u4e86\u4ed6\u4e3a\u4e86\u6211\uff0c\u90fd\u6700\u597d\u4e0d\u8981\u62e6\u4f4f\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u71d5\u4e03\u71d5\u4e03\u5728\u53d1\u6014\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u770b\u4ed6\u4eec\u65e5\u4e2d\u4eff\u4f5b\u5145\u6ee1\u4e86\u7fa1\u6155\u4e4b\u610f\u67d4\u58f0\u9053\u201c\u4f60\u4eec\u82e5\u771f\u7684\u5c06\u6211\u5f53\u505a\u670b\u53cb\xb7\u5c31\u5e0c\u671b\u80fd\u8bb0 \u4f4f\u53e5\u8bdd\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8bf4\u3002\u201d\u7ea2\u5a18\u5b50\u51dd\u6ce8\u8fdc\u65b9\u7f13\u7f13\u5730\u9053\u201c\u4e16\u4e0a\u5458\u96be\u5f97\u7684\xb7\u65e2\u4e0d\u662f\u540d\u58f0\u4e5f\u4e0d\u662f\u8d22\u5bcc\u800c \u662f\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u771f\u60c5\uff0c\u4f60\u82e5\u5f97\u5230\u4e86\u5c31\u5343\u4e07\u8981\u73cd\u4fa9\uff0c\u5343\u4e07\u83ab\u8981\u8f9c\u8d1f\u4e86\u522b\u4eba\u8f9c\u8d1f\u4e86\u81ea\u5df1\u2026\u3002\u201c\u5979 \u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u4f4e\u4f4e\u66f2\u63a5\u9053\u201c\u56e0\u4e3a\u53ea\u6709\u4e00\u4e2a\u66fe\u7ecf\u5931\u53bb\u8fc7\u771f\u60c5\u7684\u4eba\uff0c\u624d\u61c2\u5f97\u5b83\u662f\u591a\u9ebd\u503c\u5f97\u73cd\u501f \u624d\u4f1a\u4e86\u89e3\u5931\u53bb\u5b83\u4e4b\u5f8c\u662f\u591a\u9ebd\u5bc2\u5bde\u591a\u9ebd\u75db\u82e6\u3002\u201d \u3000\u3000\u71d5\u4e03\u773c\u5708\u513f\u771f\u7684\u7ea2\u4e86\u5ffd\u7136\u9053\u201c\u4f60\u5462\uff1f\u4f60\u4ee5\u524d\u662f\u4e0d\u662f\u4ee5\u771f\u500d\u5728\u5bf9\u5f85\u4ed6\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6c89\u9ed8\u4e01\u5f88\u4e45\u624d\u8f7b\u8f7b\u9053\u201c\u6211\u672c\u6765\u8fde\u81ea\u5df1\u4e5f\u5206\u4e0d\u6e05\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u79bb\u5f00\u5f8c\uff0c\u6211\u603b\u662f\u4f1a\u60f3\u8d77\u4ed6\u6211\u3002\u2026\u627e\u8fc7\u5f88\u591a\u4eba\u53ef\u662f\u5374\u6ca1\u6709\u4e2a\u4eba\u80fd\u4ee3 \u66ff\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5979\u5ffd\u7136\u4ee5\u624b\u63a9\u9762\u72c2\u5954\u800c\u51fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u60f3\u8fc7\u53bb\u62e6\u963b\u3002 \u3000\u3000\u4f46\u71d5\u4e03\u5374\u62e6\u4f4f\u4e86\u4ed6\u6f5e\u7136\u9053\u201c\u8ba9\u5979\u8d70\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u8fd9\u6837\u8ba9\u5979\u8d70\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5e7d\u5e7d\u9053\u201c\u8d70\u4e86\u4e5f\u597d\uff0c\u4e0d\u8d70\u5f7c\u6b64\u95f4\u53cd\u800c\u66f4\u75db\u82e6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u6015\u5979\u4f1a\u2026\u2026\u4f1a\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u653e\u5fc3\u5979\u7edd\u4e56\u4f1a\u505a\u51fa\u4ec0\u9ebd\u4e8b\u6765\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u5979\u73b0\u5728\u5df4\u77e5\u9053\u738b\u8001\u5927\u5bf9\u5979\u786e\u662f\u771f\u5fc3\u7684\u8fd9\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u8db3\u591f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u81f3\u5c11\u8fd9\u5df2\u8db3\u591f\u4f7f\u4e2a\u5973\u4eba\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5979\u76ee\u4e2d\u4e5f\u5df2\u6cea\u73e0\u6ee1\u7736\u8f7b\u8f7b\u63a5\u9053\u201c\u4e2a\u5973\u4eba\u4e2d\u4e2d\xb7\u53ea\u8981\u6709\u4e2a\u7537\u4eba\u7684\u786e\u662f\u771f\u5fc3\u5bf9\u5979\u7684\u5979\u8fd9\u4e2d\u5c31 \u6ca1\u6709\u767d\u6d3b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51dd\u89c6\u4ed6\u826f\u4e45\u826f\u4e45\u9053\u201c\u4f60\u5bf9\u5973\u4eba\u597d\u50cf\u5382\u89e3\u5f97\u5f88\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u626d\u8fc7\u5934\u76ee\u5149\u79fb\u5411\u8fdc\u65b9\u3002 \u3000\u3000\u5929\u7a7a\u78a7\u84dd\u9633\u5149\u707f\u70c2\u3002 \u3000\u3000\u78a7\u84dd\u7684\u5929\u7a7a\u4e0b\u5ffd\u7136\u6709\u9053\u6d45\u7d2b\u8272\u7684\u70df\u706b\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u71d5\u4e03\u76b1\u4e86\u76b1\u7709\u9053\u201c\u8fd9\u79cd\u65f6\u5019\uff0c\u600e\u9ebd\u4f1a\u6709\u4eba\u653e\u70df\u706b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u56de\u8fc7\u5934\xb7\u5c31\u770b\u89c1\u738b\u52a8\u4e5f\u6b63\u7ad9\u5728\u5c4b\u6444\u535c\uff0c\u770b\u8fd9\u9053\u70df\u706b\u3002 \u3000\u3000\u98ce\u5439\u8fc7\u6765\u7d2b\u8272\u7684\u70df\u706b\u968f\u98ce\u987a\u6563\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ea\u8981\u4eba\u5bb6\u9ad8\u5174\u968f\u65f6\u968f\u5730\u90fd\u53ef\u4ee5\u653e\u70df\u706b\xb7\u8fd9\u70b9\u4e5f\u4e0d\u7a00\u5947\u3002\u201d \u3000\u3000\u71d5\u4e03\u4f3c\u5728\u6c89\u601d\uff0c\u8d3f\u8d3f\u9053\u201c\u662f\u4e0d\u662f\u5c31\u597d\u50cf\u968f\u65f6\u968f\u5730\u90fd\u53ef\u4ee5\u653e\u98ce\u7b2c\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u542c\u6e05\u695a\u6b63\u51c6\u5907\u95ee\u4ed6\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u5ffd\u7136\u960e\u738b\u52a8\u5df2\u51b2\u5230\u4ed6\u8ba2\u9762\u524d\uff0c\u9053\u201c\u5979\u5462\uff1f\u201d \u3000\u3000\u201c\u5979\u201d\u81ea\u7136\u5c31\u662f\u7ea2\u5a18\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5979\u5df2\u7ecf\u8d70\u4e86\u56e0\u4e3a\u5979\u89c9\u5f97\u4f60\u2026\u2026\u201d\u571f\u52a8\u5927\u58f0\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\u201c\u5979\u4ec0\u9ebd\u65f6\u5019\u5b9a \u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u521a\u8d70\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5b87\u521a\u8bf4\u5b8c\u738b\u52a8\u7684\u4eba\u5df2\u6a2a\u7a7a\u63a0\u8d77\u53ea\u95ea\u5c31\u63a0\u51fa\u5899\u5916\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u9053\u201c\u539f\u6765\u4ed6\u5bf9\u5979\u8fd8\u662f\u5f88\u597d\xb7\u5979\u6839\u672c\u4e0d\u5fc5\u5b9a\u7684\u3002\u201d \u3000\u3000\u4ed6\u6447\u5934\uff0c\u7b11\u9053\u201c\u5973\u4eba\u4e3a\u4ec0\u9ebd\u603b\u662f\u8fd9\u6837\u559c\u6b22\u591a\u5fc3\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8138\u4e0a\u5374\u8fde\u4e1d\u7b11\u610f\u4e5f\u6ca1\u6709\u6c89\u58f0\u9053\u201c\u4f60\u4ee5\u4e3a\u90a3\u70df\u706b\u771f\u662f\u653e\u73a9\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6c5f\u6e56\u4e2d\u7684\u52fe\u5f53\uff0c\u770b\u6765\u4f60\u771f\u7684\u8fde\u70b9\u4e5f\u4e0d\u61c2\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u672c\u6765\u5c31\u4e0d \u662f\u4e2a\u8001\u6c5f\u6e56\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u5047\u5982\u6211\u4eec\u8981\u5bf9\u4ed8\u4e2a\u4eba\uff0c\u4f60\u5728\u8fd9\u91cc\u5b88\u4ed6\u6211\u5728\u5c71\u4e0b\u4f60\u6709\u4e86\u4ed6\u7684\u6d88\u606f\u65f6\uff0c\u7528\u4ec0\u9ebd\u6cd5\u5b50 \u6765\u901a\u77e5\u6211\u4e2d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u4f1a\u7684\u3002\u201d\u71d5\u4e03\u9053\u201c\u4e0d\u4f1a\u7684\uff1f\u8c08\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u610f\u601d\u5c31\u662f\u8bf4\u8c61\u8fd9\u79cd\u60c5\u51b5\u6839\u672c\u5c31\u4e0d\u4f1a\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u56e0\u4e3a\u4f60\u82e5\u5728\u5c71\u5c38\u5b88\uff0c\u6211\u5b9a\u4e5f\u5728\u5c71\u4e0b\u3002\u201d \u3000\u3000\u71d5\u4e03\u670d\u775b\u91cc\u9732\u51fa\u4e86\u6e29\u67d4\u4e4b\u8272\uff0c\u4f46\u8138\u5374\u677f\u4e86\u8d77\u6765\u9053\u201c\u6211\u4eec\u73b0\u5728\u8bf4\u7684\u662f\u6b63\u7ecf\u4e8b\u4f60\u80fd\u4e0d\u80fd\u597d\u597d \u5730\u8bf4\u7538\u6b63\u7ecf\u8bdd\u201d\u90ed\u5927\u8def\u9053\u201c\u80fd\u3002\u201d \u3000\u3000\u4ed6\u60f3\u4e86\u60f3\u624d\u63a5\u9053\u201c\u5c71\u548c\u5c71\u4e0b\u7684\u8ddd\u79bb\u4e0d\u8fd1\u6211\u5c31\u7b97\u5927\u558a\u5927\u53eb\u4f60\u4e5f\u672a\u5fc5\u542c\u5f97\u5230\u3002\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u806a\u660e\u806a\u660e\u4f60\u771f\u806a\u660e\u6781\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\xb7\u4e49\u60f3\u4e86\u60f3\u624d\u8bf4\u9053\u201c\u6211\u53ef\u4ee5\u53eb\u522b\u4eba\u53bb\u901a\u77e5\u4f60\u3002\u201d\u71d5\u4e03\u9053\u201c\u82e5\u6ca1\u6709\u522b\u7684\u4eba \u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5c31\u81ea\u8dd1\u4e0b\u5c71\u53bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u77aa\u4ed6\u677f\u8138\u9053\u201c\u4f60\u8111\u888b\u91cc\u88c5\u7684\u7a76\u7adf\u662f\u4ec0\u9ebd\uff1f\u7a3b\u8349\uff1f\u6728 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u9664\u4e86\u7a3b\u8349\u548c\u6728\u5934\u4e4b\u5916\uff0c\u8fd8\u6709\u8111\u95e8\u5b50\u60f3\u9017\u4f60\u751f\u6c14\u7684\u5ff5\u5934\xb7\u6211\u603b\u89c9\u5f97\u4f60\u751f\u8d77\u6c14 \u6765\u7684\u6837\u5b50\xb7\u50cf\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c0f\u59d1\u5a18\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u7b8d\u4e03\u5f00\u53e3\u62a2\u53c8\u9053\u201c\u5176\u5b9e\u6211\u5f53\u7136\u660e\u767d\u4f60\u7684\u610f\u601d\u4f60\u8ba4\u4e3a\u90a3\u70df\u706b\u4e5f\u8ddf\u98ce\u7b2c\u6837\u662f\u6c5f\u6e56\u4e2d\u4eba \u4f20\u9012\u6d88\u606f\u7684\u8baf\u53f7\u3002 \u3000\u3000\u71d5\u4e03\u8fd8\u5728\u8e6c\u4ed6\u8fc7\u4e86\u5f88\u4e45\u624d\u957f\u957f\u53f9\u4e25\u95e8\u6c14\u9053\u201c\u6211\u603b\u6709\u5929\u975e\u88ab\u4f60\u6d3b\u6d3b\u6c14\u6b7b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5c31\u4ea6\u8fd9\u65f6\u5c71\u8428\u5ffd\u7136\u4e5f\u6709\u9053\u7d2b\u8272\u7684\u65d7\u82b1\u70df\u706b\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u795e\u8272\u4e5f\u53d8\u5f97\u6b63\u7ecf\u8d77\u6765\u4e86\u9053\u201c\u4ee5\u4f60\u770b\uff0c\u662f\u672c\u662f\u6709\u6c5f\u6e56\u4eba\u5230\u4e86\u6211\u4eec\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u800c\u4e14\u8fd8\u4e0d\u6b62\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u662f\u6765\u5bf9\u4ed8\u7ea2\u5a18\u5b50\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4e0d\u77e5\u9053\xb7\u4f46\u738b\u8001\u5927\u90a6\u5fc5\u5b9a\u662f\u8fd9\u9ebd\u60f3\u6cd5\u6240\u4ee5\u4ed6\u624d\u4f1a\u8d76\u8fc7\u5de6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u52a8\u5bb9\u9053\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u8fd8\u7b49\u5728\u8fd9\u91cc\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u6211\u8fd8\u8981\u6bb5\u4f60\u5546\u91cf\u4e00\u4ef6\u4e8b\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u6b21\u4f60\u80fd\u4e0d\u80fd\u4e0d\u8981\u8ddf\u6211\xb7\u8ba9\u6211\u4e2a\u4eba\u53bb\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u90ed\u5927\u8def\u5df3\u7528\u529b\u6447\u5934\u9053\u201c\u4e0d\u80fd\u3002\u201d\u71d5\u4e03\u76b1\u76b1\u7709\u9053\u201c\u6211\u4eec\u82e5\u5168\u8d70\u4e86\u8c01\u7559\u5728 \u8fd9\u91cc\u9677\u5c0f\u6797\uff1f\u201d \u3000\u3000\u4ed6\u8ba2\u5f53\u7136\u4e0d\u80fd\u5c06\u6797\u592a\u5e73\u4e2a\u4eba\u56fe\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u535c\u6b21\u7684\u6559\u5200\u5f8c\u73b0\u5728\u65e0\u8bba\u4ec0\u9ebd\u4e8b\uff0c\u4ed6\u4eec\u90fd\u4efd\u5916\u5c0f\u5fc3\u3002\u90ed\u5927\u8def\u6c89\u541f\u9053\u201c\u8fd9\u6b21\u4f60\u80fd\u4e0d\u80fd \u8ba9\u6211\u8d70\u4f60\u7559\u5728\u8fd9\u91cc\uff1f\u201d\u71d5\u4e03\u4e5f\u7acb\u523b\u6447\u5934\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u6e29\u67d4\u8d77\u6765\u9053\u201c\u4f60\u7684\u4f24\u672c\u6765\u5c31\u6ca1\u6709\u5b8c\u5168\u597d\u518d\u52a0\u6b62\u4f60\u53c8\u6b7b\u4e0d\u8981\u547d\xb7\u4e0d\u7b49 \u4f24\u597d\u4e4b\u5f8c\u5c31\u4e2a\u4eba\u5077\u5077\u6e9c\u4e0b\u53bb\u559d\u9152\u7ff1\u5927\u8def\u9053\u201c\u8c01\u4e2a\u4eba\u5077\u5077\u559d\u9152\uff1f\u96be\u9053\u6211\u6ca1\u6709\u5e26\u9152\u56de\u6765\u2026\u2500\u201d \u3000\u3000\u71d5\u4e03\u6c89\u8138\u9053\u201c\u4e0d\u7ba1\u600e\u9ebd\u6837\u4f60\u73b0\u5728\u8fd8\u4e2d\u80fd\u8ddf\u522b\u4eba\u4ea4\u624b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u77aa\u773c\u9053\u201c\u6211\u8bf4\u7684\u4f60\u5c0f\u670d\u6c14\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u82e5\u4e0d\u670d\u6c14\u3002\u5148\u56e0\u6211\u6253\u67b6\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u644a\u5f00\u53cc\u624b\uff0c\u82e6\u7b11\u9053\u201c\u8c01\u8bf4\u6211\u4e0d\u670d\u6c14\u6211\u670d\u6c14\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u4ed6\u6367\u8d77\u90a3\u5f20\u6446\u68cb\u76d8\u7684\u5c0f\u684c\u5b50\uff0c\u8d3f\u9676\u9053\u201c\u4f60\u5feb\u4f17\u5427\u6211\u5de6\u627e\u5c0f\u6797\u4e0b\u76d8\u68cb\u4ed6\u7684\u72d7\u5c4e\u68cb\u521a\u597d\u8ddf\u6211 \u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u4f01\u8fc7\u53bb\u76ee\u5149\u53c8\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u6e29\u67d4\u6e29\u67d4\u5f97\u5c31\u8c61\u662f\u521a\u5439\u878d\u5927\u5730\u4e0a\u51b0\u96ea\u7684\u6625\u98ce\u6837\u3002 \u3000\u3000\u73b0\u5728\u6b63\u662f\u6625\u5929\u3002 \u3000\u3000\u6625\u5929\u672c\u5c31\u662f\u5c5e\u5201\u2606\u591a\u60c5\u513f\u5973\u8ba2\u7684\u5b63\u4eff\u3002 \u3000\u3000\u6625\u5929\u4e0d\u662f\u6740\u4eba\u7684\u5b63\u8282\u3002 \u3000\u3000\u6625\u5929\u53ea\u9002\u4e8e\u4eba\u6790\u6765\u542c\u97f3\u4e50\u822c\u7684\u7889\u7785\u9e1f\u8bed\u591a\u60c5\u76ef\u5401\xb7\u7edd\u4e0d\u9002\u4e8e\u542c\u5230\u63ba\u547c\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u91cc\u4ed6\u542c\u5230\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5782\u6b7b\u7684\u60e8\u547c\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u4e9b\u5730\u65b9\u7684\u6625\u5929\u5230\u5f97\u603b\u597d\u50cf\u7279\u522b\u8fdf\u4e9b\u3002 \u3000\u3000\u8fd8\u6709\u4e9b\u5730\u65b9\u751a\u81f3\u597d\u50cf\u6c38\u65e0\u6625\u5929\u3002 \u3000\u3000\u5177\u5b9e\u4f60\u82e5\u8981\u77e5\u9053\u6625\u5929\u662f\u5426\u6765\u4e86\u7528\u4e0d\u53bb\u770b\u679d\u5934\u7684\u65b0\u7eff\uff0c\u4e5f\u7528\u4e0d\u53bb\u95ee\u6625\u6c5f\u7684\u91ce\u9e2d\u3002 \u3000\u3000\u4f60\u53ea\u8981\u95ee\u4f60\u81ea\u5df2\u3002 \u3000\u3000\u56e0\u4e3a\u771f\u6b63\u7684\u6625\u5929\u65e2\u4e0d\u5728\u7eff\u6821\u535c\xb7\u4e5f\u4e0d\u5728\u6696\u6c34\u4e2d\u3002 \u3000\u3000\u771f\u6b63\u7684\u6625\u5929\u5c31\u5728\u4f60\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u94a2\u5200\u4e0b\u662f\u4e45\u8fd8\u6ca1\u6709\u6625\u5929\u7684\uff0c \u3000\u3000\u8840\u6cca\u4e2d\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4e2a\u4eba\u5367\u5728\u8840\u6cca\u4e2d\u547c\u5438\u5df2\u505c\u6b62\xb7\u5782\u6b7b\u524d\u7684\u60e8\u547c\u4e5f\u5df2\u65ad\u7edd\u3002 \u3000\u3000\u5200\u8fd8\u88ab\u7d27\u7d27\u63e1\u5728\u4ed6\u624b\u3002 \u3000\u3000\u67c4\u96ea\u4eae\u7684\u9b3c\u5934\u5200\u5f00\u6076\uff0c\u6c89\u91cd\uff01 \u3000\u3000\u4e5d\u4e2a\u4eba\uff0c\u4e5d\u67c4\u5200 \u3000\u3000\u98ce\u4e2d\u5f25\u6f2b\u4ee4\u4eba\u5455\u5410\u7684\u8840\u8165\u6c14\u6625\u5929\u672c\u5df3\u5230\u4e86\u8fd9\u6697\u6797\u4e2d\uff0c\u73b0\u5b58\u5373\u4f3c\u53c8\u5df2\u53bb\u8fdc\u3002 \u3000\u3000\u4e5d\u4e2a\u4eba\u624b\u91cc\u7d27\u63e1\u5200\u5c06\u7ea2\u5a18\u5b50\u56f4\u4fe1 \u3000\u3000\u4e5d\u4e2a\u5230\u608d\u3001\u77eb\u5065\u3001\u76ee\u5149\u6076\u6bd2\u7684\u9ed1\u8863\u4eba\u4e2a\u5df2\u5012\u5367\u5728\u76bf\u6cb9\uff0c\u6237\u3002 \u3000\u3000\u7ea2\u591f\u5b50\u770b\u4ed6\u4ecb\xb7\u8138\u4ea1\u53c8\u9732\u51fa\u4e86\u90a3\u79cd\u201c\u6551\u8425\u6551\u96be\u201d\u7684\u636e\u7b11\u7ea4\u7ea4\u7684\u624b\u6307\u5411\u8840\u6cca\u4e2d\u6307\u4e86\u6307\u5a9a\u7b11 \u9053\u201c\u8fd9\u4f4d\u662f\u8001\u51e0\uff1f\u201d \u3000\u3000\u4ea1\u4e2a\u4eba\u7d27\u54ac\u7259\u53ea\u6709\u4e2a\u6700\u7626\u7684\u9ed1\u8863\u4eba\u4ece\u7259\u7f1d\u91cc\u5410\u51fa\u4e24\u4e2a\u4e2d\u201c\u8005\u516b\u3002\u201d \u3000\u3000\u7ea2\u65d7\u5b50\u642c\u624b\u6307\u9053\u201c\u7b2c\u4e2a\u6b7b\u7684\u597d\u50cf\u662f\u8001\u516d\xb7\u7136\u5f8c\u662f\u8001\u4e8c\u3001\u8001\u4e5d\u3001\u8001\u535c\u6d74\u52a0\u4ea1\u8001\u516b\u5509\u5341\u4e8c\u628a \u5927\u5200\u5982\u4eca\u5df2\u53ea\u5269\u4e0b\u516b\u628a\u5200\u5382\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u9519\u5341\u4e8c\u628a\u5200\u5df2\u6709\u4e94\u5144\u5f1f\u6b7b\u5728\u4f60\u4eec\u624b\u91cc\u3002\u201d \u3000\u3000\u4ed6\u5589\u960e\u53d1\u51fa\u869c\u7559\u822c\u7684\u4f4e\u543c\uff0c\u5386\u58f0\u9053\u201c\u4f46\u516b\u628a\u5200\u8fd8\u662f\u5144\u5f1f\u8db3\u591f\u5c06\u4f60\u5241\u6210\u8089\u5462\u201c \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u4e86\u7b11\u58f0\u5982\u94f6\u94c3\u3002 \u3000\u3000\u516b\u4e2a\u4eba\u4e2d\u6709\u53c1\u4e2a\u5ffd\u7136\u4e0d\u7531\u767d\u4e3b\uff0c\u5411\u5f8c\u9000\u4e86\u4e2d\u6b65\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u94f6\u94c3\u822c\u7684\u7b11\u9053\u7f8e\u4eba\u7531\u6d3b\u8272\u751f\u9999\u7684\u624d\u597d\u50cf\u6211\u8fd9\u9ebd\u6837\u4e2a\u6d3b\u8272\u751f\u9999\u7684\u7f8e\u4eba\u5241\u6210\u8089\u6ce5\u5c82\u975e \u53ef\u4fd7\uff1f\u201d \u3000\u3000\u5979\u773c\u6ce2\u6d41\u52a8\u4ece\u5012\u9000\u7684\u53c1\u4e2a\u4eba\u8138\u8198\u8fc7\u5a9a\u7b11\u9053\u201c\u4f60\u4eec\u603b\u8be5\u77e5\u9053\u6211\u6709\u4e9b\u4ec0\u9ebd\u597d\u5904\u7684\uff0c\u4e3a\u4ec0\u9ebd\u4e2d \u544a\u8bc9\u4f60\u7684\u5144\u5f1f\u4eec\uff1f\u4f60\u4eec\u771f\u81ea\u79c1\u2026\u2026\u6b7b\u4eba\u5df2\u4e2d\u4f1a\u8bf4\u8bdd\u4f60\u4eec\u96be\u9053\u4e5f\u4e0d\u4f1a\uff1f\u201d \u3000\u3000\u8fd9\u4e8c\u4eba\u8138\u8272\u90fd\u53d8\u4e86\u7a81\u7136\u6325\u5200\u6251\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u6700\u7626\u5458\u9ad8\u7684\u9ed1\u8863\u4eba\u5ffd\u7136\u58f0\u4f4e\u542e\u201c\u4f4f\u624b\u201d \u3000\u3000\u4ed6\u663e\u7136\u662f\u8fd9\u5341\u4e8c\u5200\u7684\u7b2c\u628a\u5200\uff0c\u665a\u58f0\u51fa\u53e3\u5200\u707e\u523b\u5728\u534a\u7a7a\u4e2d\u505c \u3000\u3000\u7ea2\u5a18\u5b50\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u770b\u3001\u6211\u5c31\u77e5\u9053\u4f53\u4eec\u7684\u8d75\u8001\u5927\u4e5f\u820d\u4e0d\u5f97\u6740\u53cc\u7684\xb7\u4ed6\u867d\u7136\u4e0d\u662f\u4e2a\u601c\u9999 \u60dc\u7389\u7684\u4eba\u4f46\u4e2a\u4ea5\u4eba\u7684\u597d\u574f\xb7\u4ed6\u81f3\u5c11\u8fd8\u61c2 \u3000\u3000\u8d75\u8001\u5927\u6c89\u8138\u7f13\u7f13\u9053\u201c\u4f60\u5f88\u597d\uff0c\u6211\u7684\u786e\u820d\u4e0d\u5f97\u6740\u4f60\u56e0\u6211\u820d\u4e0d\u5f97\u4f60\u6b7b\u5f97\u592a\u5feb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u773c\u6ce2\u6d41\u52a8\uff0c\u7b11\u5f97\u66f4\u5a9a\u67d4\u58f0\u9053\u201c\u4f60\u8981\u6211\u4ec0\u9ebd\u65f6\u5019\u5979\u6211\u5c31\u4ec0\u9ebd\u65f6\u5019\u6b7b\u4f60\u8981\u6211\u600e\u9ebd\u6b7b\u4f60 \u77e5\u9053\u4ec0\u9ebd\u4e8b\u6211\u90fd\u60c5\u613f\u4e3a\u4f60\u505a\u7684\u3002\u201d\u8d75\u8001\u5927\u9053\u201c\u597d\xb7\u5f88\u597d\u3002\u201d \u3000\u3000\u4e2a\u4eba\u8981\u505a\u8005\u5927\u8bdd\u5c31\u4e0d\u80fd\u592a\u591a\u3002 \u3000\u3000\u56e0\u4e3a\u8d8a\u4e0d\u8bf4\u8bdd\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u5c31\u8d8a\u6709\u4ef7\u503c\u3002 \u3000\u3000\u8d75\u8001\u5927\u4e5f\u4e0d\u662f\u4e2a\u559c\u6b22\u591a\u8bdd\u7684\u4eba\u4ed6\u8bf4\u8bdd\u7b80\u77ed\u6652\u6709\u6548 \u3000\u3000\u201c\u4f60\u6740\u4e86\u6211\u4eec\u4e94\u4e2a\u5144\u5f1f\u6211\u4eec\u780d\u4f60\u4e94\u5200\u8fd9\u7b14\u5e10\u5c31\u4ece\u6b64\u62b5\u9500\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7728\u7728\u773c\u9053\u201c\u53ea\u780d\u4e94\u5200\uff1f\u201d\u8d75\u8001\u5927\u9053\u201c\u8d50\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u3001\u201c\u8fde\u5229\u606f\u90fd\u4e0d\u8981\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u5631\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u53f9\u4e86\u6c14\u201c\u8fd9\u9605\u4e5f\u4e0d\u80fd\u7b97\u77f3\u516c\u5e73\u6211\u4e5f\u5f88\u613f\u610f\u7b54\u5e94\u53eb\u53ef\u51b5\u73b0\u5728\u4f60\u4eec\u516b\u4e2a\u5bf9\u4ed8\u6211\u4e2a\xb7\u6211 \u60f3\u4e0d\u7b54\u5e9c\u4e5f\u4e0d\u884c\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u6062\u660e\u767d\u6700\u597d\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u867d\u7136\u5f88\u660e\u767d\u53ea\u53ef\u60dc\u4e00\u6837\u4e8b\u3002\u201d \u3000\u3000\u8d75\u8005\u5927\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6211\u6015\u75bc\u3002\u201d \u3000\u3000\u5979\u770b\u4ed6\u4eec\u624b\u91cc\u7684\u5200\u80a1\u4e0a\u9732\u51fa\u53ef\u601c\u4e2d\u53f7\u7684\u8868\u60c5\uff0c\u8bf4\u9053\u201c\u8fd9\u9ebd \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u4e0d\u7629\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u771f\u7684\u4e0d\u75bc\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u81f3\u5c11\u7b2c\u4e8c\u5200\u5c31\u4e0d\u4f1a\u75bc\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5a18\u4e8e\u597d\u50cf\u8fd8\u542c\u4e0d\u61c2\u7684\u6837\u5b50\u9053\u201c\u4f60\u4fdd\u8bc1\uff1f\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u6211\u4fdd\u8bc1\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u6709\u4f60\u4fdd\u8bc1\u6211\u5f53\u7136\u653e\u5fc3\u5f97\u5f88\xb7\u4f46\u6211\u4e5f\u6709\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u7b2c\u5200\u5b9a\u8981\u4f60\u6765\u780d\u3002\u201d \u3000\u3000\u5979\u51b0\u6dcb\u7433\u7684\u53cc\u773c\u775b\u77aa\u8d75\u8005\u5927\xb7\u4e49\u9053\u201c\u56e0\u4e3a\u6211\u4e0d\u4fe1\u4efb\u522b\u4eba\uff0c\u53ea\u4fe1\u4efb\u4f60\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u597d\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u8d70\u8fc7\u6765\u811a\u6b65\u5f88\u91cd\u51e0\u4e4e\u5df2\u53ef\u542c\u5230\u811a\u5e95\u63a2\u788e\u6c99\u77f3\u7684\u58f0 \u3000\u3000\u5200\u8fd8\u662f\u5782\u7684\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5bbd\u5927\u9762\u6388\u524a\uff0c\u624b\u80cc\u4e0a\u6839\u6839\u9752\u7b4b\u51f8\u8d77\u3002 \u3000\u3000\u4ed6\u5df2\u4f7f\u51fa\u4e86\u5341\u5206\u529b\u3002 \u3000\u3000\u201c\u7b2c\u4e8c\u5200\u7edd\u4e0d\u4f1a\u75bc\u7684\u3002\u201d\u8fd9\u4e00\u5200\u780d\u4e0b\u53bb\xb7\u4efb\u4f55\u4eba\u90fd\u4e0d\u53ef\u80fd\u518d\u6709\u75bc\u7684\u611f\u89c9\u4e0d\u53ef\u80fd\u518d\u6709\u4efb\u4f55 \u611f\u89c9\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5c45\u7136\u95ed\u4e0a\u4e86\u773c\u775b\xb7\u8138\u4ea1\u8fd8\u6709\u5e26\u90a3\u79cd\u4ee4\u4eba\u9500\u9b42\u7684\u5fae\u7b11\u9053\u201c\u6765\u5427\u5feb\u6765\u3002\u201d \u3000\u3000\u5200\u5149\u95ea\u5e26\u5c16\u9510\u7684\u98ce\u58f0\u780d\u4e0b\u6765\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7a81\u7136\u81ea\u5200\u5149\u4e0b\u94bb\u8fc7\u95ea\u52a8\u7684\u5200\u5149\u4e2d\u98de\u8d77\u4e00\u7247\u4e4c\u4e1d\u3002 \u3000\u3000\u5979\u5934\u53d1\u5df2\u88ab\u524a\u53bb\u5382\u5927\u7247\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u624b\uff0c\u5374\u5df2\u6258\u8d77\u4e86\u8d75\u8001\u5927\u7684\u8098\uff0c\u53e6\u53ea\u624b\u5c31\u6309\u4f4f\u4ed6\u808b\u4e0b\u7684\u7a74\u9053\u4e0a\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u5206\u8fa8\u51fa\u90a3\u662f\u4ec0\u9ebd\u7a74\uff0c\u4f46\u8c01\u90fd\u77e5\u9053\u90a3\u5fc5\u5b9a\u662f\u4e2a\u81f4\u547d\u7684\u7a74\u9000\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u8138\u4e0a\u770b\u6765\u90fd\u8c61\u662f\u88ab\u4eba\u91cd\u91cd\u5728\u5c0f\u8179\u5b50\u4e0a\u8e22\u4e86\u811a\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u8fd8\u662f\u5728\u7b11\u3002 \u3000\u3000\u90a3\u79cd\u8981\u547d\u7684\u7b11\u3002 \u3000\u3000\u5979\u94f6\u94c3\u822c\u7b11\u9053\u201c\u4f60\u73b0\u5728\u603b\u8be5\u660e\u767d\u6211\u4e3a\u4ec0\u9ebd\u5b9a\u8981\u4f60\u5148\u52a8\u624b\u4e86\u5427\u56e0\u4e3a\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u7684\u624b\u4f1a\u8f6f \u7684\u6211\u65e9\u5df3\u77e5\u9053\u4f60\u5df2\u770b\u4e0a\u4e86\u6211\u3002\u201d \u3000\u3000\u8d75\u8005\u5927\u7684\u624b\u5e76\u6ca1\u6709\u8f6f \u3000\u3000\u4ed6\u90a3\u5200\u8fd8\u662f\u5f88\u5feb\u5f88\u72e0\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u5200\u780d\u4e0b\u65f6\u7adf\u5fd8\u4e86\u5200\u4e0b\u7684\u7a7a\u95e8\u5728\u4e2a\u5df2\u95ed\u4e0a\u9650\u7b49\u6b7b\u7684\u5973\u4eba\u9762\u524d\u8c01\u90fd\u96be\u514d\u4f1a\u53d8\u5f97\u7c97\u5fc3 \u5927\u610f\u4e9b\u7684\u3002 \u3000\u3000\u4ed6\u4e49\u5f97\u5230\u4e2a\u6559\u8bad \u3000\u3000\u201c\u4f60\u82e5\u8981\u6740\u4eba\u5f97\u968f\u65f6\u968f\u523b\u9632\u5907\u522b\u4eba\u6765\u6740\u4f60\u3002\u201d \u3000\u3000\u8fd9\u5f53\u7136\u4e0d\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u201c\u4f60\u82e5\u8981\u6740\u4eba\u5f97\u51c6\u5907\u8fc7\u751f\u7d27\u5f20\u75db\u82e6\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u53f9\u4e86\u53e3\u6c14\xb7\u9053\u201c\u5f25\u60f3\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u4e5f\u4e0d\u60f3\u60f3\u9ebd\u6837\u53ea\u4e0d\u8fc7\u60f3\u8ddf\u4f60\u8c08\u7b14\u751f\u610f\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u4ec0\u9ebd\u751f\u610f\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u7528\u4f60\u7684\u6761\u547d\uff0c\u6765\u6362\u6211\u7684\u6761\u547d\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u600e\u9ebd\u6362\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u8fd9\u7b80\u5355\u5f97\u5f88\u6211\u82e5\u6b7b\u4e86\u4f60\u4e5f\u4f11\u60f3\u6d3b\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\u201c\u6211\u82e5\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u751c\u751c\u7684\u7b11\u9053\u201c\u4f60\u82e5\u6b7b\u4e86\u6211\u5f53\u7136\u4e5f\u6d3b\u4e0d\u4e0b\u53bb\u4f46\u6211\u600e\u9ebd\u820d\u5f97\u8ba9\u4f60\u6b7b\u5462\uff1f\u201d\u8d75\u8001\u5927\u60f3\u4e86 \u60f3\u9053\u201c\u597d\u3002\u201d \u3000\u3000\u8c01\u4e5f\u6ca1\u542c\u61c2\u8fd9\u201c\u597d\u201d\u5b87\u662f\u4ec0\u9ebd\u610f\u601d\u53ea\u770b\u89c1\u4ed6\u624b\u91cc\u7684\u5200\u7a81\u53c8\u780d \u3000\u3000\u5200\u780d\u5728\u4ed6\u81ea\u5df1\u7684\u5934\u5e7f\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u662f\u4e2a\u8001\u6c5f\u6e56\u3002 \u3000\u3000\u8001\u6c5f\u6e56\u82e5\u5df2\u6258\u4f50\u4e86\u4e2a\u4eba\u7684\u624b\u65f6\u5f53\u7136\u5df2\u7b97\u51c6\u4e86\u4ed6\u624b\u91cc\u7684\u5200\u5df2\u65e0\u6cd5\u4f24\u4eba\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7b97\u5f97\u5f88\u96be\uff0c\u53ea\u4e0d\u8fc7\u5fd8\u4e86\u4ef6\u4e8b\u3002 \u3000\u3000\u8d75\u8001\u5927\u624b\u91cc\u7684\u5200\u867d\u6ca1\u6cd5\u5b50\u780d\u5979\uff0c\u5374\u8fd8\u662f\u53ef\u4ee5\u5f2f\u56de\u624b\u780d\u81ea\u5df1\u3002 \u3000\u3000\u5979\u53ea\u987e\u4fdd\u5168\u81ea\u5df1\u7684\u547d\u5c31\u5fd8\u4e86\u4fdd\u5168\u522b\u4eba\u7684\u547d\u3002 \u3000\u3000\u5979\u4ee5\u4e3a\u522b\u4eba\u4e5f\u613f\u5979\u6837\u603b\u662f\u5c06\u81ea\u5df1\u7684\u547d\u770b\u5f97\u6bd4\u8f83\u91cd\u4e9b\u3002 \u3000\u3000\u5374\u5fd8\u4e86\u6709\u4e9b\u4eba\u4e3a\u4e86\u7231\u6216\u4ec7\u6839\u662f\u5f80\u5f80\u4f1a\u8fde\u81ea\u5df1\u6027\u547d\u90fd\u4e0d\u8981\u7684 \u3000\u3000\u7231\u548c\u4ec7\u6068\u7684\u529b\u91cf\u6280\u6821\u6bd4\u4ec0\u9ebd\u90fd\u5927\u3002 \u3000\u3000\u5927\u5f97\u7edd\u975e\u5979\u6240\u80fd\u60f3\u8c61\u3002\u9c9c\u8840\u98de\u6e85\u3002 \u3000\u3000\u6697\u8d64\u8272\u4e2d\u5e26\u4e73\u767d\u8272\u7684\u8840\u6d46\u98de\u6e85\u51fa\u6765\u96e8\u70b9\u822c\u6e85\u5728\u7ea2\u5a18\u5b50\u8138\u4e0a\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7684\u773c\u9669\u5df2\u88ab\u8840\u5149\u63a9\u4f4f\u53ea\u770b\u5230\u8d75\u8005\u5927\u7684\u4e00\u53cc\u5145\u6ee1\u4e86\u6124\u6012\u548c\u4ec7\u6068\u7684\u773c\u775b\u5ffd\u7136\u6b7b\u9c7c\u822c\u51f8 \u4e86\u51fa\u6765\u7136\u5f8c\u5c31\u88ab\u76bf\u5149\u63a9\u4f4f\u3002 \u3000\u3000\u5979\u7acb\u523b\u542c\u5230\u7247\u91ce\u517d\u843d\u4eba\u9677\u8df3\u65f6\u7684\u60ca\u6012\u543c\u58f0\u3002 \u3000\u3000\u51c4\u5389\u7684\u5200\u98ce\uff0c\u56db\u9762\u516b\u65b9\u5411\u5979\u780d\u4e0b\u6765\u3002 \u3000\u3000\u5979\u8dc3\u8d77\u95ea\u907f\xb7\u52c9\u5f3a\u60f3\u5f20\u5f00\u773c\u775b\u3002\u4f46\u5979\u8fd8\u662f\u8fde\u5200\u5149\u90fd\u770b\u4e0d\u89c1\u53ea\u80fd\u770b\u5f97\u5230\u4e2d\u8840\u5149\u3002 \u3000\u3000\u5979\u518d\u8dc3\u8d77\u53ea\u89c9\u5f97\u817f\u4e0a\u51c9\u597d\u50cf\u5e76\u4e0d\u592a\u75bc\xb7\u4f46\u8fd9\u6761\u817f\u4e0a\u7684\u529b\u91cf\u5374\u7a81\u7136\u6d88\u5931\u3002 \u3000\u3000\u5979\u8eab\u5b50\u7acb\u523b\u8981\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u5979\u77e5\u9053\u8fd9\u2026\u6c89\u4e0b\u53bb\u5c31\u5c06\u6c89\u4eba\u65e0\u8fb9\u7684\u9ed1\u6697\u4e07\u52ab\u4e0d\u590d\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u5979\u5fc3\u91cc\u5e76\u6ca1\u6709\u611f\u89c9\u5230\u6050\u60e7\uff0c\u53ea\u89c9\u5f97\u6709\u79cd\u5947\u5f02\u7684\u60b2\u54c0\u3002 \u3000\u3000\u5979\u5ffd\u7136\u53c8\u94f6\u8d75\u4e86\u738b\u52a8\u3002 \u3000\u3000\u4e2a\u4eba\u5728\u4e34\u6b7b\u524d\u7684\u5239\u90a3\u5fc3\u91cc\u5728\u60f3\u4ec0\u9ebd\uff1f \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u8bb8\u6ca1\u6709\u4eba\u80fd\u7b54\u7406\u3002 \u3000\u3000\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u5728\u8fd9\u79cd\u65f6\u5019\u60f3\u8d77\u7684\u4e8b\u90fd\u7edd\u5e74\u4f1a\u76f8\u540c\u3002 \u3000\u3000\u5979\u60f3\u7684\u662f\u5de5\u52a8\u60f3\u8d77\u4e86\u5e72\u52a8\u90a3\u5f20\u51b7\u51b0\u51b0\u7684\u80a1\u4e5f\u60f3\u8d77\u4e86\u738b\u52a8\u90a3\u9897\u706b\u70ed\u7684\u5fc3\u3002 \u3000\u3000\u5979\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e1d\u5fbd\u7b11\u5c31\u597d\u50cf\u89c9\u5f97\u53ea\u8981\u80fd\u542c\u5230\u8fd9\u5578\u58f0\xb7\u6b7b\u6d3b\u90fd\u65e0\u5173\u7d27\u8981\u3002 \u3000\u3000\u5578\u58f0\u6e05\u4eae\u5982\u9e70\u6620\u4e5d\u9704\u76d8\u65cb\u800c\u535c\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7684\u4eba\u4e5f\u5df2\u6c89\u4e0b\u3002 \u3000\u3000\u5979\u5ffd\u7136\u6709\u4e86\u79cd\u653e\u677e\u7684\u611f\u89c9\xb7\u89c9\u5f97\u5df2\u53ef\u4ee5\u653e\u677e\u5207\u56e0\u4e3a\u8fd9\u65f6\u4e00\u5207\u4e8b\u90fd\u5df2\u65e0\u5173\u7d27\u8981\u3002 \u3000\u3000\u5979\u5c31\u8fd9\u6837\u6c89\u5382\u4e0b\u6765\u5012\u5728\u5730\u4e0a\u5176\u81f3\u8fde\u773c\u775b\u90fd\u61d2\u5f97\u5f20\u5f00\uff0c\u5e78\u597d\u5979\u773c\u775b\u6ca1\u6709\u5f20\u5f00\u3002 \u3000\u3000\u5979\u82e5\u770b\u5230\u73b0\u5728\u7684\u60c5\u51b5\u5fc3\u4e5f\u8bb8\u4f1a\u788e\uff0c\u80a0\u4e5f\u8bb8\u4f1a\u65ad\xb7\u80c6\u4e5f\u8bb8\u4f1a\u88c2\u3002 \u3000\u3000\u95ea\u4eae\u7684\u5200\u5149\u4ea4\u7ec7\u780d\u5411\u7ea2\u5a18\u5b50\u3002\u7a81\u7136\u95f4\u2026\u4e2a\u4eba\u5e26\u957f\u5578\u81ea\u6797\u68a2\u51b2\u4e0b\xb7\u51b2\u4eba\u5200\u5149\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u5fd8\u4e86\u81ea\u5df2\u662f\u4e2a\u6709\u8840\u6709\u8089\u7684\u4eba\xb7\u4e5f\u5fd8\u4e86\u5200\u662f\u7528\u4ee5\u6740\u4eba\u7684\u3002\u4ed6\u5c31\u8fd9\u6837\u51b2\u5165\u5200\u5149\u3002 \u3000\u3000\u5200\u5149\u4e2d\u53c8\u6e85\u8d77\u4e86\u8840\u5149\u3002 \u3000\u3000\u6709\u4eba\u5728\u60ca\u547c\u201c\u9e70\u4e2d\u738b\u3002\u201d\u56db\u8273\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u6709\u4eba\u5728\u6050\u9a7e\u201c\u73b0\u5728\u5c31\u8981\u4ed6\u6b7b\u3002\u201d \u3000\u3000\u738b\u52a8\u5f53\u7136\u53ef\u80fd\u6b7b\u8fd9\u70b9\u4ed6\u77e5\u9053\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u77e5\u9053\u53ea\u8981\u4ed6\u6d3b\u5c31\u6ca1\u6709\u4eba\u80fd\u5728\u4ed6\u9762\u524d\u8981\u7ea2\u6d6a\u5b50\u6b7b\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u8840\u8089\u4e4b\u8eaf\u6321\u4f4f\u4e86\u6740\u4eba\u7684\u5200\u6321\u4f4f\u4e86\u7ea2\u5a18\u5b50\u7684\u8eab\u524d\u3002 \u3000\u3000\u5200\u867d\u950b\u5229\u800c\u6c89\u91cd\u4f46\u4ed6\u7edd\u4e0d\u9000\u5f8c\u3002 \u3000\u3000\u8fd9\u79cd\u52c7\u6c14\u4e0d\u4f46\u503c\u5f97\u5c0a\u656c\uff0c\u800c\u4e14\u53ef\u6015\u975e\u5e38\u7684\u53ef\u6015\u3002 \u3000\u3000\u71d5\u4e03\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u8eab\u4e0a\u5df2\u6709\u4e86\u4e03\u516b\u5904\u5200\u4f24\u6bcf\u9053\u521b\u53e3\u90fd\u7abf\u6d41\u8840\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u7684\u52c7\u6c14\u5f80\u5f80\u90fd\u968f\u6d41\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u3002 \u3000\u3000\u71d5\u4e03\u770b\u5230\u4ed6\u7684\u65f6\u5019\xb7\u5fc3\u867d\u6ca1\u6709\u788e\xb7\u80a0\u867d\u6ca1\u6709\u65ad\u4f97\u9c9c\u8840\u5df2\u51b2\u4e0a\u5934\u9876\u3002\u51b2\u4e0a\u54bd\u5589\u3002 \u3000\u3000\u5728\u8fd9\u77ac\u960e\u4ed6\u5ffd\u7136\u4e5f\u5fd8\u5382\u81ea\u5df1\u7684\u6b7b\u6d3b\u3002 \u3000\u3000\u52c7\u6c14\u662f\u4ece\u54ea\u91cc\u6765\u7684\u5462 \u3000\u3000\u6709\u65f6\u662f\u4e3a\u4e86\u8363\u8a89\u6709\u65f6\u662f\u4e3a\u4e86\u4ec7\u6068\xb7\u6709\u65f6\u662f\u4e3a\u4e86\u7231\u60c5\u6709\u65f6\u662f\u4e3a\u5382\u670b\u53cb\u3002 \u3000\u3000\u65e0\u8bba\u8fd9\u52c7\u6c14\u662f\u600e\u9ebd\u6765\u7684\u90fd\u540c\u6837\u503c\u5f97\u5c0a\u656c\u90fd\u540c\u6837\u53ef\u8d35\u3002 \u3000\u3000\u56db \u3000\u3000\u90ed\u5927\u8def\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u4e3a\u4e86\u4ec0\u9ebd\u65e0\u8bba\u5728\u5f81\u4f55\u60c5\u51b5\u4e0b\u4ed6\u90fd\u4e0d\u4f1a\u8ba9\u670b\u53cb\u62fc\u547d\u81ea\u5df1\u5374\u7559\u5728\u5c4b\u91cc\u4e0b\u68cb\u7684\u3002 \u3000\u3000\u53ea\u53ef\u501f\u4ed6\u6765\u7684\u65f6\u5019\u8840\u6218\u5df2\u7ed3\u675f\u3002 \u3000\u3000\u5730\u4e0a\u53ea\u6709\u4e5d\u67c4\u5200\u3002 \u3000\u3000\u6709\u7684\u5200\u8eba\u5728\u8840\u6cca\u4e2d\uff0c\u6709\u7684\u5200\u5d4c\u5728\u6811\u4e0a\uff0c\u6709\u7684\u5200\u950b\u5df2\u5377\xb7\u6709\u7684\u5200\u5df2\u65a9\u65ad\u3002 \u3000\u3000\u738b\u52a8\u6b63\u5728\u770b\u7ea2\u5a18\u5b50\u817f\u4e0a\u7684\u5200\u4f24\u5df2\u6d51\u5fd8\u4e86\u81ea\u5df1\u8eab\u7684\u5200\u4f24\u3002 \u3000\u3000\u71d5\u4e03\u9759\u9759\u770b\u4ed6\u4eec\u76ee\u5149\u4e2d\u4e5f\u4e0d\u77e5\u662f\u6b23\u559c\uff0c\u8fd8\u662f\u60b2\u4f24\u3002 \u3000\u3000\u90ed\u5927\u8def\u6084\u6084\u8d70\u8fc7\u53bb\xb7\u6084\u6084\u9053\u4eba\u5462\uff1f\u201d \u3000\u3000\u9884\u4e03\u4e5f\u540c\u65f6\u5728\u95ee\u201c\u4eba\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u95ee\u7684\u662f\u8c01\uff1f\u201d\u71d5\u4e03\u9053\u201c\u5c0f\u6797\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8bf4\u9053\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u7559\u4e0b\u5c0f\u6797\u4e2a\u4eba\u5728\u5c4b\u91cc\u7684\u3002\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u4f60\u5e26\u4ed6\u6765\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\xb7\u56de\u7b54\u9053\u201c\u4ed6\u5c31\u5750\u5728\u90a3\u8fb9\u7684\u5927\u6811\u4e0a\u9762\u3002\u201d \u3000\u3000\u4ece\u90a3\u91cc\u7684\u6811\u4e0a\u770b\u8fc7\u6765\u53ef\u4ee5\u770b\u5230\u8fd9\u91cc\u7684\u4e3e\u52a8\uff0c\u4f46\u8fd9\u91cc\u7684\u4eba\u5374\u770b\u4e0d\u89c1\u4ed6\u3002 \u3000\u3000\u8eb2\u85cf\u4e0d\u4f46\u8981\u6709\u6280\u5de7\uff0c\u4e5f\u662f\u79cd\u827a\u672f\u3002 \u3000\u3000\u201c\u5728\u6b63\u786e\u7684\u65f6\u95f4\u91cc\xb7\u627e\u4e2a\u6b63\u786e\u7684\u5730\u65b9\u3002\u201d\u8fd9\u5c31\u662f\u201c\u8eb2\u85cf\u201d\u8fd9\u4e24\u4e2a\u5b57\u5168\u90e8\u610f\u4e49\u7684\u7cbe\u7cb9\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u6211\u95ee\u7684\u662f\u90a3\u4e9b\u62ff\u5200\u7684\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u4eec\u90fd\u8d70\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5728\u5730\u4e0a\u62fe\u8d77\u628a\u5200\uff0c\u6382\u4e86\u6382\u5e26\u7b11\u9053\u201c\u96be\u602a\u4ed6\u4eec\u8981\u5c06\u5200\u7559\u4e0b\u5382\u3002\u8fd9\u9ebd\u91cd\u7684\u5200\u62ff\u5728\u624b \u91cc\xb7\u7684\u786e\u8dea\u4e0d\u5feb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u9519\u56e0\u4e3a\u4ed6\u4eec\u672c\u5c31\u4e0d\u662f\u5e38\u5e38\u4f1a\u9003\u8d70\u4e86\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u8ba4\u5f97\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u8ba4\u5f97\u4f46\u5374\u77e5\u9053\u4e2a\u53c1\u628a\u5927\u5200\u5728\u5173\u5185\u5173\u5916\u90fd\u5f88\u6709\u540d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u540d\u7684\u5f3a\u76d7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e5f\u662f\u6709\u540d\u7684\u786c\u6c49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u786c\u6c49\u8fd9\u6b21\u5374\u9003\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u6253\u6015\u6b7b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u4e0d\u6015\u6b7b\u4e3a\u4ec0\u9ebd\u8981\u9003\uff1f\u201d \u3000\u3000\u71d5\u4e03\u770b\u738b\u52a8\u9053\u201c\u4ed6\u4eec\u6015\u7684\u5e76\u4e0d\u662f\u6b7b\xb7\u800c\u662f\u6709\u4e9b\u4eba\u90a3\u79cd\u4ee4\u4eba\u4e0d\u80fd\u4e0d\u5bb3\u6015\u7684\u52c7\u6c14\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u63a5\u9053\u201c\u4e5f\u8bb8\u4ed6\u4eec\u6839\u672c\u4e0d\u662f\u5bb3\u9677\u800c\u662f\u611f\u52a8\u2026\u2026\u4ed6\u884c\u4e5f\u662f\u4eba\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53ef\u80fd\u6709\u88ab\u522b \u4eba\u611f\u52a8\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6c89\u9ed8\u4e86\u534a\u664c\u5ffd\u53c8\u95ee\u9053\u201c\u4ed6\u4eec\u600e\u9ebd\u6f6e\u9053\u7ea2\u5a18\u5b50\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u50ac\u547d\u7b26\u4ed6\u4eec\u6b7b\u5728\u8fd9\u91cc\u7684\u6d88\u606f\u6c5f\u6e56\u4e2d\u5df2\u6709\u5f88\u591a\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u9053\u201c\u6c5f\u6e56\u4e2d\u7684\u6d88\u606f\uff0c\u4f20\u5f97\u5012\u771f\u5feb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6c5f\u6e56\u4eba\u7684\u8033\u6735\u672c\u6765\u5c31\u5f88\u7075\u4f55\u51b5\u4ec7\u6068\u5f80\u5f80\u80fd\u4f7f\u4e2a\u4eba\u7684\u7b97\u6735\u66f4\u7075\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u6790\u7684\u4ec7\u7ed3\u5f97\u8fd9\u9ebd\u6df1\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5341\u53c1\u628a\u5200\u548c\u50ac\u547d\u7b26\u672c\u6765\u4e5f\u53ef\u7b97\u662f\u540c\u5925\u4ecd\u7ea2\u5a18\u5b50\u80e1\u51fa\u5356\u4e86\u4ed6\u4eec\u3002\u6709\u4e00\u6b21\u4ed6\u851a\u88ab\u4eba \u56f4\u653b\u7684\u65f6\u5019\u7ea2\u5a18\u5b50\u5c45\u7136\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\u201c\u8fd9\u79cd\u72d7\u54ac\u72d7\u7684\u4e8b\xb7\u6211\u61d2\u5f97\u542c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u542c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u738b\u52a8\u548c\u7ea2\u5a18\u5b50\u76ee\u4e2d\u6e10\u6e10\u9732\u51fa\u79cd\u67d4\u548c\u7684\u5149\u8f89\u7f13\u7f13\u9053\u201c\u73b0\u5728\u6211\u60f3\u542c\u4e00\u542c\u53ef\u4ee5\u4ee4\u4eba\u5fc3 \u91cc\u5feb\u4e50\u66f2\u4e8b\u7247\u706b\u51b3\u4e50\u7684\u6d88\u606f\xb7\u81c0\u5982\u8bf4\u2026\u2500\u201c \u3000\u3000\u71d5\u4e03\u770b\u4ed6\xb7\u76ee\u5149\u4e5f\u6e10\u6e10\u6e29\u67d4\xb7\u67d4\u58f0\u9053\u201c\u81c0\u5982\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8b6c\u5982\u8bf4\u6625\u5929\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u58f0\u97f3\u66f4\u6e29\u67d4\xb7\u9053\u201c\u4f60\u5df2\u7528\u4e0d\u518d\u95ee\u6625\u5929\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u6625\u5929\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u7728\u7728\u773c\xb7\u7b11\u9053\u201c\u5df2\u7ecf\u6765\u4e86\u9ebd\uff1f\u5728\u54ea\u91cc\uff1f\u6211\u600e\u9ebd\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8f6c\u5934\u53bb\u770b\u738b\u52a8\u548c\u7ea2\u5a18\u5b50\u6cf5\u58f0\u9053\u201c\u4f60\u5e94\u8be5\u770b\u89c1\u7684\xb7\u56e0\u4e3a\u5b83\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u58f0\u97f3\u4e5f\u5f88\u6e29\u67d4\xb7\u8f7b\u8f7b\u9053\u201c\u4e0d\u9519\u5b83\u7684\u786e\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4ed6\u770b\u66f2\u5374\u662f\u85cf\u4e03\u3002 \u3000\u3000\u738b\u52a8\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u6df3\u5929\u5c31\u5728\u71d5\u4e03\u7684\u773c\u775b\u91cc\u3002 \u3000\u3000\u9ec4\u91d1\u4e16\u754c \u3000\u3000\u75c5\u4eba\u662f\u79cd\u4ec0\u9ebd\u6837\u7684\u4eba\u5462 \u3000\u3000\u8fd9\u540d\u8bcd\u4e5f\u8c61\u5f88\u591a\u522b\u7684\u540d\u8bcd\u6837\u6709\u5f88\u591a\u79cd\u4e0d\u540c\u7684\u89e3\u91ca\u3002 \u3000\u3000\u6709\u7684\u4eba\u89e3\u91ca \u3000\u3000\u75c5\u4eba\u5c31\u662f\u79cd\u751f\u4e86\u75c5\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u79cd\u75c5\u4eba\u5f53\u7136\u65e0\u53ef\u975e\u8bae\u4f46\u5374\u8fd8\u4e0d\u591f\u5341\u5206\u6b63\u786e\u3002 \u3000\u3000\u6709\u65f6\u6ca1\u75c5\u7684\u4eba\u4e5f\u662f\u75c5\u4eba\u3002 \u3000\u3000\u8b6c\u5982\u8bf4\u53d7\u5382\u4f24\u7684\u4eba\u4e2d\u4e86\u6bd2\u7684\u4eba\u4f53\u80fd\u4e0d\u80fd\u628a\u4ed6\u4ecd\u7b97\u505a\u75c5\u4eba \u3000\u3000\u4e0d\u80fd\u3002 \u3000\u3000\u8fd8\u662f\u6625\u5929\u3002 \u3000\u3000\u738b\u6708\uff0c\u6b63\u662f\u8349\u4efb\u9e2f\u5b83\u7684\u6d53\u6625\u3002 \u3000\u3000\u767d\u96ea\u5df2\u6eb6\u5c3d\u3002\u5730\u4e0a\u4e00\u7247\u7eff\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b63\u5750\u5728\u7eff\u836b\u4e0b\u53d1\u6014\u3002 \u3000\u3000\u4ed6\u662f\u771f\u5728\u53d1\u6014\u56e0\u4e3a\u8fde\u71d5\u4e03\u8d70\u8fc7\u6765\u7684\u65f6\u5019\u4ed6\u90fd\u6ca1\u6709\u6ce8\u610f\u3002 \u3000\u3000\u75c8\u4e03\u672c\u6765\u53ef\u4ee5\u5413\u8df3\xb7\u672c\u6765\u4e5f\u5f88\u60f3\u5413\u4ed6\u773a\u7684\u3002 \u3000\u3000\u4f46\u662f\u770b\u5230\u4ed6\u7684\u6837\u5b50\uff0c\u71d5\u4e03\u5c31\u4e0d\u5fcd\u5413\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u4ec0\u9ebd\u6837\u5b50\u5462 \u3000\u3000\u4e00\u8138\u5403\u4e5f\u6c72\u5403\u9971\uff0c\u7761\u4e5f\u6ca1\u964b\u8db3\u7684\u6837\u5b50\u800c\u4e14\u5df2\u762b\u4e86\u5f88\u591a\u3002 \u3000\u3000\u71d5\u4e03\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u6084\u6084\u5730\u8d70\u8fc7\u53bb\uff0c\u8d70\u5230\u4ed6\u9762\u524d\u65f6\uff0c\u80a1\u4e0a\u5c31\u9732\u51fa\u7b11\u610f\u95ee\u9053\u201c\u5582\u6062\u5728\u53d1\u4ec0\u9ebd \u6014\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u59cb\u8d77\u5934\uff0c\u770b\u4e86\u4ed6\u534a\u5929\u5ffd\u7136\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u75c5\u4eba\u662f\u79cd\u4fd8\u9ebd\u6837\u7684\u4eba\uff1f\u201d\u6b22\u4e50\u82f1\u591f \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u79cd\u751f\u4e86\u75c5\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u64ad\u5934\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u81f3\u5c11\u4e0d\u5b8c\u5168\u5bf9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8981\u600e\u9ebd\u8bf4\u624d\u7b97\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u5382\u60f3\u9053\u201c\u5728\u5b69\u5b50\u6253\u7684\u773c\u4e2d\xb7\u53ea\u8981\u662f\u7edf\u5728\u5e8a\u4e0a\u4e0d\u80fd\u52a8\u7684\u4eba\u5c31\u662f\u75c5\u4eba\u8fd9\u79cd\u4eba\u5e76\u4e0d\u5b9a \u6709\u75c5\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e5f\u4e0d\u662f\u5b69\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u5728\u6211\u773c\u4e2d\u770b\u6765\u75c5\u4eba\u53ea\u4e0d\u8fc7\u662f\u79cd\u7279\u522b\u4f1a\u82b1\u94b1\u7684\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u662f\u4ec0\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u662f\u771f\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u786e\u5b9e\u662f\u771f\u8bdd\u3002 \u3000\u3000\u75c5\u4eba\u867d\u7136\u4e0d\u80fd\u559d\u9152\uff0c\u4f46\u5374\u8981\u5403\u836f\u3002 \u3000\u3000\u4e0d\u4f46\u5403\u836f\xb7\u800c\u4e14\u8fd8\u8981\u5403\u8865\u54c1\xb7\u8fd9\u4e9b\u4e1c\u897f\u901a\u5e38\u90fd\u6bd4\u9152\u8d35\u3002 \u3000\u3000\u71d5\u4e03\u5f53\u7136\u4e5f\u77e5\u9053\u8fd9\u662f\u771f\u8bdd\u56e0\u4e3a\u8fd9\u5730\u65b9\u73b0\u5728\u6709\u53c1\u4e2a\u75c5\u4eba\u3002 \u3000\u3000\u6797\u592a\u5e73\u7684\u4f24\u8fd8\u6ca1\u597d\xb7\u53c8\u591a\u4e86\u7ea2\u5a18\u5b50\u548c\u738b\u52a8\u3002 \u3000\u3000\u71d5\u4e03\u677f\u8d77\u4e86\u8138\u9053\u201c\u5c31\u7b97\u771f\u662f\u5b9e\u8bdd\u4f60\u4e5f\u4e0d\u8be5\u8fd9\u9ebd\u6837\u8bf4\u7684\u3002\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u7684\u786e\u4e0d\u8be5\u8fd9 \u9ebd\u6837\u8bf4\u7684\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u73b0\u5728\u5df2\u7ecf\u4fa0\u53d8\u6210\u4e2a\u6b7b\u4eba\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u671b\u9762\u524d\u7684\u53e0\u4e1c\u897f\u82e6\u8138\u9053\u201c\u7167\u8fd9\u6837\u4e0b\u53bb\u7528\u4e0d\u4e24\u5929\uff0c\u6211\u60f3\u4e0d\u8df3\u6cb3\u90fd\u4e0d\u884c\u3002\u201d \u3000\u3000\u4ed6\u9762\u524d\u6446\u7684\u662f\u5927\u53e0\u5e10\u5355\u3002 \u3000\u3000\u5e10\u5355\u7684\u610f\u601d\u5c31\u662f\u522b\u4eba\u8981\u95ee\u4ed6\u8981\u94b1\u7684\u90a3\u79cd\u5355\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ece\u4e2d\u95f4\u62bd\u51fa\u5f20\uff0c\u5ff5\u9053\u201c\u7cbe\u7eaf\u71d5\u7a9d\u4e94\u4e24\u7eb9\u94f6\u529b\u4e8c\u4e24 \u3000\u3000\u4ed6\u5c06\u8fd9\u5355\u5b50\u91cd\u91cd\u6454\uff0c\u957f\u53f9\u9053\u201c\u2019\u4e2a\u9e1f\u505a\u7684\u7a9d\u5c45\u7136\u80fd\u8fd9\u9ebd\u503c\u94b1\uff0c\u65e9\u77e5\u8fd9\u6837\u5b50\u6211\u851a\u4e0d\u5982\u53d8\u6210 \u53ea\u4e4c\u7b97\u4e86\u4e5f\u514d\u5f97\u88ab\u836f\u94fa\u7684\u4eba\u6765\u903c\u5e10\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e00\u7b11\u9053\u201c\u4f60\u672c\u6765\u5c31\u662f\u53ea\u9e1f\u5446\u9e1f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u6c14\u7684\u58f0\u97f3\u66f4\u957f\u9053\u201c\u6211\u76f8\u4fe1\u5c31\u7b97\u662f\u771f\u7684\u5446\u9e1f\xb7\u4e5f\u7edd\u4e0d\u4f1a\u6765\u7ba1\u5e10\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\u9053\u201c\u8c01\u53eb\u4f60\u6765\u7ba1\u5e10\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6307\u81fc\u5df1\u7684\u9f3b\u5b50\xb7\u8bf4\u9053\u201c\u6211\u6211\u8fd9\u53ea\u5446\u9e1f\u3002\u201d \u3000\u3000\u7684\u786e\u662f\u4ed6\u81ea\u5df1\u62a2\u5173\u8981\u7ba1\u5e10\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u3001\u7ea2\u5a18\u5b50\u548c\u738b\u52a8\u90fd\u5df2\u4e0d\u80fd\u52a8\u80fd\u52a8\u7684\u4eba\u53ea\u5269\u4e0b\u4ed6\u613f\u71d5\u4e03\u4e24\u4e2a\u8981\u505a\u7684\u4e8b\u5374\u6709\u5f88\u591a\u3002 \u3000\u3000\u71d5\u4e03\u95ee\u4ed6\u9053\u201c\u4f60\u662f\u8981\u7ba1\u5bb6\u8fd8\u662f\u7ba1\u5e10\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\xb7\u5c31\u62a2\u8bf4\u201c\u7ba1\u8d26\u3002\u201d \u3000\u3000\u5728\u4ed6\u60f3\u6765\u7ba1\u5e10\u6bd4\u716e\u836f\u4fa5\u7ca5\u4f8d\u75c5\u4eba\u5bb9\u6613\u5f97\u591a\u4e5f\u6109\u5feb\u7684\u591a\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u77e5\u9053\u81ea\u5df1\u9519\u5382\xb7\u9519\u5f97\u5f88\u5389\u5bb3\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u5929\u4e0b\u518d\u4e5f\u6ca1\u6709\u6bd4\u7ba1\u5e10\u66f4\u5bb9\u6613\u7684\u4e8b \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\u9053\u201c\u54e6\uff1f\u201d\u7ff1\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4ee5\u524d\u90a3\u51e0\u4e2a\u6708\u91cc\uff0c\u6211\u4eec\u6839\u672c\u6ca1\u6709\u5e10\u53ef\u7ba1\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u5c31\u7b97\u6709\u8d26\u4e5f\u662f\u7b14\u7cca\u6d82\u5e10\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u53c8\u56db\u4e86\u95e8\u6c14\u2606\u63a5\u9053\u201c\u90a3\u65f6\u6211\u4ecd\u6709\u94b1\u5c31\u53bb\u5403\u70b9\u559d\u70b9\u6ca1\u94b1\u5c31\u618b\uff0c\u5c31\u7b97\u6574\u5929\u4e0d\u5403\u4e0d\u559d\u90fd\u6ca1\u5173 \u7cfb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4eec\u81f3\u5c11\u8fd8\u53ef\u4ee5\u5927\u996e\u513f\u9f50\u51fa\u4e3b\u610f\u53bb\u627e\u94b1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u73b0\u5728\u5374\u4e0d\u540c\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\u4e5f\u4e0d\u7981\u957f\u53f9\u4e86\u58f0\uff0c\u9053\u201c\u73b0\u5728\u7684\u786e\u4e2a\u95ee \u3000\u3000\u75c5\u4eba\u65e2\u4e0d\u80fd\u997f\uff0c\u66f4\u4e0d\u80fd\u5403\u836f\u3002 \u3000\u3000\u6240\u4ee5\u4e0d\u7ba1\u4ed6\u4eec\u6709\u94b1\u6ca1\u94b1\uff0c\u6bcf\u5929\u90fd\u6709\u7b14\u56fa\u5b9a\u7684\u5f00\u652f\u662f\u7701\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u90a3\u7b14\u5f00\u652f\u8fd8\u771f\u4e0d\u5c11\u3002 \u3000\u3000\u51fa\u652f\u610f\u53bb\u627e\u94b1\u7684\u4eba\u53cd\u800c\u8fde\u4e2a\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u8981\u5fd9\u7167\u987e\u75c5\u4eba\xb7\u90ed\u5927\u8def\u8981\u62fc\u547d\u52a8\u8111\u7b4b\u8d4a\u5e10\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u6211\u53ea\u5947\u602a\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u867d\u7136\u6ca1\u6709\u5728\u6c5f\u6e56\u4e2d\u6df7\u8fc7\u4eba\u76ee\u6c5f\u6e56\u597d\u6c49\u7684\u6545\u4e8b\u5374\u4e5f\u542c\u8fc7\u4e0d\u5c11\u600e\u9ebd\u4ece\u6765\u6ca1\u6709\u542c \u8fc7\u6709\u4eba\u4e3a\u94b1\u53d1\u6101\u7684\uff1f\u201d \u3000\u3000\u4ed6\u82e6\u7b11\u53c8\u9053\u201c\u90a3\u4e9b\u5165\u597d\u50cf\u968f\u65f6\u90c1\u6709\u5927\u628a\u5915\u2606\u628a\u7684\u94f6\u5b50\u5f80\u5916\u638f\u90a3\u4e9b\u94f6\u5b50\u5c31\u597d\u50cf\u4ece\u5929\u4e0a\u6389\u4e0b \u6765\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u60f3\u4e86\u60f3\xb7\u9053\u201c\u4ee5\u5f8c\u82e5\u6709\u4eba\u8bf4\u8d77\u6211\u4eec\u7684\u6545\u7387\xb7\u4e5f\u7edd\u4e0d\u4f1a\u8bf4\u6211\u4eec\u4e3a\u94b1\u53d1\u6101\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u8bf4\u6545\u4e8b\u7684\u4eba\u603b\u4ee5\u4e3a\u522b\u4eba\u4e0d\u559c\u6b22\u542c\u8fd9\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f97\u8fd9\u5374\u662f\u771f\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u771f\u4e8b\u867d\u7136\u662f\u771f\u4e8b\u4f46\u8fd9\u4e16\u4e0a\u6562\u8bf4\u771f\u8bdd\u7684\u4eba\u5374\u4e0d\u591a\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u6562\u8bf4\uff1f\u4f2f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6015\u522b\u4eba\u4e0d\u542c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u90a3\u4e9b\u8bf4\u6545\u4e8b\u7684\u4eba\u90fd\u662f\u5446\u5b50\xb7\u96be\u9053\u4ed6\u4eec\u4e0d\u660e\u767d\u771f\u4e8b\u4e5f\u2026\u6837\u6709\u4eba\u559c\u6b22\u542c\u7684\u201c \u3000\u3000\u4ed6\u60f3\u4e86\u60f3\xb7\u53c8\u8865\u5145\u9053\u201c\u90a3\u4e9b\u795e\u8bdd\u4f20\u8bf4\u822c\u7684\u6545\u4e8b\u542c\u8d77\u6765\u4e5f\u8bb8\u6bd4\u8f83\u8fc7\u762a\u4e9b\u4f46\u771f\u7684\u4e8b\u5374\u5b9a\u66f4\u80fd \u611f\u52a8\u522b\u4eba\u53ea\u6709\u771f\u80fd\u611f\u52a8\u4eba\u5fc3\u7684\u6545\u4e8b\u624d\u80fd\u6c38\u8fdc\u5b58\u5728\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\u9053\u201c\u8fd9\u4e9b\u8bdd\u4f60\u6700\u597d\u53bb\u8bf4\u7ed9\u8fd9\u4e9b\u8bf4\u6545\u4e8b\u7684\u4eba\u53bb\u542c\u3001\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u4e0d\u662f\u61d2\u5f97 \u542c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u60f3\u542c\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u85cf\u4e03\u9053\u201c\u6211\u60f3\u542c\u542c\u6211\u4eec\u73b0\u5728\u7a76\u7adf\u5df2\u4e8f\u7a7a\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4e0d\u591a\u8fd8\u4e0d\u5230\u4e07\u4e24\u94f6\u5b50\u201d \u3000\u3000\u4e07\u4e24\u94f6\u5b50\u7684\u4e8f\u7a7a\u5728\u67d0\u4e9b\u4eba\u773c\u4e2d\u770b\u6765\u7684\u786e\u4e0d\u7b97\u591a\u3002 \u3000\u3000\u5728\u90ed\u5927\u8def\u6709\u94b1\u7684\u65f6\u5019\u770b\u6765\xb7\u8fd9\u4e8f\u7a7a\u4e5f\u4e0d\u80fd\u7b97\u591a\u3002 \u3000\u3000\u95ee\u9898\u5e76\u4e0d\u5728\u4e8f\u4e2d\u4e86\u591a\u5c11\u800c\u5728\u4f60\u6709\u591a\u5c11\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u4e07\u4e24\u94f6\u5b50\u7684\u5e10\xb7\u662f\u4e0d\u662f\u90fd\u6025\u8981\u8fd8\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8981\u5e10\u7684\u4eba\u5df2\u7ecf\u903c\u5f97\u6211\u8981\u773a\u6cb3\u4e86\uff0c\u4f60\u8bf4\u6025\u4e0d\u6025\uff1f\u201d\u85cf\u4e03\u9053\u89c2\u5728\u6211\u4eec\u624b\u5934\u8fd8\u5269\u591a \u5c11\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u4e0d\u5c11\u201c\u4e00\u201c\u518d\u52a0\u4e8c\u94b1\u5c31\u53ef\u4ee5\u51d1\u8db3\u4e24\u94f6\u5b50\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u5f00\u59cb\u53d1\u6014\u3002 \u3000\u3000\u4e24\u94f6\u5b50\u548c\u4e07\u4e24\u94f6\u5b50\u7684\u5dee\u522b\uff0c\u5c31\u662f\u5dee\u4e5d\u5e72\u4e5d\u767e\u4e5d\u5bf8\u2018\u4e5d\u4e24\u94f6\u5b50 \u3000\u3000\u8fd9\u7b14\u5e10\u4eba\u4eba\u90fd\u4f1a\u7b97\u7684\u3002 \u3000\u3000\u6240\u4ee5\u71d5\u4e03\u53ea\u6709\u53d1\u5f81\u3002 \u3000\u3000\u6014\u4e86\u534a\u5929\u4ed6\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u73b0\u5728\u6211\u624d\u603b\u7b97\u660e\u767d\u7a77\u7684\u610f\u601d\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u76f4\u5230\u73b0\u5728\u624d\u660e\u767d\uff1f\u201d \u3000\u3000\u9893\u4e03\u70b9\u70b9\u5934\u9053\u201c\u56e0\u4e3a\u4ee5\u524d\u6211\u4eec\u867d\u7136\u6ca1\u94b1\u4f46\u4e5f\u4e0d\u6b20\u522b\u4eba\u7684\u503a\u6240\u4ee5\u90a3\u8fd8\u4e0d\u80fd\u7b97\u7a77\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u73b0\u5728\u6211\u53ea\u8981\u80fd\u4e0d\u6b20\u522b\u4eba\u7684\u503a\u6211\u60c5\u613f\u5728\u5730\u535c\u722c\u4e8c\u5929\u53c1\u591c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ea\u53ef\u501f\u4f60\u5c31\u7b97\u722c\u53c1\u5e74\xb7\u4e5f\u722c\u4e0d\u51fa\u4e07\u4e24\u94f6\u5b50\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7528\u4e0d\u4e07\u4e24\u53ea\u8981\u4e5d\u5e72\u4e5d\u767e\u591a\u4e24\u5c31\u884c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u95ee\u9898\u662f\u4f60\u600e\u9ebd\u53bb\u5f04\u8fd9\u4e5d\u5e72\u4e5d\u767e\u591a\u4e24\u94f6\u5b50\u5462\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u6ca1\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u5382\u7728\u773c\u9053\u201c\u6211\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u591f\u53bb\u505a\u5f3a\u76d7\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u6211\u4eec\u4e0d\u662f\u505a\u5f3a\u76d7\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8981\u54ea\u79cd\u4eba\u624d\u80fd\u505a\u5f3a\u76d7\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4e0d\u662f\u4eba\u7684\u90a3\u79cd\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u80fd\u4e0d \u80fd\u52ab\u5bcc\u6d4e\u8d2b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u52ab\u5bcc\u6d4e\u8d2b\u7684\u4e49\u4e0d\u662f\u5f3a\u6d47\u53ea\u80fd\u7b97\u662f\u50a8\u6fc0\u3001\u82f1\u96c4\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u60f3 \u53bb\u52ab\u6dee\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u4e9b\u4e3a\u5bcc\u4e0d\u4ec1\u7684\u5978\u5546\u5265\u524a\u8001\u767e\u7ee7\u7684\u8d2a\u5b98\u6c61\u540f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u52ab\u5b8c\u5382\u53bb\u6d4e\u96be\u7684\u8d2b\u5462\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u662f\u5148\u6551\u54b1\u4eec\u81ea\u5df1\u7684\u6025\u6d4e\u54b1\u201c\u81ea\u5df1\u7684 \u8d2b\u3002\u201d \u3000\u3000\u71d5\u4e03\u8c08\u6de1\u9053\u201c\u90a3\u5c31\u4e0d\u662f\u82f1\u96c4\u662f\u72d7\u718a\u4e86\u3002\u201d\u4ed6\u63a5\u53c8\u9053\u201c\u5c31\u56e0\u4e3a\u4ee5\u5185\u826f\u591a\u4eba\u6709\u8fd9\u79cd\u72d7\u718a\u60f3\u6cd5 \u6240\u4ee5\u4e16\u4e0a\u624d\u4f1a\u6709\u8fd9\u9ebd\u591a\u5f3a\u64ad\u3002 \u3000\u3000\u4e5f\u8bb8\u4e16\u4e0a\u5927\u591a\u6570\u5f3a\u76d7\u5dde\u6b63\u90fd\u662f\u4ece\u8fd9\u79cd\u81ea\u5df1\u9a97\u81fc\u5df1\u7684\u60f3\u6cd5\u4e2d\u6765\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u60f3\u5382\u60f3\uff0c\u82e6\u7b11\u9053\u201c\u7167\u4f60\u8fd9\u9ebd\u6837\u8bf4\u770b\u6765\u6211\u4eec\u53ea\u6709\u6761\u8def\u5bf9\u4ee5\u8d70\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56db\u6761\u8def\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8d56\u5e10\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8981\u54ea\u79cd\u4eba\u624d\u80fd\u8d56\u5e10\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u77e5\u9053\u6240\u4ee5\u4ed6\u53f9\u4e86\u53e3\u6c14\u9053\u4e0d\u8981\u8138\u7684\u90a3\u79cd\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u4f60\u80fd\u4e0d\u80fd\u8d56\u5e10\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4f55\u51b5\u4ed6\u5c31\u7b97\u80fd\u8d56\u5e10\u4e5f\u4e0d\u884c\u3002 \u3000\u3000\u738b\u52a8\u4ed6\u4eec\u7684\u4f24\u8fd8\u6ca1\u6709\u597d\uff0c\u8fd8\u9700\u8981\u7ee7\u7eed\u5403\u836f\u7ee7\u7eed\u8fdb\u8865\u3002 \u3000\u3000\u4f60\u8d56\u4e86\u8fd9\u6b21\u5e10\xb7\u4e0b\u6b21\u8fd8\u6709\u8c01\u8d4a\u7ed9\u4f60\uff1f \u3000\u3000\u751f\u8d22\u4e4b\u9053 \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u7167\u8fd9\u6837\u8bf4\u6765\u6211\u4eec\u5c82\u975e\u5df2\u65e0\u8def\u53ef\u8d70\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8c01\u8bf4\u6211\u4f70\u5df2\u65e0\u8def\u53ef\u8d70\uff1f\u8def\u672c\u662f\u4eba\u8d70\u51fa\u6765\u7684\uff0c\u53ea\u8981\u4f60\u6709\u51b3\u5fc3\u53ea\u8981\u4f60\u80af\u8d70\xb7\u5c31\u5b9a\u6709 \u8def\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u9053\u7406\u6211\u660e\u767d\uff0c\u800c\u4e14\u4e5f\u8bf4\u7ed9\u522b\u4eba\u542c\u8fc7\xb7\u53ef\u662f\u73b0\u5728 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u6211\u53ea\u76f8\u4fe1\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54ea\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4eca\u5929\u6211\u82e5\u8fd8\u6ca1\u6709\u628a\u6b20\u7684\u94b1\u62ff\u53bb\u9001\u7ed9\u4eba\u5bb6\u4eca\u5929\u6211\u8ba2\u5c31\u5f97\u65ad\u708a\u3002\u201d \u3000\u3000\u4e16\u4e0a\u6709\u5f88\u591a\u9053\u7406\u90fd\u5f88\u597d\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u65e0\u8bba\u591a\u597d\u7684\u9053\u7406\u4e5f\u5356\u4e0d\u4e86\u4e5d\u5e72\u4e5d\u767e\u4e5d\u5341\u4e5d\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u8fde\u4e24\u94f6\u5b50\u90fd\u5356\u4e0d\u5382\u3002 \u3000\u3000\u521a\u662f\u4e2a\u53d1\u6014\uff0c\u73b0\u5728\u662f\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u53d1\u6014\u6bd4\u4e2a\u66f4\u96be\u53d7\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b80\u76f4\u5df2\u53d7\u4e0d\u5382\uff0c\u7ad9\u8d77\u6765\u515c\u5382\u5341\u4e03\u516b\u4e2a\u5708\u5b50\xb7\u5ffd\u7136\u53eb\u5382\u8d77\u6765\uff0c\u9053\u201c\u6211\u60f3\u8d77\u53e5\u8bdd\u6765 \u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u7528\u773c\u89d2\u8198\u5382\u4ed6\u4e00\u773c\u9053\u201c\u4e00\u65ec\u5229\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53e5\u5f88\u6709\u7528\u7684\u8bdd\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6709\u4ec0\u9ebd\u7528\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u81f3\u5c11\u53ef\u4ee5\u7528\u6765\u6551\u6025\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\xb7\u6211\u5012\u4e5f\u60f3\u542c\u542c\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u670b\u53cb\u6709\u901a\u8d22\u4e4b\u4e49\u8fd9\u53e5\u8bdd\u4f60\u60f3\u5fc5\u4e5f\u542c\u8fc7\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u53bb\u627e\u522b\u4eba\u501f\u94b1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u53bb\u627e\u522b\u4eba\uff0c\u662f\u53bb\u627e\u670b\u53cb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u4e16\u4e0a\u53ea\u6709\u79cd\u4eba\u7684\u670b\u53cb\u6700\u5c11\xb7\u4f60\u77e5\u4e0d\u77e5\u9053\u662f\u54ea\u79cd \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u662f\u60f3\u53bb\u627e\u670b\u53cb\u501f\u94b1\u7684\u90a3\u79cd\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u4e0d\u60f3\u53bb\u627e\u5f88\u591a\u670b\u53cb\xb7\u60f3\u53bb\u627e\u4e2a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b49\u4f60\u60f3\u53bb\u627e\u670b\u53cb\u5f00\u53e3\u501f\u94b1\u7684\u65f6\u5019\u4f60\u4e5f\u8bb8\u5c31\u4f1a\u53d1\u73b0\u81ea\u5df1\u8fde\u4e2a\u670b\u53cb\u90fd\u6ca1\u6709\u3002\u201d\uff01 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u50cf\u6211\u4eec\u8fd9\u79cd\u670b\u53cb\u201c\u2026\u201c \u3000\u3000\u71d5\u4e03\u9053\u201c\u82e5\u662f\u50cf\u6211\u4eec\u8fd9\u79cd\u670b\u53cb\u6839\u672c\u5c31\u7528\u4e0d\u7b49\u4f60\u5f00\u53e3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u5929\u4e0b\u6839\u672c\u5c31\u6ca1\u6709\u5f25\u53ef\u4ee5\u5f00\u53e3\u501f\u94b1\u7684\u670b \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e2a\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5374\u8ba4\u4e3a\u6709\u4e2a\u3001\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u9178\u6885\u6ca5\u3002\u201d \u3000\u3000\u71d5\u4e03\u6273\u8d77\u5382\u8138\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e0d\u662f\u8981\u4f60\u53bb\u5f00\u53e3\uff0c\u6211\u53ef\u4ee5\u53bb\uff0c\u6211\u603b\u7b97\u5e2e\u8fc7\u5979\u7684\u5fd9\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u51b7\u7b11\u9053\u201c\u4e16\u4e0a\u4e5f\u53ea\u6709\u79cd\u4eba\u4f1a\u72ac\u627e\u5973\u4eba\u501f\u94b1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u7684\u662f\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u5446\u5b50\u53ea\u6709\u5446\u5b50\u5201\u4f1a\u8ba4\u4e3a\u5973\u4eba\u80af\u50a8\u2018\u4e07\u4e24\u94f6\u5b50\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u77e5\u9053\u4ea5\u4e8e\u603b\u6bd4\u7537\u4eba\u5c0f\u6c14\u4e9b\uff0c\u4f46\u5728\u5979\u7684\u670d\u5dfe\u770b\u7c73\u4e07\u4e24\u94f6\u5b50\u5e94\u8be5\u7b97\u4e0d\u4e86\u4ec0\u9ebd \u7684\u3002\u201d\u71d5\u4e03\u9053\u201c\u7684\u786e\u7b97\u4e0d\u4e86\u9500\u9ebd\xb7\u53ea\u4e0d\u8fc7\u662f\u706f\u4e24\u94f6\u5b50\u800c\u5df2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u5979\u5e76\u4e0d\u5c0f\u6c14\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u518d\u5927\u65b9\u7684\u5973\u4eba\u4e5f\u4e0d\u4f1a\u501f\u94b1\u7ed9\u7537\u4eba\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u529f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff0c\u201c\u56e0\u4e3a\u5973\u4eba\u7684\u60f3\u6cd5\u4e0d\u540c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u4ec0\u9ebd\u4e0d\u540c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u5979\u6253\u603b\u8ba4\u4e3a\u80af\u5411\u4ea5\u4eba\u5f00\u53e3\u501f\u94b1\u7684\u7537\u4eba\u5b9a\u662f\u6700\u6ca1\u51fa\u606f\u7684\u7537\u4eba\u3002\u65e8\u501f\u94b1\u7ed9\u7537\u4eba \u7684\u5973\u4eba\xb7\u4e5f\u6837\u6ca1\u51fa\u606f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u5929\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\u201c\u5176\u5b9e\u5973\u4eba\u7684\u60f3\u6cd5\u7a76\u7adf\u600e\u9ebd\u6837\u4e5f\u53ea\u6709\u6587\u4eba\u81ea\u5df1\u624d\u77e5\u9053\u4f60 \u53c8\u672c\u662f\u4e2a\u4ea5\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u677f\u8138\uff0c\u9053\u201c\u6211\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6240\u4ee5\u4f60\u4e5f\u4e0d\u77e5\u9053\xb7\u6240\u4ee5\u6211\u8fd8\u60f3\u53bb\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u82e5\u662f\u53bb\u78b0\u9489\u5b50\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\xb7\u9053\u201c\u5c31\u7b97\u78b0\u9489\u5b50\uff0c\u78b0\u7684\u4e5f\u662f\u77f3\u5934\u9489\u5b50\xb7\u603b\u6bd4\u78b0\u522b\u4eba\u7684\u94c1\u9489\u4e8e\u597d\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\u54fa\u8d2d\u9053\u201c\u5047\u5982\u4e16\u4e0a\u8fd8\u6709\u91d1\u4e8e\u3001\u94f6\u9489\u5b50\xb7\u6211\u5012\u60c5\u613f\u53bb\u591a\u8e6c\u51e0\u4e2a\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u773c\u775b\u5ffd\u7136\u4eae\u4e86\xb7\u5ffd\u7136\u8df3\u8d77\u6765\u5927\u58f0\u9053\u201c\u4f60\u603b\u7b97\u8bf4\u4e86\u65ec\u771f\u6709\u7528\u7684\u8bdd\u4e86\u201c \u3000\u3000\u90ed\u5927\u8def\u53cd\u800c\u6014\u4efb\u5450\u54cd\u9053\u201c\u6211\u8bf4\u4e86\u4ec0\u9ebd\uff1f\u6709\u4ec0\u9ebd\u7528\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u53e5\u8bdd\u975e\u4f46\u771f\u6709\u7528\u800c\u4e14\u8fd8\u771f\u503c\u94b1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u66f4\u542c\u4e0d\u61c2\u3002 \u3000\u3000\u70ed\u4e03\u5df2\u4ece\u5730\u4e0a\u6361\u8d75\u5382\u4e03\u516b\u5757\u77f3\u5934\xb7\u9053\u201c\u4f60\u77e5\u4e0d\u7eab\u9053\u6211\u7684\u6697\u5668\u529f\u592b\u4e0d\u9519\uff1f\u201d\u90ed\u5927\u8def\u6447\u592e\u9053 \u201c\u672f\u7ec6\u9053\xb7\u4f60\u53c8\u6ca1\u6709\u7528\u6697\u5668\u6765\u5bf9\u4ed8\u8fc7\u6211\u3002\u201d \u3000\u3000\u85cf\u4e03\u9053\u201c\u6211\u82e5\u7528\u6697\u5668\u5bf9\u4ed8\u4f60\u4f60\u80fd\u4e0d\u80fd\u63a5\u4efb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u4e0d\u60f3\u8bd5\u8bd5\u770b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u60f3\u3002\u201d\u71d5\u4e03\u9053\u201c\u4e0d\u60f3\u4e5f\u4e0d\u884c\xb7\u4f60\u975e\u8bd5\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4ed6\u624b\u91cc\u7684\u77f3\u5934\u5ffd\u7136\u4ee5\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5\u5411\u7ff1\u5927\u8def\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u771f\u6253\u5382\u8fc7\u53bb\u70b9\u4e5f\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u6697\u5668\u4e2d\u6709\u79cd\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5\xb7\u6c5f\u6e56\u4e2d\u51e0\u4e4e\u4eba\u4eba\u90fd\u77e5\u9053\u90fd\u542c\u8fc7\u3002 \u3000\u3000\u4f46\u771f\u6b63\u770b\u8fc7\u8fd9\u79cd\u624b\u6cd5\u7684\u4eba\u5df2\u4e0d\u591a\uff0c\u771f\u4f1a\u7528\u8fd9\u79cd\u624b\u6cd5\u7684\u5f53\u7136\u66f4\u5c11\u3002 \u3000\u3000\u73b0\u5728\u90ed\u5927\u8def\u603b\u7b97\u770b\u5230\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u975e\u4f46\u771f\u4f1a\u7528\u8fd9\u79cd\u624b\u6cd5\uff0c\u4e3d\u6c11\u8fd8\u7528\u7684\u771f\u4e0d\u9519\u3002 \u3000\u3000\u4e03\u516b\u5757\u77f3\u5b50\u66b4\u96e8\u822c\u5411\u90ed\u5927\u8def\u6253\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f6c\u8eab\u3001\u9519\u6b65\xb7\u907f\u5f00\u4e86\u4e24\u53c1\u5757\u77f3\u5934\uff0c\u53c8\u4f38\u624b\u63a5\u4efb\u4e86\u53c1\u56db\u5757\u5374\u8fd8\u662f\u6709\u4e24\u5757\u6253\u5728\u4ed6\u8eab \u4ea1\uff0c\u6253\u5f97\u4ed6\u53eb\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u77aa\u71d5\u4e03\uff0c\u5927\u58f0\u9053\u201c\u4f60\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u82d1\u4e03\u7b11\u9053\u201c\u4e5f\u6ca1\u4ec0\u9ebd\u522b\u7684\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u60f3\u8981\u4f60\u53bb\u5acc\u51e0\u5343\u4e24\u94f6\u5b50\u56de\u6765\u964c\u5df2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4e86\u6014\u9053\u201c\u7528\u4ec0\u9ebd\u53bb\u8d5a\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7528\u5f25\u7684\u624b\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u63a5\u53c8\u9053\u201c\u4f60\u7684\u624b\u5df2\u7ecf\u86ee\u7075\u7684\u4e86\uff0c\u80fd\u63a5\u4efb\u6211\u56db\u4ef6\u6697\u5668\u7684\u4eba\u5df2\u4e0d\u591a\u53ea\u8981\u518d\u7ec3\u51e0 \u6b21\xb7\u53bb\u8d5a\u51e0\u5343\u4e24\u94f6\u5b50\u7b80\u76f4\u6613\u5982\u53cd\u638c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u81ea\u5df1\u7684\u624b\xb7\u8d8a\u770b\u8d8a\u6eaf\u5f90\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u770b\u4e0d\u51fa\u8fd9\u53cc\u624b\u51ed\u4ec0\u9ebd\u80fd\u86fe\u51e0\u5343\u4e24\u94f6\u5b50\u2026\u3002\u2019\u82e5\u8981\u4ed6\u8fd9\u53cc\u624b\u53bb\u8f93\u4e2a\u51e0\u5343\u4e24\u94f6\u5b50\u90a3\u5012 \u771f\u662f\u6613\u5982\u53cd\u638c \u3000\u3000\u4ed6\u628a\u50b2\u4e8e\u5c31\u8f93\u8fc7\u51e0\u5343\u4e24\u3002 \u3000\u3000\u71d5\u4e03\u53c8\u5728\u90a3\u91cc\u6361\u77f3\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u7a76\u7adf\u60f3\u8981\u6211\u53bb\u5e72\u4ec0\u9ebd\uff1f\u53bb\u63b7\u94f6\u5b50\u9a97\u4eba\u7684\u94b1\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u63b7\u822c\u4e8e\u4f60\u8fd8\u80fd\u53bb\u9a97\u8c01\u7684\u94b1\uff1f\u4f60\u5c31\u662f\u8f93\u738b\u4e4b\u738b\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u8f93\u5f97\u66f4\u5feb\u7684\u6cd5\u5b50\uff0c\u786e\u5b9e\u6ca1\u6709\u4e86\u8fd9\u6b21\u6211\u662f\u8981\u4f60\u53bb\u8d62\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8f93\u738b\u4e4b\u738b\u600e\u80fd\u6e90\u5f97\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ea\u8981\u4f60\u80fd\u4e00\u5382\u5b50\u5c06\u6211\u6240\u6709\u6697\u5668\u63a5\u4f4f\uff0c\u6211\u5c31\u5305\u4f60\u80fd\u8d62\u5f97 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u8fd8\u662f\u8f93\u5462\uff1f\u6211\u62ff\u4ec0\u9ebd\u8f93\u7ed9\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u8fd9\u6b27\u4f60\u82e5\u8fd8\u662f\u8f93\u53ea\u4f2f\u5c31\u8fde\u547d\u90fd\u5f97\u8f93\u51fa\u53bb \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u6211\u597d\u50cf\u53ea\u6709\u6761\u547d\u53ef\u8f93\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u4f60\u975e\u60f3\u6cd5\u5b50\u63a5\u4f4f\u6211\u7684\u8fd9\u4e9b\u6697\u5668\u4e0d\u53ef\uff0c\u82e5\u662f\u4f60\u7684\u624b\u63a5\u4e0d\u4f4f\xb7\u7528\u662d\u53bb\u54ac\u4e5f\u5f97\u54ac \u4f4f\u5b83\u3002\u201d \u3000\u3000\u8981\u63a5\u4efb\u7528\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u8fd9\u79cd\u624b\u6cd5\u53d1\u51fa\u7684\u6697\u5668\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u90ed\u5927\u8def\u63a5\u4e86\u53c1\u6b21\u8eab\u4e0a\u5df2\u6444\u4e86\u4e03\u4e0b\u5b50\u867d\u7136\u4e0d\u592a\u91cd\u5047\u4e5f\u6253\u5f97\u9aa8\u5934\u9690\u9690\u53d1\u75bc\u3002 \u3000\u3000\u8fd9\u6b21\u71d5\u4e03\u5c45\u7136\u70b9\u4e5f\u4e0d\u5fc3\u75bc\xb7\u53c8\u5728\u90a3\u91cc\u6ee1\u5730\u6361\u77f3\u5934\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u5728\u65c1\u8fb9\u770b\u53d1\u6014\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4ed6\u8fd8\u6478\u4e0d\u6e05\u9893\u4e03\u846b\u82a6\u91cc\u5356\u7684\u7a76\u7adf\u662f\u4ec0\u9ebd\u836f\u82e5\u662f\u6362\u5382\u522b\u4eba\xb7\u6015\u65e9\u5c31\u4e0d\u5e72 \u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4fe1\u4efb\u71d5\u4e03\u3002 \u3000\u3000\u4ed6\u76f8\u4fe1\u5c31\u7b97\u5929\u5e95\u4e0b\u7684\u4eba\u90fd\u8981\u6765\u6574\u4ed6\u7684\u51a4\u6280\xb7\u71d5\u4e03\u4e5f\u7edd\u4e0d\u4f1a\u5e2e\u4eba\u5bb6\u3002 \u3000\u3000\u9662\u5b50\u91cc\u7684\u5c0f\u77f3\u5934\u5e76\u4e0d\u591a\uff0c\u71d5\u4e03\u624b\u91cc\u6367\u6ee1\u628a\u8fd8\u89c9\u5f97\u4e0d\u591f\u53c8\u8dd1\u5230\u5899\u89d2\u90a3\u8fb9\u53bb\u6361\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u6478\u80a9\u5934\u4e0a\u88ab\u6253\u5f97\u53c8\u978d\u53c8\u75bc\u7684\u5730\u65b9\uff0c\u5fcd\u4e0d\u4f73\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8981\u4ed6\u4e0b\u4e8e\u5c31\u63a5\u4f50\u8fd9\u9ebd\u591a\u6697\u5668\u4ed6\u5b9e\u5728\u6ca1\u628a\u63e1\u3002 \u3000\u3000\u98ce\u4e2d\u5e26\u82b1\u9999\u5bf9\u9762\u7684\u6843\u82b1\u5df2\u5feb\u5f00\u653e\u90ed\u5927\u8def\u62ac\u8d77\u5934\uff0c\u5ffd\u7136\u770b\u5230\u738b\u52a8\u6b63\u5750\u5728\u7a97\u53e3\u5411\u4ed6\u62db\u624b\u3002 \u3000\u3000\u7b49\u71d5\u4e03\u6361\u597d\u77f3\u5934\u8f6c\u8eab\u4ed6\u5df2\u8dd1\u5230\u738b\u52a8\u90a3\u8fb9\u53bb\u4e86\u4e24\u4e2a\u4eba\u4e2a\u5728\u7a97\u91cc\u4e2a\u5728\u7a97\u5916\uff0c\u6307\u624b\u5212\u811a\u6652\u5676\u5495 \u5495\u4e5f\u4e0d\u77e5\u5728\u8bf4\u4e9b\u4ec0\u9ebd\u3002 \u3000\u3000\u71d5\u4e03\u53ea\u6709\u7b49\u3002 \u3000\u3000\u7b49\u4e86\u8005\u534a\u5929\xb7\u624d\u770b\u89c1\u90ed\u5927\u8def\u65bd\u65bd\u7136\u8d70\u4e86\u8fc7\u6765\u80cc\u8d1f\u53cc\u624b\u80a1\u4e0a\u7684\u8868\u60c5\u597d\u50cf\u5f88\u5f97\u610f\u7684\u6837\u5343\u3002 \u3000\u3000\u738b\u52a8\u8fd8\u5750\u5728\u7a97\u53e3\u671d\u8fd9\u8fb9\u770b\u8138\u4e0a\u4e5f\u5e26\u7b11\uff0c\u7b11\u5f97\u597d\u50cf\u5f88\u795e\u79d8\u3002\u71d5\u4e03\u5fcd\u4e0d\u4f73\uff0c\u95ee\u9053\u201c\u618b\u8ddf\u6dee\u4e24 \u4e2a\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8ddf\u738b\u52a8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\u4f60\u8bf4\u5e72\u52a8\u5440\u4ed6\u8981\u6211\u544a\u8bc9\u4f60\u4eca\u5929\u665a\u4e0a\u4ed6\u60f3\u5403\u6392\u9aa8\u5a49\u841d\u3009\u3002\u201d \u3000\u3000\u8c01\u90fd\u770b\u5f97\u51fa\u4ed6\u5728\u8bf4\u8c0e\u3002 \u3000\u3000\u90ed\u5927\u8def\u8bf4\u8d77\u8c0e\u6765\uff0c\u819b\u4e0a\u597d\u50cf\u6302\u62db\u724c\u6837\u3002 \u3000\u3000\u9893\u4e03\u77aa\u4e86\u4ed6\u773c\u51b7\u51b7\u9053\u201c\u8bf4\u8c0e\u7684\u4eba\u5c0f\u5fc3\u7259\u9f7f\u88ab\u4eba\u6253\u6389\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u563b\u563b\u9053\u201c\u4f60\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u71d5\u4e03\u901a\u201c\u597d\u3002\u201d \u3000\u3000\u8fd9\u4e0b\u4e8e\u4ed6\u975e\u4f46\u6253\u51fa\u7684\u77f3\u5934\u66f4\u591a\xb7\u800c\u4e14\u7528\u7684\u529b\u91cf\u4e5f\u66f4\u5927\u3002 \u3000\u3000\u529b\u91cf\u7528\u5f97\u5927\uff0c\u77f3\u5934\u7684\u6765\u52bf\u4e5f\u5f53\u7136\u66f4\u6025\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u8f6c\u5979\u624b\u91cc\u5ffd\u7136\u591a\u5382\u4e24\u6837\u94f6\u5149\u95ea\u95ea\u7684\u4e1c\u897f\uff0c\u5c31\u597d\u50cf\u5c0f\u5b69\u5b50\u6349\u804c\u869c\u7528\u7684 \u90a3\u79cd\u5e26\u67c4\u7684\u515c\u7f51\u3002 \u3000\u3000\u5341\u6765\u5757\u53c8\u6025\u4e49\u5feb\u7684\u575b\u8757\u77f3\u5c31\u597d\u50cf\u8717\u9c9c\u6837\u51e0\u4e4e\u5168\u88ab\u4ed6\u635e\u8fdb\u7f51\u91cc\u3002 \u3000\u3000\u6f0f\u7f51\u7684\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u6709\u4e24\u53c1\u5757\u4e3d\u5df2\u90ed\u5927\u8def\u8f7b\u8f7b\u677e\u677e\u7684\u5c31\u8eb2\u5f00 \u3000\u3000\u8fd9\u4e2b\u5b50\u71d5\u4e03\u8fde\u773c\u775b\u770b\u5f97\u597d\u50cf\u6709\u70b9\u53d1\u76f4\uff0c\u77aa\u773c\u9053\u201c\u8fd9\u662f\u4ec0\u9ebd\u73a9\u610f\u513f\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u563b\u563b\u9053\u4f60\u770b\u8fd9\u73a9\u610f\u513f\u600e\u9ebd\u6837\u4f60\u4f69\u670d\u4e0d\u4f69\u670d\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4e0d\u662f\u738b\u8001\u5927\u521a\u6559\u7ed8\u4f60\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5f97\u610f\u6d0b\u6d0b\uff0c\u9053\u201c\u5c31\u7b97\u662f\u4ed6\u6559\u7ed9\u6211\u7684\u4e5f\u5f97\u8981\u6211\u8fd9\u6837\u9634\u660e \u3000\u3000\u71d5\u4e03\u6487\u4e86\u6487\u5634\u9053\u201c\u4f60\u51e0\u65f6\u53d8\u5f97\u806a\u660e\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u90ed\u706b\u8def\u7b11\u9053\u201c\u6211\u672c\u6765\u5c31\u4e0d\u7b28\u53ea\u8981\u662f\u597d\u73a9\u7684\u82b1\u6837\u6211\u4e2d\u63a8\u4f1a\u3002\u201d \u3000\u3000\u71d5\u4e03\u4f38\u51fa\u4e4e\u9053\u201c\u62ff\u6765\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u53cc\u624b\u53cb\u523b\u7f29\u56de\u80cc\u5f8c\u9053\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u597d\u4f60\u518d\u8bd5\u8bd5\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u8fd9\u6b21\u4ed6\u53d1\u662d\u5668\u7684\u624b\u6cd5\u66f4\u5feb\u66f4\u7edd\u3002 \u3000\u3000\u5341\u6765\u5757\u5c0f\u77f3\u5934\uff0c\u597d\u50cf\u90fd\u53d8\u6210\u6d3b\u7684\xb7\u90fd\u5e26\u7fc5\u8180\u8fd8\u957f\u773c\u775b\xb7\u4e13\u627e\u90ed\u5927\u8def\u8eab\u4e0a\u6700\u5f31\u7684\u5730\u65b9\u6253\u3002 \u3000\u3000\u8c01\u77e5\u9053\u90ed\u5927\u8def\u624b\u91cc\u7684\u4e24\u53ea\u7f51\u4e5f\u597d\u50cf\u4e2d\u5c31\u7b49\u5728\u90a3\u91cc\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u5341\u6765\u5757\u77f3\u5934\u80fd\u6f0f\u51fa\u7f51\u7684\u5c45\u7136\u53ea\u6709\u5757\u3002 \u3000\u3000\u90ed\u5927\u8def\u5927\u7b11\u9053\u201c\u73b0\u5728\u4f60\u603b\u8be5\u4f69\u670d\u6211\u5382\u5427\uff1f\u201d\u71d5\u4e03\u77aa\u773c\xb7\u7ec8\u4e8e\u4e5f\u62bf\u5634\u7b11\uff0c\u9053\u770b\u6765\u4f60\u7684\u786e\u4e0d \u7b28\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u66f4\u5f97\u610f\u3001\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\u3001\u63a5\u6697\u5668\u7684\u624b\u6cd5\xb7\u6211\u4ee5\u524d\u5e76\u6ca1\u6709\u8ba4\u771f\u7ec3\u8fc7\xb7\u53ea\u56e0\u4e3a\u2026\u2026\u53ea \u56e0\u4e3a\u4ec0\u9ebd\u4f60\u731c\u4e0d\u731c\u5f97\u51fa\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u731c\u4e0d\u51fa\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ea\u56e0\u4e3a\u6211\u7684\u624b\u5929\u751f\u5c31\u6bd4\u522b\u4eba\u5feb\u773c\u775b\u4e5f\u5929\u751f\u5c31\u6bd4\u522b\u4eba\u5c16\u6240\u4ee5\u6839\u672c\u4e0d\u7528\u7ec3\u3002\u201d \u3000\u3000\u71d5\u4e03\u6de1\u6de1\u7684\u8bf4\u9053\u201c\u6240\u4ee5\u51b0\u624d\u4f1a\u63a5\u90a3\u5927\u5a31\u8681\u4e0b\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5c45\u7136\u70b9\u4e5f\u4e0d\u8138\u7ea2\u8fd8\u662f\u5e26\u7b11\u9053\u201c\u90a3\u4e0d\u7b97\uff0c\u73b0\u5728\u4f60\u518d\u53eb\u4ed6\u6765\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u4ed6\u773c\u73e0\u4e8e\u8f6c\u4e86\u8f6c\u53c8\u7b11\u9053\u201c\u542c\u8bf4\u6c5f\u6e56\u597d\u6c49\u90fd\u6709\u4e2a\u80fd\u53eb\u5f97\u54cd\u7684\u5916\u53f7\u73b0\u5728\u6211\u5012\u60f3\u51fa\u4e2a\u5916\u53f7\u7ed9\u6211 \u5012\u771f\u5408\u9002\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u5916\u53f7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u901a\u201c\u5e72\u8b6c\u5982\u6765\u9b3c\u5f71\u5b50\u6478\u4e0d\u5feb\u624b\u4eba\u9189\u4fa0\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u5382\u9053\u201c\u6211\u5012\u4e5f\u6709\u4e2a\u540d\u53f7\uff0c\u7ed9\u4f60\u66f4\u5408\u9002\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b28\u624b\u672c\u811a\uff0c\u9189\u4e86\u6ee1\u5730\u722c\u8f93\u5de5\u4e4b\u738b\u5927\u5446\u9e1f\u3002\u4f60\u8bf4\u8fd9\u4e2a\u5916\u53f7\u9002\u53f0\u4e0d\u9002\u5408\uff1f\u201d \u7b2c\u5341\u4e8c\u7ae0\u3000\u91d1\u5b50\u4e0e\u9762\u5b50>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u91d1\u5b50\u4e0e\u9762\u5b50 \u3000\u3000\u8fd9\u5bb6\u4eba\u7684\u5927\u95e8\u662f\u671d\u5357\u5f00\u7684\u53cc\u95e8\u73af\u5728\u592a\u9633\u4e0b\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fdb\u8fd9\u6761\u5df7\u5b50\u5c31\u770b\u89c1\u4e86\u8fd9\u53cc\u95e8\u73af\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4ed6\u773c\u775b\u8fd8\u662f\u76ef\u8fd9\u53cc\u95e8\u4e0d\u5c31\u597d\u50cf\u8f88\u5b50\u6ca1\u6709\u770b\u89c1\u8fc7\u73af\u4f3c\u7684\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\xb7\u4ed6\u8fd9\u8f88\u5b50\u7684\u786e\u5f88\u5c11\u6709\u673a\u4f1a\u770b\u5230\u8fd9\u9ebd\u7a00\u5947\u7684\u4e8b\u3002 \u3000\u3000\u6bcf\u5bb6\u4eba\u90fd\u6709\u5927\u95e8\u6bcf\u4e2a\u5927\u95e8\u535c\u90fd\u6709\u95e8\u73af\u3002 \u3000\u3000\u8fd9\u70b9\u4e5f\u4e0d\u7a00\u5947\u3002 \u3000\u3000\u7a00\u5947\u7684\u662f\u8fd9\u5bb6\u4eba\u5927\u95e8\u4e0a\u7684\u95e8\u73af\uff0c\u7adf\u662f\u7528\u9ec4\u91d1\u94f8\u6210\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u770b\u8fd9\u95e8\u73af\u7684\u65f6\u5019\uff0c\u71d5\u4e03\u5c31\u770b\u4ed6 \u3000\u3000\u6700\u8fd1\u8fd9\u4e24\u4eba\u8eab\u4e0a\u5c31\u597d\u50cf\u5df2\u767e\u6839\u7ef3\u5b50\u5c06\u4ed6\u884c\u4e32\u4f50\u5382\u90ed\u5927\u8def\u5728\u90a3\u91cc\u71d5\u4e03\u5c31\u5728\u90a3\u91cc\u3002\u2019 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u90ed\u5927\u8def\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5bb6\u4eba\u4e00\u5b9a\u662f\u4e2a\u66b4\u53d1\u6237\u3002\u201d\u71d5\u4e03\u7728\u7728\u773c\u9053\u201c\u66b4 \u53d1\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u53ea\u6709\u66b4\u53d1\u6237\u624d\u4f1a\u505a\u8fd9\u79cd\u8981\u3002\u2026 \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u79cd\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u9c7c\u79cd\u7b80\u76f4\u53ef\u4ee5\u53eb\u4eba\u7b11\u6389\u5927\u7259\u7684\u4e8b\u3002\u201d \u3000\u3000\u98a0\u4e03\u9053\u201c\u4f60\u9519\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u54ea\u70b9\u951a\u4e86\uff1f\u201d\u71d5\u4e03\u9053\u201c\u8fd9\u5bb6\u4eba\u975e\u4f46\u4e0d\u662f\u66b4\u53d1\u6237\u800c\u76ee\u8fd8\u662f\u6c5f\u6e56\u4e2d\u6709\u6570\u7684\u51e0\u4e2a \u8dbe\u5bb6\u5927\u65cf\u4e4b\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d\u65bd\u4e03\u7f13\u7f13\u7684\u9053\u201c\u7528\u4f1a\u5343\u505a\u95e8\u73af\u5f3a\u7136\u5f88\u4fd7\u6c14\xb7\u5f88\u53ef\u7b11\uff0c\u53ef\u662f \u6c60 \u3000\u3000\u90ed\u5927\u8def\u9053\u6211\u5c31\u89c9\u5f97\u5f88\u53ef\u7b11\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u53ea\u56e0\u4e3a\u4f60\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u85cf\u4e03\u9053\u201c\u4f60\u771f\u77e5\u9053\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u662f\u4e2a\u4eba\xb7\u2018\u4e2a\u6ee1\u8eab\u94dc\u81ed\u8d22\u5927\u6c14\u7c97\xb7\u751f\u6015\u522b\u4eba\u4e0d\u77e5\u9053\u4ed6\u6709\u94b1\u7684\u4eba\u3002\u8fd9\u79cd\u4eba\u6211 \u65e2\u4e0d\u60f3\u8ba4\u5f97\u4ed6\u4e5f\u4e0d\u60f3\u8ddf\u4ed6\u4ea4\u670b\u53cb\u3002\u8fd9\u79cd\u4eba\u65e0\u8bba\u5e72\u4ec0\u9ebd\uff0c\u90fd\u613f\u6211\u70b9\u5173\u7cfb\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\xb7\u9053\u201c\u53ea\u53ef\u63aa\u8fd9\u79cd\u4eba\u73b0\u5728\u5374\u504f\u504f\u642c\u6062\u6709\u70b9\u5173\u5427\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u90a3\u5012\u8fd8\u4e0d\u81f3\u65bc\u6211\u4eec\u8fd8\u6ca1\u7a77\u5230\u8fd9\u79cd\u5730\u6b65\u201d \u3000\u3000\u90ed\u5927\u8def\u677e\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u90a3\u7c73\u4f60\u53eb\u6211\u8d76\u4e86\u534a\u5929\u7684\u8def\u8d76\u5230\u8fd9\u91cc\u6765\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u770b\u8fd9\u5bf9\u95e8\u73af \u7684\uff1f\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u4e5f\u4e0d\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6709\u70b9\u62c5\u5fc3\u7684\u6837\u5b50\xb7\u770b\u71d5\u4e03\xb7\u9053\u201c\u6211\u77e5\u9053\u4f60\u5b9a\u6ca1\u6709\u4ec0\u9ebd\u597d\u4e3b\u610f\u6240\u4ee5\u76f4\u90fd\u4e0d\u80af\u75db\u75db \u5feb\u6289\u7684\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u4f60\u653e\u5fc3\u81f3\u5c11\u6211\u603b\u4e0d\u4f1a\u628a\u4f60\u5356\u7ed9\u4eba\u5bb6\u7684\u6211\u8fd8\u820d\u4e0d\u5f97\u54e9\u3002\u201d \u3000\u3000\u4ed6\u7684\u8138\u597d\u50cf\u6709\u70b9\u53d1\u7ea2\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u663e\u5f97\u66f4\u62c5\u5fc3\xb7\u9053\u201c\u4e2a\u4eba\u82e5\u6ca1\u6709\u505a\u4e8f\u5fc3\u4e8b\u7edd\u4e0d\u4f1a\u8138\u7ea2\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9080\u201c\u8c01\u7684\u8138\u7ea2\u4e2b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u3002\u201d \u3000\u3000\u9893\u4e03\u8f6c\u8fc7\u5934\u9053\u201c\u6211\u770b\u4f60\u773c\u775b\u53d1\u82b1\u624d\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u773c\u73e0\u4e8e\u76f4\u8f6c\u5ffd\u7136\u9053\u201c\u6211\u660e\u767d\u5382\u3002\u201d \u3000\u3000\u70ed\u4e03\u9053\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5b9a\u662f\u8fd9\u5bb6\u4eba\u6709\u4e2a\u6ca1\u51fa\u5ac1\u7684\u8005\u59d1\u5a18\u4f60\u60f3\u8981\u6211\u6765\u7528\u7f8e\u7537\u8ba1\u3002\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u201c\u6620\u6627\u201d\u58f0\u7b11\u4e86\u9053\u201c\u4f60\u89c9\u5f97\u81ea\u5df1\u5f88\u7f8e\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u867d\u7136\u4e0d\u592a\u7f8e\u5374\u6b63\u662f\u4ea5\u4eba\u89c1\u5c31\u559c\u6b22\u7684\u90a3\u79cd\u7537\u4eba\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u5012\u771f\u662f\u9a6c\u4e0d\u77e5\u80a1\u957f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u53f9\u4e86\u53e3\u6c14\xb7\u9053\u201c\u53ea\u53ef\u9519\u4f60\u4e0d\u662f\u5973\u4eba\uff0c\u5426\u5219\u4e5f\u5b9a\u770b\u535c\u6211\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u8138\u597d\u50cf\u53c8\u7ea2\u4e86\u7ea2\u5374\u6545\u610f\u677f\u8138\u9053\u201c\u6211\u82e5\u662f\u5973\u4eba\u73b0\u5728\u5c31\u811a\u628a\u4f60\u8e22\u5230\u9634\u6c9f\u91cc\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u65e0\u8bba\u4f60\u600e\u9ebd\u8bf4\u53cd\u6b63\u6211\u8fd9\u6b21\u7edd\u4e0d\u4e0a\u4f60\u7684\u5f53\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0a\u4ec0\u9ebd\u5f53\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u8001\u59d1\u5a18\u5b9a\u53c8\u4e11\u53c8\u602a\u8bf4\u4e0d\u5b9a\u8fd8\u662f\u4e2a\u5927\u9ebb\u5b50\u6240\u4ee5\u624d\u4f1a\u56fe\u4e0d\u51fa\u53bb\u5979\u5c31\u7b97\u6709\u516b\u767e\u4e24 \u94f6\u5b50\u7684\u5ac1\u5986\uff0c\u4e5f\u4f11\u60f3\u53eb\u6211\u5a36\u5979\u3002\u201d \u3000\u3000\u71d5\u4e03\u7528\u773c\u775b\u6a2a\u4ed6\u51b7\u51b7\u9053\u201c\u5979\u82e5\u957f\u5f97\u53c8\u5e74\u9752\u53c8\u6807\u81f4\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u9053\u201c\u90a3\u5012\u53ef\u4ee5\u5546\u91cf\u5546\u91cf\u6e2f\u53eb\u4f60\u6790\u662f\u6211\u7684\u597d\u670b\u53cb\u5462\u4e3a\u4e86\u670b\u53cb\u6211\u4ec0\u9ebd\u4e8b\u90fd\u80af\u505a \u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u6211\u60f3\u8981\u4f60\u505a\u4ef6\u4e8b\u4e0d\u77e5\u9053\u4f60\u65e8\u4e0d\u80af\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u60f3\u8bf7\u4f60\u5230\u9634\u6c9f\u524d\u9762\u53bb\u7167\u7167\u81ea\u5df1\u7684\u8138\xb7\u7136\u5f8c\u518d\u4e70\u5757\u81ed\u8c46\u8150\u6765\u5934\u649e\u6b7b\u3002\u201d \u3000\u3000\u8fd9\u6761\u5df7\u4e8e\u5f88\u5bbd\uff0c\u5ffd\u7136\u95f4\u8f86\u56db\u5339\u9a6c\u62c9\u7684\u5927\u9a6c\u8f66\uff0c\u5f88\u5feb\u7684\u51b2\u4eba\u5382\u5df7\u5b50\u3002 \u3000\u3000\u867d\u7136\u8fd9\u6761\u5df7\u4e86\u5f88\u5bbd\u4f46\u90ed\u5927\u8def\u548c\u71d5\u4e03\u82e5\u662f\u5c0f\u95ea\u907f\u5f97\u5feb\u8fd8\u662f\u514d\u4e0d\u5382\u8981\u88ab\u649e\u5012\u3002 \u3000\u3000\u90ed\u5927\u8def\u8ddf\u5df2\u7ecf\u51b2\u8fc7\u53bb\u7684\u9a6c\u8f66\u6068\u6839\u7684\u9053\u201c\u8fd9\u6761\u8def\u53c8\u4e0d\u662f\u4ed6\u4e2a\u4eba\u7684\u4ed6\u51ed\u54ea\u70b9\u8fd9\u9ebd\u6a2a\u51b2\u76f4 \u649e\uff1f\u201d\u71d5\u4e03\u9053\u201c\u53ea\u51ed\u2019\u70b9\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u54ea\u70b9\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u51ed\u8fd9\u6761\u5df7\u4e8e\u672c\u5c31\u662f\u4ed6\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u5382\u6014\xb7\u8fd9\u624d\u53d1\u73b0\u751a\u4e8e\u91cc\u679c\u7136\u5c31\u53ea\u6709\u90a3\u5bb6\u4eba\u3002 \u3000\u3000\u9a6c\u8f66\u5df2\u7ecf\u505c\u5728\u8fd9\u5bb6\u4eba\u7684\u5927\u95e8\u5916\u672c\u6765\u9759\u9759\u7684\u5927\u95e8\u91cc\u7acb\u523b\u6709\u5341\u6765\u4e2a\u4eba\u5feb\u6b65\u5954\u5382\u51fa\u6765\u51e0\u4e2a\u4eba\u7528 \u6700\u5feb\u7684\u901f\u5ea6\u5378\u4e0b\u4e86\u62c9\u8f66\u7684\u9a6c\u53e6\u5916\u51e0\u4e2a\u4eba\u5c31\u5c06\u9a6c\u8f66\u63a8\u4e0a\u4e86\u77f3\u9636\u4e24\u65c1\u7684\u8f66\u9053\u4e0a\u63a8\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8f66\u7a97\u91cc\u597d\u50cf\u6709\u4e2a\u4eba\u5f80\u5916\u4f38\u4e86\u4f38\u5934\u770b\u5382\u90ed\u5927\u8def\u4ed6\u4eec\u773c\u3002\u90ed\u5927\u8def\u5374\u6ca1\u6709\u770b\u6e05\u8fd9\u4e2a\u4eba\u7684\u8138\u53ea\u89c9 \u4ed6\u7684\u773c\u775b\u597d\u50cf\u6bd4\u666e\u901a\u4eba\u660e\u4eae\u4e9b\u3002\u71d5\u4e03\u9053\u201c\u770b\u6837\u5b50\u6015\u662f\u91d1\u5927\u5e05\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u91d1\u5927\u5e05\u662f\u8c01\uff1f\u201d\u71d5\u4e03\u9053\u201c\u5c31\u662f\u4f60\u8bf4\u7684\u90a3\u4e2a\u8d22\u5927\u6c14\u79df\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u679c\u7136\u6ca1\u6709\u8bf4\u9519\u5427\u3002\u201d\u4ed6\u51b7\u7b11\u4e49\u9053\u201c\u91d1\u5927\u5e05\u54fc\u4f60\u542c\u8fd9\u540d\u5b57\uff0c\u5c31\u8be5\u77e5\u9053\u4ed6\u662f\u4e2a \u600e\u9ebd\u6837\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6709\u94b1\u4eba\u5e76\u4e0d\u89c1\u5f97\u5c31\u4e0d\u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u4ed6\u51ed\u4ec0\u9ebd\u8981\u53eb\u5927\u5e05\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b2c\u4e00\u56e0\u4e3a\u4ed6\u672c\u5c31\u6709\u5927\u5e05\u7684\u6c14\u6e90\u7b2c\u4e8c\u2606\u56e0\u4e3a\u522b\u4eba\u559c\u6b22\u53eb\u4ed6\u5927\u5e05\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u770b\u6837\u5b50\u4f60\u597d\u50cf\u4e5f\u72e0\u4f69\u670d\u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u6211\u80fd\u4e0d\u80fd\u4f69\u670d\u4ed6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u80fd\u5f53\u7136\u80fd\u2500\u2026\u2018\u53ef\u662f\u6211\u80fd\u4e0d\u80fd\u4f69\u670d\u4ed6\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u672c\u80fd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e0d\u662f\u5411\u90fd\u5f88\u4f69\u670d\u5f25\u81ea\u5df1\u7684\u5417\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u563f\u563f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u4f60\u4e5f\u5e94\u8be5\u4f69\u670d\u4ed6\u56e0\u4e3a\u4ed6\u8ddf\u4f60\u672c\u662f\u540c\u6837\u7684\u4eba\u4e5f\u5f88\u8c6a\u723d\uff0c\u5f88\u5927\u8def\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u563f\u563f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u563f\u563f\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u563f\u563f\u7684\u610f\u601d\u5c31\u662f\u6211\u4e0d\u76f8\u4fe1\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u6839 \u672c\u5c31\u4e0d\u60f3\u770b\u89c1\u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ef\u662f\u4f60\u5374\u975e\u53bb\u770b\u4ed6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u4f60\u4e0d\u53bb\u770b\u4ed6\u5c31\u53ea\u6709\u53bb\u770b\u90a3\u4e9b\u503a\u4e3b\u7684\u8138\u8272\u4e86\u3002\u201d \u3000\u3000\u5929\u4e0b\u8fd8\u6709\u4ec0\u9ebd\u6bd4\u503a\u4e3b\u7684\u8138\u8272\u66f4\u8000\u770b\u7684\uff1f \u3000\u3000\u60f3\u5230\u90a3\u4e9b\u4eba\u90ed\u5927\u8def\u7684\u7709\u5934\u5c31\u76b1\u4e86\u8d77\u6765\u5450\u5450\u5730\u9053\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u8981\u6211\u53bb\u8ddf\u4e2a\u4e0d\u8ba4\u5f97\u7684\u4eba\u5f00 \u53e3\u53bb\u501f\u94b1\uff1f\u201d\u71d5\u4e03\u9053\u201c\u6211\u77e5\u9053\u4f60\u7684\u8138\u76ae\u8fd8\u6ca1\u6709\u90a3\u9ebd\u539a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u4f60\u53eb\u6211\u53bb\u770b\u4ed6\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6c89\u541f\u9053\u201c\u6b66\u6797\u4e2d\u6709\u5f88\u591a\u602a\u4eba\u8b6c\u5982\u8bf4\u90a3\u4f4d\u9178\u62d6\u6c64\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u8bf4\u90a3\u4f4d\u53eb\u2018\u77f3\u795e\u2019\u7684\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u2018\u77f3\u795e\u2019\u8fd9\u540d\u5b57\u662f\u600e\u9ebd\u6765\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4ed6\u53ea\u7528\u77f3\u5934\u505a\u7684\u5175\u5668\u800c\u4e14\u7528\u5f97\u5f88\u597d\u3002\u201d\u71d5\u4e03\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d\u4ed6\u63a5\u53c8\u9053 \u201c\u4f46\u77f3\u5668\u672c\u662f\u4e0a\u53e4\u65f6\u4eba\u7528\u7684\u56e0\u4e3a\u90a3\u65f6\u4eba\u4eec\u8fd8\u4e0d\u6a2a\u5f97\u70bc\u94c1\u6210\u94a2\u73b0\u5728\u4ec0\u9ebd\u6837\u5e72\u5947\u767e\u602a\u7684\u5175\u5668\u90fd\u6709 \u4e86\uff0c\u4ed6\u5374\u504f\u504f\u8fd8\u559c\u6b22\u7528\u53c8\u7b28\u53c8\u91cd\u7684\u77f3\u5934\u5175\u5668\u4f60\u8bf4\u4ed6\u662f\u4e0d\u662f\u4e2a\u602a\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u3002\u53ea\u4e0d\u8fc7\u2026\u2026\u4ed6\u8ddf\u8fd9\u91d1\u5927\u5e05\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u91d1\u5927\u5e05\u8ddf\u4ed6\u6837\u4e5f\u662f\u4e2a\u602a\u4eba\uff0c\u7528\u7684\u5175\u5668\u4e5f\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u7528\u4ec0\u9ebd\u5175\u5668\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u53ea\u7528\u91d1\u5b50\u505a\u7684\u5175\u5668\u800c\u4e14\u662f\u7eaf\u91d1\u505a\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u7728\u4e86\u7728\u773c\u597d\u50cf\u5df2\u6709\u70b9\u660e\u767d\u4ed6\u7684\u610f\u601d\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u6700\u5584\u7528\u7684\u5175\u5668\u5c31\u662f\u91d1\u5f13\u795e\u5f39\u5f39\u53d1\u8fde\u73af\u4ea1\u624b\u5c31\u662f\u53c1\u4e03\u4e8c\u5341\u9897\u6c5f\u6e56\u4e2d\u8fd8\u5f88\u5c11\u6709\u4eba \u80fd\u8eb2\u5f97\u5f00\u3002\u201d\u90ed\u5927\u8def\u9053\u52a9\u4e8e\u4e5f\u662f\u91d1\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7eaf\u91d1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u901a\u201c\u4f60\u60f3\u8981\u6211\u53bb\u8ddf\u4ed6\u52a8\u624b\u63a5\u4efb\u4ed6\u90a3\u4e9b\u91d1\u5f39\u62ff\u56de\u6765\u8fd8\u8d26\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u636e\u8bf4\u4ed6\u7684\u91d1\u5f39\u5b50\u6bcf\u8e48\u81f3\u5c11\u6709\u597d\u51e0\u4e24\u91cd\u800c\u4e14\u53d1\u5c31\u662f\u4e8c\u5341\u9897\u4f60\u53ea\u8981\u80fd\u63a5\u4efb\u4ed6\u53c1\u56db \u53d1\u5c31\u4e0d\u5fc5\u518d\u770b\u90a3\u4e9b\u503a\u4e3b\u7684\u8138\u8272 \u3000\u3000\u90ed\u5927\u8def\u7528\u529b\u6447\u5382\u6447\u5934\u9053\u201c\u6211\u4e0d\u5e72\u8fd9\u79cd\u4e8b\u6211\u7edd\u4e0d\u5e72\u3002\u201d \u3000\u3000\u9884\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u4e3a\u4ec0\u9ebd\uff0c\u4e0d\u5e72\u5c31\u662f\u4e0d\u5e72\u3002\u201d \u3000\u3000\u71d5\u4e03\u670d\u73e0\u5b50\u8f6c\u6de1\u6de1\u7b11\u9053\u201c\u54e6\u2026\u2026\u6211\u660e\u767d\u4e86\u4f60\u662f\u4f2f\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u5927\u58f0\u9053\u201c\u6211\u6015\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u60a0\u7136\u9053\u201c\u4f60\u5f53\u7136\u4e0d\u662f\u6015\u4ed6\u53ea\u4e0d\u8fc7\u662f\u6015\u8116\u800c\u5df4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\u9053\u201c\u6015\u80d6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u91d1\u5b50\u867d\u7136\u6bd4\u94c1\u8f6f\xb7\u4f46\u4e94\u516d\u4e24\u9897\u7684\u5f39\u5b50\u82e5\u6253\u5728\u4eba\u8eab\u4e0a\u8fd8\u662f\u5f88\u75bc\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u75bc\u8d77\u6765\u5c31\u4f1a\u80bf\u80bf\u8d77\u6765\u5c31\u80d6\u4e86\uff0c\u80d6\u8d77\u6765\u5c31\u4e0d\u592a\u597d\u770b\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8c08\u6de1\u5730\u7b11\u5382\u7b11\u63a5\u9053\u201c\u6240\u4ee5\u4f60\u5c31\u7b97\u4e0d\u53bb\u6211\u4e5f\u4e0d\u4f1a\u602a\u4f60\u7684\u4f60\u82e5\u5ffd\u7136\u80d6\u8d77\u6765\u522b\u4eba\u8bf4\u4e0d\u5b9a\u8fd8 \u4f1a\u4ee5\u4e3a\u4f60\u5403\u4e86\u53d1\u732a\u836f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u4ed6\u77aa\u4e86\u534a\u5929\u677f\u8138\u9053\u201c\u6ed1\u7a3d\u6ed1\u7a3d\xb7\u771f\u4ed6\u5988\u7684\u6ed1\u7a3d\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u4e2a\u4eba\u82e5\u80bf\u4e86\u8d77\u6765\u90a3\u624d\u771f\u7684\u6ed1\u7a3d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u77aa\u4e86\u4ed6\u773c\u626d\u5934\u5c31\u8d70\u3002 \u3000\u3000\u71d5\u4e03\u5374\u62c9\u4f4f\u4e86\u4ed6\u9053\u201c\u4f60\u5230\u804c\u91cc\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u51b7\u51b7\u9053\u201c\u6211\u6700\u8fd1\u997f\u5f97\u592a\u7626\u4e86\xb7\u672c\u6765\u5c31\u8981\u60f3\u6cd5\u5b50\u53d8\u80d6 \u3000\u3000\u71d5\u4e03\u8d61\u7136\u7b11\xb7\u9053\u201c\u4f60\u96be\u9053\u60f3\u5c31\u8fd9\u6837\u51b2\u8fdb\u53bb\uff0c\u627e\u4eba\u5bb6\u53bb\u6253\u67b6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8fd8\u80fd\u7528\u4ec0\u9ebd\u6cd5\u5b50\u53bb\u8ddf\u4eba\u5bb6\u6253\u67b6\uff1f\u96be\u9053\u8dea\u53bb\u6c42\u4ed6\u201d \u3000\u3000\u989d\u4e03\u7b11\u9053\u201c\u4f60\u5c31\u7b97\u771f\u7684\u8dea\u6c42\u4ed6\uff0c\u4ed6\u4e5f\u672a\u5fc5\u4f1a\u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u9893\u4e03\u7b11\u9053\u201c\u4e8c\u5341\u9897\u5f39\u5b50\u6bd5\u7adf\u8981\u503c\u4e0d\u5c11\u94b1\u4ed6\u53c8\u6ca1\u53d1\u75af\uff0c\u600e\u9ebd\u4f1a\u968f\u968f\u4fbf\u4fbf\u5c31\u7528\u6765\u6253\u4eba\uff1f\u4f55 \u51b5\uff0c\u4e07\u4e00\u771f\u6253\u6b7b\u4e86\u4eba\u4e5f\u4e0d\u662f\u597d\u73a9\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u8981\u53eb\u4e86\u8d77\u6765\u9053\u201c\u521a\u903c\u6211\u8981\u6211\u53bb\u7684\u662f\u4f60\xb7\u73b0\u5728\u62e6\u6211\u4e0d\u8981\u6211\u53bb\u7684\u4e5f\u662f\u4f60\xb7\u4f60\u7a76\u7adf \u5728\u6401\u4ec0\u9ebd\u9b3c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u5e76\u4e0d\u662f\u4e0d\u8981\u4f60\u53bb\u3002\u53ea\u4e0d\u8fc7\u8981\u53bb\u627e\u91d1\u5927\u5e05\u4ea4\u624b\xb7\u4e5f\u5f97\u8981\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u60f3\u8981\u4ec0\u9ebd\u6837\u7684\u4eba\u624d\u80fd\u4ee4\u91d1\u5927\u5e05\u51fa\u624b\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u6211\u60f3\u4e0d\u51fa\u4e5f\u61d2\u5f97\u60f3\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff0c\u201c\u53ea\u6709\u4e24\u79cd\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54ea\u4e24\u79cd\u4eba\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b2c\u79cd\u5f53\u7136\u662f\u4ed6\u7684\u4ec7\u5bb6\xb7\u82e5\u662f\u6709\u4ec7\u5bb6\u627e\u4ee5\u53bb\xb7\u4ed6\u5f53\u7136\u4f1a\u5b9a\u671f\u51fa\u624b\u7684\u53ea\u53ef\u60dc\u3002\u4e00\u2500 \u4f60\u670b\u4ed6\u70b9\u4ec7\u6068\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u597d\u50cf\u89c9\u5f97\u5f88\u9057\u61be\u7684\u6837\u5b50\u3002\u90ed\u5927\u8def\u677f\u8138\u9020\u201c\u96be\u9053\u8981\u6211\u53bb\u628a\u4ed6\u7684\u8001\u5a46\u62a2\u6765\xb7\u5148\u5236\u9020\u70b9 \u4ec7\u5f88\u201d \u3000\u3000\u71d5\u4e03\u5403\u5403\u7b11\u9053\u201c\u636e\u8bf4\u4ed6\u8001\u5a46\u53c8\u80d6\u53c8\u4e11\u800c\u4e14\u662f\u4e2a\u6bcd\u8005\u864e\xb7\u4f60\u82e5\u771f\u628a\u5979\u62a2\u8d70\u5382\u91d1\u5927\u5e05\u8bf4\u4e0d\u5b9a \u8fd8\u4f1a\u975e\u5e38\u611f\u6fc0\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54fc\u54fc\u6ed1\u7a3d\u6ed1\u7a3d\u3002\u201d\u71d5\u4e03\u9053\u201c\u5bb9\u597d\u9664\u6b64\u5916\u8fd8\u6709\u79cd\u6cd5\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54fc\u201d\u71d5\u4e03\u9053\u201c\u6b66\u6797\u4e2d\u4eba\u8c01\u4e5f\u4e0d\u613f\u5411\u522b\u4eba\u4f4e\u5934\u793a\u5f31\u7684\u6240\u4ee5\uff0c\u82e5\u82df\u4eba\u5149\u660e\u5802\u57a6\u7684 \u627e\u4e0a\u95e8\u53bb\u627e\u4ed6\u6bd4\u6b66\u8f83\u91cf\uff0c\u4ed6\u5c31\u6ca1\u4ee5\u6cd5\u5b50\u4e0d\u51fa\u624b\u4e01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4ece\u6000\u91cc\u62bd\u51fa\u5f20\u7ea2\u8272\u7684\u62dc\u8d34\uff0c\u5a7f\u7136\u5730\u8bf4\u9053\u201c\u4f46\u8fd9\u4eba\u5f53\u7136\u56da\u5f97\u662f\u4e2a\u6709\u540d\u6709\u80dc\u7684\u4eba\u8b6c\u5982 \u8bf4\xb7\u4f60\u7b28\u624b\u7b28\u811a\uff0c\u9189\u4e86\u6ee1\u5730\u722c\xb7\u8f93\u738b\u4e4b\u738b\u5927\u5446\u9e1f\u8fd9\u79cd\u4eba\u2019\u2026\u201c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5168\u7ea2\u7684\u62dc\u8d34\u5f88\u8003\u7a76\u3002 \u3000\u3000\u4ea1\u9762\u7aef\u6b63\u6b63\u7684\u5199\u83ca\u4e2a\u72e0\u54cd\u4eae\u7684\u540d\u5b57\u201c\u5343\u81c2\u5982\u6765\u9b3c\u5f71\u5b50\u6478\u4e0d\xb7\u8bc0\u624b\u5927\u9189\u4fa0\u90ed\u5927\u8def\u62dc\u3002 \u3000\u3000\u91d1\u516c\u9986\u7684\u95e8\u623f\u5e74\u7eaa\u5df4\u72e0\u5927\uff0c\u6ee1\u8138\u90fd\u662f\u8001\u809d\u5de8\u733e\u7684\u6837\u5b50\u63a5\u8fc7\u8fd9\u5f20\u62dc\u8d34\u81ea\u5df1\u5148\u770b\u4e86\u770b\u3002\u8138\u4e0a \u5c45\u7136\u8fde\u4e00\u70b9\u5403\u60ca\u7684\u6837\u5b50\u90fd\u6ca1\u6709\uff0c\u53ea\u662f\u8c08\u6de1\u7684\u95ee\u9053\u201c\u8fd9\u4f4d\u90ed\u5927\u4fa0\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8001\u95e8\u623f\u8fd9\u624d\u62ac\u8d77\u5934\u770b\u4e86\u4ed6\u4e24\u773c\uff0c\u4e7e\u7b11\u9053\u201c\u539f\u6765\u9601\u4e0b\u5c31\u662f\u90ed\u5927\u4fa0\u5931\u656c\u5931\u656c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u8001\u95e8\u623f\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u770b\u4ed6\uff0c\u53c8\u9053\u201c\u90ed\u5927\u4fa0\u4eba\u5230\u8fd9\u91cc\u6765\u662f\u4e0d\u662f\u60f3\u627e\u6211\u4eec\u8001\u7237\u8f83\u91cf\u6697\u5668\u7684\u529f \u592b\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d \u3000\u3000\u8005\u95e8\u623f\u7b11\u5f97\u5c31\u8c61\u53ea\u8005\u72d0\u72f8\xb7\u60a0\u7136\u9053\u201c\u6bcf\u4e2a\u6708\u91cc\u603b\u6709\u51e0\u4f9d\u5927\u4fa0\u8981\u6765\u6211\u82e5\u8fd8\u770b\u4e0d\u51fa\u9601\u4e0b\u662f\u6765 \u5e72\u4ec0\u9ebd\u7684\uff0c\u90a3\u624d\u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6c89\u4e0b\u8138\uff01\u9053\u201c\u4f60\u65e2\u770b\u51fa\u6765\u4e01\xb7\u8fd8\u4e0d\u5feb\u53bb\u901a\u62a5\uff1f\u201d \u3000\u3000\u8001\u95e8\u623f\u53c8\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\u9053\u201c\u770b\u8d77\u6765\u90ed\u5927\u4fa0\u4eca\u5929\u597d\u50cf\u8fd8\u6ca1\u6709\u559d\u9189\u5427\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u51b7\u51b7\u9053\uff1a\u201c\u5927\u9189\u4fa0\u4e5f\u5e76\u4e0d\u5b9a\u662f\u5929\u5929\u8981\u559d\u9189\u7684\uff0c\u201d \u3000\u3000\u8001\u95e8\u623f\u9053\u201c\u90a3\u672b\u6211\u529d\u90ed\u5927\u4fa0\u4e0d\u5982\u5feb\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8001\u95e8\u623f\u7b11\u5f97\u66f4\u6c14\u4eba\uff0c\u6de1\u8c08\u9053\u201c\u56e0\u4e3a\u5230\u8fd9\u91cc\u6765\u7684\u5927\u4fa0\u5b9e\u5728\u592a\u591a\u4e86\u6211\u4eec\u5bb6\u8001\u7237\u8bf4\xb7\u4ed6\u770b\u89c1\u5927 \u4fa0\u5c31\u5934\u6655\xb7\u65e9\u5c31\u5429\u5490\u8fc7\u6211\u4ec0\u9ebd\u6837\u4eba\u4ed6\u90fd\u89c1\uff0c\u8fde\u4e4c\u9f9f\u738b\u516b\u86cb\u3001\u5f3a\u76d7\u5c0f\u5077\u90fd\u53ef\u4ee5\u8bf7\u8fdb\u53bb\u53ef\u662f\u5927\u4fa0 \u561b\u2500\u4e00\u2500\u563f\u563f\u4ed6\u662f\u7edd\u4e0d\u89c1\u7684\u3002\u201d \u3000\u3000\u62dc\u8d34\u53c8\u56de\u5230\u71d5\u4e03\u624b\u4e0a\u3002 \u3000\u3000\u7ff1\u5927\u8def\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u9053\u201c\u8fd9\u90fd\u662f\u4f60\u51fa\u7684\u597d\u4e3b\u610f\u6211\u8f88\u5b50\u4e5f\u6ca1\u4e22\u8fc7\u8fd9\u79cd\u4eba\uff0c\u5c24\u5176\u662f\u90a3\u8001\u72d0 \u72f8\u5c31\u597d\u50cf\u628a\u6211\u770b\u6210\u4e2a\u8d3c\u4f3c\u7684\xb7\u6ee1\u8138\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u6837\u5b50\u7b80\u76f4\u53ef\u4ee5\u628a\u4eba\u6d3b\u6d3b\u6c14\u6b7b\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u4e86\u7728\u773c\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u7ed9\u4ed6\u4e24\u5df4\u638c\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u672c\u6765\u5c31\u662f\u4e2a\u8d3c\u6211\u505a\u8d3c\u5fc3\u865a\u4eba\u5bb6\u4e0d\u7ed9\u6211\u4e24\u5df4\u638c\u5df2\u7ecf\u5f88\u5ba2\u6c14\u4e86\u6211\u600e\u9ebd\u8fd8\u597d \u610f\u601d\u53bb\u63cd\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u3002\u4ed6\u7b11\u7684\u6837\u5b50\u5f53\u7136\u6bd4\u90a3\u8001\u95e8\u623f\u597d\u770b\u5f97\u591a\u3002 \u3000\u3000\u770b\u89c1\u4ed6\u7684\u7b11\u7ff1\u5927\u8def\u7684\u706b\u6c14\u597d\u50cf\u5c0f\u4e86\u4e9b\u3002 \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u539f\u6765\u4f60\u7684\u8138\u76ae\u5e76\u4e0d\u592a\u539a\uff0c\u6bd4\u57ce\u5899\u8fd8\u8584\u4e00\u70b9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u6240\u4ee5\u6211\u73b0\u5728\u60f3\u5feb\u70b9\u8d70\uff0c\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u71d5\u4e03\u53c8\u62c9\u4f4f\u4e86\u4ed6\uff0c\u9053\u201c\u4f60\u6025\u4ec0\u9ebd\u6211\u8fd8\u6709\u522b\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u597d\u50cf\u5413\u4e86\u8df3\xb7\u82e6\u8138\u9053\u201c\u4f60\u80fd\u4e0d\u80fd\u4e0d\u51fa\u522b\u7684\u4e3b\u610f\u4e86\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7528\u624b\u63a9\u4f50\u8033\u6735\uff0c\u9053\u201c\u6211\u80fd\u4e0d\u80fd\u4e0d\u542c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u6273\u5f00\u4e2b\u90ed\u5927\u8def\u7684\u624b\u5403\u5403\u7b11\u9053\u201c\u8fd9\u4e3b\u610f\u6bd4\u521a\u7684\u597d\u5f97\u591a\u4f60\u975e\u542c\u4e0d\u53ef\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u82e6\u7b11\u9053\u201c\u4f60\u90a3\u4e0d\u592a\u597d\u7684\u5728\u610f\u5df2\u7ecf\u5feb\u628a\u6211\u7684\u4eba\u90fd\u4e22\u5149\u8fd9\u597d\u4e3b\u610f\u6211\u600e\u9ebd\u53d7\u5f97\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u9645\u771f\u7684\u8ba4\u4e3a\u8fd9\u4ef6\u4e8b\u505a\u5f97\u4e22\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u53f9\u6c14\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u95ee\u4f60\uff0c\u5927\u6bbf\u8681\u7528\u6697\u5668\u6253\u4f60\uff0c\u4f60\u82e5\u63a5\u4f4f\u4e86\uff0c\u4f1a\u4e0d\u4f1a\u518d\u9001\u56de\u53bb\u7ed9\u4ed6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53c8\u6ca1\u6709\u75af\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u9001\u56de\u53bb\u7ed9\u4ed6\uff1f\u96be\u9053\u8fd8\u60f3\u4ed6\u518d\u62ff\u6765\u6253\u6211\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u5c31\u5bf9\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54ea\u70b9\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u82e5\u7528\u6697\u5668\u6765\u6253\u6211\u4eec\xb7\u53ea\u8981\u6211\u4eec\u80fd\u63a5\u4f50\u4ed6\u7684\u6697\u5668\u5c31\u662f\u6211\u4ec0\u7684\u672c\u4e8b\xb7\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e2a\u4eba\u82e5\u51ed\u81ea\u5df1\u7684\u672c\u4e8b\u8d5a\u94b1\u5c31\u6ca1\u4ec0\u9ebd\u597d\u4e22\u4eba\u7684\u5bf9\u4e0d\u5bf9\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u5df2\u7ecf\u6709\u51e0\u70b9\u662f\u5bf9\u7684\u4e86\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u53c1\u70b9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u672b\u4f60\u8fd8\u6709\u4ec0\u9ebd\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8fd8\u60f3\u4e0d\u60f3\u542c\u6211\u7684\u4e3b\u610f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u5382\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u7b80\u76f4\u60f3\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u5176\u5b9e\u660e\u77e5\u4ed8\u4e0d\u51fa\u94b1\xb7\u8fd8\u8981\u53bb\u8d4a\u8d26\u4e5f\u662f\u4ef6\u4e22\u4eba\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u786c\u5934\u76ae\u53bb\u8d4a\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u6765\u662f\u4e2a\u5458\u8981\u9762\u5b50\u7684\u4eba\uff0c\u4e3a\u4ec0\u9ebd\u4f1a\u505a\u8fd9\u79cd\u4e8b\u5462 \u3000\u3000\u5f53\u7136\u662f\u4e3a\u4e86\u670b\u53cb\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u8fd9\u751f\u4e2d\u82e5\u4ea4\u4e2a\u80af\u4e3a\u4ed6\u4e22\u4eba\u7684\u670b\u53cb\u6b7b\u4e86\u4e5f\u672c\u7b97\u51a4\u6821\u3002 \u3000\u3000\u8001\u72d0\u72f8\u4e0e\u5927\u9189\u4fa0 \u3000\u3000\u90ed\u5927\u8def\u5e76\u4e0d\u559c\u6b22\u7bd9\u4eba\u4e5f\u4e0d\u592a\u4f1a\u9a82\u4eba\u55d3\u95e8\u53ef\u771f\u5927\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u91d1\u5bb6\u7684\u4eba\u95e8\u53e3\u9a82\u4eba\u8fde\u5df7\u4e8e\u5916\u9762\u7684\u71d5\u4e03\u90fd\u542c\u5f97\u6e05\u6d88\u695a\u695a\u3002 \u3000\u3000\u5df7\u9644\u8fd1\u6709\u68f5\u5927\u767d\u6768\u6811\u6811\u4e0b\u6709\u4e2a\u77f3\u58a9\u5b50\u3002 \u3000\u3000\u71d5\u4e03\u5c31\u5904\u5728\u77f3\u58a9\u6237\u4e0a\uff0c\u542c\u90ed\u5927\u8def\u9a82\u4eba\xb7\u8138\u4e0a\u5e26\u5f88\u6b23\u8d4f\u7684\u8868\u60c5\u5c31\u597d\u50cf\u591c\u542c\u4e2a\u540d\u89d2\u9685\u6210\u4f3c \u7684\u3002 \u3000\u3000\u56e0\u4e3a\u90ed\u5927\u8def\u660e\u7684\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u9a82\u7684\u662f\u91d1\u5927\u5e05\u3002 \u3000\u3000\u201c\u59e5\u91d1\u7684\uff0c\u4f60\u660e\u660e\u662f\u4e2a\u4eba\u4e3a\u4ec0\u9ebd\u8981\u8eb2\u5728\u5c4b\u91cc\u505a\u5bbf\u5934\u4e4c\u9f9f\u5462\uff1f\u4f60\u6015\u4ec0\u9ebd\u96be\u9053\u4f60\u9f3b\u5b50\u5df2\u7ecf\u88ab \u4eba\u6253\u5426\u4e86\u6240\u4ee5\u4e0d\u6562\u51fa\u6765\u89c1\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8d8a\u542c\u8d8a\u5f97\u610f\u56e0\u4e3a\u8fd9\u4e9b\u8bdd\u662f\u4ed6\u6559\u7ed9\u7ff1\u5927\u8def\u7684\u3002 \u3000\u3000\u201c\u91d1\u5927\u5e05\u65e2\u7136\u4e0d\u80af\u89c1\u4f60\uff0c\u4f60\u5c31\u7ad9\u5728\u4ed6\u95e8\u53e3\u53bb\u9a82\u9a82\u5230\u4ed6\u51fa\u6765\u4e3a \u3000\u3000\u8fd9\u79cd\u6cd5\u4e25\u5c31\u53eb\u505a\u9a82\u6218\u672c\u6765\u4e5f\u662f\u79cd\u5f88\u5360\u8001\u7684\u6218\u8def\u9762\u4e14\u901a\u5e38\u90c1\u5f88\u6709\u6548\uff0c \u3000\u3000\u4e24\u4e2d\u5bf9\u5792\u7684\u53ea\u8981\u65b9\u575a\u5b88\u4e0d\u51fa\u53e6\u4e07\u5c31\u4f1a\u6d3e\u4eba\u53bb\u9a82\u6218\xb7\u9a7e\u5f97\u5bf9\u65b9\u53d7\u4e0d\u4e86\u51fa\u6765\u8fce\u6218\u65f6\u5c31\u7b97\u6210\u529f \u4e86\u3002 \u3000\u3000\u636e\u8bf4\u6e2f\u845b\u4eae\u5c31\u8fd9\u6837\u7559\u8fc7\u8611\u71e5\u3002 \u3000\u3000\u90ed\u5927\u8def\u672c\u4e0d\u80af\u8fd9\u6837\u505a\u4f97\u71d5\u4e03\u5207\u8bdd\u5c31\u52a8\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u8fde\u6e2f\u845b\u5148\u751f\u90fd\u80fd\u7528\u8fd9\u79cd\u6218\u7565\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u65e2\u7136\u8fd9\u662f\u79cd\u6218\u7565\u5e76\u4e0d\u662f\u6cfc\u76ae\u65e0\u8d56\u7684\u884c\u5f84\uff0c\u6240\u4ee5\u90ed\u5927\u8def\u5c31\u53bb\u9a82\u5382\u800c\u4e14\u9a82\u5f97\u771f\u75db\u5feb\u3002 \u3000\u3000\u91d1\u5927\u6670\u53ea\u8981\u80fd\u542c\u5f97\u89c1\u6c34\u88ab\u4ed6\u9a82\u51fa\u6765\u624d\u662f\u602a\u4e8b\u3002 \u3000\u3000\u602a\u4e8b\u5e74\u5e74\u90fd\u6709\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u55d3\u95e8\u9a82\u8d77\u4eba\u6765\uff0c\u8fde\u4e8e\u6761\u8857\u5916\u7684\u4eba\u90fd\u4e0d\u4f1a\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u4f46\u91d1\u5bb6\u7684\u5927\u95e8\u5374\u504f\u5014\u8fd8\u662f\u8fde\u70b9\u52a8\u9759\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u91d1\u5927\u5e05\u96be\u9053\u662f\u4e2a\u804b\u5b50\uff1f \u3000\u3000\u522b\u4eba\u8fd8\u6ca1\u6709\u88ab\u9a82\u51fa\u6765\xb7\u90ed\u5927\u8def\u81ea\u5df1\u53cd\u800c\u5148\u6c89\u4e0d\u4f4f\u6c14\u4e86\u3002\u71d5\u4e03\u6559\u7ed9\u4ed6\u7684\u8bdd\uff0c\u4ed6\u5df2\u7ecf\u7ffb\u6765\u7406 \u53bb\u9a82\u4e86\u597d\u51e0\u904d\u522b\u4eba\u8fd8\u6ca1\u6709\u542c\u817b\u4ed6\u81ea\u5df1\u5374\u5df2\u7ecf\u9a82\u8170\u4e86\uff0c\u60f3\u627e\u51e0\u53e5\u65b0\u9c9c\u4e9b\u7684\u8bdd\u6765\u9a82\u9a82\u504f\u504f\u53c8\u60f3\u4e0d \u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u90a3\u8001\u5978\u5de8\u733e\u7684\u95e8\u623f\u5df2\u65bd\u65bd\u7136\u8d70\u4e86\u51fa\u6765\u624b\u91cc\u8fd8\u642c\u5f20\u6905\u5b50\u3002 \u3000\u3000\u5f20\u72e0\u8212\u670d\u7684\u85e4\u6905\u3002 \u3000\u3000\u8fd9\u8001\u72d0\u72f8\u5c45\u7136\u5c06\u85e4\u6905\u642c\u5230\u90ed\u5927\u8def\u7684\u9762\u524d\u6765\u8f7b\u8f7b\u7684\u653e\u4e86\u535c\u53bb\uff0c\u8138\u4e0a\u8fd8\u662f\u90a3\u79cd\u76ae\u7b11\u8089\u4e0d\u7b11\u7684 \u6837\u5b50\u8fde\u70b9\u706b\u6c14\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f81\u4e86\u5f81\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff0c\u201c\u70ae\u8fd9\u662f\u5e72\u4ec0\u9ebd\u201d \u3000\u3000\u8001\u95e8\u623f\u7b11\u563b\u563b\u9053\u201c\u8fd9\u662f\u6211\u4ecd\u5bb6\u8001\u7237\u7279\u5730\u53eb\u6211\u9001\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u542c\u89c1\u6211\u5728\u9a82\u4ed6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8001\u95e8\u623f\u9053\u201c\u6211\u4eec\u5bb6\u8001\u7237\u5e74\u7eaa\u867d\u4e0d\u5c0f\u8033\u6735\u5374\u8fd8\u6ca1\u6709\u804b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u53eb\u4f60\u9001\u8fd9\u5f20\u85e4\u6905\u6765\u4e8e\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8001\u95e8\u623f\u9053\u201c\u4ed6\u662f\u4f2f\u90ed\u5927\u4fa0\u9a82\u5f97\u592a\u7d2f\u4e86\u6240\u4ee5\u8bf7\u90ed\u5927\u4fa0\u5750\u4e0b\u6765\u9a82\u8fd8\u8bf4\u90ed\u5927\u4fa0\u82e5\u9a82\u5f97\u66f0\u6e34\u65f6\uff0c \u65e0\u8bba\u8981\u8336\u8981\u9152\uff0c\u90fd\u53ea\u7ba1\u5429\u5490\uff0c\u6211\u7acb\u523b\u5c31\u4e3a\u90ed\u5927\u4fa0\u9001\u6765\u3002\u201d \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\u63a5\u9053\u201c\u5230\u8fd9\u91cc\u6765\u7684\u5927\u4fa0\u867d\u7136\u591a\u3002\u4f46\u9a82\u4eba\u5374\u8fd8\u6ca1\u6709\u4e2a\u9a7e\u5f97\u6bd4\u90ed\u5927\u4fa0\u66f4\u7cbe\u5f69\u7684\u6240 \u4ee5\u6211\u4eec\u5bb6\u8001\u7237\u5e0c\u671b\u90ed\u5927\u4fa0\u591a\u9a82\u4e9b\u65f6\u5019\u5047\u5982\u8fd8\u80fd\u9a82\u5f97\u5927\u58f0\u70b9\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u8fd9\u5f20\u85e4\u6905\xb7\u53d1\u4e86\u534a\u5929\u6014\u8fde\u53e5\u8bdd\u90fd\u4e0d\u8bf4\u94ae\u5934\u5c31 \u3000\u3000\u90a3\u8005\u95e8\u623f\u8fd8\u5728\u5f8c\u9762\u5927\u7b11\u201c\u90ed\u5927\u4fa0\u8981\u8d70\u4e86\u9ebd\u4e0d\u9001\u4e0d\u9001\u4ee5\u5f8c\u6709\u7a7a\u7684\u65f6\u5019\u8fd8\u8bf7\u90ed\u5927\u4fa0\u968f\u65f6\u8fc7 \u6765\uff0c\u8fd9\u91cc\u4e0d\u4f46\u6709\u83dc\u6709\u9152\u8fd8\u6709\u4e13\u6cbb\u55d3\u54d1\u7684\u836f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b80\u76f4\u8fde\u9f3b\u5b50\u90fd\u5feb\u6c14\u6b6a\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u6447\u5934\u9053\u2018\u201c\u6211\u53eb\u4f60\u53bb\u6c14\u522b\u4eba\u7684\uff0c\u4f60\u81ea\u5df1\u53cd\u9762\u6c14\u5f97\u534a\u6b7b\uff0c\u8fd9\u53c8\u4f55\u82e6\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6068\u6068\u9053\u201c\u4f60\u82e5\u770b\u89c1\u90a3\u8001\u72d0\u72f8\u7684\u6837\u5b50\xb7\u4e0d\u88ab\u4ed6\u6d3b\u6d3b\u6c14\u6b7b\u624d\u602a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u65e0\u8bba\u8bf4\u4ec0\u9ebd\u4f60\u90fd\u5f53\u4ed6\u5728\u653e\u5c41\u4e0d\u662f\u5c31\u6ca1\u6709\u6c14\u4e86\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u65e0\u8bba\u8bf4\u4ec0\u9ebd\u5979\u90fd\u5f53\u6211\u5728\u653e\u5c41\u624d\u771f\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\uff0c\u9053\u201c\u4ed6\u771f\u7684\u9a82\u4f60\u662f\u5728\u653e\u5c41\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u867d\u7136\u6ca1\u6709\u8bf4\u51fa\u53e3\u6765\uff0c\u4f46\u90a3\u6837\u5b50\u5374\u6bd4\u8bf4\u51fa\u6765\u66f4\u53ef\u6068\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u5c45\u7136\u53d7\u5f97\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53d7\u4e0d\u4e86\u4e5f\u5f97\u53d7\u3002\u201d \u3000\u3000\u85d0\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u672c\u6765\u5c31\u662f\u5728\u653e\u5c41\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u3002\u4ed6\u7b11\u7684\u6837\u5b50\u5f53\u7136\u8fd8\u662f\u6bd4\u90a3\u8001\u95e8\u623f\u597d\u770b\u5f97\u591a\xb7\u5374\u5df2\u7ecf\u597d\u50cf\u6ca1\u6709\u4ee5\u524d\u90a3\u9ebd\u597d\u770b \u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\uff0c\u677f\u8138\u9053\u201c\u4f60\u7a76\u7adf\u8fd8\u6709\u591a\u5c11\u597d\u4e3b\u610f\u7d22\u6027\u6b21\u8bf4\u51fa\u6765\u7b97\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8fd8\u60f3\u542c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u542c\u6b7b\u7b97\u4e86\u542c\u6b7b\u4e00\u4e2a\u5c11\u4e2a\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u4e5f\u53f9\u4e86\u53e3\u6c14\xb7\u82e6\u7b11\u9053\u201c\u53ea\u53ef\u60dc\u6211\u4e5f\u6c49\u6587\u610f\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51b7\u51b7\u9053\u201c\u50cf\u4f60\u8fd9\u4f6f\u7684\u5929\u624d\u513f\u7ae5\u600e\u9ebd\u4f1a\u53d8\u5f97\u6ca1\u6709\u4e3b\u610f\u4e86\u5462\uff1f\u201d\u71d5\u4e03\u53f9\u9053\u201c\u4f60\u8bf4\u90a3\u95e8 \u623f\u662f\u8001\u72d0\u72f8\u4f9d\u6211\u770b\u91d1\u5927\u5e05\u624d\u771f\u6b63\u662f\u4e2a\u8001\u72d0\u72f8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51b7\u51b7\u9053\u201c\u4f60\u5e01\u662f\u8bf4\u4ed6\u4e00\u5411\u5f88\u8c6a\u723d\uff0c\u5f88\u5927\u65b9\u7684\u5417\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u771f\u7684\u8ddf\u4f60\u52a8\u624b\u65f6\xb7\u52b3\u6253\u4e0d\u4f60\u5c31\u5f97\u8d54\u51fa\u597d\u51e0\u767e\u4e24\u91d1\u4e8e\u82e5\u6253\u4f24\u4e86\u4f60\u4e5f\u5f97\u8d54\u597d\u51e0\u767e \u4e24\u94f6\u5b50\u7684\u533b\u836f\u8d39\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u770b\u91d1\u5927\u5e05\u6700\u8fd1\u4e00\u5b9a\u4e0a\u4e86\u4e0d\u5c11\u6b21\u5f53\uff0c\u5b66\u4e86\u4e0d\u5c11\u6b21\u4e56\uff0c\u6240\u4ee5\u603b\u7b97\u5df2\u60f3\u8fd9 \u9053\u7406\u4e86\u600e\u9ebd\u80af\u518d\u4e0a\u5f53\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4e0d\u4e0a\u5f53\u6211\u5c31\u4e0a\u5f53\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u8d50\u7136\u9053\u201c\u5176\u5b9e\u4f60\u4e5f\u4e0d\u80fd\u7b97\u4e0a\u5f53\xb7\u4f60\u603b\u7b97\u75db\u75db\u5feb\u6289\u7684\u9a82\u4e86\u6b21\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u80fd\u4e0d\u80fd\u518d\u9a82\u6b21\uff1f\u201d\u71d5\u4e03\u9053\u201c\u8fd9\u6b21\u4f60\u60f3\u9a82\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u9a82\u4f60\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\u4e00\u9a91\u9a6c\u9a70\u6765\u90ed\u5927\u8def\u5df2\u6c14\u5f97\u4ec0\u9ebd\u4e8b\u90fd\u4e0d\u611f\u5174\u8da3\u4e86\u4e5f\u998f\u5f97\u56de\u5934\u53bb\u770b\u773c\xb7\u7ad9\u5728\u4ed6\u5bf9\u9762\u7684 \u71d5\u4e03\u5374\u4f4e\u4e0b\u4e86\u5934\u597d\u50cf\u4e0d\u613f\u88ab\u9a6c\u4e0a\u7684\u4eba\u770b\u89c1\u9a6c\u4e0a\u5165\u7684\u773c\u775b\u5374\u504f\u504f\u5f88\u5c16\u8fd9\u5339\u9a6c\u521a\u51b2\u4eba\u5df7\u4e8e\u7a81\u7136\u58f0 \u957f\u5636\u4eba\u7acb\u800c\u8d77\u3002 \u3000\u3000\u9a6c\u4e0a\u4eba\u597d\u4fca\u7684\u9a91\u672f\u7ec4\u7ef3\u52d2\xb7\u4eba\u5df2\u8dc3\u8d77\uff0c\u51cc\u7a7a\u4e2a\u7ffb\u8eab\uff0c\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u90ed\u5927\u8def\u4ed6\u4eec\u9762\u524d\u8eab\u8863 \u670d\u6bd4\u6885\u4e88\u8fd8\u7ea2\u7ea2\u5f97\u8000\u773c\u3002\u6b22\u4e50\u835a\u9192 \u3000\u3000\u91d1\u5927\u5e05 \u3000\u3000\u9178\u6885\u6c64\uff0c\u6885\u6c5d\u7537\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u89c9\u5f97\u773c\u524d\u4eae\u5931\u58f0\u9053\u201c\u662f\u4f60\xb7\u4f60\u600e\u9ebd\u5230\u8fd9\u91cc\u6765\u5382\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u7b11\u9053\u201c\u6211\u6b63\u60f3\u95ee\u4f60\u5546\u4f60\u4eec\u4e24\u4e2a\u4eba\u600e\u9ebd\u4f1a\u8dd1\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d\u71d5\u4e03\u9053\u201c\u6211\u4eec\u5728\u7b49 \u4f60\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u901a\u6211\u4f1a\u6765\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u4f1a\u7b97\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u5a07\u7b11\u8f7b\u8f7b\u6253\u5382\u4ed6\u62f3\u5403\u5403\u7684\u7b11\u9053\u201c\u4f60\u5440\uff0c\u4f60\u8bf4\u7684\u8bdd\u6211\u8fde\u4e2a\u5b57\u4e5f\u4e0d\u500d\xb7\u56e0\u4e3a\u4f60\u662f\u4e2a\u2500 \u4e00\u2500\u201d \u3000\u3000\u71d5\u4e03\u7a81\u7136\u63a9\u4f50\u4e86\u5979\u7684\u5634\u5df4\u8138\u535c\u4eff\u4f5b\u53c8\u6709\u70b9\u53d1\u7ea2\uff0c\u6025\u9053\u201c\u4f60\u82e5\u6562\u80e1\u8bf4\u516b\u9053\uff0c\u770b\u6211\u4e0d\u6495\u7834\u4f60 \u7684\u5634\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u5f97\u4e49\u6014\u4f50\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u660e\u660e\u5df2\u62d2\u7edd\u4e86\u9178\u6885\u6c64\u7684\u5a5a\u4e8b\u9178\u6885\u6c64\u4e2d\u8be5\u6068\u6b7b\u4ed6\u624d\u5bf9\u3002 \u3000\u3000\u8d2d\u4e2a\u4eba\u89c1\u4e86\u9762\u4e3a\u4ec0\u9ebd\u8fd9\u6837\u4eb2\u70ed\u5462\uff1f \u3000\u3000\u6885\u6c5d\u7537\u773c\u73e0\u5b50\u76f4\u8f6c\u770b\u770b\u4ed6\u4e49\u770b\u770b\u71d5\u4e03\u62bf\u5634\u7b11\u9053\u201c\u597d\xb7\u6211\u5c0f\u8bf4\u53ef\u662f\u6211\u4e5f\u4e0d\u542c\u4f60\u7684\xb7\u5c0f\u90ed\u8bf4 \u8bdd\u6bd4\u4f60\u9760\u5f97\u4f4f\u3002\u201d\u5979\u53cb\u523b\u5c31\u4e49\u95ee\u9053\u201c\u5c0f\u90ed\u6211\u95ee\u4f60\u4f60\u4eec\u6765\u5e72\u4ec0\u9ebd\u7684\uff1f\u201d \u3000\u3000\u90ed\u4eba\u8def\u4e7e\u54b3\u4e86\u7f51\u58f0\u52c9\u5f3a\u7b11\u9053\u201c\u4ec0\u9ebd\u4e5f\u4e0d\u4e0b\u53ea\u5c0f\u8fc7\u2026\u2026\u53ea\u4e0d\u8fc7\u6765\u901b\u901b\u800c\u5df3\u5230\u8fd9\u91cc\u6765\u901b\u901b\u603b \u4e0d\u7b97\u72af\u6cd5\u5427\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u7b11\u9053\u201c\u6211\u8fd8\u5728\u6211\u5a18\u809a\u5b50\u91cc\u7684\u5df2\u7ecf\u5e38\u5e38\u5230\u8fd9\u91cc\u6765\u73a9\u5382\u201d \u3000\u3000\u71d5\u4e03\u770b\u770b\u90ed\u5927\u8def\u90bb\u5927\u8def\u60f3\u8bf4\u8bdd\xb7\u53c8\u5fcd\u4f53\u3002 \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4f60\u4eec\u7a76\u7adf\u5728\u6253\u4ec0\u9ebd\u5728\u610f\uff1f\u6211\u731c\u5f97\u5bf9\u4e0d\u5bf9\uff1f\u201d\u71d5\u4e03\u9053\u73af\u5bf9\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u5480\u9053\u201c\u90a3\u9ebd\u6211\u5b9d\u4e2a\u4e3b\u610f\u4e5f\u5c31\u4e0d\u5fc5\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u5fcd\u4e0d\u4f4f\u62a2\u95ee\u9053\u201c\u4ec0\u9ebd\u4e3b\u610f\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u8c08\u6de1\u9053\u201c\u65e2\u7136\u4f9d\u4eec\u5e76\u4e0d\u662f\u4e3a\u6b64\u800c\u6765\u7684\u6211\u8bf4\u4e86\u4e5f\u662f\u767d\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u82e5\u662f\u4e3a\u6b64\u800c\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u6885\u72ec\u7537\u9053\u201c\u90a3\u672a\u6211\u4e5f\u8bb8\u8fd8\u80fd\u66ff\u4f60\u4eec\u51fa\u4e2a\u4e3b\u610f\xb7\u5e2e\u4f60\u4eec\u4e2a\u5fd9\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u6211\u5c31\u544a\u8bc9 \u4f60\xb7\u4f60\u5b8c\u5168\u731c\u5bf9\u4e86\u4f60\u7b80\u76f4\u5c31\u662f\u4e2a\u6d3b\u6d3b\u7684\u8bdd\u845b\u4eae\u3002\u201d \u3000\u3000\u6094\u6c5d\u7537\u201c\u5509\u9648\u201d\u7b11\uff0c\u9053\u201c\u6211\u5c31\u77e5\u9053\xb7\u8fd8\u662f\u4f60\u6bd4\u4ed6\u8001\u5b9e\u4e9b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u4f60\u7684\u4e3b\u610f\u5462\uff1f\u4f60\u4e0d\u8bf4\u53ef\u4e0d\u884c\u3002\u201d \u3000\u3000\u6885\u72ec\u7537\u80cc\u8d1f\u53cc\u624b\u9992\u6162\u7684\u8dea\u8d77\u65b9\u6b65\u6765\uff0c\u5c31\u597d\u50cf\u771f\u7684\u5c06\u81ea\u5df1\u5f53\u6210\u5382\u8bf8\u845b\u4eae\u3002 \u3000\u3000\u71d5\u4e03\u51b7\u51b7\u7684\u9053\u201c\u6211\u5c31\u77e5\u9053\u4f60\u8fd9\u4e2a\u4eba\u4ece\u6765\u4e0d\u8bf4\u8001\u5b9e\u8bdd\u3002\u201d\u6885\u6c5d\u7537\u7b11\u9053\u201c\u968f\u4fbf\u4f60\u600e\u9ebd\u6837\u6fc0\u6211 \u90fd\u6ca1\u6709\u70b9\u7528\u7684\xb7\u6211\u4e0d\u8bf4\u5c31\u662f\u4e0d\u8bf4\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u8981\u600e\u9ebd\u6837\u4f60\u624d\u80af\u8bf4\uff1f\u201d \u3000\u3000\u6398\u6c5d\u7537\u9053\u201c\u8981\u6709\u6761\u4ef6\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u6761\u4ef6\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u7728\u4e86\u7728\u773c\u9053\u201c\u5230\u624b\u7684\u4e70\u5356\u89c1\u9762\u5206\u534a\xb7\u8fd9\u53e5\u8bdd\u4f60\u4eec\u603b\u8be5\u542c\u8bf4\u8fc7\u201c \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u9053\u201c\u539f\u6765\u4f60\u60f3\u9ed1\u5403\u9ed1\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5176\u5b9e\u6211\u7684\u5fc3\u5e76\u4e0d\u592a\u9ed1\u4e5f\u4e0d\u60f3\u771f\u7684\u5206\u534a\u53ea\u53c1\u4e03\u6298\u5e10\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u7684\u4e3b\u610f\u82e5\u4e5f\u4e0d\u7075\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u7075\u4e0d\u7075\u5f53\u573a\u8bd5\u9a8c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u7b11\u9053\u201c\u6211\u770b\u4f60\u8be5\u6539\u884c\u53bb\u5356\u72d7\u76ae\u818f\u836f\u624d\u5bf9\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u8fd9\u72d7\u76ae\u818f\u836f\u4f60\u4eec\u4e70\u4e0d\u4e70\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u4e70\u4e5f\u662f\u53e3\u4e0d\u4e70\u3002\u201d \u3000\u3000\u6885\u6d4a\u7537\u5a9a\u7136\u2018\u7b11\u9053\u201c\u6211\u4e0d\u5356\u4e5f\u662f\u767d\u4e0d\u5356\u3002\u201d \u3000\u3000\u9ad8\u5899\u3002 \u3000\u3000\u6885\u6c5d\u7537\u5e26\u71d5\u4e03\u548c\u90ed\u5927\u8def\u4ece\u5f8c\u9762\u8f6c\u5230\u8fd9\u9ed1\u5df7\u5b50\u91cc\u6765\u3002 \u3000\u3000\u8fd9\u6761\u5df7\u5b50\u5f53\u7136\u6bd4\u524d\u9762\u7a84\u5f97\u591a\uff0c\u5df7\u5e95\u6709\u4e2a\u7a84\u7a84\u7684\u9ed1\u6f06\u95e8\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u5c31\u662f\u91d1\u5bb6\u7684\u5f8c\u95e8\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u70b9\u70b9\u5934\u9053\u201c\u5899\u91cc\u9762\u5c31\u662f\u91d1\u5bb6\u7684\u5f8c\u56ed\u5f00\u4e86\u6625\xb7\u91d1\u5927\u53d4\u5c31\u4ece\u524d\u9762\u7684\u6696\u9601\u642c\u5230\u5f8c\u56ed\u6765\u5f80 \u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u542c\u3002 \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u73b0\u5728\u6211\u5c31\u4ece\u8fd9\u91cc\u8df3\u5899\u8fdb\u53bb\xb7\u4f60\u8981\u5728\u5f8c\u9762\u8ffd\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u7136\u5f8c\u6211\u5c31\u4f1a\u627e\u5230\u91d1\u5927\u53d4\uff0c\u544a\u8bc9\u4ed6\u4f60\u6b3a\u8d1f\u4e86\u6211\u8981\u4ed6\u66ff\u6211\u51fa\u6c14\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u91d1\u5927\u53d4\u4e00\u5411\u6700\u75bc\u6211\u770b\u89c1\u4f60\u8ffd\u53bb\uff0c\u5b9a\u5c31\u4f1a\u7528\u8fde\u73e0\u5f39\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6ca1\u6709\u7136\u5f8c\u4e86\u53ea\u800d\u4f60\u80fd\u63a5\u5f97\u4f4f\u4ed6\u7684\u8fde\u73e0\u5f39\uff0c\u7acb\u523b\u5c31\u53d8\u6210\u5382\u4e2a\u5c0f\u9614\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u63a5\u4e0d\u4f4f\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u90a3\u5c31\u8bf4\u4e0d\u4f1a\u5b9a\u53d8\u6210\u4e2a\u6b7b\u4eba\u5382\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u70b9\u70b9\u5934\u9053\u201c\u4ed6\u65e2\u5df2\u77e5\u9053\u4f60\u5728\u6b3a\u8d1f\u6211\u5bf9\u4f60\u51fa\u624b\u81ea\u7136\u7edd\u4e0d\u4f1a\u5ba2\u6c14\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60 \u5462\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6211\uff1f\u6211\u5f53\u7136\u53ea\u80fd\u5728\u65c1\u8fb9\u770b\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u9614\u4e86\uff0c\u4f60\u5c31\u6765\u627e\u6211\u5206\u8d26\u6211\u82e5\u6b7b \u4e86\xb7\u4f60\u603b\u8be5\u66ff\u6211\u4e70\u53e3\u68fa\u6750\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u65e0\u8bba\u6211\u600e\u9ebd\u6837\u4f60\u90fd\u8fde\u70b9\u635f\u5931\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u7b11\u9053\u201c\u5f53\u7136\u6ca1\u6709\u5426\u5219\u6211\u4e3a\u4ec0\u9ebd\u8981\u66ff\u4f60\u51fa\u4e3b\u610f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u957f\u53f9\u4e86\u58f0\u54fa\u54fa\u9053\u201c\u597d\u5b83\u610f\xb7\u8fd9\u9ebd\u597d\u7684\u4e3b\u610f\u771f\u4e8f\u4f60\u600e\u9ebd\u60f3\u5f97\u51fa\u7684\u3002\u201d\u6885\u6c5d\u7537\u9053\u201c\u5973 \u4eba\u4e2d\u5c31\u7edd\u4e0d\u80af\u505a\u4e8f\u672c\u7684\u751f\u610f\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u53f9\u9053\u201c\u5973\u4eba\u5509\u5973\u4eba\u3002\u201d\u6885\u6c5d\u7537\u9053\u201c\u4f60\u7a76\u7adf\u5e72\u4e0d\u5e72\uff1f\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u4e0d\u5e72\u4e5f\u662f\u767d \u4e0d\u5e72\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4f60\u6b64\u5382\u53ef\u4e0d\u80fd\u6028\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u771f\u6b7b\u4e86\xb7\u611f\u6a84\u4f60\u8fd8\u6765\u4e0d\u53ca\u600e\u9ebd\u4f1a\u6028\u4f60\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u611f\u6fc0\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6b7b\u4eba\u65e2\u4e0d\u5fc5\u518d\u770b\u503a\u4e3b\u5634\u8138\uff0c\u4e5f\u4e0d\u5fc5\u518d\u542c\u5973\u4eba\u5676\u54b3\xb7\u5c82\u975e\u6bd4\u6d3b\u7a77\u53d7\u7f6a\u597d\u5f97 \u591a\u3002\u201d \u3000\u3000\u6885\u72ec\u7537\u9053\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5047\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4ece\u6765\u6ca1\u6709\u89c9\u5f97\u6d3b\u662f\u5728\u53d7\u7f6a\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u6d3b\u5f97\u5f88\u5feb\u4e50\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u9ebd\u60c5\u51b5\u4e0b\u4ed6\u90fd\u80fd\u627e\u5230\u6709\u610f\u4e49\u7684\u4e8b\u505a\u65e0\u8bba\u4ed6\u505a\u4ec0\u9ebd\u90fd\u505a\u5f97\u5f88\u8d77\u52b2\u6240\u4ee5\u4ed6\u5f88\u5feb\u4e50\u3002 \u3000\u3000\u82e5\u7b49\u5230\u4ed6\u771f\u7684\u60f3\u6b7b\u7684\u65f6\u5019\u4e16\u4e0a\u7684\u4eba\u5c31\u7b97\u6ca1\u6b7b\u5149\u5269\u4e0b\u7684\u4e5f\u5b9a\u6ca1\u6709\u51e0\u4e2a\u3002 \u3000\u3000\u666e\u901a\u4eba\u5bb6\u7684\u5899\u4e08\u56db\u5df2\u7ecf\u7b97\u5f88\u9ad8\u5382\u4f46\u8fd9\u9053\u5899\u5374\u81f3\u5c11\u6709\u4e24\u4e08\u6885\u6c5d\u7537\u62ac\u8d77\u5934\u6253\u91cf\u4e86\u51e0\u773c\u9053\u201c\u4f60 \u6ca1\u6709\u628a\u63e1\u80fd\u4e0a\u5f97\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u9a6c\u9a6c\u864e\u864e\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u9a6c\u9a6c\u864e\u864e\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u662f\u5927\u6982\u8fd8\u53ef\u535c\u5f97\u5de6\u7684\u610f\u601d\u56e0\u4e3a\u6211\u867d\u7136\u6ca1\u6709\u628a\u63e1\xb7\u5374\u6709\u52c7\u6c14\u3002 \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5728\u8f7b\u529f\u7684\u79d8\u8bc0\u91cc\u6709\u3002\u201d \u3000\u3000\u8fd9\u5012\u4e0d\u662f\u80e1\u5439\u3002 \u3000\u3000\u7ff1\u5927\u8def\u65e0\u8bba\u505a\u4ec0\u9ebd\u4e8b\uff0c\u6700\u5927\u7684\u79d8\u6620\u5374\u6b63\u662f\u201c\u52c7\u6c14\u201d\u8fd9\u4e24\u4e2a\u5b87\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u770b\u4ed6\u5480\u606f\u9053\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u83ab\u8981\u649e\u7834\u5934\u624d\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u7b97\u649e\u7834\u5934\u6211\u4e5f\u4f1a\u4e0a\u53bb\u3002\u201d\u62d6\u6c5d\u7537\u5a9a\u7136\u540d\u9053\u201c\u597d\u6211\u5148\u535c\u53bb\u770b\u770b\xb7\u6253\u62db\u547c\u4f60\u5c31 \u5feb\u8ffd\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6709\u628a\u63e1\u80fd\u4e0a\u5f97\u53bb\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5979\u53c8\u7b11\u5382\u7b11\u9053\u201c\u6211\u65e2\u6ca1\u6709\u628a\u63e1\u4e5f\u6ca1\u6709\u52c7\u6c14\u53ef\u662f\u6211\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5c31\u662f\u8fd9\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u8df3\u4e0a\u90ed\u5927\u8def\u7684\u80a9\uff0c\u518d\u4ece\u90ed\u5927\u8def\u80a9\u4e0a\u8df3\u8d77\uff0c\u5c31\u8df3\u4e0a\u5899\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u4e86\u53e3\u6c14\xb7\u9676\u95fb\u9053\u201c\u5973\u4eba\u7528\u7684\u6cd5\u5b50\u4e3a\u4ec0\u9ebd\u603b\u662f\u8981\u7537\u4eba\u5403\u4e8f\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6de1\u8c08\u9053\u201c\u90a3\u53ea\u56e0\u4e3a\u5927\u591a\u6570\u7537\u4eba\u90fd\u592a\u7b28\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u96be\u9053\u4e0d\u662f\u7537\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\u9053\u201c\u4e5f\u662f\u7537\u4eba\u53ef\u662f\u6211\u4e0d\u7b28\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u5df2\u7ecf\u5728\u4e0a\u56de\u62db\u624b\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u4f5c\u52bf\u60f3\u8df3\u8d77\u5ffd\u4e49\u505c\u6269\u6765\u56de\u987e\u770b\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8fd8\u7b49\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8fd9\u53bb\u8bf4\u4e0d\u5b9a\u771f\u7684\u4f1a\u53d8\u6210\u4e2a\u6b7b\u4eba\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u603b\u8be5\u5c06\u90a3\u4e2a\u79d8\u5bc6\u544a\u8bc9\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u8fd8\u672c\u884c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u8fd9\u6b21\u4f60\u7edd\u5bf9\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6709\u628a\u63e1\uff1f\u201d\u71d5\u4e03\u53f9\u9053\u201c\u8bf4\u4f60\u7b28\u4f60\u679c\u7136\u771f\u7b28\u3002\u201d\u4ed6\u770b\u90ed\u5927\u8def\xb7\u53e3\u5149\u5ffd\u7136\u53d8\u5f97 \u5f88\u6e29\u67d4\xb7\u8f7b\u8f7b\u9053\u201c\u6211\u82d4\u6ca1\u628a\u63e1\u600e\u9ebd\u4f1a\u653e\u5fc3\u8ba9\u4f60\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u590f\u7b28\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u770b\u90ed\u5927\u8def\u6447\u5934\xb7\u9053\u201c\u4f60\u771f\u662f\u7b28\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u773c\u9053\u201c\u4f60\u51ed\u4ec0\u9ebd\u4e5f\u8bf4\u6211\u7b28\uff1f\u201d \u3000\u3000\u6094\u6c5d\u7537\u9053\u201c\u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u7b28\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u54ea\u70b9\u7b28\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u90a3\u70b9\u90fd\u7b28\xb7\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u53d8\u5f97\u7a0d\u5fae\u806a\u660e\u4e9b\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u80fd\u4e0d\u80fd\u4e0d\u806a\u660e\uff1f\u80fd\u4e0d\u80fd\u7b28\u70b9\uff1f\u201d\u6885\u6c5d\u7537\u9053\u201c\u5f53\u7136\u80fd\u3002\u201d \u3000\u3000\u5979\u4f38\u624b\u62cd\u4e86\u62cd\u90ed\u5927\u8def\u7684\u80a9\u5934\uff0c\u5a9a\u7136\u9053\u201c\u56e0\u4e3a\u6709\u5f88\u591a\u5973\u63a5\u5b50\u90fd\u559c\u6b22\u7b28\u70b9\u7684\u7537\u4eba\u6240\u4ee5\u4f60\u5c3d\u7ba1 \u7b28\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u4e0d\u662f\u90a3\u5f88\u591a\u4ea5\u5b69\u5b50\u5176\u4e2d\u4e4b\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u7b11\u9053\u201c\u6211\u4e0d\u662f\u6211\u4e5f\u4e0d\u6562\u3002\u201d \u3000\u3000\u5979\u8198\u4e86\u5899\u4e0b\u7684\u71d5\u4e03\u773c\u5403\u5403\u7684\u7b11\u71d5\u5b50\u822c\u7684\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5979\u5f53\u7136\u4e0d\u4f1a\u98de\u53ef\u662f\u5979\u8eab\u88ab\u7684\u786e\u6709\u5982\u8304\u5b50\u822c\u7f8e\u5999\u8f7b\u76c8\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ad9\u5728\u5899\u5934\u4eff\u62c2\u5df2\u6709\u4e86\u4e9b\u75f4\u4e86\u3002\u71d5\u4e03\u54ac\u5634\u5507\xb7\u8f7b\u8f7b\u8dfa\u4e86\u8dfa\u811a\u9053\u201c\u7b28\u86cb\uff0c\u8fd8\u4e0d\u5feb\u8ffd\u4e0a \u53bb\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\xb7\u4eff\u4f5b\u770b\u51fa\u4e86\u4ec0\u9ebd\u53c8\u4eff\u4f5b\u4ec0\u9ebd\u90fd\u6ca1\u6709\u770b\u51fa\u6765\uff0c\u4eff\u4f5b\u60f3\u8bf4\u4ec0\u9ebd\u5374\u53c8\u4ec0\u9ebd\u90fd\u6ca1\u6709 \u8bf4\u3002 \u3000\u3000\u5230\u5458\u5f8c\u4ed6\u624d\u95ee\u4e86\u65ec\u201c\u4f60\u7b49\u4e0d\u7b49\u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b28\u86cb\xb7\u6211\u5f53\u7136\u7b49\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7b49\u591a\u4e45\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u591a\u4e45\u6211\u90fd\u7b49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u80fd\u8ffd\u5f97\u4e0a\u7edd\u4e0d\u4f1a\u8ffd\u9517\u4eba\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7ad9\u5728\u5899\u4e0b\xb7\u4eff\u4f5b\u4e5f\u6709\u4e9b\u75f4\u5b50\u3002 \u3000\u3000\u4e5f\u8bb8\u4e0d\u662f\u75f4\u662f\u9189\u3002 \u3000\u3000\u5979\u773c\u6ce2\u8f7b\u8ff7\u8138\u4e0a\u6cdb\u7ea2\u6655\uff0c\u4e0d\u662f\u9189\u662f\u4ec0\u9ebd\uff1f \u3000\u3000\u5979\u9189\u7684\u4e49\u662f\u4ec0\u9ebd\uff1f \u3000\u3000\u91d1\u5927\u5e05\u3002 \u3000\u3000\u2026\u4e2a\u53eb\u5927\u5e05\u7684\u4eba\u65e0\u8bba\u4ed6\u662f\u5c0f\u662f\u771f\u7684\u5927\u5e05\xb7\u81f3\u5c11\u603b\u6709\u4e9b\u5927\u5e05\u7684\u6d3e\u5934\u3002 \u3000\u3000\u91d1\u5927\u5e05\u7684\u6e90\u5934\u679c\u7136\u4e0d\u5c0f\u3002 \u3000\u3000\u4ed6\u5f88\u9ad8\uff0c\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u8981\u9ad8\u4e2d\u4e2a\u5934\u3002 \u3000\u3000\u4e0d\u4f46\u9ad8\u800c\u533b\u9b41\u4f20\u3001\u5065\u58ee\u3002 \u7b2c\u5341\u4e09\u7ae0\u3000\u91d1\u5b50\u4e0e\u6559\u8bad>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u91d1\u5b50\u4e0e\u6559\u8bad \u3000\u3000\u9ad8\u5927\u9b41\u4f1f\u7684\u4eba\u770b\u6765\u603b\u7279\u522b\u663e\u5f97\u6c14\u52bf\u51cc\u4eba\u864e\u864e\u6709\u5a01\u3002\u867d\u7136\u5df2\u7ecf\u6709\u4e94\u5341\u591a\u5c81\u4ecd\u7ad9\u5728\u90a3\u91cc\u8170\u6746 \u4ecd\u7136\u7b14\u76f4\u773c\u775b\u4ecd\u7136\u6709\u5149\u80e1\u5b50\u867d\u7136\u7559\u5f97\u4e0d\u592a\u957f\uff0c\u5374\u5f88\u6d53\u3001\u5f88\u9ed1\u3002\u4ed6\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u5f53\u7136\u4e5f\u5b9a\u526a\u88c1 \u5408\u8eab\uff0c\u6599\u5b50\u4e2d\u8d35\u4f60\u5c31\u7b97\u4e0d\u77e5\u9053\u4ed6\u662f\u91d1\u5927\u5e05\u4e5f\u7edd\u4e0d\u4f1a\u5c06\u4ed6\u770b\u6210\u4e2a\u65e0\u540d\u5c0f\u5352\u3002\u90ed\u5927\u8def\u4e00\u773c\u5c31\u770b\u51fa \u662f\u91d1\u5927\u5e05\u3002 \u3000\u3000\u6885\u6c5d\u7537\u9003\u8fc7\u53bb\u7684\u65f6\u5019\u4ed6\u5c0f\u7ad9\u5728\u5c4b\u5b50\u524d\u9762\u7684\u6843\u6811\u4e0b\u6b23\u8d4f\u6811\u4e0a\u65b0\u53d1\u7684\u6843\u82b1\u54bd\u91cc\u4eff\u4f5b\u8fd8\u5728\u4f4e\u5cad \u8bd7\u53e5\u3002 \u3000\u3000\u8fd9\u4f9d\u5927\u5e05\u770b\u6765\u8fd8\u662f\u4e2a\u98ce\u96c5\u4e4b\u4e0a\u3002 \u3000\u3000\u5f53\u770b\u5230\u4ed6\u6885\u6c5d\u7537\u773c\u775b\u91cc\u5c31\u597d\u50cf\u5df2\u6709\u5382\u773c\u6cea\u6574\u4e2a\u4eba\u90fd\u51e0\u4e4e\u6251\u5230\u4ed6\u8eab\u4e0a\u4e5f\u4e0d\u77e5\u8bf4\u5382\u4e9b\u4ec0\u9ebd\u3002 \u3000\u3000\u90ed\u5927\u8def\u542c\u4e0d\u89c1\u5979\u8bf4\u7684\u8bdd\uff0c\u5374\u770b\u89c1\u91d1\u5927\u5e05\u9762\u5e7f\u5df3\u781a\u5c71\u6012\u5bb9\u5389\u58f0\u9053\u201c\u5c31\u662f\u4ed6\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u4e0d\u505c\u7684\u70b9\u5934\u4e0d\u505c\u7684\u6d41\u6cea\u3002\u7ff1\u5927\u8def\u770b\u5f97\u53c8\u597d\u7b11\u53c8\u4f69\u670d\u201c\u6587\u4eba\u597d\u50cf\u5168\u90fd\u5927\u4e2d\u5c31\u662f\u4f1a\u6f14 \u620f\u7684\u3002\u201d \u3000\u3000\u518d\u770b\u91d1\u5927\u5e05\u7684\u6012\u5bb9\u66f4\u76db\uff0c\u77aa\u90ed\u5927\u8def\u5389\u58f0\u9000\uff0c\u201c\u4f60\u60f3\u9003\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5e76\u6ca1\u6709\u9003\u5440\u4e0d\u662f\u597d\u597d\u7684\u7ad9\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u597d\u597d\u201c\u2026\u4f60\u597d\u201d \u3000\u3000\u4ed6\u4f3c\u5df2\u6c14\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002\u90ed\u5927\u8def\u9053\u201c\u8fd9\u6b21\u4f60\u8bf4\u5bf9\u4e86\uff0c\u6211\u672c\u6765\u5c31\u597d\u597d\u7684\u3002\u201d\u91d1\u5927\u5e05\u5927 \u543c\u58f0\u9053\u201c\u6c14\u6b7b\u8001\u592b\u4e5f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6c14\u6b7b\u4e2a\u5c11\u4e2a\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u4e24\u773c\u7ffb\u767d\u597d\u50cf\u968f\u65f6\u90fd\u8981\u6c14\u6655\u8fc7\u53bb\u7684\u6837\u5b50\u3002 \u3000\u3000\u5e78\u597d\u6885\u6c5d\u7537\u5df2\u53ca\u65f6\u8fc7\u6765\u6276\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u5979\u4e0d\u77e5\u4ec0\u9ebd\u65f6\u5019\xb7\u5df2\u4ece\u5c4b\u91cc\u53d6\u51fa\u4e86\u67c4\u91d1\u5149\u95ea\u95ea\u7684\u5de8\u5f13\uff0c\u8fd8\u6709\u4e2a\uff01 \u3000\u3000\u6c89\u5308\u7538\u7684\u9b54\u76ae\u53e3\u888b\u3002 \u3000\u3000\u91d1\u5927\u5e05\u628a\u63a5\u8fc7\u4e86\u5de8\u5f13\uff0c\u6574\u4e2a\u4eba\u5c31\u597d\u50cf\u7acb\u523b\u53d8\u4e86\xb7\u4e3a\u4e4b\u7cbe\u795e\u6296\u64de\uff0c\u66f4\u6709\u6c14\u6d3e\u4e5f\u53d8\u5f97\u5e74\u9752\u4e86 \u5f88\u591a\u3002 \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u5b58\u5fc3\u60f3\u6c14\u4ed6\uff0c\u73b0\u5728\u4e5f\u4e0d\u6562\u5927\u610f\u4e86\u3002 \u3000\u3000\u6210\u540d\u7684\u9ad8\u624b\u624b\u4e0a\u5df2\u6709\u4e86\u6210\u540d\u7684\u6b66\u5668\u4f60\u5728\u4ed6\u9762\u524d\u82e5\u8fd8\u6562\u5927\u610f\u4e0d\u628a\u547d\u9001\u9677\u624d\u602a\u3002 \u3000\u3000\u53ea\u542c\u91d1\u5927\u5e05\u5927\u559d\u58f0\u201c\u201d \u3000\u3000\u8fd9\u4e2a\u5b87\u559d\u51fa\uff0c\u6ee1\u5929\u91d1\u5149\u98de\u821e\u6d41\u52a8\u5982\u66b4\u96e8\u631f\u5e26\u72c2\u98ce\u5411\u90ed\u5927\u8def\u5c04\u5382\u8fc7\u6765\u3002 \u3000\u3000\u91d1\u5927\u5e05\u7684\u8fde\u73e0\u795e\u5f39\u679c\u7136\u4e0d\u662f\u597d\u73a9\u7684\u3002 \u3000\u3000\u5e78\u597d\u90ed\u5927\u8def\u65e9\u5df2\u6709\u4e86\u51c6\u5907\u3002\u91d1\u5927\u5e05\u7684\u8fde\u73e0\u5f39\u56fa\u7136\u5feb\u4ed6\u63a5\u5f97\u4e5f\u5feb\u3002 \u3000\u3000\u5929\u4e0a\u82e5\u6709\u91d1\u5b50\u6389\u4e0b\u6765\uff0c\u65e0\u8bba\u8c01\u90fd\u4e0d\u4f1a\u63a5\u5f97\u592a\u6162\u7684\uff0c\u4f55\u51b5\u4ed6\u672c\u6765\u5c31\u6709\u70b9\u771f\u529f\u592b\u3002 \u3000\u3000\u6885\u6c5d\u7537\u5728\u65c1\u8fb9\u770b\u5ffd\u7136\u5927\u58f0\u9053\u201c\u8d2a\u5403\u7684\u732a\u8981\u5148\u6448\u5bb0\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u4e0d\u77e5\u662f\u6ca1\u542c\u89c1\u8fd8\u662f\u6ca1\u542c\u61c2\u3002 \u3000\u3000\u4ed6\u8eab\u535c\u6709\u4e24\u4e2a\u5f88\u5927\u7684\u53e3\u888b\u624b\u91cc\u7684\u7f51\u63a5\u6ee1\u4e86\u5c31\u5012\u5728\u53e3\u888b\u91cc\u3002 \u3000\u3000\u91d1\u5927\u5e05\u7684\u8fde\u73e0\u5f39\u53d1\u4e8c\u5341\u5f39\u6bcf\u53d1\u8fc7\u5f8c\uff0c\u603b\u8981\u505c\u4e0b\u6765\u5598\u95e8\u6c14\u6b63\u597d\u7ed9\u4ed6\u4e2a\u673a\u4f1a\xb7\u5c06\u7f51\u91cc\u7684\u91d1\u5f39 \u88c5\u5165\u53e3\u888b\u3002 \u3000\u3000\u65e0\u8bba\u591a\u9ebd\u5927\u7684\u53e3\u888b\uff0c\u4e5f\u4e0d\u50cf\u4eba\u7684\u6b32\u671b\u7edd\u4e2d\u4f1a\u88c5\u4e0d\u6ee1\u7684\u3002 \u3000\u3000\u7ff1\u5927\u8def\u8d70\u7684\u65f6\u5019\u888b\u91cc\u5df1\u88c5\u6ee1\u4e86\u820d\u5f39\u5b50\u3002 \u3000\u3000\u76f4\u7b49\u53e3\u888b\u88c5\u6ee1\uff0c\u4ed6\u624d\u4e58\u91d1\u5927\u5e05\u5598\u6c14\u7684\u65f6\u5019\u6e9c\u4e01\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u60f3\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u79bb\u5f00\u8fd9\u91cc\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\u4ed6\u8eab\u6cd5\u4f3c\u5df2\u6ca1\u6709\u521a\u5feb\u3002 \u3000\u3000\u5e78\u597d\u91d1\u5927\u5e05\u7684\u4f53\u79ef\u592a\u5927\uff0c\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u5c31\u7b97\u6765\u8ffd\u4e5f\u672a\u5fc5\u8feb\u5f97 \u3000\u3000\u90ed\u5927\u8def\u521a\u773a\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u8bb0\u5f97\u5899\u89d2\u4e0b\u6709\u66f0\u6c34\u4e95\u3002 \u3000\u3000\u4ed6\u8bb0\u5fc6\u529b\u5c45\u7136\u4e0d\u9519\u5c45\u7136\u8fd8\u6ca1\u6709\u88ab\u91d1\u5149\u95ea\u82b1\u4e86\u773c\u6240\u4ee5\u5f88\u5feb\u5c31\u627e\u5230\u4e86\u8fd9\u66f0\u4e95\u3002 \u3000\u3000\u71d5\u4e03\u5f53\u7136\u5b9a\u5c31\u5728\u5916\u9762\u7b49\u4ed6\u3002 \u3000\u3000\u201c\u6ca1\u6709\u7136\u5f8c\u4e86\uff0c\u53ea\u8981\u4f60\u80fd\u63a5\u5f97\u4f4f\u4ed6\u7684\u8fde\u73e0\u5f39\u7acb\u523b\u5c31\u53d8\u6210\u4e86\u4e2a\u5c0f\u9614\u8001\u3002\u201d \u3000\u3000\u9614\u8001\u5c31\u7528\u4e0d\u518d\u770b\u503a\u4e3b\u7684\u8138\u8272\u3002 \u3000\u3000\u90ed\u5927\u8def\u6478\u4e86\u6478\u53e3\u888b\u91cc\u7684\u91d1\u5f39\u5b50\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u62ac\u5934\u770b\u4e86\u770b\u5899\u5934\u5f8c\u9000\u4e86\u4e24\u6b65\u53cc\u81c2\u4e00\u632f\u201c\u71d5\u5b50 \u7a7f\u4e91\u201d\u594b\u529b\u5411\u4e0a\u2018\u8df3\u3002 \u3000\u3000\u521a\u4ed6\u5c31\u662f\u7528\u8fd9\u8eab\u6cd5\u773a\u4e0a\u5899\u7684\u73b0\u5728\u4ed6\u5f53\u7136\u4e5f\u5f88\u6709\u628a\u63e1\u3002 \u3000\u3000\u8c01\u77e5\u8c0a\u8fd9\u6b21\u7adf\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u7528\u7684\u529b\u6c14\u6bd4\u521a\u66f4\u5927\u4f46\u8df3\u5230\u9876\u70b9\u65f6\uff0c\u8ddd\u79bb\u5899\u5934\u81f3\u5c11\u8fd8\u6709\u516d\u4e03\u5c3a\u8111\u888b\u51e0\u4e4e\u6a80\u5230\u5899\u4e0a\u51e0 \u4e4e\u771f\u7684\u649e\u7834\u4e86\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u867d\u7136\u6ca1\u6709\u649e\u51fa\u4e2a\u5927\u6d1e\uff0c\u5374\u4e5f\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002 \u3000\u3000\u201c\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u96be\u9053\u4ed6\u8f7b\u529f\u5ffd\u7136\u95ee\u5c31\u9000\u6b65\u4e86\u8fd9\u9ebd\u591a\uff1f \u3000\u3000\u90ed\u5927\u8def\u6478\u8111\u888b\xb7\u89c9\u5f97\u8fd9\u5b9e\u5728\u6709\u70b9\u90aa\u95e8\xb7\u4ed6\u5b9e\u5728\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u60f3\u4e0d\u901a\u5c31\u53ea\u6709\u518d\u8bd5\u8bd5\u3002 \u3000\u3000\u8fd8\u6709\u6837\u8111\u888b\u53c8\u51e0\u4e4e\u88ab\u649e\u7834\u4e2a\u5927\u6d1e\xb7\u53c8\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u81fc\u5df1\u5f80\u4e0a\u8df3\u7684\u65f6\u5019\u8170\u7554\u7684\u95e8\u888b\u91cc\u597d\u50cf\u6709\u53cc\u624b\u5728\u5c06\u4ed6\u5f80\u4e0b\u62c9\u3002 \u3000\u3000\u53e3\u888b\u91cc\u5f53\u7136\u6ca1\u6709\u5b50\uff0c\u53ea\u6709\u91d1\u5f39\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u60f3\u901a\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\u5382\u3002 \u3000\u3000\u8f99\u91d1\u5f39\u5b50\u82e5\u6709\u56db\u4e24\xb7\u56db\u535c\u8f83\u91d1\u5f39\u5b50\u5c31\u662f\u5341\u65a4\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u8eab\u4ea1\u591a\u4e01\u4e8c\u53c1\u5341\u65a4\u91cd\u91cf\xb7\u8f7b\u529f\u90fd\u8981\u5927\u5927\u6253\u4e2a\u6298\u6263\u7684\u3002 \u3000\u3000\u521a\u4ed6\u82e5\u662f\u5c11\u63a5\u4e24\u53d1\u73b0\u5728\u4e5f\u8bb8\u5c31\u5df2\u7ecf\u8df3\u4e0a\u5899\u5df2\u7ecf\u548c\u71d5\u4e03\u89c1\u5706\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e5f\u6ca1\u5173\u7cfb\uff0c\u603b\u6709\u6cd5\u5b50\u60f3\u7684\u3002 \u3000\u3000\u5899\u89d2\u8349\u5f88\u957f\xb7\u5f88\u5bc6\u3002 \u3000\u3000\u201c\u6211\u82cd\u5c06\u8fd9\u4e9b\u91d1\u5f39\u5b50\u85cf\u5728\u8349\u53f6\u91cc\u7edd\u4e0d\u4f1a\u6709\u4eba\u60f3\u5f97\u5230\u7684\u3002 \u3000\u3000\u8c01\u80fd\u60f3\u5f97\u5230\u6709\u4eba\u4f1a\u5c06\u5df2\u5230\u624b\u7684\u91d1\u5b50\u629b\u5728\u4e71\u8349\u91cc\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u53c8\u7b11\u5382\u7acb\u523b\u5c06\u8eab\u4e0a\u7684\u4e24\u4e2a\u4ee5\u888b\u89e3\u4e0b\u6765\u85cf\u5728\u6df1\u8349\u91cc\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u8df3\u4e0a\u4e86\u5899\u3002 \u3000\u3000\u4ed6\u5f88\u4f69\u670d\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u505a\u4e8b\u5b9e\u5728\u5f88\u6709\u51b3\u65ad\u5f88\u6709\u601d\u60f3\xb7\u4e5f\u5f88\u6709\u9b44\u529b\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\u73b0\u5728\u5b9a\u8fd8\u5728\u5899\u4e0b\u4f24\u8111\u9ad3\uff0c\u90a3\u5c31\u8bf4\u4e0d\u8d70\u4f1a\u88ab\u91d1\u5927\u5e05\u8ffd\u4e0a\u4e86\u3002 \u3000\u3000\u8c61\u8fd9\u9ebd\u6837\u6709\u601d\u60f3\u7684\u4eba\u5c06\u6765\u4e0d\u53d1\u8d22\u624d\u662f\u602a\u4e8b\u3002 \u3000\u3000\u71d5\u4e03\u679c\u7136\u5c31\u5728\u5916\u9762\u7b49\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u53e3\u6c14\u8bf4\u5b8c\u4e86\u8fd9\u4ef6\u4e8b\u7684\u7ecf\u8fc7\u5fcd\u4e0d\u4f4f\u7b11\u9053\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u5f88\u4f69\u670d\u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u5c31\u4f69\u670d\u4f60\u8fd8\u5acc\u592a\u65e9\u4e86\u4e9b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u592a\u65e9\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u91d1\u5f39\u5b50\u8fd8\u5728\u522b\u4eba\u5bb6\u91cc\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u90a3\u5bb9\u6613\u2026\u2026\u9178\u6885\u6c64\u7684\u9a6c\u6838\u4e0a\u4e0d\u662f\u6709\u2019\u5708\u957f\u7ef3\u4e8e \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\uff0c\u4ed6\u521a\u4e5f\u770b\u89c1\u5e7f\u3002 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u73b0\u5728\u6211\u518d\u8fdb\u53bb\uff0c\u5c06\u90a3\u4e24\u4e2a\u53e3\u888b\u7cfb\u5728\u7ef3\u5b50\u4e0a\u4f60\u5c31\u5728\u5899\u5916\u9762\u628a\u5b83\u62c9\u51fa\u6765\u2026\u2026\u4f60\u8bf4 \u8fd9\u5bb9\u6613\u4e0d\u5bb9\u6613\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5bb9\u6613\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e2a\u4eba\u53ea\u8981\u6709\u601d\u60f3\u65e0\u8bba\u591a\u56e2\u96be\u7684\u4e8b\uff0c\u90fd\u4f1a\u53d8\u6210\u5f88\u5bb9\u6613\u7684\u3002\u201d\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u7b11\u9053 \u201c\u6240\u4ee5\u4f60\u5411\u90fd\u5f88\u4f69\u670d\u4f60\uff1f\u201d\u90ed\u4eba\u8def\u9053\u201c\u6211\u60f3\u4e0d\u4f69\u670d\u90fd\u4e0d\u884c\u3002\u201d\u6885\u6c5d\u7537\u7684\u9a6c\u5c31\u7cfb\u5728\u524d\u9762\u7684\u6811 \u4e0b\uff0c\u978d\u5ea6\u679c\u7136\u63a5\u5708\u7ef3\u4e8e\u3002\u2019 \u3000\u3000\u90ed\u5927\u8def\u5728\u5899\u5916\u7b49\u4e86\u534a\u5929\u542c\u5230\u5899\u91cc\u9762\u5e76\u6ca1\u6709\u4ec0\u9ebd\u52a8\u859b\u624d\u8df3\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u95e8\u888b\u679c\u7136\u8fd8\u5728\u539f\u5730\u672a\u52a8\u3002 \u3000\u3000\u90ed\u5927\u8def\u5bf9\u81ea\u5df1\u7684\u5224\u65ad\u89c9\u5f97\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u4ed6\u770b\u71d5\u4e03\u5728\u5916\u9762\u5c06\u8fd9\u4e24\u4e2a\u53e3\u888b\u62c9\u4e0a\u5382\u5899\u5934\u518d\u62c9\u4e0b\u53bb\u3002 \u3000\u3000\u7136\u5389\u4ed6\u5c31\u542c\u89c1\u71d5\u4e03\u5728\u5916\u9762\u4f4e\u5524\u9053\u201c\u6211\u5df2\u7ecf\u63a5\u4f4d\u4e86\u5f25\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u677e\u5382\u53e3\u6c14\u5927\u529f\u7ec8\u4e8e\u544a\u6210\xb7\u60f3\u5230\u4ed6\u53bb\u8fd8\u503a\u65f6\uff0c\u90a3\u4e9b\u503a\u672c\u5bf9\u4ed6\u5df3\u7ed3\u7684\u6837\u5b50\u4ed6\u7b80\u76f4 \u8000\u4e0d\u4f4f\u4ece\u5fc3\u91cc\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u7eb5\u8eab\u8dc3\u8f7b\u8f7b\u677e\u677e\u7684\u5c31\u4e0a\u4e86\u5899\u3002 \u3000\u3000\u8fd9\u771f\u662f\u4eba\u9022\u559c\u4e8b\u7cbe\u795e\u723d\u3002 \u3000\u3000\u71d5\u4e03\u5df2\u5230\u4e86\u5df7\u53e3\u7684\u6811\u4e0b\u7ad9\u5728\u90a3\u5339\u9a6c\u65c1\u8fb9\u7b49\u4ed6\u3002 \u3000\u3000\u4ed6\u8d70\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u9178\u6885\u4e5f\u4ece\u524d\u9762\u8d76\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u91d1\u5927\u5e05\u5462\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u62bf\u6687\u7b11\u9053\u201c\u4ed6\u5dee\u70b9\u6ca1\u88ab\u4f60\u6d3b\u6d3b\u6c14\u6b7b\u73b0\u5728\u5df2\u56de\u5c4b\u53bb\u7f16 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u73b0\u5728\u5c31\u6e9c\u51fa\u6765\xb7\u4e0d\u6015\u4ed6\u7591\u5fc3\u3002\uff1f\u201d \u3000\u3000\u6885\u6551\u7537\u9053\u201c\u6ca1\u5173\u7cfb\uff0c\u6211\u5206\u5b8c\u5e10\u4e4b\u5f8c\u518d\u56de\u53bb\xb7\u4e5f\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u5979\u8e22\u7136\u7b11\u53c8\u9053\u201c\u597d\u5728\u4ed6\u7684\u94b1\u5df2\u591a\u5f97\u82b1\u4e0d\u5b8c\uff0c\u6211\u4eec\u5206\u4e00\u70b9\u6765\u82b1\u82b1\u4e5f\u4e0d\u7b97\u7f6a\u8fc7\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u6211\u4eec\u8bf4\u597d\u4e86\xb7\u662f\u53c1\u4e03\u5206\u5e10\u7684\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u70b9\u4e5f\u4e0d\u9519\u3002\u201d\u71d5\u4e03\u9053\u597d\uff0c\u4f60\u5206\u4e03\u6210\u6211\u4eec\u53ea\u53c1\u6210\u201d \u3000\u3000\u6885\u6c5d\u7537\u6014\u4efb\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\xb7\u5931\u58f0\u9053\u201c\u4ec0\u9ebd\uff1f\u4f60\u8981\u5206\u7ed9\u5979\u4e03\u6210\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8c08\u8c08\u9053\u201c\u5979\u82e5\u8981\u5341\u6210\u6211\u5c31\u5168\u7ed9\u5979\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u201d\u2026\u4f60\u662f\u4e0d\u662f\u4e2d\u4e86\u6691\uff1f\u662f\u4e0d\u662f\u6709\u70b9\u5934\u6655\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53d1\u6655\u7684\u662f\u4f60\uff0c\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5c06\u90a3\u4e24\u4e2a\u53e3\u888b\u5f80\u90ed\u5927\u8def\u624b\u91cc\u4e00\u4e22\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e00\u4e2a\u6ca1\u7559\u5fc3\u6ca1\u63a5\u4efb\u4ee5\u888b\u91cc\u7684\u5f39\u4e25\u5c31\u6d12\u4e86\u5730\u3002 \u3000\u3000\u4e0d\u662f\u91d1\u5f39\u5b50\u662f\u94c1\u5f39\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u9897\u9897\u9ed1\u52a9\u52e4\u7684\u94c1\u5f39\u5b50\u5728\u5730\u6b62\u4e71\u6eda\u8fde\u773c\u73e0\u4e8e\u90fd\u597d\u8c61\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u71d5\u4e03\u6de1\u8c08\u9053\u201c\u7a76\u7adf\u662f\u8c01\u6655\u4f60\u603b\u8be5\u660e\u767d\u4e86\u5427\u3002\u201d \u3000\u3000\u90ed\u6b20\u8def\u5403\u5403\u9053\u201c\u53ef\u662f\u6211\u2500\u4e00\u3002\u6211\u56fd\u624d\u660e\u660e\u770b\u5230\u662f\u91d1\u5f39\u5b50\u66f2\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u770b\u6765\u8fd9\u4eba\u4e0d\u4f46\u5934\u6655\uff0c\u800c\u4e14\u773c\u82b1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u664c\u63d0\u8d77\u53e3\u888b\u4e00\u6296\u5ffd\u7136\u770b\u5230\u9897\u91d1\u5149\u95ea\u95ea\u7684\u5f39\u5b50\u6eda\u51fa\u6765\u3002 \u3000\u3000\u53ea\u6709\u9897\u771f\u7684\u662f\u91d1\u5f39\u4e8e\u3002 \u3000\u3000\u6885\u6c5d\u7537\u6361\u8d77\u6765\u770b\u4e86\u770b\u5ffd\u7136\u9053\u201c\u4f60\u4eec\u770b\uff0c\u8fd9\u4e0a\u9762\u8fd8\u523b\u5b57\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u523b\u7684\u662f\u4ec0\u9ebd\u5b57\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u770b\u8fd9\u9897\u91d1\u5f39\u5b50\u8868\u60c5\u597d\u50cf\u5f88\u5947\u602a\u8fc7\u4e86\u5f88\u4e45\xb7\u624d\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u4f60\u8fd8\u662f\u81ea\u5df1\u6765 \u5427\u3002\u201d \u3000\u3000\u91d1\u5f39\u5b50\u53ea\u8346\u4e00\u884c\u5b87\u201c\u4eba\u82e5\u662f\u592a\u8d2a\u5fc3\uff0c\u5230\u624b\u7684\u9ec4\u91d1\u4e5f\u4f1a\u53d8\u6210\u80a1\u94c1\u3002\u201d \u3000\u3000\u8d2a\u5403\u7684\u732a\u603b\u662f\u5148\u6328\u5bb0\u7684\u3002 \u3000\u3000\u60f3\u5230\u6885\u6c5d\u7537\u7684\u8fd9\u53e5\u8bdd\u518d\u770b\u770b\u91d1\u5f39\u5b50\u4e0a\u523b\u7684\u8fd9\u53e5\u8bdd\uff0c\u90ed\u5927\u8def\u8138\u4e0a\u7684\u8868\u60c5\u5c31\u597d\u50cf\u521a\u541e\u4e0b\u5382\u53c1 \u65a4\u53d1\u4e86\u9709\u7684\u9ec4\u83b2\u3002 \u3000\u3000\u71d5\u4e03\u770b\u770b\u4ed6\xb7\u518d\u770b\u770b\u6885\u6c5d\u7537\u82e6\u7b11\u9053\u201c\u91d1\u5927\u5e05\u60f3\u5fc5\u65e9\u5df2\u77e5\u9053\u6211\u4eec\u7684\u6765\u610f\u4e86\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5582\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3d\u4e14\u4ed6\u4e5f\u5df2\u770b\u51fa\uff0c\u4f60\u662f\u5e2e\u6211\u5c04\u53bb\u9a97\u4ed6\u7684\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u53ef\u662f\u4ed6\u5374\u5728\u6545\u610f\u88c5\u7cca\u6d82\u56e0\u4e3a\u2026\u3002\u2606 \u3000\u3000\u6885\u6c5d\u7537\u63a5\u9053\u201c\u56e0\u4e3a\u4ed6\u672c\u6765\u5c31\u5f88\u723d\u5f88\u5927\u8def\uff0c\u5c31\u7b97\u660e\u77e5\u9053\u6211\u8ba2\u60f3\u9a97\u4ed6\u70b9\u94b1\u7528\u4ed6\u4e5f\u4e0d\u5728\u4e4e\u53ea\u53ef \u60dc\u2026\u201d\u2019 \u3000\u3000\u5979\u770b\u4e86\u90ed\u5927\u8def\u773c\u5c31\u6ca1\u6709\u518d\u8bf4\u5382\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u66ff\u5979\u63a5\u4e86\u4e0b\u53bb\u9053\u201c\u53ea\u53ef\u60dc\u6211\u592a\u8d2a\u5fc3\xb7\u5c31\u597d\u50cf\u6068\u4e0d\u5f97\u5c06\u4ed6\u6240\u6709\u7684\u91d1\u5f39\u5b50\u5168\u90fd\u5f04\u8d70 \u624d\u8fc7\u9690\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4f46\u90a3\u4e5f\u4e0d\u80fd\u602a\u4f60\u3002\u201d\u90ed\u4eba\u8def\u9053\u201c\u6c34\u602a\u6211\u602a\u8c01\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u4eba\u90fd\u6709\u5f31\u70b9\uff0c\u65e0\u8bba\u8c01\u90fd\u96be\u514d\u6709\u8d2a\u5fc3\u7684\u65f6\u5019\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f55\u51b5\u4f60\u8d2a\u5fc3\u4e5f\u5e76\u4e0d\u662f \u4e3a\u4e86\u4f60\u81ea\u5df1\uff0c\u82e5\u4e0d\u662f\u4e3a\u670b\u53cb\u4f60\u600e\u9ebd\u4f1a\u6b20\u90a3\u8bb8\u591a\u503a\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u5176\u5b9e\u4f60\u4eec\u6839\u672c\u7528\u4e0d\u5b89\u6170\u6211\u6211\u5fc3\u91cc\u6839\u672c\u4e0d\u96be\u53d7\u201c \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u8fd9\u4e9b\u9ec4\u91d1\u867d\u53d8\u6210\u4e86\u5e9f\u94c1\xb7\u4f46\u6211\u8fd9\u6b21\u6765\u4e5f\u5e76\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u6536\u83b7\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u52c9\u5f3a\u7b11\u5382\u7b11\u9053\u201c\u4e0d\u9519\xb7\u4f60\u603b\u7b97\u8fd8\u5269\u4e0b\u9897\u91d1\u5f39\u5b50\u3002\u201d\u7ff1\u5927\u8def\u9053\u201c\u6211\u6536\u83b7\u7684\u5e76\u4e0d\u662f\u8fd9 \u91d1\u5f39\u5b50\u3002\u201d\u6885\u6c5d\u7537\u9053\u201c\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u4e2a\u5f88\u597d\u7684\u6559\u8bad\u3002\u201d \u3000\u3000\u4ed6\u770b\u5f39\u5b50\u4e0a\u523b\u7684\u90a3\u53e5\u8bdd\u6162\u6162\u7684\u63a5\u9053\u201c\u5bf9\u6211\u6765\u8bf4\xb7\u8fd9\u6559\u529b\u4e5f\u8bb8\u6bd4\u4e16\u4e0a\u6240\u6709\u7684\u9ec4\u91d1\u90e1\u6709\u4ef7\u503c \u5f97\u591a\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u770b\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u5a7f\u7136\u7b11\u9053\u201c\u73b0\u5728\u6211\u5201\u660e\u767d\u4e3a\u4ec0\u9ebd\u6709\u4eba\u90a3\u6837\u559c\u6b22\u4f60\u4e86\uff0c\u56e0\u4e3a\u4f60\u7684 \u786e\u662f\u4e2a\u5f88\u53ef\u7231\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u73b0\u5728\u624d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u5631\u3002\u201d\u7ff1\u5927\u8def\u7b11\u9053\u201c\u6211\u5374\u65e9\u5c31\u77e5\u9053\u4e86\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u53ea\u53ef\u60dc\u53e6\u5916\u6709\u4ef6\u4e8b\u4f60 \u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u54ea\u4ef6\u4e8b\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5728\u90a3\u4e9b\u9988\u4e3b\u773c\u775b\u91cc\u4f60\u552f\u53ef\u7231\u7684\u8d2d\u5019\xb7\u5c31\u662f\u8fd8\u94b1\u7684\u65f6\u5019\u82e5\u6ca1\u94b1\u8fd8\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec \u4f1a\u600e\u9ebd\u6837\u5bf9\u4ed8\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u7b11\u5bb9\u65e9\u5df3\u4e0d\u89c1\u4e86\xb7\u82e6\u8138\u6447\u5934\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u65e0\u8bba\u591a\u597d\u7684\u6559\u8bad\u90fd\u4e0d\u80fd\u62ff\u53bb\u8fd8\u503a\u7684\u3002 \u3000\u3000\u6094\u5986\u7537\u7728\u4e86\u7728\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6b20\u4e86\u4eba\u5bb6\u5f88\u591a\u7684\u503a\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u9053\u201c\u6b20\u4e86\u591a\u5c11\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8f7b\u53f9\u9053\u201c\u4e5f\u6ca1\u6709\u591a\u5c11\u53ea\u4e0d\u8fc7\u4e07\u628a\u4e24\u94f6\u4ea7\u3002\u201d \u3000\u3000\u6885\u6c5d\u7537\u597d\u50cf\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\xb7\u7ad9\u5728\u90a3\u91cc\u6014\u4e86\u534a\u5929\u5ffd\u7136\u9053\u201c\u91d1\u5927\u53d4\u4e00\u5b9a\u8fd8\u5728\u7b49\u8bad\u6211\uff0c\u6211\u4e0d\u80fd \u518d\u803d\u8bef\u5382\uff0c\u56de\u5934\u89c1\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\u5979\u7684\u4eba\u5df2\u8dc3\u4e0a\u4e86\u9a6c\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\u6253\u9a6c\u800c\u53bb\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u54fa\u54fa\u9053\u201c\u4e3a\u4ec0\u9ebd\u522b\u4eba\u542c\u5230\u4f60\u6b20\u4e86\u503a\xb7\u5c31\u7acb\u523b \u4f1a\u843d\u8352\u800c\u9003\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6c89\u601d\xb7\u7f13\u7f13\u9053\u201c\u56e0\u4e3a\u5979\u4e5f\u60f3\u7ed9\u4f60\u4e2a\u5f88\u597d\u7684\u6559\u8bad\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u6559\u8bad\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u2606\u4e2a\u4eba\u6148\u6e29\u8ba1\u5f00\u5fc3\u5fc3\u7684\u6d3b\xb7\u6700\u597d\u5c31\u4e0d\u8981\u6b20\u503a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6162\u9992\u7684\u70b9\u4e86\u70b9\u5934\u9053\u201c\u4e00\u4e2a\u4eba\u82e5\u60f3\u670b\u53cb\u559c\u6b22\u4f60\uff0c\u6700\u597d\u4e5f\u4e0d\u8981\u6b20\u503a\u3002\u201d \u3000\u3000\u8fd9\u7684\u786e\u662f\u4e2a\u5f88\u597d\u7684\u6559\u8bad\u503c\u5f97\u6bcf\u4e2a\u4eba\u90fd\u4e2d\u7262\u8bb0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u5047\u4f60\u82e5\u5df2\u4e3a\u670b\u53cb\u6b20\u5382\u503a\u5462\uff1f \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u6211\u770b\u4f60\u4e0d\u5982\u8fd8\u662f\u5148\u907f\u907f\u98ce\u5934\u6db8\u5230\u522b\u7684\u5730\u65b9\u53bb\u8eb2\u513f\u5929\u518d\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u773c\u9053\u201c\u4f60\u53eb\u6211\u6e9c\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u7b54\u5e94\u8fc7\u522b\u4eba\u4e24\u5929\u4e4b\u5185\u628a\u503a\u90fd\u8fd8\u6e05\u7684\xb7\u600e\u9ebd\u80fd\u7a7a\u624b\u56de\u53bb\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u4ee5\u4e3a \u6211\u4f1a\u505a\u8fd9\u79cd\u4e22\u4eba\u7684\u4e8b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ef\u662f\u4f60\u5374\u6b20\u4e86\u503a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6b20\u503a\u662f\u4e00\u56de\u4e8b\u6e9c\u53c8\u662f\u53e6\u5916\u56de\u4e8b\u6b20\u4e86\u503a\u603b\u53ef\u4ee5\u8fd8\u7684\uff0c\u4f46\u82e5\u6b20\u4e86\u503a\u4e4b\u5f8c\u6e9c\u90a3\u5c31\u4e0d \u662f\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u770b\u4ed6\u64ac\u7136\u7b11\u9053\u201c\u4f60\u7684\u786e\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u800c\u4e14\u662f\u4e2a\u5f88\u53ef\u7231\u7684\u4eba\xb7\u53ea\u4e0d\u8fc7\u7a77\u70b9\u800c\u5df2\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u539f\u5219\u95ee\u9898\u3002 \u3000\u3000\u4e2a\u4eba\u82e5\u800d\u5b88\u81ea\u5df1\u7684\u539f\u5219\uff0c\u6709\u65f6\u5374\u4e5f\u5e76\u4e0d\u592a\u5bb9\u6613\u7684\u3002\u4f46\u4f60\u82e5\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u767e\u90fd\u80fd\u5b88\u5f97\u4f4f \u81ea\u5df1\u7684\u539f\u5219\xb7\u90a3\u9ebd\u4f60\u5c31\u4f1a\u53d1\u73b0\u4e0d\u4f46\u6d3b\u65f6\u6bd4\u8f83\u4e0d\u5b89\u5fc3\uff0c\u5c31\u7b97\u6b7b\u4e86\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u95ed\u4e0d\u4e0a\u773c\u775b\u3002 \u3000\u3000\u4e2a\u4eba\u53ea\u8981\u80fd\u5b89\u5b89\u5fc3\u5fc3\u7684\u6d3b\uff0c\u5b89\u5b89\u5fc3\u5fc3\u7684\u6b7b\u7a77\u4e00\u70b9\u53c8\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f \u3000\u3000\u5f53\u7136\u5047\u5982\u80fd\u9614\u4e00\u70b9\xb7\u4e5f\u4e0d\u662f\u4ec0\u9ebd\u574f\u4e8b\u3002 \u3000\u3000\u201c\u4f60\u662f\u7a77\u662f\u5bcc\uff1f\u201d\u8fd9\u95ee\u9898\u5e76\u4e0d\u91cd\u8981\u3002 \u3000\u3000\u91cd\u8981\u7684\u95ee\u9898\u662f\u201c\u4f60\u7a76\u7adf\u662f\u4e0d\u662f\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u5bcc\u8d35\u5c71\u5e84\u6c34\u8fdc\u662f\u8001\u6837\u5b50\u65e0\u8bba\u4f60\u600e\u9ebd\u770b\u90fd\u770b\u4e0d\u51fa\u6709\u70b9\u5bcc\u8d35\u7684\u6c14\u8c61\u6765\uff0c \u3000\u3000\u4f46\u4eca\u5929\u65e9\u4e0a\u5374\u597d\u50cf\u6709\u70b9\u4e0d\u540c\u3002 \u3000\u3000\u51b7\u51b7\u843d\u843d\u7684\u5bcc\u8d35\u5c71\u5e84\u5927\u95e8\u5916\u4eca\u5929\u5c45\u7136\u505c\u51e0\u5339\u9aa1\u9a6c\u3002 \u3000\u3000\u8fd8\u6709\u300f\u4e2a\u7a7f\u5f88\u5149\u9c9c\u7684\u5c0f\u9890\u6b63\u5728\u4efb\u95e8\u5916\u7684\u6811\u535c\u4e58\u51c9\u3002 \u3000\u3000\u71d5\u4e03\u8fdc\u8fdc\u5c31\u770b\u5230\u4e86\u4e0d\u7531\u5f97\u5438\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u76fe\u6765\u4f60\u7684\u503a\u4e3b\u4eec\u5df2\u7ecf\u5728\u91cc\u9762\u7b49\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053 \u201c\u54bd\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u51c6\u5907\u600e\u9ebd\u6253\u53d1\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u6709\u79cd\u6cd5\u5b50\u3002\u201d\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u6837\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8bf4\u8001\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u521d\u5347\u7684\u9633\u5149\u7167\u5728\u4ed6\u8138\u535c\uff0c\u4ed6\u7684\u8138\u660e\u6717\u3001\u5766\u8bda\u4eff\u4f5b\u4e5f\u5728\u53d1\u5149\u3002 \u3000\u3000\u4ed6\u63a5\u9053\u201c\u6211\u51c6\u5907\u8001\u8005\u5b9e\u5b9e\u7684\u544a\u8bc9\u4ed6\u4eec\u73b0\u5728\u867d\u7136\u6ca1\u94b1\u8fd8\u4f46\u4ee5\u5f8c\u5b9a\u4f1a\u60f3\u6cd5\u5b50\u8fd8\u4ed6\u4eec\u7684\u2026\u2026\u8fd9 \u6cd5\u5b50\u4e5f\u8bb8\u4e0d\u597d\u53ef\u662f\u6211\u5374\u4e0d\u60f3\u4e0d\u51fa\u522b\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6b7b\u4e03\u770b\u4ed6\xb7\u5fae\u7b11\u9053\u201c\u4f60\u5f53\u7136\u60f3\u4e0d\u51fa\u56e0\u4e3a\u8fd9\u4e2d\u5c31\u662f\u6700\u597d\u7684\u6cd5\u4e88\u4ee5\u4e0a\u7edd\u6ca1\u884d\u66f4\u597d\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u503a\u4e3b\u516c\u6709\u516d\u4e2a\u3002\u516d\u4e2a\u503a\u4e3b\u90fd\u7ad9\u5728\u9662\u5343\u91cc\u7b49\u3002 \u3000\u3000\u90ed\u5927\u8def\u2500\u8fdb\u53bb\u5c31\u5927\u58f0\u9053\u201c\u5404\u4f4d\u62b1\u6b49\u5f97\u72e0\u6211\u73b0\u5728\u867d\u7136\u6ca1\u6709\u94b1\u8fd8\u7ed9\u4ed6\u4eec\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5df2\u6709\u4eba\u6253\u65ad\u5382\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u4e2a\u6027\u94b1\u7684\u8001\u677f\u62a2\u9053\u201c\u90ed\u5927\u7237\u96be\u9053\u4ee5\u4e3a\u6211\u4eec\u662f\u6765\u8981\u503a\u7684\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u5382\u6014\u9053\u201c\u4f60\u4eec\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u94b1\u8001\u677f\u7b11\u9053\u201c\u6211\u4eec\u751f\u4f2f\u8fd9\u91cc\u7684\u4e1c\u897f\u4e0d\u591f\u7528\xb7\u6240\u4ee5\u7279\u5730\u6c72\u4e3a\u5927\u7237\u9001\u6765\u7684\u3002\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u90ed\u5927\u8def\u5450\u5450\u5730\u9053\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u6b20\u4e86\u4f60\u94b3\u7684\u5e10\u5462\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u59d3\u5f20\u7684\u8001\u677f\u4e5f\u62a2\u8bf4\u9053\u201c\u5e10\u65e9\u5df2\u6709\u4eba\u8fd8\u6e05\u4e86\u3002\u201d \u3000\u3000\u94b1\u8001\u677f\u966a\u7b11\u9053\u201c\u90a3\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u6570\u76ee\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u4e2d\u664c\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u90a3\u4e9b\u5e10\u7a76\u7adf\u662f\u8c01\u8fd8\u7684\uff1f\u201d \u3000\u3000\u5f20\u8001\u677f\u7b11\u9053\u201c\u8001\u5b9e\u8bf4\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u8c01\u8fd8\u7684\u3002\u201d\u90ed\u5927\u8def\u66f4\u89c9\u602a\u95ee\u9053\u201c\u600e\u9ebd\u4f1a\u8fde\u4f60\u4eec \u4e5f\u4e0d\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u94b1\u8001\u677f\u9053\u201c\u4eca\u5929\u65e9\u535c\u6211\u8d77\u5e8a\u5c31\u770b\u5230\u5916\u9762\u7684\u684c\u4e0a\u653e\u597d\u51e0\u5806\u94f6\u4e8e\u2026\u201c\u201c \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u597d\u513f\u5806\uff1f\u94f6\u5b50\u600e\u9ebd\u4f1a\u662f\u8bba\u5806\u7684\uff1f\u201d \u3000\u3000\u94b1\u8001\u677f\u9053\u201c\u56e0\u4e3a\u90a3\u4e9b\u94f6\u5c01\u90fd\u4e0d\u6837\xb7\u6709\u7684\u662f\u6d4e\u5357\u5c01\u4e5f\u6709\u7684\u662f\u4eac\u57ce\u5c01\u4e00\u5806\u96c4\u7684\u90fd\u5206\u5f00\u4e86\uff0c\u4f46 \u4e0b\u9762\u5374\u90fd\u538b\u5f20\u7eb8\u6761\u8bf4\u660e\u662f\u90ed\u5927\u7237\u8fd8\u5e10\u7684\u3002\u201d \u3000\u3000\u5f20\u8001\u677f\u9053\u201c\u90a3\u60f3\u5fc5\u662f\u90ed\u5927\u7237\u7684\u670b\u53cb\uff0c\u77e5\u9053\u90ed\u5927\u7237\u6700\u8fd1\u624b\u5934\u4e0d\u4fbf\u6240\u4ee5\u7279\u5730\u5e26\u4e86\u94f6\u5b50\u6765\u53c8\u6015 \u90ed\u5927\u7237\u4e0d\u80af\u6536\u6240\u4ee5\u7279\u5730\u9001\u5230\u5c0f\u53f7\u53bb\u3002\u201d \u3000\u3000\u94b1\u8001\u677f\u8d54\u7b11\u9053\u201c\u90ed\u5927\u7237\u7684\u670b\u53cb\uff0c\u60f3\u5fc5\u90fd\u662f\u591f\u4e49\u6c14\u7684\u6c5f\u6e56\u597d\u6c49\u6211\u4eec\u867d\u7136\u662f\u5c0f\u672c\u751f\u610f\u7684\u53ef\u4e5f \u4e0d\u662f\u4ec0\u9ebd\u52bf\u5229\u5c0f\u4eba\u3002\u201d \u3000\u3000\u5f20\u8001\u677f\u4e5f\u968f\u7b11\u9053\u201c\u6240\u4ee5\u6211\u4eec\u65e9\u5c31\u83e0\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u8981\u65e9\u8d76\u6765\u9047\u90a3\u4e9b\u534a\u591c\u91cc\u80fd\u5728\u4ed6\u6253\u5bb6\u51fa\u5165\u81ea\u5982\u7684\u6c5f\u6e56\u597d\u6c49\u4ed6\u4eec\u600e\u9ebd\u6562\u4e0d\u5df4\u7ed3\uff1f \u3000\u3000\u4f55\u51b5\u8fd8\u6709\u5927\u628a\u7684\u94f6\u5b50\u53ef\u8d5a\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u5374\u8bc1\u4f4f\u4e86\u7b80\u76f4\u5c31\u8c61\u662f\u4e08\u7236\u7684\u91d1\u521a\u6478\u4e0d\u8111\u3002 \u3000\u3000\u71d5\u4e03\u602a\u7136\u9053\u201c\u4f60\u8d38\u6536\u4e0b\u7684\u94f6\u5b50\u5171\u6709\u51e0\u5806\uff1f\u201d\u94b1\u8001\u677f\u9053\u201c\u5171\u6709\u5361\u5806\u4e0d\u4f46\u8fd8\u5e10\u8db3\u8db3\u6709\u4f59\u8fd8\u6709 \u5269\u3014\u7684\u3002\u201d \u3000\u3000\u5f20\u8001\u677f\u9053\u201c\u6240\u4ee5\u8fd9\u4e24\u4e2a\u6708\u90ed\u5927\u7237\u65e0\u8bba\u8981\u4ec0\u9ebd\u90fd\u53ea\u7ba1\u5230\u5c0f\u53f7\u7c73 \u3000\u3000\u94b1\u8001\u677f\u7b11\u9053\u201c\u73b0\u5728\u6211\u5546\u4e5f\u4e0d\u6562\u518d\u6253\u6270\u5c31\u6b64\u544a\u8f9e\u5382\u3002\u201d \u3000\u3000\u65bc\u662f\u4e2a\u4e2a\u5c31\u6253\u8eac\u4f5c\u63d6\u3002\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9000\u5230\u5927\u95e8\u5916\xb7\u8fd8\u5728\u611f\u53f9\uff0c\u7a83\u7a83\u79c1\u8bae\u201c\u60f3\u4e0d\u5230\u90ed\u5927\u7237\u5c45\u7136\u6709\u8fd9\u9ebd\u591a\u597d\u670b\u53cb\u4e2d \u3000\u3000\u201c\u90a3\u5f53\u7136\u662f\u56e0\u4e3a\u90ed\u5927\u7237\u5e73\u65f6\u505a\u4eba\u591f\u4e49\u6c14\u3002\u201d \u3000\u3000\u201c\u4ea4\u670b\u53cb\u672c\u6765\u5c31\u662f\u4e49\u6c14\u6362\u4e49\u6c14\uff0c\u8c61\u90ed\u5927\u7237\u8fd9\u79cd\u670b\u53cb\u6211\u4e5f\u613f\u610f\u4ea4\u7684\u3002\u201d \u3000\u3000\u7b49\u5230\u5165\u5168\u90fd\u8d70\u5149\u4e86\xb7\u90ed\u5927\u8def\u624d\u5410\u51fa\u53e3\u6c14\u9053\u201c\u6211\u662f\u4e0d\u662f\u771f\u7684\u5f88\u591f\u4e49\u6c14\u201d \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\xb7\u5fbd\u7b11\u9053\u201c\u597d\u50cf\u662f\u7684\u5426\u5219\u600e\u9ebd\u4f1a\u6709\u4eba\u66ff\u4f60\u6765\u8fd8\u503a \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u539f\u6765\u5e76\u4e0d\u662f\u6bcf\u4e2a\u4eba\u542c\u8bf4\u4f60\u6b20\u4e86\u503a\xb7\u90fd\u4f1a\u843d\u8352\u800c\u9003\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7684\u786e\u4e0d\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u53ef\u662f\u6211\u8fd9\u4e9b\u591f\u4e49\u6c14\u7684\u670b\u53cb\u7a76\u7adf\u662f\u4ece\u54ea\u91cc\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u60f3\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6253\u7834\u6211\u7684\u5934\u4e5f\u60f3\u4e0d\u51fa\u3002\u201d\u71d5\u4e03\u9053\u201c\u90a3\u4f60\u5c31\u4e0d\u5fc5\u60f3\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u90a3\u4e9b\u4eba\u8bf4\u7684\u8bdd\u90fd\u5f88\u6709\u9053\u7406\u4ea4\u670b\u53cb\u672c\u5c31\u662f\u4e49\u6c14\u6362\u4e49\u6c14\u4ed6\u4eca\u5929\u6765\u66ff\u4f60\u8fd8\u503a\uff0c\u81ea \u7136\u56e0\u4e3a\u4f60\u4ee5\u524d\u4e5f\u505a\u8fc7\u5bf9\u4ed6\u4eec\u591f\u4e49\u6c14\u7684 \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u5047\u6211\u5374\u8fd8\u662f\u60f3\u4e0d\u51fa\u662f\u6dee\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6709\u5f88\u591a\u4eba\u90fd\u6709\u53ef\u80fd\uff0c\u8b6c\u5982\u8bf4\uff0c\u7ea2\u8682\u8681\u3001\u6797\u592b\u4eba\u3001\u6885\u6c5d\u7537\u8fd8\u6709\u90a3\u4e9b\u9a97\u8fc7\u4f60\u7684\u5f3a\u76d7 \u4ed6\u4eec\u82e5\u77e5\u4f60\u88ab\u4eba\u903c\u503a\u903c\u5f97\u5a36\u8df3\u6cb3\u90fd\u53ef\u80fd\u5077\u50a8\u6765\u66ff\u4f60\u8fd8\u503a\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u63a5\u9053\u201c\u5c31\u8fde\u91d1\u5927\u5e05\u548c\u5708\u6885\u6ca5\u90fd\u6709\u53ef\u80fd\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u71d5\u4e03\u5a9a\u7136\u9053\u201c\u56e0\u4e3a\u4f60\u4e2d\u4f46\u662f\u4e2a\u5f88\u597d\u7684\u670b\u53cb\uff0c\u800c\u4e14\u771f\u662f\u4e2a\u5f88\u53ef\u7231\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u7b11\u4e86\uff0c\u54fa\u54fa\u9053\u201c\u4e5f\u8bb8\u771f\u7684\u5c31\u662f\u4ed6\u4eec\xb7\u60f3\u4e0d\u5230\u4ed6\u4eec\u8fd8\u8bb0\u5f97 \u3000\u3000\u4ed6\u7684\u7b11\u5145\u6ee1\u5382\u6b22\u4e50\u548c\u611f\u6fc0\u3002 \u3000\u3000\u4ed6\u611f\u6fc0\u7684\u5012\u4e0d\u662f\u4ed6\u4eec\u4e3a\u4ed6\u8fd8\u5382\u503a\u4ed6\u611f\u6fc0\u7684\u662f\u4ed6\u8ba2\u7684\u53cb\u60c5\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u53ea\u8981\u6709\u53cb\u60c5\u5b58\u5728\u5c31\u6c38\u8fdc\u6709\u5149\u660e\u3002 \u3000\u3000\u4f60\u770b\u73b0\u5728\u9633\u5149\u6b63\u7167\u904d\u5927\u5730\xb7\u5230\u5904\u90fd\u95ea\u8000\u91d1\u5149\u5c31\u597d\u50cf\u4e0a\u5929\u7279\u5730\u4e3a\u8fd9\u4e16\u4e0a\u61c2\u5f97\u73cd\u501f\u53cb\u60c5\u7684 \u4eba\uff0c\u64a4\u4e0b\u4e86\u7247\u9ec4\u91d1\u3002 \u3000\u3000\u8fd9\u672c\u6765\u5c31\u662f\u4e2a\u9ec4\u91d1\u4e16\u754c\u53ea\u770b\u4f60\u61c2\u4e0d\u61c2\u5f97\u5982\u4f55\u53bb\u5206\u8fa8\u4ec0\u9ebd\u624d\u662f\u771f\u6b63\u7684\u9ec4\u91d1\uff1f\u4ec0\u9ebd\u624d\u662f\u771f\u6b63 \u503c\u5f97\u73cd\u501f\u7684 \u7b2c\u5341\u56db\u7ae0\u3000\u91d1\u5927\u5e05\u7684\u95ee\u9898>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u91d1\u5927\u5e05\u7684\u95ee\u9898 \u3000\u3000\u6709\u79cd\u4eba\u597d\u50cf\u547d\u4e2d\u6ce8\u5b9a\u5c31\u662f\u8981\u6bd4\u522b\u4eba\u6d3b\u5f97\u5f00\u5fc3\u5c31\u7b97\u662f\u5929\u5927\u7684\u95ee\u9898\u4ed6\u4e5f\u968f\u65f6\u90fd\u53ef\u4ee5\u653e\u5230\u8fb9 \u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u662f\u8c01\u66ff\u4ed6\u8fd8\u7684\u5e10\uff1f \u3000\u3000\u4e3a\u4ec0\u9ebd\u8981\u66ff\u4ed6\u8fd8\u8d26\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u5728\u4ed6\u770b\u6765\uff0c\u65e9\u5df3\u4e0d\u662f\u95ee\u9898\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u8eba\u4e0a\u5e8a\u7acb\u523b\u5c31\u7761\uff0c\u7761\u5c31\u7761\u5230\u4e0b\u5348\u76f4\u5230\u738b\u52a8\u5230\u4ed6\u5c4b\u91cc\u6765\u7684\u65f6\u5019\u4ed6\u624d\u9192\u3002 \u3000\u3000\u4e0a\u52a8\u7684\u884c\u52a8\u8fd8\u4e0d\u592a\u65b9\u4fbf\u6240\u4ee5\u8d77\u8fdb\u6765\u5c31\u627e\u4e86\u4e2a\u6700\u8212\u670d\u7684\u5730\u65b9\u534e\u4e0b\u5c31\u7b97\u4ed6\u884c\u52a8\u65b9\u4fbf\u7684\u65f6\u5019\u65e0 \u8bba\u8d70\u5230\u4ec0\u9ebd\u5730\u65b9\u4e5f\u90fd\u7acb\u523b\u4f1a\u627e\u4e2a\u6700\u8212\u670d\u7684\u5730\u65b9\u5750\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u7684\u5c4b\u5b50\u91cc\u6015\u90fd\u5f88\u5c11\u6709\u6bd4\u5e8a\u66f4\u52a0\u8212\u670d\u7684\u5730\u65b9\u3002 \u3000\u3000\u6240\u4ee5\u738b\u52a8\u5c31\u53eb\u90ed\u5927\u8def\u628a\u811a\u7f29\u8d77\u6765\u5ce1\xb7\u659c\u501a\u5728\u4ed6\u7684\u811a\u8ddf\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c31\u628a\u4e2a\u6795\u5934\u5de5\u5382\u8fc7\u53bb\u8ba9\u4ed6\u57ab\u80cc\u7136\u5f8c\u63c9\u773c\u775b\u9053\u201c\u73b0\u5728\u662f\u4ec0\u9ebd\u65f6\u5019\u4e86\uff1f\u201d \u3000\u3000\u738b\u52a8\u201c\u8fd8\u65e9\u8ddd\u79bb\u5403\u665a\u996d\u7684\u65f6\u5019\xb7\u8fd8\u6709\u534a\u4e2a\u591a\u65f6\u80f6\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u8d3f\u54fa\u9053\u201c\u5176\u5b9e\u4f60\u5e94 \u8be5\u8ba9\u6211\u518d\u591a\u964b\u534a\u4e2a\u65f6\u8fb0\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u4e5f\u56db\u5382\u6c14\uff0c\u9053\u201c\u6211\u53ea\u5947\u602a\uff0c\u4f60\u600e\u9ebd\u80fd\u7761\u5f97\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u597d\u50cf\u66f4\u5947\u602a\u5f20\u5927\u4e86\u773c\u775b\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u7761\u4e0d\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u82e5\u662f\u65e8\u52a8\u8111\u9ad3\u60f3\u60f3\u4e5f\u8bb8\u5c31\u4f1a\u7761\u4e0d\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u4ec0\u9ebd\u597d\u60f3\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u901a\u201c\u6ca1\u6709\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u6447\u6447\u5934\u9053\u201c\u597d\u50cf\u6ca1\u6709\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u5df2\u77e5\u9053\u662f\u8c01\u66ff\u6912\u8fd8\u7684\u5e10\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u7ba1\u662f\u63a8\u66ff\u6211\u8fd8\u7684\u5e10\u53cd\u6b63\u5e10\u5df2\u7ecf\u8fd8\u6e05\u4e86\u4ed6\u6253\u65e2\u7136\u4e0d\u613f\u610f\u6cc4\u9732\u81ea\u5df1\u7684\u8eab\u4efd\xb7\u6211 \u8fd8\u6709\u4ec0\u9ebd\u597d\u60f3\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u80fd\u4e0d\u80fd\u7a0d\u5fae\u52a8\u52a8\u8111\u7b4b\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u4e86\u9053\u201c\u80fd\uff0c\u5f53\u7136\u80fd\u3002\u201d \u3000\u3000\u4ed6\u679c\u7136\u60f3\u4e86\u60f3\xb7\u624d\u63a5\u9053\u201c\u6700\u53ef\u80fd\u66ff\u6211\u8fd8\u5f20\u7684\u4eba\u5c31\u662f\u6797\u592b\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4eec\u90a3\u6b21\u9047\u89c1\u6797\u592b\u4eba\u7684\u7ecf\u8fc7\u5f8c\u6765\u5df2\u544a\u8bc9\u8fc7\u738b\u52a8\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u6797\u592b\u4eba\u5c31\u662f\u4f60\u535c\u6b21\u8bf4\u7684\u536b\u592b\u4eba\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u70b9\u70b9\u5934\u9053\u201c\u5979\u65e2\u7136\u6797\u592a\u5e73\u5728\u8fd9\u91cc\u5f53\u7136\u4f1a\u6e90\u4eba\u968f\u65f6\u6765\u5de5\u542c\u6211\u4eec\u6b20\u4e86\u503a\u5f53\u7136\u4f1a\u670d\u4eba\u6765 \u8fd8\u7684\u3002\u201d \u3000\u3000\u4ed6\u63a5\u53c8\u9053\u201c\u53ef\u662f\u5979\u4e0d\u613f\u6797\u592a\u5e73\u77e5\u9053\u5979\u5df4\u627e\u5230\u8fd9\u5730\u65b9\u6240\u4ee5\u624d\u7792\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u5e72\u52a8\u9053\u8239\u5408\u7406\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u5f53\u7136\u5408\u7406\uff0c\u6211\u5c31\u7b97\u61d2\u5f97\u52a8\u8111\u7b4b\u4f46\u8111\u7b4b\u5e76\u4e0d\u6bd4\u522b\u4eba\u5dee\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u9664\u4e86\u6797\u592b\u4eba\u7b2c\u4e8c\u4e2a\u53ef\u80fd\u66ff\u4f60\u8fd8\u5e10\u7684\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u516b\u6210\u662f\u9178\u6885\u6c64\u3002\u201d\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\u662f\u5979\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u770b\u89c1\u5979\u542c\u6211\u4f55\u6b20\u4e86\u5e10\uff0c\u53cb\u523b\u5c31\u843d\u8352\u800c\u9003\u5fc3\u91cc\u89c9\u5f97\u5f88\u5947\u602a\u8e1e\u4e3a\u5979\u672c\u4e0d\u662f\u8fd9\u79cd \u4eba\u3002\u201d \u3000\u3000\u4e8e\u52a8\u9053\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u5979\u5b9a\u53c8\u56de\u53bb\u5411\u91d1\u5927\u5e05\u501f\u5382\u94b1\u8d76\u5230\u524d\u9762\u6765\u66ff\u4f60\u5148\u628a\u5f20\u8fd8\u4e86\uff1f\u201d \u3000\u3000\u90b9\u4eba\u8def\u9053\u201c\u4e2d\u9519\u56e0\u4e3a\u5979\u672c\u6765\u5c31\u559c\u6b22\u71d5\u4e03\u53c8\u6015\u71d5\u4e03\u4e0d\u80af\u63a5\u53d7\u5979\u7684\u597d\u610f\u6240\u4ee5\u624d\u6545\u610f\u90a3\u6837 \u505a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53ef\u662f\u5979\u600e\u9ebd\u77e5\u9053\u4f60\u6b20\u4e86\u8c01\u5bb6\u7684\u8d26\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u5f88\u5bb9\u6613\u6253\u542c\u5f97\u51fa\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u9178\u6885\u6c64\u662f\u4e2a\u591a\u9ebd\u673a\u7075\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u738b\u52a8\u9992\u9992\u7684\u70b9\u4e86\u70b9\u5934\u9053\u201c\u4e5f\u5f88\u5408\u7406\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u770b\u8fd9\u95ee\u9898\u662f\u4e0d\u662f\u5f88\u7b80\u5355\u6211\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u968f\u968f\u4fbf\u4fbf\u5c31\u60f3\u51fa\u4e86\u4e24\u4e2a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u83ab\u5fd8\u4e86\u8fd8\u6709\u7b2c\u53c1\u4e2a\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u4e2a\u4eba\u5b9a\u662f\u2026\u2500\u5c0f \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u4ed6\u5ffd\u7136\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002\u574e\u4e50\u82f1\u9192 \u3000\u3000\u56e0\u4e3a\u4ed6\u672c\u6765\u60f3\u5230\u5f88\u591a\u4eba\u90fd\u6709\u53ef\u80fd\xb7\u4f46\u4ed4\u7ec6\u60f3\u8fd9\u4e9b\u4eba\u53c8\u90fd\u4e0d\u5927\u53ef\u80fd\u3002\u738b\u52a8\u9053\u201c\u9a97\u8fc7\u4f60\u7684\u90a3 \u4e9b\u5c0f\u8e66\uff0c\u5c31\u7b97\u6ca1\u6709\u628a\u4f60\u5f53\u761f\u751f\u7b28\u86cb\uff0c\u5c31\u7b97\u5fc3\u91cc\u5f88\u611f\u6fc0\u4f60\xb7\u4e5f\u4e0d\u4f1a\u6709\u8fd9\u9ebd\u591a\u94b1\u6765\u66ff\u4f60\u8fd8\u5e10 \u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u90a3\u4e9b\u4eba\u7b80\u76f4\u7a77\u5f97\u8fde\u88e4\u5b50\u90fd\u6ca1\u5f97\u7a7f\u5426\u5219\u6211\u4e49\u600e\u9ebd\u4f1a\u5927\u53d1\u6148\u60b2\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e5f\u4e0d\u80fd\u7b97\u4e0a\u6885\u6c5d\u7537\uff0c\u4ed6\u88ab\u4f60\u5728\u809a\u5b50\u4e0a\u6253\u4e86\u62f3\u4e0d\u8fd8\u4f60\u4e24\u62f3\u5df2\u7ecf\u5ba2\u6c14\u7684\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82eb\u7b11\u9053\u201c\u6240\u4ee5\u6211\u5c31\u7b97\u88ab\u503a\u4e3b\u903c\u6b7b\u4ed6\u4e5f\u4e0d\u4f1a\u6389\u6ef4\u773c\u6cea\u7684\u3002\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u6389\u773c\u6cea\u5c0f\u4f97\u6bd4\u8fd8\u503a\u65b9\u4fbf\u4e5f\u4fbf\u5b9c\u5f97\u591a\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u8fd9\u7b2c\u53c1\u4e2a\u4eba\u4e5f\u7edd\u4e0d\u53ef\u80fd \u662f\u4ed6\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u975e\u65e5\u4e0d\u53ef\u80fd\u662f\u4ed6\uff0c\u4e5f\u7edd\u5c0f\u53ef\u80fd\u662f\u522b\u7684\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u56e0\u4e3a\u522b\u7684\u4eba\u5c31\u7b97\u77e5\u9053\u4f60\u5728\u8fd9\u91cc\u4e5f\u4e0d\u53ef\u80fd\u77e5\u9053\u4f60\u5728\u88ab\u4eba\u903c\u503a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5047\u5982\u6709\u4eba\u542c\u5230\u6211\u4eec\u8ddf\u50ac\u547d\u7b26\u548c\u5341\u53c1\u628a\u5927\u5200\u4ed6\u4eec\u51b3\u6597\u7684\u4e8b\u77e5\u9053\u6211\u4eec\u6709\u4eba\u53d7\u4e86 \u4f24\uff0c\u5c31\u8d76\u5230\u8fd9\u91cc\u6765\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6765\u5e72\u4ec0\u9ebd\u201d\u90ed\u5927\u8def\u9053\u201c\u4e5f\u8bb8\u662f\u5230\u6765\u770b\u70ed\u95f9\xb7\u4e5f\u8bb8\u662f\u60f3\u8d76\u6765\u5e2e\u6211\u4eec\u7684\u5fd9\xb7\u62a5\u6211\u4eec \u7684\u601d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u62a5\u6069\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8b6c\u5982\u8bf4\u6cbc\u53e3\u4e9b\u7ea2\u8682\u5c31\u53ef\u80fd\u4f1a\u6765\u62a5\u6211\u8ba2\u7684\u4e0d\u6740\u4e4b\u601d\u3002\u201d \u3000\u3000\u738b\u52a8\u7ec8\u4e8e\u53c8\u70b9\u70b9\u5934\uff0c\u9053\u201c\u8fd9\u4e5f\u5f88\u5408\u7406\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u542b\u7b11\u9053\u201c\u65e2\u7136\u5f88\u5408\u7406\u5c82\u975e\u5c31\u6ca1\u6709\u95ee\u9898\u5417\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u771f\u6b63\u7684\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4ed6\u8138\u8272\u5f88\u4e25\u8083\xb7\u5f88\u6c89\u91cd\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u771f\u6b63\u7684\u95ee\u9898\uff1f\u4ec0\u9ebd\u95ee\u9898\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e2\u7136\u53ef\u80fd\u6709\u4eba\u8d76\u6765\u770b\u70ed\u95f9\u8d76\u6765\u62a5\u6069\xb7\u5c31\u4e5f\u53ef\u80fd\u6709\u5165\u8fdb\u6765\u627e\u9ebb\u70e6\uff0c\u8d76\u6765\u62a5\u4ec7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u62a5\u4ec7\u4e25 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8ba4\u4e3a\u6211\u4eec\u5bf9\u90a3\u4e9b\u8682\u8681\u6709\u4e1c\u6740\u4e4b\u601d\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u5374\u53cd\u628a\u6211\u8ba2\u5f53\u4ec7\u4eba\u5462\uff1f\u4f60\u60f3\u5230\u6211 \u4eec\u653e\u4ed6\u4eec\u8d70\u7684\u65f6\u5019\u4e3a\u4ec0\u9ebd\u77f3\u4f1a\u60f3\u60f3\u6211\u4eec\u5c06\u4ed6\u4eec\u6253\u5f97\u843d\u82b1\u6d41\u6c34\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4f4f\u5382\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f55\u51b5\uff0c\u50ac\u547d\u7b26\u548c\u5341\u53c1\u628a\u5200\u4ed6\u5546\u8bf4\u4e0d\u5b9a\u4e5f\u6709\u591f\u4e49\u6c14\u7684\u670b\u53cb\uff0c\u542c\u5230\u4ed6\u4ecd\u88c1\u5728\u8fd9\u91cc\u5c31 \u72e0\u80fd\u8d76\u6765\u66ff\u4ed6\u4eec\u7684\u62a5\u4ec7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u56db\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f88\u5408\u7406\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u867d\u7136\u6ca1\u6709\u5728\u6c5f\u6e56\u4e2d\u6df7\u8fc7\uff0c\u53ef\u662f\u6211\u4eec\u5374\u4e0d\u540c\u65e0\u8bba\u8c01\u5728\u6c5f\u6e56\u4e2d\u6df7\u7684\u65f6\u5019\u90fd\u96be\u514d\u4f1a \u5728\u6709\u610f\u65e0\u610f\u95f4\u5f97\u7f6a\u4e9b\u4eba\u8fd9\u4e9b\u4eba\u82e5\u77e5\u9053\u6211\u8ba2\u7684\u884c\u8e2a\u4e5f\u5f88\u53ef\u80fd\u6bb7\u6765\u627e\u6211\u4ecd\u7b97\u7b97\u65e7\u5f20\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u82eb\u7b11\u9053\u201c\u770b\u6765\u6211\u7684\u8111\u7b4b\u5b9e\u5728\u4e0d\u80fd\u7b97\u5f88\u5546\u660e\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f46\u8fd9\u4e9b\u4eba\u8fd8\u4e0d\u80fd\u7b97\u662f\u6700\u5927\u7684\u95ee\u9898\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5413\u4e86\u8df3\u9053\u201c\u8fd9\u8fd8\u4e0d\u7b97\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6700\u5927\u7684\u95ee\u9898\u662f\xb7\u65e2\u7136\u5df2\u6709\u5f88\u591a\u4eba\u77e5\u9053\u6211\u4eec\u7684\u884c\u52a8\u5c31\u8868\u793a\u6211\u4eec\u4e0d\u5e78\u5df2\u4e2d\u540d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u95e8\u6c14\u3001\u63a5\u9053\u201c\u4e2a\u4eba\u51fa\u4e86\u540d\u4e4b\u5f8c\u5927\u5927\u5c0f\u5c0f\u7684\u9ebb\u70e6\u7acb\u523b\u5c31\u4f1a\u7f00\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u9ebb\u9885\uff1f\u201d \u3000\u3000\u58eb\u52a8\u9053\u201c\u5404\u79cd\u9ebb\u70e6\uff0c\u4f60\u60f3\u90fd\u60f3\u4e0d\u5230\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8b6c\u5982\u8bf4\u6709\u4eba\u542c\u8bf4\u4f9d\u7684\u6b66\u529f\u9ad8\u5c31\u60f3\u6765\u627e\u4f60\u8f83\u91cf\u8f83\u91cf\u5c31\u7b97\u4e0d\u80af\u52a8\u624b\u4ed6\u4eec\u4e5f\u4f1a\u60f3\u51fa \u5404\u79cd\u6cd5\u5b50\u901a\u4f60\u975e\u52a8\u624b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u8fd9\u70b9\u6211\u5012\u660e\u767d\u3002\u201d \u3000\u3000\u5929\u52a8\u9053\u201c\u4f60\u660e\u767d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u8fd9\u5c31\u597d\u50cf\u6211\u903c\u91d1\u5927\u5e05\u51fa\u624b\u6837\u53ea\u4e0d\u8fc7\u6211\u5012\u672a\u60f3\u5230\u62a5\u5e94\u6765\u5f97\u8fd9\u9ebd\u5feb\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u9664\u4e86\u6765\u62e2\u4f60\u6bd4\u6b66\u8f83\u91cf\u7684\u4eba\u4e4b\u5916\u6211\u4f60\u6765\u5e2e\u5fd9\u7684\u4e5f\u597d\u627e\u4f60\u6765\u89e3\u51b3\u95ee\u9898\u7684\u4e5f\u597d\uff0c\u627e\u4f60 \u6765\u501f\u8def\u8d39\u76d8\u7f20\u7684\u4e5f\u597d\u8fd9\u4e9b\u4eba\u968f\u65f6\u968f\u5254\u4f1a\u627e\u4e0a\u95e8\u6765\u4f60\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4ed6\u4eec\u4ec0\u9ebd\u65f6\u5019\u4f1a\u6765\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\u63a5\u9053\u201c\u4e2a\u4eba\u82e5\u5728\u6c5f\u6e56\u4e2d\u6210\u4e86\u540d\u8981\u60f3\u518d\u8fc7\u5929\u6e05\u9759\u7684\u65e5\u5b50\u53ea\u4f2f\u90fd\u4e0d\u592a\u7b80\u5355 \u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u53f9\u4e86\u53e3\u6c14\u54fa\u54fa\u9053\u201c\u539f\u6765\u6210\u540d\u4e5f\u5e76\u4e0d\u662f\u4ef6\u5f88\u6986\u5feb\u7684 \u3000\u3000\u738b\u52a8\u9053\u201c\u4e5f\u8bb8\u53ea\u6709\u79cd\u4eba\u624d\u89c9\u5f97\u6210\u540d\u5f88\u6109\u5feb\u3002\u201d\u90ed\u5927\u8def\u9053\u8fd8\u6ca1\u6709\u6210\u540d\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u53f9\u9053\u201c\u5176\u5b9e\u771f\u6b63\u6709\u9ebb\u70e6\u7684\u4eba\u4e5f\u8bb8\u5e76\u4e0d\u662f\u4f60\u8ddf\u6211\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u8bf4\xb7\u71d5\u4e03\u817f\u6797 \u592a\u5e73\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4eec\u7684\u9ebb\u70e6\u4e3a\u4ec0\u9ebd\u4f1a\u6bd4\u6211\u97f6\u591a\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u56e0\u4e3a\u4ed6\u4eec\u90fd\u6709\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ece\u5e8a\u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\u201c\u4e0d\u9519\u852c\u4e03\u7684\u786e\u6709\u4e2a\u5f88\u5927\u7684\u79d8\u5bc6\u4ed6\u603b\u662f\u4e0d\u80af\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u731c\u51fa\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u96be\u9053\u5df2\u731c\u51fa\u6765\u4e86\u201d \u3000\u3000\u4e92\u52a8\u5ffd\u7136\u7b11\u5382\u7b11\u9053\u201c\u770b\u6765\u4f60\u975e\u4f46\u8111\u7b4b\u4e0d\u592a\u9ad8\u660e\u773c\u5708\u4e5f\u2026\u3002\u5c0f\u4ed6\u5ffd\u7136\u505c\u4f4f\u4e86\u53e3\u3002 \u3000\u3000\u6709\u4eba\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u707e\u5254\u4e5f\u542c\u5230\u6709\u4eba\u8d70\u8fdb\u5916\u9762\u7684\u9662\u5b50\u3002\u8fd8\u4e0d\u6b62\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u4ece\u5e8a\u6269\u6e9c\u4e0b\u53bb\xb7\u6162\u6162\u9053\u201c\u4f60\u8bf4\u7684\u679c\u7136\u4e0d\u9519\xb7\u679c\u7136\u5df2\u6709\u5165\u627e\u4e0a\u95ee\u6765\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5b9e\u5728\u4e5f\u6ca1\u6709\u60f3\u5230\u4eba\u5c45\u7136\u6765\u5f97\u8fd9\u9ebd\u5feb\u3002 \u3000\u3000\u6765\u7684\u662f\u4ec0\u9ebd\u4eba\uff1f \u3000\u3000\u4f1a\u4e3a\u4ed6\u4eec\u5e26\u6765\u4ec0\u9ebd\u6837\u7684\u9ebb\u70e6\uff1f \u3000\u3000\u6765\u7684\u5171\u6709\u4e94\u4e2a\u4eba\u3002 \u3000\u3000\u5f8c\u9762\u7684\u56db\u4e2a\u4eba\u8eab\u6750\u90fd\u5f88\u9b41\u4f1f\u8863\u90fd\u5f88\u534e\u4e3d\u770b\u8d77\u6765\u5f88\u5230\u608d\u5f88\u795e\u6c14\u3002 \u3000\u3000\u53ef\u662f\u548c\u524d\u9762\u90a3\u4e2a\u4eba\u6bd4\uff0c\u8fd9\u56db\u4e2a\u4eba\u7b80\u76f4\u5c31\u53d8\u5f97\u597d\u50cf\u56db\u53ea\u5c0f\u9e21\u3002 \u3000\u3000\u82f1\u5b9e\u524d\u9762\u90a3\u4e2a\u4eba\u4e5f\u5e76\u4e0d\u6bd4\u4ed6\u4eec\u5546\u5f88\u591a\uff0c\u4f46\u5374\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6c14\u6d3e\u5c31\u7b97\u7ad9\u5728\u4e07\u4e2a\u4eba\u91cc\u4f60\u8fd8\u662f \u773c\u5c31\u4f1a\u770b\u5230\u4ed6\u3002 \u3000\u3000\u8fd9\u4eba\u6602\u9996\u9614\u6b65\u987e\u76fc\u81ea\u96c4\u8fde\u95e8\u90fd\u6ca1\u6709\u656c\u5c31\u5927\u6447\u5927\u6446\u7684\u5b9a\u8fdb\u4e86\u9662\u5b50\xb7\u5c31\u597d\u50cf\u4e2a\u767e\u6218\u800c\u5f52\u7684\u5c06 \u519b\xb7\u56de\u5230\u81ea\u5df1\u5bb6\u6765\u4f3c\u7684\u3002 \u3000\u3000\u738b\u52a8\u5f53\u7136\u77e5\u9053\u8fd9\u4e0d\u662f\u4ed6\u7684\u5bb6\uff0c\u90ed\u5927\u8def\u4e5f\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5df2\u51c6\u5907\u51b2\u51fa\u53bb\u7684\u82e5\u6709\u9ebb\u70e6\u4e0a\u95e8\u4ed6\u603b\u662f\u7b2c\u4e00\u4e2a\u51b2\u51fa \u3000\u3000\u53ef\u662f\u8fd9\u6b21\u4ed6\u4e00\u770b\u5230\u8fd9\u4e2a\u4eba\u5c31\u7acb\u523b\u53c8\u7f29\u4e86\u56de\u6765\u3002 \u3000\u3000\u738b\u52a8\u76b1\u4e86\u76b1\u7709\u9053\u201c\u5f25\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002\u738b\u52a8\u9053\u201c\u8fd9\u4eba\u5c31\u662f\u91d1\u5927\u5e05\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e5f\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8ba4\u5f97\u4f60\u53c8\u600e\u9ebd\u77e5\u9053\u4ed6\u662f\u91d1\u5927\u5e05\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u4eba\u82e5\u4e0d\u662f\u91d1\u5927\u5e05\u8c01\u662f\u91d1\u5927\u5e05\u201d \u3000\u3000\u7ff1\u5927\u8def\u82e6\u7b11\u9053\u201c\u4e0d\u9519\u4ed6\u7684\u786e\u72e0\u6709\u70b9\u5927\u5e05\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u7ad9\u5728\u9662\u5b50\u91cc\u80cc\u53cc\u624b\u56db\u9762\u6253\u91cf\u5ffd\u7136\u9053\u201c\u8fd9\u9662\u4e86\u8be5\u62db\u4e86\u3002\u201d \u3000\u3000\u5f8c\u9762\u613f\u7684\u4eba\u7acb\u523b\u5c04\u8eab\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u90a3\u8fb9\u7684\u6708\u5b63\u548c\u7261\u4e39\u90fd\u5e94\u8be5\u6d47\u70b9\u6c34\u8349\u5730\u4e5f\u8be5\u526a\u526a\u3002\u201d \u3000\u3000\u9650\u73ed\u95e8\u9053\u201c\u90a3\u8fb9\u6811\u4e0b\u7684\u51e0\u5f20\u85e4\u6905\uff0c\u5e9c\u8be5\u6362\u4e0a\u77f3\u58a9\u5b50\u968f\u4fbf\u628a\u6811\u679d\u4e5f\u4fee\u4fee\u3002\u201d \u3000\u3000\u8ddf\u73ed\u95e8\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u738b\u52a8\u5728\u7a97\u6237\u91cc\u770b\u5ffd\u7136\u95ee\u9053\u201c\u8fd9\u91cc\u7a76\u7adf\u662f\u8c01\u7684\u5bb6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u4f60\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6211\u672c\u6765\u4e5f\u77e5\u9053\u8fd9\u662f\u6211\u7684\u5bb6\u73b0\u5728\u5374\u6709\u70b9\u7cca \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u8981\u7b11\uff0c\u5374\u53c8\u76b1\u8d77\u7709\uff0c\u9053\u201c\u71d5\u4e03\u600e\u9ebd\u8fd8\u4e0d\u51fa\u53bb\uff1f\u201d \u3000\u3000\u4e03\u52a8\u9053\u201c\u4e5f\u8bb8\u4ed6\u8ddf\u4f60\u6837\u770b\u89c1\u91d1\u5927\u5e05\u5c31\u6709\u70b9\u5fc3\u865a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u91d1\u5927\u5e05\u53c8\u4e0d\u8ba4\u5f97\u4ed6\xb7\u4ed6\u4e3a\u4ec0\u9ebd\u4f1a\u5fc3\u865a\uff1f\u201d \u3000\u3000\u4e94\u52a8\u76ee\u5149\u95ea\u52a8\u7a81\u7136\u95ee\u9053\u201c\u4f60\u67ef\u6ca1\u6709\u60f3\u5230\u4e2a\u95ee\u9898\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u95ee\u9898\uff1f\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u71d5\u4e03\u6253\u6697\u5668\u7684\u624b\u6cd5\u5df2\u53ef\u7b97\u5f97\u662f\u6d41\u7684\u63a5\u6697\u5668\u7684\u624b\u6cd5\u5f53\u7136\u4e5f\u4e0d\u9519\u201c \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u60f3\u5fc5\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u672b\u4ed6\u81ea\u5df1\u4e3a\u4ec0\u9ebd\u4e0d\u53bb\u627e\u4f59\u5927\u5e05\u5462\uff1f\u4e3a\u4ec0\u9ebd\u8981\u4f60\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\u9053\u201c\u8fd9\u2026\u2026\u6211\u5012\u6ca1\u6709\u60f3\u8fc7\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u60f3\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u53ea\u8981\u662f\u4ed6\u8981\u6211\u505a\u7684\u4e8b\u6211\u5c31\u597d\u50cf\u89c9\u5f97\u662f\u5929\u7ecf\u5730\u4e49\xb7\u5e94\u8be5\u4e2d\u6211 \u53bb\u505a\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u770b\u4ed6\xb7\u6447\u5934\u5c31\u597d\u50cf\u5927\u54e5\u54e5\u5728\u770b\u81ea\u5df1\u7684\u5c0f\u5f1f\u5f1f\u3002 \u3000\u3000\u4e2a\u88ab\u4eba\u5c06\u7cd6\u846b\u82a6\u9a97\u8d70\u7684\u5c0f\u5f1f\u5f1f\u3002 \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u624d\u53c8\u9053\u201c\u4f60\u7684\u610f\u601d\u5c31\u662f\u8bf4\xb7\u4ed6\u81ea\u5df1\u4e0d\u5de6\u627e\u91d1\u5927\u5e05\uff0c\u5c31\u56e0\u4e3a\u751f\u4f2f\u91d1\u5927\u5e05\u4f1a\u8ba4 \u51fa\u4ed6\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u8fd8\u6ca1\u6709\u8bf4\u51fa\u8bdd\u7a81\u542c\u91d1\u5927\u5e05\u6c89\u58f0\u559d\u9053\u201c\u662f\u4ec0\u9ebd\u4eba\u9b3c\u9b3c\u795f\u795f\u8eb2\u5728\u5c4b\u5b50\u91cc\u55b7\u5495\xb7\u8fd8\u4e0d\u5feb \u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u52a8\u53c8\u770b\u4e86\u90ed\u5927\u8def\u4e00\u670d\uff0c\u7ec8\u4e8e\u9992\u9992\u7684\u63a8\u5f00\u95e8\u8d70\u51fa\u53bb\u3002\u90ed\u5927\u8def\u65e2\u7136\u4e0d\u80af\u52a8\u4ed6\u5c31\u53ea\u6709\u52a8\u4e86\u3002 \u3000\u3000\u91d1\u5927\u5e05\u77aa\u4ed6\u9053\u201c\u4f60\u8eb2\u5728\u91cc\u9762\u786e\u5495\u4e9b\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u6de1\u8c08\u9053\u201c\u6211\u6839\u672c\u4e0d\u5fc5\u8eb2\uff0c\u4f60\u4e5f\u7ba1\u4e0d\u6211\u5728\u56bc\u5495\u4e9b\u4ec0\u9ebd\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u5389\u58f0\u9053\u201c\u4f60\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5c31\u662f\u8fd9\u5730\u65b9\u7684\u4e3b\u4eba\xb7\u6211\u9ad8\u5174\u5750\u5728\u90a3\u91cc\u9ad8\u5174\u8bf4\u4ec0\u9ebd\u5c31\u53ef\u4ee5\u8bf4\u4ec0\u9ebd\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\u201c\u4e00\u4e2a\u4eba\u5728\u81ea\u5df1\u7684\u5bb6\u91cc\u5c31\u7b97\u9ad8\u5174\u8131\u4e86\u88e4\u5343\u653e\u5c41\u522b\u4eba\u4e5f\u7ba1\u4e0d\u3002\u201d \u3000\u3000\u4ed6\u5e73\u5e38\u8bf4\u8bdd\u672c\u6ca1\u6709\u5982\u6b64\u523b\u8584\u7684\u73b0\u5728\u5374\u597d\u50cf\u6545\u610f\u8981\u6740\u6740\u91d1\u5927\u5e05\u7684\u5a01\u98ce\u3002 \u3000\u3000\u8c01\u77e5\u91d1\u5927\u5e05\u53cd\u4e3d\u7b11\u2026\u535c\u4e0b\u4e0b\u770b\u4e86\u4ed6\u51e0\u773c\u7b11\u9053\u201c\u8fd9\u4eba\u679c\u7136\u8c61\u662f\u4e2a\u59d3\u738b\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5e76\u4e0d\u662f\u8c61\u59d3\u738b\u7684\u6211\u672c\u6765\u5c31\u662f\u4e2a\u59d3\u738b\u7684\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u770b\u6765\u4f60\u6015\u5c31\u662f\u738b\u8001\u5927\u7684\u513f\u5b50\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u738b\u8005\u5927\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u8bf4\u9053\u6237\u738b\u8001\u5927\u5c31\u662f\u738b\u6f5c\u77f3\xb7\u4e5f\u5c31\u662f\u4f60\u7684\u8001\u4e8e\u3002\u201d \u3000\u3000\u4e0a\u52a8\u53cd\u5012\u6014\u4f50\u4e86\u3002 \u3000\u3000\u571f\u6f5c\u77f3\u7684\u786e\u662f\u4ed6\u7236\u4eb2\u4ed6\u5f53\u7136\u77e5\u9053\u4ed6\u7236\u4eb2\u7684\u540d\u5b57\u3002 \u3000\u3000\u4f46\u522b\u4eba\u77e5\u9053\u738b\u6f5c\u77f3\u8fd9\u540d\u5b57\u7684\u5374\u5f88\u5c11\u3002 \u3000\u3000\u5927\u591a\u6570\u4eba\u90fd\u53ea\u77e5\u9053\u738b\u8001\u5148\u751f\u7684\u53f7\u738b\u9038\u9f50\u3002 \u3000\u3000\u77e5\u9053\u738b\u6f5c\u77f3\u8fd9\u540d\u5b57\u7684\u4eba\u5f53\u7136\u662f\u738b\u6f5c\u77f3\u7684\u6545\u4ea4\u3002 \u3000\u3000\u738b\u52a8\u7684\u6001\u5ea6\u7acb\u523b\u53d8\u4e86\u53d8\u5f97\u5ba2\u6c14\u5f97\u591a\u8bd5\u63a2\u95ee\u901a\u201c\u9601\u4e0b\u8ba4\u5f97\u5bb6\u7236\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u4e5f\u4e0d\u56de\u7b54\u4ed6\u7684\u8bdd\u5374\u5927\u6b65\u8d70\u4e0a\u4e86\u56de\u5eca\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd9\u5c4b\u5b50\u7684\u95e8\u662f\u5f00\u7684\u3002 \u3000\u3000\u91d1\u5927\u5e05\u5c31\u6602\u7136\u8d70\u4e86\u8fdb\u6765\u5927\u9a6c\u91d1\u5200\u5f80\u6905\u5b50\u4e0a\u5750\u5c31\u5750\u5728\u90ed\u5927\u8def\u7684\u9762\u524d\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\u201c\u4f60\u597d\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u54bd\xb7\u8fd8\u597d\u603b\u7b97\u8fd8\u6ca1\u6709\u88ab\u4eba\u6c14\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e7e\u54b3\u51e0\u58f0\u9053\u201c\u4f60\u662f\u5728\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u6765\u627e\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\u9053\u201c\u90a3\u672b\xb7\u5927\u5e05\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u5e72\u4ec0\u9ebd\u7684\u5462\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6211\u96be\u9053\u4e0d\u80fd\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u80fd\u5f53\u7136\u80fd\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u51b7\u51b7\u9053\u201c\u544a\u8bc9\u4f60\uff0c\u6211\u5230\u8fd9\u91cc\u6765\u7684\u65f6\u5019\u4f60\u6015\u8fd8\u6ca1\u6709\u751f\u51fa\u6765\u3002\u201d \u3000\u3000\u8fd9\u4eba\u809a\u5b50\u91cc\uff0c\u597d\u50cf\u88c5\u809a\u5b50\u706b\u836f\u6765\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5e76\u4e0d\u662f\u4f2f\u4ed6\xb7\u53ea\u4e0d\u8fc7\u5b9e\u5728\u89c9\u5f97\u6709\u70b9\u5fc3\u865a\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u505a\u7684\u90a3\u624b\u5b9e\u5728\u4ee4\u4eba\u670d\u8d34\uff0c\u90a3\u6559\u8bad\u4e5f\u6ca1\u6709\u9519\u3002 \u3000\u3000\u90ed\u5927\u8def\u65e2\u7136\u6ca1\u522b\u7684\u6cd5\u5b50\u5bf9\u4ed8\xb7\u53ea\u597d\u6e9c\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u91d1\u4eba\u5e05\u7684\u773c\u775b\u8fd8\u771f\u707e\u4ed6\u7684\u811a\u521a\u52a8\uff0c\u91d1\u5927\u5e05\u5c31\u559d\u9053\u201c\u7ad9\u4efb\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u966a\u7b11\u9053\u201c\u4f60\u65e2\u7136\u4e0d\u662f\u6765\u627e\u6211\u7684\u8981\u6211\u7559\u5728\u8fd9\u91cc\u5e72\u4ec0 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6211\u6709\u8bdd\u95ee\u4f60\u3002\u201d\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u597d\u7f51\u5427\u201d\u91d1\u4eba\u5e05\u9053\u201c\u4f60\u4eec\u665a\u4e0a\u5403\u4ec0 \u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u7684\u5c45\u7136\u662f\u8fd9\u9ebd\u6837\u7684\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u9053\u201c\u6211\u521a\u55c5\u5230\u7ea2\u70e7\u8089\u7684\u5473\u9053\xb7\u5927\u6982\u5403\u7684\u662f\u7af9\u8461\u70e7\u8089\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u597d\u7094\u5f00\u996d\u6211\u997f\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u5740\u4f4f\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u6709\u70b9\u5f04\u4e0d\u6e05\u8c01\u662f\u8fd9\u5730\u65b9\u7684\u58eb\u4eba\u4e86\u3002 \u3000\u3000\u91d1\u5927\u5e05\u53c8\u559d\u9053\u201c\u53eb\u4f60\u5f00\u996d\u4f60\u8fd8\u7ad9\u5728\u8fd9\u91cc\u53d1\u4ec0\u9ebd\u679c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u770b\u4e0a\u52a8\u3002 \u3000\u3000\u738b\u52a8\u5374\u597d\u50cf\u4ec0\u9ebd\u90fd\u770b\u4e0d\u89c1\u4ec0\u9ebd\u90fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u53f9\u606f\u54fa\u54fa\u901a\u201c\u662f\u8be5\u5f00\u996d\u4e86\u6211\u4e5f\u997f\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u996d\u5f00\u59cb\u4e0a\u684c\xb7\u679c\u7136\u6709\u82df\u4fa5\u5185\u3002 \u3000\u3000\u91d1\u5927\u5e05\u4e5f\u4e0d\u5ba2\u6c14\u5c41\u80a1\u5c31\u5750\u5728\u4e0a\u5ea7\u4e0a\u3002 \u3000\u3000\u738b\u52a8\u548c\u90ed\u5927\u8def\u5c31\u53ea\u6709\u6253\u6a2a\u76f8\u966a\u3002\u91d1\u5927\u5e05\u521a\u4e3e\u8d77\u7b77\u5b50\u5ffd\u7136\u95ee\u9053\u201c\u8fd8\u6709\u522b\u7684\u4eba\u5462\uff1f\u4e3a\u4ec0\u9ebd\u4e0d \u6765\u5403\u996d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u4e24\u4e2a\u4eba\u75c5\u5382\u53ea\u80fd\u559d\u7ca5\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u8fd8\u6709\u4e2a\u6ca1\u75c5\u7684\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5730\u65b9\u7684\u4e8b\u4ed6\u77e5\u9053\u5f97\u5012\u8fd8\u6e05\u695a\u3002 \u3000\u3000\u90ed\u5927\u8def\u652f\u543e\u82e6\u7b11\u9053\u201c\u597d\u50cf\u5728\u53a8\u623f\u91cc\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u786e\u5728\u53a8\u623f\u91cc\u3002 \u3000\u3000\u4ed6\u4e0d\u80af\u51fa\u6765\u56e0\u4e3a\u592a\u8d43\uff0c\u6240\u4ee5\u4e0d\u60f3\u89c1\u4eba\u3002 \u3000\u3000\u65e2\u7136\u4ed6\u8fd9\u9ebd\u8bf4\xb7\u90ed\u5927\u8def\u5c31\u53ea\u80fd\u542c\u56e0\u4e3a\u82e5\u518d\u95ee\u4e0b\u53bb\u3002\u71d5\u4e03\u5c31\u4f1a\u77aa\u773c\u775b\u3002 \u3000\u3000\u71d5\u4e03\u77aa\u773c\u775b\xb7\u90ed\u5927\u8def\u5c31\u8f6f\u4e86\u3002 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4ed6\u53c8\u4e0d\u662f\u53a8\u5b50\uff0c\u4e3a\u4ec0\u9ebd\u8eb2\u5728\u53a8\u623f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\xb7\u9053\u201c\u597d\uff0c\u6211\u53bb\u53eb\u4ed6\u3002\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u7ad9\u8d77\u6765\xb7\u71d5\u4e03\u5df2\u5782\u5934\u8d70\u4e86\u8fdb\u6765\xb7\u597d\u50cf\u672c\u5c31\u8eb2\u5728\u95e8\u53e3\u50a8\u542c\u3002\u91d1\u5927\u5e05\u4e0a\u3014\u770b\u4e86\u4ed6\u4e24 \u773c\u9053\u201c\u5750\u3002\u201d \u3000\u3000\u71d5\u4e03\u5c45\u7136\u5c31\u771f\u7684\u5782\u5934\u5750\u535c\u8fd9\u4eba\u4eca\u5929\u597d\u50cf\u4e5f\u53d8\u4e56\u5382 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u597d\u5403\u5427\u3002\u201d \u3000\u3000\u4ed6\u72fc\u541e\u864e\u98ce\u5377\u6d45\u4e91\u822c\u5382\u5b50\u5c31\u628a\u684c\u4e0a\u7684\u83b1\u626b\u7a7a\u4e86\u3002\u90ed\u5927\u8def\u4ed6\u4eec\u4e4e\u8fde\u4f38\u7b77\u5343\u7684\u673a\u4f1a\u90fd\u5f88\u5c11\u3002 \u3000\u3000\u789f\u5b50\u5e95\u5168\u90fd\u671d\u4e86\u5927\u4e4b\u5f8c\u91d1\u5927\u5e05\u624d\u653e\u4e86\u7b77\u5b50\uff0c\u53cc\u864e\u864e\u6709\u5a01\u7684\u773c\u775b\uff0c\u4ece\u738b\u52a8\u770b\u5230\u90ed\u5927\u8def\xb7\u4ece \u90ed\u5927\u8def\u770b\u5230\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u4f60\u4eec\u53bb\u6253\u8df3\u7684\u4e3b\u610f\u4e3b\u610f\u662f\u96c4\u51fa\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5782\u5934\u9053\u201c\u6211\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u54fc\u6211\u5c31\u77e5\u9053\u662f\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u5934\u5782\u5f97\u66f4\u4f4e\u3002 \u3000\u3000\u91d1\u5927\u5e05\u65e5\u5149\u8f6c\u5411\u90ed\u5927\u8def\u9053\u201c\u4f60\u80fd\u63a5\u5f97\u4f73\u6211\u4e94\u53d1\u8fde\u6b8a\u5f39\uff0c\u8fd9\u79cd\u624b\u6c88\u6c5f\u6e56\u4e2d\u5df2\u5c11\u89c1\u5f97\u5f88\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u5fcd\u5c0f\u4f4f\u7b11\u4e86\u7b11\u9053\u201c\u8fd8\u8fc7\u5f97\u53bb\u3002\u201d\u91d1\u5927\u5e05\u9053\u201c\u8fd9\u624b\u6cd5\u662f\u8c01\u6559\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u5de5\u52a8\u901a\u201c\u6211\u3002\u201d\u91d1\u5927\u5e05\u9053\u201c\u6211\u5c31\u7eab\u901a\u662f\u4f60\u3002\u201d \u3000\u3000\u5e72\u52a8\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u7684\uff1f\u201d\u91d1\u5927\u5e05\u9053\u201c\u6211\u4e0d\u4f46\u77e5\u9053\u4ed6\u662f\u4f60\u6559\u7684\u4e5f\u77e5\u9053\u4f60\u662f\u8c01\u6559 \u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u7a81\u7136\u6c89\u4e0b\u4e86\u80ce\u9053\u201c\u4f60\u7236\u4eb2\u6559\u7ed9\u4f60\u8fd9\u624b\u6cd5\u65f6\xb7\u8fd8\u544a\u8bc9\u4e86\xb7\u4f60\u4e9b\u4ec0\u9ebd\u8bdd\uff1f\u201d\u738b\u52a8\u9053 \u201c\u4ec0\u9ebd\u8bdd\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u600e\u9ebd\u4f1a\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u8fd9\u624b\u6cd5\u4e0d\u662f\u4ed6\u8001\u4eba\u5bb6\u4f20\u6388\u7684\u3002\u201d\u2606 \u3000\u3000\u91d1\u5927\u5e05\u5389\u58f0\u9053\u201c\u4f60\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u738b\u52a8\u4e5f\u6c89\u4e0b\u4e86\u8138\u51b7\u51b7\u9053\u201c\u4f60\u53ef\u4ee5\u542c\u5230\u6211\u8bf4\u5404\u79cd\u8bdd\u5374\u7edd\u4e0d\u4f1a\u542c\u5230\u6211\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u76ef\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u95f7\u901a\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u7236\u4eb2\u6559\u7684\uff1f\u662f\u8c01\u6559\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u77e5\u9053\u5c31\u662f\u672c\u77e5\u9053\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u53c8\u5f00\u59cb\u76ef\u4ed6\u53c8\u8fc7\u4e86\u5f88\u4e45\u96f9\u7136\u957f\u8eab\u4e3d\u8d77\u9053\u201c\u4f60\u8ddf\u6211\u51fa\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5927\u6b65\u5b9a\u5230\u9662\u5b50\u91cc\u3002\u738b\u52a8\u4e5f\u6162\u6162\u7684\u8ddf\u4e86\u51fa\u53bb\u8fd9\u4e2a\u4eba\u4eca\u5929\u597d\u8c61\u4e5f\u53d8\u5f97\u6709\u70b9\u5947\u602a\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u6084\u6084\u9053\u201c\u6211\u73b0\u5728\u4e60\u77e5\u9053\u8fd9\u4f4d\u5927\u5e05\u662f\u6765\u5e72\u4ec0\u9ebd\u7684\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5ce8\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u7834\u5e7f\u4ed6\u7684\u8fde\u73e0\u5f39\u4ed6\u5fc3\u91cc\u5b9a\u5f88\u4e0d\u670d\u6c14\u6240\u4ee5\u8fd8\u60f3\u627e\u6559\u6211\u7684\u4eba\u6bd4\u5212\u6bd4\u5212\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u8bdd\uff0c\u4eba\u4e5f\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f11\u60f3\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5de5\u8001\u4eba\u817f\u4e0a\u7684\u4f24\u8fd8\u6ca1\u6709\u597d\u6211\u600e\u9ebd\u80fd\u770b\u4ed6\u2026\u2500\u201c \u3000\u3000\u85fb\u4e03\u6253\u65ad\u4ed6\u7684\u8bdd\xb7\u51b7\u51b7\u9053\u201c\u4f60\u6700\u597d\u8fd8\u662f\u5750\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u71d5\u4e03\u9053\u201c\u4f60\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\uff0c\u4ed6\u6765\u627e\u7684\u662f\u738b\u52a8\xb7\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u2606\u53ef\u662f\u738b\u52a8\u7684\u817f\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8981\u63a5\u4ed6\u7684\u8fde\u73e0\u5f39\u5e76\u4e0d\u662f\u7528\u817f\u7684\u3002\u201d \u3000\u3000\u591c\u8272\u6e05\u6717\u3002 \u3000\u3000\u91d1\u5927\u5e05\u770b\u738b\u52a8\u8d70\u8fc7\u6765\u5ffd\u7136\u76b1\u4e86\u76b1\u7709\uff0c\u9053\u201c\u4f60\u7684\u817f\uff1f\u2026\u2026\u201d\u738b\u52a8\u51b7\u51b7\u9053\u201c\u6211\u5f88\u5c11\u7528\u817f\u63a5\u6697 \u5668\xb7\u6211\u8fd8\u6709\u624b\u3002\u201d\u91d1\u5927\u5e05\u9053\u597d\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4f38\u51fa\u624b\u3002\u7acb\u523b\u5c31\u6709\u4eba\u6367\u4e0a\u4e86\u91d1\u5f13\u9769\u56ca\u3002 \u3000\u3000\u91d1\u5927\u5e05\u628a\u6284\u8fc7\u91d1\u5f13\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u7a81\u7136\u95f4\u6ee1\u5929\u91d1\u5149\u95ea\u52a8\u3002 \u3000\u3000\u8c01\u4e5f\u6295\u770b\u6e05\u4ed6\u662f\u600e\u9ebd\u51fa\u624b\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5012\u62cd\u4e86\u53e3\u51c9\u6c14\u9053\u201c\u8fd9\u6b21\u4ed6\u51fa\u4e4e\u600e\u9ebd\u6bd4\u4e0a\u6b21\u8fd8\u8981\u4f7f\u5f97\u591a\uff1f\u201d \u3000\u3000\u71d5\u4e03\u6de1\u8c08\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4e0d\u60f3\u66ff\u4f60\u4e70\u68fa\u6750\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u65e2\u7136\u4e0d\u80af\u7528\u6740\u624b\u5bf9\u4ed8\u6211\uff0c\u4e3a\u4ec0\u9ebd\u8981\u7528\u6740\u624b\u5bf9\u4ed8\u738b\u52a8\uff1f\u96be\u9053\u4ed6\u548c\u738b\u52a8\u6709 \u4ec7\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u8fde\u71d5\u4e03\u4e5f\u56de\u7b54\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u4ed6\u867d\u5df2\u770b\u51fa\u91d1\u5927\u5e05\u8fd9\u6b21\u6765\u5fc5\u5b9a\u6709\u4e2a\u72e0\u5947\u602a\u7684\u76ee\u7684\xb7\u5374\u8fd8\u662f\u731c\u51fa\u8fd9\u76ee\u7684\u662f\u4ec0\u9ebd\uff1f \u3000\u3000\u5c31\u5728\u90ed\u5927\u8def\u66ff\u738b\u52a8\u62c5\u5fc3\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u95f4\u6ee1\u5929\u91d1\u5149\u5168\u4e0d\u89c1\u65a4\u3002 \u3000\u3000\u5e72\u52a8\u8fd8\u662f\u597d\u597d\u7684\u7ad9\uff0c\u624b\u4e0a\u4e24\u53ea\u7f51\u91cc\u88c5\u6ee1\u4e86\u91d1\u5f39\u5b50\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u770b\u6e05\u4ed6\u7528\u7684\u662f\u4ec0\u9ebd\u624b\u6cd5\uff0c\u751a\u81f3\u6839\u672c\u6ca1\u770b\u6e05\u4ed6\u51fa\u624b\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u5382\u53e3\u6c14\u54fa\u54cd\u9053\u201c\u539f\u6765\u4ed6\u624b\u6cd5\u4e5f\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u624b\u6cd5\u7edd\u4e0d\u662f\u4e00\u5929\u7ec3\u51fa\u6765\u7684\xb7\u4f60\u51ed\u4ec0\u9ebd\u80fd\u5728\u5929\u91cc\u5c31\u80fd\u5168\u5b66\u4f1a\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u4f60\u771f \u662f\u5929\u624d\u201d\u90ed\u5927\u8def\u9053\u201c\u65e0\u8bba\u5982\u4f55\u8fd9\u624b\u6cd5\u7684\u51b3\u7a8d\u6211\u603b\u5df2\u61c2\u5f97\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5e08\u5085\u6559\u5f97\u597d\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u9053\u201c\u5e08\u5085\u5f53\u7136\u597d\u4f46\u5f92\u5f1f\u603b\u7b97\u4e5f\u4e0d\u9519\u5426\u5219\u5c82\u975e\u4e5f\u65e9\u5c31\u8fdb\u4e86\u68fa\u6750\uff1f\u201d \u3000\u3000\u9891\u4e03\u770b\u4ed6\u5ffd\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u51e0\u65f6\u82e5\u80fd\u628a\u8fd9\u5439\u725b\u7684\u6bdb\u75c5\u6539\u6389\xb7\u6211\u5c31\u3001\u2500\u3002\u201d\u90ed\u5927\u8def\u9053 \u201c\u5c31\u600e\u9ebd\u2026\u3002\uff0e\u662f\u672c\u662f\u4f60\u628a\u4f60\u90a3\u79d8\u5bc6\u544a\u8bc9\u6211\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u8ba2\u8bf4\u4e86\u5341\u6765\u53e5\u8bdd\u91d1\u5927\u5e05\u8fd8\u662f\u5728\u9662\u5b50\u91cc\u7ad9\u3002 \u3000\u3000\u5de5\u52a8\u4e5f\u7ad9\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u6211\u770b\u4f60\u4f60\u770b\u6211\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e2d\u5929\uff0c\u91d1\u5927\u5e05\u5ffd\u7136\u5c06\u624b\u91cc\u7684\u91d1\u5f13\u5f80\u5730\u4e0a\u7529\u5927\u6b65\u8d70\u5382\u8fdb\u6765\u91cd\u91cd\u7684\u5f80\u6905\u5b50\u4e0a\u5750\u3002 \u3000\u3000\u71d5\u4e03\u548c\u90ed\u5927\u8def\u4e5f\u5750\u5728\u90a3\u91cc\uff0c\u770b\u4ed6\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u5929\uff0c\u91d1\u5927\u5e05\u5ffd\u7136\u5927\u58f0\u8c0a\u201c\u9152\u5462\uff1f\u4f60\u4eec\u96be\u9053\u4ece\u6765\u4e0d\u559d\u9152\u7684\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u5076\u800c\u4e5f\u559d\u7684\u53ea\u4e0d\u8fc7\u5f88\u5c11\u559d\u6bcf\u5929\u5458\u591a\u4e5f\u53ea\u4e0d\u8fc7\u559d\u56db\u4e94\u6b21\u800c\u5df2\u3002\u559d\u5f97\u4e5f \u4e0d\u592a\u591a\u4e00\u6b21\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u559d\u4e03\u516b\u65a4\u3002\u201d \u3000\u3000\u9152\u5b50\u5df2\u4e0a\u4e86\u684c\u3002 \u3000\u3000\u4eca\u5929\u65e9\u4e0a\u5f53\u7136\u4e5f\u6709\u4eba\u9009\u4e86\u9152\u6765\xb7\u4ed6\u8ba2\u6ca1\u6709\u559d\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8fd8\u4e0d\u662f\u771f\u6b63\u7684\u9152\u9b3c\u3002 \u3000\u3000\u8fd8\u6ca1\u5f04\u6e05\u91d1\u5927\u5e05\u7684\u6765\u610f\uff0c\u4ed6\u4eec\u8c01\u4e5f\u4e0d\u613f\u559d\u9189\u3002 \u3000\u3000\u4f46\u91d1\u5927\u5e05\u5374\u5148\u559d\u4e86\u3002 \u3000\u3000\u4ed6\u559d\u9152\u4e5f\u771f\u6709\u4e9b\u5927\u5e05\u7684\u6c14\u6d3e\u4ef0\u8116\u5b50\u5c31\u662f\u5927\u8111\u3002 \u3000\u3000\u4ed6\u65e2\u5df2\u559d\u5382\u90ed\u5927\u8def\u4e49\u600e\u7518\u843d\u5f8c\u3002 \u3000\u3000\u5c31\u51ed\u4ed6\u559d\u9152\u7684\u6837\u5b50\u770b\u6765\u8fdf\u65e9\u603b\u6709\u5927\u4e5f\u4f1a\u6709\u4eba\u53eb\u4ed6\u5927\u5e05\u7684\u3002 \u3000\u3000\u820d\u5927\u5e05\u770b\u4ed6\u95e8\u6c14\u559d\u4e86\u4e03\u516b\u7897\u9152\uff0c\u5ffd\u7136\u7b11\u5382\u7b11\u9053\u201c\u770b\u8d77\u6765\u4f60\u6b21\u679c\u7136\u5475\u4ee5\u559d\u5f97\u4e0b\u4e03\u516b\u65a4\u9152 \u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u659c\u773c\u8198\u4ed6\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u5728\u5439\u725b\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u5f25\u672c\u6765\u5c31\u4e0d\u8c61\u662f\u4e2a\u8001\u5b9e\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u8bb8\u4e0d\u8c61\u4e2a\u8001\u5b9e\u4eba\u4f46\u6211\u5374\u662f\u4e2a\u8001\u5b9e\u4eba\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u7684\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4eec\u6bd4\u6211\u8fd8\u8001\u5b9e\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u4f60\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u4ed6\u4eec\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ece\u6765\u6ca1\u6709\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u77aa\u4ed6\u770b\u5382\u5f88\u4e45\xb7\u5ffd\u7136\u8f6c\u5411\u738b\u52a8\u9053\u201c\u4f60\u90a3\u624b\u6cd5\u771f\u4e0d\u662f\u4f60\u8001\u4e8e\u6559\u7684\uff1f\u201d\u5de5\u52a8\u9053\u201c\u4e0d \u662f\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u662f\u8c01\u6559\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u8bf4\u8fc7\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u600e\u9ebd\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u4ece\u6765\u6ca1\u6709\u544a\u8bc9\u8fc7\u6211\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u81f3\u5c11\u603b\u89c1\u8fc7\u4ed6\u7684\u6837\u5b50\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e5f\u6ca1\u6709\xb7\u56e0\u4e3a\u4ed6\u6559\u6211\u7684\u65f6\u5019\u603b\u662f\u5728\u665a\u4e0a\u800c\u4e14\u603b\u662f\u8499\u8138\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u76ee\u5149\u95ea\u52a8\u9053\u201c\u4f60\u662f\u8bf4\uff0c\u6709\u4e2a\u4e0d\u77e5\u9053\u8eab\u4efd\u7684\u795e\u79d8\u8499\u9762\u4eba\u6bcf\u5929\u665a\u4e0a\u6765\u627e\u4f60\u201d\u2026\u201c \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u662f\u6765\u627e\u6211\u662f\u6bcf\u5929\u665a\u4e0a\u5728\u575f\u573a\u90a3\u8fb9\u7684\u6811\u6797\u91cc\u7b49\u6211\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u5c31\u7b97\u522e\u98ce\u4e0b\u96e8\u4ed6\u4e5f\u7b49\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u9664\u4e86\u8fc7\u5e74\u7684\u90a3\u51e0\u5929\u5c31\u7b97\u5728\u51b7\u5f97\u773c\u6db8\u90fd\u53ef\u4ee5\u51bb\u6210\u51b0\u7684\u665a\u4e0a\u4ed6\u4e5f\u4f1a\u5728\u90a3\u91cc\u7b49\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4ed6\u4e0d\u8ba4\u5f97\u4f60\xb7\u4f60\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u4f46\u662f\u4ed6\u5374\u6bcf\u5929\u7b49\u4f60\xb7\u4e3a\u7684\u53ea\u4e0d\u8fc7\u5c06\u81ea\u5df1\u7684\u6b66 \u529f\u6559\u7ed9\u4f60\xb7\u800c\u4e14\u7edd\u4e0d\u8981\u4f53\u70b9\u62a5\u916c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u7b11\u9053\u201c\u4f60\u771f\u76f8\u4fe1\u5929\u4e0b\u6709\u8fd9\u9ebd\u597d\u7684\u4e8b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u82e5\u662f\u522b\u4eba\u8bb2\u7ed9\u6211\u542c\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\xb7\u4f46\u662f\u4e16\u4e0a\u5374\u5021\u504f\u6709\u8fd9\u79cd\u4e8b\u6211\u60f3\u4e0d\u60c5 \u4e5f\u4e0d\u884c\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u53c8\u77aa\u4ed6\u770b\u4e86\u534a\u5929\u9053\u201c\u4f60\u6709\u6ca1\u6709\u8ddf\u8e2a\u8fc7\u4ed6\u770b\u4ed6\u4f50\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u8bd5\u8fc7\uff0c\u4f46\u5374\u6ca1\u6709\u6210\u529f\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4ed6\u65e2\u7136\u6bcf\u5929\u90fd\u6765\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u4f50\u5f97\u5f88\u8fdc\u3002\u201d \u3000\u3000\u5929\u52a8\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u8fd9\u9644\u8fd1\u6709\u6ca1\u6709\u522b\u7684\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u5c71\u4e0a\u5c31\u53ea\u6709\u6211\u4eec\u5bb6\u4eba\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u4eec\u600e\u9ebd\u4f1a\u4f4f\u5728\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u5148\u7236\u559c\u6b22\u6e05\u9759\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u8fd9\u9644\u8fd1\u65e2\u6ca1\u6709\u522b\u7684\u4eba\u5bb6\uff0c\u90a3\u8499\u9762\u4eba\u96be\u9053\u662f\u4ece\u68fa\u6750\u91cc\u722c\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u4e5f\u8bb8\u4f50\u5728\u5c71\u8428\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u6709\u6ca1\u6709\u53bb\u627e\u8fc7\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5f53\u7136\u53bb\u627e\u8fc7\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f46\u4f60\u5374\u627e\u4e0d\u51fa\u4e2a\u4eba\u8c61\u662f\u6709\u90a3\u9ebd\u9ad8\u6b66\u529f\u7684\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5c71\u5382\u4f50\u7684\u4eba\u4e5f\u5e76\u4e0d\u592a\u591a\u5047\u5982\u771f\u6709\u90a3\u9ebd\u6837\u7684\u5546\u624b\u4f60\u81f3\u5c11\u603b\u53ef\u4ee5\u770b\u51fa\u70b9\u884c\u8e2a\u6765\u7684\u5bf9 \u4e0d\u5bf9\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u8bf4\u4ed6\u65e2\u7136\u6bcf\u5929\u665a\u4e0a\u90fd\u5728\u6559\u4f60\u6b66\u529f\uff0c\u767d\u5929\u603b\u8981\u7761\uff01 \u3000\u3000\u89c9\u7684\uff0c\u5728\u8fd9\u79cd\u5c0f\u57ce\u91cc\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u6bcf\u5929\u767d\u5929\u90fd\u5728\u7761\u89c9\xb7\u81ea\u7136\u5c31\u96be\u514d\u8981\u88ab\u4eba\u6ce8\u610f\uff0c\u5bf9\u4e0d \u5bf9\uff1f\u201d \u3000\u3000\u738b\u52a8\u8c0a\uff1a\u201c\u662d\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u65e2\u7136\u5982\u6b64\xb7\u4f60\u4e3a\u4ec0\u9ebd\u627e\u4e0d\u51fa\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e5f\u8bb8\u4ed6\u6839\u672c\u4e0d\u4f4f\u5728\u57ce\u91cc\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u65e2\u4e0d\u662f\u4efb\u5728\u5c71\u4e0a\uff0c\u53c8\u4e0d\u662f\u4f4f\u5728\u57ce\u91cc\u4ed6\u8fd8\u80fd\u4f50\u5728\u4ec0\u9ebd\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u771f\u6b63\u7684\u9ad8\u624b\xb7\u65e0\u8bba\u5728\u4ec0\u9ebd\u5730\u65b9\u90fd\u53ef\u4ee5\u7761\u89c9\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u5c31\u7b97\u4ed6\u5708\u5728\u5c71\u6d1e\u91cc\u7761\u89c9\xb7\u4f46\u5403\u996d\u5462\uff1f\u65e0\u8bba\u4ec0\u9ebd\u6837\u7684 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u53ef\u4ee5\u5230\u57ce\u91cc\u4e70\u996d\u5403\u3001\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u6bcf\u5929\u90fd\u5728\u5916\u9762\u5403\u996d\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4f50\u5728\u90a3\u91cc\u5c82\u975e\u66f4\u52a0\u7684\u8981\u88ab \u4eba\u6ce8\u610f\u201d \u3000\u3000\u738b\u52a8\u4e5f\u56de\u77aa\u4ed6\xb7\u770b\u4e86\u5f88\u4e45\u51b7\u51b7\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u4ece\u8d70\u8fdb\u5927\u95e8\u5f8c\u76f4\u5230\u73b0\u5728\u4e00\u5171\u95ee\u4e86\u591a\u5c11\u53e5 \u8bdd\u4e86\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\uff0c\u201c\u6062\u662f\u4e0d\u662f\u5acc\u6211\u95ee\u5f97\u592a\u591a\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u5947\u602a\u4f60\u4e3a\u4ec0\u9ebd\u4e00\u5b9a\u8981\u95ee\u8fd9\u4e9b\u8ddf\u4f60\u70b9\u5173\u7cfb\u4e5f\u6ca1\u6709\u7684\u95ee\u9898\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u5ffd\u53c8\u7b11\u4e86\u7b11\u53d8\u5f97\u4eff\u4f5b\u72e0\u795e\u79d8\uff0c\u4e00\u53e3\u6c14\u53c8\u559d\u4e86\u53c1\u7897\u9152\uff0c\u624d\u7f13\u7f13\u5730\u8bf4\u9053\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5 \u9053\u90a3\u8499\u9762\u5165\u662f\u8c01\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5f53\u7136\u60f3\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u201c\u65e2\u7136\u60f3\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u6211\u5c31\u7b97\u95ee\u4e86\u4e5f\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9992\u9992\u7684\u70b9\u4e86\u70b9\u5934\u9053\u201c\u4e0d\u9519\xb7\u8fd9\u4e16\u4e0a\u7684\u786e\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u9664\u4e86\u4ed6\u81ea\u5df1\u5916\u6839\u672c\u6ca1\u6709\u522b\u7684\u4eba\u77e5\u9053\u8fde\u2500\u4e2a\u4eba\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6709\u4e2a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff0c\u201c\u8c01\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6211\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u8fde\u71d5\u4e03\u90fd\u6014\u4f50\u4e86\u3002\u738b\u52a8\u6014\u4e86\u534a\u664c\uff0c\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5df2\u7ecf\u662f\u591a\u4e45\u4ee5\u524d\u7684 \u4e8b\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d\u738b\u52a8\u9053\u201c\u4f46\u4f60\u5374\u77e5\u9053\u4ed6\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u65e2\u7136\u6c72\u6709\u770b\u89c1\u8fc7\u4ed6\u751a\u81f3\u8fde\u8fd9\u4ef6\u4e8b\u662f\u4ec0\u9ebd\u65f6\u5019\u53d1\u751f\u90fd\u4e0d\u77e5\u9053\xb7\u4f46\u4f60\u5374\u77e5\u9053\u4ed6\u662f \u8c01\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u52a8\u51b7\u7b11\u9053\u201c\u4f60\u771f\u76f8\u4fe1\u5929\u4e0b\u4f1a\u6709\u8fd9\u79cd\u4e8b\u201d \u3000\u3000\u91d1\u5927\u8111\u9053\u201c\u6211\u60f3\u4e0d\u4fe1\u90fd\u4e0d\u884c\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u51ed\u4ec0\u9ebd\u80fd\u5982\u6b64\u786e\u5b9a\u201d \u3000\u3000\u91d1\u5927\u5e05\u6c49\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u53c8\u5148\u559d\u4e86\u53c1\u9152\u624d\u7f13\u7f13\u5730\u95ee\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u7684\u8fde\u73e0\u5f39\u8f6e\u8fde\u53d1\u591a \u5c11\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e8c\u5341\u4e2a\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u53c8\u5341\u4e00\u53d1\u8fde\u73e0\u5f39\u4e2d\u54ea\u51e0\u53d1\u5feb\uff1f\u56db\u51e0\u53d1\u6162\uff1f\u53c8\u6709\u51e0\u53d1\u662f\u53d8\u5316\u65cb\u8f6c \u7684\uff1f\u51e0\u53d1\u662f\u6e2f\u5907\u4e92\u76f8\u6377\u51fb\u7684\uff1f\u201d\u738b\u52a8\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4f60\u8fde\u8fd9\u70b9\u90fd\u4e0d\u77e5\u9053\u600e\u80fd\u63a5\u5f97\u4f50\u6211\u7684\u8fde\u71ac\u5f39\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u53c8\u6014\u4f50\u3002 \u3000\u3000\u91d1\u5927\u5e05\u201c\u6211\u4ee5\u8fde\u73e0\u5f39\u6210\u540d\uff0c\u81f3\u4eca\u5df2\u6709\u53c1\u5341\u5e74\u6c5f\u6e56\u4e2d\u4eba\u80fd\u95ea\u907f\u62db\u67b6\u7684\u4eba\u5df2\u4e0d\u591a\xb7\u4f97\u4f60\u5374\u968f \u968f\u4fbf\u4fbf\u5c31\u63a5\u4efb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u66f0\u6c14\uff0c\u4e49\u9053\u201c\u975e\u4f46\u4f60\u63a5\u4f4f\u5382\xb7\u8fde\u4f60\u6559\u51fa\u6765\u7684\u4eba\u90fd\u80fd\u63a5\u4efb\u7b80\u76f4\u5c31\u62ff\u6211\u8fd9\u8fde\u73e0\u5f39\u5f53\u5c0f \u5b69\u73a9\u7684\u6837\u4f60\u96be\u9053\u70b9\u4e5f\u4e0d\u89c9\u5f97\u5947\u602a\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u53c8\u6014\u5382\u534a\u664c\u6c89\u541f\u9053\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u6211\u7684\u6cd5\u5b50\u7528\u5bf9\u4e86\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u5ffd\u7136\u62cd\u684c\u4e88\xb7\u9053\u201c\u4e0d\u9519\u4f60\u7528\u7684\u4e0d\u4f46\u662f\u6700\u6b63\u786e\u7684\u79cd\u6cd5\u5b50\u4e5f\u662f\u6700\u5de7\u5999\u7684\u79cd\u624b\u6cd5\u8fd9\u79cd\u624b \u6cd5\u4e0d\u4ec5\u53ef\u4ee5\u7834\u6211\u7684\u8fde\u73e0\u5f39\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u5929\u4e0b\u6240\u6709\u6697\u5668\u7684\u514b\u5c4b\u3002\u201d \u3000\u3000\u4e0a\u52a8\u53ea\u6709\u542c\u56e0\u4e3a\u8fde\u4ed6\u81ea\u5df2\u5b9e\u5728\u4e5f\u4e0d\u77e5\u9053\u8fd9\u79cd\u624b\u6cd5\u7adf\u662f\u5982\u6b64\u5965\u5999\u3002 \u3000\u3000\u91d1\u5927\u5e05\u770b\u4ed6\xb7\u53c8\u95ee\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u4e16\u4e0a\u4f1a\u8fd9\u79cd\u624b\u6cd5\u7684\u4eba\u6709\u51e0 \u3000\u3000\u4e0a\u52a8\u6447\u6447\u5934\u3002 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u53ea\u98df\u4e2a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u957f\u957f\u53f9\u606f\u5382\u58f0\u7f13\u7f13\u9053\u201c\u6211\u627e\u8fd9\u4e2a\u4eba\xb7\u5df2\u7ecf\u627e\u5382\u5341\u51e0\u5e74 \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u9ebd\u8981\u627e\u4ed6\uff1f\u201d\u91d1\u5927\u5e05\u9053\u201c\u56e0\u4e3a\u6211\u5e73\u4e2d\u4e0e\u4eba\u4ea4\u624b\u8d25\u5f97\u6700\u60e8\u7684\u6b21\uff0c\u5c31 \u662f\u8d25\u5728\u4ed6\u624b\u2606\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u60f3\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u90a3\u5012\u5e76\u5c0f\u5b8c\u5168\u662f\u4e3a\u4e86\u62a5\u4ec7\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u662f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6211\u7684\u8fde\u73e0\u5f39\u65e2\u7136\u6709\u4eba\u80fd\u7834\u81ea\u7136\u5c31\u6709\u7f3a\u70b9\u4f46\u662f\u6211\u60f3\u4e86\u51e0\u5341\u5e74\uff0c\u8fd8\u662f\u60f3\u4e0d\u51fa\u5176\u4e2d \u7684\u5173\u952e\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u65e2\u7136\u80fd\u7834\u4f60\u7684\u8fde\u73e0\u5f39\uff0c\u60f3\u5fc5\u5c31\u5b9a\u77e5\u9053\u4f60\u7684\u7f3a\u70b9\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8ba4\u4e3a\u90a3\u8499\u9762\u4eba\u5c31\u662f\u4ed6\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u8bf4\u9053\u201c\u7edd\u5bf9\u662f\u4ed6\u7edd\u4e0d\u53ef\u80fd\u518d\u6709\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u4f60\u63a5\u6211\u8fde\u73e0 \u3000\u3000\u738b\u52a8\u76ee\u4e2d\u5df2\u9732\u51fa\u6025\u5207\u76fc\u671b\u4e4b\u8272\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u66f4\u6025\xb7\u62a2\u9053\u201c\u4f60\u8bf4\u6765\u8bf4\u53bb\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u51dd\u89c6\u738b\u52a8\u5b87\u5b57\u9053\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u738b\u6f5c\u77f3\u5c31\u662f\u4f60\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u5c31\u7b97\u50ac\u547d\u7b26\u4ece\u575f\u5893\u91cc\u4f38\u624b\u51fa\u6765\u5c06\u4ed6\u628a\u6293\u4f4f\u7684\u65f6\u5019\u738b\u52a8\u8138\u4e0a\u7684\u8868\u60c5\u4e5f\u6ca1\u6709\u73b0\u5728\u8fd9\u9ebd\u60ca\u8bb6\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u6bd4\u4ed6\u66f4\u60ca\u8bb6\u62a2\u9053\u201c\u4f60\u8bf4\u90a3\u8499\u9762\u4eba\u5c31\u662f\u4ed6\u7684\u7236 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u7edd\u5bf9\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u4ed6\u7236\u4eb2\u4e0d\u5728\u5bb6\u91cc\u6559\u4ed6\u529f\u592b\xb7\u5374\u8981\u8499\u8d77\u8138\u5728\u5916\u9762\u7684\u6811\u6797\u5b50\u91cc\u7b49\u4ed6\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u7b11\u4e49\u7b11\u4e0d\u51fa\uff0c\u5374\u56db\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u4f60\u771f\u76f8\u4fe1\u4e16\u4e0a\u6709\u8fd9\u79cd\u602a\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\uff0c\u201c\u8fd9\u4ef6\u4e8b\u5e76\u4e2a\u80fd\u7b97\u5947\u602a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd8\u4e0d\u7b97\u5947\u602a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u4ec0\u9ebd\u9053\u7406\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u62e8\u6de1\u5730\u9053\u201c\u6211\u672c\u6765\u4e5f\u60f3\u4e0d\u901a\u7684\u4f46\u770b\u5230\u4ed6\u4f4d\u5728\u8fd9\u79cd\u5730\u65b9\u5c31\u60f3\u51fa\u4e86\u70b9\u770b\u5230\u4f60\u4eec\u8fd9\u4e9b\u670b \u53cb\u53c8\u60f3\u51fa\u4e86\u7b2c\u4e8c\u70b9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u5148\u8bf4\u7b2c\u70b9\u3002\u201d\u91d1\u5927\u5e05\u9053\u201c\u5de5\u6ee1\u77f3\u5c11\u5e74\u65f6\u8fd8\u6709\u4e2a\u540d\u5b57\u53eb\u738b\u4f0f\u96f7\u90a3\u610f\u601d\u5c31\u662f\u8bf4 \u5c31\u7b97\u662f\u5929\u4e0a\u51fb\u4e0b\u6765\u7684\u96f7\u7535\u4ed6\u4e5f\u6837\u80fd\u63a5\u5f97\u4efb\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5c3d\u676f\u63a5\u9053\u201c\u8fd9\u540d\u5b57\u867d\u7136\u56a3\u5f20\u4f46\u4ed6\u4e8c\u5341\u53c1\u5c81\u65f6\u5df2\u88ab\u6b66\u6797\u4e2d\u516c\u8ba4\u4e3a\u5929\u4e0b\u63a5\u6697\u5668\u7684\u7b2c\u9ad8 \u624b\uff0c\u5c31\u7b97\u72c2\u5984\u4e9b\u522b\u4eba\u4e5f\u6ca1\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u5728\u542c\xb7\u8fde\u90ed\u5927\u8def\u90fd\u6ca1\u6709\u63d2\u53e3\u3002 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u7b49\u4ed6\u5e74\u7eaa\u5927\u4e86\u4e9b\uff0c\u52b2\u6c14\u5185\u653f\u624d\u6539\u540d\u4e3a\u738b\u6f5c\u77f3\u90a3\u65f6\u4ed6\u5df2\u7ecf\u5f88\u5c11\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8 \u4e86\uff0c\u53c8\u8fc7\u4e86\u4e24\u5e74\uff0c\u5c31\u5ffd\u7136\u5931\u7efc\u3002\u201d \u3000\u3000\u5230\u8fd9\u65f6\u90ed\u5927\u8def\u624d\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\u201c\u90a3\u60f3\u5fc5\u662f\u56e0\u4e3a\u4ed6\u5df2\u538c\u5026\u4e86\u6c5f\u6e56\u95f4\u7684\u4e89\u6740\u6240\u4ee5\u5c31\u9000\u9690\u5230\u6797 \u4e0b\u8fd9\u79cd\u4e8b\u81ea\u53e4\u5c31\u6709\u5f88\u591a\xb7\u4e5f\u4e0d\u80fd\u7b97\u5947\u602a\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u6447\u4e86\u6447\u5934\xb7\u9053\u201c\u8fd9\u5012\u5e76\u4e0d\u662f\u6700\u4e3b\u8981\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6700\u4e3b\u8981\u7684\u662f\uff0c\u4ed6\u7ed3\u4e86\u4e2a\u6781\u5389\u5bb3\u7684\u4ec7\u5bb6\uff0c\u4ed6\u81ea\u77e5\u7edd\u4e0d\u662f\u8fd9\u4eba\u7684\u654c\u624b\u6240\u4ee5\u624d\u9690\u59d3 \u57cb\u540d\xb7\u9000\u9690\u5230\u8fd9\u79cd\u8352\u50fb\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u9053\u201c\u4ed6\u7684\u4ec7\u5bb6\u662f\u8c01\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u5c31\u56e0\u4e3a\u4ed6\u4e0d\u613f\u8ba9\u4f60\u77e5\u9053\u4ed6\u4ec7\u5bb6\u662f\u8c01\uff0c\u6240\u4ee5\u624d\u4e0d\u80af\u4eb2\u81ea\u51fa\u9762\u6559\u4f60\u6b66\u529f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u56e0\u4e3a\u4f60\u82e5\u77e5\u9053\u4ed6\u8fc7\u53bb\u7684\u4e8b\u8fdf\u65e9\u603b\u4f1a\u542c\u5230\u4ed6\u7ed3\u4ec7\u7684\u7ecf\u8fc7\xb7\u4f60\u82e5\u77e5\u9053\u4ed6\u7684\u4ec7\u5bb6\u662f \u8c01\xb7\u5c11\u5e74\u4eba\u76bf\u6c14\u65b9\u521a\xb7\u81ea\u7136\u96be\u514d\u8981\u53bb\u5bfb\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f46\u4ed6\u8fd9\u4ec7\u5bb6\u5b9e\u5728\u592a\u53ef\u6015\u975e\u4f46\u4fae\u7edd\u4e0d\u662f\u654c\u624b\uff0c\u6c5f\u6e56\u4e2d\u53ea\u4f2f\u8fd8\u6ca1\u6709\u4e2a\u4eba\u80fd\u63a5 \u5f97\u4f4f\u4ed6\u4e94\u5341\u62db\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff0c\u9053\u2019\u6211\u60f3\u77e5\u9053\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u73b0\u5728\u4f60\u77e5\u9053\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u56e0\u4e3a\u4ed6\u7eb5\u7136\u5df2\u5929\u4e0b\u65e0\u654c\uff0c\u5374\u8fd8\u771f\u6709\u51e0\u6837\u65e0\u6cd5\u62b5\u6297\u7684\u7b97\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ec0\u9ebd\u4e8b\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u8005\u3001\u75c5\u3001\u6b7b\u201d \u3000\u3000\u738b\u52a8\u52a8\u5bb9\u9053\u201c\u4ed6\u75c5\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u957f\u53f9\u9053\u201c\u53e4\u5f80\u4eca\u6765\u7684\u82f1\u9525\u8c6a\u6770\xb7\u53c8\u6709\u6e2f\u80fd\u591f\u9003\u5f97\u8fc7\u8fd9\u5173\u5462\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53ef\u662f\u4ed6\u7a76\u7adf\u201d\u4e00\u201c \u3000\u3000\u91d1\u5927\u5e05\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\u201c\u4ed6\u7684\u4eba\u65e2\u5df2\u6b7b\u4e86\u540d\u5b57\u4e5f\u968f\u957f\u57cb\u4e8e\u5730\u4e0b\uff0c\u4f60\u53c8\u4f55\u5fc5\u518d\u95ee\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u738b\u52a8\u5f00\u53e3\uff0c\u5f88\u5feb\u7684\u63a5\u53c8\u9053\u201c\u81ea\u4ece\u5230\u4e86\u8fd9\u91cc\u4e4b\u5f8c\u738b\u4f0f\u96f7\u8fd9\u4e2a\u4eba\u4e5f\u5df2\u7b97\u6b7b\u4e86\u6240\u4ee5\u5c31\u7b97 \u5728\u81ea\u5df1\u7684\u513f\u5b50\u9762\u524d\xb7\u4e5f\u7edd\u53e3\u4e0d\u80af\u518d\u63d0\u6b66\u529f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u662f\u7b2c\u4e00\u70b9\u3002\u201d \u3000\u3000\u91d1\u5927\u8111\u9053\u201c\u770b\u5230\u4f60\u4eec\u8fd9\u79cd\u670b\u53cb\u5c31\u53ef\u4ee5\u60f3\u89c1\u738b\u52a8\u5c0f\u65f6\u5019\u5fc5\u5b9a\u4e5f\u662f\u4e2a\u72e0\u987d\u76ae\u7684\u5b69\u4e88\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u867d\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u8138\u4e0a\u7684\u8868\u60c5\u5378\u5df2\u65e0\u5f02\u66ff\u738b\u52a8\u627f\u8ba4\u4e86\u3002 \u3000\u3000\u91d1\u5927\u80f8\u9053\u201c\u987d\u76ae\u7684\u5b69\u5b50\u968f\u65f6\u90fd\u53ef\u4ee5\u95ef\u7978\uff0c\u738b\u6f5c\u77f3\u751f\u4f2f\u81ea\u5df1\u7684\u513f\u5b50\u4f1a\u5403\u4e8f\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u60f3\u6559 \u4ed6\u4e00\u4e9b\u9632\u8eab\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u9053\u201c\u4f46\u82e5\u8981\u4e2a\u987d\u76ae\u7684\u5b69\u5b50\u597d\u597d\u7684\u5728\u5bb6\u5b66\u6b66\uff0c\u90a3\u7b80\u76f4\u6bd4\u6536\u4f0f\u4e00\u5339\u91ce\u9a6c\u8fd8\u56f0\u96be\u5f97 \u591a\uff0c\u6240\u4ee5\u738b\u6f5c\u77f3\u624d\u60f3\u51fa\u8fd9\u4e2a\u6cd5\u4e88\uff0c\u65e2\u4e0d\u5fc5\u9732\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u53c8\u53ef\u4ee5\u6fc0\u8d77\u738b\u52a8\u5b66\u6b66\u7684\u5174\u8da3\u5b69\u5b50\u4eec \u5bf9\u4e00\u4e9b\u795e\u79d8\u7684\u4e8b\u5174\u8da3\u603b\u662f\u7279\u522b\u6d53\u539a\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u9053\u201c\u83ab\u8bf4\u662f\u5b69\u5b50\xb7\u5927\u4eba\u4e5f\u6837\u3002\u201d \u3000\u3000\u9ed1\u52e4\u98a4\u7684\u665a\u4e0a\u575f\u573a\u65c1\u7684\u8352\u6797\uff0c\u8fd8\u6709\u8499\u9762\u7684\u6b66\u6797\u9ad8\u624b\u201d\u4e00\u201d \u3000\u3000\u8c61\u8fd9\u9ebd\u6837\u7684\u795e\u79d8\u7684\u4e8b\u53ea\u4f2f\u8fde\u8003\u5934\u5b50\u90fd\u65e0\u6cd5\u4e0d\u52a8\u5fc3\u3002 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u8fd9\u4ef6\u4e8b\u73b0\u5728\u4f60\u4eec\u8be5\u5b8c\u5168\u660e\u767d\u4e86\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd8\u6709\u4e00\u70b9\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u5ce8\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u738b\u8001\u4f2f\u7684\u5fc3\u610f\xb7\u4f60\u600e\u9ebd\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u56e0\u4e3a\u6211\u4e5f\u662f\u505a\u7236\u4eb2\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\uff0c\u63a5\u9053\u201c\u7236\u4eb2\u5bf9\u513f\u5b50\u7684\u7231\u5fc3\u548c\u82e6\u5fc3\uff0c\u4e5f\u53ea\u6709\u505a\u7236\u4eb2\u7684\u4eba\u624d\u80fd\u4f53\u4f1a\u5f97\u5230\u3002\u201d \u3000\u3000\u738b\u52a8\u7a81\u7136\u7ad9\u8d77\u6765\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u60f3\u627e\u4e2a\u6ca1\u4eba\u7684\u5730\u65b9\u53bb\u75db\u54ed\u573a\uff1f \u3000\u3000\u71d5\u4e03\u672c\u5c31\u76f4\u5782\u5934\u7684\uff0c\u73b0\u5728\u90ed\u5927\u8def\u7684\u5934\u4e5f\u5782\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u505a\u513f\u5b50\u7684\u4eba\uff0c\u4e3a\u4ec0\u9ebd\u603b\u8981\u7b49\u5230\u5df2\u8ffd\u6094\u83ab\u53ca\u65f6\u624d\u80fd\u4e86\u89e3\u7236\u4eb2\u5bf9\u4ed6\u7684\u7231\u5fc3\u548c\u82e6\u5fc3\u5462\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u770b\u4ed6\u4eec\u5ffd\u7136\u4e3e\u8d77\u9152\u676f\u5927\u58f0\u9053\u201c\u4f60\u4eec\u96be\u9053\u4ece\u6765\u4e0d\u559d\u9152\u7684\uff1f\u201d \u3000\u3000\u4e16\u4e0a\u7684\u786e\u6709\u5f88\u591a\u5947\u602a\u800c\u795e\u79d8\u7684\u95ee\u9898\u90fd\u2026\u5b9a\u6709\u7b54\u6848\u7684\xb7\u5c31\u6b63\u5982\u5730\u4e0b\u4e00\u5b9a\u6709\u6cc9\u6c34\u548c\u9ec4\u91d1\uff0c\u4e16 \u4e0a\u5b9a\u6709\u516c\u9053\u548c\u6b63\u4e49\u4eba\u95f4\u5b9a\u6709\u53cb\u60c5\u548c\u6e29\u6696\u3002 \u3000\u3000\u4f60\u5c31\u7b97\u770b\u4e0d\u5230\u542c\u4e0d\u5230\uff0c\u627e\u4e0d\u5230\uff0c\u4e5f\u7edd\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u5b83\u7684\u5b58\u5728\u3002\u53ea\u8981\u4f60\u76f8\u4fe1\xb7\u5c31\u603b\u4f1a\u627e\u5230\u7684 \u5929\u3002 \u3000\u3000\u201c\u4e16\u4e0a\u6709\u6ca1\u6709\u4ece\u6765\u4e0d\u9189\u7684\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u6700\u6b63\u786e\u7684\u7b54\u6848\u662f\u201c\u6709\u3002\u201d \u3000\u3000\u4ece\u6765\u4e0d\u559d\u9152\u7684\u4eba\uff0c\u5c31\u7edd\u4e0d\u4f1a\u9189\u7684\u3002 \u3000\u3000\u53ea\u8981\u4f60\u559d\xb7\u4f60\u5c31\u4f1a\u9189\uff0c\u4f60\u82e5\u4e0d\u505c\u7684\u874e\u4e0b\u53bb\uff0c\u5c31\u975e\u9189\u4e0d\u53ef\u3002\u6240\u4ee5\u90ed\u5927\u8def\u9189\u4e86\u3002 \u3000\u3000\u91d1\u5927\u5e05\u7684\u5934\u597d\u50cf\u5728\u4e0d\u505c\u7684\u6444\u6765\u6447\u53bb\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u91d1\u5927\u5e05\u8fde\u70b9\u90fd\u4e0d\u8c61\u662f\u4e2a\u5927\u5e05\xb7\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u624d\u771f\u7684\u662f\u4e2a\u5927\u5e05\u800c\u4e14\u662f\u4e2a\u5927\u5e05\u4e2d \u7684\u5927\u5e05\u3002 \u3000\u3000\u91d1\u5927\u5e05\u4e5f\u5728\u770b\u4ed6\xb7\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5934\u4e3a\u4ec0\u9ebd\u8981\u4e0d\u505c\u7684\u6447\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u5927\u7b11\u9053\u201c\u4f60\u770b\u8fd9\u4e2a\u4eba\u660e\u660e\u662f\u4ed6\u81ea\u5df1\u7684\u5934\u5728\u6447\u8fd8\u8bf4\u4eba\u5bb6\u7684\u5934\u5728\u6447\u3002\u7f57 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u4eba\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4eba\u5bb6\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u660e\u660e\u662f\u4f60\u4e3a\u4ec0\u9ebd\u53c8\u662f\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u6700\u5927\u7684\u6bdb\u75c5\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u4e5f\u60f3\u5382\u60f3\xb7\u95ee\u9053\u201c\u662f\u4e0d\u662f\u6211\u7684\u9152\u559d\u5f97\u592a\u591a\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u77f3\u662f\u9152\u559d\u5f97\u592a\u591a\u662f\u95ee\u8bdd\u592a\u591a\u7b80\u76f4\u53eb\u4eba\u53d7\u4e0d\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u5927\u7b11\xb7\u9053\u201c\u597d\u5427\xb7\u6211\u4e0d\u95ee\u8bf4\u4e0d\u95ee\u5c31\u4e0d\u95ee\u2026\u2026\u6211\u80fd\u4e0d\u80fd\u518d\u95ee\u6700\u5f8c\u6b21\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u95ee\u5427\u3002\u201d\u91d1\u5927\u5e05\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u8fd9\u6b21\u6765\u7a76\u7adf\u662f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\xb7\u5927\u7b11\u9053\u201c\u4f60\u770b\u8fd9\u4e2a\u4eba\uff1f\u4ed6\u81ea\u5df1\u6765\u8981\u4e8e\u4ec0\u9ebd\u8fde\u4ed6\u8089\u5df1\u90fd\u4e0d\u77e5\u9053\uff0c\u5374\u53cd\u800c\u8981 \u6765\u95ee\u6211\uff0c\u6211\u53c8\u4e0d\u662f\u4ed6\u809a\u5b50\u91cc\u7684\u86d4\u5965\uff0c\u6211\u600e\u9ebd\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u597d\u50cf\u6839\u672c\u6ca1\u542c\u89c1\u4ed6\u5728\u8bf4\u4ec0\u9ebd\uff0c\u773c\u775b\u671b\u81ea\u5df1\u624b\u91cc\u7684\u7a7a\u7897\u5c31\u597d\u50cf\u968f\u65f6\u8981\u54ed\u51fa\u6765\u7684\u6837 \u4e8e\u3002\u8fc7\u4e86\u5f88\u4e45\u529b\u7f13\u7f13\u9053\u201c\u6211\u5728\u5bb6\u91cc\u53c8\u7ec3\u4e86\u5341\u5e74\u8fde\u73e0\u5f39\u4ee5\u4e3a\u5df2\u7ecf\u53ef\u4ee5\u5bf9\u4ed8\u738b\u4f0f\u96f7\u4e86\u8c01\u77e5\u8fde\u4ed6\u7684 \u513f\u5e72\u90fd\u5bf9\u4ed8\u4e0d\u4e86\u6211\u2026\u3002\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8df3\u8d77\u6765\u4eff\u4f5b\u4e5f\u60f3\u51b2\u51fa\u53bb\u627e\u4e2a\u6ca1\u4eba\u7684\u5730\u65b9\u75db\u54ed\u573a\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7b49\u7b49\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u77aa\u773c\u9053\u201c\u8fd8\u7b49\u4ec0\u9ebd\uff1f\u7b49\u518d\u4e22\u6b21\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6307\u684c\u4e0a\u5927\u6ca5\u7897\u91cc\u7684\u91d1\u5f39\u5b50\u9053\u201c\u4f60\u8981\u8d70\u4e5f\u5f97\u628a\u8fd9\u4e9b\u4e1c\u897f\u5e26\u8d70\u3002\u201d \u3000\u3000\u6c64\u7897\u91cc\u88c5\u7684\u672c\u662f\u7ea2\u70e7\u8089\uff0c\u662f\u4ed6\u5c06\u91d1\u5f39\u5b50\u5012\u8fdb\u53bb\u7684\u3002 \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u5e26\u8d70\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u4e9b\u4e1c\u897f\u672c\u6765\u662f\u4f60\u7684\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u201c\u8c01\u8bf4\u662f\u6211\u7684\uff1f\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\u95ee\u5b83\u770b\u5b83\u59d3\u4e0d\u59d3\u91d1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u91d1\u5927\u5e05\u7a81\u53c8\u5927\u7b11\xb7\u9053\u201c\u8fd9\u4e1c\u897f\u65e2\u4e0d\u662f\u7ea2\u70e7\u8089\u4e5f\u4e0d\u662f\u8089\u4e38\u5b50\uff0c \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4ee5\u5f8c\u96be\u9053\u4e0d\u7528\u8fde\u73e0\u5f39\u4e86\uff1f\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\uff0c\u201c\u8c01\u4ee5\u5f8c\u7528\u8fde\u73e0\u5f39\uff0c\u8c01\u5c31\u662f\u9f9f\u5b59\u5b50\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u8ddf\u8ddf\u8def\u8dea\u7684\u51b2\u4e86\u51fa\u53cb\u51b2\u5230\u95e8\u53e3\u7a81\u4e49\u56de\u8fc7\u5934\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4ee5\u524d\u4e3a\u4ec0\u9ebd\u559c\u6b22\u7528\u91d1 \u5f39\u5b50\u6253\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u9053\u54bd\u4e3a\u91d1\u5b50\u672c\u662f\u4eba\u4eba\u90fd\u559c\u6b22\u7684\u82e5\u7528\u91d1\u5b50\u6253\u4eba\u522b\u4eba\u603b\u662f\u60f3\u63a5\u8fc7\u6765\u770b\u770b\u5c31\u5fd8\u4e86\u95ea\u907f\u8981 \u63a5\u4f4f\u5b83\u603b\u6bd4\u907f\u5f00\u5b83\u56f0\u96be\u4e9b\u4f55\u51b5\u91d1\u4e8e\u8fd8\u80fd\u4f7f\u4eba\u773c\u82b1\u7ec7\u4e71\uff0c\u6240\u4ee5\u65e0\u8bba\u8c01\u7528\u91d1\u5b50\u505a\u6697\u5668\u5b9a\u4f1a\u5360\u5f88\u5927 \u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u7528\u4e86\u5462\uff1f\u201d \u3000\u3000\u91d1\u4eba\u5e05\u53c8\u60f3\u5382\u60f3\u9053\u201c\u56e0\u4e3a\u5360\u4fbf\u5b9c\u5c31\u662f\u5403\u4e8f\uff0c\u5403\u4e8f\u624d\u662f\u5360\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u770b\u6765\u4f60\u5e76\u6ca1\u6709\u559d\u9189\u4f60\u8bf4\u8bdd\u8fd8\u6e05\u695a\u5f97\u5f88\u3002\u201d \u3000\u3000\u91d1\u5927\u5e05\u77aa\u773c\u9053\u201c\u6211\u5f53\u7136\u6ca1\u9189\u8c01\u8bf4\u6211\u559d\u915a\u4e86\u8c01\u5c31\u662f\u9f9f\u5b59\u5b50\u7684\u5b59\u5b50\u4e2d \u3000\u3000\u91d1\u5927\u5e05\u7ec8\u4e01\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u786e\u70b9\u4e5f\u6ca1\u6709\u9189\uff0c\u53ea\u4e0d\u8fc7\u9189\u4e86\u516b\u4e5d\u5206\u800c\u5df4\u3002 \u3000\u3000\u7ff1\u5927\u8def\u5462\uff1f \u3000\u3000\u4ed6\u6b63\u5728\u770b\u770b\u7897\u91cc\u7684\u91d1\u5f39\u5b50\u53d1\u6014\uff0c\u6014\u4e86\u534a\u5929\u624d\u53f9\u4e86\u53e3\u6c14\u54fa\u54fa\u9053\u201c\u4ee5\u4e0a\u6709\u4e9b\u4e1c\u897f\u771f\u5947\u602a\uff0c\u4f60 \u60f3\u8981\u5b83\u7684\u65f6\u5019\xb7\u4e2a\u4e5f\u6ca1\u6709\u4e0d\u60f3\u5b83\u7684\u65f6\u5019\u504f\u504f\u6765\u4e86\u5927\u5806\u4f60\u8bf4\u8981\u547d\u4e0d\u8981\u547d\u3002\u201d \u7b2c\u5341\u4e94\u7ae0\u3000\u9b3c\u516c\u5b50>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u9b3c\u516c\u5b50 \u3000\u3000\u5047\u5982\u4f60\u4f50\u5728\u4e2a\u5f88\u8352\u50fb\u7684\u5730\u65b9\u3002 \u3000\u3000\u5047\u5982\u6709\u4e2a\u4eba\u5728\u534a\u591c\u53c1\u66f4\u91cc\u6765\u6572\u4f60\u7684\u95e8\u4f46\u5ba2\u6c14\u7684\u5bf9\u4f60\u8bf4\u201c\u6211\u53c8\u7d2f\u53c8\u6e34\u53c8\u9519\u8fc7\u4e86\u5bbf\u5934\u60f3\u5728\u4f60 \u4eec\u8fd9\u91cc\u501f\u5bbf\u5bb5\uff0c\u8ba8\u70b9\u6c34\u559d\u3002\u201d \u3000\u3000\u90a3\u9ebd\u53ea\u8981\u4f60\u662f\u4e2a\u4eba\u4f60\u5c31\u5b9a\u4f1a\u8bf4\u201c\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u5e73\u65f6\u5c31\u662f\u4e2a\u5f88\u8c6a\u723d\u3001\u5f88\u597d\u5ba2\u7684\u4eba\u559d\u4e86\u9152\u4e4b\u5f8c\u5c31\u6bd4\u5e73\u65f6\u66f4\u8c6a\u723d\xb7\u66f4\u597d\u5ba2\u5341\u500d\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u559d\u4e86\u9152\uff0c\u800c\u4e14\u559d\u5f97\u771f\u4e0d\u5c11\u3002 \u3000\u3000\u91d1\u5927\u5e05\u521a\u8d70\u4e86\u6295\u591a\u4e45\uff0c\u4ed6\u5c31\u542c\u5230\u6572\u95e8\u5c31\u62a2\u51fa\u53bb\u5f00\u95e8\u3002 \u3000\u3000\u6572\u95e8\u7684\u4eba\u5c31\u5ba2\u6c14\u7684\u5bf9\u4ed6\u8bf4\u201c\u6211\u53c8\u7d2f\u53c8\u6e34\u53c8\u9519\u8fc7\u4e86\u5bbf\u5934\u2019\u60f3\u5728\u8fd9\u91cc\u501f\u5bbf\u5bb5\uff0c\u8ba8\u70b9\u6c34\u559d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u5f53\u7136\u5e94\u8be5\u8bf4\u201c\u8bf7\u8fdb\u3002\u201d\u53ef\u662f\u8fd9\u4e24\u4e2a\u5b87\u4ed6\u7adf\u504f\u504f\u8bf4\u4e0d\u51fa\u53e3\u6765\u3002 \u3000\u3000\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\u4ed6\u5589\u5499\u5c31\u597d\u50cf\u5ffd\u7136\u88ab\u585e\u4f4f\u4e86\u7b80\u76f4\u8fde\u4e2a\u5b57\u90fd\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u6765\u6572\u95e8\u7684\u662f\u4e2a\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u6ee1\u8eab\u9ed1\u8863\xb7\u9ed1\u88e4\u5b50\u3001\u9ed1\u9774\u5b50\uff0c\u8138\u4e0a\u4e5f\u8499\u5757\u9ed1\u5dfe\u53ea\u9732\u51fa\u53cc\u4e4c\u9ed1\u6709\u5149\u7684\u773c\u775b\u8eab\u5f8c\u8fd8\u80cc\u67c4 \u4e4c\u8e87\u7684\u957f\u5251\u3002 \u3000\u3000\u67c4\u4e94\u5c3a\u591a\u957f\u7684\u5251\u3002 \u3000\u3000\u95e8\u53e3\u6ca1\u6709\u706f\u3002 \u3000\u3000\u8fd9\u4eba\u6454\u7b80\u7684\u7ad9\u5728\u90a3\u91cc\u7b80\u76f4\u5c31\u597d\u50cf\u662f\u9ed1\u6697\u7684\u5316\u8eab\u3002 \u3000\u3000\u770b\u89c1\u8fd9\u4e2a\u4eba\xb7\u90ed\u5927\u8def\u7684\u9152\u610f\u5c31\u597d\u50cf\u5df2\u7ecf\u6e05\u7530\u4e86\u53c1\u5206\u3002 \u3000\u3000\u518d\u770b\u5230\u8fd9\u4eba\u7684\u5251\uff0c\u4ed6\u9152\u610f\u5c31\u6e05\u9192\u4e86\u53c1\u5206\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5931\u58f0\u53eb\u4e86\u51fa\u6765 \u3000\u3000\u201c\u5357\u5bab\u4e11\u201d \u3000\u3000\u5176\u5b9e\u5357\u5bab\u4e11\u7a76\u7adf\u662f\u4ec0\u9ebd\u6837\u5b50\uff0c\u4ed6\u5e76\u6ca1\u6709\u771f\u7684\u770b\u89c1\u8fc7\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u7684\u662f\u6885\u6c5d\u7537\u3002 \u3000\u3000\u867d\u7136\u4ed6\u7684\u88c5\u67ec\u6253\u626e\u751a\u81f3\u8fde\u8eab\u4e0a\u4f69\u7684\u5251\u90fd\u548c\u6885\u6c5d\u7537\u90a3\u6b21\u548c\u68cd\u5b50\u4ed6\u4eec\u5728\u9ea6\u8001\u5e7f\u7684\u70e7\u814a\u5e97\u91cc\u51fa \u73b0\u65f6\uff0c\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u77e5\u9053\u4ed6\u7edd\u4e0d\u662f\u8bf2\u72ec\u7537\u3002 \u3000\u3000\u90a3\u5012\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u6bd4\u6885\u6c5d\u7537\u66f4\u9ad8\u4e00\u70b9\u66f4\u75d8\u70b9\u7a76\u7adf\u662f\u4e3a\u4ec0\u9ebd\u5462\uff1f\u8fde\u90ed\u5927\u8def\u81ea\u5df1\u4e5f\u4e0d\u592a\u6e05 \u695a\u3002 \u3000\u3000\u6885\u6c5d\u7537\u7a7f\u4e0a\u9ed1\u8863\u670d\u7684\u65f6\u5019\u4f24\u4f5b\u4e5f\u5e26\u79cd\u51cc\u5389\u903c\u4eba\u7684\u6740\u6c14\u3002 \u3000\u3000\u8fd9\u4eba\u5374\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u65e2\u6ca1\u6709\u6740\u6c14\u4e5f\u6ca1\u6709\u4eba\u6c14\uff0c\u7b80\u76f4\u8fde\u4ec0\u9ebd\u6c14\u90fd\u6ca1\u6709\uff0c\u4f60\u5c31\u7b97\u7530\u4ed6\u811a\xb7\u4ed6\u597d\u50cf\u4e5f\u4e0d\u4f1a\u6709\u70b9\u53cd \u5e94\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u65e0\u8bba\u8c01\u90fd\u7edd\u4e0d\u6562\u53bb\u6cbe\u4ed6\u6839\u624b\u6307\u3002 \u3000\u3000\u4ed6\u7741\u5b50\u5f88\u9ed1\u3001\u5f88\u4eae\uff0c\u548c\u666e\u901a\u7ec3\u6b66\u7684\u4eba\u597d\u50cf\u5e76\u6ca1\u6709\u4ec0\u9ebd\u4e0d\u540c\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u53ea\u8981\u4ed6\u770b\u4f60\u4e00\u773c\u4f60\u514b\u523b\u5c31\u4f1a\u89c9\u5f97\u5168\u8eab\u4e0d\u8212\u670d\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u770b\u90ed\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u89c9\u5f97\u5168\u8eab\u4e0d\u8212\u670d\xb7\u5c31\u597d\u50cf\u559d\u9189\u9152\u7b2c\u4e8c\u5929\u9192\u6765\u7684\u65f6\u5019\u2019\u6837\u624b\u5fc3\u91cc\u6d41\u51b7\u6c57\u5934\u75bc\u5f97\u6068 \u4e0d\u5f97\u62ff\u628a\u5200\u6765\u5c06\u8111\u888b\u780d\u6389\u3002 \u3000\u3000\u8e81\u8863\u4eba\u770b\u4ed6\u663e\u7136\u8fd8\u5728\u7b49\u4ed6\u7684\u7b54\u590d\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u4f3c\u5df2\u5fd8\u5382\u7b54\u590d\u3002 \u3000\u3000\u9ed1\u8863\u5165\u4ec0\u9ebd\u8bdd\u90fd\u6ca1\u6709\u518d\u8bf4\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u6162\u6162\u7684\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u8def\u7684\u6837\u5b50\u4e5f\u5f88\u6b63\u5e38\uff0c\u53ea\u4e0d\u8fc7\u5b9a\u5f97\u7279\u522b\u9992\u800c\u5df2\u6bcf\u8d70\u4e00\u6b65\uff0c\u90fd\u8981\u5148\u5f80\u524d\u9762\u770b\u4e00\u773c\u624d\u843d \u811a\xb7\u5c31\u597d\u50cf\u751f\u4f2f\u811a\u8e29\u7a7a\xb7\u8dcc\u8fdb\u4e2a\u5f88\u6df1\u7684\u6c34\u6c9f\u91cc\u53c8\u597d\u50cf\u751f\u4f2f\u8e29\u6b7b\u4e86\u5730\u4e0a\u7684\u8682\u8681\u3002 \u3000\u3000\u50cf\u4ed6\u8fd9\u6837\u5b50\u8d70\u8def\xb7\u8d70\u5230\u660e\u5929\u4e0b\u5348\xb7\u6015\u4e5f\u8d70\u4e0d\u5230\u5c71\u4e0b\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u7b49\u7b49\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5934\u4e5f\u4e0d\u56de\u9053\u201c\u4e0d\u5fc5\u7b49\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u8fd9\u91cc\u65e2\u4e0d\u4fbf\u6211\u4e5f\u4e0d\u52c9\u5f3a\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5b8c\u4ed6\u624d\u8d70\u51fa\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u90ed\u5927\u8def\u5927\u7b11\u9053\u201c\u8c01\u8bf4\u8fd9\u91cc\u4e0d\u4fbf\uff1f\u9644\u8fd1\u516b\u767e\u91cc\u5185\uff0c\u7edd\u6ca1\u6709\u6bd4\u8fd9\u91cc\u66f4\u6b22\u8fce\u5ba2\u4eba\u7684\u5730\u65b9\u4e86\uff0c\u4f60 \u5feb\u8bf7\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u8fd8\u5728\u72b9\u8c6b\u8fc7\u4e86\u5f88\u4e45\u624d\u6162\u6162\u7684\u8f6c\u8fc7\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u7b49\u4e86\u5f88\u4e45\u4ed6\u624d\u8d70\u56de\u95e8\u53e3\u9053\u201c\u9601\u4e0b\u771f\u8bf7\u6211\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e5f\u6162\u541e\u541e\u7684\u4f46\u7528\u7684\u5b57\u5374\u5f88\u5c11\u522b\u4eba\u8981\u7528\u5341\u4e2a\u5b87\u624d\u80fd\u8bf4\u5b8c\u7684\u8bdd\u4ed6\u6700\u591a\u53ea\u7528\u516d\u4e03\u4e2a\u5b57\u3002 \u3000\u3000\u90ed\u5927\u8def\u8c0a\u201c\u771f\u7684\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u5f8c\u6094\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e3a\u4ec0\u9ebd\u800d\u5f8c\u6094\uff1f\u9601\u4e0b\u83ab\u8bf4\u53ea\u501f\u5bbf\u5bb5\u5c31\u7b97\u4efb\u4e0a\u4e8c\u4e94\u4e2a\u6708\u6211\u8ba2\u4e5f\u662f\u6837\u6b22\u8fce \u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u8c6a\u6c14\u53c8\u53d1\u4f5c\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u8c22\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u50bb\u6162\u7684\u8d70\u8fdb\u9662\u5b50\xb7\u773c\u775b\u53ea\u770b\u524d\u9762\u7684\u8def\u522b\u7684\u4ec0\u9ebd\u5730\u65b9\u90fd\u4e0d\u770b\u3002 \u3000\u3000\u71d5\u4e03\u548c\u4e0a\u52a8\u90fd\u5728\u7a97\u6237\u91cc\u770b\u4ed6\u4e24\u4eba\u7684\u795e\u8272\u4e5f\u663e\u5f97\u5f88\u60ca\u8bb6\u3002 \u3000\u3000\u9ed1\u519c\u4eba\u8d70\u5230\u957f\u5eca\u4e0a\u5c31\u505c\u4e0b\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u5148\u8bf7\u8fdb\u6765\u559d\u676f\u9152\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4ece\u6765\u4e0d\u559d\u9152\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6709\u65f6\u77a9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u65f6\u5019\u624d\u8d50\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6740\u8fc7\u4eba\u5f8c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\u8d3f\u8d2d\u9053\u201c\u8fd9\u9ebd\u6837\u8bf4\u6765\u4f60\u8fd8\u662f\u77f3\u8981\u559d\u9152\u7684\u597d\u3002\u201d \u3000\u3000\u5f8c\u6765\u4ed6\u65e5\u5df1\u60f3\u60f3\u53c8\u89c9\u5f97\u5f88\u597d\u7b11\u3002 \u3000\u3000\u90ed\u5148\u4e2d\u5c45\u7136\u53eb\u4eba\u4e0d\u8981\u559d\u9152\uff0c\u8fd9\u5012\u771f\u662f\u5e73\u751f\u7b2c\u906d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5c31\u5730\u5e03\u5eca\u4e0a\xb7\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f8c\u9762\u6709\u5ba2\u623f\u4f60\u65e2\u7136\u4e0d\u559d\u9152\u5c31\u8bf7\u8fc7\u53bb\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u8bc1\u4e86\u6014\xb7\u9053\u201c\u4e0d\u5fc5\uff1f\u4e0d\u5fc5\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u5fc5\u53bb\u5ba2\u623f\u3002\u201d\u90ed\u5927\u8def\u9041\u201c\u4f60\u96be\u9053\u5c31\u7761\u5728\u8fd9\u91cc\uff1f\u201d\u9ed1\u8863\u4eba\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u5df2\u61d2\u5f97\u518d\u8ddf\u90ed\u5927\u8def\u8bf4\u8bdd\u9992\u6162\u7684\u95ed\u8d77\u4e86\u773c\u775b\uff0c\u501a\u5728\u5eca\u524d\u7684\u67f1\u5b50\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u65e2\u7136\u8981\u7761\u5728\u8fd9\u91cc\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u8eba\u4e0b\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u5fc5\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u5fc5\u8eba\u4e0b\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8bf4\u4e0d\u51fa\u8bdd\u4e86\xb7\u8138\u4e0a\u7684\u8868\u60c5\u5c31\u597d\u50cf\u770b\u5230\u4e86\u5339\u4f1a\u8bf4\u8bdd\u7684\u9a6c\u6837\u3002 \u3000\u3000\u201c\u9a6c\u4e0d\u4f1a\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u201c\u4f46\u53ea\u6709\u9a6c\u624d\u7ad9\u7761\u89c9\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u5339\u9a6c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u201c\u5357\u5bcc\u4e11\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u8fd9\u6b21\u603b\u7b97\u540c\u610f\u5382\u90ed\u5927\u8def\u7684\u8bdd\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u501a\u5728\u5eca\u535c\u6309\u4e8e\u4ea1\u7adf\u4f3c\u771f\u7684\u7761\u4e86\u4ed6\u8fd9\u4eba\u672c\u8eab\u5c31\u8c61\u662f\u6839\u67f1\u4e8e\u76f4\u3001\u51b7\u3001\u786c\u6ca1\u6709\u53cd\u5e94\u6ca1\u6709 \u611f\u60c5\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\uff0c\u9053\u201c\u8fd9\u4eba\u82e5\u4e0d\u662f\u5357\u5bab\u4e11\u5929\u4e0b\u5c31\u7edd\u4e0d\u53ef\u80fd\u518d\u6709\u522b\u7684\u4eba\u662f\u5357\u7a9d\u4e11\u5382\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u65e0\u8bba\u4ed6\u662f\u9a6c\u4e5f\u597d\u662f\u5357\u5bab\u536b\u4e5f\u597d\u90fd\u8ddf\u6211\u4eec\u70b9\u5173\u7cfb\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c61\u7638\u5bab\u4e11\u8fd9\u79cd\u4eba\uff0c\u82e5\u6ca1\u6709\u65e5\u7684\u600e\u9ebd\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff0c\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u6765\u201d\u90ed\u5927\u8def\u9053\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u6765\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e0\u8bba\u90a3\u79cd\u4eba\uff0c\u665a\u4e0a\u90fd\u8981\u627e\u4e2a\u5730\u65b9\u7761\u89c9\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u771f\u8ba4\u4e3a\u4ed6\u662f\u6765\u7761\u89c9\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u6b63\u5728\u7761\u89c9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c61\u8fd9\u6837\u5b50\u7761\u89c9\xb7\u4ec0\u9ebd\u5730\u65b9\u4e0d\u80fd\u7761\u4e3a\u4ec0\u9ebd\u504f\u504f\u8981\u5230\u8fd9\u91cc\u6765\u7761\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e0\u8bba\u4ed6\u4e3a\u7684\u662f\u4ec0\u9ebd\u4ed6\u73b0\u5728\u603b\u662f\u76ae\u7761\u89c9\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6240\u4ee5\u6211\u4eec\u5927\u5bb6\u90fd\u5e94\u8be5\u53bb\u531d\u89c9\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u7684\u7ed3\u8bba\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u53bb\u7761\u89c9\u4e86\u3002 \u3000\u3000\u738b\u52a8\u8bf4\u8981\u53bb\u7761\u89c9\u7684\u65f6\u5019\xb7\u4f60\u65e0\u8bba\u60f3\u53eb\u4ed6\u53bb\u505a\u4efb\u4f55\u522b\u7684\u4e8b\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u8fd8\u7ad9\u5728\u7a97\u53e3\u770b\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u53bb\u7761\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u9521\u770b\u770b\uff0c\u4ed6\u662f\u4e0d\u662f\u771f\u7684\u7761\u4e86\u80fd\u7761\u591a\u4e45\uff1f\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5883\u5c42\u8bf4\u9053\u201c\u4f46\u8fd9\u662f\u6211\u7684\u623f\u95f4\u6211\u8981\u7761\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u7761\u4f60\u7684\uff0c\u6211\u53c8\u4e0d\u4f1a\u5435\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u884c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6709\u522b\u4eba\u5728\u6211\u5c4b\u91cc\u6211\u7761\u4e0d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u9053\u201c\u4f60\u4ee5\u5f8c\u82e5\u5a36\u4e86\u8001\u5a46\xb7\u96be\u9053\u8fd8\u8981\u5979\u5230\u522b\u7684\u5c4b\u91cc\u53bb\u7761\u89c9\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7684\u8138\u4eff\u4f5b\u53c8\u6709\u4e9b\u7ea2\u4e86\u77aa\u773c\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u6211\u5b9a\u8981\u5a36\u8001\u5a46\u4f0a \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4e16\u4e0a\u53ea\u6709\u4e24\u79cd\u4eba\u4e0d\u5a36\u8005\u5a46\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7530\u4e24\u79cd\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4e00\u79cd\u548c\u5c1a\u79cd\u662f\u534a\u7537\u4e0d\u4ea5\u7684\u4eba\xb7\u4f60\u603b\u4e0d\u662f\u8fd9\u4e24\u79cd\u4eba\u5427\u3002\u201d \u3000\u3000\u71d5\u4e03\u6709\u4e9b\u751f\u6c14\u4e86\u9053\u201c\u5c31\u7b97\u6211\u8981\u5a36\u8001\u5a46\uff0c\u4e5f\u4e0d\u4f1a\u5a36\u4e2a\u50cf\u4f60\u8fd9\u6837\u7684\u81ed\u7537\u4eba\u5427\u3002\u201d \u3000\u3000\u4ed6\u4e2d\u6765\u6709\u4e9b\u751f\u6c14\u7684\u4f46\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u80a1\u5374\u53cd\u800c\u66f4\u7ea2\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u628a\u5c06\u4ed6\u62ab\u4e86\u8fc7\u6765\xb7\u6084\u58f0\u9053\u201c\u4f60\u770b\u90a3\u8fb9\u5899\u4e0a\u662f\u4ec0 \u3000\u3000\u71d5\u4e03\u521a\u51c6\u5907\u7529\u8131\u4ed6\u7684\u65f6\u5019\u5df2\u770b\u5230\u5bf9\u9762\u5899\u5934\u4e0a\u4f38\u51fa\u4e00\u4e2a\u8111\u888b\u6765\u3002 \u3000\u3000\u591c\u8272\u5f88\u6697\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u770b\u6e05\u8fd9\u4eba\u7684\u80a1\u957f\u5f97\u4ec0\u9ebd\u6837\u5b50\u53ea\u770b\u89c1\u53cc\u70df\u70af\u6709\u5149\u7684\u77aa\u775b\u56db\u9762\u770b\u4e86\u770b\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u5c4b\u91cc\u5e76\u6ca1\u6709\u71c3\u706f\u6240\u4ee5\u8fd9\u4eba\u4e5f\u6ca1\u6709\u770b\u89c1\u4ed6\u4eec\u56db\u9762\u770b\u4e86\u51e0\u773c\u5ffd\u7136\u53c8\u7f29\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f7b\u8f7b\u7684\u51b7\u7b11\u9053\u201c\u4f60\u770b\u6211\u731c\u7684\u4e0d\u9519\u8fd9\u4eba\u975e\u4f46\u4e0d\u6000\u597d\u610f\u800c\u4e14\u6765\u7684\u8fd8\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u8ba4\u4e3a\u4ed6\u662f\u5148\u5230\u8fd9\u91cc\u6765\u5367\u5e95\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5b9a\u662f\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u867d\u7136\u8fd8\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4f46\u71d5\u4e03\u5374\u4e5f\u4e0d\u7981\u770b\u5f97 \u3000\u3000\u6ca1\u6709\u52a8\u4f5c\u5f80\u5f80\u4e5f\u662f\u79cd\u5f88\u53ef\u6015\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u71d5\u4e03\u5c31\u7b97\u771f\u7684\u60f3\u7761\u89c9\xb7\u73b0\u5728\u4e5f\u65e9\u5df3\u5fd8\u5f97\u5e72\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u7a81\u542c\u90ed\u5927\u8def\u95fb\u95fb\u9053\u201c\u5947\u602a\u771f\u5947\u602a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u4e8b\u5947\u602a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u4e3a\u4ec0\u9ebd\u4e00\u70b9\u4e5f\u4e0d\u81ed\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8fd9\u624d\u53d1\u89c9\u4ed6\u7ad9\u5f97\u79bb\u90ed\u5927\u8def\u5f88\u8fd1\u51e0\u4e4e\u5df2\u9760\u5728\u90ed\u5927\u8def\u6000\u91cc\u3002 \u3000\u3000\u5e78\u597d\u5c4b\u91cc\u6ca1\u6709\u706f\u4e5f\u770b\u4e0d\u51fa\u4ed6\u8138\u4e0a\u662f\u4ec0\u9ebd\u98a7\u8272\xb7\u4ec0\u9ebd\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u9000\u51fa\u4e86\u4e24\u6b65\xb7\u54ac\u7530\u5507\xb7\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u4e0d\u81ed\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u71d5\u4e03\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4ece\u6765\u6ca1\u770b\u8fc7\u4f60\u6d17\u6fa1\xb7\u4e5f\u6ca1\u770b\u8fc7\u4f60\u6362\u8863\u670d\uff0c\u4f60\u672c\u6765\u5e94\u8be5\u81ed\u5f97\u8981\u547d\u624d\u5bf9 \u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u653e\u5c41\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u653e\u5c41\u5c31\u66f4\u81ed\u4e86\u3002\u201d \u3000\u3000\u85cf\u4e03\u72e0\u72e0\u7684\u77aa\u4ed6\u597d\u50cf\u72e0\u60f3\u7ed9\u4ed6\u4e00\u4e2a\u8033\u5229\u65bc\uff0c\u5e78\u597d\u5c31\u5728\u8fd9\u65f6\u5899\u5916\u5ffd\u7136\u6709\u4e2a\u4eba\u8f7b\u70df\u822c\u63a0\u4e86\u8fdb \u6765\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u771f\u7684\u8c61\u70df\u4e00\u6837\u4f46\u5374\u771f\u8f7b\uff0c\u63a0\u53c1\u4e08\u5f8c\u843d\u5728\u5730\u4e0a\u5c45\u7136\u8fde\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u4e0d\u4f46\u8f7b\xb7\u800c\u4e14\u7279\u522b\u75c9\u5c0f\u7b80\u76f4\u6218\u5c0f\u5b69\u5b50\u7684\u8eab\u6750\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8138\u4e0a\u5374\u5df2\u6709\u4e86\u5f88\u957f\u7684\u80e1\u5b50\uff0c\u51e0\u4e4e\u5df2\u548c\u4e71\u677e\u6781\u7684\u5934\u53d1\u8fde\u5728\u4e00\u8d77\u906e\u4f50\u4e86\u5927\u534a\u4e2a\u8138\u53ea\u80fd \u770b\u5230\u53cc\u72d0\u72f8\u822c\u72e1\u733e\u7684\u9650\u775b\u3002 \u3000\u3000\u4ed6\u773c\u775b\u56db\u4e0b\u8f6c\uff0c\u5c31\u76ef\u5728\u501a\u67f1\u5b50\u7684\u9ed1\u8863\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fd8\u662f\u6ca1\u6709\u52a8\u4e5f\u6ca1\u6709\u7741\u5f00\u773c\u775b\u3002 \u3000\u3000\u8fd9\u4eba\u5ffd\u7136\u62db\u624b\u5899\u5916\u7acb\u523b\u5c31\u53c8\u63a0\u4eba\u4e86\u53c1\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u53c1\u4e2a\u4eba\u7684\u8eab\u6750\u5f53\u7136\u9ad8\u5927\u4e9b\u4f46\u8f7b\u529f\u5374\u90fd\u4e0d\u5f31\xb7\u53c1\u4e2a\u4eba\u90fd\u662f\u8f7b\u88c5\u8eab\u591c\u884c\u9760\u624b\u4e0a\u90fd\u62ff\u5175\u5668\u3002 \u3000\u3000\u4e2a\u4eba\u7528\u7684\u662f\u5224\u5b98\u7b14\xb7\u4e2a\u4eba\u7528\u7684\u662f\u5f27\u5f62\u5251\xb7\u4e2a\u4eba\u7528\u7684\u662f\u94fe\u4e88\u67aa\uff0c\u90a3\u67af\u7626\u7684\u8001\u4eba\u4e5f\u4eae\u51fa\u4e86\u4e00 \u5bf9\u53cc\u73af\u3002 \u3000\u3000\u56db\u79cd\u90fd\u662f\u5f88\u7280\u5229\xb7\u4e5f\u72e0\u96be\u7ec3\u7684\u5916\u95e8\u5175\u5668\u3002 \u3000\u3000\u80fd\u7528\u8fd9\u79cd\u5175\u5668\u7684\u4eba\u6b66\u529f\u7edd\u4e0d\u4f1a\u5dee\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u8fd8\u662f\u4e0d\u52a8\u7684\u7ad9\xb7\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u7684\u795e\u60c5\u90fd\u5f88\u7d27\u5f20\uff0c\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u7684\u76ef\u5728\u4ed6\u8eab\u4e0a\xb7\u6b65\u6b65\u5411\u903c\u4e86\u8fc7\u53bb\uff0c\u663e\u7136\u968f\u65f6\u90fd\u53ef \u80fd\u4f7f\u51fa\u6740\u624b\uff0c\u4e00\u4e0b\u5b50\u5c31\u8981\u4ed6\u7684\u547d\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u71d5\u4e03\u773c\xb7\u610f\u601d\u8c61\u662f\u8bf4\u201c\u539f\u6765\u4ed6\u4eec\u5e76\u4e0d\u662f\u540c\u8def\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u6309\u5175\u4e0d\u52a8\u5fc3\u5934\u90fd\u6709\u540c\u6837\u7684\u6253\u7b97\uff0c\u8981\u770b\u770b\u8fd9\u56db\u4e2a\u4eba\u7528\u5916\u95e8\u5175\u5668\u7684\u591c\u884c\u76d7\u600e\u9ebd\u6837\u6765 \u5bf9\u8ba8\u8fd9\u795e\u79d8\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\u5927\u95e8\u5ffd\u7136\u5f00\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u660e\u660e\u8bb0\u5f97\u5df2\u5c06\u5927\u95e8\u63a5\u4e0a\u4e86\u73b0\u5728\u4e0d\u77e5\u600e\u7684\u7adf\u4e49\u65e0\u58f0\u65e0\u6069\u7684\u5f00\u4e86\u3002 \u3000\u3000\u4e2a\u7a7f\u78a7\u7eff\u957f\u886b\u7684\u4eba\u624b\u91cc\u6447\u6298\u6247\u65bd\u65bd\u7136\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u7a7f\u5f97\u5f88\u534e\u4e3d\xb7\u795e\u60c5\u5f88\u84b2\u6d12\u770b\u6765\u5c31\u8c61\u662f\u4e2a\u8d70\u9a6c\u7ae0\u53f0\u7684\u82b1\u82b1\u516c \u3000\u3000\u90ed\u5927\u8def\u770b\u6e05\u4ed6\u7684\u8138\u65f6\u5374\u4e0d\u7981\u5413\u4e86\u8df3\u3002 \u3000\u3000\u90a3\u7b80\u76f4\u5c31\u4e0d\u8c61\u662f\u5f20\u4eba\u7684\u8138\u5c31\u8fde\u897f\u85cf\u5587\u561b\u5e99\u91cc\u7684\u9b54\u9b3c\u9762\u5177\u90fd\u6ca1\u6709\u8fd9\u5f20\u8138\u53ef\u9633\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u786e\u662f\u5f20\u6d3b\u751f\u751f\u7684\u8138\u800c\u4e14\u8138\u4e0a\u8fd8\u6709\u8868\u60c5\u3002 \u3000\u3000\u79cd\u4ee4\u4eba\u770b\u4e86\u4e4b\u5f8c\u7761\u4e86\u90fd\u4f1a\u5728\u534a\u591c\u91cc\u60ca\u9192\u7684\u8868\u60c5\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e5\u975e\u4eb2\u773c\u770b\u5230\u7b80\u76f4\u4e0d\u76f8\u4fe1\u8fd9\u9ebd\u6837\u4e2a\u4eba\u8eab\u4e0a\u4f1a\u957f\u8fd9\u9ebd\u5f20\u80a1\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u7528\u5916\u95e8\u5175\u5668\u7684\u4eba\uff0c\u5c45\u7136\u8fd8\u6ca1\u6709\u53d1\u89c9\u53c8\u6709\u4e2a\u4eba\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u7eff\u886b\u4eba\u7684\u811a\u6b65\u8f7b\u5f97\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u6709\u6cbe\u4ed6\u4f3c\u7684\u98d8\u98d8\u7136\u8d70\u5230\u90a3\u7528\u5224\u5b98\u7b14\u7684\u4eba\u80cc\u5f8c\u7528\u624b\u91cc\u7684 \u6298\u6247\u8f7b\u8f7b\u62cd\u8fd9\u4eba\u7684\u80a9\u3002 \u3000\u3000\u8fd9\u4eba\u7acb\u523b\u5c31\u8c61\u53ea\u4e2d\u77ff\u7bad\u7684\u5154\u5b50\u822c\u8df3\u4e86\u8d77\u6765\u51cc\u7a7a\u4e2a\u7ffb\u8eab\u843d\u5728\u90a3\u63cf\u7626\u8001\u4eba\u7684\u65c1\u8fb9\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u624d\u770b\u89c1\u5382\u8fd9\u7eff\u886b\u4eba\u8138\u4e0a\u7acb\u523b\u5145\u6ee1\u5382\u60ca\u9a87\u4e4b\u610f\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u548c\u71d5\u4e03\u4ea4\u6362\u4e86\u4e2a\u773c\u8272\u201c\u539f\u6765\u8fd9\u4e9b\u4eba\u4e5f\u4e0d\u662f\u2500\u8def\u6765\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u5c31\u8c61\u662f\u6b63\u5728\u6f14\u51fa\u65e0\u58f0\u7684\u54d1\u5267\xb7\u606b\u5374\u5b9e\u5728\u5f88\u795e\u79d8\u3001\u5f88\u523a\u6fc0\u3002 \u3000\u3000\u7eff\u886b\u4eba\u624b\u91cc\u8fd8\u5728\u8f7b\u6447\u6298\u6247\xb7\u663e\u5f97\u4ece\u5bb9\u5f97\u5f88\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u7528\u5916\u5175\u5668\u7684\u4eba\u5374\u66f4\u7d2b\u5f20\uff0c\u624b\u91cc\u7684\u5175\u5668\u63e1\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u7eff\u886b\u4eba\u5ffd\u7136\u7528\u624b\u91cc\u7684\u6298\u6247\u6307\u4e86\u6307\u4ed6\u4eec\xb7\u53c8\u5411\u95e8\u5916\u6307\u4e86\u6307\u3002 \u3000\u3000\u8fd9\u610f\u601d\u663e\u7136\u662f\u53eb\u51fa\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u7528\u5916\u95e8\u5175\u5668\u7684\u4eba\u5bf9\u671b\u4e86\u773c\u90a3\u8001\u4eba\u54ac\u4e86\u54ac\u7259\u6447\u4e86\u6447\u5934\u7528\u624b\u91cc\u7684\u94a2\u73af\u6307\u4e86\u6307\u8fd9\u680b\u5c4b\u5b50\u53c8 \u5411\u4ed6\u4eec\u81ea\u5df1\u6307\u4e86\u6307\u3002 \u3000\u3000\u4ed6\u7684\u610f\u601d\u663e\u7136\u662f\u8bf4\u201c\u8fd9\u5730\u76d8\u662f\u6211\u4eec\u7684\xb7\u6211\u4eec\u4e0d\u51fa\u53bb\u3002\u201d \u3000\u3000\u7eff\u6749\u4eba\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u4e0d\u53ef\u80fd\u770b\u5230\u8fd9\u6837\u5b50\u7684\u7b11\u3002 \u3000\u3000\u65e0\u8bba\u6dee\u770b\u5230\u8fd9\u6837\u5b50\u7684\u7b11\u90fd\u4e00\u5b9a\u4f1a\u4e3a\u4e4b\u6bdb\u9aa8\u68d8\u7136\u3002 \u3000\u3000\u56db\u4e2a\u7528\u5916\u95e8\u5175\u5668\u7684\u4eba\u811a\u6b65\u79fb\u52a8\u5df2\u7ad9\u5728\u8d77\u989d\u4e0a\u5192\u5149\u663e\u89c1\u5df2\u662f\u6ee1\u5934\u51b7\u6c57\u3002 \u3000\u3000\u7eff\u886b\u4eba\u6298\u6247\u53c8\u5411\u4ed6\u4eec\u624b\u91cc\u7684\u5175\u5668\u6307\u4e86\u6307\u597d\u50cf\u662f\u5728\u8bf4\u201c\u4f60\u4eec\u4e00\u8d77\u535c\u6765\u5427\u201d \u3000\u3000\u56db\u4e2a\u4eba\u5bf9\u671b\u4e86\u8d29\uff0c\u8c61\u662f\u5df2\u51c6\u5907\u51fa\u624b\xb7\u4f46\u5c31\u5728\u8fd9\u65f6\xb7\u7eff\u886b\u4eba\u5ffd\u7136\u95f4\u5df1\u5230\u4e86\u4ed6\u4eec\u9762\u524d\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u7684\u6298\u6247\u8f7b\u8f7b\u5728\u90a3\u7528\u94fe\u5b50\u67aa\u7684\u4eba\u5934\u4e0a\u4e00\u6572\u3002 \u3000\u3000\u6572\u5f97\u597d\u50cf\u4e95\u6c34\u91cd\u3002 \u3000\u3000\u4ecd\u8fd9\u4eba\u7acb\u523b\u5c31\u8c61\u662f\u6ee9\u6ce5\u822c\u8f6f\u8f6f\u7684\u5012\u5382\u4e0b\u53bb\u4e2a\u5927\u597d\u7684\u5934\u9885\u7ade\u53e3\u88ab\u6572\u5f97\u88c2\u5f00\u98de\u6e85\u51fa\u7684\u8840\u6d46\u5728 \u4fed\u5305\u4e2d\u770b\u6765\u5c31\u4eff\u4f5b\u662f\u7247\u843d\u82b1\u3002 \u3000\u3000\u4ed6\u5012\u535c\u5de6\u7684\u65f6\u5019\u5f27\u5f62\u5251\u5df2\u5212\u5411\u7eff\u886b\u4eba\u7684\u80f8\u819b\u3002 \u3000\u3000\u5251\u8d70\u8f7b\u7075\u6ed1\u3001\u72e0\u800c\u4e14\u5feb\u3002 \u3000\u3000\u4f46\u7eff\u886b\u4eba\u66f4\u5feb\u3002\u4ed6\u4f38\u624b\uff0c\u5c31\u542c\u5230\u201c\u568e\u201d\u58f0\u63a5\u53c8\u662f\u201c\u9699\u201d\u58f0\u3002 \u3000\u3000\u5f27\u5f62\u5251\u201c\u53ee\u201d\u7684\u6389\u5728\u5730\u4e0a\u8fd9\u4eba\u7684\u4e24\u53ea\u624b\u5df2\u5f00\u8155\u6298\u65ad\u53ea\u5269\u4e0b\u5c42\u76ae\u8fde\u5728\u8155\u4e86\u535c\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8fd8\u662f\u7ad9\u7684\u606b\u770b\u4e01\u770b\u81ea\u5df1\u8fd9\u53cc\u624b\uff0c\u7a81\u7136\u5c31\u6655\u5382\u8fc7\u58eb\u3002 \u3000\u3000\u8fd9\u4e54\u8fc7\u8db3\u77ac\u95f4\u7684\u4e8b\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4e2a\u5df4\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u4e24\u6761\u817f\u4e0d\u505c\u7684\u5728\u5f39\u7435\u7436\u3002 \u3000\u3000\u90a3\u8001\u4eba\u603b\u7b97\u6c89\u5f97\u4f4f\u6c14\u5ffd\u7136\u5411\u7eff\u886b\u4eba\u5f2f\u4e86\u5f2f\u8170\xb7\u7528\u94a2\u73af\u5411\u95e8\u5916\u6307\u4e86\u6307\u3002 \u3000\u3000\u6e38\u90fd\u770b\u5f97\u51fa\u4ed6\u5df4\u8ba4\u8f93\u5382\u5df2\u51c6\u5907\u8981\u8d70\u3002 \u3000\u3000\u7eff\u886b\u4eba\u53c8\u7b11\u4e86\u7b11\u70b9\u5382\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7acb\u523b\u5c06\u5730\u4e0a\u7684\u4e24\u4e2a\u2019\u4f53\u59cb\u8d77\u6765\u5927\u6b65\u5954\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u521a\u8d70\u51fa\u95e8\u7eff\u886b\u4eba\u8eab\u5f62\u95ea\uff0c\u5ffd\u7136\u960e\u4e5f\u5df2\u5230\u4e86\u95e8\u5916\u3002 \u3000\u3000\u95e8\u5916\u53d1\u751f\u4e86\u4ec0\u9ebd\u4e8b\xb7\u90ed\u5927\u8def\u5e76\u6ca1\u6709\u770b\u89c1\u53ea\u542c\u5230\u4e24\u58f0\u60e8\u547c\u3002 \u3000\u3000\u63a5\uff0c\u51e0\u6837\u4e1c\u897f\u4ece\u95e8\u5916\u5382\u8fdb\u6765\u8dcc\u5728\u5730\u539f\u6765\u6b63\u662f\u5bf9\u5224\u5b98\u7b14\u5bf9\u94a2\u73af\u3002 \u3000\u3000\u4f46\u5224\u5b98\u7b14\u5df2\u65ad\u6210\u56db\u622a\u94a2\u73af\u4e5f\u5df2\u5f2f\u66f2\u6839\u672c\u5df2\u4e0d\u8c61\u662f\u4e2a\u94a2\u73af\u3002 \u3000\u3000\u90ed\u5927\u8def\u5012\u62bd\u5382\u53e3\u51c9\u6c14\u770b\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u773c\u775b\u91cc\u4f3c\u4e5f\u6709\u4e9b\u60ca\u6050\u4e4b\u8272\u3002 \u3000\u3000\u8fd9\u7eff\u886b\u4eba\u7684\u6b66\u529f\u4e0d\u4f46\u9ad8\uff0c\u800c\u4e14\u9ad8\u5f97\u90aa\u6c14\u3002 \u3000\u3000\u6700\u53ef\u6015\u7684\u662f\xb7\u4ed6\u6740\u8d77\u4eba\u6765\uff0c\u7b80\u76f4\u5c31\u597d\u50cf\u522b\u4eba\u5728\u5207\u83b1\u4f3c\u7684\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u5230\u4ed6\u6740\u4eba\u7684\u6837\u5b50\uff0c\u60f3\u4e0d\u6d41\u51b7\u6c57\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u5165\u8fd8\u662f\u6c72\u770b\u89c1\u56e0\u4e3a\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u52a8\u6ca1\u6709\u7741\u5f00\u773c\u6765\u3002 \u3000\u3000\u9662\u5b50\u91cc\u53d1\u751f\u4e86\u8fd9\u9ebd\u591a\u4e8b\u5c31\u5728\u4ed6\u6670\u524d\u6b7b\u4e86\u8fd9\u4e9b\u4eba\uff0c\u4ed6\u8fd8\u662f\u8fde\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c31\u7b97\u5929\u4e0b\u7684\u5165\u90fd\u5728\u4ed6\u9762\u524d\u6b7b\u5149\u4e86\xb7\u4ed6\u597d\u50cf\u4e5f\u4e0d\u4f1a\u6709\u70b9\u53cd\u5e94\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u7eff\u6749\u4eba\u53c8\u65bd\u65bd\u7136\u4ece\u95e8\u5916\u8d70\u4e86\u8fdb\u6765\xb7\u624b\u91cc\u8f7b\u6447\u6298\u6247\u663e\u5f97\u53c8\u7b4b\u6d12\u3001\u53c8\u60a0\u56f0\u3002 \u3000\u3000\u7740\u6709\u8c01\u80fd\u770b\u5f97\u51fa\u4ed6\u521a\u53e3\u6c14\u6740\u4e86\u56db\u4e2a\u4eba\uff0c\u90a3\u624d\u662f\u602a\u4e8b\u3002 \u3000\u3000\u4ed6\u6709\u610f\u65e0\u610f\u5411\u7ff1\u5927\u8def\u4ed6\u4eec\u90a3\u7a97\u53e3\u9690\u4e86\u773c\uff0c\u4f46\u8fd8\u662f\u7b14\u76f4\u8d70\u5230\u4e86\u9ed1\u8863\u7684\u9762\u524d\u3002 \u3000\u3000\u8d70\u5eca\u524d\u6709\u51e0\u7f00\u77f3\u9636\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u7b2c\u4e8c\u7ea7\u77f3\u9636\u5c31\u7ad9\u4f50\u770b\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u53d1\u73b0\u8fd9\u9ed1\u8863\u4eba\u4e0d\u77e5\u5728\u4ec0\u9ebd\u65f6\u5019\u4e5f\u5f20\u5f00\u773c\u775b\u6765\u4e86\u4e5f\u6b63\u5728\u770b\u4ed6\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4f60\u770b\u770b\u6211\xb7\u6211\u770b\u4f60\u90a3\u6837\u5b50\u770b\u6765\u672c\u8be5\u72e0\u6ed1\u7a3d\u7684\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u8fde\u70b9\u6ed1\u7a3d\u7684\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u53ea\u89c9\u5f97\u624b\u5fc3\u91cc\u6709\u70b9\u53d1\u51b7\u3002 \u3000\u3000\u8fde\u4ed6\u624b\u5fc3\u90fd\u5df2\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u7eff\u886b\u4eba\u5ffd\u7136\u9053\u201c\u521a\u2018\u6076\u9e1f\u2019\u5eb7\u540c\u5df2\u5e26\u4ed6\u7684\u5144\u5f1f\u6765\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u5f00\u53e3\uff0c\u539f\u6765\u4ed6\u4e0d\u4f46\u98ce\u5ea6\u7530\u5708\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u5f88\u597d\u542c\u3002 \u3000\u3000\u53ea\u8981\u4e0d\u770b\u4ed6\u7684\u8138\uff0c\u53ea\u542c\u4ed6\u8bf4\u8bdd\uff0c\u53ea\u770b\u4ed6\u7684\u98ce\u59ff\uff0c\u771f\u662f\u4f0e\u6d4a\u4e16\u4f73 \u3000\u3000\u9ed1\u8863\u4eba\u201c\u54fc\u3002\u201d \u3000\u3000\u7eff\u886b\u4eba\u9053\u201c\u6211\u751f\u4f2f\u4ed6\u4eec\u6253\u6270\u4e86\u4f9d\u7684\u6e05\u68a6\u5df2\u6253\u53d1\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u7eff\u886b\u4eba\u9053\u201c\u4f60\u83ab\u975e\u4e5f\u5df2\u77e5\u9053\u4ed6\u4eec\u8981\u6765\uff0c\u6240\u4ee5\u5148\u5728\u8fd9\u91cc\u7b49\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4ed6\u4eec\u4e0d\u914d\u3002\u201d \u3000\u3000\u7eff\u886b\u4eba\u9053\u201c\u4e0d\u9519\u8fd9\u4e9b\u4eba\u7684\u786e\u4e0d\u914d\u4f60\u51fa\u624b\u90a3\u672b\u4f60\u662f\u5728\u7b49\u8c01\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u660e\u516c\u5b50\u3002\u201d \u3000\u3000\u7eff\u886b\u4eba\u7b11\u9053\u627f\u8499\u4f60\u770b\u5f97\u8d77\xb7\u771f\u662f\u8363\u5e78\u4e4b\u81f3\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u53eb\u505a\u9b3c\u516c\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u89c9\u5f97\u8fd9\u540d\u5b57\u771f\u662f\u518d\u6070\u5f53\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u9ed1\u8863\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u201c\u662f\u4e0d\u662f\u5357\u5bab\u4e11\uff1f\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u5728\u8fd9\u91cc\u7b49\u8fd9\u9b3c\u516c\u4e8e\uff1f \u3000\u3000\u9b3c\u516c\u5b50\u53c8\u9053\u201c\u4f60\u5728\u8fd9\u91cc\u65e2\u7136\u7b49\u6211\u7684\uff0c\u83ab\u975e\u5df2\u77e5\u9053\u6211\u7684\u6765\u610f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u9053\u201c\u6211\u4eec\u4ee5\u524d\u4e5f\u89c1\u8fc7\u9762\xb7\u5f7c\u6b64\u76f4\u90fd\u5f88\u5ba2\u6c14\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4f60\u5ba2\u6c14\u3002\u201d \u3000\u3000\u9b3c\u516c\u5b50\u7b11\u9053\u201c\u4e0d\u9519\uff0c\u6211\u5bf9\u4f60\u5f53\u7136\u5f88\u5ba2\u6c14\uff0c\u4f46\u4f60\u5374\u4e5f\u66fe\u627e\u8fc7\u6211\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u9053\u201c\u8fd9\u6b21\u6211\u5e0c\u671b\u5927\u5bb6\u8fd8\u662f\u5ba2\u5ba2\u6c14\u6c14\u7684\u89c1\u9762\u5bb9\u5ba2\u6c14\u6c14\u7684\u5206\u624b\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u3002\u2019 \u3000\u3000\u9b3c\u516c\u5b50\u9053\u201c\u6211\u53ea\u8981\u95ee\u8fd9\u91cc\u7684\u4e3b\u4eba\u51e0\u53e5\u8bdd\uff0c\u7acb\u523b\u5c31\u8d70\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u884c\u201d \u3000\u3000\u9b3c\u516c\u5b50\u9053\uff1a\u201c\u53ea\u95ee\u4e24\u53e5\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u884c\u201d \u3000\u3000\u9b3c\u516c\u5b50\u5c45\u7136\u8fd8\u662f\u5ba2\u5ba2\u6c14\u6c14\u7684\u5fae\u7b11\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u884c\xb7\u96be\u9053\u4f60\u548c\u8fd9\u91cc\u7684\u4e3b\u4eba\u662f\u670b\u53cb\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u7b11\u9053\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u4f60\u548c\u6211\u4e00\u6837\xb7\u4ece\u6765\u90fd\u6ca1\u6709\u670b\u53cb\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u9053\u201c\u65e2\u7136\u4e0d\u662f\u670b\u53cb\xb7\u4f60\u4e3a\u4ec0\u9ebd\u8981\u7ba1\u8fd9\u95f2\u4e8b\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6211\u5df2\u7ba1\u4e86\u3002\u201d \u3000\u3000\u9b3c\u516c\u4e88\u76ee\u5149\u95ea\u52a8\uff0c\u9053\u201c\u83ab\u975e\u4f60\u4e5f\u5728\u8ddf\u6211\u6253\u6837\u7684\u4e3b\u610f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u3002\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u9053\u201c\u507f\u547d\u7b26\u7684\u94b1\u662f\u4e0d\u662f\u5728\u8fd9\u91cc\u4e0d\u5b9a\xb7\u6211\u5546\u53c8\u4f55\u5fc5\u4e3a\u6b64\u4f24\u4e86\u548c\u6c14\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6eda\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u7b11\u9053\u201c\u6211\u4e0d\u4f1a\u6eda\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u6eda\u5c31\u6b7b\u201d \u3000\u3000\u9b3c\u516c\u4e8e\u9053\u201c\u8c01\u6b7b\u96be\u6d3b\u4e5f\u8fd8\u4e0d\u4e00\u5b9a\xb7\u4f60\u53c8\u4f55\u5fc5\u8981\u51fa\u624b\uff1f\u201d \u3000\u3000\u4ed6\u770b\u6765\u5c45\u7136\u8fd8\u662f\u70b9\u706b\u6c14\u90fd\u6ca1\u6709\uff0c\u4e00\u76f4\u90fd\u597d\u50cf\u662f\u5fcd\u6c14\u541e\u58f0\xb7\u59d4\u66f2\u6c42\u5168\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u6765\u770b\u90fd\u7edd\u5bf9\u770b\u4e0d\u51fa\u4ed6\u51fa\u624b\u7684\u6837\u5b50\u3002 \u3000\u3000\u4f46\u5728\u90a3\u8fb9\u7a97\u95e8\u770b\u7684\u90ed\u5927\u8def\u548c\u71d5\u4e03\xb7\u5374\u7a81\u7136\u540c\u65f6\u9053\u201c\u770b\uff0c\u8fd9\u4eba\u8981\u51fa\u624b\u4e86\u201d \u3000\u3000\u8bf4\u5230\u7b2c\u53c1\u4e2a\u5b57\u65f6\u9b3c\u516c\u5b50\u679c\u7136\u5df4\u51fa\u624b\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u540c\u5239\u90a3\u95ee\u9ed1\u8863\u4eba\u7684\u53cc\u624b\u6307\u63e1\u4f4f\u5382\u80a9\u5f8c\u7684\u5251\u67c4\u3002 \u3000\u3000\u4ed6\u4e24\u53ea\u624b\u5168\u90fd\u4e3e\u8d77\u6574\u4e2a\u4eba\u524d\u9762\u90fd\u53d8\u6210\u4e86\u7a7a\u95e8\uff0c\u5c31\u597d\u50cf\u4e2a\u5b8c\u4e2d\u4e0d\u8bbe\u9632\u7684\u57ce\u5e02\u7b49\u6563\u519b\u957f\u9a71\u76f4 \u5165\u3002 \u3000\u3000\u9b3c\u516c\u5b50\u7684\u6298\u6247\u672c\u6765\u662f\u4ee5\u5224\u5b98\u7b14\u7684\u62db\u5f0f\u70b9\u4ed6\u524d\u80f8\u5893\u673a\u4eba\u7684\u8fd9\u65f6\u6298\u6247\u7a81\u7136\u6cc5\u5f00\xb7\u6247\u6cbf\u968f\u6d12\u4e4b \u52bf\u81ea\u4ed6\u7684\u5c0f\u8179\u523a\u5411\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u7684\u53d8\u5316\u770b\u6765\u597d\u50cf\u5e76\u6ca1\u6709\u4ec0\u9ebd\u7279\u522b\u7cbe\u5999\u4e4b\u5904\uff0c\u5176\u5b9e\u5c31\u5728\u8fd9\u6298\u6247\u64a4\u4e4b\u95f4\u51fa\u624b\u7684\u65b9\u5411\u62db\u5f0f\u7684 \u8def\u6570\u5c31\u597d\u50cf\u4ed6\u624b\u91cc\u7a81\u7136\u95ee\u5df4\u6362\u4e86\u79cd\u5175\u5668\u3002 \u3000\u3000\u8fd9\u7a81\u7136\u5df4\u7531\u70b9\u53d8\u6210\u4e86\u5212\u653b\u52bf\u4e5f\u7a81\u7136\u7531\u70b9\u53d8\u6210\u5382\u9762\u3002 \u3000\u3000\u8d1d\u53d8\u5316\u4e4b\u7cbe\u5999\u5947\u7a81\u5b9e\u5728\u80fd\u4ee4\u4ed6\u7684\u5bf9\u624b\u65e0\u6cd5\u60f3\u8c61\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u80cc\u5f8c\u501a\u67f1\u4e86\u7ad9\u7684\u5730\u65b9\u672c\u6765\u662f\u4e2a\u9000\u65e0\u53ef\u9000\u7684\u6b7b\u5730\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u4ed6\u53cc\u624b\u9ad8\u4e3e\u7a7a\u95e8\u5168\u9732\u53ea\u8981\u662f\u4e2a\u7a0d\u5fae\u61c2\u5f97\u70b9\u6b66\u529f\u7684\u4eba\u5bf9\u654c\u65f6\u90fd\u7edd\u4e0d\u4f1a\u9009\u62e9\u8fd9\u79cd\u5730\u65b9 \u518d\u672f\u4f1a\u9009\u62e9\u8fd9\u79cd\u7684\u59ff\u52bf\u3002 \u3000\u3000\u4ed6\u7684\u5251\u957f\u8fbe\u516d\u5c3a\xb7\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u6839\u672c\u5c31\u6ca1\u6cd5\u5b50\u62d4\u5c71\u6765\u3002 \u3000\u3000\u522b\u4eba\u6839\u672c\u5c31\u6ca1\u6cd5\u4e86\u62d4\u6bd4\u7c73\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6709\u3002 \u3000\u3000\u4e2a\u4eba\u82e5\u9009\u62e9\u4e01\u4e2a\u8fd9\u9ebd\u574f\u7684\u5730\u52bf\u8fd9\u9ebd\u574f\u7684\u59ff\u52bf\u6765\u548c\u4eba\u4ea4\u624b\u4ed6\u82e5\u6c34\u662f\u7b28\u86cb\u5c31\u5b9a\u67ef\u4ed6\u81ea\u5df1\u72ec\u7279 \u7684\u6cd5\u5341\u3002 \u3000\u3000\u9b3c\u516c\u4e8e\u2018\u6247\u5212\u51fa\uff0c\u9ed1\u519c\u4eba\u8eab\u5b50\u7a81\u7136\u8f6c\xb7\u53d8\u6210\u9762\u5bf9\u6954\u5b50\u597d\u8c61\u8981\u548c\u8fd9\u67f1\u4e88\u62e5\u62b1\u6837\u3002 \u3000\u3000\u4ed6\u867d\u7136\u582a\u582a\u5c06\u8fd9\u4e00\u907f\u5347\u4e86\u76ee\u5374\u80cc\u90e8\u5b8c\u4f1e\u5356\u7ed9\u5382\u5bf9\u65b9\u3002 \u3000\u3000\u8fd9\u6cd5\u5b50\u66f4\u662f\u7b28\u4e0d\u53ef\u4e91\u3002 \u3000\u3000\u8fde\u9b3c\u516c\u5b50\u90fd\u4e0d\u7981\u6014\u5382\u6014\u3002\u4ed6\u4e4e\u751f\u548c\u4eba\u4ea4\u624b\u81f3\u5c11\u4e5f\u6709\u7684\u53c1\u767e\u6b21\u5176\u4e2d\u5f53\u7136\u6709\u5404\u5f0f\u5404\u6837\u7684\u4eba\u6709 \u7684\u5f88\u9ad8\u660e\u4e5f\u6709\u7684\u5f88\u5dee\u52b2\u3002 \u3000\u3000\u4f46\u8c61\u8fd9\u6837\u7b28\u7684\u4eba\u4ed6\u5012\u8fd8\u771f\u662f\u5e73\u751f\u7b2c\u6b21\u89c1\u5230\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\xb7\u9ed1\u8863\u4eba\u7684\u624b\u7a81\u7136\u7528\u529b\u5411\u67f1\u5b50\u4e0a\u62a5\u4e24\u6761\u817f\u4e5f\u540c\u65f6\u5411\u67f1\u5b50\u4e0a\u9876\xb7\u8179\u90e8\u5411\u5f8c\u6536 \u7f29\u81c0\u90e8\u5411\u5f8c\u7a81\u8d77\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u4e5f\u7bad\u4e00\u822c\u5411\u5f8c\u7a9c\u4e86\u51fa\u53bb\xb7\u6574\u4e2a\u4eba\u8c61\u662f\u7a81\u7136\u81ea\u4e2d\u95f4\u6298\u6210\u4e86\u4e24\u622a\xb7\u624b\u548c\u817f\u90fd\u53e0\u5230\u8d77\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u65f6\u5251\u5149\u95ea\u3002 \u3000\u3000\u4e00\u67c4\u516d\u5c3a\u957f\u7684\u5bd2\u673a\u5251\u5df2\u51fa\u9798\u3002 \u3000\u3000\u8fd9\u79cd\u62d4\u5228\u7684\u6cd5\u5b50\u4e0d\u4f46\u5947\u7279\u5df1\u6781\uff0c\u800c\u4e14\u8be1\u79d8\u5df2\u6781\u3002 \u3000\u3000\u9b3c\u516c\u5b50\u60f3\u8f6c\u8eab\u8ffd\u51fb\u65f6\uff0c\u5c31\u53d1\u73b0\u8fd9\u67c4\u5bd2\u673a\u5251\u7684\u5251\u5c16\u6b63\u5728\u6307\u4ed6\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7684\u6574\u4e2a\u8eab\u5b50\u90fd\u5728\u957f\u5228\u7684\u5f8c\u9762\u5df2\u8fde\u70b9\u7a7a\u95e8\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u6700\u7b28\u7684\u6cd5\u5b50\u7a81\u7136\u5df2\u53d8\u6210\u4e86\u6700\u7edd\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u9b3c\u516c\u5e72\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u5df3\u8fde\u4e00\u70b9\u8fdb\u51fb\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u9000\u8eab\u5f62\u95ea\u9000\u5230\u6388\u4e88\u5f8c\u3002 \u3000\u3000\u67f1\u65bc\u662f\u5706\u7684\u9ed1\u8863\u4eba\u7684\u5251\u592a\u957f\u4e5f\u7edd\u5bf9\u65e0\u6cd5\u56f4\u67f1\u4e88\u5411\u4ed6\u8fdb\u51fb\u3002 \u3000\u3000\u4ed6\u53ea\u8981\u8d34\u6842\u5b50\u8f6c\xb7\u9ed1\u8863\u4eba\u7684\u5251\u5c31\u4e0d\u53ef\u80fd\u523a\u5230\u4ed6\u3002 \u3000\u3000\u4ed6\u5c31\u53ef\u4ee5\u7b49\u5230\u7b2c\u2606\u6b21\u8fdb\u901d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u8d25\u4e2d\u6c42\u80dc\u3001\u6b7b\u4e2d\u6c42\u6d3b\u7684\u6cd5\u5b50\uff0c\u8fd9\u6cd5\u5b50\u5b9e\u5728\u4e0d\u9519\u3002 \u3000\u3000\u9b3c\u516c\u4e8e\u8d34\u51b3\u67f1\u5b50\u4e0a\u53ea\u7b49\u9ed1\u8863\u4eba\u4ece\u524d\u9762\u7ed5\u8fc7\u6765\u3002\u9ed1\u8863\u4eba\u8fd8\u5728\u683c\u5b50\u7684\u53e6\u8fb9\u8fde\u70b9\u52a8\u9759\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u96be\u9053\u4ed6\u4e5f\u5728\u7b49\u673a\u4f1a\uff1f \u3000\u3000\u9b3c\u516c\u5b50\u677e\u4e86\u53e3\u6c14\u4ed6\u4e0d\u6015\u7b49\u4e0d\u6015\u8017\u65f6\u95f4\u53cd\u6b63\u4ed6\u5df2\u5148\u7acb\u65bc\u4e0d\u8d25\u4e4b\u5730\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8981\u6765\u653b\u5c31\u5f97\u4ece\u524d\u9762\u7ed5\u5927\u5708\u5b50\u4ed6\u5374\u53ea\u8981\u8d34\u68ad\u5b50\u8f6c\u5c0f\u5708\xb7\u4e24\u4e2a\u4eba\u4f53\u529b\u7684\u6d88\u8017\uff0c\u76f8\u5dee\u6700 \u5c11\u6709\u53c1\u56db\u500d\u3002 \u3000\u3000\u90a3\u9ebd\u7528\u4e0d\u591a\u4e45\uff0c\u9ed1\u8863\u4eba\u4f53\u529b\u5c31\u4f1a\u8017\u5c3d\u4ed6\u7684\u673a\u4f1a\u5c31\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u7b14\u5e10\u4ed6\u7b97\u5f97\u5f88\u6e05\u695a\uff0c\u6240\u4ee5\u4ed6\u5f88\u653e\u5fc3\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u542c\u5230\u675c\u4e8e\u5f8c\u9762\u6709\u201c\u9a82\u201d\u7684\u54cd\u5c31\u8c61\u662f\u5544\u6728\u9e1f\u5728\u5544\u6811\u7684 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u7559\u610f\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8feb\u4e00\u5239\u90a3\u4ed6\u7a81\u53c8\u89c9\u5f97\u80cc\u810a\u4e0a\u4e00\u51c9\u3002 \u3000\u3000\u7b49\u4ed6\u53d1\u89c9\u4e0d\u5999\u65f6\u5df2\u611f\u89c9\u5230\u6709\u6837\u51b0\u51b7\u7684\u4e1c\u897f\u523a\u4eba\u4e86\u4ed6\u7684\u80cc\u810a\u3002 \u3000\u3000\u63a5\uff0c\u4ed6\u5c31\u770b\u5230\u8fd9\u6837\u4e1c\u897f\u4ece\u4ed6\u524d\u80f8\u7a7f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u622a\u95ea\u4e4c\u5149\u7684\u5251\u5c16\u3002 \u3000\u3000\u9c9c\u8840\u6b63\u6ef4\u6ef4\u4ece\u5228\u5c16\u4e0a\u6ef4\u4e0b\u6765\u3002 \u3000\u3000\u4f60\u82e5\u7a81\u7136\u770b\u5230\u6234\u5251\u5c16\u4ece\u4f60\u7684\u80f8\u819b\u8179\u7a7f\u51fa\u6765\u4f60\u4f1a\u6709\u4ec0\u9ebd\u611f\u89c9\u5462\uff1f \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u6015\u5f88\u5c11\u6709\u4eba\u80fd\u4f53\u4f1a\u5f97\u5230\uff0c \u3000\u3000\u9b3c\u516c\u5b50\u770b\u8fd9\u6bb5\u5251\u5c16\u8138\u4e0a\u7684\u8868\u60c5\u663e\u5f97\u5f88\u60ca\u8bb6\xb7\u597d\u50cf\u7a81\u7136\u770b\u5230\u4e86\u6837\u72e0\u5947\u602a\uff0c\u5f88\u6709\u8da3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5446\u5446\u7684\u770b\u4e86\u4e24\u77aa\xb7\u5f20\u80dc\u7a81\u7136\u56e0\u6050\u60e7\u800c\u626d\u66f2\u53d8\u5f62\uff0c\u5f20\u5927\u4e86\u77aa\u8c61\u662f\u60f3\u653e\u58f0\u5927\u558a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u51cf\u58f0\u8fd8\u6ca1\u6709\u53d1\u51fa\u6765\uff0c\u6574\u4e2a\u4eba\u5c31\u7a81\u7136\u51b0\u51c9\u7530\u786c\u3002 \u3000\u3000\u5b8c\u5168\u7530\u786c\u3002 \u3000\u3000\u8fdc\u8fdc\u770b\u8fc7\u6765\u597d\u50cf\u4ed6\u8fd8\u5728\u51dd\u89c6\u81ea\u5df2\u80f8\u524d\u7684\u5228\u5c16\u6c89\u601d\u3002 \u3000\u3000\u9c9c\u8840\u8fd8\u5728\u4e0d\u505c\u7684\u81ea\u5228\u5c16\u6ef4\u843d\u3002 \u3000\u3000\u6ef4\u5f97\u5f88\u4fb5\u8d8a\u6765\u8d8a\u9992\u2026\u2026 \u3000\u3000\u4ed6\u7684\u4eba\u8fd8\u662f\u4fdd\u6301\u540c\u6837\u7684\u59ff\u52bf\u79cd\u8bf4\u4e0d\u51fa\u6709\u591a\u9ebd\u8be1\u79d8\u53ef\u6016\u7684\u59ff\u52bf\u3002 \u3000\u3000\u71d5\u4e03\u5df2\u8f6c\u8fc7\u5934\u4e0d\u5fcd\u518d\u770b\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u773c\u775b\u867d\u7136\u5f20\u5f97\u5f88\u5927\u5176\u5b9e\u4e5f\u5e76\u6ca1\u6709\u771f\u7684\u770b\u89c1\u4ec0\u9ebd\u3002\u521a\u624d\u90a3\u2500\u5e55\xb7\u5df2\u7ecf\u628a\u4ed6\u770b\u5f97\u5446 \u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u6e05\u6e05\u695a\u695a\u7684\u770b\u89c1\xb7\u9ed1\u8863\u4eba\u9f13\u6c14\u4f5c\u52bf\u7a81\u7136\u4e00\u5251\u523a\u4eba\u4e86\u67f1\u5b50\u3002 \u3000\u3000\u4ed6\u4e5f\u6e05\u6e05\u695a\u695a\u7684\u770b\u89c1\uff0c\u5251\u5c16\u6295\u5165\u6309\u4e8e\uff0c\u7a81\u7136\u53c8\u4ece\u9b3c\u516c\u5b50\u7684\u524d\u80f8\u7a7f\u51fa\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5f88\u96be\u76f8\u4fe1\u81ea\u5df1\u770b\u5230\u7684\u8fd9\u4ef6\u4e8b\u662f\u771f\u7684\u3002 \u3000\u3000\u4f60\u542c\u6765\u4e5f\u8bb8\u4f1a\u7acb\u523b\u76f8\u4fe1\u4f46\u82e5\u4eb2\u773c\u770b\u5230\u53cd\u800c\u5f88\u96be\u76f8\u4fe1\u3002 \u3000\u3000\u8fd9\u662f\u67c4\u4ec0\u9ebd\u5251\u8fd9\u662f\u4ec0\u9ebd\u5251\u6cd5\uff1f \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u7b49\u4ed6\u773c\u775b\u518d\u80fd\u770b\u5230\u4e1c\u897f\u65f6\u5c31\u53d1\u73b0\u9ed1\u8863\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df2\u5c06\u5228\u62d4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u9b3c\u516c\u5b50\u7684\u4eba\u5374\u8fd8\u7559\u5728\u5251\u5c16\u4e0a\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6b63\u7528\u5251\u5c16\u6311\u9b3c\u516c\u4e8e\u7684\u4f53\u9992\u9992\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e2a\u770b\u4e0d\u89c1\u9762\u76ee\u7684\u9ed1\u8863\u4eba\xb7\u80a9\u4e0a\u625b\u67c4\u516d\u5c3a\u957f\u7684\u5251\u3002 \u3000\u3000\u5251\u950b\u53d1\u4e4c\u5149\u5228\u5c16\u4e0a\u59da\u4e2a\u50f5\u786c\u6254\u66f2\u7684\u7eff\u8863\u4eba\u2026\u2026 \u3000\u3000\u591c\u8272\u51c4\u6e05\uff0c\u5ead\u9662\u5bc2\u9759\u3002 \u3000\u3000\u5047\u5982\u8fd9\u7eb5\u7136\u53ea\u4e0d\u8fc7\u662f\u9609\u56fe\u753b\u770b\u89c1\u8fd9\u5e45\u753b\u56fe\u7684\u4eba\xb7\u4e5f\u4e00\u5b9a\u4f1a\u6bdb\u9aa8\u60ca\u7136\u7684\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u5e76\u4e0d\u662f\u56fe\u753b\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u89c9\u5f97\u5f88\u51b7\u7a81\u7136\u60f3\u627e\u4ef6\u8863\u670d\u62ab\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u4eca\u5929\u665a\u4e0a\u53d1\u751f\u7684\u8fd9\u4ef6\u4e8b\u53ea\u4e0d\u8fc7\u662f\u573a\u7530\u68a6\u800c\u5df2\u3002 \u3000\u3000\u73b0\u5728\u68a6\u5df2\u9192\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5df2\u8d70\u4e86\u51fa\u53bb\u9662\u5b50\u91cc\u5df2\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u8fd8\u662f\u540c\u6837\u7684\u9662\u5b50\uff0c\u540c\u6837\u7684\u591c\u8272\u4ed6\u54fa\u54fa\u9053\u201c\u73b0\u5728\u5230\u8fd9\u91cc\u6765\u7684\u4eba\xb7\u82e5\u80fd\u60f3\u8c61\u5230\u521a\u8fd9\u91cc\u53d1\u751f\u8fc7 \u4ec0\u9ebd\u4e8b\u60c5\u6211\u5c31\u4f69\u670d\u4ed6\u3002\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u521a\u8fd9\u91cc\u53d1\u751f\u8fc7\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u738b\u52a8\u901a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u521a\u8fd9\u91cc\u96be\u9053\u4ec0\u9ebd\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\xb7\u9053\u201c\u4e0d\u9519\xb7\u5df2\u7ecf\u8fc7\u53bb\u4e86\u7684\u4e8b\u6839\u672c\u5c31\u963b\u4ece\u672a\u53d1\u751f\u8fc7\u6c72\u4ec0\u9ebd\u4e24\u6837\u3002\u201d\u738b\u52a8\u9053 \u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f60\u6700\u597d\u83ab\u8981\u591a\u60f3\u60f3\u591a\u4e01\u53cd\u800c\u70e6\u607c\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53c8\u7b54\u5bf9\u4e86\u3002\u201d\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u8fd9\u6b21\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54e6\u201d \u3000\u3000\u85cf\u4e03\u9053\u201c\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u65e0\u8bba\u4f11\u60f3\u4e0d\u60f3\xb7\u90fd\u6837\u4f1a\u6709\u70e6\u607c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u70e6\u607c\uff1f\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u73b0\u5728\u6211\u8fd8\u770b\u4e0d\u51fa\xb7\u4e5f\u60f3\u4e0d\u51fa\u6240\u4ee5\u6211\u624d\u77e5\u9053\u90a3\u5b9a\u662f\u5f88\u5927\u7684\u70e6\u607c\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5ffd\u7136\u540c\u65f6\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u65f6\u90a3\u9ed1\u8863\u4eba\u53c8\u6162\u6162\u7684\u8d70\u4e86\u8fdb\u6765\xb7\u7a7f\u8fc7\u9662\u5b50\xb7\u8d70\u4e0a\u77f3\u9636\uff0c\u7ad9\u5728\u6954\u5b50\u524d\u3002 \u3000\u3000\u4ed6\u80cc\u5f8c\u7684\u957f\u5251\u5df2\u5165\u7bb1\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u6211\u53bb\u95ee\u95ee\u4ed6\u3002\u201d\u4ed6\u4e0d\u7b49\u522b\u4eba\u5f00\u53e3\u5df2\u8df3\u51fa\u7a97\u5b50\xb7\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4ffa\u67f1\u4e8e\u95ed\u773c\u775b\u4f3c\u53c8\u7761\u3002 \u3000\u3000\u7ff1\u5927\u8def\u6545\u610f\u5927\u58f0\u54b3\u55fd\uff0c\u54b3\u5f97\u81ea\u5df2\u7684\u55d3\u5b50\u771f\u7684\u5df2\u6709\u4e9b\u53d1\u75d2\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fd9\u624d\u5f20\u5f00\u773c\uff0c\u51b7\u51b7\u7684\u770b\u4ed6\u51b7\u51b7\u9053\u201c\u770b\u6765\u4f60\u5e94\u8be5\u8d76\u5feb\u53bb\u627e\u4e2a\u5927\u592b\u624d\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\u201c\u6211\u7528\u4e0d\u627e\u5927\u592b\uff0c\u6211\u81ea\u5df1\u4e5f\u6709\u4e13\u6cbb\u54b3\u8d4e\u7684\u836f\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54e6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u65e0\u8bba\u6709\u4ec0\u9ebd\u5927\u5927\u5c0f\u5c0f\u7684\u6bdb\u75c5\uff0c\u4e00\u559d\u9152\u5c31\u597d\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54e6\u3002\u3002\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u559d\u4e24\u676f\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u4f60\u521a\u4e0d\u662f\u5df2\u7ecf\u2026\u2026\u5df2\u7ecf\u6740\u8fc7\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6e2f\u8bf4\u6211\u6740\u8fc7\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u5382\u6014\u9053\u201c\u4f60\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ed1\u519c\u4eba\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u521a\u4f60\u6740\u7684\u90a3\u2026\u2026\u201d\u9ed1\u8863\u4eba\u9053\u201c\u90a3\u4e0d\u662f\u4eba\u4e25\u90ed\u5927\u8def\u6c7e\u7136\u9053\u201c\u90a3\u4e0d\u662f\u4eba\uff1f\u8981\u4ec0\u9ebd \u6837\u7684\u4eba\u624d\u80fd\u7b97\u662f\u4eba\uff1f\u201d\u70c8\u5973\u4eba\u9053\u201c\u8fd9\u4e16\u7684\u4eba\u5f88\u5c11\u3002\u201d\u90ed\u5927\u8def\u53c8\u7b11\xb7\u9053\u201c\u6211\u5462\uff1f\u80fd\u4e0d\u80fd\u7b97\u662f \u4eba\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4f60\u8981\u6211\u6740\u4f60\uff1f\u201d \u3000\u3000\u90ed\u4eba\u8def\u65e5\u5149\u95ea\u52a8\u8fd4\u201c\u4f60\u82e5\u5c0f\u6740\u6211\u600e\u9ebd\u80fd\u5f97\u5230\u50ac\u547d\u7b26\u7684\u8d3c\u8d43\u70c8\u8863\u4eba\u9053\u4e16\u91cc\u6ca1\u6709\u8d3c\u8d43\u8fd9\u91cc\u4ec0 \u9ebd\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u77e5\u9053\uff1f\u201d\u7199\u5973\u4eba\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u672b\u4f60\u4e3a\u4ec0\u9ebd\u6765\u7684\uff1f\u201d \u3000\u3000\u9ed1\u519c\u4eba\u9053\u201c\u9519\u8fc7\u5bbf\u5934\u6765\u501f\u5bbf\u4e00\u5bb5\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u521a\u300f\u4f60\u5374\u4e3a\u8fd9\u4ef6\u4e8b\u6740\u201c\u90a3\u4e2a\u4e0d\u662f\u4eba \u7684\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4e0d\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u9053\u201c\u4f60\u662f\u4e3a\u5382\u6211\u4eec\u6740\u4ed6\u7684\uff1f\u201d\u9ed1\u8863\u4eba\u9053\u201c\u5c0f\u662f\u3002\u201d\u7ff1\u5927\u8def\u201c\u4f60\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d\u9ed1\u8863 \u4eba\u51b7\u51b7\u9053\u201c\u6211\u8981\u7761\u4e86\u6211\u7761\u7684\u65f6\u5019\xb7\u4e0d\u559c\u6b22\u522b\u4eba\u6253\u6270\u3002\u201d\uff01 \u3000\u3000\u4ed6\u679c\u7136\u53c8\u6162\u9992\u7684\u95ed\u8d77\u773c\u775b\u5189\u4e5f\u4e0d\u8bf4\u4e2a\u5b87\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\u770b\u4ed6\u80a9\u5f8c\u7684\u5251\u7adf\u7136\u89c9\u5f97\u81ea\u5df1\u5f88\u8d70\u8fd0\u3002 \u3000\u3000\u7b2c\u98de\u72ac\u65e9\u9ed1\u8863\u4eba\u679c\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u5e26\u8d70\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u7559\u4e0b\u53ea\u7559\u535c\u4e86\u63a5\u4e86\u4e0a\u7684\u4e2a\u6d1e\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u67f1\u5b50\u535c\u7684\u8fd9\u4e2a\u6d1e\uff0c\u5ffd\u7136\u7b11\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u5728\u60f3\u4ec0 \u3000\u3000\u71d5\u4e03\u6447\u6447\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u60f3\u6211\u5b9e\u5728\u5f88\u8d70\u8fd0\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8d70\u8fd0\uff1f\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u535c\u6b21\u9047\u89c1\u7684\u90a3\u9ed1\u8863\u4eba\uff0c\u4e0d\u662f\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u541f\u9053\u201c\u4f46\u8fd9\u6b21\u4f60\u8fd8\u662f\u9047\u89c1\u5382\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u6b21\u6211\u4e5f\u6ca1\u6709\u5012\u6bd2\uff0c\u4ed6\u5bf9\u6211\u4eec\u975e\u4f46\u8fde\u70b9\u6076\u610f\u90fd\u6c72\u884c\u800c\u4e14\u8fd8\u597d\u50cf\u662f\u7279\u5730\u6765\u5e2e\u6211 \u8ba2\u7684\u5fd9\u7684\u3002\u201d\u71d5\u4e03\u9053\u201c\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4f60\u513f\u5b50\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u82e5\u6709\u8fd9\u9ebd\u6837\u4e2a\u513f\u5b50\u4e2d\u53d1\u75af\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4ee5\u4e3a\u4ed6\u771f\u7684\u65e0\u610f\u4e2d\u5230\u8fd9\u91cc\u6765\u7684\u5e2e\u4e86\u6211\u4eec\u4e2a\u5fd9\u4e4b\u7528\u5c31\u5c0f\u58f0\u4e0d\u54cd\u7684\u8d70\u4e86\u975e\u4f46\u4e0d\u8981 \u6211\u6253\u9053\u8c22\u8fde\u6211\u4eec\u7684\u9152\u90fd\u4e0d\u80af\u559d\u676f\u3002\u201d \u3000\u3000\u4ed6\u6447\u5934\u51b7\u7b11\u9053\u201c\u4f60\u4ee5\u4e3a\u5929\u4e0b\u771f\u6709\u8fd9\u9ebd\u6837\u7684\u597d\u4eba\u597d\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\u4ed6\u5b9a\u8fd8\u53e6\u6709\u76ee\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u7684\u76ee\u7684\u662f\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u672c\u7ec6\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u56e0\u4e3a\u4f60\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u624d\u8ba4\u4e3a\u4ed6\u5b9a\u4f1a\u4e3a\u6211\u4eec\u5e26\u6765\u5f88\u591a\u9ebb\u70e6\u7684\u662f\u4e0d\u662f\uff1f\u201d\u853d\u4e03 \u9053\uff0c\u201c\u662f\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u4f60\u60f3\u8fd9\u9ebb\u70e6\u4ec0\u9ebd\u65f6\u5019\u4f1a\u6765\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u76ee\u5149\u51dd\u89c6\u8fdc\u65b9\u7f13\u7f13\u9053\u201c\u5c31\u56e0\u4e3a\u4f60\u4e0d\u77e5\u901a\u90a3\u662f\u4ec0\u9ebd\u9ebb\u70e6\u5426\u5219\u5c31\u4e5f\u7528\u672c\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u795e\u79d8\u7684\u5357\u5bab\u4e11 \u3000\u3000\u4e16\u4e0a\u5e76\u6ca1\u6709\u771f\u6b63\u201c\u7edd\u5bf9\u201d\u7684\u4e8b\u3002 \u3000\u3000\u540c\u6837\u7684\u4ef6\u4e8b\u4f60\u82e5\u7531\u4e0d\u540c\u7684\u89d2\u5ea6\u53bb\u770b\u5c31\u5f80\u5f80\u4f1a\u6709\u4e0d\u540c\u7684\u7ed3 \u3000\u3000\u82e5\u6709\u4e2a\u8ff7\u8def\u5728\u8352\u5c71\u4e2d\u7684\u65c5\u5165\u591c\u534a\u6765\u6572\u95ee\u6c42\u5bbf\u4f53\u53ea\u8981\u8fd8\u6709\u70b9\u540c\u60c5\u5fc3\u5c31\u201c\u7edd\u5bf9\u201d\u5e94\u8be5\u6536\u5bb9\u4ed6 \u7684\u3002 \u3000\u3000\u6765\u7684\u82e5\u662f\u4e2a\u8499\u9762\u7684\u9ed1\u8863\u4eba\uff0c\u4f60\u662f\u4e0d\u662f\u6536\u5bb9\u4ed6\u5c31\u4e0d\u4e00\u5b9a\u4e86\u3002 \u3000\u3000\u5c31\u7b97\u6536\u5bb9\u4ed6\uff0c\u4e5f\u201c\u7edd\u5bf9\u201d\u5e94\u8be5\u6709\u6212\u5fc3\u7684\u591a\u591a\u5c11\u5c11\u603b\u4f1a\u63d0\u9632 \u3000\u3000\u4f46\u6765\u7684\u8fd9\u9ed1\u8863\u4eba\u82e5\u662f\u6628\u5929\u665a\u4e03\u521a\u4e3a\u4f60\u51fa\u8fc7\u529b\u5e2e\u8fc7\u4f60\u5fd9\u7684\xb7\u90a3\u60c5\u51b5\u662f\u4e0d\u662f\u53c8\u5b8c\u5168\u4e0d\u540c\u4e86 \u5462\uff1f \u3000\u3000\u60c5\u51b5\u4e0d\u540c\u505a\u6cd5\u5f53\u7136\u4e5f\u5c31\u4f1a\u6539\u53d8\u3002 \u3000\u3000\u53ea\u6709\u539f\u5219\u624d\u662f\u4e0d\u53d8\u7684\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u65e0\u8bba\u505a\u4ec0\u9ebd\u4e8b\xb7\u65e0\u8bba\u600e\u9ebd\u53bb\u505a\u90fd\u6709\u5b9a\u7684\u539f\u5219\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ed6\u4eec\u7684\u539f\u5219\u662f\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u4ed6\u4eec\u5f88\u5bb9\u6613\u5c31\u4f1a\u5fd8\u8bb0\u522b\u4eba\u7684\u4ec7\u6068\xb7\u5374\u5f88\u96be\u5fd8\u8bb0\u522b\u4eba\u7684\u6069\u60c5\u3002 \u3000\u3000\u4f60\u53ea\u8981\u5bf9\u4ed6\u4eec\u6709\u8fc7\u597d\u5904\uff0c\u65e0\u8bba\u5728\u4ec0\u9ebd\u60c5\u51b5\u4e0b\xb7\u4ed6\u4eec\u90fd\u4e00\u5b9a\u4f1a\u60f3\u6cd5\u5b50\u62a5\u7b54\u4f60\u3002 \u3000\u3000\u53ea\u8981\u662f\u4ed6\u4eec\u7b54\u5e94\u8fc7\u7684\u8bdd\uff0c\u65e0\u8bba\u5728\u4ec0\u9ebd\u60c5\u51b5\u4e0b\xb7\u90fd\u5b9a\u4f1a\u60f3\u6cd5\u5b50\u505a\u5230\u7684\u3002 \u3000\u3000\u5c31\u7b97\u6253\u7834\u5934\u4e5f\u8981\u53bb\u505a\u5230\u3002 \u3000\u3000\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u627e\u85c9\u53e3\u6765\u63a8\u9010\u81ea\u5df1\u7684\u8d23\u4efb\xb7\u66f4\u4e0d\u4f1a\u539a\u8138\u76ae\u8d56\u5e10\u3002 \u3000\u3000\u65e0\u8bba\u9047\u4ec0\u9ebd\u6837\u7684\u4e8b\uff0c\u4ed6\u4eec\u90fd\u7edd\u4e0d\u4f1a\u9003\u907f\u3002 \u3000\u3000\u591c\u534a\u53c8\u6709\u4eba\u6765\u6572\u95e8\u3002 \u3000\u3000\u6572\u95e8\u58f0\u5f88\u6025\u3002 \u3000\u3000\u7b2c\u4e2a\u542c\u5230\u6572\u95e8\u58f0\u7684\uff0c\u4e5f\u8bb8\u662f\u85cf\u4e03\uff0c\u4e5f\u8bb8\u662f\u738b\u52a8\xb7\u4f46\u7b2c\u4e00\u4e2a\u62a2\u53bb\u5e94\u95e8\u7684\xb7\u5374\u5b9a\u662f\u90ed\u5927\u8def\u3002 \u3000\u3000\u6765\u7684\u8fd8\u662f\u6628\u591c\u90a3\u795e\u79d8\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u5e7d\u7075\u822c\u7ad9\u5728\u90a3\u91cc\uff0c\u7f13\u7f13\u9053\u201c\u8352\u5c71\u8ff7\u8def\xb7\u9519\u8fc7\u4e86\u5bbf\u5934\u4e0d\u77e5\u662f\u5426\u80fd\u5728\u8fd9\u91cc\u501f\u5bbf\u4e00 \u5bb5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u9053\u201c\u80fd\uff0c\u5f53\u7136\u80fd\uff0c\u83ab\u8bf4\u53ea\u501f\u5bbf\u4e00\u5bb5\u5c31\u7b97\u5728\u8fd9\u91cc\u4f50\u5e74\u4e5f\u8bbe\u95ee\u9898\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u771f\u7684\u6ca1\u95ee\u9898\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e00\u70b9\u95ee\u9898\u4e5f\u6ca1\u6709\u4e0d\u7ba1\u4f60\u662f\u4e0d\u662f\u9519\u8fc7\u4e86\u5bbf\u5934\u4f60\u968f\u65f6\u6765\u6211\u4eec\u968f\u65f6\u6b22\u8fce\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u9601\u4e0b\u867d\u5982\u6b64\u53ea\u4f2f\u522b\u4eba\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u62a2\u9053\u522b\u4eba\u4e5f\u6837\u4f60\u65e2\u7136\u6765\u4e86\u5c31\u662f\u6211\u4eec\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54ea\u79cd\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u6211\u4eec\u7684\u5ba2\u4eba\u53ea\u6709\u4e00\u79cd\u3002\u201d\u7167\u8863\u4eba\u9053\u201c\u4e3b\u4eba\u5374\u6709\u5f88\u591a\u79cd\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6709\u79cd\u4e3b\u4eba\u968f\u65f6\u90fd\u4f1a\u9010\u5bb9\u7684\u3002\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u90a3\u79cd\u4e3b\u4eba\u8fd9\u5730\u65b9\u7edd\u6ca1\u6709\xb7\u4f60\u53ea\u8981 \u8fdb\u4e86\u8fd9\u9053\u9664\u975e\u4f60\u81ea\u5df1\u613f\u610f\u51fa\u53bb\u5426\u5219\u5c31\u7edd\u4e0d\u4f1a\u6709\u4efb\u4f55\u4eba\u8981\u4f60\u8d70\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5ffd\u7136\u957f\u957f\u53f9\u606f\u4e00\u58f0\u9053\u201c\u770b\u6765\u6211\u679c\u7136\u6ca1\u6709\u6572\u9519\u95e8\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u624d\u998f\u4fb5\u7684\u8d70\u4e86\u8fdb\u6765\u7a7f\u8fc7\u9662\u5b50\xb7\u8d70\u4e0a\u957f\u5eca\u3002 \u3000\u3000\u4ed6\u8d70\u8def\u7684\u59ff\u52bf\u8fd8\u662f\u6ca1\u6709\u53d8\u6837\u5b50\u4e5f\u6ca1\u6709\u53d8\u4f46\u5374\u81f3\u5c11\u6709\u6837\u4e8b\u53d8\u4e86\u53d8\u5f97\u8bdd\u591a\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u8bf4\u7684\u8bdd\u6bd4\u6628\u5929\u4e00\u665a\u4e0a\u52a0\u8d77\u6765\u90fd\u591a\u4e86\u4e24\u53c1\u500d\u3002 \u3000\u3000\u591c\u867d\u5df2\u5f88\u6df1\uff0c\u4f46\u8fd8\u6709\u4e24\u53c1\u95ee\u5c4b\u5b50\u706f\u5149\u662f\u4eae\u7684\u3002 \u3000\u3000\u6797\u592a\u5e73\u597d\u50cf\u8fd8\u5728\u770b\u4e66\u3002\u71d5\u4e03\u5462\uff1f\u4ed6\u5728\u5c4b\u91cc\u505a\u4ec0\u9ebd\uff0c\u4ece\u6765\u90fd\u8bbe\u522b\u4eba\u70df\u9053\uff0c\u56e0\u4e3a\u4ed6\u603b\u662f\u559c\u6b22 \u5c06\u95e8\u7a97\u98a7\u5173\u5f97\u5f88\u87f9\u3002 \u3000\u3000\u7199\u8863\u4eba\u770b\u7a97\u4e0a\u7684\u706f\u5149\u5ffd\u7136\u9053\u4f60\u7684\u670b\u53cb\u90fd\u4f50\u5728\u524d\u9762\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\uff0c\u7b11\u9053\u201c\u6211\u4f4f\u7684\u662f\u6700\u5f8c\u95f4\xb7\u79bb\u5403\u996d\u7684\u5730\u65b9\u5458\u8fd1\u3002\u201d \u3000\u3000\u6700\u5f8c\u95ef\u623f\uff0c\u4e0d\u4f46\u706f\u8fd8\u6ca1\u7184\u95e8\u4e5f\u662f\u5f00\u7684\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8d70\u8fc7\u53bb\xb7\u7ad9\u5728\u95e8\u53e3\u8fc7\u4e86\u5f88\u4e45\u624d\u7f13\u7f13\u9053\u201c\u6709\u4ef6\u4e8b\u9601\u4e0b\u867d\u7136\u672a\u8bf4\uff0c\u60f3\u5fc5\u4e5f\u65e9\u5c31\u77e5 \u9053\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54ea\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6ca1\u6709\u4eba\u771f\u80fd\u7ad9\u7761\u89c9\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u9053\u201c\u8fde\u5750\u7761\u90fd\u5f88\u96be\u3002\u201d \u3000\u3000\u4ece\u62dc\u7684\u95ee\u91cc\u671b\u8fdb\u53bb\u53ef\u4ee5\u770b\u5230\u5c4b\u91cc\u7684\u5f20\u5927\u5e8a\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u770b\u8fd9\u5f20\u5e8a\xb7\u5ffd\u53c8\u53f9\u606f\u58f0\u9053\u201c\u4f46\u8fd8\u6709\u4e9b\u4e8b\u9601\u4e0b\u5374\u60f3\u5fc5\u4e0d\u4f1a\u77e5\u9053\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u54e6\u4f0a \u3000\u3000\u9ed1\u8863\u4eba\u7f13\u7f13\u9053\u201c\u9601\u4e0b\u7edd\u4e0d\u4f1a\u77e5\u9053\uff0c\u6211\u5df2\u6709\u591a\u4e45\u672a\u66fe\u5728\u8fd9\u9ebd\u5927\u7684\u5f20\u5e8a\u4e0a\uff0c\u5b89\u5b89\u7a33\u7a33\u7684\u7761\u8fc7 \u5bb5\u4e86\u3002\u201d\u90ed\u5927\u8def\u7b11\u7b11\uff0c\u9053\u5fb7\u4ef6\u4e8b\u6211\u7684\u786e\u4e0d\u77e5\u9053\u4f97\u5374\u77e5\u9053\u53e6\u5916\u4ef6 \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u77e5\u9053\u4f60\u4eca\u5929\u665a\u4e0a\u5b9a\u53ef\u4ee5\u5728\u8fd9\u5f20\u5e8a\u4e0a\uff0c\u5b89\u5b89\u7a33\u7a33\u7684\u964b\u5bb5\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u96f9\u7136\u56de\u5934\uff0c\u9053\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u9601\u4e0b\u80fd\u8ba9\u6211\u4e00\u76f4\u7761\u5230\u5929\u4eae\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5fae\u7b11\u9053\u201c\u5c31\u7b97\u7761\u5230\u4e2d\u5348\u4e5f\u65e0\u59a8\xb7\u6211\u4fdd\u8bc1\u7edd\u6ca1\u6709\u4eba\u4f1a\u6765\u6253\u6270\u3002\u201d \u3000\u3000\u9ed1\u519c\u4eba\u770b\u4ed6\u773c\u775b\u91cc\u53d1\u5149\u5ffd\u7136\u957f\u957f\u63d6\u518d\u4e5f\u4e0d\u8bf4\u522b\u7684\uff0c\u5c31\u5927\u6b65\u8d70\u4e86\u8fdb\u53bb\u800c\u4e14\u5173\u8d77\u4e86\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u5c4b\u91cc\u7684\u706f\u4e5f\u7184\u706d\u4e86\u3002 \u3000\u3000\u706f\u5df2\u706d\u4e86\u5f88\u4e45\u90ed\u5927\u8def\u624d\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\xb7\u5750\u5728\u95e8\u5916\u5eca\u524d\u7684\u77f3\u9636 \u3000\u3000\u5bcc\u8d35\u5c71\u5e84\u91cc\u5e76\u4e0d\u662f\u6ca1\u6709\u522b\u7684\u7a7a\u623f\u522b\u7684\u7a7a\u5e8a\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u504f\u504f\u8981\u5750\u5728\u8fd9\u91cc\u597d\u50cf\u5df2\u51c6\u5404\u8981\u66ff\u8fd9\u9ed1\u8863\u4eba\u5b88\u591c\u4e00\u6837\u3002 \u3000\u3000\u7d2b\u8863\u5973 \u3000\u3000\u591c\u5f88\u51c9\u77f3\u9636\u66f4\u51c9\u4f46\u4ed6\u4e0d\u5e86\u4e4e\u56e0\u4e3a\u4ed6\u7684\u5fc3\u662f\u70ed\u7684\u3002 \u3000\u3000\u957f\u5eca\u4e0a\u54cd\u8d77\u4e86\u9635\u5f88\u8f7b\u7684\u811a\u6b65\u58f0\u4e2a\u4eba\u8f7b\u8f7b\u7684\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u56de\u5934\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6765\u7684\u662f\u8c01\u3002 \u3000\u3000\u6765\u7684\u5f53\u7136\u662f\u71d5\u4e03\u3002 \u3000\u3000\u4ed6\u62ab\u4ef6\u5f88\u957f\u7684\u888d\u5b50\u888d\u5b50\u62d6\u5728\u5730\u4e0a\xb7\u4ed6\u4e5f\u5728\u77f3\u9636\u4e0a\u5750\u4e0b\u6765\u3002 \u3000\u3000\u7e41\u661f\u6ee1\u5929\u94f6\u6cb3\u5c31\u8c61\u662f\u6761\u53d1\u5149\u7684\u4e1d\u5e26\xb7\u7275\u4e2d\u661f\u548c\u7ec7\u5973\u661f\u5c31\u4eff\u4f5b\u8fd9\u4e1d\u5e26\u4e0a\u7684\u4e24\u9f13\u660e\u73e0\u3002 \u3000\u3000\u5929\u4e0a\u6709\u6bd4\u4ed6\u4eec\u66f4\u4eae\u7684\u5c4b\u4f46\u5374\u6ca1\u6709\u6bd4\u4ed6\u4eec\u66f4\u7f8e\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u8c61\u522b\u7684\u661f\u90a3\u9ebd\u65e0\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u662f\u795e\uff0c\u4ed6\u4ecd\u4e5f\u6709\u548c\u4eba\u7c7b\u540c\u6837\u7684\u7231\u60c5\u548c\u82e6\u96be\u3002 \u3000\u3000\u4ed6\u8ba2\u7684\u82e6\u96be\u867d\u591a\uff0c\u8ddd\u79bb\u867d\u8fdc\u4f46\u4ed6\u4eec\u7684\u7231\u60c5\u5374\u6c38\u8fdc\u5b58\u5728\u3002\u71d5\u4e03\u5ffd\u7136\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u9053\u89c2\u5728\u4f60 \u603b\u8be5\u5df2\u77e5\u9053\u4e86\u5427\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u77e5\u9053\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u9ebb\u70e6\u4f60\u6628\u5929\u665a\u535c\u8fd8\u60f3\u4e0d\u80fd\u7684\xb7\u73b0\u5728\u5374\u5df2\u7ecf\u6765\u4e86\u201c \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u7b11\u9053\u201c\u628a\u81ea\u5df1\u7684\u5e8a\u8ba9\u7ed9\u5ba2\u4eba\u7761\u591c\u5e76\u4e0d\u80fd\u7b97\u9ebb\u70e6\u3002\u201d \u3000\u3000\u989d\u4e03\u9053\u201c\u8fd9\u80fd\u4e0d\u80fd\u7b97\u662f\u9ebb\u70e6\uff0c\u8fd8\u5f97\u770b\u6765\u7684\u5ba2\u4eba\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4e2a\u6709\u9ebb\u70e6\u7684\u4eba\u800c\u4e14\u9ebb\u70e6\u8fd8\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df2\u6709\u9ebb\u70e6\u6240\u4ee5\u624d\u8eb2\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u56e0\u4e3a\u4ed6\u4eca\u5929\u665a\u4e0a\u8981\u8eb2\u5230\u8fd9\u91cc\u6765\u6240\u4ee5\u6628\u5929\u665a\u4e0a\u624d\u5148\u6765\u66ff\u6211\u4eec\u505a\u90a3\u4e9b\u4e8b\u5c31\u597d\u50cf\u8981 \u79df\u623f\u5b50\u7684\u4eba\uff0c\u5148\u6765\u4ed8\u8ba2\u91d1\u6837\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u7528\u4e0d\u88c5\u50bb\xb7\u5176\u5b9e\u8fd9\u9053\u7406\u4f60\u65e9\u4e5f\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u77e5\u9053\u4ec0\u9ebd\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u77e5\u9053\u4eca\u5929\u665a\u4e0a\u5b9a\u4f1a\u6709\u4eba\u6765\u627e\u4ed6\u6240\u4ee5\u624d\u4f1a\u5b88\u5728\u8fd9\u91cc\u51c6\u5907\u66ff\u4ed6\u6321\u4f4f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6c89\u9ed8\u4e86\u534a\u664c\u7f13\u7f13\u9053\u201c\u6628\u5929\u665a\u4e0a\u6709\u4eba\u6765\u627e\u6211\u4eec\u9ebb\u70e6\u7684\u65f6\u5019\u662f\u8c01\u66ff\u6211\u8861\u6321\u4f4f\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u662f\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u672b\uff0c\u4eca\u5929\u665a\u4e0a\u5c31\u7b97\u771f\u6709\u4eba\u8981\u6765\u627e\u4ed6\u9ebb\u70e6\uff0c\u6211\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u66ff\u4ed6\u6321\u6321\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u4e5f\u5f97\u770b\u662f\u4ec0\u9ebd\u6837\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u7763\u4ec0\u9ebd\u6837\u7684\u9ebb\u70e6\u90fd\u6837\uff0c\u6211\u4eec\u65e2\u5df2\u6536\u4e0b\u4e86\u4ed6\u7684\u8ba2\u91d1\u5c31\u5f97\u628a\u623f\u5b50\u79df\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u6c89\u9ed8\u4e86\u4e2d\u664c\u624d\u7f13\u7f00\u9053\u201c\u4f60\u770b\u4ed6\u6b66\u529f\u6bd4\u4f60\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u50cf\u6bd4\u6211\u9ad8\u660e\u4e9b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u73b0\u5728\u6211\u4eec\u8fd9\u91cc\u80fd\u51fa\u4e4e\u7684\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u4ed6\u6321\u4e0d\u4f4f\u7684\u9ebb\u70e6\u6211\u4eec\u80fd\u6321\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4eec\u603b\u5f97\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u201c\u8bd5\u4e00\u8bd5\u201d\u7684\u610f\u601d\u5c31\u662f\u8bf4\u5df2\u6e2f\u5907\u62fc\u547d\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u82e5\u662f\u4e2a\u5f3a\u76d7\xb7\u662f\u4e2a\u6740\u4eba\u7684\u51f6\u624b\u5462\uff1f\u4f60\u4e5f\u66ff\u4ed6\u6321\u4f4f\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u90a3\u5b8c\u5168\u662f\u4e24 \u56de\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u4e24\u56de\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u522b\u4eba\u4e3a\u4ec0\u9ebd\u627e\u4ed6\xb7\u662f\u56de\u4e8b\u6211\u4e3a\u4ec0\u9ebd\u8981\u66ff\u4ed6\u6321\u4f4f\u53c8\u6709\u53e6\u56de\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e3a\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4ed6\u4eca\u5929\u665a\u4e0a\u662f\u6211\u7684\u5ba2\u4eba\xb7\u56e0\u4e3a\u6211\u5df2\u7b54\u5e94\u8fc7\u4ed6\u8ba9\u4ed6\u5b89\u5b89\u7a33\u7a33\u7684\u7761\u591c\u3002\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u522b\u7684\u4f60\u90fd\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53cd\u6b63\u4eca\u5929\u665a\u4e0a\u6211\u7ba1\u7684\u5c31\u53ea\u8fd9\u4e00\u6837\u3002\u201d \u3000\u3000\u85cf\u4e03\u77aa\u4ed6\xb7\u54ac\u7530\u5507\u201c\u4f60\u201d\u3002\u201d\u4f60\u7a76\u7adf\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u5165\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5c31\u662f\u4e2a\u8fd9\u6837\u4e8e\u7684\u4eba\uff0c\u4f60\u65e9\u5c31\u5e94\u8be5\u94a2\u9053\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7530\u4ed6\u7a81\u7136\u8dfa\u4e86\u8dfa\u811a\uff0c\u7ad9\u8d77\u6765\uff0c\u626d\u5934\u5c31\u8d70\xb7 \u3000\u3000\u8d70\u4e86\u4e24\u6b65\xb7\u53c8\u505c\u4e0b\uff0c\u5c06\u8eab\u4e0a\u8fdb\u7684\u888d\u4e88\u4e00\u62c9\xb7\u7529\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u9053\u4f11\u4f2f\u8d39\u51b7\uff0c\u5c31\u6700\u597d\u66ff\u6211\u627e\u74f6\u7586\u6765\u3002\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5634\u5507\xb7\u72e0\u68cd\u9053\u201c\u6211\u6015\u4f60\u51b7\uff1f\u6211\u6015\u51bb\u4e0d\u6b7b\u4f60\u3002\u201d \u3000\u3000\u888d\u5b50\u53c8\u5bbd\u53c8\u5927\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u7684\u3002 \u3000\u3000\u71d5\u4e03\u7684\u5c4b\u4e88\u91cc\u9762\u597d\u50cf\u603b\u662f\u4f1a\u51fa\u73b0\u4e9b\u5947\u5947\u602a\u602a\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4ee5\u524d\u4ed6\u6bcf\u9694\u4e00\u9635\u4e88\u603b\u800d\u5931\u8e2a\u51e0\u5929\uff0c\u8fd1\u6765\u8fd9\u6bdb\u75c5\u4f3c\u5df2\u6e10\u6e10\u6539\u4e86\uff0c\u4f46\u90ed\u5927\u8def\u603b\u89c9\u5f97\u4ed6\u8fd8\u662f\u6709 \u70b9\u795e\u79d8\u8e6c\u6bcf\u4e2a\u4eba\u90fd\u6709\u70b9\u8ddd\u79bb\u3002 \u3000\u3000\u50cf\u4ed6\u4f55\u8fd9\u9ebd\u597d\u7684\u670b\u53cb\xb7\u8fd9\u79cd\u8ddd\u79bb\u672c\u6765\u5e94\u8be5\u65e9\u5df3\u4e0d\u518d\u5b58\u5728\u3002 \u3000\u3000\u888d\u5b50\u5f88\u65e7\u4e86\u4e5f\u5f88\u810f\u800c\u4e14\u5230\u5904\u90fd\u662f\u8865\u9489\xb7\u4f46\u5374\u70b9\u4e5f\u4e0d\u81ed\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u90ed\u5927\u8def\u4e00\u76f4\u90fd\u72e0\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u71d5\u4e03\u597d\u50cf\u4ece\u6765\u90fd\u6ca1\u6709\u6d17\u8fc7\u6fa1\u4f46\u70b9\u4e5f\u4e0d\u81ed\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u8eab\u4e0a\u867d\u7136\u810f\u4f46\u5c4b\u4e88\u91cc\u5374\u603b\u662f\u6536\u62fe\u5f97\u4e8e\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e0b\u5b9a\u51b3\u5fc3\xb7\u660e\u5929\u5b9a\u8981\u95ee\u4ed6\u53e5\u201c\u4f60\u7a76\u7adf\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u73b0\u5728\u9893\u4e03\u5c4b\u5b50\u91cc\u7684\u706f\u4e5f\u7184\u4e86\u4f46\u90ed\u5927\u8def\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u771f\u7761\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c06\u888d\u5b50\u62ab\u5728\u8eab\u4e0a\u5fc3\u91cc\u7acb\u523b\u5145\u6ee1\u4e86\u6e29\u6696\u4e4b\u610f\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u77e5\u9053\u71d5\u4e03\u5634\u91cc\u65e0\u8bba\u8bf4\u5f97\u591a\u9ebd \u786c\u4f46\u53ea\u8981\u662f\u4ed6\u7684\u4e8b\u71d5\u4e03\u5c31\u2026\u5b9a\u6bd4\u8c01\u90fd\u5173\u5fc3\uff0c\u6bd4\u8c01\u90fd\u6025\u3002 \u3000\u3000\u591c\u5f88\u9759\u98ce\u5439\u5899\u89d2\u7684\u5939\u7af9\u6843\u82b1\u5f71\u5a46\u5a46\u3002 \u3000\u3000\u90ed\u5927\u8def\u771f\u60f3\u627e\u70b9\u9152\u6765\u559d\u559d\uff0c\u4f46\u5c31\u662f\u8fd9\u65f6\xb7\u4ed6\u5ffd\u7136\u542c\u5230\u9635\u5947\u5f02\u7684\u4e50\u58f0\u3002 \u3000\u3000\u4e50\u58f0\u8f7b\u5999\u98d8\u5ffd\u5f00\u59cb\u7684\u65f6\u5019\u4eff\u4f5b\u5728\u4e1c\u8fb9\u5ffd\u7136\u53c8\u5230\u4e86\u897f\u8fb9\u3002 \u3000\u3000\u63a5\xb7\u56db\u9762\u516b\u65b9\u597d\u50cf\u90fd\u54cd\u8d77\u4e86\u8fd9\u9ebd\u5947\u5f02\u7684\u4e50\u58f0\u3002 \u3000\u3000\u201c\u6765\u4e86\u627e\u9ebb\u70e6\u7684\u4eba\u4e2d\u7ade\u6765\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u89c9\u5f97\u5168\u8eab\u53d1\u70ed\u8fde\u5fc3\u8df3\u90fd\u53d8\u5f97\u6bd4\u5e73\u5e38\u5feb\u4e86\u4e24\u53c1\u500d\u3002 \u3000\u3000\u6765\u7684\u7a76\u7adf\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u4eba\uff1f\u4ed6\u5f53\u7136\u731c\u4e0d\u51fa\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u77e5\u9053\u90a3\u5b9a\u662f\u4e2a\u5f88\u5389\u5bb3\u7684\u89d2\u8272\uff0c\u5426\u5219\u9ed1\u8863\u4eba\u53c8\u600e\u4f1a\u6015\u5f97\u8eb2\u8d77\u6765\uff1f\u6765\u7684\u4eba\u8d8a\u5389\u5bb3\u8fd9\u4ef6 \u4e8b\u5c31\u8d8a\u523a\u6fc0\u3002 \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u77aa\u5f97\u5927\u5927\u7684\u8eab\u4e0a\u88ab\u66f2\u888d\u5b50\u4e5f\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7a81\u7136\u201c\u7830\u201d\u7684\u4e00\u58f0\u5927\u95e8\u88ab\u649e\u5f00\u3002 \u3000\u3000\u4e24\u4e2a\u5377\u53d1\u89c1\u7530\uff0c\u52fe\u9f3b\u78a7\u773c\u7a57\u8d64\u4e0a\u8eab\u7684\u6606\u4ed1\u5974\uff0c\u7a81\u7136\u5728\u95e8\u53e3\u51fa\u73b0\uff0c\u8eab\u4e0a\u53ea\u7a7f\u6761\u7ee3\u91d1\u7684\u6492\u811a \u88e4\uff0c\u5de6\u8033\u4e0a\u63a5\u4e2a\u5f88\u5927\u7684\u91d1\u73af\u3002 \u3000\u3000\u4ed6\u4eec\u624b\u91cc\u6367\u5377\u7ea2\u6be1\xb7\u4ece\u95e8\u53e3\u4e00\u76f4\u94fa\u5230\u9662\u5b50\u91cc\uff0c\u7136\u5f8c\u5c31\u51cc\u7a7a\u4e2a\u7ffb\u8eab\uff0c\u540c\u65f6\u9000\u4e86\u51fa\u53bb\u8fde\u9668\u89d2 \u90fd\u6ca1\u6709\u8198\u90ed\u5927\u8def\u773c\uff0c\u5c31\u597d\u50cf\u9662\u4e8e\u91cc\u6839\u672c\u6ca1\u6709\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u867d\u5df2\u5174\u594b\u5f97\u8fde\u6c57\u90fd\u5192\u4e86\u51fa\u6765\u5374\u8fd8\u662f\u6c89\u4f4f\u4e86\u6c14\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u597d\u620f\u5b9a\u8fd8\u5728\u5f8c\u5934\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6606\u4ed1\u5974\u6765\u5f97\u867d\u5947\u7a81\u8be1\u79d8\xb7\u4f46\u4e5f\u53ea\u4e0d\u8fc7\u662f\u8dd1\u9f99\u5957\u7684\u4e3b\u89d2\u5b9a\u8fd8\u6ca1\u6709\u767b\u573a\u3002 \u3000\u3000\u95e8\u5916\u679c\u7136\u7acb\u523b\u53c8\u6709\u4e24\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u6253\u626e\u5f97\u5947\u5f62\u602a\u72b6\u7684\u86ee\u5973\u6ee1\u5934\u9ed1\u53d1\u68b3\u6210\u4e86\u4e03\u516b\u535c\u6839\u5c0f\u8fab\u5b50\u4e1c\u4e00\u6839\u897f\u6839\u968f\u4e50\u58f0\u64ad\u6765\u6447 \u53bb\u3002 \u3000\u3000\u4e24\u4eba\u624b\u4e0a\u90fd\u63d0\u5f88\u5927\u7684\u82b1\u7bee\uff0c\u6b63\u7528\u5ae9\u85d5\u822c\u7684\u7269\u81c2\u5c06\u6735\u6735\u4e94\u989c\u516d\u8272\u7684\u9c9c\u82b1\u64a4\u5728\u7ea2\u6be1\u4e0a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u957f\u5f97\u5f88\u7f8e\uff0c\u77ed\u88d9\u4e0b\u9732\u51fa\u622a\u4e70\u767d\u6676\u8282\u7684\u5c0f\u817f\u3002 \u3000\u3000\u817f\u4e0a\u6234\u4e32\u91d1\u94c3\u968f\u821e\u59ff\u201c\u53ee\u53ee\u5f53\u5f53\u201d\u7684\u54cd\u3002 \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u5f20\u5f97\u66f4\u5927\u5382\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u4eec\u90ca\u4e5f\u773c\u89d2\u90fd\u6ca1\u6709\u889c\u8fd9\u8fb9\u8198\u773c\uff0c\u64a4\u5b8c\u4e86\u9c9c\u82b1\u4e5f\u51cc\u4e2d\u4e2a\u7ffb\u8eab\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u770b\u6765\u8fd9\u4ef6\u4e8b\u4e0d\u4f46\u8d8a\u6765\u8d8a\u523a\u6fc0\u800c\u4e14\u4e5f\u8d8a\u6765\u8d8a\u6709\u8da3\u4e86\u3002\u201d \u3000\u3000\u65e0\u6ca6\u4ec0\u9ebd\u4e8b\uff0c\u5177\u589e\uff0c\u82e5\u6709\u7f8e\u5973\u53c2\u52a0\u603b\u662f\u7279\u522b\u523a\u6fc0\u6709\u8da3\u7684\u3002 \u3000\u3000\u4f55\u51b5\u7f8e\u5973\u597d\u50cf\u4e5f\u8d8a\u6765\u8d8a\u591a\u4e86\u3002 \u3000\u3000\u56db\u4e2a\u957f\u88d9\u66f3\u5730\u9ad8\u53ec\u5806\u4e91\u7684\u5bab\u88c5\u5c11\u5973\u624b\u63d0\u56db\u76cf\u5bab\u706f\u7231\u888b\u800c\u6765\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u90c1\u662f\u98ce\u59ff\u7ef0\u7ea6\u7f8e\u5982\u5929\u4ed9\xb7\u521a\u505c\u4e0b\u811a\u6b65\uff0c\u90a3\u4e24\u4e2a\u8eab\u9ad8\u817f\u957f\u7684\u6606\u4ed1\u5974\u5c31\u62a2\u67b6\u80e1\u5e86\u81ea\u95e8 \u5916\u5927\u6b65\u7f51\u4eba\u3002 \u3000\u3000\u80e1\u5e8a\u4e0a\u659c\u501a\u4e2a\u7d2b\u519c\u8d35\u5987\u624b\u91cc\u6258\u4e2a\u4eae\u94f6\u6c34\u70df\u888b\xb7\u60a0\u60a0\u95f2\u95f2\u7684\u5438\u8f7b\u70df\u4e91\u96fe\u822c\u56db\u50b2\u7ef8\u7f34\u5979\u7684\u9762 \u76ee\u5982\u5728\u4e91\u96fe\u91cc\u3002 \u3000\u3000\u5979\u624b\u91cc\u67b6\u6839\u5f88\u957f\u7684\u9f99\u5934\u626c\u6756\u5e8a\u8fb9\u8fd8\u6709\u4fdd\u5112\u5c11\u5973\uff0c\u6b63\u5728\u8f7b\u8f7b\u7684\u66ff\u4ed6\u6376\u817f\u3002 \u3000\u3000\u90ed\u5927\u8def\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u867d\u7136\u770b\u4e0d\u5230\u8fd9\u7d2b\u8863\u8d35\u5987\u7684\u9762\u76ee\u4f46\u770b\u5230\u8fd9\u8001\u6756\u770b\u5230\u8fd9\u7761\u817f\u7684\u5c11\u5973\xb7\u65e0\u8bba\u8c01\u90fd\u5df2\u80fd\u731c\u5f97\u51fa \u5979\u5e74\u7eaa\u5b9a\u5df2\u4e0d\u5c0f\u3002 \u3000\u3000\u8fd9\u771f\u662f\u552f\u7f8e\u4e2d\u4e0d\u8db3\u7684\u4e8b\u3002 \u3000\u3000\u4e8b\u60c5\u53d1\u5c55\u5230\u8fd9\u91cc\xb7\u76f4\u90fd\u5f88\u6709\u8da3\uff0c\u4e3b\u89d2\u82e5\u4e5f\u662f\u4e2a\u82b1\u5bb9\u6708\u8c8c\u7684\u7f8e\u4eba\u5c82\u975e\u5c31\u66f4\u5341\u5168\u5341\u7f8e\u4e86\uff1f \u3000\u3000\u5e78\u597d\u90ed\u5927\u8def\u4e00\u5411\u5f88\u4f1a\u5b89\u6170\u81ea\u5df1\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u8001\u592a\u5a46\u4e00\u5b9a\u662f\u4e2a\u72e0\u4e86\u4e0d\u8d77\u7684\u89d2\u8272\uff0c\u53ea\u770b\u5230 \u5979\u8fd9\u79cd\u6c14\u5faa\uff0c\u6c5f\u6e56\u4e2d\u53ea\u4f2f\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4 \u3000\u3000\u6240\u4ee5\u8fd9\u4ef6\u4e8b\u6bd5\u7adf\u8fd8\u662f\u5f88\u6709\u8da3\u7684\u3002 \u3000\u3000\u81f3\u65bc\u8fd9\u8001\u592a\u5a46\u662f\u4ec0\u9ebd\u4eba\uff1f\u600e\u9ebd\u4f1a\u548c\u90a3\u9ed1\u8863\u4eba\u7ed3\u4e0b\u4e86\u4ec7\uff1f \u3000\u3000\u4ec7\u6068\u7a76\u7adf\u6709\u591a\u6df1\u90ed\u5927\u8def\u662f\u4e0d\u662f\u80fd\u6321\u5f97\u4f50\uff1f \u3000\u3000\u8fd9\u51e0\u70b9\u4ed6\u597d\u50cf\u8fde\u60f3\u90fd\u6c72\u6709\u60f3\u3002 \u3000\u3000\u4e8b\u60c5\u65e2\u7136\u5df4\u9971\u63fd\u5728\u81ea\u5df1\u8eab\u4e0a\xb7\u53cd\u6b63\u6321\u4e0d\u4f4f\u4e5f\u8981\u6321\u7684\u60f3\u53c8\u6709\u4ed8\u9ebd\u7528\uff1f \u3000\u3000\u6240\u4ee5\u4ed6\u7d22\u6027\u6c89\u4f4f\u4e86\u6c14\u7b49\u522b\u4eba\u4e0d\u5f00\u53e3\u4ed6\u4e5f\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u522b\u7684\u4eba\u4e5f\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u90a3\u7d2b\u8863\u5987\u4eba\u5634\u91cc\u7a81\u7136\u55b7\u51fa\u4e86\u53e3\u6d53\u70df\u7bad\u822c\u5411\u90ed\u5927\u8def\u55b7\u5382\u8fc7\u6765\u3002 \u3000\u3000\u597d\u6d53\u7684\u59fb\u3002 \u3000\u3000\u90ed\u5927\u8def\u867d\u7136\u559d\u9152\uff0c\u90e8\u4ece\u4e0d\u62bd\u70df\u88ab\u545b\u5f97\u51e0\u4e4e\u8fde\u773c\u6cea\u90fd\u6d41\u4e86\u51fa\u6765\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u9a82\u4e86\u3002 \u3000\u3000\u5047\u4e2a\u4eba\u82e5\u80fd\u5c06\u53e3\u70df\u55b7\u5f97\u8fd9\u9ebd\u76f4\u8fd9\u9ebd\u8fdc\u4f60\u5bf9\u5979\u8fd8\u662f\u5ba2\u6c14\u70b9\u7684\u597d\u3002 \u3000\u3000\u70df\u96fe\u8fd8\u672a\u6d88\u6563\uff0c\u53ea\u542c\u4eba\u9053\u201c\u4f60\u662f\u4ec0\u9ebd\u4eba\u53c1\u66f4\u534a\u591c\u7684\u5750\u5728\u8fd9\u91cc\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u58f0\u97f3\u53c8\u54cd\u53c8\u8106\uff0c\u542c\u8d77\u6765\u5012\u4e0d\u8c61\u8001\u592a\u5a46\u7684\u58f0\u97f3\uff0c\u4f46\u4e5f\u5e76\u4e0d\u597d\u542c\xb7\u95ee\u8d77\u8bdd\u6765\u66f4\u662f\u53c8\u51f6\u53c8\u6a2a\u5c31 \u597d\u50cf\u516c\u5dee\u5728\u95ee\u5c0f\u5077\u4f3c\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u201c\u8fd9\u91cc\u597d\u50cf\u662f\u6211\u7684\u5bb6\xb7\u4e0d\u662f\u4f60\u7684\u4e2a\u4eba\u5750\u5728\u81ea\u5df1\u7684\u5bb6\u91cc\uff0c\u603b\u4e0d\u8be5\u72af \u6cd5\u5427\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\u53c8\u662f\u95e8\u70df\u8fce\u9762\u55b7\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u53e3\u59fb\u66f4\u6d53\xb7\u90f8\u5927\u8def\u88ab\u545b\u5f97\u5fcd\u4e0d\u4f4f\u54b3\u55fd\u8d77\u6765\u800c\u4e14\u8138\u4e0a\u597d\u50cf\u88ab\u9488\u5728\u5269\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u4eba\u9053\u201c\u6211\u95ee\u4f60\u53e5\u4f60\u5c31\u7b54\u4e00\u53e5\u6700\u597d\u5c11\u73a9\u82b1\u8154\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u6478\u8138\xb7\u82e6\u7b11\u9053\u201c\u770b\u6837\u5b50\u6211\u60f3\u4e0d\u660e\u767d\u4e5f\u4e0d\u884c\u3002\u201d \u3000\u3000\u8363\u8863\u8d35\u5987\u9053\u201c\u5357\u5bcc\u4e11\u5728\u54ea\u91cc\xb7\u50cf\u4fa0\u70b9\u53bb\u53eb\u4ed6\u7f00\u51fa\u6765\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u679c\u7136\u662f\u5357\u5bab\u4e11\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u62b1\u6b49\u5f97\u72e0\u6211\u4e0d\u80fd\u53eb\u4ed6\u6eda\u51fa\u6765\uff0c\u201d \u3000\u3000\u7d2b\u8863\u8d35\u5987\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7b2c\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u662f\u7403\xb7\u4e0d\u4f1a\u6eda\u7b2c\u4e8c\u56e0\u4e3a\u4ed6\u5df2\u7761\uff0c\u65e0\u8bba\u8c01\u8981\u53bb\u53eb\u9178\u4ed6\u90fd\u5f97\u5148\u505a\u4ef6 \u4e8b\u3002 \u3000\u3000\u7d2b\u8863\u8d35\u5987\u9053\u201c\u4ec0\u9ebd\u4e8b\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5148\u8ba9\u6211\u5012\u4e0b\u53bb\u3002\u201d \u3000\u3000\u7d2b\u8863\u8d35\u5987\u51b7\u7b11\u9053\u201c\u90a3\u5bb9\u6613\u3002\u201d \u3000\u3000\u8fd9\u53c1\u4e2a\u5b87\u8fd8\u672a\u8bf4\u5b8c\xb7\u70df\u96fe\u4e2d\u7a81\u7136\u98de\u6765\u4e00\u6761\u4eba\u5f71\xb7\u5bd2\u5149\u95ea\u76f4\u53d6\u90ed\u5927\u8def\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u4eba\u6765\u5f97\u771f\u5feb\uff0c\u5e78\u597d\u90ed\u5927\u8def\u7684\u53cd\u5e94\u4e5f\u4e0d\u6162\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u521a\u8eb2\u5f00\u8fd9\u4e00\u5251\u7b2c\u4e8c\u5251\u53c8\u8ddf\u6765\u4e86\xb7\u4e00\u5251\u63a5\u5251\xb7\u53c8\u72e0\u53c8\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u907f\u5f00\u7b2c\u56db\u5251\u65f6\u624d\u770b\u51fa\u8fd9\u4eba\u539f\u6765\u7adf\u662f\u90a3\u6376\u817f\u7684\u682a\u5112\u5c11\u5973\u3002 \u3000\u3000\u5979\u8eab\u9ad8\u4e0d\u6ee1\u53c1\u5c3a\xb7\u7528\u7684\u5251\u4e5f\u6700\u591a\u53ea\u6709\u4e00\u5c3a\u516d\u4e03\u4f46\u634c\u6cd5\u5374\u8f9b\u9b4f\u8be1\u79d8\u5df2\u53ef\u7b97\u662f\u6c5f\u6e56\u4e2d\u7684\u6d41\u8eab \u624b\u3002 \u3000\u3000\u53ea\u53ef\u63aa\u5979\u7684\u4eba\u5b9e\u5728\u592a\u5c0f\xb7\u5251\u5b9e\u5728\u592a\u77ed \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u6284\u4f50\u4e86\u90a3\u4ef6\u957f\u888d\xb7\u968f\u624b\u64a4\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u888d\u5b50\u53c8\u957f\u53c8\u5927\uff0c\u5c31\u8c61\u662f\u4e00\u5927\u7247\u4e4c\u4e91\u6837\uff0c\u90a3\u9ebd\u5c0f\u7684\u4e00\u4e2a\u4eba\u8981\u60f3\u4e0d\u88ab\u5b83\u5305\u4f50\u5b9e\u5728\u5f88\u96be\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u201c\u7530\u54fc\u201d\u2500\u58f0\u5a07\u5598\u9053\u201c\u4ee5\u5927\u6b3a\u5c0f\u4e0d\u8981\u8138\u4e0d\u8981\u80a1\u3002\u201d \u3000\u3000\u8bdd\u624d\u8bf4\u5b8c\u4eba\u5df4\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u4e0d\u8981\u8106\u81f3\u5c11\u4e5f\u603b\u6bd4\u4e0d\u8981\u547d\u597d\u3002\u201d \u3000\u3000\u7d2b\u8863\u8d35\u5987\u51b7\u7b11\u9053\u201c\u4f60\u6562\u6765\u7ba1\u6211\u7684\u95ed\u4e8b\uff0c\u8fd8\u60f3\u8981\u547d\u9ebd\uff1f\u201d \u3000\u3000\u51b7\u7b11\u58f0\u4e2d\uff0c\u90a3\u4e24\u4e2a\u5377\u53d1\u89c1\u7537\u7684\u6606\u4ed1\u5974\u5df2\u51fa\u73b0\u5728\u4ed6\u9762\u524d\xb7\u770b\u6765\u5c31\u8c61\u662f\u4e24\u5ea7\u94c1\u5854\u4f3c\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u53f9\u4e86\u53e3\u6c14\u7483\u54cd\u9053\u201c\u5c0f\u66f2\u5b9e\u5728\u592a\u5c0f\xb7\u5927\u66f2\u53c8\u5b9e\u5728\u592a\u5927\xb7\u8fd9\u600e\u9ebd\u529e\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u8fd9\u4e24\u4eba\u51fa\u624b\xb7\u8eab\u5b50\u7a81\u7136\u5f80\u524d\u4e00\u51b2\xb7\u5df2\u81ea\u4ed6\u4eec\u7684\u808b\u4e0b\u6e38\u9c7c\u822c\u94bb\u4e86\u51fa\u53bb\xb7\u4e00\u6b65\u5c31\u7a9c\u5230 \u80e1\u5e8a\u524d\xb7\u7b11\u901a\u201c\u8fd8\u662f\u4f60\u4e0d\u5927\u4e0d\u5c0f\uff0c\u4f60\u82e5\u4e0d\u662f\u592a\u8001\u4e86\u4e9b\u521a\u521a\u597d\u613f\u6211\u80fd\u914d\u5f97\u4e0a\u3002\u201d \u3000\u3000\u7d2b\u8863\u5458\u5987\u51b7\u7b11\u9053\u201c\u4f60\u8bf4\u6211\u592a\u8001\u4e86\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5979\u9762\u524d\u7684\u59fb\u96fe\u5df2\u6e10\u6e10\u6d88\u6563\xb7\u90ed\u5927\u8def\u7ec8\u4e8e\u770b\u5230\u4e86\u5979\u7684\u8111\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u5fcd\u4e0d\u4f4f\u60ca\u547c\u4e86\u4e00\u58f0\u5c31\u8c61\u662f\u770b\u5230\u4e86\u9b3c\u4f3c\u7684\uff0c\u4e00\u6b65\u6b65\u5f80\u5f8c \u3000\u3000\u4ed6\u4ece\u672a\u60f3\u5230\u770b\u89c1\u7684\u5c45\u7136\u662f\u8fd9\u9ebd\u6837\u5f20\u8138\u3002 \u3000\u3000\u4e00\u5f20\u53c8\u6f02\u4eae\uff0c\u53c8\u5e74\u9752\u7684\u8138\uff0c\u867d\u7136\u53c8\u5f90\u59fb\u814a\u53c8\u62b9\u7c89\uff0c\u5c3d\u91cf\u6253\u626e\u6210\u5927\u4eba\u7684\u6837\u5b50\xb7\u5374\u8fd8\u662f\u63a9\u4e0d \u4f4f\u8138\u4e0a\u7684\u7a1a\u6c14\u5c31\u6b63\u5982\u8005\u592a\u5a46\u6c38\u8fdc\u8bbe\u6cd5\u5b50 \u3000\u3000\u8fd9\u6c14\u6d3e\u5947\u5927\uff0c\u53c8\u62bd\u70df\uff0c\u53c8\u8981\u4eba\u6376\u817f\u7684\u201c\u8005\u592a\u5a46\u201d\uff0c\u7adf\u662f\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u5927\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u7d2b\u8863\u4ea5\u5df2\u9992\u6162\u7684\u4ece\u80e1\u5e8a\u4e0a\u7ad9\u4e86\u8d77\u6765 \u3000\u3000\u53cc\u773c\u775b\u94dc\u94c3\u822c\u77aa\u4ed6\u3002 \u3000\u3000\u4ed6\u6b65\u6b65\u5f80\u5f8c\u9000\u3002 \u3000\u3000\u7d2b\u519c\u5973\u5c31\u6b65\u6b65\u903c\u524d\u6765\u624b\u91cc\u5c45\u7136\u8fd8\u6280\u90a3\u6839\u9f99\u5934\u6756\u3002 \u3000\u3000\u8fd9\u5c0f\u7ad9\u5a18\u660e\u660e\u53c8\u5e74\u9752\u3001\u53c8\u6f02\u4eae\u3001\u4e3a\u4ec0\u9ebd\u504f\u504f\u8981\u505a\u51fa\u8001\u592a\u5a46\u7684\u6a21 \u3000\u3000\u770b\u5979\u81f3\u591a\u4e5f\u53ea\u4e0d\u8fc7\u5341\u516d\u4e03\u5c81\u53c8\u600e\u4f1a\u6709\u90a3\u9ebd\u6df1\u539a\u7684\u529f\u529b\xb7\u5c31\u8fde\u5979\u624b\u4e0b\u4e2a\u5c0f\u4e2b\u5934\xb7\u90fd\u6709\u90a3\u9ebd \u9ad8\u7684\u5251\u672f\xb7\u90a3\u4e24\u4e2a\u6606\u4ed1\u5974\u5f53\u7136\u4e5f\u7edd\u4e0d\u4f1a\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u89d2\u8272\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u662f\u51ed\u4ec0\u9ebd\u80fd\u670d\u5f97\u4f4f\u8fd9\u4e9b\u4eba\u7684\u5462\uff1f \u3000\u3000\u5979\u53c8\u600e\u4f1a\u548c\u6210\u540d\u5df2\u5728\u4e8c\u5341\u5e74\u4ee5\u4e0a\u7684\u5357\u5bab\u4e11\u7ed3\u4e0b\u4e86\u4ec7\u6068\uff1f \u3000\u3000\u4ee5\u5357\u5bcc\u4e11\u7684\u540d\u58f0\u548c\u5251\u6cd5\u4e3a\u4ec0\u9ebd\u5bf9\u8fd9\u5c0f\u59d1\u5a18\u6015\u5f97\u8981\u547d\uff1f \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u60f3\u4e0d\u901a\xb7\u73b0\u5728\u4ed6\u6839\u672c\u4e5f\u8bbe\u5de5\u592b\u60f3\u3002\u7d2b\u8863\u4ea5\u7684\u773c\u775b\u867d\u7f8e\u77aa\u4f60\u7684\u65f6\u5019\uff0c\u5374\u597d\u50cf\u8001 \u864e\u8981\u5403\u4eba\u4f3c\u7684\u51b7\u51b7\u9053\u201c\u6211\u8001\u4e0d\u8001\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8001\u70b9\u4e5f\u4e0d\u8001\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u79be\u662f\u60f3\u8ddf\u6211\u914d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u672f\u2026\u2026\u4e0d\u60f3\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5012\u4e0d\u662f\u5047\u8bdd\uff0c\u8c61\u8fd9\u6837\u7684\u5973\u6444\u5b50\u4e5f\u6ca1\u4eba\u80fd\u53d7\u5f97\u4e86\u7684\u3002\u7d2b\u8863\u5973\u9053\u201c\u4f60\u60f3\u4e2d\u60f3\u8981\u547d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u60f3\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u60f3\u8981\u547d\u5c31\u53bb\u53eb\u5357\u5bcc\u4e11\u6eda\u51fa\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f25\u53eb\u4ed6\u6eda\u51fa\u6765\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u8981\u4ed6\u7684\u547d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5b9a\u8981\u5728\u4eca\u5929\u665a\u535c\u6740\u4ed6\uff1f\u201d \u3000\u3000\u7d2b\u8863\u4ea5\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5b9a\u8981\u5728\u4eca\u5929\u665a\u4e0a\u6740\u4ed6\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u56e0\u4e3a\u6211\u8bf4\u8fc7\xb7\u5929\u4eae\u524d\u82e5\u8fd8\u6740\u4e0d\u4e86\u4ed6\xb7\u5c31\u9976\u4ed6\u547d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u8fc7\u7684\u8bdd\u8981\u7b97\u6570\u522b\u4eba\u8bf4\u7684\u8bdd\u4e5f\u6837\u4e0d\u80fd\u4e0d\u7b97\u6570\u7684\u3002\u201d\u7d2b\u8863\u5973\u9053\u201c\u4f60\u8bf4\u8fc7\u4ec0 \u9ebd\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u6211\u8bf4\u8fc7\xb7\u4eca\u5929\u665a\u4e0a\u8981\u8ba9\u4ed6\u5b89\u5fc3\u7761\u89c9\u7761\u5230\u5929\u4eae\u6240\u4ee5\u2026\u2500\u201c \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u6240\u4ee5\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f60\u8981\u6740\u4ed6\u5c31\u5f97\u5148\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u4f60\u662f\u4ed6\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u505a\u8fc7\u591a\u5c11\u574f\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7d2b\u8863\u4ea5\u9053\u201c\u4f46\u4f60\u8fd8\u662f\u8981\u4e3a\u4ed6\u62fc\u547d\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u51b7\u7b11\u9053\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u6562\u6740\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u4f60\u770b\u6765\u7684\u786e\u4e0d\u8c61\u4f1a\u6740\u4eba\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u7d2b\u8863\u6587\u51b7\u73b2\u9053\u201c\u6211\u4e5d\u5c81\u65f6\u5df2\u5f00\u59cb\u6740\u4eba\xb7\u6bcf\u4e2a\u6708\u81f3\u5c11\u6740\u4e2a\xb7\u4f60\u7b97\u7b97\u5df2\u6709\u591a\u5c11\u4e2a\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u9053\u201c\u597d\u50cf\u5df2\u6709\u4e03\u516b\u5341\u4e2a\u4e86\u5427\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u6240\u4ee5\u518d\u591a\u52a0\u4f60\u4e2a\uff0c\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u8fd8\u672a\u8bf4\u8bdd\xb7\u7a81\u542c\u4eba\u73b2\u51b7\u9053\u201c\u4f60\u82e5\u8981\u6740\u4ed6\uff0c\u5c31\u5f97\u5148\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u8fd9\u5806\u662f\u71d5\u4e03\u7684\u58f0\u97f3\u662f\u6797\u592a\u5e73\u3002 \u3000\u3000\u591c\u8272\u51c4\u6e05\u6797\u592a\u5e73\u4e0d\u77e5\u4f55\u65f6\u5df2\u8d70\u4e86\u8fc7\u6765\u8138\u8272\u82cd\u767d\u5982\u7eb8\u3002\u7d2b\u8863\u5973\u77aa\u773c\u9053\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u51b7\u51b7\u9053\u201c\u4f60\u7528\u4e0d\u7ba1\u6211\u662f\u8c01\uff0c\u4f60\u65e2\u5df2\u6740\u4e86\u4e03\u516b\u5341\u4e2a\u4eba\u518d\u591a\u52a0\u4e2a\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u7d2b\u8863\u4eba\u51b7\u51b7\u7b11\u9053\u201c\u60f3\u4e0d\u5230\u8fd9\u91cc\u4e0d\u6015\u6b7b\u7684\u4eba\u8fd8\u771f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u7684\u786e\u4e0d\u5c11\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u9053\u201c\u65e2\u7136\u5982\u6b64\u6211\u5c31\u6210\u5168\u4e86\u4f60\u3002\u201d \u3000\u3000\u5979\u8eab\u5b50\u8f6c\u4e88\u91cc\u7684\u9f99\u5934\u6756\u7a81\u7136\u201c\u5206\u82b1\u5a1c\u67f3\u201d\u5411\u6797\u592a\u5e73\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u7528\u7684\u7adf\u662f\u5251\u6cd5\u3002 \u3000\u3000\u4e0d\u4f46\u662f\u5251\u6cd5\xb7\u800c\u4e14\u662f\u5251\u6cd5\u4e2d\u6700\u8f7b\u76c8\u7684\u79cd\u3002 \u3000\u3000\u8fd9\u9ebd\u957f\u8fd9\u957f\u91cd\u7684\u6839\u6756\uff0c\u5728\u5979\u4e00\u53cc\u81ea\u751f\u751f\u7684\u5c0f\u624b\u91cc\uff0c\u7ade\u53d8\u5f97\u597d\u50cf\u6ca1\u6709\u56db\u4e24\u91cd\u3002 \u3000\u3000\u90ed\u5927\u8def\u5927\u559d\u9053\u201c\u4f60\u7684\u75c5\u8fd8\u6ca1\u597d\u8ba9\u6211\u6765\u3002\u201d \u3000\u3000\u4f46\u8fd9\u65f6\u4ed6\u60f3\u62a2\u51fa\u624b\xb7\u90fd\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u7d2b\u8863\u4ea5\u5df2\u95ea\u7535\u822c\u5411\u6797\u592a\u5e73\u653b\u51fa\u4e86\u4e03\u62db\u5251\u8d70\u8f7b\u7075\uff0c\u53d8 \u5316\u65e0\u65b9\u3002 \u3000\u3000\u6797\u592a\u5e73\u7684\u4eba\u5df2\u88ab\u56f4\u4f4f\u3002 \u3000\u3000\u4ed6\u4f53\u529b\u663e\u7136\u8fd8\u672a\u6062\u590d\u4f3c\u5df2\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u4f46\u7d2b\u8863\u5973\u5bc6\u5982\u62bd\u4e1d\u7684\u5251\u6cd5\u5374\u5014\u717d\u6cbe\u4e0d\u5230\u4ed6\u4e00\u7247\u8863\u89d2\u3002 \u3000\u3000\u7a81\u542c\u58f0\u6e05\u5578\xb7\u4e5d\u5c3a\u957f\u7684\u6756\u7b14\u76f4\u63d2\u5165\u5730\u4e0a\xb7\u7d2b\u8863\u4ea5\u7684\u4eba\u5374\u5df2\u5728\u6756\u4e0a\u98ce\u8f66\u822c\u5411\u6797\u592a\u5e73\u5377\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u8fd9\u5979\u7adf\u4ee5\u6756\u4f5c\u9aa8\u5e72\uff0c\u4ee5\u4eba\u4f5c\u6b66\u5668\u62db\u5f0f\u53d8\u5316\u4e4b\u8be1\u5f02\u66f4\u51fa\u5165\u60f3\u8c61\u3002 \u3000\u3000\u6797\u592a\u5e73\u811a\u6b65\u9519\u52a8\uff0c\u8fde\u9000\u4e86\u4e5d\u6b65\u3002 \u3000\u3000\u7d2b\u8863\u5973\u7a81\u53c8\u4e00\u58f0\u6e05\u5578\u51b2\u5929\u800c\u8d77\u6756\u4ecd\u63d2\u5728\u5730\u4e0a\u5979\u624b\u91cc\u5374\u591a\u4e86\u67c4\u7cbe\u5149\u56db\u5c04\u7684\u77ed\u5251\u3002 \u3000\u3000\u5251\u672c\u6765\u85cf\u5728\u80e1\u6756\u4e2d\u7684\xb7\u5230\u4e86\u5979\u624b\u91cc\uff0c\u5979\u7684\u4eba\u4e0e\u5251\u5c31\u4f3c\u5df2\u6eb6\u5408\u4e3a\u8fde\u4eba\u5e26\u5251\u5411\u6797\u592a\u5e73\u523a\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u8fd9\u62db\u66f4\u662f\u5999\u7edd\u3001\u9669\u7edd\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u51b7\u6c57\u5df2\u88ab\u5413\u4e86\u51fa\u6765\u4ed6\u82e5\u9047\u8fd9\u80fd\u907f\u5f00\u7684\u5e0c\u671b\u5b9e\u5728\u4e0d\u591a\u3002 \u3000\u3000\u4f46\u6797\u592a\u5e73\u5374\u4f3c\u4e4e\u5bf9\u5979\u62db\u5f0f\u7684\u6bcf\u79cd\u53d8\u5316\u90fd\u65e9\u5df3\u719f\u6089\u5f97\u5f88\u3002 \u3000\u3000\u5979\u7684\u5251\u5982\u7ecf\u5929\u957f\u8679\u521a\u98de\u5230\u6797\u592a\u5e73\u9762\u524d\u6797\u592a\u5e73\u8eab\u5b50\u7a81\u7136\u4e00\u8f6c\xb7\u5411\u524d\u51b2\u51fa\xb7\u5df2\u62d4\u51fa\u4e86\u5730\u4e0a\u7684 \u6756\u3002 \u3000\u3000\u7d2b\u8863\u5973\u957f\u5578\u4e0d\u7edd\u51cc\u7a7a\u7ffb\u8eab\uff0c\u56de\u5251\u53cd\u523a\u3002 \u3000\u3000\u6797\u592a\u5e73\u5934\u4e5f\u4e0d\u56de\u968f\u624b\u5c06\u80e1\u6756\u4e00\u626c\u3002 \u3000\u3000\u53ea\u542c\u201c\u6323\u201d\u7684\u2500\u58f0\xb7\u706b\u661f\u56db\u6e85\u77ed\u5251\u7adf\u5df2\u6ca1\u4eba\u6756\u91cc\u3002 \u3000\u3000\u7d2b\u8863\u5973\u7684\u8eab\u5b50\u5374\u5df2\u51b2\u5929\u64e6\u8d77\uff0c\u51cc\u7a7a\u7530\u4e86\u56db\u4e2a\u8000\u6597\uff0c\u624d\u98d8\u98d8\u843d\u4e0b\u6765\u843d\u5728\u80e1\u5e8a\u524d\u770b\u6797\u592a\u5e73\u53d1 \u5f81\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u770b\u5f97\u6014\u4efb\u5382\u3002 \u3000\u3000\u521a\u6797\u592a\u5e73\u6325\u8d77\u7684\u6756\u82e5\u6709\u534a\u5206\u504f\u5dee\u7d2b\u8863\u5973\u7684\u5251\u53ea\u4f2f\u5df2\u523a\u4eba\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u7d2b\u8863\u4ea5\u51fa\u624b\u7684\u65b9\u5411\u90e8\u4f4d\u4ed6\u7adf\u7b97\u5f97\u8fde\u534a\u5206\u90fd\u4e0d\u5dee\uff0c\u5c31\u597d\u50cf\u4ed6\u613f\u7d2b\u8863\u5973\u4ea4\u624b\u8fc7\u51e0\u767e\u6b21\xb7\u5979\u8fd8 \u672a\u51fa\u624b\u4ed6\u5c31\u5df2\u77e5\u9053\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u6797\u592a\u5e73\u968f\u624b\u5c06\u6756\u5f80\u5730\u4e0a\u4e00\u63d2\uff0c\u6389\u5934\u5c31\u8d70\u3002 \u7b2c\u5341\u516d\u7ae0\u3000\u5192\u540d\u8005\u6b7b>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u5192\u540d\u8005\u6b7b \u3000\u3000\u7d2b\u8863\u5973\u5ffd\u7136\u5927\u58f0\u9053\u201c\u7b49\u4e00\u7b49\u3002\u201d\u6797\u592a\u5e73\u51b7\u51b7\u9053\u201c\u8fd8\u7b49\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5973\u54ac\u7530\u5507\u9053\u201c\u4f60\u201d\u2026\u4f60\u96be\u9053\u8fd9\u9ebd\u6837\u5c31\u60f3\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5979\u597d\u50cf\u7a81\u7136\u53d8\u5f97\u5f88\u6fc0\u52a8\xb7\u8fde\u624b\u811a\u90fd\u5728\u53d1\u6296\u3002 \u3000\u3000\u6797\u592a\u5e73\u8fdf\u7591\u7ec8\u4e8e\u6162\u9992\u7684\u8f6c\u8fc7\u8eab\xb7\u9053\uff1a\u201c\u4f60\u60f3\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u7d2b\u8863\u6587\u9053\u201c\u6211\u201c\u4e00\u201c\u6211\u2026\u3002\u2019\u6211\u60f3\u95ee\u4f60\u53e5\u8bdd\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4f60\u95ee\u5427\u3002\u201d \u3000\u3000\u7d2b\u8863\u4ea5\u63e1\u7d27\u4e86\u53cc\u624b\xb7\u9053\u201c\u4f60\u662f\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\xb7\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\u201c\u597d\u90a3\u672b\u6211\u95ee\u4f60\u4f60\u90a3\u5929\u4e3a\u4ec0\u9ebd\u8981\u9003\u8d70\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u9ad8\u5174\u3002\u201d \u3000\u3000\u7d2b\u8863\u5973\u7684\u624b\u63e1\u5f97\u66f4\u7d27\xb7\u8fde\u5634\u5507\u90fd\u53d1\u767d\u4e86\u98a4\u58f0\u9053\u201c\u6211\u6709\u54ea\u70b9\u914d\u4e0d\u4e0a\u4f60\u4f60\u5b9a\u8981\u8ba9\u6211\u90a3\u6837\u5b50\u4e22 \u4eba\u201d \u3000\u3000\u6797\u592a\u5e73\u51b7\u73b2\u9053\u201c\u662f\u6211\u914d\u4e0d\u4e0a\u4f60\u4e22\u4eba\u7684\u4e5f\u662f\u6211\uff0c\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u7d2b\u8863\u6587\u9053\u201c\u73b0\u5728\u6211\u65e2\u7136\u5df2\u627e\u5230\u4e86\u4f60\u4f60\u51c6\u5907\u600e\u9ebd\u529e\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e0d\u600e\u9ebd\u529e\u3002\u201d \u3000\u3000\u9988\u8863\u5973\u9053\u201c\u4f60\u8fd8\u662f\u4e0d\u80af\u56de\u53bb\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u9664\u975e\u4f60\u6740\u4e86\u6211\uff0c\u62a2\u6211\u7684\u4f53\u56de\u53bb\u5426\u5219\u5c31\u4f11\u60f3\u3002\u201d \u3000\u3000\u7eed\u8863\u5973\u773c\u775b\u53d1\u7ea2\u7530\u5507\u90fd\u5df2\u54ac\u51fa\u8840\u6765\xb7\u6068\u6839\u9053\u201c\u597d\uff0c\u4f60\u653e\u5fc3\u6211\u7edd\u4e0d\u4f1a\u627e\u4eba\u6765\u901a\u4f60\u56de\u53bb\u7684\u4f46 \u603b\u6709\u4e00\u5929\xb7\u6211\u8981\u53eb\u4f60\u8dea\u6765\u6c42\u6211\u603b\u6709\u5929\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u786c\u54bd\u5df2\u5b8c\u5168\u5fd8\u8bb0\u6765\u627e\u5357\u5bab\u4e11\u7684\u4e8b\u4e86\u7a81\u53c8\u8dfa\u4e86\u8dfa\u811a\u51cc\u7a7a\u4e2a\u7ffb\u8eab\u63a0\u51fa\u5899\u5916\u3002 \u3000\u3000\u613f\u5979\u6765\u7684\u4eba\u7728\u773c\u95f4\u4e5f\u5168\u90fd\u4e0d\u89c1\u3002 \u3000\u3000\u53ea\u7559\u4e0b\u6ee1\u5730\u9999\u82b1\u5377\u7ea2\u6be1\u3002 \u3000\u3000\u591c\u66f4\u6df1\xb7\u706f\u5149\u8fdc\uff0c\u9ed1\u6697\u4e2d\u770b\u4e0d\u51fa\u6797\u592a\u5e73\u9762\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\uff0c\u65e2\u4e0d\u4fbf\u95ee\u4e5f\u4e0d\u5fc5\u95ee\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u6797\u592a\u5e73\u624d\u8f6c\u8fc7\u5934\uff0c\u52c9\u5f3a\u5411\u90ed\u5927\u8def\u7b11\u4e86\u7b11\u9053\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5e94\u8be5\u662f\u6211\u591a\u8c22\u4f60\u624d\u5bf9\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8c22\u6211\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u56e0\u4e3a\u4f60\u6ca1\u6709\u95ee\u5979\u662f\u8c01\uff0c\u4e5f\u6ca1\u6709\u95ee\u6211\u600e\u9ebd\u8ba4\u5f97\u5979\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u7b11\xb7\u9053\u201c\u4f60\u82e5\u60f3\u8bf4\uff0c\u6211\u4e0d\u5fc5\u95ee\u4f60\u82e5\u4e0d\u60f3\u8bf4\u6211\u53c8\u4f55\u5fc5\u7f51\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6709\u4e9b\u4e8b\xb7\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u4ed6\u9992\u9992\u7684\u8f6c\u8fc7\u8eab\u8d70\u56de\u5c4b\u91cc\u3002 \u3000\u3000\u7ff1\u5927\u8def\u770b\u4ed6\u7626\u524a\u7684\u80cc\u5f71\u5fc3\u91cc\u5b9e\u5728\u89c9\u5f97\u5f88\u60ed\u6127\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u95ee\xb7\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u5df2\u731c\u51fa\u8fd9\u8d2b\u6b21\u5973\u662f\u8c01\xb7\u4ed6\u77e5\u9053\u7684\u4e8b\u8fdc\u6bd4\u6797\u592a\u5e73\u60f3\u8c61\u4e2d\u591a\u5f97 \u591a\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\u662f\u4ed6\u5728\u7792\u6797\u592a\u5e73\u4e0d\u662f\u6797\u592a\u5e73\u7792\u4ed6\u3002\u90a3\u6b21\u4ed6\u548c\u71d5\u4e03\u9047\u89c1\u6797\u592a\u5e73\u6bcd\u4eb2\u7684\u4e8b\uff0c\u76f4\u5230\u73b0 \u5728\xb7\u6797\u592a\u5e73\u8fd8\u88ab\u8499\u5728\u9f13\u91cc\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4eec\u662f\u597d\u610f\u4f46\u90ed\u5927\u8def\u5fc3\u91cc\u603b\u8fd8\u662f\u89c9\u5f97\u6709\u70b9\u4e0d\u8212\u670d\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u6ca1\u6709\u5728\u670b\u53cb\u9762\u524d\u9690\u7792\u8fc7\u4efb\u4f55\u4e8b\u65e0\u8bba\u4e3a\u4e86\u4ec0\u9ebd\u539f\u56e0\u90fd\u6ca1 \u3000\u3000\u6709\u98ce\u5439\u8fc7\u5439\u8d77\u4e86\u5730\u4e0a\u7684\u6b8b\u82b1\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u542c\u89c1\u4e86\u71d5\u4e03\u7684\u58f0\u97f3\u3002 \u3000\u3000\u71d5\u4e03\u8f7b\u8f7b\u9053\u201c\u73b0\u5728\u4f60\u60f3\u5fc5\u5df2\u7ec6\u9053\u90a3\u4f4d\u7b56\u8863\u732b\u5a18\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u5df2\u731c\u51fa\u5979\u5c31\u662f\u6797\u592a\u5e73\u672a\u8fc7\u95e8\u7684\u59bb\u5b50\xb7\u6797\u592a\u5e73\u5c31\u662f\u4e3a\u4e86\u4e0d\u613f\u8981\u8fd9\u9ebd\u6837\u4e2a\u59bb\u5b50\uff0c\u624d\u9003 \u51fa\u6765\u7684\u3002 \u3000\u3000\u71d5\u4e03\u53f9\u9053\u201c\u76f4\u5230\u73b0\u5728\u6211\u624d\u5b8c\u5168\u660e\u767d\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u9003\u51fa\u6765\u3002\u201d\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u8c61\u90a3\u6837\u7684\u5973\u5b69 \u5b50\u8fde\u6211\u90fd\u53d7\u4e0d\u4e86\uff0c\u4f55\u51b5\u5c0f\u6797\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u539f\u6765\u4f60\u4e5f\u6709\u53d7\u4e0d\u4e86\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u957f\u5f97\u4e0d\u662f\u5f88\u7f8e\u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u957f\u5f97\u7f8e\u53c8\u6709\u4ec0\u9ebd\u7528\uff1f\u7537\u4eba\u770b\u5973\u5b69\u5b50\xb7\u5e76\u4e0d\u662f\u53ea\u770b\u5979\u5f20\u8154\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u7728\u7728\u773c\uff0c\u9053\u201c\u7537\u4eba\u600e\u9ebd\u6837\u770b\u5973\u5b69\u5b50\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u8981\u770b\u5979\u662f\u4e0d\u662f\u6e29\u67d4\u8d24\u6167\uff0c\u662f\u4e0d\u662f\u4fc3\u5f97\u4f53\u8d34\u4e08\u592b\xb7\u5426\u5219\u5979\u5c31\u7b97\u957f\u5f97\u613f\u5929\u4ed9 \u6837\xb7\u4e5f\u4e0d\u89c1\u6709\u4eba\u559c\u6b22\u3002\u201d \u3000\u3000\u71d5\u4e03\u7528\u773c\u89d2\u8198\u4ed6\xb7\u9053\u201c\u4f60\u5462\uff1f\u4f60\u559c\u6b22\u4ec0\u9ebd\u6837\u7684\u5973\u5b69\u4e8e\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u559c\u6b22\u7684\u5973\u5b69\u5b50\u613f\u522b\u7684\u7537\u4eba\u4e0d\u6837\u3002\u201d\u71d5\u4e03\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u6709\u4e2a\u4ea5\u5b69\u5b50\u771f\u7684\u80fd\u4e86\u89e3\u6211\u5173\u5fc3\u6211\u5979\u5c31\u7b97\u957f\u5f97\u4e11\u70b9\u51f6\u70b9\xb7\u6211\u8fd8\u662f\u6837\u5168\u5fc3\u5168\u610f \u7684\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ae1\u7136\u7b11\xb7\u5782\u4e0b\u5934\u4ece\u4ed6\u8eab\u65c1\u8d70\u8fc7\u53bb\u8d70\u5230\u5899\u89d2\u7684\u82b1\u77ed\u7684 \u3000\u3000\u4f7c\u8272\u4eff\u4f5b\u5ffd\u7136\u4e49\u53d8\u5f97\u6e29\u67d4\u8d77\u6765\u3002 \u3000\u3000\u5899\u89d2\u7684\u5de7\u836f\u4ec0\u5f97\u6b63\u8273\u71d5\u4e03\u8f7b\u629a\u82b1\u74e3\u4e0a\u7684\u9732\u5e86\u8fc7\u4e86\u5f88\u4e45\u624d\u56de\u8fc7\u5934\u5c31\u53d1\u73b0\u90ed\u5927\u8def\u597d\u50cf\u771f\u90fd\u5728 \u51dd\u89c6\u4ed6\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u76b1\u5382\u76b1\u7709\xb7\u9053\u201c\u6211\u53c8\u4e0d\u662f\u5973\u4eba\xb7\u6709\u4ec0\u9ebd\u597d\u770b\u7684\uff1f\u4f60\u4e3a\u4ec0\u9ebd\u8001\u662f\u76ef\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u3002\u2026\u6211\u89c9\u5f97\u4f60\u4eca\u5929\u8d70\u8def\u7684\u6837\u5b50\u597d\u50cf\u8ddf\u5e73\u5e38\u6709\u70b9\u4e0d\u540c\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6709\u4ec0\u9ebd\u4e2a\u540c\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u4eca\u5929\u8d70\u8def\u7684\u6837\u5b50\uff0c\u597d\u50cf\u7279\u522b\u597d\u770b\u7b80\u76f4\u6bd4\u4ea5\u5b69\u5b50\u8d70\u8def\u8fd8\u597d\u770b\u201c \u3000\u3000\u71d5\u4e03\u7684\u80a1\u4f3c\u53c8\u6709\u4e9b\u7ea2\u4e86\xb7\u5374\u6545\u610f\u677f\u8d77\u4e86\u8138\uff0c\u51b7\u51b7\u9053\u201c\u6211\u770b\u4f60\u8fd1\u6765\u597d\u50cf\u4e5f\u6709\u70b9\u53d8\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u6700\u8fd1\u597d\u50cf\u5f97\u4e86\u79cd\u83ab\u540d\u5176\u5999\u7684\u6bdb\u75c5\xb7\u603b\u662f\u4f1a\u505a\u4e9b\u83ab\u540d\u5176\u5999\u7684\u4e8b\u8bf4\u4e9b\u83ab\u540d\u5176\u5999\u7684 \u8bdd\u771f\u8be5\u66ff\u4f60\u627e\u4e2a\u5927\u592b\u6765\u770b\u770b\u624d\u5bf9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u664c\u76ee\u4e2d\u7ade\u771f\u7684\u9732\u51fa\u4e86\u79cd\u5fe7\u90c1\u6050\u60e7\u4e4b\u8272\u7adf\u771f\u7684\u597d\u50cf\u4e2a\u4eba\u77e5\u9053\u81ea\u5df1\u67d3\u4e0a\u5927\u75c5\u7684 \u6837\u4e8e\u3002 \u3000\u3000\u71d5\u4e03\u5374\u53c8\u7b11\u4e86\u6e90\u7136\u9053\u201c\u4f46\u4f60\u4e5f\u7528\u4e0d\u592a\u626d\u5fc3\u5176\u5b9e\u6bcf\u4e2a\u4eba\u591a\u591a\u5c11\u5c11\u90fd\u6709\u70b9\u6bdb\u75c5\u7684\u3002\u201d\u90ed\u5927\u8def \u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u77e5\u6728\u77e5\u9053\u6bdb\u75c5\u5458\u5927\u7684\u662f\u8c01\uff1f\u2019 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5c31\u662f\u90a3\u4f0e\u4e94\u5993\u5a18\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e94\u59d1\u5a18\u662f\u8c01\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e94\u59a8\u5a18\u5c31\u662f\u521a\u6765\u7684\u90a3\u5973\u5b69\u5b50\u5979\u59d3\u4e94\u53eb\u7389\u73b2\u6b8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7389\u73b2\u6591\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4ee5\u524d\u96be\u9053\u4ece\u6765\u6ca1\u6709\u542c\u8bf4\u8fc7\u5979\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u56db\u4e86\u53e3\u6c14\xb7\u6447\u5934\u9053\u201c\u770b\u6765\u4f60\u771f\u662f\u5b64\u964b\u5be1\u95fb\u4e00\u70b9\u5b66\u95ee\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u770b\u5f97\u51fa\u5979\u6bdb\u75c5\u5b9e\u5728\u4e0d\u5c0f\u4f46\u662f\u6211\u4e3a\u4ec0\u9ebd\u4e00\u5b9a\u8981\u542c\u8bf4\u8fc7\u5979\u5462\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u5979\u4e5d\u5c81\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u662f\u6c5f\u6e56\u4e2d\u7684\u540d\u4eba\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e5d\u5c81\uff1f\u4f60\u662f\u8bf4\u4e5d\u5c81\uff1f\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\uff0c\u9053\u201c\u5979\u5bb6\u4e16\u663e\u8d6b\u800c\u4e14\u4ece\u5c0f\u5c31\u662f\u4e2a\u5973\u795e\u7ae5\uff0c\u636e\u8bf4\u8fd8\u672a\u6ee1\u4e24\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u7ecf \u5f00\u59cb\u7ec3\u6b66\uff0c\u4e94\u5c81\u65f6\u5c31\u5df2\u628a\u62db\u5f0f\u53d8\u5316\u6700\u7e41\u590d\u7684\u5957\u2018\u4e03\u4e03\u56db\u5341\u4e5d\u5f0f\u56de\u98ce\u821e\u67f3\u5228\u2019\u5b66\u5168\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5979\u8bf4\u5979\u4e5d\u5c81\u7684\u65f6\u5019\u5df2\u6740\u8fc7\u4eba\u542c\u4f60\u8fd9\u9ebd\u8bb2\u5979\u8bf4\u7684\u8bdd\u597d\u50cf\u5e76\u4e0d\u5047\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u70b9\u4e5f\u4e0d\u5047\uff0c\u5979\u4e5d\u5c81\u7684\u65f6\u5019\u975e\u4f46\u771f\u7684\u6740\u8fc7\u4eba\xb7\u800c\u4e14\u88ab\u6740\u7684\u8fd8\u662f\u6c5f\u6e56\u4e2d\u4e2a\u5f88\u6709\u540d\u6c14 \u7684\u5251\u5ba2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u95ee\u9053\u201c\u4ece\u90a3\u65f6\u4ee5\u5f8c\u5979\u6bcf\u4e2a\u6708\u90fd\u8981\u6740\u4e2a\u628a\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u90a3\u4e5f\u4e0d\u5047\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f50\u7b11\u9053\u201c\u4e16\u4e0a\u987e\u6709\u8fd9\u9ebd\u591a\u4eba\u9001\u53bb\u7ed9\u5979\u6740\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u662f\u522b\u4eba\u9001\u53bb\u662f\u5979\u81ea\u5df1\u53bb\u627e\u522b\u4eba\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u5230\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5230\u5404\u5904\u53bb\u627e\u3002\u53ea\u8981\u5979\u542c\u8bf4\u6709\u4eba\u505a\u4e86\u4ef6\u8be5\u6740\u7684\u4e8b\u5c31\u7acb\u523b\u4f1a\u8fdb\u53bb\u627e\u90a3\u4e2a\u4eba\u7b97\u5e10\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u5979\u6bcf\u6b21\u90fd\u80fd\u5f97\u624b\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u81ea\u5df1\u6b66\u529f\u9ad8\u4f4e\u4f60\u521a\u529b\u5df3\u89c1\u8fc7\u5382\u518d\u52a0\u4e0a\u90a3\u4e24\u4e2a\u6606\u4ed1\u5974\u548c\u4e24\u4e2a\u86ee\u5973\u4e5f\u90fd\u662f\u7b49\u7684\u9ad8 \u624b\u751a\u81f3\u8fde\u90a3\u56db\u4e2a\u6311\u706f\u7684\u5a76\u5973\uff0c\u6b66\u529f\u90fd\u4e0d\u5f31\uff0c\u6240\u4ee5\u53ea\u8981\u5979\u627e\u4e0a\u53bb\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u9003\u907f\u5f97\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u5c31\u6ca1\u6709\u4eba\u7ba1\u7ba1\u5979\uff1f\u201d \u3000\u3000\u71d5\u4e03\u901a\u201c\u5979\u7236\u4eb2\u6b7b\u5f97\u5f88\u65e9\uff0c\u6bcb\u4eb2\u662f\u6c5f\u6e56\u4e2d\u5458\u96be\u60f9\u7684\u6bcd\u8001\u864e\u5bf9\u8fd9\u5b9d\u5973\u513f\u5411\u5e72\u4f9d\u66f0\u987a\u70b9\uff1b \u5973\u3002\u201d \u3000\u3000\u5979\u53f9\u5382\u53e3\u6c14\u63a5\u53c8\u9053\u201c\u4f55\u51b5\u5979\u6740\u7684\u4eba\u672c\u6765\u5c31\u8be5\u6740\uff0c\u6240\u4ee5\u6c5f\u6e56\u4e2d\u8001\u8f88\u7684\u4eba\u975e\u4f46\u6ca1\u6709\u8d23\u5907\u5979\uff0c \u53cd\u800c\u53ea\u6709\u5938\u5956\u5979\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u5979\u5341\u53c1\u56db\u5c81\u7684\u65f6\u5019\u5c31\u5df2\u6210\u4e3a\u6c5f\u6e56\u4e2d\u6e90\u5934\u6700\u5927\u6b66\u529f\u4e5f\u6700\u9ad8\u7684\u6587\u5b69\u5b50\u6740\u7684\u4eba \u8d8a\u591a\u6b66\u529f\u81ea\u7136\u4e5f\u8d8a\u9ad8\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c31\u56e0\u4e3a\u5982\u6b64\u6240\u4ee5\u8fde\u5357\u5bcc\u4e11\u8fd9\u6837\u7684\u4eba\u77e5\u9053\u5979\u8981\u6765\u627e\u9ebb\u70e6\u7684\u65f6\u5019\u90fd\u53ea\u6709\u8eb2\u8d77\u6765\u4e0d \u6562\u9732\u9762\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5357\u5bcc\u4e11\u5f53\u7136\u5df2\u77e5\u9053\u5979\u548c\u5c0f\u6797\u7684\u5173\u7cfb\u6240\u4ee5\u624d\u4f1a\u8eb2\u4e0d\u9732\u9762\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f46\u5357\u5bcc\u4e11\u82e5\u4e0d\u662f\u771f\u7684\u5f88\u8be5\u6b7b\u5979\u4e5f\u4e0d\u4f1a\u6765\u627e\u4ed6\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u4e0d\u9519\u5979\u4ee5\u524d\u4ece\u6765\u4e5f\u6ca1\u6709\u627e\u9519\u8fc7\u5165\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u82e6\u7b11\u9053\u6240\u4ee5\u9519\u7684\u5e76\u4e0d\u662f\u5979\u662f\u6211\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u4f60\u4e5f\u6ca1\u6709\u9519\u3002\u201d \u3000\u3000\u4ed6\u67d4\u58f0\u63a5\u9053\u201c\u6709\u601d\u5fc5\u62a5\u8bfa\u5e72\u91d1\u672c\u6765\u662f\u7537\u5b50\u6c49\u5927\u4e08\u592b\u7684\u672c\u8272\u4f60\u8fd9\u9ebd\u6837\u505a\u7edd\u6ca1\u6709\u4eba\u4f1a\u602a \u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u4f1a\u3002\u201d\u71d5\u4e03\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u5929\u5df2\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u7ff1\u5927\u8def\u8eab\u4e0a\u8fd8\u88ab\u90a3\u4ef6\u888d\u5b50\uff0c\u4e2a\u4eba\u5750\u5728\u90a3\u91cc\uff0c\u770b\u4e73\u767d\u8272\u7684\u6668\u96fe\u9992\u9992\u7684\u4ece\u9662\u5b50\u91cc\u5347\u8d77\u542c\u6653\u98ce \u81ea\u8fdc\u65b9\u4f20\u6765\u7684\u9e21\u8e44\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u542c\u5230\u5f00\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u56de\u5934\u8111\u4e0a\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u8868\u60c5\u3002 \u3000\u3000\u4e00\u9635\u6839\u8f7b\u5f88\u6162\u7684\u811a\u6b65\u8d70\u5230\u4ed6\u8eab\u8d28\u505c\u5c38\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u6ca1\u6709\u56de\u5934\u53ea\u8c08\u6de1\u7684\u95ee\u4e86\u53e5\u201c\u4f60\u7761\u5f97\u8fd8\u597d\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5c31\u7ad9\u5728\u4ed6\u8eab\u5f8c\xb7\u51dd\u89c6\u4ed6\u7684\u8116\u5b50\xb7\u9053\u201c\u535c\u5e74\u6765\u6211\u4ece\u672a\u7761\u5f97\u5982\u6b64\u5b89\u9002\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u56e0\u4e3a\u4ece\u6765\u6ca1\u6709\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\u66ff\u6211\u5728\u95e8\u5916\u770b\u5b88\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u6ca1\u6709\u4eba\u4e3a\u4f60\u770b\u95e8\u4f60\u5c31\u964b\u4e0d\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6709\u4eba\u66ff\u6211\u770b\u95e8\u6211\u4e5f\u4e00\u6837\u7761\u4e0d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u56e0\u4e3a\u6211\u4ece\u4e0d\u76f8\u4fe1\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4f46\u4f60\u5374\u597d\u50cf\u5f88\u4fe1\u4efb\u6211\u3002\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u770b\u6765\xb7\u4f60\u597d\u50cf\u4e5f\u72e0\u4fe1\u4efb\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u600e\u89c1\u5f97\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7f13\u7f13\u9053\u201c\u56e0\u4e3a\u9664\u4e86\u4f60\u4e4b\u5916\xb7\u4ece\u6ca1\u6709\u522b\u7684\u4eba\u6562\u8ba9\u6211\u7ad9\u5728\u4ed6\u80cc\u5f8c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6211\u5e76\u4e0d\u4e2a\u541b\u5b50\xb7\u6211\u5e38\u5e38\u5728\u80cc\u5f8c\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9992\u998f\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\u201c\u80cc\u5f8c\u6740\u4eba\u7684\u786e\u65b9\u4fbf\u5f97\u591a\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u5c24\u5176\u662f\u5728\u8fd9\u70b9\u5934\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5728\u70b9\u5934\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6bcf\u4e2a\u4eba\u5f8c\u9888\u4e0a\uff0c\u90fd\u6709\u4e00\u5904\u6700\u597d\u4e0b\u5200\u7684\u5730\u65b9\u4f60\u53ea\u6709\u627e\u5230\u8fd9\u5730\u65b9\u624d\u80fd\u5200\u780d\u4e0b\u4ed6\u7684 \u8111\u888b\u6765\u8fd9\u9053\u7406\u6709\u7ecf\u9a8c\u7684\u523d\u4e8e\u624b\u90fd\u660e\u767d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u9992\u6162\u7684\u70b9\u5382\u70b9\u5934\uff0c\u9053\u201c\u7684\u786e\u6709\u9053\u7406\u72e0\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\xb7\u624d\u7f13\u7f13\u5730\u9053\u2018\u201c\u4f60\u2500\u76f4\u6ca1\u6709\u7761\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u7761\u4e86\u4f60\u8fd8\u80fd\u964b\u9ebd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u5c16\u9510\u800c\u77ed\u4fc3\u5c31\u597d\u50cf\u5200\u950b\u5728\u8611\u64e6\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u8d70\u5230\u90ed\u5927\u8def\u524d\u9762\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e3a\u4ed8\u9ebd\u8ba9\u6211\u7ad9\u5728\u4f60\u80cc\u5f8c\uff1f\u201d\u9ed1\u8863\u4eba\u9053\u201c\u56e0\u4e3a\u6211\u4e0d\u613f\u88ab\u4f60\u8bf1\u60d1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8bf1\u60d1\uff1f\u201d\u9ed1\u8863\u4eba\u9053\u201c\u6211\u82e5\u7784\u5728\u4f60\u80cc\u5f8c\u770b\u5230\u4f60\u518d\u70b9\u5934\u65f6\uff0c\u624b\u4f1a\u75d2\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u624b\u75d2\u7684\u65f6\u5019\u5c31\u8981\u6740\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u53ea\u6709\u6b21\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54ea\u6b21\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u521a\u90a3\u4e00\u6b21\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\u4ed6\u5ffd\u7136\u5934\u4e5f\u4e0d\u56de\u7684\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002\u90ed\u5927\u8def\u770b\u4ed6\u76f4\u5230\u4ed6\u8d70\u5230\u95e8\u53e3\u5ffd\u7136\u9053\u201c\u7b49\u4e00 \u7b49\u3002\u201d \u3000\u3000\u9ed1\u519c\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u4ec0\u9ebd\u8bdd\u8981\u8bf4\uff1f\u6f14\u8bf4\u7684\u4f3c\u5df2\u5168\u90fd\u8bf4\u5b8c\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u901a\u201c\u6211\u53ea\u6709\u53e5\u8bdd\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u95ee\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6162\u9992\u7684\u6cb9\u8d77\u6765\uff0c\u5b57\u5b57\u9053\u201c\u4f60\u662f\u4e0d\u662f\u5357\u5bcc\u4e11\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6ca1\u6709\u56de\u7b54\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u4f46\u90ed\u5927\u8def\u5374\u53ef\u4ee5\u770b\u5f97\u51fa\u4ed6\u80a9\u5185\u787c\u8089\u4f3c\u5df2\u7a81\u7136\u5f3a\u786c\u3002 \u3000\u3000\u98ce\u4e5f\u4f3c\u4e4e\u7a81\u7136\u505c\u4e86\u9662\u5b50\u91cc\u7a81\u7136\u53d8\u5f97\u6b7b\u5bc2\u65e0\u58f0\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u90ed\u5927\u8def\u624d\u7f13\u7f13\u9053\u201c\u4f60\u82e5\u4e0d\u613f\u8bf4\u8bdd\uff0c\u70b9\u70b9\u5934\u4e5f\u884c\u4f46\u4f60\u53ef\u4ee5\u653e\u5fc3\u6211\u4ece\u6765\u6ca1\u6709\u780d\u4eba \u8111\u888b\u7684\u7ecf\u9a8c\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u5728\u80cc\u5f8c\u6740\u4eba\u3002\u201d \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u98ce\xb7\u6ca1\u6709\u58f0\u97f3\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\u9ed1\u8863\u4eba\u624d\u7f13\u7f13\u9053\u201c\u5341\u5e74\u6765\u4f60\u662f\u7b2c\u4e03\u4e2a\u95ee\u6211\u8fd9\u53e5\u8bdd\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u524d\u9762\u90a3\u516d\u4e2a\u4eba\u662f\u4e0d\u662f\u5168\u90fd\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u79be\u9519\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u6253\u5c31\u662f\u56e0\u4e3a\u95ee\u4e86\u8fd9\u53e5\u8bdd\u624d\u6b7b\u7684\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u65e0\u8bba\u8c01\u8981\u95ee\u8fd9\u53e5\u8bdd\uff0c\u90fd\u5f97\u4ed8\u51fa\u4ee3\u4ef7\uff0c\u6240\u4ee5\u5f25\u5458\u597d\u8fd8\u662f\u5148\u8003\u8651\u8003\u8651\u518d\u95ee\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u6211\u4e5f\u72e0\u60f3\u8003\u8651\u8003\u8651\u53ea\u53ef\u60dc\u73b0\u5728\u6211\u5df2\u7ecf\u95ee\u8fc7\u5382\u3002\uff1f \u3000\u3000\u9ed1\u8863\u4eba\u7cb9\u7136\u56de\u8eab\u76ee\u5149\u5200\u822c\u77aa\u4ed6\u5389\u58f0\u9053\u201c\u6211\u82e5\u662f\u5357\u5bab\u4e11\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6de1\u6de1\u5730\u9053\u201c\u6628\u5927\u665a\u535c\u6211\u5df3\u7b54\u5e94\u8fc7\u4f60\u53ea\u8981\u4f60\u8d70\u8fdb\u8fd9\u6247\u95e8\u5c31\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u7edd\u6ca1\u6709\u4eba\u4f1a \u4f24\u5bb3\u4f60\u4e5f\u6ca1\u6709\u4eba\u4f1a\u8d76\u4f60\u51fa\u53bb\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u89c2\u5728\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u8fd9\u53e5\u8bdd\u8fd8\u662f\u540c\u6837\u6709\u6548\u6211\u53ea\u672f\u8fc7\u60f3\u7559\u4f60\u591a\u4f4d\u4e9b\u65f6\u5019\u800c\u5df2\u201c \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u4f4f\u5230\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u662f\u6de1\u6de1\u9053\uff1a\u201c\u5f80\u5230\u4f60\u60f3\u901a\u81ea\u5df1\u4ee5\u524d\u6240\u505a\u7684\u4e8b\u90fd\u4e0d\u5bf9\u3002\u4efb\u5230\u4f60\u81ea\u5df1\u89c9\u5f97\u60ed\u6127\u3001\u4edf \u6094\u7684\u65f6\u5019\u4f60\u5c31\u53ef\u4ee5\u8d70\u5382\u3002\u201d \u3000\u3000\u9ed1\u519c\u4eba\u7684\u77aa\u5b54\u4f3c\u5728\u6536\u7f29\u5389\u58f0\u9053\u201c\u6211\u82e5\u4e0d\u80af\u53c8\u5982\u4f97\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u77ff\u7b11\u9053\u201c\u90a3\u4e5f\u5f88\u7b80\u5355\u3002\u201d \u3000\u3000\u4ed6\u9992\u6162\u7684\u8d70\u8fc7\u53bb\u5fae\u7b11\u9053\u201c\u6211\u8116\u5b50\u5f8c\u9762\u662f\u4e0d\u662f\u4e5f\u6709\u5904\u6bd4\u8f83\u5bb9\u6613\u535c\u5200\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u82e5\u80fd\u627e\u51fa\u6765\u5200\u780d\u5382\u6211\u7684\u8111\u888b\u4e5f\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u9053\u201c\u6211\u5df2\u7528\u4e0d\u518d\u627e\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u521a\u5201\u5c31\u5df4\u627e\u5382\u51fa\u6765\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u521a\u6211\u672a\u66fe\u8428\u624b\uff0c\u662f\u4e3a\u4e86\u62a5\u7b54\u4f60\u6628\u591c\u4e4b\u60c5\u4f46\u73b0\u5728 \u3000\u3000\u4ed6\u8eab\u5b50\u7a81\u7136\u5411\u5f8c\u8def\u4eba\u5df4\u7bad\u822c\u5ba1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u7adf\u4e5f\u8ddf\u5ba1\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7adf\u4e5f\u8ddf\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51cc\u4e2d\u7ffb\u5251\u5df2\u51fa\u5708\u4e03\u5c3a\u957f\u5251\u5982\u6fc0\u79cb\u6c34\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u201c\u545b\u201d\u7684\u58f0\u3002 \u3000\u3000\u8fd9\u67c4\u79cb\u6c34\u822c\u7684\u957f\u5251\u4e0a\xb7\u7ade\u4e49\u591a\u5382\u4e2a\u5251\u6a2a\u3002 \u3000\u3000\u5251\u9798\u662f\u4ece\u90ed\u5927\u8def\u7684\u957f\u888d\u4e0b\u62ff\u51fa\u6765\u7684\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8eab\u5b50\u5f80\u5f8c\u7a9c\uff0c\u4ed6\u4e5f\u56e0\u7a9c\u51fa\xb7\u9ed1\u8863\u4eba\u7684\u957f\u5251\u51fa\u5708\u4ed6\u5c31\u62ff\u51fa\u4e86\u888d\u5b50\u4e0b\u7684\u5251\u77aa\uff0c\u5f80\u524d\u9762 \u5957\uff0c\u5957\u4f50\u4e86\u9ed1\u8863\u4eba\u7684\u5228\u3002 \u3000\u3000\u5251\u957f\u4e03\u5c3a\u5251\u8e6d\u5374\u53ea\u6709\u53c1\u5c3a\u4e03\u5bf8\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u7684\u5251\u65e2\u5df2\u88ab\u5957\u4f50\u5c31\u518d\u4e5f\u65e0\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u8fd8\u662f\u5728\u5f80\u5f8c\u9000\u56e0\u4e3a\u4ed6\u5df2\u6ca1\u6cd5\u5b50\u4e0d\u9000\u90ed\u5927\u8def\u53cc\u624b\u63e1\u4f4f\u5251\u9798\u7528\u529b\u5f80\u524d\u9001\u4ed6\u957f\u5228\u82e5\u4e0d\u6492 \u624b\u5c31\u53ea\u6709\u88ab\u76f4\u63a8\u5f97\u5f80\u5f8c\u9000\u3002 \u3000\u3000\u4ed6\u957f\u5228\u82e5\u662f\u64a4\u624b\uff0c\u90a3\u9ebd\u5c31\u52bf\u5fc5\u8981\u88ab\u81ea\u5df1\u7684\u5251\u67c4\u6253\u5728\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u672c\u5c31\u662f\u5f80\u5f8c\u9000\u7684\u73b0\u5728\u60f3\u6539\u53d8\u7528\u529b\u7684\u65b9\u5411\xb7\u518d\u5f80\u524d\u63a8\u5df2\u4e0d\u53ef\u80fd\xb7\u6240\u4ee5\u73b0\u5728\u6839\u672c\u5df2\u8eab \u4e0d\u7531\u4e3b\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f80\u524d\u63a8\u2606\u5c3a\u4ed6\u5c31\u5f97\u67aa\u76fe\u9000\u5c3a\u3002 \u3000\u3000\u53ea\u542c\u201c\u7830\u201d\u7684\u58f0\u4ed6\u8eab\u5b50\u5df2\u88ab\u63a8\u649e\u5728\u5899\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd8\u662f\u7528\u53cc\u8155\u63e1\u4f4f\u5251\u671f\u5c06\u4ed6\u7684\u4eba\u7d27\u7d27\u5730\u987e\u5728\u5899\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u9000\u65e0\u53ef\u80fd\uff0c\u5996\u5251\u66f4\u4e0d\u80fd\u6492\u624b\u53ea\u8981\u6492\u624b\uff0c\u5251\u67c4\u5c31\u4f1a\u91cd\u91cd\u7684\u6253\u4e0a\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u8fd9\u60c5\u51b5\u4e4b\u5999\uff0c\u82e5\u975e\u4eb2\u773c\u770b\u5230\u7684\u4eba\u53ea\u4f2f\u8c01\u4e5f\u60f3\u8c61\u4e0d\u51fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u4f60\u5927\u6982\u6ca1\u6709\u60f3\u81f3\u8fc7\u5427\u201d \u3000\u3000\u9ed1\u591c\u4eba\u54ac\u7259\uff0c\u9053\u201c\u8fd9\u7b97\u662f\u4ec0\u9ebd\u529f\u592b\uff1f\u201d\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u6839\u672c\u5c31\u4e0d\u80fd\u591f\u7b97\u662f\u4ec0\u9ebd\u529f\u592b\uff0c\u56e0 \u4e3a\u8fd9\u79cd\u529f\u592b\xb7\u9664\u5382\u5bf9\u4ed8\u5f25\u4e4b\u5916\u5bf9\u4ed8\u522b\u7684\u4eba\u6839\u672c\u5c31\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u4ed6\u597d\u50cf\u8fd8\u751f\u4f2f\u8fd9\u9ed1\u519c\u4eba\u4e0d\u61c2\u6240\u4ee5\u53c8\u89e3\u91ca\u9053\u201c\u56e0\u4e3a\u4e16\u4e0a\u8d4a\u5382\u4f60\u4e4b\u5916\u7edd\u6ca1\u6709\u522b\u7684\u4eba\u4f1a\u7528\u8fd9\u79cd \u6cd5\u5b50\u62d4\u5251\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u9053\u201c\u4f60\u7279\u5730\u60f3\u51fa\u4e86\u8fd9\u9ebd\u4e00\u6765\u5bf9\u4ed8\u6211\u7684\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u7b54\u5bf9\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u9053\u201c\u4f60\u5176\u5b9e\u65e9\u5df3\u5b58\u5fc3\u8981\u5c06\u6211\u8d2d\u5728\u8fd9\u91cc\u7684\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u5176\u5b9e\u7559\u5728\u8fd9\u91cc\u4e5f\u6ca1\u4ec0\u9ebd\u4e2a\u597d\u81f3\u5c11\u6bcf\u5929\u90fd\u53ef\u4ee5\u5b89\u5fc3\u7761\u89c9\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ea\u8981\u4f60\u80af\u7b54\u5e94\u6211\u7559\u4e0b\u6765\u6211\u7acb\u523b\u5c31\u653e\u624b\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\u201c\u54fc\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54fc\u201d\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u9053\u201c\u73b0\u5728\u6211\u867d\u7136\u65e0\u6cd5\u6740\u4f60\u4f46\u4f60\u4e5f\u62ff\u6211\u65e0\u53ef\u5948\u4f55\xb7\u53ea\u8981\u4f60\u677e\u624b\u6211\u8fd8\u662f\u53ef\u4ee5\u7acb\u523b \u5192\u4f60\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u5012\u4e5f\u5e76\u975e\u5b8c\u5168\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u9ed1\u519c\u4eba\u9053\u201c\u6240\u4ee5\u4f60\u4f11\u60f3\u4ee5\u6b64\u8981\u80c1\u6211\xb7\u6211\u5c31\u7b97\u80af\u7b54\u5e94\uff0c\u4e5f\u5f97\u7b49\u4f60\u5148\u653e\u5f00\u624b\u518d\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u4ed6\u534a\u8d2d\u5ffd\u53c8\u7b11\u4e86\u7b11\xb7\u9053\u201c\u597d\u6211\u4e0d\u59a8\u518d\u60c5\u4efb\u4f60\u6b21\u53ea\u8981\u4f60\u2026\u3002\u2606 \u3000\u3000\u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u8fd8\u6ca1\u6709\u653e\u624b\u7adf\u7136\u770b\u5230\u4e00\u6837\u4e1c\u897f\u4ece\u9ed1\u8863\u4eba\u7684\u80f8\u819b\u94bb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e00\u6bb5\u5251\u5c16 \u3000\u3000\u5228\u5c16\u4e0a\u8fd8\u5728\u6ef4\u8840\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u770b\u8fd9\u6bb5\u5251\u5c16\u6cea\u4e2d\u7684\u8868\u60c5\u5c31\u548c\u9b3c\u516c\u4e8e\u4e34\u6b7b\u524d\u5b8c\u5168\u4e00\u6837\u3002\u90ed\u5165\u8def\u4e5f\u770b\u5f97\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u53ea\u542c\u9ed1\u8863\u4eba\u5589\u91cc\u201c\u683c\u683c\u201d\u4f5c\u54cd\u4eff\u4f5b\u60f3\u8bf4\u4ec0\u9ebd\u5374\u53c8\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u5927\u559d\u58f0\u51cc\u7a7a\u63a0\u8d77\u63a0\u51fa\u5899\u5916\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u679c\u7136\u662f\u4ece\u5899\u5916\u523a\u8fdb\u6765\u7684\uff0c\u7a7f\u8fc7\u4e86\u9ed1\u8863\u4eba\u7684\u80f8\u819b\uff0c\u5251\u67c4\u8fd8\u7559\u5728\u5899\u5916\u3002 \u3000\u3000\u4f46\u53ea\u6709\u5251\u67c4\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u98ce\u53c8\u5439\u8d77\u3001\u5c71\u5761\u4e0a\u91ce\u8349\u5982\u6ce2\u6d6a\u822c\u8d77\u4f0f\xb7\u4f46\u5374\u770b\u4e0d\u89c1\u534a\u6761\u5165\u5f71\u3002 \u3000\u3000\u5251\u67c4\u4e0a\u7cfb\u5757\u767d\u7ef8\u5b50\u4e5f\u5728\u968f\u98ce\u5377\u821e\u3002 \u3000\u3000\u7ff1\u5927\u8def\u60f3\u53bb\u62d4\u5251\u5374\u53c8\u53d1\u73b0\u81ea\u56e0\u4e0a\u8fd8\u5199\u4e03\u4e2a\u56e0\u6e83\u6dcb\u6f13\u7684\u5b87 \u3000\u3000\u201c\u5192\u540d\u8005\u6b7b \u3000\u3000\u5357\u5bcc\u4e11\u3002\u201d \u3000\u3000\u5251\u5c16\u4e0a\u8840\u6e0d\u5df2\u5343\u9ed1\u8863\u4eba\u5374\u4eff\u4f5b\u8fd8\u5728\u5782\u9996\u7591\u89c6\u8fd9\u6bb5\u5251\u5c16\u53c8\u4eff\u4f5b\u8fd8\u5728\u6c89\u601d\u3002 \u3000\u3000\u90a3\u795e\u60c5\u4e5f\u6b63\u548c\u9b3c\u516c\u4e88\u6b7b\u65f6\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u71d5\u4e03\u3001\u738b\u52a8\u3001\u6797\u592a\u5e73\u90fd\u8fdc\u8fdc\u7684\u7ad9\u5728\u8d70\u5eca\u4e0a\u770b\u4ed6\u4f53\u3002 \u3000\u3000\u4ed6\u6765\u5f97\u5947\u7a81\u6b7b\u5f97\u66f4\u5947\u7a81\u3002\uff01 \u3000\u3000\u4f46\u5458\u5947\u7a81\u7684\u8fd8\u662f\uff0c\u539f\u6765\u8fde\u4ed6\u4e5f\u4e0d\u662f\u5357\u5bab\u4e11\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ad9\u5728\u4ed6\u8eab\u65c1\u770b\u4ed6\u80f8\u4e0a\u7684\u5228\u5c16\uff0c\u4f3c\u4e4e\u4e5f\u5728\u6c89\u601d\u3002 \u3000\u3000\u71d5\u4e03\u6084\u6084\u8d70\u8fc7\u53bb\u9053\u201c\u4f60\u5728\u60f3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u6211\u5728\u60f3\u4ed6\u65e2\u4e0d\u662f\u5357\u5bab\u4e11\uff0c\u4e3a\u4ec0\u9ebd\u8981\u66ff\u5357\u5b98\u4e11\u80cc\u8fd9\u66f0\u9ed1\u7530\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u9ed1\u9505\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u662f\u5357\u5bab\u4e11\xb7\u7389\u73b2\u6591\u5c31\u4e0d\u4f1a\u6740\u4ed6\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u662f\u4e0d\u662f\u4e3a\u4ed6\u96be\u53d7\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6709\u70b9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f46\u6211\u5374\u53ea\u66ff\u5357\u5bab\u4e11\u96be\u53d7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u9893\u4e03\u9053\u201c\u4ed6\u5192\u4e86\u5357\u5bab\u4e11\u7684\u540d\u5728\u5916\u9762\u4e5f\u4e0d\u77e5\u6740\u4e86\u591a\u5c11\u4eba\u505a\u4e86\u591a\u5c11\u574f\u4e8b\uff0c\u5357\u5bcc\u4e11\u4e5f\u8bb8\u8fde\u5f71\u5b50 \u90fd\u4e0d\u77e5\u9053\u6240\u4ee5\u4f60\u672c\u8be5\u8bf4\uff0c\u662f\u5357\u5bcc\u4e11\u66ff\u4ed6\u5728\u80cc\u9ed1\u9505\uff0c\u4e0d\u662f\u4ed6\u66ff\u5357\u5bab\u4e11\u80cc\u9ed1\u9505\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u4e86\u60f3\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u8fd8\u662f\u53f9\u606f\u9053\u201c\u4f46\u65e0\u8bba\u5982\u4f55\xb7\u4ed6\u603b\u662f\u6211\u7684\u5ba2\u4eba\u603b\u662f\u6b7b\u5728\u6211 \u4eec\u9662\u5b50\u91cc\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u4f60\u8fd8\u662f\u5728\u4e3a\u4ed6\u96be\u53d7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd8\u662f\u6709\u70b9\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u2018\u201c\u4f53\u521a\u82e5\u771f\u7684\u677e\u4e86\u624b\uff0c\u4e0d\u77e5\u9053\u4ed6\u73b0\u5728\u4f1a\u4e0d\u4f1a\u66ff\u4f60\u96be\u53d7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u6211\u82e5\u677e\u5f00\u4e86\u624b\u4ed6\u96be\u9053\u5c31\u4f1a\u4e58\u795d\u6740\u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4ee5\u4e3a\u4ed6\u4e0d\u4f1a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u65e0\u8bba\u4f60\u600e\u9ebd\u8bf4\xb7\u6211\u8fd8\u662f\u89c9\u5f97\xb7\u4eba\u603b\u662f\u4eba\u603b\u6709\u4e9b\u4eba\u6027\u7684\uff0c\u4f60\u867d\u7136\u770b\u4e0d\u89c1\uff0c\u6478 \u4e0d\u4f46\u5374\u4e5f\u7edd\u4e0d\u80fd\u591f\u4e0d\u76f8\u4fe1\u5b83\u7684\u5b58\u5728\u5426\u5219\u4f60\u505a\u4eba\u8fd8\u6709\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u71d5\u4e03\u51dd\u89c6\u4ed6\u5ffd\u4e5f\u53f9\u606f\u4e01\u58f0\u67d4\u58f0\u9053\u201c\u5176\u5b9e\u6211\u53c8\u4f55\u5c1d\u4e0d\u5e0c\u671b\u4f60\u7684\u770b\u6cd5\u6bd4\u6211\u6b63\u786e\uff1f\u2026\xb7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u59cb\u8d77\u5934\u901a\u89c6\u4e91\u5929\u6df1\u5904\u6c89\u7f04\u4e86\u5f88\u4e45\u5ffd\u53c8\u9053\u201c\u73b0\u5728\u6211\u4e5f\u5728\u5e0c\u671b\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u2018\u201c\u4f60\u5e0c\u671b\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u5e0c\u671b\u6709\u4e00\u5929\u6211\u80fd\u770b\u5230\u771f\u7684\u5357\u5bcc\u4e11\u770b\u4ed6\u7a76\u7adf\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u5165\u2500\u4e00\u3001\u201d \u3000\u3000\u4ed6\u773c\u775b\u91cc\u53d1\u5149\uff0c\u7f00\u7f13\u63a5\u9053\u201c\u6211\u60f3\uff0c\u4ed6\u4e00\u5b9a\u6bd4\u6211\u4ee5\u524d\u770b\u5230\u7684\u4efb\u4f55\u4eba\u90fd\u795e\u79d8\u5f97\u591a\uff0c\u53ef\u4f2f\u5f97 \u591a\u3002\u201d \u3000\u3000\u4f46\u4e16\u4e0a\u662f\u4e0d\u662f\u771f\u7684\u6709\u5357\u5bab\u4e11\u8fd9\u9ebd\u6837\u7684\u4e00\u4e2a\u4eba\u5b58\u5728\u5462\uff1f \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\xb7\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u3002 \u7b2c\u5341\u4e03\u7ae0\u3000\u6625\u53bb\u4f55\u5904\uff1f>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u6625\u53bb\u4f55\u5904\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u5357\u5bab\u536b\u7684\u4e0b\u843d\uff0c\u6b63\u5982\u6ca1\u6709\u4eba\u80fd\u77e5\u9053\u6625\u7684\u53bb\u5904\u3002 \u3000\u3000\u4f46\u6625\u53bb\u8fd8\u4f1a\u518d\u6765\u5357\u5bcc\u4e11\u5374\u2019\u5b9d\u65e0\u6d88\u606f\u3002 \u3000\u3000\u73b0\u5728\u6625\u5df2\u5c06\u53bb\u3002 \u3000\u3000\u9662\u5b50\u91cc\u7684\u82b1\u867d\u5f00\u5f97\u66f4\u8273\u53ea\u53ef\u60dc\u65e0\u8bba\u591a\u7f8e\u7684\u82b1\uff0c\u4e5f\u4e0d\u80fd\u5c06\u6625\u7559 \u3000\u3000\u5929\u6c14\u5df4\u6e10\u9884\u70ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u738b\u52a8\u7684\u4f24\u52bf\u867d\u5df2\u597d\u4e86\u4f46\u4eba\u5374\u53d8\u5f97\u66f4\u61d2\u6574\u5929\u8eba\u5728\u4f51\u6905\u4e0a\uff0c\u51e0\u4e4e\u8fde\u52a8\u90fd\u4e0d\u52a8\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4eec\u4e3a\u90a3\u9ed1\u8863\u4eba\u4e0b\u846c\u7684\u90a3\u5929\u2026\u201c \u3000\u3000\u90a3\u5929\u867d\u8fd1\u6e05\u660e\u5374\u6ca1\u6709\u4ee4\u4eba\u65ad\u9b42\u7684\u96e8\u3002 \u3000\u3000\u5929\u6c14\u597d\u5f97\u5f88\u4ed6\u4eec\u4ece\u5893\u5730\u4e0a\u56de\u6765\u738b\u52a8\u53c8\u8c61\u5f80\u5e38\u6837\uff0c\u8d70\u5728\u6700 \u3000\u3000\u7ea2\u5a18\u5b50\u6ca1\u6709\u6765\u3002 \u3000\u3000\u5979\u7684\u4f24\u867d\u4e5f\u5df3\u5feb\u597d\u4e86\u5374\u8fd8\u662f\u6574\u5929\u628a\u81ea\u5df1\u5173\u5728\u623f\u5b50\u91cc\u73b0\u5728\u4e0d\u662f\u738b\u52a8\u5728\u8eb2\u5979\xb7\u5979\u53cd\u800c\u597d\u50cf\u603b \u662f\u5728\u8eb2\u738b\u52a8\u3002 \u3000\u3000\u5973\u4eba\u7684\u5fc3\u603b\u662f\u4ee4\u4eba\u6349\u6388\u4e0d\u900f\u7684\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u5947\u602a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u90ed\u5927\u8def\u6700\u8fd1\u597d\u50cf\u4e5f\u603b\u662f\u5e03\u8eb2\u71d5\u4e03\u3002 \u3000\u3000\u71d5\u4e03\u548c\u6797\u592a\u5e73\u5728\u524d\u9762\u8d70\xb7\u4ed6\u5c31\u50b2\u6d0b\u6d0b\u7684\u5728\u5f8c\u9762\u8ddf\u738b\u52a8\u3002 \u3000\u3000\u534a\u8def\u4e0a\u738b\u52a8\u627e\u4e86\u4e2a\u6709\u6811\u836b\u7684\u5730\u65b9\u5750\u5382\u6765\u4ef0\u4e86\u4e2a\u50b2\u8170\uff0c\u6253\u4e86\u4e2a\u5475\u6b20\u3002 \u3000\u3000\u4ed6\u4e5f\u8ddf\u5750\u4e0b\u6765\u4f38\u4e2a\u61d2\u8170\u6253\u4e86\u4e24\u4e2a\u5475\u6b20\u3002\u738b\u52a8\u7b11\u201c\xb7\u770b\u4ed6\u5fae\u7b11\u9053\u201c\u6700\u8fd1\u4f60\u597d\u50cf\u53d8\u5f97\u6bd4\u6211\u8fd8 \u61d2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u51c6\u89c4\u5b9a\u53ea\u6709\u4f60\u624d\u80fd\u6700\u736d\u7684\uff1f\u6211\u80fd\u4e0d\u80fd\u6bd4\u4f60\u736d\u4e00\u70b9\uff1f\u201d\u738b\u52a8\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6700\u8fd1\u672c\u8be5\u6bd4\u8c01\u90fd\u6709\u52b2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u90a3\u5929\u71d5\u4e03\u8bf4\u4f60\u7684\u8bdd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4e0d\u8bb0\u5f97\u3002\u4ed6\u8bf4\u7684\u8bdd\u6211\u4e3a\u4ec0\u9ebd\u5b9a \u8981\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5c31\u597d\u50cf\u521a\u541e\u4e0b\u72ac\u5428\u706b\u836f\u554a\u4e8e\u90fd\u88c5\u6ee1\u4e86\u706b\u836f\u6c14\u3002 \u3000\u3000\u5e72\u52a8\u5e76\u4e0d\u5728\u610f\u8fd8\u662f\u5fbd\u7b11\u9053\u201c\u4ed6\u8bf4\xb7\u6211\u4ecd\u8fd9\u56db\u4e2a\u4eba\u4e4b\u4e2d\u672c\u6765\u4ee5\u4f60\u7684\u6b66\u529f\u6700\u5dee\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4eec\u90fd\u6709\u597d\u5e08\u5085\u6211\u6ca1\u6709\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53ef\u662f\u81ea\u4ece\u90a3\u5929\u4f60\u8ddf\u90a3\u9ed1\u8863\u4eba\u4ea4\u8fc7\u624b\u4e4b\u5f8c\u4ed6\u624d\u53d1\u73b0\u6211\u4eec\u7684\u6b66\u529f\u867d\u7136\u6bd4\u4f60\u9ad8\u4f46\u82e5\u771f \u548c\u4f60\u6253\u8d77\u6765\xb7\u4e5f\u8bb8\u5168\u90fd\u4e0d\u662f\u4f60\u7684\u5bf9 \u3000\u3000\u90ed\u5927\u8def\u51b7\u51b7\u9053\u201c\u4ed6\u8bf4\u7684\u8bdd\uff0c\u4e5f\u8bb8\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f46\u6211\u76f8\u4fe1\u56e0\u4e3a\u6211\u7684\u770b\u6cd5\u4e5f\u8ddf\u4ed6\u6837\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u5de5\u52a8\u9053\u201c\u4f60\u6b66\u529f\u867d\u7136\u4e0d\u5982\u6211\u4eec\xb7\u4f46\u662f\u548c\u4eba\u4ea4\u624b\u65f6\u5374\u80fd\u968f\u673a\u5e9c\u53d8\u5236\u654c\u89c6\u5148\u82e5\u98df\u53e5\u8001\u8bdd\u6765\u8bf4 \u4f60\u6b63\u662f\u4e2a\u5929\u8d4b\u5f02\u5bdf\xb7\u767e\u7586\u96be\u9047\u7684\u7ec3\u6b66\u597d\u6750\u6599\u6240\u4ee5\u2026\u2026\u201d\u90ed\u5927\u8def\u9053\u6240\u4ee5\u6211\u4eec\u5e94\u8be5\u6253\u6881\u6765\u8bd5\u8bd5\u770b \u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4ed6\u7684\u706b\u836f\u6627\u8fd8\u5f88\u91cd\u738b\u52a8\u8fd8\u662f\u4e0d\u7406\u4ed6\uff0c\u5fbd\u7b11\u9053\u201c\u6240\u4ee5\u4f60\u5e94\u8be5\u632f\u4f5c\u8d77\u7cbe\u795e\u6765\u518d\u597d\u597d\u7684\u7ec3\u7ec3\u529f \u592b\u82e5\u80fd\u591f\u627e\u4e2a\u597d\u5e08\u535a\u4ee5\u5f8c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u5929\u4e0b\u6b66\u6797\u7684\u7b2c\u9762\u624b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u73b0\u5728\u6211\u5012\u5e76\u4e2d\u60f3\u4f10\u4e2a\u597d\u5e08\u5085\u60f3\u627e\u4e2a\u597d\u4e08\u592b\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u54ac\u56ed\u5df1\u7684\u624b\u6307\u7532\xb7\u9053\u201c\u56e0\u4e3a\u2026\u3002\u2018\u56e0\u4e3a\u6211\u6709\u75c5\u3002\u201d \u3000\u3000\u738b\u52a8\u52a8\u5bb9\u9053\u201c\u4f60\u6709\u75c5\u4ec0\u9ebd\u75c5\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e00\u79cd\u72e0\u5947\u602a\u7684\u75c5\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u4ee5\u524d\u4e3a\u4ec0\u9ebd\u6ca1\u6709\u8bf4\u8d75\u8fc7\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u2500\u4e00\u2500\u6211\u4e0d\u80fd\u8bf4\u3002\u3002 \u3000\u3000\u4ed6\u7684\u786e\u6ee1\u8138\u90fd\u662f\u75db\u82e6\u4e4b\u8272\u5e76\u4e0d\u8c61\u8db3\u5728\u5f00\u73a9\u7b11\u7684\u6837\u5b50\u3002 \u3000\u3000\u738b\u52a8\u5c45\u7136\u4e5f\u6ca1\u6709\u518d\u95ee\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u95ee\u5f97\u8d8a\u6025\xb7\u90ed\u5927\u8def\u8d8a\u4e0d\u4f1a\u8bf4\u7684\u3002 \u3000\u3000\u4ed6\u65e2\u7136\u4e0d\u95ee\u90ed\u5927\u8def\u53cd\u9762\u618b\u4e0d\u4f4f\u4e86\uff0c\u53cd\u800c\u7f51\u4ed6\uff1a\u201c\u4f60\u96be\u9053\u6ca1\u6709\u53d1\u73b0\u6700\u8fd1\u6211\u6709\u70b9\u53d8\u4e86\uff1f\u201d \u3000\u3000\u738b\u52a8\u76b1\u5cbf\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u7530\xb7\u597d\u50cf\u6709\u90a3\u9ebd\u4e00\u70b9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u56db\u9053\u201c\u90a3\u5c31\u56e0\u4e3a\u6211\u6709\u75c5\u3002\u201d \u3000\u3000\u738b\u52a8\u8bd5\u63a2\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u7684\u6bdb\u75c5\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6307\u81ea\u5df1\u7684\u5fc3\u53e3\xb7\u9053\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u738b\u52a8\u76b1\u7709\u9053\u201c\u4f60\u5f97\u7684\u662f\u5fc3\u75c5\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u8138\u8272\u66f4\u75db\u82e6\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u5fc3\u75c5\u4e5f\u6709\u5f88\u591a\u79cd\u636e\u6211\u6240\u77e5\u6700\u5389\u5bb3\u7684\u4e00\u79cd\u5c31\u662f\u76f8\u601d\u75c5\u4f60\u96be\u9053\u5f97\u4e86\u76f8\u601d\u75c5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e0d\u505c\u7684\u53f9\u6c14\u3002 \u3000\u3000\u738b\u52a8\u5374\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u76f8\u601d\u75c5\u5e76\u4e0d\u4e22\u4eba\u7684\xb7\u6062\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u8bf4\u51fa\u6765\uff1f\u8bf4\u4e0d\u5b9a\u6211\u8fd8\u53ef\u4ee5\u66ff\u4f60\u53bb \u4f5c\u5a92\u5462\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7528\u529b\u54ac\u7259\xb7\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u628a\u6293\u4f4f\u738b\u52a8\u7684\u80a9\u9053\u201c\u4f60\u662f\u4e0d\u662f\u6211\u7684\u597d\u670b\u53cb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5f53\u7136\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u670b\u53cb\u662f\u4e0d\u662f\u5e94\u8be5\u4e92\u76f8\u4fdd\u5b88\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5f53\u7136\u5e94\u8be5\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u6709\u4e2a\u79d8\u5bc6\xb7\u5df2\u61a8\u4e86\u5f88\u4e45\u518d\u4e0d\u8bf4\u51fa\u6765\u53ea\u4f2f\u5c31\u8981\u53d1\u75af\u4e86\u53ef\u662f\u2026\u201d\u53ef\u662f\u6211\u60f3\u8bf4\u51fa \u6765\uff0c\u53c8\u4f2f\u4f60\u7b11\u6211\u3002\u201d\u738b\u52a8\u9053\u201c\u4f60\u3002\u2026\u4f60\u5f97\u7684\u96be\u9053\u662f\u201d\u2026\u4fb5\u82b1\u67f3\u75c5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u738b\u52a8\u677e\u4e86\u53e3\u6c14\u3001\u9053\uff1a\u201c\u90a3\u5c31\u6ca1\u5173\u7cfb\u4e86\u4f60\u5c3d\u7ba1\u8bf4\u51fa\u6765\uff0c\u6211\u7edd\u4e0d\u7b11\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u72b9\u8c6b\u534a\u5929\u624d\u52b3\u8138\u9053\u201c\u76f8\u601d\u75c5\u4e5f\u4e0d\u53ea\u79cd\uff0c\u6211\u5f97\u7684\u5374\u662f\u6700\u89c1\u4e0d\u5f97\u4eba\u7684\u90a3\u79cd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\u89c1\u4e0d\u5f97\u4eba\uff1f\u52e4\u7aa5\u6dd1\u4ea5\uff0c\u541b\u5b50\u597d\u9010\xb7\u6c42\u4e4b\u4e0d\u5f97\xb7\u78be\u8f6c\u53cd\u4fa7\u90a3\u672c\u662f\u5929\u7ecf\u5730\u4e49 \u7684\u4e8b\uff0c\u6709\u4ec0\u9ebd\u4e22\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u3002\u6211\u8fd9\u76f8\u601d\u75c5\u5e76\u4e0d\u662f\u4e3a\u5973\u4eba\u5f97\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u4e5f\u6014\u4fe1\u5382\uff0c\u6014\u4e86\u534a\u5929\xb7\u624d\u8bd5\u63a2\u95ee\u9053\u201c\u4f11\u76f8\u601d\u7684\u96be\u9053\u662f\u4e2a\u7537\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u7b80\u76f4\u597d\u50cf\u8981\u54ed\u51fa\u6765\u7684\u6837\u5b50\u3002 \u3000\u3000\u738b\u52a8\u597d\u50cf\u5f88\u5bb3\u4f2f\u7684\u6837\u4e8e\uff0c\u6545\u610f\u538b\u4f4e\u4e86\u58f0\u97f3\uff0c\u6084\u6084\u9053\uff0c\u201c\u4e0d\u4f1a\u662f\u6211\u5427\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u662f\u60f3\u54ed\uff0c\u8fd8\u662f\u60f3\u7b11\xb7\u53ea\u6709\u677f\u8138\u9053\u201c\u6211\u7684\u75c5\u8fd8\u6ca1\u6709\u8fd9\u9ebd\u91cd\u3002\u201d \u3000\u3000\u738b\u52a8\u5374\u4f3c\u53c8\u677e\u4e86\u53e3\u6c14\xb7\u7b11\u9053\u201c\u53ea\u8981\u4e0d\u662f\u6211\u5c31\u6ca1\u6709\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u538b\u4f4e\u58f0\u97f3\u9053\u201c\u662f\u4e0d\u662f\u6797\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u89c1\u4e86\u6d3b\u9b3c\u3002\u201d \u3000\u3000\u738b\u52a8\u53c8\u76b1\u60f3\u4e86\u534a\u5929\uff0c\u624d\u5c55\u989c\u7b11\u9053\u201c\u6211\u660e\u767d\u4e86\u4f60\u559c\u6b22\u7684\u662f\u71d5 \u3000\u3000\u90ed\u5927\u8def\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u738b\u52a8\u60a0\u7136\u9053\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u5df2\u770b\u4e86\u51fa\u6765\u4f60\u8001\u662f\u559c\u6b22\u8ddf\u4ed6\u5728\u8d77\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u8138\u9053\u201c\u4ee5\u524d\u6211\u8fd8\u5e76\u6ca1\u6709\u89c9\u5f97\u6709\u4ec0\u9ebd\u4e0d\u5bf9\u8fd8\u4ee5\u4e3a\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u4eec\u662f\u597d\u670b\u53cb\u4f46\u5f8c \u6765\u2026\u3002\u2019\u5f8c\u6765\u2026\u2026\u201d \u3000\u3000\u738b\u52a8\u7728\u4e86\u7728\u773c\xb7\u9053\u201c\u5f8c\u6765\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u901a\u201c\u5f8c\u6765\u2026\u3002\u5f8c\u6765\u5c31\u672f\u5bf9\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u2018\u201c\u4ec0\u9ebd\u5730\u65b9\u4e0d\u5bf9\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e5f\u8bf4\u4e0d\u51fa\u6765\u7a76\u7adf\u4ec0\u9ebd\u5730\u65b9\u4e0d\u5bf9\xb7\u53cd\u6b63\u53ea\u8981\u6211\u8ddf\u4ed6\u5728\u8d77\u7684\u65f6\u5019\u5fc3\u60c5\u5c31\u7279\u4e0d \u6837\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u4f55\u6837\uff1f\u201d \u3000\u3000\u4ed6\u5012\u771f\u662f\u6253\u7834\u7802\u9505\u95ee\u5230\u5e95\u8fde\u70b9\u90fd\u4e0d\u80af\u653e\u677e\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u6837\u5c31\u662f\u4e0d\u6837\u53cd\u6b63\u2500\u4e00\u201c\u53cd\u6b63\u5c31\u662f\u4e0d\u6837\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u4e86\u4e5f\u7b49\u65bc\u6ca1\u8bf4\u3002 \u3000\u3000\u738b\u52a8\u597d\u50cf\u5df2\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u6765\u4e86\u4f46\u603b\u7b97\u8fd8\u662f\u5fcd\u4efb\xb7\u6b63\u8272\u9053\u201c\u5176\u5b9e\u8fd9\u4e5f\u4e0d\u80fd\u7b97\u4e22\u4eba\u7684\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd8\u4e0d\u4e22\u4eba\uff1f\u50cf\u6211\u8fd9\u6837\u4e00\u4e2a\u7537\u5b50\u6c49\xb7\u5c45\u7136\u2026\u3002\u201c \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u8fd9\u79cd\u6bdb\u75c5\u7684\u4eba\u4f60\u4e5f\u4e0d\u662f\u7b2c\u4e2a\u3002\u65ad\u8896\u5206\u6843\xb7\u8fde\u7687\u5e1d\u8005\u4e8e\u90fd\u6709\u8fd9\u79cd\u55dc\u597d\xb7\u800c\u4e14\u5343 \u53e4\u4f20\u4e3a\u4f73\u8bdd\u6211\u770b\u4f60\u5012\u4e0d\u5982\u7d22\u6027\u8ddf\u4ed6 \u3000\u3000\u90ed\u5927\u8def\u8df3\u4e86\u8d77\u6765\uff0c\u77aa\u4ed6\u6012\u9053\u201c\u539f\u6765\u4f60\u4e0d\u662f\u6211\u7684\u670b\u53cb\u6211\u770b\u9519\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ed6\u626d\u5934\u5c31\u60f3\u5b9a\u4e86\u3002 \u3000\u3000\u738b\u52a8\u5374\u62c9\u4f73\u4e86\u4ed6\u9053\u201c\u522b\u751f\u2019\u522b\u751f\u6c14\u6211\u8fd9\u53ea\u4e0d\u8fc7\u662f\u5728\u8bd5\u8bd5\u4f60\u7684\xb7\u5176\u5b9e\u6211\u4e5f\u65e9\u5df2\u770b\u51fa\u6765\uff0c\u71d5 \u4e03\u8fd9\u4e2a\u4eba\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002\u201d\u6b22\u4e50\u835a\u6218 \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u6014\xb7\u9053\u201c\u4ed6\u6709\u4ec0\u9ebd\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u738b\u52a8\u597d\u5bb9\u6613\u624d\u603b\u7b97\u6ca1\u6709\u7b11\u51fa\u6765\u677f\u8138\u9053\u201c\u4f60\u96be\u9053\u6ca1\u6709\u770b\u51fa\u4ed6\u8fd9\u4eba\u6709\u70b9\u90aa\u6c14\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90aa\u6c14\uff1f\u4ec0\u9ebd\u90aa\u6c14\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u867d\u7136\u662f\u8fd9\u9ebd\u597d\u7684\u670b\u53cb\uff0c\u4f46\u4ed6\u90e8\u8fd8\u662f\u8c61\u9632\u5077\u4f3c\u7684\u9632\u6211\u4eec\u7761\u89c9\u7684\u65f6\u5019\u5b9a\u5148\u628a\u95e8 \u7a97\u90fd\u62f4\u4e0a\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u6bcf\u6b21\u51fa\u53bb\u7684\u65f6\u5019\u603b\u662f\u5077\u5077\u7684\u6e9c\u8d70\uff0c\u597d\u50cf\u751f\u6015\u6211\u8ba2\u4f1a\u8ddf\u4ed6\u4f3c\u7684\xb7\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5bf9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u597d\u50cf\u4ece\u6765\u6ca1\u6d17\u8fc7\u6fa1\uff1b\u4f46\u8eab\u4e0a\u5374\u5e76\u5c0f\u592a\u81ed\xb7\u7a7f\u7684\u8863\u670d\u867d\u7136\u53c8\u810f\u53c8\u7834\u4f46\u5c4b\u5e90\u91cc\u90ae \u6bd4\u8c01\u90fd\u4e7e\u51c0\u2026\u2026\u4f60\u8bf4\u8fd9\u4e9b\u5730\u65b9\u662f\u4e0d\u662f\u90fd\u6709\u70b9\u90aa\u6c14\u201d \u3000\u3000\u90ed\u5927\u8def\u8138\u8272\u4f3c\u4e4e\u6709\u4e9b\u53d1\u767d\xb7\u8fdf\u7591\u9053\u201c\u4f60\u7684\u610f\u601d\u96be\u9053\u662f\u8bf4\u4ed6 \u3000\u3000\u5e72\u52a8\u9053\u201c\u6211\u4ec0\u9ebd\u90fd\u6ca1\u6709\u8bf4\uff0c\u4e5f\u6ca1\u6709\u8bf4\u4ed6\u662f\u9b54\u6559\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5927\u58f0\u54b3\u55fd\u3001\u4ee5\u4e3a\u82e5\u518d\u4e0d\u54b3\u55fd\xb7\u53ea\u4f2f\u5c31\u8981\u7b11\u51fa\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u8138\u8272\u5374\u66f4\u53d1\u767d\uff0c\u5634\u91cc\u7ffb\u6765\u8986\u53bb\u7684\u5ff5\u4e24\u4e2a\u5b87\u201c\u9b54\u6559\u2500\u4e00\u2500\u9b54\u6559\u3002\u4e00\u3002\u201d \u3000\u3000\u738b\u52a8\u54b3\u55fd\u5382\u4e2d\u5929\u624d\u603b\u7b97\u5fcd\u4f4f\u7b11\u58f0\u53c8\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u542c\u8bf4\u9b54\u6559\u4e2d\u6709\u51e0\u5bf9\u592b\u59bb\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u5730\u65b9\u5947\u602a\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u51e0\u5bf9\u592b\u59bb\u6587\u5927\u662f\u7537\u4eba\uff0c\u592a\u5927\u4e5f\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u5c31\u8c61\u662f\u5ffd\u7136\u4e2d\u5382\u4e00\u6839\u51b7\u7bad\u4f3c\u7684\uff0c\u6574\u4e2a\u4eba\u90fd\u8df3\u5382\u8d77\u6765\u628a\u6293\u5316\u4e86\u571f\u52a8\u560e\u58f0\u9053\u201c\u4f60\u2026\u2026 \u4f60\u5b9a\u8981\u5e2e\u6211\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u600e\u9ebd\u5e2e\u6cd5\uff1f\u201d\u7ff1\u5927\u8def\u9053\u201c\u60f3\u6cd5\u5b50\u5927\u5435\u67b6\u3002\u201d \u3000\u3000\u738b\u52a8\u901a\u201c\u5927\u5435\u67b6\uff1f\u600e\u9ebd\u5435\u6cd5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u968f\u4fbf\u600e\u9ebd\u5435\u90fd\u5f79\u5173\u7cfb\u5435\u5f97\u8d8a\u5389\u5bb3\u8d8a\u597d\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u5435\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u529b\u5435\u8fc7\u4e4b\u5f8c\u6211\u5c31\u53ef\u4ee5\u8d70\u5382\u4e4b\u3002\u201d \u3000\u3000\u738b\u52a8\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u53d8\u4f3c\u4e4e\u89c9\u5f97\u81ea\u5df1\u8fd9\u73a9\u7b11\u5f00\u5f97\u592a\u5927\u4e01\uff0c\u8fc7\u201c\u534a\u664c\uff0c\u624d\u52c9\u5f3a\u7b11\u9053\u201c\u5176\u5b9e\u4f60 \u4e5f\u4e0d\u5fc5\u8981\u8d70\xb7\u5176\u5b9e\u4ed6\u2026\u2026\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u8981\u8bf4\u51fa\u4ec0\u9ebd\u79d8\u5bc6\uff0c\u4f46\u90ed\u5927\u8def\u5374\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u62a2\u9053 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7136\u5f8c\u6211\u5c31\u5728\u5c71\u4e0b\u7b49\u4ed6\u53ea\u8981\u4ed6\u51fa\u53bb\u6211\u5c31\u53ef\u4ee5\u6697\u5dfe\u9650\u8e2a\u770b\u770b\u4ed6\u7a76\u7adf\u5230\u4e9b\u4ec0\u9ebd\u5730\u65b9 \u53bb\u613f\u4e9b\u4ec0\u9ebd\u4eba\u89c1\u9762\u3002\u201d \u3000\u3000\u4ed6\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u63a5\u9053\u201c\u65e0\u8bba\u5982\u4f55\u6211\u4e5f\u8981\u67e5\u51fa\u4ed6\u7a76\u7adf\u6709\u4ec0\u9ebd\u79d8\u5bc6\u3002\u201d \u3000\u3000\u571f\u52a8\u6c89\u541f\xb7\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u5728\u5bb6\u91cc\u7b49\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u82e5\u5c31\u8fd9\u6837\u7f00\u8e2a\u4ed6\u5b9a\u4f1a\u88ab\u4ed6\u53d1\u89c9\u7684\u3002\u201d\u738b\u52a8\u9053\u201c\u96be\u9053\u4f60\u60f3\u5230\u5c71\u4e0b\u53bb\u6613\u5bb9 \u6539\u626e\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5631\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u998f\u5f97\u6613\u5bb9\u672f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5c0f\u61c2\u4f46\u6211\u5374\u6709\u6211\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u571f\u52a8\u6b6a\u5934\u8003\u8651\u4e86\u534a\u5929\xb7\u7f13\u7f13\u9053\u201c\u4f60\u65e2\u7136\u5df2\u51b3\u5fc3\u8981\u8fd9\u9ebd\u505a\u4e5f\u672a\u5c1d\u4e0d\u53ef\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ea\u4e0d\u8fc7\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u4eec\u8981\u5435\u5c31\u5f97\u5435\u5f97\u8c61\u4e2a\u6837\u5b50\xb7\u5426\u5219\u4ed6\u7edd\u77f3\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u97f6\u5927\u8def\u9053\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u571f\u52a8\u9053\u201c\u6240\u4ee5\u6211\u4eec\u5c31\u8981\u7b49\u673a\u4f1a\u7edd\u4e0d\u80fd\u5c31\u8fd9\u6837\u65e0\u7f18\u65e0\u6545\u7684\u5435\u8d77 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8981\u7b49\u4ec0\u9ebd\u6837\u7684\u673a\u4f1a\u5462\uff1f\u201d \u3000\u3000\u5de5\u529f\u7b11\u4e86\u7b11\u9053\u201c\u6211\u867d\u7136\u4e0d\u592a\u7235\u6b22\u8ddf\u522b\u4eba\u5435\u67b6\u4f46\u8981\u627e\u4e2a\u5435\u67b6\u7684\u673a\u4f1a\u5012\u5e76\u4e0d\u592a\u56f0\u96be\u3002\u201d\u90ed\u5927 \u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u5e38\u5e38\u4e0d\u8bf4\u4eba\u8bdd\u7684\u3002\u201d\u90ed\u5927\u8def\u4e5f\u7b11\u201c\u9053\u201c\u82e5\u662f\u71d5\u4e03\u5728\u8fd9\u91cc\u6211\u73b0\u5728\u5c31 \u53ef\u4ee5\u8ddf\u4f60\u5435\u8d77\u6765\u201c \u3000\u3000\u738b\u52a8\u9053\u201c\u73b0\u5728\u6211\u53ea\u62c5\u5fc3\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u62c5\u5fc3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u6015\u4ed6\u5e2e\u4f60\u8ddf\u6211\u5435\u5435\u5b8c\u4e86\u8ddf\u4f60\u8d77\u8d70\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u4e86\u7728\u8ddf\uff0c\u9053\u201c\u8fd9\u70b9\u4f60\u5012\u7528\u4e0d\u62c5\u5fc3\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u65e2\u7136\u80fd\u8ddf\u4f60\u5435\u96be\u9053\u5c31\u4e2d\u80fd\u8ddf\u4ed6\u5435\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u53c8\u7b11\u4e86\uff0c\u9053\u201c\u5f53\u7136\u80fd\u3002\u6709\u65f6\u4f60\u8bf4\u7684\u8bdd\u8db3\u8db3\uff1b\u3008\u201c\u7684\u4eba\uff0c\u65e0\u8bba\u8c01\u8ddf\u4f60\u5435\u8d77\u6765\u6211\u90fd\u4e0d\u4f1a \u89c9\u5f97\u5f88\u5947\u602a\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8fd8\u6ca1\u6709\u5f00\u53e3\u7a81\u7136\u542c\u5230\u4e00\u58f0\u60ca\u547c\u4ece\u90a3\u8fb9\u7684\u6811\u6797\u4e2d\u4f20\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u5728\u653e\u58f0\u5927\u53eb\u201c\u6551\u547d\u5440\u3001\u4e00\u3002\u6551\u547d\u201d \u3000\u3000\u7537\u4eba\u542c\u5230\u5973\u5b69\u5b50\u53eb\u201c\u6551\u547d\u201d\uff0c\u5927\u591a\u6570\u90fd\u4f1a\u7acb\u523b\u8d76\u8fc7\u53bb\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u5e76\u6ca1\u6709\u771f\u7684\u51c6\u5907\u53bb\u6551\u4ed6\u81f3\u5c11\u4e5f\u4f1a\u8d76\u8fc7\u53bb\u770b\u770b\u3002 \u3000\u3000\u6bcf\u4e2a\u7537\u4eba\u2019\u751f\u4e2d\uff0c\u591a\u591a\u5c11\u5c11\u603b\u4f1a\u5e7b\u60f3\u8fc7\u4e00\u4e24\u6b21\u201c\u82f1\u96c4\u6551\u7f8e\u201d\u8fd9\u79cd\u4e8b\u7684\uff0c\u53ea\u53ef\u501f\u4e8b\u5b9e\u4e0a\u8fd9 \u79cd\u673a\u4f1a\u5e76\u4e0d\u592a\u591a\u9762\u5df2\u3002 \u3000\u3000\u73b0\u5728\u673a\u4f1a\u6765\u4e86\uff0c\u90ed\u5927\u8def\u600e\u9ebd\u80af\u9976\u8fc7\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e0d\u7b49\u738b\u52a8\u6709\u6240\u884c\u52a8\u5c31\u5df2\u7ecf\u8df3\u4e86\u8d77\u6765\u76f4\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u597d\u50cf\u8fd8\u662f\u8fdf\u4e86\u6b65\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u521a\u8df3\u8d77\u6765\u5c31\u770b\u5230\u4e2a\u4eba\u7bad\u4e5f\u4f3c\u7684\u51b2\u4eba\u4e86\u6811\u6797\u3002 \u3000\u3000\u53eb\u201c\u6551\u547d\u201d\u7684\u5973\u5b69\u5b50\uff0c\u5927\u591a\u6570\u90fd\u4e0d\u4f1a\u957f\u5f97\u592a\u4e11\xb7\u4f46\u8c61\u73b0\u5728\u53eb\u6551\u547d\u7684\u8fd9\u4e2a\u4ea5\u63a5\u5382\u8fd9\u9ebd\u6837\u6f02 \u4eae\u7684\u5012\u4e5f\u5e76\u4e0d\u592a\u591a\u3002 \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u5e74\u7eaa\u4e0d\u5927\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u5341\u4e03\u516b\u5c81\u68b3\u4e24\u6839\u6cb9\u5149\u6c34\u6ed1\u7684\u5927\u8fab\u5b50\xb7\u66f4\u663e\u5f97\u4fcf\u76ae\u4f36\u4fd0\u3002 \u3000\u3000\u5979\u624b\u91cc\u63d0\u4e2a\u82b1\u7bee\u2019\u5f20\u767d\u751f\u751f\u7684\u74dc\u5b50\u8138\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u6b63\u56f4\u68f5\u6811\u5728\u6253\u8f6c\u3002 \u3000\u3000\u4e2a\u6ee1\u8138\u80e1\u5b50\u7684\u5f6a\u5f62\u5927\u6c49\u8138\u4e0a\u5e26\u72de\u7b11\uff0c\u56f4\u6811\u8feb\u3002 \u3000\u3000\u4ed6\u8feb\u5f97\u5e76\u4e0d\u6025\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4ea5\u5b69\u5b50\u5df2\u7ecf\u662f\u4ed6\u53e3\u4e2d\u7684\u98df\u7269\u5df2\u7ecf\u4f11\u60f3\u9003\u51fa\u4ed6\u7684\u638c\u5fc3\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u534a\u8def\u4e0a\u7adf\u4f1a\u6740\u51fa\u4e2a\u7a0b\u54ac\u91d1\u6765\u3002 \u3000\u3000\u5bb0\u597d\u6765\u7684\u8fd9\u7a0b\u54ac\u91d1\uff0c\u53ea\u4e0d\u5730\u662f\u5e74\u9752\u5c0f\u4f19\u4e88\uff0c\u957f\u5f97\u4e5f\u8ddf\u5927\u52c9\u5a18\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e0d\u7b49\u6797\u592a\u5e73\u5f00\u53e3\u4ed6\u53cd\u800c\u5148\u543c\u4e86\u8d77\u6765\u5927\u58f0\u9053\u201c\u4f60\u8fd9\u514d\u60a3\u5b50\u8c01\u53eb\u4f60\u6765\u7684\uff1f\u82e5\u662f\u649e\u8d70\u4e86 \u8001\u4e8e\u7684\u597d\u4e8b\u5f53\u5fc3\u8005\u5b50\u628a\u4f60\u7684\u8111\u888b\u62e7\u4e0b\u6765\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6c89\u8138\xb7\u9053\u201c\u4ec0\u9ebd\u597d\u4e8b\uff1f\u201d \u3000\u3000\u5927\u6c49\u72de\u7b11\u9053\u201c\u8001\u5b50\u5728\u65bc\u7684\u4ec0\u9ebd\u4e8b\xb7\u4f60\u5c0f\u65bc\u96be\u9053\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u7ad9\u5a18\u5df2\u8eb2\u5230\u6797\u592a\u5e73\u80cc\u5f8c\u5598\u6c14\xb7\u98a4\u58f0\u9053\u201c\u4ed6\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u2026\u3002\u2019\u4ed6\u8981\u6b3a\u8d1f\u6211\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u6de1\u8c08\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u4eba\u80fd\u6b3a\u8d1f\u4f60\u4e86\u3002\u201d \u3000\u3000\u5927\u6c49\u6012\u543c\u9053\u201c\u96be\u9053\u4f60\u8fd9\u4e2a\u514d\u6247\u5b50\u8fd8\u60f3\u591a\u7ba1\u95f9\u4e8b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u5927\u6c49\u72c2\u543c\u4e00\u58f0\xb7\u997f\u864e\u6251\u7f8a\u822c\u5411\u6797\u592a\u5e73\u72e0\u6444\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u770b\u6765\u4ed6\u4e5f\u662f\u7ec3\u8fc7\u51e0\u5929\u529f\u592b\u7684\u4e0d\u4f46\u4e0b\u76d8\u5f88\u7a46\u800c\u4e14\u51fa\u624b\u4e5f\u5f88\u5feb\u3002 \u3000\u3000\u53ea\u53ef\u500d\u4ed6\u9047\u7684\u662f\u6797\u592a\u5e73\u3002 \u3000\u3000\u6797\u592a\u5e73\u6325\u624b\xb7\u4ed6\u5c31\u5df2\u8c61\u91ce\u72d7\u88ab\u8e22\u4e86\u4e00\u817f\u201c\u9aa8\u788c\u788c\u201d\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u53c8\u60ca\u53c8\u6012\uff0c\u786e\u91cc\u5927\u9a82\uff0c\u770b\u6837\u5b50\u8fd8\u60f3\u722c\u8d77\u6765\xb7\u518d\u62fc\u62fc\u3002 \u3000\u3000\u8c01\u77e5\u5f8c\u9762\u5df2\u6709\u4e2a\u4eba\u628a\u79cb\u4f50\u4e86\u4ed6\u7684\u8863\u9886\u628a\u4ed6\u6574\u4e2a\u4eba\u62ce\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u4e0d\u4f46\u529b\u6c14\u5927\uff0c\u8eab\u6750\u9971\u4e0d\u6bd4\u4ed6\u77ee\xb7\u53ea\u7528\u53ea\u624b\u62ce\u4f4f\u4ed6\xb7\u4ed6\u5c45\u7136\u8fde\u70b9\u53cd\u6297\u7684\u6cd5\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u603b\u7b97\u8d76\u6765\u4e86\uff0c\u62ce\u4ed6\u8d70\u5230\u6797\u592a\u5e73\u9762\u524d\xb7\u5fae\u7b11\u9053\u201c\u4f24\u8bf4\u5e94\u8be5\u600e\u9ebd\u6253\u53d1\u8fd9\u5c0f\u65bc\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u90a3\u5c31\u5f97\u770b\u8fd9\u4f4d\u7ad9\u5a18\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u60ca\u9b42\u672a\u5b9a\u8eab\u5b50\u8fd8\u5728\u53d1\u6296\u3002 \u3000\u3000\u90ed\u5927\u8def\u51b2\u5979\u6324\u4e86\u6324\u773c\xb7\u7b11\u9053\u201c\u8fd9\u4eba\u6570\u8d1f\u4e86\u4f60\u6211\u4eec\u628a\u4ed6\u5bb0\u4e86\u5582\u72d7\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u60ca\u547c\u58f0\uff0c\u5413\u5f97\u4eba\u90fd\u8981\u6655\u4e01\u8fc7\u53bb\uff0c\u4e00\u4e0b\u5b50\u5012\u5728\u6797\u592a\u5e73\u8eab \u3000\u3000\u90ed\u5927\u8def\u5927\u7b11\uff0c\u9053\uff0c\u201c\u6211\u6708\u4e0d\u8fc7\u662f\u8bf4\u73a9\u7684\uff0c\u8c61\u8fd9\u79cd\u81ed\u5c0f\u65bc\u8fde\u91ce\u72d7\u90fd\u4e0d\u80af\u55c5\u55c5\u7684\u3002\u201d\u4ed6\u6325 \u624b\xb7\u8d50\u9053\u201c\u6eda\u5427\uff0c\u6eda\u5f97\u8d8a\u5feb\u8d8a\u597d\xb7\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d \u3000\u3000\u7528\u4e0d\u4ed6\u8bf4\u8fd9\u5927\u6c49\u65e9\u5df2\u8fde\u6eda\u5e26\u722c\u7684\u8dd1\u4e86\u3002 \u3000\u3000\u5c0f\u59a8\u5974\u8fd9\u65f6\u624d\u677e\u4e86\u53e3\u5927\u6c14\uff0c\u7ea2\u80a1\u7ad9\u4e86\u8d77\u6765\u76c8\u76c8\u62dc\u5012\u9053\u201c\u591a\u8c22\u8fd9\u4f4d\u516c\u4e8e\u76f8\u6551\uff0c\u5426\u5219\u2026\u2500 \u2018\u5426\u5219\u2026\u3002\u201c \u3000\u3000\u5979\u773c\u5708\u53c8\u5f00\u59cb\u53d1\u7ea2\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\uff0c\u8c61\u662f\u6068\u4e0d\u5f97\u62b1\u4f50\u6797\u592a\u5e73\u7684\u811a\u6765\u8868\u793a\u81ea\u5df1\u5fc3\u91cc\u6709\u591a \u9ebd\u611f\u6fc0\u3002 \u3000\u3000\u6797\u592a\u5e73\u7684\u8138\u4e5f\u7ea2\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6551\u4f60\u7684\u53c8\u4e0d\u662f\u8fd9\u4f0d\u516c\u5b50\u4e00\u4e2a\u4eba\uff0c\u6211\u4e5f\u6709\u4efd\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u6765\u8c22\u8c22\u6211\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u59d0\u7684\u8138\u66f4\u7ea2\uff0c\u66f4\u4e0d\u77e5\u9053\u5e94\u8be5\u641e\u9ebd\u529e\u624d\u597d\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u65f6\u9890\u4e03\u5df2\u9f13\u6765\xb7\u77aa\u90ed\u5927\u8def\uff0c\u9053\u201c\u4eba\u5bb6\u5df2\u7ecf\u53d7\u4e86\u7f6a\uff0c\u4f60\u8fd8\u8981\u6b3a\u8d1f\u5979\uff1f\u201d \u3000\u3000\u4ed6\u5c06\u8fd9\u5c0f\u59cb\u5a18\u4ece\u5730\u4e0a\u62c9\u8d77\u6765\uff0c\u53c8\u9053\u201c\u4ed6\u8fd9\u4eba\u4e5f\u6709\u70b9\u6bdb\u75c5\xb7\u4f60\u7528\u4e0d\u7406\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u5782\u5934\xb7\u9053\u201c\u591a\u2026\u2026\u591a\u8c22\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u4e2a\u5c0f\u59d1\u5a18\u5bb6\u600e\u9ebd\u4f1a\u8ddf\u90a3\u79cd\u4eba\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u5462\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u5934\u5782\u5f97\u66f4\u4f4e\u5438\u54fa\u9053\u201c\u6211\u662f\u4e2a\u5356\u82b1\u7684\xb7\u4ed6\u8bf4\u8fd9\u5730\u65b9\u6709\u4eba\u8981\u628a\u6211\u8fd9\u4e00\u7bee\u5b50\u82b1\u90fd\u4e70\u4e0b \u6765\xb7\u6240\u4ee5\u2026\u2026\u6240\u4ee5\u6211\u5c31\u8ddf\u4ed6\u6765\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u53f9\u4e86\u66f0\u6c14\xb7\u9053\u201c\u8fd9\u4e16\u535c\u7537\u4eba\u574f\u7684\u6bd4\u597d\u7684\u591a\u4e0b\u6b21\u4f60\u5343\u4e07\u8981\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u5f00\u53e3\u95ee\u9053\u201c\u4f60\u8fd9\u7bee\u5b50\u82b1\uff0c\u5171\u503c\u591a\u5c11\u94b1\uff1f\u201d \u3000\u3000\u5356\u82b1\u7ad9\u5a18\u9053\u201c\u53c1\u3002\u4e00\u201c\u53c1\u201c\u2606\u201c\u201c \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u597d\u6211\u5c31\u7ed9\u4f60\u53c1\u94f6\u5b50\xb7\u8fd9\u7bee\u82b1\u6211\u5168\u4e70\u4e0b\u6765\u3002\u201d \u3000\u3000\u5356\u82b1\u5973\u62a2\u8d77\u5934\uff0c\u770b\u4ed6\u6e29\u67d4\u7684\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u611f\u6fc0\u3002 \u3000\u3000\u6797\u592a\u5e73\u5374\u53c8\u7ea2\u8138\xb7\u94ae\u8fc7\u5934\u5de6\u53cd\u800c\u597d\u50cf\u4e0d\u6562\u9762\u5bf9\u5979\u3002 \u3000\u3000\u7ff1\u5927\u8def\u770b\u770b\u6797\u592a\u5e73\u53c8\u770b\u770b\u8fd9\u5356\u82b1\u4ea5\u5ffd\u7136\u95ee\u9053\u201c\u5c0f\u59d1\u5a18\u4f60\u8d35 \u3000\u3000\u5356\u82b1\u59d1\u5a49\u5374\u597d\u50cf\u5f88\u6015\u4ed6\u7684\u6837\u5b50\u4ed6\u5f00\u53e3\xb7\u8fd9\u5c0f\u59a8\u5a18\u5c31\u5413\u5f97\u9000\u5382\u4e24\u6b65\u3002 \u3000\u3000\u90ed\u5927\u8def\u8c0a\u201c\u4f60\u662f\u4e0d\u662f\u4f50\u5728\u5c71\u4e0b\uff1f\u662f\u4e0d\u662f\u6700\u8fd1\u624d\u642c\u6765\u7684\uff1f\u6211\u4ee5\u524d\u600e\u9ebd\u6ca1\u89c1\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u5356\u82b1\u59d1\u5a18\u7ea2\u8138\uff0c\u5782\u5934\uff0c\u54ac\u8e72\u5507\uff0c\u2018\u53e5\u8bdd\u4e5f\u672f\u8bf4\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u7b11\u9053\u201c\u4f60\u600e\u9ebd\u4e0d\u8bf4\u8bdd\u5440\uff1f\u96be\u9053\u662f\u4e2a\u54d1\u5df4\uff1f\u201d \u3000\u3000\u5356\u82b1\u59d1\u5a18\u8c61\u662f\u60f3\u8bf4\u4ec0\u9ebd\xb7\u4f46\u8fd8\u662f\u4ec0\u9ebd\u90fd\u6ca1\u8bf4\xb7\u5ffd\u7136\u626d\u5934\u5c31\u8dd1\u3002 \u3000\u3000\u53ea\u89c1\u5979\u4e24\u6761\u5927\u8fab\u5b50\u5728\u80cc\u5f8c\u7529\u6765\u7529\u53bb\u8dd1\u51fa\u53bb\u5f88\u8fdc\u5ffd\u53c8\u56de\u8fc7\u5934\u6765\xb7\u8198\u4e86\u6797\u592a\u5e73\u773c\u628a\u7bee\u5b50\u91cc\u7684 \u82b1\u5168\u90fd\u62ff\u51fa\u6765\xb7\u653e\u5728\u5730\u4e0a\uff0c\u9053\u201c\u8fd9\u4e9b\u82b1\u5168\u90fd\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u8138\u66f4\u7ea2\u8dea\u5f97\u66f4\u4fa0\u597d\u50cf\u751f\u6015\u522b\u4eba\u4f1a\u8ffd\u8fc7\u53bb\u4f3c\u56db\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u8fd9\u5c0f\u59cb\u5a18\u80c6\u5b50\u771f\u5c0f\u3002\u201d\u71d5\u4e03\u51b7\u51b7\u9053\u201c\u770b\u89c1\u4f60\u90a3\u526f\u7a77\u51f6\u6781\u6076\u7684\u6837\u5343\u80c6\u4e8e\u518d\u5927 \u7684\u5973\u4eba\u4e5f\u6837\u4f1a\u88ab\u4f60\u5413\u8dd1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u53ea\u5c0f\u8fc7\u95ee\u4e86\u5979\u4e24\u53e5\u8bdd\u800c\u5df2\u53c8\u6ca1\u6709\u600e\u9ebd\u6837\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4eba\u5bb6\u59d3\u4ec0\u9ebd\u53eb\u4ec0\u9ebd\u516b\u5728\u4ec0\u9ebd\u5730\u65b9\u53c8\u6b20\u4f60\u4ec0\u9ebd\u4e8b\uff1f\u4f60\u6709\u4ec0\u9ebd\u597d\u95ee\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u53c8\u4e0d\u662f\u81ea\u5df1\u8981\u95ee\u3002\u201d\u9893\u4e03\u9053\u201c\u4f60\u66ff\u51c6\u95ee\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5411\u6797\u592a\u5e73\u5496\u4e86\u54d1\u665a\uff0c\u7b11\u9053\u201c\u4f60\u96be\u9053\u6ca1\u770b\u89c1\u6211\u4eec\u8fd9\u4f4d\u591a\u60c5 \u3000\u3000\u6797\u592a\u5e73\u597d\u50cf\u6839\u672c\u6ca1\u542c\u89c1\u4ed6\u5728\u8bf4\u4ec0\u9ebd\xb7\u773c\u775b\u8fd8\u76ef\u5728\u5c0f\u59a8\u59ec\u8eab\u5f71\u6d88\u5931\u7684\u5730\u65b9\u7adf\u4f3c\u6709\u4e9b\u75f4\u4e86\u3002 \u3000\u3000\u66ff\u5927\u8fd8\u6ca1\u6709\u53bb\u8fdc\u65e9\u4e0a\u2606\u7684\u98ce\u91cc\u8fd8\u5e26\u6625\u5bd2\u3002 \u3000\u3000\u90ed\u5927\u8def\u63a8\u5f00\u95e8\u6df1\u6df1\u5438\u5382\u53e3\u6c14\u9662\u5b50\u6625\u98ce\u5c31\u4f3c\u5df2\u5168\u90fd\u6251\u4eba\u4ed6\u4ec0\u91cc\u3002 \u3000\u3000\u6bcf\u5929\u8d77\u5f97\u6700\u4e2d\u7684\u4eba\u5b9a\u662f\u4ed6\u56e0\u4e3a\u4ed6\u89c9\u5f97\u5c06\u5927\u597d\u65f6\u5149\u6d88\u78e8\u5728\u5e8a\u4e0a\u5b9e\u5728\u662f\u4ef6\u5f88\u6d6a\u8d39\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4eca\u5929\u4ed6\u63a8\u5f00\u95e8\u7684\u65f6\u5019\xb7\u5374\u53d1\u73b0\u6797\u5927\u5e73\u5df2\u7ecf\u7ad9\u5728\u9662\u5b50\u91cc\u3002 \u3000\u3000\u7ad9\u5728\u9662\u5382\u91cc\u53d1\u6014\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f7b\u8f7b\u54b3\u55fd\u4e86\u51e0\u58f0\u4ed6\u6ca1\u542c\u89c1\u90ed\u5927\u8def\u4e49\u6572\u4e86\u6572\u680f\u79c6\xb7\u4ed6\u4e5f\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u4ed6\u773c\u775b\u76f4\u52fe\u52fe\u7684\u5728\u5899\u89d2\u7684\u4e00\u4ece\u9886\u836f\u4e0a\u2606\u5df3\u91cc\u5374\u4e0d\u77e5\u5728\u60f3\u4ec0\u9ebd\uff1f\u90ed\u5927\u8def\u8f7b\u8f7b\u8d70\u8fc7\u53bb\xb7\u7a81\u7136 \u5927\u58f0\u9053\u201c\u65e9\u3002 \u3000\u3000\u6797\u592a\u5e73\u8fd9\u56de\u7ec8\u4e8e\u542c\u89c1\u4e86\xb7\u540c\u65f6\u4e5f\u5413\u4e86\u4e00\u8df3\u56de\u5934\u770b\u89c1\u90ed\u5927\u8def\u624d\u52c9\u5f3a\u7b11\u9053\u201c\u65e9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u76ef\u4ed6\u7684\u8138\u9053\u201c\u770b\u4f60\u773c\u775b\u7ea2\u7ea2\u7684\u8db3\u4e0d\u662f\u6628\u5929\u665a\u4e0a\u6ca1\u7761\u597d\u201d \u3000\u3000\u6797\u592a\u5e73\u652f\u543e\u9053\u201c\u662d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u9053\u201c\u4f60\u770b\u6765\u597d\u50cf\u6709\u70b9\u5fc3\u4e8b\u7a76\u7adf\u5728\u60f3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u5728\u60f3\u2026\u2026\u6625\u5929\u597d\u50cf\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002\u201d\u90ed\u5927\u8def\u70b9\u70b9\u5934\xb7\u9053\u201c\u4e0d\u9519\uff0c\u6625\u5929\u5df2\u7ecf\u8fc7 \u53bb\u4e86\uff0c\u6628\u5929\u521a\u8fc7\u53bb\u7684\uff1b\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6628\u5927\u8fc7\u53bb\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5fae\u7b11\u9053\u201c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u9ebd\uff1f\u6628\u5929\u90a3\u4f4d\u5c0f\u7ad9\u5a18\u8dd1\u8d70\u7684\u65f6\u5019\u6625\u5929\u5c82\u975e\u4e5f\u5df2\u8ddf\u5979\u4e00\u8d77\u8d70 \u4e86\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u8138\u7ea2\u4e86\uff0c\u90ed\u5927\u8def\u6545\u610f\u53f9\u4e86\u53e3\u6c14\u54cd\u95fb\u9053\u201c\u6625\u5929\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u8c01\u77e5\u9053\uff1f\u82e5\u6709\u4eba\u77e5 \u9053\u53bb\u5904\u53c8\u4f55\u59a8\u5524\u53d6\u5f52\u6765\u540c\u4f4f\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7ea2\u8138\u8c0a\u201c\u4f60\u80fd\u4e0d\u80fd\u5c11\u8bf4\u51e0\u65ec\u7f3a\u5fb7\u8bdd\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u8fd9\u8bdd\u96be\u9053\u5c31\u9519\u4e86\u9ebd\uff1f\u4f60\u96be\u9053\u4e0d\u60f3\u5c06\u6625\u5929\u7559\u4f4f\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u201d\u4e00\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u505c\u4f4f\u4e86\u53e3\uff0c\u56e0\u4e3a\u8fd9\u65f6\u6625\u98ce\u5ffd\u7136\u4f20\u6765\u4e86\u4e00\u9635\u60a0\u626c\u7684\u592a\u58f0\u201c\u5c0f\u5c0f\u59d1\u5a18\uff0c\u6e05\u65e9\u8d77\u5e8a\uff0c \u3000\u3000\u63d0\u82b1\u7bee\u513f\u4e0a\u5e02\u573a\u3002 \u3000\u3000\u7a7f\u8fc7\u5927\u8857\uff0c\u8d70\u8fc7\u5c0f\u5df7 \u3000\u3000\u5356\u82b1\u5356\u82b1\uff0c\u58f0\u58f0\u56b7\u3002 \u3000\u3000\u82b1\u513f\u867d\u7f8e\u3001\u82b1\u513f\u867d\u9999\u3001 \u3000\u3000\u6ca1\u6709\u4eba\u4e70\u600e\u9ebd\u6837 \u3000\u3000\u63d0\u82b1\u7bee\u513f\uff0c\u7a7a\u94b1\u888b\u3002 \u3000\u3000\u600e\u9ebd\u56de\u53bb\u89c1\u7239\u5a18\uff1f\u201d \u3000\u3000\u6b4c\u58f0\u53c8\u751c\u53c8\u7f8e\u53c8\u6709\u4e9b\u9178\u9178\u7684\u4e0d\u4f46\u6797\u592a\u5e73\u542c\u75f4\u4e86\xb7\u5c31\u8fde\u90ed\u5927\u8def\u90fd\u5df1\u542c\u5f97\u51fa\u795e\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u4ed6\u624d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\xb7\u54fa\u54fa\u9053\u201c\u770b\u6765\u6625\u5929\u5e76\u6ca1\u6709\u53bb\u8fdc\u73b0\u5728\u53c8\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7528\u529b\u63a8\u6797\u592a\u5e73\uff0c\u7b11\u9053\u201c\u4f60\u8fd8\u4e0d\u51fa\u53bb\u8fd8\u6014\u5728\u8fd9\u91cc\u5e72\u4ec0 \u3000\u3000\u6797\u592a\u5e73\u7ea2\u8138\u9053\u201c\u51fa\u53bb\u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u7728\u4e86\u7728\u773c\u9053\u201c\u4eba\u5bb6\u6628\u5929\u9001\u4e86\u4f60\u90a3\u9ebd\u591a\u82b1\u4eca\u5929\u4f60\u81f3\u5c11\u4e5f\u8be5\u5bf9\u4eba\u5bb6\u8868\u793a\u70b9\u610f\u601d\u5440\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u8fd8\u5728\u72b9\u8c6b\u5370\u7ec8\u4e8e\u8fd8\u662f\u534a\u63a8\u534a\u5c31\u7684\xb7\u88ab\u90ed\u5927\u8def\u63a8\u4e86\u51fa \u3000\u3000\u96fe\u5df2\u6563\u9633\u5149\u6ee1\u5730\u3002 \u3000\u3000\u4e2a\u624b\u63d0\u82b1\u7bee\u7684\u5c0f\u7ad9\u5a18\u3001\u6b63\u8e29\u6ee1\u5730\u9633\u5149\xb7\u9992\u9992\u7684\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u5979\u59cb\u8d77\u5934\xb7\u5ffd\u7136\u770b\u89c1\u6797\u592a\u5e73\uff0c\u6ee1\u5730\u9633\u5149\u5ffd\u7136\u5168\u90fd\u5230\u4e86\u5979\u80a1\u4e0a\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd8\u6709\u534a\u5728\u6797\u592a\u5e73\u8138\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u770b\u4ed6\uff0c\u53c8\u770b\u4e86\u770b\u90a3\u5c0f\u7ad9\u5a18\u6084\u6084\u7684\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u63a9\u4e0a\u95e8\uff0c\u5c06\u4ed6\u4eec\u7559\u5728\u95e8\u5916\u3002 \u3000\u3000\u6625\u98ce\u6e29\u67d4\u7684\u5c31\u8c61\u662f\u60c5\u4eba\u7684\u773c\u6ce2\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fae\u7b11\u5fc3\u91cc\u89c9\u5f97\u6109\u5feb\u6781\u4e86\uff0c\u80cc\u8d1f\u8d77\u53cc\u624b\xb7\u5728\u9662\u5b50\u91cc\u6162\u6398\u7684\u8e6c\u6b65\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5e76\u4e0d\u60f3\u627e\u71d5\u4e03\u53bb\u7684\xb7\u4f46\u6307\u8d77\u5934\u6765\u65f6\xb7\u5ffd\u7136\u53d1\u89c9\u5df2\u5230\u4e86\u71d5\u4e03\u95e8\u5916\u3002 \u3000\u3000\u5982\u6b64\u7f8e\u7684\u6625\u5149\uff0c\u600e\u80fd\u4e0d\u8ba9\u670b\u53cb\u6765\u540c\u4e8b\uff1f \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u4f38\u51fa\u624b\u8f7b\u8f7b\u7684\u6572\u95e8\u3002 \u3000\u3000\u6c72\u6709\u56de\u531d\u3002 \u3000\u3000\u6572\u95e8\u58f0\u518d\u5927\uff0c\u8fd8\u662f\u6ca1\u6709\u56de\u5e94\u3002 \u3000\u3000\u9893\u4e03\u600e\u4f1a\u7761\u5f97\u8fd9\u9ebd\u6b7b\uff1f \u3000\u3000\u90ed\u5927\u8def\u5927\u58f0\u5524\u9053\u201c\u592a\u9633\u5df2\u7ecf\u6652\u5728\u5934\u4e0a\u4e86\uff0c\u8fd8\u4e0d\u8d77\u6765\u201d \u3000\u3000\u95e8\u91cc\u9759\u6084\u6084\u7684\xb7\u70b9\u58f0\u97f3\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u80cc\u5f8c\u5374\u6709\u4e86\u58f0\u97f3\xb7\u662f\u738b\u52a8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ed6\u4e0d\u5728\u5f8c\u9762\u9662\u5b50\uff0c\u4e5f\u4e0d\u5728\u53a8\u623f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u8138\u8272\u5df2\u6709\u4e9b\u53d8\u4e86\u5fcd\u4e0d\u4f4f\u7528\u529b\u53bb\u63a8\u95e8\u3002 \u3000\u3000\u95e8\u6839\u672c\u662f\u865a\u63a9\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u63a8\u5f00\u95e8\u4e00\u9662\u5b50\u6625\u5149\u597d\u50cf\u90fd\u5df2\u88ab\u4ed6\u63a8\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c4b\u4e88\u91cc\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u5e8a\u4e0a\u7684\u88ab\u8925\uff0c\u8fd8\u6574\u6574\u9f50\u9f50\u7684\u53e0\u5728\u90a3\u91cc\uff0c\u9664\u6b64\u4e4b\u5916\u5c31\u6ca1\u6709\u522b\u7684\u3002 \u3000\u3000\u975e\u4f46\u71d5\u4e03\u7684\u4eba\u4e0d\u5728\u5c4b\u4e8e\u91cc\u4ed6\u7684\u4e00\u4e9b\u96f6\u661f\u4e1c\u897f\u4e5f\u5168\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ad9\u5728\u90a3\u91cc\u624b\u811a\u51b0\u51b7\u3002 \u3000\u3000\u738b\u52a8\u7684\u7709\u4e5f\u76b1\u4e86\u8d77\u6765\u8d2d\u54cd\u9053\u201c\u770b\u6837\u5b50\u4ed6\u597d\u50cf\u662f\u6628\u5929\u665a\u4e0a\u8d70\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd9\u6b21\u4ed6\u4e3a\u4ec0\u9ebd\u628a\u4e1c\u897f\u4e5f\u5e26\u8d70\u4e86\u5462\uff1f\u4e3a\u4ec0\u9ebd\u8fde\u53e5\u8bdd\u90fd\u6ca1\u6709\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u8f6c\u8eab\u7528\u529b\u6293\u4f4f\u4e86\u738b\u52a8\u7684\u80a9\uff0c\u9053\u201c\u6628\u5929\u665a\u4e0a\u4f60\u6709\u6ca1\u6709\u544a\u8bc9\u4ed6\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u60f3\u6211\u4f1a\u544a\u8bc9\u4ed6\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8ddf\u4f60\u8bf4\u7684\u90a3\u4e9b\u8bdd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u4f60\u4ee5\u4e3a\u6211\u662f\u90a3\u79cd\u5165\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u771f\u7684\u4ec0\u9ebd\u90fd\u6ca1\u6709\u8bf4\u201d \u3000\u3000\u738b\u52a8\u53f9\u4e86\u53e3\u6c14\u63a5\u9053\u201c\u73b0\u5728\u6211\u4eec\u5df2\u7528\u4e0d\u5435\u67b6\u4e86\u5426\u5219\u5c31\u51ed\u8fd9\u53e5\u8bdd\u6211\u5df2\u7ecf\u53ef\u4ee5\u8ddf\u4f60\u5435\u8d77\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u9611\u7ec8\u4e8e\u4e5f\u957f\u53f9\u4e86\u53e3\u6c14\u9992\u6162\u7684\u677e\u5f00\u624b\u3002 \u3000\u3000\u738b\u52a8\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u5176\u5b9e\u4f60\u4e5f\u7528\u4e0d\u6025\u4ee5\u524d\u4ed6\u4e5f\u6e9c\u51fa\u53bb\u8fc7\xb7\u8fc7\u51e0\u5929\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u636e\u5934\xb7\u82e6\u7b11\u9053\u201c\u4f60\u81ea\u5df1\u521a\u4e5f\u8bf4\u8fc7\uff0c\u8fd9\u6b21\u4e0d\u540c\u3002\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u53ef\u662f\u4ed6\u6839\u672c\u6ca1\u6709\u539f\u56e0\u800d\u4e0d\u8f9e\u800c\u522b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4f4e\u4e0b\u5934\xb7\u9053\u201c\u4e5f\u8bb8\u2026\u3002\u2019\u4e5f\u8bb8\u4ed6\u4e5f\u613f\u6211\u4e00\u6837\uff0c\u4e5f\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\u4e86\u6240\u4ee5\u201c\u4e00\u201c\u6240 \u4ee5\uff0c\u8fd8\u662f\u4e0d\u5982\u8d70\u5382\u7684\u597d\u3002\u201d \u3000\u3000\u738b\u52a8\u72b9\u8c6b\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u4eec\u6839\u672c\u4e5f\u5e76\u6ca1\u6709\u4ec0\u9ebd\u4e0d\u5bf9\u52b2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u8fd8\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u52a8\u8c0a\u201c\u5176\u5b9e\u4ed6\u2026\u2026\u4ed6\u2026\u3002\u2606 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u600e\u9ebd\u6837\uff1f\u201d \u3000\u3000\u738b\u52a8\u51dd\u89c6\u4ed6\u8fc7\u4e86\u534a\u664c\uff0c\u5ffd\u53c8\u6447\u4e86\u6447\u5934\xb7\u9053\u201c\u6ca1\u600e\u9ebd\u6837\uff0c\u6ca1\u600e\u9ebd\u6837\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u8bf4\u5b8c\u8bdd\u5c31\u6389\u5934\u8d70\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u53bb\u627e\u676f\u9152\u559d\u559d\u3002\u201d \u3000\u3000\u5176\u5b9e\u738b\u52a8\u4e5f\u5e76\u4e0d\u662f\u4e2a\u80fd\u5c06\u8bdd\u85cf\u5728\u5fc3\u91cc\u7684\u4eba\u53ea\u4e0d\u8fc7\u89c9\u5f97\xb7\u6709\u4e9b\u8bdd\u8fd8\u662f\u4e0d\u8981\u8bf4\u51fa\u6765\u7684\u597d\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u89c9\u5f97\xb7\u6709\u4e9b\u4e8b\u90ed\u5927\u8def\u4e5f\u662f\u4e0d\u77e5\u9053\u7684\u597d\u77e5\u9053\u5f97\u591a\u4e86\u53cd\u800c\u66f4\u70e6\u607c\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4e0d\u77e5\u9053\u4e5f\u540c\u6837\u70e6\u607c\u3002 \u3000\u3000\u73b0\u5728\u6625\u5929\u624d\u771f\u7684\u53bb\u8fdc\u5382\u3002 \u3000\u3000\u6625\u53bb\u4f55\u5904\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\u9053 \u3000\u3000\u201c\u5c0f\u5c0f\u59a8\u5a18\u6e05\u65e9\u8d77\u5e8a\u3002 \u3000\u3000\u63d0\u82b1\u7bee\u513f\uff0c\u4e0a\u5e02\u573a\u2026\u2026\u201d \u3000\u3000\u751c\u7f8e\u7684\u6b4c\u58f0\xb7\u6bcf\u5929\u6e05\u6668\u90fd\u80fd\u542c\u5f97\u5230\u3002 \u3000\u3000\u53ea\u8981\u542c\u5230\u8fd9\u6b4c\u58f0\u6797\u592a\u5e73\u5c31\u89c9\u5f97\u6625\u5929\u5df2\u56de\u6765\u5382\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u7684\u6625\u5929\u5374\u5df4\u53bb\u4e0d\u8fd4\u3002 \u3000\u3000\u71d5\u4e03\u7684\u4eba\u4e5f\u548c\u6625\u98ce\u6837\xb7\u53bb\u5c31\u65e0\u8e2a\u5f71\uff0c\u4e00\u53bb\u5c31\u65e0\u6d88\u606f\u3002 \u3000\u3000\u201c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4e3a\u4ec0\u9ebd\u53e5\u8bdd\u90fd\u4e0d\u7559\u4e0b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u51b3\u5fc3\u8981\u5c06\u8fd9\u539f\u56e0\u62e2\u51fa\u6765\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e5f\u8d70\u4e86\u3002 \u3000\u3000\u8d70\u7684\u65f6\u5019\u53ea\u7559\u4e0b\u4e86\u4e00\u53e5\u8bdd\u201c\u4e0d\u627e\u5230\u4ed6\uff0c\u6211\u7edd\u4e0d\u56de\u6765\u201d \u3000\u3000\u5bcc\u8d35\u5c71\u5e84\u4e2d\u7684\u7b11\u58f0\u5c11\u4e86\u5929\u6c14\u867d\u4e00\u5929\u6bd4\u5929\u70ed\u4f46\u5728\u738b\u52a8\u7684\u611f\u89c9\u4e2d\u8fd9\u5730\u65b9\u5374\u4f3c\u5929\u6bd4\u4e00\u5929\u51b7\u3002 \u3000\u3000\u6ca1\u6709\u90ed\u5927\u8def\u7684\u6d88\u606f\xb7\u6ca1\u6709\u71d5\u4e03\u7684\u6d88\u606f\xb7\u4e5f\u6ca1\u6709\u6625\u5929\u7684\u6d88\u606f\u3002 \u3000\u3000\u53ea\u6709\u90a3\u751c\u7f8e\u7684\u6b4c\u58f0\u8fd8\u662f\u6bcf\u5929\u90fd\u53ef\u4ee5\u542c\u5230\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\u552f\u4e00\u4ee4\u4eba\u7a0d\u89c9\u5077\u5feb\u7684\u5c31\u662f\u7ea2\u5a18\u5b50\u7684\u4f24\u4e5f\u5df2\u75ca\u6108\u3002 \u3000\u3000\u6709\u5929\u5979\u548c\u6797\u592a\u5e73\u966a\u738b\u52a8\u5750\u5728\u5c4b\u64c5\u535c\u3002 \u3000\u3000\u82cd\u5f2f\u672c\u6765\u78a7\u52a0\u6d17\u4f46\u5ffd\u7136\u95f4\uff0c\u4e4c\u4e91\u5df2\u8fde\u5929\u9762\u8d77\u3002 \u3000\u3000\u63a5\u590f\u65e5\u7684\u96f7\u96e8\u5c31\u5df2\u503e\u76c6\u800c\u843d\u3002 \u3000\u3000\u96e8\u6c34\u91cd\u822c\u4ece\u5c4b\u642a\u4e0a\u5012\u6302\u800c\u4e0b\uff0c\u5899\u89d2\u7684\u6b8b\u82b1\u4e5f\u5df2\u4e0d\u77e5\u88ab\u96e8\u6c34\u51b2\u5411\u4f55\u5904\u3002 \u3000\u3000\u738b\u52a8\u770b\u636e\u4e0a\u7684\u96e8\u5ffd\u7136\u957f\u53f9\u4e86\u58f0\xb7\u54fa\u54fa\u9053\u201c\u6625\u5929\u771f\u7684\u5df2\u7ecf\u8fc7\u53bb\u5382\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u67d4\u58f0\u9053\u201c\u73b0\u5728\u867d\u5df2\u8fc7\u53bb\u4e86\uff0c\u4f46\u5f88\u5feb\u5c31\u4f1a\u518d\u6765\u7684\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6728\u9519\u6625\u5929\u65e0\u8bba\u53bb\u5f97\u591a\u8fdc\uff0c\u90fd\u5b9a\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u5b9a\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5b9a\u201d \u3000\u3000\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba \u7b2c\u5341\u516b\u7ae0\u3000\u96f7\u3000\u96e8>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u96f7\u3000\u96e8 \u3000\u3000\u96e8\u70b9\u4e71\u77f3\u822c\u6253\u5728\u90ed\u5927\u8def\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u9192\u5382\u3002 \u3000\u3000\u964b\u5df7\u3001\u4f4e\u5899\u4ed6\u9192\u6765\u624d\u53d1\u89c9\u81ea\u5df1\u7761\u5728\u5899\u89d2\u7684\u6ce5\u6cde\u4e2d\xb7\u81f3\u65bc\u4ed6\u662f\u600e\u9ebd\u4f1a\u7761\u5728\u8fd9\u91cc\u7684\uff1f\u5df2\u7761\u4e86 \u591a\u4e45\uff1f\u8fd9\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u53ea\u8bb0\u5f97\u6628\u591c\u5148\u8ddf\u4e1c\u57ce\u7684\u5144\u5f1f\u4eec\u8d77\u53bb\u8e66\u897f\u57ce\u8001\u5927\u7684\u8d4c\u573a\u6253\u5f97\u90a3\u91cc\u9e21\u98de\u72d7\u8df3\u584c\u6eaf\u5f90\u3002 \u3000\u3000\u7136\u5f8c\u4e1c\u57ce\u7684\u8001\u5927\u5c31\u7279\u5730\u4e3a\u4ed6\u5728\u5c0f\u51ac\u74dc\u7684\u5993\u9662\u91cc\u5927\u6446\u5e86\u529f\u5bb4\u4e8c\u53c1\u5341\u4e2a\u5f1f\u5144\u8f6e\u6d41\u649e\u4ed6\u7684\u9152\u3002 \u3000\u3000\u4e1c\u57ce\u8001\u5927\u8fd8\u5f53\u4f17\u62cd\u80f8\u812f\u8868\u793a\u53ea\u8981\u4ed6\u80fd\u628a\u897f\u57ce\u90a3\u5e2e\u6253\u57ae\u4ee5\u5f8c\u897f\u57ce\u90a3\u8fb9\u7684\u5730\u76d8\u5c31\u5f52\u4ed6\u5f8c\u6765\u4e24 \u4e2a\u4eba\u597d\u50cf\u8fd8\u78d5\u5934\u62dc\u4e86\u628a\u5b50\u3002 \u3000\u3000\u518d\u5f8c\u9762\u7684\u4e8b\u4ed6\u5c31\u66f4\u8bb0\u4e0d\u6e05\u4e86\u597d\u50cf\u662f\u5c0f\u51ac\u74dc\u7684\u59b9\u59b9\u5c0f\u871c\u6843\u628a\u4ed6\u6276\u56de\u53bb\u7684\u6b63\u5728\u66ff\u4ed6\u8131\u9774\u5b50\u8131 \u8863\u88f3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5ffd\u7136\u5374\u4e0d\u80af\u4e00\u5b9a\u8981\u8d70\u8981\u51fa\u53bb\u627e\u71d5\u4e03\u3002 \u3000\u3000\u5c0f\u871c\u6843\u60f3\u62c9\u4ed6\u53cd\u800c\u6328\u4e86\u4e2a\u8033\u522e\u5b50\u3002 \u3000\u3000\u7136\u5f8c\u4ed6\u5c31\u53d1\u73b0\u81ea\u5df2\u7ef8\u5728\u8fd9\u91cc\u4e2d\u95f4\u90a3\u4e00\u5927\u6bb5\u5b8c\u5168\u53d8\u6210\u4e86\u7a7a\u81ea\u3002 \u3000\u3000\u4e25\u683c\u8bf4\u6765\uff0c\u8fd9\u534a\u4e2a\u591a\u6708\u7684\u65e5\u5b50\xb7\u7a76\u7adf\u662f\u600e\u9ebd\u8fc7\u7684\uff0c\u4ed6\u4e5f\u5f04\u4e0d\u6e05\u3002 \u3000\u3000\u4ed6\u672c\u662f\u51fa\u6765\u627e\u71d5\u4e03\u7684\uff0c\u4f46\u4eba\u6d77\u832b\u832b\xb7\u53c8\u5230\u9890\u91cc\u53bb\u627e\u5462\uff1f \u3000\u3000\u6240\u4ee5\u4ed6\u5230\u4e86\u8fd9\u91cc\u5f8c\u5c31\u7d22\u6027\u7559\u4e86\u4e0b\u6765\u6bcf\u5929\u72c2\u6f02\u4e71\u9189\u3002\u6709\u5929\u5927\u9189\u5f8c\uff0c\u548c\u4e1c\u57ce\u7684\u8001\u5927\u51b2\u7a81\u4e86\u8d77 \u6765\u4e24\u4eba\u4e0d\u6253\u4e0d\u76f8\u8bc6\u8fd9\u6253\uff0c\u7adf\u6210\u4e86\u670b\u53cb\u3002 \u3000\u3000\u90a3\u65f6\u4e1c\u57ce\u8001\u5927\u6b63\u88ab\u897f\u57ce\u5e2e\u538b\u5f97\u900f\u4e0d\u8fc7\u6c14\uff0c\u90ed\u5927\u8def\u5c31\u62cd\u80f8\u812f\uff0c\u4fdd\u8bc1\u4e3a\u4ed6\u51fa\u6c14\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5c31\u7530\u4e1c\u57ce\u7684\u5f1f\u5144\u4eec\u6df7\u5728\u8d77\u4e86\u3002\u6bcf\u5929\u559d\u9152\u3001\u8d4c\u94b1\u3001\u6253\u67b6\u3001 \u3000\u3000\u4f46\u4e3a\u4ec0\u9ebd\u6bcf\u6b21\u5927\u9189\u5f8c\u4ed6\u90fd\u8981\u4e00\u4e2a\u4eba\u6e9c\u8d70\uff0c\u7b2c\u4e8c\u5929\u9192\u6765\u65f6\xb7\u4e0d\u662f\u5012\u5728\u8def\u4e0a\u5c31\u662f\u8eba\u5728\u9634\u6c9f \u91cc\uff1f \u3000\u3000\u4e2a\u4eba\u82e5\u8981\u6298\u78e8\u522b\u4eba\u4e5f\u8bb8\u5f88\u96be\u4f46\u82e5\u8981\u6298\u78e8\u81ea\u5df1\u5c31\u5f88\u5bb9\u6613 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u5728\u6545\u610f\u62c6\u78e8\u81ea\u5df1\uff1f \u3000\u3000\u597d\u5927\u7684\u96e8\uff0c\u96e8\u70b9\u6253\u5728\u4eba\u8eab\u4e0a\uff0c\u5c31\u597d\u50cf\u77f3\u5b50\u822c\u3002 \u3000\u3000\u90ed\u5927\u8def\u6323\u624e\xb7\u52c9\u5f3a\u7ad9\u8d77\u6765\uff0c\u5934\u75bc\u5f97\u4eff\u4f5b\u968f\u65f6\u90fd\u4f1a\u88c2\u5f00\u6765\u820c\u5934\u4e0a\u4e5f\u8c61\u662f\u957f\u51fa\u4e86\u5c42\u539a\u539a\u7684\u9752 \u82d4\u3002 \u3000\u3000\u8fd9\u79cd\u65e5\u4e8e\u8fc7\u5f97\u771f\u7684\u6709\u610f\u601d\u5417\uff1f \u3000\u3000\u4ed6\u4e0d\u613f\u60f3\u3002 \u3000\u3000\u4ed6\u4ec0\u9ebd\u4e8b\u90fd\u4e0d\u613f\u60f3\xb7\u5458\u597d\u7acb\u523b\u6709\u9152\xb7\u518d\u5f00\u59cb\u559d\xb7\u6700\u597d\u6bcf\u5929\u90fd\u6ca1\u6709\u6e05\u9192\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ef0\u8d77\u8180\u5b50\u60f3\u63a5\u51e0\u53e3\u96e8\u6c34\u6765\u559d\uff0c\u96e8\u70b9\u867d\u7136\u5f88\u591a\u5f88\u5bc6\uff0c\u80fd\u843d\u5230\u5730\u5634\u91cc\u7684\uff0c\u5374\u504f\u504f\u6ca1\u6709\u591a\u5c11\u3002 \u3000\u3000\u4e16\u4e0a\u5c82\u975e\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff1f \u3000\u3000\u4f60\u770b\u660e\u660e\u53ef\u4ee5\u5f97\u5230\u7684\u5374\u504f\u504f\u5f97\u4e0d\u5230\u3002\u4f60\u6124\u6012\u3001\u75db\u82e6\xb7\u7528\u81ea\u5df1\u7684\u5934\u53bb\u6a80\u5899\xb7\u628a\u81ea\u5df1\u6298\u78e8\u5f97 \u4e0d\u6210\u4eba\u5f62\uff0c\u5374\u8fd8\u662f\u70b9\u7528\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u7528\u529b\u633a\u8d77\u4e86\u80f8\u819b\u80f8\u819b\u91cc\u5fc3\u53e3\u4e0a\xb7\u5c31\u8c61\u662f\u6709\u9488\u5728\u523a\u3002 \u3000\u3000\u660e\u660e\u4e0d\u8be5\u60f3\u7684\u4e8b\xb7\u4e3a\u4ec0\u9ebd\u504f\u504f\u53c8\u8981\u60f3\u5462\uff1f \u3000\u3000\u5f3a\u96f7\u58f0\uff0c\u95ea\u7535\u51fb\u4e0b\u3002 \u3000\u3000\u4ed6\u54ac\u4e86\u54ac\u7259\u5927\u6b65\u5411\u524d\u8d70\uff0c\u521a\u8d70\u4e86\u4e24\u6b65\uff0c\u5ffd\u7136\u770b\u5230\u524d\u9762\u4e00\u6247\u5c0f\u95e8\u201c\u5440\u201d\u7684\u4e00\u58f0\u5f00\u4e86\u3002 \u3000\u3000\u4e2a\u6392\u8863\u5782\u7559\u7684\u5c0f\u4e2b\u5934\u624b\u91cc\u6491\u628a\u82b1\u6cb9\u4f1e\u6b63\u7ad9\u5728\u95e8\u53e3\xb7\u770b\u4ed6\u76c8\u76c8\u7684\u7b11\uff0c\u7b11\u8d77\u6765\u4e24\u4e2a\u9152\u7a9d\u597d \u6df1\u3002 \u3000\u3000\u6709\u4e2a\u8fd9\u9ebd\u751c\u7684\u5c0f\u59a8\u70ae\xb7\u5bf9\u4f60\u7b11\xb7\u4efb\u4f55\u7537\u4eba\u90fd\u514d\u4e0d\u4e86\u8981\u4e0a\u53bb\u642d\u6c55\u642d\u6c55\u7684\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u73b0\u5728\u5374\u6ca1\u6709\u8fd9\u79cd\u5fc3\u60c5\uff0c\u4ed6\u73b0\u5728\u7684\u5fc3\u60c5\xb7\u7b80\u76f4\u6bd4\u4ed6\u7684\u6837\u5b50\u8fd8\u7cdf\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u8fce\u4e86\u4e0a\u6765\xb7\u751c\u751c\u7684\u7b11\u9053\u201c\u6211\u53eb\u5fc3\u5fc3\u3002\u201d \u3000\u3000\u5979\u4e0d\u7b49\u522b\u4eba\u5f00\u53e3\u7b2c\u4e00\u53e5\u8bdd\u5c31\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u540d\u5b57\u8fd9\u79cd\u4e8b\u5012\u4e5f\u5c11\u89c1\u5f97\u5f88\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u5979\u4e24\u773c\uff0c\u6162\u4fb5\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\u201c\u5fc3\u5fc3\u597d\u597d\u952f\u4e2d\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u8bdd\u8bf4\u5b8c\uff0c\u53c8\u60f3\u8d70\u5382\u3002 \u3000\u3000\u8c01\u77e5\u5fc3\u5fc3\u5374\u8fd8\u662f\u4e0d\u80af\u653e\u8fc7\u4ed6\xb7\u53c8\u7b11\u9053\u201c\u6211\u8ba4\u5f97\u4f60\u3002\u201d\u90ed\u5927\u8def\u8fd9\u624d\u89c9\u5f97\u6709\u70b9\u5947\u602a\u8f6c\u8fc7\u8eab\u505c \u4e0b\u6765\u9053\u201c\u4f60\u8ba4\u5f97\u6211\uff1f\u201d \u3000\u3000\u5fc3\u5fc3\u7728\u773c\xb7\u9053\u201c\u4f60\u662f\u4e0d\u662f\u90ed\u5bb6\u7684\u5927\u5c11\u7237\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u66f4\u5947\u602a\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u4ee5\u524d\u5728\u54ea\u91cc\u89c1\u8fc7\u6211\uff1f\u201d \u3000\u3000\u5fc3\u5fc3\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u90a3\u672b\u4f60\u600e\u9ebd\u8ba4\u5f97\u6211\u7684\uff1f\u201d\u5fc3\u5fc3\u8d61\u7136\u9053\u201c\u4f60\u53bb\u95ee\u95ee\u6211\u4eec\u5bb6\u5c0f\u59d0\u5c31\u77e5\u9053\u3002\u201d\u90ed\u5927 \u8def\u9053\u201c\u4f60\u4eec\u5bb6\u7684\u5c0f\u59d0\u662f\u8c01\uff1f\u201d \u3000\u3000\u5fc3\u5fc3\u9053\u201c\u4f60\u770b\u89c1\u5979\u65f6\xb7\u5c31\u7ec6\u9053\u4e86\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u5979\u5728\u54ea\u91cc\uff1f\u201d\u5fc3\u5fc3\u62bf\u5634\u7b11\uff0c\u9053\u201c\u4f60\u8ddf\u6211 \u6765\xb7\u5c31\u4ec0\u9ebd\u4e8b\u5168\u77e5\u9053\u201c\u3002\u201d \u3000\u3000\u5979\u8f6c\u8fc7\u8eab\u8d70\u8fdb\u4e86\u90a3\u6247\u5c0f\u95e8\uff0c\u53c8\u56de\u5934\u5411\u90ed\u5927\u8def\u62db\u4e86\u62db\u624b\u201c\u6765\u5440\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4ec0\u9ebd\u8bdd\u90fd\u6ca1\u6709\u8bf4\u5927\u6b65\u8d70\u4e86\u8fdb\u53bb\u73b0\u5728\u4ed6\u7684\u597d\u5947\u5fc3\u5df2\u88ab\u5f15\u8d77\u4f11\u60f3\u4e0d\u53eb\u4ed6\u8fdb\u53bb\u90fd\u5f88\u96be \u4e86\u3002 \u3000\u3000\u95e8\u91cc\u662f\u4e2a\u5c0f\u5c0f\u7684\u9662\u5b50\u68da\u7d2b\u85e4\u82b1\u5728\u66b4\u96e8\u4e2d\u770b\u6765\u663e\u5f97\u602a\u53ef\u601c\u7684\u3002 \u3000\u3000\u5c4b\u6444\u4e0b\u63a5\u53c1\u4e24\u53ea\u9e1f\u7b3c\xb7\u9ec4\u9a7e\u513f\u6b63\u5728\u7b3c\u5b50\u91cc\u54b3\u54ce\u7684\u5435\u597d\u50cf\u6b63\u5728\u602a\u5979\u4eec\u7684\u4e3b\u4eba\u592a\u4e0d\u4f53\u6064\xb7\u4e3a \u4ec0\u9ebd\u8fd8\u4e0d\u628a\u6211\u8ba2\u5e26\u4eba\u9999\u95fa\u91cc\u3002 \u3000\u3000\u5fc3\u5fc3\u8d70\u4e0a\u7f51\u5ed3\u7528\u4e00\u72e0\u767d\u751f\u751f\u7684\u5c0f\u624b\u6307\uff0c\u8f7b\u8f7b\u5728\u7b3c\u5b50\u4e0a\u5f39\uff0c\u9677\u773c\u9053\u201c\u5c0f\u9b3c\u5435\u6b7b\u4eba\u5382\u4eca\u5929\u5c0f \u59d0\u623f\u91cc\u6709\u5ba2\u4eba\u4f60\u4eec\u518d\u5435\uff0c\u5979\u4e5f\u672f\u4f1a\u8e29\u4f60\u4ecd\u7684\u3002\u201d \u3000\u3000\u5979\u53c8\u56de\u5b75\u95ee\u90ed\u5927\u8def\u7b11\xb7\u5a9a\u7136\u9053\u201c\u4f60\u770b\uff0c\u4f60\u8fd8\u6ca1\u8fdb\u53bb\u5979\u4eec\u5df2\u5728\u5403\u918b\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u53ea\u597d\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u73b0\u5728\u5979\u5fc3\u91cc\u9664\u4e86\u597d\u5947\u4e4b\u5916\uff0c\u53c8\u591a\u4e86\u79cd\u8bf4\u4e0d\u51fa\u662f\u4ec0\u9ebd\u6ecb\u5473\u7684\u611f\u89c9\u4eff\u4f5b\u6709\u70b9\u751c\u9165\u9165\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\u4ed6\u4ecd\u7136\u5982\u5728\u5341\u91cc\u96fe\u4e2d\uff0c\u8fde\u4e00\u70b9\u5f71\u4e88\u90fd\u6478\u4e0d\u3002 \u3000\u3000\u201c\u96be\u9053\u6211\u5ffd\u7136\u4ea4\u4e0a\u59da\u82b1\u8fd0\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u53ea\u4e0d\u8fc7\xb7\u4e2b\u5934\u867d\u7136\u4fcf\u5e76\u4e0d\u4e00\u5b9a\u5c31\u8868\u793a\u5c0f\u59d0\u4e5f\u5f88\u5708\u4eae\u3002 \u3000\u3000\u90a3\u4f0e\u5c0f\u59d0\u82e5\u662f\u6bcd\u591c\u53c9\xb7\u4f60\u8bf4\u600e\u9ebd\u529e\uff1f \u3000\u3000\u95e8\u4e0a\u6302\u7684\u6e58\u7eaa\u7af9\u7684\u5b50\u5f53\u7136\u662f\u5929\u6c14\u5f00\u59cb\u70ed\u4e86\u4e4b\u5f8c\u521a\u6362\u4e0a\u53bb\u7684\u3002 \u3000\u3000\u95e8\u91cc\u68a2\u65e0\u4eba\u58f0\u3002 \u3000\u3000\u5fc3\u5fc3\u6380\u8d77\u5b50\u5a9a\u7136\u9053\u201c\u4f60\u5148\u8bf7\u91cc\u9762\u5750\u6211\u53bb\u8bf7\u5c0f\u59d0\u6765\u3002\u201d \u3000\u3000\u91cc\u9762\u662f\u4e2a\u7cbe\u81f4\u9ad8\u96c5\u7684\u5c0f\u5ba2\u5385\xb7\u5730\u4e0a\u8fd8\u94fa\u539a\u539a\u7684\u6ce2\u65af\u6be1\u3002 \u3000\u3000\u8fde\u90ed\u5927\u8def\u90fd\u4e0d\u7531\u81ea\u4e3b\xb7\u5148\u64e6\u4e86\u64e6\u811a\u5e95\u7684\u6ce5\uff0c\u624d\u80fd\u8d70\u5f97\u8fdb\u53bb\u3002 \u3000\u3000\u201c\u8c61\u8fd9\u79cd\u5730\u65b9\u7684\u4e3b\u4eba\xb7\u4e3a\u4ec0\u9ebd\u8981\u8bf7\u6211\u8fd9\u9ebd\u6837\u4e2a\u5ba2\u4eba\u8fdb\u6765\uff1f\u201d \u3000\u3000\u90a3\u5f53\u7136\u5b9a\u6709\u76ee\u7684\u3002 \u3000\u3000\u4ec0\u9ebd\u76ee\u7684\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u770b\u4e86\u770b\u81ea\u5df1\xb7\u5168\u8eab\u4e0a\u4e0a\u5382\u535c\u8fde\u4e94\u94b1\u94f6\u5b50\u90fd\u4e0d\u503c\u3002 \u3000\u3000\u4ed6\u5bf9\u81ea\u5df1\u7b11\u4e86\u7b11\u7d22\u6027\u627e\u4e86\u5f20\u5458\u8212\u670d\u3001\u6700\u4e7e\u51c0\u7684\u6905\u5b50\u5750\u4e0b\u6765\u3002 \u3000\u3000\u684c\u4e0a\u6709\u58f6\u8336\xb7\u8fd8\u662f\u65b0\u6ce1\u7684\u3002\u51e0\u4e2a\u5c0f\u789f\u5b50\u91cc\uff0c\u6446\u5f88\u7cbe\u7f8e\u7684\u8336\u98df\u3002 \u3000\u3000\u90ed\u5927\u8def\u66ff\u81ea\u5df2\u5012\u4e86\u7897\u8336\u8fb9\u559d\u8336\u2026\u8fb9\u5403\u674f\u812f\u5c31\u597d\u50cf\u662f\u8fd9\u5730\u65b9\u7684\u8001\u5ba2\u4eba\u4f3c\u7684\u70b9\u4e5f\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u7136\u5f8c\xb7\u4ed6\u5c31\u542c\u5230\u9635\u201c\u53ee\u53ee\u5f53\u5f53\u201d\u7684\u73af\u54cd\u58f0\u5fc3\u5fc3\u7ec8\u4e8e\u6276\u4ed6\u4eec\u5bb6\u7684\u5c0f\u59d0\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u59cb\u5934\u770b\u4e86\u773c\xb7\u773c\u775b\u5c31\u5df2\u53d1\u76f4\u3002 \u3000\u3000\u90ed\u5148\u751f\u5e76\u4e0d\u662f\u6ca1\u89c1\u8fc7\u5973\u4eba\u7684\u6bdb\u5934\u5c0f\u4f19\u5b50\u4f46\u8c61\u8fd9\u6837\u7684\u7f8e\u4eba\xb7\u5012\u8fd8\u771f\u662f\u5c11\u89c1\u6e0a\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u8fd9\u6837\u7684\u7f8e\u4eba\uff0c\u53c8\u600e\u914d\u4f4f\u8fd9\u6837\u7684\u5730\u65b9\uff1f \u3000\u3000\u90ed\u5927\u8def\u5634\u91cc\u542b\u534a\u7247\u674f\u812f\uff0c\u65e2\u5fd8\u4e86\u541e\u4e0b\u53bb\u4e5f\u5fd8\u4e86\u62ff\u51fa\u6765\u3002 \u3000\u3000\u4e0d\u77e5\u4ec0\u9ebd\u65f6\u5019\uff0c\u8fd9\u4f4d\u5c0f\u59d0\u4e5f\u5750\u4e0b\u6765\u4e86\uff0c\u5c31\u5750\u5728\u4ed6\u5bf9\u9762\u5f20\u5b9c\u559c\u5b9c\u55c5\u7684\u8138\u4e0a\u4eff\u4f5b\u8fd8\u5e26\u70b9\u7ea2 \u6655\xb7\u4e5f\u4e0d\u77e5\u662f\u59fb\u8102\uff0c\u8fd8\u662f\u5bb3\u7f9e\u53cc\u660e\u5982\u79cb\u6c34\u822c\u7684\u773c\u6ce2\u6b63\u8109\u8109\u542b\u60c5\u7684\u770b\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f00\u59cb\u6709\u70b9\u5750\u7acb\u4e0d\u5b89\u4e86\uff0c\u60f3\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\u5374\u5c06\u5634\u91cc\u542b\u7684\u534a\u7247\u674f\u812f\u54bd\u5728\u5589\u5499 \u91cc\u3002 \u3000\u3000\u5fc3\u5fc3\u5fcd\u4e0d\u4f4f\u201c\u5509\u9632\u201d\u4e00\u7b11\u5f00\u59cb\u7b11\u2606\u5c31\u518d\u4e5f\u505c\u4e0d\u5c38\u6765\u63a5\u809a\u5b50\xb7\u5403\u5403\u7684\u7b11\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5c0f\u59d0\u77aa\u4e86\u5979\u4e00\u773c\u4eff\u4f5b\u5728\u602a\u5979\u7b11\u5f97\u4e0d\u8be5\u4f46\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\u4e3a\u4e4b\u6444\u7136\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\u5546\u7a81\u4e5f\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7b11\u7684\u58f0\u97f3\u53cd\u800c\u6bd4\u8c01\u90fd\u5927\xb7\u4f60\u53ea\u6709\u5728\u542c\u5230\u8fd9\u7b11\u58f0\u7684\u65f6\u5019\uff0c\u624d\u80fd\u611f\u89c9\u5230\u4ed6\u662f\u771f\u6b63\u7684\u90ed\u5927 \u8def\u3002 \u3000\u3000\u65e0\u8bba\u591a\u9ebd\u4e25\u8083\u591a\u9ebd\u5de1\u5979\u7684\u573a\u9762\u53ea\u8981\u90ed\u5927\u8def\u4e00\u7b11\u4e94\u523b\u5c31\u4f1a\u8f7b\u677e\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4f4d\u7f9e\u4eba\u7b54\u7b54\u7684\u5c0f\u59d0\uff0c\u7ec8\u4e8e\u4e5f\u5f00\u53e3\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5979\u7684\u58f0\u755c\u5c31\u548c\u5979\u7684\u4eba\u540c\u6837\u6e29\u67d4\xb7\u83dc\u58f0\u9053\u201c\u8fd9\u5730\u65b9\u867d\u7136\u4e0d\u592a\u597d\u4f46\u90ed\u5927\u7237\u65e2\u7136\u5df2\u6765\u4e86\xb7\u5c31\u4e0d \u8981\u8fc7\u65bc\u62d8\u675f\u201d\u4e00\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u7b11\u9053\u201c\u4f60\u770b\u6211\u8c61\u662f\u4e2a\u62d8\u675f\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u5c0f\u59d0\u5a9a\u7136\u9053\u4e0d\u8c61\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u4e5f\u7b11\u9053\u201c\u6761\u662f\u5c0f\u59d0\u521a\u6258\u4eba\u4ece\u666e\u6d31\u634e\u6765\u7684\uff0c\u90ed\u5927\u7237\u591a\u559d\u4e24\u676f\u4e5f\u597d\u9192\u9192\u9152\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8336\u7684\u786e\u4e0d\u9519\u4f60\u90e8\u9519\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u6014\u4e86\u6014\u9053\u201c\u6211\u4ec0\u9ebd\u5730\u65b9\u9519\u4e86\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u65e0\u8bba\u591a\u597d\u7684\u83dc\xb7\u4e5f\u4e0d\u80fd\u9192\u9152\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u9053\u201c\u8981\u4ec0\u9ebd\u624d\u80fd\u9192\u9152\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u9152\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u7b11\u9053\u201c\u518d\u559d\u9152\u5c82\u975e\u66f4\u9189\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u53c8\u94dd\u4e86\xb7\u53ea\u6709\u9152\u624d\u80fd\u89e3\u9152\u8fd9\u53eb\u505a\u8fd8\u9b42\u9152\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u7728\u7728\u773c\u9053\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u6cd5\u5b50\u662f\u6211\u79ef\u6570\u5e74\u7ecf\u9a8c\u5f97\u6765\u7684\xb7\u7edd\u5bf9\u9519\u5e01\u4e01\u3002\u201d \u3000\u3000\u5c0f\u59d0\u4e5f\u7b11\u9053\u201c\u65e2\u7136\u5982\u6b64\xb7\u8fd8\u4e0d\u5feb\u53bb\u4e3a\u90ed\u5927\u7237\u659f\u9152\u3002\u201d \u3000\u3000\u9152\u6765\u4e86\u3001\u662f\u597d\u9152\u3002 \u3000\u3000\u83dc\u5f53\u7136\u4e5f\u6c34\u503a\u3002 \u3000\u3000\u90ed\u5927\u8def\u5f00\u6000\u7545\u996e\xb7\u771f\u7684\u597d\u50cf\u5df2\u5c06\u8fd9\u4f4d\u5c0f\u59d0\u5f53\u505a\u8001\u670b\u53cb\xb7\u70b9\u4e5f\u4e0d\u5ba2\u6c14\u3002 \u3000\u3000\u8fd9\u503c\u5c0f\u59d0\u5c45\u7136\u4e5f\u80fd\u559d\u4e24\u676f\uff0c\u9152\u8272\u67d3\u7ea2\u4e86\u5979\u7684\u53cc\u9893\u770b\u6765\u66f4\u8273\u5149\u7167\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u775b\u76f4\u52fe\u52fe\u7684\u4e0d\u4f4f\u5979\u8fde\u9152\u90fd\u4f3c\u5df2\u5fd8\u8bb0\u77a9\u5382\u3002 \u3000\u3000\u5c0f\u59d0\u4f4e\u4e0b\u5934\u8f7b\u8f7b\u9053\u201c\u90ed\u5927\u7237\u82e5\u518d\u559d\u5361\u676f\uff0c\u6211\u966a\u676f\u3002\u201d \u3000\u3000\u4e8c\u676f\u9152\u7728\u773c\u95f4\u5c31\u4e0b\u4e86\u809a\uff0c\u90ed\u5927\u8def\u5ffd\u7136\u9053\u201c\u6211\u6709\u4ef6\u4e8b\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5c0f\u59d0\u9053\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7b2c\uff0c\u6211\u4e0d\u53eb\u90ed\u5927\u7237\xb7\u53eb\u90ed\u706b\u8def\xb7\u6211\u7684\u670b\u53cb\u90fd\u53eb\u6211 \u3000\u3000\u5c0f\u59d0\u5a9a\u7136\u9053\u201c\u6709\u4e9b\u4eba\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u8001\u7684\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e5f\u6709\u4e9b\u4eba\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u6210\u5927\u7237\u3002\u201d \u3000\u3000\u4ed6\u53c8\u559d\u5382\u676f\u9152\uff0c\u624d\u63a5\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u7a77\u5149\u86cb\u800c\u4e14\u53c8\u8d43\u53c8\u81ed\u4f60\u5374\u662f\u4f4d\u65bc\u91d1\u5c0f\u59d0\xb7\u800c\u4e14\u4e0d \u8ba4\u5f97\u6211\u4e3a\u4ec0\u9ebd\u8981\u8bf7\u6211\u6765\u559d\u9152\uff1f\u201d \u3000\u3000\u5c0f\u59d0\u773c\u6ce2\u6d41\u52a8\u9053\u201c\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba\u82e5\u662f\u6ca1\u7f18\u53c8\u4f55\u5fc5\u8ba4\u5f97\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u62a2\u901a\u201c\u6211\u4efd\u5bb6\u5c0f\u59d0\u59d3\u6c34\u95fa\u540d\u53eb\u67d4\u9752\u73b0\u5728\u4f60\u4eec\u603b\u8be5\u5df2\u8ba4\u5f97\u4e86\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u629a\u638c\u7b11\u9053\u201c\u6c34\u67d4\u9752\xb7\u597d\u540d\u5b57\u503c\u5f97\u559d\u4e8c\u5927\u676f\u3002\u201d\u6c34\u67d4\u9752\u9010\u914b\u9053\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u996e\u800c\u5c3d\u5979\uff0c\u8fc7\u4e86\u5f88\u4e45\u5ffd\u53c8\u9053\u201c\u6211\u7684\u80a0\u5b50\u662f\u76f4\u7684\u65e0\u8bba\u6709\u4ec0\u9ebd\u90a3\u90fd\u662f\u5b58\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u5a7f\u7136\u9053\u201c\u6211\u770b\u5f97\u51fa\u4f60\u662f\u4e2a\u8c6a\u6c14\u5982\u4e91\u7684\u5927\u4e08\u592b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u672b\u6211\u95ee\u4f60\u662f\u4e0d\u662f\u6709\u4eba\u6b3a\u8d1f\u4e86\u4f60\u4f60\u8981\u6211\u66ff\u4f60\u51fa \u3000\u3000\u5fc3\u5fc3\u53c8\u62a2\u9053\u201c\u6211\u4eec\u5bb6\u5c0f\u59d0\u8db3\u4e0d\u51fa\u6237\u600e\u9ebd\u4f1a\u6709\u4eba\u6b3a\u8d1f\u5979\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u4e0d\u662f\u9047\u4e86\u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\u8981\u6211\u66ff\u4f11\u53bb\u89e3\u51b3\u201d\u5fc3\u5fc3\u9053\u201c\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7f13\u7f13\u5730\u9053\u201c\u6211\u65e2\u7136\u6765\u5382\u53c8\u559d\u4e86\u4f60\u4eec\u7684\u9152\xb7\u65e0\u8bba\u4ec0\u9ebd\u4e8b\uff0c\u53ea\u8981\u4f60\u4eec\u5f00\u53e3\u6211\u5b9a\u5c3d\u529b\u53bb \u505a\u3002\u201d\u6c34\u67d4\u9752\u67d4\u58f0\u9053\u201c\u53ea\u8981\u4f60\u6709\u8fd9\u6837\u7684\u5fc3\u610f\uff0c\u6211\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u77aa\u5979\uff0c\u9053\u201c\u4f60\u771f\u7684\u6ca1\u6709\u4ec0\u9ebd\u4e8b\u6c42\u6211\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u590f\u7684\u6ca1\u6709\u3002\u201d\u90ed\u5927\u8def\u9053\u54ea\u672b\xb7\u4f60\u4e3a\u4ecd\u9ebd\u5bf9\u4e2a\u53c8\u8d43\u53c8\u81ed\u7684\u7a77\u5149\u86cb\u8fd9\u9ebd\u597d\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u62a2\u8d77\u5934\uff0c\u770b\u5979\u773c\u6db2\u5982\u9189\u3002 \u3000\u3000\u88ab\u5979\u8fd9\u6837\u5b50\u770b\u7684\u4eba\u80fd\u4e0d\u9189\u7684\u53c8\u6709\u51e0\u4e2a\uff1f \u3000\u3000\u5fc3\u5fc3\u770b\u90ed\u5927\u8def\uff0c\u53c8\u770b\u770b\u5979\u7684\u5c0f\u59d0\u5ffd\u7136\u7b11\u9053\u201c\u6709\u53e5\u8bdd\u90ed\u5927\u7237\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d\u5fc3\u5fc3\u9053\u4e50\u5b50\u91cd\u82f1\u8c6a\uff0c\u7f8e\u4eba\u559c\u6b22\u7684\u4e5f\u662f\u771f\u6b63\u7684\u82f1\u96c4\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u7684\u8138\u66f4\u7ea2\uff0c\u5a07\u55c5\u8f7b\u5564\u9053\u201c\u5c0f\u9b3c\u518d\u4e71\u56bc\u820c\xb7\u770b\u6211\u4e0d\u6495\u4f60\u7684\u5634\u3002\u201d \u3000\u3000\u5fc3\u5fc3\u7b11\u9053\u201c\u6211\u4e5f\u662f\u76f4\u80a0\u5b50\u5fc3\u91cc\u6709\u4ec0\u9ebd\u8bdd\u4e5f\u5b58\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u7ea2\u8138\u7ad9\u8d77\u6765\u771f\u7684\u8c61\u662f\u8981\u53bb\u62e7\u5979\u3002 \u3000\u3000\u5fc3\u5fc3\u5374\u5df1\u5403\u5403\u7684\u5a07\u7b11\u6e9c\u59fb\u8dd1\u4e86\u51fa\u53bb\xb7\u8dd1\u51fa\u53bb\u65f6\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u66ff\u4ed6\u4eec\u5173\u4e0a\u95e8\u3002 \u3000\u3000\u6c34\u67d4\u9752\u5782\u9996\u7ad9\u5728\u90a3\u91cc\u53c8\u5fcd\u4e0d\u4f4f\u5077\u5077\u8198\u4e86\u90ed\u5927\u8def\u773c\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd8\u5728\u76ef\u5979\u3002 \u3000\u3000\u5979\u7684\u8138\u5df2\u7ea2\u5f97\u8c61\u662f\u79cb\u5915\u7684\u665a\u971e\u3002 \u3000\u3000\u9189\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\u6b64\u60c5\u6b64\u666f\u4e0d\u9189\u7684\u4eba\u4e5f\u8be5\u9189\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u63e1\u4f4f\u4e86\u6c34\u67d4\u9752\u7684\u5b50\u3002 \u3000\u3000\u5979\u7684\u624b\u51b0\u51b7\u8138\u5374\u662f\u706b\u70eb\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u6b63\u60f3\u6402\u5979\u8fd8\u6ca1\u6709\u697c\u5979\u5979\u5df2\u201c\u56fe\u5401\u201d\u58f0\uff0c\u5012\u4eba\u4ed6\u6000 \u3000\u3000\u7a97\u5916\u662f\u76db\u590f\u7a97\u5185\u5374\u662f\u6d53\u6625\u3002 \u3000\u3000\u6625\u8272\u6d53\u5f97\u5316\u4e5f\u5316\u4e0d\u5f00\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u867d\u7136\u7d20\u4e0d\u5dde\u8bc6\u4f46\u53ea\u8981\u89c1\u9762\uff0c\u5c31\u597d\u50cf\u94c1\u9047\u89c1\u78c1\u77f3\u6837\u7acb\u523b\u4f1a\u7d27\u7d27\u7c98\u4f73\u3002 \u3000\u3000\u6c34\u67d4\u9752\u7c98\u5728\u90ed\u5927\u8def\u8eab\u4e0a\u5979\u7684\u808c\u80a4\u67d4\u8f6f\u3001\u5149\u6ed1\u5982\u4e1d\u7f0e\u3002 \u3000\u3000\u5979\u7684\u8170\u80f6\u76c8\u76c8\u63e1\u3002 \u3000\u3000\u90ed\u5927\u8def\u63e1\u5979\u7684\u8170\u5ffd\u7136\u8f7b\u8f7b\u53f9\u606f\u54fa\u54fa\u9053\u201c\u6211\u4e0d\u6a2a\u771f\u7684\u4e0d\u503a\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u8f7b\u8f7b\u9053\u201c\u6709\u4e9b\u4e8b\u672c\u6765\u5c31\u662f\u6ca1\u6cd5\u5b50\u89e3\u91ca\u7684\u672c\u6765\u5c31\u6ca1\u6709\u4eba\u61c2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4ee5\u524d\u65e2\u6ca1\u6709\u770b\u89c1\u8fc7\u6211\u4e5f\u4e0d\u77e5\u9053\u6211\u662f\u4e2a\u600e\u9ebd\u6837\u7684\u4eba\u4e3a\u4ec0\u9ebd\u8fd9\u6837\u5343\u5bf9\u6211\uff1f\u201d \u3000\u3000\u6c34\u6851\u9752\u9053\u201c\u6211\u867d\u7136\u6ca1\u6709\u770b\u89c1\u8fc7\u4f60\u5374\u4e2d\u5df2\u77e5\u9053\u4f60\u662f\u4e2a\u600e\u9ebd\u6837\u7684 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5ce8\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u7684\u8eab\u4e86\u7c98\u5f97\u66f4\u7d27\u7f13\u7f13\u9053\u201c\u8fd9\u6fb3\u5929\u6765\u57ce\u91cc\u7684\u4eba\u8c01\u4e0d\u77e5\u9053\u8089\u8fdc\u5730\u6765\u4e86\u4e2a\u5929\u4e0d\u6015\u5730\u4e0d\u6015 \u7684\u597d\u6c49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u597d\u6c49\uff1f\u4f60\u8c30\u4e0d\u77e5\u9053\u597d\u6c49\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u6211\u542c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u597d\u6c49\u7684\u610f\u601d\uff0c\u6709\u65f6\u5019\u5c31\u662f\u6d41\u6c13\u65e0\u8d56\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u5a7f\u7136\u9053\u201c\u6211\u4e0d\u77e5\u9053\u6211\u53ea\u77e5\u9053\u597d\u6c49\u5c31\u662f\u597d\u6c49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\uff0c\u8f7b\u629a\u5979\u7684\u80fa\u80f6\uff0c\u7b11\u9053\u201c\u4f60\u771f\u662f\u4e2a\u5947\u602a\u7684\u4ea5\u4eba\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u6240\u4ee5\u6211\u624d\u4f1a\u559c\u6b22\u50cf\u4f60\u8fd9\u9ebd\u6837\u5947\u602a\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\u5979\u7684\u8138\u53c8\u7ea2\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u51dd\u89c6\u5979\u9053\u201c\u6211\u4ee5\u524d\u4f5c\u68a6\u4e5f\u8bbe\u60f3\u5230\u4f1a\u9047\u89c1\u4f60\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u66f4\u6ca1\u6709\u60f3\u5230\u4f1a\u613f\u4f60\u8fd9\u6837\u5b50 \u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u7684\u8138\u66f4\u7ea2\u8f7b\u8f7b\u9053\u201c\u53ea\u8981\u4f60\u613f\u610f\u6211\u5c31\u6c38\u8fdc\u8fd9\u6837\u5b50\u8ddf\u4f60\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u51dd\u89c6\u5382\u5979\u5f88\u4e45\xb7\u5ffd\u53c8\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\xb7\u7ffb\u4e86\u4e2a\u8eab\xb7\u5f20\u5927\u4e86\u773c\u775b\u77aa\u5c4b\u9876\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4f60\u5728\u53f9\u6c14\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4f60\u5728\u60f3\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u4e5f\u7ffb\u4e86\u4e2a\u8eab\u4f0f\u5728\u4ed6\u80f8\u819b\u4e0a\u8f7b\u629a\u4ed6\u7684\u8138\xb7\u67d4\u58f0\u9053\u201c\u6211\u53ea\u95ee\u4f60\xb7\u4f60\u613f\u4e0d\u613f\u610f\u6c38\u8fdc\u8ddf\u6211 \u8fd9\u6837\u5b50\u5728\u8d77\uff1f\u201d \u3000\u3000\u90ed\u4eba\u8def\u6c89\u9ed8\uff0c\u6c89\u9ed8\u5382\u5f88\u4e45\u624d\u5b57\u5b87\u9053\u201c\u4e0d\u613f\u610f\u3002\u201d\uff01 \u3000\u3000\u6c34\u67d4\u9752\u67d4\u8f6f\u7684\u8eab\u5e72\u7a81\u7136\u50f5\u786c\u560e\u58f0\u9053\u201c\u4f60\u4e0d\u613f\u610f\u4e25 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u4e0d\u613f\u610f\u662f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4e0d\u80fd\uff1f\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6162\u6162\u7684\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u6c34\u6cf5\u9752\u9053\u201c\u4f60\u6447\u5934\u662f\u4ec0\u9ebd\u610f\u601d\u4e0d\u559c\u6b22\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4ea5\u4eba\u82e5\u6709\u7537\u4eba\u4e0d\u559c\u6b22\u4f60\u90a3\u4eba\u5b9a\u6709\u6bdb\u75c5\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u6c34\u6851\u9752\u9053\u201c\u53ef\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u82eb\u7b11\u9053\u201c\u65f6\u662f\u6211\u6709\u6bdb\u75c5\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u770b\u4ed6\xb7\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u63a0\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u6211\u662f\u4e2a\u7537\u4eba\uff0c\u5df2\u6709\u5f88\u4e45\u6ca1\u63a5\u8fd1\u8fc7\u5973\u4eba\u4f60\u662f\u4e2a\u975e\u5e38\u7f8e\u7684\u5973\u4eba\u800c\u4e14\u5bf9\u6211\u5f88\u597d\u8fd9\u5730 \u65b9\u53c8\u5982\u6b64\u6e29\u67d4\uff0c\u6211\u4eec\u53c8\u559d\u4e86\u70b9\u9152\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u6211\u600e\u9ebd\u80fd\u4e0d\u52a8\u5fc3\uff0c\u6240\u4ee5\u2500\u2026\u201c \u3000\u3000\u6c34\u67d4\u9752\u54ac\u5634\u5c5e\u9053\u201c\u6240\u4ee5\u4f60\u800d\u4e86\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u60a3\uff0c\u9053\u201c\u53ef\u662f\u6211\u4eec\u4e4b\u95f4\u5e76\u6ca1\u6709\u4ec0\u9ebd\u771f\u7684\u611f\u60c5\u3002\u6211\u2500\u4e00\u201c\u6211\u201d\u4e00\u3002\u201d \u3000\u3000\u6c34\u8363\u9752\u9053\u201c\u4f60\u600e\u9ebd\u6837\uff1f\u2026\u2019\u3002\u96be\u9053\u4f24\u5fc3\u91cc\u5728\u60f3\u53e6\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002\u6c34\u67d4\u9752\u9053\u201c\u4f60\u8ddf\u5979\u771f\u66f2\u6709\u611f\u60c5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\uff0c\u5ffd\u53c8\u6447\u6447\u5934\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u5230\u5e95\u662f\u4e0d\u662f\u771f\u7684\u6709\u611f\u60c5\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u90a3\u662f\u79cd\u4ec0\u9ebd\u6837\u7684\u611f\u60c5\uff0c\u6211\u4e0d\u77e5\u9053\u6211\u770b\u4e0d\u89c1\u4ed6\u7684\u65f6\u5019\u65f6\u65f6\u523b\u523b\u90fd \u5728\u60f3\u4ed6\u3002\u4f60\u867d\u7136\u53c8\u7f8e\u3001\u53c8\u6e29\u67d4\u6211\u867d\u7136\u4e5f\u62a5\u559c\u6b22\u4f60\u4f46\u5728\u6211\u5fc3\u91cc\u65e0\u8bba\u8c01\u4e5f\u65e0\u6cd5\u4ee3\u66ff\u4ed6\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u6240\u4ee5\u4f60\u8fd8\u53ea\u6709\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u975e\u627e\u5230\u4e0d\u53ef\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u8c0a\u201c\u6240\u4ee5\u4f60\u8981\u8d70\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u95ed\u4e0a\u773c\u775b\u70b9\u5382\u70b9\u5934\u3002 \u3000\u3000\u6c34\u67d4\u9752\u770b\u4ed6\u773c\u775b\u91cc\u5e76\u6ca1\u6709\u7406\u6028\u53cd\u800c\u4f3c\u4e5f\u9f13\u611f\u52a8\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5979\u624d\u957f\u53f9\u606f\u4e86\u58f0\u5e7d\u5e7d\u7684\u9053\u201c\u4e16\u4e0a\u82e5\u6709\u4e2a\u7537\u4eba\u4e5f\u8c61\u8fd9\u6837\u5b50\u5bf9\u6211\u6211\u2500\u4e00\u3002\u6211\u5c31\u7b97\u6b7b \u4e5f\u7af9\u5fc3\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u67d4\u58f0\u9053\u201c\u4f60\u8fdf\u65e9\u5b9a\u4e5f\u4f1a\u627e\u5230\u8fd9\u9ebd\u6837\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u6447\u6447\u5934\uff0c\u9053\u201c\u6c38\u8fdc\u672f\u4f1a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u201d \u3000\u3000\u6c34\u67d4\u9752\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\xb7\u5ffd\u7136\u9053\u201c\u4f60\u662f\u4e2a\u5f88\u597d\u7684\u4eba\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u5230\u4f60\u8fd9\u6837\u7684\u597d\u4eba\u6240\u4ee5\u6211 \u4e5f\u613f\u610f\u5bf9\u4f60\u8bf4\u8001\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u542c\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4f60\u7ec6\u5c0f\u77e5\u9053\u6211\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u59d3\u6c34\u53eb\u6c34\u67d4\u80c3\u662f\u4f4d\u5e72\u91d1\u5c0f\u59d0\u800c\u4e14\u6e29\u67d4\u7f8e\u4e3d\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4f60\u9519\u4e25\u6211\u5e76\u4e0d\u662f\u4ec0\u9ebd\u5e72\u91d1\u5c0f\u59d0\u53ea\u4e0d\u8fc7\u662f\u4e2a\u2026\u2026\u662f\u4e2a\u2026\u2026\u201d\u5979\u54ac\u5634\u5507\u7a81\u53c8\u6c0f\u957f \u53f9\u606f\u9053\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5993\u5973\u3002\u201d \u3000\u3000\u201c\u5993\u5973\u201d\u90ed\u5927\u8def\u51e0\u4e4e\u4ece\u5e8a\u3014\u76f4\u8df3\u8d77\u6765\u5927\u58f0\u53eb\u9053\u201c\u4f60\u4e0d\u662f\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u9053\u201c\u6211\u662f\u7684\u3002\u4e0d\u4f46\u662f\xb7\u800c\u5df4\u662f\u8fd9\u5730\u65b9\u8eab\u4ef7\u6700\u9ad8\u7684\u540d\u5993\uff0c\u4e0d\u662f\u63b7\u5343\u91d1 \u7684\u738b\u5b59\u516c\u5b50\u5c31\u4f11\u60f3\u505a\u6211\u7684\u5165\u5e55\u4e4b\u5bbe\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4f4f\uff0c\u6014\u5382\u534a\u5929\u54e8\u54fa\u9053\uff1a\u201c\u4f46\u6211\u5e76\u4e0d\u662f\u4ec0\u9ebd\u5929\u5b59\u516c\u5b50\xb7\u800c\u4e14\u8eab\u4e0a\u8fde\u4e00\u91d1\u90fd\u6ca1 \u6709\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u5ffd\u7136\u7ad9\u8d77\u6765\u6253\u5f00\u4e86\u5986\u53f0\u7684\u62bd\u5c49\xb7\u6367\u4e86\u628a\u660e\u73e0\u9053\u201c\u4f60\u867d\u7136\u6ca1\u6709\u4e3a\u6211\u2018\u63b7\u4e8e\u91d1\u4f46\u5374\u5df2 \u6709\u4eba\u4e3a\u4f60\u4e70\u4e0b\u4e86\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u66f4\u5403\u60ca\uff0c\u9053\uff1a\u201c\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4e5f\u8bb8\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u662f\u4e1c\u57ce\u7684\u8001\u5927\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u6de1\u8c08\u9053\u201c\u4ed6\u8fd8\u4e0d\u914d\u5230\u6211\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u9ebd\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u662f\u4e2a\u6211\u4ece\u672a\u89c1\u8fc7\u7684\u4eba\u3002\u201d\u7ff1\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u6c34\u6851\u9752\u9053\u201d\u662f\u4e2a\u9ebb\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7cdf\u7136\u9053\u201c\u9ebb\u4e88\uff1f\u6211\u7684\u670b\u53cb\u91cc\u8fde\u4e00\u4e2a\u9ebb\u4e8e\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u6c34\u67d4\u80b2\u9053\uff0c\u201c\u4f46\u73cd\u6b8a\u5374\u7684\u786e\u662f\u4ed6\u4e3a\u4f53\u8ba8\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5403\u60ca\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\uff1a\u4ed6\u53eb\u6211\u597d\u597d\u7684\u4f8d\u5019\u4f60\u65e0\u8bba\u6062\u8981\u4ec0\u9ebd\u90fd\u7ed9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f60\u624d\u201c\u3002\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u4e0d\u8ba1\u4ed6\u8bf4\u4e0b\u53bb\u53c8\u9053\u201c\u4f46\u4ed6\u4e5f\u7b97\u51fa\u6765\u4f60\u72e0\u53ef\u80fd\u4e0d\u613f\u7559\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u7b49\u5230\u4f60\u4e0d\u613f\u7559\u5382\u6765\u7684\u65f6\u5019\u4ed6\u624d\u8981\u544a\u8bc9\u4f60\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d\u6c34\u67d4\u80b2\u9053\u201c\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\u3002\u201d \u3000\u3000\u5979\u9992\u9992\u7684\u63a5\u9053\u201c\u51e0\u4e2a\u6708\u4ee5\u524d\u8fd9\u91cc\u5ffd\u7136\u6765\u4e86\u4e2a\u5f88\u5947\u9524\u7684\u5ba2\u4eba\u8ddf\u4f60\u6837\u7a7f\u5f97\u53c8\u810f\u53c8\u7834\xb7\u6211\u672c\u6765 \u60f3\u819b\u4ed6\u51fa\u53bb\u7684\u3002\u201d\u90ed\u5927\u8def\u9053\u201c\u5f8c\u6765\u5462\uff1f\u201d \u3000\u3000\u6c34\u6817\u80b2\u9053\u201c\u53ef\u662f\u4ed6\u8fdb\u6765\u5c31\u5728\u684c\u4e0a\u6446\u4e0b\u4e86\u767e\u4e24\u9ec4\u91d1\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f11\u5c31\u8ba9\u4ed6\u7559\u4e0b\u6765\u4e86\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u773c\u76ee\u4e2d\u9732\u51fa\u4e1d\u5e7d\u6028\u4e4b\u8272\xb7\u6de1\u6de1\u5730\u9053\u201c\u6211\u672c\u6765\u5c31\u662f\u4e2a\u505a\u8fd9\u79cd\u4e8b\u7684\u5973\u4eba\u53ea\u8ba4\u91d1\u5b50\u4e0d\u8ba4 \u4eba\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u6211\u660e\u767d\xb7\u53ef\u662f\u2026\u2026\u53ef\u662f\u4f60\u5e76\u4e0d\u8c61\u8fd9\u6837\u66f2\u5973\u4eba\u3002 \u3000\u3000\u6c34\u67d4\u9752\u5ffd\u7136\u6254\u8fc7\u5934\u4eff\u4f5b\u4e0d\u613f\u8ba9\u90ed\u5927\u8def\u770b\u5230\u5979\u819b\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5979\u624d\u6162\u9992\u7684\u63a5\u9053\uff1a\u201c\u4e16\u4e0a\u672c\u6765\u5c31\u6709\u5f88\u591a\u5bcc\u5bb6\u5c0f\u4e52\u559c\u6b22\u6545\u610f\u88c5\u6210\u8fd9\u79cd\u6837\u77db\xb7\u6765\u5bfb \u6b22\u4f5c\u5bfb\u627e\u522b\u4eba\u6c57\u5fc3\u8fd9\u5e76\u4e0d\u5947\u602a\u3002\u201d \u3000\u3000\u90ed\u5165\u8def\u9053\u201c\u5947\u602a\u662f\u4ec0\u9ebd\u5462\uff1f\u201d \u3000\u3000\u6c34\u67d4\u80b2\u9053\u201c\u5947\u602a\u7684\u4e8b\u4ed6\u82b1\u4e86\u767e\u4e24\u9ec4\u91d1\uff0c\u5374\u8fde\u78b0\u90fd\u6ca1\u6709\u78b0\u6211\u53ea\u4e0d\u8fc7\u5728\u8fd9\u91cc\u6d17\u4e86\u4e2a\u6fb3\u4e3d\u6c11\u8fd8 \u7a7f\u4e86\u6211\u5957\u8863\u670d\u8d70\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u7a7f\u4e86\u4f60\u5957\u8863\u670d\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u70b9\u70b9\u5934\u3002\u90ed\u5927\u8def\u9053\u201c\u4ed6\u7a76\u7adf\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4ed6\u6765\u7684\u65f6\u5019\u672c\u662f\u4e2a\u7537\u4eba\u516b\u65e5\u7a7f\u4e0a\u6211\u7684\u519c\u670d\u5f8c\u7b80\u76f4\u6bd4\u6211\u8fd8\u597d\u770b\u3002\u201d \u3000\u3000\u5979\u82e6\u7b11\u63a5\u9053\u201c\u8001\u5b9e\u8bf4\u6211\u867d\u7136\u89c1\u8fc7\u8bb8\u8bb8\u591a\u591a\u5947\u602a\u7684\u4eba\xb7\u6709\u7684\u4eba\u559c\u6b22\u8981\u6211\u7528\u97ad\u5b50\u62bd\u4ed6\u7528\u811a\u8e29 \u4ed6\xb7\u53ef\u662f\xb7\u50cf\u4ed6\u8fd9\u6837\u7684\u4eba\uff0c\u6211\u5012\u662f\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u5230\u5f8c\u6765\u8fde\u6211\u90fd\u5206\u4e0d\u6e05\u4ed6\u7a76\u7adf\u662f\u7537\u662f\u4ea5\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4f4f\u4f46\u773c\u775b\u5374\u5df2\u53d1\u51fa\u5382\u5149\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u9690\u9690\u731c\u51fa\u5979\u8bf4\u7684\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u8fd9\u4e9b\u8bdd\u6211\u76f4\u5230\u73b0\u5728\u624d\u8bf4\u51fa\u6765\xb7\u53ea\u56e0\u4e3a\u90a3\u9ebb\u5b50\u518d\u53c1\u5631\u5490\u6211\uff0c\u4f60\u82e5\u613f\u610f\u7559\u4e0b \u6765\xb7\u6211\u5c31\u6c38\u8fdc\u4e0d\u80fd\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u2026\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u5947\u602a\u7684\u5ba2\u4eba\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u201d \u3000\u3000\u4ed6\u4f3c\u5df2\u7d27\u5f20\u5f97\u8fde\u624b\u90fd\u5728\u53d1\u6296\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u5979\u5e76\u6ca1\u6709\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\u6765\u53ea\u544a\u8bc9\u6211\u4ed6\u59d3\u71d5\u71d5\u5b50\u7684\u71d5\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u8df3\u8d77\u6765\u7528\u529b\u63e1\u5979\u7684\u80a9\xb7\u560e\u58f0\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u73b0\u5728\u5728\u4ec0\u9ebd\u5730\u65b9\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5012\u9000\u4e86\u4e24\u6b65\u4f3c\u5df2\u8fde\u7ad9\u90fd\u7ad9\u6c34\u4f0e\u4e86\uff0c\u201c\u5634\u201d\u7684\u53c8\u5750\u5230\u5e8a\u4e0a\u3002 \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u53ef\u662f\u5979\u6700\u8fd1\u53c8\u6765\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7acb\u523b\u53c8\u8c61\u4e2d\u4e86\u7bad\u822c\u8df3\u8d77\u6765\u5927\u58f0\u9053\u201c\u6700\u8fd1\u662f\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u5c31\u5728\u524d\u5341\u6765\u5929\u3002\u201d \u3000\u3000\u5979\u63a5\u53c8\u9053\u201c\u8fd9\u6b21\u5979\u6765\u7684\u65f6\u5019\u6837\u5b50\u770b\u6765\u597d\u50cf\u6709\u5f88\u591a\u5fc3\u4e8b\xb7\u5728\u6211\u8fd9\u91cc\u559d\u4e86\u5f88\u591a\u9152\uff0c\u7b2c\u4e8c\u5929\u5c31 \u7a7f\u4e86\u6211\u5957\u8863\u88f3\u8d70\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u66f4\u7d27\u5f20\uff0c\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u8d70\u5230\u4ec0\u9ebd\u5730\u65b9\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u597d\u50cf\u53c8\u8981\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e78\u597d\u6c34\u67d4\u9752\u5f88\u5feb\u7684\u63a5\u53c8\u9053\u201c\u4f46\u5979\u559d\u9189\u4e86\u7684\u65f6\u5019\u8bf4\u4e86\u5f88\u591a\u9189\u8bdd\u8bf4\u5979\u8fd9\u6b21\u56de\u53bb\u4e4b\u5f8c\xb7\u5c31\u6c38\u8fdc \u4e0d\u4f1a\u518d\u56de\u6765\u6211\u6c38\u8fdc\u518d\u4e5f\u4e0d\u4f1a\u89c1\u5230\u5979\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u201d\u4e00\u201c\u4f60\u6ca1\u6709\u95ee\u8fc7\u5979\xb7\u5979\u7684\u5bb6\u5728\u7530\u91cc\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u6211\u672c\u6765\u662f\u968f\u66f0\u95ee\u7684\u5e76\u6ca1\u6709\u60f3\u5230\u5979\u4f1a\u544a\u8bc9\u6211\u201c \u3000\u3000\u90ed\u5927\u8def\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u8feb\u5207\u7684\u671f\u671b\u62a2\u9053\u201c\u4f46\u5979\u90fd\u544a\u8bc9\u4e86\u4f60\uff1f\u201d \u3000\u3000\u6c34\u6cf5\u9752\u70b9\u70b9\u5934\xb7\u9053\u201c\u5979\u8bf4\u5979\u7684\u5bb6\u5728\u6d4e\u5357\u5e9c\u8fd8\u8bf4\u90a3\u91cc\u7684\u5927\u660e\u6e56\u6625\u8272\u4e4b\u7f8e\u8fde\u897f\u6e56\u90fd\u6bd4\u4e0d \u4e0a\xb7\u53eb\u6211\u4ee5\u5f8c\u6709\u673a\u4f1a\u65f6\u4e00\u5b9a\u8981\u53bb\u901b\u901b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u7136\u53c8\u5012\u4e86\u4e0b\u53bb\xb7\u5c31\u8c61\u662f\u8dd1\u51e0\u5929\u51e0\u591c\u7684\u4eba\u5386\u5c3d\u4e86\u5e72\u8f9b\u4e07\u82e6\xb7\u7ec8\u4e8e\u5230\u8fbe\u4e86\u4ed6\u7684\u76ee\u7684 \u5730\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5012\u4e86\u4e0b\u53bb\u4f46\u5fc3\u91cc\u5374\u662f\u5e78\u798f\u6109\u5feb\u7684\u3002 \u3000\u3000\u6c34\u67d4\u9752\u770b\u4ed6\u76ee\u4e2d\u5145\u6ee1\u4e86\u601c\u60dc\u8f7b\u8f7b\u9053\u201c\u4f60\u8981\u627e\u7684\u5c31\u662f\u5979\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\u3002\u6c34\u67d4\u9752\u9053\u201c\u5979\u77e5\u4e0d\u77e5\u9053\u4f60\u5bf9\u5979\u5982\u6b64\u75f4\u60c5\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u70b9\u4e86\u70b9\u5934\u53c8\u6447\u4e86\u6447\u5934\u6587\u4eba\u7684\u5fc3\u6709\u8c01\u77e5\u9053\u5462\uff1f \u3000\u3000\u6c34\u67d4\u9752\u53c8\u8f7b\u8f7b\u7684\u53f9\u606f\u6b79\u58f0\u5e7d\u5e7d\u9053\u201c\u5979\u4e3a\u4ec0\u9ebd\u8981\u8d70\uff1f\u82e5\u662f\u6211\u4f60\u5c31\u7b97\u7528\u97ad\u5b50\u8d76\u6211\u6211\u4e5f\u4e0d\u4f1a\u8d70 \u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u54fa\u54fa\u9053\u201c\u5979\u4e0d\u662f\u4f60\u201d\u2026\u5979\u4e5f\u662f\u4e2a\u5947\u602a\u7684\u4eba\u6211\u59cb\u7ec8\u90fd\u6ca1\u6709\u4e86\u89e3\u8fc7\u5979\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u74e3\u7136\u9053\u201c\u5979\u4e0d\u662f\u6211\u6240\u4ee5\u5979\u624d\u4f1a\u8d70\u53ea\u6709\u50cf\u6211\u8fd9\u6837\u7684\u5973\u4eba\u624d\u61c2\u5f97\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e1c\u56db \u6bd4\u771f\u60c5\u66f4\u53ef\u8d35\u3002\u201d \u3000\u3000\u5979\u53f9\u601d\uff0c\u53c8\u9053\u201c\u4e2a\u5973\u4eba\u82e5\u4e0d\u50f5\u5f97\u73cd\u60dc\u8fd9\u4efd\u771f\u60c5\uff0c\u5979\u5b9a\u4f1a\u76fe\u4fae\u7ec8\u751f\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e49\u6c89\u9ed8\u4e86\u5f88\u4e45\u5ffd\u7136\u95ee\u9053\u201c\u4f60\u770b\u5979\u7a76\u7adf\u662f\u4e0d\u662f\u4e2a\u4ea5\u4eba\uff1f\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u96be\u9053\u4f60\u5230\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4ef0\u9762\u5012\u5728\u5e8a\u4e0a\uff0c\u957f\u6c0f\u53f6\u51fa\u53e3\u6c14\uff0c\u54fa\u54fa\u9053\u201c\u5e78\u597d\u73b0\u5728\u6211\u603b\u7b97\u77e5\u9053\u4ef6\u4e8b\u4e86\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5fbd\u7b11\xb7\u7f13\u7f13\u9053\u201c\u6211\u5e76\u6ca1\u6709\u6bdb\u75c5\u201c\u2026\u2018\u70b9\u6bdb\u75c5\u90fd\u6ca1\u6709\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u778e\u5b50\u800c\u5df2\u3002\u201d \u7b2c\u5341\u4e5d\u7ae0\u3000\u9ec4\u3000\u660f>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u9ec4\u3000\u660f \u3000\u3000\u5915\u9633\u7167\u8fdb\u7a97\u6237\u7167\u5728\u90ed\u5927\u8def\u521a\u6362\u7684\u5957\u65b0\u8863\u670d\u4e0a\u4ed6\u4f3c\u5df2\u5b8c\u5168\u53d8\u4e2b\u4e2a\u4eba\u53d8\u5f97\u5bb9\u5149\u7115\u53d1\u800c\u4e14\u975e\u5e38 \u6e2d\u9192\u3002\u6c34\u67d4\u9752\u770b\u4ed6\u54ac\u5676\u5507\u9053\u201c\u4f60\u2026\u201c\u4f60\u73b0\u5728\u5c31\u8981\u8d70\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u8001\u5b9e\u8bf4\u6211\u7b80\u76f4\u5f88\u4e0d\u5f97\u957f\u51fa\u4e24\u53ea\u7fc5\u8180\u6765\u98de\u8d70\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u5782\u4e0b\u5934\uff0c\u76ee\u4e2d\u53c8\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u5e7d\u6028\u51c4\u695a\u4e4b\u8272\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\uff0c\u7b11\u5bb9\u4e5f\u6e10\u6ca5\u6f5e\u8c08\u76ee\u4e2d\u4e5f\u5145\u6ee1\u601c\u60dc\uff0c\u5fcd\u4e0d\u4f4f\u62cd\u4e86\u62cd\u5979\u7684\u80a9\uff0c\u67d4\u58f0\u9053\u201c\u4f53\u662f\u4e2a \u5f88\u597d\u7684\u5973\u5b69\u5b50\xb7\u5c06\u6765\u603b\u6709\u5929\u2026\u2026\u201d \u3000\u3000\u6c34\u67d4\u9752\u51c4\u7136\u7b11\u9053\u201c\u5c06\u6765\u603b\u6709\u5929\u6211\u4e5f\u4f1a\u627e\u5230\u4e2a\u50cf\u4f60\u8fd9\u6837\u7684\u7537\u4eba\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u4e5f\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\u201c\u89c1\u5230\u90a3\u4f0e\u71d5\u59a8\u5a18\u65f6\u83ab\u5fd8\u8bb0\u66ff\u6211\u5411\u5979\u95ee\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4f1a\u7684\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u9053\u201c\u544a\u8bc9\u5979\u4ee5\u5f8c\u82e5\u6709\u673a\u4f1a\xb7\u6211\u5b9a\u4f1a\u5230\u5927\u660e\u6e56\u53bb\u770b\u4f60\u4eec\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u8bf4\u4e0d\u5b9a\u6211\u4eec\u4f1a\u5148\u6765\u770b\u5f25\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u5728\u7b11\xb7\u6cae\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u9ebd\xb7\u5fc3\u91cc\u603b\u8c61\u662f\u6709\u70b9\u9178\u9178\u7684\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5df2\u4e0d\u5fcd\u518d\u7559\u4e0b\u53bb\u5b9e\u5728\u4e0d\u5fcd\u518d\u770b\u5979\u7684\u773c\u775b\xb7\u5ffd\u7136\u8f6c\u8fc7\u5934\u671b\u7a97\u5916\u7684\u5915\u9633\u54fa\u54fa\u9053\u201c\u73b0\u5728 \u5927\u8fd8\u6ca1\u6709\u9ed1\u6211\u8fd8\u6765\u5f97\u53ca\u8d74\u6bb5\u8def\u3002\u201d\u6c34\u67d4\u9752\u5782\u5934\u8f7b\u8f7b\u9053\u201c\u4e0d\u9519\u4f60\u8fd8\u662f\u5feb\u8d70\u7684\u597d\uff0c\u5979\u8bf4\u4e0d\u5b9a\u4e5f\u5728 \u7b49\u4f60\u5de6\u627e\u5979\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\u4eff\u4f5b\u60f3\u8bf4\u4ec0\u9ebd\u606b\u7ec8\u4e8e\u4ec0\u9ebd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u5c31\u8fd9\u6837\u8d70\u4e01\u51fa\u53bb\u3002 \u3000\u3000\u4e0d\u8d70\u53c8\u80fd\u600e\u6837\u5462\uff1f\u8fd8\u662f\u8d70\u4e86\u7684\u597d\u8fd8\u662f\u5feb\u8d70\u7684\u597d\u3002\u6c34\u67d4\u9752\u7a81\u7136\u9053\u201c\u7b49\u7b49\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9992\u6162\u7684\u56de\u8fc7\u8eab\u9053\u201c\u4f60\u2026\u3002\u201d \u3000\u3000\u6c34\u67d4\u9752\u6ca1\u6709\u8ba9\u4ed6\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u81ea\u6000\u4e2d\u53d6\u51fa\u4e86\u4e2a\u6d45\u7d2b\u8272\u7684\u7ee3\u82b1\u8377\u5305\xb7\u9012\u7ed9\u4ed6\u67d4\u58f0\u9053\u201c\u8fd9\u4e2a\u7ed9 \u4f60\uff0c\u8bf7\u8f6c\u4ea4\u7ed9\u71d5\u59d1\u5a18\xb7\u5c31\u8bf4\u201c\u2026\u5c31\u8bf4\u8fd9\u662f\u6211\u9001\u7ed9\u4f60\u4eec\u7684\u8d3a\u793c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8fd9\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u4ed6\u63a5\u8fc7\u5c31\u5df2\u7528\u4e0d\u518d\u95ee\u3002 \u3000\u3000\u4ed6\u5df2\u53ef\u611f\u89c9\u5230\u8377\u5305\u91cc\u7684\u660e\u8e29\u7684\u5149\u6ed1\u5706\u6da6\u3002 \u3000\u3000\u6c34\u67d4\u9752\u5df2\u8f6c\u8fc7\u8eab\u770b\u4e5f\u4e0d\u53bb\u770b\u7a97\u5916\u7684\u5915\u9633\u6de1\u8c08\u9053\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u8d70\u5382\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7d27\u7d27\u63e1\u8fd9\u8377\u5305\u5979\u7684\u5fc3\u5c82\u975e\u4e5f\u5320\u5982\u8377\u5305\u4e2d\u7684\u660e\u73e0\u6837\u5c82\u975e\u4e5f\u5df2\u88ab\u4ed6\u63e1\u5728\u624b\u91cc\uff1f \u3000\u3000\u5979\u6ca1\u6709\u518d\u56de\u5934\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u6709\u4e9b\u8bdd\u662f\u6839\u672c\u5c31\u7528\u4e0d\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u6216\u8bb8\u4e5f\u53ea\u6709\u5728\u5929\u75b2\u6ca6\u843d\u7684\u4eba\u624d\u80fd\u4e86\u89e3\u8fd9\u79cd\u5fc3\u60c5\uff0c\u8fd9\u79cd\u610f\u5883\u3002 \u3000\u3000\u8fd9\u79cd\u610f\u5883\u867d\u7136\u51c4\u51c9\u5374\u53c8\u662f\u591a\u9ebd\u7f8e\u4e3d\uff1f \u3000\u3000\u6751\u59d1 \u3000\u3000\u8fdc\u5c71\u9752\u7eff\u6e56\u6c34\u6e5b\u84dd\u3002 \u3000\u3000\u9752\u7eff\u7684\u8fdc\u5c71\u5012\u6620\u5728\u84dd\u6e56\u6c34\u91cc\u84dd\u7fe0\u5982\u7eff\u7eff\u6d53\u5982\u84dd\u3002 \u3000\u3000\u90ed\u5927\u8def\u6cbf\u6e56\u5cb8\u6162\u6162\u7684\u5f80\u524d\u8d70\u5c31\u8c61\u662f\u4e2a\u6e38\u9b42\u4f3c\u7684\u65e2\u6ca1\u6709\u76ee\u7684\u4e5f\u4e0d\u8fa8\u65b9\u5411\u3002 \u3000\u3000\u542c\u5230\u4e86\u71d5\u4e03\u7684\u6d88\u606f\uff0c\u4ed6\u5c31\u6839\u4e0d\u5f97\u808b\u725b\u53cc\u7fc5\u957f\u5230\u6d4e\u5357\u5e9c\u6765\xb7\u597d\u8c61\u53ea\u8981\u4ed6\u5230\u4e86\u6d4e\u5357\u5e9c\xb7\u7acb\u523b \u5c31\u53ef\u4ee5\u627e\u5230\u71d5\u4e03\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u5230\u5382\u6d4e\u5357\u5e9c\uff0c\u5200\u77e5\u9053\u81ea\u5df1\u60f3\u5f97\u5b9e\u5728\u592a\u5929\u771f\u5382\u3002 \u3000\u3000\u8fd9\u89c1\u9b3c\u7684\u6d4e\u5357\u5e9c\u53ef\u771f\u4e0d\u5c0f\u57ce\u91cc\u81f3\u5c11\u6709\u51e0\u5343\u51e0\u767e\u6237\u4eba\u5bb6\u51e0\u5343\u51e0\u4e07\u4e2a\u4eba\u3002 \u3000\u3000\u8981\u5230\u8fd9\u9ebd\u5927\u7684\u5730\u65b9\xb7\u8fd9\u9ebd\u591a\u4eba\u4e2d\u6765\u627e\u71d5\u4e03\xb7\u8fd8\u662f\u597d\u50cf\u60f3\u5728\u5927\u6d77\u91cc\u635e\u9488\u6837\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u6bcf\u5929\u5728\u8fd9\u91cc\u6e38\u9b42\u822c\u901b\u6765\u901b\u53bb\u5e0c\u671b\u6709\u4e00\u5929\u8fd0\u6c14\u7279\u522b\u597d\u80fd\u649e\u535c\u71d5\u4e03\u3002 \u3000\u3000\u53ef\u662f\u8fde\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u8fd9\u5e0c\u671b\u867d\u7136\u592a\u6e3a\u832b\xb7\u5047\u65e0\u8bba\u591a\u6e3a\u832b\u7684\u5e0c\u671b\u603b\u6bd4\u6ca1\u6709\u5e0c\u671b\u597d\u3002 \u3000\u3000\u73b0\u5728\u8fde\u6e56\u5cb8\u65c1\u6709\u591a\u5c11\u68f5\u6811\u4ed6\u51e0\u4e4e\u90fd\u5df2\u80fd\u6570\u5f97\u51fa\u6765\u4e86\u3002 \u3000\u3000\u524d\u9762\u7684\u5782\u67f3\u4e0b\xb7\u505c\u6cca\u6761\u5356\u83b2\u84ec\u9c9c\u85cf\u7684\u5c0f\u8239\u6447\u8239\u7684\u5c0f\u5993\u5a18\u4e5f\u5df2\u613f\u4ed6\u62a5\u6570\u8fdc\u8fdc\u5730\u5411\u4ed6\u5a7f\u7136\u800c \u7b11\uff0c\u7b11\u5bb9\u707f\u70c2\u5982\u9633\u5149\u3002 \u3000\u3000\u5c31\u53ea\u4e3a\u4e86\u8fd9\u751c\u7b11\u90ed\u5927\u8def\u5c31\u5df2\u4e0d\u80fd\u4e0d\u53bb\u4e70\u51e0\u652f\u83b2\u84ec\u3002 \u3000\u3000\u83b2\u5b50\u7684\u5fc3\u662f\u82e6\u7684\u5c31\u8c61\u73b0\u5728\u90ed\u5927\u8def\u7684\u5fc3\u6837\u3002 \u3000\u3000\u522b\u4eba\u4e24\u5206\u94f6\u5b50\u53ea\u80fd\u4e70\u516d\u53ea\u83b2\u84ec\u90ed\u5927\u8def\u5374\u4e70\u5230\u4e03\u516b\u53ea\u3002 \u3000\u3000\u8fd9\u6234\u6597\u7b7e\xb7\u8d64\u53cc\u767d\u8db3\u7684\u5c0f\u59d1\u59fb\u4eff\u4f5b\u5bf9\u90ed\u5927\u8def\u4e5f\u5f88\u6709\u610f\u601d\u53ea\u8981\u90ed\u5927\u8def\u6765\uff0c\u5979\u603b\u662f\u989d\u5916\u591a\u9001 \u4e24\u53ea\uff0c\u6709\u65f6\u751a\u81f3\u8fd8\u4f1a\u5077\u5077\u585e\u4e0a\u8282\u9c9c\u85d5\u3002 \u3000\u3000\u82e5\u662f\u5728\u4ee5\u524d\uff0c\u90ed\u5927\u8def\u8bf4\u4e0d\u5b9a\u65e9\u5df2\u5750\u4e0a\u5979\u7684\u8239\u628a\u8239\u8361\u5230\u6e56\u5fc3\u53bb\u4eb2\u4eb2\u5979\u82f9\u679c\u822c\u7684\u5c0f\u8138\u6478\u6478\u5979 \u5ae9\u85d5\u822c\u7684\u767d\u8db3\u4e86\u3002 \u3000\u3000\u4f46\u73b0\u5728\u90ed\u5927\u8def\u5b9e\u5728\u6ca1\u6709\u8fd9\u79cd\u5fc3\u60c5\u3002 \u3000\u3000\u4ed6\u7684\u70e6\u607c\u5df2\u591f\u591a\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u63a5\u83b2\u84ec\xb7\u51c6\u5907\u5b9a\u4e86\xb7\u8c01\u77e5\u9053\u5c0f\u59d0\u5374\u53c8\u5411\u4ed6\u62db\u4e86\u62db\u624b\u6084\u6084\u5730\u9053\u201c\u4f60\u8fc7\u6765\u6211\u6709\u8bdd\u8ddf\u4f60 \u8bf4\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u672c\u60f3\u518d\u60f9\u9ebb\u70e6\u5374\u53c8\u5b9e\u5728\u4e0d\u5fcd\u62d2\u7edd\u8fd9\u5c0f\u59cb\u5a18\u7684\u597d\u610f\u3002 \u3000\u3000\u4ed6\u5728\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\u96be\u5907\u4f5c\u51fa\u526f\u5927\u54e5\u54e5\u7684\u6837\u5b50\u6765\uff0c\u8fd9\u5c0f\u591f\u5a18\u82e5\u662f\u60f3\u7ea6\u4ed6\u5e7d\u4f1a\xb7\u4ed6\u5b9a\u8981\u597d\u597d \u6559\u8bad\u5979\u987f\uff0c\u544a\u8bc9\u5979\u5929\u535c\u7684\u7537\u4eba\u90fd\u4e0d\u662f\u597d\u4e1c\u897f\u5979\u5e78\u597d\u9047\u89c1\u4e86\u4ed6\u5426\u5219\u5b9a\u4f1a\u4e0a\u5f53\u7684\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\u4ed6\u89c9\u5f97\u81ea\u5df1\u7b80\u76f4\u662f\u4e2a\u5723\u4eba\u3002 \u3000\u3000\u53ea\u53ef\u63aa\u8001\u5929\u504f\u504f\u6c34\u7ed9\u4ed6\u4e2a\u673a\u4f1a\uff0c\u8ba9\u4ed6\u6765\u4f5c\u4e24\u6b21\u5723\u4eba\u3002 \u3000\u3000\u4ed6\u53ea\u7528\u53ea\u811a\u4e0a\u8239\u5934\u6545\u610f\u677f\u8d77\u8138\u9053\u201c\u4f60\u6709\u4ec0\u9ebd\u998f\u8981\u613f\u6211\u8bf4\uff1f\u201d \u3000\u3000\u5c0f\u59a8\u5a18\u773c\u775b\u91cc\u53d1\u5149\u6084\u6084\u9053\u201c\u4f60\u662f\u4e2d\u662f\u4e2a\u5316\u4e86\u88c5\u51fa\u6765\u79c1\u8bbf\u6c11\u60c5\u7684\u5927\u81e3\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u5316\u4e86\u6014\u4e86\u534a\u664c\u5fcd\u4e0d\u4f4f\u7b11\u9053\u201c\u6211\u7531\u5934\u5230\u811a\uff0c\u6709\u54ea\u70b9\u8c61\u662f\u5927\u5b98\u7684\u6837\u5b50\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u9053\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u975e\u4f46\u6c34\u662f\uff0c\u800c\u6c11\u6211\u89c1\u5230\u5927\u5b98\u5c31\u4f1a\u53d1\u6296\u7684\u3002\u201d \u3000\u3000\u5c0f\u591f\u5a18\u7684\u795e\u60c5\u66f4\u5174\u594b\u58f0\u97f3\u66f4\u4f4e\u9053\u201c\u90a3\u672a\u4f60\u5b9a\u662f\u4e2a\u5927\u5f3a\u642c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82eb\u7b11\u9053\u201c\u4e5f\u4e0d\u662f\u6211\u8fde\u505a\u5f3a\u76d7\u90fd\u4f1a\u8680\u672c\u7684\u3002\u201d \u3000\u3000\u5c0f\u8111\u5a18\u77aa\u4ed6\u9053\u201c\u4f60\u771f\u7684\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u9a97\u4f60\uff1f\u201d\u5c0f\u59d1\u5a18\u53f9\u4e86\u53e3\u6c14\u663e\u5f97\u5931\u671b\u6781\u4e86\xb7\u597d\u50cf\u8fde\u8bdd\u90fd\u61d2\u5f97\u642c\u4ed6\u518d \u8bf4\u3002 \u3000\u3000\u539f\u6765\u5979\u5bf9\u90ed\u5927\u8def\u6709\u5174\u8da3\u4ece\u4eca\u8fc7\u4ee5\u4e3a\u90ed\u5927\u8def\u662f\u4e2a\u5927\u76d7\u3002 \u3000\u3000\u5927\u7bee\u5728\u5c11\u5973\u4eec\u7684\u5fc3\u65e5\u4e2d\uff0c\u6709\u65f6\u7684\u786e\u6bd4\u5404\u79cd\u4eba\u90fd\u6709\u5438\u5f15\u529b\u3002 \u3000\u3000\u90ed\u5927\u8def\u73b0\u5728\u624d\u77e5\u9053\u8fd9\u5c0f\u59d1\u5a18\u5e76\u4e0d\u662f\u771f\u7684\u5bf9\u4ed6\u6709\u610f\u601d\u3002 \u3000\u3000\u4ed6\u4e5f\u7528\u4e0d\u518d\u62c5\u5fc3\u4f1a\u60f9\u4e0a\u9ebb\u70e6\u5382\uff0c\u672c\u6765\u5e94\u8be5\u89c9\u5f97\u5f88\u5f00\u5fc3\u624d\u662f\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u5382\u4ec0\u9ebd\u4ed6\u53cd\u800c\u504f\u504f\u89c9\u5f97\u6709\u70b9\u5931\u671b\u6709\u4e9b\u4e0d\u7518\u5fc3\u7684\u95ee\u201c\u4f60\u4ece\u54ea\u70b9\u770b\u6211\u8c61\u662f\u5927 \u76d7\uff1f\u201d \u3000\u3000\u5c0f\u591f\u5a18\u6001\u5ea6\u5df2\u51b7\u6de1\u4e86\u4e0b\u6765\u9053\u201c\u56e0\u4e3a\u8fd9\u4e24\u5929\u6765\xb7\u6211\u603b\u89c9\u5f97\u6709\u4e2a\u4eba\u5728\u5f8c\u9762\u76ef\u4f60\u7684\u68a2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u59a8\u5a18\u8c0a\u201c\u8fd9\u4eba\u6709\u65f6\u6253\u626e\u6210\u5c0f\u8d29\xb7\u6709\u65f6\u6253\u626e\u6210\u4e5e\u6cfb\uff0c\u4f46\u65e0\u8bba\u4ed6\u6253\u626e\u6210\u4ec0\u9ebd\u6837\u5b50\u90fd\u4f11\u60f3\u7792 \u8fc7\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5c0f\u8111\u5a18\u9732\u51fa\u5f88\u5f97\u610f\u7684\u6837\u5b50\u9053\u201c\u56e0\u4e3a\u4ed6\u7684\u80a1\u6211\u4e00\u773c\u5c31\u80fd\u591f\u8ba4\u51fa\u6765\u201c \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ed6\u8138\u662f\u4e0d\u662f\u6709\u4e0e\u4f17\u4e0d\u540c\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u70b9\u70b9\u5934\uff0c\u9053\u201c\u4ed6\u662f\u4e2a\u5927\u9ebb\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u8df3\u4e86\u8d77\u6765\u8fde\u8840\u90fd\u4f3c\u5df2\u6d41\u5f97\u5feb\u4e86\u5f88\u591a\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u770b\u4ed6\u65e5\u4e2d\u53c8\u9732\u51fa\u671f\u671b\u4e4b\u8272\uff0c\u9053\u201c\u4ed6\u662f\u4e0d\u662f\u6765\u76ef\u4f60\u68a2\u7684\uff1f\u4f60\u8ba4\u4e0d\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u4e86\u7728\u773c\u4e5f\u6545\u610f\u538b\u4f4e\u8bdd\u58f0\u9053\u201c\u6211\u8ddf\u4f60\u8bf4\u8001\u5b9e\u8bdd\u4f60\u53ef\u4e0d\u8bb8\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u7acb\u523b\u9053\u201c\u6211\u53d1\u8a93\u4e0d\u670d\u522b\u4eba\u8bf4\u5426\u5219\u4ee5\u5f8c\u53eb\u6211\u4e5f\u53d8\u6210\u4e2a\u5927\u9ebb\u5b50\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6084\u6084\u9053\u201c\u597d\u6211\u544a\u8bc9\u4f60\u90a3\u5927\u9ebb\u65bc\u662f\u4e2a\u5f88\u6709\u540d\u7684\u6355\u5934\uff0c\u7684\u786e\u662f\u6765\u76ef\u6211\u68a2\u7684\u3002\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u53c8\u5174\u594b\u4e86\u8d77\u6765\uff0c\u9053\u201c\u4ed6\u201d\u2026\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u76ef\u4f60\u7684\u68a2\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u58f0\u97f3\u66f4\u4f4e\u9053\u201c\u56e0\u4e3a\u6211\u7684\u786e\u662f\u4e2a\u5927\u76d7\u522b\u4eba\u90fd\u53eb\u6211\u5927\u76df\u6ee1\u5929\u5bbd\u2019\xb7\u521a\u5728\u4eac\u57ce\u91cc\u505a\u4e86\u4e03 \u5341\u516b\u4ef6\u5de8\u6848\u624d\u9003\u5230\u8fd9\u91cc\u6765\u907f\u98ce\u5934\u3002\u201d \u3000\u3000\u5c0f\u59d1\u59fb\u5174\u594b\u5f97\u5168\u8eab\u90fd\u53d1\u6296\u8d77\u6765\u54ac\u7792\u5507\uff0c\u9053\u201c\u4f60\u2026\u2026\u4f60\u662f\u4e2d\u662f\u4e2a\u91c7\u82b1\u76d7\u201c \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4f4f\u7b11\uff0c\u5411\u5979\u6324\u4e86\u6324\u773c\u775b\u9053\u201c\u4f60\u731c\u6211\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u7684\u8138\u5df2\u70eb\u5f97\u8c61\u662f\u4e2a\u521a\u70e4\u900f\u4e86\u7684\u7ea2\u5c71\u828b\u54ac\u9c9c\u7ea2\u5634\u5507\u9053\u201c\u5c31\u7b97\u4f60\u662f\u6211\u4e5f\u4e0d\u4f2f\u4f60\u6211\u2026\u2026 \u6211\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u817f\u8c61\u662f\u5df2\u6709\u70b9\u53d1\u8f6f\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u7a33\xb7\u51e0\u4e4e\u8e6c\u8dcc\u4e0b\u6c34\u91cc\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u5927\u7b11\u4f38\u624b\u6478\u4e86\u6478\u5979\u7684\u8138\uff0c\u9053\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u5c31\u7b97\u800d\u6765\u627e\u4f60\u4e5f\u5f97\u518d\u8fc7\u4e24\u53c1\u5e74\u73b0\u5728\u4f60\u53ea \u4e0d\u8fc7\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u5c0f\u59a8\u5a18\u770b\u4ed6\u53d1\u4e86\u534a\u5929\u6014\uff0c\u4e5f\u4e0d\u77e5\u662f\u6709\u610f\uff0c\u8fd8\u662f\u65e0\u610f\u5077\u5077\u7528\u624b\u78b0\u4e86\u78c1\u81ea\u5df1\u7684\u80f8\u8138\u4e0a\u7684\u7ea2\u971e \u5df2\u7ea2\u5230\u8033\u6735\u6839\u4e8e\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff0c\u77e5\u9053\u8fd9\u59d1\u5a18\u4eca\u5929\u665a\u535c\u4e00\u5b9a\u662f\u7761\u4e0d\u89c9\u7684 \u3000\u3000\u4ed6\u8fd9\u5012\u7edd\u4e0d\u662f\u5b58\u5fc3\u613f\u5bb3\u5979\xb7\u53ea\u4e0d\u8fc7\u662f\u60f3\u4e3a\u8fd9\u59d1\u5a18\u5e73\u51e1\u7684\u751f\u6dfb\u4e9b\u4f5c\u6599\u52a0\u4e9b\u8272\u5f69\u8ba9\u5979\u4ee5\u5f8c\u6210 \u4e86\u4eb2\u62b1\u5b69\u5b50\u94e3\u7897\u65f6\u4e5f\u4f1a\u6709\u6bb5\u53ef\u4ee5\u4ee4\u81ea\u5df1\u5fc3\u8df3\u7684\u56de\u5fc6\u6765\u60f3\u60f3\u3002 \u3000\u3000\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u5973\u63a5\u4e88\u80fd\u4eb2\u773c\u770b\u5230\u4e2a\u6d3b\u751f\u751f\u7684\u91c7\u82b1\u5927\u76d7\u5462\uff1f \u7b2c\u4e8c\u5341\u7ae0\u3000\u76ef\u634e\u7684\u9ebb\u5b50>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u5341\u7ae0\u3000\u76ef\u634e\u7684\u9ebb\u5b50 \u3000\u3000\u98ce\u5439\u5782\u67f3\uff0c\u5439\u8d77\u4e86\u6e56\u6c34\u4e2d\u9635\u6d9f\u7b4b\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd8\u662f\u6162\u6162\u7684\u5411\u524d\u8d70\uff0c\u9762\u5265\u83b2\u5b50\xb7\u9762\u54fc\u5c0f\u8c03\u3001 \u3000\u3000\u8d70\u5382\u4e0d\u7b97\u5f88\u8fd1\u7684\u6bb5\u8def\u4ed6\u624d\u5ffd\u7136\u56de\u5934\u3002 \u3000\u3000\u4ed6\u4e94\u523b\u53d1\u73b0\u6709\u4e2a\u624b\u91cc\u6367\u4e2a\u88ab\u7897\u7684\u4e5e\u5199\uff0c\u800c\u4e14\u679c\u7136\u662f\u4e2a\u9ebb\u5b50\u3002 \u3000\u3000\u4ed6\u56db\u5934\u8fd9\u9ebb\u4e25\u7acb\u523b\u8eb2\u5230\u6811\u5f8c\u3002 \u3000\u3000\u8fd9\u9ebb\u5b50\u76ef\u68a2\u7684\u6280\u672f\u5e76\u4e0d\u9ad8\u660e\u82e5\u4e0d\u662f\u90ed\u5927\u8def\u8fd9\u4e24\u5929\u603b\u662f\u5fc3\u4e0d\u5728\u7109\u80e1\u601d\u4e71\u60f3\uff0c\u65e9\u5c31\u5df2\u7ecf\u5e94\u8be5 \u53d1\u73b0\u4ed6\u4e86\u3002 \u3000\u3000\u8fd9\u9ebb\u65bc\u662f\u4e0d\u662f\u6c34\u67d4\u9752\u8bf4\u7684\u90a3\u4e2a\u9ebb\u5b50\uff1f \u3000\u3000\u90ed\u5927\u8def\u6709\u610f\u65e0\u610f\u95f4\u8f6c\u56de\u5934\xb7\u671d\u8fd9\u9ebb\u5b50\u8d70\u4e86\u8fc7\u53bb\u8d70\u5f97\u5f88\u6162\u3002 \u3000\u3000\u4ed6\u692d\u5907\u5feb\u8d70\u5230\u65f6\u518d\u4e0b\u4e8e\u8def\u8fc7\u53bb\u6293\u4f4f\u4ed6\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u9ebb\u4e8e\u5c45\u7136\u4e5f\u6709\u4e86\u8b66\u89c9\u7acb\u523b\u4e5f\u5f80\u56de\u5934\u7684\u8def\u8d70\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u811a\u6b65\u52a0\u5feb\uff0c\u4ed6\u7684\u811a\u6b65\u7acb\u523b\u4e5f\u52a0\u5feb\u3002 \u3000\u3000\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\xb7\u5728\u8fd9\u9ebd\u591a\u4eba\u7684\u9762\u524d\u82e5\u662f\u65bd\u5c55\u8d77\u8f7b\u529f\u672a\u514d\u6709\u70b9\u4e0d\u50cf\u8bdd\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u653e\u5927\u811a\u6b65\u5728\u5f8c\u9762\u8ffd\u3002 \u3000\u3000\u672c\u6765\u662f\u4ed6\u76ef\u90ed\u5927\u8def\u7684\u73b0\u5728\u53cd\u800c\u53d8\u6210\u90ed\u5927\u8def\u5728\u76ef\u4ed6\u7684\u68a2\u4e86\u3002 \u3000\u3000\u8239\u4e0a\u7684\u5c0f\u59d1\u5ac2\uff0c\u770b\u4ed6\u4eec\u524d\u4e00\u5f8c\u8dd1\u8fc7\u53bb\u6ee1\u8138\u90fd\u662f\u5403\u8c05\u4e4b\u8272\u3002 \u3000\u3000\u5979\u5b9e\u5728\u4e0d\u8c28\u4e3a\u4ec0\u9ebd\u6355\u5934\u4e0d\u53bb\u6293\u5f3a\u76d7\u5f3a\u76d7\u53cd\u800c\u8ffd\u6355\u5934\u3002 \u3000\u3000\u5bf9\u5979\u8bf4\u6765\uff0c\u8fd9\u4e16\u4e0a\u65e0\u6cd5\u89e3\u91ca\u7684\u4e8b\u5b9e\u5728\u592a\u591a\uff0c\u6240\u4ee5\u5979\u603b\u662f\u89c9\u5f97\u5f88\u70e6\u607c\u3002 \u3000\u3000\u7b49\u5979\u5e74\u7eaa\u6ca5\u6e10\u5927\u4e86\uff0c\u61c2\u5f97\u7684\u4e8b\u6e10\u6e10\u591a\u4e86\u5979\u624d\u660e\u767d\u8fd8\u662f\u4ee5\u524d\u4ec0\u9ebd\u90fd\u4e0d\u50f5\u7684\u65f6\u5019\u6d3b\u5f97\u5feb\u4e50 \u4e9b\u3002 \u3000\u3000\u521d\u590f\u6b63\u662f\u6e38\u6e56\u7684\u65f6\u5019\xb7\u6e56\u5cb8\u4e0a\u7ea2\u7537\u7eff\u5973\u6e38\u4eba\u5982\u7ec7\u3002 \u3000\u3000\u6e38\u5ba2\u591a\u7684\u5730\u65b9\u4e5e\u5de7\u81ea\u7136\u4e5f\u7279\u522b\u591a\u51fa\u6765\u73a9\u7684\u4eba\u51fa\u624b\u603b\u662f\u6bd4\u8f83\u5927\u65b9\u4e9b\u5c24\u5176\u662f\u5728\u8eab\u8845\u8fd8\u5e26\u4e2a\u5982 \u82b1\u7f8e\u7737\u7684\u65f6\u5019\u3002 \u3000\u3000\u6240\u4ee5\u4eba\u4e1b\u4e2d\u4e1c\u4e5f\u6709\u4e2a\u4e5e\u5f13\u897f\u4e5f\u6709\u4e2a\u4e5e\u5de7\u8fd9\u672c\u662f\u4ed6\u4eec\u7684\u8d2c\u5b63\u8fde\u6700\u998f\u7684\u4e5e\u5199\u90fd\u51fa\u52a8\u4e86\u3002 \u3000\u3000\u90a3\u9ebb\u5b50\u5728\u4eba\u4e1b\u4e2d\u94bb\u6765\u94bb\u53bb\u6709\u597d\u51e0\u6b21\u90ed\u5927\u8def\u90fd\u51e0\u4e4e\u88ab\u4ed6\u7529\u6389\u3002 \u3000\u3000\u5e78\u597d\u90ed\u5927\u8def\u7684\u8fd0\u6c14\u4e0d\u9519\u6bcf\u6b21\u5230\u7be1\u8981\u5173\u5934\xb7\u603b\u662f\u51d1\u5de7\u770b\u5230\u4e86\u4ed6\u8138\u4e0a\u7684\u9ebb\u5b50\u3002 \u3000\u3000\u76f8\u8c8c\u7279\u522b\u7684\u4eba\u672c\u5c31\u4e0d\u9002\u4e8e\u76ef\u522b\u4eba\u7684\u68a2\u3002 \u3000\u3000\u5230\u5f8c\u6765\u8fd9\u9ebb\u4e8e\u4f3c\u4e5f\u88ab\u904d\u5f97\u6025\u4e86\uff0c\u7d22\u6027\u79bb\u5f00\u4e86\u6e56\u533a\xb7\u5411\u5165\u5c11\u7684\u5730\u65b9\u8d70\u4f3c\u4e4e\u60f3\u5c06\u90ed\u5927\u8def\u8bf1\u5230 \u8352\u50fb\u65e0\u4eba\u5904\u597d\u597d\u4fee\u7406\u987f\u3002 \u3000\u3000\u90ed\u5927\u8def\u975e\u4f46\u2500\u70b9\u4e5f\u4e0d\u5728\u4e4e\u53cd\u800c\u8ffd\u5f97\u66f4\u8d77\u52b2\u3002 \u3000\u3000\u4ed6\u672c\u5c31\u60f3\u627e\u4e2a\u6295\u5165\u7684\u5730\u65b9\u6293\u4efb\u8fd9\u9ebb\u5b50\u95ee\u4e2a\u6e2d\u695a\u95ee\u95ee\u4ed6\u662f\u4e0d\u662f\u8ba4\u5f97\u71d5\u4e03\u77e5\u4e0d\u77e5\u9053\u71d5\u4e03\u7684\u4e0b \u843d\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u786e\u5df2\u4ece\u68cd\u5b50\u90a3\u91cc\xb7\u5b66\u4f1a\u5382\u51e0\u624b\u8981\u4eba\u8bf4\u5b9e\u8bdd\u7684\u672c\u4e8b\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4ee5\u4e3a\u5f88\u5feb\u5c31\u80fd\u8ffd\u4e0a\u8fd9\u9ebb\u5b50\u7684\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u9ebb\u5b50\u975e\u4f46\u8d70\u5f97\u5f88\u5feb\u4f53\u529b\u4e5f\u5f88\u597d\u5c31\u597d\u50cf\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u7d2f\u4f3c\u7684\u5c45\u7136\u8d8a\u6765\u8d8a\u5feb\u3002 \u3000\u3000\u90ed\u5927\u8def\u53cd\u9762\u89c9\u5f97\u6709\u70b9\u5403\u4e0d\u6d88\u4e86\u6700\u8fd1\u4ed6\u8fc7\u7684\u90a3\u79cd\u65e5\u5b50\uff0c\u8fc7\u5929\u5c31\u53ef\u4ee5\u4ee4\u4eba\u8005\u5e74\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u51fa\u6765\u5927\u58f0\u9053\u201c\u5582\u4f60\u522b\u8dd1\xb7\u6211\u5e76\u4e0d\u662f\u6765\u627e\u4f60\u9ebb\u70e6\u7684\u53ea\u4e0d\u8fc7\u6709\u51e0\u53e5\u8bdd\u60f3\u8981\u95ee\u95ee \u4f60\u3002\u201d \u3000\u3000\u8fd9\u9ebb\u4e8e\u672c\u6765\u6ca1\u6709\u771f\u7684\u8dd1\uff0c\u542c\u5230\u8fd9\u53e5\u8bdd\u53cd\u800c\u653e\u5f00\u811a\u6b65\u6bef\u5954\u5382\u8d77\u6765\u3002 \u3000\u3000\u4e5e\u5de7\u672c\u5c31\u5e38\u5e38\u4f1a\u88ab\u8ffd\u5f97\u6ee1\u8857\u4e71\u8dd1\u7684\xb7\u65e0\u8bba\u662f\u88ab\u4eba\u8ffd\u3001\u8fd8\u662f\u88ab\u72d7\u8ffd\u522b\u4eba\u770b\u5230\u90fd\u4e0d\u4f1a\u89c9\u5f97\u5947 \u602a\u3002 \u3000\u3000\u4f46\u2018\u4e2a\u7a7f\u5f97\u6574\u6574\u9f50\u9f50\u7684\u4eba\u5728\u8857\u4e0a\u8ffd\u4e2a\u4e5e\u5199\u4e71\u8dd1\u3001\u597d\u50cf\u5c31\u6709\u70b9\u4e0d\u50cf\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5df2\u6709\u4eba\u5f00\u59cb\u6ce8\u610f\u4ed6\u5176\u4e2d\u597d\u50cf\u8fd8\u6709\u4e24\u4e2a\u771f\u7684\u6355\u5feb\u3002 \u3000\u3000\u4ed6\u8ba2\u672c\u5c31\u662f\u5728\u9644\u8fd1\u5de1\u8fdb\u7684\uff0c\u8fd9\u65f6\u5df2\u51c6\u5907\u6765\u62e6\u4f4f\u90ed\u5927\u8def\u95ee\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u90ed\u5927\u8def\u53ea\u8981\u88ab\u4eba\u62e6\u8fd9\u9ebb\u4e8e\u7acb\u523b\u5c31\u4f1a\u8dd1\u5f97\u8e2a\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u552f\u7684\u7ebf\u7d22\uff0c\u4ed6\u7edd\u4e0d\u80fd\u8f7b\u6613\u653e\u8fc7\u3002 \u3000\u3000\u4ed6\u773c\u6b8a\u5b50\u8f6c\u7a81\u7136\u5148\u53d1\u5236\u4eba\u6307\u524d\u9762\u8dd1\u7684\u9ebb\u4e8e\u5927\u547c\u9053\u201c\u8fd9\u8981\u996d\u7684\u662f\u4e2a\u5c0f\u5077\xb7\u8c01\u5e2e\u6211\u6293\u4f50\u4ed6\u8d4f \u94f6\u4e8c\u5341\u4e24\u3002\u201d \u3000\u3000\u6700\u5f8c\u7684\u53e5\u8bdd\uff0c\u679c\u7136\u5f88\u6709\u6548\uff0c\u90a3\u4e24\u4e2a\u6355\u5feb\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\u5df2\u6389\u8f6c\u5934\u53bb\u8ffd\u90a3\u9ebb\u5b50\u3002 \u3000\u3000\u8fd8\u6709\u4e9b\u4eba\u4e5f\u5e2e\u5728\u65c1\u8fb9\u8d77\u54c4\u3002 \u3000\u3000\u8fd9\u9ebb\u4e88\u4f3c\u5df2\u771f\u7684\u4e01\u6025\u7a81\u7136\u4e00\u7eb5\u8eab\u4ece\u4e94\u516d\u4e2a\u4eba\u7684\u5934\u4e0a\u98de\u4e86\u8fc7\u53bb\u7a9c\u4e0a\u4e86\u524d\u9762\u7684\u623f\u810a\u3002 \u3000\u3000\u4ed6\u8f7b\u529f\u4e4b\u9ad8\uff0c\u5c45\u7136\u662f\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u6d41\u7684\u8eab\u624b\u3002 \u3000\u3000\u8fd9\u6765\u8fde\u4e0d\u60f3\u7ba1\u95f9\u4e8b\u7684\u4eba\u4e5f\u8d77\u4e86\u54c4\u201c\u770b\u6765\u8fd9\u4eba\u4e0d\u4f46\u662f\u4e2a\u5c0f\u5077\u8fd8\u662f\u4e2a\u98de\u8d3c\u4e0b\u4e07\u4e0d\u80fd\u8ba9\u4ed6\u6e9c \u4e86\u3002\u201d \u3000\u3000\u8d77\u54c4\u7684\u4eba\u867d\u591a\u4f46\u80fd\u4e0a\u623f\u53bb\u8ffd\u7684\u4eba\u5374\u8fde\u4e2a\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6355\u5feb\u4e5f\u53ea\u9489\u5728\u5899\u4e0b\u770b\u4e8e\u6025\u3002 \u3000\u3000\u8f7b\u529f\u6bd5\u7adf\u4e0d\u662f\u4eba\u4eba\u90fd\u5b66\u5f97\u4f1a\u8c61\u9ebb\u4e88\u8fd9\u6837\u7684\u8f7b\u529f\uff0c\u5341\u4e07\u4e2a\u4eba\u91cc\u9762\u6700\u591a\u4e5f\u53ea\u6709\u4e24\u4e2a\u80fd\u6bd4\u5f97 \u4e0a\u3002 \u3000\u3000\u5e78\u597d\u90ed\u5927\u8def\u5c31\u662f\u5176\u4e2d\u7684\u8fd9\u4e24\u4e2a\u3002 \u3000\u3000\u4ed6\u4e5f\u5df2\u63a0\u8fc7\u4eba\u7fa4\u7a9c\u4e0a\u623f\u5b50\u5634\u91cc\u8fd8\u5728\u5927\u558a\u5927\u53eb\u201c\u6211\u662f\u4eac\u57ce\u6765\u7684\u6355\u5934\u4e13\u7a0b\u6765\u6293\u8fd9\u98de\u8d3c\u7684\u4f46\u671b \u5404\u8def\u7684\u82f1\u96c4\u597d\u6c49\u52a9\u6211\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u65e0\u8bba\u54ea\u8def\u7684\u82f1\u96c4\u597d\u6c49\u90fd\u4e0d\u4f1a\u6765\u7ba1\u8fd9\u79cd\u83ab\u540d\u5176\u5999\u7684\u95f9\u4e8b\u3002 \u3000\u3000\u4ed6\u8fd9\u6837\u5927\u558a\u5927\u53eb\u53ea\u4e2d\u8fc7\u60f3\u53eb\u5f97\u8fd9\u9ebb\u5b50\u5fc3\u614c\u610f\u4e71\u800c\u5df2\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5b9e\u5728\u6ca1\u628a\u63e1\u80fd\u8ffd\u4e0a\u8fd9\u9ebb\u4e8e\u8f7b\u529f\u4ed6\u867d\u7136\u7ec3\u5f97\u4e0d\u9519\u4f97\u5b9e\u4e60\u7684\u673a\u4f1a\u5374\u4e0d\u591a\u65e0\u8bba\u6280\u5de7\u548c\u7ecf \u9a8c\u597d\u50cf\u90fd\u6bd4\u8fd9\u9ebb\u5b50\u5dee\u4e86\u622a\u3002 \u3000\u3000\u8fd9\u9ebb\u4e86\u679c\u7136\u662f\u88ab\u4ed6\u53eb\u5f97\u6709\u70b9\u5fc3\u865a\u4e86\u3002 \u3000\u3000\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\u5728\u522b\u4eba\u7684\u623f\u7a46\u4ea1\u98de\u6765\uff1a\u53bb\uff0c\u8fd9\u76ee\u6807\u4e5f\u7684\u786e\u592a\u5927\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u53c8\u88ab\u903c\u5f97\u8e22\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0b\u9762\u662f\u6761\u5e76\u4e0d\u7b97\u5f88\u5bbd\u7684\u5df7\u5b50\u5171\u53ea\u4e0d\u8fc7\u6709\u516d\u4e03\u6237\u4eba\u5bb6\u3002 \u3000\u3000\u90ed\u5927\u8def\u8d76\u8fc7\u6765\u7684\u65f6\u5019\u521a\u5de7\u77a5\u89c1\u4ed6\u4eba\u5f71\u95ea\u95ea\u5165\u5382\u5df7\u53e3\u5bb6\u4eba\u7684\u5927\u95e8\u91cc\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u7684\u5927\u95e8\u5c45\u7136\u662f\u5f00\u7684\u3002 \u3000\u3000\u65e0\u8bba\u5728\u591a\u592a\u5e73\u7684\u5e74\u5934\uff0c\u7ec8\u65e5\u5f00\u5927\u95e8\u7684\u4eba\u5bb6\u4e5f\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u60f3\u5fc5\u548c\u8fd9\u9ebb\u5b50\u6709\u5173\u7cfb\u8bf4\u4e0d\u5b9a\u8fd9\u5730\u65b9\u5c31\u662f\u4ed6\u81ea\u5df1\u7684\u5bb6\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e0d\u7ba1\u53c1\u4e03\u4e8c\u5341\xb7\u7acb\u523b\u4e5f\u8ddf\u95ef\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u9662\u5b50\u91cc\u6ca1\u6709\u4eba\u524d\u9762\u7684\u5ba2\u5385\u91cc\uff0c\u5374\u6709\u4eba\u6b63\u5728\u7b11\u8bf4\u201c\u96be\u602a\u522b\u4eba\u603b\u662f\u8bf4\u5341\u4e2a\u9ebb\u4e8e\u4e5d\u4e2a\u602a\uff0c\u4f60\u679c \u7136\u771f\u662f\u5996\u602a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5927\u559c\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u4e0b\u5b50\u4f60\u603b\u6e9c\u4e0d\u6389\u4e86\u5427\u3002\u201d \u3000\u3000\u8c01\u77e5\u5ba2\u5385\u91cc\u5374\u8fde\u534a\u4e2a\u9ebb\u5b50\u90fd\u6ca1\u6709\u53ea\u6709\u4e00\u7537\u5973\uff0c\u597d\u50cf\u662f\u5bf9\u592b\u59bb\xb7\u6b63\u5728\u90a3\u91cc\u6253\u60c5\u9a82\u4fcf\u5973\u7684\u767d \u767d\u80d6\u80d6\u957f\u5f97\u5f88\u6807\u81f4\u7537\u7684\u5374\u662f\u9762\u9ec4\u808c\u7626\uff0c\u8fde\u8170\u90fd\u6709\u70b9\u4f38\u4e0d\u76f4\u4e86\u3002 \u3000\u3000\u7537\u4eba\u82e5\u8981\u4e86\u4e2a\u592a\u6807\u81f4\u7684\u8001\u5a46\u6709\u65f6\u4e5f\u4e0d\u80fd\u7b97\u662f\u597d\u798f\u6c14\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u5230\u5916\u9762\u7a81\u7136\u6709\u6761\u5927\u6c49\u95ef\u8fdb\u6765\xb7\u4e5f\u5403\u4e86\u60ca\u3002 \u3000\u3000\u707e\u592b\u7684\u80c6\u5b50\u597d\u50cf\u6bd4\u592a\u592a\u8fd8\u5c0f\xb7\u5413\u5f97\u51e0\u4e4e\u8dcc\u5012\u5728\u592a\u592a\u8eab\u4e0a\u4e86\u5403\u5403\u9053\u201c\u4f60\u2026\u2500\u4f60\u662f\u8c01\uff1f\u60f3\u6765 \u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6765\u627e\u4eba\u3002\u201d \u3000\u3000\u4e08\u592b\u9053\u201c\u627e\u2026\u2500\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6765\u627e\u4e2a\u9ebb\u4e8e\u4f60\u521a\u6240\u8bf4\u7684\u9ebb\u4e8e\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u592a\u5927\u53cc\u6c34\u6dcb\u6dcb\u7684\u773c\u775b\u672c\u5c31\u76f4\u5728\u8198\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u62a2\u9053\u201c\u4ed6\u521a\u300f\u8bf4\u7684\u9ebb\u5343\u5c31\u662f\u6211\u4f60\u96be\u9053\u662f \u6765\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u5979\u9f3b\u5c16\u4e0a\u679c\u7136\u6709\u51e0\u70b9\u6d45\u767d\u9ebb\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u4f50\u3002 \u3000\u3000\u8fd9\u4f4d\u592a\u592a\u8fd8\u662f\u7528\u773c\u89d2\u8198\u4ed6\u4f3c\u7b11\u975e\u7b11\u7684\xb7\u53c8\u9053\u201c\u4f60\u662f\u4e0d\u662f\u6155\u540d\u6765\u627e\u6211\u7684\uff1f\u53ea\u53ef\u60dc\u4f60\u6765\u8fdf\u4e86 \u73b0\u5728\u6211\u5df2\u7ecf\u5ac1\u4e86\u4eba\uff0c\u4e0d\u63a5\u5ba2\u4e86\u3002\u201d \u3000\u3000\u90ed\u4eba\u8def\u975e\u4f46\u6014\u4f7f\u7b80\u76f4\u5df2\u6709\u70b9\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u65e9\u5c31\u8be5\u770b\u51fa\u6765\u771f\u6b63\u7684\u826f\u5bb6\u5987\u5973\u54ea\u6709\u50cf\u5979\u8fd9\u6837\u5b50\u770b\u7537\u4eba\u7684\uff1f \u3000\u3000\u505a\u4e08\u592b\u7684\u7ec8\u4e8e\u53d1\u5a01\u4e86\u8df3\u8d77\u6765\u5927\u58f0\u9053\u201c\u4f60\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\u5979\u73b0\u5728\u5df2\u7ecf\u662f\u6211\u8001\u5a46\u8c01\u4e5f\u4f11\u60f3\u518d\u52a8 \u5979\u7684\u8111\u7b4b\u4f60\u8fd8\u4e0d\u51fa\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53ea\u6709\u82eb\u7b11\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u521a\u6ca1\u6709\u522b\u7684\u4eba\u8fdb\u6765\u8fc7\uff1f\u201d \u3000\u3000\u592a\u592a\u53c8\u8198\u4e86\u4ed6\u773c\uff0c\u7b11\u9053\u201c\u57ce\u91cc\u5c31\u7b97\u8fd8\u6709\u4f60\u8fd9\u6837\u7684\u5192\u5931\u9b3c\u2606\u4e5f\u6ca1\u6709\u4f60\u8fd9\u9ebd\u5927\u7684\u80c6\u5b50\u8c01\u6562\u5230 \u522b\u4eba\u5bb6\u91cc\u6765\u627e\u522b\u4eba\u7684\u8001\u5a46\uff1f\u201d \u3000\u3000\u5979\u6c11\u7136\u8ba4\u5b9a\u4ed6\u662f\u4e2a\u7279\u5730\u6765\u627e\u5979\u7684\u767b\u5f92\u5b50\u4e86\u3002 \u3000\u3000\u505a\u6587\u592b\u7684\u706b\u6c14\u66f4\u5927\u6307\u90ed\u5927\u8def\u7684\u9f3b\u5b50\xb7\u5927\u53eb\u9053\u201c\u4f60\u8fd8\u4e0d\u51fa\u53bb\uff1f\u8fd8\u5728\u8fd9\u91cc\u6253\u4ec0\u9ebd\u7cca\u6d82\u5fc3\u601d\uff1f \u5c0f\u5fc3\u6211\u4e00\u62f3\u6253\u7834\u4f60\u7684\u5934\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u624b\u770b\u6765\u7b80\u76f4\u5c31\u8c61\u662f\u4e2a\u9e21\u722a\u4e8e\xb7\u8fde\u82cd\u8747\u90fd\u672a\u5fc5\u6253\u5f97\u6b7b\u5c45\u7136\u8fd8\u60f3\u6253\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u62a5\u5382\u62db\u4ed6\u7684\u80a9\u7b11\u9053\u201c\u4f60\u653e\u5fc3\u6ca1\u6709\u4eba\u4f1a\u6765\u67aa\u4f60\u7684\u8001\u5a46\u4f69\u4f60\u81ea\u5df1\u7684\u8eab\u4f53\u4e5f\u4e0d\u662f\u5077\u6765 \u7684\xb7\u8fd8\u662f\u4fdd\u91cd\u4e9b\u597d\uff0c\u65e0\u8bba\u4ec0\u9ebd\u4e8b\u90fd\u7528\u6c34\u592a\u5356\u529b\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u8fd9\u4eba\u518d\u5f00\u53e3\xb7\u5c31\u5df2\u8f6c\u8fc7\u8eab\xb7\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u672a\u514d\u6709\u70b9\u7f3a\u5fb7\uff0c\u5e73\u65f6\u4ed6\u7edd\u4e0d\u4f1a\u8bf4\u8fd9\u79cd\u8bdd\u7684\u3002 \u3000\u3000\u4f46\u4e2a\u4eba\u81ea\u5df1\u5fc3\u91cc\u607c\u706b\u7684\u65f6\u5019\xb7\u5f80\u5f80\u5c31\u60f3\u8981\u522b\u4eba\u4e5f\u96be\u53d7\u4e0b\u5b50\u3002 \u3000\u3000\u4ed6\u660e\u660e\u770b\u5230\u9ebb\u4e8e\u8fdb\u6765\u7684\u600e\u9ebd\u4f1a\u7a81\u7136\u4e0d\u89c1\u96be\u9053\u8fdb\u95e8\u5c31\u94bb\u5230\u5730\u4e0b\u53bb\u4e86\uff1f \u3000\u3000\u8fd9\u592b\u59bb\u4e24\u4eba\uff0c\u5f53\u7136\u662f\u65e9\u5c31\u8ddf\u90a3\u9ebb\u4e88\u4e32\u901a\u597d\u5531\u53cc\u8d38\u7ed9\u4ed6\u770b\u7684\u3002 \u3000\u3000\u4ed6\u660e\u660e\u77e5\u9053\uff0c\u5374\u504f\u504f\u8bbe\u6cd5\u5b50\u63ed\u7a7f\uff0c\u4f55\u51b5\u9752\u5929\u767d\u65e5\u7684\u695e\u5f80\u4eba\u5bb6\u5c4b\u4e8e\u91cc\u95ef\u4e5f\u7a76\u7adf\u662f\u81ea\u5df1\u7406 \u4e8f\u3002 \u3000\u3000\u82e5\u8981\u4ed6\u903c\u522b\u4eba\u5e26\u4ed6\u4e00\u95f4\u95f4\u5c4b\u4e8e\u91cc\u53bb\u641c\u67e5\u4ed6\u4e5f\u505a\u4e0d\u51fa\u6765\uff0c \u3000\u3000\u4f55\u51b5\u90a3\u9ebb\u4e8e\u5f53\u7136\u65e9\u5df2\u4e58\u673a\u6e9c\u4e86\xb7\u4ed6\u5c31\u53bb\u627e\u4e5f\u5b9a\u627e\u4e0d\u5230\u7684\u3002 \u3000\u3000\u90ed\u4eba\u8def\u60f3\u6765\u60f3\u53bb\u8d8a\u60f3\u8d8a\u7a9d\u56ca\u3002 \u3000\u3000\u201c\u82e5\u662f\u6362\u77ff\u738b\u52a8\u90a3\u9ebb\u4e8e\u4eca\u5929\u5c31\u4f11\u60f3\u80fd\u6e9c\u5f97\u6389\u3002\u201d \u3000\u3000\u4ed6\u51b3\u5b9a\u5148\u627e\u4e2a\u5730\u65b9\u53bb\u5927\u5403\u5927\u559d\u987f\xb7\u5b89\u6170\u5b89\u6170\u81ea\u5df1\u665a\u535c\u518d\u5230\u8fd9\u9644\u8fd1\u6765\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u4ed6\u5df2\u51b3\u5fc3\u5728\u8fd9\u91cc\u6ce1\u4e86\u672f\u627e\u5230\u90a3\u9ebb\u4e8e\u7edd\u4e0d\u5584\u7f62\u9996\u4f11\u3002 \u3000\u3000\u592a\u9633\u5df2\u7ecf\u5feb\u4e0b\u5c71\u5382\u73b0\u5728\u5f00\u59cb\u559d\u9152\u5df2\u4e0d\u80fd\u7b97\u662f\u592a\u65e9\u3002 \u3000\u3000\u57ce\u91cc\u6700\u5927\u7684\u996d\u9986\u53eb\u4f1a\u5bbe\u697c\xb7\u9e2d\u53c1\u5403\u548c\u6d3b\u6740\u9c81\u9c7c\u662f\u4ed6\u4eec\u7684\u62db\u724c\u83dc\u4ece\u6c7e\u9633\u6765\u7684\u6c7e\u9152\u559d\u4e0b\u53bb\u4e5f \u76d7\u6709\u52b2\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u627e\u4e86\u5f20\u4e34\u7a97\u7684\u684c\u4e8e\uff0c\u53eb\u4e86\u684c\u5b50\u83b1\u3002 \u3000\u3000\u4e34\u8d70\u7684\u65f6\u5019\u4e1c\u57ce\u8001\u5927\u5b9e\u9001\u4ed6\u7b14\u76d8\u7f20\uff0c\u8fd9\u4e9b\u752b\u5e76\u4e2d\u7684\u6e38\u4fa0\u513f\u6709\u65f6\u7684\u786e\u6bd4\u6c5f\u6e56\u8c6a\u6770\u8fd8\u4e49\u6c14\u8fd8 \u591f\u670b\u53cb\u3002 \u3000\u3000\u5e73\u65f6\u53ea\u8981\u51e0\u676f\u9152\u4e0b\u809a\u90ed\u5927\u8def\u7684\u5fc3\u60c5\u7acb\u523b\u5c31\u4f1a\u5f00\u6717\u8d77\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u5929\u9152\u5230\u5634\u91cc\u5374\u597d\u50cf\u662f\u82e6\u7684\u800c\u4e14\u7279\u522b\u5bb9\u6613\u9189\u3002 \u3000\u3000\u65e2\u7136\u665a\u4e0a\u8fd8\u6709\u4e8b\u4ed6\u4e5f\u4e0d\u6562\u591a\u559d\u53ea\u6709\u62fc\u547d\u5403\u83dc\u4ed6\u7684\u5fc3\u60c5\u8d8a\u574f\uff0c\u5403\u5f97\u8d8a\u591a\uff0c\u82e5\u662f\u518d\u6211\u4e0d\u5230\u9893 \u4e03\uff0c\u4ed6\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u53d8\u5f97\u6bd4\u8fd9\u586b\u9e2d\u8fd8\u80a5\u3002 \u3000\u3000\u592a\u9633\u4e0b\u5c71\u5f8c\u996d\u9986\u91cc\u5c31\u6e10\u6e10\u5f00\u59cb\u4ea1\u5ea7\u4e86\xb7\u5404\u5f0f\u5404\u6837\u7684\u4eba\u5ddd\u6d41\u4e0d\u606f\u7684\u4e0a\u697c\u6765\uff0c\u5176\u4e2d\u8fd8\u6709\u8e7f\u5934 \u9f20\u76ee\u7684\u9f9f\u5974\u5e26\u82b1\u6280\u62db\u5c55\u7684\u76fc\u5934\u6765\u5e94\u7a96\u707f\u7684\u6761\u4e8e\u3002 \u3000\u3000\u65bc\u662f\uff0c\u65c1\u8fb9\u7528\u5c4f\u98ce\u8e0a\u8d77\u6765\u7684\u96c5\u5ea7\u91cc\uff0c\u53c8\u54cd\u8d77\u4e86\u4e1d\u7af9\u58f0\u3001\u6b4c\u66f2\u58f0\u3001\u8c03\u7b11\u58f0\u3001\u78b0\u676f\u58f0\u5939\u6742\u547c \u547d\u559d\u7ef4\u58f0\u3001\u731c\u62f3\u884c\u4ee4\u58f0\xb7\u5b9e\u5728\u70ed\u95f9\u6781\u4e86\u3002 \u3000\u3000\u4f97\u90ed\u5927\u8def\u5374\u597d\u50cf\u5750\u5728\u53e6\u2026\u4e2a\u4e16\u754c\u91cc\xb7\u8fd9\u4ef6\u4e8b\u672c\u6765\u662f\u4ed6\u6700\u611f\u5174\u8da3\u7684\xb7\u4f46\u73b0\u5728\u5374\u89c9\u5f97\u70b9\u610f\u601d \u90fd\u6ca1\u6709\u3002\u4f46\u73b0\u5728\u5374\u89c9\u5f97\u70b9\u610f\u601d\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6ca1\u6709\u71d5\u4e03\u5728\u65c1\u8fb9\u5c31\u597d\u50cf\u83b1\u91cc\u6ca1\u6709\u76d0\u6837\xb7\u7d22\u7136\u65e0\u5473\u3002 \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\u6162\u8c29\u7684\u66ff\u81ea\u5df1\u659f\u4e86\u676f\u9152\uff0c\u5ffd\u7136\u770b\u5230\u4e94\u516d\u4e2a\u5f88\u6807\u81f4\u7684\u5c0f\u7ad9\u5a18\uff0c\u62e5\u4e2a\u9526\u8863\u4f69\u5251\u7684 \u5927\u6c49\xb7\u563b\u563b\u54c8\u54c8\u7684\u4e0a\u4e86\u697c\u3002 \u3000\u3000\u83ab\u8bf4\u662f\u5e97\u91cc\u7684\u5925\u8ba1\u8fde\u90ed\u5927\u8def\u90fd\u770b\u51fa\uff0c\u8fd9\u9526\u8863\u5927\u6c49\u662f\u4e2a\u6325\u91d1\u5982\u571f\u7684\u8c6a\u5ba2\u624b\u9762\u5fc5\u5b9a\u4e0d\u4f1a\u5c0f\u3002 \u3000\u3000\u4ed6\u4e5f\u5fcd\u4e0d\u4f4f\u591a\u77a7\u4e86\u773c\u8fd9\u773c\u77a7\u8fc7\xb7\u4ed6\u624b\u91cc\u7684\u9152\u58f6\u90fd\u51e0\u4e4e\u8dcc\u4e86\u535c\u6765\u3002 \u3000\u3000\u8fd9\u9526\u8863\u8c6a\u5ba2\u7adf\u7136\u662f\u4e2a\u9ebb\u5b50\u800c\u4e14\u6b63\u662f\u521a\u5728\u6e56\u7554\u8981\u996d\u7684\u90a3\u9ebb\u4e86\u3002\u4e0b\u5348\u8fd8\u662f\u4e2a\u4e5e\u5199\uff0c\u665a\u4e0a\u5c31\u53d8 \u6210\u4e86\u9614\u4f6c\xb7\u8fd9\u53d8\u5b9e\u5728\u53d8\u5f97\u592a\u5389\u5bb3\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u4ed6\u600e\u9ebd\u53d8\u5c31\u7b97\u4ed6\u53d8\u6210\u4e86\u7070\u90ed\u5927\u8def\u8fd8\u662f\u773c\u5c31\u8ba4\u51fa\u4e86\u4ed6\u6765\u3002 \u3000\u3000\u8c01\u53eb\u4ed6\u8138\u4e0a\u7684\u9ebb\u4e8e\u8fd9\u9ebd\u591a\u7684\uff1f \u3000\u3000\u90ed\u5927\u8def\u53ea\u770b\u4e86\u4e24\u773c\u5c31\u7acb\u5230\u626d\u8fc7\u5934\u53bb\u770b\u7a97\u5b50\u5916\u7684\u62db\u724c\u3002\u8fd9\u6b21\u4ed6\u51b3\u5b9a\u5148\u6c89\u4efb\u6c14\uff0c\u7edd\u4e0d\u518d\u8f7b\u4e3e \u5984\u52a8\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u82e5\u8d70\u8fc7\u53bb\xb7\u628a\u63ea\u4f4f\u90a3\u9ebb\u5b50\u95ee\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u9001\u73cd\u73e0\u7ed9\u6c34\u67d4\u9752\uff0c\u95ee\u4ed6\u77e5\u4e0d\u77e5\u9053\u71d5\u4e03\u7684\u5382 \u843d\xb7\u522b\u4eba\u5b9a\u4f1a\u8ba4\u4e3a\u4ed6\u662f\u4e2a\u75af\u5b50\u90a3\u9ebb\u4e8e\u5f53\u7136\u4e5f\u53ef\u4ee5\u95ee\u53c1\u4e0d\u77e5\u628a\u4ec0\u9ebd\u4e8b\u90fd\u63a8\u5f97\u5e72\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u9ebb\u4e8e\u4e5f\u8fdb\u4e86\u96c5\u5ea7\u3002 \u3000\u3000\u8ddf\u4ed6\u9f50\u6765\u7684\u5973\u5ba2\u663e\u7136\u4e5f\u4e0d\u662f\u826f\u5bb6\u5987\u5973\u8fd8\u6ca1\u8fc7\u591a\u4e45\xb7\u5c31\u5728\u91cc\u9762\u5531\u4e86\u8d77\u6765\uff0c\u53c8\u662f\u201c\u5c0f\u51a4\u5bb6\u201d \u53c8\u662f\u201c\u4eb2\u54e5\u54e5\u201d\u7684\xb7\u7b80\u76f4\u62ff\u8089\u9ebb\u5f53\u6709\u8da3\u3002 \u3000\u3000\u5947\u602a\u7684\u4e8b\u3001\u4e16\u535c\u504f\u504f\u5c31\u6709\u5f88\u591a\u7537\u4eba\u559c\u6b22\u8fd9\u79cd\u8c03\u8c03\u513f\u3002 \u3000\u3000\u51ed\u826f\u5fc3\u8bf4\u90ed\u5927\u8def\u672c\u6765\u4e5f\u86ee\u559c\u6b22\u7684\u4f46\u73b0\u5728\u5374\u542c\u5f97\u5168\u8eab\u90fd\u8d77\u5382\u9e21\u76ae\u7599\u7629\u3002 \u3000\u3000\u4e2a\u4eba\u662f\u5426\u56e0\u7231\u800c\u6539\u53d8\uff0c\u5176\u5173\u952e\u5e76\u4e0d\u5728\u4ed6\u662f\u7537\u662f\u5973\xb7\u53ea\u770b\u4ed6\u770b\u5f97\u591f\u4e0d\u591f\u771f\u5b9e\u591f\u4e0d\u591f\u6df1\u5207\u3002 \u3000\u3000\u9152\u697c\u4e0a\u8fd8\u70ed\u95f9\u5f97\u5f88\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u53eb\u4e86\u58f6\u9152\xb7\u6dfb\u4e86\u6837\u83dc\uff0c\u5df2\u51c6\u5907\u957f\u671f\u4f5c\u6218\u90a3\u9ebb\u5b50\u5c31\u7b97\u8981\u559d\u5230\u5929\u4eae\xb7\u4ed6\u4e5f\u4f1a\u6c89\u4f4f\u6c14 \u7b49\u5230\u5927\u4eae\u3002 \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u9f99\u738b\u5e99>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u9f99\u738b\u5e99 \u3000\u3000\u8c01\u77e5\u8fd9\u9ebb\u4e8e\u5c45\u7136\u5f88\u5feb\u5c31\u51fa\u6765\u4e86\u5df2\u559d\u5f97\u9189\u7530\u5708\u7684\u6276\u4e2a\u5341\u4e03\u516b\u5c81\u5c11\u5973\u7684\u80a9\uff0c\u5927\u58f0\u95ee\u5925\u8ba1\uff0c\u6d17 \u624b\u7684\u5730\u65b9\u5728\u54ea\u91cc\u3002 \u3000\u3000\u539f\u6765\u4ed6\u559d\u5f97\u592a\u591a\uff0c\u60f3\u627e\u6761\u51fa\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u6c89\u4f4f\u6c14\xb7\u770b\u4ed6\u4e0b\u4e86\u697c\u7b49\u4e2d\u5929\u4e5f\u6ca1\u770b\u89c1\u4ed6\u4e0a\u6765\u3002 \u3000\u3000\u83ab\u975e\u4ed6\u5df2\u53d1\u73b0\u4e86\u6211\u5728\u8fd9\u91cc\u4e58\u673a\u501f\u5c3f\u9041\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u6b63\u6e2f\u5907\u8ffd\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u4ed6\u773c\u89d2\u5df2\u77a5\u89c1\u8857\u5bf9\u9762\u6709\u4e2a\u4eba\u4f4e\u5934\u5f80\u524d\u8d70\u6b63\u662f\u8fd9\u9ebb \u3000\u3000\u4ed6\u679c\u7136\u6e9c\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u6025\u4eba\u5df2\u4ece\u7a97\u5b50\u91cc\u7a9c\u4e86\u51fa\u53bb\u9152\u7a96\u4e2d\u5df2\u6709\u4eba\u5927\u53eb\u8d77\u6765\u8fd8\u4ee5\u4e3a\u8fd9\u4eba\u60f3\u8df3\u697c\u81ea\u6740\u3002 \u3000\u3000\u90a3\u9ebb\u4e8e\u4e5f\u56de\u5934\u8198\u4e86\u773c\u8eab\u5b50\u95ea\u5ffd\u7136\u94bb\u8fdb\u4e86\u5bf9\u9762\u5bb6\u7cae\u98df \u3000\u3000\u7cae\u98df\u574a\u7684\u95e8\u53e3\u5806\u53e3\u888b\u95e8\u888b\u7684\u9762\uff0c\u7b50\u5b50\u7b50\u5b50\u7684\u7c73\u3001\u5c0f\u7c73\u3001\u6742\u7cae\u8fd8\u6709\u6d41\u9f3b\u6d95\u7684\u987d\u7ae5\u6b63\u5728\u95e8\u95e8 \u8e22\u7a57\u5b50\u3002 \u3000\u3000\u7b49\u90ed\u5927\u8def\u8d76\u8fc7\u4f17\u7684\u65f6\u5019\u90a3\u9ebb\u4e8e\u53c8\u4eba\u5f71\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5e97\u91cc\u7684\u5925\u8ba1\u548c\u638c\u67dc\u7684\uff0c\u95f9\u6ca1\u4e8b\u505a\uff0c\u6b63\u501a\u67dc\u53f0\u5728\u4e0b\u71c3\uff0c \u3000\u3000\u770b\u4ed6\u4eec\u60a0\u60a0\u56e0\u9611\u7684\u6837\u5b50\xb7\u7edd\u4e0d\u8c61\u521a\u770b\u5230\u6709\u4eba\u95ef\u8fdb\u53bb\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u83ab\u975e\u4e5f\u548c\u90a3\u9ebb\u4e8e\u4e32\u901a\u597d\u4e86\u3001\u51c6\u5907\u6f14\u51fa\u53cc\u7c27\u7ed9\u90ed\u5927\u8def\u770b\uff1f \u3000\u3000\u4f46\u90ed\u5927\u8def\u8fd9\u6b21\u5374\u5b66\u4e56\u5382\u6839\u672c\u5c31\u4e0d\u8fdb\u53bb\u95ee\u5374\u8eb2\u5728\u65c1\u8fb9\xb7\u62a5\u624b\u5c06\u90a3\u4e2a\u6d41\u9f3b\u6d95\u7684\u5c0f\u5b69\u5b50\u53eb\u4e86\u8fc7 \u6765\xb7\u6478\u51fa\u4e32\u94dc\u94b1\u5e26\u7b11\u9053\u201c\u6211\u95ee\u4f60\u7684\u8bdd\xb7\u4f60\u82e5\u4e56\u4e56\u7684\u56de\u7b54\uff0c\u6211\u5c31\u628a\u8fd9\u4e2d\u94b1\u7ed9\u4f60\u4e70\u7ce0\u5403\u3002\u201d \u3000\u3000\u8fd9\u5c0f\u5b69\u2026\u53ea\u624b\u62ff\u7a46\u5b50\xb7\u53ea\u624b\u64e6\u9f3b\u6d95\xb7\u773c\u775b\u5374\u5df2\u76ef\u5728\u8fd9\u4e32\u94b1\u4e0a\u3002\u6b22\u4e50\u82f1\u9192 \u3000\u3000\u65e0\u8bba\u662f\u5927\u4eba\u4e5f\u597d\u662f\u5c0f\u5b69\u4e5f\u597d\u770b\u89c1\u94b1\u4e0d\u559c\u6b22\u7684\u6015\u8fd8\u6ca1\u6709\u51e0\u4e2a\u3002 \u3000\u3000\u90ed\u5927\u8def\u8c0a\u201c\u4f60\u542c\u660e\u767d\u4e86\u5417\uff1f\u53ea\u8981\u4f60\u8bf4\u5b9e\u8bdd\u8fd9\u4e32\u94b1\u5c31\u662f\u4f60\u7684\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u652f\u523b\u7528\u529b\u70b9\u5934\uff0c\u9053\u201c\u6211\u8bf4\u7684\u90fd\u662f\u5b9e\u8bdd\u7239\u7239\u544a\u8bc9\u6211\u5c0f\u5b69\u5b50\u82e5\u662f\u8bf4\u8c0e\uff0c\u5c06\u6765\u820c\u5934\u4f1a\u70c2 \u6389\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u62cd\u4e86\u62cd\u4ed6\u7684\u5934\u7b11\u9053\u201c\u4e0d\u9519\u8bf4\u5b9e\u8bdd\u7684\u624d\u662f\u597d\u5b69\u5b50\xb7\u8fd9\u7cae\u98df\u574a\u662f\u4e0d\u662f\u4f60\u5bb6\u5f00\u7684\u201d \u3000\u3000\u5b69\u5b50\u70b9\u70b9\u5934\uff0c\u9053\u201c\u6211\u4eec\u5bb6\u6709\u597d\u591a\u597d\u591a\u5927\u5185\u7c73\xb7\u5403\u767e\u5e74\u90fd\u5403\u4e0d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4eec\u5bb6\u91cc\u662f\u4e0d\u662f\u8fd8\u6709\u4e2a\u9ebb\u5b50\u201d \u3000\u3000\u5b69\u5343\u7728\u7728\u773c\u597d\u50cf\u89c9\u5f97\u72e0\u5947\u602a\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u5382\u8981\u9a97\u51fa\u4e2a\u5c0f\u5b69\u4e8e\u7684\u8001\u5b9e\u8bdd\u6765\u7684\u786e\u4e0d\u592a\u56f0\u96be \u3000\u3000\u4f46\u5927\u4eba\u9a97\u5c0f\u5b69\u6bd5\u7adf\u4e5f\u4e0d\u662f\u4ef6\u5f88\u6709\u9762\u5b50\u7684\u4e8b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4e5f\u89c9\u5f97\u6709\u70b9\u4e2d\u597d\u610f\u601d\xb7\u5148\u628a\u4e32\u94b1\u585e\u5230\u5b69\u5b50\u624b\u91cc\xb7\u5201\u5e26\u7b11\u9053\u201c\u6211\u4ece\u6765\u6ca1\u6709\u770b\u89c1\u8fc7\u9ebb \u4e8e\u4f60\u80fd\u672c\u80fd\u5e26\u6211\u53bb\u770b\u770b\u201d \u3000\u3000\u8fd9\u5b69\u4e88\u4e5f\u7b11\u5de5\u9053\u201c\u5f53\u7136\u80fd\xb7\u4ed6\u521a\u8fdb\u53bb\u9a6c\u4e0a\u5c31\u4f1a\u51fa\u6765\u7684\u3002\u90ed\u5927\u8def\u9053\u201c\u4ed6\u771f\u7684\u4f1a\u51fa\u6765\uff1f\u201d\u5b69 \u5b50\u70b9\u70b9\u5934\u773c\u73e0\u4e86\u8f6c\u5ffd\u4e49\u7b11\u9053\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u53ea\u624b\u7d27\u7d27\u6293\u90a3\u4e32\u94b1\uff0c\u5374\u629b\u5f00\u4e86\u624b\u91cc\u7684\u7a46\u5b50\u53bb\u5c06\u521a\u8d70\u51fa\u7cae\u98df\u7eba\u7684\u9ebb\u5b50\u62c9\u8fc7\u6765\u3002 \u3000\u3000\u4e2a\u53ea\u6709\u4e03\u516b\u5c81\u7684\u5c0f\u9ebb\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4f50\u53c8\u6709\u70b9\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u8be5\u4e8e\u5374\u7b11\u5f97\u5f88\u5f00\u5fc3\u9053\u201c\u4ed6\u53eb\u5c0f\u4e8c\u4e8e\u662f\u6211\u7684\u5f1f\u5f1f\u4ece\u5c0f\u5c31\u662f\u9ebb\u4e88\uff0c\u6211\u4eec\u5bb6\u53ea\u6709\u8fd9\u9ebd\u6837\u4e2a\u9ebb \u5343\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u664c\uff0c\u6389\u5934\u5c31\u8d70\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5b69\u4e88\u8fd8\u5728\u5077\u5077\u7684\u7b11\u9053\u5e7f\u5c0f\u53c1\u5b50\u82e5\u662f\u6bcf\u4e2a\u4eba\u770b\u4f60\u773c\u90fd\u7ed9\u6211\u4e32\u94b1\uff0c\u6211\u4eec\u5c31\u53d1\u8d22\u201c\uff0c\u4f60 \u5c06\u6765\u4e5f\u4e0d\u5fc5\u6101\u5a36\u4e0d\u5230\u6f02\u4eae\u5ab3\u5987\u53ea\u8981\u6709\u5927\u628a\u7684\u94b1\u5c31\u7b97\u4f60\u662f\u4e2a\u9ebb\u5b50\u4e5f\u6837\u6709\u4eba\u62a2\u8981\u5ac1\u7ed9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u597d\u6c14\u53c8\u4e86\u7b11\xb7\u6c14\u53c8\u6c14\u4e0d\u5f97\uff0c\u7b11\u4e5f\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5b69\u5b50\u5b9a\u62ff\u4ed6\u5f53\u505a\u4e2a\u6d3b\u761f\u751f\uff0c\u5927\u7b28\u725b\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u7684\u60f3\u6cd5\u4e5f\u548c\u8fd9\u5b69\u5b50\u5dee\u672f\u4e86\u591a\u5c11\u3002 \u3000\u3000\u4ed6\u56de\u5934\u5c31\u770b\u89c1\u4f1a\u5b9d\u697c\u7684\u5925\u8ba1\u5728\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u76ef\u4ed6\xb7\u9053\u201c\u5ba2\u5bcc\u521a\u7684\u5e10\u662f\u738b\u4e24\u516d\u5206\u94f6\u5b50\u5269\u4e0b \u7684\u9e2d\u67b6\u5b50\u8fd8\u53ef\u4ee5\u5305\u8d77\u6765\u5e26\u56de \u3000\u3000\u9986\u5925\u8ba1\u5bf9\u4e2a\u559d\u5b8c\u9152\u5c31\u8df3\u697c\u8d70\u4e86\u7684\u5ba2\u4eba\xb7\u5f53\u7136\u4e0d\u4f1a\u6709\u4ec0\u9ebd\u597d\u8138\u8272\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5df2\u7ecf\u8fde\u706b\u6c14\u90fd\u6ca1\u6709\u4e86\u62ff\u4e86\u952d\u94f6\u5b50\u7ed9\u4ed6\xb7\u5ffd\u53c8\u95ee\u9053\u201c\u521a\u624d\u90a3\u4e2a\u6e90\u5934\u5947\u5927\u7684\u9ebb\u4e8e\u4f60\u8ba4 \u4e0d\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u5925\u8ba1\u63a5\u94f6\u5b50\uff0c\u6382\u4e01\u6382\u7acb\u523b\u966a\u7b11\u9053\u201c\u90a3\u9ebb\u5b50\u5c0f\u7684\u867d\u4e0d\u8ba4\u5f97\u4f46\u9677\u4ed6\u6765\u7684\u51e0\u4e2a\u7c89\u5934\xb7\u5c0f\u7684\u5374\u53ef \u4ee5\u53bb\u66ff\u5927\u7237\u53eb\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8981\u627e\u7684\u662f\u90a3\u9ebb\u5b50\u4fae\u4ee5\u524d\u96be\u9053\u6ca1\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u5925\u8ba1\u6447\u4e86\u6447\u5934\u663e\u7136\u89c9\u5f97\u5f88\u5947\u602a\u201c\u8fd9\u4eba\u7a76\u7adf\u6709\u4ec0\u9ebd\u6bdb\u75c5\u82b1\u679d\u62db\u5c55\u7684\u5c0f\u59a8\u5a18\u4ed6\u4e0d\u8981\u5374\u8981\u627e\u5927 \u9ebb\u4e8e\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u61d2\u5f97\u8ddf\u4ed6\u591a\u8bf4\u4e86\u4ed6\u77e5\u9053\u82e5\u662f\u53bb\u95ee\u90a3\u4e9b\u5c0f\u59d1\u5a18\uff0c\u4e5f\u5b9a\u95ee\u4e0d\u51fa\u90a3\u9ebb\u4e8e\u7684\u5e95\u7ec6\u6765\u7684\u3002 \u3000\u3000\u8fd9\u9ebb\u5b50\u5012\u771f\u662f\u4e2a\u602a\u4eba\u3002 \u3000\u3000\u4ed6\u660e\u660e\u662f\u5728\u8eb2\u90ed\u5927\u8def\xb7\u5374\u53c8\u5014\u504f\u603b\u662f\u5728\u90ed\u5927\u8def\u773c\u524d\u51fa\u73b0\u82e5\u8bf4\u4ed6\u4e0d\u662f\u6545\u610f\u7684\u5929\u4e0b\u53c8\u600e\u9ebd\u4f1a \u6709\u8fd9\u9ebd\u5de7\u7684\u4e8b\uff1f \u3000\u3000\u8fd9\u7cae\u98df\u574a\u7684\u90a3\u592b\u59bb\u4e24\u4e2a\u4eba\u65e2\u7136\u90fd\u8ddf\u4ed6\u6709\u5f88\u5bc6\u5207\u7684\u5173\u7cfb\u4ed6\u5728\u8fd9\u57ce\u91cc\u60f3\u5fc5\u4e5f\u51fa\u803d\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4f46\u522b\u7684\u4eba\u5374\u597d\u50cf\u90fd\u6ca1\u6709\u89c1\u8fc7\u4ed6\u3002 \u3000\u3000\u4ed6\u65e0\u7f18\u65e0\u6545\u7684\u4e3a\u90ed\u5927\u8def\u9001\u4e86\u4ef7\u503c\u5343\u91d1\u7684\u73cd\u73e0\u7ed9\u6c34\u67d4\u9752\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u8fde\u70b9\u4f01\u56fe\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u4f01\u56fe\u7a76\u7adf\u662f\u4ec0\u9ebd\uff1f\u4e3a\u4ec0\u9ebd\u8981\u505a\u8fd9\u4e9b\u83ab\u540d\u5176\u5999\u7684\u4e8b\uff1f \u3000\u3000\u4f60\u5c31\u7b97\u624b\u5de5\u7834\u90ed\u5927\u8def\u7684\u5934\xb7\u4ed6\u4e5f\u60f3\u4e0d\u51fa\u4e2a\u9053\u7406\u6765\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5df2\u6e2f\u5907\u653e\u5f03\u8fd9\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\u6708\u3002\u624d\u6276\u9ebb\u5b50\u4e0b\u697c\u7684\u90a3\u5c0f\u7ad9\u5a18\u7a81\u7136\u6254\u8170\u4ece\u5bf9\u9762\u8d70\u5382\u8fc7\u6765\uff0c\u800c\u4e14\u8fd8\u7b11\u772f\u772f\u7684 \u770b\u90ed\u5927\u8def\u629b\u5a9a\u670d\u3002 \u3000\u3000\u90a3\u5e97\u4f19\u770b\u770b\u5979\xb7\u53c8\u770b\u770b\u90ed\u5927\u8def\u6084\u6084\u626e\u4e86\u4e2a\u9b3c\u8138\u6e9c\u4e86\u3002 \u3000\u3000\u505a\u8fd9\u79cd\u4e8b\u7684\u4eba\u5f88\u5c11\u6709\u4e0d\u8bc6\u76f8\u4e0d\u77e5\u8da3\u7684\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u5c0f\u59a8\u5a18\u5df2\u8d70\u5230\u90ed\u5927\u8def\u9762\u524d\u751c\u7b11\u9053\u201c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u90ed\u5bb6\u7684\u5927\u5c11\u7237\u4e86\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u70b9\u70b9\u5934\u77aa\u5979\u9053\u201c\u662f\u4e0d\u662f\u90a3\u9ebb\u5b50\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u8fd9\u5c0f\u59cb\u5a18\u4e5f\u70b9\u70b9\u5934\u5a9a\u7136\u9053\u201c\u6211\u53eb\u6885\u5170\u662f\u7559\u6625\u9662\u91cc\u7684\u4ee5\u5f8c\u8fd8\u5f97\u8bf7\u90ed\u5c11\u7237\u591a\u6367\u573a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u4f60\u82e5\u80fd\u66ff\u6211\u627e\u5230\u90a3\u5e2d\u5b50\u6211\u5c31\u5929\u5929\u53bb\u6367\u4f60\u7684\u573a\uff0c \u3000\u3000\u6885\u5170\u7728\u7728\u773c\u9053\u201c\u771f\u7684\uff1f\u201d\u6b22\u4e50\u82f1\u7ef4 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8bf4\u8bdd\u4e0d\u7b97\u6570\u7684\u662f\u738b\u516b\u3002\u201d \u3000\u3000\u62d6\u5170\u53c8\u7b11\u4e86\u7b11\u5f97\u66f4\u751c\uff0c\u9053\u201c\u6211\u6765\u627e\u90ed\u5c11\u7237\u6b63\u662f\u4e3a\u4e86\u90a3\u4f0e\u9ebb\u5927\u7237\u6709\u8bdd\u8981\u6211\u8f6c\u544a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u6885\u5170\u9053\u201c\u4ed6\u8bf4\u4ed6\u4eca\u5929\u665a\u4e0a\u53c1\u66f4\u65f6\u5728\u5927\u660e\u6e56\u4e1c\u8fb9\u7684\u9f99\u738b\u6d12\u91cc\u7b49\u4f60\uff0c\u4ed6\u8fd8\u8bf4\u2026\u3002\uff0e\u8fd8\u8bf4\u2026 \u2500\u201d \u3000\u3000\u90ed\u5927\u8def\u6025\u95ee\u9053\u201c\u4ed6\u8fd8\u8bf4\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6885\u5170\u66dd\u54fa\u9053\u201c\u4ed6\u8fd8\u8bf4\u4f60\u82e5\u662f\u6ca1\u80c6\u5b50\u4e0d\u6562\u53bb\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u5a7f\u7136\u4e00\u7b11\uff0c\u9053\u201c\u73b0\u5728\u90ed\u5c11\u7237\u5df2\u7ecf\u53ef\u4ee5\u627e\u5230\u4ed6\u4e86\uff0c\u90ed\u5c11\u7237\u4f60\u8bf4\u7684\u8bdd\uff0c\u4e5f\u5f97\u7b97\u6570\u5440\u7537 \u4eba\u505a\u5382\u738b\u516b\u90a3\u6ecb\u5473\u53ef\u4e0d\u662f\u597d\u53d7\u7684\u3002\u201d \u3000\u3000\u8fd9\u6253\u626e\u6210\u5c0f\u5996\u602a\u2606\u6837\u7684\u5973\u63a5\u4e88\uff0c\u7ec8\u4e8e\u4e49\u626d\u626d\u7684\u8d70\u4e86\u3002 \u3000\u3000\u4e34\u8d70\u65f6\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5c06\u7559\u6625\u9662\u7684\u5730\u5740\u544a\u8bc9\u90ed\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u53d1\u73b0\u81ea\u5df1\u53c8\u8bf4\u9519\u8bdd\u4e86\u4ed6\u4e3a\u4ec0\u9ebd\u4e0d\u80fd\u6c89\u4f50\u6c14\u7b49\u7b49\u7b49\u8fd9\u5c0f\u5996\u7cbe\u5148\u8bf4\u51fa\u90a3\u9ebb\u5b50\u8981\u5979 \u4f20\u7684\u8bdd\u5462\uff1f\u4ed6\u4e3a\u4ec0\u9ebd\u603b\u662f\u4f1a\u83ab\u540d\u5176\u7092\u7684\u4e3a\u81ea\u5df1\u627e\u6765\u5f88\u591a\u9ebb\u70e6 \u3000\u3000\u53ef\u662f\u90a3\u9ebb\u5b50\u5374\u66f4\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u4ed6\u660e\u660e\u5728\u8eb2\u90ed\u5927\u8def\u5374\u53c8\u8981\u7ea6\u90ed\u5927\u8def\u89c1\u9762\u3002 \u3000\u3000\u96be\u9053\u8fd9\u4e5f\u662f\u4e2a\u9634\u8c0b\u5708\u5957\uff1f \u3000\u3000\u96be\u9053\u4ed6\u5df2\u5728\u90a3\u9f99\u738b\u5e99\u91cc\u5b89\u6392\u4e86\u57cb\u4f0f\u7b49\u90ed\u5927\u8def\u53bb\u81ea\u6295\u7f57\u7f51\uff1f \u3000\u3000\u4ed6\u867d\u7136\u597d\u50cf\u5bf9\u90ed\u5927\u8def\u7684\u4e8b\u60c5\u77e5\u9053\u5f97\u5f88\u591a\uff0c\u90ed\u5927\u8def\u4ee5\u524d\u5374\u8fde\u8fd9\u4e2a\u4eba\u90fd\u6ca1\u89c1\u8fc7\u66f4\u7edd\u4e0d\u4f1a\u6709\u4ec0 \u9ebd\u6069\u6028\u3002 \u3000\u3000\u4ed6\u8d39\u4e86\u8fd9\u9ebd\u591a\u5fc3\u673a\u82b1\u4e86\u8fd9\u9ebd\u591a\u672c\u94b1\u76ee\u7684\u7a76\u7ade\u662f\u4ec0\u9ebd\uff1f \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\uff0c\u8d2d\u54fa\u9053\u201c\u5341\u4e2a\u9ebb\u4e8e\u4e5d\u4e2a\u602a\u770b\u6765\u8fd9\u53e5\u8bdd\u5012\u771f\u7684\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u9f99\u738b\u5e99\u3002 \u3000\u3000\u6709\u6c34\u7684\u5730\u65b9\xb7\u597d\u50cf\u90fd\u6709\u9f99\u738b\u5e99\u3002 \u3000\u3000\u9f99\u738b\u5e99\u5c31\u8c61\u662f\u571f\u5730\u5e99\u6837\u5df2\u6210\u5382\u804b\u5b50\u7684\u8033\u6735\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5730\u65b9\u7684\u70b9\u7f00\xb7\u65e2\u6ca1\u6709\u4ec0\u9ebd\u9999\u706b\xb7\u4e5f \u6ca1\u6709\u9053\u58eb\u548c\u5c1a\u3002 \u3000\u3000\u8fd9\u9f99\u738b\u5e99\u4e5f\u6837\u3002 \u3000\u3000\u90ed\u5927\u8def\u662f\u5750\u9a74\u8f66\u6765\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e2\u4e0d\u8ba4\u5f97\u8def\u53c8\u60f3\u8282\u7701\u4e9b\u4f53\u529b\uff0c\u597d\u6765\u5bf9\u4ed8\u90a3\u9ebb\u4e8e\u3002 \u3000\u3000\u8d76\u8f66\u7684\u662f\u4e2a\u8005\u4eba\u767d\u53d1\u82cd\u82cd\xb7\u8fd8\u9a7c\u80cc\u3002 \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u4e0d\u60f3\u5750\u8fd9\u8f86\u8f66\u7684\uff0c\u600e\u5948\u522b\u7684\u8f66\u628a\u5f0f\u665a\u4e0a\u90fd\u4e0d\u80af\u5230\u9f99\u738b\u5e99\u8fd9\u79cd\u8352\u50fb\u7684\u5730\u65b9\u6765\u3002 \u3000\u3000\u8fd9\u6761\u8def\u7684\u786e\u4e0d\u597d\u8d70\u53c8\u9ed1\u56e0\u52a9\u6ca1\u6709\u706f\u5149\u3002 \u3000\u3000\u8d76\u8f66\u7684\u8001\u5934\u5b50\u8def\u4e0a\u90fd\u8c61\u5728\u6253\u78d5\u7761\u5230\u4e86\u8fd9\u91cc\u5ffd\u7136\u201c\u7684\u515c\u201d\u58f0\u52d2\u4f4f\u4e86\u9a74\u5b50\u56de\u5934\u9053\u201c\u4e00\u76f4\u5f80\u524d \u8d70\u5c31\u662f\u9f99\u738b\u5e99\xb7\u4f60\u81ea\u5df1\u53bb\u5427\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u76f4\u9001\u6211\u5230\u95e8\u53e3\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7a81\u7136\u7b11\u4e86\u7b11\u9053\u201c\u56e0\u4e3a\u6211\u8fd9\u6761\u8001\u547d\u8fd8\u60f3\u518d\u591a\u6d3b\u4e24\u5e74\u3002\u201d \u3000\u3000\u591c\u8272\u6e05\u51b7\u2606\u4ed6\u7684\u7b11\u770b\u6765\u7adf\u6709\u70b9\u9634\u68ee\u68ee\u7684\u6837\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u76b1\u76b1\u7709\u9053\u201c\u96be\u9053\u4f60\u9001\u6211\u5230\u4e86\u90a3\u91cc\u5c31\u6d3b\u4e0d\u4e0b\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7b11\u5f97\u66f4\u8be1\u79d8\uff0c\u8c08\u8c08\u9053\u201c\u4eca\u5929\u665a\u4e0a\u5230\u90a3\u91cc\u53bb\u7684\u4eba\u6015\u5f88\u96be\u6d3b\u56de\u6765\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u8981\u53bb \u7684\u597d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4eca\u665a\u6709\u4ec0\u9ebd\u4e0d\u540c\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u773c\u775b\u5374\u76f4\u52fe\u52fe\u7684\u77aa\u90ed\u5927\u8def\u80cc\u5f8c\u7684\u591c\u8272\u5c31\u597d\u50cf\u5ffd\u7136\u770b\u89c1\u4e86\u9b3c\u4f3c \u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u80cc\u810a\u4e5f\u6709\u70b9\u53d1\u6bdb\u4e86\u4e5f\u5fcd\u4e0d\u4f73\u8f6c\u8fc7\u5934\u53bb\u770b\u3002 \u3000\u3000\u591c\u9759\u65e0\u4eba\u98ce\u5439\u67f3\u6761\u5728\u9ed1\u6697\u4e2d\u770b\u6765\u7684\u786e\u6709\u4e9b\u8c61\u662f\u4e2a\u4e2a\u5e7d\u7075\u9b3c\u5f71\u5728\u5f20\u7259\u821e\u722a\u3002 \u3000\u3000\u4f46\u90a3\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u6709\u53c1\u5206\u8c61\u9b3c\u4e3d\u5df2\u5f88\u5c11\u6709\u4eba\u4f1a\u88ab\u771f\u7684\u5413\u5012\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5931\u7b11\u9053\u201c\u4f60\u53ea\u7ba1\u653e\u5fc3\u9001\u6211\u53bb\u4f60\u82e5\u6b7b\u5382\u6211\u2026\u3002\u201c \u3000\u3000\u4ed6\u8bed\u58f0\u7a81\u7136\u505c\u987f\u3002 \u3000\u3000\u56e0\u4e3a\u7b49\u4ed6\u56de\u8fc7\u5934\u6765\u65f6\u90a3\u8d76\u8f66\u7684\u9a7c\u80b2\u8001\u4eba\u7adf\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fdc\u65b9\u4e5f\u662f\u7247\u9ed1\u6697\uff0c\u975e\u4f46\u770b\u4e0d\u89c1\u4eba\xb7\u5c31\u7b97\u771f\u7684\u6709\u9b3c\xb7\u4e5f\u6837\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u9a7c\u80cc\u8001\u4eba\u600e\u9ebd\u5ffd\u7136\u4e0d\u89c1\u201c\uff1f\u96be\u9053\u5df2\u88ab\u9ed1\u6697\u4e2d\u7b49\u62e9\u4eba\u800c\u566c\u7684\u6076\u9b3c\u6349\u8d70\uff1f \u3000\u3000\u9635\u98ce\u5439\u8fc7\xb7\u90ed\u5927\u8def\u7ade\u4e5f\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u8ba2\u4e86\u4e2a\u5bd2\u9178\u95fb\u95fb\u5730\u8bf4\u9053\u201c\u597d\u4f60\u4e0d\u5de6\uff0c\u6211\u5c31\u65e5\u5df1\u8d76\u8f66 \u53bb\u3002\u201d \u3000\u3000\u4e2a\u4eba\u5728\u9ed1\u6697\u65e0\u58f0\u65f6\u542c\u542c\u81ea\u5df1\u8bf4\u8bdd\u7684\u58f0\u97f3\xb7\u4e5f\u53ee\u4ee5\u7261\u80c6\u7684\u3002 \u3000\u3000\u4ed6\u773a\u4e0a\u524d\u5ea7\uff0c\u627e\u4e86\u9a6c\u97ad\u6325\u97ad\u8d76\u9a74\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u9a74\u5b50\u56db\u6761\u817f\u5c31\u597d\u50cf\u9489\u5728\u5730\u4e0a\u6837\u6b7b\u4e5f\u4e0d\u80af\u518d\u5f80\u524d\u8d70 \u3000\u3000\u96be\u9053\u8fde\u8fd9\u9a74\u5b50\u4e5f\u55c5\u51fa\u5382\u524d\u9762\u9ed1\u6697\u4e2d\u6709\u4ec0\u9ebd\u51f6\u6076\u4e0d\u6837\u7684\u8b66\u5146\uff1f\u6b22\u4e50\u82f1\u6444 \u3000\u3000\u5728\u8fd9\u79cd\u5730\u65b9\u8fd9\u79cd\u65f6\u5019\uff0c\u83ab\u8bf4\u804c\u9b3c\u4f1a\u5403\u4eba\uff0c\u4eba\u4e5f\u4f1a\u5403\u4eba\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u4eba\u5730\u751f\u758f\u5c31\u7b97\u771f\u7684\u88ab\u4eba\u5403\u4e86\u8fde\u8bc9\u51a4\u7684\u5730\u65b9\u90fd\u6ca1\u6709\u8fde\u5c38\u9aa8\u90fd\u627e\u4e0d\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\u5e94\u50e7\u8fd9\u79cd\u60c5\u51b5\uff0c\u6700\u597d\u7684\u6cd5\u5b50\u5c31\u662f\u8d76\u5feb\u56de\u5934\u8d70\u627e\u4e2a\u5730\u65b9\u8d50\u4e24\u676f\u70ed\u9152\u518d\u627e\u5f20\u8212 \u670d\u7684\u5e8a\u5148\u7761\u89c9\u518d\u8bf4\u3002 \u3000\u3000\u53ea\u53ef\u501f\u90ed\u5927\u8def\u504f\u504f\u4e5f\u6709\u70b9\u9aa1\u5b50\u813e\u6c14\u4f60\u82e5\u60f3\u8981\u4ed6\u5f80\u5f8c\u9000\u4ed6\u5c31\u504f\u8981\u5f80\u524d\u8d70\u3002 \u3000\u3000\u5c31\u7b97\u524d\u9762\u771f\u662f\u9f99\u6f6d\u864e\u7a74\u4ed6\u4e5f\u8981\u95ef\u4e00\u95ef\u7684\u3002 \u3000\u3000\u201c\u4f60\u65e2\u4e0d\u65e8\u8d70\uff0c\u6211\u4e5f\u6709\u817f\uff0c\u6211\u96be\u9053\u4e0d\u80fd\u81ea\u5df1\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u7d22\u6027\u8df3\u535c\u8f66\u8fc8\u5f00\u4e86\u5927\u6b65\u3002 \u3000\u3000\u201c\u9f99\u5b50\u5e99\u662f\u4e0d\u662f\u771f\u7684\u5c31\u5728\u524d\u9762\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8fd8\u4e0d\u77e5\u9053\xb7\u4e5f\u770b\u4e0d\u89c1\u5c4b\u5f71\u3002 \u3000\u3000\u524d\u9762\u7a7a\u8361\u8361\u7684\u4ec0\u9ebd\u90c1\u770b\u4e0d\u89c1\uff0c\u65e0\u8bba\u8c01\u7ea6\u4f1a\u90fd\u4e0d\u4f1a\u7ea6\u5728\u8fd9\u79cd\u9b3c\u5730\u65b9\u7684\u3002 \u3000\u3000\u9664\u975e\u4ed6\u6709\u4ec0\u9ebd\u89c1\u4e0d\u5f97\u4eba\u7684\u9634\u8c0b\u3002 \u3000\u3000\u7ff1\u5927\u8def\u633a\u80f8\u51b7\u7b11\uff0c\u8eab\u5f8c\u5ffd\u7136\u54cd\u8d77\u4e86\u79cd\u5f88\u5947\u602a\u7684\u58f0\u97f3\u5c31\u597d\u50cf\u662f\u6709\u4eba\u5728\u957f\u5636\u3002 \u3000\u3000\u4ed6\u56de\u8fc7\u5934\u624d\u53d1\u73b0\u90a3\u53ea\u77f3\u8fc7\u662f\u9a74\u5b50\u5728\u53eb\u8fd9\u5934\u9a74\u4e8e\u4e5f\u8c61\u662f\u89c1\u4e86\u9b3c\u4f3c\u7684\xb7\u4e0d\u77e5\u4f55\u65f6\u5df2\u6389\u8f6c\u5934\uff0c \u5bbd\u4e5f\u4f3c\u7684\u5411\u6765\u8def\u5954\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u51b7\u7b11\xb7\u54fa\u54fa\u9053\u201c\u6211\u4e0d\u662f\u9a74\u4e88\u5f25\u5413\u5f97\u4e86\u5b83\u5374\u5413\u4e0d\u5230\u6211\u7684\u3002\u201d \u3000\u3000\u4ed6\u56de\u8fc7\u5934\u8fd8\u662f\u5413\u4e86\u8df3\u3002 \u3000\u3000\u524d\u9762\u7684\u9ed1\u6697\u4e2d\u4e0d\u77e5\u4f55\u65f6\u5df4\u591a\u5382\u76cf\u706f\u7b3c\u6761\u4eba\u5f71\u3002 \u3000\u3000\u706f\u7b3c\u5c45\u7136\u662f\u7eff\u7684\xb7\u60e8\u78a7\u8272\u7684\u706f\u5149\xb7\u7167\u5728\u8fd9\u4e2a\u4eba\u7684\u8eab\u4ea1\u3001\u811a\u5374\u7167\u4e0d\u5230\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4ed6\u5934\u535c\u6234\u9876\u53c8\u5bbd\u53c8\u5927\u7684\u6597\u7b7e\xb7\u85cf\u5f97\u5f88\u4f4e\u51e0\u4e4e\u5c06\u6574\u5f20\u80a1\u90fd\u84c4\u4f50\u5382\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5374\u5df2\u770b\u51fa\u4ed6\u7edd\u4e0d\u662f\u90a3\u9ebb\u4e8e\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4eba\u53ea\u6709\u6761\u817f\u4ed6\u5b9a\u817f\u5df2\u9f50\u819d\u800c\u65ad\u88c5\u4e2a\u6728\u811a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u5c45\u7136\u8fd8\u662f\u8fde\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u8fdc\u8fdc\u7684\u7ad9\u5728\u90a3\u91cc\u53ea\u624b\u63d0\u706f\u7b3c\uff0c\u53e6\u53ea\u624b\u4e0a\u63d0\u6839\u9ed1\u52e4\u671f\u7684\u68cd\u5b50\u4e5f\u4e0d\u77e5\u662f\u6728\u5934\u524a\u6210\u7684\xb7\u8fd8\u662f \u94c1\u6253\u7684\u3002 \u3000\u3000\u4ed6\u867d\u7136\u53ea\u6709\u53ea\u811a\u4f46\u7ad9\u5728\u90a3\u91cc\uff0c\u5378\u662f\u6c14\u5ea6\u6c89\u51dd\u7a33\u5982\u6cf0\u5c71\uff01 \u3000\u3000\u53c1\u66f4\u534a\u591c\u65f6\xb7\u56db\u91ce\u65e0\u4eba\u5904\xb7\u7a81\u7136\u770b\u5230\u8fd9\u9ebd\u6837\u4e2a\u4eba\u51fa\u73b0\u5728\u9762\u524d\u65e0\u8bba\u8c01\u90fd\u96be\u514d\u8981\u5403\u60ca\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u975e\u4f46\u5f88\u5feb\u5c31\u9547\u5b9a\u4e86\u4e0b\u6765\uff0c\u800c\u4e14\u8fd8\u5fae\u7b11\u5411\u8fd9\u4eba\u70b9\u4e86\u70b9 \u3000\u3000\u53ea\u8981\u522b\u4eba\u8fd8\u6ca1\u6709\u4f24\u5bb3\u5230\u4ed6\u4ed6\u65e0\u8bba\u5bf9\u4ec0\u9ebd\u4eba\u90fd\u603b\u662f\u5f88\u53cb\u5584\u3002 \u3000\u3000\u8fd9\u72ec\u811a\u4eba\u5c45\u7136\u4e5f\u5411\u4ed6\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u59d3\u90ed\u53eb\u90ed\u5927\u8def\u5927\u65b9\u7684\u5927\u4e0a\u8def\u7684\u8def\u3002\u201d\u72ec\u811a\u4eba\u51b7\u51b7\u9053\u201c\u6211\u5e76\u672b\u8bf7\u6559\u5c0a\u59d3\u5927 \u540d\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f46\u6211\u4eec\u80fd\u5728\u8fd9\u79cd\u5730\u65b9\u78b0\u5230\xb7\u603b\u7b97\u662f\u6709\u7f18\u3002\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u4f60\u600e\u77e5\u6211\u662f\u78b0\u5de7\u9047\u89c1\u4f60\u7684\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u96be\u5230\u4e0d\u662f\uff1f\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u96be\u9053\u4f60\u672c\u5c31\u662f\u7279\u5730\u6765\u627e\u6211\u7684\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u627e\u6211\u5e72\u4ec0\u9ebd\uff1f\u201d\u72ec\u811a\u4eba\u9053\u201c\u8981\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56de\u53bb\uff1f\u56de\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u4ece\u90a3\u91cc\u6765\u5c31\u56de\u5230\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u7728\u773c\u9053\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u4e0d\u8ba9\u6211\u5230\u9f99\u738b\u5e99\u53bb\uff1f\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u90a3\u662f\u4e2a\u4e0d\u7965\u7684\u5730\u65b9\u53bb\u7684\u4eba\u5fc5\u7136\u6709\u7978\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u4e86\xb7\u9053\u201c\u591a\u8c22\u6307\u6559\u53ea\u4e0d\u8fc7\xb7\u6211\u4eec\u7d20\u4e0d\u76f8\u8bc6\u4f60\u53c8\u4f55\u5fc5\u5bf9\u6211\u5982\u6b64\u5173\u5fc3\uff1f\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u4f60\u5b9a\u8981\u53bb\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u597d\u5148\u51fb\u5012\u6211\uff0c\u518d\u4ece\u6211\u7684\u8eab\u4e0a\u8de8\u8fc7\u53bb\u5427\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\u9053\u201c\u539f\u6765\u4f60\u662f\u7279\u5730\u6765\u627e\u6211\u6253\u67b6\u7684\u3002\u201d \u3000\u3000\u72ec\u811a\u4eba\u518d\u4e5f\u4e0d\u8bf4\u4ec0\u9ebd\uff0c\u7a81\u7136\u6325\u624b\u624b\u91cc\u7684\u706f\u7b3c\u5c31\u5189\u5189\u7684\u575b\u4e86\u5de6\u4e0d\u504f\u4e0d\u501a\u521a\u597d\u63d2\u5728\u9053\u65c1\u7684\u6839 \u67f3\u679d\u4e0a\u3002 \u3000\u3000\u90ed\u5927\u8def\u5931\u58f0\u9053\u201c\u597d\u624b\u6cd5\u5c31\u51ed\u8fd9\u624b\xb7\u6211\u5c31\u672a\u5fc5\u6253\u5f97\u8fc7\u4f60\u3002\u201d \u3000\u3000\u72ec\u811a\u4eba\u9053\u201c\u4f60\u73b0\u5728\u8fd8\u6765\u5f97\u53ca\u56de\u53bb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u7b11\u4e86\uff0c\u9053\u5c31\u56e0\u4e3a\u6211\u672a\u5fc5\u6253\u5f97\u8fc7\u4f60\uff0c\u6240\u4ee5\u6211\u624d\u6253\u82e5\u662f\u6211\u6709\u5fc5\u80dc\u628a\u63e1\u6253\u8d77\u6765\u8fd8\u6709\u4ec0 \u9ebd\u52b2\uff1f\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u72ec\u811a\u5165\u4fb5\u6162\u7684\u70b9\u4e86\u70b9\u5934\u9053\u201c\u597d\u6709\u79cd\u6211\u4ece\u4e0d\u6740\u6709\u79cd\u7684\u4eba\u6700\u591a\u53ea\u780d\u65ad\u4ed6\u4e24\u6761\u817f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u6211\u6700\u591a\u53ea\u780d\u65ad\u4f60\u6761\u817f\u56e0\u4e3a\u4f60\u53ea\u6709\u6761\u817f\u3002\u201d \u3000\u3000\u4ed6\u672c\u4e0d\u662f\u4e2a\u5c16\u9178\u523b\u8584\u7684\u4eba\uff0c\u672c\u4e0d\u613f\u8bf4\u8fd9\u79cd\u5c16\u9178\u523b\u8584\u7684\u8bdd\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5df2\u53d1\u73b0\uff0c\u90a3\u9ebb\u4e8e\u3001\u9a7c\u4e8e\xb7\u548c\u8fd9\u72ec\u811a\u4eba\u90fd\u662f\u65e9\u5df3\u7387\u901a\u597d\u4e86\u7684\u800c\u5df2\u8bbe\u4e0b\u4e86\u5708\u5957\u5728\u7b49 \u4ed6\u6765\u4e0a\u5f53\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u5feb\u6389\u5382\u4e0b\u53bb\u5374\u8fde\u8fd9\u662f\u4e2a\u4ec0\u9ebd\u5708\u5957\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u6218\u6fc0\u662d\u6211\u660e\u654c\u4f17\u6211\u5be1\u6253\u5f97\u672a\u514d\u6709\u516c\u5e73\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u673a\u4f1a\u5b9e\u5728\u4e0d\u591a\u5c31\u7b97\u6545\u610f\u8bf4\u51e0\u53e5\u5c16\u9178\u5230\u84b2\u7684\u8bdd\u6765\u6fc0\u6012\u5bf9\u65b9\u4e5f\u662f\u503c\u5f97\u539f\u8c05\u7684\u3002 \u3000\u3000\u81f3\u5c11\u4ed6\u81ea\u5df1\u5df2\u539f\u8c05\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u72ec\u811a\u4eba\u679c\u7136\u5df4\u52a8\u5382\u706b\u6c14\u5389\u559d\u4e00\u58f0\uff0c\u624b\u91cc\u7684\u77ed\u6756\u5939\u5e26\u52b2\u98ce\uff0c\u5411\u90ed\u5927\u8def\u6a2a\u626b\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u77ed\u6756\u6700\u591a\u624d\u53c1\u56db\u5c3a\u53cd\u4ed6\u8ddd\u79bb\u90ed\u5927\u8def\u81f3\u5c11\u8fd8\u6709\u4e24\u53c1\u4e08\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u624b\u6325\u77ed\u6756\u5c31\u5df2\u5230\u4e86\u90ed\u5927\u8def\u9762\u524d\u3002 \u3000\u3000\u8fd9\u6756\u6765\u5f97\u597d\u4fa0\u3002 \u3000\u3000\u90ed\u5927\u8def\u624b\u65e0\u5bf8\u94c1\u6839\u672c\u5c31\u8bbe\u6cd5\u5b50\u62db\u67b6\u62b5\u6321\u53ea\u6709\u95ea\u907f\u3002 \u3000\u3000\u4f46\u8fd9\u72ec\u811a\u4eba\u62db\u5f0f\u8fde\u7ef5\u62db\u6bd4\u4e00\u62db\u6025\u62db\u6bd4\u62db\u5feb\u90ed\u5927\u8def\u867d\u7136\u770b\u4e0d\u51fa\u4ed6\u6756\u6cd5\u7684\u8def\u6570\u4f46\u4e5f\u77e5\u9053\u8fd9\u5957 \u6756\u6cd5\u5fc5\u5b9a\u5927\u6709\u6765\u5386\u3002 \u3000\u3000\u6c5f\u6e56\u9ad8\u624b\u4e2d\xb7\u7528\u77ed\u6756\u7684\u5411\u53ea\u6709\u4e24\u79cd\u4eba\u79cd\u662f\u4e5e\u5de7\u79cd\u662f\u548c\u5c1a\u3002 \u3000\u3000\u4e5e\uff01\u5f13\u5927\u591a\u5c5e\u65bc\u5de7\u5e2e\u4e5f\u5c31\u662f\u4fd7\u79f0\u7684\u7a77\u5bb6\u5e2e\uff0c\u4ed6\u4eec\u7528\u7684\u77ed\u6756\u901a\u5e38\u90fd\u53eb\u505a\u6253\u72d7\u68d2\u8fd9\u540d\u5b57\u636e\u8bf4 \u662f\u6614\u65e5\u4f4d\u59d3\u67e5\u7684\u5e2e\u4e3b\u8d77\u7684\u4f46\u771f\u7684\u6765\u6e90\u7a76\u7adf\u51fa\u81fc\u4f55\u5904\u8c01\u4e5f\u6ca1\u6709\u8ba4\u771f\u53bb\u8003\u636e\u8fc7\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u7528\u7684\u6756\u6309\xb7\u5c31\u53eb\u505a\u201c\u6253\u72d7\u68d2\u201d\u7cbe\u5de7\u53d8\u5316\u8be1\u5f02\u7e41\u590d\u771f\u6b63\u80fd\u591f\u5c06\u8fd9\u5957\u68d2\u6cd5\u5b66\u4f1a\u7684\u4eba \u5411\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u72ec\u811a\u4eba\u7528\u7684\u62db\u5f0f\u5374\u662f\u521a\u70c8\u5a01\u731b\u9510\u4e0d\u53ee\u5f53\uff0c\u5176\u95f4\u7684\u53d8\u5316\u5012\u5e76\u4e2a\u6709\u4ec0\u9ebd\u7cbe\u5999\u4e4b\u5904\u3002 \u3000\u3000\u90ed\u5927\u8def\u5728\u6c5f\u6e56\u4e2d\u867d\u7136\u5ae9\u5f97\u591a\u6253\u72d7\u68d2\u6cd5\u603b\u662f\u542c\u4eba\u8bf4\u8fc7\u7684\u3002 \u3000\u3000\u4ed6\u4e5f\u5df2\u770b\u51fa\u8fd9\u72ec\u811a\u4eba\u7528\u7684\u7edd\u4e0d\u662f\u6253\u72d7\u68d2\u6cd5\u5c31\u4e0d\u4f1a\u662f\u5199\u5e2e\u7684\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u773c\u73e0\u5b50\u4e00\u8f6c\xb7\u5ffd\u7136\u7b11\u9053\u201c\u6211\u77e5\u9053\u4f60\u662f\u4ec0\u9ebd\u4eba\u4e86\uff0c\u4f60\u7792\u4e0d\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u72ec\u811a\u4eba\u77ed\u6756\u7a81\u7136\u4fb5\u4e86\u4e0b\u6765\u5168\u8eab\u7684\u808c\u8089\u4f3c\u4e4e\u90fd\u5df2\u6709\u4e9b\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u542c\u5382\u8fd9\u53e5\u8bdd\uff0c\u4e3a\u4ec0\u9ebd\u4f1a\u5982\u6b64\u5403\u60ca \u3000\u3000\u96be\u9053\u4ed6\u672c\u8eab\u6709\u4ec0\u9ebd\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\u751f\u4f2f\u88ab\u4eba\u770b\u7834\u4e86\u884c\u85cf\uff1f \u3000\u3000\u72ec\u811a\u4eba\u7684\u51fa\u624b\u4fb5\u90ed\u5927\u8def\u5c31\u5feb\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u53cc\u62f3\u5982\u98ce\u5df2\u62a2\u653b\u4eba\u72ec\u811a\u4eba\u7684\u7a7a\u95e8\u4e2d\u72ec\u811a\u4eba\u7684\u6756\u6cd5\u5c31\u66f4\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u9ad8\u624b\u76f8\u4e89\u6709\u65f6\u6c47\u5982\u540d\u5bb6\u5bf9\u53d8\u6837\u53ea\u8981\u6709\u4e00\u4e4b\u9519\xb7\u5c31\u53ef\u80fd\u6ee1\u6444\u7686\u7ff0\u3002 \u3000\u3000\u7a81\u7136\u95f4\xb7\u90ed\u5927\u8def\u8fde\u653b\u53c1\u62f3\uff0c\u51fb\u5411\u72ec\u811a\u4eba\u7684\u80f8\u8179\uff0c\u4f46\u7b49\u5230\u72ec\u811a\u4eba\u7528\u62db\u5c01\u67b6\u65f6\u4ed6\u62db\u5f0f\u7a81\u4e49\u6539 \u53d8\u626c\u624b\u6253\u843d\u4e86\u72ec\u811a\u4eba\u5934\u4e0a\u7684\u6597\u7b7e\u3002 \u3000\u3000\u4ed6\u82e5\u60f3\u6253\u5230\u72ec\u811a\u4eba\u7684\u5934\u5f53\u7136\u529e\u4e0d\u5230\u3002 \u3000\u3000\u4f46\u8fd9\u6597\u63a5\u53c8\u5bbd\u53c8\u5927\u4f55\u51b5\u4efb\u4f55\u4eba\u6253\u67b6\u65f6\u90fd\u53ea\u4f1a\u60f3\u4fdd\u8bc1\u76ee\u5df1\u7684\u5934\u53c8\u6709\u8c01\u5bf9\u5934\u4e0a\u7684\u6597\u6574\u653e\u5728\u5fc3 \u4e0a\u3002 \u3000\u3000\u6597\u7b7e\u843d\u4e0b\xb7\u5c31\u9732\u51fa\u72ec\u811a\u4eba\u5f20\u60e8\u81ea\u7684\u8138\u548c\u4e2a\u5149\u79c3\u79c3\u7684\u5934\u9885\u5934\u9876\u4e0a\u8fd8\u6709\u4e5d\u9897\u53d7\u6212\u7684\u9999\u75a4\u3002 \u3000\u3000\u90ed\u5927\u8def\u51cc\u7a7a\u4e2a\u8ddf\u6597\u5012\u9000\u51fa\u4e03\u5c3a\uff0c\u5927\u58f0\u9053\u201c\u6211\u731c\u5f97\u4e0d\u9519\u4f60\u679c\u7136\u662f\u4e2a\u548c\u5c1a\u201c \u3000\u3000\u72ec\u811a\u4eba\u8138\u8272\u53d8\u5f97\u66f4\u60e8\u7a81\u7136\u8dfa\u4e86\u8dfa\u811a\xb7\u77ed\u6756\u8131\u624b\u98de\u51fa\u6253\u843d\u4e86\u6930\u6821\u4e0a\u7684\u706f\u7b3c\u3002 \u3000\u3000\u56db\u4e0b\u7acb\u523b\u53c8\u6062\u590d\u4e2d\u9ed1\u6697\u3002 \u3000\u3000\u72ec\u811a\u4eba\u7684\u4eba\u5f71\u95ea\u5df3\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u7ff1\u5927\u8def\u53cd\u800c\u6709\u70b9\u5947\u602a\u4e86\u201c\u505a\u548c\u5c1a\u53c8\u4e0d\u662f\u4ec0\u9ebd\u89c1\u4e0d\u4e86\u4eba\u7684\u4e8b\u5c31\u7b97\u88ab\u4eba\u770b\u51fa\u5382\u4e5f\u6ca1\u4ec0\u9ebd\u5382\u4e0d \u8d77\xb7\u4ed6\u4e3a\u4ec0\u9ebd\u504f\u504f\u8981\u5982\u6b64\u60ca\u614c\u751a\u81f3\u6bd4\u88ab\u4eba\u8ba4\u51fa\u4ed6\u662f\u4e2a\u901a\u7f09\u7684\u9003\u72af\u8fd8\u7d27\u5f20\u201d \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u76bf\u73b0\u5728\u4ed6\u81ea\u5df1\u7684\u9ebb\u70e6\u5df2\u7ecf\u591f\u591a\u54ea\u91cc\u8fd8\u6709\u5de5\u592b\u53bb\u60f3\u522b\u4eba\u7684\u4e8b\u3002 \u3000\u3000\u524d\u9762\u65e2\u7136\u5df2\u6ca1\u6709\u4eba\u6321\u8def\uff0c\u4ed6\u5c31\u7ee7\u7eed\u5f80\u524d\u8d70\u3002 \u3000\u3000\u8d70\u8d70\u5ffd\u7136\u770b\u5230\u524d\u9762\u6709\u5730\u65b9\uff0c\u5947\u8ff9\u822c\u4eae\u8d77\u6b79\u7247\u706f\u5149\u3002 \u3000\u3000\u706f\u5149\u660e\u4eae\uff0c\u7167\u51fa\u4e86\u4e00\u680b\u5c0f\u5c0f\u7684\u5e99\u4e2d\u3002 \u3000\u3000\u9f99\u4e94\u5e99\u7ec8\u4e8e\u5230\u4e86\u3002 \u3000\u3000\u9f99\u738b\u5e99\u867d\u7136\u5230\u4f46\u5374\u662f\u8c01\u5728\u5e87\u91cc\u70b9\u8d77\u706f\u6765\u7684\u5462\uff1f \u3000\u3000\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u5ffd\u7136\u5728\u5e99\u91cc\u70b9\u8d77\u8fd9\u9ebd\u591a\u76cf\u706f\uff1f \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u3001\u72ec\u80f8\u548c\u5c1a\uff0c\u518d\u52a0\u4e0a\u90a3\u9ebb\u5b50\u8fd9\u53c1\u4e2a\u4eba\u4e0d\u4f46\u505a\u7684\u4e8b\u8be1\u79d8\u79bb\u5947\u6765\u5386\u4e5f\u795e\u79d8\u96be\u6d4b\u3002\u6b22 \u4e50\u82f1\u96c4 \u3000\u3000\u770b\u4ed6\u4eec\u7684\u6b66\u529f\u884c\u5f84\u5f53\u7136\u5b9a\u662f\u6c5f\u6e56\u4e2d\u7b49\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u5374\u504f\u4f97\u6ca1\u6709\u4eba\u542c\u8bf4\u8fc7\u4ed6\u4eec\uff0c\u4ed6\u4eec\u672c\u8eab\u4e5f\u597d\u50cf\u6839\u672c\u5c31\u6ca1\u6709\u540d\u80dc\u3002 \u3000\u3000\u5e99\u91cc\u7ade\u71c3\u4e03\u76cf\u706f\u4f46\u5374\u6ca1\u6709\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u65e2\u7136\u70b9\u8d77\u4e86\u706f\u65e2\u7136\u8981\u90ed\u5927\u8def\u627e\u5230\u8fd9\u91cc\u6765\u4ed6\u81ea\u5df1\u4e3a\u4ec0\u9ebd\u53c8\u8d70\u4e86\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u4e1c\u5f20\u5f20\uff0c\u897f\u671b\u671b\u5c31\u597d\u50cf\u662f\u4e2a\u6e38\u5ba2\u4f3c\u7684\u8f7b\u677e\u6781\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5fc3\u91cc\u53c8\u4f55\u5c1d\u4e0d\u7d27\u5f20\uff1f\u90a3\u75af\u5b50\u8fd9\u9ebd\u6837\u505a\u5f53\u7136\u4e0d\u4f1a\u662f\u8ddf\u4ed6\u95f9\u73a9\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u4f1a\u8d39\u8fd9\u9ebd\u591a\u5fc3\u673a\xb7\u82b1\u8fd9\u9ebd\u5927\u672c\u94b1\uff0c\u4e13\u8ddf\u4e00\u4e2a\u4eba\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u73b0\u5728\u90ed\u5927\u8def\u53ea\u7b49\u4ed6\u66b4\u9732\u51fa\u81ea\u7684\u8eab\u4efd\xb7\u8bf4\u51fa\u81ea\u5df1\u7684\u76ee\u7684\u6765\u3002 \u3000\u3000\u90a3\u523b\u5fc5\u5b9a\u662f\u51f6\u9669\u5f88\u53ef\u6015\u3002 \u3000\u3000\u8bf4\u4e0d\u5b9a\u90a3\u5c31\u662f\u51b3\u5b9a\u90ed\u5927\u8def\u4e2d\u5b58\u6b7b\u4ea1\u7684\u5239\u90a3\u95f4\u3002 \u3000\u3000\u7b49\u5f85\u672c\u5c31\u662f\u4ef6\u5f88\u75db\u82e6\u7684\u4e8b\u4f55\u51b5\u4ed6\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u7b49\u7684\u662f\u4ec0\u9ebd\u3002 \u3000\u3000\u90ed\u5927\u8def\u521a\u53f9\u4e86\u53e3\u6c14\xb7\u795e\u6848\u4e18\u7684\u76cf\u5de5\u7a81\u7136\u706d\u5382\u3002 \u3000\u3000\u8fd9\u91cc\u5e76\u6ca1\u6709\u98ce\u76cf\u71c3\u5f97\u6b63\u597d\u7684\u706f\u600e\u9ebd\u4f1a\u65e0\u7f18\u65e0\u6545\u7184\u706d \u3000\u3000\u90ed\u5927\u8def\u76b1\u4e86\u7f06\u7709\u8d70\u8fc7\u53bb\u4ed4\u7ec6\u770b\u4e86\u534a\u5929\uff0c\u624d\u53d1\u73b0\u8fd9\u76cf\u706f\u7a81\u7136\u7184\u706d\xb7\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u706f\u91cc\u7684\u6cb9 \u5df2\u67af\u4e86\u3002 \u3000\u3000\u706f\u867d\u662f\u81ea\u5df1\u5ab3\u7684\u4f97\u795e\u6848\u4e0b\u90e8\u597d\u50cf\u6709\u6837\u4e1c\u897f\u5728\u4e0d\u505c\u7684\u52a8\u4e0d\u505c\u7684\u6296\u3002 \u3000\u3000\u90ed\u5927\u8def\u7acb\u523b\u5f8c\u9000\u53c1\u6b65\u6c89\u58f0\u9053\u201c\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u5e94\u4f46\u795e\u6848\u4e0b\u7684\u90a3\u6837\u4e1c\u897f\xb7\u5374\u6296\u5f97\u66f4\u5389\u5bb3\u6296\u5f97\u8986\u6848\u7684\u795e\u9992\u90fd\u8d77\u4e86\u9635\u9635\u6ce2\u7eb9\u3002 \u3000\u3000\u90ed\u5927\u8def\u7a81\u7136\u51b2\u8fc7\u53bb\u628a\u6380\u8d77\u4e86\u795e\u6162\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u6014\u4efb\u3002 \u3000\u3000\u5728\u5982\u6b64\u6df1\u591c\u5982\u6b64\u8352\u50fb\u7684\u5730\u65b9\uff0c \u3000\u3000\u4eae\u8fd9\u9634\u68ee\u8be1\u79d8\u7684\u9f99\u738b\u5e99\u91cc\u9648\u9609\u94b1\u7834\u7684\u795e\u6848\u4e0b\xb7\u7ade\u6709\u4e2a\u5341\u516d\u4e03\u5c81\u7f8e\u5982\u6625\u82b1\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u4e3a\u5382\u8981\u5230\u8fd9\u91cc\u6765\u90ed\u5927\u8def\u4e5f\u4e0d\u77e5\u9047\u591a\u5c11\u5947\u5947\u602a\u602a\u7684\u4eba\u3001\u5947\u5947\u602a\u602a\u7684\u4e8b\u3001\u751a\u81f3\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f \u5192\u4e86\u751f\u547d\u7684\u5371\u9669\u3002 \u3000\u3000\u8fd9\u795e\u6848\u4e0b\u85cf\u7684\uff0c\u65e0\u8bba\u662f\u591a\u51f6\u9669\u7684\u57cb\u4f0f\u591a\u53ef\u6015\u7684\u654c\u4eba\u4ed6\u90fd \u3000\u3000\u53ef\u662f\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u9047\u89c1\u7684\u7ade\u53ea\u4e0d\u8fc7\u662f\u8fd9\u9ebd\u6837\u4e2a\u5c0f\u7ad9\u5a18\u3002 \u3000\u3000\u5979\u770b\u6765\u662f\u90a3\u9ebd\u5a07\u5c0f\u90a3\u9ebd\u53ef\u4f36\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u53c8\u5355\u8584\u5f97\u6839\u3002 \u3000\u3000\u5979\u5168\u8eab\u6296\u4e2a\u4e0d\u505c\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u56e0\u4e3a\u51b7\u8fd8\u662f\u56e0\u4e3a\u5bb3\u6015\u3002 \u3000\u3000\u770b\u89c1\u90ed\u5927\u8def\xb7\u5979\u6296\u5f97\u66f4\u5389\u5bb3\uff0c\u53cc\u624b\u62b1\u4f4f\u5382\u80f8\uff0c\u5168\u8eab\u90fd\u7ecd\u6210\u4e86\u56e2\u7f8e\u56db\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u60ca\u60e7 \u548c\u4e5e\u601c\u4e4b\u610f\xb7\u597d\u5bb9\u6613\u624d\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u51fa\u4e86\u51e0\u4e2a\u5b87\u201c\u6c42\u6c42\u4f60\uff0c\u9976\u4e86\u6211\u5427\u201d\u2026\u201c \u3000\u3000\u90ed\u5927\u8def\u5374\u8fd8\u662f\u6014\u5728\u90a3\u91cc\uff0c\u4e5f\u8fc7\u4e86\u5f88\u4e45\u624d\u80fd\u8bf4\u5f97\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u4f60\u662f\u4ec0\u9ebd\u4eba\uff0c\u600e\u9ebd\u4f1a\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u7684\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u5631\u5507\u53d1\u767d\u7530\u58f0\u8bf4\u9053\u201c\u6c42\u6c42\u4f60\u2026\u3002\u2019\u9976\u4e86\u6211\u5427\u2026\u3002\u5c0f \u3000\u3000\u5979\u867d\u7136\u5df2\u88ab\u5413\u5f97\u8fde\u9b42\u90fd\u98de\u4e86\u9664\u4e86\u8fd9\u4e24\u53e5\u8bdd\u4e4b\u5916\uff0c\u5df2\u4e0d\u4f1a\u8bf4\u522b\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u5382\u53e3\u6c14\xb7\u9053\u201c\u4f60\u7528\u4e0d\u6c42\u6211\uff0c\u6211\u53ef\u4e0d\u662f\u6765\u5bb3\u4f60\u7684\u3002\u201d \u3000\u3000\u5c0f\u9f13\u59fb\u77aa\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\xb7\u624d\u6e10\u6e10\u56de\u8fc7\u795e\u6765\u9053\u201c\u4f60\u2500\u4e00\u2500\u4f60\u96be\u9053\u4e2a\u662f\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u4e2a\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u59cb\u5a18\u9053\u201c\u628a\u6211\u7ed1\u5230\u8fd9\u91cc\u6765\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u82e6\u7b11\u9053\u201c\u5f53\u7136\u4e0d\u662f\u4f60\u96be\u9053\u8fde\u7ed1\u4f60\u5230\u8fd9\u91cc\u6765\u7684\u4eba\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\uff1f\u201d\u5c0f\u59a8\u5a18\u54ac\u5634\u5c5e\u9053 \u201c\u6211\u2026\u2026\u6211\u6839\u672c\u5c31\u6ca1\u6709\u770b\u89c1\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u672a\u4f60\u662f\u600e\u9ebd\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u773c\u5708\u5df2\u7ea2\u5382\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u80fd\u54ed\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5927\u8def\u903c\u7d27\u9053\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u6211\u7edd\u4e0d\u4f24\u5bb3\u4f60\u6240\u4ee5\u73b0\u5728\u4f60\u5df2\u7528\u4e0d\u5bb3\u6015\u6709\u8bdd\u4fb5\u6162\u8bf4\u4e5f\u6ca1\u5173 \u7cfb\u3002\u201d \u3000\u3000\u4ed6\u4e2a\u5b89\u6170\u5979\u53cd\u800c\u597d\u8fd9\u9ebd\u6837\u5b89\u6170\u5979\u8fd9\u5c0f\u59a8\u5a18\u53cd\u5012\u63a9\u4f50\u8138\u5931\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u53c8\u4e0d\u7fd4\u8be5\u5982\u4f55\u662f\u597d\u4e86\u3002 \u3000\u3000\u8981\u53eb\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u59a8\u5a18\u5927\u54ed\u573a\u65e0\u8bba\u4ec0\u9ebd\u6837\u7537\u4eba\u90fd\u53ef\u4ee5\u505a\u5f97\u5230\u3002 \u3000\u3000\u4f46\u8981\u53eb\u5979\u4e0d\u54ed\xb7\u5c31\u5f97\u800d\u6709\u7ecf\u9a8c\u5f88\u4e30\u5bcc\u7684\u7537\u4eba\u624d\u884c\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u65b9\u5706\u90ed\u5927\u8def\u7684\u7ecf\u9a8c\u5e76\u4e0d\u4e30\u5bcc\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53ea\u6709\u5728\u65c1\u8fb9\u770b\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u5382\u591a\u4e45\uff0c\u8fd9\u5c0f\u607c\u5a18\u5200\u603b\u7b97\u62bd\u62bd\u6ce3\u6ce3\u7684\u505c\u4f4f\u4e86\u54ed\u58f0\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fd9\u624d\u677e\u53e3\u6c14\u67d4\u58f0\u9053\u201c\u96be\u9053\u4f60\u8fde\u81ea\u5df1\u662f\u600e\u9ebd\u6765\u7684\u90fd\u4e0d\u6b22\u4e50\u82f1\u96c4\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u59a8\u5a18\u8fd8\u662f\u7528\u624b\u8499\u8138\u9053\u201c\u6211\u672c\u6765\u5df2\u7761\u4e86\u5f8c\u6765\u7a81\u7136\u9192\u8fc7\u6765\u65f6\u5df2\u7ecf\u5728\u8fd9\u5730\u65b9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u9192\u8fc7\u6765\u7684\u65f6\u5019\u8fd9\u91cc\u96be\u9053\u6ca1\u6709\u522b\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u59d0\u9053\u201c\u8fd9\u91cc\u53c8\u9ed1\u53c8\u51b7\u6211\u5b9e\u5728\u6015\u5f97\u8981\u547d\u5e78\u597d\u603b\u7b97\u5728\u684c\u4e0a\u6a21\u5230\u4e86\u5757\u706b\u77f3\u201d\u2026\u201c \u3000\u3000\u795e\u6848\u7684\u706f\u65c1\u8fb9\u679c\u7136\u6709\u526f\u706b\u77f3\u706b\u5200\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6240\u4ee5\u4f60\u5c31\u5c06\u8fd9\u91cc\u7684\u706f\u5168\u90fd\u70b9\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u603b\u7b97\u660e\u767d\u4e86\u4ef6\u4e8b\u60c5\uff0c\u4f46\u5374\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u521a\u8fd9\u91cc\u65e2\u7136\u6ca1\u6709\u4eba\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u4e58\u673a\u9003\u8d70 \u5462\uff1f\u201d \u3000\u3000\u5c0f\u59cb\u5a18\u9053\u201c\u6211\u672c\u6765\u662f\u60f3\u9003\u8d70\u7684\uff0c\u53ef\u662f\u4e00\u51fa\u4e86\u95e8\u5916\u9762\u66f4\u9ed1\u66f4\u51b7\u6211\u2026\u2026\u6211\u8fde\u6b65\u90fd\u4e0d\u6562\u5f80\u5916\u8d70 \u4e86\u3002\u201d \u3000\u3000\u76f4\u5230\u73b0\u5728\u5979\u8eab\u5b50\u8fd8\u5728\u8f7b\u8f7b\u7684\u53d1\u6296\u4f46\u8bf4\u8bdd\u603b\u7b97\u5df4\u6e05\u695a\u4e86\u4e9b\u3002 \u3000\u3000\u4e2a\u8db3\u4e0d\u51fa\u6237\u7684\u56fe\u5973\xb7\u9192\u6765\u65f6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u5728\u7834\u5e99\u91cc\xb7\u5c45\u7136\u8fd8\u6ca1\u6709\u5413\u5f97\u53d1\u75af\xb7\u5df2\u7ecf\u662f\u5947\u8ff9 \u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u5979\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u601c\u63aa\u4e4b\u610f\u3002 \u3000\u3000\u5979\u7684\u624b\u867d\u7136\u8fd8\u662f\u8499\u8138\uff0c\u5374\u4e5f\u5df2\u5728\u6307\u7f1d\u91cc\u5077\u5077\u7684\u770b\u90ed\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u6765\u7684\u786e\u4e0d\u8c61\u662f\u4e2a\u574f\u4eba\u7684\u6837\u5b50\u975e\u4f46\u4e0d\u8c61\u4e5f\u7684\u786e\u4e0d\u662f\u3002 \u3000\u3000\u4ed6\u672c\u6765\u60f3\u83b7\u5979\u4ece\u684c\u4e88\u4e0b\u7ad9\u8d77\u6765\u7684\uff0c\u4f46\u521a\u4f38\u51fa\u624b\u53c8\u7acb\u523b\u7f29\u5382\u56de \u3000\u3000\u5979\u6a21\u6837\u867d\u7136\u957f\u5f97\u5a07\u5f31\u4f46\u5374\u5df2\u53d1\u80b2\u5f97\u5f88\u6210\u719f\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u5355\u8584\u5f97\u53ef\u601c\u3002 \u3000\u3000\u5979\u7684\u4e54\u65e2\u5df2\u5728\u8499\u4f50\u8138\uff0c\u5c31\u4e0d\u80fd\u518d\u53bb\u63a9\u4f4f\u522b\u7684\u5730\u65b9\u3002 \u3000\u3000\u706f\u5149\u8fd8\u662f\u5f88\u660e\u4eae\u3002 \u3000\u3000\u90ed\u5927\u8def\u975e\u4f46\u4e0d\u6562\u4f38\u51fa\u624b\xb7\u8fde\u770b\u90fd\u4e0d\u6562\u518d\u770b\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u53e6\u76cf\u706f\u4e5f\u7184\u706d\u3002 \u3000\u3000\u7b2c\u53c1\u76cf\u706f\u7184\u5f97\u66f4\u5feb\uff0c\u8fd9\u4e9b\u706f\u91cc\u7684\u6cb9\u4eff\u4f5b\u672c\u5c31\u5df2\u5168\u90fd\u5c06\u736d\u5c3d\u3002 \u3000\u3000\u5ffd\u7136\u95f4\xb7\u4e03\u76cf\u706f\u5168\u90fd\u706d\u4e86\u3002 \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u201c\u56db\u5401\u201d\u58f0\xb7\u5df2\u8c05\u547c\u6251\u4eba\u4e86\u90ed\u5927\u8def\u7684\u6000\u91cc\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\uff0c\u90ed\u5927\u8def\u9aa4\u7136\u95f4\u8f6f\u7389\u6e29\u9999\u62b1\u4e86\u4e2a\u6ee1\u6000\u5fc3\u8df3\u7acb\u523b\u5c31\u52a0\u5feb\u4e86\u4e24\u500d\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u8b66\u544a\u81ea\u5df1\u4f60\u662f\u4eba\xb7\u4e0d\u662f\u755c\u7272\u4f60\u5343\u4e07\u4e0d\u53ef\u4e58\u4eba\u4e4b\u5371\uff0c \u3000\u3000\u201c\u975e\u4f46\u4e0d\u80fd\u505a\u8fde\u60f3\u90fd\u4e0d\u60f3\u5426\u5219\u4f60\u975e\u4f46\u5bf9\u4e0d\u8d77\u81ea\u5df1\u7684\u826f\u5fc3\xb7\u4e5f\u5bf9\u4e0d\u8d77\u71d5\u4e03\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u5728\u8b66\u6212\u81ea\u5df1\xb7\u5fc3\u60f3\u8981\u63a7\u5236\u81ea\u5df1\u53ef\u662f\u4e2a\u4eba\u8eab\u4e0a\u6709\u5f88\u591a\u5730\u65b9\uff0c\u90fd\u662f\u4e0d\u53d7\u81ea\u5df1\u63a7\u5236\u7684\u3002 \u3000\u3000\u7b2c\u4e2a\u5730\u65b9\u5c31\u662f\u4ed6\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u5904\u5973\u7684\u5e7d\u9999\u53d1\u6cfd\u95f4\u7684\u751c\u9999\uff0c\u9635\u9635\u968f\u547c\u5438\xb7\u94bb\u4eba\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u6000\u62b1\u95f4\u90a3\u6e29\u9999\u67d4\u8f6f\u7684\u611f\u89c9\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u8fd9\u8981\u547d\u7684\u9ed1\u6697\u3002 \u3000\u3000\u4e0d\u6b3a\u6697\u5ba4\xb7\u8fd9\u53e5\u8bdd\u8bf4\u6765\u867d\u7b80\u5355\u53ea\u6709\u4f53\u9a8c\u8fc7\u8fd9\u79cd\u60c5\u51b5\u7684\u4eba\u624d\u80fd\u77e5\u9053\u90a3\u662f\u591a\u9ebd\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e0d\u662f\u5723\u4eba\u4e5f\u4e0d\u662f\u795e\u82e5\u8bf4\u4ed6\u5728\u6b64\u65f6\u6b64\u523b\u8fd8\u80fd\u4e0d\u5206\u5fc3\u90a3\u5c31\u662f\u9a97\u4eba\u7684\u3002 \u3000\u3000\u53ef\u662f\u5374\u6709\u2018\u80a1\u66f4\u5f3a\u5927\u7684\u529b\u91cf\u4f7f\u5f97\u4ed6\u5c45\u7136\u80fd\u63a7\u5236\u4f50\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u529b\u91cf\u65e2\u4e0d\u662f\u795e\u4e5f\u4e0d\u662f\u522b\u7684\u800c\u662f\u4ed6\u5bf9\u71d5\u4e03\u90a3\u79cd\u6df1\u631a\u7eaf\u539a\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u63a8\u5f00\u8fd9\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u4ed6\u4e0d\u5fcd\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u8d50\u4f0f\u5728\u4ed6\u6000\u91cc\u5c31\u8c61\u662f\u4e00\u53ea\u53d7\u4e86\u65e0\u6570\u6298\u78e8\u548c\u63a0\u5413\u7684\u5c0f\u9e3d\u5b50\u7ec8\u4e01\u5728\u6ee1\u5929\u98ce\u96e8\u4e2d\xb7\u627e \u5230\u4e2a\u53ef\u4ee5\u5b89\u5168\u6816\u606f\u7684\u5730\u65b9\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f7b\u8f7b\u6512\u4f4f\u5979\u7684\u80a9\u67d4\u58f0\u9053\u201c\u4f60\u7528\u4e0d\u5bb3\u65e7\xb7\u6211\u9001\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u9053\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u662f\u771f\u7684\u800c\u6c11\u73b0\u5728\u5c31\u53ef\u4ee5\u9001\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u9053\u201c\u53ef\u662f\u2026\u2500\u2019\u4f60\u53c1\u66f4\u534a\u591c\u5230\u8fd9\u91cc\u6765\u5b9a\u6709\u5f88\u91cd\u8981\u7684\u4e8b\u4f60\u600e\u9ebd\u80fd\u653e\u4e0b\u81ea\u5df2\u7684\u4e8b\u9001\u6211 \u56de\u5de6\u5462\u201d \u3000\u3000\u90ed\u5927\u8def\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u80fd\u5230\u8fbe\u8fd9\u5730\u65b9\u5b9e\u5728\u4e0d\u5bb9\u6613\xb7\u8981\u4ed6\u5c31\u8fd9\u6837\u4f01\u4e86\u4e4b\u4ed6\u5b9e\u5728\u4e0d\u7518\u5fc3\u3002 \u3000\u3000\u90a3\u9ebb\u4e7e\u8bf4\u672c\u8d70\u968f\u65f6\u4f1a\u6765\u7684\uff0c\u4ed6\u8bf4\u4e2a\u5b9a\u968f\u65f6\u90fd\u80fd\u5f97\u5230\u71d5\u4e03\u7684\u6d88\u606f\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5df2\u65e0\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u4e00\u4e2a\u7537\u4e8e\u6c49\u6d3b\u5728\u4e16\u4e0a\uff0c\u975e\u4ecd\u8981\u201c\u6709\u6240\u4e0d\u4e3a\u201d\u8fd8\u5f97\u8981\u201c\u6709\u6240\u5fc5\u4e3a\u201d\u8fd9\u5176\u95f4\u7684\u9009\u62e9\u5f53\u7136\u72e0 \u96be\xb7\u4e14\u975e\u4f46\u8981\u6709\u52c7\u6c14\uff0c\u8fd8\u5f97\u8981\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u4ed6\u53c8\u62cd\u62cd\u8fd9\u5c0f\u59a8\u5a18\u7684\u80a9\u9053\u201c\u73b0\u5728\u5929\u5df4\u7ecf\u5feb\u4eae\u4e86\uff0c\u4f60\u7236\u6bcd\u82e5\u53d1\u73b0\u4f60\u5931\u8e2a\u5b9a\u4f1a\u5f88\u6025\u522b\u7684\u4eba\u82e5 \u77e5\u9053\u4f60\u4e00\u591c\u6ca1\u56de\u53bb\u66f4\u4e0d\u634c\u6b21\u4e50\u82f1\u7f50\u4f1a\u6709\u591a\u5c11\u8bdd\u3002\u73b0\u5728\u4f60\u5e74\u7eaa\u8fd8\u5c0f\u4e5f\u8bb8\u8fd8\u4e0d\u77e5\u9053\u8bdd\u6709\u591a\u9ebd\u53ef\u6015 \u53ef\u662f\u6211\u77e5\u8c0a\u3002\u201d \u3000\u3000\u90a3\u4e9b\u8bdd\u6709\u65f6\u975e\u4f46\u53ef\u4ee5\u6bc1\u6389\u4e2a\u4eba\u7684\u540d\u8a89\uff0c\u751a\u81f3\u4f1a\u6bc1\u6389\u5979\u7684 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u90ed\u5927\u8def\u66f4\u535c\u5b9a\u51b3\u5fc3\u65ad\u7136\u9053\u201c\u6240\u4ee5\u6211\u73b0\u5728\u975e\u9001\u4f60\u56de\u5de6\u4e0d\u4e60\u3002\u201d \u3000\u3000\u5c0f\u59a8\u5974\u5ffd\u7136\u7d27\u7d27\u62b1\u4f50\u4e86\u4ed6\u8fc7\u4e86\u5f88\u4e45\u624d\u67d4\u58f0\u9053\u201c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4f60\u8fd9\u9ebd\u597d \u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u5bb6\u5c31\u5728\u524d\u9762\u90a3\u6761\u5df7\u5b50\u91cc\u53f3\u8fb9\u7684\u7b2c\u53c1\u5bb6\u524d\u9762\u79cd\u68f5\u67f3\u6811\u7684\u90a3\u6247\u95e8\u3002\u201d \u3000\u3000\u5df7\u4e8e\u91cc\u5f88\u5b89\u7a33\u3002 \u3000\u3000\u4e1c\u65b9\u521a\u521a\u51fa\u73b0\u66d9\u8272\u7167\u9752\u77f3\u677f\u4e0a\u7684\u9732\u6c34\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f7b\u8f7b\u9053\u201c\u4ed6\u4eec\u4e00\u5b9a\u8fd8\u6ca1\u6709\u53d1\u73b0\u4f60\u5931\u8e2a\u4f60\u80fd\u4e0d\u80fd\u6e9c\u5f97\u8fdb\u5de6\u4e0d\u8ba9\u4ed6\u6253\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c0f\u59d1\u5a18\u70b9\u70b9\u5934\u9053\u201c\u6211\u53ef\u4ee5\u4ece\u5f8c\u95e8\u8fdb\u53bb\xb7\u6211\u4f50\u7684\u5c4b\u5b50\u5c31\u5728\u90a3\u8fb9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff1a\u201c\u4f60\u6700\u597d\u6362\u95ee\u5c5e\u5b50\u7761\xb7\u6700\u597d\u627e\u4e2a\u5e74\u7eaa\u5927\u7684\u8001\u5988\u4e8e\u966a \u3000\u3000\u4ed6\u60f3\u4e86\u60f3\u51b2\u5145\u9053\u201c\u8fd9\u4e24\u5929\u665a\u4e0a\uff0c\u6211\u4f1a\u968f\u65f6\u5728\u9644\u8fd1\u6765\u770b\u770b\u7684\u8bf4\u4e0d\u8d70\u6211\u8fd8\u53ef\u4ee5\u66ff\u4f60\u67e5\u51fa \u6765\xb7\u8c01\u662f\u90a3\u7ed1\u8d70\u4f60\u7684\u4eba\u3002\u201d \u3000\u3000\u4e1c\u65b9\u7684\u66d9\u8272\xb7\u7167\u4ed6\u7684\u8138\xb7\u7167\u4ed6\u8138\u4e0a\u7684\u6c57\u6b8a\u5c31\u4eff\u4f5b\u9732\u73e0\u822c\u6676\u83b9\u660e\u4eae\u2606 \u3000\u3000\u4ed6\u8138\u4e0a\u4e5f\u4eff\u4f5b\u5728\u53d1\u5149\u3002 \u3000\u3000\u5c0f\u59a8\u5a18\u4ef0\u80ce\u51dd\u89c6\u4ed6\u5ffd\u7136\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u95ee\u95ee\u6211\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u96be\u9053\u4f60\u6c38\u8fdc\u4e0d\u60f3\u518d\u6765\u770b\u6211\u5382 \u5417\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u52c9\u5f3a\u7b11\u4e86\u7b11\u67d4\u58f0\u9053\u201c\u6211\u662f\u4e2a\u6d6a\u5b50\uff0c\u4e49\u662f\u4e2a\u5f88\u968f\u4fbf\u7684\u4eba\u82e5\u662f\u4e0e\u4f60\u6765\u5f80\u4e5f\u5b9a\u4f1a\u6709\u522b\u4eba \u5728\u80cc\u5730\u8bf4\u8bdd\u7684\u3002\u201d \u3000\u3000\u5c0f\u59a8\u5a18\u9053\u201c\u6211\u4e0d\u6015\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u6211\u4f2f\u201c \u3000\u3000\u5c0f\u59a8\u5a18\u7728\u773c\xb7\u9053\u201c\u9636\u9633\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u56de\u7b54\uff0c\u53c8\u62db\u4e86\u62cd\u5979\u7684\u80a9\u9053\u201c\u4ee5\u5f8c\u4f60\u5c31\u4f1a\u77e5\u9053\u6211\u6015\u7684\u662f\u4ec0\u9ebd\u4e86\xb7\u73b0\u5728\u4f60\u8d76\u7d27\u4e56 \u4e56\u7684\u56de\u623f\u5de6\uff0c\u597d\u597d\u7761\u4e00\u89c9\uff0c\u6700\u597d\u80fd\u5c06\u8fd9\u4ef6\u4e8b\u5b8c\u5168\u5fd8\u6389\u3002\u201d \u3000\u3000\u5c0f\u5993\u5a18\u5782\u4e0b\u5934\xb7\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u9053\u201c\u4f60\u8d70\u51fa\u8fd9\u6761\u5df7\u4e88\u5458\u597d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u3002\u201d \u3000\u3000\u5c0f\u7ad9\u5a18\u4e5f\u6ca1\u6709\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\u5ffd\u7136\u641e\u8d77\u5934\u8d50\u7136\u7b11\u9053\u201c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\xb7\u597d\u4eba\u662f\u4e45\u8fdc\u4e0d\u4f1a\u5bc2\u5bde \u7684\u3002\u201d \u3000\u3000\u6668\u96fe\u5df2\u5347\u8d77\u3002 \u3000\u3000\u97e7\u590f\u7684\u6e05\u6668\u98ce\u4e2d\u8fd8\u5e26\u4e9b\u5bd2\u610f\u3002 \u3000\u3000\u4f46\u90ed\u5927\u8def\u5fc3\u91cc\u5374\u662f\u6e29\u6696\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u5e76\u6ca1\u6709\u4e8f\u8d1f\u522b\u4eba\xb7\u6ca1\u6709\u4e8f\u8d1f\u90a3\u4e9b\u5bf9\u4ed6\u597d\u7684\u670b\u53cb\u4e5f\u6ca1\u6709\u4e8f\u8d1f\u81ea\u5df1\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u80fd\u505a\u5230\u8fd9\u70b9\u90fd\u5df2\u5f88\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4ed6\u4ef0\u8d77\u5934\uff0c\u4f38\u4e86\u4e2a\u61d2\u819c\xb7\u957f\u957f\u5410\u51fa\u53e3\u6c14\u3002 \u3000\u3000\u201c\u8fd9\u5929\u771f\u957f\u3002\u201d \u3000\u3000\u5728\u8fd9\u5929\u91cc\u53d1\u751f\u7684\u4e8b\xb7\u51e0\u4e4e\u6bcf\u4ef6\u90fd\u662f\u5b8c\u5168\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u7684 \u3000\u3000\u90a3\u4e2a\u795e\u79d8\u7684\u9ebb\u4e8e\u3001\u90a3\u4e2a\u7a81\u7136\u5728\u9ed1\u8272\u4e2d\u6d88\u5931\u7684\u9a7c\u7763\u8001\u4eba\u3001\u90a3\u4e2a\u6b66\u529f\u6781\u9ad8\u6765\u5386\u8be1\u79d8\u7684\u72ec\u811a\u548c \u5c1a\u3001\u8fd8\u6709\u8fd9\u53ef\u601c\u53c8\u53ef\u7231\u7684\u5c0f\u59a8\u5a18\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u7684\u51fa\u73b0\xb7\u4e5f\u5168\u90fd\u51fa\u4e4e\u4ed6\u610f\u5916\u2606 \u3000\u3000\u4ed6\u4e5f\u906d\u9047\u4e86\u5f88\u591a\u5371\u9669\xb7\u53d7\u4e86\u5f88\u591a\u6c14\u8fd8\u662f\u8fde\u70b9\u71d5\u4e03\u7684\u6d88\u606f\u4e5f\u6ca1\u6709\u5f97\u5230\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u6709\u4e86\u6536\u83b7\u3002 \u3000\u3000\u4ed6\u505a\u7684\u4e8b\u867d\u7136\u5e76\u4e0d\u5e0c\u671b\u522b\u4eba\u62a5\u7b54\xb7\u4f46\u5374\u5df4\u4f7f\u81ea\u5df1\u5fc3\u91cc\u6e29\u6696\u6109\u7094\u3002 \u3000\u3000\u597d\u4eba\u6c38\u4e0d\u4f1a\u5bc2\u5bde\uff0c\u884c\u5584\u7684\u5165\u4e5f\u662f\u6709\u798f\u7684\u3002 \u3000\u3000\u201c\u4f60\u51fa\u4e86\u8fd9\u6761\u5df7\u4e8e\u6700\u597d\u5411\u53f3\u8f6c\uff0c\u201d \u3000\u3000\u90ed\u5927\u8def\u5e76\u4e0d\u77e5\u9053\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\u4f46\u4ed6\u5374\u8fd8\u662f\u5411\u53f3\u9762\u8f6c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4ebf\u523b\u53d1\u73b0\u4ef6\u5f88\u5947\u602a\u7684\u4e8b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u79d8\u5c4b\u5947\u4eba>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u79d8\u5c4b\u5947\u4eba \u3000\u3000\u51cc\u6668\u3002 \u3000\u3000\u6668\u96fe\u521a\u521a\u4ece\u9e45\u5375\u77f3\u94fa\u6210\u7684\u9053\u8def\u4e0a\u5347\u8d77\uff0c\u8def\u5f88\u7a84\u3002 \u3000\u3000\u90ed\u5927\u8def\u8f6c\u8fc7\u4ed3\u8fb9\u8fd9\u6761\u5df7\u5b50\u5c31\u770b\u5230\u6247\u5f88\u719f\u6089\u7684\u95e8\u6237\u3002 \u3000\u3000\u90a3\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4ed6\u66fe\u5230\u8fd9\u6247\u95e8\u91cc\u53bb\u8fc7\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u57ce\u5e02\u91cc\xb7\u4ed6\u51e0\u4e4e\u8fde\u4e00\u4e2a\u719f\u4eba\u90fd\u6ca1\u6709\xb7\u66f4\u6ca1\u6709\u6237\u719f\u6089\u7684\u4eba\u5bb6\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5c31\u60f3\u8d77\u8fd9\u6247\u95e8\u5c31\u662f\u767d\u5929\u4ed6\u8feb\u8e2a\u90a3\u9ebb\u4e88\u65f6\uff0c\u66fe\u7ecf\u95ef\u8fdb\u53bb\u8fc7\u7684\u90a3\u6247\u95e8 \u3000\u3000\u73b0\u5728\u91cc\u9762\u5df3\u6ca1\u6709\u706f\u5149\u3002 \u3000\u3000\u90a3\u56de\u9ec4\u808c\u7626\u7684\u6587\u592b\u662f\u4e0d\u662f\u53c8\u6b63\u5728\u505a\u90a3\u4e9b\u4f7f\u4ed6\u9762\u9ec4\u808c\u7626\u7684\u4e8b\uff1f \u3000\u3000\u90ed\u5927\u8def\u672c\u6765\u5c31\u60f3\u665a\u4e0a\u5230\u8fd9\u91cc\u6765\u641c\u67e5\u7684\u770b\u770b\u90a3\u9ebb\u4e8e\u4f1a\u4e0d\u4f1a\u5728\u8fd9\u91cc\u51fa\u73b0\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5374\u5df2\u6539\u53d8\u4e3b\u610f\u3002 \u3000\u3000\u4ed6\u518d\u5f80\u524d\u8d70\u53c8\u5411\u77f3\u8f6c\u3002 \u3000\u3000\u8fd9\u6761\u5df7\u4e8e\u7684\u8def\u4e0a\xb7\u94fa\u5f88\u6574\u9f50\u7684\u9752\u77f3\u677f\u770b\u6765\u8fdc\u6bd4\u522b\u7684\u5df7\u4e8e\u5e72\u51c0\u6574\u9f50\u3002 \u3000\u3000\u73b0\u5728\u5df2\u662f\u51cc\u6668\u5df7\u5343\u91cc\u5c45\u7136\u8fd8\u6709\u51e0\u76cf\u706f\u662f\u4eae\u7684\u3002 \u3000\u3000\u4ed6\u770b\u5230\u5176\u4e2d\u4e24\u76cf\u706f\u7b3c\u4e0a\u7684\u5b57\u773c\u775b\u7acb\u523b\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u7559\u9999\u9662\u3002\u201d \u3000\u3000\u90a3\u4f4d\u6094\u5170\u59d1\u5a18\u7684\u9999\u5de2\u539f\u6765\u5c31\u5728\u8fd9\u6761\u5df7\u5b50\u91cc\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u73b0\u5728\u5df2\u4e0d\u662f\u5bfb\u52b3\u7684\u65f6\u5019\xb7\u6885\u5170\u59d1\u5a18\u7684\u7389\u81c2\u8bf4\u4e0d\u5b9a\u5df2\u6210\u4e86\u522b\u4eba\u7684\u6795\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u7eb5\u7136\u662f\u4e2a\u767b\u5f92\u5b50\u73b0\u5728\u4e5f\u6728\u80fd\u53bb\u715e\u522b\u4eba\u7684\u98ce\u666f\u3002 \u3000\u3000\u5478\u8db3\u4ed6\u5fc3\u91cc\u5374\u4f3c\u5df2\u6709\u4e86\u79cd\u5f88\u7279\u6b8a\u7684\u611f\u89c9\xb7\u5c31\u4eff\u4f5b\u8bd7\u4eba\u5728\u89c5\u5f97 \u3000\u3000\u4ed6\u8d70\u5f97\u66f4\u5feb\uff0c\u518d\u5411\u53f3\u8f6c\u3002 \u3000\u3000\u8fd9\u91cc\u5df2\u662f\u5927\u8857\u3001\u4ed6\u6cbf\u8857\u5b9a\u4e86\u5341\u51e0\u6b65\u5c31\u770b\u5230\u4e86\u90a3\u95f4\u7cae\u98df\u574a\u4e5f\u770b\u5230\u4e86\u659c\u5bf9\u9762\u4f1a\u5bbe\u697c\u7684\u91d1\u5b87\u62db \u724c\u3002 \u3000\u3000\u8857\u9053\u65c1\u6709\u51e0\u4e2a\u77f3\u58a9\u5b50\xb7\u90ed\u5927\u8def\u5728\u4e0a\u9762\u5750\u4e86\u4e0b\u6765\u6c89\u601d\xb7 \u3000\u3000\u5c0f\u59d1\u5a18\u4efb\u7684\u90a3\u6392\u623f\u5b50\xb7\u5047\u5982\u662f\u7b2c\u6392\u3002 \u3000\u3000\u90a3\u592b\u5987\u4f50\u7684\u623f\u5b50\u5c31\u7b97\u662f\u7b2c\u4e8c\u7b2c\u3002 \u3000\u3000\u7559\u9999\u9662\u7684\u90a3\u6392\u623f\u5b50\u7b97\u662f\u7b2c\u53c1\u6392\u3002 \u3000\u3000\u7cae\u98df\u574a\u8fd9\u5c4b\u4e8e\uff0c\u5f53\u7136\u5c31\u662f\u7b2c\u56db\u6392\u3002 \u3000\u3000\u8fd9\u56db\u6392\u5c4b\u5b50\u91cc\uff0c\u90fd\u6709\u4e00\u6237\u4eba\u5bb6\uff0c\u548c\u90a3\u9ebb\u65bc\u662f\u6709\u5173\u7cfb\u7684\uff1b \u3000\u3000\u82e5\u4e0d\u662f\u90a3\u9ebb\u5b50\u8981\u4ed6\u5230\u9f99\u738b\u5e99\u53bb\uff0c\u4ed6\u600e\u4f1a\u9047\u89c1\u90a3\u5c0f\u59a8\u72d9\uff1f \u3000\u3000\u8fd9\u7a76\u7adf\u662f\u5de7\u5408\uff1f\u8fd8\u662f\u6545\u610f\u7684\u5b89\u6392\u662f\u4e0d\u662f\u56e0\u4e3a\u5979\u77e5\u9053\u67d0\u4e9b\u79d8\u5bc6\uff0c\u5374\u4e0d\u4fbf\u8bf4\u51fa\u6765\uff0c\u6240\u4ee5\u624d\u5982 \u6b64\u6697\u793a\u4ed6\uff1f \u3000\u3000\u5979\u77e5\u9053\u7684\u79d8\u5bc6\u662f\u4ec0\u9ebd\uff1f \u3000\u3000\u4e00\u5979\u662f\u4e0d\u662f\u6545\u610f\u8eb2\u5728\u90a3\u63b7\u6848\u4e0b\uff0c\u6545\u610f\u8981\u90ed\u5927\u8def\u53d1\u73b0\u7684 \u3000\u3000\u8fd9\u4e00\u5207\u96be\u9053\u90fd\u662f\u90a3\u9ebb\u4e8e\u65e9\u5c31\u5b89\u6392\u597d\u7684\uff1f \u3000\u3000\u4ed6\u8fd9\u9ebd\u6837\u505a\u7a76\u7adf\u662f\u4ec0\u9ebd\u7528\u610f\uff1f \u3000\u3000\u90ed\u5927\u8def\u7ad9\u8d77\u6765\u53c8\u6cbf\u539f\u6765\u7684\u8def\u91cd\u8d70\u4e86\u6b21\u3002\u8fd9\u56db\u6392\u623f\u5b50\uff0c\u6b63\u662f\u4e2a\u4e0d\u7b49\u8fb9\u7684\u56db\u89d2\u5f62\u3002 \u3000\u3000\u65e0\u8bba\u4ec0\u9ebd\u57ce\u5e02\u7684\u8857\u9053\xb7\u524d\u9762\u7684\u6392\u623f\u5b50\xb7\u5fc5\u5b9a\u662f\u7d27\u9ab8\u5f8c\u9762\u6392\u623f\u5b50\u7684\u3002 \u3000\u3000\u4f46\u7b2c\u6392\u623f\u5b50\u548c\u7b2c\u53c1\u6392\u623f\u5b50\u4e4b\u95f4\u5374\u6709\u6bb5\u5f88\u5bbd\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u7b2c\u4e8c\u6392\u623f\u4e8e\u548c\u7b2c\u56db\u6392\u4e5f\u6837\u3002\u6240\u4ee5\u8fd9\u56db\u6392\u623f\u5b50\u7684\u4e2d\u95f4\uff0c\u60f3\u5fc5\u5b9a\u6709\u5757\u7a7a\u5730\u3002 \u3000\u3000\u7ff1\u5927\u8def\u7684\u5fc3\u7a81\u7136\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u8fd9\u56db\u6392\u5c4b\u4e8e\u6545\u610f\u5efa\u6210\u8fd9\u6837\u5b50\u662f\u4e0d\u662f\u6709\u67d0\u79cd\u7279\u6b8a\u7684\u539f\u56e0\uff1f\u2019 \u3000\u3000\u8981\u627e\u51fa\u8fd9\u7b54\u6848\u6765\xb7\u53ea\u6709\u2606\u79cd\u6cd5\u5b50\u3002 \u3000\u3000\u90ed\u5927\u8def\u7eb5\u8eab\u63a0\u4e0a\u4e86\u7cae\u98df\u574a\u7684\u5c4b\u810a\u3002 \u3000\u3000\u7cae\u98df\u574a\u524d\u9762\u680b\u623f\u5b50\u662f\u67dc\u53f0\u300f\u9762\xb7\u5f8c\u9762\u8fd8\u6709\u4e2a\u9662\u5b50\u3002\u9662\u5b50\u4e24\u65c1\u7684\u53a2\u623f\uff0c\u597d\u50cf\u662f\u4f4f\u4eba\u7684\u5f8c\u9762 \u7684\u4e00\u680b\u5c31\u662f\u5806\u7cae\u98df\u7684\u6b22\u4e50\u82f1\u9192\u4ed3\u623f\u3002 \u3000\u3000\u518d\u5f8c\u9762\u5c31\u5e94\u8be5\u6ca1\u6709\u522b\u7684\u5c4b\u5b50\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u73b0\u5728\u5df2\u5230\u4e86\u5f8c\u9762\u90a3\u680b\u5806\u7cae\u98df\u7684\u4ed3\u623f\u5c4b\u810a\u4e0a\u7acb\u523b\u770b\u5230\u8fd9\u56db\u6392\u623f\u5c4b\u4e2d\u95f4\xb7\u679c\u7136\u8fd8\u6709\u4e00 \u680b\u5c4b\u5b50\u3002 \u3000\u3000\u8fd9\u56db\u680b\u623f\u5c4b\u5c31\u8c61\u662f\u56db\u9762\u5899\u5c06\u8fd9\u680b\u5c4b\u5b50\u56f4\u5728\u4e2d\u95f4\u6240\u4ee5\u8fd9\u680b\u5c4b\u5b50\u65e2\u6ca1\u6709\u51fa\u8def\u4e5f\u6ca1\u6709\u5927\u95e8\u3002 \u3000\u3000\u5929\u4e0b\u8d50\u6709\u4eba\u5c06\u5c4b\u5b50\u76d6\u5728\u8fd9\u79cd\u5730\u6709\u7684\uff1f \u3000\u3000\u63a0\u8fc7\u8fd9\u680b\u5c4b\u5b50\u7684\u5c4b\u810a\u5c31\u662f\u90a3\u5bf9\u592b\u5987\u4f4f\u7684\u5730\u65b9\xb7\u4e5f\u5c31\u662f\u7b2c\u4e8c\u6392\u5c4b\u5343\u3002 \u3000\u3000\u82e5\u662f\u4e0d\u7279\u522b\u7559\u610f\u65e0\u8bba\u8c01\u90fd\u4f1a\u4ee5\u4e3a\u8fd9\u680b\u5c4b\u4e8e\u4e5f\u548c\u522b\u7684\u5c4b\u4e8e\u8fde\u8d77\u7684\u5c31\u7b97\u6709\u591c\u884c\u4eba\u4ece\u5c4b\u810a\u4e0a\u7ecf \u8fc7\u4e5f\u7edd\u4e0d\u820d\u53d1\u73b0\u8fd9\u680b\u623f\u4e8e\u7684\u5947\u602a\u4e4b\u5904\u3002 \u3000\u3000\u4f46\u73b0\u5728\u90ed\u5927\u8def\u5df2\u53d1\u73b0\u5382\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u7684\u4e3b\u5165\u83ab\u975e\u5c31\u662f\u90a3\u9ebb\u4e8e\uff1f \u3000\u3000\u4ed6\u5c06\u5c4b\u6237\u5efa\u5728\u8fd9\u79cd\u5730\u65b9\u5f53\u7136\u8d39\u95e8\u826f\u5927\u7684\u529b\u82b1\u4e86\u5f88\u5927\u7684\u4ee3\u4ef7\xb7\u4e3a\u7684\u662f\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u83ab\u975e\u4ed6\u4e5f\u548c\u90a3\u72ec\u811a\u548c\u5c1a\u6837\xb7\u6709\u4ec0\u9ebd\u4e0d\u53ef\u544a\u4eba\u66f2\u9690\u79c1\uff1f\u6291\u6216\u662f\u4e3a\u4e86\u9003\u907f\u67d0\u4e2a\u6781\u5389\u5bb3\u7684\u4ec7\u4eba \u8ffd\u8e2a\u6240\u4ee5\u624d\u8981\u8fd9\u9ebd\u6837\u680b\u623f\u6b79\u8eb2\u8d77\u6765\uff1f \u3000\u3000\u8fd9\u623f\u5b50\u7684\u786e\u6bd4\u90ed\u5927\u8def\u6240\u770b\u8fc7\u7684\u4efb\u4f55\u5730\u65b9\u90fd\u9690\u79d8\u53ef\u662f\u4ed6\u4e3a\u4ec0\u9ebd\u4e49\u8981\u5728\u6709\u610f\u65e0\u610f\u95f4\uff0c\u8ba9\u7ff1\u5927 \u8def\u53d1\u73b0\u8fd9\u79d8\u5bc6\u5462\uff1f\u4e00 \u3000\u3000\u82e5\u662f\u4ed6\u65e5\u5df1\u6ca1\u6709\u7530\u51fa\u7ebf\u7d22\u90ed\u5927\u8def\u662f\u7edd\u5bf9\u627e\u4e0d\u5230\u8fd9\u5730\u65b9\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u60f3\u6765\u60f3\u53bb\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u4e0d\u4f46\u8be1\u79d8\u5df2\u6781\uff0c\u800c\u4e14\u590d\u6742\u5df2\u6c72\u3002 \u3000\u3000\u800d\u627e\u51fa\u8fd9\u4e9b\u95ee\u9898\u7684\u7b54\u6848\u4e5f\u53ea\u6709\u2606\u79cd\u6cd5\u5b50 \u3000\u3000\u4ed6\u8df3\u5382\u5382\u53bb\u3002 \u3000\u3000\u7cae\u98df\u574a\u7684\u4ed3\u623f\xb7\u5728\u8fd9\u680b\u5c4b\u4e8e\u4e4b\u9605\xb7\u8fd8\u6709\u9053\u8def\u5883\u5185\u662f\u6761\u957f\u4e3d\u72ed\u7684\u82b1\u5703\u3002\u2019\u2606 \u3000\u3000\u73b0\u5728\u6625\u82b1\u8fd8\u672a\u51cb\u8c22\uff0c\u5728\u6668\u96fe\u4e2d\u6563\u53d1\u6e05\u9999\u3002 \u3000\u3000\u518d\u8fc7\u53bb\u5c31\u662f\u6761\u957f\u5ed3\u6668\u54e6\u6b63\u7167\u5728\u94e3\u5f97\u5319\u4e0d\u67d3\u7684\u5730\u677f\u4e0a\u3002 \u3000\u3000\u56db\u535c\u9759\u6084\u6084\u7684\uff0c\u542c\u4e0d\u5230\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u8fde\u98ce\u90fd\u5439\u4e0d\u5230\u8fd9\u91cc\u3002 \u3000\u3000\u7ea2\u5c18\u95f4\u6240\u6709\u7684\u5207\u70e6\u607c\u3001\u76df\u7ea7\u3001\u60b2\u6b22\u4e5f\u7bad\u5df2\u5b8c\u5168\u88ab\u9694\u7edd \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u5df2\u5386\u5c3d\u6ca7\u6851\u3001\u770b\u900f\u4e16\u60c5\u3001\u5df2\u5b8c\u5168\u5fc3\u5982\u6b62\u6c34\u7684\u4eba\uff0c\u624d\u80fd\u4f7f\u5728\u8fd9\u91cc\u624d\u914d\u4f4f\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u90a3\u9ebb\u4e8e\u5e76\u4e0d\u8c61\u662f\u4e2a\u8fd9\u9ebd\u6837\u7684\u4eba\xb7\u96be\u9053\u662f\u90ed\u5927\u8def\u770b\u9519\u4e86\uff1f \u3000\u3000\u60f3\u9519\u4e86\uff1f\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u9000\u4e01\u56de\u53bb\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u770b\u5230\u4e00\u4e2a\u4eba\u4ece\u6388\u98a7\u5c3d\u5934\u5904\uff0c\u6084\u6084\u7684\u8d70\u51fa\u91c7\u3002 \u3000\u3000\u4e2a\u6625\u82b1\u822c\u7f8e\u4e3d\u7684\u5c11\u4ea5\u7a7f\u4ef6\u96ea\u767d\u7684\u888d\u5b50\u4e0d\u65bd\u8102\u7c89\uff0c\u8db3\u4e0a\u53ea\u7a7f\u53cc\u767d\u889c\u6ca1\u6709\u978d\u4eff\u4f5b\u751f\u4f2f\u811a\u6b65\u58f0 \u4f1a\u8e29\u788e\u8fd9\u4ee4\u4eba\u5fd8\u4fd7\u7684\u5e7d\u9759\u3002 \u3000\u3000\u5979\u624b\u91cc\u6367\u4e2a\u96e8\u8fc7\u5929\u9752\u7684\u74f7\u76bf\u9759\u6084\u6084\u7684\u8d70\u8fc7\u957f\u5ed3\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u5979\u5ffd\u7136\u56de\u8fc7\u5934\u8198\u4e86\u90ed\u5927\u8def\u9650\xb7\u90ed\u5927\u8def\u51e0\u4e4e\u5df2\u8ba4\u4e0d\u51fa\u5730\u4e86\u3002 \u3000\u3000\u8fd9\u6587\u859b\u7d20\u7684\u5c11\u4ea5\u8d6b\u7136\u7adf\u662f\u81ea\u5929\u6253\u626e\u5f97\u8c61\u5996\u602a\u6837\u7684\u63a9\u5170\u59a8\u9668\u3002 \u3000\u3000\u5979\u56de\u5934\u770b\u5382\u773c\u660e\u660e\u770b\u89c1\u4e86\u90ed\u5927\u8def\u4f46\u5374\u53c8\u8c61\u662f\u4ec0\u9ebd\u90fd\u6ca1\u6709\u770b\u89c1\u4e49\u5782\u4e0b\u5934\u9759\u9985\u6084\u7684\u5f80\u524d\u8d70\uff0c \u3000\u3000\u90ed\u5927\u8def\u5374\u5df2\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u5c31\u8fde\u90ed\u5927\u8def\u4e5f\u4e0d\u6562\u5728\u8fd9\u79cd\u5730\u65b9\u53eb\u51fa\u58f0\u6765\u4e0d\u5fcd\u6270\u4e71\u8fd9\u91cc\u7684\u5e7d\u9759\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u6014\u5728\u90a3\u91cc\uff0c\u770b\u3002 \u3000\u3000\u6885\u5170\u5df2\u6084\u6084\u7684\u63a8\u5f00\u6247\u98de\uff0c\u6084\u6084\u7684\u5973\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u8fd8\u662f\u6ca1\u6709\u58f0\u97f3\xb7\u6ca1\u6709\u52a8\u9759\u3002 \u3000\u3000\u8fd9\u91cc\u660e\u660e\u662f\u4e0d\u5bb9\u5916\u4eba\u4fb5\u5165\u7684\u7981\u5730\xb7\u90ed\u5927\u8def\u660e\u660e\u5c31\u59a8\u5728\u8fd9\u91cc\u5374\u504f\u504f\u6ca1\u6709\u4eba\u7406\u776c\u5c31\u597d\u50cf\u6839\u672c \u6ca1\u6709\u4ed6\u8fd9\u9ebd\u6837\u4e00\u4e2a\u4eba\u5b58\u5728\u3002 \u3000\u3000\u8fd9\u5c4b\u4e8e\u91cc\u4f4f\u7684\u7a76\u7adf\u662f\u4ec0\u9ebd\u4eba\uff1f\u4ed6\u4eec\u5bf9\u4ed6\u7a76\u7adf\u662f\u4ec0\u9ebd\u610f\u601d\u3002 \u3000\u3000\u90ed\u5927\u8def\u6014\u4e86\u534a\u5929\u5ffd\u7136\u5927\u6b65\u8d70\u8fc7\u53bb\uff0c\u5927\u6b65\u8de8\u4e0a\u4e86\u957f\u5ed3\u3002 \u3000\u3000\u5c4b\u91cc\u7684\u65e0\u8bba\u662f\u4eba\u662f\u9b3c\uff0c\u4ed6\u597d\u6b79\u90fd\u5f97\u53bb\u770b\u770b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e00\u811a\u521a\u8de8\u4e0a\u53bb\uff0c\u5374\u53c8\u7f29\u4e86\u56de\u6765\u3002 \u3000\u3000\u4ed6\u770b\u5230\u4e86\u81ea\u5df1\u811a\u4e0a\u7684\u6ce5\u3002 \u3000\u3000\u8fd9\u957f\u5eca\u4eae\u5f97\u5c31\u8c61\u662f\u4e00\u9762\u955c\u5b50\u5c31\u7528\u8fd9\u53cc\u6ce5\u811a\u8e29\u4e0a\u53bb\uff0c\u8fde\u4ed6\u90fd\u6709\u4e9b\u4e0d\u5fcd\uff0c\u53c8\u6709\u70b9\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u4ed6\u8131\u4e0b\u811a\u4e0a\u7684\u6ce5\u978b\uff0c\u889c\u5b50\u603b\u7b97\u8fd8\u4e7e\u51c0\u867d\u7136\u8fd8\u6709\u70b9\u81ed\u6c14\u4e5f\u987e\u4e0d\u4e86\u90a3\u9ebd\u591a\u4e86\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u5b9a\u8fc7\u53bb\u63a8\u5f00\u4e86\u90a3\u6247\u95e8\u3002\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u5c4b\u5b50\u91cc\u5c45\u7136\u662f\u7a7a\u7684\u4ec0\u9ebd\u90fd\u6ca1\u6709\u6ca1\u6709\u5e8a\xb7\u6ca1\u6709\u684c\u6905\u6ca1\u6709\u70b9\u6446\u8bbe\uff0c\u4e5f\u6ca1\u6709\u4e00\u70b9\u7070\u5c18\u3002 \u3000\u3000\u5730\u4e0a\u94fa\u5f88\u539a\u7684\u8349\u5e2d\xb7\u8349\u5e2d\u4e0a\u94fa\u5957\u96ea\u767d\u7684\u88ab\u6444\u4e00\u4e2a\u4eba\u7ee9\u5728\u88ab\u8925\u91cc\u3002 \u3000\u3000\u5c4b\u91cc\u5145\u6ee1\u4e86\u836f\u9999\u8fd9\u4eba\u663e\u7136\u5f97\u4e86\u91cd\u75c5\u3002 \u3000\u3000\u90ed\u5927\u8def\u5e76\u6ca1\u6709\u770b\u89c1\u4ed6\u7684\u80a1\uff0c\u56e0\u4e3a\u6b63\u6709\u4e2a\u957f\u53d1\u62ab\u80a9\u7684\u767d\u8863\u5c11\u5973\u6b63\u8dea\u5728\u4ed6\u65c1\u8fb9\u6162\u6162\u7684\u5582\u4ed6\u559d \u6885\u5170\u9001\u6765\u7684\u90a3\u7897\u836f\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u770b\u4e0d\u89c1\u8fd9\u5c11\u5973\u7684\u8138\xb7\u56e0\u4e3a\u5979\u4e5f\u662f\u80cc\u5bf9\u4ed6\u7684\u3002 \u3000\u3000\u53ea\u6709\u6885\u5170\u7684\u8138\u5411\u4ed6\xb7\u4e3d\u76ee\u660e\u660e\u770b\u89c1\u4ed6\u63a8\u5f00\u4e86\u95e8\u4f46\u8138\u4e0a\u5374\u504f\u504f\u8fd8\u662f\u8fde\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\xb7\u5c31 \u597d\u50cf\u6839\u672c\u6ca1\u6709\u5c06\u4ed6\u5f53\u505a\u4e2a\u6d3b\u4eba\u3002 \u3000\u3000\u90ed\u5927\u8def\u6068\u4e0d\u5f97\u4e94\u523b\u51b2\u8fc7\u53bb\u63ea\u4efb\u5979\u7684\u5934\u53d1\uff0c\u95ee\u95ee\u5979\u76fc\u8d54\u662f\u4e0d\u662f\u957f\u5728\u5934\u9876\u4e0a\u7684\uff1f \u3000\u3000\u4f46\u8fd9\u5c4b\u5343\u91cc\u5b9e\u5728\u592a\u9759\u5df2\u9759\u5f97\u597d\u50cf\u4e2a\u795e\u6bbf\u4f3c\u7684\u4ee4\u4eba\u89c9\u5f97\u6709\u79cd\u4e0d\u53ef\u5192\u6ee8\u7684\u795e\u5723\u5e84\u4e25\u3002 \u3000\u3000\u90ed\u5927\u8def\u51e0\u4e4e\u53c8\u5fcd\u4e0d\u4f4f\u60f3\u9000\u56de\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u8981\u627e\u7684\u4eba\u5e76\u6c34\u5728\u8fd9\u91cc\u4f55\u51b5\u8fd9\u79cd\u6c14\u6c1b\u672c\u5c31\u662f\u4ed6\u6700\u53d7\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\u90a3\u957f\u53d1\u62ab\u80a9\u7684\u767d\u8863\u5c11\u5973\u5ffd\u7136\u6c89\u58f0\u9053\uff1a\u201c\u5feb\u8fdb\u6765\uff0c\u5173\u4e0a\u95e8\uff0c\u522b\u8ba9\u98ce\u5439\u8fdb \u6765\u3002\u201d \u3000\u3000\u542c\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\xb7\u5c31\u597d\u50cf\u65e9\u5c31\u77e5\u9053\u90ed\u5927\u8def\u4f1a\u6765\xb7\u53c8\u597d\u50cf\u5c06\u7ff1\u5927\u8def\u5f53\u505a\u81ea\u5df1\u5bb6\u91cc\u7684\u4eba\u6837\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fde\u5fc3\u8df3\u90fd\u5df2\u51e0\u4e4e\u505c\u6b62\u3002 \u3000\u3000\u8fd9\u660e\u660e\u662f\u71d5\u4e03\u7684\u58f0\u97f3\u3002 \u3000\u3000\u96be\u9053\u8fd9\u957f\u53d1\u62ab\u80a9\u66f2\u767d\u8863\u5c11\u5973\u5c31\u662f\u71d5\u4e03\uff1f \u3000\u3000\u95e8\u5df2\u5173\u4e0a\u4e86\u3002 \u3000\u3000\u90ed\u5927\u8def\u6728\u5934\u4eba\u822c\u7ad9\u5728\u90a3\u91cc\u77aa\u5927\u5382\u773c\u775b\uff0c\u770b\u8fd9\u767d\u8863\u5c11\u5973\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u770b\u5230\u5979\u7684\u80cc\u5f71\u3002 \u3000\u3000\u5979\u7684\u80cc\u5f71\u7626\u524a\u836b\u6761\u6c14\u9ed1\u7684\u5934\u53d1\xb7\u4e91\u6c34\u822c\u62ab\u6563\u5728\u53cc\u80a9\u3002 \u3000\u3000\u90ed\u5927\u8def\u53cc\u624b\u7d27\u63e1\xb7\u7792\u91cc\u53d1\u5e72\xb7\u5fc3\u5374\u53c8\u773a\u5f97\u8c61\u662f\u8981\u773a\u51fa\u55d3\u5b50\u670d\u6765\u3002 \u3000\u3000\u4ed6\u771f\u60f3\u51b2\u8fc7\u53bb\u6273\u4f50\u5979\u7684\u80a9\u8ba9\u5979\u56de\u8fc7\u8138\u6765\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u6709\u591a\u6e34\u671b\u60f3\u770b\u770b\u5979\u7684\u80dc\u3002 \u3000\u3000\u5bf9\u662f\u4ed6\u5374\u53ea\u80fd\u8c61\u6728\u5934\u6837\u7ad9\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u6562\uff0c\u4e0d\u6562\u5192\u7eed\u4e86\u8fd9\u5e86\u4e25\u795e\u5723\u7684\u5730\u65b9\u66f4\u4e0d\u6562\u5192\u7eed\u4e86\u5979\u3002 \u3000\u3000\u75c5\u4eba\u7ec8\u6b79\u559d\u5b8c\u4e86\u7897\u91cc\u7684\u836f\u7bb1\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7ff1\u5927\u8def\u603b\u7b97\u770b\u5230\u4e86\u4ed6\u7684\u6ee1\u5934\u767d\u53d1\u5374\u8fd8\u662f\u6ca1\u6709\u770b\u89c1\u4ed6\u7684\u8138\u3002 \u3000\u3000\u5979\u8d61\u5728\u65c1\u8fb9\u8f7b\u8f7b\u653e\u4e0b\u4e86\u7897\u4e3a\u4ed6\u62c9\u8d77\u4e86\u68c9\u88ab\u663e\u5f97\u53c8\u4eb2\u5207\u3001\u53c8\u656c\u7231\u3001\u53c8\u4f53\u8d34\u3002 \u3000\u3000\u90ed\u5927\u8def\u82e5\u4e0d\u662f\u770b\u5230\u4e86\u4ed6\u7684\u6ee1\u5934\u767d\u53d1\u7b80\u76f4\u5df2\u5fcd\u4e0d\u4f4f\u8981\u6253\u7834\u918b \u3000\u3000\u8fd9\u8001\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u5979\u4e3a\u4ec0\u9ebd\u8981\u5bf9\u4ed6\u5982\u6b64\u4f53\u8d34\uff1f \u3000\u3000\u53ea\u542c\u4ed6\u8f7b\u8f7b\u7684\u54b3\u55fd\u8fc7\u5382\u534a\u664c\u5ffd\u7136\u9053\uff0c\u201c\u662f\u4e0d\u662f\u4ed6\u5df2\u7ecf\u6765\u5382\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u9053\u201c\u53eb\u4ed6\u8fc7\u6765\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u867d\u7136\u82cd\u8001\u8870\u5f31\u4ecd\u8fd8\u662f\u5e26\u79cd\u8bf4\u4e0d\u51fa\u7684\u6151\u4eba\u4e4b\u529b\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u7ec8\u4e8e\u9992\u6162\u5730\u56db\u8fc7\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u7ec8\u4e8e\u770b\u5230\u4e86\u5979\u7684\u8138\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u95f4\u5b87\u5b99\u95f4\u7684\u4e07\u4e8b\u4e07\u7269\u4f3c\u90fd\u5df2\u7a81\u7136\u6bc1\u706d\u505c\u987f\u3002 \u3000\u3000\u201c\u71d5\u4e03\u2026\u2026\u71d5\u4e03\u3002\u2026\u201c \u3000\u3000\u90ed\u5927\u8def\u5728\u5fc3\u91cc\u547c\u5524\u70ed\u6cea\u4f3c\u5df4\u5c06\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u547c\u5524\u6ca1\u6709\u58f0\u755c\xb7\u4f46\u5979\u5374\u4f3c\u80fd\u542c\u5f97\u89c1\xb7\u4e5f\u53ea\u6709\u5979\u624d\u80fd\u542c\u5f97\u89c1\u3002 \u3000\u3000\u5979\u773c\u775b\u91cc\u4e5f\u5df4\u73e0\u6cc5\u6ee1\u76c8\u3002 \u3000\u3000\u5386\u5c3d\u5382\u5343\u5343\u4e07\u82eb\u5386\u5c3d\u4e86\u5343\u4e07\u91cd\u6298\u78e8\u5343\u4e07\u91cd\u8003\u9a8c\uff0c\u4ed6\u603b\u7b97\u53c8\u89c1\u5230\u4e86\u5979\u3002 \u3000\u3000\u90a3\u4f60\u600e\u9ebd\u8981\u4ed6\u5c0f\u6d41\u6cc5\uff1f\u4f60\u600e\u77e5\u4ed6\u8fd9\u773c\u6cea\u662f\u8f9b\u9178\uff1f\u8fd8\u662f\u6b22\u559c\uff1f \u3000\u3000\u53ef\u662f\u4ed6\u7ec8\u4e8e\u5c06\u773c\u6cea\u5fcd\u4f50\u3002\u9664\u4e86\u5979\u4e4b\u5916\u4ed6\u4e0d\u613f\u4efb\u4f55\u4eba\u770b\u5230\u4ed6\u6d41\u6cea\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u65e0\u6cd5\u5fcd\u8010\u4f4f\u4e0d\u53bb\u770b\u5979\u7684\u8138\u3002 \u3000\u3000\u8fd9\u5df4\u4e0d\u662f\u6614\u56da\u90a3\u63e9\u53c1\u5206\u4f6f\u55c5\u3001\u53c8\u5e26\u53c1\u5206\u8c03\u76ae\u7684\u8138\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u5f20\u8138\u535c\u5269\u535c\u7684\u5df2\u53ea\u6709\u771f\u60c5\u3002 \u3000\u3000\u4e5f\u5c0f\u662f\u6614\u65e5\u90a3\u867d\u7136\u72e0\u8d43\u3001\u5374\u5145\u6ee1\u4e86\u5065\u5eb7\u6b22\u6986\u4e4b\u8272\u7684\u80a1\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u5f20\u8138\u662f\u82cd\u767d\u7684\u3001\u636e\u4f53\u7684\uff0c\u7f8e\u5f97\u4ee4\u4eba\u7684\u5fc3\u90fd\u788e\u5382\u3002 \u3000\u3000\u663e\u7136\u5979\u4e5f\u7ecf\u5386\u8fc7\u65e0\u6570\u6298\u78e8\uff0c\u65e0\u6570\u75db\u82e6\u3002 \u3000\u3000\u552f\u6ca1\u6709\u53d8\u7684\u662f\u5979\u7684\u773c\u775b\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u8fd8\u662f\u90a3\u9ebd\u660e\u4eae\xb7\u90a3\u9ebd\u575a\u5f3a\u3002 \u3000\u3000\u53ef\u662f\u5979\u4e3a\u4ec0\u9ebd\u5782\u4e0b\u5934\uff1f\u96be\u9053\u5979\u773c\u6cea\u5df4\u5fcd\u4e0d\u4f4f\u6d41\u4e86\u4e0b\u6765\uff1f \u3000\u3000\u8001\u4eba\u53c8\u5728\u8f7b\u8f7b\u7684\u54b3\u55fd\u3002 \u3000\u3000\u5979\u7ec8\u4e8e\u6084\u6084\u64e6\u5e72\u4e86\u773c\u6cea\u59cb\u8d77\u5934\xb7\u5411\u90ed\u5927\u8def\u62db\u4e86\u62db\u624b\u9053\u201c\u4f60\u6b22\u4e50\u82f1\u7f1d\u8fc7\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u670d\u775b\u8fd8\u662f\u76ef\u5728\u5979\u8138\u4e0a\u5c31\u8c61\u662f\u53d7\u4e86\u67d0\u79cd\u7530\u6cd5\u7684\u50ac\u7720\u4f3c\u7684\uff0c\u6b65\u6b65\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u53c8\u5782\u4e0b\u4e86\u5934\u9762\u988a\u4e0a\u4f3c\u5df2\u6cdb\u8d77\u7ea2\u6655\uff0c\u665a\u971e\u822c\u7684\u9189\u4eba\u3002 \u3000\u3000\u4ee5\u524d\u5979\u8138\u4e0a\u4e5f\u66fe\u6cdb\u8d77\u8fd9\u79cd\u7ea2\u6655\xb7\u4f46\u90ed\u5927\u8def\u5374\u5e76\u6ca1\u6709\u5341\u5206\u7559\u610f\u3002 \u3000\u3000\u7537\u4eba\u6709\u65f6\u4e5f\u4f1a\u8138\u7ea2\u7684\u3002 \u3000\u3000\u73b0\u5728\u90ed\u5927\u8def\u53ea\u6068\u4e0d\u5f97\u91cd\u91cd\u7ed9\u76ee\u5df1\u4e03\u516b\u5341\u4e2a\u8033\u522e\u4e8e\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u660e\u767d\u81ea\u5df1\u4e3a\u4ec0\u9ebd\u4f1a\u8fd9\u9ebd\u7b28\u4e3a\u4ec0\u9ebd\u5c45\u7136\u6ca1\u6709\u770b\u51fa\u5979\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8001\u4eba\u5ffd\u53c8\u53f9\u606f\uff0c\u9053\u201c\u4f60\u518d\u8fc7\u6765\u70b9\xb7\u8ba9\u6211\u770b\u770b\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u73b0\u5728\u9664\u4e86\u5979\u4e4b\u5916\xb7\u4ec0\u9ebd\u4eba\u7684\u8bdd\u5979\u90fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u71d5\u4e03\u5374\u54ac\u5634\u5507\xb7\u9053\u201c\u6211\u7239\u7239\u7684\u8bdd\u4f60\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\u201d\u90ed\u5927\u8def\u6014\u4e86\u6014\u9053\u201c\u4ed6\u2026\u3002\uff0e\u4ed6\u8001\u4eba\u5bb6 \u5c31\u662f\u4f60\u7684\u7236\u4eb2\uff1f\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u5145\u523b\u8d70\u8fd1\u4e86\u70b9\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u4e0d\u5c0a\u91cd\u4efb\u4f55\u4eba\u53ef\u4ee5\u542c\u4e0d\u89c1\u4efb\u4f55\u4eba\u8bf4\u7684\u8bdd\uff0c\u5766\u71d5\u4e03\u7684\u7236\u4eb2\xb7\u90a3\u5f53\u7136\u662f\u4f8b\u5916\u3002 \u3000\u3000\u8001\u4eba\u770b\u5230\u4e86\u4ed6\u4ed6\u4e5f\u770b\u5230\u4e86\u8fd9\u8001\u4eba\u3002 \u3000\u3000\u4ed6\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u5f88\u591a\u79cd\u4eba\u6240\u4ee5\u4e5f\u6709\u5f88\u591a\u79cd\u8138\u3002 \u3000\u3000\u6709\u7684\u8138\u957f\uff0c\u6709\u7684\u8138\u5706\uff0c\u6709\u7684\u817f\u4fca\u6709\u7684\u8138\u660e\u6717\u7167\u4eba\u6709\u7684\u8138\u5374\u6c34\u8fdc\u90fd\u8c61\u662f\u522b\u4eba\u6b20\u4ed6\u4e8c\u4e07\u4e24\u94f6 \u5b50\u6ca1\u8fd8\u4f3c\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u8fc7\u5f88\u591a\u4eba\u770b\u8fc7\u5f88\u591a\u79cd\u8138\u3002 \u3000\u3000\u4f46\u4ed6\u4ece\u672a\u770b\u8fc7\u8fd9\u9ebd\u6837\u5f20 \u3000\u3000\u4e25\u683c\u8bf4\u6765\uff0c\u8fd9\u5df2\u4e0d\u80fd\u7b97\u662f\u4e2a\u4eba\u7684\u8138\u9762\u662f\u4e2a\u6d3b\u50b2\u6444\u3002 \u3000\u3000\u957f\u4e3d\u65b9\u7684\u8138\u4e0a\u5df2\u53ea\u5269\u4e0b\u4e00\u5f20\u76ae\u5305\u9aa8\u5934\uff0c\u4eff\u4f5b\u5df3\u5b8c\u5168\u6ca1\u6709\u8840\u56e0\u3002 \u3000\u3000\u4f46\u5200\u75a4\u7684\u4e24\u65c1\u5374\u504f\u504f\u8fd8\u6709\u8840\u8089\u7ffb\u8d77\u3002 \u3000\u3000\u6700\u53ef\u6015\u7684\u5c31\u662f\u8fd9\u5200\u75a4 \u3000\u3000\u4e24\u6761\u5200\u75a4\u5728\u4ed6\u8138\u4e0a\u5212\u6210\u4e86\u4e2a\u5341\u5b57\u5de6\u9762\u7684\u4e00\u6761\uff0c\u4ece\u773c\u775b\u5212\u8fc7\uff0c\u518d\u5212\u8fc7\u9f3b\u5b50\xb7\u76f4\u5212\u5230\u5634\u89d2\u3002 \u3000\u3000\u53f3\u8fb9\u7684\u4e00\u6761\u81ea\u6709\u9893\u5212\u65ad\u9f3b\u6881\u76f4\u5212\u5230\u8033\u6839\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u5f20\u8138\u4e0a\u5df2\u5206\u8fa8\u4e0d\u51fa\u9f3b\u5b50\u7684\u5f62\u72b6\u53ea\u5269\u4e0b\u4e00\u53ea\u773c\u662d\u3002 \u3000\u3000\u773c\u775b\u534a\u95ed\u3002 \u3000\u3000\u5200\u75a4\u65e9\u5df2\u6536\u4e86\u53e3\u4e5f\u4e0d\u77e5\u662f\u591a\u5c11\u5e74\u524d\u7559\u4e0b\u6765\u7684\xb7\u4f46\u5200\u7624\u4e24\u65c1\u7ffb\u8d77\u7684\u8840\u8089\uff0c\u90fd\u4ecd\u7136\u9c9c\u8840\u822c\u6bb7 \u7ea2\u3002 \u3000\u3000\u8840\u7ea2\u7684\u5341\u5b57\u5200\u75a4\xb7\u886c\u4ed6\u67af\u75ea\u82cd\u767d\u7684\u8138\u770b\u6765\u5c31\u8c61\u662f\u4e2a\u6b63\u5728\u71c3\u70e7\u7684\uff0c\u5730\u72f1\u4e2d\u6076\u9b3c\u7684\u7b26\u53f7\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u6839\u672c\u5c31\u8c61\u662f\u6d3b\u5728\u5730\u72f1\u4e2d\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fde\u547c\u5438\u90fd\u4f3c\u5df2\u5c06\u505c\u987f\u3002 \u3000\u3000\u4ed6\u4e0d\u5fcd\xb7\u4e5f\u4e0d\u6562\u518d\u770b\u8fd9\u5f20\u8138\xb7\u5374\u53c8\u4e0d\u80fd\u8fdb\u907f\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u751a\u81f3\u4e0d\u80fd\u9732\u51fa\u4e1d\u6beb\u538c\u6076\u6050\u60e7\u7684\u8868\u60c5\u56e0\u4e3a\u8fd9\u8001\u4eba\u662f\u71d5\u4e03\u6cb9\u7236\u4eb2\u8001\u4eba\u4e5f\u6b63\u5728\u534a\u95ed\u773c\u770b \u4ed6\xb7\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\u201c\u4f60\u5c31\u662f\u7ff1\u5927\u8def\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4f60\u662f\u6211\u6587\u513f\u7684\u597d\u670b\u53cb\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u6211\u7684\u80a1\u5f88\u96be\u770b\u800c\u4e14\u5f88\u53ef\u6015\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6c89\u9ed8\u4e86\u534a\u664c\xb7\u7ec8\u4e8e\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8001\u4eba\u4e5f\u6c89\u9ed8\u4e86\u534a\u664c\u5589\u5499\u91cc\u5ffd\u7136\u53d1\u51fa\u77ed\u4fc3\u7684\u7b11\u58f0\xb7\u9053\u201c\u96be\u602a\u6211\u5973\u513f\u8bf4\u4f60\u662f\u8001\u5b9e\u4eba\xb7\u770b\u6765\u4f60 \u679c\u7136\u662f\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u8198\u4e86\u71d5\u4e03\u773c\u71d5\u4e03\u8fd8\u662f\u5782\u5934\u3002 \u3000\u3000\u6885\u5170\u7684\u80a1\u4e0a\u4e5f\u6709\u4e86\u7b11\u610f\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u5782\u535c\u5934\u6b96\u201c\u6709\u65f6\u6211\u4e5f\u5e76\u4e0d\u592a\u8001\u5b9e\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u53e5\u8001\u5b9e\u8bdd\u3002\u4ed6\u5ffd\u7136\u53d1\u89c9\u5728\u8fd9\u8001\u4eba\u9762\u524d\u8bf4\u8001\u5b9e\u8bdd\xb7\u662f\u79cd\u5f88\u597d\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u8001\u4eba\u679c\u7136\u5fbd\u5fbd\u9886\u9996\xb7\u9053\u201c\u4e0d\u9519\u4e0d\u8001\u5b9e\u7684\u4eba\uff0c\u4f11\u60f3\u5230\u8fd9\u91cc\u6765\u592a\u8001\u5b9e\u7684\u4eba\u4e5f\u4f11\u60f3\u627e\u5f97\u5230\u8fd9\u91cc \u6765\u7684\u3002\u201d\u4ed6\u5ffd\u53c8\u611f\u6168\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u4f60\u80fd\u5230\u8fd9\u91cc\u6765\uff0c\u603b\u7b97\u4e0d\u5bb9\u6613\u201c\u2026\u5b9e\u5728\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u542c\u5728\u8033\u91cc\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u6709\u4e9b\u9178\u9178\u7684\u3002 \u3000\u3000\u71d5\u4e03\u4e3a\u4ec0\u9ebd\u8981\u8ba9\u4ed6\u53d7\u8fd9\u8bb8\u591a\u6298\u78e8\uff1f\u4e3a\u4ec0\u9ebd\u8981\u4ed6\u5982\u6b64\u82eb\u84c4\u627e\u5bfb\uff1f \u3000\u3000\u8001\u4eba\u867d\u534a\u95ed\u773c\u5374\u5df3\u4f3c\u770b\u5230\u4ed6\u5fc3\u91cc\uff0c\u5ffd\u7136\u9053\u201c\u53eb\u4ed6\u4eec\u4e5f\u8fdb\u6765\u6b22\u4e50\u82f1\u8757\u5427\u3002\u201d \u3000\u3000\u6885\u5170\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u5979\u9759\u6084\u6084\u7684\u8d70\u8fc7\u53bb\uff0c\u81c3\u6084\u6084\u7684\u6253\u5f00\u4e86\u53e6\u6247\u95e8\u3002 \u3000\u3000\u95e8\u5916\u6709\u53c1\u4e2a\u4eba\u7b80\u9759\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7b2c\u4e2a\u4eba\u5c31\u662f\u90a3\u9ebb\u5b50\u3002\u73b0\u5728\u4ed6\u4e5f\u5df2\u6362\u4e86\u4ef6\u96ea\u767d\u7684\u957f\u888d\u4e00\u8fdb\u6765\u5c31\u5782\u624b\u7ad9\u5728\u5c4b\u89d2\xb7\u663e\u5f97\u65e2\u656c \u754f\xb7\u53c8\u5c0a\u656c\u5c31\u597d\u50cf\u5974\u624d\u770b\u5230\u4e86\u4ed6\u7684\u4e3b\u5b50\u6837\u3002 \u3000\u3000\u8ddf\u5728\u4ed6\u5f8c\u9762\u7684\u5f53\u7136\u5c31\u662f\u90a3\u9a7c\u5b50\u3002 \u3000\u3000\u7b2c\u53c1\u4e2a\u4eba\u624d\u662f\u90a3\u72ec\u811a\u548c\u5c1a\u3002 \u3000\u3000\u53c1\u4e2a\u4eba\u90fd\u7a7f\u540c\u6837\u7684\u767d\u888d\u5bf9\u8fd9\u8001\u4eba\u7684\u6001\u5ea6\u90fd\u540c\u6837\u5c0a\u656c\u3002\u53c1\u4e2a\u4eba\u90fd\u5782\u5934\u770b\u90fd\u6ca1\u6709\u770b\u90ed\u5927\u8def \u773c\u3002 \u3000\u3000\u8001\u4eba\u9053\u201c\u4f60\u4eec\u60f3\u5fc5\u662f\u8ba4\u5f97\u7684\u3002\u201d \u3000\u3000\u53c1\u4e2a\u4eba\u540c\u65f6\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u5374\u5fcd\u4e0d\u4f4f\u9053\u201c\u4ed6\u4eec\u867d\u8ba4\u5f97\u6211\u6211\u5374\u4e0d\u8ba4\u5f97\u4ed6\u8ba2\u3002\u201d \u3000\u3000\u8001\u4eba\u70ef\u5618\u9053\u201c\u73b0\u5728\u7684\u5e74\u9752\u4eba\u8ba4\u5f97\u4ed6\u4eec\u7684\u786e\u5df2\u4e0d\u591a\u4e86\u4f46\u4f60\u4e5f\u8bb8\u8fd8\u542c\u8fc7\u4ed6\u8ba2\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u54e6\u201d\u8001\u4eba\u9053\u201c\u4f60\u642c\u84dd\u6606\u662f\u4ea4\u8fc7\u624b\u7684\u96be\u9053\u8fd8\u6ca1\u6709\u770b\u51fa\u4ed6\u6b66\u529f\u6765\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u84dd\u6606\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u84dd\u6606\u662f\u4ed6\u7684\u4fd7\u53f7\uff0c\u81ea\u4ece\u4ed6\u5728\u5c11\u6797\u51fa\u5bb6\u5f8c\u8c03\u4eba\u5c31\u53ea\u77e5\u9053\u4ed6\u53eb\u94c1\u677e\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u72ec\u811a\u548c\u5c1a\u7adf\u662f\u5c11\u96c4\u95e8\u4e0b\u4e5f\u53ea\u6709\u5c11\u6797\u7684\u201c\u98ce\u96f7\u964d\u7530\u6756\u201d\uff0c\u624d\u80fd\u6709\u90a3\u79cd\u60ca\u4eba\u7684\u5a01\u529b\u3002 \u3000\u3000\u90ed\u5927\u8def\u8038\u7136\u52a8\u5bb9\u9053\u201c\u83ab\u975e\u4ed6\u5c31\u662f\u6614\u65e5\u6756\u964d\u5341\u9b54\u3001\u72ec\u95ef\u661f\u5bbf\u6d77\u7684\u7877\u7f57\u6c49\u2019\u94c1\u677e\u5927\u5e08\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4e0d\u9519\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u91d1\u7f57\u6c49\u6b63\u662f\u4ed6\u5c11\u5e74\u65f6\uff0c\u5fc3\u76ee\u4e2d\u5d07\u62dc\u7684\u5076\u50cf\u4e4b\u4e00\u4ed6\u4e03\u516b\u5c81\u65f6\u5c31\u5df2\u542c\u8fc7\u8fd9\u540d\u5b57\u5f8c\u6765\u53c8\u542c\u8bf4 \u8fd9\u4eba\u5df2\u7269\u5316\u4ed9\u53bb\u4e86\u60f3\u4e0d\u5230\u7adf\u9690\u540c\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u8001\u4eba\u9053\u201c\u5929\u5916\u6e38\u9f99\u795e\u9a7c\u5b50\u8fd9\u540d\u5b57\u4f60\u60f3\u4e5f\u8be5\u542c\u4eba\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u539f\u6765\u8fd9\u9a7c\u5b50\u7adf\u662f\u6614\u5e74\u6700\u8d1f\u76db\u540d\u7684\u8f7b\u529f\u9ad8\u624b\uff0c\u96be\u602a\u4ed6\u56de\u5934\u5c31\u5df2\u4e0d\u89c1\u8fd9\u4eba\u7684\u5f71\u5b50\u5382\u3002 \u3000\u3000\u8001\u4eba\u9053\u201c\u5929\u5916\u6e38\u9f99\u795e\u9a7c\u5b50\xb7\u5343\u53d8\u4e07\u5316\u667a\u591a\u661f\uff0c\u8fd9\u4e24\u4eba\u672c\u662f\u9f50\u540d\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5403\u60ca\u7684\u770b\u90a3\u9ebb\u4e88\xb7\u5931\u58f0\u9053\u201c\u96be\u9053\u4ed6\u5c31\u662f\u667a\u591a\u661f\u8881\u5927\u5148 \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u77e5\u9053\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u6014\u5728\u90a3\u91cc\xb7\u4e45\u4e45\u90fd\u5410\u4e0d\u51fa\u6c14\u6765\u3002 \u3000\u3000\u8fd9\u53c1\u4eba\u5728\u4e8c\u5341\u5e74\u524d\u5168\u90fd\u662f\u6c5f\u6e56\u4e2d\u58f0\u540d\u663e\u8d6b\u3001\u4e0d\u53ef\u4e16\u7684\u6b66\u6797\u540c\u4e8e\u3002 \u3000\u3000\u5728\u6c5f\u6e56\u4f20\u8bf4\u4e2d\u8fd9\u53c1\u4eba\u5df2\u5168\u90fd\u6b7b\u4eba\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u53c1\u4eba\u7adf\u5168\u90fd\u8eb2\u5728\u8fd9\u91cc\u800c\u4e14\u8fd8\u597d\u50cf\u90fd\u5df2\u6210\u4e86\u8fd9\u75c5\u8001\u4eba\u7684\u5974\u4ec6\u4e0b\u5c5e\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u90ed\u5927\u8def\u5fc3\u91cc\u53c8\u60ca\u3002 \u3000\u3000\u8c61\u91d1\u7f57\u6c49\u3001\u795e\u9a7c\u5b50\u8fd9\u6837\u7684\u7edd\u9876\u9ad8\u624b\u90fd\u5df2\u505a\u4e86\u8fd9\u8001\u4eba\u7684\u5974\u4ec6\xb7\u800c\u4e14\u5bf9\u4ed6\u656c\u754f\u5982\u6b64\u5c0a\u656c\u3002 \u3000\u3000\u8fd9\u5bb9\u4eba\u53c8\u662f\u4e2a\u4ec0\u9ebd\u6837\u7684\u4eba\u7269\u5462\uff1f \u3000\u3000\u90ed\u5927\u8def\u5b9e\u5728\u60f3\u4e0d\u51fa\u3002 \u3000\u3000\u5c31\u7b97\u662f\u6614\u65e5\u7684\u5c11\u6797\u65b9\u6587\u94c1\u7709\u590d\u751f\u91d1\u7f57\u6c49\u4e5f\u4e0d\u4f1a\u5bf9\u4ed6\u5982\u6b64\u656c\u754f\u5c31\u7b97\u662f\u6614\u65e5\u7684\u5929\u4e0b\u7b2c\u8f86\u4fa0\u518d \u751f\uff0c\u795e\u9a7c\u5b50\u548c\u667a\u591a\u5c4b\u4e5f\u7edd\u4e0d\u4f1a\u7518\u5fc3\u505a\u4ed6\u7684\u5974\u4ec6\u4e0b\u4eba\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u53c8\u6709\u4ec0\u9ebd\u529b\u91cf\uff0c\u80fd\u4f7f\u5f97\u8fd9\u4e8c\u4e2a\u4eba\u5bf9\u4ed6\u5982\u6b64\u670d\u4ece\u5c0a\u656c\uff1f \u3000\u3000\u8001\u4eba\u7f13\u7f13\u9053\u201c\u4ed6\u97f6\u4eca\u5929\u8ba9\u4f60\u5403\u5382\u4e0d\u5c11\u82eb\u4f60\u5fc3\u91cc\u662f\u4e0d\u662f\u5bf9\u4ed6\u76ef\u5f88\u4e0d\u6ee1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u60f3\u6447\u5934\u5374\u6ca1\u6709\u6447\u82e6\u7b11\u9053\u201c\u6709\u70b9\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4ed6\u97f6\u8fd9\u6837\u505a\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u5f88\u5947\u602a\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u901a\u201c\u4e5f\u6709\u4e00\u70b9\u2026\u2026\u4e0d\u6b62\u70b9\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4f60\u5e72\u65b9\u767e\u8ba1\u627e\u5230\u8fd9\u91cc\u6765\u4e3a\u4e86\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9890\u7530\u4e49\u8198\u4e86\u71d5\u4e03\u773c\u5450\u5450\u9053\u201c\u6765\u627e\u5979\u7684\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4e3a\u4ec0\u9ebd\u8981\u627e\u5979\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u597d\u50cf\u6c38\u8fdc\u90fd\u662f\u5728\u53d1\u95ee\uff0c\u800c\u4e14\u95ee\u5f97\u5ce8\u7840\u904f\u4eba\u4e1d\u6beb\u4e0d\u7ed9\u522b\u4eba\u8f6c\u56da\u7684\u4f59\u5730\u3002 \u3000\u3000\u90ed\u5927\u8def\u5782\u4e0b\u5934\u4eff\u4f5b\u6781\u59ae\u4e0d\u5b89\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u71d5\u4e03\u5374\u5ffd\u7136\u641e\u8d77\u5934\u6765\uff0c\u7528\u4e00\u53cc\u660e\u5982\u79ef\u6c34\u822c\u7684\u773c\u5f7c\u51dd\u89c6\u4ed6\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u7acb\u523b\u53c8\u5145\u6ee1\u4e86\u52c7\u6c14\u548c\u4fe1\u5fc3\u62ac\u8d77\u5934\xb7\u5927\u58f0\u9053\u201c\u56e0\u4e3a\u6b22\u4e50\u82f1\u63a7\u6211\u559c\u6b22\u5979\u60f3\u6c38\u8fdc\u8ddf \u5979\u7530\u5b88\u5728\u8d77\u3002\u201d \u3000\u3000\u8fd9\u672c\u662f\u5149\u660e\u6b63\u5927\u7684\u4e8b\xb7\u4ed6\u7528\u8fd9\u79cd\u5149\u660e\u78ca\u843d\u7684\u6001\u5ea6\xb7\u6b63\u663e\u51fa\u4e86\u4ed6\u7684\u771f\u8bda\u5766\u7387\u3002 \u3000\u3000\u8001\u4eba\u7684\u58f0\u97f3\u5374\u66f4\u4e25\u8083\u5b87\u5b87\u9053\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u8981\u5979\u4f5c\u59bb\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6beb\u4e0d\u8003\u8651\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u6c38\u4e0d\u53cd\u6094\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6c38\u4e0d\u53cd\u6094\u3002\u201d \u3000\u3000\u8001\u4eba\u534a\u95ed\u2500\u53ea\u773c\u7a81\u7136\u7741\u5f00\u773c\u775b\u91cc\u5c04\u51fa\u95ea\u7535\u822c\u7684\u5149\u3002 \u3000\u3000\u90ed\u5927\u8def\u4ece\u672a\u770b\u8fc7\u5982\u6b64\u903c\u4eba\xb7\u5982\u6b64\u53ef\u4f2f\u7684\u773c\u775b\uff0c\u4f46\u4ed6\u5374\u6ca1\u6709\u9003\u907f\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u662f\u6700\u91cd\u8981\u7684 \u523b\xb7\u56e0\u4e3a\u4ed6\u5fc3\u4e2d\u5766\u7136\u65e0\u6127\u3002 \u3000\u3000\u8001\u4eba\u903c\u89c6\u4ed6\u5389\u58f0\u9053\u201c\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u6447\u5934\u8fd9\u53e5\u8bdd\u6b63\u662f\u4ed6\u618b\u5728\u5fc3\u91cc\u4e45\u5df2\u60f3\u95ee\u51fa\u6765\u7684\u3002 \u3000\u3000\u8001\u4eba\u9053\u201c\u4f9d\u770b\u5230\u6211\u8138\u4e0a\u7684\u5341\u5b57\u5251\u4f24\xb7\u8fd8\u4e0d\u77e5\u9053\u6211\u8c01\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5fc3\u91cc\u7a81\u7136\u9635\u60ca\u60b8\xb7\u6574\u4e2a\u4eba\u90fd\u4e4e\u4e3a\u4e4b\u9707\u52a8\u8d77\u6765\u3002 \u3000\u3000\u2018\u5b57\u5251\u4f24\u75af\u72c2\u5341\u5b57\u5251 \u3000\u3000\u552f\u80fd\u5728\u75af\u72c2\u5341\u5b57\u5251\u4e0b\u9003\u751f\u7684\u4eba\u5c31\u662f\u5357\u5bab\u4e11\u83ab\u975e\u8fd9\u75c5\u91cd\u5782\u5371\u7684\u8001\u4eba\u624d\u662f\u771f\u6b63\u7684\u5357\u5bab\u4e11 \u3000\u3000\u90ed\u5927\u8def\u53ea\u89c9\u81ea\u5df1\u7684\u5934\u8111\u5728\u6655\u7729\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\xb7\u6c5f\u6e56\u4e2d\u58f0\u540d\u6700\u72fc\u85c9\u7684\u7b2c\u6076\u4eba\u5357\u5bab\u4e11\u7adf\u662f\u71d5\u4e03\u7684\u7236\u4eb2\u3002 \u3000\u3000\u96be\u602a\u71d5\u4e03\u80fd\u786e\u4fe1\u90a3\u9ed1\u8863\u4eba\u7edd\u4e0d\u662f\u5357\u5bcc\u4e11\u3002 \u3000\u3000\u81ea\u5899\u5f8c\u523a\u4eba\uff0c\u7a7f\u4eba\u9ed1\u8863\u4eba\u5fc3\u810f\u7684\u90a3\u5251\uff0c\u539f\u6765\u662f\u71d5\u4e03\u535c\u7684\u624b\u3002 \u3000\u3000\u5979\u8fd9\u9ebd\u6837\u663e\u7136\u662f\u75db\u6068\u8fd9\u4eba\u5047\u5192\u5979\u7236\u4eb2\u7684\u540d\u6240\u4ee5\u5979\u4e0d\u501f\u6740\u4e86\u4ed6\uff0c\u6765\u4fdd\u62a4\u81ea\u5df1\u7236\u4eb2\u7684\u540d\u8a89\u3002 \u96be\u602a\u5979\u4ece\u4e0d\u80af\u5410\u9732\u81ea\u5df1\u968f\u8eab\u4e16\u4eff\u4f5b\u6709\u5f88\u591a\u96be\u8a00\u4e4b\u9690\u3002 \u3000\u3000\u5979\u59cb\u7ec8\u4e0d\u80af\u5bf9\u90ed\u5927\u8def\u8bf4\u51fa\u81ea\u5df1\u662f\u5973\u513f\u8eab\u6015\u4e5f\u662f\u4e3a\u5382\u81ea\u60ed\u5bb6\u4e16\u751f\u6015\u90ed\u5927\u8def\u77e5\u9053\u4e86\u5979\u7684\u51fa\u8eab \u5f8c\uff0c\u4f1a\u6539\u53d8\u5bf9\u5979\u7684\u611f\u60c5\u3002 \u3000\u3000\u6240\u4ee5\u5979\u4e00\u76f4\u8981\u7b49\u5230\u4e34\u6b7b\u524d\u624d\u80af\u8bf4\u51fa\u6765\xb7\u6240\u4ee5\u5979\u8981\u9003\u907f\u3002 \u3000\u3000\u8fd9\u4e9b\u60f3\u6765\u4eff\u4f5b\u6c38\u8fdc\u65e0\u6cd5\u89e3\u91ca\u7684\u4e8b\uff0c\u73b0\u5728\u7ec8\u4e2d\u5b8c\u5168\u6709\u5382\u7b54\u6848\u3002\u4f46\u90ed\u5927\u8def\u5374\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u3002 \u3000\u3000\u5c4b\u5343\u91cc\u66f4\u9759\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5728\u904f\u89c6\u90ed\u5927\u8def\u53ea\u6709\u71d5\u4e03\u53c8\u5782\u4e0b\u5934\u3002\u5979 \u3000\u3000\u5979\u751f\u4f2f\u90ed\u5927\u8def\u7684\u56de\u7b54\u4f1a\u4f24\u900f\u5979\u7684\u5fc3\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8001\u4eba\u624d\u7f13\u7f13\u9053\u201c\u73b0\u5728\u4f60\u5df2\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u73b0\u5728\u4f60\u7b54\u5e94\u6539\u53d8\u4e3b\u6020\uff0c\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u73b0\u5728\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u56e0\u4e3a\u4e16\u4e0a\u5df2\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u6539\u53d8\u6211\u5bf9\u5979\u7684\u611f\u60c5\uff0c\u8fde\u6211\u81ea\u5df1\u90e1\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u662f\u5982\u6b64\u575a\u5b9a\xb7\u5982\u6b64\u771f\u8bda\u3002 \u3000\u3000\u4ed6\u8f6c\u5934\u53bb\u770b\u71d5\u4e03\u7684\u773c\u775b\uff0c\u71d5\u4e03\u4e5f\u5df2\u60c5\u4e0d\u81ea\u7981\xb7\u62ac\u8d77\u5934\u6765\xb7\u51dd\u89c6\u4ed6\u3002 \u3000\u3000\u5979\u76ee\u4e2d\u5df2\u53c8\u9732\u51fa\u6cea\u5149\u4f46\u5374\u5df2\u662f\u6b22\u771f\u7684\u6cea\u4e5f\u662f\u611f\u6fc0\u7684\u6cea\u3002 \u3000\u3000\u8fde\u6885\u5170\u7684\u773c\u775b\u90fd\u5df2\u6709\u4e9b\u6f6e\u6e7f\u3002 \u3000\u3000\u8001\u4eba\u5374\u4ecd\u7136\u4ee5\u5389\u7535\u822c\u7684\u76ee\u5149\u5728\u903c\u89c6\u90ed\u5927\u8def\xb7\u9053\u201c\u4f60\u8fd8\u662f\u613f\u610f\u5a36\u5979\u505a\u59bb\u5b50\uff1f\u201d\u7ff1\u5927\u8def\u9053 \u5177\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\u201c\u4f60\u613f\u610f\u505a\u5357\u7a9d\u4e11\u5973\u513f\u7684\u6587\u592b\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u662f\u3002\u201d \u3000\u3000\u8001\u4eba\u7684\u76ee\u5149\u5ffd\u7136\u8c61\u5bd2\u51b0\u5728\u6625\u6c34\u4e2d\u878d\u5316\u4e86\u54fa\u95fb\u9053\u201c\u597d\u4f60\u679c\u7136\u662f\u4e2a\u597d\u5b69\u4e8e\u2026\u2026\u71d5\u513f\u679c\u7136\u6ca1\u6709 \u770b\u9519\u6cf3\u3002\u201d \u3000\u3000\u4ed6\u53c8\u6162\u6162\u5730\u56fe\u8d77\u8d29\u5b57\u4e2d\u9053\u201c\u73b0\u4efb\u6211\u5df2\u53ef\u653e\u5fc3\u5c06\u5979\u4ea4\u7ed9\u4f60\u73b0\u5728\u5979\u5df2\u662f\u4f60\u7684\u59bb\u5b50\u3002\u201d \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u524d\u5c18\u5f80\u4e8b>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u524d\u5c18\u5f80\u4e8b \u3000\u3000\u6d1e\u623f\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u591a\u5c11\u4e2a\u672a\u6210\u4eb2\u7684\u5c11\u5e74\xb7\u5728\u5e7b\u60f3\u82b1\u70db\u4e4b\u591c\u6d1e\u623f\u91cc\u7684\u7f14\u65bd\u98ce\u5149\u53c8\u6709\u591a\u5c11\u4e2a\u5df3\u5782\u66ae\u7684\u8001 \u4eba\xb7\u5728\u56de\u5fc6\u90a3\u5927\u6d1e\u623f\u91cc\u7684\u751c\u871c\u548c\u6e29\u6696\uff1f \u3000\u3000\u5e7b\u60f3\u548c\u56de\u5fc6\u6c38\u8fdc\u90fd\u662f\u7f8e\u4e3d\u7684\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u82b1\u70db\u4e4b\u591c\u7684\u6d1e\u623f\u91cc\u901a\u5e38\u90fd\u6ca1\u6709\u56de\u4ebf\u5dfe\u90a3\u9ebd\u6e29\u6696\u751c\u871c\u98ce\u5149\u4e5f\u8fdc\u4e0d\u5982\u5e7b\u60f3\u4e2d\u7684\u90a3\u9ebd\u7eda \u4e3d\u3002 \u3000\u3000\u6709\u4e9b\u81ea\u4ee5\u4e3a\u5f88\u806a\u660e\u7684\u4eba\u65f6\u5e38\u90fd\u559c\u6b22\u5c06\u6d1e\u623f\u5f62\u5bb9\u6210\u4e2a\u575f\u5893\u751a\u5e01\u8fd8\u8bf4\u6d1e\u623f\u91cc\u53d1\u51fa\u7684\u58f0\u97f3\u6709\u65f6 \u5c31\u8c61\u662f\u4e2a\u5c60\u5bb0\u573a\u3002 \u3000\u3000\u6d1e\u623f\u5f53\u7136\u4e5f\u5c0f\u662f\u5730\u5893\u548c\u5c60\u5bb0\u573a\u3002 \u3000\u3000\u90a3\u672a\u6d1e\u623f\u7a76\u7adf\u662f\u4ec0\u9ebd\u6837\u5b50\u5462\uff1f \u3000\u3000\u6d1e\u623f\u901a\u5e38\u662f\u95f4\u5e76\u4e0d\u592a\u6e29\u6696\u7684\u5c4b\u4e88\u5230\u5904\u90fd\u662f\u7ea2\u7ea2\u7eff\u7eff\u7684\xb7\u5230\u5904\u90fd\u5145\u6ee1\u4e86\u6cb9\u6f06\u5473\u9053\u5185\u52a0\u4ea1\u8d3a \u5ba2\u4eec\u7559\u4e0b\u7684\u9152\u81ed\uff0c\u5728\u91cc\u9762\u803d\u535c\u4e24\u4e2a\u65f6\u8fb0\u8fd8\u80fd\u5c0f\u5410\u7684\u4eba\u5b9a\u662f\u4e2a\u6784\u9020\u5f88\u7279\u522b\u7684\u9f3b\u5b50\u548c\u80c3\u3002 \u3000\u3000\u6d1e\u623f\u91cc\u5f53\u7136\u6709\u7537\u4ea5\u4e24\u4e2a\u4eba\u8fd9\u4e24\u4e2a\u4eba\u901a\u5e38\u90fd\u4e0d\u4f1a\u592a\u719f\u6240\u4ee5\u4e5f\u4e0d\u4f1a\u6709\u5f88\u591a\u8bdd\u8bf4\u3002 \u3000\u3000\u6240\u4ee5\u5916\u9762\u5c31\u7b97\u5435\u7ffb\u4e86\u5929\xb7\u6d1e\u623f\u91cc\u5374\u901a\u5e38\u90fd\u5f88\u51b7\u9759\u3002 \u3000\u3000\u8d3a\u5ba2\u4eec\u867d\u7136\u5728\u62fc\u547d\u7684\u5403\u62fc\u547d\u7684\u559d\uff0c\u751f\u4f2f\u635e\u4e0d\u56de\u672c\u94b1\u4f3c\u7684\u4f46\u65b0\u90ce\u548c\u65b0\u5a18\u901a\u5e38\u90fd\u5728\u997f\u809a\u5b50\u3002 \u3000\u3000\u8fd9\u672c\u6765\u662f\u4ed6\u8ba2\u7684\u6d1e\u623f\u82b1\u70db\u4ec5\uff0c\u4f46\u8fd9\u5929\u5374\u597d\u50cf\u662f\u4e3a\u522b\u4eba\u8fc7\u7684\u3002 \u3000\u3000\u71d5\u4e03\u8499\u53a8\u7684\u7ea2\u5dfe\u5df2\u6380\u8d77\uff0c\u6b63\u5782\u5934\u5750\u5728\u5e8a\u6cbf\u770b\u81ea\u5df1\u7684\u7ea2\u7ee3\u978b\u3002 \u3000\u3000\u90ed\u5927\u8def\u8fdc\u8fdc\u7684\u5750\u5728\u5c0f\u5706\u684c\u65c1\u7684\u6905\u5b50\u4e0a\uff0c\u4f3c\u4e4e\u4e5f\u5728\u53d1\u6014\u3002 \u3000\u3000\u5979\u4e0d\u6562\u770b\u4ed6\u4ed6\u4e5f\u672f\u6b32\u770b\u5979\u3002 \u3000\u3000\u5047\u5982\u559d\u4e86\u70b9\u9152\u4ed6\u4e5f\u8bb8\u4f1a\u8f7b\u677e\u4e9b\xb7\u5999\u7684\u662f\u4ed6\u4eca\u706b\u504f\u504f\u6ca1\u6709\u559d\u3002 \u3000\u3000\u597d\u50cf\u53ea\u8981\u505a\u65b0\u90ce\u500d\u7684\u4eba\u8981\u559d\u9152\xb7\u9a6c\u4e0a\u5c31\u4f1a\u6709\u4e9b\u201c\u597d\u5fc3\u4eba\u201d\u8fc7\u6765\u62e6\u4f4f\u62a2\u66ff\u4ed6\u628a\u9152\u559d\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5c31\u662f\u5f88\u597d\u7684\u670b\u53cb\u672c\u6765\u6bcf\u5929\u90fd\u6709\u5f88\u591a\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u4f46\u505a\u5382\u5927\u59bb\u5c31\u597d\u50cf\u4e0d\u518d\u662f\u670b\u53cb\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7adf\u597d\u50cf\u5ffd\u7136\u53d8\u5f97\u5f88\u9065\u8fdc\u5f88\u751f\u758f\u5f88\u6015\u96be\u4e3a\u60c5\u3002 \u3000\u3000\u6240\u4ee5\u8c01\u4e5f\u5c0f\u597d\u610f\u601d\u5149\u5f00\u53e3\u3002 \u3000\u3000\u7ff1\u5927\u8def\u672c\u6765\u4ee5\u4e3a\u81fc\u5df1\u53ef\u4ee5\u5e94\u4ed8\u5f97\u5f88\u597d\u7684\uff0c\u4f97\u4e00\u8fdb\u5382\u6d1e\u623f\xb7\u5c31\u5ffd\u7136\u53d1\u89c9\u81fc\u5df1\u5c31\u8c61\u662f\u53d8\u6210\u4e86 \u4e2a\u5446\u6237\u3002 \u3000\u3000\u8fd9\u79cd\u60c5\u51b5\u4ed6\u5b9e\u5728\u4e0d\u4e60\u60ef\u3002 \u3000\u3000\u4ed6\u672c\u6765\u60f3\u4f17\u8fc7\u53bb\u5750\u5230\u70ed\u4e03\u8eab\u65c1\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u5382\u4ec0\u9ebd\uff0c\u4e24\u6761\u8d9f\u504f\u504f\u5728\u53d1\u8f6f\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u4e5f\u4e2d\u77e5\u8fc7\u5382\u591a\u4e45\u90ed\u5927\u8def\u53ea\u89c9\u5f97\u8fde\u8116\u5343\u90fd\u6709\u70b9\u53d1\u786c\u7684\u65f6\u5019 \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u9053\u201c\u6211\u8981\u7761\u5382\u3002\u201d \u3000\u3000\u5979\u7ade\u81fc\u5df1\u8bf4\u7761\u5c31\u7761\u8fde\u9776\u90fd\u4e0d\u8131\uff0c\u5c31\u5f80\u5e8a\u4e0a\u2018\u5012\u62c9\u8d77\u4e0a\u66f2\u7ee3\uff01 \u3000\u3000\u6cfb\u9e2f\u620f\u6c34\u7684\u7ea2\u4e1d\u88ab\uff0c\u628a\u81fc\u5df1\u8eab\u5b50\u7d27\u7d27\u7684\u88f9\u4f4d\u3002 \u3000\u3000\u5979\u9762\u671d\u5899\u8eab\u5b50\u59e5\u66f2\u5f97\u5c31\u8c61\u662f\u53ea\u867e\u7c73\u3002 \u3000\u3000\u90ed\u5927\u8def\u54ac\u7792\u5507\u770b\u5979\xb7\u76ee\u4e2d\u6ca5\u6e10\u6709\u4e86\u7b11\u610f\u5ffd\u7136\u9053\u201c\u4eca\u5929\u4f60\u600e\u9ebd\u6ca1\u6709\u59ff\u6211\u51fa\u53d1\uff1f\u201d \u3000\u3000\u82d1\u4e03\u5c0f\u776c\u4ed6\uff0c\u8c61\u662f\u5df2\u7761\u3002\u90ed\u5927\u8def\u7b11\u9053\u201c\u6709\u522b\u4eba\u5e86\u4f60\u7684\u5c5e\u65bc\u91cc\u4f60\u4e0d\u662f\u964b\u5c0f\u7684\u5417\uff1f\u201d \u3000\u3000\u71d5\u4e03\u672c\u6765\u8fd8\u662f\u4e0d\u60f3\u776c\u4ed6\u7684\u5374\u53c8\u504f\u4fa7\u5fcd\u4e2a\u4f4d\u9053\u201c\u4f60\u5c11\u8bf4\u51e0\u53e5\u6211\u5c31\u964b\u4e86\u4e2d \u3000\u3000\u90ed\u5927\u8def\u7728\u773c\u60a0\u60a0\u9053\u201c\u6709\u6211\u5728\u5c4b\u91cc\u4f60\u4e5f\u7761\u5f97\uff1f\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5634\u5507\u8f7b\u8f7b\u9053\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u522b\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u662f\u522b\u4eba\u662f\u4ec0\u9ebd\u4eba\u201d \u3000\u3000\u7b4b\u4e03\u5ffd\u7136\u201c\u66d9\u9648\u201d\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u5927\u5934\u9b3c\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u5947\u602a\u5947\u602a\u4f60\u600e\u9ebd\u4f1a\u5ac1\u7ed9\u6211\u8fd9\u5927\u5934\u9b3c\u7684\uff1f\u6211\u8bb0\u5f97\u4f60\u4ee5\u524d\u597d\u50cf\u8bf4 \u8fc7\uff0c\u5c31\u7b97\u5929\u4e0b\u7684\u7537\u4eba\u5168\u90fd\u6b7b\u5149\u4e86\xb7\u4e5f\u4e0d\u4f1a\u5ac1\u7ed9\u6211\u3002\u201d \u3000\u3000\u71d5\u4e03\u5ffd\u7136\u7ffb\u8fc7\u8eab\uff0c\u6293\u8d77\u4e86\u68b3\u5934\u7528\u529b\u7684\u5411\u4ed6\u6454\u4e86\u8fc7\u6765\u3002\u5979\u7684\u8138\u7ea2\u5f97\u5c31\u8c61\u662f\u4e2a\u521a\u6458\u4e0b\u7684\u719f\u82f9 \u679c\u3002\u6b22\u4e50\u835a\u63a7 \u3000\u3000\u6795\u5934\u53c8\u98de\u56de\u6765\u4e86\xb7\u5e26\u90ed\u5927\u8def\u7684\u4eba\u8d77\u98de\u56de\u6765\u7684\u3002 \u3000\u3000\u71d5\u4e03\u7ea2\u8138\u9053\u201c\u4f60\u2026\u201d\u4f60\u2026\u2026\u4f60\u60f3\u5e72\u4ec0\u9ebd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u60f3\u54ac\u4f60\u5203\u3002\u201d \u3000\u3000\u7c89\u7ea2\u8272\u7684\u7ee3\u5e10\u4e0d\u77e5\u4f55\u65f6\u5df2\u5782\u4e0b\u3002 \u3000\u3000\u5047\u5982\u6709\u4eba\u4e00\u5b9a\u8981\u8bf4\xb7\u6d1e\u623f\u91cc\u7684\u58f0\u97f3\u8c61\u5c60\u5bb0\u573a\xb7\u90a3\u9ebd\u8fd9\u5c60\u5bb0\u573a\u5b9a\u662f\u6740\u868a\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u8c61\u662f\u868a\u5b50\u53eb\u3002 \u3000\u3000\u90ed\u5927\u8def\u597d\u50cf\u5728\u8f7b\u8f7b\u9053\u201c\u5947\u602a\u771f\u5947\u602a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53c8\u5947\u602a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8eab\u4e3a\u4ec0\u9ebd\u70b9\u4e5f\u4e0d\u81ed\uff1f\u201d \u3000\u3000\u53ea\u542c\u5427\u201d\u7684\u54cd\u5c31\u597d\u50cf\u6709\u4eba\u6253\u868a\u5b50\u8d8a\u6253\u8d8a\u8f7b\u8d8a\u6253\u8d8a\u8f7b \u3000\u3000\u5929\u5df2\u7ecf\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u9526\u5e10\u4e2d\u521a\u521a\u5b89\u9759\u4e0b\u6765\u4e49\u8fc7\u4e86\u534a\u5929\xb7\u5c31\u542c\u5230\u7ff1\u5927\u8def\u8f7b\u8f7b\u9053\u201c\u4f60\u77e5\u9053\u6211\u73b0\u5728\u60f3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5982\u71d5\u5b50\u5462\u77bb\u8c01\u4e5f\u542c\u4e0d\u6e05\u5979\u5728\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u60f3\u8d77\u4e86\u5f88\u591a\u6781\u5947\u602a\u7684\u4e8b\u4f46\u6700\u60f3\u7684\uff0c\u8fd8\u662f\u4e2a\u70e7\u5f97\u53c8\u7ea2\u53c8\u70c2\u7684\u5927\u8e44\u8180\u3002\u201d \u3000\u3000\u71d5\u4e03\u201c\u78fa\u9646\u201d\u7b11\uff0c\u9053\u201c\u4f60\u80fd\u4e0d\u80fd\u8bf4\u4f60\u662f\u5728\u60f3\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e0d\u80fd\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u6211\u4f2f\u628a\u4f60\u53e3\u541e\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\xb7\u54fa\u7483\u9053\u201c\u4f60\u8fd9\u8001\u7bd3\u6211\u5f97\u6765\u53ef\u771f\u4e2d\u5bb9\u6613\u82e5\u662f\u541e\u4e0b\u53bb\u51ef\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6ca1\u6709\u4e86\u5c82\u975e\u6b63\u597d\u518d\u53bb\u627e\u4e2a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u627e\u8c01\uff1f\u201d\u201d \u3000\u3000\u71d5\u4e03\u9053\u8b6c\u5982\u8bf4\u201d\u2500\u201c\u9178\u6885\u6c64\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u50f5\u9992\u7684\u9053\u201c\u4e0d\u884c\xb7\u5979\u592a\u6717\u800c\u4e14\u5979\u559c\u6b22\u7684\u662f\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u9053\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u90a3\u5929\u4f60\u4e0d\u8981\u5979\u5979\u4e3a\u4ec0\u9ebd\u70b9\u4e5f\u4e0d\u751f\u6c14\u4e86\u2026\u3002\u2019\u90a3\u5929\u4f53\u60f3\u5fc5\u5df2\u544a \u8bc9\u5979\u4f60\u4e5f\u8ddf\u5979\u4e00\u6837\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u85d0\u4e03\u9053\u201c\u6211\u82e5\u662f\u7537\u4eba\u6211\u5c31\u8981\u5979\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u76f4\u4e0d\u80af\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u4e2a\u5973\u4eba\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8c01\u53eb\u4f60\u662f\u4e2a\u778e\u5b50\xb7\u522b\u4eba\u90fd\u770b\u51fa\u6765\u4e86\u5c31\u662f\u4f60\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8981\u544a\u8bc9\u6211\u7684\u5c31\u662f\u8fd9\u4e2a\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54bd\u3002\u2019 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u4e3a\u4ec0\u9ebd\u5b9a\u8981\u7b49\u5230\u6211\u5feb\u6b7b\u7684\u65f6\u5019\u624d\u80af\u544a\u8bc9\u6211\u2606 \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u2500\u4e00\u201c\u56e0\u4e3a\u6211\u6015\u4f60\u4e0d\u8981\u6211\u201c\u2026\u2606\u2018 \u3000\u3000\u59d1\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u6a0a\u5c31\u8c61\u662f\u5df2\u88ab\u4ef6\u4ec0\u9ebd\u4e1c\u897f\u5835\u4f4f\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5979\u624d\u8f7b\u8f7b\u7684\u5598\u606f\xb7\u9053\u201c\u6211\u4eec\u597d\u597d\u7684\u804a\u804a\xb7\u4e0d\u8bb8\u4f60\u4e71\u52a8\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u597d\u4e0d\u52a8\u5c31\u4e0d\u52a8\u3002\u53ef\u662f\u4f60\u4e3a\u4ec0\u9ebd\u8981\u6015\u6211\u4e0d\u8981\u4f60\uff1f\u4f60\u96be\u9053\u5c0f\u77e5\u9053\uff0c\u5c31\u7b97\u7528\u5168\u4e16\u754c \u7684\u4eba\u6765\u6362\u4f60\u4e00\u4e2a\u6211\u4e5f\u4e0d\u6362\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u82e5\u7528\u90a3\u4e2a\u6c34\u67d4\u9752\u6765\u6362\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u5979\u7684\u786e\u662f\u4e2a\u5f88\u597d\u7684\u5973\u5b69\u5b50\u800c\u4e14\u72e0\u53ef\u601c\u53ea\u53ef\u60dc\u6211\u5fc3\u91cc\u65e9\u5df2\u7ecf\u88ab\u4f60\u4e2a\u4eba\u5360\u6ee1 \u4e86\xb7\u518d\u4e5f\u5bb9\u4e0d\u4e0b\u522b\u7684\u4eba\u3002\u201d \u3000\u3000\u85d0\u4e03\u56db\u541f\u201d\u4e00\u58f0\u3002\u9526\u5e10\u4e2d\u5ffd\u7136\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u597d\u50cf\u4e24\u4e2a\u4eba\u7684\u5634\u53c8\u5df2\u88ab\u4ec0\u9ebd\u5835\u4f4f\u3002 \u3000\u3000\u4e49\u8fc7\u4e86\u5f88\u4e45\u90ed\u5927\u8def\u624d\u53f9\u606f\u9053\u201c\u6211\u77e5\u9053\u4f60\u90a3\u9ebd\u6837\u505a\u662f\u4e3a\u4e86\u8bd5\u8bd5\u6211\u5bf9\u4f60\u662f\u4e0d\u662f\u5fe0\u5fc3\u201d\u3002\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5634\u5507\uff0c\u901a\u201c\u4f60\u82e5\u80af\u79ef\u90a3\u91cc\u7559\u4e0b\u6765\u8fd9\u8f88\u5b50\u5c31\u4f11\u60f3\u518d\u770b\u89c1\u6211\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u6211\u5df2\u7ecf\u5230\u8fd9\u91cc\u6765\u4e86\u4e4b\u5f8c\xb7\u4f60\u4e3a\u4ec0\u9ebd\u8fd8\u4e0d\u8ba9\u6211\u6765\u89c1\u4f60\u5462\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u8fd8\u6709\u522b\u7684\u4eba\u4e5f\u8981\u8bd5\u8bd5\u4f60\uff0c\u770b\u4f60\u662f\u4e0d\u662f\u591f\u806a\u660e\u3001\u591f\u80c6\u91cf\u770b\u4f60\u7684\u5fc3\u662f\u4e0d\u662f\u591f \u597d\xb7\u591f\u4e0d\u591f\u8d44\u683c\u505a\u6211\u7239\u7239\u7684\u4ea5\u5a7f\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u6240\u4ee5\u4ed6\u4eec\u5c31\u770b\u6211\u662f\u4e0d\u662f\u591f\u806a\u660e\u80fd\u627e\u51fa\u8fd9\u95f4\u5c4b\u4e8e\u7684\u79d8\u5bc6\u662f\u4e0d\u662f\u591f\u80c6\u91cf\u5230\u9f99\u738b \u5e87\u53bb\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5728\u90a3\u9f99\u738b\u5e99\u91cc\xb7\u4f60\u82e5\u662f\u6562\u52a8\u6211\u90a3\u5c0f\u8868\u59b9\u7684\u574f\u4e3b\u610f\u6216\u662f\u4e0d\u80af\u5148\u9001\u5979\u56de\u6765\uff0c\u4f60\u5c31\u7b97 \u80fd\u627e\u5230\u8fd9\u91cc\u8fd8\u662f\u770b\u4e0d\u89c1\u6211\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u5e78\u4e8f\u6211\u662f\u4e2a\u53c8\u806a\u660e\u53c8\u6709\u80c6\u91cf\u7684\u5927\u597d\u4eba \u3000\u3000\u85cf\u4e03\u7b11\u62a2\u9053\u201c\u5426\u5219\u4f60\u53c8\u600e\u9ebd\u80fd\u5a36\u5230\u6211\u8fd9\u9ebd\u597d\u7684\u8001\u5a46\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u5230\u73b0\u5728\u6211\u624d\u53d1\u73b0\u6211\u597d\u771f\u662f\u5929\u751f\u7684\u5bf9\u3002\uff1a\u6b22\u4e50\u82f1\u63a7 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u73b0\u5728\u624d\u53d1\u73b0\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u56e0\u4e3a\u6211\u73b0\u5728\u624d\u53d1\u73b0\u6211\u4eec\u4e24\u4e2a\u4eba\u7684\u8138\u76ae\u90fd\u591f\u539a\u7684\u3002\u201d \u3000\u3000\u73b0\u5728\u8fd9\u5c5e\u65bc\u624d\u771f\u7684\u8c61\u662f\u4e2a\u6d1e\u623f\u4e86\u751a\u81f3\u6bd4\u4f60\u60f3\u8c61\u4e2d\u7684\u6d1e\u623f\u8fd8\u8981\u751c\u871c\u7f8e\u4e3d\u3002 \u3000\u3000\u4ed6\u4eec\u591f\u8d44\u683c\u4eab\u53d7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u7684\u60c5\u611f\u53d7\u5f97\u4f4f\u8003\u9a8c\u4ed6\u4eec\u80fd\u6709\u8fd9\u9ebd\u6837\u5929\xb7\u53ef\u771f\u662f\u5c0f\u5bb9\u6613\u3002 \u3000\u3000\u94bb\u77f3\u8981\u7ecf\u8fc7\u7422\u78e8\u624d\u80fd\u53d1\u5f97\u51fa\u5149\u8292\u3002 \u3000\u3000\u7231\u60c5\u548c\u53cb\u8c0a\u4e5f\u6837\u3002 \u3000\u3000\u7ecf\u4e0d\u4f4f\u8003\u9a8c\u7684\u7231\u60c5\u548c\u53cb\u6f14\uff0c\u5c31\u8c61\u662f\u7eb8\u505a\u7684\u82b1\u65e2\u6ca1\u6709\u82b1\u7684\u9c9c\u8273\u548c\u82ac\u82b3\uff0c\u4e5f\u6c38\u8fdc\u7ed3\u4e0d\u51fa\u679c \u5b9e\u3002 \u3000\u3000\u6811\u4e0a\u5df2\u7ed3\u51fa\u679c\u5b9e\uff0c\u6625\u5929\u867d\u5df2\u8fdc\u53bb\u4f46\u6536\u83b7\u7684\u5b63\u8282\u5374\u5df1\u5feb\u6765\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u5750\u5728\u6811\u4e0b\uff0c\u6458\u4e0b\u4e86\u5934\u4e0a\u7684\u9a6c\u8fde\u5761\u5927\u8349\u5e3d\u505a\u6247\u5b50\xb7\u8d3f\u54e8\u9053\u201c\u597d\u70ed\u7684\u5929\u6c14\u4e0a\u8001\u5927\u60f3\u5fc5\u66f4 \u61d2\u5f97\u52a8\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u76ee\u5149\u901a\u89c6\u5411\u8fdc\u65b9\u9053\u201c\u8fd9\u4e9b\u65e5\u5b50\u6765\u4ed6\u548c\u5c0f\u6797\u4e0d\u77e5\u9053\u5728\u5e72\u4ec0\u9ebd\u3002\u201d\u71d5\u4e03\u9053\u201c\u4f60\u653e \u5fc3\xb7\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u5bc2\u5bde\u7684\uff0c\u5c24\u5176\u662f\u5c0f\u6797\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5a7f\u7136\u7b11\xb7\u9053\u4f60\u96be\u9053\u5fd8\u8bb0\u4e86\u90a3\u4e2a\u5356\u82b1\u7684\u5c0f\u59d1\u5a18\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e5f\u7b11\u4e86\xb7\u7acb\u523b\u53c8\u542c\u5230\u5382\u90a3\u6e05\u8106\u7684\u6b4c\u58f0\u201c\u5c0f\u5c0f\u72f1\u5a18\uff0c\u6e05\u65e9\u8d77\u5e8a \u3000\u3000\u63d0\u82b1\u7bee\u513f\u4e0a\u5e02\u573a \u3000\u3000\u7a7f\u8fc7\u5927\u8857\uff0c\u8d70\u8fc7\u5c0f\u5df7 \u3000\u3000\u5356\u82b1\u5356\u82b1\u58f0\u58f0\u56b7\u2026\u2026\u201d \u3000\u3000\u6b4c\u58f0\u5f53\u7136\u4e0d\u662f\u90a3\u5356\u82b1\u7684\u5c0f\u59cb\u59fb\u5531\u51fa\u6765\u7684\uff0c\u5531\u6b4c\u7684\u662f\u71d5\u4e03\u3002 \u3000\u3000\u5979\u8f7b\u6447\u8349\u5e3d\u66fc\u58f0\u4e3d\u6b47\xb7\u5f15\u5f97\u8def\u4e0a\u7684\u4eba\u90fd\u5207\u8f6c\u5934\u77aa\u5927\u4e86\u773c\u775b\u6765\u77a7\u5979\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u201c\u4f60\u83ab\u8981\u5fd8\u8bb0\u4f60\u73b0\u5728\u8eab\u4e0a\u7a7f\u7684\u662f\u4ec0\u9ebd\u8863\u670d\uff1f\u201d \u3000\u3000\u5979\u8eab\u4e0a\u7a7f\u7684\u8fd8\u662f\u7537\u4eba\u6253\u626e\xb7\u4f46\u7720\u58f0\u5374\u6e05\u8106\u5982\u9ec4\u9ec4\u51fa\u8c37\u3002 \u3000\u3000\u71d5\u4e03\u5374\u7b11\u9053\u201c\u6ca1\u5173\u7cfb\u53cd\u6b63\u6211\u5c31\u7b97\u4e0d\u5531\u522b\u4eba\u4e5f\u6837\u80fd\u770b\u51fa\u6211 \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4f60\u4ee5\u524d\u5462\uff1f\u2019 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ee5\u524d\u4e0d\u540c\u3001\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u6709\u4ec0\u9ebd\u4e0d\u540c\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u4ee5\u524d\u6211\u6bd4\u8f83\u80dc\uff0c\u2026\u3002\u2019\u72e0\u80dc\u5927\u5bb6\u90fd\u89c9\u5f97\u5973\u4eba\u603b\u5e94\u8be5\u6bd4\u7537\u4eba\u4e7e\u51c0\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5176\u5b9e\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u77aa\u6b79\u4ed6\u670d\uff0c\u9053\u201c\u5176\u5b9e\u4ea5\u4eba\u672c\u6765\u5c31\u6bd4\u7537\u4eba\u4e7e\u51c0\u3002\u201d \u3000\u3000\u8fd9\u6761\u8def\u662f\u56de\u5bcc\u8d35\u5c71\u5e86\u7684\u8def\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u6ca1\u6709\u5fd8\u8bb0\u4ed6\u4f55\u7684\u670b\u53cb\u4ed6\u554a\u8981\u5c06\u81ea\u5df1\u7684\u5feb\u4e50\u8ba9\u670b\u53cb\u5206\u4eab\u3002 \u3000\u3000\u201c\u4e0a\u8001\u5927\u548c\u5c0f\u6797\u82e5\u77e5\u9053\u6211\u4eec\u2026\u2026\u6211\u4eec\u5df2\u7ecf\u6210\u4e3a\u592b\u59bb\xb7\u5b9a\u4e5f\u4f1a\u5f88\u9ad8\u5174\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u5c0f\u6797\u4f1a\u4e0d\u4f1a\u5403\u9178\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\u4ed6\u5c31\u5f00\u59cb\u8dd1\u71d5\u4e03\u5c31\u5728\u5f8c\u9762\u8ffd\u3002 \u3000\u3000\u4ed6\u4eec\u65e2\u7136\u6ca1\u6709\u4e58\u8f66\u4e5f\u6ca1\u6709\u9a91\u9a6c\uff0c\u5728\u8def\u4e0a\u7b11\xb7\u8dd1\u8ffd\uff0c\u5c31\u8c61\u662f\u4e24\u4e2a\u8be5\u5b50\u3002 \u3000\u3000\u5feb\u4e50\u5c82\u975e\u603b\u662f\u80fd\u4ee4\u4eba\u53d8\u5f97\u5e74\u9752\u7684\uff1f \u3000\u3000\u8dd1\u7d2f\u5382\uff0c\u5c31\u5728\u6811\u9634\u91cc\u5750\u4e0b\u6765\uff0c\u4e70\u4e00\u4e2a\u70d9\u997c\u5c31\u5f53\u5348\u996d\u5403\u3002 \u3000\u3000\u5c31\u7b97\u662f\u6de1\u800c\u65e0\u9648\u7684\u786c\u9ea6\u997c\uff0c\u5403\u5728\u4ed6\u4eec\u5634\u91cc\u4e5f\u662f\u751c\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u5c45\u7136\u5df2\u7ecf\u6709\u597d\u51e0\u5929\u6ca1\u559d\u9152\u5382\uff0c\u9664\u5382\u4ed6\u4eec\u4e34\u8d70\u524d\u7684\u90a3\u5929\uff0c\u5357\u5bcc\u4e11\u4e3a\u81ea\u5df1\u7684\u4ea5\u513f\u548c\u5973 \u5a7f\u996f\u884c\uff0c\u975e\u4f46\u4ed6\u7834\u4f8b\u559d\u4e86\u534a\u676f\u800c\u8fd8\u82f1\u8981\u4ed6\u4eec\u653e\u91cf\u8d50\u4e2a\u75db\u5feb\xb7\u6240\u4ee5\u4ed6\u4eec\u5168\u9189\u4e86\u3002 \u3000\u3000\u71d5\u4e03\u5fae\u7b11\u9053\u201c\u6211\u7239\u7239\u81ea\u5df1\u73b0\u5728\u867d\u4e0d\u80fd\u559d\u9152\u4e86\uff0c\u5374\u5f88\u559c\u6b22\u770b\u522b\u4eba\u54bd\u79fd \u3000\u3000\u90ed\u5927\u8def\u7b11\u9053\u2019\u4ed6\u4ee5\u524d\u7684\u9152\u91cf\u5b9a\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f55\u6b62\u4e0d\u9519\xb7\u5341\u4e2a\u90ed\u5927\u8def\u4e5f\u672a\u5fc5\u80fd\u559d\u5f97\u8fc7\u4ed6\u4e00\u4e2a\u3002\u201d \u3000\u3000\u90ed\u706b\u8def\u9053\u201c\u54c8\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6697\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u54c8\u7684\u610f\u601d\u5c31\u662f\u6211\u975e\u4f46\u4e0d\u670d\u6c14\uff0c\u800c\u4e14\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ea\u53ef\u60dc\u4ed6\u73b0\u5728\u8001\u4e86\uff0c\u800c\u4e14\u65e7\u4f24\u590d\u53d1\xb7\u5df2\u6709\u591a\u5e74\u8eba\u5728\u5e8a\u4e0a\u4e0d\u80fd\u52a8\uff0c\u5426\u5219\u4ed6\u4e0d\u628a\u4f60 \u704c\u5f97\u6ee1\u5730\u4e71\u722c\u624d\u602a\u3002\u201d \u3000\u3000\u63d0\u8d77\u4e86\u5979\u7236\u4eb2\u7684\u75c5\u75db\xb7\u5979\u773c\u775b\u91cc\u4e5f\u4e0d\u7981\u9732\u51fa\u4e86\u60b2\u4f24\u4e4b\u8272\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u5b9e\u5728\u662f\u4e2a\u5f88\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u6211\u6b32\u987e\u82f1\u9192\u60f3\u4e0d\u5230\u4ed6\u4f1a\u8ba9\u6211 \u4eec\u8d70\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u4ed6\u5b9e\u5728\u592a\u5bc2\u5bde\uff0c\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u5b9a\u4f1a\u8981\u6211\u4eec\u8d54\u4ed6\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4e0d\u540c\uff0c\u4ed6\u4ece\u4e0d\u613f\u4e3a\u4e86\u81ea\u5df1\u8ba9\u522b\u4eba\u75db\u82e6\uff0c\u65e0\u8bba\u591a\u9ebd\u96be\u4ee5\u5fcd\u53d7\u7684\u4e8b\u4ed6\u90fd\u5b81 \u53ef\u4e2a\u4eba\u72ec\u81ea\u5fcd\u53d7\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u91cc\u53c8\u53d1\u51fa\u4e86\u5149\u663e\u7136\u56fa\u81ea\u5df1\u6709\u8fd9\u9ebd\u6837\u4e00\u4e2a\u7236\u4eb2\u800c\u9a84\u50b2\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u9053\u201c\u8bf4\u8001\u5b9e\u8bdd\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u60f3\u5230\u4ed6\u662f\u4e2a\u8fd9\u6837\u5b50\u66f2 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ece\u524d\u4f60\u4ee5\u4e3a\u4ed6\u662f\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u5450\u5450\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6c5f\u6e56\u4e2d\u7684\u4f20\u8bf4\xb7\u5c06\u4ed6\u8bf4\u5f97\u591a\u9ebd\u53ef\u6015\u3002\u201d \u3000\u3000\u7fd4\u4e03\u9053\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u606f\u9053\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u6c5f\u6e56\u4e2d\u7684\u90a3\u4e9b\u4f20\u8bf4\u624d\u771f\u6b63\u53ef\u6015\xb7\u4ed6\u5c45\u7136\u80fd\u5fcd\u53d7\u4e86\u8fd9\u9ebd\u591a\u5e74 \u5c31\u51ed\u8fd9\u70b9\uff0c\u5df2\u4e0d\u662f\u522b\u4eba\u80fd\u6bd4\u5f97\u4e0a\u7684\u5382\u3002\u201d \u3000\u3000\u71d5\u4e03\u7ee3\u7136\u9053\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u5df2\u6ca1\u6cd5\u4e0d\u5fcd\u53d7\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5e78\u597d\u4ed6\u8fd8\u6709\u670b\u53cb\u6211\u770b\u5230\u795e\u9a7c\u4e25\u4ed6\u4eec\u5bf9\u4ed6\u7684\u5fe0\u5b9e\u548c\u53cb\u60c5\u603b\u5fcd\u4e0d\u4f4f\u8981\u66ff\u4ed6\u89c9\u5f97\u6b22 \u559c\u611f\u52a8\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u9ed8\u4e2d\u8d2d\u5ffd\u7136\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u4ee5\u524d\u662f\u60f3\u600e\u9ebd\u5bf9\u8ba8\u4ed6\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u6447\u5934\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u4eec\u4ee5\u524d\u4e5f\u662f\u5fc3\u60f3\u8981\u6765\u6740\u4ed6\u7684\xb7\u53ef\u662f\u5f8c\u6765\xb7\u7ecf\u8fc7\u4e86\u51e0\u6b21\u751f\u6b7b\u7f20\u6597\u4e4b\u5f8c\u4ed6\u4eec\u624d\u53d1 \u73b0\u4ed6\u5e76\u4e0d\u662f\u4f20\u8bf4\u4e2d\u90a3\u6837\u7684\u4eba\xb7\u4e5f\u88ab\u4ed6\u7684\u4eba\u683c\u6240\u611f\u52a8\u6240\u4ee5\u624d\u6210\u4e86\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5979\u7b11\u5de5\u7b11\u7b11\u5f97\u72e0\u51c4\u51c9\xb7\u53c8\u6709\u4e9b\u5f97\u610f\xb7\u63a5\u9053\u201c\u4e3a\u4e86\u4ed6\xb7\u53f0\u7f57\u6c49\u751a\u81f3\u4e0d\u63aa\u80cc\u53db\u4e86\u5c11\u6797\uff0c\u4e0d\u60dc \u505a\u2018\u4e2a\u7ec8\u751f\u518d\u4e5f\u89c1\u4e0d\u5f97\u5929\u65e5\u7684\u53db\u5f92\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4eba\u5c82\u975e\u4e5f\u5c31\u56e0\u4e3a\u6709\u8fd9\u79cd\u4f1f\u5927\u7684\u611f\u60c5\u6240\u4ee5\u624d\u548c\u755c\u7272\u4e2d\u540c\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8fd9\u79cd\u611f\u60c5\u4e5f\u552f\u6709\u5728\u751f\u6b7b\u60a3\u96be\u4e4b\u4e2d\u624d\u80fd\u663e\u5f97\u51fa\u5b83\u7684\u4f1f\u5927\u6765\u3002\uff1f \u3000\u3000\u4ed6\u5546\u8bf4\u7684\u4e0d\u9519\u3002 \u3000\u3000\u4e2a\u4eba\u4e5f\u552f\u6709\u5728\u751f\u6b7b\u60a3\u96be\u4e4b\u4e2d\u624d\u80fd\u663e\u5f97\u51fa\u4ed6\u7684\u4f1f\u5927\u6765\u3002 \u3000\u3000\u5357\u5bab\u4e11\u80fd\u535a\u5f97\u795e\u9a7c\u5b50\u4ed6\u4eec\u7684\u53cb\u60c5\u5386\u4ed8\u51fa\u7684\u4ee3\u4ef7\u662f\u4f55\u7b49\u60e8\u6458\u53ea\u6015\u4e5f\u4e0d\u662f\u522b\u4eba\u80fd\u60f3\u8c61\u5f97\u5230 \u7684\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u5728\u751f\u6b7b\u5173\u5934\u4e2d\u5b81\u613f\u727a\u5f84\u81ea\u5df1\u6765\u4fdd\u5168\u522b\u4eba\xb7\u522b\u4eba\u53c8\u600e\u77e5\u4eba\u683c\u7684\u4f1f\u5927\uff1f\u53c8\u600e\u4f1a\u4e3a\u4e86\u4ed6 \u727a\u7272\u4e00\u5207\uff1f \u3000\u3000\u8fd9\u5176\u4e2d\xb7\u5f53\u7136\u4e5f\u6709\u6bb5\u4ee4\u4eba\u63a0\u5fc3\u52a8\u9b44\u3001\u60b2\u4f24\u6d41\u6cea\u7684\u6545\u4e8b\u3002 \u3000\u3000\u8fd9\u6545\u4e8b\u5df2\u4e0d\u5fc5\u518d\u63d0\u3002 \u3000\u3000\u56e0\u4e3a\u6211\u4eec\u73b0\u5728\u8981\u8bf4\u7684\xb7\u662f\u4ee4\u4eba\u6b22\u4e50\u66f2\u6545\u4e8b\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u60b2\u4f24\u7684\u6545\u4e8b\u5df2\u591f\u591a\u3002 \u3000\u3000\u5df2\u5927\u591a\u3002 \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u672a\u5230\u9ec4\u660f\u5df2\u8fd1\u9ec4\u660f>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u672a\u5230\u9ec4\u660f\u5df2\u8fd1\u9ec4\u660f \u3000\u3000\u672a\u5230\u9ec4\u660f\u5df3\u8fd1\u9ec4\u660f\u3002 \u3000\u3000\u65e5\u8272\u867d\u5df2\u897f\u6c89\u4f46\u788e\u77f3\u8def\u4e0a\u4ecd\u7136\u662f\u70ed\u4f9b\u70d8\u7684\xb7\u6478\u968f\u624b\u3002 \u3000\u3000\u524d\u9762\u7684\u6811\u836b\u4e0b\u6709\u4e2a\u6a21\u697c\u614c\u505c\u7684\u5987\u4eba\u624b\u91cc\u7275\u4e2a\u5b69\u5b50\xb7\u80cc\u4e5f\u80cc\u5b69\u5b50\u6b63\u5782\u5934\u4f38\u51fa\u624b\xb7\u7ad9\u5728\u90a3\u91cc \u5411\u8fc7\u8def\u4eba\u4e5e\u8ba8\u3002 \u3000\u3000\u90ed\u5927\u8def\u7acb\u523b\u5b9a\u8fc7\u53bb\u6478\u51fa\u5757\u788e\u94f6\u5b50\u6446\u5728\u5979\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u9519\u8fc7\u4efb\u4f55\u4e2a\u4e5e\u5de7\uff0c\u7eb5\u7136\u4ed6\u53ea\u5269\u4e0b\u8fd9\u5757\u788e\u94f6\xb7\u4e5f\u4f1a\u6beb\u4e0d\u8003\u8651\u5c31\u65bd\u820d\u7ed9\u522b\u4eba\u3002 \u3000\u3000\u71d5\u4e03\u770b\u6e29\u67d4\u7684\u76ee\u5149\u4e2d\xb7\u5e26\u8d5e\u8bb8\u4e4b\u8272\u3002 \u3000\u3000\u5979\u663e\u7136\u4e5f\u4ee5\u81ea\u5df2\u6709\u8fd9\u6d0b\u7684\u4e08\u592b\u4e3d\u9a84\u50b2\u3002 \u3000\u3000\u8fd9\u5987\u4eba\u5634\u91cc\u8089\u7483\u7684\u8bf4\u611f\u6fc0\u7684\u8bdd\xb7\u6b63\u60f3\u5c06\u94f6\u5b50\u6355\u5728\u6000\u91cc\xb7\u6709\u610f\u65e0\u610f\u95f4\u62ec\u8d77\u4e86\u5934\u770b\u4e86\u90ed\u5927\u8def \u773c\u3002 \u3000\u3000\u5979\u82cd\u767d\u5065\u6454\u7684\u80a1\u4e0a\u7acb\u4ea5\u53d1\u751f\u4e86\u79cd\u65e0\u6cd5\u63cf\u53d9\u7684\u53ef\u6015\u53d8\u5316\u3002 \u3000\u3000\u5979\u90a3\u53cc\u65e0\u795e\u800c\u6ee1\u5e03\u8840\u4e1d\u7684\u773c\u775b\u4e5f\u7acb\u523b\u6b7b\u9c7c\u6bb7\u51f8\u4e86\u51fa\u6765\u5c31\u597d\u8c61\u6709\u628a\u5200\u7a81\u7136\u63d2\u5165\u4e86\u5979\u7684\u5fc3 \u810f\u3002 \u3000\u3000\u7ff1\u5927\u8def\u672c\u6765\u8fd8\u5728\u5fae\u7b11\u4f46\u7b11\u5bb9\u4e5f\u6e10\u6e10\u51bb\u7ed3\xb7\u8138\u4e0a\u4e5f\u9732\u51fa\u4e86\u6177\u9a87\u7684\u8868\u60c5\u5931\u58f0\u9053\u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u7acb\u523b\u7528\u53cc\u624b\u8499\u4f4f\u4e86\u8138\xb7\u53eb\u58f0\u9053\u201c\u4f60\u8d70\uff0c\u6211\u4e0d\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7684\u8868\u60c5\u5df2\u4e2d\u60ca\u9a87\u53d8\u4e3a\u601c\u60dc\uff0c\u957f\u53f9\u9053\u201c\u4f60\u600e\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u5987\u4eba\u9053\u201c\u90a3\u662f\u6211\u7684\u4e8b\u548c\u4f60\u6c72\u5173\u7cfb\u3002\u201d \u3000\u3000\u5979\u867d\u7136\u60f3\u52c9\u5f3a\u63a7\u5236\u4f4f\u81ea\u5df1\u4f46\u5168\u8eab\u90fd\u5df2\u6296\u5f97\u8c61\u662f\u98ce\u4e2d\u7684\u52c9\u5149\u3002 \u3000\u3000\u90ed\u5927\u8def\u7684\u76ee\u5149\u5782\u5411\u90a3\u4e24\u4e2a\u53d1\u80b2\u4e0d\u5168\u3001\u6ee1\u8138\u9f3b\u6d95\u7684\u5b69\u5b50\u7406\u7136\u95ee\u9053\u201c\u8fd9\u662f\u4f60\u613f\u4ed6\u751f\u7684\u9ebd\uff1f\u4ed6 \u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u5987\u4eba\u53e3\u6296\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f73\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u63a9\u9762\u75db\u54ed\u9053\u201c\u4ed6\u9a97\u4e86\u6211\u9a97\u53bb\u4e86\u6211\u7684\u79c1\u623f\u94b1\xb7\u53c8\u548c \u522b\u7684\u5973\u4eba\u8dd1\u4e86\xb7\u5374\u5c06\u8fd9\u4e24\u4e2a\u5b7d\u79cd\u7559\u4e0b\u6765\u7ed9\u6211\uff0c\u6211\u4e3a\u4ec0\u9ebd\u8fd9\u9ebd\u82e6\u547d\u2026\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u66ff\u5979\u89e3\u7b54\xb7\u53ea\u6709\u5979\u81ea\u5df2\u3002 \u3000\u3000\u5979\u8fd9\u79cd\u60b2\u60e8\u7684\u7586\u9047\u5c82\u975e\u6b63\u662f\u5979\u81ea\u5df1\u627e\u6765\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u606f\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u9ebd\u3002 \u3000\u3000\u71d5\u4e03\u9992\u6162\u7684\u8d70\u8fc7\u6765\u65e0\u8a00\u7684\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\u8ba9\u4ed6\u77e5\u9053\u65e0\u8bba\u9047\u4ec0\u9ebd\u4e8b\u5979\u90fd\u662f\u7ad9\u8863\u4ed6\u8fd9\u8fb9\u7684\u603b\u662f \u540c\u6837\u4fe1\u4efb\u4ed6\u3002 \u3000\u3000\u5973\u4eba\u6240\u80fd\u7ed9\u7537\u4eba\u7684\xb7\u8fd8\u6709\u4ec0\u9ebd\u80fd\u6bd4\u8fd9\u79cd\u4fe1\u4efb\u548c\u4e86\u89e3\u66f4\u80fd\u4ee4\u7537\u4eba\u611f\u6fc0\uff1f \u3000\u3000\u90ed\u5927\u8def\u731c\u7591\uff0c\u9053\u201c\u4f60\u5df2\u77e5\u9053\u5979\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u71d5\u4e03\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5973\u4eba\u5bf9\u81ea\u5df2\u6240\u7231\u7684\u7537\u4eba\uff0c\u4eff\u4f5b\u5929\u751f\u5c31\u6709\u79cd\u5947\u5999\u654f\u9510\u7684\u7b2c\u516d\u611f\u3002 \u3000\u3000\u5979\u65e9\u5df3\u611f\u89c9\u51fa\u8fd9\u5987\u4eba\u548c\u5979\u7684\u4e08\u592b\u4e4b\u95f4\uff0c\u6709\u79cd\u5f88\u4e0d\u5bfb\u5e38\u7684\u5173\u7cfb\u5189\u542c\u5382\u4ed6\u4eec\u8bf4\u7684\u8bdd\u5c31\u66f4\u65e0\u7591 \u95ee\u4e86\u3002 \u3000\u3000\u8fd9\u5987\u4eba\u663e\u7136\u5c31\u662f\u4ee5\u524d\u6b3a\u9a97\u4e86\u90ed\u5927\u8def\uff0c\u5c06\u4ed6\u629b\u5f03\u4e86\u7684\u90a3\u4e2a\u4ea5\u4eba\u3002 \u3000\u3000\u7ff1\u5927\u8def\u957f\u957f\u53f9\u606f\u9053\u201c\u6211\u5b9e\u5728\u6ca1\u60f3\u5230\u4f1a\u5728\u8fd9\u91cc\u770b\u89c1\u5979\u66f4\u6ca1\u6709\u60f3\u5230\u5979\u5df2\u53d8\u6210\u8fd9\u6837\u4e8e\u3002\u201d \u3000\u3000\u71d5\u4e03\u67d4\u58f0\u9053\u201c\u5979\u65e2\u7136\u662f\u4f60\u7684\u670b\u53cb\xb7\u4f60\u5c31\u5e94\u8be5\u5c3d\u529b\u5e2e\u52a9\u5979\u3002\u201d \u3000\u3000\u8fd9\u5207\u4eba\u5ffd\u7136\u505c\u4ea7\u54ed\u58f0\uff0c\u59cb\u8d77\u5934\xb7\u8e6c\u5979\u3001\u9053\u201c\u4f60\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7684\u76ee\u5149\u67d4\u548c\u800c\u5e73\u9759\u9053\u201c\u6211\u662f\u4ed6\u7684\u59bb\u5b50\u3002\u201d \u3000\u3000\u8fd9\u5987\u4eba\u80a1\u4e0a\u53c8\u8d77\u5382\u79cd\u5947\u7279\u7684\u53d8\u5316\xb7\u8f6c\u5934\u77aa\u90ed\u5927\u8def\u8be7\u58f0\u9053\u201c\u4f60\u5df4\u7ecf\u6210\u4e86\u4eb2\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8fd9\u5987\u4eba\u770b\u4e86\u770b\u4ed6\u4e49\u770b\u4e86\u770b\u71d5\u4e03\uff0c\u65e5\u4e2d\u7a81\u7136\u9732\u51fa\u4e86\u79cd\u5ffd\u6bd2\u7684\u5ac9\u5992\u4e4b\u8272\u5ffd\u7136\u4e00\u628a\u63ea\u4f4f\u4e86\u90ed\u5927 \u8def\u7684\u8863\u895f\u5927\u58f0\u9053\u201c\u4f60\u672c\u6765\u8981\u5a36\u6211\u7684\u600e\u9ebd\u80fd\u548c\u522b\u4eba\u6210\u4eb2\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8138\u8272\u5df2\u82cd\u767d\u5982\u7eb8\u8fd9\u79cd\u60c5\u51b5\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u9ebd\u6837\u531d\u4ed8\u3002 \u3000\u3000\u71d5\u4e03\u5374\u5c06\u4ed6\u7684\u624b\u63e1\u5f97\u66f4\u7d27\u51dd\u89c6\u8fd9\u5987\u4eba\u9053\u201c\u662f\u4f60\u79bb\u5347\u4e86\u4ed6\uff0c\u4e0d\u662f\u4ed6\u4e0d\u8981\u4f60\uff0c\u4ee5\u524d\u7684\u4e8b\u4f60\u81ea \u5df1\u4e5f\u8be5\u8bb0\u5f97\u7684\u3002\u201d \u3000\u3000\u5987\u4eba\u7684\u76ee\u5149\u66f4\u6076\u6bd2\uff0c\u72de\u7b11\u9053\u201c\u6211\u8bb0\u5f97\u4ec0\u9ebd\u6211\u53ea\u8bb0\u5f97\u4ed6\u66fe\u7ecf\u544a\u8bc9\u8fc7\u6211\uff0c\u4ed6\u6c34\u8fdc\u53ea\u559c\u6b22\u6211\u4e2a \u4eba\uff0c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u4ed6\u7edd\u4e0d\u518d\u5a36\u522b\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u5979\u53c8\u4f5c\u51fa\u8981\u6d41\u6cea\u7684\u6837\u5b50\u62bd\u52a8\u5634\u89d2\u5927\u58f0\u9053\u201c\u53ef\u662f\u4ed6\u5374\u9a97\u4e86\u6211\uff0c\u9a97\u4e86\u6211\u8fd9\u4e2a\u82e6\u547d\u7684\u5973\u4eba\u4f60\u4eec \u5927\u5bb6\u6765\u8bc4\u8bc4\u7406\u201c\u4e00\u2500\u201d \u3000\u3000\u8def\u5df2\u6709\u4eba\u56f4\u4e86\u535c\u6765\uff0c\u5e26\u8f7b\u8511\u548c\u618e\u6076\u4e4b\u8272\uff0c\u770b\u90ed\u5927\u8def\u3002 \u3000\u3000\u90ed\u5927\u8def\u82cd\u767d\u7684\u8138\u53c8\u5df2\u53d8\u5f97\u8d64\u7ea2\u8fde\u6c57\u73e0\u4e8e\u90fd\u5df1\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u71d5\u4e03\u7684\u795e\u8272\u5374\u8fd8\u662f\u5f88\u5e73\u7b4b\xb7\u7f13\u7f13\u9053\u201c\u4ed6\u5e76\u6ca1\u6709\u9a97\u4f60\u4ece\u6765\u4e5f\u6ca1\u6709\u9a97\u8fc7\u4f60\u53ea\u53ee\u501f\u4f60\u5df1\u4e0d\u662f \u4ee5\u524d\u90a3\u4e2a\u4eba\u4e86\xb7\u4f60\u81ea\u5df1\u4e5f\u8be5\u660e\u767d\u3002\u201d \u3000\u3000\u8fd9\u5987\u4eba\u5927\u53eb\u5927\u8df3\u9053\u201c\u6211\u4ec0\u9ebd\u90fd\u4e0d\u660e\u767d\xb7\u6211\u4e0d\u60f3\u6d3b\u4e86\u2026\u2026\u6211\u5c31\u662f\u6b7b\u4e5f\u8981\u613f\u8fd9\u72e0\u5fc3\u7684\u7537\u4eba\u6b7b \u5728\u8d77\uff0c\u201d \u3000\u3000\u5979\u5934\u5411\u90ed\u5927\u8def\u649e\u4e86\u8fc7\u53bb\xb7\u8d56\u5728\u5730\u300f\u2606\uff0c\u518d\u4e5f\u4e0d\u80af\u8d77\u6765\u3002 \u3000\u3000\u9047\u89c1\u4e86\u8fd9\u79cd\u4f1a\u6492\u6cfc\u4f7f\u8d56\u7684\u5973\u4eba\u65e0\u8bba\u8c01\u90fd\u65e0\u6cd5\u53ef\u65bd\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b80\u76f4\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u662f\u597d\u4e86\uff0c\u53ea\u6068\u4e0d\u5f97\u627e\u4e2a\u5730\u7f1d\u94bb\u4e0b\u53bb\u3002 \u3000\u3000\u71d5\u4e03\u6c89\u541f\u5ffd\u7136\u4ece\u8eab\u5e7f\u62ff\u51fa\u4e86\u6761\u91d1\u94fe\u5b50\u9047\u5230\u8fd9\u5987\u4eba\u753b\u524d\u9053\u201c\u4f60\u8ba4\u4e0d\u8ba4\u5f97\u8fd9\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5012\u4eba\u77aa\u773c\u6014\u4e86\u534a\u664c\u624d\u5927\u58f0\u9053\u201c\u6211\u4e2d\u7136\u8ba4\u5f97\u8fd9\u672c\u6765\u4e5f\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6240\u4ee5\u6211\u73b0\u5728\u8fd8\u7ed9\u4f60\u53ea\u4e0d\u8fc7\u5e0c\u671b\u4f60\u77e5\u9053\uff0c\u4e3a\u4e86\u4fdd\u5b58\u8fd9\u6761\u91d1\u94fe\u5b50\u4ed6\u4e0d\u730e\u6328\u997f\u6328\u9a82\uff0c \u5176\u81f3\u4e0d\u60dc\u88ab\u670b\u53cb\u803b\u7b11\u4ed6\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\u4f60\u4e5f\u8be5\u60f3\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u5987\u4eba\u770b\u8fd9\u6761\u91d1\u94fe\u6237\u65e5\u4e2d\u7684\u6028\u6bd2\u4e4b\u8272\u6e10\u6e10\u53d8\u4e3a\u7f9e\u6127\u3002 \u3000\u3000\u5979\u6bd5\u7adf\u4e5f\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u4eba\u591a\u591a\u5c11\u5c11\u603b\u6709\u4e9b\u4eba\u6027\u7684\u3002 \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f60\u6362\u8fd9\u6761\u91d1\u94fe\u5b50\u5df2\u53fc\u597d\u597d\u7684\u505a\u70b9\u5c0f\u751f\u610f\u597d\u597d\u7684\u517b\u4f60\u7684\u5b69\u5b50\u4ee5\u5f8c\u4f60\u5b9a\u8fd8\u4f1a\u9047\u597d\u7537 \u4eba\u7684\u53ea\u8981\u4f60\u4e0d\u518d\u6b3a\u9a97\u522b\u4eba\u522b\u4eba\u4e5f\u4e0d\u4f1a\u6765\u7eed\u9a97\u4f60\u3002\u201d \u3000\u3000\u5987\u4eba\u7684\u8eab\u5b50\u53c8\u5f00\u59cb\u98a4\u6296\u8f6c\u8fc7\u5934\xb7\u53bb\u770b\u5979\u7684\u5b69\u6b79\u3002 \u3000\u3000\u88ab\u5b50\u8138\u4e0a\u6ee1\u662f\u60ca\u6050\u4e4b\u8272\u6487\u5634\u60f3\u54ed\xb7\u5374\u53c8\u5413\u5f97\u8fde\u54ed\u90fd\u4e0d\u6562\u54ed\u6e56\u58f0\u3002 \u3000\u3000\u71d5\u4e03\u67d4\u58f0\u9053\u201c\u83ab\u5fd8\u8bb0\u4f60\u5df2\u662f\u6bcd\u4eb2\uff0c\u5df2\u5e94\u8be5\u66ff\u4f60\u7684\u5b69\u5b50\u60f3\u4e00\u60f3\u4ed6\u5c06\u6765\u4e5f\u4f1a\u957f\u5927\u7684\xb7\u4f60\u5e94\u8be5 \u8ba9\u4ed6\u89c9\u5f97\u3001\u56e0\u4e3a\u6709\u4f60\u8fd9\u6837\u4e2a\u6bcd\u4eb2\u800c\u9a84\u50b2\u3002\u201d \u3000\u3000\u5987\u4eba\u98a4\u6296\u7a81\u53c8\u4f0f\u5728\u5730\u4e0a\u653e\u58f0\u75db\u54ed\u8d77\u6765\u75db\u54ed\u9053\u201c\u8001\u5929\u2026\u2026\u5404\u5929\u4f60\u4e3a\u4ec0\u9ebd\u53c8\u8981\u8ba9\u6211\u770b\u89c1\u4ed6\u3002 \u4e00\u3002\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u4e5f\u6ca1\u6709\u4eba\u80fd\u4e3a\u5979\u89e3\u7b54\u53ea\u6709\u5979\u81ea\u5df1\u3002 \u3000\u3000\u4f60\u683d\u4e0b\u53bb\u7684\u662f\u4ec0\u9ebd\u6837\u7684\u79cd\u5b50\u5c31\u5b9a\u4f1a\u5f97\u5230\u4ec0\u9ebd\u6837\u7684\u6536\u83b7\u3002 \u3000\u3000\u4f60\u683d\u4e0b\u53bb\u7684\u82e5\u662f\u7802\u77f3\u5c31\u6c38\u8fdc\u83ab\u8981\u671f\u671b\u5b83\u80fd\u65a4\u51fa\u7f8e\u4e3d\u7684\u82b1\u6735\u3002 \u3000\u3000\u9ec4\u660f\u3002\u6b22\u4e50\u82f1\u9192 \u3000\u3000\u5915\u9633\u5df2\u7531\u7eda\u70c2\u800c\u8f6c\u4e3a\u4e4e\u6323\u3002 \u3000\u3000\u90ed\u5927\u8def\u9992\u9992\u7684\u8d70\u5728\u9053\u65c1\xb7\u5fc3\u60c5\u663e\u7136\u4e5f\u548c\u4ed6\u8138\u8272\u540c\u6837\u6c89\u91cd\u3002 \u3000\u3000\u71d5\u4e03\u6ca1\u6709\u8bf4\u8bdd\u6ca1\u6709\u6253\u6270\u4ed6\u3002 \u3000\u3000\u5979\u77e5\u9053\u6bcf\u4e2a\u4eba\u90fd\u6709\u4ed6\u9700\u8981\u4e00\u4e2a\u4eba\u7a33\u9759\u7684\u65f6\u5019\u8fd9\u4e5f\u662f\u4e00\u4e2a\u505a\u4eba\u59bb\u5b50\u7684\u5973\u4eba\u6240\u6700\u9700\u8981\u4e86\u89e3 \u7684\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u90ed\u5927\u8def\u624d\u6c89\u58f0\u9053\u201c\u4f60\u4ec0\u9ebd\u65f6\u5019\u5c06\u90a3\u91d1\u94fe\u5b50\u9675\u51fa\u6765\u7684\uff1f\u4e3a\u4ec0\u9ebd\u4e0d\u544a\u8bc9 \u6211\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u4e86\u7b11\u9053\u201c\u56e0\u4e3a\u6211\u6839\u672c\u5c31\u6ca1\u6709\u8d4e\u51fa\u6765\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u6ca1\u6709\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u2019\u521a\u6211\u7ed9\u5979\u7684\u91d1\u94fe\u5b50\u6839\u672c\u4e0d\u662f\u4f60\u7684\u90a3\u6761\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u50e7\u7136\u9053\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u5fae\u7b11\u9053\u201c\u90a3\u662f\u6885\u5170\u59d0\u59b9\u79c1\u4e0b\u91cc\u9001\u7ed9\u6211\u7684\u8d3a\u793c\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u90a3\u4f60\u4e3a\u4ec0\u9ebd\u8981\u62ff\u51fa\u6765\uff0c\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u56e0\u4e3a\u6211\u4e5f\u662f\u4e2a\u5973\u4eba\u6211\u5bf9\u5973\u4eba\u603b\u6bd4\u4f60\u89e3\u5f97\u591a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u662f\u8bf4\u5979\u770b\u5230\u4e86\u8fd9\u6761\u91d1\u94fe\u5b50\xb7\u5c31\u4f1a\u60f3\u8d77\u6211\u4ee5\u524d\u5bf9\u5979\u603b\u7b97\u4e0d\u9519\uff0c\u6240\u4ee5\u624d\u80af\u653e\u8fc7 \u6211\uff1f\u201d \u3000\u3000\u9893\u4e03\u62bf\u5634\u7b11\u9053\u201c\u91d1\u94fe\u5b50\u770b\u8d77\u6765\u90fd\u662f\u5dee\u4e0d\u591a\u7684\u8fde\u4f60\u90fd\u5df2\u7ecf\u5206\u4e0d\u6e05\u4e86\xb7\u53c8\u4f55\u51b5\u629b\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u5f88\u6015\u5feb\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u91d1\u94fe\u5b50\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8c61\u5f81\u8c61\u5f81\u4ee5\u524d\u7684\u90a3\u6bb5\u5f80\u4e8b\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4eec\u65e2\u5df3\u8fde\u8fd9\u91d1\u94fe\u5b50\u90fd\u5206\u4e0d\u6e05\u4e86\u663e\u7136\u5df2\u5c06\u6614\u65e5\u7684\u60c5\u611f\u548c\u6028\u635f\u5168\u90fd\u6de1\u5fd8\u3002 \u3000\u3000\u65e0\u8bba\u591a\u5927\u65b9\u7684\u4ea5\u4eba\u90fd\u4e0d\u613f\u81ea\u5df1\u7684\u4e08\u592b\u8fd8\u5c06\u5f80\u4e8b\u85cf\u5728\u5fc3\u91cc\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u770b\u5230\u6211\u7684\u65f6\u5019\xb7\u5c31\u5e94\u8be5\u5df2\u7ecf\u60f3\u8d77\u4ee5\u524d\u2026\u2026\u201d \u3000\u3000\u71d5\u4e03\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\xb7\u9053\u201c\u5979\u90a3\u6837\u5b50\u5bf9\u5f25\u5e76\u4e0d\u662f\u4e3a\u4ee5\u524d\u7684\u4e8b\u800c\u662f\u56e0\u4e3a\u5ac9\u5992\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5ac9\u5992\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4e5f\u4e0d\u662f\u5ac9\u5992\u4f60\xb7\u662f\u5ac9\u5992\u6211\u3002\u770b\u770b\u5979\u81ea\u5df1\u7684\u65e5\u5b50\xb7\u518d\u770b\u770b\u4ed6\u4ec0\u5979\u66f4\u6094\u6068\u81ea\u5df1\u4ee5\u524d \u4e3a\u4ec0\u9ebd\u8981\u90a3\u6837\u505a\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\u201c\u4e2a\u4eba\u5bf9\u81ea\u5df1\u8bf2\u4fc3\u7684\u65f6\u5019\u5f80\u5f80\u5c31\u4f1a\u83ab\u540d\u5176\u5999\u7684\u5bf9\u522b\u4eba\u4e5f\u6000\u6068\u8d77\u6765\xb7\u6839 \u4e0d\u5f97\u5168\u4e16\u754c\u7684\u4eba\u90fd\u548c\u5979\u6837\u75c5\u82e6\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u53f9\u9053\u201c\u6240\u4ee5\u5979\u5c31\u60f3\u7834\u574f\u6211\u4efd\u3002\u2026 \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u6068\u4f60\xb7\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5979\u77e5\u9053\u81ea\u5df1\u5df2\u6c38\u8fdc\u65e0\u6cd5\u518d\u5f97\u5230\u4f60 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u53ef\u662f\u5979\u770b\u5230\u4e86\u90a3\u6761\u91d1\u94fe\u5b50\u65f6\u4e3a\u4ec0\u9ebd\u5ffd\u7136\u53c8\u53d8\u4e86\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u56e0\u4e3a\u91d1\u94fe\u5b50\u548c\u4f60\u4e0d\u540c\u3002\u201d \u3000\u3000\u5979\u56fa\u7136\u7b11\u63a5\u9053\u201c\u91d1\u94fe\u5b50\u4e0d\u4f46\u6bd4\u4f60\u597d\u770b\u800c\u4e14\u5979\u77e5\u9053\u81ea\u5df1\u5b9a\u53ef\u4ee5\u5f97\u5230\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u90a3\u662f\u4e0d\u662f\u56e0\u4e3a\u91d1\u94fe\u5b50\u5df2\u7ecf\u5728\u5979\u7684\u624b\u91cc\u5382\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u201d \u3000\u3000\u4e16\u535c\u7684\u786e\u53ea\u6709\u5973\u4eba\u624d\u4e86\u89e3\u4ea5\u4eba\u3002 \u3000\u3000\u5973\u4eba\u5411\u53ea\u76f8\u4fe1\u81ea\u5df1\u5df2\u62ff\u5728\u624b\u91cc\u7684\u4e1c\u897f\u5c31\u7b97\u5979\u660e\u77e5\u8fd8\u6709\u767e\u6761\u91d1\u94fe\u5b50\u53ef\u4ee5\u53bb\u62ff\u5979\u4e5f\u7edd\u4e0d\u80af\u7528 \u624b\u91cc\u8fd9\u6761\u53bb\u6362\u7684\u3002 \u3000\u3000\u4e5f\u6ca1\u6709\u51e0\u4e2a\u5973\u4eba\u80af\u5c06\u81ea\u5df1\u7684\u91d1\u94fe\u5b50\xb7\u9001\u7ed9\u4e08\u592b\u4ee5\u524d\u7684\u60c5\u4eba \u3000\u3000\u53ea\u6709\u5458\u806a\u660e\u7684\u5973\u4eba\u624d\u4f1a\u8fd9\u6837\u505a\u3002 \u3000\u3000\u5979\u53ea\u7528\u6761\u91d1\u94fe\u5b50\uff0c\u5df2\u6362\u53d6\u5979\u6587\u592b\u5bf9\u5979\u7684\u4fe1\u4efb\u548c\u611f\u6fc0\u4e5f\u6362\u6765\u5979\u81ea\u5df2\u7684\u751f\u5e78\u798f\u3002 \u3000\u3000\u90ed\u5927\u8def\u51dd\u89c6\u4ed6\u7684\u59bb\u5b50\u3001\u60c5\u4e0d\u540c\u7981\xb7\u63e1\u4f4f\u4e86\u5979\u7684\u624b\u67d4\u58f0\u9053\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u71d5\u4e5e\u7728\u773c\u7b11\u9053\u201c\u8c22\u8c22\u6211\uff1f\u2026\u2026\u8c22\u8c22\u6211\u90a3\u6761\u91d1\u94fe\u4e25\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6447\u6447\u5934\u9053\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u8c22\u7684\u662f\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7684\u786e\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u611f\u6fc0\u7684\u5f53\u7136\u4e0d\u662f\u6761\u91d1\u94fe\u5b50\u60af\u662f\u5979\u7684\u4e86\u89e3\u548c\u4f53\u8c05\u3002 \u3000\u3000\u90a3\u6bd4\u6240\u6709\u7684\u91d1\u94fe\u4e88\u52a0\u8d77\u6765\u8fd8\u8981\u73cd\u5458\u5f97\u591a\u3002 \u3000\u3000\u4e2a\u61c2\u5f97\u4e86\u89e3\u548c\u4f53\u8c05\u7684\u59bb\u5b50\u6c38\u8fdc\u662f\u7537\u4eba\u6700\u5927\u7684\u5e78\u798f\u548c\u8d22\u5bcc \u3000\u3000\u4e5f\u6c38\u8fdc\u53ea\u6709\u6700\u5e78\u8fd0\u7684\u7537\u4eba\u624d\u80fd\u5f97\u5230\u3002 \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u60c5\u3000\u4eba>> \u53e4\u9f99\u300a\u6b22\u4e50\u82f1\u96c4\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u60c5\u3000\u4eba \u3000\u3000\u60c5\u4eba\uff1f\u4ec7\u4eba\uff1f \u3000\u3000\u4e16\u4e0a\u662f\u4e0d\u662f\u771f\u6709\u5929\u751f\u5e78\u8fd0\u7684\u4eba\u5462\uff1f \u3000\u3000\u4e5f\u8bb8\u6709\xb7\u4f46\u81f3\u5c11\u6211\u5e76\u6ca1\u6709\u770b\u89c1\u8fc7\u3002 \u3000\u3000\u6211\u5f53\u7136\u4e5f\u770b\u89c1\u8fc7\u5e78\u8fd0\u7684\u4eba\u4f46\u4ed6\u4eec\u7684\u5e78\u8fd0\u5374\u90fd\u662f\u7528\u4ed6\u4eec\u7684\u667a\u6167\u3001\u51b3\u5fc3\u548c\u52c7\u6c14\u6362\u6765\u7684\u3002 \u3000\u3000\u5e78\u8fd0\u5c31\u8c61\u662f\u70d9\u997c\u6837\uff0c\u8981\u7528\u529b\u53bb\u63c9\u7528\u6cb9\u53bb\u714e\uff0c\u7528\u706b\u53bb\u62f7\uff0c\u7eaa\u4e2d\u4f1a\u4ece\u5929\u4e0a\u6389\u535c\u6765\u3002 \u3000\u3000\u5e78\u8fd0\u7684\u4eba\u5c31\u8c61\u662f\u65b0\u5a18\u5b50\u6837\u65e0\u8bba\u8d70\u5230\u90a3\u91cc\u90fd\u5b9a\u4f1a\u88ab\u4eba\u591a\u77a7\u51e0\u773c\u3002 \u3000\u3000\u65e0\u8bba\u591a\u5e73\u51e1\u7684\u4eba\xb7\u4e14\u505a\u4e86\u65b0\u5a18\u5b50\u5c31\u597d\u50cf\u5ffd\u7136\u53d8\u5f97\u7279\u522b\u4e86\u3002 \u3000\u3000\u738b\u52a8\u3001\u6797\u592a\u5e73\u3001\u7ea2\u5a18\u5b50\u53c1\u4e2a\u4eba\u7ad9\u505a\u6392\xb7\u9893\u4e03\uff0c\u4ece\u5934\u770b\u5230\u811a\u53c8\u4ece\u811a\u770b\u5230\u8138\u3002\u71d5\u4e03\u7684\u8138\u5df2\u88ab \u770b\u5f97\u8c61\u662f\u521a\u6458\u4e0b\u7684\u5c71\u91cc\u7ea2\xb7\u7ea2\u5f97\u53d1\u70eb\uff0c\u5fcd\u4e0d\u4f4f\u5782\u4e0b\u5934\u9053\u201c\u4f60\u4eec\u53c8\u4e0d\u662f\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u770b\u4ec0 \u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u8d61\u7136\u9053\u201c\u56e0\u4e3a\u4f60\u5b9e\u5728\u5df2\u7ecf\u6bd4\u4ee5\u9157\u597d\u770b\u53c1\u5e72\u516d\u767e\u500d\u3002\u201d \u3000\u3000\u71d5\u4e03\u7684\u8138\u66f4\u7ea2\uff0c\u9053\u201c\u4f46\u6211\u8fd8\u662f\u6211\u8fd9\u4e00\u70b9\u90fd\u6ca1\u6709\u53d8\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u53d8\u4e86\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u5730\u65b9\u53d8\u4e86\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u62a2\u9053\u201c\u4ee5\u524d\u4f60\u662f\u6211\u7684\u670b\u53cb\u73b0\u5728\u5374\u5df2\u53d8\u6210\u6211\u7684\u5ac2\u5b50\uff0c\u4ee5\u524d\u4f60\u662f\u71d5\u4e03\uff0c\u73b0\u5728\u5374\u5df2\u7ecf\u53d8 \u6210\u4e86\u90ed\u592a\u592a\u8fd9\u53d8\u5f97\u8fd8\u4e0d\u591f\u591a\u201d \u3000\u3000\u71d5\u4e03\u54ac\u5634\u5507\u9053\u201c\u6211\u8fd8\u662f\u9893\u4e03\uff0c\u8fd8\u662f\u4f60\u4eec\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5403\u5403\u7b11\u9053\u201c\u4f46\u8fd9\u4e2a\u71d5\u4e03\u81f3\u5c11\u5df2\u7ecf\u6bd4\u4ee5\u524d\u4e7e\u51c0\u591a\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\u201c\u7b54\u5bf9\u4e86\u5979\u73b0\u5728\u6bcf\u5929\u90fd\u6d17\u6fa1\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u8bf4\u5b8c\u7ea2\u5a18\u5b50\u5df2\u7b11\u5f2f\u8170\u3002 \u3000\u3000\u71d5\u4e03\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\u7ea2\u8138\u9053\u201c\u4f60\u5c11\u8bf4\u51e0\u53e5\u8bdd\u884c\u4e0d\u884c\uff1f\u53c8\u6ca1\u6709\u4eba\u5f53\u4f60\u54d1\u5df4\u3002\u201d \u3000\u3000\u7ea2\u65d7\u5b50\u5931\u7b11\u9053\u201c\u82e5\u80fd\u5c11\u8bf4\u51e0\u53e5\u8bdd\xb7\u5c31\u4e0d\u662f\u90ed\u5927\u8def\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e8e\u54b3\u4e86\u4e24\u58f0\xb7\u633a\u8d77\u80f8\u9053\u201c\u5176\u5b9e\u6211\u73b0\u5728\u4e5f\u53d8\u4e86\xb7\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u4e0d\u770b\u6211\uff1f\u201d \u3000\u3000\u738b\u52a8\u76b1\u7709\uff0c\u9053\u201c\u4f60\u4ec0\u9ebd\u5730\u65b9\u53d8\u4e86\uff1f\u6211\u600e\u9ebd\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u96be\u9053\u6ca1\u6709\u53d8\u5f97\u597d\u770b\u4e9b\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e0a\u4e0a\u4e0b\u4e0b\u770b\u4e86\u4ed6\u51e0\u773c\u6447\u5934\xb7\u9053\u201c\u6211\u770b\u4e0d\u51fa\u201c\u2606 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u81f3\u5c11\u6211\u603b\u4e5f\u53d8\u5f97\u4e7e\u51c0\u4e86\u4e9b\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u5fcd\u4f4f\u7b11\u9053\u201c\u73b0\u5728\u4f60\u4e5f\u5929\u5929\u6d17\u6fa1\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\xb7\u6211\u201d\u2026\u201c \u3000\u3000\u8fd9\u6b21\uff0c\u4ed6\u7684\u8bdd\u8fd8\u672a\u8bf4\u51fa\u53e3\u7ea2\u5a18\u4e86\u5df2\u53c8\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u71d5\u4e03\u8d76\u7d27\u4ecd\u5c94\u5927\u58f0\u9053\u201c\u8fd9\u5730\u65b9\u600e\u9ebd\u597d\u50cf\u5c11\u4e86\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u5979\u9053\u201c\u8c01\uff1f\u201d \u3000\u3000\u71d5\u4e03\u7728\u773c\u7b11\u9053\u201c\u5f53\u7136\u662f\u90a3\u4e2a\u6e05\u65e9\u8d77\u5e8a\xb7\u5c31\u63d0\u82b1\u6485\u4e0a\u5e02\u573a\u7684\u7b4b\u5a18\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u8fd9\u4e2a\u4eba\u5f53\u7136\u5c11\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u5979\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u4e49\u4e0a\u5e02\u573a\u53bb\u4e86\u4f46\u5374\u4e0d\u662f\u63d0\u82b1\u7bee\uff0c\u662f\u63d0\u83dc\u7bee\u56e0\u4e3a\u6211\u4eec\u7684\u6797\u5927\u5c11\u5ffd\u7136\u60f3\u5403\u65b0\u4e0a\u5e02 \u7684\u83e0\u83dc\u7092\u767e\u8150\u3002\u201d \u3000\u3000\u71d5\u4e03\u4e5f\u5fcd\u4f4f\u7b11\xb7\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u60f3\u4e0d\u5230\u5979\u5c0f\u5c0f\u7684\u5e74\u7eaa\uff0c\u5c31\u5df2\u7ecf\u8fd9\u9ebd\u6837\u61c2\u5f97\u6e29\u67d4\u4f53\u8d34\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\u201c\u5929\u751f\u6e29\u67d4\u4f53\u8d34\u7684\u4eba\uff0c\u65e0\u8bba\u5e74\u7eaa\u5927\u5c0f\uff0c\u90fd\u6837\u6e29\u67d4\u4f53\u8d34\u7684\u3002\u201d \u3000\u3000\u5979\u7528\u773c\u89d2\u77aa\u6797\u592a\u5e73\uff0c\u53c8\u9053\u201c\u90a3\u5c31\u597d\u50cf\u5929\u751f\u6709\u798f\u6c14\u7684\u4eba\u6837\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7684\u8138\u4e5f\u7ea2\u4e86\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4f53\u4eec\u5c11\u8bf4\u51e0\u53e5\u884c\u4e0d\u884c\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5f53\u4f60\u8ba2\u662f\u54d1\u5df4\u7684\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u60a0\u60a0\u9053\uff1a\u98de\u4e0d\u884c\u82e5\u80fd\u5c11\u8bf4\u51e0\u53e5\u8bdd\uff0c\u5c31\u4e0d\u662f\u5973\u4eba\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u665a\u971e\u6ee1\u5929\u2606 \u3000\u3000\u66ae\u98ce\u4e2d\u53c8\u4f20\u6765\u60a0\u626c\u6e05\u8106\u7684\u7f06\u58f0\u201c\u5c0f\u5c0f\u59d1\u5a18\u6e05\u65e9\u8d77\u5e8a\u3002 \u3000\u3000\u63d0\u82b1\u952e\u513f\xb7\u4e0a\u5e02\u573a\u201d\u2026\u2019\u6b22\u4e50\u82f1\u5df4 \u3000\u3000\u82d1\u4e03\u548c\u7ea2\u5a18\u5b50\u5bf9\u671b\u4e86\u4e00\u773c\u5fcd\u4e0d\u4f4f\u7b11\u9080\u201c\u5c0f\u5c0f\u59cb\u5a18\u5df2\u7ecf\u4ece\u5e02\u573a\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u800c\u4e14\uff0c\u5979\u7684\u82b1\u7bee\u91cc\u8fd8\u88c5\u6ee1\u4e86\u80b2\u83b1\u8c46\u8150\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u94f6\u94c3\u822c\u6e05\u8106\u7684\u58f0\u97f3\u7b11\u9053\u201c\u4e0d\u6b62\u83e0\u83dc\u8c46\u8150\xb7\u8fd8\u6709\u6d12\u3002\u201d \u3000\u3000\u5c0f\u5c0f\u59cb\u5a18\u679c\u7136\u5df2\u56de\u6765\u5382\u633d\u4e2a\u7af9\u7bee\u5b50\u7ad9\u5728\u95e8\u53e3\u53f3\u624b\u679c\u7136\u8fd8\u63d0\u5927\u9152\u3002 \u3000\u3000\u5979\u597d\u50cf\u5df2\u6ca1\u6709\u4ee5\u524d\u90a3\u9ebd\u5bb3\u7f9e\u2606\u53ea\u4e0d\u8fc7\u8138\u4e0a\u8fd8\u662f\u6709\u70b9\u53d1\u7ea2\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u6d12\uff1f\u4ec0\u9ebd\u9152\uff1f\u201d \u3000\u3000\u5c0f\u5c0f\u591f\u59ec\u8d50\u7136\u9053\u201c\u5f53\u7136\u662f\u559c\u9152\xb7\u6211\u5728\u5c71\u4e0b\u770b\u5230\u4ed6\u4eec\u4e24\u4f4d\u4eb2\u70ed\u7684\u6837\u5b50\u5c31\u77e5\u9053\u5e94\u8be5\u53bb\u4e70\u4e9b\u559c \u9152\u56de\u6765\u4e86\u201d \u3000\u3000\u71d5\u4e03\u7728\u773c\xb7\u9053\u201c\u662f\u6dee\u7684\u559c\u9152\uff1f\u662f\u6211\u4eec\u66f2\u8fd8\u662f\u4f60\u4eec\u7684\uff1f\u201d \u3000\u3000\u5c0f\u5c0f\u7ad9\u5a18\u201c\u7530\u54fc\u201d\u58f0\u7ea2\u8138\u8155\u4e86\u6cbf\u5899\u89d2\u8dd1\u5230\u5f8c\u9662\uff0c \u3000\u3000\u71d5\u4e03\u548c\u7ea2\u5a18\u4e8e\u90fd\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u56db\u4e07\u95e8\u6c14\u54fa\u54cd\u9053\u201c\u6211\u771f\u4e0d\u503c\u4e3a\u4ec0\u9ebd\u4f60\u851a\u603b\u559c\u6b22\u6b3a\u8d1f\u8001\u5b9e\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u8001\u5b9e\u4eba\u5df3\u8d8a\u6765\u8d8a\u5c11\xb7\u518d\u4e0d\u6b3a\u8d1f\u6b3a\u8d1f\uff0c\u4ee5\u5f8c\u5c31\u6ca1\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u7ed3\u8bba\u3002 \u3000\u3000\u559c\u4e8b\u91cc\u82e5\u6ca1\u6709\u9152\u5c31\u597d\u50cf\u83dc\u91cc\u6ca1\u6709\u76d0\u6837\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u5f53\u7136\u662f\u4e2a\u5f88\u806a\u660e\u7684\u4eba\u8bf4\u7684\u201c\u53ef\u60dc\u4ed6\u5fd8\u5382\u8bf4\u4e0b\u9762\u7684\u53e5\uff1a\u809a\u5b50\u91cc\u82e5\u6709\u4e86\u9152\xb7\u5934\u5c31\u4f1a\u75bc \u7684\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u4e0a\u8d77\u6765\u90ed\u5927\u8def\u7684\u5934\u5df2\u75bc\u5f97\u8981\u547d \u3000\u3000\u4ed6\u5f53\u7136\u5df4\u4e0d\u662f\u7b2c\u2018\u4e2a\u8d77\u6765\u7684\u4eba\u4ed6\u521a\u521a\u53d1\u73b0\u7761\u89c9\u6709\u65f6\u4e5f\u4e0d\u80fd\u7b97\u662f\u6d6a\u8d39\u5149\u9634\u3002 \u3000\u3000\u4ed6\u8d77\u6765\u7684\u65f6\u5019\u6797\u592a\u5e73\u548c\u670d\u5c0f\u59a8\u5a18\u53e3\u7ecf\u5728\u9662\u5b50\u91cc\u56bc\u7530\u5495\u5495\u4e5f\u4e0d\u77e5\u5728\u8bf4\u4e9b\u4ec0\u9ebd\u3002 \u3000\u3000\u65e0\u8bba\u8bf4\u4ec0\u9ebd\u4ed6\u4ecd\u90fd\u6837\u89c9\u5f97\u5f88\u6709\u8da3\uff0c\u5f88\u5f00\u5fc3\u3002 \u3000\u3000\u6625\u5929\u7684\u82b1\u867d\u5df2\u8c22\u5382\u4f46\u590f\u5929\u91cc\u7684\u82b1\u53c8\u76db\u5f00\u3002 \u3000\u3000\u4ed6\u4eec\u5c31\u7ad9\u5728\u82b1\u4e1b\u524d\u521d\u5347\u7684\u9610\u5149\u7167\u4ed6\u4eec\u5e78\u798f\u6652\u6109\u5feb\u7684\u8138 \u3000\u3000\u4ed6\u4eec\u4e5f\u538b\u548c\u521d\u5347\u7684\u592a\u9633\u6837\uff0c\u5145\u6ee1\u5382\u5149\u660e\u548c\u5e0c\u671b\u3002 \u3000\u3000\u90ed\u5927\u8def\u770b\u4ed6\u4fe9\u5934\u75bc\u5c31\u4eff\u62c2\u5df2\u597d\u4e86\u4e9b\u3002 \u3000\u3000\u71d5\u4e03\u6084\u6084\u7684\u8d70\u4e86\u51fa\u6765\xb7\u4f9d\u5047\u5728\u4ed6\u8eab\u592f\u53ea\u4e8e\u5a29\u6f06\u9ed1\u7684\u957f\u53d1 \u3000\u3000\u5929\u5730\u95f4\uff0c\u7247\u548c\u5e73\u5b81\u9189\u751f\u547d\u5b9e\u5728\u662f\u503c\u5f97\u4eba\u4eec\u73cd\u63aa\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\xb7\u71d5\u4e03\u624d\u8f7b\u8f7b\u9053\u201c\u4f60\u5728\u60f3\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u5728\u60f3\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u8c01\uff1f\u738b\u52a8\u548c\u2026\u3002\u201c \u3000\u3000\u90ed\u5927\u8def\u70b9\u70b9\u5934\uff0c\u53f9\u606f\u9053\u201c\u6211\u5728\u60f3\uff0c\u4e0d\u77e5\u8981\u7b49\u5230\u7530\u4e00\u5929\uff0c\u4ed6\u4eec\u624d\u4f1a\u8fd9\u6837\u5b50\u4eb2\u70ed\u3002\u201d \u3000\u3000\u71d5\u4e03\u51dd\u89c6\u5979\u7684\u4e08\u592b\uff0c\u826f\u4e45\u826f\u4e45\u624d\u67d4\u58f0\u9053\u201c\u4f60\u77e5\u9053\u6211\u4e3a\u4ec0\u9ebd\u559c\u6b22\u4f60\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u6ca1\u6709\u8bf4\u8bdd\u5728\u7b49\u542c\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u542c\u3002 \u3000\u3000\u71d5\u4e03\u67d4\u58f0\u9053\u201c\u56e0\u4e3a\u4f60\u5728\u4f60\u81ea\u5df2\u5e78\u798f\u7684\u65f6\u5019\uff0c\u8fd8\u80fd\u60f3\u5230\u670b\u53cb\u7684\u5e78\u798f\u56e0\u4e3a\u4f60\u65e0\u8bba\u5728\u4ec0\u9ebd\u65f6 \u5019\xb7\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u7728\u773c\uff0c\u9053\u201c\u4f60\u9519\u4e86\u6709\u65f6\u6211\u4e5f\u4f1a\u5fd8\u8bb0\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d\u90ed\u5927\u8def\u4fcf\u4fcf\u9053\u201c\u6628\u5929\u665a\u4e0a\u3002\u2026\u201c \u3000\u3000\u5979\u7684\u8bdd\u8fd8\u672a\u51fa\u71d5\u4e03\u7684\u80a1\u5df2\u98de\u7ea2\u62ff\u8d77\u4ed6\u7684\u624b\u72e0\u72e0\u54ac\u4e86\u4e00\u66f0\u3002\u53ea\u542c\u6797\u592a\u5e73\u7b11\u9053\u817f\u4e0d\u5230\u6211\u4eec\u7684 \u90ed\u5927\u5ac2\u5c45\u7136\u8fd8\u4f1a\u54ac\u4eba\u7684\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df3\u8f6c\u8fc7\u8eab\u5b50\u6b63\u5728\u770b\u8fd9\u4e24\u4e2a\u4eba\u5fae\u7b11\u3002 \u3000\u3000\u7ff1\u5927\u8def\u7b11\u9053\u201c\u8fd9\u4f60\u4e0d\u5c31\u6a2a\u4e86\xb7\u6ca1\u6709\u88ab\u5973\u4eba\u54ac\u8fc7\u7684\u7537\u4eba\uff0c\u6839\u672c\u5c31\u4e0d\u80fd\u7b97\u505a\u7537\u4eba\u3002\u201d\u6797\u592a\u5e73 \u9053\u201c\u8fd9\u662f\u54ea\u56fd\u7684\u9053\u7406\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u6211\u8fd9\u56fd\u7684\xb7\u4f46\u4f60\u8bf4\u4e0d\u5b9a\u5f88\u5feb\u4e5f\u4f1a\u5230\u6211\u8fd9\u2018\u56fd\u6765 \u3000\u3000\u5c0f\u7ad9\u5a18\u7684\u80a1\u4e5f\u5df2\u968f\u7ea2\uff0c\u5782\u4e0b\u5934\u9053\u201c\u6211\u53bb\u51c6\u5907\u65e9\u70b9\u53bb\u2018\xb7\u3002\u201d\u90ed\u5927\u8def\u5927\u7b11\uff0c\u9053\u201c\u591a\u51c6\u5907\u70b9 \u4e5f\u597d\u585e\u4f50\u6211\u4eec\u7684\u8e6d\u3002\u201d \u3000\u3000\u73b0\u5728\u6b63\u662f\u65e9\u996d\u7684\u65f6\u5019\u3002 \u3000\u3000\u6e5b\u84dd\u8272\u7684\u82cd\u5b98\u4e0b\xb7\u4e73\u767d\u8272\u7684\u708a\u70df\u56db\u8d77\u3002 \u3000\u3000\u90ed\u5927\u8def\u6307\u8d77\u5934\xb7\u54fa\u57d4\u9053\u201c\u8fd9\u5730\u65b9\u600e\u9ebd\u5ffd\u7136\u70ed\u95f9\u8d77\u6765\u4e86\xb7\u662f\u672c\u662f\u53c8\u62a5\u6765\u4e86\u5f88\u591a\u6237\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6ca1\u6709\u5440\u201c \u3000\u3000\u90ed\u5927\u8def\u671b\u81ea\u5c71\u5761\u4e0a\u4e39\u8d77\u7684\u868a\u59fb\u9053\u201c\u82e5\u6ca1\u6709\u4eba\u5bb6\uff0c\u9890\u6765\u7684\u708a\u59fb\uff1f\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u6797\u592a\u5e73\u4e5f\u5728\u671b\u708a\u70df\u5347\u8d77\u7684\u5730\u65b9\u9053\u201c\u6628\u5929\u4e0b\u5348\u6211\u8fd8\u5230\u90a3\u8fb9\u53bb\u901b\u8fc7\u8fde\u5bb6\u4eba\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u6c89\u541f\xb7\u9053\u201c\u5c31\u7b97\u6628\u5929\u665a\u4e0a\u6709\u4eba\u642c\u6765\u4e5f\u4e0d\u4f1a\u5ffd\u7136\u4e00\u4e0b\u4e8e\u642c\u6765\u8fd9\u9ebd\u591a\u5bb6\u3002\u201d\u6797\u592a\u5e73\u9053 \u201c\u4f55\u51b5\u8fd9\u9644\u8fd1\u6839\u672c\u8fde\u4efb\u4eba\u7684\u5730\u65b9\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u53ea\u4e0d\u8fc7\u9732\u5929\u4e0b\u4e5f\u53ef\u4ee5\u8d77\u706b\u7684\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u5ffd\u7136\u6709\u8fd9\u9ebd\u591a\u4eba\u5230\u8fd9\u91cc\u6765\u8d77\u706b\u5462\uff1f\u96be\u9053\u771f\u9609\u5f97\u6ca1\u4e8b\u505a\u4e86\uff1f\u201d \u3000\u3000\u53ea\u542c\u4eba\u7f13\u7f13\u9053\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u731c\u731c\u5230\u660e\u5e74\u4e5f\u731c\u4e0d\u51fa\u7ed3\u679c\u6765\u7684\u4e3a\u4ec0\u9ebd\u4e0d\u81fc\u5df1\u51fa\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u5e72\u52a8\u6c47\u65bd\u65bd\u7136\u4ece\u95e8\u5916\u8d70\u4e86\u8fdb\u6765\u8138\u4e0a\u8fd8\u662f\u4ec0\u9ebd\u53cb\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u90ed\u5927\u8def\u7b2c\u4e2a\u8fce\u4e0a\u53bb\xb7\u62a2\u95ee\u9053\u201c\u4f60\u5df2\u7ecf\u6e56\u53bb\u770b\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u77a9\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u70df\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u5e72\u52a8\u9053\u201c\u706b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u8c01\u8d77\u7684\u706b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4eba\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4ec0\u9ebd\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u4e24\u6761\u817f\u7684\u4eba\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u770b\u6765\u6211\u8fd9\u6837\u95ee\u4e0b\u5de6\uff0c\u95ee\u5230\u660e\u5e74\u4e5f\u6837\u95ee\u4e0d\u51fa\u7ed3\u679c\u6765\u7684\u8fd8\u662f\u767d\u5df2 \u51fa\u5165\u770b\u770b\u7684\u597d\u3002\u201d \u3000\u3000\u4e0a\u52a8\u9053\u201c\u4f60\u65e9\u8be5\u51fa\u53bb\u770b\u770b\u4e86\u3002\u201d \u3000\u3000\u5bcc\u8d35\u5c71\u5e84\u7684\u5f8c\u9762\u662f\u5c71\u810a\u6839\u672c\u5c31\u65e0\u8def\u53ef\u901a\u524d\u9762\u7684\u5c71\u5761\u4e0a\u2606\u7adf\u5728\u591c\u95f4\u642d\u8d77\u4e86\u516b\u5ea7\u5de8\u5927\u7684\u5e10 \u901f\u3002 \u3000\u3000\u80c0\u7bf7\u7684\u5f62\u5f0f\u6839\u5947\u7279\uff0c\u6709\u51e0\u5206\u8c61\u662f\u5173\u5916\u7267\u6c11\u7528\u7684\u8499\u53e4\u5305\u53c8\u6709\u513f\u5206\u8c61\u662f\u884c\u519b\u9a8f\u624e\u7528\u7684\u8425\u5e10\u3002 \u3000\u3000\u6bcf\u5ea7\u5e10\u7bf7\u524d\u90fd\u8d77\u5382\u5806\u706b\u3002 \u3000\u3000\u706b\u4e0a\u70e4\u6574\u53ea\u7684\u80a5\u7f8a\uff0c\u7528\u94c1\u6761\u7a7f\u6162\u6162\u7684\u8f6c\u52a8\u3002 \u3000\u3000\u4e2a\u7cbe\u8d64\u4e0a\u8eab\u7684\u5927\u6c49\u6b63\u5c06\u5df2\u8c03\u597d\u7684\u4f5c\u6599\u7528\u5237\u5b50\u5237\u5728\u7f8a\u8eab\u4e0a\u52a8\u4f5c\u8f7b\u67d4\u4e3d\u4ed4\u7ec6\uff0c\u5c31\u8c61\u662f\u4e2a\u6bcd\u4eb2 \u5728\u4e3a\u5979\u7b2c\u2019\u4e2a\u7537\u513f\u6d17\u6fa1\u2500\u6837\u3002 \u3000\u3000\u70e4\u8089\u7684\u9999\u6c14\u5f53\u7136\u6bd4\u82b1\u9999\u66f4\u6d53\u3002 \u3000\u3000\u65e9\u9910\u7684\u684c\u4e8e\u4e0a\u4e5f\u6709\u8089\u3002 \u3000\u3000\u4ed6\u4eec\u521a\u4ece\u5916\u9762\u8f6c\u4e86\u5708\u56de\u6765\u672c\u8be5\u90fd\u5df2\u7ecf\u5f88\u997f\u3002 \u3000\u3000\u4f46\u9664\u4e86\u90ed\u5927\u8def\u5916\uff0c\u522b\u4eba\u5374\u597d\u50cf\u90fd\u6ca1\u6709\u4ec0\u9ebd\u80c3\u53e3\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u6709\u6570\u90a3\u4e9b\u8d26\u7bf7\u5f53\u7136\u4e0d\u4f1a\u662f\u65e0\u7f18\u65e0\u6545\u642d\u5728\u8fd9\u91cc\u7684\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u65e2\u7136\u80fd\u5728\u4e00\u591c\u95f4\u4e0d\u58f0\u4e0d\u54cd\u7684\u642d\u8d77\u516b\u5ea7\u5982\u6b64\u76ee\u5927\u7684\u5e10\u7bf7\u6765\u4e16\u4e0a\u53ea\u4f2f\u5c31\u5f88\u5c11\u8fd8\u6709\u4ed6\u4eec \u505a\u4e0d\u5230\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u9891\u4e03\u7ec8\u4e8e\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\u201c\u770b\u6765\u6211\u4eec\u53c8\u6709\u9ebb\u70e6\u6765\u5382\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u76ee\u4e2d\u4e5f\u5145\u6ee1\u4e86\u5fe7\u90c1\u4e4b\u8272\uff0c\u9053\u201c\u800c\u4e14\u8fd9\u6b21\u7684\u9ebb\u70e6\u8fd8\u4e0d\u5c0f\u3002\u201d\u71d5\u4e03\u9053\u201c\u5374\u4e0d\u77e5\u8fd9\u6b21\u7684 \u9ebb\u70e6\u662f\u8c01\u60f9\u6765\u7684\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u4e94\u523b\u9053\u201c\u8fd9\u6b21\u7edd\u4e0d\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u8fd8\u8471\u4e0d\u8d77\u8fd9\u9ebd\u5927\u7684\u9ebb\u70e6\u6765\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\u9053\u201c\u6211\u8fd9\u4eba\u5411\u662f\u5c0f\u9ebb\u70e6\u4e0d\u65ad\xb7\u5927\u9ebb\u70e6\u6ca1\u6709\u3002\u201d \u3000\u3000\u85cf\u4e03\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u8fd9\u6b21\u9ebb\u70e6\u662f\u5927\u662f\u5c0f\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u82e5\u4e0d\u662f\u4e3a\u4e86\u4ef6\u5f88\u5927\u7684\u4e8b\uff0c\u8c01\u80af\u5728\u522b\u4eba\u95e8\u53e3\u642d\u8d77\u8fd9\u9ebd\u5927\u7684\u516b\u5ea7\u5e10\u7bf7\u6765\uff1f\u201d\u71d5\u4e03 \u9053\u201c\u4f46\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\u3002\u6211\u3002\u8fd8\u770b\u77f3\u51fa\u6709\u4ec0\u9ebd\u9ebb\u70e6\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4eba\u5bb6\u53ea\u4e0d\u8fc7\u662f\u591c\u5916\u9762\u7684\u7a7a\u5730\u4e0a\u642d\u4e86\u51e0\u5ea7\u5e10\u7bf7\u70e4\u76ee\u5df1\u7684\u8089\u53c8\u6ca1\u6709\u6765\u6e90\u6211\u4ecd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u770b\u6ca1\u6709\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u54bd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u521a\u662f\u8c01\u8bf4\u53c8\u6709\u9ebb\u70e6\u6765\u4e86\u7684\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u70bc\u600e\u9ebd\u5ffd\u7136\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u201d \u3000\u3000\u71d5\u4e03\u8e1e\u7136\u7b11\uff0c\u9053\u201c\u56e0\u4e3a\u8fd9\u5730\u65b9\u592a\u95f7\u4e86\u6211\u60f3\u8ddf\u4f60\u62a2\u59cb\u6760\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u6211\u82e5\u8bf4\u6ca1\u6709\u9ebb\u70e6\u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u6211\u5c31\u8bf4\u6709\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u82e6\u7b11\u9053\u201c\u770b\u6837\u5b50\u6211\u60f3\u4e0d\u8ddf\u4fed\u6cbb\u6760\u90fd\u4e0d\u884c\u3002\u201d \u3000\u3000\u71d5\u4e03\u7b11\u9053\u201c\u7b54\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u82e5\u60f3\u627e\u5979\u7684\u4e08\u592b\u59cb\u6760\u6bcf\u2606\u523b\u4e2d\u90e8\u53ef\u4ee5\u627e\u5f97\u51fa\u516b\u6b21\u673a\u4f1a\u6765\u3002 \u3000\u3000\u4f46\u63a8\u6760\u6709\u65f6\u4e5f\u4e0d\u662f\u574f\u4e8b\u90a3\u81f3\u5c11\u53ef\u4ee5\u8ba9\u770b\u4ed6\u4eec\u62ec\u6760\u7684\u4eba\u5fc3\u60c5\u8f7b\u677e\u4e9b\u3002\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u63b3\u6760\uff0c\u522b\u7684\u4eba\u90fd\u7b11\u4e86\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u7b11\u9053\u201c\u4e0d\u7ba1\u600e\u9ebd\u6837\uff0c\u81f3\u5c11\u4eba\u5bb6\u73b0\u5728\u8fd8\u6ca1\u6709\u627e\u4e0a\u6211\u4eec\u6211\u4eec\u4f55\u5fc5\u81ea\u627e\u70e6\u607c\uff1f\u201d \u3000\u3000\u53ea\u53ef\u501f\u73b0\u5728\u5df2\u7528\u4e0d\u4ed6\u4eec\u53bb\u627e\u70e6\u607c\u5df2\u7ecf\u8fdb\u4e86\u4ed6\u4eec\u7684\u95e8\u4e86\u3002 \u3000\u3000\u95e8\u5916\u5df2\u6709\u4e2a\u4eba\u6162\u4fc3\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u5f88\u9ad8\u3001\u5f88\u7626\uff0c\u8eab\u4e0a\u7a7f\u4ef6\u989c\u8272\u5f88\u5947\u7279\u7684\u957f\u886b\u7adf\u662f\u60e8\u78a7\u8272\u7684\u3002 \u3000\u3000\u4ed6\u8138\u8272\u4e5f\u9634\u6c89\u7684\u8c61\u662f\u8863\u88f3\u6837\u53cc\u773c\u775b\u5374\u9ab8\u6de1\u65e0\u5149\u8c61\u662f\u4e24\u4e2a\u6ca1\u6709\u5e95\u7684\u6d1e\u8fde\u773c\u81ea\u548c\u773c\u73e0\u5b50\u90fd\u5206 \u4e0d\u51fa\uff0c\u7adf\u662f\u4e2a\u778e\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u811a\u6b65\u5374\u72e0\u8f7b\uff0c\u5c31\u597d\u50cf\u5728\u811a\u5e95\u4e0b\u751f\u4e86\u53cc\u773c\u775b\u7ec8\u4e0d\u4f1a\u8e29\u77f3\u5934\u66f4\u4e0d\u4f1a\u6389\u8fdb\u6d1e\u3002 \u3000\u3000\u4ed6\u80cc\u8d1f\u53cc\u624b\u6162\u6162\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u8138\u8272\u867d\u9634\u6c89\uff0c\u795e\u6001\u5374\u5f88\u60a0\u95ed\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u9601\u4e0b\u662f\u4e0d\u662f\u6765\u627e\u4eba\u7684\uff1f\u627e\u8c01\uff1f\u201d \u3000\u3000\u78a7\u886b\u4eba\u597d\u50cf\u6839\u672c\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u90ed\u5927\u8def\u76b1\u7709\u9053\u201c\u96be\u9053\u8fd9\u4eba\u4e0d\u4f46\u662f\u4e2a\u778e\u5b50\uff0c\u8fd8\u662f\u4e2a\u804b\u5b50\uff1f\u201d \u3000\u3000\u5899\u89d2\u4e0b\u7684\u82b1\u5703\u91cc\xb7\u590f\u5b63\u7684\u82b1\u5f00\u5f97\u6b63\u8273\u3002 \u3000\u3000\u8fd9\u78a7\u886b\u4eba\u6cbf\u82b1\u5703\u8d70\u8fc7\u53bb\uff0c\u4e49\u8d70\u4e86\u56de\u6765\u6df1\u6df1\u7684\u547c\u5438\u3002 \u3000\u3000\u4ed6\u867d\u5df2\u65e0\u6cd5\u7528\u773c\u775b\u6765\u6b23\u8d4f\u82b1\u7684\u9c9c\u8273\u5374\u8fd8\u80fd\u7528\u9f3b\u5b50\u6765\u9886\u7565\u82b1\u7684\u82ac\u82b3\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u80fd\u9886\u7565\u7684\u6709\u773c\u964c\u7684\u5165\u53cd\u800c\u9886\u7565\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u6cbf\u82b1\u5703\u6765\u56de\u8d70\u4e86\u4e24\u904d\u2019\u53e5\u8bdd\u6c72\u8bf4\xb7\u53c8\u9992\u9992\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90ed\u5927\u8def\u677e\u4e86\u53e3\u6c14\uff0c\u9053\u201c\u770b\u6765\u8fd9\u4eba\u4e5f\u5e76\u4e0d\u662f\u6765\u94b1\u9ebb\u70e6\u7684\uff0c\u53ea\u4e0d\u8fc7\u5230\u8fd9\u91cc\u6765\u95fb\u95fb\u82b1\u9999\u800c \u5df2\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4ed6\u600e\u9ebd\u77e5\u9053\u8fd9\u91cc\u6709\u82b1\uff1f\u201d\u90ed\u5927\u8def\u9053\u201c\u4ed6\u9f3b\u5b50\u5f53\u7136\u6bd4\u6211\u300a\u7075\u5f97\u591a\u3002\u201d \u3000\u3000\u71d5\u4e03\u9053\u201c\u4f46\u4ed6\u662f\u4ece\u54ea\u91cc\u6765\u7684\u5462\u201d \u3000\u3000\u7ff1\u5927\u8def\u7b11\u9053\u201c\u6211\u53c8\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u6211\u600e\u9ebd\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u5ffd\u7136\u9053\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u8bf4\u4ed6\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ece\u7b3c\u91cc\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d \u3000\u3000\u738b\u52a8\u7684\u8138\u8272\u4eff\u4f5b\u72e0\u6c89\u91cd\u7f00\u7f13\u9053\u201c\u56e0\u4e3a\u522b\u7684\u4eba\u73b0\u5728\u6839\u672c\u5df2\u4e0d\u53ef\u80fd\u8d70\u5230\u8fd9\u91cc\u6765\u6211\u4eec\u4e5f\u6c72\u6cd5\u5b50 \u8d70\u5230\u522b\u7684\u5730\u65b9\u53bb\u4e86\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u56e0\u4e3a\u90a3\u516b\u5ea7\u5e10\u7bf7\u5df2\u5c06\u6240\u6709\u7684\u901a\u8def\u5168\u90fd\u5c01\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u52a8\u5bb9\u9053\u201c\u4f11\u662f\u8bf4\u4ed6\u4eec\u5728\u5916\u9762\u642d\u8d77\u90a3\u516b\u5ea7\u5f20\u7bf7\xb7\u4e3a\u7684\u5c31\u662f\u4e0d\u8ba9\u522b\u7684\u4eba\u5230\u8fd9\u91cc\u6765\xb7\u4e5f \u4e0d\u8ba9\u8fd9\u91cc\u7684\u4eba\u51fa\u53bb\uff1f\u201d \u3000\u3000\u738b\u52a8\u4e0d\u518d\u5f00\u53e3\u773c\u775b\u76ef\u5916\u9762\u7684\u82b1\u5703\u795e\u60c5\u5374\u66f4\u6c89\u91cd\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u4e5f\u8ddf\u4ed6\u56de\u5934\u77a7\u4e86\u4e00\u773c\u8138\u8272\u4e5f\u7acb\u523b\u53d8\u4e86\u3002 \u3000\u3000\u672c\u6765\u5f00\u5f97\u6b63\u597d\u7684\u9c9c\u82b1\u5c31\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\u7adf\u5df2\u5168\u90fd\u67af\u840e\u3002 \u3000\u3000\u5ae1\u7ea2\u7684\u82b1\u74e3\u7adf\u5df2\u8d6b\u7136\u53d8\u6210\u4e4c\u9ed1\u8272\u7684\u6709\u98ce\u6b27\u65f6\u5c31\u74e3\u74e3\u8521\u5382\u5382\u6765\u3002\u90ed\u5927\u8def\u5931\u58f0\u9053\u201c\u8fd9\u662f\u600e\u9ebd \u56de\u4e8b\uff1f\u662f\u4e0d\u662f\u521a\u90a3\u4e2a\u4eba\u653e\u7684\u6bd2\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u54fc\u201c \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u96be\u9053\u8fd9\u4eba\u662f\u6761\u6bd2\u86c7\u53ea\u8981\u4ed6\u8d70\u8fc7\u7684\u5730\u65b9\u8fde\u82b1\u8349\u90fd\u4f1a\u88ab\u6bd2\u6b7b\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6015\u8fde\u6bd2\u86c7\u4e5f\u6ca1\u6709\u4ed6\u6bd2\u3002\u201d \u3000\u3000\u71d5\u4e03\u901a\uff0c\u201c\u548c\u9519\u6211\u672c\u6765\u4ee5\u4e3a\u90a3\u65e0\u5b54\u4e0d\u5165\u8d64\u7ec3\u86c7\u5df2\u662f\u5929\u4e0b\u4f7f\u6bd2\u7684\u7b2c\u9ad8\u624b\u53ef\u662f\u4ed6\u548c\u8fd9\u4e2a\u4eba\u6bd4 \u597d\u50cf\u8fd8\u5dee\u4e86\u5f88\u591a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\uff0c\u201c\u8fd8\u5dee\u5f88\u591a\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5e76\u4e0d\u662f\u95ee\u71d5\u4e03\u7684\uff0c\u4ed6\u95ee\u7684\u662f\u7ea2\u5a18\u5b50\u3002\u7ea2\u5a18\u5b50\u53f9\u4e86\u53e3\u6c14\u9053\u79f0\u7ec3\u6218\u4e0b\u6bd2\u5f97\u7528\u4e1c\u897f\u5e2e\u5fd9 \u8fd8\u5f97\u4e0b\u5728\u98df\u7269\u6216\u6c34\u91cc\u3001\u5175\u5203\u6697\u5668\u4e0a\uff0c\u4f46\u8fd9\u4eba\u4e0b\u6bd2\u5374\u8fde\u70b9\u5f71\u5b50\u90fd\u6ca1\u6709\uff0c\u4eff\u4f5b\u5728\u547c\u5438\u95f4\u5c31\u80fd\u5c06\u4eba \u6bd2\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u4e0d\u518d\u95ee\u4e86\u3002 \u3000\u3000\u82e5\u8fde\u7ea2\u5a18\u5b50\u90fd\u8bf4\u8fd9\u4eba\u4e0b\u6bd2\u7684\u624b\u6bb5\u6bd4\u8d64\u7ec3\u86c7\u9ad8\xb7\u90a3\u5c31\u8868\u793a\u8fd9\u4ef6\u4e8b\u5df2\u7edd\u65e0\u7591\u95ee\u3002 \u3000\u3000\u73b0\u5728\u7684\u95ee\u9898\u662f\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u4e3a\u4ec0\u9ebd\u8981\u5230\u8fd9\u91cc\u6765\u628a\u4ed6\u4eec\u7684\u82b1\u6bd2\u6b7b\uff1f \u3000\u3000\u8fd9\u95ee\u9898\u8fd8\u6ca1\u6709\u7b54\u6848\uff0c\u7b2c\u4e8c\u4e2a\u95ee\u9898\u53c8\u6765\u4e86\u3002\xb7\u95e8\u5916\u53c8\u6709\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u72e0\u77ee\u3001\u5f88\u80d6\u8eab\u535c\u7a7f\u4ef6\u9c9c\u7ea2\u7684\u8863\u670d\u5706\u5706\u7684\u8138\u4e0a\u6ee1\u9762\u7ea2\u5149\u597d\u50cf\u6bd4\u4ed6\u7684\u8863\u88f3\u8fd8\u7ea2\u3002 \u3000\u3000\u4ed6\u4e5f\u80cc\u8d1f\u53cc\u624b\u65bd\u65bd\u7136\u8d70\u5382\u8fdb\u6765\uff0c\u795e\u60c5\u770b\u6765\u4e5f\u5f88\u60a0\u95f2\u3002\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u8fd9\u6b21\u6ca1\u6709\u4eba\u518d\u95ee\u4ed6\u662f\u6765\u5e72\u4ec0\u9ebd\u7684\u4e86\u4f46\u5374\u90fd\u7741\u5927\u4e86\u773c\u775b\u770b\u629b\u3002 \u3000\u3000\u9662\u5b50\u91cc\u7684\u82b1\u53cd\u6b63\u5df2\u5168\u88ab\u6bd2\u6b7b\u770b\u4f60\u8fd8\u6709\u4ec0\u9ebd\u82b1\u6837\u73a9\u51fa\u6765\u3002\u8fd9\u7ea2\u8863\u4eba\u5c45\u7136\u4e5f\u597d\u50cf\u6839\u672c\u6ca1\u6709\u770b \u89c1\u4ed6\u4eec\uff0c\u5728\u9662\u4e88\u91cc\u652b\u6162\u8e6c\u4e86\u4e00\u5708\u5c31\u626c\u957f\u800c\u53bb\u975e\u4f46\u6ca1\u6709\u8bf4\u53e5\u8bdd\u4e5f\u6ca1\u6709\u73a9\u4e00\u70b9\u82b1\u6837\u3002 \u3000\u3000\u4f46\u5730\u4e0a\u5374\u5df2\u591a\u4e86\u5708\u811a\u5370\u6bcf\u4e2a\u811a\u5370\u90fd\u5f88\u6df1\uff0c\u5c31\u8c61\u662f\u7528\u5200\u523b\u51fa\u6765\u7684\u3002 \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u770b\u71d5\u4e03\u95ee\u9053\u201c\u6211\u60c5\u613f\u8ba9\u5927\u8c61\u6765\u8e29\u6211\u4e00\u4e0b\u5b50\u4e5f\u4e0d\u613f\u88ab\u8fd9\u4eba\u8e29\u4e0a\u4e00\u811a\u4f60 \u5462\uff1f\u201d \u3000\u3000\u71d5\u4e03\u9053\uff1a\u201c\u6211\u4e24\u6837\u90fd\u4e0d\u613f\u610f\u3002\u3002 \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u9053\u201c\u4f60\u8fd9\u4eba\u6bd4\u6211\u806a\u660e\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u7b11\u591a\u4e45\uff0c\u56e0\u4e3a\u95e8\u5916\u5df2\u53c8\u6765\u4e86\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u6b21\u6765\u7684\u662f\u767d\u8863\u4eba\u8eab\u767d\u8863\u5982\u96ea\u8138\u8272\u4e5f\u51b7\u5f97\u8c61\u51b0\u96ea\u3002 \u3000\u3000\u522b\u4eba\u90fd\u662f\u6162\u9992\u7684\u8d70\u8fdb\u6765\u4ed6\u5374\u4e0d\u662f\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u8f7b\u98d8\u98d8\u7684\uff0c\u9635\u98ce\u5439\u8fc7\uff0c\u4ed6\u7684\u4eba\u5df2\u51fa\u73b0\u5728\u9662\u5b50\u91cc\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u95e8\u5916\u7a81\u7136\u53c8\u6709\u9053\u9752\u8679\u8272\u7684\u5251\u5149\u51b2\u5929\u800c\u8d77\u6a2a\u98de\u8fc7\u6811\u68a2\xb7\u95ea\u800c\u6c72\u3002 \u3000\u3000\u6811\u4e0a\u7684\u53f6\u5b50\u7acb\u523b\u96ea\u82b1\u822c\u98d8\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u767d\u8863\u4eba\u62ac\u5934\u770b\u4e86\u773c\u7a81\u7136\u957f\u9971\u5c55\uff0c\u5411\u4e0a\u9762\u62db\u4e86\u62db\u624b\xb7\u6f2b\u5929\u843d\u53f6\u7acb\u523b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u4e5f\u7acb\u523b\u4e0d\u89c1\u4e86\u5c31\u8c61\u662f\u7a81\u7136\u88ab\u9635\u98ce\u5439\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u91cc\u53ea\u542c\u95e8\u5916\u6709\u4eba\u6c89\u58f0\u9053\u201c\u738b\u52a8\u738b\u5e84\u4e3b\u5728\u804c\u91cc\uff1f\u201d \u3000\u3000\u4e24\u4e08\u5916\u7684\u767d\u6768\u6811\u4e0b\u59a8\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u8910\u8863\u8001\u4eba\u624b\u91cc\u62ff\u5f20\u5927\u7ea2\u8d56\u4e8e\xb7\u6b63\u76ee\u5149\u707c\u707c\u7684\u770b\u4ed6\u4eec\uff0c \u3000\u3000\u4ed6\u4eec\u516d\u4e2a\u4eba\u6392\u7ad9\u5728\u95e8\u53e3\xb7\u5c31\u597d\u50cf\u7279\u5730\u8d70\u51fa\u6765\u8ba9\u522b\u4eba\u770b\u7684\u3002 \u3000\u3000\u8910\u8863\u8005\u4eba\u7684\u76ee\u5149\uff0c\u4ece\u4ed6\u4eec\u8138\u4e0a\u4e00\u4e2a\u4e2a\u770b\u4e86\u8fc7\u53bb\xb7\u624d\u6c89\u58f0\u9053\u201c\u54ea\u4f4d\u662f\u738b\u5ea7\u4e3b\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u3002\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u8bf7\u7ad9\u5f20\uff0c\u662f\u4e13\u7a0b\u9001\u6765\u8bf7\u738b\u5e86\u4e3b\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6709\u4eba\u8981\u8bf7\u6211\u5403\u996d\u3002\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ec0\u9ebd\u65f6\u5019\uff1f\u201d \u3000\u3000\u8910\u519c\u8001\u4eba\u9053\u201c\u5c31\u5728\u4eca\u665a\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u5c31\u5728\u6b64\u5730\u3002\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u90a3\u65b9\u4fbf\u5f97\u5f88\u3002\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u4e0d\u9519\xb7\u7684\u786e\u65b9\u4fbf\u5f97\u5f88\u738b\u5e84\u4e3b\u53ea\u8981\u4e00\u51fa\u95e8\xb7\u5c31\u5df2\u5230\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u4e3b\u4eba\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u4e3b\u4eba\u4eca\u591c\u5fc5\u5b9a\u5728\u6b64\u76f8\u5019\xb7\u738b\u5e84\u4e3b\u5fc5\u5b9a\u53ef\u4ee5\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u65e2\u7136\u5982\u6b64\u53c8\u4f55\u5fc5\u4e13\u7a0b\u9001\u8fd9\u8bf7\u5e16\u6765\uff1f\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u672d\u4e0d\u53ef\u5eb7\uff0c\u8bf7\u5e16\u603b\u662f\u8981\u7684\xb7\u5c31\u8bf7\u738b\u5e84\u4e3b\u6536\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u62ac\uff0c\u624b\u4e0a\u7684\u8bf7\u7ad9\u5c31\u6162\u9992\u7684\u5411\u738b\u52a8\u98de\u4e86\u8fc7\u6765\uff0c\u98de\u5f97\u5f88\u7a33\u5f88\u6162\u7b80\u76f4\u5c31\u597d\u50cf\u4e0b\u9762\u6709\u53cc\u770b \u4e0d\u5230\u7684\u624b\u5728\u6258\u4e00\u6837\u3002 \u3000\u3000\u738b\u52a8\u53c8\u7b11\u4e86\u7b11\u624d\u8c08\u8c08\u5730\u8bf4\u9053\u201c\u539f\u6765\u9601\u4e0b\u4e13\u7a0b\u9001\u8fd9\u8bf7\u9ab8\u6765\xb7\u4e3a\u7684\u5c31\u662f\u8981\u6211\u4eec\u770b\u770b\u9601\u4e0b\u8fd9\u624b \u6c14\u529f\u7684\u3002\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u6c89\u8138\u51b7\u51b7\u9053\u201c\u738b\u5e84\u4e3b\u89c1\u7b11\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u4e5f\u6c89\u4e0b\u4e86\u8138\u9053\u201c\u521a\u8fd8\u6709\u51e0\u4f7c\u4e5f\u90fd\u9732\u4e86\u624b\u72e0\u6f02\u4eae\u7684\u6b66\u529f\u9601\u4e0b\u8ba4\u4e0d\u8ba4\u5f97\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u8ba4\u5f97\u3002\u201d \u3000\u3000\u738b\u52a8\u8c0a\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u9053\u201c\u738b\u5e84\u4e3b\u53c8\u4f55\u5fc5\u95ee\u6211\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e0d\u95ee\u4f60\u95ee\u8c01\uff1f\u201d \u3000\u3000\u8910\u8863\u8001\u4eba\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\u76ee\u5149\u6709\u610f\u65e0\u610f\u95f4\u8198\u4e86\u6797\u592a\u5e73\u4e00\u773c\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u4e0d\u7981\u8ddf\u770b\u4e86\u6797\u592a\u5e73\u4e00\u773c\uff0c\u8fd9\u624d\u53d1\u73b0\u6797\u592a\u5e73\u7684\u8138\u8272\u7adf\u5df2\u82cd\u767d\u5f97\u5168\u65e0\u8840\u8272\uff0c\u795e\u60c5\u5c31 \u4eff\u4f5b\u738b\u52a8\u90a3\u6b21\u5ffd\u7136\u770b\u89c1\u5929\u4e0a\u7684\u98ce\u7b2c\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u96be\u9053\u662f\u6765\u627e\u6797\u592a\u5e73\u7684 \u3000\u3000\u8910\u519c\u8001\u4eba\u5df2\u8d70\u4e86\u3002\u4ed6\u8d70\u7684\u65f6\u5019\u738b\u52a8\u52a9\u6ca1\u6709\u963b\u62e6\u4e5f\u6ca1\u6709\u518d\u95ee\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u5df2\u770b\u51fa\u4eca\u5929\u6765\u7684\u8fd9\u4e9b\u4eba\u5fc5\u5b9a\u548c\u6797\u592a\u5e73\u6709\u70b9\u5173\u7cfb\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u53bb\u95ee\u4ed6\u5927\u5bb6\u751a\u81f3\u8fde\u770b\u90fd\u907f\u514d\u53bb\u770b\u4ed6\uff0c\u514d\u5f97\u4ed6\u4e3a\u96be\u3002 \u3000\u3000\u90ed\u5927\u8def\u751a\u81f3\u95ee\u58ec\u52a8\uff0c\u9053\u201c\u4f60\u8bf4\u4ed6\u521a\u9732\u7684\u90a3\u624b\u662f\u54ea\u79cd\u6c14\u529f\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6c14\u529f\u5c31\u662f\u6c14\u529f\u53ea\u6709\u79cd\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4e3a\u4ec0\u9ebd\u53ea\u6709\u4e00\u79cd\u3002\u3002 \u3000\u3000\u738b\u52a8\u9053\u201c\u4ea5\u513f\u7ea2\u6709\u51e0\u79cd\uff1f\u201d\u6b22\u4e50\u82f1\u96c4 \u3000\u3000\u8036\u5927\u8def\u9053\u201c\u53ea\u6709\u79cd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4e3a\u4ec0\u9ebd\u53ea\u6709\u79cd\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u56e0\u4e3a\u5973\u513f\u7ea2\u5df2\u7ecf\u662f\u6700\u597d\u7684\u6d12\u65e0\u8bba\u4ec0\u9ebd\u4e1c\u897f\u6700\u597d\u7684\u90fd\u53ea\u6709\u79cd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u65e2\u7136\u4e5f\u660e\u767d\u8fd9\u9053\u7406\u4e3a\u4ec0\u9ebd\u8fd8\u8981\u6765\u95ee\u6211\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\u9053\u201c\u4f9d\u6211\u770b\uff0c\u6700\u53ef\u6015\u8fd8\u662f\u521a\u90a3\u5251\u90a3\u7b80\u76f4\u5df2\u7ecf\u548c\u4f20\u8bf4\u4e2d\u80fd\u53d6\u4eba\u9996\u7ea7\u4e8e \u5343\u91cc\u4e4b\u5916\u7684\u5fa1\u5251\u672f\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u8fd8\u5dee\u5f97\u591a\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u770b\u8fc7\u5fa1\u5251\u672f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u8fd8\u5dee\u5f97\u591a\uff1f\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u6211\u5c31\u662f\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u53f9\u4e86\u53e3\u6c14\xb7\u82e6\u7b11\u9053\u8fd9\u4eba\u600e\u9ebd\u5ffd\u7136\u53d8\u5f97\u4e0d\u8bb2\u7406\u5382\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u51e0\u65f6\u770b\u89c1\u6211\u8bb2\u8fc7\u7406\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f88\u5c11\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u7684\u5f53\u7136\u662f\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u60f3\u8ba9\u6797\u592a\u5e73\u89c9\u5f97\u8f7b\u677e\u4e9b\u3002 \u3000\u3000\u4f46\u6797\u592a\u5e73\u7684\u8138\u90e8\u8fd8\u662f\u82cd\u767d\u5f97\u5168\u65e0\u76bf\u8272\u751a\u81f3\u8fde\u53cc\u624b\u90fd\u7d27\u5f20\u5f97\u7d27\u7d27\u63e1\u5728\u8d77\u4e2a\u4eba\u6765\u6765\u56de\u56de\u5728\u9662 \u4e8e\u91cc\u8f6c\u4e86\u51e0\u5708\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\u5927\u58f0\u8bf4\u9053\u201c\u6211\u77e5\u9053\u4ed6\u4ec0\u662f\u8c01\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u5f00\u80f6\xb7\u4f46\u6bcf\u4e2a\u4eba\u90fd\u5728\u542c\u3002 \u3000\u3000\u6797\u592a\u5e73\u770b\u5730\u4e0a\u7684\u811a\u5370\u9053\u201c\u8fd9\u4eba\u53eb\u5f3a\u9f99\xb7\u4e5f\u6b63\u662f\u5929\u5916\u516b\u9f99\u4e2d\u786c\u529f\u5458\u5f3a\u7684\u4e2a\u3002\u201d \u3000\u3000\u738b\u52a8\u76b1\u7709\u9053\u201c\u5929\u5916\u516b\u9f99\uff1f\u521a\u51fa\u73b0\u7684\u90a3\u53c1\u4e2a\u4eba\u5168\u90fd\u662f\u5929\u5916\u516b\u9f99\u4e2d\u7684\u4eba\uff1f\u201d\u6797\u592a\u5e73\u9053\u201c\u5168\u90fd \u662f\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9646\u4e0a\u9f99\u738b\u5ea7\u524d\u7684\u5929\u9f99\u516b\u5c06\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u5929\u5916\u516b\u9f99\u4e5f\u53ea\u6709\u2018\u79cd\u3002\u201d \u3000\u3000\u738b\u52a8\u9053\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u5c31\u662f\u77e5\u9053\u3002\u201d \u3000\u3000\u58ec\u52a8\u770b\u4e86\u770b\u90ed\u5927\u8def\xb7\u4e24\u4e2a\u4eba\u90fd\u7b11\u4e86\xb7\u90ed\u5927\u8def\u9053\u201c\u8fd9\u5c31\u53eb\u62a5\u8fd8\uff01 \u3000\u3000\u62a5\uff0c\u800c\u4e14\u8fd8\u7684\u771f\u5feb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u76ee\u4e2d\u5374\u9732\u4e86\u75db\u82e6\u4e4b\u8272\u7d27\u63e1\u53cc\u624b\u6765\u6765\u56de\u56de\u53c8\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\u7a81\u53c8\u505c\u4e0b\u811a\u6b65\u5927\u58f0\u9053\u201c\u4ed6 \u4eec\u4e5f\u77e5\u9053\u6211\u662f\u8c01\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u7b11\u9053\u201c\u8fd9\u5c31\u7528\u4e0d\u4ed6\u4eec\u544a\u8bc9\u6211\u4e86\xb7\u6211\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u76ef\u4ed6\xb7\u76ee\u5149\u597d\u50cf\u72e0\u5947\u602a\u9053\u201c\u4f60\u771f\u7684\u77e5\u9053\uff1f\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u6211\u6700\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u672c\u662f\u6700\u5bb9\u6613\u56de\u7b54\u7684\u53e5\u8bdd\uff0c\u4f46\u90ed\u5927\u8def\u53cd\u5012\u88ab\u95ee\u5f97\u695e\u4f50\u4e86\u3002 \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\u8138\u4e0a\u7684\u8868\u60c5\u66f4\u75db\u82e6\u7f13\u7f13\u9053\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u6211\u662f\u8c01\u751a\u81f3\u8fde\u6211\u81ea\u5df1 \u90fd\u4e0d\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u770b\u81ea\u5df1\u7d27\u63e1\u7684\u624b\u9053\u201c\u56e0\u4e3a\u6211\u5c31\u662f\u9646\u4e0a\u9f99\u738b\u7684\u513f \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\u8fde\u738b\u52a8\u90fd\u9732\u51fa\u4e86\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e5f\u6014\u4f50\u5403\u60ca\u7684\u7a0b\u5ea6\u7b80\u76f4\u5df2\u548c\u4ed6\u542c\u5230\u9884\u4e03\u662f\u5357\u5bab\u4e11\u7684\u4ea5\u513f\u65f6\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5de5\u5a18\u5b50\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\u201c\u4ee4\u5c0a\u7eb5\u6a2a\u5929\u4e0b\uff0c\u6c14\u76d6\u5f53\u4e16\u6b66\u6797\u4e2d\u8c01\u4e0d\u656c\u4ef0\uff1f\u201d\u2500\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u7a81\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\u5927\u58f0\u9053\u201c\u6211\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6014\u5382\u8bc1\uff0c\u9053\u201c\u4f60\uff1f\u201d \u3000\u3000\u6797\u5927\u5e73\u54ac\u7259\uff0c\u9053\u201c\u6211\u53ea\u5e0c\u671b\u6ca1\u6709\u8fd9\u9ebd\u6837\u4e2a\u7236\u4eb2\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u76b1\u4e86\u76b1\u7709\u9053\u201c\u4f60\u5c31\u7b97\u5f88\u4e0d\u6ee1\u4ed6\u66ff\u4f60\u8ba2\u4e0b\u7684\u4eb2\u4e8b\uff0c\u4e5f\u4e0d\u8c08\u2026\u2026\u201d \u3000\u3000\u6797\u592a\u5e73\u7a81\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\u201c\u66ff\u6211\u8ba2\u4eb2\u7684\u4e5f\u4e0d\u662f\u4ed6\u3002\u201d \u3000\u3000\u7ff1\u5927\u8def\u4e5f\u6014\u4e86\u5f81\u9053\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u76ee\u4e2d\u5df2\u6709\u6cea\u76c8\u7736\u5782\u5934\uff0c\u9053\u201c\u6211\u4e94\u5c81\u7684\u65f6\u5019\u4ed6\u5c31\u5df3\u79bb\u5f00\u6211\u4eec\u4ece\u6b64\u4ee5\u8d28\u6211\u5c31\u6ca1\u6709\u518d\u89c1 \u8fc7\u4ed6\u9762\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u9053\u201c\u4f60\u2500\u2026\u2018\u4f60\u76f4\u8ddf\u4ee4\u5802\u7684\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u70b9\u70b9\u5934\uff0c\u773c\u6cea\u5df2\u5c06\u593a\u7736\u4e3d\u51fa\u3002 \u3000\u3000\u90ed\u5927\u8def\u4e0d\u80fd\u518d\u95ee\u4e5f\u4e0d\u5fc5\u518d\u95ee\u4e86\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u770b\u71d5\u4e03\uff0c\u4e24\u4e2a\u4eba\u5fc3\u91cc\u90fd\u5df3\u660e\u767d\uff0c\u8c61\u9646\u4e0a\u9f99\u738b\u8fd9\u6837\u66f2\u7537\u4eba\u7529\u6389\u4e2a\u5973\u4eba\u5e76\u4e0d\u662f\u4ef6\u5947\u602a \u7684\u4e8b\u3002 \u3000\u3000\u4f46\u88ab\u629b\u5f03\u7684\u5973\u4eba\u82e5\u662f\u81ea\u5df1\u7684\u6bcd\u4eb2\uff0c\u505a\u513f\u5b50\u7684\u5fc3\u91cc\u53c8\u4f1a\u6709\u4ec0\u9ebd\u611f\u89c9\uff1f \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u5bf9\u6797\u592a\u5e73\u5f88\u540c\u60c5\u5374\u53c8\u4e0d\u6562\u8868\u9732\u51fa\u6765\u540c\u60c5\u548c\u601c\u60af\u6709\u65f6\u4e5f\u4f1a\u523a\u4f24\u522b\u4eba\u7684\u5fc3\u3002 \u3000\u3000\u73b0\u5728\u80fd\u5b89\u6170\u6797\u592a\u5e73\u7684\u4e5f\u8bb8\u53ea\u6709\u90a3\u5c0f\u5c0f\u59d1\u5a18\u4e00\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u5927\u5bb6\u6b63\u60f3\u6697\u793a\u5979\uff0c\u7559\u4e0b\u5979\u4e2a\u4eba\u6765\u966a\u6797\u592a\u5e73\uff0c\u5766\u5ffd\u53c8\u53d1\u73b0\u8fd9\u5c0f\u6b22\u4e50\u82f1\u9192\u8236\u5a18\u8138\u4e0a\u7684\u8868\u60c5\u7adf \u4e5f\u548c\u6797\u592a\u5e73\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5979\u7684\u8138\u8272\u4e5f\u82cd\u767d\u5f97\u53ef\u6015\xb7\u5782\u5934\uff0c\u54ac\u5634\u5507\xb7\u8fde\u7530\u5507\u90fd\u5feb\u54ac\u7834 \u3000\u3000\u8fd9\u7eaf\u771f\u5584\u826f\u66f2\u5c0f\u5c0f\u59d1\u5a18\xb7\u96be\u9053\u4e5f\u4f1a\u6709\u4ec0\u9ebd\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\uff1f \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u5728\u54fa\u54fa\u81ea\u8bed\u9053\u201c\u4ed6\u8fd9\u6b21\u6765\xb7\u4e00\u5b9a\u662f\u8981\u903c\u6211\u8ddf\u4ed6\u56de\u53bb\u4ed6\u751f\u4f2f\u6211\u4f1a\u8d70\u3002\u6240\u4ee5\u624d\u5148 \u5c06\u51fa\u8def\u5168\u90fd\u5c01\u6b7b\u3002\u201d \u3000\u3000\u90ed\u5927\u8def\u5fcd\u4e0d\u4f4f\u9053\u201c\u4f60\u96be\u5907\u600e\u6837\u529e\u5462\uff1f\u201d \u3000\u3000\u6797\u592a\u5e73\u7d27\u63e1\u53cc\u62f3\u9053\u2019\u6211\u7edd\u4e0d\u7f00\u4ed6\u56de\u53bb\xb7\u81ea\u4ece\u4ed6\u79bb\u5f00\u6211\u4eec\u7684\u90a3\u5929\u6211\u5c31\u5df2\u6ca1\u6709\u7236\u4eb2\u3002\u201d \u3000\u3000\u4ed6\u64e6\u5e72\u4e86\u6cea\u75d5\u6301\u8d77\u5934\xb7\u8138\u4e0a\u9732\u51fa\u4e86\u575a\u51b3\u7684\u8868\u60c5\u770b\u738b\u52a8\u4ed6\u4eec\u2606\u4e00\u5b87\u5b87\u9053\u201c\u65e0\u8bba\u600e\u9ebd\u6837\u8fd9\u4ef6 \u4e8b\u90fd\u548c\u4f60\u4eec\u6ca1\u6709\u5173\u7cfb\u6240\u4ee5\u4eca\u5929\u665a\u4ea1\u4f60\u97f6\u300f\u4e5f\u4e0d\u5fc5\u53bb\u89c1\u4ed6\u6211\u2026\u2026\u201d \u3000\u3000\u90a3\u5c0f\u5c0f\u59a8\u5a18\u5ffd\u7136\u9053\u201c\u4f60\u4e5f\u4e0d\u5fc5\u53bb\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u4e5f\u6014\u4f50\xb7\u6014\u95e8\u826f\u4e45\u624d\u5fcd\u4e0d\u4f4f\u95ee\u9053\u201c\u4e3a\u4ec0\u9ebd\u6211\u4e5f\u4e0d\u5fc5\u53bb\uff1f\u201d \u3000\u3000\u5c0f\u5c0f\u59d1\u5a18\u9053\u201c\u56e0\u4e3a\u4ed6\u800d\u627e\u7684\u4e5f\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u9053\u201c\u4e0d\u662f\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u5c0f\u59a8\u59ec\u9053\uff1a\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u66f4\u5403\u60ca\u3002 \u3000\u3000\u6bd4\u95fb\u4e16\u7684\u9646\u4e0a\u9f99\u738b\u600e\u9ebd\u4f1a\u7279\u5730\u6765\u627e\u4e2a\u5356\u82b1\u7684\u5c0f\u591f\u5a18\uff1f\u8fd9\u79cd\u4e8b\u6709\u8c01\u76f8\u4fe1\u3002 \u3000\u3000\u4f46\u770b\u5230\u8fd9\u5c0f\u59a8\u5a18\u7684\u8138\u8272\uff0c\u5927\u5bb6\u53c8\u4e0d\u80fd\u4e0d\u6410\u3002 \u3000\u3000\u5979\u5c31\u8c61\u662f\u5df2\u5ffd\u7136\u53d8\u6210\u4e86\u53e6\u4e2a\u4eba\xb7\u5df2\u4e0d\u518d\u5bb3\u7f9e\u4e86\xb7\u773c\u775b\u76f4\u89c6\u6797\u592a\u5e73\u7f13\u7f13\u5730\u9053\u201c\u4f60\u77e5\u4e0d\u77e5\u9053 \u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u672c\u6765\u4e5f\u662f\u4e2a\u5f88\u5bb9\u6613\u56de\u7b54\u7684\u8bdd\u4f46\u6797\u592a\u5e73\u4e5f\u88ab\u95ee\u5f97\u6014\u4f50\u3002 \u3000\u3000\u5c0f\u5c0f\u7ad9\u5a18\u770b\u4ed6\xb7\u5631\u89d2\u9732\u51fa\u4e86\u4e1d\u51c4\u51c9\u7684\u7b11\u610f\uff0c\u7f13\u7f13\u63a5\u9053\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u6211\u662f\u8c01\u751a\u81f3\u8fde\u6211\u81ea\u5df1 \u90fd\u4e0d\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u662f\u6797\u592a\u5e73\u521a\u8bf4\u8fc7\u7684\uff0c\u5979\u73b0\u5728\u53c8\u8bf4\u4e86\u51fa\u6765\uff0c\u5927\u5bb6\u672c\u8be5\u89c9\u5f97\u72e0\u53ef\u7b11\uff0c \u3000\u3000\u53ef\u662f\u770b\u5230\u5979\u73b0\u5728\u7684\u6837\u5b50\u65e0\u8bba\u8c01\u90fd\u7b11\u4e0d\u51fa\u6765\u7684\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u6709\u9893\u4e03\u5728\u65c1\u8fb9\u90ed\u5927\u8def\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8fc7\u53bb\u63e1\u8d77\u5979\u7684\u624b\xb7\u95ee\u5979\u4e3a\u4ec0\u9ebd\u8981\u5982\u6b64\u60b2\u4f24\uff0c\u5982 \u6b64\u96be\u53d7\u3002 \u3000\u3000\u5979\u8fd8\u5e74\u9752\xb7\u751f\u547d\u53c8\u5982\u6b64\u7f8e\u4e3d\uff0c\u53c8\u6709\u4ec0\u9ebd\u4e8b\u662f\u4e0d\u80fd\u89e3\u51b3\u7684\u5462\uff1f \u3000\u3000\u6797\u592a\u5e73\u5df2\u8fc7\u53bb\u63e1\u8d77\u5979\u7684\u624b\uff0c\u67d4\u58f0\u9053\u201c\u65e0\u8bba\u4ed6\u662f\u4ec0\u9ebd\u6837\u7684\u4eba\u90c1\u65e0\u59a8\xb7\u6211\u53ea\u77e5\u9053\xb7\u4f60\u5c31\u662f \u4f60\u3002\u201d \u3000\u3000\u5c0f\u5c0f\u59a8\u5a18\u5c31\u8ba9\u81ea\u5df1\u51b0\u51b7\u7684\u624b\u88ab\u4ed6\u63e1\xb7\u9053\u201c\u6211\u77e5\u9053\u4f60\u8bf4\u7684\u662f\u771f\u5fc3\u8bdd\u53ea\u4e0d\u8fc7\u4f60\u8fd8\u662f\u5e94\u8be5\u95ee\u6e05 \u695a\u6211\u662f\u8c01\u7684\u201d \u3000\u3000\u6797\u592a\u5e73\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\u201c\u597d\u6211\u95ee\u4f60\u7a76\u7adf\u662f\u8c01\u5462\u201d \u3000\u3000\u5c0f\u5c0f\u59cb\u5a18\u95ed\u4e0a\u773c\u775b\u7f13\u7f13\u9053\u201c\u6211\u5c31\u662f\u4f60\u672a\u6765\u7684\u59bb\u5b50\xb7\u4f60\u6bcd\u4eb2\u672a\u6765\u7684\u5ab3\u5987\u4f46\u5374\u662f\u4f60\u7236\u4eb2\u4ee5\u524d \u7684\u4ec7\u4eba\u3002\u201d \u3000\u3000\u6797\u592a\u5e73\u5ffd\u7136\u5168\u8eab\u51b0\u51b7\u7d27\u63e1\u7684\u624b\u4e5f\u6162\u6162\u7684\u653e\u5f00\xb7\u5782\u4e0b\u2026\u2026 \u3000\u3000\u4ed6\u7684\u5fc3\u4e5f\u8ddf\u8d77\u6c89\u4e0b\xb7\u4eff\u4f5b\u5df2\u6c89\u5230\u4ed6\u51b0\u51b7\u7684\u80f8\u5fc3\u91cc\uff0c\u6b63\u88ab\u4ed6\u81ea\u5df1\u8df5\u8e0f\u3002"}),showOnHomepage:!0}]}}]);