"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[89571],{51249:(w,e,m)=>{m.r(e),m.d(e,{default:()=>s});var n=m(74848);const s=[{name:"\u8881\u7d2b\u70df",content:(0,n.jsx)(n.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u7ade\u8273\u4e89\u5ba0 \u3000\u3000\u8881\u7d2b\u70df\u7f13\u6b65\u884c\u51fa\u4e86\u201c\u9752\u83b2\u6d1e\u201d\uff0c\u8fce\u7740\u51db\u70c8\u7684\u5bd2\u98ce\uff0c\u767b\u4e0a\u4e86\u7ec8\u5357\u5c71\u7684\u5cf0\u9876\u3002 \u3000\u3000\u5927\u96ea\u5c01\u7fa4\u5c71\uff0c\u94f6\u88c5\u88f9\u7389\u5cf0\uff0c\u4e3e\u6b65\u96be\u7559\u8db3\uff0c\u4e0d\u89c1\u5bf8\u8349\u751f\u3002 \u3000\u3000\u597d\u4e00\u4e2a\u51b7\u51c4\u7684\u5bd2\u591c\uff01 \u3000\u3000\u4f46\u8fd9\u91cc\u9ad8\u51fa\u4e91\u8868\uff0c\u89c6\u754c\u6e05\u660e\u3002 \u3000\u3000\u8881\u7d2b\u70df\u76d8\u819d\u5750\u4e0b\uff0c\u4ef0\u671b\u7740\u591c\u7a7a\u661f\u8fb0\u3002 \u3000\u3000\u8fd9\u662f\u5e08\u7236\u4f20\u6388\u5979\u7684\u672f\u6cd5\u4e4b\u4e00\uff0c\u7531\u661f\u8c61\u53d8\u5316\u4e2d\uff0c\u9ed8\u5bdf\u51fa\u5929\u4e0b\u7684\u5174\u4ea1\u6c14\u6570\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6bcf\u6708\u4e4b\u4e2d\uff0c\u603b\u6709\u4e24\u4e09\u4e2a\u6ca1\u6709\u6708\u4eae\u7684\u665a\u4e0a\uff0c\u767b\u4e0a\u5cf0\u9876\uff0c\u5750\u5728\u96ea\u5730\u4e0a\uff0c\u591c\u89c2\u661f\u8c61\u3002 \u3000\u3000\u8fd9\u751f\u6d3b\uff0c\u8881\u7d2b\u70df\u5df2\u7ecf\u8fc7\u4e86\u4e03\u5e74\u3002 \u3000\u3000\u5c11\u5973\u60c5\u6000\u65e0\u5904\u5bc4\uff0c\u6570\u7740\u661f\u513f\u620f\u3002 \u3000\u3000\u4eca\u591c\uff0c\u7fa4\u661f\u4f9d\u7136\u707f\u70c2\uff0c\u95ea\u7740\u4eae\u4e3d\u7684\u5149\u8292\uff01 \u3000\u3000\u8881\u7d2b\u70df\u7684\u8138\u4e0a\u6cdb\u73b0\u4e86\u5fae\u5fae\u7b11\u610f\u3002 \u3000\u3000\u7f8e\u4e3d\u7684\u7b11\u9765\uff0c\u5982\u82b1\u76db\u653e\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u98ce\u96ea\u4e0d\u61c2\u5c11\u5973\u5fc3\u3002 \u3000\u3000\u662f\u7684\uff0c\u8881\u7d2b\u70df\u5df2\u5728\u8fd9\u91cc\u4f4f\u4e86\u5341\u4e94\u4e2a\u5bd2\u6691\uff0c\u5bc2\u5bde\u7684\u5341\u4e94\u5e74\u3002 \u3000\u3000\u4e09\u5e74\u524d\uff0c\u5e08\u7236\u7559\u4e0b\u4e86\u4e00\u5f20\u7b80\u67ec\uff0c\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7684\u751f\u6d3b\uff0c\u5c31\u66f4\u4e3a\u5b64\u5355\u3001\u5bc2\u5bde\u4e86\uff01 \u3000\u3000\u4f46\u5979\u7684\u7075\u667a\u5df2\u5f00\uff0c\u6280\u827a\u548c\u672f\u6cd5\uff0c\u4e5f\u5728\u5bc2\u5bde\u7684\u751f\u6d3b\u4e2d\u7a81\u98de\u731b\u8fdb\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u8881\u7d2b\u70df\u7684\u7b11\u9765\u50f5\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u98a6\u8d77\u79c0\u7709\u513f\uff0c\u51dd\u6ce8\u591c\u7a7a\u3002 \u3000\u3000\u591c\u7a7a\u4e2d\u51b2\u8d77\u4e86\u4e00\u9053\u738b\u6c14\uff0c\u76f4\u72af\u7d2b\u5fae\u3001\u592a\u5fae\u3001\u5929\u5e02\uff0c\u4e09\u57a3\u661f\u5bbf\u3002 \u3000\u3000\u53d8\u5316\u592a\u5feb\u4e86\uff0c\u4e0d\u662f\u81ea\u7136\u7684\u5929\u9053\u5faa\u73af\u8fd0\u884c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5185\u5fc3\u4e2d\u6fc0\u8d77\u4e86\u5267\u70c8\u7684\u6ce2\u52a8\u3002 \u3000\u3000\u4ec0\u4e48\u4eba\uff1f\u6b65\u7f61\u884c\u6cd5\uff0c\u5e72\u6270\u5929\u673a\uff0c\u4f7f\u4e71\u8c61\u65e9\u5448\uff0c\u4eba\u95f4\u5f88\u5feb\u53c8\u5c06\u6ca6\u5165\u52ab\u6740\u7684\u82e6\u96be\u3002 \u3000\u3000\u6211\u5df2\u7ec3\u6210\u4e00\u8eab\u672c\u9886\uff01\u96be\u9053\u5c31\u4e0d\u80fd\u963b\u6b62\u8fd9\u4e00\u573a\u4eba\u95f4\u7684\u82e6\u96be\uff1f\u4f7f\u4e07\u6c11\u82cd\u751f\u591a\u51e0\u5e74\u517b\u606f\u751f\u673a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4ef6\u5927\u5927\u7684\u529f\u5fb7\u554a\uff1f \u3000\u3000\u6211\u8981\u6251\u706d\u90a3\u5347\u8d77\u7684\u738b\u6c14\uff0c\u7184\u53bb\u90a3\u71ce\u539f\u7684\u661f\u706b\u2026\u2026 \u3000\u3000\u9501\u4e0d\u4f4f\u5fc3\u733f\u610f\u9a6c\uff0c\u8881\u7d2b\u70df\u51b3\u5fc3\u629b\u53bb\u6c42\u8bc1\u4ed9\u9053\u7684\u5927\u4e1a\uff0c\u6295\u5165\u7ea2\u5c18\uff0c\u4e3a\u5929\u4e0b\u9ece\u6c11\uff0c\u4ee5\u5c3d\u5fc3\u529b \u4e86\u3002 \u3000\u3000\u5929\u4e0b\u6cbb\u4e71\u7684\u5927\u6743\uff0c\u63e1\u5728\u4e86\u5e1d\u738b\u7684\u624b\u4e2d\u3002 \u3000\u3000\u5e1d\u738b\u5c45\u4f4f\u5728\u957f\u5b89\u7684\u5bab\u5ef7\u4e2d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8ba1\u5212\u8981\u6295\u5165\u5bab\u5ef7\uff0c\u63a5\u8fd1\u5e1d\u738b\uff0c\u501f\u91cd\u5e1d\u738b\u7684\u6743\u52bf\uff0c\u5e2e\u52a9\u4ed6\u6d88\u9664\u4e71\u6e90\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u8881\u7d2b\u70df\u79bb\u5f00\u4e86\u4fee\u70bc\u5341\u4e94\u5e74\u7684\u7ec8\u5357\u5c71\u3002 \u3000\u3000\u5979\u662f\u4e2a\u5145\u6ee1\u7075\u6167\u7684\u4eba\uff0c\u4e5f\u9047\u4e0a\u6700\u597d\u7684\u5e08\u7236\uff0c\u5341\u4e94\u5e74\u6e05\u51c0\u65e0\u5c18\u7684\u4fee\u70bc\uff0c\u5df2\u5c06\u8fbe\u7aa5\u7834\u7384\u673a\u4e4b \u5883\u3002 \u3000\u3000\u4f46\u4e00\u5ff5\u52a8\u5fc3\uff0c\u5c31\u51b3\u7136\u5f03\u7edd\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5979\u8fd8\u662f\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7f8e\u4e3d\u7edd\u4e16\uff0c\u800c\u53c8\u5bc2\u5bde\u7684\u7ea2\u7c89\u4f73\u4eba\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5927\u4e1a\u56db\u5e74\uff0c\u5929\u4e0b\u7684\u4e71\u8c61\u5df2\u660e\uff0c\u4f46\u968b\u5bab\u7684\u6deb\u4e50\u4f9d\u65e7\u3002 \u3000\u3000\u671d\u6765\u9152\u9192\u6175\u65e0\u529b\uff0c\u591c\u4f34\u7b19\u6b4c\u5230\u5929\u660e\u3002 \u3000\u3000\u5386\u53f2\u4e0a\u6700\u4f1a\u4eab\u53d7\u5973\u8272\u7684\u7687\u5e1d\uff0c\u968b\u7080\u5e1d\u5927\u6982\u662f\u6392\u540d\u7b2c\u4e00\u4e86\u3002 \u3000\u3000\u4ed6\u8ffd\u9010\u65b0\u7231\uff0c\u4e0d\u957f\u671f\u4e13\u5ba0\u4e00\u4eba\uff0c\u904d\u65bd\u96e8\u9732\uff0c\u8ba9\u540e\u5bab\u767e\u82b1\u7ade\u8273\u3002 \u3000\u3000\u968b\u671d\u540e\u5bab\u7f8e\u5973\uff0c\u4e5f\u51a0\u4e8e\u5386\u4ee3\u738b\u671d\uff0c\u771f\u662f\u4e2a\u4eab\u5c3d\u4eba\u95f4\u8273\u798f\u7684\u98ce\u6d41\u7687\u5e1d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5f88\u8f7b\u6613\u7684\u6df7\u5165\u4e86\u968b\u5bab\uff0c\u6362\u4e0a\u4e86\u5ad4\u5973\u7684\u8863\u670d\uff0c\u4e5f\u5e38\u5e38\u51fa\u73b0\u5728\u968b\u7080\u5e1d\u7684\u9762\u524d\uff0c\u62c5\u4efb\u4f3a \u9152\u7684\u5de5\u4f5c\u3002 \u3000\u3000\u4f46\u4e09\u4e2a\u6708\u8fc7\u53bb\u4e86\uff0c\u968b\u7080\u5e1d\u7adf\u6ca1\u6709\u6ce8\u610f\u5230\u5979\u3002 \u3000\u3000\u5979\u662f\u4e2a\u5a07\u8273\u5982\u82b1\u7684\u7f8e\u5973\uff0c\u4f46\u968b\u5bab\u7684\u7f8e\u5973\u592a\u591a\u4e86\uff0c\u6ee1\u56ed\u82b1\u5f00\uff0c\u82b1\u4eba\u773c\uff0c\u4e00\u5ba4\u5e7d\u9999\uff0c\u9999\u4e0d \u95fb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6709\u70b9\u6c14\u5fff\uff0c\u59be\u672c\u4eba\u9762\u59e3\u59e3\u5973\uff0c\u8299\u84c9\u4e3a\u9aa8\u6c34\u4e3a\u795e\uff0c\u600e\u7adf\u672a\u5165\u541b\u738b\u773c\uff0c\u95f2\u7f6e\u4e09\u6708\u4e0d\u76f8 \u95fb\u3002 \u3000\u3000\u4f46\u662f\u5979\u53c8\u62c5\u5fc3\u8fd9\u4e2a\u98ce\u6d41\u7684\u7687\u5e1d\uff0c\u771f\u7684\u627e\u4e0a\u4e86\u5979\uff0c\u8981\u5982\u4f55\u53bb\u6446\u8131\u4ed6\u7684\u7ea0\u7f20\uff1f\u5341\u4e94\u5e74\u4fee\u70bc\u7684 \u7075\u6c14\uff0c\u4e8c\u5341\u5e74\u7389\u6d01\u51b0\u6e05\u7684\u8eab\u5b50\uff0c\u771f\u8981\u88ab\u4ed6\u4eab\u7528\u4e86\u4e0d\u6210\uff1f \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u7518\u5fc3\u4ed8\u51fa\u91cd\u5927\u7684\u727a\u7272\uff0c\u5f00\u59cb\u4ed4\u7ec6\u5730\u89c2\u5bdf\uff0c\u5e0c\u671b\u80fd\u627e\u51fa\u4e00\u4e2a\u65e2\u4fdd\u6e05\u767d\uff0c\u53c8\u9080\u5e1d\u5ba0 \u7684\u529e\u6cd5\u3002 \u3000\u3000\u5979\u4e0d\u6562\u5192\u9669\u4ee5\u672f\u6cd5\u5e72\u72af\u5e1d\u738b\uff0c\u90a3\u4f1a\u4f7f\u540e\u679c\u6613\u751f\u4e0d\u6d4b\u4e4b\u53d8\uff0c\u6bd5\u7adf\u8fd8\u8981\u4ed6\u7edf\u5e05\u4e07\u6c11\uff0c\u6cbb\u7406\u5929 \u4e0b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53ea\u5e0c\u671b\u5f97\u5230\u626b\u5e73\u4e71\u6e90\u7684\u5927\u6743\uff0c\u529b\u633d\u72c2\u6f9c\uff1b\u963b\u6b62\u4e00\u573a\u8840\u6d41\u6210\u6cb3\u7684\u52ab\u96be\u3002 \u3000\u3000\u529f\u6210\u8eab\u9000\uff0c\u8fd8\u6211\u672c\u6765\u9762\u76ee\uff0c\u91cd\u56de\u7ec8\u5357\u5c71\u53bb\uff0c\u8ffd\u6c42\u4ed9\u9053\u5927\u4e1a\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u5979\u5931\u671b\u4e86\u3002 \u3000\u3000\u5979\u53d1\u89c9\u6deb\u4e71\u3001\u591a\u60c5\u7684\u968b\u7080\u5e1d\uff0c\u8fd8\u6709\u70b9\u597d\u5927\u559c\u529f\u7684\u7f3a\u70b9\uff0c\u81ea\u6043\u624d\u60c5\uff0c\u8f7b\u6de1\u671d\u653f\uff0c\u5374\u53c8\u624b\u63e1 \u6743\u67c4\u4e0d\u653e\u3002 \u3000\u3000\u4ed6\u80fd\u8ba9\u5404\u5730\u53f0\u6025\u7684\u594f\u7ae0\uff0c\u79ef\u6848\u76c8\u5c3a\uff0c\u4e0d\u4e88\u6279\u9605\uff0c\u5374\u4e0d\u613f\u51b7\u843d\u4e00\u4e0b\u65b0\u6b22\u3001\u65e7\u7231\u3002 \u3000\u3000\u6218\u529f\u8d6b\u8d6b\u7684\u540d\u5c06\uff0c\u9ad8\u77bb\u8fdc\u77a9\u7684\u80fd\u81e3\uff0c\u53ea\u8981\u7a0d\u5fe4\u5723\u610f\uff0c\u5c31\u4e0b\u65e8\u7acb\u523b\u5904\u6b7b\u3002 \u3000\u3000\u6ee1\u671d\u6587\u6b66\uff0c\u8c01\u518d\u6562\u591a\u8a00\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u5c31\u7b97\u8fd8\u6709\u4e0d\u6015\u6b7b\u7684\u5fe0\u81e3\u3001\u826f\u5c06\uff0c\u62da\u7740\u8840\u6e85\u4e39\u5880\uff0c\u4f46\u4e5f\u65e0\u6cd5\u89c1\u5230\u7687\u5e1d\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5df2\u7ecf\u4e24\u4e2a\u6708\u672a\u4e0a\u671d\u4e86\u3002 \u3000\u3000\u68ee\u4e25\u7684\u5bab\u7981\uff0c\u4f7f\u6c42\u89c1\u7684\u5927\u81e3\uff0c\u5e38\u5728\u5348\u95e8\u5916\u5019\u65e8\u7ec8\u65e5\uff0c\u4e5f\u65e0\u6cd5\u5f97\u5230\u89d0\u89c1\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5462\uff1f \u3000\u3000\u6000\u62b1\u7389\u4eba\uff0c\u4e24\u60c5\u7f31\u7efb\uff0c\u56fd\u5bb6\u5927\u4e8b\uff0c\u4e14\u653e\u4e00\u8fb9\u3002 \u3000\u3000\u5973\u8272\u3001\u7f8e\u9152\uff0c\u5df2\u662f\u968b\u7080\u5e1d\u5168\u90e8\u751f\u6d3b\u7684\u91cd\u5fc3\u3002 \u3000\u3000\u8fd9\u6837\u7684\u7687\u5e1d\uff0c\u5c31\u7b97\u62e5\u6709\u4e86\u80fd\u5f81\u60ef\u6218\u7684\u767e\u4e07\u5927\u519b\uff0c\u8fd0\u7b79\u5e37\u5e44\uff0c\u51b3\u80dc\u5343\u91cc\u7684\u5c06\u519b\uff0c\u7ecf\u7eb6\u6ee1 \u8179\uff0c\u80f8\u6000\u9526\u7ee3\u7684\u6cbb\u4e16\u80fd\u81e3\uff0c\u53c8\u80fd\u5982\u4f55\u5462\uff1f \u3000\u3000\u968b\u7080\u5e1d\u523b\u5fee\u3001\u6740\u622e\u7684\u751f\u6027\uff0c\u5df2\u7ecf\u4f7f\u4ed6\u4eec\u5162\u5162\u4e1a\u4e1a\uff0c\u529b\u6c42\u81ea\u4fdd\uff0c\u552f\u552f\u8bfa\u8bfa\uff0c\u7981\u82e5\u5bd2\u8749\uff0c\u80fd \u81e3\u826f\u5c06\uff0c\u5168\u90fd\u5fc3\u7070\u610f\u51b7\u4e86\u3002 \u3000\u3000\u5978\u3001\u4f5e\u3001\u8c17\u81e3\uff0c\u5374\u6d3b\u8dc3\u4e8e\u5e1d\u738b\u7684\u8eab\u8fb9\uff0c\u4ed6\u4eec\u5bdf\u989c\u89c2\u8272\uff0c\u63e3\u6469\u5e1d\u610f\uff0c\u6781\u5c3d\u963f\u8c00\uff0c\u4ee5\u8ba8\u6b22 \u5fc3\uff0c\u54ea\u7ba1\u5f97\u5929\u4e0b\u7684\u6c34\u6df1\u706b\u70ed\uff0c\u707e\u9ece\u904d\u91ce\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7ec8\u4e8e\u53d1\u89c9\u4e86\uff0c\u4e8b\u5b9e\u548c\u7406\u60f3\uff0c\u7adf\u6709\u7740\u5982\u6b64\u7684\u8ddd\u79bb\uff0c\u4e00\u8154\u62a5\u56fd\u7684\u70ed\u8bda\uff0c\u62ef\u6551\u4e07\u6c11\u7684\u5fc3 \u613f\uff0c\u9762\u5bf9\u7740\u8d25\u56fd\u7684\u5e1d\u738b\uff0c\u5374\u6ca1\u6709\u55a7\u4e4b\u4e8e\u53e3\u7684\u673a\u4f1a\uff0c\u6c14\u9981\u4e86\uff0c\u8881\u7d2b\u70df\u5174\u8d77\u4e86\u4e0d\u5982\u5f52\u53bb\uff0c\u8fd8\u6211\u672c \u6765\u9762\u76ee\u7684\u5ff5\u5934\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53c8\u89c2\u5929\u8c61\uff0c\u4e1c\u5317\u738b\u6c14\u897f\u5357\u6765\uff0c\u5df2\u903c\u8fd1\u957f\u5b89\u6240\u5728\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5927\u5927\u7684\u5403\u60ca\u4e86\uff0c\u600e\u751f\u6765\u5f97\u5982\u6b64\u4e00\u4e2a\u5feb\u6cd5\uff0c\u6211\u65e2\u7136\u5230\u4e86\u957f\u5b89\uff0c\u5c82\u771f\u8981\u7a7a\u5165\u5b9d\u5c71\uff0c\u8896 \u624b\u4e0d\u7ba1\uff1f \u3000\u3000\u96be\u9053\u5df2\u6709\u8eab\u5177\u9053\u6cd5\u7684\u4eba\uff0c\u6e17\u5165\u4e86\u5185\u5bab\uff0c\u6f5c\u4f0f\u5728\u5e1d\u738b\u7684\u8eab\u4fa7\uff0c\u50cf\u6211\u8881\u7d2b\u70df\u4e00\u6837\uff1f \u3000\u3000\u662f\u7684\uff01\u6211\u80fd\u60f3\u5230\u7684\uff0c\u522b\u4eba\u81ea\u662f\u4e5f\u80fd\u60f3\u5230\u3002 \u3000\u3000\u4f46\uff0c\u662f\u8c01\u5462\uff1f \u3000\u3000\u662f\u4ed6\uff0c\u8fd8\u662f\u5979\uff1f \u3000\u3000\u8881\u7d2b\u70df\u6253\u6d88\u4e86\u5f52\u53bb\u7684\u5ff5\u5934\uff0c\u51b3\u5fc3\u8981\u67e5\u4e00\u4e2a\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u968b\u7080\u5e1d\u867d\u7136\u8352\u6deb\u65e0\u9053\uff0c\u4f46\u7edd\u4e0d\u662f\u4e00\u4e2a\u7cca\u6d82\u65e0\u80fd\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u4e2d\u95f4\u5fc5\u7136\u53e6\u6709\u9690\u60c5\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8881\u7d2b\u70df\u5fc3\u7406\u4e0a\u53d7\u5230\u53e6\u4e00\u79cd\u51b2\u51fb\uff0c\u4e5f\u662f\u4f7f\u5979\u7559\u4e0b\u6765\u7684\u4e00\u4e2a\u539f\u56e0\u3002 \u3000\u3000\u957f\u5b89\u7684\u7e41\u534e\u666f\u8272\uff0c\u5185\u5bab\u4e2d\u60ca\u71d5\u6597\u8273\u7684\u7eb7\u6270\uff0c\u65e9\u5df2\u9690\u9690\u5730\u6e17\u5165\u4e86\u5979\u5e73\u9759\u7684\u5185\u5fc3\uff0c\u4e0d\u89c9\u95f4\u751f \u51fa\u4e86\u767e\u82b1\u9f50\u653e\u4e89\u989c\u8272\uff0c\u4e09\u5e72\u4f73\u4e3d\u8c01\u591a\u5a07\u7684\u597d\u80dc\u4e4b\u5ff5\u3002 \u3000\u3000\u6bd5\u7adf\u5979\u53ea\u662f\u4e2a\u4e8c\u5341\u5c81\u7684\u59d1\u5a18\u554a\uff01\u81ea\u8d1f\u624d\u8272\uff0c\u4e0d\u60f6\u591a\u8ba9\uff0c\u5982\u4f55\u80fd\u5fcd\u53d7\u968b\u7080\u5e1d\u5bf9\u5979\u7684\u51b7\u843d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7559\u5fc3\u89c2\u5bdf\u4e4b\u4e0b\uff0c\u53d1\u89c9\u968b\u5bab\u4e2d\u7684\u8b66\u536b\uff0c\u8868\u9762\u4e0a\u8776\u98de\u82b1\u821e\uff0c\u53ea\u89c1\u5bab\u5973\u7a7f\u68ad\u884c\uff0c\u4e0d\u89c1\u6709 \u7981\u536b\u8d70\u52a8\u3002 \u3000\u3000\u4f46\u771f\u6b63\u7684\u6212\u5907\u5374\u662f\u68ee\u4e25\u7684\uff0c\u90a3\u4e9b\u5de1\u884c\u4e8e\u5bab\u5899\u4e4b\u5916\u7684\u6b66\u58eb\uff0c\u4e00\u4e2a\u4e2a\u5e74\u8f7b\u7cbe\u58ee\uff0c\u4e0d\u4f46\u53d7\u8fc7\u4e25 \u683c\u7684\u8bad\u7ec3\uff0c\u800c\u4e14\u90fd\u6709\u7740\u76f8\u5f53\u9020\u8be3\u7684\u6b66\u529f\u3002 \u3000\u3000\u4e00\u4e9b\u7981\u519b\u9886\u73ed\uff0c\u66f4\u4e0d\u4e4f\u6770\u51fa\u7684\u4eba\u7269\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u77e5\u9053\uff0c\u8fd9\u6279\u536b\u5b88\u7687\u5bab\u7684\u7981\u519b\u6709\u591a\u5c11\u4eba\u9a6c\uff1f \u3000\u3000\u4f46\u4ed6\u4eec\u662f\u5929\u4e0b\u6700\u7cbe\u9510\u7684\u519b\u58eb\u3002 \u3000\u3000\u5982\u679c\u620d\u5b88\u957f\u5b89\u7684\u5175\u9a6c\uff0c\u4e5f\u662f\u8fd9\u6837\u7cbe\u826f\u7684\u519b\u58eb\uff0c\u90a3\u771f\u662f\u5929\u4e0b\u65e0\u654c\u7684\u4e00\u652f\u5927\u519b\u3002 \u3000\u3000\u80fd\u8bad\u7ec3\u51fa\u8fd9\u4e48\u4e00\u6279\u7cbe\u826f\u7684\u519b\u9a6c\uff0c\u7edd\u4e0d\u662f\u4e00\u4e2a\u5eb8\u788c\u65e0\u624d\u7684\u7687\u5e1d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5f88\u60f3\u89c1\u7edf\u7387\u8fd9\u6279\u5bab\u536b\u7981\u519b\u7684\u9996\u9886\uff0c\u662f\u4e2a\u4ec0\u4e48\u6837\u4eba\u7269\uff1f \u3000\u3000\u56e0\u4e3a\uff0c\u5165\u5bab\u6765\u4e09\u4e2a\u6708\u4e4b\u4e45\uff0c\u7adf\u7136\u672a\u89c1\u4ed6\u51fa\u73b0\u5728\u7687\u4e0a\u8eab\u4fa7\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u968b\u7080\u5e1d\u5bab\u7981\u68ee\u4e25\uff0c\u672b\u5f97\u5ba3\u53ec\u4e0d\u80fd\u5165\u5bab\u5462\uff1f\u8fd8\u662f\u4ed6\u4e0d\u613f\u64c5\u5165\u8fd9\u9f50\u96c6\u5929\u4e0b\u7f8e\u5973\u7684\u5bab \u5ef7\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5165\u5bab\u4e09\u4e2a\u6708\u4e86\uff0c\u53d1\u89c9\u80fd\u5728\u5bab\u4e2d\u8d70\u52a8\u7684\u7537\u4eba\uff0c\u53ea\u6709\u592a\u76d1\u3001\u4fdd\u62a4\u5bab\u5ef7\u7684\u7981\u536b\u519b\uff0c\u4e5f\u53ea \u80fd\u5b88\u536b\u5728\u5bab\u5899\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u5343\u767e\u7f8e\u5973\u96c6\u5c45\u7684\u540e\u5bab\uff0c\u7adf\u4e5f\u662f\u7537\u4eba\u7684\u7981\u5730\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u6b8b\u5fcd\u7684\u7981\u4ee4\uff0c\u5343\u767e\u7f8e\u5973\uff0c\u53ea\u80fd\u9762\u5bf9\u4e00\u4e2a\u7537\u4eba\uff0c\u8fde\u5192\u6b7b\u5077\u5c1d\u4e00\u4e0b\u7981\u679c\u7684\u673a\u4f1a\uff0c\u4e5f \u88ab\u9632\u8303\u5f97\u70b9\u6ef4\u4e0d\u6f0f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5f00\u59cb\u7559\u5fc3\u5230\u519b\u56fd\u5927\u4e8b\uff0c\u5f88\u5feb\u5730\u53d1\u89c9\u4e86\u6709\u51e0\u9053\u5341\u4e07\u706b\u6025\u7684\u594f\u7ae0\uff0c\u4e5f\u88ab\u5f03\u7f6e\u5728\u5fa1\u4e66\u623f \u7684\u9f99\u6848\u4e0a\u3002 \u3000\u3000\u968b\u7080\u5e1d\u4e24\u6708\u4e0d\u671d\uff0c\u7adf\u7136\u4e5f\u672a\u5230\u5fa1\u4e66\u623f\u53bb\u8fc7\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5077\u5165\u4e86\u5fa1\u4e66\u623f\uff0c\u62c6\u9605\u51e0\u9053\u706b\u6025\u7684\u594f\u7ae0\u3002 \u3000\u3000\u679c\u7136\uff0c\u90fd\u662f\u8bf7\u6c42\u8d48\u6d4e\uff0c\u65f1\u6d9d\u707e\u60c5\uff0c\u63d0\u8c03\u519b\u5175\uff0c\u8fdb\u884c\u629a\u527f\u532a\u4e71\u7684\u7d27\u6025\u5927\u4e8b\u3002 \u3000\u3000\u968b\u7080\u5e1d\u4f3c\u662f\u5b8c\u5168\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u662f\u7684\uff01\u661f\u706b\u5df2\u71c3\uff0c\u71ce\u539f\u7684\u706b\u52bf\uff0c\u5df2\u7ecf\u4e3a\u671f\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5f00\u59cb\u4e86\u591a\u9762\u7684\u89c2\u5bdf\uff0c\u53d1\u89c9\u968b\u7080\u5e1d\u7684\u7cbe\u795e\u4e5f\u6709\u4e9b\u604d\u60da\u4e86\u3002 \u3000\u3000\u7ec3\u6b66\u6709\u6210\uff0c\u7cbe\u4e8e\u4fdd\u517b\u7684\u968b\u7080\u5e1d\uff0c\u867d\u7136\u7eb5\u60c5\u9152\u8272\uff0c\u4f46\u5374\u6709\u7740\u5584\u81ea\u73cd\u6444\u7684\u672c\u9886\uff0c\u600e\u4f1a\u4e00\u4e0b\u5b50 \u57ae\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\uff0c\u8881\u5b9d\u513f\u3002 \u3000\u3000\u90a3\u662f\u592a\u539f\u7559\u5b88\u674e\u6e0a\u664b\u732e\u7687\u4e0a\u7684\u4e00\u4e2a\u7f8e\u5973\uff0c\u4e5f\u662f\u968b\u7080\u5e1d\u8fd1\u65e5\u7684\u65b0\u5ba0\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u5e38\u5e38\u53d1\u751f\uff0c\u672c\u4e0d\u8db3\u602a\uff0c\u5728\u4f17\u5bb6\u592b\u4eba\u7684\u4e89\u5ba0\u4e4b\u4e0b\uff0c\u7adf\u4ee5\u641c\u7f57\u5230\u7684\u7f8e\u5973\u5949\u732e\uff0c\u4e09\u4e94 \u5929\uff0c\u5c31\u4f1a\u6709\u4e00\u4f4d\u65b0\u7f8e\u5973\u5949\u4f8d\u5e1d\u738b\uff0c\u5404\u9662\u592b\u4eba\uff0c\u501f\u65b0\u5ba0\u4ee5\u56fa\u65e7\u7231\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u4f3c\u662f\u6539\u53d8\u4e86\u8fd9\u79cd\u60c5\u52bf\u3002 \u3000\u3000\u539f\u672c\u4e0d\u7559\u5fc3\u968b\u7080\u5e1d\u98ce\u6d41\u97f5\u4e8b\u7684\u8881\u7d2b\u70df\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u6539\u53d8\u4e86\uff0c\u5f00\u59cb\u6697\u4e2d\u67e5\u8bbf\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f88\u5feb\u5730\u53d1\u89c9\u4e86\u4e00\u4e2a\u60ca\u4eba\u7684\u5185\u5e55\u3002 \u3000\u3000\u968b\u7080\u5e1d\u96e8\u9732\u666e\u65bd\u7684\u884c\u4e3a\uff0c\u4f3c\u672a\u6539\u53d8\uff0c\u8f6e\u6d41\u5728\u5404\u9662\u592b\u4eba\u5904\u7559\u5bbf\uff0c\u4f46\u4e94\u66f4\u5929\u4e4b\u524d\uff0c\u5fc5\u4f1a\u79bb \u5f00\uff0c\u6084\u7136\u7684\u8d76\u5f80\u201c\u743c\u82b1\u9662\u201d\u3002 \u3000\u3000\u90a3\u6b63\u662f\u8881\u5b9d\u513f\u4f4f\u7684\u5730\u65b9\uff0c\u4f3c\u662f\u5df2\u5230\u4e86\u975e\u537f\u4e0d\u6b22\u7684\u5883\u754c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8eab\u6000\u65e0\u4e0a\u5947\u672f\u548c\u7edd\u4e16\u6b66\u529f\uff0c\u4f46\u5374\u6ca1\u6709\u5904\u7406\u4e8b\u60c5\u7684\u7ecf\u9a8c\uff01\u5c24\u5176\u662f\u8fd9\u79cd\u7537\u5973\u95f4\u60c5\u7231\u7ea0 \u7f20\u3002 \u3000\u3000\u53d1\u89c9\u4e86\u5371\u673a\u6240\u5728\uff0c\u4f46\u8881\u7d2b\u70df\u4ecd\u7136\u662f\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u968b\u7080\u5e1d\u548c\u8427\u7687\u540e\u996e\u5bb4\u4e8e\u663e\u4ec1\u5bab\u4e2d\uff0c\u8fd9\u672c\u662f\u6bcf\u6708\u4e00\u6b21\u7684\u5b9a\u4f8b\uff0c\u662f\u7687\u4e0a\u548c\u7687\u540e\u5355\u72ec\u76f8 \u5904\u7684\u65f6\u5149\u3002 \u3000\u3000\u968b\u7080\u5e1d\u867d\u7136\u98ce\u6d41\uff0c\u4f46\u5bf9\u8427\u7687\u540e\u4e00\u76f4\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u5c0a\u91cd\uff0c\u8fd9\u4e00\u5929\u7684\u5bb4\u4f1a\uff0c\u5341\u4e8c\u9662\u6b63\u5f0f\u5217\u540d \u7684\u592b\u4eba\uff0c\u90fd\u4e0d\u80fd\u53c2\u52a0\u3002 \u3000\u3000\u4f46\u8427\u7687\u540e\u5374\u8868\u73b0\u4e86\u6cf1\u6cf1\u98ce\u5ea6\uff0c\u9080\u8bf7\u4e86\u5341\u4e8c\u9662\u7684\u592b\u4eba\uff0c\u5168\u90e8\u4e0e\u4f1a\uff0c\u8881\u7d2b\u70df\u662f\u5949\u547d\u4f3a\u9152\u7684\u5973 \u5b98\u3002 \u3000\u3000\u5341\u4e8c\u9662\u592b\u4eba\uff0c\u5404\u5e26\u7740\u4e24\u4e2a\u9662\u4e2d\u6700\u7f8e\u7684\u5bab\u5973\uff0c\u76db\u88c5\u800c\u6765\uff0c\u5e0c\u671b\u4e89\u53d6\u5230\u968b\u7080\u5e1d\u7684\u9752\u7750\uff0c\u591a\u4e00 \u6b21\u7684\u7559\u5bbf\u5ba0\u5e78\u3002 \u3000\u3000\u7f8e\u9152\u3001\u4f73\u80b4\u7684\u6b22\u5bb4\uff0c\u4e5f\u662f\u7ade\u8273\u4e89\u5ba0\u7684\u6597\u4e89\u3002 \u3000\u3000\u8881\u7d2b\u70df\u539f\u5e0c\u671b\u80fd\u501f\u6b64\u673a\u4f1a\uff0c\u4ed4\u7ec6\u89c2\u5bdf\u4e00\u4e0b\u8881\u5b9d\u513f\uff0c\u53ef\u60dc\u8881\u5b9d\u513f\u7adf\u672a\u53d7\u5230\u8427\u7687\u540e\u7684\u9080\u8bf7\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u743c\u82b1\u9662\u7684\u8881\u5b9d\u513f\u8fd8\u672a\u6b63\u5f0f\u88ab\u518c\u5c01\u592b\u4eba\u3002 \u3000\u3000\u5341\u4e8c\u9662\u5404\u5177\u59ff\u8272\u7684\u592b\u4eba\uff0c\u4e8c\u5341\u56db\u4e2a\u7f8e\u4e3d\u7684\u5bab\u5973\uff0c\u518d\u52a0\u4e0a\u98ce\u534e\u6b63\u76db\u7684\u8427\u7687\u540e\uff0c\u548c\u8881\u7d2b\u70df\u7387 \u9886\u7684\u4e00\u6279\u4f3a\u9152\u5973\u5ad4\uff0c\u65e0\u4e00\u4e0d\u662f\u7531\u5404\u5730\u5f81\u9009\u800c\u6765\u7684\u7f8e\u4eba\u3002 \u3000\u3000\u5317\u5730\u80ed\u8102\uff0c\u5357\u56fd\u4f73\u4eba\uff0c\u805a\u96c6\u5728\u4e00\u5802\u4e4b\u4e0a\uff0c\u5168\u90fd\u6253\u8d77\u7cbe\u795e\uff0c\u6446\u51fa\u4e86\u65e0\u9650\u98ce\u60c5\uff0c\u8ba8\u597d\u8fd9\u4e2a\u98ce \u6d41\u7687\u5e1d\uff0c\u770b\u5f97\u4eba\u773c\u90fd\u82b1\u4e86\u3002 \u3000\u3000\u4f46\u968b\u7080\u5e1d\u4f3c\u662f\u6ca1\u6709\u5feb\u4e50\u7684\u611f\u89c9\uff0c\u8427\u7687\u540e\u53d1\u89c9\u4e86\uff0c\u4e5f\u60f3\u8d77\u4e86\u743c\u82b1\u9662\u7684\u8881\u5b9d\u513f\uff0c\u90a3\u4e2a\u8ba8\u4eba\u559c \u7231\u7684\u5c0f\u7f8e\u4eba\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u6697\u5dee\u5973\u5b98\uff0c\u53bb\u8bf7\u8881\u5b9d\u513f\u3002 \u3000\u3000\u679c\u7136\u662f\u5bf9\u75c7\u4e4b\u836f\uff0c\u8881\u5b9d\u513f\u5a07\u4fcf\u7684\u8eab\u5f71\uff0c\u4e00\u51fa\u73b0\u663e\u4ec1\u5bab\u4e2d\uff0c\u968b\u7080\u5e1d\u7acb\u523b\u7b11\u4e86\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684 \u9152\u676f\uff0c\u656c\u5411\u8427\u7687\u540e\u9053\uff1a\u201c\u77e5\u6715\u8005\uff0c\u5fa1\u59bb\u4e5f\uff01\u201d \u3000\u3000\u8427\u540e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b9d\u513f\u53ef\u4eba\uff0c\u59be\u5bf9\u5979\u4e5f\u6709\u7740\u7737\u604b\u96be\u820d\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u8bdd\u867d\u8bf4\u5f97\u5a49\u8f6c\uff0c\u4f46\u5979\u5fc3\u4e2d\u5374\u6709\u4e0d\u5b89\u7684\u611f\u89c9\uff0c\u968b\u7080\u5e1d\u4f3c\u5df2\u6709\u6e10\u96c6\u4e8e\u8881\u5b9d\u513f\u4e00\u8eab\u7684\u503e\u5411\u3002 \u3000\u3000\u5341\u4e8c\u9662\u592b\u4eba\uff0c\u4e5f\u90fd\u6709\u7740\u5371\u673a\u4e34\u5934\u4e4b\u611f\uff0c\u5bf9\u5e1d\u738b\uff0c\u5979\u4eec\u65e9\u5df2\u6709\u975e\u6211\u72ec\u5c5e\u7684\u8ba4\u8bc6\uff0c\u53ea\u6c42\u80fd\u5e38 \u6cbe\u96e8\u9732\uff0c\u4fdd\u4f4f\u4e00\u9662\u592b\u4eba\u4e4b\u4f4d\uff0c\u4f46\u5982\u4e09\u5343\u5ba0\u7231\u96c6\u4e00\u8eab\uff0c\u90a3\u5c31\u95e8\u524d\u51b7\u843d\u8f66\u9a6c\u7a00\uff0c\u4ece\u6b64\u96be\u89c1\u541b\u738b\u9762 \u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u867d\u6709\u65e0\u9650\u5fcc\u5992\uff0c\u4f46\u4ecd\u7136\u5f97\u88c5\u51fa\u4e00\u8138\u7b11\u610f\u3002 \u3000\u3000\u8881\u5b9d\u513f\u7f13\u6b65\u884c\u6765\uff0c\u53ea\u5bf9\u968b\u7080\u5e1d\u5fae\u5fae\u4e00\u7b11\uff0c\u5374\u8f6c\u5bf9\u8427\u7687\u540e\u884c\u4e86\u8fc7\u53bb\uff0c\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u59be \u5a62\u53e9\u89c1\u5a18\u5a18\u5e72\u5c81\uff01\u201d \u3000\u3000\u8427\u7687\u540e\u4e00\u628a\u633d\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u591a\u793c\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u76ee\u5149\u56db\u987e\uff0c\u798f\u4e86\u4e00\u798f\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u4f17\u5bb6\u59d0\u59d0\uff0c\u5b9d\u513f\u5c11\u4e0d\u66f4\u4e8b\uff0c\u6709\u4ec0\u4e48\u5931\u793c\u4e4b\u5904\uff0c \u8fd8\u671b\u8bf8\u4f4d\u59d0\u59d0\u6307\u6559\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u8bf8\u4f4d\u592b\u4eba\u3001\u5983\u5b50\uff0c\u90fd\u662f\u6715\u7684\u7231\u537f\uff0c\u6715\u4e4b\u6240\u7231\uff0c\u5373\u4f17\u537f\u6240\u7231 \u4e5f\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u5f97\u5f88\u660e\u767d\u4e86\uff0c\u518d\u4e0d\u77e5\u8da3\uff0c\u90a3\u5c31\u662f\u81ea\u8ba8\u82e6\u5403\u4e86\u3002 \u3000\u3000\u5341\u4e8c\u9662\u592b\u4eba\u5168\u90fd\u7ad9\u8d77\u6765\uff0c\u4e3e\u8d77\u9152\u676f\uff0c\u5411\u7740\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5b9d\u59b9\u59b9\u5929\u8d44\u56fd\u8272\uff0c\u6211\u89c1\u72b9\u601c\u3002 \u6765\uff01\u6211\u4eec\u5927\u5bb6\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u6025\u6025\u628a\u9762\u524d\u7684\u9152\u676f\uff0c\u9012\u7ed9\u8881\u5b9d\u513f\u3002 \u3000\u3000\u51b7\u773c\u65c1\u89c2\u7684\u8881\u7d2b\u70df\uff0c\u53ea\u770b\u5f97\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u4eec\u90fd\u662f\u51a0\u7edd\u7fa4\u82b3\u7684\u7f8e\u5973\uff0c\u624d\u8272\u517c \u5907\uff0c\u5374\u5168\u6d3b\u5728\u4e00\u4e2a\u7537\u4eba\u9634\u5f71\u4e4b\u4e0b\uff0c\u65e2\u53ef\u60b2\uff0c\u53c8\u53ef\u601c\u3002\u201d\u968b\u7080\u5e1d\u4e0d\u5f85\u8881\u5b9d\u513f\u559d\u5e72\u676f\u4e2d\u4e4b\u9152\uff0c\u5df2 \u628a\u8881\u5b9d\u513f\u62b1\u5165\u6000\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u5b9d\u513f\u5e74\u5e7c\uff0c\u4e0d\u80fd\u559d\uff0c\u6715\u4ee3\u5979\u559d\u4e86\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u53d6\u8fc7\u5b9d\u513f\u624b\u4e2d\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u7231\u604b\u4e4b\u6df1\uff0c\u5df2\u96be\u81ea\u5df2\u3002 \u3000\u3000\u8881\u7d2b\u70df\u51dd\u795e\u671b\u53bb\uff0c\u53d1\u89c9\u4e86\u8fd9\u4e2a\u4ec5\u4ec5\u5341\u4e94\u7684\u5c0f\u4f73\u4eba\uff0c\u5168\u8eab\u7684\u808c\u80a4\uff0c\u90fd\u6cdb\u7740\u6843\u82b1\u4e00\u822c\u7684\u989c \u8272\uff0c\u8138\u4e0a\u5e26\u7740\u5a07\u7a1a\u7684\u5fae\u7b11\uff0c\u5145\u6ee1\u7740\u5f39\u6027\u7684\u808c\u8089\uff0c\u6563\u53d1\u51fa\u4e00\u79cd\u9189\u4eba\u5982\u9152\u7684\u8bf1\u60d1\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5fc3 \u52a8\u3002 \u3000\u3000\u4e16\u4e0a\u600e\u4f1a\u6709\u5982\u6b64\u5b8c\u7f8e\u7684\u5c24\u7269\uff1f\u96c6\u5a07\u3001\u5a9a\u3001\u8272\u3001\u5996\u4e8e\u4e00\u8eab\u3002 \u3000\u3000\u52ff\u602a\u968b\u7080\u5e1d\u5bf9\u5979\u8ff7\u604b\u65e5\u6df1\u3002 \u3000\u3000\u5979\u8fdb\u5bab\u4e00\u4e2a\u6708\uff0c\u968b\u7080\u5e1d\u8fde\u8bbe\u5728\u5185\u5bab\u7684\u5fa1\u4e66\u623f\uff0c\u4e5f\u672a\u53bb\u4e00\u6b21\uff0c\u80fd\u62bd\u51fa\u7684\u65f6\u95f4\uff0c\u90fd\u548c\u5979\u817b\u5728 \u4e00\u5757\u513f\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u8f7b\u8f7b\u62c9\u5f00\u4e86\u968b\u7080\u5e1d\u7d27\u62b1\u67f3\u8170\u7684\u53cc\u624b\uff0c\u5e26\u7740\u4e09\u5206\u5a07\u7f9e\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u5ba0\u574f\u5b9d\u513f \u4e86\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u7b11\u9053\uff1a\u201c\u6715\u5386\u7ecf\u5929\u4e0b\u989c\u8272\uff0c\u8bba\u98ce\u534e\u5fa1\u59bb\u7b2c\u4e00\uff0c\u5341\u4e8c\u9662\u592b\u4eba\uff0c\u662f\u6625\u5170\u3001\u79cb\u83ca\u6781\u5176 \u7f8e\uff0c\u5983\u5b50\u3001\u624d\u4eba\uff0c\u4e2a\u4e2a\u591a\u5a07\uff0c\u4f46\u5982\u8bba\u5a07\u5a9a\u3001\u53ef\u4eba\uff0c\u5c31\u975e\u5b9d\u513f\u83ab\u5c5e\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6697\u9053\uff1a\u201c\u6211\u54ea\u91cc\u4e0d\u5982\u522b\u4eba\u4e86\uff1f\u54fc\uff01\u771f\u662f\u6709\u773c\u4e0d\u8bc6\u91d1\u9576\u7389\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u56fd\u4e8b\u4e3a\u91cd\u554a\uff01\u201d\u8881\u5b9d\u513f\u5a07\u751c\u7684\u58f0\u97f3\uff0c\u5e7d\u5e7d\u52a8\u4eba\u7684\u8bf4\u9053\uff1a\u201c\u7687\u540e\uff0c\u59be\u5a62\uff0c\u548c\u4f17\u5bb6 \u592b\u4eba\u59d0\u59d0\uff0c\u90fd\u5e0c\u671b\u56fd\u57fa\u6c38\u56fa\uff0c\u7687\u4e0a\u4e07\u5bff\u65e0\u7586\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u6c89\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e16\u65e0\u767e\u5c81\u82f1\u96c4\uff0c\u4e07\u5bff\u5343\u5e74\u7684\u4ed9\u9053\u4e4b\u4e3e\uff0c\u6715\u4e5f\u4e0d\u53bb\u60f3\u5b83\uff0c\u80fd\u5f97\u4f17 \u537f\u5bb6\u8fd9\u7b49\u7edd\u8272\u7f8e\u4eba\uff0c\u4f7f\u6715\u6d3b\u5f97\u5341\u5206\u5feb\u6d3b\uff0c\u519b\u56fd\u5927\u4e8b\uff0c\u81ea\u6709\u767e\u5b98\u4e3a\u6715\u5206\u52b3\uff0c\u6715\u5982\u4e8b\u5fc5\u4eb2\u606d\uff0c\u5c82 \u4e0d\u8981\u51b7\u843d\u4e86\u4f17\u4f4d\u537f\u5bb6\u3002\u201d \u3000\u3000\u771f\u662f\u4e2a\u4e0d\u7231\u6c5f\u5c71\u7231\u7f8e\u4eba\u7684\u4ea1\u56fd\u4e4b\u541b\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5ffd\u7136\u7b11\u4e86\uff0c\u7b11\u7684\u767e\u5a9a\u6a2a\u751f\uff0c\u7f57\u8896\u6325\u52a8\uff0c\u4f38\u51fa\u4e86\u7ea4\u7ea4\u7389\u6307\uff0c\u63e1\u4f4f\u4e86\u968b\u7080\u5e1d\u7684\u53f3\u624b\uff0c \u4e00\u80a1\u6d6e\u52a8\u7684\u9999\u6c14\uff0c\u6251\u5411\u4e86\u968b\u7080\u5e1d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e5f\u95fb\u5230\u4e86\uff0c\u7adf\u4e5f\u5fc3\u795e\u4e00\u8361\u3002 \u3000\u3000\u5979\u4fee\u4e3a\u6df1\u539a\uff0c\u7acb\u523b\u611f\u89c9\u5230\u8fd9\u79cd\u9999\u5473\uff0c\u4e0d\u662f\u5973\u4eba\u8eab\u4e0a\u7684\u4f53\u9999\uff0c\u800c\u662f\u4e00\u79cd\u7cbe\u5fc3\u8c03\u914d\u7684\u9999\uff0c\u5f15 \u4eba\u6b32\u5ff5\uff0c\u52fe\u4eba\u9b42\u9b44\u3002 \u3000\u3000\u679c\u7136\uff0c\u968b\u7080\u5e1d\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4e0d\u7ba1\u7687\u540e\u5728\u4fa7\uff0c\u5ad4\u5983\u73af\u4f3a\uff0c\u7adf\u628a\u8881\u5b9d\u513f\u62e5\u5165\u6000\u4e2d\uff0c\u7ad9\u8d77\u4e86\u8eab \u5b50\uff0c\u5927\u6709\u8feb\u4e0d\u53ca\u5f85\u4e4b\u611f\u3002 \u3000\u3000\u597d\u9738\u9053\u7684\u9999\u6c14\uff01 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u751f\u8b66\u60d5\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u8fd0\u6c14\u6392\u51fa\u4e86\u5438\u5165\u80ba\u8151\u7684\u9999\u6c14\uff0c\u795e\u5fd7\u987f\u590d\u6e05\u660e\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u8427\u540e\u548c\u5341\u4e8c\u592b\u4eba\uff0c\u4e2a\u4e2a\u7ea2\u6655\u53cc\u988a\uff0c\u75f4\u75f4\u5730\u671b\u7740\u8881\u5b9d\u513f\u3002 \u3000\u3000\u5973\u4eba\u5c1a\u4e14\u5982\u6b64\uff0c\u968b\u7080\u5e1d\u5c31\u4e0d\u80fd\u770b\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u8eab\u7740\u7ea2\u888d\uff0c\u624b\u4e2d\u6367\u7740\u594f\u7ae0\u7684\u5e74\u8f7b\u5b98\u5458\uff0c\u51b2\u5f00\u62e6\u963b\u7684\u592a\u76d1\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002\u4e00 \u819d\u8dea\u5730\uff0c\u9053\uff1a\u201c\u5175\u90e8\u4f8d\u90ce\u517c\u5bab\u536b\u7edf\u9886\uff0c\u5b87\u6587\u6210\u90fd\uff0c\u53e9\u89c1\u7687\u4e0a\u3002\u201d \u3000\u3000\u597d\u5927\u7684\u8138\u5b50\uff01\u968b\u7080\u5e1d\u5927\u611f\u4e0d\u60a6\u3002\u9053\uff1a\u201c\u6043\u5ba0\u800c\u9a84\uff0c\u64c5\u95ef\u540e\u5bab\uff0c\u4e0d\u6015\u6715\u6740\u4e86\u4f60\u4e48\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\u6055\u7f6a\uff0c\u6210\u90fd\u5192\u6b7b\u60ca\u9a7e\uff0c\u5b9e\u56fa\u56fd\u4e8b\u7d27\u6025\uff0c\u4e0d\u6562\u5ef6\u8bef\uff0c\u4f0f\u4e5e\u543e\u7687\u5782\u5bdf\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u8eab\u4e0a\u6563\u53d1\u51fa\u7684\u5947\u9999\uff0c\u914d\u5408\u5979\u5a07\u5a9a\u4e4b\u6001\uff0c\u52fe\u8d77\u968b\u7080\u5e1d\u7684\u6b32\u706b\uff0c\u5df2\u71c3\u70e7\u7ea2\u4e86\u4ed6\u7684\u53cc \u988a\uff0c\u54ea\u91cc\u8fd8\u6709\u5fc3\u542c\u95fb\u56fd\u4e8b\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\u201c\u6709\u4e8b\u660e\u65e5\u65e9\u671d\u518d\u594f\uff01\u201d \u3000\u3000\u62b1\u7740\u8881\u5b9d\u513f\uff0c\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u201c\u7687\u4e0a\uff0c\u5df2\u4e09\u4e2a\u6708\u672a\u4e0a\u671d\u4e86\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u7f6e\u82e5\u7f54\u95fb\uff0c\u5934\u4e5f\u672a\u56de\u4e00\u4e0b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4e0b\u4e86\uff0c\u7f57\u8896\u8f7b\u62c2\uff0c\u51b7\u98ce\u56db\u8d77\uff0c\u626b\u8fc7\u4e86\u8427\u540e\u548c\u5341\u4e8c\u592b\u4eba\uff0c \u4e5f\u626b\u8fc7\u4e86\u968b\u7080\u5e1d\u3002 \u3000\u3000\u8427\u7687\u540e\u548c\u4f17\u592b\u4eba\u5c3d\u590d\u5e38\u6001\uff0c\u4e5f\u9010\u8d70\u4e86\u968b\u7080\u5e1d\u711a\u8eab\u6b32\u706b\uff0c\u4e00\u4e0b\u5b50\u51b7\u9759\u4e0b\u6765\uff0c\u653e\u5f00\u4e86\u8881\u5b9d \u513f\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6210\u90fd\uff01\u201d \u3000\u3000\u201c\u81e3\u5728\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u6709\u7d27\u6025\u56fd\u4e8b\uff0c\u662f\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d\u968b\u7080\u5e1d\u4f3c\u662f\u7531\u8ff7\u9189\u4e2d\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u76f4\u7559\u5fc3\u7740\u8881\u5b9d\u513f\uff0c\u770b\u5230\u5979\u8138\u4e0a\u95ea\u63a0\u8fc7\u4e00\u62b9\u8bb6\u5f02\u7684\u795e\u8272\uff0c\u4f46\u5f88\u5feb\u6062\u590d\u5e73\u9759\uff0c\u5996\u5a9a \u4e4b\u6001\uff0c\u4ea6\u7136\u6d88\u5931\u3002 \u3000\u3000\u4e00\u4e2a\u6e05\u96c5\u5e84\u91cd\u7684\u8881\u5b9d\u513f\uff0c\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u8fd9\u624d\u662f\u771f\u6b63\u7684\u8881\u5b9d\u513f\uff0c\u4e00\u4e2a\u6e05\u4e3d\u7edd\u4fd7\u7684\u5c0f\u4f73\u4eba\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e2a\u7f8e\u4e3d\u7684\u5c0f\u5c24\u7269\u554a\uff01\u80fd\u5982\u6b64\u63a7\u5236\u795e\u6001\u3001\u8868\u60c5\uff0c\u77ac\u606f\u95f4\u5343\u53d8 \u4e07\u5316\uff0c\u672f\u6cd5\u4e4b\u7cbe\uff0c\u5b9e\u975e\u7b49\u95f2\u3002\u201d \u3000\u3000\u5979\uff01\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u5462\uff1f \u3000\u3000\u201c\u7687\u4e0a\uff0c\u4e5d\u6c5f\u3001\u664b\u6c5f\u3001\u9a6c\u9091\u3001\u66f9\u5dde\u3001\u6cb3\u5357\u3001\u5357\u9633\uff0c\u4e71\u6c11\u9020\u53cd\u4e0d\u4e0b\u6570\u5341\u4e07\uff0c\u5404\u5730\u5dde\u5e9c\uff0c\u591a \u88ab\u653b\u9677\u3002\u201d\u5b87\u6587\u6210\u90fd\u4f83\u4f83\u800c\u8c08\uff0c\u9053\uff1a\u201c\u70fd\u706b\u4e4b\u4e0b\uff0c\u6c11\u4e0d\u804a\u751f\uff0c\u518d\u4e0d\u63d0\u8c03\u5927\u519b\uff0c\u80fd\u5c06\u629a\u527f\uff0c\u6050 \u5c06\u91ce\u706b\u71ce\u539f\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u6715\u8bbe\u767e\u5b98\uff0c\u4efb\u5dde\u5e9c\uff0c\u5404\u6709\u4e13\u53f8\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u4e3a\u6715\u5206\u5fe7\uff0c\u65e9\u65e5\u5f2d\u5e73\u4e71\u6e90 \u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u2026\u201d\u6210\u90fd\u4e0d\u6562\u8bf4\u3002 \u3000\u3000\u201c\u8bf4\u5427\uff01\u6715\u6055\u4f60\u65e0\u7f6a\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\u5929\u5a01\uff0c\u8c03\u52a8\u5175\u9a6c\uff0c\u52a8\u652f\u5e93\u94f6\u7684\u6743\u529b\uff0c\u5168\u63e1\u4e8e\u7687\u4e0a\u4e4b\u624b\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6ca1\u6709\u7687 \u4e0a\u7684\u8c15\u65e8\uff0c\u767e\u5b98\u90fd\u65e0\u80fd\u4e3a\u529b\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u4f3c\u4e4e\u662f\u5f88\u6ee1\u610f\u541b\u6743\u7684\u5a01\u671b\uff0c\u4e00\u624b\u63e1\u5929\u4e0b\u7684\u6cbb\u4e71\uff0c\u4e07\u6c11\u7684\u751f\u6b7b\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982 \u6b64\u5927\u4e8b\uff0c\u600e\u4e0d\u65e9\u4e00\u4e9b\u5448\u4e0a\u594f\u7ae0\u5462\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u5341\u4e8c\u9053\u7d27\u6025\u594f\u7ae0\uff0c\u90fd\u79ef\u538b\u5728\u5fa1\u4e66\u623f\u9f99\u6848\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u201d\u968b\u7080\u5e1d\u60f3\u5230\u79ef\u538b\u5230\u9ad8\u8fbe\u6570\u5c3a\u7684\u5448\u9605\u594f\u7ae0\uff0c\u4e0d\u7981\u751f\u51fa\u70e6\u607c\uff0c\u5fae\u5fae\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a \u201c\u516d\u90e8\u516c\u537f\u3001\u8c0f\u8bae\u5927\u592b\uff0c\u600e\u4e0d\u9762\u594f\u4e8e\u6715\u5462\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\u5df2\u4e09\u4e2a\u6708\u672a\u671d\uff01\u201d \u3000\u3000\u201c\u6715\u6709\u4e09\u4e2a\u6708\u672a\u4e0a\u671d\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5c31\u662f\u8bbe\u5728\u5bab\u5185\u7684\u5fa1\u4e66\u623f\uff0c\u7687\u4e0a\u4e5f\u6709\u4e00\u4e2a\u6708\u672a\u53bb\u8fc7\u4e86\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u610f\u6c14\u6602\u626c\u5730\u8bf4\uff1a\u201c\u516c\u537f\u5927\u4eba\uff0c\u6c42\u89c1\u9762\u5723\u8005\uff0c\u4e0d\u4e0b\u5341\u4f59\u4eba\uff0c\u53ea\u662f\u5bab\u7981\u68ee\u4e25\uff0c\u672a\u5f97 \u5ba3\u53ec\uff0c\u4e0d\u80fd\u5165\u89d0\uff0c\u9e44\u5019\u7adf\u65e5\uff0c\u81e3\u5f97\u7687\u4e0a\u6069\u8d50\uff0c\u517c\u4efb\u5bab\u536b\u7edf\u9886\uff0c\u5f97\u6709\u51fa\u5165\u5bab\u95e8\u4e4b\u4fbf\uff0c\u624d\u6709\u5192\u6b7b \u9762\u5723\u7684\u673a\u4f1a\uff0c\u803f\u803f\u6b64\u5fc3\uff0c\u8bf7\u6055\u81e3\u72af\u9a7e\u76f4\u8a00\u4e4b\u7f6a\uff01\u201d \u3000\u3000\u201c\u6210\u90fd\uff0c\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u8c22\u4e3b\u9686\u6069\uff01\u201d\u5b87\u6587\u6210\u90fd\u7f13\u7f13\u7ad9\u8d77\uff0c\u5782\u9996\u800c\u7acb\u3002 \u3000\u3000\u968b\u7080\u5e1d\u4ef0\u671b\u7740\u4e07\u91cc\u65e0\u4e91\u7684\u78a7\u7a7a\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u5c71\u5982\u753b\uff0c\u540e\u5bab\u591a\u5a07\uff0c\u6210\u90fd\uff0c\u53ea\u6015\u6715 \u5f88\u96be\u5f97\u5230\u8fd9\u4e24\u5168\u5176\u7f8e\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5927\u968b\u671d\u5e1d\u57fa\u7262\u56fa\uff0c\u80fd\u5c06\u4f17\u591a\uff0c\u53ea\u8981\u7687\u4e0a\u4e00\u9053\u8c15\u65e8\u9881\u4e0b\uff0c\u5927\u519b\u51fa\u52a8\uff0c\u4e0d\u96be \u4e00\u9f13\u626b\u5e73\u516d\u8def\u5c18\u70df\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u597d\uff01\u6210\u90fd\u3002\u201c\u968b\u7080\u5e1d\u62cd\u7740\u5b87\u6587\u6210\u90fd\u7684\u80a9\u8180\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7267\u573a\u593a\u9b41\uff0c\u4e00\u65e5\u95f4\u8fde\u8d25\u4e86 \u4e03\u5341\u4e8c\u4f4d\u597d\u6c49\uff0c\u6715\u6709\u4f60\u8fd9\u6837\u4e00\u5458\u864e\u5c06\uff0c\u4f55\u6101\u516d\u8def\u5c18\u70df\u4e0d\u5e73\uff1f\u201d \u3000\u3000\u5c31\u7531\u4f60\u7edf\u5e05\u5927\u519b\uff0c\u626b\u5e73\u4e71\u6e90\uff0c\u6715\u5c01\u4f60\u5175\u9a6c\u5927\u5143\u5e05\uff0c\u8282\u5236\u5929\u4e0b\u5175\u9a6c\uff0c\u514b\u65e5\u51fa\u5e08\uff0c\u5f97\u80dc\u5f52 \u6765\uff0c\u5c31\u5728\u8fd9\u663e\u4ec1\u5bab\u4e2d\uff0c\u8bbe\u7b75\u4e3a\u4f60\u63a5\u98ce\uff01\u6715\u5c06\u5c3d\u51fa\u5bab\u4e2d\u7f8e\u5973\uff0c\u4e3a\u4f60\u795d\u8d3a\u3002\u201d \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u5584\u4e8e\u7528\u4eba\u7684\u7687\u5e1d\uff0c\u4f60\u80fd\u8bf4\u4ed6\u660f\u5eb8\u5417\uff1f \u3000\u3000\u201c\u81e3\u9075\u65e8\u2026\u2026\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u5927\u624d\u5c0f\u7528\u4e86\uff01\u201d\u8881\u7d2b\u70df\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u771f\u6b63\u7684\u5371\u673a\u5728\u4eac\u5e08\uff0c\u628a\u5b87\u6587\u5c06\u519b\u8fd9 \u6837\u7684\u52c7\u6b66\u4e4b\u58eb\uff0c\u9063\u5dee\u4e8e\u5916\uff0c\u4f7f\u7981\u5bab\u7a7a\u865a\uff0c\u5982\u4f55\u80fd\u5bf9\u4ed8\u4fb5\u72af\u7687\u5bab\u7684\u5f3a\u654c\uff0c\u4fdd\u62a4\u7687\u4e0a\u7684\u5b89\u5168 \u5462\uff1f\u201d \u3000\u3000\u5979\u5df2\u7ecf\u5fcd\u8010\u592a\u4e45\uff0c\u5929\u8c61\u7684\u6025\u901f\u53d8\u5316\uff0c\u8881\u5b9d\u513f\u7684\u60ca\u4eba\u672f\u6cd5\uff0c\u5df2\u663e\u793a\u51fa\u5371\u673a\u8feb\u5728\u7709\u776b\u3002 \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u201d\u968b\u7080\u5e1d\u6709\u70b9\u8bb6\u5f02\uff0c\u5fff\u6012\u5730\u8bf4\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u72af\u7687\u5bab\uff0c\u5371\u8a00\u8038\u542c\uff0c\u60d1\u4e71\u4eba\u5fc3\uff0c\u60ca \u5413\u4e86\u6715\u7684\u4f17\u5bb6\u7f8e\u4eba\uff0c\u6740\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u8881\u7d2b\u70df\u7684\u8eab\u4e0a\uff0c\u987f\u7136\u4f4f\u53e3\uff0c\u6740\u65e0\u8d66\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7684\u7f8e\u4e3d\uff0c\u662f\u90a3\u79cd\u98d8\u7136\u51fa\u5c18\uff0c\u7075\u6c14\u903c\u4eba\u4e4b\u7f8e\uff0c\u5177\u6709\u4ed9\u5973\u4e4b\u59ff\uff0c\u5929\u4eba\u4e4b\u95f4\u7684\u96c5\u4e3d\u3002 \u3000\u3000\u968b\u5bab\u591a\u5a07\uff0c\u5c3d\u6536\u5929\u4e0b\u989c\u8272\uff0c\u4f46\u968b\u7080\u5e1d\u5374\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u4e0e\u4f17\u4e0d\u540c\u7684\u7f8e\u4e3d\u3002 \u3000\u3000\u201c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u5ad4\u5973\u8881\u7d2b\u70df\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u5165\u5bab\u591a\u4e45\u4e86\uff0c\u6715\u600e\u4e48\u4ece\u672a\u89c1\u8fc7\u2026\u2026\u201d\u968b\u7080\u5e1d\u5b8c\u5168\u5fd8\u53bb\u4e86\u8ffd\u95ee\u5371\u76f2\u8038\u542c\u7684\u4e8b\uff0c\u5168\u90e8 \u7cbe\u795e\u90fd\u7528\u5728\u51dd\u6ce8\u8881\u7d2b\u70df\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4e00\u5411\u5728\u54ea\u5ea7\u5bab\u9662\u5f53\u5dee\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u7684\u56db\u5468\uff0c\u4f3c\u662f\u6d6e\u52a8\u7740\u4e00\u5c42\u4e91\u6c14\uff0c\u968b\u7080\u5e1d\u5f88\u60f3\u770b\u4e2a\u6e05\u695a\uff0c\u5374\u662f\u603b\u96be\u5982\u613f\uff0c\u6709\u5982\u770b\u96fe \u4e2d\u4e4b\u82b1\u3002 \u3000\u3000\u6726\u80e7\u4e2d\u7684\u7f8e\u4e3d\uff0c\u7ed9\u4eba\u4e00\u79cd\u65e0\u6cd5\u6349\u6478\u7684\u611f\u89c9\u3002 \u3000\u3000\u968b\u7080\u5e1d\u4e24\u5ea6\u62ac\u8d77\u624b\u6765\uff0c\u63c9\u6413\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u201c\u5165\u5bab\u4e09\u4e2a\u6708\u4e86\u3002\u201d\u8881\u7d2b\u70df\u6de1\u6de1\u7684\u7b11\u9053\uff1a\u201c\u4e00\u5411\u5949\u6d3e\u5728\u7687\u4e0a\u8eab\u4fa7\u4f3a\u9152\u3002\u201d \u3000\u3000\u201c\u6715\u4e4b\u8fc7\u4e5f\uff0c\u6715\u4e4b\u8fc7\u4e5f\uff01\u201d\u968b\u7080\u5e1d\u7528\u529b\u7684\u62cd\u6253\u7740\u8111\u888b\uff0c\u7528\u81ea\u8650\u8868\u8fbe\u51fa\u5185\u5fc3\u4e2d\u7684\u61ca\u607c\u3002 \u3000\u3000\u6bcf\u65e5\u7684\u8ffd\u9010\u6253\u96c1\uff0c\u7ade\u4e0d\u77e5\u5929\u9e45\u5728\u4fa7\u3002 \u3000\u3000\u8fd9\u4f3c\u4e4e\u662f\u6bd4\u4ed6\u542c\u5230\u516d\u5904\u532a\u4e71\u7684\u62a5\u544a\uff0c\u66f4\u4ee4\u4ed6\u4e0d\u80fd\u539f\u8c05\u81ea\u5df1\u7684\u660f\u5eb8\uff0c\u5145\u5206\u8868\u73b0\u51fa\u4ed6\u8ffd\u9010\u7f8e \u8272\u7684\u6267\u7740\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5df2\u770b\u51fa\u968b\u7080\u5e1d\u88ab\u8881\u7d2b\u70df\u53e6\u4e00\u79cd\u6e05\u96c5\u4e4b\u7f8e\u6240\u5438\u5f15\uff0c\u5fc3\u4e2d\u751a\u611f\u5403\u60ca\uff0c\u5165\u5bab\u4e00\u6708\uff0c\u7adf\u672a \u80fd\u53d1\u89c9\u540e\u5bab\u4e4b\u4e2d\uff0c\u6709\u8fd9\u4e48\u4e00\u4f4d\u7075\u6167\u7edd\u4e16\u7684\u9ad8\u4eba\uff01 \u3000\u3000\u5979\u5b9e\u5728\u592a\u5927\u610f\u4e86\uff01 \u3000\u3000\u539f\u672c\u8981\u8bf1\u4f7f\u968b\u7080\u5e1d\u8868\u73b0\u51fa\u6c47\u96c6\u5ba0\u7231\u4e8e\u4e00\u8eab\uff0c\u8ba9\u7687\u540e\u548c\u5341\u4e8c\u9662\u592b\u4eba\uff0c\u5fc3\u751f\u5992\u6068\u7684\u76ee\u7684\uff0c\u5df2 \u96be\u8fbe\u6210\uff0c\u8fd9\u4e00\u4ed7\u8d25\u5f97\u5f88\u60e8\uff01 \u3000\u3000\u201c\u7687\u4e0a\u3002\u201d\u8881\u5b9d\u513f\u5fae\u7b11\u5982\u82b1\u7684\u9053\uff1a\u201c\u59be\u5a62\u544a\u9000\u4e86\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u597d\u3002\u5b9d\u5983\u679c\u7136\u53ef\u4eba\u3002\u201d\u968b\u7080\u5e1d\u7b11\u9053\uff1a\u201c\u6715\u91cd\u91cd\u6709\u8d4f\u3002\u201d \u3000\u3000\u6446\u660e\u4e86\u4e00\u526f\u559c\u89c1\u65b0\u4eba\uff0c\u5fd8\u65e7\u4eba\u7684\u59ff\u6001\u3002 \u3000\u3000\u201c\u591a\u8c22\u7687\u4e0a\u3002\u201d\u8881\u5b9d\u513f\u5a07\u7b11\u4f9d\u7136\uff0c\u770b\u4e0d\u51fa\u4e00\u70b9\u5992\u5fcc\u7684\u795e\u8272\uff0c\u63a5\u9053\uff1a\u201c\u7d2b\u70df\u59d0\u59d0\uff0c\u597d\u597d\u6c60 \u7167\u987e\u7687\u4e0a\u554a\uff01\u201d \u3000\u3000\u67d4\u67d4\u7ec6\u8bed\u4e2d\uff0c\u5e26\u7740\u4e00\u9635\u9999\u6c14\uff0c\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u201c\u59be\u5a62\u606d\u9001\u5b9d\u5983\u5a18\u5a18\u3002\u201d\u8881\u7d2b\u70df\u7f13\u7f13\u5411\u4e0b\u8dea\u53bb\uff0c\u5fc3\u4e2d\u4e5f\u5341\u5206\u9707\u60ca\u8881\u5b9d\u513f\u7684\u5e94\u53d8\u80fd\u529b\u3002 \u3000\u3000\u4e0d\u8bba\u8881\u7d2b\u70df\u662f\u5426\u613f\u610f\uff1f\u4f46\u8881\u5b9d\u513f\u5df2\u662f\u4f4f\u5165\u201c\u743c\u82b1\u9662\u201d\u7684\u5983\u5b50\u8eab\u4efd\u3002 \u3000\u3000\u4e00\u53ea\u624b\u4f38\u4e86\u8fc7\u6765\uff0c\u633d\u8d77\u4e86\u8881\u7d2b\u70df\u3002 \u3000\u3000\u662f\u968b\u7080\u5e1d\uff0c\u501f\u673a\u4f1a\u7d27\u7d27\u5730\u63e1\u4f4f\u4e86\u8881\u7d2b\u70df\u7ea4\u5de7\u7684\u7389\u624b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u6709\u6323\u8131\uff0c\u4e5f\u4e0d\u80fd\u6323\u8131\uff0c\u8c01\u6562\u62d2\u7edd\u5e1d\u738b\u5403\u8c46\u8150\u5462\uff1f \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u968b\u7080\u5e1d\u662f\u5e2e\u4e86\u8881\u7d2b\u70df\u4e00\u4e2a\u5927\u5fd9\uff0c\u5979\u6839\u672c\u4e0d\u613f\u8dea\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u53ea\u662f\u501f\u957f\u88d9\u63a9\u906e\uff0c\u76d8\u6536\u4e86\u53cc\u817f\uff0c\u6446\u51fa\u4e2a\u4e0b\u8dea\u7684\u59ff\u52bf\uff0c\u7687\u4e0a\u4f38\u624b\u4e00\u63e1\uff0c\u4e5f\u5c31\u501f\u52bf\u7ad9\u8d77\u8eab \u5b50\u3002 \u3000\u3000\u968b\u7080\u5e1d\u8fd1\u5728\u54ab\u5c3a\uff0c\u5e76\u672a\u53d1\u89c9\u3002 \u3000\u3000\u4e0a\u767e\u53ea\u7684\u7f8e\u5973\u773c\u775b\uff0c\u4e5f\u672a\u53d1\u89c9\u3002 \u3000\u3000\u90a3\u662f\u5f88\u9ad8\u660e\u7684\u6b66\u529f\u6280\u827a\uff0c\u786c\u628a\u4e00\u53cc\u4fee\u957f\u7684\u7389\u817f\uff0c\u76d8\u6536\u5f97\u4e0d\u89c1\u7834\u7efd\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u770b\u5230\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6b63\u56de\u8fc7\u5934\u6765\uff0c\u60f3\u8868\u73b0\u4e00\u4e0b\u8c26\u900a\u7684\u793c\u8c8c\uff0c\u51c6\u5907\u62c9\u62e2\u8fd9\u4e2a\u7075\u6167\u7684\u7f8e\u5973\u4e86\u3002 \u3000\u3000\u4f46\u5374\u53d1\u89c9\u4e86\u8881\u7d2b\u70df\u5168\u8eab\u5728\u7a7a\u4e2d\u865a\u6d6e\u7740\uff0c\u4f3c\u662f\u5fc3\u4e2d\u5df2\u5b58\u4e86\u4e00\u79cd\u654c\u610f\uff0c\u4e0d\u7981\u5927\u5927\u4e00\u9707\uff0c\u5374\u4ecd \u80fd\u4e0d\u5fd8\u63a9\u9970\uff0c\u6325\u624b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d0\u59d0\u8bf7\u8d77\uff0c\u5c0f\u59b9\u5f53\u53d7\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u5979\u9065\u9065\u4e00\u793c\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7684\u4e3e\u52a8\uff0c\u4e5f\u6ca1\u6709\u7792\u8fc7\u5b87\u6587\u6210\u90fd\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u8eab\u8d1f\u7edd\u9876\u6b66\u529f\u7684\u9ad8\u624b\uff0c\u8033\u76ee\u7075\u654f\uff0c\u6d1e\u5bdf\u7ec6\u5fae\u3002 \u3000\u3000\u4f46\u8fd9\u4e2a\u53d1\u73b0\uff0c\u4f7f\u5f97\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u9707\u52a8\u6781\u4e86\uff0c\u4ed6\u591c\u5de1\u5185\u5bab\uff0c\u5df2\u53d1\u89c9\u8881\u5b9d\u513f\u662f\u8eab\u6000\u5947\u6280\u7684\u4eba \u7269\u3002 \u3000\u3000\u56e0\u5b9d\u513f\u662f\u968b\u7080\u5e1d\u7684\u5ba0\u5bb6\uff0c\u4e0d\u6562\u8d38\u7136\u4e0b\u4ee4\u8c03\u67e5\uff0c\u53ea\u80fd\u5728\u6697\u4e2d\u7559\u5fc3\u3002 \u3000\u3000\u73b0\u5728\uff0c\u53c8\u5192\u51fa\u4e86\u4e00\u4e2a\u8881\u7d2b\u70df\u6765\uff0c\u5979\u5165\u5bab\u4e09\u6708\uff0c\u81ea\u5df1\u5374\u5168\u65e0\u5bdf\u89c9\uff0c\u6bd4\u8d77\u8881\u5b9d\u513f\uff0c\u4f3c\u4e4e\u662f\u66f4 \u4e3a\u53ef\u6015\u4e86\u3002 \u3000\u3000\u201c\u6210\u90fd\uff0c\u4e0d\u8981\u53f9\u6c14\uff01\u54b1\u4eec\u5230\u5fa1\u4e66\u623f\u53bb\uff0c\u6715\u8981\u548c\u4f60\u7814\u5546\u4e00\u4e0b\uff0c\u629a\u527f\u516d\u8def\u4e71\u532a\u7684\u56fd\u4e8b\uff01\u201d \u3000\u3000\u6e05\u9192\u65f6\u7684\u968b\u7080\u5e1d\uff0c\u5b9e\u5728\u662f\u4e2a\u77e5\u4eba\u5584\u4efb\u7684\u7cbe\u660e\u5e1d\u738b\u3002 \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u81e3\u5728\u5019\u9a7e\u3002\u201d\u5b87\u6587\u6210\u90fd\u6062\u590d\u4e86\u9547\u9759\u3002 \u3000\u3000\u201c\u7d2b\u70df\uff0c\u4e00\u8d77\u6765\u5427\uff01\u201d\u968b\u7080\u5e1d\u633d\u4f4f\u4e86\u8881\u7d2b\u70df\u7684\u53f3\u624b\uff0c\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u968b\u7080\u5e1d\u671b\u7740\u5806\u6ee1\u5fa1\u6848\u7684\u594f\u7ae0\uff0c\u6709\u4e9b\u6c14\u9981\u5730\u8bf4\u9053\uff1a\u201c\u6210\u90fd\uff0c\u4f60\u624d\u517c\u6587\u6b66\uff0c\u66ff\u6715\u5206\u52b3\uff0c\u68c0\u9605 \u4e00\u4e0b\u8fd9\u4e9b\u594f\u7ae0\uff0c\u9009\u51fa\u4e00\u4e9b\u91cd\u8981\u7684\u7ed9\u6715\u6279\u9605\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u62e8\u6b3e\u8d61\u707e\uff0c\u4ee5\u5b89\u6c11\u751f\uff0c\u527f\u706d\u4e71\u532a\uff0c\u4ee5\u632f\u6c11 \u6c14\u3002\u56fd\u4e8b\u867d\u7136\u7eb7\u9648\uff0c\u4f46\u5927\u5c40\u5c1a\u672a\u7d0a\u4e71\uff0c\u66b4\u6c11\u5e73\u5b9a\u4e4b\u540e\uff0c\u5c31\u4e0d\u96be\u7406\u51fa\u4e00\u4e2a\u5934\u7eea\u4e86\uff1f\u201d \u3000\u3000\u968b\u7080\u5e1d\u867d\u5728\u9759\u542c\u5b87\u6587\u6210\u90fd\u7684\u610f\u89c1\uff0c\u4f46\u53cc\u76ee\u5982\u8d3c\uff0c\u4e0d\u505c\u5730\u5728\u8881\u7d2b\u70df\u8eab\u4e0a\u641c\u89c5\uff0c\u770b\u7684\u8881\u7d2b\u70df \u8138\u90fd\u7ea2\u4e86\uff0c\u53ea\u597d\u5fae\u5782\u8793\u9996\uff0c\u542b\u7f9e\u6d45\u7b11\u3002 \u3000\u3000\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u4ed6\u662f\u624b\u63e1\u751f\u6740\u5927\u6743\u7684\u5e1d\u738b\uff01 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u7684\u7f8e\u4e3d\uff0c\u662f\u6e05\u79c0\u3001\u98d8\u9038\u7684\u4ed9\u59ff\u7389\u5bb9\uff0c\u4e0d\u5e26\u60d1\u4eba\u7684\u5996\u6c14\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u968b\u7080\u5e1d\u8fd8\u5f88\u6e05\u9192\uff0c\u4e5f\u542c\u6e05\u695a\u4e86\u5b87\u6587\u6210\u90fd\u7684\u594f\u7ae0\u3002 \u3000\u3000\u201c\u6d3e\u9ad8\u988d\u4ee3\u6715\u653e\u6d4e\u8d48\u94f6\uff0c\u97e9\u64d2\u864e\u63d0\u8c03\u5927\u519b\uff0c\u5206\u527f\u516d\u8def\u4e71\u6c11\uff0c\u537f\u5bb6\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\u82f1\u660e\uff0c\u9ad8\u988d\u6e05\u5ec9\uff0c\u671b\u91cd\u4e00\u65f6\uff0c\u7531\u4ed6\u51fa\u9762\u53d1\u653e\u8d48\u94f6\uff0c\u5b9e\u4e3a\u7b2c\u4e00\u4eba\u9009\u2026\u2026\u201d\u5b87\u6587\u6210\u90fd \u9053\uff1a\u201c\u97e9\u64d2\u864e\u4e00\u4ee3\u540d\u5c06\uff0c\u5e74\u4e8b\u867d\u957f\uff0c\u4f59\u5a01\u72b9\u5b58\uff0c\u4e0d\u5c11\u7edf\u5175\u4e4b\u5e05\uff0c\u591a\u4e3a\u4ed6\u7684\u65e7\u5c5e\uff0c\u7531\u4ed6\u603b\u7efe\u603b \u7b26\uff0c\u4e71\u4e8b\u6307\u65e5\u53ef\u5e73\u4e86\u3002\u201d \u3000\u3000\u6715\u62e8\u5e93\u94f6\u767e\u4e07\u4e24\uff0c\u7531\u9ad8\u988d\u643a\u5f80\u8d48\u707e\uff0c\u4e0d\u8db3\u4e4b\u6570\uff0c\u7531\u5404\u9053\u5dde\u5e9c\u7b79\u57ab\uff0c\u62b5\u7f34\u6765\u5e74\u7cae\u8d4b\u3002\u201d\u968b \u7080\u5e1d\u9053\uff1a\u201c\u5c01\u97e9\u64d2\u864e\u4e3a\u516d\u8def\u90fd\u62db\u8ba8\uff0c\u63d0\u5927\u519b\u4e09\u5341\u4e07\uff0c\u514b\u65e5\u51fa\u5e08\uff0c\u6715\u7ed9\u4ed6\u4e00\u5e74\u9650\u671f\uff0c\u5e73\u706d\u516d\u8def \u5c18\u70df\uff0c\u65f6\u95f4\u662f\u5426\u591f\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u9053\uff1a\u201c\u518d\u6388\u4ed6\u5c1a\u65b9\u5b9d\u5251\uff0c\u8282\u5236\u5404\u9053\u519b\u5175\uff0c\u4fbf\u5b9c\u653e\u624b\u884c\u4e8b\uff0c\u629a\u527f\u5e76\u884c\uff0c\u5206\u8def\u8fdb \u5175\uff0c\u4e00\u5e74\u5e94\u8be5\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6715\u8fd9\u5c31\u53d1\u4e0b\u8bcf\u65e8\u3002\u201d\u968b\u7080\u5e1d\u53ec\u6765\u592a\u76d1\uff0c\u4eb2\u4e66\u8bcf\u65e8\uff0c\u8d70\u7b14\u5982\u98de\uff0c\u7247\u523b\u800c\u5c31\u3002 \u3000\u3000\u8881\u7d2b\u70df\u51b7\u773c\u65c1\u89c2\uff0c\u770b\u4ed6\u5904\u4e8b\u7684\u679c\u51b3\u82f1\u660e\uff0c\u5b9e\u5728\u662f\u4e2a\u624d\u60c5\u7eb5\u6a2a\u7684\u597d\u7687\u5e1d\uff0c\u5982\u679c\uff0c\u4ed6\u80af\u628a\u4e09 \u5206\u4e4b\u4e00\u7684\u7cbe\u529b\u82b1\u5728\u671d\u653f\u4e0a\uff0c\u5927\u968b\u671d\u7edd\u4e0d\u4f1a\u6709\u4ea1\u56fd\u4e4b\u5fe7\u3002 \u3000\u3000\u8bcf\u65e8\u9881\u4e0b\uff0c\u968b\u7080\u5e1d\u4f3c\u662f\u53d7\u4e86\u4e00\u756a\u59d4\u5c48\uff0c\u6447\u6447\u5934\uff0c\u53f9\u9053\uff1a\u201c\u6210\u90fd\uff0c\u8fd9\u79ef\u6848\u594f\u7ae0\uff0c\u65e2\u975e\u4ec0\u4e48 \u5927\u4e8b\uff0c\u5c31\u7531\u4f60\u4ee3\u6715\u5904\u7f6e\u4e01\u5427\uff01\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u8fd9\u4e2a\u6210\u90fd\u600e\u6562\u627f\u62c5\uff01\u7531\u6210\u90fd\u4f1a\u540c\u7980\u7b14\u592a\u76d1\uff0c\u6574\u5217\u6761\u9648\uff0c\u518d\u8bf7\u7687\u4e0a\u51b3\u793a\u5427\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u8868\u73b0\u51fa\u4e86\u7edd\u5bf9\u7684\u5c0a\u656c\u3002 \u3000\u3000\u201c\u597d\u5427\uff01\u201d\u968b\u7080\u5e1d\u5df2\u663e\u51fa\u6781\u4e0d\u8010\u70e6\u7684\u795e\u8272\uff0c\u62c9\u4f4f\u8881\u7d2b\u70df\u79bb\u5f00\u4e86\u5fa1\u4e66\u623f\u3002 \u3000\u3000\u770b\u51fa\u968b\u7080\u5e1d\u7684\u4e0d\u60a6\u795e\u60c5\uff0c\u5b87\u6587\u6210\u90fd\u4e0d\u6562\u518d\u591a\u8a00\u4e86\u3002 \u3000\u3000\u867d\u7136\uff0c\u4ed6\u60f3\u63d0\u51fa\u8881\u5b9d\u513f\u4e00\u4e9b\u53ef\u7591\u7684\u4e8b\u8ff9\u3002 \u3000\u3000\u8881\u7d2b\u70df\u56de\u987e\u4e86\u5b87\u6587\u6210\u90fd\u4e00\u773c\uff0c\u53e3\u9f7f\u542f\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u5979\u53d1\u89c9\u4e86\u5b87\u6587\u6210\u90fd\u4ee5\u76ee\u793a\u610f\u5979\uff0c\u6b64\u65f6\u4e0d\u5b9c\u591a\u8a00\u3002 \u3000\u3000\u8881\u7d2b\u70df\u60f3\u4e0d\u901a\uff0c\u5979\u5df2\u660e\u767d\u7684\u8bf4\u51fa\u6765\uff0c\u5371\u9669\u5728\u4eac\u90fd\uff0c\u6cc4\u6f0f\u51fa\u5929\u673a\uff0c\u968b\u7080\u5e1d\u4e3a\u4ec0\u4e48\u4e0d\u8ffd\u95ee \u5462\uff1f \u3000\u3000\u5979\u539f\u60f3\u5fa1\u4e66\u623f\u4e00\u756a\u6df1\u8c08\uff0c\u80fd\u53d6\u5f97\u968b\u7080\u5e1d\u7684\u4fe1\u4efb\uff0c\u4e5f\u53d6\u5f97\u5b87\u6587\u6210\u90fd\u7684\u5408\u4f5c\uff0c\u7528\u5bab\u5ef7\u7981\u536b\u7684 \u529b\u91cf\uff0c\u5c55\u5f00\u6251\u706d\u738b\u6c14\u7684\u5de5\u4f5c\uff0c\u4ee5\u633d\u6551\u4e00\u573a\u6740\u52ab\u3002\u73b0\u5728\u770b\u6765\u5df2\u975e\u6613\u4e8b\u3002 \u3000\u3000\u968b\u7080\u5e1d\u76f4\u5954\u6b63\u623f\uff0c\u4e24\u4e2a\u5750\u5728\u5385\u623f\u7684\u5bab\u5973\uff0c\u4e00\u89c1\u968b\u7080\u5e1d\u7a81\u7136\u5230\u6765\uff0c\u6025\u6025\u8dea\u4e0b\u63a5\u9a7e\uff0c\u968b\u7080\u5e1d \u5374\u4e00\u6325\u624b\uff0c\u628a\u4e24\u4e2a\u5bab\u5973\u64b5\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u968b\u7080\u5e1d\u653e\u5f00\u8881\u7d2b\u70df\uff0c\u5927\u5598\u4e86\u4e24\u53e3\u6c14\u3002 \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u53d1\u89c9\u8fd9\u662f\u5904\u5f88\u5947\u602a\u7684\u5730\u65b9\uff0c\u50cf\u4e00\u5904\u5bdd\u5bab\uff0c\u5374\u4e0d\u89c1\u5e8a\u5e10\uff0c \u4e24\u5f20\u592a\u5e08\u6905\u4e0a\uff0c\u94fa\u7740\u7c89\u7ea2\u8272\u7684\u5750\u57ab\u3002 \u3000\u3000\u4e00\u5f20\u7ea2\u6f06\u516b\u4ed9\u684c\u4e0a\uff0c\u653e\u7740\u7ec6\u74f7\u8336\u58f6\u548c\u56db\u4e2a\u74f7\u676f\u3002 \u3000\u3000\u6700\u5947\u602a\u7684\u4e00\u5f20\u5947\u5927\u7684\u6728\u6905\u4e0a\uff0c\u8986\u76d6\u7740\u7c89\u7ea2\u8272\u7684\u7eff\u82b1\u88ab\u5957\u3002 \u3000\u3000\u8881\u7d2b\u70df\u770b\u4e0d\u61c2\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u4e5f\u4e0d\u61c2\u623f\u4e2d\u7684\u8bbe\u5907\u4f5c\u7528\u3002 \u3000\u3000\u5979\u5947\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u900d\u9065\u5c45\uff01\u201d\u968b\u7080\u5e1d\u6380\u53bb\u5927\u6728\u6905\u4e0a\u7684\u7c89\u7ea2\u62ab\u5957\uff0c\u7b11\u9053\uff1a\u201c\u7d2b\u70df\uff0c\u5750\u4e0a\u53bb\u8bd5\u8bd5\u770b\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u7728\u52a8\u4e86\u4e00\u4e0b\u5927\u773c\u775b\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u662f\u4e00\u56fd\u4e4b\u541b\uff0c\u600e\u7684\u8fd8\u7ae5\u5fc3\u672a\u9000\uff0c\u8fd9\u4e0d\u8fc7\u662f\u4e00\u5f20\u5927 \u6728\u6905\u7f62\u4e86\uff0c\u6709\u4ec0\u4e48\u597d\u8bd5\u7684\uff1f\u201d \u3000\u3000\u5e1d\u738b\u662f\u62e5\u6709\u7edd\u5bf9\u6743\u5a01\u7684\u4eba\uff01\u4e00\u53e5\u8bdd\u51b3\u4eba\u751f\u6b7b\uff0c\u6ca1\u6709\u4eba\u6562\u8fdd\u6297\u4ed6\u7684\u4ee4\u8c15\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff0c\u7adf\u7136\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u201c\u5750\u4e0a\u53bb\u554a\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u8be1\u5f02\u7684\u7b11\u7740\uff0c\u6276\u7740\u8881\u7d2b\u70df\u884c\u8fd1\u6728\u6905\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u987d\u52a3\u7684\u7687\u4e0a\u3002\u201d\u8881\u7d2b\u70df\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u8981\u501f\u52a9\u4ed6\u7684\u6743\u52bf\uff0c\u5b8c\u6210\u62ef\u6551\u4e07\u6c11\u82cd\u751f\u7684\u529f \u5fb7\uff0c\u5012\u662f\u4e0d\u5b9c\u592a\u8fc7\u5fe4\u9006\u4ed6\uff0c\u5750\u5c31\u5750\u5427\uff1a\u96be\u9053\u8fd9\u5f20\u6728\u6905\u4e0a\uff0c\u5e03\u4e0b\u4e86\u5929\u7f57\u5730\u7f51\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5728\u968b\u7080\u5e1d\u534a\u63a8\u534a\u6276\u4e0b\uff0c\u8881\u7d2b\u70df\u5750\u4e0a\u4e86\u5927\u6728\u6905\u3002 \u3000\u3000\u67d4\u8f6f\u7684\u6905\u9762\uff0c\u4f3c\u662f\u7528\u9526\u7f0e\u7ec7\u6210\uff0c\u5750\u4e0a\u53bb\u76f8\u5f53\u7684\u8212\u9002\u3002 \u3000\u3000\u968b\u7080\u5e1d\u7b11\u5f97\u66f4\u8be1\u5f02\u4e86\uff0c\u62ff\u8d77\u8881\u7d2b\u70df\u7684\u53cc\u8155\u653e\u5728\u6905\u5b50\u4e24\u4fa7\u7684\u6276\u624b\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u53eb\u900d\u9065\u6905\uff0c \u6715\u8981\u5de7\u624b\u7684\u5de5\u5320\u5236\u6210\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e00\u5f20\u6728\u6905\uff0c\u6709\u4ec0\u4e48\u7528\u5904\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5750\u4e0a\u6765\u5230\u5f88\u5b89\u9002\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff01\u201d\u968b\u7080\u5e1d\u7a81\u7136\u4e00\u6309\u673a\u94ae\uff0c\u5927\u6728\u6905\u6276\u624b\u4e4b\u4e0a\uff0c\u7acb\u523b\u4f38\u51fa\u4e24\u9053\u94c1\u7b8d\uff0c\u7d27\u7d27\u5730\u628a\u8881 \u7d2b\u70df\u53cc\u8155\u6263\u5728\u4e86\u6276\u624b\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u8fd9\u662f\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\uff0c\u770b\u4e0a\u53bb\uff0c\u4f60\u662f\u4e2a\u5f88\u5014\u5f3a\u7684\u4eba\uff0c\u6715\u81ea\u77e5\u8bf4\u670d\u4f60\u4e0d\u592a\u5bb9\u6613\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u592a\u660e\u767d\uff01\u4f60\u6709\u8bdd\uff0c\u5c3d\u7ba1\u8bf4\u4e2a\u6e05\u695a\uff01\u201d\u8881\u7d2b\u70df\u7684\u795e\u60c5\u5341\u5206\u9547\u9759\uff0c\u9053\uff1a\u201c\u4f60\u662f\u7687\u4e0a\uff0c \u8c01\u6562\u4e0d\u542c\u5462\uff1f\u201d \u3000\u3000\u6728\u6905\u7a81\u7136\u5f00\u59cb\u5feb\u901f\u7684\u65cb\u52a8\uff0c\u6905\u80cc\u4e5f\u5411\u540e\u5012\u53bb\uff0c\u53e6\u4e00\u9053\u94c1\u7b8d\uff0c\u4f38\u4e86\u51fa\u6765\uff0c\u6263\u7d27\u4e86\u8881\u7d2b\u70df\u7684 \u67f3\u8170\u2026\u2026 \u3000\u3000\u4e0d\u7528\u518d\u95ee\u4ec0\u4e48\uff0c\u8881\u7d2b\u70df\u5df2\u660e\u767d\u4e86\uff0c\u900d\u9065\u6905\u7684\u7528\u9014\uff0c\u662f\u5e2e\u52a9\u5e1d\u738b\u5f81\u670d\u5014\u5f3a\u5973\u4eba\u7684\u8c03\u548c\u8bbe \u5907\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u5347\u8d77\u4e86\u6012\u706b\u3002 \u3000\u3000\u201c\u7d2b\u70df\uff01\u6715\u4e0d\u4f1a\u8f9c\u8d1f\u4f60\u7684\uff0c\u4e8b\u6bd5\u4e4b\u540e\uff0c\u6715\u5c01\u4f60\u4e00\u9662\u592b\u4eba\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u957f\u957f\u5410\u4e00\u53e3\u6c14\uff0c\u5f3a\u5fcd\u4e0b\u6012\u706b\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u7537\u5973\u76f8\u60a6\uff0c\u4e5f\u4e0d\u7528\u628a\u624b\u811a\u3001\u8eab\u4f53\uff0c \u7528\u94c1\u7b8d\u7b8d\u8d77\u6765\uff01\u8fd9\u4e0d\u662f\u6c42\u7231\uff0c\u662f\u5f3a\u66b4\uff01\u201d \u3000\u3000\u201c\u5f3a\u66b4\uff01\u201d\u968b\u7080\u5e1d\u6123\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u61c2\uff0c\u4e0d\u8fc7\uff0c\u6211\u5374\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u6709\u4eba\u5bf9\u6211\u8bf4\u51fa \u8fd9\u53e5\u8bdd\u6765\uff01\u201d \u3000\u3000\u201c\u4f60\u80fd\u4f5c\u7684\u51fa\u6765\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8bf4\uff01\u201d\u8881\u7d2b\u70df\u9053\u3002 \u3000\u3000\u201c\u7d2b\u70df\uff0c\u4e0d\u8981\u751f\u6c14\uff0c\u6715\u53ea\u662f\u597d\u73a9\u554a\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u5927\u968b\u671d\u7684\u6c5f\u5c71\uff0c\u5c31\u5feb\u88ab\u4f60\u73a9\u5b8c\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u518d\u8fd9\u6837\u73a9\u4e0b\u53bb\uff0c\u53ea\u6015\u4e00\u5e74\u4e5f\u73a9 \u4e0d\u5230\u4e86\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u7d2b\u70df\uff01\u8fd9\u53ef\u662f\u780d\u5934\u7684\u5927\u7f6a\uff01\u4e0d\u80fd\u80e1\u8bf4\u554a\uff01\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u80e1\u8bf4\uff01\u4e1c\u5317\u738b\u6c14\u897f\u5357\u6765\uff0c\u5df2\u7136\u903c\u8fd1\u957f\u5b89\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u90fd\u611f\u89c9\u4e0d\u5230 \u5417\uff1f\u201d \u3000\u3000\u201c\u6715\u786e\u5b9e\u611f\u89c9\u4e0d\u5230\uff0c\u4f60\u53c8\u600e\u4e48\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u770b\u5929\u8c61\u554a\uff01\u201d \u3000\u3000\u201c\u770b\u5929\u8c61\uff1f\u201d\u968b\u7080\u5e1d\u9053\uff1a\u201c\u5929\u8c61\u6709\u4ec0\u4e48\u53d8\u5316\uff1f\u201d \u3000\u3000\u201c\u4e09\u57a3\u661f\u5bbf\uff0c\u5df2\u5feb\u88ab\u56f0\u4f4f\u4e86\uff0c\u4f60\u8fd8\u73a9\u7684\u5f00\u5fc3\u554a\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e00\u65e6\u7d2b\u5fae\u6666\u6627\uff0c\u4f60\u8fd9\u4e2a \u7687\u5e1d\u5c31\u4f5c\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u4e48\u4e25\u91cd\u5417\uff1f\u6715\u4e0d\u8fc7\u559c\u597d\u6e38\u4e50\uff0c\u533a\u533a\u5c0f\u4e8b\uff0c\u4e0a\u5929\u661f\u6587\u4e5f\u4f1a\u5782\u8c61\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u610f\u5982\u6b64\uff0c\u6211\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\u4e86\u3002\u201d\u53cc\u624b\u5fae\u6323\uff0c\u94c1\u7b8d\u65ad\u88c2\uff0c\u67f3\u8170\u5fae \u6446\uff0c\u6574\u5ea7\u900d\u9065\u6905\u7247\u7247\u788e\u6563\u4e8e\u5730\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5446\u5446\u5730\u7ad9\u5728\u4e00\u4fa7\uff0c\u832b\u7136\u5730\u770b\u7740\u8881\u7d2b\u70df\u3002 \u3000\u3000\u8881\u7d2b\u70df\u51b7\u51b7\u8bf4\uff1a\u201c\u660e\u5929\uff0c\u6211\u5c31\u8981\u79bb\u5f00\u5bab\u5ef7\uff0c\u4efb\u4f60\u8fd9\u4e2a\u98ce\u6d41\u7687\u5e1d\uff0c\u81ea\u751f\u81ea\u706d\u5427\uff01\u201d\u5feb\u6b65\u884c \u51fa\u4e86\u900d\u9065\u5c45\u3002 \u3000\u3000\u968b\u7080\u5e1d\u6ca1\u6709\u963b\u6b62\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u5df2\u5fc3\u77e5\u65e0\u80fd\u963b\u6b62\uff0c\u751a\u81f3\u8881\u7d2b\u70df\u8981\u4f24\u5bb3\u4ed6\uff0c\u4ed6\u4e5f\u5df2\u65e0\u80fd\u9003\u907f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u771f\u7684\u51c6\u5907\u4e86\uff0c\u548c\u7687\u4e0a\u95f9\u4e2a\u6c34\u706b\u4e0d\u5bb9\u7684\u5c40\u9762\uff0c\u501f\u91cd\u4ed6\u6743\u52bf\u633d\u6551\u82cd\u751f\u7684\u613f\u671b\uff0c\u5df2\u96be\u5b9e \u73b0\u3002 \u3000\u3000\u8fd9\u957f\u5b89\u6df1\u5bab\u4e2d\uff0c\u65e0\u5979\u7559\u604b\u7684\u5730\u65b9\u4e86\u3002 \u3000\u3000\u5e7d\u5c45\u5728\u6df1\u5bab\u7684\u5ad4\u3001\u5983\u3001\u624d\u4eba\uff0c\u5927\u6982\u6ca1\u6709\u4e00\u4e2a\u4f1a\u62d2\u7edd\u5e1d\u738b\u7684\u8981\u6c42\u3002 \u3000\u3000\u5979\u4eec\u6bcf\u65e5\u6574\u7406\u59ff\u5bb9\uff0c\u6d82\u8102\u62b9\u7c89\uff0c\u4e0d\u5c31\u662f\u76fc\u671b\u7740\u8fd9\u4e00\u5929\u5417\uff1f\u6709\u7684\u4eba\u82e6\u82e6\u7b49\u5f85\u4e86\u4e09\u4e94\u5e74\uff0c\u624d \u9047\u4e0a\u8fd9\u4e2a\u673a\u4f1a\uff0c\u4e5f\u6709\u4eba\u76fc\u671b\u5230\u4eba\u8001\u73e0\u9ec4\uff0c\u4e24\u9b13\u6591\u767d\uff0c\u4e5f\u6ca1\u6709\u8fd9\u6837\u7684\u673a\u4f1a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u662f\u559c\u6b22\u968b\u7080\u5e1d\uff0c\u65e0\u6cd5\u655e\u5f00\u5fc3\u6000\u63a5\u7eb3\u4ed6\uff0c\u4f46\u5728\u4e86\u89e3\u4e86\u5bab\u4e2d\u7684\u60c5\u52bf\uff0c\u968b\u7080\u5e1d\u7684\u4e3a\u4eba \u4e4b\u540e\uff0c\u4e5f\u9690\u9690\u7684\u751f\u51fa\u4e86\u5949\u732e\u7684\u5fc3\u6001\u3002 \u3000\u3000\u53ea\u6709\u4efb\u4ed6\u6c42\u5f97\u5230\u4e91\u96e8\u4e4b\u6b22\u540e\uff0c\u624d\u6709\u548c\u4ed6\u7545\u8c08\u7684\u673a\u4f1a\uff0c\u624d\u6709\u5f97\u5230\u4ed6\u5b8c\u5168\u4fe1\u4efb\u7684\u53ef\u80fd\u3002 \u3000\u3000\u4f46\u5979\u5fcd\u53d7\u4e0d\u4e86\u968b\u7080\u5e1d\u90a3\u79cd\u9738\u738b\u786c\u4e0a\u5f13\u7684\u624b\u6bb5\uff0c\u4e0d\u80fd\u63a5\u53d7\u5f3a\u8feb\u6027\u7684\u5360\u6709\u2026\u2026 \u3000\u3000\u5979\u5ffd\u7136\u60f3\u5230\uff0c\u5f53\u65f6\u5982\u679c\u4e00\u4e2a\u8033\u5149\u7529\u8fc7\u53bb\uff0c\u4e0d\u77e5\u9053\u968b\u7080\u5e1d\u4f1a\u6709\u4e9b\u4ec0\u4e48\u53cd\u5e94\uff1f \u3000\u3000\u60f3\u5230\u4e86\u5f00\u5fc3\u7684\u5730\u65b9\uff0c\u8881\u7d2b\u70df\u7b11\u4e86\uff0c\u5012\u662f\u6709\u4e9b\u540e\u6094\u521a\u624d\u6ca1\u6709\u6253\u4ed6\u4e00\u4e2a\u8033\u5149\u3002 \u3000\u3000\u5979\u53ea\u8eab\u800c\u6765\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u6574\u7406\u7684\u4e1c\u897f\uff01\u770b\u770b\u5c45\u4f4f\u4e09\u4e2a\u591a\u6708\u7684\u5c0f\u5de7\u96c5\u5ba4\uff0c\u6bd4\u8d77\u7ec8\u5357\u5c71\u7684 \u201c\u9752\u83b2\u6d1e\u201d\u662f\u591a\u4e86\u4e00\u79cd\u6e29\u6696\u7684\u611f\u89c9\u3002 \u3000\u3000\u90a3\u91cc\u5317\u98ce\u51db\u70c8\uff0c\u89c1\u4e0d\u5230\u76db\u5f00\u7684\u82b1\u6735\uff0c\u53ea\u6709\u9ad8\u5927\u7684\u53e4\u677e\uff0c\u627f\u53d7\u7740\u971c\u3001\u96ea\u7684\u51b2\u51fb\u3002 \u3000\u3000\u4eca\u591c\u518d\u4f4f\u4e00\u665a\uff0c\u660e\u5929\u5c31\u8981\u79bb\u5f00\u8fd9\u4e2a\u5730\u65b9\u4e86\uff0c\u597d\u597d\u7684\u7761\u4e00\u4e0b\u5427\uff01 \u3000\u3000\u8eba\u5728\u5e8a\u4e0a\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u7adf\u7136\u771f\u7684\u7761\u7740\u4e86\u3002 \u3000\u3000\u7741\u5f00\u773c\u6765\uff0c\u5929\u5df2\u5165\u591c\u3002 \u3000\u3000\u4eca\u591c\uff0c\u5e94\u8be5\u53bb\u67e5\u770b\u4e00\u4e0b\u5929\u8c61\u7684\u53d8\u5316\uff0c\u4f46\u8881\u7d2b\u70df\u61d2\u5f97\u770b\u4e86\uff0c\u7ba1\u4ed6\u5462\uff1f \u3000\u3000\u5e1d\u738b\u7684\u6210\u8d25\uff0c\u548c\u5979\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u5979\u5df2\u51b3\u5b9a\u56de\u7ec8\u5357\u5c71\u4e86\u3002 \u3000\u3000\u96c5\u5ba4\u5916\u6709\u706f\u706b\u5728\u95ea\u52a8\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u56db\u4e2a\u592a\u76d1\u6267\u7740\u7eb1\u706f\uff0c\u9759\u9759\u5730\u5b88\u5728\u5ba4\u5916\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u662f\u4e0d\u6562\u592a\u8fc7\u63a5\u8fd1\uff0c\u5374\u7ad9\u5728\u76f8\u5f53\u7684\u8ddd\u79bb\u4e4b\u5916\u3002 \u3000\u3000\u201c\u597d\u554a\uff01\u6d3e\u4eba\u76d1\u89c6\u6211\u4e86\uff0c\u5927\u6982\u51c6\u5907\u8981\u5bab\u536b\u6765\u6293\u4e86\u2026\u2026\u201d\u8881\u7d2b\u70df\u5fc3\u4e2d\u5fd6\u601d\u9053\uff1a\u201c\u6211\u5012\u8981\u770b \u770b\u5b87\u6587\u6210\u90fd\u8bad\u7ec3\u51fa\u6765\u7684\u5bab\u536b\uff0c\u80fd\u6709\u591a\u5927\u7684\u672c\u9886\uff1f \u3000\u3000\u90a3\u4e9b\u53f7\u79f0\u4e07\u592b\u4e0d\u5f53\u4e4b\u52c7\u7684\u5341\u5927\u526f\u7edf\u9886\uff0c\u6709\u4e9b\u4ec0\u4e48\u60ca\u4eba\u7684\u6280\u827a\uff0c\u751a\u81f3\u5b87\u6587\u6210\u90fd\u7684\u672c\u4eba\uff0c\u53c8 \u6709\u4ec0\u4e48\u8fc7\u4eba\u4e4b\u5904\uff1f\u201d \u3000\u3000\u6084\u7136\u6253\u5f00\u6728\u95e8\uff0c\u8881\u7d2b\u70df\u7f13\u6b65\u800c\u51fa\uff0c\u79fb\u52a8\u4e86\u51e0\u76c6\u79cb\u83ca\uff0c\u6446\u5728\u95e8\u53e3\uff0c\u56de\u623f\u53bb\u63a9\u4e0a\u4e86\u6728\u95e8\uff0c\u76d8 \u819d\u5750\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9759\u9759\u5730\u7b49\u5f85\u7740\u3002 \u3000\u3000\u5f88\u5feb\u5c31\u6709\u4e86\u53cd\u5e94\uff0c\u6b65\u5c65\u58f0\u884c\u8fd1\u8fc7\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u80fd\u7834\u4e86\u6211\u7684\u4e59\u6728\u5c0f\u9635\uff0c\u518d\u51fa\u624b\u6253\u53d1\u4f60\u5427\uff01\u201d \u3000\u3000\u95e8\u5916\u54cd\u8d77\u4e86\u6089\u7d22\u7684\u58f0\u97f3\uff0c\u4f3c\u662f\u6709\u4eba\u5728\u649e\u51fb\u4e1c\u897f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u95ed\u76ee\u800c\u5750\uff0c\u4e0d\u4e88\u7406\u4f1a\u3002 \u3000\u3000\u201c\u7d2b\u70df\uff0c\u5f00\u95e8\u5440\uff0c\u6211\u662f\u7687\u4e0a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a\u201c\u600e\u4e48\u4eb2\u81ea\u6765\u4e86\uff0c\u4f60\u90a3\u5feb\u88ab\u8881\u5b9d\u513f\u638f\u7a7a\u7684\u8eab\u4f53\uff0c\u5c31\u7b97\u6709\u4e00 \u8eab\u70bc\u6210\u7684\u6b66\u529f\uff1b\u53ea\u6015\u4e5f\u627f\u53d7\u4e0d\u4f4f\u6211\u4e00\u638c\u53cd\u51fb\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u672c\u9886\u5c31\u81ea\u5df1\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u8fdb\u4e0d\u53bb\u554a\uff01\u600e\u4e48\u666f\u7269\u5168\u53d8\u4e86\u3002\u201d\u968b\u7080\u5e1d\u9053\uff1a\u201c\u597d\u50cf\u662f\u53e4\u6728\u4e1b\u6797\u4e00\u822c\uff0c\u65e0\u8def\u53ef\u901a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u5730\u7b11\u4e86\uff0c\u8fd9\u5c0f\u5c0f\u7684\u4e59\u6728\u9635\uff0c\u8fd8\u771f\u7684\u5f88\u7ba1\u7528\u554a\uff01 \u3000\u3000\u201c\u7d2b\u70df\uff01\u6211\u90fd\u5feb\u8ff7\u5931\u65b9\u5411\u4e86\uff0c\u8bf7\u5f00\u95e8\u5427\uff01\u6715\u6709\u8bdd\u8ddf\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff01\u6211\u660e\u5929\u5c31\u8981\u8d70\u4e86\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u5927\u95f9\u4e00\u573a\uff0c\u6bc1\u4f24\u4f60\u7684\u7687\u5bab\u5185 \u9662\uff0c\u4f46\u6211\u4e5f\u4e0d\u80fd\u5bb9\u5fcd\u4f60\u7684\u5bab\u536b\u60ca\u6270\u6211\uff0c\u56de\u53bb\u5427\uff01\u597d\u6765\u597d\u6563\u561b\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\uff0c\u6211\u8981\u649e\u4e0a\u4e00\u68f5\u5927\u6811\u4e86\uff01\u201d\u968b\u7080\u5e1d\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5f3a\u66b4\u4f60\u7684\u7528\u5fc3\uff0c\u6211\u53ea\u662f\u60f3\u8ba9\u4f60\u60ca \u5947\u4e00\u4e0b\uff01\u201d \u3000\u3000\u201c\u54fc\uff01\u60ca\u5947\u4e00\u4e0b\uff0c\u90a3\u79cd\u6076\u6bd2\u7684\u8bbe\u8ba1\uff0c\u4f24\u5bb3\u4e86\u591a\u5c11\u5c11\u5973\uff1f\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u6715\u4ee5\u540e\uff0c\u518d\u4e5f\u4e0d\u7528\u8fd9\u4e9b\u4e1c\u897f\u4e86\uff0c\u5f00\u95e8\u5427\uff01\u6715\u8fd8\u6709\u5f88\u591a\u4e8b\u548c\u4f60\u5546\u91cf\uff01\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u51b3\u5b9a\u8981\u8d70\u4e86\uff0c\u4e0d\u60f3\u518d\u7406\u4f1a\u4f60\u7684\u4e8b\u60c5\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u522b\u518d\u70e6\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\uff01\u4f60\u771f\u7684\u8fd9\u6837\u5fcd\u5fc3\u4e48\uff1f\u4f60\u6df7\u5165\u6df1\u5bab\uff0c\u4e00\u5b9a\u6709\u5f88\u91cd\u5927\u7684\u76ee\u7684\uff0c\u6715\u5f88\u60f3\u542c\u542c\u4f60\u7684\u9ad8\u89c1 \u554a\uff01\u201d \u3000\u3000\u201c\u6211\u5374\u61d2\u5f97\u518d\u7406\u4f60\u4e86\uff01\u56de\u53bb\u5427\uff01\u4f60\u6709\u5341\u4e8c\u4e2a\u592b\u4eba\uff01\u65e0\u6570\u7684\u624d\u4eba\u3001\u5ad4\u5983\uff0c\u522b\u518d\u60f3\u6253\u6211\u7684\u4e3b \u610f\u4e86\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u8981\u5b8c\u5b8c\u6574\u6574\u6765\uff0c\u518d\u5b8c\u5b8c\u6574\u6574\u5730\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\uff01\u542c\u6211\u8bf4\u554a\uff01\u6211\u6709\u4e8b\u6c42\u4f60\uff01\u201d \u3000\u3000\u5b8c\u5168\u6ca1\u6709\u4e86\u7687\u5e1d\u7684\u5c0a\u4e25\uff0c\u53eb\u58f0\u4e2d\u5145\u6ee1\u7740\u54c0\u6c42\u3002 \u3000\u3000\u201c\u4e0d\u8981\u542c\u4e86\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u6211\u63a9\u4e0a\u8033\u6735\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5403\u4e86\u79e4\u7823\u94c1\u4e86\u5fc3\uff0c\u4f38\u51fa\u53cc\u624b\uff0c\u63a9\u4f4f\u4e86\u8033\u6735\u3002 \u3000\u3000\u4ee5\u5979\u7684\u4fee\u4e3a\u4e4b\u6df1\uff0c\u5c31\u7b97\u91cd\u697c\u6df1\u9501\uff0c\u4e5f\u53ef\u4ee5\u542c\u5230\u5341\u4e08\u5916\u8f7b\u8f7b\u7684\u58f0\u606f\uff0c\u63a9\u4e0a\u53cc\u8033\uff0c\u53ea\u662f\u4e00\u79cd \u81ea\u7136\u7684\u672c\u80fd\uff01 \u3000\u3000\u5979\uff01\u4ecd\u662f\u4e00\u4e2a\u53cc\u5341\u5e74\u534e\u7684\u5c0f\u59d1\u5a18\u554a\uff01 \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u8881\u7d2b\u70df\u8f7b\u8f7b\u5730\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u771f\u7684\u8d70\u4e86\uff0c\u8fd9\u4e2a\u6ca1\u6709\u8010\u6027\u7684\u7687\u5e1d\uff0c\u6211\u4eec \u7f18\u5c3d\u4e8e\u6b64\u4e86\u3002\u201d \u3000\u3000\u7f13\u7f13\u7ad9\u8d77\u8eab\uff0c\u8131\u4e0b\u5ad4\u5973\u7684\u8863\u773c\uff0c\u4e5f\u8131\u4e0b\u4e00\u8eab\u70e6\u607c\uff0c\u6697\u9053\uff1a\u8d70\u5427\uff01\u201d\u4f55\u5fc5\u4e00\u5b9a\u8981\u7b49\u5230\u660e\u5929 \u5462\uff1f\u65e2\u7136\u51b3\u5fc3\u7edd\u53bb\u5c18\u7f18\uff0c\u53c8\u4f55\u5fc5\u591a\u53d7\u4e00\u591c\u714e\u71ac\uff0c\u6211\u5df2\u5c3d\u4e86\u5fc3\u529b\uff0c\u4f59\u4e0b\u7684\u5c31\u662f\u5728\u52ab\u96be\u9003\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6362\u4e0a\u4e86\u9752\u8272\u7684\u8863\u888d\u3002 \u3000\u3000\u90a3\u662f\u7ec8\u5357\u5c71\u4fee\u9053\u65f6\u7a7f\u7740\u7684\u8863\u670d\uff0c\u53bb\u610f\u65e2\u51b3\uff0c\u8fd8\u6211\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u7f13\u7f13\u6253\u5f00\u623f\u95e8\uff0c\u5929\u7237\u554a\uff01\u4e5d\u4e94\u4e4b\u5c0a\u7684\u7687\u4e0a\uff0c\u76f4\u633a\u633a\u5730\u8dea\u5728\u95e8\u5916\u3002 \u3000\u3000\u4ed6\u771f\u7684\u5f88\u4f24\u5fc3\uff0c\u53cc\u988a\u4e0a\u7684\u6cea\u6c34\u4e00\u76f4\u4e0d\u505c\u5730\u6eda\u4e0b\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u832b\u7136\u4e86\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6c42\u4f60\u5440\uff01\u6211\u8fdb\u4e0d\u53bb\u4f60\u7684\u623f\u95e8\uff0c\u4e5f\u89c1\u8bc6\u5230\u4e86\u4f60\u672f\u6cd5\u7684\u5389\u5bb3\uff0c\u53ea\u597d\u6c42\u4f60\u5f00\u95e8\u4e86\u3002\u201d\u968b\u7080\u5e1d \u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e16\u4e0a\u771f\u6709\u4f60\u8fd9\u6837\u7684\u5947\u5973\u5b50\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u79fb\u5f00\u51e0\u76c6\u79cb\u83ca\uff0c\u6276\u8d77\u4e86\u968b\u7080\u5e1d\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u53ea\u79fb\u5f00\u51e0\u76c6\u83ca\u82b1\u561b\uff01 \u3000\u3000\u7acb\u523b\u6062\u590d\u4e86\u65e7\u65f6\u666f\u89c2\uff0c\u5c0f\u5de7\u7684\u96c5\u5ba4\uff0c\u5e7d\u9759\u7684\u5ead\u9662\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5446\u4e86\u4e00\u5446\uff0c\u6253\u91cf\u7740\u8881\u7d2b\u70df\u7684\u8863\u7740\uff0c\u9053\uff1a\u201c\u4f60\u7a7f\u7684\u4ec0\u4e48\u8863\u670d\uff1f\u201d \u3000\u3000\u201c\u6751\u5973\u8863\u670d\u554a\uff01\u6211\u6765\u81ea\u5c71\u6797\u4e2d\uff0c\u518d\u56de\u5c71\u4e2d\u53bb\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u624d\u662f\u6211\u672c\u6765\u7684\u771f\u9762 \u76ee\u3002\u201d \u3000\u3000\u201c\u4e0d\u80fd\u8d70\u554a\uff01\u201d\u968b\u7080\u5e1d\u53cc\u81c2\u4e00\u5f20\uff0c\u7d27\u7d27\u5730\u62b1\u4f4f\u4e86\u8881\u7d2b\u70df\uff0c\u9053\uff1a\u201c\u6715\u77e5\u9519\u4e86\uff0c\u4e0d\u5e94\u8be5\u5f00\u8fd9 \u4e2a\u73a9\u7b11\uff01\u7559\u4e0b\u6765\u5427\uff01\u8981\u6715\u529e\u4ec0\u4e48\uff1f\u53ea\u7ba1\u5429\u5490\uff0c\u6715\u5168\u90fd\u4f1a\u4f9d\u4f60\uff01\u201d \u3000\u3000\u8fd9\u54ea\u91cc\u50cf\u662f\u541b\u738b\u7684\u53e3\u6c14\uff0c\u6781\u5c3d\u59d4\u5a49\uff0c\u6ceb\u7136\u6b32\u6ce3\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u8f6f\u5566\uff01\u4efb\u4ed6\u7d27\u7d27\u7684\u6402\u62b1\u7740\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u771f\u7684\u60f3\u8981\u6211\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u968b\u7080\u5e1d\u56de\u7b54\u7684\u65a9\u9489\u622a\u94c1\uff0c\u9053\uff1a\u201c\u65e5\u6708\u9274\u6211\u5fc3\uff0c\u6715\u5982\u8a00\u4e0d\u7531\u8877\u2026\u2026\u201d \u3000\u3000\u8981\u53d1\u8a93\u4e86\uff0c\u4f46\u5374\u88ab\u8881\u7d2b\u70df\u7684\u4e00\u53ea\u7389\u624b\uff0c\u5835\u4f4f\u4e86\u5634\u5df4\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u4f60\u662f\u5929\u4e0b\u7684\u81f3\u5c0a\u554a\uff01 \u600e\u53ef\u53e3\u51fa\u620f\u8a00\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\uff01\u4f60\u5982\u8d70\u4e86\uff0c\u6715\u5c06\u7ec8\u751f\u6000\u5ff5\u4e0d\u606f\uff0c\u751f\u6d3b\u7684\u4e0d\u4f1a\u5feb\u4e50\uff0c\u8fd9\u4e2a\u7687\u5e1d\uff0c\u5e72\u7684\u4ec0\u4e48\u5473\u9053 \u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5750\u4e0b\u6765\uff0c\u6211\u4eec\u597d\u597d\u5730\u8c08\u8c08\uff01\u6211\u6df7\u5165\u5bab\u5ef7\u6765\uff0c\u5fc3\u65e0\u79c1\u6b32\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u7acb\u523b\u653e\u4e86\u8881\u7d2b\u70df\uff0c\u9053\uff1a\u201c\u6715\u8fd9\u53a2\u6d17\u8033\u606d\u542c\u3002\u201d\u795e\u60c5\u7adf\u4e5f\u53d8\u5f97\u5e84\u91cd\u8d77\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u642c\u8fc7\u5ba4\u4e2d\u7684\u9526\u58a9\uff0c\u9053\uff1a\u201c\u5750\u4e0b\u5440\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\uff01\u4f60\u8bf4\u4e1c\u5317\u738b\u6c14\u897f\u5357\u6765\uff0c\u6307\u7684\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u968b\u7080\u5e1d\u7f13\u7f13\u5750\u4e0b\uff0c\u4f38\u624b\u53bb\u62c9\u8881\u7d2b\u70df\uff0c\u628a\u5979\u63fd\u5165\u6000\u4e2d\uff0c\u4f46\u89e6\u53ca\u5230\u8881\u7d2b\u70df\u7684\u8863\u670d\u65f6\uff0c\u7acb\u523b\u53c8 \u6536\u4e86\u56de\u6765\u3002 \u3000\u3000\u4e00\u56fd\u4e4b\u541b\u7684\u9738\u6c14\uff0c\u4f3c\u4e4e\u5df2\u5b8c\u5168\u6d88\u5931\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7406\u4e00\u4e0b\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5927\u6982\u5728\u592a\u539f\u9644\u8fd1\u2026\u2026\u201d \u3000\u3000\u201c\u674e\u6e0a\u3002\u201d\u968b\u7080\u5e1d\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff01\u4ed6\u4e0a\u6708\u624d\u664b\u732e\u8881\u5b9d\u513f\uff0c\u5982\u6709\u4e8c\u5fc3\uff0c\u600e\u4f1a \u628a\u5b9d\u513f\u90a3\u6837\u7684\u7f8e\u4eba\u9001\u5165\u957f\u5b89\u7684\u5bab\u5ef7\u4e2d\uff0c\u6715\u76f8\u4fe1\u4ed6\u82b1\u4e86\u4e0d\u5c11\u65f6\u95f4\uff0c\u624d\u80fd\u8bbf\u67e5\u5230\u5b9d\u5983\u90a3\u6837\u7684\u7f8e \u5973\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u5929\u4e0b\u7684\u82f1\u96c4\u4eba\u7269\u90fd\u50cf\u4f60\u5440\uff01\u65e5\u591c\u8ffd\u9010\u5973\u8272\uff0c\u7231\u7f8e\u4eba\u91cd\u8fc7\u6c5f \u5c71\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\uff01\u6715\u5e94\u8be5\u600e\u4e48\u505a\u5462\uff1f\u201d \u3000\u3000\u201c\u5bab\u536b\u7edf\u9886\u5b87\u6587\u6210\u90fd\u8bad\u7ec3\u4e86\u2014\u6279\u52c7\u731b\u7684\u6b66\u58eb\uff0c\u4e5f\u7f57\u81f4\u4e86\u4e0d\u5c11\u6c5f\u6e56\u9ad8\u624b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a \u201c\u5982\u82e5\u80fd\u5584\u7528\u8fd9\u6279\u6b66\u58eb\uff0c\u6251\u706d\u5d1b\u8d77\u7684\u738b\u6c14\uff0c\u4ee5\u56fa\u5927\u968b\u671d\u7684\u56fd\u57fa\uff0c\u5e76\u975e\u96be\u4e8b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u672a\u96e8\u7ef8\u7f2a\uff0c\u660e\u5929\u6715\u53ec\u89c1\u5b87\u6587\u6210\u90fd\uff0c\u8ba9\u4ed6\u67e5\u8bbf\u674e\u6e0a\u7684\u53cd\u8ff9\uff0c\u627e\u51fa\u5b9e\u636e\uff0c\u6715\u5c31\u5904\u6b7b\u674e \u6c0f\u4e00\u95e8\uff0c\u4ee5\u7edd\u540e\u60a3\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b87\u6587\u7edf\u9886\u7684\u6b66\u529f\uff0c\u5341\u5206\u9ad8\u5f3a\uff0c\u4f46\u59be\u770b\u5929\u8c61\u8fd0\u884c\uff0c\u662f\u53d7\u5230\u4e86\u4eba\u529b \u7684\u5e72\u9884\uff0c\u5fc5\u6709\u9ad8\u4eba\u5728\u5e55\u540e\u4e3b\u6301\uff0c\u90a3\u5c31\u4e0d\u662f\u5355\u51ed\u6b66\u529b\uff0c\u80fd\u591f\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u5947\u9053\uff1a\u201c\u4eba\u529b\u80fd\u5e72\u9884\u5929\u8c61\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u672f\u6cd5\u7cbe\u6e5b\u7684\u4eba\uff0c\u4ee5\u6b65\u7f61\u8e0f\u6597\u7684\u5947\u672f\uff0c\u4fc3\u4f7f\u5929\u8c61\u52a0\u901f\u8fd0\u884c\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5f53\u7136\uff0c \u5b83\u8fd8\u8981\u501f\u52a9\u4eba\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u7d2b\u70df\uff0c\u600e\u4e48\u4e0d\u8bf4\u4e0b\u53bb\u4e86\uff0c\u6715\u5728\u542c\u554a\uff01\xa8 \u3000\u3000\u201c\u59be\u5a62\u5df2\u6cc4\u6f0f\u4e86\u592a\u591a\u7684\u5929\u673a\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u518d\u8bf4\u4e0b\u53bb\uff0c\u59be\u5a62\u6050\u8981\u906d\u5230\u5929\u8c34\u4e86\u3002\u201d \u3000\u3000\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6715\u4e0d\u8ffd\u95ee\uff01\u4f46\u5b87\u6587\u6210\u90fd\u4e43\u6715\u624b\u4e0b\u7b2c\u4e00\u52c7\u58eb\uff0c\u4ed6\u5982\u4e0d\u80fd\u80dc\u4efb\uff0c\u6715\u5230\u54ea\u91cc\u518d\u8bf7 \u9ad8\u4eba\u5e2e\u5fd9\u5462\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u7d2b\u70df\u5c31\u662f\u4e3a\u6b64\u4e0b\u5c71\u7684\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u6715\u7ade\u8651\u672a\u53ca\u6b64\uff0c\u4e0d\u8fc7\uff0c\u8ba9\u4f60\u4eb2\u5192\u77e2\u77f3\uff0c\u5343\u91cc\u5954\u6ce2\uff0c\u6715\u5fc3\u4f55\u5fcd\u5462\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u59be\u56e0\u6b64\u800c\u6765\uff0c\u4e00\u4e3a\u5927\u968b\u671d\u7684\u6c5f\u5c71\uff0c\u4e8c\u4e3a\u5929\u4e0b\u9ece\u6c11\uff0c\u4e09\u4e3a\u6559\u8bad\u4e00\u4e0b\u5984\u52a0\u5e72\u9884\u5929\u673a \u7684\u72c2\u4eba\u3002\u201d \u3000\u3000\u201c\u537f\u5bb6\u5b9a\u8981\u5982\u6b64\uff0c\u6715\u4e5f\u53ea\u597d\u7167\u529e\u4e86\uff0c\u660e\u65e5\u5ba3\u53ec\u5b87\u6587\u6210\u90fd\uff0c\u7531\u4ed6\u6765\u8f85\u4f50\u537f\u5bb6\u2026\u2026\u201d \u3000\u3000\u201c\u59be\u65e0\u610f\u8ffd\u6c42\u6743\u52bf\u540d\u4f4d\uff01\u4f46\u8fd9\u4ef6\u4e8b\u5173\u7cfb\u7740\u56fd\u8109\u6c11\u8fd0\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5982\u4e0d\u80fd\u6388\u4e88\u751f\u6740\u5927 \u6743\uff0c\u90a3\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\uff01\u201d \u3000\u3000\u201c\u6715\u660e\u767d\uff0c\u6715\u6388\u4f60\u5c1a\u65b9\u5b9d\u5251\uff0c\u751f\u6740\u4e8e\u593a\uff0c\u5982\u6715\u4eb2\u4e34\uff0c\u671d\u4e2d\u767e\u5b98\uff0c\u5c01\u7586\u5927\u540f\uff0c\u5982\u82e5\u8c0b\u53cd\u6709 \u636e\uff0c\u537f\u5bb6\u53ef\u5148\u65a9\u540e\u594f\u3002\u201d \u3000\u3000\u771f\u662f\u7b80\u5355\u554a\uff01\u4e09\u8a00\u4e24\u8bed\uff0c\u5c31\u51b3\u5b9a\u4e86\u4e00\u4ef6\u5982\u6b64\u91cd\u5927\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u5bf9\u968b\u7080\u5e1d\u51b3\u65ad\u7684\u660e\u5feb\uff0c\u8881\u7d2b\u70df\u4e5f\u4e0d\u80fd\u4e0d\u4f69\u670d\u4e86\u3002 \u3000\u3000\u201c\u7d2b\u70df\uff0c\u6715\u7231\u6c5f\u5c71\uff0c\u66f4\u7231\u7f8e\u4eba\uff0c\u537f\u5bb6\u5fcd\u5fc3\u8d1f\u6211\u4e48\uff1f\u201d\u795e\u60c5\u9eef\u7136\uff0c\u4f3c\u662f\u6781\u529b\u5728\u63a7\u5236\u7740\u5fc3\u4e2d \u7684\u60b2\u51c4\u3002 \u3000\u3000\u4e00\u4e0b\u5b50\u628a\u56fd\u5bb6\u5927\u4e8b\uff0c\u8f6c\u5411\u7537\u5973\u95f4\u7684\u60c5\u7231\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5446\u4f4f\u4e86\uff0c\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u7d2b\u70df\uff01\u6715\u8981\u5c01\u4f60\u4e3a\u4e00\u9662\u592b\u4eba\uff0c\u5e0c\u671b\u537f\u5bb6\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e0d\u8981\u592b\u4eba\u7684\u540d\u53f7\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u6715\u5c01\u4f60\u4e3a\u7981\u536b\u7edf\u9886\uff0c\u62a4\u56fd\u5927\u6cd5\u5e08\uff0c\u52a0\u8d50\u63d0\u8c03\u5404\u8def\u519b\u9a6c\u7684\u5927\u6743\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u6211\u4e00\u65e6\u8eab\u81ba\u91cd\u4efb\uff0c\u519b\u52a1\u5306\u5fd9\uff0c\u54ea\u91cc\u8fd8\u6709\u65f6\u95f4\u987e\u53ca\u5230\u513f\u5973\u67d4\u60c5\u3002\u201d \u3000\u3000\u201c\u6715\u7edd\u4e0d\u76f8\u5f3a\uff0c\u537f\u5bb6\u5728\u519b\u52a1\u7a7a\u6687\u4e4b\u65f6\uff0c\u62bd\u51fa\u4e00\u70b9\u65f6\u95f4\u6765\uff0c\u966a\u6715\u996e\u9152\u8d4f\u6708\uff0c\u6715\u5c31\u5f88\u6ee1\u8db3 \u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u8f99\u4e86\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u4f60\u53ef\u662f\u771f\u4f1a\u7f20\u4eba\u54ea\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\uff0c\u60f3\u8131\u4e0b\u8881\u7d2b\u70df\u7684\u8863\u670d\uff0c\u624b\u6307\u7adf\u7136\u6709\u70b9\u98a4\u6296\u3002 \u3000\u3000\u4ed6\u6709\u6570\u5343\u6b21\u4e0a\u5e8a\u7684\u7ecf\u9a8c\uff0c\u53ef\u4ece\u6ca1\u6709\u52a8\u624b\u53bb\u5e2e\u5973\u4eba\u8131\u8863\u670d\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u65e9\u6295\u964d\u4e86\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u5982\u4e0d\u8ba9\u8fd9\u4e2a\u7687\u5e1d\u5f97\u507f\u5fc3\u613f\uff0c\u8fd9\u7801\u4e8b\uff0c\u5c31\u4f1a\u6ca1\u5b8c\u6ca1\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u968b\u7080\u5e1d\u6c42\u7231\u7684\u65b9\u6cd5\uff0c\u662f\u90a3\u4e48\u4e13\u5fc3\u4e00\u5fd7\uff0c\u767e\u822c\u987a\u4ece\uff0c\u4e5f\u8ba9\u8881\u7d2b\u70df\u6709\u4e9b\u611f\u52a8\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5f97\u5230\u4e86\u7edd\u5927\u7684\u6743\u52bf\uff0c\u53ef\u4e5f\u8d54\u4e0a\u4e86\u6e05\u767d\u7684\u8eab\u4f53\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5f97\u5230\u541b\u738b\u5ba0\u5e78\u7684\u4e8b\uff0c\u5f88\u5feb\u7684\u4f20\u904d\u4e86\u5bab\u5ef7\u3002 \u3000\u3000\u4f46\u6c5f\u5c71\u4e0d\u6539\u65e7\u989c\u8272\uff0c\u8881\u7d2b\u70df\u62d2\u7edd\u4e86\u4e00\u9662\u592b\u4eba\u7684\u5c01\u8d50\uff0c\u4e5f\u6ca1\u6709\u534e\u5c4b\u7ed3\u91c7\u7eb3\u559c\u6c14\uff0c\u4f17\u591a\u5bab\u5973 \u7d27\u76f8\u4ece\u7684\u65b0\u5ba0\u6c14\u6d3e\u3002 \u3000\u3000\u5979\u9009\u62e9\u7684\u4f4f\u5904\uff0c\u7adf\u662f\u51e0\u906d\u5f3a\u66b4\u7684\u201c\u900d\u9065\u5c45\u201d\u3002 \u3000\u3000\u5f53\u7136\u201c\u900d\u9065\u5c45\u201d\u4e0d\u518d\u6cbf\u7528\u65e7\u540d\u4e86\uff0c\u6539\u4f5c\u4e86\u201c\u9752\u83b2\u5c0f\u9986\u201d\u3002 \u3000\u3000\u5979\u8981\u968b\u7080\u5e1d\u5c65\u884c\u627f\u8bfa\uff0c\u4e0d\u518d\u7528\u9738\u9053\u7684\u201c\u900d\u9065\u6905\u201d\u5f3a\u66b4\u5973\u4eba\uff0c\u5e72\u8106\u5360\u636e\u4e86\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u5c31\u7b97\u5979\u4eec\u4ecd\u613f\u610f\u5427\uff01\u4f46\u90a3\u79cd\u94c1\u7b8d\u52a0\u8eab\u7684\u7981\u5236\uff0c\u9664\u4e86\u53ef\u6ee1\u8db3\u7537\u4eba\u66b4\u8650\u7684\u5feb\u611f\u4e4b\u5916\uff0c\u5b9e\u5728\u7f3a \u5c11\u90ce\u60c5\u59be\u610f\u4e24\u76f8\u6295\u7684\u6c14\u6c1b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7531\u4f17\u591a\u7684\u5bab\u5973\u4e2d\uff0c\u9009\u51fa\u4e86\u4e24\u4e2a\u8d44\u8d28\u4f18\u5f02\uff0c\u624d\u6167\u8fc7\u4eba\u7684\u5c0f\u59d1\u5a18\uff0c\u4f5c\u4e3a\u8d34\u8eab\u7684\u5973\u5a62\uff0c \u4f20\u6388\u5979\u4eec\u7b80\u6613\u7684\u5947\u672f\uff0c\u4fbf\u4e8e\u901f\u6210\u7684\u6b66\u529f\uff0c\u4ee5\u4f9b\u5dee\u9063\uff0c\u8d50\u540d\u83b2\u513f\u3001\u5de7\u513f\uff0c\u4e5f\u6539\u7a7f\u7740\u7d27\u8eab\u7684\u77ed\u886b \u957f\u88e4\uff0c\u6709\u522b\u4e8e\u4e00\u822c\u5bab\u5973\u3002 \u3000\u3000\u968b\u7080\u5e1d\u518d\u4e09\u7684\u8bf7\u6c42\u4e0b\uff0c\u8881\u7d2b\u70df\u4e5f\u53ea\u80af\u7559\u4e0b\u4e86\u516b\u4e2a\u7cbe\u4e8e\u7406\u4e8b\u7684\u5bab\u5973\uff0c\u7167\u987e\u5979\u7684\u751f\u6d3b\u8d77\u5c45\u3002 \u3000\u3000\u5341\u4e8c\u9662\u7684\u592b\u4eba\uff0c\u90fd\u6765\u62dc\u8bbf\u65b0\u5ba0\uff0c\u770b\u5230\u4e86\u201c\u9752\u83b2\u5c0f\u9986\u201d\u7684\u7b80\u964b\u5e03\u7f6e\uff0c\u5fc3\u4e2d\u751a\u611f\u8bb6\u5f02\uff0c\u4e5f\u5bf9 \u8881\u7d2b\u70df\u53e6\u6709\u4e00\u756a\u8bc4\u4ef7\u3002 \u3000\u3000\u8427\u7687\u540e\u4e5f\u6765\u4e86\uff0c\u8fd9\u4e2a\u98ce\u534e\u7edd\u4ee3\u7684\u5973\u4eba\uff0c\u786e\u6709\u7740\u4e0e\u4f17\u4e0d\u540c\u7684\u6c14\u8d28\u3001\u624d\u6167\u3002\u63a5\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u7687\u4e0a\u5929\u6027\u653e\u8361\uff0c\u54c0\u5bb6\u4e5f\u65e0\u610f\u963b\u6b62\uff0c\u53ea\u662f\u4e3a\u4ed6\u7684\u8eab\u4f53\u62c5\u5fe7\uff0c\u73b0\u4e0b\u7684\u60c5\u666f\uff0c\u662f\u4e00\u65e5\u65e0\u5b9d\u513f \u5c31\u9f99\u989c\u4e0d\u6b22\uff0c\u54c0\u5bb6\u771f\u662f\u5fe7\u5fc3\u5982\u711a\u4e86\u3002\u201d \u3000\u3000\u201c\u7687\u540e\uff0c\u8fd9\u65b9\u9762\u59be\u5a62\u5012\u53ef\u7565\u6548\u5fae\u52b3\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u59be\u5a62\u7c97\u901a\u533b\u9053\uff0c\u53ef\u4ee5\u5f00\u4e2a\u836f\u65b9\uff0c\u7531 \u7687\u540e\u51fa\u9762\uff0c\u714e\u716e\u4e4b\u540e\uff0c\u8ba9\u7687\u4e0a\u670d\u7528\u3002\u201d \u3000\u3000\u8427\u7687\u540e\u6709\u4e9b\u60ca\u559c\u7684\u9053\uff1a\u201c\u5f88\u6709\u7528\u5417\uff1f\u201d \u3000\u3000\u201c\u7acb\u7aff\u89c1\u5f71\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u4f1a\u5f88\u5feb\u611f\u89c9\u5230\u836f\u529b\u7684\u795e\u5999\uff01\u4e0d\u8fc7\uff0c\u59be\u5a62\u62c5\u5fc3\u7684\u662f\uff0c\u5982 \u6b64\u4e00\u6765\uff0c\u4f1a\u4e0d\u4f1a\u4f7f\u7687\u4e0a\u66f4\u96be\u81ea\u7981\u2026\u2026\u201d \u3000\u3000\u201c\u7d2b\u70df\uff0c\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\uff0c\u6551\u4ed6\u8981\u7d27\u3002\u201d\u8427\u7687\u540e\u5e7d\u5e7d\u7684\u8bf4\u9053\uff1a\u201c\u770b\u4ed6\u5f62\u8272\u65e5\u8870\uff0c\u54c0\u5bb6\u5fc3 \u4e2d\u6025\u75db\u4ea4\u4f5c\uff0c\u4f55\u51b5\uff0c\u5b9d\u513f\u7684\u5a07\u5a9a\uff0c\u5982\u78c1\u5438\u94c1\uff0c\u7687\u4e0a\u653e\u7eb5\uff0c\u5df2\u6210\u4e60\u60ef\uff0c\u5c31\u662f\u4e0d\u670d\u836f\u7269\uff0c\u4e5f\u96be\u81ea \u7981\uff0c\u90a3\u4e0d\u662f\u8981\u4ed6\u7684\u2026\u2026\u201d \u3000\u3000\u60f3\u8bf4\u8981\u4ed6\u7684\u6027\u547d\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u786c\u751f\u751f\u7684\u541e\u4e86\u4e0b\u53bb\uff0c\u5492\u8a00\u5e1d\u738b\u9f99\u9a7e\u65e9\u5d29\uff0c\u53ef\u662f\u6284\u5bb6\u706d \u95e8\u7684\u5927\u7f6a\u3002 \u3000\u3000\u8427\u7687\u540e\u4e5f\u4e0d\u6562\u5ba3\u8bf8\u4e8e\u53e3\u3002 \u3000\u3000\u201c\u59be\u5a62\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4eba\u7ea2\u4e86\uff0c\u771f\u80fd\u4f7f\u5929\u5730\u53d8\u8272\uff0c\u8427\u7687\u540e\u7ea1\u5c0a\u964d\u8d35\uff0c\u4e0d\u4f46\u4eb2\u6765\u9752\u83b2\u5c0f\u9986\u795d\u8d3a\uff0c\u8fd8\u8981\u548c\u8881\u7d2b\u70df\u624b \u5e15\u8bba\u4ea4\uff0c\u8fd9\u5c31\u4e0d\u662f\u5341\u4e8c\u9662\u592b\u4eba\u80fd\u591f\u5f97\u5230\u7684\u6b8a\u8363\u4e86\u3002 \u3000\u3000\u9001\u8d70\u4e86\u8427\u7687\u540e\uff0c\u8881\u7d2b\u70df\u9759\u4e0b\u6765\uff0c\u601d\u7d22\u65e5\u540e\u7684\u5904\u5883\uff0c\u9996\u5148\u8981\u5b87\u6587\u6210\u90fd\u548c\u4ed6\u51e0\u4e2a\u91cd\u8981\u7684\u526f\u7edf \u9886\uff0c\u5bf9\u5979\u5fc3\u60a6\u8bda\u670d\uff0c\u624d\u80fd\u4f7f\u4ed6\u4eec\u5fc3\u7518\u6548\u547d\uff0c\u8fd9\u5c31\u8981\u4e00\u4e9b\u624b\u6bb5\u4e86\uff0c\u5982\u4f55\u80fd\u5728\u4e0d\u4f24\u5bb3\u5bf9\u65b9\u7684\u5c0a\u4e25 \u4e0b\uff0c\u5c55\u73b0\u7edd\u6280\uff0c\u672f\u6cd5\uff0c\u5f97\u8981\u8d39\u756a\u601d\u91cf\uff0c\u8ba1\u5212\u5468\u8be6\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e5f\u53d1\u89c9\u4e86\u4e3a\u56fd\u6548\u547d\uff0c\u5e76\u4e0d\u662f\u5355\u7eaf\u7684\u548c\u654c\u4eba\u4ee5\u51b3\u751f\u6b7b\uff0c\u4eba\u4e8b\u5173\u7cfb\uff0c\u4e5f\u5fc5\u9700\u59a5\u5f53\u5e94 \u4ed8\uff0c\u624d\u80fd\u5316\u963b\u529b\u4e3a\u52a9\u529b\uff0c\u6c34\u5230\u6e20\u6210\u3002 \u3000\u3000\u5355\u662f\u6df1\u5bab\u5185\u82d1\u4e2d\uff0c\u8fd9\u4e9b\u6625\u82b1\u822c\u7684\u5983\u5b50\u3001\u592b\u4eba\uff0c\u8981\u5e94\u4ed8\u8d77\u6765\uff0c\u5c31\u53eb\u4eba\u5934\u5927\u5982\u6597\u3002 \u3000\u3000\u8881\u5b9d\u513f\u4e5f\u6765\u4e86\uff0c\u5e26\u6765\u4e86\u4e00\u9635\u9999\u98ce\u548c\u4eb2\u5207\u7684\u5fae\u7b11\u3002 \u3000\u3000\u5979\u6ca1\u6709\u4e00\u822c\u592b\u4eba\u3001\u8d35\u5983\u7684\u6c14\u52bf\uff0c\u5bab\u5973\u968f\u4ece\uff0c\u524d\u547c\u540e\u62e5\uff0c\u53ea\u5e26\u4e86\u4e00\u4e2a\u8d34\u8eab\u4e2b\u5934\uff0c\u800c\u4e14\uff0c\u8fd8 \u7559\u5728\u95e8\u5916\u7b49\u5019\uff0c\u8fdb\u6765\u7684\u53ea\u662f\u8881\u5b9d\u513f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u6211\u662f\u6765\u5411\u59d0\u59d0\u795d\u8d3a\u7684\u3002\u201d\u8881\u5b9d\u513f\u5fae\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u59b9\u8d44\u671b\u6d45\u8584\uff0c\u5165\u5bab\u4e0d\u8fc7\u4e00\u6708\uff0c\u4e0d\u6562\u548c \u4f17\u5bb6\u592b\u4eba\u3001\u8d35\u5983\u4e89\u957f\u77ed\uff0c\u62a2\u5148\u8d76\u6765\uff0c\u53ea\u597d\u5728\u4e00\u4fa7\u7b49\u5019\u3002\u201d \u3000\u3000\u8bf4\u7684\u597d\u59d4\u5a49\uff0c\u4e5f\u8868\u73b0\u4e86\u52a8\u4eba\u7684\u8bda\u6073\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fd6\u9053\uff1a\u5982\u6b64\u53ef\u4eba\u7684\u7f8e\u5973\uff0c\u5f62\u5c06\u89c6\u4f5c\u5bc7\u4ec7\uff0c\u771f\u662f\u4e00\u4ef6\u53ef\u60b2\u7684\u4e8b\uff0c\u662f\u4e0a\u5929\u7684\u6349\u5f04\uff1f\u8fd8 \u662f\u4eba\u4e3a\u7684\u60b2\u5267\uff1f \u3000\u3000\u5979\u53e3\u4e2d\u5374\u5e94\u9053\uff1a\u201c\u5148\u540e\u6709\u5e8f\uff0c\u957f\u5e7c\u9f7f\u5206\uff0c\u6211\u5e94\u8be5\u53eb\u4f60\u59d0\u59d0\u5462\uff0c\u8fd8\u662f\u59b9\u59b9\u5462\uff1f\u201d \u3000\u3000\u201c\u59b9\u59b9\u7f57\uff01\u201d\u8881\u5b9d\u513f\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u662f\u8bda\u5fc3\u6500\u4ea4\u800c\u6765\uff0c\u8fd8\u671b\u59d0\u59d0\u6709\u4ee5\u6559\u6211\uff0c\u75bc\u601c\u59b9\u5b50\uff01\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u5b9d\u513f\u59b9\u59b9\u3002\u201d\u8881\u7d2b\u70df\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6211\u65e0\u610f\u548c\u4f60\u4e89\u5ba0\u5bab\u5ef7\uff0c\u9053\u57fa\u6709\u6df1\u6d45\uff0c\u672f\u6cd5 \u5404\u4e0d\u540c\uff0c\u5c31\u7b97\u6211\u60f3\u548c\u4f60\u4e89\u4e2a\u4e00\u65e5\u957f\u77ed\uff0c\u6211\u4e5f\u6ca1\u6709\u4e89\u5ba0\u7684\u672c\u9886\u554a\uff01\u201d \u3000\u3000\u201c\u770b\u8d77\u6765\u7d2b\u70df\u59d0\u59d0\u5bf9\u5c0f\u59b9\u6709\u6240\u8bef\u4f1a\u3002\u201d\u8881\u5b9d\u513f\u51c4\u51c9\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5929\u98ce\u5439\u843d\u62a4\u82b1\u9732\uff0c\u5c24\u672a\u76db\u5f00\u5df2\u6210\u79cb\uff0c\u9053\u4eba\u4e0d\u662f\u60b2\u79cb\u5ba2\uff0c\u95f4\u5173\u4e07\u91cc\u76f8\u601d\u6101\uff01\u7d2b\u70df\u59d0\u59d0\uff0c \u5b88\u5f97\u4e91\u5f00\u6708\u660e\uff0c\u5e0c\u671b\u80fd\u7559\u4e2a\u89c1\u9762\u60c5\u7f18\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u8bf4\u7684\u5f88\u5766\u767d\u4e86\uff0c\u4e5f\u9053\u5c3d\u4e86\u5fc3\u4e2d\u7684\u5fe7\u82e6\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u60b8\u52a8\u4e86\uff0c\u8fd9\u4e2a\u7ea2\u7c89\u5c0f\u4f73\u4eba\u554a\uff01\u4e00\u8154\u60c5\u6000\uff0c\u6ee1\u8179\u6101\u80a0\uff0c\u548c\u5979\u7684\u5a07\u5a06\u7f8e\u8c8c\uff0c\u5b8c\u5168 \u4e0d\u76f8\u96f7\u540c\uff0c\u4e3a\u4ec0\u4e48\u5440\uff1f \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4f60\u597d\u50cf\u6709\u75db\u82e6\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u6253\u65ad\u4e86\u8881\u7d2b\u70df\u7684\u8bdd\u63a5\u9053\uff1a\u201c\u6211\u8981\u548c\u7d2b\u70df\u59d0\u503e\u5fc3\u76f8\u8c08\u2026\u2026\u201d \u3000\u3000\u201c\u7687\u4e0a\u9a7e\u5230\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u7684\u8f66\u8f87\u5df2\u5230\u4e86\u9752\u83b2\u5c0f\u9986\u7684\u95e8\u5916\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff01\u6709\u6687\u65f6\u8bf7\u5230\u2018\u743c\u82b1\u9662\u2019\u4e2d\u5c0f\u5750\u7247\u523b\uff0c\u5c0f\u59b9\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u4e5f\u8dd1\u5230\u8fd9\u91cc\u6765\u5566\uff01\u771f\u662f\u62e3\u65e5\u4e0d\u5982\u649e\u65e5\u554a\uff01\u5feb\u5feb\u6446\u4e0a\u9152\u5bb4\uff0c\u6715\u8981\u548c\u4e24\u4f4d\u7231\u5983\uff0c \u559d\u4e2a\u75db\u5feb\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u8fd9\u91cc\u592a\u5c0f\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u91cc\u592a\u5c0f\u4e86\uff0c\u5230\u5b9d\u5983\u7684\u2018\u743c\u82b1\u9662\u2019\u53bb\uff0c\u54c8\u54c8\uff01\u5929\u4e0a\u4eba\u95f4\u4e24\u7f8e\u5973\uff0c\u5c3d\u4e3a\u6715\u6536\u5165\u540e \u5bab\uff0c\u5929\u4e0b\u5feb\u4e50\u4e8b\uff0c\u83ab\u8fc7\u4e8e\u6b64\u2026\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u7d2b\u70df\u59d0\u6709\u5f88\u591a\u91cd\u8981\u4e8b\u5f85\u7406\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4f60\u8981\u601c\u60dc\u5979\uff0c\u522b\u8981\u628a\u5979\u7ed9\u7d2f\u574f \u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff01\u6715\u80fd\u517c\u5f97\u9c7c\u4e0e\u718a\u638c\uff0c\u5374\u4e0d\u80fd\u8ba9\u65e5\u6708\u540c\u65f6\u4e89\u8f89\u5929\u4e0a\u3002\u201d \u3000\u3000\u201c\u8bf4\u5230\u54ea\u513f\u53bb\u4e86\u3002\u201d\u8881\u5b9d\u513f\u7b11\u9053\uff1a\u201c\u7d2b\u70df\u59d0\u4ed9\u5bb9\u4e4b\u59ff\uff0c\u8fd8\u4e0d\u662f\u88ab\u4f60\u9738\u5360\u4e86\uff0c\u7687\u4e0a\uff0c\u8fd8\u4e0d \u77e5\u8db3\u554a\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u63a9\u53e3\u5fae\u7b11\uff0c\u72b9\u5e26\u4e00\u5206\u7f9e\u6da9\u3002 \u3000\u3000\u968b\u7080\u5e1d\u770b\u770b\u8881\u7d2b\u70df\uff0c\u53c8\u770b\u770b\u8881\u5b9d\u513f\uff0c\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e0a\u5929\u539a\u6211\uff0c\u4e0a\u5929\u539a\u6211\u5440\uff01\u6715 \u5e94\u8be5\u6ee1\u8db3\u4e86\uff0c\u6ee1\u8db3\u4e86\u2026\u2026\u6715\u5728\u60f3\u554a\u2026\u2026\u201d \u3000\u3000\u771f\u7684\u60f3\u5165\u795e\u4e86\uff0c\u5446\u5446\u7ad9\u7740\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u201c\u7687\u4e0a\uff0c\u60f3\u4ec0\u4e48\u5440\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u60f3\u7684\u90a3\u4e48\u5165\u795e\u3002\u201d \u3000\u3000\u201c\u60f3\u5165\u975e\u975e\u4e86\u3002\u201d\u8881\u5b9d\u513f\u4f4e\u58f0\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u6298\u817e\u4eba\u7684\u82b1\u6837\uff0c\u8ba9\u4eba\u53c8\u7f9e\u53c8\u6015\u3002\u201d \u3000\u3000\u201c\u6715\u5728\u60f3\uff01\u6715\u5728\u60f3\u2026\u2026\u201d \u3000\u3000\u8bf4\u4e0d\u51fa\u53e3\u4e86\uff0c\u4f46\u8138\u4e0a\u5374\u6cdb\u8d77\u5174\u594b\u7684\u7b11\u610f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6447\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u8bf4\u5427\uff01\u6709\u4ec0\u4e48\u4e0d\u6562\u8bf4\u7684\uff0c\u4f60\u662f\u7687\u4e0a\u554a\uff01\u201d \u3000\u3000\u201c\u4ed6\u6709\u70b9\u6015\u4f60\uff0c\u7d2b\u70df\u59d0\uff0c\u4f60\u662f\u5c0f\u5ae1\u4eba\u95f4\u7684\u4ed9\u5b50\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7687\u4e0a\u60f3\u7684\u5f88\u90aa\u6c14\uff0c\u4e0d\u6562 \u5f53\u4f60\u9762\u8bf4\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u6715\u6df1\u7231\u4e24\u4f4d\u537f\u5bb6\uff0c\u7231\u5f97\u592a\u6df1\u4e86\uff0c\u5012\u4e5f\u662f\u6709\u70b9\u754f\u60e7\u3002\u201d \u3000\u3000\u5f53\u9762\u627f\u8ba4\u4e86\u3002 \u3000\u3000\u201c\u6715\u662f\u5426\u8981\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u81ea\u8a00\u81ea\u8bed\uff0c\u5374\u53c8\u50cf\u5f81\u8be2\u4e24\u4eba\u7684\u610f\u89c1\u3002 \u3000\u3000\u201c\u8bf4\u5427\uff01\u6211\u4eec\u6d17\u8033\u4ee5\u5f85\uff0c\u51c6\u5907\u606d\u8046\u96c5\u6559\u5462\uff1f\u201d\u8881\u7d2b\u70df\u597d\u5947\u7684\u8bf4\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u5374\u597d\u50cf\u65e9\u77e5\u9053\u4e86\uff0c\u5a07\u5a9a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6015\u662f\u96c5\u4e0d\u8d77\u6765\u5440\uff01\u8981\u662f\u98ce\u96c5\u4e8b\uff0c\u5c31\u65e9\u8bf4 \u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u867d\u4e0d\u592a\u96c5\uff0c\u53ef\u4e5f\u4e0d\u662f\u5f88\u90aa\uff0c\u6715\u5728\u60f3\uff0c\u5982\u80fd\u548c\u4e24\u4f4d\u537f\u5bb6\u5171\u5bbf\u4e8e\u4e00\u5f20\u9f99\u5e8a\u4e4b\u4e0a\uff0c\u90a3\u79cd\u5de6\u62e5 \u53f3\u62b1\u4e4b\u4e50\uff0c\u624d\u662f\u4eba\u95f4\u7684\u81f3\u4e50\u3002\u201d \u3000\u3000\u201c\u90aa\u7684\u5389\u5bb3\u5440\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u8fd8\u771f\u80fd\u8bf4\u5f97\u51fa\u53e3\uff1f\u201d \u3000\u3000\u201c\u6715\u672c\u6765\u4e0d\u8bf4\u7684\uff01\u62e5\u5728\u5fc3\u91cc\u60f3\u60f3\u7b97\u4e86\uff0c\u662f\u4f60\u4eec\u903c\u6211\u8bf4\u7684\uff01\u600e\u80fd\u602a\u6715\u5462\uff1f\u201d \u3000\u3000\u201c\u8010\u5fc3\u70b9\u7b49\u7740\u5427\uff01\u201d\u8881\u5b9d\u513f\u7b11\u9053\uff1a\u201c\u7d2b\u70df\u59d0\u59d0\uff0c\u5fd9\u5b8c\u4e86\u56fd\u5bb6\u5927\u4e8b\uff01\u7687\u4e0a\u518d\u6c42\u5979\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u8981\u8d70\u4e86\u3002\u201d\u968b\u7080\u5e1d\u6709\u4e9b\u5403\u60ca\u7684\u8bf4\uff1a\u201c\u600e\u4f1a\u5982\u6b64\u4e00\u4e2a\u5feb\u6cd5\uff0c\u5b87\u6587\u6210\u90fd\u68c0\u70b9\u5175\u9a6c\uff0c\u603b \u4e5f\u8981\u5341\u5929\u534a\u4e2a\u6708\u5427\uff01\u201d \u3000\u3000\u201c\u592a\u6162\u4e86\uff0c\u59be\u5a62\u660e\u65e5\u4f1a\u89c1\u5b87\u6587\u5c06\u519b\uff0c\u4e09\u65e5\u5185\u4e00\u5b9a\u52a8\u8eab\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u7b49\u59be\u5fcd\u6c14 \u541e\u58f0\u5317\u5f81\u5f52\u6765\uff0c\u5378\u4e0b\u5f81\u8863\u6c90\u9999\u6c64\uff0c\u8584\u65bd\u8102\u7c89\u4f8d\u541b\u738b\uff0c\u7ea2\u8896\u6dfb\u9999\u591c\u8bfb\u4e66\uff0c\u753b\u7709\u4e4b\u4e50\u5929\u5730\u957f\u3002\u7687 \u4e0a\uff0c\u5c0f\u522b\u80dc\u65b0\u5a5a\u554a\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\uff01\u62a4\u56fd\u5317\u4e0a\uff0c\u603b\u5f97\u8981\u4e09\u4e94\u4e2a\u6708\u4e86\u3002\u201d\u968b\u7080\u5e1d\u671b\u7740\u8881\u7d2b\u70df\uff0c\u65e0\u9650\u7231\u604b\u5730\u8bf4\uff1a\u201c\u4ee5\u4f60 \u5343\u5a07\u767e\u5a9a\u4e4b\u8eab\uff0c\u7eb5\u9a6c\u9a70\u9a8b\u4e8e\u4e07\u91cc\u98ce\u6c99\u4e4b\u4e2d\uff0c\u6715\u5fc3\u600e\u5fcd\u5462\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\u591a\u60c5\uff0c\u611f\u4e8e\u5fc3\uff0c\u56fd\u4e8b\u4e3a\u91cd\uff0c\u7687\u4e0a\u5c31\u5fcd\u8010\u51e0\u4e2a\u6708\u5427\uff01\u201d \u3000\u3000\u201c\u6715\u53ef\u4ee5\u5fcd\u53d7\u6570\u6708\u76f8\u601d\u4e4b\u82e6\uff0c\u53ef\u662f\uff0c\u7d2b\u70df\uff0c\u6715\u73b0\u5728\u8981\u6c42\u7684\uff0c\u53ea\u662f\u4e00\u5915\u4e4b\u6b22\uff0c\u537f\u5bb6\u4f55\u4e0d\u6210 \u5168\u4e86\u6715\u5462\uff1f\u201d \u3000\u3000\u7ed5\u4e86\u4e2a\u5927\u5708\u5b50\uff0c\u53c8\u56de\u5230\u4e86\u5de6\u62e5\u53f3\u62b1\u7684\u5e8a\u4e8b\u4e0a\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5446\u4f4f\u4e86\uff0c\u8981\u5979\u88f8\u4f53\u76f8\u88ce\uff0c\u548c\u8881\u5b9d\u513f\u5171\u4e8b\u541b\u738b\u4e8e\u4e00\u5f20\u5927\u5e8a\u4e4b\u4e0a\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u4ee4\u5979\u4e3a \u96be\u7684\u4e8b\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u806a\u660e\u83ab\u8fc7\u5e1d\u738b\u3002\u201d\u8881\u5b9d\u513f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6597\u4e0d\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u5bf9\u540c\u5e8a\u5171\u4f8d\u4e00\u541b\u738b\uff0c\u8fd9\u7801\u5b50\u4e8b\uff0c\u8881\u5b9d\u513f\u4f3c\u4e4e\u662f\u5e76\u4e0d\u53cd\u5bf9\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f\u5979\u662f\u522b\u6709\u7528\u5fc3\u4e4e\uff1f \u8fd8\u662f\u8981\u4e00\u7aa5\u8881\u7d2b\u70df\u7684\u88f8\u4f53\u4e4b\u7f8e\uff0c\u662f\u5426\u548c\u5979\u7684\u5f62\u8c8c\u4e00\u6837\u52a8\u4eba\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u537f\u5bb6\u601c\u6715\u4e00\u7247\u75f4\u5ff5\uff0c\u60f3\u662f\u4e0d\u4f1a\u62d2\u6715\u4e8e\u5343\u91cc\u4e4b\u5916\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7684\u59d4\u5a49\u52a8\u4eba\uff0c\u75f4\u7684\u53ef\u7b11\u53c8\u53ef\u601c\uff0c\u53ef\u662f\uff0c\u975e\u5e38\u6709\u6548\u554a\uff01 \u3000\u3000\u8881\u7d2b\u70df\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7f20\u4eba\u5440\uff01\u65e9\u665a\u8981\u88ab\u4f60\u6298\u817e\u7684\u9b42\u516e\u5f52\u6765\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u59d0\u7b54\u5e94\u4e86\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u4e9b\u8c22\u8fc7\u7d2b\u70df\u59d0\u7684\u5927\u5ea6\u5305\u5bb9\uff0c\u7687\u4e0a\uff0c\u6253\u94c1\u4e0d\u8d81 \u70ed\uff0c\u51b7\u4e86\u5c31\u6253\u4e0d\u52a8\u4e86\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u8fd8\u771f\u7684\u542c\u8bdd\uff0c\u884c\u8fd1\u8881\u7d2b\u70df\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8c22\u8fc7\u537f\u5bb6\u7684\u539a\u7231\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u55e4\u5730\u4e00\u58f0\uff0c\u7b11\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u7687\u5e1d\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\u6765\uff0c\u4e0d\u7b11\u53c8\u80fd\u600e\u4e48\u6837\u5462\uff1f\u603b\u4e0d\u80fd\u5927\u653e\u60b2\u58f0\u7684\u54ed\u4e00\u573a\u5427\uff01 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e24\u4e2a\u5c0f\u7f8e\u4eba\u5404\u5448\u5fc3\u673a\uff0c\u4e00\u5f39\u4e00\u5531\uff0c\u771f\u4e0d\u77e5\u9053\u662f\u968b\u7080\u5e1d\u6298\u817e\u5979\u4eec\u5462\uff1f\u8fd8\u662f\u5979\u4eec\u5728 \u6349\u5f04\u7687\u5e1d\u3002 \u3000\u3000\u90a3\u4e00\u591c\uff0c\u9f99\u5e8a\u4e0a\u53cc\u51e4\u4f34\u51f0\uff0c\u968b\u7080\u5e1d\u4eab\u53d7\u4e86\u4eba\u95f4\u7684\u81f3\u4e50\u3002 \u3000\u3000\u4e00\u591c\u6619\u82b1\u5306\u5306\u901d\uff0c\u53ea\u7559\u4f59\u97f5\u5171\u54c1\u8bc4\uff0c\u660e\u6708\u666e\u7167\u4e09\u5343\u5ba2\uff0c\u4e5f\u6709\u7f8e\u5973\u4e5f\u6709\u50e7\u3002 \u3000\u3000\u660e\u6708\u4e00\u6837\u7167\u6c9f\u6e20\uff0c\u4e5f\u7167\u5bd2\u6c5f\u4e00\u9493\u7fc1\uff0c\u591a\u5c11\u5a06\u5a07\u98ce\u6d41\u4e8b\uff0c\u5c3d\u4ed8\u660e\u6708\u4e00\u7167\u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u5b87\u6587\u6210\u90fd\u7edf\u9886\u7684\u603b\u90e8\uff0c\u5bab\u5899\u5916\u4e00\u5927\u7247\u9752\u7816\u74e6\u820d\u3002 \u3000\u3000\u5bbd\u655e\u7684\u5385\u5802\u4e0a\uff0c\u7ad9\u7acb\u7740\u5341\u4e8c\u4e2a\u62ab\u7532\u6b66\u58eb\uff0c\u5b87\u6587\u6210\u90fd\u4eb2\u7387\u4e86\u56db\u4e2a\u526f\u7edf\u9886\u5750\u5728\u4e0b\u9996\u4e00\u6392\u592a\u5e08 \u6905\u4e0a\u3002 \u3000\u3000\u56db\u4e2a\u526f\u7edf\u9886\u90fd\u662f\u5b87\u6587\u6210\u90fd\u7684\u4eb2\u4fe1\uff0c\u4e5f\u662f\u5341\u5927\u526f\u7edf\u9886\u4e2d\u6b66\u529f\u6700\u5f3a\u7684\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u4e0a\u9996\u5462\uff1f\u4e0a\u9996\u5750\u7740\u8881\u7d2b\u70df\uff1b\u8eab\u540e\u5206\u7ad9\u7740\u83b2\u513f\u3001\u5de7\u513f\u3002 \u3000\u3000\u5385\u5802\u4e2d\uff0c\u9e26\u96c0\u65e0\u58f0\u3002 \u3000\u3000\u6c14\u6c1b\u8083\u7a46\uff0c\u6240\u6709\u7684\u76ee\u5149\u90fd\u6295\u6ce8\u5728\u8881\u7d2b\u70df\u7684\u8138\u4e0a\uff0c\u770b\u6765\u90fd\u5bf9\u5979\u5341\u5206\u656c\u91cd\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e9b\u4eba\u90fd\u5728\u9971\u9910\u79c0\u8272\u3002 \u3000\u3000\u8fd9\u4e9b\u8d73\u8d73\u6b66\u592b\uff0c\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u662f\u65e0\u6cd5\u7528\u7f8e\u4e3d\u7edf\u9a6d\u7684\uff0c\u5fc5\u9700\u8981\u771f\u624d\u5b9e\u5b66\uff0c\u624d\u80fd\u591f\u4f7f\u4ed6\u4eec\u5fc3\u60a6 \u8bda\u670d\u3002 \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5728\u3002\u201d\u5b87\u6587\u6210\u90fd\u7ad9\u8d77\u5b50\u8eab\u5b50\u3002 \u3000\u3000\u5e1d\u738b\u7684\u8bcf\u65e8\uff0c\u4f7f\u5b87\u6587\u603b\u7edf\u9886\uff0c\u5b98\u964d\u4e00\u7ea7\uff0c\u4e5f\u6210\u4e86\u526f\u603b\u7edf\u9886\u3002 \u3000\u3000\u8bf4\u4ed6\u5fc3\u4e2d\u4e0d\u670d\u6c14\u5427\uff01\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u8083\u7136\u800c\u8d77\uff0c\u4e00\u7247\u606d\u656c\u795e\u8272\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c06\u519b\u4ecd\u662f\u5bab\u536b\u603b\u7edf\u9886\uff0c\u7d2b\u70df\u4ee5\u62a4\u56fd\u6cd5\u5e08\u8eab\u4efd\uff0c\u968f\u884c\u76d1\u519b\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u7687\u4e0a\u5df2\u9881\u4e0b\u8bcf\u65e8\uff0c\u541b\u4ee4\u5982\u5c71\u2026\u2026\u201d \u3000\u3000\u201c\u7687\u4e0a\u90a3\u91cc\u7531\u6211\u53bb\u8bf4\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5c06\u519b\u4f46\u8bf7\u5b89\u5fc3\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6210\u90fd\u9075\u547d\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\u8bf7\u5750\u3002\u201d\u8881\u7d2b\u70df\u7684\u76ee\u5149\u626b\u63a0\u8fc7\u5341\u4e8c\u4e2a\u62ab\u7532\u6b66\u58eb\uff0c\u9053\uff1a\u201c\u7d2b\u70df\u7a81\u7136\u4ee5\u5bab\u4e2d\u5973\u5b98\u8eab \u4efd\uff0c\u7edf\u7387\u8bf8\u4f4d\u82f1\u96c4\u58ee\u58eb\uff0c\u8bf8\u4f4d\u6709\u4ec0\u4e48\u7591\u8651\u4e4b\u5904\uff0c\u6211\u4f1a\u7ed9\u8bf8\u4f4d\u4e00\u4e2a\u6ee1\u610f\u7684\u56de\u7b54\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\uff0c\u7687\u4e0a\u8bcf\u65e8\uff0c\u8c01\u6562\u8fdd\u6297\uff0c\u6210\u90fd\u65e2\u5f52\u5165\u9ebe\u4e0b\uff0c\u4ed6\u4eec\u600e\u4f1a\u4e0d\u670d\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\uff0c\u6211\u8981\u7684\u662f\u4ed6\u4eec\u5fc3\u7518\u60c5\u613f\uff0c\u6b64\u540e\uff0c\u950b\u955d\u76f8\u4ece\uff0c\u751f\u6b7b\u4e0e\u5171\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5982\u4e0d\u80fd \u63a8\u5fc3\u7f6e\u8179\uff0c\u771f\u8bda\u76f8\u5bf9\uff0c\u90a3\u5c31\u5f88\u96be\u594b\u52c7\u5411\u524d\uff0c\u6218\u65e0\u4e0d\u80dc\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u5fc3\u6709\u7591\u8651\u7684\uff0c\u5c31\u5411\u56fd\u5e08\u8bf7\u6559\u5427\uff01\u201d\u76ee\u5149\u7f13\u7f13\u7531 \u56db\u5927\u526f\u7edf\u9886\u7684\u8138\u4e0a\u626b\u8fc7\u3002 \u3000\u3000\u8fd9\u662f\u5bab\u536b\u94c1\u9a91\u4e2d\uff0c\u56db\u4e2a\u6700\u96be\u7f20\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u5bf9\u8881\u7d2b\u70df\u53d6\u4ee3\u5b57\u6587\u6210\u90fd\u7684\u4e8b\uff0c\u65e9\u5df3\u5fc3\u6000\u4e0d \u6ee1\uff0c\u867d\u7136\u8881\u7d2b\u70df\u5df2\u53d6\u6d88\u4e86\u5723\u65e8\u9881\u793a\uff0c\u8ba9\u5b87\u6587\u6210\u90fd\u5b98\u590d\u539f\u804c\uff0c\u4f46\u662f\u4ecd\u7136\u5728\u5979\u62a4\u56fd\u6cd5\u5e08\u7684\u7edf\u7387\u4e4b \u4e0b\u3002 \u3000\u3000\u7537\u5b50\u6c49\u3001\u5927\u4e08\u592b\u8981\u5728\u4e00\u4e2a\u7ea2\u7c89\u4f73\u4eba\u624b\u4e0b\u542c\u547d\uff0c\u5fc3\u4e2d\u81ea\u4e0d\u670d\u6c14\uff0c\u5b87\u6587\u6210\u90fd\u65e2\u7136\u4e0d\u963b\u6b62\u4e86\uff0c \u90a3\u5c31\u501f\u673a\u4f1a\u8003\u8003\u5979\u8fd9\u62a4\u56fd\u5927\u6cd5\u5e08\uff0c\u662f\u4e0d\u662f\u540d\u7b26\u5176\u5b9e\uff0c\u771f\u6709\u62a4\u56fd\u4e4b\u80fd\u3002 \u3000\u3000\u201c\u672b\u5c06\u5c71\u540e\u5218\u98de\u9e4f\uff0c\u8bf7\u6559\u56fd\u5e08\uff0c\u51c6\u5907\u4ee5\u4f55\u79cd\u624b\u6bb5\uff0c\u7edf\u7387\u6211\u7b49\uff0c\u4e89\u9738\u6c5f\u6e56\uff0c\u9010\u9e7f\u4e2d\u539f\uff1f\u201d \u3000\u3000\u8bed\u6c14\u4e2d\u867d\u7136\u6709\u70b9\u5fff\u6168\uff0c\u53ef\u4e5f\u7559\u6709\u4f59\u5730\uff0c\u5e76\u672a\u63d0\u51fa\u76f4\u63a5\u7684\u6311\u6218\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5218\u5c06\u519b\u5e0c\u671b\u6211\u4ee5\u4ec0\u4e48\u624b\u6bb5\u7edf\u9886\u8bf8\u4f4d\u5462\uff1f\u201d \u3000\u3000\u6b65\u6b65\u903c\u8fd1\uff0c\u662f\u8bda\u5fc3\u6311\u8d77\u5bf9\u65b9\u7684\u6012\u706b\u4e86\u3002 \u3000\u3000\u679c\u7136\uff0c\u5218\u98de\u9e4f\u6309\u8010\u4e0d\u4f4f\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5982\u679c\u672b\u5c06\u76f4\u8a00\uff0c\u56fd\u5e08\u8981\u62ff\u51fa\u4e00\u4e9b\u672c\u9886\u51fa\u6765\uff0c\u624d \u80fd\u4f7f\u6211\u7b49\u5fc3\u670d\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u6709\u7406\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f46\u827a\u5206\u767e\u5bb6\uff0c\u5404\u6709\u77ed\u957f\uff0c\u5218\u5c06\u519b\u4f55\u4e0d\u63d0\u51fa\u4e00\u4e2a\u660e\u786e\u7684\u529e \u6cd5\uff0c\u8ba9\u7d2b\u70df\u9886\u6559\u4e00\u4e0b\u5462\uff1f\u6700\u597d\u662f\u5218\u5c06\u519b\u6700\u7cbe\u6e5b\u7684\u6280\u827a\uff0c\u4e00\u6b21\u4fbf\u80fd\u5206\u51fa\u80dc\u8d1f\uff01\u201d \u3000\u3000\u8fd9\u662f\u6311\u6218\u5566\uff01 \u3000\u3000\u5218\u98de\u9e4f\u9ad8\u5927\u7684\u8eab\u8eaf\u5fae\u5fae\u98a4\u6296\uff0c\u663e\u7136\u5fc3\u4e2d\u5df2\u7ecf\u5192\u51fa\u6fc0\u70c8\u7684\u6012\u706b\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56fd\u5e08\uff0c \u672b\u5c06\u7cbe\u901a\u5341\u4e8c\u628a\u8fde\u73af\u98de\u5200\uff0c\u53ea\u4e0d\u8fc7\u98de\u5203\u65e0\u773c\uff0c\u4f24\u4e86\u56fd\u5e08\uff0c\u5982\u4f55\u5411\u5b87\u6587\u7edf\u9886\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u63d0\u7687\u4e0a\uff0c\u53ea\u63d0\u5b87\u6587\u6210\u90fd\uff0c\u663e\u89c1\u5b87\u6587\u6210\u90fd\u5728\u4ed6\u5fc3\u4e2d\u5730\u4f4d\uff0c\u662f\u4f55\u7b49\u5d07\u9ad8\u4e86\u3002 \u3000\u3000\u201c\u98de\u9e4f\uff0c\u662f\u5207\u78cb\u4e00\u4e0b\u6280\u827a\u5440\uff01\u201d\u5b87\u6587\u6210\u90fd\u6025\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u65bd\u5c55\u8fde\u73af\u98de\u5200\u3002\u201d \u3000\u3000\u201c\u4e0d\u59a8\u4e8b\uff0c\u5b87\u6587\u5c06\u519b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u5f88\u60f3\u89c1\u8bc6\u4e00\u4e0b\uff0c\u4ec0\u4e48\u53eb\u8fde\u73af\u98de\u5200\uff1f\u201d \u3000\u3000\u201c\u56fd\u5e08\u4e00\u518d\u76f8\u903c\uff0c\u672b\u5c06\u53ea\u597d\u653e\u8086\u4e86\u3002\u201d\u5218\u98de\u9e4f\u63a5\u4e86\u53e3\u3002 \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u5218\u5c06\u519b\u3002\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u8bf7\u5c06\u519b\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u5218\u98de\u9e4f\u7f13\u7f13\u89e3\u4e0b\u8170\u95f4\u4e00\u6761\u5bbd\u5927\u7684\u76ae\u5e26\uff0c\u62c9\u5f00\u5e03\u5957\uff0c\u5341\u4e8c\u628a\u7cbe\u5149\u8000\u76ee\u7684\u98de\u5200\uff0c\u8d6b\u7136\u51fa\u73b0\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u6210\u540d\u7684\u5229\u5668\uff0c\u5411\u4e0d\u79bb\u8eab\uff0c\u53ea\u4e0d\u8fc7\u7528\u4e00\u4e2a\u9ed1\u5e03\u5957\u5957\u4e86\u8d77\u6765\uff0c\u675f\u5728\u8170\u95f4\uff0c\u5c31\u5f88\u96be\u770b\u5f97 \u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u8bf7\u56fd\u5e08\u79fb\u9a7e\u5385\u4e2d\uff0c\u672b\u5c06\u4e5f\u597d\u732e\u4e11\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u7684\u6012\u706b\u5df2\u7184\uff0c\u8bed\u58f0\u4e5f\u89c1\u51b7\u9759\uff0c\u679c\u7136\u662f\u7528\u5200\u7684\u9ad8\u624b\uff0c\u5200\u5df2\u5728\u4e4e\uff0c\u7acb\u523b\u590d\u5e38\u3002 \u3000\u3000\u201c\u7d2b\u70df\u5c31\u5750\u5728\u5385\u91cc\uff0c\u63a5\u4e0b\u5c06\u519b\u7684\u98de\u5200\uff0c\u83b2\u513f\u3001\u5de7\u513f\uff0c\u4f60\u4eec\u9000\u51fa\u4e00\u4e08\u3002\u201d \u3000\u3000\u4e8c\u5973\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u9000\u51fa\u4e08\u5916\u3002 \u3000\u3000\u592a\u6258\u5927\u4e86\uff0c\u5b87\u6587\u6210\u90fd\u62c5\u8d77\u5fc3\u6765\uff0c\u4ed6\u77e5\u9053\u8881\u7d2b\u70df\u662f\u7687\u4e0a\u7684\u65b0\u5ba0\uff0c\u4f24\u4e86\u5979\uff0c\u8fd9\u4e2a\u7f6a\u540d\uff0c\u8c01\u4e5f \u62c5\u5f85\u4e0d\u8d77\u3002 \u3000\u3000\u53ef\u662f\uff0c\u600e\u4e48\u529e\u5462\uff1f\u8239\u81f3\u6c5f\u5fc3\uff0c\u5f13\u62c9\u6ee1\u6708\uff0c\u5df2\u7ecf\u65e0\u6cd5\u963b\u6b62\u4e86\uff0c \u3000\u3000\u4ed6\u53ea\u6709\u6697\u4f5c\u6212\u5907\uff0c\u5f85\u8881\u7d2b\u70df\u8eab\u9677\u5371\u5883\u65f6\uff0c\u5c31\u6697\u4e2d\u52a9\u5979\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u5218\u98de\u9e4f\u53d6\u51fa\u4e86\u4e24\u628a\u98de\u5200\uff0c\u5206\u63e1\u4e24\u624b\u3002 \u3000\u3000\u8fd9\u8868\u793a\u4ed6\u53cc\u624b\u90fd\u80fd\u65bd\u653e\u98de\u5200\u3002 \u3000\u3000\u201c\u56fd\u5e08\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8bdd\u51fa\u53e3\uff0c\u4e24\u67c4\u98de\u5200\u5206\u5411\u5de6\u53f3\u6295\u51fa\u3002 \u3000\u3000\u5dee\u7684\u592a\u8fdc\u4e86\uff01 \u3000\u3000\u8881\u7d2b\u70df\u5750\u5728\u4ed6\u7684\u6b63\u5bf9\u9762\uff0c\u4e24\u628a\u98de\u5200\uff0c\u5206\u5411\u5de6\u53f3\u4e24\u8fb9\u98de\uff0c\u96be\u9053\u98de\u5200\u8fd8\u4f1a\u8f6c\u5f2f\u4e0d\u6210\uff1f \u3000\u3000\u5947\u602a\uff0c\u98de\u5200\u771f\u7684\u8f6c\u5f2f\u4e86\uff0c\u53cc\u5200\u4e00\u4e2a\u5927\u56de\u65cb\u3002\u98de\u5411\u4e86\u8881\u7d2b\u70df\uff0c\u800c\u4e14\u901f\u5ea6\u4e5f\u5feb\u4e86\uff0c\u4e00\u95ea\u800c \u81f3\u3002 \u3000\u3000\u8fd9\u662f\u56de\u98ce\u5200\uff0c\u8868\u73b0\u51fa\u4e86\u53d1\u5200\u4eba\u7684\u624b\u6cd5\uff0c\u5de7\u52b2\uff0c\u90fd\u5df2\u5165\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8fd8\u5728\u5fae\u7b11\uff0c\u7b11\u7684\u5b87\u6587\u6210\u90fd\u5fc3\u5934\u53d1\u6bdb\uff0c\u6697\u6697\u53eb\u9053\uff1a\u201c\u59d1\u5a18\u554a\uff01\u8fd9\u662f\u8981\u547d\u73a9\u827a\u5440\uff01\u4f60 \u8fd8\u7b11\u7684\u51fa\u6765\u3002\u201d \u3000\u3000\u5200\u8fd1\u4e24\u9762\u592a\u9633\u7a74\u4e24\u5c3a\u5de6\u53f3\uff0c\u5218\u98de\u9e4f\u4e5f\u5413\u5446\u4e86\uff0c\u600e\u4e48\u4e0d\u65e9\u8eb2\u5440\uff01\u5200\u5982\u95ea\u7535\uff0c\u73b0\u5728\uff0c\u8eb2\u4e5f\u6765 \u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u62ac\u8d77\u4e86\u53cc\u624b\uff0c\u4e24\u53ea\u7ea4\u5de7\u7684\u7f8e\u4e3d\u7684\u624b\uff0c\u5c16\u5c16\u7684\u624b\u6307\uff0c\u96ea\u767d\u7684\u7389\u8155\u3002 \u3000\u3000\u5929\u554a\uff01\u5982\u6b64\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u5c31\u8981\u6e85\u8840\u5728\u98de\u5200\u4e4b\u4e0b\uff0c\u5218\u98de\u9e4f\u540e\u6094\u6781\u4e86\u3002 \u3000\u3000\u98de\u5200\u5ffd\u7136\u4e0d\u89c1\u4e86\uff0c\u539f\u6765\u5df2\u634f\u5728\u8881\u7d2b\u70df\u53cc\u624b\u98df\u3001\u4e2d\u4e24\u6307\u95f4\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4ecd\u7136\u5728\u7b11\uff0c\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u7531\u6a31\u6843\u5c0f\u53e3\u4e2d\u5a49\u8f6c\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5218\u5c06\u519b\uff0c\u98de\u5200\u6253\u9020\u7684\u5f88 \u597d\uff0c\u767e\u70bc\u7cbe\u94a2\uff0c\u950b\u5229\u65e0\u5339\uff0c\u4e22\u4e86\u53ef\u60dc\u5440\uff01\u597d\u597d\u7684\u6536\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u7389\u8155\u8f7b\u6325\uff0c\u4e24\u67c4\u5200\u5189\u5189\u98de\u51fa\u3002 \u3000\u3000\u795e\u5566\uff01\u4e0d\u504f\u4e0d\u659c\uff0c\u5c31\u90a3\u4e48\u5de7\u5999\u7684\u6295\u5165\u4e86\u653e\u5728\u6728\u684c\u4e0a\u7684\u76ae\u5957\u4e2d\u3002 \u3000\u3000\u76ae\u5957\u4e0a\u53ea\u6709\u5341\u4e8c\u4e2a\u63d2\u5200\u7684\u5200\u773c\uff0c\u5341\u628a\u5200\u8fd8\u5728\u5957\u4e2d\uff0c\u53ea\u6709\u4e24\u4e2a\u7a7a\u4f4d\uff0c\u4e24\u628a\u5200\u5c31\u662f\u5f52\u8fd8\u5728\u4e24 \u4e2a\u7a7a\u4f4d\u4e2d\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6697\u6697\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u662f\u6b66\u529f\u554a\uff0c\u8fd8\u662f\u6cd5\u672f\uff0c\u63a5\u5200\u7684\u624b\u6cd5\u4e0d\u96be\uff0c\u5b87\u6587\u6210 \u90fd\u81ea\u4fe1\u4e5f\u80fd\u529e\u5230\uff0c\u4f46\u6295\u5165\u4e00\u4e08\u5f00\u5916\u7684\u76ae\u5957\u5200\u773c\u4e2d\uff0c\u5c31\u5168\u65e0\u628a\u63e1\u4e86\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u5413\u5446\u4e86\uff0c\u770b\u770b\u5341\u4e8c\u628a\u76ae\u5957\u4e2d\u7684\u98de\u5200\uff0c\u5b8c\u5168\u4e00\u6837\uff0c\u4f3c\u4e4e\u662f\u6839\u672c\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u201c\u5927\u5f00\u773c\u754c\u4e86\u3002\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u672b\u5c06\u5e94\u8be5\u8ba4\u8f93\uff0c\u53ea\u662f\u6280\u827a\u5c1a\u672a\u5b8c\u5168\u53d1\u6325\uff0c\u56fd\u5e08\u53ef\u5426\u5bb9\u672a \u5c06\u518d\u653e\u8086\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u8bed\u6c14\u5e73\u548c\uff0c\u662f\u771f\u7684\u670d\u6c14\uff0c\u4f46\u53c8\u4e0d\u60f3\u7559\u4e0b\u4e00\u70b9\u9057\u61be\uff0c\u672a\u51fa\u5168\u529b\u5c31\u670d\u8f93\u3002 \u3000\u3000\u201c\u5218\u5c06\u519b\u4e0d\u7528\u5ba2\u6c14\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u660e\u767d\u5c06\u519b\u624b\u4e0b\u7559\u60c5\uff0c\u7edd\u6280\u5c1a\u672a\u65bd\u5c55\uff0c\u4f55\u4e0d\u5168\u529b\u53d1 \u5200\uff0c\u5c55\u73b0\u51fa\u5341\u4e8c\u8fde\u73af\u7684\u5a01\u52bf\uff0c\u8ba9\u6211\u4e5f\u5f00\u5f00\u773c\u754c\u5462\uff1f\u201d \u3000\u3000\u201c\u56fd\u5e08\u3002\u201d\u5b87\u6587\u6210\u90fd\u8138\u8272\u53c8\u53d8\u4e86\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5200\u5982\u8fde\u73af\u51b7\u98ce\u8d77\uff0c\u5149\u4f3c\u95ea\u7535\u8fc7\u957f\u7a7a\uff0c\u6211\u4eec \u5df2\u89c1\u8bc6\u8fc7\u56fd\u5e08\u7684\u8fd8\u5200\u5947\u6280\u4e0d\u7528\u518d\u8bd5\u4e86\u3002\u201d \u3000\u3000\u4ed6\u65e0\u7f18\u83b7\u5f97\u8fd9\u4e2a\u4ed9\u5973\u4e4b\u59ff\u7684\u5c11\u5973\u9752\u7750\uff0c\u56e0\u4e3a\uff0c\u4e0d\u80fd\u8ddf\u7687\u5e1d\u4e89\u554a\uff01\u4f46\u5fc3\u4e2d\u7684\u503e\u6155\u4e4b\u60c5\uff0c\u5374 \u5df2\u718a\u718a\u71c3\u8d77\uff0c\u5b9e\u5728\u6015\u8881\u7d2b\u70df\u53d7\u5230\u4f24\u5bb3\u3002 \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\uff0c\u4e0d\u7528\u62c5\u5fc3\u554a\uff01\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u6211\u8981\u5218\u5c06\u519b\u5c3d\u5c55\u6240\u957f\uff0c\u65e5\u540e\uff0c\u624d\u80fd\u591f\u7518\u5fc3 \u53d7\u547d\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u56fd\u5e08\u80fd\u907f\u5f00\u5218\u67d0\u7684\u5341\u4e8c\u8fde\u73af\u98de\u5200\uff0c\u672b\u5c06\u4e5f\u6280\u7a77\u4e8e\u6b64\u4e86\uff0c\u65e5\u540e\u65e6\u6709\u6240\u547d\uff0c\u5fc5\u5c06 \u8a93\u6b7b\u4ee5\u8d74\uff0c\u8840\u6e85\u4e94\u6b65\uff0c\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u5c06\u519b\u5c31\u5168\u529b\u53d1\u5200\u5427\uff01\u201d\u8881\u7d2b\u70df\u53c8\u7b11\u4e86\uff0c\u7b11\u7684\u5982\u82b1\u76db\u653e\u3002 \u3000\u3000\u5218\u98de\u9e4f\u795e\u8272\u51b7\u8083\uff0c\u6367\u8d77\u5200\u5957\uff0c\u5411\u540e\u9000\u4e86\u4e03\u6b65\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u672b\u5c06\u8bfb\u4e66\u4e0d\u591a\uff0c \u53ef\u4e5f\u77e5\u9053\u8fdb\u3001\u9000\u4e8c\u5b57\uff0c\u56fd\u5e08\u8fd8\u5200\u5165\u9798\u7684\u624b\u6cd5\uff0c\u672b\u5c06\u662f\u4e07\u4e07\u7684\u4e0d\u80fd\uff0c\u53ea\u6b64\u4e00\u6869\uff0c\u5218\u67d0\u5df2\u8be5\u62dc \u670d\uff0c\u4f55\u5144\uff0c\u56fd\u5e08\u53c8\u662f\u7edf\u5e05\u6211\u7b49\u7684\u9996\u9886\uff0c\u4ee5\u4e0b\u72af\u4e0a\uff0c\u7f6a\u52a0\u4e00\u7b49\u2026\u2026\u201d \u3000\u3000\u201c\u5218\u5c06\u519b\uff0c\u6487\u5f00\u4e3b\u4ece\u5173\u7cfb\u3002\u201d\u8881\u7d2b\u70df\u63a5\u9053\uff1a\u201c\u6211\u4eec\u662f\u4ee5\u6b66\u4f1a\u53cb\uff0c\u5c31\u7b97\u662f\u6211\u5411\u4f60\u6311\u6218 \u5427\uff01\u201d \u3000\u3000\u201c\u80dc\u8d1f\u5df2\u5206\u51fa\u6765\u4e86\uff0c\u56fd\u5e08\u7684\u6280\u827a\uff0c\u672b\u5c06\u7531\u8877\u4f69\u670d\uff0c\u4f46\u672b\u5c06\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u6ca1\u6709\u8bd5\u8fc7\u5c3d\u51fa \u5341\u4e8c\u5200\u8fde\u73af\u53d6\u654c\u7684\u624b\u6cd5\uff0c\u5b87\u6587\u7edf\u9886\u7834\u4e86\u6211\u516b\u5200\u5e76\u98de\u7684\u653b\u51fb\uff0c\u5df2\u4f7f\u672b\u5c06\u5fc3\u4e2d\u656c\u4f69\u4e07\u5206\u4e86\uff0c\u4eca \u5929\uff0c\u6211\u8981\u7528\u4e5d\u628a\u98de\u5200\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u597d\u662f\u5341\u4e8c\u628a\u8fde\u73af\u98de\u5200\u5168\u90e8\u51fa\u624b\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5c06\u519b\u53ef\u662f\u65e9\u5df2\u6709\u4e2a\u5fc3\u613f\uff0c\u60f3\u8ba9\u5341\u4e8c \u8fde\u73af\u98de\u5200\u4e00\u8d77\u51fa\u624b\uff0c\u4f7f\u7edd\u6280\u5c55\u51fa\u4e8e\u4e16\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff01\u4f46\u672b\u5c06\u9047\u4e0a\u7684\u5f3a\u654c\uff0c\u9664\u4e86\u5b87\u6587\u7edf\u9886\uff0c\u4ece\u65e0\u4eba\u80fd\u8eb2\u8fc7\u6211\u4e94\u628a \u98de\u5200\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u4f55\u4e0d\u8bd5\u4e00\u8bd5\uff0c\u5341\u4e8c\u5200\u8fde\u73af\u51fa\u624b\u7684\u5a01\u529b\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u4e5f\u8bb8\u65e0\u80fd\u7834\u89e3\uff0c\u4f46\u9003 \u5f00\u7684\u672c\u9886\uff0c\u4e00\u5b9a\u6709\u7684\uff0c\u5c06\u519b\u4e0d\u7528\u987e\u5fcc\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u5982\u82e5\u4f24\u4e86\u56fd\u5e08\uff0c\u672b\u5c06\u4ee5\u81ea\u6b8b\u52a0\u500d\u5949\u8fd8\uff0c\u4ee5\u8d4e\u72af\u4e0a\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u8fd8\u771f\u662f\u6b7b\u5fc3\u773c\uff0c\u4f46\u4ea6\u53ef\u89c1\u5b87\u6587\u6210\u90fd\u9a6d\u4e0b\u4e4b\u4e25\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u5c31\u66f4\u4e0d\u80fd\u53d7\u4f24\u4e86\u3002\u201d \u3000\u3000\u7b11\u7684\u597d\u7f8e\uff0c\u770b\u5f97\u5b87\u6587\u6210\u90fd\u4eba\u90fd\u9189\u4e86\uff0c\u5218\u98de\u9e4f\u4e5f\u4e3a\u4e4b\u6026\u7136\u5fc3\u52a8\uff0c\u624b\u4e00\u8f6f\uff0c\u5200\u5957\u4e5f\u51e0\u4e4e\u8dcc\u843d \u4e86\uff0c\u6025\u6025\u95ed\u4e0a\u773c\u775b\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8b66\u89c9\u4e86\uff0c\u8fd9\u7231\u7b11\u7684\u6bdb\u75c5\uff0c\u4ee5\u540e\u4e0d\u80fd\u518d\u72af\uff01 \u3000\u3000\u7edf\u5e05\u94c1\u9a91\uff0c\u51b3\u6218\u6c99\u573a\uff0c\u662f\u751f\u6b7b\u5bf9\u51b3\uff0c\u662f\u9700\u8981\u5e84\u4e25\u3001\u51b7\u8083\u7684\u7eaa\u5f8b\u7edf\u6cbb\uff0c\u8eab\u4e3a\u4e3b\u5e05\uff0c\u5c82\u53ef\u5931 \u4e4b\u8f7b\u6d6e\u3002 \u3000\u3000\u201c\u56fd\u5e08\uff0c\u5931\u656c\u4e86\u3002\u201d\u5218\u98de\u9e4f\u53cc\u624b\u9f50\u626c\uff0c\u4e24\u67c4\u98de\u5200\u95ea\u7535\u51fa\u624b\uff0c\u8fce\u9762\u5c04\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u6709\u7528\u624b\u53bb\u63a5\uff0c\u5750\u7740\u7684\u8eab\u5b50\u5411\u540e\u4e00\u4ef0\uff0c\u4e24\u67c4\u98de\u5200\u63a0\u9762\u800c\u8fc7\u3002 \u3000\u3000\u540e\u9762\u4e00\u4e08\u8ddd\u79bb\u5c31\u662f\u5899\u58c1\uff0c\u98de\u5200\u5e94\u8be5\u649e\u5728\u5899\u58c1\u4e0a\u3002 \u3000\u3000\u4f46\u5218\u98de\u9e4f\u7684\u4e09\u3001\u56db\u628a\u98de\u5200\uff0c\u5df2\u7531\u5de6\u53f3\u4e24\u4fa7\u515c\u8fc7\u6765\uff0c\u98de\u884c\u5947\u901f\uff0c\u90a3\u4e48\u51c6\u7684\u649e\u5728\u4e86\u7b2c\u4e00\u3001\u4e8c \u4e24\u67c4\u98de\u5200\u4e0a\u3002 \u3000\u3000\u56db\u5200\u4e92\u51fb\uff0c\u5200\u52bf\u5ffd\u53d8\uff0c\u8f6c\u4e2a\u5934\uff0c\u5c04\u5411\u4e86\u8881\u7d2b\u70df\u7684\u80cc\u540e\u3002 \u3000\u3000\u8fd9\u79cd\u602a\u5f02\u7684\u624b\u6cd5\uff0c\u7cbe\u6e5b\u7684\u6280\u5de7\uff0c\u8fde\u8881\u7d2b\u70df\u4e5f\u6709\u4e9b\u4f69\u670d\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fde\u7eed\u800c\u6765\u7684\u56db\u628a\u98de\u5200\u53c8\u4ee5\u4e00\u7ebf\u76f8\u8fde\uff0c\u9762\u8fce\u800c\u81f3\uff0c\u6bcf\u628a\u98de\u5200\u76f8\u8ddd\u4e00\u5c3a\u5de6\u53f3\uff0c\u56db\u628a\u4e5d \u5bf8\u957f\u7684\u98de\u5200\uff0c\u8fde\u6210\u4e00\u9053\u516b\u5c3a\u957f\u7684\u4e00\u4e32\u5200\u5149\u3002 \u3000\u3000\u795e\u4e4e\u5176\u6280\u5440\uff01 \u3000\u3000\u73b0\u5728\uff0c\u8881\u7d2b\u70df\u9762\u4e34\u7740\u524d\u3001\u540e\u98de\u5200\u7684\u5939\u51fb\uff0c\u524d\u9762\u662f\u56db\u5200\u8fde\u6210\u4e00\u7ebf\uff0c\u540e\u9762\u662f\u56db\u5200\u5e76\u6210\u4e00\u6392\u3002 \u3000\u3000\u5728\u4e00\u5ea7\u5927\u5385\u5802\u4e2d\uff0c\u4e0a\u6709\u9876\u76d6\uff0c\u524d\u6709\u6728\u6848\uff0c\u4e0a\u8dc3\u3001\u524d\u4f0f\uff0c\u90fd\u6709\u9650\u5236\u3002 \u3000\u3000\u53ea\u6709\u5411\u5730\u4e0b\u8e72\u4e00\u9014\uff01 \u3000\u3000\u5b87\u6587\u6210\u90fd\u773c\u90fd\u77aa\u5706\u4e86\uff0c\u4ed6\u62c5\u5fc3\u8881\u7d2b\u70df\u53d7\u5230\u4f24\u5bb3\uff0c\u4e5f\u4e0d\u613f\u8881\u7d2b\u70df\u8e72\u4e0b\u53bb\uff0c\u8eb2\u5165\u6728\u6848\u4e4b\u4e0b\uff0c \u90a3\u4f1a\u5728\u5979\u9886\u5bfc\u7684\u5a01\u4fe1\u4e0a\u7559\u4e0b\u7455\u75b5\u3002 \u3000\u3000\u4e0d\u8bba\u4ec0\u4e48\u82f1\u96c4\u8c6a\u6770\u4eba\u7269\uff0c\u53ea\u8981\u52a8\u4e86\u611f\u60c5\uff0c\u5229\u5bb3\u548c\u7406\u667a\uff0c\u5168\u90fd\u629b\u5230\u4e00\u8fb9\u4e86\uff0c\u8881\u7d2b\u70df\u9738\u5360\u4e86 \u5b87\u6587\u6210\u90fd\u7684\u9886\u5bfc\u6743\u4f4d\uff0c\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u5992\u5fcc\uff0c\u800c\u4e14\u662f\u5f88\u5e0c\u671b\u63a5\u53d7\u5979\u7684\u9886\u5bfc\uff0c\u4e5f\u671f\u671b\u5979\u53d7\u5230\u62e5 \u6234\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u80fd\u591a\u4eb2\u82b3\u6cfd\u3002 \u3000\u3000\u5403\u4e0d\u5230\uff0c\u591a\u770b\u51e0\u773c\u4e5f\u597d\u554a\uff01 \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u8e72\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u7ad9\u8d77\u6765\uff0c\u7a33\u7a33\u7684\u5750\u5728\u94fa\u7740\u5927\u7ea2\u57ab\u5b50\u7684\u592a\u5e08\u6905\u4e0a\uff0c\u4f46\u5374\u51fa\u4e86\u624b\uff0c \u62d4\u4e0b\u4e86\u5934\u4e0a\u7684\u91d1\u9497\u3002 \u3000\u3000\u91d1\u9497\u4e0d\u8fc7\u4e09\u5bf8\u957f\uff0c\u63e1\u5728\u7ea4\u5de7\u7684\u7389\u624b\u4e2d\uff0c\u5374\u53d1\u6325\u4e86\u60ca\u4e16\u9a87\u4fd7\u7684\u5a01\u529b\uff0c\u7ed5\u7740\u8eab\u524d\u8eab\u540e\u4e00\u9635 \u8f6c\uff0c\u516b\u628a\u98de\u5200\u5168\u88ab\u91d1\u9497\u6321\u5f00\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u662f\u8881\u7d2b\u70df\u624b\u4e0b\u7559\u60c5\uff0c\u8fd8\u662f\u98de\u5200\u84c4\u6709\u7684\u5185\u529b\u5947\u5999\uff0c\u98de\u5200\u6295\u6709\u88ab\u51fb\u843d\uff0c\u4ecd\u7136\u7ed5\u7740\u4eba\u8eab \u8f6c\u3002 \u3000\u3000\u5218\u98de\u9e4f\u5410\u6c14\u51fa\u58f0\uff0c\u6700\u540e\u56db\u628a\u98de\u5200\u4e5f\u51fa\u4e86\u624b\u3002 \u3000\u3000\u679c\u7136\u662f\u5200\u5e26\u51b7\u98ce\u8d77\uff0c\u5149\u8292\u8fc7\u957f\u7a7a\uff0c\u516b\u628a\u4f59\u52bf\u5df2\u8870\u7684\u98de\u5200\uff0c\u5728\u65b0\u53d1\u56db\u5200\u8fde\u73af\u649e\u51fb\u4e4b\u4e0b\uff0c\u7a81 \u7136\u53c8\u589e\u5f3a\u4e86\u98de\u65cb\u7684\u529b\u9053\u3002 \u3000\u3000\u5341\u4e8c\u628a\u8fde\u73af\u98de\u5200\uff0c\u73af\u4e32\u6210\u4e86\u4e00\u7247\u5200\u7f51\uff0c\u7ed5\u7740\u8881\u7d2b\u70df\u98de\u523a\u4e0d\u505c\uff0c\u5927\u6709\u4e0d\u6740\u4eba\uff0c\u4e0d\u80af\u7f62\u4f11\u7684 \u6c14\u52bf\uff0c\u5200\u4f3c\u6709\u7075\uff0c\u5b8c\u5168\u53d8\u6d3b\u4e86\u3002 \u3000\u3000\u91d1\u9497\u5e7b\u5316\u51fa\u4e00\u5708\u91d1\u5149\uff0c\u628a\u8881\u7d2b\u70df\u7684\u4e0a\u534a\u8eab\u88f9\u5728\u91d1\u5149\u4e2d\u3002 \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u5c11\u5e74\u5f1f\u5b50\u6c5f\u6e56\u8001 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u4e8c \u56de\u3000\u8f7b\u9a91\u5317\u8fdb \u3000\u3000\u5385\u4e2d\u4eba\u7684\u76ee\u5149\uff0c\u5168\u90fd\u96c6\u4e2d\u4e8e\u8881\u7d2b\u70df\u7684\u8eab\u4e0a\uff0c\u91d1\u5149\u548c\u5200\u5149\u5e76\u98de\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u201c\u5218\u5c06\u519b\uff0c\u5341\u4e8c\u8fde\u73af\u98de\u5200\uff0c\u679c\u7136\u662f\u5200\u4e2d\u5947\u6280\uff0c\u7d2b\u70df\u5f88\u4f69\u670d\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u4e2d\u91d1\u9497\u4ecd\u662f\u75be\u5982\u8f6e\u8f6c\u3002 \u3000\u3000\u4e00\u8fde\u4e32\u91d1\u94c1\u4ea4\u9e23\u4e4b\u540e\uff0c\u5200\u5149\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u91d1\u9497\u4e5f\u91cd\u56de\u5230\u8881\u7d2b\u70df\u7684\u79c0\u53d1\u4e0a\u3002 \u3000\u3000\u5341\u4e8c\u628a\u98de\u5200\uff0c\u4e5f\u6ca1\u635f\u5931\uff0c\u6574\u6574\u9f50\u9f50\u7684\u6392\u5728\u6728\u6848\u4e0a\uff0c\u4e00\u628a\u4e0d\u5c11\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u6562\u7b11\u4e86\uff0c\u4f46\u58f0\u97f3\u5374\u5341\u5206\u67d4\u548c\u7684\u8bf4\u9053\uff1a\u201c\u5218\u5c06\u519b\uff0c\u8bf7\u628a\u98de\u5200\u6536\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\u8881\u7d2b\u70df\u662f\u5982\u4f55\u7684\u628a\u98de\u5200\u63a5\u4e86\u4e0b\u6765\uff0c\u4f46\u5374\u770b\u5230\u4e86\u5218\u98de\u9e4f\u5782\u624b\u8083\u7acb\u5728\u8881\u7d2b\u70df\u6728 \u6848\u524d\u4e94\u5c3a\u8bb8\uff0c\u9053\uff1a\u201c\u672b\u5c06\u8bf7\u56fd\u5e08\u79d1\u4ee5\u5e94\u5f97\u4e4b\u7f6a\uff01\u201d \u3000\u3000\u201c\u5c06\u519b\u4f55\u7f6a\u4e4b\u6709\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u4eec\u662f\u7ea6\u597d\u7684\u6bd4\u8bd5\u554a\uff01\u201d \u3000\u3000\u201c\u56fd\u5e08\u5927\u91cf\uff0c\u672b\u5c06\u53ea\u80fd\u8ba4\u8f93\u4e86\uff01\u201d\u5218\u98de\u9e4f\u53d6\u8fc7\u98de\u5200\uff0c\u8eac\u8eab\u800c\u9000\uff0c\u8868\u73b0\u51fa\u65e0\u6bd4\u7684\u606d\u656c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u5404\u6000\u7edd\u6280\uff0c\u613f\u610f\u8d50\u6559\u7684\uff0c\u5c31\u8bf7\u51fa\u624b\uff01\u201d \u3000\u3000\u5728\u5ea7\u7684\u4e09\u4f4d\u526f\u7edf\u9886\u76f8\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u795e\u60c5\u51dd\u91cd\uff0c\u4f3c\u4e4e\u662f\u670d\u6c14\u4e86\uff0c\u4f46\u5374\u53c8\u4f3c\u4e0d\u5f88\u7518\u5fc3\uff0c\u76ee\u4e2d \u795e\u8292\u6d41\u52a8\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5b9e\u672a\u60f3\u5230\uff0c\u5982\u6b64\u7b80\u5355\u7684\u5c31\u8ba9\u7fa4\u96c4\u96cc\u4f0f\uff0c\u5fc3\u4e2d\u4e00\u9ad8\u5174\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u201c\u56fd\u5e08\uff0c\u6210\u90fd\u60f3\u9886\u6559\u9ad8\u62db\uff01\u201d \u3000\u3000\u4e8b\u51fa\u610f\u5916\uff0c\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u597d\u554a\uff01\u5b87\u6587\u5c06\u519b\u60f3\u5982\u4f55\u8d50\u6559\u5462\uff1f\u201d \u3000\u3000\u201c\u6b63\u5982\u56fd\u5e08\u6240\u8a00\uff0c\u6280\u827a\u767e\u5bb6\uff0c\u5404\u6709\u957f\u77ed\uff0c\u6210\u90fd\u6597\u80c6\uff0c\u60f3\u548c\u56fd\u5e08\u5bf9\u640f\u51e0\u62db\uff0c\u4ee5\u4fbf\u80fd\u5145\u5206\u53d1 \u6325\u672c\u8eab\u7684\u6280\u827a\u3002\u201d \u3000\u3000\u8981\u76f4\u63a5\u7684\u5bf9\u6253\u5566\uff01 \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u5c31\u4f9d\u5c06\u519b\uff01\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5fc3\u4e2d\u4e0d\u670d\uff01\u8868\u9762\u4e0a\uff0c\u5374\u53c8\u5341\u5206\u606d\u656c\uff0c\u770b\u4e0d\u51fa\u4e00\u70b9\u6015\u8272\uff0c\u8fd9 \u4e2a\u4eba\u597d\u751f\u6df1\u6c89\uff01\u201d \u3000\u3000\u65e2\u662f\u52a8\u624b\u5bf9\u6253\uff0c\u5c31\u4e0d\u80fd\u5750\u7740\u4e0d\u52a8\u4e86\uff0c\u8881\u7d2b\u70df\u7ad9\u8d77\u8eab\u5b50\uff0c\u884c\u4eba\u5927\u5385\u4e2d\u3002 \u3000\u3000\u5b87\u6587\u6b65\u884c\u5230\u8881\u7d2b\u70df\u8eab\u524d\u4e09\u5c3a\u5904\uff0c\u672d\u4f4f\u4e86\u9a6c\u6b65\u3002 \u3000\u3000\u56db\u4e2a\u526f\u7edf\u9886\u4e0d\u81ea\u89c9\u5730\u4e5f\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7ed9\u4ed6\u4eec\u7684\u662f\u4e00\u79cd\u60ca\u5947\uff0c\u90a3\u79cd\u9b54\u672f\u4e00\u822c\u7684\u63a5\u5200\u624b\u6cd5\uff0c\u53eb\u4eba\u65e0\u6cd5\u770b\u51fa\u4e00\u70b9\u95e8\u9053\uff0c\u662f\u4ed6 \u4eec\u4e60\u6b66\u6570\u5341\u5e74\u6765\uff0c\u4ece\u672a\u89c1\u5230\u8fc7\u7684\u5947\u6280\u3002 \u3000\u3000\u8fd9\u6837\u4e24\u4e2a\u96be\u6d4b\u9ad8\u6df1\u7684\u4eba\u8981\u5bf9\u6253\u4e00\u573a\uff1f\u53ef\u662f\u6c5f\u6e56\u4e0a\u96be\u5f97\u4e00\u89c1\u7684\u5947\u89c2\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u4e0d\u8981\u624b\u4e0b\u7559\u60c5\uff0c\u6211\u8f93\u4e86\uff0c\u4f1a\u5fc3\u670d\u53e3\u62a5\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4f46\u4e5f\u4e0d\u8981\u4ee5\u547d\u76f8\u62da\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u8fd8\u8981\u501f\u91cd\u5c06\u519b\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u795e\u60c5\u5f88\u51b7\u8083\uff0c\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u6055\u6211\u65e0\u793c\u3002\u201d \u3000\u3000\u5de6\u624b\u4e00\u626c\uff0c\u8fce\u9762\u5288\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5a07\u8eab\u4e00\u95ea\uff0c\u907f\u5f00\u638c\u52bf\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u5b87\u6587\u6210\u90fd\u5c06\u519b\u662f\u4e2a\u5de6\u6487\u5b50\u2026\u2026\u201d \u3000\u3000\u5fc3\u5ff5\u521d\u52a8\uff0c\u4e00\u80a1\u5f3a\u70c8\u7684\u638c\u529b\u5df2\u5377\u88ad\u800c\u81f3\uff0c\u624b\u638c\u8fd8\u5728\u4e09\u5c3a\u4e4b\u5916\uff0c\u6697\u52b2\u5df2\u6c79\u6d8c\u800c\u81f3\u3002 \u3000\u3000\u90a3\u662f\u5b87\u6587\u6210\u90fd\u53f3\u624b\u53d1\u51fa\u7684\u638c\u529b\uff0c\u4e0d\u662f\u5de6\u6487\u5b50\uff0c\u653b\u6765\u7684\u5de6\u624b\uff0c\u53ea\u662f\u4e00\u4e2a\u865a\u62db\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e5f\u53d1\u89c9\u4e86\u5b87\u6587\u6210\u90fd\u638c\u52bf\u7684\u5947\u5999\uff0c\u653b\u6765\u7684\u4e0d\u662f\u4e00\u5927\u7247\u638c\u98ce\uff0c\u800c\u662f\u51dd\u805a\u4e8e\u4e00\u56e2\u7684\u6697 \u52b2\uff0c\u90a3\u8868\u793a\u5b87\u6587\u6210\u90fd\u6280\u827a\u7684\u4fee\u4e3a\u4e0a\uff0c\u5df2\u5230\u4e86\u968f\u5fc3\u6240\u6b32\u7684\u5883\u754c\uff0c\u522b\u4eba\u611f\u89c9\u4e0d\u5230\u638c\u98ce\u547c\u5578\u7684\u5a01 \u52bf\uff0c\u4f46\u8eab\u53d7\u8005\u5374\u4f3c\u6709\u7740\u4e00\u6839\u91cd\u5927\u7684\u94c1\u67f1\uff0c\u76f4\u649e\u800c\u6765\u7684\u5a01\u731b\u3002 \u3000\u3000\u628a\u4e00\u7247\u638c\u529b\uff0c\u675f\u6210\u4e00\u80a1\u529b\u91cf\uff0c\u81ea\u7136\u662f\u5a01\u52bf\u500d\u589e\u3002 \u3000\u3000\u201c\u597d\u554a\uff01\u8bda\u5fc3\u8981\u4e00\u638c\u6253\u6b7b\u6211\u5440\uff01\u201d\u8881\u7d2b\u70df\u6697\u5fd6\uff0c\u5de6\u638c\u4e00\u7acb\uff0c\u8fce\u7740\u638c\u52bf\u649e\u53bb\u3002 \u3000\u3000\u6709\u5982\u8038\u7acb\u5728\u6d2a\u6d41\u4e2d\uff0c\u4e00\u6839\u575a\u7262\u7684\u67f1\u77f3\uff0c\u786c\u628a\u4e07\u9a6c\u5954\u817e\u822c\u7684\u6c34\u52bf\uff0c\u751f\u751f\u7684\u4e00\u5206\u4e3a\u4e8c\uff0c\u5206\u5411 \u4e24\u4fa7\u6d41\u53bb\u3002 \u3000\u3000\u5185\u529b\u5bf9\u51b2\u4e4b\u4e0b\uff0c\u5b87\u6587\u6210\u90fd\u5df2\u65e0\u6cd5\u518d\u63a7\u5236\u53d1\u51fa\u7684\u529b\u9053\uff0c\u4e00\u9053\u649e\u4e0a\u5899\u58c1\uff0c\u539f\u672c\u65e0\u58f0\u65e0\u606f\u7684\u6697 \u52b2\uff0c\u53d7\u5230\u963b\u6321\uff0c\u7a81\u7136\u66b4\u53d1\u4e00\u80a1\u5f3a\u70c8\u7684\u65cb\u98ce\uff0c\u5c4b\u5b87\u9707\u52a8\uff0c\u529b\u9053\u56de\u65cb\uff0c\u6728\u6848\u4e0a\u7684\u8336\u676f\u7eb7\u7eb7\u88ab\u51b2\u649e \u843d\u5730\uff0c\u8dcc\u6210\u788e\u7247\u3002 \u3000\u3000\u53e6\u4e00\u80a1\u529b\u9053\uff0c\u5c31\u975e\u5e38\u7684\u4e0d\u5e78\u4e86\uff0c\u649e\u5728\u4e86\u83b2\u513f\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u867d\u7136\u5df2\u5f97\u8881\u7d2b\u70df\u4f20\u6388\u4e86\u4e00\u70b9\u672f\u6cd5\u3001\u6b66\u529f\uff0c\u4f46\u521d\u5b66\u4e4d\u7ec3\uff0c\u57fa\u7840\u4e5f\u672a\u6253\u597d\uff0c\u5168\u7136\u65e0\u5907\u4e4b \u4e0b\uff0c\u5982\u4f55\u80fd\u627f\u53d7\u5f97\u4f4f\uff0c\u6574\u4e2a\u4eba\u88ab\u649e\u5f97\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\u3002\u201d \u3000\u3000\u8eab\u5982\u6d41\u77e2\uff0c\u75be\u98de\u800c\u8d77\uff0c\u60ac\u7a7a\u4e00\u628a\uff0c\u62b1\u4f4f\u4e86\u83b2\u513f\u59d1\u5a18\u3002 \u3000\u3000\u4f46\u4ed6\u53bb\u52bf\u592a\u5feb\u4e86\uff0c\u867d\u7136\u4e00\u628a\u62b1\u4f4f\u4e86\u83b2\u513f\uff0c\u4f46\u6574\u4e2a\u4eba\u5374\u5411\u5899\u58c1\u4e0a\u64c5\u53bb\u3002 \u3000\u3000\u5306\u6025\u4e4b\u95f4\uff0c\u4e00\u811a\u8e39\u51fa\uff0c\u9752\u7816\u5899\u88ab\u8e39\u51fa\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u4f46\u5b87\u6587\u6210\u90fd\u5374\u501f\u8fd9\u4e00\u811a\u4e4b\u529b\uff0c\u4e00\u4e2a\u7a7a\u4e2d\u7ffb\uff0c\u7a33\u7a33\u7684\u843d\u5728\u4e86\u5927\u5385\u4e0a\uff0c\u53cc\u624b\u4ecd\u6258\u7740\u83b2\u513f\u7684\u5a07 \u8eaf\u3002 \u3000\u3000\u4f4e\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u83b2\u513f\u8138\u8272\u82cd\u767d\uff0c\u53e3\u89d2\u6d41\u51fa\u9c9c\u8840\uff0c\u4f24\u5f97\u4f3c\u662f\u4e0d\u8f7b\u3002 \u3000\u3000\u201c\u56fd\u5e08\uff0c\u5bf9\u4e0d\u8d77\uff0c\u6211\u65e0\u6cd5\u6536\u4f4f\u53d1\u51fa\u7684\u529b\u9053\uff01\u201d\u5b87\u6587\u6210\u90fd\u6709\u4e9b\u61ca\u607c\u7684\u8bf4\u3002 \u3000\u3000\u201c\u662f\u6211\u7528\u9519\u4e86\uff0c\u63a5\u4f60\u638c\u529b\u7684\u65b9\u6cd5\uff01\u201d\u8881\u7d2b\u70df\u7f13\u6b65\u884c\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u5979\u4f24\u5f97\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4f24\u5f97\u4e0d\u8f7b\u3002\u201d\u5b87\u6587\u6210\u90fd\u4fa7\u8eab\u5728\u83b2\u513f\u80f8\u524d\uff0c\u542c\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u5979\u597d\u50cf\u5b66\u8fc7\u6b66\u529f\uff0c\u771f\u6c14 \u62a4\u4f4f\u4e86\u5fc3\u8109\uff0c\u7ed9\u5c5e\u4e0b\u4e09\u5929\u65f6\u95f4\uff0c\u6211\u8d1f\u8d23\u6cbb\u597d\u5979\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\uff0c\u53d6\u51fa\u4e00\u4e2a\u5c0f\u7389\u74f6\uff0c\u5012\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u9053\uff1a\u201c\u5148\u8ba9\u5979\u670d\u4e0b\u8fd9\u9897\u4e39\u836f\uff0c\u4fdd\u4f4f\u5979 \u4f24\u52bf\u4e0d\u8981\u6076\u5316\u3002\u201d \u3000\u3000\u5de7\u513f\u5df2\u5feb\u901f\u7684\u5954\u4e86\u8fc7\u6765\uff0c\u63a5\u8fc7\u4e39\u4e38\uff0c\u6295\u5165\u4e86\u83b2\u513f\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u201c\u5c06\u519b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u83b2\u513f\u7684\u4f24\u52bf\u53ef\u671b\u7a33\u4f4f\uff0c\u4f60\u7684\u6280\u827a\uff0c\u8fd8\u672a\u53d1\u6325\uff0c\u653e\u4e0b\u5979\uff0c\u6211\u4eec\u7ee7 \u7eed\u8fd9\u4e00\u573a\u6bd4\u8bd5\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u6211\u8ba4\u8f93\u4e86\uff0c\u6cbb\u7597\u83b2\u513f\u59d1\u5a18\u7684\u4f24\u52bf\u8981\u7d27\u3002\u201d \u3000\u3000\u6001\u5ea6\u53d8\u5f97\u5f88\u5feb\uff0c\u5df2\u65e0\u518d\u6218\u4e4b\u5fc3\u3002 \u3000\u3000\u201c\u5c06\u519b\u3002\u201d\u8881\u7d2b\u70df\u975e\u5e38\u4e0d\u6ee1\u5b87\u6587\u6210\u90fd\u7684\u591a\u53d8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u52a8\u4e0a\u4e86\u624b\uff0c\u603b\u8981\u5206\u4e2a\u80dc \u8d1f\u51fa\u6765\u624d\u662f\uff0c\u83b2\u513f\u6b7b\u4e0d\u4e86\uff0c\u4e0d\u7528\u62c5\u5fc3\uff0c\u5de7\u513f\uff0c\u628a\u83b2\u513f\u62b1\u4e00\u8fb9\u53bb\u3002\u201d \u3000\u3000\u5de7\u513f\u4f38\u51fa\u4e86\u53cc\u624b\uff0c\u5b87\u6587\u6210\u90fd\u53ea\u597d\u4ea4\u51fa\u83b2\u513f\u3002 \u3000\u3000\u201c\u5c06\u519b\uff0c\u51fa\u624b\u5427\uff01\u201d\u8881\u7d2b\u70df\u6446\u51fa\u4e86\u8fce\u6218\u7684\u59ff\u52bf\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u6211\u53ea\u60f3\u89e3\u5979\u4e4b\u5371\uff0c\u4eb2\u8eab\u51fa\u6218\uff0c\u4ee5\u7edd\u4e09\u4e2a\u526f\u7edf\u9886\u7528\u8f66\u8f6e\u6218 \u6cd5\u5bf9\u4ed8\u5979\u7684\u8ba1\u5212\uff0c\u5374\u4e0d\u6599\u5f04\u5de7\u6210\u62d9\uff0c\u60f9\u51fa\u5979\u7684\u6012\u706b\u4e86\u2026\u2026 \u3000\u3000\u201c\u5c06\u519b\u5982\u81ea\u77e5\u62f3\u811a\u5de5\u592b\u4e0a\u7684\u706b\u5019\u4e0d\u591f\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6539\u7528\u5175\u5203\u4e5f\u6210\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u6311\u6218\uff0c\u800c\u662f\u8ff9\u8fd1\u8f7b\u85d0\u7684\u8feb\u6218\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u8138\u8272\u4e00\u53d8\uff0c\u4f46\u5f88\u5feb\u53c8\u6062\u590d\u4e86\u5e38\u6001\uff0c\u9053\uff1a\u201c\u6210\u90fd\u8bef\u4f24\u83b2\u513f\uff0c\u786e\u5c5e\u610f\u5916\u2026\u2026\u201d \u3000\u3000\u201c\u83b2\u513f\u7684\u4e8b\uff0c\u4e0d\u52b3\u5173\u5fc3\uff0c\u5979\u5df2\u7ecf\u5e73\u5b89\u4e86\uff01\u4e5f\u7528\u4e0d\u7740\u4f60\u51fa\u624b\u65bd\u6551\uff0c\u8001\u5b9e\u8bf4\uff0c\u4e09\u5929\u7684\u65f6\u95f4\u592a \u4e45\u4e86\uff0c\u6211\u5e0c\u671b\u660e\u5929\u80fd\u4e0a\u8def\uff0c\u5175\u8d35\u795e\u901f\u554a\uff01\u201d\u8881\u7d2b\u70df\u7a81\u7136\u5bf9\u5b87\u6587\u6210\u90fd\u751f\u51fa\u4e86\u538c\u6076\u4e4b\u611f\uff0c\u53ea\u89c9\u4ed6 \u53cd\u590d\u65e0\u5e38\uff0c\u5ffd\u7136\u606d\u8c28\u53d7\u6559\uff0c\u5ffd\u7136\u53c8\u633a\u8eab\u6311\u6218\uff0c\u53ea\u4e0d\u8fc7\u52a8\u624b\u4e00\u62db\uff0c\u5374\u53c8\u6539\u53d8\u51fa\u53e6\u4e00\u526f\u9762\u5b54\uff0c\u4e00 \u5473\u6c42\u9976\u7684\u795e\u60c5\uff0c\u54ea\u91cc\u6709\u5927\u4e08\u592b\u7684\u6c14\u6982\uff1f \u3000\u3000\u600e\u4f1a\u4e00\u773c\u628a\u4ed6\u770b\u6210\u4e86\u82f1\u96c4\u4eba\u7269\uff0c\u8fd8\u8981\u60f3\u501f\u91cd\u4ed6\u7684\u6b66\u529f\u3001\u5a01\u671b\uff0c\u53bb\u6251\u706d\u738b\u6c14\u4e4b\u6e90\uff1f \u3000\u3000\u53ea\u542c\u8881\u7d2b\u70df\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5b87\u6587\u5c06\u519b\u8fdf\u8fdf\u4e0d\u80af\u51fa\u624b\uff0c\u662f\u60f3\u8ba9\u6211\u5148\u673a\u4e86\uff0c\u597d\uff0c\u606d\u656c\u4e0d\u5982\u4ece \u547d\uff01\u201d \u3000\u3000\u53f3\u624b\u4e00\u626c\uff0c\u70b9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e94\u6839\u7ea4\u7ec6\u7684\u624b\u6307\u4e0a\uff0c\u7adf\u7136\u5404\u5c04\u51fa\u4e86\u4e00\u80a1\u6307\u98ce\uff0c\u5206\u88ad\u5411\u5b87\u6587\u6210\u90fd\u4e94\u5904\u5927\u7a74\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u8fd0\u6c14\u82e5\u94a2\uff0c\u907f\u5f00\u4e94\u5904\u7a74\u4f4d\uff0c\u5374\u6545\u610f\u4ee5\u5de6\u80a9\uff0c\u8fce\u5411\u4e00\u7f15\u6307\u98ce\u3002 \u3000\u3000\u4ed6\u60f3\u4e86\u89e3\u4e00\u4e0b\uff0c\u8881\u7d2b\u70df\u5728\u771f\u6b63\u7684\u6b66\u529f\u4e0a\uff0c\u7a76\u7adf\u6709\u591a\u5c11\u6210\u5c31\uff0c\u6c14\u52bf\u5982\u6b64\u7684\u5484\u5484\u903c\u4eba\uff01 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5403\u60ca\u6781\u4e86\uff0c\u80fd\u4ee5\u6307\u529b\u7a7f\u900f\u4ed6\u62a4\u8eab\u6c14\u529f\u7684\u4eba\uff0c\u662f\u751f\u5e73\u521d\u6b21\u9047\u5230\uff0c\u8fd9\u4e2a\u4ed9\u5973\u4e4b\u59ff\u7684 \u7f8e\u4eba\uff0c\u7adf\u6709\u7740\u5982\u6b64\u6df1\u539a\u7684\u5185\u529b\uff0c\u4e0d\u53ef\u8f7b\u654c\uff0c\u8fd9\u4e2a\u95f7\u4e8f\u5403\u5927\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u4e5f\u6fc0\u8d77\u4e86\u5b87\u6587\u6210\u90fd\u653e\u624b\u4e00\u6218\u7684\u8c6a\u6c14\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53f3\u638c\u75be\u6263\uff0c\u5c55\u5f00\u4e86\u4e00\u8f6e\u731b\u653b\u3002 \u3000\u3000\u4ed6\u5de6\u80a9\u53d7\u4f24\uff0c\u4e00\u6761\u5de6\u81c2\u6682\u65f6\u65e0\u6cd5\u914d\u5408\u53f3\u624b\u7684\u653b\u52bf\u3002 \u3000\u3000\u5355\u662f\u4e00\u53ea\u53f3\u638c\u7684\u653b\u52bf\uff0c\u4e5f\u8db3\u591f\u60ca\u4eba\u4e86\uff01 \u3000\u3000\u638c\u52bf\u4e0a\u5e26\u51fa\u4e86\u5f3a\u5927\u7684\u5185\u529b\uff0c\u5982\u94c1\u9524\u51fb\u5ca9\uff0c\u5de8\u65a7\u5f00\u5c71\u4e00\u822c\uff0c\u6574\u5ea7\u7684\u5385\u5802\u4e2d\uff0c\u90fd\u88ab\u4ed6\u5f3a\u5927\u7684 \u638c\u529b\uff0c\u5377\u8d77\u4e86\u4e00\u80a1\u65cb\u52a8\u7684\u6c14\u6d41\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u505c\u624b\u4e0d\u653b\u4e86\uff0c\u5a07\u8eaf\u968f\u638c\u52bf\u8f6c\u52a8\uff0c\u5982\u5f71\u968f\u5f62\u3002 \u3000\u3000\u65cb\u52a8\u7684\u52b2\u529b\uff0c\u98d8\u8d77\u5979\u7684\u8863\u8882\u88d9\u89d2\u3002 \u3000\u3000\u9aa4\u7136\u95f4\uff0c\u770b\u4e0a\u53bb\uff0c\u5979\u597d\u50cf\u5df2\u88ab\u5b87\u6587\u6210\u90fd\u5f3a\u731b\u65e0\u4f26\u7684\u638c\u529b\uff0c\u903c\u5f97\u7ae0\u6cd5\u5927\u4e71\uff0c\u8ddf\u7740\u638c\u529b\u56e2\u56e2 \u8f6c\u3002 \u3000\u3000\u4f46\u5982\u770b\u5f97\u4ed4\u7ec6\uff0c\u7b80\u76f4\u5413\u5f97\u4eba\u5fc3\u60ca\u80c6\u98a4\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6574\u4e2a\u4eba\uff0c\u5316\u4f5c\u4e00\u7247\u8f7b\u53f6\uff0c\u4f3c\u662f\u9644\u5728\u4e86\u5b87\u6587\u6210\u90fd\u7684\u53f3\u638c\u4e0a\u8f6c\u52a8\uff0c\u4efb\u5f97\u5b87\u6587\u6210\u90fd\u638c\u529b \u80fd\u788e\u77f3\uff0c\u88c2\u7891\uff0c\u4f46\u8881\u7d2b\u70df\u4f3c\u662f\u4e00\u70b9\u4e5f\u627f\u53d7\u4e0d\u5230\u538b\u529b\u3002 \u3000\u3000\u56db\u5927\u526f\u7edf\u9886\uff0c\u773c\u775b\u90fd\u770b\u76f4\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4eec\u53d1\u89c9\u4e86\u53e6\u4e00\u4ef6\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7684\u8eab\u4f53\uff0c\u5728\u7a7a\u4e2d\u98d8\u6d6e\u7740\uff0c\u8db3\u4e0d\u843d\u5730\uff0c\u817f\u4e5f\u76d8\u4e86\u8d77\u6765\uff0c\u597d\u50cf\u5728\u76d8\u819d\u6253\u5750\uff0c\u800c\u4e14\uff0c\u5fae \u95ed\u7740\u53cc\u76ee\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u53cc\u624b\u5e73\u6536\u80f8\u524d\uff0c\u4fdd\u6301\u7740\u968f\u65f6\u53ef\u4ee5\u51fb\u51fa\u7684\u59ff\u6001\uff0c\u6ca1\u6709\u505a\u5408\u4ec0\u7684\u5f62\u72b6\u3002 \u3000\u3000\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u4f3c\u628a\u4e00\u4e2a\u4eba\u5b8c\u5168\u5316\u4f5c\u4e86\u4e00\u56e2\u6c14\u6d41\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u53e3\u6c14\uff0c\u5288\u51fa\u4e5d\u5341\u516d\u638c\u540e\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u56fd\u5e08\u600e\u4e0d\u8fd8\u624b\uff1f\u5728\u4e0b\u5de6\u80a9\u53d7 \u4f24\uff0c\u65e0\u529b\u5c01\u6321\uff0c\u56fd\u5e08\u9519\u5931\u4e86\u4e00\u4e2a\u5236\u670d\u6210\u90fd\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b54\u975e\u6240\u95ee\u5730\u9053\uff1a\u201c\u5b87\u6587\u5c06\u519b\u80fd\u5728\u4e00\u9762\u53d1\u638c\u653b\u654c\uff0c\u4e00\u9762\u8fd0\u529f\u7597\u4f24\uff0c\u5fc3\u5206\u4e8c\u7528\uff0c\u5de6\u624b \u53ef\u4ee5\u5212\u65b9\uff0c\u53f3\u624b\u540c\u65f6\u5212\u5706\uff0c\u76f8\u5f53\u7684\u9ad8\u660e\u4e86\uff0c\u53ea\u53ef\u60dc\u8fd9\u4e9b\u5fae\u672b\u4e4b\u6280\uff0c\u8fd8\u672a\u653e\u5728\u7d2b\u70df\u7684\u773c\u4e2d\uff0c\u6211 \u8981\u5f7b\u5e95\u6536\u670d\u4f60\uff0c\u8ba9\u4f60\u89c1\u8bc6\u5230\u6b66\u529f\u6280\u827a\u7684\u9886\u57df\u91cc\uff0c\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff01\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u5bf9\u5b87\u6587\u6210\u90fd\u7684\u4e00\u4efd\u597d\u611f\uff0c\u7834\u706d\u4e86\uff0c\u8bdd\u4e5f\u5c31\u8bf4\u5f97\u5341\u5206\u5c16\u9510\uff0c\u4e0d\u7559\u4f59\u5730\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e5f\u88ab\u6fc0\u706b\u4e86\uff0c\u5251\u7709\u8038\u52a8\uff0c\u4fca\u76ee\u653e\u5149\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56fd\u5e08\u6280\u827a\uff0c\u786e\u6709\u8fc7\u4eba\u4e4b \u5904\uff01\u4f46\u662f\u5426\u5929\u4e0b\u65e0\u654c\u5462\uff1f\u201d \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\uff0c\u4f55\u4e0d\u65bd\u51fa\u5168\u529b\uff0c\u6c42\u8bc1\u4e00\u4e0b\u3002\u201d\u8881\u7d2b\u70df\u63a5\u9053\uff1a\u201c\u767e\u62db\u6211\u4e0d\u8fd8\u624b\uff0c\u8fc7\u4e86\u767e\u62db\uff0c \u7d2b\u70df\u5c31\u624b\u4e0d\u7559\u60c5\uff0c\u5c06\u519b\u597d\u81ea\u4e3a\u4e4b\u3002\u201d \u3000\u3000\u56db\u5927\u526f\u7edf\u9886\uff0c\u542c\u4e86\u53d1\u5446\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u63a5\u4e0b\u5218\u98de\u9e4f\u7684\u5341\u4e8c\u8fde\u73af\u98de\u5200\uff0c\u5c55\u73b0\u4e86\u60ca\u4eba\u5947\u6280\uff0c\u4f46\u8a00\u8bcd\u8c26\u548c\uff0c\u4e0d\u5931\u6e29\u67d4\uff0c\u600e\u4e48\u5bf9 \u5b87\u6587\u6210\u90fd\uff0c\u5374\u662f\u8bed\u950b\u5c16\u5389\uff0c\u4e0d\u7559\u4f59\u5730\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u5934\u4e5f\u5728\u9707\u52a8\uff0c\u5fd6\u9053\uff1a\u600e\u4e48\u5f04\u5230\u5982\u6b64\u7684\u8a93\u4e0d\u4e24\u7acb\uff0c\u6211\u662f\u771f\u8bda\u7684\u62e5\u62a4\u4f60\u5440\uff01\u96be\u9053 \u771f\u60f3\u6740\u4e86\u6211\uff0c\u4ee5\u7acb\u5a01\u671b\uff0c\u8fd8\u662f\u522b\u6709\u6240\u56fe\uff1f\u53bb\u6211\u9002\u80fd\u6210\u4e8b\uff0c\u53ea\u8981\u4f60\u80af\u8bf4\u660e\u767d\uff0c\u6211\u4e5f\u8bb8\u80fd\u4e3a\u4f60\u800c \u6b7b\uff0c\u4f60\u8fd9\u6837\u8a00\u8bed\u8feb\u6324\uff0c\u6211\u5c31\u6b7b\u96be\u7791\u76ee\uff0c\u771f\u8981\u5168\u529b\u4ee5\u62da\u4e86\uff01 \u3000\u3000\u62a2\u524d\u4e24\u6b65\uff0c\u5de6\u638c\u53f3\u6307\uff0c\u4e00\u9f50\u653b\u51fa\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e5f\u4e0d\u518d\u5356\u5f04\u8eab\u6cd5\uff0c\u91c7\u7528\u6e38\u6597\uff0c\u53cc\u624b\u8fce\u51fb\uff0c\u6263\u8155\u622a\u8109\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u8fd9\u4e00\u6b21\uff0c\u65bd\u5c55\u51fa\u4e86\u771f\u7684\u672c\u9886\uff0c\u884c\u6c14\u4f3c\u73e0\uff0c\u8fd0\u52b2\u82e5\u94a2\uff0c\u638c\u6307\u7684\u53d8\u5316\uff0c\u6781\u5c3d\u51cc\u5389\u5de7 \u5999\u3002 \u3000\u3000\u4eba\u4e0d\u9000\u6b65\uff0c\u6c89\u8155\u907f\u62db\uff0c\u66f2\u8098\u8fd8\u51fb\uff01 \u3000\u3000\u4e24\u4e2a\u4eba\u5c55\u5f00\u4e86\u4e00\u573a\u8fd1\u8eab\u76f8\u640f\uff0c\u638c\u6307\u53d8\u5316\uff0c\u5404\u5c3d\u5176\u5999\u3002 \u3000\u3000\u5c31\u5728\u5f7c\u6b64\u6307\u638c\u6570\u5bf8\u8ddd\u79bb\u4e4b\u95f4\uff0c\u70b9\u3001\u6263\u3001\u65a9\u3001\u622a\uff0c\u53d8\u51fa\u4e86\u65e0\u6570\u7684\u51f6\u9669\u62db\u6cd5\uff0c\u8c01\u4e5f\u4e0d\u80af\u540e\u9000 \u4e00\u6b65\uff0c\u8c01\u4e5f\u4e0d\u80af\u64a4\u56de\u6307\u638c\u3002 \u3000\u3000\u771f\u662f\u62db\u62db\u53d8\u5316\uff0c\u95f4\u4e0d\u5bb9\u53d1\uff01 \u3000\u3000\u201c\u597d\u6b66\u529f\uff01\u201d\u8881\u7d2b\u70df\u4f4e\u58f0\u8d5e\u4e86\u4e00\u53e5\uff0c\u7a81\u7136\u4e0d\u8fd8\u624b\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5df2\u6709\u4e86\u6c42\u80dc\u4e4b\u5ff5\uff0c\u8881\u7d2b\u70df\u5ffd\u7136\u6539\u91c7\u5b88\u52bf\uff0c\u7ed9\u4e86\u4ed6\u5168\u529b\u62a2\u653b\u7684\u673a\u4f1a\uff0c\u638c\u6cd5\u4e00\u53d8\uff0c \u653b\u52bf\u66f4\u52a0\u731b\u70c8\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6574\u4e2a\u4eba\u88ab\u5708\u5165\u4e00\u7247\u638c\u5f71\u4e2d\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u8881\u7d2b\u70df\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u5c06\u519b\u5df2\u653b\u8fc7\u767e\u62db\uff0c\u6211\u8981\u8fd8\u51fb\u4e86\u3002\u201d \u3000\u3000\u968f\u7740\u5a07\u559d\u4e4b\u58f0\uff0c\u6fc0\u70c8\u7684\u640f\u6597\uff0c\u7a81\u7136\u9759\u6b62\u4e0b\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u8138\u8ff7\u60d8\u4e4b\u8272\uff0c\u5446\u5446\u7ad9\u7740\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8d25\u4e86\uff0c\u591a\u8c22 \u56fd\u5e08\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5f88\u5b8c\u597d\uff0c\u65e0\u4eba\u53d7\u4f24\u3002 \u3000\u3000\u56db\u5927\u526f\u7edf\u9886\u4e5f\u6ca1\u6709\u770b\u51fa\u6765\uff0c\u4e24\u4e2a\u4eba\u5982\u4f55\u5206\u51fa\u4e86\u80dc\u8d1f\uff0c\u53ea\u611f\u89c9\u5230\u5927\u5385\u4e2d\u6025\u5982\u65cb\u98ce\u7684\u52b2\u529b\u7a81 \u7136\u505c\u6b62\u3002 \u3000\u3000\u201c\u5c06\u519b\uff0c\u8bba\u771f\u5b9e\u7684\u6b66\u529f\uff0c\u6211\u8f93\u4e00\u7b79\u3002\u201d\u8881\u7d2b\u70df\u53c8\u53d8\u5f97\u6e29\u67d4\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u80dc\u5728\u6280\u5de7\u4e4b \u4e0a\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\u4e0d\u7528\u81ea\u8c26\uff0c\u6210\u90fd\u8f93\u7684\u5fc3\u670d\uff0c\u56fd\u5e08\u6709\u4e8b\uff0c\u4f46\u8bf7\u5429\u5490\uff0c\u4e0d\u8fc7\uff0c\u660e\u5929\u51fa\u52a8\u5927\u519b\uff0c\u7a0d\u89c9\u6025 \u8feb\u4e86\u4e9b\u3002 \u3000\u3000\u201c\u4e09\u5929\u65f6\u9650\u591f\u4e0d\u591f\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u4e0d\u8981\u70b9\u52a8\u5927\u519b\uff0c\u53ea\u8981\u5c06\u519b\u9009\u5e26\u4e24\u767e\u540d\u7cbe\u5065\u7684\u6b66 \u58eb\u540c\u884c\uff01\u201d \u3000\u3000\u201c\u4e24\u767e\u540d\u7cbe\u5065\u6b66\u58eb\uff0c\u5206\u4f5c\u56db\u961f\uff01\u201d\u5b87\u6587\u6210\u90fd\u770b\u770b\u8881\u7d2b\u70df\u7684\u8868\u60c5\uff0c\u9762\u5e26\u5fae\u7b11\uff0c\u4f3c\u5728\u9759\u542c\u81ea \u5df1\u7684\u610f\u89c1\uff0c\u63a5\u9053\uff1a\u201c\u7531\u5385\u4e2d\u56db\u4f4d\u526f\u7edf\u9886\u5404\u5e26\u4e00\u961f\uff0c\u6210\u90fd\u7387\u4e24\u540d\u4f8d\u536b\u968f\u884c\u3002 \u3000\u3000\u201c\u597d\uff0c\u4e0d\u8fc7\uff0c\u522b\u5fd8\u4e86\u6211\u548c\u4e24\u540d\u4f8d\u5973\uff0c\u83b2\u513f\u3001\u5de7\u513f\u3002\u201d \u3000\u3000\u201c\u83b2\u513f\u59d1\u5a18\u7684\u4f24\u52bf\u672a\u6108\uff0c\u80fd\u591f\u9a91\u9a6c\u957f\u5f81\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u5c06\u519b\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u9a6c\u8981\u597d\u9a6c\uff0c\u4eba\u8981\u52c7\u5065\uff0c\u8fd8\u8981\u7559\u4e0b\u4e00\u6279\u8db3\u591f\u4fdd \u536b\u5bab\u5ef7\u7684\u6b66\u58eb\u3002\u201d \u3000\u3000\u201c\u6210\u90fd\u4f1a\u4f5c\u5b89\u6392\uff0c\u4e09\u65e5\u540e\uff0c\u6559\u573a\u5019\u547d\u3002\u201d \u3000\u3000\u201c\u6709\u52b3\u5c06\u519b\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e09\u65e5\u540e\uff0c\u4e00\u65e9\u51fa\u5e08\uff0c\u5de7\u513f\uff0c\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u5de7\u513f\u62b1\u8d77\u83b2\u513f\uff0c\u968f\u7740\u8881\u7d2b\u70df\uff0c\u56de\u8f6c\u540e\u5bab\u3002 \u3000\u3000\u671b\u7740\u8881\u7d2b\u70df\u6d88\u5931\u7684\u80cc\u5f71\uff0c\u5b87\u6587\u6210\u90fd\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u771f\u7684\u60f3\u4e0d\u901a\u4e86\uff1b\u4e00\u4e2a\u5e74\u8f7b\u7684\u5c0f\u59d1\u5a18\uff0c\u600e \u4f1a\u7ec3\u6210\u4e86\u5982\u6b64\u9ad8\u660e\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u7edf\u9886\uff0c\u4ee5\u5c5e\u4e0b\u4e4b\u89c1\uff0c\u5979\u7ec3\u7684\u4e0d\u662f\u6b66\u529f\uff01\u201d\u4e00\u4e2a\u9762\u767d\u65e0\u987b\uff0c\u4e8c\u5341\u516d\u4e03\u5c81\u7684\u526f\u7edf\u9886\uff0c\u9053\uff1a \u201c\u662f\u5996\u6cd5\uff0c\u624d\u80fd\u4eba\u5316\u9b45\u5f71\uff0c\u5728\u7edf\u9886\u6f2b\u5929\u638c\u52bf\u4e2d\u6d6e\u98d8\u8f6c\u52a8\u3002\u201d \u3000\u3000\u201c\u8427\u96e8\u3002\u201d\u5b87\u6587\u6210\u90fd\u56de\u987e\u4e86\u5e74\u8f7b\u7684\u526f\u7edf\u9886\u4e00\u773c\uff0c\u9053\uff1a\u201c\u662f\u6b66\u529f\uff0c\u53ea\u662f\u7ec3\u6b66\u7684\u65b9\u6cd5\uff0c\u6709\u4e9b \u4e0d\u540c\uff0c\u5979\u4e00\u638c\u6309\u5728\u4e86\u6211\u7684\u2018\u7384\u673a\u7a74\u2019\u4e0a\uff0c\u5374\u7559\u529b\u672a\u53d1\uff0c\u90a3\u4e00\u638c\uff0c\u8db3\u4ee5\u8f7b\u6613\u7684\u53d6\u53bb\u6211\u7684\u6027 \u547d\uff01\u201d \u3000\u3000\u201c\u51cc\u4e91\uff0c\u7530\u5f53\u2026\u2026\u201d \u3000\u3000\u53e6\u5916\u4e24\u4f4d\u526f\u7edf\u9886\uff0c\u5fae\u4e00\u8eac\u8eab\uff0c\u540c\u58f0\u5e94\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u610f\u4e0b\u5982\u4f55\uff1f\u4f60\u4eec\u5c1a\u672b\u51fa\u624b\u4e00\u6218\uff1f\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u662f\u5426\u613f\u610f\u542c\u547d\u4e8e\u4e00\u4f4d\u59d1\u5a18 \u5462\uff1f\u201d \u3000\u3000\u201c\u603b\u7edf\u9886\u90fd\u8d25\u7684\u5fc3\u670d\u53e3\u670d\u3002\u201d\u51cc\u4e91\u3001\u7530\u5f53\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u5c51\u4e0b\u7b49\uff0c\u81ea\u975e\u5176\u654c\uff0c\u613f\u5728\u5979\u9ebe\u4e0b \u6548\u547d\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u56de\u5230\u4e86\u9752\u83b2\u5c0f\u9986\uff0c\u53ea\u89c1\u8881\u5b9d\u513f\u7d20\u8863\u6e05\u9762\uff0c\u606d\u5019\u5728\u5385\u95e8\u4e4b\u5916\uff0c\u5168\u8eab\u7d20\u96c5\uff0c\u4e0d\u89c1\u989c \u8272\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u5934\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u5b9d\u513f\u59b9\u59b9\uff0c\u7687\u4e0a\u600e\u80af\u820d\u4e86\u4f60\uff0c\u8102\u7c89\u4e0d\u65bd\uff0c\u4e00\u8eab\u7d20\u670d\uff0c \u5012\u662f\u522b\u6709\u4e00\u756a\u98ce\u60c5\uff0c\u771f\u662f\u6d53\u5986\u6de1\u62b9\u4e24\u76f8\u5b9c\uff0c\u4e0d\u7528\u8102\u7c89\u8272\u66f4\u5947\uff0c\u53ef\u771f\u662f\u6211\u89c1\u72b9\u601c\u3002\u201d \u3000\u3000\u201c\u771f\u8981\u7d2b\u70df\u59d0\u59d0\u601c\u60dc\u6211\u4e86\u3002\u201d\u8881\u5b9d\u513f\u7728\u52a8\u4e00\u4e0b\u5706\u5706\u7684\u5927\u773c\u775b\uff0c\u6eda\u4e0b\u6765\u4e24\u884c\u6cea\u6c34\uff0c\u7f13\u7f13\u504e \u5165\u8881\u7d2b\u70df\u7684\u6000\u4e2d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8f7b\u63fd\u4f4f\u8881\u5b9d\u513f\u7684\u9999\u80a9\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf4\u5427\uff0c\u6709\u4ec0\u4e48\u8981\u6211\u5e2e\u5fd9\u7684\uff1f\u5c3d\u7ba1\u5f00\u53e3\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u62ed\u53bb\u4e86\u8138\u4e0a\u6cea\u75d5\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\u8fdc\u884c\u5728\u5373\uff0c\u5c0f\u59b9\u5907\u4e86\u4e00\u676f\u6c34\u9152\uff0c\u4e3a\u59d0\u59d0\u996f \u884c\u3002\u201d \u3000\u3000\u8d8a\u53eb\u8d8a\u4eb2\u5bc6\u4e86\uff0c\u628a\u7d2b\u70df\u7684\u540d\u5b57\u4e5f\u7701\u53bb\u4e86\uff0c\u76f4\u63a5\u53eb\u59d0\u59d0\u5566\uff01\u53ea\u662f\u58f0\u97f3\u4e2d\u542b\u6709\u7740\u6de1\u6de1\u7684\u5e7d \u6028\uff0c\u5012\u4f3c\u5145\u6ee1\u7740\u79bb\u60c5\u522b\u7eea\u3002 \u3000\u3000\u201c\u597d\uff01\u4eca\u665a\u4e0a\u6211\u548c\u5b9d\u59b9\u59b9\u628a\u76cf\u8c08\u5fc3\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5982\u679c\u662f\u4f59\u60c5\u4e0d\u5c3d\uff0c\u59d0\u59b9\u4eec\u518d\u6765\u4e2a\u804a \u5e8a\u591c\u8bdd\uff0c\u6211\u4e00\u5b9a\u542c\u5b8c\u4f60\u4e00\u8154\u6101\u82e6\u3002\u201d \u3000\u3000\u201c\u59d0\u59d0\uff0c\u60f3\u5f97\u592a\u7f8e\u4e86\uff0c\u53ef\u662f\u529e\u4e0d\u5230\u7684\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4f60\u8d77\u7a0b\u5728\u5373\uff0c\u7687\u4e0a\u8d2a\u998b\uff0c\u5c3d\u96c6\u4e8e \u59d0\u59d0\u548c\u5c0f\u59b9\u8eab\u4e0a\uff0c\u60f9\u5f97\u5404\u9662\u592b\u4eba\uff0c\u65e0\u4e0d\u5992\u6068\u5c0f\u59b9\uff0c\u6050\u6015\u8fde\u59d0\u59d0\u4e5f\u5df2\u7ecf\u607c\u6068\u4e0a\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u968b\u7080\u5e1d\u7684\u8d2a\u604b\u60c5\u5f62\uff0c\u8881\u7d2b\u70df\u9ed8\u7136\u4e86\u3002 \u3000\u3000\u201c\u521a\u624d\uff0c\u5c0f\u59b9\u7528\u5c3d\u4e86\u5168\u8eab\u672c\u9886\uff0c\u624d\u628a\u7687\u4e0a\u9001\u5230\u7687\u540e\u7684\u5bab\u9662\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7d2b\u70df\u59d0\uff0c\u540e\u5b98\u4f73\u4e3d\u4e09\u5e72\u4f4d\uff0c\u96be\u5077\u6d6e\u751f\u534a\u65e5\u95f2\uff0c\u8d70\u5427\uff01\u62d6\u8fc7\u8fd9\u4e00\u9635\u6e05\u95f2\u8fb0 \u5149\uff0c\u7687\u4e0a\u6405\u5c40\uff0c\u6050\u6015\u6211\u4eec\u5f88\u96be\u518d\u62bd\u51fa\u628a\u76cf\u8c08\u5fc3\u7684\u65f6\u95f4\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u5982\u80af\u7a0d\u7a0d\u62bd\u51fa\u4e00\u4e9b\u8ffd\u9010\u9152\u8272\u7684\u65f6\u95f4\uff0c\u5173\u5fc3\u56fd\u4e8b\uff0c\u4ee5\u4ed6\u7684\u624d \u60c5\u8bc6\u89c1\uff0c\u90a3\u5c31\u662f\u5929\u4e0b\u4e07\u6c11\u4e4b\u798f\u4e86\u3002\u201d \u3000\u3000\u201c\u5929\u884c\u5065\uff0c\u541b\u5b50\u81ea\u5f3a\u4e0d\u606f\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7269\u5fc5\u81ea\u8150\uff0c\u800c\u540e\u866b\u86c0\u4e4b\uff0c\u7d2b\u70df\u59d0\u59d0\uff0c\u6211\u4e0d\u8be5 \u88ab\u9001\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u4f60\u4e5f\u4e0d\u8be5\u6765\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u542c\u5f97\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u201c\u5c0f\u7f8e\u4eba\u80f8\u85cf\u9526\u7ee3\uff0c\u8bfb\u4e66\u4e0d\u5c11\u3002\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u88ab\u4eba\u903c\u9001\u5165\u5bab\u7684\uff1f\u201d \u3000\u3000\u201c\u534a\u7531\u5929\u610f\uff0c\u534a\u7531\u5165\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u743c\u82b1\u9662\u6c34\u9152\u4e00\u676f\uff0c\u5c0f\u59b9\u5c06\u501f\u9152\u58ee\u80c6\uff0c\u5411\u59d0\u59d0\u4e0a\u8bc9 \u8877\u80a0\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u88ab\u5f15\u52a8\u4e86\u597d\u5947\u4e4b\u5ff5\uff0c\u70b9\u70b9\u5934\uff0c\u8fde\u8863\u670d\u4e5f\u4e0d\u6362\u4e86\uff0c\u5429\u5490\u5de7\u513f\uff0c\u7167\u987e\u83b2\u513f\uff0c\u7275\u7740\u8881\u5b9d \u513f\uff0c\u884c\u5165\u4e86\u201c\u743c\u82b1\u9662\u201d\u4e2d\u3002 \u3000\u3000\u4e00\u5f20\u5c0f\u578b\u7684\u6728\u684c\u5b50\uff0c\u6446\u5728\u79cb\u83ca\u73af\u7ed5\u7684\u6885\u6811\u4e0b\uff0c\u4e24\u5f20\u9526\u58a9\uff0c\u5206\u7f6e\u5357\u5317\uff0c\u679c\u7136\u662f\u53ea\u8bf7\u4e86\u8881\u7d2b \u70df\u4e00\u4e2a\u4eba\uff0c\u8fd9\u79cd\u5c0f\u5de7\u7684\u683c\u5c40\uff0c\u591a\u4e2a\u4eba\u5c31\u65e0\u6cd5\u5750\u4e0b\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u5bab\u5973\uff0c\u5148\u9001\u4e0a\u70ed\u6bdb\u5dfe\uff0c\u8ba9\u8881\u7d2b\u70df\u64e6\u628a\u8138\u3002 \u3000\u3000\u9152\u83dc\u7acb\u523b\u9001\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5c0f\u5c0f\u7684\u7389\u76d8\uff0c\u76db\u88c5\u7684\u7cbe\u81f4\u4f73\u80b4\uff0c\u770b\u6837\u5b50\uff0c\u4e5f\u53ea\u9002\u5408\u4e24\u4e2a\u4eba\u98df\u7528\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u660e\u767d\uff0c\u8881\u5b9d\u513f\u4e0d\u60dc\u767b\u95e8\u606d\u5019\uff0c\u8868\u73b0\u51fa\u65e0\u9650\u6e29\u67d4\uff0c\u5b9a\u7136\u662f\u6709\u8981\u4e8b\u5546\u91cf\uff0c\u4f46\u8fd9\u4e2a \u7ea2\u7c89\u5c0f\u4f73\u4eba\uff0c\u884c\u4e8b\u96be\u4f5c\u9884\u6d4b\uff0c\u8881\u7d2b\u70df\u5b9e\u5728\u60f3\u4e0d\u51fa\u5979\u8981\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4e24\u4e2a\u4eba\u90fd\u4efb\u5176\u81ea\u7136\uff0c\u4e0d\u7528\u672f\u6cd5\uff0c\u559d\u5f97\u8138\u6cdb\u9152\u7ea2\uff0c\u6709\u5982\u4e24\u682a\u76db\u653e\u7684\u9c9c\u8273\u6843\u82b1\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6325\u6325\u624b\uff0c\u4e24\u4e2a\u4f3a\u9152\u7684\u5bab\u5973\u9000\u4e0b\uff0c\u6447\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u80fd\u518d\u559d\u4e86\uff0c\u518d\u559d\u4e0b\u53bb\uff0c\u6050\u6015 \u5c31\u8bed\u8a00\u4e0d\u8be6\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u9152\u5165\u6101\u80a0\uff0c\u6613\u5316\u76f8\u601d\u6cea\uff0c\u4e0d\u559d\u4e5f\u7f62\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u4e5f\u6709\u76f8\u601d\u4e4b\u82e6\u5417\uff1f\u201d\u8881\u5b9d\u513f\u7406\u4e00\u7406\u9b13\u8fb9\u6563\u53d1\uff0c\u6cea\u73e0\u513f\u6eda\u6eda\u843d\u4e0b\u3002 \u3000\u3000\u201c\u5b9d\u513f\u59b9\u59b9\uff0c\u4e0d\u8981\u54ed\u554a\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u6765\u81ea\u6df1\u5c71\u7edd\u5cf0\u5e84\u4e0a\uff0c\u90a3\u91cc\u4eba\u8ff9\u7f55\u81f3\uff0c\u5e7d\u5bc2\u5e74 \u534e\u4e8c\u5341\u6625\uff0c\u90a3\u4f1a\u5c1d\u8fc7\u60c5\u6ecb\u5473\uff0c\u8fd9\u4e00\u8eab\u6e05\u767d\uff0c\u4fbf\u5b9c\u4e86\u98ce\u6d41\u7687\u4e0a\uff0c\u4eba\u95f4\u76f8\u601d\u4e4b\u82e6\uff0c\u53ea\u597d\u4ece\u4e66\u4e2d\u5bfb \u627e\u4e86\uff0c\u96be\u9053\u90a3\u76f8\u601d\u4e4b\u60c5\uff0c\u771f\u7684\u80fd\u8ba9\u4eba\u9b42\u7275\u68a6\u7ed5\uff0c\u67d4\u80a0\u5bf8\u65ad\u5417\uff1f\u201d \u3000\u3000\u201c\u4eba\u751f\u81f3\u82e6\u554a\uff01\u201d\u5b9d\u513f\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e9\u971c\u6467\u843d\u82b1\u5f00\u68a6\uff0c\u84d3\u857e\u67d3\u8840\u4ea6\u4e3a\u60c5\uff0c\u8eab\u5bc4\u6df1\u5bab \u7b11\u9765\u6cea\uff0c\u9b42\u7ed5\u9752\u5c71\u8305\u820d\u4e2d\u3002\u201d \u3000\u3000\u8bf4\u7684\u975e\u5e38\u660e\u767d\u4e86\uff0c\u8881\u7d2b\u70df\u4ea6\u4e3a\u4e4b\u5fc3\u5934\u51c4\u7136\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4ed6\u662f\u8c01\u554a\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u4f60\u4e00\u8eab\u672f\u6cd5\uff0c\u975e\u540c\u5e73\u5e38\u5973\u5b50\uff0c\u4e3a\u4ec0\u4e48\u7518\u613f\u5fcd\u53d7\u8fd9\u79cd\u547d\u8fd0\u7684\u62e8\u5f04\u5462\uff1f\u96be \u9053\u662f\u53d7\u5230\u631f\u6301\uff0c\u53cd\u6297\u65e0\u529b\uff1f \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7d2b\u70df\u59d0\u53ef\u662f\u60f3\u770b\u770b\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4e5f\u8ffd\u5230\u4e86\u957f\u5b89\uff1f\u679c\u771f\u5982\u6b64\uff0c\u5012\u4e5f\u662f\u4e00\u4f4d\u591a\u60c5\u79cd\u5b50\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u53e3\u4e2d\u5e94\u8bdd\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4f60\u51c6\u5907\u65bd\u5c55\u672f\u6cd5\uff0c\u62d8\u4ed6\u6765\u6b64\uff0c\u53ef\u4e5f\u662f\u8272\u80c6\u5305\u5929\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u4ed6\u5728\u8fd9\u91cc\uff01\u201d\u8881\u5b9d\u513f\u7f13\u7f13\u7531\u8896\u4e2d\u53d6\u51fa\u4e00\u5e45\u7ee2\u753b\uff0c\u9053\uff1a\u201c\u5c0f\u59b9\u4e0d\u5584\u4e39\u9752\uff0c\u753b\u7684 \u4e0d\u597d\uff0c\u4e0d\u8981\u89c1\u7b11\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u63a5\u8fc7\u7ee2\u753b\uff0c\u9053\uff1a\u201c\u8981\u4ed4\u7ec6\u7684\u77bb\u4ef0\u77bb\u4ef0\u4e86\uff0c\u4ec0\u4e48\u6837\u4e00\u4e2a\u4eba\u7269\uff0c\u8bf1\u52a8\u5f97\u9b42\u7275\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5979\u770b\u5230\u4e86\u90a3\u5e45\u753b\uff0c\u4e00\u4e2a\u8eab\u7740\u9ec4\u888d\u7684\u5e74\u8f7b\u4eba\uff0c\u9f99\u59ff\u9e70\u76ee\uff0c\u6c14\u5ea6\u8f69\u6602\uff0c\u9690\u9690\u7136\u900f\u51fa\u4e86 \u9886\u8896\u5929\u4e0b\u7684\u6c14\u52bf\u3002 \u3000\u3000\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u8881\u7d2b\u70df\u5931\u53bb\u4e86\u63a7\u5236\uff0c\u95ee\u7684\u58f0\u8272\u4ff1\u5389\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff01\u53ef\u662f\u5c0f\u59b9\u7684\u624b\u7b14\uff0c\u4e0d\u582a\u5165\u76ee\u5417\uff1f\u201d\u8881\u5b9d\u513f\u7f13\u7f13\u6536\u7ee2\u9762\uff0c\u85cf\u5165\u8896\u4e2d\u3002 \u3000\u3000\u201c\u5b9d\u513f\u59b9\u59b9\u7684\u4e39\u9752\u4f20\u795e\u3002\u201d\u8881\u7d2b\u70df\u6536\u6151\u4f4f\u9707\u8361\u7684\u5fc3\u60c5\uff0c\u6062\u590d\u4e86\u9547\u9759\uff0c\u7b11\u9053\uff1a\u201c\u753b\u5f97\u592a\u597d \u4e86\uff0c\u60c5\u90ce\u5982\u65af\uff0c\u96be\u602a\u5b9d\u513f\u59b9\u59b9\u5bc4\u60c5\u4e07\u659b\uff0c\u751f\u6b7b\u4e0d\u6094\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u59b9\u798f\u8584\u547d\u6d45\uff0c\u7d2b\u70df\u59d0\u5982\u80af\u601c\u60dc\uff0c\u7559\u4e0b\u4e09\u5206\u60c5\u4e49\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u4f60\u4eec\u5728\u8fd9\u91cc\u996e\u9152\u5feb\u6d3b\uff0c\u628a\u6715\u4e22\u5728\u4e00\u8fb9\u53bb\u4e86\u3002\u201d\u968b\u7080\u5e1d\u5feb\u6b65\u5954\u884c\u8fc7\u6765\uff0c\u8dd1\u7684\u4e0a\u6c14 \u4e0d\u63a5\u4e0b\u6c14\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u600e\u4e48\u8fde\u8f66\u8f87\u4e5f\u4e0d\u4e58\u5750\uff0c\u5c31\u8fd9\u4e48\u5927\u6b65\u8dd1\u4e86\u8fc7\u6765\uff0c\u8d76\u6765\u7684\u5982\u6b64 \u4e4b\u5de7\uff0c\u96be\u9053\u8fd9\u662f\u5929\u610f\uff0c\u903c\u6211\u4e0b\u624b\u2026\u2026 \u3000\u3000\u8d77\u8eab\u63a5\u9a7e\uff0c\u8dea\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u59be\u5983\u8881\u5b9d\u513f\u8fce\u9a7e\u4e0d\u53ca\uff0c\u7687\u4e0a\u6055\u7f6a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u867d\u89c9\u5bab\u4e2d\u7684\u7e41\u6587\u7f1b\u8282\uff0c\u607c\u4eba\u5f97\u5f88\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u9075\u4ece\uff0c\u4e5f\u53ea\u597d\u76c8\u76c8\u8dea\u4e0b\uff0c\u9053\uff1a\u201c\u59be \u5983\u63a5\u9a7e\uff01\u7687\u4e0a\u4e07\u5b89\uff01\u201d \u3000\u3000\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff0c\u6715\u4e0d\u662f\u602a\u4f60\u4eec\u5462\uff01\u201d\u4e00\u624b\u4e00\u4e2a\u633d\u8d77\u4e86\u8881\u7d2b\u70df\u3001\u8881\u5b9d\u513f\uff0c\u63a5\u9053\uff1a\u201c\u5929\u4e0a\u4eba \u95f4\u4e24\u4f73\u4e3d\uff0c\u6715\u75bc\u7231\u8fd8\u6765\u4e0d\u53ca\u4e86\uff0c\u54ea\u91cc\u5fcd\u5fc3\u8d23\u602a\u4f60\u4eec\uff0c\u4f46\u4f60\u4eec\u5f03\u6715\u4e0d\u987e\uff0c\u5e94\u8be5\u7f5a\uff01\u201d \u3000\u3000\u201c\u7687\u4e0a\u5728\u7687\u540e\u5bab\u4e2d\u5750\u4eab\u6e29\u67d4\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u59be\u5983\u5077\u5f97\u4e00\u523b\u6e05\u95f2\uff0c\u8bbe\u5bb4\u4e3a\u7d2b\u70df\u59d0\u59d0\u996f \u884c\uff0c\u4e5f\u662f\u4ee3\u7687\u4e0a\u8868\u8fbe\u5fc3\u610f\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u597d\uff0c\u6715\u6709\u8d4f\u3002\u201d\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u770b\u8fc7\u660e\u73e0\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5e74\u8f7b\u7684\u592a\u76d1\uff0c\u5feb\u901f\u5954\u4e86\u8fc7\u6765\uff0c\u53cc\u819d\u8dea\u4e0b\uff0c\u53cc\u624b\u4e3e\u8d77\u4e86\u4e00\u4e2a\u65b9\u5f62\u9526\u76d2\u3002 \u3000\u3000\u968b\u7080\u5e1d\u7b11\u9053\uff1a\u201c\u5323\u4e2d\u6709\u73cd\u73e0\u5341\u9897\uff0c\u5149\u6da6\u65e0\u7455\uff0c\u4e32\u6210\u9879\u7ec3\uff0c\u914d\u537f\u5bb6\u7edd\u4e16\u6625\u8272\uff0c\u73e0\u5149\u82b1\u5bb9\uff0c \u76f8\u4e92\u6620\u8f89\uff0c\u5f53\u53ef\u500d\u589e\u4e3d\u8272\u3002\u201d \u3000\u3000\u8dd1\u5f97\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u53ea\u4e3a\u9001\u7f8e\u4eba\u4e00\u5323\u660e\u73e0\u3002 \u3000\u3000\u8881\u5b9d\u513f\u542f\u5323\u770b\u53bb\uff0c\u5341\u9897\u660e\u73e0\uff0c\u653e\u5728\u9ec4\u7f0e\u886c\u5e95\u7684\u5323\u4e2d\uff0c\u9897\u9897\u5927\u5982\u9e3d\u5375\uff0c\u5149\u534e\u56db\u5c04\uff0c\u679c\u7136\u662f \u5c11\u89c1\u7684\u73cd\u54c1\u3002 \u3000\u3000\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u7687\u4e0a\u539a\u8d50\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\u4e3a\u56fd\u8f9b\u52b3\uff0c\u8fdc\u5f81\u5728\u5373\uff0c\u6715\u4e5f\u6709\u8d4f\u3002\u201d\u4e3e\u624b\u4e00\u62db\uff0c\u53e6\u4e00\u4e2a\u5e74\u8f7b\u592a\u76d1\uff0c\u75be\u5954\u800c\u81f3\uff0c\u9ad8 \u9ad8\u4e3e\u8d77\u4e86\u4e00\u4e2a\u5c0f\u5de7\u7684\u53e4\u94dc\u76d2\u5b50\uff0c\u968b\u7080\u5e1d\u4f38\u624b\u53d6\u8fc7\uff0c\u5fae\u4e00\u6446\u5934\uff0c\u4e24\u4e2a\u592a\u76d1\u8d77\u8eab\u9000\u4e0b\uff0c\u624d\u628a\u94dc\u76d2 \u4ea4\u7ed9\u4e86\u8881\u7d2b\u70df\uff0c\u9053\uff1a\u201c\u537f\u5bb6\u8fdc\u5f81\u5343\u91cc\uff0c\u96be\u514d\u5175\u6218\u51f6\u5371\uff0c\u4f69\u5e26\u6b64\u7269\uff0c\u4ee5\u4fdd\u5e73\u5b89\u3002\u201d \u3000\u3000\u4e0d\u7ba1\u94dc\u76d2\u4e2d\u653e\u7684\u4ec0\u4e48\uff0c\u53ea\u662f\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5df2\u8ba9\u8881\u7d2b\u70df\u5fc3\u4e2d\u611f\u52a8\u4e86\uff0c\u6bd5\u7adf\u4ed6\u662f\u5979\u7b2c\u4e00\u6b21\u6cbe\u8eab \u7684\u7537\u4eba\uff0c\u9053\u662f\u65e0\u60c5\u5374\u6709\u60c5\u3002 \u3000\u3000\u5979\u70b9\u70b9\u5934\uff0c\u63a5\u8fc7\u94dc\u76d2\uff0c\u4e5f\u4e0d\u6253\u5f00\u89c2\u770b\uff0c\u968f\u624b\u63e3\u5165\u6000\u4e2d\u3002 \u3000\u3000\u201c\u6715\u4e00\u5411\u8d4f\u7f5a\u5206\u660e\uff0c\u8d4f\u5f52\u8d4f\uff0c\u7f5a\u5f52\u7f5a\uff0c\u4e0d\u80fd\u5e76\u4e3a\u4e00\u8c08\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u7f5a\u5440\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u6012\u6c14\u5df2\u6d88\uff0c\u8fd8\u7f5a\u4e2a\u4ec0\u4e48\u52b2\u5462\uff1f\u201d \u3000\u3000\u201c\u6715\u7f5a\u4f60\u4eca\u591c\u7559\u5bbf\u5728\u2018\u743c\u82b1\u5bab\u2019\u4e2d\u3002\u201d \u3000\u3000\u53c8\u8981\u5de6\u62e5\u53f3\u62b1\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u79c0\u7709\u513f\u9053\uff1a\u201c\u5bb9\u59be\u53bb\u66f4\u8863\u518d\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u501f\u5b9d\u5983\u4e00\u88ad\u5bab\u888d\u5c31\u597d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6c89\u9ed8\u4e86\uff0c\u5fd6\u9053\uff1a\u201c\u53ef\u771f\u662f\u987d\u52a3\u5440\uff01\u4e0a\u4e00\u6b21\u6574\u7684\u6211\u7f9e\u5fff\u6ee1\u8179\uff0c\u600e\u4e48\u53c8\u8981\u73a9\u8fd9\u4e2a\u7f9e\u715e \u4eba\u7684\u628a\u620f\uff1f\u201d \u3000\u3000\u201c\u4e0a\u763e\u4e86\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u7d2b\u70df\u5c31\u8981\u51fa\u5f81\u554a\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u518d\u884c\u901a\u878d\u4e00\u6b21\u5427\uff01\u7d2b\u70df\uff0c\u6b64\u540e\u6570\u4e2a\u6708\u76f8\u601d\u4e4b\u82e6\uff0c\u591f\u6715\u53d7\u7684\u4e86\uff0c \u6715\u4eb2\u81ea\u53bb\u5e2e\u4f60\u53d6\u6765\u7761\u888d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u62db\u8fd8\u771f\u7684\u5389\u5bb3\uff0c\u8f6c\u8eab\u6b32\u8d70\uff0c\u5374\u88ab\u8881\u7d2b\u70df\u4e00\u628a\u62d6\u4f4f\u4e86\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u8863\u888d\u4e8b\u5c0f\uff0c\u59be\u5983 \u771f\u6b63\u5173\u5fc3\u7684\u662f\u7687\u4e0a\u7684\u8eab\u4f53\u3002\u201d \u3000\u3000\u201c\u9762\u5bf9\u7740\u4e24\u4f4d\u537f\u5bb6\u7684\u52a8\u4eba\u59ff\u5bb9\uff0c\u6715\u54ea\u91cc\u80fd\u591f\u81ea\u7981\uff1f\u7d2b\u70df\uff0c\u4f60\u8981\u4f53\u8c05\u6715\u7684\u4e00\u7247\u6df1\u60c5\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff1f\u541b\u738b\u5ba0\u5e78\uff0c\u7eb5\u7136\u662f\u5f3a\u4eba\u6240\u96be\uff0c\u4e5f\u6ca1\u6709\u5982\u6b64\u4e00\u4e2a\u8f9b\u82e6\u6cd5\uff0c\u7528\u751c\u8a00\u871c \u8bed\u53bb\u54c4\u9a97\u5ad4\u5983\u3002 \u3000\u3000\u201c\u4efb\u4ed6\u6298\u817e\u5427\uff01\u201d\u8881\u7d2b\u70df\u7a81\u7136\u60f3\u901a\u4e86\uff0c\u6697\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u7389\u6d01\u51b0\u6e05\u7684\u8eab\u5b50\uff0c\u90fd\u88ab\u4ed6\u541e\u4e0b\u53bb \u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u4fdd\u7559\u7684\uff1f\u201d\u4efb\u7531\u968b\u7080\u5e1d\u628a\u5a07\u8eaf\u6402\u5165\u6000\u4e2d\u3002 \u3000\u3000\u201c\u5b9d\u5983\uff0c\u4f60\u4e0d\u4f1a\u62d2\u7edd\u6715\u5427\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u90fd\u88ab\u78e8\u6298\u7684\u56db\u5e73\u516b\u7a33\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u8fd8\u80fd\u600e\u4e48\u6837\u5462\uff1f\u7687\u4e0a\u559c\u7231\uff0c\u59be\u5983\u4e5f\u53ea \u6709\u5a49\u8f6c\u627f\u6b22\u4e86\uff01\u201d \u3000\u3000\u8fd9\u2014\u6536\u4e0d\u4f3c\u4e0a\u6b21\uff0c\u7adf\u4e5f\u4f3c\u6709\u70b9\u52c9\u5f3a\u3002 \u3000\u3000\u4e09\u65e5\u540e\u3002 \u3000\u3000\u9ece\u660e\u65f6\u5206\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8d76\u5230\u6559\u573a\uff0c\u4e24\u767e\u94c1\u9a91\uff0c\u5df2\u7136\u5217\u961f\u606d\u5019\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53c8\u7ed9\u4e86\u5b87\u6587\u6210\u90fd\u7b49\u4e00\u4e2a\u5f88\u5927\u7684\u60ca\u5947\uff0c\u90a3\u662f\u8eab\u53d7\u91cd\u4f24\u7684\u83b2\u513f\uff0c\u5df2\u5b8c\u5168\u6062\u590d\u4e86\u5065\u5eb7\u3002 \u5979\u8eab\u4f69\u5c1a\u65b9\u5b9d\u5251\uff0c\u9a91\u5728\u4e00\u5339\u6a31\u82b1\u9a6c\u4e0a\uff0c\u7cbe\u795e\u9971\u6ee1\uff0c\u4e00\u8138\u7b11\u5bb9\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9a91\u5728\u4e00\u5339\u767d\u9a6c\u4e0a\uff0c\u5bf9\u56db\u4e2a\u7387\u961f\u7684\u526f\u603b\u7edf\u9886\u542b\u7b11\u62db\u547c\uff0c\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u3001\u5218\u98de \u9e4f\uff0c\u56db\u4eba\u4e5f\u8868\u73b0\u51fa\u5341\u5206\u7684\u656c\u91cd\uff0c\u4e2a\u4e2a\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\u3002\u201d\u8881\u7d2b\u70df\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4fdd\u62a4\u7687\u4e0a\u7684\u4e8b\uff0c\u53ef\u6709\u5b89\u6392\uff1f\u201d \u3000\u3000\u201c\u4e24\u4f4d\u5973\u526f\u603b\u7edf\u9886\uff0c\u5316\u8eab\u5bab\u5973\uff0c\u6697\u4e2d\u5b88\u5019\u5728\u7687\u4e0a\u5de6\u53f3\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u53e6\u6709\u56db\u4f4d\u526f\u603b \u7edf\u9886\uff0c\u5404\u7387\u4e00\u767e\u540d\u6b66\u58eb\uff0c\u5206\u4e24\u73ed\u65e5\u591c\u5f85\u547d\uff0c\u6536\u5230\u4fe1\u53f7\uff0c\u7acb\u523b\u8d76\u5f80\u652f\u63f4\uff0c\u7687\u5bab\u56db\u5468\uff0c\u5206\u5e03\u4e86\u4e8c \u767e\u540d\u6b66\u58eb\u5de1\u5b88\uff0c\u6212\u5907\u68ee\u4e25\uff0c\u98de\u9e1f\u96be\u5165\uff0c\u5c5e\u4e0b\u62c5\u5fc3\u7684\u662f\u53d8\u751f\u8098\u814b\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u7565\u4e00\u6c89\u541f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u743c\u82b1\u9662\u7684\u8881\u592b\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5979\u4e0d\u662f\u5e73\u5e38\u4eba\u554a\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u6050\u6015\u4e0d\u5728\u6210\u90fd\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u201c\u5979\u4e0d\u4f1a\u76f4\u63a5\u7684\u4e0b\u624b\u5427\uff01\u201d\u6c89\u601d\u4e86\u7247\u523b\uff0c\u63a5\u9053\uff1a\u201c\u5979\u771f\u6709\u4e0b\u624b\u4e4b\u5fc3\uff0c\u6211\u4eec\u7559\u5728\u957f\u5b89\uff0c\u4e5f \u4e00\u6837\u65e0\u6cd5\u9632\u8303\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u2026\u2026\u201d \u3000\u3000\u662f\u4ec0\u4e48\uff1f\u5b87\u6587\u6210\u90fd\u5374\u6ca1\u6709\u63a5\u4e0b\u53bb\uff0c\u5fc3\u7167\u4e0d\u5ba3\u4e86\u3002 \u3000\u3000\u201c\u51fa\u53d1\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u517c\u7a0b\u76f4\u5954\u592a\u539f\u90e1\uff0c\u6cbf\u9014\u4e0a\uff0c\u4e0d\u8981\u60ca\u52a8\u5b98\u5e9c\u4e2d\u4eba\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u5b87\u6587\u6210\u90fd\u4e3e\u624b\u4e00\u6325\uff0c\u4e24\u767e\u94c1\u9a91\uff0c\u8fce\u7740\u62c2\u9762\u6653\u98ce\uff0c\u79bb\u5f00\u4e86\u957f\u5b89\u3002 \u3000\u3000\u4eba\u662f\u9ad8\u624b\uff0c\u9a6c\u662f\u597d\u9a6c\uff0c\u65e5\u591c\u517c\u7a0b\uff0c\u65ec\u65e5\u5de5\u592b\uff0c\u5df2\u8d76\u4e86\u4e24\u5343\u4f59\u91cc\u7684\u8def\u7a0b\uff0c\u5728\u592a\u539f\u8fd1\u90ca\u7684\u767d \u5bb6\u5e84\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5730\u65b9\u867d\u53eb\u767d\u5bb6\u5e84\uff0c\u4e8b\u5b9e\u662f\u9887\u5177\u89c4\u6a21\u7684\u5e02\u9547\uff0c\u5ba2\u6808\u5e02\u8086\uff0c\u4e00\u5e94\u4ff1\u5168\u3002 \u3000\u3000\u5341\u4f59\u65e5\u517c\u7a0b\u8d76\u8def\uff0c\u4e24\u767e\u94c1\u9a91\u5168\u65e0\u75b2\u6001\uff0c\u53ea\u6709\u5f88\u5c11\u9a91\u9a6c\u7684\u83b2\u513f\u3001\u5de7\u513f\uff0c\u6709\u4e9b\u4e0d\u80dc\u5176\u82e6\u7684\u611f \u89c9\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53ec\u96c6\u4e86\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u3001\u5218\u98de\u9e4f\u56db\u4f4d\u9886\u961f\u548c\u5b87\u6587\u6210\u90fd\uff0c\u7814\u5546\u8fdb\u5165\u592a\u539f\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u5979\u5e0c\u671b\u80fd\u591f\u4e0d\u60ca\u52a8\u7559\u5b88\u4faf\u674e\u6e0a\uff0c\u6084\u7136\u5165\u592a\u539f\u3002 \u3000\u3000\u201c\u8fd9\u6050\u6015\u662f\u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\u60c5\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u674e\u4faf\u7237\uff0c\u662f\u4e00\u4f4d\u80f8\u6709\u97ec\u7565\u7684\u5c06\u519b\uff0c\u5e55\u4e2d \u4e0d\u4e4f\u8c0b\u58eb\u3001\u9ad8\u624b\uff0c\u6211\u60f3\u54b1\u4eec\u517c\u7a0b\u75be\u8fdb\u7684\u901f\u5ea6\u867d\u5feb\uff0c\u4f46\u5374\u65e0\u6cd5\u7792\u8fc7\u674e\u6e0a\u7684\u8033\u76ee\uff0c\u4e5f\u8bb8\uff0c\u6211\u4eec\u65e9 \u5df2\u5728\u4ed6\u7684\u76d1\u89c6\u4e4b\u4e0b\uff01\u201d \u3000\u3000\u201c\u5c06\u519b\u8bf4\u7684\u6709\u7406\uff0c\u7d2b\u70df\u76f8\u4fe1\u6211\u4eec\u5df2\u5728\u592a\u539f\u9ad8\u624b\u76d1\u89c6\u4e4b\u4e0b\u3002\u201d\u5fae\u6563\u2014\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u674e\u6e0a\u8fd8 \u4e0d\u6562\u6b63\u9762\u548c\u671d\u5ef7\u4e3a\u654c\uff0c\u6240\u4ee5\uff0c\u4ed6\u8fd8\u4e0d\u6562\u660e\u76ee\u5f20\u80c6\u7684\u5bf9\u4ed8\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u6ca1\u63d0\u62ff\u674e\u6e0a\u7684\u7528\u5fc3\uff0c\u6211 \u53ea\u8981\u6697\u4e2d\u4fa6\u5bdf\uff0c\u4ec0\u4e48\u4eba\u5728\u884c\u6cd5\u7b56\u52a8\u5929\u673a\u53d8\u5316\u3002\u201d \u3000\u3000\u201c\u7b56\u52a8\u5929\u673a\u53d8\u5316\uff1f\u201d\u5b87\u6587\u6210\u90fd\u6709\u4e9b\u5403\u60ca\u5730\u9053\uff1a\u201c\u592a\u539f\u7684\u5e55\u5bbe\u4e2d\uff0c\u4f1a\u6709\u5982\u6b64\u7684\u9ad8\u624b\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u660e\u767d\uff0c\u5929\u673a\u8fd0\u884c\uff0c\u89e3\u91ca\u8d77\u6765\uff0c\u975e\u5e38\u7d2f\u4eba\uff0c\u7528\u4e0d\u7740\u591a\u4f5c\u8bf4\u660e\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6240\u4ee5\uff0c\u6211\u4eec\u624d\u6765\u6b64\u4fa6\u5bdf\u6c34\u843d\u77f3\u51fa\uff0c\u6211\u4eec\u4e0d\u52a8\u674e\u6e0a\uff0c\u4f46\u4e0d\u80fd\u653e\u8fc7\u7b56\u52a8\u5929\u673a\u53d8\u5316\u7684\u4eba\uff01\u201d \u3000\u3000\u201c\u56fd\u5e08\u5df2\u6210\u7af9\u5728\u80f8\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u8981\u6211\u4eec\u5982\u4f55\u914d\u5408\uff0c\u53ea\u7ba1\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u4e24\u767e\u94c1\u9a91\uff0c\u6682\u65f6\u5728\u8fd9\u91cc\u517b\u7cbe\u84c4\u9510\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u660e\u5929\uff0c\u6211\u4eec\u5206\u4e09\u6279\u8fdb\u5165\u592a\u539f\uff0c\u8427 \u96e8\u3001\u7530\u5f53\uff0c\u5404\u9009\u4e09\u540d\u9ad8\u624b\uff0c\u6613\u88c5\u5165\u57ce\uff0c\u5b87\u6587\u5c06\u519b\u548c\u6211\u8d70\u5728\u4e00\u8d77\uff0c\u51cc\u4e91\u548c\u5218\u98de\u9e4f\u4e24\u4f4d\u5c06\u519b\uff0c\u7559 \u5b88\u767d\u5bb6\u5e84\uff0c\u7b49\u4faf\u8baf\u606f\uff0c\u8d76\u5f80\u63a5\u5e94\uff0c\u4e09\u6279\u5165\u57ce\u4eba\u624b\uff0c\u6697\u4f5c\u547c\u5e94\uff0c\u4f46\u8981\u88c5\u51fa\u4e92\u4e0d\u76f8\u8bc6\u7684\u6e38\u5ba2\u3002\u201d \u3000\u3000\u201c\u5c31\u4f9d\u56fd\u5e08\u5429\u5490\uff01\u201d\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6cdb\u5347\u8d77\u4e00\u80a1\u559c\u60a6\uff0c\u5fd6\u9053\uff1a\u201c\u6613\u88c5\u5165\u57ce\uff0c\u8d70\u5728\u4e00\u8d77\uff0c\u4e0d \u77e5\u4f60\u8981\u628a\u6211\u5b89\u6392\u4e2a\u4ec0\u4e48\u8eab\u4efd\uff1f\u7eb5\u662f\u6267\u97ad\u5760\u956b\u7684\u5c0f\u53ae\uff0c\u6211\u4e5f\u5fc3\u7518\u60c5\u613f\u7684\u8ba4\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u6539\u626e\u5c0f\u53ae\uff0c\u5374\u6613\u626e\u4e3a\u4e00\u4e2a\u7559\u7740\u77ed\u987b\u7ed5\u988a\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e5f\u53d8\u4e86\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u6e38\u5b66\u4e66\u751f\uff0c\u83b2\u513f\u548c\u5de7\u513f\uff0c\u626e\u6210\u4e86\u968f\u884c\u7684\u4e66\u50ee\uff0c\u4e00\u62c5\u7434\u5251\uff0c\u4e00 \u62c5\u4e66\uff0c\u5012\u4e5f\u76f8\u6a21\u76f8\u6837\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u88ad\u9752\u888d\uff0c\u8881\u7d2b\u70df\u4e00\u4ef6\u84dd\u886b\uff0c\u6309\u6b65\u5f53\u8f66\uff0c\u901b\u8fdb\u4e86\u592a\u539f\u57ce\u4e2d\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\uff0c\u4e5f\u5728\u5bb9\u8c8c\u4e0a\u7a0d\u7ecf\u6539\u53d8\uff0c\u5404\u5e26\u7740\u4e09\u4e2a\u9ad8\u624b\uff0c\u8fdc\u968f\u5728\u8881\u7d2b\u70df\u8eab\u540e\u3002 \u3000\u3000\u8881\u7d2b\u70df\u884c\u7ecf\u8fc7\u7559\u5b88\u4faf\u5e9c\uff0c\u4e00\u5927\u7247\u9752\u7816\u56f4\u7ed5\u7684\u5927\u5b85\u9662\uff0c\u5360\u5730\u5728\u767e\u4ea9\u4ee5\u4e0a\u3002 \u3000\u3000\u4faf\u5e9c\u7684\u6c14\u6d3e\u867d\u5927\uff0c\u4f46\u6212\u5907\u5374\u4e0d\u68ee\u4e25\uff0c\u5927\u95e8\u53e3\u5904\u4e03\u5c42\u77f3\u9636\u4e0a\uff0c\u53ea\u7ad9\u7740\u56db\u4e2a\u5b88\u95e8\u7684\u6b66\u58eb\uff0c\u95e8 \u524d\u4eba\u6765\u4eba\u5f80\u3002 \u3000\u3000\u7559\u6b65\u89c2\u770b\uff0c\u6b66\u58eb\u4eec\u4e5f\u4e0d\u5e72\u4e0e\uff0c\u9887\u6709\u5e73\u6613\u8fd1\u4eba\u7684\u98ce\u8303\u3002 \u3000\u3000\u4f46\u770b\u4e0d\u5230\u4efb\u4f55\u79d8\u5bc6\u4e86\uff0c\u5927\u95e8\u655e\u5f00\uff0c\u4f46\u8fce\u9762\u4e00\u9053\u5899\u58c1\uff0c\u906e\u4f4f\u4e86\u5927\u95e8\u5185\u7684\u666f\u7269\uff0c\u53ea\u80fd\u770b\u5230\u8fd9 \u58c1\u4e0a\u4e00\u5e45\u56fe\u753b\uff0c\u84dd\u5929\u767d\u4e91\u4e4b\u4e0b\uff0c\u6709\u51e0\u53ea\u9e1f\u513f\u5728\u5c55\u7ffc\u7ff1\u7fd4\uff0c\u5176\u4e2d\u4e00\u53ea\u7a0d\u5927\u4e00\u4e9b\uff0c\u4f3c\u662f\u4e00\u53ea\u98de \u9e70\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u674e\u6e0a\u4e5f\u662f\u5927\u968b\u671d\u7684\u5f00\u56fd\u540d\u5c06\uff0c\u592a\u539f\u4faf\u5e9c\u7684\u8fce\u58c1\u5899\u4e0a\uff0c\u4ec5\u753b\u4e86\u4e09\u53ea \u96c0\u513f\u4e00\u53ea\u9e70\uff0c\u672a\u514d\u6709\u4e9b\u4e0d\u767b\u5927\u96c5\u4e4b\u5802\u4e86\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u5bf9\u90a3\u5e45\u753b\uff0c\u5374\u770b\u7684\u5341\u5206\u5165\u795e\uff0c\u53cc\u76ee\u4e2d\u6709\u5149\u8292\u5c04\u51fa\uff0c\u663e\u7136\u5df2\u8fd0\u8d77\u795e\u529f\u3002 \u3000\u3000\u4e0d\u662f\u968f\u610f\u7684\u6d4f\u89c8\u7740\uff0c\u4f3c\u662f\u8981\u628a\u90a3\u5e45\u753b\u6df1\u5370\u5728\u8111\u9645\u4e2d\u3002 \u3000\u3000\u8fd9\u5c31\u5f15\u8d77\u4e86\u5b87\u6587\u6210\u90fd\u7684\u5f3a\u70c8\u597d\u5947\uff0c\u63d0\u771f\u6c14\uff0c\u51dd\u795e\u770b\u53bb\u3002 \u3000\u3000\u51e0\u4eba\u505c\u8eab\u4e4b\u5904\uff0c\u5728\u4faf\u5e9c\u5bf9\u9762\uff0c\u9694\u7740\u4e00\u9053\u5f88\u5bbd\u7684\u8857\u9053\uff0c\u76f8\u8ddd\u81f3\u5c11\u5728\u4e03\u4e08\u4ee5\u4e0a\uff0c\u8981\u770b\u7684\u6e05\u695a \u4ed4\u7ec6\uff0c\u5c31\u4e0d\u662f\u4e00\u822c\u4eba\u76ee\u529b\u80fd\u53ca\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5185\u529f\u7cbe\u6e5b\uff0c\u8fd9\u4e00\u8fd0\u8db3\u76ee\u529b\uff0c\u679c\u7136\u770b\u51fa\u4e86\u4e00\u70b9\u95e8\u9053\uff0c\u4e00\u884c\u8349\u4e66\uff0c\u5199\u7684\u662f\u201c\u7075\u96c0\u620f \u9e70\u201d\uff0c\u4f46\u5374\u6ca1\u4e66\u4e0b\u753b\u56fe\u4eba\u7684\u540d\u5b57\u3002 \u3000\u3000\u4e09\u53ea\u98de\u96c0\uff0c\u5206\u5728\u4e09\u4e2a\u65b9\u4f4d\u4e0a\uff0c\u795e\u6001\u7075\u52a8\uff0c\u6829\u6829\u5982\u751f\uff0c\u4e00\u53ea\u98de\u9e70\uff0c\u4f3c\u662f\u7531\u9ad8\u7a7a\u6251\u4e0b\uff0c\u4f46\u5374 \u4e0d\u77e5\u6251\u7740\u90a3\u4e00\u53ea\u98de\u96c0\u624d\u597d\uff0c\u4e09\u96c0\u548c\u98de\u9e70\u4e4b\u95f4\uff0c\u8ddd\u79bb\u76f8\u82e5\u3002 \u3000\u3000\u753b\u56fe\u5e03\u5c40\uff0c\u5e76\u4e0d\u51fa\u5947\uff0c\u5999\u5728\u795e\u97f5\uff0c\u770b\u4e45\u4e86\uff0c\u4f60\u4f1a\u611f\u89c9\u5230\u7075\u96c0\u548c\u98de\u9e70\u5168\u662f\u6d3b\u7684\uff0c\u4e5f\u4f1a\u611f\u53d7 \u5230\u98de\u9e70\u7684\u8ff7\u60d8\uff0c\u4e09\u53ea\u98de\u96c0\u7684\u5201\u86ee\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u4e2a\u4e30\u795e\u4fca\u90ce\uff0c\u4e8c\u5341\u5de6\u53f3\u7684\u5e74\u8f7b\u4eba\uff0c\u6b65\u51fa\u4faf\u5e9c\uff0c\u6321\u4f4f\u4e86\u58c1\u753b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7acb\u523b\u8f6c\u8eab\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u83b2\u513f\u548c\u5de7\u513f\uff0c\u6b63\u611f\u5230\u65e0\u804a\uff0c\u5979\u4eec\u770b\u4e0d\u51fa\u90a3\u5e45\u753b\uff0c\u6709\u4ec0\u4e48\u597d\u770b\uff0c\u66f4\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u52a8\u4eba\u7684\u7279 \u8272\u3002 \u3000\u3000\u9752\u888d\u4eba\u76f4\u770b\u5230\u56db\u4eba\u80cc\u5f71\u6d88\u5931\u4e86\uff0c\u624d\u8f6c\u56de\u5165\u5e9c\u4e2d\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u4e0d\u662f\u56e0\u4e8b\u5916\u51fa\uff0c\u800c\u662f\u4e13\u4e3a\u67e5\u770b\u8881\u7d2b\u70df\u7b49\u51fa\u5e9c\u3002 \u3000\u3000\u201c\u6211\u4eec\u627e\u4e2a\u5ba2\u6808\u4f4f\u4e0b\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u4f3c\u662f\u6ca1\u6709\u8ddf\u4e0a\u6765\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u62a2\u5148\u800c\u884c\u3002 \u3000\u3000\u957f\u5b89\u5c45\uff0c\u4e00\u5ea7\u5f88\u9ad8\u96c5\u7684\u5ba2\u6808\uff0c\u517c\u8425\u7740\u9152\u996d\u751f\u610f\uff0c\u5b87\u6587\u6210\u90fd\u5305\u4e0b\u4e86\u4e00\u5ea7\u8de8\u9662\uff0c\u5429\u5490\u5e97\u5bb6\u628a \u9152\u996d\u5f00\u5165\u8de8\u9662\u4e2d\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4f4f\u5165\u4e86\u5357\u53a2\u623f\u4e2d\uff0c\u6d17\u628a\u8138\uff0c\u5c31\u50ac\u4fc3\u5e97\u5bb6\u9001\u4e0a\u9152\u996d\u3002 \u3000\u3000\u7b49\u9152\u83dc\u6446\u5165\u4e86\u623f\u5385\u4e2d\uff0c\u5374\u4e0d\u6562\u592a\u653e\u8086\u548c\u8881\u7d2b\u70df\u540c\u684c\u8fdb\u9910\u3002 \u3000\u3000\u6b20\u8eab\uff0c\u9000\u51fa\u4e0a\u623f\uff0c\u51c6\u5907\u8f6c\u5f80\u9152\u697c\u5403\u996d\u3002 \u3000\u3000\u4f46\u5374\u88ab\u8881\u7d2b\u70df\u7559\u4e0b\u4e86\u3002 \u3000\u3000\u83b2\u513f\u3001\u5de7\u513f\uff0c\u4e24\u4fa7\u4f8d\u9152\uff0c\u8fd9\u5c31\u4f7f\u5f97\u5fc3\u4e2d\u6781\u611f\u5174\u594b\u7684\u5b87\u6587\u6210\u90fd\uff0c\u8499\u4e0a\u4e00\u5c42\u60f6\u6050\uff0c\u5982\u662f\u4e8c\u5a62 \u6cc4\u51fa\u4eca\u65e5\u4e4b\u79d8\uff0c\u968b\u7080\u5e1d\u6700\u6068\u522b\u4eba\u4fb5\u72af\u4ed6\u5fc3\u7231\u7684\u5973\u4eba\uff0c\u4f1a\u4e0d\u4f1a\u6740\u4e86\u4ed6\uff1f\u53ef\u771f\u662f\u65e0\u6cd5\u9884\u6599\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5374\u6ca1\u6709\u8fd9\u4e9b\u5fc3\u673a\u4e86\uff0c\u5979\u5728\u6df1\u5c71\u4e2d\u5b64\u5bc2\u7684\u957f\u5927\uff0c\u53ea\u89c9\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5fc3\u65e0\u6302 \u788d\uff0c\u4fbf\u662f\u5766\u7136\u3002 \u3000\u3000\u201c\u5c06\u519b\uff0c\u770b\u8fc7\u90a3\u5e45\u753b\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u53ef\u6709\u611f\u53d7\uff1f\u201d \u3000\u3000\u201c\u770b\u8fc7\u4e86\uff0c\u753b\u5f97\u5f88\u4f20\u795e\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u53ea\u662f\uff0c\u5c5e\u4e0b\u8ff0\u662f\u65e0\u6cd5\u660e\u767d\uff0c\u90a3\u5e45\u753b\u6709\u4ec0\u4e48\u7279 \u6b8a\u4e4b\u5904\uff1f\u753b\u4e0a\u6ca1\u6709\u843d\u6b3e\uff0c\u770b\u4e0d\u51fa\u662f\u90a3\u4f4d\u540d\u5bb6\u7684\u624b\u7b14\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u540d\u5bb6\u7684\u753b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u662f\u9ad8\u4eba\u7684\u624b\u7b14\uff0c\u90a3\u662f\u4e00\u5e45\u6d3b\u753b\u3002\u201d \u3000\u3000\u201c\u6d3b\u753b\uff1f\u201d\u5b87\u6587\u6210\u90fd\u4ece\u672a\u542c\u95fb\u8fc7\u8fd9\u6837\u7684\u4e8b\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u56fd\u5e08\u80fd\u5426\u8bf4\u7684\u8be6\u7ec6\u4e00 \u4e9b\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u8881\u7d2b\u70df\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e09\u4e2a\u96c0\u513f\u4e00\u53ea\u9e70\uff0c\u4f1a\u4f20\u8baf\u7ed9\u4f5c\u753b\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u5979\u867d\u7136\u7a7f\u7740\u7537\u88c5\uff0c\u4f46\u7b11\u8d77\u6765\uff0c\u4ecd\u7136\u662f\u4f1a\u6563\u53d1\u51fa\u52a8\u4eba\u7684\u5a07\u5a9a\uff0c\u76f8\u5bf9\u800c\u5750\uff0c\u8fd1\u5728\u54ab\u5c3a\uff0c\u5b87\u6587 \u6210\u90fd\u611f\u53d7\u4e5f\u7279\u522b\u7684\u5f3a\u70c8\uff0c\u4e00\u4e0b\u5b50\u770b\u5f97\u53d1\u5446\u4e86\uff0c\u6839\u672c\u6ca1\u542c\u5230\u8881\u7d2b\u70df\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u5982\u6b64\u5f3a\u70c8\u7684\u53cd\u5e94\uff0c\u8881\u7d2b\u70df\u81ea\u7136\u662f\u611f\u53d7\u5230\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8881\u7d2b\u70df\u5374\u88c5\u4f5c\u6d51\u7136\u4e0d\u77e5\uff0c\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b87\u6587\u5c06\u519b\uff0c\u4ed6\u4eec\u662f\u4f5c\u753b\u4eba\u7684\u8033 \u76ee\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u9634\u6c14\u52a0\u91cd\uff0c\u4ed6\u4eec\u8fd8\u53ef\u4ee5\u98de\u8d77\u6765\u4f24\u4eba\uff01\u201d\u3002 \u3000\u3000\u201c\u4f1a\u6709\u8fd9\u7b49\u5947\u4e8b\uff1f\u201d\u8fd9\u4e00\u6b21\u5b87\u6587\u6210\u90fd\u542c\u6e05\u695a\u4e86\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u60ed\u6127\u5440\uff01\u60ed\u6127\uff0c\u600e\u4e48\u89c1\u7740 \u5979\uff0c\u5c31\u5b8c\u5168\u5931\u53bb\u4e86\u5b9a\u529b\uff1f\u5982\u6b64\u7684\u4e0d\u582a\uff0c\u600e\u4e48\u5f97\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u81ea\u8d23\uff0c\u53e3\u4e2d\u63a5\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u662f\u4e00\u5e45\u753b\u4e86\uff0c\u662f\u4e00\u79cd\u6cd5\u672f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u6cd5\u672f\uff0c\u52a0\u4e0a\u4f5c\u753b\u4eba\u7684\u7075\u529b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u90a3\u4e2a\u4eba\u5c31\u4f4f\u5728\u7559\u5b88\u4faf\u674e\u6e0a \u7684\u5e9c\u4e2d\uff0c\u4ee5\u7075\u529b\u76d1\u89c6\u4e86\u4faf\u5e9c\u5916\u7684\u4eba\u7269\u4e3e\u52a8\uff0c\u770b\u4f3c\u6212\u5907\u677e\u6563\uff0c\u5e73\u6613\u8fd1\u4eba\u7684\u4faf\u5e9c\uff0c\u5b9e\u9645\u4e0a\uff0c\u5374\u662f \u4e00\u5904\u6212\u5907\u68ee\u4e25\u7684\u5730\u65b9\uff0c\u53ea\u662f\u4e00\u822c\u4eba\u611f\u89c9\u4e0d\u5230\u7f62\u4e86\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u6210\u90fd\u5c31\u5168\u65e0\u6240\u89c9\uff0c\u5f53\u771f\u662f\u5929\u4e0b\u4e4b\u5927\uff0c\u65e0\u5947\u4e0d\u6709\u3002\u5982\u975e\u968f\u56fd\u5e08\u8fdc\u5f81\u5317\u4e0a\uff0c\u5f00\u6b64\u773c\u754c \u4e86\uff0c\u6210\u90fd\u4ecd\u7136\u4ee5\u5fae\u672b\u4e4b\u6280\u81ea\u8d1f\uff0c\u5982\u4eca\u60f3\u6765\uff0c\u5b9e\u662f\u5750\u4e95\u89c2\u5929\u4e86\u3002\u201d\u8a00\u6765\u795e\u9eef\u7136\uff0c\u5927\u6709\u82f1\u96c4\u6c14\u77ed \u4e4b\u611f\u3002 \u3000\u3000\u201c\u5c06\u519b\uff0c\u4e0d\u8981\u6c14\u9981\u3002\u201d\u8881\u7d2b\u70df\u5fae\u7b11\u5982\u82b1\uff0c\u76ee\u5149\u4e2d\u6d41\u9732\u51fa\u65e0\u9650\u5173\u6000\uff0c\u9053\uff1a\u201c\u4ee5\u4f60\u6b66\u529f\u7684\u7cbe \u6e5b\uff0c\u5b9e\u5df2\u51fa\u7c7b\u62d4\u8403\uff0c\u5929\u4e0b\u4e60\u6b66\u4e4b\u4eba\uff0c\u5343\u5343\u4e07\u4e07\uff0c\u80fd\u8fbe\u5c06\u519b\u8fd9\u7b49\u5883\u754c\u7684\uff0c\u5b9e\u5c5e\u7edd\u65e0\u4ec5\u6709\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u591a\u8c22\u56fd\u5e08\u6170\u52c9\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u867d\u53d7\u5c01\u56fd\u5e08\uff0c\u4f46\u4ecd\u7136\u65e0\u6cd5\u9003\u8fc7\u7687\u4e0a\u7684\u7ea0\u7f20\uff0c\u6240\u4ee5\uff0c\u6211\u4e5f\u662f\u7687\u5983\u7684\u8eab \u4efd\u3002\u201d \u3000\u3000\u201c\u6210\u90fd\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6280\u827a\u6709\u9ad8\u4f4e\uff0c\u672f\u6cd5\u6709\u6df1\u6d45\uff0c\u4ee5\u5c06\u519b\u7684\u6b66\u529f\u4fee\u4e3a\uff0c\u8db3\u53ef\u5bf9\u6297\u4e00 \u822c\u7684\u672f\u6cd5\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u56fd\u5e08\u6307\u6559\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5c5e\u4e0b\u9152\u996d\u5df2\u9971\uff0c\u8fd9\u5c31\u544a\u9000\u53bb\u770b\u770b\u8427\u96e8\u3001\u7530\u5f53\uff0c\u4e24\u4f4d \u526f\u7edf\u9886\uff0c\u662f\u5426\u5df2\u8ffd\u8e2a\u800c\u81f3\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\uff0c\u89c1\u5230\u4e86\u8427\u3001\u7530\u4e24\u4f4d\uff0c\u8981\u4ed6\u4eec\u5728\u6b64\u4f4f\u4e0b\uff0c\u4eca\u591c\u5c06\u519b\u548c\u6211\u540c\u5165\u4faf\u5e9c\uff0c\u4e00\u63a2\u7a76\u7adf\uff1f\u201d \u3000\u3000\u201c\u6210\u90fd\u9075\u547d\u3002\u201d \u3000\u3000\u6b20\u8eab\u4e00\u793c\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u201c\u5c06\u519b\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u4eba\u5df2\u8d70\u51fa\u95e8\u5916\uff0c\u95fb\u58f0\u505c\u6b65\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u56fd\u5e08\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u201c\u6709\uff0c\u5403\u9971\u70b9\uff0c\u665a\u4e0a\uff0c\u5f88\u53ef\u80fd\u4f1a\u6709\u4e00\u756a\u6fc0\u6218\u554a\uff1f\u201d\u8881\u7d2b\u70df\u8bf4\u5b8c\u8bdd\uff0c\u53c8\u662f\u4e00\u7b11\u3002 \u3000\u3000\u8fd9\u8bf4\u660e\u4e86\uff0c\u5979\u77e5\u9053\u5b87\u6587\u6210\u90fd\u6839\u672c\u6ca1\u6709\u5403\u9971\uff0c\u90a3\u4e00\u7b11\uff0c\u662f\u5173\u5fc3\uff0c\u4e5f\u662f\u5631\u5490\u3002 \u3000\u3000\u8fd9\u5c31\u628a\u5b87\u6587\u603b\u7edf\u9886\u6298\u817e\u60e8\u4e86\u3002 \u3000\u3000\u8d70\u5f97\u5fc3\u51c4\u51c4\uff0c\u60c5\u60d8\u60d8\uff0c\u6709\u632f\u594b\uff0c\u4e5f\u6709\u611f\u4f24\u3002 \u3000\u3000\u5c11\u5973\u5fc3\u554a\uff01\u662f\u90a3\u4e48\u96be\u4ee5\u6349\u6478\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u662f\u666e\u901a\u4eba\uff0c\u4f46\u4e5f\u662f\u4e00\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u5c11\u5973\u60c5\u6000\u603b\u662f\u8bd7\uff0c\u4f55\u51b5\uff0c\u8881\u7d2b\u70df\u662f\u7f8e\u5973\uff0c\u4e5f\u662f\u4e00\u9996\u597d\u8bd7\u3002 \u3000\u3000\u597d\u8bd7\u8bcd\u534e\u4e3d\u52a8\u4eba\uff0c\u4f46\u603b\u5e26\u7740\u4e09\u5206\u5632\u7b11\uff0c\u4e00\u5206\u6349\u5f04\u3002 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6539\u7a7f\u4e86\u4e00\u8eab\u7384\u8272\u52b2\u88c5\uff0c\u5b87\u6587\u6210\u90fd\u4ecd\u7136\u662f\u4e00\u88ad\u9752\u886b\uff0c\u53ea\u662f\u8170\u4e2d\u6a2a\u675f\u4e86\u4e00\u6761\u4e94\u5bf8\u5bbd\u7684 \u7ea2\u8170\u5e26\uff0c\u770b\u4e0a\u53bb\uff0c\u733f\u81c2\u8702\u8170\uff0c\u5a01\u6b66\u4e2d\uff0c\u53c8\u5e26\u7740\u4e00\u4efd\u6587\u96c5\u3002 \u3000\u3000\u4e24\u6761\u4eba\u5f71\u8fde\u7fe9\u800c\u8d77\uff0c\u591c\u8272\u4e2d\u6709\u5982\u8759\u8760\u5bb5\u98de\u3002 \u3000\u3000\u4ed6\u4eec\u907f\u5f00\u4e86\u6b63\u95e8\uff0c\u8dc3\u5165\u674e\u5e9c\u3002 \u3000\u3000\u4f46\u95fb\u82b1\u9999\u88ad\u4eba\uff0c\u7adf\u7136\u8fdb\u5165\u4e86\u4faf\u5e9c\u7684\u82b1\u56ed\u4e4b\u4e2d\u3002 \u3000\u3000\u82b1\u6811\u6df1\u5904\uff0c\u6709\u4e00\u5ea7\u5c0f\u5de7\u7684\u82b1\u9601\uff0c\u70db\u706b\u9ad8\u70e7\uff0c\u7167\u5f97\u4e00\u5ba4\u901a\u660e\uff0c\u4e24\u6247\u9601\u95e8\u5927\u5f00\uff0c\u9690\u9690\u53ef\u89c1\u9601 \u4e2d\u4e4b\u4eba\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u56de\u5934\u770b\u4e86\u8881\u7d2b\u70df\u4e00\u773c\uff0c\u4f3c\u5728\u8bf7\u793a\u884c\u6b62\u3002 \u3000\u3000\u201c\u8001\u673d\u5df2\u70f9\u597d\u9999\u8317\uff0c\u4e24\u4f4d\u8bf7\u5165\u82b1\u9601\uff0c\u54c1\u5c1d\u4e00\u4e0b\u8001\u673d\u7684\u624b\u827a\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u6709\u70b9\u95e8\u9053\u3002\u201d\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u4eec\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u4ecd\u7136\u672a\u9003\u8fc7\u4ed6\u4eec\u7684\u76d1 \u89c6\uff0c\u592a\u539f\u674e\u4faf\u5e9c\u80fd\u4eba\u4f17\u591a\uff0c\u786e\u662f\u4e0d\u53ef\u8f7b\u4fae\uff0c\u8881\u56fd\u5e08\u627e\u6765\u6b64\u5730\uff0c\u4e5f\u975e\u65e0\u56e0\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u76f4\u6b65\u5165\u9601\uff0c\u5b87\u6587\u6210\u90fd\u7d27\u968f\u8eab\u540e\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u7070\u888d\uff0c\u9762\u5982\u53e4\u6708\uff0c\u4ed9\u98ce\u98d8\u98d8\u7684\u8001\u8005\uff0c\u5353\u7acb\u5728\u4e00\u5f20\u6728\u51e0\u4e4b\u540e\uff0c\u6728\u51e0\u4e0a\u7089\u706b\u6b63 \u76db\uff0c\u74f7\u58f6\u4e2d\u70ed\u6c14\u84b8\u817e\u3002 \u3000\u3000\u8001\u8005\u679c\u7136\u6b63\u5728\u716e\u8336\u54c1\u8317\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u8bf7\u5750\u3002\u201d\u7070\u8863\u8001\u8005\u63d0\u8d77\u74f7\u58f6\uff0c\u659f\u4e86\u4e24\u676f\u8336\uff0c\u63a5\u9053\uff1a\u201c\u591c\u534a\u5ba2\u6765\u8336\u5f53\u9152\uff0c\u4e24\u4f4d\u8bf7 \u7528\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u7f13\u7f13\u5728\u4e00\u5f20\u85e4\u6905\u4e0a\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u51a4\u5bb6\u4e0d\u78b0\u5934\uff0c\u8001\u4e08\u8d35\u59d3\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u5750\uff0c\u8083\u7136\u7ad9\u7acb\u5728\u8881\u7d2b\u70df\u7684\u8eab\u540e\u3002 \u3000\u3000\u201c\u8001\u673d\u59d3\u674e\uff0c\u59d1\u5a18\u65e2\u5df2\u5165\u9601\u5c0f\u5750\uff0c\u5374\u4e0d\u80af\u53d6\u4e0b\u9762\u7eb1\uff0c\u8ba9\u8001\u673d\u4e00\u77bb\u7389\u5bb9\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u53d6\u4e0b\u9762\u7eb1\uff0c\u73b0\u9732\u51fa\u7edd\u4e16\u5bb9\u8c8c\u3002 \u3000\u3000\u201c\u5e78\u4f1a\u4e86\u3002\u201d\u7070\u8863\u8001\u4eba\u5f88\u4ed4\u7ec6\u6253\u91cf\u4e86\u8881\u7d2b\u70df\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u59ff\u5bb9\u8d85\u51e1\uff0c\u5e94\u662f\u6765\u81ea \u4e5d\u5929\u7684\u4ed9\u5973\uff1f\u4e0d\u77e5\u4f55\u4e8b\u4e0b\u51e1\u5c18\uff1f\u201d \u3000\u3000\u201c\u8001\u4e08\u4ed9\u98ce\u9053\u9aa8\uff0c\u5206\u660e\u662f\u4f4d\u4e16\u5916\u9ad8\u4eba\uff0c\u600e\u4f1a\u4e00\u88ad\u8f7b\u888d\u5165\u4faf\u5e9c\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u674e\u6df3\u98ce\uff0c\u501f\u4f4f\u5317\u5cad\u60ac\u7a7a\u5bfa\uff0c\u674e\u4faf\u7237\u5c0f\u6e38\u91d1\u9f99\u5ce1\uff0c\u6350\u94f6\u4fee\u5bfa\uff0c\u5f97\u4ee5\u7ed3\u7f18\uff0c\u6e38\u8e2a\u592a \u539f\uff0c\u6682\u6816\u4faf\u5e9c\u3002\u201d \u3000\u3000\u201c\u59be\u665a\u8f88\u8881\u7d2b\u70df\uff0c\u7ec3\u6c14\u7ec8\u5357\u5c71\uff0c\u4e0d\u5fcd\u89c1\u751f\u7075\u6d82\u70ad\uff0c\u8dcc\u5165\u7e41\u534e\uff0c\u8eab\u9677\u957f\u5b89\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u59d1\u5a18\u662f\u77e5\u673a\u4e2d\u4eba\uff0c\u5f53\u77e5\u5929\u610f\u4e0d\u53ef\u8fbe\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5929\u9053\u8fd0\u884c\u6709\u5e8f\uff0c\u5c82\u53ef\u5984\u52a0\u4eba\u529b\u5e72\u9884\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u8138\u8272\u5fae\u53d8\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u6062\u590d\u5e38\u6001\uff0c\u9053\uff1a\u201c\u8881\u59d1\u5a18\u53ea\u662f\u4e3a\u8d4c\u4e00\u53e3\u6c14\u5417\uff1f\u9700\u77e5\u5929\u610f \u96be\u8fbe\uff0c\u59d1\u5a18\u4f55\u4e0d\u5230\u4e94\u6e56\u3001\u56db\u6d77\u8d70\u4e00\u8d70\uff0c\u4f53\u9a8c\u4e00\u4e0b\uff0c\u6c11\u95f4\u7684\u75be\u82e6\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u8881\u59d1\u5a18\uff0c\u4e09\u4f0f\u70c8\u65e5\u6c57\u900f\u8863\uff0c\u79cb\u98ce\u53c8\u9001\u4e5d\u6708\u971c\uff0c\u4e07\u6c11\u672c\u5df2\u591a\u82e6\u96be\uff0c\u4eba\u4e3a\u7978\u4e71\u66f4\u53ef \u60e8\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u4e08\u8bf4\u7684\u662f\uff01\u201d\u8881\u7d2b\u70df\u6253\u65ad\u4e86\u674e\u6df3\u98ce\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u59be\u665a\u6b64\u884c\uff0c\u5c31\u662f\u8981\u6d88\u5f2d\u53bb\u4eba\u4e3a\u7684 \u7978\u4e71\uff01\u201d \u3000\u3000\u8bdd\u4e0d\u6295\u673a\u4e86\uff1f \u3000\u3000\u60c5\u5b57\u8bef\u4eba\uff0c\u8881\u7d2b\u70df\u4f3c\u4e4e\u5df2\u51c6\u5907\u4e0d\u8c08\u5929\u7406\uff0c\u8bf4\u56fd\u6cd5\u4e86\uff1f \u3000\u3000\u201c\u53ef\u60dc\u5440\uff01\u53ef\u60dc\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u8001\u673d\u6ca1\u6cd5\u7ed9\u59d1\u5a18\u4e00\u4e2a\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u8001\u4e08\u7684\u610f\u601d\u662f\uff1f\u201d\u8881\u7d2b\u70df\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u51c6\u5907\u8d56\u6389\u4e86\uff1f\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff01\u8881\u59d1\u5a18\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u8001\u673d\u4e00\u70b9\u5fae\u672b\u9053\u672f\uff0c\u8fd8\u4e0d\u8db3\u5e72\u9884\u5929\u673a\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u53cc\u80a9\u4e00\u6643\uff0c\u4eba\u5df2\u95ea\u51fa\u9601\u5916\uff0c\u4ef0\u671b\u5929\u4e0a\u661f\u5bbf\uff0c\u8db3\u8db3\u6709\u4e00\u523b\u5de5\u592b\uff0c\u91cd\u8fd4\u82b1\u9601\u3002 \u3000\u3000\u5979\u6765\u53bb\u5982\u98ce\uff0c\u5b87\u6587\u6210\u90fd\u662f\u5c31\u7ad9\u5728\u5979\u7684\u8eab\u540e\uff0c\u7adf\u4e5f\u770b\u4e0d\u51fa\u5979\u7528\u7684\u4ec0\u4e48\u8eab\u6cd5\u3002 \u3000\u3000\u201c\u59d1\u5a18\uff01\u201d\u674e\u6df3\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u673d\u6ca1\u8bf4\u8c0e\u5427\uff1f\u201d\u795e\u8272\u5e73\u9759\uff0c\u4e00\u8138\u548c\u853c\u3002 \u3000\u3000\u201c\u4f46\u8001\u4e08\u5b9a\u7136\u77e5\u9053\uff0c\u4ed6\u662f\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ee5\u8001\u4e08\u6df1\u539a\u7684\u9053\u57fa\uff0c\u548c\u957f\u8005\u98ce\u8303\uff0c \u60f3\u6765\u4e0d\u4f1a\u8c0e\u8a00\u63a8\u8131\u4e86\uff1f\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u5f88\u96be\u542c\uff0c\u4f46\u4e5f\u7d27\u7d27\u7684\u6263\u4f4f\u4e86\u674e\u6df3\u98ce\u3002 \u3000\u3000\u201c\u597d\u5389\u5bb3\u7684\u8881\u59d1\u5a18\uff01\u201d\u674e\u6df3\u98ce\u65e0\u6cd5\u5426\u8ba4\u4e86\uff0c\u6447\u6447\u5934\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u673d\u77e5\u9053 \u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u8001\u4e08\u53ef\u4ee5\u544a\u8bc9\u6211\u5427\uff1f\u201d\u8881\u7d2b\u70df\u5ae3\u7136\u4e00\u7b11\u3002\u9053\uff1a\u201c\u80fd\u5177\u6b64\u65e0\u4e0a\u6cd5\u529b\uff0c\u5e94\u662f\u4e00\u4e2a\u6781\u53d7\u656c\u91cd \u7684\u524d\u8f88\uff0c\u59be\u665a\u7edd\u4e0d\u4f1a\u8f7b\u8a00\u5192\u72af\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8001\u673d\u53ef\u4ee5\u5949\u544a\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u201c\u8001\u4e08\u53ef\u662f\u8981\u5f81\u5f97\u4ed6\u7684\u540c\u610f\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4e0d\u613f\u610f\u63a5\u89c1\u59be\u665a\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u4e0d\u5f97\u4ed6\u4e86\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u8001\u673d\u7b54\u5e94\u4e86\u59d1\u5a18\uff0c\u4e00\u5b9a\u771f\u8bda\u5949\u544a\uff0c\u5148\u884c\u77e5\u4f1a\u4ed6\u4e00 \u58f0\uff0c\u53ea\u8981\u82b1\u8d39\u4e00\u4e9b\u65f6\u65e5\uff0c\u5c31\u4e0d\u96be\u8ffd\u67e5\u5230\u4ed6\u9690\u8eab\u4e4b\u5904\uff1f\u201d \u3000\u3000\u201c\u591a\u8c22\u5938\u5956\uff0c\u7d2b\u70df\u5fc3\u9886\u9686\u60c5\uff0c\u4f46\u4e0d\u77e5\u51e0\u65f6\u53ef\u542c\u5230\u56de\u97f3\uff1f\u201d \u3000\u3000\u201c\u7ed9\u8001\u673d\u4e24\u5929\u65f6\u95f4\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\u4ece\u547d\uff0c\u4e24\u5929\u540e\uff0c\u518d\u6765\u6b64\u606d\u5019\u4f73\u97f3\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\u53ea\u662f\u6211\u501f\u6816\u4e4b\u5904\uff0c\u4e0d\u5b9c\u591a\u6240\u60ca\u6270\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4e24\u5929\u540e\u7533\u521d\u5149\u666f\uff0c\u8001\u673d\u5728\u957f\u5b89 \u5c45\u9152\u697c\u5019\u9a7e\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u6de1\u6de1\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59be\u665a\u7684\u4f4f\u5904\uff0c\u4e5f\u5df2\u5728\u8001\u4e08\u7684\u76d1\u89c6\u4e4b\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u9e64\u9e23\u4e8e\u768b\uff0c\u58f0\u95fb\u4e8e\u5929\uff0c\u518d\u996e\u676f\u7c97\u8336\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5fc3\u9886\u4e86\uff0c\u7d2b\u70df\u544a\u8f9e\uff0c\u4e24\u65e5\u540e\u518d\u884c\u9762\u9886\u6559\u76ca\u3002\u201d\u5fae\u4e00\u9894\u9996\uff0c\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u53ea\u89c9\u4e24\u4eba\u4ea4\u8c08\u4e4b\u4e2d\uff0c\u5ffd\u800c\u8bed\u542b\u7384\u673a\uff0c\u5ffd\u800c\u76f8\u4e92\u63a8\u5d07\uff0c\u7adf\u7136\u4e00\u53e5\u4e5f\u63a5\u4e0d\u4e0a\u53e3\uff0c\u4ed6 \u8fd9\u957f\u5b89\u6559\u573a\u4e2d\u9009\u51fa\u7b2c\u4e00\u597d\u6c49\uff0c\u6bbf\u524d\u5927\u5c06\u519b\uff0c\u5bab\u8859\u603b\u7edf\u9886\uff0c\u8fd9\u4e00\u6b21\u6210\u4e86\u771f\u6b63\u7684\u8ddf\u73ed\u5566\uff01 \u3000\u3000\u82b1\u9601\u53f3\u9996\u7684\u4e00\u9053\u5899\u58c1\uff0c\u7a81\u7136\u88c2\u5f00\u4e86\uff0c\u5f62\u5982\u4e00\u9053\u95e8\u6237\uff0c\u7f13\u6b65\u884c\u51fa\u4e86\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u9762\u8272\u91d1\u9ec4\u7684\u5927\u6c49\u8d70\u5728\u524d\u9762\uff0c\u8eab\u540e\u7d27\u968f\u7740\u4e00\u4e2a\u8eab\u8eaf\u9ad8\u5927\u7684\u9ed1\u8138\u6c49\u5b50\u3002 \u3000\u3000\u6700\u540e\u4e00\u4eba\u8eab\u7740\u9752\u888d\uff0c\u9762\u5982\u51a0\u7389\uff0c\u4fee\u7709\u5165\u9b13\uff0c\u4e30\u795e\u4fca\u6717\u3002\u6b63\u662f\u5348\u95f4\uff0c\u6b65\u51fa\u4faf\u5e9c\u67e5\u770b\u8881\u7d2b\u70df \u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u8fd9\u5ea7\u82b1\u9601\uff0c\u770b\u4e0a\u53bb\u5c0f\u5de7\u7cbe\u81f4\uff0c\u7adf\u7136\u8fd8\u6709\u5939\u58c1\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u5bf9\u674e\u6df3\u98ce\uff0c\u90fd\u8868\u793a\u51fa\u4e86\u76f8\u5f53\u7684\u656c\u91cd\uff0c\u62b1\u62f3\u65bd\u793c\u3002 \u3000\u3000\u201c\u5148\u751f\uff0c\u770b\u8d77\u6765\uff0c\u662f\u5f88\u96be\u8bf4\u670d\u5979\u4e86\uff1f\u79e6\u53d4\u5b9d\u613f\u548c\u656c\u5fb7\u5144\uff0c\u591c\u88ad\u957f\u5b89\u5c45\uff0c\u6740\u5979\u4eec\u4e00\u4e2a\u63aa\u624b \u4e0d\u53ca\u3002\u201d\u9ec4\u8138\u5927\u6c49\u8bf4\u3002 \u3000\u3000\u201c\u5bf9\uff01\u201d\u9ed1\u8138\u5927\u6c49\u63a5\u9053\uff1a\u201c\u79e6\u743c\u5144\u7684\u4e00\u5bf9\u51f9\u9762\u91d1\u950f\u529b\u654c\u4e07\u4eba\uff0c\u54b1\u5c09\u8fdf\u606d\u4e00\u53cc\u96cc\u96c4\u94a2\u97ad\uff0c \u4e5f\u6253\u904d\u5c71\u540e\u672a\u9022\u8fc7\u654c\u624b\uff0c\u5b87\u6587\u6210\u90fd\u53f7\u79f0\u957f\u5b89\u7b2c\u4e00\u597d\u6c49\uff0c\u656c\u5fb7\u5fc3\u4e2d\u4e0d\u670d\u4e45\u77e3\uff0c\u96be\u5f97\u6709\u6b64\u673a\u4f1a\uff0c \u6b63\u597d\u8ba9\u4ed6\u89c1\u8bc6\u4e00\u4e0b\u54b1\u5bb6\u96cc\u96c4\u53cc\u97ad\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u201c\u674e\u9756\u3002\u201d\u674e\u6df3\u98ce\u671b\u7740\u8eab\u7740\u9752\u888d\u7684\u5e74\u8f7b\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u7684\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u8f7b\u654c\u5440\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u5b87\u6587\u6210\u90fd\u867d\u7136\u53f7\u79f0\u957f\u5b89\u7b2c\u4e00\u597d\u6c49\uff0c\u5728\u4e0b\u76f8\u4fe1\u5c09\u8fdf\u5c06\u519b\u548c\u79e6 \u5c06\u519b\u7684\u97ad\u950f\u5408\u74a7\uff0c\u5f53\u53ef\u6297\u8861\uff0c\u53ef\u6015\u7684\u662f\u90a3\u4f4d\u8881\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u5c0f\u59d1\u5a18\uff0c\u6709\u4ec0\u4e48\u597d\u6015\u7684\uff1f\u201d\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u4ee5\u4f60\u674e\u5c11\u5144\u7684\u7cbe\u6e5b\u5251\u6cd5\uff0c\u96be\u9053 \u8fd8\u80dc\u5979\u4e0d\u8fc7\uff1f\u201d\u201d \u3000\u3000\u201c\u5c09\u8fdf\u5144\u3002\u201d\u674e\u9756\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u53ef\u8f7b\u654c\u5440\uff01\u770b\u5979\u5168\u8eab\u6563\u53d1\u7740\u4e00\u7247\u4e91\u6c14\uff0c\u5f53\u662f\u4e00\u4f4d\u672f\u6cd5 \u975e\u5e38\u6df1\u539a\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5979\u80fd\u7aa5\u7834\u5929\u8c61\u8fd0\u884c\u4e4b\u673a\uff0c\u9053\u6cd5\u7684\u6df1\u539a\uff0c\u7edd\u4e0d\u53ef\u7b49\u95f2\u89c6\u4e4b\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u968b\u7080 \u5e1d\u6768\u5e7f\u7684\u624b\u4e0b\uff0c\u600e\u6709\u7740\u8fd9\u6837\u4e00\u4e2a\u8d85\u51e1\u51fa\u4f17\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u9ad8\u4f30\u5979\u4e86\uff1f\u201d\u5927\u5c06\u79e6\u743c\u9053\uff1a\u201c\u5979\u80fd\u76f4\u63a5\u5730\u627e\u5165\u592a\u539f\u4faf\u5e9c\uff0c\u786e\u4e5f\u6709\u70b9\u95e8\u9053\uff0c\u4f46\uff0c \u8fd9\u5c31\u66f4\u4e0d\u80fd\u653e\u8fc7\u5979\u4e86\uff0c\u5148\u751f\uff0c\u4e0d\u80fd\u8ba9\u5979\u7834\u574f\u4e86\u2018\u79fb\u8f6c\u5929\u8fd0\u7684\u5927\u6cd5\u2019\u5440\uff01\u201d \u3000\u3000\u201c\u674e\u9756\uff0c\u4f60\u53bb\u89c1\u89c1\u8881\u5929\u7f61\uff0c\u544a\u8bc9\u4ed6\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4e24\u65e5\u540e\uff0c\u8001\u673d\u548c\u5979\u4f1a\u9762\uff0c \u4f1a\u5c3d\u91cf\u7684\u529d\u963b\u5979\u5e72\u9884\u6b64\u4e8b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u5979\u4e0d\u80af\u542c\u5462\uff1f\u201d\u79e6\u53d4\u5b9d\u9053\uff1a\u201c\u5148\u751f\u662f\u5426\u5df2\u51c6\u5907\u4e0d\u60dc\u2014\u6218\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u795e\u60c5\u6c89\u91cd\u7684\u7f13\u6b65\u884c\u51fa\u82b1\u9601\uff0c\u4ef0\u671b\u7740\u5929\u4e0a\u7684\u95ea\u70c1\u7684\u7fa4\u661f\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u53f9\u606f\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u53d4\u5b9d\u3001\u656c\u5fb7\u3002\u201d \u3000\u3000\u201c\u606d\u5019\u5148\u751f\u5429\u5490\uff01\u201d \u3000\u3000\u539f\u6765\uff0c\u4e24\u4e2a\u4eba\u5df2\u8ddf\u5728\u674e\u6df3\u98ce\u8eab\u540e\uff0c\u884c\u51fa\u82b1\u9601\u3002 \u3000\u3000\u4e24\u4eba\u4e5f\u5728\u770b\u7740\u5929\u4e0a\u7684\u661f\u8fb0\uff0c\u53ea\u89c9\u4e07\u661f\u660e\u706d\u5165\u5ffd\u9690\u5ffd\u4eae\u3002 \u3000\u3000\u4e98\u53e4\u4ee5\u6765\uff0c\u5c31\u662f\u5982\u6b64\uff0c\u5b9e\u5728\u77a7\u4e0d\u51fa\u6709\u4f55\u4e0d\u540c\u7684\u5730\u65b9\uff1f \u3000\u3000\u5929\u8c61\u8fd0\u884c\u4e2d\u6f5c\u4f0f\u673a\u8fd0\uff0c\u662f\u5929\u6587\u5b66\u4e2d\u6700\u9ad8\u6df1\u7684\u5965\u79d8\uff0c\u5982\u975e\u6709\u660e\u5e08\u6307\u70b9\uff0c\u7ec8\u5176\u4e00\u751f\uff0c\u6050\u6015\u4e5f \u5f88\u96be\u770b\u51fa\u4e00\u70b9\u95e8\u9053\u6765\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u79e6\u743c\u548c\u5c09\u8fdf\u606d\u770b\u4e0d\u51fa\u4efb\u4f55\u5f81\u8c61\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u52c7\u51a0\u4e09\u519b\uff0c\u51b2\u950b\u9677\u9635\u7684\u7edf\u5175\u5c06\u519b\uff0c\u4e0d\u662f\u80f8\u7f57\u4e07\u6709\uff0c\u6d1e\u5f97\u7384\u673a\u7684\u4ed9\u9053\u4e2d\u4eba\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u5c06\u519b\uff0c\u8bf7\u5c3d\u5feb\u8d76\u5f80\u4e91\u4e2d\u5c71\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u4e91\u4e2d\u5c71\uff0c\u90a3\u4e0d\u662f\u4e8c\u516c\u5b50\u79d8\u5bc6\u7ec3\u5175\u7684\u5730\u65b9\u4e48\uff1f\u201d\u79e6\u53d4\u5b9d\u9053\uff1a\u201c\u8981\u5bf9\u4e8c\u516c\u5b50\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u544a\u8bc9\u4ed6\u6682\u65f6\u505c\u606f\u4e0b\u6765\uff0c\u6700\u597d\u8ba9\u4ed6\u4eec\u5206\u522b\u6f5c\u9690\u4e8e\u5c71\u6da7\u3001\u6c11\u5b85\u4e4b\u4e2d\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u5251\u5165 \u9798\u5185\uff0c\u5200\u8fd8\u5957\u4e2d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u7684\u4e09\u5343\u94c1\u7532\u52c7\u58eb\uff0c\u6b63\u503c\u8bad\u7ec3\u91cd\u8981\u5173\u5934\uff0c\u8981\u4ed6\u4eec\u7a81\u7136 \u505c\u4e0b\u6765\uff0c\u5c82\u4e0d\u662f\u592a\u53ef\u60dc\u4e86\u3002\u201d \u3000\u3000\u201c\u6740\u6c14\u592a\u91cd\u554a\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u624d\u6c14\u7eb5\u6a2a\uff0c\u5df2\u628a\u4e09\u5343\u94c1\u7532\u52c7\u58eb\uff0c\u7ec3\u51fa\u4e86\u51cc\u5389\u7684\u6740 \u6c14\uff0c\u76f4\u51b2\u4e8e\u4e91\u4e2d\u5c71\u9704\u6c49\u4e4b\u4e0a\uff0c\u7792\u4e0d\u8fc7\u8881\u7d2b\u70df\u7684\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u4e24\u4f4d\u5c31\u7acb\u523b\u8d77\u7a0b\u5427\uff01\u201d \u3000\u3000\u5c09\u8fdf\u606d\u8fd8\u8981\u6297\u8fa9\uff0c\u4f46\u5374\u88ab\u79e6\u53d4\u5b9d\u62c9\u8d70\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u4e24\u9a91\uff0c\u7acb\u523b\u5c31\u9053\uff0c\u79bb\u5f00\u7559\u5b88\u4faf\u5e9c\uff0c\u76f4\u5954\u5411\u4e91\u4e2d\u5c71\u3002 \u3000\u3000\u542c\u5f97\u9a6c\u8e44\u58f0\u9010\u6e10\u8fdc\u53bb\uff0c\u674e\u6df3\u98ce\u624d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u674e\u9756\u3002\u201d \u3000\u3000\u201c\u5728\uff01\u201d\u674e\u9756\u7f13\u6b65\u884c\u51fa\u82b1\u9601\uff0c\u4ed6\u672a\u968f\u79e6\u743c\u3001\u656c\u5fb7\uff0c\u884c\u51fa\u82b1\u9601\uff0c\u5374\u4e00\u76f4\u5750\u5728\u82b1\u9601\u4e2d\uff0c\u72ec\u81ea \u54c1\u8317\u3002 \u3000\u3000\u4ed6\u806a\u660e\u3001\u9ad8\u50b2\uff0c\u719f\u8bfb\u6218\u7b56\uff0c\u80f8\u6000\u7532\u5175\uff0c\u5728\u534e\u5c71\u4fee\u4e60\u9053\u672f\uff0c\u4e03\u5e74\u6709\u6210\u3002 \u3000\u3000\u51fa\u5c71\u540e\uff0c\u66fe\u6295\u6548\u8d8a\u516c\u6768\u7d20\u5e9c\u4e2d\uff0c\u76ee\u7779\u968b\u7080\u5e1d\u6deb\u4e71\u671d\u653f\uff0c\u5929\u4e0b\u4e71\u76f8\u840c\u751f\uff0c\u987f\u751f\u9010\u9e7f\u5929\u4e0b\u7684 \u5fc3\u613f\uff0c\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18\uff0c\u4e3a\u8d8a\u516c\u5e9c\u4e2d\u5343\u767e\u5973\u5b98\u3001\u6b4c\u59ec\u4e2d\u7b2c\u4e00\u7f8e\u5973\uff0c\u6167\u773c\u8bc6\u674e\u9756\uff0c\u591c\u5954\u6295\u6000\uff0c\u53cc \u53cc\u51fa\u8d70\u3002 \u3000\u3000\u7ed3\u8bc6\u4e86\u866c\u9aef\u5ba2\u5f20\u4ef2\u575a\uff0c\u82f1\u96c4\u76f8\u60dc\uff0c\u4e49\u7ed3\u91d1\u5170\u3002 \u3000\u3000\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18\u6b63\u4ece\u4e49\u5144\u866c\u9aef\u4e60\u7ec3\u4e0a\u4e58\u5251\u672f\uff0c\u674e\u9756\u5374\u6e38\u8d70\u6c5f\u6e56\uff0c\u5e0c\u80fd\u7ed3\u8bc6\u5929\u4e0b\u82f1\u96c4\uff0c\u5907\u5c06 \u7ec3\u5175\uff0c\u4ee5\u56fe\u5927\u4e3e\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5f88\u4e0d\u5e78\u7684\uff0c\u9047\u4e0a\u4e86\u674e\u6df3\u98ce\u548c\u8881\u5929\u7f61\u3002 \u3000\u3000\u674e\u9756\u53d1\u89c9\u4e86\u4e24\u4f4d\u8001\u8005\u90fd\u662f\u8eab\u5177\u9053\u6cd5\u7684\u9ad8\u4eba\u3002 \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4e5f\u53d1\u73b0\u4e86\u674e\u9756\u662f\u7edf\u7387\u5927\u519b\uff0c\u53f1\u54a4\u98ce\u4e91\u7684\u5c06\u624d\uff0c\u4e0d\u80fd\u6536\u7528\u6b64\u4eba\uff0c\u52bf\u5c06\u6210\u4e3a \u5510\u516c\u674e\u6e0a\u7684\u9738\u4e1a\u5927\u654c\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u751f\u51fa\u4e86\u6536\u7528\u5bf9\u65b9\u7684\u6253\u7b97\uff0c\u65e2\u5b58\u7b3c\u7edc\u4e4b\u610f\uff0c\u4e5f\u5b58\u654c\u89c6\u4e4b\u5fc3\uff0c\u4e0d\u4e3a\u6211\u7528\uff0c\u5c31\u6740\u4e4b\u4ee5\u7edd \u540e\u60a3\u3002 \u3000\u3000\u4e09\u4eba\u4e00\u663c\u591c\u8bba\u9053\u6597\u6cd5\uff0c\u674e\u9756\u4e0d\u654c\u843d\u8d25\u3002 \u3000\u3000\u674e\u9756\u4e5f\u5c65\u884c\u7ea6\u5b9a\uff0c\u664b\u89c1\u4e86\u5510\u516c\u674e\u6e0a\u3002 \u3000\u3000\u4f46\u8ba9\u674e\u9756\u771f\u6b63\u656c\u670d\u7684\u4eba\uff0c\u662f\u5510\u516c\u674e\u6e0a\u7684\u4e8c\u5b50\u674e\u4e16\u6c11\uff0c\u4ed6\u5177\u6709\u7684\u9886\u8896\u9b45\u529b\uff0c\u535a\u5927\u80f8\u6000\uff0c\u4f7f \u674e\u9756\u4e3a\u4e4b\u5fc3\u6298\u3002 \u3000\u3000\u4f46\u674e\u6df3\u98ce\u4ecd\u7136\u65e0\u6cd5\u8bf4\u670d\u674e\u9756\uff0c\u63a5\u53d7\u5510\u516c\u674e\u6e0a\u7684\u91cd\u7528\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e24\u4eba\u53c8\u65e7\u4e8b\u91cd\u63d0\u3002 \u3000\u3000\u674e\u6df3\u98ce\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4e2a\u4e8b\u6682\u4e14\u4e0d\u8c08\uff01\u5bf9\u4ed8\u8881\u7d2b\u70df\u7684\u4e8b\uff0c\u4f60\u6709\u4ec0\u4e48\u9ad8 \u89c1\uff1f\u201d \u3000\u3000\u201c\u770b\u4e0a\u53bb\uff0c\u5979\u5e74\u9f84\u6ca1\u6709\u6211\u5927\u2026\u2026\u201d\u674e\u9756\u9053\uff1a\u201c\u4f46\u5f97\u5929\u72ec\u539a\uff0c\u4e00\u773c\u4e4b\u95f4\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u5979\u6709 \u7740\u6df1\u539a\u7684\u9053\u6cd5\u57fa\u7840\uff0c\u4ee5\u5148\u751f\u548c\u8881\u8001\u7684\u6df1\u7cbe\u6d69\u535a\u5b66\u8bc6\uff0c\u4e5f\u8bb8\u80fd\u8bf4\u4e4b\u4ee5\u7406\uff0c\u52a8\u4e4b\u4ee5\u60c5\uff0c\u529d\u5979\u9000\u9690 \u6df1\u5c71\uff0c\u90a3\u624d\u662f\u4e0a\u4e0a\u4e4b\u7b56\u3002\u201d \u3000\u3000\u201c\u4eca\u591c\u4e2d\u6570\u8bed\u4ea4\u4ee3\uff0c\u5df2\u9690\u89c1\u950b\u8292\u903c\u4eba\uff0c\u529d\u5979\u9000\u51fa\uff0c\u673a\u4f1a\u4e0d\u5927\uff01\u201d\u674e\u6df3\u98ce\u53f9\u606f\u7684\u8bf4\u3002 \u3000\u3000\u201c\u5148\u751f\u7684\u610f\u601d\u662f\u8981\u6740\u4e86\u5979\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u4e0d\u8981\u8ba9\u4e16\u6c11\u4f5c\u4e2a\u51b3\u5b9a\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u4e8c\u516c\u5b50\u4f5c\u6700\u540e\u7684\u88c1\u51b3\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u7b54\u5141\u63a5\u638c\u5e05\u5370\uff0c\u4e09\u5343\u94c1\u7532\u52c7\u58eb\uff0c \u4e5f\u672a\u5b8c\u5168\u7ec3\u6210\uff0c\u4ee5\u5510\u516c\u674e\u6e0a\u7684\u5a01\u671b\uff0c\u632f\u81c2\u4e00\u547c\uff0c\u4e5f\u53ea\u80fd\u53f7\u53ec\u592a\u539f\u3001\u6cb3\u4e1c\u3001\u96c1\u95e8\u3001\u4e0a\u8c37\uff0c\u6e14 \u9633\u3001\u90af\u90f8\u8bf8\u90e1\uff0c\u4f1a\u4e0d\u4f1a\u54cd\u5e94\u8d77\u4e8b\uff0c\u8fd8\u65e0\u628a\u63e1\u3002 \u3000\u3000\u8d22\u529b\u3001\u5175\u6e90\uff0c\u90fd\u4e0d\u8db3\u548c\u968b\u671d\u5927\u519b\u6297\u8861\uff0c\u6211\u4eec\u7684\u8ba1\u5212\u662f\uff0c\u8150\u5176\u6881\u3001\u67f1\uff0c\u8ba9\u5927\u53a6\u81ea\u503e\uff0c\u5374\u4e0d \u6599\uff0c\u534a\u9014\u4e2d\u6740\u51fa\u4e2a\u8881\u7d2b\u70df\u6765\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u7684\u8138\u4e0a\uff0c\u6d6e\u73b0\u8d77\u5fe7\u82e6\u4e4b\u8272\uff0c\u63a5\u9053\uff1a\u201c\u706f\u5149\u4e0b\uff0c\u6211\u65e0\u6cd5\u770b\u5f97\u592a\u8fc7\u771f\u5207\uff0c\u4f46\u5979\u5168\u8eab\u4f3c \u4e4e\u90fd\u88ab\u4e00\u5c42\u4e91\u6c14\u88f9\u4f4f\uff0c\u5e94\u8be5\u662f\u9053\u5bb6\u7684\u62a4\u8eab\u7f61\u6c14\uff0c\u4ee5\u5979\u5e74\u7eaa\u4e4b\u8f7b\uff0c\u80fd\u6709\u8fd9\u6837\u7684\u6210\u5c31\uff0c\u5fc5\u662f\u9ad8\u4eba \u95e8\u4e0b\u3002 \u3000\u3000\u4f46\u6211\u5374\u60f3\u4e0d\u51fa\u5f53\u4e16\u9ad8\u4eba\u4e2d\uff0c\u6709\u8c01\u80fd\u8bad\u7ec3\u51fa\u5982\u8bb8\u7684\u6770\u51fa\u7f8e\u4eba\u3002\u201d \u3000\u3000\u674e\u9756\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5148\u751f\u90fd\u60f3\u4e0d\u51fa\u6765\uff0c\u674e\u9756\u5c31\u66f4\u96be\u8ffd\u5fc6\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5148\u751f\u548c\u674e\u9756\u518d\u52a0\u4e0a \u8881\u8001\u7684\u529b\u91cf\uff0c\u64d2\u670d\u5979\uff0c\u5e94\u8be5\u4e0d\u96be\u3002\u201d \u3000\u3000\u201c\u96be\u4e0b\u5b9a\u8bba\u554a\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4e24\u5929\u540e\u6211\u53bb\u89c1\u5979\uff0c\u5e0c\u671b\u80fd\u5bf9\u5979\u591a\u70b9\u4e86\u89e3\uff0c\u4f60\u53bb\u901a\u77e5\u5929\u7f61 \u4e00\u58f0\uff0c\u8881\u7d2b\u70df\u5982\u82e5\u8ffd\u8feb\u592a\u7d27\uff0c\u6211\u548c\u5979\u4f1a\u9762\u4e4b\u540e\uff0c\u5c31\u53ef\u80fd\u5e26\u5979\u53bb\u89c1\u5929\u7f61\u4e86\u3002\u201d \u3000\u3000\u201c\u5b66\u751f\u4e5f\u5728\u90a3\u91cc\u7b49\u5019\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u5e0c\u671b\u4e8c\u516c\u5b50\u4e5f\u80fd\u8d76\u5230\u3002\u4ee5\u4e16\u6c11\u7684\u98ce\u91c7\u3001\u9b45\u529b\uff0c\u8fd8\u4e0d \u80fd\u4f7f\u5979\u7f62\u624b\u6295\u964d\uff0c\u53ea\u6709\u6740\u6389\u5979\u4e00\u6761\u8def\u4e86\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u59d3\u8881\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e9b\u5b97\u4eb2\u6e0a\u6e90\uff0c\u770b\u770b\u5929\u7f61\u7684\u610f\u601d\u5427\uff1f\u201d \u3000\u3000\u201c\u5341\u4f59\u5e74\u6765\u7684\u5c71\u5ddd\u7075\u6c14\uff0c\u5c3d\u6295\u5165\u8881\u95e8\u4e4b\u4e2d\uff0c\u5b55\u80b2\u51fa\u95ed\u6708\u7f9e\u82b1\u7684\u7f8e\u5973\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u8881\u5b9d \u513f\u96c6\u4eba\u95f4\u7f8e\u8272\u4e4b\u6700\uff0c\u8881\u7d2b\u70df\u5374\u5360\u5c3d\u4e86\u79c0\u7edd\u5929\u4e0b\u7684\u98ce\u91c7\uff0c\u4e00\u4e2a\u4e3a\u60c5\u820d\u8eab\uff0c\u629b\u5f03\u6240\u7231\u5165\u6df1\u5bab\uff0c\u4e00 \u4e2a\u60c5\u7518\u5165\u7ea2\u5c18\uff0c\u96c4\u5173\u4e07\u91cc\u4fdd\u541b\u738b\u3002\u201d \u3000\u3000\u82e6\u7b11\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u901a\u554a\uff01\u600e\u4e48\u4f1a\u8fd9\u6837\u5462\uff1f\u8881\u6c0f\u4e8c\u7f8e\uff0c\u5404\u96c6\u7edd\u8272\uff0c\u5374\u53c8\u5206\u9053\u626c\u9573\uff0c \u5404\u4e3a\u5176\u4e3b\u3002\u201d \u3000\u3000\u771f\u662f\u53f1\u54a4\u53e3\u820c\u4ea6\u95fb\u9999\uff0c\u7edd\u4e16\u7f8e\u5973\uff0c\u672c\u5c31\u4ee4\u4eba\u52a8\u5fc3\uff0c\u7f8e\u5973\u518d\u52a0\u4e0a\u624d\u6167\u3001\u6280\u827a\uff0c\u90a3\u5c31\u4ee4\u82f1\u96c4 \u5fc3\u6298\u4e86\u3002 \u3000\u3000\u201c\u8881\u7d2b\u70df\u4ee4\u4eba\u5fc3\u52a8\uff0c\u5b9d\u513f\u4ee4\u4eba\u5fc3\u75bc\uff0c\u4e8c\u516c\u5b50\u9001\u8d70\u4e86\u5b9d\u513f\u65f6\uff0c\u9669\u4e0a\u72b9\u5e26\u7b11\u610f\u3002\u201d\u674e\u6df3\u98ce \u9053\uff1a\u201c\u4f46\u4ed6\u56de\u5230\u5c45\u5ba4\uff0c\u63a9\u95e8\u800c\u6ce3\uff0c\u5455\u8840\u8fd1\u69f2\uff0c\u591c\u770b\u5b64\u706f\u7adf\u7ec8\u5bb5\uff0c\u76ee\u7686\u8840\u6d41\uff0c\u8001\u673d\u548c\u8881\u5929\u7f61\u9690 \u5728\u6697\u4e2d\u770b\u4e86\u4e00\u591c\u3002 \u3000\u3000\u81f3\u6b64\uff0c\u5b9e\u5df2\u65e0\u6cd5\u5fcd\u8010\uff0c\u5546\u91cf\u51b3\u5b9a\uff0c\u8ffd\u56de\u5b9d\u513f\uff0c\u4e0d\u8ba9\u4ed6\u4e27\u5931\u4e86\u82f1\u96c4\u5fd7\u4e1a\uff0c\u5374\u4e0d\u6599\u8fd9\u65f6\u542c \u5230\uff0c\u4ed6\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff01\u201d \u3000\u3000\u674e\u9756\u6025\u6025\u7684\u95ee\u9053\uff1a\u201d\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u80fd\u4ee4\u4e24\u4f4d\u6539\u53d8\u5fc3\u610f\uff0c\u786c\u628a\u91d1\u7ae5\u3001\u7389\u5973\u822c\u7684\u597d\u59fb\u7f18\u751f\u751f \u5206\u5f00\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u795e\u60c5\u8083\u7136\u5730\u9053\uff1a\u201c\u4ed6\u8f6c\u671b\u7a97\u5916\u7684\u6653\u8272\uff0c\u8bf4\uff1a\u201c\u674e\u4e16\u6c11\u554a\uff0c\u674e\u4e16\u6c11\uff0c\u4f60\u4e0d\u8fc7\u662f\u548c\u60c5 \u4fa3\u5206\u88fe\uff0c\u5c31\u5982\u6b64\u7684\u75db\u4e0d\u6b32\u751f\uff0c\u4f46\u4e16\u4e0a\u90a3\u4e9b\u5343\u5343\u4e07\u4e07\u7684\u5bb6\u5ead\u5462\uff1f\u541b\u4e3b\u6deb\u4e71\uff0c\u6740\u622e\u6210\u6027\uff0c\u5b98\u540f\u8d2a \u6e0e\uff0c\u6536\u8d3f\u6789\u6cd5\u3002 \u3000\u3000\u6709\u591a\u5c11\u59bb\u79bb\u5b50\u6563\uff0c\u9aa8\u8089\u53d7\u6b8b\u7684\u60b2\u5267\uff0c\u4f60\u53ea\u6709\u4e2a\u4eba\u4f24\u75bc\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u60f3\uff0c\u5929\u4e0b\u7684\u9ece\u6c11\u767e\u59d3 \u5462\uff1f\u674e\u4e16\u6c11\u554a\uff01\u4f60\u8be5\u5fcd\u4e0b\u690e\u5fc3\u7684\u60b2\u82e6\uff0c\u632f\u594b\u8d77\u6765\uff01 \u3000\u3000\u4f55\u51b5\u5b9d\u513f\u505a\u7684\u662f\u5343\u79cb\u4e07\u4e16\u5927\u4e1a\uff0c\u4f60\u600e\u4ee4\u5979\u5931\u671b\uff0c\u8ba9\u5979\u727a\u7272\u7684\u6beb\u65e0\u4ef7\u503c\uff0c\u9700\u77e5\u4f60\u62a5\u7b54\u5b9d\u513f \u7684\u6700\u597d\u529e\u6cd5\uff0c\u5c31\u662f\u8981\u521b\u9020\u4e00\u4e2a\u592a\u5e73\u76db\u4e16\uff0c\u8ba9\u5929\u4e0b\u767e\u59d3\uff0c\u5bb6\u5bb6\u80fd\u5b89\u5c45\u4e50\u4e1a\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u4e16\u6c11\u662f\u4e2d\u571f\u4e07\u6c11\u7684\u5e0c\u671b\u6240\u5bc4\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u6bd4\u8d77\u4ed6\uff0c\u6211\u662f\u96be\u53ca\u4e07\u4e00 \u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u8881\u5929\u7f61\u90fd\u88ab\u611f\u52a8\u4e86\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u88c5\u4f5c\uff0c\u90a3\u662f\u9ece\u660e\u4e4d\u73b0\u7684\u65e0\u4eba\u65f6\u5206\uff0c\u6211 \u4eec\u51b3\u5b9a\u4e0d\u8ffd\u5b9d\u513f\uff0c\u5929\u7f61\u4e5f\u51b3\u5b9a\u5168\u529b\u884c\u6cd5\uff0c\u4f7f\u5929\u673a\u8fd0\u884c\u52a0\u901f\uff0c\u65e9\u65e5\u5e2e\u52a9\u4ed6\u5b8c\u6210\u5927\u613f\u3002\u201d \u3000\u3000\u201c\u674e\u9756\u4e5f\u613f\u4e3a\u4e16\u6c11\u6548\u547d\uff0c\u6211\u66fe\u5168\u529b\u8bf4\u670d\u5f20\u51fa\u5c18\uff0c\u4ee5\u6211\u4eec\u7684\u81f3\u8bda\uff0c\u53bb\u611f\u5316\u866c\u9aef\u5ba2\uff0c\u8ba9\u4ed6\u9000 \u51fa\u4e2d\u539f\u2026\u2026\u201d \u3000\u3000\u201c\u674e\u9756\u3002\u201d\u674e\u6df3\u98ce\u63a5\u9053\uff1a\u201c\u4e0d\u7528\u592a\u6025\uff0c\u6025\u6613\u8bef\u4e8b\uff0c\u8fd9\u8981\u5341\u5206\u8bda\u5fc3\uff0c\u4e09\u5206\u673a\u7f18\uff0c\u6211\u4eec\u5bfb\u627e \u65f6\u673a\uff0c\u5b89\u6392\u4e16\u6c11\u548c\u866c\u9aef\u5ba2\u89c1\u89c1\u9762\uff0c\u8ba9\u4ed6\u4e86\u89e3\u4e16\u6c11\u6551\u4e16\u6551\u6c11\u7684\u5927\u5fd7\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f46\u65f6\u673a\u8981\u5b89\u6392\u7684\u9002\u5f53\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u4ef2\u575a\u5144\uff0c\u6027\u5982\u70c8\u706b\uff0c\u4e00\u65e6\u4ed6\u52a8\u4e86\u6740\u6b7b\u4e16\u6c11\u7684 \u5fc3\u610f\uff0c\u90a3\u5c31\u4e0d\u662f\u4e00\u822c\u4eba\u6240\u80fd\u963b\u6b62\u4e86\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4ed6\u5982\u6b64\u63a8\u5d07\u866c\u9aef\u5ba2\uff0c\u81ea\u7136\u4e0d\u53ef\u8f7b\u89c6\uff0c\u4f55\u4e0d\u85c9\u673a\u63a2\u6c42\u4e00\u4e0b\u866c\u9aef\u5ba2\u7684\u6280 \u827a\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5982\u679c\u8ba9\u53d4\u5b9d\u3001\u656c\u5fb7\u4e24\u5927\u52c7\u58eb\uff0c\u5b88\u5728\u4e16\u6c11\u8eab\u65c1\uff0c\u80fd\u4e0d\u80fd\u963b\u6b62\u866c\u9aef \u5ba2\u7684\u4e0b\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\uff0c\u7b97\u4e0a\u6211\u4e5f\u4e0d\u80fd\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u4ef2\u575a\u5927\u54e5\uff0c\u662f\u4f4d\u5927\u5251\u5ba2\uff0c\u767e\u6b65\u4e4b\u5185\uff0c\u4e00\u6325 \u624b\u5c31\u80fd\u53d6\u4eba\u9996\u7ea7\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff1f\u662f\u4e00\u4f4d\u975e\u5e38\u53ef\u6015\u7684\u52b2\u654c\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4ed6\u62e5\u6709\u767e\u4e2a\u4ee5\u4e0a\u7684\u9ad8\u624b\uff0c\u9010\u9e7f\u4e2d\u539f\u9738\u4e3b\u7684\u8ba1\u5212\uff0c\u4ed6\u5df2\u51c6\u5907\u4e86\u4e8c\u5341 \u5e74\uff0c\u5c24\u5176\u662f\u8d22\u6e90\u7684\u5145\u8db3\uff0c\u7edd\u975e\u5510\u516c\u674e\u6e0a\u80fd\u53ca\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u674e\u9756\uff0c\u8881\u7d2b\u70df\u548c\u866c\u9aef\u5ba2\u76f8\u8f83\uff0c\u5b70\u4f18\u6267\u52a3\uff1f\u201d \u3000\u3000\u674e\u9756\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5148\u751f\uff0c\u4f60\u548c\u8881\u516c\u5929\u7f61\u7684\u80dc\u8d1f\u4e4b\u6bd4\uff0c\u8c01\u9ad8\u4e00\u7b79\uff1f\u201d \u3000\u3000\u95ee\u7684\u6709\u5fc3\u673a\uff0c\u7b54\u7684\u4e5f\u51fa\u4eba\u610f\u6599\u3002 \u3000\u3000\u674e\u6df3\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u7b11\u9053\uff1a\u201c\u8bba\u5929\u673a\u6d1e\u5f7b\u4e4b\u80fd\uff0c\u6211\u4e0d\u5982\u5929\u7f61\uff1b\u5385\u6cd5\u5f79\u7269\u4e4b\u672f\uff0c\u7a0d\u9ad8\u4e00\u7b79\uff0c \u771f\u8981\u7b97\u8d77\u6765\uff0c\u662f\u4f2f\u4ef2\u4e4b\u95f4\u5427\uff01\u201d \u3000\u3000\u201c\u5982\u82e5\u5148\u751f\u548c\u8881\u516c\u5408\u529b\u5bf9\u4ed8\u8881\u7d2b\u70df\u5462\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u771f\u662f\u4f5c\u8327\u81ea\u7f1a\u554a\uff1f\u4f46\u53c8\u4e0d\u80fd\u4e0d\u56de\u7b54\u674e\u9756\u3002 \u3000\u3000\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u624d\u9053\uff1a\u201c\u8881\u7d2b\u70df\u662f\u4e2a\u5947\u4eba\uff0c\u56e0\u4e3a\uff0c\u4e00\u822c\u4eba\u90fd\u4e0d\u53ef\u80fd\u5728\u5979\u90a3\u79cd\u5e74\u9f84\u4e2d\uff0c\u6709\u5979 \u90a3\u6837\u7684\u6210\u5c31\uff0c\u5979\u5f88\u5c0f\u5f88\u5c0f\uff0c\u5c31\u4ece\u4e8b\u91d1\u5c71\u5927\u9053\u7684\u4fee\u884c\uff0c\u6211\u548c\u5929\u7f61\u5408\u529b\u62d2\u6297\uff0c\u53ef\u4fdd\u4e0d\u8d25\uff0c\u4f46\u5979\u5982 \u53e6\u6709\u83ab\u6d4b\u672f\u6cd5\uff0c\u5c31\u65e0\u6cd5\u9884\u4f30\u4e86\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u6211\u5c31\u53bb\u89c1\u8881\u516c\u5929\u7f61\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u5bf9\u4ed8\u8881\u7d2b\u70df\u7684\u8ba1\u5212\uff0c\u5b66\u751f\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u53f3\u8896\u4e00\u6325\uff0c\u98d8\u7a7a\u800c\u8d70\uff0c\u98de\u51fa\u4e86\u82b1\u56ed\u4e4b\u5916\u3002 \u3000\u3000\u8881\u7d2b\u70df\u671b\u7740\u6848\u4e0a\u7ea2\u70db\uff0c\u5df2\u71c3\u70e7\u6210\u7070\uff0c\u7ec8\u4e8e\u4e00\u95ea\u800c\u7184\u3002 \u3000\u3000\u5979\u7761\u4e0d\u7740\uff0c\u674e\u6df3\u98ce\u7684\u5934\u9876\u4e0a\uff0c\u6d6e\u73b0\u51fa\u4e09\u5c3a\u7075\u5149\uff0c\u662f\u4e00\u79cd\u660e\u663e\u7684\u8b66\u544a\uff0c\u4e5f\u662f\u8868\u73b0\u51fa\u4ed6\u6df1\u539a \u7684\u9053\u57fa\u548c\u6cd5\u672f\u3002 \u3000\u3000\u957f\u5b89\u5c45\u7684\u4f1a\u6664\uff0c\u9690\u9690\u6709\u56de\u62dc\u7684\u793c\u8c8c\u548c\u5c0a\u91cd\uff0c\u4f46\u4e5f\u53ef\u80fd\u662f\u654c\u53cb\u4e4b\u5206\u7684\u4e00\u6b21\u8c08\u5224\u51b3\u5b9a\u3002 \u3000\u3000\u6b65\u7f61\u8e0f\u6597\uff0c\u4fc3\u4f7f\u5929\u673a\u8fd0\u884c\u53d8\u5316\u7684\u4e0d\u662f\u4ed6\uff0c\u90a3\u662f\u8fd8\u6709\u2014\u4e2a\u9ad8\u660e\u4eba\u7269\u4e86\u3002 \u3000\u3000\u90a3\u4e2a\u4eba\uff0c\u7edd\u4e0d\u4f1a\u6bd4\u674e\u6df3\u98ce\u5dee\u4e0a\u591a\u5c11\u3002 \u3000\u3000\u7a76\u7adf\u8fd8\u6709\u591a\u5c11\u8fd9\u6837\u8eab\u6000\u9053\u6cd5\u7684\u9ad8\u660e\u4eba\u7269\uff0c\u5728\u53cd\u5bf9\u968b\u7080\u5e1d\u5462\uff1f \u3000\u3000\u4ed6\u4eec\u8981\u5e2e\u52a9\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u4fee\u4e60\u9053\u6cd5\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u51fa\u9762\u4e89\u9010\u541b\u738b\u7684\u9738\u4e1a\u3002 \u3000\u3000\u662f\u5510\u516c\u674e\u6e0a\uff0c\u8fd8\u662f\u53e6\u6709\u5176\u4eba\uff1f \u3000\u3000\u8881\u5b9d\u513f\u85cf\u4e8e\u8896\u4e2d\u7684\u90a3\u5e45\u753b\u50cf\uff0c\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f \u3000\u3000\u4ed6\u662f\u8881\u5b9d\u513f\u5fc3\u4e2d\u7684\u60c5\u90ce\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u9519\u4e86\uff0c\u6240\u4ee5\uff0c\u624d\u5b81\u53ef\u5192\u7740\u6781\u5927\u7684\u5371\u9669\uff0c\u628a\u4ed6\u85cf\u5165\u8896 \u4e2d\u3002 \u3000\u3000\u90a3\u4e2a\u4eba\u9f99\u59ff\u51e4\u76ee\uff0c\u6709\u7740\u9886\u8896\u5929\u4e0b\u7684\u6c14\u52bf\uff0c\u5c31\u7b97\u5728\u753b\u50cf\u4e0a\uff0c\u4e5f\u6709\u7740\u4ee4\u4eba\u5fc3\u6298\u7684\u9b45\u529b\u3002\u5982\u679c \u6709\u4eba\u80fd\u593a\u53bb\u5927\u968b\u671d\u7684\u6c5f\u5c71\uff0c\u5c31\u975e\u4ed6\u83ab\u5c5e\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u662f\u8c01\u5462\uff1f \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u7eea\u7eb7\u4e71\uff0c\u5c31\u8fd9\u4e48\u7684\u60f3\u4e86\u4e00\u591c\uff0c\u7ea2\u70db\u71c3\u5c3d\uff0c\u4ecd\u672a\u5b89\u7720\u3002 \u3000\u3000\u201c\u59d1\u5a18\uff0c\u8fd8\u672a\u8d77\u5e8a\u5417\uff1f\u201d \u3000\u3000\u662f\u83b2\u513f\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5929\u5df2\u5927\u4eae\uff0c\u60f3\u662f\u9001\u9762\u6c34\u6765\u4e86\u3002 \u3000\u3000\u201c\u8ba9\u6211\u591a\u7761\u4e00\u523b\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u544a\u8bc9\u5b87\u6587\u5148\u751f\uff0c\u4e0d\u7528\u7b49\u6211\u5403\u65e9\u9910\u4e86\u3002\u201d \u3000\u3000\u5979\u659c\u501a\u5728\u6728\u69bb\u4e0a\uff0c\u4e0d\u662f\u6175\u61d2\u4e0d\u8d77\uff0c\u800c\u662f\u4e0d\u613f\u65ad\u4e86\u5979\u7684\u5982\u6f6e\u601d\u7eea\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4f1a\u89c1\u4e86\u7530\u5f53\u3001\u8427\u96e8\u5e26\u6765\u7684\u516d\u4e2a\u9ad8\u624b\uff0c\u4e5f\u805a\u5728\u957f\u5b89\u5c45\u7684\u9152\u697c\u4e0a\uff0c\u5171\u8fdb\u65e9\u9910\u3002 \u3000\u3000\u8881\u59d1\u5a18\u8981\u591a\u7761\u7247\u523b\uff0c\u5b87\u6587\u6210\u90fd\u4e5f\u5077\u5f97\u8fd9\u523b\u6e05\u95f2\uff0c\u7ea6\u7530\u5f53\u3001\u8427\u96e8\u89c1\u4e2a\u9762\u3002 \u3000\u3000\u201c\u660e\u65e5\u7533\u65f6\uff0c\u56fd\u5e08\u8981\u548c\u4eba\u4f1a\u9762\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u957f\u5b89\u5c45\u9152\u697c\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u201c\u53ef\u8981\u5c5e\u4e0b\u7b49\u65e9\u4f5c\u57cb\u4f0f\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u8fd8\u8bf7\u7edf\u9886\u88c1\u793a\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u8981\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u6211\u4eec\u9762\u5bf9\u7684\u654c\u4eba\u4e0d\u662f\u4e00\u822c\u7684\u6218\u5c06\u9ad8\u624b\uff0c\u800c\u662f\u8d85\u8d8a\u6b66\u529f\u8303\u7574\u4e4b\u5916\u7684\u9053 \u6cd5\u9690\u58eb\uff0c\u4ed6\u4eec\u5177\u6709\u4e00\u79cd\u7075\u529b\uff0c\u6211\u65e0\u6cd5\u9884\u6d4b\u5c06\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u795e\u5947\u529b\u91cf\uff1f\u4e5f\u4e0d\u77e5\u9632\u4e4b\u6cd5\u3002\u4e0d\u8fc7\uff0c \u8881\u56fd\u5e08\u5012\u662f\u63d0\u8fc7\uff0c\u7cbe\u6e5b\u7684\u6280\u827a\u662f\u53ef\u4ee5\u548c\u672f\u6cd5\u5bf9\u6297\uff0c\u8bf8\u4f4d\u8bf7\u8c28\u614e\u8b66\u60d5\uff0c\u7559\u5fc3\u7a81\u7136\u51fa\u73b0\u7684\u5f02 \u5f81\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\u7a81\u5982\u5176\u6765\uff0c\u8427\u96e8\u3001\u7530\u5f53\u542c\u5f97\u5446\u4f4f\u4e86\uff0c\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\uff0c\u53ea\u597d\u8fde\u8fde\u70b9\u5934\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u5929\u6ca1\u6709\u51fa\u8fc7\u623f\u95e8\uff0c\u8fde\u5de7\u513f\u3001\u83b2\u513f\u4e5f\u4e0d\u51c6\u8fdb\u5979\u7684\u623f\u4f3a\uff0c\u4e00\u65e5\u95f4\u8336\u996d\u672a\u7528\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e24\u5ea6\u63a2\u95ee\u8881\u7d2b\u70df\u7684\u884c\u8e2a\u3002 \u3000\u3000\u5de7\u513f\u90fd\u56de\u7b54\u81ea\u95ed\u5728\u623f\u4e2d\uff0c\u8fde\u9001\u8336\u90fd\u4e0d\u8bb8\u4eba\u5165\u623f\u6253\u6270\u3002 \u3000\u3000\u76f4\u5230\u4e0a\u706f\u65f6\u5206\uff1a\u8881\u7d2b\u70df\u624d\u5f00\u542f\u623f\u95e8\u8d70\u51fa\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u548c\u5de7\u513f\u3001\u83b2\u513f\u4e00\u5757\u513f\u5750\u5728\u5385\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u4e00\u65e5\u8c03\u606f\uff0c\u5e94\u8be5\u662f\u5bb9\u5149\u7115\u53d1\uff0c\u4f46\u8881\u7d2b\u70df\u5374\u5e26\u7740\u5026\u5bb9\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u770b\u5f97\u6709\u70b9\u5fc3\u5934\u4f5c\u75bc\uff0c\u4f46\u5374\u4e0d\u77e5\u8be5\u5982\u4f55\u5f00\u53e3\u8868\u793a\u3002 \u3000\u3000\u5de7\u513f\u3001\u83b2\u513f\u8fce\u4e0a\u53bb\uff0c\u9f50\u9f50\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u60f3\u5403\u70b9\u4ec0\u4e48\u4e1c\u897f\u5417\uff1f\u5c0f\u5a62\u53bb\u5429\u5490\u5e97\u5bb6\u51c6 \u5907\u3002\u201d \u3000\u3000\u5728\u5bab\u4e2d\uff0c\u5979\u4eec\u558a\u592b\u4eba\uff0c\u4e00\u79bb\u5f00\u4e86\u7687\u5bab\uff0c\u5979\u4eec\u5c31\u6539\u79f0\u5c0f\u59d0\u3002\u8881\u7d2b\u70df\u6ca1\u6709\u7ea0\u6b63\uff0c\u5c31\u8fd9\u6837\u53eb\u8d77 \u6765\u4e86\u3002 \u3000\u3000\u867d\u7136\u9690\u9690\u5026\u610f\u4e0a\u7709\u68a2\uff0c\u4f46\u5fc3\u60c5\u4f3c\u662f\u5f88\u6109\u5feb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a \u3000\u3000\u201c\u5429\u5490\u5e97\u5bb6\u6574\u6cbb\u4e9b\u53ef\u53e3\u4f73\u80b4\uff0c\u518d\u9001\u4e0a\u4e24\u58f6\u597d\u9152\uff0c\u6211\u8981\u548c\u5b87\u6587\u5c06\u519b\u628a\u9152\u6e05\u8c08\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6709\u70b9\u53d7\u5ba0\u82e5\u60ca\uff0c\u6b20\u6b20\u8eab\u8868\u793a\u4e86\u8c22\u610f\u3002 \u3000\u3000\u9152\u548c\u83dc\u5f88\u5feb\u9001\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u5de7\u513f\u548c\u83b2\u513f\u5373\u5bf9\u671b\u4e86\u773c\uff0c\u6084\u7136\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8d70\u5c31\u8d70\u554a\uff01\u7adf\u7136\u8fd8\u5e26\u4e0a\u4e86\u4e24\u6247\u5385\u95e8\u3002 \u3000\u3000\u8fd9\u4e3e\u52a8\uff0c\u5982\u679c\u88ab\u968b\u7080\u5e1d\u77e5\u9053\u4e86\uff0c\u5b9a\u4f1a\u628a\u5979\u4fe9\u62d6\u53bb\u780d\u5934\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u6709\u963b\u6b62\u4e24\u4e2a\u4e2b\u5934\u7684\u653e\u8086\u884c\u4e3a\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e24\u4e2a\u4e2b\u5934\u4e5f\u4e0d\u662f\u65e0\u7684\u653e\u77e2\uff0c\u8881\u59d1\u5a18\u65e2\u7136\u8981\u548c\u5c06\u519b\u628a\u9152\u6e05\u8c08\uff0c\u4e24\u4e2a\u4eba\u4e5f\u4e0d\u7528\u5fe4\u5728\u4e00 \u8fb9\u4e86\u3002 \u3000\u3000\u5979\u4fe9\u662f\u5584\u89e3\u4eba\u610f\u5440\uff01 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5c06\u519b\u8fdb\u8fc7\u665a\u9910\u4e86\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6447\u6447\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u56fd\u5e08\u597d\u50cf\u4e00\u5929\u672a\u8fdb\u98df\u7269\uff0c\u8fde\u6c34\u4e5f\u672a\u559d\u4e00\u676f\u3002\u201d \u3000\u3000\u56de\u7b54\u5f97\u5f88\u666e\u901a\uff0c\u4f46\u5173\u5fc3\u7684\u60c5\u610f\u5374\u5b8c\u5168\u900f\u9732\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5f88\u5173\u5fc3\u6211\u5440\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\uff01\u56fd\u5e08\u8eab\u7cfb\u5927\u968b\u671d\u7684\u5b89\u5371\uff0c\u6210\u90fd\u5e94\u8be5\u5173\u5fc3\u3002\u201d \u3000\u3000\u4f46\u8881\u7d2b\u70df\u7684\u56de\u8bdd\u5374\u51fa\u4e86\u5b87\u6587\u6210\u90fd\u7684\u610f\u5916\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u611f\u89c9\u5230\u5927\u968b\u671d\u7684\u57fa\u4e1a\u6709\u4e9b\u52a8\u8361\u4e0d\u7a33\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u756a\u5317\u4e0a\uff0c\u624d \u53d1\u89c9\u4e86\u4e8b\u6001\u4e25\u91cd\u5f97\u51fa\u4e86\u6211\u4f30\u7b97\u4e4b\u5916\u3002\u201d \u3000\u3000\u201c\u90a3\u674e\u6df3\u98ce\u662f\u4f4d\u9ad8\u4eba\uff0c\u4f46\u4e5f\u672a\u5fc5\u80fd\u5f3a\u8fc7\u56fd\u5e08\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u53ea\u8981\u56fd\u5e08\u4e00\u9053\u594f\u7ae0\uff0c\u7acb \u523b\u53ef\u8c03\u52a8\u4e8c\u5341\u4e07\u5927\u519b\u76f4\u903c\u592a\u539f\uff0c\u5510\u516c\u674e\u6e0a\u80fd\u591f\u51fa\u52a8\u7684\u5175\u529b\u4e0d\u4f1a\u8d85\u8fc7\u4e24\u4e07\u4eba\uff0c\u7edd\u65e0\u6cd5\u548c\u5929\u671d\u7684 \u5927\u519b\u5bf9\u6297\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5c06\u519b\uff0c\u7d2b\u70df\u867d\u4e0d\u61c2\u5175\u7565\u6218\u7b56\uff0c\u4f46\u6211\u77e5\u9053\u5c06\u5f3a\u624d\u80fd\u5175\u52c7\uff0c\u8001\u4e00\u4ee3\u540d\u5c06\u80fd\u81e3\uff0c \u4e0d\u662f\u88ab\u7687\u4e0a\u6740\u4e86\uff0c\u5c31\u662f\u88ab\u903c\u9000\u5c71\u6797\uff0c\u653e\u773c\u5e99\u5802\uff0c\u5927\u968b\u671d\u8fd8\u6709\u591a\u5c11\u6cbb\u4e16\u671d\u81e3\uff1f\u8fd8\u6709\u591a\u5c11\u80fd\u6218\u7684 \u52c7\u5c06\uff1f\u51e0\u5904\u805a\u5578\u7684\u66b4\u6c11\uff0c\u5df2\u503e\u672c\u56fd\u4e4b\u5175\uff0c\u4f46\u771f\u6b63\u4f01\u56fe\u9020\u53cd\u7684\u8fd8\u90fd\u6309\u5175\u672a\u52a8\u5462\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u56fd\u5e08\u7684\u610f\u601d\u662f\u8bf4\uff0c\u9664\u4e86\u5510\u516c\u674e\u6e0a\u4e4b\u5916\uff0c\u8fd8\u6709\u5176\u4ed6\u7684\u738b\u4faf\u56fd\u516c\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u957f\u5b89\u57ce\u5185\uff0c\u516c\u4ec6\u767e\u5bb6\uff0c\u9664\u4e86\u4f60\u5b87\u6587\u6210\u90fd\u4e4b\u5916\uff0c\u8fd8\u6709\u51e0\u4f4d\u613f\u5c3d\u81e3\u8282\u60c5\u7518\u8d74\u6b7b \u7684\u4e49\u58eb\u5fe0\u826f\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u601d\u7d22\u4e86\u4e00\u9635\uff0c\u7adf\u7136\u7b54\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u201c\u4f17\u53db\u4eb2\u79bb\u5b50\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u957f\u671f\u7684\u5fcd\u53d7\u7740\u662f\u975e\u4e0d\u660e\u751f\u6b7b\u96be\u6599\u7684\u714e\u71ac\u65e5\u5b50\uff0c \u4e00\u65e6\u5927\u4e71\u5f00\u59cb\uff0c\u75be\u98ce\u4e2d\u5c06\u4e0d\u4f1a\u89c1\u5230\u52b2\u8349\uff0c\u4e5f\u5f88\u96be\u89c1\u5230\u7f6e\u751f\u6b7b\u4e8e\u5ea6\u5916\u7684\u5fe0\u826f\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u60f6\u60d1\u4e86\u3002 \u3000\u3000\u4ed6\u5934\u9876\u4e0a\u6eda\u843d\u4e0b\u9897\u9897\u7684\u6c57\u73e0\u513f\u3002 \u3000\u3000\u8fd9\u4e2a\u7f8e\u4e3d\u7684\u738b\u5983\u8bf4\u51fa\u4e86\u8fd9\u4e48\u4e00\u756a\u8bdd\uff0c\u662f\u4ec0\u4e48\u7528\u5fc3\u5462\uff1f \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u5c06\u519b\uff0c\u660e\u767d\u6211\u8fd9\u756a\u8bdd\u7684\u7528\u5fc3\u5417\uff1f\u4e0d\u8981\u4fdd\u7559\uff0c\u60f3\u5230\u4ec0\u4e48\uff1f\u5c3d\u7ba1\u5766\u7136\u7684\u8bf4\u51fa \u6765\u3002\u201d \u3000\u3000\u201c\u4e0d\u660e\u767d\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u56fd\u5e08\u7a76\u7adf\u60f3\u8bf4\u4ec0\u4e48\uff1f\u4f60\u5c31\u660e\u767d\u7684\u8bf4\u51fa\u6765\u5427\uff1f\u201d \u3000\u3000\u8bed\u6c14\u6709\u70b9\u4e0d\u5bf9\u4e86\uff0c\u8fde\u5fc3\u4e2d\u4eba\u90a3\u4efd\u7231\u6155\u7684\u60c5\u610f\u4e5f\u4f3c\u662f\u7a81\u7136\u6d88\u9000\u4e86\u3002 \u3000\u3000\u201c\u770b\u8d77\u6765\uff0c\u4f60\u548c\u7687\u4e0a\u771f\u6709\u70b9\u76f8\u8bb8\u7684\u60c5\u610f\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u51b7\u9177\u6deb\u4e71\uff0c\u4f46\u4ed6\u6709\u624d\u6c14\uff0c \u81f3\u5c11\u770b\u5bf9\u4e86\u4e24\u4e2a\u4eba\uff01\u201d \u3000\u3000\u201c\u5662\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5b8c\u5168\u65e0\u6cd5\u4e86\u89e3\u8881\u7d2b\u70df\u5fc3\u4e2d\u60f3\u4ec0\u4e48\uff1f\u4e5f\u65e0\u6cd5\u63a5\u53e3\u7b54\u8bdd\u3002 \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u5c11\u5e74\u5f1f\u5b50\u6c5f\u6e56\u8001 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u4e09 \u56de\u3000\u7d2b\u70df\u8d74\u4f1a \u3000\u3000\u8881\u7d2b\u70df\u6de1\u6de1\u7684\u9053\uff1a\u201c\u4e00\u4e2a\u662f\u4f60\u5b87\u6587\u5c06\u519b\uff0c\u4e00\u4e2a\u662f\u6211\u8881\u59d1\u5a18\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6210\u90fd\u53d7\u7687\u4e0a\u63d0\u643a\uff0c\u77e5\u9047\u4e4b\u6069\uff0c\u7ec8\u8eab\u96be\u5fd8\u3002\u4e0d\u7ba1\u7687\u4e0a\u4f5c\u4e3a\u5982\u4f55\uff1f\u6210\u90fd\u6548\u547d \u4e00\u751f\uff0c\u7edd\u4e0d\u66f4\u6539\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u5c06\u519b\u5bf9\u6211\u7684\u8bdd\uff0c\u4f3c\u662f\u6709\u4e9b\u8bef\u4f1a\uff0c\u6211\u60f3\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u628a\u5929\u4e0b\u7684\u4e8b\u4f5c\u4e86\u4e00\u6b21 \u63a8\u65ad\uff0c\u4e5f\u51b3\u5b9a\u628a\u60c5\u5f62\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u6210\u90fd\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u201c\u4f60\u548c\u6211\uff0c\u5728\u652f\u6491\u4e00\u4e2a\u6881\u67f1\u5df2\u673d\u7684\u5c06\u503e\u5927\u53a6\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\u80fd\u591f\u652f\u6491\u591a\u5c11\u65f6 \u95f4\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u63a5\u9053\uff1a\u201c\u5c3d\u529b\u800c\u4e3a\u4e86\uff0c\u4ee5\u6b7b\u76f8\u916c\u77e5\u9047\u6069\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\uff1a\u201c\u5982\u80fd\u6d88\u706d\u4e86\u5347\u8d77\u7684\u738b\u6c14\uff0c\u5927\u5c40\u4e5f\u8bb8\u8fd8\u6709\u53ef\u4e3a\u3002\u53ea\u662f\u2026\u2026\u8fd9\u6709\u70b9\u9006\u5929\u800c\u884c\uff0c\u540e \u679c\u96be\u6599\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u56fd\u5e08\u672f\u6cd5\u7cbe\u6e5b\uff0c\u4e0a\u77e5\u5929\u6587\uff0c\u4e0b\u77e5\u5730\u7406\uff0c\u80fd\u63a8\u7b97\u8fc7\u53bb\u672a\u6765\uff1b\u667a \u8005\u754f\u56e0\uff0c\u611a\u8005\u754f\u679c\u3002\u6211\u5982\u6b64\u4e00\u5fc3\u62a5\u6548\u7687\u4e0a\uff0c\u751f\u6b7b\u5728\u6240\u4e0d\u8ba1\uff0c\u662f\u4e0d\u662f\u9006\u5929\u800c\u884c\u4e5f\u5c31\u4e0d\u4f1a\u8ba1\u8f83 \u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c06\u519b\u7684\u5fe0\u541b\u4e4b\u8bda\u4e5f\u8ba9\u6211\u6709\u4e9b\u611f\u52a8\u4e86\uff0c\u597d\u5427\uff01\u56e0\u679c\u3001\u5929\u610f\u6682\u6446\u4e00 \u65c1\uff0c\u73b0\u5728\u8981\u8c08\u4e00\u8c08\u6211\u4eec\u7684\u5904\u5883\u4e86\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4f46\u5f97\u56fd\u5e08\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7531\u6210\u90fd\u5f00\u59cb\uff0c\u4e2a\u4e2a\u4f1a\u594b\u52c7\u5411\u524d\uff0c\u6211\u4e0d\u6562\u62c5\u4fdd\u80fd\u6218\u65e0 \u4e0d\u80dc\uff0c\u53ea\u80fd\u8bf4\u5168\u529b\u4ee5\u8d74\uff0c\u81f3\u6b7b\u65b9\u4f11\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c06\u519b\uff0c\u4f60\u7684\u8c6a\u52c7\u4e4b\u6c14\u4f3c\u4e5f\u6fc0\u8d77\u6211\u7684\u6740\u673a\u4e86\uff01\u60f3\u60f3\u4e00\u6761\u6d3b\u8e66\u9c9c\u8df3 \u7684\u4eba\u547d\uff0c\u6b7b\u4e8e\u6211\u672f\u6cd5\u5229\u5203\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u603b\u662f\u4e0d\u5fcd\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u4e86\uff0c\u5fc3\u4e2d\u7684\u60f6\u60d1\u5c3d\u9664\u3002\u4ed6\u6025\u6025\u9053\uff1a\u201c\u56fd\u5e08\u53ea\u8981\u5236\u670d\u5f3a\u654c\uff0c\u8fd9\u6740\u622e\u7684\u4e8b\u5c31\u4e0d\u7528 \u52b3\u52a8\u56fd\u5e08\uff0c\u7531\u6211\u4eec\u62c5\u5f53\u8d77\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u795e\u8272\u8083\u7136\u7684\u9053\uff1a\u201c\u9664\u4e86\u674e\u6df3\u98ce\u4e4b\u5916\uff0c\u81f3\u5c11\u8fd8\u6709\u4e24\u4f4d\u7cbe\u901a\u672f\u6cd5\u7684\u9ad8\u4eba\uff0c\u53ef\u80fd\u548c\u6211\u4eec \u6b63\u9762\u51b2\u7a81\u3002\u6211\u65e0\u6cd5\u9884\u77e5\u80dc\u8d1f\uff1f\u4e5f\u53ef\u80fd\u5bf9\u4f60\u4eec\u4fdd\u62a4\u4e0d\u5468\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fd9\u63a5\u53e3\u9053\uff1a\u201c\u56fd\u5e08\u4e0d\u7528\u591a\u8651\uff0c\u4ed6\u4eec\u90fd\u662f\u6b7b\u58eb\uff0c\u6210\u90fd\u8bad\u7ec3\u4ed6\u4eec\uff0c\u6559\u7684\u90fd\u662f\u8d74\u6b7b\u53d6 \u654c\u7684\u4fe1\u5ff5\uff0c\u4e49\u65e0\u53cd\u987e\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\uff0c\u597d\u597d\u7684\u53bb\u4f11\u606f\u4e00\u591c\uff0c\u660e\u65e5\u4f1a\u5514\u674e\u6df3\u98ce\u65f6\uff0c\u6211\u66fe\u903c\u4ed6 \u5728\u548c\u6218\u4e4b\u95f4\u4f5c\u4e00\u6289\u62e9\uff0c\u4ed6\u5982\u4e0d\u80af\u5c31\u8303\uff0c\u6211\u4eec\u5c31\u76f4\u6363\u9ec4\u9f99\uff0c\u653e\u624b\u4e00\u6218\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u5982\u7ea6\u800c\u6765\u3002 \u3000\u3000\u957f\u888d\u5927\u8896\uff0c\u6ee1\u9762\u6625\u98ce\u3002 \u3000\u3000\u8eab\u540e\u8ddf\u7740\u4e24\u4e2a\u62a4\u9a7e\u9ad8\u624b\u3002 \u3000\u3000\u5de6\u9996\u4e00\u4eba\u9762\u5982\u84dd\u975b\uff0c\u53d1\u4f3c\u6731\u7802\uff0c\u864e\u718a\u8170\uff0c\u6709\u7740\u4e00\u79cd\u5a01\u731b\u903c\u4eba\u7684\u6c14\u52bf\uff0c\u8170\u4e2d\u6302\u7740\u4e24\u67c4\u77ed \u65a7\uff0c\u7cfb\u4ee5\u7cbe\u94a2\u94fe\u5b50\uff0c\u7ed5\u5728\u81c0\u4e0a\u3002 \u3000\u3000\u663e\u7136\u5728\u90a3\u4e00\u5bf9\u77ed\u65a7\u4e0a\u5fc5\u6709\u7279\u6b8a\u7684\u9020\u8be3\uff0c\u4e00\u773c\u4e4b\u4e0b\uff0c\u5373\u53ef\u770b\u51fa\u662f\u4e00\u5458\u5584\u6218\u7684\u864e\u5c06\u3002\u53ea\u662f\u4ed6 \u7d27\u7ef7\u7740\u4e00\u5f20\u8138\uff0c\u5706\u7741\u7740\u4e00\u5bf9\u773c\uff0c\u4e00\u526f\u516d\u4eb2\u4e0d\u8ba4\u7684\u6c14\u52bf\uff0c\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u80fd\u51fa\u624b\u63cd\u4eba\u7684\u6837\u5b50\u3002 \u3000\u3000\u53f3\u9996\u4e00\u4eba\u770b\u4e0a\u53bb\u5c31\u6587\u660e\u591a\u4e86\uff0c\u767d\u9762\u65e0\u53d1\uff0c\u5e74\u4e0d\u8fc7\u4e8c\u5341\u56db\u4e94\uff0c\u8170\u60ac\u957f\u5251\uff0c\u4f46\u7cbe\u534e\u5185\u8574\uff0c\u4e24 \u9762\u592a\u9633\u7a74\u9ad8\u9ad8\u7a81\u8d77\uff0c\u5206\u660e\u662f\u4e00\u4f4d\u7cbe\u4e60\u5251\u672f\u7684\u5185\u5bb6\u9ad8\u624b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u65e9\u5df2\u5750\u5728\u684c\u65b9\u9996\u4f4d\uff0c\u7b49\u5019\u5ba2\u4eba\u3002 \u3000\u3000\u5bf9\u65b9\u65e2\u7136\u6765\u4e86\u4e09\u4eba\uff0c\u8427\u96e8\u3001\u7530\u5f53\u4e5f\u5c31\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u8427\u96e8\u4f69\u957f\u5251\u3002 \u3000\u3000\u7530\u5f53\u6302\u53cc\u5200\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7684\u7ea2\u8272\u8170\u5e26\u4e2d\u4e5f\u6697\u85cf\u4e86\u9554\u94c1\u8f6f\u5200\u3002 \u3000\u3000\u8881\u7d2b\u70df\u672a\u5e26\u5175\u5203\u3002 \u3000\u3000\u5de7\u513f\u6367\u7740\u4e0a\u65b9\u5b9d\u5251\u3002 \u3000\u3000\u83b2\u513f\u5374\u6367\u7740\u4e00\u4e2a\u7af9\u5236\u7684\u7bee\u5b50\u3002 \u3000\u3000\u7bee\u5b50\u91cc\u5c45\u7136\u63d2\u4e86\u5f88\u591a\u82b1\uff0c\u6709\u83ca\u3001\u6709\u6885\uff0c\u4e5f\u6709\u51ac\u5929\u4e0d\u5f00\u7684\u5170\u8377\u3001\u6843\u3001\u674f\u548c\u7261\u4e39\u3002\u867d\u7136\u8bf4\u4e0d \u4e0a\u878d\u767e\u82b1\u4e8e\u4e00\u7bee\u4e4b\u4e2d\uff0c\u4f46\u82b1\u8272\u7e41\u591a\uff0c\u56db\u5b63\u7684\u82b1\u8272\u5168\u6709\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u814a\u51ac\u5b63\u8282\u5440\uff01 \u3000\u3000\u9664\u4e86\u5e94\u65f6\u7684\u51ac\u6885\u4e4b\u5916\uff0c\u79cb\u83ca\u5df2\u6b8b\uff0c\u5176\u4ed6\u7684\u82b1\u8272\u8fd8\u662f\u6323\u624e\u5728\u98ce\u96ea\u4e0b\u7684\u67af\u679d\uff0c\u8fde\u5ae9\u82bd\u4e5f\u672a\u53d1 \u51fa\uff0c\u54ea\u91cc\u4f1a\u6709\u7efd\u653e\u51fa\u82b1\u6735\u5462\uff1f \u3000\u3000\u53ea\u6b64\u4e00\u6869\uff0c\u5df2\u8868\u73b0\u51fa\u4e86\u593a\u5929\u5730\u9020\u5316\u7684\u672f\u6cd5\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u8bda\u5fc3\u8868\u73b0\u656c\u91cd\uff0c\u8fd8\u662f\u6545\u610f\u4e89\u53d6\u8881\u7d2b\u70df\u7684\u597d\u611f\uff1f\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u5750\uff0c\u5e26\u7740\u7530\u5f53\u3001\u8427\u96e8 \u5e76\u80a9\u513f\u7ad9\u5728\u8881\u7d2b\u70df\u7684\u8eab\u540e\u3002 \u3000\u3000\u674e\u6df3\u98ce\u4e5f\u672a\u5e26\u5175\u5203\uff0c\u4f46\u624b\u4e2d\u5374\u6267\u7740\u4e00\u67c4\u62c2\u5c18\uff0c\u5c18\u4e1d\u5f88\u957f\uff0c\u8db3\u8db3\u6709\u4e24\u5c3a\u4ee5\u4e0a\uff0c\u4e0d\u4f3c\u9a6c\u5c3e\uff0c \u4e5f\u4e0d\u662f\u94a2\u4e1d\u3001\u94c1\u7ebf\uff0c\u4e0d\u77e5\u662f\u4f55\u7269\u4f5c\u6210\uff1f \u3000\u3000\u8881\u7d2b\u70df\u6b20\u8eab\u8fce\u5ba2\u3002 \u3000\u3000\u5979\u7f13\u7f13\u7684\u9053\uff1a\u201c\u5148\u751f\u8bf7\u5750\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u76ef\u5728\u62c2\u5c18\u7684\u5782\u4e1d\u4e0a\u3002 \u3000\u3000\u8fd9\u5c31\u4f7f\u5f97\u674e\u6df3\u98ce\u6709\u70b9\u5c34\u5c2c\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u66ff\u8881\u7d2b\u70df\u5f15\u89c1\u4e00\u4e0b\uff0c\u4e5f\u53ea\u597d\u5fcd\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u6df3\u98ce\u7684\u76ee\u5149\u5f88\u5feb\u88ab\u90a3\u4e2a\u82b1\u7bee\u5b50\u5438\u5f15\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u4e86\u7bee\u4e2d\u4e0d\u4f46\u76db\u653e\u4e86\u56db\u5b63\u82b1\u8272\uff0c\u800c\u4e14\u8fd8\u5728\u4e0d\u505c\u7684\u6210\u957f\u8301\u58ee\u3002\u4e00\u682a\u6843\u6811\u5c45\u7bee\u4e4b\u4e2d\uff0c\u4e0d \u4f46\u5f00\u82b1\uff0c\u8fd8\u5728\u5feb\u901f\u5730\u957f\u9ad8\u3002 \u3000\u3000\u53ef\u771f\u662f\u90aa\u6c14\u5f97\u5389\u5bb3\u5440\uff01 \u3000\u3000\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7af9\u7bee\u4e2d\u7adf\u7136\u8574\u85cf\u4e86\u5316\u80b2\u4e07\u7269\u7684\u751f\u673a\u3002 \u3000\u3000\u201c\u674e\u5148\u751f\uff01\u201d\u8881\u7d2b\u70df\u7684\u58f0\u97f3\u662f\u5a07\u8106\u52a8\u542c\u7684\uff0c\u4f46\u8bed\u6c14\u5374\u4e0d\u662f\u5f88\u53cb\u5584\uff0c\u9053\uff1a\u201c\u6211\u8981\u89c1\u89c1\u90a3\u4f4d \u62e8\u52a8\u5929\u673a\u7684\u4eba\u3002\u4fa0\u4ee5\u6b66\u72af\u7981\uff0c\u9053\u4ee5\u672f\u7f6a\u5929\uff0c\u662f\u4e0d\u53ef\u539f\u5ba5\u7684\u6076\u884c\uff0c\u4ed6\u4eec\u5e94\u8be5\u53d7\u5230\u671d\u5ef7\u738b\u6cd5\u60e9 \u5904\uff0c\u5148\u751f\u4ee5\u4e3a\u7136\u5426\uff1f\u201d \u3000\u3000\u4e00\u5f00\u53e3\u5c31\u5145\u6ee1\u4e86\u706b\u836f\u6c14\u606f\uff0c\u65a9\u65ad\u4e86\u5546\u91cf\u7684\u4f59\u5730\uff0c\u5484\u5484\u903c\u4eba\u3002 \u3000\u3000\u674e\u6df3\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6270\u4e71\u4e86\u5929\u65f6\u8282\u4ee4\uff0c\u805a\u56db\u5b63\u82b1\u5349\u4e8e\u4e00\u7bee\uff0c\u662f\u4e0d\u662f\u4e5f\u5e72\u72af\u4e86\u5929\u673a \u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fce\u63a5\u9ad8\u5bbe\uff0c\u7a0d\u8868\u656c\u610f\u3002\u5148\u751f\u672a\u514d\u592a\u5c0f\u9898\u5927\u4f5c\u4e86\uff1f\u201d \u3000\u3000\u542c\u4e24\u4eba\u8fd9\u4e00\u756a\u8c08\u8bdd\uff0c\u624d\u5f15\u8d77\u4e86\u522b\u4eba\u7684\u6ce8\u610f\uff0c\u6240\u6709\u7684\u76ee\u5149\u90fd\u6295\u5411\u4e86\u83b2\u513f\u6367\u7740\u7684\u7af9\u7bee\u5b50\u770b \u53bb\u3002 \u3000\u3000\u53d1\u89c9\u4e86\uff0c\u7bee\u4e2d\u4e0d\u4f46\u6709\u56db\u5b63\u82b1\u5349\uff0c\u800c\u4e14\u90a3\u682a\u6843\u6811\u5df2\u7136\u9ad8\u8fc7\u7af9\u7bee\u4e24\u5c3a\u4ee5\u4e0a\u3002 \u3000\u3000\u83b2\u513f\u7f13\u7f13\u628a\u624b\u4e2d\u6367\u7684\u7af9\u7bee\u5b50\u653e\u5728\u9762\u524d\u7684\u6728\u6848\u4e0a\u3002 \u3000\u3000\u5947\u4e8b\u53d1\u751f\u4e86\u2026\u2026 \u3000\u3000\u9ad8\u957f\u7684\u6843\u6811\uff0c\u7adf\u7136\u5728\u4eba\u7684\u76ee\u89c6\u4e0b\u7ed3\u679c\u6210\u5b9e\u3002 \u3000\u3000\u4e0d\u8fc7\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u7ed3\u51fa\u4e86\u4e24\u53ea\u6843\u5b50\u6765\u3002 \u3000\u3000\u6843\u5b50\u5728\u4e0d\u505c\u7684\u957f\u5927\uff0c\u4f7f\u679d\u5e72\u90fd\u5782\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u602a\u4e8b\u554a\u602a\u4e8b\uff01\u8fd9\u662f\u4ec0\u4e48\u5996\u6cd5\uff1f\u60d1\u4eba\u8033\u76ee\uff0c\u5403\u4ffa\u8001\u7a0b\u4e00\u65a7\u3002\u201d \u3000\u3000\u7ad9\u5728\u674e\u6df3\u98ce\u5de6\u9996\u7684\u8d64\u53d1\u5927\u6c49\uff0c\u8bf4\u52a8\u5c31\u52a8\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u5bd2\u8292\u95ea\u52a8\uff0c\u4e00\u67c4\u77ed\u65a7\u75be\u5411\u6843\u6811\u5288\u4e86 \u53bb\u3002 \u3000\u3000\u4e00\u7247\u5200\u5149\u98de\u4e86\u8fc7\u6765\uff0c\u67b6\u4f4f\u5229\u65a7\u3002 \u3000\u3000\u540c\u65f6\u54cd\u8d77\u4e86\u51b7\u51b7\u58f0\u97f3\u9053\uff1a\u201c\u592a\u83bd\u649e\u4e86\uff0c\u5f00\u82b1\u7ed3\u679c\u6210\u4e8e\u4e00\u523b\u4e4b\u95f4\uff0c\u662f\u4f55\u7b49\u5947\u5e7b\u4ed9\u672f\uff1f\u5c82\u5bb9 \u5f97\u4f60\u8fd9\u4e2a\u83bd\u592b\u7834\u574f\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u662f\u7530\u5f53\uff0c\u53f3\u624b\u7684\u5bbd\u9762\u5200\u4ecd\u67b6\u5728\u5229\u65a7\u4e4b\u4e0b\u3002 \u3000\u3000\u674e\u6df3\u98ce\u7f13\u7f13\u5730\u9053\uff1a\u201c\u7a0b\u77e5\u8282\uff0c\u4e0d\u53ef\u52a8\u7c97\uff01\u7834\u574f\u4e86\u4ed9\u679c\u7f8e\u5473\uff01\u201d \u3000\u3000\u7a0b\u77e5\u8282\u4f3c\u662f\u6709\u4e9b\u4e0d\u5f88\u670d\u6c14\uff0c\u4f46\u53c8\u4e0d\u6562\u6297\u62d2\u674e\u6df3\u98ce\u7684\u4ee4\u8c15\uff0c\u6068\u6068\u5730\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6536\u56de\u77ed \u65a7\u3002 \u3000\u3000\u674e\u6df3\u98ce\u6de1\u6de1\u5730\u9053\uff1a\u201c\u8881\u59d1\u5a18\u4ed9\u6843\u8fce\u5ba2\uff0c\u4e0d\u77e5\u53ef\u5426\u8ba9\u6df3\u98ce\u54c1\u5c1d\u4e00\u679a\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u7b11\u9053\uff1a\u201c\u5148\u751f\u5982\u4e0d\u6015\u679c\u4e2d\u6709\u6bd2\uff0c\u5c3d\u7ba1\u98df\u7528\u3002\u4e0d\u8fc7\u2026\u2026\u5148\u751f\u627f\u8bfa\u4e4b\u8a00\uff0c\u5e0c\u671b\u80fd\u8a00 \u800c\u6709\u4fe1\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u6458\u4e86\u4e00\u679a\u6843\u5b50\uff0c\u54ac\u4e86\u4e00\u53e3\uff0c\u7adf\u89c9\u6843\u6c41\u751c\u7f8e\uff0c\u987f\u7136\u4e00\u5446\uff01\u8fd9\u4e0d\u662f\u4e00\u822c\u7684\u5e7b\u53d8\u969c\u773c \u672f\uff0c\u800c\u662f\u4e94\u884c\u642c\u8fd0\u5927\u6cd5\u3002 \u3000\u3000\u674e\u6df3\u98ce\u4e0d\u7981\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u673d\u53ea\u60f3\u548c\u59d1\u5a18\u4f5c\u6b21\u6df1\u8c08\uff0c\u81f3\u4e8e\u7b54\u5141\u5e26\u4f60\u53bb\u89c1\u655d\u53cb\uff0c\u7edd\u4e0d \u4f1a\u5931\u4fe1\u4e8e\u4f60\u3002\u4f46\u4f20\u8baf\u4e4b\u4eba\u5c1a\u672a\u56de\u62a5\uff0c\u4e0d\u77e5\u59d1\u5a18\u53ef\u5426\u5bbd\u9650\u51e0\u65e5\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u5c31\u6709\u4e9b\u77eb\u60c5\u4e86\uff0c\u4f60\u65e2\u5df2\u6d3e\u4eba\u901a\u77e5\u4e86\uff0c\u60f3\u4ed6\u5df2\u7136\u6709\u5907\uff0c\u591a\u5019\u51e0\u65e5\uff0c\u663e\u662f\u522b \u6709\u7528\u5fc3\u2026\u2026\u201d \u3000\u3000\u674e\u6df3\u98ce\u5fd9\u63a5\u53e3\u9053\uff1a\u201c\u8001\u673d\u5e0c\u671b\u59d1\u5a18\u591a\u7ed9\u4e24\u5929\u65f6\u95f4\uff0c\u5012\u4e0d\u662f\u4e3a\u655d\u53cb\u62c5\u5fc3\uff0c\u66f4\u65e0\u5b89\u6392\u57cb\u4f0f\u7684 \u7528\u610f\uff0c\u800c\u662f\u5e0c\u671b\u59d1\u5a18\u80fd\u89c1\u5230\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u53ef\u5426\u5148\u884c\u793a\u77e5\uff0c\u8ba9\u6211\u6709\u4e2a\u88c1\u91cf\u7684\u4f59\u5730\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u4f4e\u58f0\u9053\uff1a\u201c\u89c1\u9762\u5c31\u4f1a\u77e5\u9053\uff0c\u8001\u673d\u4e0d\u4fbf\u5148\u884c\u8bf4\u51fa\u4ed6\u7684\u59d3\u540d\u8eab\u4efd\u3002\u90a3\u4e2a\u4eba\u5f88\u91cd\u8981\uff0c\u8881 \u59d1\u5a18\u5f53\u53ef\u76f8\u4fe1\u8001\u673d\u4e0d\u662f\u5356\u5f04\u53e3\u820c\u7684\u4eba\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u95ee\u9053\uff1a\u201c\u8fd8\u8981\u7b49\u5019\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u5feb\u5219\u4e24\u65e5\uff0c\u8fdf\u5728\u4e09\u5929\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u5c4a\u65f6\uff0c\u8001\u673d\u5907\u9a6c\u8fce\u63a5\uff0c\u76f4\u5954\u4ed6\u8bbe\u575b\u884c\u6cd5\u4e4b \u5904\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u7b11\u9053\uff1a\u201c\u7d2b\u70df\u4fe1\u5f97\u8fc7\u5148\u751f\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u539f\u60f3\u676f\u9152\u8a00\u6b22\uff0c\u548c\u59d1\u5a18\u4f5c\u756a\u6df1\u8c08\u3002\u60f3\u4e0d\u5230\u7adf\u7136\u5f04\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4e0d\u582a\u7684\u5c40 \u9762\uff0c\u610f\u5728\u613f\u5916\uff0c\u771f\u6709\u5929\u610f\u96be\u6d4b\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u6211\u6709\u610f\u9020\u6210\u50f5\u5c40\uff0c\u5012\u662f\u5982\u613f\u5f97\u507f\uff0c\u7701\u53bb\u4e86\u4e0d\u5c11\u53e3\u820c\u3002\u770b\u6837\u5b50\u4ed6\u4eec\u5df2 \u8c03\u52a8\u4eba\u624b\uff0c\u51c6\u5907\u4e00\u573a\u51b3\u6218\uff0c\u4e91\u4e2d\u5c71\u4e0a\u7684\u6740\u6c14\uff0c\u6628\u591c\u5df2\u6d88\u5931\u4e0d\u89c1\uff0c\u60f3\u662f\u5df2\u628a\u4eba\u624b\u96c6\u4e2d\u4e8e\u6cd5\u575b\u5de6 \u8fd1\u3002\u8fd9\u6837\u4e5f\u597d\uff0c\u5e0c\u671b\u4e00\u6218\u626b\u706d\u738b\u6c14\uff0c\u4e5f\u7b97\u5b8c\u6210\u4e0b\u5c71\u4e4b\u613f\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5c31\u7ed9\u5148\u751f\u4e09\u5929\u65f6\u95f4\uff0c\u5e0c\u671b\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d \u3000\u3000\u987f\u4e86\u987f\uff0c\u8bed\u6c14\u8f6c\u8d8b\u4e25\u5389\uff0c\u63a5\u9053\uff1a\u201c\u4e09\u65e5\u4e4b\u540e\uff0c\u4e0d\u89c1\u5148\u751f\u56de\u97f3\uff0c\u5c31\u522b\u602a\u7d2b\u70df\u65e0\u793c\u4e86\uff0c\u5510\u516c \u7684\u7559\u5b88\u4faf\u5e9c\uff0c\u6050\u5c06\u6709\u4e00\u573a\u6d69\u52ab\u4e86\uff01\u201d \u3000\u3000\u8fd9\u662f\u4e0d\u7559\u4f59\u5730\u7684\u6050\u5413\u3002 \u3000\u3000\u674e\u6df3\u98ce\u8138\u8272\u53d8\u4e86\uff0c\u8bed\u58f0\u4e5f\u8f6c\u51b7\u6f20\u9053\uff1a\u201c\u8881\u59d1\u5a18\u8a00\u91cd\u4e86\uff0c\u8001\u673d\u548c\u655d\u53cb\u4e00\u5b9a\u4f1a\u5982\u59d1\u5a18\u4e4b \u613f\u3002\u201d \u3000\u3000\u7a0b\u77e5\u8282\u5fcd\u4e86\u53c8\u5fcd\uff0c\u8fd8\u662f\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff01 \u3000\u3000\u4ed6\u621f\u6307\u7740\u7530\u5f53\uff0c\u559d\u9053\uff1a\u201c\u6765\u6765\u6765\uff0c\u4f60\u6211\u662f\u7b28\u9e1f\u5148\u98de\uff0c\u6253\u65d7\u7684\u5148\u4e0a\uff0c\u4eca\u65e5\u5148\u6765\u4e2a\u5f00\u573a\u5c0f \u6218\uff01\u201d \u3000\u3000\u7530\u5f53\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5411\u6211\u6311\u6218\uff1f\u201d \u3000\u3000\u7a0b\u77e5\u8282\u54fc\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6562\u4e0d\u6562\u548c\u4ffa\u8001\u7a0b\u5148\u6218\u4e09\u767e\u4e2a\u56de\u5408\uff1f\u201d\u7530\u5f53\u56de\u5934\u770b\u53bb\u2026\u2026 \u3000\u3000\u8881\u7d2b\u70df\u548c\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u963b\u6b62\u7684\u610f\u601d\u3002 \u3000\u3000\u674e\u6df3\u98ce\u4e5f\u6ca1\u559d\u6b62\u7a0b\u77e5\u8282\uff0c\u597d\u50cf\u53cc\u65b9\u7684\u9996\u8111\u4eba\u7269\u90fd\u540c\u5141\u5148\u6253\u4e00\u573a\uff0c\u4ee5\u6d4b\u8bd5\u4e00\u4e0b\u5bf9\u65b9\u7684\u5b9e \u529b\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u5df2\u8feb\u4e0d\u53ca\u5f85\u7684\u4eae\u51fa\u53cc\u65a7\uff0c\u51b2\u5165\u573a\u4e2d\u3002 \u3000\u3000\u8fd9\u5ea7\u4e8c\u697c\u7684\u684c\u6905\u90fd\u5df2\u88ab\u642c\u81f3\u5899\u89d2\uff0c\u53ea\u6709\u4e2d\u95f4\u4e24\u5f20\u5bf9\u6392\u7684\u957f\u6848\uff0c\u4f5c\u4e3a\u53cc\u65b9\u8c08\u5224\u4e4b\u7528\uff0c\u7a7a\u5730 \u5f88\u5927\u3002 \u3000\u3000\u7530\u5f53\u62bd\u51fa\u53cc\u5200\uff0c\u7f13\u6b65\u884c\u51fa\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5374\u5728\u76d8\u7b97\u7740\uff0c\u8fd9\u4e00\u6218\u5c31\u662f\u6253\u6389\u4e86\u6027\u547d\uff0c\u4e5f\u4e0d\u80fd\u8d25\u5728\u8d64\u987b\u5927\u6c49\u7684\u624b\u4e0b\uff0c\u4e00\u4e0a\u9635\u5c31\u5b58 \u4e0b\u4e86\u51b3\u6b7b\u4e4b\u5fc3\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u65a7\u98de\u5377\u3002\u7530\u5f53\u8fd8\u5728\u4e03\u516b\u5c3a\u5916\uff0c\u4ed6\u5df2\u98de\u6740\u8fc7\u53bb\u3002 \u3000\u3000\u7530\u5f53\u53cc\u5200\u5e76\u4e3e\uff0c\u63a5\u4e0b\u53cc\u65a7\u3002 \u3000\u3000\u91d1\u94c1\u5927\u9707\u58f0\u4e2d\uff0c\u95ea\u98de\u8d77\u4e00\u4e32\u706b\u661f\u3002 \u3000\u3000\u8fd9\u662f\u786c\u78b0\u7684\u4e00\u51fb\uff0c\u7530\u5f53\u7adf\u6709\u7740\u53cc\u81c2\u4e00\u9ebb\u7684\u611f\u89c9\u3002 \u3000\u3000\u7530\u5f53\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u53ae\u81c2\u529b\u60ca\u4eba\uff0c\u4e0d\u5b9c\u786c\u62da\uff01\u201d \u3000\u3000\u6b63\u5f85\u6325\u5200\u8fdb\u51fb\uff0c\u7a0b\u77e5\u8282\u5df2\u75af\u72c2\u7684\u731b\u653b\u8fc7\u6765\uff0c\u53cc\u65a7\u5982\u96ea\u82b1\u76d6\u9876\u2014\u822c\uff0c\u76f4\u538b\u800c\u4e0b\u3002 \u3000\u3000\u8fd9\u5373\u903c\u5f97\u7530\u5f53\u4e0d\u5f97\u4e0d\u6325\u5200\u786c\u63a5\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u95ea\u907f\u5f00\u53bb\u7684\uff0c\u4f46\u4ed6\u4e0d\u613f\u793a\u5f31\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4e00\u53e3\u6c14\u72c2\u653b\u4e86\u4e8c\u5341\u4e03\u65a7\uff0c\u6253\u5f97\u7530\u5f53\u63e1\u5200\u53cc\u81c2\u9178\u9ebb\u4e0d\u582a\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u770b\u51fa\u4e86\u7530\u5f53\u7684\u5904\u5883\u4e0d\u59a5\uff0c\u4f46\u5374\u6ca1\u6709\u51fa\u624b\u63f4\u6551\uff0c\u4ed6\u77e5\u9053\u7530\u5f53\u7684\u6280\u827a\u5728\u5bf9\u65b9\u72c2\u5389\u7684 \u653b\u52bf\u4e0b\uff0c\u5c1a\u672a\u53d1\u6325\u3002 \u3000\u3000\u679c\u7136\uff0c\u7a0b\u77e5\u8282\u4e00\u8f6e\u72c2\u653b\u4e4b\u540e\uff0c\u6c14\u52bf\u7a0d\u6b47\u3002 \u3000\u3000\u7530\u5f53\u5374\u4e00\u54ac\u7259\uff0c\u5c55\u5f00\u53cd\u51fb\uff0c\u4eba\u968f\u5200\u8f6c\uff0c\u5f62\u5982\u98de\u8f6e\uff0c\u76f4\u5411\u7a0b\u77e5\u8282\u6eda\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u53cc\u65a7\u6325\u52a8\uff0c\u63a5\u4e0b\u653b\u52bf\u3002 \u3000\u3000\u7530\u5f53\u4e5f\u53ca\u65f6\u4fee\u6b63\u4e86\u5bf9\u654c\u7684\u6253\u6cd5\uff0c\u4e0d\u518d\u548c\u5bf9\u65b9\u786c\u62da\u81c2\u529b\uff0c\u4f46\u6253\u6cd5\u4e4b\u51f6\u72e0\uff0c\u5374\u53c8\u662f\u51fa\u4eba\u610f\u6599 \u4e4b\u5916\u3002 \u3000\u3000\u4ed6\u4e0d\u987e\u81ea\u8eab\u7684\u5b89\u5371\uff0c\u53ea\u6c42\u4f24\u654c\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4e00\u65a7\u5288\u4e0b\uff0c\u53ef\u80fd\u8981\u65a9\u4e0b\u7530\u5f53\u4e00\u6761\u5de6\u80f3\u818a\uff0c\u4f46\u7530\u5f53\u53f3\u624b\u653b\u51fa\u7684\u4e00\u5200\u80fd\u5207\u5165\u5bf9\u65b9\u7684\u808b \u5185\uff0c\u4ed6\u5c31\u4e0d\u8981\u90a3\u6761\u80f3\u818a\u4e86\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4e0d\u60f3\u540c\u53d7\u91cd\u4f24\uff0c\u5c31\u53ea\u6709\u95ea\u907f\u5200\u52bf\u3002 \u3000\u3000\u4e00\u95ea\u4e4b\u4e0b\uff0c\u65a7\u5203\u4e5f\u65e0\u5288\u4e2d\u5bf9\u65b9\u3002 \u3000\u3000\u4f46\u8fd9\u5c31\u7ed9\u4e86\u7530\u5f53\u62a2\u653b\u7684\u5148\u673a\u3002 \u3000\u3000\u8fd9\u79cd\u4e0d\u8981\u547d\u7684\u6253\u6cd5\uff0c\u770b\u5f97\u8881\u7d2b\u70df\u76f4\u76b1\u7709\u5934\u3002 \u3000\u3000\u674e\u6df3\u98ce\u8f7b\u62c2\u957f\u9aef\uff0c\u5fae\u5fae\u6447\u5934\uff0c\u5fc3\u4e2d\u4e5f\u4f3c\u6709\u4e0d\u4ee5\u4e3a\u7136\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u6bd4\u6597\u6280\u827a\uff0c\u662f\u4ee5\u8840\u6362\u8840\uff0c\u4ee5\u547d\u640f\u547d\uff0c\u592a\u8fc7\u51f6\u72e0\u9738\u9053\uff0c\u770b\u4e0a\u53bb\u5c31\u5168\u65e0\u6280\u827a\u5c55\u73b0\u7684\u7f8e \u611f\u4e86\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4e5f\u88ab\u7530\u5f53\u7684\u6253\u6cd5\u903c\u706b\u4e86\uff0c\u7adf\u4e5f\u4e0d\u518d\u95ea\u907f\uff0c\u65a7\u5149\u5200\u5203\u5c31\u5f88\u4e0d\u5e78\u5730\u5168\u62db\u547c\u5728\u5bf9\u65b9\u7684\u8eab \u4e0a\u4e86\u3002 \u3000\u3000\u53cc\u65b9\u540c\u65f6\u53d1\u51fa\u4e86\u4e00\u58f0\u95ea\u54fc\uff0c\u9c9c\u8840\u6e85\u98de\u800c\u8d77\u3002 \u3000\u3000\u7530\u5f53\u7684\u540e\u80cc\u4e0a\uff0c\u88ab\u5229\u65a7\u5212\u88c2\u4e86\u4e00\u6761\u957f\u8fc7\u4e00\u5c3a\u7684\u8840\u53e3\uff0c\u53ea\u89c1\u5230\u9c9c\u8840\u55b7\u51fa\uff0c\u5374\u77a7\u4e0d\u51fa\u4f24\u53e3\u7684 \u6df1\u5ea6\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4e5f\u88ab\u4e00\u5200\u5212\u5f00\u4e86\u524d\u80f8\uff0c\u8840\u50cf\u6d41\u6c34\u822c\u6eda\u51fa\u3002 \u3000\u3000\u4e24\u6761\u4eba\u5f71\uff0c\u540c\u65f6\u95ea\u52a8\u3002 \u3000\u3000\u674e\u6df3\u98ce\u8eab\u540e\u90a3\u4f4d\u5e74\u8f7b\u4eba\u548c\u8427\u96e8\uff0c\u540c\u65f6\u98de\u8eab\u800c\u51fa\u3002 \u3000\u3000\u4e24\u67c4\u5251\u540c\u65f6\u51fa\u9798\uff0c\u6307\u5411\u4e86\u5bf9\u65b9\u3002 \u3000\u3000\u674e\u6df3\u98ce\u7a81\u7136\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4f24\u5f97\u90fd\u4e0d\u8f7b\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u662f\uff01\u8840\u67d3\u5f81\u8863\u6620\u82b1\u7ea2\uff0c\u518d\u4e0d\u6551\u6cbb\uff0c\u4ed6\u4eec\u90fd\u53ef\u80fd\u53d8\u6210\u6b8b\u5e9f\u4e86\uff01\u201d \u3000\u3000\u201c\u5230\u6b64\u4e3a\u6b62\u5427\uff01\u59d1\u5a18\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u7684\u5c5e\u4e0b\u89c6\u6b7b\u5982\u5f52\uff0c\u8001\u673d\u9886\u6559\u4e86\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5148\u751f\u9a6d\u4eba\u6709\u672f\uff0c\u4ed6\u4eec\u8c6a\u52c7\u65e0\u6bd4\uff0c\u4e09\u65e5\u540e\u8fd8\u8981\u4f1a\u9762\uff0c\u53c8\u4f55\u82e6 \u6025\u5728\u4e00\u65f6\uff0c\u6210\u90fd\uff01\u6211\u4eec\u56de\u53bb\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u62b1\u8d77\u4e86\u7530\u5f53\uff0c\u53d1\u89c9\u7530\u5f53\u662f\u4f24\u5f97\u5f88\u91cd\uff0c\u82e5\u662f\u4e0d\u53ca\u65f6\u6551\u6cbb\uff0c\u6050\u6015\u5f88\u96be\u4fdd\u4f4f\u8fd9\u4e00\u6761\u547d \u4e86\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u5f3a\u901e\u786c\u6717\uff0c\u4e00\u624b\u63a9\u4f4f\u4f24\u53e3\uff0c\u5927\u6b65\u4e0b\u697c\u800c\u53bb\u3002\u4f46\u4e00\u51fa\u957f\u5b89\u5c45\uff0c\u4eba\u5c31\u6491\u4e0d\u4f4f\u4e86\uff0c\u4e00\u8de4 \u8dcc\u5728\u5927\u8857\u4e0a\u3002 \u3000\u3000\u201c\u56fd\u5e08\uff0c\u6551\u6551\u7530\u5f53\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4ed6\u4e0d\u7518\u793a\u5f31\uff0c\u820d\u957f\u53d6\u77ed\uff0c\u6253\u5f97\u592a\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u5f88\u52c7\u6562\uff0c\u662f\u4e00\u5458\u864e\u5c06\uff1f\u6240\u4ee5\uff0c\u4ed6\u4e0d\u4f1a\u6b7b\uff0c\u800c\u4e14\u4f1a\u5f88\u5feb\u590d\u539f\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5ffd\u7136\u60f3\u8d77\u4e86\u83b2\u513f\uff0c\u90a3\u5e94\u8be5\u662f\u5367\u5e8a\u4e00\u6708\u517b\u606f\u7684\u4f24\u52bf\uff0c\u4e24\u5929\u5c31\u5168\u597d\u4e86\u3002 \u3000\u3000\u7530\u5f53\u8eba\u5728\u5b57\u6587\u6210\u90fd\u7684\u5e8a\u4e0a\uff0c\u51fa\u6c14\u591a\u8fdb\u6c14\u5c11\u3002\u6240\u8c13\u5944\u5944\u4e00\u606f\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u4e2a\u6837\u5b50\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53d6\u51fa\u4e00\u9897\u4e39\u4e38\uff0c\u6295\u5165\u7530\u5f53\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u5de7\u513f\u7528\u6e29\u6c34\uff0c\u51b2\u5165\u8179\u5185\u3002 \u3000\u3000\u201c\u8bf8\u4f4d\uff0c\u6682\u65f6\u9000\u51fa\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4e00\u822c\u836f\u7269\u80fd\u6cbb\u7684\u4f24\uff01\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u5979\u53e6\u6709\u7597\u4f24\u5947\u672f\uff0c\u53ea\u662f\u4e0d\u4fbf\u8ba9\u4eba\u770b\u5230\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7acb\u523b\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u63a9\u4e0a\u4e86\u623f\u95e8\uff0c\u4e5f\u95ed\u4e0a\u4e86\u7a97\u6237\u3002 \u3000\u3000\uff0c\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6697\u9053\uff1a\u4e0d\u77e5\u8881\u7d2b\u70df\u8981\u7528\u4ec0\u4e48\u65b9\u6cd5\u5e2e\u7530\u5f53\u7597\u6cbb\u4f24\u52bf\uff0c\u8fd9\u4e9b\u5b8c\u5168\u8131\u51fa\u5e38\u60c5\u533b \u9053\u7684\u4ed9\u6cd5\u3001\u5947\u672f\uff0c\u771f\u7684\u80fd\u8d77\u6b7b\u56de\u751f\u5417\uff1f \u3000\u3000\u5c31\u5728\u4ed6\u5fd6\u601d\u4e4b\u95f4\uff0c\u6728\u95e8\u6253\u5f00\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7f13\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u8981\u5e97\u5bb6\u4e70\u51e0\u53ea\u9e21\uff0c\u6211\u5df2\u7559\u4e0b\u836f\u65b9\uff0c\u5408\u836f\u7269\u7096\u6210\u6d53\u6c64\uff0c\u8ba9\u7530 \u5f53\u5c3d\u5feb\u6062\u590d\uff0c\u518d\u901a\u77e5\u5218\u98de\u9e4f\u548c\u51cc\u4e91\u4e24\u4f4d\u526f\u603b\u7edf\u9886\uff0c\u8981\u4ed6\u4eec\u6574\u88c5\u5f85\u547d\uff0c\u4e09\u65e5\u540e\u5f00\u59cb\u884c\u52a8\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u8eac\u8eab\u5e94\u547d\u3002\u4ed6\u5fc3\u4e2d\u5374\u6697\u5fd6\u9053\uff1a \u3000\u3000\u73b0\u5728\u6551\u7530\u5f53\u6027\u547d\u6700\u4e3a\u91cd\u8981\uff0c\u5979\u5374\u8bf4\u5f97\u5982\u6b64\u8f7b\u677e\uff0c\u5012\u8981\u770b\u4e2a\u660e\u767d\u4ed6\u5982\u4f55\u6574\u6cbb\u7530\u5f53\u7684\u4f24 \u52bf\u2026\u2026 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u601d\u5fd6\u7740\uff0c\u5373\u5306\u5306\u884c\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u7530\u5f53\u4f24\u5728\u540e\u80cc\uff0c\u4f0f\u5367\u5e8a\u4e0a\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u8986\u7740\u4e00\u6761\u68c9\u88ab\uff0c\u9f3e\u58f0\u5fae\u95fb\uff0c\u4f3c\u4e4e\u7761\u5f97\u6b63\u719f\u3002 \u3000\u3000\u8fd9\u5c31\u591f\u5b87\u6587\u6210\u90fd\u5927\u5403\u4e00\u60ca\u4e86\uff01\u7530\u5f53\u4f24\u5f97\u5341\u5206\u4e25\u91cd\uff0c\u600e\u4e48\u4f1a\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\u80fd\u591f\u6c89\u6c89\u7761\u53bb \u5462\uff1f \u3000\u3000\u63ed\u5f00\u68c9\u88ab\u4e00\u770b\uff0c\u53d1\u89c9\u7530\u5f53\u4e0a\u534a\u8eab\u8863\u670d\u5c3d\u53bb\uff0c\u96be\u602a\u8881\u7d2b\u70df\u4e0d\u8bb8\u6709\u4eba\u7559\u5728\u73b0\u573a\u3002\u8981\u4e00\u4e2a\u8d64\u88f8 \u4e0a\u8eab\u7684\u81e3\u5b50\u9762\u5bf9\u738b\u5983\uff0c\u5c31\u662f\u4e00\u6869\u5927\u9006\u4e0d\u9053\u7684\u7f6a\u884c\u4e86\u3002 \u3000\u3000\u4f46\u66f4\u8ba9\u5b87\u6587\u6210\u90fd\u60ca\u5f02\u7684\u662f\u90a3\u4e00\u9053\u65e2\u6df1\u53c8\u957f\u7684\u4f24\u53e3\uff0c\u6b64\u65f6\u5df2\u6210\u4e86\u4e00\u6761\u7ea2\u8272\u7684\u75a4\u75d5\u3002 \u3000\u3000\u8fd9\u5c31\u4e0d\u662f\u51ed\u85c9\u9ad8\u660e\u7684\u533b\u672f\u6240\u80fd\u4f5c\u5230\u4e86\uff0c\u4f24\u53e3\u4f3c\u662f\u88ab\u4e00\u79cd\u5f3a\u5927\u795e\u5947\u7684\u529b\u91cf\u7c98\u5408\u5728\u4e00\u8d77\uff0c\u5c31 \u8fd9\u6837\u7684\u6108\u5408\u4e86\u2026\u2026 \u3000\u3000\u4f3c\u662f\u7ecf\u5386\u4e86\u4e00\u573a\u68a6\uff0c\u5b87\u6587\u6210\u90fd\u6447\u6447\u5934\uff0c\u8ba9\u81ea\u5df1\u6e05\u9192\u4e00\u4e0b\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u6728\u6848\u4e0a\u653e\u7740\u836f\u65b9\u3002 \u3000\u3000\u518d\u65e0\u7591\u8651\u4e86\uff0c\u5b87\u6587\u6210\u90fd\u7acb\u523b\u9063\u4eba\u6293\u836f\u3002 \u3000\u3000\u968f\u4e5f\u6d3e\u4eba\u901a\u77e5\u51cc\u4e91\u3001\u5218\u98de\u9e4f\u6574\u88c5\u5f85\u547d\u3002 \u3000\u3000\u559d\u4e86\u4e24\u6b21\u836f\u7269\u7096\u6210\u7684\u9e21\u6c64\uff0c\u7530\u5f53\u4e00\u8dc3\u4e0b\u5e8a\u3002\u4f38\u5c55\u4e00\u4e0b\u53cc\u81c2\uff0c\u7adf\u4f3c\u5b8c\u5168\u590d\u539f\u4e86\uff0c\u524d\u540e\u53ea\u4e0d \u8fc7\u4e24\u65e5\u65f6\u95f4\u3002 \u3000\u3000\u201c\u660e\u5929\uff0c\u5c31\u662f\u5e94\u7ea6\u8d74\u4f1a\u4e4b\u671f\u3002\u201d\u5b87\u6587\u6210\u90fd\u6b63\u8272\u9053\uff1a\u201c\u4f60\u8981\u7559\u5728\u8fd9\u91cc\u4f11\u517b\u5462\uff1f\u8fd8\u662f\u5148\u56de\u957f \u5b89\u2026\u2026\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u518d\u4f11\u517b\u4e86\uff01\u201d\u7530\u5f53\u5fd9\u9053\uff1a\u201c\u5c5e\u4e0b\u5df2\u89c9\u5f97\u529f\u529b\u5c3d\u590d\uff0c\u660e\u65e5\u8d74\u7ea6\uff0c\u5f53\u53ef\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u52c9\u5f3a\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5200\u5251\u950b\u5229\u3001\u547d\u60ac\u77ac\u606f\uff0c\u4f53\u80fd\u672a\u590d\uff0c\u53ef\u662f\u767d\u767d\u9001\u6b7b\u7684 \u4e8b\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\u5168\u597d\u4e86\uff01\u201d\u7530\u5f53\u9053\uff1a\u201c\u5c5e\u4e0b\u65e9\u5df2\u8fd0\u6c14\u8bd5\u8fc7\uff0c\u771f\u6c14\u7545\u901a\uff0c\u5185\u8151\u65e0\u4f24\u3002\u9002\u624d\u4e0b\u5e8a\u4f38\u5c55 \u53cc\u81c2\uff0c\u808c\u8089\u4ea6\u65e0\u4f24\u75bc\u7684\u611f\u89c9\uff0c\u56fd\u5e08\u7597\u4f24\u65b9\u6cd5\u795e\u5947\u3002\u201d \u3000\u3000\u4e00\u62b1\u62f3\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u671b\u7740\u7530\u5f53\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u6cdb\u5347\u8d77\u8bb8\u591a\u611f\u6168\u3002 \u3000\u3000\u4e24\u65e5\u5185\uff0c\u4ed6\u91cd\u4f24\u7684\u8eab\u8eaf\u6062\u590d\u5982\u5e38\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u7530\u5f53\u7684\u5185\u5fc3\u4e4b\u4e2d\u5bf9\u8881\u7d2b\u70df\u751f\u51fa\u4e86\u65e0\u6bd4\u7684\u5d07\u656c\uff0c\u201d\u5927\u6709\u56fd\u5e08\u5728\u4fa7\uff0c\u5929\u4e0b\u65e0\u5904\u4e0d\u53ef\u53bb \u7684\u611f\u89c9\u3002 \u3000\u3000\u8427\u96e8\u7b11\u4e86\u7b11\uff0c\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u7530\u5f53\u5df2\u628a\u8881\u7d2b\u70df\u89c6\u82e5\u5929\u4eba\uff0c\u518d\u8bf4\u4e0b\u53bb\uff0c\u5c31\u96be\u514d\u5f15\u8d77\u4e89\u6267\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8427\u96e8\u8fd8\u65e0\u6cd5\u5bf9\u8881\u7d2b\u70df\u751f\u51fa\u7edd\u5bf9\u7684\u656c\u670d\u548c\u4fe1\u4efb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5374\u6084\u7136\u8dd1\u4e0a\u4e8c\u697c\u3002 \u3000\u3000\u5230\u5904\u67e5\u770b\uff0c\u4e8c\u697c\u666f\u89c2\u590d\u65e7\uff0c\u98df\u5ba2\u6ee1\u5750\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u6084\u7136\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u6ee1\u5ea7\u4e86\uff0c\u6211\u5e26\u4f60\u5230\u67dc\u4e0a\u627e\u4e2a\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u6562\u548c\u7559\u5b88\u4faf\u5e9c\u4f5c\u5bf9\u7684\u4eba\uff0c\u81ea\u7136\u662f\u5927\u6709\u6765\u5934\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5583\u5583\u5730\u9053\uff1a\u201c\u6050\u6015\u5df2\u7ecf\u4e22\u4e86\u2026\u2026\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u6210\u90fd\u9053\uff1a\u201c\u90a3\u7bee\u82b1\uff01\u201d\u201c\u5728\u5728\u5728\uff01\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a \u3000\u3000\u201c\u6211\u53bb\u53d6\u6765\uff01\u201d\u5b87\u6587\u6210\u90fd\u62c9\u4f4f\u4e86\u5e97\u5c0f\u4e8c\uff0c\u9053\uff1a\u201c\u4e0d\u7528\uff01\u5e26\u6211\u53bb\u770b\u770b\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u5e26\u7740\u5b87\u6587\u6210\u90fd\uff0c\u884c\u5165\u4e86\u5927\u638c\u67dc\u7684\u516c\u4e8b\u623f\u91cc\u3002 \u3000\u3000\u82b1\u5728\u6848\u4e0a\uff0c\u9c9c\u8273\u4f9d\u65e7\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4ed4\u7ec6\u7684\u67e5\u770b\u4e86\u4e00\u9635\uff0c\u8bc1\u5b9e\u4e01\u82b1\u662f\u771f\u82b1\uff0c\u4e5f\u6709\u7740\u82ac\u82b3\u7684\u82b1\u9999\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4e0d\u662f\u969c\u773c\u6cd5\u5440\uff01 \u3000\u3000\u5e97\u5c0f\u4e8c\u4f4e\u58f0\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u8981\u4e0d\u8981\u628a\u82b1\u7bee\u9001\u5230\u4f60\u7684\u4f4f\u623f\u4e2d\u53bb\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u770b\u770b\u5c31\u597d\uff0c\u770b\u770b\u5c31\u597d\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u5bf9\u8881\u7d2b\u70df\u7684\u656c\u670d\uff0c\u53c8\u52a0\u91cd\u4e86\u51e0\u5206\uff0c\u8fd9\u4e2a\u98ce\u59ff\u7edd\u4e16\u7684\u7f8e\u4eba\u7adf\u7136\u771f\u6709\u7740\u672f\u593a\u9020\u5316\u4e4b\u80fd\uff0c \u662f\u4f4d\u5b58\u5728\u4eba\u95f4\u7684\u4ed9\u5973\u5462\uff01 \u3000\u3000\u7b2c\u4e09\u5929\u4e2d\u5348\u8fc7\u540e\u2014\u2014 \u3000\u3000\u674e\u6df3\u98ce\u679c\u7136\u4eb2\u6765\u8fce\u63a5\u3002 \u3000\u3000\u5e97\u95e8\u5916\u6709\u8f66\u3001\u6709\u9a6c\u3002 \u3000\u3000\u8f66\u6709\u4e09\u8f86\uff0c\u9a6c\u5907\u5341\u5339\u3002 \u3000\u3000\u6562\u60c5\u65e9\u5df2\u628a\u8881\u7d2b\u70df\u7b49\u4e00\u884c\u7684\u4eba\u6570\u90fd\u7b97\u597d\u4e86\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u672c\u6765\u5404\u5e26\u4e09\u4eba\u5165\u592a\u539f\uff0c\u4f46\u9063\u8d70\u4e86\u4e24\u4eba\u53bb\u8fde\u7edc\u51cc\u4e91\u548c\u5218\u98de\u9e4f\uff0c\u5982\u4eca\u53ea\u5269\u4e0b\u5341\u4eba \u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9009\u62e9\u4e86\u4e00\u8f86\u7bf7\u8f66\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5148\u751f\uff0c\u53ef\u613f\u548c\u7d2b\u70df\u5171\u4e58\u4e00\u8f66\uff0c\u4e5f\u597d\u606d\u8046\u96c5\u6559\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u6562\u8bf7\u5c14\uff01\u8001\u673d\u4e5f\u6b63\u60f3\u5411\u59d1\u5a18\u8ba8\u7ffb\u6559\u76ca\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6325\u6325\u624b\uff0c\u5de7\u513f\u548c\u83b2\u513f\u767b\u4e0a\u4e86\u7b2c\u4e8c\u8f86\u7bf7\u8f66\u3002 \u3000\u3000\u9686\u51ac\u5bd2\u98ce\u5229\u5982\u5200\uff0c\u4e58\u8f66\u603b\u6bd4\u4e58\u9a6c\u597d\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u548c\u8427\u96e8\u3001\u7530\u5f53\u8f9e\u8c22\u4e86\u4faf\u5e9c\u7684\u8f66\u9a6c\uff0c\u4ed6\u4eec\u81ea\u6709\u5750\u9a91\u3002 \u3000\u3000\u7528\u5f53\u548c\u7a0b\u77e5\u8282\u5bf9\u671b\u4e00\u773c\uff0c\u5f7c\u6b64\u90fd\u611f\u60ca\u5f02\uff01\u4e24\u4e2a\u4eba\u90fd\u5df2\u7ecf\u5b8c\u597d\u5982\u521d\uff0c\u4ed6\u4eec\u90fd\u53d7\u4e86\u5f88\u91cd\u7684\u5200 \u65a7\u4e4b\u4f24\uff0c\u662f\u90a3\u79cd\u4e0d\u4f11\u517b\u4e2a\u5341\u5929\u534a\u4e2a\u6708\u65e0\u6cd5\u4e0b\u5e8a\u7684\u91cd\u4f24\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5374\u662f\u5168\u90fd\u590d\u539f\u4e86\u3002\u4e00\u53f6\u77e5\u79cb\uff0c\u53cc\u65b9\u9762\u679c\u7136\u90fd\u6709\u7740\u8d85\u8d8a\u4eba\u95f4\u533b\u5b66\u7684\u7597\u4f24\u795e\u6548\u3002 \u3000\u3000\u4ecd\u662f\u7a0b\u77e5\u8282\u548c\u90a3\u4e2a\u5e74\u8f7b\u4eba\u968f\u62a4\u800c\u6765\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u77aa\u7740\u7530\u5f53\u4e00\u773c\uff0c\u5927\u6709\u4ec7\u4eba\u89c1\u9762\u5206\u5916\u773c\u7ea2\u4e4b\u6982\u3002 \u3000\u3000\u90a3\u5e74\u8f7b\u4eba\u5374\u671b\u7740\u8427\u96e8\uff0c\u70b9\u5934\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53eb\u67f4\u7ecd\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u8427\u96e8\uff01\u201d \u3000\u3000\u201c\u8427\u5144\u53f7\u79f0\u98d8\u82b1\u5251\uff0c\u662f\u6c5f\u6e56\u4e2d\u77e5\u540d\u7684\u5251\u5ba2\u3002\u201d\u67f4\u7ecd\u9053\uff1a\u201c\u73b0\u5728\u662f\u5bab\u8859\u526f\u7edf\u9886\uff0c\u5f03\u4fa0\u5c31 \u5b98\uff0c\u524d\u9014\u65e0\u53ef\u9650\u91cf\u3002\u201d \u3000\u3000\u201c\u67f4\u5144\u9f99\u6f5c\u6df1\u6e0a\uff0c\u864e\u9690\u5c71\u5c97\uff0c\u6b63\u5728\u5f85\u673a\u800c\u51fa\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u770b\u6765\u2026\u2026\u5510\u516c\u7684\u5e55\u5e9c\u4e2d\u80fd\u4eba \u4e0d\u5c11\u3002\u201d \u3000\u3000\u67f4\u7ecd\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e71\u4e16\u82f1\u96c4\u5f53\u81ea\u60dc\uff01\u201d \u3000\u3000\u8427\u96e8\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e07\u90a6\u888d\u7b0f\u5949\u5929\u671d\u3002\u201d \u3000\u3000\u8bdd\u4e0d\u6295\u673a\u4e86\uff01 \u3000\u3000\u4f46\u53cc\u65b9\u90fd\u8fd8\u80fd\u8868\u73b0\u51fa\u4e92\u5fcd\u7684\u98ce\u5ea6\uff0c\u76f8\u89c6\u4e00\u7b11\uff0c\u4e0d\u518d\u4ea4\u8c08\u3002 \u3000\u3000\u7bf7\u8f66\u4e2d\u4e5f\u5728\u4ea4\u8c08\uff0c\u6e29\u8a00\u7b11\u8bed\u4e2d\u8ff8\u901e\u8ba5\u8bbd\u3002 \u3000\u3000\u201c\u5148\u751f\u7b49\u7684\u4eba\uff0c\u5df2\u7ecf\u5230\u4e86\u5417\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u53ef\u5426\u900f\u9732\u4e00\u4e8c\uff0c\u8ba9\u7d2b\u70df\u65e9\u6709\u4e2a\u5fc3\u7406\u51c6 \u5907\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u89c1\u9762\u5728\u5373\uff0c\u4f55\u4e0d\u5c0f\u5fcd\u534a\u65e5\uff1f\u4ee5\u59d1\u5a18\u4e4b\u80fd\uff0c\u81ea\u4f1a\u8bc4\u9274\u5b9a\u8bba\uff0c\u8001\u673d\u6b64\u65f6\u591a\u8a00\uff0c \u96be\u514d\u6709\u8c17\u8a00\u6e38\u8bf4\u4e4b\u5acc\u3002\u4e0d\u8fc7\uff0c\u59d1\u5a18\u8bf7\u653e\u5fc3\uff0c\u4ed6\u7edd\u4e0d\u662f\u8001\u673d\u8bf7\u6765\u5bf9\u4ed8\u59d1\u5a18\u7684\u9ad8\u4eba\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u53c8\u4f55\u59a8\uff1f\u59be\u665a\u6b64\u756a\u5317\u4e0a\uff0c\u5c31\u662f\u8981\u4f1a\u89c1\u9ad8\u4eba\uff0c\u665a\u89c1\u5c31\u4e0d\u5982\u65e9 \u89c1\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u59d1\u5a18\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u9053\u57fa\u3001\u672f\u6cd5\u90fd\u5f88\u6df1\u539a\u3002\u4e0d\u77e5\u4ee4\u5e08\u662f\u90a3\u4f4d\u4ed9\u957f\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5bb6\u5e08\u5df2\u98d8\u7136\u8fdc\u9690\uff0c\u73b0\u5728\u4f55\u5904\uff1f\u59be\u5a62\u4e5f\u4e0d\u77e5\u6653\uff0c\u8001\u4e08\u662f\u5426\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f53\u7136\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u4f60\u5e08\u7236\u5982\u5728\uff1f\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u51c6\u8bb8\u4f60\u8fd9\u4e48\u9006\u5929\u800c\u884c\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u4e08\uff0c\u59be\u665a\u60f3\u5750\u606f\u4e00\u4e0b\uff0c\u4eca\u65e5\u4f1a\u89c1\u8d35\u53cb\uff0c\u5982\u662f\u8bdd\u4e0d\u6295\u673a\uff0c\u4e5f\u8bb8 \u4f1a\u95f9\u51fa\u4e00\u573a\u98ce\u6ce2\uff01\u201d \u3000\u3000\u8f66\u9a6c\u6025\u9a70\uff0c\u52a0\u5feb\u4e86\u884c\u901f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u95ed\u76ee\u800c\u5750\uff0c\u771f\u7684\u8fd0\u529f\u8c03\u606f\u8d77\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6cbf\u9014\u7559\u4e0b\u4e86\u7ea6\u5b9a\u7684\u6697\u8bb0\uff0c\u6307\u793a\u7740\u968f\u540e\u8ffd\u8e2a\u800c\u6765\u7684\u5927\u6279\u4eba\u9a6c\u3002 \u3000\u3000\u9a6c\u8f66\u505c\u4e0b\uff0c\u5df2\u662f\u665a\u971e\u7eda\u70c2\u7684\u5915\u9633\u4e0b\u5c71\u65f6\u523b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u542f\u5e18\u4e0b\u8f66\uff0c\u53d1\u89c9\u662f\u4e00\u5904\u5f88\u7f8e\u7684\u6240\u5728\uff0c\u5de6\u4e34\u6c7e\u6cb3\u5cb8\uff0c\u53f3\u65c1\u5c0f\u77f3\u5c71\uff0c\u5c0f\u5c71\u4e2d\u677e\u6811\u6210 \u6797\uff0c\u4e00\u7247\u7fe0\u8272\u3002 \u3000\u3000\u5728\u9ec4\u571f\u9ad8\u539f\u4e0a\u5f88\u96be\u89c1\u5230\u7684\u72ec\u7279\u666f\u7269\u3002 \u3000\u3000\u66f4\u662f\u4e00\u7247\u5145\u6ee1\u7740\u7075\u79c0\u7684\u5730\u65b9\u3002 \u3000\u3000\u5c71\u4e0d\u8fc7\u6570\u4e08\u9ad8\u4f4e\uff0c\u4f46\u5374\u8d77\u4f0f\u6709\u81f4\u3002 \u3000\u3000\u5927\u4e0d\u8fc7\u4e09\u4e94\u767e\u4ea9\uff0c\u4f46\u5374\u6709\u5cf0\u58d1\u9f50\u5168\uff0c\u6f7a\u6f7a\u6d41\u6c34\u7ed5\u5c71\u8f6c\uff0c\u8305\u820d\u8d77\u4f0f\u5c71\u8272\u95f4\u3002 \u3000\u3000\u8fd9\u5730\u65b9\u7684\u5c0f\u5de7\u5e7d\u7f8e\uff0c\u5c31\u50cf\u662f\u7ecf\u8fc7\u5320\u5fc3\u8bbe\u8ba1\u7684\u4e00\u5ea7\u82b1\u56ed\uff0c\u518d\u52a0\u4e0a\u6c7e\u6cb3\u76f8\u886c\u773c\u5e95\u95f4\uff0c\u53c8\u5f97\u4e86 \u4e00\u79cd\u58ee\u4f1f\u76f8\u6258\u7684\u5947\u8da3\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6021\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5915\u9633\u65e0\u9650\u597d\uff0c\u53e4\u4eba\u4e0d\u6b3a\u6211\uff01\u201d\u5979\u5e38\u5c45\u7ec8\u5357\u5c71\u4e0a\uff0c\u8fd9\u7b49\u666f\u8272\u5012\u662f \u521d\u6b21\u89c1\u5230\u3002 \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5915\u9633\u867d\u7136\u597d\uff0c\u53ea\u662f\u8fd1\u9ec4\u660f\u3002\u6b64\u5c71\u867d\u5c0f\uff0c\u5374\u805a\u96c6\u4e86\u5c71\u53f3\u7075\u6c14\uff0c\u53ea\u662f\u8305 \u820d\u7af9\u7bf1\uff0c\u5c11\u4e00\u79cd\u957f\u5b89\u5bab\u5ead\u7684\u6c14\u6d3e\u3002\u8001\u673d\u4e3a\u59d1\u5a18\u5e26\u8def\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u65e9\u5df2\u5907\u597d\u9152\u7b75\uff0c\u6b63\u606d\u5019\u82b3 \u9a7e\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u7740\u9053\uff1a\u201c\u6df1\u5c71\u80b2\u4fca\u9e1f\uff0c\u8305\u820d\u51fa\u4f73\u4e3d\u3002\u975e\u5c71\u53f3\u7075\u6c14\u6240\u805a\uff0c\u5982\u4f55\u80fd\u5b55\u957f\u51fa\u8881\u5b9d\u513f \u90a3\u79cd\u4eba\u95f4\u7edd\u8272\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u5df2\u7ecf\u8ba4\u8bc6\u4e86\uff01\u597d\u82e6\u547d\u7684\u5b9d\u513f\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u8138\u4e0a\u6cdb\u8d77\u4e00\u80a1\u6012\u8272\uff0c\u9053\uff1a \u3000\u3000\u201c\u5b9d\u59b9\u59b9\u4e00\u8154\u6101\u611f\uff0c\u6ee1\u8179\u60b2\u4f24\uff0c\u4ee5\u76c8\u76c8\u5341\u4e94\u4e4b\u5e74\uff0c\u627f\u53d7\u4e86\u4eba\u95f4\u81f3\u82e6\u3002\u8ba9\u5bb3\u5979\u4f24\u5fc3\u5343\u56de\uff0c \u90a3\u4e2a\u4eba\u7f6a\u8be5\u4e07\u6b7b\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u6ca1\u6709\u7b54\u8bdd\uff0c\u52a0\u5feb\u811a\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5ff5\u5934\u767e\u8f6c\uff0c\u7adf\u662f\u60f3\u4e0d\u51fa\u4e00\u53e5\u9002\u5f53\u7684\u8bdd\uff0c\u6765\u4f5c\u56de\u7b54\u3002\u5bb3\u90a3\u4e2a\u5343\u5a07\u767e\u5a9a\u5c0f\u4f73\u4eba\u60c5\u5929\u62b1 \u6068\uff0c\u674e\u6df3\u98ce\u4e5f\u7b97\u662f\u5143\u51f6\u4e4b\u4e00\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u81ea\u4f5c\u4e2a\u80c6\u5927\u7684\u51b3\u5b9a\uff0c\u7559\u4e0b\u4e86\u56db\u4e2a\u4ece\u8859\uff0c\u5feb\u9a6c\u56de\u7a0b\u8fce\u63a5\u51cc\u4e91\u548c\u5218\u98de\u9e4f\u7387\u9886\u7684\u4e24\u767e \u94c1\u9a91\u3002 \u3000\u3000\u8981\u4ed6\u4eec\u5e03\u5b88\u5728\u5c0f\u5c71\u4e0b\u7684\u677e\u6797\u65c1\uff0c\u5f85\u547d\u884c\u52a8\uff0c\u672a\u5f97\u8b66\u8baf\u4e0d\u53ef\u64c5\u81ea\u95ef\u5165\u3002\u53ea\u5e26\u7740\u83b2\u3001\u5de7\u53cc \u5a62\u3001\u8427\u96e8\u3001\u7530\u5f53\uff0c\u4e94\u4e2a\u4eba\u8ffd\u968f\u7740\u8881\u7d2b\u70df\u8eab\u540e\u5165\u5c71\u3002 \u3000\u3000\u5f2f\u66f2\u5c0f\u5f84\u901a\u5e7d\u5904\uff0c\u4e00\u5e62\u8305\u820d\u5230\u773c\u524d\u3002 \u3000\u3000\u662f\u4e00\u5e62\u76f8\u5f53\u5927\u7684\u8305\u820d\uff01 \u3000\u3000\u7bf1\u95e8\u65e9\u5f00\uff0c\u4e00\u4e2a\u4e30\u795e\u4fca\u6717\u7684\u5e74\u8f7b\u4eba\u5f53\u95e8\u800c\u7acb\u3002 \u3000\u3000\u674e\u6df3\u98ce\u6a2a\u79fb\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u674e\u9756\uff0c\u89c1\u8fc7\u8881\u59d1\u5a18\uff01\u201d \u3000\u3000\u674e\u9756\u5fc3\u4e2d\u5fd6\u601d\u9053\uff1a\u597d\u7f8e\u7684\u59d1\u5a18\uff0c\u7075\u6c14\u4e0d\u8f93\u5f20\u51fa\u5c18\uff0c\u8273\u8272\u76f4\u903c\u8881\u5b9d\u513f\u3002 \u3000\u3000\u674e\u9756\u968f\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4e09\u5143\u674e\u9756\uff0c\u5019\u9a7e\u591a\u65f6\uff0c\u59d1\u5a18\u8bf7\uff01\u201d \u3000\u3000\u6b20\u8eab\u8ba9\u5ba2\u3002 \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u626b\u8fc7\u2026\u2026 \u3000\u3000\u5979\u5fc3\u5934\u5fae\u5fae\u9707\u52a8\uff0c\u6697\u9053\uff1a\u597d\u4e00\u4e2a\u4fca\u7f8e\u4eba\u7269\uff0c\u4ed9\u98ce\u9690\u9690\uff0c\u5168\u65e0\u4fd7\u6c14\uff0c\u771f\u662f\u4f4d\u5177\u6709\u9053\u6cd5\u7684 \u4eba\u3002\u5510\u516c\u674e\u6e0a\u4ece\u54ea\u513f\u5bfb\u5f97\u5982\u6b64\u4f17\u591a\u7684\u9ad8\u4eba\u52a9\u4ed6\uff0c\u7687\u4e0a\u7684\u6c5f\u5c71\u5f53\u771f\u662f\u98d8\u6447\u5728\u98ce\u96e8\u4e2d\u4e86\u3002 \u3000\u3000\u201c\u591a\u8c22\u674e\u516c\u5b50\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u6709\u5fd8\u8bb0\u4e86\u8c26\u865a\u793c\u8c8c\uff0c\u9053\u8fc7\u8c22\uff0c\u624d\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u5ead\u9662\u4e2d\u7adf\u7136\u6709\u82b1\uff0c\u800c\u4e14\u662f\u767e\u82b1\u4e89\u8273\u3002 \u3000\u3000\u95ee\u9898\u662f\u4e0d\u8be5\u6709\u7684\u5168\u6709\u4e86\uff0c\u4e0d\u8be5\u5f00\u7684\u5168\u5f00\u4e86\u3002 \u3000\u3000\u82b1\u8272\u6392\u5217\u6709\u81f4\uff0c\u5206\u660e\u662f\u521a\u521a\u683d\u79cd\u4e0d\u4e45\u3002 \u3000\u3000\u8881\u7d2b\u70df\u660e\u767d\u4e86\uff01 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u8b66\u544a\uff0c\u7af9\u7bee\u76db\u88c5\u56db\u5b63\u82b1\uff0c\u628a\u4e8c\u5341\u56db\u8282\u4ee4\u4f1a\u805a\u4e00\u5904\u7684\u4ed9\u6cd5\u9053\u672f\uff0c\u8fd9\u91cc\u4e5f\u80fd\u3002 \u3000\u3000\u8fd9\u5373\u6fc0\u8d77\u4e86\u8881\u59d1\u5a18\u5fc3\u5934\u7684\u6012\u706b\u3002 \u3000\u3000\u5979\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u53f8\u82b1\u4e4b\u795e\uff0c\u6709\u6127\u804c\u5b88\uff0c\u5f04\u4e71\u4e86\u6625\u3001\u590f\u3001\u79cb\u3001\u51ac\uff01\u201d \u3000\u3000\u4e3e\u624b\u4e00\u6325\uff0c\u767e\u82b1\u98d8\u96f6\uff0c\u6563\u843d\u4e00\u5730\u3002 \u3000\u3000\u201c\u597d\uff01\u5316\u4f5c\u6625\u6ce5\u4ea6\u62a4\u82b1\uff0c\u8001\u673d\u8881\u5929\u7f61\u89c1\u8fc7\u59d1\u5a18\uff01\u201d \u3000\u3000\u4e00\u4e2a\u8eab\u7740\u7f01\u8863\u3001\u767d\u7709\u767d\u9aef\u7684\u8001\u8005\u7f13\u6b65\u7531\u8305\u820d\u5927\u5385\u4e2d\u8fce\u4e86\u51fa\u6765\uff0c\u62b1\u62f3\u4f5c\u793c\u3002 \u3000\u3000\u201c\u7d2b\u70df\u5c11\u4e0d\u66f4\u4e8b\uff0c\u5c11\u4e86\u51e0\u5206\u6e29\u67d4\uff0c\u683d\u82b1\u8fce\u5ba2\u662f\u4f55\u7b49\u98ce\u96c5\u7684\u4e8b\u6211\u5374\u5927\u715e\u98ce\u666f\uff0c\u5f04\u5f97\u843d\u82b1\u98d8 \u96f6\uff0c\u5c31\u8ba9\u5b83\u4eec\u518d\u5f00\u4e00\u6b21\u5427\uff01\u201d \u3000\u3000\u4e00\u53cc\u7389\u624b\u5408\u4ec0\u5f53\u80f8\uff0c\u53e3\u4e2d\u5ff5\u52a8\u5492\u8bc0\uff0c\u53cc\u624b\u6325\u6563\u800c\u51fa\u3002 \u3000\u3000\u90aa\u6c14\u5440\uff01 \u3000\u3000\u98d8\u843d\u7684\u59f9\u7d2b\u5ae3\u7ea2\u4f3c\u88ab\u4e00\u9635\u5fae\u98ce\u5439\u8d77\uff0c\u91cd\u4e0a\u679d\u5934\uff0c\u5c3d\u590d\u65e7\u89c2\u3002 \u3000\u3000\u674e\u9756\u8138\u8272\u5927\u53d8\uff01\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u52a8\u5bb9\u3002 \u3000\u3000\u843d\u82b1\u5bb9\u6613\uff0c\u590d\u65e7\u96be\u5462\uff01 \u3000\u3000\u8881\u7d2b\u70df\u5374\u5ae3\u7136\u4e00\u7b11\uff0c\u76f4\u5411\u8305\u820d\u5385\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u5385\u4e2d\u5bf9\u6446\u7740\u4e24\u6761\u957f\u6848\uff0c\u4e3b\u5ba2\u5750\u4f4d\u5206\u660e\uff0c\u8881\u7d2b\u70df\u65e9\u5df2\u5728\u5ba2\u4f4d\u7684\u9996\u5e2d\u5750\u4e0b\uff0c\u793a\u610f\u5b87\u6587\u6210\u90fd\u5750 \u5728\u5979\u7684\u5de6\u9996\u3002 \u3000\u3000\u662f\u4f53\u8d34\uff0c\u4e5f\u662f\u547d\u4ee4\u3002 \u3000\u3000\u5426\u5219\uff0c\u5b87\u6587\u6210\u90fd\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u5904\u63aa\uff1f \u3000\u3000\u83b2\u513f\u3001\u5de7\u513f\u5750\u53f3\u4fa7\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u65c1\u7740\u5b87\u6587\u6210\u90fd\u5750\u3002 \u3000\u3000\u5ba2\u5df2\u5165\u4f4d\uff0c\u4e3b\u4eba\u4e5f\u7f13\u6b65\u5165\u5ea7\uff0c\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u3001\u674e\u9756\u3001\u67f4\u7ecd\u3001\u7a0b\u77e5\u8282\uff0c\u4f9d\u5e8f\u5c31\u4f4d\uff0c\u4f46\u5374 \u7a7a\u4e86\u4e2a\u4e3b\u4f4d\u6ca1\u4eba\u5750\u3002 \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u4e00\u63a0\u674e\u9756\uff0c\u6697\u9053\uff1a\u539f\u6765\u4ed6\u8fd8\u4e0d\u662f\u8bf7\u6765\u5bf9\u4ed8\u6211\u7684\u4eba\uff01 \u3000\u3000\u8881\u5929\u7f61\u770b\u4e86\u674e\u6df3\u98ce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u5ba2\u4eba\u4e45\u7b49\uff0c\u6211\u4eec\u4e0a\u83dc\u5427\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u4e00\u5411\u5b88\u65f6\uff0c\u8fd9\u4e00\u56de\u600e\u4f1a\u8bef\u4e86\u65f6\u95f4\uff1f\u201d \u3000\u3000\u674e\u9756\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u53bb\u63a5\u4ed6\u4e00\u7a0b\u3002\u201d \u3000\u3000\u4f3c\u662f\u5ffd\u7136\u60f3\u5230\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u67f4\u7ecd\u3001\u7a0b\u77e5\u8282\u63a5\u7740\u7ad9\u8d77\uff0c\u540c\u9053\uff1a\u201c\u6211\u966a\u674e\u516c\u5b50\u53bb\uff01\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u54cd\u4eae\u7684\u58f0\u97f3\u4f20\u8fdb\u5385\u4e2d\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u9014\u4e2d\u9047\u88ad\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u547d\u4ffa\u656c\u5fb7\u5148\u6765\u901a\u62a5 \u4e00\u58f0\uff0c\u4ed6\u968f\u540e\u5c31\u5230\u3002\u201d \u3000\u3000\u58f0\u5230\u4eba\u5230\uff0c\u9ed1\u9762\u4fee\u8eaf\u7684\u5c09\u8fdf\u606d\u50cf\u4e00\u80a1\u9ed1\u70df\u822c\u51b2\u5165\u5927\u5385\u4e2d\u3002 \u3000\u3000\u674e\u9756\u795e\u60c5\u51b7\u8083\u7684\u9053\uff1a\u201c\u4e16\u6c11\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u5c09\u8fdf\u606d\u5144\u4fdd\u9a7e\u6709\u8d23\uff0c\u600e\u53ef\u5f03\u4e3b\u5148\u8d70\uff1f\u201d \u3000\u3000\u201c\u4ffa\u8bf4\u4e0d\u6765\u7684\uff0c\u4f46\u4e8c\u516c\u5b50\u5ff5\u5ff5\u4e0d\u5fd8\u7ea6\u4f1a\u65f6\u95f4\uff0c\u4e00\u5b9a\u8981\u4ffa\u5148\u6765\u81f4\u6b49\uff0c\u656c\u5fb7\u4e0d\u6562\u8fdd\u547d\uff0c\u53ea\u597d \u5148\u6765\u4e00\u6b65\u3002\u201d \u3000\u3000\u201c\u5e78\u597d\u8fd8\u672a\u5f00\u5e2d\uff01\u201d \u3000\u3000\u79e6\u53d4\u5b9d\u80cc\u7740\u4e00\u4e2a\u5934\u53d1\u6563\u4e71\u8840\u67d3\u524d\u80f8\u7684\u5e74\u8f7b\u4eba\uff0c\u5feb\u6b65\u51b2\u5165\u5385\u4e2d\u3002 \u3000\u3000\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u3001\u674e\u9756\u7b49\u9f50\u9f50\u8fce\u4e86\u4e0a\u53bb\uff0c\u6276\u7740\u90a3\u8840\u67d3\u9ec4\u888d\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u79e6\u743c\u5927\u5598\u606f\u51e0\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u5fc3\u6025\u7ea6\u4f1a\uff0c\u53d7\u4f24\u540e\u653e\u6b65\u5954\u884c\uff0c\u81f4\u5931\u8840\u8fc7\u591a\u6655\u5012\u5728\u5730\uff0c \u53d4\u5b9d\u53ea\u597d\u80cc\u7740\u4ed6\u8d76\u6765\u3002\u201d \u3000\u3000\u7a0b\u77e5\u8282\u6012\u9053\uff1a\u201c\u79e6\u4e8c\u54e5\uff0c\u4f60\u597d\u7b28\u554a\uff01\u4e3a\u4ec0\u4e48\u4e0d\u5148\u66ff\u4e8c\u516c\u5b50\u5305\u624e\u8d77\u6765\uff0c\u653e\u7740\u9a6c\u4e0d\u9a91\uff0c\u80cc\u7740 \u4eba\u8d76\u8def\u2026\u2026\u201d \u3000\u3000\u201c\u9a6c\uff01\u201d\u79e6\u53d4\u5b9d\u82e6\u7b11\u7740\u9053\uff1a\u201c\u4e09\u5339\u9a6c\u90fd\u88ab\u523a\u5ba2\u6740\u6b7b\u4e86\u3002\u201d \u3000\u3000\u7a0b\u77e5\u8282\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u6211\u60f3\u79e6\u4e8c\u54e5\u4e5f\u4e0d\u4f1a\u8fd9\u4e48\u7b28\u554a\uff01\u201d \u3000\u3000\u987e\u4e0d\u5f97\u793c\u8c8c\u4e86\uff0c\u6551\u4eba\u8981\u7d27\uff0c\u674e\u9756\u6495\u5f00\u4e86\u8863\u888d\uff0c\u53ea\u89c1\u4e00\u9053\u4f24\u75d5\u659c\u659c\u7531\u524d\u80f8\u5212\u8fc7\uff0c\u9c9c\u8840\u4ecd\u7136 \u4e0d\u505c\u7684\u6d8c\u51fa\u6765\u3002 \u3000\u3000\u8881\u5929\u7f61\u5de6\u624b\u6276\u7740\u4f24\u8005\uff0c\u53f3\u638c\u5374\u7d27\u538b\u5728\u9ec4\u888d\u4eba\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u674e\u6df3\u98ce\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u5012\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u6295\u5165\u9ec4\u888d\u4eba\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u7ae5\u5b50\u5df2\u6367\u7740\u836f\u7269\u3001\u70ed\u6c34\uff0c\u884c\u4eba\u5385\u4e2d\u3002 \u3000\u3000\u674e\u9756\u4ee5\u767d\u5e03\u64e6\u53bb\u4f24\u53e3\u8840\u8ff9\u3002 \u3000\u3000\u674e\u6df3\u98ce\u6577\u836f\u5305\u624e\u3002 \u3000\u3000\u65b9\u5305\u597d\u4f24\u53e3\uff0c\u9ec4\u888d\u4eba\u4f3c\u662f\u8fd8\u672a\u5b8c\u5168\u6e05\u9192\u8fc7\u6765\uff0c\u53e3\u4e2d\u5374\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u674e\u4e16\u6c11\u6765\u665a\u4e86\uff0c\u6709 \u52b3\u4e45\u5019\uff0c\u62b1\u6b49\u62b1\u6b49\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u4e00\u76f4\u9759\u9759\u5730\u5750\u5728\u4f4d\u7f6e\u4e0a\uff0c\u51b7\u773c\u770b\u7740\u4e8b\u6001\u7684\u53d1\u5c55\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u523b\u5374\u4e0d\u80fd\u4e0d\u5f00\u53e3\u4e86\uff0c\u7f13\u7f13\u5730\u9053\uff1a\u201c\u4e8b\u51fa\u610f\u5916\uff0c\u602a\u4e0d\u5f97\u4f60\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u5374\u6697\u5fd6\u9053\uff1a\u4ed6\u4eba\u4e00\u82cf\u9192\uff0c\u53e3\u80fd\u51fa\u58f0\uff0c\u610f\u8bc6\u5c1a\u5728\u60f6\u5ffd\u4e2d\uff0c\u5c31\u5148\u81f4\u6b49\u610f\uff0c\u5fc3\u4e2d\u679c \u7136\u662f\u6446\u7740\u8fd9\u4ef6\u8d74\u7ea6\u7684\u4e8b\uff0c\u5e76\u4e0d\u662f\u80fd\u88c5\u4f5c\u5f97\u51fa\u6765\uff0c\u662f\u4e00\u4f4d\u5fc3\u53e3\u5982\u4e00\u7684\u541b\u5b50\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u6211\u6276\u4f60\u53bb\u623f\u5185\u4f11\u606f\u4e00\u4e0b\uff0c\u5ba2\u4eba\u7684\u4e8b\uff0c\u674e\u9756\u4f1a\u4ee3\u4e8c\u516c\u5b50\u5411\u5979\u8c22\u7f6a\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u663e\u7136\u6697\u4e2d\u628a\u529f\u529b\u8f93\u5165\u4e86\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u674e\u4e16\u6c11\u539f\u672c\u82cd\u767d\u7684\u8138\u8272\u5df2\u6cdb\u73b0\u51fa\u8840\u8272\uff0c\u7f13\u7f13\u7741\u5f00\u4e86\u773c\u775b\u9053\uff1a\u201c\u4e0d\u4e0d\u4e0d\uff01\u521d\u5ea6\u4f1a\u6664\uff0c\u5c31\u5931 \u4fe1\u4e8e\u4eba\uff0c\u4e16\u6c11\u5982\u4f55\u5b89\u5fc3\uff1f\u201d \u3000\u3000\u76ee\u5149\u659c\u52a8\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8881\u7d2b\u70df\u59d1\u5a18\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\u5728\u6b64\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u6323\u8131\u8881\u5929\u7f61\u548c\u674e\u9756\u7684\u53cc\u81c2\uff0c\u6447\u6447\u98a4\u98a4\u7684\u884c\u8fd1\u6728\u6848\uff0c\u957f\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u4e16\u6c11\u60ed\u6127\uff0c \u62d6\u7d2f\u59d1\u5a18\u4e45\u5019\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4f53\u80fd\u672a\u6062\u590d\uff0c\u957f\u63d6\u5927\u793c\u7275\u52a8\u4e86\u4f24\u53e3\uff0c\u7adf\u7136\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e00\u4e0b\u5b50\u8dea\u5012\u5728\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53f3\u624b\u4f38\u51fa\uff0c\u4e00\u80a1\u6697\u52b2\u6d8c\u4e86\u8fc7\u53bb\uff0c\u6258\u8d77\u4e86\u674e\u4e16\u6c11\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u7d2b\u70df\u6ca1\u6709\u602a\u4f60\u5440\uff01\u5148\u8bf7\u517b\u606f\u4f24\u52bf\uff0c\u6211\u53ef\u4ee5\u7b49\u4f60\u4e24\u5929\u3002\u201d \u3000\u3000\u201c\u4f24\u4e0d\u81f4\u547d\uff0c\u4e16\u6c11\u5949\u966a\u59d1\u5a18\u996e\u5b83\u51e0\u676f\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u76ee\u5149\u4e00\u63a0\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e24\u4f4d\u5148\u751f\uff0c\u6211\u4eec\u5165\u5ea7\u5427\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u5230\u674e\u9756\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u6765\u4e86\uff0c\u4e16\u6c11\u5fc3\u4e2d\u597d\u9ad8\u5174\u3002\u201d \u3000\u3000\u674e\u9756\u6276\u7740\u674e\u4e16\u6c11\uff0c\u884c\u5411\u5e2d\u4f4d\u3002 \u3000\u3000\u674e\u9756\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u771f\u80fd\u652f\u6491\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u53f3\u638c\u7d27\u63e1\u4e16\u6c11\u4e00\u53ea\u624b\uff0c\u4e00\u80a1\u70ed\u6d41\u4f20\u9012\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u662f\u674e\u9756\u82e6\u4fee\u7684\u771f\u5143\u4e4b\u6c14\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5f88\u5feb\u611f\u5e94\u5230\u8fd9\u80a1\u6df1\u539a\u7684\u60c5\u610f\uff0c\u4f24\u75bc\u51cf\u8f7b\u4e86\uff0c\u7cbe\u795e\u632f\u4f5c\u4e86\uff0c\u4f53\u80fd\u4e5f\u5927\u5927\u7684\u589e\u5f3a\u4e86\u4e0d \u5c11\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u8881\u5929\u7f61\u548c\u674e\u6df3\u98ce\u624d\u5b9a\u4e0b\u4e86\u5fc3\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u6b65\u5165\u5e2d\u4f4d\u3002 \u3000\u3000\u4ed6\u4eec\u53d1\u89c9\u4e86\u674e\u9756\u662f\u771f\u7684\u88ab\u4e16\u6c11\u90a3\u79cd\u6c11\u80de\u7269\u4e0e\u7684\u80f8\u895f\u5f81\u670d\uff0c\u751f\u51fa\u4e86\u8f85\u4f50\u4e4b\u5fc3\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6709\u70b9\u5931\u5e38\uff0c\u671b\u7740\u674e\u4e16\u6c11\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u53d1\u89c9\u4e86\uff0c\u4ed6\u5c31\u662f\u8881\u5b9d\u513f\u7684\u5fc3\u4e0a\u60c5\u90ce\uff0c\u4e71\u53d1\u62ab\u8138\uff0c\u8840\u6c61\u9ec4\u888d\uff0c\u82cd\u767d\u7684\u8138\u8272\uff0c\u6df7\u4e71\u7684\u573a\u9762\uff0c \u4f7f\u5f97\u8881\u7d2b\u70df\u758f\u5ffd\u4e86\u3002 \u3000\u3000\u5f85\u4ed6\u4e71\u53d1\u629a\u6574\uff0c\u7cbe\u795e\u7a0d\u590d\uff0c\u8881\u7d2b\u70df\u624d\u53d1\u89c9\u4e86\u56e0\u91cd\u4f24\u5931\u53bb\u7684\u4e00\u79cd\u795e\u97f5\uff0c\u5df2\u5728\u9690\u9690\u6d41\u73b0\u3002 \u3000\u3000\u6240\u6709\u4eba\u7684\u6ce8\u610f\u529b\u90fd\u6295\u6ce8\u5728\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\u65f6\uff0c\u5374\u542c\u5f97\u7830\u7136\u4e00\u58f0\u9707\u52a8\u3002 \u3000\u3000\u79e6\u53d4\u5b9d\u6454\u5012\u4e86\u2026\u2026 \u3000\u3000\u4f17\u76ee\u8f6c\u6ce8\uff0c\u624d\u53d1\u89c9\u79e6\u53d4\u5b9d\u7ad9\u7acb\u4e4b\u5904\u4e00\u5927\u6ee9\u9c9c\u8840\u8000\u76ee\u3002 \u3000\u3000\u4e00\u5219\u662f\u4ed6\u6709\u610f\u5730\u7528\u811a\u63a9\u906e\uff0c\u4e8c\u5219\u662f\u4ed6\u90a3\u80a1\u795e\u8272\u4e0d\u52a8\u7684\u6c14\u52bf\uff0c\u5b8c\u5168\u77a7\u4e0d\u51fa\u662f\u4e2a\u8eab\u53d7\u91cd\u4f24\u7684 \u4eba\u3002 \u3000\u3000\u201c\u53d4\u5b9d\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u7ad9\u8d77\u6765\uff0c\u8981\u5411\u524d\u6251\uff0c\u5374\u88ab\u674e\u9756\u4e00\u628a\u62d6\u4f4f\u3002 \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4f73\u5bbe\u5728\u5ea7\u5440\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u6447\u6447\u5934\uff0c\u6d41\u4e0b\u4e86\u4e24\u884c\u6e05\u6cea\uff0c\u9053\uff1a\u201c\u4ed6\u80cc\u7740\u6211\u5954\u8d70\u5341\u4f59\u91cc\uff0c\u6211\u7adf\u7136\u4e0d\u77e5\u9053\u4ed6\u4e5f\u53d7\u4e86 \u91cd\u4f24\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u7ad9\u8d77\u8eab\u5b50\u884c\u8fc7\u53bb\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u5df2\u62a2\u5148\u62b1\u8d77\u4e86\u79e6\u53d4\u5b9d\u7684\u8eab\u5b50\uff0c\u4e00\u628a\u6495\u5f00\u4e86\u8eab\u4e0a\u8863\u888d\uff0c \u3000\u3000\u5929\u554a\uff01 \u3000\u3000\u5de6\u808b\u4e0a\u4e00\u628a\u77ed\u5200\uff0c\u53ea\u9732\u51fa\u4e8c\u5bf8\u9aa8\u67c4\uff0c\u5200\u5203\u5168\u90fd\u63d2\u5165\u808b\u4e2d\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u75bc\u5f7b\u5fc3\u80ba\u7684\u5927\u4f24\uff01 \u3000\u3000\u79e6\u53d4\u5b9d\u4e0d\u662f\u4e0d\u75bc\uff0c\u800c\u662f\u5fcd\u4e0b\u4e0d\u53eb\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u795e\u60c5\u4e0d\u53d8\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e5f\u770b\u5f97\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u8fd9\u79cd\u8c6a\u58ee\u7684\u4e3e\u6b62\uff0c\u4ed6\u81ea\u89c9\u65e0\u6cd5\u6bd4\u62df\uff0c\u60f3\u4e0d\u4f69\u670d\u4e5f\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u624b\u63e1\u9aa8\u67c4\u8981\u62d4\u5200\uff0c\u5374\u8ba9\u674e\u6df3\u98ce\u4f38\u624b\u6321\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u6df3\u98ce\u4f4e\u58f0\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u5c09\u8fdf\u606d\u4e00\u624b\u63a2\u5728\u79e6\u743c\u7684\u9f3b\u606f\u95f4\uff0c\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u674e\u5148\u751f\uff0c\u53d4\u5b9d\u6c14\u7edd\u4e86\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ed6\u5fcd\u8010\u5f97\u592a\u4e45\uff0c\u8017\u5c3d\u4e86\u5143\u6c14\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u82f1\u96c4\u4eba\u7269\uff0c\u600e\u4f1a\u6709\u4e0d\u6d4b\u4e4b\u7978\uff1f\u201d\u8881\u7d2b\u70df\u4e3e\u6b65\u4e00\u8de8\uff0c\u4eba\u5df2\u5230\u7a0b\u77e5\u8282\u7684\u8eab\u524d\uff0c\u4f38\u51fa\u53f3 \u8155\u7389\u6307\uff0c\u8f7b\u8f7b\u4e00\u6309\u79e6\u743c\u7684\u9f3b\u606f\uff0c\u7b11\u9053\uff1a\u201c\u5c09\u8fdf\u5c06\u519b\uff0c\u79e6\u5c06\u519b\u6c14\u606f\u672a\u7edd\u5440\uff01\u201d \u3000\u3000\u5c09\u8fdf\u606d\u518d\u4f38\u51fa\u9ed1\u5927\u7684\u624b\u638c\u6309\u5728\u79e6\u743c\u7684\u9f3b\u606f\u4e4b\u95f4\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u771f\u7684\u6c14\u606f\u672a\u7edd\uff0c\u53ef\u771f\u662f\u5fd9\u4e2d\u6709\u9519\u554a\uff01\u201d \u3000\u3000\u201c\u4f60\u8fd9\u9ed1\u5927\u4e2a\u5b50\uff0c\u5413\u4e86\u54b1\u8001\u7a0b\u4e00\u8df3\uff01\u201d \u3000\u3000\u7a0b\u77e5\u8282\u62b1\u7740\u79e6\u53d4\u5b9d\u5927\u6b65\u5954\u5165\u5385\u540e\u3002 \u3000\u3000\u5c09\u8fdf\u656c\u5fb7\u4e00\u8f6c\u5934\uff0c\u7ad9\u5728\u8eab\u4fa7\u7684\u8870\u7d2b\u70df\u5ffd\u7136\u4e0d\u89c1\u4e86\uff0c\u8f6c\u5934\u518d\u770b\uff0c\u53ea\u89c1\u8881\u7d2b\u70df\u7aef\u5750\u5728\u5ba2\u4f4d\u9996 \u5e2d\u4e0a\uff0c\u597d\u50cf\u4e00\u76f4\u5c31\u672a\u66fe\u52a8\u8fc7\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\u9053\uff1a\u674e\u5148\u751f\u8bf4\u5979\u4e0d\u662f\u597d\u4e0e\u7684\u4eba\u7269\uff0c\u770b\u6765\u679c\u4e0d\u7b80\u5355\u3002\u54ea\u660e\u7ad9\u5728\u8eab\u8fb9\uff0c\u4e00\u7728\u773c\u5c31\u56de\u5230 \u4e86\u5750\u4f4d\u4e0a\uff0c\u771f\u662f\u4e2a\u5996\u5973\uff01 \u3000\u3000\u4ed6\u662f\u4e2a\u7c97\u8c6a\u4eba\u7269\uff0c\u8fd9\u672c\u662f\u5fc3\u4e2d\u60f3\u7684\u4e8b\u60c5\uff0c\u4f46\u5374\u561f\u561f\u56d4\u56b7\u5730\u8bf4\u4e86\u51fa\u6765\u3002\u867d\u662f\u58f0\u97f3\u6781\u5fae\uff0c\u8881 \u7d2b\u70df\u8fd8\u662f\u542c\u5230\u4e86\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u6ca1\u6709\u8ba1\u8f83\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5c3d\u91cf\u4f7f\u6fc0\u52a8\u7684\u60c5\u7eea\u5e73\u9759\u4e0b\u6765\uff0c\u4e3e\u8d77\u9152\u676f\uff0c\u9053\uff1a\u201c\u4e16\u6c11\u4e09\u751f\u6709\u5e78\uff0c\u4eca\u65e5\u5f97\u4f1a\u8881\u59d1 \u5a18\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4f11\u606f\u53bb\u5427\uff01\u8fd9\u91cc\u5c3d\u96c6\u4e86\u5c71\u53f3\u7075\u6c14\uff0c\u7d2b\u70df\u4e5f\u60f3\u5728\u6b64\u5730\u591a\u7559\u4e24\u65e5\uff0c\u4e0d \u4e3a\u81ea\u5df1\uff0c\u4e3a\u76ca\u53cb\uff0c\u7075\u836f\u4e5f\u8981\u4e00\u4e9b\u65f6\u95f4\u624d\u80fd\u968f\u8840\u6c14\u884c\u5f00\uff0c\u6b62\u4f4f\u4f24\u52bf\uff0c\u6709\u8bdd\uff0c\u6211\u4eec\u660e\u5929\u518d\u8c08\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u4e0d\u7981\u8138\u4e0a\u4e00\u70ed\uff0c\u4ed6\u786e\u5b9e\u5f3a\u5fcd\u7740\u4f24\u75bc\u786c\u6491\u3002 \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u8881\u59d1\u5a18\u8bf4\u5f97\u5bf9\uff0c\u4f60\u4f24\u5230\u5185\u8151\uff0c\u4e0d\u80fd\u559d\u9152\uff0c\u4e5f\u4e0d\u5b9c\u8fdb\u98df\u8364\u8165\u3002\u4f55\u51b5\uff0c\u8881\u59d1\u5a18\u53c8 \u7b54\u5e94\u591a\u7559\u4e24\u65e5\uff0c\u4e0d\u7528\u786c\u6491\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u4e16\u6c11\u544a\u9000\uff0c\u660e\u65e5\u518d\u4e3a\u7d2b\u70df\u59d1\u5a18\u63a5\u98ce\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u540e\u884c\u53bb\u3002 \u3000\u3000\u67f4\u7ecd\u548c\u5c09\u8fdf\u606d\u968f\u62a4\u8eab\u540e\uff0c\u9000\u51fa\u5927\u5385\u3002 \u3000\u3000\u8881\u5929\u7f61\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u771f\u662f\u4e00\u6b21\u6ce2\u6298\u591a\u53d8\u7684\u665a\u9910\uff0c\u4e0a\u83dc\u5427\uff01\u201d \u3000\u3000\u5929\u5df2\u5165\u591c\u4e86\uff0c\u5927\u5385\u4e2d\u65e9\u5df2\u70b9\u8d77\u706f\u706b\u3002 \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\uff0c\u662f\u4e0d\u662f\u6211\u4eec\u7684\u4eba\u9014\u4e2d\u622a\u6740\u4e8c\u516c\u5b50\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u672a\u5f97\u56fd\u5e08\u4e4b\u547d\uff0c\u8c01\u6562\u79c1\u4e0b\u51fa\u624b\uff1f\u201d \u3000\u3000\u89e3\u91ca\u4e86\u53ef\u80fd\u7684\u8bef\u4f1a\uff0c\u4e5f\u8868\u660e\u4e86\u56fd\u5e08\u7684\u8eab\u4efd\u3002 \u3000\u3000\u201c\u8881\u56fd\u5e08\u4e0d\u8981\u591a\u5fc3\uff01\u201d\u674e\u6df3\u98ce\u7f13\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u7edd\u4e0d\u6562\u6000\u7591\u5230\u59d1\u5a18\u5934\u4e0a\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\u4e0b\u7684\u624b\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4e0d\u662f\u5927\u519b\u963b\u6740\uff0c\u800c\u662f\u4e09\u4e24\u4e2a\u9ad8\u624b\u72d9\u51fb\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a \u3000\u3000\u201c\u80fd\u5728\u53d4\u5b9d\u3001\u656c\u5fb7\u7684\u97ad\u548c\u950f\u62a4\u8859\u4e4b\u4e0b\u4f24\u4e86\u4e8c\u516c\u5b50\uff0c\u5f53\u5f97\u662f\u7edd\u9876\u9ad8\u624b\u4e4b\u79f0\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u8138\u8272\u5fae\u53d8\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u56db\u4e2a\u9752\u8863\u7ae5\u5b50\u6367\u7740\u6728\u76d8\uff0c\u5949\u4e0a\u9152\u83dc\uff0c\u662f\u522b\u5f00\u751f\u9762\u7684\u5bb4\u4f1a\uff0c\u4eba\u5404\u4e00\u4efd\uff0c\u6446\u5728\u5404\u4eba\u7684\u9762\u524d\u3002 \u3000\u3000\u504f\u662f\u8881\u7d2b\u70df\u4e0d\u80af\u653e\u8fc7\uff0c\u76ee\u5149\u4e00\u63a0\u674e\u9756\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u9605\u5386\u4e30\u5bcc\uff0c\u60f3\u662f\u770b\u51fa\u4e00\u4e9b\u95e8\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u9690\u6709\u6240\u611f\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u53ea\u662f\u8bc1\u636e\u4e0d\u8db3\uff0c\u4e0d\u6562\u5984\u8a00\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8c08\u8c08\u4f55\u59a8\uff1f\u592a\u539f\u90e1\u4e2d\uff0c\u771f\u7684\u662f\u85cf\u9f99\u5367\u864e\uff1f\u201d \u3000\u3000\u674e\u9756\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6c5f\u6e56\u4e4b\u5927\uff0c\u5947\u4eba\u4f17\u591a\uff0c\u53c8\u4f55\u81f3\u592a\u539f\u4e00\u90e1\u5462\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u5148\u8bf7\u8fdb\u9910\uff0c\u5403\u8fc7\u996d\uff0c\u6311\u706f\u591c\u8c08\u3002\u4e8c\u516c\u4e8e\u9063\u6765\u4e86\u4faf\u5e9c\u4e2d\u4e24\u4f4d\u540d\u53a8\uff0c\u7279\u5730\u4e3a\u8881 \u56fd\u5e08\u51c6\u5907\u4e86\u5c71\u53f3\u4f73\u80b4\uff0c\u8001\u673d\u5148\u656c\u56fd\u5e08\u548c\u5b87\u6587\u5c06\u519b\u4e00\u676f\u3002\u201d \u3000\u3000\u65e2\u7136\u8bf4\u201c\u5403\u8fc7\u996d\uff0c\u6311\u706f\u591c\u8c08\u201d\uff0c\u4e3b\u4eba\u5373\u5df2\u8868\u660e\u4e86\u51c6\u5907\u63a5\u4e0b\u4efb\u4f55\u9ebb\u70e6\uff0c\u90a3\u5c31\u53ea\u5148\u5403\u996d\u4e86\u3002 \u3000\u3000\u53a8\u662f\u540d\u53a8\uff0c\u6599\u662f\u7cbe\u9009\uff0c\u70e7\u7684\u5473\u9053\u4e5f\u5f88\u597d\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u5fc3\u4e2d\u60f3\u7684\u662f\u6311\u706f\u591c\u8c08\uff0c\u5979\u5e0c\u671b\u80fd\u591f\u5728\u6311\u706f\u957f\u8c08\u4e2d\uff0c\u627e\u51fa\u4e00\u4e2a\u5904\u7406\u53cc\u65b9\u6b67\u89c1\u7684\u529e \u6cd5\u3002 \u3000\u3000\u8881\u5929\u7f61\u3001\u674e\u9756\u3001\u674e\u6df3\u98ce\u90fd\u662f\u4fee\u4e60\u9053\u6cd5\u7684\u4eba\u7269\uff0c\u975e\u540c\u5bfb\u5e38\u3002\u4f46\u660e\u663e\u7684\uff0c\u90fd\u5df2\u5bf9\u674e\u4e16\u6c11\u5fc3\u60a6 \u8bda\u670d\u3002 \u3000\u3000\u674e\u4e8c\u516c\u5b50\u8eab\u53d7\u91cd\u4f24\uff0c\u4ecd\u5ff5\u5ff5\u4e0d\u5fd8\u8d74\u4f1a\u4e4b\u7ea6\uff0c\u5145\u5206\u7684\u8868\u73b0\u51fa\u541b\u5b50\u91cd\u8bfa\u7684\u98ce\u8303\uff0c\u4f7f\u4eba\u5fc3\u6298\u3002 \u5c1a\u672a\u548c\u4ed6\u6df1\u8c08\uff0c\u5df2\u611f\u53d7\u4ed6\u9886\u8896\u7684\u9b45\u529b\uff0c\u900f\u53d1\u51fa\u738b\u8005\u7684\u6c14\u52bf\u3002\u4e3a\u53d4\u5b9d\u91cd\u4f24\u843d\u6cea\uff0c\u66f4\u5c3d\u73b0\u4ec1\u539a\u7684 \u80f8\u6000\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u4e5f\u90fd\u662f\u53ef\u656c\u7684\u82f1\u96c4\u4eba\u7269\uff0c\u4e5f\u5e26\u7740\u6d53\u539a\u7684\u7eaf\u6734\u6c14\u606f\uff0c\u7ed9\u4eba\u4e00\u79cd\u53ef\u4ee5\u4fe1\u8d56\u7684 \u611f\u89c9\u3002 \u3000\u3000\u8fd8\u6709\u90a3\u8eab\u5728\u957f\u5b89\uff0c\u5fc3\u7559\u592a\u539f\uff0c\u695a\u695a\u53ef\u4eba\u7684\u8881\u5b9d\u513f\uff0c\u4e3a\u60c5\u4ed8\u51fa\u4e86\u7f8e\u4e3d\u7684\u8eab\u4f53\uff0c\u5fcd\u53d7\u7740\u5343\u822c \u59d4\u5c48\u3002 \u3000\u3000\u62ac\u5934\u7b11\u989c\u52a8\u4eba\u601c\uff0c\u4f4e\u5934\u60c5\u6cea\u6e7f\u8863\u886b\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u5207\u90fd\u5728\u5f71\u54cd\u7740\u8881\u7d2b\u70df\uff0c\u4f7f\u5979\u5728\u4e0d\u505c\u7684\u6539\u53d8\u3002 \u3000\u3000\u4e89\u80dc\u4e4b\u5fc3\u6d88\u706d\u4e86\uff01 \u3000\u3000\u80f8\u6000\u7684\u6740\u673a\u6d88\u6563\u4e86\uff01 \u3000\u3000\u53ea\u5e0c\u671b\u80fd\u5728\u5f02\u4e2d\u6c42\u540c\uff0c\u514d\u53bb\u4e86\u4e00\u573a\u6740\u622e\u3002 \u3000\u3000\u8868\u9762\u770b\u53bb\uff0c\u8fd9\u9910\u996d\u5403\u5f97\u5f88\u5e73\u9759\uff0c\u6ca1\u6709\u4eba\u8bf4\u4e00\u53e5\u715e\u98ce\u666f\u7684\u8bdd\uff0c\u90fd\u5728\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u907f\u514d\u7834\u574f\u6b22 \u5bb4\u7684\u6c14\u6c1b\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u5927\u5bb6\u90fd\u5728\u60f3\u5fc3\u4e8b\uff0c\u6697\u4f5c\u4f30\u7b97\u3002 \u3000\u3000\u8881\u5929\u7f61\u65e0\u6cd5\u9884\u6d4b\u51fa\u8881\u7d2b\u70df\u5982\u4f55\u5904\u65ad\u4ed6\u5e72\u4e0e\u5929\u673a\u7684\u4e8b\uff0c\u4f46\u4ed6\u5df2\u89c1\u8bc6\u4e86\u8881\u7d2b\u70df\u7684\u7cbe\u5947\u672f\u6cd5\uff0c \u6709\u4e00\u79cd\u81ea\u53f9\u5f17\u5982\u4e4b\u611f\u3002 \u3000\u3000\u53ef\u662f\u52a0\u4e0a\u674e\u9756\u3001\u674e\u6df3\u98ce\uff0c\u81f3\u5c11\u4e5f\u8be5\u4fdd\u6709\u4e86\u4e03\u5206\u80dc\u7b97\uff0c\u8981\u4e0d\u8981\u4e09\u4eba\u5408\u529b\u4e00\u4e3e\u6b7c\u706d\u4e86\u8881\u7d2b\u70df \u5462\uff1f \u3000\u3000\u5355\u6253\u72ec\u6597\uff0c\u4e09\u4e2a\u4eba\u8c01\u4e5f\u4e0d\u662f\u5979\u7684\u654c\u624b\u3002 \u3000\u3000\u4eca\u665a\uff0c\u4e09\u4eba\u4f1a\u9f50\uff0c\u5e94\u8be5\u662f\u6700\u597d\u4e00\u4e2a\u6740\u5979\u7684\u673a\u4f1a\uff0c\u9519\u8fc7\u4eca\u665a\uff0c \u3000\u3000\u518d\u627e\u8fd9\u6837\u4e00\u4e2a\u5de7\u5408\u7684\u673a\u4f1a\u5c31\u975e\u5e38\u4e0d\u6613\u4e86\u3002 \u3000\u3000\u674e\u9756\u7684\u60f3\u6cd5\u5c31\u5b8c\u5168\u4e0d\u540c\u4e86\uff0c\u4ed6\u5728\u60f3\u996d\u540e\u7684\u6311\u706f\u591c\u8c08\u8be5\u4e0d\u8be5\u8bf4\u51fa\u674e\u4e16\u6c11\u9047\u523a\u4e8b\u60c5\uff0c\u4ed6\u8ba4\u8bc6 \u79e6\u743c\u808b\u91cc\u90a3\u628a\u5200\uff0c\u5f53\u7136\u5c31\u77e5\u9053\u4e86\u523a\u5ba2\u662f\u8c01\uff1f \u3000\u3000\u95ee\u9898\u662f\u8be5\u4e0d\u8be5\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u8bf4\u51fa\u6765\uff0c\u5bf9\u5927\u4e8b\u672a\u5fc5\u6709\u76ca\u3002 \u3000\u3000\u4e0d\u8bf4\uff0c\u53c8\u89c9\u5f97\u6127\u5bf9\u4e16\u6c11\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8881\u7d2b\u70df\u4f3c\u662f\u5df2\u7ecf\u77a7\u51fa\u4e86\u4ed6\u5fc3\u4e2d\u9690\u79d8\uff0c\u5982\u679c\u88ab\u5979\u6b65\u6b65\u903c\u8fdb\uff0c\u53ea\u6015\u662f\u5f88\u96be\u6709\u6240\u4fdd\u7559\u3002 \u3000\u3000\u9152\u83dc\u7528\u7f62\uff0c\u6362\u4e0a\u9999\u8317\u3002 \u3000\u3000\u4e24\u4e2a\u5949\u8336\u7ae5\u5b50\u5254\u4eae\u4e86\u56db\u76cf\u706f\u706b\uff0c\u5ba4\u5185\u66f4\u89c9\u660e\u4eae\u3002 \u3000\u3000\u8881\u7d2b\u70df\u559d\u53e3\u8336\uff0c\u76ee\u5149\u6295\u5411\u674e\u9756\uff0c\u9053\uff1a\u201c\u6311\u706f\u591c\u8c08\u7b2c\u4e00\u9898\uff0c\u7d2b\u70df\u5411\u674e\u9756\u5144\u8bf7\u6559\uff0c\u4ec0\u4e48\u4eba\u884c \u523a\u674e\u4e16\u6c11\uff1f\u6740\u5f97\u4ed6\u8840\u900f\u8863\u888d\uff0c\u79e6\u53d4\u5b9d\u808b\u91cc\u63d2\u5200\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u5fc3\u4e2d\u6697\u9053\uff1a \u3000\u3000\u201c\u7b2c\u4e00\u9898\uff0c\u5e94\u8be5\u662f\u5e72\u4e0e\u5929\u673a\u5f53\u4f55\u7f6a\u5440\uff1f\u600e\u4e48\u4f1a\u626f\u5230\u674e\u4e16\u6c11\u8eab\u4e0a\u53bb\uff0c\u4e8c\u516c\u5b50\u771f\u6709\u90a3\u4e48\u5927\u7684 \u9b45\u529b\u5440\uff01\u4e00\u89c1\u9762\u5c31\u8ba9\u4eba\u6446\u5728\u5fc3\u5934\u4e0a\uff01\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u7d2b\u70df\u59d1\u5a18\u5982\u6b64\u5173\u5fc3\u4e8c\u516c\u5b50\uff0c\u5728\u4e0b\u5f00\u5fc3\u5f97\u5f88\uff01\u201d \u3000\u3000\u201c\u5f53\u7136\u5173\u5fc3\uff01\u201d\u8881\u7d2b\u70df\u5a07\u7b11\u5982\u82b1\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u6211\u624b\u5e15\u59d0\u59b9\u7684\u597d\u670b\u53cb\uff0c\u4e0d\u4e5f\u662f\u4f60\u4eec\u8bf7\u6765\u7684 \u5bf9\u4ed8\u6211\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u56de\u7b54\u5f97\u65e2\u6709\u201c\u56db\u4e24\u62e8\u5343\u91d1\u201d\u7684\u5965\u5999\uff0c\u4e5f\u672a\u5b8c\u5168\u628a\u81ea\u5df1\u7f6e\u8eab\u4e8b\u5916\u3002 \u3000\u3000\u201c\u4f60\u7684\u670b\u53cb\u662f\u8c01\u5440\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u65e2\u79f0\u59d0\u59b9\u5e15\u4ea4\uff0c\u60f3\u662f\u60c5\u4e49\u6df1\u5982\u6d77\u4e86\uff01\u201d \u3000\u3000\u201c\u79c1\u4ea4\u60c5\u6df1\u4e0d\u5e9f\u516c\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7b2c\u4e00\u7f8e\u5973\u8881\u5b9d\u513f\uff0c\u60f3\u6765\u4f60\u4eec\u5f88\u719f\u4e86\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u5728\u4e0b\u662f\u4e45\u95fb\u5176\u540d\uff0c\u8fd9\u4e00\u6b21\u8881\u5927\u56fd\u5e08\u731c\u9519\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u8c08\u5b9d\u513f\uff0c\u4ec0\u4e48\u4eba\u6740\u4f24\u4e8c\u516c\u5b50\uff1f\u4f60\u65e9\u5df2\u5fc3\u77e5\u809a\u660e\u3002\u4e3a\u4ec0\u4e48\u4e0d\u613f\u8bf4\u51fa \u6765\uff1f\u662f\u5bb3\u6015\u62a5\u590d\uff1f\u8fd8\u662f\u522b\u6709\u6240\u56fe\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u5f97\u592a\u91cd\u4e86\uff01 \u3000\u3000\u674e\u9756\u53c8\u6025\u53c8\u6c14\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u8bc1\u636e\u4e0d\u8db3\uff0c\u8bf4\u4e86\u56fe\u4e71\u4eba\u610f\uff01\u201d \u3000\u3000\u201c\u8fd8\u662f\u8001\u8bcd\u554a\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u610f\u5374\u672a\u4e71\uff1f\u53ea\u662f\u542c\u5f97\u6709\u4e9b\u96be\u8010\u7f62\u4e86\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4e5f\u90fd\u611f\u5230\u5947\u602a\u4e86\uff01\u674e\u9756\u660e\u660e\u5df2\u731c\u5230\u4f55\u4eba\u884c\u523a\uff1f\u4e3a\u4ec0\u4e48\u5c31\u662f\u4e0d\u80af\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u56db\u9053\u76ee\u5149\u4e00\u9f50\u5411\u674e\u9756\u770b\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u56de\u5f52\u5165\u5e2d\u7684\u5c09\u8fdf\u606d\u4e00\u62cd\u684c\u5b50\uff0c\u9053\uff1a\u201c\u674e\u836f\u5e08\uff0c\u4e8c\u516c\u5b50\u5bf9\u4f60\u4f55\u7b49\u5668\u91cd\uff0c\u62ff\u4f60\u5f53\u624b\u8db3\u540c \u80de\u770b\u5f85\uff0c\u4f60\u5374\u8fde\u6740\u4ed6\u7684\u523a\u5ba2\u90fd\u4e0d\u80af\u8bf4\u51fa\u6765\uff0c\u8fd9\u54ea\u91cc\u8fd8\u6709\u4e00\u70b9\u670b\u53cb\u5473\u9053\uff1f\u201d \u3000\u3000\u201c\u79e6\u4e8c\u54e5\u808b\u4e2d\u63d2\u5200\uff0c\u53ef\u662f\u4f24\u4e2d\u8981\u5bb3\u5440\uff01\u201d\u7a0b\u77e5\u8282\u6b63\u7531\u5ba4\u5185\u8d70\u51fa\u6765\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u660f\u8ff7 \u672a\u9192\u554a\uff01\u8fd9\u4e2a\u4ec7\u5c82\u80fd\u4e0d\u62a5\uff1f\u674e\u9756\u554a\uff01\u65e2\u77e5\u523a\u5ba2\u662f\u8c01\uff0c\u4e3a\u5565\u4e0d\u80af\u8bf4\u51fa\u6765\uff0c\u4ffa\u8005\u7a0b\u7d20\u6765\u4f69\u670d\u4f60\uff0c \u8fd9\u4e00\u6b21\u4ffa\u53ef\u4e5f\u770b\u4e0d\u60ef\u4e86\uff01\u201d \u3000\u3000\u674e\u9756\u6ca1\u6709\u53d1\u706b\uff0c\u800c\u4e14\u8138\u8272\u5982\u5e38\uff0c\u6c14\u4e5f\u5e73\u4e86\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4e00\u4e24\u53e5\u8bdd\uff0c\u5c31\u628a\u6211\u63a8\u6210\u4f17\u77e2\u4e4b\u7684\uff0c\u5389\u5bb3\u5440\uff01\u5389\u5bb3\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5403\u60ca\u4e86\uff01 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u5fcd\u5c48\u8fb1\uff0c\u4f46\u4e8b\u60c5\u6269\u5927\u4e86\uff0c\u4ed6\u53c8\u80fd\u7a81\u7136\u7684\u9547\u9759\u4e0b\u6765\uff0c\u4ece\u5bb9\u4e0d\u8feb\u7684\u601d\u8651\u5e94\u5bf9\uff0c\u628a\u5927 \u4e8b\u5316\u5c0f\uff0c\u63a8\u5378\u91cd\u62c5\uff0c\u70b9\u7834\u5173\u8981\uff0c\u4ee5\u606f\u4f17\u6012\uff0c\u662f\u4e00\u4e2a\u5927\u5c06\u4e4b\u624d\u3002 \u3000\u3000\u5fc3\u5934\u8f6c\u5ff5\uff0c\u4eba\u5374\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8a00\u91cd\u4e86\u554a\uff01\u674e\u516c\u5b50\uff0c\u65e2\u77e5\u4f17\u6012\u96be\u72af\uff0c\u4f55\u4e0d\u8bf4\u4e2a\u660e\u767d \u5462\uff1f\u8a00\u6709\u6240\u672c\uff0c\u9519\u4e86\uff01\u4e5f\u6ca1\u4eba\u602a\u4f60\u5440\uff01 \u3000\u3000\u8d8a\u5957\u8d8a\u7d27\u4e86\uff0c\u4e0d\u8bf4\u5c31\u8bef\u4f1a\u66f4\u5927\u4e86\u3002 \u3000\u3000\u674e\u9756\u63a5\u9053\uff1a\u201c\u523a\u4f24\u4e86\u4e8c\u516c\u5b50\u548c\u53d4\u5b9d\u5144\u7684\uff0c\u53ef\u80fd\u662f\u9f99\u3001\u864e\u4e8c\u5c06\u6211\u8bf4\u7684\u53ea\u662f\u53ef\u80fd\uff0c\u56e0\u4e3a\u6211\u53ea \u8ba4\u51fa\u4e86\u90a3\u67c4\u77ed\u5200\uff0c\u725b\u89d2\u5200\u67c4\u4e0a\u96d5\u6709\u4e2a\u864e\u5934\uff0c\u53f7\u79f0\u864e\u5934\u5315\uff1b\u53ef\u4f5c\u5175\u5203\uff0c\u4e5f\u53ef\u4f5c\u98de\u5200\u4f24\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u4f1a\u9519\u4e86\uff0c\u4e00\u4e2a\u53c8\u9ed1\u53c8\u9ad8\u7684\u5927\u4e2a\u5b50\u548c\u6211\u6076\u6597\u6570\u5341\u56de\u5408\u3002\u201d\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u90a3\u4e2a\u5927\u9ed1 \u5c0f\u5b50\u7684\u8170\u4e2d\u5c31\u63d2\u4e86\u8fd9\u4e48\u56db\u67c4\u864e\u5934\u5315\u3002\u201d \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u5929\u4e0b\u4e00\u5bb6 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u56db \u56de\u3000\u7eb5\u9a91\u89c5\u60c5 \u3000\u3000\u5c09\u8fdf\u606d\u957f\u5f97\u65e2\u9ed1\u53c8\u9ad8\uff0c\u53e3\u4e2d\u5374\u8bf4\u522b\u4eba\u662f\u5927\u9ed1\u5c0f\u5b50\uff0c\u6d3b\u8131\u8131\u50cf\u662f\u5728\u9a82\u81ea\u5df1\u4e86\u3002\u4f46\u5927\u5bb6\u90fd\u5f3a \u884c\u5fcd\u4f4f\u4e86\uff0c\u6ca1\u6709\u4eba\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u201c\u656c\u5fb7\u5144\uff01\u201d\u674e\u9756\u795e\u60c5\u8083\u7136\u5730\u9053\uff1a\u201c\u4f60\u8981\u4ed4\u7ec6\u7684\u60f3\u60f3\uff0c\u90a3\u4e2a\u9ed1\u5927\u4e2a\u5b50\u8fd8\u6709\u4ec0\u4e48\u7279\u5f81\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u606d\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6709\u4e86\uff0c\u90a3\u4eba\u5de6\u988a\u4e0b\u9762\u6709\u4e00\u4e2a\u5927\u9ed1\u75d4\uff0c\u4e0a\u9762\u8fd8\u957f\u4e86\u4e00\u64ae\u6bdb\u3002\u201d \u3000\u3000\u201c\u864e\u5c06\u71d5\u53ef\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4ed6\u7528\u7684\u53ef\u662f\u4e00\u628a\u4e09\u5341\u4e8c\u65a4\u7684\u864e\u53c9\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6bd4\u8d77\u4e00\u822c\u7684\u864e\u53c9\u77ed\u4e86\u4e00\u4e9b\uff0c\u4f46\u5374\u5206\u91cf\u5f88\u91cd\uff0c\u6211\u4e00\u8fde\u4e09\u97ad\u51fb\u5728\u4ed6\u864e\u53c9\u4e4b\u4e0a\uff0c\u90fd\u672a \u80fd\u628a\u4ed6\u7684\u864e\u53c9\u9707\u8131\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u662f\u4ed6\u4eec\u4e86\uff1f\u201d\u674e\u9756\u53f9\u53e3\u6c14\uff0c\u8138\u4e0a\u6cdb\u73b0\u51fa\u5fe7\u82e6\u7684\u795e\u8272\u3002 \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u9f99\u3001\u864e\u4e8c\u5c06\u662f\u4f55\u65b9\u795e\u5723\uff1f\u5728\u53d4\u5b9d\u3001\u656c\u5fb7\u7684\u62a4\u536b\u4e4b\u4e0b\uff0c\u8fd8\u88ab\u4ed6\u4eec\u4f24\u4e86\u4e16\u6c11\uff0c \u662f\u4e0d\u53ef\u8f7b\u89c6\u7684\u654c\u4eba\uff01\u201d \u3000\u3000\u201c\u4e3b\u4eba\u5c31\u66f4\u53ef\u6015\u4e86\uff0c\u201d\u674e\u9756\u9053\uff1a\u201c\u9f99\u3001\u864e\u4e8c\u5c06\u53ea\u662f\u866c\u9aef\u5ba2\u7684\u56db\u5927\u62a4\u4ece\u4e2d\u7684\u4e8c\u4eba\uff0c\u866c\u9aef\u5ba2 \u89c4\u4ee4\u68ee\u4e25\uff0c\u672a\u5f97\u4ed6\u7684\u5141\u51c6\uff0c\u4e24\u4eba\u51b3\u4e0d\u6562\u64c5\u81ea\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u866c\u9aef\u5ba2\u6d3e\u4ed6\u4eec\u523a\u6740\u4e8c\u516c\u5b50\u4e86\uff1f\u201d\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5728\u54ea\u91cc\uff1f\u4ffa\u8001\u7a0b\u627e\u4ed6\u6597\u4e2a\u4e09 \u767e\u56de\u5408\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4ed6\u5982\u60f3\u6740\u4f60\uff0c\u4f60\u4e00\u56de\u5408\u4e5f\u63a5\u4e0d\u4e0b\u6765\u3002\u4e0d\u53ef\u8d38\u7136\u884c\u52a8\uff0c\u81ea\u6c42\u901f\u6b7b\u3002\u201d \u3000\u3000\u201c\u4e16\u4e0a\u771f\u6709\u5982\u6b64\u5389\u5bb3\u7684\u4eba\u7269\u5417\uff1f\u5996\u6cd5\u5947\u672f\uff0c\u8be1\u5947\u96be\u6d4b\uff0c\u4e0a\u4e0d\u4e86\u53f0\u76d8\u3002\u4ffa\u8001\u7a0b\u4e0d\u61c2\uff0c\u4f46\u5982 \u8bba\u771f\u5b9e\u7684\u672c\u9886\u3001\u6b66\u529f\uff0c\u4ffa\u8001\u7a0b\u6015\u8fc7\u8c01\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u597d\uff0c\u4e00\u53e5\u8bdd\uff0c\u628a\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u3001\u674e\u9756\u548c\u8881\u7d2b\u70df\u5168\u9a82\u4e0a\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u6447\u6447\u5934\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u4e0d\u4f1a\u672f\u6cd5\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u60e7\u672f\u6cd5\uff0c\u4ed6\u662f\u5251\u5ba2\uff0c\u98de\u884c\u7edd\u8ff9\u5feb\u5982\u95ea\u7535\u7684 \u5251\u5ba2\u3002\u77e5\u8282\u5144\u4e0d\u8981\u901e\u5f3a\uff0c\u5c31\u7b97\u4ed6\u4e0d\u7528\u5feb\u5251\uff0c\u4f46\u4ed6\u5929\u751f\u795e\u529b\u60ca\u4eba\uff0c\u529b\u62d4\u5c71\u516e\u6c14\u76d6\u4e16\uff0c\u5c31\u662f\u4ed6\u90a3 \u4e2a\u6837\u5b50\uff0c\u4f60\u6297\u62d2\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u90fd\u542c\u4ed6\u8bf4\u8fc7\u866c\u9aef\u5ba2\u7684\u4e8b\uff0c\u4e5f\u77e5\u9053\u4ed6\u548c\u866c\u9aef\u5ba2\u4e49\u7ed3\u91d1\u5170\u7684\u5173\u7cfb\uff0c\u4e24\u4eba\u9ed8\u7136 \u672a\u8bed\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u3001\u5c09\u8fdf\u606d\u7d20\u670d\u674e\u9756\uff0c\u770b\u4ed6\u8bf4\u5f97\u8ba4\u771f\uff0c\u4e5f\u672a\u63a5\u53e3\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u674e\u836f\u5e08\u8bf4\u4ed6\u98de\u884c\u7edd\u8ff9\uff0c\u60f3\u6765\u5e94\u8be5\u662f\u4f20\u8a00\u4e2d\u5fa1\u5251\u98de\u884c\u77ac\u606f\u5343 \u91cc\u7684\u5251\u4ed9\u4e86\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u4ed6\u5fa1\u5251\u98de\u884c\uff0c\u4e0d\u6562\u5984\u8a00\u3002\u4f46\u6211\u89c1\u8fc7\u4ed6\u6325\u5251\u4e00\u51fb\uff0c\u5288\u4e86\u7a7a \u4e2d\u4e00\u53ea\u98de\u9e70\uff0c\u7fbd\u6bdb\u96f6\u843d\uff0c\u8840\u8089\u4e00\u56e2\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u95ee\u9053\uff1a\u201c\u674e\u836f\u5e08\uff0c\u866c\u9aef\u5ba2\u4e3a\u4ec0\u4e48\u8981\u6740\u674e\u4e16\u6c11\u5462\uff1f\u4e00\u65e6\u4ed6\u884c\u5982\u6240\u613f\uff0c\u8bf8\u4f4d\u662f\u5426\u51c6\u5907 \u4e3a\u674e\u4e8c\u516c\u5b50\u62a5\u4ec7\u5462\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4e0d\u53ea\u662f\u95ee\u674e\u9756\u4e86\uff0c\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u4e5f\u90fd\u5305\u62ec\u5728\u5185\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u4e92\u76f8\u770b\u4e86\u4e00\u4e0b\uff0c\u672a\u56de\u7b54\u3002 \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u5fc3\u6709\u987e\u5fcc\uff0c\u4e0d\u4fbf\u5f00\u53e3\u3002 \u3000\u3000\u674e\u9756\u5012\u662f\u5f88\u8ba4\u771f\u7684\u5728\u60f3\u7740\u8fd9\u4ef6\u4e8b\uff0c\u8be5\u5982\u4f55\u56de\u7b54\uff1f \u3000\u3000\u201c\u8881\u59d1\u5a18\uff0c\u4f60\u8981\u542c\u771f\u8bdd\u5417\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u4e0d\u8fc7\u5fe0\u8a00\u9006\u8033\uff0c\u771f\u8bdd\u542c\u8d77\u6765\u5c31\u5c11\u4e86\u4e00\u4e9b\u4eba\u60c5\u5473 \u4e86\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\u53ea\u60f3\u542c\u542c\u674e\u836f\u5e08\u7684\u9ad8\u89c1\uff0c\u8bf4\u7684\u662f\u771f\u8a00\u5047\u8bdd\u5c31\u65e0\u5173\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u662f\u4ed6\u4eec\u63b3\u53bb\u4e16\u6c11\uff0c\u6211\u674e\u9756\u4f1a\u8054\u5408\u6df3\u98ce\u3001\u5929\u7f61\u4e24\u4f4d\u8001\u524d\u8f88\uff0c\u5168\u529b\u4ee5\u8d74\uff0c\u7eb5\u7136\u548c\u866c\u9aef\u5ba2 \u95f9\u5230\u5272\u888d\u65ad\u4e49\u3001\u7ffb\u8138\u7edd\u4ea4\uff0c\u4e5f\u8981\u628a\u4e16\u6c11\u6551\u51fa\u6765\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5982\u679c\u9f99\u3001\u864e\u4e8c\u5c06\u4e00\u4e3e\u4e4b\u4e0b\uff0c\u51fb\u6740\u4e86\u674e\u4e8c\u516c\u5b50\uff0c\u836f\u5e08\u662f\u4e0d\u51c6\u5907\u66ff\u4ed6\u62a5\u4ec7 \u4e86\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u5927\u4e08\u592b\u653e\u773c\u5929\u4e0b\uff0c\u5fd7\u5728\u4e07\u6c11\u82cd\u751f\uff0c\u5c82\u4f1a\u4e3a\u901d\u8005\u4f24\u75db\uff1f\u4e16\u6c11\u5728\uff0c\u6211\u613f\u4e3a\u4e16\u6c11 \u6b7b\uff1b\u56e0\u4e3a\u4ed6\u8eab\u7cfb\u4e86\u4e07\u6c11\u5e0c\u671b\u3002\u5982\u679c\u4ed6\u6b7b\u4e86\uff0c\u6211\u4e0d\u4f1a\u4e3a\u4ed6\u62a5\u4ec7\uff0c\u56e0\u4e3a\u866c\u9aef\u5ba2\u662f\u4e2a\u975e\u5e38\u53ef\u6015\u7684\u654c \u4eba\uff0c\u4e0d\u62ff\u6027\u547d\u4e00\u62da\uff0c\u662f\u65e0\u6cd5\u62a5\u4ec7\u7684\u3002\u4e3a\u6b7b\u4eba\u62da\u547d\uff0c\u975e\u543e\u4e4b\u613f\u4e5f\uff01\u201d \u3000\u3000\u5c09\u8fdf\u606d\u6012\u58f0\u53eb\u9053\uff1a\u201c\u674e\u836f\u5e08\uff0c\u4f60\u597d\u8584\u60c5\uff0c\u4e8c\u516c\u5b50\u4e00\u76f4\u628a\u4f60\u5f53\u597d\u670b\u53cb\u770b\u5f85\uff0c\u4eca\u65e5\u76fc\u4f60\u4e0d \u81f3\uff0c\u660e\u65e5\u671b\u4f60\u4e0d\u6765\uff0c\u65e5\u65e5\u4ee5\u4f60\u4e3a\u5ff5\uff0c\u5343\u671f\u4e07\u76fc\u7684\u597d\u4e0d\u5bb9\u6613\u628a\u4f60\u76fc\u5230\u4e86\uff0c\u4f60\u7adf\u662f\u4e00\u4e2a\u5982\u6b64\u65e0\u60c5 \u65e0\u4e49\u7684\u4eba\uff0c\u54b1\u656c\u5fb7\u597d\u4f24\u5fc3\u554a\uff01\u4e5f\u4e3a\u4e8c\u516c\u5b50\u8bc6\u4eba\u4e0d\u503c\uff01\u201d \u3000\u3000\u201c\u4f4f\u53e3\uff01\u201d\u674e\u9756\u5927\u58f0\u7684\u559d\u4f4f\u4e86\u5c09\u8fdf\u606d\uff0c\u9053\uff1b\u201c\u4e16\u6c11\u77e5\u6211\uff0c\u4e0d\u5e0c\u671b\u6211\u4e3a\u4ed6\u62a5\u4ec7\uff0c\u671f\u671b\u4e8e\u6211 \u80fd\u627f\u7ee7\u4ed6\u7684\u672a\u5b8c\u5fc3\u613f\u3002\u6211\u77e5\u4e16\u6c11\uff0c\u4e0d\u5fcd\u8ba9\u4ed6\u542b\u6068\u4e5d\u6cc9\uff0c\u4ee5\u4ed6\u4e2a\u4eba\u7684\u751f\u6b7b\u800c\u8bef\u4e86\u6551\u4e16\u5927\u4e1a\u3002 \u3000\u3000\u8881\u5b9d\u513f\u4eba\u95f4\u81f3\u7f8e\uff0c\u4e16\u4e0a\u7edd\u8272\uff0c\u674e\u4e16\u6c11\u90fd\u80fd\u820d\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u4ed6\u820d\u4e0d\u4e86\u7684\uff1f\u4ed6\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u662f \u8981\u5f00\u521b\u4e00\u4ee3\u592a\u5e73\u76db\u4e16\uff0c\u8ba9\u5929\u4e0b\u4e07\u6c11\u5b89\u5c45\u4e50\u4e1a\u3002\u4f60\u4eec\u8ffd\u968f\u4e16\u6c11\uff0c\u751f\u6b7b\u4e0d\u6e1d\uff0c\u56fa\u7136\u53ef\u656c\uff1b\u4f46\u8981\u4e86 \u89e3\u4ed6\u6c11\u80de\u7269\u4e0e\u7684\u80f8\u895f\uff0c\u624d\u662f\u4ed6\u771f\u6b63\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u5c09\u8fdf\u606d\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u674e\u836f\u5e08\uff0c\u54b1\u656c\u5fb7\u662f\u4e2a\u7c97\u4eba\uff0c\u54ea\u91cc\u80fd\u60f3\u5f97\u5982\u6b64\u900f \u5f7b\uff1f\u5f00\u7f6a\u4e4b\u5904\uff0c\u836f\u5e08\u4e0d\u53ef\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u674e\u9756\u633d\u8d77\u5c09\u8fdf\u606d\uff0c\u9053\uff1a\u201c\u5c06\u519b\uff0c\u4e0d\u53ef\u884c\u6b64\u5927\u793c\uff0c\u674e\u9756\u62c5\u5f53\u4e0d\u8d77\u3002\u201d \u3000\u3000\u201c\u674e\u4e16\u6c11\u771f\u6709\u5982\u6b64\u7684\u9886\u8896\u9b45\u529b\u5417\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8c26\u606d\u4e0b\u58eb\uff0c\u5fc3\u6000\u4e07\u6c11\uff0c\u90fd\u53ef\u4ee5\u88c5\u51fa\u6765 \u7684\u3002\u820d\u5f03\u4e86\u8881\u5b9d\u513f\u66f4\u662f\u4e00\u6869\u5f88\u5927\u7684\u7f6a\u6076\uff0c\u6b63\u5982\u4f60\u674e\u9756\u6240\u8a00\uff0c\u8fde\u5b9d\u513f\u90fd\u80fd\u820d\u5f03\u7684\u4eba\uff0c\u5929\u4e0b\u6709\u4ec0 \u4e48\u4ed6\u820d\u4e0d\u4e86\u7684\uff1f\u65e0\u60c5\u65e0\u4e49\uff0c\u8840\u51b7\u3001\u5fc3\u51c9\uff0c\u6211\u4e0d\u61c2\uff1f\u8fd9\u6837\u4e00\u4e2a\u4eba\u600e\u4e48\u4f1a\u53d7\u5230\u4f60\u4eec\u5982\u6b64\u7684\u62e5\u6234 \u5462\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4e16\u6c11\u8ba9\u4eba\u656c\u670d\u7684\uff0c\u662f\u4ed6\u7684\u59cb\u7ec8\u5982\u4e00\u7684\u6551\u6c11\u7cbe\u795e\u3002\u4f60\u53ef\u77e5\u591c\u6ce3\u76ee\u7726\u3001 \u5455\u8840\u8fd1\u6597\uff0c\u662f\u591a\u4e48\u6df1\u523b\u7684\u690e\u5fc3\u4e4b\u75db\u5417\uff1f\u5b9d\u513f\u96be\u820d\uff0c\u53ef\u662f\u4e07\u6c11\u82cd\u751f\u66f4\u96be\u820d\uff0c\u4ee5\u59d1\u5a18\u5927\u6167\u624d\u80fd\uff0c \u4f60\u53c8\u80fd\u4f5c\u4f55\u53d6\u820d\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u6447\u6447\u5934\u9053\uff1a\u201c\u8981\u7d2b\u70df\u4f5c\u53d6\u820d\uff0c\u6211\u4e0d\u4f1a\u80cc\u5f03\u5b9d\u513f\uff0c\u90a3\u662f\u4eba\u95f4\u7684\u5927\u60b2\u81f3\u82e6\u554a\uff01\u201d \u3000\u3000\u201c\u5929\u4e0b\u7684\u4e07\u6c11\u3001\u82cd\u751f\u5462\uff1f\u201d\u8881\u5929\u7f61\u795e\u60c5\u4e25\u8083\u5730\u95ee\u9053\uff1a\u201c\u4efb\u4ee4\u4ed6\u4eec\u6c38\u9694\u4e8e\u6c34\u6df1\u706b\u70ed\u4e4b\u4e2d\uff0c \u4e0d\u4f5c\u95fb\u95ee\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u627e\u5148\u751f\u7684\u91cd\u70b9\u4e86\uff0c\u5929\u6570\u52ab\u8fd0\uff0c\u672c\u4e8e\u81ea\u7136\uff1b\u4eba\u751f\u7684\u82e6\u3001\u96be\u3001\u6b22\u3001\u4e50 \u7686\u5bd3\u4e8e\u5b9a\u6570\u4e4b\u4e2d\uff0c\u5148\u751f\u64c5\u6539\u5929\u8fd0\uff0c\u4f7f\u4e00\u5207\u53d8\u5316\u5931\u4e86\u5e38\u6001\uff0c\u4eba\u95f4\u7684\u8fd0\u6570\u987a\u5e8f\u5168\u88ab\u4f60\u641e\u4e71\u4e86\u3002 \u3000\u3000\u53ea\u4e3a\u4e86\u8ba9\u5927\u968b\u671d\u6c14\u6570\u65e9\u5c3d\uff0c\u674e\u4e16\u6c11\u65e9\u767b\u4e0a\u4e5d\u4e94\u4e4b\u4f4d\u3002\u9700\u77e5\u5929\u4e0b\u80fd\u4eba\u4f17\u591a\uff0c\u4f60\u72c2\u5984\u81ea\u6043\uff0c \u5e72\u4e0e\u5929\u673a\uff0c\u5f53\u7136\u4f1a\u5f15\u8d77\u4ed6\u4eec\u5fc3\u4e2d\u4e4b\u6012\uff0c\u8fd9\u5c31\u662f\u6b32\u901f\u5219\u4e0d\u8fbe\u4e86\u3002 \u3000\u3000\u661f\u706b\u5982\u71ce\u539f\u800c\u8d77\uff0c\u8881\u5148\u751f\u81ea\u4fe1\u80fd\u63a7\u5236\u5f97\u4f4f\u5417\uff1f\u866c\u9aef\u5ba2\u9063\u4eba\u523a\u6740\u674e\u4e16\u6c11\uff0c\u5c31\u662f\u4e00\u4e2a\u4e25\u91cd\u7684 \u8b66\u544a\uff0c\u8fd9\u4ef6\u4e8b\u662f\u5426\u4e5f\u5728\u4f60\u9884\u4f30\u4e4b\u5185\u5462\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u5e72\u4e0e\u5929\u673a\u8fd0\u884c\uff0c\u5173\u7cfb\u91cd\u5927\uff0c\u8001\u673d\u548c\u674e\u6df3\u98ce\u7814\u8bae\u518d\u4e09\uff0c\u4f46\u6700\u5927\u7684\u5931\u7b97\u8fd8\u662f\u672a \u6599\u5230\u4f1a\u5f15\u8d77\u59d1\u5a18\u7684\u6012\u706b\uff0c\u7adf\u5c14\u7ea2\u989c\u4e00\u6012\u5165\u7ea2\u5c18\uff0c\u4f7f\u6211\u4eec\u4e71\u4e86\u7ae0\u6cd5\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9063\u4eba\u884c\u523a\uff0c\u786e\u5b9e\u672a\u5728\u9884\u4f30\u4e4b\u5185\uff0c\u4f46\u662f\u4e16\u6c11\u8fd1\u65e5\u6709\u6b64\u4e00\u52ab\uff0c\u5012\u662f\u5f97\u5230\u8fc7\u5929\u673a\u9884\u793a \u4e86\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u51b7\u5389\u7684\u76ee\u5149\u7f13\u7f13\u7531\u8881\u5929\u7f61\u3001\u674e\u9756\u3001\u674e\u6df3\u98ce\u4e09\u4eba\u7684\u8138\u4e0a\u626b\u8fc7\uff0c \u3000\u3000\u5979\u51b7\u51b7\u7684\u9053\uff1a\u201c\u5929\u673a\u7684\u8fd0\u884c\u52a0\u901f\u4e86\uff0c\u662f\u4e0d\u662f\u4e00\u5b9a\u4f1a\u5e94\u5728\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u5982\u679c\u65e0\u80fd\u4eba\u51fa\u624b\u5e72\u4e0e\uff0c\u8001\u673d\u8ba4\u4e3a\u5c31\u516b\u4e5d\u4e0d\u79bb\u5341\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u5949\u547d\u6d88\u706d\u738b\u6c14\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u674e\u4e16\u6c11\u65e2\u5df2\u88ab\u8bf8\u4f4d\u9009\u5b9a\u4e86\uff0c\u8fd9\u5c31\u662f\u4ed6\u7684\u4e0d\u5e78\u3002\u201d \u3000\u3000\u4e00\u53e5\u8bdd\uff0c\u5c31\u628a\u4e8b\u63a8\u5230\u4e86\u65e0\u53ef\u8f6c\u5706\u7684\u4f59\u5730\u3002\u5b87\u6587\u6210\u90fd\u3001\u7530\u5f53\u3001\u8427\u96e8\u3001\u5de7\u513f\u3001\u83b2\u513f\uff0c\u5168\u90fd\u7ad9 \u8d77\u4e86\u8eab\u5b50\uff0c\u4e5f\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u5239\u90a3\u95f4\u5c40\u9762\u4e00\u53d8\uff0c\u8981\u7ffb\u8138\u52a8\u624b\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5750\u7740\u672a\u52a8\u3002 \u3000\u3000\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u4e5f\u90fd\u8fd8\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u674e\u9756\u867d\u7136\u6709\u4e9b\u6fc0\u52a8\uff0c\u4f46\u8fd8\u80fd\u81ea\u6043\u7684\u5750\u7740\uff0c\u53ea\u4e0d\u8fc7\u5df2\u63d0\u805a\u771f\u6c14\uff0c\u76ee\u5149\u5982\u7535\uff0c\u4f3c\u662f\u5df2\u7ecf\u5b8c\u6210 \u4e86\u6253\u67b6\u7684\u51c6\u5907\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u5c31\u6ca1\u6709\u90a3\u4e48\u6587\u96c5\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f46\u5175\u5203\u51fa\u624b\uff0c\u800c\u4e14\u4f5c\u52bf\u6b32\u6251\uff0c\u6bd4\u8d77\u4e86\u5b87\u6587\u6210\u90fd\u7b49\u51e0\u4eba\uff0c\u66f4\u591a\u4e86\u4e00\u4efd\u6fc0\u6012\u548c\u6124\u6168\u3002 \u201c\u8881\u5148\u751f\uff0c\u4ed6\u4eec\u90fd\u662f\u864e\u5c06\u3001\u52c7\u58eb\uff0c\u4e00\u65e6\u52a8\u624b\u62da\u547d\uff0c\u5fc5\u5c06\u662f\u4e00\u4e2a\u8840\u67d3\u6218\u888d\u4e0d\u6b7b\u4e0d\u4f11\u7684\u5c40\u9762\u3002\u201d \u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f55\u4e0d\u8ba9\u60c5\u52bf\u6587\u96c5\u4e00\u70b9\uff0c\u5927\u5bb6\u4e00\u51b3\u80dc\u8d1f\uff0c\u53c8\u80fd\u4e0d\u5e26\u8840\u8165\u6c14\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u6709\u4f55\u9ad8\u89c1\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7531\u6211\u548c\u4e09\u4f4d\u6bd4\u8bd5\u4e09\u9635\uff0c\u5982\u662f\u6211\u80dc\u4e86\uff0c\u5c31\u8ba9\u6211\u628a\u4e16\u6c11\u5e26\u8d70\u3002\u5982\u679c\u6211\u8d25\u4e86\uff0c\u6211 \u5c31\u79bb\u5f00\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u4ece\u6b64\u4e0d\u518d\u8fc7\u95ee\u5929\u4e0b\u5927\u4e8b\uff01\u201d\u674e\u9756\u63a5\u9053\uff1a\u201c\u5982\u6b64\u5012\u4e5f\u516c\u5e73\uff0c\u59d1\u5a18\u53e3\u6c14\u867d\u662f\u72c2\u5984\u4e00\u4e9b\uff0c\u8fd8 \u7b97\u8bb2\u7406\u3002\u4f46\u662f\u5982\u80fd\u628a\u4e8b\u60c5\u518d\u8bf4\u5f97\u66f4\u660e\u6717\u4e00\u4e9b\uff0c\u5c31\u5168\u65e0\u9057\u61be\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u674e\u836f\u5e08\u662f\u8981\u6211\u8bf4\u660e\u767d\u662f\u4e09\u9635\u4e8c\u80dc\u8fd8\u662f\u6211\u8981\u4e09\u9635\u5168\u80dc\uff1f\u6211\u8bf4\u7684 \u662f\u4e09\u9635\u5168\u80dc\uff0c\u6211\u53ea\u8981\u8d25\u4e00\u9635\uff0c\u5c31\u7b97\u5168\u8f93\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u5979\u628a\u4e16\u6c11\u5e26\u8d70\uff01\u201d \u3000\u3000\u201c\u80dc\u4e86\u4e09\u9635\u4e5f\u4e0d\u884c\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u603b\u8be5\u6709\u4e2a\u529e\u6cd5\u80fd\u5e26\u4ed6\u5165\u957f\u5b89\u5427\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u8ba9\u6211\u4eec\u6240\u6709\u7684\u4eba\u5168\u90fd\u5931\u53bb\u62e6\u963b\u4f60\u7684\u80fd\u529b\uff0c\u4f60\u5927\u6982\u5c31\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u6b7b\u4ea1\u7684\u8b66\u544a\uff0c\u6311\u660e\u4e86\u4ee5\u547d\u62a4\u4e3b\uff0c\u4e0d\u6b7b\u4e0d\u4f11\u3002 \u3000\u3000\u201c\u65e0\u53ef\u5948\u4f55\u5440\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e00\u65e6\u5200\u5251\u51fa\u9798\uff0c\u603b\u5e26\u4e9b\u4f24\u4ea1\u7684\u6c14\u606f\uff0c\u6211\u662f\u738b\u547d\u5728\u8eab\uff0c\u4e0d \u80fd\u8ba9\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e16\u6c11\u4e3a\u5929\u547d\u6240\u5e94\uff0c\u4e07\u6c11\u7684\u798f\u7978\u6240\u7cfb\uff0c\u7edd\u4e0d\u80fd\u4efb\u4f60\u5e26\u8d70\u3002\u8881\u59d1\u5a18\uff0c\u8fd9\u5c31\u662f\u4eba\u751f \u7684\u60b2\u54c0\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u53cc\u624b\u5408\u5341\u5f53\u80f8\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u51b7\u8083\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u660e\u77e5\u51f6\u9669 \u53ef\u671f\uff0c\u5374\u53c8\u65e0\u6cd5\u9003\u907f\u5b83\u3002\u201d \u3000\u3000\u674e\u9756\u7684\u795e\u60c5\u4e0d\u50cf\u9762\u5bf9\u7740\u5373\u5c06\u66b4\u53d1\u7684\u4e00\u573a\u6076\u6218\uff0c\u5374\u50cf\u5728\u4f5c\u5e84\u4e25\u7684\u7948\u7977\u3002 \u3000\u3000\u8881\u7d2b\u70df\u770b\u6e05\u695a\u674e\u9756\u7684\u795e\u60c5\uff0c\u8138\u8272\u53d8\u4e86\u3002\u53f3\u624b\u4e3e\u8d77\uff0c\u98df\u3001\u4e2d\u4e8c\u6307\u634f\u5728\u4e00\u5904\uff0c\u4f3c\u662f\u5728\u6390\u8bc0\u3001 \u5ff5\u5492\u3002 \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u4f46\u7ad9\u5728\u5385\u4e2d\uff0c\u624b\u63e1\u5175\u5203\u7684\u5b87\u6587\u6210\u90fd\u3001\u7530\u5f53\u3001\u8427\u96e8\u5374\u662f\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u5927\u5371\u673a\u6b63\u5f00\u59cb\u7b3c\u7f69\u4e86 \u8305\u820d\u5927\u5385\u3002\u2019 \u3000\u3000\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u4e5f\u77a7\u4e0d\u51fa\u4ec0\u4e48\uff1f\u656c\u5fb7\u624b\u4e2d\u96cc\u96c4\u94a2\u97ad\u5df2\u7136\u4e3e\u8d77\uff0c\u7a0b\u77e5\u8282\u624b\u4e2d\u7684\u4e24\u67c4\u77ed\u65a7\u4e5f \u5df2\u84c4\u52bf\u5f85\u53d1\u3002 \u3000\u3000\u8881\u5929\u7f61\u5927\u58f0\u53eb\u9053\uff1a\u201c\u836f\u5e08\uff0c\u4e0d\u8981\u554a\uff01\u8fd9\u4ef6\u4e8b\u662f\u8001\u673d\u505a\u7684\uff0c\u5e72\u4e0e\u4e86\u5929\u673a\uff0c\u5982\u679c\u6709\u4efb\u4f55\u62a5\u5e94 \u4e5f\u8be5\u7531\u8001\u673d\u627f\u62c5\uff0c\u836f\u5e08\uff0c\u4f60\u662f\u5c40\u5916\u4eba\u554a\uff01\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u4e16\u6c11\u53ea\u8981\u8fd8\u6d3b\u7740\uff0c\u6211\u4f1a\u7528\u6027\u547d\u4fdd\u62a4\u4ed6\uff0c\u8881\u59d1\u5a18\u80fd\u4f7f\u7247\u7247\u843d\u82b1\u91cd\u4e0a\u679d \u5934\uff0c\u672f\u6cd5\u4e4b\u7cbe\u9ad8\u8fc7\u674e\u9756\u751a\u591a\uff0c\u5982\u4e0d\u4f5c\u540c\u5f52\u4e8e\u5c3d\u7684\u96f7\u9706\u4e00\u51fb\uff0c\u6211\u662f\u5168\u65e0\u80dc\u7b97\u3002\u4e24\u4f4d\u524d\u8f88\uff0c\u8bf7\u8f6c \u544a\u51fa\u5c18\u4e00\u58f0\uff0c\u660e\u6708\u6709\u5706\u5c31\u6709\u7f3a\uff0c\u4eba\u751f\u957f\u77ed\u5948\u82e5\u4f55\uff1f\u6211\u8981\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u660e\u767d\uff0c\u7559\u8a00\u8bc0\u522b\uff0c\u674e\u9756\u51c6\u5907\u6b7b\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\u554a\uff1f\u8fd8\u672a\u51fa\u624b\uff0c\u5c31\u5148\u4f5c\u6b7b\u4ea1\u7684\u51c6\u5907\u3002\u201d \u3000\u3000\u4ed6\u60f3\u4e0d\u901a\uff0c\u4e16\u4e0a\u600e\u4e48\u4f1a\u6709\u8fd9\u79cd\u6b66\u529f\uff01 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\u5b87\u6587\u6210\u90fd\u60f3\u4e0d\u901a\u4e86\uff0c\u4e16\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u79cd\u6b66\u529f\uff0c\u4f46\u5374\u6709\u8fd9\u79cd\u6cd5\u672f\uff0c\u7206\u8840\u788e\u9aa8\uff0c \u4e0e\u654c\u5055\u4ea1\u3002 \u3000\u3000\u674e\u6df3\u98ce\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u836f\u5e08\uff0c\u7a0d\u5b89\u52ff\u8e81\uff01\u7d2b\u70df\u59d1\u5a18\uff0c\u8bb0\u5f97\u4f60\u521a\u624d\u8bb8\u4e0b\u7684\u8bfa\u8a00\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u5728\u8fd9\u91cc\u7559\u4e24\u5929\uff0c\u7b49\u5019\u4e16\u6c11\u7684\u4f24\u52bf\u597d\u4e00\u4e9b\u4f60\u8981\u548c\u4ed6\u4f5c\u4e00\u6b21\u957f\u8c08\u3002\u201d \u3000\u3000\u201c\u5012\u662f\u4e0d\u9519\uff0c\u7d2b\u70df\u8bf4\u8fc7\u8fd9\u4e9b\u8bdd\u3002\u201d \u3000\u3000\u201c\u5982\u4eca\u4e16\u6c11\u4f24\u8fd8\u672a\u6108\uff0c\u59d1\u5a18\u5c31\u51c6\u5907\u64d2\u4ed6\u56de\u957f\u5b89\u3002\u201d \u3000\u3000\u201c\u738b\u547d\u5728\u8eab\uff0c\u4e8b\u96be\u5584\u4f11\uff0c\u674e\u5148\u751f\u8981\u591a\u591a\u539f\u8c05\u7d2b\u70df\uff01\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u6025\u5728\u4e00\u65f6\uff0c\u5c65\u7f62\u7ea6\u8a00\u518d\u6597\u6cd5\uff0c\u4e8b\u72b9\u672a\u8fdf\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7bad\u5728\u5f26\u4e0a\u554a\uff01\u836f\u5e08\u60c5\u6025\u62da\u547d\uff0c\u4e0d\u60dc\u7c89\u8eab\u788e\u9aa8\uff0c\u503e\u5de2\u4e4b\u4e0b\u65e0\u5b8c\u5375\uff0c\u6211\u4e0d\u80fd\u518d \u5750\u4ee5\u5f85\u6bd9\u554a\uff01\u201d \u3000\u3000\u201c\u59d1\u5a18\uff0c\u602a\u4e0d\u5f97\u836f\u5e08\u554a\uff01\u4f60\u8f7b\u8f7b\u4e00\u8a00\uff0c\u628a\u4e8b\u60c5\u903c\u5165\u4e86\u6b7b\u89d2\uff0c\u6fc0\u51fa\u8fd9\u4e00\u756a\u751f\u6b7b\u5bf9\u51b3\u3002\u59d1\u5a18 \u8fd8\u672a\u548c\u4e16\u6c11\u8bba\u53ca\u5929\u4e0b\u653f\u60c5\uff0c\u8fd9\u4e00\u6218\uff0c\u6253\u5f97\u592a\u8fc7\u5306\u5fd9\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u674e\u6df3\u98ce\u63a5\u7740\u9053\uff1a\u201c\u836f\u5e08\uff0c\u5929\u7f61\u8bf4\u7684\u4e0d\u9519\uff0c\u5e72\u4e0e\u5929\u673a\uff0c\u7eb5\u7136\u9519\uff0c\u4e5f\u8be5\u7531\u6211\u548c\u5929\u7f61\u627f\u62c5\u3002\u8881 \u59d1\u5a18\u867d\u7136\u6765\u52bf\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u4f46\u8001\u673d\u548c\u5929\u7f61\u4e5f\u65e9\u5df2\u4e0b\u5b9a\u4e86\u51b3\u5fc3\uff0c\u51b3\u4e0d\u8ba9\u5979\u8f7b\u6613\u5730\u5e26\u8d70\u4e16\u6c11\uff0c\u660e \u65e5\u8ba9\u5979\u548c\u4e16\u6c11\u8c08\u8fc7\u4e4b\u540e\uff0c\u518d\u884c\u5175\u6208\u76f8\u89c1\u3002\u836f\u5e08\uff0c\u6682\u8bf7\u4f4f\u624b\u5427\uff01\u201d \u3000\u3000\u201c\u6b7b\u6211\u674e\u9756\u4e00\u4eba\uff0c\u4e16\u6c11\u5c11\u4e00\u52b2\u654c\uff0c\u674e\u9756\u6b7b\u800c\u65e0\u61be\uff01\u518d\u8bf4\uff0c\u6709\u7d2b\u70df\u59d1\u5a18\u8fd9\u7b49\u4ed9\u8d44\u7389\u5bb9\u7684\u7f8e \u5973\u76f8\u966a\u4f34\uff0c\u6b7b\u540e\u4e5f\u53ef\u542b\u7b11\u5165\u4e5d\u6cc9\u4e86\uff01\u201d \u3000\u3000\u4ed6\u62e9\u5584\u56fa\u6267\uff0c\u5fc3\u4e2d\u6709\u4e00\u5957\u8861\u91cf\u4eba\u751f\u7684\u6807\u51c6\uff0c\u65e2\u73b0\u5b9e\u53c8\u5145\u6ee1\u81f3\u6b7b\u4e0d\u6e1d\u7684\u52c7\u6c14\u3002 \u3000\u3000\u201c\u843d\u82b1\u91cd\u590d\u679d\u5934\u4e0a\uff0c\u60ca\u96f7\u672a\u5fc5\u80fd\u5055\u4ea1\u3002\u6210\u90fd\uff0c\u4f60\u4eec\u5feb\u4e9b\u9000\u51fa\u5927\u5385\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u79c0\u76ee\u5706\u7741\uff0c\u5934\u9876\u4e0a\u5347\u8d77\u4e86\u4e09\u5c3a\u7075\u5149\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u8fd8\u662f\u65e0\u6cd5\u5b8c\u5168\u4e86\u89e3\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f \u3000\u3000\u4f46\u4ed6\u770b\u5230\u8881\u7d2b\u70df\u5934\u4e0a\u5347\u8d77\u7684\u7075\u5149\u6b63\u5feb\u901f\u6269\u5c55\uff0c\u628a\u6574\u4e2a\u4eba\u90fd\u7f6e\u4e8e\u7075\u5149\u7684\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u53ea\u597d\u4f9d \u8a00\u9000\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u4e5f\u7ad9\u8d77\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u201c\u836f\u5e08\u5144\uff0c\u4e0d\u53ef\u65e0\u793c\uff0c\u4e16\u6c11\u8fd8\u672a\u80fd\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u4e5f\u672a\u8046\u6559\u8881\u59d1\u5a18\u6cbb\u4e16\u7684\u6559\u76ca\uff0c\u5c82\u53ef\u6162 \u5f85\u4e86\u8d35\u5bbe\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u6276\u7740\u67f4\u7ecd\u7684\u80a9\u5934\uff0c\u7f13\u6b65\u800c\u51fa\u3002 \u3000\u3000\u674e\u4e16\u6c11\u53c8\u63a5\u7740\u9053\uff1a\u201c\u836f\u5e08\u5144\uff0c\u6210\u5168\u6211\u4e00\u756a\u5fc3\u613f\u5427\uff01\u201d \u3000\u3000\u674e\u9756\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7f62\u7f62\u7f62\uff01\u6b64\u756a\u6d88\u9664\u6211\u8c6a\u52c7\u4e4b\u6c14\uff0c\u518d\u89c1\u4e86\u51fa\u5c18\u4e4b\u9762\uff0c\u771f\u4e0d\u77e5\u6b64 \u540e\u662f\u5426\u8fd8\u80fd\u518d\u9f13\u8d77\u8fd9\u756a\u52c7\u6c14\u4e86\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u540c\u65f6\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u7ef7\u7d27\u7684\u4e00\u9897\u5fc3\u624d\u5b8c\u5168\u653e\u677e\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u662f\u5426\u80fd\u6740\u4e86\u8881\u7d2b\u70df\uff0c\u8fd8\u662f\u672a\u5b9a\u4e4b\u6570\u3002\u4f46\u662f\u674e\u9756\u662f\u6b7b\u5b9a\u4e86\uff0c\u5385\u4e2d\u7684\u4eba\u4e5f\u5f88\u96be\u9003\u8fc7\u52ab \u6570\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u4e5f\u6709\u4e9b\u9707\u52a8\uff0c\u56fa\u7136\u662f\u6ca1\u6709\u5341\u5206\u628a\u63e1\u80fd\u6321\u4f4f\u674e\u9756\u7684\u5055\u4ea1\u4e00\u51fb\uff0c\u56e0\u4e3a\u8fd9\u662f\u6cd5\u672f\u4e2d \u6700\u53ef\u6016\u7684\u4e00\u79cd\u653b\u52bf\u3002 \u3000\u3000\u65bd\u672f\u8005\uff0c\u8840\u8089\u8ff8\u5c04\uff0c\u4efb\u4f55\u672f\u6cd5\u90fd\u53ef\u80fd\u6682\u5931\u6548\u7528\uff0c\u9003\u8fc7\u90a3\u77f3\u7834\u5929\u60ca\u7684\u4e00\u51fb\uff0c\u5168\u8981\u51ed\u672c\u8eab\u7684 \u771f\u529f\u5b9e\u5b66\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u8f7b\u6613\u65bd\u7528\uff0c\u66f4\u6ca1\u4eba\u5728\u4e00\u5f00\u59cb\u5c31\u51c6\u5907\u4e0e\u654c\u5055\u4ea1\u3002 \u3000\u3000\u8881\u7d2b\u70df\u88ab\u674e\u9756\u51e0\u8fd1\u75af\u72c2\u7684\u52c7\u6c14\u9707\u52a8\u4e86\uff0c\u4e5f\u4e3a\u4ed6\u5fe0\u4e8e\u674e\u4e16\u6c11\u7684\u8c6a\u58ee\u4e4b\u6c14\uff0c\u7ed9\u593a\u8d70\u4e86\u5148\u58f0\u3002 \u3000\u3000\u5979\u5ae3\u7136\u4e00\u7b11\uff0c\u6325\u624b\u9053\uff1a\u201c\u60ca\u52a8\u4e8c\u516c\u5b50\u517b\u606f\u4f24\u52bf\uff0c\u662f\u7d2b\u70df\u7684\u4e0d\u656c\u4e86\u3002\u6765\u65e5\u65b9\u957f\uff0c\u4e8c\u516c\u5b50\u517b \u4f24\u8981\u7d27\uff0c\u7b49\u5f85\u4e8c\u516c\u5b50\u4f24\u52bf\u5927\u597d\uff0c\u6211\u4eec\u518d\u54c1\u8317\u8be6\u8c08\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\u6539\u53d8\u4e86\u2014\u2014\u6765\u5982\u6025\u98ce\u9aa4\u96e8\uff0c\u5929\u5730\u53d8\u8272\uff0c\u53bb\u5219\u4e4c\u4e91\u6d88\u6563\uff0c\u6674\u7a7a\u4e07\u91cc\u3002 \u3000\u3000\u201c\u597d\uff01\u7d2b\u70df\u59d1\u5a18\u8a00\u800c\u6709\u4fe1\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u76d8\u9f99\u5c45\u5df2\u4e3a\u59d1\u5a18\u51c6\u5907\u4e86\u96c5\u9759\u7684\u5ba2\u820d\uff0c\u65b0\u5f00\u7684 \u6e29\u6cc9\u6c90\u6c64\uff0c\u5c0f\u606f\u4e00\u4e24\u65e5\uff0c\u4e0d\u96be\u6da4\u9664\u5fc3\u4e2d\u70e6\u607c\uff0c\u8001\u673d\u4e3a\u59d1\u5a18\u5e26\u8def\u3002\u201d \u3000\u3000\u201c\u8fd9\u91cc\u53eb\u2018\u76d8\u9f99\u5c45\u2019\u554a\uff1f\u201d\u8881\u7d2b\u70df\u4e3e\u6b65\u968f\u884c\uff0c\u4e00\u9762\u4f4e\u58f0\u95ee\u9053\u3002 \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u674e\u6df3\u98ce\u63a5\u9053\uff1a\u201c\u9762\u5bf9\u6c7e\u6cb3\uff0c\u5730\u85cf\u70ed\u8336\uff0c\u96ea\u4e0d\u8986\u5730\uff0c\u6c34\u4e0d\u7ed3\u51b0\uff0c\u5bd2\u98ce\u81f3\u6b64\u800c \u4f4f\uff0c\u9ec4\u571f\u9ad8\u539f\u4e0a\u7684\u8349\u6811\u7686\u67af\u65f6\u8282\uff0c\u60df\u8fd9\u91cc\u9752\u5c71\u4e0d\u6539\uff0c\u5982\u65e0\u7ecf\u6c14\u76d8\u5c45\uff0c\u600e\u4f1a\u6709\u6b64\u5f02\u5f81\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u5440\uff01\u4f46\u522b\u5fd8\u4e86\u6211\u548c\u4e8c\u516c\u5b50\u4e00\u573a\u7ea6\u4f1a\uff0c\u4ed6\u7684\u4f24\u52bf\u597d\u8f6c\uff0c\u5c31 \u5c3d\u5feb\u901a\u77e5\u6211\u4f1a\u6664\u664b\u89c1\u3002\u6b64\u5730\u867d\u597d\uff0c\u5374\u975e\u6211\u8881\u7d2b\u70df\u4e45\u7559\u4e4b\u5904\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff01 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u662f\u5f88\u96be\u8bf4\u5f97\u52a8\u8fd9\u4f4d\u59d1\u5a18\u4e86\uff0c\u6bcf\u5230\u91cd\u8981\u7740\u529b\u4e4b\u5904\uff0c\u5c31\u628a\u8bdd\u9898\u626d\u8f6c\uff0c\u770b\u6765\u771f\u5f97\u4f5c \u6700\u574f\u7684\u51c6\u5907\u4e86\u3002 \u3000\u3000\u5ba2\u820d\u4e0d\u8fdc\uff0c\u5c31\u5728\u9752\u677e\u63a9\u6620\u95f4\uff0c\u4e24\u4e2a\u7ae5\u5b50\u8fce\u5ba2\u6765\u3002 \u3000\u3000\u674e\u6df3\u98ce\u7acb\u523b\u544a\u9000\u3002 \u3000\u3000\u5927\u5385\u4e2d\u706f\u706b\u4f9d\u65e7\u3002 \u3000\u3000\u674e\u9756\u548c\u8881\u5929\u7f61\u679c\u7136\u4ecd\u5728\u5385\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u201c\u5973\u4eba\u64c5\u53d8\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u559c\u6012\u4e5f\u5728\u77ac\u606f\u95f4\uff0c\u4e00\u53e5\u8bdd\u80fd\u628a\u4e8b\u60c5\u903c\u5165\u6b7b\u89d2\uff0c\u8981\u4f60\u4f5c\u751f\u6b7b\u4e4b \u6218\uff0c\u4e00\u8f6c\u773c\uff0c\u4e5f\u80fd\u591f\u7b11\u8bdd\u89e3\u51bb\uff0c\u628a\u4e00\u573a\u5927\u5e72\u6208\u5316\u4e8e\u65e0\u5f62\uff0c\u8fd9\u53ef\u771f\u662f\u53eb\u4eba\u65e0\u6cd5\u6349\u6478\u5440\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u5f20\u7f8e\u4eba\u4eea\u6001\u4e07\u5343\uff0c\u8bba\u59ff\u8272\u4e0d\u5728\u8881\u7d2b\u70df\u548c\u5b9d\u513f\u4e4b\u4e0b\uff0c\u836f\u5e08\u5e38\u65e5\u548c\u5979\u76f8\u5904\uff0c\u96be \u9053\u5bf9\u5973\u4eba\u8fd8\u662f\u4e00\u70b9\u4e5f\u4e0d\u4e86\u89e3\u5417\uff1f \u3000\u3000\u968b\u671d\u7684\u5929\u6570\u3001\u7075\u6c14\uff0c\u5168\u6295\u5728\u5973\u4eba\u8eab\u4e0a\u4e86\uff0c\u968b\u7080\u5e1d\u5bab\u5ef7\u591a\u5a07\uff0c\u8001\u673d\u4e5f\u4e3a\u4e4b\u773c\u82b1\u7f2d\u4e71\uff0c\u4f46\u5982 \u8a00\u82b1\u4e2d\u9b41\u9996\uff0c\u7d2b\u70df\u3001\u5b9d\u513f\u3001\u5f20\u51fa\u5c18\u53ef\u8c13\u82b1\u56fd\u4e09\u9f0e\u7532\u3002\u201d \u3000\u3000\u201c\u8bf4\u771f\u7684\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u8bc4\u8bba\u5230\u5f53\u4ee3\u4e09\u7f8e\uff0c\u51fa\u5c18\u51b7\u8273\u51a0\u7fa4\u82b3\uff0c\u5374\u8f93\u5b9d\u513f\u4e09\u5206\u5a9a\uff1b\u7d2b\u70df\u5e94 \u662f\u5929\u4e0a\u6765\uff0c\u7adf\u81ea\u6295\u8eab\u5165\u6c5f\u6c9f\uff0c\u968b\u7080\u5e1d\u6768\u5e7f\u4f27\u4fd7\u5339\u592b\uff0c\u600e\u4f1a\u5f97\u5176\u5782\u9752\uff1f\u4e0d\u901a\u554a\uff0c\u4e0d\u901a\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u836f\u5e08\uff0c\u8881\u7d2b\u70df\u5fd7\u5728\u6743\u52bf\uff0c\u6068\u8001\u673d\u5e72\u4e0e\u5929\u673a\u3002\u6295\u8eab\u5bab\u5ef7\uff0c\u662f\u8981\u7a83\u53d6\u6743\u67c4\uff0c\u968b \u7080\u5e1d\u597d\u8272\u5982\u547d\uff0c\u8881\u7d2b\u70df\u4e5f\u53ea\u597d\u4ece\u4fd7\u732e\u8eab\uff0c\u8c0b\u5f97\u4e86\u56fd\u5e08\u804c\u4f4d\uff0c\u51c6\u5907\u548c\u5929\u4e0b\u82f1\u96c4\u4e00\u8f83\u957f\u77ed\u3002\u201d \u3000\u3000\u201c\u8bdd\u8bf4\u5f97\u592a\u8fdc\u4e86\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u8881\u7d2b\u70df\u70ed\u8877\u6743\u52bf\uff0c\u6050\u96be\u4ee5\u529d\u5176\u964d\u670d\uff0c\u6211\u4eec\u8981\u4f5c\u6700\u574f\u7684 \u6253\u7b97\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u4e00\u5bf9\u4e00\uff0c\u8001\u673d\u81ea\u77e5\u4e0d\u662f\u5979\u7684\u654c\u624b\uff01\u4e09\u4eba\u56f4\u653b\uff0c\u53c8\u975e\u541b\u5b50\u884c\u5f84\u3002\u201d \u3000\u3000\u201c\u4e09\u4eba\u5408\u529b\uff0c\u662f\u4e0d\u662f\u4e00\u5b9a\u80fd\u8d62\u5462\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u5728\u4e0b\u6709\u4e00\u4e2a\u5f88\u5947\u602a\u7684\u611f\u89c9\uff0c\u5408\u54b1\u4eec\u4e09\u4eba \u4e4b\u529b\uff0c\u4e5f\u672a\u5fc5\u7a33\u64cd\u80dc\u7b97\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u5982\u679c\u6211\u4eec\u5148\u6709\u51c6\u5907\uff0c\u6446\u4e0b\u4e00\u5ea7\u9635\u52bf\uff0c\u80fd\u4e0d\u80fd\u628a\u5979\u56f0\u4f4f\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u9635\u6cd5\u5462\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u8ba9\u843d\u82b1\u91cd\u4e0a\u679d\u5934\uff0c\u56fa\u7136\u662f\u6709\u610f\u7684\u5356\u5f04\uff0c\u4f46\u5374\u4e5f\u662f\u7ed9\u6211\u4eec\u63d0 \u51fa\u4e86\u4e00\u4e2a\u8b66\u8baf\uff0c\u770b\u4f3c\u96d5\u866b\u5c0f\u6280\uff0c\u4f46\u674e\u67d0\u8fde\u756a\u8bd5\u8fc7\uff0c\u5747\u65e0\u6cd5\u8ba9\u5b83\u590d\u5143\u3002 \u3000\u3000\u674e\u67d0\u518d\u4e09\u601d\u7d22\uff0c\u7ec8\u4e8e\u4e86\u89e3\u5230\u8fd9\u662f\u672f\u6cd5\u7684\u4e00\u79cd\u5c42\u6b21\uff0c\u8881\u7d2b\u70df\u5f97\u5929\u72ec\u539a\uff0c\u5979\u9ad8\u4e86\u6211\u4eec\u2026\u2026 \u6211\u2026\u2026\u4e5f\u8bb8\u53ea\u9ad8\u8fc7\u674e\u9756\u4e00\u4e2a\u5c42\u6b21\u3002 \u3000\u3000\u672f\u6cd5\u6709\u767e\u53d8\uff0c\u53ef\u80fd\u674e\u9756\u53ea\u6709\u4e94\u5341\uff0c\u8881\u7d2b\u70df\u9ad8\u4e86\u4e00\u5c42\uff0c\u53c8\u4f55\u81f3\u591a\u4e86\u4e94\u5341\u53d8\u5462\uff1f\u4e00\u822c\u9635\u6cd5\uff0c \u6050\u65e0\u6cd5\u56f0\u5f97\u4f4f\u5979\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5355\u4ee5\u672f\u6cd5\u800c\u8bba\uff0c\u5c0f\u4e2b\u5934\u6050\u9ad8\u8fc7\u6211\u4eec\u4e00\u7b79\uff01\u8fd9\u53ef\u80fd\u56e0\u5e08\u627f\u6709\u522b\uff0c\u800c\u5f62 \u6210\u7684\u843d\u5dee\uff0c\u5c31\u5f88\u96be\u4ee5\u672c\u8eab\u7684\u4fee\u4e3a\u4f5c\u4e3a\u5f25\u8865\u4e86\u3002\u4e0d\u8fc7\uff0c\u9501\u9f99\u9635\u6cd5\u4e0d\u662f\u4e00\u822c\u7684\u9635\u6cd5\uff0c\u662f\u4e13\u4ee5\u5bf9\u4ed8 \u5177\u6709\u6cd5\u672f\u4eba\u7684\u4e00\u79cd\u9635\u6cd5\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201d\u4f60\u662f\u8bf4\u7f6e\u8eab\u9635\u4e2d\uff0c\u4e00\u5207\u7684\u672f\u6cd5\u5c31\u96be\u4ee5\u65bd\u5c55\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4f46\u82e6\u5fc3\u4fee\u70bc\u7684\u6b66\u529f\u5374\u662f\u5168\u65e0\u5f71\u54cd\u3002\u201d\u8881\u5929\u7f61\u9053\uff1a \u3000\u3000\u201c\u5927\u5bb6\u90fd\u662f\u8981\u4ee5\u672c\u8eab\u7684\u82e6\u7ec3\u6b66\u529f\uff0c\u4ed7\u5251\u4e00\u6218\uff0c\u674e\u9756\uff01\u771f\u6b63\u7684\u6b66\u529f\uff0c\u4f60\u662f\u5426\u53ef\u548c\u8881\u7d2b\u70df\u653e \u624b\u4e00\u640f\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u7528\u6015\u5979\u4e86\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u8881\u7d2b\u70df\u795e\u5145\u6c14\u8db3\uff0c\u5e94\u8be5\u662f\u4ece\u5c0f\u5c31\u5165\u9053\u7684\u624e\u5b9e\u4eba\u7269\u3002\u4e0d \u8fc7\uff0c\u8fd9\u65b9\u9762\u6211\u76f8\u4fe1\u53ef\u4ee5\u548c\u5979\u4e00\u51b3\u80dc\u8d1f\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u6709\u6211\u548c\u6df3\u98ce\uff0c\u4e3a\u4e86\u4e16\u6c11\u7684\u5927\u4e1a\uff0c\u4e0d\u7528\u548c\u5979\u8bb2\u4ec0\u4e48\u4ec1\u4e49\u9053\u5fb7\u3001\u6c5f\u6e56\u89c4\u77e9\u4e86\uff0c \u8fd9\u4e00\u6218\u6700\u597d\u80fd\u628a\u5979\u8bdb\u6740\u4e8e\u2018\u9501\u9f99\u9635\u2019\u4e2d\u3002\u201d \u3000\u3000\u201c\u5b87\u6587\u6210\u90fd\u662f\u4e00\u4f4d\u4e0d\u51e1\u9ad8\u624b\u3002\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4e5f\u4e0d\u5fc5\u5fc3\u5b58\u4ec1\u6148\u7684\u653e\u8fc7\u4ed6\uff0c\u6740\u6b64\u4e8c\u4eba\uff0c\u5927 \u968b\u671d\u7eb5\u8fd8\u6709\u80fd\u5f81\u60ef\u6218\u7684\u540d\u5c06\uff0c\u4f46\u968b\u7080\u5e1d\u4ea6\u96be\u4efb\u7528\uff0c\u4e16\u6c11\u7684\u5927\u4e1a\u5373\u6210\u5c31\u4e00\u534a\u4e86\u3002\u201d \u3000\u3000\u201c\u53e6\u4e00\u534a\u5462\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u867d\u662f\u95ee\u674e\u6df3\u98ce\uff0c\u4f46\u76ee\u5149\u5374\u770b\u5411\u674e\u9756\u3002 \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u8981\u770b\u674e\u836f\u5e08\u4e86\uff0c\u866c\u9aef\u5ba2\u5982\u679c\u9000\u51fa\u4e2d\u539f\u4e89\u9738\uff0c\u836f\u5e08\u53c8\u613f\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u592a\u539f\u8d77 \u5175\uff0c\u7531\u4e16\u6c11\u3001\u836f\u5e08\u5206\u8fdb\u5357\u5317\uff0c\u4e09\u5e74\u5185\u5929\u4e0b\u53ef\u5b9a\u3002\u201d \u3000\u3000\u674e\u9756\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u672a\u548c\u5f20\u51fa\u5c18\u548c\u866c\u9aef\u5ba2\u4f5c\u4e00\u756a\u6df1\u8c08\u4e4b\u524d\uff0c\u6211\u65e0\u6cd5\u4f5c\u4efb\u4f55\u51b3\u5b9a\u3002\u4f46 \u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u4e2a\u4eba\u7edd\u4e0d\u548c\u4e16\u6c11\u4e3a\u654c\u3002\u4e24\u4f4d\u524d\u8f88\u6682\u8bf7\u628a\u5fc3\u4e8b\u96c6\u4e2d\u5728\u5bf9\u4ed8\u8881\u7d2b\u70df\u8eab\u4e0a\uff0c\u8fc7\u4e86\u76ee\u524d \u8fd9\u4e00\u5173\u518d\u5546\u91cf\u866c\u9aef\u5ba2\u7684\u4e8b\u3002 \u3000\u3000\u665a\u8fdb\u60f3\u4e0d\u901a\u7684\u662f\u2018\u9501\u9f99\u9635\u2019\u600e\u4e48\u4f1a\u8ba9\u4e00\u4e2a\u8eab\u5177\u672f\u6cd5\u7684\u4eba\u65e0\u6cd5\u65bd\u5c55\uff1f\u8fd8\u671b\u4e24\u4f4d\u8001\u524d\u8f88\u6307\u70b9 \u4e00\u4e8c\uff0c\u4f7f\u674e\u9756\u4e00\u5f00\u8305\u585e\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u836f\u5e08\u77e5\u9053\uff0c\u4f1a\u96c6\u5929\u4e0b\u81f3\u79fd\uff0c\u80fd\u4f7f\u672f\u6cd5\u5931\u7075\u3002\u2018\u9501\u9f99\u9635\u2019\u53ea\u662f\u6563\u5e03\u4e0a\u4e00\u79cd\u836f\u7269\uff0c\u6682\u8ba9\u672f \u6cd5\u5931\u8d25\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53ea\u6709\u51ed\u85c9\u6b66\u529f\u548c\u6211\u4eec\u4e00\u51b3\u751f\u6b7b\u4e86\uff0c\u836f\u5e08\u5982\u81ea\u4fe1\u80fd\u51ed\u85c9\u6b66\u529f\uff0c\u80dc\u8fc7\u8881\u7d2b\u70df\uff0c\u5f53\u53ef\u53d6 \u5176\u6027\u547d\uff0c\u4e3a\u4e16\u6c11\u7acb\u4e0b\u7b2c\u4e00\u5927\u529f\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u9002\u624d\u5de1\u5b88\u7ae5\u5b50\u6765\u62a5\uff0c\u4e24\u767e\u94c1\u9a91\u5df2\u5230\u76d8\u9f99\u5c45\u5916\uff0c\u5b89\u4e0b\u8425\u5be8\u3002\u6570\u5341\u5ea7\u5e10\u7bf7\u8fc5\u901f \u642d\u8d77\uff0c\u5835\u4f4f\u4e86\u51fa\u5165\u8981\u9053\u3002\u201d \u3000\u3000\u674e\u9756\u5fd9\u9053\uff1a\u201c\u8881\u8001\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u9501\u9f99\u9635\u4e2d\u5982\u4e0d\u80fd\u4e00\u4e3e\u51fb\u6740\u8881\u7d2b\u70df\u548c\u5b87\u6587\u6210\u90fd\uff0c\u82e5\u662f\u8ba9\u8fd9\u6279\u4eba\u51b2\u6740\u5165\u8c37\uff0c\u5c31\u5f88\u96be\u62b5\u5fa1 \u4e86\uff01\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9501\u9f99\u9635\u4e2d\u4e00\u51fb\uff0c\u662f\u552f\u4e00\u51fb\u6740\u8881\u7d2b\u70df\u7684\u673a\u4f1a\u4e86\uff01\u820d\u5f03\u9501\u9f99\u4e4b\u672f\uff0c\u5408 \u6211\u4eec\u4e09\u4eba\u4e4b\u529b\uff0c\u662f\u5426\u53ef\u4ee5\u548c\u5979\u653e\u624b\u4e00\u62da\u672f\u6cd5\u5462\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u80dc\u7b97\u7684\u673a\u4f1a\u4e0d\u5927\uff0c\u8881\u7d2b\u70df\u53ef\u80fd\u5e08\u627f\u4e86\u7b2c\u4e00\u672f\u6cd5\u9ad8\u624b\u5343\u81c2\u89c2\u97f3\u7684\u8863\u94b5\uff0c\u90a3\u5c31 \u7edd\u975e\u6211\u4eec\u80fd\u654c\u4e86\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u6b64\u5973\u4e0d\u9664\uff0c\u4e16\u6c11\u7684\u57fa\u4e1a\u5f88\u96be\u6709\u6210\u529f\u4e4b\u671b\u3002\u6267\u5927\u4e49\u4e0d\u62d8\u5c0f\u8282\uff0c\u836f\u5e08\uff01\u9519\u8fc7\u4eca \u65e5\u5c31\u673a\u4f1a\u96be\u518d\u5440\uff01\u201d \u3000\u3000\u201c\u8881\u8001\u524d\u8f88\uff0c\u8c0b\u4e8b\u5728\u4eba\uff0c\u6210\u4e8b\u5728\u5929\u3002\u201d\u674e\u4e16\u6c11\u7f13\u6b65\u884c\u5165\u5385\u4e2d\uff0c\u9053\uff1a\u201c\u6697\u7b97\u8881\u59d1\u5a18\uff0c\u4e07\u4e07 \u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u679c\u7136\u662f\u80f8\u895f\u535a\u5927\uff0c\u6c14\u5ea6\u8fc7\u4eba\uff0c\u6211\u8fd9\u91cc\u5148\u8c22\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u51fa\u73b0\uff0c\u6b65\u5165\u5385\u4e2d\uff0c\u76ee\u5149\u63a0\u8fc7\u8881\u5929\u7f61\u548c\u674e\u6df3\u98ce\u3002 \u3000\u3000\u5979\u6de1\u6de1\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u9501\u9f99\u9635\u672a\u5fc5\u80fd\u9501\u5f97\u4f4f\u6211\uff0c\u4f46\u4e09\u4f4d\u5374\u5148\u628a\u81ea\u5df1\u903c\u5165\u4e86\u7edd\u5883\u3002\u201d\uff0c \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u8881\u59d1\u5a18\u7684\u672f\u6cd5\u662f\u9ad8\u660e\uff0c\u6f5c\u8fdb\u5927\u5385\uff0c\u6211\u7b49\u7adf\u65e0\u6240\u89c9\u3002\u8001\u592b\u65e9\u5df2\u5728\u5927\u5385\u4e4b\u5916\u5e03 \u4e0b\u57cb\u4f0f\uff0c\u7adf\u662f\u672a\u80fd\u963b\u6b62\u59d1\u5a18\uff0c\u4e5f\u672a\u80fd\u4f20\u5165\u8b66\u8baf\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u524d\u8f88\u7684\u5f79\u9e1f\u672f\u5341\u5206\u9ad8\u660e\uff0c\u5343\u53ea\u9ec4\u83ba\u3001\u91d1\u96c0\u4e5f\u90fd\u662f\u8b66\u89c9\u6027\u5f88\u9ad8\u7684\u9e1f\u513f\uff0c\u4f46\u524d \u8f88\u5374\u4e0d\u77e5\u665a\u8f88\u8fd9\u65b9\u9762\u4e5f\u6709\u6210\u5c31\u3002\u201d \u3000\u3000\u201c\u8881\u59d1\u5a18\u53ef\u662f\u628a\u5b83\u4eec\u5168\u90fd\u6740\u4e86\uff1f\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u65b9\u5706\u6570\u5341\u91cc\u7684\u83ba\u3001\u96c0\u5c3d\u96c6\u4e8e\u65af\uff0c\u4f60\u5982\u4e00 \u4e3e\u95f4\u628a\u5b83\u4eec\u5168\u90fd\u6740\u6b7b\uff0c\u6b64\u5730\u6570\u5e74\uff0c\u5f88\u96be\u518d\u542c\u5230\u83ba\u3001\u96c0\u4e4b\u58f0\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1b\u201c\u83ba\u3001\u96c0\u867d\u5c0f\uff0c\u4e5f\u662f\u4e00\u547d\u3002\u59d1\u5a18\u80fd\u4e00\u4e3e\u6740\u6b7b\u5343\u6570\u767e\u53ea\uff0c\u53ef\u771f\u662f\u8c8c\u5982\u6625\u82b1\uff0c\u5fc3\u4f3c \u86c7\u874e\uff01\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u6740\u5979\u4eec\uff0c\u53ea\u662f\u8ba9\u5b83\u4eec\u597d\u597d\u7761\u4e00\u4f1a\u513f\u3002\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u591c\u5bd2\u9732\u91cd\uff0c\u6211\u803d\u5fc3\u5b83\u4eec \u53ef\u80fd\u4f1a\u88ab\u591c\u6765\u7684\u5bd2\u971c\u51bb\u6b7b\uff0c\u4f46\u6b64\u5730\u6f5c\u5b58\u7684\u5730\u70ed\u5b8c\u5168\u6539\u53d8\u4e86\u8282\u4ee4\u6c14\u5019\uff0c\u6e29\u6696\u5982\u6625\uff0c\u4f1a\u8ba9\u4ed6\u4eec\u8fd9 \u4e00\u89c9\u7761\u5f97\u5f88\u597d\u3002\u660e\u65e5\u4e4b\u6668\uff0c\u6211\u4eec\u5c06\u542c\u5230\u83ba\u5531\u3001\u96c0\u9e23\uff0c\u5b83\u4eec\u4e2a\u4e2a\u7cbe\u795e\u9971\u6ee1\uff0c\u975e\u5f97\u5531\u3001\u9e23\u4e00\u9635\u624d \u4f1a\u6563\u53bb\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u6563\u53bb\uff1f\u53ea\u6015\u5b83\u4eec\u820d\u4e0d\u5f97\u6b64\u5730\u7684\u9752\u8349\u7fe0\u6811\u548c\u4e00\u7247\u6625\u8272\u5427\uff01\u201d\u2018 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u7528\u672f\u6cd5\u62d8\u7981\u4e86\u5b83\u4eec\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8ba9\u5b83\u4eec\u5728\u8fd9\u91cc\u56db\u5904\u98de\u7fd4\uff0c\u4f5c\u4e3a\u4f60\u7684\u8033 \u76ee\uff0c\u4f46\u6211\u8ba9\u5b83\u4eec\u5b89\u7761\u4e00\u9635\u4e4b\u540e\uff0c\u62d8\u7559\u5b83\u4eec\u7684\u672f\u6cd5\u81ea\u89e3\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u4e00\u7b11\uff0c\u76ee\u5149\u8f6c\u6ce8\u5230\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u7f13\u7f13\u5730\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff01\u4eca\u591c\u7684\u7cbe\u795e\u5982\u4f55\uff1f\u4f24\u52bf\u53ef\u597d\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4f24\u867d\u672a\u6108\uff0c\u4f46\u7cbe\u795e\u5c1a\u597d\u3002\u6211\u5df2\u8bf7\u4ed6\u4eec\u51c6\u5907\u4f73\u80b4\uff0c\u660e\u5929\u518d\u4e3a\u59d1\u5a18\u63a5\u98ce\u3002\u201d \u3000\u3000\u201c\u5fc3\u9886\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u738b\u547d\u5728\u8eab\uff0c\u4e0d\u5b9c\u4e45\u7559\uff0c\u4e8c\u516c\u5b50\u8eab\u53d7\u91cd\u4f24\uff0c\u7d2b\u70df\u662f\u65e0\u53ef\u5948\u4f55\uff01 \u53ea\u597d\u7559\u4e0b\uff0c\u65e2\u7136\u80fd\u591f\u652f\u6491\uff0c\u53ef\u4e0d\u4f5c\u4e00\u6b21\u79c9\u70db\u591c\u8bdd\uff0c\u8c08\u5f97\u6295\u673a\uff0c\u7d2b\u70df\u660e\u5929\u4e00\u65e9\u5c31\u53ef\u4ee5\u56de\u957f\u5b89 \u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u76ee\u5149\u4e00\u63a0\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u548c\u674e\u9756\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u597d\u56de\u907f\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u653e\u5fc3\uff0c\u53ef\u4ee5\u5728\u56db\u5916\u5e03\u4e0b\u9635\u52bf\u7b49\u6211\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u5728\u5385\u5916\u7b49\u5019\uff01\u201d\u674e\u6df3\u98ce\u5927\u8896\u4e00\u6325\uff0c\u5f53\u5148\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u4e09\u4eba\u79bb\u53bb\u4e4b\u540e\uff0c\u674e\u4e16\u6c11\u624d\u6b20\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u5750\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u897f\u5317\u738b\u6c14\u6240\u949f\uff0c\u5fc5\u51fa\u4e00\u4ee3\u541b\u4e3b\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\u4e86\uff01\u201d \u3000\u3000\u201c\u4eba\u95f4\u7684\u82e6\u96be\uff0c\u8fd8\u9700\u8981\u4eba\u53bb\u89e3\u51b3\uff1f\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e0a\u5929\u5782\u8c61\uff0c\u5e72\u4e0e\u5929\u673a\uff0c\u4f3c\u90fd\u5df2\u8d85\u8d8a\u4e86 \u4eba\u7684\u8303\u7574\uff0c\u6211\u7684\u5fc3\u613f\u662f\u53ea\u8981\u4e07\u6c11\u751f\u6d3b\u5f97\u5feb\u4e50\uff0c\u8c01\u5f53\u7687\u5e1d\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u6709\u4e09\u5927\u7f6a\uff0c\u4ef6\u4ef6\u90fd\u4e0d\u53ef\u8d66\uff0c\u6211\u8981\u5e26\u4f60\u56de\u957f\u5b89\uff0c\u542c\u5019\u7687\u5e1d\u7684 \u5904\u7f6e\uff0c\u738b\u6cd5\u68ee\u68ee\uff0c\u4f60\u4f1a\u88ab\u65a9\u9996\u5904\u51b3\u3002\u201d \u3000\u3000\u201c\u751f\u6b7b\u7684\u4e8b\uff0c\u975e\u6211\u6240\u91cd\uff0c\u4e0d\u53bb\u8bf4\u5b83\u4e5f\u7f62\u3002\u4f46\u4e16\u6c11\u8eab\u72af\u4e09\u5927\u7f6a\u5012\u662f\u4ee4\u6211\u60f6\u7136\uff0c\u8fd8\u671b\u8881\u59d1\u5a18 \u6307\u6559\u6307\u6559\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u7f13\u7f13\u5730\u9053\uff1a\u201c\u592a\u539f\u7559\u5b88\u4faf\u5e9c\u4e2d\uff0c\u805a\u96c6\u4e86\u6c5f\u6e56\u9ad8\u624b\u2026\u201d \u3000\u3000\u201c\u59d1\u5a18\uff01\u516c\u4faf\u5e9c\u7b2c\u62e5\u6709\u5bb6\u5c06\u4eb2\u5175\uff0c\u662f\u7687\u4e0a\u6240\u5141\uff0c\u4e0d\u80fd\u7b97\u4f5c\u8fdd\u6cd5\uff0c\u5c82\u53ef\u5b9a\u4e3a\u5927\u4e0d\u8d66\u7684\u7f6a \u72b6\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e91\u4e2d\u5c71\u5185\uff0c\u6697\u7ec3\u7532\u5175\uff0c\u4e5f\u662f\u7687\u4e0a\u5141\u51c6\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5012\u662f\u4e00\u4ef6\u4e0d\u8d66\u4e4b\u7f6a\uff0c\u8bf7\u95ee\u4e16\u6c11\u72af\u7684\u7b2c\u4e8c\u4ef6\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u65e0\u60c5\u65e0\u4e49\uff0c\u629b\u5f03\u4e86\u8881\u5b9d\u513f\uff0c\u8ba9\u5979\u8eab\u5c45\u957f\u5b89\u6df1\u5bab\uff0c\u5fc3\u7cfb\u94c1\u77f3\u60c5\u90ce\uff0c\u8fd9\u662f\u7537 \u5b50\u6c49\u5927\u4e08\u592b\u4f5c\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u548c\u5b9d\u513f\u5f88\u719f\uff1f\u201d \u3000\u3000\u201c\u957f\u5b89\u7ed3\u8bc6\uff0c\u60c5\u540c\u59d0\u59b9\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u8881\u59d1\u5a18\u5c31\u8be5\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5979\u4e3a\u60c5\u727a\u7272\uff0c\u84d3\u857e\u672a\u7efd\u82b1\u5df2\u6b8b\uff01\u4e00\u4e2a\u76c8\u76c8\u5341\u4e94\u7684\u59d1\u5a18\u7acb\u8eab\u4e8e\u4f73\u4e3d \u4e09\u5343\u7684\u6df1\u5bab\u5185\u82d1\uff0c\u548c\u5929\u4e0b\u7f8e\u5973\u4e89\u5ba0\u6597\u8273\uff0c\u65bd\u5c55\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u5f97\u5ba0\u7231\u96c6\u4e00\u8eab\uff0c\u6697\u5904\u65e0\u4eba\u73e0\u6cea \u6d9f\uff1b\u4e00\u4e2a\u7537\u4eba\u628a\u5fc3\u7231\u7684\u5973\u4eba\u53cc\u624b\u6367\u732e\u5e1d\u738b\u524d\uff0c\u4ee5\u56fa\u6743\u4f4d\u3002\u8fd9\u4e2a\u4eba\u8fd8\u7b97\u82f1\u96c4\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8981\u8bf4\u4e86\uff01\u201d\u674e\u4e16\u6c11\u5927\u58f0\u559d\u9053\uff1a\u201c\u6211\u4e0d\u662f\u82f1\u96c4\u2026\u2026\u201d \u3000\u3000\u4ed6\u5f20\u5634\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u5b9d\u513f\u662f\u7f8e\u5973\uff0c\u4eba\u7f8e\u5fc3\u66f4\u7f8e\uff1b\u5979\u820d\u8eab\u5165\u5bab\uff0c\u5fc3\u6000\u82cd \u751f\uff0c\u6211\u96be\u53ca\u5979\u4e07\u4e00\uff0c\u53ea\u6b64\u4e00\u6869\uff0c\u6211\u5c31\u8be5\u675f\u624b\u5c31\u7f1a\u5165\u957f\u5b89\uff0c\u5f85\u7f6a\u5bab\u95e8\u524d\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u4e0d\u8981\u542c\u542c\u7b2c\u4e09\u6869\u5927\u7f6a\u5462\uff1f\u8fd9\u5c31\u548c\u6211\u6709\u5173\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9664\u4e86\u6b64\u5730\u4f1a\u6664\u4e4b\u5916\uff0c\u4e16\u6c11\u548c\u59d1\u5a18\u7d20\u6627\u5e73\u751f\uff0c\u6211\u7684\u4e8b\u7275\u6d89\u4e86\u5b9d \u513f\uff0c\u5df2\u7ecf\u5fc3\u4e2d\u96be\u5b89\uff0c\u600e\u4f1a\u53c8\u7275\u626f\u4e0a\u4e86\u4f60\u8881\u7d2b\u70df\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u522b\u8bef\u4f1a\u5440\uff01\u5b9d\u59b9\u59b9\u60c5\u6709\u72ec\u949f\uff0c\u6211\u53ef\u662f\u4e0d\u89e3\u98ce\u60c5\uff01\u5173\u4e8e\u4f60\u7b2c\u4e09\u4ef6\u5927 \u7f6a\u7275\u6d89\u4e0a\u6211\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u7b54\u5e94\u7687\u4e0a\uff0c\u8981\u5e2e\u4ed6\u6d88\u9664\u738b\u6c14\uff01\u5929\u4e0a\u4eba\u95f4\uff0c\u9065\u9065\u4e07\u91cc\uff0c\u5929\u8c61\u7684\u53d8\u5316\u662f \u5426\u771f\u548c\u4eba\u95f4\u7684\u53d8\u6545\u6709\u7740\u76f8\u8fde\u7684\u5173\u7cfb\uff1f\u662f\u4e00\u79cd\u4e13\u95e8\u7684\u5b66\u8bc6\u9886\u57df\uff0c\u4f60\u662f\u5426\u76f8\u4fe1\u5b83\uff1f\u4f46\u6211\u7684\u672f\u6cd5\u4fee \u517b\u4f7f\u6211\u6df1\u4fe1\u4e0d\u7591\uff0c\u897f\u5317\u738b\u6c14\u5165\u957f\u5b89\uff0c\u5f88\u4e0d\u5e78\u5c31\u5e94\u5728\u4e86\u4f60\u7684\u8eab\u4e0a\uff0c\u6240\u4ee5\u6211\u8981\u5e26\u4f60\u56de\u957f\u5b89\uff0c\u4ea4\u7ed9 \u7687\u4e0a\u5904\u7f6e\u3002\u5f53\u7136\uff0c\u6211\u4e5f\u53ef\u80fd\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u3001\u674e\u9756\u3001\u656c\u5fb7\u7b49\u4e00\u6279\u4eba\uff0c\u4f1a\u5168\u529b\u963b\u6b62\u4f60\u5e26\u6211\u79bb\u5f00\u3002\u4f60\u5e26\u6211 \u5165\u957f\u5b89\u7684\u6700\u597d\u529e\u6cd5\u662f\u5148\u6740\u4e86\u6211\uff0c\u5e26\u7740\u6211\u7684\u4eba\u5934\u53bb\uff01\u6211\u6b7b\u4e86\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u518d\u5168\u529b\u963b\u6b62\u4f60\u3002 \u4e0d\u8fc7\u2026\u2026\u6211\u6709\u4e24\u4e2a\u5fc3\u613f\uff0c\u5e0c\u671b\u7d2b\u70df\u59d1\u5a18\u5e2e\u6211\u5b8c\u6210\uff01\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u80fd\u5e2e\u7684\u6211\u4e0d\u63a8\u8f9e\uff0c\u4e0d\u80fd\u5e2e\u7684\uff0c\u5c31\u8bf7\u4f60\u539f\u8c05\u4e86\uff01\u674e\u6df3\u98ce\u3001\u8881 \u5929\u7f61\u6700\u5927\u7684\u5931\u7b56\uff0c\u5c31\u662f\u4ed6\u4eec\u5bf9\u6211\u8881\u7d2b\u70df\u4e86\u89e3\u5f97\u4e0d\u591f\u591a\uff01\u4ed6\u4eec\u8ba4\u4e3a\u6211\u4eec\u4f1a\u8c08\u8bba\u5929\u4e0b\u53e4\u4eca\u4e8b\uff0c\u4e07 \u6c11\u82cd\u751f\u82e6\u4e0e\u4e50\uff1b\u4ed6\u4eec\u4e0d\u77e5\u9053\u6211\u65e9\u5df2\u4e0b\u5b9a\u4e86\u6740\u4f60\u7684\u51b3\u5fc3\uff0c\u6211\u662f\u5927\u968b\u671d\u7684\u56fd\u5e08\uff0c\u6700\u91cd\u5927\u7684\u8d23\u4efb\uff0c \u662f\u4fdd\u62a4\u56fd\u57fa\u6c38\u56fa\u3002\u201d \u3000\u3000\u201c\u6740\u4e86\u6211\u674e\u4e16\u6c11\uff0c\u5927\u968b\u671d\u771f\u80fd\u56fd\u57fa\u6c38\u56fa\u5417\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5230\u6c5f\u5357\u6c5f\u5317\u8d70\u4e00\u8d9f\uff1f\u770b\u770b\u90a3\u4e9b\u55f7 \u55f7\u5f85\u54fa\u7684\u9965\u6c11\uff0c\u9020\u6210\u5982\u6b64\u5c40\u9762\u7684\u4e0d\u662f\u65e0\u53ef\u6297\u62d2\u7684\u5929\u707e\uff0c\u800c\u662f\u4eba\u4e3a\u7684\u9519\u5931\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u5f88\u5927\u7684\u4e00\u6869\u9057\u61be\u7684\u4e8b\u4e86\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u53ef\u60dc\u5fc3\u6025\u5929\u4e0b\u4e8b\uff0c\u4e00\u6b65\u8e0f\u5165\u7e41\u534e\u5bab\uff0c \u53ea\u9053\u662f\u5df2\u7ecf\u6446\u8131\u4fd7\u70e6\u4e8b\uff0c\u5374\u4e0d\u6599\u6b64\u8eab\u4ecd\u5728\u7ea2\u5c18\u4e2d\uff0c\u4e8c\u516c\u5b50\uff01\u76f8\u9022\u6068\u665a\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6c42\u4f60\u7b2c\u4e00\u4ef6\u4e8b\u662f\u5e26\u6211\u4eba\u5934\u5165\u957f\u5b89\uff0c\u5148\u8ba9\u8881\u5b9d\u513f\u770b\u6211\u4e00\u773c\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u5b9d\u513f\u5982\u60c5\u6025\u62da\u547d\uff0c\u90a3\u53ef\u662f\u4f60\u5bb3\u4e86\u5979\uff0c\u6211\u7b54\u5e94\u4f60\uff01\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u4ef6\u2026\u2026\u201d\u674e\u4e16\u6c11\u63a5\u9053\uff1a\u201c\u6211\u5199\u4e86\u4e00\u672c\u2018\u7acb\u56fd\u4e4b\u9053\u2019\uff0c\u5e0c\u671b\u8881\u59d1\u5a18\u770b\u8fc7\u4e4b\u540e\uff0c\u5982\u679c \u4f60\u89c9\u5f97\u503c\u5f97\u7559\u4f20\u4e8e\u4e16\uff0c\u5c31\u5e2e\u6211\u9009\u4e00\u503c\u5f97\u4f60\u4fe1\u6258\u7684\u4eba\uff0c\u4ea4\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u4ea4\u7ed9\u674e\u6df3\u98ce\u6216\u662f\u674e\u9756\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u7ed9\u4ed6\u4eec\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u6b64\u65f6\u6b64\u60c5\uff0c\u4ea4\u7ed9\u4ed6\u4eec\uff0c\u4f1a\u6fc0\u8d77\u4ed6\u4eec\u7684\u62da\u547d\u4e4b\u5fc3\uff0c\u4f60\u5c31\u7b97 \u80fd\u591f\u5e26\u6211\u7684\u4eba\u5934\u79bb\u5f00\uff0c\u4e5f\u5fc5\u5c06\u4f24\u75d5\u7d2f\u7d2f\u3002\u4f55\u51b5\uff0c\u4f60\u79bb\u5f00\u6b64\u5730\u7684\u673a\u4f1a\u4e0d\u662f\u5f88\u5927\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\u4ed6\u4eec\u6709\u80dc\u6211\u4e4b\u80fd\u5417\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u7d2b\u70df\u59d1\u5a18\uff0c\u4e0d\u8981\u4f4e\u4f30\u4e86\u8881\u5929\u7f61\u548c\u674e\u6df3\u98ce\u7684\u80fd\u529b\u6211\u662f\u4e0d\u613f\u4e16\u95f4\u7684\u7cbe\u82f1\u4eba\u7269\u5728 \u6b64\u5730\u62da\u6740\u4e2a\u540c\u5f52\u4e8e\u5c3d\u3002\u6211\u4e5f\u76f8\u4fe1\u6709\u4e00\u5929\u8881\u59d1\u5a18\u4f1a\u5e61\u7136\u56de\u609f\uff0c\u4f1a\u5408\u4ed6\u4eec\u4e4b\u529b\u5f00\u521b\u51fa\u4e00\u4e2a\u592a\u5e73\u65f6 \u4ee3\u6765\uff0c\u4e00\u5b9a\u6709\u8fd9\u4e48\u4e00\u5929\u7684\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u5bf9\u5929\u4e0b\u5927\u4e8b\uff0c\u4f3c\u4e4e\u662f\u4e86\u89e3\u5f88\u591a\uff01\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e16\u6c11\u5168\u529b\u89c2\u5bdf\u5929\u4e0b\u5927\u4e8b\uff0c\u6709\u4e2a\u975e\u5e38\u7684\u53ef\u6015\u7684\u5927\u5371\u6025\uff0c\u9690\u4f0f\u4e8e\u4fa7\u3002\u5982 \u679c\u4f60\u8881\u59d1\u5a18\u548c\u674e\u6df3\u98ce\u7b49\u4eba\u62da\u4e86\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u968b\u671d\u57fa\u4e1a\u56fa\u662f\u65e0\u6cd5\u518d\u4fdd\uff0c\u866c\u9aef\u5ba2\u5f20\u4ef2\u575a\u5f53\u4f1a\u8131\u9896 \u800c\u51fa\u3002\u5982\u4e0d\u662f\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u7275\u5236\u4f4f\u4ed6\uff0c\u957f\u5b89\u57ce\u65e9\u5df2\u7ecf\u7e41\u534e\u6210\u68a6\uff0c\u8fd9\u4e2a\u4eba\u4e00\u65e6\u8eab\u767b\u4e0a\u4e5d\u4e94\u4e4b \u4f4d\uff0c\u8840\u8165\u7684\u5c60\u6740\u5c24\u8fc7\u79e6\u7687\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fd9\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u866c\u9aef\u5ba2\u7684\u6cbb\u4e16\u624b\u6bb5\u8fd8\u4e0d\u5982\u968b\u7080\u5e1d\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u8c6a\u52c7\u76d6\u4e16\uff0c\u76ee\u65e0\u4f59\u5b50\uff0c\u4ed6\u662f\u6c5f\u6e56\u4e2d\u7684\u5927\u4fa0\u8c6a\u6770\uff0c\u4f46\u4e5f\u662f\u4e00\u4e2a\u975e\u5e38\u53ef \u6015\u7684\u541b\u738b\uff0c\u4ed6\u672c\u8eab\u52c7\u6b66\u3001\u521a\u6b63\uff0c\u81ea\u975e\u6768\u5e7f\u80fd\u53ca\uff0c\u4e5f\u975e\u4e16\u6c11\u80fd\u6bd4\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u4e00\u4e2a\u52c7\u6b66\u3001\u521a\u6b63\u7684\u7687\u5e1d\uff0c\u53ef\u4ee5\u4f7f\u767e\u5b98\u656c\u754f\uff0c\u5978\u4f5e\u7edd \u8ff9\uff0c\u671d\u7eb2\u91cd\u632f\uff0c\u5929\u4e0b\u592a\u5e73\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u59d1\u5a18\uff01\u6cbb\u4e16\u4e4b\u9053\u4e0d\u662f\u65ad\u5904\u4e8b\u975e\uff0c\u4e00\u5200\u4e24\u65ad\u518d\u65e0\u7275\u8fde\u3002\u5f3a\u56fd\u4e4b\u672c\uff0c\u9996\u5728\u7528 \u4eba\uff1b\u866c\u9aef\u5ba2\u521a\u610e\u51b7\u5389\uff0c\u5982\u950b\u5229\u4e4b\u5200\uff0c\u4e00\u9635\u4e71\u6740\uff0c\u5c06\u4f7f\u7fa4\u8d24\u8d70\u907f\uff0c\u56fd\u65e0\u6cbb\u81e3\uff0c\u90a3\u5c31\u5f62\u6210\u8d8a\u6740\u8d8a \u4e71\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u8bf4\u866c\u9aef\u5ba2\u52c7\u4e0d\u53ef\u6321\uff0c\u4f46\u4f60\u80fd\u591f\u5bf9\u4ed8\u4ed6\u5417\uff01\u201d \u3000\u3000\u201c\u4e16\u6c11\u4e0d\u80fd\uff0c\u4f46\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u6216\u8bb8\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u201c\u6216\u8bb8\u7684\u610f\u601d\u662f\u6ca1\u6709\u628a\u63e1\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u5a07\u7b11\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u5462\uff1f\u201d\uff0e \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4f60\u7d2b\u70df\u59d1\u5a18\uff0c\u5982\u518d\u52a0\u4e0a\u674e\u9756\uff0c\u90a3\u5c31\u5341\u62ff\u4e5d\u7a33\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u5012\u662f\u4e0d\u76f8\u4fe1\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u771f\u6709\u901a\u5929\u5f7b\u5730\u4e4b\u80fd\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u65e0\u6cd5\u5bf9\u6297\u59d1\u5a18\u7684\u7cbe\u5947\u6cd5\u672f\uff0c\u4f46\u4ed6\u6709\u5341\u4e07\u7532\u5175\u554a\uff01\u8fd9\u4e9b\u4eba\u4e00\u65e6\u8d77\u6765\uff0c \u662f\u9700\u8981\u5175\u7565\u3001\u6218\u7b56\uff0c\u51b3\u80dc\u4e8e\u7586\u573a\u4e4b\u4e0a\uff0c\u96be\u9053\u59d1\u5a18\u80fd\u5f79\u4f7f\u672f\u6cd5\u548c\u6570\u5341\u4e07\u5927\u519b\u4e00\u6218\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u8fd9\u786e\u662f\u4e07\u4e07\u4e0d\u6210\u7684\u4e8b\uff0c\u672f\u6cd5\u53ea\u80fd\u4e58\u52bf\u52a9\u5a01\uff0c\u51b3\u80dc\u4e8e\u9ad8\u624b\u640f\u6740\u4e4b \u6218\uff0c\u5bf9\u6297\u5343\u519b\u4e07\u9a6c\uff0c\u6050\u662f\u529b\u6709\u4e0d\u654c\u3002 \u3000\u3000\u4f46\u95fb\u674e\u4e16\u6c11\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u65e0\u9053\u57fa\uff0c\u4e0d\u901a\u672f\u6cd5\uff0c\u4f46\u53e4\u5f80\u4eca\u6765\uff0c\u7cbe\u4e8e\u6b64\u9053\u7684\u4eba\u4ece\u65e0\u4e00\u4e2a \u80fd\u5728\u9010\u9e7f\u5929\u4e0b\u7684\u5927\u4e1a\u4e2d\u4e89\u53d6\u5230\u4e00\u5e2d\u4e4b\u5730\u3002\u201d \u3000\u3000\u201c\u8bf4\u5f97\u5bf9\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f46\u4e8c\u516c\u5b50\u5ffd\u7565\u4e00\u4e2a\u975e\u5e38\u91cd\u8981\u7684\u5173\u952e\uff0c\u9053\u6cd5\u4e0d\u8db3\u4ee5\u7acb\u56fd\u5b89\u90a6\uff0c \u4f46\u5374\u80fd\u6d1e\u900f\u5148\u673a\uff0c\u638c\u63e1\u53d8\u5c40\u3002\u738b\u6c14\u96c6\u805a\u4e8c\u516c\u5b50\u7684\u8eab\u4e0a\uff0c\u4e00\u4ee3\u5e1d\u738b\u57fa\u4e1a\u975e\u541b\u83ab\u5c5e\u4e86\u3002\u65a9\u541b\u4e4b \u9996\uff0c\u65ad\u53bb\u738b\u6c14\uff0c\u5929\u4e0b\u7684\u53d8\u5316\u53c8\u662f\u4e00\u756a\u65b0\u5c40\u4e86\u3002\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4e43\u81f3\u517c\u901a\u9053\u6cd5\u3001\u6218\u7b56\u7684\u4e00\u4ee3\u5c06 \u624d\u674e\u9756\uff0c\u90fd\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u4e8c\u516c\u5b50\uff0c\u602a\u53ea\u602a\u8881\u5929\u7f61\u76ee\u7a7a\u56db\u6d77\uff0c\u81ea\u6043\u624d\u6c14\uff0c\u5984\u81ea\u5e72\u4e0e\u5929\u673a\uff0c\u624d\u5f15 \u52a8\u6211\u8fd9\u6df1\u85cf\u5c71\u6797\u7684\u5c0f\u5973\u5b50\u52a8\u4e86\u4e89\u80dc\u4e4b\u5fc3\uff0c\u4e0b\u5f97\u5c71\u5c97\u5165\u51e1\u5c18\uff0c\u4e00\u5934\u683d\u8fdb\u4e86\u662f\u975e\u4e2d\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u4f60\u662f\u8bf4\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u5982\u4e0d\u5e72\u4e0e\u5929\u673a\uff0c\u4f60\u5c31\u4e0d\u4f1a\u629b\u53bb\u4ed9\u5883\u5165\u51e1 \u5c18\uff0c\u53c2\u4e0e\u8fd9\u573a\u7eb7\u4e89\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u5c45\u4f4f\u5728\u7ec8\u5357\u5c71\u7edd\u5cf0\u4e4b\u9876\uff0c\u7ecf\u5e74\u548c\u767d\u96ea\u5bd2\u51b0\u4e3a\u4f34\uff0c\u542c\u677e\u6d9b\u5531\u548c\uff0c\u770b\u660e \u6708\u6e05\u98ce\uff0c\u4e0d\u89e3\u7537\u5973\u98ce\u60c5\u4e8b\uff0c\u600e\u77e5\u4eba\u95f4\u6709\u7eb7\u4e89\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u80f8\u65e0\u5757\u5792\uff0c\u4e0d\u89e3\u5929\u8c61\uff0c\u5982\u679c\u771f\u6709\u5929\u673a\u5b58\u5728\uff0c\u88ab\u4f60\u4eec\u4e00\u6405 \u548c\uff0c\u5c31\u5929\u673a\u5927\u53d8\uff0c\u90a3\u8fd8\u80fd\u79f0\u5929\u673a\u5417\uff1f\u201d\u2019 \u3000\u3000\u8881\u7d2b\u70df\u88ab\u95ee\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u65b9\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4eba\u5b9a\u80dc\u5929\u554a\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u7b11\u9053\uff1a\u201c\u5982\u662f\u4eba\u7b97\u80dc\u8fc7\u5929\u7b97\uff0c\u5929\u673a\u53c8\u4f55\u5728\u5462\uff1f\u81ea\u7136\u754c\u7684\u4e00\u4e9b\u53d8\u8c61\u786e\u548c\u4eba\u7c7b\u7684\u547d\u8fd0 \u606f\u606f\u76f8\u5173\uff0c\u5927\u65f1\u4e09\u5e74\uff0c\u6c34\u6d8c\u4e24\u8f7d\uff0c\u8757\u866b\u4e3a\u5bb3\uff0c\u7530\u5730\u9f9f\u88c2\uff0c\u518d\u82f1\u660e\u7684\u7687\u5e1d\uff0c\u518d\u8d24\u80fd\u7684\u5bb0\u76f8\uff0c\u4e5f \u65e0\u6cd5\u8ba9\u5929\u4e0b\u7684\u767e\u59d3\u4eec\u4e30\u8863\u8db3\u98df\u3002\u4f46\u660e\u4e3b\u8d24\u76f8\u53ef\u4ee5\u51cf\u5c11\u707e\u5bb3\u81f3\u6700\u8f7b\uff0c\u4f8b\u5982\u5357\u8d27\u5317\u8fd0\u3001\u503e\u5e93\u8d48 \u707e\u3001\u51cf\u514d\u7cae\u79df\u7b49\uff0c\u81f3\u5c11\u53ef\u4ee5\u591a\u4fdd\u4e00\u4e9b\u4eba\u547d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u542c\u5f97\u795e\u5f80\u4e86\u3002\u5979\u7cbe\u7814\u672f\u6cd5\uff0c\u8ffd\u6c42\u7684\u662f\u4ed9\u5b66\u91d1\u4e39\u5927\u9053\uff0c\u4f01\u56fe\u5ef6\u5e74\u76ca\u5bff\uff0c\u957f\u751f\u4e0d\u8001\uff0c \u4e3a\u7684\u5168\u662f\u4e2a\u4eba\uff0c\u4ece\u672a\u4e3a\u522b\u4eba\u60f3\u8fc7\u3002\u5982\u4eca\u542c\u5f97\u674e\u4e16\u6c11\u5a13\u5a13\u9053\u6765\uff0c\u987f\u89c9\u5fc3\u80f8\u6269\u5c55\uff0c\u89c6\u754c\u4e00\u5f00\u3002 \u3000\u3000\u5979\u6697\u5fd6\u9053\uff1a\u5929\u4e0b\u6709\u90a3\u4e48\u591a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u6211\u4ece\u6765\u5c31\u6ca1\u6709\u60f3\u8fc7\u522b\u4eba\u5462\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5c31\u7b97\u592a\u5e73\u4e4b\u4e16\u5427\uff01\u4f46\u4eba\u8c0b\u8d2a\u8d43\uff0c\u5b98\u8d2a\u5175\u9a84\uff0c\u4e5f\u4e00\u6837\u4f7f\u751f\u6c11\u56f0\u82e6\uff0c\u4e0d \u540c\u7684\u662f\u6731\u95e8\u9152\u8089\u81ed\uff0c\u8def\u6709\u997f\u6b7b\u9aa8\u3002\u6cbb\u56fd\u4e4b\u9053\uff0c\u9996\u91cd\u7528\u4eba\uff0c\u6709\u82f1\u660e\u4e4b\u4e3b\uff0c\u624d\u80fd\u7528\u8d24\u80fd\u4e4b\u81e3\u3002\u7267 \u6c11\u4e4b\u5b98\uff0c\u5982\u80fd\u7231\u6c11\u5982\u5b50\uff0c\u5929\u4e0b\u6cbb\u77e3\uff01\u201d \u3000\u3000\u201c\u542c\u8d77\u6765\u5f88\u7b80\u5355\u561b\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5386\u6765\u6cbb\u56fd\u7684\u7687\u5e1d\u5927\u90fd\u4e0d\u80af\u8fd9\u6837\u505a\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4e0d\u80af\uff0c\u800c\u662f\u4e0d\u80fd\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u7687\u5e1d\u4e5f\u662f\u4e00\u4e2a\u4eba\uff0c\u76ee\u4e0d\u53ca\u5341\u4e08\u4e4b\u8fdc\uff0c\u8033\u4e0d\u95fb\u767e \u5c3a\u5916\u8a00\uff1b\u8981\u8fdc\u5978\u81e3\u3001\u4eb2\u8d24\u81e3\uff0c\u624d\u80fd\u8eab\u5728\u6df1\u5bab\uff0c\u5fc3\u53ca\u5343\u91cc\uff1b\u8fd9\u5c31\u8981\u4e0b\u756a\u5927\u529f\u592b\u4e86\u3002\u8981\u719f\u77e5\u5929\u4e0b \u4e8b\u7269\uff0c\u80f8\u7f57\u5343\u53e4\u6587\u7ae0\uff0c\u541b\u80fd\u660e\u67e5\u7ec6\u5fae\uff0c\u81e3\u5c31\u77e5\u65e0\u4e0d\u8a00\uff0c\u5929\u4e0b\u624d\u58eb\u624d\u80af\u6295\u6548\uff0c\u5f00\u4e00\u4ee3\u6c11\u5bcc\u56fd\u5f3a \u7684\u592a\u5e73\u76db\u4e16\uff0c\u5c3d\u5728\u5176\u4e2d\u77e3\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b66\u4e60\u9053\u6cd5\u7684\u4eba\uff0c\u53ea\u77e5\u6709\u5df1\uff0c\u4e0d\u77e5\u6709\u4eba\uff1b\u4eca\u5bb5\u4e0e\u541b\u4e00\u5e2d\u8bdd\uff0c\u80dc\u8bfb \u5bd2\u7a97\u5341\u5e74\u4e66\u3002\u4e00\u4ee3\u5929\u9a84\uff0c\u975e\u541b\u83ab\u5c5e\u3002\u53ea\u53ef\u60dc\uff0c\u4f60\u6211\u52bf\u540c\u6c34\u706b\uff0c\u541b\u867d\u6709\u5f3a\u56fd\u5bcc\u6c11\u4e4b\u624d\uff0c\u6211\u5374\u65e0 \u60dc\u624d\u653e\u7eb5\u4e4b\u5fc3\u3002\u4e8c\u516c\u5b50\uff0c\u53ea\u6015\u8981\u591a\u591a\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u6ca1\u6709\u754f\u60e7\u3001\u75db\u82e6\uff0c\u53cd\u800c\u6709\u4e00\u79cd\u56de\u5f52\u7684\u8f7b\u677e\uff0c\u4e0d\u77e5\u60f3\u5230\u4ec0\u4e48\u9ad8\u5174\u7684\u4e8b\uff1f\u5634\u89d2\u95f4\u7adf\u6d6e \u73b0\u51fa\u5fae\u5fae\u7684\u7b11\u610f\u3002\u8fd9\u4e2a\u4eba\u7684\u4e00\u5207\uff0c\u90fd\u548c\u5e38\u4eba\u4e0d\u540c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5200\u5df2\u5728\u624b\uff0c\u5374\u6709\u7740\u96be\u4ee5\u4e0b\u624b\u7684\u611f\u89c9\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5145\u6ee1\u7740\u5f3a\u70c8\u7684\u597d\u5947\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff1f\u5200\u8fc7\u4eba\u5934\u843d\uff0c\u9c9c\u8840\u6e85\u8863\u888d\uff0c\u662f\u4ef6\u5f88 \u75db\u82e6\u7684\u4e8b\u554a\uff01\u674e\u6df3\u98ce\u53ef\u4ee5\u7528\u7075\u4e39\u3001\u5999\u836f\u7597\u597d\u4f60\u4e25\u91cd\u7684\u4f24\u52bf\uff0c\u4f46\u4ed6\u6ca1\u6709\u80fd\u529b\u628a\u65a9\u4e0b\u7684\u8111\u888b\u91cd\u65b0 \u63a5\u7eed\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d1\u5a18\uff0c\u6b7b\u4ea1\u5bf9\u6211\u6765\u8bf4\uff0c\u662f\u4e00\u4ef6\u89e3\u8131\uff01\u201d\u674e\u4e16\u6c11\u53cc\u76ee\u5fae\u7741\uff0c\u8138\u4e0a\u7684\u7b11\u610f\u4f9d\u7136\uff0c\u63a5\u7740 \u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u5df2\u7ecf\u5141\u8bfa\u963b\u6b62\u866c\u9aef\u5ba2\uff0c\u4e16\u6c11\u6b7b\u540e\uff0c\u4f60\u624d\u80fd\u96c6\u4e2d\u5168\u529b\u53bb\u5bf9\u4ed8\u4ed6\uff0c\u672a\u5fc5\u4e00\u5b9a\u8981\u53d6 \u4ed6\u4e4b\u547d\uff0c\u903c\u4ed6\u79bb\u5f00\u4e2d\u571f\u5c31\u597d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6697\u5fd6\u9053\uff1a\u4ed6\u5ff5\u5ff5\u4e0d\u5fd8\u866c\u9aef\u5ba2\uff0c\u770b\u6765\u5728\u4ed6\u5fc3\u4e2d\uff0c\u8fd9\u786e\u5b9e\u662f\u4ef6\u5341\u5206\u91cd\u5927\u7684\u4e8b\uff01 \u3000\u3000\u8881\u7d2b\u70df\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u903c\u4ed6\u79bb\u5f00\u4e2d\u571f\uff0c\u4e0d\u6015\u4ed6\u53bb\u800c\u590d\u8fd4\u5417\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e0d\u4f1a\u7684\uff0c\u866c\u9aef\u5ba2\u751f\u6027\u521a\u70c8\uff0c\u4e00\u65e6\u9047\u4e0a\u632b\u8d25\uff0c\u5c31\u8c6a\u6c14\u5168\u5931\uff0c\u53ea\u8981\u79bb\u5f00\u4e86\uff0c\u7edd \u4e0d\u4f1a\u518d\u56de\u4e2d\u571f\u3002\u8fd9\u4e2a\u6027\u683c\u6ce8\u5b9a\u4ed6\u521a\u610e\u81ea\u7528\uff0c\u6c38\u4e0d\u56de\u5934\u3002\u6240\u4ee5\uff0c\u4ed6\u4e0d\u80fd\u4f5c\u7687\u5e1d\uff0c\u53ea\u8981\u4ed6\u5f00\u59cb\u5c60 \u6740\uff0c\u4e5f\u5c31\u6c38\u65e0\u4f11\u6b62\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u751f\u51fa\u4e00\u4efd\u656c\u6155\u4e4b\u5fc3\u3002 \u3000\u3000\u4ed6\u6b66\u529f\u5e73\u5e38\uff0c\u4f46\u9886\u8896\u7684\u9b45\u529b\u5374\u7b51\u5efa\u5728\u5168\u65e0\u79c1\u5ff5\u7684\u80f8\u895f\u4e4b\u4e0a\uff0c\u4ed6\u5766\u7136\u9762\u5bf9\u6b7b\u4ea1\uff0c\u6709\u5982\u89e3\u53bb \u8eab\u5fc3\u91cd\u8d1f\uff0c\u4f3c\u662f\u6d3b\u7740\u7684\u8d23\u4efb\u538b\u529b\u5c24\u5728\u6b7b\u4ea1\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u4f1f\u5927\u7684\u6551\u4e16\u82f1\u96c4\uff0c\u5982\u4f55\u80fd\u591f\u8ba9\u4ed6\u6b7b\u53bb\uff1f \u3000\u3000\u5979\u60f3\u5230\u4e86\u968b\u7080\u5e1d\u7684\u751f\u6d3b\uff0c\u65e5\u65e5\u76fc\u671b\u7684\u662f\u5979\u548c\u8881\u5b9d\u513f\uff0c\u5171\u4f8d\u541b\u738b\u4e8e\u4e00\u69bb\u4e4b\u4e0a\uff0c\u4e24\u4e2a\u7edd\u4e16\u65e0 \u4f26\u7684\u7f8e\u4e3d\u80f4\u4f53\u8ba9\u4ed6\u5de6\u62e5\u6709\u62b1\uff0c\u540c\u8d74\u5deb\u5c71\u5171\u4e91\u96e8\uff0c\u628a\u73a9\u7ec8\u5bb5\u4e0d\u7a0d\u505c\u3002 \u3000\u3000\u968b\u7080\u5e1d\u548c\u674e\u4e16\u6c11\uff0c\u7adf\u662f\u4e24\u4e2a\u5982\u6b64\u5168\u4e0d\u76f8\u540c\u7684\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u4e3a\u5e1d\u738b\uff0c\u5374\u65e5\u65e5\u8ffd\u9010\u6deb\u4e50\uff0c\u7f6e\u653f\u52a1\u56fd\u4e8b\u4e8e\u4e0d\u987e\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u751f\u957f\u4e8e\u4faf\u5e9c\u7684\u5e74\u5c11\u516c\u5b50\uff0c\u5374\u4ee5\u5929\u4e0b\u4e07\u6c11\u4e3a\u5ff5\uff0c\u65e5\u601d\u591c\u8651\uff0c\u7b79\u5212\u51fa\u4e00\u5957\u6cbb\u56fd\u5927\u9053\uff0c \u8981\u5f00\u521b\u4e00\u4ee3\u76db\u4e16\uff0c\u4f7f\u4e07\u6c11\u5171\u4eab\u7e41\u8363\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7f13\u7f13\u6536\u8d77\u5229\u5203\uff0c\u6740\u4e0d\u4e0b\u624b\u4e86\u3002 \u3000\u3000\u5979\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u4f7f\u5fc3\u60c5\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u5979\u4e0d\u80fd\u8ba9\u5bf9\u65b9\u611f\u53d7\u5230\u5fc3\u4e2d\u751f\u51fa\u7684\u656c\u6155\u4e4b\u60c5\u3002 \u3000\u3000\u5979\u53ea\u7f13\u7f13\u5730\u9053\uff1b\u201c\u4e8c\u516c\u5b50\uff0c\u6211\u60f3\u5e26\u4f60\u5165\u957f\u5b89\u3002\u6765\u65f6\uff0c\u66fe\u53d7\u5b9d\u513f\u59b9\u59b9\u4e00\u756a\u5631\u5490\u7167\u987e\u4f60\u3002\u82e5 \u5e26\u4f60\u9996\u7ea7\u56de\u5bab\u5ef7\uff0c\u5c82\u4e0d\u65ad\u4e86\u6211\u4eec\u59d0\u59b9\u7684\u60c5\u610f\u3002\u541b\u65e2\u4e0d\u754f\u6b7b\uff0c\u4f55\u59a8\u751f\u79bb\u6b64\u5730\u5165\u957f\u5b89\uff0c\u5148\u89c1\u5b9d\u513f \u4e00\u9762\uff0c\u518d\u6b7b\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d1\u5a18\uff0c\u53ea\u6015\u662f\u96be\u5982\u4eba\u610f\u554a\uff01\u201d\u674e\u4e16\u6c11\u7741\u5f00\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u5982\u4e0d\u6b7b\uff0c\u674e\u9756\u3001\u674e\u6df3 \u98ce\u3001\u8881\u5929\u7f61\u7edd\u4e0d\u4f1a\u4efb\u4f60\u5e26\u6211\u79bb\u5f00\u6b64\u5730\u3002\u56db\u4f4d\u4e00\u756a\u6597\u6cd5\u62da\u6740\uff0c\u80dc\u8d1f\u96be\u6599\uff1f\u4f46\u4f24\u4ea1\u53ef\u671f\uff0c\u6700\u4e0d\u5e78 \u7684\u662f\u4f60\u4eec\u6253\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u90a3\u5c31\u7559\u7ed9\u4e86\u866c\u9aef\u5ba2\u53ef\u4e58\u4e4b\u673a\uff0c\u4e5f\u975e\u6211\u7684\u5fc3\u613f\u4e86\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e24\u767e\u94c1\u9a91\u5df2\u7ecf\u5e03\u5b88\u5728\u2018\u76d8\u9f99\u5c45\u2019\u5916\uff0c\u8881\u5929\u7f61\u548c\u674e\u9756\u90fd\u5df2\u77e5\u6653\uff0c\u6211\u53ea\u8981\u4e00\u58f0 \u4ee4\u4e0b\uff0c\u4ed6\u4eec\u7acb\u523b\u51b2\u4e86\u8fdb\u6765\u3002\u5c31\u7b97\u4ed6\u4eec\u4e09\u4f4d\u60f3\u548c\u6211\u62fc\u6b7b\u4e00\u6218\uff0c\u5374\u4e5f\u673a\u4f1a\u4e0d\u5927\uff0c\u6218\u4e0e\u4e0d\u6218\u4e4b\u6743\u64cd \u4e4b\u5728\u6211\u4e86\u554a\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u7684\u672f\u6cd5\u4fee\u517b\u4e5f\u8bb8\u786e\u6709\u80dc\u8fc7\u4e09\u4eba\u4e4b\u5904\uff0c\u4f46\u7edd\u4e0d\u80fd\u5fc3\u5b58\u8f7b\u654c\u3002\u674e \u6df3\u98ce\u3001\u8881\u5929\u7f61\u6570\u5341\u5e74\u7684\u4fee\u4e3a\uff0c\u5c82\u662f\u7b49\u95f2\uff1f\u6b64\u523b\u4f60\u4e0d\u6740\u6211\uff0c\u65e5\u540e\u518d\u60f3\u627e\u6740\u6211\u7684\u673a\u4f1a\uff0c\u53ea\u6015\u5c31\u5f88 \u96be\u518d\u6709\u4e86\uff01\u201d \u3000\u3000\u4f46\u95fb\u4e00\u9635\u6ce2\u6ce2\u8f7b\u54cd\uff0c\u5bc6\u5982\u8fde\u73e0\u3002 \u3000\u3000\u5bd2\u8292\u95ea\u52a8\uff0c\u5927\u5385\u4e2d\u706f\u706b\u590d\u660e\uff0c\u4f3c\u662f\u5385\u5916\u9762\u6709\u4eba\u5df2\u5c55\u5f00\u4e00\u573a\u5bf9\u9635\u640f\u6740\uff0c\u53ea\u4e0d\u8fc7\u5f88\u5feb\u5c31\u6d88\u5931 \u4e0d\u95fb\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u8bd5\u56fe\u8981\u95ef\u5165\u5927\u5385\uff1f\u201d \u3000\u3000\u968f\u624b\u4e00\u62c2\uff0c\u539f\u672c\u9690\u5931\u4e0d\u89c1\u7684\u5385\u95e8\u7a81\u7136\u51fa\u73b0\u3002 \u3000\u3000\u674e\u6df3\u98ce\u62ab\u53d1\u4ed7\u5251\uff0c\u5f53\u95e8\u800c\u7acb\uff0c\u9053\uff1a\u201c\u8001\u592b\u674e\u6df3\u98ce\uff0c\u59d1\u5a18\u7adf\u5728\u8305\u820d\u5385\u5916\u5e03\u4e0b\u4e86\u7981\u6cd5\uff0c\u963b\u7edd \u542c\u95fb\uff0c\u672a\u514d\u6709\u4e9b\u55a7\u5bbe\u593a\u4e3b\uff01\u8001\u592b\u53ea\u597d\u8bd5\u56fe\u7834\u9664\u7981\u6cd5\uff0c\u5f97\u7f6a\u4e4b\u5904\u8fd8\u8bf7\u59d1\u5a18\u5305\u6db5\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8001\u524d\u8f88\u8981\u89c1\u4e8c\u516c\u5b50\uff0c\u8fd8\u662f\u8981\u89c1\u665a\u8fdb\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u51e0\u4e4e\u6740\u4e86\u4e8c\u516c\u5b50\uff0c\u662f\u5417\uff1f\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u59d1\u5a18\u867d\u4ee5\u7981\u6cd5\u963b\u7edd\u4e86\u76ee\u89c6\u3001\u8033\u95fb\uff0c\u4f46\u5374 \u65e0\u6cd5\u65ad\u9664\u5929\u7f61\u7684\u6167\u773c\u900f\u5bdf\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u6211\u4e0d\u662f\u597d\u597d\u7684\u6d3b\u7740\u5417\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d1\u5a18\u7684\u624b\u4e2d\u5229\u5203\u4e00\u5ea6\u67b6\u4f4f\u4e86\u4e8c\u516c\u5b50\u7684\u9879\u9888\u4e4b\u4e0a\u3002\u201d\u8881\u5929\u7f61\u7f13\u6b65\u800c\u5165\uff0c\u9053\uff1a\u201c\u5982\u975e \u8001\u673d\u4ee5\u6167\u773c\u900f\u8fc7\u7981\u6cd5\u5bdf\u770b\uff0c\u8fd8\u771f\u60f3\u4e0d\u5230\u7d2b\u70df\u59d1\u5a18\u8c0b\u53d6\u4e8c\u516c\u5b50\uff0c\u662f\u5982\u6b64\u4e00\u4e2a\u6025\u8feb\u6cd5\uff0c\u8001\u673d\u548c\u6df3 \u98ce\u3001\u674e\u9756\u4f5c\u756a\u5546\u8bae\uff0c\u7531\u674e\u9756\u5e26\u7740\u8001\u592b\u5b88\u5c71\u56db\u7ae5\uff0c\u5e03\u5b88\u5165\u53e3\uff0c\u62d2\u6321\u6765\u654c\uff0c\u8001\u673d\u548c\u6df3\u98ce\u51c6\u5907\u548c\u7d2b \u70df\u59d1\u5a18\u653e\u624b\u4e00\u62da\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8881\u5148\u751f\uff0c\u542c\u5230\u6211\u548c\u7d2b\u70df\u59d1\u5a18\u7684\u8c08\u8bdd\u4e86\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u7d2b\u70df\u59d1\u5a18\u7684\u7981\u6cd5\u5389\u5bb3\uff0c\u58f0\u4e0d\u51fa\u5927\u5385\u4e4b\u5916\u3002\u8feb\u5f97\u8001\u673d\u4ee5\u6167\u773c\u6d1e\u5bdf\uff0c\u6b63\u89c1\u5230\u7d2b \u70df\u59d1\u5a18\u4ee5\u5229\u5203\u903c\u9888\uff0c\u8fd9\u5c31\u662f\u8001\u673d\u548c\u6df3\u98ce\u3001\u674e\u9756\u4e0d\u5f97\u4e0d\u4f5c\u6700\u574f\u7684\u6253\u7b97\u4e86\u3002 \u3000\u3000\u7d2b\u70df\u59d1\u5a18\u7684\u6167\u6cd5\u4f7f\u5f97\u6211\u4eec\u542c\u4e0d\u5230\u5979\u5982\u4f55\u903c\u95ee\u4e8c\u516c\u5b50\uff0c\u4f46\u5979\u4e5f\u542c\u4e0d\u5230\u6211\u4eec\u7814\u5546\u7684\u62d2\u654c\u4e4b \u7b56\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u5927\u8896\u4e00\u6325\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u7a81\u7136\u6321\u5728\u674e\u4e16\u6c11\u7684\u8eab\u524d\uff0c\u9053\uff1a\u201c\u7531\u6b64\u523b\u8d77\uff0c\u59d1\u5a18\u518d\u60f3\u63a5\u8fd1 \u4e8c\u516c\u5b50\uff0c\u5c31\u5f97\u5148\u53d6\u53bb\u8001\u673d\u7684\u6027\u547d\u624d\u884c\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u5fd6\u9053\uff1a\u6211\u5982\u8981\u6740\u4ed6\uff0c\u65e9\u5c31\u6740\u4e86\uff0c\u8fd8\u4f1a\u7b49\u5230\u73b0\u5728\uff1f\u4f46\u6211\u4e5f\u4e0d\u80fd\u7b54\u5e94\u653e\u4e86\u4ed6\u5440\uff01 \u7edf\u5e08\u5317\u4e0a\uff0c\u527f\u706d\u738b\u6c14\uff0c\u662f\u6211\u8bf7\u547d\u800c\u6765\u3002\u5982\u679c\u4e0d\u80fd\u62bc\u89e3\u4e16\u6c11\u5165\u957f\u5b89\uff0c\u8981\u6211\u5982\u4f55\u5411\u7687\u4e0a\u4ea4\u4ee3\uff1f \u3000\u3000\u5979\u5fc3\u4e2d\u4e00\u6025\uff0c\u8138\u8272\u53d8\u4e86\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u8feb\u6211\u4e00\u6218\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u59d1\u5a18\uff01\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u65e2\u65e0\u4e24\u5168\u4e4b\u7b56\uff0c\u53ea\u597d\u653e\u624b\u4e00\u62da\uff0c\u59d1\u5a18\u5982\u60f3\u5e26\u8d70\u4e16\u6c11\uff0c\u90a3 \u662f\u4e07\u4e07\u4e0d\u80fd\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u6025\u6025\u5730\u9053\uff1a\u201c\u4e24\u4f4d\u5148\u751f\uff0c\u53ef\u6709\u80dc\u8fc7\u8881\u7d2b\u70df\u59d1\u5a18\u7684\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6562\u7792\u54c4\u4e8c\u516c\u5b50\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u8881\u7d2b\u70df\u59d1\u5a18\u662f\u4e13\u4fee\u672f\u6cd5\u7684\u9ad8\u624b\uff0c\u672f\u6cd5\u6df1\u4e0d\u53ef\u6d4b\uff0c\u6211 \u4eec\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\u3002\u4e0d\u8fc7\uff0c\u6211\u548c\u5929\u7f61\u5df2\u7ecf\u53d6\u5f97\u5171\u8bc6\uff0c\u5fc5\u8981\u65f6\uff0c\u4e0d\u60dc\u540c\u5f52\u4e8e\u5c3d\uff0c\u62da\u4e0a\u8fd9\u6761\u8001\u547d \u4e86\u3002\u201d\uff0c \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e0d\u60dc\u4e00\u6b7b\uff0c\u4e5f\u672a\u5fc5\u80fd\u62da\u4e2a\u540c\u5f52\u4e8e\u5c3d\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u5c31\u592a\u5c0f\u89d1\u8001\u673d\u4e86\uff0c\u6211\u548c\u5929\u7f61\u90fd\u6709\u4e00\u7532\u5b50\u4ee5\u4e0a\u7684\u4fee\u4e3a\uff0c\u867d\u975e\u4e13\u7ec3\u672f \u6cd5\uff0c\u53ef\u4e5f\u901a\u8fbe\u8840\u62fc\u4e4b\u672f\uff0c\u8001\u673d\u5bf9\u6b64\u4e0b\u8fc7\u4e00\u756a\u7279\u522b\u7684\u529f\u592b\uff0c\u7edd\u975e\u674e\u9756\u80fd\u53ca\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d1\u5a18\uff0c\u9053\u6cd5\u7684\u7981\u5fcc\u662f\u4ee5\u672f\u6cd5\u4f24\u53ca\u65e0\u8f9c\u3002\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u674e\u4e16\u6c11\u4e0d\u662f\u6211\u8f88\u4e2d\u4eba\uff0c\u59d1 \u5a18\u4ee5\u672f\u6cd5\u5bf9\u4ed8\u4ed6\uff0c\u662f\u5426\u6709\u4e9b\u8fc7\u5206\u4e86\u5462\uff1f\u5982\u679c\u5927\u5bb6\u90fd\u4e0d\u9075\u5b88\u8fd9\u4e00\u4f20\u7edf\u4e0a\u7684\u7981\u5fcc\uff0c\u5b9d\u513f\u6b32\u53d6\u968b\u7080 \u5e1d\u7684\u6027\u547d\u6709\u5982\u63a2\u56ca\u53d6\u7269\u4e00\u822c\uff0c\u8fd8\u4f1a\u7528\u5f97\u7740\u7559\u5c45\u5bab\u5ef7\uff0c\u5fcd\u53d7\u7740\u8eab\u5fc3\u4e0a\u7684\u714e\u71ac\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\u4eec\u903c\u5979\u5165\u957f\u5b89\u7684\uff0c\u5b9d\u513f\u662f\u7edd\u4e16\u7f8e\u5973\uff0c\u5374\u8fc7\u7740\u5929\u4e0b\u6700\u75db\u82e6\u7684\u751f\u6d3b\u3002 \u6211\u60f3\u4e0d\u901a\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u7528\u51fa\u8fd9\u6837\u7684\u624b\u6bb5\uff1f\u8ba9\u5979\u9762\u5bf9\u541b\u738b\u4e89\u5ba0\u7231\uff0c\u5fc3\u7cfb\u60c5\u90ce\u6697\u6ce3\u8840\uff1b\u628a\u4e00\u4e2a\u5c0f \u59d1\u5a18\u6298\u78e8\u5f97\u67d4\u80a0\u767e\u7ed3\uff0c\u8c01\u4e4b\u8fc7\u4e5f\uff1f\u8bf4\uff01\u662f\u4f60\u4eec\u54ea\u4e00\u4e2a\u7684\u4e3b\u610f\uff1f\u201d \u3000\u3000\u201c\u662f\u5b9d\u513f\u81ea\u5df1\u7684\u51b3\u5b9a\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5979\u4e0d\u5fcd\u770b\u6211\u7ec8\u65e5\u5fe7\u4e07\u6c11\u4e4b\u5fe7\uff0c\u81ea\u613f\u5165\u5bab\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u90a3\u5c31\u7f62\u4e86\uff0c\u81ea\u5df1\u7684\u51b3\u5b9a\u6028\u4e0d\u5f97\u522b\u4eba\uff0c\u53ea\u597d\u81ea\u4f5c\u81ea\u53d7\u4e86\u3002\u4f46\u4f60\u80fd\u820d\u4e86\u5979\uff0c\u5374 \u662f\u5927\u6709\u4fee\u9053\u4eba\u7684\u51b7\u9177\u6027\u683c\uff0c\u65a9\u60c5\u65ad\u4e49\u2026\u2026\u201d \u3000\u3000\u8881\u5929\u7f61\u63a5\u53e3\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u60c5\u517c\u5929\u4e0b\u4e07\u6c11\uff0c\u4e49\u6cfd\u56db\u6d77\u82cd\u751f\uff0c\u59d1\u5a18\u51b7\u773c\u770b\u4e16\u6001\uff0c\u674e\u4e16\u6c11\u662f\u4e0d \u662f\u4f1a\u4ee4\u4eba\u8083\u7136\u751f\u656c\u5462\uff1f\u6211\u5e72\u4e0e\u5929\u673a\u7684\u8fd0\u884c\uff0c\u662f\u5e0c\u671b\u4eba\u95f4\u5c11\u51e0\u5e74\u5175\u7978\u6740\u622e\uff0c\u5374\u4e0d\u6599\u5f15\u51fa\u4f60\u59d1\u5a18 \u8fd9\u4e48\u4e00\u4f4d\u5389\u5bb3\u7684\u4eba\u7269\u51fa\u6765\u3002\u903c\u5f97\u5927\u519b\u672a\u52a8\u4e4b\u524d\uff0c\u5148\u8981\u4e00\u573a\u672f\u6cd5\u5bf9\u51b3\uff0c\u8fd9\u662f\u5927\u5927\u7684\u51fa\u4e86\u6211\u610f\u6599 \u4e4b\u5916\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5ffd\u7136\u7b11\u4e86\uff1b\u7b11\u5f97\u5982\u6625\u82b1\u76db\u653e\u3002 \u3000\u3000\u5979\u7f13\u7f13\u5730\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u524d\u8f88\u5bf9\u6211\u5a01\u80c1\u6050\u5413\u4e86\u534a\u5929\uff0c\u65e2\u8981\u65bd\u5c55\u8840\u62fc\u5927\u6cd5\u548c\u6211\u540c\u5f52\u4e8e\u5c3d\uff0c\u53c8 \u8981\u5e03\u4e0b\u2018\u9501\u9f99\u9635\u2019\uff0c\u8ba9\u672f\u6cd5\u6682\u5931\u6548\u7528\uff0c\u4ee5\u6b66\u529f\u640f\u6740\u4e8e\u6211\uff0c\u6211\u60f3\u8fd9\u662f\u4e24\u4f4d\u8001\u524d\u8f88\u5bf9\u6211\u7684\u5e08\u627f\u6765 \u5386\u4e0d\u591f\u4e86\u89e3\u7684\u7f18\u6545\uff01\u201d \u3000\u3000\u201c\u59d1\u5a18\u4e0d\u662f\u5343\u81c2\u89c2\u97f3\u7684\u9ad8\u8db3\u5417\uff1f\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u5979\u662f\u5929\u4e0b\u7b2c\u4e00\u672f\u6cd5\u9ad8\u624b\uff0c\u800c\u4e14\u6280\u827a\u53e4 \u602a\uff0c\u83ab\u53ef\u9884\u6d4b\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e0d\u9519\uff01\u7d2b\u70df\u5e08\u95e8\u5411\u4e3a\u4eba\u89c6\u4f5c\u4e86\u65c1\u95e8\u5de6\u9053\uff0c\u672c\u95e8\u4e5f\u786e\u5b9e\u4ee5\u6280\u827a\u4e3a\u91cd\u3002\u4e0d\u8fc7\uff0c \u6211\u4eec\u7ec3\u7684\u4e0d\u662f\u969c\u773c\u90aa\u672f\uff0c\u800c\u662f\u771f\u6b63\u7684\u79fb\u8f6c\u5927\u6cd5\u3002\u8bf8\u4f4d\u5982\u679c\u4ee5\u65c1\u95e8\u90aa\u672f\u6765\u5b9a\u4f4d\u672c\u95e8\u6280\u827a\uff0c\u9519\u4f30 \u7684\u5931\u8bef\uff0c\u5c06\u4f7f\u4e24\u4f4d\u8001\u524d\u8f88\u4ed8\u51fa\u5de8\u5927\u7684\u4ee3\u4ef7\u3002\u6211\u5df2\u7ec3\u6210\u4e86\u8eab\u5916\u5316\u8eab\uff0c\u89e3\u4f53\u8840\u62fc\u5927\u6cd5\u672a\u5fc5\u80fd\u4f24\u5f97 \u4e86\u6211\u3002\u4f46\u65bd\u672f\u8005\u5374\u662f\u975e\u6b7b\u4e0d\u53ef\uff0c\u8fd9\u4e00\u70b9\u665a\u8f88\u8981\u5148\u4f5c\u8bf4\u660e\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u8f6c\u52a8\uff0c\u626b\u63a0\u4e86\u674e\u6df3\u98ce\u548c\u8881\u5929\u7f61\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u5bf9\u631b\u4e16\u6c11\u7684\u5fe0\u8bda\uff0c\u6211\u5f88\u611f \u52a8\u3002\u4e0d\u8fc7\uff0c\u4ed6\u662f\u738b\u6c14\u6240\u949f\u7684\u4eba\uff0c\u4e5f\u662f\u6211\u6765\u6b64\u7684\u76ee\u6807\u3002\u6240\u4ee5\uff0c\u6211\u4e00\u5b9a\u8981\u628a\u4ed6\u5e26\u56de\u957f\u5b89\uff0c\u6211\u53ea\u80fd \u4fdd\u8bc1\u8ba9\u4ed6\u89c1\u89c1\u8881\u5b9d\u513f\uff0c\u7136\u540e\u628a\u4ed6\u4ea4\u7ed9\u7687\u4e0a\u5904\u7f6e\uff0c\u7ed3\u679c\u5982\u4f55\uff1f\u5c31\u975e\u6211\u6240\u80fd\u4f5c\u4e3b\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u5f3a\u4eba\u6240\u96be\u554a\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u5207\u5e03\u5c40\uff0c\u5931\u53bb\u4e86\u4e8c\u516c\u5b50\uff0c\u5c31\u5168\u65e0\u610f\u4e49 \u4e86\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u8981\u548c\u4e24\u4f4d\u8001\u524d\u8f88\u6765\u4e2a\u7ea6\u5b9a\uff0c\u7d2b\u70df\u613f\u5728\u4e24\u4f4d\u8001\u524d\u8f88\u5e03\u8bbe\u7684\u2018\u9501\u9f99\u9635\u2019\u4e2d\uff0c\u548c\u4e24\u4f4d \u4f5c\u4e00\u573a\u516c\u5e73\u7684\u6597\u6cd5\u3002\u5982\u662f\u672f\u6cd5\u771f\u7684\u5df2\u65e0\u6cd5\u65bd\u5c55\uff0c\u90a3\u5c31\u5404\u51ed\u6b66\u529f\u4e00\u6218\uff0c\u7d2b\u70df\u8d25\u4e86\uff0c\u53ea\u602a\u6211\u5b66\u827a \u4e0d\u7cbe\uff0c\u5c0f\u5c0f\u5f31\u5973\u5b50\u5984\u56fe\u5e72\u4e0e\u5929\u4e0b\u5927\u4e8b\uff0c\u5b9e\u662f\u81ea\u4e0d\u91cf\u529b\uff0c\u6211\u56de\u5934\u5c31\u8d70\uff0c\u4e0d\u56de\u957f\u5b89\u56de\u7ec8\u5357\uff0c\u4e0a\u8868 \u8bf7\u8f9e\u56fd\u5e08\u4f4d\uff0c\u4ece\u6b64\u4e0d\u518d\u5165\u7ea2\u5c18\uff0c\u5982\u679c\u7d2b\u70df\u80dc\u4e86\u4e24\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u5e26\u6211\u4eec\u56de\u957f\u5b89\u590d\u65e8\u3002\u201d\u8881\u5929\u7f61\u63a5\u7740\u9053\uff1a\u201c\u628a\u6240\u6709\u7684\u7f6a\u72b6\u90fd\u63a8\u5728\u6211\u4eec\u4e24\u4eba\u8eab\u4e0a\uff0c \u4f60\u53ef\u4ee5\u8bf4\u6211\u4eec\u5996\u8a00\u60d1\u4f17\uff0c\u4e58\u52bf\u4f5c\u4e71\u3002\u968b\u70bb\u5e1d\u8981\u6740\u8981\u5250\uff0c\u7531\u4ed6\u5904\u7f6e\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u4e0d\u2026\u2026\u6211\u8981\u5e26\u8d70\u674e\u4e16\u6c11\uff0c\u4e24\u4f4d\u662f\u4e16\u5916\u9ad8\u4eba\uff0c\u7d2b\u70df\u4e0d\u6562\u5192\u72af\uff0c\u201d \u3000\u3000\u201c\u8fd9\u5c31\u6709\u4e9b\u77eb\u60c5\u4e86\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u7533\u8ff0\u51fa\u4e00\u767e\u4e2a\u7406\u7531\uff0c\u8bf4\u670d\u968b\u7080\u5e1d\uff0c\u4ed6\u4e5f\u4f1a\u76f8 \u4fe1\u59d1\u5a18\uff0c\u4f46\u662f\u5e26\u8d70\u674e\u4e16\u6c11\uff0c\u7edd\u4e0d\u53ef\u4ee5\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e24\u4f4d\u524d\u8f88\uff0c\u4e16\u6c11\u4e00\u4ecb\u51e1\u592b\u8033\uff0c\u600e\u4f1a\u786c\u88ab\u6307\u5b9a\u4e3a\u738b\u6c14\u6240\u949f\u7684\uff1f\u5982\u679c\u738b\u6c14\u949f \u6211\uff0c\u81ea\u4f1a\u6709\u767e\u7075\u62a4\u4f51\uff0c\u7d2b\u70df\u59d1\u5a18\u5c82\u80fd\u6740\u5f97\u4e86\u6211\uff1f\u5982\u662f\u7d2b\u70df\u59d1\u5a18\u628a\u6211\u6740\u4e86\uff0c\u90a3\u8bf4\u660e\u6211\u53ea\u662f\u4e00\u4e2a \u4fd7\u51e1\u4eba\u7269\uff0c\u6b7b\u4e4b\u4f55\u60dc\uff1f\u4e24\u4f4d\u8001\u524d\u8f88\u8bc6\u89c1\u8fc7\u4eba\uff0c\u4f55\u4e0d\u8d4c\u4e00\u8d4c\u4e16\u6c11\u7684\u8fd0\u6c14\u5462\uff1f\u8ba9\u8881\u7d2b\u70df\u59d1\u5a18\u5e26\u6211 \u5165\u957f\u5b89\uff0c\u81f3\u5c11\u6211\u53ef\u4ee5\u89c1\u4e0a\u5b9d\u513f\u4e00\u9762\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u592a\u5192\u9669\u4e86\uff01\u5343\u91d1\u4e4b\u8eaf\uff0c\u5750\u4e0d\u5782\u5802\uff0c\u4e8c\u516c\u5b50\u7eb5\u7136\u6709\u767e\u7075\u62a4\u4f51\uff0c\u4e5f\u4e0d \u80fd\u5192\u8fd9\u4e2a\u9669\u554a\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u60f3\u8fc7\u6ca1\u6709\uff0c\u4f60\u8ddf\u7d2b\u70df\u59d1\u5a18\u540c\u8d74\u957f\u5b89\uff0c\u79e6\u743c\u3001\u656c\u5fb7\u3001\u67f4\u7ecd\u3001 \u7a0b\u77e5\u8282\u7b49\u4e00\u822c\u82f1\u96c4\u597d\u6c49\u5c82\u80af\u5750\u89c6\u4e0d\u7406\uff1f\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u8d76\u5f80\u957f\u5b89\u6551\u4f60\uff0c\u4ee5\u7d2b\u70df\u59d1\u5a18\u7cbe\u6e5b\u7684\u672f\u6cd5\u800c \u8a00\uff0c\u4e00\u7f51\u6253\u5c3d\uff0c\u65e0\u5eb8\u7f6e\u7591\u3002\u4e8c\u516c\u5b50\uff0c\u4f60\u5fcd\u53d7\u8ba9\u4ed6\u4eec\u5168\u90e8\u53d7\u5bb3\u5417\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6df3\u98ce\uff0c\u4f60\u8981\u60f3\u6cd5\u5b50\u963b\u6321\u4ed6\u4eec\u554a\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4ed6\u4eec\u62e5\u6234\u4e8c\u516c\u5b50\uff0c\u662f\u51fa\u4e8e\u4e00\u7247\u81f3\u8bda\u3002\u5934\u53ef\u65a9\uff0c\u8840\u53ef\u6d41\uff0c\u5fd7\u4e0d\u53ef\u5c48\u3002\u8fd9\u4e00 \u70b9\uff0c\u4e8c\u516c\u5b50\u6bd4\u8001\u673d\u8fd8\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u554a\uff01\u6240\u4ee5\u8981\u4e24\u4f4d\u8001\u524d\u8f88\u963b\u6321\u4ed6\u4eec\uff0c\u5c31\u8bf4\u4e16\u6c11\u6709\u4e8b\u4ed6\u5f80\u2026\u2026\u201d \u3000\u3000\u674e\u6df3\u98ce\u63a5\u53e3\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u8981\u6211\u8bf4\u8c0e\uff1f\u6b64\u4e8b\u4e07\u4e07\u4e0d\u53ef\uff01\u201d \u3000\u3000\u201c\u6df3\u98ce\uff0c\u6709\u65f6\u5019\uff0c\u51e0\u53e5\u5584\u610f\u7684\u8c0e\u8a00\u662f\u53ef\u4ee5\u6551\u5f88\u591a\u6761\u4eba\u547d\u7684\uff0c\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u591a\u8bf4\u51e0\u53e5\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u201c\u6df3\u98ce\u5144\uff01\u201d\u8881\u5929\u7f61\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u65e2\u7136\u4f5c\u4e86\u51b3\u5b9a\uff0c\u54b1\u4eec\u518d\u591a\u5e72\u4e0e\uff0c\u5c31\u6709\u70b9 \u72ec\u884c\u5176\u662f\u4e86\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u2026\u4e0d\u7ba1\u4e86\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u7ba1\u5f97\u4e86\u5417\uff1f\u4ee5\u672f\u6cd5\u800c\u8bba\uff0c\u6211\u4eec\u4e24\u4eba\u8054\u624b\uff0c\u4e5f\u672a\u5fc5\u662f\u7d2b\u70df\u59d1\u5a18\u7684\u654c\u624b\uff1f\u5c31\u7b97 \u6211\u4eec\u548c\u7d2b\u70df\u59d1\u5a18\u62da\u4e86\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e8c\u516c\u5b50\u4e5f\u672a\u5fc5\u4f1a\u611f\u6fc0\u6211\u4eec\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u6447\u6447\u5934\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u5982\u679c\u575a\u6301\u8981\u53bb\u957f\u5b89\uff0c\u54b1\u4eec\u53ea\u597d\u4ece\u547d\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fd9\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff01\u4e24\u4f4d\u524d\u8f88\u82e5\u4e0d\u76f8\u4fe1\u4e16\u6c11\uff0c\u8bf7\u4ee3\u6211\u5b8c\u6210\u4e00\u4ef6\u5fc3\u613f\u2026\u2026\u201d \u3000\u3000\u8881\u5929\u7f61\u63a5\u53e3\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u8bf7\u5429\u5490\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u963b\u6b62\u866c\u9aef\u5ba2\u5f20\u4ef2\u575a\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u5750\u4e0a\u7687\u5e1d\u4e4b\u4f4d\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u5c06\u4f1a\u6740\u5f97\u5929\u5730\u53d8\u8840\u6d41\u6210\u6cb3\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u4ea6\u9053\uff1a\u201c\u662f\u7684\uff0c\u7edd\u4e0d\u80fd\u8ba9\u866c\u9aef\u5ba2\u5f97\u901e\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff01\u4e16\u6c11\u8fd9\u4e00\u4e2a\u5fc3\u613f\u5c31\u91cd\u6258\u4e24\u4f4d\u4e86\uff0c\u4e5f\u8bf7\u4e24\u4f4d\u963b\u6b62\u656c\u5fb7\u3001\u67f4\u7ecd\u3001\u7a0b\u77e5 \u8282\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6df3\u98ce\uff0c\u4f60\u53bb\u529d\u674e\u9756\u8ba9\u8def\uff0c\u6211\u963b\u6b62\u656c\u5fb7\u7b49\u4eba\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u70b9\u70b9\u5934\uff0c\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u4e8b\u60c5\u6025\u8f6c\u76f4\u4e0b\uff0c\u6210\u4e86\u5982\u6b64\u4e00\u4e2a\u53d8\u5316\uff0c\u5374\u662f\u5927\u5927\u5730\u51fa\u4e86\u8881\u7d2b\u70df\u610f\u5916\u3002 \u3000\u3000\u5979\u4e5f\u77e5\u9053\uff0c\u4e8b\u60c5\u7edd\u4e0d\u4f1a\u5982\u6b64\u7684\u5355\u7eaf\uff0c\u4e2a\u4e2d\u5fc5\u6709\u539f\u56e0\u3002\u4f46\u601d\u524d\u60f3\u540e\uff0c\u77a7\u4e0d\u51fa\u7834\u7efd\u4f55\u5728\uff0c\u53ea \u597d\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u674e\u6df3\u98ce\u5f88\u5feb\u5c31\u56de\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8138\u8272\u51dd\u91cd\u5730\u9053\uff1a\u201c\u7d2b\u70df\u59d1\u5a18\uff0c\u5b87\u6587\u6210\u90fd\u5df2\u548c\u656c\u5fb7\u3001\u67f4\u7ecd\u7b49\u5bf9\u5cd9\u5728\u5927\u5385\u4e4b\u5916\u3002\u53cc\u65b9\u5251\u62d4 \u5f29\u5f20\uff0c\u5927\u6709\u7acb\u523b\u52a8\u624b\u4e4b\u610f\u3002\u8001\u673d\u53ea\u80fd\u529d\u963b\u656c\u5fb7\u7b49\u4eba\u6682\u65f6\u5fcd\u8010\uff0c\u4f46\u5b87\u6587\u6210\u90fd\u5374\u5484\u5484\u903c\u4eba\uff0c\u8001\u673d \u4e5f\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fd9\u63a5\u53e3\u9053\uff1a\u201c\u8981\u5feb\u4e9b\u963b\u6b62\u4ed6\u4eec\uff0c\u7d2b\u70df\u59d1\u5a18\uff0c\u4ed6\u4eec\u90fd\u662f\u7cbe\u52c7\u4e4b\u58eb\uff0c\u4e00\u65e6\u62da\u4e0a\u547d\uff0c\u4e5d \u725b\u62d6\u4e0d\u5f00\uff0c\u4e0d\u80fd\u8ba9\u60e8\u5267\u53d1\u751f\u554a\uff01\u201d \u3000\u3000\u201c\u653e\u5fc3\uff01\u6211\u7684\u4eba\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4ed6\u4eec\u5c31\u4f1a\u5377\u65d7\u6536\u5175\uff0c\u7edd\u4e0d\u6562\u4e0d\u542c\u547d\u4ee4\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u73b0\u5728 \u8ddf\u6211\u4e00\u8d77\u8d70\u5427\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8981\u4e0d\u8981\u66ff\u5728\u4e0b\u52a0\u4e0a\u5211\u5177\uff1f\u201d \u3000\u3000\u201c\u770b\u5728\u5b9d\u513f\u59b9\u59b9\u7684\u4efd\u4e0a\uff0c\u514d\u4e86\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u9001\u7d2b\u70df\u59d1\u5a18\u79bb\u5f00\u8fd9\u91cc\uff0c\u6700\u597d\u662f\u5e76\u80a9\u800c\u884c\uff0c\u8c08\u7b11\u98ce\u751f\uff0c\u6709\u4e00\u79cd\u9001 \u5ba2\u767b\u7a0b\u7684\u79bb\u522b\u60c5\u7eea\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5a07\u7b11\u9053\uff1a\u201c\u8fd8\u8981\u5982\u6b64\u505a\u4f5c\u5417\uff1f\u201d \u3000\u3000\u201c\u907f\u514d\u9ebb\u70e6\u554a\uff01\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u5982\u679c\u6ca1\u6709\u4eba\u80fd\u77a7\u5f97\u51fa\u4f60\u662f\u62bc\u89e3\u4e8c\u516c\u5b50\u56de\u5230\u957f\u5b89\u5904\u6b7b\uff0c\u987a \u987a\u5f53\u5f53\u7684\u79bb\u5f00\u8fd9\u91cc\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\u5462\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff01\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u65e2\u7136\u8981\u8c08\u7b11\u98ce\u751f\uff0c\u90a3\u5c31\u8ba9\u5b83\u4f20\u795e\u4e00 \u4e9b\u5427\uff01\u201d \u3000\u3000\u7275\u8d77\u674e\u4e16\u6c11\u4e00\u53ea\u624b\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u4e0d\u5f97\u4e86\u5566\uff01 \u3000\u3000\u4ed9\u59ff\u7389\u5bb9\u7684\u8881\u7d2b\u70df\u7adf\u7275\u7740\u82f1\u59ff\u51e4\u76ee\u7684\u674e\u4e16\u6c11\uff0c\u7b80\u76f4\u770b\u5f97\u4eba\u8000\u9192\u751f\u82b1\u5440\uff01 \u3000\u3000\u4f46\u662f\u674e\u4e16\u6c11\u5374\u5413\u50bb\u4e86\uff01 \u3000\u3000\u8fd9\u50cf\u8bdd\u5417\uff1f\u5374\u53c8\u4e0d\u80fd\u8eb2\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u6837\u4e00\u4e2a\u753b\u9762\u51fa\u73b0\u5728\u5b87\u6587\u6210\u90fd\u548c\u5c09\u8fdf\u656c\u5fb7\u7684\u9762\u524d\uff0c\u4e0d\u7528\u529d\u8bf4\u81ea\u7136\u5c31\u6253\u4e0d\u8d77\u6765\u4e86\uff01 \u679c\u5982\u6240\u6599\u2026\u2026 \u3000\u3000\u53cc\u65b9\u6240\u6709\u4eba\u7684\u76ee\u5149\u90fd\u6295\u6ce8\u4e24\u4eba\u8eab\u4e0a\uff0c\u4e0d\u81ea\u89c9\u7684\u6536\u8d77\u4e86\u5175\u5203\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4f4e\u58f0\u9053\uff1a\u201c\u6210\u90fd\uff0c\u6211\u4eec\u8d70\uff01\u51fa\u53bb\u548c\u5218\u98de\u9e4f\u7b49\u4f1a\u5408\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u83b2\u513f\u3001\u5de7\u513f\u90fd\u62a4\u7740\u8881\u7d2b\u70df\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u671b\u7740\u674e\u4e16\u6c11\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u624d\u56de\u8fc7\u5934\u6765\uff0c\u9053\uff1a\u201c\u674e\u5148\u751f\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff1f\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u201c\u770b\u4e0a\u53bb\uff0c\u4e0d\u50cf\u662f\u654c\u6211\u76f8\u5bf9\uff0c\u6709\u4e9b\u50cf\u8c08\u60c5\u8bf4\u7231\u5440\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u4f60\u7684\u770b\u6cd5\uff0c\u53ef\u548c\u8001\u673d\u65e0\u5173\uff01\u201d \u3000\u3000\u5c09\u8fdf\u606d\u4e00\u638c\u62cd\u5728\u8111\u888b\u4e0a\uff0c\u9053\uff1a\u201c\u643a\u624b\u540c\u884c\uff0c\u7b11\u8bed\u76c8\u76c8\uff1b\u8fd9\u4e00\u70b9\u96be\u9053\u54b1\u656c\u5fb7\u8fd8\u4f1a\u770b\u9519\u5417\uff1f \u9519\u4e0d\u4e86\u7684\uff01\u201d \u3000\u3000\u674e\u6df3\u98ce\u82e6\u7b11\u4e00\u4e0b\uff0c\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u674e\u9756\u4e5f\u6ca1\u6709\u963b\u62e6\u3002 \u3000\u3000\u4ed6\u5012\u4e0d\u662f\u56e0\u4e3a\u8881\u7d2b\u70df\u3001\u674e\u4e16\u6c11\u643a\u624b\u5e76\u884c\uff0c\u8bef\u4f1a\u4e24\u4eba\u5728\u8c08\u60c5\u8bf4\u7231\uff0c\u800c\u662f\u63a5\u5230\u674e\u6df3\u98ce\u7684\u901a \u77e5\uff0c\u8981\u653e\u8fc7\u4ed6\u4e24\u4eba\u3002 \u3000\u3000\u674e\u9756\u53f3\u624b\u4ed7\u5251\uff0c\u4f2b\u7acb\u9053\u65c1\uff0c\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u591a\u4fdd\u91cd\u554a\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u674e\u9756\u5144\uff0c\u548c\u6df3\u98ce\u3001\u5929\u7f61\u4ed4\u7ec6\u7684\u7814\u7a76\u4e00\u4e0b\uff0c\u4e0d\u80fd\u8ba9\u866c\u9aef\u5ba2\u767b\u4e0a\u5e1d\u4f4d\uff01\u201d \u3000\u3000\u674e\u9756\u9ed8\u7136\u65e0\u8bed\uff0c\u6ca1\u6709\u53cd\u5bf9\uff0c\u4e5f\u6ca1\u6709\u7b54\u5e94\u3002 \u3000\u3000\u5218\u98de\u9e4f\u548c\u51cc\u4e91\u4e5f\u65e9\u5f97\u5230\u4e86\u5b87\u6587\u6210\u90fd\u7684\u901a\u77e5\uff0c\u6574\u961f\u5f85\u547d\uff0c\u7b49\u5f85\u6587\u6210\u90fd\u7684\u4fe1\u53f7\u4f20\u6765\uff0c\u7acb\u523b\u5c31 \u5411\u91cc\u51b2\u6740\u3002 \u3000\u3000\u89c1\u5b87\u6587\u6210\u90fd\u63d0\u5251\u800c\u51fa\uff0c\u7acb\u523b\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u603b\u7edf\u9886\uff01\u53ef\u8981\u6740\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u56fd\u5e08\u6765\u4e86\uff0c\u4e00\u5207\u542c\u56fd\u5e08\u5429\u5490\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u7275\u7740\u674e\u4e16\u6c11\u884c\u51fa\u8c37\u53e3\u3002 \u3000\u3000\u5979\u4f4e\u58f0\u9053\uff1a\u201c\u5218\u526f\u7edf\u9886\uff01\u201d \u3000\u3000\u201c\u98de\u9e4f\u5728\uff01\u201d \u3000\u3000\u201c\u628a\u4e8c\u516c\u5b50\u7684\u53cc\u624b\u7f1a\u8d77\u6765\uff0c\u66ff\u4ed6\u9009\u5339\u597d\u9a6c\uff0c\u5c31\u7531\u4f60\u8d1f\u8d23\u4fdd\u62a4\u4e8c\u516c\u5b50\u7684\u5b89\u5168\u3002\u201d\u8881\u7d2b\u70df \u9053\uff1a\u201c\u592a\u539f\u7559\u5b88\u4faf\u5e9c\u7684\u674e\u4e8c\u516c\u5b50\uff0c\u53ef\u662f\u94a6\u62ff\u8981\u72af\uff0c\u4e0d\u51c6\u4ed6\u53d7\u5230\u4efb\u4f55\u4f24\u5bb3\uff0c\u66f4\u4e0d\u80fd\u88ab\u4ed6\u9003 \u8d70\uff01\u201d \u3000\u3000\u5218\u98de\u9e4f\u63a5\u8fc7\u674e\u4e16\u6c11\uff0c\u6346\u4e86\u4ed6\u53cc\u624b\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u90fd\u542c\u5230\u4e86\uff0c\u5e0c\u671b\u5408\u4f5c\uff0c\u522b\u8ba9\u5218\u67d0\u4eba \u4e3a\u96be\u3002\u201d \u3000\u3000\u8bdd\u867d\u8bf4\u5f97\u5ba2\u6c14\uff0c\u4f46\u65e0\u7591\u662f\u63d0\u51fa\u4e86\u8b66\u544a\uff0c\u8981\u662f\u4e0d\u5408\u4f5c\uff0c\u90a3\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff0c\u4e0d\u4f46\u8981\u6346\u8d77\u53cc\u624b\uff0c \u8fde\u53cc\u817f\u4e5f\u8981\u6346\u8d77\u6765\u4e86\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u653e\u5fc3\uff0c\u5728\u4e0b\u662f\u7518\u613f\u53d7\u7f1a\uff1b\u7edd\u4e0d\u4f1a\u9003\u8d70\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u76ee\u7779\u8881\u7d2b\u70df\u7275\u7740\u674e\u4e16\u6c11\u7684\u624b\uff0c\u72b6\u751a\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u53c8\u5992\u53c8\u706b\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u662f\u5bab\u5983\u7684 \u8eab\u4efd\uff0c\u4e3e\u6b62\u95f4\u600e\u80fd\u5982\u6b64\u653e\u8086\uff1f\u201d \u3000\u3000\u4f46\u51fa\u4e86\u76d8\u9f99\u5c45\uff0c\u8881\u7d2b\u70df\u7adf\u628a\u674e\u4e16\u6c11\u4ea4\u7ed9\u5218\u98de\u9e4f\uff0c\u8fd8\u4ea4\u4ee3\u6346\u4e0a\u53cc\u624b\uff0c\u4e0d\u7981\u6697\u53eb\u60ed\u6127\uff0c\u5979\u516c \u4e8b\u516c\u529e\uff0c\u7275\u7740\u674e\u4e16\u6c11\u7684\u624b\uff0c\u662f\u6015\u4ed6\u9003\u8d70\uff0c\u4e5f\u7792\u8fc7\u4e86\u654c\u6211\u8033\u76ee\uff0c\u5b9e\u5728\u662f\u51a4\u8bec\u5979\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u8881\u7d2b\u70df\u6ca1\u6709\u4ed6\u60f3\u5f97\u90a3\u4e48\u597d\uff0c\u7275\u7740\u674e\u4e16\u6c11\u7684\u624b\u65f6\uff0c\u8881\u7d2b\u59fb\u5fc3\u4e2d\u5e76\u65e0\u4ec0\u4e48\u6742\u5ff5\uff0c\u4f46\u63e1\u7d27 \u4e86\u5374\u7a81\u7136\u6026\u7136\u5fc3\u52a8\u3002 \u3000\u3000\u539f\u6765\u8881\u7d2b\u70df\u662f\u7b2c\u4e00\u6b21\u7275\u4f4f\u4e86\u7537\u4eba\u7684\u624b\u3002 \u3000\u3000\u968b\u7080\u5e1d\u63e1\u7740\u5979\u7684\u7389\u624b\u5f88\u591a\u6b21\uff0c\u4f46\u6bcf\u6b21\u90fd\u662f\u968b\u7080\u5e1d\u7275\u5979\uff0c\u4e0d\u662f\u5979\u53bb\u7275\u4eba\u3002 \u3000\u3000\u63e1\u4f4f\u4e86\uff0c\u5c31\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\uff0c\u518d\u60f3\u60f3\u674e\u4e16\u6c11\u7684\u82f1\u4fca\u633a\u79c0\uff0c\u8881\u59d1\u5a18\u8fde\u770b\u4e5f\u4e0d\u6562\u518d\u770b\u674e\u4e16\u6c11\u4e00 \u773c\u4e86\u3002 \u3000\u3000\u5979\u6108\u60f3\u538b\u4e0b\u5fc3\u4e2d\u5fae\u52a8\u7684\u60c5\u611f\uff0c\u5374\u6108\u611f\u5230\u5fc3\u4e2d\u6ce2\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7684\u624b\u4f3c\u662f\u4f1a\u653e\u7535\uff0c\u7535\u5f97\u7d2b\u70df\u59d1\u5a18\u5fc3\u8df3\u4e0d\u5df2\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u6697\u81ea\u9a82\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u4ece\u6ca1\u5bf9\u7537\u4eba\u52a8\u8fc7\u5fc3\u554a\uff01\u4eca\u591c\u600e\u4f1a\u795e\u4e0d\u5b88\u820d\uff1f\u201d \u3000\u3000\u968b\u7080\u5e1d\u62b1\u7740\u5979\u53c8\u4eb2\u53c8\u6293\uff0c\u8881\u7d2b\u70df\u4e5f\u6ca1\u6709\u52a8\u8fc7\u60c5\u6f6e\uff0c\u56e0\u4e3a\u5979\u7cbe\u4fee\u672f\u6cd5\uff0c\u65e9\u5df2\u7ecf\u662f\u5fc3\u5982\u6b62\u6c34 \u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u53ea\u4e0d\u8fc7\u7275\u4f4f\u7537\u4eba\u7684\u624b\u7f62\u4e86\uff01\u600e\u4e48\u4f1a\u6709\u5f02\u6837\u7684\u611f\u89c9\u5462\uff1f \u3000\u3000\u5979\u8981\u63a9\u9970\uff0c\u4e0d\u80fd\u8ba9\u674e\u4e16\u6c11\u53d1\u89c9\u4ec0\u4e48\uff1f\u66f4\u4e0d\u80fd\u8ba9\u522b\u4eba\u77a7\u51fa\u5979\u5fc3\u795e\u4e0d\u5b81\u3002 \u3000\u3000\u662f\u4e86\uff0c\u8fd9\u4e2a\u7537\u4eba\u5c31\u662f\u4e0e\u4f17\u4e0d\u540c\uff0c\u5426\u5219\u4ee5\u8881\u5b9d\u513f\u7684\u4eba\u95f4\u6b8a\u8272\uff0c\u600e\u4f1a\u4e3a\u4ed6\u52a8\u5fc3\uff1f\u800c\u4e14\u53c8\u7231\u5f97 \u90a3\u4e48\u6df1\uff0c\u7518\u613f\u727a\u7272\u4e00\u5207\u53bb\u5e2e\u52a9\u4ed6\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u60f3\uff0c\u5fc3\u6ce2\u5c31\u6fc0\u52a8\u5f97\u66f4\u5389\u5bb3\u4e86\u3002 \u3000\u3000\u628a\u674e\u4e16\u6c11\u4ea4\u7ed9\u4e86\u5218\u98de\u9e4f\uff0c\u597d\u50cf\u7529\u6389\u4e86\u4e00\u4e2a\u70eb\u624b\u7684\u5c71\u828b\uff0c\u5982\u91ca\u91cd\u8d1f\u4e00\u822c\uff0c\u8f6c\u5934\u5c31\u8d70\uff0c\u8fde\u770b \u4e5f\u672a\u518d\u770b\u674e\u4e16\u6c11\u4e00\u773c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u9a6c\u5f53\u5148\uff0c\u5411\u524d\u98de\u9a70\u3002 \u3000\u3000\u5979\u8981\u8fce\u7740\u62c2\u9762\u6653\u98ce\uff0c\u4e5f\u597d\u5439\u6563\u5fc3\u4e2d\u4e00\u80a1\u60c5\u6101\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u63d0\u9a6c\u7f30\uff0c\u8ffd\u4e0a\u4e86\u8881\u7d2b\u70df\u3002 \u3000\u3000\u4ed6\u4f4e\u58f0\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u73b0\u5728\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u56de\u957f\u5b89\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5317\u5f81\u7684\u76ee\u7684\u5df2\u7ecf\u8fbe\u6210\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fd9\u9053\uff1a\u201c\u5c31\u6349\u4e00\u4e2a\u674e\u4e16\u6c11\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u662f\u7684\uff01\u738b\u6c14\u6240\u949f\u7684\uff0c\u53ea\u6709\u4e00\u4eba\uff0c\u54b1\u4eec\u628a\u4ed6\u6349\u4f4f\u4e86\uff0c\u81ea\u7136\u53ef\u4ee5\u56de\u957f\u5b89\u590d\u65e8 \u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u670d\u6c14\uff1b\u4f46\u5374\u53c8\u4e0d\u6562\u8fa9\u9a73\uff0c\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4f1a\u8ba9\u6211 \u4eec\u5e26\u8d70\u674e\u4e16\u6c11\u5417\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u4e0d\u613f\u610f\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u674e\u4e16\u6c11\u8bf4\u670d\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u674e\u4e16\u6c11\u81ea\u613f\u8ddf\u6211\u4eec\u8d70\u4e86\uff1f\u201d\u5b87\u6587\u6210\u90fd\u6709\u4e9b\u832b\u7136\u7684\u9053\uff1a\u201c\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\uff0c\u4ed6\u4eec \u4e5f\u540c\u610f\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u674e\u4e16\u6c11\u7684\u6b7b\u515a\uff0c\u800c\u4e14\u90fd\u662f\u5b81\u6298\u4e0d\u5f2f\u7684\u4eba\u3002\u8bf4\u670d\u4ed6\u4eec\uff0c\u5f53\u7136\u662f\u4e0d\u53ef \u80fd\u4e86\u3002\u6240\u4ee5\u6211\u624d\u7565\u65bd\u5c0f\u8ba1\u3002\u521a\u624d\u4f60\u4eec\u90fd\u770b\u5230\u4e86\uff0c\u656c\u5fb7\u90a3\u822c\u4eba\u81ea\u7136\u4e5f\u88ab\u641e\u8ff7\u7cca\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6697\u5fd6\u9053\uff1a\u539f\u6765\u5982\u6b64\uff01 \u3000\u3000\u4ed6\u53e3\u4e2d\u5374\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u56fd\u5e08\u9ad8\u660e\uff0c\u5175\u4e0d\u8840\u5203\u3001\u8349\u6728\u4e0d\u60ca\u7684\u5e26\u8d70\u4e86\u674e\u4e16\u6c11\u3002\u4e0d\u8fc7\uff0c\u5c5e \u4e0b\u8fd8\u662f\u6709\u4e00\u70b9\u60f3\u4e0d\u901a\uff1f\u201d \u3000\u3000\u201c\u60f3\u4e0d\u901a\uff0c\u4f60\u5c31\u95ee\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u90fd\u80fd\u7ed9\u4e2a\u6ee1\u610f\u7684\u89e3\u7b54\uff01\u201d \u3000\u3000\u201c\u674e\u4e16\u6c11\u7684\u5b9e\u529b\u4e0d\u5f31\uff0c\u4ed6\u53c8\u4e3a\u4ec0\u4e48\u613f\u968f\u6211\u4eec\u56de\u957f\u5b89\uff1f\u201d\u5b87\u6587\u6210\u90fd\u95ee\u9053\uff1a\u201c\u53c8\u529d\u963b\u4ed6\u7684\u624b \u4e0b\u4e0d\u8981\u963b\u62e6\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u4ed6\u60f3\u6d3b\u7740\u518d\u89c1\u8881\u5b9d\u513f\u4e00\u9762\u2026\u2026\u201d \u3000\u3000\u201c\u5b9d\u5983\u5a18\u5a18\uff1f\u201d\u5b87\u6587\u6210\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5979\u548c\u674e\u4e16\u6c11\u6709\u4ec0\u4e48\u7275\u8fde\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7275\u8fde\u53ef\u5927\u4e86\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4f46\u4f60\u8981\u5bf9\u6211\u627f\u8bfa\u3002\u4e0d\u5f97\u6cc4\u6f0f\u51fa\u53bb\uff01\u201d \u3000\u3000\u201c\u662f\uff0c\u6210\u90fd\u7edd\u5bf9\u4fdd\u5bc6\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u5c06\u8881\u5b9d\u513f\u4e0e\u674e\u4e16\u6c11\u7684\u60c5\u4fa3\u5173\u7cfb\u8bf4\u4e86\u4ed6\u542c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5374\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff01 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4ee5\u7687\u4e0a\u7684\u624d\u60c5\uff0c\u53ea\u8981\u628a\u4e00\u534a\u7cbe\u795e\u653e\u5728\u56fd\u4e8b\u4e0a\uff0c\u4e0d\u987b\u4e00\u4e24\u5e74\u65f6\u95f4\uff0c\u5929\u4e0b\u5927 \u6cbb\u77e3\uff01\u201d \u3000\u3000\u201c\u6210\u90fd\uff0c\u6211\u4e5f\u6709\u6b64\u770b\u6cd5\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u53ea\u662f\u60f3\u4e0d\u901a\u554a\uff01\u4ed6\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u80af\u5206\u51fa\u8fd9\u4e00\u534a\u7cbe \u795e\u5462\uff1f\u56fd\u5f3a\u6c11\u5bcc\uff0c\u5929\u4e0b\u5927\u6cbb\uff0c\u518d\u6765\u4eab\u4e50\u65f6\u72b9\u672a\u665a\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u795e\u60c5\u8083\u7136\u7684\u9053\uff1a\u201c\u53ea\u6015\u4e0d\u662f\u5982\u6b64\u5427\uff01\u7687\u4e0a\u975e\u5e38\u4e86\u89e3\u81ea\u5df1\u7684\u5904\u5883\uff0c\u800c\u6211\u5374\u80fd\u4e86\u89e3 \u4ed6\u7684\u5fc3\u60c5\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u4e86\u89e3\uff01\u600e\u4e48\u4f1a\u5462\uff1f\u8fd9\u4e00\u70b9\u6211\u5c31\u60f3\u4e0d\u900f\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u70b9\u62e8\u6211\uff0c\u800c\u4e14\u4ed6\u8bf4\u5f97\u975e\u5e38\u6709\u7406\uff0c\u6211\u4e5f\u4e0d\u4fbf\u591a\u8a00\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4f60\u4eec\u541b\u81e3\u4e4b\u95f4\u5fc3\u610f\u76f8\u8bb8\uff0c\u65e0\u8bdd\u4e0d\u8c08\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f46\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u6211\u5c31 \u60f3\u4e0d\u901a\u4ed6\u5982\u4f55\u80fd\u8bf4\u670d\u4f60\uff1f\u6210\u90fd\uff0c\u4e00\u65e6\u6c5f\u5c71\u53d8\u989c\u8272\uff0c\u540e\u5bab\u54ea\u6709\u963f\u5a07\u58f0\uff1f\u8fd9\u4e00\u70b9\u4ed6\u5c31\u60f3\u4e0d\u901a\u5417\uff1f \u4ed6\u9009\u4e86\u5929\u4e0b\u7f8e\u5973\uff0c\u7ade\u8273\u957f\u5b89\u5bab\u5ef7\uff0c\u4eab\u53d7\u5962\u534e\uff1b\u8fd9\u90fd\u662f\u4ed6\u8eab\u4e3a\u541b\u4e3b\u7684\u7f18\u6545\uff0c\u4e00\u65e6\u4e27\u5931\u4e86\u541b\u4e3b\u4e4b \u4f4d\uff0c\u8fd9\u4e9b\u4e8b\u90fd\u5c06\u5982\u4e91\u70df\u8fc7\u773c\uff0c\u5316\u4f5c\u4e00\u573a\u6625\u68a6\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u7687\u4e0a\u4e5f\u60f3\u5230\u4e86\uff0c\u800c\u4e14\u60f3\u5f97\u66f4\u900f\u5f7b\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\u542c\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u65e2\u77e5\u6c5f\u5c71\u5982\u753b\uff0c\u53c8\u7231\u540e\u5bab\u591a\u5a07\uff0c\u4e3a\u4ec0\u4e48 \u4e0d\u7528\u5fc3\u53bb\u4fdd\u62a4\u5b83\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6b63\u8272\u9053\uff1a\u201c\u53ef\u4ee5\u8bf4\uff0c\u4f46\u6211\u4e5f\u8981\u6c42\u56fd\u5e08\u4fdd\u5bc6\uff0c\u4e0d\u80fd\u6cc4\u6f0f\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u7b54\u5e94\uff01\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u541b\u81e3\u4e4b\u95f4\u79d8\u5bc6\uff0c\u6bd4\u6211\u4eec\u7684\u6795\u8fb9\u7ec6\u8bed\u8fd8\u8981\u591a\u5462\uff01\u201d \u3000\u3000\u201c\u4e8b\u60c5\u6d89\u53ca\u5230\u5b9d\u5983\u5a18\u5a18\u548c\u56fd\u5e08\uff0c\u6210\u90fd\u81ea\u662f\u4e0d\u6562\u8f7b\u6613\u51fa\u53e3\uff0c\u8bf7\u56fd\u5e08\u5148\u6055\u6211\u5510\u7a81\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u597d\u5427\uff01\u653e\u5fc3\u8bf4\u4e86\uff0c\u6211\u4e0d\u602a\u7f6a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5b9d\u5983\u5a18\u5a18\u4ee5\u4eba\u95f4\u7edd\u8272\u5165\u957f\u5b89\uff0c\u522b\u6709\u6240\u56fe\u3002\u56fd\u5e08\u4ee5\u5929\u4e0a\u4ed9\u5b50\u4e0b\u51e1\u6765\uff0c\u53ea\u662f \u56e0\u4e00\u80a1\u597d\u80dc\u4e4b\u5fc3\u3002\u9020\u6210\u8fd9\u6837\u7684\u60c5\u52bf\uff0c\u90fd\u56e0\u4ed6\u7eb5\u6b32\u660f\u5eb8\u3002\u4e00\u65e6\u4ed6\u6e05\u9192\u4e86\uff0c\u628a\u56fd\u4e8b\u6cbb\u7406\u5f97\u4e95\u7136\u6709 \u5e8f\u4e86\uff0c\u7b2c\u4e00\u4e2a\u7559\u4e0d\u4f4f\u7684\u5c31\u662f\u56fd\u5e08\uff0c\u5b9d\u5983\u5a18\u5a18\u4e5f\u4e0d\u4f1a\u7559\u5728\u957f\u5b89\u5bab\u5ef7\u3002\u5931\u53bb\u4e86\u4e24\u4f4d\u7684\u989c\u8272\uff0c\u957f\u5b89 \u7684\u7687\u5bab\u5c31\u518d\u65e0\u7559\u604b\u4e4b\u5904\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u540e\u5bab\u4f73\u4e3d\u4e09\u5343\uff0c\u5931\u53bb\u4e24\u4e2a\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u6210\u90fd\u4e5f\u8fd9\u6837\u529d\u8fc7\u7687\u4e0a\uff0c\u6682\u5fcd\u4e00\u65f6\u4e4b\u75db\u2026\u2026\u201d \u3000\u3000\u201c\u7687\u4e0a\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u95ee\u51fa\u53e3\uff0c\u5df2\u89c9\u5f97\u95ee\u5f97\u592a\u591a\u4e86\uff0c\u53ef\u662f\u5df2\u65e0\u6cd5\u6536\u56de\u3002 \u3000\u3000\u201c\u6210\u90fd\u4e0d\u6562\u8bf4\uff0c\u4e0d\u6562\u8bf4\uff0c\u4e0d\u6562\u8bf4\u2026\u2026\u201d \u3000\u3000\u4e00\u8fde\u51e0\u4e2a\u201c\u4e0d\u6562\u8bf4\u201d\u3002 \u3000\u3000\u8fd9\u53c8\u5f15\u8d77\u4e86\u8881\u7d2b\u70df\u597d\u5947\u4e4b\u5ff5\u3002 \u3000\u3000\u5979\u6697\u5fd6\u9053\uff1a\u6211\u4e0d\u4fe1\u7687\u4e0a\u4f1a\u628a\u5e8a\u4e0a\u7684\u4e8b\u544a\u8bc9\u5927\u81e3\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u662f\u5168\u65e0\u987e\u5fcc\uff0c\u8981\u4ed6\u8bf4\u8bf4\u4f55 \u59a8\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u65e2\u7136\u8bf4\u4e86\uff0c\u5c31\u4e00\u80a1\u8111\u7684\u8bf4\u51fa\u6765\u5417\uff01\u201d \u3000\u3000\u201c\u662f\u56fd\u5e08\u903c\u8feb\uff0c\u53ef\u4e0d\u662f\u6210\u90fd\u4e0d\u656c\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u6697\u5fd6\u9053\uff1a\u96be\u9053\u98ce\u6d41\u7687\u5e1d\u771f\u7684\u628a\u5e8a\u7b2c\u95f4\u7684\u4e8b\u60c5\u4e5f\u544a\u8bc9\u4e86\u4eba\u5417\uff1f \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u6253\u8f6c\uff0c\u5b87\u6587\u6210\u90fd\u5df2\u7ecf\u5f00\u4e86\u53e3\u3002 \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u968f\u884c\u7684\u94c1\u9a91\u90fd\u5728\u6570\u5341\u4e08\u5916\uff0c\u4e0d\u77e5\u662f\u6709\u5fc3\u9003\u907f\u5462\u8fd8\u662f\u65e0\u610f\u95f4\u628a\u8ddd\u79bb\u62c9\u5f00\uff0c\u4f46\u80af \u5b9a\u7684\u4e00\u70b9\u662f\u7edd\u5bf9\u542c\u4e0d\u5230\u4e24\u4eba\u4f4e\u58f0\u4ea4\u8c08\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4ecd\u7136\u662f\u4e0d\u6562\u770b\u7740\u8881\u7d2b\u70df\u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\u53bb\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u8bf4\uff0c\u81ea\u548c\u5b9d\u5983\u5a18\u5a18\u3001\u56fd\u5e08\u6709\u8fc7\u7537\u5973\u6b22\u7231\u4e4b\u540e\uff0c\u5176\u4ed6\u7684\u5ad4\u5983\u3001\u592b \u4eba\u5c31\u5473\u540c\u56bc\u8721\u4e86\uff0c\u5c31\u7b97\u98ce\u534e\u64a9\u4eba\u7684\u8427\u7687\u540e\uff0c\u4e5f\u662f\u96be\u53ca\u4e07\u4e00\uff01\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u8fde\u7687\u540e\u7684\u4e8b\u4e5f\u6562\u544a\u8bc9\u4f60\u5440\uff01\u771f\u662f\u6ee5\u5f97\u4e0d\u50cf\u8bdd\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5550\u4e86\u4e00\u58f0\u9a82\u7740\uff01\u5979\u5fc3\u4e2d\u5374\u6697\u81ea\u5fd6\u9053\uff1a\u5b9d\u513f\u5982\u4f55\uff1f\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u6bcf\u6b21\u88ab\u4ed6\u62e5\u62b1\u5360 \u6709\u65f6\uff0c\u4ece\u672a\u7528\u8fc7\u672f\u6cd5\u3002\u60f3\u4e0d\u901a\uff0c\u548c\u522b\u7684\u5973\u4eba\u4f1a\u6709\u4ec0\u4e48\u4e0d\u540c\u5462\uff1f \u3000\u3000\u4f46\u60f3\u5230\u968b\u7080\u5e1d\u62b1\u7d27\u5979\u90a3\u7f8e\u4e3d\u7684\u80f4\u4f53\u65f6\uff0c\u8138\u4e0a\u5982\u9189\u5982\u75f4\u7684\u795e\u8272\uff0c\u5374\u6b32\u6b7b\u6b32\u4ed9\u7684\u8868\u60c5\uff0c\u786e\u662f \u4eba\u95f4\u81f3\u4e50\u7684\u5f62\u8272\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u7981\u8138\u4e0a\u4e00\u70ed\uff0c\u63d0\u9a6c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u80c6\u5927\u7684\u5b87\u6587\u6210\u90fd\u7adf\u6562\u7eb5\u9a6c\u8ffd\u4e86\u4e0a\u53bb\u3002\u4ed6\u60f3\u591a\u770b\u4e00\u773c\u8881\u7d2b\u70df\u90a3\u7ea2\u6655\u672a\u9000\u3001\u5a07\u7f9e\u4e0d\u80dc\u7684\u795e \u60c5\u3002 \u3000\u3000\u53ef\u771f\u662f\u8272\u80c6\u5305\u5929\u554a\uff01 \u3000\u3000\u201c\u56fd\u5e08\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u7687\u4e0a\u8bf4\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u6211\u4e0d\u8981\u518d\u542c\u4e0b\u53bb\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u53e3\u6ca1\u906e\u62e6\uff0c\u771f\u4e0d\u77e5\u8fd8\u4f1a\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u7f9e\u6b7b \u4eba\u4e86\uff01\u201d \u3000\u3000\u201c\u7687\u4e0a\u8bf4\u2026\u2026\u5929\u4e0b\u5927\u6cbb\uff0c\u5148\u8d70\u7684\u662f\u56fd\u5e08\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4ee5\u56fd\u5e08\u4e4b\u80fd\uff0c\u4ed6\u5c3d\u51fa\u4e3e\u56fd\u4e4b \u5175\uff0c\u4e5f\u65e0\u6cd5\u5bfb\u89c5\u5230\u56fd\u5e08\u7684\u8e2a\u8ff9\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u771f\u7684\u8fd9\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u6210\u90fd\u8f6c\u8ff0\u7687\u4e0a\u7684\u8bdd\uff0c\u4e00\u5b57\u4e0d\u51cf\uff0c\u4e00\u5b57\u672a\u6539\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c \u4e94 \u56de\u3000\u9752\u9f99\u817e\u98de",content:(0,n.jsx)(n.Fragment,{children:"\u201c\u4e00\u5929\u5230\u665a\u80e1\u601d\u4e71\u60f3\uff0c\u53ef\u771f\u662f\u70e6\u4eba\u5f97\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u79bb\u5f00\u957f\u5b89\u5bab\u5ef7\u7684\u662f\u5b9d\u5983\u5a18\u5a18\uff0c\u7687\u4e0a\u8bf4\u4e24\u4f4d\u90fd\u8d70\u4e86\uff0c\u4ed6\u8fd8\u6d3b\u5f97\u6709\u4ec0 \u4e48\u610f\u601d\uff1f\u5982\u679c\u90a3\u65f6\u76f8\u601d\u75c5\u8d77\uff0c\u8fd8\u4e0d\u5982\u62fc\u7740\u56fd\u5b58\u5bb6\u4ea1\uff0c\u591a\u7559\u4f4f\u4e24\u4f4d\u4e00\u4e9b\u65f6\u95f4\u3002\u201d\u8881\u7d2b\u70df\u53f9\u606f\u4e00 \u58f0\uff0c\u9053\uff1a\u201c\u771f\u662f\u4e2a\u98ce\u6d41\u7684\u7687\u5e1d\u554a\uff01\u5b9d\u513f\u5584\u826f\uff0c\u867d\u662f\u522b\u6709\u6240\u56fe\uff0c\u4f46\u5374\u4e00\u76f4\u4e0d\u80af\u4e0b\u624b\u3002\u5509\uff01\u53e4\u5f80 \u4eca\u6765\uff0c\u591a\u5c11\u4f4d\u671d\u4ee3\u79fb\u8f6c\uff0c\u4e0d\u8bba\u662f\u5978\u4f5e\u7be1\u4f4d\u3001\u7ea2\u989c\u8bef\u56fd\uff0c\u5f53\u4e8b\u4eba\u5927\u90fd\u662f\u5728\u75f4\u8ff7\u4e2d\u6e21\u8fc7\uff0c\u50cf\u7687\u4e0a \u8fd9\u6837\u5fc3\u77e5\u809a\u660e\u4ed8\u8bf8\u4e00\u63b7\u7684\u5f53\u4e8b\u4eba\uff0c\u5012\u662f\u5c11\u89c1\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u201c\u4e0d\u80fd\u602a\u7687\u4e0a\uff0c\u5c31\u662f\u6210\u90fd\u8eab\u5904\u5176\u5883\uff0c\u4e5f\u4e0d\u77e5\uff1f\u201d \u3000\u3000\u8fd9\u5c31\u6709\u4e9b\u8fc7\u5206\u4e86\uff0c\u76f4\u63a5\u7684\u8868\u8fbe\u4e86\u5fc3\u4e2d\u7684\u601d\u6155\u4e4b\u60c5\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u6ca1\u6709\u751f\u6c14\uff0c\u5979\u8eab\u5165\u7ea2\u5c18\uff0c\u9010\u6e10\u4e86\u89e3\u4eba\u6027\u4e2d\u6709\u5f88\u591a\u7684\u7f3a\u70b9\uff0c\u597d\u59d8\u6076\u5ab8\u662f\u5176\u4e2d\u4e4b \u4e00\uff0c\u4f46\u968b\u7080\u5e1d\u5728\u8fd9\u65b9\u9762\u7279\u522b\u7684\u5f3a\u70c8\u7f62\u4e86\u3002 \u3000\u3000\u65e2\u7136\u662f\u7537\u4eba\u4eec\u90fd\u7231\u7f8e\u5973\uff0c\u5b87\u6587\u6210\u90fd\u7684\u601d\u6155\u4e4b\u5fc3\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\u7f62\u4e86\uff01\u53ea\u8981\u4e0d\u662f\u76f4\u63a5\u7684\u4fb5 \u72af\uff0c\u7531\u4ed6\u53bb\u5427\uff01 \u3000\u3000\u8f6c\u5ff5\u81f3\u6b64\uff0c\u7a81\u7136\u60f3\u5230\u4e86\u674e\u4e16\u6c11\uff0c\u53ea\u4ece\u90a3\u4f38\u624b\u4e00\u7275\uff0c\u5c31\u50cf\u5728\u5979\u5fc3\u4e2d\u64ad\u79cd\u4e86\u4e00\u9897\u79cd\u5b50\uff0c\u5f88\u96be \u518d\u628a\u5b83\u8d77\u51fa\u6765\u7f6e\u8eab\u4e8b\u5916\u4e86\u3002 \u3000\u3000\u5fcd\u4e0d\u4f4f\u5411\u540e\u770b\u53bb\u2026\u2026\u4f46\u89c1\u4e00\u9a91\u5feb\u9a6c\u76f4\u5411\u4e24\u4eba\u5954\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u52d2\u4f4f\u9a6c\u7f30\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8427\u96e8\uff0c\u4ec0\u4e48\u4e8b\u554a\uff01\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u6211\u4eec\u88ab\u4eba\u8ffd\u8e2a\u4e86\uff0c\u6765\u4eba\u7684\u6b66\u529f\u975e\u5e38\u9ad8\uff0c\u4ed6\u4eec\u6ca1\u6709\u9a91\u9a6c\uff0c\u5374\u80fd\u4e00\u76f4\u8ffd\u5728\u8eab\u540e\uff0c \u4fdd\u6301\u7740\u4e00\u5b9a\u7684\u8ddd\u79bb\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u53ef\u662f\u592a\u539f\u4faf\u5e9c\u4e2d\u4eba\uff1f\u4ed6\u4eec\u7684\u9ad8\u624b\u3001\u4eba\u624d\u5927\u90fd\u96c6\u4e2d\u5728\u76d8\u9f99 \u8c37\u4e2d\uff0c\u80af\u653e\u6211\u4eec\u51fa\u6765\uff0c\u5e94\u8be5\u662f\u4e0d\u4f1a\u8ffd\u8e2a\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\u8ffd\u8e2a\u8005\u53e6\u6709\u5176\u4eba\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e5f\u8bb8\u514d\u4e0d\u4e86\u4e00\u573a\u6076\u6218\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5583\u5583\u9053\uff1a\u201c\u4f1a\u662f\u8c01\u5462\uff1f\u5403\u4e86\u718a\u5fc3\u8c79\u80c6\u5566\uff01\u201d \u3000\u3000\u201c\u866c\u9aef\u5ba2\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u56fa\u6267\u3001\u56a3\u5f20\uff0c\u8981\u6740\u674e\u4e16\u6c11\u3002 \u3000\u3000\u65e2\u7136\u4e00\u6b21\u6ca1\u6740\u6b7b\uff0c\u81ea\u7136\u4f1a\u6709\u7b2c\u4e8c\u6b21\u3001\u7b2c\u4e09\u6b21\u7684\u8ffd\u6740\u4e0b\u53bb\uff0c\u4e00\u76f4\u5230\u6740\u6b7b\u4e3a\u6b62\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\uff01\u6211\u4eec\u5e94\u8be5\u5982\u4f55\u5462\uff1f\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4f55\u4e0d\u8ba9\u4ed6\u4eec\u4e8c\u864e\u76f8\u6597\uff0c\u6211\u4eec\u5750\u89c2\u6210\u8d25\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u653e\u4e86\u674e\u4e16\u6c11\uff01\u65e2\u7136\u88ab\u4ed6\u8ffd\u4e0a\u4e86\uff0c\u653e\u4eba\u5c31\u662f\u8ba9\u4ed6\u53bb\u6b7b\u3002\u4eba\u65e2\u5728\u6211\u4eec \u624b\u4e2d\uff0c\u6211\u4eec\u5c31\u5e94\u8be5\u4fdd\u62a4\u3002\uff0e\u866c\u9aef\u5ba2\u867d\u975e\u738b\u6c14\u6240\u949f\u7684\u4eba\uff0c\u4f46\u5374\u662f\u53cd\u968b\u57fa\u4e1a\u4e2d\uff0c\u5175\u591a\u5c06\u5e7f\u6700\u5f3a\u5927 \u7684\u4e00\u80a1\u529b\u91cf\uff0c\u65e9\u665a\u90fd\u514d\u4e0d\u4e86\u4e00\u573a\u5927\u51b3\u6218\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\uff0c\u8fd9\u4e2a\u53d8\u5316\u662f\u4e0d\u662f\u674e\u6df3\u98ce\u7b49\u6709\u610f\u7684\u7eb5\u5bb9\u5462\uff1f\u8ba9\u6211\u4eec\u5148\u548c\u866c\u9aef\u5ba2\u62da\u4e2a\u4e24\u8d25\u4ff1\u4f24\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u611f\u89c9\u5230\u5b87\u6587\u6210\u90fd\u4f3c\u56fe\u5728\u81ea\u4f5c\u4e3b\u5f20\u3002 \u3000\u3000\u5979\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u5c06\u519b\uff0c\u5e94\u8be5\u5982\u4f55\u5904\u7f6e\u674e\u4e16\u6c11\uff1f\u5c31\u7531\u9601\u4e0b\u4f5c\u4e2a\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u201c\u591a\u8c22\u56fd\u5e08\uff0c\u6210\u90fd\u89c9\u5f97\u5982\u679c\u5e26\u7740\u674e\u4e16\u6c11\u7684\u516d\u9633\u9b41\u9996\u56de\u957f\u5b89\u53bb\uff0c\u5e94\u8be5\u662f\u6bd4\u5e26\u4e2a\u6d3b\u4eba\u66f4\u5bb9 \u6613\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u65e0\u610f\u548c\u5c06\u519b\u4e89\u8bba\u674e\u4e16\u6c11\u7684\u751f\u6b7b\uff01\u53ea\u8981\u5c06\u519b\u80fd\uff0c\u5411\u7687\u4e0a\u4ea4\u4ee3\uff0c\u90a3\u5c31\u4efb\u7531\u5c06 \u519b\u5904\u7406\u4e86\u3002\u201d\uff0e\u5b87\u6587\u6210\u90fd\u6709\u7740\u975e\u5e38\u610f\u5916\u4e4b\u611f\uff0c\u539f\u60f3\u8881\u7d2b\u70df\u4e00\u5b9a\u4f1a\u5c3d\u529b\u7ef4\u6301\u674e\u4e16\u6c11\u7684\u6027\u547d\uff0c\u5374 \u4e0d\u6599\u5979\u5bf9\u674e\u4e16\u6c11\u7684\u751f\u6b7b\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u5e26\u4e00\u9897\u9996\u7ea7\u56de\u53bb\uff0c\u5f53\u7136\u4e0d\u5982\u5e26\u4e00\u4e2a\u5927\u6d3b\u4eba\u597d\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u5934\u767e\u8f6c\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u5e26\u4e00\u4e2a\u6d3b\u7684\u674e\u4e16\u6c11\uff0c\u624d\u80fd\u5411\u7687\u4e0a\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u4e00\u98a6\u79c0\u7709\uff0c\u9053\uff1a\u201c\u5c06\u519b\uff0c\u4f60\u7a76\u7adf\u5728\u60f3\u4ec0\u4e48\u554a\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6211\u53bb\u4ea4\u4ee3\u4ed6\u4eec\uff0c\u597d\u597d\u7684\u4fdd\u62a4\u674e\u4e16\u6c11\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u7eb5\u9a6c\u5411\u7530\u5f53\u7b49\u8fce\u53bb\u3002 \u3000\u3000\u8427\u96e8\u7d27\u968f\u5728\u5b87\u6587\u6210\u90fd\u8eab\u540e\u3002 \u3000\u3000\u671b\u7740\u5b87\u6587\u6210\u90fd\u7684\u80cc\u5f71\uff0c\u8881\u7d2b\u70df\u51b7\u51b7\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4ed6\u77a7\u51fa\u4e86\u4ec0\u4e48\uff1f\u53c8\u51c6\u5907\u8bd5\u63a2\u4ec0\u4e48\uff1f \u5218\u98de\u9e4f\u7b49\u4f3c\u662f\u5df2\u8b66\u89c9\u5230\u6709\u4eba\u8ffd\u8e2a\uff0c\u5bf9\u674e\u4e16\u6c11\u91c7\u53d6\u4e86\u4e25\u683c\u7684\u4fdd\u62a4\uff0c\u7edf\u7387\u7684\u4e94\u5341\u540d\u94c1\u9a91\u52c7\u58eb\u5206\u4e09 \u5c42\u628a\u674e\u4e16\u6c11\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u3001\u8427\u96e8\u7edf\u7387\u7684\u6b66\u58eb\u53c8\u5728\u5915\u6e4e\u91cd\u91cd\u5730\u62a4\u536b\u7740\uff0c\u4fdd\u62a4\u4e4b\u5bc6\uff0c\u4e5f\u662f\u674e\u4e16\u6c11\u4ece\u672a\u7ecf\u5386 \u8fc7\u7684\u5b89\u6392\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u98de\u9e4f\uff0c\u53d1\u89c9\u4e86\u51e0\u4e2a\u4eba\u8ffd\u8e2a\u4e0b\u6765\uff1f\u201d \u3000\u3000\u968f\u884c\u56db\u5927\u526f\u7edf\u9886\u4e2d\uff0c\u4ee5\u5218\u98de\u9e4f\u6c5f\u6e56\u7ecf\u9a8c\u6700\u4e3a\u4e30\u5bcc\u3002 \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a\u201c\u8be6\u7ec6\u7684\u8ffd\u8e2a\u4eba\u6570\uff0c\u6211\u4e0d\u77e5\u9053\uff1b\u81f3\u5c11\u5728\u4e24\u4eba\u4ee5\u4e0a\uff0c\u6211\u4eec\u91cd\u91cd\u62a4\u536b\u674e\u4e16\u6c11\uff0c\u662f \u51c6\u5907\u5f15\u4ed6\u51fa\u6765\uff0c\u975e\u6253\u4e0d\u53ef\uff0c\u5c31\u4e0d\u5982\u65e9\u4e9b\u6253\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u98de\u9e4f\uff0c\u4f9d\u4f60\u770b\u2026\u2026\u8ffd\u6765\u7684\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\u662f\u592a\u539f\u4faf\u5e9c\u7684\u4eba\uff0c\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u5c31\u7b97\u8981\u8ffd\u4e0a\u6765\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u6211\u4eec\u53d1 \u89c9\u5230\u4ed6\u4eec\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u592a\u539f\u7559\u5b88\u4faf\u5e9c\u4e2d\u7684\u4eba\uff0c\u90a3\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u4eca\u5929\u4e0b\u6709\u4e24\u80a1\u6700\u5f3a\u7684\u53cd\u5bf9\u529b\u91cf\u3002\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u4e00\u80a1\u6d1b\u9633\uff0e\u7684\u738b\u4e16\u5145\uff0c\u4e00\u80a1\u662f\u795e\u51fa \u9b3c\u6ca1\u7684\u866c\u9aef\u5ba2\uff1b\u56fd\u5e08\u5343\u91cc\u5317\u5f81\uff0c\u9009\u62e9\u4e86\u592a\u539f\u674e\u4e16\u6c11\uff0c\u81ea\u6709\u5979\u72ec\u5230\u4e4b\u5904\u3002\u4f46\u5982\u5148\u884c\u5168\u529b\u641c\u6740\u866c \u9aef\u5ba2\uff0c\u90a3\u5c31\u66f4\u4e3a\u9002\u5408\u5148\u540e\u7684\u987a\u5e8f\u4e86\u3002\u6bd5\u7adf\u76ee\u524d\u80fd\u591f\u5bf9\u9020\u6210\u5927\u968b\u671d\u5a01\u80c1\u7684\uff0c\u866c\u9aef\u5ba2\u6392\u540d\u7b2c \u4e00\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u786e\u5b9a\u662f\u866c\u9aef\u5ba2\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u516b\u4e5d\u4e0d\u79bb\u5341\u4e86\uff01\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u738b\u4e16\u5145\u5168\u529b\u7ec3\u5175\uff0c\u636e\u8bf4\u5df2\u8bad\u7ec3\u6210\u5927\u519b\u4e8c\u5341\u4e07\uff0c\u65e5\u540e\u5bf9 \u9635\u4ea4\u5175\u3001\u653b\u57ce\u63a0\u5730\uff0c\u662f\u4e00\u80a1\u53ef\u6015\u7684\u529b\u91cf\u3002\u4f46\u5982\u8bb2\u622a\u62e6\u3001\u523a\u6740\u3001\u795e\u51fa\u9b3c\u6ca1\u7684\u6697\u88ad\uff0c\u90a3\u5c31\u4e0d\u5982\u866c \u9aef\u5ba2\u4e86\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5f88\u6ee1\u610f\u5730\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u597d\uff01\u8bf8\u4f4d\u90fd\u719f\u77e5\u5929\u4e0b\u5f62\u52bf\uff0c\u4e34\u654c\u81ea\u6709\u6289\u62e9\uff0c\u4e0d\u7528\u6211 \u591a\u8d39\u5fc3\u4e86\u3002\u4f46\u671b\u56db\u4f4d\u80fd\u540c\u5fc3\u534f\u529b\uff0c\u5408\u4f5c\u65e0\u95f4\uff01\u201d \u3000\u3000\u201c\u603b\u7edf\u9886\u653e\u5fc3\uff01\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u8bf7\u770b\u8fd9\u4e2a\u67b6\u5f0f\uff1a\u5c31\u662f\u6211\u56db\u4eba\u5408\u4f5c\u65e0\u95f4\u7684\u4fdd\u8bc1\uff0c\u4e0d\u7ba1\u4ed6\u662f \u6d1b\u9633\u6765\u4eba\uff0c\u8fd8\u662f\u866c\u9aef\u5ba2\u7684\u624b\u4e0b\uff0c\u53ea\u8981\u8bf1\u4ed6\u73b0\u8eab\uff0c\u5c31\u4e0d\u8ba9\u4ed6\u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u4e00\u8f6c\u9a6c\u5934\u53c8\u5954\u5411\u8881\u7d2b\u70df\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u51c6\u5907\u597d\u4e86\uff0c\u53ea\u8981\u654c\u4eba\u4e00\u73b0\u8eab\uff0c \u5c31\u628a\u4ed6\u5305\u56f4\u8d77\u6765\uff0c\u4e0d\u8ba9\u4ed6\u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u201c\u51c6\u5907\u5927\u5f00\u6740\u6212\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u5148\u4e86\u89e3\u654c\u4eba\u6765\u5386\u4e4b\u540e\uff0c\u518d\u4f5c\u51b3\u5b9a\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4e0d\u8bba\u6765\u4eba\u662f\u8c01\uff1f\u6211\u4eec\u5df2\u7ecf\u51b3\u5b9a\u4e0d\u653e\u8fc7\u4ed6\u4eec\u4e86\uff0c\u8fd8\u8bf7\u56fd\u5e08\u5fc5\u8981\u65f6\u8d50\u52a9\u4e00\u81c2 \u4e4b\u529b\u3002\u201d \u3000\u3000\u6b64\u65f6\uff0c\u5927\u4f19\u513f\u6b63\u884c\u5728\u4e00\u5ea7\u7a7a\u65f7\u7684\u5927\u5730\u4e4b\u65c1\u5b87\u6587\u6210\u90fd\u4e3e\u624b\u4e00\u6325\uff0c\u5927\u519b\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e8c\u767e\u5750\u9a91\u5e03\u6210\u4e86\u4e00\u5ea7\u65b9\u9635\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5c31\u505c\u5728\u65b9\u9635\u4e2d\u95f4\u3002 \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u6295\u6ce8\u5728\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\uff0c\u70b9\u70b9\u5934\uff0c\u5fae\u5fae\u4e00\u7b11\uff01 \u3000\u3000\u53cc\u65b9\u7684\u8ddd\u79bb\u4e0d\u662f\u592a\u8fdc\uff0c\u674e\u4e16\u6c11\u77a7\u5230\u4e86\uff0c\u7adf\u7136\u4e5f\u70b9\u5934\u4e00\u7b11\u3002\u4ed6\u53cc\u624b\u88ab\u7f1a\uff0c\u5b58\u4ea1\u96be\u535c\uff0c\u7adf\u7136 \u4e5f\u7b11\u5f97\u51fa\u6765\uff1f\u771f\u662f\u4e0d\u628a\u751f\u6b7b\u6446\u5728\u5fc3\u4e0a\u7684\u82f1\u96c4\u4eba\u7269\u3002 \u3000\u3000\u201c\u770b\u6837\u5b50\uff0c\u8bf8\u4f4d\u662f\u7b49\u6211\u4eec\u73b0\u8eab\u4e86\uff1f\u201d\u58f0\u97f3\u51b0\u51b7\u3001\u4f4e\u6c89\uff0c\u4f3c\u7531\u7a7a\u4e2d\u98d8\u843d\u800c\u4e0b\u3002 \u3000\u3000\u679c\u7136\u968f\u7740\u90a3\u559d\u53eb\u7684\u58f0\u97f3\uff0c\u98d8\u843d\u4e0b\u56db\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u7a7f\u7740\u4e00\u8272\u7684\u7f01\u8863\uff0c\u4f69\u7740\u76f8\u540c\u7684\u5bbd\u9762\u957f\u5251\uff0c\u5e74\u7eaa\u4e5f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u56db\u5341\u5de6\u53f3\uff0c\u663e\u7136\u662f\u7ecf \u8fc7\u4e86\u7279\u6b8a\u8bad\u7ec3\u7684\u4eba\u624d\u3002 \u3000\u3000\u201c\u56db\u4f4d\u662f\u2026\u2026\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u7684\u5c5e\u4e0b\u4e86\uff1f\u4f46\u4e0d\u77e5\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u201c\u91d1\u3001\u94f6\u3001\u94dc\u3001\u94c1\uff0c\u56db\u5251\u5ba2\uff01\u201d\u5de6\u9996\u4e00\u4eba\u51b7\u7136\u5730\u56de\u7b54\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u4ed7\u51ed\u4eba\u591a\uff0c\u662f\u51c6\u5907\u5408 \u51fb\u7f57\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4f38\u624b\u62d4\u5251\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u540c\u65f6\u5251\u63e1\u624b\u4e2d\u3002 \u3000\u3000\u679c\u7136\uff0c\u56db\u628a\u989c\u8272\u4e0d\u540c\u7684\u5251\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56db\u4f4d\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u624b\u6267\u91d1\u5251\u7684\u9ed1\u8863\u4eba\u7b11\u9053\uff1a\u201c\u5bab\u536b\u603b\u7edf\u9886\uff0c\u5b87\u6587\u6210\u90fd\uff01\u6211\u4eec\u5bf9\u5929\u4e0b\u7684\u5f62\u52bf\u90fd\u5f88\u4e86\u89e3\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u5728\u4e0b\u786e\u662f\u5b87\u6587\u6210\u90fd\uff0c\u56db\u4f4d\u7684\u6765\u610f\u80fd\u4e0d\u80fd\u8bf4\u4e2a\u660e\u767d\u5462\uff1f\u201d \u3000\u3000\u201c\u4ea4\u51fa\u674e\u4e16\u6c11\uff0c\u6211\u4eec\u56de\u5934\u5c31\u8d70\uff01\u201d\u91d1\u5251\u4eba\u9053\uff1a\u201c\u6211\u4eec\u65e9\u665a\u4f1a\u6709\u5927\u6218\uff0c\u53ea\u4e0d\u8fc7\u73b0\u5728\u8fd8\u4e0d\u662f \u65f6\u673a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u90a3\u5c31\u7531\u4e0d\u5f97\u8bf8\u4f4d\u4e86\uff0c\u65e2\u7136\u9047\u4e0a\u4e86\uff0c\u53ea\u597d\u5148\u62da\u4e00\u573a\u4e86\u3002\u8bf8\u4f4d\u53f7\u79f0\u91d1\u3001\u94f6\u3001 \u94dc\u3001\u94c1\u56db\u5251\u5ba2\uff0c\u60f3\u5fc5\u5728\u5251\u672f\u4e0a\u6709\u7740\u7279\u6b8a\u9020\u8be3\u4e86\uff1f\u201d \u3000\u3000\u91d1\u5251\u4eba\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u4e3b\u52a8\u653b\u51fb\u5bab\u536b\u7edf\u9886\uff0c\u4f46\u4e5f\u4ece\u4e0d\u907f\u6218\u3002\u9601\u4e0b\u8981\u5355\u6253\u72ec\u6597\uff0c\u8fd8\u662f\u8981\u4ed6\u4eec \u4e00\u62e5\u800c\u4e0a\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u62d4\u51fa\u957f\u5251\uff0c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u5148\u9886\u6559\u4e00\u4e8c\uff01\u201d \u3000\u3000\u98de\u8eab\u4e0b\u9a6c\uff0c\u76f4\u5411\u56db\u4eba\u8fce\u4e0a\u53bb\u3002 \u3000\u3000\u5218\u98de\u9e4f\u3001\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u90fd\u4e3b\u52a8\u5411\u573a\u4e2d\u56f4\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u8bf8\u4f4d\u53ea\u53ef\u63a0\u9635\uff0c\u4e0d\u53ef\u51fa\u624b\uff01\u4e45\u95fb\u866c\u9aef\u5ba2\u7684\u624b\u4e0b\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u4eca\u5929\u6211\u8981 \u5355\u72ec\u9886\u6559\u4e00\u573a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5438\u4e00\u53e3\u6c14\uff0c\u6a2a\u5251\u800c\u7acb\u3002 \u3000\u3000\u4ed6\u4e0d\u6015\u654c\u4eba\uff0c\u4f46\u5374\u4ece\u4e0d\u5c0f\u89d1\u654c\u4eba\u3002 \u3000\u3000\u91d1\u5251\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u8fce\u5934\u4e00\u5251\u5288\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u679c\u7136\u4e0d\u662f\u5251\u6cd5\uff0c\u8fd9\u4e00\u5251\u7684\u529b\u9053\u50cf\u662f\u94a6\u68cd\u3001\u7985\u4ed7\uff0c\u631f\u5e26\u7740\u547c\u5578\u98ce\u58f0\uff01 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u63d0\u771f\u6c14\uff0c\u624b\u4e2d\u4e4b\u5251\u7adf\u5411\u91d1\u5251\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u542c\u4e0d\u5230\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u53cc\u5251\u76f8\u89e6\uff0c\u7adf\u7136\u8fde\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u51ed\u7cbe\u6df1\u7684\u5185\u529f\uff0c\u4f7f\u51fa\u9ecf\u5b57\u8bc0\uff0c\u7adf\u628a\u6c89\u91cd\u7684\u91d1\u5251\u8f7b\u8f7b\u5730\u62e8\u5728\u4e00\u65c1\uff0c\u5251\u52bf\u76f4\u8fdb\uff0c\u987a \u52bf\u65a9\u4e0b\uff0c\u76f4\u53d6\u63e1\u5251\u7684\u53f3\u8155\u3002 \u3000\u3000\u91d1\u5251\u4eba\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u4e0d\u5f03\u53bb\u91d1\u5251\uff0c\u5373\u5f88\u96be\u4fdd\u4f4f\u63e1\u5251\u7684\u53f3\u624b\u5f03\u53bb\u91d1\u5251\uff0c\u8fd9\u4e00\u9635\u4e5f\u5c31\u7b97\u8f93\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6562\u4ee5\u8f7b\u7075\u7684\u5b9d\u5251\u63a5\u4ed6\u5982\u6b64\u6c89\u91cd\u7684\u91d1\u5251\uff0c\u8f7b\u3001\u91cd\u76f8\u5dee\u5341\u5206\u60ac\u6b8a\u7684\u5175\u5203\uff0c\u649e\u51fb\u7684\u7ed3 \u679c\uff0c\u8f7b\u7075\u7684\u5b9d\u5251\u4e0d\u662f\u88ab\u4e00\u51fb\u9707\u65ad\uff0c\u5c31\u662f\u7838\u5f97\u8131\u624b\u800c\u98de\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7684\u5b9d\u5251\u770b\u4f3c\u7f13\u6162\uff0c\u5b9e\u6781\u5feb\u901f\u3002\u5c31\u5728\u91d1\u5251\u4eba\u7565\u4e00\u72b9\u8c6b\uff0c\u5229\u5251\u5df2\u9f50\u8155\u800c\u8fc7\uff0c\u91d1\u5251\u843d \u5730\uff0c\u8fd8\u5e26\u7740\u4e00\u53ea\u8840\u6dcb\u6dcb\u7684\u53f3\u624b\u3002 \u3000\u3000\u9c9c\u8840\u902c\u5c04\u51fa\u4e24\u5c3a\u591a\u8fdc\uff0c\u91d1\u5251\u4eba\u8fd8\u672a\u6765\u5f97\u53ca\u8868\u793a\u51fa\u75db\u75bc\u7684\u53cd\u5e94\uff0c\u5b87\u6587\u6210\u90fd\u5251\u52bf\u626c\u8d77\u53c8\u5212\u8fc7 \u91d1\u5251\u4eba\u7684\u54bd\u5589\u3002 \u3000\u3000\u91d1\u5251\u4eba\u7684\u5c38\u4f53\u6ca1\u6709\u5012\u4e0b\uff0c\u5b87\u6587\u6210\u90fd\u624b\u4e2d\u4e4b\u5251\u5df2\u6307\u5411\u94f6\u5251\u4eba\u3002 \u3000\u3000\u94f6\u5251\u4eba\u75be\u5feb\u5411\u540e\u8dc3\u9000\u3002 \u3000\u3000\u94a2\u3001\u94c1\u4e24\u5251\u4e5f\u88ab\u4e00\u9053\u95ea\u8fc7\u7684\u5bd2\u8292\u8feb\u9000\u4e86\u4e09\u5c3a\u3002 \u3000\u3000\u653b\u51fa\u4e00\u5251\u7684\u8fde\u7eed\u53d8\u5316\uff0c\u6df1\u5f97\u4e00\u4e2a\u5feb\u5b57\uff0c\u4e5f\u5c55\u73b0\u4e86\u5b87\u6587\u6210\u90fd\u771f\u6b63\u7684\u5251\u672f\u9020\u8be3\u3002 \u3000\u3000\u94f6\u3001\u94dc\u3001\u94c1\u4e09\u5251\u5ba2\u5168\u88ab\u903c\u9000\u4e86\u4e09\u5c3a\uff0c\u91d1\u5251\u4eba\u7684\u5c38\u4f53\u624d\u7830\u7136\u683d\u5012\u3002 \u3000\u3000\u5218\u98de\u9e4f\u3001\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\uff0c\u56db\u5927\u526f\u7edf\u9886\u65e9\u77e5\u9053\u603b\u7edf\u9886\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u4e5f\u672a\u6599\u5230\u4ed6\u6740\u4eba \u7684\u624b\u6cd5\u7adf\u4e5f\u662f\u5982\u6b64\u7684\u6f47\u6d12\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u53d6\u4eba\u6027\u547d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u770b\u5f97\u5f88\u4ed4\u7ec6\uff0c\u5979\u9700\u8981\u4e86\u89e3\u5b87\u6587\u6210\u90fd\u7684\u6b66\u529f\u3002 \u3000\u3000\u770b\u4f3c\u53d6\u5de7\uff0c\u5b87\u6587\u6210\u90fd\u4ee5\u5feb\u5251\u6740\u4eba\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u5374\u770b\u51fa\u4e86\u771f\u6b63\u7684\u539f\u56e0\uff0c\u662f\u5b87\u6587\u6210\u90fd\u4ee5\u5185\u529f\u903c\u51fa\u7684\u6f5c\u529b\uff0c\u963b\u6b62\u4e86\u5bf9\u65b9\u5e94\u53d8\u7684\u901f \u5ea6\uff0c\u800c\u4efb\u4ed6\u6325\u5251\u5bb0\u5272\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u540d\u9707\u5929\u4e0b\uff0c\u600e\u4e48\u4f1a\u6709\u4f60\u4eec\u8fd9\u7b49\u4e0d\u582a\u4e00\u51fb\u7684\u90e8\u4e0b\uff1f\u4e09\u4f4d\u662f\u51c6\u5907\u62a5 \u4ec7\u5462\uff1f\u8fd8\u662f\u60f3\u56de\u53bb\u4f20\u4e2a\u8baf\u606f\uff1f\u201d \u3000\u3000\u4e09\u5251\u5ba2\u4f3c\u662f\u5f88\u96be\u7acb\u523b\u51b3\u5b9a\uff0c\u5f7c\u6b64\u770b\u4e86\u4e00\u773c\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u5f00\u53e3\u5546\u91cf\uff0c\u5b87\u6587\u6210\u90fd\u5df2\u6325\u52a8\u4e86\u5de6 \u624b\u3002 \u3000\u3000\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u3001\u5218\u98de\u9e4f\u56db\u5927\u526f\u7edf\u9886\u5df2\u6325\u52a8\u5175\u5203\uff0c\u51b2\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e24\u767e\u540d\u968f\u884c\u6b66\u58eb\u4e2d\uff0c\u4e5f\u81ea\u52a8\u5206\u51fa\u4e86\u56db\u5341\u516b\u540d\uff0c\u56f4\u5835\u5728\u56db\u5468\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7ec8\u4e8e\u660e\u767d\u4e86\uff0c\u5b87\u6587\u6210\u90fd\u6740\u6b7b\u4e86\u91d1\u5251\u4eba\u4e4b\u540e\uff0c\u5df2\u4e0b\u4e86\u5168\u6570\u8bdb\u7edd\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u201c\u5b87\u6587\u6210\u90fd\uff0c\u4f60\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u94f6\u5251\u4eba\u6321\u5f00\u4e86\u51cc\u4e91\u7684\u79cb\u6c34\u96c1\u7fce\u5200\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec \u5df2\u51b3\u5b9a\u8981\u4f20\u56de\u8baf\u606f\uff0c\u4f60\u53ef\u662f\u6709\u4e9b\u6015\u4e86\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u592a\u665a\u4e86\uff0c\u4e00\u4e2a\u7edf\u519b\u7684\u4e3b\u5e05\uff0c\u5076\u52a8\u6148\u60b2\u4e4b\u5fc3\uff0c\u4e5f\u662f\u7a0d\u7eb5\u5373\u901d\u3002\u8bf8\u4f4d\u4e0d\u80fd\u7acb \u523b\u56de\u5e94\uff0c\u5df2\u6ce8\u5b9a\u57cb\u9aa8\u4e8e\u6b64\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u6251\u5411\u94f6\u5251\u4eba\u3002 \u3000\u3000\u8fd9\u6b21\u6ca1\u6709\u51fa\u5251\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u56e2\u91d1\u8292\u98de\u5c04\u8fc7\u53bb\uff0c\u4e0d\u77e5\u662f\u5426\u6709\u610f\u5728\u8881\u7d2b\u70df\u9762\u524d\u5356\u5f04\uff0c\u91d1\u8292\u5982\u6cf0\u5c71\u538b\u9876\u4e00\u822c\uff0c\u76f4\u51fb \u800c\u4e0b\u3002 \u3000\u3000\u6765\u52bf\u592a\u5feb\u4e86\uff0c\u770b\u4e0d\u6e05\u695a\u662f\u4e00\u4e2a\u4ec0\u4e48\u5175\u5203\uff0c\u53ea\u597d\u4e3e\u5251\u4e00\u6321\u3002 \u3000\u3000\u91d1\u94c1\u5927\u9707\u58f0\u4e2d\uff0c\u94f6\u5251\u4eba\u7684\u5251\u88ab\u9707\u98de\u4e86\uff0c\u8111\u888b\u4e5f\u88ab\u51fb\u788e\uff0c\u5012\u5730\u800c\u4ea1\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4eba\u4ecd\u5728\u5411\u524d\u98de\u5c04\uff0c\u843d\u7740\u5b9e\u5730\uff0c\u5df2\u628a\u98de\u5c04\u800c\u51fa\u91d1\u8292\u6536\u5165\u8170\u4e2d\u4e00\u5177\u9769\u56ca\u4e2d\uff0c\u4f3c\u662f\u6709 \u610f\u9690\u85cf\uff0c\u4e0d\u8ba9\u4eba\u770b\u6e05\u695a\u662f\u4ec0\u4e48\u5175\u5203\u3002 \u3000\u3000\u8fd9\u5177\u9769\u56ca\u5e73\u5e38\u662f\u6302\u5728\u4ed6\u7684\u9a6c\u978d\u524d\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u88ab\u5b87\u6587\u6210\u90fd\u80cc\u5728\u4e86\u8eab\u4e0a\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u653b\u53d6\u94dc\u3001\u94c1\u53cc\u5251\uff0c\u4e5f\u66f4\u52a0\u51cc\u5389\u4e86\u3002\u5218\u98de\u9e4f\u4e5f\u611f\u89c9\u5b87\u6587\u6210\u90fd\u4e0d\u7559\u6d3b\u53e3\u6253\u7b97\uff0c\u98de \u5200\u7acb\u523b\u51fa\u624b\u3002 \u3000\u3000\u5200\u5982\u95ea\u7535\u8fc7\u957f\u7a7a\uff0c\u8ba4\u4f4d\u5947\u51c6\u3002 \u3000\u3000\u94dc\u3001\u94c1\u53cc\u5251\u5404\u4e2d\u4e86\u4e00\u67c4\u98de\u5200\u3002 \u3000\u3000\u8427\u96e8\u5251\u6c14\u5982\u8679\uff0c\u7a7f\u5fc3\u800c\u8fc7\u3002 \u3000\u3000\u94dc\u5251\u4eba\u8fde\u4e00\u58f0\u568e\u53eb\u4e5f\u672a\u51fa\u53e3\uff0c\u4eba\u5df2\u5012\u6bd9\u3002 \u3000\u3000\u7530\u5f53\u4e00\u5200\u65a9\u843d\u4e0b\u94c1\u5251\u4eba\u7684\u8111\u888b\uff0c\u56de\u987e\u5218\u98de\u9e4f\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u8c22\u4e86\uff0c\u5982\u975e\u5218\u5144\u7684\u98de\u5200\u76f8 \u52a9\uff0c\u5c0f\u5f1f\u4e09\u4e94\u5341\u56de\u5408\u672a\u5fc5\u80fd\u6740\u5f97\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u5728\u94dc\u3001\u94c1\u4e8c\u5251\u7684\u5c38\u4f53\u4e0a\u62d4\u4e0b\u98de\u5200\uff0c\u6536\u5165\u6000\u4e2d\u3002 \u3000\u3000\u4ed6\u4e5f\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u7edf\u9886\u5df2\u52a8\u4e86\u6740\u673a\uff0c\u5c0f\u5144\u6765\u4e0d\u53ca\u5f81\u8be2\u4e24\u4f4d\u540c\u610f\uff0c\u8d38\u7136\u51fa\u624b\uff0c\u5510\u7a81\u5f97\u5f88\uff0c \u4e24\u4f4d\u8001\u5f1f\u4e07\u52ff\u4ecb\u610f\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u770b\u6765\u54b1\u4eec\u7684\u6b66\u529f\u548c\u7edf\u9886\u4e4b\u95f4\uff0c\u6709\u4e00\u6bb5\u5f88\u5927\u7684\u8ddd\u79bb\uff0c\u4eca\u65e5\u66f4\u89c1\u660e\u6717\uff0c\u4ed6\u6740\u91d1\u3001 \u94f6\u53cc\u5251\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u3002 \u3000\u3000\u7530\u5144\u548c\u6211\u5206\u6597\u94dc\u3001\u94c1\u53cc\u5251\uff0c\u5982\u975e\u5218\u5144\u7684\u98de\u5200\u76f8\u52a9\uff0c\u6050\u6015\u5f88\u96be\u5728\u4e09\u4e94\u5341\u62db\u5185\uff0c\u5236\u654c\u4e8e\u6b7b \u5462\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u884c\u8fd1\u4e86\u674e\u4e16\u6c11\uff0c\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u7684\u95e8\u4e0b\u4e0d\u8fc7\u5982\u6b64\uff0c\u4e0d\u77e5\u4e3a\u4f55\u79e6\u743c\u3001\u656c \u5fb7\u7adf\u7136\u65e0\u6cd5\u4fdd\u62a4\u4f60\u4e0d\u53d7\u4f24\u5bb3\uff1f\u4f20\u8bf4\u592a\u539f\u4faf\u5e9c\u4e2d\u8c6a\u6770\u4e91\u96c6\uff0c\u770b\u6765\u662f\u6709\u4e9b\u5938\u5927\u4e86\uff01\u201d \u3000\u3000\u201c\u5c06\u519b\uff0c\u79e6\u743c\u3001\u656c\u5fb7\u548c\u8bf8\u4f4d\u9047\u4e0a\u7684\uff0c\u4e0d\u662f\u540c\u4e00\u4f19\u4eba\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8fd9\u62fc\u6218\u7684\u7ed3\u679c\u5c31\u5f88 \u96be\u8bba\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u597d\u4e3b\u5b50\uff0c\u4ed6\u4eec\u6253\u8d25\u4e86\uff0c\u53d7\u4f24\u4e86\uff1b\u4e00\u6837\u53d7\u5230\u4f60\u7684\u7231\u60dc\u548c \u656c\u91cd\uff0c\u4e8c\u516c\u5b50\u5728\u5f85\u4eba\u65b9\u9762\u7b97\u5f97\u5341\u5206\u539a\u9053\u4e86\u3002\u201d \u3000\u3000\u542c\u8d77\u6765\u662f\u5728\u8d5e\u8d4f\uff0c\u4f46\u9aa8\u5b50\u91cc\u5374\u5341\u5206\u8bbd\u523a\u3002 \u3000\u3000\u674e\u4e16\u6c11\u8868\u73b0\u51fa\u4e86\u96cd\u5bb9\u5927\u5ea6\uff0c\u7b11\u4e00\u7b11\uff0c\u672a\u518d\u7b54\u8baa\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4f38\u624b\u89e3\u4e0b\u674e\u4e16\u6c11\u675f\u8170\u4e1d\u5e26\uff0c\u6295\u5411\u7a7a\u4e2d\uff0c\u62d4\u5251\u51fb\u51fa\uff0c\u65ad\u6210\u4e24\u65ad\u3002\u968f\u5373\u62cd\u51fa\u4e00\u638c\uff0c \u5f3a\u5927\u7684\u638c\u98ce\u628a\u8170\u5e26\u9001\u5230\u6570\u4e08\u4e4b\u5916\uff0c\u98d8\u843d\u5728\u91d1\u3001\u94f6\u3001\u94dc\u3001\u94c1\u56db\u5251\u5ba2\u7684\u5c38\u4f53\u65c1\u4fa7\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u662f\u5ac1\u7978\u4e4b\u8ba1\u4e86\u3002 \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5c06\u519b\uff0c\u53ea\u51ed\u4e00\u6761\u8170\u5e26\uff0c\u4ed6\u4eec\u672a\u5fc5\u80fd\u8ba4\u5f97\u51fa\u6765\uff01\u201d \u3000\u3000\u5728\u4e0b\u8eab\u4e0a\u8fd8\u6709\u4e00\u5757\u7389\u4f69\uff0c\u5982\u80fd\u5f03\u7f6e\u73b0\u573a\uff0c\u5c31\u53ef\u4ee5\u7559\u4e0b\u8f83\u4e3a\u660e\u663e\u7684\u7ebf\u7d22\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u591a\u6b64\u4e00\u4e3e\uff01\u201c\u5382\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u9057\u7559\u7389\u4f69\u3002\u5c31\u6709\u4e9b\u7275\u5f3a\u505a\u4f5c\u4e86\u3002\u6c5f\u6e56\u8c6a\u6770\u5927\u90fd\u675f \u6709\u8170\u5e26\uff0c\u4f46\u9ec4\u8272\u7684\u8170\u5e26\u4e0d\u591a\u3002\u4f55\u51b5\uff0c\u4f60\u675f\u7684\u9ec4\u8272\u4e1d\u5e26\u7ec7\u6709\u7279\u6b8a\u7684\u7ec6\u81f4\u82b1\u7eb9\u3002\u5982\u679c\u866c\u9aef\u5ba2\u7684\u4eba \u77a7\u4e0d\u51fa\u6765\uff0c\u53ea\u80fd\u8bf4\u4ed6\u4eec\u592a\u7b28\uff0c\u592a\u539f\u7684\u674e\u4faf\u5e9c\u5c31\u8fd0\u6c14\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5012\u4e5f\u6709\u7406\uff0c\u4f46\u613f\u5c06\u519b\u7684\u5ac1\u7978\u4e4b\u8ba1\u5f97\u5230\u6210\u529f\uff0c\u866c\u9aef\u5ba2\u6740\u5954\u592a\u539f\u674e \u5e9c\uff0c\u4ee5\u6fc0\u8d77\u8881\u5929\u7f61\u548c\u674e\u9756\u7b49\u4eba\u6297\u62d2\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u5e0c\u671b\u6211\u5ac1\u7978\u6210\u529f\uff0c\u96be\u9053\u4e0d\u62c5\u5fc3\u4f60\u7236\u6bcd\u3001\u5bb6\u4eba\u7684\u5b89\u5371\u5417\uff1f\u201d \u3000\u3000\u201c\u62c5\u5fc3\u554a\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4f46\u6211\u66f4\u62c5\u5fc3\u9664\u4e86\u671d\u5ef7\u548c\u5bd2\u5bb6\u4e4b\u5916\u8fd8\u6709\u4ec0\u4e48\u529b\u91cf\u80fd\u548c\u866c\u9aef\u5ba2\u5bf9 \u6297\uff1f\u671d\u5ef7\u5168\u529b\u5bf9\u4ed8\u592a\u539f\u674e\u4faf\u5e9c\uff0c\u8ba9\u4ed6\u9aef\u5ba2\u5750\u6536\u6e14\u5229\uff0c\u5bf9\u5929\u4e0b\u4e07\u6c11\u5c31\u9057\u5bb3\u592a\u5927\u4e86\uff1b\u90a3\u5c31\u4e0d \u5982\u2026\u2026\u201d \u3000\u3000\u53f9\u53e3\u6c14\uff0c\u5782\u9996\u4e0d\u8a00\uff0c\u4f3c\u662f\u6709\u4e9b\u4e0d\u5fcd\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u4e0d\u5982\u600e\u6837\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8bf4\u5f97\u6709\u7406\uff0c\u671d\u5ef7\u4e5f\u8bb8\u80fd\u5e2e\u4f60\u4e00\u628a\u3002\u201d \u3000\u3000\u201c\u80fd\u5982\u6b64\uff0c\u5c31\u4e07\u6c11\u6709\u5e78\u4e86\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u674e\u9756\u548c\u866c\u9aef\u5ba2\u4e49\u7ed3\u91d1\u5170\uff0c\u5176\u59bb\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18 \u53c8\u4ece\u866c\u9aef\u5ba2\u5b66\u4e60\u5251\u672f\uff0c\u674e\u9756\u5982\u679c\u4e0d\u80af\u548c\u5929\u7f61\u3001\u6df3\u98ce\u8054\u624b\u5bf9\u4ed8\u866c\u9aef\u5ba2\uff0c\u8fd9\u80dc\u8d1f\u5c31\u5f88\u96be\u9884\u6599\uff0c\u5bd2 \u5bb6\u7eb5\u6709\u591a\u4f4d\u6c5f\u6e56\u8c6a\u6770\u52a9\u9635\uff0c\u4e5f\u975e\u866c\u9aef\u5ba2\u4e4b\u654c\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u5e0c\u671b\u671d\u5ef7\u6d3e\u8881\u56fd\u5e08\u52a9\u592a\u539f\u674e\u4faf\u5e9c\u4e00\u81c2\u4e4b\u529b\uff1f\u201d \u3000\u3000\u201c\u4e16\u6c11\u867d\u7136\u5c11\u4e0d\u66f4\u4e8b\uff0c\u53ef\u4e5f\u4e0d\u6562\u4f5c\u6b64\u5984\u60f3\uff0c\u53ea\u5e0c\u671b\u5728\u592a\u539f\u674e\u4faf\u5e9c\u8986\u706d\u4e4b\u540e\uff0c\u671d\u5ef7\u80fd\u96c6\u4e2d \u5168\u529b\uff0c\u4e00\u9f13\u4f5c\u6c14\u56f4\u6740\u4e86\u866c\u9aef\u5ba2\uff0c\u81f3\u5c11\u4e5f\u8981\u628a\u4ed6\u903c\u51fa\u4e2d\u571f\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u95ee\u9053\uff1a\u201c\u4f60\u548c\u866c\u9aef\u5ba2\u6709\u4ec0\u4e48\u6df1\u4ec7\u3001\u5927\u6068\uff1f\u5ff5\u5ff5\u4e0d\u5fd8\u7f6e\u4ed6\u4e8e\u6b7b\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u79c1\u4eba\u4ec7\u6068\uff0c\u4e3a\u7684\u662f\u9526\u7ee3\u5c71\u6cb3\u4e0a\u5343\u4e07\u9ece\u6c11\u3002\u4e16\u6c11\u613f\u4ee5\u592a\u539f\u674e\u4faf\u5e9c\u767e\u4f59\u4eba\u547d\u4f5c\u8d4c\u6ce8\uff0c \u6362\u5f97\u5343\u4e07\u767e\u59d3\u7684\u5e73\u5b89\u3002\u60e8\u7684\u53ea\u662f\u674e\u6c0f\u6ee1\u95e8\uff0c\u6bd4\u8d77\u5343\u91cc\u54ed\u58f0\u8fc7\u957f\u5b89\uff0c\u4e00\u8def\u8840\u6cb3\u98d8\u6775\u7684\u6740\u622e\uff0c\u8981 \u597d\u5f97\u591a\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u5fc3\u7cfb\u4e07\u6c11\u3001\u82cd\u751f\uff0c\u6ca1\u6709\u4e00\u70b9\u513f\u7684\u79c1\u4eba\u597d\u6076\u3001\u6b32\u5ff5\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u53ef\u771f\u662f\u795e\u5f97\u5f88 \u554a\uff01\u201d \u3000\u3000\u201c\u4e16\u6c11\u4e5f\u662f\u4eba\uff0c\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u65e2\u4e0d\u80fd\u56db\u5927\u7686\u7a7a\uff0c\u4e5f\u65e0\u6cd5\u65a9\u60c5\u706d\u6027\uff0c\u79c1\u4eba\u7684\u5fc3\u5934\u6068\u610f\u4e5f\u662f \u65e0\u6cd5\u907f\u514d\u7684\u4e86\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5012\u8981\u542c\u542c\u4f60\u7684\u79c1\u8877\u4f55\u5728\uff1f\u8fd8\u671b\u636e\u5b9e\u76f8\u544a\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e4b\u4e2d\u5df2\u4e0d\u4f3c\u5148\u524d\u7684\u51b7\u8bbd\u70ed\u5632\uff0c\u9690\u542b\u4e86\u51e0\u5206\u656c\u610f\u3002 \u3000\u3000\u201c\u968b\u7080\u5e1d\u593a\u53bb\u4e86\u6211\u7684\u81f3\u7230\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u593a\u59bb\u4e4b\u6068\uff0c\u4eba\u95f4\u81f3\u5927\u6068\u4e8b\uff0c\u6211\u4e5f\u4e0d\u80fd\u514d\u4fd7 \u4e86\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6de1\u7136\u9053\uff1a\u201c\u539f\u6765\u8881\u8d35\u5983\u662f\u4f60\u7684\u8001\u5a46\uff01\u4f46\u662f\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u8881\u8d35\u5983\u662f\u592a\u539f\u4faf\u5e9c\u664b \u732e\uff0c\u5e76\u975e\u662f\u7687\u4e0a\u5f3a\u7d22\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u89e3\u5730\u9053\uff1a\u201c\u8881\u5b9d\u513f\u5165\u5bab\u65f6\u7389\u6d01\u51b0\u6e05\uff0c\u5979\u600e\u4e48\u4f1a\u662f\u4f60\u59bb\u5b50\uff1f\u201d \u3000\u3000\u201c\u867d\u672a\u6d1e\u623f\u82b1\u70db\uff0c\u4f46\u5df2\u4e24\u60c5\u76f8\u8bb8\uff0c\u5fc3\u5fc3\u76f8\u5370\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u51b7\u7b11\u4e00\u58f0\uff0c\u6253\u65ad\u4e86\u674e\u4e16\u6c11\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u65e2\u662f\u4e24\u60c5\u65e9\u76f8\u8bb8\uff0c\u4f60\u662f\u552f\u4e00\u53ef\u4ee5\u529d\u963b\u5979\u7684 \u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u963b\u6b62\u5979\u5462\uff1f\u4f60\u5fcd\u5fc3\u628a\u4e00\u4e2a\u7edd\u4e16\u7f8e\u5973\u9001\u5165\u522b\u4eba\u6000\u62b1\uff1f\u8ba9\u5979\u4e00\u8154\u60c5\u7231\u5316\u4f5c\u5fe7\u82e6\u3002 \u3000\u3000\u4f60\u5fe7\u4e07\u6c11\u4e4b\u5fe7\uff0c\u5fd7\u5728\u5929\u4e0b\u5927\u4e1a\uff0c\u5374\u8fde\u4e00\u4e2a\u5fc3\u7231\u7684\u5973\u5b50\u4e5f\u4fdd\u4e0d\u4f4f\uff1b\u8fd8\u8981\u8c08\u4ec0\u4e48\u9e4f\u7a0b\u4e07\u91cc\uff1f \u9010\u9e7f\u4e2d\u539f\uff1f\u53ea\u6b64\u4e00\u6869\uff0c\u5c31\u4e0d\u4e3a\u6709\u8bc6\u4e4b\u58eb\u8c05\u89e3\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u56fd\u5e08\u7684\u60c5\u7eea\u9887\u4e3a\u6fc0\u52a8\uff0c\u770b\u6765\u5979\u548c\u8881\u5b9d\u513f\u7684\u611f\u60c5\u751a\u662f\u6df1\u539a\u3002\u5979\u4eec\u76f8 \u8bc6\u4e0d\u4e45\uff0c\u5374\u80fd\u4e8c\u9a6c\u540c\u69fd\uff0c\u4e0d\u4e89\u5148\u540e\uff0c\u8fd9\u5927\u6982\u4e5f\u662f\u53cc\u7f8e\u4e92\u5438\u3001\u60fa\u60fa\u76f8\u60dc\u4e86\u3002\u4f46\u8fd9\u6709\u8bc6\u4e4b\u58eb\uff0c\u53c8 \u6307\u7684\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e16\u6c11\u5fe7\u56fd\u5fe7\u6c11\uff0c\u5b9d\u513f\u5fe7\u6211\u4e4b\u5fe7\uff0c\u662f\u6211\u8d1f\u60c5\uff0c\u5bf9\u4e0d\u8d77\u5b9d\u513f\uff0c\u6240\u4ee5\uff0c\u6211\u60c5\u7518\u8d74\u6b7b\uff0c\u89c1\u5f97\u5b9d \u513f\u4e00\u9762\uff0c\u5f03\u5c38\u957f\u5b89\u4e5f\u5f97\u7791\u76ee\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u653e\u5f97\u4e0b\u82cd\u751f\u4e07\u6c11\u5417\uff1f\u201d\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\u8bdd\u8981\u8bf4\u56de\u5934\u4e86\uff0c\u4e0d\u7ecf\u51e0\u756a\u5bd2\u5f7b \u9aa8\uff0c\u90a3\u5f97\u6885\u82b1\u6251\u9f3b\u9999\uff1f\u5b9d\u59b9\u59b9\u5982\u4e0d\u820d\u8eab\u8fdb\u5bab\uff0c\u4e5f\u5c31\u65e0\u6cd5\u8bc1\u660e\u5979\u7231\u4f60\u4e4b\u6df1\u3002\u4f60\u5982\u6ca1\u6709\u8fd9\u4e00\u756a\u820d \u6b7b\u5fd8\u751f\u5165\u957f\u5b89\uff0c\u53ea\u4e3a\u4e00\u7779\u5b9d\u513f\u9762\uff0c\u4e5f\u65e0\u6cd5\u8bc1\u660e\u4f60\u5bf9\u5b9d\u59b9\u59b9\u7684\u76f8\u601d\u4e4b\u60c5\u3002 \u3000\u3000\u5509\uff01\u5929\u4e0b\u4e8b\uff0c\u90fd\u4e0d\u5b9c\u592a\u8fc7\u8ba4\u771f\uff0c\u5c24\u5176\u662f\u4e00\u4e2a\u60c5\u5b57\uff0c\u4e00\u8ba4\u771f\u5c31\u795e\u667a\u5927\u4e71\u4e86\u3002\u51b3\u5b9a\u7684\u4e8b\uff0c\u5e38 \u5e38\u662f\u6628\u662f\u4eca\u975e\uff0c\u5c31\u7b97\u6ca1\u6709\u5916\u529b\u5e72\u4e0e\uff0c\u53ef\u4e5f\u4f1a\u60c5\u5230\u6df1\u5904\u731c\u5fcc\u591a\uff0c\u5c0f\u5fc3\u773c\u91cc\u5bb9\u4e0d\u4e0b\u4e00\u9897\u7802\u5b50\uff0c\u672c \u662f\u5feb\u5feb\u4e50\u4e50\u7684\u76f8\u89c1\u6b22\uff0c\u504f\u5374\u65e0\u4e2d\u751f\u6709\uff0c\u9e21\u86cb\u6311\u9aa8\u5934\uff0c\u603b\u89c9\u5bf9\u65b9\u7231\u5f97\u4e0d\u591f\u6df1\uff0c\u65e0\u7f18\u65e0\u6545\u7684\u95f9\u4e00 \u4e2a\u6cea\u6d12\u95fa\u623f\uff0c\u6b22\u7231\u7684\u60c5\u8da3\u4e5f\u5c31\u5168\u88ab\u9010\u8d70\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\uff0c\u51fa\u81ea\u4e8e\u8d35\u5983\u4e4b\u53e3\uff0c\u5927\u51fa\u610f\u5916\u3002 \u3000\u3000\u674e\u4e16\u6c11\u3001\u5b87\u6587\u6210\u90fd\uff0c\u8fde\u540c\u7ad9\u5728\u65c1\u8fb9\u7684\u56db\u4f4d\u526f\u7edf\u9886\uff0c\u5168\u90fd\u542c\u5f97\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u65e2\u89c9\u5f97\u65b0\u5947\u6709\u8da3\uff0c\u53c8\u89c9\u7740\u56fd\u5e08\u8bc6\u60c5\uff0c\u628a\u7537\u5973\u95f4\u60c5\u7231\u7684\u4e8b\u4e5f\u770b\u5f97\u5982\u6b64\u900f\u5f7b\u3002 \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u7d2b\u70df\u59d1\u5a18\u80fd\u77e5\u5929\u6587\u5730\u7406\uff0c\u7cbe\u901a\u672f\u6cd5\u6280\u827a\uff0c\u60f3\u4e0d\u5230\u5bf9\u7537\u5973\u95f4\u76f8\u7231\u60c5\u4e8b\uff0c\u4e5f\u80fd \u4e86\u89e3\u5f97\u5982\u6b64\u6df1\u523b\uff0c\u4f69\u670d\u554a\uff01\u4f69\u670d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u8138\u4e0a\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u4f69\u670d\u7684\uff01\u6211\u4e0b\u5c71\u4e4b\u65f6\uff0c\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u4f46\u770b\u4e86\u4f60\u548c\u5b9d \u513f\u59b9\u59b9\u8fd9\u573a\u4eba\u95f4\u7684\u60b2\u5267\uff0c\u6d3b\u751f\u751f\u7684\u5c55\u73b0\u5728\u6211\u7684\u9762\u524d\u3002\u518d\u4e0d\u61c2\uff0c\u6211\u4e0d\u662f\u592a\u7b28\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u4ef0\u5929\u5401\u4e00\u53e3\u6c14\uff0c\u5782\u4e0b\u5934\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u6325\u624b\uff0c\u4e24\u767e\u94c1\u9a91\u653e\u8f94\u9a70\u9a8b\uff0c\u201c\u5f97\u5f97\u201d\u8e44\u58f0\u8361\u8d77\u4e86\u4e00\u7247\u70df\u5c18\u3002 \u3000\u3000\u8881\u7d2b\u70df\u611f\u89c9\u5230\u4e86\u3002 \u3000\u3000\u5979\u786e\u5b9e\u5bf9\u674e\u4e16\u6c11\u52a8\u4e86\u60dc\u601c\u4e4b\u60c5\u3002 \u3000\u3000\u4ed6\u662f\u80f8\u6000\u535a\u5927\u7684\u6551\u4e16\u82f1\u96c4\uff0c\u5374\u8981\u6d3b\u751f\u751f\u7684\u6b7b\u5728\u81ea\u5df1\u624b\u4e2d\u3002 \u3000\u3000\u56e0\u6127\u548e\u751f\u51fa\u7684\u601c\u60dc\uff0c\u6df7\u4e71\u4e86\u7d2b\u70df\u59d1\u5a18\u7684\u4e00\u5bf8\u82b3\u5fc3\u3002 \u3000\u3000\u201c\u674e\u4e16\u6c11\u5df2\u4e3a\u56fd\u5e08\u751f\u64d2\u6d3b\u6349\u4e86\uff0c\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6b63\u628a\u4ed6\u62bc\u5411\u957f\u5b89\uff0c\u6211\u76f8\u4fe1\u7687\u4e0a\u4f1a\u7acb\u523b \u5904\u51b3\u4e86\u4ed6\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u5bf9\u5440\uff01\u674e\u4e16\u6c11\u5df2\u6210\u7b3c\u4e2d\u4e4b\u9e1f\uff0c\u4e0d\u662f\u95ee\u9898\uff0c\u6709\u4ec0\u4e48\u597d\u60f3\u7684 \u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u88ab\u7b11\u7cca\u6d82\u4e86\uff0c\u5168\u77a7\u4e0d\u51fa\u4e00\u70b9\u5173\u5fc3\u7684\u611f\u89c9\uff0c\u7b11\u5f97\u5982\u82b1\u76db\u653e\uff0c\u674e\u4e16\u6c11\u7684\u751f\u6b7b\u53c8\u548c\u5979 \u8881\u7d2b\u70df\u6709\u4f55\u5173\u7cfb\u5462\uff1f\u8881\u7d2b\u70df\u63a5\u9053\uff1a\u201c\u6211\u53ea\u7b54\u5e94\u8fc7\u4ed6\uff0c\u8ba9\u4ed6\u548c\u5b9d\u8d35\u5983\u89c1\u4e0a\u4e00\u9762\uff0c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u7247 \u523b\u5de5\u592b\u5427\uff01\u6211\u76f8\u4fe1\u7687\u4e0a\u4e5f\u4f1a\u6709\u8fd9\u4e00\u70b9\u6210\u4eba\u4e4b\u7f8e\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u7684\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u4f1a\u529d\u7687\u4e0a\u7b54\u5141\uff0c\u5c5e\u4e0b\u96be\u4f53\u4e0a\u610f\uff0c\u771f\u662f\u60ed\u6127\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u60f3\u7684\u95ee\u9898\u662f\u866c\u9aef\u5ba2\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u4e0d\u662f\u771f\u7684\u90a3\u4e48\u53ef\u6015\u5462\uff1f\uff0c\u671d\u5ef7\u4e2d\uff0c\u5bf9 \u4ed6\u6709\u591a\u5c11\u4e86\u89e3\uff1f\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff01\u6210\u90fd\u65e9\u8be5\u60f3\u5230\u7684\uff0c\u866c\u9aef\u5ba2\u6f5c\u9690\u6e38\u8e2a\uff0c\u6ca1\u6709\u4e00\u5904\u5f00\u5c71\u7acb\u5be8\u7684\u57fa\u5730\uff0c\u4f46\u542c\u8bf4\u6709\u5341 \u4e07\u7532\u5175\uff0c\u9690\u4f0f\u4e8e\u957f\u5b89\u5468\u56f4\uff0c\u53ea\u8981\u4ed6\u4e00\u2014\u58f0\u4ee4\u4e0b\uff0c\u4e00\u5915\u95f4\u5c31\u53ef\u4ee5\u628a\u957f\u5b89\u62ff\u4e0b\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u9009\u8bad\u4e86\u7cbe\u826f\u7684\u6b66\u58eb\uff0c\u4ee5\u4fdd\u536b\u7687\u4e0a\u7684\u5b89\u5168\u3002\u5bf9\u6297\u866c\u9aef\u5ba2\u5341\u4e07\u4e4b\u4f17\uff0c \u4f60\u8bad\u7ec3\u7684\u6b66\u58eb\u4eba\u6570\uff0c\u662f\u4e0d\u662f\u5c11\u4e86\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u957f\u5b89\u57ce\u5185\u5bb9\u4e0d\u4e0b\u592a\u591a\u7684\u6b66\u58eb\u554a\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u53ea\u8981\u6211\u80fd\u591f\u652f\u6491\u4e24\u5929\uff0c\u4f4f\u5728\u957f\u5b89\u5de6 \u8fd1\u7684\u52e4\u738b\u5927\u519b\u5c31\u53ef\u4ee5\u653b\u5165\u957f\u5b89\u3002\u866c\u9aef\u5ba2\u7684\u94c1\u7532\u519b\u80cc\u3001\u8179\u53d7\u654c\uff0c\u5f88\u96be\u652f\u6491\u4e0b\u53bb\u3002\u4f55\u51b5\uff0c\u66f4\u591a\u52e4 \u738b\u7684\u5927\u519b\u5c06\u9646\u7eed\u5f00\u5230\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u539f\u6765\u5c06\u519b\u5df2\u8ba2\u4e0b\u4e86\u8ba1\u5212\uff0c\u4ee5\u4fdd\u536b\u957f\u5b89\u7684\u5b89\u5168\uff0c\u5bf9\u4ed8\u866c\u9aef\u5ba2\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u53ea\u5bf9\u4ed8\u866c\u9aef\u5ba2\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u8fd9\u662f\u7687\u4e0a\u548c\u5c5e\u4e0b\u7814\u5546\u7684\u8ba1\u5212\uff0c\u4e5f\u82b1\u53bb\u4e86\u6570\u5e74\u7684 \u51c6\u5907\u65f6\u95f4\u3002\u201d \u3000\u3000\u539f\u6765\u968b\u7080\u5e1d\u5bf9\u4fdd\u62a4\u957f\u5b89\u7684\u5b89\u5371\uff0c\u4e5f\u65e9\u5df2\u6709\u4e86\u51c6\u5907\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u4e2a\u4eba\u771f\u662f\u81ea\u79c1\u554a\uff01\u4ed6\u5173\u5fc3\u7684\u53ea\u662f\u4e2a\u4eba\u7684\u4eab\u4e50\u3001\u5b89\u5371\uff1b\u5374\u4e0d\u80af\u82b1\u8d39\u624d \u667a\u7b79\u5212\u4e00\u4e0b\u56fd\u8ba1\u6c11\u751f\uff0c\u8fd9\u6837\u7684\u7687\u5e1d\u5982\u4f55\u8981\u5929\u4e0b\u7684\u5b50\u6c11\u90fd\u7231\u6234\u4ed6\u5462\uff1f\u77e5\u9053\u7684\u6108\u591a\uff1b\u8881\u7d2b\u70df\u5bf9\u968b \u7080\u5e1d\u6108\u89c9\u4e0d\u6ee1\uff0c\u5bf9\u674e\u4e16\u6c11\u7684\u656c\u91cd\u4e4b\u5fc3\u4e5f\u9010\u6e10\u7684\u589e\u52a0\u3002 \u3000\u3000\u201c\u5c06\u519b\uff0c\u6211\u4eec\u8981\u4e0d\u8981\u9632\u60a3\u6765\u7136\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5148\u5bf9\u866c\u9aef\u5ba2\u53d1\u52a8\u4e00\u6b21\u5f81\u4f10\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6697\u9053\uff1a\u5979\u6ca1\u6709\u9a97\u6211\uff0c\u679c\u7136\u662f\u5728\u601d\u7d22\u866c\u9aef\u5ba2\u7684\u95ee\u9898\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u8138\u4e0a\u4e5f\u6cdb\u5347\u8d77\u4e00\u80a1\u7b11\u610f\uff0c\u9053\uff1a\u201c\u5e94\u8be5\u5f81\u4f10\uff0c\u6740\u4e86\u674e\u4e16\u6c11\uff0c\u89e3\u51b3\u4e86\u592a\u539f\u674e\u4faf \u5e9c\u7684\u4e8b\uff0c\u5c31\u53ef\u4ee5\u5168\u529b\u5bf9\u4ed8\u866c\u9aef\u5ba2\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u5fd6\u601d\uff1a\u5947\u602a\u5440\uff01\u5b87\u6587\u6210\u90fd\u600e\u4e48\u4f1a\u7a81\u7136\u91cd\u89c6\u8d77\u674e\u4e16\u6c11\u4e86\uff1f\u975e\u8981\u7f6e\u4ed6\u4e8e\u6b7b\u5730\u4e0d \u53ef\uff1f\u201c\u539f\u672c\u5c5e\u4e0b\u5e76\u672a\u628a\u592a\u539f\u674e\u4faf\u5e9c\u8fd9\u80a1\u529b\u91cf\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u5b87\u6587\u6210\u90fd\u4e3b\u52a8\u7684\u89e3\u7b54\u51fa\u8881\u7d2b\u70df\u5fc3\u4e2d \u7684\u7591\u95ee\uff0c\u9053\uff1a\u201c\u8fd9\u756a\u89c1\u5230\u4e86\u674e\u5e9c\u7684\u5b9e\u529b\uff0c\u679c\u7136\u975e\u540c\u5c0f\u53ef\u3002\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u90fd\u662f\u795e\u4ed9\u4e2d\u4eba\uff0c\u4e0d \u662f\u4e00\u822c\u7684\u5175\u5c06\u80fd\u591f\u5bf9\u4ed8\u7684\u3002\u4f46\u6700\u53ef\u6015\u7684\u8fd8\u662f\u674e\u4e16\u6c11\u8fd9\u4e2a\u4eba\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fd9\u9053\uff1a\u201c\u5662\uff01\u674e\u4e16\u6c11\u6709\u4ec0\u4e48\u597d\u6015\u5462\uff1f\u4ed6\u6b66\u529f\u5e73\u5e73\uff0c\u5c06\u519b\u624b\u4e0b\u4efb\u4f55\u4e00\u4f4d\u526f\u7edf\u9886\u5c31\u53ef \u4ee5\u628a\u4ed6\u751f\u64d2\u6d3b\u6349\u6765\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\uff0e\u201c\u662f\u7684\uff0c\u4ed6\u7684\u6b66\u529f\u5e73\u5eb8\uff0c\u4f46\u4ed6\u7684\u7edf\u9a6d\u9b45\u529b\u80fd\u8feb\u4eba\u5fc3\u60a6\u8bda\u670d\u3002\u6211 \u5bf9\u4ed6\u6df1\u6000\u654c\u610f\uff0c\u4f46\u5728\u51e0\u6b21\u4ea4\u8c08\u4e4b\u540e\uff0c\u7adf\u7136\u751f\u51fa\u4e86\u656c\u6155\u7684\u611f\u89c9\uff01\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5f88\u53ef\u6015\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ee5\u4f60\u5b87\u6587\u5c06\u519b\u5b9a\u529b\u4e4b\u6df1\uff0c\u548c\u7687\u4e0a\u60c5\u610f\u4e4b\u539a\uff0c\u80fd\u4e3a\u4ed6\u6026 \u7136\u5fc3\u52a8\uff0c\u522b\u4eba\u5462\uff1f\u662f\u4e0d\u662f\u66f4\u96be\u81ea\u7981\u4e86\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u674e\u4e16\u6c11\u4e0d\u80fd\u7559\uff0c\u8981\u65e9\u4e9b\u6740\u4e86\u4ed6\uff0c\u8d8a\u5feb\u8d8a\u597d\uff0c\u7559\u4ed6\u76f8\u5904\u4e09\u4e2a\u6708\uff0c\u5c31\u4e0d\u5fcd\u4f24\u5bb3\u4ed6 \u4e86\u3002\u201d\u5b87\u6587\u6210\u90fd\u82e6\u7b11\u2014\u4e0b\uff0c\u9053\uff1a\u201c\u4f20\u8bf4\u7684\u771f\u9f99\u5929\u5b50\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u6837\u4e86\u3002\u6240\u8c13\u767e\u7075\u62a4\u4f51\uff0c\u5c31\u662f \u4ed6\u7edf\u9a6d\u7684\u9b45\u529b\u53d1\u6325\u51fa\u7684\u795e\u79d8\u529b\u91cf\uff0c\u8ba9\u4eba\u5fc3\u4e2d\u6298\u670d\uff0c\u628a\u654c\u4eba\u53d8\u6210\u670b\u53cb\uff0c\u7518\u613f\u4e3a\u4ed6\u6548\u547d\uff0c\u674e\u4e16\u6c11 \u4f3c\u4e4e\u5c31\u5177\u6709\u8fd9\u79cd\u795e\u5947\u529b\u91cf\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u6211\u8fd8\u8ba4\u4e3a\u674e\u4e16\u6c11\u7684\u9b45\u529b\u53ea\u80fd\u5438\u5f15\u5973\u4eba\uff0c\u7537\u4eba\u7adf\u4e5f\u662f\u9003\u907f \u4e0d\u4e86\u554a\uff01\u201d \u3000\u3000\u201c\u56fd\u5e08\uff0c\u6211\u4eec\u8d76\u4e00\u7a0b\u5427\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4e94\u5929\u4e4b\u5185\uff0c\u4e00\u5b9a\u8981\u56de\u5230\u957f\u5b89\uff0c\u8981\u7687\u4e0a\u5148\u6740\u4e86 \u674e\u4e16\u6c11\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u90a3\u8981\u65e5\u591c\u517c\u7a0b\u4e86\uff1f\u201d \u3000\u3000\u597d\u4e00\u9635\u7eb5\u9a91\u5982\u98de\uff0c\u517c\u7a0b\u75be\u8fdb\uff0c\u5929\u9ed1\u65f6\u5df2\u8d76\u4e86\u4e24\u767e\u591a\u91cc\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5429\u5490\u624e\u4e0b\u4e00\u8425\u76d8\uff0c\u57cb\u9505\u9020\u996d\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fae\u7b11\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u8fd9\u4e48\u4e00\u4e2a\u8d76\u6cd5\uff0c\u4e0d\u7528\u4e94\u5929\u5c31\u53ef\u4ee5\u5230\u957f\u5b89\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u5446\uff0c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u5175\u7cbe\u9a6c\u5065\uff0c\u8d76\u8def\u81ea\u7136\u5feb\u901f\u3002\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u674e\u4e16\u6c11\u554a\uff01\u674e\u4e16\u6c11\uff0c\u6211\u4e0d\u80fd\u80cc\u53db\u7687\u4e0a\u3001\u80cc\u5f03\u4e86\u81ea\u5df1\u7684\u8a93\u8a00\u51fa\u624b\u6551 \u4f60\uff0c\u738b\u6c14\u6240\u949f\u7684\u4eba\uff0c\u5e94\u8be5\u6709\u767e\u7075\u62a4\u4f51\uff0c\u80fd\u4e0d\u80fd\u628a\u4f60\u9001\u5165\u957f\u5b89\uff0c\u4ea4\u7ed9\u7687\u4e0a\uff0c\u8981\u770b\u4f60\u7684\u8fd0\u6c14\u4e86\uff0c \u4f60\u8981\u602a\u4e5f\u53ea\u80fd\u602a\u6211\u4eec\u76f8\u9022\u6068\u665a\uff01 \u3000\u3000\u5de7\u513f\u3001\u83b2\u513f\u53d1\u89c9\u4e86\u8881\u7d2b\u70df\u7684\u60c5\u7eea\u4e0d\u5bf9\uff0c\u5374\u60f3\u4e0d\u51fa\u539f\u56e0\u4f55\u5728\uff1f\u4e24\u4e2a\u5c0f\u4e2b\u5934\u5343\u601d\u4e07\u60f3\uff0c\u4e5f\u60f3 \u4e0d\u51fa\u7d2b\u70df\u59d1\u5a18\u5fc3\u4e2d\u7684\u60c5\u7ed3\u7adf\u662f\u4e3a\u674e\u4e16\u6c11\u7684\u751f\u6b7b\u62c5\u5fe7\uff01 \u3000\u3000\u8fd9\u662f\u5979\u5317\u5f81\u7684\u76ee\u7684\u554a\uff01 \u3000\u3000\u5982\u613f\u5b8c\u6210\u4e86\uff0c\u5374\u5e26\u6765\u4e86\u65e0\u9650\u70e6\u607c\uff0c\u8c01\u53c8\u4f1a\u60f3\u5f97\u5230\u5462\uff1f\u201c\u59d1\u5a18\u7684\u60c5\u7eea\u6709\u4e9b\u4e0d\u5bf9\u5440\uff01\u201d\u5de7\u513f \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u4f7f\u5979\u70e6\u607c\u5462\uff1f\u5317\u5f81\u987a\u5229\uff0c\u5e26\u56de\u4e86\u738b\u6c14\u6240\u949f\u7684\u674e\u4e16\u6c11\u3002\u867d\u6709\u51e0\u573a\u640f\u6740\uff0c\u5374\u65e0\u4e00\u4e2a \u4f24\u4ea1\uff0c\u8fd9\u4e9b\u53c8\u90fd\u662f\u53ef\u5e86\u53ef\u8d3a\u7684\u4e8b\uff01\u201d \u3000\u3000\u83b2\u513fL\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u5c31\u731c\u4e0d\u900f\u4e86\uff0c\u4f1a\u4e0d\u4f1a\u662f\u5b87\u6587\u5c06\u519b\u76ef\u5f97\u592a\u7d27\uff0c\u4f7f\u59d1\u5a18\u70e6\u607c\u4e86\uff01\u201d \u3000\u3000\u201c\u6709\u53ef\u80fd\u554a\uff01\u201d\u5de7\u513f\u9053\uff1a\u201c\u5c06\u519b\u82f1\u4fca\u3001\u9ad8\u5927\uff0c\u6b66\u529f\u7edd\u4e16\uff0c\u6b63\u662f\u5973\u5b69\u5b50\u5fc3\u76ee\u4e2d\u7406\u60f3\u60c5\u90ce\u3002 \u4e0d\u8fc7\uff0c\u6211\u4eec\u7684\u59d1\u5a18\u662f\u738b\u5983\u7684\u8eab\u4efd\uff0c\u600e\u80fd\u548c\u81e3\u5b50\u4ea4\u5f80\u5462\uff1f\u518d\u8bf4\uff0c\u59d1\u5a18\u662f\u4ed9\u9732\u660e\u73e0\uff0c\u4e5f\u4e0d\u4f1a\u4e3a\u5b87 \u6587\u5c06\u519b\u52a8\u5fc3\u554a\uff01\u201d \u3000\u3000\u83b2\u513f\u9053\uff1a\u201c\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\u4e86\uff0c\u6240\u4ee5\u59d1\u5a18\u624d\u4f1a\u70e6\u607c\u3002\u70c8\u5973\u6015\u7f20\u554a\uff01\u5b87\u6587\u5c06\u519b\u7f20\u5f97\u7d27\uff0c\u59d1\u5a18 \u5fc3\u4e2d\u5c31\u70e6\u607c\u591a\u4e86\uff01\u201d \u3000\u3000\u5de7\u513f\u7a81\u7136\u55e4\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u662f\u770b\u4e66\u6389\u773c\u6cea\uff0c\u66ff\u53e4\u4eba\u62c5\u5fe7\u554a\uff01\u59d1\u5a18\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u98de\u5929 \u9041\u5730\uff0c\u65e0\u6240\u4e0d\u80fd\uff0c\u600e\u4e48\u88ab\u8fd9\u7b49\u533a\u533a\u5c0f\u4e8b\u56f0\u4f4f\uff1f\u83b2\u513f\uff0c\u6211\u4eec\u7761\u89c9\u5427\uff01\u201d \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u4e00\u65e9\u8d77\u7a0b\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5df2\u8d76\u51fa\u4e00\u767e\u591a\u91cc\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6447\u6447\u4e00\u5934\u79c0\u53d1\uff0c\u4e5f\u6447\u53bb\u5fc3\u4e2d\u70e6\u607c\u3002\u6b7b\u5b9a\u4e86\uff0c\u6b64\u60c5\u6b64\u666f\u4e4b\u4e0b\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u8c01\u80fd\u6765\u6551 \u674e\u4e16\u6c11\uff1f\u606f\u9a6c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5403\u8fc7\u5e72\u7cae\u996e\u8fc7\u6c34\uff0c\u5b87\u6587\u6210\u90fd\u5c31\u4e0b\u4ee4\u4e0a\u9a6c\u8d77\u7a0b\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u4ed6\u51c6\u5907\u8981\u5c3d\u5feb\u8d76\u56de\u957f\u5b89\u3002 \u3000\u3000\u56db\u5929\u80fd\u5230\uff0c\u5c31\u7528\u4e0d\u7740\u82b1\u8d39\u4e94\u5929\u3002 \u3000\u3000\u5348\u65f6\u8fc7\u540e\u4e0d\u4e45\uff0c\u9633\u5149\u666e\u7167\uff0c\u5ffd\u7136\u95f4\u4e00\u9053\u95ea\u5149\u5212\u8fc7\uff0c\u63a5\u8fde\u7740\u96f7\u58f0\u9686\u9686\uff01 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u5b63\u8282\u5e94\u8be5\u53ea\u6709\u98ce\u96ea\uff0c\u542c\u8fd9\u96f7\u58f0\uff0c\u5012\u4f3c\u662f\u6709\u4e00\u573a\u5927\u96e8\u3002\u201d \u3000\u3000\u201c\u770b\u4e0d\u51fa\u96e8\u52bf\u4f55\u5728\uff1f\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u8fd9\u5b63\u8282\uff0c\u98ce\u96ea\u963b\u4eba\uff0c\u8001\u5929\u7237\u5374\u7ed9\u4e86\u6211\u5341\u51e0\u5929\u7684\u6674\u6717 \u65e5\u5b50\u3002\u518d\u6709\u4e09\u4e2a\u6674\u5929\uff0c\u6211\u4eec\u5c31\u8d76\u5230\u957f\u5b89\u4e86\uff0c\u4f20\u8bf4\u738b\u6c14\u6240\u949f\u7684\u4eba\uff0c\u6709\u767e\u7075\u62a4\u4f51\uff0c\u53ef\u5168\u662f\u80e1\u8bf4\u516b \u9053\uff01\u201d \u3000\u3000\u51cc\u4e91\u9053\uff1b\u201d\u603b\u7edf\u9886\uff0c\u4f1a\u4e0d\u4f1a\u662f\u56fd\u5e08\u770b\u9519\u4e86\u5929\u8c61\uff1f\u6211\u4eec\u4e5f\u6293\u9519\u4e86\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4ee5\u56fd\u5e08\u4e4b\u80fd\uff0c\u600e\u4f1a\u6709\u6b64\u9519\u5931\uff1f\u4f55\u51b5\uff0c\u674e\u4e16\u6c11\u786e\u6709\u5438\u5f15\u5165\u7684 \u6c14\u5ea6\u9b45\u529b\uff0c\u4f60\u4eec\u8981\u5c0f\u5fc3\u554a\uff01\u522b\u88ab\u4ed6\u5438\u5f15\u4e86\uff1f\u201d \u3000\u3000\u53c8\u4e00\u9053\u95ea\u5149\u5212\u8fc7\u3002 \u3000\u3000\u897f\u5317\u65b9\u4e00\u5757\u4e4c\u4e91\u8fc5\u901f\u7684\u98d8\u98de\u8fc7\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u662f\u4e00\u5757\u4e0b\u96e8\u7684\u4e4c\u4e91\uff0c\u8d70\u5feb\u4e00\u4e9b\uff0c\u8fd9\u79cd\u5927\u96f7\u96e8\u6765\u5f97\u5feb\uff0c\u53bb\u5f97\u4e5f\u5feb\uff0c\u8fd9\u9644\u8fd1 \u56db\u65e0\u6751\u820d\uff0c\u65e0\u5904\u907f\u96e8\u554a\uff01\u201d \u3000\u3000\u4e8c\u767e\u94c1\u9a91\u52a0\u901f\u524d\u8fdb\u3002 \u3000\u3000\u884c\u7ecf\u5728\u4e00\u5ea7\u53e4\u5e99\u524d\u65f6\uff0c\u5927\u96e8\u7a81\u7136\u843d\u4e0b\uff0c\u5176\u52bf\u4e4b\u70c8\u6709\u5982\u503e\u76c6\uff0c\u5b87\u6587\u6210\u90fd\u4e0b\u4ee4\u5168\u519b\u907f\u5165\u53e4\u5e99 \u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u5ea7\u89c4\u6a21\u751a\u5927\u7684\u5bfa\u5e99\uff0c\u4f46\u5df2\u9999\u706b\u96f6\u843d\u3002 \u3000\u3000\u4f46\u6bbf\u53a2\u4ecd\u751a\u5b8c\u6574\uff0c\u4e24\u767e\u4eba\u9a6c\u5c1a\u53ef\u5bb9\u7eb3\u3002 \u3000\u3000\u8881\u7d2b\u70df\u3001\u5de7\u513f\u3001\u83b2\u513f\u3001\u5b87\u6587\u6210\u90fd\u548c\u56db\u5927\u526f\u7edf\u9886\u90fd\u907f\u5165\u4e86\u5927\u6bbf\u5185\u3002\u53e6\u6709\u516b\u4e2a\u6b66\u58eb\u62bc\u7740\u674e\u4e16 \u6c11\u4e5f\u8fdb\u5165\u5927\u6bbf\u907f\u96e8\u3002 \u3000\u3000\u539f\u6765\u5927\u6bbf\u662f\u8fd9\u5ea7\u53e4\u5e99\u4e2d\u6700\u5b8c\u597d\u7684\u6240\u5728\uff0c\u9ad8\u5927\u3001\u5bbd\u655e\u3002 \u3000\u3000\u4e4c\u4e91\u4f3c\u662f\u7531\u5929\u4e0a\u538b\u4e86\u4e0b\u6765\uff0c\u5929\u8272\u5927\u53d8\uff0c\u9ed1\u5982\u6df1\u591c\uff0c\u8fde\u7ad9\u5728\u5bf9\u9762\u7684\u4eba\uff0c\u5f71\u5b50\u4e5f\u6a21\u7cca\u5f97\u770b\u4e0d \u6e05\u695a\u4e86\u3002 \u3000\u3000\u96e8\u52bf\u4e5f\u5927\u5f97\u51fa\u5947\uff0c\u6709\u5982\u5929\u6cb3\u5012\u6302\u3001\u98de\u7011\u6d41\u6cc9\u4e00\u822c\u7684\u6cfc\u4e0b\u6765\u3002 \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a\u201c\u5947\u602a\u5440\uff01\u8fd9\u4e2a\u5bd2\u98ce\u98d8\u96ea\u7684\u5b63\u8282\uff0c\u600e\u4e48\u4f1a\u4e0b\u8d77\u8fd9\u6837\u7684\u503e\u76c6\u5927\u96e8\uff1f\u8fd9\u53ef\u662f\u4ece\u672a \u6709\u8fc7\u7684\u4e8b\uff1f\u201d \u3000\u3000\u7530\u5f53\u9053\uff1a\u201c\u4e4c\u4e91\u853d\u5929\uff0c\u8c6a\u96e8\u5982\u6ce8\uff0c\u5927\u5730\u53d8\u8272\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff1b\u5012\u662f\u9887\u7b26\u6b64\u65f6\u7684\u666f\u8c61\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a\u201c\u8fd9\u79cd\u5927\u96e8\uff0c\u8981\u4e0d\u4e86\u534a\u5929\u65f6\u5149\uff0c\u5927\u6982\u5c31\u53ef\u4ee5\u9646\u5730\u884c\u8239\u4e86\u3002\u6570\u5341\u5e74\u6c5f\u6e56\u884c\u8d70\uff0c \u89c1\u8fc7\u7684\u5927\u96e8\u4e0d\u5c11\uff0c\u5374\u4ee5\u6b64\u6b21\u4e3a\u6700\u3002\u201d \u3000\u3000\u201c\u5929\u8c61\u6709\u53d8\uff0c\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u5feb\u4e9b\u770b\u2014\u770b\u674e\u4e16\u6c11\uff01\u201d\u5b87\u6587\u6210\u90fd\u5927\u58f0\u547c\u53eb\u9053\u3002 \u3000\u3000\u201c\u603b\u7edf\u9886\u653e\u5fc3\uff01\u201d\u4e00\u4e2a\u6b66\u58eb\u9886\u73ed\u7b54\u9053\uff1a\u201c\u4ed6\u5750\u5728\u6211\u4eec\u516b\u4eba\u7684\u4e2d\u95f4\uff0c\u53ea\u8981\u52a8\u4e00\u4e0b\uff0c\u6211\u4eec\u5c31 \u611f\u89c9\u5230\u7684\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u4ecd\u4e0d\u653e\u5fc3\u5730\u95ee\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u4ed6\u5728\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b66\u58eb\u9886\u73ed\u9053\uff1a\u201c\u4ed6\u62b1\u819d\u800c\u5750\uff0c\u5782\u5934\u5c0f\u606f\u3002\u521a\u624d\u4e00\u9635\u7d27\u8d76\uff0c\u4ed6\u53cc\u624b\u88ab\u7f1a\uff0c\u65e0\u6cd5\u63a7\u7f30\uff0c\u5927\u6982 \u662f\u6709\u70b9\u7d2f\u4e86\u5427\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4eba\u5728\u5c31\u597d\uff0c\u4eba\u5728\u5c31\u597d\uff01\u7531\u6b64\u523b\u8d77\uff0c\u4f60\u4eec\u8981\u7279\u522b\u7559\u5fc3\u7740\u4ed6\uff0c\u4e0d\u80fd\u6709\u6240\u5931 \u95ea\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u6765\u5f97\u5feb\uff0c\u53bb\u5f97\u4e5f\u5feb\uff1b\u8fd9\u9635\u5929\u5730\u53d8\u8272\u7684\u5927\u96e8\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\u5c31\u96e8\u6536\u4e91\u6563\uff0c\u5929\u5730\u6e05 \u660e\uff0c\u4e00\u5207\u590d\u5e38\u3002 \u3000\u3000\u5218\u98de\u9e4f\u6b65\u51fa\u5927\u6bbf\uff0c\u56db\u4e0b\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u518d\u8fc7\u4e00\u9635\u3002\u79ef\u6c34\u4e0b\u53bb\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u4e0a\u8def\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u4f38\u5c55\u4e00\u4e0b\u53cc\u81c2\uff0c\u7b11\u9053\uff1a\u201c\u5927\u81ea\u7136\u7684\u5a01\u529b\uff0c\u679c\u975e\u4eba\u529b\u80fd\u53ca\u3002\u521a\u624d\u90a3\u4e00\u9635\u5929\u660f\u5730\u6697 \u7684\u6837\u5b50\uff0c\u8ba9\u4eba\u611f\u89c9\u5230\u5929\u5feb\u8981\u584c\u4e0b\u6765\u4e86\u3002\u4e00\u65e6\u4e91\u6563\u96e8\u6536\uff0c\u4e00\u5207\u4ecd\u7136\u662f\u8fd9\u4e2a\u6837\u5b50\u3002 \u3000\u3000\u7a81\u95fb\u6467\u5fc3\u88c2\u809d\u7684\u5927\u53eb\u58f0\u9053\uff1a\u201c\u674e\u4e16\u6c11\u4e0d\u89c1\u4e86\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u60ca\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u521a\u624d\u4e0d\u662f\u8fd8\u5728\u5417\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u7acb\u523b\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u56db\u4f4d\u526f\u7edf\u9886\u4e5f\u5feb\u901f\u7684\u56f4\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u6de1\u9ec4\u8888\u88df\uff0c\u5934\u6709\u6212\u75a4\u7684\u548c\u5c1a\u62b1\u819d\u800c\u5750\uff0c\u95ed\u7740\u53cc\u76ee\uff0c\u6b63\u4f0f\u5728\u81ea\u5df1\u819d\u4e0a\u7761\u89c9\u3002 \u3000\u3000\u90a3\u4f4d\u6b66\u58eb\u9886\u73ed\u624b\u4e2d\u6293\u7740\u4e00\u5757\u65b9\u5dfe\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u548c\u5c1a\u4f60\u8bf4\u554a\uff01\u4f60\u8bf4\u8bdd\u554a\uff01\u53ef\u662f \u8981\u5b58\u5fc3\u5bb3\u6b7b\u6211\u4e86\uff1f\u201d \u3000\u3000\u201c\u8d2b\u50e7\u65e0\u610f\u5bb3\u4eba\uff0c\u65bd\u4e3b\u4e0d\u8981\u8bef\u4f1a\u3002\u201d \u3000\u3000\u6b66\u58eb\u9886\u73ed\u6025\u9053\uff1a\u201c\u660e\u660e\u662f\u674e\u4e16\u6c11\uff0c\u6211\u4eec\u516b\u4e2a\u4eba\u73af\u7740\u4ed6\u8eab\u4fa7\u56f4\u5750\uff0c\u4e00\u9635\u5927\u96e8\u4e4b\u540e\uff0c\u600e\u51ac\u4f1a \u53d8\u6210\u4e86\u4f60\u8fd9\u4e48\u4e00\u4e2a\u548c\u5c1a\uff0c\u674e\u4e16\u6c11\u5462\uff1f\u201d \u3000\u3000\u201c\u8d2b\u50e7\u5728\u6bbf\u4e2d\u6253\u5750\u5df2\u7ecf\u4e00\u65e5\u4e00\u591c\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u674e\u4e16\u6c11\u5462\uff1f\u53ea\u6709\u4e00\u6761\u9752\u9f99\u968f\u7740\u503e\u76c6\u5927\u96e8\u817e \u7a7a\u800c\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\u5440\u5382\u6b66\u58eb\u9886\u73ed\u9053\uff1a\u201c\u4f60\u2026\u2026\u674e\u4e16\u6c11\u88ab\u6211\u4eec\u516b\u4e2a\u4eba\u524d\u547c\u540e\u62e5\u7684\u63a8\u8fdb\u5165\u5927\u6bbf\uff0c\u6211\u4eec\u56f4 \u7740\u4ed6\u5750\u5728\u4e2d\u95f4\uff0c\u5c31\u7b97\u674e\u4e16\u6c11\u5316\u9f99\u98de\u53bb\uff0c\u4f60\u5462\uff1f\u600e\u4e48\u4f1a\u6324\u5230\u4e86\u6211\u4eec\u516b\u4eba\u4e2d\u95f4\u5750\u7740\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u5929\u5927\u7684\u8bef\u4f1a\u554a\uff01\u516b\u4f4d\u7d27\u7d27\u7684\u73af\u56f4\u7740\u674e\u4e16\u6c11\uff0c\u8d2b\u50e7\u53c8\u600e\u80fd\u6324\u5f97\u8fdb\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u8fd9\u5996\u50e7\u5bb3\u6b7b\u6211\uff0c\u6211\u5c31\u5148\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u6b66\u58eb\u9886\u73ed\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u4e86\u8170\u5200\u3002 \u3000\u3000\u5931\u53bb\u4e86\u674e\u4e16\u6c11\uff0c\u8fd9\u516b\u4e2a\u6b66\u58eb\u90fd\u53ef\u80fd\u7acb\u523b\u88ab\u5904\u6b7b\u3002 \u3000\u3000\u9886\u73ed\u7684\u5c0f\u5934\u76ee\u80af\u5b9a\u662f\u975e\u6b7b\u4e0d\u53ef\u4e86\uff0c\u536f\u4e0a\u4e86\u548c\u5c1a\uff0c\u662f\u51c6\u5907\u4e34\u6b7b\u62c9\u4e2a\u57ab\u80cc\u7684\uff0c\u4e00\u53e3\u54ac\u5b9a\u4e86\u7edd \u4e0d\u6094\u6539\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e00\u6325\u624b\uff0c\u62e6\u4f4f\u6b66\u58eb\u9886\u73ed\uff0c\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u6cd5\u53f7\u5982\u4f55\u79f0\u547c\uff1f\u201d \u3000\u3000\u201c\u8d2b\u50e7\u5510\u4e09\u85cf\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u4e09\u85cf\u548c\u5c1a\uff0c\u8ba4\u8bc6\u5728\u4e0b\u5417\uff1f\u201d \u3000\u3000\u201c\u770b\u58ee\u58eb\u7684\u6c14\u5ea6\uff0c\u662f\u4e00\u4f4d\u5927\u5b98\u7f57\uff01\u201d\u4e09\u85cf\u548c\u5c1a\u9053\uff1a\u201c\u4e5f\u662f\u6b66\u529f\u9ad8\uff0c\u8033\u76ee\u7075\u654f\u7684\u9ad8\u4eba\uff0c\u674e \u4e16\u6c11\u5982\u88ab\u62bc\u5165\u4e86\u5927\u6bbf\u4e4b\u4e2d\uff0c\u5c31\u7b97\u80fd\u8131\u51fa\u51e0\u4f4d\u6b66\u58eb\u7684\u56f4\u56f0\uff0c\u53c8\u600e\u80fd\u9003\u8fc7\u58ee\u58eb\u7684\u8033\u76ee\uff1f\u201d \u3000\u3000\u516b\u4f4d\u6b66\u58eb\u5fc3\u4e2d\u6697\u9053\uff1a\u548c\u5c1a\u8bf4\u7684\u6709\u7406\uff0c\u4f60\u603b\u7edf\u9886\u548c\u56db\u4f4d\u526f\u7edf\u9886\u6b66\u529f\u80dc\u8fc7\u6211\u4eec\u5343\u500d\uff0c\u90fd\u5728\u73b0 \u573a\uff0c\u76f8\u8ddd\u4e5f\u4e0d\u8fc7\u6570\u5c3a\u8ddd\u79bb\uff0c\u800c\u4e5f\u4e0d\u77e5\u674e\u4e16\u6c11\u5982\u4f55\u8131\u56f4\u800c\u53bb\uff0c\u82e5\u662f\u8fc1\u6012\u5230\u6211\u4eec\u8eab\u4e0a\uff0c\u628a\u6211\u4eec\u4e00 \u8d77\u5904\u6b7b\uff0c\u90a3\u771f\u662f\u6b7b\u5f97\u51a4\u6789\u554a\uff01\u201d \u3000\u3000\u201c\u8bf4\u5f97\u4e5f\u662f\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6211\u90fd\u4e0d\u77e5\u9053\u674e\u4e16\u6c11\u5982\u4f55\u9003\u51fa\u4e86\u5927\u6bbf\uff0c\u4ed6\u4eec\u90fd\u8be5\u65e0 \u7f6a\u2026\u2026\u201d \u3000\u3000\u201c\u591a\u8c22\u603b\u7edf\u9886\u4e0d\u6740\u4e4b\u6069\uff01\u201d \u3000\u3000\u516b\u4f4d\u52b2\u88c5\u6b66\u58eb\u9f50\u9f50\u62dc\u4f0f\u4e8e\u5730\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff01\u201d \u3000\u3000\u516b\u4f4d\u6b66\u58eb\u6b7b\u91cc\u9003\u751f\uff0c\u6361\u56de\u4e86\u6027\u547d\uff0c\u54ea\u91cc\u8fd8\u6562\u591a\u8a00\uff0c\u6084\u7136\u8d77\u8eab\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u4e00\u6392\u8083\u7acb\u4e09\u85cf \u548c\u5c1a\u53cc\u624b\u5408\u5341\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u96f7\u58f0\u60ca\u86f0\u8d77\uff0c\u9f99\u98de\u4e5d\u91cd\u5929\uff0c\u767e\u7075\u76f8\u62a4\u4f51\uff0c\u4e07\u6c11\u5f85\u5927\u8d24\u3002\u963f\u5f25\u9640 \u4f5b\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5401\u51fa\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u4e5f\u5410\u51fa\u4e86\u4e00\u8154\u5fe7\u95f7\uff0c\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u6211\u4eec\u8fdb\u5165\u6bbf\u4e2d\u65f6\uff0c\u6ca1\u770b\u5230 \u4f60\u5440\uff1f\u201d \u3000\u3000\u201c\u5927\u96e8\u503e\u76c6\uff0c\u5929\u8272\u5e7d\u6697\uff01\u201d\u4e09\u85cf\u548c\u5c1a\u9053\uff1a\u201c\u76f8\u9022\u5bf9\u9762\u4e0d\u76f8\u89c1\uff0c\u4f55\u51b5\u548c\u5c1a\u5728\u5927\u6bbf\u4e00\u89d2\u6253 \u5750\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u559d\u9053\uff1a\u201c\u6211\u80fd\u591c\u95f4\u89c6\u7269\uff0c\u795e\u76ee\u5982\u7535\uff0c\u5bb9\u4e0d\u5f97\u4f60\u8c0e\u8a00\u6b3a\u9a97\uff01\u201d \u3000\u3000\u4e8c\u85cf\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u58ee\u58eb\u5df2\u5728\u5927\u6bbf\u4e2d\u4f2b\u7acb\u4e86\u826f\u4e45\uff0c\u53ef\u77e5\u8fd9\u5927\u6bbf\u4e2d\u6709\u51e0\u5ea7\u795e\u50cf\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u76ee\u5149\u4e00\u8f6c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u8fd9\u4e5f\u8981\u8003\u6211\u5417\uff1f\u4e5f\u592a\u5c0f\u770b\u5728\u4e0b\u4e86\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5df2\u611f\u89c9\u5230\u4e09\u85cf\u548c\u5c1a\u6709\u9b3c\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0b\u4e86\u5fc3\u4e2d\u6c14\u6012\uff0c\u4e3e\u6b62\u7b54\u8bdd\u975e\u5e38\u5c0f\u5fc3\uff0c\u501f\u8bf4\u8bdd\u673a\u4f1a\uff0c\u76ee\u5149\u626b\u63a0\u4e86\u6bbf\u4e2d\u4e24\u6b21\uff0c\u770b\u5f97\u6e05\u6e05 \u695a\u695a\u7684\u3002 \u3000\u3000\u201c\u4e09\u5c0a\u4f5b\u50cf\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5df2\u7ecf\u4e24\u5ea7\u53d7\u635f\uff0c\u5728\u4e0b\u8981\u7740\u4ee4\u5730\u65b9\u5b98\u5458\u91cd\u4fee\u5e99\u5b87\uff0c\u518d\u5851\u91d1 \u8eab\u3002\u201d \u3000\u3000\u9690\u9690\u95f4\uff0c\u900f\u51fa\u4e86\u4e0d\u51e1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4e09\u85cf\u548c\u5c1a\u5374\u4e00\u76f4\u5f88\u541d\u60dc\uff0c\u7ade\u4e0d\u80af\u53eb\u4ed6\u4e00\u58f0\u5c06\u519b\u3002 \u3000\u3000\u4ed6\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u58ee\u58eb\u9519\u4e86\uff0c\u5927\u6bbf\u4e2d\u660e\u660e\u6709\u56db\u5c0a\u4f5b\u50cf\uff0c\u58ee\u58eb\u600e\u4e48\u53ea\u770b\u5230\u4e09\u5c0a\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u4e09\u5c0a\uff0c\u5927\u548c\u5c1a\u80e1\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e09\u85cf\u548c\u5c1a\u5fae\u7b11\u9053\uff1a\u201c\u58ee\u58eb\u4e0d\u4fe1\uff0c\u548c\u5c1a\u5c31\u6570\u7ed9\u4f60\u770b\uff0c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5446\u4f4f\u4e86\uff01\u5510\u4e09\u85cf\u771f\u7684\u6570\u51fa\u4e86\u56db\u5c0a\u4f5b\u50cf\uff0c\u800c\u4e14\u90fd\u662f\u9ad8\u53ca\u534a\u4eba\u7684\u5927\u4f5b\u50cf\uff01\u201c\u600e\u4f1a\u591a \u4e86\u4e00\u5c0a\uff1f\u201d\u5b87\u6587\u6210\u90fd\u51b7\u7b11\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u4f60\u4f1a\u5996\u6cd5\uff1f\u201d \u3000\u3000\u5510\u4e09\u85cf\u9053\uff1a\u201c\u4eba\u4f1a\u773c\u82b1\uff0c\u4e5f\u4f1a\u95ea\u795e\u3002\u58ee\u58eb\uff0c\u53c8\u4f55\u5fc5\u82e6\u82e6\u8ffd\u95ee\u5462\uff1f\u201d \u3000\u3000\u201c\u548c\u5c1a\uff01\u201d\u8881\u7d2b\u70df\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u6211\u8bf4\u6709\u4e94\u5c0a\u4f5b\u50cf\uff0c\u8981\u4e0d\u8981\u6211\u6570\u7ed9\u4f60\u770b \u770b\uff1f\u201d \u3000\u3000\u5979\u4e00\u76f4\u5728\u6bbf\u89d2\u6253\u5750\uff0c\u95ed\u76ee\u5165\u5b9a\u3002 \u3000\u3000\u5bf9\u5b87\u6587\u6210\u90fd\u548c\u5510\u4e09\u85cf\u7684\u4e89\u6267\u672a\u52a0\u7406\u4f1a\uff0c\u5de7\u513f\u3001\u83b2\u513f\u770b\u59d1\u5a18\u4e0d\u613f\u63d2\u624b\uff0c\u4e5f\u5c31\u4e0d\u6562\u591a\u7ba1\u95f2\u4e8b \u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u63d2\u624b\u5e72\u4e0e\u4e86\uff01\u674e\u4e16\u6c11\u5982\u4f55\u79bb\u5f97\u4e86\u8fd9\u5ea7\u5927\u6bbf\uff1f\u660e\u660e\u662f\u4e09\u5c0a\u4f5b \u50cf\uff0c\u548c\u5c1a\u7adf\u80fd\u6570\u6210\u4e86\u56db\u5c0a\u3002\u6211\u4eec\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u5927\u6982\u4e5f\u53ea\u6709\u4f60\u77e5\u9053\u56e0\u7531\uff1f\u4f60\u5982\u518d\u4e0d\u7406\u4f1a\uff0c\u6211\u5c31\u4e0d \u77e5\u9053\u8be5\u5982\u4f55\u5bf9\u4ed8\u4e09\u85cf\u548c\u5c1a\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u8f7b\u79fb\u83b2\u6b65\uff0c\u7f13\u7f13\u8d70\u5230\u4e86\u4e09\u85cf\u548c\u5c1a\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u5979\u4f38\u51fa\u4e86\u7ea4\u7ea4\u7389\u6307\uff0c\u9053\uff1a\u201c\u770b\u6e05\u695a\u554a\uff01\u5927\u548c\u5c1a\uff0c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u3001\u4e94\uff01\u201d \u3000\u3000\u602a\u4e86\uff01\u771f\u88ab\u5979\u6570\u51fa\u4e86\u4e94\u5c0a\u4f5b\u50cf\uff0c\u4e5f\u662f\u9ad8\u8fc7\u4e09\u5c3a\u7684\u5927\u4f5b\u50cf\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7684\u5fc3\u5934\u9707\u52a8\u4e86\u3002\u6697\u6697\u60ca\u9053\uff1a\u5929\u554a\uff01\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u7528\u4e0d\u7740\u62bd\u5200\u3001\u62d4\u5251\u7684\u52a8\u624b\u6253 \u67b6\u4e86\uff0c\u5355\u662f\u8fd9\u6570\u4f5b\u50cf\u4e00\u7801\u5b50\u4e8b\uff0c\u5c31\u628a\u4ed6\u60ca\u5f97\u5fc3\u60ca\u80c6\u98a4\u3001\u6597\u5fd7\u6d88\u9000\u3001\u843d\u8352\u800c\u9003\u4e86\u3002\u2018\u4e8b\u5b9e\u4e0a\uff0c \u56db\u4f4d\u526f\u7edf\u9886\u4e5f\u5df2\u770b\u5f97\u8138\u8272\u5927\u53d8\uff0c\u56db\u4eba\u4e5f\u770b\u5f97\u975e\u5e38\u6e05\u695a\uff0c\u5927\u6bbf\u4e2d\u786e\u5b9e\u53ea\u6709\u4e09\u5c0a\u4f5b\u50cf\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u4e09\u85cf\u548c\u5c1a\u80fd\u6570\u51fa\u56db\u5c0a\uff0c\u8881\u7d2b\u70df\u80fd\u6570\u51fa\u4e94\u5c0a\uff1f\u8fd9\u4f3c\u4e4e\u662f\u4e00\u4ef6\u5f88\u5c0f\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u6570\u4e00\u4e0b\uff0c\u5c31\u591a\u4e86\u4e00\u5c0a\u4f5b\u50cf\u51fa\u6765\uff0c\u4e24\u4eba\u5982\u679c\u6570\u4e0a\u4e00\u767e\u6b21\uff0c\u90a3\u4e48\u8fd9\u5ea7\u5927\u6bbf\u5c31\u5168\u662f\u4e09\u5c3a\u9ad8\u7684 \u5927\u4f5b\u50cf\u4e86\u3002 \u3000\u3000\u4e0d\u60f3\u5b83\uff0c\u4e5f\u5c31\u7f62\u4e86\uff01 \u3000\u3000\u60f3\u4e00\u60f3\uff0c\u4f1a\u8ba9\u4eba\u5fc3\u751f\u5bd2\u610f\uff0c\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u5510\u4e09\u85cf\u5408\u638c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5973\u65bd\u4e3b\u76ee\u660e\u8033\u806a\uff0c\u770b\u5f97\u6e05\u695a\uff0c\u7684\u786e\u662f\u4e94\u5c0a\u4f5b\u50cf\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f46\u6211\u770b\u4e0d\u5230\u674e\u4e16\u6c11\u8eb2\u5728\u4f55\u5904\uff0c\u5927\u548c\u5c1a\u53ef\u5426\u6559\u6211\uff1f\u201d \u3000\u3000\u5510\u4e09\u85cf\u9053\uff1a\u201c\u83e9\u63d0\u672c\u65e0\u6811\uff0c\u660e\u955c\u4ea6\u975e\u53f0\uff1b\u5973\u65bd\u4e3b\u770b\u4e0d\u5230\uff0c\u5fc5\u662f\u8d70\u8fdc\u4e86\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u8df3\u51fa\u4e09\u754c\u5916\uff0c\u4e0d\u5728\u4e94\u884c\u4e2d\uff0c\u548c\u5c1a\u662f\u5f53\u4e16\u9ad8\u4eba\uff0c\u5c82\u53ef\u5984\u8a00\uff01\u201d \u3000\u3000\u7a81\u7136\u75be\u51fa\u53f3\u624b\uff0c\u7389\u7b0b\u4e00\u822c\u7684\u98df\u6307\u6307\u5411\u4e00\u5c0a\u4f5b\u50cf\u3002\u597d\u597d\u7684\u4e00\u5c0a\u4f5b\u50cf\u7a81\u7136\u71c3\u70e7\u8d77\u6765\uff0c\u5192\u51fa\u4e86 \u6d53\u70df\u706b\u82d7\u3002 \u3000\u3000\u201c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\uff01\u201d \u3000\u3000\u5510\u4e09\u85cf\u5408\u638c\u5f53\u80f8\uff0c\u4f4e\u55a7\u4f5b\u53f7\uff01 \u3000\u3000\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u4e00\u5c0a\u6728\u96d5\u4f5b\u50cf\u5728\u70c8\u7130\u4e2d\u5316\u4f5c\u4e86\u7070\u70ec\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u662f\u4e09\u6627\u771f\u706b\uff0c\u70e7\u6bc1\u6709\u5f62\u4e4b\u4f53\uff0c\u4e5f\u80fd\u70e7\u4f24\u4ed6\u7684\u795e\u7075\u6c14\uff01\u548c\u5c1a\uff0c\u9690\u5f62\u9041\u6cd5\u8fd0 \u4e0d\u8d70\u674e\u4e16\u6c11\u7684\u8840\u8089\u4e4b\u8eaf\u3002\u4e0d\u8981\u903c\u5f97\u6211\uff0c\u505a\u51fa\u4e86\u4ee4\u4eba\u627c\u8155\u7684\u6068\u4e8b\u3002\u201d \u3000\u3000\u201c\u5973\u65bd\u4e3b\u6740\u673a\u903c\u4eba\uff0c\u6211\u548c\u5c1a\u53ea\u6709\u751f\u53d7\u4e86\u3002\u201d \u3000\u3000\u7f13\u6b65\u79fb\u52a8\u8eab\u8eaf\uff0c\u76d8\u819d\u6253\u5750\u5728\u4f9b\u53f0\u524d\u9762\u3002 \u3000\u3000\u4f9b\u53f0\u4e0a\u672c\u6709\u4e94\u5c0a\u4f5b\u50cf\uff0c\u4e00\u5c0a\u88ab\u8881\u7d2b\u70df\u70e7\u6210\u7070\u70ec\uff0c\u56db\u5c0a\u5374\u672a\u6ce2\u53ca\u3002\u5510\u4e09\u85cf\u5750\u5728\u4f9b\u53f0\u524d\uff0c\u51c6 \u5907\u820d\u8eab\u62a4\u4f5b\u4e86\u3002 \u3000\u3000\u770b\u6e05\u695a\uff0c\u60f3\u4e00\u60f3\uff0c\u53c8\u4f1a\u89c9\u5f97\u5947\u602a\u4e86\uff1f\u4e94\u5c0a\u4f5b\u50cf\u5e76\u5217\u4f9b\u53f0\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u662f\u6570\u5bf8\u8fdc\u8fd1\uff0c\u90a3\u718a\u718a \u70c8\u706b\u7adf\u672a\u6ce2\u53ca\u5176\u4ed6\u56db\u5c0a\uff0c\u4f3c\u4e4e\u662f\u4e2d\u95f4\u6709\u4e00\u79cd\u529b\u91cf\u628a\u706b\u7130\u9694\u5f00\u4e86\u3002 \u3000\u3000\u5510\u4e09\u85cf\u8eab\u4e0a\u7684\u9ec4\u8272\u8888\u88df\u5ffd\u7136\u5f00\u59cb\u81a8\u80c0\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6574\u4e2a\u4eba\u5c31\u88ab\u9ec4\u8272\u5927\u8888\u88df\u7ed9\u5305\u4e86\u8d77\u6765 \u4e86\u3002 \u3000\u3000\u4ed4\u7ec6\u770b\uff0c\u56db\u5c0a\u4f5b\u50cf\u548c\u4f9b\u53f0\u4e5f\u5168\u5305\u5728\u9ec4\u8272\u7684\u8888\u88df\u4e2d\u4e86\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u4e00\u4ef6\u4eba\u62ab\u7684\u8888\u88df\u7adf\u7136\u81a8\u5927\u5f97\u50cf\u4e2a\u8499\u53e4\u5305\u3002 \u3000\u3000\u201c\u7985\u4e91\u5f25\u516d\u5408\uff0c\u548c\u5c1a\uff01\u4e0d\u8981\u903c\u6211\u62da\u547d\u554a\uff01\u201d\u8881\u7d2b\u70df\u7684\u8138\u4e0a\u6cdb\u51fa\u60ca\u60e7\u7684\u795e\u8272\uff0c\u53f3\u624b\u6325\u52a8\uff0c \u793a\u610f\u5b87\u6587\u6210\u90fd\u7b49\u9000\u51fa\u5927\u6bbf\u3002 \u3000\u3000\u4ed6\u4eec\u542c\u4ece\u8881\u7d2b\u70df\u7684\u547d\u4ee4\uff0c\u9000\u51fa\u4e86\u5927\u6bbf\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u8eab\u524d\u4e09\u5c3a\u5904\uff0c\u4f3c\u662f\u6811\u7acb\u4e86\u4e00\u6839\u94c1\u6869\uff0c\u81a8\u6d8c\u800c\u81f3\u7684\u9ec4\u96fe\u53d7\u5230\u4e86\u963b\u62e6\u3002 \u3000\u3000\u5de7\u513f\u3001\u83b2\u513f\u4e0d\u6562\u8dd1\u3002\u4f46\u4e24\u4eba\u90fd\u8eb2\u5165\u4e86\u8881\u7d2b\u70df\u7684\u8eab\u540e\u3002 \u3000\u3000\u8513\u5ef6\u3001\u4f38\u5f20\u7684\u9ec4\u96fe\u7ed5\u8fc7\u4e86\u8881\u7d2b\u70df\u505c\u8eab\uff0c\u6d8c\u5411\u5927\u6bbf\u7684\u95e8\u53e3\u3002 \u3000\u3000\u771f\u662f\u795e\u5947\u5440\uff01 \u3000\u3000\u5c31\u50cf\u662f\u5927\u6bbf\u7684\u95e8\u53e3\uff0c\u6709\u7740\u4e00\u9053\u65e0\u5f62\u4e4b\u5899\uff0c\u9ec4\u96fe\u6c9b\u7136\u800c\u81f3\uff0c\u5835\u585e\u5728\u95e8\u53e3\uff0c\u4e0d\u518d\u5411\u5916\u6269\u5c55 \u4e86\u3002 \u3000\u3000\u4f46\u5927\u6bbf\u4e4b\u5185\u5374\u5168\u88ab\u4e00\u56e2\u9ec4\u96fe\u7b3c\u7f69\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u5de8\u5927\u7684\u9ec4\u8272\u96fe\u5305\uff0c\u628a\u6bbf\u4e2d\u6240\u6709\u7684\u4eba\u4eba\u7269\u7269 \u90fd\u5305\u5165\u5176\u4e2d\u3002 \u3000\u3000\u4e00\u7247\u6d53\u9ec4\u989c\u8272\u5145\u6ee1\u4e86\u7a7a\u95f4\uff0c\u4f46\u4ecd\u53ef\u4ee5\u770b\u5230\u8881\u7d2b\u70df\u3002\u56e0\u4e3a\u8881\u7d2b\u5168\u8eab\u6563\u53d1\u51fa\u4e00\u56e2\u7ea2\u5149\uff0c\u8fde\u5de7 \u513f\u3001\u83b2\u513f\u4e5f\u7f69\u5728\u7ea2\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u7ea2\u5f97\u5982\u8840\u5982\u82b1\uff0c\u4e5f\u7ea2\u5f97\u6676\u83b9\u900f\u660e\u3002 \u3000\u3000\u5c31\u50cf\u5728\u4e00\u56e2\u6d53\u70c8\u7684\u9ec4\u96fe\u4e2d\u71c3\u8d77\u4e86\u4e00\u56e2\u70c8\u706b\uff0c\u4e00\u76cf\u5de8\u5927\u7684\u7ea2\u706f\uff0c\u597d\u770b\u6781\u4e86\uff0c\u4e5f\u5996\u5f02\u6781\u4e86\uff1b \u8ba9\u4eba\u4e0d\u5fcd\u4e0d\u770b\uff0c\u4e5f\u8ba9\u4eba\u770b\u5f97\u5fc3\u5934\u98a4\u52a8\u3002 \u3000\u3000\u6676\u83b9\u8000\u76ee\u7684\u7ea2\u5149\u7a81\u7136\u5f00\u59cb\u5411\u56db\u5916\u81a8\u80c0\uff0c\u5c31\u50cf\u7206\u5c04\u7684\u70df\u82b1\uff0c\u4f46\u8fdb\u5165\u9ec4\u96fe\u4e09\u5c3a\uff0c\u5c31\u53c8\u6d88\u5931\u4e0d \u89c1\u3002\u5b87\u6587\u6210\u90fd\u65e0\u6cd5\u5b8c\u5168\u4e86\u89e3\u60c5\u52bf\u6f14\u5316\u8fc7\u7a0b\u6240\u8868\u8fbe\u51fa\u7684\u8baf\u606f\uff0c\u4f46\u5374\u77e5\u6653\u53cc\u65b9\u6b63\u5728\u6597\u6cd5\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8eab\u4e0a\u56f4\u7ed5\u7684\u7ea2\u5149\u4f3c\u662f\u60f3\u7a81\u7834\u4e09\u85cf\u548c\u5c1a\u90a3\u4ef6\u9ec4\u8888\u88df\u6240\u5e7b\u51fa\u7684\u9ec4\u4e91\u3001\u6d53\u96fe\uff0c\u4f46\u5374\u662f\u529b \u6240\u4e0d\u53ca\u3002 \u3000\u3000\u5f25\u6f2b\u91d1\u6bbf\u7684\u9ec4\u4e91\u4e5f\u65e0\u6cd5\u628a\u90a3\u56e2\u7ea2\u5149\u6df9\u6ca1\u3002 \u3000\u3000\u8881\u7d2b\u70df\u3001\u5de7\u513f\u3001\u83b2\u513f\uff0c\u5728\u8840\u7ea2\u900f\u660e\u7684\u5149\u534e\u7b3c\u7f69\u4e0b\u6e05\u695a\u53ef\u89c1\u3002 \u3000\u3000\u4e24\u56e2\u5149\u534e\u4f3c\u5b9e\u4f3c\u865a\uff0c\u5728\u5927\u6bbf\u4e2d\u76f8\u6301\u4e0d\u4e0b\u3002 \u3000\u3000\u5947\u4e8b\u53d1\u751f\u4e86\u2026\u2026\u8881\u7d2b\u70df\u6447\u52a8\u7740\u5934\uff0c\u79c0\u53d1\u6563\u843d\u4e86\u4e0b\u6765\uff0c\u63a9\u9762\u8986\u767d\u3002 \u3000\u3000\u62ab\u5934\u6563\u53d1\uff0c\u4f7f\u5f97\u5929\u59ff\u56fd\u8272\u7684\u7f8e\u8c8c\u4e00\u53d8\uff0c\u795e\u60c5\u6709\u4e9b\u8be1\u5f02\u83ab\u6d4b\u3002 \u3000\u3000\u53ea\u89c1\u5979\u4e00\u53cc\u7f8e\u5de7\u7684\u7389\u624b\u5ffd\u7136\u4e00\u9635\u6025\u6413\uff0c\u5411\u5916\u63a8\u51fa\uff0c\u7ea4\u7ea4\u7684\u5341\u6839\u624b\u6307\u7a81\u7136\u65e9\u51fa\u4e86\u706b\u82d7\uff0c\u55b7 \u5c04\u51fa\u4e09\u56db\u5c3a\u8fdc\u3002 \u3000\u3000\u706b\u82d7\u5230\u5904\uff0c\u9ec4\u4e91\u6d88\u9000\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7f13\u6b65\u884c\u51fa\u4e86\u5927\u6bbf\u3002 \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0cfrog OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u516d \u56de\u3000\u9ad8\u50e7\u672f\u6cd5 \u3000\u3000\u5de7\u513f\u548c\u83b2\u513f\u8ddf\u5728\u8881\u7d2b\u70df\u7684\u8eab\u540e\uff0c\u6b65\u5c65\u8f7b\u677e\uff0c\u5012\u662f\u8d70\u5f97\u4ece\u5bb9\uff0c\u770b\u4e0d\u51fa\u5979\u4eec\u6709\u4efb\u4f55\u6050\u60e7\u5403\u529b \u7684\u611f\u89c9\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8e0f\u51fa\u5927\u6bbf\uff0c\u624b\u4e0a\u5192\u73b0\u7684\u706b\u82d7\u4e5f\u7a81\u7136\u7184\u53bb\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u548c\u56db\u4f4d\u526f\u7edf\u9886\u867d\u7136\u90fd\u4eb2\u773c\u770b\u5230\u4e86\uff0c\u4f46\u4ecd\u7136\u65e0\u6cd5\u786e\u5b9a\u90a3\u662f\u771f\u7684\u706b\u82d7\uff1f\u8fd8\u53ea\u662f\u4e00\u79cd \u5149\u5f71\uff1f \u3000\u3000\u8881\u7d2b\u70df\u4f3c\u4e4e\u5f88\u75b2\u7d2f\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u900f\u6e7f\u4e86\u4e00\u8eab\u7f57\u8863\uff0e\u6709\u5982\u5728\u6025\u6d41\u3001\u72c2\u6d9b\u4e2d\u6323\u624e\u4e0a\u5cb8\u3002 \u3000\u3000\u5df2\u7ecf\u505c\u4e0b\u811a\u6b65\u7684\u5b57\u6587\u6210\u90fd\u7a81\u7136\u5feb\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u56fd\u5e08\u4f60\u53d7\u4f24\u4e86\uff1f\u201d \u3000\u3000\u8bed\u6c14\u4e2d\u5145\u6ee1\u7740\u5173\u5207\u60c5\u610f\u3002 \u3000\u3000\u201c\u548c\u5c1a\u5389\u5bb3\uff01\u201d\u8881\u7d2b\u70df\u82e6\u7b11\u9053\uff1a\u201c\u51e0\u4e4e\u88ab\u4ed6\u56f0\u4f4f\u4e86\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u5374\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u6697\u9053\uff1a\u90a3\u8499\u8499\u4e00\u7247\u6709\u5982\u6c34\u6c14\u7684\u9ec4\u8272\u4e91\u96fe\u771f\u80fd\u56f0\u5f97\u4f4f\u4eba\u5417\uff1f \u3000\u3000\u62ac\u5934\u770b\u53bb\u2014\u2014 \u3000\u3000\u5927\u6bbf\u4e2d\u5df2\u5168\u88ab\u9ec4\u8272\u7684\u96fe\u4e91\u5145\u6ee1\uff0c\u4efb\u4f55\u666f\u7269\u90fd\u5df2\u65e0\u6cd5\u77a7\u5230\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u8fd9\u6d53\u70df\u4f3c\u7684\u4e00\u56e2\u96fe\u4e91\uff0c\u8fd8\u771f\u80fd\u628a\u4eba\u56f0\u4f4f\u4e0d\u6210\uff1f \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u53d1\u89c9\u8eab\u4fa7\u4e0d\u8fdc\u5904\u4e00\u7247\u9752\u7816\uff0c\u6697\u8fd0\u771f\u6c14\uff0c\u53f3\u811a\u8f7b\u6311\uff0c\u534a\u5757\u9752\u7816\u5e26\u7740\u4e00\u9635\u8f7b\u5578\u5c04 \u5165\u4e86\u5927\u6bbf\u7684\u96fe\u4e91\u4e4b\u4e2d\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u7b97\u597d\u4e86\u65b9\u4f4d\uff0c\u7528\u51fa\u4e86\u76f8\u5f53\u7684\u5185\u529b\uff0c\u8fd9\u5757\u9752\u7816\u5e94\u8be5\u51fb\u7834\u4f9b\u53f0\u4e0a\u4e00\u5c0a\u4f5b\u50cf\uff0c\u7136\u540e\u6d1e \u7a7f\u5927\u6bbf\u7684\u5899\u58c1\u800c\u51fa\u3002 \u3000\u3000\u4f46\u9752\u7816\u6295\u5165\u90a3\u9ec4\u8272\u96fe\u4e91\u4e2d\uff0c\u4f3c\u662f\u51fb\u5728\u4e00\u7247\u539a\u9769\u4e4b\u4e0a\uff0c\u963b\u529b\u5947\u5f3a\u3002\u6df1\u5165\u4e0d\u8fc7\u534a\u5c3a\uff0c\u7adf\u7136\u5316 \u4f5c\u4e00\u56e2\u7070\u6cab\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u9707\u60ca\u4e86\uff01 \u3000\u3000\u8fd9\u662f\u4ec0\u4e48\u529b\u91cf\u554a\uff0c\u7adf\u4f7f\u7816\u77f3\u5316\u7070\uff1f \u3000\u3000\u60f3\u5411\u8881\u7d2b\u70df\u8bf7\u6559\uff0c\u4f46\u56de\u5934\u4e00\u671b\uff0c\u53c8\u4f7f\u5fc3\u795e\u9707\u98a4\u4e0d\u5df2\uff01 \u3000\u3000\u53ea\u89c1\u7d2b\u70df\u95ed\u76ee\u800c\u7acb\uff0c\u4e00\u5411\u5ae9\u7ea2\u7684\u8138\u513f\u4e5f\u53d8\u5f97\u4e00\u7247\u82cd\u767d\uff0c\u4f3c\u662f\u5728\u8fd0\u6c14\u8c03\u606f\u3002\u53ea\u662f\u7531\u5927\u6bbf\u4e2d \u884c\u51fa\u6bbf\u5916\uff0c\u4e5f\u4e0d\u8fc7\u4e00\u4e08\u591a\u4e9b\u7684\u8ddd\u79bb\uff0c\u7adf\u628a\u5979\u7d2f\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\u3002 \u3000\u3000\u81ea\u8ba4\u8bc6\u8881\u7d2b\u70df\u540e\uff0c\u4ece\u672a\u89c1\u8fc7\u5979\u5982\u6b64\u72fc\u72c8\u7684\u5f62\u8c61\u3002 \u3000\u3000\u9664\u4e86\u4e89\u53d6\u751f\u547d\u4e4b\u5916\uff0c\u8881\u7d2b\u70df\u4e0d\u4f1a\u5728\u4f17\u591a\u5c5e\u4e0b\u9762\u524d\u5982\u6b64\u4e0d\u582a\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u62db\u6765\u56db\u4e2a\u526f\u7edf\u9886\uff0c\u4f4e\u58f0\u9053\uff1b\u201c\u5168\u529b\u4fdd\u62a4\u56fd\u5e08\uff0c\u6211\u4eec\u975e\u4eba\u4e4b\u654c\uff0c\u4f46\u8981\u5148\u56fd\u5e08\u800c \u6b7b\u3002\u201d \u3000\u3000\u62bd\u51fa\u957f\u5251\uff0c\u524d\u884c\u4e09\u6b65\uff0c\u6321\u5728\u8881\u7d2b\u70df\u7684\u8eab\u540e\uff0c\u9762\u5bf9\u5927\u6bbf\u95e8\u53e3\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u3001\u5218\u98de\u9e4f\u4e5f\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u5b88\u5728\u8881\u7d2b\u70df\u7684\u56db\u5468\u3002\u4ed6\u4eec\u80cc\u5bf9\u8881\u7d2b\u70df\uff0c\u660e\u663e \u5730\u8868\u793a\u51fa\u4e00\u526f\u62fc\u6b7b\u76f8\u62a4\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u5fe0\u81e3\u3001\u4e49\u4ec6\uff0c\u62fc\u6b7b\u62a4\u4e3b\u7684\u666f\u8c61\u5927\u6982\u4e5f\u662f\u5982\u6b64\u4e86\u3002 \u3000\u3000\u56db\u4f4d\u526f\u7edf\u9886\u5fc3\u4e2d\u4e5f\u90fd\u660e\u767d\uff0c\u8881\u7d2b\u70df\u62b5\u62d2\u4e0d\u4f4f\u7684\u5f3a\u654c\uff0c\u4ed6\u4eec\u53c8\u4f55\u582a\u4e00\u51fb\uff1f \u3000\u3000\u5b57\u6587\u6210\u90fd\u5316\u4f5c\u6625\u6ce5\u4e3a\u62a4\u82b1\u7684\u5173\u7231\u60c5\u610f\uff0c\u4e0b\u4e86\u8fd9\u4e00\u9996\u4e25\u9177\u7684\u53e3\u8c15\uff0c\u56db\u4e2a\u526f\u7edf\u9886\u4e5f\u53ea\u597d\u8ba4\u547d \u4e86\u3002 \u3000\u3000\u201c\u8c22\u8c22\u8bf8\u4f4d\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u6e05\u67d4\u7684\u58f0\u97f3\u5728\u8033\u9645\u95f4\u54cd\u8d77\uff01 \u3000\u3000\u5b57\u6587\u6210\u90fd\u56de\u5934\u770b\u6765\uff0c\u8881\u7d2b\u70df\u5df2\u5c3d\u590d\u65e7\u89c2\uff0c\u62ab\u6563\u7684\u957f\u53d1\u4e5f\u5df2\u7ecf\u633d\u8d77\uff0c\u82b1\u5bb9\u6708\u8c8c\u4f3c\u5f80\u6614\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u4f4e\u58f0\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u5168\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u98de\u8d77\u4e86\u4e24\u988a\u7ea2\u6655\uff0c\u5e26\u7740\u4e09\u5206\u7f9e\u6029\uff0c\u9053\uff1a\u201c\u4f5b\u6cd5\u65e0\u8fb9\uff0c\u5e08\u7236\u7684\u544a\u8beb\uff0c\u8bda\u4e0d\u6b3a\u6211\uff0c\u4f46 \u4e09\u85cf\u548c\u5c1a\u53ef\u6076\uff0c\u4e0d\u80fd\u9976\u6055\uff01\u201d \u3000\u3000\u201c\u56fd\u5e08\uff0c\u6bbf\u4e2d\u65e0\u4eba\u4e86\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u5510\u4e09\u85cf\u548c\u674e\u4e16\u6c11\u90fd\u5df2\u9003\u8d70\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5148\u770b\u5230\u5927\u6bbf\u4e2d\u7684\u60c5\u52bf\uff0c\u5c31\u662f\u8fd9\u4e00\u5239\u4e4b\u95f4\uff0c\u96fe\u4e91\u6563\u5c3d\uff0c\u666f\u81f4\u5c3d\u590d\uff0c\u5927\u6bbf\u4e2d\u7a7a\u7a7a\u8361\u8361\uff0c\u54ea \u91cc\u8fd8\u6709\u4eba\u8e2a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7728\u52a8\u4e00\u4e0b\u5706\u5927\u7684\u773c\u775b\uff0c\u53cc\u76ee\u4e2d\u7a81\u7136\u653e\u5c04\u51fa\u4e24\u9053\u5947\u5149\u3002 \u3000\u3000\u6050\u6016\u7684\u773c\u795e\u7ed9\u4eba\u4e00\u79cd\u5229\u5203\u7a7f\u5fc3\u7684\u611f\u89c9\uff0c\u5b57\u6587\u6210\u90fd\u548c\u56db\u4f4d\u526f\u7edf\u9886\u90fd\u4e0d\u6562\u548c\u5979\u7684\u76ee\u5149\u89e6\u63a5\uff0c \u6025\u6025\u7684\u8f6c\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u201c\u548c\u5c1a\uff0c\u4e0d\u8981\u8d70\u554a\uff01\u54b1\u4eec\u8fd8\u672a\u51b3\u80dc\u8d1f\u5462\uff01\u201d \u3000\u3000\u968f\u7740\u90a3\u8361\u6f3e\u5728\u8033\u9645\u7684\u751c\u7f8e\u58f0\u97f3\uff0c\u8881\u7d2b\u70df\u5374\u7a81\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ee5\u5b57\u6587\u6210\u90fd\u7b49\u8033\u76ee\u4e4b\u7075\uff0c\u4e5f\u4e0d\u77e5\u8881\u7d2b\u70df\u5982\u4f55\u8d70\u7684\uff1f\u5954\u5411\u4e86\u54ea\u4e2a\u65b9\u4f4d\uff1f \u3000\u3000\u201c\u542c\u4e0d\u5230\u4e00\u70b9\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff01\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u8f7b\u529f\u4e4b\u9ad8\uff0c\u5df2\u5230\u4e86\u98de\u884c\u7edd\u8ff9\u7684\u5883\u754c\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u4e24\u4eba\u4e5f\u4e0d\u77e5\u9053\u8881\u7d2b\u70df\u600e\u4e48\u8d70\u7684\uff1f \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u5510\u4e09\u85cf\u3001\u674e\u4e16\u6c11\u5df2\u7ecf\u8d70\u5931\uff0c\u8881\u56fd\u5e08\u4f3c\u662f\u5df2\u6444\u654c\u800c\u53bb\uff0c\u6211\u4eec\u5462\uff1f\u662f\u8981\u7559\u8fd9\u91cc\uff1f \u8fd8\u662f\u8981\u8ffd\u89c5\u8e2a\u8ff9\u6709\u6240\u884c\u52a8\uff1f\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u674e\u4e16\u6c11\u3001\u8881\u56fd\u5e08\u7684\u53bb\u5411\uff0c\u5982\u4f55\u8ffd\u89c5\u654c\u8e2a\uff1f\u4f46\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u62c5\u5fc3\u59d1\u5a18\u884c\u8e2a\uff01\u201d\u83b2\u513f\u9053\uff1b\u201c\u6211\u4eec\u627e\u4e0d\u5230\u5979\uff0c\u5979\u5374\u80fd\u627e\u5230\u6211\u4eec\u3002\u603b\u7edf\u9886\uff0c\u82e5\u662f\u89c9 \u5f97\u4e0d\u5b9c\u5728\u6b64\u5730\u505c\u7559\uff0c\u6362\u4e2a\u5730\u65b9\u7b49\u5019\u59d1\u5a18\u3002\u201d \u3000\u3000\u8fd9\u5ea7\u5927\u6bbf\u7559\u4e0b\u4e86\u5927\u591a\u7684\u7591\u95ee\u3001\u53e4\u602a\uff0c\u4e45\u7559\u5fc5\u4f1a\u60d1\u4e71\u519b\u5fc3\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5b57\u6587\u6210\u90fd\u4e0b\u4ee4\u64a4\u79bb\u4e86\u8352\u51c9\u7684\u53e4\u5bfa\u3002 \u3000\u3000\u519b\u9a6c\u64a4\u8d70\uff0c\u5927\u6bbf\u4e00\u89d2\u5904\u7a81\u7136\u51fa\u73b0\u4e86\u8eab\u7740\u9ec4\u888d\u8888\u88df\u7684\u4e09\u85cf\u548c\u5c1a\u548c\u4f9b\u53f0\u4e0a\u56db\u5c0a\u4f5b\u50cf\u3002 \u3000\u3000\u5510\u4e09\u85cf\u5408\u638c\u5f53\u80f8\uff0c\u5bf9\u7740\u5176\u4e2d\u4e00\u5c0a\u4f5b\u50cf\u8eac\u8eab\u4e00\u62dc\uff0c\u4e00\u9635\u9ec4\u70df\u95ea\u8fc7\uff0c\u771f\u76f8\u663e\u73b0\uff0c\u5316\u4f5c\u674e\u4e16\u6c11 \u95ed\u76ee\u800c\u5750\u3002 \u3000\u3000\u5510\u4e09\u85cf\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u8881\u7d2b\u70df\u5df2\u88ab\u6211\u884c\u6cd5\u5f15\u5165\u6b67\u9014\uff0c\u4f46\u6050\u6015\u4e0d\u80fd\u8ff7\u60d1\u5979\u592a\u4e45\uff0c\u4e8c\u516c\u5b50\u8bf7 \u501f\u6b64\u65f6\u673a\u79bb\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u548c\u5c1a\u4f5b\u6cd5\u65e0\u8fb9\uff0c\u4f55\u4e0d\u968f\u4e16\u6c11\u540c\u5f80\u592a\u539f\uff0c\u4f60\u51fa\u5bb6\u793c\u4f5b\uff0c\u7acb\u5fd7\u4ee5\u6148\u822a\u666e\u6e21\u4f17\u751f\uff0c\u81ea\u5f53\u5148\u62ef \u6551\u4e07\u6c11\u4e8e\u6c34\u706b\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u5c0f\u50e7\u4fee\u7985\uff0c\u4e0d\u61c2\u6cd5\u672f\uff0c\u8fd9\u88ad\u8888\u88df\u4e3a\u8fbe\u6469\u7956\u5e08\u9057\u7269\uff0c\u501f\u7528\u6570\u65e5\u5373\u5c06\u5f52\u8fd8\u3002\u4eca\u65e5\u4e00\u6664\uff0c\u7ed3 \u7f18\u4e8e\u4e8c\u5341\u5e74\u540e\uff0c\u5c4a\u65f6\u8fd8\u671b\u4e8c\u516c\u5b50\u80fd\u8bb0\u5f97\u4eca\u65e5\u4f1a\u6664\u4e4b\u60c5\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8881\u7d2b\u70df\u672f\u6cd5\u9ad8\u5f3a\u3002\u8881\u5929\u7f61\u548c\u674e\u6df3\u98ce\u90fd\u975e\u5176\u654c\uff0c\u548c\u5c1a\u6709\u4ee5\u6559\u6211\u4e4e\uff1f\u201d \u3000\u3000\u5510\u4e09\u85cf\u9053\uff1a\u201c\u5929\u884c\u5065\uff0c\u541b\u5b50\u81ea\u5f3a\u4e0d\u606f\uff1b\u8fd9\u756a\u52ab\u6740\u4e4b\u540e\uff0e\u5929\u4e0b\u6709\u6570\u5341\u5e74\u592a\u5e73\u5c81\u6708\uff0c\u4e8c\u516c\u5b50 \u5927\u613f\u53ef\u507f\uff0c\u5c0f\u50e7\u65e0\u80fd\u76f8\u52a9\uff0c\u4f46\u53e6\u6709\u9ad8\u4eba\u6548\u547d\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u4f1a\u5728\u5bfa\u5916\u57cb\u4f0f\uff0c\u4e0d\u8fc7\u5c0f\u50e7\u4e5f\u5df2\u9080\u4eba\u76f8\u52a9\u3002\u8fd9\u91cc\u6709\u9ed1\u7ee2\u4e00\u65b9\uff0c\u8bf7\u63a9\u8986\u5934\u8138\uff0c\u51fa\u5bfa \u53bb\u5427\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u548c\u5c1a\u65e2\u65e0\u51fa\u4e16\u4e4b\u5ff5\uff0c\u533a\u533a\u600e\u6562\u52c9\u5f3a\u3002 \u3000\u3000\u53ea\u8981\u82cd\u751f\u6709\u5e78\uff0c\u533a\u533a\u4e00\u4eba\u7684\u751f\u6b7b\u4f55\u59a8\uff1f\u201d \u3000\u3000\u63a5\u8fc7\u9ed1\u7ee2\uff0c\u5305\u4f4f\u5934\u8138\uff0c\u5927\u6b65\u884c\u51fa\u6bbf\u5916\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u6ca1\u6709\u56de\u5934\u5f20\u671b\uff0e\u770b\u4e00\u773c\uff01\u4ed6\u5373\u4f1a\u91cd\u56de\u5927\u6bbf\u3002 \u3000\u3000\u201c\u5c0f\u50e7\u4fee\u60ee\u5b97\uff0c\u7acb\u5fd7\u53d6\u56de\u5343\u5377\u7ecf\u6587\uff0c\u5149\u5927\u6211\u4f5b\u6559\u4e49\u3002\u672f\u6cd5\u4e00\u9053\uff0c\u53ea\u89c9\u5f97\u4e00\u70b9\u76ae\u6bdb\uff0c\u804a\u5907 \u4e00\u683c\u62a4\u8eab\u800c\u5df2\uff0c\u975e\u4e0d\u4e3a\u4e5f\uff0c\u5b9e\u4e0d\u80fd\u4e5f\u2026\u2026\u201d \u3000\u3000\u6697\u6697\u7684\u4f4e\u8bf5\u5b8c\u5fc3\u4e2d\u610f\u613f\uff0c\u7a81\u7136\u5f20\u5634\u5410\u51fa\u4e24\u53e3\u9c9c\u8840\uff0c\u6025\u6025\u4f38\u624b\u6276\u4f4f\u4e86\u4f9b\u53f0\u624d\u7a33\u4f4f\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u5982\u679c\u674e\u4e16\u6c11\u770b\u5230\u4e86\uff0c\u7edd\u4e0d\u4f1a\u51b3\u7136\u8d70\u5f00\u3002 \u3000\u3000\u5510\u4e09\u85cf\u7684\u63a8\u65ad\u4e0d\u9519\uff0c\u5b57\u6587\u6210\u90fd\u679c\u7136\u5728\u5bfa\u5916\u5e03\u4e0b\u4e86\u773c\u7ebf\uff0c\u76d1\u89c6\u4e86\u56db\u9762\u516b\u65b9\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e00\u51fa\u5bfa\u9662\uff0c\u5c31\u843d\u5165\u4e86\u76d1\u89c6\u4e4b\u4e2d\u3002 \u3000\u3000\u4f46\u674e\u4e16\u6c11\u9ed1\u7ee2\u5305\u5934\uff0c\u867d\u7136\u843d\u5165\u773c\u7ebf\u7684\u53cc\u76ee\u4e4b\u4e2d\uff0c\u4f46\u90a3\u4eba\u5374\u65e0\u6cd5\u80af\u5b9a\u4ed6\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u6562\u8f7b \u6613\u51fa\u624b\uff0c\u73b0\u8eab\u963b\u62e6\uff0c\u53ea\u597d\u628a\u8baf\u606f\u4f20\u62a5\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u786e\u5b9e\u662f\u4e00\u6279\u8bad\u7ec3\u6709\u7d20\u7684\u6b66\u58eb\uff0c\u4ed6\u4eec\u6709\u4e00\u5957\u4f20\u8baf\u4e4b\u6cd5\uff0c\u7528\u4e0d\u540c\u989c\u8272\u7684\u7ee2\u5e15\u4f20\u51fa\u8baf\u606f\u7684\u91cd \u70b9\uff0c\u91cd\u91cd\u4f20\u63a5\uff0c\u65e2\u5feb\u901f\u4e5f\u79d8\u5bc6\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u64a4\u79bb\u5f97\u5e76\u4e0d\u592a\u8fdc\uff0c\u5f88\u5feb\u63a5\u5230\u8baf\u606f\u3002 \u3000\u3000\u7530\u5f53\u3001\u8427\u96e8\u7acb\u523b\u4e0a\u9a6c\uff0c\u7eb5\u9a91\u5954\u6765\u3002 \u3000\u3000\u4f46\u4e24\u4eba\u5fc3\u4e2d\u540c\u65f6\u6709\u7740\u4e00\u4e2a\u7591\u95ee\uff0c\u674e\u4e16\u6c11\u8eb2\u5728\u54ea\u91cc\uff1f\u5927\u6bbf\u4e2d\u4e0d\u89c1\u6709\u4eba\uff0c\u4e24\u767e\u6b66\u58eb\u6ee1\u5e03\u5eca \u53a2\uff0c\u5bfa\u4e2d\u7edd\u65e0\u53ef\u4f9b\u85cf\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u5982\u679c\u90a3\u4eba\u4e0d\u662f\u674e\u4e16\u6c11\uff0c\u90a3\u53c8\u4f1a\u662f\u8c01\u5462\uff1f \u3000\u3000\u600e\u4e48\u80fd\u591f\u7792\u7740\u8881\u7d2b\u70df\u7684\u8033\u76ee\uff1f \u3000\u3000\u7530\u5f53\u3001\u8427\u96e8\u6700\u6015\u7684\u662f\u8ffd\u7684\u662f\u5510\u4e09\u85cf\uff0c\u5c0f\u548c\u5c1a\u80fd\u628a\u8881\u7d2b\u70df\u6597\u5f97\u62ab\u5934\u6563\u53d1\u3001\u8eab\u4f53\u53d7\u4f24\uff0c\u8981\u640f \u6740\u4e24\u4eba\u5c82\u4e0d\u662f\u6613\u5982\u53cd\u638c\uff01 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u4e0d\u6015\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u5c31\u7b97\u9047\u4e0a\u5f3a\u654c\uff0c\u4e5f\u53ef\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u652f\u6491\u4e2a\u4e8c \u4e94\u5341\u62db\uff0c\u518d\u62db\u4eba\u5e2e\u624b\uff0c\u6765\u4e2a\u8054\u624b\u653b\u654c\u6216\u8f66\u8f6e\u5927\u6218\u3002 \u3000\u3000\u4f46\u5982\u9047\u4e0a\u7cbe\u901a\u672f\u6cd5\u7684\u4eba\uff0c\u90a3\u5c31\u5168\u65e0\u62db\u67b6\u4e4b\u529b\u4e86\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u4e24\u4eba\u5fc3\u4e2d\u867d\u6709\u7740\u76f8\u540c\u7684\u60f3\u6cd5\uff0c\u4f46\u90fd\u6ca1\u6709\u5f00\u53e3\u8bf4\u51fa\u6765\uff0c\u7167\u7740\u773c\u7ebf\u7559\u4e0b\u7684\u8def\u6807\u8ffd \u8d76\u3002 \u3000\u3000\u8bf4\u4e86\u53c8\u5982\u4f55\uff1f \u3000\u3000\u5f92\u4e71\u4eba\u610f\u554a\uff1f \u3000\u3000\u9a6c\u884c\u5982\u98de\uff0c\u5df2\u8d8a\u8fc7\u53e4\u5bfa\uff0c\u5230\u4e86\u4e00\u5c94\u8def\u53e3\u5904\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u6b66\u58eb\u7ad9\u5728\u8def\u53e3\u51fa\u795e\u3002 \u3000\u3000\u8427\u96e8\u4e00\u52d2\u9a6c\u7f30\uff0c\u9053\uff1a\u201c\u600e\u4e48\u505c\u4e0b\u6765\u4e86\uff1f\u201d \u3000\u3000\u6b66\u58eb\u60f6\u7136\u5730\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u600e\u4e48\u8ffd\u5440\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\u554a\uff1f\u201d\u7530\u5f53\u9053\uff1a\u201c\u7ed9\u6211\u8bf4\u6e05\u695a\u4e00\u4e9b\uff01\u201d \u3000\u3000\u770b\u51fa\u4e86\u90a3\u6b66\u58eb\u662f\u5c5e\u4e8e\u81ea\u5df1\u7edf\u5e05\u7684\u4e00\u961f\uff0c\u7530\u5f53\u975e\u5e38\u4e0d\u6ee1\u3002 \u3000\u3000\u6b66\u58eb\u614c\u609a\u5730\u9053\uff1a\u201c\u8ffd\u5230\u4e86\u8fd9\u5c94\u8def\u53e3\u5904\uff0c\u4e00\u4e2a\u4eba\u7a81\u7136\u53d8\u6210\u4e86\u4e24\u4e2a\u4eba\uff0c\u4e00\u822c\u7684\u4e2a\u5934\uff0c\u4e00\u6837\u7684 \u8863\u670d\uff0c\u4e00\u6837\u7684\u9ed1\u7ee2\u5305\u5934\uff0c\u5374\u5206\u884c\u5165\u4e24\u4e2a\u5c94\u8def\uff0c\u5c5e\u4e0b\u4e0d\u77e5\u9053\u600e\u4e48\u8ffd\u5440\uff1f\u201d \u3000\u3000\u8427\u96e8\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6709\u4eba\u63a5\u5e94\uff01\u4ed6\u4eec\u8d70\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u7247\u523b\u5de5\u592b\u3002\u201d \u3000\u3000\u201c\u7530\u5144\uff0c\u4e0d\u80fd\u602a\u4f60\u7684\u4eba\uff0c\u54b1\u4eec\u5206\u5934\u8ffd\u5427\uff01\u201d \u3000\u3000\u8427\u96e8\u66ff\u90a3\u4e2a\u6b66\u58eb\u8bb2\u4e86\u4eba\u60c5\uff0c\u5f53\u5148\u5411\u53f3\u8fb9\u7684\u5c94\u8def\u8ffd\u53bb\u3002 \u3000\u3000\u201c\u5feb\u53bb\u7980\u62a5\u603b\u7edf\u9886\uff01\u201d \u3000\u3000\u7530\u5f53\u5429\u5490\u8fc7\u5c5e\u4e0b\u7684\u6b66\u58eb\u4e4b\u540e\uff0c\u8f6c\u8eab\u5411\u5de6\u9762\u5c94\u9053\uff0c\u653e\u8f94\u5982\u98de\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u7247\u5e7f\u88a4\u7684\u7530\u91ce\uff0c\u6625\u8015\u672a\u64ad\uff0c\u5e73\u65f6\u5343\u91cc\u89c6\u754c\u8fbd\u9614\u3002\u7530\u5f53\u6781\u76ee\u56db\u987e\uff0c\u770b\u5230\u4e86\u8427\u96e8\u7eb5\u9a91 \u5982\u98de\uff0c\u6b63\u5168\u529b\u8ffd\u8d76\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u770b\u5230\u4e86\u524d\u9762\u7684\u5927\u9053\uff0c\u4e5f\u6709\u4e00\u6761\u4eba\u5f71\uff0c\u6b63\u65bd\u5c55\u9646\u5730\u98de\u884c\u529f\u592b\uff0c\u5411\u524d\u5954\u9a70\uff0c\u7acb\u523b\u52a0\u97ad\u98de\u5954\u8ffd \u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8f7b\u8eab\u98de\u7eb5\u672f\uff0c\u77ed\u7a0b\u7ade\u901f\uff0c\u80fd\u591f\u5feb\u903e\u5954\u9a6c\u3002\u4f46\u5728\u4e00\u671b\u65e0\u9645\u7684\u5e73\u539f\u4e0a\uff0c\u5f88\u96be\u548c\u9a6c\u7684\u8010\u529b\u76f8\u7ade \u4e89\u3002 \u3000\u3000\u4f55\u51b5\u7530\u5f53\u7b49\u9a91\u7684\u9a6c\u53c8\u662f\u5343\u4e2d\u9009\u4e00\u7684\u597d\u9a6c\u3002 \u3000\u3000\u5927\u6982\u9ed1\u8863\u4eba\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u81ea\u52a8\u653e\u7f13\u4e86\u5954\u884c\u7684\u901f\u5ea6\uff0c\u4ee5\u514d\u8017\u5c3d\u4f53\u529b\u3002 \u3000\u3000\u7530\u5f53\u5f88\u5feb\u5730\u8ffd\u8fd1\u4e94\u5341\u4e08\u5185\u3002 \u3000\u3000\u8499\u9762\u4eba\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u663e\u662f\u8981\u5c3d\u5feb\u6062\u590d\u4f53\u80fd\u3002\u51c6\u5907\u5e94\u6218\u3002 \u3000\u3000\u53cc\u65b9\u76f8\u8ddd\u4e94\u4e08\u5de6\u53f3\u65f6\uff0c\u7530\u5f53\u7a81\u7136\u98de\u8eab\u79bb\u978d\uff0c\u4e00\u63a0\u4e4b\u52bf\u5c31\u505c\u5728\u8499\u9762\u4eba\u8eab\u524d\u56db\u4e94\u5c3a\u5904\uff0c\u53cc\u65b9 \u4e5f\u540c\u65f6\u5251\u51fa\u9798\uff0c\u7d27\u63e1\u4e8e\u624b\u4e2d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9759\u7acb\u672a\u52a8\uff0c\u9690\u9690\u95fb\u5f97\u4ed6\u5598\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u653e\u817f\u5954\u8d70\uff0c\u75b2\u52b3\u5c1a\u672a\u5c3d\u590d\u3002 \u3000\u3000\u7530\u5f53\u53ea\u6253\u91cf\u90a3\u4eba\u8eab\u6750\u4e00\u773c\uff0c\u5df2\u77e5\u4e0d\u662f\u674e\u4e16\u6c11\u4e86\uff0c\u7a81\u7136\u706b\u4e0a\u5fc3\u5934\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f \u7adf\u7136\u5192\u5145\u674e\u4e16\u6c11\uff0c\u81ea\u5bfb\u6b7b\u8def\u3002\u201d \u3000\u3000\u53cc\u5200\u9f50\u6325\uff0c\u731b\u7136\u653b\u4e0a\u3002 \u3000\u3000\u8499\u9762\u4eba\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u53f3\u624b\u4e00\u7ffb\uff0c\u4e00\u628a\u5355\u5200\u98de\u5c04\u800c\u51fa\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5bf9\u5f00\u4e86\u53cc\u5200\uff0c\u529b\u9053\u5341 \u5206\u5f3a\u5927\u3002 \u3000\u3000\u7530\u5f53\u53cc\u5200\u4ea4\u9519\uff0c\u653b\u52bf\u5feb\u5982\u95ea\u7535\uff0c\u6068\u4e0d\u5f97\u628a\u8499\u9762\u4eba\u5288\u5728\u4e71\u5200\u4e4b\u4e0b\uff0c\u63ed\u5f00\u4ed6\u8499\u9762\u9ed1\u5dfe\uff0c\u4e00\u89c1 \u4ed6\u771f\u6b63\u9762\u76ee\u3002 \u3000\u3000\u4f46\u8499\u9762\u4eba\u5200\u6cd5\u7eaf\u719f\uff0c\u800c\u4e14\u6ca1\u6709\u604b\u6218\u4e4b\u5fc3\uff0c\u4e14\u6218\u4e14\u9000\u3002 \u3000\u3000\u8fde\u653b\u4e86\u6570\u5341\u5200\u4e4b\u540e\uff0e\u65e5\u5f53\u5df2\u5fc3\u4e2d\u660e\u767d\u8499\u9762\u4eba\u4e0d\u613f\u89c1\u4eba\uff0c\u4f46\u5374\u529f\u529b\u6df1\u539a\uff0c\u662f\u4e00\u4f4d\u6c5f\u6e56\u9ad8 \u4eba\uff0c\u771f\u6253\u8d77\u6765\uff0c\u4e0d\u662f\u4e00\u4e24\u767e\u62db\u5185\u53ef\u4ee5\u5206\u51fa\u80dc\u8d1f\u7684\u3002 \u3000\u3000\u7530\u5f53\u53ea\u597d\u6309\u4e0b\u5fc3\u5934\u7684\u6012\u706b\uff0c\u96c6\u4e2d\u7cbe\u795e\u62fc\u6218\u3002 \u3000\u3000\u8427\u96e8\u4e5f\u9047\u4e0a\u4e86\u540c\u6837\u7684\u70e6\u607c\uff0c\u5feb\u8ffd\u4e0a\u8499\u9762\u4eba\u65f6\uff0c\u8499\u9762\u4eba\u7acb\u523b\u8f6c\u8eab\u63a5\u6218\uff0c\u4e5f\u662f\u4e00\u628a\u5355\u5200\uff0c\u529b \u654c\u8427\u96e8\u7684\u98ce\u82b1\u5251\u6cd5\uff0c\u4e14\u6218\u4e14\u9000\u3002 \u3000\u3000\u8427\u96e8\u4e5f\u7531\u90a3\u8499\u9762\u4eba\u7684\u8eab\u6750\u770b\u51fa\uff0c\u4e0d\u662f\u674e\u4e16\u6c11\u4e86\u3002 \u3000\u3000\u4f46\u90a3\u8499\u9762\u4eba\u5200\u6cd5\u7eaf\u719f\uff0c\u5185\u529b\u6df1\u539a\u3002 \u3000\u3000\u8427\u96e8\u6025\u5251\u5feb\u653b\uff0c\u53ea\u80fd\u903c\u5f97\u4ed6\u8fde\u8fde\u540e\u9000\uff0c\u5374\u65e0\u6cd5\u4f24\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u8427\u96e8\u4e5f\u540c\u65f6\u53d1\u89c9\u4e86\u9ed1\u8863\u4eba\u540e\u9000\u5e76\u975e\u662f\u5168\u65e0\u7ae0\u6cd5\uff0c\u800c\u662f\u6709\u4e00\u5b9a\u7684\u65b9\u4f4d\uff0c\u79bb\u5f00\u4e86\u5927\u9053\uff0c\u5411\u4e00 \u5904\u571f\u5761\u4e0a\u9000\u53bb\u3002 \u3000\u3000\u90a3\u91cc\u957f\u6ee1\u4e86\u8346\u4e1b\u3001\u6742\u8349\uff0c\u662f\u8fd9\u4e00\u7247\u5e7f\u9614\u5e73\u539f\u4e2d\u5c11\u89c1\u7684\u4e00\u5904\u6240\u5728\u3002 \u3000\u3000\u5b83\u8fdc\u79bb\u5927\u9053\u6570\u91cc\uff0c\u7a81\u73b0\u5728\u4e00\u7247\u7530\u91ce\u4e2d\u3002 \u3000\u3000\u8427\u96e8\u770b\u51fa\u654c\u4eba\u76ee\u7684\uff0c\u4f46\u5374\u65e0\u80fd\u963b\u6b62\u3002 \u3000\u3000\u4e5f\u65e0\u6cd5\u6539\u53d8\u8499\u9762\u4eba\u540e\u9000\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8427\u96e8\u5fc3\u4e2d\u5df2\u660e\u767d\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u7684\u6b66\u529f\u5c31\u7b97\u5dee\u4ed6\u4e00\u70b9\uff0c\u4e5f\u5c5e\u6709\u9650\u5f97\u5f88\uff0c\u4ed6\u6709\u7740\u8db3\u591f\u7684\u81ea\u4fdd\u80fd\u529b\uff0c\u5374\u4e0d\u6c42\u80dc\u654c\uff0c\u8282 \u8282\u8d25\u9000\uff0c\u638c\u63e1\u4e86\u9000\u56de\u65b9\u4f4d\u3002 \u3000\u3000\u600e\u4e48\u5192\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u6b66\u529f\u9ad8\u5f3a\u7684\u8499\u9762\u4eba\u5462\uff1f \u3000\u3000\u8427\u96e8\u5fc3\u4e2d\u6697\u9053\uff1a\u662f\u7559\u5b88\u5019\u5e9c\u4e2d\u7684\u52c7\u58eb\uff0c\u6551\u4ed6\u4eec\u7684\u5c0f\u4e3b\u4eba\uff0c\u5374\u53c8\u4e0d\u6562\u660e\u706b\u6267\u4ed7\u548c\u671d\u5ef7\u4f5c \u5bf9\uff0c\u7528\u9ed1\u5dfe\u5305\u4e86\u5934\u8138\uff1f\u8fd8\u662f\u6c5f\u6e56\u4e0a\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u95fb\u5f97\u4e86\u674e\u4e16\u6c11\u88ab\u64d2\u89e3\u62bc\u4eba\u4eac\u7684\u6d88\u606f\uff0c\u805a\u4f1a\u4e8e \u6b64\uff0c\u8bbe\u8ba1\u6551\u4eba\uff1f\u8fd9\u4e9b\u4eba\u662f\u5426\u548c\u5510\u4e09\u85cf\u548c\u5c1a\u6709\u6240\u5173\u8fde\uff1f \u3000\u3000\u5fc3\u4e8b\u7eb7\u81f3\u6c93\u6765\uff0c\u8427\u96e8\u624b\u4e2d\u7684\u5251\u52bf\u53d8\u7f13\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u624b\u4e2d\u5355\u5200\u4e5f\u968f\u7740\u7f13\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u662f\u7684\uff0c\u4ed6\u6ca1\u6709\u4e89\u80dc\u4e4b\u610f\uff0c\u4e5f\u6ca1\u6709\u4f24\u4eba\u4e4b\u5fc3\uff0c\u552f\u4e00\u7684\u76ee\u7684\u4f3c\u662f\u5728\u66ff\u674e\u4e16\u6c11\u4e89\u53d6\u9003\u8d70\u7684\u65f6 \u95f4\u3002 \u3000\u3000\u4e00\u9635\u6025\u9aa4\u7684\u9a6c\u8e44\u58f0\u72c2\u5954\u800c\u6765\u3002 \u3000\u3000\u8427\u96e8\u5251\u52bf\u4e00\u7d27\uff0c\u5e0c\u671b\u963b\u6b62\u8499\u9762\u4eba\u540e\u9000\u4e4b\u52bf\u3002 \u3000\u3000\u53ea\u542c\u8e44\u58f0\uff0c\u4ed6\u5df2\u77e5\u6765\u4eba\u662f\u8c01\uff1f \u3000\u3000\u4e00\u9053\u6025\u98ce\u63a0\u8fc7\u8427\u96e8\u7684\u5934\u9876\uff0c\u6251\u5411\u90a3\u8499\u9762\u4eba\u3002 \u3000\u3000\u8499\u9762\u4eba\u6025\u4ee5\u5200\u5149\u62a4\u8eab\uff0c\u4f46\u662f\u6765\u4eba\u5374\u4ee5\u4e00\u80a1\u6d8c\u51fa\u7684\u6f5c\u529b\u903c\u4f4f\uff0c\u626f\u8d70\u4e86\u90a3\u8499\u9762\u4eba\u5305\u5934\u7684\u9ed1 \u5dfe\u3002 \u3000\u3000\u201c\u662f\u548c\u5c1a\uff01\u201d \u3000\u3000\u8427\u96e8\u505c\u4f4f\u4e86\u624b\u4e2d\u7684\u957f\u5251\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u5de6\u624b\u6293\u7740\u626f\u4e0b\u7684\u9ed1\u5dfe\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u5510\u4e09\u85cf\u548c\u5c1a\u7684\u540c\u4f19\uff01\u51fa\u5bb6\u4eba\uff0c\u4f60\u4e5f\u6562\u548c\u671d \u5ef7\u4f5c\u5bf9\uff1f\u4e0d\u6015\u7687\u4e0a\u4e0b\u4ee4\u62c6\u4e86\u4f60\u4eec\u7684\u5e99\u5b87\uff1f\u201d \u3000\u3000\u662f\u7684\uff0c\u9664\u4e86\u5b57\u6587\u6210\u90fd\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u5728\u4e00\u51fb\u4e4b\u4e0b\u626f\u4e0b\u548c\u5c1a\u7684\u5305\u5934\u9ed1\u5dfe\uff1f \u3000\u3000\u548c\u5c1a\u624b\u6a2a\u5355\u5200\uff0c\u9ad8\u55a7\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u8d2b\u50e7\u6613\u88c5\u800c\u884c\u6216\u6709\u4e0d\u5f53\uff0c\u4f46\u8bf8\u4f4d\u65e0\u7aef\u8ffd\u6740\uff0c\u6b32 \u53d6\u8d2b\u50e7\u6027\u547d\uff0c\u8d2b\u50e7\u867d\u7136\u62d4\u5200\u62d2\u654c\uff0c\u4e14\u6218\u4e14\u9000\uff0c\u5168\u91c7\u5b88\u52bf\uff0c\u4ece\u672a\u53cd\u51fb\u8fd8\u653b\uff0c\u5b9e\u4e0d\u77e5\u4f55\u7f6a\u4e4b \u6709\uff1f\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u8bdd\u662f\u5b9e\u8bdd\uff0c\u4f46\u7528\u5fc3\u53ef\u8bdb\uff01\u4f60\u4eec\u63a9\u9970\u53bb\u672c\u6765\u9762\u76ee\uff0c\u662f\u5e2e\u52a9\u674e\u4e16\u6c11\u9003\u907f\u8ffd\u6740\uff0c\u7518 \u613f\u4f5c\u4ed6\u66ff\u8eab\uff0c\u4ed6\u53ef\u662f\u94a6\u62ff\u8981\u72af\uff01\u201d \u3000\u3000\u201c\u6b32\u52a0\u4e4b\u7f6a\uff0c\u4f55\u60a3\u65e0\u8bcd\uff1f\u8d2b\u50e7\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u5c1a\u4e0d\u8981\u72e1\u8fa9\uff01\u201d\u5b57\u6587\u6210\u90fd\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eca\u5929\u5982\u4e0d\u6740\u4f60\u4eec\uff0c\u53ea\u6015\u65e0\u6cd5\u4f7f\u4f60\u4eec\u77e5\u6653 \u5389\u5bb3\uff0c\u8eab\u5728\u4e94\u884c\u5916\uff0c\u5fc3\u5728\u7ea2\u5c18\u4e2d\uff1b\u4e00\u6837\u8981\u53d7\u5230\u671d\u5ef7\u5f8b\u6cd5\u7684\u5236\u88c1\uff01\u201d \u3000\u3000\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u4e00\u5251\u523a\u53bb\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u8fd9\u4e00\u5251\u7684\u653b\u52bf\u548c\u8427\u96e8\u7684\u5927\u5927\u7684\u4e0d\u540c\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5251\u5982\u7f51\u7f57\uff0c\u7b3c\u7f69\u4e86\u56db\u9762\u516b\u65b9\u3002 \u3000\u3000\u548c\u5c1a\u65e0\u6cd5\u53ef\u9003\uff0c\u4e5f\u65e0\u5904\u53ef\u907f\uff0c\u53ea\u80fd\u4e3e\u5200\u5c01\u6321\u3002 \u3000\u3000\u4ed6\u7528\u7684\u672c\u662f\u6212\u5200\uff0c\u6b64\u6b21\u4e3a\u63a9\u9970\u8eab\u4efd\uff0c\u6362\u7528\u4e86\u4e00\u822c\u7684\u5355\u5200\uff0c\u4e3e\u5200\u8fce\u5251\uff0c\u7528\u4e00\u5f0f\u201c\u91ce\u706b\u70e7 \u5929\u201d\uff0c\u5200\u52bf\u5e7b\u5316\u51fa\u4e00\u7247\u5149\u8292\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u6321\u4e0d\u4f4f\u5b57\u6587\u6210\u90fd\u7684\u51cc\u5389\u5251\u52bf\uff0c\u5200\u5149\u88ab\u957f\u5251\u9707\u5f00\uff0c\u4eba\u4e5f\u88ab\u5b57\u6587\u6210\u90fd\u4e00\u811a\u8e22\u51fa\u4e86\u516b\u5c3a \u4ee5\u5916\u3002 \u3000\u3000\u8427\u96e8\u4e00\u63a0\u800c\u81f3\uff0c\u4e00\u5251\u523a\u4e0b\u3002 \u3000\u3000\u548c\u5c1a\u8fd8\u672a\u7ad9\u8d77\uff0c\u5251\u52bf\u5df2\u5230\u3002 \u3000\u3000\u53ea\u542c\u4e00\u58f0\uff1b\u201c\u770b\u6253\uff01\u201d \u3000\u3000\u4e00\u8f6e\u91d1\u98ce\u98de\u65cb\u800c\u81f3\u3002 \u3000\u3000\u8427\u96e8\u4e3e\u5251\u4e00\u6321\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u91d1\u94c1\u5927\u9707\uff01 \u3000\u3000\u4e00\u9762\u5927\u52a0\u8f6e\u6708\u7684\u98de\u94b9\uff0c\u649e\u5f00\u4e86\u957f\u5251\u4e4b\u540e\uff0c\u501f\u52bf\u6253\u65cb\uff0c\u8f6c\u5411\u98de\u4e86\u53bb\uff0c\u6298\u56de\u4e86\u6765\u65f6\u7684\u65b9\u5411\u3002 \u3000\u3000\u4e09\u4e08\u5916\uff0c\u4e00\u4f4d\u8eab\u7740\u7070\u8272\u50e7\u888d\u7684\u9ad8\u5927\u548c\u5c1a\u5de6\u624b\u63e1\u7740\u4e00\u9762\u94dc\u94b9\uff0c\u5411\u4e0a\u4e00\u4e3e\uff0c\u201c\u94ff\u9535\u201d\u4e00\u58f0\uff0c \u6321\u4f4f\u65cb\u98de\u5f52\u6765\u7684\u98de\u94b9\uff0c\u53f3\u624b\u8f7b\u5de7\u7684\u63e1\u4f4f\u63e1\u628a\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u94ff\u94ff\u9535\u9535\u54cd\u58f0\uff0c\u7070\u8863\u548c\u5c1a\u53cc\u624b\u7684\u94dc\u94b9\u649e\u51fb\u4e86\u4e00\u9635\uff0c\u8eab\u4fa7\u5730\u4e0a\u7a81\u7136\u7ad9\u8d77\u4e86\u516b\u4e2a\u548c \u5c1a\uff0c\u624b\u6267\u7985\u6756\u3002 \u3000\u3000\u4ed6\u4eec\u8eab\u7740\u7684\u50e7\u888d\u548c\u5927\u5730\u6ce5\u571f\u4e00\u8272\uff0c\u6f5c\u4f0f\u5728\u5730\u4e0a\uff0c\u4e0d\u7559\u5fc3\uff0c\u5f88\u96be\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u201c\u7adf\u7136\u65e9\u6709\u4f0f\u5175\u3002\u201d\u5b57\u6587\u6210\u90fd\u51b7\u51b7\u5730\u9053\uff1a\u201c\u8fd9\u662f\u6709\u8ba1\u5212\u7684\u9634\u8c0b\u6210\u90fd\u8981\u5927\u5f00\u6740\u6212\u4e86\u3002\u201d \u3000\u3000\u624b\u6267\u53cc\u94b9\u7684\u7070\u8863\u548c\u5c1a\u9053\uff1a\u201c\u5c06\u519b\uff0c\u8bf7\u6682\u606f\u65e0\u540d\u6012\u706b\uff0c\u8d2b\u50e7\u7b49\u65e0\u610f\u5192\u72af\u5c06\u519b\u7684\u864e\u5a01\uff0c\u66f4\u65e0 \u610f\u53c2\u4e0e\u4e16\u4fd7\u7684\u6740\u4f10\u6597\u4e89\u2026\u2026\u201d \u3000\u3000\u201c\u5de7\u8a00\u4ee4\u8272\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u65e0\u610f\u4e16\u4fd7\u7eb7\u4e89\uff0c\u5230\u6b64\u4f5c\u751a\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u501f\u4e24\u4f4d\u5c06\u519b\u4e00\u4e2a\u65f6\u8fb0\uff0c\u8d2b\u501f\u7b49\u7acb\u523b\u64a4\u8d70\u3002\u6b64\u756a\u4e0a\u547d\u96be\u8fdd\uff0c\u8fd8\u8bf7\u4e24\u4f4d\u5c06\u519b\u539f\u5ba5\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u4e0d\u5b88\u6e05\u89c4\uff0c\u6301\u68b0\u901e\u5f3a\uff0c\u53ea\u6b64\u4e00\u6869\u5c31\u8db3\u4ee5\u81f4\u7f6a\uff0c\u6740\u4e4b\u4f55\u60dc\uff01\u201d \u3000\u3000\u957f\u5251\u4e00\u6325\uff0c\u51b2\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u201c\u5c06\u519b\u6b66\u529f\u9ad8\u5f3a\uff0c\u6740\u6cd5\u9a81\u52c7\uff0c\u8d2b\u50e7\u7b49\u53ea\u597d\u4ee5\u9635\u52bf\u62d2\u654c\u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u50e7\u4eba\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u800c\u51fb\u54cd\u94dc\u94b9\u3002 \u3000\u3000\u516b\u4f4d\u624b\u6267\u7985\u6756\u7684\u548c\u5c1a\u4e00\u9f50\u8fce\u4e0a\uff0c\u5e03\u6210\u4e86\u4e00\u5ea7\u62d2\u654c\u9635\u52bf\uff0c\u7acb\u523b\u628a\u5b87\u6587\u6210\u90fd\u5708\u5165\u4e86\u9635\u4e2d\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u957f\u5251\u98de\u8f6c\uff0c\u5c55\u5f00\u6025\u653b\u3002 \u3000\u3000\u516b\u4f4d\u548c\u5c1a\u4ea4\u53c9\u7a7f\u884c\uff0c\u6325\u6756\u62d2\u6321\u3002 \u3000\u3000\u6bcf\u4e00\u6b21\u90fd\u6709\u4e24\u53ea\u63b8\u6756\u652f\u63f4\u53e6\u4e00\u53ea\u7985\u6756\uff0c\u786c\u63a5\u4e0b\u5b57\u6587\u6210\u90fd\u7684\u5251\u52bf\u3002 \u3000\u3000\u7b80\u5355\u7684\u8bf4\uff0c\u5373\u662f\u4e09\u4e2a\u548c\u5c1a\u5408\u529b\u6321\u4f4f\u4e86\u5b57\u6587\u6210\u90fd\u7684\u51cc\u5389\u5251\u52bf\u3002 \u3000\u3000\u8fd9\u7b49\u8fc5\u5feb\u5b8c\u7f8e\u7684\u6280\u827a\u7ec4\u5408\uff0c\u81ea\u975e\u4e00\u671d\u4e00\u5915\u80fd\u7ec3\u6210\u529f\u7684\uff0c\u5fc5\u987b\u957f\u671f\u7684\u540c\u4e60\u540c\u7ec3\uff0c\u624d\u6709\u4e09\u4eba \u5982\u4e00\u7684\u884c\u52a8\u9ed8\u5951\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u90a3\u7a7f\u63d2\u884c\u8d70\u7684\u8d70\u6cd5\uff0c\u4e5f\u662f\u4e00\u79cd\u5341\u5206\u7cbe\u5999\u7684\u914d\u5408\u3002 \u3000\u3000\u516b\u652f\u7985\u6756\uff0c\u7ed3\u5408\u6210\u4e00\u7247\u4e25\u5bc6\u6756\u7f51\uff0c\u5c01\u9501\u4e86\u56db\u9762\u516b\u65b9\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u9ad8\u50b2\u81ea\u8d1f\uff0c\u5185\u529b\u5947\u5f3a\uff0c\u9a6c\u4e0a\u51b2\u950b\u9677\u9635\u65f6\uff0c\u65bd\u7528\u91cd\u903e\u4e00\u767e\u4e94\u5341\u65a4\u7684\u954f\u91d1\u9524\u3002 \u3000\u3000\u6b64\u756a\u5317\u4e0a\uff0c\u8f7b\u9a91\u6025\u8fdb\uff0c\u53ea\u5e26\u4e86\u4e00\u67c4\u957f\u5251\uff0c\u4f46\u4e5f\u662f\u91cd\u903e\u5eff\u65a4\u5bbd\u9762\u539a\u5251\uff0c\u548c\u4eba\u52a8\u624b\u65f6\uff0c\u7d20\u4e0d \u559c\u6709\u4eba\u52a9\u6218\u3002 \u3000\u3000\u6240\u4ee5\u8427\u96e8\u4e0d\u6562\u51fa\u624b\u5e2e\u5fd9\u3002 \u3000\u3000\u770b\u51fa\u6765\uff0c\u5b87\u6587\u6210\u90fd\u771f\u7684\u5403\u529b\u65f6\uff0c\u8427\u96e8\u51c6\u5907\u51fa\u624b\uff0c\u5206\u62d2\u4e00\u4e24\u4e2a\u548c\u5c1a\uff0c\u4ee5\u51cf\u8f7b\u5b57\u6587\u6210\u90fd\u7684\u538b \u529b\uff0c\u7834\u574f\u5bf9\u65b9\u9635\u52bf\u3002 \u3000\u3000\u4f46\u8427\u96e8\u5374\u53d1\u89c9\u7985\u6756\u7ed3\u5408\u9635\u52bf\uff0c\u91cc\u5916\u4e00\u822c\u7684\u4e25\u5bc6\uff0c\u627e\u4e0d\u51fa\u4e0b\u624b\u51fa\u5251\u7684\u7a7a\u9699\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u5927\u58f0\u559d\u9053\uff1a\u201c\u548c\u5c1a\uff0c\u8bf4\u51fa\u4f60\u4eec\u4fee\u884c\u7684\u5bfa\u9662\uff0c\u4e0d\u7136\u6211\u8981\u8fc1\u6012\u5929\u4e0b\u7684\u548c\u5c1a\u3002\u201d \u3000\u3000\u4f46\u95fb\u8e44\u58f0\u5f97\u5f97\uff0c\u4e8c\u767e\u5339\u5065\u9a6c\u98ce\u9a70\u7535\u63a3\u800c\u6765\u3002\u5218\u98de\u9e4f\u3001\u7530\u5f53\u3001\u51cc\u4e91\u4e09\u9a91\u9886\u5148\uff0c\u56e2\u56e2\u628a\u6218\u573a \u56f4\u4f4f\u3002 \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u7530\u5144\uff0c\u8ffd\u5f97\u53ef\u6709\u674e\u4e16\u6c11\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u548c\u5c1a\uff0c\u548c\u5144\u5f1f\u640f\u6740\u751a\u4e45\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u5218\u5144\uff0c\u51cc\u5144\uff01\u7387\u4eba\u8d76\u5230\u4e4b\u540e\uff0c\u4ed6\u5ffd\u7136\u5f03\u5200 \u76d8\u5750\uff0c\u81ea\u65ad\u5fc3\u8109\u800c\u6b7b\uff0c\u56e0\u800c\u5c31\u5168\u65e0\u7ebf\u7d22\u8ffd\u5bfb\u4e86\u3002\u201d \u3000\u3000\u56db\u4f4d\u526f\u7edf\u9886\u90fd\u6b32\u63d2\u624b\u640f\u6218\uff0c\u4f46\u5b57\u6587\u6210\u90fd\u548c\u516b\u4f4d\u548c\u5c1a\u6253\u5f97\u6d51\u6210\u4e00\u4f53\uff0c\u56db\u4eba\u624b\u6267\u5175\u5203\u7adf\u7136\u627e \u4e0d\u51fa\u4e0b\u624b\u4e4b\u5904\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u6108\u6253\u6108\u5fc3\u60ca\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u8d1f\u6b66\u529f\u592b\u4e0b\u65e0\u654c\uff0c\u4f46\u8fd9\u516b\u4f4d\u548c\u5c1a\u5374\u662f\u51ed\u7c4d\u771f\u5b9e\u7684\u6b66\u529f\u548c\u4ed6\u6253\u5f97\u96be\u89e3\u96be\u5206\uff0c\u867d\u7136\u662f\u516b \u4eba\u8054\u624b\u5408\u51fb\uff0c\u4f46\u90a3\u5de7\u5999\u7684\u914d\u5408\uff0c\u5374\u6d51\u6210\u4e00\u4f53\uff0c\u5929\u4e0b\u8bad\u7ec3\u7684\u5408\u640f\u4e4b\u672f\u5982\u6b64\u4f73\u5999\uff0c\u53ea\u8981\u8bad\u7ec3\u51fa\u5341 \u4e2a\u516b\u4e2a\u8fd9\u6837\u9635\u6cd5\uff0c\u4ed6\u5b57\u6587\u6210\u90fd\u5c31\u6709\u4e86\u5341\u4e2a\u516b\u4e2a\u7684\u654c\u624b\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4f55\u7b49\u53ef\u6015\u7684\u4e8b\u554a\uff01 \u3000\u3000\u4ed6\u771f\u7684\u52a8\u4e86\u6740\u673a\uff0c\u60f3\u65bd\u6bd2\u624b\uff0c\u53ea\u8981\u80fd\u53d6\u51fa\u4f69\u5e26\u5728\u8eab\u4e0a\u7684\u51e4\u7fc5\u98de\u5200\uff0c\u5c31\u4e0d\u96be\u6740\u6b7b\u8fd9\u4e9b\u548c \u5c1a\uff0c\u51cf\u5c11\u4e00\u4e2a\u654c\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u5c31\u662f\u65e0\u6cd5\u817e\u51fa\u624b\u6765\u53d6\u98de\u5200\u3002 \u3000\u3000\u539f\u6765\uff0c\u516b\u6756\u914d\u5408\u4e0d\u4f46\u53ef\u4ee5\u5408\u529b\u62d2\u6297\u5f3a\u5389\u7684\u653b\u52bf\uff0c\u4e5f\u6709\u7ef5\u8fde\u4e0d\u7edd\u7684\u8fd8\u51fb\uff0c\u5b57\u6587\u6210\u90fd\u5fc5\u987b\u53cc \u624b\u9f50\u65bd\uff0c\u638c\u5251\u5e76\u51fa\uff0c\u624d\u80fd\u4fdd\u6301\u7740\u4e0d\u80dc\u4e0d\u8d25\u7684\u5c40\u9762\u3002 \u3000\u3000\u90a3\u624b\u6267\u53cc\u94b9\u7684\u7070\u8863\u50e7\u4eba\u4ef0\u5934\u770b\u770b\u5929\u8272\uff0c\u7a81\u7136\u53cc\u94b9\u4e92\u51fb\u4e00\u54cd\uff0c\u58f0\u97f3\u5947\u5927\uff0c\u9707\u5f97\u4eba\u8033\u9645\u55e1\u55e1 \u4f5c\u54cd\uff01 \u3000\u3000\u624b\u4e2d\u4e24\u9762\u94dc\u94b9\u7adf\u88ab\u8fd9\u4e00\u51fb\u9707\u88c2\u6210\u5341\u4f59\u7247\uff0c\u6563\u843d\u4e00\u5730\u3002 \u3000\u3000\u7070\u8863\u50e7\u4eba\u628a\u788e\u7247\u6361\u8d77\uff0c\u53c8\u62fc\u6210\u539f\u94b9\u5f62\u72b6\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u788e\u7247\u7adf\u4f3c\u7c98\u5728\u4e00\u8d77\u4e86\uff0c\u4ecd\u662f\u4e24\u9762\u94dc \u94b9\u3002 \u3000\u3000\u516b\u4e2a\u5408\u51fb\u5b57\u6587\u6210\u90fd\u7684\u548c\u5c1a\uff0c\u5374\u7a81\u7136\u6536\u4e86\u7985\u6756\u3002\u75be\u5982\u98d8\u98ce\u822c\u9000\u5230\u7070\u8863\u548c\u5c1a\u7684\u8eab\u540e\u3002 \u3000\u3000\u4e24\u767e\u52c7\u58eb\u5e03\u6210\u4e86\u4e00\u4e2a\u5706\u73af\uff0c\u628a\u548c\u5c1a\u56e2\u56e2\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u7070\u8863\u50e7\u4eba\u9053\uff1a\u201c\u5b57\u6587\u5c06\u519b\uff01\u5192\u72af\u864e\u5a01\uff0c\u6df1\u4ee5\u4e3a\u6b49\u3002\u6740\u4eba\u4e0d\u8fc7\u5934\u70b9\u5730\uff0c\u5c06\u519b\u8981\u8d2b\u50e7\u5982\u4f55\u4ea4 \u4ee3\uff0c\u53ea\u7ba1\u5429\u5490\u4e00\u58f0\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u53f3\u624b\u6267\u5251\uff0c\u5de6\u624b\u5374\u6df1\u5165\u8eab\u4f69\u888b\u56ca\u4e4b\u4e2d\uff0c\u63e1\u4f4f\u4e86\u4e00\u679a\u51e4\u7fc5\u5200\u3002 \u3000\u3000\u4ed6\u5df2\u5403\u8fc7\u4e86\u516b\u4f4d\u548c\u5c1a\u5408\u51fb\u7684\u82e6\u5934\uff0c\u4e0d\u613f\u518d\u88ab\u56f0\u4f4f\u3002 \u3000\u3000\u56db\u5927\u526f\u7edf\u9886\u4e5f\u90fd\u84c4\u52bf\u6212\u5907\u3002 \u3000\u3000\u4e8c\u767e\u52c7\u58eb\uff0c\u9664\u4e86\u5341\u4e2a\u63a7\u9a6c\u4eba\u3001\u7275\u9a6c\u7b49\u5728\u5341\u4e08\u4e4b\u5916\uff0c\u4f59\u4e0b\u4e00\u767e\u4e5d\u5341\u4eba\uff0c\u5168\u90fd\u56f4\u4e86\u4e0a\u6765\uff0c\u5de6 \u624b\u5175\u5203\uff0c\u53f3\u624b\u6697\u5668\uff0c\u679c\u7136\u662f\u4e00\u652f\u8bad\u7ec3\u6709\u7d20\u3001\u5e94\u53d8\u6709\u65b9\u7684\u7cbe\u731b\u52c7\u58eb\u3002 \u3000\u3000\u201c\u6211\u8981\u4f60\u4eec\u5148\u8bf4\u51fa\u4fee\u884c\u5bfa\u9662\uff01\u201d\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u5426\u5219\uff0c\u5168\u90e8\u5904\u6b7b\uff0c\u4e00\u4e2a\u4e5f\u522b\u60f3\u8d70\uff01\u201d \u3000\u3000\u201c\u5b57\u6587\u5c06\u519b\uff0c\u8d2b\u50e7\u662f\u7387\u961f\u9996\u8c0b\uff0c\u613f\u4ee5\u6027\u547d\u8865\u507f\u5192\u72af\u4e4b\u7f6a\u3002\u53ea\u8981\u5b57\u6587\u5c06\u519b\u653e\u4e86\u4ed6\u4eec\uff0c\u8d2b\u50e7 \u7acb\u523b\u81ea\u7edd\u3002\u201d \u3000\u3000\u201c\u4e00\u4eba\u53d7\u6b7b\uff0c\u592a\u4fbf\u5b9c\u4e86\u5427\uff01\u201d\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u4f60\u4eec\u653e\u7eb5\u94a6\u72af\uff0c\u90fd\u662f\u6b7b\u7f6a\uff01\u201d \u3000\u3000\u7070\u8863\u50e7\u4eba\u4f4e\u55a7\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u9053\uff1a\u201c\u5c06\u519b\uff0c\u4e0a\u5929\u6709\u597d\u751f\u4e4b\u5fb7\uff0c\u5927\u5c06\u6709\u4fa7\u9690\u4e4b\u5fc3\uff1b\u5c06\u519b\uff0c\u653e \u4ed6\u4eec\u53bb\u5427\uff01\u7f6a\u5728\u8d2b\u60dc\u4e00\u4eba\uff0c\u5904\u6b7b\u8d2b\u50e7\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u6267\u6cd5\u5c82\u53ef\u5f87\u79c1\u2026\u2026\u201d \u3000\u3000\u201c\u5c06\u519b\uff01\u201d\u7070\u8863\u50e7\u4eba\u6253\u65ad\u4e86\u5b57\u6587\u6210\u90fd\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8d2b\u50e7\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u6218\u6b7b\uff0c\u4f46\u4e0d\u5fcd\u4e0b\u4ee4\u4ed6 \u4eec\u81ea\u7edd\u3002\u8d2b\u50e7\u76f8\u4fe1\u5c06\u519b\u6709\u6740\u5c3d\u6211\u4eec\u4e4b\u80fd\uff0c\u4f46\u8d35\u5c5e\u7684\u635f\u4f24\uff0c\u4e5f\u76f8\u5f53\u7684\u53ef\u89c2\u4e86\u3002\u6b64\u884c\uff0c\u5168\u662f\u6211\u7b49 \u79c1\u4eba\u53d7\u9080\uff0c\u548c\u5bfa\u9662\u50e7\u7fa4\u65e0\u5e72\uff0c\u5c06\u519b\u5c31\u4e0d\u8981\u82e6\u82e6\u8ffd\u6c42\u4e86\u3002\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u76f8\u4fe1\u7070\u8863\u50e7\u4eba\u8bf4\u7684\u4e0d\u662f\u6050\u5413\u4e4b\u8a00\uff0c\u8fd9\u4e9b\u50e7\u4fa3\u7ec4\u5408\u6280\u827a\uff0c\u5982\u679c\u5c55\u5f00\u5c60\u6740\uff0c\u968f\u884c\u94c1 \u9a91\u5b9a\u7136\u6709\u91cd\u5927\u7684\u4f24\u4ea1\uff0c\u4f46\u53c8\u4e0d\u7518\u5fc3\u4efb\u7fa4\u50e7\u9000\u53bb\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e5d\u4eba\u9f50\u4e0a\uff0c\u4ee5\u767e\u5408\u4e3a\u9650\uff0c\u80fd\u652f\u6491\u8fc7\u53bb\u7684\uff0c\u672c\u5c06\u519b\u5c31\u653e\u4ed6\u8d70\u8def\u3002 \u4e0d\u8fc7\uff0c\u6211\u5148\u8981\u8bf4\u660e\u767d\uff0c\u8fd9\u6b21\u51fa\u624b\uff0c\u672c\u5c06\u519b\u8981\u65bd\u5c55\u9739\u96f3\u624b\u6bb5\uff0c\u4e0d\u4f1a\u518d\u7ed9\u4f60\u4eec\u8054\u624b\u5408\u535a\u7684\u673a\u4f1a \u4e86\uff01\u201d \u3000\u3000\u7070\u8863\u50e7\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c06\u519b\u6267\u610f\u5982\u6b64\uff0c\u8d2b\u50e7\u7b49\u53ea\u597d\u8ba4\u547d\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u4e00\u9635\u6025\u5feb\u7684\u9a6c\u8e44\u58f0\u4f20\u5165\u8033\u9645\uff0c\u4e09\u4e2a\u5feb\u9a6c\u65cb\u98ce\u4e00\u822c\u7684\u8f6c\u4e86\u8fc7\u6765\uff0c\u5f15\u5f97\u5468\u56f4\u4e24\u767e\u591a\u5339\u5065 \u9a6c\u957f\u5578\u76f8\u5e94\u3002 \u3000\u3000\u8fd9\u7a81\u7136\u5176\u6765\u7684\u53d8\u5316\uff0c\u8fd8\u771f\u662f\u6270\u4eba\u5f97\u5f88\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u548c\u56db\u4f4d\u526f\u7edf\u9886\u90fd\u4e0d\u7981\u8f6c\u5934\u770b\u53bb\u3002 \u3000\u3000\u4e09\u5339\u5feb\u9a6c\u51b2\u8fc7\u6765\uff0c\u4f46\u5374\u5728\u4e00\u4e08\u5916\u52d2\u4f4f\u4e86\u9a6c\u7f30\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u662f\u5973\u7684\uff0c\u9752\u8272\u5bf9\u895f\u5c0f\u68c9\u8884\uff0c\u9752\u5e15\u5305\u5934\uff0c\u9752\u86ee\u9774\uff0c\u80cc\u4e0a\u80cc\u4e86\u4e00\u53e3\u9752\u94dc\u9798\u7684\u957f\u5251\u3002 \u3000\u3000\u518d\u770b\u770b\u90a3\u5f20\u8138\uff0c\u8fd8\u771f\u628a\u4eba\u5413\u4e00\u8df3\uff0c\u5f88\u50cf\u8881\u7d2b\u70df\uff0c\u4e5f\u50cf\u8881\u5b9d\u513f\uff0c\u53c8\u4e00\u4e2a\u51fa\u8272\u81f3\u6781\u7684\u5927\u7f8e\u4eba \u5462\uff01 \u3000\u3000\u5b57\u6587\u6210\u90fd\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u53ef\u60dc\u8881\u56fd\u5e08\u4e0d\u5728\u8fd9\u91cc\uff0c\u5982\u679c\u649e\u4e0a\u4e86\uff0c\u4e8c\u7f8e\u4e89\u8273\uff0c\u5012\u53ef\u4ee5 \u4e00\u9971\u773c\u798f\u4e86\u3002 \u3000\u3000\u8eab\u540e\u4e24\u4e2a\u5927\u7537\u4eba\uff0c\u90fd\u7a7f\u7740\u7d27\u8eab\u76ae\u8863\uff0c\u4e00\u4e2a\u978d\u524d\u6a2a\u6302\u7740\u4e00\u67c4\u56db\u5c3a\u4ee5\u4e0a\u7684\u4e09\u5c16\u864e\u53c9\uff0c\u53e6\u4e00\u4e2a \u6302\u7740\u4e00\u67c4\u4e94\u5c3a\u957f\u7684\u65a9\u9a6c\u5200\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u6750\u90fd\u76f8\u5f53\u9ad8\u5927\uff0c\u5750\u5728\u9a6c\u4e0a\u4e5f\u9ad8\u4e86\u9752\u8863\u5c11\u5973\u534a\u4e2a\u5934\u3002 \u3000\u3000\u201c\u770b\u7740\u8fd9\u67b6\u52bf\u3001\u6392\u573a\uff0c\u5927\u6982\u627e\u5bf9\u4eba\u4e86\u3002\u201d\u9752\u8863\u5c11\u5973\u76ee\u5149\u4e00\u63a0\u5b87\u6587\u6210\u90fd\uff0c\u9053\uff1a\u201c\u4f60\u5927\u6982\u5c31 \u662f\u4ec0\u4e48\u7981\u5bab\u603b\u7edf\u9886\u7684\u5b57\u6587\u6210\u90fd\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u59d1\u5a18\u679c\u7136\u627e\u5bf9\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u544a\u8bc9\u6211\u674e\u4e16\u6c11\u73b0\u5728\u4f55\u5904\uff1f\u201d\u9752\u8863\u5c11\u5973\u6253\u65ad\u4e86\u5b57\u6587\u6210\u90fd\u7684\u8bdd\uff0c\u4e00\u4e0b\u5b50\u8f6c\u5165\u4e3b\u9898\uff0c\u63a5 \u9053\uff1a\u201c\u6700\u597d\u628a\u4ed6\u4ea4\u51fa\u6765\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\u554a\uff01\u59d1\u5a18\u6562\u4e0d\u6562\u62a5\u4e0a\u59d3\u540d\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u5b50\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u6562\u5462\uff1f\u4f60\u56de\u53bb\u95ee\u95ee\u968b\u7080\u5e1d\uff0c\u4ed6\u5e94\u8be5\u8fd8\u4f1a\u8bb0\u5f97\u6211\uff0c\u6211\u53eb\u5f20\u51fa \u5c18\u2026\u2026\u201d \u3000\u3000\u201c\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18\uff1f\u201d\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u95fb\u540d\u4e45\u77e3\uff01\u4eca\u65e5\u6709\u5e78\u5f97\u89c1\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u5bab\u536b\u603b\u7edf\u9886\uff0c\u548c\u6267\u62c2\u7684\u5973\u5b98\u6bd4\u8d77\u6765\uff0c\u4e5f\u9ad8\u4e0d\u5230\u54ea\u91cc\u53bb\u3002\u8bf4\u7a7f\u4e86\uff0c\u8fd8\u4e0d\u662f\u4e00 \u6837\u662f\u4f8d\u5019\u4eba\u7684\uff0c\u4ea4\u51fa\u6765\u674e\u4e16\u6c11\uff0c\u6211\u4eec\u8fd9\u7b2c\u4e00\u9762\u5c31\u597d\u89c1\u597d\u6563\u4e86\u3002\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u5484\u5484\u903c\u4eba\u5462\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4e00\u9762\u6325\u624b\uff0c\u793a\u610f\u548c\u5c1a\u9000\u8d70\uff0c\u4e00\u9762\u9053\uff1a\u201c\u4e0d\u80af\u4ea4\uff0c\u5c31\u6709\u4e00\u573a\u9ebb\u70e6\u4e86\uff01\u6700\u597d\u4e0d\u8981\u6709\u522b \u7684\u4eba\u5728\u573a\uff0c\u6740\u8d77\u6765\uff0c\u4e5f\u907f\u514d\u7275\u8fde\u5230\u65e0\u8f9c\u4e8b\u3002\u201d \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u5bf9\u8bdd\u65f6\uff0c\u7070\u8863\u50e7\u4eba\u5df2\u5e26\u7740\u516b\u4e2a\u548c\u5c1a\u5306\u5306\u79bb\u53bb\u4e86\u3002 \u3000\u3000\u542c\u8bf4\u5f20\u51fa\u5c18\u8ddf\u7740\u866c\u9aef\u5ba2\u5b66\u4e60\u5251\u672f\uff0c\u600e\u4e48\u7a81\u7136\u5230\u6b64\u5730\u5462\uff1f \u3000\u3000\u96be\u9053\u6211\u5b57\u6587\u6210\u90fd\u8fd8\u6015\u4e86\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18\u4e0d\u6210\uff1f \u3000\u3000\u5012\u8981\u501f\u673a\u4f1a\u89c1\u8bc6\u89c1\u8bc6\u866c\u9aef\u5ba2\u7684\u5251\u6cd5\u6709\u4ec0\u4e48\u60ca\u4eba\u4e4b\u5904\uff1f \u3000\u3000\u5b57\u6587\u6210\u90fd\u5fc3\u4e2d\u4f5c\u4e86\u51b3\u5b9a\uff0c\u4eba\u4e5f\u51b7\u9759\u4e0b\u6765\uff0c\u4e3e\u624b\u4e0a\u6325\uff0c\u73af\u56f4\u5728\u56db\u5468\u7684\u4e24\u767e\u6b66\u58eb\u7a81\u7136\u64a4\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u9000\u5230\u4e86\u5341\u4e08\u4e4b\u5916\uff0c\u5404\u7275\u5750\u9a6c\u7ffb\u8eab\u8dc3\u4e0a\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u7740\u9a6c\u4e0a\u6218\u6597\u3001\u653b\u654c\u7684\u7f16\u7ec4\u5e8f\u5217\uff0c\u7acb\u523b\u6563\u5e03\u5f00\u53bb\uff0c\u7b49\u7740\u5b57\u6587\u6210\u90fd\u6216\u56db\u5927\u526f\u7edf\u9886\u7684\u6218 \u6597\u6307\u4ee4\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u7559\u4e0b\u4e86\u56db\u5927\u526f\u7edf\u9886\u966a\u5728\u8eab\u4fa7\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5df2\u7ecf\u7b49\u5f97\u5fc3\u5934\u4e0d\u8010\uff0c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5b57\u6587\u6210\u90fd\uff0c\u4f5c\u4f55\u6253\u7b97\uff0c\u660e\u767d\u5730\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u9053\uff1b\u201c\u674e\u4e16\u6c11\u88ab\u90a3\u4e9b\u548c\u5c1a\u52ab\u8d70\u4e86\uff0c\u4f60\u5374\u653e\u8d70\u4e86\u8ffd\u674e\u4e16\u6c11\u4e0b\u843d\u7684\u548c\u5c1a\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5406\u559d\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\uff01\u4ed6\u4eec\u662f\u9041\u8eab\u4e16\u5916\u7684\u4eba\uff0c\u52ab\u6301\u674e\u4e16\u6c11\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u8981\u674e\u4e16\u6c11\uff0c\u53c8\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u201d\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u4ed6\u53ef\u662f\u7687\u4e0a\u94a6\u547d\u6349\u62ff\u8981\u72af\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u968b\u7080\u5e1d\u8981\u6349\u8981\u6740\u7684\u4eba\uff0c\u4e0d\u662f\u8d24\u76f8\u540d\u5c06\uff0c\u5c31\u662f\u82f1\u96c4\u624d\u4eba\uff0c\u4e5f\u662f\u975e \u6551\u4e0d\u53ef\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u80c6\uff01\u201d\u5b57\u6587\u6210\u90fd\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5b58\u5fc3\u548c\u7687\u4e0a\u4f5c\u5bf9\uff1f\u662f\u4e0d\u662f\u60f3\u9020\u53cd\uff1f\u8fd9\u53ef\u662f\u682a\u8fde\u4e5d\u65cf \u7684\u5927\u7f6a\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u54fc\u58f0\u9053\uff1a\u201c\u968b\u7080\u5e1d\u8981\u6740\u6211\uff0c\u5df2\u7ecf\u662f\u51e0\u5e74\u524d\u7684\u4e8b\u4e86\u3002\u90a3\u65f6\uff0c\u4f60\u5b57\u6587\u6210\u90fd\u8fd8\u672a\u88ab\u9009\u5165 \u5bab\u5ef7\u3002\u4e0d\u9519\uff0c\u968b\u7080\u5e1d\u662f\u4f1a\u6740\u4eba\uff0c\u4ed6\u5df2\u7ecf\u6740\u4e86\u6210\u5343\u4e0a\u4e07\u7684\u4eba\uff0c\u90a3\u4e9b\u4eba\u90fd\u4e0d\u8be5\u6b7b\uff0c\u6709\u5f88\u591a\u662f\u5e2e\u4ed6 \u6253\u5929\u4e0b\u7684\u529f\u81e3\u5404\u5c06\u3002 \u3000\u3000\u592a\u539f\u7684\u7559\u5b88\u4faf\u5510\u516c\u674e\u6e0a\uff0c\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5982\u975e\u4ed6\u89c1\u673a\u7684\u65e9\uff0c\u63d0\u524d\u8f9e\u5b98\u5f52\u7c4d\uff0c\u65e9\u5c31\u88ab\u501f\u6545 \u5904\u65a9\u4e86\u3002\u201d \u3000\u3000\u201c\u6ee1\u53e3\u80e1\u8a00\uff0c\u6210\u90fd\u5949\u547d\u62ff\u4eba\uff0c\u4e5f\u53ea\u9650\u674e\u4e16\u6c11\u4e00\u4e2a\uff0c\u5e76\u672a\u60ca\u52a8\u4faf\u5e9c\u4e2d\u4e00\u8349\u4e00\u6728\u3002\u674e\u6e0a\u7eb5\u5b50 \u9020\u53cd\uff0c\u7687\u4e0a\u4e5f\u672a\u8ffd\u7a76\uff0c\u8fd9\u662f\u4f55\u7b49\u7684\u5bbd\u5927\u4ec1\u6148\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4ef0\u5929\u800c\u7b11\uff0c\u9053\uff1a\u201c\u5b57\u6587\u6210\u90fd\uff0c\u4f60\u8fd8\u662f\u771f\u6709\u70b9\u51a5\u987d\u4e0d\u7075\u554a\uff01\u6218\u529f\u5f6a\u70b3\u7684\u540d\u5c06\uff0c\u6cbb\u4e16 \u7ecf\u56fd\u7684\u8d24\u81e3\uff0c\u4ed6\u8981\u6740\u5c31\u6740\u4e86\uff0c\u4f55\u51b5\u4f60\u8fd9\u53ea\u62a4\u9662\u5b88\u95e8\u7684\u9e70\u72ac\uff0c\u6709\u4e00\u5929\u4f60\u89e6\u72af\u5230\u4ed6\u7684\u7981\u5fcc\uff0c\u4f1a\u628a \u4f60\u4e00\u5bb6\u4eba\u6740\u4e2a\u9e21\u72ac\u4e0d\u7559\u5462\uff01\u201d \u3000\u3000\u201c\u679c\u7136\u662f\u5fc3\u5b58\u53db\u9006\uff0c\u7f6a\u4e0d\u53ef\u8d66\uff0c\u5355\u662f\u5bf9\u7687\u4e0a\u7684\u5927\u4e0d\u656c\uff0c\u5c31\u8be5\u7acb\u65a9\u9a6c\u524d\u3002\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u4f38\u624b\u62d4\u5251\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u4eba\u79bb\u9a6c\u978d\uff0c\u5251\u5df2\u5728\u624b\u3002 \u3000\u3000\u51fa\u5c18\u59d1\u5a18\u7f8e\u4f3c\u4ed9\u5b50\uff0c\u4f46\u6027\u5b50\u5374\u662f\u706b\u7206\u5f02\u5e38\uff0c\u672a\u5f85\u5b57\u6587\u6210\u90fd\u51fa\u624b\uff0c\u5979\u5df2\u7ecf\u62a2\u5148\u53d1\u5251\u3002 \u3000\u3000\u4e00\u9053\u5bd2\u8292\u7ed5\u8eab\u98de\u8d77\uff0c\u5c04\u5411\u5b57\u6587\u6210\u90fd\u3002 \u3000\u3000\u8427\u96e8\u7684\u957f\u5251\u6025\u523a\u3002 \u3000\u3000\u51cc\u4e91\u7684\u79cb\u6c34\u96c1\u7fce\u5200\u4e5f\u98de\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u5200\u4e00\u5251\uff0c\u8054\u624b\u800c\u51fa\uff0e\u60f3\u62e6\u4e0b\u5f20\u59d1\u5a18\u7684\u4eba\u5230\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u5f20\u51fa\u5c18\u5251\u52bf\u592a\u5feb\uff0c\u5feb\u5982\u95ea\u7535\u8fc7\u957f\u7a7a\uff0c\u4e24\u4e2a\u4eba\u53ea\u89c9\u5f97\u51b7\u98ce\u6251\u9762\uff0c\u5251\u6c14\u5df2\u63a0\u8eab\u800c\u51fa\uff0c \u96c1\u7fce\u5200\u548c\u957f\u5251\u4e00\u8d77\u843d\u7a7a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u6ca1\u6709\u51fa\u58f0\uff0c\u4f46\u5fc3\u4e2d\u90fd\u660e\u767d\u662f\u5f20\u59d1\u5a18\u5251\u4e0b\u7559\u60c5\uff0c\u5982\u679c\u5979\u5b58\u5fc3\u4f24\u4eba\uff0c\u4e24\u4e2a\u4eba\u5c31\u7b97\u4e0d \u6b7b\uff0c\u4e5f\u5f97\u8eab\u53d7\u91cd\u4f24\u3002 \u3000\u3000\u5f20\u59d1\u5a18\u7684\u76ee\u6807\u662f\u5b57\u6587\u6210\u90fd\uff0c\u68ee\u68ee\u5251\u6c14\u76f4\u5c04\u8fc7\u53bb\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u6697\u53eb\u4e86\u4e00\u58f0\u60ed\u6127\uff01 \u3000\u3000\u76f8\u4fe1\u81ea\u5df1\u5168\u529b\u653b\u51fa\u4e00\u5251\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e48\u4e2a\u5feb\u6cd5\uff0c\u53ea\u9053\u4e16\u4e0a\u5947\u5973\u5b50\uff0c\u53ea\u6709\u4e00\u4e2a\u8881\u7d2b\u70df\uff0c\u600e\u4e48 \u53c8\u8dd1\u51fa\u4e00\u4e2a\u5f20\u51fa\u5c18\u6765\uff1f \u3000\u3000\u5979\u7528\u7684\u53ef\u662f\u771f\u771f\u6b63\u6b63\u7684\u6b66\u529f\u3002 \u3000\u3000\u5fc3\u4e2d\u60f3\u7740\uff0c\u53c8\u957f\u53c8\u91cd\u7684\u957f\u5251\u5df2\u8fce\u9762\u51fb\u51fa\u3002 \u3000\u3000\u4ecd\u662f\u6162\u4e86\u90a3\u4e48\u4e00\u70b9\u70b9\uff0c\u5251\u6c14\u63a0\u9762\u800c\u8fc7\uff0c\u65a9\u843d\u4e86\u9b13\u8fb9\u4e00\u64ae\u6563\u53d1\uff0c\u5dee\u90a3\u4e48\u4e00\u70b9\u70b9\u5c31\u524a\u53bb\u4e86\u534a \u4e2a\u8111\u888b\u3002 \u3000\u3000\u597d\u5feb\u7684\u4e00\u5251\uff01 \u3000\u3000\u5b57\u6587\u6210\u90fd\u4e5f\u65e0\u6cd5\u77e5\u9053\u662f\u5f20\u59d1\u5a18\u624b\u4e0b\u7559\u60c5\uff0c\u8fd8\u662f\u81ea\u5df1\u8eab\u6cd5\u5947\u5999\uff1f\u79fb\u4f4d\u5feb\u901f\uff0c\u51ed\u6b66\u529f\u95ea\u8fc7\u4e86 \u8fd9\u5feb\u901f\u4e00\u5251\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\u9669\u91cc\u9003\u751f\uff0c\u5374\u6fc0\u8d77\u4e86\u5b57\u6587\u6210\u90fd\u7684\u597d\u80dc\u4e4b\u5fc3\uff0c\u65e9\u628a\u63e1\u5728\u624b\u4e2d\u7684\u51e4\u7fc5\u5200\u84e6\u7136\u51fa \u624b\u3002 \u3000\u3000\u8fd9\u624d\u662f\u5b57\u6587\u6210\u90fd\u7684\u771f\u6b63\u7edd\u6280\uff01 \u3000\u3000\u53ea\u89c1\u4e00\u7247\u91d1\u8292\uff0c\u7834\u7a7a\u800c\u81f3\uff0c\u98de\u5411\u5750\u5728\u9a6c\u4e0a\u7684\u5f20\u51fa\u5c18\u3002 \u3000\u3000\u539f\u6765\uff0c\u5f20\u51fa\u5c18\u4eba\u5251\u9f50\u98de\uff0c\u7ed5\u4e86\u4e00\u4e2a\u5927\u5708\u5b50\uff0c\u53c8\u56de\u5230\u9a6c\u80cc\u7684\u978d\u4e0a\u3002 \u3000\u3000\u51e4\u7fc5\u5200\u5c31\u5728\u6b64\u523b\u653b\u5230\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u4e5f\u66fe\u5728\u56db\u5927\u526f\u7edf\u9886\u7684\u9762\u524d\u65bd\u5c55\u8fc7\u8fd9\u7b49\u7edd\u6280\u3002 \u3000\u3000\u4f46\u5374\u6ca1\u6709\u4eba\u770b\u5f97\u6e05\u695a\u662f\u4e00\u4ef6\u4ec0\u4e48\u6837\u5b50\u7684\u4e1c\u897f\uff0c\u53ea\u89c1\u91d1\u8292\u98de\u7ed5\uff0c\u5bf9\u65b9\u5c31\u6e85\u8840\u635f\u547d\uff0c\u9887\u6709\u4f20 \u8bf4\u4e2d\u7684\u98de\u5251\u6c14\u52bf\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u7b2c\u4e8c\u5ea6\u79bb\u978d\u98de\u8d77\u2014\u2014 \u3000\u3000\u4e00\u9053\u9752\u5149\u968f\u7740\u5979\u98de\u8d77\u7684\u8eab\u4e8e\u5212\u51fa\u4e00\u7247\u9752\u8679\u3002 \u3000\u3000\u4f46\u95fb\u5f97\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u90a3\u65cb\u98de\u7684\u4e00\u7247\u91d1\u8292\u91cd\u56de\u5b57\u6587\u6210\u90fd\u624b\u4e2d\uff0c\u98de\u7edf\u7a7a\u4e2d\u7684\u9752\u8292\u4e5f\u6d88 \u5931\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4ecd\u7136\u5b8c\u597d\u5982\u521d\u7684\u5750\u5728\u9a6c\u978d\u4e0a\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u8138\u4e0a\u4e00\u7247\u82cd\u767d\uff0c\u4f3c\u4e4e\u662f\u7528\u529b\u8fc7\u5ea6\uff0c\u6c57\u6c34\u6e7f\u900f\u4e86\u8d34\u8eab\u5185\u8863\u3002\u5e78\u597d\u5916\u7f69\u72d0\u76ae\u77ed\u8902\uff0c \u522b\u4eba\u4e5f\u77a7\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u53ea\u6709\u5b57\u6587\u6210\u90fd\u81ea\u5df1\u660e\u767d\uff0c\u8fd9\u4e00\u6b21\u62fc\u51fb\u5bf9\u4ed6\u6709\u591a\u5927\u7684\u4f24\u5bb3\u3002 \u3000\u3000\u4ece\u65e0\u865a\u53d1\uff0c\u51fa\u5fc5\u4f24\u4eba\u53d6\u547d\u7684\u51e4\u7fc5\u5200\uff0c\u662f\u7b2c\u4e00\u6b21\u88ab\u4eba\u7834\u89e3\uff0c\u4e5f\u632b\u6298\u4e86\u5b57\u6587\u6210\u90fd\u5929\u4e0b\u65e0\u654c\u7684 \u4fe1\u5fc3\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u6709\u7740\u5fc3\u5934\u9707\u60ca\u7684\u611f\u89c9\uff0c\u866c\u9aef\u5ba2\u544a\u8bc9\u5979\u8fd9\u9a6d\u5251\u4e4b\u672f\uff0c\u662f\u5251\u672f\u4e2d\u6700\u9ad8\u7684\u6210\u5c31\uff0c\u53ea\u8981 \u7ec3\u6210\u6b64\u6280\uff0c\u5c31\u53ef\u5929\u4e0b\u65e0\u654c\u4e86\u3002 \u3000\u3000\u5979\u82b1\u4e86\u4e24\u5e74\u5de5\u592b\uff0c\u4e0d\u60dc\u6682\u522b\u592b\u541b\uff0c\u5fcd\u53d7\u7740\u4e24\u5730\u76f8\u601d\u4e4b\u82e6\uff0c\u65e5\u670d\u836f\u7269\uff0c\u52a9\u957f\u529f\u529b\uff0c\u7ec8\u4e8e\u7ec3 \u6210\u4e86\u9a6d\u5251\u4e4b\u672f\u3002 \u3000\u3000\u7b2c\u4e00\u6b21\u4f7f\u51fa\u6765\uff0c\u5c31\u88ab\u5b57\u6587\u6210\u90fd\u95ea\u907f\u5f00\u53bb\u3002 \u3000\u3000\u867d\u7136\u65a9\u843d\u4ed6\u9b13\u8fb9\u4e00\u64ae\u6563\u53d1\uff0c\u4f46\u5374\u672a\u4f24\u5230\u4eba\u5bb6\u4e00\u70b9\u76ae\u8089\u3002 \u3000\u3000\u9002\u624d\u9a6d\u5251\u62a4\u8eab\u7684\u4e00\u51fb\uff0c\u66f4\u4ee4\u5f20\u59d1\u5a18\u6709\u70b9\u6c14\u9981\u3002 \u3000\u3000\u5bf9\u65b9\u90a3\u4e00\u5927\u7247\u98de\u52a8\u7684\u91d1\u8292\uff0c\u4f3c\u662f\u6d3b\u7684\u3002 \u3000\u3000\u5251\u52bf\u51fb\u4e2d\u4e24\u6b21\uff0c\u7adf\u672a\u80fd\u628a\u5b83\u51fb\u843d\u5728\u5730\u3002 \u3000\u3000\u4e24\u4eba\u540c\u65f6\u751f\u51fa\u4e86\u9000\u7f29\u4e4b\u5fc3\u3002 \u3000\u3000\u201c\u674e\u4e16\u6c11\u786e\u5df2\u88ab\u90a3\u4e00\u7fa4\u548c\u5c1a\u8bbe\u8ba1\u6551\u8d70\u4e86\uff01\u201d\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u533a\u533a\u4e5f\u5728\u62c5\u5fe7\u5982\u4f55\u5411\u7687\u4e0a\u4ea4 \u4ee3\uff0c\u6211\u7edf\u7387\u5317\u5f81\u7684\u4eba\u624b\u5168\u90fd\u5728\u6b64\uff0c\u4e00\u76ee\u4e86\u7136\uff0c\u59d1\u5a18\u4e0d\u80af\u76f8\u4fe1\uff0c\u5728\u4e0b\u4e5f\u6ca1\u529e\u6cd5\u4e86\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u5916\u77a7\u4e86\u4e00\u9635\uff0c\u9053\u3002\u201c\u4ee5\u4f60\u5b57\u6587\u6210\u90fd\u7684\u8eab\u4efd\uff0c\u5982\u679c\u8bf4\u4e86\u8c0e\u8a00\uff0c\u65e5\u540e\u8fd8 \u6709\u4f55\u989c\u9762\u5728\u4e16\u95f4\u7acb\u8db3\uff1f\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u5de6\u53f3\u4ece\u4eba\uff0c\u63a5\u9053\uff1a\u201c\u8d70\uff01\u6211\u4eec\u53bb\u8ffd\u674e\u4e16\u6c11\uff01\u201d \u3000\u3000\u4e00\u5e26\u7f30\u7ef3\uff0c\u653e\u8f94\u75be\u884c\u3002 \u3000\u3000\u4e24\u4e2a\u4ece\u4eba\u7eb5\u9a91\u6025\u8feb\u3002 \u3000\u3000\u201c\u90a3\u4e24\u4eba\u5c31\u662f\u9f99\u3001\u864e\u4e8c\u5c06\u4e86\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u662f\u866c\u9aef\u5ba2\u8fd1\u8eab\u4ece\u536b\u5374\u968f\u62a4\u5728\u5f20\u51fa\u5c18\u7684\u8eab \u8fb9\u3002\u201d \u3000\u3000\u51cc\u4e91\u7591\u7136\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80af\u5b9a\u4ed6\u4eec\u662f\u9f99\u3001\u864e\u4e8c\u5c06\uff1f\u542c\u8bf4\u866c\u9aef\u5ba2\u4ece\u536b\u4f17\u591a\uff0c\u4e0d\u4e4f\u597d\u624b\u3002\u201d \u3000\u3000\u201c\u864e\u5c06\u7528\u53c8\uff0c\u9f99\u5c06\u7528\u5200\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u674e\u9756\u5728\u76d8\u9f99\u5c45\u7684\u5927\u5385\u8bf4\u5f97\u5f88\u660e\u767d\uff0c\u4f46\u6700\u91cd\u8981\u7684\u662f \u6211\u770b\u5230\u4e86\u9f99\u5c06\u8170\u4e0a\u6302\u56db\u6816\u77ed\u5200\uff0e\u548c\u90a3\u79e6\u743c\u7684\u77ed\u5200\u4e00\u6837\u3002\u201d \u3000\u3000\u51cc\u4e91\u70b9\u70b9\u5934\uff0c\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u8d70\uff01\u5230\u90a3\u5ea7\u53e4\u5bfa\u4e2d\u7b49\u5019\u56fd\u5e08\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u611f\u89c9\u8881\u7d2b\u70df\u662f\u90a3\u4e48\u91cd\u8981\uff01 \u3000\u3000\u6b64\u756a\u5317\u5f81\uff0c\u4e00\u5207\u90fd\u7531\u5979\u4f5c\u4e3b\uff0c\u5b57\u6587\u6210\u90fd\u6697\u4e2d\u4e5f\u6709\u5f88\u591a\u7684\u4e0d\u6ee1\u610f\u3002\u4f46\u548c\u5f20\u51fa\u5c18\u4e00\u6218\u4e4b\u540e\uff0c \u9887\u6709\u5927\u632b\u4e4b\u611f\u3002 \u3000\u3000\u4e24\u767e\u6b66\u58eb\u91cd\u884c\u8fdb\u5165\u53e4\u5bfa\uff0c\u5b89\u7f6e\u4e0b\u6765\u3002 \u3000\u3000\u8427\u96e8\u5429\u5490\u57cb\u9505\u9020\u996d\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u51c6\u5907\u957f\u4f4f\u4e0b\u6765\uff0c\u7b49\u5019\u8881\u7d2b\u70df\u4e86\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u5fc3\u4e2d\u90fd\u6709\u7740\u8fd9\u79cd\u611f\u89c9\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e00\u7b49\u5c31\u662f\u4e24\u5929\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u8868\u9762\u5e73\u9759\uff0c\u5fc3\u4e2d\u6025\u3002 \u3000\u3000\u4f46\u66f4\u6025\u7684\u662f\u84ec\u513f\u548c\u5de7\u513f\uff0c\u5931\u53bb\u4e86\u8881\u7d2b\u70df\uff0c\u4e24\u4e2a\u4e2b\u5934\u4e5f\u987f\u5931\u6240\u4f9d\uff0c\u6574\u5929\u76b1\u7740\u7709\u5934\u4e0d\u8bf4\u8bdd\uff0c \u5077\u5077\u7684\u76f8\u62e5\u800c\u6ce3\u3002 \u3000\u3000\u4e24\u4e2a\u5c0f\u59d1\u5a18\u81ea\u7136\u6ca1\u6709\u5b57\u6587\u6210\u90fd\u90a3\u4efd\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\u3002 \u3000\u3000\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u4f7f\u7687\u5e1d\u4f4e\u5934\u3001\u5927\u5c06\u503e\u5fc3\uff0c\u8fde\u83b2\u513f\u3001\u5de7\u513f\u8fd9\u6837\u7684\u5c0f\u59d1\u5a18\u4e5f\u4e3a\u4e4b\u601d\u5ff5\u96be\u5fd8 \u6d12\u6cea\u54ed\u6ce3\u3002 \u3000\u3000\u4e8c\u767e\u4f4d\u52c7\u6b66\u7684\u6218\u58eb\uff0c\u7adf\u4e5f\u662f\u5bb9\u6709\u621a\u621a\u7109\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4eec\u4e5f\u5728\u601d\u5ff5\u8881\u56fd\u5e08\u3002 \u3000\u3000\u5979\u968f\u884c\u519b\u4e2d\uff0c\u53ea\u611f\u89c9\u5230\u5979\u662f\u6735\u8000\u773c\u4e4b\u82b1\uff0c\u770b\u5f97\u4eba\u5fc3\u65f7\u795e\u6021\u3002 \u3000\u3000\u5979\u79bb\u5f00\u51e0\u5929\uff0c\u624d\u611f\u89c9\u5230\u5979\u662f\u4e3b\u5bb0\u4e8b\u7269\u7684\u7075\u9b42\u3002 \u3000\u3000\u6709\u5979\u5728\u573a\uff0c\u5927\u5bb6\u90fd\u6709\u7740\u65e0\u6240\u754f\u60e7\u7684\u52c7\u6c14\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u8d70\u4e86\u4e24\u5929\uff0c\u8fd9\u73ed\u52c7\u58eb\u4eec\u90fd\u597d\u50cf\u53d7\u5230\u4e86\u4e00\u79cd\u51b2\u51fb\uff0c\u4eba\u4eba\u90fd\u53d8\u5f97\u65e0\u7cbe\u6253\u91c7\u4e86\u3002\u4e5f\u8bb8 \u662f\u53d7\u5230\u4e86\u5b57\u6587\u6210\u90fd\u7684\u5f71\u54cd\u5427\uff01 \u3000\u3000\u4ed6\u7684\u6cae\u4e27\u5fc3\u60c5\u5f71\u54cd\u4e86\u5c5e\u4e0b\u3002 \u3000\u3000\u8427\u96e8\u4ef0\u5929\u957f\u5578\u2014\u58f0\uff0c\u56de\u987e\u7740\u8eab\u4fa7\u7684\u5218\u98de\u9e4f\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u5929\u6c14\u6674\u6717\uff0c\u4e07\u91cc\u65e0\u4e91\uff0c\u84dd\u5929\u5982 \u6d17\uff0c\u6211\u4eec\u4e0d\u80fd\u518d\u7b49\u4e0b\u53bb\uff0c\u8be5\u52a8\u8eab\u56de\u957f\u5b89\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u7eb5\u9a6c\u56de\u957f\u5b89\uff01\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u8d70\uff01\u6211\u4eec\u53bb\u664b\u89c1\u603b\u7edf\u9886\uff0c\u529d\u4ed6\u52a8\u8eab\u3002\u201d \u3000\u3000\u8fd9\u65f6\u7530\u5f53\u795e\u60c5\u8083\u7136\u7684\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u603b\u7edf\u9886\u8981\u5144\u5f1f\u7ea6\u8bf7\u4e24\u4f4d\uff0c\u4ed6\u5df2\u547d\u4eba\u5907\u597d\u4e86\u9187\u9152\u3001 \u7f8e\u80b4\uff0c\u4eca\u65e5\u8981\u548c\u8bf8\u4f4d\u5171\u8c0b\u4e00\u9189\u3002\u201d \u3000\u3000\u201c\u603b\u7edf\u9886\u4f3c\u662f\u6d88\u6c89\u4e86\u3002\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u4ed6\u672c\u662f\u53f1\u54a4\u98ce\u4e91\u7684\u5c06\u519b\uff0c\u6c14\u541e\u6cb3\u5cb3\u3001\u8c6a\u58ee\u51cc\u4e91\uff0c \u6709\u767e\u6298\u4e0d\u56de\u7684\u52c7\u6bc5\uff0c\u73b0\u5728\u770b\u4e0a\u53bb\u5374\u6709\u4e9b\u82f1\u96c4\u6c14\u77ed\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u662f\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18\u90a3\u4e00\u5251\uff0c\u8fd8\u662f\u8881\u56fd\u5e08\u8fdf\u8fdf\u672a\u5f52\uff1f \u3000\u3000\u4e0d\u50cf\u8fc7\u53bb\u7684\u603b\u7edf\u9886\u4e86\u3002\u5c82\u771f\u4e5f\u82f1\u96c4\u6c14\u77ed\uff0c\u513f\u5973\u60c5\u957f\u4e4e\uff1f\u201d \u3000\u3000\u7530\u5f53\u9053\uff1a\u201c\u4e24\u4f4d\u4f4e\u4f30\u603b\u7edf\u9886\u4e86\uff0c\u4ed6\u662f\u5728\u5fe7\u5fc3\u56fd\u4e8b\u554a\uff01\u4e0d\u9519\u592a\u539f\u4e4b\u884c\u4f7f\u6211\u4eec\u89c1\u5230\u4e86\u5929\u5916\u4e4b \u5929\uff0c\u5c09\u8fdf\u606d\u3001\u79e6\u53d4\u5b9d\u90fd\u662f\u82f1\u96c4\uff0c\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4eba\u95f4\u5947\u624d\uff0c\u5f20\u51fa\u5c18\u90a3\u4e00\u5251\u3001\u4e09\u85cf\u548c\u5c1a\u7684\u672f \u6cd5\uff0c\u90fd\u8ba9\u603b\u7edf\u9886\u89e6\u76ee\u60ca\u5fc3\uff0c\u8881\u56fd\u5e08\u8fdf\u8fdf\u672a\u5f52\u4e5f\u4f7f\u4ed6\u5fe7\u5fc3\u5fe1\u5fe1\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u4ed6\u957f\u601d\u6df1\u8651\uff0c\u4ea6\u611f\u89c9\u5230\u5927\u8fc7\u6d88\u6c89\uff0c\u4ed6\u544a\u8bc9\u6211\u548c\u51cc\u4e91\uff0c\u4eca\u65e5\u4e00\u9189\u4eca\u65e5\u6b7b\uff0c\u660e\u65e5\u9192\u6765 \u5373\u91cd\u751f\uff0c\u7136\u540e\u52a8\u8eab\u56de\u957f\u5b89\u3002 \u3000\u3000\u4ed6\u8981\u9762\u541b\u8bf7\u7f6a\uff0c\u5982\u5f97\u7687\u4e0a\u6069\u51c6\u514d\u8d23\uff0c\u5c06\u75db\u9648\u65f6\u5f0a\uff0c\u8bf7\u7687\u4e0a\u6539\u9769\u671d\u653f\uff0c\u4e00\u65b9\u9762\u8bbf\u8d24\u56db\u6d77\uff0c \u627e\u51e0\u4f4d\u9690\u4e16\u9ad8\u4eba\uff0c\u5171\u8c0b\u5927\u8ba1\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u603b\u7edf\u9886\u5982\u80fd\u632f\u4f5c\u8d77\u6765\uff0c\u6211\u4eec\u8fd9\u6570\u5343\u7cbe\u9510\u4e4b\u5e08\u8fd8\u6709\u53ef\u4e3a\uff01\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a\u201c\u5bf9\uff01\u4eca\u65e5\u4e00\u9189\u89e3\u5343\u6101\uff0c\u653e\u4e0b\u5fc3\u4e2d\u4e07\u822c\u5fe7\uff0c\u9192\u65f6\u91cd\u8de8\u4e4c\u96c5\u9a6c\uff0c\u5c06\u519b\u6218\u6b7b\u4e0d\u56de \u5934\u3002\u201d \u3000\u3000\u201c\u5218\u5c06\u519b\u8c6a\u6c14\u5e72\u4e91\uff0c\u597d\u4e00\u4e2a\u5c06\u519b\u6218\u6b7b\u4e0d\u56de\u5934\uff01\u201d \u3000\u3000\u5996\u58f0\u5456\u5456\uff0c\u5982\u51fa\u8c37\u9ec4\u83ba\u3002\u867d\u7136\u662f\u60b2\u58ee\u6177\u6168\u7684\u8bdd\uff0c\u7531\u5979\u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u5374\u4e5f\u591a\u4e86\u51e0\u5206\u6e29\u67d4 \u5462\uff01 \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u8881\u7d2b\u70df\u9762\u5e26\u5fae\u7b11\uff0c\u7ad9\u5728\u4e09\u5c3a\u5916\u5904\u3002 \u3000\u3000\u5979\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u4e09\u5927\u9ad8\u624b\u7adf\u65e0\u6240\u89c9\u3002 \u3000\u3000\u201c\u56fd\u5e08\u56de\u6765\u5566\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\u3001\u8fd9\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8427\u5c06\u519b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u56fd\u5e08\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u4e24\u5929\u4e3a\u56fd\u5e08\u5b89\u5371\u5fe7\u5fc3\uff0c\u81ea\u5b87\u6587\u603b\u7edf\u9886\u59cb\u4e2a\u4e2a\u65e0\u7cbe\u6253\u91c7\u3002\u201d\u8427\u96e8\u9053\uff1a \u201c\u83b2\u513f\u3001\u5de7\u513f\u4e24\u4f4d\u59d1\u5a18\u66f4\u662f\u76f8\u62e5\u800c\u6ce3\uff0c\u795e\u9b42\u65e0\u4e3b\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u771f\u662f\u82e6\u4e86\u5979\u4eec\u4e86\uff0c\u6211\u56e0\u664b\u89c1\u4e00\u4f4d\u524d\u8f88\u7b49\u4ed6\u5750\u5173\u9192\u6765\u3002\u60f3\u4e0d\u5230\u4e00\u7b49 \u5c31\u662f\u4e24\u5929\uff0c\u7d2f\u8bf8\u4f4d\u4e45\u5019\u4e86\u3002\u201d \u3000\u3000\u201c\u7ea2\u62c2\u5973\u5f20\u51fa\u5c18\u5251\u672f\u7cbe\u6e5b\uff0c\u5df2\u53ef\u9a6d\u5251\u4f24\u4eba\u4e8e\u767e\u4e08\u4e4b\u5916\uff0c\u5c5e\u4e0b\u63a5\u4e86\u5979\u4e00\u62db\uff0c\u679c\u975e\u865a\u4f20\u3002\u201d \u5b57\u6587\u6210\u90fd\u5927\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5bf9\u5927\u968b\u671d\u7684\u5a01\u80c1\uff0c\u4f3c\u5df2\u8d85\u8d8a\u674e\u4e16\u6c11\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5df2\u8c03\u6559\u51fa\u5f20\u51fa\u5c18\u4e86\uff0c\u4ed6\u548c\u674e\u9756\u540c\u65f6\u503e\u5fc3\u5f20\u51fa\u5c18\uff0c\u8fd9\u5176\u95f4 \u662f\u5426\u53ef\u8d44\u5229\u7528\u4e4b\u5904\u2026\u2026\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u542c\u8fc7\u8fd9\u4e2a\u4f20\u8bf4\uff0c\u4f46\u674e\u9756\u548c\u5f20\u51fa\u5c18\u5df2\u7ecf\u7ed3\u6210\u592b\u5987\uff0c\u7eb5\u7136 \u6709\u4e00\u4e9b\u7ea0\u845b\uff0c\u4e5f\u5e94\u8be5\u65e9\u5df2\u5e73\u606f\u4e86\u3002\u201d \u3000\u3000\u201c\u50cf\u674e\u4e16\u6c11\u90a3\u6837\u535a\u5927\u5fc3\u80f8\u7684\u4eba\uff0c\u4e16\u95f4\u53ea\u6015\u4e0d\u591a\u3002\u201d\u8881\u7d2b\u70df\u9053\u3002 \u3000\u3000\u201c\u674e\u9756\u4e0d\u80af\u4ece\u4e49\u5144\u8eab\u4fa7\u5171\u56fe\u5927\u4e1a\uff0c\u5374\u4f5c\u4e86\u674e\u4e16\u6c11\u624b\u4e0b\u8c0b\u81e3\uff0c\u867d\u56e0\u8bc6\u89c1\u8fc7\u4eba\uff0c\u4f46\u4e0d\u613f\u770b\u4e24 \u4eba\u671d\u5915\u76f8\u5904\u7ec3\u5251\uff0c\u4e5f\u662f\u5f71\u54cd\u4e4b\u4e00\u5427\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u65e0\u6cd5\u53cd\u9a73\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\u3002\u201c\u56fd\u5e08\uff0c\u73ed\u5e08\u957f\u5b89\u5427\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e22\u4e86\u674e\u4e16\u6c11\uff0c\u54ea\u91cc\u80fd\u8bf4\u2018\u73ed\u5e08\u2019\u4e8c\u5b57\u3002\u56de\u5230\u957f\u5b89\uff0c\u9762\u541b\u8bf7\u7f6a\u5427\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u7687\u4e0a\u5341\u5206\u5ba0\u7231\u56fd\u5e08\uff0c\u4e0d\u4f1a\u4f9d\u5f8b\u6cbb\u7f6a\u5427\uff1f\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u90e8\u7684\u867d\u8822\uff0c\u4f46\u5374\u8868\u8fbe\u4e86\u4e00\u7247\u5173\u7231\u4e4b\u5fc3\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5929\u5a01\u96be\u6d4b\u5440\uff01\u8c01\u80fd\u77e5\u9053\u7687\u4e0a\u7684\u771f\u5fc3\u5883\u5462\uff1f\u201d \u3000\u3000\u201c\u56fd\u5e08\u610f\u601d\u662f\u2026\u2026\u201d\u5b57\u6587\u6210\u90fd\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u624d\u8270\u6da9\u5730\u8bf4\u9053\uff1b\u201c\u65e2\u7136\u662f\u5409\u51f6\u96be\u65ad\uff0c\u4e0d\u5982\u95ea \u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u8d70\u4e86\uff0c\u5c06\u519b\u5462\uff1f\u201d\u8881\u7d2b\u70df\u7684\u76ee\u5149\u51dd\u6ce8\u5728\u5b87\u6587\u6210\u90fd\u7684\u8138\u4e0a\uff0c\u6d41\u73b0\u51fa\u4e00\u7247\u6e29\u67d4\uff0c\u9053\uff1a \u201c\u7687\u4e0a\u4f1a\u653e\u8fc7\u4f60\u5417\uff1f\u201d \u3000\u3000\u201c\u8d70\u5931\u4e86\u674e\u4e16\u6c11\uff0c\u672c\u5c31\u662f\u6210\u90fd\u7684\u8d23\u4efb\uff0c\u7687\u4e0a\u6740\u4e86\u6211\u4e5f\u5c5e\u5e94\u8be5\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u76f8\u5904\u6570\u5e74\uff0c\u541b\u81e3\u95ee\u60c5\u610f\u751a\u91cd\uff0c\u4e5f\u8bb8\u4f1a\u8d66\u6211\u65e0\u7f6a\u5462\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u8d70\uff0c\u4f60\u4f1a\u65e0\u7f6a\uff1b\u6211\u4e00\u8d70\uff0c\u4ed6\u4f1a\u8fc1\u6012\u3002\u6210\u90fd\uff0c\u6740\u4f60\u4e0d\u662f\u4e3a\u4e86\u8d70\u8131\u4e2a\u674e\u4e16\u6c11\uff0c\u800c\u662f\u8d70 \u4e86\u6211\u8881\u7d2b\u70df\u554a\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff0c\u662f\u6211\u591a\u8651\u4e86\u3002\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u5f52\u53bb\u957f\u5b89\uff0c\u4e0d\u7ba1\u540e\u679c\u5982\u4f55\uff1f\u5f88\u96be\u518d\u89c1\u56fd\u5e08\u4e4b\u9762\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u5c31\u4f4f\u5728\u5bab\u9662\u4e2d\uff0c\u5c06\u519b\u53ef\u4ee5\u968f\u65f6\u8fdb\u5bab\u89c1\u6211\u5440\uff01 \u3000\u3000\u4f60\u662f\u5bab\u536b\u603b\u7edf\u9886\uff0c\u4e5f\u662f\u7687\u4e0a\u7684\u8fd1\u81e3\uff0c\u8fdb\u5bab\u4e0d\u96be\u554a\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6210\u90fd\u4e0d\u6562\uff0c\u6b64\u756a\u5317\u5f81\uff0c\u5f97\u4ee5\u6267\u97ad\u968f\u4fa7\uff0c\u6210\u90fd\u5df2\u611f\u6ee1\u610f\u4e86\u3002\u7f18 \u5c3d\u4e8e\u6b64\uff0c\u6210\u90fd\u5df2\u4e0d\u6562\u518d\u6709\u5984\u6c42\u4e86\u3002\u201d \u3000\u3000\u5df2\u7ecf\u8bed\u65e0\u4f26\u6b21\uff0c\u8bf4\u5f97\u4e0d\u50cf\u8bdd\u4e86\u3002 \u3000\u3000\u4f46\u80c6\u5927\u7684\u662f\u8881\u7d2b\u70df\u3002 \u3000\u3000\u5979\u7b11\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u53d6\u6b21\u82b1\u4e1b\u6175\u56de\u987e\uff0c\u534a\u7f18\u4fee\u9053\u534a\u7f18\u541b\uff0c\u4f60\u5df2\u611f\u6ee1\u610f\uff0c\u6ee1\u610f\u4ec0\u4e48\u5462\uff1f \u4e0d\u6562\u5984\u6c42\uff0c\u53c8\u60f3\u6c42\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5374\u5ae3\u7136\u4e00\u7b11\uff0c\u8f6c\u8eab\u8d70\u4e86\u3002 \u3000\u3000\u8fdc\u8fdc\u8dd1\u8fc7\u6765\u7684\u5de7\u513f\u3001\u83b2\u513f\u98de\u4e00\u822c\u7684\u6251\u5411\u4e86\u8881\u7d2b\u70df\u3002 \u3000\u3000\u5979\u4eec\u4e0d\u662f\u6251\u5165\u8881\u59d1\u5a18\u7684\u6000\u4e2d\uff0c\u5374\u662f\u6251\u5012\u5728\u5730\u4e0a\u3002\u4e00\u4eba\u62b1\u4f4f\u4e86\u8881\u7d2b\u70df\u4e00\u6761\u817f\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a \u201c\u5c0f\u59d0\uff0c\u60f3\u6b7b\u5c0f\u5a62\u4e86\u3002\u201d \u3000\u3000\u4e0d\u53eb\u8d35\u5983\uff0c\u53eb\u5c0f\u59d0\uff01 \u3000\u3000\u4e24\u4e2a\u4e2b\u5934\u597d\u50cf\u4e5f\u4e0d\u592a\u6ee1\u610f\u8d35\u5983\u8fd9\u4e2a\u8eab\u4efd\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6276\u8d77\u4e86\u4e24\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4e24\u5929\u8fb0\u5149\uff0c\u4f60\u4eec\u54ed\u5f97\u773c\u775b\u90fd\u7ea2\u4e86\u3002\u201d \u3000\u3000\u201c\u62c5\u5fc3\u5c0f\u59d0\u5440\uff01\u201d\u5de7\u513f\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u6b7b\uff0c\u5c0f\u59d0\u4e0d\u80fd\u4f24\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5bf9\u4e8c\u5a62\u7684\u5fe0\u5fc3\u4e5f\u6709\u4e9b\u611f\u52a8\uff0c\u9053\uff1a\u201c\u6b64\u756a\u56de\u5230\u957f\u5b89\u53bb\u6211\u8981\u597d\u597d\u4f20\u6388\u4f60\u4eec\u4e00\u4e9b\u672f\u6cd5\u3001 \u6b66\u529f\uff0c\u6211\u5df2\u7ecf\u4e3a\u4f60\u4eec\u91c7\u96c6\u4e86\u4e00\u4e9b\u836f\u7269\u3002\u201d \u3000\u3000\u83b2\u513f\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u5510\u4e09\u85cf\u5462\uff1f\u8ffd\u4e0a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5510\u4e09\u85cf\u662f\u5927\u4e58\u4f5b\u9053\u9ad8\u50e7\uff0c\u7cbe\u4fee\u7ecf\u6587\uff0c\u4f20\u64ad\u4f5b\u6559\u672a\u4e60\u672f\u6cd5\uff0c\u6240\u4ee5\u2026\u2026\u6211\u4e0d\u80fd \u8ffd\u6740\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u674e\u4e16\u6c11\u5462\uff1f\u201d\u83b2\u513f\u9053\uff1a\u201c\u5c0f\u59d0\u4e5f\u653e\u8fc7\u4ed6\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1b\u201c\u4ed6\u662f\u771f\u9f99\u5929\u5b50\u554a\uff01\u6709\u767e\u7075\u62a4\u4f51\uff0c\u5c31\u7b97\u771f\u60f3\u6740\u4e86\u4ed6\u3002\u4e5f\u662f\u6709\u6240\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5de7\u513f\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u5e0c\u671b\u674e\u4e16\u6c11\u6b7b\uff0c\u4ed6\u597d\u53ef\u601c\u4e5f\u597d\u4f1f\u5927\u554a\uff01\u201d \u3000\u3000\u201c\u53ef\u601c\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u674e\u4e16\u6c11\u54ea\u91cc\u53ef\u601c\u4e86\uff1f\u201d \u3000\u3000\u5de7\u513f\u9053\uff1a\u201c\u90a3\u4e48\u53ef\u4eba\u7684\u8d35\u5983\uff0c\u7adf\u7136\u8ba9\u7ed9\u4e86\u7687\u4e0a\u3002\u5c0f\u59d0\uff0c\u60c5\u4fa3\u79bb\u5206\uff0c\u53ef\u662f\u4eba\u95f4\u6700\u60b2\u60e8\u7684\u4e8b \u554a\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\uff0c\u63a5\u9053\uff1b\u201c\u5979\u53c8\u54ea\u91cc\u4f1f\u5927\u5462\uff1f\u201d \u3000\u3000\u7ea2\u5c18\u4e4b\u4e2d\u4f4f\u4e45\u4e86\uff0c\u8881\u7d2b\u70df\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u7684\u4ed9\u6c14\u5728\u9010\u6e10\u6d88\u9000\uff0c\u4eba\u6027\u3001\u60c5\u6101\u5374\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4 \u6210\u957f\u51fa\u6765\u3002 \u3000\u3000\u5de7\u513f\u7b54\u9053\uff1a\u201c\u4ee5\u4e07\u6c11\u5feb\u4e50\u4e3a\u5ff5\uff0c\u628a\u75db\u82e6\u7559\u7ed9\u81ea\u5df1\uff0c\u8fd9\u79cd\u4eba\u5f88\u5c11\u6709\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u51dd\u795e\u601d\u7d22\u4e86\u4e00\u9635\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u4f60\u4eec\u53bb\u4e70\u51e0\u5341\u53ea\u9e21\u6765\uff0c\u54b1\u4eec\u505a\u9053\u83dc\u7ed9 \u5927\u4f19\u513f\u5403\u3002\u201d \u3000\u3000\u83b2\u513f\u5fd9\u9053\uff1a\u201c\u6709\u706b\u5934\u519b\u554a\uff01\u505a\u83dc\u54ea\u91cc\u8fd8\u7528\u5c0f\u59d0\u52a8\u624b\uff1f\u4e00\u5b9a\u8981\u81ea\u5df1\u4f5c\uff0c\u4e5f\u6709\u6211\u548c\u5de7\u513f\uff0c\u54ea \u6562\u52b3\u52a8\u5c0f\u59d0\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u8fd9\u4e9b\u83dc\u6211\u8981\u81ea\u5df1\u52a8\u624b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4f5c\u6211\u52a9\u624b\u3002\u201d \u3000\u3000\u6d88\u606f\u4e00\u4f20\u51fa\uff0c\u5168\u519b\u54c4\u52a8\u4e86\uff01\u83dc\u8fd8\u6ca1\u6709\u4f5c\u51fa\u6765\uff0c\u4e8c\u767e\u6b66\u58eb\u5df2\u89c9\u53e3\u91cc\u751f\u6d25\uff0c\u5403\u5f97\u5fc3\u6ee1\u610f\u8db3 \u4e86\u3002 \u3000\u3000\u8d35\u5983\u5a18\u5a18\u70e7\u7684\u83dc\uff0c\u6b64\u5473\u5f97\u80fd\u51e0\u4eba\u5c1d\uff1f \u3000\u3000\u4e0d\u7ba1\u83dc\u662f\u5426\u70e7\u5f97\u597d\u5403\uff0c\u53ea\u662f\u8363\u8000\uff0c\u5c31\u80fd\u53eb\u4eba\u6b7b\u800c\u65e0\u61be\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u9910\uff0c\u8881\u7d2b\u70df\u548c\u5168\u519b\u5171\u5e2d\u3002 \u3000\u3000\u4e8c\u767e\u6b66\u58eb\u90fd\u60f3\u501f\u673a\u63a5\u8fd1\uff0c\u7785\u4e00\u7785\u8fd9\u4f4d\u7edd\u8272\u738b\u4eba\u3002 \u3000\u3000\u5b57\u6587\u6210\u90fd\u7edf\u519b\u4e25\u5389\uff0c\u5e73\u5e38\u4e4b\u65e5\uff0c\u5c31\u7b97\u8881\u56fd\u5e08\u8fd1\u5728\u54ab\u5c3a\uff0c\u4e5f\u4e0d\u6562\u8f6c\u5934\u659c\u89d1\u3002\u6b64\u523b\u5e2d\u5730\u540c \u5750\uff0c\u4e3e\u676f\u8a00\u6b22\uff0c\u5927\u5bb6\u90fd\u8fc7\u6765\u656c\u9152\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7adf\u4e5f\u653e\u4e0b\u4e86\u8d35\u5983\u7684\u8eab\u4efd\uff0c\u6765\u8005\u4e0d\u62d2\uff0c\u9152\u5230\u676f\u5e72\uff0c\u548c\u4f17\u6b66\u58eb\u4eec\u95f9\u4e2a\u4e0d\u4ea6\u4e50\u4e4e\uff01 \u3000\u3000\u5b57\u6587\u6210\u90fd\u5fcd\u4e86\u53c8\u5fcd\uff0c\u5b9e\u5728\u662f\u5fcd\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u5b50\u8981\u53d1\u4f5c\u2026\u2026 \u3000\u3000\u8427\u96e8\u626f\u4e86\u4e00\u4e0b\u4ed6\u8863\u8896\uff0c\u9053\uff1a\u201c\u603b\u7edf\u9886\uff0c\u8ba9\u56fd\u5e08\u81ea\u5df1\u5e94\u4ed8\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u6837\u95f9\u4e0b\u53bb\u600e\u4e48\u5f97\u4e86\uff1f\u201d\u5b57\u6587\u6210\u90fd\u9053\uff1a\u201c\u8881\u56fd\u5e08\u5df2\u88ab\u4ed6\u4eec\u704c\u4e86\u51e0\u5341\u676f\u5566\uff01\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u8881\u56fd\u5e08\u4e0d\u662f\u666e\u901a\u4eba\u554a\uff01\u5979\u5df2\u662f\u534a\u4ed9\u4e4b\u4f53\uff0c\u5c31\u7b97\u559d\u4e0a\u4e00\u5343\u676f\u4e5f\u4e0d\u4f1a\u9189\u5440\uff01\u201d \u3000\u3000\u5b57\u6587\u6210\u90fd\u51dd\u76ee\u770b\u53bb\uff0c\u53ea\u89c1\u8881\u7d2b\u70df\u8138\u4e0a\u98de\u6cdb\u51fa\u4e00\u7247\u6843\u7ea2\uff0c\u5a07\u8273\u5f97\u8ba9\u4eba\u5fc3\u8df3\u52a0\u901f\u3002\u4f46\u9664\u6b64\u4e4b \u5916\uff0c\u5c31\u5168\u65e0\u9189\u9152\u5f81\u8c61\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u9910\uff0c\u5168\u519b\u653e\u91cf\u3002 \u3000\u3000\u5927\u90fd\u9189\u5012\u4e86\uff0c\u8fde\u5b57\u6587\u6210\u90fd\u4e5f\u559d\u4e86\u516b\u5206\u9189\u610f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u559d\u5f97\u6700\u591a\u4e86\u3002 \u3000\u3000\u4e24\u767e\u6b66\u58eb\u5206\u522b\u656c\u9152\uff0c\u6ca1\u6709\u4e00\u4e2a\u80af\u81ea\u613f\u6f0f\u6389\u3002 \u3000\u3000\u770b\uff0c\u53ea\u89c9\u9152\u610f\u591a\u589e\u4e09\u5206\u5a07\uff0c\u8ba9\u4eba\u770b\u5f97\u8fc7\u763e\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u662f\u5426\u559d\u9189\u4e86\uff0c\u5c31\u6ca1\u6709\u4eba\u77e5\u9053\u3002 \u3000\u3000\u83b2\u513f\u3001\u5de7\u513f\u6276\u5979\u8fdb\u5165\u84ec\u5e10\u4e2d\uff0c\u5c31\u672a\u518d\u51fa\u6765\u8fc7\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5348\u540e\u8d77\u9a6c\u3002 \u3000\u3000\u4e24\u767e\u94c1\u9a91\u8361\u8d77\u4e86\u4e00\u9053\u70df\u5c18\uff0c\u76f4\u5954\u957f\u5b89\u3002 \u3000\u3000\u4e0d\u77e5\u968b\u7080\u5e1d\u600e\u4e48\u4f1a\u5f97\u5230\u4e86\u6d88\u606f\uff0c\u4eb2\u7387\u4e86\u8427\u7687\u540e\u3001\u5b9d\u8d35\u5983\u3001\u5341\u4e8c\u9662\u7684\u592b\u4eba\uff0c\u8fce\u63a5\u5230\u957f\u5b89\u57ce \u5916\u3002 \u3000\u3000\u524d\u5546\u662f\u6587\u6b66\u767e\u5b98\uff0c\u540e\u9762\u662f\u4e00\u5927\u7fa4\u82b1\u679d\u62db\u5c55\u7684\u5bab\u5973\uff0c\u62e5\u62a4\u7740\u7687\u4e0a\u3001\u7687\u540e\u548c\u5341\u4e8c\u9662\u7684\u592b\u4eba\u3002 \u3000\u3000\u5206\u522b\u5904\u5728\u5404\u5b98\u8863\u670d\u7684\u989c\u8272\u4e0d\u540c\u3002 \u3000\u3000\u5f69\u8272\u8fce\u98ce\uff0c\u7eeb\u7f57\u98d8\u98de\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u4e24\u767e\u6587\u6b66\u5206\u5217\u4e24\u8fb9\u3002 \u3000\u3000\u8881\u7d2b\u70df\u548c\u5b57\u6587\u6210\u90fd\u4e24\u9a91\u9a6c\u8d8a\u4f17\u800c\u51fa\u3002 \u3000\u3000\u5982\u6b64\u7684\u6d69\u5927\u6b22\u8fce\u573a\u9762\uff0c\u628a\u8881\u7d2b\u70df\u4e5f\u770b\u50bb\u4e86\u3002 \u3000\u3000\u7a7a\u624b\u5f52\u6765\uff0c\u5982\u4f55\u5411\u7687\u4e0a\u590d\u547d\u5462\uff1f\u8881\u7d2b\u70df\u60f3\u5230\u5317\u5f81\u524d\u6240\u5938\u4e0b\u7684\u6d77\u53e3\uff0c\u987f\u89c9\u53cc\u988a\u751f\u70ed\uff0c\u53cc\u817f \u53d1\u8f6f\u3002 \u3000\u3000\u770b\u5230\u4e86\u8881\u7d2b\u70df\u7389\u5bb9\u4f9d\u7136\uff0c\u968b\u7080\u5e1d\u7a81\u7136\u60f3\u5230\u4eba\u95f4\u81f3\u4e50\uff0c\u4e00\u628a\u6293\u4f4f\u4e86\u8881\u5b9d\u513f\uff0c\u5feb\u6b65\u8fce\u4e86\u4e0a \u6765\u3002 \u3000\u3000\u8fde\u4e00\u4fa7\u7684\u8427\u7687\u540e\u4e5f\u51b2\u8fc7\u6587\u6b66\u767e\u5b98\uff0c\u4e00\u4e0b\u5b50\u8dd1\u81f3\u6700\u524d\u9762\u3002 \u3000\u3000\u5bb3\u5f97\u56db\u4e2a\u62a4\u9a7e\u7684\u6b66\u58eb\u4e0d\u5f97\u4e0d\u65bd\u51fa\u8f7b\u529f\uff0c\u7531\u5bab\u5973\u3001\u767e\u5b98\u4eba\u5934\u4e0a\u98de\u8fc7\u53bb\uff0c\u8ffd\u4e0a\u7687\u5e1d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u83b2\u6b65\u59d7\u59d7\u7684\u8fce\u4e0a\u53bb\u3002 \u3000\u3000\u5979\u64a9\u8d77\u8863\u88d9\uff0c\u8dea\u4e0b\u9053\uff1a\u201c\u7d2b\u70df\u5931\u804c\uff0c\u7687\u4e0a\u8d50\u7f6a\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u539f\u60f3\u4e00\u628a\u62b1\u6ee1\u6000\uff0c\u54ea\u7ba1\u4ed6\u767e\u5b98\u5728\u4fa7\uff1f \u3000\u3000\u5374\u4e0d\u6599\u8881\u7d2b\u70df\u4e00\u4e0b\u77ee\u534a\u622a\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u5bb3\u5f97\u88ab\u8272\u60c5\u638f\u7a7a\u8eab\u5b50\u7684\u968b\u7080\u5e1d\uff0c\u51e0\u4e4e\u8981\u4e00\u8de4\u8dcc \u5012\u3002 \u3000\u3000\u5e78\u597d\u8881\u7d2b\u70df\u6084\u7136\u4f38\u51fa\u4e86\u5de6\u624b\uff0c\u4e0d\u7740\u75d5\u8ff9\u7684\u6276\u4e86\u7687\u4e0a\u4e00\u628a\u3002 \u3000\u3000\u201c\u537f\u5bb6\u4e0d\u7528\u591a\u793c\uff0c\u5feb\u5feb\u8bf7\u8d77\u3002\u201d \u3000\u3000\u7687\u4e0a\u4f38\u624b\u53bb\u6276\u8881\u7d2b\u70df\u3002 \u3000\u3000\u201c\u81e3\u59be\u6709\u7f6a\u5440\uff01\u201d \u3000\u3000\u201c\u8d77\u6765\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u7687\u4e0a\u7528\u529b\u63e1\u4f4f\u8881\u7d2b\u70df\u7684\u5c0f\u624b\uff0c\u597d\u4e45\u6ca1\u6709\u78b0\u8fc7\u4e86\uff0c\u6293\u7d27\u5c0f\u624b\u7528\u529b\u62d6\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u80af\u8d77\u6765\uff0c\u968b\u7080\u5e1d\u5982\u4f55\u80fd\u62d6\u5f97\u52a8\u5979\uff1f \u3000\u3000\u201c\u7687\u4e0a\uff01\u201d\u8881\u5b9d\u513f\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u9976\u6055\u7d2b\u70df\u59d0\u59d0\u7684\u7f6a\uff0c\u5979\u5982\u4f55\u6562\u7ad9\u8d77\u6765\u5440\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\uff01\u537f\u5bb6\u65e0\u7f6a\uff0c\u7d2b\u70df\uff0c\u6709\u8bdd\u8d77\u6765\u8bf4\u5427\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u62ac\u5934\u671b\u4e86\u8881\u5b9d\u513f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u81e3\u59be\u672c\u5df2\u751f\u64d2\u674e\u4e16\u6c11\uff0c\u4f46\u4e2d\u9014\u53c8\u88ab\u4ed6\u9003\u8131\u4e86\u3002\u201d \u3000\u3000\u201c\u9003\u4e86\u4e2a\u674e\u4e16\u6c11\uff0c\u7b97\u5f97\u4ec0\u4e48\uff1f\u537f\u5bb6\u5e73\u5b89\u56de\u6765\u5c31\u597d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6d41\u4e0b\u773c\u6cea\uff0c\u9053\uff1a\u201c\u81e3\u59be\u65e0\u80fd\uff0c\u5317\u5f80\u672a\u5f97\u4e3b\u72af\uff0c\u8fd8\u8bf7\u7687\u4e0a\u8d50\u7f6a\u5427\uff01\u201d \u3000\u3000\u5979\u4e0d\u662f\u4e3a\u968b\u7080\u5e1d\u4e0d\u9677\u7f6a\u800c\u611f\u52a8\u843d\u6cea\uff0c\u800c\u662f\u60ed\u6127\u7684\u6cea\u6c34\u3002 \u3000\u3000\u867d\u672a\u6298\u635f\u4e00\u5175\u4e00\u5352\uff0c\u4f46\u4e5f\u672a\u64d2\u56de\u654c\u4eba\u4e3b\u72af\uff0c\u521d\u6b21\u51fa\u5e08\uff0c\u5982\u6b64\u4e0d\u5229\uff0c\u8881\u7d2b\u70df\u662f\u771f\u7684\u5fc3\u4e2d\u96be \u8fc7\u3002 \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u6587\u6b66\u767e\u5b98\u5728\u6b64\uff0c\u8981\u7d2b\u70df\u59d0\u59d0\u5982\u4f55\u4e0b\u53f0\uff0c\u5feb\u4e9b\u56de\u5230\u5bab\u4e2d\u8bf4\u5427\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u5927\u58f0\u559d\u9053\uff1a\u201c\u8d77\u9a7e\u56de\u5b98\uff01\u201d \u3000\u3000\u60f3\u62d6\u8881\u7d2b\u70df\u4e00\u8d77\u4e0a\u8f66\uff0c\u4e00\u526f\u8feb\u4e0d\u53ca\u5f85\u7684\u9965\u6e34\u6837\u5b50\uff0c\u4f46\u5374\u88ab\u8881\u5b9d\u513f\u63a8\u5f00\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u4f4e\u58f0\u9053\uff1a\u201c\u7687\u4e0a\u8bf7\u5148\u8d70\uff0c\u6211\u966a\u7d2b\u70df\u59d0\u59d0\u56de\u5bab\uff0c\u7a0d\u5b89\u52ff\u8e81\uff0c\u9a6c\u4e0a\u5c31\u5230\u3002\u65e2\u5df2\u8dea\u4e0b\u6cbb \u7f6a\uff0c\u600e\u80fd\u4e0a\u4f60\u8f66\u8f87\uff1f\u201d \u3000\u3000\u7687\u4e0a\u60f3\u60f3\u4e5f\u5bf9\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u4e0a\u8f66\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u662f\u607c\u6839\u8881\u7d2b\u70df\u4e0d\u4f53\u5723\u5fc3\uff0c\u800c\u662f\u607c\u6068\u6587\u6b66\u767e\u5b98\u8ddf\u7740\u6765\uff0c\u788d\u4ed6\u7684\u4e8b\u3002 \u3000\u3000\u5982\u975e\u6587\u6b66\u767e\u5b98\u968f\u884c\uff0c\u8881\u7d2b\u70df\u4e5f\u8bb8\u65e9\u5c31\u4e0a\u4ed6\u7684\u8f66\u8f87\u56de\u5bab\u3002 \u3000\u3000\u5341\u4e8c\u9662\u7684\u592b\u4eba\u548c\u6587\u6b66\u767e\u5b98\u5148\u540e\u52a8\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5374\u8e72\u5728\u8881\u7d2b\u70df\u8eab\u4fa7\u966a\u4f34\u3002 \u3000\u3000\u539f\u6765\u8881\u7d2b\u70df\u4f4e\u7740\u5934\uff0c\u4e00\u76f4\u8dea\u7740\uff0c\u5979\u7f9e\u89c1\u6587\u6b66\u767e\u5b98\uff0c\u4e5f\u7f9e\u89c1\u5bab\u4e2d\u5ad4\u5983\u3002 \u3000\u3000\u8427\u7687\u540e\u6ca1\u6709\u8d70\uff0c\u5a09\u5a09\u5a77\u5a77\u7684\u884c\u8fc7\u6765\u3002 \u3000\u3000\u5979\u7275\u7740\u8881\u7d2b\u70df\u4e00\u53ea\u624b\uff0c\u9053\uff1b\u201c\u59b9\u59b9\uff0c\u8d77\u6765\u5427\uff01\u7687\u4e0a\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5728\u8427\u7687\u540e\u7275\u62c9\u4e0b\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u597d\u60ed\u6127\uff0c\u6211\u5df2\u7ecf\u628a\u674e\u4e16\u6c11\u903c\u51fa\u76d8\u9f99\u5c45\uff0c\u751f \u64d2\u4e86\u4ed6\uff0c\u60f3\u4e0d\u5230\u62bc\u89e3\u56de\u957f\u5b89\u7684\u9014\u4e2d\uff0c\u9047\u4e0a\u4e86\u4e2a\u548c\u5c1a\u5510\u4e09\u85cf\uff0c\u53c8\u628a\u4ed6\u7ed9\u6551\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u4e5f\u4e0d\u80fd\u602a\u59b9\u59b9\u5440\uff01\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u5929\u610f\u5440\uff01\u5b9d\u513f\uff01\u201d \u3000\u3000\u201c\u59be\u5983\u5728\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u672a\u80fd\u548c\u8427\u7687\u540e\u624b\u5e15\u8ba1\u4ea4\uff0c\u5bf9\u7687\u540e\u5c31\u5341\u5206\u656c\u91cd\u4e86\u3002 \u3000\u3000\u201c\u966a\u7740\u7d2b\u70df\u56de\u5bab\u9662\uff0c\u7687\u4e0a\u8981\u964d\u7f6a\uff0c\u5c31\u628a\u6211\u4e5f\u5173\u8d77\u6765\uff01\u201d \u3000\u3000\u201c\u591a\u8c22\u7687\u540e\u59d0\u59d0\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6d41\u9732\u51fa\u65e0\u9650\u611f\u6fc0\u3002 \u3000\u3000\u8427\u7687\u540e\u6325\u6325\u624b\uff0c\u4e0a\u8f66\u800c\u53bb\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6276\u7740\u8881\u7d2b\u70df\uff0c\u767b\u4e0a\u4e86\u7bf7\u8f66\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u867d\u7136\u4e5f\u6ca1\u6709\u8d70\uff0c\u4f46\u4ed6\u5df2\u5e26\u7740\u56db\u4f4d\u526f\u7edf\u9886\u548c\u4e8c\u767e\u94c1\u9a91\uff0c\u9000\u5230\u4e86\u56db\u4e94\u4e08\u5916\u3002 \u3000\u3000\u5de7\u513f\u3001\u83b2\u513f\u3001\u5b9d\u8d35\u5983\u968f\u6765\u7684\u5bab\u5973\u4eec\uff0c\u7275\u7740\u8863\u8896\u4e0a\u4e86\u8f66\u3002 \u3000\u3000\u653e\u4e0b\u5782\u5e18\uff0c\u8881\u5b9d\u513f\u7acb\u523b\u5bf9\u8881\u7d2b\u70df\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1b\u201c\u591a\u8c22\u59d0\u59d0\u653e\u4e86\u4ed6\u4e00\u6761\u6027\u547d\uff0c\u5b9d\u513f\u7ed9\u4f60 \u78d5\u5934\u5566\uff01\u201d \u3000\u3000\u62c9\u4f4f\u4e86\u5b9d\u8d35\u5983\uff0c\u8881\u7d2b\u70df\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u4e09\u85cf\u548c\u5c1a\u6551\u8d70\u4ed6\uff0c\u4e0d\u662f\u6211\u8be2\u79c1\u653e\u7eb5\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u662f\u59d0\u59d0\u60c5\u610f\u91cd\uff0c\u8ba9\u4ed6\u5148\u8dd1\u4e00\u5343\u91cc\uff0c\u4e5f\u9003\u4e0d\u8fc7\u59d0\u59d0\u7684\u624b\u638c\u5fc3\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5343 \u6069\u4e07\u60c5\u5b9d\u513f\u627f\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff01 \u3000\u3000\u4e3a\u6b64\u4e89\u6267\uff0c\u5f92\u8d39\u53e3\u820c\uff0c\u53ea\u597d\u6c89\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u8881\u5b9d\u513f\u7406\u4e00\u7406\u53d1\u8fb9\u6563\u53d1\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u60f3\u59d0\u59d0\uff0c\u60f3\u5f97\u5feb\u53d1\u75af\u4e86\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u5b9a\u4f60\u7684\u7f6a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u59b9\u59b9\uff0c\u89c1\u8fc7\u4e86\u674e\u4e16\u6c11\uff0c\u6211\u5f88\u540e\u6094\u79bb\u5f00\u6df1\u5c71\u5165\u7ea2\u5c18\u3002\u6df1\u5c71\u4e2d\u867d\u7136\u751f\u6d3b\u5f97\u5bc2 \u5bde\u4e00\u4e9b\uff0c\u4f46\u5374\u65e0\u70e6\u607c\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5fd9\u9053\uff1a\u201c\u600e\u4e48\uff1f\u59d0\u59d0\u73b0\u5728\u6709\u70e6\u607c\u4e86\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u5c0f\u59b9\uff0c\u59d0\u59d0\u5206\u62c5\u6211\u7684\u592a\u591a\u4e86\uff0c\u73b0 \u5728\u4e5f\u8ba9\u5c0f\u59b9\u5206\u4e00\u70b9\u59d0\u59d0\u7684\u5fe7\u82e6\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u8be5\u6295\u5165\u957f\u5b89\u7687\u5bab\u4e2d\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u70e6\u607c\u7686\u56e0\u5f3a\u51fa\u5934\uff0c\u6211\u72af\u4e86\u597d\u5927\u4e00\u4e2a\u9519\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u77e5\u9519\u5c31\u80fd\u6539\u5440\uff01\u7d2b\u70df\u59d0\uff0c\u4f60\u72af\u4e86\u4ec0\u4e48\u9519\u5462\uff1f\u5c0f\u59b9\u529b\u6709\u80fd\u53ca\uff0c\u613f\u66ff\u59d0\u59d0\u627f\u62c5 \u4e00\u5207\u9519\u5931\u554a\uff01\u201d \u3000\u3000\u201c\u5b9d\u513f\uff0c\u6211\u77e5\u9053\u4f60\u624d\u6167\u7edd\u4eba\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u672f\u6cd5\u7cbe\u6e5b\uff0c\u4e5f\u957f\u4e8e\u7b97\u8ba1\u4e4b\u5b66\u2026\u2026\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff01\u201d\u8881\u5b9d\u513f\u63a5\u9053\uff1a\u201c\u5c0f\u59b9\u4e00\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u6bd4\u8d77\u59d0\u59d0\uff0c\u4f55\u557b\u5c0f\u5deb\u89c1\u5927\u5deb\uff0c\u4e0d\u63d0\u4e5f \u7f62\uff01\u201d \u3000\u3000\u201c\u5b9d\u513f\uff0c\u53ef\u4ee5\u8d70\u554a\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5929\u5730\u8fbd\u9614\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u7559\u5728\u957f\u5b89\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u80fd\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u7d2b\u70df\u59d0\uff0c\u4e00\u5bf8\u76f8\u601d\u4e00\u5bf8\u7070\uff0c\u6211\u4e0d\u80fd\u771f\u7684\u9003\u51fa\u7f57\u7f51\u5916\uff0c\u8ba9\u4ed6 \u4e00\u4eba\u53d7\u6298\u78e8\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u82e6\u7b11\u9053\uff1a\u201c\u5509\uff01\u4f60\u4eec\u771f\u662f\u4e00\u5bf9\u5b9d\u554a\uff01\u6211\u53ea\u544a\u8bc9\u674e\u4e16\u6c11\uff0c\u8ba9\u4ed6\u8ddf\u4f60\u89c1\u4e00\u9762\uff0c\u4ed6\u5c31\u7b54 \u5e94\u968f\u6211\u5165\u957f\u5b89\u4e86\u3002\u201d \u3000\u3000\u201c\u59d0\u59d0\u5728\u76d8\u9f99\u5c45\u89c1\u4ed6\u7684\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u7684\uff01\u4ed6\u8bf7\u6211\u5403\u996d\uff0e\u4e5f\u8ba9\u6211\u89c1\u5230\u4e86\u5f88\u591a\u82f1\u96c4\u4eba\u7269\u3002\u79e6\u743c\u3001\u656c\u5fb7\u3001\u67f4 \u7ecd\u3001\u7a0b\u77e5\u8282\uff0c\u8fd8\u6709\u4e24\u4f4d\u5f02\u4eba\u674e\u6df3\u98ce\u548c\u8881\u5929\u7f61\u3002\u201d \u3000\u3000\u201c\u674e\u9756\u5462\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5728\u4e0d\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u7cbe\u8bfb\u5175\u6cd5\uff0c\u4ea6\u901a\u5947\u672f\uff0c\u662f\u4e00\u4ee3\u5927\u5e05\u4e4b\u624d\uff0c\u4f46\u4ed6\u5374\u503e\u670d\u5728\u674e\u4e16\u6c11 \u7684\u4ec1\u80de\u7269\u4e0e\u4e4b\u4e0b\u3002\u5b9d\u513f\uff0c\u56de\u53bb\u5427\uff01\u4ed6\u60f3\u4f60\uff0c\u60f3\u5f97\u597d\u82e6\u554a\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u51c4\u51c9\u4e00\u7b11\uff0c\u9053\uff1b\u201c\u6765\u65f6\u7389\u6d01\u51b0\u6e05\uff0c\u5982\u4eca\u662f\u70c2\u6843\u7834\u74dc\uff0c\u4f60\u53eb\u6211\u54ea\u91cc\u6709\u8138\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4f60\u662f\u672f\u6cd5\u4e2d\u4eba\uff0c\u8fd8\u5728\u4e4e\u8fd9\u4ef6\u4e8b\u5417\uff1f\u6211\u53c8\u5982\u4f55\u5462\uff1f\u4e8c\u5341\u5e74\u6e05\u767d\u8eab\u8eaf\uff0c \u8fd8\u4e0d\u662f\u88ab\u7687\u4e0a\u7ed9\u767d\u767d\u5403\u6389\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u540c\u554a\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4f60\u5fc3\u65e0\u6240\u5c5e\uff0c\u5929\u4e0b\u4f55\u5904\u4e0d\u53ef\u53bb\uff0c\u7537\u5b50\u4f55\u4eba\u4e0d\u53ef\u9002\uff1f\u4f46\u6211\u548c \u4ed6\u65e9\u5df2\u60c5\u7ed3\u540c\u5fc3\uff0c\u975e\u90ce\u4e0d\u5ac1\uff0c\u5374\u628a\u6e05\u767d\u7ed9\u4e86\u522b\u4eba\u3002 \u3000\u3000\u60f3\u8d77\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u6211\u5c31\u5982\u5229\u5203\u7a7f\u5fc3\uff0c\u6211\u65e0\u6cd5\u9884\u6599\uff0c\u89c1\u5230\u4ed6\u6211\u5982\u4f55\u53cd\u5e94\uff1f\u662f\u4e94\u6b65\u6e85\u8840\u6b7b\uff0c\u6216 \u662f\u9e33\u9e2f\u5e76\u5c38\u4ea1\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5b9d\u59b9\u59b9\uff0c\u4f60\u53ef\u662f\u6709\u70b9\u6068\u4ed6\uff1f\u201d \u3000\u3000\u201c\u7231\u6068\u4ea4\u7ec7\u554a\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u53ef\u4ee5\u628a\u6211\u7559\u5728\u8eab\u4fa7\u7684\uff0c\u4f46\u4ed6\u5374\u8ba9\u6211\u8fdb\u4e86\u957f\u5b89\u3002\u201d \u3000\u3000\u201c\u8fd9\u7a76\u7adf\u662f\u8c01\u7684\u4e3b\u610f\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u96be\u9053\u674e\u4e16\u6c11\u4e0d\u77e5\u9053\u4f60\u4e00\u8eab\u6240\u5b66\u7edd\u4e0d\u5728\u674e\u9756\u4e4b \u4e0b\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8bba\u5175\u6cd5\uff0c\u884c\u519b\u5e03\u9635\uff0c\u6211\u4e0d\u5982\u4ed6\u3002\u4f46\u884c\u6cd5\u62fc\u6740\uff0c\u6211\u7edd\u5bf9\u5f3a\u8fc7\u674e\u6e05\uff0c\u53ea\u4e3a\u4e86\u4ed6 \u7239\u4e00\u53e5\u8bdd\uff0c\u6211\u5c31\u88ab\u9001\u5165\u957f\u5b89\u57ce\u4e86\u3002\u201d \u3000\u3000\u201c\u7559\u5b88\u5019\u674e\u6e0a\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8001\u5934\u5b50\u7ba1\u7684\u4ec0\u4e48\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u4e5f\u6068\u53d4\u53d4\u8881\u5929\u7f61\uff0c\u4ed6\u540c\u610f\u8ba9\u6211\u5165\u5bab\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6447\u5934\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u6211\u6240\u542c\u5230\u7684\u5c31\u4e0d\u662f\u5982\u6b64\u4e86\uff0c\u60c5\u7518\u820d\u5165\u957f\u5b89\uff0c\u52a9\u4ed6\u5b8c\u6210\u6551\u4e16\u613f\uff1b \u5b9d\u513f\uff0c\u662f\u4f60\u81ea\u5df1\u8981\u6765\u7684\u554a\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u60c5\u975e\u5f97\u5df2\u5440\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u548c\u4e16\u6c11\u4e4b\u95f4\uff0c\u8fd8\u631f\u7740\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u7528\u4e0a\u5fc3\u673a\u4e86\u3002\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u6211\u867d\u5728\u6df1\u5c71\u53e4\u6d1e\u4e2d\u957f\u5927\uff0c\u4e0d\u660e\u7ea2\u5c18\u60c5\u7231\uff0c\u4f46\u6211\u8bfb\u8fc7\u4e66 \u554a\uff01\u7537\u5973\u76f8\u60a6\uff0c\u4e24\u60c5\u76f8\u6295\uff0c\u5982\u4f55\u8fd8\u80fd\u631f\u4e2a\u7b2c\u4e09\u8005\u3002\u800c\u4e14\uff0c\u4e00\u52a0\u5c31\u662f\u4e24\u4e2a\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6ca1\u6cd5\u5b50\u554a\uff01\u5510\u516c\u674e\u6e0a\u751f\u4e86\u4e09\u4e2a\u513f\u5b50\uff0c\u4e16\u6c11\u884c\u4e8c\uff0c\u4ed6\u4eec\u662f\u5144\u5f1f\uff0c\u6211\u88ab\u4ed6\u4eec\u770b \u5230\u4e86\uff0c\u60f9\u6765\u4e00\u9635\u7ea0\u7f20\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ee5\u4f60\u672f\u6cd5\u4e4b\u7cbe\uff0c\u6446\u8131\u4ed6\u4eec\u7ea0\u7f20\u6613\u5982\u53cd\u638c\uff0c\u96be\u9053\u4ed6\u4eec\u6562\u627e\u4e0a\u95e8\u53bb\uff1f\u201d \u3000\u3000\u201c\u8bf4\u8d77\u6765\u548e\u7531\u81ea\u53d6\u554a\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u60f3\u548c\u4e16\u6c11\u5e38\u76f8\u5de6\u53f3\uff0c\u6240\u4ee5\u8981\u6c42\u4ed6\u8ba9\u6211\u5230\u4e91\u4e2d\u5c71 \u4eb2\u4f8d\u996e\u98df\u3002\u4e8b\u5b9e\u4e0a\u4ed6\u5e7f\u8bfb\u4e07\u5377\u4e66\uff0c\u7ecf\u53f2\u4e4b\u5916\uff0c\u8fd8\u517c\u5175\u6cd5\u3001\u5929\u6587\uff0c\u53c8\u8d1f\u8d23\u8bad\u7ec3\u4e09\u5343\u94c1\u7532\u5175\uff0c\u65e5 \u95f4\u7ec3\u5175\u591c\u8bfb\u7ecf\u4e66\uff0c\u8f9b\u82e6\u5f97\u4eba\u90fd\u7626\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u5f97\u5fc3\u75bc\u4e86\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u7231\u4ed6\u4e4b\u6df1\uff0c\u5b9e\u5df2\u8d85\u8d8a\u4e86\u751f\u6b7b\u754c\u9650\uff0c\u4e3a\u4ed6\u751f\u800c\u6b22\uff0c\u4e3a\u4ed6\u6b7b\u65e0\u6028\u3002 \u7d2b\u70df\u59d0\uff0c\u770b\u5230\u4ed6\u8f9b\u82e6\u6d88\u7626\uff0c\u6211\u7684\u5fc3\u5728\u6ef4\u8840\u3002 \u3000\u3000\u6211\u65e0\u6cd5\u4ee5\u8eab\u76f8\u4ee3\uff0c\u53ea\u597d\u5b88\u5728\u4ed6\u7684\u8eab\u4fa7\uff0c\u5e2e\u4ed6\u8c03\u7406\u996e\u98df\uff0c\u5f85\u4ed6\u75b2\u5026\u65f6\uff0c\u5e2e\u4ed6\u6309\u6469\u7b4b\u9aa8\u3002\u5f53 \u7136\uff0c\u6211\u4e5f\u6697\u4e2d\u628a\u5185\u529b\u8f93\u9001\u5230\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u5e2e\u4ed6\u8212\u6563\u7b4b\u9aa8\uff0c\u7537\u5973\u4e24\u4eba\u808c\u80a4\u76f8\u4eb2\uff0c\u96be\u9053\u4ed6\u5c31\u6ca1\u6709\u52a8\u8fc7\u4f60\u5417\uff1f\u8bf4 \u5b9e\u8bdd\u5440\uff01\u53ef\u4e0d\u80fd\u9a97\u6211\u3002\u201d \u3000\u3000\u201c\u4ed6\u6709\u5f88\u5f3a\u7684\u81ea\u6211\u514b\u5236\u80fd\u529b\uff0c\u6211\u53cc\u624b\u9f50\u65bd\uff0c\u5728\u4ed6\u8eab\u4e0a\u6309\u6765\u6478\u53bb\uff0c\u4ed6\u7adf\u80fd\u95ed\u76ee\u517b\u795e\uff0c\u98df\u6307 \u4e0d\u52a8\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u53ea\u6709\u90a3\u4e48\u4e00\u6b21\uff0c\u4ed6\u5ffd\u7136\u4e00\u633a\u800c\u8d77\uff0c\u628a\u6211\u63fd\u5165\u6000\u4e2d\u3002\u4e8b\u60c5\u5927\u7a81\u7136\u4e86\uff0c\u6ca1 \u6709\u4e00\u70b9\u9884\u5146\uff0c\u5413\u4e86\u6211\u4e00\u8df3\uff0c\u4e0d\u81ea\u4e3b\u63a8\u4e86\u4ed6\u4e00\u628a\uff0c\u5c31\u8fd9\u4e00\u628a\u63a8\u574f\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u600e\u4e48\uff0c\u607c\u7f9e\u6210\u6012\u4e86\uff1f\u4f60\u7231\u4ed6\u91cd\u8fc7\u6027\u547d\uff0c\u63fd\u4f60\u5165\u6000\uff0c\u4e0d\u662f\u6b63\u5982\u537f\u613f\u5417\uff1f\u4e3a\u4ec0 \u4e48\u8981\u63a8\u4ed6\u4e00\u628a\uff1f\u201d \u3000\u3000\u201c\u4e00\u65f6\u7f9e\u6025\u7684\u53cd\u5e94\uff0c\u548c\u5fc3\u613f\u80cc\u9053\u800c\u9a70\u4e86\uff01\u201d\u8881\u5b9d\u513f\u9eef\u7136\u9053\uff1a\u201c\u66f4\u60f3\u4e0d\u5230\u7684\u662f\u4f1a\u5bf9\u4ed6\u9020\u6210 \u90a3\u4e48\u5927\u7684\u4f24\u5bb3\uff0c\u4ed6\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u6562\u770b\u6211\uff0c\u6211\u6309\u6469\u4ed6\u7684\u53cc\u988a\u65f6\uff0c\u53d1\u89c9\u70ed\u70eb\u5982\u706b\uff0c\u90a3\u662f\u7f9e\u4e0e\u5fff\u4ea4 \u4f5c\u7684\u611f\u53d7\u3002 \u3000\u3000\u4ed6\u95ed\u7740\u773c\u8bf4\u4ed6\u8f7b\u4fae\u4e86\u6211\uff0c\u5fc3\u4e2d\u597d\u60ed\u6127\uff0c\u6211\u662f\u4ed6\u6700\u656c\u91cd\u7684\u5973\u4eba\uff0c\u8981\u6211\u539f\u8c05\u4ed6\uff0c\u4ed6\u4e00\u65f6\u60c5\u4e0d \u81ea\u7981\uff0c\u4ee5\u540e\u7edd\u4e0d\u4f1a\u518d\u72af\u4e86\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u55e4\u5730\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u5982\u6709\u7687\u4e0a\u5341\u5206\u4e4b\u4e00\u7684\u539a\u8138\u76ae\uff0c\u65e9\u5c31\u628a\u4f60\u62b1\u4e0a\u5e8a \u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u867d\u7136\u5e74\u7eaa\u8f7b\u4e9b\uff0c\u4f46\u6211\u4fee\u4e60\u7384\u529f\u672f\u6cd5\uff0c\u5fc3\u667a\u7684\u6210\u719f\uff0c\u5927\u6211\u5341\u5c81\u516b\u5c81\u7684\u4eba\u4e5f \u672a\u5fc5\u80dc\u5f97\u8fc7\u6211\u3002\u6211\u542c\u5f97\u5934\u76ae\u53d1\u9ebb\uff0c\u5374\u53c8\u65e0\u53ef\u5948\u4f55\uff01\u201d \u3000\u3000\u201c\u8fd9\u771f\u662f\u4e00\u4ef6\u9ebb\u70e6\u4e8b\u60c5\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5973\u4eba\u5fc3\u91cc\u80af\uff0c\u4e5f\u65e0\u6cd5\u8bf4\u51fa\u6765\u3002\u4f60\u6211\u90fd\u662f\u4fee\u4e60\u672f \u6cd5\u7684\u4eba\uff0c\u4f46\u8fd9\u8fd1\u5343\u5e74\u7684\u4f20\u7edf\uff0c\u6211\u4eec\u4e5f\u4e0d\u6562\u6253\u7834\u5440\uff01 \u3000\u3000\u4ec0\u4e48\u4e09\u4ece\u56db\u5fb7\uff1f\u5bb3\u4eba\u4e0d\u6d45\u5440\uff01\u4e0d\u8fc7\u53e3\u91cc\u867d\u4e0d\u80fd\u8bf4\uff0c\u53ef\u4ee5\u6311\u9017\u4ed6\u5440\uff01\u4f60\u4eec\u4e00\u5ba4\u76f8\u5904\uff0c\u5b64\u7537 \u5be1\u5973\uff0c\u4f60\u5e2e\u4ed6\u63a8\u62ff\u6309\u6469\uff0c\u673a\u4f1a\u591a\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u90a3\u4e00\u6b21\u628a\u4ed6\u5413\u6015\u4e86\uff0c\u4ee5\u540e\u662f\u6cf0\u5c71\u4e0d\u52a8\uff0c\u6211\u80c6\u5927\u7684\u8131\u4e0b\u4e86\u7f57\u886b\u2026\u2026\u201d \u3000\u3000\u201c\u5b9d\u59b9\u59b9\u4e4b\u7f8e\uff0c\u80a4\u5982\u51dd\u8102\uff0c\u8131\u4e0b\u7f57\u886b\uff0c\u5f53\u5982\u70ed\u7130\u7099\u4eba\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u674e\u4e16\u6c11\u53d7\u5f97\u4e86 \u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u7528\u5343\u6761\u8ba1\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u6709\u7b28\u4e3b\u610f\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u770b\u4e86\uff01\u4efb\u4f60\u6625\u5149\u65e0\u9650\u597d\uff0c\u96be \u52a8\u4e16\u6c11\u4e00\u5bf8\u5fc3\uff1b\u5012\u662f\u6211\uff0c\u8981\u7684\u81ea\u5df1\u6625\u60c5\u8361\u6f3e\uff0c\u6709\u4e9b\u96be\u4ee5\u81ea\u7981\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u96be\u4e3a\u4f60\u5440\uff01\u5b9d\u59b9\u59b9\uff0e\u5929\u4e0b\u771f\u6709\u8fd9\u6837\u7684\u6728\u5934\u4eba\uff0c\u4eba\u95f4\u7edd\u8272\u5e03\u4e0b\u7684\u8272\u60c5\u4e4b\u7f51\uff0c \u4e5f\u65e0\u6cd5\u4f7f\u4ed6\u52a8\u5fc3\uff0c\u8fd9\u4e2a\u4eba\u53ef\u771f\u6709\u4e9b\u975e\u540c\u5bfb\u5e38\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u662f\u4e00\u671d\u88ab\u86c7\u54ac\uff0c\u5341\u5e74\u6015\u8349\u7ef3\u3002\u4ed6\u53ea\u8981\u4e00\u89c1\u5230\u6211\uff0c\u5c31\u6025\u6025\u95ed\u4e0a\u773c\u775b\uff0c\u5c0f\u59b9 \u82b1\u679d\u62db\u5c55\u3001\u9526\u8863\u591c\u884c\uff0c\u4ed6\u4e00\u70b9\u4e5f\u770b\u4e0d\u5230\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u6447\u6447\u5934\uff0c\u9053\uff1b\u201c\u51a4\u5b7d\u5440\uff01\u90a3\u4e00\u628a\u63a8\u5f00\u4e86\u4f60\u4eec\u4e00\u4e16\u60c5\u7f18\uff01\u201d \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u98ce\u4e91\u6f5c\u9f99 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u4e03 \u56de\u3000\u6df1\u5bab\u5bc6\u8bed \u3000\u3000\u8881\u5b9d\u513f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u679c\u6211\u7231\u4ed6\u6d45\u4e00\u4e9b\uff0c\u5982\u679c\u4ed6\u5fc3\u4e2d\u4e0d\u592a\u656c\u91cd\u6211\uff0c\u522b\u628a\u6211\u770b\u6210\u5929\u4e0a \u7684\u4ed9\u5973\uff0c\u5c31\u4e0d\u4f1a\u6709\u8fd9\u6837\u7684\u4e0b\u573a\u4e86\u2026\u2026\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u4e00\u987f\uff0c\u6539\u4e86\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u7d2b\u70df\u59d0\uff0c\u4f60\u662f\u771f\u6b63\u7684\u4ed9\u5973\uff0c\u674e\u4e16\u6c11\u600e\u4e48\u770b\u5f85 \u4f60\uff1f\u201d \u3000\u3000\u201c\u5509\u5440\uff01\u626f\u5230\u54ea\u53bb\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u8fde\u4f60\u8fd9\u6295\u6000\u9001\u62b1\u7684\u4ed9\u5973\uff0c\u78b0\u90fd\u4e0d\u6562\u78b0\u4e00\u4e0b\uff0c\u8fd8 \u6562\u52a8\u6211\u8fd9\u51b7\u82e5\u51b0\u971c\u7684\u4eba\u5417\uff1f\u4ed6\u662f\u541b\u5b50\uff0c\u6211\u662f\u6dd1\u5973\uff1b\u4e00\u76f4\u5230\u4ed6\u88ab\u4e09\u85cf\u548c\u5c1a\u6551\u8d70\uff0c\u6211\u4eec\u597d\u50cf\u6ca1\u6709 \u8c08\u8fc7\u4e00\u53e5\u95f2\u8bdd\uff0c\u674e\u4e16\u6c11\u7684\u5fc3\u4e2d\u5168\u88ab\u5b9d\u513f\u59b9\u59b9\u5145\u6ee1\u4e86\uff0c\u54ea\u91cc\u8fd8\u4f1a\u5bb9\u5f97\u4e0b\u522b\u7684\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u81ea\u53e4\u591a\u60c5\u7a7a\u4f59\u6068\uff0c\u53e4\u4eba\u662f\u8bda\u4e0d\u6b3a\u6211\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u4eec\u90fd\u4e3a\u5bf9\u65b9\u60f3\u5f97\u592a\u591a\u4e86\uff0c \u53cd\u800c\u628a\u81ea\u5df2\u6324\u5f97\u65e0\u5730\u81ea\u5bb9\u4e86\u3002\u4e91\u4e2d\u5c71\u662f\u8bad\u7ec3\u94c1\u7532\u5175\u7684\u5730\u65b9\uff0c\u4e0d\u8bb8\u5973\u4eba\u6d89\u5165\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u80fd\u5bb9\u4e0b\u4f60\u8fd9\u6837\u7684\u7f8e\u4eba\uff0c\u522b\u4eba\u5374\u4e0d\u80fd\u53bb\uff0c\u674e\u4e16\u6c11\u81ea\u79c1\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u6709\u4e00\u70b9\u4e0d\u540c\uff0c\u8981\u8d1f\u8d23\u8bad\u7ec3\u94c1\u7532\u5175\u4e24\u79cd\u7279\u522b\u7684\u6280\u827a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u6280\u827a\uff1f\u80fd\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c\u8ba9\u6211\u4e00\u5e7f\u89c1\u95fb\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9762\u6709\u96be\u8272\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u6709\u6240\u609f\uff0c\u7b11\u9053\uff1a\u201c\u519b\u4e8b\u673a\u5bc6\uff0c\u5c82\u53ef\u5916\u6cc4\uff01\u4e0d\u80fd\u8bf4\u5c31\u4e0d\u8981\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e0d\u7ba1\u4ec0\u4e48\u6280\u827a\uff1f\u6446\u5230\u7d2b\u70df\u59d0\u7684\u9762\u524d\uff0c\u90fd\u662f\u96d5\u866b\u5c0f\u6280\u3002\u201d \u3000\u3000\u201c\u8bf4\u4f60\u7684\u674e\u4e16\u6c11\u5427\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ee5\u540e\u53c8\u600e\u4e48\u6837\u5462\uff1f\u8fd9\u5f88\u91cd\u8981\uff0c\u6211\u548c\u4ed6\u65e5\u540e\u6050\u96be\u514d\u51b2 \u7a81\u4e8e\u6218\u9635\u4e4b\u4e0a\uff0c\u5e0c\u671b\u5b9e\u8bdd\u5b9e\u8bdd\uff0c\u52a0\u6cb9\u6dfb\u918b\uff0c\u9690\u533f\u4e8b\u5b9e\uff0c\u6050\u5f71\u54cd\u4e86\u6211\u7684\u5224\u65ad\u3002\u4e0d\u662f\u8bef\u4ed6\u5c31\u662f\u8bef \u6211\uff0c\u5c31\u4e0d\u77e5\u8fd9\u4f1a\u9020\u6210\u4ec0\u4e48\u6837\u7684\u60e8\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6c5f\u5c71\u65e0\u9650\u597d\uff0c\u9c9c\u8840\u67d3\u5f81\u888d\u3002\u81ea\u4e09\u4ee3\u4ee5\u4e0b\uff0c\u5c31\u518d\u65e0\u7985\u8ba9\u7684\u4e8b\u3002\u674e \u4e16\u6c11\u8981\u4e89\u6c5f\u5c71\uff0c\u5b9e\u73b0\u4ed6\u4e00\u5f00\u592a\u5e73\u76db\u4e16\u7684\u5fc3\u613f\uff0c\u4e5f\u8981\u7ecf\u4e00\u573a\u8840\u6d41\u6210\u6cb3\u7684\u5927\u6218\uff0c\u624d\u80fd\u6a2a\u626b\u5343\u519b\u5b9a \u4e00\u5c0a\uff0c\u5e7f\u7eb3\u8d24\u58eb\u7267\u4e07\u6c11\uff1b\u6211\u7231\u4ed6\u7684\u4eba\uff0c\u4e5f\u559c\u6b22\u4ed6\u6cbb\u56fd\u7684\u8ba1\u5212\u3002 \u3000\u3000\u6211\u5df2\u51b3\u5fc3\u5c3d\u6211\u6240\u6709\u5e2e\u52a9\u4ed6\uff0c\u6211\u5f88\u60f3\u81ea\u8350\u6795\u5e2d\uff0c\u8ba9\u4ed6\u7684\u751f\u6d3b\u4e2d\u5145\u6ee1\u7740\u5feb\u4e50\uff0c\u4f7f\u4ed6\u80fd\u5168\u5fc3\u5168 \u610f\u7684\u6295\u5165\u56fd\u4e8b\u3002 \u3000\u3000\u4f46\u6211\u6015\u4ed6\u62d2\u7edd\uff0c\u6015\u4ed6\u8f7b\u89c6\u6211\u662f\u4e00\u4e2a\u6c34\u6027\u6768\u82b1\u7684\u5973\u4eba\u3002\u6240\u4ee5\uff0c\u6211\u5fcd\u4e0b\u4e86\u3002\u8fd9\u4e00\u5fcd\uff0c\u5c31\u767d\u767d \u5730\u4fbf\u5b9c\u4e86\u7687\u4e0a\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u548c\u674e\u4e16\u6c11\u7684\u5144\u5f1f\u4f55\u5173\u5462\uff1f\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u674e\u4e16\u6c11\u603b\u4e0d\u4f1a\u5fc3\u91cc\u5bb3\u6015\uff0c\u628a\u6211\u7684\u5b9d\u59b9 \u59b9\u8ba9\u51fa\u53bb\u5427\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u4f1a\uff0c\u771f\u8981\u5982\u6b64\uff0c\u6069\u65ad\u4e49\u7edd\uff0c\u5404\u884c\u5176\u662f\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5982\u6b64\u7684\u81ea\u82e6\u4e86\uff01\u574f \u5728\u4ed6\u592a\u591a\u60c5\u554a\uff01\u201d \u3000\u3000\u62ed\u4e00\u4e0b\u53cc\u76ee\u8574\u542b\u7684\u6cea\u6c34\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4e00\u5929\u5efa\u6210\u548c\u5143\u5409\u53cc\u53cc\u8d76\u5230\u4e91\u4e2d\u5c71\uff0c\u4ee3\u8868\u674e\u4faf\u7237\u6170\u95ee \u4e16\u6c11\uff0c\u5e26\u6765\u7eb9\u94f6\u4e00\u4e07\u4e24\uff0c\u7292\u8d4f\u4e09\u5343\u94c1\u7532\u3002 \u3000\u3000\u4e16\u6c11\u5728\u5c71\u4e2d\u7ec3\u5175\u672a\u5f52\uff0c\u6211\u5c31\u81ea\u4f5c\u806a\u660e\u5e2e\u4e16\u6c11\u63a5\u5f85\u4e24\u4eba\uff0c\u4ed6\u4eec\u662f\u674e\u4e16\u6c11\u7684\u8840\u7f18\u5144\u5f1f\uff0c\u5374\u4e0d \u6599\u662f\u4e24\u4e2a\u8272\u80c6\u5305\u5929\u7684\u4eba\u3002\u4e00\u89c1\u9762\uff0c\u56db\u53ea\u773c\u775b\u5c31\u5728\u6211\u8eab\u4e0a\u6253\u91cf\uff0c\u90a3\u79cd\u8086\u65e0\u5fcc\u60ee\u7684\u8272\u72fc\u773c\u5149\uff0c\u5168 \u76ef\u5728\u4e0d\u8be5\u770b\u7684\u5730\u65b9\u770b\uff0c\u770b\u5f97\u6211\u4e00\u8154\u6012\u706b\uff0c\u6068\u4e0d\u5f97\u51fa\u624b\u6740\u4eba\uff0c\u4f46\u6211\u54ac\u7740\u7259\u5ffd\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4e5f\u4e0d\u80fd\u5168\u602a\u522b\u4eba\u5450\uff01\u4f60\u8fd9\u80a1\u82b1\u679d\u4eba\u6837\uff0c\u4e0d\u7779\u5b9d\u513f\u4e4b\u7f8e\u8005\uff0c\u65e0\u76ee\u4e5f\u3002 \u80fd\u602a\u4eba\u5bb6\u76ef\u7740\u770b\u5417\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u770b\u7684\u5730\u65b9\u4e0d\u5bf9\u5440\uff01\u770b\u5f97\u4e0b\u6d41\u554a\uff01\u53ef\u4e0d\uff0c\u5b57\u6587\u6210\u90fd\u770b\u4f60\uff0c\u770b\u5f97\u5168\u795e\u8d2f \u6ce8\uff0c\u770b\u5f97\u65e0\u9650\u51c4\u8ff7\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8c6a\u95e8\u4e16\u5bb6\uff0c\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u5403\u559d\u73a9\u4e50\uff0c\u653e\u9010\u60c5\u6b32\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u63a5\u9053\uff1a\u201c\u6211\u7b80\u76f4\u6c14\u50bb\u4e86\uff0c\u771f\u60f3\u628a\u4ed6\u4eec\u540a\u5728\u7a7a\u4e2d\u6bd2\u6253\u4e00\u987f\uff0c\u4f46\u6015\u95f9\u5f97\u674e\u4e16\u6c11\u4e0b\u4e0d\u4e86 \u53f0\uff0c\u6240\u4ee5\u6211\u5fcd\u4e0b\u53bb\u4e86\u3002\u8f6c\u8eab\u56de\u623f\uff0c\u4e24\u4e2a\u4e0d\u8bc6\u8da3\u7684\u4eba\u7adf\u7136\u8ffd\u4e0a\u6211\u7684\u7ee3\u623f\u4e2d\uff0c\u662f\u53ef\u5fcd\u5b70\u4e0d\u53ef\u5fcd\uff0c \u6211\u706b\u5927\u4e86\uff0c\u6697\u4f7f\u672f\u6cd5\uff0c\u628a\u4e24\u4eba\u6444\u5165\u4e86\u4e00\u4e2a\u732a\u5708\u4e2d\uff0c\u6eda\u4e86\u4e00\u8eab\u81ed\u6ce5\u3002 \u3000\u3000\u4ed6\u4eec\u6323\u624e\u56de\u6765\uff0c\u4e16\u6c11\u4e5f\u7ec3\u5175\u5f52\u6765\uff0c\u770b\u4ed6\u4eec\u4e00\u8eab\u732a\u7caa\u81ed\u6ce5\uff0c\u62db\u547c\u4ed6\u4eec\u6d17\u6fa1\u66f4\u8863\uff0c\u4e24\u4e2a\u4eba\u5fc3 \u91cc\u6709\u6570\uff0c\u4ea4\u4ee3\u4e86\u4faf\u7237\u81f4\u6170\u4e4b\u610f\uff0c\u653e\u4e0b\u9ec4\u91d1\uff0c\u4e00\u9910\u996d\u4e5f\u6ca1\u5403\uff0c\u5c31\u8fd4\u56de\u592a\u539f\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u628a\u5efa\u6210\u3001\u5143\u5409\u7684\u6076\u52a3\u884c\u4e3a\u544a\u8bc9\u4e86\u674e\u4e16\u6c11\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u4f55\u89e3\u8bf4\u8fd9\u4e00\u8eab\u732a \u7caa\u6c61\u6ce5\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u9a6c\u60ca\u5931\u8e44\uff0c\u8dcc\u5165\u4e86\u732a\u5708\u3002\u6211\u6ca1\u6709\u544a\u8bc9\u674e\u4e16\u6c11\u4e8b\u4ef6\u7ecf\u8fc7\uff0c\u6211\u6015\u4ed6\u5fc3\u4e2d \u96be\u8fc7\uff0c\u4e00\u5ff5\u4ec1\u6148\uff0c\u5bb3\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u5efa\u6210\u3001\u5143\u5409\u56de\u5230\u4e86\u592a\u539f\u4e4b\u540e\uff0c\u5f00\u59cb\u8ba1\u5212\u9677\u5bb3\u6211\u3002\u9002\u5de7\u7687\u4e0a\u6709\u4e00\u9053\u8868\u7ae0\uff0c\u4f20\u5230\u592a\u539f\uff0c\u8d23\u9063 \u674e\u4faf\u7237\uff0c\u6211\u4e0d\u8fc7\u662f\u8bf4\u4ed6\u51e0\u53e5\uff0c\u8981\u4ed6\u5fe0\u8bda\u8c0b\u56fd\uff0c\u76d1\u89c6\u897f\u5317\u4e00\u5e26\uff0c\u5982\u6709\u53db\u9006\u4e4b\u4e8b\uff0c\u8981\u53ca\u65e9\u4e0a\u8868\u594f \u660e\u3002 \u3000\u3000\u5efa\u6210\u3001\u5143\u5409\u6293\u5230\u4e86\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5c31\u6307\u8bf4\u7687\u4e0a\u5df2\u5bf9\u592a\u539f\u52a8\u7591\uff0c\u4e16\u6c11\u7ec3\u5175\u4e91\u4e2d\u5c71\u53c8\u662f\u4e8b\u5b9e\uff0c\u4e00 \u65e6\u7687\u4e0a\u76f4\u6d3e\u4eba\u67e5\u660e\uff0c\u52bf\u5fc5\u8c03\u52a8\u5927\u519b\u56f4\u653b\u5927\u539f\u3002\u7687\u4e0a\u597d\u8272\u5982\u547d\uff0c\u5982\u679c\u80fd\u9009\u4e00\u4e24\u4f4d\u7f8e\u5973\uff0c\u664b\u732e\u957f \u5b89\uff0c\u4ee5\u5b89\u7687\u4e0a\u4e4b\u5fc3\uff0c\u592a\u539f\u5c31\u53ef\u4fdd\u5e73\u5b89\u65e0\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u63a5\u9053\uff1a\u201c\u674e\u6e0a\u542c\u4fe1\u4e86\u4ed6\u4eec\u7684\u8bdd\uff0c\u628a\u4f60\u9009\u9001\u5165\u957f\u5b89\u57ce\uff0c\u674e\u4e16\u6c11\u5c31\u4e0d\u51fa\u9762\u5417\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e16\u6c11\u8dea\u5728\u674e\u4faf\u7237\u5367\u5ba4\u95e8\u5916\uff0c\u4e00\u5929\u4e00\u591c\uff0c\u5e0c\u671b\u5019\u7237\u6536\u56de\u6210\u547d\uff0c\u4faf\u7237\u7ea6\u89c1\u8881\u5929 \u7f61\u5fd6\u8bba\u6b64\u4e8b\uff0c\u4ed6\u7adf\u7136\u6df1\u4ee5\u4e3a\u7136\uff0c\u8054\u5408\u674e\u6df3\u98ce\uff0c\u5408\u529b\u529d\u4f4f\u4e86\u674e\u4e16\u6c11\uff0c\u5c31\u8fd9\u6837\u6211\u534a\u51fa\u52c9\u5f3a\u534a\u7531\u4eba \u7684\u5b89\u6392\u4e0b\uff0c\u8fdb\u5165\u4e86\u957f\u5b89\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u591c\u5bdf\u674e\u4e16\u6c11\uff0c\u770b\u5230\u4ed6\u4e3a\u6b64\u4e8b\u5455\u8840\u6570\u6597\uff0c\u5dee\u4e00\u70b9\u5c31 \u6d3e\u4eba\u8ffd\u4f60\u56de\u53bb\uff0c\u8c01\u77e5\u9053\u5929\u4eae\u65f6\u5206\uff0c\u674e\u4e16\u6c11\u7adf\u7136\u6d3b\u751f\u751f\u541e\u4e0b\u4e86\u76f8\u601d\u75db\u82e6\uff0c\u4f0f\u6848\u800c\u7761\u4e86\uff0c\u5c31\u8fd9\u6837 \u628a\u4f60\u4eec\u5206\u5f00\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5c82\u771f\u662f\u5929\u610f\u4e4e\uff1f\u6211\u6068\u4ed6\u5931\u53bb\u6211\uff0c\u7adf\u7136\u5168\u4e0d\u5728\u610f\uff0c\u539f\u6765\u4e5f\u66fe\u7ecf\u4f24\u5fc3\u5455\u8840\uff0c\u4e5f \u771f\u662f\u82e6\u4e86\u4ed6\u3002\u6211\u6700\u6068\u7684\u662f\u672a\u628a\u6e05\u81ea\u8eab\u8eaf\u4ea4\u7ed9\u4ed6\uff0c\u6b64\u751f\u4e4b\u4e2d\u7559\u4e0b\u4e86\u4e00\u7247\u7a7a\u767d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6447\u6447\u5934\u9053\uff1a\u201c\u5b9d\u513f\u3001\u6211\u6709\u4e9b\u60f3\u4e0d\u901a\u554a\uff01\u5efa\u6210\u3001\u5143\u5409\u8bbe\u8ba1\u4f60\uff0c\u65e8\u5728\u62a5\u4ec7\uff0c\u4e8c\u4eba\u611a \u6627\uff0c\u674e\u6e0a\u660f\u5eb8\uff0c\u505a\u4ef6\u7cca\u6d82\u4e8b\u4e5f\u5c31\u7f62\u4e86\u3002 \u3000\u3000\u8fde\u8881\u5929\u7f61\u7adf\u4e5f\u4f1a\u6247\u98ce\u52a9\u706b\uff0c\u8fd9\u4e2d\u95f4\u5b9a\u7136\u6709\u7279\u522b\u539f\u56e0\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u6545\u610f\u5bb3\u6211\uff0c\u4ed6\u89c2\u5bdf\u5929\u8c61\uff0c\u5f97\u5230\u4e86\u4e00\u4e2a\u5366\u8c61\uff0c\u53cd\u590d\u63a8\u6f14\uff0c\u6c42\u5f97\u4e2a\u9634\u4eba \u7978\u56fd\u7684\u7ed3\u8bba\uff0c\u6240\u4ee5\u4e3b\u5f20\u6211\u8fdb\u5165\u957f\u5b89\u2026\u2026\u201d \u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u5929\u8c61\u8fd0\u8f6c\uff0c\u53ef\u53d7\u8e0f\u7f61\u6b65\u6597\u7684\u672f\u6cd5\u5f71\u54cd\uff0c\u4f46\u4e5f\u53ea\u80fd\u4f7f\u8fd0\u884c\u52a0\u901f\u3002\u201d\u8881\u7d2b\u70df\u54fc \u58f0\u9053\uff1a\u201c\u8fd9\u79cd\u6709\u80cc\u5929\u610f\u7684\u4e8b\uff0c\u8881\u5929\u7f61\u5df2\u7ecf\u5e72\u4e86\uff0c\u9001\u4f60\u5165\u957f\u5b89\u7684\u7528\u5fc3\u5f88\u660e\u663e\uff0c\u662f\u5e0c\u671b\u4f60\u5fcd\u4e0d\u4f4f \u5fc3\u4e2d\u7684\u6012\u706b\u714e\u71ac\u65f6\uff0c\u51fa\u624b\u6740\u4e86\u7687\u4e0a\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u60f3\uff0c\u4ed6\u786e\u6709\u8fd9\u4e2a\u6253\u7b97\uff0c\u4f46\u4ed6\u7b97\u4e0d\u51fa\u4f1a\u5f15\u52a8\u7d2b\u70df\u59d0\u8fd9\u4e48\u4e2a\u5927\u4eba\u7269 \u51fa\u6765\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5c31\u662f\u59d0\u59d0\u4e0d\u6765\uff0c\u6211\u4e5f\u4e0b\u4e0b\u4e86\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u77e5\u6211\u6740\u968b\u7080\u5e1d\u6613\u5982\u53cd\u638c\uff0c\u53ea\u77e5\u4ee5\u6211\u7684\u672c\u9886\uff0c\u5bab\u536b\u6b66\u58eb\u7edd\u65e0\u6cd5\u62e6\u5f97\u4f4f\u6211\uff0c\u4f46\u4ed6\u5374\u5ffd \u7565\u4e86\u6211\u662f\u5973\u4eba\uff01\u8fd9\u4e00\u70b9\u8fde\u6211\u8fdb\u5165\u957f\u5b89\u65f6\u4e5f\u672a\u60f3\u5230\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\u4f7f\u4f60\u6709\u4e86\u5982\u6b64\u91cd\u5927\u7684\u6539\u53d8\uff1f\u4e8b\u5b9e\u4e0a\uff0c\u6211\u5c31\u662f\u7559\u5728\u5bab\u4e2d\u4e5f\u672a\u5fc5\u80fd\u963b \u6b62\u4f60\u6740\u5bb3\u7687\u4e0a\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u8c01\u4e5f\u65e0\u6cd5\u963b\u6b62\u6211\u6740\u5bb3\u4ed6\uff0c\u673a\u4f1a\u592a\u591a\u4e86\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u591c\u591c\u6625\u5bb5\uff0c\u8eab\u5b50\u65e9\u5df2\u88ab \u638f\u7a7a\u4e86\uff0c\u5df2\u7ecf\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u4e0d\u77e5\u6700\u8fd1\u4ed6\u670d\u7528\u4e86\u4ec0\u4e48\u836f\u7269\uff0c\u7adf\u7136\u53c8\u751f\u9f99\u6d3b\u864e\u4e86\u3002\u53ea\u8981\u770b\u4ed6\u89c1\u5230 \u4f60\u65f6\u3002\u90a3\u526f\u6025\u8272\u513f\u7684\u6837\u5b50\uff0c\u5c31\u77e5\u9053\u4ed6\u5728\u60f3\u4e9b\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u544a\u8bc9\u6211\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6740\u4e86\u7687\u4e0a\uff1f\u6211\u4eec\u59d0\u59b9\u4e00\u573a\uff0c\u5e0c\u671b\u4e0d\u8981\u95f9\u5230\u53cd\u76ee\u6210 \u4ec7\u3001\u5175\u5203\u76f8\u89c1\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u5012\u4e0d\u662f\u6015\u59d0\u59d0\u4e3a\u4ed6\u62a5\u4ec7\u6740\u4e86\u6211\u3002\u56e0\u4e3a\u4ed6\u662f\u7b2c\u4e00\u4e2a\u5360\u4e86\u6211\u8eab\u5b50\u7684\u7537 \u4eba\uff0c\u867d\u7136\u5fc3\u4e2d\u672c\u65e0\u60c5\uff0c\u4f46\u4e5f\u4e0b\u4e0d\u4e86\u624b\u3002 \u3000\u3000\u968b\u7080\u5e1d\u62c8\u82b1\u6210\u4e60\uff0c\u540e\u5bab\u7f8e\u5973\u65e0\u6570\uff0c\u4f46\u5bf9\u6211\u5374\u6781\u5c3d\u5c55\u60dc\u7231\u3002\u7d2b\u70df\u59d0\uff0c\u8981\u6740\u4ed6\u65e9\u5c31\u6740\u4e86\uff0c\u90a3 \u8fd8\u4f1a\u7b49\u5230\u4f60\u6df7\u5165\u5bab\u4e2d\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u5f88\u60f6\u60d1\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5e38\u5728\u60f3\uff0c\u4e00\u65e6\u4f60\u6740\u4e86\u7687\u4e0a\uff0c\u6211\u8981\u600e\u4e48\u529e\uff1f\u8ffd\u4f60\u5230\u5929\u6daf\u6d77 \u89d2\uff0c\u653e\u624b\u4e00\u640f\uff0c\u8fd8\u662f\u5750\u89c6\u4e0d\u7ba1\uff1f\u8427\u7687\u540e\u7ed3\u8bc6\u6211\uff0c\u6700\u5927\u7684\u7528\u5fc3\u5c31\u662f\u8981\u6211\u76d1\u89c6\u4f60\uff0c\u4e0d\u51c6\u4f60\u52a0\u5bb3\u7687 \u4e0a\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u592a\u4f4e\u4f30\u7687\u4e0a\u4e86\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u5165\u5bab\u4e0d\u5230\u4e00\u4e2a\u6708\uff0c\u7687\u4e0a\u5df2\u53d1\u73b0\u6211\u8eab\u5177\u672f\u6cd5\u3001 \u6b66\u529f\uff0c\u6709\u4e00\u6b21\u9189\u5367\u743c\u82b1\u9662\uff0c\u7b11\u7740\u5bf9\u6211\u8bf4\u5200\u8fc7\u4eba\u5934\u843d\uff0c\u53ef\u522b\u624b\u8f6f\u3002\u4ed6\u4e0d\u6015\u6b7b\uff0c\u5374\u975e\u5e38\u6015\u75bc\u3002\u8bf4 \u5b8c\u8bdd\uff0e\u8131\u5149\u4e86\u6211\u7684\u8863\u670d\uff0c\u7d27\u7d27\u5730\u62b1\u4f4f\u6211\uff0c\u4f3c\u68a6\u5453\u53c8\u5982\u503e\u8bc9\u3002 \u3000\u3000\u544a\u8bc9\u6211\uff0c\u6211\u7ed9\u4ed6\u7684\u5feb\u4e50\u826f\u591a\uff0c\u523a\u6740\u4e86\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u602a\u6211\u3002\u9632\u60a3\u672a\u7136\uff0c\u5c31\u5e94\u8be5\u5148\u6740\u6211\uff0c\u4f46 \u4ed6\u4e0d\u5fcd\u5fc3\uff0c\u4e5f\u4e0b\u4e0d\u4e86\u624b\u3002\u6740\u4e86\u6211\uff0c\u4ed6\u4f1a\u56e0\u76f8\u601d\u800c\u4ea1\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fd9\u9053\uff1a\u201c\u8fd9\u4e0d\u50cf\u4e00\u4e2a\u559d\u9189\u9152\u8bf4\u7684\u6d3b\u5440\uff01\u96be\u9053\u7687\u4e0a\u65e9\u7aa5\u77e5\u4e86\u4f60\u5165\u5bab\u7684\u7528\u5fc3\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6ca1\u6709\u559d\u9189\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u53ea\u662f\u501f\u9152\u88c5\u75af\u554a\uff01\u73b0\u4e00\u73b0\u4ed6\u7684\u806a\u660e\u624d\u667a\uff0c\u674e\u4faf\u7237\u7684\u8bbe \u8ba1\u3001\u8881\u5929\u7f61\u7684\u7b97\u76d8\u65e9\u5df2\u88ab\u7687\u4e0a\u770b\u7a7f\u4e86\uff0c\u5b57\u6587\u6210\u90fd\u6000\u7591\u6211\uff0c\u8427\u7687\u540e\u9632\u8303\u6211\uff0c\u5374\u5df2\u843d\u5728\u7687\u4e0a\u4e4b\u540e \u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u2026\u2026\u4ee5\u8fd9\u822c\u76f8\u6301\u4e0b\u53bb\uff0c\u7ec8\u975e\u4e86\u5c40\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u674e\u4e16\u6c11\u7275\u4f4f\u4e86\u4f60\u7684\u5fc3\uff0c\u603b\u6709\u4e00 \u5929\u4f60\u4f1a\u88ab\u8feb\u4e0b\u624b\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u6b21\u5317\u5f81\u5f52\u6765\uff0c\u4e00\u5b9a\u4f1a\u5f15\u8d77\u5c40\u52bf\u53d8\u5316\uff0c\u6240\u4ee5\u6211\u4eec\u8981\u6765\u4e00\u4e2a\u541b\u5b50 \u534f\u5b9a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ec0\u4e48\u534f\u5b9a\u5462\uff1f\u53ea\u8981\u516c\u5e73\u3001\u5408\u7406\uff0c\u6211\u5927\u6982\u4e0d\u4f1a\u62d2\u7edd\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u6740\u5bb3\u968b\u7080\u5e1d\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4f60\u8981\u4e09\u653e\u674e\u4e16\u6c11\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u65e5\u540e\u59d0\u59b9\u4eec\u7eb5\u7136\u88ab\u5f62 \u52bf\u903c\u5230\u975e\u6253\u4e00\u67b6\u4e0d\u53ef\uff0c\u5fc3\u4e2d\u4e5f\u65e0\u79c1\u4eba\u4ec7\u6068\u3002 \u3000\u3000\u6b64\u756a\u5317\u5f81\uff0c\u4f60\u5fc3\u5b58\u5fe0\u539a\uff0c\u5df2\u653e\u8fc7\u4e16\u6c11\u4e00\u6b21\uff0c\u53ea\u8981\u518d\u6709\u4e24\u6b21\u653e\u8fc7\u4ed6\u7684\u627f\u8bfa\uff0c\u6211\u4eec\u8fd9\u4e00\u6bb5\u59d0 \u59b9\u60c5\u5c31\u5341\u5206\u5706\u6ee1\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u5f88\u4e0d\u5e78\u7684\uff0c\u8ba9\u6211\u6349\u5230\u4ed6\u7b2c\u56db\u6b21\uff0c\u53c8\u8be5\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u987f\u4e86\u987f\uff0e\u53c8\u9053\uff1a\u201c\u8fd9\u4e2a\u534f\u5b9a\uff0c\u4f60\u4e0d\u5403\u4e8f\uff1b\u8eab\u5728\u957f\u5b89\uff0c\u4f60\u80fd\u591f\u770b\u6e05\u695a\u5929\u4e0b\u5f62\u52bf\u3002\u6740 \u4e86\u7687\u4e0a\uff0c\u4e8e\u4e8b\u65e0\u8865\uff0c\u957f\u5b89\u5148\u4e71\u8d77\u6765\uff0c\u5bf9\u674e\u4e16\u6c11\u672a\u5fc5\u6709\u5229\u3002\u866c\u9aef\u5ba2\u5341\u4e07\u4f0f\u5175\uff0c\u4e00\u65e6\u633a\u8eab\u800c\u51fa\uff0c \u4e00\u5915\u4e4b\u95f4\u5c31\u53ef\u4ee5\u5360\u636e\u957f\u5b89\u7a83\u53d6\u5e1d\u4f4d\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u4e5f\u6ca1\u6709\u4e0a\u5f53\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6740\u4e86\u674e\u4e16\u6c11\uff0c\u6740\u4f10\u5f88\u53ef\u80fd\u5ef6\u7eed\u767e\u5e74\uff0c\u4f60\u5982\u4fdd\u4e0d\u4f4f \u968b\u7080\u5e1d\uff0c\u6050\u4e5f\u65e0\u6cd5\u5728\u4eba\u95f4\u7acb\u8db3\u3002\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\uff0c\u5c0f\u59b9\u7b79\u601d\u518d\u4e09\uff0c\u8fd8\u662f\u51b3\u5b9a\u5766\u7136\u76f8\u544a\u3002\u524d\u4e00 \u5e74\uff0c\u6211\u5982\u6709\u8fd9\u4e2a\u89c1\u8bc6\u601d\u60f3\uff0c\u4e5f\u4e0d\u4f1a\u4efb\u4eba\u6446\u5e03\u5165\u540e\u5bab\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u4e00\u756a\u4eae\u8bed\u660e\u8a00\uff0c\u628a\u54b1\u4eec\u59d0\u59b9\u4e8c\u4eba\u7f6e\u4e8e\u4e86\u654c\u3001\u53cb\u4e4b\u95f4\u3002\u6211\u540c\u610f\u8fd9\u4e2a\u534f\u5b9a\uff0c \u4f46\u613f\u674e\u4e16\u6c11\u80fd\u9003\u8fc7\u7b2c\u56db\u6b21\u7684\u52ab\u96be\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5982\u679c\u4f60\u518d\u653e\u4ed6\u4e24\u6b21\uff0c\u4ed6\u8fd8\u662f\u843d\u4f60\u624b\u4e2d\uff0c\u6740\u5250\u4efb\u4f60\uff0c\u5c0f\u59b9\u7edd\u65e0\u6028\u8a00\u3002\u6b63\u5982\u7687 \u4e0a\u548c\u4f60\u3001\u6211\u4e4b\u95f4\u7684\u5fae\u5999\u60c5\u7f18\u3002\u7d2b\u70df\u59d0\u5e38\u88ab\u4ed6\u52c9\u5f3a\u4e0a\u5e8a\uff0c\u4f60\u4e0d\u60c5\u4e0d\u613f\uff0c\u4f46\u5fc3\u4e2d\u5374\u4ee5\u4ed6\u5b89\u5371\u4e3a \u5ff5\uff0c\u914d\u7075\u836f\u5e2e\u52a9\u4ed6\u7eb5\u6a2a\u6b32\u6d77\u3002\u5c0f\u59b9\u5fc3\u6000\u4ed6\u5fd7\uff0c\u5374\u6e29\u5a49\u7684\u4efb\u4ed6\u4e88\u53d6\u4e88\u6c42\uff0c\u540c\u5e8a\u5f02\u68a6\uff0c\u53c8\u9c7c\u6c34\u5c3d \u6b22\u2026\u2026\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u8f66\u5df2\u505c\u5bab\u95e8\u4e4b\u5916\u3002 \u3000\u3000\u968b\u7080\u5e1d\u65e9\u5df2\u5728\u5bab\u95e8\u4e4b\u5185\u7b49\u5019\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u5954\u5411\u4e86\u743c\u82b1\u9662\u3002 \u3000\u3000\u5de7\u513f\u3001\u83b2\u513f\u4e5f\u65e9\u5df2\u7b49\u5019\u5728\u5bab\u95e8\u4e4b\u5185\uff0c\u51c6\u5907\u4f8d\u5019\u8881\u7d2b\u70df\u6c90\u6d74\u66f4\u8863\uff0c\u4f46\u770b\u6837\u5b50\u4f3c\u662f\u5df2\u7528\u4e0d\u7740 \u4e24\u4eba\u8d39\u5fc3\u591a\u4e8b\u4e86\u3002 \u3000\u3000\u743c\u82b1\u9662\u4e2d\u6d74\u6d17\u5c18\uff0c\u6362\u4e0a\u7f57\u8863\u518d\u4f8d\u541b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u672a\u5fc5\u662f\u5fc3\u7518\u60c5\u613f\uff0c\u4f46\u4e5f\u65e0\u6cd5\u62d2\u4e4b\u4e8e\u5343\u91cc\u4e4b\u5916\uff0c\u60f3\u4e00\u60f3\u771f\u6709\u4e9b\u540e\u6094\uff0c\u4e3a\u4ec0\u4e48\u63d0\u4f9b\u4ed6 \u4e00\u4e2a\u57f9\u5143\u56fa\u672c\u7684\u836f\u65b9\u5b50\uff1f \u3000\u3000\u743c\u82b1\u9662\u6625\u5149\u65e0\u9650\u597d\uff01 \u3000\u3000\u968b\u7080\u5e1d\u8ffd\u9010\u6b22\u5a31\u7684\u7cbe\u795e\uff0c\u4e00\u5411\u52c7\u6562\u65e0\u6bd4\u3002 \u3000\u3000\u5929\u4e0a\u4eba\u95f4\u4e24\u4e3d\u4eba\uff0c\u4e5f\u53ea\u597d\u4efb\u4ed6\u6298\u817e\u4e86\u3002 \u3000\u3000\u7edb\u5e10\u6625\u6696\uff0c\u5deb\u5c71\u4e91\u96e8\u53d8\u51e0\u56de\uff0c\u4e0d\u8bf4\u4e5f\u7f62\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u4e16\u6c11\u4e3a\u4e00\u7fa4\u50e7\u4fa3\u6240\u6551\uff0c\u4ed6\u4eec\u6613\u5bb9\u6539\u88c5\uff0c\u7528\u9ed1\u5dfe\u5305\u4f4f\u88ab\u70d9\u6212\u75a4\u7684\u8111\u888b\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6570\u6b21\u8ffd\u8be2\u4ed6\u4eec\u6765\u5386\uff0c\u90fd\u5f97\u5230\u8eab\u53d7\u4e09\u85cf\u4e4b\u6258\u7684\u56de\u7b54\uff0c\u6700\u540e\u8ffd\u95ee\u4e00\u4e2a\u5e74\u8f7b\u548c\u5c1a\uff0c\u624d\u5f97 \u5230\u6765\u81ea\u5c11\u6797\u5bfa\u7684\u56de\u5e94\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4f53\u529b\u4e0d\u7ee7\uff0c\u4ed6\u4eec\u8f6e\u6d41\u80cc\u4ed6\u8d76\u8def\u3002 \u3000\u3000\u8dd1\u5f97\u8fc5\u5feb\uff0c\u5954\u884c\u5982\u51e4\u3002 \u3000\u3000\u4e00\u8def\u5954\u884c\u4e0d\u7a0d\u505c\uff0c\u4e0d\u77e5\u9053\u5954\u884c\u591a\u8fdc\uff0c\u674e\u4e16\u6c11\u88ab\u653e\u5230\u4e00\u5904\u519c\u820d\u524d\u9762\uff0c\u62a4\u884c\u7684\u618e\u7fa4\u7a81\u7136\u6563 \u53bb\uff0c\u6765\u7684\u65e0\u58f0\uff0c\u4e5f\u53bb\u7684\u65e0\u606f\u3002 \u3000\u3000\u7f18\u5c3d\u4e8e\u6b64\u4e86\uff0c\u674e\u4e16\u6c11\u6ca1\u6709\u5f3a\u7559\uff0c\u7fa4\u50e7\u4e5f\u672a\u8bdd\u522b\u3002 \u3000\u3000\u674e\u4e16\u6c11\u611f\u89c9\u5230\u9965\u997f\u56f0\u5026\uff0c\u9f13\u8d77\u52c7\u6c14\u5411\u519c\u820d\u8ba8\u53d6\u98df\u7269\u3002 \u3000\u3000\u5f00\u95e8\u7684\u662f\u4e00\u4e2a\u5e03\u8863\u8001\u59aa\u3002 \u3000\u3000\u5c3d\u51fa\u7f8e\u5473\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e24\u7897\u5c0f\u7c73\u7a00\u996d\u3002 \u3000\u3000\u95ee\u660e\u4e86\u6c11\u95f4\u7684\u6e05\u82e6\u751f\u6d3b\uff0c\u674e\u4e16\u6c11\u4e3a\u4e4b\u6f5c\u7136\u6cea\u4e0b\u3002\u5bcc\u6c11\u88d5\u56fd\uff0c\u5f00\u521b\u5927\u5e73\u76db\u4e16\u7684\u5fc3\u613f\u4e5f\u66f4\u52a0 \u575a\u5b9a\u4e86\u3002 \u3000\u3000\u4e24\u7897\u5c0f\u7c73\u7ca5\u589e\u52a0\u4e86\u674e\u4e16\u6c11\u4e0d\u5c11\u4f53\u529b\uff0c\u51c6\u5907\u8d77\u8eab\u5317\u4e0a\u8d76\u56de\u592a\u539f\u3002 \u3000\u3000\u4f46\u95fb\u8e44\u58f0\u201c\u5f97\u5f97\u201d\uff01 \u3000\u3000\u8033\u9645\u4e14\u54cd\u8d77\u5c09\u8fdf\u656c\u5fb7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5728\u90a3\u91cc\uff0c\u8881\u5148\u751f\u8fd9\u4e00\u5366\u535c\u5f97\u597d\u51c6\u3002\u201d \u3000\u3000\u4e24\u5339\u5feb\u9a6c\u5954\u9a70\u800c\u81f3\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u8054\u8882\u800c\u6765\u3002 \u3000\u3000\u4e8c\u4eba\u6eda\u978d\u4e0b\u9a6c\uff0c\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u53d7\u82e6\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u633d\u8d77\u4e8c\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u4e8c\u5144\u5c11\u793c\uff0c\u8881\u5148\u751f\u4e5f\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u8881\u5148\u751f\u535c\u4e86\u4e00\u5366\uff0c\u8981\u6211\u4eec\u8ffd\u8bbf\u4e8c\u516c\u5b50\u7684\u884c\u8e2a\uff0c\u5357\u884c\u5341\u91cc\uff0c\u5c31\u53ef\u80fd\u9047\u4e0a\u4e8c\u516c \u5b50\uff0c\u53ef\u771f\u535c\u7684\u51c6\u4e86\uff01\u8fd9\u4e00\u7a0b\u4e5f\u4e0d\u8fc7\u662f\u5341\u91cc\u5de6\u53f3\u3002\u201d \u3000\u3000\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u592a\u51c6\u4e86\u4e5f\u4e0d\u597d\uff0c\u8881\u5148\u751f\u8bf4\u6211\u4eec\u53ef\u80fd\u9047\u4e0a\u4e00\u4f4d\u9634\u4eba\u62e6\u9053\uff0c\u8981\u6211\u4eec\u5c0f\u5fc3\u5e94 \u4ed8\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u83ab\u975e\u8981\u9047\u4e0a\u8881\u7d2b\u70df\uff0c\u90a3\u5c31\u4e0d\u597d\uff0c\u5feb\u4e9b\u4e0a\u8def\u624d\u6210\u3002\u201d \u3000\u3000\u7a0b\u77e5\u8282\u62c9\u8fc7\u5065\u9a6c\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u5148\u8bf7\u4e0a\u9a6c\uff0c\u54b1\u4eec\u53bb\u548c\u8881\u5148\u751f\u4f1a\u5408\u4e00\u5904\u3002\u201d \u3000\u3000\u4e00\u9635\u70df\u5c18\u6eda\u52a8\uff0c\u4e00\u9a91\u9a8f\u9a6c\u5982\u98de\u800c\u81f3\u3002 \u3000\u3000\u90aa\u95e8\u5f97\u5f88\u5462\uff01 \u3000\u3000\u9a6c\u4e0a\u7f57\u5e26\u98d8\u98ce\uff0c\u771f\u7684\u5750\u4e86\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u8fd9\u4e00\u5366\u51c6\u5f97\u90aa\u6c14\u5440\uff01\u771f\u7684\u627e\u5230\u4e86\u4e8c\u516c\u5b50\uff0c\u4e5f\u771f\u7684\u9047\u4e0a\u4e86\u4e00\u4e2a\u9634\u4eba\u3002\u201d \u3000\u3000\u201c\u5e78\u597d\u4e0d\u662f\u8881\u7d2b\u70df\uff01\u201d\u674e\u4e16\u6c11\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u62c5\u5fc3\uff0c\u662f\u674e\u9756\u592b\u4eba\u5f20\u51fa\u5c18\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9752\u8863\u4f69\u5251\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u5fae\u7b11\u7740\u8d70\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u8d25\u59be\u5f20\u51fa\u5c18\uff0c\u4e8c\u516c\u5b50\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u7ea2\u62c2\u5973\u9762\u5982\u6625\u82b1\u5a07\uff0c\u8eab\u5982\u73b2\u73d1\u900f\uff0c\u5973\u4eba\u7684\u7279\u8272\u5b8c\u5168\u5730\u7a81\u73b0\u51fa\u6765\uff0c\u53c8\u4e00\u4e2a\u7edd\u4e16\u7f8e\u5973\u3002 \u3000\u3000\u674e\u4e16\u6c11\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u597d\uff0c\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u89c1\u8fc7\u674e\u592b\u4eba\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6b20\u8eab\u8fd8\u793c\uff0c\u95ee\u9053\uff1a\u201c\u62d9\u592b\u674e\u9756\uff0c\u6ca1\u6709\u968f\u62a4\u516c\u5b50\u6765\u5417\uff1f\u201d \u3000\u3000\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u674e\u516c\u5b50\u5750\u9547\u76d8\u9f99\u5c45\uff0e\u6b64\u756a\u672a\u6765\u3002\u201d \u3000\u3000\u201c\u597d\u6781\u4e86\uff01\u201d\u5f20\u51fa\u5c18\u7b11\u9053\uff1a\u201c\u5929\u4ece\u4eba\u613f\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u5df2\u542c\u51fa\u82d7\u5934\u4e0d\u5bf9\u4e86\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u88ab\u5b57\u6587\u6210\u90fd\u751f\u64d2\u4e86\uff0c\u62bc\u8d74\u957f\u5b89\u65a9\u5211\u3002\u59be\u8eab\u5feb\u9a6c\u8ddf\u8e2a\uff0c\u8ffd\u4e0a\u4e86\u5b57\u6587\u6210 \u90fd\uff0c\u53cc\u65b9\u6253\u4e86\u4e00\u67b6\uff0c\u624d\u95ee\u51fa\u4f60\u88ab\u548c\u5c1a\u6551\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u52b3\u5ac2\u592b\u4eba\u7684\u7389\u8dbe\uff0c\u4e16\u6c11\u611f\u6fc0\u4e0d\u5c3d\uff0c\u89c1\u7740\u674e\u9756\u5144\uff0c\u518d\u81f4\u8c22\u610f\uff0c\u5bb6\u6bcd\u6302\u6000\uff0c\u5c31\u6b64\u522b\u8fc7 \u4e86\u3002\u201d \u3000\u3000\u7ffb\u8eab\u8dc3\u4e0a\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u6162\u884c\u4e00\u6b65\uff0c\u542c\u59be\u6570\u8a00\u5982\u4f55\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u83b2\u6b65\u59d7\u59d7\u8d70\u8fc7\u6765\uff0c\u7adf\u7136\u6321\u5728\u4e8c\u516c\u5b50\u9a6c\u5934\u524d\u9762\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u6709\u70b9\u5192\u706b\uff0c\u4f46\u770b\u5728\u674e\u9756\u7684\u4efd\u4e0a\uff0c\u5fcd\u4e0b\u4e86\uff0c\u4eba\u8fd8\u662f\u5411\u524d\u4e24\u6b65\u9760\u8fd1\u5728\u674e\u4e16\u6c11\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u201c\u5ac2\u592b\u4eba\u6709\u4f55\u6307\u6559\uff0c\u8bf7\u8bb2\u5f53\u9762\uff0c\u4e16\u6c11\u80fd\u591f\u505a\u5230\u7684\u7edd\u4e0d\u63a8\u8fdf\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u80fd\u591f\u505a\u5230\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u5c31\u884c\u3002\u201d \u3000\u3000\u843d\u4e86\u5708\u5957\u4e86\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6697\u6697\u5fd6\u9053\uff1a\u201c\u505a\u4eba\u4e0d\u80fd\u592a\u539a\u9053\uff0c\u4e0d\u80fd\u518d\u88ab\u5979\u6293\u4f4f\u8bdd\u67c4\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u4e5f\u5c0f\u5fc3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u548c\u674e\u9756\u60c5\u540c\u5144\u5f1f\uff0c\u5ac2\u592b\u4eba\u7684\u4e8b\u4e16\u6c11\u65e0\u4e0d\u9075\u4ece\u3002\u4e0d\u8fc7 \u603b\u5f97\u5148\u56de\u5927\u539f\u4e00\u8d9f\uff0c\u8bf7\u8fc7\u7236\u6bcd\u5927\u5b89\uff0c\u518d\u529e\u522b\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u6709\u4e9b\u9ebb\u70e6\u4e86\uff0c\u51fa\u5c18\u5949\u547d\u9080\u7ea6\u4e8c\u516c\u5b50\u534e\u9634\u4e00\u6664\u2026\u2026\u201d \u3000\u3000\u201c\u592a\u534e\u5c71\u4e0b\u7684\u534e\u9634\u57ce\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u5949\u547d\u7ea6\u6211\uff0c\u662f\u5949\u4f55\u4eba\u4e4b\u547d\u5462\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u60f3\u4e8c\u516c\u5b50\u65e9\u5df2\u77e5\u6653\uff0c\u8981\u59be\u8eab\u8bf4\u51fa\u6765\u4ea6\u65e0\u4e0d\u53ef\uff0c\u866c\u9aef\u5ba2\u5f20\u4ef2\u575a\uff0c\u6155\u540d\u5df2 \u4e45\uff0c\u60f3\u7ea6\u4e8c\u516c\u4e8e\u676f\u9152\u8a00\u6b22\uff0c\u5374\u53c8\u82e6\u65e0\u673a\u7f18\u3002 \u3000\u3000\u8fd9\u6b21\u98ce\u95fb\u4e8c\u516c\u5b50\u88ab\u62bc\u5f80\u957f\u5b89\uff0c\u7279\u547d\u51fa\u5c18\u4e2d\u9014\u622a\u6740\uff0c\u52a1\u5fc5\u6551\u51fa\u4e8c\u516c\u5b50\uff0c\u987a\u4fbf\u9080\u516c\u5b50\u534e\u9634\u4e00 \u6664\uff0c\u90a3\u867d\u7136\u53ea\u662f\u5ea7\u5c0f\u57ce\u5c0f\u53bf\uff0c\u4f46\u5374\u6df1\u85cf\u4e00\u6279\u9f99\u864e\u4fca\u5f66\uff0c\u671d\u4ee5\u5feb\u9a6c\u96f7\u9706\u8d77\uff0c\u4e24\u65e5\u4e00\u591c\u8fd1\u957f\u5b89\u3002 \u4e8c\u516c\u5b50\u82e5\u80fd\u548c\u866c\u9aef\u5ba2\u4f1a\u5546\u534e\u9634\uff0c\u8fd9\u5929\u4e0b\u5927\u4e8b\u5f88\u5feb\u5c31\u53ef\u4ee5\u6572\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u5ac2\u592b\u4eba\uff01\u201d\u674e\u4e16\u6c11\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f88\u611f\u6fc0\u4f60\u8fd9\u756a\u5b89\u6392\uff0c\u4e0d\u8fc7\u5148\u5bb9\u6211\u56de\u592a\u539f\u62dc\u6170\u8fc7\u7236 \u6bcd\u4e4b\u540e\uff0c\u518d\u548c\u674e\u9756\u5144\u7ed3\u4f34\u540c\u5f80\u534e\u9634\u4e00\u884c\uff0c\u4e0d\u77e5\u5ac2\u592b\u4eba\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e0d\u597d\uff01\u6b64\u5730\u8ddd\u79bb\u534e\u9634\u6bd4\u592a\u539f\u8fd8\u8fd1\u4e00\u4e9b\uff0c\u59be\u8eab\u7684\u610f\u601d\u662f\u5148\u5f80\u534e\u9634\u5546\u5927\u8ba1\uff0c\u518d \u56de\u592a\u539f\u53d9\u4eb2\u60c5\u3002\u201d \u3000\u3000\u201c\u5ac2\u592b\u4eba\u5f3a\u6211\u6240\u96be\u4e86\uff0c\u4e16\u6c11\u5f52\u5fc3\u4f3c\u7bad\u554a\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fae\u7b11\u9053\uff1a\u201c\u98ce\u96ea\u963b\u5f52\u4eba\uff0c\u4e8c\u516c\u5b50\uff01\u6bd4\u98ce\u96ea\u66f4\u53ef\u6015\u7684\u662f\u4eba\u4e86\u3002\u9f99\u864e\u4e8c\u5c06\u3001\u516b\u5927\u91d1\u521a \u7387\u9886\u4e86\u4e09\u5341\u4e8c\u540d\u9f99\u864e\u52c7\u58eb\u5df2\u5e03\u5b88\u56db\u5468\u4e86\u2026\u2026\u201d \u3000\u3000\u5c09\u8fdf\u606d\u5927\u58f0\u559d\u9053\uff1a\u201c\u5a01\u80c1\u4e8c\u516c\u5b50\uff0c\u656c\u5fb7\u5728\u6b64\uff0c\u5c82\u5bb9\u8096\u5c14\u653e\u8086\u3002\u201d \u3000\u3000\u4f38\u624b\u6458\u4e0b\u4e86\u80cc\u4e0a\u7684\u96cc\u96c4\u53cc\u97ad\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4e5f\u6458\u4e0b\u4e86\u6302\u5728\u8170\u95f4\u7684\u4e24\u5c0f\u578b\u5229\u65a7\u3002\u4ed6\u7528\u4f5c\u9a6c\u4e0a\u5bf9\u654c\u957f\u903e\u4e5d\u5c3a\u7684\u5ba3\u5316\u65a7\u6ca1\u6709\u5e26\u6765\uff0c \u53ea\u597d\u7528\u968f\u8eab\u7684\u98de\u65a7\u5bf9\u654c\u4e86\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u6447\u6447\u5934\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff01\u4e0d\u8981\u767d\u767d\u7684\u727a\u7272\u4e86\u4e24\u5458\u864e\u5c06\uff0c\u4e00\u5bf9\u4e00\u4e5f\u8bb8\u4ed6 \u4eec\u53ef\u4ee5\u548c\u9f99\u864e\u4e8c\u5c06\u6218\u4e00\u4e2a\u5e73\u5206\u79cb\u8272\uff0c\u4f46\u52a0\u4e0a\u516b\u5927\u91d1\u521a\uff0c\u4e09\u5341\u4e8c\u52c7\u58eb\uff0c\u8fd9\u4e2a\u4ed7\u5c31\u65e0\u6cd5\u6253\u4e86\u3002\u52bf \u4e0d\u5747\uff0c\u529b\u96be\u654c\u5440\uff01\u4e8c\u516c\u5b50\uff0c\u8bf7\u76f8\u4fe1\u51fa\u5c18\uff0c\u534e\u9634\u4e4b\u884c\uff0c\u6211\u4fdd\u4f60\u5e73\u5b89\u65e0\u4e8b\uff0c\u5c31\u7b97\u4e24\u4f4d\u8bdd\u4e0d\u6295\u673a\uff0c \u6211\u62c5\u4fdd\u9001\u4f60\u56de\u592a\u539f\u3002\u201d \u3000\u3000\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u674e\u592b\u4eba\uff0c\u8fd9\u662f\u903c\u8feb\u4e8c\u516c\u5b50\u8ba2\u57ce\u4e0b\u4e4b\u76df\u4e86\u3002\u656c\u5fb7\u613f\u5148\u6218\u6b7b\uff0c\u6211\u6709\u4e09\u5bf8\u6c14\u5728\uff0c \u7edd\u4e0d\u8ba9\u4e8c\u516c\u5b50\u53d7\u5230\u5a01\u80c1\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6b7b\u5f97\u4e0d\u503c\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u7a0b\u67d0\u4eba\uff0c\u5b81\u613f\u8840\u6e85\u4e94\u6b65\uff0c\u4e0d\u80fd\u8ba9\u4e8c\u516c\u5b50\u53d7\u5230\u59d4\u5c48\u3002\u674e\u592b\u4eba\u5982\u4e0d\u5ff5\u6211\u7b49\u548c\u674e\u9756\u5144\u5f1f \u7684\u60c5\u610f\uff0c\u5c31\u62db\u547c\u4ed6\u4eec\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e24\u4f4d\u4e0d\u8ba1\u5148\u6b7b\u4fdd\u62a4\u4e8c\u516c\u5b50\uff0c\u7528\u5fc3\u53ef\u5609\uff1b\u4f46\u4e24\u4f4d\u53ef\u80fd\u7f6e\u4e8c\u516c\u5b50\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff1f\u201d\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u656c\u5fb7\u6709\u4e09\u5bf8\u6c14\u5728\uff0c\u5c82\u80fd\u8ba9\u4eba\u4f24\u5bb3\u4e8c\u516c\u5b50\uff1f\u201d \u3000\u3000\u201c\u8eab\u96be\u7531\u5df1\u5440\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7f13\u7f13\u62d4\u51fa\u4e86\u80cc\u4e0a\u7684\u957f\u5251\uff0c\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u4eba\u5251\u5408\u4e00\uff0c\u5347\u8d77\u4e86\u4e09\u4e08\u591a\u9ad8\uff0c\u5728\u7a7a\u4e2d\u6253 \u4e86\u4e00\u4e2a\u8f6c\uff0c\u7a81\u7136\u5316\u4f5c\u4e00\u56e2\u6570\u4e08\u65b9\u5706\u7684\u5251\u7f51\uff0c\u7f69\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u97ad\u6025\u4e3e\uff0c\u821e\u51fa\u4e00\u7247\u62a4\u8eab\u97ad\u5f71\uff0c\u4e0d\u987e\u81ea\u5df1\uff0c\u5374\u62a4\u536b\u674e\u4e16\u6c11\u7684\u5934\u9876\u3002 \u3000\u3000\u4e24\u5c06\u519b\u611f\u5230\u5251\u8292\u5e26\u4e00\u80a1\u5bd2\u98ce\uff0c\u7ed5\u8eab\u800c\u8fc7\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5251\u5df2\u5165\u9798\uff0c\u7ad9\u7acb\u539f\u5904\u3002 \u3000\u3000\u5c09\u8fdf\u606d\u4e5f\u6536\u4f4f\u53cc\u97ad\u3002 \u3000\u3000\u7a0b\u77e5\u8282\u4ecd\u7136\u9ad8\u4e3e\u7740\u4e24\u67c4\u5c0f\u65a7\uff0c\u62a4\u5728\u5934\u9876\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u53d7\u5230\u4f24\u5bb3\uff0c\u8fde\u8eab\u4fa7\u5065\u9a6c\u4e5f\u5206\u6beb\u672a\u635f\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6b63\u8272\u9053\uff1a\u201c\u674e\u592b\u4eba\u624b\u4e0b\u7559\u60c5\uff0c\u65ad\u53d1\u88c2\u5e26\uff0c\u5374\u672a\u4f24\u635f\u5230\u4e8c\u5144\u7684\u808c\u80a4\u3002\u7167\u6211\u7684\u5429\u5490\u529e \u5427\uff01\u56de\u53bb\u548c\u8881\u5929\u7f61\u5546\u91cf\u4e00\u4e0b\uff0c\u5c31\u8bf4\u6211\u548c\u674e\u592b\u4eba\u53bb\u89c1\u866c\u9aef\u5ba2\u4e86\u3002\u201d \u3000\u3000\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u5927\u4e08\u592b\u6b7b\u800c\u4f55\u60e7\uff1f\u5bb9\u77e5\u8282\u548c\u656c\u5fb7\u5144\u6218\u6b7b\u4e4b\u540e\uff0c\u4e8c\u516c\u5b50\u518d\u53bb\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u5fe0\u70c8\uff0c\u5b9e\u662f\u53ef\u656c\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u6b7b\u554a\uff01\u4e5f\u8bb8\u4e24\u4f4d\u5c06\u519b\u53ef\u4ee5 \u548c\u9f99\u3001\u864e\u4e8c\u5c06\u6218\u4e00\u4e2a\u5e73\u5206\u79cb\u8272\u3002\u4f46\u4e09\u5341\u4e8c\u4f4d\u9f99\u3001\u864e\u52c7\u58eb\u51fa\u624b\u56f4\u653b\uff0e\u80af\u5b9a\u975e\u4e24\u4e2a\u5c06\u519b\u80fd\u654c\u3002\u201d \u3000\u3000\u201c\u674e\u592b\u4eba\u5251\u4e0b\u7559\u60c5\uff0c\u4f46\u8fd8\u4e0d\u5982\u6740\u4e86\u656c\u5fb7\u3002\u8981\u6211\u773c\u770b\u7740\u4e8c\u516c\u5b50\u88ab\u4eba\u5e26\u8d70\uff0c\u90a3\u5c31\u751f\u4e0d\u5982\u6b7b \u4e86\uff01\u201d \u3000\u3000\u201c\u5c06\u519b\uff01\u201d\u5f20\u51fa\u5c18\u4f4e\u58f0\u9053\uff1a\u201c\u59be\u8eab\u4ee5\u6027\u547d\u62c5\u4fdd\uff0c\u4e8c\u516c\u5b50\u7edd\u4e0d\u4f1a\u53d7\u5230\u635f\u4f24\uff0c\u6b64\u65f6\u6b64\u60c5\uff0c\u901e \u5f3a\u4e0d\u5f97\u5440\uff01\u201d \u3000\u3000\u201c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e24\u4f4d\u56de\u53bb\u5427\uff01\u518d\u4e09\u8fdd\u547d\u5c31\u6d41\u5165\u521a\u610e\u81ea\u7528\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5f88\u91cd\uff0c\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u4e0d\u6562\u518d\u4e89\uff0c\u957f\u63d6\u62dc\u522b\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\u679c\u7136\u5fb7\u5a01\u670d\u4eba\uff0c\u62d9\u592b\u5ff5\u5ff5\u4e0d\u5fd8\u4f60\uff0c\u5fcd\u629b\u5a07\u59bb\u8f7b\u522b\u79bb\u662f\u975e\u65e0\u56fa\u4e86\u3002\u201d\u5f20\u51fa\u5c18\u8d5e\u7f8e \u9053\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u548c\u674e\u9756\u5144\uff0c\u4e24\u5fc3\u76f8\u77e5\u554a\uff01\u4f46\u613f\u592b\u4eba\u4e5f\u80fd\u8d74\u592a\u539f\u5c0f\u4f4f\u2026\u2026\u201d \u3000\u3000\u201c\u603b\u662f\u8981\u53bb\u7684\u3002\u201d\u5f20\u51fa\u5c18\u63a5\u9053\uff1a\u201c\u4e5f\u8bb8\u4e8c\u516c\u5b50\u8fd9\u756a\u534e\u9634\u4e4b\u884c\uff0c\u4e5f\u4f1a\u628a\u62d9\u592b\u5f15\u6765\u534e \u9634\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u6b65\u5c65\u58f0\u52a8\uff0c\u9f99\u5c06\u6c5f\u68ee\u3001\u864e\u5c06\u71d5\u53ef\u5e26\u7740\u4e09\u5341\u4e8c\u540d\u9f99\u864e\u52c7\u58eb\uff0c\u5982\u98de\u800c\u81f3\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u4e86\u9f99\u3001\u864e\u4e8c\u5c06\u4e00\u773c\u3002 \u3000\u3000\u968f\u53c8\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u5df2\u5141\u51c6\u534e\u9634\u4e4b\u884c\uff0c\u8bf8\u4f4d\u8981\u606d\u656c\u5949\u4f8d\uff0c\u4e0d\u53ef\u5931\u793c\u3002\u201d \u3000\u3000\u6c5f\u68ee\u3001\u71d5\u53ef\u53cc\u53cc\u62b1\u62f3\u4f5c\u793c\uff0c\u9053\uff1a\u201c\u4e45\u95fb\u4e8c\u516c\u5b50\u4ec1\u4e49\u5927\u540d\uff0c\u4eca\u65e5\u6709\u5e78\u4e00\u4f1a\u3002\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff01\u201d\u674e\u4e16\u6c11\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9f99\u3001\u864e\u4e8c\u5c06\u5927\u540d\uff0c\u5982\u96f7\u8d2f\u8033\uff0c\u4eca\u65e5\u6709\u5e78\u7ed3\u8bc6\uff0c\u8db3 \u6170\u6e34\u6155\u3002\u201d \u3000\u3000\u6c5f\u68ee\u4eb2\u7275\u5750\u9a6c\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u5148\u8bf7\u4e0a\u9a6c\u3002\u201d \u3000\u3000\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u89c1\u5230\u4e86\u8881\u5929\u7f61\u65f6\uff0c\u80f8\u4e2d\u6012\u706b\u672a\u6d88\u3002 \u3000\u3000\u51b7\u7740\u4e24\u5f20\u8138\uff0c\u8bf7\u6c42\u5904\u5206\u3002 \u3000\u3000\u8881\u5929\u7f61\u95ee\u660e\u4e86\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5c06\u519b\uff0c\u6682\u606f\u6012\u706b\uff0c\u54b1\u4eec\u5148\u56de\u592a\u539f\uff0c\u518d\u4f5c \u8ba1\u8f83\u3002\u201d \u3000\u3000\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u8881\u5148\u751f\uff0c\u6551\u4eba\u5982\u6551\u706b\uff0c\u5148\u56de\u592a\u539f\u518d\u8ba1\u8f83\uff0c\u5c82\u4e0d\u5751\u5bb3\u4e86\u4e8c\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u201c\u89e3\u94c3\u8fd8\u9700\u7cfb\u94c3\u4eba\u3002\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u674e\u9756\u592b\u4eba\u628a\u4e8c\u516c\u5b50\u903c\u5230\u534e\u9634\uff0c\u5c31\u8be5\u7531\u674e\u9756\u903c\u5979\u5c06\u4e8c \u516c\u5b50\u4ea4\u56de\u5927\u539f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5c09\u8fdf\u606d\u9053\uff1a\u201c\u5148\u751f\u8981\u4ed6\u4eec\u592b\u59bb\u53cd\u76ee\u5417\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u674e\u9756\u4f5c\u4e3b\u5f20\u4e86\uff0c\u4ed6\u4eec\u592b\u59bb\u4e24\u5730\u65b9\u5206\u79bb\uff0c\u53ea\u6015\u6709\u4e00\u5e74\u591a\u6ca1\u89c1\u8fc7\u9762 \u4e86\uff0c\u8fd9\u756a\u53d8\u5316\u4e5f\u8bb8\u4f1a\u7ed9\u4ed6\u4eec\u592b\u59bb\u4e00\u4e2a\u805a\u6664\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u5148\u751f\uff0c\u4f60\u5f88\u8f7b\u677e\u554a\uff01\u201d\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u6551\u4e8c\u516c\u5b50\u91cd\u8981\u5462\uff1f\u8fd8\u662f\u674e\u9756\u592b\u5987\u805a\u6664\u91cd\u8981\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u4f3c\u662f\u6709\u5f88\u591a\u8bdd\u8981\u8bf4\uff0c\u4f46\u76b1\u76b1\u7709\u5934\u5fcd\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5728\u534e\u9634\u5c6f\u4f4f\u4e86\u6570\u4e07\u7532\u5175\uff0c\u6211\u4eec\u51e0\u4e2a\u4eba\u529b\u91cf\u5927\u5355\u8584\u4e86\uff0c\u8981\u6551\u4e8c\u516c \u5b50\u5b9c\u7528\u667a\u53d6\uff0c\u4e0d\u80fd\u529b\u6597\u3002\u201d \u3000\u3000\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u5fc3\u4e2d\u867d\u6709\u4e9b\u4e0d\u4ee5\u4e3a\u7136\uff0c\u4f46\u5374\u60f3\u4e0d\u51fa\u53cd\u9a73\u7684\u7406\u7531\u3002 \u3000\u3000\u8881\u5929\u7f61\u6709\u6240\u4fdd\u7559\uff0c\u4ed6\u4e0d\u80fd\u6cc4\u6f0f\u592a\u591a\u5929\u673a\u3002 \u3000\u3000\u7cbe\u5947\u7684\u672f\u6cd5\u4e5f\u8bb8\u80fd\u8865\u6551\u4e00\u4e9b\u4e8b\u52a1\u7f3a\u5931\uff0c\u4f46\u662f\u9006\u5929\u800c\u884c\uff0c\u8fdd\u80cc\u81ea\u7136\u6cd5\u5219\uff0c\u4f1a\u62db\u81f3\u66f4\u5927\u7684\u707e \u96be\u3002 \u3000\u3000\u867d\u7136\u5c09\u8fdf\u606d\u3001\u7a0b\u77e5\u8282\u5fc3\u4e2d\u6709\u7740\u4e0d\u6ee1\uff0c\u4e5f\u53ea\u6709\u7a0d\u4f5c\u793a\u610f\uff0c\u5a49\u8f6c\u76f8\u529d\uff0c\u56de\u8f6c\u5927\u539f\u3002 \u3000\u3000\u674e\u9756\u542c\u5b8c\u4e86\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u7acb\u523b\u5f15\u8d77\u4e86\u4e00\u8154\u6012\u706b\uff01 \u3000\u3000\u5229\u7528\u5f20\u51fa\u5c18\u628a\u674e\u4e16\u6c11\u7275\u5165\u534e\u9634\uff0c\u5176\u5fc3\u53ef\u8bdb\uff0c\u866c\u9aef\u5ba2\u7528\u610f\u4f55\u5728\u5462\uff1f\u662f\u5426\u60f3\u903c\u4ed6\u4eec\u592b\u59bb\u53cd \u76ee\uff1f \u3000\u3000\u674e\u9756\u4e0d\u81ea\u7981\u6cb9\u7136\u751f\u4e86\u5e2e\u52a9\u674e\u4e16\u6c11\u9010\u9e7f\u4e2d\u539f\u7684\u610f\u5ff5\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u80f8\u6000\u535a\u5927\uff0c\u719f\u8bfb\u5175\u6cd5\uff0c\u5bf9\u559c\u6012\u60c5\u7eea\u7684\u63a7\u5236\u9887\u6709\u529f\u592b\u3002 \u3000\u3000\u4ed6\u6291\u5236\u4e0b\u4e00\u8154\u6012\u610f\uff0c\u6de1\u6de1\u5730\u9053\uff1a\u201c\u51fa\u5c18\u5982\u6b64\u653e\u8086\uff0c\u4e0d\u77e5\u662f\u5426\u56e0\u53d7\u60c5\u52bf\u903c\u8feb\uff1f\u4e0d\u7ba1\u5982\u4f55\uff0c \u8fd9\u4ef6\u4e8b\u90fd\u548c\u6211\u674e\u9756\u6709\u5173\uff0c\u6211\u5f97\u53bb\u4e00\u8d9f\u534e\u9634\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u674e\u9756\uff0c\u4f60\u548c\u866c\u9aef\u5ba2\u5f20\u4ef2\u575a\u662f\u4e49\u7ed3\u91d1\u5170\u7684\u5144\u5f1f\uff0c\u5e94\u4ee5\u548c\u4e3a\u8d35\uff0c\u80fd\u5fcd\u5c31\u5fcd\u4e0b\u53bb \u554a\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\u5fcd\u5450\uff1f\u201d\u5c09\u8fdf\u606d\u5927\u58f0\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u6709\u4e00\u4e2a\u4e09\u957f\u4e24\u77ed\uff0c\u6211\u4eec\u805a\u7ed3\u7684\u610f\u4e49\u5c31\u5168\u7136\u4e0d\u5b58 \u5728\u4e86\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\u4f46\u8bf7\u653e\u5fc3\uff0c\u8fd9\u4ef6\u4e8b\u4e09\u5143\u674e\u9756\u5fc5\u6709\u4e00\u4e2a\u4ea4\u4ee3\uff0c\u4e8c\u516c\u5b50\u5982\u5230\u4efb\u4f55\u4f24\u5bb3\uff0c\u6211\u674e\u9756\u5fc5\u5c06\u52a0 \u500d\u5949\u8fd8\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u201d\u5c09\u8fdf\u606d\u5fc3\u4e2d\u6709\u4e9b\u6025\u4e86\uff0c\u9053\uff1a\u201c\u656c\u5fb7\u2026\u2026\u656c\u5fb7\u610f\u601d\u662f\u8bf4\u2026\u2026 \u6211\u4eec\u5e94\u8be5\u5c3d\u8d77\u7cbe\u9510\uff0c\u8d76\u5f80\u534e\u9634\uff0c\u62fc\u4e00\u4e2a\u8840\u6e85\u4e94\u6b65\u4e5f\u8981\u5148\u628a\u4e8c\u516c\u5b50\u6551\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u6df3\u98ce\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u4e2a\u4eba\u7684\u8840\u6e85\u4e94\u6b65\uff0c\u53ef\u80fd\u662f\u53cc\u65b9\u7389\u77f3\u4ff1\u711a\u3001\u540c\u5f52\u4e8e\u5c3d\uff0c\u90a3\u5c31\u767d\u767d\u7684\u4fbf \u5b9c\u4e86\u968b\u7080\u5e1d\u554a\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u5fcd\u5fcd\u5fcd\uff0c\u70e7\u70e7\u70e7\uff0c\u70e7\u5b57\u5934\u4e0a\u4e09\u628a\u706b\uff0c\u5fcd\u5b57\u6000\u63e3\u4e00\u628a\u5200\u3002\u674e \u9756\uff0c\u522b\u8ba9\u4e00\u9635\u6012\u6c14\u51b2\u660f\u4e86\u8111\u888b\u3002\u201d \u3000\u3000\u674e\u9756\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u591a\u627f\u6307\u6559\uff01\u8d70\u4e00\u8d9f\u534e\u9634\uff0c\u52bf\u5df2\u96be\u514d\uff0c\u6162\u4e0d\u5982\u5feb\uff0c\u6211\u8fd9\u5c31 \u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e3e\u6b65\uff0c\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u201c\u656c\u5fb7\u4e0e\u541b\u540c\u884c\u3002\u201d \u3000\u3000\u674e\u9756\u6ca1\u6709\u56de\u7b54\uff0c\u5df2\u7ecf\u8d70\u8fdc\u4e86\u3002 \u3000\u3000\u201c\u8881\u5148\u751f\uff01\u201d\u4e00\u76f4\u5750\u5728\u65c1\u8fb9\u6ca1\u6709\u5f00\u53e3\u7684\u79e6\u743c\u7a81\u7136\u9053\uff1a\u201c\u53d4\u5b9d\u7684\u4f24\u52bf\u5df2\u6108\uff0c\u613f\u548c\u656c\u5fb7\u7ed3\u4f34 \u5148\u884c\uff0c\u63a5\u5e94\u674e\u9756\u3002\u201d\u2019 \u3000\u3000\u201c\u5927\u5bb6\u4e00\u8d77\u53bb\u5566\uff01\u201d\u7a0b\u77e5\u8282\u9053\uff1a\u201c\u6700\u597d\u628a\u4e91\u4e2d\u5c71\u7684\u4e09\u5343\u94c1\u7532\u5175\u4e5f\u70b9\u9f50\u51fa\u52a8\uff0c\u534e\u9634\u4e00\u6218\u6210 \u529f\uff0c\u5c31\u8f6c\u5f80\u957f\u5b89\uff0c\u4e00\u9f13\u4f5c\u6c14\uff0c\u6210\u5c31\u5927\u4e1a\uff0c\u628a\u4e8c\u516c\u5b50\u62e5\u4e0a\u7687\u5e1d\u7684\u5ea7\u4f4d\u3002\u201d \u3000\u3000\u201c\u674e\u4faf\u7237\u5462\uff1f\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u5c31\u7b97\u8981\u5750\u7687\u5e1d\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4e8c\u516c\u5b50\u554a\uff01\u201d \u3000\u3000\u7a0b\u77e5\u8282\u6c89\u541f\u4e0d\u8bed\u4e86\u3002 \u3000\u3000\u600e\u4e48\u8bf4\u5462\uff1f\u4eba\u4f26\u5927\u9053\uff0c\u5c82\u53ef\u80e1\u8a00\u4e71\u8bed\uff1f \u3000\u3000\u674e\u6df3\u98ce\u63d2\u8a00\u9053\uff1a\u201c\u4e09\u5343\u94c1\u7532\u5175\u8fd8\u672a\u8bad\u7ec3\u5230\u529f\u5fb7\u5706\u6ee1\uff0c\u4e0d\u5b9c\u51fa\u52a8\uff0c\u957f\u5b89\u57ce\u4e5f\u4e0d\u80fd\u8f7b\u6613\u4fb5 \u72af\uff1b\u8bf8\u4f4d\u522b\u5fd8\u4e86\uff0c\u957f\u5b89\u57ce\u4e2d\u8fd8\u6709\u4e00\u4f4d\u8881\u7d2b\u70df\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8981\u5750\u4ee5\u5f85\u6bd9\uff1f\u201d\u67f4\u7ecd\u9053\uff1a\u201c\u6211\u4eec\u6309\u5175\u4e0d\u52a8\uff0c\u4efb\u4ee4\u866c\u9aef\u5ba2\u631f\u6301\u4e16\u6c11\uff1f\u201d \u3000\u3000\u201c\u662f\u5750\u4ee5\u5f85\u673a\u5566\uff01\u201d\u674e\u6df3\u98ce\u63a5\u9053\uff1a\u201c\u8881\u7d2b\u70df\u4e0d\u4f1a\u8ba9\u866c\u9aef\u5ba2\u5f97\u507f\u5fc3\u613f\uff0c\u8bf8\u4f4d\u5c06\u519b\u653e\u5f00\u80f8 \u6000\uff0c\u660e\u5929\u968f\u6211\u5230\u4e91\u4e2d\u5c71\u53bb\uff0c\u4e0d\u80fd\u5ef6\u8bef\u4e86\u4e09\u5343\u94c1\u7532\u5175\u7684\u8bad\u7ec3\u5de5\u4f5c\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u6211\u5230\u534e\u9634\u8d70\u4e00\u8d9f\uff0c\u987a\u4fbf\u67e5\u770b\u4e00\u4e0b\u866c\u9aef\u5ba2\u7684\u771f\u6b63\u5b9e\u529b\u3002\u201d \u3000\u3000\u4e24\u4f4d\u4e16\u5916\u9ad8\u4eba\uff0c\u51e0\u53e5\u4ea4\u8c08\uff0c\u5c31\u4f5c\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u4e00\u4e2a\u56de\u4e91\u4e2d\u5c71\u5750\u9547\u7ec3\u5175\uff0c\u4e00\u4e2a\u5230\u534e\u9634\u67e5\u770b\u6545\u60c5\uff0c\u6ca1\u6709\u5bd2\u7740\u8138\uff0c\u9881\u4e86\u547d\u4ee4\uff0c\u4f46\u79e6\u743c\u3001\u656c\u5fb7 \u7b49\u4e00\u822c\u864e\u5c06\uff0c\u9ed8\u7136\u4ece\u547d\u3002 \u3000\u3000\u674e\u4e16\u6c11\u53d7\u5230\u5341\u5206\u7684\u4f18\u5f85\uff0c\u88ab\u63a5\u5230\u534e\u5c71\u4e00\u5ea7\u7edd\u5cf0\u4e0a\u6d17\u5c18\u63a5\u98ce\u3002 \u3000\u3000\u90a3\u4e0d\u662f\u534e\u5c71\u7684\u6700\u9ad8\u4e3b\u5cf0\uff0c\u4f46\u5374\u9669\u5cfb\u5929\u6210\u3002 \u3000\u3000\u56db\u5468\u58c1\u7acb\u5982\u524a\uff0c\u90fd\u662f\u60ac\u5d16\uff0c\u53ea\u6709\u4e00\u6761\u8def\u80fd\u901a\u4e0a\u4e0b\uff0c\u5cf0\u9876\u6709\u6570\u5341\u4e08\u65b9\u5706\u7684\u4e00\u7247\u5e73\u5730\uff0c\u5c31\u5728 \u90a3\u5e73\u5730\u4e0a\u5efa\u7b51\u4e86\u4e00\u5ea7\u5bbd\u655e\u3001\u8c6a\u534e\u7684\u5b85\u9662\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7784\u4e00\u773c\u5927\u95e8\u4e0a\u7684\u91d1\u5b57\u6a2a\u533e\uff0c\u5199\u7684\u662f\u201c\u534e\u5c71\u884c\u5bab\u201d\u3002 \u3000\u3000\u65f6\u5df2\u5165\u591c\uff0c\u5927\u5385\u4e0a\u70db\u5149\u8f89\u714c\u3002 \u3000\u3000\u5341\u4e8c\u4f4d\u5e74\u8f7b\u7f8e\u5973\u7a7f\u7740\u77f3\u69b4\u957f\u88d9\uff0c\u5929\u84dd\u8884\uff0c\u526a\u88c1\u7684\u7a84\u8170\u88f9\u8eab\uff0c\u51f8\u73b0\u51fa\u5973\u4eba\u8702\u8170\u9686\u80f8\u7684\u7f8e \u611f\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u4eb2\u81ea\u4e3b\u6301\u5bb4\u4f1a\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6253\u91cf\u4e86\u8fd9\u4f4d\u8c6a\u52c7\u76d6\u4e16\u3001\u5a01\u540d\u8d6b\u8d6b\u7684\u4eba\u7269\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u8eab\u9ad8\u4e5d\u5c3a\u4ee5\u4e0a\uff0c\u864e\u80cc\u718a\u8170\uff0c\u866c\u9aef\u7ed5\u988a\uff1b\u5934\u5927\u5982\u6597\uff0c\u76ee\u4f3c\u94dc\u94c3\uff0c\u53cc\u76ee\u5f00\u9616\u4e4b\u95f4\u95ea\u52a8 \u51fa\u903c\u4eba\u7684\u51b7\u8292\u3002 \u3000\u3000\u770b\u4e00\u773c\uff0c\u5c31\u7ed9\u4eba\u4e00\u79cd\u529b\u62d4\u5c71\u516e\u6c14\u76d6\u4e16\u7684\u5a01\u80c1\u611f\u89c9\u3002 \u3000\u3000\u966a\u4f8d\u5728\u5bb4\u5e2d\u4e0a\u7684\u53ea\u6709\u56db\u4e2a\u4eba\u2014\u2014 \u3000\u3000\u51b7\u8273\u7edd\u7f8e\u7684\u5f20\u51fa\u5c18\u5916\uff0c\u8fd8\u6709\u4e2a\u8eab\u7740\u9053\u888d\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u5176\u4eba\u9762\u767d\u5982\u7389\uff0c\u4e2d\u7b49\u8eab\u6750\u3002\u6700\u7279\u6b8a\u7684\u662f\u5de6\u624b\u98df\u6307\u3001\u5c0f\u6307\u4e0a\u7559\u4e86\u4e8c\u5bf8\u4ee5\u4e0a\u7684\u957f\u6307\u7532\uff0c\u628a\u4e00 \u4e2a\u597d\u597d\u7684\u4eba\u6258\u886c\u5f97\u6709\u70b9\u795e\u79d8\u4e86\u3002 \u3000\u3000\u53e6\u5916\u4e00\u4e2a\u5168\u8eab\u7ec6\u8863\u3001\u9762\u5982\u67af\u6728\u7684\u4eba\u3002 \u3000\u3000\u4f3c\u662f\u6b7b\u4e86\u51e0\u5341\u5e74\u540e\uff0c\u53c8\u8fd8\u9b42\u91cd\u751f\u4e00\u822c\uff0c\u5750\u7740\u4e0d\u8bf4\u8bdd\uff0c\u5c31\u50cf\u662f\u4e00\u6839\u53e4\u6728\u6869\u5b50\uff0c\u600e\u4e48\u770b\u4e5f\u627e \u4e0d\u51fa\u4e00\u70b9\u751f\u4eba\u6c14\u3002 \u3000\u3000\u4f46\u5bb4\u5e2d\u4e0a\u7684\u9152\u80b4\u5374\u662f\u5c71\u73cd\u6d77\u5473\uff0c\u9187\u7f8e\u7cbe\u81f4\uff0c\u662f\u674e\u4e16\u6c11\u7684\u592a\u539f\u4faf\u5e9c\u4e2d\u4e5f\u96be\u89c1\u5230\u7684\u73cd\u8d35\u4e4b \u7269\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u4e5f\u4e0d\u4ecb\u7ecd\u5e2d\u4e2d\u4eba\u7269\uff0c\u662f\u4e00\u526f\u76ee\u65e0\u4f59\u5b50\u7684\u67b6\u52bf\u3002\u4ed6\u4e3e\u8d77\u9152\u676f\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e16\u6c11 \u8001\u5f1f\u5728\u592a\u539f\u62db\u5175\u4e70\u9a6c\uff0c\u805a\u96c6\u4e86\u4e0d\u5c11\u7684\u6c5f\u6e56\u8c6a\u6770\uff0c\u5982\u80fd\u548c\u4ef2\u575a\u5408\u800c\u4e3a\u4e00\uff0c\u914d\u5408\u51fa\u5175\uff0c\u968b\u738b\u671d\u7684 \u4e00\u7247\u6c5f\u5c71\u4e0d\u96be\u5c3d\u5165\u6211\u4eec\u7684\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5165\u4e86\u638c\u63e1\u600e\u4e48\u529e\uff1f \u3000\u3000\u866c\u9aef\u5ba2\u6ca1\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e5f\u4e0d\u591a\u95ee\uff0c\u4e3e\u8d77\u9152\u676f\uff0c\u9053\uff1a\u201c\u501f\u82b1\u732e\u4f5b\uff0c\u656c\u8bf8\u4f4d\u4e00\u676f\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u3001\u9053\u88c5\u4e2d\u5e74\uff0c\u8fde\u90a3\u4e0d\u5e26\u4e00\u70b9\u751f\u6c14\u7684\u7f01\u8863\u4eba\u4e5f\u4e3e\u8d77\u4e86\u9152\u676f\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7ed9\u4eba\u4e00\u79cd\u4e0d\u80fd\u62d2\u7edd\u7684\u5a01\u4eea\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u559d\u4e86\uff0c\u800c\u4e14\u662f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u653e\u4e0b\u9152\u676f\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u5bf9\u5728\u4e0b\u521a\u624d\u7684\u63d0\u8bae\u5982\u4f55\u51b3\u5b9a\uff0c\u8fd8\u671b\u7ed9\u5f20\u67d0\u4e00\u4e2a\u7b54 \u590d\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u4e3e\u676f\u6c89\u541f\uff0c\u4f3c\u662f\u5f88\u96be\u60f3\u51fa\u4e00\u4e9b\u9002\u5f53\u7684\u63aa\u8bcd\u56de\u7b54\u3002 \u3000\u3000\u201c\u5927\u54e5\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u65e2\u662f\u53cc\u65b9\u5408\u4f5c\uff0c\u5c31\u5e94\u8be5\u9884\u4f5c\u7ea6\u5b9a\uff0c\u5982\u662f\u5206\u5730\u800c\u6cbb\uff0c\u4e5f\u8981\u5212\u5206\u4e00 \u4e2a\u7586\u754c\u51fa\u6765\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5982\u9ec4\u83ba\u51fa\u9e23\uff0c\u5a49\u8f6c\u5165\u8033\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5929\u65e0\u4e8c\u65e5\uff0c\u56fd\u65e0\u4e8c\u738b\uff0c\u5206\u7586\u800c\u6cbb\uff0c\u5c82\u4e0d\u57cb\u4e0b\u4e86\u5e72\u6208\u4e0d\u606f\u4e4b\u4e89\uff0c \u4e8c\u516c\u5b50\u806a\u660e\u7edd\u4e16\uff0c\u5fc5\u6709\u826f\u7b56\u6559\u6211\u3002 \u3000\u3000\u6b64\u5730\u9ad8\u51fa\u4e91\u8868\u3001\u5b64\u7edd\u9057\u4e16\uff0c\u6b63\u597d\u7ed9\u4e8c\u516c\u5b50\u4e00\u4e2a\u6df1\u601d\u957f\u8651\u7684\u673a\u4f1a\u3002\u4e49\u59b9\uff0c\u6211\u4eec\u8d70\u5427\uff01\u4e0d\u8981 \u60ca\u6270\u4e86\u4e8c\u516c\u5b50\u7684\u601d\u7eea\u554a\uff01\u201d \u3000\u3000\u201c\u5927\u54e5\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u548c\u674e\u9756\u60c5\u540c\u5144\u5f1f\uff0c\u6211\u7275\u4ed6\u5165\u534e\u9634\uff0c\u81f3\u5c11\u4e5f\u8be5\u591a\u966a\u4ed6\u4e00 \u523b\uff0c\u5927\u54e5\u5148\u8d70\u5427\uff01\u5c0f\u59b9\u968f\u540e\u5c31\u5230\u3002\u201d \u3000\u3000\u4f46\u866c\u9aef\u5ba2\u4f3c\u662f\u660e\u767d\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u4ee3\u4e3a\u5144\u529d\u529d\u4e8c\u516c\u5b50\uff0c\u6c5f\u5c71\u867d\u597d\uff0c\u4f46\u8981\u6709\u8db3\u591f \u7684\u5b9e\u529b\u624d\u80fd\u7edf\u6cbb\uff0c\u51fa\u5c06\u5165\u76f8\uff0c\u4e5f\u662f\u4e00\u4eba\u4e4b\u4e0b\u3001\u4e07\u4eba\u4e4b\u4e0a\u5927\u5b98\uff0c\u5c0f\u5144\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u63a8\u676f\u800c\u8d77\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u90a3\u8eab\u7740\u9053\u888d\u7684\u4e2d\u5e74\u4eba\u7d27\u968f\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u5f62\u5982\u67af\u6728\u7684\u7f01\u8863\u4eba\u5374\u9759\u5750\u4e0d\u52a8\u3002 \u3000\u3000\u201c\u6728\u8001\uff0c\u8bf7\u56de\u907f\u4e00\u4e0b\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u8981\u5411\u4e8c\u516c\u5b50\u63a2\u95ee\u674e\u9756\u7684\u4e8b\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u7f13\u7f13\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u5f20\u59d1\u5a18\uff0c\u6211\u56de\u907f\u534a\u4e2a\u65f6\u8fb0\uff0c\u591f\u4e24\u4f4d\u8c08\u4e86\u5427\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e00\u5b57\u4e00\u53e5\uff0c\u6bcf\u4e2a\u5b57\u4f3c\u90fd\u7528\u5f88\u5927\u529b\u91cf\u5f39\u51fa\u6765\u7684\uff0c\u597d\u50cf\u751a\u662f\u8f9b\u82e6\u3002 \u3000\u3000\u4ed6\u4e0d\u79f0\u674e\u592b\u4eba\uff0c\u800c\u53eb\u5f20\u59d1\u5a18\uff0c\u4e5f\u8ba9\u674e\u4e16\u6c11\u6709\u4e9b\u4e0d\u89e3\uff1f\u96be\u9053\u8fd9\u91cc\u7684\u4eba\u90fd\u4e0d\u613f\u627f\u8ba4\u674e\u9756\u548c\u5f20 \u51fa\u5c18\u7684\u5a5a\u59fb\u5173\u7cfb\u3002 \u3000\u3000\u201c\u591f\u4e86\uff0c\u591a\u8c22\u6728\u8001\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fae\u7b11\u70b9\u5934\uff0c\u8868\u73b0\u51fa\u5341\u5206\u6e29\u67d4\u3002 \u3000\u3000\u7f01\u8863\u4eba\u7ad9\u8d77\u8eab\u5b50\u6b65\u51fa\u5927\u5385\uff0c\u6325\u6325\u624b\uff0c\u5385\u4e2d\u7684\u6b66\u58eb\u3001\u7f8e\u5973\u5168\u90fd\u9000\u4e0b\u3002\u6ca1\u518d\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u8868 \u60c5\uff0c\u8d70\u8def\u4e5f\u8d70\u5f97\u50f5\u786c\uff0c\u597d\u50cf\u5168\u8eab\u5173\u8282\u4e0d\u6613\u5f2f\u66f2\u4e00\u822c\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u88ab\u56da\u7981\u4e86\u3002\u201d\u5f20\u51fa\u5c18\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u53eb\u4e00\u79c0\u5cf0\uff0c\u8fdc\u79bb\u534e\u5c71\u4e3b\u5cf0\uff0c\u5e73\u5e38\u5f88\u5c11 \u4eba\u8ff9\u3002\u5f20\u4ef2\u575a\u770b\u4e0a\u4e86\u5b83\u7684\u5e7d\u9759\uff0c\u5728\u8fd9\u91cc\u7b51\u5efa\u4e86\u4e00\u5ea7\u884c\u5bab\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u65e0\u60ca\u65e0\u60e7\uff0c\u795e\u60c5\u5e73\u548c\u5730\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56fa\u65e9\u5728\u9884\u6599\u4e4b\u4e2d\u4e5f\u3002\u201d \u3000\u3000\u201c\u6211\u5f88\u62b1\u6b49\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u4e8c\u516c\u5b50\uff0c\u4e5f\u89c9\u5f97\u6709\u8d1f\u674e\u9756\uff0c\u5f20\u4ef2\u575a\u8981\u6211\u8bf7\u4f60\u6765\uff0c\u5546 \u91cf\u519b\u56fd\u5927\u4e8b\uff0c\u5374\u4e0d\u6599\u662f\u8bf7\u541b\u4eba\u74ee\uff0c\u4ed6\u4e00\u5411\u5149\u660e\u6b63\u5927\uff0c\u4e0d\u559c\u5978\u8ba1\uff0c\u8fd9\u6b21\u7adf\u7136\u628a\u6211\u4e5f\u800d\u4e86\uff01\u201d \u3000\u3000\u201c\u4e16\u6c11\u8363\u5e78\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u771f\u7684\u62b1\u6b49\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u7adf\u7136\u4e00\u53e5\u4e5f\u4e0d\u8d23\u5907\u59be\u8eab\uff0c\u6211\u5fc3\u4e2d\u66f4\u96be\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u866c\u9aef\u5ba2\u5bf9\u4e16\u6c11\u52a8\u7528\u5fc3\u673a\uff0c\u8db3\u8bc1\u5bf9\u4e16\u6c11\u5f88\u662f\u91cd\u89c6\uff0c\u5929\u4e0b\u82f1\u96c4\u552f\u4e16\u6c11\u662f\u5176\u654c\u624b\uff0c\u5ac2\u592b\u4eba\u4e0d \u7528\u4e3a\u6b64\u803f\u803f\u4e8e\u6000\uff0c\u9700\u77e5\u5982\u65e0\u5ac2\u592b\u4eba\u8fd9\u91cd\u5173\u7cfb\uff0c\u866c\u9aef\u5ba2\u5c31\u4e0d\u4f1a\u56da\u7981\u4e16\u6c11\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u60ed\u6127\u5440\uff01\u4ed6\u5bf9\u6211\u534a\u4e3a\u5144\u957f\u534a\u4e3a\u5e08\uff0c\u7adf\u7136\u4f1a\u5229\u7528\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u4e5f\u6570\u4e86\u4e16\u6c11\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fae\u5fae\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8bf4\u554a\uff1f\u8bbd\u523a\u6211\uff0c\u5c31\u4e0d\u5982\u9a82\u6211\u4e00\u987f\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u6b63\u8272\u9053\uff1a\u201c\u662f\u771f\u7684\uff0c\u866c\u9aef\u5ba2\u5982\u975e\u76f8\u4fe1\u5ac2\u592b\u4eba\u6709\u80fd\u529b\u7275\u6211\u6765\u6b64\uff0c\u4f1a\u9063\u6d3e\u9ad8\u624b\u628a\u4e16\u6c11 \u523a\u6740\u9014\u4e2d\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u60f3\u4e86\u4e00\u60f3\uff0c\u7a0d\u73b0\u7b11\u610f\u3002 \u3000\u3000\u4f46\u968f\u53c8\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u662f\u6211\u5bb3\u4e86\u4e8c\u516c\u5b50\u3002\u949f\u6728\u9b41\u662f\u8d1f\u8d23\u7559\u6b64\u76d1\u89c6\u4e8c\u516c\u5b50\u7684\u9996 \u8111\u4eba\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u949f\u6728\u9b41\uff1f\u201d\u674e\u4e16\u6c11\u5fd9\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4f4d\u7f01\u8863\u4eba\u4e86\uff0c\u4ed6\u4e3e\u6b62\u602a\u5f02\uff0c\u4f3c\u6709\u5947\u75be\uff0c\u548c\u5e38\u4eba\u5927\u4e0d \u76f8\u540c\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u4ed6\u7ec3\u7684\u662f\u4e59\u6728\u795e\u529f\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6210\u5c31\u592a\u9ad8\u4e86\uff0c\u628a\u6574\u4e2a\u7684\u8eab\u4f53\u7ec3\u6210\u4e86\u67af\u6728\u4e00 \u822c\uff0c\u770b\u4e0a\u53bb\u884c\u52a8\u6709\u4e9b\u4e0d\u4fbf\uff0c\u4e00\u65e6\u63d0\u805a\u529f\u529b\uff0c\u884c\u52a8\u8d77\u6765\u4e0d\u4f46\u5341\u5206\u7075\u6d3b\uff0c\u4e5f\u5a01\u529b\u60ca\u4eba\u3002\u4e00\u51fb\u4e4b \u4e0b\uff0c\u80fd\u788e\u4e00\u5757\u5343\u65a4\u5de8\u77f3\uff0c\u5168\u8eab\u5200\u62a2\u4e0d\u5165\uff0c\u662f\u8fd9\u91cc\u6392\u540d\u7b2c\u4e09\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u6bd4\u8d77\u5ac2\u592b\u4eba\u7684\u5251\u672f\uff0c\u662f\u5426\u7a0d\u6709\u900a\u8272\u5462\uff1f\u949f\u6728\u9b41\u6392\u540d\u7b2c\u4e09\uff0c\u54ea\u7b2c\u4e00\u3001\u7b2c\u4e8c\u53c8\u662f\u4e9b\u4ec0\u4e48\u4eba \u5462\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u5927\u611f\u5174\u8da3\u7684\u95ee\u7740\u3002 \u3000\u3000\u201c\u949f\u6728\u9b41\u4e0d\u6015\u6211\u7684\u9a6d\u5251\u672f\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u4f24\u4e0d\u4e86\u4ed6\uff0c\u6211\u60f3\u8fd9\u5c31\u662f\u6d3e\u4ed6\u76d1\u63a7\u4f60\u7684\u539f\u56e0 \u5427\uff01\u4e0d\u6015\u6211\u6551\u4f60\u51fa\u53bb\u3002\u201d \u3000\u3000\u5979\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u5410\u51fa\u5fc3\u4e2d\u7684\u5fe7\u5fff\uff0c\u63a5\u9053\uff1a\u201c\u7b2c\u4e00\u9ad8\u624b\uff0c\u5f53\u7136\u5c31\u662f\u6211\u548c\u674e\u9756\u7684\u7ed3\u4e49\u5927\u54e5 \u2014\u2014\u866c\u9aef\u5ba2\uff0c\u7b2c\u4e8c\u9ad8\u624b\u4f60\u5df2\u7ecf\u89c1\u8fc7\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u4e16\u6c11\u63a5\u9053\uff1a\u201c\u90a3\u4f4d\u7a7f\u7740\u9053\u888d\u3001\u9762\u5982\u767d\u7389\u7684\u4eba\u4e86\uff0c\u4ed6\u7684\u7edd\u6280\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4ed6\u81ea\u53f7\u4e03\u7edd\u9053\u957f\uff0c\u662f\u4e00\u4f4d\u96c6\u591a\u79cd\u7edd\u6280\u4e8e\u4e00\u8eab\u7684\u4eba\u3002\u662f\u866c\u9aef\u5ba2\u9690\u4f0f\u5728\u957f\u5b89\u9644 \u8fd1\u7684\u9ad8\u624b\u4e4b\u4e00\uff0c\u6211\u53ea\u77e5\u9053\u4ed6\u9ad8\u8fc7\u949f\u6728\u9b41\uff0c\u5374\u672a\u89c1\u8bc6\u8fc7\u4ed6\u7684\u672c\u9886\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u8fd1\u4e00\u5e74\u4e2d\u51fa\u73b0\u5728 \u866c\u9aef\u5ba2\u7684\u8eab\u4fa7\uff0c\u8fde\u674e\u9756\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9707\u52a8\u4e86\uff01 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u7a76\u7adf\u6709\u591a\u5c11\u4f0f\u5175\uff1f\u591a\u5c11\u9ad8\u624b\u6f5c\u4f0f\u5728\u957f\u5b89\u5de6\u8fd1\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u5982\u4f55\u5904\u63aa\u4e86\uff1f\u201d\u5f20\u51fa\u5c18\u63a5\u9053\uff1a\u201c\u62fc\u4e86\u547d\uff0c\u6050\u6015\u4e5f\u65e0\u6cd5\u6559\u4f60\u79bb\u6b64\u2026\u2026\u201d \u3000\u3000\u201c\u5343\u4e07\u4e0d\u53ef\uff0c\u5343\u4e07\u4e0d\u53ef\uff01\u201d\u674e\u4e16\u6c11\u63a5\u8fc7\uff1a\u201c\u90a3\u4f1a\u8fde\u4f60\u4e5f\u8eab\u9677\u5371\u5883\u4e86\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6211\u82e6\u601d\u65e0\u7b56\u7684\u539f\u56e0\uff0c\u6211\u5fc3\u4e2d\u867d\u6709\u4e0d\u6ee1\uff0c\u53ea\u6709\u6ca1\u6709\u884c\u52a8\uff0c\u8fd8\u53ef\u7ef4\u6301\u4e2a \u8868\u9762\u548c\u5584\u7684\u5c40\u9762\uff0c\u4e00\u65e6\u884c\u52a8\u5931\u8d25\uff0c\u59be\u8eab\u6b7b\u4e0d\u8db3\u60dc\uff0c\u6050\u4e5f\u5c06\u8fde\u7d2f\u5230\u4e8c\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u8a00\u6709\u7406\uff0c\u4e00\u79c0\u5cf0\u7edd\u5d16\u5343\u5bfb\uff0c\u50fb\u5904\u7fa4\u5cf0\u4e4b\u4e2d\u3002\u8fd9\u91cc\u662f\u63d2\u7fc5\u96be\u98de \u5440\uff01\u866c\u9aef\u5ba2\u4f3c\u4e5f\u4e0d\u6025\u4e8e\u6740\u6211\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff0c\u770b\u6837\u5b50\u4ed6\u4f3c\u4ee5\u4e8c\u516c\u5b50\u4f5c\u4e3a\u4eba\u8d28\uff0c\u4ee5\u4f7f\u8ffd\u968f\u4e8c\u516c\u5b50\u7684\u9ad8\u4eba\u864e \u5c06\uff0c\u2014\u4e00\u4e3a\u4ed6\u6240\u7528\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u7528\u6025\u6025\u8131\u56f0\u4e86\u3002\u201d\u674e\u4e16\u6c11\u63a5\u9053\uff1a\u201c\u6211\u4eec\u6709\u65f6\u95f4\u4ece\u957f\u8ba1\u8bae\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u679c\u674e\u9756\u80fd\u65e9\u65e5\u5230\u6b64\uff0c\u5408\u6211\u4eec\u592b\u5987\u4e4b\u529b\uff0c\u4e5f\u8bb8\u80fd\u6551\u8d70\u4e8c\u516c\u5b50\uff0c \u4f46\u4e0d\u77e5\u4ed6\u4f1a\u4e0d\u4f1a\u95fb\u8baf\u8d76\u6765\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u541e\u541e\u5410\u5410\u5730\u9053\uff1a\u201c\u6765\u662f\u4e00\u5b9a\u4f1a\u6765\uff0c\u4e0d\u8fc7\u798f\u7978\u96be\u6599\u554a\uff01\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u8a00\u5916\u6709\u610f\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4f55\u4e0d\u8bf4\u4e2a\u6e05\u695a\u5462\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u76b1\u8d77\u7709\u5934\uff0c\u9053\uff1a\u201c\u5982\u6709\u5931\u8a00\uff0c\u8fd8\u671b\u5ac2\u592b\u4eba\u591a\u591a\u539f\u8c05\uff0c\u4e0d\u8981\u8d23\u602a\u3002\u201d \u3000\u3000\u201c\u5c3d\u7ba1\u8bf7\u8bf4\uff0c\u51fa\u5c18\u5bf9\u4e8c\u516c\u5b50\u53ea\u6709\u6127\u759a\uff0c\u54ea\u91cc\u8fd8\u6562\u8d23\u602a\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5bf9\u5ac2\u592b\u4eba\u4ecd\u6709\u4e9b\u65e7\u60c5\u96be\u5fd8\u554a\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7684\u8138\u7ea2\u4e86\uff0c\u4f4e\u4e0b\u5934\uff0c\u5e26\u7740\u4e09\u5206\u7f9e\u6127\uff0c\u9053\uff1a\u201c\u8fd9\u65b9\u9762\uff0c\u674e\u9756\u5c31\u6bd4\u6211\u654f\u611f\uff0c\u4ed6\u4e0d\u613f\u7559 \u5728\u8fd9\u91cc\uff0c\u5927\u6982\u662f\u65e9\u6709\u6240\u89c9\u4e86\uff0c\u4e8c\u516c\u5b50\u4e5f\u80fd\u89c2\u5bdf\u5165\u5fae\u3002\u201d \u3000\u3000\u201c\u65c1\u89c2\u8005\u6e05\u554a\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u653e\u5230\u4e86\u81ea\u5df1\u5934\u4e0a\uff0c\u5c31\u672a\u5fc5\u80fd\u5982\u6b64\u660e\u5bdf\u79cb\u6beb\uff0c\u6211\u66fe\u628a\u4e00\u4ef6 \u7b80\u5355\u660e\u4e86\u7684\u4e8b\u5904\u7406\u5f97\u4e00\u584c\u7cca\u6d82\u3002\u8fd8\u4e0d\u77e5\u9053\u4f1a\u9020\u6210\u591a\u5927\u7684\u9057\u6068\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u53ef\u662f\u8bf4\u7684\u8881\u5b9d\u513f\uff0c\u542c\u8bf4\u5979\u989c\u8272\u51a0\u7edd\u4e00\u4ee3\uff0c\u540e\u5bab\u4f73\u4e3d\u5168\u5931\u8272\uff0c\u4e8c\u516c\u5b50\u4e5f\u771f\u80fd \u72e0\u4e0b\u5fc3\u820d\u5f97\u5979\u5440\uff01\u201d \u3000\u3000\u201c\u5904\u7f6e\u5931\u63aa\u4e86\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5982\u4eca\u662f\u540e\u6094\u83ab\u53ca\uff0c\u5ac2\u592b\u4eba\uff0c\u866c\u9aef\u5ba2\u4ed6\u2026\u2026\u201d \u3000\u3000\u95ee\u4e0d\u51fa\u53e3\u4e86\uff0c\u53ea\u597d\u4e2d\u9014\u6253\u4f4f\u3002 \u3000\u3000\u4f46\u5f20\u51fa\u5c18\u660e\u767d\uff0c\u4f3c\u4e5f\u613f\u5728\u674e\u4e16\u6c11\u7684\u9762\u524d\u8868\u8fbe\u51fa\u5fc3\u4e2d\u7684\u610f\u613f\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u8d8a\u89c4\u7684\u884c\u52a8\uff0c\u4f46\u6211\u5df2\u4ece\u4ed6\u7684\u773c\u795e\u4e2d\u53d1\u89c9\u4ed6\u5fc3\u4e2d\u7684\u6b32\u671b\u5341\u5206\u5f3a\u70c8\uff0c\u4ed6 \u4ee5\u6781\u5927\u7684\u5b9a\u529b\u514b\u5236\u7740\u81ea\u5df1\u3002 \u3000\u3000\u6211\u4e0d\u77e5\u9053\u4ed6\u8fd9\u79cd\u5fcd\u8010\u7684\u529f\u592b\u8fd8\u80fd\u6301\u7eed\u591a\u4e45\uff0c\u4e8c\u516c\u5b50\uff0c\u5f88\u53ef\u6015\u5440\uff01\u8ba9\u6211\u751f\u6d3b\u5f97\u5982\u5750\u9488\u6be1\u3002 \u8fc7\u53bb\u5168\u5fc3\u7ec3\u5251\uff0c\u8fd8\u65e0\u6240\u89c9\uff0c\u5982\u4eca\u662f\u5251\u672f\u6709\u6210\uff0c\u624d\u611f\u5230\u5371\u673a\u56db\u4f0f\u3002\u674e\u9756\u597d\u72e0\u5fc3\uff0c\u4e22\u4e0b\u6211\u4e00\u4e2a\u4e0d \u7ba1\u4e86\u3002 \u3000\u3000\u5f53\u5e74\u6211\u4e3a\u4ed6\uff0c\u591c\u5954\u5ba2\u820d\uff0c\u59d4\u8eab\u76f8\u5c31\uff0c\u628a\u5973\u4eba\u7684\u5c0a\u4e25\u4e00\u628a\u629b\u5f00\uff0c\u96be\u9053\u8fd9\u4e00\u6b21\u8fd8\u8981\u6211\u6295\u6000\u9001 \u62b1\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u5ac2\u592b\u4eba\u5fc3\u4e2d\u8fd8\u6709\u5982\u6b64\u7684\u4ecb\u8482\uff0c\u90fd\u662f\u592b\u59bb\u4e86\uff0c\u8fd8\u8981\u8ba1\u8f83\u4ec0 \u4e48\uff1f\u674e\u9756\u5144\u5bf9\u5ac2\u592b\u4eba\u6000\u8877\u5982\u4e00\uff0c\u8fd9\u4e00\u70b9\u4e16\u6c11\u613f\u4ee5\u751f\u6b7b\u62c5\u4fdd\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6709\u4e9b\u60ca\u559c\u5730\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5bf9\u6211\u6ca1\u6709\u8bef\u4f1a\uff1f\u201d \u3000\u3000\u201c\u5c0f\u8bef\u4f1a\u5728\u6240\u96be\u514d\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5927\u8bef\u4f1a\u5c31\u4e0d\u81f4\u4e8e\u4e86\uff0c\u674e\u9756\u4ed6\u76f8\u4fe1\u4f60\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u6709\u9519\uff0c\u6211\u88ab\u5251\u672f\u8ff7\u4f4f\u4e86\uff0c\u758f\u5ffd\u4e86\u4f5c\u59bb\u5c0f\u7684\u8d23\u4efb\uff0c\u53ea\u9053\u4ed6\u4fee\u4e60\u9053\u6cd5\u4f1a\u628a\u513f \u5973\u67d4\u60c5\u770b\u5f97\u5f88\u6de1\uff0c\u6211\u6709\u6b64\u4e00\u5ff5\uff0c\u8bef\u4eba\u8bef\u5df1\uff0c\u76f4\u5230\u4ed6\u62c2\u8896\u800c\u53bb\uff0c\u6211\u7adf\u7136\u8fd8\u65e0\u8b66\u89c9\uff0c\u4e00\u76f4\u8ba4\u4e3a\u5fc3 \u7cfb\u5728\u60c5\u90ce\u8eab\u4e0a\uff0c\u4ed6\u5e94\u8be5\u611f\u89c9\u5f97\u5230\u554a\uff01\u201d \u3000\u3000\u201c\u6295\u5165\u5f97\u592a\u6df1\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u96be\u602a\u3002\u4e16\u6c11\u542c\u8bf4\u4e60\u7ec3\u4e0a\u4e58\u5251\u6cd5\u7684\u4eba\uff0c\u5230\u4e86\u95ed\u53e3\u6240\u5728\uff0c\u7edd\u4e0d\u8bb8 \u7a0d\u6709\u5206\u5fc3\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u7ec3\u6210\u4e86\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u4f46\u5374\u5dee\u4e00\u70b9\u5931\u53bb\u4e86\u4e08\u592b\u3002\u6211\u4ece\u866c \u9aef\u5ba2\u7684\u53cc\u76ee\u4e2d\u53d1\u89c9\u5230\u6b32\u671b\u4e4b\u5149\uff0c\u624d\u53d1\u89c9\u5230\u5904\u5883\u7684\u9669\u6076\u3002\u674e\u9756\u53ef\u80fd\u65e9\u5c31\u53d1\u89c9\u4e86\uff0c\u6211\u4e0d\u61c2\u7684\u662f\u4e3a \u4ec0\u4e48\u4e0d\u65e9\u4e9b\u544a\u8bc9\u6211\u4e00\u58f0\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u5bf9\u5251\u672f\u8ff7\u604b\u6b63\u6df1\uff0c\u674e\u9756\u5144\u5c31\u7b97\u8bf4\u51fa\u6765\uff0c\u4f60\u4f1a\u76f8\u4fe1\u5417\uff1f\u4e00\u6709\u4e89\u6267\uff0c\u5c31 \u5168\u7136\u4e0d\u662f\u90a3\u4e48\u4e00\u56de\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u5b8c\u5168\u6ca1\u6709\u53d1\u89c9\u5230\u866c\u9aef\u5ba2\u7684\u5f02\u6837\u60c5\u6000\uff0c\u5c31\u5f88\u96be\u8ba4\u540c\u674e\u9756\u7684\u529d\u544a\uff0c\u4e00\u4e89\u6267\uff0c\u5c31\u6cc4\u9732\u4e86\u8fd9 \u4e2a\u9690\u5bc6\u4e86\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5230\u4e86\u90a3\u79cd\u5c40\u9762\uff0c\u8981\u674e\u9756\u600e\u4e48\u529e\u5462\uff1f\u4e89\u5435\u4e00\u9635\u62c2\u8896\u800c\u53bb\uff0c\u8fd8\u662f\u53cd\u76ee \u52a8\u624b\u3001\u5175\u5203\u6839\u89c1\u5462\uff1f\u90fd\u4e0d\u662f\u597d\u7ed3\u5c40\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u4f46\u5df2\u7ecf\u591f\u4e86\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u9886\u7565\u4e86\u8bdd\u4e2d\u542b\u610f\uff0c\u542c\u5f97\u51b7\u6c57\u6dcb\u6f13\uff0c\u53cc\u988a\u4f3c\u706b\uff0c\u5fc3\u4e2d\u7684\u6fc0\u52a8\u5df2\u96be\u81ea\u7981\u3002 \u3000\u3000\u5979\u7f13\u7f13\u7684\u9053\uff1a\u201c\u6211\u4f1a\u758f\u4e8e\u9632\u8303\uff0c\u88ab\u4eba\u4e58\u865a\u800c\u5165\uff0c\u5360\u636e\u82b3\u5fc3\uff0c\u4e00\u5931\u8db3\u6210\u5343\u53e4\u6068\uff0c\u5927\u6982\u5c31\u662f \u8fd9\u4e2a\u4e0b\u573a\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u54ed\u4e86\uff0c\u662f\u90a3\u79cd\u65e0\u58f0\u4e4b\u6ce3\u3002 \u3000\u3000\u4e24\u884c\u6e05\u6cea\uff0c\u4e00\u9897\u63a5\u4e00\u9897\u5730\u987a\u816e\u800c\u4e0b\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e0d\u518d\u591a\u8a00\uff0c\u518d\u8bf4\u5c31\u753b\u86c7\u6dfb\u8db3\u4e86\u3002 \u3000\u3000\u201c\u6211\u5bf9\u4e0d\u8d77\u674e\u9756\u3002\u201d\u5f20\u51fa\u5c18\u7684\u58f0\u97f3\u5e73\u9759\uff0c\u62ed\u7740\u6cea\u75d5\uff0c\u9053\uff1a\u201c\u6211\u771f\u7684\u5bf9\u4e0d\u8d77\u4ed6\uff0c\u6211\u8981\u5411\u4ed6 \u8ba4\u9519\uff0c\u6c42\u4ed6\u539f\u8c05\u6211\uff0c\u4ed6\u662f\u4e2a\u806a\u660e\u3001\u4e13\u60c5\u3001\u53c8\u4f1f\u5927\u7684\u4e08\u592b\uff0c\u6211\u5374\u662f\u4e00\u4e2a\u611a\u7b28\u53c8\u81ea\u79c1\u7684\u59bb \u5c0f\u2026\u2026\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5ac2\u592b\u4eba\uff0c\u4f60\u4eec\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\uff0c\u6b64\u756a\u5ac2\u592b\u4eba\u80fd\u5c3d\u53bb\u5fc3\u4e2d\u4ecb \u8482\uff0c\u674e\u9756\u5144\u8fd9\u4e00\u751f\u5c31\u4eab\u4e0d\u5c3d\u6e29\u67d4\u4e4b\u798f\u4e86\u3002\u201d \u3000\u3000\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u51fa\u5c18\u7834\u6d95\u4e3a\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u4e8e\u4e00\u756a\u5f00\u5bfc\uff0c\u4f7f\u51fa\u5c18\u91cd\u89c1\u5929\u65e5\uff0c\u52ff\u602a\u674e \u9756\u80fd\u503e\u5fc3\u76f8\u8bb8\u3002\u4e8c\u516c\u5b50\u9664\u4e86\u738b\u8005\u7684\u5a01\u4e25\u3001\u6c14\u52bf\u4ee4\u4eba\u5fc3\u6298\u4e4b\u5916\uff0c\u5bf9\u4eba\u3001\u7269\u3001\u4e8b\u7684\u5256\u6790\u3001\u9ad8\u8bba\uff0c \u4e5f\u8ba9\u4eba\u4e94\u4f53\u6295\u5730\u3002\u4e8c\u516c\u5b50\uff0c\u51fa\u5c18\u611f\u6fc0\u83ab\u540d\uff0c\u53ea\u6709\u4e00\u62dc\u916c\u6069\u60c5\u4e86\u3002\u201d \u3000\u3000\u8bf4\u62dc\u5c31\u62dc\uff0c\u7adf\u7136\u771f\u7684\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4f38\u624b\u76f8\u6276\uff0c\u4f46\u5c06\u89e6\u53ca\u5f20\u51fa\u5c18\u7684\u8eab\u4f53\u65f6\uff0c\u7a81\u7136\u6536\u4e86\u56de\u6765\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\u9053\uff1a\u5982\u6b64\u7f8e\u5973\uff0c\u5c82\u53ef\u4ee5\u624b\u76f8\u89e6\u3001\u808c\u80a4\u76f8\u4eb2\uff1f \u3000\u3000\u4ed6\u4fbf\u6025\u6025\u5730\u9053\uff1a\u201c\u5feb\u8d77\u6765\u5440\uff01\u518d\u8981\u5982\u6b64\uff0c\u4e16\u6c11\u4ee5\u540e\u5982\u4f55\u8fd8\u6562\u518d\u53d1\u8c2c\u8bba\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff01\u201d \u3000\u3000\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\u63d2\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6728\u8001\uff0c\u5df2\u6ee1\u4e86\u534a\u4e2a\u65f6\u8fb0\u5417\uff1f\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u949f\u6728\u9b41\u624b\u6267\u7740\u4e00\u6839\u9ed1\u8272\u624b\u6756\u7ad9\u5728\u5927\u5385\u95e8\u5916\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u4e0d\u5230\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u4f46\u4e5f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u5982\u975e\u5927\u738b\u98de\u4ee4\u4e0a\u5c71\uff0c\u4f20\u53ec\u5f20\u59d1\u5a18\u4e0b\u5c71\u8bae \u4e8b\uff0c\u6728\u9b41\u518d\u591a\u7ed9\u4e24\u4f4d\u4e00\u4e9b\u65f6\u95f4\uff0c\u6709\u4f55\u4e0d\u53ef\u5462\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u54e6\uff01\u5927\u54e5\u6709\u4ee4\u4f20\u53ec\uff0c\u4e8c\u516c\u5b50\uff01\u6055\u6211\u4e0d\u5949\u966a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5ac2\u592b\u4eba\u591a\u591a\u73cd\u91cd\uff0c\u4e8b\u52a1\u7e41\u5fd9\uff0c\u5c31\u4e0d\u7528\u591a\u6765\u63a2\u671b\u6211 \u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u6728\u8001\u7167\u987e\uff0c\u4e8c\u516c\u5b50\u5b89\u5168\u53ef\u4fdd\u65e0\u8651\u3002\u201d \u3000\u3000\u98de\u8eab\u4e00\u8dc3\uff0c\u50cf\u4e00\u53ea\u5927\u9e1f\u822c\u76f4\u5411\u5385\u5916\u98de\u5c04\u800c\u51fa\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u7684\u8bdd\u534a\u771f\u534a\u5047\uff0c\u4e8c\u516c\u5b50\u4e0d\u53ef\u5168\u4fe1\u5462\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff1f\u201d\u674e\u4e16\u6c11\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6728\u8001\u53ef\u5426\u8bf4\u7684\u660e\u767d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u597d\uff01\u4e8c\u516c\u5b50\u65e2\u4e0d\u660e\u5f26\u5916\u4e4b\u97f3\uff0c\u8001\u592b\u5c31\u8bf4\u6e05\u695a\u4e86\u3002 \u3000\u3000\u82e5\u662f\u6709\u4eba\u60f3\u6765\u523a\u6740\u4e8c\u516c\u5b50\uff0c\u6709\u8001\u592b\u6321\u5173\uff0c\u81ea\u53ef\u4fdd\u5b89\u5168\u65e0\u8651\u3002 \u3000\u3000\u4f46\u5982\u6709\u4eba\u60f3\u6559\u4e8c\u516c\u5b50\u79bb\u5f00\u6b64\u5730\uff0c\u4e8c\u516c\u5b50\u5c31\u8981\u591a\u60f3\u60f3\u4e86\uff0c\u8001\u592b\u5949\u5230\u7684\u4ee4\u8c15\u662f\u4e00\u6709\u9003\u8d70\u7684\u884c \u52a8\u5c31\u683c\u6740\u52ff\u8bba\u3002\u201d \u3000\u3000\u8fd9\u662f\u6b63\u5f0f\u7684\u8b66\u544a\uff0c\u8bf4\u660e\u4e86\u9003\u8d70\u5fc5\u6740\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u6b63\u5728\u5fd6\u601d\u4e4b\u95f4\uff0c\u4f20\u6765\u4e86\u8f7b\u5de7\u7684\u6b65\u5c65\u4e4b\u58f0\uff01 \u3000\u3000\u4e00\u4e2a\u77f3\u69b4\u957f\u88d9\u3001\u84dd\u4e0a\u8863\u3001\u68b3\u7740\u4e24\u6761\u5927\u8fab\u5b50\u7684\u59d1\u5a18\uff0c\u63d0\u7740\u4e00\u76cf\u5bab\u706f\uff0c\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u59d1\u5a18\u7b11\u9053\uff1a\u201c\u4e3a\u4e86\u4e0d\u60ca\u6270\u4e8c\u516c\u5b50\u7684\u601d\u8651\uff0c\u5a62\u5b50\u4eec\u5949\u547d\u5168\u90fd\u642c\u4e86\u51fa\u53bb\u3002\u8fd9\u91cc\u9664\u4e86\u5927\u5385\u3001 \u53a8\u5395\u4e4b\u5916\uff0c\u8fd8\u6709\u5341\u4e8c\u623f\u5927\u5c0f\u4e0d\u540c\u7684\u5367\u5ba4\uff0c\u4e00\u5927\u95f4\u6ee1\u67b6\u5b58\u4e66\u7684\u4e66\u623f\uff0c\u4e8c\u516c\u5b50\u90fd\u53ef\u4ee5\u968f\u65f6\u4f7f \u7528\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u8fd9\u5ea7\u534e\u5c71\u884c\u9986\u4e4b\u4e2d\u53ea\u6709\u6211\u4e00\u4eba\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u708a\u98df\u6d17\u5237\u4e5f\u8981\u81ea\u7406\u5417\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u662f\u8d35\u5bbe\uff0c\u90a3\u80fd\u6162\u6020\u3002\u201d\u5973\u5a62\u7b11\u9053\uff1a\u201c\u6d12\u626b\u5385\u623f\uff0c\u81ea\u6709\u6211\u4eec\u6253\u7406\uff0c\u4e09\u9910\u996e\u98df\u6211\u4eec \u4f1a\u6309\u65f6\u9001\u4e0a\u3002\u4e8c\u516c\u5b50\uff0c\u4f8d\u5019\u4f60\u7684\u5973\u5a62\u6b4c\u59ec\u52a0\u8d77\u6765\u6709\u5341\u516b\u4e2a\u4eba\uff0c\u4e5f\u90fd\u662f\u8fd9\u5ea7\u5bab\u7684\u4eba\u3002\u4f60\u559c\u6b22\uff0c \u53ef\u4ee5\u5ba3\u5979\u4eec\u4e3a\u541b\u4f8d\u5bdd\u3002 \u3000\u3000\u8fd9\u91cc\u53eb\u2018\u534e\u5c71\u884c\u5bab\u2019\uff0c\u4e0d\u662f\u2018\u534e\u5c71\u884c\u9986\u2019\uff0c\u672c\u662f\u5927\u738b\u517b\u606f\u3001\u9759\u601d\u4e4b\u6240\uff0c\u73b0\u5728\u5168\u7531\u4e8c\u516c\u5b50 \u4e00\u4eba\u4eab\u53d7\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u6697\u6697\u5fd6\u9053\uff1a\u542c\u53e3\u6c14\uff0c\u8fd9\u4e2b\u5934\u4f3c\u662f\u8fd9\u91cc\u7684\u6b4c\u59ec\u5973\u5a62\u7684\u73ed\u5934\uff0c\u60f3\u63a2\u5f97\u4e00\u4e9b\u5185\u60c5\u3001\u6d88 \u606f\uff0c\u8981\u653e\u4e0b\u8eab\u4efd\u62c9\u4e0b\u8138\u76ae\u624d\u884c\u3002 \u3000\u3000\u5b9a\u4e86\u4e3b\u610f\uff0c\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f8d\u5bdd\u7684\u4eba\u4e5f\u5305\u62ec\u4f60\u59d1\u5a18\u5728\u5185\u4e86\uff01\u201d \u3000\u3000\u90a3\u5973\u5a62\u4f3c\u662f\u672a\u6599\u5230\u6709\u6b64\u4e00\u95ee\uff0c\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u6c89\u541f\u4e86\u597d\u4e00\u9635\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5728\u5341\u516b\u540d\u6b4c\u59ec\u3001\u4f8d\u5a62\u4e2d\uff0c\u59be\u5a62\u7684\u59ff\u8272\u6700\u4e0d\u517b\u773c\uff0c\u4e8c\u516c \u5b50\u5e94\u8be5\u4e0d\u4f1a\u9009\u4e2d\u59be\u5a62\u624d\u5bf9\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fd6\u9053\uff1a\u201c\u8fd9\u662f\u5979\u6700\u5927\u7684\u8106\u5f31\u4e4b\u5904\uff0e\u8fd9\u4e2b\u5934\u65e9\u5df2\u5fc3\u6709\u6240\u5c5e\uff0c\u6211\u5982\u7c4d\u6545\u7ed9\u4ed6\u4e00\u4e9b\u538b \u529b\uff0c\u53ef\u80fd\u4f1a\u903c\u51fa\u4e00\u4e9b\u4ec0\u4e48\uff1f\u6536\u653e\u4e4b\u95f4\u5c31\u53ef\u80fd\u6709\u6240\u6536\u83b7\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u968f\u5373\u5fae\u7b11\u9053\uff1a\u201c\u60c5\u4eba\u773c\u91cc\u51fa\u897f\u65bd\u554a\uff01\u592a\u539f\u4faf\u5e9c\u4e4b\u4e2d\u4e5f\u517b\u4e86\u4e00\u6279\u63a5\u5f85\u8d35\u5bbe\u7684\u6b4c\u59ec\u5973 \u4f8d\uff0c\u6709\u5357\u56fd\u4f73\u4e3d\uff0c\u4e5f\u6709\u5317\u5730\u80ed\u8102\u2026\u2026\u201d \u3000\u3000\u5973\u5a62\u9eef\u7136\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u8d35\u80c4\u516c\u5b50\uff0c\u53ea\u77e5\u9152\u8272\u5f81\u9010\uff0c\u6e38\u620f\u4eba\u95f4\uff0c\u5f97\u507f\u4e00\u5df1\u79c1\u6b32\u5c31 \u597d\uff0c\u54ea\u4f1a\u7ba1\u522b\u4eba\u6b7b\u6d3b\u3002\u4e8c\u516c\u5b50\u4e00\u5b9a\u8981\u9009\u6211\u4f8d\u5bdd\uff0c\u59be\u5a62\u662f\u65e0\u80fd\u53cd\u6297\u7684\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u770b\u5979\u6ee1\u8138\u6101\u82e6\u4e4b\u8272\uff0c\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u8881\u5b9d\u513f\u4f55\u7b49\u59ff\u8272\uff1f \u3000\u3000\u4f55\u7b49\u98ce\u60c5\uff1f\u6211\u90fd\u80fd\u628a\u6301\u4f4f\u81ea\u5df1\uff0c\u95ef\u8fc7\u4e86\u5927\u6c5f\u98ce\u6d6a\uff0c\u600e\u4f1a\u9634\u6c9f\u91cc\u7ffb\u8239\uff1f\u6b64\u4e00\u756a\u5f04\u5de7\u3001\u8bd5 \u60c5\uff0c\u65e8\u5728\u6d4b\u9a8c\u4e00\u4e0b\u866c\u9aef\u5ba2\u7edf\u519b\u4e4b\u9053\uff0c\u5c0f\u6709\u5192\u72af\u4e4b\u5904\uff0c\u8fd8\u8bf7\u4f60\u591a\u591a\u539f\u5ba5\u4e86\u554a\uff01 \u3000\u3000\u5fc3\u4e2d\u9ed8\u7977\uff0c\u53e3\u91cc\u7b11\u9053\uff1a\u201c\u6562\u95ee\u59d1\u5a18\u82b3\u540d\uff1f\u656c\u8bf7\u8d50\u544a\uff0c\u4e5f\u597d\u7559\u4e00\u4e2a\u65e5\u540e\u91cd\u53d9\u65e7\u60c5\u7684\u673a \u7f18\u3002\u201d \u3000\u3000\u201c\u59be\u5a62\u5982\u7389\uff0c\u534e\u5c71\u884c\u5bab\u4e2d\u7684\u4f8d\u5a62\u73ed\u5934\u3002\u5728\u6b64\u4e09\u5e74\uff0c\u5e78\u4fdd\u81ea\u585e\u65e0\u6687\u3002\u4e8c\u516c\u5b50\u5982\u575a\u6301\u9009\u6211\u4f8d \u5bdd\uff0c\u4e5f\u662f\u5982\u7389\u5728\u52ab\u96be\u9003\u3002\u201d \u3000\u3000\u201c\u5982\u7389\u59d1\u5a18\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8fd9\u5c31\u8bf4\u5f97\u6709\u4e9b\u51c4\u51c9\u4e86\uff0c\u95fb\u4e4b\u4e0d\u5fcd\u554a\uff01\u4f46\u4f60\u8eab\u5728\u864e\u53e3\uff0c\u8eb2\u8fc7 \u4eca\u5929\uff0c\u5982\u4f55\u80fd\u8eb2\u5f97\u660e\u5929\uff0c\u6211\u4e0d\u9009\u4f60\uff0c\u600e\u4fdd\u8bc1\u522b\u4eba\u4e5f\u4e0d\u9009\u4f60\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u770b\u4f3c\u8eab\u5728\u864e\u53e3\u4e2d\uff0c\u5374\u662f\u76f8\u5f53\u7684\u5b89\u5168\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u534e\u5c71\u884c\u5bab\u5f88\u5c11\u62db\u5f85\u5ba2\u4eba\uff0c\u59be\u5a62 \u9009\u5165\u6b64\u5904\u4e09\u5e74\uff0c\u4f60\u4e8c\u516c\u5b50\u662f\u552f\u4e00\u88ab\u63a5\u5f85\u7684\u5ba2\u4eba\u4e86\u3002\u5927\u738b\u4e5f\u5f88\u5c11\u6765\uff0c\u6240\u4ee5\u6211\u4eec\u6e05\u95f2\u5f97\u5f88\uff0c\u4e5f\u5b89 \u5168\u5f97\u5f88\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5982\u662f\u866c\u9aef\u5ba2\u770b\u4e0a\u4f60\u5462\uff1f\u4f60\u5f53\u5982\u4f55\uff1f\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u5927\u738b\u7231\u8272\uff0c\u4f46\u975e\u7edd\u8272\u4e0d\u7231\uff0c\u50cf\u5f20\u59d1\u5a18\u90a3\u79cd\u5929\u59ff\u56fd\u8272\uff0c\u624d\u80fd\u4f7f\u5927\u738b\u5fc3\u52a8\uff0c\u6211\u4eec \u8fd9\u79cd\u5c71\u8c37\u91ce\u82b1\u3001\u6cb3\u8fb9\u9752\u8349\uff0c\u54ea\u91cc\u4f1a\u770b\u5728\u5927\u738b\u773c\u91cc\uff0c\u5012\u662f\u4f60\u4e8c\u516c\u5b50\u554a\uff01\u6211\u53ef\u662f\u95fb\u540d\u4e45\u77e3\uff0c\u60f3\u4e0d \u5230\u7adf\u4f1a\u2026 \u3000\u3000\u2026\u201d \u3000\u3000\u4e0d\u8bf4\u4e86\uff0c\u5927\u6982\u662f\u4e0d\u592a\u597d\u542c\uff0c\u62c5\u5fc3\u5510\u7a81\u8d35\u5ba2\u4e86\u3002 \u3000\u3000\u201c\u8bf4\u4e0b\u53bb\uff0c\u5982\u7389\u59d1\u5a18\uff01\u662f\u4e0d\u662f\u6211\u674e\u4e16\u6c11\u5927\u597d\u8272\u4e86\uff1f\u201d \u3000\u3000\u201c\u7537\u4eba\u561b\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u5c24\u5176\u662f\u4f60\u4eec\u8fd9\u4e9b\u8c6a\u95e8\u516c\u5b50\uff0c\u5bb6\u4e2d\u517b\u6709\u6b4c\u59ec\uff0c\u95fa\u623f\u4e2d\u4e09\u59bb\u56db\u59be\uff0c \u4ea4\u5f80\u7684\u670b\u53cb\u4e5f\u90fd\u5982\u6b64\u3002\u5728\u5bb6\u4e2d\u7b19\u6b4c\u4e0d\u7edd\uff0c\u51fa\u95e8\u540e\u7f8e\u5973\u73af\u7ed5\uff0c\u5230\u5904\u53ef\u4ee5\u73a9\uff0c\u5230\u5904\u6709\u5f97\u73a9\u3002 \u3000\u3000\u50cf\u8fd9\u79cd\u56da\u5c45\u7684\u751f\u6d3b\uff0c\u4e5f\u6709\u6211\u4eec\u5341\u516b\u4e2a\u5973\u4eba\u966a\u4f60\uff0c\u4efb\u4f60\u6311\u6765\u9009\u53bb\u3002\u4efb\u4f60\u591c\u591c\u6625\u5bb5\uff0c\u4efb \u4f60\u2026\u2026\u201d \u3000\u3000\u8bed\u6c14\u4e2d\u5145\u6ee1\u6124\u6168\uff0c\u4f46\u53c8\u6709\u4e00\u4e9b\u65e0\u53ef\u5948\u4f55\uff1f \u3000\u3000\u674e\u4e16\u6c11\u63a5\u7740\u9053\uff1a\u201c\u5982\u7389\u59d1\u5a18\u8ba4\u4e3a\u5728\u4e0b\u88ab\u56da\u4e8e\u6b64\u4e86\uff0c\u4f55\u4ee5\u89c1\u5f97\u8bb0\uff1f\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u77f3\u9636\u5343\u7ea7\uff0c\u901a\u8def\u4e00\u6761\uff0c\u4f59\u4e0b\u7684\u5ced\u58c1\u5343\u5bfb\uff0c\u7329\u733f\u96be\u6e21\uff0c\u53ea\u6709\u808b\u751f\u53cc\u7fc5\u98de\u4e0b\u53bb \u4e86\u3002\u4e8c\u516c\u5b50\u4e0d\u80fd\u98de\u4e0b\u53bb\uff0c\u53ea\u597d\u7559\u5728\u8fd9\u91cc\u73a9\u5973\u4eba\u4e86\uff01\u201d \u3000\u3000\u201c\u59d1\u5a18\uff0c\u4f60\u5fc3\u4e2d\u4f3c\u6709\u5757\u5792\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u53ef\u5426\u4e00\u8bc9\u8877\u60c5\uff0c\u6211\u8fd9\u91cc\u613f\u4e3a\u542c\u4f17\u3002\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u8a00\u91cd\u4e86\uff0c\u5982\u7389\u53ea\u4e0d\u8fc7\u4e00\u4e2a\u5a62\u5973\uff0c\u751f\u800c\u4f55\u6b22\uff1f\u6b7b\u800c\u4f55\u61be\uff1f\u5012\u662f\u4f60\u4e8c\u516c\u5b50\u554a\uff01 \u65e2\u613f\u5728\u9ec4\u83b2\u6811\u4e0b\u5f39\u7434\uff0c\u82e6\u4e2d\u4f5c\u4e50\uff0c\u5982\u7389\u613f\u4ee5\u6e05\u767d\u8eab\u8eaf\u76f8\u4f34\uff0c\u6f14\u4e00\u51fa\u9e33\u9e2f\u4ea4\u9888\u6b7b\u8352\u5c71\uff0c\u6258\u4f60\u4e8c \u516c\u5b50\u7684\u798f\uff0c\u8ba9\u6211\u8fd9\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u4f8d\u5ba2\u5a62\u5973\u4eca\u751f\u6709\u5e78\u9ad8\u6500\uff0c\u57cb\u9aa8\u4e8e\u534e\u5c71\u884c\u5bab\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5982\u7389\uff0c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e2b\u5934\u554a\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u8eac\u8eab\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u5f97\u7f6a\u4e86\uff0c\u8a00\u8bed\u5192\u72af\uff0c\u8fd8\u671b\u6d77\u6db5\u3002\u201d \u3000\u3000\u5982\u7389\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u820d\u4e86\u8881\u5b9d\u513f\u4eba\u95f4\u7edd\u8272\uff0c\u90a3\u4f1a\u771f\u7684\u770b\u4e0a\u6211\u8fd9\u4e2a\u7c97\u964b\u7684\u4e2b \u5934\u3002\u201d \u3000\u3000\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u674e\u4e16\u6c11\u6709\u4e9b\u9707\u60ca\u4e86\uff0c\u9053\uff1a\u201c\u600e\u4f1a\u77e5\u6653\u8fd9\u4e9b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u6211\u66fe\u662f\u674e\u7237\u8eab\u8fb9\u7684\u4f8d\u5a62\u2026\u2026\u201d \u3000\u3000\u201c\u674e\u9756\uff1f\u201d \u3000\u3000\u5982\u7389\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4e16\u6c11\u8be5\u6b7b\uff0c\u6211\u2026\u2026\u8be5\u79f0\u547c\u4f60\u4e00\u58f0\u5c0f\u5ac2\u592b\u4eba\uff01\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80e1\u8bf4\uff01\u201d\u5982\u7389\u9762\u6cdb\u7f9e\u7ea2\uff0c\u9053\uff1a\u201c\u674e\u7237\u662f\u4f55\u7b49\u82f1\u96c4\u4eba\u7269\uff0c\u54ea\u91cc\u4f1a\u6536\u6211\u4e3a\u59be\uff0c\u6211\u4eec\u4e4b \u95f4\u6e05\u6e05\u767d\u767d\uff0c\u627f\u674e\u7237\u770b\u5f97\u8d77\u6211\uff0c\u6536\u6211\u4e3a\u4e00\u4e2a\u8bb0\u540d\u5f1f\u5b50\uff0c\u4f20\u4e86\u6211\u4e00\u70b9\u6b66\u6280\u3001\u672f\u6cd5\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6211\u2026\u2026\u201d \u3000\u3000\u6211\u4e86\u534a\u5929\uff0c\u6211\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u674e\u7237\u544a\u8bc9\u6211\u4e00\u4e9b\u4e8b\uff0c\u5929\u4e0b\u6700\u8ba9\u4eba\u656c\u4f69\u7684\u4eba\u5c31\u662f\u4f60\u4e8c\u516c\u5b50\uff0c\u6211\u521d\u89c1\u4e8c\u516c\u5b50\u65f6\uff0c \u5c31\u4e3a\u516c\u5b50\u7684\u98ce\u91c7\u5a01\u4eea\u6151\u670d\uff0c\u4f46\u9002\u624d\u89c1\u9762\uff0c\u5374\u53c8\u4e3a\u4e8c\u516c\u5b50\u7684\u8f7b\u6d6e\u4e4b\u6c14\u9707\u60ca\uff0c\u662f\u674e\u7237\u770b\u9519\u4e86\u4eba\uff0c \u8fd8\u662f\u4f60\u5192\u5145\u7684\uff1f\u4e8c\u516c\u5b50\uff0c\u4e00\u756a\u5bf9\u8bdd\u6298\u8f6c\uff0c\u624d\u77e5\u4e8c\u516c\u5b50\u522b\u6709\u7528\u5fc3\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u8fd8\u662f\u83bd\u649e\u4e86\uff0c\u51e0\u4e4e\u5192\u72af\u4e86\u4f60\uff0c\u60ed\u6127\u5440\uff01 \u3000\u3000\u60ed\u6127\uff01\u201d \u3000\u3000\u201c\u8c08\u4e0d\u4e0a\u5192\u72af\u3002\u201d\u5982\u7389\u5fae\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u662f\u771f\u7684\u4e8c\u516c\u5b50\uff0c\u59be\u5a62\u80fd\u4ee5\u8eab\u4f8d\u5bdd\uff0c\u53ef\u662f\u6c42\u4e4b\u4e0d \u5f97\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u866c\u9aef\u5ba2\u65e9\u6709\u8bbe\u8ba1\uff0c\u4e16\u6c11\u5df2\u8eab\u5165\u7262\u7b3c\u3002\u949f\u6728\u9b41\u6280\u827a\u7edd\u9ad8\uff0c\u4e00\u592b\u6321\u5173\uff0c\u516d\u4eb2\u4e0d\u8ba4\u3002\u5904 \u5883\u6700\u9669\u7684\u53c8\u662f\u51fa\u5c18\u59d1\u5a18\u674e\u592b\u4eba\uff0c\u6211\u6015\u866c\u9aef\u5ba2\u4f1a\u653e\u624b\u86ee\u5e72\u3002\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u8bf4\u7684\u4e0d\u9519\uff0c\u5927\u738b\u624b\u4e2d\u51e0\u4f4d\u5fc3\u8179\u9ad8\u624b\u90fd\u4e0d\u80af\u627f\u8ba4\u674e\u7237\u548c\u5f20\u59d1\u5a18\u7684\u5a5a\u59fb\uff0c\u4ed6\u4eec\u8ba4\u4e3a \u53ea\u6709\u5927\u738b\u624d\u914d\u5f97\u4e0a\u5f20\u59d1\u5a18\u7684\u7edd\u4e16\u5bb9\u8272\uff0c\u5927\u738b\u4e5f\u786e\u5b9e\u7231\u4e0a\u4e86\u5f20\u59d1\u5a18\u3002\u600e\u4e48\u529e\u5462\uff1f\u5c0f\u5a62\u8fd9\u70b9\u6280\u827a \u662f\u65e0\u6cd5\u963b\u62e6\u4f4f\u5927\u738b\uff0c\u4e5f\u4e0d\u6562\u548c\u5f20\u59d1\u5a18\u8868\u660e\u5fc3\u610f\uff0c\u53ea\u6709\u6697\u4e2d\u7559\u5fc3\u3002\u5979\u8b66\u89c9\u5371\u9669\uff0c\u9003\u8d70\u65f6\uff0c\u518d\u6697 \u52a9\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u4f46\u81ea\u5979\u7ec3\u6210\u4e86\u4e0a\u4e58\u5251\u672f\u4e4b\u540e\uff0c\u8fd9\u70b9\u5fd9\u4e5f\u4e0d\u7528\u6211\u6765\u5e2e\u4e86\u3002\u5f20\u59d1\u5a18\u5df2\u5230\u98de\u884c\u7edd\u8ff9\u65e5\u884c\u6570\u767e\u91cc \u7684\u901f\u5ea6\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u534e\u5c71\u884c\u5bab\u7684\u9669\u8981\u4e5f\u56f0\u5979\u4e0d\u4f4f\u5417\uff1f\u5979\u5982\u60f3\u8d70\uff0c\u968f\u65f6\u53ef\u4ee5\u8d70\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u662f\u56f0\u5979\u4e0d\u4f4f\uff0c\u4f46\u5979\u65e0\u6cd5\u5feb\u8fc7\u5927\u738b\u554a\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u3001\u6728\u957f\u8001\u4e5f\u90fd\u6709\u8ffd\u4e0a \u5979\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5982\u6709\u59d1\u5a18\u534f\u52a9\uff0c\u5979\u53ef\u4ee5\u8d70\u5f97\u5f88\u5b89\u5168\u3002\u59d1\u5a18\u4f55\u4e0d\u8868\u767d\u4e00\u4e0b\u8eab\u4efd\uff0c\u4ee5\u53d6\u5f97\u5f20\u59d1 \u5a18\u7684\u4fe1\u4efb\u5462\uff1f\u201d \u3000\u3000\u5982\u7389\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8868\u767d\uff0c\u4e00\u65e6\u5f15\u8d77\u8bef\u4f1a\uff0e\u5992\u706b\u53ef\u4ee5\u70e7\u6bc1\u4ed6\u4eec\u592b\u59bb\u7684\u60c5\u610f\uff0c\u90a3\u4e0d\u4f46 \u4e3a\u6211\u62db\u6765\u6740\u8eab\u4e4b\u7978\uff0c\u4e5f\u53ef\u80fd\u9677\u674e\u7237\u4e8e\u5371\u5883\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u5982\u7389\u59d1\u5a18\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4f60\u4e0d\u95fb\u4e0d\u95ee\uff0c\u4e00\u65e6\u8ba9\u51fa\u5c18\u5931\u8eab\u4e8e\u866c\u9aef\u5ba2\uff0c\u5982\u4f55\u662f\u597d\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u65e0\u53ef\u5948\u4f55\u82b1\u843d\u53bb\u4e86\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u8fd9\u4e00\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u5927\u738b\u548c\u5f20\u59d1\u5a18\u51e0\u4e4e\u662f\u671d\u5915\u76f8 \u5904\uff0c\u6f14\u7ec3\u5251\u672f\u3002\u4ed6\u4eec\u662f\u4e49\u5144\u4e49\u59b9\u7684\u8eab\u4efd\uff0c\u4f46\u63a5\u8fd1\u5927\u738b\u7684\u4eba\uff0c\u4f3c\u5df2\u628a\u5f20\u59d1\u5a18\u770b\u6210\u738b\u5983\u4e86\u3002\u6700\u5927 \u7684\u969c\u788d\u662f\u674e\u9756\uff0c\u6740\u4e86\u674e\u9756\u5c31\u53ef\u4ee5\u540d\u6b63\u8a00\u987a\u7684\u62e5\u7740\u5f20\u59d1\u5a18\u4f5c\u738b\u5983\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9a82\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u771f\u662f\u80e1\u95f9\u554a\uff01\u866c\u9aef\u5ba2\u7684\u624b\u4e0b\u5c31\u6ca1\u6709\u4e00\u4e2a\u77e5\u4e66\u660e\u7406\u3001\u80c6\u6562\u8c0f\u8a00\u7684 \u4eba\u529d\u529d\u4ed6\u5417\uff1f\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u201c\u77e5\u4e66\u660e\u7406\u7684\u4eba\u5f53\u7136\u662f\u6709\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u8fd9\u91cc\u96c6\u805a\u4e86\u4e0d\u5c11\u5177\u6709\u6587\u624d\u6b66\u7565\u7684\u9ad8\u4eba\uff0c\u53ef\u662f\u6ca1 \u6709\u4eba\u6562\u8bf4\u8bdd\u5440\uff01\u5927\u738b\u6027\u5982\u70c8\u706b\uff0c\u53c8\u81ea\u4efb\u521a\u610e\u81ea\u5927\uff0c\u518d\u52a0\u4e0a\u4e00\u90e8\u5206\u4eba\u8ba4\u77e5\u4e0d\u540c\uff0c\u4e5f\u6709\u70b9\u5ac9\u5992\u674e \u9756\uff0c\u5f20\u59d1\u5a18\u5728\u4ed6\u4eec\u7684\u5fc3\u76ee\u4e2d\u4e5f\u4e0d\u8fc7\u662f\u4e00\u7f8e\u4e3d\u7684\u5973\u4eba\u3002 \u3000\u3000\u5927\u738b\u559c\u7231\u5f20\u59d1\u5a18\uff0c\u8981\u4e4b\u4f55\u59a8\uff1f\u5927\u738b\u5728\u4ed6\u4eec\u773c\u4e2d\u662f\u4e00\u5c0a\u5929\u795e\uff0c\u4ed6\u8981\u505a\u4ec0\u4e48\uff1f\u4f3c\u90fd\u53ef\u4efb\u610f\u800c \u4e3a\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8fd9\u5c31\u6ca1\u6709\u662f\u975e\u3001\u60c5\u4e49\u53ef\u8a00\u4e86\uff0c\u674e\u9756\u53d1\u89c9\u4e86\u8fd9\u4e9b\u60c5\u52bf\uff0c\u624d\u5fcd\u5f03\u7ed3\u4e49\u4e4b\u60c5\uff0c\u907f \u8d70\u4e8e\u6c5f\u6e56\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u8fd9\u4ef6\u4e8b\u522b\u4eba\u65e0\u6cd5\u63d2\u624b\uff0c\u4e5f\u65e0\u80fd\u63d2\u624b\uff0c\u53ea\u6709\u5f20\u59d1\u5a18\u72ec\u5584\u5176\u4e8b\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u4e0d\u518d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u660e\u767d\u4e86\u4e0d\u5b9c\u518d\u6709\u6fc0\u70c8\u7684\u53cd\u5e94\uff0c\u8fd9\u6863\u4e8b\uff0c\u8d70\u6f0f\u4e86\u4e00\u70b9\u98ce\u58f0\uff0c\u5c31\u4f1a\u6fc0\u53d1\u51fa\u53ef\u6015\u7684\u53d8\u5316\uff0c \u53cc\u65b9\u5fc3\u4e2d\u5df2\u79ef\u5b58\u4e86\u76f8\u5f53\u7684\u59d4\u5c48\uff0c\u7a0d\u7ecf\u6fc0\u52a8\uff0c\u7206\u51fa\u706b\u82b1\uff0c\u5c31\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\u3002 \u3000\u3000\u5982\u7389\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u89e3\u94c3\u8fd8\u662f\u7cfb\u94c3\u4eba\uff0c\u4e5f\u8bb8\u674e\u7237\u7684\u5927\u667a\u5927\u6167\u80fd\u8ba9\u5b83\u5316\u89e3\u4e8e\u65e0\u5f62\u4e4b\u4e2d\u3002\u81f3 \u5c11\uff0c\u4ed6\u5df2\u8ba9\u5f20\u51fa\u5c18\u5b66\u6210\u4e86\u7edd\u4e16\u5251\u672f\uff0c\u6b64\u540e\uff0c\u662f\u5206\u662f\u5408\uff1f\u5f20\u59d1\u5a18\u5fc3\u4e2d\u90fd\u4e0d\u4f1a\u518d\u6709\u9057\u61be\u3002\u201d \u3000\u3000\u201c\u8bf4\u5f97\u6709\u7406\uff0c\u5982\u7389\u59d1\u5a18\u89c1\u89e3\u9ad8\u6df1\uff0c\u4e16\u6c11\u4f69\u670d\u3002\u201d \u3000\u3000\u201c\u8bf4\u4ec0\u4e48\u89c1\u89e3\u9ad8\u6df1\uff0c\u662f\u4e8b\u60c5\u903c\u5f97\u6211\u82e6\u601d\u719f\u8651\uff0c\u60f3\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u624d\u6709\u8fd9\u4e48\u4e00\u4e2a\u770b\u6cd5\u51fa\u6765\uff0c \u8fd9\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u4e2d\u7684\u529e\u6cd5\u5440\uff01\u6c5f\u6c34\u4e1c\u6d41\uff0c\u8c01\u80fd\u963b\u6b62\uff0c\u4efb\u5b83\u81ea\u7136\u5230\u5c3d\u5934\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5982\u7389\uff0c\u4f55\u4ee5\u6559\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u5750\u4ee5\u5f85\u673a\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u540e\u63f4\u672a\u81f3\uff0c\u5343\u4e07\u4e0d\u53ef\u5984\u52a8\u9003\u8d70\u4e4b\u5fc3\uff0c\u7559\u5728\u8fd9\u91cc\u59be\u5a62 \u8fd8\u53ef\u4ee5\u7a0d\u4f5c\u7ef4\u62a4\uff0c\u4e00\u65e6\u884c\u52a8\u5931\u8d25\uff0c\u59be\u5a62\u62fc\u7740\u4e00\u6b7b\uff0c\u4e5f\u65e0\u6cd5\u4fdd\u62a4\u4f60\u3002\u5f20\u51fa\u5c18\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u4fdd\u6301\u73b0\u72b6\uff0c\u81f3\u5c11\u8fd8\u80fd\u6682\u65f6\u7ef4\u6301\u4e2a\u5e73\u9759\u5c40\u9762\u3002\u4e0d\u8fc7\uff0c\u6211\u76f8\u4fe1\u8fd9\u4e2a\u8868\u9762\u7684\u5e73\u9759\uff0c\u7ef4\u6301\u4e0d\u4e86\u591a \u4e45\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff1f\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5982\u7389\u59d1\u5a18\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5927\u738b\u4f3c\u662f\u51c6\u5907\u52a8\u4e86\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u5927\u6982\u4e5f\u6709\u4e86\u8b66\u89c9\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u53ea\u4e0d\u77e5\u957f\u5b89\u57ce\u4e2d\u662f\u5426\u771f\u6709\u6770\u51fa\u4eba\u624d\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e00\u4e2a\u5b57\u6587\u6210\u90fd\uff0c\u624d\u517c\u6587\u6b66\uff0c \u6280\u827a\u975e\u51e1\u3002\u5982\u679c\u60f3\u548c\u5927\u738b\u7684\u795e\u529f\u6297\u62d2\uff0c\u6050\u6015\u662f\u529b\u6240\u96be\u654c\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5dee\u4e00\u70b9\u8bf4\u51fa\u4e86\u8881\u7d2b\u70df\u3002 \u3000\u3000\u4f46\u8bdd\u5230\u53e3\u8fb9\u5fcd\u4e86\u4e0b\u53bb\uff0c\u866c\u9aef\u5ba2\u5982\u679c\u7531\u534e\u9634\u51fa\u5175\uff0c\u653b\u51fb\u957f\u5b89\uff0c\u52bf\u5fc5\u548c\u8881\u7d2b\u70df\u649e\u4e0a\u4e0d\u53ef\uff0c\u8fd9 \u4e00\u6218\u5c31\u5173\u7cfb\u91cd\u5927\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u5982\u7389\u8bf4\u9053\uff1a\u201c\u5927\u738b\u8868\u9762\u7c97\u8c6a\uff0c\u5374\u662f\u7c97\u4e2d\u6709\u7ec6\uff0c\u4ed6\u4f20\u4ee4\u53ec\u8d70\u5f20\u51fa\u5c18\uff0c\u53ef\u80fd\u5df2\u6709\u6240\u8b66 \u60d5\u3002\u4e0d\u8fc7\uff0c\u4ed6\u662f\u771f\u7684\u559c\u6b22\u5f20\u59d1\u5a18\uff0c\u53ea\u8981\u5979\u5e94\u4ed8\u5f97\u597d\uff0c\u5f53\u53ef\u4fdd\u5e73\u5b89\u65e0\u4e8b\u3002 \u3000\u3000\u8ba8\u538c\u7684\u662f\u4e03\u7edd\u9053\u957f\uff0c\u4ed6\u4e0d\u4f46\u4e3b\u5f20\u6740\u4e86\u674e\u7237\uff0c\u4e5f\u4e3b\u5f20\u6740\u4e86\u4e8c\u516c\u5b50\u3002\u8fd9\u4e2a\u4eba\u6df1\u6c89\u3001\u9634\u9669\uff0c\u8c01 \u4e5f\u6599\u4e0d\u51c6\u4ed6\u4f1a\u4f5c\u4e9b\u4ec0\u4e48\u4e8b\u60c5\uff1f \u3000\u3000\u6211\u672c\u662f\u674e\u7237\u5e03\u4e0b\u7684\u4e00\u4e2a\u68cb\u5b50\uff0c\u770b\u6765\u4e5f\u5230\u4e86\u8be5\u8d77\u4f5c\u7528\u7684\u65f6\u5019\uff0c\u6211\u4f1a\u60f3\u529e\u6cd5\u5c3d\u5feb\u4f20\u51fa\u6d88\u606f\u3002 \u73b0\u5728\uff0c\u4f60\u8be5\u633d\u4f4f\u6211\u5230\u5367\u5ba4\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e0d\u77e5\u8005\u65e0\u7f6a\uff0c\u660e\u77e5\u6545\u72af\uff0c\u5c31\u6709\u4e9b\u7f6a\u4e0d\u53ef\u8d66\u4e86\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u53ef\u80fd\u6709\u4eba\u6765\u4e86\uff0c\u4ed6\u4f1a\u5728\u6697\u4e2d\u7aa5\u89c6\uff0c\u73b0\u5728\u662f\u4f5c\u620f\uff0c\u4e0d\u8fc7\u8981\u4f5c\u5f97\u903c\u771f\u4e00\u4e9b\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u53ea\u597d\u62b1\u4f4f\u4e86\u5982\u7389\u7684\u67f3\u8170\uff0c\u4e5f\u8bb8\u4ed6\u4e0d\u6562\u7528\u529b\uff0c\u5982\u7389\u5c31\u53ea\u597d\u65bd\u5c55\u51fa\u8d56\u5b57\u8bc0\u4e86\uff0c\u6574\u4e2a\u4eba \u5012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u4e16\u6c11\u53ea\u597d\u52a0\u5927\u529b\u91cf\uff0c\u62b1\u7740\u5982\u7389\u59d1\u5a18\u5165\u5367\u5ba4\u3002 \u3000\u3000\u884c\u5165\u4e86\u4e00\u5ea7\u5367\u5ba4\u4e2d\uff0c\u5982\u7389\u59d1\u5a18\u624d\u79bb\u5f00\u4e86\u4e16\u6c11\u7684\u6000\u62b1\uff0c\u9053\uff1a\u201c\u59be\u5a62\u6574\u7406\u5e8a\u8925\uff0c\u4e8c\u516c\u5b50\u7a0d\u5b89 \u52ff\u8e81\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u4e0d\u77e5\u8be5\u5982\u4f55\u7b54\u7406\uff0c\u53ea\u597d\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u71c3\u8d77\u706f\u706b\uff0c\u670d\u4f8d\u674e\u4e16\u6c11\u7761\u4e0b\u3002 \u3000\u3000\u5982\u7389\u624d\u7b11\u9053\uff1a\u201c\u59be\u5a62\u53bb\u70e7\u58f6\u8336\u6765\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u8eba\u5728\u5e8a\u4e0a\u6ca1\u6709\u52a8\uff0c\u5982\u7389\u59d1\u5a18\u5374\u662f\u81ea\u8bf4\u81ea\u8bdd\u81ea\u5df1\u884c\u52a8\uff0c\u53c8\u7184\u53bb\u4e86\u623f\u4e2d\u706f\u706b\uff0c\u8f6c\u8eab\u800c \u53bb\u3002 \u3000\u3000\u5982\u7389\u771f\u7684\u5e2e\u674e\u4e16\u6c11\u5bbd\u8863\u89e3\u5e26\u65f6\uff0c\u674e\u4e16\u6c11\u5c31\u7acb\u523b\u95ed\u4e0a\u773c\uff0c\u8fde\u770b\u4e5f\u4e0d\u6562\u518d\u770b\u4e00\u773c\u4e86\u3002\u65e2\u7136\u8bf4 \u660e\u4e86\u662f\u4f5c\u620f\uff0c\u4e5f\u4e0d\u6562\u518d\u62d2\u7edd\u3002 \u3000\u3000\u5982\u7389\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u540d\u9707\u5929\u4e0b\u7684\u674e\u4e8c\u516c\u5b50\uff0c\u539f\u6765\u5982\u6b64\u6015\u5973\u4eba\uff0c\u4faf\u95e8\u5b50\u5f1f\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4e0d \u6562\u9762\u5bf9\u5973\u8272\u7684\u4eba\uff0c\u53ef\u771f\u662f\u7b11\u8bdd\uff01\u201d \u3000\u3000\u542c\u5230\u5982\u7389\u7684\u811a\u6b65\u51fa\u8fdc\uff0c\u674e\u4e16\u6c11\u624d\u7741\u5f00\u4e86\u773c\u775b\u3002 \u3000\u3000\u5ba4\u4e2d\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5f00\u59cb\u601d\u7d22\u76ee\u524d\u7684\u5883\u51b5\u548c\u5e94\u5bf9\u4e4b\u6cd5\u3002 \u3000\u3000\u9996\u5148\u60f3\u5230\u4e86\u674e\u9756\u2014\u2014 \u3000\u3000\u679c\u7136\u662f\u957f\u8c0b\u6df1\u8651\u7684\u5927\u5e05\u4e4b\u624d\uff0c\u5904\u5904\u6709\u4f0f\u5175\uff0c\u73af\u73af\u6709\u63a5\u5e94\u3002\u8c01\u80fd\u60f3\u5230\u4ed6\u5df2\u5728\u866c\u9aef\u5ba2\u7684\u5185\u5e10 \u5973\u5a62\u4e2d\u5e03\u4e0b\u4e86\u5982\u7389\u8fd9\u9897\u68cb\u5b50\uff1f \u3000\u3000\u662f\u5426\u8fd8\u6709\u5462\uff1f \u3000\u3000\u4e00\u65e6\u5230\u4e86\u8eab\u9677\u7edd\u5883\u65f6\uff0c\u4f1a\u4e0d\u4f1a\u8fd8\u6709\u4f0f\u5175\u51fa\u73b0\uff1f \u3000\u3000\u674e\u9756\u6df1\u7231\u5f20\u51fa\u5c18\uff0c\u674e\u4e16\u6c11\u5341\u5206\u4e86\u89e3\uff0c\u4e24\u4eba\u8c08\u8bdd\u4e2d\uff0c\u674e\u9756\u66fe\u900f\u9732\u51fa\u4e0d\u5c11\u5fc3\u58f0\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u8ff7\u604b\u5251\u672f\uff0c\u5168\u5fc3\u6295\u6ce8\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u7adf\u4e5f\u80af\u5927\u529b\u5468\u5168\uff0c\u6682\u65f6\u653e\u5f03\u4e86\u4e89\u593a\u5929\u4e0b\u7684\u5e03\u7f72\u5de5\u4f5c\uff0c\u9690\u5c45\u4e8e\u534e\u5c71\u4e4b\u4e2d\uff0c\u4f20\u6388\u5f20\u51fa \u5c18\u7684\u5251\u6cd5\u3002 \u3000\u3000\u674e\u9756\u7adf\u4e5f\u80fd\u5fcd\u6027\u65ad\u60c5\uff0c\u6210\u5168\u5a07\u59bb\u7ec3\u5251\u7684\u5fc3\u613f\u3002 \u3000\u3000\u8fd9\u8981\u591a\u5927\u7684\u81ea\u5236\u529b\uff0c\u624d\u53ef\u4ee5\u505a\u5230\uff0c\u629b\u53bb\u70e6\u607c\u8d70\u6c5f\u6e56\uff0c\u5fcd\u4ee4\u5a07\u59bb\u548c\u522b\u4eba\u65e5\u4e45\u76f8\u5904\uff0c\u6765\u4e00\u4e2a \u773c\u4e0d\u89c1\u4e3a\u51c0\u3002 \u3000\u3000\u4e24\u4e2a\u76d6\u4e16\u8c6a\u6770\u6000\u5fd7\u82f1\u96c4\uff0c\u90fd\u4f5c\u4e86\u5f88\u5927\u7684\u727a\u7272\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f\u53ea\u4e3a\u8ba9\u5f20\u51fa\u5c18\u5251\u672f\u65e9\u6210\uff0c\u5fc3\u613f \u65e9\u507f\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5251\u672f\u5b66\u6210\u4e86\uff0c\u624d\u60f3\u5230\u662f\u81ea\u5df1\u628a\u4e08\u592b\u903c\u8d70\u4e86\u3002 \u3000\u3000\u674e\u4e16\u6c11\u601d\u7eea\u98de\u8f6c\uff0c\u53c8\u60f3\u5230\u957f\u5b89\u540e\u5bab\u7684\u8881\u5b9d\u513f\uff0c\u7edb\u5e10\u6625\u60c5\u5a07\u65e0\u9650\uff0c\u5e38\u5728\u7687\u4e0a\u6000\u62b1\u4e2d\uff1b\u8fd8\u4f1a \u60f3\u5230\u4ed6\u674e\u4e16\u6c11\u5417\uff1f \u3000\u3000\u8fd8\u6709\u90a3\u4ed9\u5973\u822c\u7684\u8881\u7d2b\u70df\uff0c\u672f\u6cd5\u7cbe\u6df1\uff0c\u6c14\u52bf\u51cc\u4eba\uff0c\u542c\u674e\u6df3\u98ce\u548c\u8881\u5929\u7f61\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u662f\u4e24\u4e2a \u4eba\u5408\u8d77\u6765\u4e5f\u672a\u5fc5\u662f\u5979\u7684\u654c\u624b\u3002 \u3000\u3000\u4f46\u52a0\u4e0a\u674e\u9756\u5462\uff1f \u3000\u3000\u53cc\u65b9\u603b\u662f\u96be\u514d\u4e00\u6218\uff0c\u6709\u6ca1\u6709\u529e\u6cd5\u4f7f\u53cc\u65b9\u7684\u51b2\u7a81\u5316\u89e3\u5f00\u53bb\uff1f\u5982\u679c\u628a\u8881\u7d2b\u70df\u5f15\u5411\u866c\u9aef\u5ba2\uff0c\u4e00 \u573a\u5927\u6218\u4e0b\u6765\uff0c\u53c8\u4f1a\u662f\u8c01\u80dc\u8c01\u8d25\uff1f \u3000\u3000\u542c\u5f20\u51fa\u5c18\u548c\u5982\u7389\u4e24\u4eba\u4e00\u756a\u8a00\u8bed\uff0c\u8bc1\u660e\u81ea\u5df1\u7684\u770b\u6cd5\u4e0d\u9519\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u53ef\u4e3a\u6c5f\u6e56\u5927\u4fa0\uff0c\u4ee5\u4ed6\u5bf9\u5f20\u51fa\u5c18\u7231\u604b\u4e4b\u6df1\uff0c\u5374\u80fd\u6446\u5728\u8eab\u8fb9\u4e00\u5e74\u591a\uff0c\u5fcd\u4e0b\u4e0d\u52a8\uff0c\u53ef\u8d44 \u4e3a\u8bc1\uff0c\u7ea6\u675f\u4ed6\u7684\u529b\u91cf\u4e0d\u662f\u662f\u975e\u9053\u5fb7\uff0c\u662f\u4e00\u80a1\u4fa0\u4e49\u4e4b\u6c14\u3002\u8fd9\u80a1\u6c14\u80fd\u7ef4\u6301\u591a\u4e45\uff0c\u5f88\u96be\u9884\u6599\uff1f\u5fc3\u8d3c \u4e0d\u53bb\uff0c\u7ec8\u96be\u6709\u8822\u52a8\u4e4b\u65e5\u3002 \u3000\u3000\u6709\u4e00\u5929\u8272\u6b32\u4e4b\u5ff5\u8d85\u8d8a\u8fc7\u4fa0\u4e49\u4e4b\u6c14\uff0c\u90a3\u5c31\u516d\u4eb2\u4e0d\u8ba4\u4e86\u3002 \u3000\u3000\u4e00\u65e6\u4ed6\u767b\u4e0a\u7687\u5e1d\u4e4b\u4f4d\uff0c\u5929\u4e0b\u90fd\u5165\u638c\u63e1\u4e2d\uff0c\u627f\u5929\u4e4b\u547d\u4e3a\u6240\u6b32\u4e3a\uff0c\u60f3\u5f53\u7136\u8033\uff0c\u9006\u6211\u8005\u6b7b\uff0c\u66b4 \u9996\u7684\u8086\u8650\u6050\u4e0d\u5728\u79e6\u7687\u4e4b\u4e0b\u4e86\u3002 \u3000\u3000\u5149\u8292\u95ea\u52a8\uff0c\u6b65\u5c65\u58f0\u54cd\uff0c\u6253\u65ad\u4e86\u674e\u4e16\u6c11\u7684\u601d\u6f6e\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6025\u6025\u95ed\u4e0a\u773c\u775b\uff0c\u88c5\u7761\u4e86\u3002 \u3000\u3000\u5982\u7389\u4e00\u624b\u63d0\u7740\u7eb1\u706f\uff0c\u4e00\u624b\u7aef\u7740\u8336\u76d8\uff0c\u884c\u5165\u4e86\u5367\u5ba4\u4e4b\u4e2d\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e0d\u6562\u7741\u773c\u770b\uff0c\u88c5\u51fa\u5747\u5300\u7684\u547c\u5438\uff0c\u7761\u7740\u4e86\u3002 \u3000\u3000\u5982\u7389\u5012\u4e86\u4e00\u676f\u8336\uff0c\u6367\u5230\u5e8a\u8fb9\u3002 \u3000\u3000\u770b\u4e16\u6c11\u7761\u5f97\u9999\u751c\uff0c\u6539\u53d8\u4e86\u5fc3\u610f\u3002\u653e\u4e0b\u8336\u676f\uff0c\u5bbd\u519c\u89e3\u5e26\uff0c\u8fde\u4eb5\u8863\u4e5f\u8131\u5149\u4e86\uff0c\u4f3c\u662f\u8bda\u5fc3\u8981\u5e03 \u65bd\u8272\u8eab\u3002 \u3000\u3000\u534e\u5c71\u4e00\u79c0\u5cf0\u9876\uff0c\u591c\u5bd2\u88ad\u4eba\u3002 \u3000\u3000\u5982\u7389\u4e00\u6293\u88ab\u5b50\uff0c\u94bb\u5165\u674e\u4e16\u6c11\u7684\u88ab\u7a9d\u4e2d\u4e86\u3002 \u3000\u3000\u5979\u5e2e\u674e\u4e16\u6c11\u8131\u8863\u670d\u65f6\uff0c\u5012\u672a\u8131\u5f97\u5168\u8eab\u8d64\u88f8\uff0c\u4f46\u5982\u7389\u81ea\u5df1\u5374\u8131\u5f97\u4e00\u4e1d\u4e0d\u6302\u3002 \u3000\u3000\u674e\u4e16\u6c11\u611f\u89c9\u5230\u4e00\u4e2a\u5149\u6ed1\u7684\u5a07\u8eaf\u504e\u4e86\u4e0a\u6765\uff0c\u9f3b\u606f\u95f4\u4e5f\u95fb\u5230\u4e00\u9635\u5c11\u5973\u5e7d\u9999\u3002 \u3000\u3000\u6ca1\u6709\u6311\u9017\uff0c\u4e5f\u6ca1\u6709\u5f02\u5e38\u4e3e\u52a8\u3002 \u3000\u3000\u597d\u50cf\u504e\u5728\u674e\u4e16\u6c11\u7684\u6000\u4e2d\u7761\u89c9\uff0c\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002 \u3000\u3000\u5982\u7389\u5f88\u5feb\u7761\u719f\u4e86\u3002 \u3000\u3000\u674e\u4e16\u6c11\u53ef\u82e6\u4e0d\u582a\u8a00\u4e86\uff0c\u4ed6\u4e0d\u80fd\u63a8\u5f00\u5982\u7389\uff0c\u4e5f\u4e0d\u80fd\u8f97\u8f6c\u53cd\u4fa7\uff0c\u53ea\u597d\u4e00\u76f4\u88c5\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u56db\u66f4\u65f6\u5206\uff0c\u674e\u4e16\u6c11\u624d\u771f\u7684\u7761\u53bb\u3002 \u3000\u3000\u9192\u6765\u65f6\uff0c\u5982\u7389\u65e9\u5df2\u8d77\u5e8a\uff0c\u6367\u6765\u4e86\u9762\u6c34\u3001\u9762\u5dfe\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\u7761\u5f97\u597d\u554a\uff01\u201d\u5982\u7389\u5e26\u7740\u4e24\u988a\u7ea2\u6655\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6d17\u4e2a\u8138\u6211\u53bb\u51c6\u5907\u65e9\u70b9\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\u51fa\u5367\u5ba4\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7acb\u523b\u4e00\u633a\u800c\u8d77\uff0c\u5168\u8eab\u4e0a\u4e0b\u770b\u4e00\u770b\uff0c\u4e00\u5207\u5982\u5e38\u3002 \u3000\u3000\u7a7f\u4e0a\u8863\u9774\u6d17\u8fc7\u8138\uff0c\u5982\u7389\u5df2\u6367\u7740\u65e9\u70b9\u8d70\u8fdb\u6765\u3002 \u3000\u3000\u5982\u7389\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u4ec0\u4e48\u90fd\u6709\uff0c\u53ea\u662f\u6211\u7684\u624b\u827a\u5dee\uff0c\u4e8c\u516c\u5b50\u5c06\u5c31\u4e00\u987f\uff0c\u4eca\u5929\u6211\u628a\u53a8\u5e08\u8c03\u4e0a \u6765\uff0c\u8ba9\u4ed6\u4eec\u4f4f\u5728\u8fd9\u91cc\uff0c\u4e8c\u516c\u5b50\u98df\u5bbf\u5c31\u65b9\u4fbf\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u5947\u602a\u5440\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fde\u53a8\u5e08\u4e5f\u7559\u4e0b\uff0c\u4e0d\u662f\u8981\u628a\u6211\u56da\u5c45\u4e8e\u6b64\u5417\uff1f\u201d \u3000\u3000\u201c\u6015\u4f60\u5bb3\u7f9e\u554a\uff01\u4e5f\u6015\u4eba\u591a\u5634\u6742\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u8ba9\u4f60\u4e00\u4e2a\u4eba\u5b64\u72ec\u4f4f\u5728\u8fd9\u5e62\u5927\u623f\u5b50\u4e2d\uff0c\u4f60\u624d \u4f1a\u611f\u89c9\u5230\u5bc2\u5bde\u96be\u8010\uff0c\u4e03\u7edd\u9053\u957f\u7684\u63a8\u65ad\u662f\u4f60\u53ef\u4ee5\u5fcd\u8010\u4e03\u5929\u3002\u4e03\u5929\u540e\uff0c\u4f60\u5c31\u4f1a\u53ec\u4eba\u966a\u5bbf\uff0c\u4e0d\u662f\u4e3a \u8272\u6b32\uff0c\u662f\u8981\u6392\u9063\u9ad8\u5c71\u4e4b\u9876\u3001\u6df1\u5927\u5b85\u9662\u4e2d\u8fd9\u4efd\u51b7\u6e05\u7684\u5bc2\u5bde\u3002\u201d \u3000\u3000\u201c\u597d\u6bd2\u7684\u624b\u6bb5\u554a\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u53ea\u8981\u6211\u62db\u4eba\u5165\u5bab\uff0c\u90a3\u6015\u662f\u79c9\u70db\u8fbe\u65e6\uff0c\u4e5f\u662f\u8df3\u5230\u9ec4\u6cb3\u6d17 \u4e0d\u6e05\u4e86\u3002\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u5728\u6015\u4ec0\u4e48\u5462\uff1f\u201d\u5982\u7389\u7b11\u9053\uff1a\u201c\u8d35\u80c3\u516c\u5b50\u3001\u4faf\u95e8\u5c11\u7237\uff0c\u54ea\u4e00\u4e2a\u6ca1\u6709\u5a62\u59be\u4f34 \u5bbf\u3001\u6b4c\u59ec\u4f8d\u5bdd\uff0c\u6b64\u4e8b\u4eba\u4eba\u7686\u77e5\uff0c\u5c31\u7b97\u4e8c\u516c\u5b50\u628a\u8fd9\u534e\u5c71\u884c\u5bab\u7684\u6b4c\u5973\u821e\u59ec\u2014\u4e00\u62db\u6765\u5f85\u5bdd\uff0c\u4f20\u626c\u51fa \u53bb\u4e5f\u4e0d\u81f4\u635f\u5bb3\u5230\u4e8c\u516c\u5b50\u7684\u76db\u540d\u554a\uff01\u201d \u3000\u3000\u201c\u8bf4\u5f97\u4e0d\u9519\uff0c\u5982\u7389\u59d1\u5a18\u5bf9\u738b\u5e9c\u4faf\u95e8\u4e2d\u6deb\u9038\u8150\u5316\u60c5\u4e8b\u77e5\u6653\u5f97\u4e0d\u5c11\uff0c\u4e16\u6c11\u957f\u4e8e\u4faf\u95e8\uff0c\u5927\u54e5\u5efa \u6210\u53c8\u662f\u6b64\u9053\u4e2d\u7684\u9ad8\u624b\u3002 \u3000\u3000\u592a\u539f\u4faf\u5e9c\u4e2d\u7684\u6b4c\u59ec\u4f8d\u5a62\uff0c\u90fd\u662f\u7cbe\u6311\u7ec6\u9009\u7684\u7f8e\u4eba\uff0c\u4e16\u6c11\u867d\u672a\u6c89\u8ff7\u5176\u4e2d\uff0c\u4f46\u4e5f\u65e0\u6392\u65a5\u7684\u5fc3 \u7406\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e8c\u516c\u5b50\u4e5f\u9009\u8fc7\u5979\u4eec\u4f8d\u5bdd\u4e86\uff0c\u662f\u5426\u5e38\u5e38\u5982\u6b64\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u8fc7\u4e24\u4e09\u6b21\u7684\u7ecf\u9a8c\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u540e\u6765\u56e0\u4e3a\u516c\u52a1\u5306\u5fd9\uff0c\u673a\u4f1a\u4e0d\u591a\uff0c\u4e5f\u5c31\u4e0d\u5f39\u6b64\u8c03\u4e45 \u77e3\uff01\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u6628\u591c\u6211\u88f8\u8eab\u6295\u6000\uff0c\u4e8c\u516c\u5b50\u7761\u5f97\u597d\u9999\uff0c\u4e0d\u8fc7\u6211\u77e5\u9053\u4f60\u6ca1\u6709\u771f\u7684\u7761\u7740\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u9053\uff1a\u201c\u56e0\u65c5\u9014\u52b3\u7d2f\uff0c\u4e0a\u5e8a\u540e\u7761\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u4ece\u672a\u5b8c\u5168\u6e05\u9192\uff0c\u603b\u5728\u534a \u7761\u534a\u9192\u4e2d\u3002\u201d \u3000\u3000\u201c\u674e\u7237\u7559\u8fc7\u8bdd\uff0c\u8981\u6211\u4ece\u4f17\u968f\u4fd7\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u674e\u7237\u4e0d\u4f1a\u78b0\u6211\uff0c\u90a3\u662f\u56e0\u4e3a\u5f20\u59d1\u5a18\u3002\u6211\u80fd\u4fdd \u4f4f\u6b64\u8eab\uff0c\u5c1a\u672a\u6210\u6b8b\u82b1\u8d25\u67f3\uff0c\u662f\u6b64\u5730\u7684\u73af\u5883\u5355\u7eaf\uff0c\u5927\u738b\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a\u4e3d\u5f71\uff0c\u6211\u4eec\u5c31\u6210\u4e86\u864e\u53e3\u4f59 \u751f\u5566\uff01 \u3000\u3000\u4f46\u6211\u77e5\u9053\u4e0d\u53ef\u80fd\u6c38\u8fdc\u5982\u6b64\uff0c\u65e2\u96be\u514d\u84ec\u95e8\u5927\u5f00\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u9009\u4e2a\u5fc3\u4eea\u7684\u7537\u4eba\uff0c\u5949\u732e\u4e0a\u521d\u591c\u7684 \u8d1e\u64cd\uff1f \u3000\u3000\u4e8c\u516c\u5b50\u7adf\u7136\u80fd\u6295\u6000\u4e0d\u4e71\uff0c\u5982\u7389\u4e5f\u672a\u7ecf\u4e91\u96e8\u60c5\u3001\u4e0d\u4f1a\u6311\u60c5\u52a8\u541b\u5fc3\uff0c\u767d\u767d\u7684\u865a\u6e21\u4e86\u4e00\u591c\uff0c\u4f46 \u80fd\u62b1\u7740\u4e8c\u516c\u5b50\u5374\u4e5f\u7761\u5f97\u9999\u751c\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u975e\u94c1\u77f3\u4eba\uff0c\u5c82\u4f1a\u5168\u65e0\u6240\u89c9\uff1f\u53ea\u662f\u5fc3\u7ed3\u6c89\u91cd\uff0c\u5c31\u63d0\u4e0d\u8d77\u8fd9 \u4efd\u5174\u81f4\u4e86\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u5c0f\u5a62\u88f8\u4f53\u6295\u6000\uff0c\u4e8c\u516c\u5b50\u80fd\u591f\u4e0d\u53ca\u4e8e\u4e71\uff0c\u8fd9\u4efd\u5b9a\u529b\uff0c\u8db3\u4ee5\u6bd4 \u7f8e\u524d\u8d24\uff0c\u4e0d\u8fc7\uff0c\u5371\u9669\u554a\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e0d\u6562\u63a0\u524d\u8d24\u7f8e\u540d\u3001\u4f46\u5371\u9669\u4f55\u5728\uff1f\u6211\u5c31\u60f3\u4e0d\u660e\u767d\u4e86\uff0c\u96be\u9053\u975e\u8981\u4e91\u96e8\u5deb\u5c71\u95f9\u4e00 \u573a\uff0c\u624d\u80fd\u5f97\u4fdd\u5e73\u5b89\u5417\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u5982\u7389\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6015\u88ab\u4f60\u8bf4\u5bf9\u4e86\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\uff01\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u6ca1\u6709\u8d70\uff0c\u8fd8\u7559\u5728\u5c71\u4e0b\u8425\u5be8\u4e2d\uff0c\u90a3\u8868\u793a\u4ed6\u6709\u4e8b\u8981\u529e\uff0c\u4e8c\u516c\u5b50\u81ea\u7136\u662f\u4e8b \u4ef6\u4e2d\u7684\u4e3b\u89d2\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u7591\u7136\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4e03\u7edd\u9053\u957f\u4e0d\u4f4f\u5728\u534e\u9634\u519b\u8425\uff0c\u4ed6\u4f4f\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u957f\u5b89\u57ce\u4e2d\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u90a3\u4e00\u5ea7\u9053\u89c2\u8be6\u7ec6\u7684\u5730\u5740\u5c0f\u5a62\u4e0d\u6e05\u695a\u4e5f\u4e0d\u4fbf\u6253\u542c\u3002\u4ed6\u662f\u957f\u5b89\u5730 \u533a\u7684\u4e3b\u8111\uff0c\u957f\u5b89\u57ce\u5185\u3001\u90ca\u5916\u6f5c\u4f0f\u6709\u4e0d\u5c11\u7684\u7532\u5175\uff0c\u8fd9\u4e9b\u4eba\u90fd\u5728\u4e03\u7edd\u9053\u957f\u7684\u7edf\u5e05\u6307\u6325\u4e4b\u4e0b\uff0c\u5982\u6b64 \u91cd\u8981\u5730\u65b9\u804c\u4f4d\uff0c\u4e03\u7edd\u9053\u957f\u5374\u6ede\u7559\u4e0d\u5f52\uff0c\u8fd9\u534e\u9634\u5730\u533a\u5e94\u8be5\u6709\u66f4\u91cd\u5927\u7684\u4e8b\u60c5\u672a\u4e86\uff0c\u4f46\u662f\u9664\u4e86\u4f60\u4e8c \u516c\u5b50\u4e4b\u5916\uff0c\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5b64\u8eab\u4e00\u4eba\uff0c\u6b66\u529f\u5e73\u5eb8\uff0c\u56f0\u5728\u534e\u5c71\u4e00\u79c0\u5cf0\u7edd\u9876\u4e4b \u4e0a\uff0c\u5df2\u662f\u4e00\u7b79\u83ab\u5c55\uff0c\u8fd8\u7528\u5f97\u7740\u4e03\u7edd\u9053\u957f\u7559\u4e0b\u6765\u76d1\u89c6\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u76d1\u89c6\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u7531\u949f\u6728\u9b41\u5b88\u7740\u5bab\u95e8\uff0c\u5343\u519b\u4e07\u9a6c\u4e5f\u90fd\u96be\u6551\u4f60\u51fa\u53bb\uff0c\u4e03\u7edd\u9053\u957f\u5728 \u8fd9\u91cc\u6709\u4e24\u4e2a\u91cd\u5927\u76ee\u7684\uff0c\u4e00\u662f\u7814\u7a76\u4f60\u8fd9\u4e2a\u4eba\uff0c\u4e8c\u662f\u4e0b\u624b\u5bb3\u4e86\u4f60\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u503c\u5f97\u5982\u6b64\u91cd\u89c6\u5417\uff1f\u201d \u3000\u3000\u201c\u5927\u738b\u76ee\u4e2d\u65e0\u4eba\uff0c\u773c\u7a7a\u56db\u6d77\uff0c\u662f\u4e0d\u600e\u4e48\u91cd\u89c6\u4f60\u3002\u4e0d\u8fc7\uff0c\u4e03\u7edd\u9053\u957f\u7684\u770b\u6cd5\u4e0d\u540c\uff0c\u4ed6\u8ba4\u4e3a\u5927 \u968b\u671d\u6c14\u6570\u5c06\u5c3d\uff0c\u65e5\u540e\u80fd\u548c\u5927\u738b\u4e89\u593a\u6c5f\u5c71\u7684\uff0c\u53ea\u6709\u4f60\u4e8c\u516c\u5b50\u548c\u674e\u9756\u3002 \u3000\u3000\u6240\u4ee5\u4e03\u7edd\u9053\u957f\u4e00\u76f4\u4e3b\u5f20\u5148\u53bb\u7978\u6839\uff0c\u7136\u540e\u518d\u62ff\u4e0b\u957f\u5b89\u4e0d\u8fdf\u3002\u8fd9\u7978\u6839\u6307\u7684\u5c31\u662f\u4f60\u4e8c\u516c\u5b50\u548c\u674e \u9756\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e03\u7edd\u9053\u957f\u6709\u4e9b\u8be1\u5f02\u795e\u79d8\uff0c\u5374\u4e5f\u662f\u4e00\u4e2a\u975e\u5e38\u5389\u5bb3\u7684\u4eba\u7269\uff0c\u4e0d \u53ef\u8f7b\u89c6\u3002\u201d \u3000\u3000\u201c\u5355\u662f\u88f8\u5973\u5728\u6000\uff0c\u5fcd\u4e0b\u4e0d\u52a8\uff0c\u8fd9\u4e00\u9879\u975e\u5e38\u4e4b\u5904\uff0c\u5c31\u65e0\u6cd5\u9003\u8fc7\u4e03\u7edd\u9053\u957f\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u201c\u9636\u4e0b\u4e4b\u56da\uff0c\u7b3c\u4e2d\u4e4b\u9e1f\uff0c\u9003\u8fc7\u6bd2\u624b\u7684\u673a\u4f1a\u662f\u975e\u5e38\u6e3a\u5c0f\u4e86\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e0d\u77e5\u4e03\u7edd\u9053\u957f \u6740\u6211\u7684\u624b\u6cd5\u662f\u660e\u53d6\u5462\uff1f\u8fd8\u662f\u6697\u6740\uff1f\u201d \u3000\u3000\u201c\u82e5\u51b3\u5b9a\u8981\u6740\u4f60\u4e86\uff0c\u4ec0\u4e48\u624b\u6bb5\u4ed6\u90fd\u80fd\u4f7f\u5f97\u51fa\u6765\u3002\u7559\u4e0b\u6765\u6697\u4f5c\u89c2\u5bdf\uff0c\u5c31\u8868\u793a\u4ed6\u8fd8\u672a\u4e0b\u5b9a\u51b3 \u5fc3\u3002\u6240\u4ee5\u8981\u8bbe\u8ba1\u9003\u907f\uff0c\u8fd9\u662f\u4e00\u573a\u51b7\u9177\u7684\u6597\u667a\u6bd4\u8d5b\uff0c\u4f60\u5fc5\u9700\u6d3b\u4e0b\u53bb\uff0c\u4e0d\u60dc\u59d4\u5c48\u6c42\u5168\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9ed8\u7136\u4e86\u3002 \u3000\u3000\u5982\u7389\u53c8\u9053\uff1a\u201c\u5982\u679c\u6362\u4e86\u4e00\u4e2a\u4f8d\u5a62\u4e0e\u541b\u540c\u5e8a\uff0c\u4e8c\u516c\u5b50\u5c31\u4e0d\u80fd\u8868\u73b0\u5f97\u751f\u6000\u4e0d\u4e71\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u8981\u5b58\u6740\u6211\u4e4b\u5fc3\uff0c\u4f55\u60a3\u65e0\u8bcd\uff0c\u53c8\u4f55\u82e6\u5982\u6b64\u7684\u82b1\u8d39\u5fc3\u673a\u3002\u201d \u3000\u3000\u201c\u4e03\u7edd\u9053\u957f\u6740\u4f60\u4e4b\u5fc3\u867d\u5f88\u575a\u5b9a\uff0c\u4f46\u4ed6\u8981\u627e\u4e00\u4e2a\u8bf4\u670d\u5927\u738b\u548c\u5f20\u59d1\u5a18\u7684\u7406\u7531\u3002\u201d\u5982\u7389\u9053\uff1a \u201c\u5982\u679c\u4ed6\u5728\u4e09\u4e94\u65e5\u5185\u627e\u4e0d\u51fa\u7406\u7531\uff0c\u5c31\u5fc5\u9700\u79bb\u5f00\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u201c\u627e\u4e0d\u51fa\u7406\u7531\uff0c\u4e5f\u53ef\u4ee5\u5631\u5490\u4eba\u6697\u4e2d\u4e0b\u624b\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u996e\u98df\u8336\u6c34\uff0c\u523a\u5ba2\u6697\u7b97\uff0c\u5b9e\u662f\u9632 \u4e0d\u80dc\u9632\u3002\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u4f60\u597d\u50cf\u7518\u613f\u8ba4\u547d\uff0c\u6597\u5fd7\u4e0d\u5f3a\u554a\uff01\u996e\u6c34\u4e0b\u6bd2\uff0c\u6211\u53ef\u4ee5\u5e2e\u4f60\u9632\u8303\uff0c \u523a\u5ba2\u6697\u7b97\u5148\u8981\u9003\u8fc7\u6728\u8001\u90a3\u5173\uff0c\u4ed6\u9632\u4f60\u9003\u8d70\uff0c\u4e5f\u9632\u4f60\u88ab\u4eba\u523a\u6740\uff0c\u4f55\u51b5\u4e09\u4e94\u65e5\u5185\u4e5f\u8bb8\u4f1a\u6709\u63f4\u519b\u8d76 \u5230\u554a\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u7cbe\u795e\u4e00\u632f\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u5bf9\uff0c\u4e89\u53d6\u4e00\u5929\u662f\u4e00\u5929\uff0c\u6597\u80dc\u4e00\u573a\u662f\u4e00\u573a\uff01\u201d \u3000\u3000\u5982\u7389\u5fc3\u4e2d\u5927\u559c\uff0c\u7b11\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u5982\u5c31\u6b64\u653e\u5f03\u4e86\uff0c\u5c0f\u5a62\u5982\u4f55\u5bf9\u674e\u7237\u4ea4\u4ee3\uff1f\u60f3\u4e00\u60f3\uff0c\u8981 \u5b9e\u73b0\u4f60\u535a\u5927\u7684\u62b1\u8d1f\uff0c\u4e00\u5b9a\u8981\u4ece\u6743\u5e94\u53d8\uff0c\u5fcd\u8fb1\u8d1f\u91cd\uff1b\u8eb2\u8fc7\u4e86\u8fd9\u4e00\u52ab\uff0c\u624d\u6709\u5e0c\u671b\u3002\u201d \u3000\u3000\u201c\u591a\u627f\u59d1\u5a18\u6307\u70b9\uff0c\u4e16\u6c11\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u5982\u7389\u7b11\u4e86\uff0c\u662f\u771f\u7684\u5f00\u5fc3\u4e4b\u7b11\uff0c\u4e5f\u89c9\u5f97\u505a\u4e86\u4e00\u4ef6\u5927\u4e8b\uff0c\u8bf4\u670d\u4e86\u674e\u4e16\u6c11\u4e5f\u7b97\u5bf9\u674e\u9756\u6709\u4e86\u4ea4\u4ee3 \u4e86\u3002 \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u98ce\u4e91\u6f5c\u9f99 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u516b \u56de\u3000\u5982\u7389\u4f8d\u5bdd \u3000\u3000\u534e\u5c71\u884c\u5bab\u4e2d\u4e0d\u518d\u51b7\u6e05\u51c4\u51c9\uff0c\u4e09\u4f4d\u53a8\u5e08\u3001\u4e24\u4f4d\u7167\u987e\u674e\u4e16\u6c11\u751f\u6d3b\u7684\u4f8d\u5a62\u642c\u5165\u4e86\u884c\u5b98\uff0c\u4e0d\u8bba\u65e5 \u591c\uff0c\u53a8\u623f\u4e2d\u90fd\u53ef\u4ee5\u4f9b\u5e94\u7f8e\u9152\u4f73\u80b4\u3001\u9999\u8317\u7ec6\u70b9\u3002 \u3000\u3000\u5341\u4e8c\u4f4d\u80fd\u6b4c\u5584\u821e\u7684\u5bab\u59ec\u4e5f\u5e38\u88ab\u674e\u4e16\u6c11\u53ec\u5165\u884c\u5bab\uff0c\u4f8d\u5949\u9152\u5bb4\u3002 \u3000\u3000\u5982\u7389\u7b2c\u4e8c\u6b21\u5165\u5bab\u4f34\u5bbf\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7684\u884c\u52a8\u653e\u5f00\u4e86\uff0c\u4f46\u5fc3\u7ed3\u672a\u89e3\u3002\u53ec\u4eba\u966a\u5bbf\u7684\u4e8b\uff0c\u603b\u662f\u4f5c\u4e0d\u51fa\u6765\uff0c\u6240\u4ee5\u53c8\u8bf7\u4e86\u5982\u7389\u59d1 \u5a18\u6765\u3002 \u3000\u3000\u5982\u7389\u5df2\u6709\u8fc7\u4e00\u6b21\u7ecf\u9a8c\uff0c\u884c\u52a8\u8d77\u6765\u8001\u7ec3\u591a\u4e86\uff0c\u4e5f\u6446\u8131\u7f9e\u6127\u7684\u56f0\u6270\u3002 \u3000\u3000\u5148\u670d\u4f8d\u674e\u4e16\u6c11\u6c90\u6d74\u66f4\u8863\uff0c\u9001\u4ed6\u4e0a\u5e8a\u3002\u518d\u81ea\u5df1\u6c90\u6d74\u4e00\u756a\uff0c\u88f9\u7740\u4e00\u6761\u5927\u68c9\u888d\uff0c\u7f13\u7f13\u8fdb\u5165\u4e86\u5367 \u5ba4\u3002 \u3000\u3000\u4e00\u8131\u68c9\u888d\uff0c\u5f00\u95e8\u89c1\u5c71\uff0c\u5168\u8eab\u4e00\u4e1d\u4e0d\u6302\u7684\u6eda\u5165\u4e86\u88ab\u7a9d\u4e2d\u3002 \u3000\u3000\u6709\u8fc7\u4e86\u4e00\u6b21\u76f8\u62e5\u800c\u5367\u7684\u7ecf\u9a8c\uff0c\u674e\u4e16\u6c11\u4e5f\u65e0\u6cd5\u518d\u884c\u62d2\u7edd\u3002 \u3000\u3000\u5982\u7389\u59d1\u5a18\u662f\u65e9\u5df2\u7b97\u8ba1\u597d\u4e86\uff0c\u53cc\u624b\u4e00\u62b1\uff0c\u4e00\u4e2a\u5149\u6ed1\u7684\u80f4\u4f53\u7d27\u7d27\u5730\u8d34\u5728\u4e86\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u5982\u7389\uff0c\u6211\u60f3\u54b1\u4eec\u6765\u4e2a\u541b\u5b50\u534f\u5b9a\u2026\u2026\u201d \u3000\u3000\u5982\u7389\u6253\u65ad\u4e86\u674e\u4e16\u6c11\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u5148\u542c\u6211\u8bf4\u51e0\u53e5\u8bdd\uff0c\u518d\u8c08\u4f60\u7684\u541b\u5b50\u534f\u5b9a\u5982\u4f55\uff1f \u4f60\u751f\u6d3b\u7684\u8f6c\u53d8\uff0c\u5df2\u6210\u5927\u738b\u3001\u5f20\u59d1\u5a18\u548c\u4e03\u7edd\u9053\u957f\u7684\u8bdd\u9898\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u4ed6\u4eec\u8ba5\u7b11\u6211\uff0c\u662f\u5427\uff01\u7b11\u6211\u674e\u4e16\u6c11\u662f\u4f2a\u541b\u5b50\u3001\u5047\u6b63\u7ecf\u3002\u201d \u3000\u3000\u201c\u5012\u6ca1\u6709\u90a3\u4e48\u4e25\u91cd\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u5927\u738b\u5bf9\u5f20\u59d1\u5a18\u8bf4\uff0c\u5929\u4e0b\u7684\u4e4c\u9e26\u4e00\u822c\u9ed1\uff0c\u674e\u4e16\u6c11\u7ea8\u7ed4\u5b50 \u5f1f\uff0c\u73a9\u4e50\u60ef\u4e86\uff0c\u53d7\u4e0d\u5f97\u4e00\u70b9\u632b\u6298\uff0c\u4e00\u9047\u5230\u632b\u6298\uff0c\u96c4\u5fc3\u58ee\u5fd7\u5c31\u5168\u62a5\u9500\u4e86\u3002\u4e0d\u8981\u4e00\u4e2a\u6708\uff0c\u4ed6\u5c31\u4f1a \u81ea\u52a8\u5c48\u819d\u6295\u964d\u4e86\u3002\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u5728\u4e4e\u7684\u4f3c\u4e4e\u662f\u5f20\u51fa\u5c18\u7684\u770b\u6cd5\u3002 \u3000\u3000\u201c\u5f20\u59d1\u5a18\u6ca1\u6709\u8bf4\u8bdd\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u5979\u7ef7\u7740\u4e00\u5f20\u8138\uff0c\u8c01\u4e5f\u4e0d\u77e5\u5979\u5fc3\u91cc\u60f3\u4e9b\u4ec0\u4e48\uff1f\u4e0d\u8fc7\uff0c\u8981 \u53bb\u63a8\u65ad\u3001\u8054\u60f3\uff0c\u90a3\u5c31\u53ef\u4ee5\u60f3\u5f88\u591a\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u53d1\u89c9\u4e86\u5982\u7389\u59d1\u5a18\u7684\u5e72\u7ec3\u591a\u667a\uff0c\u674e\u4e16\u6c11\u5bf9\u674e\u9756\u53c8\u591a\u4e86\u4e00\u5c42\u8ba4\u8bc6\uff0c\u662f\u4e2a\u53ef\u4ee5\u4e3a\u53cb\u800c\u7edd\u4e0d \u53ef\u4ee5\u4e3a\u654c\u7684\u4eba\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u4e2d\u611f\u6168\u4e07\u5343\uff0c\u4f38\u624b\u5728\u5982\u7389\u5149\u6ed1\u7684\u80cc\u4e0a\u8f7b\u8f7b\u62cd\u4e86\u4e24\u4e0b\uff0c\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u201c\u5927\u738b\u53ef\u4ee5\u8ba4\u4e3a\u5f20\u59d1\u5a18\u662f\u5fc3\u4e2d\u96be\u8fc7\uff0c\u674e\u9756\u4e00\u5411\u63a8\u5d07\u7684\u597d\u670b\u53cb\u2014\u2014\u540d\u6ee1\u5929\u4e0b\u7684\u674e\u4e8c\u516c\u5b50\uff0c \u600e\u4f1a\u5982\u6b64\u6ca1\u6709\u9aa8\u6c14\uff1f\u201d\u5982\u7389\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u51e0\u5929\u8fb0\u5149\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u9732\u51fa\u539f\u5f62\uff0c\u4ee5\u9152\u6d47\u6101\uff0c\u4e5f\u4e00\u5934 \u683d\u8fdb\u5973\u5165\u6000\u4e2d\uff0c\u53ef\u4ee5\u548c\u968b\u7080\u5e1d\u6bd4\u7f8e\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u95ee\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u8fd9\u4e48\u8bf4\u5417\uff1f\u201d \u3000\u3000\u5982\u7389\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5927\u738b\u6ca1\u6709\u8bf4\uff0c\u662f\u6211\u5728\u731c\u4ed6\u7684\u5fc3\u4e8b\u3002\u4f46\u4e03\u7edd\u9053\u957f\u7684\u770b\u6cd5\u5c31\u5b8c\u5168\u4e0d\u540c \u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u6709\u4ec0\u4e48\u770b\u6cd5\u5462\uff1f\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u53ef\u4e5f\u662f\u4f60\u731c\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6b21\u4e0d\u662f\u731c\u7684\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u662f\u4e03\u7edd\u9053\u957f\u4eb2\u53e3\u8bf4\u51fa\u6765\u7684\uff0c\u5e78\u597d\u4f60\u5ff5\u65e7\u591a\u60c5\uff0c\u53c8\u628a\u6211\u53ec \u6765\u966a\u5bbf\u3002\u5426\u5219\uff0c\u8981\u544a\u8bc9\u4f60\u8fd9\u4e9b\u4e8b\uff0c\u8fd8\u771f\u5f97\u8d39\u4e0a\u4e00\u756a\u5fc3\u601d\u5462\uff01\u4f20\u8bf4\u7684\u771f\u9f99\u5929\u5b50\u6709\u767e\u7075\u62a4\u4f51\uff0c\u53e4 \u4eba\u662f\u8bda\u4e0d\u6b3a\u6211\u3002\u201d \u3000\u3000\u201c\u4e8b\u60c5\u597d\u50cf\u5f88\u4e25\u91cd\u554a\uff01\u7a76\u7adf\u8bf4\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u4e0d\u76f8\u4fe1\u4f60\u8fd9\u4e48\u5feb\u5c31\u8ff7\u5931\u6539\u53d8\u4e86\uff0c\u4ed6\u8ba4\u4e3a\u4f60\u6709\u4e03\u5929\u5fcd\u53d7\u5bc2\u5bde\u7684\u8010\u529b\uff0c \u4f46\u4f60\u5fcd\u53d7\u4e0d\u5230\u4e09\u5929\u3002\u4ed6\u8ba4\u4e3a\u5176\u4e2d\u6709\u8bc8\uff0c\u6240\u4ee5\u51c6\u5907\u4e0a\u5c71\u6765\u4eb2\u81ea\u8c03\u67e5\u4e00\u756a\uff0c\u4ed6\u4f1a\u4e9b\u4ec0\u4e48\u672f\u6cd5\uff0c\u6211 \u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u7edd\u5bf9\u65e0\u80fd\u6297\u62d2\u4ed6\u3002 \u3000\u3000\u5982\u679c\u67e5\u51fa\u6211\u4e24\u6b21\u966a\u5bbf\uff0c\u4ecd\u662f\u5904\u5b50\u4e4b\u8eab\uff0c\u8fd9\u6863\u4e8b\u5c31\u5f88\u96be\u89e3\u8bf4\u6e05\u695a\u4e86\u3002\u6240\u4ee5\uff0c\u4eca\u665a\u4e0a\u6211\u60e8 \u4e86\uff0c\u5fc5\u5f97\u5fcd\u53d7\u7834\u74dc\u4e4b\u82e6\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f62\u52bf\u903c\u4eba\uff0c\u53ea\u597d\u59d4\u5c48\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u7a81\u7136\u6709\u6240\u8b66\u89c9\u5730\u9053\uff1a\u201c\u4e0d\u5bf9\u5440\uff01\u8fd9\u4e9b\u8bdd\u4e00\u5b9a\u5728\u866c\u9aef\u5ba2\u7684\u8425\u5e10\u4e2d\u8bf4\u7684\uff0c\u4f60\u600e\u4e48\u4f1a\u542c \u5230\u5462\uff1f\u4f60\u662f\u6b4c\u59ec\u9886\u73ed\uff0c\u548c\u5979\u4eec\u4f4f\u5728\u4e00\u8d77\uff0c\u867d\u4e0d\u5728\u8fd9\u5cf0\u9876\u4e4b\u4e0a\uff0c\u4f46\u597d\u50cf\u4e5f\u4e0d\u4f1a\u592a\u8fdc\u3002\u201d \u3000\u3000\u201c\u731c\u7684\u597d\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u5728\u5cf0\u4e0b\u5341\u4e08\u4e4b\u5904\uff0c\u6709\u4e00\u6761\u5c0f\u5f84\u5de6\u8f6c\u4e09\u4e08\uff0c\u6709\u5ea7\u5929\u7136\u77f3\u6d1e\uff0c\u6211\u4eec \u548c\u949f\u6728\u9b41\u90fd\u4f4f\u5728\u90a3\u91cc\uff0c\u949f\u6728\u9b41\u4f4f\u5728\u6d1e\u53e3\u4e00\u95f4\u77f3\u5ba4\u4e2d\uff0c\u6211\u4eec\u4f4f\u5728\u4e24\u4e08\u540e\u7684\u5927\u5c71\u6d1e\u4e2d\uff0c\u4f9d\u58c1\u67b6 \u5e8a\uff0c\u77f3\u5e18\u9694\u95f4\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u7b80\u964b\uff0c\u4f46\u6d1e\u91cc\u51ac\u6696\u590f\u51c9\uff0c\u4e0d\u82db\u6c42\uff0c\u8fd8\u53ef\u4ee5\u8fc7\u5f97\u53bb\u3002\u6211\u4eec\u770b\u4f3c\u81ea \u7531\uff0c\u4f46\u6d3b\u52a8\u7684\u5730\u76d8\u53ea\u9650\u5728\u6d1e\u5185\u3002 \u3000\u3000\u4e2a\u4eba\u82e5\u60f3\u51fa\u6d1e\u8d70\u8d70\uff0c\u5148\u5f97\u6728\u8001\u540c\u610f\u3002\u4f60\u89c1\u8fc7\u949f\u6728\u9b41\u4e86\uff0c\u90a3\u5f20\u8138\u5c31\u7b97\u7b11\u8d77\u6765\uff0c\u4e5f\u5e26\u4e86\u4e09\u5206 \u6050\u6016\u3002\u6240\u4ee5\uff0c\u5f88\u5c11\u6709\u4eba\u6562\u8bf7\u5047\u5916\u51fa\uff0c\u6211\u662f\u7ba1\u7406\u6b4c\u59ec\u7684\u73ed\u5934\uff0c\u90a3\u5c31\u66f4\u5f97\u81ea\u6211\u7ea6\u675f\u4e00\u6253\uff0c\u6240\u4ee5\u5f88 \u5c11\u6765\u770b\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u4e16\u6c11\u63d2\u8a00\u9053\uff1a\u201c\u5982\u7389\uff0c\u6211\u5f88\u60f3\u77e5\u9053\u4f60\u600e\u4e48\u77e5\u6653\u4e03\u7edd\u9053\u957f\u51c6\u5907\u4e0a\u5c71\u6765\uff0c\u4eb2\u81ea\u8c03\u67e5\u3002\u4f60\u662f \u9a97\u6211\u5462\uff1f\u8fd8\u662f\u522b\u6709\u6240\u56fe\uff1f\u201d \u3000\u3000\u201c\u662f\u771f\u7684\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u522b\u6709\u6240\u56fe\uff01\u8bf4\u5f97\u592a\u96be\u542c\u4e86\uff0c\u56fe\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5403\u4e8f\u7684\u662f\u6211\uff0c\u5fcd\u75bc\u7684\u4e5f\u662f\u6211\uff0c\u4e0d\u5c0f\u5fc3\u84dd\u7530\u79cd\u7389\uff0c\u4ee5\u540e\u7684\u751f\u80b2\u6559\u517b\u4e5f\u662f\u6211\uff0c\u600e\u4e48\u7b97\u6211\u90fd\u635e \u4e0d\u5230\u4ec0\u4e48\u597d\u5904\u3002\u6d88\u606f\u662f\u522b\u4eba\u4f20\u6765\u7684\uff0c\u8fd9\u4ef6\u79d8\u5bc6\u672c\u6765\u4e0d\u8be5\u544a\u8bc9\u4f60\u7684\uff0c\u8fd9\u548c\u4f60\u4e5f\u6ca1\u6709\u76f4\u63a5\u7684\u5173 \u7cfb\u3002\u53ef\u662f\uff0c\u73b0\u5728\u5982\u4e0d\u8bf4\u660e\u767d\uff0c\u5c31\u5f88\u96be\u89e3\u91ca\u6e05\u695a\u3002\u8bdd\u5230\u6b64\u5904\uff0c\u5c0f\u5a62\u5b9e\u5728\u4e0d\u80fd\u4e0d\u4f69\u670d\u674e\u7237 \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u548c\u674e\u9756\u6709\u5173\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u8bb6\u7136\u7740\u3002 \u3000\u3000\u201c\u662f\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u674e\u7237\u53d1\u89c9\u6211\u8c03\u5165\u884c\u5b98\uff0c\u7acb\u523b\u53c8\u6536\u4e86\u4e00\u4e2a\u8bb0\u540d\u5f1f\u5b50\uff0c\u662f\u5927\u738b\u8eab\u8fb9\u7684\u4f3a \u9152\u4e2b\u5934\uff0c\u540d\u53eb\u95fb\u9999\u3002\u5979\u6709\u7279\u522b\u7684\u672c\u9886\uff0c\u80fd\u77e5\u9152\u3001\u76d1\u9152\uff0c\u8d1f\u8d23\u66ff\u5927\u738b\u4f3a\u9152\u3001\u6536\u9152\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u597d\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8fd9\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u8c03\u8d70\u4e86\uff0c\u5979\u4f1a\u957f\u671f\u7684\u7559\u5728\u866c\u9aef\u5ba2\u7684\u8eab\u4fa7\u3002\u201d \u3000\u3000\u201c\u66f4\u91cd\u8981\u7684\u662f\u5979\u5f88\u81ea\u7531\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u9664\u4e86\u5927\u738b\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u7ba1\u5979\u3002\u53ea\u8981\u5927\u738b\u559d\u9152\u65f6\uff0c \u5979\u5728\u65c1\u8fb9\u3002\u5e73\u5e38\u4e0a\u5929\u5165\u5730\uff0c\u4e5f\u6ca1\u4eba\u7406\u4f1a\u5979\u3002 \u3000\u3000\u674e\u7237\u544a\u8bc9\u5979\uff0c\u6709\u91cd\u8981\u7684\u4e8b\u60c5\u901a\u77e5\u6211\u3002\u6628\u591c\u5979\u6765\u627e\u6211\uff0c\u8868\u9762\u4e0a\u662f\u4e3a\u949f\u6728\u9b41\u9001\u9152\u6765\uff0c\u90a3\u662f\u949f \u6728\u9b41\u6700\u7231\u559d\u7684\u9152\uff0c\u6728\u8001\u9ad8\u5174\u5f97\u4e0d\u5f97\u4e86\uff0c\u9001\u4e86\u5979\u4e00\u652f\u795e\u6728\u4ee4\u3002\u5979\u627e\u5230\u6211\uff0c\u8bf4\u51fa\u6697\u8bed\uff0c\u53c8\u6446\u4e86\u6697 \u8bb0\uff0c\u624d\u5f00\u59cb\u8c08\u5165\u6b63\u9898\u3002 \u3000\u3000\u5979\u542c\u674e\u7237\u8bf4\u8fc7\uff0c\u592a\u539f\u674e\u4e16\u6c11\u662f\u674e\u7237\u6700\u656c\u91cd\u7684\u670b\u53cb\uff0c\u6240\u4ee5\u7279\u522b\u8dd1\u6765\u544a\u8bc9\u6211\u3002\u4e8c\u516c\u5b50\uff0c\u5185\u60c5 \u5168\u90fd\u544a\u8bc9\u4f60\u4e86\uff0c\u4fe1\u4e5f\u5728\u4f60\uff0c\u4e0d\u4fe1\u4e5f\u5728\u4f60\uff1b\u6211\u8d70\u4e86\uff0c\u6211\u4f1a\u66ff\u4f60\u9009\u4e00\u4e2a\u6700\u6e29\u67d4\u59d1\u5a18\u6765\u966a\u4f60\uff0c\u4f60\u81ea \u5df1\u4fdd\u91cd\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u6380\u5f00\u88ab\u5b50\uff0c\u6293\u8fc7\u8863\u670d\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u91cc\u614c\u4e86\uff01 \u3000\u3000\u4ed6\u4e00\u4e0b\u5b50\u62c9\u8fc7\u5982\u7389\uff0c\u62d6\u5165\u88ab\u7a9d\u4e2d\uff0c\u9053\uff1a\u201c\u5929\u6c14\u51b7\u554a\uff01\u4f60\u4e0d\u6015\u53d7\u4e86\u51c9\uff01\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6500\u9f99\u9644\u51e4\u7684\u7528\u5fc3\uff0c\u6211\u81ea\u8350\u6795\u5e2d\uff0c\u53ea\u662f\u8868\u793a\u51fa\u6211\u5fc3\u4e2d\u7684\u4ef0 \u6155\u548c\u656c\u91cd\u3002\u5341\u4e5d\u5e74\u7389\u6d01\u51b0\u6e05\u7684\u8eab\u4f53\u80fd\u591f\u732e\u7ed9\u5fc3\u4eea\u7684\u7537\u4eba\uff0c\u6211\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u7389\uff0c\u7ed9\u674e\u9756\u554a\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4ed6\u5feb\u6765\u4e86\uff01\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u4e00\u4e2a\u8bb0\u540d\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u4f7f\u6211\u77ee\u4e86\u4e00\u8f88\uff0c\u6240\u4ee5\u674e\u7237\u4e0d\u4f1a\u8981\u6211\u3002\u4ed6\u4e5f\u6ca1\u6709\u5371\u673a\uff0c \u9700\u8981\u6211\u727a\u7272\u6e05\u767d\u5e2e\u52a9\u4ed6\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fae\u7b11\u9053\uff1a\u201c\u5982\u679c\u80fd\u7559\u7ed9\u4f60\u6765\u6765\u7684\u4e08\u592b\uff0c\u5c82\u4e0d\u662f\u4e00\u4ef6\u5927\u559c\u4e8b\uff01\u201d \u3000\u3000\u201c\u7559\u5f97\u5230\u5417\uff1f\u201d\u5982\u7389\u9053\uff1a\u201c\u4eba\u7684\u8fd0\u6c14\u4e0d\u4f1a\u6c38\u8fdc\u90a3\u4e48\u597d\uff0c\u5927\u738b\u4e00\u884c\u52a8\uff0c\u6211\u4eec\u5c31\u53ef\u80fd\u8ddf\u7740\u4ed6 \u79bb\u5f00\u534e\u5c71\u884c\u5bab\uff0c\u90a3\u65f6\u5019\u5927\u738b\u968f\u65f6\u90fd\u53ef\u80fd\u628a\u6211\u8d4f\u8d50\u7ed9\u4ed6\u7684\u90e8\u4e0b\uff0c\u4e0d\u8c08\u4e86\uff0c\u6211\u5f97\u8d70\u5566\uff01\u201d \u3000\u3000\u5982\u7389\u6323\u624e\u6b32\u53bb\uff0c\u674e\u4e16\u6c11\u7d27\u62b1\u4e0d\u653e\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u4f5c\u76d8\u7b97\u9053\uff1a\u201c\u5979\u4e24\u5ea6\u88f8\u4f53\u8350\u6795\uff0c\u6211\u5982\u518d\u62d2\u7edd\u5979\uff0c\u53ef\u80fd\u4f24\u5bb3\u5230\u5979\u4e00\u5bf8\u82b3\u5fc3\uff0c\u63a5\u7eb3 \u4e86\u5427\uff01\u65e2\u53ef\u575a\u5b9a\u5979\u5411\u6211\u5fc3\u610f\u3002\u4e5f\u53ef\u907f\u8fc7\u4e03\u7edd\u9053\u957f\u7684\u8ffd\u67e5\u3002\u201d \u3000\u3000\u4e00\u756a\u601d\u5fd6\u4e4b\u540e\uff0c\u674e\u4e16\u6c11\u653e\u5f00\u4e86\u624b\u811a\u3002 \u3000\u3000\u5982\u7389\u4e5f\u5982\u613f\u5f97\u507f\u4e86\u5fc3\u613f\uff0c\u867d\u7136\u5fcd\u53d7\u4e86\u4e00\u70b9\u8eab\u4f53\u4e0a\u7684\u5c0f\u75db\u82e6\uff0c\u4f46\u5fc3\u4e2d\u5145\u6ee1\u4e86\u6b22\u6b23\u3002 \u3000\u3000\u5982\u7389\u6ca1\u6709\u8bf4\u8c0e\uff0c\u679c\u7136\u4ecd\u662f\u5904\u5b50\u8eab\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e5f\u5f97\u5230\u4e86\u4e00\u79cd\u8212\u9002\u7684\u6ee1\u8db3\uff0c\u4f7f\u7d27\u5f20\u7684\u60c5\u7eea\u6d88\u9000\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u201c\u5982\u7389\uff0c\u8981\u597d\u597d\u7684\u4fdd\u91cd\u81ea\u5df1\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5929\u4e0b\u5e73\u5b9a\u4e4b\u540e\u6211\u4e00\u5b9a\u8981\u628a\u4f60\u6536\u5165\u5bab\u4e2d\u3002\u201d \u3000\u3000\u5982\u7389\u4f9d\u504e\u5165\u6000\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5982\u679c\u4e00\u5207\u987a\u5229\uff0c\u674e\u7237\u5f88\u53ef\u80fd\u5e26\u6211\u4eec\u6295\u6548\u5165\u4e8c\u516c\u5b50\u7684\u519b\u8425\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u4f1a\u8ba9\u674e\u9756\u628a\u4f60\u8c03\u6d3e\u5230\u6211\u7684\u8eab\u8fb9\uff0c\u4e0d\u8fc7\u90a3\u91cc\u7684\u751f\u6d3b\u5341\u5206\u8f9b\u82e6\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u53ef\u4ee5\u5e2e\u516c\u5b50\u6309\u6469\u7b4b\u9aa8\uff0c\u8212\u89e3\u5fe7\u95f7\uff0c\u8ba9\u4f60\u65e9\u5165\u68a6\u4e61\uff0c\u517b\u597d\u4f53\u80fd\uff0c\u4ee5\u5e94 \u4ed8\u6218\u9635\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u5982\u7389\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u518d\u78b0\u522b\u7684\u5973\u4eba\u4e86\uff0c\u7559\u8fd9\u91cc\u65f6\u5c31\u591c\u591c\u53ec\u4f60\u4f34\u5bbf\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u76f8\u604b\u60c5\u70ed\uff0c\u4e09\u4e94\u5929\u7edd\u4e0d\u4f1a\u5f15\u4eba\u6000\u7591\uff0c\u4f46\u592a\u4e45\u4e86\u5c31\u96be\u8bf4\uff0c\u4e03\u7edd\u9053\u957f\u6700\u53ef\u6015\uff0c\u4ed6 \u5982\u4e00\u65e6\u8d77\u7591\uff0c\u8fde\u6211\u4e5f\u4f1a\u5728\u4ed6\u6000\u7591\u4e4b\u4e2d\u3002 \u3000\u3000\u6211\u4f55\u5c1d\u5e0c\u671b\u522b\u7684\u5973\u4eba\uff0c\u4eab\u6211\u5fc3\u4eea\u7684\u60c5\u90ce\u5462\uff1f\u4f46\u662f\u4e3a\u4e86\u5927\u5c40\uff0c\u6709\u65f6\u5019\u5fc5\u9700\u8981\u5fcd\u53d7\u727a\u7272\uff0c\u4e0d \u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u5f88\u8bc6\u5927\u4f53\uff0c\u601d\u8651\u4e5f\u5f88\u5468\u5bc6\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u73b0\u5728\u6211\u4eec\u8981\u5982\u4f55\u5e94\u4ed8\u5462\uff1f\u4f60\u719f \u6089\u8fd9\u91cc\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u60f3\u51fa\u6765\u7684\u4e3b\u610f\u5fc5\u5c06\u662f\u5bf9\u75c7\u4e0b\u836f\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u4f60\u88c5\u51fa\u70b9\u7ea8\u7ed4\u5b50\u5f1f\u7684\u795e\u97f5\u4e86\uff0c\u8868\u73b0\u51fa\u4e00\u70b9\u8ff7\u604b\uff0c\u4e03\u7edd\u9053\u957f\u95ee\u4f60\u4ec0\u4e48\uff1f \u4f60\u5c31\u56de\u7b54\u4ec0\u4e48\uff1f\u8981\u8868\u73b0\u51fa\u4e00\u70b9\u4e50\u4e0d\u601d\u8700\u7684\u5473\u9053\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u8fc7\u5206\u7684\u88c5\u4f5c\u63a9\u9970\uff0c\u53cd\u5c06\u753b\u864e\u6210 \u72ac\u3002\u4e8c\u516c\u5b50\uff0c\u8fd9\u662f\u4e00\u573a\u6597\u667a\u554a\uff01\u52a0\u4e0a\u4e00\u70b9\u7537\u6b22\u5973\u7231\uff0c\u5c31\u66f4\u6613\u4f20\u795e\uff0c\u4f60\u53ea\u7ba1\u5e94\u4ed8\u4e03\u7edd\u9053\u957f\uff0c\u5176 \u4ed6\u7684\u7531\u6211\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u2014\u2014 \u3000\u3000\u674e\u4e16\u6c11\u628a\u4f4f\u52a0\u7389\u4e0d\u653e\uff0c\u4e8b\u5b9e\u4e0a\u6b64\u5973\u5584\u4f53\u4eba\u610f\uff0c\u751f\u7c73\u5df2\u716e\u6210\u4e86\u719f\u996d\uff0c\u674e\u4e16\u6c11\u4e5f\u771f\u7684\u6709\u70b9\u820d \u4e0d\u5f97\u653e\u5f00\u5979\u4e86\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u6765\u5f97\u5f88\u5feb\uff0c\u7b2c\u4e09\u5929\u4e2d\u5348\u5c31\u5230\u4e86\u534e\u5c71\u884c\u5bab\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5750\u62e5\u5982\u7389\uff0c\u4e00\u9762\u996e\u9152\uff0c\u4e00\u9762\u6b23\u8d4f\u7740\u6b4c\u5531\u821e\u8e48\uff0c\u90a3\u4efd\u9676\u9189\u6b22\u4e50\uff0c\u628a\u4e00\u4e2a\u8c6a\u95e8\u7ea8\u7ed4\u5b50 \u5f1f\u7684\u8150\u5316\u3001\u8f7b\u6d6e\uff0c\u8868\u73b0\u5f97\u5341\u5206\u4f20\u795e\u3002 \u3000\u3000\u674e\u4e8c\u516c\u5b50\u867d\u7136\u5c11\u4e86\u8fd9\u4efd\u7ecf\u9a8c\uff0c\u4f46\u4ed6\u770b\u8fc7\u5927\u54e5\u5efa\u6210\u3001\u4e09\u5f1f\u5143\u5409\u7684\u73a9\u4e50\u795e\u8272\uff0c\u6a21\u4eff\u8d77\u6765\u5012\u4e5f \u795e\u4f3c\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u5168\u795e\u6295\u5165\u73a9\u4e50\u7684\u4eba\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u96c4\u5fc3\u5927\u5fd7\u4e86\u3002 \u3000\u3000\u7eb5\u7136\u662f\u6709\uff0c\u4e5f\u88ab\u9152\u8272\u6d88\u78e8\u53bb\u4e86\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u5f88\u591a\u7591\uff0c\u4ecd\u662f\u6709\u4e9b\u4e0d\u653e\u5fc3\u3002 \u3000\u3000\u4ed6\u6325\u6325\u624b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u674e\u4e8c\u516c\u5b50\uff0c\u53ef\u771f\u662f\u89c1\u9762\u4e0d\u5982\u95fb\u540d\u554a\uff01 \u3000\u3000\u4e16\u4f20\u4e8c\u516c\u5b50\u80f8\u6000\u5927\u5fd7\uff0c\u793c\u8d24\u4e0b\u58eb\uff0c\u559c\u6b22\u7ed3\u4ea4\u5929\u4e0b\u82f1\u96c4\uff0c\u600e\u4e48\uff1f\u534e\u5c71\u884c\u5bab\u8fd9\u4e2a\u5c0f\u573a\u9762\uff0c\u51e0 \u4e2a\u7a0d\u5177\u59ff\u8272\u7684\u6751\u5973\uff0c\u5c31\u8ba9\u4e8c\u516c\u5b50\u4e50\u4e0d\u601d\u8700\u4e86\uff1f\u201d \u3000\u3000\u5982\u7389\u975e\u5e38\u62c5\u5fc3\u674e\u4e16\u6c11\u88ab\u6fc0\u51fa\u6012\u706b\uff0c\u5728\u53cc\u65b9\u6fc0\u70c8\u7684\u53e3\u89d2\u51b2\u7a81\u4e4b\u4e0b\uff0c\u62db\u60f9\u51fa\u4e03\u7edd\u9053\u957f\u7684\u6740 \u673a\u3002 \u3000\u3000\u6240\u4ee5\u5979\u7528\u529b\u62c9\u674e\u4e16\u6c11\u4e00\u4e0b\uff0c\u76c8\u76c8\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u6b4c\u59ec\u9886\u73ed\u5982\u7389\uff0c\u53e9\u89c1\u9053\u957f\uff01\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u53cc\u76ee\u4e2d\u66b4\u5c04\u51fa\u4e24\u9053\u5947\u5149\u3001\u76ef\u4f4f\u674e\u4e16\u6c11\u770b\uff0c\u4f3c\u662f\u8981\u770b\u900f\u4e00\u4e2a\u4eba\u7684\u5185\u5fc3\uff0c\u53e3\u4e2d\u5374\u56de \u7b54\u5982\u7389\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u85d0\u4ec5\u4e2d\u59ff\uff0c\u7f3a\u5c11\u6e29\u67d4\uff0c\u51fa\u8eab\u4e8e\u519c\u820d\u4e4b\u5bb6\uff0c\u674e\u4e8c\u516c\u5b50\u600e\u4e48\u4f1a\u5bf9\u4f60\u4e00\u89c1\u503e \u5fc3\uff1f\u8fd9\u4e2d\u95f4\u662f\u4f55\u9053\u7406\uff1f\u201d \u3000\u3000\u5996\u9053\u679c\u7136\u591a\u7591\uff0c\u767b\u4e0a\u4e00\u79c0\u5cf0\u524d\uff0c\u4ed6\u5df2\u628a\u5982\u7389\u7684\u51fa\u8eab\u3001\u6027\u683c\u63a2\u542c\u5f97\u975e\u5e38\u6e05\u695a\u4e86\u3002 \u3000\u3000\u201c\u8d31\u59be\u5c24\u5c5e\u5904\u5b50\u8eab\uff0c\u4e8c\u516c\u5b50\u5927\u6982\u770b\u4e0a\u8fd9\u4e00\u70b9\uff0c\u5bf9\u5982\u7389\u5c31\u591a\u7559\u4e86\u4e00\u70b9\u5ba0\u7231\u3002\u201d \u3000\u3000\u201c\u96be\u5f97\u554a\uff01\u6b4c\u821e\u59ec\u4e2d\uff0c\u80fd\u591f\u5b88\u8eab\u5982\u7389\uff0c\u5b9e\u662f\u53eb\u4eba\u96be\u4fe1\uff0c\u6240\u4ee5\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u5c31\u4eb2\u8eab\u4e00\u8bd5\u4e86\uff1f\u201d\u4e03\u7edd\u9053\u957f\u9053\uff1a\u201c\u662f\u771f\u7684\u8fd8\u662f\u5047\u7684\u54ea\uff1f \u3000\u3000\u9b3c\u4e2b\u5934\u5982\u662f\u4fe1\u53e3\u5f00\u6cb3\uff0c\u8d2b\u9053\u5c31\u7acb\u523b\u53d6\u5979\u6027\u547d\uff0c\u4ee5\u6d88\u4e8c\u516c\u5b50\u80f8\u4e2d\u4e4b\u6c14\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5982\u679c\u5979\u6b3a\u9a97\u4e86\u6211\uff0c\u65e9\u5df2\u88ab\u6211\u6253\u51fa\u4e86\u534e\u5c71\u884c\u5bab\uff0c\u54ea\u91cc\u8fd8\u7528\u5f97\u7740\u4f60\u9053 \u957f\u51fa\u624b\u5462\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u5374\u6697\u6697\u5fd6\u9053\uff1a\u4ed6\u4e24\u9053\u76ee\u5149\u6709\u5982\u5229\u5203\uff0c\u6d1e\u4eba\u80ba\u8151\uff0c\u4e5f\u662f\u4e00\u79cd\u6b66\u529f\uff0c\u5e0c\u671b\u5982\u7389\u80fd\u652f\u6491 \u5f97\u4f4f\u3002 \u3000\u3000\u201c\u539f\u6765\u674e\u4e8c\u516c\u5b50\u6709\u7740\u8fd9\u6837\u7684\u96c5\u5174\u3001\u55dc\u597d\uff0c\u597d\uff01\u4e0b\u6b21\u518d\u6709\u805a\u6664\u8d2b\u9053\u5f53\u66ff\u4e8c\u516c\u5b50\u51c6\u5907\u4e0a\u5341\u4e2a \u516b\u4e2a\u672a\u66fe\u5165\u9053\u7684\u59d1\u5a18\uff0c\u8ba9\u674e\u4e8c\u516c\u5b50\u73a9\u4e2a\u5c3d\u5174\uff0c\u53ef\u771f\u662f\u4eba\u5404\u6709\u5fd7\uff0c\u4eba\u5404\u6709\u5fd7\u554a\uff01\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u5927\u58f0\u7b11\u4e2d\uff0c\u8d77\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u4f46\u662f\u90a3\u8361\u6f3e\u4e8e\u5927\u5385\u4e2d\u7684\u7b11\u58f0\uff0c\u9707\u5f97\u674e\u4e16\u6c11\u5168\u8eab\u8840\u6c14\u7ffb\u6d8c\uff0c\u5fcd\u4e0d\u4f4f\u5f20\u5634\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u5385\u4e2d\u7684\u6b4c\u59ec\u548c\u4f8d\u9152\u5973\u5a62\u65e9\u5df2\u5728\u4e03\u7edd\u9053\u957f\u8fdb\u5165\u5927\u5385\u540e\uff0c\u5f97\u5230\u793a\u610f\uff0c\u6084\u7136\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u73b0\u5728\u7684\u5927\u5385\u4e2d\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u674e\u4e16\u6c11\u548c\u5982\u7389\u3002 \u3000\u3000\u76ee\u7779\u4e03\u7edd\u9053\u957f\u79bb\u53bb\uff0c\u5982\u7389\u6323\u624e\u5750\u8d77\uff0c\u5de6\u624b\u63e1\u8bc0\uff0c\u53f3\u624b\u6309\u5728\u674e\u4e16\u6c11\u80cc\u5fc3\u4e0a\uff0c\u9053\uff1a\u201c\u6740\u4eba\u9b54 \u97f3\uff0c\u4e8c\u516c\u5b50\u8981\u5168\u529b\u6297\u62d2\uff0c\u4e0d\u53ef\u7a0d\u61c8\uff0c\u5feb\u4e9b\u53cc\u624b\u63a9\u8033\uff0c\u7531\u5a62\u5b50\u6321\u5b83\u4e00\u9635\u3002\u201d \u3000\u3000\u6b64\u523b\u674e\u4e16\u6c11\u624d\u53d1\u89c9\u5185\u529f\u3001\u6280\u827a\u7684\u4fee\u4e3a\u4e0a\uff0c\u5982\u7389\u4f3c\u662f\u90fd\u5f3a\u4ed6\u4e00\u7b79\u3002 \u3000\u3000\u5947\u602a\u7684\u90a3\u7b11\u58f0\u4f3c\u662f\u505c\u7559\u5728\u5927\u5385\u4e2d\uff0c\u56de\u65cb\u6fc0\u8361\uff0c\u4e45\u4e45\u4e0d\u53bb\u3002\u674e\u4e16\u6c11\u63a9\u4e0a\u53cc\u8033\uff0c\u624d\u89c9\u5f97\u6c14\u8840 \u9010\u6e10\u5e73\u590d\u4e0b\u6765\u3002 \u3000\u3000\u7a81\u7136\u95f4\u2014\u2014 \u3000\u3000\u4e00\u58f0\u6e05\u5578\u4f20\u6765\uff0c\u5feb\u5982\u6d41\u77e2\uff0c\u4e00\u4e0b\u5b50\u523a\u5165\u90a3\u56de\u8361\u7684\u7b11\u58f0\u4e2d\u3002 \u3000\u3000\u5c31\u50cf\u662f\u523a\u7834\u4e86\u6c34\u7f38\uff0c\u50a8\u6c34\u8fc5\u5feb\u7684\u6cc4\u53bb\uff0c\u56de\u8361\u7684\u7b11\u58f0\u968f\u7740\u90a3\u6e05\u5578\u9038\u51fa\u5927\u5385\u3002 \u3000\u3000\u674e\u4e16\u6c11\u653e\u4e0b\u53cc\u624b\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u8fd8\u672a\u53ca\u5f00\u53e3\uff0c\u884c\u5bab\u5916\u4f20\u51fa\u4e86\u4e00\u4e2a\u51b7\u786c\u7684\u58f0\u97f3\uff0c\u9053\uff1a \u201c\u725b\u9f3b\u5b50\u8001\u9053\uff0c\u4f60\u8dd1\u5230\u6211\u8fd9\u91cc\u6740\u4eba\u554a\uff01 \u3000\u3000\u662f\u5b58\u5fc3\u8ddf\u6211\u8fc7\u4e0d\u53bb\u4e86\uff01\u201d \u3000\u3000\u201c\u6728\u8001\u8a00\u91cd\u4e86\uff0c\u8d2b\u9053\u6740\u4eba\u81ea\u5f53\u8d1f\u8d23\uff0c\u7edd\u4e0d\u4f1a\u62d6\u7d2f\u5230\u6728\u8001\u8eab\u4e0a\uff0c\u4f55\u51b5\u8fd9\u8d2f\u6ce8\u4e94\u5206\u5185\u529b\u7684\u9b54 \u97f3\u5982\u4f55\u6740\u5f97\u4e86\u674e\u4e16\u6c11\uff0c\u81f3\u4e8e\u5982\u7389\u90a3\u4e2b\u5934\uff0c\u6b7b\u6d3b\u53c8\u7b97\u5f97\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u6e05\u695a\u7684\u8fa8\u51fa\u662f\u949f\u6728\u9b41\u548c\u4e03\u7edd\u9053\u957f\u5bf9\u8bdd\uff0c\u8c08\u7684\u6b63\u662f\u4ed6\u548c\u5982\u7389\u3002 \u3000\u3000\u201c\u5e0c\u671b\u674e\u4e8c\u516c\u5b50\u6ca1\u6709\u53d7\u5230\u4f24\u5bb3\uff0c\u6211\u82e5\u65e0\u6cd5\u5411\u5927\u738b\u548c\u5f20\u59d1\u5a18\u4ea4\u4ee3\u65f6\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u725b\u9f3b\u5b50\u597d \u8fc7\u3002\u201d \u3000\u3000\u201c\u770b\u6765\u6728\u8001\u5e76\u6ca1\u6709\u7eb5\u5bb9\u4e03\u7edd\u9053\u957f\u4f24\u6211\u4eec\u7684\u7528\u5fc3\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4ed6\u653e\u8fdb\u6765\u4e03\u7edd\u9053\u957f\u4e5f\u53ea \u662f\u65e0\u5fc3\u4e4b\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f5c\u620f\u554a\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u591a\u7528\u5fc3\u60f3\u60f3\u554a\uff01\u6211\u4e5f\u8be5\u53d7\u4f24\u4e86\u3002\u201d \u3000\u3000\u4e00\u5f20\u5634\uff0c\u4e00\u5927\u53e3\u7684\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u5f04\u5f97\u5168\u8eab\u90fd\u662f\u3002 \u3000\u3000\u674e\u4e16\u6c11\u65e0\u6cd5\u8fa8\u51fa\u662f\u771f\u662f\u5047\uff1f\u4e00\u65f6\u95f4\u4e0d\u77e5\u8be5\u5982\u4f55\u5904\u7f6e\uff1f \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u771f\u7684\u88ab\u90a3\u725b\u9f3b\u5b50\u8001\u9053\u5f04\u4f24\u4e86\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u5a07\u6ef4\u6ef4\u7684\u5927\u59d1\u5a18\uff0c\u5982\u4f55\u80fd\u7981\u5f97\u4f4f\u9b54\u97f3\u6298\u817e\uff0e\u53ef\u6076\u554a\uff01\u53ef\u6076\uff01\u201d \u3000\u3000\u4ed6\u76ee\u5149\u53c8\u6295\u6ce8\u5230\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u4e5f\u53d7\u4e86\u4f24\uff0c\u4e0d\u884c\uff01\u8fd9\u8981\u8001\u592b\u5982\u4f55\u5411\u5f20\u59d1\u5a18 \u4ea4\u4ee3\uff0c\u6211\u53bb\u8ffd\u4ed6\u53d6\u89e3\u836f\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u4e00\u8dfa\u811a\uff0c\u98de\u8eab\u800c\u8d77\uff0c\u5316\u6210\u4e00\u9053\u9ed1\u5149\u7a7f\u95e8\u800c\u51fa\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u95ed\u76ee\u4f9d\u504e\u5728\u674e\u4e16\u6c11\u6000\u4e2d\u7684\u5982\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u89e3\u836f\u7edd\u4e0d\u53ef\u4ee5\u541e\u4e0b\u53bb\uff0c\u4f46\u4e5f\u4e0d\u80fd \u4e0d\u5403\uff0c\u628a\u4ed6\u85cf\u5165\u820c\u5e95\u4e4b\u4e0b\uff0c\u4e00\u6709\u673a\u4f1a\u5c31\u5410\u51fa\u6765\uff0c\u653e\u5165\u8863\u888b\u4e2d\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u6655\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u6655\u8fc7\u53bb\uff0c\u8c01\u6765\u4fdd\u62a4\u4e8c\u516c\u5b50\uff1f\u8bb0\u7740\uff0c\u4e0d\u80fd\u541e\u4e0b\u89e3\u836f\u3002\u201d \u3000\u3000\u201c\u89e3\u836f\u6709\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u4e00\u6b65\u5927\u610f\uff0c\u5c31\u53ef\u80fd\u7ec8\u8eab\u9057\u6068\uff0c\u516c\u5b50\u591a\u73cd\u91cd\uff0c \u6211\u4f24\u5f97\u5f88\u91cd\uff0c\u4e0d\u80fd\u591a\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5979\u95ed\u4e0a\u53cc\u76ee\uff0c\u5634\u89d2\u95f4\u7acb\u523b\u6d41\u51fa\u4e86\u4e24\u884c\u8840\u6765\u3002 \u3000\u3000\u5929\u554a\uff01\u8fd9\u662f\u91cd\u4f24\u554a\uff01\u968f\u65f6\u4f1a\u4e22\u6389\u6027\u547d\u7684\u5927\u4f24\u5f62\u8c61\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6700\u5927\u7684\u56f0\u60d1\u662f\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4ed6\u8fd8\u65e0\u6cd5\u80af\u5b9a\u5982\u7389\u7684\u4f24\u662f\u771f\u7684\u8fd8\u662f\u5047\u7684\u3002 \u3000\u3000\u4f46\u53c8\u600e\u4e48\u80fd\u88c5\u5f97\u8fd9\u822c\u7684\u50cf\u5462\uff1f\u9c9c\u8840\u4f3c\u662f\u88c5\u5728\u53e3\u888b\u91cc\uff0c\u8981\u5410\u4e00\u5f20\u5634\u5c31\u5410\u51fa\u4e00\u53e3\uff0c\u771f\u662f\u4e0d\u53ef \u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u949f\u6728\u9b41\u53bb\u4f3c\u6012\u77e2\uff0c\u56de\u6765\u5f97\u4e5f\u5f88\u5feb\u901f\uff0c\u94c1\u6756\u987f\u5904\uff0c\u4eba\u5df2\u843d\u5728\u674e\u4e16\u6c11\u548c\u5982\u7389\u76f8\u62e5\u800c\u5750\u7684\u684c\u5b50 \u524d\u9762\u3002 \u3000\u3000\u770b\u8fc7\u4e86\u5982\u7389\u59d1\u5a18\u7684\u4f24\u52bf\uff0c\u949f\u6728\u9b41\u53cc\u76ee\u95ea\u8fc7\u4e00\u62b9\u5947\u5149\uff0c\u9053\uff1a\u201c\u5982\u7389\u59d1\u5a18\u662f\u4e0d\u662f\u5df2\u7ecf\u6b7b\u4e86 \u5462\uff1f\u201d \u3000\u3000\u201c\u4f3c\u8fd8\u6709\u4e00\u53e3\u6c14\u5728\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5982\u4e0d\u65bd\u6551\uff0c\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u6276\u4f4f\u6211\uff0c\u6211\u5e2e\u5979\u4e00\u628a\u56de\u5929\u4e4b\u529b\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u62ac\uff0c\u62cd\u5728\u5982\u7389\u7684\u9876\u95e8\u4e4b\u4e0a\u3002 \u3000\u3000\u674e\u4e16\u6c11\u60f3\u4e0d\u51fa\u5982\u7389\u7684\u611f\u53d7\uff0c\u4f46\u5374\u770b\u51fa\u5982\u7389\u5168\u8eab\u6c57\u51fa\u5982\u6d46\u3002 \u3000\u3000\u949f\u6728\u9b41\u62ff\u5f00\u53f3\u624b\u3002 \u3000\u3000\u5982\u7389\u5df2\u7741\u5f00\u773c\u775b\uff0c\u6323\u624e\u8dea\u5728\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u6728\u8001\u6551\u4e86\u6211\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u6d3b\u8fc7\u6765\uff0c\u7b97\u662f\u8fd0\u6c14\u3002\u6211\u4e0d\u61c2\u4ed6\u4e3a\u4ec0\u4e48\u8981\u81f4\u4f60\u4e8e\u6b7b\u5730\uff0c\u4f60\u53ea \u662f\u4e00\u4e2a\u6b4c\u59ec\u9886\u73ed\u554a\u2026\u2026\u201d \u3000\u3000\u201c\u6728\u8001\u662f\u8bf4\u4e03\u7edd\u9053\u957f\u5bf9\u5982\u7389\u7279\u522b\u4e0b\u4e86\u6bd2\u624b\uff1f\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5728\u4e0b\u600e\u4e48\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u6765 \u5462\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u53ef\u662f\u95ee\u8001\u592b\u8981\u8bc1\u636e\uff1f\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u8fd8\u662f\u903c\u8001\u592b\u5e2e\u4e24\u4f4d\u8ba8\u56de\u516c\u9053\uff1f\u201d \u3000\u3000\u5982\u7389\u95ed\u4e0a\u773c\u775b\uff0c\u5782\u7740\u5934\u4e0d\u6562\u8bf4\u8bdd\uff0c\u4f46\u674e\u4e16\u6c11\u5374\u611f\u89c9\u5230\u4e00\u80a1\u7ec6\u5fae\u7684\u529b\u91cf\u5728\u8eab\u4e0a\u8f7b\u8f7b\u7684\u649e \u51fb\uff0c\u662f\u63d0\u9192\u4ed6\u8981\u5c0f\u5fc3\u5e94\u4ed8\u3002 \u3000\u3000\u4f46\u5982\u7389\u5168\u8eab\u62dc\u4f0f\u4e8e\u5730\uff0c\u4e0d\u89c1\u4efb\u4f55\u52a8\u4f5c\u3002 \u3000\u3000\u8fd9\u4e2b\u5934\u679c\u7136\u5df2\u5f97\u674e\u9756\u4f20\u6388\u4e86\u672f\u6cd5\uff0c\u800c\u4e14\u4e5f\u4fee\u70bc\u6709\u6210\uff0c\u8eaf\u4f53\u548c\u56db\u80a2\u672a\u66fe\u4e3e\u52a8\uff0c\u7adf\u80fd\u53d1\u51fa\u529b \u91cf\uff0c\u4e5f\u7792\u8fc7\u949f\u6728\u9b41\u8fd9\u822c\u7684\u9ad8\u624b\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u8c03\u6574\u4e00\u4e0b\u6ce2\u52a8\u7684\u5fc3\u60c5\uff0c\u9053\uff1a\u201c\u4e0d\u6562\u6709\u6b64\u5984\u5ff5\uff0c\u53ea\u5e0c\u671b\u6728\u8001\u8bf4\u51fa\u7ecf \u8fc7\uff0c\u4e00\u5f00\u5728\u4e0b\u8305\u585e\u3002\u201d \u3000\u3000\u201c\u8bf4\u51fa\u6765\u4ea6\u662f\u65e0\u59a8\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u8001\u592b\u4e5f\u4e0d\u6015\u725b\u9f3b\u8001\u9053\u627e\u6211\u9ebb\u70e6\uff0c\u4ed6\u7528\u56de\u8361\u7684\u6740\u4eba\u9b54 \u97f3\uff0c\u8bd5\u8bd5\u4f60\u674e\u4e8c\u516c\u5b50\u7684\u529f\u529b\u5982\u4f55\uff1f\u5374\u7528\u6467\u5fc3\u638c\u4f24\u4e86\u5982\u7389\u2026\u2026\u201d \u3000\u3000\u674e\u4e16\u6c11\u60f6\u6025\u7684\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u5982\u7389\u59d1\u5a18\u7684\u4f24\u52bf\u662f\u5426\u6709\u6551\u5462\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u6467\u5fc3\u638c\u4f24\u4eba\u5185\u8151\u7ecf\u8109\uff0c\u4f46\u8001\u592b\u7684\u4e59\u6728\u795e\u529f\u5374\u6709\u8d77\u6b7b\u56de\u751f\u4e4b \u529b\uff0c\u5982\u7389\u4e2b\u5934\u7684\u4f24\u52bf\u5df2\u7ecf\u65e0\u788d\uff0c\u4f46\u4e0d\u77e5\u4e8c\u516c\u5b50\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4e16\u6c11\u4f24\u52bf\u4ea6\u65e0\u5927\u5f97\uff0c\u53ea\u5410\u8fc7\u4e00\u6b21\u6de4\u8840\u2026\u2026\u201d \u3000\u3000\u949f\u6728\u9b41\u53d6\u51fa\u4e00\u4f4d\u7ea2\u8272\u4e39\u4e5d\uff0c\u9053\uff1a\u201c\u4f24\u5230\u5185\u8151\u4e86\uff0c\u4e0d\u53ef\u5927\u610f\uff0c\u8fd9\u6709\u836f\u4e38\u4e00\u7c92\uff0c\u4e8c\u516c\u5b50\u5148\u8bf7 \u670d\u4e0b\uff0c\u4f11\u606f\u4e00\u65e5\uff0c\u5927\u6982\u5c31\u53ef\u4fdd\u5e73\u5b89\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u4e2d\u5fd6\u9053\uff1a\u679c\u4e0d\u51fa\u5982\u7389\u6240\u6599\uff0c\u53ea\u770b\u8fd9\u4e39\u4e5d\u7684\u989c\u8272\uff0c\u5c31\u7ed9\u4eba\u4e00\u79cd\u6050\u6016\u7684\u611f\u89c9\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u4f38\u624b\u63a5\u8fc7\uff0c\u6295\u5165\u53e3\u4e2d\u85cf\u5e72\u820c\u4e0b\u3002 \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u4e24\u4f4d\u4f11\u606f\u4e00\u4e0b\uff0c\u8001\u592b\u4e0d\u6253\u6270\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u6b65\u51fa\u884c\u5bab\u3002 \u3000\u3000\u674e\u4e16\u6c11\u618b\u4f4f\u4e00\u53e3\u6c14\uff0c\u4fdf\u949f\u6728\u9b41\u53bb\u8fdc\u4e4b\u540e\uff0c\u624d\u5410\u51fa\u836f\u4e38\uff0c\u6258\u4e8e\u638c\u4e2d\u4ed4\u7ec6\u67e5\u770b\u3002 \u3000\u3000\u5982\u7389\u62ac\u8d77\u5934\u6765\uff0c\u53d6\u8fc7\u836f\u4e5d\uff0c\u6536\u5165\u6000\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u674e\u7237\u4e86\u7136\u836f\u6027\uff0c\u65e5\u540e\u89c1\u7740\u674e\u7237\u65f6\uff0c\u8ba9\u4ed6 \u77a7\u77a7\u7a76\u7adf\u662f\u4ec0\u4e48\u6bd2\u7269\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u6253\u91cf\u5982\u7389\u597d\u4e00\u9635\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u6ca1\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u5982\u7389\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6728\u8001\u4e5f\u6ca1\u6709\u67e5\u51fa\u6211\u8eab\u6000\u6b66\u529f\uff0c\u674e\u7237\u5728\u4f20\u6211\u6b66\u529f\u3001\u672f\u6cd5\u65f6\uff0c\u65e9\u5df2\u60f3\u5230 \u4eca\u65e5\uff0c\u6240\u4ee5\u7279\u522b\u4f20\u4e86\u6211\u606f\u9690\u4e4b\u672f\uff0c\u6563\u53bb\u529f\u529b\u540e\u548c\u5e38\u4eba\u4e00\u822c\u3002\u949f\u6728\u9b41\u67e5\u51fa\u4e86\u6211\u8eab\u53d7\u6467\u5fc3\u638c\u4f24\uff0c \u5374\u6ca1\u6709\u67e5\u5230\u6211\u7ec3\u6709\u6b66\u529f\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u6328\u4e86\u4e03\u7edd\u9053\u957f\u4e00\u8bb0\u63a8\u5fc3\u638c\uff1f\u4e5f\u771f\u7684\u5410\u4e86\u90a3\u4e48\u591a\u9c9c\u8840\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u8001\u9053\u58eb\u770b\u6211\u5f88\u4e0d\u987a\u773c\uff1f\u65e0\u58f0\u65e0\u606f\u7684\u9065\u51fb\u6211\u4e00\u638c\uff0c\u5e78\u597d\u6211\u65e9\u5df2\u6709\u5907\uff0c\u4f24\u5f97 \u4e0d\u7b97\u592a\u91cd\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u949f\u6728\u9b41\u5012\u662f\u771f\u7684\u5e2e\u5fd9\uff0c\u4ee5\u4ed6\u7684\u4e59\u6728\u795e\u529f\u5e2e\u6211\u6253\u901a\u4e86\u53d7\u4f24\u7684\u7ecf\u8109\uff0c\u4e8c \u516c\u5b50\u4f46\u8bf7\u653e\u5fc3\uff0c\u5c0f\u5a62\u73b0\u5728\u5b8c\u5168\u6ca1\u4e8b\u4e86\uff0c\u5012\u662f\u53e6\u6709\u4e00\u4ef6\u4e8b\u53eb\u4eba\u70e6\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u949f\u6728\u9b41\u770b\u4f3c\u61a8\u539a\uff0c\u5b9e\u4ea6\u9634\u9669\uff0c\u53ea\u662f\u592a\u8fc7\u81ea\u8d1f\uff0c\u5c31\u96be\u514d\u6709\u4e9b \u7c97\u5fc3\u5927\u610f\u4e86\uff0c\u53ea\u8981\u7a0d\u7528\u5fc3\u673a\uff0c\u4e0d\u96be\u5e94\u4ed8\u3002\u4f60\u8fd8\u6709\u4ec0\u4e48\u70e6\u5fc3\u4e4b\u4e8b\uff0c\u4e0d\u59a8\u8bf4\u51fa\u6765\u7814\u5546\u4e00\u756a\uff0c\u4e5f\u8bb8 \u53ef\u4ee5\u8ba8\u8bba\u51fa\u4e00\u4e2a\u5e94\u4ed8\u4e4b\u7b56\u554a\uff01\u201d \u3000\u3000\u201c\u836f\u529b\u7684\u53cd\u5e94\u2026\u2026\u201d\u5982\u7389\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u836f\u7269\uff0c\u65e0\u6cd5\u5e94\u53d8\uff0c\u5c82\u4e0d\u662f\u8ba9\u4eba\u4e00\u4e0b\u5b50 \u5c31\u770b\u51fa\u7834\u7efd\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8fd9\u2026\u2026\u786e\u5b9e\u6709\u4e9b\u9ebb\u70e6\u4e86\uff01\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u53ea\u6709\u59d4\u5c48\u4e8c\u516c\u5b50\u6682\u5367\u69bb\u4e0a\uff0c\u4f2a\u79f0\u4e0d\u9002\uff0c\u59be\u5a62\u542c\u4e86\u6765\u4eba\u53e3\u98ce\u518d\u4f5c\u8ba1 \u8bae\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u592a\u59d4\u5c48\u4e8c\u516c\u5b50\u4e86\uff0c\u4ec0\u4e48\u6837\u7684\u836f\u7269\uff1f\u8ba9\u8001\u673d\u77a7\u77a7\uff0c\u4e5f\u8bb8\u80fd\u770b\u51fa\u5b83\u7684\u836f\u6027\u3002\u201d \u3000\u3000\u53ea\u95fb\u58f0\u97f3\u4e0d\u89c1\u4eba\uff0c\u674e\u4e16\u6c11\u4f3c\u662f\u542c\u51fa\u662f\u8c01\u4e86\uff0c\u4f46\u5fc3\u4e2d\u6ca1\u6709\u5341\u5206\u628a\u63e1\uff0c\u53ea\u597d\u5fcd\u4e0b\u4e86\u6ca1\u6709\u518d\u5f00 \u53e3\u3002 \u3000\u3000\u201c\u662f\u674e\u7237\u5417\uff1f\u201d\u5982\u7389\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u4f60\u8001\u5feb\u73b0\u8eab\u54ea\uff01 \u3000\u3000\u4e8c\u516c\u5b50\u8eab\u5904\u5371\u9669\uff0c\u5f1f\u5b50\u5feb\u63a9\u62a4\u4e0d\u4f4f\u4e86\uff0c\u4e00\u65e6\u9732\u51fa\u9a6c\u811a\uff0c\u96be\u514d\u4e00\u573a\u6076\u6218\uff0c\u5f1f\u5b50\u8fd9\u70b9\u6280\u827a\u63a5 \u4e0d\u4e0b\u6728\u8001\u5341\u62db\u3002\u201d \u3000\u3000\u201c\u674e\u9756\u88ab\u866c\u9aef\u5ba2\u63a5\u5165\u5927\u8425\u4e86\uff0c\u8001\u673d\u8881\u5929\u7f61\u3002\u201d \u3000\u3000\u4e00\u9635\u5149\u5f71\u95ea\u8fc7\uff0c\u73b0\u51fa\u4e2a\u9752\u888d\u7b56\u6756\u7684\u8001\u4eba\u3002 \u3000\u3000\u201c\u8881\u5929\u7f61\uff01\u201d\u5982\u7389\u5446\u5446\u5730\u671b\u7740\u9752\u8863\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u4f1a\u9690\u8eab\u672f\uff1f\u201d \u3000\u3000\u201c\u96d5\u866b\u5c0f\u6280\u5c14\uff0c\u4e0d\u767b\u5927\u96c5\u4e4b\u5802\uff0c\u4f46\u95e8\u5916\u6212\u5907\u68ee\u4e25\uff0c\u4e3a\u4e86\u907f\u4eba\u8033\u76ee\uff0c\u4e0d\u5f97\u4e0d\u7528\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u8881\u5148\u751f\uff0c\u89c1\u8fc7\u5982\u7389\u59d1\u5a18\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5979\u662f\u674e\u9756\u57cb\u85cf\u4e8e\u6b64\u4e00\u7740\u4f0f\u5175\uff0c\u6b64\u756a\u975e\u5979\u76f8 \u6551\uff0c\u6211\u53ea\u6015\u65e9\u5df2\u6027\u547d\u4e0d\u4fdd\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u884c\u8fd1\u5982\u7389\uff0c\u606d\u606d\u656c\u656c\u62b1\u62f3\u4e00\u793c\u3002\u9053\uff1a\u201c\u8881\u5929\u7f61\u89c1\u8fc7\u5982\u7389\u59d1\u5a18\uff0c\u4e5f\u4ee3\u8868\u592a\u539f\u4f17\u5bb6\u5c06 \u519b\u8c22\u8c22\u5982\u7389\u59d1\u5a18\u4fdd\u62a4\u4e8c\u516c\u5b50\u7684\u6069\u60c5\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u674e\u7237\u6536\u6211\u4e3a\u8bb0\u540d\u5f1f\u5b50\u65f6\uff0c\u5c31\u6709\u4ea4\u4ee3\uff0c\u4e00\u65e6\u9047\u4e0a\u592a\u539f\u674e\u4e8c\u516c\u5b50\uff0c\u5c31\u7b97\u727a\u7272\u4e86\u6027 \u547d\uff0c\u4e5f\u8981\u4fdd\u62a4\u4ed6\u7684\u5b89\u5168\uff0c\u5c0f\u5a62\u662f\u5728\u5949\u884c\u674e\u7237\u7684\u4ee4\u8c15\u3002\u201d \u3000\u3000\u201c\u674e\u9756\u591a\u667a\u7565\u3002\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u679c\u7136\u662f\u4e00\u4ee3\u5927\u5c06\u4e4b\u624d\uff0c\u4e8c\u516c\u5b50\u53ef\u5426\u628a\u4e39\u4e5d\u8d50\u4e0b\uff0c\u8ba9\u8001\u673d \u4e00\u9605\uff0c\u770b\u770b\u80fd\u5426\u8ba4\u51fa\u5b83\u7684\u836f\u6027\uff1f\u201d \u3000\u3000\u672a\u6301\u674e\u4e16\u6c11\u5429\u5490\uff0c\u5982\u7389\u5df2\u53d6\u51fa\u4e86\u6536\u5165\u888b\u4e2d\u7684\u7ea2\u8272\u4e39\u4e5d\uff0c\u53cc\u624b\u5949\u4e0a\u3002 \u3000\u3000\u8881\u5929\u7f61\u53f3\u624b\u634f\u5f00\u4e39\u4e5d\uff0c\u95fb\u4e86\u4e00\u95fb\uff0c\u9053\uff1a\u201c\u5f88\u6076\u6bd2\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u7f6e\u5165\u4e00\u4e2a\u9152\u676f\u4e2d\uff0c\u7528\u9152\u51b2\u5316\u3002 \u3000\u3000\u7247\u523b\u95f4\uff0c\u9152\u4e2d\u5192\u8d77\u767d\u6cab\uff0c\u5982\u540c\u70e7\u6eda\u7684\u5f00\u6c34\u3002 \u3000\u3000\u8881\u5929\u7f61\u7528\u6728\u7b77\u5feb\u8d77\u4e00\u7247\u80b4\u8089\uff0c\u653e\u5165\u836f\u9152\u4e4b\u4e2d\uff0c\u9152\u6cab\u6e10\u6d88\u3002\u518d\u631f\u51fa\u8089\u5757\uff0c\u8089\u5757\u5df2\u6210\u5e72\u67af\u4e4b \u72b6\u3002 \u3000\u3000\u8881\u5929\u7f61\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u5e78\u672a\u670d\u4e0b\uff0c\u5426\u5219\u5fc3\u80ba\u809d\u810f\u5c06\u90fd\u4f1a\u4e3a\u836f\u529b\u6215\u5bb3\uff0c\u9010\u6e10\u67af\u786c\uff0c \u5341\u65e5\u4e4b\u5185\u5fc5\u6b7b\u65e0\u7591\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u6076\u6bd2\u7684\u624b\u6bb5\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u679c\u7136\u5fc3\u72e0\u624b\u8fa3\u3002\u201d \u3000\u3000\u987f\u4e86\u9886\uff0c\u7a81\u7136\u53c8\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e03\u7edd\u9053\u957f\u73b0\u4ecd\u5728\u866c\u9aef\u5ba2\u7684\u5927\u8425\u4e4b\u4e2d\uff0c\u674e\u9756\u5144\u5f1f\u88ab\u63a5\u5165\u5927\u8425 \u5185\uff0c\u83ab\u8981\u4e2d\u4e86\u4ed6\u7684\u8be1\u8ba1\u624d\u597d\uff01\u201d \u3000\u3000\u201c\u4e0d\u59a8\u4e8b\uff01\u201d\u8881\u5929\u7f61\u9053\uff1a\u201c\u674e\u9756\u77e5\u836f\u7684\u672c\u9886\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u6b64\u7b49\u7edd\u6bd2\u836f\u7269\uff0c\u5982\u4f55\u7792\u5f97\u674e \u9756\u7684\u53cc\u76ee\uff1f\u201d \u3000\u3000\u201c\u8881\u5148\u751f\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\u554a\uff01\u5f20\u59d1\u5a18\u4e5f\u5728\u73b0\u573a\uff0c\u674e\u7237\u592b\u59bb\u4fe9\u4e45\u522b\u91cd \u9022\uff0c\u96be\u514d\u5fc3\u60c5\u6fc0\u52a8\uff0c\u89c2\u5bdf\u5c31\u672a\u5fc5\u7ec6\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u2026\u4ed6\u4eec\u8fde\u674e\u592b\u4eba\u4e5f\u7b97\u8ba1\u5728\u5185\uff01\u201d \u3000\u3000\u201c\u5927\u738b\u662f\u4e0d\u4f1a\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u4ed6\u4e0d\u5fcd\uff0c\u4e5f\u820d\u4e0d\u5f97\u7b97\u8ba1\u5f20\u59d1\u5a18\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u5c31\u96be\u8bf4\u4e86\uff0c\u4ed6\u53f7\u79f0\u4e03\u7edd\uff0c\u4ec0\u4e48\u7edd\u4e8b\u505a\u4e0d\u51fa\u6765\uff1f\u6240\u4ee5\u5c0f\u5a62\u5f97\u53bb\u901a\u77e5\u674e\u7237\u4e00\u58f0\uff0c\u8981 \u4ed6\u5c0f\u5fc3\u9632\u8303\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u89c6\u6211\u5982\u9636\u4e0b\u4e4b\u56da\uff0c\u6240\u4ee5\u4f24\u6211\u3001\u4e0b\u836f\u90fd\u6709\u70b9\u5f3a\u8feb\u7684\u9738\u6c14\u3002\u8f6c\u9001\u836f\u7269 \u7684\u949f\u6728\u9b41\uff0c\u53c8\u6709\u70b9\u7c97\u679d\u5927\u53f6\uff0c\u624d\u7ed9\u4e86\u6211\u4eec\u7559\u836f\u672a\u670d\u7684\u673a\u4f1a\u3002\u4e03\u7edd\u9053\u957f\u5982\u679c\u7b97\u8ba1\u674e\u9756\u5144\u5f1f\uff0c\u5fc5 \u5c06\u662f\u8ba1\u7b97\u7cbe\u5bc6\u4e0d\u7559\u7834\u7efd\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4e0d\u53ef\u5927\u610f\uff0c\u8001\u673d\u8d70\u4e00\u8d9f\u5427\uff01\u201d \u3000\u3000\u8881\u5929\u7f61\u53d6\u51fa\u836f\u9152\uff0c\u51c6\u5907\u79bb\u53bb\u3002 \u3000\u3000\u5982\u7389\u5374\u4f4e\u58f0\u9053\uff1a\u201c\u4ea4\u7ed9\u5c0f\u5a62\uff0c\u6b64\u7269\u8981\u5904\u7406\u5f97\u4e0d\u7559\u75d5\u8ff9\uff0c\u949f\u6728\u9b41\u5e76\u975e\u7c97\u5fc3\u5927\u610f\u7684\u4eba\uff0c\u53ea\u5728 \u672a\u5b58\u9632\u8303\u4e8c\u516c\u5b50\u7684\u7528\u5fc3\uff0c\u4e00\u65e6\u8ba9\u4ed6\u5fc3\u4e2d\u52a8\u7591\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u7389\u59d1\u5a18\u5982\u6b64\u7ec6\u5fc3\uff0c\u8001\u673d\u5b89\u5fc3\u591a\u4e86\uff0c\u4e00\u5207\u8c28\u614e\u3001\u5fcd\u8010\uff0c\u8001\u673d\u672a \u56de\u8f6c\u4e4b\u524d\uff0c\u5343\u4e07\u4e0d\u53ef\u548c\u4ed6\u4eec\u51b2\u7a81\u3002\u201d \u3000\u3000\u4e00\u95ea\u8eab\uff0c\u7a81\u7136\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u53bb\u5982\u98d8\u98ce\uff0c\u5f62\u5f71\u987f\u5931\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u8881\u8001\u7237\u7684\u672f\u6cd5\u4e4b\u7cbe\uff0c\u7edd\u4e0d\u5728\u674e\u7237\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u5f02\u4eba\u4f17\u591a\uff0c\u8fd9\u6570\u6708\u4e4b\u95f4\u6211\u5f00\u4e86\u4e0d\u5c11\u773c\u754c\uff0c\u90a3\u4f4d\u4e03\u7edd\u9053\u957f\u7684 \u8863\u7740\u8be1\u5f02\uff0c\u6050\u6015\u4e5f\u662f\u4f4d\u7cbe\u901a\u672f\u6cd5\u7684\u4eba\u3002\u201d \u3000\u3000\u5982\u7389\u5904\u7f6e\u4e86\u836f\u9152\uff0c\u6d17\u51c0\u9152\u676f\uff0c\u5401\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u63a8\u65ad\u4e0d\u9519\uff0c\u53ea\u770b\u4e03\u7edd\u9053\u957f\u7684\u8863 \u7740\u3001\u5f62\u8c8c\uff0c\u5f53\u77e5\u5176\u5fc5\u5177\u5947\u672f\u3002\u4f46\u4e8c\u516c\u5b50\u6709\u674e\u7237\u548c\u8881\u8001\u7237\u5b50\u8fd9\u7b49\u9ad8\u4eba\u76f8\u52a9\uff0c\u8db3\u4ee5\u6297\u8861\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u592a\u539f\u5e9c\u51dd\u805a\u7684\u519b\u4e8b\u529b\u91cf\u5982\u679c\u5148\u7528\u5728\u548c\u866c\u9aef\u5ba2\u7684\u51b3\u6218\u4e4b\u4e0a\uff0c\u5c31\u662f \u4e00\u4ef6\u5f88\u5927\u7684\u4e0d\u5e78\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5fe7\u8651\u8fd9\u4e24\u80a1\u529b\u91cf\u7684\u6297\u4e89\u3001\u5bf9\u6d88\uff0c\u5c06\u7ed9\u8881\u7d2b\u70df\u4e00\u4e2a\u638c\u63e1\u5168\u5c40\u7684\u673a\u4f1a\uff0c\u4f46\u5374\u5fcd\u5728\u5fc3\u4e2d \u672a\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u5982\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u738b\u638c\u63e1\u7684\u529b\u91cf\u975e\u5e38\u96c4\u58ee\uff0c\u592a\u539f\u6709\u591a\u5c11\u5175\u529b\u6211\u4e0d\u6e05\u695a\uff1f\u4f46 \u53cc\u65b9\u4e00\u65e6\u5927\u519b\u5bf9\u9635\uff0c\u540e\u679c\u53ef\u80fd\u662f\u4e24\u8d25\u4ff1\u4f24\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u8fd9\u573a\u6218\u4e89\u6253\u8d77\u6765\uff0c\u5b81\u53ef\u8ba9\u866c\u9aef\u5ba2\u5148\u5165\u957f\u5b89\u3002\u201d \u3000\u3000\u5982\u7389\u6ca1\u6709\u63a5\u53e3\uff0c\u5979\u9690\u9690\u77e5\u9053\u866c\u9aef\u5ba2\u6709\u5341\u4e07\u7532\u5175\u6f5c\u4f0f\uff0c\u4e5f\u77e5\u9053\u5929\u4e0b\u591a\u5904\u4e71\u6c11\u5578\u805a\uff0c\u7687\u4e0a\u6b63 \u6d3e\u51fa\u6570\u5341\u4e07\u5927\u519b\u56db\u4e0b\u5f81\u527f\uff0c\u4f46\u771f\u6b63\u5929\u4e0b\u5927\u52bf\u5f62\u6001\u5c31\u4e0d\u662f\u5f88\u6e05\u695a\u4e86\uff0c\u6240\u4ee5\u63a5\u4e0d\u4e0a\u53e3\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e5f\u672a\u518d\u8bf4\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u4eba\u5730\u4e24\u4e0d\u6613\uff0c\u5b9e\u975e\u8c08\u8bba\u5929\u4e0b\u5927\u4e8b\u7684\u65f6\u673a\u3002 \u3000\u3000\u7a81\u95fb\u94c1\u6756\u89e6\u5730\u4e4b\u58f0\u4f20\u4e86\u8fc7\u6765\uff01 \u3000\u3000\u4f3c\u662f\u949f\u6728\u9b41\u6765\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u58f0\u97f3\u5165\u8033\uff0c\u949f\u6728\u9b41\u5df2\u51fa\u73b0\u884c\u5bab\u95e8\u5916\u3002 \u3000\u3000\u5982\u7389\u53cd\u5e94\u5947\u5feb\uff0c\u6276\u7740\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u6211\u6276\u4f60\u5230\u5367\u623f\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u949f\u6728\u9b41\u4e3e\u6b65\u4e00\u8de8\uff0c\u4e24\u4e08\u591a\u7684\u8ddd\u79bb\u88ab\u4ed6\u4e00\u6b65\u8d70\u5b8c\u4e86\uff0c\u8eab\u5b50\u5df2\u5230\u4e86\u674e\u4e16\u6c11\u5750\u7684\u684c\u5b50\u524d\u9762\u3002 \u3000\u3000\u7ec8\u4e8e\u9732\u4e86\u4e00\u624b\u7edd\u4e16\u8f7b\u529f\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff0c\u54ea\u91cc\u4e0d\u8212\u670d\u554a\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u53cc\u76ee\u795e\u5149\u5982\u7535\uff0c\u903c\u6ce8\u5728\u674e\u4e16\u6c11\u7684\u8138\u4e0a\u3002 \u3000\u3000\u674e\u4e16\u6c11\u7f13\u7f13\u5730\u9053\uff1a\u201c\u524d\u80f8\u2026\u2026\u201d \u3000\u3000\u5fc3\u60f3\uff1a\u5fc3\u3001\u809d\u3001\u80ba\u8151\u7686\u5728\u4e00\u5904\uff0c\u8bf4\u524d\u80f8\u603b\u4e0d\u4f1a\u9519\u3002 \u3000\u3000\u5982\u7389\u63a5\u7740\u9053\uff1a\u201c\u670d\u4e86\u90a3\u9897\u836f\u4e38\u4e4b\u540e\uff0c\u4e0d\u591a\u4e45\u5373\u611f\u5230\u524d\u80f8\u4e0d\u9002\uff0c\u6728\u8001\u554a\uff01\u4e8c\u516c\u5b50\u5982\u6709\u95ea \u5931\uff0c\u5c0f\u5a62\u53ef\u8d1f\u4e0d\u8d77\u8fd9\u4e2a\u8d23\u4efb\uff0c\u8981\u4e0d\u8981\u901a\u77e5\u5f20\u59d1\u5a18\u4e00\u58f0\u3002 \u3000\u3000\u949f\u6728\u9b41\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u597d\u9738\u9053\u7684\u836f\u529b\uff0c\u674e\u4e16\u6c11\u53d1\u75c5\u592a\u6025\uff0c\u8fd8\u771f\u4e0d\u597d\u5411\u51fa\u5c18\u90a3\u4e2a\u4e2b\u5934\u4ea4 \u4ee3\u3002\u725b\u9f3b\u5b50\u8001\u9053\u554a\uff0c\u8fd9\u4e00\u6b21\u53c8\u88ab\u4f60\u800d\u4e86\uff0c\u674e\u4e16\u6c11\u662f\u4f60\u7b97\u8ba1\u7684\uff0c\u4ec7\u6068\u5374\u7559\u5230\u6211\u8001\u949f\u7684\u5934\u4e0a\uff0c\u6211 \u548c\u5f20\u51fa\u5c18\u8fd9\u573a\u8bef\u4f1a\uff0c\u53ea\u6015\u662f\u5f88\u96be\u89e3\u91ca\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u7a9d\u56ca\u4e4b\u5904\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u4e00\u53e3\u6c14\uff01 \u3000\u3000\u674e\u4e16\u6c11\u6697\u9053\uff1a\u679c\u7136\u662f\u5408\u4f5c\u505a\u620f\uff0c\u4e0d\u77e5\u866c\u9aef\u5ba2\u662f\u5426\u4e5f\u77e5\u6653\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u201c\u600e\u4e48\u4f1a\u8fd9\u6837\u5b50\u5462\uff1f\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u4f60\u65e5\u591c\u90fd\u548c\u4e8c\u516c\u5b50\u817b\u5728\u4e00\u8d77\uff0c\u9152\u540e\u8272\uff0c\u8272\u540e \u9152\uff0c\u8fde\u7ef5\u4e0d\u7edd\uff0c\u662f\u4e0d\u662f\u4f24\u4e86\u4e8c\u516c\u5b50\u7684\u5143\u6c14\uff1f\u201d \u3000\u3000\u201c\u63a8\u5230\u6211\u5934\u4e0a\u4e86\uff01\u201d\u5982\u7389\u5fc3\u4e2d\u6697\u9a82\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u51a4\u6789\u554a\uff01\u6728\u8001\uff0c\u65e9\u4e9b\u901a\u77e5\u5f20\u59d1\u5a18\u5427\uff01 \u4e8c\u516c\u5b50\u4e00\u65e6\u75c5\u60c5\u6709\u53d8\uff0c\u5f20\u59d1\u5a18\u706b\u4e0a\u5fc3\u5934\uff0c\u5c82\u4e0d\u8981\u6740\u4e86\u5c0f\u5a62\uff1f\u201d \u3000\u3000\u201c\u55ef\uff01\u8fd9\u5c31\u5f88\u96be\u8bf4\u4e86\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u5f20\u59d1\u5a18\u4e5f\u4ea4\u4ee3\u8fc7\u8001\u673d\uff0c\u5584\u5f85\u4e8c\u516c\u5b50\u3002\u5982\u4eca\u88ab\u4f60\u641e \u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u8001\u673d\u5c31\u7b97\u60f3\u5e2e\u4f60\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u5e2e\u5f97\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5982\u7389\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u5b8c\u5168\u63a8\u7ed9\u6211\u4e86\uff0c\u8fd9\u4e2a\u67af\u6728\u3001\u50f5\u5c38\u4e00\u6837\u7684\u8001\u5c0f\u5b50\u4e5f\u591f\u6b79\u6bd2\u4e86\uff01\u4e0d \u884c\uff0c\u6211\u5f97\u8ba9\u4ed6\u591a\u62db\u8ba4\u4e00\u70b9\u7f6a\u72b6\uff0c\u7559\u4e2a\u65e5\u540e\u5bf9\u8d28\u7684\u53e3\u5b9e\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u88c5\u51fa\u65e0\u9650\u7684\u51c4\u4f24\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u4e00\u6751\u5973\u5c14\u3002\u751f\u6b7b\u4f55\u8db3\u6302\u9f7f\uff0c\u4f46\u4e8c\u516c\u5b50\u75c5\u52bf\u602a \u5f02\uff0c\u5c0f\u5a62\u5c31\u7b97\u7518\u613f\u9876\u7f6a\u4e00\u6b7b\uff0c\u53ea\u6015\u5f20\u59d1\u5a18\u4e5f\u672a\u5fc5\u76f8\u4fe1\uff01\u201d \u3000\u3000\u949f\u6728\u9b41\u6c89\u541f\u4e0d\u8bed\uff0c\u663e\u662f\u88ab\u5982\u7389\u8fd9\u4e00\u8a00\u51fb\u4e2d\u8981\u5bb3\u3002 \u3000\u3000\u7247\u523b\uff0c\u949f\u6728\u9b41\u65b9\u9053\uff1a\u201c\u5982\u7389\uff0c\u4f60\u771f\u7684\u613f\u610f\u4ee3\u7f6a\u4e00\u6b7b\uff1f\u8001\u592b\u5012\u662f\u60f3\u8d77\u4e00\u4e2a\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u5a62\u547d\u5982\u8349\u82a5\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u6d3b\u7740\u4e0d\u591a\uff0c\u6b7b\u4e86\u4e0d\u5c11\uff1b\u5982\u80fd\u5e2e\u886c\u6728\u8001\u4e00\u628a\uff0c\u6b7b\u800c\u4f55 \u61be\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u5bf9\uff01\u4f60\u7684\u6b7b\uff0c\u5316\u89e3\u53bb\u8001\u673d\u548c\u5f20\u59d1\u5a18\u4e00\u573a\u8bef\u4f1a\uff0c\u4e5f\u7b97\u6b7b\u7684\u503c\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c0f\u5a62\u8be5\u5f53\u5982\u4f55\uff1f\u201d\u5982\u7389\u9053\uff1a\u201c\u8fd8\u671b\u6728\u8001\u6307\u70b9\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u674e\u4e16\u6c11\u670d\u7528\u7684\u836f\u7269\uff0c\u5c06\u4f7f\u5fc3\u809d\u67af\u786c\uff0c\u5f88\u5feb\u5c06\u4eba\u4e8b\u4e0d\u7701\uff0c\u4f46\u62d6\u4e2a\u4e09 \u4e94\u65e5\u8fd8\u4e0d\u4f1a\u65ad\u6c14\u3002\u5f20\u59d1\u5a18\u8fdb\u5165\u884c\u5b98\uff0c\u4f60\u5c31\u5168\u529b\u649e\u58c1\u800c\u4ea1\uff0c\u4f60\u4e00\u6b7b\uff0c\u5f20\u59d1\u5a18\u4ec0\u4e48\u4e5f\u65e0\u6cd5\u95ee\u4e86\u3002 \u8bb0\u7740\uff0c\u8981\u5168\u529b\u649e\u5899\uff0c\u7acb\u523b\u6bd9\u547d\u3002\u201d \u3000\u3000\u5982\u7389\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\uff01\u6b7b\u65e0\u5bf9\u8bc1\u3002\u5c0f\u5a62\u754f\u7f6a\u81ea\u6740\uff0c\u5f20\u59d1\u5a18\u5c31\u65e0\u6cd5\u602a\u6728\u8001\u7237\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e11\u8bdd\u8bb2\u5728\u524d\u9762\uff0c\u4f60\u5982\u98df\u8a00\u4e0d\u6b7b\uff0c\u843d\u5165\u8001\u592b\u624b \u4e2d\uff0c\u90a3\u5c31\u8981\u4f60\u6c42\u751f\u4e0d\u80fd\uff0c\u6c42\u6b7b\u4e0d\u5f97\u4e86\uff0c\u8001\u592b\u8981\u6298\u78e8\u4f60\u4e09\u4e2a\u6708\uff0c\u518d\u5c06\u4f60\u632b\u9aa8\u626c\u7070\u3002\u4f60\u5982\u6b7b\u5f97\u53ca \u65f6\uff0c\u8001\u592b\u5c06\u534e\u670d\u4e0a\u68fa\uff0c\u539a\u846c\u4e8e\u4f60\u3002\u201d \u3000\u3000\u201c\u5982\u7389\u4e0d\u6562\u8bef\u4e8b\uff0c\u6728\u8001\u653e\u5fc3\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u518d\u7ed9\u4f60\u4e00\u591c\u6b22\u5a31\u3002\u201d\u949f\u6728\u9b41\u8f6c\u671b\u7740\u674e\u4e16\u6c11\u9053\uff1a\u201c\u660e\u65e5\u8001\u592b\u5c31\u70b9\u4e86\u674e\u4e16\u6c11\u7684\u7a74\u9053\uff0c\u4ee5\u6c42 \u4fdd\u9690\u79d8\u4e0d\u6cc4\u3002\u4e8c\u516c\u5b50\uff0c\u4eba\u5728\u82b1\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u98ce\u6d41\u3002\u8001\u592b\u4e0d\u6253\u6270\u4f60\u4e86\uff0c\u4eca\u591c\u65e0\u9650\u597d\uff0c\u6000\u62b1\u5973\u591a \u5a07\uff0c\u5982\u7389\u59d1\u5a18\u5bf9\u4f60\u4e00\u7247\u771f\u60c5\uff0c\u4f60\u8981\u597d\u597d\u601c\u60dc\u5979\u5440\uff01\u5f80\u4e8b\u4ff1\u6210\u70df\u4e91\u68a6\uff0c\u4e8c\u516c\u5b50\uff0c\u60f3\u5f00\u4e00\u4e9b\u5440\uff01 \u4eca\u591c\u653e\u6000\u4e14\u5c3d\u6b22\uff0c\u660e\u65e5\u9ec4\u6cc9\u8def\u9014\u9065\uff1b\u8001\u592b\u53c8\u4f5c\u4e86\u4e00\u4ef6\u597d\u4e8b\u554a\uff01\u201d \u3000\u3000\u54c8\u54c8\u4e00\u7b11\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u201c\u597d\u9634\u68ee\u3001\u597d\u51b7\u9177\uff01\u201d\u674e\u4e16\u6c11\u5750\u6b63\u4e86\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4ee5\u5413\u5f97\u4eba\u5fc3\u60ca\u3001\u80c6\u98a4\u4e3a\u4e50\uff0c\u8fd9\u4e2a\u4eba\u5df2 \u7ecf\u5168\u65e0\u4eba\u6027\u4e86\u3002\u201d \u3000\u3000\u5982\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u80fd\u529b\u654c\u5343\u4eba\uff0c\u6076\u6218\u4e00\u65e5\u591c\u6ef4\u6c34\u4e0d\u8fdb\uff0c\u4ecd\u80fd\u6218\u4e0b \u53bb\uff0c\u5176\u8010\u529b\u4e4b\u5f3a\uff0c\u5927\u738b\u4ea6\u662f\u6709\u6240\u4e0d\u53ca\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4eba\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u600e\u80fd\u4e45\u6218\u4e0d\u75b2\uff1f\u201d \u3000\u3000\u201c\u6240\u4ee5\u949f\u6728\u9b41\u4e0d\u7b97\u662f\u4eba\u4e86\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u4ed6\u628a\u81ea\u5df1\u7ec3\u5f97\u8ddf\u4e00\u822c\u67af\u6728\u4e00\u822c\uff0c\u6ca1\u6709\u7231\u6b32\uff0c\u4e0d \u80fd\u4eb2\u8fd1\u5973\u8272\uff0c\u9664\u4e86\u80fd\u559d\u70b9\u597d\u9152\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u7684\u5a31\u4e50\u4e86\uff0c\u5269\u4e0b\u7684\u53ea\u662f\u6218\u6597\u673a\u68b0\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4ed6\u65e0\u7231\u65e0\u6b32\uff0c\u600e\u7684\u7518\u613f\u53d7\u866c\u9aef\u5ba2\u7684\u8282\u5236\uff1f\u800c\u4e14\u5fe0\u5fc3\u4e0d\u6e1d\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\u6050\u975e\u4e8c\u516c\u5b50\u6240\u60f3\u7684\u9053\u4e49\u4ea4\u5f80\uff0c\u4e00\u4e2a\u4eba\u5230\u4e86\u65e0\u6b32\u65e0\u7231\u7684\u5883\u754c\uff0c\u5b58\u5728\u5fc3\u4e2d \u7684\u53ef\u80fd\u662f\u754f\u3001\u6068\u4e8c\u5b57\uff0c\u6068\u4e16\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u548c\u4ed6\u4e00\u6837\uff0c\u5f62\u5982\u67af\u6728\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u6574\u8d77\u4eba\u6765\uff0c\u72e0\u5f97\u53ef\u6015\uff0c\u4e5f\u754f\u60e7\u80fd\u4e00\u51fb\u5236\u4ed6\u4e8e\u6b7b\u7684\u4eba\uff0c\u5bf9\u90a3\u4eba\u5c31\u53d8\u5f97\u767e\u4f9d\u767e\u987a\u4e86\u3002 \u4ed6\u8ba4\u4e3a\u5927\u738b\u80fd\u5236\u4ed6\u4e8e\u6b7b\uff0c\u5c31\u5bf9\u5927\u738b\u5fe0\u5fc3\u4e0b\u4e8c\u4e86\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u542c\u8d77\u6765\u5927\u6709\u9053\u7406\uff0c\u4e0d\u77e5\u8a00\u6709\u6240\u672c\u547c\uff1f\u96be\u9053\u4e03\u7edd\u9053\u957f\u3001\u674e\u9756\u3001\u5f20\u59d1\u5a18\u90fd\u6ca1\u6709 \u6740\u4ed6\u7684\u80fd\u529b\u5417\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u6240\u672c\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u8fd9\u4e9b\u8bba\u636e\u90fd\u662f\u59be\u5a62\u6697\u4e2d\u89c2\u5bdf\u6240\u5f97\uff0c\u53ea\u4ece\u674e\u7237\u4f20\u4e86\u59be\u5a62\u6b66\u529f\u3001 \u672f\u6cd5\u4e4b\u540e\uff0c\u59be\u5a62\u4e5f\u5f00\u59cb\u7559\u5fc3\u5468\u56f4\u7684\u4eba\u4eba\u4e8b\u4e8b\u3002\u591a\u5e74\u7d2f\u79ef\uff0c\u5f97\u5230\u4e86\u8fd9\u4e9b\u7ed3\u8bba\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5927\u738b\u5177\u6709\u7684\u795e\u529b\u5a01\u731b\uff0c\u786e\u4e5f\u662f\u60ca\u5929\u52a8\u5730\uff0c\u8ba9\u4eba\u4e00\u89c1\u4e4b\u4e0b\uff0c\u6c38\u6000\u656c\u754f\uff0c\u4e0d\u6562\u751f\u53db\u9006 \u4e4b\u5fc3\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u795e\u5a01\u51db\u4eba\uff0c\u662f\u4e00\u4f4d\u83ab\u53ef\u5c0f\u89d1\u7684\u5927\u654c\u3002\u201d \u3000\u3000\u201c\u6709\u4eba\u6765\u4e86\uff0c\u4e8c\u516c\u5b50\u8bf7\u4f0f\u6848\u5047\u5bd0\u3002\u201d \u3000\u3000\u5982\u7389\u4f4e\u58f0\u8bf4\u7740\uff0c\u4e00\u9762\u4f38\u51fa\u7389\u624b\u8f7b\u8f7b\u5728\u674e\u4e16\u6c11\u7684\u8eab\u4e0a\u6572\u6253\u3002 \u3000\u3000\u4f46\u95fb\u6b65\u5c65\u58f0\u54cd\uff0c\u9f99\u5c06\u6c5f\u68ee\u3001\u864e\u5c06\u71d5\u53ef\u5e76\u80a9\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u540e\u7d27\u968f\u7740\u624b\u6267\u94c1\u6756\u7684\u949f\u6728\u9b41\u3002 \u3000\u3000\u9f99\u5c06\u6c5f\u68ee\u9053\uff1a\u201c\u6728\u8001\uff0c\u4e8c\u516c\u5b50\u786e\u4f3c\u6709\u4e00\u4e9b\u4e0d\u8212\u670d\u7684\u6837\u5b50\uff0c\u5927\u738b\u6b22\u5bb4\u674e\u9756\uff0c\u8bf7\u4e8c\u516c\u5b50\u4e0b\u5c71 \u4f5c\u966a\uff0c\u8981\u600e\u4e48\u4ea4\u4ee3\u624d\u597d\uff1f\u201d \u3000\u3000\u201c\u5b9e\u8bdd\u5b9e\u8bf4\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u544a\u8bc9\u5927\u738b\uff0c\u4eca\u756a\u4f5c\u7f62\uff0c\u6765\u65e5\u65b9\u957f\u554a\uff01\u201d \u3000\u3000\u864e\u5c06\u71d5\u53ef\u9053\uff1a\u201c\u575a\u6301\u8bf7\u4e8c\u516c\u5b50\u8d74\u5bb4\u4f5c\u966a\u7684\u662f\u674e\u9756\uff0c\u54b1\u4eec\u5982\u4e0d\u80fd\u8bf7\u5f97\u674e\u4e8c\u516c\u5b50\u4e0b\u5c71\uff0c\u53ea\u6015 \u674e\u9756\u8981\u4eb2\u81ea\u8d76\u6765\u63a2\u89c6\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u5c31\u7b97\u674e\u9756\u4eb2\u81ea\u8d76\u6765\uff0c\u4e8c\u516c\u5b50\u4e5f\u662f\u75c5\u4e86\u3002\u8001\u592b\u53c8\u4e0d\u662f\u8bf4\u8c0e\u4e86\uff01\u201d \u3000\u3000\u201c\u6728\u8001\uff0c\u8fd9\u6837\u5427\u2026\u2026\u201d\u6c5f\u68ee\u9053\uff1a\u201c\u6211\u4eec\u95ee\u4e00\u4e0b\u4e8c\u516c\u5b50\uff0c\u7531\u4ed6\u81ea\u5df1\u51b3\u5b9a\uff0c\u4ed6\u5982\u8981\u62b1\u75c5\u53c2 \u52a0\uff0c\u54b1\u4eec\u5c31\u5e26\u4ed6\u4e0b\u5c71\uff1b\u4ed6\u5982\u4e0d\u613f\u8d74\u5bb4\uff0c\u54b1\u4eec\u4e5f\u4e0d\u80fd\u76f8\u5f3a\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u4eec\u4eb2\u81ea\u95ee\u95ee\u4ed6\uff0c\u7531\u4ed6\u81ea\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u628a\u4e09\u4eba\u7684\u5bf9\u8bdd\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f46\u5374\u88c5\u4f5c\u672a\u95fb\uff0c\u4f0f\u6848\u672a\u52a8\u3002 \u3000\u3000\u53ea\u5f85\u6c5f\u68ee\u4e8c\u5ea6\u559d\u95ee\uff0c\u674e\u4e16\u6c11\u624d\u62ac\u8d77\u5934\u6765\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u9f99\u3001\u864e\u4e8c\u5c06\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c06\u519b\u53eb\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\uff01\u201d\u6c5f\u68ee\u9053\uff1a\u201c\u5927\u738b\u8bf7\u4e8c\u516c\u5b50\u8d74\u5bb4\u5927\u8425\uff0c\u4e0d\u77e5\u4e8c\u516c\u5b50\u4e2d\u5426\u524d\u5f80\uff1f\u201d \u3000\u3000\u201c\u6c5f\u68ee\u3001\u71d5\u53ef\uff0c\u4f60\u4eec\u8bf4\u6e05\u695a\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u7a76\u7adf\u662f\u674e\u9756\u8981\u8bf7\u4e8c\u516c\u5b50\u8d74\u5bb4\u5462\uff1f\u8fd8\u662f\u5927\u738b \u8981\u8bf7\u4e8c\u516c\u5b50\u8d74\u5bb4\u3002\u201d \u3000\u3000\u201c\u674e\u9756\u63d0\u8bae\uff01\u201d\u71d5\u53ef\u9053\uff1a\u201c\u9080\u8bf7\u4e8c\u516c\u5b50\u864e\u5e10\u4f5c\u966a\uff0c\u5927\u738b\u540c\u610f\u4e86\uff0c\u4e0b\u4ee4\u6211\u7b49\u6765\u89c1\u6728\u8001\u63a5 \u4eba\uff0c\u8bf4\u662f\u674e\u9756\u9080\u8bf7\u4e8c\u516c\u5b50\u6ca1\u6709\u9519\uff0c\u8bf4\u662f\u5927\u738b\u9080\u8bf7\u4e5f\u4e0d\u7b97\u9519\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u65e2\u662f\u5927\u738b\u6709\u4ee4\uff0c\u4e24\u4f4d\u5c31\u548c\u674e\u4e8c\u516c\u5b50\u5546\u91cf\u5427\uff01\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u4e2d\u6b63\u662f\u53cd\u590d\u601d\u8651\uff0c\u662f\u5426\u8be5\u4e0b\u5c71\u8d74\u5bb4\uff1f\u5982\u88ab\u4e03\u7edd\u9053\u957f\u53d1\u89c9\u4e86\u81ea\u5df1\u6ca1\u6709\u670d\u836f\u4e2d\u6bd2\uff0c \u5f88\u53ef\u80fd\u4f1a\u4e0d\u987e\u4e00\u5207\u7684\u4e0b\u624b\uff0c\u4e00\u573a\u5bb4\u4f1a\u5f88\u53ef\u80fd\u53d8\u6210\u4e00\u573a\u6076\u6218\uff0c\u4e5f\u53ef\u80fd\u662f\u4e03\u7edd\u9053\u957f\u7684\u9634\u8c0b\u5f97\u901e\uff0c \u6210\u5c31\u4e00\u573a\u8c0b\u6740\u60b2\u5267\u3002 \u3000\u3000\u800c\u866c\u9aef\u5ba2\u4e5f\u5728\u5bb4\u4f1a\u4e2d\uff0c\u4ed6\u5982\u652f\u6301\u4e03\u7edd\u9053\u957f\uff0c\u674e\u9756\u7edd\u96be\u72ec\u529b\u652f\u6301\uff0c\u52a0\u4e0a\u5f20\u51fa\u5c18\u4e5f\u662f\u80dc\u7b97\u6781 \u5fae\uff0c\u4f46\u674e\u9756\u4e3a\u4e86\u4fdd\u62a4\u4ed6\uff0c\u52bf\u5c06\u529b\u62fc\u5230\u5e95\uff0c\u8fd9\u4e5f\u5c31\u88ab\u4e00\u7f51\u6253\u5c3d\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5343\u56de\u767e\u8f6c\uff0c\u8861\u91cf\u8fc7\u5229\u5bb3\u4e4b\u540e\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5927\u738b\u7684\u597d\u610f\u5fc3\u9886\u4e86\uff0c\u4e16\u6c11\u5fc3\u80f8\u80c0\u95f7\uff0c \u660f\u660f\u6b32\u7761\uff0c\u6050\u6015\u96be\u8d74\u76db\u5bb4\u4e86\u3002\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u5982\u6b64\u8bf4\uff0c\u6211\u7b49\u4e0d\u4fbf\u52c9\u5f3a\u3002\u201d\u9f99\u5c06\u6c5f\u68ee\u9053\uff1a\u201c\u56de\u590d\u5927\u738b\u5c31\u662f\uff0c\u4e0d\u8fc7\uff0c\u674e\u9756\u4f3c\u662f\u6781 \u4e3a\u6302\u5ff5\u4e8c\u516c\u5b50\uff0c\u4e5f\u8bb8\u4f1a\u6765\u884c\u5b98\u63a2\u89c6\uff0c\u5982\u7389\u59d1\u5a18\u597d\u597d\u7684\u7167\u987e\u4e8c\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u7684\u75c5\u60c5\u6765\u5f97\u51f6\u731b\uff0c\u5f81\u5146\u5374\u4e0d\u660e\uff0c\u5c0f\u5a62\u4e0d\u662f\u5927\u592b\uff0c\u4e0d\u4f1a\u7528\u836f\uff0c \u53ea\u80fd\u5c3d\u529b\u4f8d\u5019\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u52a0\u91cd\u8bed\u6c14\uff0c\u4e5f\u662f\u8bf1\u903c\u674e\u9756\u4e0a\u5c71\u63a2\u75c5\u7684\u8c0b\u7565\u3002 \u3000\u3000\u6c5f\u68ee\u5012\u672a\u60f3\u5f97\u5982\u6b64\u6df1\u8fdc\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u544a\u8bc9\u5927\u738b\uff0c\u5dee\u5927\u592b\u4e0a\u5c71\uff0c\u4e3a\u4e8c\u516c\u5b50\u628a\u8109\u8bca \u7597\u75c5\u60c5\u3002\u201d \u3000\u3000\u71d5\u53ef\u63a5\u8fc7\uff1a\u201c\u6211\u4eec\u4e5f\u4f1a\u544a\u8bc9\u674e\u9756\uff0c\u7531\u4ed6\u51b3\u5b9a\u51e0\u65f6\u4e0a\u5c71\u63a2\u89c6\u4e8c\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4e8c\u4f4d\u5c06\u519b\uff01\u201d\u5982\u7389\u9053\uff1a\u201c\u5927\u592b\u8bca\u89c6\u8fc7\u4e8c\u516c\u5b50\u7684\u75c5\u60c5\uff0c\u5c0f\u5a62\u4e5f\u53ef\u4ee5\u89e3\u53bb\u5fc3\u4e2d\u4e00\u4e9b\u538b \u529b\u3002\u201d \u3000\u3000\u6c5f\u68ee\u9053\uff1a\u201c\u6211\u4eec\u968f\u540c\u5f20\u59d1\u5a18\uff0c\u628a\u4e8c\u516c\u5b50\u5f3a\u9080\u4e0a\u5c71\uff0c\u4e00\u65e6\u4e8c\u516c\u5b50\u75c5\u5371\u534e\u5c71\u884c\u5b98\uff0c\u6211\u548c\u71d5\u53ef \u90fd\u6709\u4e00\u4efd\u6127\u759a\uff0c\u4e8c\u516c\u5b50\u591a\u591a\u4fdd\u91cd\uff0c\u6211\u7b49\u56de\u53bb\u590d\u547d\u3002\u201d \u3000\u3000\u201c\u6055\u4e16\u6c11\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u4ed6\u88c5\u5f97\u5f88\u50cf\uff0c\u9887\u6709\u75c5\u52bf\u903c\u4eba\u6079\u6079\u65e0\u529b\u7684\u6837\u5b50\uff0c\u8fde\u949f\u6728\u9b41\u4e5f\u770b\u5f97\u6709\u4e9b\u4e0d\u5fcd\u4e86\u3002 \u3000\u3000\u9f99\u3001\u864e\u4e8c\u5c06\u5306\u5306\u800c\u53bb\u3002 \u3000\u3000\u949f\u6728\u9b41\u5374\u4f4e\u58f0\u53f9\u9053\uff1a\u201c\u628a\u884c\u5bab\u4e2d\u73cd\u85cf\u7684\u5343\u5e74\u8001\u53c2\u53d6\u4e00\u652f\u6765\u66ff\u4e8c\u516c\u5b50\u716e\u4e00\u7897\u53c2\u6c64\u559d\u4e86\uff0c\u8fd9 \u4e24\u5929\u8981\u4ed6\u6d3b\u5f97\u5feb\u4e50\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u6728\u8001\u4e0d\u4e0b\u4ee4\uff0c\u53ea\u6015\u4ed6\u4eec\u4e0d\u80af\u4ea4\u51fa\u8001\u53c2\u3002\u201d\u5982\u7389\u9053\uff1a\u201c\u90a3\u53ef\u662f\u73cd\u8d35\u975e\u51e1\u4e4b\u7269\uff0c\u884c\u5bab\u4e2d\u4e5f \u5b58\u91cf\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u53ea\u6709\u4e24\u652f\u4e86\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u6211\u4f1a\u8981\u4ed6\u4eec\u9001\u4e00\u652f\u6765\uff0c\u5927\u738b\u795e\u529f\u975e\u51e1\uff0c\u4e5f\u7528\u4e0d\u7740\u8fd9\u4e9b\u8865 \u54c1\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e00\u8dc3\uff0c\u5df2\u5931\u8eab\u5f71\u3002 \u3000\u3000\u5982\u7389\u201c\u5657\u201d\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u4f5c\u8d77\u620f\u6765\uff0c\u53ef\u4e5f\u662f\u60df\u5999\u60df\u8096\u554a\uff01\u9a97\u4e00\u652f\u5343\u5e74\u8001\u53c2\u716e\u6c64 \u559d\uff0c\u53ef\u4ee5\u589e\u52a0\u4f60\u5341\u5e74\u529f\u529b\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u5982\u771f\u662f\u5343\u5e74\u8001\u53c2\uff0c\u53ef\u662f\u5f97\u4e4b\u4e0d\u6613\uff0c\u592a\u539f\u4faf\u5e9c\u4e2d\u7684\u5b58\u53c2\uff0c\u6700\u540d\u8d35\u7684\u4e00\u652f\u4e5f\u53ea \u6709\u4e09\u767e\u5e74\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u6211\u542c\u8bf4\u8fc7\u8fd9\u91cc\u7684\u4e24\u652f\u8001\u53c2\uff0c\u53ef\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u5343\u5e74\u4e4b\u7269\u3002\u539f\u672c\u85cf\u6709\u4e09\u652f\uff0c\u4e3a\u4e86\u8ba9\u5f20\u59d1\u5a18 \u5251\u672f\u65e9\u6210\uff0c\u5927\u738b\u628a\u4e00\u652f\u8d60\u9001\u5f20\u59d1\u5a18\uff0c\u716e\u6210\u4eba\u53c2\u9e21\u6c64\u5403\u4e86\uff0c\u5f20\u59d1\u5a18\u80fd\u5728\u4e00\u5e74\u591a\u7684\u65f6\u5149\u4e2d\u7ec3\u6210\u4e86 \u9a6d\u5251\u672f\uff0c\u90a3\u652f\u5343\u5e74\u8001\u53c2\u5e94\u5c45\u9996\u529f\u3002 \u3000\u3000\u4e0d\u662f\u5a62\u5b50\u591a\u5634\uff0c\u5f20\u59d1\u5a18\u5bf9\u5927\u738b\u9053\u4f3c\u65e0\u60c5\u82e5\u6709\u60c5\uff0c\u4e5f\u96be\u602a\u5927\u738b\u6709\u4e9b\u5fc3\u9189\u3002\u8001\u5b9e\u8bf4\uff0c\u674e\u7237\u5982 \u975e\u65e9\u548c\u5f20\u59d1\u5a18\u7ed3\u6210\u592b\u5987\uff0c\u73b0\u5728\u8981\u5f20\u59d1\u5a18\u91cd\u65b0\u9009\u5a5a\uff0c\u662f\u5927\u738b\uff1f\u662f\u674e\u7237\uff1f\u8fd8\u662f\u672a\u5b9a\u4e4b\u5929\u3002\u201d \u3000\u3000\u201c\u4eba\u975e\u8349\u6728\uff0c\u5b70\u80fd\u65e0\u60c5\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5bf9\u5f20\u51fa\u5c18\u7528\u60c5\u4e4b\u6df1\uff0c\u4e00\u773c\u53ef\u89c1\u3002\u9519\u9519 \u9519\uff01\u628a\u6211\u674e\u4e16\u6c11\u5f15\u6765\u4e86\u534e\u5c71\u884c\u5bab\uff0c\u4e5f\u62db\u6765\u674e\u9756\u5230\u6b64\u3002\u201d \u3000\u3000\u5982\u7389\u9053\uff1a\u201c\u5982\u679c\u672a\u628a\u4e8c\u516c\u5b50\u63b3\u6765\u5462\uff1f\u4f1a\u662f\u4e2a\u4ec0\u4e48\u7ed3\u679c\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a\u201c\u674e\u9756\u592b\u5987\u4e0d\u89c1\u9762\uff0c\u5c31\u8bef\u4f1a\u96be\u89e3\uff0c\u518d\u62d6\u4e2a\u4e00\u5e74\u4e24\u8f7d\uff0c\u5f7c\u6b64\u90fd\u4f1a\u6de1\u4e86\u4e0b\u6765\u3002\u866c \u9aef\u5ba2\u548c\u5f20\u51fa\u5c18\u7684\u611f\u60c5\uff0c\u5374\u56e0\u671d\u5915\u76f8\u5904\uff0c\u4e0e\u65e5\u4ff1\u589e\u3002\u540e\u679c\u5982\u4f55\uff1f\u4e0d\u8a00\u53ef\u8c15\u4e86\uff0c\u5c31\u522b\u8bf4\u8fd9\u4e2d\u95f4\u8fd8 \u53ef\u80fd\u53d1\u751f\u4e9b\u610f\u5916\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u5982\u7389\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u4f60\u60f3\u7684\u6bd4\u59be\u5a62\u8fd8\u8981\u900f\u5f7b\uff0c\u674e\u7237\u771f\u8981\u5931\u53bb\u5a07\u59bb\uff0c\u4e0d\u77e5\u8981\u5982 \u4f55\u75db\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u7389\uff0c\u4e0d\u8981\u4f4e\u4f30\u4e86\u674e\u9756\uff0c\u4ee5\u4ed6\u7684\u4fca\u9038\u3001\u624d\u534e\uff0c\u6b63\u662f\u5929\u4e0b\u5c11\u5973\u4eec\u68a6\u4e2d\u7684\u60c5\u90ce\uff0c\u53ea\u8981\u4ed6\u677e \u677e\u53e3\uff0c\u7f8e\u5973\u4f1a\u6392\u961f\u4ee5\u8fce\u3002 \u3000\u3000\u4f60\u77e5\u9053\u5417\uff1f\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u5f20\u51fa\u5c18\u3001\u8881\u5b9d\u513f\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4f4d\u4ed9\u5973\u4e0b\u51e1\u4e00\u822c\u7684\u7f8e\u5973\uff0c\u540d\u53eb \u8881\u7d2b\u70df\u2026\u2026\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u89c1\u8fc7\u5979\u5417\uff1f\u201d \u3000\u3000\u201c\u89c1\u8fc7\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u8bba\u6c14\u8d28\u9ad8\u96c5\uff0c\u59ff\u5bb9\u6e05\u4e3d\uff0c\u5f20\u51fa\u5c18\u672a\u5fc5\u80fd\u53ca\uff0c\u674e\u9756\u4e5f\u89c1\u8fc7\u5979\u3002\u201d \u3000\u3000\u201c\u674e\u7237\u52a8\u5fc3\u4e86\uff1f\u201d\u5982\u7389\u9053\uff1a\u201c\u4e16\u4e0a\u8fd8\u6709\u6bd4\u8fc7\u5f20\u59d1\u5a18\u7684\u7f8e\u5973\uff0c\u771f\u662f\u96be\u4ee5\u76f8\u4fe1\u54ea\uff01\u201d \u3000\u3000\u201c\u674e\u9756\u5fc3\u4e2d\u53ea\u6709\u5f20\u51fa\u5c18\u3002\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4e0d\u66fe\u52a8\u5fc3\uff0c\u4f46\u5982\u5f20\u59d1\u5a18\u79fb\u60c5\u522b\u604b\uff0c\u90a3\u5c31 \u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5982\u7389\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u8881\u7d2b\u70df\u662f\u5426\u559c\u6b22\u674e\u7237\u5462\uff1f\u4e8c\u516c\u5b50\u662f\u5426\u5df2\u770b\u51fa\u4e00\u4e9b\u82d7\u5934\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u518d\u8c08\u4e86\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u8fd9\u4e00\u4ee3\u4eba\u4e2d\u7cbe\u82f1\uff0c\u90fd\u5177\u9f99\u51e4\u4e4b\u59ff\uff0c\u8c01\u80fd\u5984\u4f5c\u6d4b \u65ad\uff1f\u4f46\u613f\u51fa\u5c18\u59d1\u5a18\u60c5\u575a\u91d1\u77f3\u3002\u674e\u9756\u7231\u60c5\u4e13\u4e00\uff0c\u90a3\u624d\u662f\u4e00\u6bb5\u4eba\u95f4\u4f73\u8bdd\uff0c\u7f8e\u6ee1\u826f\u7f18\u3002\u5982\u7389\uff0c\u6211\u4eec \u4e00\u540c\u5411\u5929\u7948\u6c42\uff0c\u795d\u4ed6\u4eec\u65e0\u96be\u65e0\u707e\uff0c\u4f73\u5076\u5929\u6210\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u795d\u4ed6\u4eec\u767e\u5e74\u597d\u5408\uff01\u201d \u3000\u3000\u5982\u7389\u771f\u7684\u95ed\u4e0a\u53cc\u76ee\uff0c\u5408\u638c\u5f53\u80f8\uff0c\u9ed8\u9ed8\u5730\u795d\u7977\u3002 \u3000\u3000\u674e\u9756\u7684\u795e\u60c5\u5f88\u4e25\u8083\uff0c\u53cc\u76ee\u76ef\u5728\u5f20\u51fa\u5c18\u8138\u4e0a\u770b\u3002 \u3000\u3000\u4ed6\u4eec\u5c06\u8fd1\u4e00\u5e74\u6ca1\u89c1\u8fc7\u9762\u4e86\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u51dd\u795e\u671b\u7740\u592b\u541b\uff0c\u76ee\u5149\u6ee1\u662f\u6e29\u67d4\u3001\u60c5\u610f\u3002 \u3000\u3000\u706f\u5149\u4e0b\uff0c\u770b\u7f8e\u4eba\uff0c\u5f20\u51fa\u5c18\u589e\u6dfb\u4e0d\u5c11\u5a07\u7f8e\u3002 \u3000\u3000\u201c\u51fa\u5c18\uff0c\u4f60\u7626\u4e86\uff0c\u4e5f\u500d\u589e\u4e86\u6e05\u4e3d\u4e4b\u6c14\u3002\u201d \u3000\u3000\u674e\u9756\u8bf4\u51fa\u4e86\u7b2c\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u201c\u76f8\u601d\u607c\u4eba\u554a\uff01\u6211\u6068\u4f60\u597d\u5fcd\u5fc3\uff0c\u629b\u4e0b\u6211\uff0c\u4e00\u4e2a\u4eba\u8fdc\u8d70\u5929\u6daf\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u5168\u4e0d\u5ff5\u65b0\u5a5a\u71d5\u5c14\u7684\u592b\u59bb\u60c5\u610f\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u6df1\u95fa\u7f20\u7ef5\uff0c\u6625\u60c5\u65e0\u9650\u3002\u51fa\u5c18\uff0c\u4f60\u80fd\u7ec3\u6210\u8fd9\u9ad8\u6df1\u5251\u672f\u5417\uff1f\u6211\u8d70\u5f00\uff0c\u662f\u4e3a\u4e86\u4f60 \u597d\uff0c\u6210\u5168\u4f60\u7ec3\u6210\u4e0a\u4e58\u5251\u672f\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u201c\u89c1\u8fc7\u4e86\u674e\u4e16\u6c11\uff0c\u6211\u624d\u77e5\u9053\u4f60\u7684\u6069\u60c5\u4f1f\u5927\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u81ea\u79c1\uff0c\u6c89\u8ff7\u4e8e\u7ec3\u5251\u4e4b\u4e2d\uff0c \u903c\u8d70\u4e86\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u4f60\u5374\u9ed8\u9ed8\u5730\u627f\u53d7\u4e86\u6211\u7684\u51b7\u6de1\uff0c\u4e0d\u53d1\u4e00\u53e5\u6028\u8a00\uff0c\u6210\u5168\u6211\u7684\u81ea\u79c1\u5fc3\u613f\u3002 \u3000\u3000\u6211\u597d\u60ed\u6127\uff0c\u7adf\u7136\u4f53\u4f1a\u4e0d\u5230\u4f60\u4ed8\u51fa\u6df1\u60c5\u7231\u610f\uff0c\u53cd\u800c\u611f\u89c9\u88ab\u4f60\u629b\u5f03\uff0c\u4e8c\u516c\u5b50\u5206\u6790\u4e86\u5176\u4e2d\u60c5 \u52bf\uff0c\u624d\u77e5\u9053\u5168\u662f\u6211\u7684\u9519\uff0c\u674e\u9756\uff0c\u539f\u8c05\u6211\uff01\u9976\u6055\u6211\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6251\u4eba\u674e\u9756\u7684\u6000\u91cc\uff0c\u7d27\u7d27\u62b1\u4f4f\u674e\u9756\uff0c\u53c8\u9053\uff1a\u201c\u9192\u6765\u624d\u77e5\u68a6\u4e2d\u9669\uff0c\u5fc6\u5f80\u4e8b\u4f59\u60b8\u72b9\u5b58\u3002 \u4e0d\u8981\u518d\u79bb\u5f00\u6211\uff0c\u8981\u8d70\uff0c\u5c31\u5e26\u7740\u6211\u4e00\u8d77\u8d70\uff0c\u5929\u6daf\u6d77\u89d2\u968f\u592b\u884c\u3002\u201d \u3000\u3000\u201c\u90fd\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002\u201d\u674e\u9756\u8f7b\u8f7b\u62cd\u7740\u5f20\u51fa\u5c18\u7684\u80a9\u80cc\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u4f60\u5df2\u7ecf\u7ec3\u6210 \u54cd\u5f80\u7684\u9a6d\u5251\u4e4b\u672f\uff0c\u5fc3\u613f\u5f97\u507f\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u63a5\u9053\uff1a\u201c\u53ef\u662f\uff0c\u51e0\u4e4e\u5931\u53bb\u4e86\u4e08\u592b\uff0c\u73b0\u5728\u518d\u8ba9\u6211\u4f5c\u9009\u62e9\uff0c\u6211\u4e0d\u8981\u7ec3\u5251\u4e86\uff0c\u5b81\u53ef\u4f5c\u4e00 \u4e2a\u716e\u996d\u6d17\u8863\u7684\u5c0f\u5987\u4eba\uff0c\u7ec8\u65e5\u968f\u4f8d\u592b\u541b\u4fa7\u3002\u201d \u3000\u3000\u201c\u51fa\u5c18\uff0c\u4f60\u4f1a\u5fc3\u6709\u9057\u61be\u7684\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u4e8b\u975e\u7ecf\u8fc7\u4e0d\u77e5\u96be\uff0c\u5f53\u65f6\u4f60\u6c89\u9189\u5251\u672f\u7684\u72c2\u70ed\uff0c\u8fde \u6027\u547d\u90fd\u53ef\u4ee5\u629b\u5f03\uff0c\u5251\u672f\u4e0d\u53ef\u4e0d\u7ec3\uff0c\u6211\u5982\u5f3a\u529b\u963b\u6b62\u4f60\uff0c\u4f60\u4f1a\u6068\u6211\u7684\uff0c\u5c31\u7b97\u4f60\u6e29\u67d4\u7684\u7b54\u5e94\u4e86\uff0c\u4e5f \u4f1a\u5728\u5fc3\u4e2d\u7559\u4e0b\u4e86\u5f88\u5927\u7684\u4f24\u5bb3\u548c\u9057\u61be\uff0c\u4f1a\u8ba9\u4f60\u4e00\u751f\u4e0d\u5feb\u4e50\u3002 \u3000\u3000\u6211\u7528\u5168\u90e8\u7231\u4f5c\u8865\u507f\uff0c\u4e5f\u4e0d\u80fd\u586b\u8865\u5fc3\u4e2d\u7559\u4e0b\u7684\u9057\u61be\uff0c\u6211\u600e\u80fd\u5982\u6b64\u7684\u81ea\u79c1\uff1f\u518d\u8bf4\u4e09\u5143\u674e\u9756\u7684 \u592b\u4eba\u4e5f\u5e94\u8be5\u662f\u7eb5\u6a2a\u5929\u4e0b\uff0c\u9a6d\u5251\u98de\u884c\u5dfe\u5e3c\u82f1\u8c6a\u554a\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7f13\u7f13\u79bb\u5f00\u674e\u9756\u7684\u6000\u62b1\uff0c\u9053\uff1a\u201c\u674e\u9756\uff0c\u4f60\u771f\u7684\u8fd9\u6837\u60f3\u5417\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u771f\u7684\uff0c\u6211\u8d70\u5f97\u80f8\u65e0\u5757\u5792\uff0c\u5fc3\u5b89\u7406\u5f97\u3002\u53ea\u662f\u4f60\u5728\u77ed\u77ed\u4e00\u5e74\u591a\u7684\u65f6\u95f4\u4e2d\uff0c\u7adf\u7136\u4f7f \u5251\u672f\u6709\u6210\uff0c\u8fdb\u6b65\u5feb\u901f\uff0c\u5012\u662f\u51fa\u4e86\u6211\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u4e00\u70b9\u4e5f\u4e0d\u6000\u7591\u6211\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u653e\u5fc3\u7684\u79bb\u5f00\u6211\uff0c\u8fdc\u8d70\u5230\u6570\u5343\u91cc\u5916\u3002\u201d \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u6211\u76f8\u4fe1\u4f60\u81ea\u5df1\u7684\u4e3b\u89c1\uff0c\u6211\u7684\u5224\u65ad\u4e0d\u9519\u554a\uff01\u6211\u4e5f\u76f8\u4fe1\u4ef2\u575a\u5927 \u54e5\u662f\u4e00\u4f4d\u5f88\u6709\u4e49\u6c14\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u674e\u9756\uff0c\u4eba\u5fc3\u96be\u6d4b\u554a\u2026\u2026\u201d \u3000\u3000\u674e\u9756\u7a81\u7136\u63a8\u5f00\u4e86\u5f20\u51fa\u5c18\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f3\u624b\u626c\u5904\uff0c\u4e00\u7247\u91d1\u8292\u75be\u5411\u800c\u51fa\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u970d\u7136\u62d4\u51fa\u58c1\u4e0a\u7684\u957f\u5251\uff0c\u51c6\u5907\u51fa\u624b\u4e86\u3002 \u3000\u3000\u201c\u662f\u8d2b\u9053\uff0c\u674e\u7237\u7684\u593a\u547d\u91d1\u73af\u66f4\u6709\u5a01\u529b\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u4e03\u7edd\u9053\u5144\uff0c\u8bf7\u8fdb\u623f\u91cc\u5750\u3002\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u63a8\u95e8\u800c\u5165\uff0c\u53f3\u624b\u6267\u8457\u4e00\u679a\u91d1\u73af\uff0c\u606d\u606d\u656c\u656c\u5730\u9012\u7ed9\u674e\u9756\u3002 \u3000\u3000\u674e\u9756\u795e\u8272\u5e73\u9759\uff0c\u7b11\u4e00\u7b11\uff0c\u63a5\u8fc7\u91d1\u73af\uff0c\u5957\u5728\u53f3\u8155\u4e0a\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5374\u9762\u6709\u4e0d\u8c6b\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u9053\u957f\uff0c\u8fd9\u662f\u6211\u7684\u95fa\u623f\uff0c\u9053\u957f\u6084\u7136\u95ef\u5165\uff0c\u4e0d\u89c9\u5f97\u6709\u70b9\u5931\u793c \u5417\uff1f\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u9053\uff1a\u201c\u5927\u738b\u6025\u4ee4\u5ba3\u53ec\u674e\u7237\u548c\u5f20\u59d1\u5a18\u5373\u523b\u5165\u5e10\u8bae\u4e8b\uff0c\u4f20\u62a5\u4e0d\u53ca\uff0c\u8fd8\u8bf7\u5f20\u59d1\u5a18\u6d77 \u6db5\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4ec0\u4e48\u65f6\u5019\u4e86\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u8fd8\u5251\u4eba\u9798\uff0c\u53e3\u6c14\u51b7\u7136\u7684\u9053\uff1a\u201c\u521d\u66f4\u5c06\u5c3d\uff0c\u4e8c\u66f4\u4e0d\u5230\u3002\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u9053\uff1a\u201c\u89c1\u4e24\u4f4d\u623f\u4e2d\u4ecd\u71c3\u706f\u5149\uff0c\u8d2b\u9053\u60f3\u674e\u7237\u65e2\u5df2\u5f52\u6765\uff0c\u5f7c\u6b64\u8c0a\u5c5e\u597d\u53cb\uff0c\u5927\u738b\u53ec\u8bf7 \u4e24\u4f4d\u7814\u5546\u673a\u5bc6\u5927\u4e8b\uff0c\u4e5f\u4e0d\u4fbf\u60ca\u52a8\u4ed6\u4eba\uff0c\u5c31\u8d38\u7136\u95ef\u4e86\u6765\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e5f\u8be5\u5148\u884c\u62db\u547c\u4e00\u58f0\u554a\uff01\u5982\u662f\u6025\u5207\u51fa\u624b\uff0c\u6709\u6240\u635f\u4f24\uff0e\u5c82\u4e0d\u4f24\u4e86\u548c\u6c14\u3002\u201d \u3000\u3000\u5979\u4f3c\u4e4e\u5bf9\u4e03\u7edd\u9053\u957f\u6709\u7740\u5f88\u6df1\u7684\u6210\u89c1\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u6c89\u6df1\u51b7\u9759\uff0c\u4e00\u526f\u53e4\u4e95\u4e0d\u6ce2\u7684\u6837\u5b50\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b63\u8981\u51fa\u8a00\u62db\u547c\uff0c\u4f46\u674e\u7237\u5df2 \u91d1\u73af\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u51fa\u5c18\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4e03\u7edd\u9053\u5144\u662f\u5927\u54e5\u7684\u5fc3\u8179\u91cd\u81e3\uff0c\u5c51\u4e9b\u5c0f\u4e8b\u5c31\u4e0d\u7528\u8ba1\u8f83\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u662f\u674e\u7237\u8c41\u8fbe\uff0c\u8d2b\u9053\u5728\u5927\u738b\u8425\u5e10\u606d\u5019\uff0c\u4e24\u4f4d\u8bf7\u65e9\u5149\u4e34\uff0c\u5927\u738b\u4e5f\u5728\u864e\u5e10\u7b49\u5019\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u5947\u9053\uff1a\u201c\u5728\u8425\u5e10\u7b49\u5019\uff0c\u6df1\u591c\u4e4b\u4e2d\uff0c\u8fd8\u6709\u519b\u60c5\u8bae\u8bba\u5417\uff1f\u201d \u3000\u3000\u201c\u5982\u975e\u662f\u519b\u60c5\u5927\u4e8b\uff0c\u8d2b\u9053\u53c8\u600e\u6562\u8d38\u7136\u95ef\u6765\uff1f\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u8bf4\u5b8c\u8bdd\uff0c\u5408\u638c\u800c\u7acb\uff0c\u5ffd\u7136\u95f4\u6d88\u5931\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\u554a\uff1f\u4e00\u4e2a\u4eba\u4e0d\u52a8\u4e0d\u8dd1\uff0c\u5ffd\u7136\u95f4\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u662f\u9041\u672f\uff0c\u4e03\u7edd\u9053\u957f\u4e0d\u4f46\u6b66\u529f\u7cbe\u7edd\uff0c\u800c\u4e14\u517c\u901a\u672f\u6cd5\uff0c\u4e34\u53bb\u65f6\u70ab\u9732\u4e00\u624b\uff0c\u8ba9\u6211\u4eec \u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u674e\u9756\uff0c\u4f60\u4e5f\u4f1a\u5427\uff1f\u672f\u6cd5\u8be1\u5f02\u795e\u79d8\uff0c\u6740\u4eba\u4e8e\u65e0\u58f0\u65e0\u606f\u4e4b\u4e2d\uff0c\u4efb\u4f55\u6b66\u529f\u4e5f\u90fd\u65e0 \u6cd5\u6297\u62d2\u5b83\u5427\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u5c3d\u7136\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4ef2\u575a\u5927\u54e5\u7684\u6b66\u529f\u5df2\u8fbe\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5c31\u975e\u672f\u6cd5\u80fd\u4f24\u3002\u4f60\u7684\u9a6d \u5251\u65bd\u5c55\u51fa\u624b\uff0c\u4e5f\u4e0d\u662f\u4e00\u822c\u7684\u672f\u6cd5\u80fd\u4f24\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7591\u7136\u9053\uff1a\u201c\u521a\u624d\u4ed6\u6f5c\u5165\u6211\u4eec\u4f4f\u5904\uff0e\u4e3a\u4ec0\u4e48\u4e0d\u65bd\u5c55\u9041\u672f\u5462\uff1f\u90a3\u5c31\u4e0d\u88ab\u6211\u4eec\u53d1\u89c9 \u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u4ed6\u672c\u60f3\u4fdd\u7559\u4e0b\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u53ea\u662f\u5fcd\u4e0d\u4e0b\u5fc3\u4e2d\u7684\u6012\u6c14\uff0c\u4e34\u53bb\u79cb\u6ce2\uff0c\u662f \u53cd\u51fb\u4f60\u7684\u8d23\u602a\uff0c\u8bf4\u660e\u4e86\u4ed6\u4e0d\u662f\u6015\u4f60\uff0c\u53ea\u662f\u5fcd\u8ba9\u3002\u4ef2\u575a\u5927\u54e5\u8eab\u4fa7\u6709\u8fd9\u6837\u7684\u5947\u4eba\uff0c\u8fc7\u53bb\u6211\u4eec\u5168\u65e0 \u6240\u77e5\uff0c\u4e0d\u77e5\u9053\u662f\u5426\u8fd8\u6709\u522b\u7684\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4e2a\u949f\u6728\u9b41\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u5176\u4ed6\u7684\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u6211\u4eec\u867d\u7136\u4e49\u7ed3\u91d1\u5170\uff0c\u4f3c\u662f\u8fd8 \u672a\u5f97\u5230\u4ed6\u7684\u7edd\u5bf9\u4fe1\u4efb\uff0c\u8fd8\u6709\u5f88\u591a\u79d8\u5bc6\u4e0d\u8ba9\u6211\u4eec\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u9756\u6c89\u541f\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8d70\u5427\uff0c\u522b\u8ba9\u5927\u54e5\u7b49\u5019\u592a\u4e45\u3002\u201d \u3000\u3000\u8fdb\u5165\u4e86\u4e2d\u519b\u5927\u5e10\uff0c\u674e\u9756\u7acb\u523b\u53d1\u89c9\u4e86\u8fd9\u662f\u4e00\u6b21\u5341\u5206\u91cd\u8981\u7684\u519b\u4e8b\u4f1a\u8bae\u3002 \u3000\u3000\u5927\u5e10\u4e2d\u5750\u6ee1\u4e86\u4eba\uff0c\u8db3\u8db3\u6709\u516d\u5341\u4f4d\u4e4b\u591a\u3002\u5c24\u3001\u864e\u4e8c\u5c06\u4e4b\u5916\u3002\u8fd8\u6709\u6570\u5341\u4f4d\u7a7f\u7740\u4e0d\u540c\u989c\u8272\u7684\u957f \u888d\u5927\u6c49\u3002 \u3000\u3000\u6392\u6210\u4e00\u4e2a\u9a6c\u8e44\u5f62\uff0c\u73af\u56f4\u7740\u866c\u9aef\u5bb9\u800c\u5750\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u548c\u949f\u6728\u9b41\u5750\u5728\u866c\u9aef\u5ba2\u7684\u53f3\u9996\u3002 \u3000\u3000\u5de6\u9996\u4e24\u4e2a\u4f4d\u5b50\u4f3c\u662f\u7559\u7ed9\u674e\u9756\u548c\u5f20\u51fa\u5c18\u7684\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u5bf9\u674e\u9756\u548c\u5f20\u51fa\u5c18\u6781\u4e3a\u5ba2\u6c14\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4e49\u5f1f\u3001\u4e49\u59b9\u8bf7\u5750\u3002\u201d \u3000\u3000\u65e9\u5df2\u6392\u597d\u7684\u4f4d\u7f6e\uff0c\u674e\u9756\u4e5f\u65e0\u6cd5\u63a8\u8ba9\uff0c\u7275\u7740\u5f20\u51fa\u5c18\u5750\u5165\u5e2d\u4f4d\uff0c\u4e5f\u4e8e\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u8868\u73b0\u4e86\u592b \u5987\u4e4b\u95f4\u4eb2\u5bc6\u548c\u7231\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u5fae\u5fae\u4e00\u76b1\u7709\u5934\u3002 \u3000\u3000\u5f20\u4ef2\u575a\u5374\u6765\u4e2a\u89c6\u800c\u672a\u89c1\uff0c\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u672c\u738b\u539f\u6709\u610f\u548c\u674e\u4e16\u6c11\u5408\u4f5c\uff0c\u5206\u5934\u8fdb\u5175\uff0c \u653b\u5165\u957f\u5b89\u3002\u4f46\u674e\u4e16\u6c11\u7eb5\u60c5\u9152\u8272\uff0c\u81f4\u8eab\u4f53\u4e0d\u9002\uff0c\u65e0\u6cd5\u53c2\u52a0\u8fd9\u573a\u8a93\u5e08\u5927\u4f1a\u3002\u201d \u3000\u3000\u674e\u9756\u4e00\u626c\u53cc\u7709\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\u4e86\u8fd9\u662f\u4e00\u6b21\u65e9\u6709\u8ba1\u5212\u7684\u519b\u4e8b\u884c\u52a8\uff0c\u5728\u672a\u4e86\u89e3\u5185\u60c5\u4e4b\u524d\uff0c\u51fa\u8a00\u963b\u6b62\uff0c\u5f88\u96be\u6709\u8bf4 \u670d\u7684\u529b\u91cf\uff0c\u5012\u4e0d\u5982\u5148\u542c\u4e9b\u5185\u5bb9\u518d\u884c\u53d1\u8a00\u3002 \u3000\u3000\u201c\u4e49\u5f1f\u6709\u4f55\u9ad8\u89c1\uff1f\u201d\u866c\u9aef\u5ba2\u9053\uff1a\u201c\u4f60\u56de\u6765\u5f97\u53ca\u65f6\uff0c\u5929\u52a9\u5c0f\u5144\u4e5f\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u5c0f\u5f1f\u4eca\u65e5\u5f52\u6765\uff0c\u5168\u7136\u4e0d\u77e5\u519b\u60c5\uff0c\u4fdf\u5c0f\u5f1f\u4e86\u89e3\u81f3\u5168\u76d8\u60c5\u52bf\u4e4b\u540e\uff0c\u81ea\u5f53\u63d0\u51fa\u610f \u89c1\uff0c\u606d\u8bf7\u5927\u54e5\u88c1\u51b3\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u8bf7\u8bf4\u660e\u8fd9\u6b21\u884c\u52a8\u5e03\u7f72\uff0c\u518d\u7531\u674e\u5144\u5f1f\u63d0\u51fa\u4fee\u6b63\uff0c\u4f5c\u4e2a\u51b3 \u5b9a\u3002\u201d \u3000\u3000\u201c\u968b\u671d\u540d\u5c06\u76f8\u7ee7\u51cb\u8c22\uff01\u201d\u4e03\u7edd\u9053\u957f\u4f83\u4f83\u800c\u8c08\uff0c\u9053\uff1a\u201c\u7eb5\u6709\u4f59\u8005\uff0c\u4e5f\u88ab\u968b\u7080\u5e1d\u731c\u5fcc\u7f62\u804c\u4e0d \u662f\u9000\u4f11\u7530\u56ed\uff0c\u5c31\u906d\u6b8b\u6740\u706d\u95e8\uff0c\u8d24\u8def\u95ed\u585e\uff0c\u5978\u4f5e\u5f53\u9053\u3002 \u3000\u3000\u968b\u7080\u5e1d\u7eb5\u60c5\u9152\u8272\uff0c\u5962\u4f88\u6d6e\u534e\uff0c\u5df2\u8017\u5c3d\u56fd\u5e93\u516c\u5e11\uff0c\u8fd1\u5e74\u6765\u5929\u6012\u4eba\u6028\uff0c\u65f1\u3001\u6d9d\u6210\u707e\uff0c\u4e71\u6c11\u5578 \u805a\uff0c\u968b\u7080\u5e1d\u4e0d\u5f97\u4e0d\u503e\u4e3e\u56fd\u4e4b\u5175\uff0c\u56db\u91ce\u527f\u629a\uff0c\u4f46\u4e71\u6e90\u5df2\u6210\uff0c\u904d\u5730\u70fd\u70df\uff0c\u957f\u5b89\u57ce\u65e9\u5df2\u7a7a\u865a\uff0c\u6b63\u662f \u5927\u738b\u4e3e\u4e8b\u4e4b\u79cb\u3002 \u3000\u3000\u6211\u9690\u4f0f\u4e8e\u4e2d\u539f\u7684\u7532\u5175\u5341\u4e07\uff0c\u534a\u6570\u805a\u96c6\u957f\u5b89\u56db\u5468\uff0c\u4e00\u65e6\u8d77\u5175\uff0c\u4e0d\u96be\u4e00\u4e3e\u53d6\u4e0b\u957f\u5b89\uff0c\u6d3b\u63b3\u968b \u7080\u5e1d\uff0c\u62e5\u5927\u738b\u767b\u4e0a\u5e1d\u4f4d\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff01\u201d\u674e\u9756\u63a5\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u957f\u5b89\u57ce\u4e2d\u81f3\u5c11\u8fd8\u6709\u4e94\u4e07\u7981\u536b\u519b\uff0c\u53e6\u6709\u5b57\u6587\u6210 \u90fd\u7edf\u5e05\u7684\u6570\u5343\u5bab\u536b\u52c7\u58eb\uff0c\u90a3\u662f\u5929\u4e0b\u6700\u7cbe\u9510\u7684\u6218\u58eb\uff0c\u4ee5\u4e00\u62b5\u5341\uff0c\u5176\u4e2d\u81f3\u5c11\u6709\u8fd1\u767e\u5458\u8c6a\u52c7\u4e4b\u58eb\u5f53 \u5f97\u6b66\u6797\u9ad8\u624b\u4e4b\u79f0\u3002\u8fdb\u5175\u957f\u5b89\u4e8b\uff0c\u5c31\u7b97\u6311\u8d77\u4e86\u53cd\u968b\u65d7\u5e1c\uff0c\u518d\u65e0\u56de\u65cb\u4f59\u5730\u3002\u7687\u4e0a\u53ef\u4ee5\u4e0b\u4ee4\u5f81 \u527f\u2026\u2026\u201d \u3000\u3000\u201c\u7eb5\u6709\u6b64\u5fc3\uff0c\u4e5f\u65e0\u6b64\u529b\u3002\u201d\u4e03\u7edd\u9053\u957f\u9053\uff1a\u201c\u8d2b\u9053\u4f4f\u5728\u957f\u5b89\u57ce\u4e2d\u5341\u5e74\uff0c\u76ee\u7779\u968b\u6587\u5e1d\u6269\u5c55\u7586 \u57df\uff0c\u7edf\u4e00\u5168\u56fd\uff0c\u968b\u7080\u5e1d\u8c0b\u5144\u9738\u5ac2\uff0c\u6bd2\u7236\u767b\u57fa\uff0c\u5efa\u9020\u8ff7\u5bab\uff0c\u5e7f\u5f81\u7f8e\u5973\uff0c\u6215\u5bb3\u8d24\u81e3\u3001\u80fd\u5c06\uff0c\u8d25\u574f \u671d\u653f\uff0c\u7531\u76db\u800c\u8870\u3002\u5982\u4e0d\u65e9\u56fe\uff0c\u8ba9\u522b\u4eba\u6377\u8db3\u5148\u5f97\uff0c\u5c31\u6094\u4e4b\u665a\u77e3\uff01\u201d \u3000\u3000\u201c\u81f3\u4e8e\u4e94\u4e07\u7981\u536b\u519b\u539f\u5c5e\u7cbe\u9510\u4e4b\u5e08\uff0c\u4f46\u7edf\u5e05\u975e\u4eba\uff0c\u79ef\u5f31\u65e5\u751a\uff0c\u65e9\u5df2\u6218\u529b\u6d88\u9000\uff0c\u5f62\u540c\u865a\u8bbe\u3002 \u5b57\u6587\u6210\u90fd\u7edf\u7387\u7684\u5bab\u536b\u7981\u519b\u786e\u5c5e\u7cbe\u9510\uff0c\u53ea\u662f\u4eba\u6570\u592a\u5c11\u4e86\uff0c\u5355\u7528\u4f5c\u4fdd\u536b\u7687\u5bab\u4ea6\u663e\u4e0d\u8db3\uff0c\u54ea\u91cc\u8fd8\u6709 \u53cd\u51fb\u4e4b\u80fd\uff1f \u3000\u3000\u5927\u738b\u7684\u9f99\u3001\u864e\u52c7\u58eb\u5c31\u8db3\u4ee5\u514b\u5236\u4ed6\u4eec\uff0c\u4f55\u51b5\u8fd8\u6709\u8d2b\u9053\u8bad\u7ec3\u5df2\u6210\u7684\u4e09\u767e\u5251\u624b\uff0c\u53cc\u65b9\u4e00\u65e6\u5bf9 \u9635\uff0c\u591a\u5219\u4e09\u65e5\uff0c\u5c11\u5219\u4e00\u5929\uff0c\u53ef\u51fb\u6e83\u5b57\u6587\u6210\u90fd\u7edf\u5e05\u7684\u5bab\u536b\u6218\u58eb\uff0c\u5355\u662f\u957f\u5b89\u57ce\u5185\u3001\u57ce\u5916\uff0c\u5927\u738b\u6563 \u5e03\u7684\u7532\u58eb\uff0c\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7acb\u53ef\u632f\u8d77\u4e09\u4e07\u7cbe\u9510\uff0c\u53ea\u8981\u4e00\u65e5\u9e88\u6218\uff0c\u5373\u53ef\u4f7f\u4e94\u4e07\u7981\u536b\u519b\u5f03\u68b0\u6e83\u9003\u3002\u4f55 \u51b5\u5927\u738b\u53ec\u96c6\u7684\u56db\u65b9\u7532\u5175\u8702\u62e5\u800c\u81f3\uff0c\u8d2b\u9053\u4fdd\u8bc1\uff0c\u957f\u5b89\u57ce\u6307\u65e5\u53ef\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u9053\u957f\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4e8b\u5173\u91cd\u5927\uff0c\u8981\u4e09\u601d\u800c\u884c\u554a\uff01\u6211\u4eec\u6ca1\u6709\u636e\u4ee5\u81ea\u7acb\u7684\u5730\u76d8\uff0c\u5175\u6e90\u7cae\u889c \u7b79\u96c6\u4e0d\u6613\uff0c\u4ea6\u65e0\u6765\u5904\uff0c\u5982\u662f\u4e00\u6218\u4e0d\u80fd\u6210\u529f\uff0c\u5c31\u5f88\u96be\u518d\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5175\u8d35\u795e\u901f\uff0c\u5b57\u6587\u6210\u90fd\u6b63\u5728\u62db\u8058\u80fd\u4eba\uff0c\u8c03\u6574\u4eba\u4e8b\uff0c\u4e00\u65b9\u9762\u6269\u5927\u5bab \u536b\u7684\u7ec4\u7ec7\uff1b\u4e00\u65b9\u9762\u51c6\u5907\u63a5\u624b\u7981\u536b\u519b\uff0c\u6574\u987f\u6210\u7cbe\u9510\u4e4b\u5e08\u3002 \u3000\u3000\u6b64\u523b\u653b\u4e0b\u957f\u5b89\uff0c\u662f\u5343\u8f7d\u96be\u9022\u7684\u826f\u673a\uff0c\u7b49\u5230\u5b87\u6587\u6210\u90fd\u5b8c\u6210\u4e86\u51c6\u5907\uff0c\u9519\u5931\u6218\u673a\uff0c\u62b1\u61be\u4e8b\u5c0f\uff0c \u53ef\u80fd\u8bef\u4e86\u5927\u738b\u7684\u5343\u79cb\u57fa\u4e1a\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u674e\u9756\u8f6c\u671b\u7740\u866c\u9aef\u5ba2\uff0c\u9053\uff1a\u201c\u6b63\u5f0f\u5174\u5175\u9020\u5de8\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u5927\u54e5\u4f55\u4e0d\u4eb2\u81ea\u8fdb\u5165\u957f \u5b89\u57ce\u5185\uff0c\u89c2\u5bdf\u4e00\u4e0b\uff0c\u518d\u4f5c\u51b3\u5b9a\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e48\u529e\u5427\uff01\u201d\u4e03\u7edd\u9053\u957f\u9053\uff1a\u201c\u5927\u738b\u4e00\u9762\u4f20\u4eca\u53d1\u5175\uff0c\u4e00\u9762\u8fdb\u5165\u957f\u5b89\u89c2\u5bdf\uff0c\u5982\u89c9\u4e0d\u59a5\uff0c\u6084 \u7136\u9000\u8d70\uff0c\u5982\u89c9\u673a\u4e0d\u53ef\u5931\uff0c\u7acb\u523b\u4e0b\u4ee4\u653b\u57ce\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u4f5c\u4e86\u51b3\u5b9a\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u5c31\u4f9d\u4e03\u7edd\u9053\u5144\u7684\u8ba1\u5212\u6267\u884c\u3002\u201d \u3000\u3000\u53c8\u671b\u7740\u674e\u9756\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u80fd\u7f6e\u8eab\u4e8b\u5916\u5440\uff01\u201d \u3000\u3000\u674e\u9756\u6b20\u8eab\u5e94\u9053\uff1a\u201c\u5c0f\u5f1f\u606d\u5019\u5927\u54e5\u7684\u5429\u5490\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a\u201c\u4f60\u5c31\u7559\u5b88\u5728\u534e\u9634\u5427\uff01\u7559\u4e00\u4e07\u7cbe\u9510\u90e8\u961f\uff0c\u7531\u4f60\u6307\u6325\uff0c\u7b49\u5019\u6211\u7531\u957f\u5b89\u4f20\u6765\u7684\u6307 \u793a\u3002\u201d \u3000\u3000\u674e\u9756\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\uff01\u5c0f\u5f1f\u7559\u6b64\u5019\u547d\u3002\u201d \u3000\u3000\u201c\u51fa\u5c18\uff0c\u4f60\u5462\uff1f\u201d\u866c\u9aef\u5ba2\u53c8\u9053\uff1a\u201c\u662f\u548c\u4e3a\u5144\u8fdb\u5165\u957f\u5b89\uff0c\u5e2e\u6211\u89c2\u5bdf\u98ce\u8272\uff1f\u8fd8\u662f\u7559\u8fd9\u91cc\u966a\u966a \u674e\u9756\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u867d\u7136\u5df2\u5fc3\u751f\u8b66\u60d5\uff0c\u4f46\u5728\u866c\u9aef\u5ba2\u9762\u524d\u4ecd\u7136\u4fdd\u6709\u5341\u5206\u7684\u6e29\u67d4\uff0c\u9053\uff1a\u201c\u5927\u54e5\u5982\u80af\u5141\u51c6\uff0c \u6211\u8981\u7559\u8fd9\u91cc\u966a\u966a\u674e\u9756\uff0c\u6211\u4eec\u592b\u59bb\u4e00\u5e74\u6ca1\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u5bf9\uff0c\u5c0f\u522b\u80dc\u65b0\u5a5a\uff0c\u4f60\u4e5f\u7559\u4e0b\u6765\u5427\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\uff0c\u6211\u5148\u51fa\u53d1\uff0c\u5927 \u519b\u660e\u65e5\u665a\u4e0a\u767b\u7a0b\u3002\u201d \u3000\u3000\u5728\u4f17\u4eba\u9762\u524d\uff0c\u866c\u9aef\u5ba2\u76f4\u8a00\u4e0d\u8bb3\u3002 \u3000\u3000\u5f20\u59d1\u5a18\u4e5f\u6709\u7740\u7f9e\u610f\u96be\u6291\u4e4b\u611f\uff0c\u53cc\u988a\u7eb7\u7ea2\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u4f46\u866c\u9aef\u5ba2\u5df2\u6325\u624b\u6563\u4f1a\uff0c\u8d77\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u4e00\u5239\u90a3\u95f4\uff0c\u8d70\u5f97\u4e00\u4e2a\u4e0d\u5269\uff0c\u53ea\u7559\u4e0b\u674e\u9756\u592b\u5987\u4e24\u4eba\u3002 \u3000\u3000\u674e\u9756\u51b7\u773c\u65c1\u89c2\uff0c\u53d1\u89c9\u4e86\u866c\u9aef\u5ba2\u6811\u7acb\u5728\u5f20\u51fa\u5c18\u5fc3\u4e2d\u7684\u5f62\u8c61\uff0c\u4ecd\u7136\u6781\u5177\u5a01\u671b\uff0c\u5f20\u51fa\u5c18\u6297\u62d2\u529b \u91cf\u5341\u5206\u8106\u5f31\u3002 \u3000\u3000\u674e\u9756\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u51fa\u5c18\uff0c\u6211\u4eec\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u62ac\u5934\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5947\u602a\u5440\uff01\u65e2\u7136\u62d4\u51fa\u4e86\u4e00\u4e07\u5175\u9a6c\uff0c\u7531\u4f60\u5750\u9547\u534e\u9634\uff0c\u5c31\u8be5\u4ea4 \u7ed9\u4f60\u5175\u7b26\u3001\u547d\u724c\u5440\uff01\u201d \u3000\u3000\u674e\u9756\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u60f3\uff0c\u4ed6\u4eec\u665a\u4e00\u70b9\u4f1a\u5411\u6211\u5448\u4ea4\u5175\u7b26\uff0c\u56de\u623f\u53bb\u5427\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u70b9\u70b9\u5934\uff0c\u7ad9\u8d77\u8eab\u5b50\u4f9d\u504e\u5728\u674e\u9756\u8eab\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u5979\u7a81\u7136\u5174\u8d77\u4e86\u4e00\u79cd\u60f6\u6127\u548c\u4e0d\u5b89\u4e4b\u611f\uff0c\u6df1\u6df1\u611f\u89c9\u5230\u6127\u5bf9\u592b\u541b\u3002 \u3000\u3000\u201c\u5927\u54e5\u7684\u5a01\u4e25\uff0c\u6211\u4f3c\u662f\u6709\u4e9b\u754f\u60e7\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u4e00\u76f4\u6ca1\u6cd5\u575a\u51b3\u6297\u62d2\u4ed6\u547d\u4ee4\u7684\u80fd \u529b\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u534a\u5e08\u534a\u53cb\u7684\u5927\u54e5\uff0c\u65e9\u5df2\u5728\u4f60\u6211\u5fc3\u4e2d\u94fa\u8bbe\u4e0b\u4e00\u79cd\u6743\u5a01\uff0c\u6211\u660e\u77e5\u957f\u5b89\u57ce\u4e2d\u5145\u6ee1\u7740 \u6740\u673a\u3001\u51f6\u9669\uff0c\u7edd\u4e0d\u662f\u51fa\u5175\u65f6\u673a\u3002\u6211\u4e5f\u6ca1\u6709\u5168\u529b\u6297\u4e89\uff0c\u5927\u54e5\u4f5c\u4e86\u88c1\u51b3\uff0c\u53ea\u597d\u9075\u7167\u884c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5fc3\u6709\u987e\u53ca\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u611f\u53d7\u5f97\u5230\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u9756\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u518d\u4e09\u5f3a\u8c03\uff0c\u4e5f\u6697\u793a\u6211\u522b\u6709\u7528\u5fc3\uff0c\u6211\u82e5\u575a\u6301\u4e0b\u53bb\uff0c\u6050\u5c06\u95f9 \u51fa\u4e2a\u4e0d\u6d4b\u4e4b\u5c40\u3002\u51fa\u5c18\uff0c\u6211\u4eec\u592b\u5987\u4f3c\u662f\u5df2\u88ab\u6392\u6324\u51fa\u6838\u5fc3\u4e4b\u5916\u3002 \u3000\u3000\u5509\uff01\u6211\u662f\u548e\u7531\u81ea\u53d6\uff0c\u5e38\u4f4f\u5927\u539f\uff0c\u548c\u674e\u4e16\u6c11\u8d70\u7684\u592a\u8fd1\u4e86\uff0c\u7591\u6211\u3001\u6392\u6211\uff0c\u4e8b\u5c5e\u5e94\u8be5\uff0c\u53ea\u662f\u628a \u4f60\u4e5f\u62d6\u5165\u6f29\u6da1\u4e2d\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u592b\u5987\u672c\u5c5e\u4e00\u4f53\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u6392\u6324\u6211\u4eec\uff0c\u4e5f\u8bb8\u522b\u6709\u539f\u56e0\uff0c\u674e\u9756\uff0c\u6211\u4e0d \u80fd\u575a\u6bc5\u6297\u62d2\u5927\u54e5\u7684\u547d\u4ee4\uff0c\u662f\u56e0\u4e3a\u4ed6\u4e00\u76f4\u89c6\u6211\u5982\u59b9\uff0c\u4e0d\u7ba1\u4ed6\u5fc3\u4e2d\u5982\u4f55\u60f3\uff0c\u6211\u5fc3\u4e2d\u6709\u4e9b\u4ec0\u4e48\u8b66 \u60d5\uff0c\u90fd\u662f\u51fa\u81ea\u6211\u5fc3\u4e2d\u7684\u731c\u60f3\uff0c\u6ca1\u6709\u8bc1\u660e\u548c\u6839\u636e\u3002\u201d \u3000\u3000\u201c\u6211\u660e\u767d\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u5927\u54e5\u8981\u8d8a\u8fc7\u9053\u4e49\u5173\u53e3\uff0c\u5bf9\u4ed6\u800c\u8a00\u662f\u4e00\u4ef6\u5341\u5206\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u56de\u5230\u623f\u4e2d\uff0c\u71c3\u8d77\u4e86\u706f\u706b\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u62c9\u4e0a\u4e86\u7a97\u5e18\uff0c\u5173\u4e0a\u4e86\u623f\u95e8\uff0c\u7adf\u7136\u4e3b\u52a8\u5730\u5e2e\u674e\u9756\u5bbd\u8863\u89e3\u5e26\u3002 \u3000\u3000\u5979\u4e00\u9762\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u4e00\u5e74\u4e86\uff0c\u592b\u5987\u672a\u540c\u5e8a\uff0c\u76f8\u601d\u82e6\u96be\u5f53\uff0c\u96be\u9053\u4f60\u5c31\u6ca1\u6709\u60f3\u8fc7\u6211\u5417\uff1f\u201d \u3000\u3000\u674e\u9756\u6ca1\u6709\u62d2\u7edd\uff0c\u4efb\u5979\u8131\u53bb\u8863\u670d\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4f3c\u662f\u8981\u5c3d\u91cf\u5c55\u73b0\u51fa\u4e00\u4e2a\u59bb\u5b50\u7684\u6e29\u67d4\uff0c\u8dea\u5728\u69bb\u524d\uff0c\u5e2e\u674e\u9756\u8131\u53bb\u76ae\u9774\u3002 \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u6ca1\u6709\u4e00\u756a\u5bd2\u5f7b\u9aa8\uff0c\u90a3\u5f97\u6885\u82b1\u6251\u9f3b\u9999\uff1f\u6211\u5982\u4e0d\u5fcd\u53d7\u8fd9\u4e00\u5e74\u7684\u76f8\u601d\uff0c\u518d\u7b49\u4e2a\u5341\u5e74 \u516b\u5e74\u4e5f\u96be\u5f97\u5a07\u59bb\u8fd9\u756a\u6069\u9047\u3002\u201d \u3000\u3000\u201c\u8bf4\u5f97\u597d\u53ef\u601c\u54ea\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u8fc7\u53bb\u5e74\u7eaa\u5c0f\uff0c\u4e0d\u89e3\u592b\u59bb\u60c5\uff0c\u95fa\u623f\u4e2d\u5c11\u6709\u4f8d\u5019\uff0c\u592b\u541b\u83ab \u89c1\u602a\u3002\u201d \u3000\u3000\u674e\u9756\u7b11\u4e86\uff0c\u662f\u4e00\u79cd\u6ee1\u8db3\u3001\u5f00\u5fc3\u7684\u7b11\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e00\u628a\u62b1\u4f4f\u674e\u9756\uff0c\u5012\u5165\u88ab\u7a9d\u4e2d\uff0c\u8fc5\u5feb\u7684\u8131\u53bb\u4e86\u81ea\u5df1\u7684\u8863\u670d\uff0c\u504e\u5165\u4e08\u592b\u6000\u4e2d\u3002 \u3000\u3000\u5979\u8f7b\u58f0\u7684\u9053\uff1a\u201c\u5927\u54e5\u7684\u611f\u60c5\u4f3c\u6b63\u5728\u5f00\u59cb\u8f6c\u53d8\uff0c\u6211\u5df2\u4ece\u4ed6\u70bd\u70ed\u7684\u76ee\u5149\u4e2d\u611f\u53d7\u5230\u5f3a\u5927\u538b\u529b\u3002 \u4ed6\u529b\u5927\u65e0\u7a77\uff0c\u4e00\u65e6\u52a8\u5f3a\uff0c\u6211\u7edd\u5bf9\u65e0\u6cd5\u6297\u62d2\u3002 \u3000\u3000\u4e8b\u540e\u62d4\u5251\u76f8\u5411\uff0c\u8840\u6d41\u4e94\u6b65\uff0c\u53c8\u80fd\u5f97\u56de\u5230\u4ec0\u4e48\uff1f\u674e\u9756\uff0c\u5e26\u6211\u8d70\u5427\uff01\u6b64\u5730\u5df2\u975e\u5584\u5730\uff0c\u4e0d\u80fd\u518d \u7559\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u51b3\u5b9a\u8981\u8d70\u5417\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u51fa\u5c18\uff0c\u6211\u4eec\u4e24\u4e2a\u4e00\u8d77\u8d70\uff0c\u5c31\u6c38\u65e0\u56de\u5934\u4e4b\u65e5\u4e86\uff0c\u4e5f\u8bb8\u5144 \u5f1f\u518d\u76f8\u9022\u53ef\u80fd\u5728\u4e24\u519b\u5bf9\u9635\u4e4b\u4e0a\uff0c\u4f60\u8981\u4ed4\u7ec6\u7684\u60f3\u4e00\u60f3\u554a\uff01\u201d \u3000\u3000\u201c\u4e0d\u76f8\u89c1\uff0c\u4e5f\u5c31\u7b97\u4e86\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5175\u6208\u76f8\u89c1\u5462\uff1f\u201d \u3000\u3000\u201c\u53cc\u96c4\u4e0d\u5e76\u7acb\u5440\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u674e\u4e16\u6c11\u548c\u4ef2\u575a\u5927\u54e5\u5fc5\u6709\u4e00\u756a\u640f\u6740\u3001\u4e89\u6267\uff0c\u6211\u4eec\u6295\u6548\u4e86\u674e \u4e16\u6c11\uff0c\u5c31\u65e0\u6cd5\u907f\u53bb\u5144\u5f1f\u53cd\u76ee\u8fd9\u4e00\u5173\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5750\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u674e\u4e8c\u516c\u5b50\u8fd8\u5728\u534e\u5c71\u884c\u5bab\uff0c\u4e03\u7edd\u9053\u957f\u60f3\u5bb3\u4ed6\uff0c\u5feb\u8d70\uff01\u6211\u4eec\u5230\u884c\u5bab \u4f1a\uff0c\u770b\u770b\u4ed6\u662f\u5426\u65e0\u6059\u3002\u201d \u3000\u3000\u674e\u9756\u62c9\u5012\u5f20\u51fa\u5c18\uff0c\u62e5\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u73b0\u5728\u53bb\uff0c\u53ea\u6015\u6765\u4e0d\u53ca\u4e86\uff0c\u4e03\u7edd\u9053\u957f\u8981\u5bb3\u4ed6\uff0c\u6050\u6015\u65e9 \u5df2\u4e0b\u4e86\u6bd2\u624b\u3002\u201d \u3000\u3000\u201c\u53bb\u4e86\u603b\u6bd4\u4e0d\u53bb\u597d\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u674e\u9756\uff0c\u4e8c\u516c\u5b50\u5bf9\u4f60\u597d\u554a\uff01 \u3000\u3000\u6211\u6709\u8fd9\u756a\u8f6c\u53d8\uff0c\u4e5f\u5f97\u4ed6\u6307\u70b9\u4e0d\u5c11\uff0c\u4f60\u2026\u2026\u4f60\u600e\u80fd\u5750\u89c6\u4ed6\u6709\u96be\u4e0d\u6559\u3002\u201d \u3000\u3000\u674e\u9756\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u53ea\u6709\u548c\u4f60\u4e00\u6b21\u4ea4\u8c08\uff0c\u5c31\u8ba9\u4f60\u5982\u6b64\u656c\u670d\u2026\u2026\u201d\u3002\u5ddd \u3000\u3000\u201c\u4f60\u626f\u4ec0\u4e48\u5440\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u674e\u4e16\u6c11\u662f\u4f60\u6700\u5fc3\u4eea\u7684\u670b\u53cb\uff0c\u96be\u9053\u4f60\u5c31\u8fd9\u4e48\u5c0f\u5fc3\u773c\uff1f\u201d \u3000\u3000\u201c\u8d24\u59bb\u8bef\u4f1a\u4e86\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u5f20\u4ef2\u575a\u5927\u54e5\u548c\u4f60\u65e5\u5915\u76f8\u5904\u6709\u4e00\u5e74\u4e4b\u4e45\uff0c\u6211\u90fd\u80fd\u5fcd\u4e0b\u4e86\uff0c\u4f55 \u51b5\u662f\u674e\u4e16\u6c11\uff0c\u4ed6\u53ea\u548c\u4f60\u6709\u4e00\u6b21\u6df1\u8c08\uff0c\u4f60\u80fd\u5982\u6b64\u5173\u6ce8\u5230\u4ed6\u7684\u5b89\u5371\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u63a5\u9053\uff1a\u201c\u4ed6\u662f\u771f\u6b63\u541b\u5b50\u554a\uff01\u503c\u5f97\u4eba\u5c0a\u91cd\uff0c\u628a\u4e8b\u7406\u5206\u6790\u5f97\u8ba9\u4eba\u503e\u670d\u3002\u201d \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u591a\u51e0\u65e5\u76f8\u5904\uff0c\u4f60\u4f1a\u53d1\u89c9\u4ed6\u662f\u4e00\u4f4d\u6551\u4e16\u5929\u795e\uff0c\u53ea\u6709\u674e\u4e16\u6c11 \u5f97\u5230\u5929\u4e0b\uff0c\u4eba\u95f4\u624d\u80fd\u5e78\u798f\u53ef\u8a00\uff0c\u5929\u4e0b\u5927\u6cbb\uff0c\u4eba\u6c11\u5b89\u4e50\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u91cd\u8981\u7684\u4eba\uff0c\u8fd8\u4e0d\u5feb\u53bb\u6551\u4ed6\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4f38\u624b\u53bb\u6293\u8863\u670d\u3002 \u3000\u3000\u4f46\u674e\u9756\u5374\u7d27\u7d27\u7684\u62b1\u4f4f\u5f20\u51fa\u5c18\u4e0d\u653e\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u771f\u7684\u6025\u4e86\uff0c\u6293\u4f4f\u674e\u9756\u7684\u53cc\u624b\uff0c\u9053\uff1a\u201c\u6025\u4e5f\u4e0d\u5728\u4e00\u65f6\u554a\uff01 \u3000\u3000\u4ee5\u540e\u592b\u8d70\u5343\u91cc\u8def\uff0c\u59bb\u5728\u9a6c\u540e\u968f\uff0c\u591c\u591c\u4efb\u541b\u62b1\u5165\u6000\uff1b\u4f60\u600e\u5fcd\u5fc3\u4e0d\u987e\u597d\u670b\u53cb\u7684\u751f\u6b7b\u554a\uff01\u201d \u3000\u3000\u201c\u65e9\u5df2\u6709\u4eba\u53bb\u4e86\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u82e5\u975e\u5982\u6b64\uff0c\u6211\u600e\u80fd\u5982\u6b64\u6c89\u5f97\u4f4f\u6c14\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e26\u4e86\u592a\u539f\u7684\u4eba\u6765\u4e86\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u90a3\u4e5f\u9760\u4e0d\u4f4f\u554a\uff01\u4e03\u7edd\u9053\u957f\u5c82\u662f\u597d\u4e0e\u4eba\u7269\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4ed6\u6765\u81ea\u592a\u539f\uff0c\u4f46\u975e\u6211\u5e26\u6765\u3002\u6211\u4eec\u662f\u4e0d\u671f\u800c\u9047\uff0c\u8bba\u672f\u6cd5\u4e4b\u9ad8\uff0c\u4e3a\u592b\u662f\u671b\u5c18\u83ab \u53ca\uff0c\u4e03\u7edd\u9053\u957f\u7edd\u5bf9\u6597\u4e0d\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u554a\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u6211\u8fd8\u6ca1\u6709\u6536\u5230\u4ed6\u544a\u6025\u7684\u4fe1\u53f7\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5a07\u54fc\u9053\uff1a\u201c\u597d\u554a\uff01\u4f60\u4eec\u65e9\u6709\u8ba1\u7b97\u4e86\uff0c\u5374\u6765\u51a4\u6211\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4e0d\u6293\u8863\u670d\u4e86\uff0c\u5374\u4e00\u8f6c\u8eab\uff0c\u628a\u540e\u80cc\u7ed9\u4e86\u674e\u9756\u3002 \u3000\u3000\u674e\u9756\u6273\u8fc7\u4e86\u5f20\u51fa\u5c18\u7684\u5a07\u8eaf\uff0c\u9053\uff1a\u201c\u662f\u4f60\u6253\u5c94\u3001\u63a5\u53e3\uff0c\u4e0d\u8ba9\u6211\u8bf4\u6e05\u695a\u554a\uff01\u660e\u5929\u6211\u4eec\u4e00\u8d77\u53bb \u770b\u4ed6\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u7b49\u4f60\u63a5\u4e86\u5175\u7b26\u3001\u4eca\u724c\uff0c\u5c31\u53ef\u4ee5\u653e\u674e\u4e16\u6c11\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u51fa\u5c18\uff0c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u771f\u7684\u4f1a\u628a\u5175\u7b26\u3001\u4ee4\u724c\u4ea4\u7ed9\u6211\u5417\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u5c31\u7b97\u4ef2\u575a\u5927\u53ef\u771f\u6709\u6b64 \u610f\uff0c\u4e03\u7edd\u9053\u957f\u4e5f\u4f1a\u522b\u4f5c\u5b89\u6392\uff0c\u7559\u4e0b\u4e00\u65b9\u5927\u519b\uff0c\u53ef\u80fd\u5c31\u662f\u8981\u76d1\u89c6\u674e\u4e16\u6c11\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u4f1a\u5427\uff01\u4ed6\u4ee4\u51fa\u5fc5\u884c\uff0c\u65e0\u4eba\u6562\u4e8e\u6298\u6263\uff0c\u4ed6\u65e2\u7136\u5429\u5490\u4e86\uff0c\u4e03\u7edd\u9053\u957f\u4e5f\u4e0d \u6562\u64c5\u4f5c\u4e3b\u610f\u554a\uff01\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\uff1f\u660e\u5929\u5373\u53ef\u77e5\u6653\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u6211\u5012\u5e0c\u671b\u4e03\u7edd\u9053\u957f\u6697\u4e2d\u641e\u9b3c\uff0c\u4e0d\u4ea4\u5175\u7b26\u3001\u4ee4 \u724c\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6ca1\u6709\u5175\u7b26\u3001\u4ee4\u724c\uff0c\u60f3\u6551\u674e\u4e16\u6c11\u5c31\u5f97\u5927\u8d39\u4e00\u756a\u624b\u811a\uff0c\u660e\u5929\u6211\u8981\u67e5\u95ee\u4e00\u4e0b\uff0c\u8c01 \u6562\u8fdd\u6297\u4ed6\u7684\u4ee4\u8c15\u3002\u201d \u3000\u3000\u674e\u9756\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u63a5\u4e86\u5175\u7b26\u3001\u4ee4\u724c\uff0c\u9ebb\u70e6\u5c31\u66f4\u5927\u4e86\uff0c\u8fd8\u662f\u4e0d\u63a5\u7684\u597d\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4f60\u5df2\u51c6\u5907\u548c\u4ed6\u4eec\u7ffb\u8138\u52a8\u624b\u4e86\uff0c\u6211\u4eec\u53ef\u4ee5\u8d70\uff01\u53ef\u4ee5\u6295\u6548\u674e\u4e16 \u6c11\uff0c\u4f46\u8d81\u5927\u54e5\u79bb\u53bb\u7684\u673a\u4f1a\uff0c\u52a8\u624b\u6559\u4eba\u6740\u5f97\u8840\u6d41\u4e94\u6b65\uff0c\u5c31\u6709\u5931\u539a\u9053\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\u957f\u5b89\u4e4b\u884c\uff0c\u80af\u5b9a\u78b0\u5728\u94c1\u677f\u4e0a\uff0c\u6211\u5982\u63a5\u4e86\u5175\u7b26\u3001\u4ee4\u724c\uff0c\u5927\u54e5\u4e0b \u4ee4\u76f8\u53ec\uff0c\u6211\u80fd\u62d2\u4e0d\u8d74\u63f4\u5417\uff1f\u6211\u53bb\u4e86\u4e5f\u672a\u5fc5\u80fd\u6709\u80dc\u673a\u3002\u201d \u3000\u3000\u201c\u957f\u5b89\u57ce\u4e2d\u771f\u6709\u8fd9\u4e48\u4e2a\u5389\u5bb3\u4eba\u7269\u5417\uff1f\u4e03\u7edd\u9053\u957f\u52a0\u4e0a\u5927\u54e5\uff0c\u4ecd\u975e\u4ed6\u7684\u654c\u624b\uff1f\u201d \u3000\u3000\u674e\u9756\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff01\u662f\u6709\u4e00\u4e2a\u975e\u5e38\u96be\u60f9\u7684\u9ad8\u4eba\uff0c\u5927\u54e5\u4e5f\u8bb8\u5175\u5f3a\uff0c\u4f46\u662f\u5bf9\u65b9\u5c06\u80fd\uff0c \u80dc\u7b97\u673a\u4f1a\u5c31\u4e0d\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7591\u7136\u9053\uff1a\u201c\u957f\u5b89\u57ce\u4e2d\u6709\u6b64\u80fd\u4eba\uff1f\u4f60\u8ba4\u8bc6\u5417\uff1f\u4ed6\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8881\u7d2b\u70df\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4e00\u4e2a\u672f\u6cd5\u80dc\u6211\u5341\u500d\u7684\u9ad8\u4eba\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\u542c\u8fc7\uff0c\u5979\u662f\u968b\u671d\u7684\u56fd\u5e08\u3002\u4e5f\u662f\u968b\u7080\u5e1d\u7684\u7231\u5983\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6551\u52a9\u674e\u4e16\u6c11\u7684\uff0c\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u201c\u8881\u5929\u7f61\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4e5f\u662f\u4e00\u4f4d\u80dc\u8fc7\u6211\u674e\u9756\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u674e\u4e16\u6c11\u624b\u4e0b\u6709\u5982\u6b64\u7684\u5389\u5bb3\u4eba\u7269\uff0c\u4e3a\u4ec0\u4e48\u4e00\u76f4\u6309\u5175\u4e0d\u52a8\u5462\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e5f\u5bb3\u6015\u8881\u7d2b\u70df\uff1f\u201d \u3000\u3000\u201c\u54ea\u53ea\u662f\u539f\u56e0\u4e4b\u4e00\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u65f6\u673a\u672a\u81f3\uff0c\u987a\u5e94\u5929\u65f6\uff0c\u624d\u80fd\u53d6\u5f97\u5929\u4e0b\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u4e48\u8bf4\uff0c\u5927\u54e5\u6b64\u6b21\u5165\u957f\u5b89\uff0c\u5fc5\u8d25\u65e0\u7591\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff01\u5bf9\u5927\u54e5\u800c\u8a00\uff0c\u53ef\u80fd\u6709\u76ca\u65e0\u5bb3\uff0c\u4ed6\u521a\u610e\u81ea\u7528\uff0c\u521a\u5219\u6613\u6298\uff0c\u8ba9\u4ed6 \u53d7\u4e9b\u632b\u6298\uff0c\u624d\u4f1a\u77e5\u9053\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e9b\u53d8\u5316\uff0c\u6211\u4eec\u518d\u4ece\u4e2d\u65a1\u65cb\uff0c\u5927\u54e5\u80fd\u653e\u5f03\u9010\u9e7f\u4e2d\u539f\u7684\u5fd7\u5411\uff0c\u4e5f\u8bb8\u4f1a\u7559\u4e00\u4e2a\u5168\u8eab\u800c\u9000 \u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u54ea\u91cc\u80fd\u591f\u5bb9\u5f97\u4e0b\u4ed6\u5462\uff1f\u674e\u4e16\u6c11\u4e0d\u4f1a\u8ba9\u4ed6\u5272\u636e\u4e00\u56f4\uff0c\u5750\u5c45\u4e00\u628a\u4ea4\u6905\uff0c\u4ed6\u53c8\u4e0d \u7518\u5c48\u5c45\u4eba\u4e0b\uff0c\u4e5f\u662f\u6b7b\u8def\u4e00\u6761\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4e2d\u539f\u4e4b\u5916\u7586\u57df\u5e7f\u5927\uff0c\u6211\u4eec\u53ef\u4ee5\u5e2e\u52a9\u4ed6\u5f00\u62d3\u4e00\u7247\u65b0\u5929\u5730\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4e00\u4e0b\u5b50\u6eda\u8fdb\u674e\u9756\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u8001\u516c\u603b\u6bd4\u8001\u5a46\u9ad8\u2026\u2026\u201d \u3000\u3000\u674e\u9756\u6367\u4f4f\u4e86\u5f20\u59d1\u5a18\u7684\u53cc\u988a\uff0c\u63a5\u9053\uff1a\u201c\u51fa\u5c18\uff0c\u4e00\u5e74\u5c0f\u522b\uff0c\u4f60\u51fa\u843d\u5f97\u66f4\u7f8e\u4e3d\u4e86\u3002\u8fd9\u4e00\u6bb5\u76f8\u601d \u4e4b\u82e6\uff0c\u8fd8\u771f\u7684\u523b\u9aa8\u94ed\u5fc3\uff0c\u4f7f\u4eba\u6709\u7740\u6e21\u65e5\u5982\u5e74\u4e4b\u611f\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u518d\u4e5f\u4e0d\u4f1a\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u4e86\uff0c\u6df1\u591c\u5fc6\u592b\u541b\uff0c\u6cea\u6e7f\u7ee3\u82b1\u6795\uff1b\u90a3\u51c4\u51b7\u7684\u957f\u591c\uff0c\u9b42 \u8426\u68a6\u7ed5\u7684\u75db\u82e6\uff0c\u5e38\u4f7f\u6211\u901a\u5bb5\u96be\u7720\u3002 \u3000\u3000\u6709\u51e0\u6b21\u96be\u8010\u76f8\u601d\u7684\u5bc2\u5bde\uff0c\u6211\u771f\u60f3\u653e\u5f03\u7ec3\u5251\u7684\u5fc3\u613f\uff0c\u5954\u5411\u592a\u539f\uff0c\u6295\u5165\u4f60\u6000\u62b1\u4e4b\u4e2d\uff0c\u4f46\u4ef2\u575a \u5927\u54e5\u5374\u4e00\u518d\u593a\u5956\u6211\u8fdb\u6b65\u795e\u901f\uff0c\u4e13\u5fc3\u4e00\u5fd7\uff0c\u4e00\u5e74\u5185\u53ef\u80fd\u5927\u6210\u3002\u201d \u3000\u3000\u201c\u8fd9\u5b9e\u5728\u662f\u4e00\u4ef6\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u4f46\u4f60\u7adf\u7136\u4f5c\u5230\u4e86\uff0c\u5251\u672f\u7684\u62db\u672f\uff0c\u9a6d\u5251\u7684\u6280 \u5de7\uff0c\u53ef\u4ee5\u7528\u806a\u660e\u3001\u667a\u6167\u514b\u670d\uff0c\u4f46\u5b9e\u529b\u548c\u9a6d\u5251\u7684\u771f\u5143\u4e4b\u6c14\u5374\u65e0\u6cd5\u901f\u6210\uff0c\u4f60\u7adf\u7136\u517c\u5f97\u4e86\u9c7c\u4e0e\u718a \u638c\u3002\u201d \u3000\u3000\u201c\u731c\u731c\u770b\u90a3\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7d27\u7d27\u62b1\u4f4f\u4e86\u674e\u9756\uff0c\u6574\u4e2a\u5a07\u8eaf\u8d34\u7d27\u5728\u674e\u9756\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u4e00\u5b9a\u662f\u4ef2\u575a\u5927\u54e5\u5e2e\u52a9\u4e86\u4f60\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u4ed6\u8981\u4f60\u901f\u6210\u75be\u8fdb\uff0c\u624d\u4f1a\u575a\u5b9a\u4f60\u5b66\u5251\u7684\u5174\u8da3 \u5427\uff01\u201d \u3000\u3000\u201c\u8981\u731c\u51fa\u4ed6\u600e\u4e48\u5e2e\u52a9\u6211\uff1f\u624d\u80fd\u7b97\u6570\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u81f3\u4e8e\u4ed6\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u674e\u9756\u4e5f\u4e0d\u8ffd\u95ee\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ef2\u575a\u5927\u54e5\u4e00\u5b9a\u662f\u8ba9\u4f60\u670d\u7528\u4e86\u4ed6\u6536\u85cf\u73cd\u5947\u795e\u54c1\uff0c\u589e\u957f\u4e86 \u4f60\u7684\u529f\u529b\u3002\u201d \u3000\u3000\u201c\u8ba8\u538c\u54ea\uff01\u600e\u4e48\u4ec0\u4e48\u4e8b\u90fd\u4f1a\u88ab\u4f60\u731c\u4e2d\uff0c\u4ed6\u8ba9\u6211\u670d\u7528\u4e86\u4e00\u652f\u5343\u5e74\u4eba\u53c2\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u5b8c\uff01\u6574\u4e2a\u4eba\u504e\u5165\u4e86\u674e\u9756\u6000\u4e2d\u3002 \u3000\u3000\u5c0f\u522b\u4e09\u6708\u80dc\u65b0\u5a5a\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5206\u522b\u4e86\u4e00\u5e74\u4e4b\u4e45\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u98ce\u60c5\u65e0\u9650\uff0c\u5f20\u51fa\u5c18\u5a49\u8f6c\u5a07\u541f\uff0c\u8c31\u51fa\u4e86\u9e3e\u51e4\u548c\u9e23\u3002 \u3000\u3000\u6625\u7720\u4e0d\u89c9\u6653\uff0c\u9192\u6765\u65f6\u5df2\u7136\u65e5\u4e0a\u4e09\u7aff\u3002 \u3000\u3000\u7406\u4e00\u7406\u6563\u94fa\u5728\u6795\u7554\u7684\u79c0\u53d1\uff0c\u5f20\u51fa\u5c18\u633a\u8eab\u5750\u8d77\uff0c\u56de\u9996\u770b\u674e\u9756\u4e5f\u7741\u5f00\u4e86\u773c\u775b\u3002 \u3000\u3000\u5979\u5a07\u58f0\u55d4\u9053\uff1a\u201c\u4e00\u5411\u7686\u662f\u4e94\u66f4\u5373\u8d77\uff0c\u7ec3\u4e00\u9635\u5251\u6cd5\uff0c\u518d\u68b3\u5986\u6f31\u6d17\uff0c\u6628\u591c\u88ab\u4f60\u2026\u2026\u65e5\u4e0a\u4e09\u7aff \u4e86\u624d\u9192\u6765\u3002\u201d \u3000\u3000\u674e\u9756\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u6837\u554a\uff01\u6625\u7720\u4e0d\u89c9\u6653\uff0c\u53ef\u60dc\u8eab\u5728\u662f\u975e\u4e2d\uff0c\u5c11\u4e86\u90a3\u4efd\u5904\u5904\u95fb\u557c\u9e1f\u7684\u8bd7 \u60c5\u753b\u610f\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4e00\u8df3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u7b49\u7740\uff0c\u6211\u53bb\u66ff\u4f60\u51c6\u5907\u68b3\u6d17\u9762\u6c34\u3002\u201d \u3000\u3000\u679c\u7136\u672a\u5524\u4f7f\u5973\uff0c\u4eb2\u81ea\u66ff\u674e\u9756\u5907\u597d\u9762\u6c34\u3002 \u3000\u3000\u4e2b\u5934\u9001\u4e0a\u65e9\u70b9\uff0c\u9053\uff1a\u201c\u4f7f\u5973\u95fb\u9999\uff0c\u4e00\u65e9\u5c31\u6765\u6c42\u89c1\uff0c\u5df2\u88ab\u5c0f\u5a62\u6321\u5728\u95e8\u5916\u4e00\u9635\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u5feb\u4e9b\u8bf7\u5979\u8fdb\u6765\uff01\u201d \u3000\u3000\u201c\u95fb\u9999\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e0d\u662f\u5927\u54e5\u7684\u4f3a\u9152\u4e2b\u5934\u5417\uff1f\u600e\u4e48\u4f1a\u4e00\u65e9\u5c31\u6765\u89c1\u4f60\uff1f\u201d \u3000\u3000\u201c\u5343\u4e07\u4e0d\u8981\u591a\u7591\u3002\u201d\u674e\u9756\u7b11\u9053\uff1a\u201c\u89c1\u4e86\u5979\u4e4b\u540e\uff0c\u4e00\u5207\u5c31\u660e\u767d\u4e86\uff0c\u4e5f\u8bb8\u6709\u519b\u60c5\u62a5\u544a\u3002\u201d \u3000\u3000\u201c\u519b\u60c5\uff1f\u201d\u5f20\u51fa\u5c18\u5947\u9053\uff1a\u201c\u5979\u662f\u5927\u54e5\u7684\u5fc3\u8179\uff0e\u6709\u519b\u60c5\u4e5f\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u95fb\u9999\u4e5f\u5feb\u6b65\u800c\u5165\uff0c\u6253\u65ad\u4e86\u5f20\u51fa\u5c18\u672a\u5b8c\u4e4b\u8a00\u3002 \u3000\u3000\u674e\u9756\u6325\u6325\u624b\uff0c\u8ddf\u7740\u95fb\u9999\u8fdb\u6765\u7684\u4e2b\u5934\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u95fb\u9999\u5f88\u6c89\u7740\uff0c\u56de\u987e\u4e2b\u5934\u53bb\u8fdc\uff0c\u65b9\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u53e9\u89c1\u5e08\u7236\u3002\u201d \u3000\u3000\u201c\u8d77\u6765\uff0c\u4e0d\u7528\u62d8\u793c\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4f60\u804c\u4f3a\u7406\u9152\uff0c\u5e94\u968f\u501a\u5927\u738b\u8eab\u4fa7\uff0c\u600e\u4f1a\u7559\u5728\u534e\u9634\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u5927\u738b\u4e0b\u4ee4\uff0c\u5165\u957f\u5b89\u4e0d\u5f97\u996e\u9152\uff0c\u4e0d\u8ba9\u5c0f\u5a62\u968f\u884c\uff0c\u4ee5\u793a\u51b3\u5fc3\u3002\u5f1f\u5b50\u6b64\u756a\u5192\u9669\u6c42 \u89c1\uff0c\u6709\u4e24\u4ef6\u91cd\u8981\u4e8b\u5949\u544a\u5e08\u7236\u3002\u201d \u3000\u3000\u674e\u9756\u795e\u60c5\u51dd\u91cd\uff0c\u9053\uff1a\u201c\u5feb\u4e9b\u8bf4\uff0c\u8bf4\u5b8c\u5c31\u8d70\u3002\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u5728\u5982\u7389\u7684\u7167\u987e\u4e2d\uff0c\u5927\u738b\u4e0d\u4e0b\u65a9\u6740\u547d\u3002\u53ef\u4fdd\u5e73\u5b89\u3002\u53ef\u6015\u7684\u662f\u4e03\u7edd\u9053\u957f\u6570 \u5ea6\u8fdb\u8a00\uff0c\u8981\u5904\u6b7b\u674e\u4e16\u6c11\u3002\u5927\u738b\u788d\u7740\u5f20\u59d1\u5a18\u7684\u9762\u5b50\uff0c\u672a\u7f6e\u53ef\u5426\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u5df2\u662f\u674e\u9756\u7684\u59bb\u5b50\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u53eb\u6211\u5f20\u59d1\u5a18\u5462\uff1f \u3000\u3000\u4e0d\u79f0\u6211\u674e\u592b\u4eba\uff01\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u674e\u592b\u4eba\uff01\u5927\u8425\u4e2d\u90fd\u8fd9\u4e48\u79f0\u547c\u4f60\uff0c\u5728\u5927\u738b\u9762\u524d\u66f4\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\uff0e\u8c01\u6562 \u53eb\u674e\u592b\u4eba\u5462\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fae\u5fae\u4e00\u5446\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u672a\u7f6e\u53ef\u5426\uff01\u610f\u601d\u662f\u4e0d\u8d5e\u6210\uff0c\u4e5f\u4e0d\u53cd\u5bf9\u4e86\uff01\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u662f\u7684\uff01\u5927\u738b\u6ca1\u6709\u575a\u51b3\u53cd\u5bf9\u7684\u610f\u601d\uff0c\u4f46\u5927\u738b\u672a\u540c\u610f\uff0c\u4e03\u7edd\u9053\u957f\u5c31\u4e0d\u6562\u660e\u706b\u6267\u4ed7 \u7684\u53bb\u6740\u4e8c\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u6697\u7b97\u5c31\u66f4\u6076\u6bd2\u4e86\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u9669\u8ba1\u5343\u767e\uff0c\u9632\u4e0d\u80dc\u9632\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fd9\u9053\uff1a\u201c\u8d70\uff01\u6211\u4eec\u53bb\u770b\u4e8c\u516c\u5b50\uff01\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u5403\u8fc7\u65e9\u9910\u518d\u53bb\uff0c\u4f55\u51b5\u95fb\u9999\u8fd8\u6709\u91cd\u8981\u7684\u4e8b\u8bf4\u3002\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u662f\u7684\uff0c\u5e08\u7236\uff01\u770b\u8fc7\u674e\u4e16\u6c11\u540e\uff0c\u5e08\u7236\u3001\u5e08\u5a18\u5c31\u8ddf\u7740\u674e\u4e8c\u516c\u5b50\u4e00\u8d77\u8d70\u5427\uff01\u8fd9\u5730\u65b9 \u5df2\u4e0d\u9002\u5408\u4e24\u4f4d\u4e45\u7559\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7b2c\u4e00\u6b21\u88ab\u4eba\u79f0\u4f5c\u957f\u8f88\uff0c\u800c\u4e14\u79f0\u547c\u4e2d\u8fd8\u5e26\u4e2a\u5a18\u5b57\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff0c\u66f4\u662f\u6709\u7740\u8212\u7545\u4e4b \u611f\u3002 \u3000\u3000\u5979\u6697\u5fd6\u9053\uff1a\u8981\u751f\u4e2a\u5973\u513f\uff0c\u957f\u7684\u548c\u95fb\u9999\u4e00\u6837\u5927\uff0c\u6050\u6015\u8981\u4e8c\u5341\u5e74\u7b49\u5f85\u3002\u8fd9\u51e0\u5e74\u6c5f\u5c71\u4e89\u9738\uff0c\u5175 \u7ed3\u7978\u8fde\uff0c\u5c31\u7b97\u5f88\u60f3\u751f\uff0c\u6050\u6015\u4e5f\u6ca1\u6709\u7a7a\u751f\u4e86\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u8d70\u7684\u8fd9\u4e48\u6025\u5462\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u53ef\u662f\u6709\u4ec0\u4e48\u4e0d\u5bfb\u5e38\u7684\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4e03\u7edd\u9053\u957f\u8c0b\u4f60\u5f88\u6025\u3002\u201d\u95fb\u9999\u9053\uff1a\u201c\u5927\u738b\u662f\u88c5\u4f5c\u4e0d\u77e5\uff0c\u552f\u4e00\u51fa\u5927\u738b\u610f\u6599\u4e4b\u5916\u7684\u662f \u5f20\u59d1\u5a18\u62d2\u7edd\u4e86\u540c\u5f80\u957f\u5b89\u4e00\u884c\uff0c\u4e0d\u8fc7\u8fd9\u5374\u6fc0\u8d77\u4e86\u5927\u738b\u7684\u6012\u706b\uff0c\u6240\u4ee5\u5927\u738b\u4f3c\u662f\u540c\u610f\u4e86\u4e03\u7edd\u9053\u957f\u7684 \u8ba1\u5212\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u8138\u8272\u5927\u53d8\uff0c\u5fc3\u4e2d\u6fc0\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u4f46\u674e\u9756\u8fd8\u662f\u5341\u5206\u6c89\u7740\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u662f\u4ec0\u4e48\u8ba1\u5212\u5417\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u5927\u738b\u8981\u7559\u4e0b\u4e00\u4e9b\u4eba\u9a6c\uff0c\u9632\u5b88\u534e\u9634\uff0c\u5e08\u7236\u53ef\u77e5\u7559\u7684\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u6211\u4e0d\u77e5\u9053\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u4f46\u7559\u4e0b\u4e00\u4e07\u4eba\u9a6c\uff0c\u8981\u7531\u6211\u7edf\u7387\uff0c\u4ee5\u56fa\u534e\u9633\uff0c\u96be\u9053\u8fd9\u4e00\u70b9\u5df2\u6709\u6539\u53d8\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f46\u6709\u53d8\uff0c\u800c\u4e14\u662f\u7ffb\u5929\u8986\u5730\u7684\u5927\u53d8\u54ea\uff01\u201d\u95fb\u9999\u9053\uff1a\u201c\u4e00\u4e07\u519b\u5175\u5df2\u7ecf\u79bb\u5f00\u8425\u5730\uff0c\u5206\u522b\u57cb \u4f0f\uff0c\u5175\u529b\u5982\u4f55\u5e03\u7f72\uff1f\u5f1f\u5b50\u4e0d\u592a\u6e05\u695a\uff0c\u4f46\u91cd\u70b9\u662f\u534e\u5c71\u884c\u5bab\uff0c\u4e0a\u5c71\u5bb9\u6613\u4e0b\u5c71\u96be\u3002\u201d \u3000\u3000\u201c\u53ef\u6076\u5440\uff01\u53ef\u6076\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u4e03\u7edd\u9053\u957f\u641e\u7684\u9b3c\u3002\u201d \u3000\u3000\u674e\u9756\u53c8\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u5e03\u7f72\u5417\uff1f\u90a3\u4e9b\u5175\u529b\u4ee5\u9632\u5835\u4e3a\u4e3b\u3002\u8981\u6349\u62ff\u6211\u4eec\u592b\u5987\uff0c\u8fd8\u8981\u7279\u522b\u7684 \u9ad8\u624b\u624d\u6210\u3002\u201d \u3000\u3000\u95fb\u9999\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u7684\u662f\uff0c\u7559\u5728\u57ce\u4e2d\u7684\u516b\u5251\u5ba2\u662f\u4e3b\u529b\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6253\u65ad\u4e86\u95fb\u9999\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u516b\u5251\u5ba2\uff1f\u4e0d\u662f\u5927\u738b\u7684\u516b\u4e2a\u5f1f\u5b50\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u95fb\u9999\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u8ffd\u968f\u5927\u738b\u5230\u957f\u5b89\uff0c\u5374\u7559\u5728\u534e\u9634\uff0c\u73b0\u5728\u5206\u5b88\u5728\u5e08\u7236\u3001\u5e08\u5a18 \u4f4f\u5b85\u7684\u4e1c\u3001\u5357\u4e24\u9762\uff0c\u9664\u4e86\u516b\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u767e\u540d\u9f99\u3001\u864e\u6b66\u58eb\uff0c\u6bcf\u4e00\u4e2a\u65b9\u4f4d\u4e94\u5341\u4eba\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6c14\u5f97\u8138\u4e5f\u9752\u4e86\uff0c\u5168\u8eab\u5fae\u5fae\u6296\u52a8\u3002 \u3000\u3000\u201c\u5b88\u5728\u5317\u65b9\u548c\u897f\u65b9\u7684\u4eba\u53c8\u662f\u4e9b\u4ec0\u4e48\u9ad8\u624b\u5462\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u4e8b\u5173\u91cd\u5927\uff0c\u60f3\u6e05\u695a\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u95fb\u9999\u679c\u7136\u51dd\u795e\u601d\u7d22\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u89c1\u8fc7\uff0e\u597d\u50cf\u662f\u4e03\u7edd\u9053\u957f\u7684\u4eba\uff0c\u4ed6\u4eec\u7a7f\u7684\u8863\u670d\u5f88 \u602a\uff0c\u4e00\u534a\u81ea\uff0c\u4e00\u534a\u7ea2\u3002\u6bcf\u4e2a\u4eba\u9664\u80cc\u63d2\u957f\u5251\u4e4b\u5916\uff0c\u8fd8\u6000\u62b1\u4e00\u4e2a\u9ed1\u8272\u957f\u7b80\uff0c\u9ed1\u5f97\u5f88\u6d53\uff0c\u770b\u4e0d\u51fa\u4ec0 \u4e48\u8d28\u5730\u3002\u201d \u3000\u3000\u674e\u9756\u4e00\u76b1\u7709\u5934\uff0c\u4f3c\u662f\u5bf9\u90a3\u9ed1\u7b80\u5f88\u662f\u5728\u610f\u3002 \u3000\u3000\u987f\u4e86\u987f\uff0c\u674e\u9756\u53c8\u95ee\uff1a\u201c\u90a3\u9ed1\u7b80\u957f\u77ed\u5982\u4f55\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u957f\u7ea6\u4e00\u5c3a\u4e94\u5bf8\uff0c\u4f46\u5374\u7c97\u5982\u4eba\u81c2\uff0c\u6709\u4e00\u6839\u9ed1\u8272\u7ef3\u5b50\u7ed1\u4f4f\uff0c\u6302\u5728\u80a9\u4e0a\uff0c\u77a7\u4e0d\u51fa\u5185 \u85cf\u7740\u4f55\u7269\uff01\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u4eec\u5df2\u7ecf\u5e03\u7f6e\u597d\u4e86\uff0c\u6211\u4eec\u82e5\u79bb\u5f00\u6b64\u5730\uff0c\u4ed6\u4eec\u5c31\u52a8\u624b\u62e6\u622a\uff0c\u8fde\u674e\u4e16 \u6c11\u4e5f\u4e0d\u8ba9\u6211\u4eec\u89c1\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u5f1f\u5b50\u5c31\u60f3\u4e0d\u901a\u4e86\uff0c\u597d\u50cf\u5835\u51fb\u7684\u91cd\u70b9\u662f\u534e\u5c71\u884c\u5bab\uff0c\u4e3a\u4ec0\u4e48\u53c8\u628a\u5e08\u7236\u3001\u5e08 \u5a18\u4f4f\u7684\u5b85\u9662\u4e5f\u5305\u56f4\u4f4f\u5462\uff1f\u800c\u4e14\u5728\u56db\u5468\u5e03\u7f6e\u4e86\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u7b97\u8fc7\u6ca1\u6709\uff0c\u90a3\u7a7f\u7740\u534a\u7ea2\u534a\u767d\u8863\u670d\u7684\u4eba\uff0e\u4e00\u5171\u6709\u591a\u5c11\u4e2a\uff1f\u201d \u3000\u3000\u674e\u9756\u95ee\u7684\u975e\u5e38\u8be6\u5c3d\u3002 \u3000\u3000\u201c\u4eba\u6570\u6211\u6ca1\u7b97\u8fc7\u3002\u201d\u95fb\u9999\u9053\uff1a \u3000\u3000\u201c\u7ea6\u7565\u4f30\u8ba1\u603b\u6709\u4e8c\u5341\u4e0a\u4e0b\uff0c\u5206\u5b88\u5728\u897f\u3001\u5317\u4e24\u9762\uff0c\u4ed6\u4eec\u4e5f\u6709\u4ece\u5c5e\uff0e\u5168\u90fd\u662f\u9ed1\u8272\u7684\u52b2\u88c5\uff0c\u5947 \u602a\u7684\u662f\u80cc\u540e\u7528\u7fbd\u6bdb\u672d\u4e86\u4e24\u4e2a\u7fc5\u8180\uff0c\u8e72\u5728\u54ea\u91cc\uff0c\u5c31\u50cf\u4e00\u53ea\u5947\u5927\u7684\u4e4c\u9e26\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u50cf\u4e4c\u9e26\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u50cf\u522b\u7684\u9e1f\u5462\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6697\u81ea\u751f\u4e86\u534a\u5929\u7684\u6c14\uff0c\u8fd8\u662f\u56de\u5230\u4e86\u73b0\u5b9e\uff0c\u60c5\u52bf\u53d1\u5c55\u4f3c\u96be\u514d\u4e00\u573a\u5927\u6218\uff0c\u4e86\u89e3\u654c\u60c5\uff0c\u624d \u662f\u7834\u56f4\u800c\u51fa\u7684\u91cd\u8981\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u56de\u5e08\u5a18\u8bdd\uff0c\u4ed6\u4eec\u5168\u8eab\u9ed1\u8863\uff0c\u80cc\u4e0a\u7fbd\u7fc5\u4e5f\u662f\u9ed1\u7684\uff0c\u5934\u4e0a\u53c8\u6234\u4e86\u4e00\u9876\u9ed1\u5e3d\u5b50\uff0c\u88c5\u4e86\u4e00\u4e2a\u957f \u5634\u5df4\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u4e00\u53ea\u5927\u4e4c\u9e26\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u674e\u9756\uff0c\u8fd9\u662f\u4ec0\u4e48\u602a\u88c5\u675f\u5440\uff1f\u5e3d\u5b50\u4e0a\u88c5\u5634\u5df4\uff0c\u80cc\u4e0a\u672d\u7fc5\u8180\uff0c\u6253\u8d77\u4ed7\u6765\u65b9\u4fbf \u5417\uff1f\u201d \u3000\u3000\u674e\u9756\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u6b64\u7684\u5927\u8d39\u5468\u6298\uff0c\u5fc5\u6709\u5b83\u7279\u6b8a\u7684\u4f5c\u7528\uff0c\u4e0d\u8981\u5c0f\u89d1\u4e86\u5b83\u3002\u95fb\u9999\uff0c\u4f60 \u6050\u6015\u5df2\u7ecf\u8d70\u4e0d\u4e86\u5566\u2026\u2026\u201d \u3000\u3000\u201c\u5f1f\u5b50\u6b7b\u4e0d\u8db3\u60dc\u3002\u201d\u95fb\u9999\u9053\uff1a \u3000\u3000\u201c\u53ea\u662f\u6b64\u756a\u8eab\u4efd\u6cc4\u6f0f\uff0c\u65e5\u540e\u65e0\u6cd5\u518d\u4e3a\u5e08\u7236\u6548\u52b3\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u7528\u56de\u53bb\u4e86\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u91cd\u8981\u4e1c\u897f\u8981\u5e26\u5417\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u5f1f\u5b50\u4eba\u4e00\u4e2a\uff0c\u547d\u4e00\u6761\uff0c\u8eab\u5916\u65e0\u957f\u7269\u3002\u53ea\u8981\u5e08\u5a18\u8d50\u5f1f\u5b50\u4e00\u652f\u5b9d\u5251\uff0c\u7b49\u4e00\u4e0b\u5f1f\u5b50 \u4e3a\u5e08\u7236\u5f00\u9053\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u95fb\u9999\uff0c\u8fc7\u6765\uff0c\u6211\u4eec\u6362\u8863\u670d\u53bb\u3002\u201d \u3000\u3000\u95fb\u9999\u968f\u7740\u5f20\u51fa\u5c18\u8fdb\u5165\u5185\u5ba4\u3002 \u3000\u3000\u674e\u9756\u975e\u5e38\u6c89\u5f97\u4f4f\u6c14\uff0c\u6d88\u6d88\u4ead\u4ead\u7684\u5403\u4e86\u4e24\u7897\u7a00\u996d\uff0c\u4e24\u4e2a\u9992\u5934\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u548c\u95fb\u9999\u6362\u597d\u4e86\u4e00\u8eab\u52b2\u88c5\uff0c\u4f69\u5e26\u597d\u5b9d\u5251\uff0c\u8d70\u51fa\u6765\u3002 \u3000\u3000\u674e\u9756\u653e\u4e0b\u7897\u7b77\uff0c\u64e6\u64e6\u5634\uff0c\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u4f8d\u5019\u5f20\u51fa\u5c18\u7684\u5973\u5a62\u8138\u8272\u82cd\u767d\u7684\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u2026\u2026\u5c0f\u5a62\u88ab\u4ed6\u4eec\u5f3a\u8feb\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5973\u5a62\u6253\u91cf\u4e86\u5f20\u51fa\u5c18\u548c\u95fb\u9999\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u73b0\u5728\u95e8\u5916\u7b49\u5019\uff0c\u674e\u7237\uff01\u4ed6\u4eec\u4e5f\u5e26\u7740\u5175\u5203\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u8981\u674e\u9756\u4e5f\u62ff\u51fa\u5175\u5203\u3002 \u3000\u3000\u674e\u9756\u6325\u6325\u624b\uff0c\u8ba9\u5973\u5a62\u9000\u5230\u4e00\u4fa7\u3002 \u3000\u3000\u4ed6\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u65e2\u7136\u5df2\u7ecf\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8fdb\u6765\u5462\uff1f\u201d \u3000\u3000\u4eba\u5f71\u95ea\u52a8\uff0c\u4e24\u4e2a\u4eba\u5e76\u80a9\u884c\u5165\u4e86\u623f\u4e2d\u3002 \u3000\u3000\u5de6\u9996\u5927\u6c49\u52b2\u88c5\u4f69\u5251\uff0c\u5e74\u7ea6\u4e09\u5341\u56db\u4e94\uff0c\u6b63\u662f\u866c\u9aef\u5ba2\u5927\u5f1f\u5b50\u516b\u5927\u5251\u5ba2\u4e4b\u9996\u2014\u2014\u7834\u5929\u5251\u9ec4\u4e91\u3002 \u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u9ec4\u4e91\u89c1\u8fc7\u5f20\u59d1\u5a18\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u5f20\u51fa\u5c18\u606d\u656c\u6709\u52a0\uff0c\u4f46\u5bf9\u674e\u9756\u5374\u89c6\u800c\u4e0d\u89c1\u3002 \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u98ce\u4e91\u6f5c\u9f99 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u4e5d \u56de\u3000\u667a\u8131\u864e\u7a74 \u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u597d\uff01\u201d \u3000\u3000\u674e\u9756\u4e0d\u4f46\u6ca1\u6709\u751f\u6c14\uff0c\u800c\u4e14\u8fde\u53eb\u4e86\u4e09\u4e2a\u2018\u597d\u2019\u5b57\uff0c\u4e0d\u4f46\u628a\u9ec4\u4e91\u53eb\u7cca\u6d82\u4e86\uff0c\u8fde\u5f20\u51fa\u5c18\u4e5f\u88ab\u4ed6 \u53eb\u5f97\u4e00\u5934\u96fe\u6c34\u3002 \u3000\u3000\u6781\u5c3d\u6536\u655b\u5b66\u6e29\u67d4\uff01 \u3000\u3000\u5f20\u51fa\u5c18\u975e\u5e38\u60f3\u95ee\u674e\u9756\u597d\u4ec0\u4e48\u5462\uff1f\u4f46\u662f\u5374\u5fcd\u4e0b\u672a\u8bf4\uff0c\u4e5f\u672a\u7406\u4f1a\u9ec4\u4e91\u3002\u592b\u541b\u5f53\u5385\u5750\uff0c\u4e00\u5207\u5c31 \u7531\u4ed6\u4f5c\u4e3b\u4e86\u3002 \u3000\u3000\u201c\u9ec4\u4e91\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u4f60\u4e0d\u7ed9\u4e8c\u7237\u89c1\u793c\u4e5f\u5c31\u7b97\u4e86\u3002\u4e00\u5e74\u591a\u4e0d\u89c1\uff0c\u5927\u5bb6\u53d8\u751f\u758f\u4e86\uff0c\u8fd9\u4f4d\u5947 \u88c5\u5f02\u773c\u4e00\u534a\u7ea2\u886b\u4e00\u534a\u767d\u7684\u602a\u7269\uff0c\u662f\u4eba\u662f\u9b3c\uff1f\u8be5\u7ed9\u6211\u4ecb\u7ecd\u4e00\u4e0b\u5427\uff01\u201d \u3000\u3000\u9ec4\u4e91\u5fc3\u4e2d\u662f\u6709\u70b9\u60ed\u6127\u3002 \u3000\u3000\u4e00\u5e74\u524d\u662f\u8fd9\u4e48\u53eb\u7684\uff0c\u89c1\u674e\u9756\u65f6\u606d\u656c\u6709\u793c\uff0c\u8fde\u547c\u4e8c\u7237\u3002\u76f8\u9694\u4e00\u5e74\uff0c\u795e\u6001\u5927\u53d8\uff0c\u7ecf\u674e\u9756\u5f53\u9762 \u70b9\u7834\uff0c\u4ed6\u4e00\u5f20\u8138\u5168\u7f9e\u7ea2\u4e86\u3002 \u3000\u3000\u5e78\u597d\u53f3\u9762\u8eab\u7740\u6c34\u706b\u8863\uff0c\u80cc\u63d2\u957f\u5251\uff0c\u4e0d\u8fc7\u624b\u4e2d\u672a\u62b1\u95fb\u9999\u8bf4\u7684\u9ed1\u7b52\uff0c\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u7528\u4e0d\u7740 \u9ec4\u7237\u4ecb\u7ecd\uff0c\u5728\u4e0b\u81ea\u62a5\u59d3\u540d\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u597d\uff01\u4f60\u65e2\u7136\u6709\u540d\u5b57\uff0c\u90a3\u5c31\u8bf4\u51fa\u6765\u542c\u542c\u3002\u5c3d\u8be6\u7ec6\u4e00\u4e9b\uff0c\u6700\u597d\u628a\u4f60\u7684\u51fa\u8eab\u6765\u5386\u4e00 \u5e76\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u674e\u9756\uff0c\u522b\u592a\u8fc7\u5206\u4e86\uff01\u8f9b\u8001\u4e8c\u4e0d\u5403\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u201c\u4f60\u59d3\u8f9b\uff0c\u53eb\u8001\u4e8c\uff1f\u201d\u674e\u9756\u9053\uff1a\u201c\u6ca1\u6709\u4e2a\u50cf\u6837\u7684\u540d\u5b57\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e0d\u52b3\u4f60\u8d39\u5fc3\u4e86\uff0c\u77e5\u9053\u6211\u662f\u8f9b\u8001\u4e8c\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u53cc\u76ee\u795e\u5149\u5982\u7535\uff0c\u76ef\u6ce8\u5728\u8f9b\u8001\u4e8c\u8138\u4e0a\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u6839\u672c\u6ca1\u6709\u540d\u5b57\uff0c\u4f60\u4e5f\u4e0d\u59d3\u8f9b\uff0c\u4f60\u4eec\u662f\u4e03\u7edd\u9053\u957f\u8bad\u7ec3\u7684\u6740\u624b\u3002\u6211\u4e0d\u660e\u767d\uff0c \u4ed6\u4e3a\u4ec0\u4e48\u4ed8\u4e8e\u4f60\u4eec\u4e00\u4e2a\u8f9b\u59d3\u3002 \u3000\u3000\u4f46\u6211\u77e5\u9053\u8001\u4e8c\u53ea\u662f\u4e00\u4e2a\u7f16\u53f7\uff0c\u8f9b\u8001\u4e8c\u3001\u8f9b\u5341\u4e8c\u3001\u8f9b\u4e5d\u4e5d\uff0c\u90fd\u662f\u4e00\u4e2a\u610f\u601d\uff0c\u4e5f\u8bb8\u4e00\u4e2a\u8f9b\u8001 \u4e8c\u5c31\u6c38\u8fdc\u6b7b\u4e0d\u5b8c\u4e86\uff0c\u6740\u4e00\u4e2a\uff0c\u518d\u6709\u4e00\u4e2a\u9876\u4e0a\u6765\u3002\u201d \u3000\u3000\u8f9b\u8001\u4e8c\u7684\u8138\u4e0a\u98de\u8d77\u4e86\u4e00\u7247\u7ea2\u6655\uff0c\u4f3c\u662f\u88ab\u674e\u9756\u8bf4\u7834\u4e86\u5fc3\u4e8b\u3002 \u3000\u3000\u4f46\u8f9b\u8001\u4e8c\u5e76\u672a\u627f\u8ba4\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u4f60\u7684\u6b66\u529f\u548c\u4f60\u53e3\u624d\u4e00\u6837\u7684\u51cc\u5389\uff0c\u5426\u5219\u5c31\u662f\u4f60 \u7684\u5927\u4e0d\u5e78\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\u7684\uff0c\u4f60\u5c3d\u7ba1\u51fa\u624b\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u9ec4\u4e91\u4e00\u6446\u624b\uff0c\u5236\u6b62\u4e86\u8f9b\u8001\u4e8c\uff0c\u9053\uff1a\u201c\u674e\u7237\uff0c\u6211\u4eec\u5949\u547d\u884c\u4e8b\uff0c\u60c5\u975e\u5f97\u5df2\uff0c\u8bf7\u674e\u7237\u8d50\u8c05\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u4e0d\u4f1a\u602a\u4f60\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u4e49\u7ed3\u91d1\u5170\u7684\u5927\u54e5\uff0c\u8981\u6740\u4ed6\u7684\u5144\u5f1f\u3001\u4e49\u59b9\uff0c\u8fd9\u8fd8\u6709 \u4ec0\u4e48\u597d\u8bf4\u7684\u3002\u4e0d\u8fc7\uff0c\u8981\u4f60\u9ec4\u4e91\u5e2e\u6211\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u80cc\u53db\u8a93\u8a00\u3001\u53cd\u76ee\u6210\u4ec7\u7684\uff0c\u4e0d\u662f\u6211\u674e\u9756\u674e\u8001 \u4e8c\uff0c\u662f\u5f20\u4ef2\u575a\u3002\u5f20\u5927\u54e5\u8bbe\u4e0b\u4ee3\u5175\uff0c\u8981\u6740\u6211\u4eec\u592b\u5987\uff0c\u6211\u4eec\u592b\u5987\u662f\u88ab\u8feb\u53cd\u6297\u3002\u201d \u3000\u3000\u9ec4\u4e91\u9053\uff1a\u201c\u674e\u7237\uff0c\u6211\u4e0d\u4f1a\u548c\u4eba\u8c08\u8d77\u6b64\u4e8b\uff0c\u4f46\u5982\u6211\u4e00\u65e6\u8bf4\u4e86\uff0c\u4e00\u5b9a\u4f1a\u5b9e\u8bdd\u5b9e\u8bf4\u3002\u201d \u3000\u3000\u201c\u53ea\u6b64\u4e00\u6869\uff0c\u4f60\u9ec4\u4e91\u8fd8\u4fdd\u6709\u541b\u5b50\u6c14\u5ea6\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u6211\u4eec\u8981\u4e0a\u534e\u5c71\u884c\u5bab\u63a2\u671b\u674e\u4e16\u6c11\uff0c\u4f60 \u4eec\u8981\u96c6\u4e2d\u529b\u91cf\uff0c\u53ef\u4ee5\u64a4\u5230\u534e\u5c71\u4e0b\u9762\uff0c\u548c\u4f0f\u5175\u5408\u800c\u4e3a\u4e00\uff0c\u73b0\u5728\u662f\u653e\u6211\u4eec\u8fc7\u53bb\uff0c\u8fd8\u662f\u8981\u6211\u4eec\u6253\u8fc7 \u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5949\u547d\u4e0d\u8bb8\u674e\u7237\u79bb\u5f00\u534e\u9634\u3002\u201d\u9ec4\u4e91\u9053\uff1a\u201c\u674e\u7237\u8981\u4e0a\u534e\u5c71\u884c\u5bab\uff0c\u6211\u4eec\u662f\u606d\u9001\u5982\u4eea\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u597d\uff01\u96c6\u4e2d\u529b\u91cf\uff0c\u4e00\u6b21\u5bf9\u51b3\uff0c\u5bf9\u53cc\u65b9\u90fd\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u9ec4\u4e91\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u96c6\u4e2d\u529b\u91cf\uff0c\u4f7f\u6211\u4eec\u5b9e\u529b\u589e\u5f3a\u6570\u500d\uff0c\u5bf9\u674e\u7237\u4f1a\u6709\u4ec0\u4e48\u597d\u5904\u5462\uff1f\u96be\u9053 \u5728\u6211\u4eec\u5927\u519b\u56de\u5e03\u7684\u5730\u76d8\u4e0a\uff0c\u674e\u7237\u4e5f\u80fd\u53d8\u51fa\u63f4\u519b\u6765\uff1f\u201d \u3000\u3000\u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u673a\u8d35\u795e\u51fa\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u5c4a\u65f6\u8bf8\u4f4d\u5c31\u53ef\u4ee5\u89c1\u8bc6\u5230\u4e86\u3002\u201d \u3000\u3000\u9ec4\u4e91\u7684\u76ee\u5149\u7a81\u7136\u8f6c\u6ce8\u5728\u95fb\u9999\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u7684\u9053\uff1a\u201c\u95fb\u9999\uff0c\u4f60\u662f\u66ff\u674e\u9756\u9001\u9152\u83dc\u6765\uff0c\u73b0\u5728\u8ddf\u6211\u56de\u53bb\u5427\uff01\u5927\u738b\u51ef\u65cb\u5f52\u6765\uff0c\u8fd8\u8981\u4f60 \u659f\u9152\u559d\u5462\uff01\u201d \u3000\u3000\u4ed6\u5df2\u66ff\u95fb\u9999\u94fa\u597d\u4e86\u53f0\u9636\u3002 \u3000\u3000\u95fb\u9999\u53ea\u8981\u6b65\u9636\u800c\u4e0b\u5c31\u6210\u3002 \u3000\u3000\u4f46\u95fb\u9999\u4e0d\u9886\u60c5\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7237\uff0c\u4f60\u770b\u5c0f\u5a62\u8fd9\u4e2a\u6837\u5b50\u8fd8\u80fd\u56de\u5f97\u53bb\u5417\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d\u9ec4\u4e91\u9053\uff1a\u201c\u4f60\u5170\u8d28\u6167\u5fc3\uff0c\u5584\u89e3\u4eba\u610f\uff0c\u8bc6\u9152\u4e4b\u80fd\uff0c\u5929\u4e0b\u7b2c\u4e00\uff1b\u8c03\u9152\u7684\u672c \u9886\uff0c\u65e0\u4eba\u80fd\u53ca\u3002\u5927\u738b\u5982\u4f55\u80fd\u79bb\u5f97\u5f00\u4f60\uff1f\u201d \u3000\u3000\u95fb\u9999\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u9ec4\u7237\u7684\u5938\u5956\uff0c\u53ef\u60dc\u6211\u5df2\u7ecf\u4e0d\u80fd\u56de\u53bb\u4e86\uff0c\u6211\u7a7f\u7684\u662f\u674e\u592b\u4eba\u7684\u8863 \u670d\uff0c\u4f69\u7684\u662f\u674e\u592b\u4eba\u8d50\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u201c\u5927\u738b\u9700\u8981\u4f60\uff0c\u4e0d\u4f1a\u548c\u4f60\u8ba1\u8f83\u8fd9\u4e9b\u5c0f\u8282\u7684\uff0c\u6211\u9ec4\u4e91\u6562\u62ff\u4eba\u5934\u62c5\u4fdd\uff0c\u95fb\u9999\u59d1\u5a18\uff01\u4f60\u7edd\u4e0d\u4f1a \u6709\u4e8b\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd8\u662f\u4e0d\u660e\u767d\u5440\uff01\u201d\u95fb\u9999\u9053\uff1a\u201c\u6211\u8fd9\u4e48\u8bf4\u5427\uff01\u9ec4\u7237\uff0c\u6211\u662f\u674e\u7237\u7684\u4f0f\u5175\u4e4b\u4e00\u3002\u201d \u3000\u3000\u9ec4\u4e91\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u4e86\uff0c\u8fd8\u4f1a\u9a97\u4f60\u5417\uff1f\u6211\u4eec\u8fd9\u756a\u5bf9\u8bdd\uff0c\u4e0d\u5c31\u662f\u6211\u53db\u79bb\u5927\u738b\u3001\u8ffd\u968f\u674e\u7237\u7684 \u660e\u8bc1\uff0c\u8fd8\u8981\u600e\u4e48\u6837\u4f60\u624d\u4f1a\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u9ec4\u4e91\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u674e\u7237\u80fd\u5728\u5927\u738b\u7684\u5fc3\u8179\u4e2d\u5e03\u4e0b\u4f0f\u5175\uff0c\u679c\u7136\u5389\u5bb3\uff0c\u4f46\u4e0d\u77e5\u50cf\u4f60\u95fb\u9999\u7684 \u4eba\u8fd8\u6709\u591a\u5c11\uff1f\u201d \u3000\u3000\u95fb\u9999\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u4f60\u81ea\u5df1\u95ee\u674e\u7237\u5427\uff01\u6211\u53ea\u80fd\u544a\u8bc9\u9ec4\u7237\uff0c\u7edd\u4e0d\u6b62\u6211\u95fb\u9999 \u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ec4\u4e91\u7a81\u7136\u653e\u58f0\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u90fd\u50cf\u4f60\u95fb\u9999\u8fd9\u6837\u7684\u4eba\uff0c\u591a\u51e0\u4e2a\u53c8\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u624b\u65e0\u7f1a\u9e21 \u4e4b\u529b\uff0c\u60e8\u4f5c\u5200\u4e0b\u4e4b\u9b3c\u3002\u201d \u3000\u3000\u201c\u9ec4\u7237\uff01\u201d\u95fb\u9999\u9053\uff1a\u201c\u5c0f\u5a62\u4e5f\u8bb8\u4e0d\u662f\u516b\u5251\u5ba2\u4e4b\u9996\u7684\u654c\u624b\uff0c\u4f46\u4e09\u4e94\u5341\u5251\u6211\u603b\u80fd\u63a5\u5f97\u4e0b \u6765\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5df2\u7ecf\u6709\u4e9b\u4e0d\u8010\uff0c\u62d4\u5251\u5728\u624b\uff0c\u9053\uff1a\u201c\u8981\u51fa\u624b\u5feb\u4e9b\u62d4\u5251\uff0c\u5426\u5219\u5c31\u5feb\u8ba9\u5f00\u8def\u3002\u201d \u3000\u3000\u9ec4\u4e91\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u5f20\u59d1\u5a18\u7b49\u662f\u5230\u534e\u5c71\u884c\u5bab\uff0c\u90a3\u5c31\u8bf7\u4e0a\u8def\uff0c\u4e0d\u8fc7\u5982\u4e0d\u662f\u53bb\u5411\u534e\u5c71\u884c \u5bab\uff0c\u90a3\u5c31\u522b\u602a\u6211\u4eec\u65bd\u5c55\u5404\u79cd\u622a\u51fb\u7684\u624b\u6bb5\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u9ec4\u4e91\uff0c\u4f60\u7adf\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u4e8c\u7237\u51e0\u65f6\u8bf4\u8bdd\u4e0d\u7b97\u4e86\uff1f\u201d \u3000\u3000\u674e\u9756\u8fc8\u5f00\u5927\u6b65\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u3001\u95fb\u9999\u5251\u5df2\u51fa\u9798\uff0c\u7d27\u968f\u5728\u674e\u9756\u8eab\u540e\u3002 \u3000\u3000\u9ec4\u4e91\u548c\u8f9b\u8001\u4e8c\u4fdd\u6301\u4e86\u4e08\u8bb8\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u5012\u9000\u800c\u884c\u3002 \u3000\u3000\u5728\u4e24\u4eba\u4e24\u4fa7\u524d\u540e\uff0c\u96c6\u7ed3\u4e86\u516b\u5927\u5251\u5ba2\u3001\u4e8c\u5341\u56db\u4e2a\u7740\u6c34\u706b\u670d\u80cc\u63d2\u957f\u5251\u6000\u62b1\u5c3a\u4e94\u957f\u77ed\u4e00\u4e2a\u9ed1\u8272 \u7b52\u5b50\u7684\u6740\u624b\uff0c\u516d\u5341\u56db\u4e2a\u4e4c\u9e26\u5175\uff0c\u767e\u591a\u4f4d\u9f99\u3001\u864e\u6218\u58eb\uff0c\u56f4\u5728\u674e\u9756\u592b\u5987\u56db\u5468\u3002 \u3000\u3000\u674e\u9756\u5f88\u7528\u5fc3\u53bb\u770b\uff0c\u4f46\u5374\u770b\u4e0d\u51fa\u90a3\u662f\u4ec0\u4e48\u8d28\u6599\u4f5c\u6210\u7684\u957f\u7b52\uff1f \u3000\u3000\u4e5f\u770b\u4e0d\u51fa\u7b52\u91cc\u88c5\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f \u3000\u3000\u674e\u9756\u884c\u8fdb\u7684\u65b9\u5411\u679c\u7136\u662f\u534e\u5c71\u884c\u5bab\u3002 \u3000\u3000\u9ec4\u4e91\u8ffd\u968f\u5728\u674e\u9756\u8eab\u540e\u3002 \u3000\u3000\u767b\u4e0a\u4e86\u767e\u5c42\u77f3\u7ea7\uff0c\u624d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ec4\u4e91\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u7237\uff0c\u949f\u6728\u9b41\u9632\u5b88\u5bab\u95e8\u3002\u201d \u3000\u3000\u6446\u8131\u4e86\u968f\u884c\u540c\u4f34\uff0c\u7ec8\u4e8e\u53eb\u51fa\u4e86\u4e00\u58f0\u4e8c\u7237\u3002 \u3000\u3000\u674e\u9756\u56de\u9996\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u9ec4\u4e91\uff01\u8fd9\u4e00\u6b21\u53cd\u8138\u6210\u4ec7\uff0c\u7acb\u523b\u5c31\u5175\u6208\u76f8\u89c1\uff0c\u6765\u5f97\u592a\u5feb\u4e86 \u4e5f\u592a\u6025\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u9ec4\u4e91\u77e5\u9053\u4e8c\u7237\u7684\u624d\u80fd\u3001\u5fe0\u8bda\uff0c\u4f46\u6211\u65e0\u80fd\u4e3a\u529b\u5401\uff01\u4eba\u5fae\u8a00\u8f7b\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u6709\u8fd9\u4efd\u660e\u8bc6\u5b58\u5fc3\uff0c\u5c31\u662f\u77e5\u5df1\u3002\u9ec4\u4e91\uff0c\u7b49\u4e00\u4e0b\u53cc\u65b9\u52a8\u624b\u65f6\uff0c\u4f60\u7559\u4efd\u60c5\u610f\uff0c\u522b\u592a \u52c7\u5f80\u76f4\u524d\uff0c\u5bf9\u4f60\u6211\u90fd\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u9ec4\u4e91\u6ca1\u6709\u56de\u7b54\uff0c\u4ee5\u4e24\u9053\u660e\u6f88\u7684\u76ee\u5149\u9001\u674e\u9756\u4e00\u884c\uff0c\u767b\u4e0a\u4e86\u534e\u5c71\u884c\u5bab\u3002 \u3000\u3000\u949f\u6728\u9b41\u624b\u6267\u94c1\u6756\uff0c\u7ad9\u5728\u5bab\u95e8\u53e3\u5904\u3002\u67af\u5e72\u7684\u8138\u4e0a\uff0c\u8fd8\u5e26\u7740\u5fae\u7b11\uff0c\u8bf4\u662f\u7b11\uff0c\u4e8b\u5b9e\u4e0a\u6bd4\u54ed\u8fd8\u96be \u770b\u3002 \u3000\u3000\u54ed\u65f6\u5927\u5f20\u7740\u5634\u5df4\uff0c\u8fd8\u6709\u4e00\u5b9a\u7684\u5f62\u6001\uff0c\u8fd9\u5fae\u7b11\u5c31\u65e0\u6cd5\u5f62\u5bb9\u4e86\uff0e\u6709\u5982\u5200\u5b50\u5728\u67af\u6728\u4e0a\u5f00\u4e00\u9053\u53e3 \u5b50\uff0c\u7b11\u5f97\u4e00\u8138\u50f5\u786c\u3002 \u3000\u3000\u674e\u9756\u65e9\u5df2\u89c1\u8bc6\u8fc7\u4e86\uff0c\u77e5\u4ed6\u5e76\u975e\u505a\u4f5c\u3002 \u3000\u3000\u674e\u9756\u8fd8\u4ee5\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u4e91\u548c\u8f9b\u8001\u4e8c\u5e26\u4e00\u6279\u5251\u5ba2\u3001\u6740\u624b\uff0c\u9001\u6211\u4eec\u4e0a\u534e\u5c71\u884c\u5bab\uff0c\u6728\u8001\u80af\u5426 \u8ba9\u8ba9\u8def\u5462\uff1f\u201d \u3000\u3000\u201c\u8ba9\uff0c\u8ba9\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e8c\u7237\uff01\u4e0a\u5c71\u5bb9\u6613\u4e0b\u5c71\u96be\uff0c\u4e00\u79c0\u5cf0\u53ea\u6709\u8fd9\u4e00\u6761\u8def\uff0c\u4e8c\u7237 \u548c\u5f20\u59d1\u5a18\u53ef\u4ee5\u98de\u8fc7\u5343\u4e08\u8c37\uff0c\u8d8a\u6e21\u4e07\u91cd\u5c71\uff0c\u4f46\u522b\u4eba\u4e0d\u80fd\uff0c\u674e\u4e8c\u516c\u5b50\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u674e\u9756\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u6728\u8001\u7a76\u7adf\u60f3\u8bf4\u4ec0\u4e48\uff1f\u8fd8\u662f\u76f4\u8bf4\u7684\u597d\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u6211\u53ea\u662f\u8bf4\u660e\u4e00\u58f0\uff0c\u514d\u5f97\u4e8c\u7237\u8d23\u602a\u6211\u4e0d\u591f\u670b\u53cb\uff0c\u674e\u4e16\u6c11\u75c5\u5f97\u5f88\u91cd\uff0c\u4e8c\u7237\u5e94\u8be5 \u5e26\u4e2a\u5927\u592b\u6765\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u597d\u597d\u4e00\u4e2a\u4eba\uff0c\u600e\u4e48\u5ffd\u7136\u75c5\u4e86\uff1f\u4ec0\u4e48\u4eba\u5bb3\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5979\u5fcd\u4e86\u53c8\u5fcd\uff0c\u7ec8\u662f\u5fcd\u8010\u4e0d\u4f4f\u7684\u95ee\u3002 \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5bb3\u4e86\u4ed6\uff1f\u5982\u7389\u4f1a\u8bf4\u660e\u767d\uff0c\u5f20\u59d1\u5a18\u8bf7\u95ee\u5982\u7389\u5427\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u4e0d\u60a6\u5730\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u6211\u7167\u987e\u674e\u4e16\u6c11\u7684\uff0c\u4f46\u5374\u8a00\u800c\u65e0\u4fe1\u3002\u201d \u3000\u3000\u201c\u6ca1\u6cd5\u5b50\u554a\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u5927\u738b\u7684\u547d\u4ee4\uff0c\u8001\u592b\u53ef\u4e0d\u6562\u8fdd\u6297\uff0c\u4e5f\u53ea\u597d\u7741\u53ea\u773c\u95ed\u53ea\u773c \u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u5927\u738b\u4e0b\u4ee4\u5bb3\u4ed6\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4ed6\u4fdd\u8bc1\u4e0d\u4f1a\u52a0\u5bb3\u674e\u4e16\u6c11\u7684\uff0c\u6211\u624d\u628a\u4ed6\u5e26\u6765\u534e\u5c71\u884c \u5bab\u2026\u2026\u201d \u3000\u3000\u949f\u6728\u9b41\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u4e0d\u662f\u5927\u738b\u4e0b\u7684\u4ee4\uff0c\u5927\u6982\u662f\u4e03\u7edd\u9053\u957f\u62ff\u7740\u9e21\u6bdb\u5f53\u4ee4 \u7bad\uff0c\u9a97\u4e86\u6211\u4e5f\u4e0d\u4e00\u5b9a\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u53c8\u95ee\u9053\uff1a\u201c\u674e\u4e16\u6c11\u75c5\u60c5\u5982\u4f55\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4e0d\u8fc7\u4e5f\u7b97\u5dee\u4e0d\u591a\u4e86\u3002\u4e03\u7edd\u9053\u4eba\u7684\u836f\u7269\u3001\u624b\u6bb5\u6837\u6837\u7edd\uff0c\u4e5f\u96be\u602a\u674e \u4e16\u6c11\u53d7\u4e0d\u4e86\u554a\uff01\u201d \u3000\u3000\u949f\u6728\u9b41\u8bf4\u7740\u8bdd\uff0c\u4e00\u526f\u4e8b\u4e0d\u5173\u5df1\u7684\u6837\u5b50\u3002 \u3000\u3000\u9aa4\u95fb\u674e\u4e16\u6c11\u5982\u6b64\u75c5\u91cd\uff0c\u674e\u9756\u4e5f\u5fc3\u5934\u9707\u52a8\u4e86\u3002\u4f46\u662f\u60f3\u5230\u8881\u5929\u7f61\u7adf\u672a\u63d0\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5fc3\u60f3\u5176\u4e2d \u5fc5\u6709\u8e4a\u8df7\u3002 \u3000\u3000\u4ed6\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u6211\u674e\u9756\u7cbe\u901a\u836f\u7269\u3001\u533b\u7406\uff0c\u5c31\u662f\u6700\u597d\u7684\u5927\u592b\uff0c\u674e\u4e16\u6c11\u53ea\u8981\u8fd8\u6709\u4e00\u53e3\u6c14\u5728\uff0c \u6211\u5c31\u80fd\u4fdd\u4ed6\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e4b\u5927\uff0c\u7adf\u628a\u949f\u6728\u9b41\u4e5f\u552c\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e8c\u7237\uff01\u8fd9\u4e16\u4e0a\u771f\u6709\u2026\u2026\u771f\u6709\u8d77\u6b7b\u56de\u751f\u7684\u836f\u7269\u5417\uff1f\u6211\u5df2\u7ecf\u7ed9\u4ed6\u670d\u4e86\u4e00 \u652f\u5343\u5e74\u4eba\u53c2\uff0c\u53ef\u662f\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u8fd8\u4e0d\u80fd\u8fd8\u9b42\u91cd\u751f\u5417\uff1f\u201d\u674e\u9756\u7b11\u9053\uff1a\u201c\u4e00\u652f\u5343\u5e74\u8001\u53c2\u5168\u7ed9\u4ed6\u5403\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u674e\u4e16\u6c11\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5343\u5e74\u8001\u53c2\u4e5f\u4e0d\u80fd\u4f7f\u4ed6\u75c5\u4f53\u590d\u539f\uff0c\u5509\uff01\u771f\u662f\u767d\u767d \u7cdf\u8e4b\u4e86\u90a3\u652f\u73cd\u8d35\u7684\u8001\u53c2\u554a\uff01\u201d \u3000\u3000\u674e\u9756\u4e0d\u89e3\u5730\u9053\uff1a\u201c\u6211\u6709\u70b9\u5947\u602a\uff0c\u6728\u8001\u4e3a\u4ec0\u4e48\u8981\u5bf9\u674e\u4e16\u6c11\u8fd9\u6837\u597d\u5462\uff1f\u636e\u6211\u6240\u77e5\uff0c\u4ef2\u575a\u5927\u54e5 \u5b58\u6709\u7684\u8001\u53c2\u4e0d\u591a\u554a\uff01\u201d \u3000\u3000\u201c\u53ea\u6709\u4e24\u652f\u4e86\u3002\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u8001\u592b\u90a3\u5929\u665a\u4e0a\u559d\u4e86\u70b9\u9152\uff0c\u773c\u770b\u674e\u4e16\u6c11\u5944\u5944\u4e00\u606f\uff0c\u5982\u7389\u6025 \u5f97\u773c\u6cea\u76f4\u6d41\uff0c\u6211\u83ab\u540d\u5176\u5999\u7684\u4e00\u51b2\u52a8\uff0c\u5c31\u628a\u4e00\u652f\u5343\u5e74\u8001\u53c2\u9001\u7ed9\u4e86\u674e\u4e16\u6c11\u3002 \u3000\u3000\u8001\u592b\u60f3\u8d77\u6765\uff0c\u6b64\u4e8b\u4e0d\u901a\uff0c\u674e\u4e16\u6c11\u5c31\u8981\u6b7b\u4e86\uff0c\u8fd8\u5403\u4ec0\u4e48\u5343\u5e74\u8001\u53c2\u5462\uff1f\u627e\u5230\u5982\u7389\u4e2b\u5934\uff0c\u60f3\u8ffd \u56de\u90a3\u652f\u8001\u53c2\uff0c\u60f3\u4e0d\u5230\u5979\u5feb\u624b\u5feb\u811a\u7684\u5df2\u7ecf\u716e\u6210\u9e21\u6c64\u7ed9\u674e\u4e16\u6c11\u559d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6728\u8001\u4e00\u751f\u5343\u9519\u4e07\u9519\uff0c\u603b\u7b97\u4f5c\u4e86\u8fd9\u4e48\u4e00\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u201c\u597d\u4e8b\uff1f\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u8001\u592b\u53ef\u662f\u8d8a\u60f3\u8d8a\u6004\uff0c\u5f88\u60f3\u6740\u4e86\u5982\u7389\uff0c\u51fa\u53e3\u9e1f\u6c14\uff0c\u4f46\u53c8\u8981\u7559\u7740\u5979 \u7167\u987e\u674e\u4e16\u6c11\u7684\u540e\u4e8b\uff0c\u8fd9\u6863\u4e8b\u4e00\u5b9a\u662f\u95fb\u9999\u641e\u9b3c\uff0c\u5728\u9152\u4e2d\u52a8\u4e86\u624b\u811a\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u7136\u53d1\u89c9\u4e86\u5f20\u51fa\u5c18\u8eab\u8fb9\u7684\u95fb\u9999\u3002 \u3000\u3000\u949f\u6728\u9b41\u6de1\u6de1\u5730\u9053\uff1a\u201c\u54c8\u54c8\uff01\u5c0f\u4e2b\u5934\uff0c\u4f60\u8ba4\u4e3a\u6362\u4e86\u8863\u670d\uff0c\u5c31\u80fd\u7792\u5f97\u8fc7\u8001\u592b\u5417\uff1f\u8bf4\uff01\u4f60\u7a76\u7adf \u5728\u9152\u4e2d\u52a8\u4e86\u4ec0\u4e48\u624b\u811a\uff1f\u201d \u3000\u3000\u201c\u54ea\u662f\u5927\u738b\u6700\u7231\u559d\u7684\u4e09\u82b1\u767d\uff0c\u7528\u4e0a\u7b49\u6c7e\u9152\uff0c\u8c03\u548c\u4e86\u5170\u3001\u83ca\u3001\u6885\u4e09\u82b1\u4e4b\u9999\uff0c\u540d\u8d35\u5f97\u5f88 \u5462\uff01\u201d\u95fb\u9999\u5fae\u7b11\u9053\uff1a\u201c\u5927\u738b\u5e38\u5e38\u559d\uff0c\u5c31\u6ca1\u6709\u4e8b\u3002\u4f60\u600e\u4e48\u4e00\u559d\u5c31\u53d1\u4e86\u9152\u75af\u5462\uff1f\u53ef\u771f\u662f\u547d\u7a77\uff0c\u5403 \u4e0d\u5f97\u9e21\u9aa8\u5934\uff0c\u5f53\u5fc3\u88ab\u5361\u6b7b\u554a\uff01\u201d \u3000\u3000\u949f\u6728\u9b41\u559d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u6562\u9a82\u6211\uff0c\u8001\u592b\u5148\u6bd9\u4e86\u4f60\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u957f\u5251\u51fa\u9798\uff0c\u5251\u5c16\u5df2\u76f4\u4e0a\u949f\u6728\u9b41\u7684\u54bd\u5589\u3002 \u3000\u3000\u5979\u5389\u58f0\u9053\uff1a\u201c\u4f60\u6700\u597d\u7948\u7977\u674e\u4e16\u6c11\u6d3b\u5f97\u6ca1\u4e8b\uff0c\u5426\u5219\u6211\u8ffd\u4f60\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u4e5f\u8981\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6ca1\u6cd5\u5b50\u4e86\uff0c\u4e00\u7b14\u5e10\u4e5f\u8981\u7b97\uff0c\u90a3\u5c31\u5341\u7b14\u52a0\u5728\u4e00\u8d77\u7b97\u5427\uff01\u5f20\u59d1\u5a18\u7684 \u5251\u672f\u867d\u7136\u7cbe\u7edd\uff0c\u4f46\u5374\u672a\u5fc5\u80fd\u6740\u5f97\u4e86\u6211\u949f\u67d0\u4eba\u3002\u201d \u3000\u3000\u201c\u6728\u8001\uff0c\u8bdd\u5df2\u7ecf\u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u8ba9\u8ba9\u8def\u5427\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u9664\u975e\u4f60\u81ea\u4fe1\u80fd\u62b5\u6321\u6211\u548c\u51fa\u5c18\u8054 \u624b\u4e00\u51fb\u3002\u201d \u3000\u3000\u674e\u9756\u548c\u5f20\u51fa\u5c18\u8054\u624b\u4e00\u51fb\uff0c\u4f1a\u6709\u591a\u5927\u7684\u5a01\u529b\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4ed6\u4eec\u4ece\u672a\u8054\u8fc7\u624b\uff0c\u4e00\u5e74\u524d\u5f20\u51fa \u5c18\u5251\u672f\u672a\u6210\uff0c\u4e5f\u65e0\u6cd5\u548c\u674e\u9756\u8054\u624b\u3002 \u3000\u3000\u4f46\u5374\u662f\u5413\u4eba\u5f97\u5f88\uff01 \u3000\u3000\u949f\u6728\u9b41\u4e0d\u6562\u6321\u5728\u5bab\u95e8\u53e3\u4e86\uff0c\u95ea\u5230\u4e00\u4fa7\u3002 \u3000\u3000\u674e\u9756\u53f3\u624b\u634f\u7740\u4e00\u4e2a\u5251\u8bc0\uff0c\u8083\u7acb\u4e0d\u52a8\uff0c\u76f4\u5f85\u5f20\u51fa\u5c18\u548c\u95fb\u9999\u8fdb\u5165\u4e86\u884c\u5bab\u4e4b\u540e\uff0c\u624d\u7a81\u7136\u626c\u624b\u51fb \u51fa\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u9053\u9ed1\u5149\uff0c\u51fb\u5728\u5c71\u58c1\u95f4\uff0c\u201c\u84ec\u201d\u7136\u4e00\u58f0\uff0c\u77f3\u6cab\u7eb7\u98de\uff0c\u51fb\u51fa\u4e00\u4e2a\u4e00\u5c3a\u65b9\u5706\u7684\u5927\u6d1e\u6765\u3002 \u3000\u3000\u949f\u6728\u9b41\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5982\u6b64\u5a01\u52bf\uff0c\u51fb\u4e2d\u6211\u8eab\uff0c\u5b9a\u7136\u53d7\u4f24\uff0c\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\u5462\uff1f\u542c\u8bf4\u674e\u9756\u4f1a\u672f\u6cd5\uff0c\u96be\u9053\u8fd9\u662f\u5996\u6cd5 \u96f7\u8bc0\uff1f\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u770b\u6e05\u695a\u674e\u9756\u662f\u5426\u6253\u51fa\u4e86\u6697\u5668\uff1f\u4e5f\u65e0\u6cd5\u4f5c\u6b63\u786e\u7684\u5224\u65ad\uff0c\u4f46\u662f\u4ed6\u5185\u5fc3\u5374\u7559\u4e0b\u4e86\u4e00\u7247\u9707 \u60ca\u3002 \u3000\u3000\u674e\u4e16\u6c11\u54ea\u91cc\u50cf\u6709\u75c5\u7684\u6837\u5b50\uff1f \u3000\u3000\u4ed6\u5750\u5728\u4e00\u5f20\u5927\u5e08\u6905\u4e0a\uff0c\u5bb9\u5149\u7115\u53d1\u3002 \u3000\u3000\u5982\u7389\u7ad9\u5728\u8eab\u4fa7\u3002 \u3000\u3000\u8881\u5929\u7f61\u5374\u5750\u5728\u674e\u4e16\u6c11\u8eab\u524d\u4e09\u56db\u5c3a\u5904\uff0c\u8868\u793a\u4e86\u4e3b\u4ece\uff0c\u4e5f\u6613\u4e8e\u4fdd\u62a4\u3002 \u3000\u3000\u674e\u9756\u5feb\u6b65\u5411\u524d\u8d70\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e5f\u7ad9\u8d77\u8eab\u5b50\u8fce\u4e0a\u6765\u3002 \u3000\u3000\u4e24\u4eba\u5728\u4e94\u5c3a\u8ddd\u79bb\u5904\u78b0\u5934\u3002 \u3000\u3000\u674e\u9756\u64a9\u8d77\u888d\u89d2\u6b32\u8dea\u62dc\uff0c\u5374\u88ab\u674e\u4e16\u6c11\u4e00\u628a\u62b1\u4f4f\u4e86\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u671b\u773c\u6b32\u7a7f\u4e86\uff0c\u8881\u5148\u751f\u544a \u8bc9\u6211\uff0c\u5144\u5f1f\u5e94\u6211\u6240\u6c42\u7684\u65f6\u673a\u5df2\u81f3\uff0c\u4f46\u4e0d\u77e5\u7a76\u7adf\u662f\u771f\u662f\u5047\u5440\uff1f\u201d \u3000\u3000\u674e\u9756\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u786e\u5982\u4e3b\u516c\u6240\u8a00\uff0c\u7ffb\u8138\u65ad\u4ea4\uff0c\u65a9\u60c5\u6740\u4eba\uff0c\u4e0d\u8fc7\u4e3b\u516c\u8981\u6c42\u7684\u4e8b\uff0c\u4fdf\u56de\u5927\u539f\u518d\u8bf4\uff0c\u8fd8 \u6709\u4e00\u4e9b\u5c0f\u8282\u5fc5\u987b\u5904\u7f6e\uff0c\u773c\u4e0b\u91cd\u8981\u7684\u662f\u5148\u8981\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5927\u559c\u82e5\u72c2\uff0c\u5bf9\u5f20\u51fa\u5c18\u4e00\u4e2a\u957f\u63d6\u3002 \u3000\u3000\u8fd9\u4e00\u793c\u6765\u5f97\u7a81\u7136\uff0c\u8fd8\u771f\u628a\u5f20\u51fa\u5c18\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4e8c\u516c\u5b50\uff0c\u8fd9\u662f\u5e72\u4ec0\u4e48\u610f\u601d\u5440\uff01\u201d \u3000\u3000\u5979\u4e00\u9762\u8bb2\u8bdd\uff0c\u4e00\u9762\u8fd8\u793c\u3002 \u3000\u3000\u201c\u611f\u8c22\u5ac2\u592b\u4eba\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u4e16\u6c11\u5f88\u60f3\u8dea\u4e0b\u6765\u62dc\u8c22\u5927\u6069\uff0c\u5982\u975e\u5ac2\u592b\u4eba\u5e26\u6211\u81f3\u6b64\uff0c\u674e\u9756 \u5144\u8fd8\u4e0d\u77e5\u8981\u575a\u6301\u5230\u54ea\u4e00\u5929\u624d\u80af\u7b54\u5e94\u6211\u7684\u6073\u6c42\u3002\u201d \u3000\u3000\u201c\u4e3b\u516c\u8a00\u91cd\u4e86\uff0c\u674e\u9756\u591a\u6b21\u63a8\u6258\uff0c\u4e0d\u5e94\u4e3b\u516c\u4e4b\u547d\uff0c\u8fd9\u65b9\u9762\u5148\u884c\u8c22\u7f6a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u79f0\u4e8c\u516c\u5b50\u548c\u4e16\u6c11\u5144\uff0c\u6539\u4ee5\u4e3b\u516c\uff0c\u5fc3\u4e2d\u663e\u7136\u5df2\u7b54\u5141\u4e86\u767b\u53f0\u62dc\u5e05\u7684\u4e8b\u3002 \u3000\u3000\u201c\u7b54\u5141\u5c31\u597d\uff0c\u7b54\u5141\u5c31\u597d\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a\u201c\u674e\u5144\u4f55\u7f6a\u4e4b\u6709\uff0c\u662f\u4e16\u6c11\u4e4b\u5e78\u4e5f\uff1b\u866c\u9aef\u5ba2\u7f57\u81f4\u4e86 \u5f53\u4e16\u7b2c\u4e00\u82f1\u624d\uff0c\u5374\u53c8\u628a\u4ed6\u53cc\u624b\u9001\u7ed9\u4e86\u6211\uff0c\u4e16\u6c11\u4f55\u5fb7\uff0c\u4e07\u6c11\u6709\u5e78\uff0c\u5929\u4f51\u4e16\u6c11\uff0c\u5929\u4f51\u4e07\u6c11\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u7740\u8bdd\uff0c\u4eba\u5374\u5bf9\u7740\u5bab\u95e8\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5934\u7740\u77f3\u5730\uff0c\u78b0\u7136\u6709\u58f0\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u3001\u5982\u7389\u90fd\u8981\u4e0a\u524d\u6400\u6276\uff0c\u5374\u88ab\u8881\u5929\u7f61\u793a\u610f\u963b\u6b62\u3002 \u3000\u3000\u8881\u5929\u7f61\u4f4e\u58f0\u9053\uff1a\u201c\u8ba9\u4ed6\u758f\u89e3\u4e00\u4e0b\u5fc3\u4e2d\u7684\u6b22\u5a31\uff0c\u8fd9\u662f\u5b58\u5728\u5fc3\u4e2d\u6570\u5e74\u7684\u5fc3\u613f\uff0c\u4e3a\u4e86\u8fd9\u4e8b\u65e5\u601d \u591c\u8651\uff0c\u4e00\u76f4\u60f3\u4e0d\u51fa\u4e00\u4e2a\u6cd5\u5b50\u8ba9\u674e\u9756\u7b54\u5e94\uff0c\u8fd9\u51e0\u4e4e\u4f7f\u4ed6\u5bf9\u4e89\u9738\u5929\u4e0b\u7684\u4e8b\u4e5f\u6709\u4e9b\u7070\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5077\u671b\u4e86\u674e\u9756\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8881\u5148\u751f\uff0c\u62d9\u592b\u771f\u6709\u90a3\u4e48\u91cd\u8981\u5417\uff1f\u201d \u3000\u3000\u8881\u5929\u7f61\u7b11\u9053\uff1a\u201c\u4f60\u770b\u770b\u4e8c\u516c\u5b50\u90a3\u4efd\u9ad8\u5174\uff0c\u5fc3\u4e2d\u4e5f\u8be5\u660e\u767d\u4e86\u3002 \u3000\u3000\u674e\u9756\u662f\u4eca\u4e16\u7b2c\u4e00\u5e05\u624d\uff0c\u8c01\u80fd\u5f97\u674e\u9756\u4e3a\u5e05\uff0c\u5929\u4e0b\u5c31\u534a\u5165\u638c\u63e1\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6211\u7684\u4e08\u592b\u6709\u5982\u6b64\u7684\u80fd\u8010\uff0c\u6211\u5374\u628a\u4ed6\u903c\u5f97\u6d41\u6d6a\u5929\u6daf\uff0c\u51e0\u4e4e\u628a\u591c\u5954\u4e4b\u60c5\u4ed8\u4e4b\u4e1c\u6d41\u3002\u4eca\u540e\uff0c \u8981\u52a0\u500d\u6e29\u67d4\u5f85\u4ed6\uff0e\u4ee5\u8865\u5fc3\u4e2d\u4e4b\u759a\u3002\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u5fd6\u601d\uff0c\u51dd\u6ce8\u674e\u9756\u7684\u53cc\u76ee\u60c5\u7231\u6a2a\u6ea2\u3002 \u3000\u3000\u5982\u7389\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u4e2d\u6697\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u4eec\u592b\u5987\u60c5\u7231\u5982\u6b64\u6df1\u91cd\uff0c\u6211\u7adf\u5bf9\u674e\u7237\u6709\u7740\u8fd1\u4e24\u5e74\u7684 \u61a7\u61ac\uff0c\u603b\u60f3\u7740\u6709\u4e00\u5929\u80fd\u4f8d\u5949\u674e\u7237\u4e8e\u6795\u5e2d\u4e4b\u4e0a\uff0c\u8584\u547d\u601c\u537f\u7518\u4f5c\u59be\uff0c\u6709\u4f55\u4e0d\u53ef\uff0c\u53cd\u6b63\u5f20\u59d1\u5a18\u5fc3\u5206 \u4e8c\u7528\uff0c\u60c5\u7cfb\u4e24\u4eba\uff0c\u534a\u7cfb\u5927\u738b\uff0c\u534a\u674e\u9756\uff0c\u8fd9\u5bf9\u674e\u9756\u5927\u4e0d\u516c\u5e73\u4e86\u3002\u54ea\u77e5\u5f20\u51fa\u5c18\u7adf\u7136\u80fd\u628a\u60c5\u7231\u548c\u827a \u4e8b\u5206\u914d\u5f97\u5982\u6b64\u6e05\u695a\uff0c\u5b66\u5251\u5f52\u5b66\u5251\uff0c\u7231\u60c5\u5f52\u7231\u60c5\uff0c\u8fd9\u7b49\u5947\u5973\u5b50\uff0c\u6211\u5982\u7389\u662f\u96be\u53ca\u4e07\u4e00\u4e86\uff0c\u5e78\u5f97\u901a \u4e0a\u4e86\u674e\u4e8c\u516c\u5b50\uff0c\u4e00\u8154\u5984\u5ff5\u91ce\u706b\u81ea\u7136\u7184\u53bb\uff0c\u4e5f\u7b97\u5bf9\u5f97\u4f4f\u5f20\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u7a81\u95fb\u4e00\u58f0\u5927\u559d\uff1a\u201c\u9f20\u8f88\u6562\u5c14\uff01\u201d \u3000\u3000\u8033\u9645\u95f4\u54cd\u8d77\u4e86\u51e0\u58f0\u91d1\u94c1\u4ea4\u9e23\uff01 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u949f\u6728\u9b41\u957f\u53d1\u62ab\u5782\uff0c\u53cc\u624b\u9ad8\u4e3e\u94c1\u6756\uff0c\u8881\u5929\u7f61\u624b\u6301\u957f\u5251\uff0c\u6321\u5728\u674e\u4e16\u6c11\u8eab\u524d\u4e09 \u5c3a\u5904\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u4e24\u4eba\u4f3c\u5df2\u4ea4\u624b\u6570\u62db\u3002 \u3000\u3000\u949f\u6728\u9b41\u4f3c\u662f\u88ab\u8881\u5929\u7f61\u903c\u5728\u539f\u5730\uff0c\u65e0\u6cd5\u524d\u8fdb\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5fc3\u4e2d\u6697\u9053\uff1a \u3000\u3000\u201c\u674e\u9756\u5938\u8d5e\u4e8c\u516c\u5b50\u624b\u4e0b\u591a\u5947\u4eba\uff0c\u8fd9\u8881\u5929\u7f61\u5f53\u662f\u5176\u4e2d\u4e4b\u4e00\u4e86\uff0c\u53ea\u770b\u4ed6\u7528\u4e00\u628a\u8f7b\u7075\u7684\u5b9d\u5251\uff0c \u80fd\u628a\u949f\u6728\u9b41\u6c89\u91cd\u7684\u94c1\u6756\u5c01\u4f4f\uff0c\u96be\u8d8a\u96f7\u6c60\u4e00\u6b65\uff0c\u6050\u6015\u6211\u5c31\u96be\u6709\u8fd9\u4efd\u529f\u529b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u674e\u4e16\u6c11\u5df2\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u3000\u3000\u201c\u6728\u8001\uff0c\u5ff5\u5728\u4f60\u6168\u8d60\u5343\u5e74\u8001\u53c2\u7684\u5206\u4e0a\uff0c\u5f80\u4e8b\u4e00\u7b14\u52fe\u9500\uff0c\u8bf7\u8ba9\u5f00\u53bb\u8def\uff0c\u6211\u4eec\u8981\u4e0b\u5c71\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff01\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u5927\u738b\u4e4b\u547d\uff0c\u8981\u6211\u4e25\u5bc6\u770b\u5b88\u4f60\uff0c\u4e0d\u5f97\u79bb\u5f00\u534e\u5c71\u884c\u5bab\uff0c\u73b0\u82e5\u662f\u653e\u4f60 \u79bb\u5f00\u884c\u5bab\uff0c\u5982\u4f55\u5411\u5927\u738b\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u6728\u8001\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4f60\u81ea\u4fe1\u80fd\u591f\u62e6\u5f97\u4f4f\u5417\uff1f\u6628\u591c\u5927\u5e10\u8bba\u5175\uff0c\u5927\u738b\u4ee4\u8c15\u628a\u7559\u5b88\u5728\u534e\u9634 \u7684\u519b\u6743\u4ea4\u4ed8\u674e\u9756\uff0c\u7adf\u6709\u4eba\u6297\u547d\u4e0d\u9075\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff01\u201d\u949f\u6728\u9b41\u63a5\u53e3\u9053\uff1a\u201c\u4e00\u4e07\u5927\u519b\u4ecd\u7559\u534e\u9634\uff0c\u672a\u4ea4\u674e\u9756\u7edf\u7387\uff0c\u4e5f\u7ecf\u5927\u738b\u540c \u610f\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fd9\u9053\uff1a\u201c\u54ea\u662f\u5927\u54e5\u8d1f\u4e86\u4ed6\u7684\u4e49\u5f1f\u3001\u4e49\u59b9\uff0c\u4e0d\u662f\u6211\u548c\u674e\u9756\u8d1f\u4e86\u4ed6\uff0c\u6728\u8001\u5982\u679c\u5fc3\u5b58\u6b63 \u4e49\uff0c\u5c31\u8be5\u52a9\u6211\u4eec\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u201c\u949f\u67d0\u4eba\u5fc3\u4e2d\u53ea\u6709\u5927\u738b\uff0c\u5927\u738b\u65e2\u6709\u4ee4\u8c15\u4e0d\u51c6\u674e\u4e16\u6c11\u79bb\u5f00\u534e\u5c71\u884c\u5bab\uff0c\u949f\u67d0\u5c82\u6562\u8fdd\u6297\uff1f\u4f55\u51b5 \u79bb\u5f00\u4e86\u534e\u5c71\u884c\u5bab\uff0c\u5bf9\u4f60\u4eec\u672a\u5fc5\u6709\u5229\uff0c\u4f55\u4e0d\u7559\u5728\u884c\u5b98\uff0c\u7b49\u5927\u738b\u51ef\u65cb\u5f52\u6765\u3002 \u3000\u3000\u4f60\u4eec\u662f\u5927\u738b\u7684\u4e49\u5f1f\u3001\u4e49\u59b9\uff0c\u5982\u80af\u6c42\u4ed6\uff0c\u5927\u738b\u5b9a\u4f1a\u7f51\u5f00\u4e00\u9762\uff0c\u653e\u674e\u4e16\u6c11\u4e00\u6761\u53bb\u8def\uff0c\u81f3\u5c11\u53ef \u4ee5\u4fdd\u5168\u4ed6\u7684\u6027\u547d\u2026\u2026\u201d \u3000\u3000\u8c08\u5230\u6027\u547d\uff0c\u949f\u6728\u9b41\u7a81\u7136\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u674e\u4e16\u6c11\uff0c\u4f60\u670d\u4e86\u4e03\u7edd\u9053\u957f\u7684\u836f\u7269\uff0c\u5df2\u662f\u547d\u60ac\u65e6 \u5915\u4e4b\u95f4\uff0c\u600e\u4e48\u7a81\u7136\u597d\u4e86\uff0c\u662f\u88c5\u75c5\u6b3a\u9a97\u8001\u592b\uff1f\u201d \u3000\u3000\u674e\u4e16\u6c11\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6211\u672c\u6765\u65e0\u75c5\uff0c\u4e03\u7edd\u9053\u4eba\u4ee5\u836f\u6bd2\u6211\uff0c\u662f\u9634\u8c0b\u6697\u7b97\uff0c\u8feb\u6211\u670d\u6bd2\u66f4\u662f\u5f3a\u884c \u52a0\u5bb3\uff0c\u4e16\u6c11\u7b49\u7565\u65bd\u5c0f\u8ba1\uff0c\u9a97\u8fc7\u4e03\u7edd\u9053\u4eba\u548c\u4f60\u6728\u8001\uff0c\u6709\u4f55\u4e0d\u53ef\u5462\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u9a97\u53bb\u4e86\u4e00\u652f\u5343\u5e74\u8001\u53c2\uff01\u201d \u3000\u3000\u201c\u8d60\u53c2\u4e4b\u60c5\uff0c\u5168\u51fa\u6728\u8001\u81ea\u613f\u3002\u201d\u5982\u7389\u63a5\u9053\uff1a\u201c\u8fd9\u4efd\u60c5\u610f\uff0c\u4e8c\u516c\u5b50\u5df2\u94ed\u8bb0\u5728\u5fc3\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u9053\uff1a\u201c\u90a3\u5c31\u7559\u5728\u534e\u5c71\u884c\u5bab\uff0c\u7b49\u5019\u5927\u738b\u4eca\u8c15\u53d1\u843d\uff0c\u4f55\u51b5\u5927\u519b\u5df2\u8c03\u96c6\u4e00\u79c0\u5cf0\u4e0b\uff0c\u8bf8\u4f4d \u5982\u4f55\u80fd\u95ef\u8fc7\uff1f\u4e00\u4e07\u52c7\u58eb\u7684\u5305\u56f4\u5835\u51fb\uff0c\u5f3a\u5f13\u5343\u767e\u5f20\uff0c\u4e00\u65e6\u5f00\u5c04\uff0c\u7bad\u5982\u9aa4\u96e8\u98de\u8757\uff0c\u4f60\u674e\u9756\u3001\u5f20\u59d1 \u5a18\u4e5f\u8bb8\u80fd\u591f\u8eb2\u8fc7\uff0c\u4f46\u674e\u4e16\u6c11\u3001\u5982\u7389\u3001\u95fb\u9999\u7edd\u5bf9\u65e0\u6cd5\u8eb2\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u671d\u95fb\u9053\uff0c\u5915\u6b7b\u53ef\u77e3\uff01\u6211\u5982\u7389\u548c\u95fb\u9999\u867d\u662f\u5c0f\u5c0f\u5973\u5b50\uff0c\u4e5f\u4e0d\u628a\u751f\u6b7b\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u6728\u8001\u8bf7\u8ba9 \u8ba9\u8def\u5427\uff01\u4e8c\u516c\u5b50\u5fc3\u6000\u8d60\u53c2\u4e4b\u60c5\uff0c\u4e0d\u613f\u4e0b\u4ee4\u6740\u4f60\uff01\u201d \u3000\u3000\u201c\u6740\u6211\uff1f\u6740\u5f97\u4e86\u5417\uff1f\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\u80fd\u591f\u6740\u6211\u7684\u4eba\uff0c\u5c48\u6307\u53ef\u6570\u3002\u201d \u3000\u3000\u201c\u54bb\uff01\u201d \u3000\u3000\u4f46\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u4e00\u67c4\u957f\u5251\u523a\u5165\u949f\u6728\u9b41\u7684\u5de6\u80a9\u4e0a\uff0c\u76f4\u900f\u80a9\u80db\u3002 \u3000\u3000\u8033\u9645\u95f4\u54cd\u8d77\u4e86\u8881\u5929\u7f61\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u592a\u72c2\u4e86\uff0c\u5982\u975e\u4e8c\u516c\u5b50\u5fc3\u6000\u65e7\u60c5\uff0c\u8fd9\u4e00\u5251\u5c31\u5207\u4e0b \u4f60\u7684\u8111\u888b\u3002\u201d \u3000\u3000\u949f\u6728\u9b41\u7ec3\u7684\u4e59\u6728\u795e\u529f\uff0c\u529f\u529b\u5df2\u8fbe\u5200\u3001\u67aa\u4e0d\u5165\u7684\u5883\u754c\uff0c\u884c\u52a8\u4e5f\u5feb\u5982\u95ea\u7535\uff0c\u80fd\u4f24\u4ed6\u7684\u9ad8\u4eba\u7edd \u5bf9\u4e0d\u591a\u3002 \u3000\u3000\u4f46\u8881\u5929\u7f61\u5c31\u662f\u4e0d\u591a\u7684\u9ad8\u4eba\u4e4b\u4e00\u3002 \u3000\u3000\u949f\u6728\u9b41\u65e0\u6cd5\u77a7\u51fa\u90a3\u4e00\u5251\u662f\u5982\u4f55\u523a\u5165\u80a9\u80db\uff1f\u6574\u4e2a\u4eba\u7684\u9510\u6c14\u548c\u6597\u5fd7\uff0c\u4e5f\u88ab\u8fd9\u4e00\u5251\u523a\u57ae\u4e86\uff0c\u7f13 \u7f13\u653e\u4e0b\u4e86\u624b\u4e2d\u94c1\u6756\u3002 \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u4e8c\u516c\u5b50\u6b20\u4f60\u4e00\u652f\u5343\u5e74\u8001\u53c2\u7684\u4eba\u60c5\uff0c\u8881\u67d0\u4ee3\u4ed6\u8fd8\u4e86\uff0c\u8fd9\u4e00\u5251\u4e0d\u523a\u54bd\u5589\uff0c\u523a\u80a9 \u80db\uff0c\u5168\u770b\u5728\u90a3\u652f\u8001\u53c2\u7684\u5206\u4e0a\uff0c\u518d\u8981\u4e0d\u77e5\u8fdb\u9000\uff0c\u4f11\u602a\u5b9d\u5251\u53d6\u547d\u4e86\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u62ac\uff0c\u5b9d\u5251\u5165\u624b\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\uff1f\u201d\u949f\u6728\u9b41\u9053\uff1a\u201c\u662f\u6cd5\u672f\uff1f\u8fd8\u662f\u6b66\u529f\uff1f\u201d \u3000\u3000\u5251\u900f\u80a9\u80db\u4f3c\u662f\u6ca1\u6709\u4f7f\u949f\u6728\u9b41\u6709\u7740\u5f88\u5927\u7684\u75db\u82e6\uff0c\u4e5f\u4e0d\u89c1\u9c9c\u8840\u6d8c\u51fa\uff0c\u8fd9\u4eba\u7684\u4fee\u4e3a\u679c\u7136\u5df2\u5230\u4e86 \u5f62\u5982\u67af\u6728\u7684\u5883\u754c\u3002 \u3000\u3000\u8881\u5929\u7f61\u4ea6\u6709\u4e9b\u5fae\u5fae\u5403\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u5982\u679c\u5251\u7a7f\u5fc3\u80ba\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u4e0d\u652f\u5012\u5730\uff0c\u5c82\u4e0d\u6210\u4e86\u4e00 \u4e2a\u6740\u4e0d\u6b7b\u7684\u602a\u4eba\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u6012\u58f0\u559d\u9053\uff1a\u201c\u949f\u6728\u9b41\uff01\u8881\u5927\u5e08\u5df2\u5251\u4e0b\u7559\u60c5\uff0c\u4f60\u8fd8\u4e0d\u89c1\u673a\u800c\u9000\uff0c\u771f\u8981\u627e\u6b7b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u949f\u6728\u9b41\u5fc3\u5934\u4e00\u60ca\uff0c\u679c\u7136\u4e0d\u6562\u518d\u95ee\uff0c\u9000\u51fa\u5bab\u95e8\u800c\u53bb\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u674e\u9756\uff0c\u8981\u4e0d\u8981\u501f\u673a\u4f1a\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u674e\u9756\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e00\u52c7\u5339\u592b\u5c14\uff01\u6740\u4e86\u4ed6\uff0c\u5c06\u6fc0\u8d77\u4e0d\u5c11\u540c\u4ec7\u654c\u5ffe\u4e4b\u5fc3\uff0c\u7559\u4e0b\u4ed6\u4e00\u6761\u547d \u5427\uff01\u54b1\u4eec\u51b2\u4e0b\u5c71\u53bb\uff0c\u5929\u7f61\u5144\uff0c\u8bf7\u4fdd\u62a4\u4e8c\u516c\u5b50\uff01\u201d \u3000\u3000\u5f53\u5148\u5411\u5c71\u4e0b\u51b2\u53bb\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u98de\u8eab\u4e00\u8dc3\uff0c\u7d27\u8ffd\u5728\u674e\u9756\u8eab\u4fa7\u3002 \u3000\u3000\u8881\u5929\u7f61\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u8bf7\u65ad\u540e\u62a4\u4e3b\u3002\u201d \u3000\u3000\u5982\u7389\u3001\u95fb\u9999\u4e5f\u6b63\u611f\u89c9\u5230\u968f\u884c\u65e0\u5904\uff0c\u8881\u5929\u7f61\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7ed9\u5979\u4eec\u4e00\u4e2a\u4e3b\u8981\u7684\u4efb\u52a1\uff0c\u4e5f\u5b9a\u4e86\u4e00 \u4e2a\u968f\u884c\u7684\u8eab\u4efd\u4f4d\u7f6e\u3002 \u3000\u3000\u4e8c\u5973\u8ddf\u674e\u9756\u7ec3\u8fc7\u672f\u6cd5\u3002 \u3000\u3000\u674e\u9756\u4e5f\u5c3d\u5fc3\u5c3d\u529b\u5730\u6559\u5bfc\u8fc7\u5979\u4eec\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u60c5\u5fc3\u96be\u6d4b\uff0c\u674e\u9756\u4e5f\u65e0\u6cd5\u9884\u8ba1\u4f1a\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u5982\u7389\u3001\u95fb\u9999\u5c31\u662f\u4ed6\u7559\u5728\u866c\u9aef\u5ba2\u5927\u5e10\u4e2d \u4e3b\u529b\u4eba\u7269\u3002 \u3000\u3000\u56e0\u4e3a\u65f6\u95f4\u592a\u4ed3\u4fc3\uff0c\u4f20\u6388\u4e8c\u4eba\u7684\u6b66\u529f\u5df2\u7ecf\u6709\u4e9b\u6765\u4e0d\u53ca\u4e86\uff0c\u674e\u9756\u65b9\u51b3\u5b9a\u4e86\u4f20\u6388\u4e24\u4eba\u7684\u672f\u6cd5\u3002 \u3000\u3000\u672f\u6cd5\u662f\u4e00\u79cd\u5145\u6ee1\u7740\u8be1\u5f02\u7684\u90aa\u95e8\u6280\u827a\uff0c\u4f46\u5982\u679c\u6559\u5bfc\u7684\u4eba\u7cbe\u901a\u8bc0\u7a8d\uff0c\u90a3\u5c31\u6613\u5b66\u6613\u6210\uff0c\u800c\u6548\u7528 \u76f4\u63a5\u663e\u8457\u3002 \u3000\u3000\u5982\u7389\u3001\u95fb\u9999\u90fd\u6295\u6ce8\u4e86\u5168\u90e8\u5fc3\u795e\uff0c\u6697\u4e2d\u82e6\u7ec3\u3002 \u3000\u3000\u6240\u4ee5\u4e24\u4eba\u90fd\u6709\u4e86\u76f8\u5f53\u7684\u6210\u5c31\u3002 \u3000\u3000\u4e8c\u5973\u4e5f\u770b\u51fa\u6765\u8881\u5929\u7f61\u523a\u4e2d\u949f\u6728\u9b41\u90a3\u4e00\u5251\u4e0d\u662f\u4e00\u822c\u7684\u5251\u672f\u3001\u6b66\u529f\uff0c\u51fa\u5251\u4e4b\u5feb\u95ea\u7535\u96be\u53ca\uff0c\u6240 \u4ee5\u949f\u6728\u9b41\u95ea\u907f\u4e0d\u5f00\u3002 \u3000\u3000\u8fd9\u4f4d\u8881\u5929\u7f61\u4e5f\u662f\u7cbe\u901a\u672f\u6cd5\u7684\u9ad8\u4eba\u3002 \u3000\u3000\u949f\u6728\u9b41\u53d7\u4f24\u4e0d\u91cd\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\uff0c\u4f46\u662f\u4ed6\u88ab\u90a3\u4e00\u5251\u9547\u4f4f\u4e86\uff0c\u6597\u5fd7\u6d88\u9000\uff0c\u773c\u770b\u7740\u674e\u4e16\u6c11\u7b49\u4eba\u4e0b\u4e86\u4e00\u79c0 \u5cf0\u3002 \u3000\u3000\u4e00\u79c0\u5cf0\u4e0b\uff0c\u65e9\u5df2\u6446\u597d\u4e86\u5408\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u5de6\u9996\u662f\u516b\u5927\u5251\u5ba2\u3002 \u3000\u3000\u4ee5\u7834\u5929\u5251\u9ec4\u4e91\u4e3a\u9996\u3002 \u3000\u3000\u8fd9\u662f\u866c\u9aef\u5ba2\u4eb2\u81ea\u6388\u827a\u7684\u516b\u5927\u5f1f\u5b50\uff0c\u866c\u9aef\u5ba2\u7adf\u7136\u6ca1\u6709\u5e26\u4ed6\u4eec\u8fdb\u5165\u957f\u5b89\uff0c\u5374\u7559\u4e0b\u6765\u5bf9\u4ed8\u674e\u9756 \u592b\u5987\u3002 \u3000\u3000\u8fd9\u663e\u7136\u662f\u65e9\u6709\u9884\u8c0b\u4e86\u3002 \u3000\u3000\u674e\u9756\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u51fa\u5c18\uff0c\u8fd9\u516b\u5927\u5251\u5ba2\u662f\u4ef2\u575a\u5927\u54e5\u7684\u4eb2\u4f20\u5f1f\u5b50\uff0c\u4e5f\u662f\u4ef2\u575a\u5927\u54e5\u7684\u4eb2 \u4fe1\uff0c\u4f60\u4eec\u5e38\u5e38\u5728\u4e00\u8d77\u7ec3\u5251\u5427\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u521d\u5b66\u5251\u672f\u65f6\uff0c\u5e38\u548c\u4ed6\u4eec\u4e00\u8d77\u7ec3\u5251\uff0c\u4e09\u4e2a\u6708\u540e\uff0c\u6211\u4eec\u5c31\u5206\u5f00\u5b66\u827a\u4e86\uff0c\u6211\u8bd5\u8d70 \u6377\u5f84\uff0c\u5148\u7ec3\u9a6d\u5251\u6cd5\u3002\u201d \u3000\u3000\u674e\u9756\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5426\u90fd\u5df2\u5b66\u4f1a\u4e86\u9a6d\u5251\u672f\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u6ca1\u6709\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u636e\u5927\u54e5\u8bf4\uff0c\u7ec3\u4e60\u9a6d\u5251\u672f\u8981\u6709\u4e00\u80a1\u6e05\u4e91\u4e4b\u6c14\uff0c\u624d\u6613\u901f\u6210\u3002\u6211 \u975e\u5e38\u9002\u5408\u4e60\u7ec3\u6b64\u672f\uff0c\u53ea\u662f\u6211\u7684\u57fa\u672c\u529f\u529b\u592a\u5dee\uff0c\u6240\u4ee5\u4ed6\u4ee5\u73cd\u85cf\u7684\u4e00\u652f\u5343\u5e74\u8001\u53c2\u52a9\u6211\uff0c\u4e60\u6210\u4e86\u9a6d \u5251\u672f\u3002 \u3000\u3000\u8fd9\u516b\u5927\u5251\u5ba2\u4e2d\uff0c\u5982\u679c\u6709\u4eba\u7ec3\u6210\u4e86\u9a6d\u5251\u672f\uff0c\u81f3\u591a\u5e94\u662f\u4e00\u4e8c\u4eba\u800c\u5df2\uff0c\u7834\u5929\u5251\u9ec4\u4e91\u4e5f\u8be5\u662f\u6700\u6709 \u53ef\u80fd\u7684\u4eba\u3002\u201d \u3000\u3000\u674e\u9756\u76ee\u5149\u8f6c\u5411\u53f3\u8fb9\uff0c\u8f9b\u8001\u4e8c\u7387\u9886\u7684\u4e8c\u5341\u56db\u4e2a\u8eab\u7740\u6c34\u3001\u706b\u670d\u7684\u5927\u6c49\uff0c\u6bcf\u4eba\u6000\u91cc\u5404\u62b1\u4e00\u4e2a\u9ed1 \u7b52\u3002 \u3000\u3000\u9664\u4e86\u8fd9\u4e00\u6279\u5217\u5b88\u5728\u524d\u6392\u7684\u9ad8\u624b\u4e4b\u5916\uff0c\u6570\u5341\u4e08\u540e\u65d7\u5e1c\u98d8\u52a8\uff0c\u53ef\u89c1\u5927\u519b\u5217\u5e03\uff0c\u95ef\u51fa\u8fd9\u4e00\u5173\uff0c \u5373\u5c06\u9762\u4e34\u7740\u6570\u5343\u5927\u519b\u7684\u56f4\u653b\u3002 \u3000\u3000\u674e\u9756\u4f4e\u58f0\u9053\uff1a\u201c\u51fa\u5c18\uff0c\u4eca\u65e5\u4e4b\u6218\uff0c\u53ea\u6015\u5f88\u96be\u5584\u4e86\u3002\u90a3\u9ed1\u7b52\u4e4b\u4e2d\u85cf\u7269\u5fc5\u6781\u6076\u6bd2\u53ef\u6016\uff0c\u6050\u6015 \u662f\u4e03\u7edd\u9053\u957f\u4e13\u4e3a\u5bf9\u4ed8\u6211\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u201c\u4f1a\u662f\u4ec0\u4e48\u5462\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u6211\u4ee5\u5feb\u5251\u5288\u5f00\u4e00\u4e2a\u77a7\u77a7\uff01\u201d \u3000\u3000\u201c\u5343\u4e07\u4e0d\u53ef\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u7b52\u4e2d\u6bd2\u7269\u5982\u679c\u65e0\u6cd5\u5236\u670d\uff0c\u6211\u4eec\u56fa\u662f\u8eab\u53d7\u5176\u5bb3\uff0c\u4e8c\u516c\u5b50\u548c\u5982 \u7389\u3001\u95fb\u9999\u4e5f\u8981\u53d7\u5176\u837c\u6bd2\u4e86\uff0c\u6240\u4ee5\u8981\u8bbe\u6cd5\u4f7f\u5176\u80ce\u6b7b\u8179\u4e2d\uff0c\u4e0d\u8981\u8ba9\u5b83\u653e\u51fa\u4f24\u4eba\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u6bd2\u6c34\u98de\u9488\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u5374\u5df2\u80af\u5b9a\u4e86\u9ed1\u7b52\u5185\u88c5\u7684\u662f\u4f55\u7269\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4f60\u5982\u4ed4\u7ec6\u770b\uff0c\u53ef\u4ee5\u770b\u5230\u90a3\u7b52\u4e0a\u6709\u7740\u7ec6\u5c0f\u7684\u5b54\u6d1e\uff0c\u8fd9\u5c31\u8868\u793a\u7b52\u5b50\u4e0d\u662f\u5bc6\u5c01\u3002\u5982 \u679c\u662f\u6bd2\u6c34\u98de\u9488\uff0c\u5c31\u7528\u4e0d\u7740\u7559\u901a\u6c14\u5b54\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u679c\u7136\u5982\u6b64\uff0c\u4f60\u89c2\u5bdf\u5165\u5fae\uff0c\u4e3a\u59bb\u96be\u53ca\uff0c\u662f\u5426\u4e5f\u5df2\u60f3\u597d\u4e86\u7834\u89e3\u4e4b\u6cd5\uff1f\u201d \u3000\u3000\u201c\u53bb\u901a\u77e5\u8881\u524d\u8f88\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u8981\u4ed6\u6212\u5907\uff0c\u6211\u53bb\u6d4b\u8bd5\u4e00\u4e0b\u662f\u4f55\u6bd2\u7269\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u660e\u767d\u4e86\u3002\u674e\u9756\u771f\u6b63\u7684\u7528\u5fc3\u662f\u628a\u5979\u5b89\u7f6e\u5728\u8881\u5929\u7f61\u7684\u4fdd\u62a4\u4e4b\u4e0b\uff0c\u8981\u72ec\u529b\u6297\u62d2\u6bd2\u7269\u3002\u6df1 \u60c5\u53ef\u611f\uff0c\u4e0d\u518d\u575a\u6301\uff0c\u7f13\u6b65\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u674e\u9756\u53f3\u624b\u634f\u4e86\u5929\u96f7\u8bc0\uff0c\u5374\u672a\u62d4\u5251\uff0c\u7f13\u7f13\u4e3e\u6b65\u5411\u524d\u884c\u53bb\uff0c\u4f46\u5168\u8eab\u7684\u8863\u670d\u968f\u7740\u4ed6\u4e3e\u6b65\u7684\u884c\u52a8 \u7a81\u7136\u81a8\u80c0\u8d77\u6765\uff0c\u4f3c\u662f\u704c\u6ee1\u4e86\u6c14\u3002 \u3000\u3000\u8f9b\u8001\u4e8c\u51b7\u7b11\u4e00\u58f0\uff01\u53f3\u624b\u9ad8\u4e3e\uff0c\u6b63\u8981\u4e0b\u4ee4\u653e\u51fa\u6bd2\u7269\uff0c\u7a81\u95fb\u4e00\u7eed\u7bab\u97f3\u8885\u8885\uff0c\u4f20\u5165\u4e86\u8033\u9645\u3002 \u3000\u3000\u7bab\u58f0\u67d4\u7ec6\uff0c\u4f46\u5374\u6539\u53d8\u8f9b\u8001\u4e8c\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u8f6c\u8eab\uff0c\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u9886\u5934\u7684\u4e00\u8d70\uff0c\u5168\u5c40\u5927\u53d8\u3002 \u3000\u3000\u4e8c\u5341\u56db\u4e2a\u7a7f\u7740\u6c34\u706b\u8863\u7684\u4eba\u5168\u90fd\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u53d1\u89c9\u4e86\u674e\u9756\u786e\u6709\u7740\u81ea\u5df1\u65e0\u6cd5\u6bd4\u62df\u7684\u80fd\u8010\u3002 \u3000\u3000\u674e\u9756\u4e3e\u6b65\u800c\u884c\uff0c\u770b\u4e0a\u53bb\u65af\u6587\u7f13\u6162\uff0c\u4e8b\u5b9e\u4e0a\u4ed6\u53cc\u8db3\u672a\u843d\u5b9e\u5730\uff0c\u8e0f\u7a7a\u800c\u884c\uff0c\u6574\u4e2a\u4eba\u79bb\u5730\u4e09 \u5bf8\uff0c\u5728\u7a7a\u4e2d\u865a\u6d6e\u7740\u884c\u52a8\u3002 \u3000\u3000\u8f9b\u8001\u4e8c\u7387\u4eba\u9000\u8d70\uff0c\u4f46\u516b\u5927\u5251\u5ba2\u5374\u53ca\u65f6\u8865\u4e0a\u4e86\u7a7a\u9699\uff0c\u5206\u7ad9\u65b9\u4f4d\uff0c\u628a\u674e\u9756\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8981\u7528\u5251\u9635\u5408\u51fb\u674e\u9756\u4e86\uff0c\u60f3\u7684\u7f8e\u5440\uff01\u201d \u3000\u3000\u5feb\u6b65\u98de\u5954\u800c\u4e0a\u3002 \u3000\u3000\u5979\u53c8\u5927\u58f0\u559d\u9053\uff1a\u201c\u8bf8\u4f4d\u5fd8\u4e86\u8fd8\u6709\u6211\u5f20\u51fa\u5c18\u5728\u6b64\uff0c\u516b\u5251\u5408\u51fb\u7684\u6253\u7b97\u5c31\u5f88\u96be\u5982\u613f\u4e86\u3002\u201d \u3000\u3000\u201c\u5f20\u59d1\u5a18\uff01\u201d\u7834\u5929\u5251\u9ec4\u4e91\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e4b\u95f4\u4e5f\u8bb8\u96be\u514d\u4e00\u573a\u751f\u6b7b\u7684\u640f\u6218\uff0c\u4f46\u76f8\u714e \u4f55\u592a\u6025\u5462\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u9ec4\u4e91\uff01\u9000\u4e00\u6b65\u6d77\u9614\u5929\u7a7a\uff0c\u5e76\u975e\u662f\u4e0d\u80fd\u907f\u5f00\u8fd9\u4e00\u573a\u81ea\u76f8\u6b8b\u6740\uff0c\u4f46\u8981\u51fa\u81ea\u5185\u5fc3\u7684 \u5fcd\u8ba9\uff0c\u6765\u81ea\u80f8\u6000\u7684\u5fe0\u8bda\u3002\u201d \u3000\u3000\u9ec4\u4e91\u65e0\u5948\u5730\u9053\uff1a\u201c\u4e8c\u7237\uff0c\u6211\u4eec\u80fd\u4f5c\u4e3b\u5417\uff1f\u5e08\u7236\u4e00\u58f0\u4ee4\u4e0b\uff0c\u6211\u4eec\u54ea\u6709\u8fd8\u4ef7\u7684\u4f59\u5730\uff0c\u6240\u4ee5\u6211 \u4eec\u5e0c\u671b\u80fd\u5728\u53e6\u4e00\u4e2a\u6218\u573a\u9047\u4e0a\u5f3a\u654c\uff0c\u62fc\u4e00\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff0c\u5c31\u53ef\u514d\u53bb\u4e86\u548c\u4e8c\u7237\u592b\u5987\u4e00\u573a\u751f\u6b7b\u4e4b\u6218 \u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u6b64\u4e00\u5ff5\uff0c\u5c31\u7559\u4e86\u56de\u65cb\u7684\u4f59\u5730\u3002\u201d\u674e\u9756\u9053\uff1a\u201c\u9ec4\u4e91\u4e03\u7edd\u9053\u957f\u5473\u4e8e\u60c5\u52bf\uff0c\u5728\u4e0b\u53ef\u65ad\u8a00\u5927 \u738b\u7684\u957f\u5b89\u4e4b\u884c\u5c06\u65ad\u7fbd\u800c\u5f52\uff0c\u7559\u5b88\u5728\u534e\u9634\u7684\u519b\u9a6c\u5bf9\u5927\u738b\u5c31\u975e\u5e38\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u201c\u4e8c\u7237\uff01\u201d\u9ec4\u4e91\u9053\uff1a\u201c\u8fd9\u4f30\u7b97\u6b63\u786e\u5417\uff1f\u9f99\u5c06\u6c5f\u68ee\u3001\u864e\u5c06\u71d5\u53ef\u7edf\u5e05\u7684\u516d\u767e\u540d\u9f99\u864e\u52c7\u58eb\uff0c\u53ef \u90fd\u4ee5\u4e00\u5f53\u5341\u7684\u52c7\u58eb\uff0c\u5927\u738b\u4e4b\u52c7\u66f4\u662f\u4e07\u592b\u83ab\u654c\u3002 \u3000\u3000\u8fd8\u6709\u4e03\u7edd\u9053\u957f\u7684\u4e09\u767e\u540d\u52c7\u608d\u5251\u624b\uff0c\u5c31\u522b\u8bf4\u6570\u4e07\u9a81\u52c7\u5584\u6218\u7684\u7532\u5175\u4e86\uff0c\u957f\u5b89\u57ce\u4e2d\u53ea\u6709\u4e2a\u5b57\u6587 \u6210\u90fd\uff0c\u548c\u4ed6\u7edf\u5e05\u7684\u5bab\u9886\u7981\u536b\uff0c\u4e0d\u8fc7\u6570\u767e\u540d\u52c7\u58eb\uff0c\u5982\u4f55\u80fd\u548c\u5927\u738b\u6297\u4e89\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u9ec4\u4e91\uff0c\u6211\u4e0d\u61c2\u4e03\u7edd\u9053\u957f\u5728\u957f\u5b89\u57ce\u4e2d\u73a9\u7684\u4ec0\u4e48\u628a\u620f\uff1f \u3000\u3000\u7adf\u662f\u5982\u6b64\u7684\u8033\u76ee\u853d\u585e\uff0c\u5355\u662f\u8881\u7d2b\u70df\u8881\u56fd\u5e08\u4e00\u4eba\u4e4b\u80fd\uff0c\u5c31\u591f\u4f60\u4eec\u624b\u5fd9\u811a\u4e71\u4e86\uff0c\u4f55\u51b5\u8fd8\u6709\u4e2a \u9632\u5b88\u5bab\u5ef7\u7684\u8881\u5b9d\u513f\uff0c\u5927\u738b\u957f\u5b89\u4e4b\u884c\u52bf\u5fc5\u53d7\u632b\uff0c\u8fd9\u8f7b\u91cd\u5c31\u8981\u770b\u4e34\u673a\u7684\u4e3e\u63aa\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf7\u4e8c\u7237\u6307\u70b9\u9ec4\u4e91\u4e00\u6761\u660e\u8def\uff0c\u6211\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u7edf\u7387\u534e\u9634\u5927\u519b\u7684\u5175\u7b26\u4ee4\u724c\uff0c\u73b0\u5728\u4f55\u4eba\u624b\u4e2d\uff1f\u201d \u3000\u3000\u201c\u9ec4\u4e91\u53d7\u547d\u638c\u7406\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u597d\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u7f6e\u91cd\u5175\u4e8e\u534e\u9634\u57ce\u5916\uff0c\u5e03\u6210\u4e00\u5b57\u957f\u86c7\u9635\uff0c\u5341\u65e5\u5185\u5927\u738b\u5fc5\u7387\u6b8b\u5175\u8d25 \u5c06\u9000\u5165\u534e\u9634\uff0c\u5982\u6709\u8ffd\u5175\uff0c\u5c31\u5168\u529b\u62e6\u51fb\uff0c\u52a1\u6c42\u4e00\u6218\u9000\u654c\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u9ec4\u4e91\u9053\uff1a\u201c\u5927\u738b\u602a\u7f6a\u4e0b\u6765\uff0c\u5728\u4e0b\u5982\u4f55\u627f\u62c5\uff1f\u201d \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7acb\u4e0b\u4e00\u4ef6\u5927\u5927\u7684\u529f\u52b3\uff0c\u4f55\u7f6a\u4e4b\u6709\u3002 \u3000\u3000\u51fb\u9000\u8ffd\u5175\uff0c\u5fc5\u8981\u5145\u5206\u7684\u51c6\u5907\uff0c\u5f13\u7bad\u4e4b\u4e00\u2026\u2026\u201d \u3000\u3000\u9ec4\u4e91\u63a5\u53e3\u9053\uff1a\u201c\u5f13\u7bad\u5b58\u91cf\u4e0d\u8db3\u2026\u2026\u201d \u3000\u3000\u201c\u7cae\u8349\u5462\uff1f\u201d \u3000\u3000\u201c\u8db3\u591f\u4e09\u4e07\u4eba\u534a\u5e74\u4e4b\u7528\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u6210\u4e86\uff0c\u5e03\u7f6e\u53bb\u5427\uff01\u201d\u674e\u9756\u9053\uff1a\u201c\u5927\u519b\u4ea4\u6218\uff0c\u4e0d\u80fd\u9000\u5339\u592b\u4e4b\u52c7\uff0c\u53d6\u654c\u6570\u767e\u4eba\uff0c\u65e0\u6d4e \u4e8e\u4e8b\u3002\u4e00\u5b9a\u8981\u7a33\u4f4f\u9635\u89d2\uff0c\u632b\u5176\u950b\u9510\uff0c\u80fd\u51fb\u9000\u654c\u4eba\u4e09\u6ce2\u653b\u52bf\uff0c\u5927\u6982\u5c31\u53ef\u4ee5\u4fdd\u4f4f\u9635\u5730\u4e86\u3002\u6211\u4f20\u4f60 \u4e00\u5b57\u957f\u86c7\u9635\u7684\u514b\u654c\u4e4b\u5999\uff0c\u4f60\u8981\u7262\u7262\u8bb0\u5728\u5fc3\u4e2d\u3002\u201d \u3000\u3000\u674e\u9756\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u89e3\u8bf4\u9635\u52bf\u53d8\u5316\uff0c\u8c03\u5ea6\u514b\u654c\u7684\u5999\u7528\u3002 \u3000\u3000\u9ec4\u4e91\u542c\u5f97\u9891\u9891\u70b9\u5934\uff0c\u6697\u9053\uff1a\u540c\u6837\u4e00\u5ea7\u4e00\u5b57\u957f\u86c7\u9635\uff0c\u52a0\u4e0a\u4e8c\u7237\u7684\u8c03\u5ea6\u53d8\u5316\uff0c\u4e0d\u4f46\u80fd\u9996\u5c3e\u76f8 \u987e\uff0c\u800c\u4e14\u80fd\u9635\u4e2d\u6709\u9635\uff0c\u5175\u529b\u8fd0\u7528\u4e0a\uff0c\u5982\u589e\u52a0\u4e09\u500d\u4e4b\u4f17\uff0c\u7edf\u5175\u624d\u80fd\u4e8c\u7237\u5141\u5f53\u7b2c\u4e00\uff0c\u4e3a\u4ec0\u4e48\u5927\u738b \u7adf\u7136\u4e0d\u77e5\u73cd\u60dc\u91cd\u7528\uff1f \u3000\u3000\u4ed6\u8f6c\u5934\u53c8\u671b\u4e86\u5f20\u51fa\u5c18\u4e00\u773c\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u9178\uff0c\u5fd6\u9053\uff1a\u5e08\u7236\u554a\uff01\u2026\u2026\u5927\u738b\u554a\uff01\u7ea2\u989c\u7978\u6c34\uff0c\u4e3a \u4e86\u51fa\u5c18\u59d1\u5a18\u4e4b\u7f8e\uff0c\u4f60\u767d\u767d\u65ad\u9001\u4e86\u9010\u9e7f\u7687\u4f4d\u57fa\u4e1a\uff0c\u5212\u5f97\u6765\u5417\uff1f \u3000\u3000\u60f3\u5230\u4e86\u4f24\u5fc3\u4e4b\u5904\uff0c\u4e0d\u7981\u9eef\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u674e\u9756\u95ee\u9053\uff1a\u201c\u9ec4\u4e91\uff0c\u8fd8\u6709\u4ec0\u4e48\u672a\u89e3\u7684\u4e8b\u60c5\u5417\uff1f\u201d \u3000\u3000\u201c\u4e8c\u7237\u8fd8\u6709\u4e00\u4ef6\u2026\u2026\u201d \u3000\u3000\u9ec4\u4e91\u541e\u541e\u5410\u5410\uff0c\u7adf\u7136\u65e0\u6cd5\u51fa\u53e3\u3002 \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u4f60\u803d\u5fc3\u653e\u6211\u8d70\uff0c\u65e0\u6cd5\u5411\u5927\u738b\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u9ec4\u4e91\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff0c\u4e00\u4e07\u4eba\u9a6c\u65e0\u635f\u65e0\u4f24\uff0c\u7559\u4e0d\u4f4f\u4e8c\u7237\u592b\u5987\u3002\u5927\u738b\u5929\u5a01\u96be\u6d4b\uff0c\u4e00\u6c14 \u4e4b\u4e0b\uff0c\u4f1a\u4e0d\u5206\u7682\u767d\u7684\u628a\u6211\u6740\u4e86\u3002\u6740\u6211\u4e00\u4eba\uff0c\u4e5f\u5c31\u662f\u4e86\uff0c\u53ea\u6015\u5728\u6012\u706b\u51b2\u5fc3\u4e4b\u4e0b\uff0c\u628a\u6211\u4eec\u516b\u4e2a\u5e08 \u5144\u5f1f\u5168\u90fd\u7ed9\u5bb0\u4e86\u2026\u2026\u201d \u3000\u3000\u674e\u9756\u6697\u9053\uff1a\u674e\u4e16\u6c11\u5bf9\u866c\u9aef\u5ba2\u7684\u770b\u6cd5\u771f\u662f\u5165\u6728\u4e09\u5206\uff0c\u8fd9\u4e2a\u4eba\u6ca1\u6709\u6076\u884c\uff0c\u4e5f\u4e0d\u9634\u9669\uff0c\u5c31\u662f\u521a \u610e\u81ea\u7528\uff0c\u597d\u5f3a\u55dc\u6740\uff0c\u4ed6\u672c\u6765\u5df2\u662f\u8fc7\u4eba\u7684\u5f3a\u8005\uff0c\u4e00\u65e6\u6210\u4e3a\u5929\u4e0b\u81f3\u5c0a\uff0c\u4efb\u6027\u65bd\u4e3a\uff0c\u5c31\u975e\u5e38\u53ef\u6016\u53ef \u6015\u4e86\u3002 \u3000\u3000\u674e\u9756\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u2026\u2026\u6211\u4eec\u4e4b\u95f4\u975e\u5f97\u6709\u4e00\u573a\u6076\u6218\uff0c\u4f60\u4eec\u624d\u80fd\u6709\u6240\u4ea4 \u4ee3\u3002\u201d \u3000\u3000\u201c\u4e0d\uff01\u9ec4\u4e91\u65e0\u6b64\u5b58\u5fc3\uff0c\u53ea\u8981\u4e8c\u7237\u7ed9\u6211\u4eec\u4e00\u4e2a\u7406\u7531\uff0c\u6c42\u4e00\u4e2a\u501f\u53e3\u3002\u201d \u3000\u3000\u674e\u9756\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6c42\u5b57\u3002\u201d \u3000\u3000\u7a81\u7136\u7531\u8eab\u4e0a\u6478\u51fa\u4e00\u4e2a\u6d41\u661f\u706b\u70ae\uff0c\u6295\u5165\u9ad8\u7a7a\u3002 \u3000\u3000\u706b\u70ae\u5728\u6570\u5341\u4e08\u7684\u9ad8\u7a7a\u4e2d\u7206\u51fa\u4e00\u58f0\u5de8\u54cd\uff0c\u5316\u6210\u4e00\u7247\u6d41\u661f\u56db\u5c04\u3002 \u3000\u3000\u4e00\u523b\u5de5\u592b\u4e4b\u540e\uff0c\u4e09\u9897\u7c7b\u4f3c\u7684\u6d41\u661f\u706b\u70ae\u5206\u7531\u4e0d\u540c\u7684\u65b9\u4f4d\u5c04\u5165\u7a7a\u4e2d\uff0c\u76f8\u4f5c\u547c\u5e94\u3002 \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e09\u8def\u4eba\u9a6c\uff0c\u5728\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5206\u7531\u897f\u3001\u5357\u3001\u5317\u4e09\u4e2a\u65b9\u5411\u6740\u5165\u534e\u9634\u3002\u9ec4\u4e91\uff0c\u4f60 \u7684\u4e00\u4e07\u4eba\u9a6c\u7ecf\u6b64\u4e00\u756a\u51b2\u6740\uff0c\u5f88\u96be\u518d\u632f\u519b\u5a01\uff0c\u4e5f\u65e0\u6cd5\u63a5\u8fce\u5927\u738b\u4e86\uff0c\u8981\u4e0d\u8981\u8bd5\u8bd5\u770b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff01\u201d\u9ec4\u4e91\u9053\uff1a\u201c\u6709\u8fd9\u501f\u53e3\uff0c\u76f8\u4fe1\u53ef\u4ee5\u4f7f\u5927\u738b\u5fc3\u5e73\u6c14\u548c\u3002 \u3000\u3000\u4e8c\u7237\uff0c\u591a\u8c22\u4e86\uff01\u201d \u3000\u3000\u516b\u5251\u5ba2\u5206\u95ea\u5411\u4e24\u4fa7\uff0c\u6b20\u8eab\u76f8\u8ba9\u3002 \u3000\u3000\u674e\u9756\u5148\u8ba9\u8881\u5929\u7f61\u3001\u674e\u4e16\u6c11\u3001\u5982\u7389\u3001\u95fb\u9999\u3001\u5f20\u51fa\u5c18\u7b49\u901a\u8fc7\uff0c\u81ea\u5df1\u8d70\u5728\u6700\u540e\u3002 \u3000\u3000\u4ed6\u4f4e\u58f0\u9053\uff1a\u201c\u9ec4\u4e91\uff0c\u4e16\u5916\u6709\u6843\u6e90\uff0c\u6d77\u5916\u6709\u5730\u5929\uff0c\u5f00\u7586\u5f02\u57df\uff0c\u81ea\u6210\u4e00\u56fd\u4e4b\u4e3b\uff0c\u6709\u4f55\u4e0d \u53ef\uff1f\u201d \u3000\u3000\u201c\u9886\u6559\u4e86\uff0c\u4e8c\u7237\uff01\u201d\u9ec4\u4e91\u9053\uff1a\u201c\u90a3\u6728\u7b52\u4e4b\u4e2d\u6709\u6570\u767e\u53ea\u6bd2\u86db\uff0c\u4e13\u4e3a\u5bf9\u4ed8\u4e8c\u7237\u4e4b\u7528\u7684\u3002\u201d \u3000\u3000\u674e\u9756\u53c8\u95ee\u9053\uff1a\u201c\u8427\u58f0\u5462\uff1f\u6765\u81ea\u4f55\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u4e91\u9053\uff1a\u201c\u542c\u8bf4\u662f\u4e03\u7edd\u9053\u957f\u4e00\u4f4d\u5e08\u59b9\uff0c\u8eab\u5177\u5947\u672f\uff0c\u80fd\u5e03\u65bd\u5404\u79cd\u6bd2\u7269\u3002\u5979\u4e00\u76f4\u9690\u8eab\u5728\u6697 \u5904\uff0c\u4eca\u65e5\u7a81\u7136\u62db\u56de\u5c5e\u4e0b\uff0c\u653e\u4e86\u4e8c\u7237\u4e00\u9a6c\uff0c\u4e0d\u77e5\u662f\u4f55\u539f\u56e0\uff1f \u3000\u3000\u4e5f\u4f7f\u6211\u7b49\u6709\u673a\u4f1a\u4e00\u8046\u4e8c\u7237\u9ad8\u8bba\uff0c\u4f46\u6b64\u4e8b\u4e0d\u53ef\u6301\uff0c\u53d8\u5c5e\u610f\u5916\u3002 \u3000\u3000\u4e8c\u7237\uff01\u4ee5\u540e\u8fd8\u662f\u5c0f\u5fc3\u4e00\u70b9\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u627f\u6559\u4e86\uff01\u201d \u3000\u3000\u674e\u9756\u4e00\u62b1\u62f3\uff0c\u5feb\u6b65\u884c\u53bb\u3002 \u3000\u3000\u8ffd\u4e0a\u674e\u4e16\u6c11\uff0c\u7ed3\u4f34\u897f\u884c\u3002 \u3000\u3000\u5341\u91cc\u540e\uff0c\u53ea\u89c1\u5c18\u571f\u98de\u626c\uff0c\u94c1\u8e44\u96f7\u52a8\uff0c\u4e00\u5904\u4eba\u9a6c\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u4e2d\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u8fd8\u6709\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u674e\u9756\u5fae\u7b11\u9053\uff1a\u201c\u8fce\u63a5\u4e3b\u516c\u7684\u4eba\u9a6c\u5230\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u5c09\u8fdf\u606d\u5168\u8eab\u62ab\u6302\uff0c\u8de8\u4e0b\u4e4c\u96be\u9a6c\uff0c\u8eab\u4f69\u96cc\u96c4\u97ad\uff0c\u624b\u6267\u70b9\u94a2\u67aa\uff0c\u4e00\u9a91\u5f53\u5148\uff0c\u98de\u5954\u800c\u81f3\u3002 \u3000\u3000\u79e6\u53d4\u5b9d\u3001\u7a0b\u77e5\u8282\u5206\u7531\u5357\u3001\u5317\u4e24\u65b9\uff0c\u7eb5\u9a91\u800c\u6765\u3002 \u3000\u3000\u4e09\u54e8\u4eba\u9a6c\u4f1a\u5408\u4e00\u5904\uff0c\u62a4\u62e5\u7740\u674e\u4e16\u6c11\u4e0a\u4e86\u5065\u9a6c\u3002 \u3000\u3000\u674e\u9756\u624d\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e3b\u516c\u8bf7\u5e26\u9886\u8bf8\u4f4d\u5c06\u519b\u56de\u8f6c\u592a\u539f\uff0c\u4e09\u5343\u94c1\u7532\u5175\u4ea6\u5c06\u529f\u884c\u5706\u6ee1\uff0c\u6b64\u540e \u6218\u9f13\u50ac\u5f81\u4eba\uff0c\u8981\u6709\u4e00\u756a\u957f\u671f\u5f81\u6218\uff0c\u611a\u592b\u5987\u8bf7\u4e3b\u516c\u51c6\u5047\u5341\u65e5\uff0c\u5341\u65e5\u540e\u5fc5\u81f3\u592a\u539f\u5019\u547d\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u5fc3\u4e2d\u6025\u4e86\uff0c\u4e00\u4e0b\u5b50\u8dc3\u4e0b\u9a6c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u8bb8\u6211\u592a\u539f\u62dc\u5e05\uff0c\u96be\u9053\u53c8\u8981\u5ef6\u671f\u4e0d \u6210\uff1f\u201d \u3000\u3000\u201c\u674e\u9756\u4e0d\u6562\uff0c\u866c\u9aef\u5ba2\u53ef\u4ee5\u65e0\u60c5\uff0c\u9756\u592b\u5987\u4e0d\u80fd\u65e0\u4e49\uff0c\u6211\u548c\u51fa\u5c18\u884c\u8d9f\u957f\u5b89\uff0c\u5982\u6709\u673a\u7f18\uff0c\u4ea6\u4fbf \u7a0d\u5c3d\u5144\u5f1f\u60c5\u4e49\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u4fe1\u4eba\u4e5f\uff0c\u91d1\u5170\u4e4b\u60c5\u5c82\u6562\u62e6\u963b\uff1f \u3000\u3000\u5341\u65e5\u4e4b\u540e\uff0c\u6211\u5728\u592a\u539f\u8bbe\u5bb4\u5019\u9a7e\uff0c\u5144\u5f1f\u4e00\u65e5\u4e0d\u5230\uff0c\u5bb4\u5e2d\u4e00\u65e5\u4e0d\u5f00\uff0c\u5144\u5f1f\u5341\u65e5\u4e0d\u5230\uff0c\u4e16\u6c11\u5c31 \u4e0d\u8fdb\u996e\u98df\u3002\u201d \u3000\u3000\u674e\u9756\u62dc\u4f0f\u4e8e\u5730\uff0c\u9053\uff1a\u201c\u4e3b\u516c\u4fdd\u91cd\uff0c\u9756\u751f\u662f\u592a\u539f\u4eba\uff0c\u6b7b\u4e3a\u592a\u539f\u9b3c\uff0c\u6b64\u751f\u518d\u65e0\u4ed6\u6295\u4e4b\u5904\u3002\u201d \u3000\u3000\u201c\u6709\u6b64\u8a93\u8a00\uff0c\u4e16\u6c11\u5b89\u5fc3\u77e3\uff01\u8881\u5148\u751f\uff0c\u770b\u770b\u5dee\u9063\u54ea\u5458\u5927\u5c06\uff0c\u968f\u540c\u674e\u9756\u5165\u957f\u5b89\u3002\u201d \u3000\u3000\u674e\u9756\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u8bf8\u4f4d\u5c06\u519b\uff0c\u957f\u5b89\u4e4b\u884c\uff0c\u53ea\u5b9c\u6697\u4e2d\u6d3b\u52a8\uff0c\u987a\u4fbf\u4e86\u89e3\u4e00\u4e0b\u957f\u5b89\u57ce\u7684\u5b9e \u529b\u3002\u4e3b\u516c\u4e45\u79bb\u592a\u539f\uff0c\u8fd8\u8bf7\u65e9\u65e5\u5f52\u53bb\uff0c\u4ee5\u5b89\u519b\u5fc3\u3002\u95fb\u9999\u77e5\u9152\uff0c\u4ea6\u80fd\u8fa8\u6bd2\uff1b\u5982\u738b\u5584\u542c\uff0c\u53ef\u95fb\u5341\u4e08 \u5916\u5fae\u7269\u52a8\u9759\u3002\u8bf7\u7559\u8eab\u4fa7\uff0c\u4ee5\u4fdd\u5e73\u5b89\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8881\u7d2b\u70df\u975e\u540c\u5c0f\u53ef\uff0c\u5144\u5f1f\u591a\u52a0\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u626c\u97ad\u800c\u53bb\u3002 \u3000\u3000\u95fb\u9999\u3001\u5982\u7389\u540c\u65f6\u5bf9\u674e\u9756\u4e00\u793c\uff0c\u9053\uff1a\u201c\u62dc\u8f9e\u5e08\u7236\uff01\u201d \u3000\u3000\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u7d27\u8ffd\u674e\u4e16\u6c11\u8eab\u540e\u800c\u884c\u3002 \u3000\u3000\u79e6\u743c\u3001\u656c\u5fb7\u3001\u7a0b\u77e5\u8282\u5404\u5e26\u4e94\u767e\u7cbe\u9a91\uff0c\u62a4\u4ece\u968f\u884c\u3002 \u3000\u3000\u8881\u5929\u7f61\u76ee\u7779\u5f81\u5c18\u8fdc\u53bb\uff0c\u624d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u836f\u5e08\uff01\u653b\u5176\u4e0d\u5907\uff0c\u89c1\u597d\u5c31\u6536\u3002\u4e0d\u5b9c\u548c\u8881\u7d2b\u70df\u6b63 \u9762\u4e3a\u654c\uff0c\u4e00\u65e6\u51b2\u7a81\u5230\u64a4\u9000\u4e0d\u6613\u65f6\uff0c\u8bf7\u4ee3\u8001\u673d\u548c\u674e\u6df3\u98ce\u548c\u5730\u8ba2\u4e0b\u4f1a\u6664\u4e4b\u7ea6\uff0c\u4ee5\u5b9a\u672f\u6cd5\u4e4b\u5c0a\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u8881\u8001\uff0c\u674e\u9756\u544a\u8f9e\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5bf9\u4e24\u4eba\u7684\u4ea4\u8c08\uff0c\u4f3c\u61c2\u975e\u61c2\u3002 \u3000\u3000\u674e\u9756\u4e5f\u4e0d\u89e3\u91ca\uff0c\u62c9\u7740\u5f20\u51fa\u5c18\u4e0a\u9a6c\u75be\u884c\uff0c\u8f6c\u5954\u957f\u5b89\u3002 \u3000\u3000\u539f\u6765\u5c09\u8fdf\u606d\u65e9\u5df2\u4e3a\u4ed6\u4fe9\u9009\u4e86\u4e24\u5339\u957f\u7a0b\u5065\u9a6c\uff0c\u7559\u5728\u539f\u5730\u3002 \u3000\u3000\u957f\u5b89\u7684\u5bab\u5ef7\u4e2d\u7e41\u534e\u4f9d\u65e7\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5374\u4e00\u88ad\u9752\u8863\uff0c\u72ec\u5750\u5728\u4e00\u5ea7\u9ad8\u53f0\u4e0a\uff0c\u4ef0\u671b\u7740\u591c\u7a7a\u7684\u661f\u8c61\u51fa\u795e\u3002 \u3000\u3000\u8fd9\u662f\u4e13\u95e8\u4e3a\u5979\u642d\u5efa\u4e00\u5ea7\u9ad8\u53f0\uff0c\u4fbf\u4e8e\u5979\u89c2\u5bdf\u5929\u8c61\uff0c\u53d6\u540d\u4e3a\u201c\u89c2\u661f\u53f0\u201d\uff0c\u662f\u642d\u5efa\u5728\u5e7d\u9759\u7684\u82b1 \u56ed\u4e2d\u3002 \u3000\u3000\u5979\u5bf9\u968b\u7080\u5e1d\u4e00\u5473\u7684\u8089\u6b32\u8ffd\u6c42\uff0c\u5df2\u5f00\u59cb\u538c\u5026\u3002 \u3000\u3000\u5979\u56e0\u800c\u5e38\u5e38\u8eb2\u5f00\u7687\u4e0a\u7684\u7ea0\u7f20\u3002 \u3000\u3000\u66f4\u5f00\u59cb\u540e\u6094\u6295\u5165\u957f\u5b89\u5bab\u5ef7\u4e2d\u3002 \u3000\u3000\u4eca\u591c\u6708\u4e0a\u67f3\u68a2\u5934\u2014\u2014 \u3000\u3000\u8881\u7d2b\u70df\u5c31\u8eb2\u5728\u89c2\u661f\u53f0\u4e0a\uff0c\u4e00\u76f4\u9759\u9759\u5730\u5750\u7740\u3002\u591c\u98ce\u8f7b\u62c2\uff0c\u5fae\u5e26\u5bd2\u610f\uff0c\u4f46\u4e5f\u7ed9\u4e86\u5979\u4e00\u4efd\u5b81\u9759 \u548c\u5b89\u8be6\uff0c\u8ba9\u5979\u611f\u89c9\u5230\u63a5\u8fd1\u4e86\u81ea\u7136\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u5929\u8c61\u53ef\u6709\u53d8\u5316\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u4e00\u8eab\u767d\u8863\uff0c\u5982\u540c\u767d\u8863\u5929\u4f7f\u822c\uff0c\u8f7b\u5de7\u5730\u98de\u4e0a\u89c2\u661f\u53f0\u3002 \u3000\u3000\u8fd9\u5ea7\u9ad8\u53f0\u8db3\u8db3\u6709\u4e94\u4e08\u591a\u9ad8\u3002 \u3000\u3000\u662f\u82b1\u56ed\u4e2d\u6700\u9ad8\u7684\u5730\u65b9\u3002 \u3000\u3000\u8881\u5b9d\u513f\u7fe9\u7136\u98de\u6765\uff0c\u6709\u5982\u4e00\u53ea\u5927\u767d\u9e64\uff0c\u51cc\u7a7a\u800c\u964d\u3002 \u3000\u3000\u201c\u6211\u6ca1\u6709\u89c2\u5bdf\u5929\u8c61\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u597d\u70e6\uff0c\u597d\u70e6\uff01\u8fc7\u53bb\u751f\u6d3b\u5728\u9752\u5c71\u53e4\u6d1e\u4e2d\uff0c\u90a3\u91cc \u4eba\u8ff9\u7f55\u81f3\uff0c\u53ea\u6709\u5bc2\u5bde\uff0c\u5374\u65e0\u70e6\u607c\u3002\u73b0\u5728\uff0c\u98df\u4e86\u4eba\u95f4\u70df\u706b\uff0c\u5374\u53d8\u5f97\u70e6\u607c\u91cd\u91cd\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7ea2\u5c18\u554a\uff01\u540d\u5229\u7ea0\u7f20\uff0c\u60c5\u6b32\u523a\u5fc3\uff0c\u70e6\u607c\u5c31\u7d27\u968f\u800c\u5230\u4e86\u3002\u56fd\u5bb6\u5927\u4e8b\u3001\u79c1 \u4eba\u5fe7\u60a3\uff0c\u54ea\u4e00\u6837\u4e0d\u4f7f\u4eba\u4f24\u795e\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u79c1\u4eba\u5fe7\u60a3\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u53ef\u4e0d\u50cf\u4f60\u5b9d\u513f\u59b9\u59b9\u4e00\u76f4\u6302\u5ff5\u7740\u592a\u539f\u7684\u60c5\u90ce\u3002\u201d \u3000\u3000\u201c\u6302\u5ff5\u53c8\u5982\u4f55\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4e24\u5730\u76f8\u601d\u4ee5\u60c5\u7275\uff0c\u592a\u539f\u4e00\u522b\uff0c\u5c31\u4ece\u672a\u6664\u9762\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ee5\u4f60\u4e4b\u80fd\uff0c\u5343\u91cc\u8def\u9014\u4f55\u8db3\u9065\uff0e\u5f80\u8fd4\u4e0d\u8fc7\u4e00\u591c\u95f4\uff0c\u771f\u7684\u96be\u8010\u76f8\u601d\u82e6\uff0c\u4e3a\u4ec0\u4e48 \u4e0d\u8d70\u8d9f\u592a\u539f\u770b\u770b\u4ed6\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e00\u662f\u548c\u59d0\u59d0\u66fe\u6709\u7ea6\u5b9a\uff0c\u4e0d\u4fbf\u64c5\u81ea\u884c\u52a8\uff0c\u514d\u5f97\u59d0\u59d0\u7591\u5fc3\u3002\u56e0\u800c\u5b81\u5fcd\u76f8\u601d\u82e6\uff0c \u8db3\u4e0d\u51fa\u5bab\u95e8\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u751c\u7684\u4e00\u5f20\u5c0f\u5634\uff0c\u6211\u7b54\u5e94\u4f60\u7684\u4e8b\uff0c\u4e00\u5b9a\u505a\u5230\uff0c\u4e09\u653e\u674e\u4e16\u6c11\u7edd\u4e0d\u98df \u8a00\uff0c\u8bf4\u8bf4\u771f\u6b63\u7684\u539f\u56e0\u5427\uff01\u201d \u3000\u3000\u201c\u5509\u2026\u2026\u5931\u8db3\u6210\u6068\u4e8b\uff0c\u56de\u9996\u5df2\u767e\u5e74\u3002\u65e2\u7136\u7f18\u5df2\u5c3d\uff0c\u89c1\u9762\u4ea6\u65e0\u8a00\uff0c\u5f92\u589e\u60c6\u6005\uff0c\u4e0d\u89c1\u4e5f \u7f62\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u53ea\u8981\u59d0\u59d0\u4e0d\u8d1f\u6211\u6240\u6258\uff01 \u3000\u3000\u5c0f\u59b9\u4e3a\u4ed6\u5c3d\u4e86\u5fc3\u610f\uff0c\u4e5f\u5c31\u6b7b\u800c\u65e0\u61be\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9661\u7136\u60ca\u89c9\uff0c\u53cc\u76ee\u795e\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u5b9d\u513f\uff01\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5b57\u6587\u6210\u90fd\u664b\u8c12\u7687\u4e0a\uff0c\u62a5\u8bf4\u957f\u5b89\u57ce\u4e2d\u666f\u8c61\u5f02\u5e38\uff0c\u6050\u6709\u60ca\u53d8\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\u600e\u4e48\u8bf4\uff1f\u201d\u8881\u7d2b\u70df\u6025\u9053\uff1a\u201c\u53ef\u66fe\u9881\u5e03\u4e86\u5e94\u53d8\u7684\u8bcf\u65e8\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff01\u7687\u4e0a\u53cd\u95ee\u5b87\u6587\u6210\u90fd\u53ec\u52df\u7684\u52c7\u58eb\u5982\u4f55\uff1f\u7687\u5bab\u5185\u82d1\u4e2d\u662f\u5426\u4f1a\u53d7 \u60ca\u6270\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u771f\u662f\u4e2a\u81ea\u79c1\u7684\u7687\u4e0a\uff0c\u53ea\u6015\u60ca\u6270\u4e86\u4ed6\u7684\u540e\u5bab\u4f73\u4e3d\uff0c\u5374\u662f\u4e0d\u80af\u82b1\u5fc3 \u601d\u7167\u987e\u957f\u5b89\u57ce\u4e2d\u7684\u767e\u59d3\u4eec\u3002\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u5c3d\u7136\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5b57\u6587\u6210\u90fd\u4fdd\u8bc1\u4e0d\u4f1a\u8ba9\u53db\u5f92\u63a5\u8fd1\u7687\u5bab\uff0c\u7eb5\u6709\u4e09\u4e94\u4e2a\u4e0d\u77e5\u6b7b\u6d3b \u7684\u9006\u8d3c\u8fdb\u5165\u7687\u5bab\uff0c\u4e5f\u65e0\u6cd5\u9003\u8fc7\u56fd\u5e08\u624b\u638c\u5fc3\uff0c\u8fd8\u662f\u628a\u59d0\u59d0\u62d6\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u600e\u4e48\u8bf4\uff1f\u4ed6\u4e0d\u4fee\u653f\u4e8b\uff0c\u4e00\u5473\u7684\u8ffd\u6c42\u5973\u8272\u60c5\u6b32\uff0c\u6211\u771f\u7684\u6709\u70b9\u7070\u5fc3\u4e86\uff0c\u4e0d \u60f3\u518d\u7ba1\u4ed6\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u4f60\u592a\u4f4e\u4f30\u7687\u4e0a\u4e86\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7687\u4e0a\u662f\u5927\u667a\u82e5\u611a\u554a\uff01\u4ed6\u9881\u5e03\u4e86\u4e00\u9053\u4e25\u5389\u7684 \u8bcf\u65e8\uff0c\u8981\u671d\u4e2d\u767e\u5b98\u914d\u5408\u5b57\u6587\u6210\u90fd\uff0c\u5728\u4e00\u5e74\u5185\u8bad\u7ec3\u51fa\u5341\u4e07\u7cbe\u9510\u65b0\u519b\uff0c\u5175\u8425\u5341\u5904\uff0c\u5206\u8bbe\u5728\u957f\u5b89\u767e \u91cc\u4e4b\u5185\u3002 \u3000\u3000\u7687\u4e0a\u8981\u4eb2\u81ea\u5230\u7ec3\u5175\u7684\u5730\u65b9\u89c2\u5bdf\uff0c\u5e76\u4efb\u547d\u5b57\u6587\u6210\u90fd\u4e3a\u65b0\u519b\u5927\u5143\u5e05\uff0c\u4e0d\u8fc7\uff0c\u5b57\u6587\u6210\u90fd\u63a8\u8350\u59d0 \u59d0\u3002\u201d \u3000\u3000\u201c\u80e1\u95f9\u554a\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u600e\u4e48\u80fd\u4e3a\u7edf\u519b\u4e4b\u5e05\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u7687\u4e0a\u548c\u59d0\u59d0\u7684\u770b\u6cd5\u76f8\u540c\uff0c\u5f53\u573a\u5c31\u4ee3\u4f60\u63a8\u8f9e\u4e86\uff0c\u5b57\u6587\u6210\u90fd\u6c42 \u89c1\u59d0\u59d0\uff0c\u4e5f\u88ab\u7687\u4e0a\u5a49\u62d2\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u51e0\u65e5\u6211\u5fc3\u91cc\u70e6\u5f97\u5f88\uff0c\u6211\u5df2\u5411\u7687\u4e0a\u53e3\u8f9e\u4e86\u56fd\u5e08\u4e4b\u4f4d\u3002\u201d\u8881\u7d2b\u70df\u9053\u3002 \u3000\u3000\u201c\u7687\u4e0a\u5f88\u5389\u5bb3\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u80af\u7528\u70b9\u5fc3\u673a\uff0c\u4efb\u4f55\u4e8b\u90fd\u4f1a\u5904\u7f6e\u5f97\u6070\u5230\u597d\u5904\u3002\u4ed6\u8bf4 \u59d0\u59d0\u5fc3\u60c5\u4e0d\u597d\uff0c\u8fde\u4ed6\u90fd\u4e0d\u6562\u60ca\u6270\u4f60\uff0c\u4f55\u51b5\u522b\u4eba\uff0c\u8981\u5b57\u6587\u6210\u90fd\u7b49\u5019\u673a\u4f1a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u76f4\u6447\u7740\u5934\u9053\uff1a\u201c\u5509\u2026\u2026\u7687\u4e0a\u806a\u660e\u591a\u624d\uff0c\u5c31\u662f\u4e0d\u80af\u5e72\u6b63\u4e8b\u3002\u9187\u9152\u7f8e\u5973\u5bb3 \u4e86\u4ed6\uff0c\u5bb3\u4ed6\u5931\u53bb\u6cbb\u7406\u671d\u653f\u7684\u65f6\u673a\uff0c\u4e5f\u5bb3\u4ed6\u5931\u53bb\u5927\u968b\u671d\u7684\u6c5f\u5c71\u2026\u2026\u4ed6\u7684\u6027\u547d\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7d2b\u70df\u59d0\uff0c\u6ca1\u6709\u6551\u4e86\u5417\uff1f\u4ee5\u4f60\u4e4b\u80fd\uff0c\u96be\u9053\u4e5f\u65e0\u529b\u56de\u5929\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\u8be5\u4e0d\u8be5\u9006\u5929\u800c\u884c\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u4e5f\u77e5\u9053\u6c5f\u5c71\u5df2\u96be\u4fdd\uff0c\u7528\u5c3d\u6211\u4e00\u5207\u80fd\u529b\u4e5f \u53ea\u662f\u62d6\u5ef6\u4e00\u4e9b\u65f6\u95f4\uff0c\u5728\u8fd9\u79cd\u5175\u8352\u9a6c\u4e71\u4e2d\u751f\u6d3b\u7684\u4e07\u6c11\u82cd\u751f\uff0c\u662f\u4e0d\u662f\u66f4\u4e3a\u8f9b\u82e6\u5462\uff1f\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u5fc3\u5934\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u53cc\u76ee\u76ef\u6ce8\u5728\u8881\u7d2b\u70df\u7684\u8138\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u6211\u4eec\u8be5\u600e\u4e48\u529e\u5462\uff1f\u662f\u773c\u770b\u7740\u6c5f\u5c71\u6ca6\u5931\uff0c\u8fd8\u662f\u4e00\u8d70\u4e86 \u4e4b\uff1f\u201d \u3000\u3000\u8bf4\u7740\u8bdd\uff0c\u62ac\u5934\u671b\u7740\u5929\u4e0a\u7684\u661f\u8c61\u3002 \u3000\u3000\u8881\u5b9d\u513f\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5fcd\u770b\u7687\u4e0a\u6ca6\u5165\u8d25\u4ea1\uff0c\u4e5f\u4e0d\u4f1a\u4e00\u8d70\u4e86\u4e4b\u3002\u4f60\u8868\u9762\u51b7\u9177\uff0c\u5185\u5fc3 \u591a\u60c5\uff0c\u4f60\u660e\u77e5\u5929\u610f\u96be\u8fdd\uff0c\u5374\u53c8\u60f3\u4eba\u5b9a\u80dc\u5929\u3002 \u3000\u3000\u7d2b\u70df\u59d0\uff0c\u6211\u5f88\u660e\u767d\uff0c\u4f60\u5982\u4e0d\u5c3d\u529b\u4e00\u8bd5\uff0c\u5185\u5fc3\u4f1a\u6709\u4e00\u80a1\u4e0d\u5fff\u4e4b\u6c14\uff0c\u8fd9\u80a1\u6c14\u5e73\u4e0d\u4e0b\u6765\uff0c\u4e0d\u4f46 \u4f60\u4e2a\u4eba\u5f88\u96be\u5b89\u5b81\u4e0b\u6765\uff0c\u4e5f\u53ef\u80fd\u7559\u4e0b\u957f\u671f\u7684\u7978\u5bb3\uff0c\u9884\u671f\u5982\u6b64\uff0c\u5012\u4e0d\u5982\u73b0\u5728\u5c3d\u4f60\u4e4b\u80fd\uff0c\u4e3a\u7687\u4e0a\u4e00 \u5c3d\u5fc3\u529b\u5427\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u771f\u7684\u5e0c\u671b\u6211\u4fdd\u536b\u5927\u968b\u6c5f\u5c71\uff0c\u548c\u674e\u4e16\u6c11\u5bf9\u51b3\u4e8e\u6218\u9635\u4e4b\u4e0a\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u82e5\u529d\u4f60\u653e\u624b\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u542c\u4ece\uff0c\u5982\u679c\u53e3\u662f\u5fc3\u975e\uff0c\u90a3\u5c31\u4e0d\u5982\u5b9e\u8bdd\u5b9e\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8003\u8651\u4e09\u5929\u4e86\uff0c\u8fd8\u662f\u65e0\u6cd5\u4f5c\u51fa\u4e2a\u51b3\u5b9a\uff0c\u4e5f\u8bb8\u6211\u5e94\u8be5\u7167 \u4f60\u8bf4\u7684\u53bb\u505a\uff0c\u4e0d\u8ba9\u81ea\u5df1\u6709\u9057\u61be\uff0c\u4e5f\u4e0d\u8981\u5927\u8fc7\u901e\u5f3a\uff0c\u8feb\u903c\u81ea\u5df1\u62fc\u4e0a\u6027\u547d\u3002\u201d \u3000\u3000\u5979\u4ef0\u671b\u7740\u591c\u7a7a\u7684\u795e\u60c5\uff0c\u7a81\u7136\u4e00\u5446\u3002 \u3000\u3000\u7247\u523b\uff0c\u7a81\u53c8\u95ee\uff1a \u3000\u3000\u201c\u5b9d\u59b9\u59b9\uff0c\u6709\u4e8b\u60c5\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8c08\u5230\u4e86\u4f60\u7684\u70e6\u607c\uff0c\u5fd8\u8bb0\u6765\u8fd9\u91cc\u7684\u7528\u610f\uff0c\u7d2b\u70df\u59d0\uff0c\u5929\u8c61\u4e0a\u663e\u793a\u4e86 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5200\u5175\u4e4b\u52ab\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u59b9\u59b9\u60f3\u8003\u8003\u6211\uff0c\u4e0d\u77e5\u9053\u6211\u731c\u5bf9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5929\u8c61\u7684\u53d8\u5316\uff0c\u6709\u5982\u6b64\u7684\u7075\u9a8c\u6cd5\uff0c\u5b9e\u5728\u53eb\u4eba\u5bb3\u6015\u3002 \u3000\u3000\u7687\u4e0a\u8981\u6211\u8f6c\u544a\uff0c\u5b57\u6587\u6210\u90fd\u5448\u62a5\u4e86\u91cd\u8981\u519b\u60c5\uff0c\u4e00\u80a1\u6765\u52bf\u5947\u901f\u7684\u5175\u9a6c\u6b63\u5411\u957f\u5b89\u903c\u6765\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u4e0a\u8981\u6211\u62d2\u654c\u2026\u2026\u201d \u3000\u3000\u201c\u5012\u6ca1\u6709\u4f20\u8fd9\u5723\u8c15\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7687\u4e0a\u53ea\u8981\u6211\u901a\u62a5\u59d0\u59d0\u4e00\u58f0\uff0c\u957f\u5b89\u9a7b\u624e\u7684\u5175\u9a6c\u5df2\u5168\u5f52 \u5b57\u6587\u6210\u90fd\u8282\u5236\uff0c\u5b57\u6587\u5c06\u519b\u6b63\u5728\u9063\u5175\u8c03\u5c06\uff0c\u5b89\u6392\u62d2\u654c\u4e4b\u7b56\u3002\u201d \u3000\u3000\u51cc\u7d2b\u70df\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7981\u536b\u519b\u79ef\u5f31\u5df2\u6df1\uff0c\u5b57\u6587\u5c06\u519b\u4e00\u65f6\u4e4b\u95f4\u4e5f\u96be\u6709\u8f6c\u5f31\u4e3a\u5f3a\u7684\u80fd\u529b\uff0c \u8c03\u5927\u519b\u51fa\u57ce\u4f1a\u654c\uff0c\u6050\u975e\u826f\u7b56\uff0c\u8fd9\u4e00\u6218\u6050\u5c06\u4e00\u8d25\u6d82\u5730\u3002 \u3000\u3000\u5bab\u536b\u52c7\u58eb\u867d\u7136\u4e2a\u4e2a\u5584\u6218\uff0c\u53ea\u662f\u4eba\u6570\u592a\u5c11\u4e86\uff0c\u4fdd\u536b\u7687\u5bab\u5df2\u4e0d\u5145\u88d5\uff0c\u5b57\u6587\u5c06\u519b\u53ea\u6015\u4e0d\u6562\u8c03\u96c6 \u4ed6\u4eec\u51fa\u57ce\u8fce\u6545\u4e86\u3002\u201d \u3000\u3000\u201c\u5b57\u6587\u5c06\u519b\u6ca1\u6709\u548c\u5c0f\u59b9\u5546\u91cf\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4f46\u6211\u770b\u4ed6\u8c03\u96c6\u5175\u529b\u7684\u5b89\u6392\uff0c\u9887\u5177\u81ea\u77e5\u4e4b \u660e\uff0c\u4e00\u9762\u6574\u987f\u7981\u536b\u519b\uff0c\u91cd\u65b0\u7f16\u7ec4\uff0c\u5f3a\u5f31\u5206\u5f00\uff0c\u5206\u7f16\u6210\u4e8c\u5341\u56db\u8425\uff0c\u4f3c\u4e4e\u662f\u4ee5\u5b88\u536b\u957f\u5b89\u4e3a\u4e3b\uff0c\u4f7f \u5175\u529b\u6df1\u539a\uff0c\u76f8\u4e92\u63a5\u5e94\uff0c\u4e00\u9762\u9009\u62e9\u90e8\u5206\u5bab\u536b\u597d\u624b\uff0c\u7ec4\u6210\u5f3a\u5927\u7684\u963b\u51fb\u529b\u91cf\uff0c\u4fbf\u4e8e\u9a70\u63f4\u5404\u5904\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u5b57\u6587\u5c06\u519b\u552f\u4e00\u80fd\u5b89\u6392\u7684\u90e8\u7f72\u4e86\uff0c\u4e5f\u662f\u6700\u597d\u7684\u5b89\u6392\u3002\u5b9d\u513f\uff0c \u7687\u4e0a\u4ea4\u7ed9\u4f60\u7684\u5de5\u4f5c\u662f\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4fdd\u62a4\u5bab\u4e2d\u540e\u3001\u5983\u7684\u5b89\u5168\uff0c\u4ed6\u4e0d\u6562\u60ca\u6270\u59d0\u59d0\uff0c\u5374\u4e0b\u4e86\u4e00\u9053\u5723\u8c15\u7ed9 \u6211\u3002\u6211\u8bf4\u7687\u4e0a\u5389\u5bb3\uff0c\u4e5f\u662f\u6307\u6b64\u800c\u8a00\uff0c\u4e0d\u95ee\u6211\u662f\u5426\u4f1a\u6b66\u529f\u3001\u672f\u6cd5\uff0c\u4e5f\u4e0d\u5f81\u6c42\u6211\u662f\u5426\u540c\u610f\uff0c\u5c31\u628a \u5b98\u4e2d\u5fa1\u654c\u4e8b\u52a1\u5168\u90e8\u4ea4\u7ed9\u4e86\u6211\u3002 \u3000\u3000\u8fd9\u8bf4\u660e\u4e86\u4e00\u4ef6\u4e8b\uff0c\u4ed6\u65e9\u5df2\u77e5\u9053\u6211\u8eab\u6709\u672f\u6cd5\u7684\u4e8b\uff0c\u4e0d\u662f\u51ed\u7a7a\u731c\u60f3\u7684\uff0c\u800c\u662f\u65e9\u6709\u6240\u77e5\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8981\u83b2\u513f\u3001\u5de7\u513f\u5e2e\u52a9\u4f60\uff0c\u5979\u4eec\u7528\u529f\u52e4\u594b\uff0c\u5df2\u53ef\u4e0a\u9635\u5bf9\u654c\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7d2b\u70df\u59d0\uff01\u4f60\u96be\u9053\u771f\u7684\u60f3\u7f6e\u8eab\u4e8b\u5916\uff0c\u53ea\u6015\u662f\u4e0d\u592a\u53ef\u80fd\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u7b11\u9053\uff1a\u201c\u6709\u4f60\u5b9d\u513f\u59b9\u59b9\u51fa\u9762\uff0c\u5b9e\u5728\u4e5f\u7528\u4e0d\u7740\u6211\u63d2\u624b\u4e86\u3002\u6765\u7684\u4e0d\u4f1a\u662f\u674e\u4e16\u6c11 \u5427\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u5df2\u88ab\u59d0\u59d0\u6574\u6015\u4e86\uff0c\u54ea\u8fd8\u6562\u8f7b\u4e3e\u5984\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u8881\u7d2b\u70df\u63a5\u53e3\u9053\uff1a\u201c\u60f3\u4e0d\u51fa\u5bf9\u4ed8\u6211\u7684\u529e\u6cd5\u4e4b\u524d\uff0c\u4e0d\u4f1a\u8f7b\u4e3e\u5984\u52a8\u3002\u674e\u4e16\u6c11\u662f\u4e00\u7edf\u5929 \u4e0b\u7684\u4eba\u624d\uff0c\u5fcd\u8010\u7684\u529f\u592b\u4e5f\u975e\u5e38\u4eba\u80fd\u53ca\u3002 \u3000\u3000\u5b9d\u59b9\u59b9\uff0c\u5fcd\u5b57\u6000\u4e2d\u4e00\u628a\u5200\u554a\uff01\u6211\u5b9e\u5728\u4e0d\u613f\u548c\u4ed6\u4e3a\u654c\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u548c\u597d\u5427\uff01\u7537\u6000\u5927\u5fd7\uff0c\u5973\u6000\u5947\u624d\uff1b\u4f60\u4eec\u80fd\u5408\u4f5c\uff0c\u90a3\u53ef\u662f\u5929\u4e0b\u4e07\u6c11\u4e4b\u798f \u4e86\u3002\u201d \u3000\u3000\u201c\u60f3\u7684\u7f8e\u5440\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5929\u4e0b\u4e0d\u5982\u4eba\u610f\u4e8b\uff0c\u5341\u5360\u516b\u4e5d\u3002\u8fd1\u65e5\u6765\u6211\u5348\u591c\u68a6\u56de\uff0c\u7a81\u7136\u6709 \u6240\u4f53\u609f\uff0c\u6211\u4eec\u4fee\u4e60\u672f\u6cd5\u7684\u4eba\uff0c\u7ec8\u8001\u6df1\u5c71\uff0c\u4ee5\u672f\u81ea\u5a31\uff0c\u6216\u53ef\u5f97\u4fdd\u5929\u5e74\u3002\u51fa\u73b0\u5728\u7ea2\u5c18\u5341\u4e08\u4e2d\uff1b\u5df2 \u5c5e\u903e\u5206\uff0c\u518d\u4ee5\u672f\u6cd5\u4f24\u4eba\uff0c\u5b9e\u5df2\u89e6\u72af\u65e0\u6761\u3002 \u3000\u3000\u5982\u679c\u4f60\u548c\u674e\u4e16\u6c11\u5728\u4e16\u4fd7\u5e38\u4eba\u773c\u5149\u4e2d\uff0c\u6216\u6709\u91cd\u91cd\u56f0\u96be\u963b\u7edd\uff0c\u65e0\u6cd5\u4e00\u5706\u9e2f\u68a6\u3002\u4f46\u5728\u6211\u4eec\u770b \u6765\uff0c\u8fd9\u4e9b\u963b\u529b\u4f55\u582a\u4e00\u51fb\uff1f\u4f46\u4f60\u4eec\u5fc3\u9501\u91cd\u91cd\uff0c\u786c\u662f\u65e0\u6cd5\u5f00\u7ed3\uff0c\u8fd9\u5927\u6982\u5c31\u662f\u5929\u610f\u4e86\uff0c\u51a5\u51a5\u4e2d\u4e00\u80a1 \u4e3b\u88c1\u529b\u91cf\u3002 \u3000\u3000\u6211\u4eec\u6d1e\u6f88\u53d8\u5316\uff0c\u5374\u65e0\u6cd5\u638c\u63e1\uff0c\u660e\u77e5\u9053\u5b7d\u969c\u8bef\u4eba\uff0c\u5374\u53c8\u81ea\u6295\u5176\u4e2d\uff0c\u5509\u2026\u2026\u771f\u662f\u4ece\u4f55\u8bf4\u8d77 \u5440\uff01\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5c0f\u59b9\u82e5\u80fd\u591f\u91cd\u65b0\u6765\u8fc7\uff0c\u7edd\u4e0d\u4e60\u7ec3\u672f\u6cd5\u3002 \u3000\u3000\u672f\u6cd5\u4f24\u4eba\uff0c\u5374\u9501\u95ed\u81ea\u5fc3\uff0c\u660e\u77e5\u6545\u72af\u7684\u6295\u8eab\u5165\u9677\u9636\u4e4b\u4e2d\u3002\u7d2b\u70df\u59d0\uff0c\u82e6\u554a\uff01\u82e6\u554a\uff01 \u3000\u3000\u4f60\u60f3\u6e05\u51c0\u51e0\u65e5\uff0c\u601d\u89e3\u7834\u8327\u4e4b\u7b56\uff0c\u5c0f\u59b9\u4ea6\u613f\u671b\u4f60\u6709\u6210\uff0c\u501f\u59d0\u59d0\u4e4b\u529b\uff0c\u4ee5\u89e3\u5c0f\u59b9\u4e4b\u82e6\u96be\u3002\u53ea \u53ef\u60dc\uff0c\u4f60\u8fd9\u4e00\u70b9\u5fc3\u613f\u6050\u6015\u4e5f\u96be\u5b9e\u73b0\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u8427\u540e\u59d0\u59d0\u6765\u4e86\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u82e6\u7b11\u4e00\u4e0b\uff0c\u4e3e\u6b65\u5411\u524d\u8fce\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u5bab\u5a25\u5206\u6267\u7740\u56db\u76cf\u5bab\u706f\uff0c\u5bfc\u5f15\u7740\u8427\u7687\u540e\u8885\u8885\u5a77\u5a77\u5730\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59b9\u59b9\uff0c\u4e0d\u5f97\u4e86\u5566\uff01\u201d \u3000\u3000\u8427\u7687\u540e\u4eba\u8fd8\u5728\u4e24\u4e08\u5916\uff0c\u58f0\u97f3\u5df2\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5929\u4e0d\u8fc7\u521a\u521a\u9ed1\u5440\uff01\u8fd8\u672a\u5230\u8d77\u66f4\u65f6\u523b\uff0c\u5df2 \u7ecf\u6709\u523a\u5ba2\u627e\u4e0a\u95e8\u5566\uff01\u5bab\u5899\u5916\u9762\u5f00\u4e86\u6253\uff0c\u5200\u5251\u649e\u51fb\u7684\u58f0\u97f3\u90fd\u4f20\u5230\u7687\u5bab\u5185\u9662\u6765\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u662f\u4f55\u65b9\u7684\u53db\u519b\uff1f\u600e\u4e48\u5982\u6b64\u5feb\u901f\u5730\u6740\u8fdb\u4e86\u7687\u5bab\uff1f \u3000\u3000\u7687\u4e0a\u53ef\u66fe\u544a\u8bc9\u4e86\u7687\u540e\u59d0\u59d0\uff1f\u201d \u3000\u3000\u201c\u4e0d\u50cf\u5927\u519b\u53ae\u6740\uff01\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u53ef\u80fd\u53ea\u662f\u4e00\u4e9b\u523a\u5ba2\u6363\u4e71\uff0c\u4e0d\u8fc7\u523a\u5ba2\u624d\u53ef\u6015\u5440\uff01\u4f5c\u523a\u5ba2 \u7684\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u6740\u4eba\u624b\u4e0d\u4f1a\u8f6f\uff0c\u4e00\u51fb\u53d6\u547d\u554a\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u7687\u540e\u653e\u5fc3\uff0c\u7d2b\u70df\u5728\u6b64\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u60ca\u5413\u5230\u7687\u540e\u3002\u8d70\uff01\u56de\u662d\u9633\u5bab\u9662\u53bb\uff0c\u7687 \u540e\u9881\u4e0b\u4e00\u9053\u61ff\u65e8\uff0c\u8bf7\u5404\u5bab\u5983\u5b50\u3001\u592b\u4eba\u90fd\u5230\u662d\u9633\u5bab\u53bb\u907f\u96be\u3002\u4e0d\u613f\u6765\u7684\uff0c\u4e5f\u4e0d\u52c9\u5f3a\u3002\u81f3\u4e8e\u5ad4\u5973\u5bab \u5a25\uff0c\u8bf7\u96c6\u4e2d\u5728\u743c\u82b1\u9662\u4e2d\u2026\u2026\u201d \u3000\u3000\u8427\u7687\u540e\u5947\u9053\uff1a\u201c\u96c6\u4e2d\u5230\u743c\u82b1\u9662\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7531\u5b9d\u8d35\u5983\u4fdd\u62a4\u5979\u4eec\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6709\u6b64\u4e00\u756a\u60ca\u95f9\uff0c\u53ef\u80fd\u8fd8\u4f1a\u6709\u60ca\u6270\uff0c\u8ba9\u5b9d\u513f\u6311\u51fa\u4e00 \u4e9b\u9002\u4e8e\u7ec3\u6b66\u3001\u4e60\u6cd5\u7684\u5bab\u5973\uff0c\u4f20\u6388\u4e00\u4e9b\u672f\u6cd5\u6b66\u529f\uff0c\u4e5f\u53ef\u589e\u5f3a\u4e00\u4e9b\u5bab\u5ef7\u4e2d\u81ea\u536b\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u8427\u7687\u540e\u7591\u7136\u9053\uff1a\u201c\u7d2b\u70df\u59b9\u59b9\uff0c\u4f60\u662f\u8bf4\u5b9d\u8d35\u5983\u4e5f\u662f\u4e00\u4f4d\u7cbe\u901a\u672f\u6cd5\u3001\u6b66\u529f\u7684\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u5979\u53cc\u76ee\u76ef\u6ce8\u8881\u5b9d\u513f\u8138\u4e0a\uff0c\u795e\u8272\u95f4\u4f3c\u662f\u6709\u4e9b\u4e0d\u592a\u76f8\u4fe1\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7687\u540e\u548c\u7d2b\u70df\u59d0\u5546\u8ba8\u5bab\u4e2d\u5927\u4e8b\uff0c\u59be\u5983\u53bb\u63a2\u89c6\u4e00\u4e0b\u654c\u60c5\uff0c\u522b\u8ba9\u4ed6\u4eec \u60ca\u6270\u5230\u4e86\u7687\u540e\u3002\u201d \u3000\u3000\u957f\u8896\u6325\u52a8\uff0c\u6574\u4e2a\u4eba\u5c31\u5728\u957f\u8896\u5e26\u52a8\u4e0b\u98de\u8eab\u800c\u8d77\uff0c\u50cf\u4e00\u53ea\u5347\u8d77\u7684\u5927\u9e1f\u822c\u76f4\u4e0a\u591c\u7a7a\u3002 \u3000\u3000\u56db\u4e2a\u6267\u706f\u7684\u5bab\u5a25\u9ad8\u4e3e\u8d77\u5bab\u706f\uff0c\u4ef0\u7740\u8116\u5b50\u770b\u3002\u8427\u7687\u540e\u4e5f\u770b\u5f97\u5168\u795e\u8d2f\u6ce8\uff0c\u770b\u7740\u8881\u5b9d\u513f\u9ad8\u98de\u5728 \u591c\u7a7a\u4e2d\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59b9\u59b9\uff0c\u8fd9\u4e0d\u50cf\u6b66\u529f\u554a\uff01\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u50cf\u662f\u957f\u4e86\u7fc5\u8180\u7684\u4eba\u5728\u98de\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5b9d\u8d35\u5983\u65bd\u5c55\u7684\u4e0d\u662f\u6b66\u529f\uff0c\u662f\u672f\u6cd5\u3002\u5979\u7684\u6b66\u529f\u4e5f\u5f88\u9ad8\u660e\uff0c\u771f\u5200\u771f \u67aa\u7684\u6253\u8d77\u6765\uff0c\u5b57\u6587\u6210\u90fd\u5c06\u519b\u4e5f\u672a\u5fc5\u80fd\u591f\u80dc\u5979\u3002\u201d \u3000\u3000\u8427\u7687\u540e\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u4e2a\u5389\u5bb3\u4eba\u7269\uff0c\u6446\u5728\u7687\u5bab\u5185\u82d1\uff0c\u6446\u5728\u7687\u4e0a\u548c\u672c\u540e\u7684\u8eab\u8fb9\uff0c\u4e00\u65e6\u6709\u53d8\uff0c \u53ef\u662f\u5e94\u53d8\u4e0d\u53ca\uff0c\u7687\u4e0a\u548c\u672c\u540e\u5c31\u4efb\u5bb0\u5272\u4e86\u3002\u201d \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u98ce\u4e91\u6f5c\u9f99 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c \u5341 \u56de\u3000\u6bd2\u88ad\u5bab\u5a25 \u3000\u3000\u201c\u5b9d\u513f\u6e29\u987a\uff0c\u4e5f\u5f88\u7eaf\u826f\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5979\u5982\u6709\u52a0\u5bb3\u4f60\u548c\u7687\u4e0a\u4e4b\u5fc3\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u7b49\u5230\u4eca \u65e5\u4e86\u3002\u201d \u3000\u3000\u8427\u7687\u540e\u9053\uff1a\u201c\u7d2b\u70df\u54ea\uff01\u542c\u8bf4\u5979\u662f\u592a\u539f\u674e\u4e16\u6c11\u7684\u60c5\u4fa3\uff0c\u88ab\u9010\u5165\u957f\u5b89\u5bab\u5ef7\u4e4b\u4e2d\uff0c\u53ef\u662f\u522b\u6709\u7528 \u5fc3\u3002\u592a\u539f\u7559\u5b88\u4faf\u674e\u6e0a\u65e9\u6709\u4e0d\u4e8c\u4e4b\u5fc3\uff0c\u5c82\u53ef\u4e0d\u9632\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u9632\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u59be\u5a62\u548c\u5b87\u6587\u5c06\u519b\u5317\u4e0a\u5f81\u4f10\uff0c\u8881\u5b9d\u513f\u7559\u5728\u5bab\u5ef7\uff0c\u90a3\u65f6\u5979\u8981\u4e0b \u624b\uff0c\u7687\u5bab\u5185\u5916\u8c01\u662f\u5979\u7684\u654c\u624b\uff1f\u4f46\u5979\u6ca1\u6709\u52a8\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u5012\u4e5f\u662f\uff0c\u5979\u5982\u5b58\u5fc3\u52a0\u5bb3\u7687\u4e0a\u548c\u672c\u540e\uff0c\u4e0b\u624b\u7684\u673a\u4f1a\u751a\u591a\uff0c\u4f46\u5979\u4e3a\u4ec0\u4e48\u4e0d\u4e0b\u624b\u5462\uff1f\u4e3a\u4ec0 \u4e48\u5462\u2026\u2026\u201d \u3000\u3000\u8427\u7687\u540e\u6ee1\u8138\u6000\u7591\u5730\u671b\u7740\u8881\u7d2b\u70df\u3002 \u3000\u3000\u5e0c\u671b\u80fd\u5f97\u5230\u89e3\u7b54\u3002 \u3000\u3000\u201c\u5b9d\u513f\u662f\u674e\u4e16\u6c11\u7684\u60c5\u4fa3\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f46\u5979\u5374\u628a\u6e05\u6d01\u767d\u767d\u7684\u8eab\u5b50\u4ea4\u7ed9\u4e86\u7687\u4e0a\uff0c\u7687\u4e0a\u601c \u60dc\u5979\uff0c\u7231\u62a4\u5979\uff1b\u56fa\u7136\u662f\u5979\u59ff\u8272\u8fc7\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u770b\u770b\u6e29\u67d4\u7684\u5929\u6027\uff0c\u5145\u6ee1\u4e86\u5973\u6027\u5a07\u67d4\uff0c\u90a3\u624d\u662f\u7537 \u4eba\u65e0\u6cd5\u4e22\u820d\u7684\u91cd\u8981\u539f\u56e0\u3002\u201d \u3000\u3000\u8427\u7687\u540e\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8bf4\u7684\u5bf9\uff01\u6211\u4e5f\u7f3a\u5c11\u4e86\u8881\u5b9d\u513f\u90a3\u4efd\u67d4\u5a9a\uff0c\u65e0\u6cd5\u7559\u4f4f\u7687\u4e0a\u7684\u5fc3\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u7687\u540e\uff0c\u4f60\u98ce\u534e\u903c\u4eba\uff0c\u5c0f\u59b9\u548c\u5b9d\u513f\u90fd\u96be\u53ca\u5f97\u3002 \u3000\u3000\u7687\u4e0a\u6000\u62e5\u7f8e\u5973\u5343\u767e\uff0c\u59cb\u7ec8\u4e0d\u6562\u820d\u5f03\u7687\u540e\uff0c\u653e\u4e0b\u540e\u5bab\u4f73\u4e3d\uff0c\u7687\u540e\u53ea\u8981\u7ad9\u5728\u573a\u4e2d\uff0c\u987f\u6709\u4e00\u79cd \u9886\u8896\u7fa4\u4f26\u7684\u6c14\u52bf\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u8863\u88fe\u98d8\u98ce\u4e4b\u58f0\u98de\u9a70\u800c\u6765\uff01 \u3000\u3000\u8881\u7d2b\u70df\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u6562\u60ca\u540e\u9a7e\uff0c\u6740\u65e0\u8d66\uff01\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u53f3\u624b\u6325\u51fa\uff0c\u4e00\u7247\u7f61\u98ce\u98de\u7f69\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u56fd\u5e08\u624b\u4e0b\u7559\u60c5\uff0c\u5c5e\u4e0b\u8427\u96e8\uff0c\u5949\u547d\u5165\u5bab\u4fdd\u62a4\u7687\u540e\uff0c\u5374\u56e0\u904d\u5bfb\u4e0d\u7740\uff0c\u624d\u95ef\u5165\u4e86\u540e\u82b1\u56ed \u4e2d\u2026\u2026\u201d \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8427\u96e8\u624b\u6267\u957f\u5251\uff0c\u4eba\u5374\u8dea\u5728\u4e00\u7247\u82b1\u4e1b\u524d\u9762\u3002 \u3000\u3000\u4ed6\u8dea\u7740\u7684\u59ff\u52bf\u5f88\u5947\u602a\uff0c\u4f3c\u662f\u88ab\u4eba\u6309\u8dea\u5728\u5730\u4e0a\u4e00\u822c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u5566\uff01\u8427\u5c06\u519b\uff01\u201d \u3000\u3000\u53f3\u624b\u8f7b\u62c2\u3002 \u3000\u3000\u8427\u96e8\u987f\u590d\u81ea\u7531\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u6b20\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u6765\u7684\u9c81\u83bd\uff0c\u56fd\u5e08\u6055\u7f6a\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u519b\u60c5\u5927\u4e8b\uff1f\u5feb\u5411\u7687\u540e\u7980\u62a5\uff01\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u8427\u96e8\u8f6c\u5411\u8427\u7687\u540e\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u4e00\u6279\u5251\u624b\uff1f\u975e\u5e38\u52c7\u608d\uff0c\u5251\u672f\u7cbe\u5947\uff0c\u6740\u6cd5 \u9a81\u52c7\uff0c\u5b87\u6587\u603b\u7edf\u9886\u4ea6\u4eb2\u8eab\u4e34\u654c\uff0c\u53cc\u65b9\u5728\u5bab\u5916\u6fc0\u70c8\u7684\u6076\u6218\u4e2d\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\u5462\uff1f\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u662f\u4e0d\u662f\u592a\u539f\u6d3e\u6765\u7684\u523a\u5ba2\uff1f\u7687\u4e0a\u5462\uff1f\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u5b87\u6587\u603b\u7edf\u9886\u5df2\u9063\u6d3e\u9ad8\u624b\uff0c\u4e25\u5bc6\u7684\u4fdd\u62a4\u7687\u4e0a\u3002\u5c5e\u4e0b\u5949\u547d\u7387\u9886\u4e09\u5341\u4e8c\u4f4d\u5bab\u536b\u4fdd\u62a4 \u7687\u540e\u3002\u4ed6\u4eec\u5df2\u5728\u662d\u9633\u9662\u4e2d\u5e03\u9635\uff0c\u5c5e\u4e0b\u6765\u8bf7\u7687\u540e\u56de\u5bab\u2026\u2026\u201d \u3000\u3000\u8bed\u6c14\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u8fd9\uff1a\u201c\u548c\u56fd\u5e08\u5728\u4e00\u8d77\uff0c\u6bd4\u5c5e\u4e0b\u7684\u9632\u536b\u66f4\u4e3a\u5b89\u5168\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5c06\u519b\u770b\u4ed6\u4eec\u4e0d\u50cf\u592a\u539f\u6d3e\u6765\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u8427\u96e8\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u662f\uff01\u5c5e\u4e0b\u968f\u56fd\u5e08\u5317\u4e0a\uff0c\u4f1a\u89c1\u4e0d\u5c11\u7684\u592a\u539f\u9ad8\u624b\uff0c\u4e00\u4e2a\u4e5f\u672a\u89c1\u5230 \u88ad\u51fb\u5bab\u5ef7\u7684\u6740\u624b\uff0c\u4e0d\u4f46\u5168\u662f\u751f\u4eba\uff0c\u800c\u4e14\u5175\u5203\u7edf\u4e00\uff0c\u5168\u6570\u7528\u5251\u3002\u201d \u3000\u3000\u201c\u4eba\u6570\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5251\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u4eba\u6570\u6108\u6253\u6108\u591a\uff0c\u5251\u672f\u5f3a\u8fc7\u5bab\u536b\u52c7\u58eb\uff0c\u548c\u5c5e\u4e0b\u7565\u900a\u4e00\u7b79\uff0c\u4f46\u5374\u76f8\u53bb\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u201c\u7b97\u662f\u9ad8\u624b\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u591a\u7528\u5251\u7684\u9ad8\u624b\u51fa\u73b0\u5462\uff1f\u53ef\u77e5\u4ed6\u4eec\u6765\u8def\uff1f\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u552f\u4e00\u7684\u53ef\u7591\uff0c\u4ed6\u4eec\u5bf9\u957f\u5b89\u7684\u5f62\u52bf\u4f3c\u662f\u5341\u5206\u719f\u6089\uff0c\u6210\u7fa4\u7ed3\u961f\u7684\u7a81\u7136\u51fa \u73b0\uff0c\u4e5f\u80fd\u7a81\u7136\u9690\u6ca1\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u5fd6\u9053\uff1a\u8fd9\u6279\u4eba\u5206\u660e\u65e9\u5df2\u5728\u957f\u5b89\u6f5c\u4f0f\uff0c\u800c\u4e14\u65f6\u65e5\u4e0d\u77ed\uff0c\u4eca\u65e5\u7a81\u7136\u51fa \u73b0\uff0c\u5fc5\u7136\u6709\u9634\u8c0b\u914d\u5408\uff0c\u6b64\u4e8b\u4e0d\u53ef\u5c0f\u89c6\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u56de\u987e\u4e86\u7687\u540e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7687\u540e\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u8427\u7687\u540e\u9053\uff1a\u201c\u8fd9\u79cd\u821e\u5200\u5f04\u5251\u3001\u5bf9\u9762\u640f\u6740\u7684\u4e8b\uff0c\u6211\u54ea\u91cc\u77e5\u9053\u5462\uff1f \u3000\u3000\u4e00\u5207\u7531\u7d2b\u70df\u59b9\u59b9\u4f5c\u4e3b\u4e86\uff01\u201d \u3000\u3000\u201c\u4e8b\u60c5\u6709\u70b9\u5947\u602a\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u957f\u5b89\u57ce\u65e9\u65e5\u6f5c\u4f0f\u4e86\u5982\u6b64\u4f17\u591a\u7684\u9ad8\u624b\uff0c\u5374\u53c8\u80fd\u79d8\u800c\u4e0d \u6cc4\uff0c\u653e\u773c\u5929\u4e0b\u53ea\u6709\u866c\u9aef\u5ba2\u6709\u8fd9\u4e2a\u80fd\u529b\u3002\u8427\u5c06\u519b\uff0c\u6211\u4eec\u56de\u662d\u9633\u5bab\u53bb\uff0c\u6211\u8981\u6293\u4e00\u4e2a\u95ee\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\u6709\u52b3\u59d0\u59d0\u4e86\uff01\u201d\u8881\u5b9d\u513f\u83b2\u6b65\u59d7\u59d7\u7684\u8d70\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5f97\u5de7\u513f\u548c\u83b2\u513f\u4e4b\u52a9\uff0c\u5c0f\u59b9\u8fde\u4f24 \u4e86\u4ed6\u4eec\u5341\u4e8c\u4e2a\uff0c\u5f3a\u654c\u5df2\u9000\uff0c\u5c0f\u59b9\u6293\u4e86\u4e24\u4e2a\u6d3b\u7684\uff0c\u6b63\u5f85\u7687\u540e\u548c\u7d2b\u70df\u59d0\u59d0\u5ba1\u95ee\u5462\uff1f\u201d \u3000\u3000\u6765\u53bb\u4e0d\u8fc7\u4e00\u523b\u95f4\uff0c\u56f4\u653b\u5bab\u5ef7\u7684\u5f3a\u654c\uff0c\u7adf\u5df2\u88ab\u5979\u9000\u53bb\u3002 \u3000\u3000\u8427\u7687\u540e\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u8881\u5b9d\u513f\u4e4b\u80fd\uff0c\u53ea\u6015\u662f\u4e0d\u5728\u8881\u7d2b\u70df\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4ea4\u7687\u4e0a\u5ba1\u95ee\u5462\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5b87\u6587\u5c06\u519b\u4e5f\u6293\u4e86\u4e00\u4e2a\u6d3b\u7684\uff0c\u7687\u4e0a\u8981\u4eb2\u81ea\u5ba1\u95ee\uff0c\u59be\u5983\u6293\u7684\u4e24\u4e2a\uff0c\u5c31\u4ea4\u7ed9\u7687\u540e \u5ba1\u95ee\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4f60\u7acb\u4e86\u5927\u529f\uff0c\u6211\u4eec\u56de\u662d\u9633\u9662\u53bb\u3002\u201d \u3000\u3000\u8427\u96e8\u5446\u5446\u7684\u671b\u7740\u8881\u5b9d\u513f\u8885\u8885\u800c\u884c\u80cc\u5f71\uff0c\u5fc3\u4e2d\u6cdb\u51fa\u4e86\u91cd\u91cd\u7684\u7591\u95ee\uff1f \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u600e\u4e48\u4f1a\u8fd9\u6837\u5b50\u554a\uff01\u4e00\u4e2a\u4ed9\u5973\u822c\u7684\u8881\u7d2b\u70df\uff0c\u5df2\u8ba9\u4eba\u4e0d\u53ef\u601d\u8bae\u4e86\uff1b\u8fd9\u5a07\u5a07\u53ef \u4eba\u5c0f\u7f8e\u5973\uff0c\u7adf\u4e5f\u662f\u4e00\u4e2a\u672f\u6cd5\u7cbe\u5947\u3001\u6b66\u529f\u8fc7\u4eba\u7684\u9ad8\u624b\uff0c\u4e0a\u5929\u554a\uff01\u592a\u4e0d\u516c\u5e73\u4e86\uff0c\u4e3a\u4ec0\u4e48\u628a\u59ff\u8272\u548c \u624d\u827a\u90fd\u96c6\u4e8e\u8fd9\u4e9b\u52a8\u4eba\u7684\u7f8e\u5973\u8eab\u4e0a\uff1f \u3000\u3000\u662f\u7684\uff01\u5979\u4eec\u624d\u8272\u50b2\u4eba\u3002 \u3000\u3000\u4f46\u8c01\u53c8\u77e5\u9053\u5979\u4eec\u5185\u5fc3\u6df1\u85cf\u7684\u75db\u82e6\u5462\uff1f \u3000\u3000\u5455\u5fc3\u5456\u8840\u7684\u75bc\uff0c\u4e0d\u662f\u7528\u624d\u827a\u548c\u59ff\u8272\u6240\u80fd\u5f25\u8865\u7684\u3002 \u3000\u3000\u8427\u7687\u540e\u9ad8\u5750\u5728\u98de\u51e4\u6905\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u70df\u3001\u8881\u5b9d\u513f\u5206\u7ad9\u5730\u7684\u4e24\u4fa7\u3002 \u3000\u3000\u8427\u96e8\u8eab\u4f69\u957f\u5251\uff0c\u7ad9\u5728\u4e0b\u9996\u3002 \u3000\u3000\u9762\u524d\u8dea\u7740\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u8499\u9762\u4eba\u3002 \u3000\u3000\u201c\u53d6\u4e0b\u4ed6\u7684\u8499\u9762\u9ed1\u7eb1\uff01\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u6211\u8981\u770b\u770b\u4ed6\u7684\u771f\u6b63\u9762\u76ee\uff0c\u80c6\u6562\u5982\u6b64\u5927\u80c6\u7684\u64c5\u95ef\u7687 \u5bab\uff1f\u201d \u3000\u3000\u8427\u96e8\u4f9d\u8a00\u626f\u4e0b\u9ed1\u8863\u4eba\u7684\u8499\u9762\u9ed1\u7eb1\uff0c\u4f46\u4e00\u7528\u529b\uff0c\u8fde\u5934\u4e0a\u7684\u65b9\u5dfe\u4e5f\u626f\u4e86\u4e0b\u6765\u3002\u6562\u60c5\u8fd9\u662f\u4e00\u9876 \u7279\u5236\u5dfe\u5e3d\uff0c\u9762\u7eb1\u548c\u5e3d\u5b50\u8fde\u7ed3\u5728\u4e00\u8d77\u3002 \u3000\u3000\u539f\u5f62\u6bd5\u9732\u4e86\uff0c\u7adf\u7136\u662f\u4e00\u4e2a\u633d\u7740\u53d1\u9afb\u7684\u9053\u58eb\u3002 \u3000\u3000\u201c\u4f60\u662f\u51fa\u5bb6\u4eba\uff1f\u201d \u3000\u3000\u8427\u7687\u540e\u5403\u60ca\u5730\u95ee\u7740\u3002 \u3000\u3000\u8881\u7d2b\u70df\u3001\u8881\u5b9d\u513f\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u96be\u602a\u4ed6\u4eec\u80fd\u9690\u533f\u5f62\u8e2a\uff0c\u539f\u6765\u662f\u8eb2\u5728\u9053\u89c2\u91cc\u2026\u2026 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u9053\u89c2\u4e4b\u4e2d\u9690\u85cf\u4e86\u53db\u9006\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u52ff\u602a\u8fd1\u534a\u4e2a\u6708\u7684\u5927\u8086\u641c\u67e5\uff0c\u627e\u904d\u4e86\u957f\u5b89 \u57ce\uff0c\u5374\u627e\u4e0d\u5230\u4e00\u4e2a\u53db\u9006\u884c\u8e2a\u3002\u8bf4\uff01\u4f60\u4eec\u6709\u591a\u5c11\u4eba\uff1f\u85cf\u5728\u54ea\u4e00\u5ea7\u9053\u89c2\u4e2d\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u53cc\u81c2\u3001\u53cc\u817f\u90fd\u5df2\u53d7\u5236\uff0c\u65e0\u6cd5\u6323\u52a8\u3002 \u3000\u3000\u4f46\u53e3\u8fd8\u80fd\u8a00\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9053\u7237\u4eca\u65e5\u88ab\u64d2\uff0c\u53ea\u7b97\u8fd0\u6c14\u4e0d\u597d\uff0c\u60ed\u6127\u7684\u662f\u683d\u5728\u5973\u4eba\u624b \u4e2d\u3002\u4e0d\u8fc7\u2026\u2026\u6211\u5728\u88ab\u64d2\u4e4b\u65f6\uff0c\u5df2\u4f20\u51fa\u6d88\u606f\uff0c\u957f\u5b89\u5bab\u5ef7\u4e2d\u6709\u4e00\u4e2a\u7cbe\u901a\u672f\u6cd5\u7684\u5973\u4eba\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\u8bdd\uff0c\u53cc\u76ee\u8f6c\u76ef\u5728\u8881\u5b9d\u513f\u7684\u8138\u4e0a\uff0c\u6068\u6068\u5730\u9053\uff1a\u201c\u4e0b\u4e00\u6b21\uff0c\u4ed6\u4eec\u6709\u4e86\u51c6\u5907\uff0c\u4f60\u5c31\u4e0d\u4f1a\u8fd9 \u4e48\u5e78\u8fd0\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u54ac\u7259\uff0c\u54ac\u65ad\u4e86\u820c\u6839\u3002 \u3000\u3000\u9c9c\u8840\u6cc9\u6d8c\uff0c\u51b2\u53e3\u800c\u51fa\u3002 \u3000\u3000\u597d\u70c8\u6027\u7684\u9053\u4eba\uff01 \u3000\u3000\u8427\u7687\u540e\u54ea\u89c1\u8fc7\u8fd9\u79cd\u573a\u9762\uff0c\u8138\u8272\u90fd\u5413\u767d\u4e86\u3002 \u3000\u3000\u5979\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\uff0c\u5feb\uff01\u62d6\u4e0b\u53bb\uff01\u201d \u3000\u3000\u8427\u96e8\u4e00\u62db\u624b\uff0c\u5b88\u5728\u5bab\u95e8\u53e3\u7684\u6b66\u58eb\u6025\u6025\u8dd1\u5165\u4e24\u4eba\uff0c\u62ac\u8d77\u9053\u4eba\u5c38\u4f53\u800c\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u662f\u4e00\u8bad\u7ec3\u7cbe\u826f\u7684\u6b7b\u58eb\uff0c\u5b9d\u513f\uff01\u8fd8\u6709\u4e00\u4e2a\u5462\uff1f\u8fd9\u4e00\u6b21\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e0d\u80fd\u518d\u8ba9\u4ed6 \u54ac\u820c\u81ea\u5c3d\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u59b9\u4ee5\u672f\u6cd5\u4e0b\u4e86\u7981\u5236\uff0c\u4eba\u8fd8\u5728\u660f\u8ff7\u4e4b\u4e2d\uff0c\u5c0f\u59b9\u53bb\u63d0\u4ed6\u6765\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u53e3\u4e2d\u5e94\u7740\u8bdd\uff0c\u4eba\u5df2\u51fa\u4e86\u5bab\u95e8\u3002 \u3000\u3000\u201c\u7d2b\u70df\u59b9\u59b9\uff01\u201d\u8427\u7687\u540e\u9053\uff1a\u201c\u6211\u770b\u2026\u2026\u4f60\u6765\u5ba1\u95ee\uff0c\u8fd9\u79cd\u4e8b\u6211\u592a\u5916\u884c\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7687\u540e\u8bf7\u9000\u4e0b\u4f11\u606f\uff0c\u7d2b\u70df\u95ee\u51fa\u53e3\u4f9b\uff0c\u518d\u5411\u7687\u540e\u62a5\u544a\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5a07\u5c0f\u73b2\u73d1\uff0c\u989c\u82e5\u6625\u82b1\u3002 \u3000\u3000\u4f46\u5979\u624b\u4e2d\u63d0\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u7adf\u4f3c\u884c\u82e5\u65e0\u4e8b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4f3c\u662f\u5df2\u6655\u4e86\u8fc7\u53bb\uff0c\u4f46\u8881\u5b9d\u513f\u628a\u9ed1\u8863\u5927\u6c49\u6295\u63b7\u5728\u5730\u4e0a\u65f6\uff0c\u9ed1\u8863\u4eba\u5df2\u6e05\u9192\u8fc7\u6765\uff0c\u633a\u8eab \u800c\u8d77\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u7247\u5f69\u5149\u95ea\u4e86\u4e00\u95ea\uff0c\u90a3\u9ed1\u8863\u4eba\u521a\u521a\u7ad9\u8d77\u7684\u8eab\u5b50\u7a81\u7136\u5f00\u59cb\u98a4\u6296\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ece\u5b9e\u62db\u6765\u5427\uff01\u4e94\u9b3c\u641c\u9b42\u5927\u6cd5\uff0c\u4e0d\u662f\u4eba\u53ef\u80fd\u5fcd\u53d7\u7684\u3002\u201d \u3000\u3000\u53f3\u624b\u62c2\u52a8\uff0c\u4e00\u9635\u8f7b\u98ce\u8fc7\u5904\uff0c\u9ed1\u8863\u4eba\u98a4\u6296\u7684\u8eab\u5b50\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u4e00\u4f1a\u513f\u5de5\u592b\uff0c\u9ed1\u8863\u4eba\u5df2\u75db\u82e6\u7684\u6c57\u900f\u8863\u88e4\u3002 \u3000\u3000\u201c\u597d\u7cbe\u6e5b\u7684\u672f\u6cd5\uff01\u201d\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u4e00\u51fa\u624b\u5c31\u89e3\u4e86\u6211\u7684\u641c\u9b42\u5927\u6cd5\uff0c\u5b9d\u513f\u59b9\u59b9\uff0c\u4f60\u662f\u6df1\u85cf \u4e0d\u9732\u554a\uff01\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u8bef\u4f1a\u4e86\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u6b66\u529f\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u5982\u4f55\u80fd\u627f\u53d7\u641c\u9b42\u5927\u6cd5\u7684\u6298 \u78e8\uff1f\u4ed6\u5df2\u7ecf\u5c1d\u5230\u4e86\u75db\u82e6\u4e86\uff0c\u6211\u60f3\u4ed6\u4e5f\u5df2\u7ecf\u77e5\u9053\u5389\u5bb3\u4e86\u3002 \u3000\u3000\u73b0\u5728\u5927\u6982\u53ef\u4ee5\u62db\u4f9b\u4e86\uff0c\u82e5\u662f\u771f\u628a\u4ed6\u5f04\u6b7b\u4e86\uff0c\u5c0f\u59b9\u5c31\u5f88\u96be\u518d\u627e\u4e2a\u4eba\u6765\u3002\u56e0\u4e3a\u6240\u6709\u7684\u654c\u4eba\u5168 \u90fd\u64a4\u8d70\u4e86\u3002\u201d \u3000\u3000\u501f\u5ba1\u95ee\u72af\u4eba\uff0c\u4e24\u4eba\u4ea4\u624b\u4e00\u62db\uff0c\u5f7c\u6b64\u5df2\u5fc3\u4e2d\u6709\u6570\u3002 \u3000\u3000\u8881\u5b9d\u513f\u9000\u540e\u4e86\u4e24\u6b65\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u59d0\u59d0\u8bf7\u95ee\u6848\u60c5\uff0c\u6211\u60f3\u4ed6\u4eec\u8fd8\u6709\u63f4\u624b\uff0c\u8fd9\u4e00\u6b21\u53ea\u662f\u8bd5\u63a2\u4e00 \u4e0b\u5bab\u5ef7\u5b9e\u529b\uff0c\u4e0b\u6b21\u518d\u6765\uff0c\u53ef\u80fd\u5927\u4e3e\u6765\u72af\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\uff0c\u4e00\u638c\u6325\u53bb\u3002\u9ed1\u8863\u4eba\u8eab\u4e0a\u8863\u5e3d\u7247\u7247\u788e\u88c2\uff0c\u73b0\u51fa\u672c\u6765\u9762\u76ee\u3002\u679c\u7136\u4ecd\u662f\u9053\u88c5\u5251 \u624b\u3002 \u3000\u3000\u201c\u4f60\u4eec\u662f\u866c\u9aef\u5ba2\u7684\u5c5e\u4e0b\uff1f\u201d\u8881\u7d2b\u70df\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4e0d\u60f3\u518d\u5403\u641c\u9b42\u5927\u6cd5\u4e4b\u82e6\uff0c\u5c31\u636e\u5b9e\u56de\u7b54\u6211 \u7684\u95ee\u8bdd\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u771f\u7684\u6015\u4e86\u3002 \u3000\u3000\u4ed6\u6025\u6025\u5730\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u5927\u738b\u7684\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u201c\u5927\u738b\u5c31\u662f\u866c\u9aef\u5ba2\u4e86\uff1f\u201d\u8881\u5b9d\u513f\u63a5\u9053\uff1a\u201c\u8bf4\u4e0d\u901a\u554a\uff01\u4e3a\u4ec0\u4e48\u4f60\u4eec\u90fd\u7a7f\u9053\u88c5\uff0c\u866c\u9aef\u5ba2\u53ef\u4e0d \u662f\u51fa\u5bb6\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u4e03\u7edd\u9053\u957f\u7684\u90e8\u5c5e\u3002\u201d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f4f\u5728\u957f\u5b89\u57ce\u4e2d\u4e03\u516b\u5e74\u4e86\uff0c\u57ce\u4e2d\u7684\u9053\u89c2\u5185\u6700\u4e3a \u5b89\u5168\uff0c\u5b98\u5e9c\u4e2d\u4eba\u60f3\u4e0d\u5230\uff0c\u6240\u4ee5\u5e08\u7236\u5c31\u8ba9\u6211\u4eec\u7a7f\u4e0a\u9053\u88c5\uff0c\u5206\u5934\u5f80\u5165\u957f\u5b89\u5185\u5916\u516d\u5ea7\u9053\u89c2\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5e08\u7236\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u7edd\u9053\u957f\u3002\u201d\u9ed1\u8863\u4eba\u6253\u65ad\u4e86\u8881\u7d2b\u70df\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u5e08\u7236\u662f\u5927\u738b\u7684\u5c5e\u4e0b\uff0c\u4e5f\u662f\u4ed6\u7684\u597d\u670b \u53cb\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u201c\u5509\u5537\u201d\u4e00\u58f0\uff01 \u3000\u3000\u9ed1\u8863\u4eba\u7a81\u7136\u5012\u5728\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6012\u9053\uff1a\u201c\u5927\u80c6\u532a\u5f92\uff0c\u7adf\u6562\u6740\u4eba\u706d\u53e3\uff01\u201d \u3000\u3000\u4e3e\u6b65\u4e00\u8de8\uff0c\u4eba\u5df2\u98de\u5165\u9662\u4e2d\u3002 \u3000\u3000\u4f46\u89c1\u591c\u7a7a\u5bc2\u5bc2\uff0c\u54ea\u91cc\u8fd8\u6709\u4eba\u8e2a\uff1f \u3000\u3000\u8881\u7d2b\u70df\u8138\u8272\u51b7\u8083\uff0c\u770b\u7740\u7f13\u6b65\u5165\u5ba4\u7684\u8881\u5b9d\u513f\uff0c\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u8d70\u4e86\uff01\u5982\u679c\u4ed6\u9690\u8eab\u5728\u5341\u4e08\u4e4b\u5185\uff0c\u7edd\u5bf9\u9003\u4e0d\u8fc7\u5c0f\u59b9\u7684\u8033\u76ee\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5341\u4e08\u4e4b\u5916\uff0c \u5c31\u975e\u5c0f\u59b9\u529b\u80fd\u6240\u53ca\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u6b7b\u4e8e\u5200\u3001\u5251\u3001\u6697\u5668\u4e4b\u4e0b\uff0c\u770b\u6765\u866c\u9aef\u5ba2\u624b\u4e0b\u80fd\u4eba\u4e0d\u5c11\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u6b7b\u4e8e\u672f\u6cd5\u88ad\u6740\u4e4b\u4e0b\uff0c\u4efb\u4f55\u672f\u6cd5\u90fd\u65e0\u6cd5\u9003\u8fc7\u7d2b\u70df\u59d0\u59d0\u7684\u611f\u5e94\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6012\u6c14\u7a0d\u6d88\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5012\u8981\u67e5\u4e2a\u6e05\u695a\uff0c\u4ed6\u662f\u6b7b\u4e8e\u4ec0\u4e48\u6837\u4f24\u5bb3\u4e4b\u4e0b\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u5feb\u6b65\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u53cc\u76ee\u5c04\u51fa\u4e24\u9053\u51b7\u7535\u4e00\u822c\u7684\u5149\u8292\uff0c\u5149\u8292\u5728\u9ed1\u8863\u4eba\u8eab\u4e0a\u8f6c\u52a8\u4e00\u9635\uff0c\u7a81\u7136\u4f38\u51fa\u7ea4\u5de7\u7684\u7389\u6307\uff0c \u5728\u9ed1\u8863\u4eba\u8eab\u4e0a\u634f\u8d77\u4e00\u6761\u9ed1\u8272\u7684\u874e\u5b50\u3002 \u3000\u3000\u201c\u662f\u6bd2\u874e\u87ab\u6b7b\u7684\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u63a5\u7740\u9053\uff1a\u201c\u662f\u6df1\u5c71\u5927\u6cfd\u4e2d\u751f\u957f\u7684\u6bd2\u874e\uff0c\u4f46\u4f53\u79ef\u5c0f\u4e86\u4e00\u4e9b\uff0c\u600e\u4f1a\u5728\u957f\u5b89\u57ce\u91cc\u51fa\u73b0 \u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4eba\u517b\u7684\u7f57\uff01\u201d \u3000\u3000\u4e24\u4eba\u501f\u673a\u6597\u4e86\u4e00\u62db\u672f\u6cd5\uff0c\u73b0\u5728\u53c8\u5f00\u59cb\u6bd4\u5b66\u95ee\u89c1\u8bc6\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4f46\u5b83\u600e\u4f1a\u722c\u5230\u4e86\u8fd9\u9ed1\u8863\u4eba\u7684\u8eab\u4e0a\u5462\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e0d\u662f\u722c\u8fc7\u6765\u7684\uff0c\u65e9\u5c31\u85cf\u5728\u8fd9\u9ed1\u8863\u4eba\u7684\u8eab\u4e0a\uff0c\u4e5f\u8bb8\u4ed6\u77e5\u9053\uff0c\u4e5f\u8bb8\u4e0d\u77e5\u9053\u3002\u8fd9 \u6bd2\u874e\u662f\u7528\u836f\u7269\u63a7\u5236\uff0c\u5728\u4e00\u5b9a\u7684\u65f6\u9650\u5185\u87ab\u4eba\u6ce8\u6bd2\uff0c\u8fd8\u662f\u7528\u672f\u6cd5\u63a7\u5236\uff0c\u53ca\u65f6\u4f24\u4eba\uff1b\u5c0f\u59b9\u65e0\u6cd5\u77e5 \u6653\uff0c\u8981\u7814\u7a76\u4e00\u4e0b\u624d\u80fd\u7ed9\u7d2b\u70df\u59d0\u59d0\u56de\u62a5\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7684\u76ee\u5149\u4f55\u7b49\u7684\u51cc\u5389\u3001\u5c16\u9510\uff1f \u3000\u3000\u8881\u5b9d\u513f\u8ff0\u8bf4\u4e4b\u65f6\uff0c\u5979\u65e9\u5df2\u77a7\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002 \u3000\u3000\u5979\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u5bf9\u65b9\u6709\u4e00\u4e2a\u5584\u4f7f\u5404\u79cd\u6bd2\u7269\u7684\u9ad8\u4eba\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u63a7\u5236\u4e86\u8fd9\u4e9b\u9ed1\u8863\u5251\u624b\u7684\u751f\u6b7b\uff0c\u5f53\u7136\u4e5f\u53ef\u4ee5\u4f7f\u8fd9\u4e9b\u6bd2\u7269\u5bf9 \u4ed8\u654c\u4eba\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4e5f\u53ef\u4ee5\u4f7f\u8fd9\u4e9b\u6bd2\u7269\u6f5c\u5165\u5bab\u4e2d\uff0c\u4f24\u4e00\u4e9b\u738b\u5983\u3001\u592b\u4eba\uff0c\u9020\u6210\u5bab\u4e2d\u7684\u6050\u6016\u3001\u60ca \u614c\uff0c\u5c31\u5f71\u54cd\u7687\u4e0a\u8c03\u5ea6\u5175\u5c06\u7684\u5224\u65ad\u4e86\u3002\u201d \u3000\u3000\u201c\u4e5f\u5f71\u54cd\u5230\u5b87\u6587\u5c06\u519b\u7684\u62d2\u654c\u5e03\u7f72\u3002\u201d\u8881\u5b9d\u513f\u63a5\u7740\u9053\uff1a\u201c\u59d0\u59d0\u5982\u8981\u5f7b\u5e95\u5bf9\u4ed8\u654c\u4eba\uff0c\u5c31\u8be5\u4e3b \u52a8\u51fa\u51fb\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5bab\u4e2d\u7684\u5b89\u5371\u5462\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5c0f\u59b9\u8d1f\u8d23\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u8981\u591a\u5c11\u4eba\u624b\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u6211\u8981\u5b87\u6587\u5c06\u519b\u6d3e\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8427\u96e8\u5c06\u519b\u5df2\u5e26\u4e86\u4e09\u5341\u4e8c\u540d\u6b66\u58eb\u5728\u6b64\uff0c\u518d\u6d3e\u4e00\u4f4d\u526f\u7edf\u9886\uff0c\u5e26\u516d\u5341\u540d\u6b66\u58eb\u5165 \u5bab\u3002\u4ee5\u7687\u540e\u662d\u9633\u9662\u4e3a\u9632\u5b88\u91cd\u70b9\uff0c\u96c6\u4e2d\u5404\u9662\u592b\u4eba\u3001\u5983\u5b50\u3001\u624d\u4eba\u4e8e\u6b64\uff0c\u5e94\u8be5\u53ef\u4ee5\u5e94\u4ed8\u3002\u4e0d\u8fc7\u7687\u4e0a \u90a3\u8fb9\uff0c\u5c0f\u59b9\u5c31\u65e0\u6cd5\u7167\u987e\uff0c\u8981\u59d0\u59d0\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8bf7\u7687\u4e0a\u4e5f\u6765\u662d\u9633\u9662\u5427\uff01\u5404\u9662\u592b\u4eba\u548c\u624d\u4eba\u3001\u5983\u5b50\u5c3d\u96c6\u4e8e\u6b64\uff0c\u4ed6\u4e5f\u65e0\u6cd5\u72ec\u81ea\u900d \u9065\u3002\u6211\u4eec\u4e5f\u4e0d\u80fd\u5728\u7687\u5bab\u5185\u82d1\u4e2d\u548c\u654c\u4eba\u51b3\u6218\u3002 \u3000\u3000\u6211\u8981\u8bbe\u8ba1\u628a\u4ed6\u4eec\u963b\u6740\u5728\u5bab\u95e8\u4e4b\u5916\uff0c\u5b9d\u59b9\u59b9\u62c5\u8d1f\u8d77\u4fdd\u536b\u5185\u5bab\u7684\u8d23\u4efb\uff0c\u7d2b\u70df\u5c31\u53ef\u4ee5\u653e\u5fc3\u5bf9\u4ed8 \u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u968b\u7080\u5e1d\u5df2\u5e26\u7740\u5b87\u6587\u6210\u90fd\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u63a0\u8881\u7d2b\u70df\uff0c\u7a81\u7136\u653e\u5feb\u811a\u6b65\u5954\u4e86\u8fc7\u6765\u3002\u9053\uff1a\u201c\u6715\u627e\u4e86\u4f60\u4e09\u5929\u4e09\u591c\uff0c\u90fd\u6ca1\u627e\u5230\uff0c\u537f \u5bb6\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u60f3\u715e\u6715\u4e86\u3002\u201d \u3000\u3000\u5f20\u5f00\u53cc\u81c2\uff0c\u62b1\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u6709\u95ea\u907f\uff0c\u5374\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u5f3a\u654c\u5f53\u524d\uff0c\u5c31\u8981\u6740\u8fdb\u5bab\u4e86\uff0c\u4f60\u8fd8\u6709\u5fc3 \u60c5\u62b1\u6211\u5440\uff01\u201d \u3000\u3000\u201c\u5982\u975e\u8fd9\u573a\u6218\u4e71\uff0c\u6715\u54ea\u6709\u673a\u4f1a\u62b1\u4f4f\u537f\u5bb6\u2026\u2026\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u7d2b\u70df\u53ea\u4e0d\u8fc7\u4e09\u5929\u6ca1\u6709\u89c1\u5230\u5723\u9a7e\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u65e5\u4e0d\u89c1\u5982\u9694\u4e09\u79cb\uff0c\u4f55\u51b5\u4e09\u5929\u4e0b\u89c1\u537f\u5bb6\uff0c\u8054\u5df2\u627e\u904d\u4e86\u957f\u5b89\u5bab\u5ef7\u5404\u9662\uff0c\u5982\u975e\u8fd9\u573a\u6740\u4f10\u903c \u8fd1\u5bab\u5ef7\uff0c\u537f\u5bb6\u53ea\u6015\u8fd8\u4e0d\u4f1a\u73b0\u8eab\uff0c\u6715\u8981\u7d27\u7d27\u62b1\u7740\u4f60\uff0c\u4e0d\u518d\u653e\u4f60\u8d70\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u542c\u6765\u867d\u7136\u5c24\u5e26\u7a1a\u6c14\uff0c\u4f46\u5374\u5145\u6ee1\u75f4\u60c5\u3001\u8ff7\u604b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6709\u4e9b\u611f\u52a8\u4e86\uff0c\u65e9\u5df2\u51b0\u51b7\u7684\u5fc3\u5df2\u53c8\u5f00\u59cb\u6d3b\u52a8\u8d77\u6765\u3002 \u3000\u3000\u5979\u6697\u5fd6\u9053\uff1a\u4ed6\u867d\u7eb5\u60c5\u6d12\u8272\uff0c\u4e0d\u7406\u671d\u653f\uff0c\u4f46\u5bf9\u6211\u5374\u662f\u6781\u5c3d\u8fc1\u5c31\uff0c\u4e00\u7247\u75f4\u60c5\uff1b\u5982\u4eca\u5371\u673a\u5df2\u5230 \u4e86\u957f\u5b89\u4e4b\u4e2d\uff0c\u6211\u771f\u80fd\u5f03\u7f6e\u4e0d\u987e\u5417\uff1f \u3000\u3000\u5973\u4eba\u5fc3\uff0c\u6d77\u5e95\u9488\uff0c\u53ef\u771f\u662f\u65e0\u6cd5\u6349\u6478\u5440\uff01 \u3000\u3000\u8881\u7d2b\u70df\u8ba8\u538c\u968b\u7080\u5e1d\u7ea0\u7f20\u4e0d\u4f11\uff0c\u8ba9\u5979\u5fc3\u70e6\uff0c\u7528\u8d77\u9690\u8eab\u6cd5\u85cf\u4e86\u8d77\u6765\u3002\u5979\u672f\u6cd5\u9ad8\u5f3a\uff0c\u4e00\u65e6\u65bd \u51fa\uff0c\u5c31\u7b97\u968b\u7080\u5e1d\u4ece\u5979\u8eab\u4fa7\u7ecf\u8fc7\u4e5f\u770b\u4e0d\u5230\u5979\u3002 \u3000\u3000\u968b\u7080\u5e1d\u8dd1\u904d\u4e86\u4e09\u5bab\u5927\u9662\uff0c\u627e\u4e0d\u5230\u8881\u7d2b\u70df\u3002\u800c\u8881\u7d2b\u70df\u5374\u4e00\u4e2a\u4eba\u8eb2\u8d77\u6765\uff0c\u4ed4\u7ec6\u8003\u8651\u662f\u5426\u8be5\u56de \u5f52\u81ea\u7136\uff0c\u518d\u4e0a\u6df1\u5c71\uff1f \u3000\u3000\u8fd9\u7ea2\u5c18\u95f4\u7684\u7537\u6b22\u5973\u7231\uff0c\u9152\u8272\u5f81\u9010\uff0c\u5b9e\u5728\u8ba9\u5979\u538c\u70e6\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u5982\u679c\u665a\u6765\u4e00\u4e2a\u6708\uff0c\u8881\u7d2b\u70df\u53ef\u80fd\u91cd\u8fd4\u6df1\u5c71\u3002\u4eba\u4e8b\u65e2\u975e\uff0c\u6574\u4e2a\u5929\u4e0b\u5927\u4e8b\u5c31\u5b8c\u5168\u6539\u53d8 \u4e86\u3002 \u3000\u3000\u504f\u504f\u5728\u8881\u7d2b\u70df\u601d\u7d22\u79bb\u53bb\u65f6\uff0c\u866c\u9aef\u5ba2\u5e26\u7740\u4eba\u9a6c\u6740\u5165\u4e86\u957f\u5b89\u3002 \u3000\u3000\u8fd9\u5927\u6982\u5c31\u53eb\u673a\u7f18\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u6765\u7684\u673a\u7f18\u4e0d\u5de7\u3002 \u3000\u3000\u968b\u7080\u5e1d\u8bc9\u8bf4\u51fa\u601d\u5ff5\u4e4b\u82e6\uff0c\u8881\u7d2b\u70df\u53c8\u52a8\u4e86\u607b\u9690\u4e4b\u5fc3\uff0c\u6bd5\u7adf\u968b\u7080\u5e1d\u662f\u7b2c\u4e00\u4e2a\u548c\u5979\u4e0a\u5e8a\u7684\u7537 \u4eba\uff0c\u8feb\u5728\u7709\u776b\u7684\u4e8b\u5982\u4f55\u80fd\u5f03\u7f6e\u4e0d\u7ba1\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u8881\u7d2b\u70df\u51b3\u5fc3\u7559\u4e0b\u6765\uff0c\u4fdd\u536b\u5927\u968b\u671d\u7684\u57fa\u4e1a\uff0c\u51c6\u5907\u548c\u866c\u9aef\u5ba2\u4f5c\u4e00\u756a\u751f\u6b7b\u4e4b\u6597\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u773c\u770b\u7740\u8881\u7d2b\u70df\u7f13\u7f13\u63a8\u5f00\u4e86\u968b\u7080\u5e1d\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u5c5e\u4e0b\u8981\u8bf7\u793a\u56fd\u5e08\uff0c\u5982\u4f55\u8fce\u6218\u6765 \u654c\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u5bf9\uff01\u4f60\u4eec\u8981\u8ba8\u8bba\u5927\u4e8b\uff0c\u6210\u90fd\uff01\u8bf7\u5411\u56fd\u5e08\u8ba8\u6559\u5427\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u9000\u5f00\u4e09\u6b65\uff0c\u5750\u5728\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u51c6\u5907\u4e00\u4fa7\u65c1\u542c\u4e86\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u7687\u5e1d\u3002 \u3000\u3000\u5fc3\u77e5\u6b64\u523b\u4e0d\u5b9c\u591a\u8a00\uff0c\u5927\u96be\u5df2\u903c\u8fd1\u5728\u5bab\u5ef7\u4e4b\u5916\uff0c\u7687\u5e1d\u7684\u4e00\u53e5\u8bdd\u5c31\u6210\u4e3a\u6700\u540e\u7684\u88c1\u793a\uff0c\u6210\u8d25\u7684 \u7ed3\u679c\u5c31\u843d\u5728\u4ed6\u7684\u5934\u4e0a\u3002\u4f55\u4e0d\u8ba9\u8881\u7d2b\u70df\u53bb\u4f5c\u51b3\u5b9a\u5462\uff1f\u5979\u81ea\u5df1\u51b3\u5b9a\u7684\u4e8b\u81ea\u7136\u4f1a\u5168\u529b\u4ee5\u8d74\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u4fa7\u51b7\u773c\u65c1\u89c2\u7684\u8881\u5b9d\u513f\uff0c\u4e5f\u770b\u5f97\u4f69\u670d\u4e86\u3002 \u3000\u3000\u5979\u6697\u5fd6\u9053\uff1a\u7687\u4e0a\u5e73\u65e5\u4e0d\u7406\u56fd\u4e8b\uff0c\u4f46\u4ed6\u5728\u9762\u5bf9\u7d27\u6025\u4e8b\u4ef6\u7684\u5904\u7f6e\u65b9\u6cd5\uff0c\u5374\u662f\u4eba\u6240\u96be\u53ca\uff0c\u4e00\u4e0b \u5b50\u95ea\u51fa\u4e8b\u5916\uff0c\u628a\u62fc\u547d\u9000\u654c\u7684\u4e8b\u5168\u4ea4\u7ed9\u8881\u7d2b\u70df\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u7a81\u7136\u95f4\u6d8c\u51fa\u4e86\u4e0a\u767e\u7684\u5251\u624b\uff0c\u4e2a\u4e2a\u5251\u6cd5\u9ad8\u5f3a\uff0c\u51e0\u4e4e\u95f9\u5f97\u5c5e\u4e0b\u5e94\u63a5\u4e0d \u6687\uff0c\u6b64\u4e8b\u5f53\u975e\u5076\u7136\uff0c\u56fd\u5e08\u6709\u4ee5\u6559\u6211\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u4ed6\u4eec\u9690\u8eab\u5728\u957f\u5b89\u57ce\u5185\u3001\u57ce\u5916\u9053\u89c2\u4e4b\u4e2d\uff0c\u800c\u4e14\u4f4f\u4e86\u5f88\u4e45\uff0c\u6240\u4ee5\u719f\u6089\u957f\u5b89\u5f62 \u52bf\u3002\u4eba\u6709\u6570\u767e\u4e4b\u4f17\uff0c\u5355\u662f\u8fd9\u4e00\u80a1\u529b\u91cf\u5c31\u975e\u5e38\u53ef\u6015\uff0c\u975e\u5f97\u65e9\u4e9b\u6d88\u706d\u4e0d\u53ef\u3002\u4e0d\u8fc7\uff0c\u8fd8\u672a\u67e5\u51fa\u9886\u5bfc \u8fd9\u6279\u4eba\u7684\u9996\u8111\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u6279\u4eba\u4e4b\u5916\uff0c\u4e00\u8def\u7cbe\u5175\u5df2\u8fd1\u957f\u5b89\uff0c\u9a7b\u624e\u5728\u4e94\u5341\u91cc\u5185\uff0c\u671d\u53d1\u5915\u6b62\uff0c\u968f \u65f6\u53ef\u4ee5\u5411\u957f\u5b89\u5c55\u5f00\u653b\u51fb\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u95ee\u9053\uff1a\u201c\u5c06\u519b\u662f\u5426\u5df2\u6709\u62d2\u654c\u4e4b\u7b56\uff1f\u201d \u3000\u3000\u201c\u65f6\u95f4\u592a\u6025\u8feb\u4e86\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u56fd\u5e08\uff0c\u9632\u5b88\u957f\u5b89\u7684\u7981\u536b\u519b\u79ef\u5f31\u5df2\u4e45\uff0c\u96be\u518d\u6d3e\u4e0a\u7528 \u573a\u3002\u65b0\u519b\u867d\u6210\uff0c\u5c1a\u672a\u5b8c\u6210\u8bad\u7ec3\uff0c\u5bab\u536b\u52c7\u58eb\u53ea\u6709\u6570\u767e\u4eba\uff0c\u4eba\u6570\u592a\u5c11\uff0c\u52c9\u53ef\u4f5c\u4fdd\u536b\u7687\u5bab\u4e4b\u7528\u3002 \u3000\u3000\u5c5e\u4e0b\u4e3a\u6b64\uff0c\u7126\u8651\u4e0d\u5b89\uff0c\u53ea\u6709\u5411\u56fd\u5e08\u6c42\u6551\u4e86\uff0c\u5982\u80fd\u7ed9\u5c5e\u4e0b\u4e00\u5e74\u65f6\u95f4\uff0c\u65b0\u5175\u6210\u519b\u4e4b\u540e\uff0c\u957f\u5b89 \u57ce\u5175\u7cbe\u7cae\u8db3\uff0c\u5c31\u4e0d\u754f\u5f3a\u654c\u3001\u5927\u519b\u7684\u653b\u52bf\u4e86\u3002\u201d \u3000\u3000\u8c6a\u6c14\u5e72\u4e91\u7684\u5b87\u6587\u6210\u90fd\uff0c\u5df2\u88ab\u8fd9\u6b21\u7a81\u53d1\u7684\u4e8b\u4ef6\u60ca\u6270\u5f97\u4e71\u4e86\u65b9\u5bf8\u3002 \u3000\u3000\u4ed6\u4eb2\u773c\u76ee\u7779\u8fc7\u8881\u7d2b\u70df\u7684\u672f\u6cd5\uff0c\u662f\u552f\u4e00\u53ef\u4ee5\u4e0d\u501f\u5916\u529b\uff0c\u800c\u80fd\u591f\u5e2e\u52a9\u4ed6\u7684\u4eba\uff0c\u624d\u80af\u5411\u8881\u7d2b\u70df \u6c42\u52a9\u3002 \u3000\u3000\u5979\u662f\u56fd\u5e08\u7684\u8eab\u4efd\uff0c\u6b64\u4e8b\u5c31\u7b97\u4f20\u626c\u51fa\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u6709\u5931\u4ed6\u5927\u5c06\u519b\u7684\u8eab\u4efd\u3002 \u3000\u3000\u201c\u5c06\u519b\uff01\u201d\u8881\u7d2b\u70df\u7684\u76ee\u5149\u626b\u63a0\u8fc7\u968b\u7080\u5e1d\uff0c\u9053\uff1a\u201c\u8981\u6211\u4e3a\u4f60\u62d2\u6321\u654c\u5175\u5462\uff1f\u8fd8\u662f\u8981\u5e2e\u4f60\u4fdd\u536b \u5bab\u5ef7\uff1f\u201d \u3000\u3000\u8bed\u6c14\u4e2d\u6d41\u9732\u51fa\u80f8\u4e2d\u7684\u5757\u5792\uff0c\u800c\u4e14\u8c08\u8bdd\u7684\u5bf9\u8c61\u76f4\u6307\u5b87\u6587\u6210\u90fd\uff0c\u5bf9\u5927\u968b\u671d\u7684\u57fa\u4e1a\u548c\u7687\u4e0a\u7684\u5c0a \u4e25\u90fd\u672a\u6d89\u53ca\u3001\u5173\u6ce8\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8bf7\u7687\u4e0a\u5723\u88c1\u3002\u201d \u3000\u3000\u201c\u6715\u4e0d\u89e3\u7528\u5175\u6253\u4ed7\u7684\u4e8b\uff0c\u4f60\u548c\u56fd\u5e08\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u201c\u6210\u90fd\u60f3\u8bf7\u56fd\u5e08\u62d2\u6321\u57ce\u5916\u7684\u5927\u519b\uff0c\u4e0d\u8fc7\u5374\u65e0\u6cd5\u8c03\u52a8\u5927\u519b\u968f\u884c\uff0c\u4ee5\u52a9\u56fd\u5e08\uff1b\u4f46\u6210\u90fd\u4e2a\u4eba\u613f \u4e3a\u524d\u9a71\u2026\u2026\u201d \u3000\u3000\u201c\u5c06\u519b\u5982\u80fd\u8c03\u52a8\u5927\u519b\u51fa\u57ce\u8fce\u654c\uff0c\u4e5f\u4e0d\u4f1a\u9080\u8bf7\u7d2b\u70df\u51fa\u9a6c\u4e86\uff0c\u4e0d\u52b3\u8d39\u5fc3\uff0c\u6211\u53ea\u5e26\u83b2\u513f\u3001\u5de7\u513f \u4e24\u4e2a\u5973\u5a62\u968f\u884c\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u968b\u7080\u5e1d\uff0c\u63a5\u9053\uff1a\u201c\u7687\u4e0a\u4fdd\u91cd\u9f99\u4f53\uff0c\u5bab\u4e2d\u5b89\u5168\u6211\u5df2\u8d23\u6210\u5b9d\u513f\u8d1f\u8d23\uff0c\u5b87\u6587\u5c06\u519b\u4e5f\u8bf7 \u7559\u4e0b\uff0c\u653e\u624b\u5728\u5bab\u95e8\u5916\u9762\u622a\u6740\u5f3a\u654c\uff0c\u8fd9\u4e00\u6218\u4e4b\u540e\uff0c\u4e00\u5b9a\u8981\u8083\u6e05\u957f\u5b89\u57ce\u5185\u9690\u85cf\u7684\u5996\u9053\u5251\u624b\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u7b11\u9053\uff1a\u201c\u537f\u5bb6\u679c\u7136\u4e0d\u4f1a\u7f6e\u6715\u4e0d\u987e\u3002\u201d \u3000\u3000\u60f3\u5230\u9ad8\u5174\u5904\uff0c\u4ef0\u5929\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6ee1\u8179\u72d0\u7591\u7684\u671b\u7740\u8881\u7d2b\u70df\uff0c\u9053\uff1a\u201c\u5b9d\u8d35\u5983\u80af\u548c\u5c5e\u4e0b\u5408\u4f5c\u5417\uff1f\u201d \u3000\u3000\u662f\u8d28\u7591\u4e5f\u662f\u6c42\u6559\u3002 \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\u653e\u5fc3\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7d2b\u70df\u59d0\u59d0\u4e0b\u4e86\u4ee4\u5077\uff0c\u59be\u5983\u600e\u6562\u8fdd\u6297\uff1f\u5b87\u6587\u5c06\u519b\u8981\u6211\u5982 \u4f55\u914d\u5408\uff0c\u4f46\u8bf7\u5429\u5490\u4e00\u58f0\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a\u201c\u5b9d\u8d35\u5983\u7684\u672f\u6cd5\u3001\u6280\u827a\u4e0d\u5728\u7d2b\u70df\u4e4b\u4e0b\uff0c\u662f\u53ef\u6258\u5927\u4e8b\u7684\u4eba\uff0c\u5c06\u519b\u53ef\u653e\u5fc3\u7684\u8bf7 \u5979\u62c5\u5f53\u91cd\u4efb\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u5b9d\u8d35\u5983\u7edf\u7387\u5bab\u5ef7\u5168\u519b\uff0c\u6210\u90fd\u606d\u5019\u9063\u5dee\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e0d\u7528\u5982\u6b64\u8d39\u4e8b\u4e86\uff0c\u6211\u548c\u7d2b\u70df\u59d0\u59d0\u4e0d\u540c\uff0c\u5979\u662f\u56fd\u5e08\uff0c\u6211\u53ea\u662f\u4e34\u5371\u53d7\u547d\u7684\u4e00\u4e2a \u5ad4\u5983\u7f62\u4e86\uff0c\u7d2b\u70df\u59d0\u8981\u6211\u4fdd\u62a4\u7687\u4e0a\u3001\u7687\u540e\u4e3a\u4e3b\uff0c\u6211\u4eec\u5c31\u5bab\u5899\u4e3a\u51c6\uff0c\u5bab\u5899\u4e4b\u5916\u7531\u5c06\u519b\u8d1f\u8d23\uff1b\u8fdb\u5165 \u5bab\u5899\u5c31\u7531\u6211\u8d1f\u8d23\u9010\u6740\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5b9d\u8d35\u5983\uff0c\u8981\u6210\u90fd\u5982\u4f55\u9057\u6d3e\u4eba\u624b\uff0c\u8fd8\u8bf7\u5429\u5490\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8427\u96e8\u526f\u7edf\u9886\u548c\u5176\u7edf\u5e05\u7684\u4e09\u5341\u4e8c\u540d\u5bab\u536b\uff0c\u8bf7\u7559\u5728\u8fd9\u91cc\uff0c\u5c06\u519b\u518d\u6d3e\u4e00\u4f4d\u526f\u7edf\u9886 \u7387\u516d\u5341\u540d\u52c7\u58eb\u5165\u5bab\uff0c\u5185\u5bab\u5730\u65b9\u5927\uff0c\u6211\u8981\u8bbe\u7acb\u4e00\u4e9b\u4f20\u8b66\u7684\u5c97\u54e8\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u6210\u90fd\u6d3e\u7530\u5f53\u3001\u51cc\u4e91\u4e24\u4f4d\u526f\u7edf\u9886\uff0c\u5404\u7387\u4e09\u5341\u4e8c\u540d\u5c5e\u4e0b\u5165\u5bab\uff0c\u5411\u5b9d\u5983\u8bf7\u547d\uff0c\u4ed6\u4eec\u548c\u8427 \u96e8\u5e38\u5e74\u642d\u5f53\uff0c\u4e09\u4eba\u7684\u9ed8\u5951\u826f\u597d\uff0c\u5f7c\u6b64\u4e5f\u79c1\u4ea4\u6781\u7b03\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u591a\u8c22\u5c06\u519b\u4e86\u3002\u201d\u8881\u5b9d\u513f\u56de\u76ee\u4e00\u987e\u968b\u7080\u5e1d\uff0c\u7b11\u9053\uff1a\u201c\u5404\u9662\u592b\u4eba\uff0c\u548c\u7687\u4e0a\u559c\u6b22\u7684\u5ad4\u5983 \u624d\u4eba\uff0c\u5927\u90fd\u96c6\u4e2d\u5728\u662d\u9633\u9662\u4e2d\uff0c\u4f60\u5c31\u5728\u662d\u9633\u9662\u996e\u9152\u4f5c\u4e50\u5427\uff01\u4e0d\u8fc7\uff0c\u5343\u4e07\u4e0d\u8981\u7f20\u6211\uff0c\u8fd9\u662f\u73a9\u547d\u7684 \u65f6\u523b\uff0c\u7d2b\u70df\u59d0\u4ee4\u51fa\u5982\u5c71\uff0c\u6211\u53ef\u4e0d\u6562\u9a6c\u864e\uff0c\u4e11\u8bdd\u8bf4\u524d\u9762\uff0c\u7687\u4e0a\uff0c\u4f60\u53ef\u8981\u591a\u4f53\u60dc\u59be\u5983\u5440\uff01\u201d \u3000\u3000\u968b\u7080\u5e1d\u6709\u70b9\u5bb3\u6015\u8881\u7d2b\u70df\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u6015\u8881\u5b9d\u513f\uff0c\u9ad8\u58f0\u56b7\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u5bf9\u9635\u6740\u654c\uff0c\u4e5f\u6709\u7a7a \u95f2\uff0c\u654c\u4eba\u672a\u6765\u4e4b\u524d\uff0c\u966a\u6715\u559d\u4e24\u676f\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u5b87\u6587\u6210\u90fd\u5df2\u7ecf\u6084\u7136\u9000\u4e0b\uff0c\u8881\u5b9d\u513f\u5374\u88ab\u968b\u7080\u5e1d\u53cd\u8bd8\u7684\u819b\u76ee\u7ed3\u820c\uff0c\u4e0d \u77e5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u653e\u7eb5\u3001\u7231\u8272\u7684\u968b\u7080\u5e1d\u5df2\u611f\u89c9\u5b89\u5168\u4e0a\u5f97\u5230\u4fdd\u969c\uff0c\u4f38\u624b\u4e00\u628a\uff0c\u7adf\u628a\u8881\u5b9d\u513f\u63fd\u5165\u6000\u4e2d\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6709\u70b9\u706b\u4e86\uff0c\u8bed\u542b\u5fff\u6012\u5730\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u5f3a\u654c\u73af\u4f3a\u5728\u5bab\u9662\u4e4b\u5916\uff0c\u5927\u519b\u968f\u65f6\u4f1a\u6740\u5165\u957f\u5b89 \u57ce\u6765\uff0c\u4f60\u5c31\u4e0d\u80fd\u5206\u70b9\u5fc3\u4e8e\u56fd\u4e8b\u5b89\u5371\u4e4b\u4e0a\u5417\uff1f\u201d \u3000\u3000\u201c\u6715\u662f\u601c\u7231\u5b9d\u5983\u5440\uff01\u5979\u5982\u6b64\u5a07\u67d4\uff0c\u6325\u5251\u5bf9\u654c\u4e8e\u6218\u9635\u4e4b\u4e0a\uff0c\u53eb\u6715\u5982\u4f55\u5b89\u5fc3\uff1f\u201d \u3000\u3000\u201c\u7687\u4e0a\uff0c\u751c\u8a00\u871c\u8bed\u65e0\u6cd5\u9000\u654c\u5927\u519b\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8f7b\u601c\u871c\u7231\u4e5f\u65e0\u6cd5\u963b\u6b62\u523a\u5ba2\u6740\u5165\u5bab\u4e2d\uff0c \u4f60\u662f\u5b81\u613f\u82b1\u4e0b\u6b7b\u505a\u9b3c\u4e5f\u98ce\u6d41\uff0c\u96be\u9053\u5c31\u4e0d\u66ff\u7687\u540e\u548c\u4f17\u5bb6\u592b\u4eba\u60f3\u60f3\u5417\uff1f\u653e\u4e86\u5b9d\u513f\u5427\uff01\u8ba9\u5979\u5e03\u7f6e\u4e00 \u4e0b\u62d2\u654c\u7684\u9635\u52bf\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u5f97\u592a\u91cd\u4e86\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u4e00\u70b9\u4e5f\u65e0\u6127\u759a\u4e4b\u8272\uff0c\u5979\u5df2\u51c6\u5907\u968f\u65f6\u79bb\u53bb\uff0c\u8fd9\u756a\u4e0b\u5c71\uff0c\u62ef\u6551\u4e07\u6c11\u7684\u5fd7\u613f\u672a\u80fd\u5b9e \u73b0\uff0c\u53cd\u800c\u7ea0\u7ed3\u5728\u79c1\u4eba\u7684\u60c5\u6b32\u6069\u6028\u4e4b\u4e2d\uff0c\u5fc3\u4e2d\u70e6\u607c\u900f\u4e86\uff0c\u5df2\u4e0d\u518d\u7559\u604b\u5bab\u5ef7\u3001\u4eba\u95f4\u7684\u7e41\u534e\u751f\u6d3b\uff0c \u60f3\u56de\u5230\u6df1\u5c71\u53e4\u6d1e\u53bb\u4e86\u3002 \u3000\u3000\u968b\u7080\u5e1d\u4e5f\u770b\u51fa\u4e86\u82d7\u5934\u4e0d\u5bf9\uff0c\u8881\u7d2b\u70df\u771f\u4f1a\u7ffb\u8138\uff0c\u5355\u662f\u5979\u7529\u624b\u4e00\u8d70\uff0c\u5c31\u65e0\u4eba\u80fd\u963b\u6321\u5df2\u903c\u8fd1\u957f \u5b89\u7684\u5927\u519b\u3002 \u3000\u3000\u53ea\u597d\u8ba4\u8f93\u4e86\u3002 \u3000\u3000\u4ed6\u653e\u4e86\u8881\u5b9d\u513f\uff0c\u9053\uff1a\u201c\u6715\u53ea\u662f\u5f00\u4e2a\u73a9\u7b11\uff0c\u537f\u5bb6\u53c8\u4f55\u5fc5\u8ba4\u771f\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a\u201c\u5982\u4eca\u5df2\u4e0d\u662f\u5f00\u73a9\u7b11\u7684\u65f6\u5019\u4e86\uff0c\u5b9d\u513f\u662f\u536b\u5b88\u5bab\u5ef7\u7684\u4e3b\u529b\u5927\u5c06\uff0c\u522b\u628a\u5979\u518d\u5f53\u4f5c \u5a07\u5a07\u5f31\u5f31\u7684\u5c0f\u5973\u4eba\u53bb\u6298\u817e\u3002\u6b64\u756a\u8bdd\u8a00\uff0c\u662f\u6211\u6700\u540e\u4e00\u6b21\uff0c\u6b64\u540e\u7edd\u4e0d\u518d\u62db\u60f9\u7687\u4e0a\u70e6\u5fc3\uff0c\u5de7\u513f\u3001\u83b2 \u513f\uff0c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u4e09\u4eba\u8054\u8882\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u968b\u7080\u5e1d\u9053\uff1a\u201c\u5df2\u662f\u4e07\u5bb6\u706f\u706b\u65f6\u5206\uff0c\u5929\u4eae\u518d\u8d70\u4e5f\u4e0d\u8fdf\u554a\uff01\u201d \u3000\u3000\u201c\u7687\u4e0a\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u519b\u60c5\u7d27\u6025\u5440\uff01\u522b\u518d\u60f3\u4eba\u95f4\u81f3\u4e50\uff0c\u5de6\u62e5\u53f3\u62b1\u7684\u5e8a\u4e0a\u4e8b\u4e86\u3002\u7d2b\u70df\u59d0 \u5f88\u8f9b\u82e6\uff0c\u5b87\u6587\u5c06\u519b\u4e5f\u6709\u7740\u6765\u52bf\u592a\u5feb\u7684\u538b\u8feb\u611f\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u6715\u4e5f\u5728\u62c5\u5fc3\u5462\uff01\u7d2b\u70df\u53ea\u5e26\u4e24\u4e2a\u5973\u5a62\uff0c\u5982\u4f55\u62d2\u6321\u654c\u4eba\u6570\u4e07\u5927\u519b\u5462\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5979\u662f\u4eba\u95f4\u7684\u5973\u795e\uff0c\u81ea\u6709\u62d2\u654c\u5947\u672f\uff0c\u7687\u4e0a\u5982\u80fd\u65e9\u4f5c\u672a\u96e8\u7ef8\u7f2a\u4e4b\u8ba1\uff0c\u5c31\u4e0d\u4f1a\u6709 \u4eca\u65e5\u7684\u5371\u5883\u4e86\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5b9d\u513f\uff0c\u4eca\u65e5\u7684\u5904\u5883\u771f\u7684\u4f1a\u6709\u5371\u9669\u5417\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u5fc3\u4e2d\u5fd6\u9053\uff1a\u539f\u6765\u7687\u4e0a\u5f88\u6015\u6b7b\uff0c\u6574\u65e5\u8ffd\u9010\u9152\u8272\uff0c\u53ea\u662f\u4e3a\u4e86\u9003\u907f\uff0c\u5012\u8981\u5413\u4ed6\u4e00\u5413\uff0c\u770b \u770b\u4ed6\u53cd\u5e94\u5982\u4f55\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u5b9d\u513f\u53ef\u662f\u65e0\u6cd5\u548c\u7d2b\u70df\u59d0\u59d0\u76f8\u6bd4\uff0c\u7d2b\u70df\u59d0\u662f\u4eba\u95f4\u5947\u624d\uff0c\u4e0a\u77e5\u5929\u6587\u4e0b\u77e5\u5730\u7406\uff0c\u672f\u6cd5\u7cbe \u6e5b\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u6211\u53ea\u4f1a\u51e0\u62db\u5e84\u7a3c\u628a\u5f0f\uff0c\u5bf9\u4ed8\u4e00\u822c\u654c\u4eba\u53ef\u4ee5\uff0c\u5982\u662f\u78b0\u4e0a\u6b66\u529f\u7279\u522b\u9ad8\u5f3a\u7684\u4eba\uff0c\u6211 \u662f\u5e94\u4ed8\u4e0d\u4e86\u3002\u7687\u4e0a\u53ef\u8981\u89c1\u673a\u800c\u4f5c\uff0c\u53d1\u89c9\u6211\u5e94\u4ed8\u4e0d\u6765\uff0c\u5c31\u65e9\u4e9b\u9003\u8d70\u3002\u201d \u3000\u3000\u968b\u7080\u5e1d\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u537f\u5bb6\u4e0d\u662f\u7cbe\u901a\u672f\u6cd5\u5417\uff1f\u201d \u3000\u3000\u201c\u7a0d\u901a\u672f\u6cd5\uff0c\u53ea\u671b\u9047\u4e0a\u7684\u90fd\u662f\u4e0d\u901a\u672f\u6cd5\u7684\u4eba\uff0c\u59be\u5983\u8fd8\u53ef\u4ee5\u5e94\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u7530\u5f53\u3001\u51cc\u4e91\u5df2\u5e26\u4e09\u5341\u4e8c\u540d\u6b66\u58eb\uff0c\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u201c\u537f\u5bb6\u548c\u4ed6\u4eec\u7814\u5546\u4e00\u4e0b\u9632\u5b88\u4e4b\u7b56\uff0c\u6715\u4e0d\u6253\u6405\u4e86\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0e\u76f4\u5165\u540e\u5bab\u800c\u53bb\uff0c\u5fc3\u4e2d\u4f3c\u662f\u6709\u4e9b\u5bb3\u6015\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5374\u7b11\u4e86\uff01 \u3000\u3000\u5979\u56de\u5934\u671b\u7740\u7530\u5f53\u3001\u51cc\u4e91\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u628a\u5e26\u6765\u7684\u4eba\u624b\u5206\u5e03\u662d\u9633\u5bab\u9662\u7684\u56db\u5468\uff0c\u7531\u8427\u96e8\u5c06\u519b \u7edf\u4e00\u6307\u6325\uff0c\u4e24\u4f4d\u8ddf\u968f\u7740\u6211\uff0c\u6551\u5e94\u5168\u5c40\u3002\u201d \u3000\u3000\u51cc\u4e91\u3001\u7530\u5f53\u8eac\u8eab\u5e94\u547d\uff0c\u628a\u5e26\u6765\u7684\u4eba\u624b\u4ea4\u4ed8\u8427\u96e8\uff0c\u9000\u5230\u8881\u5b9d\u513f\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u8881\u5b9d\u513f\u8272\u7edd\u4eba\u5bf0\uff0c\u7530\u5f53\u3001\u51cc\u4e91\u662f\u65e9\u5df2\u542c\u8fc7\u4e86\uff0c\u4f46\u5374\u6ca1\u673a\u4f1a\u89c1\u3002\u73b0\u5728\u7389\u4eba\u5c31\u5728\u8eab\u4fa7\uff0c\u54ea\u91cc \u80fd\u5fcd\u5f97\u4f4f\uff1f\u56db\u9053\u76ee\u5149\u4e00\u9f50\u6295\u5411\u4e86\u8881\u5b9d\u513f\u3002 \u3000\u3000\u679c\u7136\u662f\u779f\u4e00\u773c\u5c31\u8ba9\u4eba\u52a8\u5fc3\u7684\u7f8e\u5973\u3002 \u3000\u3000\u5b9d\u513f\u4e4b\u7f8e\uff0c\u548c\u8881\u7d2b\u70df\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5982\u6765\u81ea\u5929\u4e0a\u7684\u4ed9\u5973\uff0c\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u3002 \u3000\u3000\u5979\u7f8e\u6781\u4e86\uff0c\u4f46\u5374\u7f8e\u5f97\u9ad8\u8d35\uff0c\u7f8e\u5f97\u7aef\u5e84\uff0c\u7f8e\u5f97\u8ba9\u4eba\u4e0d\u6562\u5984\u751f\u975e\u5206\u4e4b\u60f3\uff0c\u4e0d\u6562\u751f\u51fa\u4fb5\u72af\u7684\u610f \u5ff5\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u5374\u5927\u5927\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u5979\u662f\u4eba\u95f4\u7684\u7f8e\u5973\uff0c\u7f8e\u4e0e\u5a9a\u4ea4\u9519\u800c\u6210\u7684\u7ec4\u5408\uff0c\u8ba9\u7537\u4eba\u5fc3\u52a8\uff0c\u8ba9\u7537\u4eba\u840c\u751f\u51fa\u5360\u6709\u7684\u7f6a\u6076\u5fc3\u3001 \u4fb5\u72af\u7684\u51b2\u52a8\u611f\u2026\u2026 \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u90fd\u662f\u9887\u4e3a\u81ea\u8d1f\u7684\u7537\u4eba\u3002 \u3000\u3000\u4f46\u4e5f\u770b\u5f97\u5fc3\u4e2d\u60b8\u52a8\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5f53\u7136\u77e5\u9053\u7530\u5f53\u3001\u51cc\u4e91\u5728\u770b\u5979\uff0c\u800c\u4e14\u770b\u5f97\u5fc3\u65cc\u52a8\u6447\u3002\u77e5\u9053\u88c5\u4f5c\u4e0d\u77e5\u9053\uff0c\u4efb\u4ed6\u9971\u9910\u79c0 \u8272\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4f55\u6b62\u662f\u7530\u5f53\u3001\u51cc\u4e91\uff0c\u80fd\u591f\u770b\u5230\u7684\u5bab\u536b\u90fd\u5728\u770b\u3002 \u3000\u3000\u8fd9\u7b49\u7edd\u4e16\u59ff\u5bb9\uff0c\u4eba\u95f4\u5c24\u7269\uff0c\u4e0d\u770b\u7684\u624d\u662f\u50bb\u74dc\u3002 \u3000\u3000\u4e0d\u80fd\u518d\u8ba9\u4ed6\u4eec\u770b\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5df2\u611f\u89c9\u5230\u7530\u5f53\u3001\u51cc\u4e91\u5df2\u770b\u5f97\u9501\u4e0d\u4f4f\u5fc3\u733f\u610f\u9a6c\uff0c\u518d\u8ba9\u4ed6\u4eec\u770b\u4e0b\u53bb\uff0c\u6050\u96be\u81ea\u7981\u800c\u52a8\u624b \u52a8\u811a\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u88ab\u7687\u4e0a\u4efb\u610f\u8e42\u8e8f\u8fd1\u5e74\u4e4b\u4e45\uff0c\u8df5\u8e0f\u4e86\u5979\u7684\u8eab\u8eaf\uff0c\u4e5f\u63c9\u788e\u4e86\u5979\u7684\u5fc3\uff0c\u9ebb\u6728\u4e86\u5979\u7684\u611f \u60c5\u3002 \u3000\u3000\u5979\u4e0d\u5728\u4e4e\u8ba9\u4eba\u591a\u770b\u51e0\u773c\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u7537\u4eba\u7684\u90a3\u79cd\u6fc0\u52a8\u795e\u8272\uff0c\u4f7f\u5979\u751f\u51fa\u4e86\u4e00\u79cd\u62a5\u590d\u7684\u5feb\u611f\u3002 \u3000\u3000\u4f46\u4e0d\u80fd\u8ba9\u7530\u5f53\u3001\u51cc\u4e91\u52a8\u624b\uff0c\u5b9d\u513f\u7684\u5fc3\u6001\u4e5f\u4e0d\u662f\u5f88\u5728\u4e4e\u8ba9\u7537\u4eba\u6478\u4e00\u628a\uff0c\u800c\u662f\u4e00\u52a8\u624b\uff0c\u4e24\u4eba \u5373\u72af\u4e86\u780d\u5934\u7684\u6b7b\u7f6a\u3002 \u3000\u3000\u7687\u4e0a\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u4eec\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e5f\u4e0d\u4f1a\u9976\u8fc7\u4ed6\u4eec\u3002 \u3000\u3000\u4e3e\u624b\u7406\u4e00\u4e0b\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u7f13\u7f13\u8f6c\u8fc7\u4e86\u8138\u513f\u3002 \u3000\u3000\u770b\u5f97\u66f4\u6e05\u695a\u4e86\u2014\u2014 \u3000\u3000\u5b9d\u8d35\u5983\u7684\u8138\u4e0a\u8fd8\u5e26\u7740\u52a8\u4eba\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4f46\u8fd9\u5fae\u7b11\u4e5f\u4f7f\u7530\u5f53\u548c\u51cc\u4e91\u5728\u610f\u4e71\u60c5\u8ff7\u4e2d\u89c9\u9192\u8fc7\u6765\uff0c\u4ed6\u4eec\u60f3\u5230\u4e86\u7687\u4e0a\uff0c\u4e5f\u60f3\u5230\u4e86\u89e6\u72af\u7684\u6740 \u5934\u4e4b\u7f6a\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u5782\u4e0b\u5934\u53bb\uff0c\u9053\uff1a\u201c\u53e9\u89c1\u8d35\u5983\u5a18\u5a18\uff01\u201d \u3000\u3000\u4e00\u64a9\u888d\u89d2\uff0c\u51c6\u5907\u8dea\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u7528\u884c\u6b64\u5927\u793c\u3002\u201d\u8881\u5b9d\u513f\u4f38\u51fa\u4e86\u7ea4\u5de7\u7684\u7389\u624b\uff0c\u62e6\u4f4f\u4e86\u51cc\u4e91\u3001\u7530\u5f53\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5c06\u519b\u548c \u523a\u5ba2\u4ea4\u8fc7\u624b\u5417\uff1f\u201d \u3000\u3000\u201c\u4ea4\u8fc7\u624b\uff01\u201d\u7530\u5f53\u9053\uff1a\u201c\u529b\u640f\u4e94\u5341\u62db\uff0c\u6ca1\u6709\u5206\u51fa\u80dc\u8d1f\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u9664\u4e86\u5251\u672f\u7cbe\u5947\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u9634\u9669\u7684\u62db\u672f\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u4e9b\u4e8b\u8881\u5b9d\u513f\u90fd\u77e5\u9053\uff0c\u7528\u4e0d\u7740\u95ee\u4ed6\u4eec\u3002\u4f46\u60f3\u591a\u7ed9\u4e24\u4eba\u4e00\u4e9b\u9971\u9910\u79c0\u8272\u7684\u673a\u4f1a\uff0c\u8fd9\u4e9b \u8bdd\u9898\u6700\u9002\u5408\u4e86\u3002 \u3000\u3000\u201c\u97f3\u4e50\uff01\u201d\u51cc\u4e91\u9053\uff1a\u201c\u597d\u50cf\u4e00\u7f15\u7bab\u58f0\uff0c\u5f88\u4f4e\u6c89\uff0c\u9690\u9690\u53ef\u95fb\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u771f\u7684\u5f15\u8d77\u8881\u5b9d\u513f\u7684\u5174\u8da3\u4e86\u3002 \u3000\u3000\u5979\u5fae\u7b11\u9053\uff1a\u201c\u51cc\u5c06\u519b\uff0c\u4ed4\u7ec6\u60f3\u4e00\u60f3\uff0c\u662f\u4e0d\u662f\u7bab\u58f0\uff1f\u201d \u3000\u3000\u51cc\u4e91\u9053\uff1a\u201c\u542c\u4e0d\u771f\u5207\uff0c\u4f46\u6211\u80fd\u786e\u5b9a\u6709\u4e00\u79cd\u4e50\u58f0\u4f20\u6765\uff0c\u7bab\u58f0\u3001\u7b1b\u58f0\uff1f\u6211\u65e0\u6cd5\u8ba4\u5b9a\uff0c\u4e5f\u8bb8\u6839 \u672c\u4e0d\u662f\u7ba1\u4e50\u2026\u2026\u201d \u3000\u3000\u7530\u5f53\u63a5\u7740\u9053\uff1a\u201c\u7bab\u58f0\u7684\u6210\u5206\u6700\u5927\uff0c\u6211\u4e5f\u542c\u5230\u8fc7\u90a3\u79cd\u58f0\u97f3\uff0c\u8fdc\u8fdc\u7684\u4f20\u6765\uff0c\u4f3c\u65ad\u82e5\u7eed\uff0c\u662f\u7bab \u58f0\uff0c\u4e5f\u4e0d\u662f\u7af9\u5236\u7684\u6d1e\u7bab\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7b11\u5bb9\u5982\u82b1\u5730\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6709\u94dc\u7bab\u3001\u94c1\u7bab\uff0c\u4e5f\u6709\u7389\u7bab\uff1b\u4e50\u58f0\u4f20\u6765\uff0c\u5fc5\u6709\u5176\u56e0\uff0c\u4e24\u4f4d \u6162\u6162\u5730\u60f3\uff0c\u4e50\u58f0\u914d\u5408\u7740\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7530\u5f53\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u6709\u4e00\u79cd\u7ec6\u5fae\u58f0\u97f3\u6df7\u5408\u5728\u4e50\u58f0\u4e2d\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u67d4\u58f0\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e00\u79cd\u866b\u722c\u7684\u58f0\u97f3\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u4e00\u79cd\u5c0f\u866b\u98de\u52a8\u7684\u58f0\u97f3\u2026\u2026\u201d \u3000\u3000\u201c\u98de\u52a8\uff1f\u201d\u8881\u5b9d\u513f\u5403\u60ca\u5730\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4e00\u79cd\u6bd2\u7269\u5728\u97f3\u4e50\u58f0\u4e2d\u98de\u52a8\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u770b\u5230\u6bd2\u7269\u2026\u2026\u201d\u51cc\u4e91\u9053\uff1a\u201c\u53ea\u542c\u5230\u4e00\u79cd\u7fbd\u7ffc\u5212\u7a7a\u7684\u58f0\u97f3\uff0c\u5076\u800c\u5728\u542c\u89c9\u51fa\u73b0\uff0c\u6ca1\u6709 \u4eba\u53d7\u5230\u6bd2\u7269\u7684\u4f24\u5bb3\uff0c\u6240\u4ee5\u4e5f\u65e0\u6cd5\u5224\u77e5\u4ed6\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6b21\u8bd5\u4e60\uff0c\u7b49\u4ed6\u4eec\u51b3\u5fc3\u5165\u4fb5\u4e86\uff0c\u5c31\u53ef\u80fd\u5927\u4e3e\u65bd\u5c55\uff0c\u8fd9\u5c31\u975e\u5e38\u53ef\u6015\u4e86\u3002 \u6211\u8981\u901a\u77e5\u7687\u4e0a\u3001\u7687\u540e\u3001\u5404\u9662\u5ad4\u5983\u4f5c\u51c6\u5907\uff0c\u4e24\u4f4d\u4e5f\u8981\u548c\u8427\u5c06\u519b\u7814\u7a76\u5982\u4f55\u9632\u8303\uff0c\u4ee5\u514d\u6b66\u58eb\u4eec\u4f24\u5728 \u90a3\u4e9b\u6bd2\u7269\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u7530\u5f53\u9053\uff1a\u201c\u5b9d\u8d35\u5983\uff01\u6211\u4eec\u5b8c\u5168\u4e0d\u77e5\u662f\u4ec0\u4e48\u6bd2\u7269\uff0c\u5982\u4f55\u9632\u8303\u5462\uff1f \u3000\u3000\u8fd8\u671b\u5b9d\u8d35\u5983\u7ed9\u4e88\u660e\u793a\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u53ea\u662f\u731c\u6d4b\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5b83\u591f\u98de\u884c\u4f24\u4eba\u7684\u53ef\u80fd\u662f\u6bd2\u8702\u3001\u8840\u8747\u4e4b\u7c7b\uff0c\u6211\u8981\u5ad4\u5983\u4eec \u9632\u8303\u7684\u529e\u6cd5\u662f\u8eb2\u5728\u868a\u5e10\u4e4b\u4e2d\u3002\u5f53\u7136\u60f3\u529e\u6cd5\u52a0\u5f3a\u868a\u5e10\u7684\u5f3a\u5ea6\u3002 \u3000\u3000\u5979\u4eec\u53ef\u4ee5\u5728\u5ba4\u4e2d\u71c3\u8d77\u5bab\u706f\u3001\u706b\u70ac\uff0c\u4f7f\u6bd2\u7269\u65e0\u6240\u9041\u5f62\uff0c\u6613\u4e8e\u6d88\u706d\uff1b\u4f46\u5b88\u5728\u5bab\u5916\u7684\u6b66\u58eb\u5c31\u6ca1 \u6709\u8fd9\u4e9b\u8bbe\u5907\u3002\u201d \u3000\u3000\u7530\u5f53\u9053\uff1a\u201c\u660e\u5929\u53ef\u4ee5\u53ec\u96c6\u957f\u5b89\u57ce\u4e2d\u7684\u540d\u624b\u5de7\u5320\uff0c\u8d76\u5236\u4e09\u4e24\u767e\u4ef6\u9632\u868a\u8747\u7684\u8863\u670d\uff0c\u6bcf\u4eba\u5404\u4e00 \u4ef6\uff1b\u4f46\u4eca\u591c\u53ea\u597d\u6682\u7531\u5bab\u5185\u501f\u51fa\u4e00\u4e9b\u868a\u5e10\uff0c\u6682\u4f5c\u9632\u62a4\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u5947\u6bd2\u874e\u5b50\uff0c\u662f\u6df1\u5c71\u5927\u6cfd\u4e2d\u7684\u54c1\u79cd\uff0c\u6bd2\u6027\u5947\u70c8\uff0c\u87ab\u4eba\u5fc5\u6b7b\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u5fc5\u754f\u60e7\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u5b83\u884c\u52a8\u7f13\u6162\uff0c\u6613\u88ab\u51fb\u6bd9\u3002\u62c5\u5fc3\u7684\u662f\u98de\u884c\u6bd2\u7269\uff0c\u5b83\u4eec\u6765\u52bf \u5feb\u901f\uff0c\u4e0d\u6613\u9632\u8303\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4f46\u6bd2\u874e\u4ea6\u4e0d\u53ef\u8f7b\u89c6\uff0c\u5b83\u4eec\u5e26\u5728\u523a\u5ba2\u8eab\u4e0a\uff0c\u53ef\u80fd\u5728\u4ea4\u624b\u4e2d\u79fb\u653e\u4f60\u8eab\u4e0a\uff0c\u800c\u4e0d \u81ea\u89c9\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u5c31\u6709\u70b9\u53ef\u6015\u4e86\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u662d\u9633\u9662\u56db\u5468\u4e5f\u8981\u70b9\u71c3\u767e\u76cf\u4ee5\u4e0a\u7684\u7eb1\u7b3c\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u5012\u5bb9\u6613\uff0c\u6211\u5429\u5490\u4ed6\u4eec\u51c6\u5907\uff0c\u4e09\u4f4d\u8bf7\u81ea\u884c\u5206\u914d\u4e00\u4e0b\u62d2\u654c\u4efb\u52a1\uff0c\u4ee5\u5168\u529b\u4fdd\u62a4 \u662d\u9633\u9662\u4e3a\u91cd\u70b9\uff0c\u522b\u8ba9\u4eba\u51b2\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u5c5e\u4e0b\u60f3\u628a\u6b64\u4e8b\u901a\u77e5\u5b87\u6587\u603b\u7edf\u9886\uff0c\u8981\u65e9\u4f5c\u9632\u5907\uff0c\u5bab\u5899\u5916 \u62e6\u6740\u7684\u529b\u91cf\u4fdd\u6301\u5f3a\u5927\uff0c\u4ed6\u4eec\u8fdb\u5165\u5185\u5bab\u7684\u673a\u4f1a\u5c31\u8d8a\u5c11\u4e86\u3002\u201d \u3000\u3000\u7530\u5f53\u7a81\u53c8\u9053\uff1a\u201c\u5b9d\u8d35\u5983\uff0c\u8881\u56fd\u5e08\u53ea\u5e26\u4e86\u83b2\u513f\u3001\u5de7\u513f\u4e24\u4e2a\u5973\u5a62\uff0c\u8981\u62d2\u62e6\u8bad\u7ec3\u7cbe\u826f\u6570\u4e07\u5927 \u519b\uff0c\u8fd9\u4e2a\u4ed7\u600e\u4e48\u6253\u6cd5\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u5927\u6982\u662f\u4e00\u6b21\u6700\u5947\u7279\u7684\u6218\u4e89\u4e86\uff0c\u6211\u4e0d\u77e5\u9053\u7d2b\u70df\u59d0\u59d0\u5982\u4f55\u6253\uff0c\u4e0d\u8fc7\u6211\u5012\u53ef\u4ee5 \u544a\u8bc9\u8bf8\u4f4d\uff0c\u5979\u7edd\u4e0d\u4f1a\u8d25\u3002\u201d \u3000\u3000\u201c\u4e0d\u8d25\u5c31\u662f\u80dc\u4e86\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e09\u4f4d\u5c06\u519b\uff0c\u6211\u8bf4\u662f\u4e0d\u4f1a\u8d25\uff0c\u5927\u519b\u65e0\u63a8\u8fdb\uff0c\u653b\u51fb\u957f\u5b89\uff0c\u4f46\u8881\u56fd\u5e08\u8981\u4e0d\u8981\u51fb\u6e83 \u5bf9\u65b9\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u80dc\u8d1f\u4f3c\u662f\u5168\u64cd\u4e8e\u8881\u7d2b\u70df\u7684\u624b\u4e2d\u3002 \u3000\u3000\u4e09\u4f4d\u5c06\u519b\u600e\u4e48\u60f3\u4e5f\u60f3\u4e0d\u51fa\u6709\u4e00\u79cd\u62d2\u6570\u4e07\u654c\u4eba\uff0c\u800c\u53c8\u6709\u53d6\u80dc\u7684\u672c\u9886\uff0c\u4e0d\u7981\u542c\u5f97\u77a0\u76ee\u7ed3\u820c\u3002 \u3000\u3000\u8881\u5b9d\u513f\u53c8\u9053\uff1a\u201c\u8881\u56fd\u5e08\u8eab\u5177\u5947\u672f\uff0c\u4e0d\u80fd\u4ee5\u5e73\u5e38\u4eba\u89c6\u4e4b\uff0c\u6211\u4e5f\u65e0\u6cd5\u548c\u4e09\u4f4d\u5c06\u519b\u8bf4\u4e2a\u6e05\u695a\uff0c \u4f46\u6211\u4fdd\u8bc1\u8881\u56fd\u5e08\u4f1a\u62d2\u6321\u4f4f\u90a3\u4e00\u8def\uff0c\u4e0d\u80fd\u653b\u51fb\u957f\u5b89\u3002\u201d \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u65e0\u8bdd\u53ef\u95ee\u4e86\uff0c\u4e09\u4eba\u5206\u5934\u529e\u4e8b\u3002 \u3000\u3000\u8881\u5b9d\u513f\u4e5f\u901a\u77e5\u5404\u9662\u592b\u4eba\u3001\u5973\u7f24\u3001\u7687\u540e\uff0c\u51c6\u5907\u62d2\u654c\u5e94\u7528\u4e4b\u7269\uff0c\u628a\u81ea\u5df1\u743c\u82b1\u9662\u4e2d\u4e24\u4e2a\u5bab\u5973\u6d3e \u5b88\u7687\u4e0a\u3001\u7687\u540e\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u5979\u4eec\u662f\u5f69\u8679\u3001\u98de\u71d5\uff0c\u8ddf\u7740\u8881\u5b9d\u513f\u7ec3\u4e86\u4e00\u70b9\u6b66\u529f\u3001\u672f\u6cd5\uff0c\u53ea\u4e0d\u8fc7\u4e0d\u50cf\u83b2\u513f\u3001\u5de7\u513f\u660e\u76ee\u5f20\u80c6 \u7684\u7ec3\uff0c\u4e24\u4eba\u53ea\u662f\u5728\u6697\u4e2d\u5077\u7ec3\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5165\u5bab\u4e4b\u540e\uff0c\u4e00\u76f4\u4e0d\u60f3\u628a\u81ea\u5df1\u66b4\u9732\u7684\u592a\u591a\u3002 \u3000\u3000\u4e00\u5411\u82b1\u6728\u6276\u758f\u3001\u6574\u9f50\u3001\u5e7d\u96c5\u7684\u662d\u9633\u9662\u5916\uff0c\u6b64\u523b\u53d8\u5f97\u5f88\u602a\u5f02\uff0c\u4e1c\u4e00\u868a\u5e10\uff0c\u897f\u4e00\u868a\u5e10\uff0c\u868a\u5e10 \u56db\u5468\u5404\u6311\u4e24\u76cf\u6740\u866b\u706f\u3002 \u3000\u3000\u662d\u9633\u9662\u767e\u95f4\u4ee5\u4e0a\u5367\u5ba4\uff0c\u706f\u5149\u901a\u660e\uff0c\u868a\u5e10\u9ad8\u6302\u3002 \u3000\u3000\u5e10\u4e2d\u96c6\u6ee1\u4e86\u4eba\uff0c\u6709\u4e9b\u4eba\u624b\u4e2d\u8fd8\u62ff\u7740\u6247\u5b50\uff0c\u6709\u4e9b\u4eba\u624b\u4e2d\u4e5f\u62ff\u7740\u7af9\u8282\u6728\u68cd\uff0c\u4e00\u7fa4\u82b1\u679d\u62db\u5c55\u7684 \u5927\u59d1\u5a18\u4e5f\u8981\u52a8\u624b\u81ea\u4fdd\u4e86\u3002 \u3000\u3000\u5979\u4eec\u65e0\u6cd5\u62d2\u654c\u3002 \u3000\u3000\u4f46\u62cd\u868a\u5b50\u3001\u6253\u6bd2\u8702\uff0c\u81ea\u4fe1\u8fd8\u53ef\u80dc\u4efb\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u662f\u81ea\u5df1\u4fdd\u547d\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u6253\u6bd2\u8702\u662f\u4e0d\u80fd\u7528\u624b\u6253\uff0c\u4eba\u624b\u4e00\u7269\uff0c\u4e8b\u6240\u5fc5\u7136\uff1b\u5bab\u5a25\u5f69\u5973\u5404\u6709\u6240\u6267\uff0c\u8fd9\u5c31\u5f62\u6210\u4e86\u7687\u5bab\u4e2d \u7684\u5343\u53e4\u5947\u89c2\u3002 \u3000\u3000\u770b\u5427\u2014\u2014 \u3000\u3000\u5f69\u8863\u6620\u8f89\u5bab\u706f\u4e0b\uff0c\u624b\u4e2d\u5374\u62ff\u7740\u5343\u5947\u767e\u602a\u7684\u4e1c\u897f\uff0c\u6709\u7af9\u677f\u3001\u4ee4\u724c\u3001\u6728\u68cd\u3001\u94c1\u94f2\uff0c\u4e5f\u6709\u4eba\u62ff \u7740\u6795\u5934\u3001\u8863\u670d\u3002 \u3000\u3000\u53cd\u6b63\u662f\u80fd\u7528\u6765\u62cd\u6253\u8840\u8747\u3001\u6bd2\u8702\u7684\u4e1c\u897f\u90fd\u884c\u3002 \u3000\u3000\u5979\u4eec\u6ca1\u89c1\u8fc7\u8840\u8747\u3001\u6bd2\u8702\uff0c\u60f3\u8c61\u4e2d\u548c\u82cd\u8747\u3001\u871c\u8702\u4e00\u6837\uff0c\u6240\u4ee5\u5927\u5bb6\u90fd\u6709\u52c7\u6c14\uff0c\u62ff\u8d77\u4e1c\u897f\u6253 \u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u7ed5\u7740\u662d\u9633\u5bab\u5ef7\u8d70\u4e00\u8f6c\uff0c\u76ee\u7779\u5f69\u5a25\u3001\u5bab\u5973\u4eec\u7684\u602a\u5f02\u5f62\u72b6\uff0c\u5f88\u60f3\u5927\u7b11\u4e00\u573a\uff0c\u4f46\u5374\u5fcd\u4e0b \u4e86\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u53ea\u8981\u5979\u53d1\u4e0b\u4e00\u9053\u5b89\u5168\u4fdd\u969c\u7684\u627f\u8bfa\uff0c\u60c5\u51b5\u5c31\u7acb\u523b\u5927\u53d8\uff0c\u6df1\u5bab\u6062\u590d\u65e7\u989c\u8272\uff0c \u5f69\u5973\u5b98\u5a25\u6367\u9152\u5fd9\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u6ca1\u6709\u8bb2\u8bdd\uff0c\u5979\u559c\u6b22\u770b\u8fd9\u4efd\u6df7\u4e71\uff0c\u4e5f\u8ba8\u538c\u770b\u5f69\u5973\u5bab\u5a25\u4eec\u5e73\u65e5\u5e2e\u7740\u4e3b\u5b50\u4e89\u5ba0\u7684\u90a3\u526f \u5634\u8138\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u4e5f\u770b\u5230\u4e86\u3002 \u3000\u3000\u770b\u5230\u4e86\u5bab\u5973\u4eec\u90a3\u4efd\u72fc\u72c8\uff0c\u80ed\u8102\u96f6\u843d\uff0c\u82b1\u7c89\u9000\uff1b\u5df2\u975e\u7f8e\u4e3d\u4fcf\u4f73\u4eba\u3002 \u3000\u3000\u662d\u9633\u5bab\u9662\u76f8\u5f53\u5927\uff0c\u4f46\u4e00\u4e0b\u5b50\u6324\u4e86\u5341\u4e8c\u9662\u592b\u4eba\u7684\u5b98\u5a25\u3001\u5f69\u5973\u3002 \u3000\u3000\u5c31\u663e\u5f97\u5c0f\u4e00\u4e9b\u4e86\u3002 \u3000\u3000\u5341\u4e8c\u9662\u592b\u4eba\u966a\u7740\u7687\u540e\u548c\u7687\u4e0a\uff0c\u5728\u7687\u540e\u5bdd\u5bab\u996e\u9152\u3002 \u3000\u3000\u5728\u6570\u5341\u4f4d\u592a\u76d1\u4fdd\u62a4\u9632\u5b88\u4e4b\u4e0b\uff0c\u5f88\u5c11\u5bab\u5a25\u80fd\u8fdb\u5f97\u53bb\u7687\u540e\u7684\u5bdd\u5bab\u3001\u5ead\u9662\uff0c\u5c31\u53ea\u597d\u6324\u5728\u5404\u5904\u5bab \u5973\u4f4f\u7684\u504f\u623f\u4e2d\u4e86\u3002 \u3000\u3000\u539f\u672c\u4f4f\u4e24\u4e2a\u5bab\u5973\u7684\u5730\u65b9\uff0c\u6324\u4e86\u5341\u51e0\u4e2a\uff0c\u522b\u8bf4\u7761\u89c9\u4e86\uff0c\u5750\u4e5f\u611f\u89c9\u5230\u5730\u65b9\u5c0f\u4e86\u4e00\u4e9b\u3002\u4f46\u5374\u6ca1 \u6709\u4eba\u6562\u5230\u95e8\u5916\u7ad9\u3002 \u3000\u3000\u56e0\u4e3a\u6015\u6bd2\u8702\u554a\uff01 \u3000\u3000\u5b88\u5728\u662d\u9633\u9662\u5916\u7684\u5bab\u536b\u3001\u6b66\u58eb\u4eec\u4e5f\u770b\u5230\u4e86\u3002 \u3000\u3000\u540e\u5bab\u7c89\u9edb\u4e5f\u90fd\u662f\u9009\u51fa\u6765\u7684\u7f8e\u5973\uff0c\u5357\u56fd\u4f73\u4e3d\uff0c\u5317\u5730\u80ed\u8102\uff0c\u4e2a\u4e2a\u90fd\u6709\u51e0\u5206\u59ff\u8272\u3002\u518d\u52a0\u4e0a\u7f8e\u4e3d \u7684\u8863\u7740\u4fcf\u6253\u626e\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u53ef\u770b\u7684\u5730\u65b9\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u8ff7\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u4f46\u73b0\u5728\u770b\u4e0a\u53bb\uff0c\u5168\u4e0d\u662f\u90a3\u4e48\u56de\u4e8b\u4e86\u3002\u6b8b\u810f\u6c61\u4eba\uff0c\u539f\u672c\u6e05\u79c0\u7684\u8138\u4e0a\u4e00\u7247\u7ea2\u3001\u4e00\u7247\u767d\uff0c\u4e0d\u4f46 \u6ca1\u6709\u4e00\u70b9\u7f8e\u611f\uff0c\u770b\u4e45\u4e86\u8fd8\u6709\u4e9b\u5413\u4eba\u3002 \u3000\u3000\u5a07\u751f\u60ef\u517b\u7684\u5bab\u5973\u4eec\uff0c\u5982\u4f55\u7981\u53d7\u5f97\u8fd9\u4e00\u591c\u6298\u817e\u3002 \u3000\u3000\u5929\u8fd1\u9ece\u660e\u65f6\uff0c\u5168\u5012\u5728\u5730\u4e0a\u7761\u7740\u4e86\u3002 \u3000\u3000\u4f60\u8eba\u5728\u6211\u817f\u4e0a\uff0c\u6211\u5012\u5728\u4f60\u6000\u4e2d\u3002\u8863\u88d9\u62c6\u76b1\uff0c\u7761\u59ff\u602a\u5f02\uff0c\u7b80\u76f4\u50cf\u5927\u6218\u8d25\u9000\u3001\u75b2\u5367\u6c99\u573a\u7684\u6b8b \u519b\u3002 \u3000\u3000\u8427\u96e8\u770b\u4e86\u597d\u4e00\u9635\uff0c\u6447\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u5206\u59ff\u8272\uff0c\u4e03\u5206\u88c5\u626e\uff0c\u6b8b\u8102\u5269\u7c89\uff0c\u7b80\u76f4\u662f\u4e0d\u80fd\u770b \u554a\uff01\u201d \u3000\u3000\u201c\u66f4\u96be\u770b\u7684\u662f\u5979\u4eec\u624b\u4e2d\u62ff\u7684\u4e1c\u897f\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u5343\u5947\u767e\u602a\uff0c\u65e0\u6240\u4e0d\u6709\u3002\u6a2a\u626b\u4e00\u5730\uff0c\u89e6\u76ee \u60ca\u5fc3\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u73b0\u5728\u6709\u4e00\u6279\u6740\u624b\u51b2\u5165\u5bab\u6765\uff0c\u8fd9\u756a\u96f6\u4e71\u7684\u60c5\u666f\uff0c\u5c31\u53d8\u5f97\u60e8\u4e0d\u5fcd\u7779\u4e86\u3002\u201d\u51cc\u4e91\u9053\u3002 \u3000\u3000\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u5f20\u671b\u3002 \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u3001\u8427\u96e8\uff0c\u4e09\u4eba\u6ca1\u6709\u4ea4\u8c08\u3002 \u3000\u3000\u4f46\u4e09\u4eba\u5fc3\u4e2d\u5374\u60f3\u7684\u540c\u4e00\u4ef6\u4e8b\uff0c\u5e0c\u671b\u770b\u5230\u8881\u5b9d\u513f\uff0c\u4eba\u95f4\u7edd\u8272\u7684\u8881\u8d35\u5983\uff0c\u662f\u4e0d\u662f\u4e5f\u4e00\u6837\u8102\u7c89 \u96f6\u843d\u989c\u8272\u6b8b\u3002 \u3000\u3000\u6ca1\u6709\u770b\u5230\u5b9d\u8d35\u5983\u3002 \u3000\u3000\u4f46\u516d\u9053\u65e5\u5149\u5374\u8f6c\u52a8\u7684\u4ea4\u7ec7\u5728\u4e00\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u4f1a\u5fc3\u4e00\u7b11\uff01 \u3000\u3000\u201c\u5929\u4eae\u4e86\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u4eca\u591c\u5927\u6982\u53ef\u4ee5\u5e73\u5b89\u5ea6\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u7530\u5c06\u519b\uff0c\u592a\u4e50\u89c2\u4e86\u3002\u5371\u9669\u603b\u5728\u75b2\u7d2f\u65f6\uff01\u201d \u3000\u3000\u4e00\u4e2a\u5a07\u751c\u7684\u58f0\u97f3\u4f20\u5165\u4e86\u4e09\u4eba\u8033\u9645\u3002 \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u4e0d\u77e5\u4f55\u65f6\u8881\u5b9d\u513f\u5df2\u7ad9\u5728\u4e09\u4eba\u8eab\u540e\u3002 \u3000\u3000\u5979\u4ecd\u7136\u5bb9\u5149\u7115\u53d1\uff0c\u5a07\u8273\u4f9d\u65e7\u3002 \u3000\u3000\u4eba\u95f4\u7684\u7edd\u8272\u7f8e\u5973\uff0c\u679c\u7136\u548c\u4e00\u822c\u7684\u7f8e\u5973\u4e0d\u540c\u3002 \u3000\u3000\u5979\u8138\u8272\u4f9d\u7136\u662f\u7c89\u7ea2\u5982\u82b1\uff0c\u4ee5\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u7b49\u4e09\u4eba\u7684\u76ee\u529b\uff0c\u4e5f\u65e0\u6cd5\u8fa8\u8bc6\u51fa\u662f\u5929\u751f\u4e3d\u8d28 \u8272\u81ea\u6210\uff0c\u8fd8\u662f\u82b1\u7c89\u80ed\u8102\u8c03\u5408\u7684\u4eba\u5de5\u7ec4\u5408\u3002 \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u9ed1\u591c\u5c06\u901d\uff0c\u5929\u8272\u9ece\u660e\u3002\u5b9d\u8d35\u5983\u7684\u610f\u601d\u662f\u654c\u4eba\u4f1a\u6765\u4e00\u6b21\u62c2\u6653\u5077\u88ad\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u610f\u601d\uff0c\u662f\u4e8b\u5b9e\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e09\u4f4d\u51dd\u795e\u503e\u542c\u4e00\u4e0b\uff0c\u4e5f\u8be5\u4e0b\u4ee4\u5e03\u7f72\uff0c\u51c6\u5907\u8fce\u654c \u4e86\u3002\u201d \u3000\u3000\u7530\u5f53\u3001\u8427\u96e8\u3001\u51cc\u4e91\u65e9\u5df2\u5728\u51dd\u795e\u503e\u542c\uff0c\u56e0\u4e3a\u672a\u66fe\u95fb\u5f97\u5f02\u58f0\uff0c\u8427\u96e8\u624d\u5411\u5b9d\u8d35\u5983\u8d28\u7591\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u8881\u5b9d\u513f\u8bf4\u7684\u5341\u5206\u80af\u5b9a\uff0c\u4e09\u4eba\u4e0d\u5f97\u4e0d\u51dd\u805a\u5168\u795e\u542c\u53bb\u3002 \u3000\u3000\u679c\u7136\u542c\u5230\u4e86\u4e00\u7f15\u67d4\u7ec6\u7684\u4e50\u58f0\uff0c\u9065\u9065\u98d8\u6765\u3002 \u3000\u3000\u201c\u662f\u7bab\u58f0\uff01\u201d\u7530\u5f53\u9053\uff1a\u201c\u5176\u58f0\u4f3c\u9010\u6e10\u589e\u5f3a\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u7389\u8427\u58f0\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u53ef\u80fd\u662f\u5927\u4e3e\u6765\u72af\uff0c\u4f46\u4e0d\u77e5\u5b87\u6587\u5c06\u519b\u662f\u5426\u4e5f\u542c \u5230\u4e86\uff1f\u201d \u3000\u3000\u51cc\u4e91\u9053\uff1a\u201c\u5b87\u6587\u603b\u7edf\u9886\u6b66\u529f\u5f3a\u8fc7\u6211\u4eec\u751a\u591a\uff0c\u5e94\u8be5\u65e9\u6709\u8b66\u89c9\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u5e0c\u671b\u80fd\u963b\u654c\u4e8e\u5bab\u5899\u4e4b\u5916\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5982\u679c\u88ab\u4ed6\u4eec\u6740\u5165\u5bab\u4e2d\uff0c\u8fd9\u4e9b\u5982\u82b1\u4f3c\u7389\u7684\u5bab\u5a25 \u5f69\u5973\u5c31\u8981\u7ecf\u5386\u4e00\u756a\u6d69\u52ab\u4e86\uff0c\u4e00\u573a\u6740\u622e\u4e0b\u6765\uff0c\u5f88\u96be\u60f3\u8c61\u5979\u4eec\u8fd8\u6709\u591a\u5c11\u80fd\u6d3b\u5f97\u4e0b\u6765\uff1f\u201d \u3000\u3000\u8427\u96e8\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8d35\u5983\u771f\u7684\u6ca1\u6709\u628a\u63e1\u80fd\u4fdd\u536b\u5979\u4eec\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7f13\u7f13\u7684\u9053\uff1a\u201c\u6211\u2026\u2026\u4e0d\u6562\u4fdd\u8bc1\u2026\u2026\u201d \u3000\u3000\u201c\u8d35\u5983\u5a18\u5a18\uff0c\u56fd\u5e08\u4f3c\u662f\u5bf9\u8881\u8d35\u5983\u5145\u6ee1\u4e86\u4fe1\u5fc3\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u56fd\u5e08\u8a00\u51fa\u5982\u5c71\uff0c\u6211\u4eec\u90fd\u5341\u5206 \u656c\u670d\u3002\u201d \u3000\u3000\u786c\u628a\u4fdd\u536b\u5185\u5bab\u7684\u6210\u8d25\u8d23\u4efb\u5411\u8881\u5b9d\u513f\u7684\u5934\u4e0a\u5957\uff0c\u8fd8\u771f\u6b6a\u6253\u6b63\u7740\uff0c\u786c\u628a\u8881\u5b9d\u513f\u5957\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5fc3\u5934\u5145\u6ee1\u60b2\u5fff\u4e4b\u6c14\uff0c\u60f3\u8896\u624b\u65c1\u89c2\uff0c\u8ba9\u8fd9\u7687\u5bab\u5185\u82d1\u4e2d\u6740\u4e2a\u8840\u6e85\u5bab\u5899\uff0c\u5230\u4e86\u4e0d\u53ef\u5f00 \u4ea4\uff0c\u518d\u51fa\u624b\u6536\u62fe\u6b8b\u5c40\uff0c\u53ea\u8981\u80fd\u5bf9\u8881\u7d2b\u70df\u4ea4\u4ee3\u5c31\u597d\u3002 \u3000\u3000\u4f46\u8427\u96e8\u786c\u628a\u5979\u62d6\u5411\u524d\u9762\u7ad9\uff0c\u5f53\u5b88\u5bab\u4e2d\u7b2c\u4e00\u5173\u3002 \u3000\u3000\u8881\u5b9d\u513f\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u9053\uff1a\u201c\u6211\u600e\u4e48\u8bb0\u4e0d\u5f97\u4e86\uff1f\u201d \u3000\u3000\u8bba\u6c5f\u6e56\u9605\u5386\uff0c\u8881\u5b9d\u513f\u518d\u806a\u660e\u4e5f\u6597\u4e0d\u8fc7\u8427\u96e8\uff0c\u4e00\u542c\u56de\u7b54\uff0c\u5c31\u77e5\u9053\u8881\u5b9d\u513f\u65e0\u610f\u51fa\u5168\u529b\u4fdd\u62a4\u5185 \u5bab\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e86\u89e3\uff0c\u7acb\u523b\u63a5\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u9519\uff0c\u8881\u56fd\u5e08\u4ed8\u6258\u8d35\u5983\uff0c\u8d35\u5983\u4e5f\u4e00\u80a9\u627f\u62c5\uff0c\u8981\u56fd\u5e08\u5b89\u5fc3 \u9000\u654c\uff0c\u6211\u7b49\u5949\u5b87\u6587\u603b\u7edf\u9886\u4e4b\u4ee4\u5165\u5bab\uff0c\u53ea\u662f\u8d35\u5983\u7684\u52a9\u624b\uff0c\u4e00\u5207\u542c\u51ed\u5429\u5490\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u4e07\u6b7b\u4e0d \u8f9e\u3002\u201d \u3000\u3000\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6211\u4eec\u5168\u529b\u9000\u654c\u5c31\u662f\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u53bb\u901a\u77e5\u5b87\u6587\u5c06\u519b\u4e00\u58f0\uff0c\u8981\u4ed6\u5c0f\u5fc3\u6bd2 \u8702\u2026\u2026\u201d \u3000\u3000\u8427\u96e8\u5e94\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5e94\u8be5\u662f\u7530\u5f53\u6216\u51cc\u4e91\u53bb\uff0c\u4f46\u8427\u96e8\u5374\u4e89\u5148\u800c\u5f80\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8981\u5411\u5b81\u6587\u6210\u90fd\u590d\u547d\u3002 \u3000\u3000\u8fd9\u662f\u4e24\u4eba\u65e9\u5c31\u5546\u91cf\u597d\u7684\u8ba1\u8c0b\uff0c\u8981\u8427\u96e8\u5229\u7528\u8881\u7d2b\u70df\u7684\u6c14\u52bf\uff0c\u5957\u4f4f\u8881\u5b9d\u513f\uff0c\u8981\u5979\u62c5\u8d1f\u8d77\u4fdd\u536b \u5bab\u5ef7\u7684\u5927\u8d23\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u62c5\u5fc3\u8881\u5b9d\u513f\u4f59\u6068\u79ef\u5fc3\uff0c\u653e\u624b\u4e0d\u7ba1\uff0c\u5c31\u522b\u8bf4\u91cd\u8981\u65f6\u523b\u53cd\u6208\u4e00\u51fb\u4e86\u3002 \u3000\u3000\u7531\u4e8e\u8881\u7d2b\u70df\u6570\u5ea6\u63d0\u793a\u8881\u5b9d\u513f\u7684\u672f\u6cd5\u9ad8\u5f3a\uff0c\u5b87\u6587\u6210\u90fd\u5370\u8c61\u6df1\u523b\uff0c\u5fc3\u4e2d\u5df2\u4e0d\u6562\u7a0d\u5b58\u8f7b\u89c6\u3002 \u3000\u3000\u8427\u96e8\u56de\u6765\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u800c\u4e14\u8fd8\u5e26\u6765\u4e86\u5b87\u6587\u6210\u90fd\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5df2\u4e0b\u4ee4\u53eb\u9192\u968f\u5730\u800c\u5367\u7684\u5bab\u5973\u5f69\u5a25\uff0c\u4e00\u542c\u6709\u6bd2\u7269\u4fb5\u88ad\uff0c\u778c\u7761\u866b\u5168\u88ab\u5413\u8d70\uff0c\u8d76\u7d27\u6361\u8d77 \u5bb6\u4f19\uff0c\u6709\u7684\u7406\u88c5\u6253\u626e\uff0c\u91cd\u65b0\u5316\u5986\u4e00\u756a\u3002 \u3000\u3000\u5927\u90fd\u6d17\u53bb\u6b8b\u80ed\u4f59\u7c89\uff0c\u6062\u590d\u6211\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u5979\u4eec\u672c\u662f\u5404\u5730\u7684\u51fa\u8272\u7f8e\u5973\uff0c\u5e74\u7eaa\u53c8\u8f7b\uff0c\u5012\u4e5f\u4e2a\u4e2a\u79c0\u4e3d\uff0c\u53ea\u662f\u548c\u90a3\u8eab\u534e\u4e3d\u7684\u8863\u670d\u4e0d\u642d\u8c03\uff0c \u770b\u8d77\u6765\u6709\u70b9\u602a\u5f02\u3002 \u3000\u3000\u6bd4\u6b8b\u8102\u5269\u7c89\u6c61\u5bb9\u8272\uff0c\u90a3\u53c8\u597d\u770b\u591a\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u975e\u5e38\u606d\u8c28\uff0c\u5bf9\u7740\u8881\u5b9d\u513f\u4e00\u4e2a\u957f\u63d6\uff0c\u64a9\u888d\u5c31\u8981\u8dea\uff0c\u51c6\u5907\u5927\u793c\u53c2\u62dc\u4e86\uff0c\u53ea\u542c\u8881\u5b9d\u513f \u9053\uff1a\u201c\u5c06\u519b\uff0c\u4e0d\u53ef\u5982\u6b64\uff0c\u5927\u793c\u53c2\u62dc\uff0c\u59be\u5983\u53ef\u5f53\u53d7\u4e0d\u8d77\u3002\u53ef\u6709\u7d2b\u70df\u59d0\u59d0\u62d2\u654c\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4f46\u63a2\u9a6c\u56de\u62a5\uff0c\u654c\u519b\u4e5f\u672a\u5411\u957f\u5b89\u63a8\u8fdb\u7684\u6d88\u606f\u3002\u60f3\u662f\u88ab\u56fd\u5e08\u6321\u5728 \u90a3\u91cc\u4e86\uff0c\u5509\uff01\u771f\u53eb\u4eba\u60f3\u4e0d\u660e\uff0c\u56fd\u5e08\u53ea\u5e26\u4e86\u83b2\u513f\u3001\u5de7\u513f\u4e24\u4e2a\u52a9\u624b\uff0c\u5982\u6b64\u62d2\u654c\u6570\u4e07\u5927\u519b\uff0c\u90a3\u662f\u4e00 \u6279\u7cbe\u9510\u65e0\u5339\u7684\u90e8\u961f\uff0c\u6709\u4e9b\u8eab\u624b\u4e4b\u9ad8\u53ef\u5217\u6b66\u6797\u9ad8\u624b\uff0c\u5728\u4e0b\u82e6\u601d\u957f\u8651\uff0c\u5e0c\u671b\u80fd\u62bd\u8c03\u4e00\u6279\u4eba\u9a6c\uff0c\u8d76 \u5f80\u76f8\u52a9\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u7d2b\u70df\u59d0\u5982\u65e0\u72ec\u9000\u6570\u4e07\u5f3a\u654c\u7684\u80fd\u529b\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u8bf7\u547d\u51fa\u6218\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6253\u86c7\u968f\u68cd\u4e0a\u5730\u9053\uff1a\u201c\u8d35\u5983\u5bf9\u56fd\u5e08\u5145\u6ee1\u4fe1\u5fc3\uff0c\u56fd\u5e08\u5bf9\u8d35\u5983\u4e5f\u76f8\u8bb8\u5f97\u5f88\uff0c\u5979\u5429\u5490\u6210 \u90fd\uff0c\u4e00\u5207\u542c\u4ece\u8d35\u5983\u7684\u4ee4\u8c15\u884c\u4e8b\u3002\u201d \u3000\u3000\u53c8\u4e00\u9876\u538b\u6b7b\u4eba\u7684\u9ad8\u5e3d\u5b50\u6263\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5982\u4f55\u62db\u67b6\u5f97\u4f4f\uff1f \u3000\u3000\u5979\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c06\u519b\u96c6\u4e2d\u4e86\u591a\u5c11\u4eba\u9632\u5b88\u5bab\u5ef7\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4eba\u6570\u4e0d\u5c11\uff0c\u5343\u540d\u4ee5\u4e0a\uff0c\u4f46\u771f\u6b63\u5217\u5165\u9ad8\u624b\u7684\u53ea\u6709\u5341\u5206\u4e4b\u4e00\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u5341\u5927\u526f\u7edf\u9886\u5462\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u5f53\u4e16\u7684\u82f1\u96c4\u8c6a\u6770\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u5341\u5927\u526f\u7edf\u9886\u7b97\u5f97\u4e0a\u4e00\u6d41\u9ad8\u624b\u7684\u7530\u5f53\u3001\u51cc\u4e91\u3001\u8427\u96e8\uff0c\u5c31\u5728\u8d35\u5983\u7684\u8eab\u4fa7\u542c \u547d\u3002\u53e6\u5916\uff0c\u7531\u6210\u90fd\u5206\u5b88\u5728\u5bab\u5899\u95e8\u5916\uff0c\u6211\u4eec\u7684\u8ba1\u5212\u662f\u8840\u6218\u5bab\u5899\u5916\uff0c\u4e0d\u8bb8\u4ed6\u4eec\u51b2\u5165\u5bab\u4e2d\u963b\u6270\u5723 \u9a7e\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5f88\u597d\uff01\u5723\u4e0a\u5bf9\u4f60\u5bc4\u671b\u751a\u6df1\uff0c\u4f60\u4e5f\u8d64\u80c6\u5fe0\u8d1e\uff0c\u4e00\u5fc3\u4fdd\u56fd\uff0c\u4f60\u4eec\u82e5\u76f8\u77e5\u76f8\u8bb8\uff0c \u5b9e\u4e5f\u662f\u4e00\u573a\u5343\u53e4\u4f73\u8bdd\u3002\u201d \u3000\u3000\u201c\u6210\u90fd\u5fc3\u6709\u4f59\uff0c\u552f\u6050\u529b\u4e0d\u8db3\u3002\u4ed6\u4eec\u4ee5\u6bd2\u5cf0\u7a7f\u9699\u800c\u5165\uff0c\u963b\u6321\u7684\u80fd\u529b\u5c31\u975e\u5e38\u7684\u5c0f\u4e86\uff0c\u4f55\u51b5\u521d \u5ea6\u548c\u8fd9\u6837\u7684\u6bd2\u7269\u5bf9\u9635\uff0c\u5168\u65e0\u6218\u9635\u7ecf\u9a8c\uff0c\u4e5f\u4e0d\u80fd\u5750\u89c6\u52c7\u58eb\u4eec\u6b7b\u5728\u6bd2\u8702\u4e4b\u4e0b\uff0c\u8ba9\u4ed6\u4eec\u5404\u51ed\u673a\u667a\u7684 \u907f\u4e00\u907f\uff0c\u654c\u4eba\u4e58\u865a\u800c\u5165\u7684\u673a\u4f1a\u5c31\u5927\u5927\u7684\u589e\u52a0\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u6709\u4e9b\u660e\u767d\u4e86\u3002 \u3000\u3000\u4f46\u5979\u5374\u6545\u4f5c\u4e0d\u77e5\uff0c\u9053\uff1a\u201c\u5c06\u519b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u54ea\u4e9b\u5251\u624b\u5728\u6bd2\u8702\u5f00\u9053\u4e0b\uff0c\u51b2\u5165\u5bab\u4e2d\uff0c\u60ca\u6270\u5230\u5723\u9a7e\u6050\u6240\u96be\u514d\uff0c\u6210\u90fd\u65e0\u80fd\uff0c\u8fd9\u4e9b\u8981\u4f9d\u9760\u8d35 \u5983\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u8bf4\u4e86\u534a\u5929\uff0c\u662f\u8981\u628a\u4fdd\u536b\u5bab\u5ef7\u7684\u5927\u4efb\u5168\u4ea4\u5230\u8881\u5b9d\u513f\u7684\u624b\u4e0a\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u5168\u90fd\u88ab\u4ed6\u4eec\u5957\u7262\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u8881\u7d2b\u70df\u7684\u610f\u601d\u5462\uff1f\u8fd8\u662f\u5b87\u6587\u6210\u90fd\u7b49\u8be1\u8ba1\u591a\u7aef\uff0c\u7ad9\u5728\u5c71\u524d\u89c2\u864e\u6597\uff0c\u6050\u6015\u884c\u4e0d\u901a \u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b87\u6587\u5c06\u519b\uff0c\u8981\u6211\u5982\u4f55\u4f5c\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u4f1a\u513f\u8881\u5b9d\u513f\u5c55\u73b0\u5a07\u5a9a\uff0c\u5de7\u7b11\u5029\u516e\uff0c\u52a8\u4eba\u5fc3\u5f26\u3002 \u3000\u3000\u679c\u7136\u5b87\u6587\u6210\u90fd\u5fc3\u5f26\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6210\u90fd\u600e\u6562\u5429\u5490\u8d35\u5983\uff0c\u4e00\u5207\u542c\u51ed\u8d35\u5983\u5429\u5490\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5982\u679c\u6211\u5168\u529b\u5bf9\u4ed8\u6bd2\u7269\uff0c\u4f60\u4eec\u662f\u5426\u6709\u628a\u63e1\u963b\u6321\u4f4f\u654c\u4eba\u5165\u5bab\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6211\u4eec\u5c3d\u529b\u6740\u654c\uff0c\u8840\u67d3\u5bab\u5899\uff0c\u6b7b\u800c\u540e\u5df2\u3002\u4f46\u5374\u4e0d\u6562\u5728\u8d35\u5983\u9762\u524d\u63d0\u51fa\u4efb\u4f55\u4fdd \u8bc1\uff0c\u6210\u90fd\u8ffd\u968f\u56fd\u5e08\u5317\u5f81\u4e4b\u540e\uff0c\u624d\u53d1\u89c9\u4eba\u5916\u6709\u4eba\uff0c\u5929\u5916\u6709\u5929\uff1b\u6614\u5e74\u5750\u4e95\u89c2\u5929\u7684\u81ea\u8d1f\u3001\u8c6a\u6c14\uff0c\u5df2 \u7ecf\u4e0d\u5b58\u5728\u4e86\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u95ee\u7684\u5e84\u4e25\uff0c\u4f46\u5b87\u6587\u6210\u90fd\u5374\u56de\u7b54\u5f97\u5a49\u8f6c\u6709\u81f4\u3002 \u3000\u3000\u8881\u5b9d\u513f\u63a5\u7740\u9053\uff1a\u201c\u5bf9\u6211\u5bc4\u671b\u5982\u6b64\u4e4b\u5927\uff0c\u53ea\u6015\u6211\u4e00\u4ecb\u5973\u6d41\u62c5\u8d1f\u4e0d\u8d77\u5440\uff01\u201d \u3000\u3000\u201c\u56fd\u5e08\u518d\u4e09\u63a8\u5d07\u5b9d\u8d35\u5983\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u9519\uff0c\u6210\u90fd\u7b49\u552f\u547d\u662f\u4ece\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u95ee\u9053\uff1a\u201c\u662f\u7d2b\u70df\u59d0\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u4e8b\u5230\u5173\u952e\u6240\u5728\uff0c\u5b87\u6587\u6210\u90fd\u6697\u91cc\u4e00\u5509\u5440\uff0c\u9053\uff1a\u201c\u662f\uff0c\u56fd\u5e08\u8fd9\u6837\u5429\u5490\u7684\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u201c\u55e4\u201d\u7684\u4e00\u58f0\u7b11\u4e86\uff01 \u3000\u3000\u8fd9\u4e00\u7b11\u56fa\u7136\u662f\u5a07\u5a9a\u6a2a\u751f\uff0c\u770b\u5f97\u5b87\u6587\u6210\u90fd\u3001\u8427\u96e8\u7b49\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u4e2a\u4e2d\u7684\u542b\u610f\uff0c\u66f4\u662f\u5145\u6ee1\u7740 \u795e\u79d8\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u548c\u8427\u96e8\u90fd\u65e0\u6cd5\u4e86\u89e3\uff0c\u5979\u4e3a\u4ec0\u4e48\u5982\u6b64\u9ad8\u5174\uff1f \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\uff01\u201d\u8881\u5b9d\u513f\u7b11\u5bb9\u5982\u82b1\u5730\u9053\uff1a\u201c\u5bf9\u6bd2\u7269\u7684\u653b\u51fb\uff0c\u5c06\u519b\u662f\u5426\u6709\u9632\u8303\u4e4b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d\u5b87\u6587\u6210\u90fd\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u6709\u51ed\u501f\u6b66\u529f\u548c\u5b83\u6297\u62d2\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5a07\u58f0\u9053\uff1a\u201c\u8981\u4e0b\u8981\u6211\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u53d7\u5ba0\u82e5\u60ca\u5730\u9053\uff1a\u201c\u5b9d\u8d35\u5983\uff0c\u53ef\u5df2\u6709\u4e86\u5bf9\u4ed8\u6bd2\u7269\u7684\u65b9\u6cd5\uff1f\u90a3\u771f\u662f\u5728\u4e0b\u548c\u5168\u90e8\u6b66\u58eb \u4e4b\u5e78\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5e2e\u5fd9\u561b\uff01\u6211\u62c5\u5fc3\u7684\u662f\u4e0d\u613f\u6740\u751f\uff0c\u4e00\u65e6\u8981\u5f00\u6740\u6212\u4e86\uff0c\u5c11\u6740\u548c\u591a\u6740\u4e5f\u5c31\u4e00\u6837 \u4e86\u3002\u201d \u3000\u3000\u201c\u5b9d\u8d35\u5983\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u90a3\u4e9b\u6bd2\u7269\u6b7b\u4e4b\u4f55\u60dc\uff0c\u4e3a\u4eba\u95f4\u9664\u5bb3\u4e5f\u7b97\u662f\u5f00\u6740\u6212\u5417\uff1f\u201d \u3000\u3000\u201c\u5b83\u4eec\u6709\u6bd2\uff0c\u4f46\u4e5f\u6709\u751f\u547d\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5b83\u4eec\u8fce\u7a7a\u98de\u821e\uff0c\u6d3b\u5f97\u5f88\u5feb\u4e50\uff0c\u6211\u5374\u8981\u5b83\u4eec\u65e0 \u58f0\u65e0\u606f\u7684\u5316\u4f5c\u6625\u6ce5\uff0c\u5509\u2026\u2026\u8fd9\u4e00\u6740\u5440\uff01 \u3000\u3000\u5c31\u662f\u5343\u767e\u4e07\u53ea\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u7a81\u7136\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5b9d\u8d35\u5983\uff0c\u53ea\u8981\u80fd\u6392\u9664\u6bd2\u7269\uff0c\u6210\u90fd\u76f8\u4fe1\u4ed6\u4eec\u51b2\u5165\u5bab\u4e2d \u7684\u673a\u4f1a\u5c31\u4e0d\u5927\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5feb\u53bb\u51c6\u5907\u8fce\u654c\u5427\uff01\u4ed6\u4eec\u7684\u653b\u52bf\u5373\u5c06\u5c55\u5f00\uff0c\u5982\u679c\u6211\u6551\u63f4\u4e0d\u53ca\uff0c\u5c31\u60f3\u529e\u6cd5\u901a\u77e5 \u6211\u4e00\u58f0\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5b50\uff0c\u6b65\u6b65\u751f\u83b2\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u770b\u4e0a\u53bb\u8881\u5b9d\u513f\u8d70\u5f97\u5f88\u6162\uff0c\u5176\u5b9e\u5341\u5206\u5feb\u901f\uff0c\u53ea\u89c1\u5979\u67f3\u8170\u6446 \u52a8\u4e86\u4e24\u4e0b\uff0c\u4eba\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8427\u96e8\u770b\u4e0d\u51fa\u90a3\u662f\u4ec0\u4e48\u6b66\u529f\uff1f \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e5f\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u201c\u4e0d\u662f\u6b66\u529f\u3002\u201d\u5b87\u6587\u6210\u90fd\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b66\u529f\u6ca1\u6709\u90a3\u4e48\u4e00\u4e2a\u5feb\u6cd5\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u7edf\u9886\u7684\u610f\u601d\u2026\u2026\u90a3\u662f\u672f\u6cd5\u4e86\uff0c\u662f\u9690\u8eab\u672f\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u770b\u4e0d\u61c2\u554a\uff1f\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u662f\u9690\u8eab\u672f\uff1f\u8fd8\u662f\u6f5c\u8e2a\u672f\uff1f \u3000\u3000\u4ee5\u5979\u884c\u8d70\u7684\u901f\u5ea6\u8ba1\u7b97\uff0c\u5979\u4e3e\u6b65\uff0c\u6211\u4eec\u51fa\u5200\uff0c\u5927\u6982\u5200\u5c06\u8fd1\u8eab\uff0c\u4eba\u5c31\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u7b11\u9053\uff1a\u201c\u83b2\u6b65\u6b3e\u6b3e\uff0c\u6447\u66f3\u751f\u59ff\u3002\u8c01\u53c8\u80fd\u5728\u80cc\u540e\u51fa\u5200\u5462\uff1f \u3000\u3000\u8fd9\u4e00\u6218\u7531\u8881\u56fd\u5e08\u548c\u5b9d\u8d35\u5983\u51fa\u9a6c\u76f8\u52a9\uff0c\u7edf\u9886\u5bf9\u80dc\u8d1f\u7684\u770b\u6cd5\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6218\u80dc\u5229\u53ef\u671f\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u5982\u4e0d\u80fd\u89c1\u673a\u800c\u4f5c\uff0c\u53ef\u80fd\u8981\u95f9\u5230\u5168\u519b\u5c3d\u6ca1\u3002 \u4e0d\u8fc7\uff0c\u674e\u4e16\u6c11\u5c11\u4e86\u866c\u9aef\u5ba2\u8fd9\u4e00\u80a1\u5236\u8861\u529b\u91cf\uff0c\u5c06\u4f1a\u8fc5\u901f\u58ee\u5927\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u7edf\u9886\uff0c\u65e0\u6bd2\u4e0d\u4e08\u592b\uff0c\u51fb\u6e83\u4e86\u866c\u9aef\u5ba2\u4e4b\u540e\uff0c\u7acb\u523b\u5f81\u4f10\u592a\u539f\uff0c\u4e58\u80dc\u514b\u654c\uff0c\u4e00\u9f13\u8361 \u5e73\u3002\u201d \u3000\u3000\u201c\u54ea\u6709\u5f81\u4f10\u7684\u519b\u529b\uff1f\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u56fd\u5e08\u53ef\u4ee5\u7528\u672f\u6cd5\u9000\u654c\uff0c\u53ea\u6015\u672a\u5fc5\u80af\u4ee5\u672f\u6cd5\u5f81\u4f10\u592a \u539f\u3002\u5b9d\u8d35\u5983\u66f4\u7edd\u4e0d\u53ef\u80fd\u4e86\uff0c\u5979\u4e0d\u53cd\u6208\u4e00\u51fb\uff0c\u5df2\u7b97\u7687\u6069\u6d69\u8361\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u592a\u539f\u8fd8\u6709\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4e24\u4f4d\u7cbe\u901a\u672f\u6cd5\u7684\u5947\u4eba\uff0c\u6b64\u756a\u866c\u9aef\u5ba2\u653b\u6253\u957f\u5b89\uff0c\u4e0d\u89c1\u674e\u9756 \u7edf\u5175\uff0c\u5f88\u53ef\u80fd\u674e\u9756\u4e5f\u4e3a\u674e\u4e16\u6c11\u6536\u4e3a\u5df2\u7528\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u674e\u4e16\u6c11\u771f\u7684\u5341\u5206\u53ef\u6015\u4e86\uff01\u90a3\u5c31\u66f4\u8981\u65e9\u4e9b\u4e0b\u624b\uff0c\u83ab\u8981\u4ed6\u7fbd\u7ffc\u4e30\u6ee1\uff0c \u5c3e\u5927\u4e0d\u6389\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6ca1\u6709\u56fd\u5e08\u963b\u6321\u866c\u9aef\u5ba2\u8fd9\u756a\u653b\u52bf\uff0c\u5927\u968b\u671d\u80fd\u5426\u8fc7\u8fd9\u4e00\u5173\uff0c\u5c31\u5f88\u96be\u9884\u6599\u3002\u6211 \u4eec\u6ca1\u6709\u53ef\u7528\u4e4b\u5175\uff0c\u54ea\u91cc\u6709\u5f81\u4f10\u654c\u4eba\u7684\u80fd\u529b\uff1f\u201d \u3000\u3000\u201c\u7edf\u9886\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u516d\u5341\u4e07\u5927\u519b\u56f4\u527f\u5404\u5904\u5578\u805a\u7684\u66b4\u6c11\uff0c\u4ed6\u4eec\u90fd\u662f\u7cbe\u9510\u4e4b\u5e08\uff0c\u8981\u4e0d\u8981\u62db \u4ed6\u56de\u5e08\u957f\u5b89\uff0c\u6574\u88c5\u518d\u53d1\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56f0\u96be\u5f88\u591a\uff0c\u8fd8\u5f97\u4ed4\u7ec6\u7814\u7a76\uff0c\u5b9d\u8d35\u5983\u4e34\u53bb\u7559\u8a00\uff0c\u662f\u8981\u6211\u4eec\u6c42 \u5979\u3002\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e0d\u4fbf\u4f5c\u4e3b\u610f\uff0c\u4f60\u4eec\u770b\u7740\u529e\u5427\uff01\u5e94\u4ed8\u4e0d\u4e86\uff0c\u5c31\u4e0d\u8981\u5927\u52c9\u5f3a\u3002\u81f3\u5c11\uff0c\u6211\u4eec\u90fd\u6ca1\u6709 \u5e94\u4ed8\u5343\u767e\u53ea\u4ee5\u4e0a\u6bd2\u7269\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u7edf\u9886\u6307\u6559\uff0c\u6211\u660e\u767d\u4e86\u3002\u201d\u8427\u96e8\u9053\uff1a\u201c\u5bab\u5899\u5916\u5982\u65e0\u6cd5\u6297\u62d2\u6bd2\u7269\u7684\u653b\u51fb\uff0c\u4e5f\u53ef\u4ee5\u5411\u5b9d \u8d35\u5983\u6c42\u63f4\uff0c\u603b\u7edf\u9886\u4e0d\u4fbf\u5f00\u53e3\uff0c\u4f55\u59a8\u8bf7\u4e00\u4f4d\u526f\u7edf\u9886\u6c42\u6551\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u53ea\u6709\u4ed6\u548c\u8427\u96e8\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u62ff\u7740\u5144\u5f1f\u4eec\u6027\u547d\u5f00\u73a9 \u7b11\uff0c\u4e5f\u5f88\u60f3\u89c1\u8bc6\u4e00\u4e0b\u5b9d\u8d35\u5983\u7684\u672c\u9886\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u6211\u81ea\u5df1\u4f1a\u5411\u5979\u6c42\u63f4\uff0c\u5f53\u7136\u5f97\u7ecf\u8fc7\u4e00\u756a\u93d6\u6218\u4e4b\u540e\u3002\u201d \u3000\u3000\u8427\u96e8\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5c5e\u4e0b\u53bb\u548c\u7530\u5f53\u3001\u51cc\u4e91\u7814\u5546\u4e00\u4e0b\u62d2\u654c\u4e4b\u7b56\uff0c\u4e5f\u4f1a\u5411\u5b9d\u8d35\u5983\u8bf7\u6559\u5bf9\u654c\u4e4b \u6cd5\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\u3002 \u3000\u3000\u8427\u96e8\u9000\u5165\u5bab\u5ef7\u3002 \u3000\u3000\u4f46\u8427\u96e8\u6765\u4e0d\u53ca\u65bd\u5c55\u76d8\u7b97\u597d\u7684\u8ba1\u5212\uff0c\u8033\u9645\u95f4\u5df2\u54cd\u8d77\u4e86\u5435\u4eba\u7684\u55e1\u55e1\u4e4b\u58f0\uff0c\u5927\u6279\u6bd2\u8702\u653b\u4e86\u8fdb \u6765\u3002 \u3000\u3000\u8fd9\u663e\u7136\u662f\u6b66\u58eb\u4eec\u65e0\u6cd5\u963b\u6b62\u7684\u4e8b\uff0c\u56e0\u4e3a\u672a\u95fb\u5f97\u5bab\u5899\u5916\u559d\u6b62\u547c\u53eb\u4e4b\u58f0\uff0c\u6bd2\u5cf0\u5df2\u98de\u5165\u5bab\u5899\u3002 \u3000\u3000\u8427\u96e8\u5927\u58f0\u559d\u9053\uff1a\u201c\u6bd2\u5cf0\u6765\u88ad\uff0c\u8bf8\u4f4d\u8981\u5c0f\u5fc3\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u53eb\uff0c\u5f62\u52bf\u7acb\u523b\u5927\u53d8\u2014\u2014 \u3000\u3000\u5bab\u5a25\u3001\u5f69\u5973\u4eec\u6284\u8d77\u4e86\u5bb6\u4f19\uff0c\u51c6\u5907\u62cd\u6253\u6bd2\u8702\u3002\u9632\u5b88\u5728\u662d\u9633\u9662\u5916\u7684\u6b66\u58eb\u4eec\uff0c\u5200\u5251\u867d\u5df2\u51fa\u9798\uff0c \u4eba\u5374\u8eb2\u5728\u84ec\u5e10\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8272\u662f\u5927\u4eae\u524d\u7684\u4e00\u6bb5\u9ed1\u6697\uff0c\u9664\u4e86\u662d\u9633\u5bab\u9662\u706f\u706b\u5982\u663c\uff0c\u5176\u4ed6\u5730\u65b9\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6574\u4e2a\u7687\u5bab\u4e2d\u7684\u5983\u5b50\u3001\u5bab\u5973\uff0c\u5927\u90fd\u96c6\u4e2d\u5230\u662d\u9633\u5bab\u9662\u4e2d\u6765\uff0c\u5bab\u4e2d\u7684\u9632\u536b\u4e5f\u4ee5\u6b64\u4e3a\u91cd \u70b9\u3002 \u3000\u3000\u8fd9\u91cc\u8102\u7c89\u9999\u6d53\u3002 \u3000\u3000\u8fd9\u91cc\u4eba\u6c14\u8302\u76db\u3002 \u3000\u3000\u6210\u7fa4\u7ed3\u961f\u7684\u6bd2\u5cf0\u4e5f\u56e0\u95fb\u5230\u4eba\u8eab\u900f\u53d1\u51fa\u7684\u6d53\u91cd\u6c14\u606f\u800c\u6765\u3002 \u3000\u3000\u8427\u96e8\u53f7\u79f0\u98d8\u82b1\u5251\uff0c\u4ee5\u5251\u52bf\u5feb\u901f\u8457\u79f0\u6c5f\u6e56\u3002\u5fc3\u4e2d\u6709\u70b9\u4e0d\u4fe1\u90aa\uff0c\u5251\u5982\u95ea\u5149\u6d41\u8f6c\uff0c\u821e\u6210\u4e86\u4e00\u56e2 \u5149\u5f71\u767d\u8292\uff0c\u8fce\u5411\u8702\u7fa4\u3002 \u3000\u3000\u98de\u6765\u7684\u679c\u7136\u662f\u6bd2\u8702\u3002 \u3000\u3000\u662f\u4e00\u79cd\u6781\u5c11\u89c1\u5230\u7684\u6bd2\u8702\u3002 \u3000\u3000\u957f\u903e\u5c0f\u6307\uff0c\u53cc\u7ffc\u5c55\u5f00\uff0c\u5bbd\u903e\u4e09\u5bf8\uff0c\u5168\u8eab\u8c08\u9ed1\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u662f\u4e00\u79cd\u5947\u6bd2\u4e4b\u7269\uff0c\u87ab\u4e00\u4e0b\uff0c\u4e0d \u6b7b\u4e5f\u8981\u4f60\u534a\u6761\u8001\u547d\u3002 \u3000\u3000\u8427\u96e8\u7684\u5251\u6cd5\u679c\u7136\u5feb\u901f\uff0c\u5251\u5149\u5982\u8f6e\uff0c\u6bd2\u8702\u7eb7\u7eb7\u5760\u843d\u3002 \u3000\u3000\u7247\u523b\u95f4\uff0c\u8702\u5c38\u903e\u767e\u3002 \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u5df2\u8eb2\u5165\u4e86\u7eb1\u5e10\u4e4b\u4e2d\u3002 \u3000\u3000\u4f46\u89c1\u8427\u96e8\u72ec\u5251\u6b8b\u8702\u3002 \u3000\u3000\u7530\u5f53\u4f4e\u58f0\u9053\uff1a\u201c\u51cc\u5144\uff0c\u8bf7\u7559\u5728\u5e10\u4e2d\u6307\u6325\uff0c\u6211\u53bb\u52a9\u8427\u5144\u4e00\u81c2\u4e4b\u529b\uff0c\u63a5\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u8427\u96e8\u4e5f\u786e\u5b9e\u88ab\u6bd2\u8702\u56f0\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u6bd2\u8702\u4f53\u578b\u5de8\u5927\uff0c\u800c\u4e14\u608d\u4e0d\u754f\u6b7b\u3002 \u3000\u3000\u8427\u96e8\u5251\u52bf\u7a0d\u6709\u7a7a\u9699\uff0c\u5b83\u4eec\u5c31\u62fc\u547d\u51b2\u5165\u3002 \u3000\u3000\u8427\u96e8\u5fc3\u4e2d\u6709\u70b9\u614c\u4e86\uff0c\u5fd6\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e0d\u6b7b\u4e8e\u6218\u9635\u3001\u5200\u5251\u4e4b\u4e0b\uff0c\u88ab\u51e0\u53ea\u6bd2\u8702\u87ab\u6b7b\uff0c\u90a3\u53ef\u80fd \u4e0d\u7791\u76ee\u554a\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u8f6c\u52a8\uff0c\u4e00\u4e2a\u95ea\u795e\uff0c\u4e00\u53ea\u6bd2\u8702\u4e58\u865a\u800c\u5165\u3002 \u3000\u3000\u8427\u96e8\u5de6\u80a9\u62ab\u87ab\u4e86\u4e00\u4e0b\uff0c\u611f\u89c9\u4e00\u9635\u5947\u75bc\u523a\u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u4e5f\u628a\u8427\u96e8\u75db\u9192\u4e86\u3002 \u3000\u3000\u4f7f\u4ed6\u8b66\u89c9\u5230\u6b63\u5904\u5728\u751f\u6b7b\u5173\u952e\u65f6\u523b\uff0c\u53ea\u8981\u5251\u62db\u518d\u4e00\u7f13\u6162\uff0c\u6bd2\u8702\u653b\u5165\uff0c\u518d\u88ab\u4e24\u53ea\u87ab\u4e2d\uff0c\u5373\u5c06 \u4e2d\u6bd2\u800c\u4ea1\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e0d\u80fd\u5927\u610f\u554a\uff01 \u3000\u3000\u8427\u96e8\u4e00\u9762\u8fd0\u6c14\u6b62\u75db\uff0c\u4e00\u9762\u5168\u529b\u6325\u5251\uff0c\u5de6\u624b\u884c\u6c14\u4f3c\u73e0\uff0c\u4e00\u628a\u6293\u4e86\u4e0b\u53bb\uff0c\u628a\u722c\u5411\u9888\u95f4\u7684\u6bd2\u5cf0 \u634f\u6b7b\u3002 \u3000\u3000\u7530\u5f53\u53cc\u5200\u8fd0\u8f6c\u5982\u98de\uff0c\u5316\u6210\u4e00\u7247\u5200\u8292\uff0c\u6eda\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u5927\u58f0\u559d\u9053\uff1a\u201c\u8427\u5144\uff0c\u9760\u8fc7\u6765\uff0c\u56de\u5165\u84ec\u5e10\u4e4b\u4e2d\uff0c\u54b1\u4eec\u8fd8\u8981\u7559\u4e0b\u6c14\u529b\u5bf9\u4ed8\u654c\u4eba\u5251 \u624b\u3002\u201d \u3000\u3000\u8427\u96e8\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u5251\u5149\u79fb\u52a8\uff0c\u7f13\u7f13\u5411\u7530\u5f53\u9760\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u56de\u7b54\uff0c\u4ed6\u8981\u8fd0\u6c14\u6b62\u75bc\u3002 \u3000\u3000\u7530\u5f53\u5200\u8292\u6269\u5c55\uff0c\u66ff\u8427\u96e8\u5f00\u4e86\u4e00\u6761\u8def\uff0c\u8ba9\u8427\u96e8\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u7530\u5f53\u672c\u8eab\u5374\u7acb\u523b\u9762\u5bf9\u4e86\u6bd2\u8702\u7684\u538b\u529b\u3002 \u3000\u3000\u8427\u96e8\u6321\u5728\u524d\u9762\u65f6\uff0c\u5c1a\u4e0d\u89c9\u5f97\uff0c\u4f46\u8427\u96e8\u4e00\u9000\u4e0b\uff0c\u7530\u5f53\u5373\u53d1\u89c9\u6210\u5343\u4e0a\u4e07\u7684\u6bd2\u8702\u76f4\u649e\u8fc7\u6765\uff0c\u9ed1 \u6726\u6726\u7684\uff0c\u50cf\u4e00\u7247\u538b\u4e0b\u6765\u7684\u4e4c\u4e91\uff0c\u6c14\u52bf\u5341\u5206\u9a87\u4eba\u3002 \u3000\u3000\u4e0d\u77e5\u6709\u591a\u5c11\u6bd2\u8702\u51b2\u5165\u4e86\u662d\u9633\u9662\u4e2d\uff1f \u3000\u3000\u8033\u9645\u5df2\u54cd\u8d77\u4e86\u4e24\u58f0\u5c16\u5389\u7684\u60e8\u53eb\uff01 \u3000\u3000\u5e94\u662f\u6bd2\u8702\u627e\u5230\u4e86\u4e00\u4e2a\u7a7a\u9699\uff0c\u8fdb\u5165\u5ba4\u4e2d\u87ab\u4f24\u4e86\u4e24\u4e2a\u5bab\u5973\u3002 \u3000\u3000\u8427\u96e8\u4eba\u5df2\u63a5\u8fd1\u7bf7\u5e10\u3002 \u3000\u3000\u4f46\u662f\u7530\u5f53\u5374\u65e0\u6cd5\u9000\u4e86\uff0c\u6bd2\u8702\u867d\u7136\u88ab\u7530\u5f53\u7684\u53cc\u5200\u6740\u5f97\u5c38\u5982\u843d\u96e8\uff0c\u4f46\u4e5f\u628a\u7530\u5f53\u7ed9\u56e2\u56e2\u56f4\u4f4f \u4e86\u3002 \u3000\u3000\u5934\u548c\u56db\u5468\u7684\u7a7a\u95f4\u5168\u662f\u6bd2\u8702\uff0c\u6574\u4e2a\u88ab\u6bd2\u5cf0\u9694\u7edd\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u6d8c\u5165\u7684\u6bd2\u8702\u4e4b\u591a\uff0c\u4f3c\u5df2\u5230\u4e86\u906e\u5929\u853d\u65e5\u7684\u5883\u754c\u3002 \u3000\u3000\u65e5\u672a\u5347\u8d77\uff0c\u4f46\u6570\u767e\u76cf\u5bab\u706f\u4e0a\u5168\u90fd\u722c\u6ee1\u4e86\u6bd2\u8702\uff0c\u706f\u5149\u90fd\u88ab\u6321\u4f4f\u4e86\u3002 \u3000\u3000\u84ec\u5e10\u5916\u3001\u95e8\u7a97\u4e0a\u4e5f\u90fd\u88ab\u6bd2\u8702\u722c\u6ee1\u4e86\u3002 \u3000\u3000\u5929\u554a\uff01 \u3000\u3000\u6574\u4e2a\u7684\u662d\u9633\u5bab\u88ab\u6bd2\u8702\u56f4\u56f0\u4e86\u3002 \u3000\u3000\u5982\u6b64\u4f17\u591a\u7684\u5de8\u578b\u5947\u8702\uff0c\u770b\u5f97\u8427\u96e8\u5fc3\u5934\u751f\u51fa\u4e86\u5bd2\u610f\u3002 \u3000\u3000\u4ed6\u5e94\u8be5\u548c\u7530\u5f53\u5200\u5251\u8054\u624b\u62d2\u6321\u6bd2\u8702\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e0d\u6562\u518d\u5411\u524d\u79fb\u52a8\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5927\u6279\u6bd2\u8702\u98de\u884c\u7a7f\u68ad\uff0c\u8427\u96e8\u4e5f\u65e0\u6cd5\u79fb\u52a8\uff0c\u5de6\u80a9\u7684\u5de8\u75bc\u4e5f\u5f71\u54cd\u4e86\u4ed6\u7684\u52c7\u6c14\uff0c\u66f4\u5f71\u54cd \u4e86\u4ed6\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u5c31\u7b97\u60f3\u79fb\u52a8\u4e5f\u79fb\u52a8\u4e0d\u4e86\uff0c\u56e0\u4e3a\u7530\u5f53\u7684\u5200\u5149\u6321\u4f4f\u4e86\u4e03\u6210\u4ee5\u4e0a\u7684\u6bd2\u8702\u3002 \u3000\u3000\u4f46\u8427\u96e8\u5fc3\u4e2d\u660e\u767d\uff0c\u5728\u90a3\u79cd\u5de8\u5927\u7684\u538b\u529b\u4e4b\u4e0b\uff0c\u7530\u5f53\u662f\u65e0\u6cd5\u6301\u4e45\u7684\u3002\u81ea\u5df1\u65e0\u6cd5\u6551\u63f4\uff0c\u51cc\u4e91\u548c \u4f17\u6b66\u58eb\u4e5f\u96be\u65bd\u6551\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u62fc\u547d\u5373\u80fd\u529e\u5230\u7684\u4e8b\u3002 \u3000\u3000\u8427\u96e8\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u4e3a\u4e86\u6551\u7530\u5f53\uff0c\u6c42\u6c42\u5b9d\u8d35\u5983\u5427\uff01 \u3000\u3000\u5979\u53ef\u80fd\u5c31\u5728\u9644\u8fd1\u3002 \u3000\u3000\u4f46\u5982\u4e0d\u5f00\u53e3\u6c42\u5979\uff0c\u770b\u6837\u5b50\u5979\u662f\u4e0d\u4f1a\u73b0\u8eab\u51fa\u624b\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u5927\u58f0\u559d\u9053\uff1a\u5b9d\u8d35\u5983\uff0c\u8427\u96e8\u3001\u7530\u5f53\u56f0\u4e8e\u6bd2\u8702\uff0c\u8eab\u9677\u5371\u5883\uff0c\u8bf7\u8d50\u63f4\u624b\u3002 \u3000\u3000\u53ea\u542c\u8881\u5b9d\u513f\u5a07\u751c\u7684\u58f0\u97f3\u5e94\u9053\uff1a\u201c\u8427\u5c06\u519b\uff0c\u6bd2\u8702\u53ef\u6076\uff0c\u6211\u5df2\u51b3\u5fc3\u9664\u5b83\uff0c\u8bf7\u518d\u591a\u6491\u7247\u523b\u3002\u201d \u3000\u3000\u8427\u96e8\u957f\u5251\u6025\u821e\uff0c\u628a\u4e00\u4e9b\u8fd1\u8eab\u7684\u6bd2\u8702\u65a9\u843d\u4e8e\u5730\u3002 \u3000\u3000\u7a81\u95fb\u7530\u5f53\u5927\u58f0\u559d\u9053\uff1a\u201c\u6211\u5df2\u88ab\u6bd2\u8702\u6240\u4f24\uff0c\u5c31\u8981\u652f\u6491\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u8427\u5144\uff01\u5feb\u8bf7\u8eb2\u5165\u84ec\u5e10\uff0c\u5144\u5f1f\u62fc\u5c3d\u6700\u540e\u4e00\u53e3\u771f\u6c14\u4e3a\u4f60\u963b\u6321\u8702\u7fa4\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u7530\u5144\uff0c\u8981\u6b7b\u5c31\u6b7b\u5728\u4e00\u8d77\uff0c\u7530\u5144\u4e3a\u6551\u6211\u800c\u4f24\uff0c\u8427\u67d0\u5982\u4f55\u80fd\u770b\u7740\u4f60\u72ec\u6b7b\u4e8e\u8702\u6bd2\u4e4b \u4e0b\uff1f\u201d \u3000\u3000\u201c\u4e24\u4f4d\u90fd\u4e0d\u4f1a\u6b7b\uff0c\u53ea\u8981\u591a\u5fcd\u8010\u7247\u523b\u75db\u82e6\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u5979\u51cc\u7a7a\u800c\u6765\uff0c\u98de\u7a7f\u4e8e\u6bd2\u8702\u7fa4\u4e2d\u3002 \u3000\u3000\u4e0d\u77e5\u4f55\u6545\uff0c\u6bd2\u8702\u7eb7\u7eb7\u907f\u5f00\uff0c\u884c\u8fd1\u8427\u96e8\uff0c\u843d\u7740\u5b9e\u5730\uff0c\u53cc\u8896\u4e00\u6325\uff0c\u4e00\u9635\u4f3c\u4e91\u4f3c\u96fe\u7684\u70df\u6c14\uff0c\u6269 \u5c55\u5f00\u53bb\u3002 \u3000\u3000\u5e26\u7740\u4e00\u80a1\u5bd2\u610f\u548c\u96e8\u4e1d\u822c\u7684\u51b7\u7c92\uff0c\u6bd2\u8702\u7eb7\u7eb7\u843d\u4e0b\u3002 \u3000\u3000\u597d\u50cf\u4e00\u79cd\u6781\u5fae\u5c0f\u7684\u6697\u5668\uff0c\u4f46\u5b83\u5374\u4e0d\u4f24\u4eba\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u90fd\u611f\u53d7\u5230\u4e00\u9635\u51b7\u98ce\u7531\u8eab\u4fa7\u5439\u8fc7\uff0c\u51b7\u51b7\u96e8\u7c92\u6253\u5728\u8138\u4e0a\uff0c\u5468\u56f4\u7684\u6bd2\u8702\u5374\u5982\u96ea\u4e0b\u6eda \u6c64\uff0c\u5316\u4f5c\u4e4c\u6709\u3002 \u3000\u3000\u5343\u767e\u53ea\u6bd2\u8702\u6cf0\u5c71\u538b\u9876\u7684\u6c14\u52bf\uff0c\u5ffd\u4f53\u6d88\u5931\u4e86\u3002 \u3000\u3000\u8702\u5c38\u5760\u5730\uff0c\u539a\u79ef\u5bf8\u8bb8\u4e4b\u9ad8\u3002 \u3000\u3000\u4e5f\u8bb8\u6709\u4e9b\u4fa5\u5e78\u672a\u6b7b\u7684\uff0c\u4f46\u5df2\u9003\u4e4b\u592d\u592d\uff0c\u98de\u5f97\u65e0\u5f71\u65e0\u8e2a\u4e86\u3002 \u3000\u3000\u8427\u96e8\u5446\u5446\u5730\u671b\u7740\u5c0f\u7f8e\u4eba\u2014\u2014\u8881\u5b9d\u513f\u3002 \u3000\u3000\u53ea\u89c1\u5979\u5de6\u624b\u6258\u7740\u4e00\u4e2a\u7389\u74f6\uff0c\u5927\u6709\u6551\u4e16\u6551\u96be\u89c2\u97f3\u83e9\u8428\u7684\u6c14\u52bf\u3002 \u3000\u3000\u8427\u96e8\u4e0d\u7981\u8eac\u8eab\u4e0b\u62dc\u3002 \u3000\u3000\u201c\u5c06\u519b\u8bf7\u8d77\u3002\u201d\u8881\u5b9d\u513f\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u513f\u6709\u4e39\u4e38\u4e24\u9897\uff0c\u4e0b\u77e5\u9053\u662f\u5426\u5bf9\u75c7\uff0c\u4e24\u4f4d\u8bf7\u670d \u4e0b\u8bd5\u8bd5\u5427\uff01\u201d \u3000\u3000\u5979\u4f38\u51fa\u4e86\u767d\u7389\u822c\u7684\u7ea4\u5de7\u53f3\u624b\uff0c\u638c\u5fc3\u4e2d\u6258\u7740\u4e24\u7c92\u767d\u8272\u4e39\u4e38\u3002 \u3000\u3000\u8427\u96e8\u5341\u5206\u606d\u656c\u5730\u53d6\u8fc7\u4e39\u4e38\uff0c\u81ea\u5df1\u541e\u4e0b\u4e00\u9897\uff0c\u53e6\u4e00\u9897\u6295\u5165\u4e86\u7530\u5f53\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u8bdd\u8bf4\u5f97\u867d\u7136\u5ba2\u6c14\uff0c\u4f46\u836f\u53ef\u662f\u7075\u4e39\u5999\u836f\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u541e\u4e0b\u53bb\uff0c\u75bc\u75db\u5373\u6b62\u3002 \u3000\u3000\u201c\u591a\u8c22\u8d35\u5983\u6551\u63f4\uff01\u201d \u3000\u3000\u7530\u5f53\u3001\u8427\u96e8\u9f50\u9f50\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u201c\u5929\u5df2\u5927\u4eae\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u5982\u679c\u6211\u7684\u63a8\u60f3\u4e0d\u9519\uff0c\u5927\u6279\u6740\u624b\u5373\u5c06\u6d8c\u5165\u5bab\u5ef7\uff0c\u5b87\u6587\u5c06\u519b\u80fd \u591f\u963b\u4e8e\u5bab\u5899\u4e4b\u5916\u7684\uff0c\u6700\u591a\u534a\u6570\u3002\u662d\u9633\u9662\u4e2d\u6570\u767e\u4f4d\u5bab\u5a25\u5f69\u5973\u3001\u624d\u4eba\u3001\u5983\u5b50\u3001\u7687\u540e\u3001\u7687\u4e0a\uff0c\u8fd8\u987b \u8bf8\u4f4d\u4fdd\u62a4\uff0c\u8fd8\u6709\u7247\u523b\u65f6\u95f4\uff0c\u4e24\u4f4d\u53ef\u4ee5\u8c03\u606f\u4e00\u9635\u3002\u201d \u3000\u3000\u8427\u96e8\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5b87\u6587\u5c06\u519b\u7edf\u5e05\u7684\u662f\u5bab\u536b\u4e03\u6210\u4ee5\u4e0a\u7684\u5b9e\u529b\uff0c\u5982\u679c\u4ed6\u4eec\u53ea\u80fd\u963b\u6b62\u534a\u6570 \u6740\u624b\uff0c\u8fd9\u662d\u9633\u9662\u5c31\u5341\u5206\u5371\u6025\u4e86\u3002\u5c5e\u4e0b\u7b49\u548c\u4f17\u5bab\u536b\u6218\u6b7b\u4e8b\u5c0f\uff0c\u60ca\u52a8\u4e86\u7687\u4e0a\u3001\u7687\u540e\uff0c\u90a3\u5c31\u7f6a\u8be5\u4e07 \u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\uff01\u201d\u8881\u5b9d\u513f\u5a07\u5a9a\u6a2a\u751f\u7b11\u9053\uff1a\u201c\u522b\u5fd8\u4e86\uff0c\u8fd8\u6709\u6211\u5440\uff01\u6211\u4f1a\u52a9\u51e0\u4f4d\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u6709\u8d35\u5983\u8fd9\u53e5\u5429\u5490\uff0c\u5c5e\u4e0b\u5c31\u5b89\u5fc3\u591a\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c06\u519b\u5c3d\u7ba1\u653e\u624b\u5bf9\u4ed8\u6765\u4eba\uff0c\u6211\u4f1a\u5728\u6697\u4e2d\u76f8\u52a9\u3002\u201d \u3000\u3000\u201c\u8d35\u5983\u2026\u2026\u201d \u3000\u3000\u8427\u96e8\u53eb\u51fa\u4e86\u201c\u8d35\u5983\u201d\u4e24\u4e2a\u5b57\uff0c\u7a81\u53c8\u82e6\u7b11\u4f4f\u53e3\u4e0d\u8a00\u4e86\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u5374\u4e0d\u653e\u8fc7\uff0c\u8ffd\u7740\u95ee\u9053\uff1a\u201c\u8427\u5c06\u519b\u60f3\u8bf4\u4ec0\u4e48\uff1f\u4f46\u8bf7\u51fa\u53e3\uff0c\u6211\u4e0d\u4f1a\u602a\u4f60\uff0c\u4e5f\u4e0d\u4f1a\u544a \u8bc9\u7687\u4e0a\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u5c31\u66f2\u6298\u6709\u81f4\u4e86\u3002 \u3000\u3000\u8427\u96e8\u5fc3\u5934\u9707\u52a8\u6781\u4e86\uff0c\u54ea\u91cc\u6562\u7acb\u523b\u56de\u7b54\uff1f \u3000\u3000\u4ed6\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u7684\u610f\u601d\u662f\u6211\u7b49\u662f\u5426\u8981\u8eab\u9677\u5371\u5883\u65f6\uff0c\u518d\u5411\u8d35\u5983\u6c42\u52a9\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u201d\u8881\u5b9d\u513f\u4f38\u51fa\u4e86\u7ea4\u5de7\u7684\u53f3\u624b\u98df\u6307\uff0c\u6307\u7740\u8427\u96e8\u7684\u9f3b\u5c16\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4f1a\u5e2e\u4f60\uff0c \u4f46\u6211\u559c\u6b22\u4f60\u53eb\u6211\uff0c\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u8427\u96e8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8427\u96e8\u4e00\u70b9\u4e5f\u4e0d\u660e\u767d\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u8d35\u5983\u5728\u8bf1\u60d1\u4ed6\uff0c\u8ba9\u4ed6\u5fc3\u4e2d\u5174\u594b\uff0c\u60f3\u53bb\u72af\u7f6a\u3002 \u3000\u3000\u6307\u5c31\u6307\u5427\uff01 \u3000\u3000\u4f46\u6307\u5c16\u513f\u78b0\u5728\u9f3b\u5b50\u4e0a\uff0c\u5c31\u6709\u70b9\u6d53\u91cd\u7684\u6311\u9017\u610f\u5473\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u786e\u662f\u5728\u65bd\u5c55\u9b45\u529b\uff0c\u5927\u5c40\u53d8\u5316\uff0c\u5730\u52a8\u5929\u6447\uff0c\u5979\u5e0c\u671b\u6536\u7f57\u51e0\u4e2a\u5fc3\u8179\uff0c\u5efa\u7acb\u8d77\u901a\u8fbe\u5185\u5916 \u6d88\u606f\u7684\u6e20\u9053\u3002 \u3000\u3000\u8fd9\u81ea\u975e\u4e00\u671d\u4e00\u5915\u53ef\u4ee5\u6210\u529f\uff0c\u4f46\u6700\u5feb\u7684\u529e\u6cd5\u5c31\u662f\u65bd\u5c55\u5979\u5929\u8d4b\u7684\u9b45\u529b\uff0c\u8ba9\u7537\u4eba\u4e3a\u5979\u9676\u9189\uff0c\u4e3a \u5979\u6548\u547d\u3002 \u3000\u3000\u5979\u9009\u4e0a\u4e86\u7b2c\u4e00\u4e2a\u4e0b\u624b\u7684\u5bf9\u8c61\u2014\u2014\u8427\u96e8\u3002 \u3000\u3000\u4eba\u4e0d\u80fd\u5b66\u5947\u672f\u3001\u90aa\u6cd5\uff0c\u4e00\u5b66\u4f1a\u4e86\u5373\u4f1a\u52a8\u90aa\u5ff5\u3001\u8d77\u90aa\u5fc3\uff1b\u800c\u90aa\u9053\u5f80\u5f80\u53c8\u662f\u901a\u8fbe\u76ee\u7684\u7684\u6377 \u5f84\u3002 \u3000\u3000\u50cf\u8881\u7d2b\u70df\u3001\u8881\u5b9d\u513f\u8fd9\u7b49\u5929\u4eba\u4e4b\u95f4\u7684\u4e00\u7b49\u7f8e\u5973\uff0c\u4e5f\u56e0\u4e3a\u5b66\u4f1a\u4e86\u90aa\u6cd5\u5947\u672f\uff0c\u800c\u4e0d\u80fd\u5b89\u5206\u4e0b \u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u534a\u771f\u534a\u5047\u7684\u63a7\u5236\u4e86\u5b87\u6587\u6210\u90fd\u3002 \u3000\u3000\u8881\u5b9d\u513f\u4e5f\u60f3\u4f7f\u6cd5\u9009\u62e9\u4e86\u8427\u96e8\u3002 \u3000\u3000\u5982\u679c\u4e24\u4eba\u90fd\u662f\u5e7c\u8bfb\u8bd7\u4e66\u3001\u5ead\u8bad\u68ee\u4e25\u7684\u540d\u95e8\u95fa\u79c0\uff0c\u7edd\u4e0d\u4f1a\u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\u3002 \u3000\u3000\u81ea\u7518\u843d\u82b1\u9010\u6d41\u6c34\uff0c\u8f7b\u6446\u7f57\u8863\u6697\u9001\u9999\u3002 \u3000\u3000\u8881\u5b9d\u513f\u56de\u5934\u8d70\u4e86\u3002 \u3000\u3000\u8427\u96e8\u5374\u5446\u5446\u5730\u671b\u7740\u8fdc\u53bb\u7684\u80cc\u5f71\u51fa\u795e\u3002 \u3000\u3000\u201c\u8427\u5144\uff0c\u5979\u4f3c\u662f\u6709\u610f\u7684\u6311\u9017\u4f60\u5440\uff01\u201d\u7530\u5f53\u4f4e\u58f0\u9053\uff1a\u201c\u5b9d\u8d35\u5983\u5fc3\u91cc\u5728\u6253\u7b97\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u201d\u8427\u96e8\u9053\uff1a\u201c\u89e6\u72af\u738b\u5983\uff0c\u53ef\u662f\u6740\u5934\u7684\u5927\u7f6a\uff0c\u5979\u7a76\u7adf\u662f\u4ec0\u4e48\u7528\u5fc3\u5462\uff1f\u201d \u3000\u3000\u7530\u5f53\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u771f\u6b63\u80fd\u4e00\u89c2\u82b3\u6cfd\uff0c\u4f60\u5c31\u5b89\u5fc3\u53bb\u6b7b\u5427\uff01\u6b7b\u5f97\u7791\u76ee\u554a\uff01\u6015\u7684\u662f\u7f8a\u8089\u6ca1\u5403 \u5230\uff0c\u6cbe\u4e86\u4e00\u8eab\u7f8a\u9a9a\u5473\uff0c\u843d\u4e2a\u8111\u888b\u642c\u5bb6\uff0c\u90a3\u5c31\u5212\u4e0d\u6765\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u4e00\u62cd\u8111\u888b\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u5bf9\u5440\uff01\u6211\u5df2\u7ecf\u88ab\u5979\u6405\u5f97\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u8981\u5c0f\u5fc3\u5e94\u4ed8\u4e86\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u51b7\u5bab\u5c81\u6708\uff0c\u6e21\u65e5\u5982\u5e74\u3002 \u3000\u3000\u540d\u867d\u5983\u5b50\u3001\u624d\u4eba\uff0c\u4f46\u5341\u5e74\u516b\u5e74\u4e5f\u96be\u89c1\u7687\u4e0a\u4e00\u9762\uff0c\u5979\u4eec\u7ed3\u4ea4\u5916\u4eba\uff0c\u503c\u5f97\u540c\u60c5\u3002\u4f46\u5b9d\u8d35\u5983\u4e0d \u4e00\u6837\u554a\uff0c\u5979\u5e1d\u7737\u6b63\u9686\uff0c\u600e\u4f1a\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\u8bdd\uff0c\u76f4\u6447\u7740\u5934\u3002 \u3000\u3000\u8427\u96e8\u63a5\u7740\u9053\uff1a\u201c\u5bf9\uff01\u662f\u522b\u6709\u76ee\u7684\u4e86\uff0c\u4f46\u76ee\u7684\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u7530\u5f53\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5979\u8981\u5f81\u670d\u4f60\uff0c\u4e0d\u53ea\u662f\u4f60\u7684\u4eba\uff0c\u4e5f\u8981\u4f60\u7684\u5fc3\uff0c\u8981\u4f60\u5168\u5fc3\u5168\u610f\u5730\u6548\u5fe0 \u5979\uff0c\u5fc3\u65e0\u4e8c\u5fd7\u5730\u6548\u5fe0\u5979\u3002\u201d \u3000\u3000\u8427\u96e8\u9605\u5386\u4e30\u5bcc\uff0c\u7acb\u523b\u660e\u65e5\u4e86\u7530\u5f53\u8a00\u4e2d\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u968b\u5bab\u4e2d\uff0c\u4e24\u4e2a\u8881\u666f\u5983\u5404\u5177\u7edd\u8272\uff0c\u8881\u7d2b\u70df\u8d35\u5983\u8eab\u517c\u56fd\u5e08\uff0c\u4f7f\u54b1\u4eec\u5934\u513f\u52a8 \u5fc3\uff0c\u60c5\u7518\u6548\u547d\uff0c\u5bf9\u7d2b\u70df\u8d35\u5983\u7684\u5fe0\u8bda\uff0c\u6050\u4e0d\u5728\u7687\u4e0a\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u7530\u5f53\u9053\uff1a\u201c\u5b9d\u8d35\u5983\u60f3\u62c9\u62e2\u4f60\uff0c\u6211\u62c5\u5fc3\u6709\u4e00\u5929\u56fd\u5e08\u548c\u5b9d\u8d35\u5983\u8d77\u4e86\u51b2\u7a81\uff0c\u4f60\u548c\u5934\u513f\u5404\u4e3a\u4e00 \u4eba\uff0c\u600e\u4e48\u5904\u4e0b\u53bb\u5462\uff1f\u201d \u3000\u3000\u8427\u96e8\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u7b11\u9053\uff1a\u201c\u591a\u8651\u4e86\uff0c\u591a\u8651\u4e86\uff01\u5b9d\u8d35\u5983\u4e0d\u6562\u6297\u62d2\u56fd\u5e08\uff0c\u6211\u4e5f\u4e0d\u6562\u4e0d\u542c\u5934 \u513f\u7684\u547d\u4ee4\u3002\u201d \u3000\u3000\u7530\u5f53\u4e5f\u5fae\u7b11\u9053\uff1a\u201c\u8427\u5144\uff0c\u53ea\u542c\u4f60\u8fd9\u53e5\u8bdd\uff0c\u5df2\u7ecf\u5fc3\u6709\u5b9a\u4f4d\u4e86\u3002 \u3000\u3000\u8fd9\u6863\u4e8b\u4e0d\u597d\u73a9\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\uff0c\u76ee\u5149\u8f6c\u5411\u4e1c\u5357\u65b9\u4f4d\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u4f4e\u58f0\u9053\uff1a\u201c\u5934\u513f\u6765\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u786e\u5b9e\u6709\u70b9\u5fc3\u4e71\u4e86\uff0c\u8033\u76ee\u806a\u7075\uff0c\u6025\u6025\u8f6c\u5934\u770b\u53bb\u2014\u2014 \u3000\u3000\u53ea\u89c1\u5b87\u6587\u6210\u90fd\u5de6\u624b\u6267\u5251\uff0c\u53f3\u624b\u63e1\u5200\uff0c\u5168\u8eab\u8863\u888d\u6e85\u6ee1\u8840\u8ff9\uff0c\u663e\u7136\u662f\u7ecf\u8fc7\u4e00\u756a\u975e\u5e38\u6fc0\u70c8\u7684\u6076 \u6218\u3002 \u3000\u3000\u591a\u5c11\u5e74\u6765\uff0c\u5341\u5927\u526f\u7edf\u9886\u90fd\u77e5\u9053\u5b87\u6587\u6210\u90fd\u5251\u6cd5\u7cbe\u6e5b\uff0c\u5584\u7528\u5404\u79cd\u5175\u5668\uff0c\u9a6c\u4e0a\u5175\u5668\u662f\u4e00\u4ef6\u91cd\u8fbe \u4e00\u767e\u516d\u5341\u65a4\u7684\u954f\u91d1\u9557\uff0c\u529b\u5927\u52bf\u731b\uff0c\u51b2\u950b\u9677\u9635\u52c7\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u51e4\u7fc5\u98de\u5200\uff0c\u66f4\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u767d\u5149\u4e00\u95ea\uff0c\u5c31\u53d6\u4eba\u9996\u7ea7\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u6b65\u6218\u8fce\u654c\u65f6\uff0c\u4e5f\u80fd\u5200\u5251 \u5e76\u7528\u3002 \u3000\u3000\u90a3\u628a\u63e1\u5728\u53f3\u624b\u7684\u5200\uff0c\u5c1a\u6709\u8840\u8ff9\uff0c\u662f\u521a\u521a\u7528\u8fc7\u4e86\u3002 \u3000\u3000\u8427\u96e8\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u7edf\u9886\uff0c\u5916\u9762\u60c5\u5f62\u5982\u4f55\uff1f\u662d\u9633\u9662\u4e2d\uff0c\u4e00\u591c\u6625\u8272\uff0c\u5bab\u5973\u4eec\u6a2a\u8eba\u575a\u5012\uff0c \u770b\u5f97\u4ee4\u4eba\u5fc3\u9178\u3002\u4f46\u5929\u4eae\u524d\uff0c\u654c\u4eba\u8fd9\u573a\u6bd2\u8702\u653b\u52bf\uff0c\u66f4\u662f\u53ef\u6015\u3002\u7530\u5144\u548c\u6211\u90fd\u53d7\u4e86\u87ab\u4f24\uff0c\u4eba\u548c\u6bd2\u8702 \u4e4b\u6218\uff0c\u65e0\u6cd5\u4ee5\u547d\u76f8\u62fc\uff0c\u5e78\u5f97\u5b9d\u8d35\u5983\u53ca\u65f6\u51fa\u624b\uff0c\u6d88\u706d\u4e86\u6bd2\u8702\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6bd2\u8702\u5a01\u529b\u60ca\u4eba\uff0c\u4e0d\u5c11\u5bab\u536b\u4f24\u5728\u4e86\u6bd2\u8702\u4e4b\u4e0b\uff0c\u5982\u975e\u5b9d\u8d35\u5983\u51fa\u624b\u76f8 \u6551\uff0c\u6bd2\u8702\u914d\u5408\u7740\u9ed1\u8863\u5251\u624b\u7684\u884c\u52a8\uff0c\u5f88\u53ef\u80fd\u51fb\u6e83\u6211\u4eec\uff0c\u4e5f\u903c\u5f97\u6211\u5927\u5f00\u4e86\u4e00\u6b21\u6740\u6212\uff0c\u6211\u5e0c\u671b\u80fd\u628a \u90a3\u4e9b\u9ed1\u8863\u5251\u624b\u6321\u5728\u5bab\u5899\u4e4b\u5916\u7684\uff0c\u4f46\u4ecd\u88ab\u4ed6\u4eec\u7a81\u5165\u4e0d\u5c11\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u88ab\u57cb\u4f0f\u7684\u5f13\u7bad\u624b\u6740\u4e86\uff0c\u5982\u88ab\u6bd2\u8702\u5f15\u5bfc\u7740\u4ed6\u4eec\u8fdb\u5165\u4e86\u662d\u9633\u9662\u4e2d\uff0c\u8702\u87ab\u4eba\u6740\uff0c\u72c2 \u98ce\u66b4\u96e8\u6253\u68a8\u82b1\uff0c\u60b2\u60e8\u7684\u5883\u51b5\uff0c\u5c31\u8ba9\u4eba\u89e6\u76ee\u60ca\u5fc3\u4e86\uff0c\u5c5e\u4e0b\u4e5f\u52bf\u5fc5\u7f6a\u754f\u81ea\u6215\u2026\u2026\u201d \u3000\u3000\u201c\u8427\u5144\u5f1f\uff01\u201d\u5b87\u6587\u6210\u90fd\u5145\u6ee1\u7740\u611f\u60c5\u5730\u9053\uff1a\u201c\u672a\u963b\u6765\u654c\u5165\u5bab\u5899\uff0c\u5176\u7f6a\u5728\u6211\u3002\u5915\u9633\u843d\u5c71\uff0c\u6c5f \u6cb3\u65e5\u4e0b\uff0c\u5927\u52bf\u6240\u8d8b\uff0c\u975e\u6211\u7b49\u5fae\u672b\u4e4b\u529b\u80fd\u591f\u529b\u633d\u72c2\u6f9c\uff0c\u5c3d\u5176\u5728\u6211\u5427\uff01 \u3000\u3000\u6211\u8fdb\u5165\u5bab\u6765\uff0c\u4e00\u662f\u5411\u7687\u4e0a\u8bf7\u7f6a\uff0c\u4e8c\u5411\u7687\u540e\u95ee\u5b89\uff0c\u4e09\u5411\u5b9d\u8d35\u5983\u81f4\u8c22\uff1b\u975e\u5979\u4e4b\u80fd\uff0c\u8fd9\u4e00\u591c\u6211 \u4eec\u5c31\u9632\u5b88\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u5c06\u519b\uff01\u201d\u8881\u5b9d\u513f\u7a81\u7136\u51fa\u73b0\uff0c\u83b2\u6b65\u59d7\u59d7\u7684\u8d70\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u5728\u4e00\u5ea7\u7bf7\u5e10\u4e2d\u76ee \u7779\u4e86\u6bd2\u8702\u4e4b\u5a01\uff0c\u4e5f\u770b\u5230\u4e86\u51b2\u5165\u662d\u9633\u9662\u4e2d\u7684\u5251\u624b\u548c\u8427\u5c06\u519b\u8fce\u6218\u7684\u60c5\u5f62\u3002 \u3000\u3000\u5bf9\u5b87\u6587\u5c06\u519b\u7684\u4fe1\u4efb\u66f4\u4e3a\u575a\u5f3a\uff0c\u6b64\u4e8b\u4e00\u4e86\uff0c\u8981\u52a0\u5c01\u5c06\u519b\u4e3a\u5929\u4e0b\u90fd\u62db\u8ba8\uff0c\u628a\u56fd\u5bb6\u5174\u4ea1\u5927\u4e8b\u5168 \u4ed8\u6258\u4e8e\u5c06\u519b\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u6837\u7684\u91cd\u8d23\u5927\u4efb\uff0c\u6210\u90fd\u5982\u4f55\u62c5\u5f53\u5f97\u8d77\u5462\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd8\u6709\u4f59\u7eea\u5440\uff01\u7687\u4e0a\u4ed8\u4e8e\u4e86\u5c06\u519b\u6cbb\u4e71\u7684\u5927\u6743\u540e\uff0c\u4ed6\u5c31\u4e0d\u518d\u8fc7\u95ee\u653f\u4e8b\u3002\u5c06\u519b\u8981 \u8c03\u519b\u706d\u5bc7\uff0c\u4e5f\u8981\u6cbb\u56fd\u7406\u6c11\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u5c31\u66f4\u52a0\u4f7f\u4e0d\u5f97\u4e86\uff0c\u6210\u90fd\u4e3a\u7687\u4e0a\u8d74\u6c64\u8e48\u706b\uff0c\u7edf\u519b\u6218\u6b7b\uff0c\u7edd\u79d2\u63a8\u8f9e\u3002\u4ee5\u56fd\u4e8b\u76f8\u6258\uff0c\u4e0d\u6562 \u627f\u53d7\uff0c\u6211\u8981\u53bb\u9762\u541b\u7533\u8ff0\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u63a5\u7740\u9053\uff1a\u201c\u5c06\u519b\uff0c\u7533\u8ff0\u4ec0\u4e48\u5462\uff1f\u8bcf\u65e8\u8fd8\u672a\u53d1\u4e0b\u5440\uff01\u5929\u5a01\u96be\u6d4b\uff0c\u8c01\u77e5\u9053\u4f1a\u4e0d\u4f1a\u6539\u53d8 \u4e3b\u610f\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u8bf4\u7684\u662f\uff01\u6211\u6709\u4e9b\u83bd\u649e\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u6709\u4e86\u8fd9\u4e2a\u5fc3\uff0c\u6211\u5148\u900f\u4e2a\u8baf\u606f\u7ed9\u4f60\u3002\u4ed6\u4e0d\u662f\u8bf4\u8bf4\u7b97\u4e86\uff0c\u800c\u662f\u5f88\u8ba4\u771f\u7684 \u5728\u60f3\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u542c\u4ed6\u53e3\u6c14\uff0c\u4f3c\u662f\u60f3\u5230\u4e1c\u90fd\u6d1b\u9633\u5c0f\u4f4f\uff0c\u7136\u540e\u518d\u4e0b\u6c5f\u5357\uff0c\u5728\u626c\u5dde\u4f4f\u4e0b\u6765\uff0c\u4ed6\u5411\u5f80 \u626c\u5dde\u7684\u7e41\u82b1\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u63a5\u9053\uff1a\u201c\u53ef\u662f\u957f\u5b89\u56fd\u4e8b\u5982\u9ebb\uff01\u201d \u3000\u3000\u201c\u4ea4\u7ed9\u4f60\u5566\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8981\u597d\u597d\u5730\u60f3\u60f3\u7406\u56fd\u4e4b\u7b56\uff0c\u597d\u5728\u65f6\u95f4\u8fd8\u65e9\uff0c\u7687\u4e0a\u5fc3\u6709 \u6b64\u5ff5\uff0c\u4f46\u51e0\u65f6\u5b9e\u73b0\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u8d35\u5983\u7684\u770b\u6cd5\u5462\uff1f\u201d\u8427\u96e8\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u62c5\u5b50\u592a\u91cd\u4e86\uff0c\u6211\u4eec\u7684\u5934\u513f\u600e\u6562\u8f7b\u4f5c\u627f\u8bfa \u5462\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u522b\u591a\u60f3\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u53ea\u662f\u5fc3\u4e2d\u6709\u70b9\u5e95\u5c31\u884c\u4e86\uff0c\u8fd9\u4e48\u91cd\u5927\u7684\u4e8b\u771f\u8981\u4ed8 \u8bf8\u884c\u52a8\uff0c\u81f3\u5c11\u8981\u56fd\u5e08\u540c\u610f\uff0c\u7687\u4e0a\u7eb5\u7136\u6709\u5fc3\uff0c\u4f46\u5173\u952e\u5374\u5728\u8881\u56fd\u5e08\u7684\u8eab\u4e0a\uff0c\u5b87\u6587\u5c06\u519b\u80fd\u4f5c\u7684\uff0c\u5c31 \u662f\u5168\u529b\u5bf9\u4ed8\u3001\u6d88\u706d\u76ee\u524d\u7684\u5f3a\u654c\uff0c\u65e5\u540e\u6cbb\u56fd\u7684\u5927\u8ba1\uff0c\u65e5\u540e\u518d\u4f5c\u7b56\u5212\u3002\u8a00\u5c3d\u4e8e\u6b64\uff0c\u4ee5\u540e\u6211\u4e0d\u518d\u63d0 \u4e86\uff0c\u4f60\u4eec\u4e5f\u4e0d\u8bb8\u591a\u95ee\u3002\u201d \u3000\u3000\u201c\u591a\u627f\u8d35\u5983\u6559\u8a00\uff0c\u6210\u90fd\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u201c\u7687\u4e0a\u76ee\u7779\u6bd2\u8702\u7684\u5a01\u52bf\u3001\u640f\u6740\u7684\u6fc0\u70c8\uff0c\u53d7\u5230\u4e86\u4e00\u70b9\u60ca\u5413\uff0c\u5df2\u7531\u7687\u540e\u4f8d\u5165\u5bdd\u5bab\u4f11\u606f\u3002\u201d\u8881 \u5b9d\u513f\u9053\u3002 \u3000\u3000\u201c\u662f\uff0c\u6210\u90fd\u6539\u5929\u518d\u606d\u8bf7\u5723\u5b89\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6700\u597d\u5728\u51fb\u9000\u5f3a\u654c\u4e4b\u540e\uff0c\u8ba9\u5bab\u4e2d\u540e\u5983\u5b89\u5fc3\uff0c\u9f99\u5fc3\u5927\u60a6\uff0c\u624d\u80fd\u548c\u4f60\u4f5c\u957f\u65f6\u53d9 \u8c08\uff0c\u4e5f\u8bb8\u8bdd\u4e2d\u4f1a\u9732\u51fa\u4e00\u4e9b\u86db\u4e1d\u9a6c\u8ff9\u3002\u201d \u3000\u3000\u201c\u656c\u9886\u6559\u76ca\uff0c\u6210\u90fd\u544a\u9000\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u6562\u591a\u770b\u8881\u7d2b\u70df\uff0c\u4e5f\u4e0d\u6562\u591a\u770b\u8881\u5b9d\u513f\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e0d\u80fd\u591a\u770b\uff0c\u770b\u4e45\u4e86\u4eba\u4f1a\u7740\u8ff7\u3002 \u3000\u3000\u201c\u5c06\u519b\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5df2\u4e3e\u6b65\u884c\u53bb\uff0c\u8881\u5b9d\u513f\u53c8\u628a\u4ed6\u53eb\u4f4f\u3002 \u3000\u3000\u201c\u5b9d\u8d35\u5983\u8fd8\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u62b1\u62f3\u8eac\u8eab\uff0c\u76ee\u5149\u7740\u5730\uff0c\u5c31\u662f\u4e0d\u770b\u4e86\u3002 \u3000\u3000\u65e2\u7136\u6321\u4e0d\u4f4f\u90a3\u80a1\u5438\u5f15\u529b\uff0c\u4e0d\u770b\u603b\u53ef\u4ee5\u5427\uff1f \u3000\u3000\u201c\u4f24\u4ea1\u5982\u4f55\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u60f3\u77e5\u9053\u8be6\u7ec6\u7684\u6570\u5b57\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u6b7b\u4e86\u5341\u516b\u4f4d\u5bab\u536b\u6b66\u58eb\uff0c\u5341\u4e2a\u6b7b\u5728\u8702\u5c3e\u7684\u6bd2\u9488\u4e4b\u4e0b\uff0c\u516b\u4e2a\u6b7b\u5728\u8fce\u654c\u640f\u6740\u4e4b \u4e2d\uff0c\u4f24\u4e86\u4e8c\u5341\u516d\u4eba\uff1b\u5176\u4e2d\u6709\u4e24\u4e2a\u662f\u526f\u603b\u7edf\u9886\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u526f\u603b\u7edf\u9886\u4f24\u52bf\u5982\u4f55\uff1f\u201d\u8881\u5b9d\u513f\u795e\u60c5\u4e25\u8083\u5730\u9053\uff1a\u201c\u5343\u519b\u6613\u5f97\uff0c\u4e00\u5c06\u96be\u6c42\uff0c\u80fd\u6551\u4ed6 \u4eec\uff0c\u4e0d\u8981\u653e\u5f03\u3002\u201d \u3000\u3000---------- \u3000\u3000xmwjw \u626b\u63cf\uff0c\u98ce\u4e91\u6f5c\u9f99 OCR\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c\u5341\u4e00\u56de\u3000\u8840\u67d3\u5bab\u95e8 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a\u201c\u4e00\u4e2a\u4f24\u5728\u5de6\u808b\u4e0a\uff0c\u5f00\u4e86\u4e00\u6761\u534a\u5c3a\u957f\u7684\u53e3\u5b50\uff0c\u8840\u67d3\u5168\u8eab\u7ea2\uff0c\u4f24\u5f97\u76f8\u5f53\u91cd\u3002 \u53e6\u4e00\u4f4d\u4f24\u5728\u8702\u9488\u4e4b\u4e0b\uff0c\u534a\u8eab\u80bf\u80c0\uff0c\u82e6\u4e0d\u582a\u8a00\uff0c\u5c5e\u4e0b\u5df2\u5168\u529b\u65bd\u4e3a\uff0c\u5374\u65e0\u6cd5\u963b\u6b62\u4ed6\u6bd2\u4f24\u6076\u5316\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u6709\u7075\u4e39\u4e24\u9897\uff0c\u767d\u8272\u7684\u53ef\u89e3\u8702\u6bd2\uff0c\u9ed1\u8272\u7684\u80fd\u6cbb\u5251\u4f24\u3002\u201d \u3000\u3000\u5979\u628a\u4e24\u9897\u7075\u4e39\u6258\u4e8e\u638c\u5fc3\uff0c\u4ea4\u5411\u5b87\u6587\u6210\u90fd\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u671b\u7740\u90a3\u9ed1\u8272\u836f\u4e38\uff0c\u7ea6\u53ea\u4e00\u9897\u82b1\u751f\u5927\u5c0f\uff0c\u4e0d\u7981\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u5355\u4ee5\u4f24\u53e3\u800c \u8bba\uff0c\u5c31\u8981\u5927\u8fc7\u8fd9\u9897\u836f\u4e38\u5341\u500d\u4ee5\u4e0a\uff0c\u4e0d\u77e5\u662f\u7528\u6765\u5185\u670d\u8fd8\u662f\u5916\u6577\uff1f\u201d \u3000\u3000\u4f46\u95fb\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u9ed1\u8272\u836f\u4e38\u4e00\u5206\u4e3a\u4e8c\uff0c\u4e00\u534a\u548c\u6e29\u9152\u541e\u4e0b\uff0c\u4e00\u534a\u548c\u5728\u4e0a\u597d\u91d1\u521b\u836f\uff0c\u6577\u5728\u4f24\u5904\u3002\u81f3\u4e8e\u767d\u8272\u836f \u4e38\uff0c\u8981\u4f24\u8005\u541e\u4e0b\u5373\u53ef\u3002 \u3000\u3000\u5982\u8981\u52a0\u901f\u6548\u7528\uff0c\u541e\u4e0b\u836f\u4e38\u540e\u518d\u8ba9\u4ed6\u670d\u4e0b\u4e00\u74f6\u8001\u9152\uff0c\u836f\u4e0d\u591a\uff0c\u53d7\u4f24\u7684\u6b66\u58eb\uff0c\u6055\u6211\u4e0d\u80fd\u9001\u836f \u6551\u6cbb\uff0c\u5fc3\u4e2d\u5341\u5206\u4e0d\u5b89\u3002 \u3000\u3000\u4e0d\u8fc7\u2026\u2026\u8702\u6bd2\u592a\u91cd\u7684\u4eba\uff0c\u4e0d\u7528\u7559\u4e0b\u4ed6\u4eec\u53d7\u7f6a\uff0c\u90a3\u662f\u751f\u4e0d\u5982\u6b7b\u7684\u75db\u82e6\uff0c\u70b9\u4e86\u4ed6\u4eec\u7684\u6b7b\u7a74\uff0c \u8ba9\u4ed6\u4eec\u65e9\u767b\u6781\u4e50\u3002\u201d \u3000\u3000\u5b66\u672f\u6cd5\u7684\u4eba\uff0c\u601d\u60f3\u4e5f\u8131\u53bb\u5e38\u89c4\uff0c\u548c\u5e38\u4eba\u4e0d\u540c\uff0c\u52a1\u5b9e\u4efb\u6027\uff0c\u4e0d\u592a\u8bb2\u865a\u60c5\u9053\u5fb7\u3002 \u3000\u3000\u201c\u662f\uff01\u6210\u90fd\u4e5f\u6709\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u4f46\u5374\u8bf4\u4e0d\u51fa\u6765\uff0c\u4e5f\u4e0d\u6562\u4e0b\u624b\u53bb\u505a\uff0c\u4ed6\u4eec\u5927\u75db\u82e6\u4e86\uff0c\u4e00\u6307\u70b9\u4e2d \u6b7b\u7a74\uff0c\u4e00\u4e86\u767e\u4e86\u3002\u4f46\u6211\u6015\u4f20\u8a00\u51fa\u53bb\uff0c\u8bf4\u6211\u6740\u4e86\u53d7\u4f24\u5c5e\u4e0b\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u53bb\u4f5c\u5c31\u597d\uff0c\u7528\u4e0d\u7740\u8bf4\u51fa\u53bb\u3002\u5c06\u519b\uff0c\u8fd9\u4e00\u6279\u5251\u624b\u653b\u52bf\uff0c\u770b\u60c5\u5f62\u53ea\u662f\u521d \u671f\u653b\u52bf\uff0c\u4ed6\u4eec\u771f\u6b63\u7684\u9886\u5bfc\u5934\u5b50\u8fd8\u672a\u51fa\u73b0\uff0c\u5982\u679c\u6211\u7684\u63a8\u60f3\u4e0d\u9519\uff0c\u4ed6\u4eec\u8fd8\u6709\u5947\u5175\u51fa\u73b0\uff0c\u56fd\u5e08\u963b\u6b62 \u4e86\u4ed6\u4eec\u7684\u5927\u519b\u524d\u8fdb\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u662f\u5df2\u7ecf\u628a\u8fdb\u653b\u7684\u91cd\u70b9\u6446\u5728\u8fd9\u91cc\u4e86\uff0c\u96c6\u4e2d\u9ad8\u624b\uff0c\u51fb\u6e83\u5c06\u519b\u7387\u9886\u7684\u5bab\u536b\uff0c\u6d88\u706d\u7687\u5bab\u4e2d \u4eba\u2026\u2026\u201d \u3000\u3000\u5979\u672a\u63d0\u7687\u4e0a\uff0c\u4f46\u5df2\u9690\u9690\u542b\u7687\u4e0a\u5728\u5185\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6211\u867d\u60f3\u4e0d\u51fa\u662f\u4ec0\u4e48\u6837\u7684\u5947\u5175\uff0c\u4f46\u4e00\u5b9a\u975e\u5e38\u53ef\u6015\u3002\u8fd9\u4e24\u6b21\u653b \u52bf\uff0c\u6211\u5df2\u5c3d\u5168\u529b\u5e94\u4ed8\uff0c\u5f3a\u654c\u82b1\u62db\u9891\u51fa\uff0c\u6211\u5df2\u5fc3\u4f59\u529b\u62d9\uff0c\u4fdd\u536b\u5bab\u5ef7\u7684\u5927\u4e8b\u5168\u8981\u4f9d\u8d56\u8d35\u5983\u4e4b\u80fd\uff0c \u5c5e\u4e0b\u5df2\u65e0\u5236\u654c\u7684\u4fe1\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\uff01\u201d\u8881\u5b9d\u513f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5bab\u536b\u4eba\u6570\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u5374\u662f\u4ee5\u4e00\u62b5\u5341\u7684\u7cbe\u9510\uff0c\u5341\u5927\u526f\u7edf\u9886\u66f4\u662f\u4f60\u7684\u91cd\u8981\u52a9\u624b\u3002\u65e5\u540e\uff0c \u4f60\u638c\u7406\u56fd\u4e8b\uff0c\u4ed6\u4eec\u90fd\u662f\u72ec\u5f53\u4e00\u9762\u7684\u5927\u5c06\u519b\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u65e5\u4ee5\u591c\u7ee7\u5730\u93d6\u6218\u81f3\u6b7b\u3002\u5c06\u519b\u594b\u52c7\u9635\u4e0a \u4ea1\uff0c\u4f60\u8fd9\u4e9b\u5fe0\u8bda\u90e8\u5c5e\uff0c\u4e2a\u4e2a\u6218\u6b7b\uff0c\u5c06\u519b\u4e00\u4eba\u867d\u7136\u8eab\u8d1f\u7edd\u6280\uff0c\u4e5f\u5c31\u4e00\u679d\u7ea2\u82b1\u7f3a\u7eff\u53f6\u4e86\uff0c\u6211\u76f8\u4fe1 \u7d2b\u70df\u59d0\u59d0\u4e5f\u4e0d\u613f\u4f60\u5982\u6b64\u3002\u201d \u3000\u3000\u63d0\u5230\u8881\u7d2b\u70df\uff0c\u4f3c\u662f\u6709\u4eba\u7528\u9488\u5728\u5b87\u6587\u6210\u90fd\u5fc3\u4e0a\u523a\u4e86\u4e00\u4e0b\uff0c\u5168\u8eab\u4e00\u98a4\u3002 \u3000\u3000\u4ed6\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u592a\u5306\u5306\uff0c\u8c22\u4e86\u6625\u7ea2\u3002\u6210\u90fd\u5df2\u7ecf\u5c3d\u4e86\u5fc3\u529b\uff0c\u5b9e\u5728\u65e0\u80fd\u529b\u633d\u72c2\u6f9c\u4e86\u3002\u4e0d\u6562\u6b3a\u7792\u8d35\u5983\uff0c\u6210\u90fd \u5df2\u6d3e\u5341\u8def\u6c42\u6551\u4eba\u9a6c\uff0c\u8bf7\u5546\u6545\u65e7\u76f8\u8bc6\uff0c\u8d76\u6765\u52a9\u6211\u3002\u4e5f\u5a01\u8feb\u5229\u8bf1\u7ea6\u8bf7\u6c5f\u6e56\u4e0a\u9ad8\u4eba\u8d76\u6765\u52a9\u6218\uff0c\u8fc4\u4eca \u4e3a\u6b62\uff0c\u5c1a\u672a\u5f97\u4e00\u8def\u56de\u62a5\u3002\u201d \u3000\u3000\u4f46\u95fb\u6b65\u5c65\u58f0\u6025\u901f\u4f20\u6765\uff01 \u3000\u3000\u4e00\u4e2a\u6ee1\u8138\u98ce\u5c18\u7684\u52b2\u88c5\u5927\u6c49\u76f4\u51b2\u5b87\u6587\u6210\u90fd\u9762\u524d\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u6025\u6025\u5730\u9053\uff1a \u3000\u3000\u201c\u5c11\u6797\u5bfa\u6d3e\u51fa\u4e86\u5341\u516b\u7f57\u6c49\uff0c\u7531\u4e86\u4e86\u5927\u5e08\u7387\u961f\uff0c\u5df2\u8fdb\u5165\u4e86\u957f\u5b89\u57ce\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff0c\u59a5\u5584\u63a5\u5f85\uff01\u201d\u5b87\u6587\u6210\u90fd\u4e00\u6325\u624b\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e0d\u5f97\u6709\u4e1d\u6beb\u6162\u5ba2\u884c\u4e3a\uff0c\u5feb\u53bb\u5b89\u6392\u4ed6\u4eec\u4f11\u606f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u8981\u6c42\u6539\u7a7f\u5bab\u536b\u8863\u670d\uff0c\u5341\u4e5d\u4eba\u96c6\u4e8e\u4e00\u961f\uff0c\u4e0d\u80fd\u5206\u6563\uff0c\u90a3\u4f1a\u51cf\u5f31\u4ed6\u4eec\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1b \u3000\u3000\u201c\u5c31\u4f9d\u4ed6\u4eec\u7684\u8981\u6c42\uff0c\u7ed9\u4ed6\u4eec\u8863\u670d\u8170\u724c\uff0c\u7f16\u6210\u4e00\u961f\uff0c\u7531\u672c\u7edf\u9886\u4eb2\u81ea\u6307\u6325\u3002\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u53c8\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u7ea6\u8bf7\u4e86\u9ec4\u5c71\u4e03\u5251\u968f\u6765\u52a9\u6218\u2026\u2026\u201d \u3000\u3000\u201c\u5f88\u597d\uff01\u201d\u5b87\u6587\u6210\u90fd\u6253\u65ad\u4e86\u52b2\u88c5\u5927\u6c49\u7684\u8bdd\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4eb2\u81ea\u53bb\u63a5\u5f85\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u548c\u5c1a\u8fd8\u6709\u8981\u6c42\uff01\u201d\u52b2\u88c5\u5927\u6c49\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4f1a\u51fa\u529b\u6b7b\u6218\uff0c\u76f4\u81f3\u6b64\u95f4\u4e8b\u4e86\u3002\u5982\u679c\u4ed6\u4eec\u6ca1\u6709\u6218\u6b7b\uff0c\u5c31\u8981\u79bb\u5f00\u6b64\u5730\uff0c\u4ece\u6b64\u6069\u6028\u4e24 \u7edd\uff0c\u4e0d\u518d\u5f80\u6765\u3002 \u3000\u3000\u5341\u516b\u7f57\u6c49\u662f\u5bfa\u4e2d\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u4e86\u4e86\u5927\u5e08\u4e5f\u662f\u4e5d\u5927\u957f\u8001\u4e4b\u4e00\uff0c\u6b66\u529f\u5df2\u767b\u5cf0\u9020\u6781\u3002\u9ec4\u5c71\u4e03\u5251 \u66f4\u662f\u540d\u6ee1\u6c5f\u6e56\uff0c\u8bf7\u7edf\u9886\u4e0d\u8981\u8bef\u89e3\u4e86\u4ed6\u4eec\u7684\u8bda\u610f\u3002\u201d \u3000\u3000\u201c\u660e\u767d\u4e86\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u53bb\u544a\u8bc9\u4e86\u4e86\u5927\u5e08\uff0c\u4e00\u5207\u5e94\u4ed6\u6240\u6c42\u3002\u6b64\u4e8b\u65e0\u6687\u591a\u8c08\uff0c\u8981\u4ed6\u4eec\u7acb\u523b\u66f4\u8863\uff0c\u7528\u4e9b\u9910\u70b9\uff0c\u51c6\u5907 \u8fce\u654c\uff0c\u9000\u4e86\u654c\u4eba\u8fd9\u4e00\u6ce2\u653b\u52bf\uff0c\u6211\u518d\u8bbe\u5bb4\u4e3a\u4ed6\u4eec\u63a5\u98ce\u8be6\u8c08\u3002\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u559c\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u8981\u6c42\u7684\uff0c\u603b\u7edf\u9886\u5168\u7b54\u5e94\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u3002\u4ed6\u4eec\u66f4\u8863\u4e4b\u540e\uff0c\u6211\u5c31\u5e26\u4ed6\u4eec\u6765\u89c1\u603b \u7edf\u9886\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u80fd\u591f\u7ea6\u5230\u5c11\u6797\u548c\u5c1a\u52a9\u6218\uff0c\u5c06\u519b\u679c\u7136\u975e\u51e1\u5440\uff01\u7531\u8fd9\u4e00\u5927\u6279\u9ad8\u624b\u52a9\u6218\uff0c\u53ea\u8981\u8ba9\u4ed6\u4eec\u6bd2\u7269\u65e0 \u6cd5\u53d1\u6325\u6240\u957f\uff0c\u8fd9\u4e2a\u4ed7\u53ef\u4ee5\u6253\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u5c5e\u4e0b\u4fe1\u5fc3\u500d\u589e\u4e86\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u6700\u6ca1\u6709\u628a\u63e1\u8bf7\u5230\u7684\u4eba\uff0c\u7adf\u6700\u5148\u8d76\u5230\u4e86\u8fd9\u91cc\u3002\u5509\uff01\u770b\u8d77\u6765\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u4ea4\u60c5\u9047\u5230\u751f\u6b7b \u5173\u5934\u65f6\uff0c\u5c31\u4e0d\u5982\u5229\u5bb3\u91cd\u8981\u4e86\u3002\u5c11\u6797\u5bfa\u53ea\u4e3a\u4e86\u6362\u53d6\u6211\u4e00\u53e5\u627f\u8bfa\uff0c\u5c31\u5c3d\u5feb\u6d3e\u51fa\u9ad8\u624b\uff0c\u8d76\u6765\u957f\u5b89\u52a9 \u6218\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4ed6\u4eec\u6709\u4ec0\u4e48\u628a\u67c4\u843d\u5728\u4f60\u7684\u624b\u4e2d\uff1f\u5f53\u7136\uff0c\u82e5\u662f\u4e0d\u65b9\u4fbf\u8bf4\uff0c\u672c\u5bab\u4e5f\u4e0d\u4fbf\u52c9 \u5f3a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8427\u96e8\u3001\u7530\u5f53\u90fd\u501f\u6545\u9000\u5f00\uff0c\u8d76\u8d70\u4e86\u56db\u5468\u5bab\u536b\u6b66\u58eb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u56fd\u5e08\u5317\u5f81\uff0c\u5e26\u6765\u4e86\u674e\u4e16\u6c11\uff0c\u9014\u4e2d\u5374\u4e3a\u5c11\u6797\u548c\u5c1a\u6551\u8d70\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5fc3\u5934\u5267\u70c8\u5730\u8df3\u52a8\u7740\u3002 \u3000\u3000\u4f46\u5979\u4ecd\u7f13\u7f13\u5730\u9053\uff1a \u3000\u3000\u201c\u7d2b\u70df\u59d0\u59d0\u5982\u4f55\u80af\u653e\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56fd\u5e08\u88ab\u4e00\u4f4d\u9ad8\u50e7\u2014\u2014\u4e09\u85cf\u548c\u5c1a\u5f15\u8d70\u4e86\uff0c\u6210\u90fd\u53d1\u73b0\u5c11\u6797\u548c\u5c1a\u8eab\u4efd\uff0c\u4e5f\u52a8\u624b\u6253\u4e86\u4e00\u573a\uff0c\u53cc \u65b9\u4e92\u6709\u4f24\u4ea1\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u8d76\u6765\u52a9\u6218\uff0c\u662f\u8d3f\u8d42\u4f60\u4e86\uff0c\u5e0c\u671b\u4f60\u4e0d\u8981\u628a\u6b64\u594f\u660e\u7687\u4e0a\uff0c\u4e0d\u8981\u8c03\u52a8\u5927\u519b\u5f81\u527f\u5c11\u6797\uff0c\u5c06 \u519b\u662f\u5426\u63a5\u53d7\u5462\uff1f\u201d \u3000\u3000\u201c\u6765\u7684\u592a\u91cd\u8981\u4e86\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u53d1\u52a8\u8fd9\u4e00\u573a\u62c2\u6653\u653b\u51fb\uff0c\u4f3c\u662f\u5b58\u5fc3\u628a\u5927\u968b\u671d\u4e00\u6218\u6b8b\u706d\uff0c\u5c11\u6797\u5bfa\u9ad8\u624b\u6765\u63f4\uff0c\u662f\u4e00\u573a \u6551\u4ea1\u6d3b\u547d\u7684\u5927\u6069\u60c5\uff0c\u8fd9\u4ef6\u4e8b\u81ea\u5f53\u4e00\u7b14\u52fe\u8fc7\uff0c\u4ece\u6b64\u4e0d\u518d\u63d0\u8d77\uff0c\u4e5f\u51b3\u5fc3\u5c65\u884c\u627f\u8bfa\uff0c\u6b64\u540e\u4e0d\u518d\u548c\u5c11 \u6797\u5bfa\u6765\u5f80\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6240\u4ee5\u4e0d\u80fd\u8ba9\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u6709\u592a\u5927\u7684\u4f24\u4ea1\uff0c\u6b7b\u5b8c\u6b7b\u7edd\u4e86\uff0c\u5c06\u519b\u4e5f\u4e0d\u597d\u5bf9\u4eba \u4ea4\u4ee3\u5440\uff01\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\uff0c\u6210\u90fd\u5c31\u5168\u65e0\u628a\u63e1\u4e86\u3002\u542c\u8bf4\u866c\u9aef\u5ba2\u548c\u4e00\u5e72\u5c5e\u4e0b\uff0c\u6b66\u529f\u5341\u5206\u9ad8\u5f3a\uff0c\u5982\u518d\u6709\u6bd2\u7269\u3001 \u672f\u6cd5\u52a9\u6218\uff0c\u4e2a\u4eba\u751f\u6b7b\u90fd\u65e0\u80fd\u4e3b\u5bb0\uff0c\u5982\u4f55\u80fd\u987e\u5230\u4ed6\u4eba\u5462\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a\u201c\u6211\u4f1a\u5168\u529b\u6210\u5168\u5c06\u519b\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b87\u6587\u6210\u90fd\u5df2\u5b8c\u5168\u660e\u767d\u8881\u5b9d\u513f\u662f\u4e00\u4f4d\u975e\u5e38\u9ad8\u660e\u7684\u4eba\u7269\u4e86\u3002\u5f97\u6b64\u627f\u8bfa\uff0c\u5fc3\u4e2d\u66f4\u662f\u5927\u611f \u5b89\u6170\u3002 \u3000\u3000\u4ed6\u957f\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u591a\u8c22\u8d35\u5983\uff0c\u6210\u90fd\u544a\u9000\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u4e0d\u80af\u591a\u770b\u6211\u4e00\u773c\uff0c\u53ea\u4e3a\u4e0d\u613f\u53d7\u6211\u5a9a\u672f\u6240\u593a\uff0c\u662f\u4e00\u4e2a\u5947\u7537\u5b50\uff0c\u52ff\u602a\u80fd \u548c\u7687\u4e0a\u63a8\u5fc3\u7f6e\u8179\uff0c\u541b\u81e3\u76f8\u8bb8\uff1b\u52ff\u602a\u7d2b\u70df\u59d0\u59d0\u559c\u6b22\u4ed6\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u554a\u5b87\u6587\u6210\u90fd\uff01\u8881\u7d2b\u70df\u53ea\u80fd\u753b\u997c\u5145\u9965\u671b\u6885\u6b62\u6e34\uff0c\u5168\u5fc3\u5168\u610f\u7684\u8ffd\u6c42\u6211\u8881\u5b9d\u513f\uff0c\u90a3\u4e00 \u5929\u6211\u5fcd\u53d7\u8d85\u8d8a\u4e86\u6781\u9650\uff0c\u4e5f\u8bb8\u4f1a\u8ba9\u4f60\u62b1\u4e0a\u5e8a\u53bb\u3002\u201d \u3000\u3000\u601d\u5fd6\u95f4\uff0c\u7530\u5f53\u3001\u51cc\u4e91\u3001\u8427\u96e8\u4e09\u4eba\u5e76\u80a9\u884c\u6765\uff0c\u9f50\u9f50\u884c\u793c\u3002 \u3000\u3000\u201c\u4e0d\u7528\u591a\u793c\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e09\u4f4d\u5c06\u519b\uff0c\u5982\u4f55\u8c03\u6574\u5e03\u7f72\uff1f\u662f\u5426\u8981\u5411\u5b87\u6587\u5c06\u519b\u8981\u6c42\u63f4 \u5175\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u5bab\u536b\u4eec\u5df2\u7ecf\u5206\u6279\u8fdb\u9910\u4f11\u606f\uff0c\u4ee5\u4fdd\u6301\u968f\u65f6\u518d\u6218\u7684\u4f53\u80fd\uff0c\u963b\u654c\u4e8e\u5bab\u5899\u4e4b\u5916\uff0c\u662f\u5b87 \u6587\u7edf\u9886\u6700\u5927\u7684\u5fc3\u613f\uff0c\u6240\u4ee5\u5c5e\u4e0b\u4e0d\u51c6\u5907\u8981\u6c42\u63f4\u624b\u3002 \u3000\u3000\u5b87\u6587\u7edf\u9886\u5982\u679c\u88ab\u51fb\u6e83\uff0c\u5c5e\u4e0b\u7b49\u4e5f\u65e0\u80fd\u4fdd\u536b\u8fd9\u5ea7\u662d\u9633\u5bab\u9662\uff0c\u53ea\u6709\u6218\u6b7b\u4ee5\u8c22\u541b\u6069\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8427\u5c06\u519b\uff0c\u4f60\u4e0d\u4f1a\u6b7b\u7684\u3002\u4f60\u8fd8\u662f\u9752\u58ee\u4e4b\u5e74\uff0c\u6b7b\u4e86\u5c82\u4e0d\u53ef\u60dc\u3002\u201d \u3000\u3000\u7b11\u5f97\u5982\u82b1\u5f15\u8776\u3002 \u3000\u3000\u8427\u96e8\u53ea\u611f\u89c9\u5934\u8111\u8f70\u4e86\u4e00\u58f0\uff0c\u4e00\u9635\u8ff7\u7cca\uff0c\u771f\u8981\u51b2\u8fc7\u53bb\u62b1\u4f4f\u8881\u5b9d\u513f\u4eb2\u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u8fc8\u6b65\u8d70\u4e86\u3002 \u3000\u3000\u7530\u5f53\u7684\u58f0\u97f3\u5374\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u8427\u5144\uff0c\u5bf9\u4ed8\u5404\u79cd\u6bd2\u7269\u7684\u836f\u7269\u5df2\u7ecf\u8fd0\u6765\u4e86\uff0c\u6211\u5df2\u4e0b\u4ee4\u8c03\u548c \u4e86\u96c4\u9ec4\u836f\u9152\uff0c\u6bcf\u4eba\u996e\u7528\u4e09\u676f\u3002\u5e94\u8be5\u6d12\u5728\u5730\u4e0a\u7684\u836f\u7c89\u4e5f\u547d\u4ed6\u4eec\u94fa\u6d12\u5728\u662d\u9633\u9662\u5916\u2026\u2026\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u53ef\u662f\u6bd2\u5cf0\u4f1a\u98de\uff0c\u94fa\u6d12\u5730\u4e0a\u7684\u836f\u7c89\u5bf9\u4ed6\u4eec\u5168\u65e0\u4f5c\u7528\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u6bd2\u8702\u5df2\u4e3a\u5b9d\u8d35\u5983\u5168\u6570\u6d88\u706d\u4e86\u3002\u201d\u7530\u5f53\u9053\uff1a\u201c\u6bd2\u86c7\u3001\u6bd2\u874e\u5c31\u8fdb\u4e0d\u6765\u662d\u9633\u9662\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u591a\u4e8f\u4e86\u5b9d\u8d35\u5983\uff01\u201d \u3000\u3000\u76ee\u5149\u4e00\u77a5\u2014\u2014 \u3000\u3000\u7a81\u7136\u4e00\u5446\uff0c\u9053\uff1a\u201c\u770b\u554a\uff01\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5927\u9e1f\uff01\u201d\u7530\u5f53\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u5996\u4eba\uff1f\u8fde\u5927\u9e1f\u4e5f\u80fd\u62db\u6765\u5440\uff01\u201d \u3000\u3000\u201c\u4e0d\u662f\u9e1f\uff0c\u662f\u4eba\uff01\u201d\u51cc\u4e91\u9053\uff1a\u201c\u7a7f\u7740\u4e00\u8eab\u9e1f\u7fbd\u5236\u6210\u7684\u8863\u670d\uff0c\u518d\u52a0\u4e0a\u4e00\u9876\u5947\u602a\u7684\u5e3d\u5b50\uff0c\u4e00 \u4e2a\u957f\u957f\u7684\u5634\u5df4\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u4e00\u53ea\u5927\u602a\u9e1f\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u4f1a\u98de\u5440\uff01\u201d \u3000\u3000\u8427\u96e8\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u53f3\u624b\u5df2\u62bd\u51fa\u4e86\u957f\u5251\u3002 \u3000\u3000\u957f\u5634\u5de8\u9e1f\u679c\u7136\u4f1a\u98de\u3002 \u3000\u3000\u5341\u51e0\u53ea\u8fde\u6210\u4e00\u4e32\uff0c\u98de\u8fc7\u5bab\u5899\uff0c\u8fdb\u5165\u4e86\u662d\u9633\u9662\u4e2d\u3002 \u3000\u3000\u5c45\u7136\u8fd8\u80fd\u76d8\u6816\u5728\u5927\u6811\u4e0a\u5462\uff01 \u3000\u3000\u5341\u51e0\u53ea\u5927\u9e1f\u5c31\u5206\u843d\u5bab\u5899\u5185\u7684\u9ad8\u5927\u6811\u4e0a\u4e86\u3002 \u3000\u3000\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u5b87\u6587\u603b\u7edf\u9886\u662f\u5426\u53d1\u89c9\u4e86\u8fd9\u53ea\u9e1f\u4eba\u5947\u5175\u5462\uff1f\u600e\u4f1a\u4e0d\u95fb\u963b\u51fb\u7684\u58f0\u97f3\u5462\uff1f\u201d \u3000\u3000\u4f46\u95fb\u5f13\u5f26\u58f0\u52a8\uff0c\u5341\u4e94\u652f\u957f\u7bad\u5206\u5411\u9e1f\u4eba\u5c04\u53bb\u3002\u6562\u60c5\u662f\u57cb\u4f0f\u5728\u5bab\u6bbf\u9ad8\u5904\u7684\u5f13\u7bad\u624b\u5df2\u7ecf\u53d1\u89c9 \u4e86\u3002 \u3000\u3000\u5341\u4e94\u5f20\u5f3a\u5f13\u62c9\u6210\u6ee1\u6708\uff0c\u5c04\u51fa\u4e86\u4e00\u7bad\u3002 \u3000\u3000\u6d41\u77e2\u7834\u7a7a\uff0c\u6668\u66e6\u4e2d\u5e26\u51fa\u4e00\u7247\u7bad\u5578\u4e4b\u58f0\uff01 \u3000\u3000\u8fd9\u7b49\u5a01\u529b\u5f3a\u5927\u4e4b\u7bad\uff0c\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u9e1f\u4eba\u4e5f\u4e0d\u6562\u627f\u53d7\uff0c\u53cc\u7fc5\u5c55\u5f00\uff0c\u4eba\u5374\u5411\u4e0b\u6c89\u53bb\u3002 \u3000\u3000\u907f\u5f00\u98de\u77e2\u7684\u8eab\u6cd5\uff0c\u5b8c\u5168\u51fa\u4eba\u610f\u5916\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5341\u4e8c\u53ea\u9e1f\u4eba\u5168\u90fd\u672a\u906d\u4f24\u5bb3\u3002 \u3000\u3000\u4f46\u4e5f\u663e\u9732\u51fa\u4ed6\u4eec\u7684\u7f3a\u5931\uff0c\u6c89\u4e0b\u4e86\u4e03\u516b\u5c3a\u624d\u7a33\u4f4f\u8eab\u5b50\u3002\u6247\u52a8\u53cc\u7ffc\uff0c\u7a33\u4f4f\u4e86\u4e0b\u843d\u4e4b\u52bf\uff0c\u5411\u524d \u98de\u53bb\u4e94\u516d\u4e08\u540e\uff0c\u624d\u6062\u590d\u6b63\u5e38\u901f\u5ea6\u3002 \u3000\u3000\u4e16\u4e0a\u6ca1\u6709\u98de\u5f97\u5982\u6b64\u62d9\u7b28\u7684\u9e1f\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u6062\u590d\u4e86\u6b63\u5e38\u4e4b\u540e\uff0c\u7adf\u80fd\u52a0\u5feb\u901f\u5ea6\u4e5f\u5f00\u59cb\u5347\u9ad8\u3002\u53ea\u53ef\u60dc\u8fd9\u5730\u65b9\u7684\u7a7a\u95f4\u4e0d\u5927\uff0c\u964d\u4f4e\u4e86 \u9ad8\u5ea6\u5c31\u65e0\u6cd5\u8d8a\u8fc7\u5bab\u6bbf\u3002 \u3000\u3000\u4f46\u5b83\u4eec\u6709\u529e\u6cd5\u7ed5\u7740\u5bab\u6bbf\u98de\u3002 \u3000\u3000\u5bab\u5a25\u5f69\u5973\u4eec\u5168\u90fd\u62e5\u6324\u95e8\u524d\u3001\u7a97\u53e3\u770b\u3002 \u3000\u3000\u4e5f\u6709\u4eba\u5927\u80c6\u5730\u8d70\u51fa\u95e8\u5916\u3002 \u3000\u3000\u8fd9\u4e9b\u5927\u9e1f\u4f3c\u662f\u633a\u559c\u6b22\u7f8e\u5973\u5462\uff01 \u3000\u3000\u5bab\u5a25\u3001\u5f69\u5973\u770b\u4ed6\u4eec\uff0c\u5b83\u4eec\u4e5f\u770b\u7f8e\u5973\uff0c\u4e5f\u8868\u73b0\u51fa\u4e86\u5bf9\u7f8e\u5973\u7684\u5174\u8da3\u3002\u6709\u65f6\u6296\u52a8\u4e00\u4e0b\u53cc\u7ffc\uff0c\u4f5c \u51fa\u4e00\u4e9b\u602a\u5f02\u7684\u98de\u884c\u59ff\u52bf\uff0c\u5f15\u4eba\u53d1\u7b11\u3002 \u3000\u3000\u8fd9\u5c31\u628a\u53cc\u65b9\u6253\u4ed7\u3001\u640f\u547d\u6c14\u6c1b\u51b2\u6de1\u4e86\u3002 \u3000\u3000\u8d8a\u6765\u8d8a\u591a\u7684\u5bab\u5973\u4eec\u8d70\u5165\u4e86\u5ead\u9662\u3002 \u3000\u3000\u8427\u96e8\u53ea\u4e0b\u4ee4\u5bab\u536b\u4eec\u5411\u662d\u9633\u9662\u5185\u96c6\u4e2d\uff0c\u8c03\u6574\u4e86\u4ed6\u4eec\u9632\u5b88\u7684\u5e03\u7f72\uff0c\u5e76\u672a\u4e0b\u4ee4\u653b\u51fb\u3002 \u3000\u3000\u4ed6\u5df2\u80af\u5b9a\u8fd9\u4e9b\u602a\u5f02\u7684\u5927\u9e1f\u662f\u4eba\u6240\u626e\uff0c\u4f46\u80fd\u5728\u7a7a\u4e2d\u957f\u4e45\u98de\u884c\uff0c\u53c8\u80fd\u6298\u8f6c\u968f\u5fc3\uff0c\u663e\u7136\u662f\u7ecf\u8fc7 \u4e86\u957f\u4e45\u800c\u8270\u82e6\u7684\u8bad\u7ec3\u3002 \u3000\u3000\u4ed6\u4eec\u98de\u5165\u5bab\u5899\u8fdb\u5bab\u9662\uff0c\u5fc5\u6709\u4e00\u5957\u7280\u5229\u7684\u653b\u51fb\u65b9\u6cd5\u3002 \u3000\u3000\u8427\u96e8\u975e\u5e38\u5e0c\u671b\u80fd\u77a7\u5230\u4ed6\u4eec\u653b\u51fb\u7684\u65b9\u6cd5\uff0c\u4ee5\u4fbf\u601d\u7d22\u7834\u89e3\u4e4b\u7b56\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5f88\u8010\u5fc3\u5730\u770b\u4e0b\u53bb\u3002 \u3000\u3000\u7ed5\u7740\u662d\u9633\u5bab\u5927\u6bbf\u98de\u884c\u4e09\u531d\u4e4b\u540e\uff0c\u5e26\u5934\u7684\u9e1f\u4eba\u7a81\u7136\u5411\u4e0a\u5347\u53bb\uff0c\u767b\u4e0a\u4e86\u5bab\u6bbf\u3002 \u3000\u3000\u5341\u4e94\u540d\u5f13\u7bad\u624b\u4e00\u7bad\u672a\u4e2d\uff0c\u7acb\u523b\u6263\u4e0a\u7b2c\u4e8c\u652f\u7bad\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4e0d\u80fd\u5c04\u51fa\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u957f\u7bad\u53ef\u4ee5\u504f\u9ad8\uff0c\u53ef\u4ee5\u5e73\u5c04\uff0c\u4f46\u4e0d\u80fd\u5411\u4e0b\u5c04\uff0c\u90a3\u53ef\u80fd\u4f1a\u5c04\u4e2d\u5e03\u5b88\u5728\u4e0b\u9762\u7684\u5bab\u536b\u6216\u662f \u5f69\u5973\u3001\u5bab\u5a25\u3002 \u3000\u3000\u9e1f\u4eba\u98de\u8fd1\u4e86\u662d\u9633\u5bab\u6bbf\u540e\uff0c\u5f62\u6210\u6b7b\u89d2\u3002 \u3000\u3000\u5f13\u7bad\u624b\u5c31\u66f4\u96be\u4e0b\u624b\u4e86\u3002 \u3000\u3000\u8427\u96e8\u7a81\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u957f\u5578\uff0c\u9053\uff1a \u3000\u3000\u201c\u95f2\u4eba\u9000\u907f\u3002\u201d \u3000\u3000\u5438\u4e00\u53e3\u6c14\uff0c\u7a81\u7136\u98de\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u4e09\u4e08\u516d\u5c3a\u9ad8\u7684\u662d\u9633\u5bab\u6bbf\u7adf\u88ab\u4ed6\u4e00\u8dc3\u800c\u4e0a\u3002 \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u76f8\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u5410\u6c14\u51fa\u58f0\uff0c\u4eba\u4e5f\u98de\u4e0a\u4e86\u5927\u6bbf\u5c4b\u9762\u3002 \u3000\u3000\u6563\u7acb\u4e8e\u5ead\u9662\u7684\u5bab\u5a25\u8fc5\u5feb\u5730\u9000\u56de\u623f\u4e2d\u3002 \u3000\u3000\u6492\u5165\u5bab\u9662\u4e2d\u7684\u5bab\u536b\u4e5f\u5f00\u59cb\u4e86\u65b0\u7684\u5e03\u7f72\u3002 \u3000\u3000\u5341\u4e94\u540d\u5f13\u7bad\u624b\u542c\u5f97\u8427\u96e8\u7684\u559d\u58f0\uff0c\u76ee\u5149\u8f6c\u987e\uff0c\u624d\u53d1\u89c9\u4e86\u9e1f\u4eba\u5df2\u98de\u4e0a\u6bbf\u9762\u3002\u6b63\u8981\u5c04\u51fa\u957f\u7bad\uff0c \u9e1f\u4eba\u5df2\u62a2\u5148\u53d1\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u53d1\u89c9\u4e86\uff0c\u8fd9\u5f3a\u5f13\u957f\u7bad\u662f\u5bf9\u4ed8\u4ed6\u4eec\u7684\u5229\u5668\uff0c\u5fc5\u5f97\u5931\u4e88\u9664\u53bb\uff0c\u56e0\u800c\u65e9\u5df2\u60f3\u597d\u4e86\u5bf9\u4ed8\u4e4b \u6cd5\u3002 \u3000\u3000\u7ed5\u98de\u5bab\u6bbf\u4e09\u531d\uff0c\u65e9\u5df2\u5b8c\u6210\u51c6\u5907\uff0c\u5206\u4e09\u4e2a\u65b9\u4f4d\u98de\u4e0a\u6bbf\u9762\uff0c\u540c\u65f6\u53d1\u52a8\u653b\u52bf\uff0c\u957f\u5634\u4e00\u5f20\uff0c\u4e00\u5341 \u4e8c\u9053\u9ed1\u70df\u76f4\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u5341\u4e94\u4e2a\u5f13\u7bad\u624b\u770b\u4e0d\u51fa\u662f\u4ec0\u4e48\u6697\u5668\uff1f \u3000\u3000\u4ed6\u4eec\u7565\u4e00\u72b9\u8c6b\uff0c\u4eba\u5df2\u4e3a\u9ed1\u70df\u51fb\u4e2d\u3002 \u3000\u3000\u4e0d\u662f\u91d1\u94c1\u6253\u9020\u7684\u6697\u5668\uff0c\u800c\u662f\u65e0\u5b54\u4e0d\u5165\u7684\u9ed1\u70df\u3002 \u3000\u3000\u5341\u4e94\u4e2a\u5f13\u7bad\u624b\u7acb\u523b\u5012\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\uff0c\u4e09\u4eba\u867d\u5df2\u767b\u4e0a\u4e86\u5927\u6bbf\uff0c\u76ee\u7779\u60e8\u5267\uff0c\u5374\u4e5f\u6551\u63f4\u4e0d\u53ca\u3002 \u3000\u3000\u539f\u6765\u9e1f\u4eba\u53e3\u4e2d\u80fd\u55b7\u51fa\u53d6\u4eba\u6027\u547d\u7684\u6bd2\u70df\u3002 \u3000\u3000\u4f46\u4e0d\u77e5\u95ea\u907f\u653b\u51fb\u7684\u8eab\u6cd5\u5982\u4f55\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u957f\u5251\u4e00\u6325\uff0c\u98de\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u8eab\u4e0a\u62ab\u7740\u5de8\u5927\u7684\u7fbd\u7ffc\u7684\u957f\u5634\u9e1f\u4eba\uff0c\u5c31\u7b97\u8bad\u7ec3\u6709\u7d20\uff0c\u4e3e\u52a8\u95f4\u4e5f\u662f\u4e0d\u591f\u7075\u6d3b\u3002 \u3000\u3000\u8427\u96e8\u7684\u8eab\u6cd5\u5feb\u901f\uff0c\u5251\u5982\u6253\u95ea\uff0c\u5df2\u8fd1\u9e1f\u8eab\u3002 \u3000\u3000\u5de8\u9e1f\u7a81\u7136\u4e00\u4f38\u5de6\u7ffc\uff0c\u67b6\u4f4f\u4e86\u8427\u96e8\u7684\u5251\u52bf\u3002 \u3000\u3000\u7fbd\u6bdb\u6a2a\u98de\u4e2d\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u91d1\u94c1\u4ea4\u9e23\uff01 \u3000\u3000\u6562\u60c5\u8fd9\u7ffc\u67b6\u662f\u7cbe\u94a2\u6253\u9020\u7684\u3002 \u3000\u3000\u6321\u5f00\u4e00\u5251\uff0c\u957f\u5634\u5df2\u8f6c\u5411\u8427\u96e8\u3002\u8427\u96e8\u7a81\u7136\u98de\u8dc3\u800c\u8d77\uff0c\u60ac\u7a7a\u4e24\u4e2a\u8ddf\u6597\uff0c\u4eba\u4e5f\u9000\u51fa\u4e86\u4e24\u4e08\u5f00 \u5916\uff0c\u843d\u5411\u5b9e\u5730\u3002 \u3000\u3000\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u7b49\u6269\u6563\u6781\u5feb\u7684\u6bd2\u70df\u7f69\u4e0a\u3002 \u3000\u3000\u7530\u5f53\u53cc\u5200\u4e00\u6325\uff0c\u653b\u5411\u53f3\u4fa7\u4e00\u4f4d\u9e1f\u4eba\uff0c\u5200\u4e0a\u84c4\u6ee1\u5185\u529b\u3002 \u3000\u3000\u76ee\u7779\u8427\u96e8\u7684\u5251\u52bf\u4e3a\u9e1f\u7ffc\u6240\u963b\uff0c\u7530\u5f53\u5e0c\u671b\u8fd9\u4e00\u5200\u4e4b\u529b\u7eb5\u7136\u4e0d\u80fd\u628a\u9e1f\u7ffc\u65a9\u65ad\uff0c\u4e5f\u8981\u4ed6\u5931\u53bb\u98de \u884c\u7684\u80fd\u529b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u7684\u53cc\u5200\u5404\u91cd\u4e5d\u65a4\u591a\u3002 \u3000\u3000\u6bd4\u8d77\u8427\u96e8\u7684\u5251\uff0c\u91cd\u4e86\u6570\u500d\u3002 \u3000\u3000\u9e1f\u4eba\u5e76\u672a\u7528\u7fbd\u7ffc\u6297\u62d2\u7530\u5f53\u4e4b\u5200\uff0c\u8eab\u8eaf\u4e00\u8f6c\uff0c\u53cc\u7ffc\u4e2d\u7a81\u7136\u5c04\u51fa\u4e00\u7247\u5bd2\u8292\uff0c\u770b\u6570\u91cf\u4e4b\u591a\uff0c\u4e0d \u4e0b\u6570\u5341\u767e\u652f\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u94f6\u9488\u4e4b\u7c7b\u7684\u6697\u5668\u3002 \u3000\u3000\u7530\u5f53\u662f\u65e9\u5df2\u6709\u5907\uff0c\u4f46\u5374\u672a\u6599\u5230\u662f\u4e00\u7247\u5149\u5e55\u4f3c\u7684\u6b79\u6bd2\u6697\u5668\u3002 \u3000\u3000\u8fd9\u4e00\u7247\u5c04\u51fa\u7684\u94f6\u9488\u7b3c\u7f69\u4e86\u4e94\u5c3a\u65b9\u5706\u3002 \u3000\u3000\u7530\u5f53\u8eab\u5b50\u5411\u540e\u4e00\u4ef0\uff0c\u53cc\u5200\u4ea4\u9519\uff0c\u633d\u51fa\u4e24\u4e2a\u5200\u82b1\uff0c\u62a4\u4f4f\u8981\u5bb3\uff0c\u811a\u8ddf\u52a0\u529b\u4e00\u8e6c\u5411\u540e\u8e7f\u53bb\uff0c\u987a \u7740\u5c4b\u9762\u7684\u5761\u5ea6\u6ed1\u4e0b\u3002 \u3000\u3000\u5f62\u52bf\u5e2e\u4e86\u7530\u5f53\u5f88\u5927\u7684\u5fd9\uff0c\u767e\u591a\u679a\u94f6\u9488\u63a0\u7740\u524d\u80f8\u3001\u9762\u95e8\u800c\u8fc7\u3002 \u3000\u3000\u5341\u4e8c\u9e1f\u4eba\u4e00\u4e3e\u6d88\u706d\u4e86\u5341\u4e94\u4f4d\u5bf9\u4ed6\u4eec\u5a01\u80c1\u6700\u5927\u7684\u5f13\u7bad\u624b\uff0c\u53c8\u628a\u8427\u96e8\u3001\u7530\u5f53\u4e24\u4eba\u903c\u4e0b\u4e86\u5c4b \u9762\u3002 \u3000\u3000\u6c14\u52bf\u5927\u76db\uff0c\u5c55\u5f00\u4e86\u4e3b\u52a8\u653b\u51fb\u3002 \u3000\u3000\u4e24\u4f4d\u9e1f\u4eba\u98de\u5411\u51cc\u4e91\u653b\u4e86\u53bb\u3002 \u3000\u3000\u51cc\u4e91\u65e9\u5df2\u63e1\u5200\u6212\u5907\uff0c\u4e5f\u4e8b\u5148\u770b\u597d\u4e86\u540e\u9000\u4e4b\u8def\u3002 \u3000\u3000\u4f46\u53c8\u4e0d\u7518\u5fc3\u4e00\u5200\u672a\u51fa\u5373\u9000\u4e0b\u5c4b\u9762\uff0c\u5e0c\u671b\u80fd\u518d\u627e\u4e00\u4e2a\u51fa\u5200\u7684\u673a\u4f1a\uff0c\u5c31\u7b97\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4e5f\u7b97\u51fa \u5200\u4e86\u3002 \u3000\u3000\u773c\u89c1\u9e1f\u4eba\u7684\u957f\u5634\u5728\u524d\uff0c\u4f46\u5374\u672a\u5f20\u5634\u55b7\u6bd2\uff0c\u4f3c\u662f\u628a\u957f\u5634\u5f53\u4f5c\u957f\u67aa\u4e00\u822c\u7684\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u51cc\u4e91\u5fc3\u4e2d\u6697\u559c\u9053\uff1a \u3000\u3000\u201c\u597d\u673a\u4f1a\u5440\uff01\u201d \u3000\u3000\u8eab\u5b50\u5411\u524d\u4e00\u4f0f\uff0c\u4e0d\u9000\u53cd\u5e94\uff0c\u4eba\u5411\u5c4b\u9762\u4ec6\u5012\u7684\u540c\u65f6\uff0c\u79cb\u6c34\u96c1\u7fce\u5200\u5df2\u631f\u7740\u4e00\u80a1\u5947\u5389\u7684\u5200\u98ce\uff0c \u659c\u5411\u4e0a\u9762\u51fb\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5200\uff0c\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u51fa\u5200\u7684\u89d2\u5ea6\u602a\u5f02\uff0c\u6b63\u51fb\u5728\u957f\u5634\u4e4b\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff01 \u3000\u3000\u8bc1\u5b9e\u957f\u5634\u786e\u4e3a\u94a2\u94c1\u6253\u6210\uff0c\u4f46\u5374\u88ab\u51cc\u4e91\u90a3\u4e00\u5200\u65a9\u4f5c\u4e86\u4e24\u65ad\u3002 \u3000\u3000\u96c1\u7fce\u5200\u867d\u975e\u5207\u91d1\u65ad\u7389\u7684\u5b9d\u5200\uff0c\u4f46\u5b83\u5341\u5206\u950b\u5229\u3002 \u3000\u3000\u51cc\u4e91\u53c8\u7528\u8db3\u4e86\u5168\u8eab\u6c14\u529b\u3002 \u3000\u3000\u65ad\u88c2\u7684\u957f\u5634\u4e2d\u7a81\u7136\u5192\u51fa\u4e00\u56e2\u706b\u5149\uff0c\u5f62\u6210\u4e00\u7247\u6570\u5c3a\u65b9\u5706\u7684\u706b\u5e55\u3002 \u3000\u3000\u51cc\u4e91\u65e9\u6709\u7b97\u8ba1\uff0c\u65a9\u65ad\u4e86\u94c1\u5634\u4e4b\u540e\uff0c\u4eba\u4e5f\u6eda\u4e0b\u5c4b\u9762\u3002 \u3000\u3000\u4f7f\u4e24\u4f4d\u542b\u6012\u6025\u88ad\u7684\u9e1f\u4eba\uff0c\u6697\u5668\u3001\u6bd2\u70df\u5168\u6570\u843d\u7a7a\uff0c\u53cd\u88ab\u66b4\u8d77\u7684\u706b\u52bf\u6cbe\u67d3\u4e0a\u4e86\u7fbd\u6bdb\uff0c\u7acb\u65f6\u71c3 \u70e7\u8d77\u6765\u3002 \u3000\u3000\u4e0d\u77e5\u9e1f\u7ffc\u662f\u4f55\u7269\u505a\u6210\uff1f \u3000\u3000\u4f46\u8fd9\u4e9b\u9e1f\u4eba\u4f3c\u662f\u975e\u5e38\u6015\u706b\u3002 \u3000\u3000\u706b\u661f\u7a9c\u98de\u4e2d\uff0c\u53ea\u89c1\u5c55\u7ffc\u98de\u8d77\uff0c\u5411\u662d\u9633\u9662\u5916\u98de\u53bb\u3002 \u3000\u3000\u201c\u8f70\uff01\u201d \u3000\u3000\u90a3\u65ad\u4e86\u957f\u5634\u7684\u9e1f\u4eba\uff0c\u7adf\u7136\u7206\u70b8\u51fa\u4e00\u7247\u6d53\u70df\u70c8\u706b\u3002 \u3000\u3000\u4ed6\u5df2\u98de\u79bb\u5bab\u6bbf\uff0c\u5728\u7a7a\u4e2d\u7206\u70b8\uff0c\u7fbd\u7ffc\u788e\u98de\u3002 \u3000\u3000\u4e00\u4e2a\u7626\u5c0f\u7684\u9ed1\u8863\u4eba\u8dcc\u843d\u4e0b\u6765\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u662f\u4e2a\u4eba\uff01 \u3000\u3000\u8eab\u4f53\u76f8\u5f53\u7626\u5c0f\uff0c\u4e00\u8eab\u9ed1\u8863\uff0c\u624b\u81c2\u548c\u53cc\u80a9\u8fd8\u7559\u7740\u70b8\u65ad\u7684\u4e1d\u7d22\uff0c\u663e\u7136\u662f\u64cd\u7eb5\u9e1f\u8eab\u3001\u7fbd\u7ffc\u4e4b\u7528 \u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5df2\u4f24\u91cd\u800c\u4ea1\u3002 \u3000\u3000\u65e0\u6cd5\u5728\u4ed6\u53e3\u4e2d\u95ee\u51fa\u4ec0\u4e48\u4e86\uff1f \u3000\u3000\u62ac\u5934\u770b\u53bb\u2014\u2014 \u3000\u3000\u4f59\u4e0b\u7684\u9e1f\u4eba\u5df2\u98de\u51fa\u662d\u9633\u5bab\u5916\u3002 \u3000\u3000\u201c\u5173\u952e\u5728\u90a3\u957f\u5927\u7684\u94c1\u5634\u4e2d\u3002\u201d\u51cc\u4e91\u9053\uff1a \u3000\u3000\u201c\u836f\u7269\u3001\u6bd2\u7c89\u90fd\u50a8\u5b58\u5176\u95f4\uff0c\u8fd8\u60f3\u4e0d\u51fa\u4ed6\u4eec\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u628a\u836f\u7269\u3001\u6bd2\u7c89\u55b7\u51fa\u6765\uff0c\u53ca\u65f6\u5f15\u71c3 \u5316\u6210\u6bd2\u70df\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u96be\u4e0d\u4f4f\u4ed6\u4e86\u3002\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u6574\u4e2a\u9e1f\u4eba\u7684\u6784\u9020\u5c31\u662f\u4e00\u79cd\u903e\u8d8a\u5e38\u4eba\u7684\u5de7\u601d\uff0c\u80fd\u6293\u4e00\u53ea\u4e0d\u53d7\u635f\u4f24\u7684\u9e1f\u4eba\uff0c\u4ed4\u7ec6\u5730\u7814\u7a76\u4e00 \u4e0b\uff0c\u5b9a\u6709\u5f88\u5927\u7684\u6536\u83b7\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u201d\u5218\u98de\u9e4f\u7f13\u7f13\u884c\u6765\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6574\u4e2a\u7684\u9e1f\u4eba\u662f\u7528\u4e1d\u7d22\u7ed3\u6210\u7f51\u8def\uff0c\u4ee5\u4eba\u7684\u624b\u3001\u8db3\u3001\u80a9\u3001\u81c2\uff0c\u63a7\u5236\u5b83\u98de\u884c\u6d3b\u52a8\uff0c\u6697\u5668\u3001\u6bd2 \u70df\u7531\u673a\u7c27\u53d1\u5c04\u3002 \u3000\u3000\u521d\u5ea6\u9047\u4e0a\uff0c\u8fd8\u771f\u7684\u5413\u4eba\uff0c\u56db\u4e2a\u5bab\u536b\u5144\u5f1f\u5c31\u4f24\u5728\u4ed6\u7684\u6bd2\u70df\u3001\u6697\u5668\u4e4b\u4e0b\u3002\u603b\u7edf\u9886\u706b\u4e86\uff0c\u6293\u4e86 \u4e00\u4e2a\u9e1f\u4eba\uff0c\u62c6\u5f00\u4e00\u77a7\uff0c\u4e00\u4e0b\u5b50\u5c31\u5168\u76d8\u4e86\u7136\u3002 \u3000\u3000\u5bf9\u4ed8\u8fd9\u79cd\u9e1f\u4eba\uff0c\u4e0d\u80fd\u6b63\u9762\u62d2\u6321\uff0c\u7ed5\u5728\u4fa7\u9762\u88ad\u51fb\uff0c\u5f3a\u529b\u7684\u6697\u5668\u4e5f\u662f\u6709\u6548\u6b66\u5668\u3002\u54a6\uff01\u4e0d\u662f\u98de \u8fdb\u6765\u5341\u51e0\u53ea\u5417\uff1f\u201d \u3000\u3000\u76ee\u5149\u8f6c\u52a8\u7740\u3002 \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u51cc\u5144\u51fb\u6bc1\u4e86\u4e00\u53ea\uff0c\u5176\u4f59\u7684\u5168\u98de\u51fa\u662d\u9633\u9662\u4e86\u3002\u201d \u3000\u3000\u5e76\u8bf4\u660e\u4e86\u7ecf\u8fc7\u3002 \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u5b87\u6587\u603b\u7edf\u9886\u4e00\u76f4\u6302\u5ff5\u5bab\u5185\u5b89\u5371\uff0c\u4e00\u65b9\u9762\u547d\u5144\u5f1f\u5165\u5b98\u6765\u4f20\u8fbe\u5bf9\u4ed8\u9e1f\u4eba\u7684\u4fe1\u606f\uff0c\u4e00\u65b9\u9762\u8981 \u5144\u5f1f\u7559\u4e0b\u52a9\u9635\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u963b\u654c\u4e8e\u5bab\u5899\u4e4b\u5916\uff0c\u662f\u5b87\u6587\u603b\u7edf\u9886\u62d2\u654c\u76ee\u6807\uff0c\u91cd\u70b9\u5e94\u8be5\u5728\u5bab\u5916\u624d\u5bf9\uff0c\u9063\u6d3e\u5218\u5144\u6765\u6b64\uff0c\u5bab \u5899\u5916\u5c82\u4e0d\u662f\u5c11\u4e86\u4e00\u5458\u62d2\u654c\u7684\u5927\u5c06\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u4e86\u4e86\u5927\u5e08\u7387\u9886\u4e86\u5341\u516b\u7f57\u6c49\uff0c\u5316\u8eab\u5bab\u536b\uff0c\u5b88\u4f4f\u4e24\u5904\u91cd\u8981\u6240\u5728\uff0c\u603b\u7edf\u9886\u548c\u9ec4\u5c71\u4e03\u5251\u5de1\u884c\u5404 \u5904\uff0c\u6551\u5371\u89e3\u56f0\u3002 \u3000\u3000\u4e00\u4e0b\u5b50\u589e\u52a0\u4e86\u4e8c\u5341\u4e94\u5458\u9ad8\u624b\uff0c\u548c\u4e00\u4f4d\u7edd\u9876\u9ad8\u4eba\uff0c\u8c03\u52a8\u5c31\u8f7b\u677e\u591a\u4e86\u3002\u6240\u4ee5\uff0c\u5144\u5f1f\u5c31\u88ab\u6d3e\u6765 \u5185\u5bab\u542c\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u5218\u5144\uff01\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u8bba\u5bf9\u654c\u7ecf\u9a8c\uff0c\u5218\u5144\u5f3a\u8fc7\u6211\u7b49\u751a\u591a\uff0c\u8fd8\u671b\u4e0d\u541d\u8d50\u6559\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u8427\u5144\u5982\u6b64\u8bf4\uff0c\u5218\u67d0\u5c31\u6597\u80c6\u8bf4\u51e0\u53e5\u80ba\u8151\u4e4b\u8a00\u4e86\uff0c\u654c\u4eba\u5728\u653b\u52bf\u4e2d\u542b\u6709\u8c0b\u7565\uff0c\u6211\u4eec\u65e0\u6cd5\u62ff\u634f \u4ed6\u4f55\u65f6\u3001\u4f55\u5730\u5c55\u5f00\u4e3b\u653b\u3002\u6240\u4ee5\uff0c\u4f60\u3001\u6211\u3001\u5bab\u536b\u90fd\u8981\u8f6e\u6d41\u4f5c\u606f\u3001\u7761\u7720\uff0c\u4ee5\u4fdd\u6301\u4f53\u80fd\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u5144\u5f1f\u65e9\u5df2\u4e0b\u4ee4\u5bab\u536b\u9075\u529e\u2026\u2026\u201d \u3000\u3000\u7a81\u95fb\u51e0\u58f0\u957f\u5578\u4f20\u6765\uff01 \u3000\u3000\u7d27\u63a5\u7740\u4f20\u6765\u4e86\u5175\u5203\u4ea4\u51fb\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u6050\u6015\u662f\u4f11\u606f\u4e0d\u6210\u4e86\uff01\u201d\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u6b64\u756a\u653b\u52bf\u4e0d\u540c\uff0c\u4e5f\u8bb8\u662f\u4e00\u6b21\u5168\u9762\u653b\u52bf\u3002\u201d \u3000\u3000\u4f46\u95fb\u5175\u5203\u7684\u4ea4\u51fb\u58f0\uff0c\u5ef6\u6f2b\u3001\u6269\u5c55\uff0c\u7ef5\u8fde\u4e86\u5341\u4f59\u4e08\u957f\u3002 \u3000\u3000\u786e\u4f3c\u662f\u4e00\u6b21\u5168\u9762\u653b\u52bf\uff0c\u53ea\u542c\u5175\u5203\u649e\u51fb\u7684\u58f0\u52bf\uff0c\u81f3\u5c11\u6709\u767e\u4eba\u4ee5\u4e0a\u5728\u5bab\u5899\u5916\u9762\u52a8\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u516d\u5341\u591a\u540d\u5bab\u536b\u62ff\u8d77\u4e86\u5175\u5203\uff0c\u7ad9\u5728\u4ed6\u4eec\u7684\u5c97\u4f4d\u4e0a\uff0c\u5168\u795e\u6212\u5907\u3002 \u3000\u3000\u5bab\u5973\u3001\u5f69\u5a25\u4eec\u5168\u90fd\u6324\u5165\u4e86\u623f\u4e2d\uff0c\u9664\u4e86\u7a97\u524d\u53ef\u89c1\u4eba\u9762\u6643\u52a8\u5411\u5916\u63a2\u89c6\u4e4b\u5916\uff0c\u5bbd\u5e7f\u7684\u5ead\u9662\u4e2d\u4e0d \u518d\u6709\u4eba\u8d70\u52a8\u3002 \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u771f\u662f\u96be\u4e3a\u5979\u4eec\u4e86\uff0c\u5185\u5bab\u4e00\u5411\u662f\u83ba\u58f0\u3001\u71d5\u8bed\u4e0d\u7edd\u4e8e\u8033\uff0c\u73b0\u5728\u4e5f\u53d8\u5f97\u9e26\u96c0\u65e0\u58f0\u4e86\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u5979\u4eec\u5df2\u4f53\u4f1a\u5230\u77ac\u95f4\u5c31\u5931\u53bb\u751f\u547d\u7684\u53ef\u6015\uff0c\u4e5f\u611f\u89c9\u5230\u5200\u67aa\u65e0\u60c5\u4e86\uff0c\u66f4\u4e0d\u4f1a\u601c\u9999\u60dc\u7389\u4e86\u3002\u201d \u3000\u3000\u4f46\u95fb\u7fbd\u7ffc\u5212\u7a7a\uff0c\u516d\u4e2a\u9e1f\u4eba\u8d8a\u8fc7\u5bab\u5899\u800c\u81f3\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u4eec\u4e0d\u518d\u5ba2\u6c14\u4e86\u3002 \u3000\u3000\u8fd8\u8ddd\u79bb\u4e09\u4e08\u591a\u8fdc\uff0c\u5df2\u5f20\u5f00\u957f\u5634\u55b7\u51fa\u4e86\u6bd2\u70df\u3002 \u3000\u3000\u5218\u98de\u9e4f\u53cc\u624b\u9f50\u626c\uff0c\u516d\u628a\u98de\u5200\u4e00\u8d77\u51fa\u624b\uff0c\u4eba\u5374\u4ef0\u8eab\u800c\u5367\uff0c\u8d34\u5730\u6eda\u51fa\u4e86\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u65e9\u77e5\u6bd2\u70df\u5389\u5bb3\uff0c\u98de\u8eab\u8dc3\u5165\u4e00\u5835\u5899\u58c1\u4e4b\u540e\u3002 \u3000\u3000\u5bf9\u4ed8\u9e1f\u4eba\uff0c\u667a\u53d6\u80dc\u8fc7\u529b\u654c\u3002 \u3000\u3000\u516d\u9053\u5bd2\u8292\uff0c\u5206\u88ad\u5165\u9e1f\u4eba\u9888\u4e0b\u3002 \u3000\u3000\u90a3\u6b63\u662f\u9a7e\u9e1f\u4eba\u7684\u5fc3\u80f8\u8981\u5bb3\u3002 \u3000\u3000\u51e0\u58f0\u60e8\u53eb\uff01 \u3000\u3000\u516d\u53ea\u5de8\u9e1f\u4e00\u8d77\u8dcc\u843d\u5730\u4e0a\uff0c\u6bd2\u70df\u4e5f\u5931\u53bb\u4e86\u51c6\u5934\uff0c\u55b7\u5411\u4e00\u4fa7\u3002 \u3000\u3000\u5218\u98de\u9e4f\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u6211\u6ca1\u6709\u8bb0\u9519\u4ed6\u4eec\u7684\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u9ad8\u660e\u554a\uff01\u5218\u5144\uff0c\u5200\u4e0d\u865a\u53d1\uff0c\u5f53\u4eca\u98de\u5200\u624b\uff0c\u53ea\u6015\u65e0\u4eba\u80fd\u51fa\u5218\u5144\u4e4b\u53f3\u4e86\u3002\u201d \u3000\u3000\u4f46\u89c1\u4ed6\u7531\u5899\u540e\u7ed5\u51fa\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5fc3\u6697\u7b97\uff01\u201d \u3000\u3000\u957f\u5251\u51fa\u624b\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u67b6\u5f00\u4e86\u523a\u5411\u5218\u98de\u9e4f\u7684\u4e00\u5251\u3002 \u3000\u3000\u6562\u60c5\u90a3\u5de8\u9e1f\u4e4b\u4e2d\u8fd8\u85cf\u7740\u4eba\u3002 \u3000\u3000\u662f\u7684\uff0e\u516d\u53ea\u5de8\u9e1f\u7684\u8eab\u8eaf\u4e2d\u5192\u51fa\u4e94\u4e2a\u9ed1\u8863\u4eba\uff0c\u52a0\u4e0a\u7a81\u88ad\u5218\u98de\u9e4f\u7684\u4e00\u4e2a\uff0c\u516d\u53ea\u5de8\u9e1f\u8fd0\u5165\u4e86\u516d \u4f4d\u9ed1\u8863\u5251\u624b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e9b\u5de8\u9e1f\u9664\u4e86\u4ee5\u6bd2\u70df\u3001\u6697\u5668\u4f24\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u8fd0\u9001\u4eba\u624b\u7684\u4f5c\u7528\u3002 \u3000\u3000\u4e94\u4f4d\u9ed1\u8863\u5251\u624b\u633a\u5251\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u3001\u5218\u98de\u9e4f\u5404\u63a5\u4e0b\u4e00\u4eba\uff0c\u5c55\u5f00\u6076\u6218\u3002 \u3000\u3000\u8fd8\u6709\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\uff0c\u5e76\u65e0\u52a9\u6218\u7684\u610f\u601d\uff0c\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u4f3c\u5728\u5bfb\u89c5\u7a7a\u9699\uff0c\u6709\u7740\u51b2\u5165 \u5bab\u4e2d\u7684\u6253\u7b97\u3002 \u3000\u3000\u4e24\u7ec4\u5bab\u536b\u6025\u5feb\u7684\u51b2\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6bcf\u7ec4\u4e94\u4e2a\u4eba\uff0c\u628a\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\u5206\u5f00\u5305\u56f4\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u4e2d\u660e\u767d\uff0c\u4e00\u65e6\u8ba9\u8fd9\u4e9b\u9ed1\u8863\u5251\u624b\u51b2\u5165\u4e86\u5bab\u4e2d\uff0c\u5c31\u5982\u9c7c\u5165\u5927\u6d77\uff0c\u864e\u5165\u7f8a\u7fa4\u4e2d\u3002\u518d\u60f3\u56f4 \u5835\u4ed6\u4eec\uff0c\u5c31\u5341\u5206\u56f0\u96be\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5230\u5904\u53ef\u95ef\uff0c\u89c1\u4eba\u5c31\u6740\uff0c\u8840\u6e85\u6df1\u5bab\uff0c\u60ca\u6270\u7687\u9a7e\uff0c\u5ad4\u5983\u906d\u6b83\uff0c\u5bab\u5973\u6b92\u547d\uff0c\u90a3\u4e2a\u60e8\u72b6\u4e00\u60f3 \u5230\u5c31\u53eb\u4eba\u5fc3\u751f\u5bd2\u610f\u3002 \u3000\u3000\u51e0\u5341\u4e2a\u5bab\u536b\u5c31\u7b97\u5168\u90fd\u62fc\u4e0a\u8001\u547d\uff0c\u4e5f\u672a\u5fc5\u80fd\u628a\u4e24\u4eba\u56f4\u4f4f\u3002 \u3000\u3000\u73b0\u5728\u4e94\u4e2a\u4eba\u5bf9\u4ed8\u4e00\u4e2a\uff0c\u867d\u7136\u672a\u5fc5\u80fd\u591f\u5f97\u80dc\uff0c\u4f46\u662f\u820d\u547d\u653b\u51fb\uff0c\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\u5374\u4e5f\u65e0\u6cd5\u8131\u8eab \u4e86\u3002 \u3000\u3000\u8427\u96e8\u662f\u5fc3\u4e2d\u6709\u8c31\u4e86\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5b9d\u8d35\u5983\u5df2\u627f\u8bfa\u8d1f\u8d23\u5bab\u4e2d\u5b89\u5371\uff0c\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\u51b2\u8fdb\u53bb\uff0c\u4f60\u53ef\u4ee5\u4e0d\u7ba1\u3002\u4f46\u4ed6\u4eec\u51fa\u624b\u6740\u4e86 \u4eba\uff0c\u96be\u9053\u4f60\u4e5f\u4e0d\u7ba1\u5417\uff1f\u201d \u3000\u3000\u5f88\u5e0c\u671b\u63a2\u77e5\u4e00\u4e0b\u5b9d\u8d35\u5983\u7684\u5fc3\u610f\uff0c\u727a\u7272\u4e00\u4e24\u4e2a\u5bab\u5973\u6027\u547d\uff0c\u4e5f\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u4f46\u5bab\u536b\u6b66\u58eb\u4eec\u4e0d\u4e86\u89e3\u8427\u96e8\u7684\u5fc3\u610f\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u5df2\u4e0b\u5b9a\u51b3\u5fc3\u5168\u529b\u963b\u6b62\u9ed1\u8863\u5251\u624b\u95ef\u5165\u5185\u5bab\uff0c\u8981\u6218\u6b7b\uff0c\u8fd9\u662d\u9633\u5bab\u9662\u95e8\u524d\u548c\u5ead\u9662\u4e2d\u5c31 \u662f\u57cb\u9aa8\u7684\u597d\u5730\u65b9\u3002 \u3000\u3000\u81f3\u5c11\u53ef\u4ee5\u8ba9\u90a3\u4e9b\u4e91\u96c6\u5929\u4e0b\u7684\u7f8e\u5973\u4eec\uff0c\u770b\u5230\u4e86\u4ed6\u4eec\u7684\u52c7\u6562\u3002 \u3000\u3000\u6b66\u58eb\u594b\u6218\u6b7b\uff0c\u8840\u67d3\u5bab\u95e8\u7ea2\uff1b\u4e5f\u662f\u4e3a\u4e86\u4fdd\u536b\u5979\u4eec\u800c\u6b7b\uff0c\u6362\u5f97\u51e0\u70b9\u540c\u60c5\u7684\u6cea\u73e0\uff0c\u51e0\u58f0\u627c\u8155\u53f9 \u606f\uff0c\u603b\u6bd4\u6b7b\u5f97\u65e0\u58f0\u65e0\u606f\u7684\u597d\u3002 \u3000\u3000\u4eba\u5230\u4e86\u65e0\u5948\u7684\u65f6\u5019\uff0c\u603b\u4f1a\u60f3\u51fa\u5f88\u591a\u7406\u7531\u53bb\u7f8e\u5316\u5b83\u3002 \u3000\u3000\u6b7b\u4ea1\u4ea6\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u8bad\u7ec3\u51fa\u6765\u7684\u5bab\u536b\u6b66\u58eb\u662f\u5927\u968b\u671d\u4e2d\u6700\u597d\u7684\u6218\u58eb\uff0c\u4ed6\u4eec\u4e2a\u4e2a\u594b\u52c7\u3001\u4e3b\u52a8\uff0c\u89c6\u6b7b\u5982 \u65e7\u3002 \u3000\u3000\u6240\u4ee5\u6709\u5f88\u591a\u7684\u5bab\u536b\u8dd1\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u624b\u6267\u5175\u5203\uff0c\u795e\u60c5\u4e25\u8083\u7684\u7ad9\u5728\u4e00\u4fa7\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u7b49\u5f85\u7740\u62d2\u6321\u5f3a\u654c\u7684\u5144\u5f1f\u4eec\u6b7b\u4ea1\u540e\uff0c\u7acb\u523b\u8865\u4e0a\uff0c\u4e0d\u80fd\u518d\u7559\u7ed9\u654c\u4eba\u5f3a\u6e21\u5173\u5c71\u7684\u7a7a\u9699\u3002 \u3000\u3000\u8fd9\u516d\u4e2a\u9ed1\u8863\u5251\u624b\u5251\u827a\u7cbe\u6e5b\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u662f\u5c5e\u4e8e\u4e00\u6d41\u7684\u9ad8\u660e\u5251\u624b\u3002\u7530\u5f53\u3001\u8427\u96e8\u3001\u51cc\u4e91\u5c3d\u5c55 \u6240\u5b66\uff0c\u4e5f\u53ea\u80fd\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u5218\u98de\u9e4f\u672c\u6709\u8f7b\u654c\u4e4b\u5ff5\uff0c\u4e00\u5f00\u59cb\u5e76\u672a\u65bd\u5c55\u4ed6\u9738\u7edd\u5929\u4e0b\u7684\u98de\u5200\u672f\uff0c\u4ee5\u624b\u4e2d\u539a\u80cc\u5bbd\u9762\u5200\u62d2\u654c\u3002 \u4f46\u53d1\u89c9\u654c\u52bf\u5f3a\u5927\uff0c\u5251\u62db\u8be1\u5947\uff0c\u518d\u60f3\u65bd\u5c55\u98de\u5200\u7edd\u6280\u65f6\uff0c\u7adf\u7136\u65e0\u6cd5\u817e\u51fa\u4e00\u53ea\u624b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u624d\u60f3\u5230\u640f\u6740\u7684\u8f9b\u82e6\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u96c6\u4e2d\u5168\u795e\u5728\u8fce\u654c\u3002 \u3000\u3000\u5982\u6b64\u7684\u5f3a\u654c\uff0c\u5bab\u536b\u4eec\u7684\u6218\u6597\u5c31\u60e8\u4e0d\u5fcd\u7779\u4e86\u3002 \u3000\u3000\u5df2\u6709\u5341\u4e2a\u52c7\u6562\u7684\u5bab\u536b\u6a2a\u5c38\u5730\u4e0a\uff0c\u8840\u6d41\u6210\u6e20\uff0c\u6c14\u7edd\u6b7b\u4ea1\u4e86\u3002 \u3000\u3000\u4f46\u4ecd\u7136\u6709\u5341\u4e2a\u5206\u6210\u4e24\u7ec4\uff0c\u6bcf\u7ec4\u4e94\u4eba\uff0c\u56f4\u6218\u4e24\u4e2a\u9ed1\u8863\u5251\u58eb\u3002 \u3000\u3000\u53e6\u6709\u4e8c\u5341\u591a\u4e2a\u624b\u6267\u5175\u5203\u7684\u5bab\u536b\uff0c\u9762\u76ee\u51b7\u8083\u5730\u7ad9\u5728\u4e00\u4fa7\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u7b49\u5f85\u7740\u8865\u5145\u6b7b\u4ea1\u3002 \u3000\u3000\u8427\u96e8\u770b\u5230\u4e86\u6ee1\u5730\u9c9c\u8840\uff0c\u5fc3\u4e2d\u5341\u5206\u6c89\u75db\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4e00\u4e2a\u5a07\u5c0f\u7f8e\u4e3d\u7684\u5c0f\u4f73\u4eba\uff0c\u7adf\u662f\u5982\u6b64\u7684\u5fc3\u5982\u94c1\u77f3\uff0c\u773c\u770b\u5341\u4e2a\u4eba\u6b7b\u4e8e\u5bf9\u65b9\u5251\u4e0b\uff0c\u7adf\u80fd\u89c6\u82e5 \u65e0\u7779\uff0c\u96be\u9053\u8981\u7b49\u5230\u8fd9\u4e9b\u5bab\u536b\u5168\u6570\u6218\u6b7b\uff0c\u5979\u624d\u51fa\u624b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u65f6\u9ed1\u8863\u5251\u624b\u95ef\u5165\u4e86\u662d\u9633\u9662\u4e2d\uff0c\u6b7b\u7684\u5c31\u4e0d\u662f\u8fd9\u4e9b\u820d\u8eab\u62d2\u654c\u6b7b\u800c\u65e0\u6028\u7684\u5bab\u536b\u4e86\uff0c\u800c\u662f\u5a07\u67d4 \u7f8e\u4e3d\u7684\u5bab\u5973\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u5229\u5203\u5212\u8fc7\u8840\u8089\u7684\u201c\u6c99\u5566\u201d\u4e4b\u58f0\uff01 \u3000\u3000\u53c8\u662f\u4e00\u4e2a\u5bab\u536b\u88ab\u5229\u5251\u523a\u7a7f\u524d\u80f8\u800c\u4ea1\u3002 \u3000\u3000\u8427\u96e8\u6574\u4e2a\u4eba\u8981\u6c14\u66b4\u4e86\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5168\u529b\u653b\u51fa\u4e09\u5251\uff0c\u628a\u5bf9\u624b\u903c\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4ed6\u5927\u58f0\u559d\u9053\uff1a \u3000\u3000\u201c\u5b9d\u8d35\u5983\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u58f0\u97f3\u523a\u8033\uff0c\u4f3c\u662f\u5168\u529b\u568e\u51fa\u6765\u7684\uff0c\u96be\u542c\u6781\u4e86\u3002 \u3000\u3000\u201c\u6211\u5728\u6210\u5168\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5e0c\u671b\u52c7\u5f80\u65e0\u60e7\u7684\u7cbe\u795e\u80fd\u5f97\u5230\u7f8e\u5973\u5782\u9752\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u4e0d\u77e5\u4f55\u65f6\u51fa\u73b0\uff0c\u624b\u4e2d\u4e00\u67c4\u5251\uff0c\u529b\u62d2\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\uff0c\u6b63\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u5979\u7f13\u7f13\u5730\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u60f3\u5f97\u7684\uff0c\u90fd\u5f97\u5230\u4e86\uff0c\u6211\u770b\u5230\u5979\u4eec\u611f\u541b\u820d\u8eab\u53cc\u5782\u6cea\uff0c\u6b32\u62a5\u541b\u6069\u82e6\u65e0\u95e8\uff0c\u6211\u542c\u5230\u5979\u4eec \u53f9\u606f\u3001\u4f4e\u6ce3\u2026\u2026\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u770b\u5230\u4e09\u4e94\u4e2a\u820d\u8eab\u6210\u4ec1\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u5341\u4e2a\u4eba\u6a2a\u5c38\u5bab\u95e8\uff0c\u8840\u6d41\u6ee1\u5730\uff0c\u4f60\u5c31\u5168\u65e0\u607b\u9690\u4e4b\u5fc3 \u5417\uff1f\u4ed6\u4eec\u4e0d\u662f\u548c\u4eba\u62fc\u6597\uff0c\u800c\u662f\u5341\u6210\u5341\u7684\u9001\u6b7b\u554a\uff01\u201d \u3000\u3000\u201c\u6b7b\u540e\u65b9\u77e5\u60c5\u610f\u771f\uff01\u201d\u8881\u5b9d\u513f\u7b11\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e9b\u5bab\u5973\u4eec\u65e9\u5c31\u6ed1\u51fa\u6cb9\u4e86\uff0c\u5982\u679c\u4e0d\u8ba9\u5979\u4eec\u5370\u8c61\u6df1\u523b\uff0c\u4e00\u8f6c\u773c\u5c31\u5fd8\u8bb0\u4e86\u3002\u8ba9\u5979\u6df1\u8bb0\u60e8 \u72b6\uff0c\u4e5f\u8981\u8ba9\u5979\u4f53\u4f1a\u5230\u90a3\u4e9b\u4eba\u662f\u4e3a\u4fdd\u62a4\u5979\u4eec\u800c\u6b7b\uff0c\u6ca1\u6709\u90a3\u4e9b\u5bab\u536b\u4eec\u62fc\u6b7b\u62d2\u654c\uff0c\u6b7b\u7684\u5c31\u662f\u5979 \u4eec\u3002\u201d \u3000\u3000\u8427\u96e8\u542c\u5f97\u4e00\u5446\uff0c\u4f3c\u662f\u88ab\u523a\u4e2d\u4e00\u5251\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u90aa\u7684\u5389\u5bb3\u5440\uff01\u5341\u4e94\u516d\u5c81\u7684\u6e05\u79c0\u5c0f\u59d1\u5a18\uff0c\u8bf4\u51fa\u6b6a\u7406\u5341\u516b\u7ae0\uff0c\u54ea\u91cc\u6709\u4e00\u70b9\u59d1\u5a18\u5bb6\u7684\u6e29\u67d4\u3001 \u4ec1\u6148\u3002\u201d \u3000\u3000\u4ec0\u4e48\u539f\u56e0\u5462\uff1f \u3000\u3000\u662f\u4e00\u4e2a\u4eba\u6709\u5982\u6b64\u51b7\u9177\u8f6c\u53d8\uff0c\u5185\u5fc3\u548c\u5916\u8c8c\u6781\u7aef\u4e0d\u540c\u5417\uff1f \u3000\u3000\u201c\u8427\u5c06\u519b\uff0c\u6211\u4e5f\u7f69\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u72ec\u62d2\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\uff0c\u5251\u6cd5\u5df2\u7ecf\u6709\u4e9b\u6563\u4e71\u4e86\uff0c\u82e5\u518d\u8fc7\u4e09\u4e94\u4e2a\u56de\u5408\uff0c\u5f88\u53ef\u80fd\u8981\u4f24\u5728\u5bf9 \u65b9\u5251\u4e0b\u3002 \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u8fd9\u516d\u4f4d\u9ed1\u8863\u5251\u624b\u90fd\u662f\u8d85\u7ea7\u5251\u58eb\uff0c\u6211\u4eec\u90fd\u81ea\u987e\u4e0d\u6687\u4e86\uff0c\u6ca1\u6709\u4eba\u80fd\u817e\u51fa\u5de5\u592b\u6765\u5e2e\u52a9\u4f60 \u5440\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u6211\u5802\u5802\u8d35\u5983\u8eab\u4efd\uff0c\u4f60\u4eec\u7adf\u6562\u89c1\u6b7b\u4e0d\u6551\uff1f\u201d \u3000\u3000\u8427\u96e8\u641e\u4e0d\u61c2\u8881\u5b9d\u513f\u7684\u76ee\u7684\u4e86\uff1f \u3000\u3000\u56e0\u800c\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u201c\u5b9d\u8d35\u5983\uff01\u201d\u7530\u5f53\u5f00\u4e86\u53e3\uff0c\u9053\uff1a \u3000\u3000\u201c\u5200\u53bb\u5251\u6765\uff0c\u751f\u6b7b\u4e00\u53d1\uff0c\u6211\u4eec\u60f3\u6551\u4f60\u4e5f\u4e0d\u6015\u6b7b\u3002\u53ef\u662f\uff0c\u6ca1\u529e\u6cd5\u5440\uff01\u5bf9\u65b9\u5251\u52bf\u7d27\u5bc6\uff0c\u817e\u4e0d \u51fa\u624b\u811a\uff0c\u8d35\u5983\u672f\u6cd5\u7cbe\u6e5b\uff0c\u4e3a\u4f55\u4e0d\u80af\u65bd\u5c55\u5462\uff1f\u201d \u3000\u3000\u201c\u65e9\u8bf4\u5440\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u6b66\u58eb\u51b3\u6218\uff0c\u4e0d\u5916\u5200\u5251\uff0c\u539f\u6765\u4e5f\u53ef\u4ee5\u65bd\u5c55\u672f\u6cd5\u5440\uff01\u201d \u3000\u3000\u6ca1\u4eba\u80fd\u60f3\u5f97\u901a\u8881\u5b9d\u513f\u8fd9\u51e0\u53e5\u8bdd\u7684\u610f\u601d\uff1f \u3000\u3000\u8427\u96e8\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u7530\u5f53\u4e5f\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u8fd9\u662f\u94f2\u9664\u53db\u9006\u7684\u751f\u6b7b\u4e4b\u6218\uff0c\u53c8\u4e0d\u662f\u6bd4\u6b66\u5b9a\u540d\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5df2\u5c55\u5f00\u4e86\u65e0\u60c5\u7684\u5c60\u622e\uff0c\u53ea\u89c1\u5979\u5fae\u7b11\u5982\u82b1\uff0c\u626c\u624b\u4e00\u6307\u4e24\u4e2a\u9ed1\u8863\u5251\u624b\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8be5 \u6b7b\u554a\uff01\u6740\u4e86\u90a3\u4e48\u591a\u5bab\u536b\uff0c\u5e94\u5904\u8170\u65a9\u3001\u780d\u8111\u888b\uff0c\u53ef\u4ee5\u75bc\u56db\u5341\u4e94\u91cc\uff0c\u8170\u65a9\u500d\u4e4b\u3002\u201d \u3000\u3000\u83ba\u58f0\u71d5\u8bed\uff0c\u5a07\u751c\u52a8\u4eba\uff0c\u8bf4\u7684\u5374\u662f\u5929\u4e0b\u6700\u6b8b\u5fcd\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u800c\u4e14\u8bf4\u4e86\u5c31\u505a\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u4e24\u4e2a\u5251\u624b\u88ab\u62e6\u8170\u4e2d\u622a\u56db\u65ad\uff0c\u8eaf\u4f53\u5728\u8840\u6cca\u4e2d\u98a4\u6296\u3002 \u3000\u3000\u201c\u8fd8\u6709\u4f60\u4eec\u56db\u4e2a\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u7ea4\u624b\u4e00\u6307\u548c\u8427\u96e8\u6076\u6218\u7684\u9ed1\u8863\u5251\u624b\u3002 \u3000\u3000\u602a\u5566\uff01 \u3000\u3000\u9ed1\u8863\u5251\u624b\u7a81\u7136\u7ad9\u7740\u4e0d\u52a8\u4e86\uff0c\u786c\u633a\u7740\u7b49\u5f85\u8881\u5b9d\u513f\u7684\u5251\u52bf\u62e6\u8170\u65a9\u8fc7\u3002 \u3000\u3000\u5218\u98de\u9e4f\u3001\u7530\u5f53\u3001\u51cc\u4e91\uff0c\u4e09\u4eba\u5bf9\u624b\u5982\u51fa\u4e00\u8f99\uff0c\u7ad9\u5728\u90a3\u91cc\u7b49\u7740\u8881\u5b9d\u513f\u6325\u5251\u628a\u4ed6\u4eec\u6740\u4e86\u3002 \u3000\u3000\u654c\u4eba\u8840\u6e85\u4e94\u6b65\u3002 \u3000\u3000\u8427\u96e8\u7b49\u4e5f\u5168\u90fd\u770b\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u597d\u9ad8\u660e\u7684\u9694\u7a7a\u6253\u7a74\uff01\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u6c5f\u6e56\u7edd\u6280\uff0c\u5f53\u4e4b\u65e0\u6127\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7b11\u4e00\u7b11\uff0c\u4e5f\u4e0d\u89e3\u91ca\u3002 \u3000\u3000\u5979\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\uff0c\u6211\u5e94\u8be5\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u8d35\u5983\u8eab\u4efd\uff0c\u6211\u4eec\u90fd\u5e94\u8be5\u542c\u4f60\u5429\u5490\u554a\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\u662f\u4e2a\u4eba\u624d\uff0c\u80fd\u8bad\u7ec3\u51fa\u89c6\u6b7b\u5982\u5f52\u7684\u52c7\u58eb\uff0c\u96be\u80fd\u53ef\u8d35\u5440\uff01\u6211\u5728\u60f3\u2026\u2026\u5e94\u4e0d\u5e94\u8be5\u518d \u5e2e\u4ed6\u4e00\u628a\uff0c\u4e00\u7adf\u5168\u529f\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u60f3\u7684\u7f8e\u5440\uff01\u53ef\u60dc\u5929\u4e0b\u4e0d\u5982\u4eba\u610f\u4e8b\u5341\u5360\u516b\u4e5d\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u8f6c\u5934\u770b\u53bb\u2014\u2014 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u5f69\u8863\u7684\u4fee\u8eaf\u9053\u4eba\uff0c\u795e\u8272\u51b0\u51b7\u5730\u7ad9\u5728\u56db\u4e94\u5c3a\u5916\u3002 \u3000\u3000\u4e0d\u77e5\u4ed6\u4f55\u65f6\u6765\u7684\uff1f \u3000\u3000\u4e5f\u4e0d\u77e5\u4ed6\u5982\u4f55\u6765\u7684\uff1f \u3000\u3000\u50cf\u662f\u5f88\u4e45\u4e4b\u524d\uff0c\u4ed6\u5c31\u7ad9\u5728\u90a3\u513f\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6253\u773c\u4e00\u7784\uff0c\u5df2\u77e5\u6653\u6765\u4e86\u5bf9\u5934\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e5f\u4f3c\u7cbe\u901a\u672f\u6cd5\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u548c\u5218\u98de\u9e4f\u5df2\u88ab\u6765\u4eba\u8eab\u4e0a\u6563\u53d1\u51fa\u7684\u8be1\u5f02\u6c14\u6c1b\u6240\u9707\u52a8\uff0c\u8fd9\u4eba\u5168\u8eab \u4f3c\u88ab\u4e00\u7247\u9ed1\u6c14\u5305\u56f4\uff0c\u9b3c\u6c14\u68ee\u68ee\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5ae3\u7136\u4e00\u7b11\uff01 \u3000\u3000\u5979\u5f00\u4e86\u53e3\uff0c\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\uff0c\u600e\u4e48\u79f0\u547c\u554a\uff1f\u201d \u3000\u3000\u201c\u8d2b\u9053\u4e03\u7edd\uff0c\u59d1\u5a18\u662f\u8881\u7d2b\u70df\uff1f\u6216\u662f\u8881\u5b9d\u513f\uff1f\u201d \u3000\u3000\u6562\u60c5\u4e24\u4f4d\u8d35\u5983\u7684\u5927\u540d\u65e9\u5df2\u4f20\u5165\u6c5f\u6e56\u4e2d\u4e86\u3002 \u3000\u3000\u201c\u743c\u82b1\u9662\u7684\u8881\u5b9d\u513f\uff0c\u6709\u5e78\u5f97\u9047\u9053\u957f\u3002\u201d \u3000\u3000\u5979\u59ff\u8272\u7edd\u4e16\uff0c\u7b11\u5f97\u52a8\u4eba\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u5df2\u88ab\u5979\u95f9\u5f97\u5fc3\u733f\u610f\u9a6c\u4e86\u3002 \u3000\u3000\u201c\u6b64\u8bed\u4f55\u89e3\uff1f\u8d2b\u9053\u9886\u6559\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u7ec8\u4e8e\u89c1\u5230\u4e86\u4e00\u4e2a\u6709\u70b9\u5206\u91cf\u7684\u4eba\u7269\uff0c\u5e78\u4f55\u5982\u4e4b\u3002\u201d \u3000\u3000\u539f\u6765\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u67d4\u60c5\u871c\u610f\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u9053\uff1a \u3000\u3000\u201c\u53ea\u6709\u4e00\u70b9\u5206\u91cf\u800c\u5df2\uff0c\u59d1\u5a18\u7684\u53e3\u6c14\u597d\u751f\u6258\u5927\u554a\uff01\u4f60\u6740\u4e86\u5927\u738b\u7684\u516d\u4f4d\u5251\u624b\uff0c\u51c6\u5907\u5982\u4f55\u4ea4\u4ee3 \u554a\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u64c5\u95ef\u7981\u5bab\uff0c\u6b7b\u5f97\u5e94\u8be5\u3002\u201d\u8881\u5b9d\u513f\u7b11\u9053\uff1a \u3000\u3000\u201c\u9053\u957f\u60f3\u4e3a\u4ed6\u4eec\u62a5\u4ec7\uff0c\u653e\u9a6c\u8fc7\u6765\u5c31\u662f\u3002\u4f60\u53ef\u8981\u4ed4\u7ec6\u60f3\u60f3\u554a\uff01 \u3000\u3000\u6211\u8d25\u4e86\uff0c\u9876\u591a\u4e0d\u51fa\u743c\u82b1\u9662\uff1b\u4f60\u8d25\u4e86\uff0c\u5c31\u8981\u5728\u6c5f\u6e56\u9664\u540d\u4e86\u3002\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u95ee\u9898\u662f\u8d2b\u9053\u4e0d\u4f1a\u8d25\uff0c\u5c0f\u5c0f\u5973\u5b50\u80e1\u8a00\u4e71\u8bed\uff0c\u8d2b\u9053\u8981\u628a\u4f60\u6293\u56de\u53bb\uff0c\u597d\u597d\u7684\u60e9\u7f5a\u4f60\u4e00 \u756a\u2026\u2026\u201d \u3000\u3000\u6700\u540e\u8fd9\u53e5\u8bdd\u624d\u662f\u4e03\u7edd\u9053\u957f\u5fc3\u4e2d\u8981\u8bf4\u7684\u6d3b\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u6293\u6211\u56de\u53bb\u5e72\u4ec0\u4e48\uff1f\u4f60\u5df2\u7ecf\u8272\u5fc3\u5927\u52a8\u4e86\uff1f\u201d \u3000\u3000\u624b\u4e2d\u957f\u5251\u4e00\u6307\uff0c\u98de\u8d77\u4e00\u9053\u5bd2\u8292\uff0c\u75be\u5c04\u8fc7\u53bb\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u8c08\u8c08\u4e00\u7b11\uff0c\u53f3\u624b\u8f7b\u5f39\uff0c\u98de\u51fa\u4e86\u4e94\u4e2a\u91d1\u4e38\uff0c\u6321\u4f4f\u957f\u5251\u3002 \u3000\u3000\u770b\u4f3c\u5e7b\u5f71\uff01 \u3000\u3000\u4f46\u8033\u9645\u95f4\u53ef\u95fb\u91d1\u94c1\u649e\u51fb\u4e4b\u58f0\uff01 \u3000\u3000\u8881\u5b9d\u513f\u54af\u54af\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u56db\u4f4d\u5c06\u519b\uff0c\u8ba9\u4e00\u8ba9\u5427\uff01\u76f8\u6253\u65e0\u597d\u624b\uff0c\u522b\u8ba9\u9c9c\u8840\u6e85\u810f\u4e86\u8bf8\u4f4d\u7684\u8863\u670d\u3002\u201d \u3000\u3000\u8427\u96e8\u7b49\u89c1\u8fc7\u4e86\u8881\u7d2b\u70df\u548c\u4e09\u85cf\u548c\u5c1a\u6597\u6cd5\uff0c\u6b66\u529f\u4e00\u70b9\u4e5f\u6d3e\u4e0d\u4e0a\u7528\u573a\u3002 \u3000\u3000\u56db\u4eba\u968f\u5373\u5411\u540e\u9000\uff0c\u5fc3\u4e2d\u77e5\u9053\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u53cd\u800c\u53ef\u80fd\u4f7f\u8881\u8881\u513f\u5fc3\u751f\u987e\u5fcc\u3002\u6240\u4ee5\uff0c\u4e00\u9000\u9000\u5230\u4e86 \u5bab\u5899\u8fb9\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u63a5\u8d2b\u9053\u4e00\u8bb0\u4e09\u6627\u795e\u5149\uff01\u201d \u3000\u3000\u53f3\u624b\u8fce\u9762\u62cd\u6765\u3002 \u3000\u3000\u672c\u53ea\u662f\u4e00\u8bb0\u666e\u901a\u7684\u638c\u98ce\uff0c\u4f46\u4e8e\u4e09\u5c3a\u540e\uff0c\u7a81\u7136\u751f\u51fa\u4e86\u4e00\u7247\u706b\u82d7\uff0c\u76f4\u5411\u8881\u5b9d\u513f\u63b7\u88ad\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u4e94\u884c\u795e\u672f\u554a\uff01\u59d1\u5a18\u5c31\u966a\u4f60\u73a9\u51e0\u62db\u5427\uff01\u201d \u3000\u3000\u5de6\u624b\u5c31\u5730\u753b\u51fa\u4e86\u4e24\u4e2a\u5341\u5b57\u3002 \u3000\u3000\u7adf\u7136\u5192\u51fa\u4e86\u4e00\u80a1\u55b7\u6cc9\u3002 \u3000\u3000\u6cc9\u6c34\u795e\u901f\u6269\u5c55\u3001\u6321\u4f4f\u4e86\u98de\u6765\u7684\u706b\u82d7\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u59d1\u5a18\uff0c\u4e0d\u7b80\u5355\u554a\uff01\u8fd9\u6837\u62fc\u4e0b\u53bb\uff0c\u8d2b\u9053\u6050\u6015\u8981\u5931\u624b\u4f24\u4eba\u4e86\uff0c\u4f60\u8fd9\u4e48\u5982\u82b1\u4f3c\u7389\u7684\u4eba\u95f4\u7edd \u8272\uff0c\u6b7b\u4e86\u5b9e\u5728\u53ef\u60dc\u3002\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u77e5\u8c01\u80dc\u8c01\u8d25\u5462\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u4e3a\u4f60\u81ea\u5df1\u803d\u803d\u5fc3\u5427\uff01\u201d \u3000\u3000\u773c\u770b\u7a7a\u4e2d\u7a81\u7136\u5192\u51fa\u706b\u82d7\uff0c\u800c\u4e14\u4e0d\u505c\u7684\u66b4\u957f\uff0c\u4f3c\u662f\u6709\u6108\u70e7\u6108\u70c8\u4e4b\u52bf\u3002 \u3000\u3000\u5730\u4e0a\u88ab\u753b\u4e86\u4e00\u4e0b\uff0c\u5192\u51fa\u4e00\u80a1\u6cc9\u6c34\u6765\uff0c\u7ed3\u6210\u4e86\u96fe\u5e18\u6c34\u5e55\uff0c\u6321\u4f4f\u4e86\u51b2\u8fc7\u6765\u7684\u718a\u718a\u706b\u52bf\uff0c\u4e0d\u5927 \u4e5f\u4e0d\u5c0f\u3002 \u3000\u3000\u8427\u96e8\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u5440\uff1f\u6f88\u5171\u6f88\u5c3e\u7684\u90aa\u672f\u3002\u90a3\u51e0\u80a1\u70c8\u7130\u3001\u706b\u82d7\u662f\u4e0d\u662f\u771f\u7684\u706b\u5462\uff1f\u80fd\u5426\u70e7\u7269\u4f24\u4eba\uff1f \u6c34\u4ece\u54ea\u91cc\u6765\uff1f\u80fd\u4e0d\u80fd\u70eb\u4eba\u8863\u886b\uff1f\u4f1a\u4e0d\u4f1a\u662f\u969c\u773c\u6cd5\u5462\uff1f\u201d \u3000\u3000\u5218\u98de\u9e4f\u7591\u7136\u9053\uff1a \u3000\u3000\u201c\u6211\u770b\u4e0d\u662f\u771f\u7684\u2026\u2026\u4f46\u662f\u770b\u4e0a\u53bb\u5374\u50cf\u771f\u7684\u4e00\u6837\uff0c\u6c34\u706b\u62d2\u6301\uff0c\u8000\u773c\u751f\u82b1\uff0c\u4e0d\u8bd5\u8bd5\u5f88\u96be\u7518\u5fc3 \u554a\uff01\u201d \u3000\u3000\u201c\u65e2\u6709\u540c\u611f\uff0c\u4f55\u4e0d\u8bd5\u5b83\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u8427\u96e8\u62d4\u51fa\u4e86\u957f\u5251\uff0c\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u5218\u98de\u9e4f\u4e00\u628a\u62e6\u4f4f\u4e86\u8427\u96e8\uff0c\u9053\uff1a \u3000\u3000\u201c\u8ba9\u6211\u6765\uff01\u201d \u3000\u3000\u4ed6\u4eec\u4e60\u6b66\u3001\u7ec3\u5200\u6570\u5341\u5e74\uff0c\u53d7\u5c3d\u4e86\u8f9b\u82e6\uff0c\u53ef\u4e0d\u987e\u672f\u6cd5\u7684\u6210\u5c31\uff0c\u4e00\u4e0b\u5b50\u5c31\u593a\u53bb\u4e86\u6b66\u529f\u7684\u5149 \u8f89\u3002 \u3000\u3000\u5fc3\u4e2d\u4e0d\u4fe1\u662f\u672f\u6cd5\u7684\u5947\u53d8\uff0c\u8ba4\u5b9a\u5b83\u662f\u969c\u773c\u6cd5\u3002 \u3000\u3000\u5218\u98de\u9e4f\u53cc\u624b\u4e00\u626c\uff0c\u4e24\u67c4\u98de\u5200\u7a7f\u8fc7\u4e86\u706b\u82d7\u3002 \u3000\u3000\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff01 \u3000\u3000\u98de\u5200\u53c8\u7a7f\u8fc7\u706b\u82d7\u98de\u56de\u6765\u3002 \u3000\u3000\u63a5\u4f4f\u98de\u5200\u2026\u2026 \u3000\u3000\u5218\u98de\u9e4f\u5dee\u4e00\u70b9\u677e\u624b\u4e22\u5200\u3002 \u3000\u3000\u539f\u6765\uff0c\u53cc\u5200\u4e0a\u8fd8\u6709\u4f59\u70ed\u3002 \u3000\u3000\u70ed\u5f97\u70eb\u624b\uff0c\u662f\u771f\u6b63\u7531\u9ad8\u70ed\u7684\u706b\u7130\u4e2d\u7a7f\u8fc7\u3002 \u3000\u3000\u706b\u662f\u771f\u706b\uff0c\u800c\u4e14\u70ed\u5ea6\u5f88\u9ad8\u3002 \u3000\u3000\u6c34\u5462\uff1f \u3000\u3000\u5218\u98de\u9e4f\u53c8\u53d1\u51fa\u4e24\u628a\u98de\u5200\uff0c\u7a7f\u8fc7\u4e86\u96fe\u5e18\u6c34\u5e55\u3002 \u3000\u3000\u5200\u529b\u56de\u65cb\uff0c\u91cd\u5165\u624b\u4e2d\u3002 \u3000\u3000\u679c\u7136\u6c34\u6e7f\u6cbe\u624b\uff0c\u5c31\u50cf\u4e24\u628a\u98de\u5200\u521a\u7531\u6c34\u4e2d\u635e\u51fa\u6765\u3002 \u3000\u3000\u4e0d\u4fe1\u4e5f\u4e0d\u884c\u4e86\uff01 \u3000\u3000\u5218\u98de\u9e4f\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u662f\u771f\u7684\uff0c\u771f\u706b\u771f\u6c34\uff0c\u7adf\u80fd\u4ee5\u672f\u6cd5\u8ba9\u5b83\u4eec\u5e73\u7a7a\u51fa\u73b0\uff0c\u8fd9\u6b66\u529f\u4e00\u9053\uff0c\u7ec3\u8d77\u6765\u53ef\u5c31\u5473\u540c\u56bc\u8721 \u4e86\u3002\u201d \u3000\u3000\u201c\u5218\u5144\uff01\u201d\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u672f\u6cd5\u548c\u6b66\u529f\u662f\u4e24\u6869\u6f20\u4e0d\u76f8\u5173\u7684\u4e8b\uff0c\u672f\u6cd5\u80fd\u632a\u79fb\u7269\u4e8b\u4e8e\u5343\u91cc\u4e4b\u5916\uff0c\u80fd\u65e0\u4e2d\u751f\u6709\u3002\u4f46\u672f\u6cd5 \u4e0d\u662f\u4eba\u4eba\u80fd\u5b66\uff0c\u4e5f\u65e0\u6cd5\u957f\u5b58\u4e8e\u6c5f\u6e56\u3002 \u3000\u3000\u4ed6\u4eec\u4fee\u7ec3\u6570\u5341\u767e\u5e74\uff0c\u53ea\u80fd\u5929\u6daf\u4e00\u89d2\u3001\u4eba\u8db3\u8ff9\u7f55\u81f3\u7684\u6240\u5728\uff0c\u548c\u4e00\u4e9b\u9634\u90aa\u6c47\u805a\u7684\u9b51\u9b45\u9b4d\u9b49\u5468 \u65cb\u3002\u6b66\u529f\u5374\u5802\u5802\u6b63\u6b63\u51fa\u73b0\u5728\u4eba\u4e16\u95f4\u3001\u6c5f\u6e56\u9053\u4e0a\u3002\u201d \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u4e5f\u51fa\u73b0\u5728\u4eba\u4e16\u95f4\u4e86\u3002\u201d\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u800c\u4e14\u662f\u96c6\u4e2d\u5929\u4e0b\u6743\u52bf\u7684\u5bab\u5ef7\u3002\u201d \u3000\u3000\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u6240\u4ee5\uff0c\u5929\u4e0b\u8981\u5927\u4e71\u4e86\u3002\u8fd9\u4e00\u9635\u6df7\u4e71\u4e4b\u540e\uff0c\u8eab\u5177\u672f\u6cd5\u7684\u4eba\u5c31\u4f1a\u6d88\u5931\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4f1a\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u5929\u9063\u554a\uff01\u201d\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u4e0d\u662f\u906d\u62a5\u8eab\u4ea1\uff0c\u5c31\u662f\u8fd8\u5176\u672c\u6027\uff0c\u53d8\u6210\u4e86\u666e\u901a\u5e73\u51e1\u7684\u4eba\uff0c\u5931\u53bb\u65bd\u672f\u4e4b\u80fd\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u70b9\u5934\u53c8\u6447\u5934\u3002 \u3000\u3000\u663e\u7136\u5fc3\u4e2d\u4f3c\u4fe1\u975e\u4fe1\u3002 \u3000\u3000\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u662f\u4eba\u7684\u4e16\u754c\uff0c\u624d\u80fd\u867d\u7136\u6709\u9ad8\u4f4e\uff0c\u5f62\u8c8c\u4ea6\u5206\u5978\u5ab8\uff0c\u4f46\u662f\u4e00\u5b9a\u5728\u4eba\u7684\u80fd\u529b\u8303\u56f4\u4e4b\u5185\u3002 \u8eab\u5177\u672f\u6cd5\uff0c\u5e38\u98df\u4eba\u95f4\u70df\u706b\uff0c\u7075\u5149\u3001\u4ed9\u6c14\u90fd\u5728\u6d88\u9000\u3002\u56e0\u4e3a\uff0c\u8fd9\u91cc\u6709\u4e03\u60c5\u516d\u6b32\uff0c\u662f\u4e0d\u9002\u5408\u4ed6\u4eec\u751f \u5b58\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u9053\u7406\u554a\uff01\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u5973\u4eba\u751f\u5b69\u5b50\u540e\uff0c\u7075\u529b\u3001\u4ed9\u6c14\u6050\u6015\u5168\u90fd\u4f1a\u6d88\u5931\u4e0d\u89c1\u4e86\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u8881\u5b9d\u513f\u5a07\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u8001\u5996\u9053\uff0c\u8fd8\u6709\u4ec0\u4e48\u90aa\u672f\u5996\u6cd5\uff1f\u5feb\u4e9b\u4f7f\u51fa\u6765\uff0c\u5426\u5219\uff0c\u6211\u8981\u51fa\u624b\u53d6\u547d\u4e86\uff01\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\uff01\u81ea\u53d6\u706d\u4ea1\uff0c\u602a\u4e0d\u5f97\u8d2b\u9053\u4e86\u3002\u201d \u3000\u3000\u5de6\u624b\u98df\u3001\u4e2d\u4e8c\u6307\u7a81\u7136\u4e00\u5f39\uff0c\u90a3\u63b7\u66f2\u5728\u4e8c\u4e2a\u6307\u5934\u4e0a\u7684\u6307\u7532\u7a81\u7136\u4f38\u76f4\u4e86\u3002\u7adf\u7136\u6709\u4e00\u5c3a\u591a\u957f\uff0c \u8fd8\u771f\u662f\u5413\u4eba\u5462\uff01 \u3000\u3000\u4f38\u76f4\u7684\u6307\u7532\u4e2d\uff0c\u5192\u51fa\u4e86\u4e24\u70b9\u9752\u5149\u3002 \u3000\u3000\u706b\u82d7\u6d88\u5931\u4e86\uff0c\u4f46\u9752\u5149\u5374\u8fc5\u5feb\u5730\u6269\u5927\u3002 \u3000\u3000\u8881\u5b9d\u513f\u88ab\u9752\u5149\u88f9\u4f4f\u4e86\u3002 \u3000\u3000\u9752\u8272\u8d8a\u6765\u8d8a\u6d53\uff0c\u5a07\u7f8e\u7684\u8eab\u5f71\u6d88\u5931\u5728\u9752\u5149\u4e2d\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6d8c\u51fa\u7684\u6c34\u96fe\u5e18\u5e55\u4e5f\u6d88\u5931\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8881\u5b9d\u513f\u4f3c\u662f\u6574\u4e2a\u4eba\u88ab\u5305\u56f4\u5728\u9752\u5149\u91cc\u3002 \u3000\u3000\u9752\u5149\u6eda\u52a8\uff0c\u5f62\u6210\u4e86\u4e00\u4e2a\u5927\u5706\u7403\uff0c\u76f4\u98de\u9ad8\u7a7a\u3002 \u3000\u3000\u7530\u5f53\u62d4\u51fa\u4e86\u53cc\u5200\u3002 \u3000\u3000\u4ed6\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u8427\u5144\u3001\u5218\u5144\u3001\u51cc\u5144\uff0c\u4eca\u671d\u662f\u54b1\u4eec\u6548\u547d\u7687\u4e0a\uff0c\u62a5\u7b54\u5b87\u6587\u603b\u7edf\u9886\u7684\u65f6\u523b\u4e86\uff0c\u5e76\u80a9\u5b50\u4e00\u9f50\u4e0a \u5427\uff01\u201d \u3000\u3000\u8427\u96e8\u4e5f\u62e8\u51fa\u4e86\u957f\u5251\uff0c\u82e6\u7b11\u9053\uff1a \u3000\u3000\u201c\u5b9d\u8d35\u5983\u7684\u672f\u6cd5\u4e0d\u53ca\u56fd\u5e08\u591a\u77e3\uff01\u201d \u3000\u3000\u5218\u98de\u9e4f\u53cc\u624b\u5404\u63e1\u4e86\u56db\u628a\u98de\u5200\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u516b\u5200\u51fa\u624b\uff0c\u4e24\u4f4d\u518d\u653b\u4e0a\u53bb\uff0c\u6211\u8fd8\u6709\u56db\u67c4\u98de\u5200\u652f\u63f4\uff0c\u7528\u5c3d\u4e86\u538b\u7bb1\u7684\u672c\u9886\u3001\u4e0d\u80fd\u80dc\u654c\u4e5f \u662f\u6b7b\u800c\u65e0\u61be\u4e86\u3002\u201d \u3000\u3000\u7a81\u89c1\u706b\u5149\u4e00\u95ea\uff0c\u7d27\u63a5\u7740\u4e00\u58f0\u5927\u7206\uff0c\u58f0\u5982\u5de8\u96f7\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5de6\u624b\u634f\u8bc0\uff0c\u53f3\u8896\u62c2\u52a8\uff0c\u98d8\u98d8\u7136\u4ece\u7a7a\u800c\u964d\u3002 \u3000\u3000\u518d\u770b\u4e03\u7edd\u9053\u957f\uff0c\u4e24\u6839\u4e00\u5c3a\u591a\u957f\u7684\u6307\u7532\uff0c\u65e9\u5df2\u788e\u88c2\u65ad\u53bb\u3002 \u3000\u3000\u770b\u4f3c\u4ed9\u5973\u51cc\u6ce2\uff0c\u5189\u5189\u800c\u4e0b\uff0c\u5176\u5b9e\u76f8\u5f53\u5feb\u901f\u3002\u7530\u5f53\u3001\u8427\u96e8\u8fd8\u672a\u6765\u53ca\u51fa\u624b\uff0c\u8881\u5b9d\u513f\u5df2\u843d\u7740\u5b9e \u5730\u3002 \u3000\u3000\u4ecd\u662f\u5979\u505c\u8eab\u7684\u5730\u65b9\uff0c\u5206\u6beb\u4e0d\u5dee\u3002 \u3000\u3000\u201c\u9ed4\u9a74\u6280\u7a77\u4e86\u5427\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u4f60\u7adf\u7136\u7ec3\u6210\u4e86\u4e59\u6728\u7cbe\u6c14\u2026\u2026\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u6211\u53ea\u60f3\u628a\u4f60\u79fb\u690d\u4e8e\u5ce8\u5d4b\u5c71\u91d1\u9876\u5cf0\u4e0b\uff0c\u90a3\u91cc\u5f97\u5929\u751a\u539a\uff0c\u4e07\u7269\u5411\u8363\uff0c\u5e76\u65e0\u52a0\u5bb3\u59d1\u5a18\u7684\u610f \u601d\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff01\u8ba9\u6211\u53d8\u6210\u4e00\u9897\u6811\u7f8e\u4eba\uff0c\u957f\u5e74\u76db\u5f00\u5404\u8272\u5947\u82b1\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u628a\u6211\u5236\u4e8e\u4f60\u7684\u63a7\u5236\u4e4b\u4e0b\uff0c\u9ad8\u5174\u4e86\u5c31\u628a\u6211\u62db\u51fa\u6765\uff0c\u966a\u4f60\u996e\u9152\u4f5c\u4e50\uff0c\u4e0d\u9ad8\u5174\u5c31\u628a\u6211\u85cf\u4e8e\u6811 \u4e2d\uff0c\u6c38\u8fdc\u4e0d\u80fd\u7ffb\u8eab\u3002\u201d \u3000\u3000\u8427\u96e8\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u600e\u4e48\u4f1a\u6709\u8fd9\u7b49\u4e8b\u60c5\uff0c\u628a\u4eba\u6eb6\u4e8e\u6811\u4e2d\uff0c\u8fd8\u80fd\u53ec\u5979\u51fa\u6765\u966a\u9152\uff0c\u8fd9\u672f\u6cd5\u4e4b\u5947\uff0c\u5f53\u771f\u662f\u532a\u5937\u6240 \u601d\u4e86\u3002\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u6ca1\u60f3\u5230\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u7136\u7ec3\u6210\u4e86\u5929\u96f7\u795e\u672f\u3002\u8fd9\u4e00\u8bb0\u795e\u96f7\u70b8\u6563\u4e86\u8d2b\u9053\u82e6\u7ec3\u4e94\u5341\u5e74\u805a \u96c6\u7684\u4e59\u6728\u7cbe\u6c14\u3002\u8d2b\u9053\u8ba4\u4e86\uff0c\u5c31\u6b64\u7f62\u624b\uff0c\u8d2b\u9053\u5c06\u9041\u5165\u6df1\u5c71\uff0c\u6c38\u4e0d\u518d\u5c65\u7ea2\u5c18\u4e00\u6b65\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u662f\u866c\u9aef\u5ba2\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u4e03\u7edd\u9053\u957f\u9053\uff1a \u3000\u3000\u201c\u662f\u7684\uff01\u4f46\u662f\u8d2b\u9053\u662f\u5ba2\u537f\u7684\u8eab\u4efd\uff0c\u968f\u65f6\u53ef\u4ee5\u79bb\u5f00\uff0c\u59d1\u5a18\u5982\u80fd\u653e\u624b\uff0c\u8d2b\u9053\u5373\u523b\u5f52\u9690\u5c71 \u4e2d\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u4e59\u6728\u7cbe\u6c14\u4e43\u4e07\u6728\u4e4b\u6e90\uff0c\u5dee\u4e00\u70b9\u56f0\u4f4f\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u8d2b\u9053\u5df2\u7ecf\u518d\u4e09\u81f4\u6b49\uff0c\u59d1\u5a18\u4e0d\u53ef\u903c\u4eba\u8fc7\u751a\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u542c\u8d77\u6765\uff0c\u4f60\u597d\u50cf\u8fd8\u6709\u5947\u6280\u672a\u7528\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u5947\u6280\uff0c\u662f\u62fc\u547d\u4e00\u6218\uff01\u201d\u4e03\u7edd\u9053\u957f\u9053\uff1a \u3000\u3000\u201c\u8d2b\u9053\u4ee5\u5206\u8840\u5927\u6cd5\u548c\u59d1\u5a18\u5bf9\u51b3\uff0c\u5c31\u7b97\u547d\u6b92\u5f53\u573a\uff0c\u76f8\u4fe1\u59d1\u5a18\u4e5f\u4f1a\u6709\u6240\u635f\u4f24\u4e86\uff0c\u4e0d\u7ba1\u4f24\u91cd\u3001 \u4f24\u8f7b\uff0c\u53ea\u6050\u82b1\u5bb9\u6708\u8c8c\u4e5f\u4f1a\u6709\u635f\u4f24\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u76f4\u63a5\u7684\u5a01\u80c1\u3002 \u3000\u3000\u8bf4\u660e\u4e86\uff0c\u8981\u5bf9\u7740\u8881\u5b9d\u513f\u7684\u8138\u4e0a\u5e72\u3002 \u3000\u3000\u4ec0\u4e48\u662f\u5206\u8840\u5927\u6cd5\uff1f \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u3001\u5218\u98de\u9e4f\u90fd\u542c\u4e0d\u61c2\u3002 \u3000\u3000\u4f46\u8881\u5b9d\u513f\u61c2\u3002 \u3000\u3000\u90a3\u662f\u4fee\u4e60\u9053\u6cd5\u4eba\u7684\u640f\u547d\u4e4b\u6218\uff0c\u7528\u5185\u529b\u9000\u51fa\u8eab\u4e0a\u9c9c\u8840\uff0c\u6df7\u5165\u4e86\u771f\u5143\u4e4b\u6c14\uff0c\u6709\u5982\u5927\u96e8\u503e\u76c6\u800c \u4e0b\uff0c\u6570\u4e08\u65b9\u5706\u8bf4\u4e3a\u8840\u96e8\u7b3c\u7f69\u3002\u4e2d\u4e00\u70b9\uff0c\u5c31\u5f97\u8eab\u53d7\u91cd\u4f24\u3002 \u3000\u3000\u4f46\u5206\u8840\u5927\u6cd5\u5bf9\u65bd\u7528\u4eba\u7684\u4f24\u5bb3\u66f4\u5927\uff0c\u5206\u8840\u4e0d\u591a\uff0c\u5a01\u529b\u4e0d\u591f\u5f3a\u5927\uff1b\u5206\u8840\u5927\u591a\uff0c\u81ea\u7136\u4f1a\u5143\u6c14\u5927 \u4f24\u3002\u77ed\u65f6\u95f4\u5185\uff0c\u4e5f\u65e0\u62d2\u654c\u4e4b\u80fd\u3002 \u3000\u3000\u4e5f\u6709\u4eba\u5b64\u6ce8\u4e00\u63b7\uff0c\u51c6\u5907\u4e0e\u654c\u5055\u4ea1\uff0c\u4e00\u6b21\u5c3d\u51fa\u5168\u8eab\u4e4b\u8840\u3002 \u3000\u3000\u8881\u5b9d\u513f\u6c89\u601d\u4e86\u2026\u2026 \u3000\u3000\u5206\u8840\u5927\u6cd5\u4e0d\u662f\u4e00\u822c\u672f\u6cd5\u53ef\u4ee5\u963b\u6321\u7684\uff0c\u4eba\u8eab\u4e4b\u8840\uff0c\u52a0\u4e0a\u4ed6\u4fee\u4e3a\u529f\u529b\uff0c\u5fc5\u987b\u8981\u771f\u6b63\u7684\u6b66\u529f\u548c \u771f\u6b63\u7684\u7269\u5668\u624d\u80fd\u963b\u6b62\u3002 \u3000\u3000\u5979\u4e0d\u60f3\u4f24\u5728\u5206\u8840\u5927\u6cd5\u4e2d\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u613f\u653e\u8fc7\u4e03\u7edd\u9053\u957f\uff0c\u56e0\u4e3a\u8fd9\u4eba\u662f\u866c\u9aef\u5ba2\u7684\u5c5e\u4e0b\uff0c\u4e5f\u662f\u674e\u4e16\u6c11\u7684\u91cd\u8981\u654c\u624b\uff0c\u7edd\u5bf9\u4e0d\u80fd \u653e\u8fc7\u3002 \u3000\u3000\u76ee\u7779\u8881\u5b9d\u513f\u8138\u4e0a\u795e\u60c5\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u4e03\u7edd\u9053\u957f\u7a81\u7136\u54ac\u7834\u820c\u5c16\uff0c\u55b7\u51fa\u4e00\u7247\u8840\u96e8\uff0c\u4eba\u5df2\u7eb5\u8eab\u800c \u8d77\uff0c\u9690\u4e8e\u8840\u96e8\u4e4b\u4e2d\u3002 \u3000\u3000\u8840\u96e8\u8fc5\u5feb\u6269\u5927\uff0c\u5f62\u6210\u4e86\u6570\u4e08\u65b9\u5706\u4e00\u56e2\u7ea2\u4e91\uff0c\u5411\u4e0a\u5347\u53bb\u3002 \u3000\u3000\u8033\u9645\u95f4\u4f20\u6765\u4e86\u4e03\u7edd\u9053\u957f\u7684\u58f0\u97f3\uff0c\u9053\uff1a \u3000\u3000\u201c\u60f3\u4e0d\u5230\u5185\u5bab\u4e4b\u4e2d\u7adf\u6709\u8d35\u5983\u8fd9\u7b49\u9ad8\u4eba\uff0c\u8d2b\u9053\u7b54\u5e94\u4f60\u5f52\u9690\u6df1\u5c71\uff0c\u7edd\u4e0d\u98df\u8a00\u3002\u4e0d\u8fc7\uff0c\u4eba\u95f4\u70df \u706b\u4f1a\u6d88\u8680\u9053\u57fa\uff0c\u59d1\u5a18\u771f\u9634\u5df2\u5931\uff0c\u5982\u4e0d\u9041\u5c71\u82e6\u4fee\uff0c\u4e0d\u51fa\u5341\u5e74\uff0c\u5373\u5c06\u53d8\u6210\u4eba\u95f4\u8102\u7c89\uff0c\u7075\u529b\u3001\u672f\u6cd5 \u5c31\u4f1a\u5b8c\u5168\u6d88\u5931\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5f88\u53ef\u60dc\uff0c\u6211\u8fde\u9041\u5165\u6df1\u5c71\u7684\u5fc3\u613f\uff0c\u6050\u4e5f\u6709\u6240\u4e0d\u80fd\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u5a07\u53f1\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f55\u65b9\u5996\u4eba\uff0c\u7adf\u6562\u60ca\u6270\u540e\u5bab\uff0c\u81ea\u5bfb\u6b7b\u8def\u3002\u201d \u3000\u3000\u4e00\u58f0\u9739\u96f3\u96f7\u9707\uff01 \u3000\u3000\u7d27\u63a5\u7740\u4e00\u9053\u91d1\u8679\u76f4\u5c04\u7ea2\u4e91\u4e4b\u4e2d\u3002 \u3000\u3000\u8033\u9645\u95f4\u54cd\u8d77\u4e03\u7edd\u9053\u957f\u4e00\u58f0\u60e8\u53eb\uff01 \u3000\u3000\u7ea2\u4e91\u6d88\u6563\uff0c\u8840\u96e8\u98d8\u6d12\u3002 \u3000\u3000\u4e03\u7edd\u9053\u957f\u7684\u5c38\u4f53\u7adf\u88ab\u8170\u65a9\u4e24\u622a\uff0c\u8dcc\u843d\u4e0b\u6765\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7d27\u63a5\u7740\u73b0\u8eab\u671b\u4e86\u5b9d\u513f\u4e00\u773c\uff0c\u9053\uff1a \u3000\u3000\u201c\u7687\u4e0a\u3001\u7687\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u9686\u4e2d\u9ad8\u5367\uff0c\u5c31\u662f\u5728\u996e\u9152\u4f5c\u4e50\u5427\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u3001\u5218\u98de\u9e4f\uff0c\u56db\u4f4d\u5c06\u519b\u628a\u654c\u4eba\u963b\u4e8e\u5bab\u95e8\u4e4b\u524d\uff0c\u672a\u5165\u662d\u9633\u9662\u4e2d\uff0c\u5bab\u5a25\u4eec \u53ea\u53d7\u4e00\u4e9b\u865a\u60ca\u3002\u201d \u3000\u3000\u201c\u5168\u662f\u5b9d\u8d35\u5983\u7684\u529f\u52b3\u3002\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u5df2\u62b5\u654c\u4e0d\u4f4f\uff0c\u5b9d\u8d35\u5983\u53ca\u65f6\u800c\u81f3\uff0c\u72ec\u5288\u516d\u5251\u58eb\uff0c\u53c8\u548c\u8fd9\u4e2a\u5996\u9053\u6597\u6cd5\u5f88\u4e45\uff0c\u5996\u9053\u4e0d\u654c \u9003\u8d70\u3002\u56fd\u5e08\u5c31\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u8eab\u5177\u672f\u6cd5\u7684\u4eba\uff0c\u600e\u80fd\u5165\u5c18\u4e16\u548c\u51e1\u4eba\u4e89\u529f\uff1f\u8fd9\u4e2a\u4eba\u975e\u6740\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u624d\u60f3\u5230\u81ea\u5df1\u4e5f\u662f\u8eab\u5177\u672f\u6cd5\u7684\u4eba\uff0c\u5982\u6b64\u65bd\u4e3a\uff0c\u5fc5\u906d\u5929\u5fcc\u3002 \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u5982\u975e\u6709\u4eba\u5e72\u4e0e\u5929\u673a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u52a8\u5fc3\u65bd\u6027\u4e0b\u51e1\u5c18\u3002\u60f3\u4e0d\u5230\uff0c\u4e00\u6cbe\u4e0a\u4eba\u95f4\u7684\u70df\u706b\u6c14\uff0c\u60f3\u6446 \u8131\uff0c\u7adf\u662f\u5982\u6b64\u7684\u56f0\u96be\uff01\u201d \u3000\u3000\u201c\u771f\u7684\u80fd\u6446\u8131\u5f97\u4e86\u5417\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u53ea\u6015\u5df2\u662f\u6ce5\u8db3\u6df1\u9677\uff0c\u6b32\u62d4\u4e0d\u80fd\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u5f88\u96be\u3002\u201d\u8881\u7d2b\u70df\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c3d\u529b\u800c\u4e3a\u5427\uff01\u770b\u5bab\u5899\u5916\u5c38\u4f53\u6a2a\u9648\uff0c\u4f3c\u7ecf\u8fc7\u4e86\u591a\u6b21\u51b2\u6740\u6076\u6218\uff0c\u5b87\u6587\u5c06\u519b\u8bad\u7ec3\u51fa\u7684\u52c7\u58eb\uff0c \u679c\u7136\u662f\u80fd\u5f53\u5927\u4efb\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u662f\u554a\uff01\u5982\u975e\u5b87\u6587\u5c06\u519b\u8fd9\u4e00\u6279\u52c7\u58eb\u4eec\u820d\u6b7b\u4ea1\u751f\uff0c\u62d2\u6297\u5f3a\u654c\uff0c\u8fd9\u662d\u9633\u9662\u53ea\u6015\u4e5f\u4f1a\u6709\u8840\u6d17\u4e4b \u5371\u3002\u6b64\u5904\u5f3a\u654c\u6682\u9000\uff0c\u4f46\u4e0d\u77e5\u5927\u519b\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u88ab\u6211\u7528\u4e94\u884c\u5947\u672f\u5927\u632a\u79fb\u628a\u4ed6\u4eec\u963b\u5728\u5f53\u5730\uff0c\u4e24\u65e5\u591c\u4e0d\u80fd\u63a8\u8fdb\uff0c\u519b\u5fc3\u6563\u4e71\uff0c\u5df2\u7ecf\u64a4\u9000\u4e86\uff0c \u53ef\u60dc\u6ca1\u6709\u8ffd\u6740\u7684\u5175\u529b\uff0c\u5426\u5219\u8fd9\u4e00\u6218\u5c31\u4f1a\u6d88\u706d\u6389\u4ed6\u4eec\u5168\u90e8\u6218\u529b\u3002\u201d \u3000\u3000\u201c\u7d2b\u70df\u59d0\u6ca1\u6709\u65bd\u5c55\u672f\u6cd5\u8ffd\u6740\u5417\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u5f3a\u654c\u9000\u800c\u4e0d\u6563\uff0c\u8fd8\u4f1a\u518d\u6765\u5440\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u539f\u60f3\u4ee5\u672f\u6cd5\u8ffd\u51fb\uff0c\u6740\u4ed6\u4e2a\u7247\u7532\u4e0d\u7559\uff0c\u4f46\u60f3\u4e00\u60f3\u6709\u5e72\u5929\u548c\uff0c\u4e5f\u5927\u80cc\u4fee\u9053\u4eba\u7684\u89c4\u6212\uff0c\u53ea\u597d \u4efb\u654c\u9000\u53bb\u3002\u7b49\u5b87\u6587\u5c06\u519b\u7ec3\u6210\u7cbe\u5175\uff0c\u518d\u51fa\u5175\u5f81\u8ba8\u5427\uff01\u201d \u3000\u3000\u201c\u603b\u7b97\u6e21\u8fc7\u4e86\u8fd9\u573a\u52ab\u96be\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a\u201c\u4e03\u7edd\u9053\u957f\u7684\u4e59\u6728\u7cbe\u6c14\u4f24\u6211\u5f88\u91cd\uff0c\u6211\u5f97\u53bb\u597d\u597d\u5750 \u606f\u4e00\u9635\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u53bb\uff0c\u7f13\u6b65\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u8881\u5b9d\u513f\u7684\u80cc\u5f71\uff0c\u8881\u7d2b\u70df\u5f00\u53e3\u60f3\u53eb\uff0c\u4f46\u5374\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u76ee\u5149\u4e00\u63a0\u5218\u3001\u51cc\u3001\u8427\u3001\u7530\uff0c\u9053\uff1a \u3000\u3000\u201c\u8bf8\u4f4d\u5c06\u519b\uff0c\u5371\u673a\u5c1a\u672a\u8fc7\u53bb\uff0c\u866c\u9aef\u5ba2\u52c7\u51a0\u4e09\u519b\uff0c\u5251\u4e0a\u5de8\u5927\u7684\u529b\u91cf\u7adf\u7136\u4ee5\u5251\u6c14\u7a81\u7834\u672f\u6cd5\u7684 \u56f4\u56f0\uff0c\u8131\u8eab\u800c\u53bb\uff0c\u5bf9\u8fd9\u6b21\u5931\u8d25\u975e\u5e38\u61ca\u607c\uff0c\u5f88\u540e\u6094\u4e0d\u542c\u674e\u9756\u4e4b\u8a00\u3002 \u3000\u3000\u4f46\u4ed6\u4e2a\u6027\u5f3a\u608d\uff0c\u4e5f\u4e0d\u613f\u5c31\u6b64\u670d\u8f93\uff0c\u51c6\u5907\u5165\u5bab\u5ef7\u6740\u4e86\u7687\u4e0a\u3001\u7687\u540e\uff0c\u4ee5\u8212\u80f8\u4e2d\u4e4b\u6c14\uff0c\u6240\u4ee5\u5371 \u673a\u5e76\u672a\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\uff01\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u4ec0\u4e48\u65f6\u5019\u4f1a\u6765\u5462\uff1f\u6211\u4eec\u597d\u597d\u5730\u57cb\u4f0f\u4ee5\u5f85\u3002\u201d \u3000\u3000\u201c\u89c1\u8fc7\u56fd\u5e08\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5168\u8eab\u662f\u8840\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u201c\u5c06\u519b\u53d7\u4f24\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u95ee\u7684\u5f88\u5173\u5fc3\uff0c\u76ee\u5149\u4e2d\u6ee1\u662f\u60c5\u6000\u3002 \u3000\u3000\u201c\u640f\u6740\u5f97\u592a\u6fc0\u70c8\u4e86\uff0c\u4eba\u5728\u8840\u4e2d\u7a9c\u52a8\uff0c\u5168\u8eab\u5c31\u6ca1\u6709\u4e00\u5904\u5e72\u51c0\u4e86\u3002 \u3000\u3000\u4f46\u6210\u90fd\u6258\u56fd\u5e08\u4e4b\u798f\uff0c\u4eba\u5c1a\u5e73\u5b89\u65e0\u4e8b\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u662f\u4f60\u6b66\u529f\u9ad8\u5f3a\uff0c\u624d\u80fd\u5728\u6fc0\u70c8\u7684\u640f\u6740\u4e2d\u5b89\u7136\u65e0\u6059\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u89c1\u8bc6\u5230\u866c\u9aef\u5ba2\u7684\u5251\u6cd5\uff0c\u624d\u77e5\u4ec0\u4e48\u662f\u5251\u5ba2\uff1f\u4ec0\u4e48\u53eb\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u4e45\u95fb\u4ed6\u5251\u672f\u3001\u6b66\u529f\u5df2\u7ecf\u767b\u5cf0\u9020\u6781\uff0c\u6210\u90fd\u4e00\u76f4\u672a\u5f97\u4f1a\u6664\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5c06\u519b\uff01\u5f88\u4e0d\u5e78\uff0c\u4ed6\u8981\u6765\u957f\u5b89\u5bab\u5ef7\uff0c\u51c6\u5907\u6740\u541b\u4e86\uff0c\u5c06\u519b\u867d\u7136\u6b66\u52c7\u8fc7\u4eba\uff0c\u6050\u6015\u4e5f\u4e0d\u662f\u4ed6\u7684 \u654c\u624b\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u8038\u52a8\u4e00\u4e0b\u53cc\u7709\uff0c\u9053\uff1a \u3000\u3000\u201c\u518d\u52a0\u4e0a\u516d\u4f4d\u526f\u7edf\u9886\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u95ee\u9053\uff1a \u3000\u3000\u201c\u53ea\u5269\u4e0b\u516d\u4f4d\u526f\u7edf\u9886\u4e86\uff0c\u5176\u4f59\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u4e3a\u56fd\u6b89\u804c\u4e86\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u4e00\u4f4d\u4f24\u91cd\u800c\u6b7b\uff0c\u4e09\u4f4d\u5f53\u573a\u6b89\u8eab\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u8fd8\u4f59\u4e0b\u54ea\u4e24\u4f4d\uff1f\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u4e2d\u6d41\u51fa\u4e86\u6cea\u6c34\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5468\u4fe1\u548c\u738b\u5e73\uff0c\u4ed6\u4eec\u7684\u5c0f\u5de7\u529f\u592b\u9ad8\u660e\uff0c\u957f\u4e8e\u95ea\u907f\uff0c\u5168\u8eab\u5b8c\u597d\uff0c\u8fde\u4e00\u70b9\u5c0f\u4f24\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u5c11\u6797\u5341\u516b\u7f57\u6c49\u548c\u9ec4\u5c71\u4e03\u5251\u5462\uff1f\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u8fd8\u672a\u8d70\uff0c\u5c31\u7559\u4ed6\u4eec\u4e0b\u6765\uff0c\u518d\u5e2e\u4e00\u6b21\u5fd9\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u5c31\u7b97\u672a\u8d70\uff0c\u6211\u4e5f\u4e0d\u5fcd\u5f00\u53e3\u4e86\u3002\u5c11\u6797\u5341\u516b\u7f57\u6c49\u6218\u6b7b\u4e86\u5341\u4e00\u4e2a\uff0c\u4f59\u4e0b\u7684\u4e03\u4e2a\u4eba\u4e2d\u4e09\u4f4d\u53d7 \u4f24\uff0c\u4e86\u4e86\u5927\u5e08\u4e5f\u65ad\u4e86\u4e00\u81c2\u3002 \u3000\u3000\u9ec4\u5c71\u4e03\u5251\uff0c\u6b7b\u4e86\u4e09\u4e2a\uff0c\u4f59\u4e0b\u56db\u4f4d\u6ca1\u6709\u4e00\u4eba\u5b8c\u6574\u7684\u3002\u6211\u529d\u7559\u4e0b\u533b\u4f24\uff0c\u4ed6\u4eec\u4e0d\u80af\u3002\u5305\u624e\u4e86\u4e00 \u4e0b\uff0c\u7acb\u523b\u52a8\u8eab\u4e86\u3002 \u3000\u3000\u8d60\u4e0e\u91cd\u91d1\uff0c\u4e5f\u88ab\u62d2\u6536\u3002\u5c31\u8fd9\u6837\u5e26\u7740\u4f24\u52bf\u800c\u53bb\uff0c\u6b7b\u5c38\u4e5f\u7531\u4ed6\u4eec\u96c7\u8f66\u8fd0\u8d70\uff1b\u542c\u8bf4\u8981\u5728\u57ce\u5916\u706b \u5316\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u6447\u6447\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e00\u5343\u540d\u6311\u9009\u7684\u7cbe\u9510\u52c7\u58eb\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u6b7b\u4e09\u767e\u591a\u4e2a\uff0c\u867d\u7136\u4e0d\u662f\u5343\u519b\u4e07\u9a6c\u7684\u5bf9\u9635\uff0c\u4f46\u5374\u662f\u6700\u6b8b\u9177\u7684\u8840\u62fc\uff0c\u6740\u5f97\u65e0\u58f0\u65e0\u606f\uff0c\u6740\u5f97 \u8840\u8089\u6a2a\u98de\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u518d\u52a0\u5468\u4fe1\u3001\u738b\u5e73\uff0c\u548c\u773c\u524d\u7684\u56db\u4f4d\u5c06\u519b\uff0c\u53ea\u6015\u4e5f\u96be\u662f\u866c\u9aef\u5ba2\u7684\u654c\u624b\u3002\u4ed6\u7eb5\u8dc3\u5982\u98de\uff0c\u9a6d\u5251 \u4e00\u51fb\uff0c\u80fd\u53d6\u4eba\u5341\u4e08\u5916\u7684\u9879\u4e0a\u9996\u7ea7\u3002\u5c06\u519b\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u53ef\u6043\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5c31\u8981\u56fd\u5e08\u51fa\u624b\u4e86\uff0c\u7ecf\u8fc7\u5317\u5f81\u51e0\u573a\u4f1a\u6218\uff0c\u548c\u8fd9\u4e24\u65e5\u5bab\u5899\u5916\u7684\u706b\u62fc\uff0c\u6210\u90fd\u7684\u8c6a\u6c14\u3001\u50b2\u9aa8 \u5df2\u5b8c\u5168\u6d88\u5931\u4e86\u3002 \u3000\u3000\u5929\u4e0b\u9ad8\u4eba\u662f\u5982\u6b64\u4e4b\u591a\uff0c\u4ee5\u5f80\u5750\u4e95\u89c2\u5929\uff0c\u81ea\u8d1f\u82f1\u96c4\uff0c\u5982\u4eca\u60f3\u6765\uff0c\u5341\u5206\u53ef\u7b11\u3002\u5982\u975e\u5c11\u6797\u9ad8\u50e7 \u548c\u9ec4\u5c71\u4e03\u5251\u820d\u547d\u52a9\u6218\uff0c\u6628\u5929\u5c31\u88ab\u4eba\u6740\u5165\u5bab\u4e2d\u6765\u4e86\uff0c\u6210\u90fd\u65e0\u80fd\u554a\uff0c\u65e0\u80fd\uff01\u201d \u3000\u3000\u201c\u5c06\u519b\uff0c\u4e0d\u8981\u81ea\u8d23\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4f60\u8840\u67d3\u8863\u5c65\uff0c\u8bc1\u660e\u4f60\u4e00\u76f4\u5728\u53c2\u4e0e\u6076\u6218\uff1b\u4f60\u80a4\u53d1\u672a\u4f24\uff0c\u8bc1\u660e\u4e86\u4f60\u7684\u6280\u827a\u7cbe\u6e5b\uff0c\u6b66\u529f\u9ad8 \u5f3a\u3002\u4f60\u53ef\u80fd\u4e0d\u662f\u866c\u9aef\u5ba2\u7684\u5bf9\u624b\uff0c\u4f46\u653e\u773c\u5929\u4e0b\uff0c\u8c01\u53c8\u80fd\u80dc\u8fc7\u4ed6\u5462\uff1f\u6211\u5728\u60f3\u2026\u2026\u8fd9\u6837\u4e00\u4e2a\u5f3a\u52b2\u7684 \u4eba\u7269\uff0c\u8be5\u4e0d\u8be5\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u8427\u96e8\u9053\uff1a\u201c\u6740\u4ed6\u4e00\u4eba\uff0c\u53ef\u6551\u5343\u767e\u4eba\u7684\u6027\u547d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8be5\u6740\uff1f\u5e94\u8be5\u662f\u6740\u65e0\u8d66\u5440\uff01\u201d \u5367\u9f99\u751f\u300a\u8881\u7d2b\u70df\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u8c37\u5e95\u6cea\u75d5 \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4f1a\u672f\u6cd5\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4ed6\u7684\u6280\u827a\u90fd\u662f\u82e6\u7ec3\u51fa\u6765\u7684\uff0c\u6211\u82e5\u4ee5\u672f\u6cd5\u6740\u4ed6\uff0c\u6709\u4e9b\u80dc\u4e4b\u4e0d\u6b66\uff0c\u4e5f\u8fdd\u80cc\u4e86\u8eab\u5177\u672f\u6cd5\u7684\u6212 \u5f8b\u3002\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e48\u529e\u5427\uff01\u56fd\u5e08\u53ea\u628a\u4ed6\u5236\u670d\uff0c\u5728\u4e0b\u518d\u4ee5\u98de\u5200\u53d6\u4ed6\u6027\u547d\uff0c\u4eba\u4e0d\u662f\u56fd\u5e08\u6740\u7684\uff0c\u5e94\u8be5\u548c\u56fd\u5e08 \u65e0\u5173\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4ee5\u672f\u6cd5\u4f7f\u4ed6\u65e0\u80fd\u8fd8\u624b\uff0c\u4e0e\u6211\u6740\u4ed6\u4f55\u5f02\uff1f\u8fd9\u4ef6\u4e8b\u5bb9\u6211\u60f3\u4e00\u60f3\uff0c\u4e0d\u7ba1\u5982\u4f55\uff1f\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u4f24 \u4e86\u7687\u4e0a\u3001\u7687\u540e\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\uff0c\u6210\u90fd\u6709\u4e2a\u4e0d\u60c5\u4e4b\u6c42\uff0c\u8fd8\u671b\u56fd\u5e08\u6210\u5168\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u8bf4\u5427\uff01\u6211\u80fd\u505a\u5230\u7684\uff0c\u4e00\u5b9a\u6210\u5168\u4f60\u3002\u201d \u3000\u3000\u201c\u866c\u9aef\u5ba2\u4e00\u65e6\u95ef\u5165\u5bab\u5ef7\uff0c\u8bf7\u56fd\u5e08\u7ed9\u6210\u90fd\u673a\u4f1a\uff0c\u8ba9\u6211\u63a5\u4ed6\u4e09\u5251\u3002\u201d \u3000\u3000\u201c\u4e09\u5251\u592a\u591a\u4e86\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4e00\u5251\u5c31\u8be5\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u4e24\u5251\u5427\uff01\u81f3\u4eca\u4e3a\u6b62\uff0c\u6210\u90fd\u8fd8\u672a\u9047\u8fc7\u654c\u624b\uff0c\u866c\u9aef\u5ba2\u5c31\u7b97\u5929\u795e\u4e0b\u51e1\uff0c\u6210\u90fd\u76f8\u4fe1\u4e24\u5251\u4e5f\u5e94\u8be5 \u63a5\u5f97\u4e0b\u6765\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\uff0c\u5207\u4e0d\u53ef\u548c\u4ed6\u5355\u72ec\u4ea4\u624b\u3002\u201d \u3000\u3000\u201c\u662f\uff0c\u56fd\u5e08\u5429\u5490\uff0c\u6210\u90fd\u81ea\u5f53\u9075\u4ece\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8bf8\u4f4d\u53bb\u66f4\u6362\u8863\u670d\uff0c\u6d3e\u4eba\u6e05\u7406\u6e05\u7406\uff0c\u6211\u53bb\u664b\u89c1\u7687\u4e0a\u3002\u201d \u3000\u3000\u671b\u7740\u8881\u7d2b\u70df\u80cc\u5f71\u53bb\u8fdc\u2026\u2026 \u3000\u3000\u5218\u98de\u9e4f\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u771f\u6709\u90a3\u4e48\u5389\u5bb3\u5417\uff1f\u201d \u3000\u3000\u51cc\u4e91\u6447\u6447\u5934\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u592a\u76f8\u4fe1\uff0c\u771f\u672c\u9886\u786c\u529f\u592b\uff0c\u518d\u5389\u5bb3\u7684\u9ad8\u624b\uff0c\u81f3\u5c11\u6211\u4eec\u53ef\u4ee5\u63a5\u4ed6\u4e2a\u4e09\u4e24\u4e2a\u56de\u5408\uff01\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u6211\u5012\u8981\u8bd5\u4e00\u8bd5\u5341\u4e8c\u628a\u98de\u5200\u4e00\u9f50\u51fa\u624b\u7684\u5a01\u529b\uff0c\u53ea\u51ed\u6b66\u529f\u80fd\u4e0d\u80fd\u63a5\u5f97\u4e0b\u6765\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u53c2\u4e0e\u4ed6\u4eec\u7684\u8ba8\u8bba\u3002 \u3000\u3000\u8881\u7d2b\u70df\u8d70\u4e86\uff0c\u4ed6\u4e5f\u8d70\u4e86\u3002 \u3000\u3000\u8981\u4eba\u6e05\u7406\uff0c\u4e5f\u8981\u91cd\u6574\u519b\u5a01\u3002\u8fd9\u4e00\u6b21\u53c2\u4e0e\u6218\u6597\u7684\uff0c\u5c3d\u91cf\u8c03\u4ed6\u56de\u8425\u4f11\u606f\uff0c\u518d\u8c03\u53e6\u4e00\u6279\u540e\u5907\u7684 \u5bab\u536b\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u771f\u7684\u6740\u5f97\u592a\u6fc0\u70c8\u4e86\uff0c\u51e0\u4e4e\u6ca1\u6709\u4e0d\u53d7\u4f24\u7684\u5bab\u536b\u3002 \u3000\u3000\u771f\u8ba9\u4eba\u770b\u5f97\u5fc3\u60ca\u80c6\u98a4\u3002 \u3000\u3000\u9047\u4e0a\u866c\u9aef\u5ba2\u8fd9\u6837\u7684\u5f3a\u654c\uff0c\u6050\u6ca1\u6709\u518d\u6218\u7684\u52c7\u6c14\u4e86\u3002 \u3000\u3000\u53ea\u8981\u6709\u4e00\u6b21\u6e83\u6563\uff0c\u8fd9\u4e9b\u5bab\u536b\u52c7\u58eb\u5fc3\u7406\u4e0a\u5373\u4f1a\u5927\u53d7\u5f71\u54cd\uff0c\u65e5\u540e\u6050\u6015\u4e5f\u96be\u518d\u52c7\u5f80\u76f4\u524d\u3001\u608d\u4e0d \u754f\u6b7b\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5f88\u6ee1\u610f\u8bad\u7ec3\u5f97\u6210\u529f\uff0c\u4ed6\u4eec\u867d\u7136\u6ca1\u6709\u65f6\u95f4\u5b66\u5230\u5f88\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u4f46\u5374\u7ec3\u51fa\u4e86\u94a2\u94c1\u822c \u7684\u610f\u5fd7\u529b\u3002 \u3000\u3000\u90a3\u4e48\u60b2\u60e8\u7684\u6740\u622e\uff0c\u6ca1\u6709\u4e00\u4e2a\u547c\u53eb\u9976\u547d\uff0c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u9003\u547d\u9000\u8dd1\u3002 \u3000\u3000\u8fd9\u867d\u53ea\u662f\u4e00\u4e24\u5343\u4eba\u4e4b\u519b\uff0c\u4f46\u53ef\u5f53\u5f97\u6570\u4e07\u5f3a\u654c\u51b2\u6740\u3002 \u3000\u3000\u591c\u8272\u5e7d\u9759\u2014\u2014 \u3000\u3000\u957f\u5b89\u7684\u5bab\u5ef7\u4e2d\uff0c\u867d\u7136\u4ecd\u662f\u534e\u706f\u4e07\u76cf\uff0c\u7167\u5f97\u4e00\u7247\u901a\u660e\uff0c\u4f46\u5374\u5c11\u4e86\u90a3\u79cd\u5230\u5904\u83ba\u58f0\u71d5\u8bed\u7684\u5435\u6742 \u58f0\u3002 \u3000\u3000\u53d8\u5f97\u5b89\u9759\u591a\u4e86\u3002 \u3000\u3000\u4e5f\u591a\u4e86\u4e00\u80a1\u51b7\u8083\u4e4b\u6c14\u3002 \u3000\u3000\u7ecf\u5386\u8fc7\u90a3\u573a\u6740\u52ab\uff0c\u5f88\u591a\u5bab\u5973\u4eec\u770b\u5230\u4e86\u73b0\u573a\uff0c\u6ca1\u770b\u5230\u7684\u4e5f\u542c\u4e86\u4e9b\u63cf\u8ff0\u3002\u5bab\u5973\u4eec\u8bb0\u5fc6\u96be\u5fd8\uff0c \u8fde\u591c\u6076\u68a6\u3002 \u3000\u3000\u5979\u4eec\u6446\u5728\u5fc3\u4e0a\u7684\uff0c\u4e0d\u518d\u662f\u5e2e\u4e3b\u5b50\u53d6\u60a6\u4e89\u5ba0\uff0c\u800c\u662f\u5ff5\u5ff5\u96be\u5fd8\u6740\u622e\u7684\u6050\u6016\u3002\u4e00\u76f4\u62c5\u5fc3\u5982\u518d\u6709 \u4e00\u6b21\u5f3a\u654c\u88ad\u51fb\uff0c\u5979\u4eec\u662f\u5426\u8fd8\u4f1a\u6709\u8fd9\u6b21\u4e00\u822c\u7684\u5e78\u8fd0\uff1f \u3000\u3000\u5b87\u6587\u6210\u90fd\u5219\u662f\u5979\u4eec\u5fc3\u4e2d\u6700\u60f3\u89c1\u5230\u7684\u4eba\u3002 \u3000\u3000\u96c4\u6b66\u7684\u5bab\u536b\u4e5f\u6210\u4e86\u5979\u4eec\u5fc3\u76ee\u4e2d\u7684\u82f1\u96c4\u3002 \u3000\u3000\u4f3c\u4e4e\u591a\u770b\u5b87\u6587\u5c06\u519b\u4e00\u773c\uff0c\u5979\u4eec\u5fc3\u4e2d\u5c31\u5b89\u5b9a\u4e0d\u5c11\u3002\u591a\u548c\u5bab\u536b\u4eec\u63a5\u8fd1\u4e00\u4e9b\uff0c\u5c31\u591a\u4e86\u4e00\u4efd\u5b89\u5168 \u7684\u4fdd\u969c\u3002 \u3000\u3000\u5bab\u536b\u4eec\u5927\u90fd\u5728\u5bab\u5899\u5916\u9762\u5de1\u5b88\u3002 \u3000\u3000\u5bab\u5973\u4eec\u80c6\u5927\u7684\u5f00\u4e86\u5c0f\u95e8\uff0c\u6e9c\u51fa\u5bab\u5899\u548c\u4ed6\u4eec\u8c08\u5929\u8bf4\u5730\u3002\u6ca1\u6709\u522b\u4eba\u5728\u573a\u65f6\uff0c\u5c31\u9001\u4e0a\u6a31\u6843\u5c0f \u53e3\uff0c\u4eb2\u4eb2\u4ed6\u4eec\uff0c\u5145\u5206\u7684\u8868\u8fbe\u4e86\u5fc3\u4e2d\u611f\u6fc0\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u53d1\u89c9\u4e86\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u5de1\u89c6\u5bab\u5899\u5185\u5916\uff0c\u5f53\u7136\u4e5f\u53d1\u89c9\u4e86\u3002 \u3000\u3000\u4f46\u4e09\u4eba\u773c\u7741\u3001\u773c\u95ed\uff0c\u770b\u89c1\u4e86\u5374\u88c5\u4f5c\u6ca1\u770b\u89c1\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u4e2d\u660e\u767d\uff0c\u8ba9\u4ed6\u4eec\u611f\u60c5\u4ea4\u6d41\uff0c\u4e92\u76f8\u5173\u5fc3\uff0c\u624d\u80fd\u9501\u4f4f\u8fd9\u4e9b\u5bab\u536b\u4eec\u7684\u5fc3\uff0c\u62d2\u654c\u65f6\u4e5f\u624d\u80fd \u62fc\u547d\u8d74\u6b7b\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8881\u7d2b\u70df\u3001\u8881\u5b9d\u513f\u3001\u624d\u4eba\u7b49\u5927\u90fd\u77e5\u9053\u4e86\u3002 \u3000\u3000\u4f46\u65e0\u4eba\u963b\u6b62\uff0c\u65e0\u4eba\u591a\u95ee\u3002 \u3000\u3000\u7ecf\u5386\u4e00\u573a\u751f\u6b7b\u4e4b\u52ab\uff0c\u4eba\u7684\u6027\u513f\u5168\u53d8\u4e86\u3002 \u3000\u3000\u671b\u4e00\u773c\u6717\u6717\u661f\u6cb3\uff0c\u8881\u7d2b\u70df\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u5b9d\u59b9\u59b9\uff0c\u5929\u8c61\u53c8\u6709\u53d8\u5316\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u59b9\u8fd9\u65b9\u9762\u77e5\u8bc6\u6d45\u8584\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u5b9e\u5728\u77a7\u4e0d\u51fa\u4e0a\u5929\u5782\u8c61\u610f\u82e5\u4f55\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u5bf9\u6211\u53d8\u5f97\u51b7\u6de1\u4e86\uff0c\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u53f9\u9053\uff1a \u3000\u3000\u201c\u7d2b\u70df\u59d0\u60f3\u542c\u771f\u8bdd\u5417\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u5982\u679c\u81ea\u4fe1\u80fd\u9a97\u5f97\u8fc7\u6211\uff0c\u4e5f\u80fd\u9a97\u5f97\u8ba9\u6211\u5f00\u5fc3\uff0c\u4f55\u59a8\u9a97\u9a97\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u9a97\u4e0d\u8fc7\u4e86\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u4f55\u51b5\u5c0f\u59b9\u6839\u672c\u4e0d\u60f3\u9a97\u4f60\u3002\u8fd1\u6765\u611f\u89c9\u5230\u8eab\u4f53\u6709\u4e86\u53d8\u5316\u2026\u2026\u201d \u3000\u3000\u201c\u6000\u5b55\u4e86\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5e72\u561b\u4e0d\u544a\u8bc9\u7687\u4e0a\uff1f\u660e\u5929\uff0c\u6211\u5e2e\u4f60\u53bb\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u6000\u5b55\uff0c\u4e5f\u975e\u5173\u60c5\u4e8b\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u6211\u89c9\u5f97\u6d4a\u6c14\u4e0b\u6c89\uff0c\u8eab\u5b50\u8d8a\u6765\u8d8a\u91cd\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a \u3000\u3000\u201c\u9053\u57fa\u6d88\u8680\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u59d0\u59d0\u4ed9\u7f18\u6df1\u539a\uff0c\u9053\u57fa\u575a\u56fa\uff0c\u5c0f\u59b9\u65e0\u6cd5\u6bd4\u5f97\uff0c\u5df2\u89c9\u5f97\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u53ef\u6709\u8865\u6551\u4e4b\u9053\uff1f\u6211\u5982\u80fd\u529b\u6240\u53ca\uff0c\u4e00\u5b9a\u5168\u529b\u52a9\u4f60\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u6447\u6447\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u6050\u6015\u662f\u4e0d\u884c\u4e86\uff0c\u6211\u4eec\u9006\u5929\u800c\u884c\uff0c\u4e56\u79bb\u9053\u6212\uff0c\u4ee5\u672f\u6cd5\u6740\u6b7b\u4e0d\u61c2\u672f\u6cd5\u7684\u4eba\uff0c\u8fd9\u540e\u679c\u65e9\u5c31\u8be5 \u60f3\u5230\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4eca\u4e16\u503a\u4ece\u4eca\u4e16\u8fd8\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u4fdd\u4f4f\u6211\u4e00\u70b9\u5143\u7075\u4e0d\u6627\uff0c\u5982\u80fd\u8f6c\u52ab\u518d\u5165\u9053\u95e8\u3002\u5982\u679c\u8f6c\u52ab\u4e0d\u6210\uff0c\u6211\u8bd5\u5c06\u4ee5\u9634\u7075\u4e4b\u8eab\u518d\u56de\u5e08 \u95e8\u3002\u201d \u3000\u3000\u201c\u4e0d\u6210\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u8f6c\u52ab\u4e0d\u6210\uff0c\u5c31\u5316\u6210\u70df\u4e91\u5165\u957f\u7a7a\uff0c\u5fd8\u56fe\u4ee5\u9634\u7075\u4e4b\u8eab\u518d\u4fee\u9053\u6cd5\uff0c\u90a3\u5c31\u6210\u5b66\u9053\u4eba\u8ffd\u6740\u7684\u76ee\u6807 \u4e86\u3002\u975e\u88ab\u8feb\u5165\u7edd\u5730\uff0c\u5e38\u5e74\u53d7\u56f0\u5929\u96f7\u5730\u706b\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u59d0\u59d0\u5982\u80af\u6210\u5168\uff0c\u73b0\u5728\u5c31\u5288\u5b9d\u513f\u4e00\u5251\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u6447\u6447\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u5288\u4e0d\u4e0b\u624b\uff0c\u4f60\u8fc7\u53bb\u4e5f\u5927\u8d2a\u5a6a\u4e86\uff0c\u5e38\u5e38\u8d70\u5931\u771f\u9634\uff0c\u867d\u7136\u8ba8\u53d6\u4e86\u7687\u4e0a\u7684\u5ba0\u7231\uff0c\u4f46\u4e5f\u5bb3\u82e6 \u4e86\u81ea\u5df1\u3002 \u3000\u3000\u5b9d\u513f\uff0c\u6211\u52a9\u4e00\u53e3\u771f\u6c14\uff0c\u8bd5\u8bd5\u770b\u80fd\u4e0d\u80fd\u4fdd\u4f4f\u9053\u57fa\u4e0d\u518d\u6d88\u8680\u3002\u53ea\u8981\u80fd\u7a33\u5b9a\u5f97\u4f4f\uff0c\u8fd8\u6709\u673a\u4f1a\u3002 \u6211\u4e5f\u8981\u4ece\u6b64\u6d01\u8eab\u81ea\u4fee\uff0c\u4e0d\u8bb8\u7687\u4e0a\u518d\u78b0\u6211\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u6211\u539f\u60f3\u80fd\u548c\u674e\u4e16\u6c11\u4f5c\u4e00\u5bf9\u6069\u7231\u592b\u59bb\uff0c\u53ea\u4fee\u4e00\u4e9b\u535c\u5366\u4e4b\u672f\uff0c\u52a9\u4ed6\u6cbb\u7406\u51fa\u4e00\u4e2a\u592a\u5e73\u76db\u4e16\u3002 \u4ed9\u9053\u4e4b\u5b66\u4e0d\u7ec3\u4e5f\u7f62\uff0c\u5374\u4e0d\u6599\u4e8b\u4e0e\u613f\u8fdd\uff0c\u4fbf\u5b9c\u4e86\u7687\u4e0a\u2026\u2026\u201d \u3000\u3000\u8bf4\u66f9\u64cd\uff0c\u66f9\u64cd\u5c31\u5230\u2014\u2014 \u3000\u3000\u968b\u7080\u5e1d\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u537f\u5bb6\u5bf9\u5ea7\u8c08\u5fc3\uff0c\u8fde\u76cf\u706f\u4e5f\u4e0d\u70b9\u8d77\uff0c\u6211\u53eb\u5bab\u5973\u6765\u638c\u8d77\u5bab\u706f\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4e00\u6709\u706f\u706b\uff0c\u5c31\u7834\u574f\u8fd9\u4efd\u5e7d\u9759\u4e86\uff0c\u7687\u4e0a\u5306\u5306\u800c\u6765\uff0c\u4e0d\u77e5\u6709\u4f55\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u957f\u5b89\u4f3c\u5df2\u975e\u4e45\u5c45\u4e4b\u5730\uff0c\u6715\u60f3\u4e1c\u6e38\u2026\u2026\u201d \u3000\u3000\u201c\u53bb\u4e1c\u90e8\u6d1b\u9633\u6563\u6563\u5fc3\u4e5f\u597d\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4f46\u8fd9\u957f\u5b89\u7684\u4e8b\u52a1\u5462\uff1f\u603b\u4e0d\u80fd\u4e00\u8d70\u4e86\u4e4b\u3002\u201d \u3000\u3000\u201c\u7531\u537f\u5bb6\u4ee3\u7406\uff0c\u5b9d\u513f\u968f\u6715\u4e1c\u6e38\u2026\u2026\u201d \u3000\u3000\u8881\u5b9d\u513f\u5403\u4e86\u4e00\u60ca\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u8981\u53bb\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u6715\u5982\u5931\u53bb\u537f\u5bb6\uff0c\u6d3b\u5f97\u5c31\u5168\u65e0\u5473\u9053\u4e86\u3002\u201d \u3000\u3000\u987f\u4e86\u987f\uff0c\u968b\u7080\u5e1d\u53c8\u82e6\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e0d\u8bba\u4f60\u5fc3\u4e2d\u60f3\u7684\u4ec0\u4e48\uff1f\u53ea\u8981\u4f60\u5728\u6211\u8eab\u8fb9\u5c31\u597d\u4e86\uff0c\u5982\u679c\u771f\u7684\u5230\u4e86\u65e0\u5948\u5883\u754c\uff0c\u6715\u4f1a\u6210\u5168\u537f \u5bb6\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u8bf4\u5f97\u591f\u660e\u767d\u4e86\u3002 \u3000\u3000\u8881\u5b9d\u513f\u5fc3\u4e2d\u6709\u7740\u5200\u523a\u4e4b\u75db\uff0c\u8fd9\u4e2a\u4eba\u662f\u771f\u7684\u7231\u4ed6\uff0c\u53d7\u5230\u53ef\u4ee5\u629b\u5f03\u6c5f\u5c71\u4e0d\u987e\uff0c\u4e5f\u53ef\u4ee5\u4e22\u5f03\u6027 \u547d\u4e0d\u7ba1\u3002 \u3000\u3000\u770b\u6765\u4eba\u95f4\u7684\u7edd\u8272\u6bd4\u5929\u4e0a\u7684\u4ed9\u5973\u53ef\u7231\u591a\u4e86\uff01 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5b9d\u59b9\u59b9\uff0c\u7687\u4e0a\u4f3c\u6709\u70b9\u5f3a\u4f60\u6240\u96be\uff0c\u4f46\u4ed6\u7231\u4f60\u4e4b\u6df1\u5374\u4e5f\u662f\u65e0\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u7f13\u7f13\u7ad9\u8d77\uff0c\u5bf9\u7740\u968b\u7080\u5e1d\u76c8\u76c8\u8dea\u4e0b\uff0c\u9053\uff1a \u3000\u3000\u201c\u7687\u4e0a\u539a\u7231\uff0c\u5b9d\u513f\u552f\u6709\u4e00\u6b7b\u76f8\u62a5\u5c14\u3002\u201d \u3000\u3000\u5979\u53d7\u7684\u4eba\u95f4\u6559\u80b2\uff0c\u5929\u3001\u5730\u3001\u541b\u3001\u4eb2\u3001\u5e08\uff0c\u6df1\u503c\u5fc3\u95f4\u3002 \u3000\u3000\u4e0d\u50cf\u8881\u7d2b\u70df\u5e7c\u5c0f\u751f\u957f\u6df1\u5c71\u95f4\uff0c\u53ea\u8bc6\u4e00\u4e2a\u201c\u7406\u201d\u5b87\uff0c\u8bdd\u4e0d\u6295\u673a\uff0c\u7acb\u523b\u7ffb\u8138\uff0c\u7ba1\u4f60\u662f\u7687\u4e0a\u8fd8 \u662f\u5c06\u519b\u3002 \u3000\u3000\u968b\u7080\u5e1d\u4e00\u628a\u6276\u8d77\u8881\u5b9d\u513f\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u6715\u4e0d\u8981\u4f60\u6b7b\uff0c\u8981\u6b7b\u5417\uff1f\u6715\u4e5f\u8be5\u5728\u4f60\u524d\u9762\u3002\u6715\u8981\u4f60\u5feb\u4e50\uff0c\u6715\u5c06\u503e\u6240\u6709\u5c3d\u4ed8\u537f\u5bb6\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u611f\u52a8\u5f97\u73e0\u6cea\u53cc\u6d41\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5374\u62c2\u7740\u5b9d\u513f\u79c0\u53d1\uff0c\u5927\u7b11\u9053\uff1a \u3000\u3000\u201c\u5e1d\u738b\u9738\u4e1a\u6210\u4f55\u7528\uff1f\u79e6\u7687\u3001\u6c49\u6b66\u65e9\u6210\u4e86\u9675\u5bb6\uff0c\u4e43\u5f97\u537f\u5bb6\u4e09\u5206\u60c5\uff0c\u591f\u6715\u53d7\u7528\uff0c\u8db3\u6170\u751f\u5e73 \u554a\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u7275\u7740\u8881\u5b9d\u513f\u8d70\u4e86\u3002 \u3000\u3000\u4e00\u6761\u7ea4\u5de7\u7684\u4eba\u5f71\u75be\u5954\u800c\u81f3\uff0c\u662f\u5de7\u513f\u3002 \u3000\u3000\u5979\u8eac\u8eab\u9053\uff1a \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\u5019\u547d\u591a\u65f6\uff0c\u59d1\u5a18\u8981\u4e0d\u8981\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u3000\u3000\u201c\u53bb\u53eb\u4ed6\u6765\uff01\u201d \u3000\u3000\u5de7\u513f\u5e26\u6765\u5b87\u6587\u6210\u90fd\uff0c\u81ea\u5df1\u5374\u8f6c\u8eab\u8d70\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90e8\u4ecd\u4fdd\u6301\u541b\u81e3\u7684\u8eab\u4efd\uff0c\u5c48\u819d\u9053\uff1a \u3000\u3000\u201c\u6210\u90fd\u53e9\u89c1\u5a18\u5a18\u56fd\u5e08\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u201c\u5657\u201d\u5730\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u7a76\u7adf\u60f3\u53eb\u4ec0\u4e48\uff1f\u662f\u5a18\u5a18\u8fd8\u662f\u56fd\u5e08\uff1f\u201d \u3000\u3000\u201c\u6210\u90fd\u6709\u4e8b\u664b\u89c1\uff0c\u5e94\u8be5\u79f0\u56fd\u5e08\u624d\u5bf9\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6e29\u67d4\u5730\u9053\uff1a \u3000\u3000\u201c\u8d77\u6765\u5427\uff01\u6709\u4e8b\u5c31\u76f4\u8bf4\u5427\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u5c5e\u4e0b\u63a2\u5f97\u6d88\u606f\uff0c\u866c\u9aef\u5ba2\u5df2\u5165\u957f\u5b89\uff0c\u4f46\u5374\u7a81\u7136\u6d88\u5931\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u957f\u5b89\u57ce\u4e2d\u5ba2\u6808\u6709\u9650\uff0c\u4ed6\u4eec\u4e0d\u662f\u4f4f\u5728\u5ba2\u6808\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u957f\u5b89\u57ce\u6bcf\u4e00\u5bb6\u5ba2\u6808\u90fd\u67e5\u8fc7\u4e86\uff0c\u627e\u4e0d\u5230\u866c\u9aef\u5ba2\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u201c\u5bfa\u9662\u9053\u89c2\u5462\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5e9f\u5f03\u7684\u5b85\u9662\u4e5f\u4e0d\u80fd\u653e\u8fc7\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u90fd\u67e5\u8fc7\u4e86\uff0c\u5f88\u53ef\u80fd\u9690\u533f\u4e8e\u6c11\u5b85\u4e4b\u4e2d\uff0c\u866c\u9aef\u5ba2\u5728\u957f\u5b89\u57ce\u4e2d\u672c\u5c31\u9690\u4f0f\u4e86\u4e0d\u5c11\u4eba\u624b\uff0c\u9664\u975e\u5168 \u957f\u5b89\u5bb6\u5bb6\u666e\u67e5\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4ed6\u8981\u5165\u5bab\uff0c\u6211\u4eec\u5c31\u5728\u5bab\u4e2d\u7b49\u4ed6\u5427\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u957f\u5b89\u57ce\u4e2d\uff0c\u8fd8\u53d1\u73b0\u4e86\u53e6\u4e24\u4e2a\u4eba\u7684\u884c\u8e2a\uff0c\u4ed6\u4eec\u4f4f\u5165\u5ba2\u6808\uff0c\u884c\u8e2a\u5728\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5f88\u91cd\u8981\u5417\uff1f\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u662f\u91cd\u8981\u7684\u4eba\u5c31\u4e0d\u7528\u8bf4\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u674e\u9756\u548c\u5f20\u51fa\u5c18\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u5446\uff0c\u9053\uff1a \u3000\u3000\u201c\u674e\u6df3\u98ce\u548c\u8881\u5929\u7f61\u5462\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u53d1\u73b0\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u4e5f\u672a\u53d1\u73b0\u592a\u539f\u6765\u7684\u4e00\u5175\u4e00\u5c06\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53c8\u95ee\u9053\uff1a \u3000\u3000\u201c\u674e\u9756\u548c\u5f20\u51fa\u5c18\u4f4f\u5728\u4e00\u4e2a\u5ba2\u6808\u4e2d\u5417\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u800c\u4e14\u662f\u4e00\u95f4\u4e0a\u623f\u3002\u56fd\u5e08\uff0c\u4ed6\u4eec\u672c\u6765\u5c31\u662f\u4e00\u5bf9\u592b\u5987\u554a\uff1f\u201d \u3000\u3000\u8881\u7d2b\u59fb\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u8d39\u5c3d\u4e86\u5fc3\u673a\uff0c\u8fd8\u662f\u6ca1\u6709\u62a2\u8d70\u5f20\u51fa\u5c18\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u7687\u4e0a\u5374\u62a2\u8d70\u4e86\u674e\u4e16\u6c11\u7684\u60c5\u4fa3\u8881\u5b9d\u513f\uff0c\u4f46\u4e5f\u53ea\u5360\u6709\u4e86\u8881\u5b9d\u513f\u7684\u8eab\u4f53\uff0c\u5374\u5f97\u4e0d\u5230\u5979\u7684 \u5fc3\uff0c\u7687\u4e0a\u4ed8\u7684\u4ee3\u4ef7\u5c31\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u4ecd\u662f\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u7b54\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4f20\u4ee4\u5bab\u536b\u4eec\uff0c\u89c1\u5230\u866c\u9aef\u5ba2\u4e0d\u7528\u62e6\u963b\uff0c\u53ea\u8981\u4f20\u8b66\u5373\u53ef\u3002\u4ed6\u4eec\u4e0d\u4f1a\u5927\u961f\u4eba\u9a6c\u6765\uff0c\u4f60\u5e26\u7740\u8427 \u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u548c\u5218\u98de\u9e4f\u4f4f\u8fdb\u5185\u5bab\u5427\uff01\u6211\u53bb\u594f\u660e\u7687\u4e0a\uff0c\u5bab\u5973\u4eec\u4f1a\u4e3a\u4e94\u4f4d\u5b89\u6392\u4f4f\u5904\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5408\u9002\u5417\uff1f\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u5408\u9002\u7684\uff1f\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u662f\u4e3a\u4e86\u65b9\u4fbf\u4fdd\u62a4\u7687\u4e0a\u554a\uff01\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6ca1\u8bdd\u8bf4\u4e86\u3002 \u3000\u3000\u6709\u4ec0\u4e48\u4e8b\u80fd\u6bd4\u4fdd\u62a4\u7687\u4e0a\u66f4\u91cd\u8981\u5462\uff1f \u3000\u3000\u4e00\u5ea7\u5e7d\u9759\u7684\u56db\u5408\u9662\u2014\u2014 \u3000\u3000\u6709\u8fd1\u4e8c\u5341\u95f4\u7684\u623f\u95f4\uff0c\u53a8\u6d74\u9f50\u5168\uff0c\u8fd8\u6709\u4e94\u4e2a\u5e03\u7f6e\u534e\u4e3d\u7684\u623f\u95f4\uff0c\u5206\u4f4f\u4e86\u5b87\u6587\u6210\u90fd\u7b49\u4e94\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u5ea7\u70b9\u4e86\u516d\u76cf\u5782\u82cf\u5bab\u706f\u7684\u5927\u5ba2\u5385\u4e2d\uff0c\u5206\u7ad9\u5341\u4e2a\u5bab\u5973\uff0c\u5341\u4e2a\u7f8e\u4e3d\u7684\u5bab\u5973\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff1f\u6deb\u4e71\u540e\u5bab\uff0c\u53ef\u662f\u8981\u6ee1\u95e8\u5904\u65a9\u7684\u5927\u7f6a\u554a\uff01\u8fd9\u4e9b\u4e2b\u5934\u53c8\u662f\u4ec0\u4e48\u7528\u5fc3 \u5462\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5341\u4e2a\u7f8e\u5973\u5df2\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6367\u8336\u7684\u4e0a\u8336\uff0c\u770b\u5750\u7684\u770b\u5750\uff0c\u4e00\u9635\u8102\u7c89\u9999\u6c14\u6251\u9f3b\u6b32\u9189\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u8bf8\u4f4d\u59d1\u5a18\uff0c\u4e0d\u6562\u5f53\u554a\uff01\u8fd9\u4e9b\u4e8b\u6211\u4eec\u81ea\u5df1\u4f1a\u4f5c\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5e74\u9f84\u8f83\u5927\u7684\u5bab\u5973\u9053\uff1a \u3000\u3000\u201c\u5b87\u6587\u5c06\u519b\uff0c\u6211\u4eec\u662f\u51fa\u81ea\u5185\u5fc3\u6765\u670d\u4f8d\u8bf8\u4f4d\u7684\uff0c\u6211\u4eec\u770b\u5230\u4e86\u5bab\u536b\u4eec\u4e3a\u4e86\u4fdd\u62a4\u6211\u4eec\u524d\u4ec6\u540e\u7ee7 \u7684\u820d\u547d\u62fc\u6218\uff0c\u6211\u4eec\u60f3\u5e2e\u5fd9\uff0c\u53ef\u662f\u65e0\u529b\u5e2e\u554a\uff01 \u3000\u3000\u4e00\u4e2a\u5f31\u5973\u5b50\u65e0\u529b\u6267\u5200\u63e1\u5251\uff0c\u5e73\u65e5\u91cc\u5bab\u5899\u963b\u9694\uff0c\u54ab\u5c3a\u5929\u6daf\u3002\u4e94\u4f4d\u5c06\u519b\u4eca\u756a\u5949\u53ec\u5165\u5bab\uff0c\u660e\u663e \u7684\u662f\u4fdd\u62a4\u6211\u4eec\u800c\u6765\uff0c\u6211\u4eec\u80fd\u4e0d\u80fd\u6d3b\u4e0b\u53bb\uff1f\u5173\u952e\u5168\u5728\u4e94\u4f4d\u5c06\u519b\u7684\u8eab\u4e0a\uff0c\u6211\u4eec\u5fc3\u4e2d\u611f\u6fc0\uff0c\u4f46\u4e5f\u53ea \u80fd\u5c3d\u529b\u7167\u987e\u4e94\u4f4d\u5c06\u519b\u751f\u6d3b\u8d77\u5c45\uff0c\u804a\u8868\u5bf8\u5fc3\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u4fdd\u536b\u7687\u5bab\u7684\u5b89\u5168\u662f\u6211\u4eec\u7684\u804c\u8d23\uff0c\u8bf8\u4f4d\u59d1\u5a18\u7528\u4e0d\u7740\u8fd9\u6837\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u7b2c\u4e00\u6279\u4e2d\u7b7e\u7684\u4eba\u3002\u201d\u5e74\u9f84\u8f83\u5927\u7684\u5bab\u5973\u7b54\u9053\uff1a \u3000\u3000\u201c\u5927\u5bb6\u90fd\u8981\u6765\u4f8d\u5019\u5c06\u519b\uff0c\u4f46\u4f60\u4eec\u53ea\u6709\u4e94\u4e2a\u4eba\uff0c\u4e89\u7740\u60f3\u6765\u7684\u5bab\u5973\u6709\u4e00\u5343\u591a\u4eba\uff0c\u6240\u4ee5\u6211\u4eec\u53ea \u6709\u62bd\u7b7e\u51b3\u5b9a\uff0c\u6bcf\u6b21\u6765\u5341\u4e2a\u4eba\uff0c\u4f8d\u5019\u4e94\u4f4d\u5c06\u519b\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u662f\u8fd9\u6837\u554a\uff01\u591a\u8c22\u8bf8\u4f4d\u59d1\u5a18\u5173\u6000\u3002\u201d \u3000\u3000\u7530\u5f53\u3001\u51cc\u4e91\u3001\u8427\u96e8\u3001\u5218\u98de\u9e4f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5374\u542c\u5f97\u6026\u7136\u5fc3\u52a8\u3002 \u3000\u3000\u4e00\u5343\u591a\u4e2a\u5929\u4e0b\u9009\u51fa\u7684\u7f8e\u5973\uff0c\u5305\u62ec\u5317\u5730\u80ed\u8102\u3001\u5357\u56fd\u4f73\u4e3d\uff0c\u5206\u6279\u6765\u4f8d\u5019\u4e00\u4e2a\u7537\u4eba\uff0c\u8fd9\u4e2a\u7537\u4eba \u6b7b\u4e5f\u7791\u76ee\u4e86\u3002 \u3000\u3000\u53ea\u662f\u4e0d\u77e5\u9053\uff0c\u5979\u4eec\u6240\u8c13\u7684\u4f8d\u5019\u662f\u4ec0\u4e48\u6837\u4e00\u4e2a\u9650\u5ea6\uff0c\u662f\u4e0d\u662f\u4e5f\u53ef\u4ee5\u4e0a\u5e8a\u5462\uff1f \u3000\u3000\u60f3\u5230\u5f00\u5fc3\u5904\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff01 \u3000\u3000\u8fd9\u7b11\u5bb9\u5c31\u6709\u70b9\u90aa\u6c14\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u770b\u5230\u4e86\uff0c\u5374\u88c5\u4f5c\u672a\u89c1\u3002 \u3000\u3000\u8fd9\u4e9b\u82f1\u96c4\u597d\u6c49\u4e5f\u662f\u4eba\uff0c\u4ed6\u4eec\u867d\u4e0d\u4f1a\u89c1\u8272\u52a8\u5fc3\u3001\u4f5c\u5978\u72af\u79d1\uff0c\u4f46\u6709\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u5973\u81ea\u4e2a\u9001\u4e0a \u95e8\u6765\uff0c\u8981\u4ed6\u4eec\u4e2a\u4e2a\u5b66\u4f5c\u67f3\u4e0b\u60e0\uff0c\u5c31\u6709\u4e9b\u5f3a\u4eba\u6240\u96be\u4e86\u3002 \u3000\u3000\u90a3\u4e9b\u5b98\u5973\u4eec\u4e5f\u770b\u5230\u4e86\u3002 \u3000\u3000\u5979\u4eec\u867d\u5c45\u6df1\u5bab\uff0e\u4f46\u5374\u770b\u5230\u4e86\u4e3b\u5b50\u3001\u592b\u4eba\u4eec\u4e89\u5ba0\u7684\u624b\u6bb5\uff0c\u6492\u5a07\u65bd\u5a9a\uff0c\u8ba8\u53d6\u7687\u4e0a\u6b22\u5fc3\uff1b\u8fd9\u65b9 \u9762\u5979\u4eec\u975e\u5e38\u654f\u611f\u3002 \u3000\u3000\u56f4\u7740\u9886\u5934\u7684\u5bab\u5973\u4f4e\u58f0\u4ea4\u8c08\u4e86\u4e00\u9635\u3002 \u3000\u3000\u9886\u5934\u7684\u5bab\u5973\u70b9\u70b9\u5934\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u8bf4\u5c31\u8bf4\u5427\uff01\u6211\u4eec\u662f\u611f\u6069\u800c\u6765\uff0c\u613f\u610f\u5949\u4e0a\u6211\u4eec\u6240\u6709\uff0c\u53ea\u662f\u6211\u4eec\u6240\u6709\u7684\u592a\u5c11\u4e86\uff0c\u6700\u91cd \u8981\u7684\u662f\u4e00\u4e2a\u6e05\u767d\u7684\u8eab\u4f53\u3002 \u3000\u3000\u4e94\u4f4d\u5c06\u519b\u5982\u4e0d\u5acc\u5f03\uff0c\u6211\u4eec\u613f\u8350\u6795\u5e2d\uff0c\u4e0d\u4f46\u53ea\u6709\u6211\u4eec\u5341\u4e2a\uff0c\u65e5\u540e\u6765\u4f8d\u5019\u5404\u4f4d\u59d0\u59b9\u4eec\u4e5f\u90fd\u6709 \u732e\u8eab\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u8bf4\u7684\u5982\u6b64\u5766\u767d\uff0c\u8fd8\u771f\u628a\u5b87\u6587\u6210\u90fd\u7b49\u5413\u4f4f\u4e86\uff01 \u3000\u3000\u201c\u6700\u96be\u6d88\u53d7\u7f8e\u4eba\u6069\uff01\u201d\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\uff0c\u53ea\u6015\u6211\u7b49\u6d88\u53d7\u4e0d\u8d77\u5440\uff01\u201d \u3000\u3000\u9886\u5934\u7684\u5bab\u5973\u9053\uff1a \u3000\u3000\u201c\u5f53\u7136\uff0c\u8bf8\u4f4d\u5982\u5acc\u5f03\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u607c\u7f9e\uff0c\u4e00\u5343\u591a\u4e2a\u7f8e\u5973\uff0c\u8bf8\u4f4d\u6162\u6162\u7684\u770b\u5427\uff01\u6211\u76f8\u4fe1 \u603b\u80fd\u627e\u5230\u4e00\u4e2a\u5408\u610f\u7684\u4eba\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e0d\u80fd\u5f00\u73a9\u7b11\u554a\uff01\u59d1\u5a18\uff0c\u79fd\u4e71\u5bab\u5ef7\u53ef\u662f\u6b7b\u7f6a\u4e00\u6761\uff0c\u6211\u4eec\u4f1a\u88ab\u5904\u6b7b\uff0c\u8bf8\u4f4d\u59d1\u5a18\u4e5f\u4f1a\u88ab\u5904 \u6b7b\u554a\uff01\u201d \u3000\u3000\u5341\u4f4d\u5bab\u5973\u63a9\u5634\u7b11\u4e86\u3002 \u3000\u3000\u6709\u70b9\u7f9e\u6029\uff0c\u5374\u6709\u66f4\u591a\u7684\u5632\u7b11\u548c\u8bbd\u523a\u3002 \u3000\u3000\u9886\u5934\u7684\u5bab\u5973\u9053\uff1a \u3000\u3000\u201c\u7687\u4e0a\u77e5\u9053\u4e86\uff0c\u4f1a\u4e0d\u4f1a\u4e0b\u4ee4\u8bdb\u6740\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u4e0d\u8fc7\uff0c\u7687\u4e0a\u600e\u4e48\u4f1a\u77e5\u9053\u5462\uff1f\u6ca1\u6709\u4eba\u6562\u594f\u77e5 \u7687\u4e0a\u554a\uff01\u4f55\u51b5\u6211\u4eec\u7684\u5fc3\u610f\u5df2\u5f97\u5230\u4e3b\u5b50\u7684\u9ed8\u8bb8\uff0c\u5979\u4eec\u6bd4\u6211\u4eec\u66f4\u62c5\u5fc3\u81ea\u5df1\u7684\u5b89\u5371\u5440\uff01\u5979\u4eec\u4e0d\u53cd\u5bf9 \u6211\u4eec\u6765\u8fd9\u513f\u4f8d\u5019\u5c06\u519b\uff0c\u4e5f\u5c31\u662f\u540c\u610f\u4e86\u6211\u4eec\u7684\u4f5c\u4e3a\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u9009\u96c6\u5929\u4e0b\u7684\u7f8e\u5973\uff0c\u773c\u770b\u7687\u4e0a\u7684\u98ce\u6d41\u751f\u6d3b\uff0c\u65e9\u5df2\u5fc3\u5411\u5f80\u4e4b\u3002\u53ef\u662f\u5bab\u5ef7\u4e2d\u5f88\u96be\u770b\u5230\u4e00\u4e2a \u7537\u4eba\uff0c\u867d\u7136\u4e5f\u6709\u592a\u76d1\u5f80\u6765\uff0c\u4f46\u4ed6\u4eec\u5df2\u7ecf\u4e0d\u80fd\u7b97\u4e3a\u7537\u4eba\u4e86\u3002 \u3000\u3000\u5bab\u5973\u7684\u80c6\u5927\u3001\u5766\u767d\uff0c\u53cd\u628a\u4e94\u4f4d\u8c6a\u52c7\u8fc7\u4eba\u3001\u89c6\u6b7b\u5982\u5f52\u7684\u5c06\u519b\u5413\u4f4f\u4e86\uff0c\u4e94\u4eba\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u7adf \u7136\u65e0\u4eba\u63a5\u53e3\u3002 \u3000\u3000\u201c\u6211\u4eec\u53bb\u62ff\u51c0\u9762\u5e03\uff0c\u4e94\u4f4d\u64e6\u628a\u8138\uff0c\u4e5f\u8be5\u8fdb\u7528\u9152\u80b4\u4e86\u3002\u201d \u3000\u3000\u5341\u4f4d\u5bab\u5973\u5fae\u5fae\u6b20\u8eab\u4f5c\u793c\uff0c\u4e00\u9f50\u9000\u51fa\u5927\u5385\u3002 \u3000\u3000\u201c\u4e56\u4e56\uff01\u201d\u5218\u98de\u9e4f\u9053\uff1a \u3000\u3000\u201c\u5bab\u4e2d\u751f\u6d3b\u662f\u8fd9\u4e48\u4e00\u526f\u666f\u8c61\uff1f\u8eab\u4e3a\u7687\u738b\uff0c\u4e0d\u604b\u5973\u8272\u4e5f\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u5929\u4e0b\u7f8e\u5973\u90fd\u96c6\u4e2d\u5728\u4e09\u91cc\u65b9\u5706\u7684\u5bab\u5ef7\u4e2d\uff0c\u60f3\u5c3d\u529e\u6cd5\u5f15\u8bf1\u4ed6\uff0c\u8c01\u771f\u80fd\u5750\u6000\u4e0d\u4e71\u5462\uff1f\u6570\u5c3d\u53e4\u4eca \u4eba\u7269\uff0c\u4e5f\u53ea\u6709\u4e00\u4e2a\u67f3\u4e0b\u60e0\u5440\u2026\u2026\u201d \u3000\u3000\u8427\u96e8\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u5fc3\u4e2d\u4e00\u76f4\u5b58\u7591\uff1f\u6625\u79cb\u9c81\u4eba\u5c55\u79bd\u83b7\uff0c\u5c45\u67f3\u4e0b\uff0c\u6b7b\u540e\u6ea2\u4e3a\u60e0\uff0c\u540e\u4eba\u591a\u8bef\u4e3a\u67f3\u4e0b \u60e0\u4e3a\u5176\u59d3\u540d\uff0c\u548c\u5750\u6000\u4e0d\u4e71\u4e8b\u5e76\u4e3a\u6d41\u4f20\u3002\u90a3\u4e2a\u5973\u4eba\u4e0d\u662f\u5927\u4e11\uff0c\u5c31\u662f\u88ab\u8feb\uff0c\u5c55\u79bd\u5168\u5176\u8d1e\uff0c\u6216\u6709\u53ef \u80fd\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e0d\u53ef\u6c61\u8511\u5148\u8d24\uff0c\u5b5f\u5b50\u79f0\u4ed6\u4e3a\u2018\u5723\u4e4b\u548c\u2019\uff0c\u5c82\u53ef\u7b49\u95f2\u89c6\u4e4b\uff0c\u6211\u4eec\u4e0d\u80fd\u5750\u6000\u4e0d\u4e71\uff0c\u90a3\u662f\u81ea \u5df1\u2026\u2026\u201d \u3000\u3000\u5341\u4e2a\u5bab\u5973\u9c7c\u8d2f\u884c\u5165\u5385\u5185\uff0c\u5b87\u6587\u6210\u90fd\u53ea\u597d\u4f4f\u53e3\u4e0d\u8a00\u4e86\u3002 \u3000\u3000\u4f46\u4ecd\u7136\u88ab\u5bab\u5973\u542c\u5230\u4e86\u540e\u9762\u4e00\u53e5\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u8f7b\u542f\u6a31\u5507\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e94\u4f4d\u5c06\u519b\u90fd\u53ef\u6bd4\u67f3\u4e0b\u60e0\u5440\uff01\u6211\u4eec\u5341\u4e2a\u867d\u975e\u7edd\u4e16\u7f8e\u5973\uff0c\u4f46\u59ff\u8272\u4e5f\u76f8\u5f53\u53ef\u4eba\uff0c\u800c\u4e14\u6b63\u503c\u9526 \u7ee3\u5e74\u534e\uff0c\u4e94\u4f4d\u80fd\u4e0d\u5c51\u4e00\u987e\uff0c\u5b9e\u5df2\u5b9a\u529b\u8fc7\u4eba\u3002\u4f46\u4e94\u4f4d\u5982\u9047\u4e0a\u4e86\u8881\u56fd\u5e08\u548c\u5b9d\u8d35\u5983\u90a3\u7b49\u5929\u4eba\u7edd\u8272\uff0c \u4e5f\u80fd\u6709\u6b64\u5b9a\u529b\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\uff01\u201d\u7b2c\u4e8c\u4e2a\u5bab\u5973\u63a5\u9053\uff1a \u3000\u3000\u201c\u67f3\u4e0b\u60e0\u8fd8\u9b42\u91cd\u751f\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u3001\u8427\u96e8\u7a81\u7136\u611f\u89c9\u5230\u8138\u4e0a\u7f9e\u70ed\uff0c\u4e0d\u6562\u518d\u63a5\u53e3\u591a\u8a00\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u6709\u9b3c\u5440\uff01 \u3000\u3000\u201c\u6211\u4eec\u4e0d\u662f\u67f3\u4e0b\u60e0\uff01\u201d\u7530\u5f53\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e5f\u4e0d\u80fd\u5750\u6000\u4e0d\u4e71\uff0c\u4f46\u4e5f\u4e0d\u80fd\u5e72\u4e71\u6cd5\u7eaa\u3002\u8bf8\u4f4d\u59d1\u5a18\u7684\u610f\u601d\u6211\u4eec\u90fd\u660e\u767d\u4e86\uff0c\u5bb9\u6211\u4eec\u60f3 \u4e00\u60f3\u518d\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u5bab\u5973\u4eec\u5949\u4e0a\u9152\u996d\uff0c\u4e5f\u66ff\u4e94\u4eba\u659f\u9152\u628a\u76cf\u3002 \u3000\u3000\u4e00\u76f4\u4f8d\u5019\u5230\u4e94\u4eba\u56de\u623f\u5b89\u6b47\uff0c\u624d\u6084\u7136\u9000\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7761\u4e0d\u7740\uff0c\u4f46\u5fc3\u4e2d\u60f3\u7684\u4e0d\u662f\u7537\u5973\u95f4\u4e8b\uff0c\u800c\u662f\u866c\u9aef\u5ba2\u51e0\u65f6\u4f1a\u5165\u5bab\u884c\u523a\u7684\u4e8b\u3002 \u3000\u3000\u7a7f\u4e0a\u8863\u670d\uff0c\u884c\u5165\u5ead\u9662\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u4e86\u7530\u5f53\u3001\u8427\u96e8\u3001\u51cc\u4e91\u548c\u5218\u98de\u9e4f\u5168\u90fd\u5728\u5ead\u9662\u4e2d\u7ad9\u7740\u3002\u56db\u4eba\u4f3c\u4e4e\u6b63\u5728\u4f4e\u58f0\u5546\u91cf\u7740\u4ec0\u4e48 \u4e8b\u60c5\uff1f \u3000\u3000\u5b87\u6587\u6210\u90fd\u7b11\u9053\uff1a \u3000\u3000\u201c\u56db\u4f4d\u8fde\u65e5\u8f9b\u52b3\uff0c\u600e\u4e0d\u597d\u597d\u7761\u4e00\u4e0b\u5462\uff1f\u8fde\u573a\u8840\u6218\uff0c\u751f\u6b7b\u51b3\u5b9a\u4e8e\u77ac\u606f\u4e4b\u95f4\uff0c\u6240\u4ee5\u56db\u4f4d\u60f3\u4f5c \u4ec0\u4e48\u4e5f\u4e0d\u7528\u592a\u8fc7\u62d8\u675f\u4e86\u3002\u201d \u3000\u3000\u867d\u7136\u672a\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u4f46\u753b\u9f99\u70b9\u775b\uff0c\u56db\u4eba\u542c\u5f97\u4e5f\u90fd\u5fc3\u4e2d\u660e\u767d\u3002 \u3000\u3000\u201c\u603b\u7edf\u9886\u8bef\u4f1a\u4e86\u3002\u201d\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u6b63\u8981\u5546\u91cf\u8981\u4e0d\u8981\u53bb\u5435\u9192\u4f60\uff1f\u60f3\u4e0d\u5230\u603b\u7edf\u9886\u4e5f\u662f\u5e2d\u4e0d\u5b89\u6795\u3002\u201d \u3000\u3000\u201c\u6709\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u671b\u7740\u56db\u4eba\uff0c\u5fae\u89c6\u8bb6\u5f02\u3002 \u3000\u3000\u51cc\u4e91\u9053\uff1a \u3000\u3000\u201c\u5341\u4f4d\u5bab\u5973\u5728\u573a\uff0c\u4e0d\u597d\u5f00\u53e3\uff0c\u6015\u5979\u4eec\u53d7\u5230\u60ca\u5413\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u63a5\u9053\uff1a \u3000\u3000\u201c\u662f\u5173\u4e8e\u866c\u9aef\u5ba2\u7684\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u60f3\u548c\u603b\u7edf\u9886\u5546\u8ba8\u4e00\u4e2a\u62d2\u654c\u4e4b\u7b56\uff0c\u4ee5\u56fd\u5e08\u4e4b\u80fd\uff0c\u8fde\u8d5e\u5176\u4eba\u8c6a\u52c7\u3002\u6050\u975e\u5c0f\u53ef\u4e86\uff0c\u6211\u4eec\u8be5\u65e9 \u6709\u4e00\u4e2a\u51c6\u5907\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u56db\u4f4d\u4e0d\u7528\u7ba1\u4e86\uff0c\u4f60\u4eec\u53ef\u4ee5\u63a5\u6218\u866c\u9aef\u5ba2\u4ece\u536b\u3001\u5c5e\u4e0b\u4eec\uff0c\u4f46\u662f\u4e0d\u8bb8\u62d2\u62e6\u866c\u9aef \u5ba2\u2026\u2026\u201d \u3000\u3000\u201c\u96be\u9053\u603b\u7edf\u9886\u8981\u72ec\u6218\u5f3a\u654c\uff1f\u201d\u5218\u98de\u9e4f\u9053\uff1a\u201c\u6b64\u4e8b\u91cd\u5927\uff0c\u5173\u7cfb\u5230\u7687\u4e0a\u7684\u5b89\u5371\uff0c\u5c5e\u4e0b\u6597\u80c6\u76f4 \u8a00\uff0c\u4e0d\u5ba3\u79f0\u5f3a\u8f7b\u654c\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u7167\u56fd\u5e08\u7684\u8bf4\u6cd5\uff0c\u6211\u8fde\u4ed6\u4e09\u62db\u90fd\u63a5\u4e0d\u4e0b\u6765\uff0c\u54ea\u91cc\u8fd8\u80fd\u79f0\u5f3a\u7ecf\u654c\uff1f\u56fd\u5e08\u4e4b\u8a00\uff0c\u4e0d\u5bb9\u7f6e\u7591\u3002 \u6240\u4ee5\uff0c\u5bf9\u4ed8\u866c\u9aef\u5ba2\u7684\u4e8b\u5c31\u7531\u56fd\u5e08\u51fa\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u5218\u98de\u9e4f\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u53ea\u8d1f\u4f20\u8b66\u7684\u8d23\u4efb\uff0c\u4ee5\u6700\u5feb\u7684\u65b9\u6cd5\u628a\u866c\u9aef\u5ba2\u51fa\u73b0\u7684\u6d88\u606f\u901a\u62a5\u56fd\u5e08\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u6211\u7684\u770b\u6cd5\u662f\u8bf8\u4f4d\u5c3d\u91cf\u7684\u907f\u5f00\u4ed6\uff0c\u866c\u9aef\u5ba2\u542b\u6068\u800c\u6765\uff0c\u5fc3\u4e2d\u71c3\u70e7\u7740\u6012\u706b\uff0c\u770b\u5230\u4e86\u5bab\u536b\u3001\u5f69 \u5973\uff0c\u4e5f\u8bb8\u4f1a\u4e0d\u5c51\u52a8\u624b\u3002 \u3000\u3000\u4f46\u662f\u5982\u770b\u5230\u8bf8\u4f4d\uff0c\u7edd\u4e0d\u4f1a\u653e\u8fc7\uff0c\u4f20\u793a\u8b66\u8baf\u65e0\u5f02\u662f\u544a\u8bc9\u4ed6\u505c\u8eab\u4e4b\u5904\uff0c\u5b9e\u4e0d\u5982\u5c0f\u5fc3\u4e00\u4e9b\u7684 \u597d\u3002\u201d \u3000\u3000\u5218\u3001\u8427\u3001\u51cc\u3001\u7530\u6ca1\u6709\u56de\u7b54\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u4e0d\u670d\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5f53\u7136\u77a7\u51fa\u4e86\u56db\u4eba\u5fc3\u4e2d\u7684\u611f\u53d7\u3002 \u3000\u3000\u4ed6\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8bf8\u4f4d\u53ef\u4ee5\u4e0d\u4fe1\u6210\u90fd\u4e4b\u8a00\uff0c\u4f46\u4e0d\u53ef\u4e0d\u4fe1\u4efb\u56fd\u5e08\uff0c\u5979\u662f\u4e3a\u6211\u4eec\u597d\u554a\uff01\u201d \u3000\u3000\u5218\u98de\u9e4f\u9053\u3002 \u3000\u3000\u201c\u5c5e\u4e0b\u4ee5\u98de\u5200\u52a9\u6218\uff0c\u6211\u7684\u98de\u5200\u7edd\u6280\u603b\u7edf\u9886\u5e94\u8be5\u4fe1\u5f97\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u5c06\u519b\uff0c\u4e0d\u8981\u901e\u5f3a\u3002\u201d\u8881\u7d2b\u70df\u7684\u58f0\u97f3\u98d8\u4f20\u8fc7\u6765\uff0c\u4eba\u4e5f\u540c\u65f6\u73b0\u8eab\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5df2\u7b54\u5141\u8ba9\u5b87 \u6587\u5c06\u519b\u63a5\u4ed6\u4e24\u62db\uff0c\u8bf8\u4f4d\u53ef\u4ee5\u9690\u8eab\u5728\u4e00\u4fa7\u770b\u770b\uff0c\u5982\u679c\u81ea\u4fe1\u6709\u6321\u5f97\u866c\u9aef\u5ba2\u4e00\u51fb\u4e4b\u80fd\uff0c\u518d\u51fa\u624b\u4e0d \u8fdf\u3002\u201d \u3000\u3000\u51cc\u3001\u7530\u3001\u8427\u3001\u5218\u832b\u7136\u4e86\u3002 \u3000\u3000\u5bf9\u4e8e\u8881\u7d2b\u70df\uff0c\u4ed6\u4eec\u65e9\u5df2\u7ecf\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u4e86\uff0c\u5982\u4eca\u542c\u5979\u8bf4\u5f97\u5982\u6b64\u614e\u91cd\uff0c\u4e0d\u76f8\u4fe1\u4e5f\u4e0d\u884c \u4e86\u3002 \u3000\u3000\u4f46\u5218\u98de\u9e4f\u8fd8\u662f\u4e0d\u670d\uff0c\u6697\u9053\uff1a\u6211\u9690\u5728\u6697\u4e2d\u53d1\u51fa\u98de\u5200\uff0c\u6697\u52a9\u5b87\u6587\u603b\u7edf\u9886\u4e00\u81c2\u4e4b\u529b\uff0c\u8be5\u4e0d\u4f1a\u6709 \u4ec0\u4e48\u51f6\u9669\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u6708\u5706\u4e4b\u591c\u3002 \u3000\u3000\u4e07\u91cc\u65e0\u4e91\uff0c\u661f\u7a7a\u5982\u6d17\u3002 \u3000\u3000\u957f\u5b89\u5bab\u7684\u5bab\u5ef7\u4e2d\u4e5f\u94fa\u4e86\u4e00\u5c42\u6708\u8272\u3002 \u3000\u3000\u8981\u6765\u7684\uff0c\u7ec8\u4e8e\u6765\u4e86\u3002 \u3000\u3000\u51fa\u4eba\u610f\u5916\u7684\u662f\u592a\u80c6\u5927\uff0c\u6765\u5f97\u592a\u610f\u5916\uff0c\u4e0d\u662f\u6708\u9ed1\u98ce\u9ad8\u7684\u6740\u4eba\u591c\uff0c\u800c\u662f\u660e\u6708\u5982\u663c\u7684\u6708\u5706\u591c\u3002 \u3000\u3000\u4f46\u8881\u7d2b\u70df\u8fd8\u662f\u77e5\u9053\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u51fa\u73b0\u7b2c\u4e00\u91cd\u5c4b\u9762\u4e0a\u65f6\uff0c\u8881\u7d2b\u70df\u5df2\u98d8\u7136\u800c\u81f3\u3002 \u3000\u3000\u5979\u51b7\u7136\u5730\u9053\uff1a \u3000\u3000\u201c\u4e45\u5019\u591a\u65f6\u4e86\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u67d0\u5bb6\u672c\u5c31\u4e0d\u6253\u7b97\u6697\u6765\u6697\u5f80\uff0c\u6240\u4ee5\u624d\u9009\u62e9\u8fd9\u6708\u5706\u4e4b\u591c\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5c31\u7b97\u4f60\u9752\u5929\u767d\u65e5\u4e4b\u4e0b\u6765\u8fd9\u91cc\uff0c\u4e5f\u662f\u4e00\u4e2a\u523a\u5ba2\u3002\u4f60\u52c7\u6b66\u51a0\u4e16\uff0c\u4e09\u767e\u5e74\u5185\u7edd\u4e0d\u4f1a\u518d\u51fa\u73b0\u4f60 \u8fd9\u6837\u7684\u4eba\u7269\u3002\u6240\u4ee5\uff0c\u6211\u8981\u6740\u4e86\u4f60\uff0c\u4ee5\u7edd\u540e\u60a3\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u54fc\u9053\uff1a \u3000\u3000\u201c\u5996\u5973\u51ed\u4ed7\u672f\u6cd5\u5bf9\u654c\uff0c\u7b97\u4ec0\u4e48\u82f1\u96c4\u4eba\u7269\uff1f\u4f60\u5982\u6562\u4ee5\u771f\u5b9e\u6b66\u529f\u63a5\u6211\u4e09\u62db\uff0c\u67d0\u5bb6\u56de\u5934\u5c31\u8d70\uff0c \u6c38\u4e0d\u518d\u51fa\u73b0\u6c5f\u6e56\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u8f6f\u786c\u4e0d\u5403\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u672c\u5c31\u4e0d\u662f\u82f1\u96c4\u4eba\u7269\uff0c\u5f53\u4eca\u4e4b\u4e16\u4e5f\u6ca1\u6709\u4eba\u80fd\u4ee5\u6b66\u529f\u63a5\u4f60\u4e09\u62db\uff0c\u4f60\u5e94\u8be5\u6709\u9010\u9e7f\u9738\u4e1a\u7684\u673a \u4f1a\uff0c\u4f46\u4f60\u521a\u610e\u81ea\u7528\uff0c\u786c\u628a\u8fd9\u4e07\u91cc\u6c5f\u5c71\u8ba9\u7ed9\u522b\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u6307\u674e\u4e16\u6c11\uff1f\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u6740\u4e86\u968b\u7080\u5e1d\uff0c\u67d0\u5bb6\u5c31\u8d76\u8d74\u592a\u539f\u53d6\u674e\u4e16\u6c11\u7684\u9879\u4e0a\u9996\u7ea7\u3002\u201d \u3000\u3000\u201c\u4f60\u6740\u4e0d\u4e86\u674e\u4e16\u6c11\uff0c\u4e5f\u6740\u4e0d\u4e86\u7687\u4e0a\u3002\u6709\u6211\u8881\u7d2b\u70df\u5728\uff0c\u5927\u968b\u671d\u7684\u57fa\u4e1a\u4e5f\u4e0d\u4f1a\u6367\u624b\u8ba9\u4eba\u3002\u6740 \u4e86\u4f60\u4e4b\u540e\uff0c\u53ea\u4f59\u4e0b\u80fd\u6210\u6c14\u5019\u7684\u674e\u4e16\u6c11\uff0c\u5c31\u5bb9\u6613\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u5996\u5973\u597d\u5927\u7684\u53e3\u6c14\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u67d0\u5bb6\u867d\u7136\u672a\u4e60\u7ec3\u672f\u6cd5\uff0c\u4f46\u672f\u6cd5\u672a\u5fc5\u80fd\u591f\u4f24\u6211\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u53ea\u53ef\u60dc\uff0c\u4f60\u5230\u4e86\u957f\u5b89\u5bab\u5ef7\u3002\u4f60\u4e0d\u8be5\u6765\u8fd9\u91cc\u7684\uff0c\u4f46\u4f60\u5374\u6765\u4e86\u3002 \u3000\u3000\u6765\u5f97\u5bb9\u6613\uff0c\u56de\u53bb\u96be\uff01\u5f20\u4ef2\u7ad6\uff0c\u4f60\u9003\u8d70\u7684\u673a\u4f1a\u4e0d\u5927\u3002\u201d \u3000\u3000\u201c\u597d\u72c2\u5984\u7684\u53e3\u6c14\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u62d4\u51fa\u8eab\u4f69\u5b9d\u5251\u3002 \u3000\u3000\u662f\u53c8\u91cd\u53c8\u5927\u7684\u94c1\u5251\u3002 \u3000\u3000\u5b8c\u5168\u80cc\u79bb\u4e86\u5251\u8d70\u8f7b\u7075\u7684\u539f\u5219\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5de6\u624b\u634f\u8bc0\uff0c\u53f3\u624b\u63e1\u62f3\uff0c\u6446\u51fa\u4e86\u62d2\u654c\u67b6\u5f0f\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u6709\u5251\uff0c\u4e3a\u4f55\u4e0d\u62d4\u5251\u800c\u6597\uff1f\u96be\u9053\u8981\u4ee5\u4e00\u53ea\u7ea4\u5de7\u7684\u7389\u624b\u53bb\u63a5\u866c\u9aef\u5ba2\u91cd\u903e\u516d\u5341\u65a4\u7684\u94c1\u5251 \u5417\uff1f \u3000\u3000\u201c\u56fd\u5e08\uff0c\u8ba9\u6210\u90fd\u63a5\u4ed6\u4e24\u5251\uff0c\u8bd5\u8bd5\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u9ad8\u624b\u7a76\u7adf\u6709\u591a\u5c11\u65a4\u4e24\uff1f \u3000\u3000\u5b87\u6587\u6210\u90fd\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u7532\u80c4\uff0c\u624b\u4e2d\u62ff\u7684\u662f\u91cd\u903e\u767e\u65a4\u4ee5\u4e0a\u7684\u954f\u91d1\u9557\u3002 \u3000\u3000\u4e00\u526f\u4e34\u9635\u5bf9\u654c\u51b2\u950b\u9677\u9635\u7684\u67b6\u52bf\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5fc3\u4e2d\u5fd6\u9053\uff1a \u3000\u3000\u5b87\u6587\u6210\u90fd\u662f\u957f\u5b89\u6559\u573a\u593a\u9b41\u7684\u7b2c\u4e00\u597d\u624b\uff0c\u6c5f\u5317\u82f1\u96c4\u65e0\u51fa\u5176\u53f3\uff0c\u770b\u4ed6\u624b\u4e2d\u7684\u5175\u5203\u91cd\u91cf\u4f3c\u662f\u72b9 \u5728\u866c\u9aef\u5ba2\u4e4b\u4e0a\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u90fd\u662f\u9876\u5c16\u9ad8\u624b\uff0c\u8ba9\u4ed6\u4eec\u5bf9\u6253\u4e24\u62db\uff0e\u5b9a\u7136\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\u2026\u2026 \u3000\u3000\u4e0d\u95fb\u8881\u7d2b\u70df\u7684\u56de\u7b54\uff0c\u5b87\u6587\u6210\u90fd\u6709\u4e9b\u6025\u4e86\uff0c\u9053\uff1a \u3000\u3000\u201c\u56fd\u5e08\u4e0d\u662f\u5e94\u5141\u8fc7\u6210\u90fd\u5417\uff1f\u6210\u90fd\u5148\u63a5\u866c\u9aef\u5ba2\u4e24\u5251\uff0c\u8fd9\u673a\u4f1a\u5982\u679c\u9519\u8fc7\uff0c\u4eca\u751f\u5c31\u5f88\u96be\u518d\u6709 \u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c06\u519b\u5c0f\u5fc3\uff0c\u866c\u9aef\u5ba2\u7684\u5de6\u53f3\u8fd8\u9690\u4f0f\u6709\u76f8\u673a\u51fa\u5200\u7684\u5e2e\u624b\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u51fa\u6765\u5427\uff01\u65e2\u7136\u88ab\u53d1\u73b0\u4e86\uff0c\u5c31\u4e0d\u7528\u518d\u85cf\u5934\u9732\u5c3e\u4e86\u3002\u201d \u3000\u3000\u4e24\u6761\u4eba\u5f71\u5e94\u58f0\u800c\u51fa\uff0c\u98de\u843d\u5728\u866c\u9aef\u5ba2\u7684\u8eab\u540e\u3002 \u3000\u3000\u6b63\u662f\u9f99\u5c06\u6c5f\u68ee\u3001\u864e\u5c06\u71d5\u53ef\uff0c\u662f\u866c\u9aef\u5ba2\u7684\u4e24\u4e2a\u968f\u8eab\u4f8d\u536b\u3002 \u3000\u3000\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u4e5f\u8ddf\u7740\u73b0\u8eab\u4e86\u3002 \u3000\u3000\u5218\u98de\u9e4f\u6ca1\u6709\u51fa\u73b0\uff0c\u4ed6\u53cc\u624b\u5404\u63a7\u63e1\u56db\u67c4\u98de\u5200\uff0c\u51c6\u5907\u6697\u52a9\u624b\u6587\u6210\u90fd\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u671b\u4e86\u8427\u96e8\u7b49\u4e09\u4eba\u4e00\u773c\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd8\u6709\u591a\u5c11\uff0c\u8981\u4ed6\u4eec\u7edf\u7edf\u51fa\u6765\u5427\uff01\u4f60\u4eec\u548c\u5b87\u6587\u6210\u90fd\u8054\u624b\uff0c\u80fd\u591f\u63a5\u6211\u4e09\u62db\uff0c\u6211\u8fde\u6768\u5e7f\u4e5f\u4e0d \u6740\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u542c\u5f97\u706b\u5192\u4e09\u4e08\u3002 \u3000\u3000\u4f46\u4ed6\u62c5\u5fc3\u8427\u96e8\u7b49\u4e09\u4eba\u5fcd\u53d7\u4e0d\u4f4f\u5bf9\u65b9\u8bbd\u6fc0\u800c\u51fa\u624b\uff0c\u5fd9\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5927\u72c2\u4e86\uff0c\u5f53\u771f \u662f\u773c\u7a7a\u56db\u6d77\u76ee\u4e2d\u65e0\u4eba\u3002\u201d \u3000\u3000\u559d\u58f0\u98de\u6251\u800c\u4e0a\uff0c\u624b\u4e2d\u954f\u91d1\u9557\u631f\u7740\u4e00\u80a1\u7834\u7a7a\u91d1\u98ce\u7838\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u954f\u91d1\u9557\u91cd\u8fbe\u4e00\u767e\u516d\u5341\u65a4\uff0c\u5b87\u6587\u6210\u90fd\u62a1\u52a8\u8d77\u6765\u7838\u4e0b\uff0c\u771f\u6709\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\u3002 \u3000\u3000\u5982\u6b64\u60ca\u4eba\u7684\u5a01\u52bf\uff0c\u866c\u9aef\u5ba2\u7adf\u7136\u662f\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46\u89c1\u4ed6\u94c1\u5251\u4e00\u4e3e\uff0c\u786c\u5411\u954f\u91d1\u9557\u4e0a\u8fce\u4e86 \u53bb\u3002 \u3000\u3000\u4e00\u58f0\u91d1\u94c1\u5de8\u9707\uff01 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7684\u954f\u91d1\u9557\u7adf\u88ab\u4e00\u5251\u9707\u98de\u4e86\u3002 \u3000\u3000\u4ed6\u4eba\u968f\u7740\u91d1\u9557\u98de\u51fa\u4e86\u4e24\u4e09\u4e08\u9ad8\uff0c\u8fd8\u662f\u65e0\u6cd5\u63e1\u4f4f\u5175\u5203\uff0c\u677e\u5f00\u53cc\u624b\uff0c\u954f\u91d1\u9557\u76f4\u98de\u5230\u5341\u4f59\u4e08\u5916 \u624d\u5411\u5730\u4e0a\u843d\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u4eba\u4e5f\u88ab\u9707\u843d\u5728\u4e24\u4e08\u5f00\u5916\u3002 \u3000\u3000\u53ea\u89c9\u90a3\u80a1\u53cd\u5f39\u4e4b\u529b\u5f3a\u5927\u65e0\u6bd4\uff0c\u4e0d\u4f46\u9707\u9ebb\u4e86\u53cc\u81c2\u800c\u5175\u5203\u51fa\u624b\uff0c\u4e14\u88ab\u53cd\u9707\u7684\u6c14\u8840\u6d6e\u52a8\uff0c\u8fde\u771f \u6c14\u4e5f\u65e0\u6cd5\u63d0\u805a\u3002 \u3000\u3000\u81ea\u7136\u4e5f\u65e0\u6cd5\u65bd\u5c55\u8f7b\u529f\uff0c\u662f\u4ece\u4e24\u4e08\u591a\u9ad8\u6454\u4e0b\u53bb\uff0c\u7740\u5730\u65f6\u7830\u7136\u6709\u58f0\uff0c\u76f4\u6389\u5f97\u5b87\u6587\u6210\u90fd\u6655\u5934\u8f6c \u5411\uff0c\u773c\u82b1\u9aa8\u75db\u3002 \u3000\u3000\u7740\u5730\u540e\u597d\u4e00\u9635\u5de5\u592b\uff0c\u624d\u7f13\u8fc7\u4e00\u53e3\u6c14\u6765\uff0e\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u5982\u975e\u8881\u7d2b\u70df\u5728\u4e00\u4fa7\u9632\u8303\uff0c\u65e9\u88ab\u9f99\u3001\u864e\u4e8c\u5c06\u53d6\u4e86\u9996\u7ea7\u3002 \u3000\u3000\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u770b\u5446\u4e86\u3002 \u3000\u3000\u770b\u5f97\u5fd8\u8bb0\u4e86\u53bb\u6276\u5b87\u6587\u6210\u90fd\u3002 \u3000\u3000\u4e09\u4eba\u4e0d\u77e5\u9053\u866c\u9aef\u5ba2\u7684\u6b66\u529f\u9ad8\u5230\u4f55\u7b49\u5883\u754c\uff1f\u4f46\u5374\u77e5\u5b87\u6587\u6210\u90fd\u7684\u672c\u9886\u4e09\u4e2a\u4eba\u52a0\u8d77\u6765\uff0c\u4e5f\u63a5\u4e0b \u4e0b\u5b87\u6587\u6210\u90fd\u4e09\u5341\u62db\u3002 \u3000\u3000\u4f46\u5b87\u6587\u6210\u90fd\u5374\u8fde\u866c\u9aef\u5ba2\u4e00\u5251\u4e5f\u63a5\u4e0d\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u662f\u4f55\u7b49\u9065\u8fdc\u7684\u8ddd\u79bb\uff0c\u4e09\u4e2a\u4eba\u54ea\u91cc\u8fd8\u6562\u51fa\u624b\u3002 \u3000\u3000\u4f46\u662f\u5218\u98de\u9e4f\u5374\u6ca1\u6709\u770b\u5f97\u8fd9\u4e48\u6e05\u695a\uff0c\u516b\u628a\u98de\u5200\u7834\u7a7a\u800c\u6765\uff0c\u5206\u5411\u866c\u9aef\u5ba2\u516b\u5904\u7a74\u4f4d\u624e\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5927\u58f0\u53eb\u9053\uff1a \u3000\u3000\u201c\u4e0d\u53ef\u51fa\u624b\uff01\u5feb\u4e9b\u6536\u5200\u9000\u5f00\uff01\u201d \u3000\u3000\u592a\u665a\u4e86\uff01 \u3000\u3000\u866c\u9aef\u5ba2\u7684\u94c1\u5251\u5df2\u7ed5\u8eab\u98de\u8f6c\u3002 \u3000\u3000\u516b\u628a\u98de\u5200\u672c\u6765\u6709\u591a\u79cd\u7684\u53d8\u5316\uff0c\u4f46\u5f3a\u70c8\u7684\u5251\u98ce\u4e4b\u4e0b\uff0e\u53d8\u5316\u6ede\u6b62\u4e86\uff0c\u88ab\u94c1\u5251\u4e0a\u529b\u9053\u7838\u5f97\u56db\u4e0b \u6a2a\u98de\u3002 \u3000\u3000\u65ad\u7684\u65ad\u4e86\uff0c\u98de\u7684\u98de\u5f00\u4e86\u3002 \u3000\u3000\u4f46\u5218\u98de\u9e4f\u6700\u5389\u5bb3\u7684\u56db\u628a\u98de\u5200\u4e5f\u51fa\u4e86\u624b\uff0c\u98de\u5411\u866c\u9aef\u5ba2\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u559d\u9053\uff1a \u3000\u3000\u201c\u81ea\u53d6\u6b7b\u4ea1\uff0c\u602a\u4e0d\u5f97\u67d0\u5bb6\u5fc3\u72e0\u624b\u8fa3\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u53e3\u4e2d\u8bf4\uff0c\u94c1\u5251\u5374\u7ed5\u8eab\u98de\u821e\u3002 \u3000\u3000\u4e00\u80a1\u51cc\u5389\u7684\u5251\u98ce\u5728\u4ed6\u8eab\u8eaf\u7684\u56db\u5468\u8f6c\u52a8\u3002 \u3000\u3000\u56db\u628a\u53d8\u5316\u83ab\u6d4b\u800c\u80fd\u591f\u4e92\u76f8\u649e\u51fb\u63a5\u529b\u53d8\u5316\u7684\u98de\u5200\uff0c\u5728\u5f3a\u70c8\u5251\u98ce\u7684\u6f29\u6da1\u4e2d\uff0c\u5b8c\u5168\u6d88\u5931\u4e86\u84c4\u542b \u5728\u5200\u4e0a\u7684\u5185\u529b\uff0c\u968f\u7740\u5f3a\u70c8\u7684\u5251\u98ce\u8f6c\u52a8\u7740\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u7a81\u7136\u628a\u624b\u4e2d\u7684\u94c1\u5251\u6307\u5411\u5218\u98de\u9e4f\u85cf\u8eab\u4e4b\u5904\uff0c\u56db\u628a\u98de\u5200\u75be\u77e2\u4e00\u822c\u5c04\u4e86\u51fa\u53bb\u3002\u53bb\u52bf\u4e4b \u5feb\uff0c\u6bd4\u5218\u98de\u9e4f\u53d1\u5200\u7684\u901f\u5ea6\u8fd8\u8981\u8d85\u8fc7\u4e00\u4e9b\u3002 \u3000\u3000\u66f4\u53ef\u6015\u7684\u662f\u4e00\u80a1\u5251\u98ce\u5148\u5200\u800c\u81f3\uff0c\u786c\u662f\u628a\u5218\u98de\u9e4f\u7ed9\u56f0\u4f4f\u4e86\uff0c\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u5374\u65e0\u6cd5\u79fb\u52a8\u4e00 \u6b65\u3002 \u3000\u3000\u7b49\u5728\u90a3\u91cc\u6328\u98de\u5200\u4e86\uff0c\u56db\u628a\u98de\u5200\u4e00\u7ebf\u7a7f\u5165\uff0c\u540e\u5200\u649e\u5728\u524d\u5200\u4e0a\uff0c\u56db\u628a\u5200\u8fde\u6210\u4e00\u628a\u5200\uff0c\u7a7f\u8fc7\u4e86 \u5fc3\u810f\u8981\u5bb3\u3002 \u3000\u3000\u5251\u98ce\u6d88\u89e3\uff0c\u5218\u98de\u9e4f\u624d\u5927\u53eb\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u5c38\u4f53\u4e5f\u5012\u5728\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u8fd9\u53e3\u8840\u5012\u4e0d\u662f\u98de\u5200\u6740\u51fa\u6765\u7684\uff0c\u5200\u5728\u524d\u80f8\u6d1e\u7a7f\u5fc3\u810f\uff1b\u662f\u6012\u6025\u4e4b\u4e0b\uff0c\u884c\u8840\u4e0a\u6d8c\u800c\u6c14\u51fa\u6765\u7684\u4e00 \u53e3\u8840\u3002 \u3000\u3000\u5584\u6cf3\u8005\uff0c\u6b7b\u4e8e\u6c34\u3002 \u3000\u3000\u8be1\u5f02\u591a\u53d8\u7684\u98de\u5200\u5947\u672f\uff0c\u5374\u6b7b\u5728\u81ea\u5df1\u7684\u98de\u5200\u4e4b\u4e0b\u3002 \u3000\u3000\u5218\u98de\u9e4f\u4e5f\u8bb8\u4f1a\u6b7b\u4e0d\u7791\u76ee\u7684\u3002 \u3000\u3000\u8c01\u77e5\u8eb2\u5728\u8fdc\u5904\u53d1\u98de\u5200\uff0c\u5374\u662f\u6b7b\u5728\u81ea\u5df1\u7684\u98de\u5200\u4e4b\u4e0b\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u51fa\u795e\u5165\u5316\u7684\u6b66\u529f\uff0c\u5b9e\u5df2\u5230\u4e86\u65e0\u6240\u4e0d\u80fd\u7684\u5883\u754c\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u6389\u843d\u7684\u5730\u65b9\u8ddd\u79bb\u5218\u98de\u9e4f\u4e0d\u8fdc\uff0c\u4e5f\u611f\u53d7\u5230\u90a3\u80a1\u5f3a\u70c8\u7684\u5251\u98ce\u5982\u6c34\u4e2d\u7684\u6f29\u6da1\uff0c\u5218\u98de\u9e4f \u5b8c\u5168\u88ab\u5b9a\u4f4f\u4e86\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u505c\u8eab\u5728\u4e00\u4e08\u4e4b\u5916\uff0c\u4e5f\u611f\u5230\u5251\u98ce\u51cc\u5389\uff0c\u4f3c\u6b32\u8981\u88c2\u80a4\u800c\u5165\u3002 \u3000\u3000\u4e0d\u670d\u6c14\u4e5f\u4e0d\u884c\u4e86\uff01 \u3000\u3000\u8881\u7d2b\u70df\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u662f\u4eba\u4e2d\u7684\u6218\u795e\uff0c\u6ca1\u6709\u4eba\u80fd\u6297\u62d2\u4ed6\u5168\u529b\u7684\u4e00\u51fb\u3002 \u3000\u3000\u60f3\u5230\u81ea\u5df1\u8981\u63a5\u4ed6\u4e09\u62db\u7684\u8c6a\u8bed\uff0c\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff01\u8881\u7d2b\u70df\u8981\u4ed6\u6539\u6210\u4e00\u62db\uff0c\u5fc3\u4e2d\u8fd8\u6709\u4e9b\u4e0d \u592a\u670d\u6c14\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e00\u62db\u4e5f\u63a5\u4e0d\u4e0b\u6765\u3002 \u3000\u3000\u201c\u6740\u4eba\u507f\u547d\uff01\u201d \u3000\u3000\u8881\u7d2b\u70df\u52a8\u4e86\u6012\u706b\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u987e\u5fcc\u4e86\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u9053\u91d1\u5149\u76f4\u5c04\u8fdb\u53bb\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u5927\u559d\u4e00\u58f0\uff0c\u6709\u5982\u4e00\u58f0\u95f7\u96f7\u6c89\u843d\uff0c\u624b\u4e2d\u94c1\u5251\u8fd0\u8f6c\u5982\u98de\uff0c\u5168\u8eab\u4e5f\u90fd\u5728\u94c1\u5251\u62a4\u536b\u4e4b\u4e0b \u4e86\u3002 \u3000\u3000\u672c\u662f\u4e91\u8212\u96fe\u6d8c\u4e00\u822c\u7684\u91d1\u5149\u6f2b\u6df9\u8fc7\u53bb\uff0c\u4f46\u7adf\u88ab\u5251\u6c14\u6321\u4f4f\u4e86\u3002 \u3000\u3000\u91d1\u5149\u6269\u6563\uff0c\u628a\u866c\u9aef\u5ba2\u5168\u5305\u5728\u4e00\u7247\u91d1\u5149\u4e4b\u4e2d\uff0c\u4f46\u5374\u65e0\u6cd5\u8fd1\u8eab\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u821e\u5251\u5982\u8f6e\uff0c\u5e03\u6210\u4e00\u4e2a\u4e08\u4f59\u7684\u5251\u7f51\uff0c\u91d1\u5149\u4e91\u6eda\u6d6a\u7ffb\uff0c\u5c31\u662f\u65e0\u6cd5\u903c\u5165\u5251\u7f51\u4e4b\u5185\u3002 \u3000\u3000\u5f53\u771f\u662f\u58ee\u89c2\u554a\uff01 \u3000\u3000\u672f\u6cd5\u548c\u5251\u672f\uff0c\u90fd\u5c55\u73b0\u51fa\u4e86\u6781\u81f4\u5a01\u52bf\u3002 \u3000\u3000\u7adf\u7136\u5f62\u6210\u4e86\u534a\u65a4\u516b\u4e24\u7684\u76f8\u6301\u5c40\u9762\u3002 \u3000\u3000\u4e5f\u51fa\u73b0\u4e86\u6c5f\u6e56\u4e0a\u524d\u6240\u672a\u6709\u8fc7\u7684\u5947\u89c2\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53f3\u624b\u4e2d\u91d1\u5149\u4e0d\u505c\u6d8c\u51fa\uff0c\u989c\u8272\u4e5f\u6108\u89c1\u91d1\u9ec4\uff0c\u5bc6\u5ea6\u4e5f\u6e10\u52a0\u6d53\u3002\u770b\u5979\u795e\u8272\u51dd\u91cd\uff0c\u9762\u76ee\u8083 \u7136\uff0c\u867d\u662f\u5360\u4e86\u4f18\u52bf\uff0c\u770b\u4e0a\u53bb\u4e5f\u4e0d\u8f7b\u677e\u3002 \u3000\u3000\u53ef\u662f\u866c\u9aef\u5ba2\u7684\u5251\u52bf\u4e5f\u662f\u8d8a\u821e\u8d8a\u75be\uff0c\u6570\u4e08\u5916\u90fd\u53ef\u542c\u5230\u5251\u98ce\u7834\u7a7a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u662f\u6b66\u4e4b\u5065\u8005\uff0c\u4f3c\u6709\u7740\u65e0\u7a77\u7684\u529b\u91cf\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u62d4\u51fa\u4e86\u80cc\u4e0a\u7684\u5b9d\u5251\uff0c\u5439\u4e00\u53e3\u771f\u6c14\uff0c\u5b9d\u5251\u817e\u7a7a\u800c\u8d77\uff0c\u76f4\u5411\u866c\u9aef\u5ba2\u5c04\u51fa\u3002 \u3000\u3000\u4e0d\u662f\u9a6d\u5251\u672f\uff0c\u800c\u662f\u98de\u5251\uff0c\u4f3c\u901a\u7075\u7684\u6d3b\u5251\u3002\u8fd9\u4e9b\u672f\u6cd5\u7684\u53d8\u5316\u4e4b\u5947\uff0c\u9a6d\u5251\u672f\u662f\u4e60\u5251\u4eba\u6700\u9ad8\u7684 \u6210\u5c31\u3002 \u3000\u3000\u7530\u5f53\u3001\u8427\u96e8\u3001\u51cc\u4e91\u770b\u5f97\u76ee\u77aa\u53e3\u5446\u4e86\u3002 \u3000\u3000\u98de\u5251\u7a7f\u8fc7\u4e86\u4e91\u5c01\u96fe\u9501\u4f3c\u7684\u91d1\u5149\u3002 \u3000\u3000\u4f46\u5374\u7a7f\u4e0d\u8fc7\u866c\u9aef\u5ba2\u7684\u5251\u7f51\u3002 \u3000\u3000\u8fde\u58f0\u91d1\u94c1\u4ea4\u9e23\u4e2d\uff0c\u98de\u5251\u88ab\u9707\u5f97\u8fde\u8fde\u5012\u98de\u56de\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u7ecf\u8fc7\u597d\u4e00\u9635\u4f11\u606f\uff0c\u6b66\u529f\u6062\u590d\uff0c\u867d\u7136\u5f88\u75db\u60dc\u5218\u98de\u9e4f\u7684\u6b7b\u4ea1\uff0c\u4f46\u56de\u5929\u4e4f\u672f\u4e86\u3002\u5f3a\u5fcd \u75db\u82e6\uff0c\u62ed\u53bb\u843d\u4e0b\u7684\u773c\u6cea\uff0c\u4e00\u54ac\u7259\u98de\u4e0a\u5c4b\u9876\u3002 \u3000\u3000\u4ed6\u62c5\u5fc3\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\uff0c\u5fc3\u5207\u5218\u98de\u9e4f\u6b7b\u4ea1\u7684\u4ec7\u6068\uff0c\u60c5\u6025\u51fa\u624b\uff0c\u90a3\u5c31\u662f\u4e2a\u60e8\u75db\u7684\u60b2\u5267\uff0c \u5c5e\u4e0b\u7cbe\u9510\uff0c\u4e5f\u5c06\u5c3d\u8d1f\u4e8e\u6b64\u4e86\u3002 \u3000\u3000\u89c1\u4e09\u4eba\u5e73\u5b89\u65e0\u635f\u7684\u7ad9\u5728\u4e00\u4fa7\u89c2\u6218\uff0c\u800c\u4e14\u5168\u5165\u4e86\u5fd8\u6211\u4e4b\u5883\uff0c\u5fc3\u4e2d\u7a0d\u611f\u5b89\u6170\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e09 \u4f4d\uff0c\u8fc7\u6765\u4e00\u6b65\u3002\u201d \u3000\u3000\u58f0\u97f3\u7528\u5185\u529b\u9001\u51fa\u3002 \u3000\u3000\u8427\u3001\u51cc\u3001\u7530\uff0c\u4e09\u4eba\u90fd\u542c\u5230\u4e86\uff0c\u56de\u5934\u770b\u5230\u4e86\u5b87\u6587\u6210\u90fd\uff0c\u624d\u60f3\u8d77\u4ed6\u88ab\u9707\u4f24\u4e86\uff0c\u6025\u6025\u884c\u4e86\u8fc7 \u53bb\uff0c\u9053\uff1a \u3000\u3000\u201c\u603b\u7edf\u9886\u6ca1\u6709\u4e8b\u5427\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u6211\u5f88\u597d\uff0c\u53ea\u662f\u5218\u526f\u7edf\u9886\u6350\u8eaf\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\uff01\u6211\u4eec\u6ca1\u6cd5\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u5728\u866c\u9aef\u5ba2\u7684\u94c1\u5251\u4e0b\uff0c\u6211\u4eec\u4e0d\u582a\u4e00\u51fb\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u8bf4\u7684\u5bf9\uff01\u6211\u4e00\u76f4\u62c5\u5fc3\u4f60\u4eec\u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\u800c\u8d38\u7136\u51fa\u624b\uff0c\u90a3\u5c31\u662f\u6211\u4eec\u6700\u5927\u7684\u4e0d\u5e78\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u3000\u3000\u201c\u6211\u62db\u547c\u4f60\u4eec\u8fc7\u6765\uff0c\u662f\u56e0\u4e3a\u7ad9\u7684\u8ddd\u79bb\u592a\u8fd1\u4e86\uff0c\u6211\u6015\u697c\u53f0\u5931\u706b\uff0c\u6b83\u53ca\u6c60\u9c7c\u3002\u8fd9\u4e00\u573a\u4ed7\uff0c\u4e0d \u8bba\u8c01\u80dc\u8c01\u8d1f\uff0c\u6211\u4eec\u90fd\u65e0\u6cd5\u5e2e\u5fd9\uff0c\u5982\u679c\u56fd\u5e08\u4e0d\u654c\uff0c\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u8427\u96e8\u3001\u7530\u5f53\u3001\u51cc\u4e91\u4e09\u4eba\u5fc3\u4e2d\u90fd\u660e\u767d\u8a00\u672a\u5c3d\u610f\u7684\u610f\u601d\u3002 \u3000\u3000\u201c\u603b\u7edf\u9886\u662f\u60f3\u8ba9\u6211\u4eec\u8d70\uff1f\u201d\u7530\u5f53\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u7559\u8fd9\u91cc\uff0c\u53ea\u4e0d\u8fc7\u591a\u9001\u51e0\u6761\u6027\u547d\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u866c\u9aef\u5ba2\u662f\u4e0d\u53ef\u4e0e\u654c\u7684\uff0c\u4ed6\u7684\u6b66\u529f\u9ad8\u8fc7\u6211\u4eec\u592a\u591a\u4e86\uff0c\u96c6\u5927\u519b\u5341\u4e07\u4e5f\u56f0\u4e0d\u4f4f\u4ed6\uff0c\u4f60\u4eec\u53c8\u4f55 \u5fc5\u767d\u767d\u727a\u7272\u5462\uff1f\u201d \u3000\u3000\u201c\u603b\u7edf\u9886\u5462\uff1f\u201d\u8427\u96e8\u9053\uff1a \u3000\u3000\u201c\u662f\u4e0d\u662f\u548c\u6211\u4eec\u4e00\u8d77\u8d70\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u80fd\u8d70\u3002\u201d\u5b87\u6587\u6210\u90fd\u9053\uff1a \u3000\u3000\u201c\u7687\u4e0a\u5bf9\u6211\u6069\u60c5\u6df1\u91cd\uff0c\u6211\u8981\u4ee5\u6b7b\u62a5\u6548\uff0c\u4ee5\u5c3d\u4eba\u81e3\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u51cc\u4e91\u7a81\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u603b\u7edf\u9886\u591a\u8651\u4e86\uff0c\u56fd\u5e08\u4e0d\u4f1a\u8d25\uff0c\u771f\u7684\u4e0d\u5e78\u8d25\u4e86\uff0c\u6211\u4eec\u4e5f\u8d70\u4e0d\u4e86\uff0c\u866c\u9aef\u5ba2\u9a6d\u5251\u4e00\u51fb\uff0c\u5feb\u52a0 \u95ea\u7535\uff0c\u600e\u4e48\u4e2a\u8d70\u6cd5\u5462\uff1f\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u4e0d\u518d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u660e\u77e5\u7ed3\u679c\u4e86\uff0c\u518d\u8c08\u4e0b\u53bb\u5c31\u6beb\u65e0\u610f\u4e49\u4e86\u3002 \u3000\u3000\u4f46\u95fb\u866c\u9aef\u5ba2\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u672f\u6cd5\u4e4b\u529b\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u67d0\u5bb6\u8981\u53cd\u51fb\u4e86\u3002\u201d \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u5934\u4e00\u8df3\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u4ed6\u88ab\u56f0\u5728\u91cd\u91cd\u7684\u91d1\u5149\u4e4b\u4e2d\uff0c\u8fd8\u6709\u53cd\u51fb\u4e4b\u80fd\uff0c\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u4e4b\u8a89\uff0c\u679c\u7136\u662f\u5f53\u4e4b\u65e0\u6127 \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u94ff\u9535\u201d\u4e00\u58f0\u5927\u9707\uff01 \u3000\u3000\u8881\u7d2b\u70df\u653e\u51fa\u653b\u51fb\u866c\u9aef\u5ba2\u7684\u98de\u5251\uff0c\u7adf\u7136\u88ab\u94c1\u5251\u51fb\u6210\u4e24\u65ad\uff0c\u7834\u4e86\u8881\u7d2b\u70df\u7684\u98de\u5251\u6cd5\u672f\uff0c\u8dcc\u843d\u5730 \u4e0a\uff0c\u6210\u4e86\u4e24\u6bb5\u5e9f\u94c1\u3002 \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5c06\u6b66\u529f\u7ec3\u5230\u4e86\u5982\u6b64\u5883\u754c\uff0c\u4e0d\u4f46\u524d\u65e0\u53e4\u4eba\uff0c\u53ea\u6050\u540e\u65e0\u6765\u8005\u4e86\uff0c\u6740\u4e86\u4f60\uff0c\u4e5f\u5b9e\u5728\u53ef \u60dc\u2026\u2026\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u5996\u5973\u4e0d\u8981\u5938\u53e3\uff0e\u67d0\u5bb6\u6765\u77e3\uff01\u201d \u3000\u3000\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u62a1\u52a8\u624b\u4e2d\u94c1\u5251\uff0c\u6fc0\u70c8\u7684\u5251\u6c14\u7adf\u7136\u628a\u91d1\u5149\u51b2\u5f00\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u98de\u4e0d\u8fc7\u4e00\u4e08\u5de6\u53f3\u53c8\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u7ed5\u8eab\u91d1\u5149\u6709\u5982\u6de4\u6ce5\u3001\u6d41\u6c99\uff0c\u963b\u529b\u5947\u5927\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u6325\u5251\u963b\u6321\u91d1\u5149\uff0c\u4e0d\u8ba9\u5b83\u4eec\u5408\u4e00\u5904\u65f6\uff0c\u8fd8\u53ef\u8f7b\u677e\u5e94\u4ed8\uff0c\u8eab\u5b50\u98de\u8d77\u5411\u5916\u4e00\u51b2\uff0c\u624d\u77e5\u5389 \u5bb3\u3002 \u3000\u3000\u98de\u884c\u4e0d\u8fc7\u4e08\u8bb8\uff0c\u5df2\u89c9\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u89c9\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u866c\u9aef\u5ba2\u5374\u662f\u4e00\u4ee3\u6b66\u5b66\u5947\u624d\uff0c\u53cc\u8db3\u5df2\u843d\u5b9e\u5730\uff0c\u6c14\u529b\u5df2\u590d\uff0c\u94c1\u5251\u5288\u51fa\u7684\u5251\u98ce\u53c8\u628a\u5408\u805a\u7684\u91d1 \u5149\u903c\u5f00\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u5bf9\u5cd9\u4e4b\u6218\uff0c\u866c\u9aef\u5ba2\u4e5f\u5b66\u5230\u8c28\u614e\u4e8c\u5b57\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u5288\u51fa\u91d1\u98ce\u5251\u6c14\uff0c\u4e0d\u8ba9\u91d1\u5149\u8fd1\u8eab\uff0c\u4e00\u9762\u4e3e\u6b65\u5411\u524d\u884c\u4e86\u53bb\uff0c\u4ecd\u662f\u5bf9\u51c6\u4e86\u8881\u7d2b\u70df\u7684\u505c\u8eab \u4e4b\u5904\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7a81\u7136\u6536\u4e86\u53f3\u624b\uff0c\u53cc\u624b\u5408\u5341\u5f53\u80f8\uff0c\u795e\u60c5\u8083\u7136\uff0c\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\u3002 \u3000\u3000\u6d8c\u805a\u91d1\u5149\u540e\u7ee7\u65e0\u529b\u4e4b\u4e0b\uff0c\u6e10\u8f6c\u7a00\u8584\uff0c\u5728\u866c\u9aef\u5ba2\u7684\u5f3a\u70c8\u5251\u98ce\u6fc0\u8361\u4e4b\u4e0b\uff0c\u7adf\u7136\u9010\u6e10\u7684\u6d88\u6563 \u4e86\u3002 \u3000\u3000\u6708\u534e\u4f3c\u6c34\uff0c\u666f\u7269\u53c8\u6062\u590d\u6e05\u660e\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u5c31\u7ad9\u5728\u4ed6\u4e24\u4e08\u5916\u5c4b\u74e6\u4e4b\u4e0a\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u65e9\u77e5\u6280\u4ec5\u53ca\u6b64\uff0c\u5927\u519b\u4e5f\u4e0d\u7528\u64a4\u56de\u534e\u9634\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u6ca1\u6709\u7406\u4ed6\uff0c\u53ea\u662f\u8083\u7136\u5730\u7ad9\u7740\u3002 \u3000\u3000\u6708\u5149\u4e0b\u8863\u88d9\u98d8\u98de\uff0c\u6258\u6751\u51fa\u5979\u7684\u5982\u82b1\u5bb9\u8c8c\uff0c\u771f\u5982\u4e34\u51e1\u4ed9\u5b50\uff0c\u53ea\u4e0d\u8fc7\u53cc\u624b\u5408\u5341\uff0c\u770b\u4e0a\u53bb\u53c8\u591a \u4e86\u4e00\u4efd\u5e84\u4e25\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u4e0d\u662f\u83bd\u649e\u7684\u4eba\uff0c\u8881\u7d2b\u70df\u65e2\u65e0\u52a8\u4f5c\uff0c\u4e5f\u4e0d\u7406\u4ed6\uff0c\u8ba9\u4ed6\u52a8\u4e86\u6000\u7591\u3002 \u3000\u3000\u4ed4\u7ec6\u770b\u53bb\uff0c\u8881\u7d2b\u70df\u5728\u4e00\u5c42\u6de1\u6de1\u7684\u767d\u6c14\u4fdd\u62a4\u4e4b\u4e2d\uff0c\u4f3c\u662f\u62ab\u4e0a\u4e86\u4e00\u5c42\u767d\u8272\u8584\u7eb1\u3002\u6708\u5149\u4e0b\uff0c\u5f88 \u96be\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u53ef\u60dc\u554a\uff01 \u3000\u3000\u866c\u9aef\u5ba2\u6ca1\u6709\u4e60\u7ec3\u5230\u9053\u6cd5\uff0c\u770b\u4e0d\u51fa\u8881\u7d2b\u70df\u5934\u9876\u4e09\u5c3a\u7075\u5149\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u3001\u8427\u96e8\u3001\u51cc\u4e91\u3001\u7530\u5f53\u90fd\u9000\u5230\u4e86\u5f88\u8fdc\u7684\u5730\u65b9\uff0c\u8fd9\u4ef6\u4e8b\u4ed6\u4eec\u5df2\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u7ad9\u5f97\u592a\u8fd1 \u4e86\uff0c\u53cd\u800c\u788d\u624b\u788d\u811a\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u4e5f\u53d8\u5f97\u5c0f\u5fc3\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u56de\u76ee\u4e00\u987e\u9f99\u3001\u864e\u4e8c\u5c06\uff0c\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u8bb8\u8ffd\u4e0a\u6765\uff0c\u8fd9\u662f\u6211\u751f\u5e73\u6240\u9047\u7684\u7b2c\u4e00\u5f3a\u654c\uff0c\u4f60\u4eec\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u4e5f\u4e0d\u8bb8\u5e2e\u5fd9\uff0c\u6211\u8d25 \u4e86\uff0c\u4f60\u4eec\u5c31\u56de\u534e\u9634\u53bb\uff0c\u4e00\u5207\u542c\u547d\u4e8e\u7834\u5929\u5251\u9ec4\u4e91\u3002\u201d \u3000\u3000\u9f99\u5c06\u6c5f\u68ee\u9053\uff1a \u3000\u3000\u201c\u5927\u738b\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u866c\u9aef\u5ba2\u559d\u9053\uff1a \u3000\u3000\u201c\u4f4f\u53e3\uff01\u8c01\u4e0d\u542c\u547d\u4ee4\uff0c\u6211\u5c31\u5148\u6740\u4e86\u8c01\uff1f\u67d0\u5bb6\u662f\u4e0d\u5141\u8bb8\u4e00\u4e2a\u80cc\u53db\u6211\u7684\u4eba\uff0c\u6d3b\u5728\u4e16\u4e0a\u3002\u201d \u3000\u3000\u9f99\u5c06\u6c5f\u68ee\u3001\u864e\u5c06\u71d5\u53ef\uff0c\u9f50\u9f50\u8dea\u4e0b\u9053\uff1a \u3000\u3000\u201c\u5c5e\u4e0b\u4e0d\u6562\uff0c\u4e00\u5207\u9075\u7167\u5927\u738b\u5429\u5490\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u4ef0\u5929\u957f\u53f9\uff0c\u9053\uff1a \u3000\u3000\u201c\u738b\u56fe\u3001\u9738\u4e1a\u5728\u6b64\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u8a00\u6765\u6d41\u9732\u51fa\u82f1\u96c4\u5fc3\u60c5\u7684\u60b2\u6006\u3002 \u3000\u3000\u864e\u5c06\u71d5\u53ef\u9053\uff1b \u3000\u3000\u201c\u5c5e\u4e0b\u6597\u80c6\u76f4\u8a00\uff0c\u4e8c\u7237\u4e0d\u8d70\uff0c\u7edd\u4e0d\u4f1a\u6709\u4eca\u65e5\u4e4b\u5c40\u3002\u201d \u3000\u3000\u201c\u4e03\u7edd\u8bef\u6211\uff0c\u592b\u590d\u4f55\u8a00\u3002\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u8881\u59d1\u5a18\uff0c\u5c0f\u5fc3\u4e86\uff01\u6211\u5c06\u5168\u529b\u51fa\u5251\u4e00\u51fb\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u5c31\u662f\u6211\u4ea1\u4e86\u3002\u4f60\u6709\u60dc\u624d\u4e4b\u610f\uff0c\u4e0d\u5fcd \u6740\u6211\uff0c\u6211\u4e5f\u6709\u601c\u9999\u4e4b\u5fc3\uff0c\u4f55\u5fcd\u4f24\u5bb3\u59d1\u5a18\u3002\u53ef\u662f\u6211\u8239\u884c\u6c5f\u5fc3\uff0c\u9a6c\u4e34\u60ac\u5d16\uff0c\u4e00\u5207\u4e5f\u90fd\u5df2\u7ecf\u6765\u4e0d\u53ca \u4e86\u3002\u201d \u3000\u3000\u201c\u6765\u5f97\u53ca\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4f60\u73b0\u5728\u53ef\u4ee5\u8d70\uff0c\u4ece\u6b64\u606f\u9690\u5c71\u6797\u2026\u2026\u201d \u3000\u3000\u201c\u8881\u59d1\u5a18\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u67d0\u5bb6\u6700\u4e0d\u80fd\u5fcd\u53d7\u7684\u5c31\u662f\u5931\u8d25\uff0c\u6211\u7d2f\u79ef\u4e86\u5982\u5c71\u7684\u91d1\u94f6\uff0c\u6697\u85cf\u4e86\u5341\u4e07\u7532\u5175\uff0c\u5f53\u65f6\u674e\u4e16\u6c11\u8fd8 \u672a\u5d1b\u8d77\uff0c\u67d0\u5bb6\u6240\u4ee5\u4e0d\u6562\u5984\u52a8\uff0c\u662f\u5927\u968b\u671d\u8fd8\u6709\u51e0\u5458\u80fd\u5f81\u8d2f\u6218\u7684\u5927\u5c06\uff0c\u6211\u5fc3\u5b58\u987e\u5fcc\uff0c\u6240\u4ee5\u4e0d\u6562\u8d77 \u4e8b\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u8001\u57ce\u8c0b\u56fd\uff0c\u963b\u788d\u4e86\u968b\u7080\u5e1d\u6768\u5e7f\u7684\u80e1\u4f5c\u975e\u4e3a\uff0c\u6768\u5e7f\u4e0d\u80fd\u5fcd\u53d7\uff0c\u628a\u4ed6\u4eec\u5168\u6740\u4e86\uff0c\u89c1\u673a \u7684\u4e5f\u544a\u9000\u5f52\u9690\u6797\u6cc9\uff0c\u80fd\u5c06\u5c3d\u53bb\uff0c\u8001\u6210\u51cb\u8c22\u3002 \u3000\u3000\u6211\u60f3\u8d77\u4e8b\u4e86\uff0c\u6ca1\u60f3\u5230\u53c8\u51fa\u73b0\u4f60\u8fd9\u4e48\u4e00\u4f4d\u672f\u6cd5\u7cbe\u6e5b\u7684\u4eba\u7269\u3002\u6211\u60f3\u4e0d\u901a\u7684\u662f\u4f60\u4e3a\u4ec0\u4e48\u8981\u8f85\u4f50 \u968b\u7080\u5e1d\uff1f\u4ed6\u6bd2\u7236\u7be1\u4f4d\uff0c\u9738\u5ac2\u6740\u5144\u3002\u8fd9\u4e2a\u4eba\u597d\u8272\u65e0\u5fb7\uff0c\u4e00\u65e0\u53ef\u53d6\uff0c\u4f60\u5374\u7518\u4e3a\u6240\u7528\uff0c\u7a76\u7adf\u662f\u4e3a\u4ec0 \u4e48\u5462\uff1f\u201d \u3000\u3000\u8881\u7d2b\u70df\u5446\u4e86\u4e00\u5446\uff0c\u53f9\u9053\uff1a \u3000\u3000\u201c\u4e8b\u5982\u6d41\u6c34\uff0c\u56de\u5934\u96be\uff1b\u5df2\u7ecf\u6210\u4e86\u76ee\u4e0b\u8fd9\u4e2a\u5c40\u9762\uff0c\u8c08\u4e4b\u4f55\u76ca\uff1f \u3000\u3000\u5c31\u7b97\u5927\u968b\u671d\u57fa\u4e1a\u5df2\u5c3d\uff0c\u4e5f\u5e94\u4e0d\u5230\u4f60\u7684\u8eab\u4e0a\u3002\u56de\u53bb\u5427\uff01\u4e24\u5e74\u4e4b\u540e\uff0c\u6211\u4e5f\u8981\u91cd\u56de\u6df1\u5c71\u5927\u6cfd\uff0c \u9762\u58c1\u601d\u8fc7\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u54fc\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6211\u5c31\u4e0d\u670d\u6c14\u4e86\uff0c\u8fd9\u738b\u4f4d\u9738\u4e1a\u53c8\u8be5\u5e94\u5728\u4f55\u4eba\u8eab\u4e0a\u5462\uff1f\u738b\u4e16\u5145\u7ec3\u4e86\u4e00\u6279\u5927\u519b\uff0c\u674e\u4e16\u6c11\u53ea \u4e0d\u8fc7\u591a\u4e86\u51e0\u4e2a\u95e8\u5ba2\uff0c\u53ea\u8981\u4f60\u8881\u59d1\u5a18\u653e\u624b\u4e0d\u7ba1\uff0c\u8fd9\u4e07\u91cc\u6c5f\u5c71\u5c31\u975e\u6211\u83ab\u5c5e\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u4f60\u95ef\u4e0d\u8fc7\u6211\u8fd9\u4e00\u5173\uff0c\u5929\u4e0b\u5927\u4e8b\u53c8\u4ece\u4f55\u8c08\u8d77\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u903c\u6211\u5168\u529b\u4e00\u62fc\u4e86\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u7f13\u7f13\u4e3e\u8d77\u4e86\u624b\u4e2d\u94c1\u5251\u3002 \u3000\u3000\u90a3\u662f\u4e00\u628a\u6beb\u4e0d\u8d77\u773c\u7684\u5251\uff0c\u4e0d\u89c1\u5149\u4eae\uff0c\u4e0d\u89c1\u950b\u8292\uff0c\u53ea\u662f\u91cd\u91cf\u5927\u4e00\u4e9b\u3002 \u3000\u3000\u4f46\u866c\u9aef\u5ba2\u4e00\u6325\u624b\uff0c\u5374\u5e26\u8d77\u4e86\u5f3a\u70c8\u7684\u5251\u6c14\uff0c\u4eba\u968f\u5251\u4e00\u8d77\u98de\u8d77\u6765\u3002 \u3000\u3000\u9a6d\u5251\u672f\uff0c\u679c\u7136\u662f\u77f3\u7834\u5929\u60ca\u7684\u4e00\u51fb\u3002 \u3000\u3000\u4e00\u4e08\u591a\u7684\u8ddd\u79bb\u7728\u773c\u5c31\u5230\uff0c\u94c1\u5251\u51fb\u4e2d\u4e86\u8881\u7d2b\u70df\u3002\u4ed6\u4e00\u5251\u53ef\u4ee5\u628a\u4e00\u5757\u5de8\u5927\u7684\u5c71\u77f3\u51fb\u6210\u788e\u7c89\uff0c \u4f46\u5374\u6ca1\u51fb\u4f24\u8881\u7d2b\u70df\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7684\u56db\u5468\uff0c\u597d\u50cf\u6709\u7740\u4e00\u5835\u65e0\u5f62\u4e4b\u5899\uff0e\u67d4\u800c\u575a\u6df1\uff0c\u65e0\u6cd5\u903e\u8d8a\uff1b\u4ee5\u866c\u9aef\u5ba2\u7684\u94c1\u5251\u4e5f\u65e0\u6cd5 \u8d76\u5f97\u52a8\u5b83\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u843d\u7740\u5c4b\u9762\uff0c\u8fde\u5288\u4e09\u5251\u3002 \u3000\u3000\u4f46\u5251\u5230\u7ae5\u7d2b\u70df\u8eab\u524d\u4e09\u5bf8\u5904\uff0c\u5c31\u65e0\u6cd5\u518d\u8fdb\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u51b7\u51b7\u5730\u9053\uff1a \u3000\u3000\u201c\u5f20\u4ef2\u575a\uff0c\u6211\u5df2\u8ba9\u4f60\u56db\u5251\uff0c\u7adf\u8fd8\u4e0d\u77e5\u8fdb\u9000\uff0c\u4f11\u602a\u6211\u8981\u8fd8\u51fb\u4e86\u3002\u201d \u3000\u3000\u5408\u5341\u7684\u53cc\u624b\u7a81\u7136\u5411\u5916\u63a8\u51fa\uff0c\u8fce\u7740\u866c\u9aef\u5ba2\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u7ea2\u5149\u95ea\u52a8\uff0c\u54cd\u8d77\u5de8\u5927\u7684\u9739\u96f3\uff01 \u3000\u3000\u866c\u9aef\u5ba2\u7adf\u7136\u88ab\u51fb\u5012\u5728\u74e6\u9762\u4e0a\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e00\u63a2\u624b\uff0c\u7acb\u523b\u591a\u4e86\u4e00\u628a\u950b\u5229\u7684\u5c0f\u5200\uff0c\u51c6\u5907\u523a\u4e0b\u2026\u2026 \u3000\u3000\u201c\u8881\u59d1\u5a18\uff0c\u624b\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u4e00\u9635\u75be\u98ce\u5377\u8fc7\uff0c\u866c\u9aef\u5ba2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u674e\u9756\u5374\u7a81\u7136\u51fa\u73b0\u4e8e\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u674e\u9756\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u4e49\u7ed3\u91d1\u5170\uff0c\u674e\u67d0\u4eba\u4e0d\u80fd\u4e0d\u6551\uff0c\u8fd8\u8bf7\u59d1\u5a18\u5927\u5ea6\u5305\u5bb9\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u836f\u5e08\u51fa\u9762\uff0c\u592b\u590d\u4f55\u8a00\uff1f\u8bf7\u5e26\u8d70\u866c\u9aef\u5ba2\u5427\uff01\u89c1\u7740\u674e\u4e16\u6c11\u65f6\uff0c\u4ee3\u6211\u95ee\u5019\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u5927\u91cf\uff0c\u674e\u9756\u611f\u6fc0\u5f97\u5f88\uff0c\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u674e\u9756\u5df2\u628a\u866c\u9aef\u5ba2\u4ea4\u5230\u4e86\u9f99\u5c06\u6c5f\u68ee\u7684\u624b\u4e2d\u3002 \u3000\u3000\u864e\u5c06\u71d5\u53ef\u62a4\u7740\u6c5f\u68ee\uff0e\u5148\u884c\u8d70\u4e86\u3002 \u3000\u3000\u201c\u674e\u9756\uff0c\u8ba4\u8bc6\u6211\u5417\uff1f\u201d \u3000\u3000\u674e\u9756\u56de\u5934\uff0c\u4e00\u4e2a\u7edd\u8272\u5973\u5b50\u7ad9\u5728\u9762\u524d\u3002 \u3000\u3000\u201c\u8881\u5b9d\u513f\u3002\u201d \u3000\u3000\u674e\u9756\u4f5c\u4e86\u4e00\u4e2a\u957f\u63d6\u3002 \u3000\u3000\u201c\u6211\u4eec\u6ca1\u6709\u89c1\u8fc7\u9762\u554a\uff01\u4f60\u600e\u4e48\u4f1a\u8ba4\u8bc6\u8881\u5b9d\u513f\uff1f\u201d \u3000\u3000\u201c\u770b\u4f60\u7684\u5bb9\u8272\uff0c\u5df2\u7ecf\u591f\u4e86\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u9664\u4e86\u8881\u5b9d\u513f\uff0c\u4eba\u95f4\u54ea\u6709\u5982\u6b64\u7edd\u8272\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u5982\u7d2b\u70df\u59d0\u59d0\u2026\u2026\u201d \u3000\u3000\u201c\u4e24\u4f4d\u662f\u6625\u5170\u79cb\u83ca\uff0c\u5404\u6781\u5176\u7f8e\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u5927\u968b\u671d\u7684\u7075\u6c14\u3001\u98ce\u534e\u5168\u96c6\u4e2d\u4e24\u4f4d\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u542c\u8bf4\u51fa\u5c18\u59d0\u4e5f\u662f\u4eba\u95f4\u7edd\u8272\uff0e\u4e3a\u4f55\u4eca\u591c\u672a\u6765\uff1f\u7f18\u60ad\u4e00\u9762\uff0c\u53ef\u80fd\u4f7f\u5b9d\u513f\u7ec8\u8eab\u9057\u61be\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u6211\u4eec\u592b\u5987\u5df2\u6295\u5728\u4e8c\u516c\u5b50\u4e16\u6c11\u9ebe\u4e0b\uff0c\u59d1\u5a18\u56de\u592a\u539f\u4e4b\u540e\uff0c\u4e0d\u5c31\u53ef\u4ee5\u671d\u5915\u76f8\u5904 \u4e86\u3002\u201d \u3000\u3000\u201c\u56de\u5927\u539f\uff1f\u201d \u3000\u3000\u8881\u5b9d\u513f\u8038\u8038\u80a9\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e86\u6de1\u6de1\u7684\u7f9e\u7ea2\uff0c\u4e00\u7247\u795e\u9a70\u6545\u56ed\u7684\u5411\u5f80\u4e4b\u60c5\uff0c\u6708\u5149\u4e0b\u5a07\u75f4\u6a2a\u751f\uff0c \u52a8\u4eba\u601c\u60dc\u3002 \u3000\u3000\u53ea\u662f\u795e\u60c5\u4e2d\u6709\u7740\u4e00\u79cd\u5e7d\u4f24\u3001\u6006\u51c9\uff0c\u770b\u4e0a\u53bb\u53c8\u589e\u591a\u4e00\u79cd\u51c4\u8ff7\u4e4b\u7f8e\u3002 \u3000\u3000\u4ee5\u674e\u9756\u7684\u5b9a\u529b\u3001\u4e5f\u770b\u5f97\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u8881\u5b9d\u513f\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8eab\u5728\u6df1\u5bab\u9662\uff0c\u5fc3\u7cfb\u6545\u4e61\u60c5\uff0c\u89c1\u7740\u4e8c\u516c\u5b50\u65f6\uff0c\u4ee3\u6211\u8bf7\u5b89\u95ee\u597d\uff0c\u4eca\u751f\u65e0\u7f18\uff0c\u671f\u6709\u6765\u751f\u518d\u7eed \u4e86\u3002\u5509\uff01\u65e0\u53ef\u5948\u4f55\u82b1\u843d\u53bb\uff0c\u557c\u8840\u675c\u9e43\u5524\u4e0d\u56de\u3002\u201d \u3000\u3000\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\uff0c\u8e0f\u7740\u6708\u8272\u800c\u53bb\u3002 \u3000\u3000\u5979\u8d70\u5f97\u5f88\u6162\uff0c\u80cc\u5f71\u4e2d\u6d41\u9732\u51fa\u65e0\u9650\u7684\u51c4\u51c9\u3002 \u3000\u3000\u674e\u9756\u51e0\u6b21\u60f3\u558a\u5979\u56de\u6765\uff0c\u5374\u90fd\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u201c\u557c\u8840\u675c\u9e43\u5524\u4e0d\u56de\uff01\u201d \u3000\u3000\u597d\u60b2\u51c4\u7684\u5fc3\u58f0\uff01 \u3000\u3000\u597d\u82cd\u51c9\u7684\u611f\u6168\uff01 \u3000\u3000\u674e\u9756\u6ca1\u6709\u52c7\u6c14\u7834\u574f\u8fd9\u4efd\u51c4\u8ff7\u4e4b\u7f8e\uff0c\u7f8e\u5f97\u6c38\u4e16\u96be\u5fd8\uff0c\u7f8e\u5f97\u4eba\u5fc3\u90fd\u51c9\u4e86\u3002 \u3000\u3000\u674e\u9756\u611f\u89c9\u5230\u773c\u4e2d\u6709\u70b9\u6e7f\u6da6\uff0c\u4e3e\u624b\u62c2\u53d1\uff0c\u501f\u6545\u62ed\u53bb\u6cea\u6c34\u3002 \u3000\u3000\u5077\u773c\u671b\u53bb\u2014\u2014 \u3000\u3000\u53ea\u89c1\u8881\u7d2b\u70df\u75f4\u75f4\u5730\u671b\u7740\u8881\u5b9d\u513f\u7684\u53bb\u5411\uff0c\u4efb\u4ee4\u6e05\u6cea\u6eda\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4f4d\u5929\u4e0a\u4ed9\u5b50\uff0c\u98df\u591a\u4e86\u4eba\u95f4\u70df\u706b\uff0c\u7adf\u7136\u4e5f\u6709\u4e86\u89e6\u666f\u4f24\u611f\u7684\u60c5\u6000\u3002 \u3000\u3000\u201c\u674e\u9756\uff0c\u4f60\u8d70\u5427\uff01\u5b9d\u513f\u5fc3\u5df2\u788e\uff0c\u6cea\u5df2\u5c3d\uff0c\u56de\u592a\u539f\u4e5f\u65e0\u6cd5\u6d3b\u5f97\u4e0b\u53bb\u3002\u201d\u8881\u7d2b\u70df\u9053\uff1a\u201c\u8ba9\u5979 \u7559\u4e0b\u5427\uff01\u201d \u3000\u3000\u674e\u9756\u606d\u606d\u656c\u656c\u5730\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a \u3000\u3000\u201c\u8881\u59d1\u5a18\uff0e\u4e00\u8c22\u59d1\u5a18\u624b\u4e0b\u7559\u60c5\uff0c\u7559\u4ef2\u575a\u5927\u54e5\u6027\u547d\uff0c\u4e5f\u662f\u7ed9\u674e\u9756\u9762\u5b50\u3002\u8fd9\u4efd\u60c5\u610f\uff0c\u6211\u4f1a\u6c38 \u8bb0\u5fc3\u4e2d\u3002\u4e8c\u8bf7\u59d1\u5a18\u6148\u60b2\uff0c\u6551\u6551\u5b9d\u513f\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53d6\u51fa\u7ee2\u5e15\uff0c\u62ed\u7740\u8138\u4e0a\u7684\u6c68\u75d5\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u60c5\u540c\u59d0\u59b9\uff0c\u6211\u4f1a\u5c3d\u529b\u800c\u4e3a\uff0c\u7687\u4e0a\u5bf9\u5b9d\u513f\u5bc4\u60c5\u4e4b\u6df1\uff0c\u91cd\u8fc7\u4ed6\u7684\u9526\u7ee3\u5c71\u6cb3\uff0c\u6ca1\u6709\u8881\u5b9d \u513f\uff0c\u7687\u4e0a\u53ef\u80fd\u4f1a\u632f\u4f5c\u3002\u5b9d\u513f\u5bb3\u4e86\u4ed6\uff0c\u4ed6\u4e5f\u5bb3\u4e86\u5b9d\u513f\u3002\u5929\u554a\uff01\u4eba\u95f4\u7684\u60c5\u7231\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u82e6 \u6da9\uff01\u201d \u3000\u3000\u674e\u9756\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u674e\u4e16\u6c11\u4e3a\u5b9d\u513f\u5455\u8840\u6570\u6597\uff0c\u51e0\u4e4e\u5815\u843d\u4e86\u6551\u4e16\u7684\u5fd7\u613f\u3002\u201d \u3000\u3000\u201c\u674e\u9756\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u6211\u4e5f\u6709\u4e00\u4e8b\u8bf7\u6258\uff0c\u8981\u674e\u4e16\u6c11\u665a\u4e09\u5e74\u518d\u51fa\u5175\u957f\u5b89\u3002\u5929\u8c61\u5df2\u6210\uff0c\u4eba\u529b\u5df2\u96be\u633d\u56de\u3002\u63d0\u524d\u51fa \u5175\uff0c\u6709\u5bb3\u65e0\u76ca\uff0c\u4f55\u4e0d\u987a\u5e94\u5929\u673a\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u8881\u59d1\u5a18\u8bf7\u653e\u5fc3\uff0c\u674e\u9756\u4fdd\u8bc1\u4e09\u5e74\u5185\u7ec3\u5175\u4e0d\u52a8\u5175\uff0c\u4e00\u65e6\u5927\u519b\u51fa\u52a8\uff0c\u6211\u8981\u5728\u4e94\u5e74\u626b\u5e73\u5404 \u5730\u9738\u4e3b\u3001\u72fc\u70df\uff0c\u7edf\u4e00\u5929\u4e0b\uff0c\u4f7f\u6c11\u95f4\u5c11\u53d7\u4e9b\u6218\u4e71\u4e4b\u82e6\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u53c8\u9053\uff1a \u3000\u3000\u866c\u9aef\u5ba2\u7684\u4e8b\uff0c\u4e5f\u8bf7\u4f60\u5904\u7f6e\u4e86\u3002\u8fd9\u5173\u7cfb\u7740\u674e\u4e16\u6c11\u7684\u5927\u4e1a\u6210\u8d25\uff0c\u4e5f\u4e0d\u5bb9\u8bb8\u4f60\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4ef2\u575a\u5927\u54e5\u81ea\u8ba4\u5929\u4e0b\u65e0\u654c\uff0c\u53d7\u6b64\u632b\u6298\uff0c\u4e5f\u65e0\u989c\u9010\u9e7f\u4e2d\u539f\u4e86\u3002 \u3000\u3000\u59d1\u5a18\u4fdd\u91cd\uff0c\u674e\u9756\u544a\u8f9e\uff01\u201d \u3000\u3000\u674e\u9756\u62b1\u62f3\u4f5c\u793c\u3002 \u3000\u3000\u8881\u7d2b\u70df\u7adf\u7136\u4e5f\u76c8\u76c8\u4f5c\u6001\uff0c\u8fd8\u4e86\u4e00\u793c\u3002 \u3000\u3000\u8fd9\u53ef\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\u554a\uff01 \u3000\u3000\u5979\u81ea\u5165\u7687\u5bab\uff0c\u5c31\u5f88\u8ba8\u538c\u8fd9\u4e9b\u7e41\u6587\u8925\u8282\u3002\u793c\u8c8c\u7684\u8868\u8fbe\uff0c\u90fd\u662f\u5f88\u76f4\u63a5\u65b9\u5f0f\uff0c\u6325\u6325\u624b\uff0c\u62b1\u62b1 \u62f3\u3002 \u3000\u3000\u5979\u81ea\u4efb\u56fd\u5e08\u4e4b\u540e\uff0c\u89c1\u7687\u4e0a\u4e5f\u61d2\u5f97\u884c\u5927\u793c\u4e86\uff0c\u5c31\u66f4\u522b\u8bf4\u8981\u5979\u5b66\u4e60\u5987\u9053\u4eba\u5bb6\uff0c\u53cc\u624b\u629a\u7740\u67f3\u8170 \u5b66\u4e07\u798f\u4e86\u3002 \u3000\u3000\u4f46\u5979\u4f5c\u8d77\u6765\uff0c\u8fd8\u771f\u7684\u597d\u770b\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u9192\u8fc7\u6765\uff0c\u970d\u7136\u5750\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u9f99\u5c06\u6c5f\u68ee\u3001\u864e\u5c06\u71d5\u53ef\u8083\u7acb\u69bb\u524d\u3002 \u3000\u3000\u770b\u5230\u866c\u9aef\u5ba2\u4e00\u4e0b\u5b50\u5750\u4e86\u8d77\u6765\uff0c\u4e8c\u4eba\u559c\u9053\uff1a \u3000\u3000\u201c\u5927\u738b\u9192\u6765\u4e86\uff0c\u5927\u738b\u9192\u6765\u4e86\uff01\u5927\u738b\u7ec8\u4e8e\u9192\u8fc7\u6765\u4e86\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u624b\u6367\u7740\u767d\u74f7\u7897\uff0c\u7531\u95e8\u5916\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5979\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff0c\u8fd9\u662f\u4f60\u6700\u559c\u6b22\u7684\u51b0\u7cd6\u71d5\u7a9d\u7ca5\uff0c\u5148\u8fdb\u7528\u4e00\u70b9\uff0c\u6211\u4e00\u76f4\u628a\u5b83\u653e\u5728\u6e29\u706b\u4e0a\uff0c\u4e0d\u70eb\u4e5f\u4e0d \u51c9\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u6ca1\u6709\u4f38\u624b\u53bb\u63a5\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u53ea\u597d\u7528\u7389\u5319\u5582\u4ed6\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u6447\u6447\u5934\uff0c\u5927\u5634\u5c31\u5728\u74f7\u7897\u53e3\u4e00\u53e3\u6c14\u5c06\u4e00\u7897\u71d5\u7a9d\u7ca5\u5168\u5438\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u6c5f\u5c71\u4e0d\u6539\u65e7\u989c\u8272\uff0c\u8eab\u4e0a\u6709\u4f24\u554a\uff01\u8fd8\u90a3\u4e48\u72fc\u541e\u864e\u54bd\u7684\u3002\u201d \u3000\u3000\u201c\u672c\u6027\u96be\u79fb\u5440\uff01\u201d\u866c\u9aef\u5ba2\u7b11\u9053\uff1a \u3000\u3000\u201c\u8981\u6211\u5403\u996d\u50cf\u7ee3\u82b1\uff0c\u5c31\u4e0d\u5982\u997f\u6b7b\u7b97\u4e86\u3002\u4e09\u59b9\uff0c\u6211\u6655\u8ff7\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u8fd9\u662f\u7b2c\u4e94\u5929\uff0c\u4e09\u5929\u524d\u4f60\u8fd8\u6655\u8ff7\u4e0d\u9192\uff0c\u6211\u4eec\u6709\u4e9b\u6025\u4e86\uff0c\u674e\u9756\u53bb\u627e\u8881\u7d2b\u70df\u2026\u2026\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u4e09\u59b9\uff0c\u4e0d\u80fd\u6c42\u5979\uff0c\u4e3a\u5144\u53ef\u4ee5\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u5411\u90a3\u4e2a\u5996\u5973\u793a\u5f31\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u6c42\u5979\uff0c\u662f\u95ee\u5979\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u5979\u8bf4\u5927\u54e5\u662f\u8d85\u4eba\u4f53\u8d28\uff0c\u4e03\u5929\u5185\u4e00\u5b9a\u53ef\u4ee5\u9192\u8fc7\u6765\uff0c\u800c\u4e14\u9192\u4e86\u4e5f\u5c31\u75ca\u6108\u4e86\uff0c\u4e0d\u4f1a\u6709\u4e8b\uff0c\u5979 \u8fd8\u7ed9\u4e86\u4e00\u4f4d\u4e39\u4e38\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba9\u6211\u670d\u4e0b\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u7684\u5f88\u9eef\u7136\u3002 \u3000\u3000\u201c\u6ca1\u6709\u554a\uff01\u201d\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u4e39\u4e38\u8fd8\u5728\u6211\u7684\u8863\u888b\u4e2d\uff0c\u674e\u9756\u77e5\u9053\u5927\u54e5\u7684\u813e\u6c14\uff0c\u4e0d\u613f\u53d7\u4eba\u70b9\u6ef4\u6069\u60e0\uff0c\u6240\u4ee5\u4e0d\u6562\u7ed9\u4f60\u5403 \u554a\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u53f9\u9053\uff1a \u3000\u3000\u201c\u5509\uff01\u8fd8\u662f\u4e8c\u5f1f\u77e5\u6211\uff0c\u53ea\u53ef\u60dc\u6211\u53d7\u4e03\u7edd\u9053\u4eba\u4e4b\u611a\uff0c\u628a\u4e8c\u5f1f\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u4f1a\u8bb0\u5728\u5fc3\u4e0a\u7684\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4ed6\u53ea\u662f\u6015\u60f9\u4f60\u751f\u6c14\uff0c\u624d\u8eb2\u8d77\u6765\u4e0d\u6562\u89c1\u4f60\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u795e\u60c5\u5fae\u73b0\u6fc0\u52a8\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e8c\u5f1f\u73b0\u5728\u4f55\u5904\uff0c\u5feb\u8bf7\u4ed6\u8fc7\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u674e\u9756\u4e3e\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u64a9\u8863\uff0c\u5355\u819d\u8dea\u5728\u69bb\u524d\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff01\u5c0f\u5f1f\u5c31\u5728\u95e8\u5916\u606d\u5019\uff0c\u672a\u5f97\u5927\u54e5\u53ec\u5524\uff0c\u4e0d\u6562\u8d38\u7136 \u8fdb\u89c1\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u6293\u4f4f\u4e86\u674e\u9756\u4e00\u53ea\u624b\uff0c\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff01\u662f\u4f5c\u5927\u54e5\u7684\u5bf9\u4e0d\u8d77\u4f60\uff0c\u8fd8\u8981\u6c42\u4f60\u591a\u591a\u539f \u8c05\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u5fc3\u4e2d\u7edd\u65e0\u4ecb\u8482\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u5bf9\u5927\u54e5\u7684\u8446\u91cd\u4e5f\u65e0\u4e1d\u6beb\u6539\u53d8\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u8bef\u542c\u4e03\u7edd\u9053\u4eba\u4e4b\u8a00\uff0c\u81f4\u6709\u4eca\u65e5\u7684\u60e8\u8d25\uff0c\u5982\u7167\u4e8c\u5f1f\u7684\u5b89\u6392\uff0c\u54ea\u4f1a\u6709\u4eca\u65e5\u4e4b\u5931\uff1f\u201d \u3000\u3000\u674e\u9756\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff0c\u5929\u610f\u5df2\u5b9a\uff0c\u5c31\u7b97\u5c0f\u5f1f\u548c\u5927\u54e5\u8054\u624b\uff0c\u53ef\u80fd\u6709\u4e00\u65f6\u5174\u76db\uff0c\u4f46\u7edd\u96be\u5f97\u6301\u4e45\uff0c\u4f55\u51b5\u8881\u7d2b \u70df\u8fd9\u4e00\u5173\u4e5f\u5f88\u96be\u95ef\u8fc7\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u2026\u2026\u201d\u866c\u9aef\u5ba2\u9eef\u7136\u4e00\u53f9\uff0c\u9053\uff1a \u3000\u3000\u201c\u8d25\u519b\u4e4b\u5c06\uff0c\u4f55\u8db3\u8a00\u52c7\uff1f\u4e8c\u5341\u5e74\u7684\u51c6\u5907\u5e9f\u4e8e\u4e00\u65e6\uff0c\u597d\u68a6\u8fde\u5e8a\uff0c\u5374\u5728\u5173\u952e\u65f6\u523b\uff0c\u5c3d\u6210\u7a7a \u5e7b\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u5979\u5f88\u60f3\u529d\u8bf4\u51e0\u53e5\uff0c\u4f46\u662f\u89c1\u674e\u9756\u8083\u7136\u800c\u7acb\uff0c\u4e14\u4e0d\u80af\u63a5\u53e3\uff0c\u56e0\u800c\u4e5f\u5c31\u4e0d\u6562 \u591a\u8bf4\u4e86\u3002 \u3000\u3000\u5979\u5bf9\u592b\u5a7f\u7684\u624d\u6167\u65e9\u5df2\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u60f3\u5230\u674e\u9756\u4e0d\u5f00\u53e3\uff0c\u5fc5\u6709\u6df1\u610f\uff0c\u81ea\u5df1\u63d2\u53e3\u591a\u8a00\uff0c\u5f88 \u53ef\u80fd\u4f1a\u628a\u4e8b\u60c5\u7834\u574f\u4e86\u3002 \u3000\u3000\u679c\u7136\uff0c\u674e\u9756\u5f85\u866c\u9aef\u5ba2\u4e00\u9635\u5401\u5618\u8fc7\u540e\uff0c\u624d\u63a5\u53e3\u9053\uff1a \u3000\u3000\u201c\u5929\u5730\u8fbd\u9614\uff0c\u9664\u4e86\u4e2d\u539f\u4e00\u7247\u9526\u7ee3\u5bb6\u90a6\u4e4b\u5916\uff0c\u8fd8\u6709\u65e0\u6570\u7684\u58ee\u4e3d\u5c71\u6cb3\uff0c\u7686\u53ef\u4f9b\u5927\u54e5\u7eb5\u6a2a\u9a70 \u9a8b\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u773c\u775b\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e58\u8239\u5357\u884c\uff0c\u5927\u6d77\u4e4b\u6ee8\uff0c\u522b\u6709\u5929\u5730\uff0c\u5343\u5c9b\u4e07\u5d74\uff0c\u51fa\u4ea7\u4e30\u9976\uff0c\u4e5f\u6709\u5f88\u591a\u7537\u5973\u751f\u6d3b \u5728\u90a3\u91cc\u5462\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u9756\u5df2\u77e5\u9053\u4ed6\u4e0d\u662f\u5f88\u559c\u6b22\u90a3\u4e9b\u5730\u65b9\uff0c\u5927\u6d77\u832b\u832b\uff0c\u98ce\u6d6a\u96be\u6d4b\uff0c\u6ca1\u6709\u77e5\u6d77\u7684\u628a\u63e1\uff0c\u5c31\u4e0d\u613f\u592a \u5192\u9669\u4e86\u3002 \u3000\u3000\u674e\u9756\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u897f\u884c\u6c99\u6f20\u4e4b\u5916\uff0c\u6709\u4e00\u7247\u8fbd\u9614\u5929\u5730\uff0c\u8349\u539f\u4e30\u76db\uff0c\u4eba\u53e3\u4f17\u591a\uff0c\u725b\u7f8a\u65e0\u6570\uff0c\u65c1\u4f9d\u6606\u4ed1\u5c71\uff0c\u6c34 \u6e90\u4ea6\u5f88\u5145\u6c9b\u2026\u2026\u201d \u3000\u3000\u866c\u9aef\u5ba2\u63a5\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u597d\uff01\u5927\u54e5\u5c31\u7a7f\u8d8a\u6c99\u6f20\uff0c\u627e\u4e00\u5904\u4eba\u95f4\u7684\u65b0\u5929\u5730\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u63a0\u9f99\u3001\u864e\u4e8c\u5c06\uff0c\u9053\uff1a \u3000\u3000\u201c\u6c5f\u68ee\uff01\u201d \u3000\u3000\u9f99\u5c06\u6c5f\u68ee\u4e00\u8eac\u8eab\uff0c\u9053\uff1a \u3000\u3000\u201c\u5927\u738b\u5429\u5490\uff01\u201d \u3000\u3000\u201c\u901f\u5f80\u534e\u9634\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u8981\u7834\u5929\u5251\u9ec4\u4e91\u4e3a\u7edf\u5e05\uff0c\u628a\u4e09\u4e07\u5927\u519b\u88c1\u51cf\u6210\u4e09\u5343\u4eba\uff0c\u4f46\u8fd9\u4e09\u5343\u4eba\u4e00\u5b9a\u8981\u5e74\u7eaa\u8f7b\u3001\u6b66\u52c7\u8fc7 \u4eba\u3002\u9063\u8d70\u7684\u4eba\uff0c\u6bcf\u4eba\u81f4\u94f6\u4e00\u767e\u4e24\uff0c\u8ba9\u4ed6\u4eec\u56de\u5bb6\u4e61\u540e\u80fd\u5b89\u5c45\u4e50\u4e1a\u3002\u901a\u77e5\u949f\u6728\u9b41\uff0c\u8bf7\u4ed6\u968f\u9ec4\u4e91\u884c \u52a8\uff0c\u5e26\u4e0a\u8425\u5e10\u3001\u7ec6\u8f6f\u3001\u7cae\u8349\u3001\u91d1\u94f6\uff0c\u4e00\u8def\u897f\u884c\uff0c\u5728\u5609\u88d5\u5173\u5916\u624e\u8425\u7b49\u6211\u3002\u201d \u3000\u3000\u201c\u6c5f\u68ee\u9886\u547d\uff01\u201d \u3000\u3000\u4e00\u4e2a\u957f\u63d6\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u70b9\u70b9\u5934\uff0c\u53c8\u9053\uff1a \u3000\u3000\u201c\u71d5\u53ef\uff01\u201d \u3000\u3000\u201c\u672b\u5c06\u5728\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u53bb\u901a\u77e5\u4f60\u4eec\u7387\u9886\u7684\u4e09\u5341\u516d\u540d\u9f99\u864e\u6b66\u58eb\uff0c\u5728\u57ce\u897f\u4e94\u5341\u91cc\u76d8\u7f8a\u53e3\u7b49\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u5927\u738b\uff01\u201d\u71d5\u53ef\u9053\uff1a \u3000\u3000\u201c\u957f\u5b89\u57ce\u4e2d\u8fd8\u6709\u4e00\u4e07\u540d\u5206\u5e03\u5728\u5404\u884c\u4e1a\u4e2d\u7684\u7cbe\u9510\uff0c\u4e0d\u901a\u77e5\u4ed6\u4eec\u4e00\u58f0\u5417\uff1f\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u897f\u884c\u4e07\u91cc\uff0c\u4f55\u5904\u662f\u5bb6\u56ed\uff1f\u4eba\u4e00\u591a\uff0c\u7cae\u8349\u4f9b\u5e94\u56f0\u96be\u3002\u6240\u4ee5\uff0c\u6211\u8981\u9ec4\u4e91\u7531\u4e09\u4e07\u4eba\u4e2d\u9009\u51fa\u4e09 \u5343\u4eba\u3002 \u3000\u3000\u957f\u5b89\u57ce\u4e2d\u7684\u4f0f\u5175\u65e9\u5df2\u4e60\u60ef\u4e8e\u4ed6\u4eec\u8fc7\u4e86\u4e94\u516d\u5e74\u751f\u6d3b\uff0c\u5927\u90e8\u5206\u5a36\u59bb\u751f\u5b50\u4e86\u3002\u4f55\u51b5\uff0c\u5175\u5728\u7cbe\uff0c \u800c\u4e0d\u5728\u591a\uff0c\u4e0d\u7528\u60ca\u6270\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u738b\u8bf4\u7684\u662f\uff0c\u71d5\u53ef\u8fd9\u5c31\u4f1a\u901a\u77e5\u4ed6\u4eec\uff0c\u660e\u65e5\u52a8\u8eab\u3002\u201d \u3000\u3000\u62b1\u62f3\u957f\u63d6\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u9f99\u3001\u864e\u4e8c\u5c06\u53bb\u8fdc\uff0c\u866c\u9aef\u5ba2\u6380\u88ab\u4e0b\u5e8a\uff0c\u6293\u4e00\u4ef6\u957f\u888d\u62ab\u4e0a\u3002 \u3000\u3000\u4ed6\u4f24\u611f\u5730\u9053\uff1a \u3000\u3000\u201c\u4e8c\u5f1f\u3001\u4e09\u59b9\uff0c\u6211\u4eec\u5f88\u4e45\u6ca1\u6709\u597d\u597d\u8c08\u5fc3\u4e86\uff0c\u4eca\u65e5\u5f00\u6000\u7545\u8a00\uff0c\u5c3d\u5410\u5fc3\u4e2d\u7684\u5757\u5792\u3002\u5c0f\u5144\u897f\u4e0b \u4e4b\u540e\uff0c\u4eca\u751f\u662f\u5426\u8fd8\u6709\u89c1\u9762\u4e4b\u65e5\uff0c\u5c31\u5f88\u96be\u9884\u6599\u4e86\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u6709\u70b9\u6e7f\u6da6\uff0c\u6d8c\u51fa\u4e86\u4e24\u7736\u6cea\u6c34\u3002 \u3000\u3000\u674e\u9756\u4e5f\u5f88\u611f\u4f24\uff0c\u6cea\u6c34\u6d8c\u51fa\u773c\u5916\uff0c\u987a\u816e\u800c\u4e0b\u3002 \u3000\u3000\u628a\u4e00\u4e2a\u4e0d\u53ef\u4e00\u4e16\u7684\u8c6a\u6770\u903c\u5f97\u897f\u884c\u4e07\u91cc\uff0c\u6e21\u8fc7\u5927\u6c99\u6f20\uff0c\u8ffd\u5bfb\u65b0\u5929\u5730\uff0c\u662f\u4e00\u4ef6\u4f55\u7b49\u51c4\u51c9\u7684\u4e8b \u554a\uff01 \u3000\u3000\u4f55\u51b5\uff0c\u8fd9\u4e2a\u4eba\u53c8\u662f\u4ed6\u4e49\u7ed3\u91d1\u5170\u7684\u5927\u54e5\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u65e9\u5df2\u7ecf\u6cea\u5982\u6eda\u73e0\uff0c\u54ed\u5f97\u545c\u54bd\u51fa\u58f0\u4e86\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u866c\u9aef\u5ba2\u662f\u8d25\u5728\u4e86\u8881\u7d2b\u70df\u7684\u624b\u4e0b\u3002 \u3000\u3000\u4f46\u903c\u4ed6\u653e\u5f03\u9010\u9e7f\u4e2d\u539f\u9738\u4e1a\u7684\uff0c\u5374\u662f\u4ed6\u674e\u9756\u3002\u4e0d\u7ba1\u63aa\u8bcd\u5982\u4f55\u7684\u59d4\u5a49\uff0c\u6001\u5ea6\u5982\u4f55\u7684\u8bda\u6073\uff0c\u4f46 \u8fd8\u662f\u628a\u4eba\u64b5\u8d70\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u674e\u9756\u5fc3\u4e2d\u9664\u4e86\u522b\u79bb\u7684\u4f24\u611f\u4e4b\u5916\uff0c\u8fd8\u591a\u4e86\u4e00\u4efd\u6127\u759a\u3002 \u3000\u3000\u5367\u5ba4\u5916\u9762\u662f\u4e00\u4e2a\u7cbe\u5de7\u7684\u5ba2\u5385\uff0c\u4e24\u4e2a\u5973\u5a62\u65e9\u5df2\u7ad9\u5728\u5385\u4e2d\uff0c\u89c1\u4e09\u4eba\u6b65\u5165\u5ba2\u5385\u4e2d\uff0c\u7acb\u523b\u5949\u4e0a\u9762 \u5dfe\u3001\u9999\u8317\uff0c\u7aef\u4e0a\u7ec6\u70b9\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u62c9\u5f00\u9760\u5728\u7ea2\u6f06\u684c\u5b50\u4e0a\u592a\u5e08\u6905\uff0c\u9053\uff1a\u201c\u5750\uff0c\u5750\uff01\u201d \u3000\u3000\u8fd9\u662f\u5f20\u4e0d\u592a\u5927\u7684\u65b9\u684c\uff0c\u56db\u5f20\u592a\u5e08\u6905\u5360\u6ee1\u4e86\u56db\u9762\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u674e\u9756\u9009\u4e86\u866c\u9aef\u5ba2\u5bf9\u9762\u7684\u4f4d\u7f6e\u5750\u4e0b\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u5c31\u53ea\u597d\u5750\u5728\u5927\u54e5\u7684\u8eab\u65c1\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u7531\u6000\u4e2d\u53d6\u51fa\u9526\u56ca\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e09\u59b9\u6536\u8d77\u6765\uff0c\u8fd9\u662f\u7ed9\u4f60\u7684\u4e00\u4efd\u793c\u7269\uff0c\u4f60\u548c\u4e8c\u5f1f\u90fd\u5f88\u9700\u8981\u5b83\uff0c\u5509\uff0c\u53ea\u6050\u4f1a\u4fbf\u5b9c\u4e86\u674e\u4e16 \u6c11\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u64e6\u5e72\u4e86\u4e00\u8138\u6cea\u75d5\uff0c\u63a5\u5165\u624b\u4e2d\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u597d\u50cf\u5f88\u540d\u8d35 \u7684\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u4e0d\u662f\u540d\u8d35\uff0c\u4f46\u5374\u662f\u4e00\u4efd\u5f88\u91cd\u7684\u793c\u7269\u3002\u5c0f\u5144\u5728\u6c5f\u6e56\u4e0a\u7ecf\u8425\u4e86\u4e8c\u5341\u5e74\uff0c\u5728\u957f\u5b89\u548c\u4e2d\u539f\u5404 \u5730\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\uff0c\u6325\u624b\u4ee4\u4e8c\u4f4d\u5973\u5a62\u9000\u51fa\u5ba2\u5385\uff0c\u624d\u63a5\u9053\uff1a \u3000\u3000\u201c\u4f0f\u85cf\u4e86\u5341\u4e07\u7532\u5175\uff0c\u79ef\u5b58\u4e86\u5343\u4e07\u9977\u94f6\uff0c\u5c3d\u5728\u8fd9\u9526\u56ca\u4e4b\u4e2d\u3002\u91cc\u9762\u6709\u5404\u5927\u94b1\u5e84\u5b58\u6b3e\u7684\u5e10\u53f7\uff0c \u4e5f\u6709\u9ec4\u91d1\u3001\u767d\u94f6\u57cb\u85cf\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u867d\u7136\u662f\u85cf\u4f0f\u5728\u5404\u5730\u6c11\u95f4\uff0c\u4f46\u9526\u56ca\u5175\u518c\u4e0a\u8bb0\u6709\u8be6\u7ec6\u7684\u5730\u5740\u3001\u59d3\u540d\uff0c\u4ed6\u4eec\u90fd\u662f\u5343\u592b \u957f\uff0c\u4e00\u5171\u6709\u4e00\u767e\u4e8c\u5341\u516b\u4eba\uff0c\u4f60\u82e5\u8981\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eec\u80fd\u5728\u4e09\u5929\u5185\u96c6\u9f50\u6240\u5c5e\uff0c\u96c6\u4e2d\u5019\u547d\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u542c\u5f97\u795e\u5f80\u4e86\u3002 \u3000\u3000\u5979\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u771f\u662f\u5343\u53e4\u81f3\u4eca\u6700\u91cd\u7684\u4e00\u4efd\u793c\u7269\uff0c\u524d\u65e0\u53e4\u4eba\uff0c\u6050\u6015\u4e5f\u540e\u65e0\u6765\u8005\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\uff01\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u9009\u4e00\u4e9b\u52c7\u6b66\u5065\u8005\uff0c\u5e26\u5f80\u897f\u7586\u5462\uff1f\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u5982\u679c\u4e16\u4e0a\u6709\u7740\u548c\u6211\u93d6\u6218\u6570\u5341\u5408\u7684\u4eba\uff0c\u5e26\u518d\u591a\u7684\u4eba\u4e5f\u65e0\u6cd5\u5f00\u8f9f\u897f\u7586\u3002\u6211\u8981\u4ee5\u5929\u795e\u4e34\u51e1\u7684 \u5a01\u52bf\uff0c\u4f7f\u4ed6\u4eec\u81e3\u4f0f\u6211\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\u662f\u6709\u8fd9\u4e2a\u80fd\u529b\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u5c31\u5730\u53d6\u624d\uff0c\u7528\u800c\u6cbb\u4e4b\uff0c\u53cd\u6613\u751f\u6839\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u6240\u4ee5\u8fd9\u4e9b\u8d22\u5bcc\u3001\u5175\u9a6c\u6211\u5df2\u6ca1\u6709\u7528\u4e86\uff0c\u6211\u548c\u4ed6\u4eec\u76f8\u7ea6\u4ee5\u5341\u5e74\u4e3a\u671f\uff0c\u8fc7\u4e86\u8fd9\u4e2a\u9650\u671f\uff0c\u8fd9\u4e9b \u4ee3\u5175\u5c31\u81ea\u52a8\u89e3\u6563\uff0c\u4e0d\u518d\u53d7\u7ea6\u675f\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4f60\u4eec\u8981\u65e9\u901a\u77e5\u4ed6\u4eec\uff0c\u8fd8\u53ef\u4ee5\u5ef6\u957f\u4e09\u5e74\u3002\u518d\u4e0d\u52a8\u7528\uff0c\u4ed6\u4eec\u5df2\u5e74\u82b1\u6e10\u8001\uff0c\u4e0d\u7b97\u662f\u7cbe\u9510 \u4e4b\u5e08\u4e86\u3002 \u3000\u3000\u4f60\u4eec\u638c\u63e1\u4e86\u8fd9\u4e9b\u8d22\u5bcc\u3001\u4eba\u9a6c\uff0c\u53ef\u4ee5\u548c\u5929\u4e0b\u4efb\u4f55\u8bf8\u4faf\u6297\u793c\uff0c\u674e\u4e16\u6c11\u4e5f\u6ca1\u6709\u4e8c\u5f1f\u3001\u4e09\u59b9\u7684\u5b9e \u529b\u96c4\u539a\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\u8fd9\u4e9b\u51c6\u5907\u786e\u662f\u773c\u4e0b\u7adf\u53d6\u9738\u4e1a\u7684\u5f3a\u52bf\u5175\u529b\uff0c\u4e00\u65e6\u52a8\u5458\uff0c\u4e00\u547c\u767e\u5e94\uff0c\u653e\u773c\u5929\u4e0b\u65e0\u4eba\u80fd \u53ca\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u866c\u9aef\u5ba2\u4e00\u6325\u624b\uff0c\u4e0d\u8ba9\u674e\u9756\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u53c8\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4efd\u8d60\u9001\u4f60\u548c\u4e09\u59b9\u7684\u793c\u7269\u5df2\u4e3a\u4f60\u4eec\u6240\u6709\uff0c\u4f60\u4eec\u5982\u4f55\u8fd0\u7528\uff0c\u5c31\u4e0d\u5173\u6211\u7684\u4e8b\u4e86\uff0c\u6211\u4e0d\u60f3\u77e5 \u9053\uff0c\u4e5f\u4e0d\u613f\u591a\u7ba1\u3002\u5012\u662f\u6709\u4e00\u4ef6\u79c1\u4e8b\uff0c\u6211\u8981\u5bf9\u4e8c\u5f1f\u8bf4\u660e\u767d\u2026\u2026\u201d \u3000\u3000\u674e\u9756\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\u8bf7\u8bf4\uff0c\u5c0f\u5f1f\u8fd9\u53a2\u6d17\u8033\u606d\u542c\u4e86\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u65e5\u4e45\u60c5\u751f\uff0c\u53e4\u4eba\u8bda\u4e0d\u6b3a\u6211\u3002\u4e3a\u4f20\u4e09\u59b9\u7684\u5251\u6cd5\uff0c\u6211\u548c\u5979\u592a\u8fc7\u63a5\u8fd1\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a0d\u987f\uff0c\u8f6c\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u674e\u9756\u7b11\u5bb9\u6ee1\u9762\uff0c\u5168\u65e0\u4e0d\u8c6b\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u624d\u53c8\u63a5\u4e0b\u53bb\u9053\uff1a \u3000\u3000\u201c\u7adf\u7136\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u52a8\u4e86\u60c5\u6000\u3002\u201d \u3000\u3000\u674e\u9756\u7b11\u9053\uff1a \u3000\u3000\u201c\u51fa\u5c18\u7f8e\u4e3d\u7edd\u4e16\uff0c\u5a07\u5a9a\u53ef\u4eba\uff0c\u5982\u679c\u6709\u4eba\u548c\u5979\u5e38\u65e5\u76f8\u5904\uff0c\u4e0d\u52a8\u60c5\u6000\uff0c\u90a3\u4e2a\u4eba\u5373\u662f\u6ce5\u5851\u3001\u6728 \u96d5\u7684\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u6700\u53ef\u6076\u7684\u662f\u4e03\u7edd\u9053\u4eba\u4ece\u4e2d\u6311\u62e8\uff0c\u4e0d\u8ba9\u4eba\u79f0\u4e09\u59b9\u4e3a\u674e\u592b\u4eba\uff0c\u8fd9\u4e9b\u4e8b\u6211\u867d\u6709\u6240\u95fb\uff0c\u5374\u672a\u8ffd \u7a76\uff0c\u6240\u4ee5\u4eba\u4eba\u90fd\u79f0\u5979\u5f20\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u4e5f\u65e0\u6cd5\u8ffd\u7a76\u554a\uff01\u201d\u674e\u9756\u7b11\u9053\uff1a \u3000\u3000\u201c\u558a\u51fa\u5c18\u5f20\u59d1\u5a18\uff0c\u6ca1\u6709\u9519\u5440\uff01\u5979\u672c\u6765\u5c31\u59d3\u5f20\u3002\u201d \u3000\u3000\u201c\u4e8c\u5f1f\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u4e8b\uff0c\u4f60\u771f\u7684\u4e0d\u653e\u5fc3\u4e0a\u5417\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u6211\u542c\u5230\u4f20\u8a00\uff0c\u4f46\u6211\u6ca1\u6709\u95ee\u8fc7\u51fa\u5c18\uff0c\u6211\u4eec\u662f\u91d1\u5170\u5144\u59b9\uff0c\u5927\u54e5\u5173\u5fc3\u4e09\u59b9\uff0c\u6211\u5f88\u9ad8\u5174\uff0c\u6709\u4ec0 \u4e48\u4e8b\u8981\u6211\u653e\u5728\u5fc3\u4e0a\u5462\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u8c08\u8bdd\u7684\u4eba\u795e\u60c5\u5982\u5e38\uff0c\u4f46\u542c\u4e24\u4eba\u8c08\u8bdd\u7684\u5f20\u51fa\u5c18\u5374\u662f\u5dee\u7ea2\u6ee1\u8138\uff0c\u7ea2\u5230\u8033\u6735\u540e\u8fb9\u53bb\u4e86\u3002 \u3000\u3000\u5979\u867d\u7136\u7f9e\u4e0d\u53ef\u4ef0\uff0c\u4f46\u7f9e\u4e2d\u65e0\u6127\u3002 \u3000\u3000\u201c\u8bf4\u7684\u662f\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u4e5f\u8bb8\u6211\u5fc3\u6709\u6240\u601d\uff0c\u4f46\u5374\u7edd\u65e0\u4fb5\u72af\u8fc7\u4e09\u59b9\u7684\u884c\u52a8\uff0c\u867d\u7136\u5982\u6b64\uff0c\u4e5f\u591f\u6211\u8fd9\u4e2a\u4f5c\u5927\u54e5\u7684\u60ed\u6127 \u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff0c\u4ee5\u51fa\u5c18\u4e4b\u7f8e\uff0c\u5929\u4e0b\u6709\u51e0\u4eba\u80fd\u4e0d\u52a8\u5fc3\uff0c\u4f55\u51b5\u4f60\u4eec\u65e5\u4e45\u76f8\u5904\uff0c\u6307\u5bfc\u5251\u6cd5\uff0c\u4f20\u6388\u5185 \u529f\uff0c\u96be\u514d\u6709\u808c\u80a4\u76f8\u89e6\u4e4b\u5904\u3002\u60c5\u6000\u867d\u52a8\uff0c\u4f46\u5374\u53d1\u4e4b\u4e8e\u60c5\uff0c\u6b62\u4e4e\u4e8e\u793c\u3002\u8fd9\u4e00\u70b9\uff0c\u5c0f\u5f1f\u6050\u6015\u5c31\u4e0d\u5982 \u5927\u54e5\u591a\u77e3\uff01\u201d \u3000\u3000\u866c\u9aef\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\u5144\u5f1f\uff0c\u4f60\u80fd\u4fe1\u4efb\u5927\u54e5\uff0c\u4e5f\u65e0\u6789\u6211\u4eec\u7ed3\u4e49\u4e00\u573a\uff0c\u5c0f\u5144\u897f\u884c\uff0c\u5982\u6709\u6240\u6210\uff0c\u5b9a\u4f1a\u6d3e\u4eba\u901a\u77e5 \u4f60\u4eec\u3002 \u3000\u3000\u5982\u6709\u9700\u8981\u5927\u54e5\uff0c\u4e0d\u7ba1\u5b83\u5c71\u6c34\u9065\u9694\uff0c\u5343\u91cc\u51b0\u5c01\uff0c\u9ec4\u6c99\u6f2b\u5929\uff0c\u70c8\u65e5\u7099\u4eba\uff1b\u53ea\u8981\u6211\u5f97\u5230\u8baf\u606f\uff0c \u4e00\u5b9a\u517c\u7a0b\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\u82f1\u96c4\u76d6\u4e16\uff0c\u5929\u4e0b\u65e0\u654c\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u51b2\u950b\u9677\u9635\uff0c\u65e0\u4eba\u80fd\u6321\u3002\u4e0d\u8fc7\uff0c\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\uff0c\u8fd9\u65b9\u9762\u8fd8\u671b\u591a\u52a0\u5c0f\u5fc3\u3002\u7834\u5929\u5251\u9ec4 \u4e91\u8bda\u539a\u8c28\u614e\uff0c\u53ef\u5bc4\u91cd\u4efb\u3002 \u3000\u3000\u949f\u6728\u9b41\u53ea\u662f\u4e00\u5458\u6218\u5c06\uff0c\u5374\u4e0d\u662f\u7edf\u5175\u4e4b\u5e05\uff0c\u5927\u54e5\u6700\u597d\u628a\u4ed6\u7559\u5728\u8eab\u8fb9\uff0c\u9047\u4e0a\u5f3a\u654c\uff0c\u53ef\u4ee5\u501f \u91cd\u3002\u56e0\u4e3a\u4ed6\u5fc3\u4e2d\u53ea\u6709\u5927\u54e5\u4e00\u4eba\uff0c\u522b\u4eba\u5c31\u5f88\u96be\u6307\u6325\u4ed6\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u4e8c\u5f1f\u8d60\u8a00\uff0c\u73b0\u5728\u6211\u4eec\u518d\u65e0\u5757\u5792\uff0c\u53ef\u4ee5\u653e\u6000\u7545\u996e\u597d\u597d\u559d\u4e00\u676f\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5f1f\u5949\u966a\uff0c\u4e0d\u9189\u4e0d\u4f11\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff01\u6211\u8fd8\u6709\u4e8b\u8bf7\u6559\u5927\u54e5\uff0c\u7136\u540e\u518d\u966a\u4f60\u559d\u4e2a\u75db\u5feb\uff0c\u6211\u8fd8\u8981\u62d4\u5251\u8d77\u821e\uff0c\u653e\u58f0\u9ad8 \u6b4c\uff0c\u73cd\u60dc\u8fd9\u4e00\u6b21\u5144\u59b9\u4f1a\u805a\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u5df2\u662f\u6ce3\u4e0d\u6210\u58f0\u3002 \u3000\u3000\u201c\u522b\u54ed\uff01\u522b\u54ed\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u662f\u6709\u4e8b\u8981\u95ee\u6211\u5417\uff1f\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u64e6\u62ed\u7740\u6cea\u75d5\uff0c\u9053\uff1a \u3000\u3000\u201c\u6ca1\u51fa\u606f\u5440\uff01\u8bf4\u7740\u8bf4\u7740\u5c31\u54ed\u4e86\u8d77\u6765\uff0c\u5973\u4eba\u5bb6\u5c31\u662f\u4e0d\u5982\u4f60\u4eec\u7537\u4eba\u5566\uff01\u201d \u3000\u3000\u674e\u9756\u7b11\u9053\uff1a \u3000\u3000\u201c\u7537\u5973\u4e4b\u95f4\uff0c\u5982\u6ca1\u6709\u4e00\u70b9\u5206\u522b\uff0c\u8fd9\u4e16\u4e0a\u5c31\u5c11\u4e86\u5f88\u591a\u4eba\u60c5\u5473\u9053\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u8bf4\u5427\uff01\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\uff1f\u6211\u6025\u7740\u8981\u5429\u5490\u4ed6\u4eec\u6446\u9152\u554a\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u8fd9\u9526\u56ca\u4e2d\u7684\u540d\u5355\uff0c\u4e00\u767e\u4e8c\u5341\u516b\u4e2a\u5343\u592b\u957f\uff0c\u90fd\u662f\u5927\u54e5\u7684\u4eba\uff0c\u4ed6\u4eec\u5982\u4f55\u80af\u542c\u5c0f\u59b9\u548c\u674e\u9756\u7684 \u8bdd\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff01\u201d\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u9526\u56ca\u4e2d\u6709\u4e00\u9762\u7af9\u724c\u4ee4\u7b26\uff0c\u4efb\u4f55\u4eba\u62ff\u7740\u90a3\u9762\u7af9\u724c\uff0c\u4ed6\u4eec\u5c31\u552f\u547d\u662f\u4ece\uff0c\u597d\u597d\u6536\u5b58\uff0c\u4e0d\u8981\u9057 \u5931\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u59b9\u8bb0\u4e0b\u4e86\u3002\u201d\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u6211\u4f1a\u4e00\u76f4\u5e26\u5728\u8eab\u4e0a\uff0c\u8981\u53d6\u7af9\u724c\uff0c\u5148\u53d6\u6211\u547d\u3002\u201d \u3000\u3000\u866c\u9aef\u5ba2\u9053\uff1a \u3000\u3000\u201c\u5bc6\u800c\u4e0d\u5ba3\uff0c\u8c01\u4f1a\u77e5\u9053\u5462\uff1f\u4e09\u59b9\u53ea\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\uff01\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u8fd9\u9526\u56ca\u4e4b\u4e8b\uff0c\u9664\u5927\u54e5\u4e4b\u5916\uff0c\u771f\u7684\u518d\u65e0\u522b\u4eba\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u866c\u9aef\u5ba2\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u3000\u3000\u201c\u6c5f\u68ee\u3001\u71d5\u53ef\u4e5f\u8bb8\u77e5\u9053\u8fd9\u4e00\u4e9b\uff0c\u4f46\u4e5f\u4e0d\u662f\u5168\u90e8\uff0c\u81f3\u4e8e\u57cb\u85cf\u91d1\u94f6\u7684\u4eba\uff0c\u5148\u88ab\u8499\u4e0a\u773c\u775b\uff0c\u8bad \u7ec3\u4e86\u4e09\u4e2a\u6708\uff0c\u8ba9\u4ed6\u4eec\u4e60\u60ef\u4e8e\u8499\u773c\u4f5c\u4e8b\uff0c\u624d\u5e26\u4ed6\u4eec\u53bb\u63a9\u85cf\u91d1\u94f6\uff0c\u4e00\u5171\u6709\u4e94\u5904\u5730\u65b9\uff0c\u6211\u4e5f\u7559\u4e0b\u4e86 \u4e0d\u77e5\u60c5\u9ad8\u624b\u770b\u5b88\u3002 \u3000\u3000\u5982\u4f55\u53d6\u51fa\uff0c\u4e5f\u8bb0\u8f7d\u4e8e\u9526\u56ca\u4e2d\u4e00\u672c\u8bb0\u4e8b\u7c3f\u4e2d\uff0c\u8fd8\u6709\u8fde\u7edc\u7684\u6697\u8bed\uff0c\u4f60\u4eec\u82e5\u662f\u8bf4\u9519\u4e86\uff0c\u4ed6\u4eec\u8fd8 \u4f1a\u963b\u62e6\u4f60\u4eec\u3002 \u3000\u3000\u6211\u65e0\u6cd5\u80af\u5b9a\u4ed6\u4eec\u7684\u5fe0\u5b9e\u7a0b\u5ea6\uff0c\u81f3\u5c11\u8981\u6253\u8d70\u4ed6\u4eec\u624d\u884c\u3002\u4e09\u59b9\uff0c\u6240\u6709\u7684\u4e8b\u60c5\u90fd\u8bb0\u5728\u4e00\u672c\u8bb0\u4e8b \u4e2d\uff0c\u4e0d\u8fc7\u540d\u518c\u662f\u7531\u767d\u7ee2\u5236\u6210\u7684\u518c\u5b50\uff0c\u5e76\u8ba1\u4e24\u672c\uff0c\u540d\u518c\u8bb0\u4eba\uff0c\u8584\u5b50\u8bb0\u4e8b\u3002\u4f60\u4ed4\u7ec6\u770b\u4e00\u904d\uff0c\u5c31\u77e5 \u9053\u5982\u4f55\u5904\u7f6e\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u5927\u54e5\uff01\u201d \u3000\u3000\u8c22\u7740\u8c22\u7740\uff0c\u53c8\u6d41\u4e0b\u4e86\u773c\u6cea\u3002 \u3000\u3000\u5fc3\u4e2d\u611f\u52a8\u554a\uff01 \u3000\u3000\u674e\u9756\u4e5f\u4e0d\u81ea\u7981\u7684\u53d6\u51fa\u7ee2\u5e15\u62ed\u6cea\u3002 \u3000\u3000\u8fd9\u662f\u591a\u4e48\u5927\u7684\u6069\u60c5\uff01 \u3000\u3000\u5341\u4e07\u7532\u5175\uff0c\u5343\u4e07\u85cf\u91d1\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u4e8c\u5341\u5e74\u7684\u5fc3\u8840\uff0c\u4e00\u4e0b\u5b50\u5c31\u9001\u4eba\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u5374\u54c8\u54c8\u5927\u7b11\u7740\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\u9ad8\u58f0\u53eb\u9053\uff1a \u3000\u3000\u201c\u6446\u9152\uff01\u201d \u3000\u3000\u4e24\u4e2a\u5973\u5a62\u5f53\u5148\u5165\u5385\uff0c\u7247\u523b\u4e4b\u540e\u9152\u83dc\u9f50\u4e0a\u3002 \u3000\u3000\u8fd9\u91cc\u4e0d\u662f\u5ba2\u6808\uff0c\u4f46\u4e00\u5207\u4f3c\u90fd\u51c6\u5907\u59a5\u5f53\u3002\u4e00\u58f0\u4f20\u547c\uff0c\u5f88\u5feb\u5c31\u5230\u3002 \u3000\u3000\u8bf4\u5df2\u6ca1\u6709\u5757\u5792\uff0c\u5176\u5b9e\u4e09\u4eba\u5fc3\u4e2d\u90fd\u6709\u611f\u6168\uff0c\u79bb\u60c5\u522b\u7eea\u5145\u585e\u5fc3\u5934\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u82f1\u96c4\u76d6\u4e16\uff0c\u4f46\u4ecd\u88ab\u5f20\u51fa\u5c18\u4e00\u7f15\u6e29\u67d4\u7f1a\u7d27\u4e86\u82f1\u96c4\u809d\u80c6\u3002 \u3000\u3000\u60f3\u5230\u4eca\u65e5\u4e00\u522b\uff0c\u6b64\u540e\u5f88\u96be\u518d\u89c1\u90a3\u7f8e\u4e3d\u7684\u97f3\u5bb9\u7b11\u8c8c\uff0c\u5fc3\u4e2d\u75db\u82e6\u6781\u4e86\u3002\u75db\u82e6\u5f52\u75db\u82e6\uff0c\u8fd8\u4e0d\u80fd \u6d41\u9732\u51fa\u6765\u8ba9\u674e\u9756\u53d1\u89c9\uff0c\u53ea\u597d\u5927\u7897\u5927\u7897\u7684\u559d\u9152\u3002 \u3000\u3000\u9152\u5165\u6101\u80a0\u6101\u66f4\u6101\uff0c\u9152\u4e5f\u5c31\u8d8a\u559d\u8d8a\u591a\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5ba2\u9152\u91cf\u597d\uff0c\u5343\u676f\u4e0d\u9189\uff0c\u4f46\u4e00\u5343\u3001\u4e00\u767e\u676f\u5462\uff1f\u4f55\u51b5\u5fc3\u4e2d\u6709\u4e00\u628a\u706b\uff0c\u95f7\u58f0\u8ba9\u5b83\u70e7\uff0c\u53c8\u4e0d \u80fd\u8bf4\u51fa\u6765\u5440\uff01 \u3000\u3000\u6240\u4ee5\uff0c\u866c\u9aef\u5ba2\u9189\u4e86\uff0c\u9189\u5f97\u4e0d\u7701\u4eba\u4e8b\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u4e5f\u9189\u4e86\u3002 \u3000\u3000\u5979\u4e5f\u5728\u60f3\u5fc3\u4e8b\uff0c\u60f3\u5230\u4ee5\u540e\u8981\u81ea\u5df1\u6478\u7d22\u7740\u7ec3\u5251\uff0c\u5c11\u4e86\u866c\u9aef\u5ba2\u8fd9\u4e2a\u5c3d\u5fc3\u4f20\u6388\u7684\u4eba\uff0c\u8981\u66f4\u4e0a\u4e00 \u5c42\u697c\u4e5f\u5c31\u8981\u82b1\u8d39\u66f4\u591a\u66f4\u957f\u7684\u65f6\u95f4\u3002 \u3000\u3000\u518d\u60f3\u5230\u866c\u9aef\u5ba2\u9001\u4e86\u4e8c\u5341\u5e74\u805a\u96c6\u800c\u6210\u7684\u91cd\u793c\uff0c\u771f\u60f3\u62b1\u7d27\u4ed6\u5927\u54ed\u4e00\u573a\uff0e\u5c31\u7b97\u8ba9\u5f20\u5927\u54e5\u62b1\u7740\u4eb2 \u4eb2\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u53ea\u8981\u4e0d\u8131\u5149\u8863\u670d\u4e0a\u5e8a\uff0c\u4e5f\u8be5\u7b97\u5bf9\u5f97\u8d77\u674e\u9756\u554a\uff01 \u3000\u3000\u4e09\u4e2a\u4eba\u4e49\u7ed3\u5144\u59b9\uff0c\u4e00\u822c\u7684\u60c5\u6df1\u4e49\u91cd\uff0c\u4e3a\u4ec0\u4e48\u6211\u4f5c\u4e86\u674e\u9756\u7684\u59bb\u5b50\u4e4b\u540e\uff0c\u5c31\u8981\u548c\u5927\u54e5\u62c9\u8fdc\u8ddd \u79bb\u5462\uff1f \u3000\u3000\u8fd9\u4ef6\u4e8b\u5f88\u7f20\u5939\uff0c\u8d8a\u60f3\u8d8a\u82e6\uff0c\u8d8a\u7406\u8d8a\u4e71\u3002 \u3000\u3000\u60f3\u5f52\u60f3\uff0c\u5f20\u59d1\u5a18\u8fd8\u662f\u4e0d\u6562\u505a\u51fa\u6765\u3002 \u3000\u3000\u5979\u6309\u4e0b\u4e00\u8154\u60c5\u6101\uff0c\u559d\u95f7\u9152\uff0c\u4e0d\u9189\u66f4\u4f55\u5f85\uff1f \u3000\u3000\u56e0\u6b64\uff0c\u5f20\u59d1\u5a18\u9189\u5f97\u4f0f\u6848\u7761\u7740\u4e86\u3002 \u3000\u3000\u674e\u9756\u4e5f\u9189\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u867d\u7136\u5927\u5ea6\u96cd\u5bb9\uff0c\u5e76\u672a\u628a\u5927\u54e5\u548c\u59bb\u5b50\u7684\u4e8b\u770b\u5f97\u5f88\u91cd\uff0c\u90a3\u5f97\u8981\u6ef4\u9152\u4e0d\u6cbe\uff0c\u5b8c\u5168\u6e05\u9192\u624d\u884c\u3002 \u559d\u591a\u4e86\u9152\uff0c\u5c31\u80e1\u601d\u4e71\u60f3\u4e86\u3002 \u3000\u3000\u4ed6\u60f3\u8be5\u4e0d\u8be5\u8ba9\u866c\u9aef\u5ba2\u628a\u51fa\u5c18\u5e26\u53bb\u897f\u57df\uff1f\u771f\u7684\u63d0\u51fa\u6765\u4e86\uff0c\u866c\u9aef\u5ba2\u4f1a\u4e0d\u4f1a\u7b54\u5e94\u5462\uff1f\u51fa\u5c18\u4f1a\u6709 \u4e9b\u4ec0\u4e48\u53cd\u5e94\uff1f \u3000\u3000\u866c\u9aef\u5ba2\u90a3\u4efd\u539a\u793c\u4e0d\u7ed9\u4e8c\u5f1f\uff0c\u7ed9\u4e09\u59b9\uff0c\u6211\u674e\u9756\u8981\u4e0d\u8981\u63a5\u53d7\u5462\uff1f \u3000\u3000\u4e0d\u63a5\u53d7\uff0c\u5c31\u5e94\u5f53\u9762\u8bf4\u51fa\u6765\u3002\u4e0d\u8bf4\uff0c\u5c31\u662f\u627f\u53d7\u4e86\u3002\u8bf4\u51fa\u6765\uff0c\u662f\u4ec0\u4e48\u540e\u679c\u5462\uff1f\u4e0d\u8bf4\u53c8\u7a9d\u5728\u5fc3 \u91cc\u96be\u53d7\u3002 \u3000\u3000\u6211\u674e\u9756\u719f\u8bfb\u5175\u6cd5\uff0c\u7cbe\u6653\u97ec\u7565\u3002\u4e0d\u7528\u5343\u4e07\u8d60\u91d1\u548c\u5341\u4e07\u7532\u5175\uff0c\u4e00\u6837\u80fd\u5e2e\u52a9\u674e\u4e16\u6c11\u6253\u51fa\u5929\u4e0b \u6765\u3002 \u3000\u3000\u60f3\u6765\u60f3\u53bb\uff0c\u65e0\u6cd5\u8bf4\u51fa\u53e3\uff0c\u53ea\u6709\u95f7\u5728\u5fc3\u91cc\u4e86\u3002 \u3000\u3000\u4e3b\u8981\u662f\u820d\u4e0d\u5f97\u5f20\u51fa\u5c18\u554a\uff01 \u3000\u3000\u8fd9\u6837\u7684\u5fc3\u60c5\uff0c\u559d\u9152\u4e0d\u5b9c\u591a\uff0c\u4e00\u591a\u5c31\u5927\u9189\u4e86\u3002 \u3000\u3000\u674e\u9756\u559d\u9152\u524d\u5c31\u8bf4\u8fc7\u4e0d\u9189\u4e0d\u4f11\u3002 \u3000\u3000\u679c\u7136\u662f\u4e09\u4eba\u5168\u9189\u4e86\u3002 \u3000\u3000\u4ee5\u4e09\u4eba\u7cbe\u6df1\u7684\u5185\u529f\uff0c\u90fd\u4e0d\u8be5\u9189\uff0c\u53ea\u56e0\u5fc3\u4e2d\u591a\u4e86\u4e00\u4e2a\u6101\u5b57\uff0c\u9152\u5165\u6101\u80a0\u6613\u5316\u76f8\u601d\u6cea\uff0c\u4e5f\u5bb9\u6613 \u559d\u4e00\u4e2a\u9169\u914a\u5927\u9189\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u5fd9\u574f\u4e86\u4e24\u4e2a\u4e2b\u5934\u3002 \u3000\u3000\u62ac\u8d77\u4e86\u4e09\u4eba\u653e\u5e8a\u4e0a\uff0c\u518d\u5e2e\u4ed6\u4eec\u89e3\u677e\u8863\u88f3\u3002\u5f20\u59d1\u5a18\u5f88\u8f7b\u5de7\uff0c\u62ac\u8d77\u6765\u4e5f\u5c31\u4e0d\u8d39\u529b\u3002\u674e\u9756\u4e5f\u8fd8 \u80fd\u62ac\u8d77\u3002 \u3000\u3000\u4f46\u866c\u9aef\u5ba2\u5c31\u62ac\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u4e2b\u5934\u7528\u5c3d\u5168\u8eab\u6c14\u529b\uff0c\u866c\u9aef\u5ba2\u5206\u6beb\u4e0d\u79fb\u3002 \u3000\u3000\u6ca1\u6cd5\u5b50\uff0c\u53ea\u597d\u62db\u6765\u4e86\u4e24\u4e2a\u5927\u7537\u4eba\uff0c\u56db\u4eba\u5408\u529b\uff0c\u624d\u7b97\u628a\u866c\u9aef\u5ba2\u62ac\u4e0a\u5e8a\uff0c\u4e5f\u987e\u4e0d\u5f97\u8fd9\u4f4d\u5927\u82f1 \u96c4\u7684\u9152\u540e\u9189\u6001\u88ab\u4eba\u770b\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u2014\u2014 \u3000\u3000\u674e\u9756\u9192\u8fc7\u6765\uff0c\u5df2\u7ecf\u662f\u65e5\u4e0a\u4e09\u7aff\u4e86\u3002 \u3000\u3000\u674e\u9756\u53eb\u9192\u4e86\u5f20\u51fa\u5c18\uff0c\u4e00\u540c\u53bb\u770b\u866c\u9aef\u5ba2\u3002 \u3000\u3000\u4e24\u4e2a\u4e2b\u5934\u5374\u8bf4\u866c\u9aef\u5ba2\u5929\u4e00\u4eae\u5c31\u79bb\u6b64\u800c\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u5185\u529f\u7cbe\u6df1\uff0c\u9189\u5f97\u6df1\uff0c\u9192\u5f97\u4e5f\u5feb\u3002 \u3000\u3000\u6628\u591c\u4e00\u9189\uff0e\u5144\u5f1f\u4eec\u5c31\u6b64\u5206\u624b\u4e86\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u6478\u6478\u6000\u4e2d\u7684\u9526\u56ca\uff0c\u9053\uff1a \u3000\u3000\u201c\u674e\u9756\uff0c\u8981\u4e0d\u8981\u628a\u5341\u4e07\u7532\u5175\u548c\u5343\u4e07\u9977\u94f6\u4ea4\u7ed9\u674e\u4e16\u6c11\uff1f\u201d \u3000\u3000\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u518d\u8fc7\u51e0\u5e74\uff0c\u6b66\u58eb\u8001\u77e3\uff0c\u91d1\u94f6\u66f4\u662f\u8eab\u5916\u7269\uff0c\u9664\u4e86\u519b\u9977\u4e4b\u5916\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba\u54ea\u80fd\u7528\u5f97\u8bb8\u591a\uff1f \u7559\u4e4b\u4f55\u7528\uff1f\u201d \u3000\u3000\u5f20\u51fa\u5c18\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u81ea\u5df1\u95ef\u554a\uff01\u6709\u94b1\u6709\u4eba\uff0c\u518d\u52a0\u4e0a\u4f60\u7528\u5175\u7684\u624d\u80fd\uff0c\u770b\u8fd9\u5341\u4e07\u91cc\u9526\u7ee3\u5c71\u6cb3 \u8be5\u662f\u8c01\u5bb6\u5929\u4e0b\uff1f\u201d \u3000\u3000\u674e\u9756\u542c\u5f97\u5fc3\u5934\u4e00\u8df3\uff0c\u5fd6\u9053\uff1a\u5979\u7adf\u6709\u5982\u6b64\u60f3\u6cd5\uff0c\u771f\u662f\u5dfe\u5e3c\u5fd7\u5411\u80dc\u987b\u7709\u2026\u2026 \u3000\u3000\u60f3\u7684\u6df1\u601d\uff0c\u5c31\u5fd8\u8bb0\u7b54\u8bdd\u4e86\u3002 \u3000\u3000\u201c\u674e\u9756\uff0c\u662f\u4e0d\u662f\u6211\u8bf4\u9519\u6d3b\u4e86\uff1f\u201d\u5f20\u51fa\u5c18\u9053\uff1a \u3000\u3000\u201c\u592b\u5531\u5987\u968f\u554a\uff01\u4f46\u4ecd\u8981\u4f60\u51b3\u5b9a\u7684\u624d\u7b97\uff0c\u6211\u8bf4\u4e86\u4e00\u767e\u6b21\u4e5f\u4e0d\u7b97\u6570\u3002\u5c0f\u5987\u4eba\u4e0d\u77e5\u5929\u4e0b\u4e8b\uff0c\u592b \u5a7f\u53ef\u522b\u602a\u6211\uff01\u201d \u3000\u3000\u674e\u9756\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u51fa\u5c18\uff0c\u5343\u4e07\u85cf\u91d1\u5341\u4e07\u7532\u5175\uff0c\u8fd9\u662f\u7b14\u96c4\u539a\u7684\u672c\u94b1\uff0c\u8c01\u90fd\u4f1a\u542f\u52a8\u4e89\u9738\u5929\u4e0b\u4e4b\u5fc3\uff0c\u4e0d\u8fc7\u529e\u4e0d \u5230\u554a\uff01\u201d \u3000\u3000\u5f20\u51fa\u5c18\u6b63\u8272\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u751f\u6c14\uff0c\u4e5f\u4e0d\u602a\u6211\uff0c\u6211\u5012\u60f3\u548c\u4f60\u4e89\u8bba\u4e00\u756a\uff0c\u6b64\u5730\u6ca1\u6709\u5916\u4eba\uff0c\u8bf4\u5b8c\u5c31\u7b97\uff0c\u5e94\u8be5\u5982\u4f55\uff1f \u8fd8\u662f\u7531\u4f60\u62ff\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u9053\uff1a\u201c\u4e0d\u8bf4\u4e2a\u9752\u7ea2\u7682\u767d\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u670d\u6c14\u3002\u51fa\u5c18\uff0c\u5c3d\u4f60\u7684\u624d\u80fd\u4e89\u8bba\uff0c\u4e0d\u8fc7\u2026\u2026\u4e00\u5b9a\u8981 \u8bb2\u7406\u5440\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u70b9\u70b9\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u81ea\u8ba4\u4e0d\u5982\u674e\u4e16\u6c11\u5417\uff1f\u201d \u3000\u3000\u201c\u8bba\u6c14\u5ea6\u98ce\u8303\u3001\u6c11\u80de\u7269\u4e0e\u7684\u4eb2\u548c\u529b\uff0c\u6211\u4e0d\u5982\u674e\u4e16\u6c11\u3002\u4f46\u884c\u5175\u5e03\u9635\uff0c\u8fd0\u7b79\u5e37\u5e44\uff0c\u674e\u4e16\u6c11\u5c31 \u4e0d\u5982\u6211\u4e86\u3002\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6253\u5929\u4e0b\uff0c\u62a2\u6c5f\u5c71\uff0c\u53ef\u662f\u4e00\u5200\u4e00\u67aa\u6740\u51fa\u6765\u7684\uff0c\u98ce\u8303\u6c14\u5ea6\u6709\u4f55\u7528 \u5462\uff1f\u4f60\u7528\u5175\u624d\u80fd\u80dc\u8fc7\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df2\u4e89\u53d6\u5e1d\u4f4d\u9ec4\u888d\u52a0\u8eab\u3002\u674e\u9756\uff0c\u5f53\u4ec1\u4e0d\u8ba9\u554a\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u4e00\u8138\u5f97\u610f\u4e4b\u8272\uff0c\u4f3c\u662f\u8fd9\u756a\u8bba\u8fa8\u5df2\u638c\u63e1\u667a\u73e0\uff0c\u8d62\u5b9a\u4e86\u3002 \u3000\u3000\u201c\u6211\u662f\u4e00\u5458\u4e0a\u5c06\uff0e\u5c06\u5175\u4e4b\u80fd\u591a\u591a\u76ca\u5584\u3002\u201d\u674e\u9756\u9053\uff1a \u3000\u3000\u201c\u674e\u4e16\u6c11\u5374\u662f\u5c06\u5c06\u7684\u5947\u624d\uff0c\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u90fd\u662f\u98d8\u7136\u51fa\u5c18\u7684\u4e16\u5916\u9ad8\u4eba\uff0c\u4f46\u5374\u7518\u4e3a\u4e16\u6c11\u6240 \u7528\u3002\u79e6\u743c\u3001\u656c\u5fb7\u3001\u7a0b\u77e5\u8282\u7b49\u6570\u5341\u4f4d\u9ad8\u50b2\u4e0d\u9a6f\u7684\u864e\u5c06\uff0c\u4e5f\u90fd\u80fd\u591f\u7518\u613f\u6295\u5176\u9ebe\u4e0b\uff0c\u542c\u5019\u6240\u547d\u3002\u51fa \u5c18\uff0c\u4ed6\u751f\u5177\u5148\u5929\u4e0b\u4e4b\u5fe7\u7684\u535a\u5927\u80f8\u6000\uff0c\u662f\u4eba\u4e2d\u4e4b\u9f99\u3002 \u3000\u3000\u6211\u662f\u4e07\u96be\u53ca\u5f97\uff0c\u866c\u9aef\u5ba2\u4e5f\u4e0d\u5982\u4ed6\uff1b\u8881\u5b9d\u513f\u59ff\u8272\u4e4b\u7f8e\uff0c\u5f53\u5f97\u662f\u4eba\u4e2d\u4e4b\u6700\uff0c\u4e3a\u4e86\u5929\u4e0b\u4e07\u6c11\uff0c \u4ed6\u820d\u4e86\u3002 \u3000\u3000\u5982\u679c\u8981\u6211\u820d\u4e86\u4f60\uff0c\u6211\u53ea\u6015\u4ec0\u4e48\u4e8b\u4e5f\u65e0\u6cd5\u505a\u4e86\u3002\u7ec8\u65e5\u4ee5\u9152\u6d47\u6101\uff0c\u4ee5\u6c68\u6d17\u9762\u4e86\uff0c\u53ea\u6b64\u4e00 \u6869\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u5624\u549b\u4e00\u58f0\uff0c\u6295\u5165\u674e\u9756\u6000\u4e2d\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e0d\u7528\u518d\u8bf4\u4e0b\u53bb\u4e86\uff0c\u4f60\u8d62\u4e86\u3002\u6ca1\u6709\u674e\u4e16\u6c11\u534e\u9634\u884c\u5bab\u4e2d\u4e00\u5e2d\u8bdd\uff0c\u6211\u54ea\u91cc\u80fd\u77e5\u6653\u592b\u541b\u4e00\u756a\u82e6 \u5fc3\uff1f\u7231\u59be\u4e4b\u6df1\uff0c\u60c5\u6bd4\u5929\u9ad8\u3002\u6ca1\u6709\u4e8c\u516c\u5b50\u4e00\u756a\u5f00\u5bfc\uff0c\u54ea\u8fd8\u6709\u4eca\u65e5\u592b\u5987\u7684\u548c\u7766\uff1f\u6211\u60f3\u5230\u54ea\u91cc\u53bb \u4e86\uff0c\u94bb\u5165\u4e86\u725b\u89d2\u5c16\u4e2d\u3002 \u3000\u3000\u53ea\u60f3\u4f60\u5728\u5916\u9762\u62e5\u7f8e\u81ea\u5a31\uff0c\u54ea\u8fd8\u4f1a\u60f3\u5f97\u5230\u6211\uff1f\u4e00\u65e6\u56de\u4e0d\u8fc7\u5934\uff0c\u53ea\u6709\u4e00\u6b7b\u4e86\u4e4b\u3002\u592b\u541b\u554a\uff01\u90a3 \u53ef\u662f\u542b\u6068\u4e5d\u6cc9\u7684\u60b2\u5267\u3002 \u3000\u3000\u90a3\u7b49\u5927\u6069\u672a\u62a5\u70b9\u6ef4\uff0c\u6211\u7adf\u840c\u751f\u4e86\u8d2a\u5a6a\u4e4b\u5fc3\u3002\u8d70\uff0c\u5230\u592a\u539f\u53bb\uff0c\u6211\u628a\u4eba\u3001\u94b1\u4ea4\u51fa\uff0c\u518d\u5f53\u9762\u5411 \u4ed6\u8bf7\u7f6a\uff0c\u6211\u8be5\u6b7b\u554a\uff01\u201d \u3000\u3000\u674e\u9756\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u5973\u4eba\u5584\u53d8\uff0c\u679c\u7136\u4e0d\u9519\u3002\u516d\u6708\u5929\u53d8\u5f97\u5feb\u5440\uff01\u5feb\u5f97\u8ba9\u4eba\u6765\u4e0d\u53ca\u9002\u5e94\u3002 \u3000\u3000\u674e\u9756\u60f3\u5230\u592b\u59bb\u8bef\u4f1a\u51b0\u91ca\uff0c\u786e\u662f\u674e\u4e16\u6c11\u7684\u529f\u5fb7\uff0c\u5fc3\u4e2d\u4ea6\u5f88\u611f\u52a8\u3002 \u3000\u3000\u4ed6\u62cd\u62cd\u5f20\u51fa\u5c18\u7684\u7389\u80a9\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u8d24\u59bb\u4f55\u987b\u5982\u6b64\uff0c\u4f60\u8bf4\u8fc7\uff0c\u6b64\u5730\u6ca1\u6709\u5916\u4eba\u3002\u5230\u592a\u539f\u4ea4\u51fa\u540d\u518c\u3001\u8d22\u5355\u5c31\u662f\uff0c\u6211\u4eec\u592b\u59bb\u8fd9\u70b9 \u79d8\u5bc6\u5c31\u8ba9\u5b83\u6c38\u57cb\u4f60\u6211\u5fc3\u4e2d\u3002 \u3000\u3000\u4f60\u4ec0\u4e48\u4e5f\u6ca1\u8bf4\u8fc7\uff0c\u6211\u4ec0\u4e48\u4e5f\u6ca1\u542c\u5230\uff0c\u5982\u4f60\u5411\u674e\u4e16\u6c11\u5f53\u9762\u8bf7\u7f6a\uff0c\u4f60\u7f6a\u5728\u54ea\u91cc\uff1f\u5c82\u4e0d\u662f\u5212\u86c7 \u6dfb\u8db3\uff0c\u591a\u6b64\u4e00\u4e3e\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\u554a\uff01\u201d\u5f20\u51fa\u5c18\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u592b\u59bb\u95f4\u53ea\u662f\u8bf4\u8bf4\u95f2\u8bdd\uff0c\u6211\u4e5f\u6ca1\u6709\u505a\u9519\u4ec0\u4e48\u4e8b\u554a\uff01\u4f55\u7f6a\u4e4b\u6709\uff1f \u3000\u3000\u770b\u6765\u5973\u4eba\u53ea\u80fd\u60f3\u4e00\u4ef6\u4e8b\u60c5\uff0c\u624d\u80fd\u60f3\u5f97\u4e1d\u4e1d\u5165\u6263\uff0c\u70b9\u6ef4\u4e0d\u6f0f\u3002\u4e8b\u4e00\u591a\uff0c\u5c31\u4e71\u4e86\uff0c\u4e0d\u77e5\u8be5\u5148 \u60f3\u54ea\u6837\uff1f\u60f3\u6765\u60f3\u53bb\uff0c\u4e00\u6837\u90fd\u60f3\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u4e00\u624b\u62c9\u4f4f\u5f20\u51fa\u5c18\uff0c\u7b11\u9053\uff1a \u3000\u3000\u201c\u60f3\u60f3\u53c8\u4f55\u59a8\u5462\uff1f\u8d70\uff01\u6211\u4eec\u5230\u592a\u539f\u89c1\u674e\u4e16\u6c11\u53bb\uff0c\u4f60\u9001\u7ed9\u4ed6\u8fd9\u4efd\u539a\u793c\uff0c\u8db3\u4ee5\u60ca\u5929\u52a8\u5730 \u4e86\u2026\u2026\u201d \u3000\u3000\u5f20\u51fa\u5c18\u63a5\u7740\u9053\uff1a \u3000\u3000\u201c\u8fd8\u6709\u6211\u7684\u592b\u541b\u8981\u4e3a\u4ed6\u4eb2\u5192\u77e2\u77f3\uff0c\u8f6c\u6218\u5929\u4e0b\u3002\u4e3a\u59bb\u7684\u4e5f\u53ea\u6709\u9a6c\u524d\u978d\u540e\u7684\u8ffd\u968f\u6548\u547d\u4e86\uff0c\u8d70 \u5427\uff01\u201d \u3000\u3000\u674e\u9756\u4ef0\u5929\u5927\u7b11\uff01 \u3000\u3000\u6b22\u8fce\u674e\u9756\u5230\u592a\u539f\uff0c\u4e8c\u516c\u5b50\u4eb2\u7387\u4e86\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u3001\u79e6\u743c\u3001\u7a0b\u77e5\u8282\u7b49\u6587\u3001\u6b66\u5b98\uff0c\u6570\u5341\u4eba\u8fce \u4e8e\u57ce\u5916\u3002 \u3000\u3000\u9686\u91cd\u7684\u4e0d\u50cf\u662f\u8fce\u63a5\u4e00\u4f4d\u6545\u65e7\u3001\u540d\u58eb\u4e86\u3002 \u3000\u3000\u674e\u9756\u5fc3\u4e2d\u660e\u767d\uff0c\u6545\u4f5c\u4e0d\u77e5\u3002 \u3000\u3000\u6eda\u978d\u4e0b\u9a6c\uff0c\u62dc\u4f0f\u4e8e\u5730\u3002 \u3000\u3000\u4f46\u4eba\u8fd8\u6ca1\u6709\u62dc\u4e0b\u53bb\uff0c\u5df2\u4e3a\u674e\u4e16\u6c11\u6276\u4f4f\u3002 \u3000\u3000\u674e\u4e16\u6c11\u9053\uff1a \u3000\u3000\u201c\u5144\u5f1f\uff01\u4e0d\u7528\u591a\u793c\uff0c\u63a5\u98ce\u5bb4\u5e2d\u65e9\u5df2\u6446\u597d\uff0c\u606d\u5019\u5144\u5f1f\u5165\u5e2d\u3002\u201d \u3000\u3000\u62ac\u5934\u770b\u770b\u5929\u8272\uff0c\u5348\u65f6\u5df2\u8fc7\u3002 \u3000\u3000\u6ee1\u8425\u5b98\u5458\u4f3c\u662f\u90fd\u5728\u7b49\u4ed6\u5403\u996d\u3002 \u3000\u3000\u201c\u4e8c\u516c\u5b50\u5982\u6b64\u539a\u5f85\uff0c\u6298\u6740\u6211\u4e86\uff0c\u6570\u5341\u4f4d\u524d\u8f88\u3001\u597d\u53cb\u90fd\u997f\u7740\u809a\u5b50\uff0c\u674e\u9756\u4e4b\u7f6a\u4e5f\u3002\u201d \u3000\u3000\u201c\u6211\u65e9\u5df2\u8bb8\u8fc7\u8bfa\u8a00\uff0c\u5144\u5f1f\u7ea6\u5b9a\u7684\u5f52\u671f\u4e0d\u81f3\uff0c\u4e16\u6c11\u7531\u662f\u65e5\u8d77\u4e0d\u8fdb\u996e\u98df\uff0c\u76f4\u5230\u5144\u5f1f\u5f52\u6765\u4e3a \u6b62\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u8fd9\u53eb\u674e\u9756\u5982\u4f55\u62c5\u5f53\u5f97\u8d77\u554a\uff01\u4e00\u65e6\u884c\u7a0b\u6709\u8bef\uff0c\u5c82\u4e0d\u5bb3\u4e86\u4e8c\u516c\u5b50\uff1f\u201d \u3000\u3000\u201c\u836f\u5e08\uff01\u201d\u674e\u6df3\u98ce\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u76f8\u4fe1\u4f60\u4e00\u8a00\u5982\u5c71\uff0c\u4e8c\u516c\u5b50\u66f4\u662f\u575a\u4fe1\u4f60\u4e0d\u4f1a\u9519\u8fc7\u7ea6\u671f\u3002 \u3000\u3000\u836f\u5e08\u5982\u771f\u7684\u4e0d\u5f52\u592a\u539f\uff0c\u4e8c\u516c\u5b50\u4f3c\u51c6\u5907\u7edd\u98df\u4e00\u6b7b\uff0c\u5411\u4e07\u6c11\u8c22\u7f6a\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a \u3000\u3000\u201c\u660e\u5929\u5c31\u662f\u9ec4\u9053\u5409\u65e5\uff0c\u62dc\u5e05\u4e4b\u53f0\u5df2\u4e8e\u4eca\u65e5\u5cfb\u5de5\uff0c\u660e\u65e5\u767b\u53f0\u63a5\u4e0b\u5e05\u5370\uff0c\u662f\u5426\u6025\u4fc3\u4e86\u4e00\u4e9b \u5462\uff1f\u201d \u3000\u3000\u201c\u674e\u9756\u62a5\u6548\u4e8c\u516c\u5b50\u539a\u7231\u5fc3\u5207\uff0c\u800c\u4e14\u79c1\u4e8b\u5df2\u4e86\u3002\u5c31\u7b97\u4eca\u65e5\u5bf9\u5929\u5ba3\u8a93\uff0c\u6c38\u4e3a\u4e8c\u516c\u5b50\u9ebe\u4e0b\u4e4b \u81e3\uff0c\u674e\u9756\u4ea6\u4e0d\u6562\u8f9e\u4e5f\u3002\u201d \u3000\u3000\u674e\u4e16\u6c11\u7b11\u9053\uff1a \u3000\u3000\u201c\u5144\u5f1f\uff0c\u8a00\u91cd\u4e86\u3002\u6211\u4eec\u5148\u56de\u8425\u4e2d\u8fdb\u9910\uff0c\u5929\u7f61\u3001\u6df3\u98ce\u4e24\u4f4d\u5148\u751f\u4e5f\u662f\u6ef4\u6c34\u672a\u8fdb\u3002\u5176\u4ed6\u7684\u5144\u5f1f \u6050\u90fd\u5df2\u997f\u5f97\u524d\u5fc3\u8d34\u540e\u80cc\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u6ca1\u6709\u529d\u4ed6\u4eec\u966a\u6211\uff0c\u662f\u4ed6\u4eec\u81ea\u5df1\u613f\u610f\u966a\u6211\u997f\u809a\u5b50\u554a\uff01\u201d \u3000\u3000\u8bf4\u5f97\u4f7f\u968f\u6765\u7684\u5c06\u519b\u4eec\u5931\u58f0\u5927\u7b11\uff01 \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u839e\u5c14\u3002 \u3000\u3000\u674e\u9756\u5374\u611f\u52a8\u5f97\u957f\u63d6\u62dc\u8c22\u3002 \u3000\u3000\u56de\u5165\u8425\u5e10\uff0c\u679c\u7136\u9152\u5e2d\u65e9\u5df2\u6446\u597d\uff0c\u4f73\u80b4\u7f57\u5217\u3002 \u3000\u3000\u674e\u9756\u73af\u987e\u56db\u5468\uff0c\u4e0d\u89c1\u5c09\u8fdf\u606d\uff0c\u5fcd\u4e0d\u5f80\u4f4e\u58f0\u95ee\u9053\uff1a \u3000\u3000\u201c\u656c\u5fb7\u4f55\u4ee5\u4e0d\u5728\uff1f\u201d \u3000\u3000\u4ed6\u65e9\u5df2\u77e5\u9053\u67f4\u7ecd\u5728\u4e91\u4e2d\u5c71\u6682\u4ee3\u674e\u4e16\u6c11\u8bad\u7ec3\u4e09\u5343\u94c1\u7532\u5175\uff0c\u656c\u5fb7\u662f\u4e00\u5458\u864e\u5c06\uff0c\u52c7\u731b\u5584\u6218\uff0c\u4f46 \u5374\u4e0d\u662f\u7ec3\u5175\u7684\u6750\u6599\u3002 \u3000\u3000\u674e\u4e16\u6c11\u4e00\u7728\u53cc\u76ee\uff0c\u53f9\u9053\uff1a \u3000\u3000\u201c\u4ed6\u548c\u5927\u54e5\u5efa\u6210\u6597\u53e3\u51b2\u7a81\uff0c\u5efa\u6210\u8bec\u9677\u544a\u656c\u5fb7\u52a8\u624b\uff0c\u6fc0\u6012\u7236\u738b\uff0e\u628a\u656c\u5fb7\u62ff\u95ee\u4e0b\u7262\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u95ea\u63a0\u8fc7\u4e00\u62b9\u8bb6\u5f02\u795e\u8272\uff0c\u4f46\u5f88\u5feb\u53c8\u6062\u590d\u4e86\u5e73\u9759\u3002 \u3000\u3000\u674e\u9756\u7b11\u9053\uff1a \u3000\u3000\u201c\u656c\u5fb7\u529b\u80fd\u51fb\u6bd9\u864e\u3001\u725b\uff0c\u5efa\u6210\u5927\u516c\u5b50\u80fd\u627f\u53d7\u4e00\u62f3\u800c\u5b89\u7136\u65e0\u6059\uff0c\u4e5f\u662f\u6b66\u529f\u9ad8\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u656c\u5fb7\u54ea\u91cc\u771f\u6b63\u51fa\u62f3\uff01\u201d\u674e\u4e16\u6c11\u9053\uff1a \u3000\u3000\u201c\u4ed6\u53ea\u662f\u63e1\u62f3\u4e3e\u4e86\u4e00\u4e0b\uff0c\u5bb6\u5144\u4e00\u53e3\u54ac\u5b9a\u656c\u5fb7\u51fa\u62f3\u6253\u4ed6\uff0c\u4e5f\u6709\u4eba\u770b\u5230\u656c\u5fb7\u4e3e\u8d77\u4e86\u62f3\u5934\uff0c\u5c31 \u8fd9\u6837\u88ab\u542b\u51a4\u4e0b\u72f1\u4e86\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u63d2\u8a00\u9053\uff1a \u3000\u3000\u201c\u5c09\u8fdf\u606d\u8be5\u6709\u767e\u65e5\u7262\u72f1\u4e4b\u707e\uff0c\u5bf9\u4ed6\u6709\u5229\u65e0\u5bb3\u3002\u201d \u3000\u3000\u201c\u5982\u975e\u8881\u5148\u751f\u4ee5\u6b64\u76f8\u529d\uff0c\u4e16\u6c11\u5fc5\u5c06\u636e\u7406\u529b\u4e89\uff0c\u4e3a\u656c\u5fb7\u5f00\u8131\u3002\u201d \u3000\u3000\u674e\u9756\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8881\u5148\u751f\u535c\u7b97\u4e4b\u80fd\uff0c\u795e\u51c6\u65e0\u6bd4\uff0c\u4e8c\u516c\u5b50\u5c31\u4e0d\u7528\u628a\u6b64\u4e8b\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u53ea\u5b89\u5c45\u592a\u539f\u4e00\u9685\uff0c\u5144\u5f1f\u4e4b\u95f4\u4f3c\u5df2\u5f00\u59cb\u4e86\u593a\u6743\u593a\u52bf\u4e4b\u4e89\uff0c\u4e00\u65e6\u53d6\u5f97\u5929\u4e0b\uff0c\u8fd9\u4e00\u573a\u5144\u5f1f\u7684 \u706b\u5e76\uff0c\u53ea\u6015\u662f\u65e0\u6cd5\u907f\u514d\u4e86\u3002\u4e8c\u516c\u5b50\u8981\u6211\u63a5\u638c\u5e05\u5370\uff0c\u6b64\u540e\u5efa\u6210\u3001\u5143\u5409\u5fc5\u5c06\u89c6\u6211\u5982\u773c\u4e2d\u4e4b\u9489\uff0c\u53bb \u4e4b\u800c\u540e\u5feb\uff0c\u6b64\u4e8b\u5012\u8981\u65e9\u4f5c\u9884\u9632\u3002\u201d \u3000\u3000\u674e\u9756\u767b\u53f0\u62dc\u5e05\u662f\u4e00\u4ef6\u8f70\u52a8\u592a\u539f\u7684\u5927\u4e8b\u3002 \u3000\u3000\u674e\u6e0a\u6765\u4e86\uff0c\u5efa\u6210\u548c\u5143\u5409\u5374\u6258\u75c5\u672a\u5230\u3002 \u3000\u3000\u8881\u5929\u7f61\u4fdf\u674e\u9756\u63a5\u4e86\u5e05\u5370\uff0c\u6084\u7136\u884c\u8fd1\u674e\u6e0a\uff0c\u9053\uff1a \u3000\u3000\u201c\u4faf\u7237\uff0c\u4f60\u540c\u610f\u4e8c\u516c\u5b50\u8058\u7ea6\u674e\u9756\u4e3a\u5e05\uff0c\u53ef\u77e5\u9053\u8fd9\u4e2a\u540e\u679c\u5417\uff1f\u201d \u3000\u3000\u674e\u6e0a\u4e5f\u77e5\u9053\u8881\u5929\u7f61\u3001\u674e\u6df3\u98ce\u662f\u4e16\u5916\u9ad8\u4eba\uff0c\u5bf9\u4e24\u4eba\u6709\u7740\u76f8\u5f53\u7684\u656c\u91cd\u3002 \u3000\u3000\u4ed6\u7591\u7136\u7684\u9053\uff1a\u201c\u4ec0\u4e48\u540e\u679c\uff1f\u8fd8\u8bf7\u5148\u751f\u8bf4\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u8881\u5929\u7f61\u9053\uff1a\u201c\u7531\u4eca\u5929\u5f00\u59cb\uff0c\u592a\u539f\u5df2\u4e3e\u8d77\u4e86\u9020\u53cd\u7684\u65d7\u5e1c\uff0c\u8fd9\u53ef\u662f\u6284\u5bb6\u706d\u65cf\u7684\u5927\u7f6a\u3002\u4e8b\u60c5\u4f20 \u5165\u957f\u5b89\uff0c\u4e5f\u8bb8\u4f1a\u6fc0\u6012\u7687\u4e0a\uff0c\u8c03\u52a8\u5927\u519b\u5f81\u527f\u3002\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u674e\u6e0a\u6709\u70b9\u614c\u5f20\u5730\u9053\uff1a \u3000\u3000\u201c\u5148\u751f\u8bf4\u7684\u662f\u554a\uff01\u592a\u539f\u8fd9\u5f39\u4e38\u4e4b\u5730\uff0c\u5982\u4f55\u80fd\u548c\u4e3e\u56fd\u5927\u519b\u5bf9\u6297\uff1f \u3000\u3000\u4e00\u65e6\u767e\u4e07\u5927\u519b\u6740\u5230\uff0c\u592a\u539f\u4e0d\u8db3\u4e94\u4e07\u4eba\u9a6c\uff0c\u5982\u4f55\u80fd\u591f\u62b5\u5fa1\uff0c\u5148\u751f\u4f55\u4ee5\u6559\u6211\uff1f\u201d \u3000\u3000\u201c\u4e8b\u5df2\u6210\u771f\u56de\u5934\u96be\uff1f\u201d\u8881\u5929\u7f61\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e9b\u5c06\u5e05\u519b\u5175\u90fd\u662f\u51b2\u7740\u4e8c\u516c\u5b50\u6765\u7684\uff0c\u4e8b\u7531\u4ed6\u8d77\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u674e\u6e0a\u9053\uff1a \u3000\u3000\u201c\u641e\u5230\u4eca\u65e5\u8fd9\u4e2a\u5c40\u9762\uff0c\u5168\u7531\u4e16\u6c11\u4e5f\uff0c\u628a\u8fd9\u4e2a\u5974\u624d\u6293\u8d77\u6765\u4e0b\u5165\u5927\u7262\uff0c\u4e00\u65e6\u671d\u5ef7\u5174\u5175\u800c\u6765\uff0c \u6211\u5c31\u628a\u4e16\u6c11\u732e\u51fa\uff0c\u7528\u4ed6\u4e00\u4eba\u4e4b\u547d\u6551\u6211\u674e\u6c0f\u4e00\u65cf\u3002\u201d \u3000\u3000\u4e24\u4eba\u8c08\u8bdd\u7684\u58f0\u97f3\u4e0d\u5927\uff0c\u8ddd\u79bb\u5e05\u53f0\u6570\u4e08\u4e4b\u5916\uff0c\u9664\u4e86\u674e\u6e0a\u4e24\u4e2a\u8fd1\u8eab\u4f8d\u536b\u4e4b\u5916\uff0c\u522b\u4eba\u4e5f\u65e0\u6cd5\u542c \u5230\u3002 \u3000\u3000\u201c\u6211\u548c\u6df3\u98ce\u53ef\u4ee5\u62cd\u624b\u79bb\u53bb\u3002\u201d\u8881\u5929\u7f61\u9053\u3002 \u3000\u3000\u201c\u4f46\u6570\u5341\u5458\u52c7\u731b\u5584\u6218\u7684\u864e\u5c06\u5c31\u5f88\u96be\u5904\u7f6e\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u201d\u674e\u6e0a\u6012\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u8fd8\u6562\u9020\u53cd\u5417\uff1f\u6211\u628a\u4ed6\u4eec\u5168\u90e8\u6293\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6765\uff0c\u4ed6\u4eec\u4e0d\u53cd\u4e5f\u4e0d\u884c\u4e86\u3002\u201d\u8881\u5929\u7f61\u7b11\u9053\uff1a \u3000\u3000\u201c\u4faf\u5e9c\u4e2d\u6570\u767e\u4f4d\u5bb6\u4e01\u4e5f\u8bb8\u80af\u542c\u5019\u7237\u7684\uff0c\u5e9c\u5916\u5927\u519b\u3001\u52c7\u5c06\u5c31\u672a\u5fc5\u53d7\u4f60\u9a7e\u9a6d\u4e86\uff0c\u4ed6\u4eec\u6025\u6012\u4e4b \u4e0b\uff0c\u6740\u5165\u4faf\u5e9c\uff0c\u8c01\u53c8\u80fd\u591f\u9547\u5f97\u4f4f\u5462\uff1f\u201d \u3000\u3000\u674e\u6e0a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5148\u751f\u8bf4\u7684\u662f\uff0e\u4eca\u540e\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4e8c\u516c\u5b50\u662f\u5c06\u5c06\u4e4b\u624d\uff01\u201d\u8881\u5929\u7f61\u9053\uff1a \u3000\u3000\u201c\u5929\u4e0b\u82f1\u96c4\u65e0\u4e0d\u656c\u6155\uff0c\u4faf\u7237\u4f55\u4e0d\u653e\u624b\uff0c\u8ba9\u4e8c\u516c\u5b50\u4e3b\u6301\u5176\u4e8b\uff0c\u4e94\u5e74\u4e4b\u5185\uff0c\u4faf\u7237\u5f53\u53ef\u9ec4\u888d\u52a0 \u8eab\u3002\u201d \u3000\u3000\u674e\u6e0a\u70b9\u70b9\u5934\uff0c\u6ca1\u6709\u5f00\u53e3\uff0c\u4eba\u5374\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u7b54\u5e94\u4e86\uff0c\u4f46\u53c8\u975e\u5fc3\u7518\u60c5\u613f\u3002\u770b\u6765\u8981\u4e00\u4e2a\u4eba\u653e\u5f03\u6743\u52bf\uff0c\u5b9e\u975e\u6613\u4e8b\uff0c\u867d\u4eb2\u5982\u7236\u5b50\uff0c\u4ea6 \u4e0d\u613f\u5927\u6743\u65c1\u843d\u3002 \u3000\u3000\u8881\u5929\u7f61\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6211\u672c\u60f3\u65e9\u65e5\u4f7f\u4e8c\u516c\u5b50\u5efa\u7acb\u8d77\u5a01\u671b\uff0c\u6d88\u53bb\u5efa\u6210\u3001\u5143\u5409\u4e89\u593a\u6743\u4f4d\u4e4b\u5fc3\uff0c\u770b\u6765\u662f\u5929\u610f\u96be\u8fdd\uff0c \u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u5f88\u60f3\u628a\u8881\u5b9d\u513f\u7684\u4e8b\u544a\u8bc9\u674e\u4e16\u6c11\uff0c\u4f46\u4ed6\u8fd8\u662f\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5374\u8f6c\u544a\u4e86\u8881\u7d2b\u70df\u7684\u8bf7\u6c42\uff0c\u4e09\u5e74\u5185\u7ec3\u5175\u4e0d\u52a8\u5175\uff0c\u4e5f\u5938\u4e0b\u4e86\u6d77\u53e3\uff0c\u5927\u519b\u4e00\u52a8\uff0c\u4e24\u4e09\u5e74\u5185\u5373 \u53ef\u5e95\u5b9a\u5929\u4e0b\u3002 \u3000\u3000\u674e\u4e16\u6c11\u6709\u4e9b\u4e0d\u76f8\u4fe1\uff0c\u4f46\u8fd8\u662f\u7b54\u5e94\u4e86\u674e\u9756\u7684\u8bf7\u6c42\u3002 \u3000\u3000\u5f20\u51fa\u5c18\u732e\u51fa\u4e86\u5175\u518c\u3001\u85cf\u91d1\u3002 \u3000\u3000\u5979\u5e76\u4e14\u8bf4\u660e\u4e86\u662f\u866c\u9aef\u5ba2\u51c6\u5907\u4e89\u9738\u5929\u4e0b\u7684\u5e03\u7f72\uff0c\u4ed6\u51b3\u5fc3\u9000\u51fa\u4e2d\u539f\u9738\u4e1a\u4e4b\u4e89\uff0c\u8f6c\u9001\u7ed9\u674e\u4e16\u6c11 \u4e86\u3002 \u3000\u3000\u8fd9\u4f7f\u674e\u4e16\u6c11\u5fc3\u4e2d\u5bf9\u866c\u9aef\u5ba2\u7684\u770b\u6cd5\u6709\u4e86\u6539\u53d8\uff0c\u8fd9\u4e2a\u4eba\u867d\u7136\u9738\u9053\u4e00\u4e9b\uff0c\u5374\u80fd\u987a\u5929\u5e94\u65f6\u3002\u5fc3\u4e2d \u4e5f\u9ed8\u8bb8\u4e86\u7531\u4ed6\u5f00\u62d3\u897f\u7586\uff0c\u81ea\u7acb\u738b\u671d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u674e\u9756\u624d\u8bf4\u51fa\u4e86\u4e09\u5e74\u7684\u8ba1\u5212\u3002 \u3000\u3000\u4ed6\u867d\u7136\u63a5\u638c\u4e86\u5e05\u5370\uff0c\u4f46\u4e0d\u80fd\u5e38\u4f4f\u592a\u539f\u3002 \u3000\u3000\u4ed6\u8981\u5c3d\u4e09\u5e74\u4e4b\u5185\u8fde\u7edc\u866c\u9aef\u5ba2\u7559\u5728\u4e2d\u539f\u7684\u5341\u4e07\u7532\u5175\uff0c\u5e76\u628a\u4ed6\u4eec\u91cd\u65b0\u7ec4\u7ec7\u8d77\u6765\uff0c\u5206\u5e03\u5341\u4e2a\u91cd \u8981\u6240\u5728\uff0c\u592a\u539f\u5927\u519b\u5df2\u52a8\uff0c\u8fd9\u5341\u5904\u540c\u65f6\u8d77\u5175\u54cd\u5e94\u3002 \u3000\u3000\u674e\u4e16\u6c11\u70b9\u70b9\u5934\uff0c\u6697\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u4ed6\u65e9\u5df2\u6210\u7af9\u5728\u80f8\u4e86\uff0c\u52ff\u602a\u6562\u5938\u4e0b\u6d77\u53e3\uff0c\u4e24\u4e09\u5e74\u5e95\u5b9a\u4e2d\u539f\u4e86\u3002\u201d \u3000\u3000\u674e\u9756\u548c\u5f20\u51fa\u5c18\u7559\u592a\u539f\u5c0f\u606f\u4e09\u5929\uff0c\u5373\u53c8\u52a8\u8eab\u3002 \u3000\u3000\u7b2c\u4e94\u5929\u7834\u5929\u5251\u9ec4\u4e91\u5374\u4e13\u7a0b\u6765\u63a2\u89c6\u674e\u9756\uff0c\u60f3\u6c42\u6559\u4e00\u4e9b\u5f00\u8f9f\u897f\u57df\u7684\u65b9\u7565\u3002 \u3000\u3000\u8fd9\u4e24\u5929\u4e4b\u5dee\uff0c\u9519\u8fc7\u4e86\u6700\u540e\u4e00\u9762\u3002 \u3000\u3000\u968b\u7080\u5e1d\u51c6\u5907\u4e1c\u4e0a\u7684\u6d88\u606f\uff0c\u7ec8\u4e8e\u4f20\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5e26\u4e86\u7530\u5f53\u3001\u8427\u96e8\u3001\u51cc\u4e91\u7b49\u4e94\u4f4d\u526f\u7edf\u9886\u548c\u4e09\u5343\u5bab\u536b\uff0c\u968f\u884c\u62a4\u9a7e\u3002\u8427\u7687\u540e\u548c\u5341\u4e8c\u9662 \u592b\u4eba\u7cbe\u9009\u4e86\u516d\u767e\u540d\u5b98\u5a25\u968f\u884c\uff0c\u8f66\u9a6c\u903e\u8fe5\u4e86\u6570\u5341\u91cc\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5f88\u60f3\u7559\u5728\u957f\u5b89\uff0c\u60f3\u591a\u770b\u770b\u8881\u7d2b\u70df\u3002\u4f46\u4ed6\u548c\u968b\u7080\u5e1d\u76f8\u8bb8\u7684\u4ea4\u60c5\uff0c\u4f7f\u4ed6\u65e0\u6cd5\u62d2\u7edd\u7687 \u4e0a\u7684\u610f\u65e8\u3002 \u3000\u3000\u8881\u5b9d\u513f\u8f9e\u522b\u8881\u7d2b\u70df\u65f6\uff0c\u795e\u8272\u5982\u5e38\u3002 \u3000\u3000\u5979\u5fae\u7b11\u5982\u82b1\u5730\u9053\uff1a \u3000\u3000\u201c\u7687\u4e0a\u5f85\u6211\u6069\u5ba0\u4e09\u5343\uff0c\u6211\u51b3\u5fc3\u8ddf\u4ed6\u4e1c\u6e38\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u70df\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u5b9d\u513f\uff0c\u56de\u592a\u539f\u53bb\u5427\uff01\u7687\u4e0a\u8ffd\u95ee\u4e0b\u6765\uff0c\u5c31\u7531\u59d0\u59d0\u4e00\u529b\u627f\u62c5\uff0c\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u51fa\u5175\u8bc1\u4f10\u592a\u539f \u7684\u3002\u201d \u3000\u3000\u201c\u54ea\u91cc\u8fd8\u6709\u5175\u53ef\u7528\u554a\uff1f\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u79f0\u5f97\u4e0a\u7cbe\u9510\u7684\u5c31\u662f\u5b87\u6587\u6210\u90fd\u624b\u4e0b\u51e0\u5343\u4eba\uff0c\u5927\u968b\u671d\u6ca1\u6709\u5012\u4e0b\u53bb\uff0c\u662f\u9760\u59d0\u59d0\u4e00\u67f1\u64ce\u5929\u3002\u4f60 \u4e0d\u8d70\uff0c\u6ca1\u6709\u4eba\u80fd\u5165\u957f\u5b89\u3002 \u3000\u3000\u81f3\u4e8e\u6211\u8ddf\u7687\u4e0a\u8d70\uff0c\u5168\u51fa\u81ea\u613f\u3002\u6211\u65e0\u989c\u518d\u89c1\u674e\u4e16\u6c11\uff0c\u4e5f\u4e0d\u80fd\u518d\u56de\u5927\u539f\uff0c\u4ed6\u4eec\u8981\u6c42\u6211\u7684\u4e8b\uff0c \u6211\u6ca1\u6709\u505a\u5230\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u662f\u6211\u5bb3\u4e86\u4f60\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u6765\u957f\u5b89\u5bab\u9662\uff0c\u4f60\u968f\u65f6\u53ef\u4ee5\u6740\u4e86\u7687\u4e0a\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\u5566\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e00\u591c\u6211\u8ddf\u4ed6\u9f99\u5e8a\u4e91\u96e8\u4f1a\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u6ce8\u5b9a\u4e86\u6211\u7684\u547d\u8fd0\uff0c\u6211\u60f3\u96c6\u4ed6\u5ba0\u7231\u4e8e\u4e00\u8eab\uff0c\u5f88\u8ba4\u771f \u7684\u53d6\u60a6\u4ed6\uff0c\u6467\u6bc1\u4e86\u6211\u8584\u5f31\u7684\u9053\u57fa\uff0c\u6211\u5df2\u7ecf\u662f\u4e2a\u672f\u6cd5\u5c06\u5931\u7684\u5f31\u5973\u5b50\uff0c\u8fd8\u80fd\u6709\u4ec0\u4e48\u4f5c\u4e3a\u5462\uff1f\u201d \u3000\u3000\u201c\u5b9d\u513f\uff01\u201d\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u73b0\u5728\u8865\u6551\u8fd8\u6765\u5f97\u53ca\uff0c\u6211\u5168\u529b\u5e2e\u52a9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\u4e86\uff01\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u6211\u65e0\u6cd5\u575a\u62d2\u7687\u4e0a\u7684\u6c42\u7231\uff0c\u4e0d\u80fd\u9759\u4e0b\u6765\u91cd\u4fee\u9053\u57fa\uff0c\u6240\u4ee5\u8fd9\u4e00\u751f\u6211\u53ea\u6709\u8ddf\u7740\u4ed6\u4e86\uff0c\u4f5c\u4e00\u4e2a \u53d6\u60a6\u7687\u4e0a\u7684\u5c0f\u5973\u4eba\u3002\u5f80\u4e8b\u6d88\u5931\u5982\u4e91\u70df\uff0c\u518d\u56de\u9996\u4e3a\u65f6\u5df2\u665a\uff0c\u6625\u8695\u5230\u6b7b\u4e1d\u65b9\u5c3d\u561b\uff01\u7d2b\u70df\u59d0\uff0c\u4eca\u65e5 \u4e00\u522b\uff0c\u6050\u662f\u65e0\u7f18\u518d\u89c1\u4e86\u3002\u201d \u3000\u3000\u8881\u7d2b\u70df\u9053\uff1a \u3000\u3000\u201c\u4e09\u5e74\u540e\uff0c\u6211\u56de\u7ec8\u5357\u5c71\uff0c\u82e6\u4fee\u4e24\u5e74\uff0c\u6211\u76f8\u4fe1\u80fd\u52a9\u4f60\u91cd\u590d\u9053\u57fa\u3002 \u3000\u3000\u5b9d\u513f\uff0c\u6211\u4eec\u8ba2\u4e00\u4e2a\u4e94\u5e74\u4e4b\u7ea6\uff0c\u6211\u63a5\u4f60\u4e0a\u7ec8\u5357\u5c71\u53bb\u3002\u90a3\u513f\u592a\u9759\u4e86\uff0c\u6709\u4e86\u4f60\uff0c\u6211\u53ef\u4ee5\u89e3\u53bb\u4e0d \u5c11\u7684\u5bc2\u5bde\uff0c\u6211\u4eec\u59d0\u59b9\u4fe9\u4e00\u8d77\u4fee\u7ec3\u3002\u201d \u3000\u3000\u201c\u4f46\u613f\u6211\u80fd\u6709\u8fd9\u4e2a\u798f\u4efd\u3002\u201d\u8881\u5b9d\u513f\u9053\uff1a \u3000\u3000\u201c\u7d2b\u70df\u59d0\uff0c\u592a\u76d1\u5df2\u6765\u50ac\u4e86\uff0c\u73cd\u91cd\u4e86\uff0c\u7d2b\u70df\u59d0\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u8d77\u8eab\u5411\u5916\u884c\u53bb\uff0c\u4e24\u4e2a\u592a\u76d1\u65e9\u5df2\u5728\u95e8\u5916\u5019\u9a7e\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u9001\u51fa\u95e8\u5916\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u3000\u3000\u201c\u5b9d\u513f\uff0c\u522b\u5fd8\u4e86\u4e94\u5e74\u4e4b\u7ea6\u5440\uff01\u201d \u3000\u3000\u8881\u5b9d\u513f\u6ca1\u6709\u56de\u7b54\uff0c\u4e58\u4e0a\u8f66\u8f87\u884c\u53bb\uff0c\u5fc3\u60c5\u4e0d\u597d\uff0c\u61d2\u5f97\u8d70\u8def\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u662f\u5750\u4e0a\u8f66\u8f87\u62ed\u773c\u6cea\uff0c\u53ef\u60dc\u8881\u7d2b\u70df\u6ca1\u6709\u770b\u5230\u3002 \u3000\u3000\u8f66\u961f\u767b\u4e0a\u79e6\u5cad\u3002 \u3000\u3000\u8881\u5b9d\u513f\u4e58\u5750\u7684\u9a6c\u8f66\u7a81\u7136\u9a6c\u592b\u524d\u8e44\uff0c\u6eda\u4e0b\u4e86\u4e07\u4e08\u6df1\u8c37\u3002 \u3000\u3000\u7ad9\u5728\u8fb9\u7f18\u5f80\u4e0b\u770b\u2014\u2014 \u3000\u3000\u96fe\u6c89\u6c89\uff0c\u9ed1\u5e7d\u5e7d\uff0c\u666f\u7269\u96be\u89c1\uff0c\u522b\u8bf4\u662f\u4eba\u4e86\uff0c\u5c31\u7b97\u662f\u4e00\u5757\u77f3\u5934\uff0c\u5982\u6b64\u9ad8\u7684\u5730\u65b9\u6389\u4e0b\u53bb\uff0c\u4e5f \u8981\u88ab\u649e\u6210\u7c89\u788e\u3002 \u3000\u3000\u968b\u7080\u5e1d\u5931\u58f0\u75db\u54ed\uff01 \u3000\u3000\u4ed6\u662f\u771f\u7684\u75bc\u7231\u8881\u5b9d\u513f\u3002 \u3000\u3000\u8427\u7687\u540e\u548c\u5341\u4e8c\u9662\u592b\u4eba\u62c9\u4f4f\u4ed6\uff0c\u5b87\u6587\u6210\u90fd\u548c\u7530\u5f53\u4e5f\u5b88\u5728\u8eab\u8fb9\uff0c\u8fd9\u4e2a\u7687\u5e1d\u7adf\u6709\u8df3\u4e0b\u60ac\u5d16\u7684\u51b2 \u52a8\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u6709\u90a3\u4e48\u591a\u4eba\u62c9\u4f4f\u4ed6\uff0c\u771f\u6709\u53ef\u80fd\u8df3\u5d16\u6b89\u60c5\u3002 \u3000\u3000\u4e07\u4e08\u60ac\u5d16\u4e0a\u4e0b\u96be\uff0c\u4f46\u968b\u7080\u5e1d\u575a\u6301\u8981\u4e0b\u53bb\u5bfb\u627e\u3002 \u3000\u3000\u751f\u8981\u89c1\u4eba\uff0c\u6b7b\u8981\u89c1\u5c38\u3002 \u3000\u3000\u4ed6\u8bf4\uff1a\u5b9d\u513f\u4f1a\u672f\u6cd5\uff0c\u4e5f\u8bb8\u80fd\u9003\u8fc7\u52ab\u96be\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u53ea\u597d\u5e26\u7740\u7530\u5f53\u3001\u8427\u96e8\u4e0b\u6df1\u8c37\u3002 \u3000\u3000\u5b87\u6587\u6210\u90fd\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5979\u672f\u6cd5\u9ad8\u5f3a\uff0c\u600e\u4e48\u4f1a\u8dcc\u4e0b\u6df1\u8c37\uff1f\u8fd9\u5206\u660e\u662f\u81ea\u6740\u5440\uff01\u5979\u8981\u9b42\u7559\u957f\u5b89\uff0c\u820d\u4e0d\u5f97\u674e\u4e16\u6c11 \u5427\u2026\u2026\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u5374\u4e0d\u6562\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u8fd9\u4e07\u4e08\u5ced\u58c1\u4f7f\u4e09\u4e2a\u6b66\u6797\u9ad8\u624b\u4e5f\u8d70\u5f97\u5fc3\u60ca\u80c6\u98a4\u3002 \u3000\u3000\u4e0b\u5230\u8c37\u5e95\uff0c\u4e5f\u89c1\u5230\u4e86\u5c38\u4f53\uff0c\u4f46\u5df2\u8840\u8089\u6a21\u7cca\uff0c\u9762\u76ee\u96be\u8fa8\u3002\u5b87\u6587\u6210\u90fd\u5e26\u4e0a\u4e00\u4e9b\u6563\u843d\u7684\u73e0\u5b50\u548c \u788e\u88c2\u8863\u670d\u3002 \u3000\u3000\u968b\u7080\u5e1d\u8ba4\u51fa\u90a3\u662f\u4ed6\u9001\u7ed9\u5b9d\u513f\u7684\u660e\u73e0\uff0c\u8427\u7687\u540e\u4e5f\u8ba4\u51fa\u662f\u5b9d\u513f\u7a7f\u7684\u8863\u670d\u3002 \u3000\u3000\u662f\u771f\u7684\u9999\u6d88\u79e6\u5cad\u3001\u7389\u6c89\u5e7d\u8c37\u4e86\u3002 \u3000\u3000\u4e00\u4ee3\u4eba\u95f4\u7edd\u8272\uff0c\u5c31\u6b64\u522b\u79bb\u4e86\u4eba\u95f4\u3002 \u3000\u3000\u6d88\u606f\u4f20\u5230\u592a\u539f\uff0c\u674e\u4e16\u6c11\u4e09\u65e5\u591c\u6ef4\u6c34\u4e0d\u8fdb\uff0c\u4e00\u76f4\u8dea\u5728\u8881\u5b9d\u513f\u7684\u753b\u50cf\u524d\uff0c\u4f24\u5fc3\u767e\u6298\uff0c\u6094\u6068\u4e07 \u5343\u3002 \u3000\u3000\u674e\u6df3\u98ce\u3001\u8881\u5929\u7f61\u8d39\u4e86\u65e0\u6570\u53e3\u820c\uff0c\u624d\u529d\u6b62\u4e86\u674e\u4e16\u6c11\u7684\u4f24\u75db\u3002 \u3000\u3000\u62ef\u4e07\u6c11\u4e8e\u6c34\u706b\u4e4b\u4e2d\uff0c\u662f\u4ed6\u7684\u5fd7\u613f\uff0c\u4e0d\u5141\u8bb8\u4ed6\u6709\u592a\u591a\u7684\u513f\u5973\u60c5\u957f\u3002 \u3000\u3000\u8881\u7d2b\u70df\u4e0d\u76f8\u4fe1\u8881\u5b9d\u513f\u4f1a\u88ab\u6454\u6b7b\uff0c\u4eb2\u8d74\u6df1\u8c37\u5bfb\u89c5\uff0c\u4f46\u4e5f\u53ea\u627e\u51fa\u4e00\u4e9b\u5c38\u8eab\u4f59\u8ff9\u548c\u8863\u7269\u3001\u660e\u73e0 \u4e4b\u7c7b\u3002 \u3000\u3000\u8881\u7d2b\u70df\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u8dea\u5728\u6df1\u8c37\u4e2d\u653e\u58f0\u5927\u54ed\u4e86\uff01 \u3000\u3000\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u5b9d\u513f\u4e4b\u6b7b\u662f\u81ea\u6740\uff0c\u4f46\u5927\u534a\u90fd\u662f\u5979\u5bb3\u7684\uff0c\u5979\u82e5\u4e0d\u4e0b\u7ec8\u5357\u5c71\uff0c\u4e0d\u8fdb\u5165\u957f\u5b89\u5bab \u9662\uff0c\u8881\u5b9d\u513f\u4e0d\u4f1a\u6b7b\u3002 \u3000\u3000\u4e0d\u8bba\u591a\u5c11\u4f24\u60c5\u3001\u6cea\u75d5\uff0c\u4e5f\u627e\u4e0d\u56de\u8881\u5b9d\u513f\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u70df\u6697\u6697\u53d1\u8a93\uff0c\u9547\u5b88\u957f\u5b89\u4e09\u5e74\u7684\u8bfa\u8a00\u4e00\u6ee1\uff0c\u7acb\u523b\u56de\u7ec8\u5357\u5c71\u53bb\uff0c\u4e0d\u518d\u7559\u604b\u8fd9\u4eba\u95f4\u7684\u7e41\u534e \u7b19\u6b4c\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);