"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[36927],{96774:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c\u4e00\u7ae0\u3000\u98de\u5200\u4e0e\u5feb\u5251",content:(0,s.jsx)(s.Fragment,{children:"\u51b7\u98ce\u5982\u5200\uff0c\u4ee5\u5927\u5730\u4e3a\u7827\u677f\uff0c\u89c6\u4f17\u751f\u4e3a\u9c7c\u8089\u3002 \u3000\u3000\u4e07\u91cc\u98de\u96ea\uff0c\u5c06\u82cd\u7a79\u4f5c\u6d2a\u7089\uff0c\u6eb6\u4e07\u7269\u4e3a\u767d\u94f6\u3002 \u3000\u3000\u96ea\u5c06\u4f4f\uff0c\u98ce\u672a\u5b9a\uff0c\u4e00\u8f86\u9a6c\u8f66\u81ea\u5317\u800c\u6765\uff0c\u6eda\u52a8\u7684\u8f66\u8f6e\u78be\u788e\u4e86\u5730\u4e0a\u7684\u51b0\u96ea\uff0c\u5374\u78be\u4e0d\u788e\u5929\u5730\u95f4 \u7684\u5bc2\u5bde\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6253\u4e86\u4e00\u4e2a\u54c8\u6b20\uff0c\u5c06\u4e24\u6761\u957f\u817f\u5728\u67d4\u8f6f\u7684\u8c82\u76ae\u4e0a\u5c3d\u91cf\u4f38\u76f4\uff0c\u8f66\u7bb1\u91cc\u867d\u7136\u5f88\u6e29\u6696\u5f88\u8212\u670d \uff0c\u4f46\u8fd9\u6bb5\u65c5\u9014\u5b9e\u5728\u592a\u957f\uff0c\u592a\u5bc2\u5bde\uff0c\u4ed6\u4e0d\u4f46\u5df2\u89c9\u5f97\u75b2\u5026\uff0c\u800c\u4e14\u89c9\u5f97\u5f88\u538c\u6076\uff0c\u4ed6\u5e73\u751f\u538c\u6076\u7684\u5c31\u662f \u5bc2\u5bde\uff0c\u4f46\u4ed6\u5374\u504f\u504f\u65f6\u5e38\u4e0e\u5bc2\u5bde\u4e3a\u4f0d\u3002 \u3000\u3000\u4eba\u751f\u672c\u5c31\u5145\u6ee1\u4e86\u77db\u76fe\uff0c\u4efb\u4f55\u4eba\u90fd\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u89d2\u843d\u4e2d\u6478\u51fa\u4e86\u4e2a\u9152\u74f6\uff0c\u4ed6\u5927\u53e3\u7684\u559d\u7740\u9152\u65f6\uff0c\u4e5f\u5927\u58f0\u5730\u54b3\u55fd\u8d77\u6765\uff0c\u4e0d \u505c\u7684\u54b3\u55fd\u4f7f\u5f97\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u6cdb\u8d77\u4e00\u79cd\u75c5\u6001\u7684\u5ae3\u7ea2\uff0c\u5c31\u4eff\u4f5b\u5730\u72f1\u4e2d\u7684\u706b\u7130\uff0c\u6b63\u5728\u711a\u70e7\u7740\u4ed6\u7684 \u8089\u4f53\u4e0e\u7075\u9b42\u3002 \u3000\u3000\u9152\u74f6\u7a7a\u4e86\uff0c\u4ed6\u5c31\u62ff\u8d77\u628a\u5c0f\u5200\uff0c\u5f00\u59cb\u96d5\u523b\u4e00\u4e2a\u4eba\u50cf\uff0c\u5200\u950b\u8584\u800c\u950b\u9510\uff0c\u4ed6\u7684\u624b\u6307\u4fee\u957f\u800c\u6709\u529b \u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u5973\u4eba\u7684\u4eba\u50cf\uff0c\u5728\u4ed6\u7eaf\u719f\u7684\u624b\u6cd5\u4e0b\uff0c\u8fd9\u4eba\u50cf\u7684\u8f6e\u5ed3\u548c\u7ebf\u6761\u770b\u6765\u662f\u90a3\u4e48\u67d4\u548c\u800c\u4f18\u7f8e\uff0c \u770b\u6765\u5c31\u8c61\u662f\u6d3b\u7684\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u7ed9\u4e86\u5979\u52a8\u4eba\u7684\u7ebf\u6761\uff0c\u4e5f\u7ed9\u4e86\u5979\u751f\u547d\u548c\u7075\u9b42\uff0c\u53ea\u56e0\u4ed6\u7684\u751f\u547d\u548c\u7075\u9b42\u5df2\u6084\u6084\u5730\u81ea\u5200\u950b \u4e0b\u6e9c\u8d70\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u518d\u5e74\u8f7b\u3002 \u3000\u3000\u4ed6\u773c\u89d2\u5e03\u6ee1\u4e86\u76b1\u7eb9\uff0c\u6bcf\u4e00\u6761\u76b1\u7eb9\u90fd\u84c4\u6ee1\u4e86\u4ed6\u751f\u547d\u4e2d\u7684\u5fe7\u60a3\u548c\u4e0d\u5e78\uff0c\u53ea\u6709\u4ed6\u7684\u773c\u775b\u5374\u662f\u5e74 \u8f7b\u7684\u3002 \u3000\u3000\u8fd9\u662f\u53cc\u5947\u5f02\u7684\u773c\u775b\uff0c\u7adf\u4eff\u4f5b\u662f\u78a7\u7eff\u8272\u7684\uff0c\u4eff\u4f5b\u6625\u98ce\u5439\u52a8\u7684\u67f3\u679d\uff0c\u6e29\u67d4\u800c\u7075\u6d3b\uff0c\u53c8\u4eff\u4f5b\u590f \u65e5\u9633\u5149\u4e0b\u7684\u6d77\u6c34\uff0c\u5145\u6ee1\u4e86\u4ee4\u4eba\u6109\u5feb\u7684\u6d3b\u529b\u3002 \u3000\u3000\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u53cc\u773c\u775b\uff0c\u624d\u80fd\u4f7f\u4ed6\u6d3b\u5230\u5982\u4eca\u3002 \u3000\u3000\u73b0\u5728\u4eba\u50cf\u7ec8\u4e8e\u5b8c\u6210\u4e86\uff0c\u4ed6\u75f4\u75f4\u5730\u77a7\u7740\u8fd9\u4eba\u50cf\uff0c\u4e5f\u4e0d\u77e5\u77a7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u7136\u540e\u4ed6\u7a81\u7136\u63a8\u5f00\u8f66 \u95e8\uff0c\u8df3\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8d76\u8f66\u7684\u5927\u6c49\u7acb\u523b\u5406\u559d\u4e00\u58f0\uff0c\u52d2\u4f4f\u8f66\u9a6c\u3002 \u3000\u3000\u8fd9\u5927\u6c57\u6ee1\u9762\u866c\u9aef\uff0c\u76ee\u5149\u5c31\u5982\u9e37\u9e70\u822c\u9510\u5229\uff0c\u4f46\u7b49\u5230\u4ed6\u76ee\u5149\u79fb\u5411\u674e\u5bfb\u6b22\u65f6\uff0c\u7acb\u523b\u5c31\u53d8\u5f97\u67d4\u548c \u8d77\u6765\uff0c\u800c\u4e14\u5145\u6ee1\u4e86\u5fe0\u8bda\u7684\u540c\u60c5\uff0c\u5c31\u597d\u8c61\u4e00\u6761\u6076\u72ac\u5728\u671b\u7740\u4ed6\u7684\u4e3b\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7adf\u5728\u96ea\u5730\u4e0a\u6316\u4e86\u4e2a\u5751\uff0c\u5c06\u90a3\u521a\u96d5\u597d\u7684\u4eba\u50cf\u6df1\u6df1\u7684\u57cb\u4e86\u4e0b\u53bb\uff0c\u7136\u540e\uff0c\u4ed6\u5c31\u75f4\u75f4\u5730\u7ad9 \u5728\u96ea\u5806\u524d\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6307\u5df2\u88ab\u51bb\u50f5\uff0c\u8138\u5df2\u88ab\u51bb\u5f97\u53d1\u7ea2\uff0c\u8eab\u4e0a\u4e5f\u843d\u6ee1\u4e86\u96ea\u82b1\u3002\u4f46\u4ed6\u5374\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u51b7\uff0c\u8fd9 \u96ea\u5806\u91cc\u57cb\u7740\u7684\uff0c\u5c31\u8c61\u662f\u4e00\u4e2a\u4ed6\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u5f53\u4ed6\u5c06\u2018\u5979\u2019\u57cb\u4e0b\u53bb\u65f6\uff0c\u4ed6\u81ea\u5df1\u7684\u751f\u547d\u4e5f\u5c31\u53d8\u5f97 \u6beb\u65e0\u610f\u4e49\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u89c1\u5230\u4ed6\u8fd9\u79cd\u4e3e\u52a8\uff0c\u4e00\u5b9a\u4f1a\u89c9\u5f97\u5f88\u60ca\u5947\uff0c\u4f46\u90a3\u8d76\u8f66\u7684\u5927\u6c57\u5374\u4f3c\u5df2\u89c1\u60ef\u4e86\uff0c \u53ea\u662f\u67d4\u58f0\u9053\uff1a\u2018\u5929\u5df2\u5feb\u9ed1\u4e86\uff0c\u524d\u9762\u7684\u8def\u8fd8\u5f88\u8fdc\uff0c\u5c11\u7237\u4f60\u5feb\u4e0a\u8f66\u5427\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u8f6c\u56de\u8eab\uff0c\u5c31\u53d1\u73b0\u8f66\u8f99\u65c1\u5c45\u7136\u8fd8\u662f\u4e00\u884c\u8db3\u5370\uff0c\u81ea\u9065\u8fdc\u7684\u5317\u65b9\u5b64\u72ec\u5730\u8d70\u5230\u8fd9\u91cc\u6765 \uff0c\u53c8\u5b64\u72ec\u5730\u8d70\u5411\u524d\u65b9\u3002 \u3000\u3000\u811a\u5370\u5f88\u6df1\uff0c\u663e\u7136\u8fd9\u4eba\u5df2\u4e0d\u77e5\u8d70\u8fc7\u591a\u5c11\u8def\u4e86\uff0c\u5df2\u8d70\u5f97\u7cbe\u75b2\u529b\u7aed\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u7edd\u4e0d\u80af\u505c\u4e0b\u6765 \u4f11\u606f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a \u3000\u3000\u201c\u8fd9\u79cd\u5929\u6c14\uff0c\u60f3\u4e0d\u9053\u7adf\u8fd8\u6709\u4eba\u8981\u5728\u51b0\u5929\u96ea\u5730\u91cc\u5954\u6ce2\u53d7\u82e6\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u662f\u5f88\u5b64\u72ec\uff0c\u5f88\u53ef\u601c \u7684\u4eba\u3002\u201d \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c57\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u5fc3\u91cc\u5374\u5728\u6697\u6697\u53f9\u606f\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u4e5f\u662f\u4e2a\u5f88\u5b64\u72ec\u5f88\u53ef\u601c\u7684\u4eba\u4e48\uff1f \u4f60\u4e3a\u4f55\u603b\u662f\u53ea\u77e5\u9053\u540c\u60c5\u522b\u4eba\uff1f\u5374\u5fd8\u4e86\u81ea\u5df1\u2026\u2026\u201d \u3000\u3000\u8f66\u5ea7\u4e0b\u6709\u5f88\u591a\u5757\u575a\u5b9e\u7684\u677e\u6728\uff0c\u674e\u5bfb\u6b22\u53c8\u5f00\u59cb\u96d5\u523b\uff0c\u4ed6\u7684\u624b\u6cd5\u7cbe\u7ec3\u800c\u7eaf\u719f\uff0c\u56e0\u4e3a\u4ed6\u6240\u96d5\u523b \u7684\u6c38\u8fdc\u662f\u540c\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u5df2\u5360\u636e\u4e86\u4ed6\u7684\u5fc3\uff0c\u4e5f\u5360\u636e\u4e86\u4ed6\u7684\u8eaf\u58f3\u3002 \u3000\u3000\u96ea\uff0c\u7ec8\u4e8e\u505c\u4e86\uff0c\u5929\u5730\u95f4\u7684\u5bd2\u6c14\u5374\u66f4\u91cd\uff0c\u5bc2\u5bde\u4e5f\u66f4\u6d53\uff0c\u5e78\u597d\u8fd9\u91cc\u98ce\u4e2d\u5df2\u4f20\u6765\u4e00\u9635\u4eba\u7684\u811a\u6b65 \u58f0\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u6bd4\u9a6c\u8e44\u58f0\u8f7b\u5f97\u591a\uff0c\u4f46\u5374\u662f\u674e\u5bfb\u6b22\u6b63\u5728\u671f\u5f85\u7740\u7684\u58f0\u97f3\uff0c\u6240\u4ee5\u8fd9\u58f0\u97f3\u65e0\u8bba\u591a\u4e48\u8f7b \u5fae\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u9519\u8fc7\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u6380\u8d77\u90a3\u7528\u8c82\u76ae\u505a\u6210\u7684\u5e18\u5b50\uff0c\u63a8\u5f00\u7a97\u6237\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5c31\u89c1\u5230\u4e86\u8d70\u5728\u524d\u9762\u7684\u90a3\u5b64\u72ec\u7684\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4eba\u8d70\u5f97\u5f88\u6162\uff0c\u4f46\u5374\u7edd\u4e0d\u505c\u987f\uff0c\u867d\u7136\u542c\u5230\u4e86\u8f66\u94c3\u9a6c\u5636\u58f0\uff0c\u4f46\u5374\u7edd\u4e0d\u56de\u5934\uff01\u4ed6\u65e2\u6ca1\u6709\u5e26\u4f1e \uff0c\u4e5f\u6ca1\u6709\u6234\u5e3d\u5b50\uff0c\u6eb6\u5316\u4e86\u7684\u51b0\u96ea\uff0c\u6cbf\u7740\u4ed6\u7684\u8138\u6d41\u5230\u4ed6\u8116\u5b50\u91cc\uff0c\u4ed6\u8eab\u4e0a\u53ea\u7a7f\u4ef6\u5f88\u5355\u8584\u7684\u8863\u670d\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u80cc\u810a\u4ecd\u7136\u633a\u5f97\u7b14\u76f4\uff0c\u4ed6\u7684\u4eba\u5c31\u8c61\u662f\u94c1\u6253\u7684\uff0c\u51b0\u96ea\uff0c\u4e25\u5bd2\uff0c\u75b2\u5026\uff0c\u52b3\u7d2f\uff0c\u9965\u997f\uff0c\u90fd \u4e0d\u80fd\u4ee4\u4ed6\u5c48\u670d\u3002 \u3000\u3000\u6ca1\u6709\u4efb\u4f55\u662f\u80fd\u4ee4\u4ed6\u5c48\u670d\uff01 \u3000\u3000\u9a6c\u8f66\u8d76\u5230\u524d\u9762\u65f6\uff0c\u674e\u5bfb\u6b22\u624d\u77a7\u89c1\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4ed6\u7684\u7709\u5f88\u6d53\uff0c\u773c\u775b\u5f88\u5927\uff0c\u8584\u8584\u7684\u5634\u5507\u7d27\u7d27\u62bf\u6210\u4e86\u4e00\u6761\u7ebf\uff0c\u633a\u76f4\u7684\u9f3b\u5b50\u4f7f\u4ed6\u7684\u8138\u770b\u6765\u66f4\u7626 \u524a\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u4f7f\u4eba\u5f88\u5bb9\u6613\u5c31\u4f1a\u8054\u60f3\u5230\u82b1\u5188\u77f3\uff0c\u5014\u5f3a\uff0c\u575a\u5b9a\uff0c\u51b7\u6f20\uff0c\u5bf9\u4efb\u4f55\u4e8b\u90fd\u6f20\u4e0d\u5173\u5fc3\uff0c\u751a\u81f3 \u5bf9\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u4f46\u8fd9\u5374\u4e5f\u662f\u674e\u5bfb\u6b22\u5e73\u751f\u6240\u89c1\u5230\u7684\u6700\u82f1\u4fca\u7684\u4e00\u5f20\u8138\uff0c\u867d\u7136\u8fd8\u592a\u5e74\u8f7b\u4e86\u4e9b\uff0c\u8fd8\u4e0d\u6210\u719f\uff0c\u4f46\u5374 \u5df2\u6709\u79cd\u8db3\u591f\u5438\u5f15\u4eba\u7684\u9b45\u529b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u4e2d\u4f3c\u4e4e\u6709\u4e86\u7b11\u610f\uff0c\u4ed6\u63a8\u5f00\u8f66\u95e8\uff0c\u9053\uff1a\u201c\u4e0a\u8f66\u6765\uff0c\u6211\u8f7d\u4f60\u4e00\u6bb5\u8def\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u4e00\u5411\u8bf4\u5f97\u5f88\u7b80\u5355\uff0c\u5f88\u6709\u529b\uff0c\u5728\u8fd9\u4e00\u671b\u65e0\u9645\u7684\u51b0\u5929\u96ea\u5730\u4e2d\uff0c\u4ed6\u8fd9\u63d0\u8bae\u5b9e\u5728\u662f\u4efb\u4f55\u4eba \u90fd\u65e0\u6cd5\u62d2\u7edd\u7684\u3002 \u3000\u3000\u8c01\u77e5\u9053\u8fd9\u5c11\u5e74\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\uff0c\u811a\u6b65\u66f4\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u8c61\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u6709\u4eba\u5728\u8bf4\u8bdd \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u662f\u804b\u5b50\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7684\u624b\u5ffd\u7136\u63e1\u8d77\u4e86\u8170\u7554\u7684\u5251\u67c4\uff0c\u4ed6\u7684\u624b\u5df2\u51bb\u5f97\u6bd4\u9c7c\u7684\u8089\u8fd8\u767d\uff0c\u4f46\u52a8\u4f5c\u5374\u4ecd\u7136\u5f88\u7075\u6d3b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u662f\u804b\u5b50\uff0c\u90a3\u5c31\u4e0a\u6765\u559d\u53e3\u9152\u5427\uff0c\u4e00\u53e3\u9152\u5bf9\u4efb\u4f55\u4eba\u90fd\u4e0d\u4f1a\u6709\u5bb3 \u5904\u7684\uff01\u201d \u3000\u3000\u5c11\u5e74\u5ffd\u7136\u9053\uff1a\u201c\u6211\u559d\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u4f1a\u8bf4\u51fa\u8fd9\u4e48\u6837\u4e00\u53e5\u8bdd\u6765\uff0c\u674e\u5bfb\u6b22\u8fde\u773c\u89d2\u7684\u76b1\u7eb9\u91cc\u90fd\u6709\u4e86\u7b11\u610f\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u7b11\u51fa\u6765 \uff0c\u5374\u67d4\u58f0\u9053\uff1a\u201c\u6211\u8bf7\u4f60\u559d\u9152\uff0c\u7528\u4e0d\u7740\u4f60\u82b1\u94b1\u4e70\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u662f\u6211\u81ea\u5df1\u4e70\u6765\u7684\u4e1c\u897f\uff0c\u6211\u7edd\u4e0d\u8981\uff0c\u4e0d\u662f\u6211\u81ea\u5df1\u4e70\u6765\u7684\u9152\uff0c\u6211\u4e5f\u7edd\u4e0d\u559d\u2026\u2026 \u6211\u7684\u8bdd\u5df2\u7ecf\u8bf4\u5f97\u591f\u6e05\u695a\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u591f\u6e05\u695a\u4e86\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff01\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u8d70\uff0c\u4f46\u7b49\u4f60\u4e70\u5f97\u8d77\u9152\u7684\u65f6\u5019\uff0c\u4f60\u80af\u8bf7\u6211\u559d \u4e00\u676f\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u8bf7\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u7740\uff0c\u9a6c\u8f66\u5df2\u6025\u9a76\u800c\u53bb\uff0c\u6e10\u6e10\u53c8\u77a7\u4e0d\u89c1\u90a3\u5c11\u5e74\u7684\u4eba\u5f71\u4e86\uff0c\u674e\u5bfb\u6b22\u7b11\u7740\u9053\uff1a\u201c\u4f60 \u53ef\u66fe\u89c1\u8fc7\u5982\u6b64\u5947\u602a\u7684\u5c11\u5e74\u4e48\uff1f\u6211\u672c\u6765\u4ee5\u4e3a\u4ed6\u5fc5\u5b9a\u5df2\u9971\u7ecf\u6ca7\u6851\uff0c\u8c01\u77e5\u4ed6\u8bf4\u6765\u8bdd\u5374\u90a3\u4e48\u5929\u771f\uff0c\u90a3 \u4e48\u8001\u5b9e\u3002\u201d \u3000\u3000\u8d76\u8f66\u7684\u90a3\u866c\u9aef\u5927\u6c49\u6de1\u6de1\u9053\uff1a\u201c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5014\u5f3a\u7684\u5b69\u5b50\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u53ef\u77a7\u89c1\u4ed6\u8170\u5e26\u4e0a\u63d2\u7740\u7684\u90a3\u67c4\u5251\u4e48\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u76ee\u4e2d\u4e5f\u6709\u4e86\u7b11\u610f\uff0c\u9053\uff1a\u201c\u90a3\u4e5f\u80fd\u7b97\u662f\u4e00\u67c4\u5251\u4e48\uff1f\u201d \u3000\u3000\u4e25\u683c\u8bf4\u6765\uff0c\u90a3\u5b9e\u5728\u4e0d\u80fd\u7b97\u662f\u4e00\u67c4\u5251\uff0c\u90a3\u53ea\u662f\u4e00\u6761\u4e09\u5c3a\u591a\u957f\u7684\u94c1\u7247\uff0c\u65e2\u6ca1\u6709\u5251\u950b\uff0c\u4e5f\u6ca1\u6709 \u5251\u9102\uff0c\u751a\u81f3\u8fde\u5251\u67c4\u90fd\u6ca1\u6709\uff0c\u53ea\u7528\u4e24\u7247\u8f6f\u6728\u9489\u5728\u4e0a\u9762\uff0c\u5c31\u7b97\u662f\u5251\u53d8\u67c4\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u542b\u7b11\u63a5\u7740\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u90a3\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u5b69\u5b50\u7684\u73a9\u5177\u800c\u5df2\u3002\u201d \u3000\u3000\u8fd9\u6b21\u674e\u5bfb\u6b22\u975e\u4f46\u6ca1\u6709\u7b11\uff0c\u53cd\u800c\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u8fd9\u73a9\u5177\u5374\u5371\u9669\u5f97\u5f88\uff0c\u8fd8 \u662f\u83ab\u8981\u53bb\u73a9\u5b83\u7684\u597d\u3002\u201d \u3000\u3000\u5c0f\u9547\u4e0a\u7684\u5ba2\u6808\u672c\u5c31\u4e0d\u5927\uff0c\u8fd9\u65f6\u4f4f\u6ee1\u4e86\u88ab\u98ce\u96ea\u6240\u963b\u7684\u65c5\u5ba2\uff0c\u5c31\u663e\u5f97\u5206\u5916\u62e5\u6324\uff0c\u5206\u5916\u70ed\u95f9\u3002 \u3000\u3000\u9662\u5b50\u91cc\u5806\u7740\u5341\u51e0\u8f86\u7528\u8349\u5e2d\u76d6\u7740\u7684\u7a7a\u9556\u8f66\uff0c\u8349\u5e2d\u4e0a\u4e5f\u79ef\u6ee1\u4e86\u96ea\uff0c\u4e1c\u9762\u7684\u5c4b\u6a90\u4e0b\uff0c\u659c\u63d2\u7740\u4e00 \u9762\u9171\u8272\u9576\u91d1\u8fb9\u7684\u9556\u65d7\uff0c\u88ab\u98ce\u5439\u5f97\u8721\u8721\u4f5c\u54cd\uff0c\u4f7f\u4eba\u51e0\u4e4e\u5206\u8fa8\u4e0d\u51fa\u7528\u91d1\u7ebf\u7ee3\u5728\u4e0a\u9762\u7684\u662f\u8001\u864e\uff0c\u8fd8 \u662f\u72ee\u5b50\uff1f \u3000\u3000\u5ba2\u6808\u524d\u9762\u7684\u996d\u94fa\u91cc\uff0c\u4e0d\u65f6\u6709\u7a7f\u7740\u7f8a\u76ae\u8884\u7684\u5927\u6c49\u8fdb\u8fdb\u51fa\u51fa\uff0c\u6709\u7684\u559d\u4e86\u51e0\u676f\u9152\uff0c\u5c31\u6545\u610f\u655e\u5f00 \u8863\u895f\uff0c\u8868\u793a\u4ed6\u4eec\u4e0d\u6015\u51b7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5230\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u5ba2\u6808\u91cc\u8fde\u4e00\u5f20\u7a7a\u94fa\u90fd\u6ca1\u6709\u4e86\uff0c\u4f46\u4ed6\u4e00\u70b9\u513f\u4e5f\u4e0d\u7740\u6025\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053 \u8fd9\u4e16\u4e0a\u7528\u91d1\u94b1\u4e70\u4e0d\u5230\u7684\u4e1c\u897f\u6bd5\u7adf\u4e0d\u591a\uff0c\u6240\u4ee5\u4ed6\u5c31\u5148\u5728\u996d\u94fa\u91cc\u627e\u4e86\u5f20\u89d2\u843d\u91cc\u7684\u684c\u5b50\uff0c\u8981\u4e86\u58f6\u9152 \uff0c\u6162\u6162\u5730\u559d\u7740\u3002 \u3000\u3000\u4ed6\u9152\u559d\u5f97\u5e76\u4e0d\u5feb\uff0c\u4f46\u5374\u53ef\u4ee5\u4e0d\u505c\u5730\u559d\u51e0\u5929\u51e0\u591c\u3002\u4ed6\u4e0d\u505c\u5730\u559d\u9152\uff0c\u4e0d\u505c\u5730\u54b3\u55fd\uff0c\u5929\u5df2\u6e10\u6e10 \u5730\u9ed1\u4e86\u3002 \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c49\u4ee5\u8d70\u4e86\u8fdb\u6765\uff0c\u7ad9\u5728\u4ed6\u8eab\u540e\uff0c\u9053\uff1a\u201c\u5357\u9762\u7684\u4e0a\u623f\u5df2\u7a7a\u51fa\u6765\u4e86\uff0c\u4e5f\u5df2\u6253\u626b\u5e72\u51c0\uff0c \u5c11\u7237\u968f\u65f6\u90fd\u53ef\u4ee5\u4f11\u606f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8c61\u662f\u65e9\u5df2\u77e5\u9053\u4ed6\u4e00\u5b9a\u4f1a\u5c06\u8fd9\u4ef6\u4e8b\u529e\u597d\u4f3c\u7684\uff0c\u53ea\u70b9\u4e86\u70b9\u5934\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u866c\u9aef\u5927\u6c49 \u5ffd\u7136\u53c8\u9053\uff1a\u201c\u91d1\u72ee\u9556\u5c40\u4e5f\u6709\u4eba\u4f4f\u5728\u8fd9\u5ba2\u6808\u91cc\uff0c\u8c61\u662f\u521a\u4ece\u53e3\u5916\u62bc\u9556\u56de\u6765\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff01\u62bc\u9556\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5c31\u662f\u90a3\u2018\u6025\u98ce\u5251\u2019\u8bf8\u845b\u96f7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u72c2\u5f92\uff0c\u5c45\u7136\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u5012\u4e5f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u5728\u548c\u540e\u9762\u7684\u4eba\u8bf4\u8bdd\uff0c\u773c\u775b\u5374\u4e00\u76f4\u76ef\u7740\u524d\u9762\u90a3\u63a9\u7740\u68c9\u5e03\u5e18\u5b50\u7684\u95e8\uff0c\u4eff\u4f5b\u5728\u7b49\u7740\u4ec0 \u4e48\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u90a3\u5b69\u5b50\u7684\u811a\u7a0b\u4e0d\u5feb\uff0c\u53ea\u6015\u8981\u7b49\u5230\u8d77\u66f4\u65f6\u624d\u80fd\u8d76\u5230\u8fd9\u91cc\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u4ed6\u4e5f\u4e0d\u662f\u8d70\u4e0d\u5feb\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e0d\u80af\u6d6a\u8d39\u4f53\u529b\u800c\u5df2\uff0c\u4f60\u770b\u89c1\u8fc7\u4e00 \u5339\u72fc\u5728\u96ea\u5730\u4e0a\u8d70\u8def\u4e48\uff1f\u5047\u5982\u524d\u9762\u6ca1\u6709\u5b83\u7684\u730e\u7269\uff0c\u540e\u9762\u53c8\u6ca1\u6709\u8ffd\u5175\uff0c\u5b83\u4e00\u5b9a\u4e0d\u80af\u8d70\u5feb\u7684\uff0c\u56e0\u4e3a \u5b83\u89c9\u5f97\u5149\u5c06\u529b\u6c14\u7528\u5728\u8d70\u8def\u4e0a\uff0c\u672a\u514d\u592a\u53ef\u60dc\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f46\u90a3\u5b69\u5b50\u5374\u5e76\u4e0d\u662f\u4e00\u5339\u72fc\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u518d\u8bf4\u4ec0\u4e48\uff0c\u56e0\u4e3a\u8fd9\u65f6\u4ed6\u53c8\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u5230\u4e09\u4e2a\u4eba\u4ece\u540e\u9762\u7684\u4e00\u9053\u95e8\u8d70\u8fdb\u4e86\u8fd9\u996d\u94fa\uff0c\u4e09\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u90fd\u5f88\u5927\u6b63\u5728\u8c08 \u8bba\u90a3\u4e9b\u2018\u5200\u5934\u8214\u8840\u2019\u7684\u6c5f\u6e56\u52fe\u5f53\uff0c\u8c61\u662f\u751f\u6015\u522b\u4eba\u4e0d\u77e5\u9053\u4ed6\u4eec\u5c31\u662f\u2018\u91d1\u72ee\u9556\u5c40\u2019\u7684\u5927\u9556\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8ba4\u5f97\u90a3\u7d2b\u7ea2\u8138\u7684\u80d6\u5b50\u5c31\u662f\u2018\u6025\u98ce\u5251\u2019\uff0c\u4f46\u5374\u4f3c\u4e0d\u613f\u88ab\u5bf9\u65b9\u8ba4\u51fa\u4ed6\uff0c\u4e8e\u662f\u4ed6\u5c31\u53c8\u4f4e \u4e0b\u5934\u96d5\u4ed6\u7684\u4eba\u50cf\u3002 \u3000\u3000\u5e78\u597d\u8bf8\u845b\u96f7\u5230\u4e86\u8fd9\u5c0f\u9547\u4e4b\u540e\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6b63\u773c\u77a7\u8fc7\u4eba\uff0c\u4ed6\u4eec\u5f88\u5feb\u5730\u8981\u6765\u4e86\u9152\u83dc\uff0c\u5f00\u59cb\u5927 \u5403\u5927\u559d\u8d77\u6765\u3002 \u3000\u3000\u53ef\u662f\u9152\u83dc\u5e76\u4e0d\u80fd\u585e\u4f4f\u4ed6\u4eec\u7684\u5634\uff0c\u559d\u4e86\u51e0\u676f\u9152\u4e4b\u540e\uff0c\u8bf8\u845b\u96f7\u66f4\u662f\u8c6a\u6c14\u5982\u4e91\uff0c\u5927\u58f0\u5730\u7b11\u7740\uff1a \u201c\u8001\u4e8c\uff0c\u4f60\u8fd8\u8bb0\u5f97\u90a3\u5929\u54b1\u4eec\u5728\u592a\u884c\u5c71\u4e0b\u9047\u89c1\u2018\u592a\u884c\u56db\u864e\u2019\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u4ffa\u600e\u4e48\u4e0d\u8bb0\u5f97\uff0c\u90a3\u5929\u592a\u884c\u56db\u864e\u7adf\u6562\u6765\u52a8\u5927\u54e5\u4fdd\u7684\u90a3\u6279\u7ea2\u8d27\uff0c\u56db\u4e2a\u4eba\u8000\u6b66 \u626c\u5a01\uff0c\u8fd8\u8bf4\u4ec0\u4e48\uff1a\u2018\u53ea\u8981\u4f60\u8bf8\u845b\u96f7\u5728\u5730\u4e0a\u722c\u4e00\u5708\uff0c\u54b1\u4eec\u5144\u5f1f\u7acb\u523b\u653e\u4f60\u8fc7\u5c71\uff0c\u5426\u5219\u54b1\u4eec\u975e\u4f46\u8981 \u7559\u4e0b\u4f60\u7684\u7ea2\u8d27\uff0c\u8fd8\u8981\u7559\u4e0b\u4f60\u7684\u8111\u888b\u3002\u2019\u201d \u3000\u3000\u7b2c\u4e09\u4eba\u4e5f\u5927\u7b11\u9053\uff1a\u201c\u8c01\u77e5\u4ed6\u4eec\u7684\u5200\u8fd8\u672a\u780d\u4e0b\uff0c\u5927\u54e5\u7684\u5251\u5df2\u523a\u7a7f\u4e86\u4ed6\u4eec\u7684\u5589\u5499\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u4ffa\u8d75\u8001\u4e8c\u5439\u725b\uff0c\u82e5\u8bba\u638c\u529b\u4e4b\u96c4\u539a\uff0c\u81ea\u7136\u5f97\u6570\u54b1\u4eec\u7684\u603b\u9556\u5934\u2018\u91d1\u72ee\u638c\u2019 \uff0c\u4f46\u82e5\u8bba\u5251\u6cd5\u4e4b\u5feb\uff0c\u5f53\u4eca\u5929\u4e0b\u53ea\u6015\u518d\u4e5f\u6ca1\u6709\u4eba\u6bd4\u5f97\u4e0a\u54b1\u4eec\u5927\u54e5\u4e86\uff01\u201d \u3000\u3000\u8bf8\u845b\u96f7\u4e3e\u676f\u5927\u7b11\uff0c\u4f46\u662f\u4ed6\u7684\u7b11\u58f0\u5ffd\u7136\u505c\u987f\u4e86\uff0c\u4ed6\u53ea\u89c1\u90a3\u539a\u539a\u7684\u68c9\u5e03\u5e18\u5b50\u5ffd\u7136\u88ab\u98ce\u5377\u8d77\u3002 \u3000\u3000\u4e24\u6761\u4eba\u5f71\uff0c\u8c61\u662f\u96ea\u7247\u822c\u88ab\u98ce\u5439\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8eab\u4e0a\u90fd\u62ab\u7740\u9c9c\u7ea2\u7684\u62ab\u98ce\uff0c\u5934\u4e0a\u6234\u7740\u5bbd\u8fb9\u7684\u96ea\u7b20\uff0c\u4e24\u4eba\u51e0\u4e4e\u957f\u5f97\u540c\u6837\u578b\u72b6\uff0c\u540c\u6837\u9ad8 \u77ee\u3002 \u3000\u3000\u5927\u5bb6\u867d\u7136\u770b\u4e0d\u5230\u4ed6\u4eec\u7684\u9762\u76ee\uff0c\u4f46\u89c1\u5230\u4ed6\u4eec\u8fd9\u8eab\u51fa\u4f17\u7684\u8f7b\u529f\uff0c\u593a\u76ee\u7684\u6253\u626e\uff0c\u5df2\u4e0d\u89c9\u77a7\u5f97\u773c \u775b\u53d1\u76f4\u4e86\u3002 \u3000\u3000\u53ea\u6709\u674e\u5bfb\u6b22\u7684\u773c\u775b\uff0c\u5374\u4e00\u5411\u5728\u77aa\u7740\u95e8\u5916\uff0c\u56e0\u4e3a\u65b9\u624d\u95e8\u5e18\u88ab\u5439\u8d77\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u77a7\u89c1\u90a3\u5b64\u72ec \u7684\u5c11\u5e74\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5c31\u7ad9\u5728\u95e8\u5916\uff0c\u800c\u4e14\u8c61\u662f\u5df2\u7ad9\u4e86\u5f88\u4e45\uff0c\u5c31\u6b63\u5982\u4e00\u5339\u5b64\u72ec\u7684\u91ce\u72fc\u4f3c\u7684\uff0c\u867d\u7136\u7559\u604b\u7740\u95e8 \u91cc\u7684\u6e29\u6696\uff0c\u5374\u53c8\u754f\u60e7\u90a3\u8000\u773c\u7684\u706b\u5149\uff0c\u6240\u4ee5\u4ed6\u65e2\u820d\u4e0d\u5f97\u8d70\u5f00\uff0c\u5374\u53c8\u4e0d\u6562\u95ef\u5165\u8fd9\u4eba\u7684\u4e16\u754c\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u76ee\u5149\u8fd9\u624d\u8f6c\u5230\u4e24\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e24\u4eba\u5df2\u7f13\u7f13\u6458\u4e0b\u96ea\u7b20\uff0c\u9732\u51fa\u4e24\u5f20\u67af\u9ec4\u7626\u524a\u800c\u53c8\u4e11\u964b\u7684\u8138\uff0c\u770b\u6765\u5c31\u8c61\u662f\u4e24\u4e2a\u9ec4\u814a\u7684 \u4eba\u5934\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8033\u6735\u90fd\u5f88\u5c0f\uff0c\u9f3b\u5b50\u5374\u5f88\u5927\uff0c\u51e0\u4e4e\u5360\u636e\u4e86\u4e00\u5f20\u8138\u7684\u4e09\u5206\u4e4b\u4e00\uff0c\u5c06\u773c\u775b\u90fd\u6324\u5230\u8033\u6735\u65c1 \u8fb9\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u76ee\u5149\u5374\u5f88\u6076\u6bd2\u800c\u9510\u5229\uff0c\u5c31\u8c61\u662f\u54cd\u5c3e\u86c7\u7684\u773c\u775b\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4eec\u53c8\u5f00\u59cb\u5c06\u62ab\u98ce\u8131\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u4e86\u91cc\u9762\u4e00\u8eab\u6f06\u9ed1\u7684\u7d27\u8eab\u8863\u670d\uff0c\u539f\u6765\u4ed6\u4eec\u7684\u8eab\u5b50 \u4e5f\u8c61\u662f\u6bd2\u86c7\uff0c\u7ec6\u957f\uff0c\u575a\u97e7\uff0c\u968f\u65f6\u968f\u5730\u90fd\u5728\u8815\u52a8\u7740\uff0c\u800c\u4e14\u8fd8\u9ecf\u800c\u6f6e\u6e7f\uff0c\u53eb\u4eba\u770b\u4e86\u65e2\u4e0d\u514d\u5bb3\u6015\uff0c \u53c8\u89c9\u5f97\u6076\u5fc3\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u957f\u5f97\u51e0\u4e4e\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u5de6\u9762\u7684\u4eba\u8138\u8272\u82cd\u767d\uff0c\u53f3\u9762\u7684\u4eba\u8138\u8272\u5374\u9ed1\u5982\u9505\u5e95\u3002 \u4ed6\u4eec\u7684\u52a8\u4f5c\u90fd\u5341\u5206\u7f13\u6162\uff0c\u7f13\u7f13\u8131\u4e0b\u4e86\u62ab\u98ce\uff0c\u7f13\u7f13\u53e0\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u8d70\u8fc7\u67dc\u53f0\uff0c\u7136\u540e\uff0c\u4e24\u4eba\u4e00\u8d77 \u7f13\u7f13\u8d70\u5230\u8bf8\u845b\u96f7\u9762\u524d\uff01 \u3000\u3000\u996d\u94fa\u91cc\u9759\u5f97\u8fde\u674e\u5bfb\u6b22\u524a\u6728\u5934\u7684\u58f0\u97f3\u90fd\u542c\u5f97\u89c1\uff0c\u8bf8\u845b\u96f7\u867d\u60f3\u88c5\u4f5c\u6ca1\u6709\u770b\u5230\u8fd9\u4e24\u4eba\uff0c\u5374\u5b9e\u5728 \u529e\u4e0d\u5230\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u53ea\u662f\u77ac\u4e5f\u4e0d\u77ac\u5730\u76ef\u7740\u4ed6\uff0c\u90a3\u773c\u8272\u5c31\u8c61\u662f\u4e24\u628a\u8638\u7740\u6cb9\u7684\u6e7f\u5237\u5b50\uff0c\u5728\u8bf8\u845b\u96f7\u8eab\u4e0a\u5237\u6765 \u5237\u53bb\u3002 \u3000\u3000\u8bf8\u845b\u96f7\u53ea\u6709\u7ad9\u8d77\u6765\uff0c\u52c9\u5f3a\u7b11\u9053 \u3000\u3000\u201c\u4e24\u4f4d\u9ad8\u59d3\u5927\u540d\uff1f\u6055\u5728\u4e0b\u773c\u62d9\u2026\u2026\u201d \u3000\u3000\u90a3\u8138\u8272\u82cd\u767d\u7684\u4eba\u86c7\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u5c31\u662f\u2018\u6025\u98ce\u5251\u2019\u8bf8\u845b\u96f7\uff1f\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5c16\u9510\uff0c\u6025\u4fc3\uff0c\u800c\u4e14\u8fd8\u5728\u4e0d\u505c\u5730\u98a4\u6296\u7740\uff0c\u4e5f\u5c31\u8c61\u662f\u54cd\u5c3e\u86c7\u53d1\u51fa\u7684\u58f0\u97f3\uff0c\u8bf8\u845b\u96f7\u542c \u5f97\u5168\u8eab\u5bd2\u6bdb\u90fd\u6d91\u6817\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u6562\u3002\u201d \u3000\u3000\u90a3\u8138\u8272\u9edd\u9ed1\u7684\u4eba\u86c7\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u51ed\u4f60\uff0c\u4e5f\u914d\u79f0\u6025\u98ce\u5251\uff1f\u201d \u3000\u3000\u4ed6\u7684\u624b\u4e00\u6296\uff0c\u638c\u4e2d\u5ffd\u7136\u591a\u4e86\u67c4\u6f06\u9ed1\u7ec6\u957f\u7684\u8f6f\u5251\uff0c\u8fce\u9762\u53c8\u4e00\u6296\u8fd9\u8170\u5e26\u822c\u7684\u8f6f\u5251\uff0c\u5df2\u6296\u5f97\u7b14 \u76f4\u3002 \u3000\u3000\u4ed6\u7528\u8fd9\u67c4\u5251\u6307\u7740\u8bf8\u845b\u96f7\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u7559\u4e0b\u4f60\u4ece\u53e3\u5916\u5e26\u56de\u6765\u7684\u90a3\u5305\u4e1c\u897f\uff0c\u5c31\u9976\u4f60\u7684\u547d\u3002 \u3000\u3000\u90a3\u8d75\u8001\u4e8c\u5ffd\u7136\u957f\u8eab\u800c\u8d77\uff0c\u966a\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u53ea\u6015\u662f\u5f04\u9519\u4e86\uff0c\u54b1\u4eec\u8fd9\u8d9f\u9556\u662f\u5728\u53e3\u5916\u4ea4\u7684\u8d27\uff0c \u73b0\u5728\u9556\u8f66\u5df2\u7a7a\u4e86\uff0c\u4ec0\u4e48\u4e1c\u897f\u90fd\u6ca1\u6709\uff0c\u4e24\u4f4d\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u90a3\u4eba\u638c\u4e2d\u9ed1\u86c7\u822c\u7684\u5251\u5df2\u7f20\u4f4f\u4e86\u4ed6\u7684\u8116\u5b50\uff0c\u5251\u67c4\u8f7b\u8f7b\u4e00\u5e26\uff0c\u8d75\u8001\u4e8c\u7684\u4eba \u5934\u5c31\u5ffd\u7136\u51ed\u7a7a\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4e00\u80a1\u9c9c\u8840\u65d7\u82b1\u81ea\u4ed6\u8116\u5b50\u91cc\u51b2\u51fa\uff0c\u51b2\u5f97\u8fd9\u4eba\u5934\u5728\u534a\u7a7a\u4e2d\u53c8\u7ffb\u4e86\u4e24\u4e2a\u8eab\uff0c\u7136\u540e\u9c9c\u8840\u624d \u96e8\u70b9\u822c\u843d\u4e0b\uff0c\u4e00\u70b9\u70b9\u6d12\u5728\u8bf8\u845b\u96f7\u8eab\u4e0a\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u77a7\u76f4\u4e86\uff0c\u4e24\u6761\u817f\u5374\u5728\u4e0d\u505c\u5730\u5f39\u7435\u7436\u3002 \u3000\u3000\u4f46\u8bf8\u845b\u96f7\u80fd\u6d3b\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6b7b\uff0c\u6bd5\u7adf\u662f\u6709\u4e24\u624b\u7684\uff0c\u4ed6\u5ffd\u7136\u81ea\u6000\u4e2d\u638f\u51fa\u4e86\u4e2a\u9ec4\u5e03\u5305\u88b1\uff0c\u629b \u5728\u684c\u4e0a\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u7684\u62db\u5b50\u679c\u7136\u4eae\uff0c\u54b1\u4eec\u8fd9\u6b21\u7684\u786e\u4ece\u53e3\u5916\u5e26\u4e86\u5305\u4e1c\u897f\u56de\u6765\uff0c\u4f46\u4e24\u4f4d\u5c31\u60f3\u8fd9\u4e48 \u6837\u5e26\u8d70\uff0c\u53ea\u6015\u8fd8\u529e\u4e0d\u5230\u3002\u201d \u3000\u3000\u90a3\u9ed1\u86c7\u9634\u607b\u607b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u8bf8\u845b\u96f7\u9053\uff1a\u201c\u4e24\u4f4d\u597d\u6b79\u603b\u5f97\u7559\u4e24\u624b\u771f\u529f\u592b\u4e0b\u6765\uff0c\u53eb\u5728\u4e0b\u56de\u53bb\u4e5f\u597d\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u4eba\u5df2\u9000\u540e\u4e03\u6b65\uff0c\u5ffd\u7136\u201c\u5b57\u5f62\u5de6\u2018\u53e3\u2019\u53f3\u2018\u4ed3\u2019\u201d\u5730\u62d4\u51fa\u4e86\u5251\uff0c\u522b\u4eba\u53ea\u9053 \u4ed6\u662f\u8981\u548c\u5bf9\u65b9\u62fc\u547d\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u5374\u4e00\u53cd\u624b\uff0c\u5c06\u65c1\u8fb9\u684c\u4e0a\u7684\u4e00\u789f\u83dc\u6311\u4e86\u8d77\u6765\uff0c\u789f\u5b50\u91cc\u88c5\u7684\u662f\u867e\u7403\uff0c\u867e\u7403\u4e5f\u7acb\u523b\u98de\u4e86 \u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u5251\u98ce\u5636\u5636\uff0c\u5251\u5149\u5982\u5339\u7ec3\u5730\u4e00\u8f6c\uff0c\u5341\u591a\u4e2a\u867e\u7403\u7adf\u90fd\u88ab\u4ed6\u65a9\u4e3a\u4e24\u534a\uff0c\u7eb7\u7eb7\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8bf8\u845b\u96f7\u9762\u9732\u5f97\u8272\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4e24\u4f4d\u80fd\u7167\u6837\u73a9\u4e00\u624b\uff0c\u6211\u7acb\u523b\u5c31\u5c06\u8fd9\u5305\u4e1c\u897f\u5949\u4e0a\uff0c\u5426\u5219\u5c31\u8bf7 \u4e24\u4f4d\u8d70\u5427\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u624b\u5251\u6cd5\u5b9e\u5728\u4e0d\u5f31\uff0c\u8bdd\u4e5f\u8bf4\u5f97\u5f88\u6f02\u4eae\uff0c\u4f46\u674e\u5bfb\u6b22\u5374\u5728\u6697\u6697\u597d\u7b11\uff0c\u4ed6\u8fd9\u4e48\u6837\u4e00\u505a\uff0c\u522b\u4eba \u4e5f\u5c31\u53ea\u80fd\u65a9\u867e\u7403\uff0c\u4e0d\u80fd\u65a9\u4ed6\u7684\u8111\u888b\u4e86\uff0c\u4ed6\u65e0\u8bba\u662f\u80dc\u662f\u8d1f\uff0c\u81f3\u5c11\u5df2\u5148\u5c06\u81ea\u5df1\u7684\u6027\u547d\u4fdd\u4f4f\u518d\u8bf4\u3002 \u3000\u3000\u9ed1\u86c7\u683c\u683c\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u80fd\u7b97\u662f\u53a8\u5b50\u7684\u624b\u827a\uff0c\u4e5f\u80fd\u7b97\u6b66\u529f\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u521a\u843d\u5230\u5730\u4e0a\u7684\u867e\u7403\uff0c\u7adf\u53c8\u98d8\u98d8\u5730\u98de\u4e86\u8d77\u6765\uff0c\u7136\u540e\uff0c\u53ea\u89c1\u4e4c \u9ed1\u7684\u5149\u8292\u4e00\u95ea\uff0c\u6ee1\u5929\u7684\u867e\u7403\u5ffd\u7136\u5168\u90fd\u4e0d\u89c1\u4e86\uff0c\u539f\u6765\u7adf\u5df2\u5168\u90fd\u88ab\u4ed6\u7a7f\u5728\u5251\u4e0a\uff0c\u5c31\u7b97\u4e0d\u61c2\u6b66\u529f\u7684 \u4eba\uff0c\u4e5f\u77e5\u9053\u5251\u5288\u867e\u7403\u867d\u4e5f\u4e0d\u5bb9\u6613\uff0c\u4f46\u82e5\u60f3\u5c06\u867e\u7403\u7528\u5251\u7a7f\u8d77\u6765\uff0c\u90a3\u624b\u52b2\uff0c\u90a3\u773c\u529b\uff0c\u66f4\u4e0d\u77e5\u8981\u56f0 \u96be\u591a\u5c11\u500d\u3002 \u3000\u3000\u8bf8\u845b\u96f7\u9762\u8272\u5982\u571f\uff0c\u56e0\u4e3a\u4ed6\u89c1\u5230\u8fd9\u624b\u5251\u6cd5\uff0c\u5df2\u5ffd\u7136\u60f3\u8d77\u4e24\u4e2a\u4eba\u6765\uff0c\u4ed6\u811a\u4e0b\u53c8\u6084\u6084\u9000\u4e86\u51e0\u6b65 \uff0c\u624d\u560e\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u83ab\u975e\u5c31\u662f\u2026\u2026\u5c31\u662f\u2018\u78a7\u8840\u53cc\u86c7\u2019\u4e48\uff1f\u201d \u3000\u3000\u542c\u5230\u2018\u78a7\u8840\u53cc\u86c7\u2019\u8fd9\u56db\u4e2a\u5b57\uff0c\u53e6\u4e00\u4e2a\u5df2\u88ab\u5413\u5f97\u9762\u65e0\u4eba\u8272\u7684\u9556\u5e08\uff0c\u5ffd\u7136\u5c31\u6e9c\u5230\u684c\u5b50\u4e0b\u9762\u53bb \u4e86\u3002 \u3000\u3000\u5c31\u8fde\u674e\u5bfb\u6b22\u8eab\u540e\u90a3\u866c\u9aef\u5927\u6c57\uff0c\u4e5f\u4e0d\u7981\u76b1\u4e86\u76b1\u7709\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u77e5\u9053\u8fd1\u5e74\u9ec4\u6cb3\u4e00\u5e26\u7684\u9ed1\u9053\u670b\u53cb \uff0c\u82e5\u8bba\u5fc3\u4e4b\u9ed1\uff0c\u624b\u4e4b\u8fa3\uff0c\u5b9e\u5728\u5f88\u5c11\u6709\u4eba\u80fd\u5728\u8fd9\u2018\u78a7\u8840\u53cc\u86c7\u2019\u4e4b\u4e0a\uff0c\u542c\u8bf4\u4ed6\u4eec\u8eab\u4e0a\u62ab\u7684\u90a3\u4ef6\u7ea2 \u62ab\u98ce\uff0c\u5c31\u7528\u9c9c\u8840\u67d3\u6210\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u542c\u5230\u7684\u8fd8\u662f\u4e0d\u591a\uff0c\u56e0\u4e3a\u771f\u6b63\u77e5\u9053\u2018\u78a7\u8840\u53cc\u86c7\u2019\u505a\u8fc7\u4ec0\u4e48\u4e8b\u7684\u4eba\uff0c\u5341\u4eba\u4e2d\u5012\u6709\u4e5d\u4eba \u7684\u8111\u888b\u5df2\u642c\u5bb6\u4e86\u3002 \u3000\u3000\u53ea\u542c\u90a3\u9ed1\u86c7\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u8ba4\u51fa\u4e86\u6211\u4eec\uff0c\u603b\u7b97\u773c\u775b\u8fd8\u6ca1\u6709\u778e\u3002\u201d \u3000\u3000\u8bf8\u845b\u96f7\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u65e2\u7136\u662f\u4e24\u4f4d\u770b\u4e0a\u4e86\u8fd9\u5305\u4e1c\u897f\uff0c\u5728\u4e0b\u8fd8\u6709\u4ec0\u4e48\u8bdd\u597d\u8bf4\u7684\uff0c\u4e24\u4f4d\u5c31 \u8bf7\u2026\u2026\u5c31\u8bf7\u62ff\u53bb\u5427\u3002\u201d \u3000\u3000\u767d\u86c7\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u82e5\u80af\u5728\u5730\u4e0a\u722c\u4e00\u5708\uff0c\u54b1\u4eec\u5144\u5f1f\u7acb\u523b\u5c31\u653e\u4f60\u8d70\uff0c\u5426\u5219\u54b1\u4eec\u975e\u4f46\u8981\u7559\u4e0b\u4f60 \u7684\u5305\u88b1\uff0c\u8fd8\u8981\u7559\u4e0b\u4f60\u7684\u8111\u888b\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u6b63\u662f\u8bf8\u845b\u96f7\u4ed6\u4eec\u65b9\u624d\u81ea\u5439\u81ea\u64c2\u65f6\u8bf4\u51fa\u6765\u7684\uff0c\u6b64\u523b\u81ea\u8fd9\u767d\u86c7\u53e3\u4e2d\u8bf4\u51fa\uff0c\u6bcf\u4e2a\u5b57\u90fd\u53d8 \u5f97\u8c61\u662f\u4e00\u628a\u5200\u3002 \u3000\u3000\u8bf8\u845b\u96f7\u9762\u4e0a\u4e00\u9635\u9752\uff0c\u4e00\u9635\u767d\uff0c\u6014\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u722c\u5728\u5730\u4e0a\uff0c\u5c45\u7136\u771f\u7684\u56f4\u7740\u684c\u5b50\u722c\u4e86\u4e00\u5708\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5230\u8fd9\u65f6\u624d\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u813e\u6c14\u5df2\u53d8\u4e86\uff0c\u96be\u602a\u4ed6\u80fd\u6d3b\u5230\u73b0\u5728 \u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u6781\u5c0f\uff0c\u4f46\u9ed1\u767d\u53cc\u86c7\u7684\u773c\u775b\u5df2\u4e00\u9f50\u5411\u4ed6\u77aa\u4e86\u8fc7\u6765\uff0c\u4ed6\u5374\u4f3c\u4e4e\u6ca1\u6709\u770b\u89c1\uff0c\u8fd8\u662f \u5728\u96d5\u4ed6\u7684\u4eba\u50cf\u3002 \u3000\u3000\u767d\u86c7\u9634\u607b\u607b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u6b64\u5730\u7adf\u8fd8\u6709\u9ad8\u4eba\uff0c\u6211\u5144\u5f1f\u5012\u9669\u4e9b\u770b\u8d70\u773c\u4e86\u3002\u201d \u3000\u3000\u9ed1\u86c7\u72de\u7b11\u9053\uff1a\u201c\u8fd9\u5305\u88b1\u662f\u4eba\u5bb6\u60c5\u613f\u9001\u7ed9\u54b1\u4eec\u7684\uff0c\u53ea\u8981\u6709\u4eba\u7684\u5251\u6cd5\u6bd4\u6211\u5144\u5f1f\u66f4\u5feb\uff0c\u6211\u5144\u5f1f \u4e5f\u60c5\u613f\u5c06\u8fd9\u5305\u88b1\u53cc\u624b\u5949\u4e0a\u3002\u201d \u3000\u3000\u767d\u86c7\u7684\u624b\u4e00\u6296\uff0c\u638c\u4e2d\u4e5f\u591a\u4e86\u67c4\u6bd2\u86c7\u822c\u7684\u8f6f\u5251\uff0c\u5251\u5149\u5374\u5982\u767d\u8679\u822c\u7729\u4eba\u773c\u76ee\uff0c\u4ed6\u8fce\u98ce\u4eae\u5251\uff0c \u50b2\u7136\u9053\uff1a\u201c\u53ea\u8981\u6709\u6bd4\u6211\u5144\u5f1f\u66f4\u5feb\u7684\u5251\uff0c\u6211\u5144\u5f1f\u975e\u4f46\u5c06\u8fd9\u5305\u88b1\u9001\u7ed9\u4ed6\uff0c\u8fde\u8111\u888b\u4e5f\u9001\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u4ed6\u4eec\u7684\u773c\u775b\u6bd2\u86c7\u822c\u76ef\u5728\u674e\u5bfb\u6b22\u8138\u4e0a\uff0c\u674e\u5bfb\u6b22\u5374\u5728\u4e13\u5fc3\u523b\u4ed6\u7684\u6728\u5934\uff0c\u4eff\u4f5b\u6839\u672c\u542c\u4e0d\u61c2\u4ed6\u4eec \u5728\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u95e8\u5916\u5374\u5ffd\u7136\u4e0e\u4eba\u5927\u58f0\u9053\uff1a\u201c\u4f60\u7684\u8111\u888b\u80fd\u503c\u51e0\u4e24\u94f6\u5b50\uff1f\u201d \u3000\u3000\u542c\u5230\u4e86\u8fd9\u53e5\u8bdd\uff0c\u674e\u5bfb\u6b22\u4f3c\u4e4e\u89c9\u5f97\u5f88\u60ca\u8bb6\uff0c\u4f46\u4e5f\u5f88\u6b22\u559c\uff0c\u4ed6\u62ac\u8d77\u5934\uff0c\u90a3\u5c11\u5e74\u7ec8\u4e8e\u8d70\u8fdb\u4e86\u8fd9 \u5c4b\u5b50\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7684\u8863\u670d\u8fd8\u6ca1\u6709\u5e72\u900f\uff0c\u6709\u7684\u751a\u81f3\u5df2\u7ed3\u6210\u51b0\u5c51\uff0c\u4f46\u4ed6\u7684\u8eab\u5b50\u8fd8\u662f\u633a\u5f97\u7b14\u76f4\u7684\uff0c\u76f4\u5f97\u5c31 \u8c61\u6807\u67aa\u3002 \u3000\u3000\u4ed6\u7684\u8138\u770b\u6765\u4ecd\u662f\u90a3\u4e48\u5b64\u72ec\uff0c\u90a3\u4e48\u5014\u5f3a\u3002 \u3000\u3000\u4ed6\u7684\u773c\u91cc\u6c38\u8fdc\u5e26\u7740\u79cd\u4e0d\u53ef\u5c48\u670d\u7684\u91ce\u6027\uff0c\u8c61\u662f\u968f\u65f6\u90fd\u5728\u51c6\u5907\u4e89\u6597\uff0c\u53cd\u53db\uff0c\u4ee4\u4eba\u4e0d\u6562\u53bb\u4eb2\u8fd1 \u4ed6\u3002 \u3000\u3000\u4f46\u6700\u4ee4\u4eba\u6ce8\u610f\u7684\uff0c\u8fd8\u662f\u4ed6\u8170\u5e26\u4e0a\u63d2\u7740\u7684\u90a3\u67c4\u5251\u3002 \u3000\u3000\u77a7\u89c1\u8fd9\u67c4\u5251\uff0c\u767d\u86c7\u76ee\u5149\u4e2d\u7684\u60ca\u6012\u5df2\u53d8\u4e3a\u8baa\u7b11\uff0c\u4ed6\u683c\u683c\u7b11\u9053\uff1a\u201c\u65b9\u624d\u90a3\u53e5\u8bdd\u662f\u4f60\u8bf4\u7684\u4e48\uff1f \u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u767d\u86c7\u9053\uff1a\u201c\u4f60\u60f3\u4e70\u6211\u7684\u8111\u888b\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6211\u53ea\u60f3\u77e5\u9053\u5b83\u80fd\u503c\u51e0\u4e24\u94f6\u5b50\uff0c\u56e0\u4e3a\u6211\u8981\u5c06\u5b83\u5356\u7ed9\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u767d\u86c7\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5356\u7ed9\u6211\u81ea\u5df1\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u56e0\u4e3a\u6211\u65e2\u4e0d\u60f3\u8981\u8fd9\u5305\u88b1\uff0c\u4e5f\u4e0d\u60f3\u8981\u8fd9\u8111\u888b\u3002\u201d \u3000\u3000\u767d\u86c7\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u662f\u60f3\u6765\u627e\u6211\u6bd4\u5251\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u767d\u86c7\u4e0a\u4e0a\u4e0b\u4e0b\u671b\u4e86\u4ed6\u51e0\u773c\uff0c\u53c8\u77a7\u4e86\u77a7\u4ed6\u8170\u7554\u7684\u5251\uff0c\u5ffd\u7136\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u4ed6\u8fd9\u4e00\u751f\u4e2d\u5b9e\u5728 \u4ece\u672a\u89c1\u8fc7\u8fd9\u4e48\u597d\u7b11\u7684\u4e8b\u3002 \u3000\u3000\u5c11\u5e74\u53ea\u662f\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5b8c\u5168\u4e0d\u61c2\u5f97\u8fd9\u4eba\u5728\u7b11\u4ec0\u4e48\u3002\u4ed6\u81ea\u89c9\u8bf4\u7684\u8bdd\u5e76\u6ca1\u6709\u503c\u5f97\u522b\u4eba\u5982 \u6b64\u597d\u7b11\u7684\u3002 \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c57\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u4f3c\u4e4e\u89c9\u5f97\u8fd9\u5b69\u5b50\u5b9e\u5728\u7a77\u75af\u4e86\uff0c\u8bf8\u845b\u96f7\u4e5f\u89c9\u5f97\u4ed6\u7684\u8111\u888b\u5f88\u6709\u6bdb \u75c5\u3002 \u3000\u3000\u53ea\u542c\u767d\u86c7\u5927\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u5934\u9885\u5343\u91d1\u96be\u4e70\u2026\u2026\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u5343\u91d1\u592a\u591a\u4e86\uff0c\u6211\u53ea\u8981\u4e94\u5341\u4e24\u3002\u201d \u3000\u3000\u767d\u86c7\u9aa4\u7136\u987f\u4f4f\u4e86\u7b11\u58f0\uff0c\u56e0\u4e3a\u4ed6\u5df2\u53d1\u89c9\u8fd9\u5c11\u5e74\u65e2\u975e\u75af\u5b50\uff0c\u4ea6\u975e\u5446\u5b50\uff0c\u66f4\u4e0d\u662f\u5728\u5f00\u73a9\u7b11\u7684\uff0c \u8bf4\u7684\u8bdd\u7adf\u4f3c\u5f88\u8ba4\u771f\u3002 \u3000\u3000\u4f46\u4ed6\u518d\u4e00\u770b\u90a3\u67c4\u5251\uff0c\u53c8\u4e0d\u7981\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u80fd\u7167\u8fd9\u6837\u505a\u4e00\u904d\uff0c\u6211\u5c31\u7ed9\u4e94\u5341 \u4e24\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u4ed6\u7684\u5251\u5149\u4e00\u95ea\uff0c\u4f3c\u4e4e\u8981\u5212\u5230\u67dc\u53f0\u4e0a\u90a3\u6839\u8721\u70db\uff0c\u4f46\u5251\u5149\u8fc7\u5904\uff0c\u90a3\u6839\u8721\u70db\u5374\u8fd8\u662f\u7eb9 \u98ce\u4e0d\u52a8\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u53ef\u662f\u767d\u86c7\u8fd9\u65f6\u5df2\u5439\u4e86\u53e3\u6c14\uff0c\u4e00\u53e3\u6c14\u5439\u51fa\uff0c\u8721\u70db\u7a81\u7136\u5206\u6210\u4e03\u6bb5\uff0c\u5251 \u5149\u53c8\u4e00\u95ea\uff0c\u4e03\u6bb5\u8721\u70db\u5c31\u90fd\u88ab\u7a7f\u4e0a\u5728\u5251\u4e0a\uff0c\u6700\u540e\u4e00\u6bb5\u5149\u7130\u95ea\u52a8\uff0c\u70db\u706b\u7adf\u4ecd\u672a\u7184\u706d\u2014\u2014\u539f\u6765\u4ed6\u65b9 \u624d\u4e00\u5251\u5df2\u5c06\u8721\u70db\u524a\u6210\u4e03\u622a\u3002 \u3000\u3000\u767d\u86c7\u50b2\u7136\u9053\uff1a\u201c\u4f60\u770b\u6211\u8fd9\u4e2a\u4e00\u5251\u8fd8\u7b97\u5feb\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7684\u8138\u4e0a\u4e1d\u6beb\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u9053\uff1a\u201c\u5f88\u5feb\u3002\u201d \u3000\u3000\u767d\u86c7\u72de\u7b11\u9053\uff1a\u201c\u4f60\u600e\u6837\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6211\u7684\u5251\u4e0d\u662f\u7528\u6765\u524a\u8721\u70db\u7684\u3002\u201d \u3000\u3000\u767d\u86c7\u9053\uff1a\u201c\u90a3\u4f60\u8fd9\u628a\u7834\u94dc\u70c2\u94c1\u662f\u7528\u6765\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7684\u624b\u63e1\u4e0a\u5251\u67c4\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u7684\u5251\u662f\u7528\u6765\u6740\u4eba\u7684\uff01\u201d \u3000\u3000\u767d\u86c7\u683c\u683c\u7b11\u9053\uff1a\u201c\u6740\u4eba\uff1f\u4f60\u80fd\u6740\u5f97\u4e86\u8c01\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4f60\uff01\u201d \u3000\u3000\u8fd9\u2018\u4f60\u2019\u5b57\u8bf4\u51fa\u53e3\uff0c\u4ed6\u7684\u5251\u5df2\u523a\u4e86\u51fa\u53bb\uff01 \u3000\u3000\u5251\u672c\u6765\u8fd8\u63d2\u5728\u8fd9\u5c11\u5e74\u8170\u5e26\u4e0a\uff0c\u6bcf\u4e2a\u4eba\u90fd\u77a7\u89c1\u4e86\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8fd9\u67c4\u5251\u5df2\u63d2\u5165\u4e86\u767d\u86c7\u7684\u54bd\u5589\uff0c\u6bcf\u4e2a\u4eba\u4e5f\u90fd\u77a7\u89c1\u4e09\u5c3a\u957f\u7684\u5251\u950b\u81ea\u767d\u86c7\u7684\u54bd\u5589\u7a7f\u8fc7 \u3002 \u3000\u3000\u4f46\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u770b\u6e05\u4ed6\u8fd9\u67c4\u5251\u662f\u5982\u4f55\u523a\u5165\u767d\u86c7\u54bd\u5589\u7684\uff01 \u3000\u3000\u6ca1\u6709\u8840\u6d41\u4e0b\uff0c\u56e0\u4e3a\u8840\u8fd8\u672a\u53ca\u6d41\u4e0b\u6765\u3002 \u3000\u3000\u5c11\u5e74\u77aa\u7740\u767d\u86c7\uff0c\u9053\uff1a\u201c\u662f\u4f60\u7684\u5251\u5feb\uff1f\u8fd8\u662f\u6211\u7684\u5251\u5feb\uff01\u201d \u3000\u3000\u767d\u86c7\u5589\u5499\u91cc\u2018\u683c\u683c\u2019\u7684\u54cd\uff0c\u8138\u4e0a\u6bcf\u4e00\u6839\u808c\u8089\u90fd\u5728\u8df3\u52a8\uff0c\u9f3b\u5b54\u6e10\u6e10\u6269\u5f20\uff0c\u5f20\u5927\u4e86\u5634\uff0c\u4f38\u51fa \u4e86\u820c\u5934\u3002 \u3000\u3000\u9c9c\u8840\uff0c\u5df2\u81ea\u4ed6\u820c\u5c16\u6ef4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ed1\u86c7\u7684\u5251\u5df2\u626c\u8d77\uff0c\u4f46\u5374\u4e0d\u6562\u523a\u51fa\uff0c\u4ed6\u8138\u4e0a\u7684\u6c57\u4e0d\u505c\u7684\u5728\u5f80\u4e0b\u6d41\uff0c\u638c\u4e2d\u7684\u5251\u4e5f\u5728\u4e0d\u505c\u7684\u98a4 \u6296\u3002 \u3000\u3000\u53ea\u89c1\u5c11\u5e74\u5ffd\u7136\u62d4\u51fa\u4e86\u5251\uff0c\u9c9c\u8840\u5c31\u7bad\u4e00\u822c\u81ea\u767d\u86c7\u7684\u54bd\u5589\u91cc\u6807\u51fa\uff0c\u4ed6\u95f7\u7740\u7684\u4e00\u53e3\u6c14\u4e5f\u5410\u4e86\u51fa \u6765\uff0c\u72c2\u543c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u72c2\u543c\u53d1\u51fa\u540e\uff0c\u4ed6\u7684\u4eba\u5c31\u6251\u9762\u8dcc\u5012\u3002 \u3000\u3000\u5c11\u5e74\u5374\u5df2\u8f6c\u95ee\u9ed1\u86c7\uff0c\u9053\uff1a\u201c\u4ed6\u5df2\u8ba4\u8f93\u4e86\uff0c\u4e94\u5341\u4e24\u94f6\u5b50\u5462\uff1f\u201d \u3000\u3000\u4ed6\u7684\u4ecd\u662f\u90a3\u4e48\u8ba4\u771f\uff0c\u8ba4\u771f\u5f97\u5c31\u8c61\u4e2a\u50bb\u5b69\u5b50\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u5374\u518d\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u7b11\u4ed6\u4e86\u3002 \u3000\u3000\u9ed1\u86c7\u8fde\u5634\u5507\u90fd\u5728\u53d1\u6296\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u771f\u662f\u4e3a\u4e86\u4e94\u5341\u4e24\u94f6\u5b50\u6740\u4ed6\u7684\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ed1\u86c7\u7684\u4e00\u5f20\u8138\u5168\u90fd\u626d\u66f2\u8d77\u6765\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ed\u8fd8\u662f\u7b11\uff0c\u5ffd\u7136\u7529\u5374\u4e86\u638c\u4e2d\u7684\u5251\uff0c\u7528\u529b\u626f\u7740\u81ea\u5df1 \u7684\u5934\u53d1\uff0c\u5c06\u8eab\u4e0a\u7684\u8863\u670d\u4e5f\u5168\u6495\u788e\u4e86\uff0c\u6000\u4e2d\u7684\u94f6\u5b50\u4e00\u952d\u952d\u6389\u4e86\u4e0b\u6765\uff0c\u4ed6\u7528\u529b\u5c06\u94f6\u5b50\u63b7\u5230\u5c11\u5e74\u7684 \u9762\u524d\uff0c\u54ed\u568e\u7740\u9053\uff1a\u201c\u7ed9\u4f60\uff0c\u5168\u7ed9\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u5c31\u8c61\u4e2a\u75af\u5b50\u4f3c\u7684\u72c2\u5954\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u65e2\u4e0d\u8ffd\u8d76\uff0c\u4e5f\u4e0d\u751f\u6c14\uff0c\u5374\u5f2f\u8170\u62fe\u4e86\u4e24\u952d\u94f6\u5b50\u8d77\u6765\uff0c\u9001\u5230\u67dc\u53f0\u540e\u90a3\u638c\u67dc\u7684\u9762\u524d\uff0c\u9053 \uff1a\u201c\u4f60\u770b\u8fd9\u591f\u4e0d\u591f\u4e94\u5341\u4e24\uff1f\u201d \u3000\u3000\u90a3\u638c\u67dc\u7684\u65e9\u5df2\u77ee\u4e86\u534a\u622a\uff0c\u7f29\u5728\u67dc\u53f0\u4e0b\uff0c\u7259\u9f7f\u683c\u683c\u5730\u6253\u6218\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ea\u662f\u62da\u547d\u5730\u70b9 \u5934\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u674e\u5bfb\u6b22\u624d\u56de\u5934\u5411\u90a3\u866c\u9aef\u5927\u6c57\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8bf4\u9519\u5427\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c57\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u90a3\u73a9\u5177\u5b9e\u5728\u592a\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u4ed6\u77a7\u89c1\u90a3\u5c11\u5e74\u5df2\u5411\u4ed6\u4eec\u8d70\u4e86\u8fc7\u6765\uff0c\u4f46\u5374\u672a\u77a7\u89c1\u8bf8\u845b\u96f7\u7684\u52a8\u4f5c\uff0c\u8bf8\u845b\u96f7\u4e00\u76f4\u5c31\u6ca1\u6709\u4ece\u684c\u5b50 \u4e0b\u722c\u8d77\u6765\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7adf\u5ffd\u7136\u63a0\u8d77\uff0c\u4e00\u5251\u5411\u5c11\u5e74\u7684\u540e\u5fc3\u523a\u51fa\uff01 \u3000\u3000\u4ed6\u7684\u5251\u672c\u4e0d\u6162\uff0c\u5c11\u5e74\u66f4\u7edd\u672a\u60f3\u5230\u4ed6\u4f1a\u51fa\u624b\u6697\u7b97\u2014\u2014\u4ed6\u6740\u4e86\u767d\u86c7\uff0c\u8bf8\u845b\u96f7\u672c\u8be5\u611f\u6fc0\u4ed6\u624d\u662f \uff0c\u4e3a\u4f55\u8981\u6740\u4ed6\u5462\uff01 \u3000\u3000\u773c\u770b\u8fd9\u4e00\u5251\u5df2\u5c06\u523a\u7a7f\u4ed6\u7684\u5fc3\u7a9d\uff0c\u8c01\u77e5\u5c31\u5728\u6b64\u65f6\uff0c\u8bf8\u845b\u96f7\u5ffd\u7136\u72c2\u543c\u4e00\u58f0\uff0c\u8df3\u8d77\u6765\u6709\u516d\u5c3a\u9ad8 \uff0c\u638c\u4e2d\u7684\u5251\u4e5f\u8131\u624b\u98de\u51fa\uff0c\u63d2\u5728\u5c4b\u6881\u4e0a\u3002 \u3000\u3000\u5251\u67c4\u7684\u4e1d\u7a57\u8fd8\u5728\u4e0d\u505c\u7684\u98a4\u52a8\uff0c\u8bf8\u845b\u96f7\u53cc\u624b\u63a9\u4f4f\u4e86\u81ea\u5df1\u7684\u54bd\u5589\uff0c\u773c\u775b\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u773c\u73e0\u90fd \u5feb\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6b64\u523b\u5e76\u6ca1\u6709\u5728\u523b\u6728\u5934\uff0c\u56e0\u4e3a\u4ed6\u624b\u91cc\u90a3\u628a\u523b\u6728\u5934\u7684\u5c0f\u5200\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9c9c\u8840\u4e00\u4e1d\u4e1d\u81ea\u8bf8\u845b\u96f7\u7684\u80cc\u7f1d\u91cc\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u54bd\u5589\u91cc\u4e5f\u5728\u2018\u683c\u683c\u2019\u5730\u54cd\uff0c\u8fd9\u65f6\u624d\u6709\u4eba\u53d1\u73b0\u674e\u5bfb\u6b22\u523b\u6728\u5934\u7684\u5c0f\u5200\u5df2\u5230\u4e86 \u4ed6\u7684\u54bd\u5589\u4e0a\u3002 \u3000\u3000\u4f46\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u77a7\u89c1\u8fd9\u5c0f\u5200\u662f\u600e\u4f1a\u5230\u4ed6\u54bd\u5589\u4e0a\u7684\u3002 \u3000\u3000\u53ea\u89c1\u8bf8\u845b\u96f7\u6ee1\u5934\u5927\u6c57\u5982\u96e8\uff0c\u8138\u5df2\u75db\u5f97\u53d8\u5f62\uff0c\u5ffd\u7136\u54ac\u4e86\u54ac\u7259\uff0c\u5c06\u90a3\u67c4\u5c0f\u5200\u62d4\u4e86\u51fa\u6765\uff0c\u77aa\u7740 \u674e\u5bfb\u6b22\u72c2\u543c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u2026\u2026\u6211\u65e9\u8be5\u8ba4\u51fa\u4f60\u4e86\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u76f4\u5230\u73b0\u5728\u624d\u8ba4\u51fa\u6211\uff0c\u5426\u5219\u4f60\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u505a\u51fa\u5982\u6b64\u4e22\u4eba\u7684\u4e8b\u4e86\uff01 \u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u8bf8\u845b\u96f7\u5e76\u6ca1\u6709\u542c\u5230\uff0c\u5df2\u6c38\u8fdc\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u4e5f\u66fe\u56de\u5934\u77a7\u4e86\u4e00\u773c\uff0c\u9762\u4e0a\u4e5f\u66fe\u9732\u51fa\u4e9b\u60ca\u5947\u4e4b\u8272\uff0c\u4f3c\u4e4e\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4eba\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6 \uff1f \u3000\u3000\u4f46\u4ed6\u53ea\u4e0d\u8fc7\u77a7\u4e86\u4e00\u773c\uff0c\u5c31\u8d70\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u4ed6\u5145\u6ee1\u4e86\u91ce\u6027\u7684\u7738\u5b50\u91cc\uff0c\u7adf\u4f3c\u9732\u51fa\u4e86\u4e00\u4e1d\u6e29 \u6696\u7684\u7b11\u610f\u3002 \u3000\u3000\u4ed6\u4e5f\u53ea\u4e0d\u8fc7\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u4ed6\u8bf4\uff1a\u201c\u6211\u8bf7\u4f60\u559d\u9152\u3002\u201d \u7b2c\u4e8c\u7ae0\u3000\u6d77\u5185\u5b58\u77e5\u5df1>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u7ae0\u3000\u6d77\u5185\u5b58\u77e5\u5df1 \u3000\u3000\u9a6c\u8f66\u91cc\u5806\u7740\u597d\u51e0\u575b\u9152\uff0c\u8fd9\u9152\u662f\u90a3\u5c11\u5e74\u4e70\u7684\uff0c\u6240\u4ee5\u4ed6\u4e00\u7897\u53c8\u4e00\u7897\u5730\u559d\u7740\uff0c\u800c\u4e14\u559d\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u7740\u4ed6\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u6109\u5feb\u7684\u795e\u8272\uff0c\u4ed6\u5f88\u5c11\u9047\u89c1\u80fd\u4ee4\u4ed6\u89c9\u5f97\u6709\u8da3\u7684\u4eba\uff0c\u8fd9\u5c11\u5e74\u5374\u5b9e \u5728\u5f88\u6709\u8da3\u3002 \u3000\u3000\u9053\u4e0a\u7684\u79ef\u96ea\u5df2\u5316\u4e3a\u575a\u51b0\uff0c\u8f66\u884c\u51b0\u4e0a\uff0c\u7eb5\u662f\u826f\u9a79\u4e5f\u96be\u9a7e\u9a6d\uff0c\u90a3\u866c\u9aef\u5927\u6c49\u5df2\u5728\u8f66\u8f6e\u6346\u8d77\u51e0\u6761 \u94c1\u94fe\u5b50\uff0c\u4f7f\u8f66\u8f6e\u4e0d\u81f4\u592a\u6ed1\u3002 \u3000\u3000\u94c1\u94fe\u62d6\u5728\u51b0\u96ea\u4e0a\uff0c\u2018\u683c\u6717\u683c\u6717\u2019\u5730\u76f4\u54cd\u3002 \u3000\u3000\u5c11\u5e74\u5ffd\u7136\u653e\u4e0b\u9152\u7897\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u5b9a\u8981\u6211\u5230\u4f60\u9a6c\u8f66\u4e0a\u6765\u559d\u9152\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u90a3\u5ba2\u6808\u5df2\u975e\u4e45\u7559\u4e4b\u5730\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u6740\u4e86\u4eba\u540e\uff0c\u591a\u591a\u5c11\u5c11\u90fd\u4f1a\u6709\u4e9b\u9ebb\u70e6\u7684\uff0c\u6211\u867d\u4e0d\u6015\u6740\u4eba\uff0c\u4f46\u5e73\u751f\u6700\u6015 \u7684\u5c31\u662f\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5c11\u5e74\u9ed8\u7136\u534a\u664c\uff0c\u8fd9\u624d\u53c8\u4ece\u575b\u5b50\u91cc\u52fa\u4e86\u4e00\u7897\u9152\uff0c\u4ef0\u7740\u8116\u5b50\u559d\u4e86\u4e0b\u53bb\uff0c\u674e\u5bfb\u6b22\u542b\u7b11\u671b\u7740\uff0c\u5f88 \u6b23\u8d4f\u4ed6\u7684\u559d\u9152\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5c11\u5e74\u7adf\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6740\u4eba\u7684\u786e\u4e0d\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\uff0c\u4f46\u6709\u4e9b\u4eba\u5374\u5b9e\u5728\u8be5\u6740 \uff0c\u6211\u975e\u6740\u4eba\u4e0d\u53ef\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e3a\u4e86\u4e94\u5341\u4e24\u94f6\u5b50\u624d\u6740\u90a3\u767d\u86c7\u7684\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6ca1\u6709\u4e94\u5341\u4e24\u94f6\u5b50\uff0c\u6211\u4e5f\u8981\u6740\u4ed6\uff0c\u6709\u4e86\u4e94\u5341\u4e24\u94f6\u5b50\u66f4\u597d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u53ea\u8981\u4e94\u5341\u4e24\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u53ea\u503c\u4e94\u5341\u4e24\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u6c5f\u6e56\u4e2d\u8be5\u6740\u7684\u4eba\u5f88\u591a\uff0c\u4e5f\u6709\u4e9b\u4e0d\u53ea\u503c\u4e94\u5341\u4e24\u7684\uff0c\u6240\u4ee5\u4f60\u4ee5\u540e\u8bf4\u4e0d\u5b9a\u4f1a\u6210\u4e3a \u4e00\u4e2a\u5927\u5bcc\u7fc1\uff0c\u6211\u4e5f\u5e38\u5e38\u4f1a\u6709\u9152\u559d\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u592a\u7a77\uff0c\u5426\u5219\u6211\u4e5f\u8be5\u9001\u4f60\u4e94\u5341\u4e24\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u66ff\u6211\u6740\u4e86\u90a3\u4e2a\u4eba\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u90a3\u4eba\u975e\u4f46\u4e0d\u503c\u4e94\u5341\u4e24\uff0c\u7b80\u76f4\u8fde\u4e00\u6587\u90fd\u4e0d\u503c\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4e3a\u4f55\u8981\u6740\u4f60\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u767d\u86c7\u867d\u7136\u6ca1\u6709\u6740\u4ed6\uff0c\u4f46\u5374\u5df2\u4ee4\u4ed6\u65e0\u6cd5\u5728\u6c5f\u6e56\u4e2d\u7acb\u8db3\uff0c\u4f60\u53c8\u6740\u4e86\u767d\u86c7\u4ed6\u53ea\u6709 \u6740\u4e86\u4f60\uff0c\u4ee5\u540e\u624d\u53ef\u4ee5\u91cd\u65b0\u626c\u7709\u5410\u6c14\uff0c\u81ea\u5439\u81ea\u64c2\uff0c\u6240\u4ee5\u4ed6\u5c31\u975e\u6740\u4f60\u4e0d\u53ef\uff0c\u6c5f\u6e56\u4e2d\u4eba\u5fc3\u4e4b\u9669\u6076\uff0c \u53ea\u6015\u4f60\u96be\u4ee5\u60f3\u8c61\u7684\u3002\u201d \u3000\u3000\u5c11\u5e74\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5583\u5583\u9053\uff1a\u201c\u6709\u65f6\u4eba\u5fc3\u7684\u786e\u6bd4\u864e\u72fc\u8fd8\u6076\u6bd2\u5f97\u591a\uff0c\u864e\u72fc\u8981\u5403\u4f60\u7684\u65f6\u5019\uff0c\u6700 \u5c11\u5148\u8ba9\u4f60\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u559d\u4e0b\u4e00\u7897\u9152\u540e\uff0c\u5ffd\u53c8\u63a5\u9053\uff1a\u201c\u4f46\u6211\u53ea\u542c\u5230\u8fc7\u4eba\u8bf4\u864e\u72fc\u6076\u6bd2\uff0c\u5374\u4ece\u672a\u542c\u8fc7\u864e\u72fc\u8bf4\u4eba\u6076\u6bd2 \uff0c\u5176\u5b9e\u864e\u72fc\u53ea\u4e3a\u4e86\u751f\u5b58\u624d\u6740\u4eba\uff0c\u4eba\u5374\u53ef\u4ee5\u4e0d\u4e3a\u4ec0\u4e48\u5c31\u6740\u4eba\uff0c\u800c\u4e14\u636e\u6211\u6240\u77e5\uff0c\u4eba\u6740\u6b7b\u7684\u4eba\uff0c\u8981 \u6bd4\u864e\u72fc\u6740\u6b7b\u7684\u4eba\u591a\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u51dd\u6ce8\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u5b81\u53ef\u548c\u864e\u72fc\u4ea4\u670b\u53cb\uff1f\u201d \u3000\u3000\u5c11\u5e74\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u7b11\u7740\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u4eec\u4e0d\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u8fd9\u662f\u674e\u5bfb\u6b22\u7b2c\u4e00\u6b21\u89c1\u5230\u5c11\u5e74\u7684\u7b11\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u7b11\u5bb9\u7adf\u4f1a\u5728\u4e00\u4e2a\u4eba\u7684\u8138\u4e0a\u9020\u6210\u8fd9\u4e48\u5927\u7684\u53d8 \u5316\u3002 \u3000\u3000\u5c11\u5e74\u7684\u8138\u672c\u6765\u662f\u90a3\u4e48\u5b64\u72ec\uff0c\u90a3\u4e48\u5014\u5f3a\uff0c\u4f7f\u5f97\u674e\u5bfb\u6b22\u65f6\u5e38\u4f1a\u7406\u60f3\u5230\u4e00\u5339\u5728\u96ea\u5730\u4e0a\u6d41\u6d6a\u7684\u72fc \u3002 \u3000\u3000\u4f46\u7b49\u5230\u4ed6\u5634\u89d2\u6cdb\u8d77\u7b11\u5bb9\u7684\u65f6\u5019\uff0c\u4ed6\u8fd9\u4eba\u7adf\u5ffd\u7136\u53d8\u4e86\uff0c\u53d8\u5f97\u90a3\u4e48\u6e29\u67d4\uff0c\u90a3\u4e48\u4eb2\u5207\uff0c\u90a3\u4e48\u53ef \u7231\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ece\u672a\u89c1\u8fc7\u4efb\u4f55\u4eba\u7684\u7b11\u5bb9\u80fd\u4f7f\u4eba\u5982\u6b64\u52a8\u5fc3\u7684\u3002 \u3000\u3000\u5c11\u5e74\u4e5f\u5728\u51dd\u6ce8\u7740\uff0c\u4ed6\u5ffd\u53c8\u95ee\u5230\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e2a\u5f88\u6709\u540d\u7684\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6709\u540d\u5e76\u4e0d\u662f\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u4f46\u6211\u5374\u5e0c\u671b\u53d8\u5f97\u5f88\u6709\u540d\uff0c\u6211\u5e0c\u671b\u80fd\u6210\u4e3a\u5929\u4e0b\u6700\u6709\u540d\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u5ffd\u53c8\u53d8\u5f97\u5b69\u5b50\u822c\u8ba4\u771f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u5e0c\u671b\u6210\u540d\uff0c\u4f60\u81f3\u5c11\u6bd4\u522b\u4eba\u90fd\u8bda\u5b9e\u5f97\u591a\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6211\u548c\u522b\u4eba\u4e0d\u540c\uff0c\u6211\u975e\u6210\u540d\u4e0d\u53ef\uff0c\u4e0d\u6210\u540d\u6211\u53ea\u6709\u6b7b\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5f00\u59cb\u6709\u4e9b\u5403\u60ca\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5e74\u6ca1\u6709\u56de\u7b54\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u76ee\u4e2d\u5374\u6d41\u9732\u51fa\u4e00\u79cd\u60b2\u4f24\u6124\u6012\u4e4b\u8272\uff0c\u674e\u5bfb\u6b22\u8fd9\u624d\u53d1\u89c9\u4ed6\u6709\u65f6\u867d\u7136 \u5929\u771f\u5766\u767d\u5f97\u8c61\u4e2a\u5b69\u5b50\uff0c\u4f46\u6709\u65f6\u5374\u53c8\u4f3c\u85cf\u7740\u8bb8\u591a\u79d8\u5bc6\uff0c\u4ed6\u7684\u8eab\u4e16\uff0c\u5982\u8c1c\u5374\u53c8\u663e\u7136\u5145\u6ee1\u4e86\u60b2\u75db\u4e0e \u4e0d\u5e78\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u6210\u540d\uff0c\u81f3\u5c11\u5e94\u8be5\u5148\u8bf4\u51fa\u81ea\u5df1\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u5c11\u5e74\u8fd9\u6b21\u6c89\u9ed8\u5f97\u66f4\u4e45\uff0c\u7136\u540e\u624d\u7f13\u7f13\u9053\uff1a\u201c\u8ba4\u5f97\u6211\u7684\u4eba\uff0c\u90fd\u53eb\u6211\u963f\u98de\u3002\u201d \u3000\u3000\u963f\u98de\uff01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u59d3\u2018\u963f\u2019\u4e48\uff1f\u4e16\u4e0a\u5e76\u6ca1\u6709\u8fd9\u4e2a\u59d3\u5440\u3002\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6211\u6ca1\u6709\u59d3\uff01\u201d\u4ed6\u76ee\u5149\u4e2d\u7adf\u4f3c\u5ffd\u7136\u6709\u706b\u7130\u71c3\u70e7\u8d77\u6765\uff0c\u674e\u5bfb\u6b22\u77e5\u9053\u8fd9\u79cd\u706b\u7130\u8fde\u773c \u6cea\u90fd\u65e0\u6cd5\u7184\u706d\uff0c\u4ed6\u5b9e\u5728\u4e0d\u5fcd\u518d\u95ee\u4e0b\u53bb\u3002\u8c01\u77e5\u90a3\u5c11\u5e74\u5ffd\u53c8\u63a5\u9053\uff1a\u201c\u7b49\u5230\u6211\u6210\u540d\u7684\u65f6\u5019\uff0c\u4e5f\u8bb8\u6211 \u4f1a\u8bf4\u51fa\u59d3\u540d\uff0c\u4f46\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u6211\u5c31\u53eb\u4f60\u963f\u98de\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u5f88\u597d\uff0c\u73b0\u5728\u4f60\u5c31\u53eb\u6211\u963f\u98de\u2014\u2014\u5176\u5b9e \u4f60\u65e0\u8bba\u53eb\u6211\u4ec0\u4e48\u540d\u5b57\u90fd\u65e0\u6240\u8c13\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u963f\u98de\uff0c\u6211\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u521a\u559d\u5b8c\u4e86\u534a\u7897\u9152\uff0c\u53c8\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u53c8\u6cdb\u8d77\u90a3\u79cd\u75c5\u6001\u7684\u5ae3\u7ea2\u8272\uff0c\u4f46\u4ed6\u8fd8\u662f \u5c06\u5269\u4e0b\u7684\u534a\u7897\u9152\u4e00\u53e3\u5012\u8fdb\u8116\u5b50\u91cc\u3002 \u3000\u3000\u963f\u98de\u5403\u60ca\u5730\u77a7\u7740\u4ed6\uff0c\u4f3c\u4e4e\u60f3\u4e0d\u5230\u8fd9\u4f4d\u6c5f\u6e56\u7684\u540d\u4fa0\u8eab\u4f53\u7adf\u662f\u5982\u6b64\u865a\u5f31\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u8bf4\u4ec0\u4e48 \uff0c\u53ea\u662f\u5f88\u5feb\u5730\u559d\u5b8c\u4e86\u4ed6\u81ea\u5df1\u7684\u4e00\u7897\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u559c\u6b22\u4f60\u8fd9\u670b\u53cb\uff1f\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u7740\uff0c\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u4f60\u662f\u6211\u670b\u53cb\u4e2d\uff0c\u770b\u5230\u6211\u54b3\u55fd\uff0c\u5374\u6ca1\u6709\u529d\u6211\u6212\u9152\u7684\u7b2c \u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u54b3\u55fd\u662f\u4e0d\u662f\u4e0d\u80fd\u559d\u9152\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u672c\u6765\u8fde\u78b0\u90fd\u4e0d\u80fd\u78b0\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u559d\u5462\uff1f\u4f60\u662f\u4e0d\u662f\u6709\u5f88\u591a\u4f24\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u660e\u4eae\u7684\u773c\u775b\u9eef\u6de1\u4e86\uff0c\u77aa\u7740\u963f\u98de\u9053\uff1a\u201c\u6211\u6709\u6ca1\u6709\u95ee\u8fc7\u4f60\u4e0d\u613f\u56de\u7b54\u7684\u8bdd\uff1f\u6709\u6ca1\u6709\u95ee\u8fc7 \u4f60\u7684\u7236\u6bcd\u662f\u8c01\uff1f\u6b66\u529f\u662f\u8c01\u4f20\u6388\u7684\uff1f\u4ece\u54ea\u6765\uff1f\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u95ee\u6211\u5462\uff1f\u201d \u3000\u3000\u963f\u98de\u9759\u9759\u5730\u51dd\u6ce8\u4ed6\u534a\u664c\uff0c\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u95ee\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\uff0c\u4ed6\u4f3c\u4e4e\u60f3\u518d\u656c\u963f\u98de\u4e00\u676f\uff0c\u4f46\u521a\u52fa\u8d77\u9152\uff0c\u5df2\u54b3\u5f97\u5f2f\u4e0b\u8170\u53bb\uff0c\u8fde\u6c14\u90fd\u5598\u4e0d\u8fc7 \u6765\u3002 \u3000\u3000\u963f\u98de\u521a\u66ff\u4ed6\u63a8\u5f00\u7a97\u5b50\uff0c\u9a6c\u8f66\u5ffd\u7136\u505c\u4e0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u63a2\u9996\u7a97\u5916\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u6709\u4eba\u6321\u8def\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u4f3c\u4e4e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u96ea\u4eba\u3002\u201d \u3000\u3000\u9053\u8def\u7684\u4e2d\u592e\uff0c\u4e0d\u77e5\u88ab\u54ea\u5bb6\u987d\u7ae5\u5806\u8d77\u4e2a\u96ea\u4eba\uff0c\u5927\u5927\u7684\u809a\u5b50\uff0c\u5706\u5706\u7684\u8138\uff0c\u8138\u4e0a\u8fd8\u5d4c\u7740\u4e24\u7c92\u7164 \u7403\u7b97\u4f5c\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u4e0b\u4e86\u8f66\uff0c\u674e\u5bfb\u6b22\u5728\u957f\u957f\u5730\u547c\u5438\u7740\uff0c\u963f\u98de\u5374\u5728\u51fa\u795e\u5730\u77a7\u7740\u90a3\u96ea\u4eba\uff0c\u8c61\u662f\u4ece\u6765\u4e5f\u6ca1\u6709 \u89c1\u8fc7\u96ea\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u5411\u4ed6\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5806\u8fc7\u96ea\u4eba\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u96ea\u662f\u53ef\u6068\u7684\uff0c\u5b83\u4e0d\u4f46\u4ee4\u4eba\u5bd2\u51b7\uff0c\u800c\u4e14\u4ee4\u8349\u6728\u679c\u5b9e\u5168\u90fd\u67af\u840e\uff0c\u4ee4\u9e1f\u517d \u7edd\u8ff9\uff0c\u4ee4\u4eba\u5bc2\u5bde\u3001\u9965\u997f\u3002\u201d \u3000\u3000\u4ed6\u634f\u4e2a\u96ea\u7403\uff0c\u629b\u4e86\u51fa\u53bb\uff0c\u96ea\u7403\u547c\u5578\u7740\u98de\u5230\u8fdc\u65b9\uff0c\u6563\u5f00\uff0c\u4e0d\u89c1\uff0c\u4ed6\u76ee\u5149\u4e5f\u5728\u8fdc\u671b\u7740\u8fdc\u65b9\uff0c \u7f13\u7f13\u9053\uff1a\u201c\u5bf9\u90a3\u4e9b\u5403\u5f97\u9971\uff0c\u7a7f\u5f97\u6696\u7684\u4eba\u8bf4\u6765\uff0c\u96ea\u4e5f\u8bb8\u5f88\u53ef\u7231\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u4f46\u53ef\u4ee5\u5806\u96ea\u4eba\uff0c\u8fd8 \u53ef\u4ee5\u8d4f\u96ea\u666f\uff0c\u4f46\u5bf9\u6211\u4eec\u8fd9\u4e9b\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u5728\u8352\u91ce\u4e2d\u957f\u5927\u7684\uff0c\u98ce\u3001\u96ea\u3001\u971c\u3001\u96e8\uff0c\u90fd\u662f\u6211\u6700 \u5927\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u795e\u60c5\u4e5f\u6709\u4e9b\u9eef\u7136\uff0c\u5ffd\u4e5f\u634f\u8d77\u56e2\u96ea\u7403\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8ba8\u538c\u96ea\uff0c\u4f46\u6211\u5374\u6700\u8ba8\u538c\u522b\u4eba\u6321\u6211 \u7684\u8def\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u5c06\u96ea\u7403\u629b\u51fa\u53bb\uff0c\u2018\u7830\u2019\u5730\u51fb\u5728\u90a3\u96ea\u4eba\u4e0a\u3002 \u3000\u3000\u96ea\u82b1\u56db\u6e85\uff0c\u90a3\u96ea\u4eba\u7adf\u6ca1\u6709\u88ab\u4ed6\u51fb\u5012\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u7247\u7247\u51b0\u96ea\u81ea\u90a3\u96ea\u4eba\u8eab\u4e0a\u6563\u5f00\uff0c\u7164\u7403\u4e5f\u88ab\u51fb\u843d\uff0c\u5706\u5706\u7684\u8138\u4e5f\u6563\u5f00\uff0c\u5374\u53c8\u6709\u5f20\u6b7b\u7070\u822c \u7684\u8138\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u96ea\u4eba\u4e2d\u7adf\u85cf\u7740\u4e00\u4e2a\u771f\u6b63\u7684\u4eba\u3002 \u3000\u3000\u6b7b\u4eba\uff01 \u3000\u3000\u6b7b\u4eba\u7684\u8138\u7edd\u4e0d\u4f1a\u6709\u597d\u770b\u7684\uff0c\u8fd9\u5f20\u8138\u5c24\u5176\u72f0\u72de\u4e11\u6076\uff0c\u4e00\u53cc\u6076\u6bd2\u7684\u773c\u775b\uff0c\u6b7b\u9c7c\u822c\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u963f\u98de\u5931\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u9ed1\u86c7\uff01\u201d \u3000\u3000\u9ed1\u86c7\u600e\u4f1a\u6b7b\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u6740\u4ed6\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u5c06\u4ed6\u5806\u6210\u96ea\u4eba\uff0c\u6321\u4f4f\u9053\u8def\uff1f \u3000\u3000\u866c\u9aef\u5927\u6c49\u5c06\u4ed6\u7684\u5c38\u4f53\u81ea\u96ea\u5806\u4e2d\u63d0\u4e86\u8d77\u6765\uff0c\u8e72\u4e0b\u53bb\u4ed4\u7ec6\u5730\u77a7\u7740\uff0c\u4f3c\u4e4e\u60f3\u627e\u51fa\u4ed6\u81f4\u547d\u7684\u4f24\u75d5 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u601d\u7740\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u662f\u8c01\u6740\u6b7b\u4ed6\u7684\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5c31\u662f\u90a3\u5305\u88b1\u3002\u201d \u3000\u3000\u963f\u98de\u76b1\u7709\u9053\uff1a\u201c\u5305\u88b1\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u5305\u88b1\u4e00\u76f4\u5728\u684c\u4e0a\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u592a\u7559\u610f\uff0c\u4f46\u7b49\u5230\u9ed1\u86c7\u8d70\u4e86\u540e\uff0c\u90a3\u5305\u88b1\u4e5f\u4e0d \u89c1\u4e86\uff0c\u6240\u4ee5\u6211\u60f3\uff0c\u4ed6\u6545\u610f\u4f5c\u51fa\u90a3\u79cd\u53d1\u75af\u7684\u6837\u5b50\u6765\uff0c\u5c31\u4e3a\u7684\u662f\u8981\u5f15\u5f00\u522b\u4eba\u7684\u6ce8\u610f\u529b\uff0c\u4ed6\u624d\u597d\u8d81 \u673a\u5c06\u90a3\u5305\u88b1\u652b\u8d70\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u672a\u60f3\u5230\u90a3\u5305\u88b1\u7adf\u4e3a\u4ed6\u62db\u6765\u4e86\u6740\u8eab\u4e4b\u7978\uff0c\u6740\u4ed6\u7684\u4eba\uff0c\u60f3\u5fc5\u5c31\u662f\u4e3a\u4e86\u90a3 \u53ea\u5305\u88b1\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u4f55\u65f6\u5df2\u5c06\u90a3\u5c0f\u5200\u62ff\u5728\u624b\u4e0a\uff0c\u8f7b\u8f7b\u5730\u629a\u6478\u7740\uff0c\u5583\u5583\u9053\uff1a\u201c\u90a3\u5305\u88b1\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5462\uff1f \u4e3a\u4f55\u6709\u8fd9\u4e48\u591a\u4eba\u5bf9\u5b83\u53d1\u751f\u5174\u8da3\uff1f\u4e5f\u8bb8\u6211\u6628\u5929\u665a\u4e0a\u672c\u8be5\u62ff\u8fc7\u6765\u77a7\u77a7\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u4e00\u76f4\u5728\u9759\u9759\u5730\u542c\u7740\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6740\u4ed6\u7684\u4eba\uff0c\u65e2\u662f\u4e3a\u4e86\u90a3\u5305\u88b1\uff0c\u90a3\u4e48\u4ed6\u5c06\u5305\u88b1\u593a\u8d70\u4e4b \u540e\uff0c\u4e3a\u4ec0\u4e48\u8981\u5c06\u9ed1\u86c7\u5806\u6210\u96ea\u4eba\uff0c\u6321\u4f4f\u8def\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u795e\u60c5\u770b\u6765\u5f88\u60ca\u8bb6\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u8fd9\u5c11\u5e74\u867d\u7136\u5bf9\u4eba\u60c5\u4e16\u6545\u5f88\u4e0d\u4e86\u89e3\uff0c\u6709\u65f6\u751a\u81f3\u5929\u771f\u5f97\u8c61\u4e2a\u5b69\u5b50\uff0c\u4f46\u667a\u6167\u4e4b\u9ad8\uff0c\u601d\u8651 \u4e4b\u5bc6\uff0c\u53cd\u5e94\u4e4b\u5feb\uff0c\u4ed6\u8fd9\u79cd\u8001\u6c5f\u6e56\u4e5f\u8d76\u4e0d\u4e0a\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4e0d\u662f\u5df2\u7b97\u51c6\u8fd9\u6761\u8def\u4e0d\u4f1a\u6709\u522b\u4eba\u8d70\uff0c\u53ea\u6709\u4f60\u7684\u9a6c\u8f66\u5fc5\u5b9a\u4f1a\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u6240 \u4ee5\u8981\u5728\u8fd9\u91cc\u5c06\u4f60\u62e6\u4f4f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u627e\u51fa\u4ed6\u7684\u81f4\u547d\u4f24\u6ca1\u6709\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u8fd8\u672a\u8bf4\u8bdd\uff0c\u674e\u5bfb\u6b22\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u627e\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eba\u90fd\u5df2\u6765\u4e86\uff0c\u8fd8\u627e\u4ec0\u4e48\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8033\u529b\u4e4b\u654f\uff0c\u76ee\u529b\u4e4b\u5f3a\uff0c\u53ef\u8bf4\u51a0\u7edd\u5929\u4e0b\uff0c\u4ed6\u5b9e\u672a\u60f3\u5230\u8fd9\u5c11\u5e74\u7684\u8033\u76ee\u5c45\u7136\u4e5f\u548c\u4ed6\u540c\u6837 \u7075\u654f\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4f3c\u4e4e\u5929\u751f\u6709\u79cd\u91ce\u517d\u822c\u7684\u672c\u80fd\uff0c\u80fd\u89c9\u5bdf\u5230\u522b\u4eba\u89c9\u5bdf\u4e0d\u51fa\u7684\u4e8b\uff0c\u674e\u5bfb\u6b22\u5411\u4ed6\u8d5e\u8bb8\u5730\u4e00 \u7b11\uff0c\u7136\u540e\u5c31\u6717\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u65e2\u5df2\u5230\u4e86\uff0c\u4e3a\u4f55\u4e0d\u8fc7\u6765\u559d\u676f\u9152\u5462\uff1f\u201d \u3000\u3000\u9053\u65c1\u6797\u6728\u67af\u679d\u4e0a\u7684\u79ef\u96ea\uff0c\u5ffd\u7136\u7c0c\u7c0c\u5730\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u4eba\u5927\u7b11\u7740\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u63a2\u82b1\u90ce\u7684\u5b9d\u5200\u4f9d\u7136\u672a\u8001\uff0c\u53ef\u8d3a\u53ef\u559c\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u4e00\u4e2a\u98a7\u9aa8\u9ad8\u8038\uff0c\u9762\u5982\u6de1\u91d1\uff0c\u76ee\u5149\u5982\u7765\u7768\u9e70\u7684\u72ec\u81c2\u8001\u4eba\uff0c\u5df2\u5927\u6b65\u81ea\u5de6\u9762\u7684\u96ea\u6797\u4e2d \u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f3\u9762\u7684\u96ea\u6797\u4e2d\uff0c\u4e5f\u5ffd\u7136\u51fa\u73b0\u4e86\u4e2a\u4eba\uff0c\u8fd9\u4eba\u5e72\u67af\u7626\u5c0f\uff0c\u8138\u4e0a\u6ca1\u6709\u56db\u4e24\u8089\uff0c\u8c61\u662f\u4e00\u9635\u98ce\u5c31\u80fd \u5c06\u4ed6\u5439\u5012\u3002 \u3000\u3000\u963f\u98de\u4e00\u773c\u4fbf\u5df2\u77a5\u89c1\uff0c\u8fd9\u4eba\u8d70\u51fa\u6765\u4e4b\u540e\uff0c\u96ea\u5730\u4e0a\u7adf\u5168\u65e0\u811a\u5370\uff0c\u6b64\u5730\u96ea\u867d\u5df2\u7ed3\u51b0\uff0c\u4f46\u51b0\u4e0a\u53c8 \u6709\u79ef\u96ea\u3002 \u3000\u3000\u8fd9\u4eba\u5c45\u7136\u8e0f\u96ea\u65e0\u75d5\uff0c\u867d\u8bf4\u591a\u5c11\u5360\u4e86\u4e9b\u8eab\u6750\u7684\u4fbf\u5b9c\uff0c\u4f46\u8f7b\u529f\u4e4b\u9ad8\uff0c\u4e5f\u591f\u5413\u4eba\u7684\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5165\u5173\u8fd8\u4e0d\u5230\u534a\u4e2a\u6708\uff0c\u60f3\u4e0d\u5230\u2018\u91d1\u72ee\u9556\u5c40\u2019\u7684\u67e5\u603b\u9556\u5934\uff0c\u548c\u2018\u795e\u884c\u65e0 \u5f71\u2019\u865e\u4e8c\u5148\u751f\u5c31\u5168\u90fd\u6765\u770b\u6211\u4e86\uff0c\u5728\u4e0b\u7684\u9762\u5b50\u5b9e\u5728\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u90a3\u77ee\u5c0f\u8001\u4eba\u9634\u6c89\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u674e\u63a2\u82b1\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u8fc7\u76ee\u4e0d\u5fd8\uff0c\u54b1\u4eec\u53ea\u5728\u5341\u4e09\u5e74 \u524d\u89c1\u8fc7\u4e00\u6b21\u9762\uff0c\u60f3\u4e0d\u5230\u63a2\u82b1\u90ce\u7adf\u8fd8\u8bb0\u5f97\u6211\u865e\u4e8c\u62d0\u5b50\u8fd9\u8001\u5e9f\u7269\u3002\u201d \u3000\u3000\u963f\u98de\u8fd9\u624d\u53d1\u73b0\u4ed6\u7adf\u6709\u6761\u817f\u662f\u8ddb\u7684\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u4e00\u4e2a\u8f7b\u529f\u5982\u6b64\u9ad8\u660e\u7684\u4eba\uff0c\u7adf\u662f\u4e2a\u8ddb\u5b50\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u8fd9\u865e\u4e8c\u62d0\u5b50\u5c31\u56e0\u4e3a\u53f3\u817f\u5929\u751f\u7578\u5f62\u6b8b\u5e9f\uff0c\u662f\u4ee5\u4ece\u5c0f\u5c31\u82e6\u7ec3\u8f7b\u529f\uff0c\u4ed6\u8981\u4ee5\u8d85\u4eba\u7684\u8f7b\u529f \uff0c\u6765\u5f25\u8865\u5929\u751f\u7684\u7f3a\u9677\u3002 \u3000\u3000\u963f\u98de\u5012\u4e0d\u7981\u5bf9\u8fd9\u8001\u4eba\u89c9\u5f97\u5f88\u4f69\u670d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u65e2\u7136\u8fd8\u8bf7\u6765\u51e0\u4f4d\u670b\u53cb\uff0c\u4e3a\u4f55\u4e0d\u4e00\u9f50\u4e3a\u5728\u4e0b\u5f15\u89c1\u5f15\u89c1\u5462\uff1f\u201d \u3000\u3000\u865e\u4e8c\u62d0\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e5f\u4e45\u95fb\u5c0f\u674e\u63a2\u82b1\u7684\u5927\u540d\u4e86\uff0c\u65e9\u5c31\u60f3\u89c1\u89c1\u9601\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u8bdd\uff0c\u6811\u6797\u91cc\u5df2\u8d70\u51fa\u56db\u4e2a\u4eba\u6765\uff0c\u6b64\u523b\u867d\u7136\u662f\u767d\u5929\uff0c\u4f46\u674e\u5bfb\u6b22\u89c1\u4e86\u8fd9\u56db\u4eba\uff0c\u8fd8\u662f\u4e0d\u89c9 \u5012\u62bd\u4e86\u53e3\u51b7\u6c14\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u5e74\u7eaa\u867d\u7136\u5168\u5df2\u4e0d\u5c0f\uff0c\u4f46\u5374\u6253\u626e\u5f97\u8c61\u662f\u5c0f\u5b69\u5b50\uff0c\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u4e94\u989c\u516d\u8272\uff0c\u82b1\u82b1\u7eff\u7eff \uff0c\u811a\u4e0a\u7a7f\u7684\u4e5f\u662f\u7ee3\u7740\u8001\u864e\u7684\u7ae5\u978b\uff0c\u8170\u4e0a\u8fd8\u7cfb\u7740\u56f4\u88d9\uff0c\u56db\u4eba\u867d\u90fd\u662f\u6d53\u7709\u5927\u773c\uff0c\u50cf\u72de\u6076\uff0c\u4f46\u5374\u504f \u504f\u8981\u4f5c\u51fa\u987d\u7ae5\u7684\u6a21\u6837\uff0c\u563b\u563b\u54c8\u54c8\uff0c\u6324\u7709\u5f04\u773c\uff0c\u53eb\u4eba\u89c1\u4e86\uff0c\u8fde\u9694\u591c\u996d\u90fd\u8981\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u4ed6\u4eec\u624b\u8155\u4e0a\uff0c\u811a\u8e1d\u4e0a\uff0c\u7adf\u8fd8\u6234\u6ee1\u4e86\u53d1\u4eae\u7684\u94f6\u956f\uff0c\u8d70\u8d77\u8def\u6765\u2018\u53ee\u53ee\u5f53\u5f53\u2019\u5730\u76f4 \u54cd\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e00\u89c1\u8fd9\u56db\u4eba\uff0c\u8138\u8272\u7acb\u523b\u53d8\u5f97\u94c1\u9752\uff0c\u5ffd\u7136\u560e\u58f0\u9053\uff1a\u201c\u90a3\u9ed1\u86c7\u4e0d\u662f\u88ab\u4eba\u6740\u6b7b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4ed6\u662f\u88ab\u874e\u5b50\u548c\u8708\u86a3\u8707\u6b7b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8138\u8272\u4e5f\u53d8\u4e86\u53d8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u56db\u4f4d\u83ab\u975e\u662f\u82d7\u7586\u2018\u6781\u4e50\u5cd2\u2019\u4e94\u6bd2\u7ae5\u5b50\u7684 \u95e8\u4e0b\uff1f\u201d \u3000\u3000\u56db\u4eba\u4e2d\u7684\u9ec4\u8863\u7ae5\u5b50\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8f9b\u8f9b\u82e6\u82e6\u5806\u6210\u7684\u96ea\u4eba\u88ab\u4f60\u5f04\u574f\u4e86\uff0c\u6211\u8981\u4f60\u8d54\u3002 \u201d\u2018\u8d54\u2019\u5b57\u51fa\u53e3\uff0c\u4ed6\u8eab\u5b50\u5ffd\u7136\u98de\u63a0\u800c\u8d77\uff0c\u5411\u674e\u5bfb\u6b22\u6251\u4e86\u8fc7\u6765\uff0c\u624b\u8db3\u4e0a\u7684\u956f\u5b50\u5982\u6444\u9b42\u4e4b\u94c3\uff0c\u54cd \u58f0\u4e0d\u7edd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u662f\u542b\u7b11\u77a7\u7740\u4ed6\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u4f46\u865e\u4e8c\u9ebb\u5b50\u5374\u4e5f\u5ffd\u7136\u98de\u8d77\uff0c\u534a\u7a7a\u4e2d\u8fce\u4e0a\u4e86\u90a3\u9ec4\u8863\u7ae5\u5b50\uff0c\u62c9\u4f4f\u4ed6\u7684\u624b\u659c\u659c\u98de\u5230\u4e00\u8fb9\u3002\u2018\u91d1 \u72ee\u2019\u67e5\u731b\u4e5f\u7acb\u523b\u5927\u7b11\u9053\uff1a\u201c\u63a2\u82b1\u90ce\u5bb6\u8d22\u4e07\u8d2f\u83ab\u8bf4\u4e00\u4e2a\u96ea\u4eba\uff0c\u5c31\u7b97\u91d1\u4eba\u4ed6\u4e5f\u8d54\u5f97\u8d77\u7684\uff0c\u4f46\u56db\u4f4d \u5374\u4e0d\u53ef\u7740\u6025\uff0c\u5148\u5f85\u6211\u5f15\u89c1\u5f15\u89c1\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7ea2\u8863\u7ae5\u5b50\u7b11\u563b\u563b\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u59d3\u674e\uff0c\u53eb\u674e\u5bfb\u6b22\u3002\u201d \u3000\u3000\u53e6\u4e00\u9ed1\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6211\u8fd8\u77e5\u9053\u4ed6\u5403\u559d\u5ad6\u8d4c\uff0c\u6837\u6837\u7cbe\u901a\uff0c\u6240\u4ee5\u6211\u4eec\u65e9\u5c31\u60f3\u627e\u4ed6\u5e26\u6211\u4eec\u53bb\u5bfb \u5bfb\u6b22\uff0c\u627e\u627e\u4e50\u5b50\u4e86\u3002\u201d \u3000\u3000\u5269\u4e0b\u7684\u4e00\u4e2a\u7eff\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u6211\u8fd8\u77e5\u9053\u4ed6\u5b66\u95ee\u4e0d\u9519\uff0c\u4e2d\u8fc7\u7687\u5e1d\u8001\u513f\u70b9\u7684\u63a2\u82b1\uff0c\u542c\u8bf4\u4ed6\u8001\u5b50 \uff0c\u548c\u4ed6\u8001\u5b50\u7684\u8001\u5b50\u4e5f\u90fd\u662f\u63a2\u82b1\u3002\u201d \u3000\u3000\u7ea2\u8863\u7ae5\u5b50\u7b11\u563b\u563b\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u5c0f\u674e\u63a2\u82b1\u5374\u4e0d\u559c\u6b22\u505a\u5b98\uff0c\u53cd\u800c\u559c\u6b22\u505a\u5f3a\u76d7\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5728\u8fd9\u91cc\u8bf4\uff0c\u522b\u4eba\u8fd8\u672a\u89c9\u5f97\u600e\u6837\uff0c\u963f\u98de\u5374\u542c\u5f97\u51fa\u4e86\u795e\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u4ed6\u8fd9\u65b0\u4ea4\u7684\u670b\u53cb \uff0c\u7adf\u6709\u5982\u6b64\u591a\u59ff\u591a\u91c7\u7684\u4e00\u751f\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4eba\u53ea\u4e0d\u8fc7\u4ec5\u5c06\u674e\u5bfb\u6b22\u591a\u91c7\u7684\u4e00\u751f\uff0c\u8bf4\u51fa\u4e86\u4e00\u9cde\u534a\u722a\u800c\u5df2\uff0c\u674e\u5bfb\u6b22\u8fd9\u4e00\u751f \u7684\u6545\u4e8b\uff0c\u4ed6\u4eec\u5c31\u7b97\u4e0d\u505c\u5730\u8bf4\u4e09\u5929\u4e09\u591c\uff0c\u4e5f\u8bf4\u4e0d\u5b8c\u7684\u3002 \u3000\u3000\u963f\u98de\u4e5f\u672a\u53d1\u73b0\u674e\u5bfb\u6b22\u9762\u4e0a\u867d\u8fd8\u5e26\u7740\u5fae\u7b11\uff0c\u76ee\u4e2d\u5374\u9732\u51fa\u75db\u82e6\u4e4b\u8272\uff0c\u8c61\u662f\u522b\u4eba\u53ea\u8981\u4e00\u63d0\u53ca\u4ed6 \u7684\u5f80\u4e8b\uff0c\u5c31\u4ee4\u4ed6\u5fc3\u788e\u3002 \u3000\u3000\u7a81\u542c\u865e\u4e8c\u62d0\u5b50\u6c89\u7740\u8138\u9053\uff1a\u201c\u4f60\u4eec\u5bf9\u674e\u63a2\u82b1\u7684\u6545\u4e8b\u5b9e\u5728\u77e5\u9053\u4e0d\u5c11\uff0c\u4f46\u4f60\u4eec\u53ef\u542c\u8fc7\uff0c\u5c0f\u674e\u795e \u5200\uff0c\u51a0\u7edd\u5929\u4e0b\uff0c\u51fa\u624b\u4e00\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff01\u201d \u3000\u3000\u90a3\u9ec4\u8863\u7ae5\u5b50\u5403\u5403\u7b11\u9053\uff1a\u201c\u51fa\u624b\u4e00\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\u2026\u2026\u539f\u6765\u4f60\u662f\u6015\u6211\u88ab\u4ed6\u624b\u4e0a\u90a3\u628a\u5c0f\u5200\u5f04\u6b7b \uff0c\u56de\u53bb\u65e0\u6cd5\u5411\u6211\u5e08\u5085\u4ea4\u4ee3\uff0c\u6240\u4ee5\u624d\u62c9\u4f4f\u6211\u624b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f46\u5404\u4f4d\u53ea\u7ba1\u653e\u5fc3\uff0c\u5728\u4e0b\u7684\u7b2c\u4e8c\u5200\u5c31\u4e0d\u600e\u4e48\u6837\u9ad8\u660e\u4e86\uff0c\u800c\u4e00\u5200\u662f\u4e07\u4e07 \u6740\u4e0d\u6b7b\u516d\u4e2a\u4eba\u7684\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u4e5f\u6c89\u4e0b\u8138\uff0c\u77aa\u7740\u67e5\u731b\u9053\uff1a\u201c\u6240\u4ee5\u5404\u4f4d\u82e5\u662f\u60f3\u6765\u4e3a\u8bf8\u845b\u96f7\u590d\u4ec7\uff0c\u8fd8\u662f\u4e0d\u59a8\u52a8\u624b\uff01\u201d \u3000\u3000\u2018\u91d1\u72ee\u2019\u67e5\u731b\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u8bf8\u845b\u96f7\u81ea\u5df1\u8be5\u6b7b\uff0c\u600e\u4e48\u80fd\u602a\u674e\u5144\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5404\u4f4d\u65e2\u975e\u4e3a\u4e86\u590d\u4ec7\u800c\u6765\uff0c\u96be\u9053\u771f\u7684\u662f\u627e\u6211\u6765\u559d\u9152\u7684\u4e48\uff1f\u201d \u3000\u3000\u67e5\u731b\u6c89\u541f\u7740\uff0c\u8c61\u662f\u4e0d\u77e5\u8be5\u5982\u4f55\u63aa\u8bcd\u3002 \u3000\u3000\u865e\u4e8c\u62d0\u5b50\u5df2\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u53ea\u8981\u4f60\u5c06\u90a3\u5305\u88b1\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u5305\u88b1\uff1f\u201d \u3000\u3000\u67e5\u731b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5305\u88b1\u4e43\u662f\u522b\u4eba\u91cd\u6258\u7ed9\u2018\u91d1\u72ee\u9556\u5c40\u2019\u7684\uff0c\u82e5\u6709\u5931\u95ea\uff0c\u655d\u9556\u5c40\u6570\u5341\u5e74\u7684 \u58f0\u540d\u5c31\u4ece\u6b64\u6bc1\u4e8e\u4e00\u65e6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u4e86\u9ed1\u86c7\u7684\u5c38\u8eab\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5305\u88b1\u96be\u9053\u4e0d\u5728\u4ed6\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u67e5\u731b\u9053\uff1a\u201c\u674e\u5144\u8fd9\u662f\u8bf4\u7b11\uff0c\u6709\u674e\u5144\u5728\u573a\uff0c\u533a\u533a\u7684\u9ed1\u86c7\u600e\u4e48\u80fd\u5c06\u90a3\u5305\u88b1\u62ff\u5f97\u8d70\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u4e86\u76b1\u7709\uff0c\u53f9\u606f\u7740\u5583\u5583\u9053\uff1a\u201c\u6211\u5e73\u751f\u6700\u6015\u9ebb\u70e6\uff0c\u9ebb\u70e6\u4e3a\u4ec0\u4e48\u603b\u8981\u627e\u4e0a\u6211\uff1f\u201d \u3000\u3000\u67e5\u731b\u4e5f\u542c\u4e0d\u6e05\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u53ea\u8981\u674e\u5144\u80af\u5c06\u90a3\u5305\u88b1\u53d1\u8fd8\uff0c\u5728\u4e0b\u975e\u4f46\u7acb\u523b\u5c31\u8d70 \uff0c\u800c\u4e14\u591a\u5c11\u603b\u6709\u70b9\u5fc3\u610f\uff0c\u7ed9\u674e\u5144\u996e\u9152\u538b\u60ca\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u629a\u6478\u7740\u624b\u91cc\u7684\u5200\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5305\u88b1\u7684\u786e\u5728\u6211\u8fd9\u91cc\uff0c\u4f46\u6211\u5374\u8fd8\u672a\u51b3 \u5b9a\u662f\u5426\u5c06\u5b83\u8fd8\u7ed9\u4f60\u4eec\uff0c\u4f60\u4eec\u6700\u597d\u8ba9\u6211\u8003\u8651\u8003\u8651\u3002\u201d \u3000\u3000\u67e5\u731b\u9762\u4e0a\u5df2\u53d8\u4e86\u989c\u8272\uff0c\u865e\u4e8c\u62d0\u5b50\u5374\u62a2\u7740\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u9601\u4e0b\u8981\u8003\u8651\u591a\u4e45\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u65f6\u8fb0\u5c31\u5df2\u8db3\u591f\u4e86\uff0c\u4e00\u4e2a\u65f6\u8fb0\u540e\uff0c\u8fd8\u5728\u6b64\u5730\u76f8\u89c1\u3002\u201d \u3000\u3000\u865e\u4e8c\u62d0\u5b50\u60f3\u4e5f\u4e0d\u60f3\uff0c\u7acb\u523b\u9053\uff1a\u201c\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u4e0d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u6325\u624b\u5c31\u8d70\u3002 \u3000\u3000\u9ec4\u8863\u7ae5\u5b50\u5ffd\u7136\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u9003\u5f97\u5f88\u8fdc\u4e86\uff0c\u4f55\u5fc5\u8981\u4e00\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u865e\u4e8c\u62d0\u5b50\u6c89\u7740\u8138\u9053\uff1a\u201c\u5c0f\u674e\u63a2\u82b1\u81ea\u51fa\u9053\u4ee5\u540e\uff0c\u9000\u9690\u4e4b\u524d\uff0c\u4e03\u5e74\u4e2d\u8eab\u7ecf\u5927\u5c0f\u4e09\u767e\u4f59\u6218\uff0c\u4ece \u6765\u4e5f\u672a\u66fe\u9003\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u4ed6\u4eec\u6765\u5f97\u867d\u5feb\uff0c\u9000\u5f97\u66f4\u5feb\uff0c\u970e\u773c\u95f4\u5df2\u5168\u90fd\u5931\u53bb\u8e2a\u5f71\uff0c\u518d\u542c\u90a3\u6e05\u60a6\u7684\u624b\u956f\u58f0\uff0c\u5df2\u8fdc\u5728\u5341\u4f59 \u4e08\u5916\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u201c\u5305\u88b1\u5e76\u4e0d\u5728\u4f60\u624b\u4e0a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u5728\uff0c\u4f60\u4e3a\u4f55\u8981\u627f\u8ba4\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7eb5\u7136\u8bf4\u6ca1\u6709\u62ff\uff0c\u4ed6\u4eec\u4e5f\u7edd\u4e0d\u4f1a\u76f8\u4fe1\u7684\uff0c\u8fdf\u65e9\u8fd8\u662f\u96be\u514d\u51fa\u624b\u4e00\u6218 \uff0c\u6240\u4ee5\u6211\u5012\u4e0d\u5982\u7d22\u6027\u627f\u8ba4\u4e86\uff0c\u4e5f\u514d\u5f97\u8ddf\u4ed6\u4eec\u565c\u55e6\u9ebb\u70e6\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u65e2\u7136\u8fdf\u65e9\u96be\u514d\u4e00\u6218\uff0c\u4f60\u8fd8\u8003\u8651\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5728\u8fd9\u4e00\u4e2a\u65f6\u8fb0\u4e2d\uff0c\u6211\u8981\u5148\u627e\u5230\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5077\u90a3\u5305\u88b1\u7684\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6628\u5929\u90a3\u9152\u5e97\u4e2d\u6709\u4e09\u4e2a\u91d1\u72ee\u9556\u5c40\u7684\u9556\u5934\uff0c\u9664\u4e86\u8bf8\u845b\u96f7\u4f55\u90a3\u8d75\u8001\u4e8c\u5916 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u6211\u8981\u627e\u7684\u5c31\u662f\u4ed6\uff01\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u90a3\u7a7f\u7740\u4ef6\u7d2b\u7f0e\u56e2\u82b1\u76ae\u8884\uff0c\u8170\u4e0a\u4f3c\u4e4e\u7f20\u7740\u8f6f\u97ad\uff0c\u8033\u6735 \u8fd8\u6709\u64ae\u9ed1\u6bdb\u7684\u77ee\u5b50\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u77a7\u4e86\u4ed6\u4e24\u773c\uff0c\u60f3\u4e0d\u5230\u5df2\u5c06\u4ed6\u77a7\u5f97\u5982\u6b64\u4ed4\u7ec6\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u4e00\u773c\u5c31\u5df2\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8bf4\u7684\u5c31\u662f\u4ed6\uff0c\u6628\u5929\u5728\u9152\u5e97\u4e2d\u7684\u4eba\uff0c\u53ea\u6709\u4ed6\u77e5\u9053\u90a3\u5305\u88b1\u7684\u4ef7\u503c\uff0c\u4ed6 \u4e00\u76f4\u8eb2\u5728\u65c1\u8fb9\uff0c\u6ca1\u6709\u4eba\u6ce8\u610f\u4ed6\uff0c\u6240\u4ee5\u4e5f\u53ea\u6709\u4ed6\u6709\u673a\u4f1a\u62ff\u90a3\u5305\u88b1\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u601d\u7740\uff0c\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u77e5\u9053\u90a3\u5305\u88b1\u7684\u4ef7\u503c\uff0c\u6240\u4ee5\u5b58\u5fc3\u8981\u5c06\u4e4b\u541e\u6ca1\uff0c\u4f46\u4ed6\u5374\u6015\u67e5\u731b\u6000\u7591\u4e8e \u4ed6\uff0c\u6240\u4ee5\u5c31\u5c06\u8d23\u4efb\u63a8\u5230\u6211\u8eab\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u597d\u5728\u6211\u66ff\u522b\u4eba\u80cc\u9ed1\u9505\uff0c\u8fd9\u5df2\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u67e5\u731b\u4ed6\u4eec\u77e5\u9053\u4f60\u7684\u884c\u8e2a\uff0c\u81ea\u7136\u5c31\u662f\u4ed6\u53bb\u901a\u98ce\u62a5\u8baf\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u4e3a\u4e86\u6015\u67e5\u731b\u6000\u7591\u5230\u4ed6\uff0c\u6682\u65f6\u7edd\u4e0d\u6562\u9003\u8d70\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u73b0\u5728\u5fc5\u5b9a\u548c\u67e5\u731b\u4ed6\u4eec\u5728\u4e00\u9f50\uff0c\u53ea\u8981\u627e\u5230\u67e5\u731b\uff0c\u5c31\u53ef\u4ee5\u627e\u5f97\u5230\u4ed6\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u8981\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e09\u4e94\u5e74\uff0c\u5c31\u6ca1\u6709\u522b\u4eba\u597d\u6df7\u7684\u4e86\uff0c\u4ee5\u540e \u6211\u4eec\u82e5\u662f\u8fd8\u6709\u673a\u4f1a\u89c1\u9762\uff0c\u5e0c\u671b\u8fd8\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u63a5\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5b9e\u5728\u4e0d\u613f\u610f\u6709\u4f60\u8fd9\u6837\u7684\u4ec7\u654c\u3002\u201d \u3000\u3000\u963f\u98de\u9759\u9759\u5730\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8981\u6211\u8d70\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e8b\uff0c\u548c\u4f60\u5e76\u6ca1\u6709\u5173\u7cfb\uff0c\u522b\u4eba\u4e5f\u6ca1\u6709\u627e\u4f60\u2026\u2026\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u662f\u6015\u8fde\u7d2f\u4e86\u6211\uff0c\u8fd8\u662f\u5df2\u4e0d\u613f\u548c\u6211\u540c\u884c\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u9732\u51fa\u4e00\u4e1d\u75db\u82e6\u4e4b\u8272\uff0c\u5374\u8fd8\u662f\u5fae\u7b11\u7740\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u6563\u7684\u5bb4\u5e2d\uff0c\u6211\u4eec\u53cd\u6b63\u8fdf\u65e9 \u603b\u662f\u8981\u5206\u624b\u7684\uff0c\u65e9\u51e0\u5929\u8fdf\u51e0\u5929\uff0c\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u7740\uff0c\u5ffd\u7136\u81ea\u8f66\u53a2\u4e2d\u5012\u4e86\u4e24\u7897\u9152\uff0c\u9053\uff1a\u201c\u6211\u518d\u656c\u4f60\u4e00\u676f\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u63a5\u8fc7\u6765\u4e00\u996e\u800c\u5c3d\uff0c\u6162\u58f0\u9053\uff1a\u201c\u529d\u541b\u66f4\u5c3d\u4e00\u676f\u9152\uff0c\u4e0e\u5c14\u540c\u6d88\u4e07\u53e4\u6101\u2026\u2026\u201d \u3000\u3000\u4ed6\u60f3\u7b11\u4e00\u7b11\uff0c\u5374\u53c8\u5f2f\u4e0b\u8170\u53bb\uff0c\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u963f\u98de\u53c8\u9759\u9759\u5730\u671b\u4e86\u4ed6\u5f88\u4e45\uff0c\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8fb9\u53c8\u970f\u970f\u5730\u843d\u4e0b\u4e86\u96ea\u6765\uff0c\u5929\u5730\u95f4\u9759\u5f97\u751a\u81f3\u53ef\u4ee5\u542c\u5230\u96ea\u82b1\u98d8\u843d\u5728\u5730\u4e0a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u8fd9\u5c11\u5e74\u575a\u633a\u7684\u8eab\u5b50\u5728\u98ce\u96ea\u4e2d\u6e10\u6e10\u6d88\u5931\uff0c\u671b\u7740\u96ea\u5730\u4e0a\u90a3\u6f2b\u957f\u7684\uff0c\u5b64\u72ec\u7684\u811a\u5370\u2026 \u2026 \u3000\u3000\u4ed6\u7acb\u523b\u53c8\u5012\u4e86\u7897\u9152\uff0c\u9ad8\u4e3e\u7740\u9152\u676f\uff0c\u5583\u5583\u9053\uff1a\u201c\u6765\uff0c\u5c11\u5e74\u4eba\uff0c\u6211\u518d\u656c\u4f60\u4e00\u676f\uff0c\u4f60\u53ef\u77e5\u9053\u6211 \u5e76\u4e0d\u662f\u771f\u7684\u8981\u4f60\u8d70\uff0c\u53ea\u4e0d\u8fc7\u4f60\u524d\u7a0b\u8fdc\u5927\uff0c\u8ddf\u7740\u6211\u8d70\uff0c\u6c38\u8fdc\u6ca1\u597d\u5904\u7684\uff0c\u6211\u8fd9\u4eba\u597d\u8c61\u5df2\u548c\u5012\u9709\uff0c \u9ebb\u70e6\uff0c\u5371\u9669\uff0c\u4e0d\u5e78\u7684\u4e8b\u4ea4\u6210\u4e86\u597d\u670b\u53cb\uff0c\u6211\u5df2\u4e0d\u80fd\u518d\u4ea4\u522b\u7684\u670b\u53cb\u4e86\uff01\u201d \u3000\u3000\u963f\u98de\u81ea\u7136\u5df2\u542c\u4e0d\u5230\u4ed6\u7684\u8bdd\u4e86\u3002 \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c49\u59cb\u7ec8\u5c31\u8c61\u77f3\u50cf\u822c\u7ad9\u5728\u4e00\u8fb9\uff0c\u65e2\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6ee1\u8eab\u867d\u5df2\u79ef\u6ee1\u4e86\u51b0\u96ea\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u52a8 \u4e00\u52a8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u996e\u5c3d\u4e86\u676f\u4e2d\u7684\u9152\uff0c\u624d\u8f6c\u8eab\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u6700\u597d\u5c06\u8fd9\u6761\u86c7\u7684\u5c38\u4f53 \u4e5f\u57cb\u8d77\u6765\uff0c\u6211\u2026\u2026\u6211\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5782\u4e0b\u4e86\u5934\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u91d1\u72ee\u67e5\u731b\u867d\u4ee5\u638c\u529b\u96c4\u6d51\u6210\u540d\uff0c\u4f46\u5374\u53ea\u4e0d\u8fc7\u662f\u5f92\u6709 \u865a\u540d\u800c\u5df2\uff0c\u5c11\u7237\u4f60\u5728\u56db\u5341\u62db\u5185\u5c31\u53ef\u53d6\u4ed6\u9996\u7ea7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd8\u7528\u4e0d\u7740\u5341\u62db\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u865e\u4e8c\u62d0\u5b50\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u8f7b\u529f\u4e0d\u9519\uff0c\u636e\u8bf4\u6697\u5668\u4e5f\u5f88\u6bd2\u8fa3\uff0c\u4f46\u6211\u8fd8\u662f\u8db3\u53ef\u5bf9\u4ed8\u4ed6\u7684\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u636e\u8bf4\u2018\u6781\u4e50\u5cd2\u2019\u95e8\u4e0b\u6bcf\u4eba\u90fd\u6709\u51e0\u624b\u5f88\u90aa\u6c14\u7684\u5916\u95e8\u529f\u592b\uff0c\u65b9\u624d\u770b\u4ed6\u4eec\u7684\u51fa \u624b\uff0c\u679c\u7136\u548c\u4e2d\u539f\u7684\u6b66\u529f\u8def\u6570\u4e0d\u540c\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u5c31\u51ed\u8fd9\u4e9b\u4eba\uff0c\u6211\u8fd8\u672a\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7684\u9762\u8272\u5374\u5f88\u6c89\u91cd\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5c11\u7237\u4e5f\u7528\u4e0d\u7740\u7792\u6211\uff0c\u6211\u77e5\u9053\u6b64\u884c\u82e5\u975e\u6781\u51f6\u9669\uff0c\u5c11 \u7237\u5c31\u7edd\u4e0d\u4f1a\u8ba9\u90a3\u4f4d\u2026\u2026\u90a3\u4f4d\u98de\u5c11\u7237\u8d70\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u677f\u8d77\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u4e5f\u53d8\u5f97\u591a\u5634\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u679c\u7136\u4e0d\u6562\u518d\u8bf4\u4ec0\u4e48\uff0c\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u7b49\u4ed6\u62ac\u8d77\u5934\u6765\u65f6\uff0c\u674e\u5bfb\u6b22\u5df2\u8d70\u5165\u6811\u6797\uff0c\u4f3c\u4e4e \u53c8\u5728\u54b3\u55fd\u7740\u3002 \u3000\u3000\u8fd9\u65ad\u7eed\u7684\u54b3\u55fd\u58f0\u5728\u98ce\u96ea\u4e2d\u542c\u6765\uff0c\u5b9e\u5728\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u4f46\u98ce\u96ea\u7ec8\u4e8e\u8fde\u4ed6\u7684\u54b3\u55fd\u58f0\u4e5f\u4e00\u9f50\u541e\u6ca1\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u76ee\u4e2d\u5df2\u6cdb\u8d77\u6cea\u5149\uff0c\u9eef\u7136\u9053\uff1a\u201c\u5c11\u7237\uff0c\u54b1\u4eec\u5728\u5173\u5916\u8fc7\u5f97\u597d\u597d\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u8981\u5165 \u5173\u6765\u53d7\u82e6\u5462\uff1f\u5341\u5e74\u4e4b\u540e\uff0c\u4f60\u96be\u9053\u8fd8\u5fd8\u4e0d\u4e86\u5979\uff1f\u8fd8\u60f3\u89c1\u5979\u4e00\u9762\uff1f\u53ef\u662f\u4f60\u89c1\u7740\u5979\u4e4b\u540e\uff0c\u8fd8\u662f\u4e0d\u4f1a \u548c\u5979\u8bf4\u8bdd\u7684\uff0c\u5c11\u7237\u4f60\u2026\u2026\u4f60\u8fd9\u53c8\u4f55\u82e6\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u4e00\u8fdb\u4e86\u6811\u6797\uff0c\u674e\u5bfb\u6b22\u90a3\u79cd\u61d2\u6563\uff0c\u843d\u5bde\u7684\u795e\u60c5\u5c31\u5b8c\u5168\u6539\u53d8\u4e86\uff0c\u4ed6\u5ffd\u7136\u53d8\u5f97\u5c31\u8c61\u6761\u730e\u72ac\u90a3\u4e48 \u8f7b\u6377\uff0c\u77eb\u5065\u3002 \u3000\u3000\u4ed6\u7684\u8033\u6735\uff0c\u9f3b\u5b50\uff0c\u773c\u775b\uff0c\u4ed6\u5168\u8eab\u7684\u6bcf\u4e00\u6839\u808c\u8089\uff0c\u90fd\u5df2\u6709\u6548\u5730\u8fd0\u7528\uff0c\u96ea\u5730\u4e0a\uff0c\u67af\u679d\u95f4\u751a\u81f3 \u7a7a\u6c14\u91cc\uff0c\u53ea\u8981\u6709\u4e00\u4e1d\u654c\u4eba\u7559\u4e0b\u7684\u75d5\u8ff9\uff0c\u4e00\u4e1d\u5f02\u6837\u7684\u6c14\u606f\uff0c\u4ed6\u90fd\u7edd\u4e0d\u4f1a\u9519\u8fc7\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u4e16\u4e0a \u4ece\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u9003\u5f97\u8fc7\u4ed6\u7684\u8ffd\u8e2a\u3002 \u3000\u3000\u4ed6\u884c\u52a8\u867d\u5feb\u5982\u8131\u5154\uff0c\u4f46\u770b\u6765\u5e76\u4e0d\u6025\u8e81\u5306\u5fd9\uff0c\u5c31\u8c61\u662f\u4e2a\u7edd\u9876\u7684\u821e\u8e48\u8005\uff0c\u65e0\u8bba\u5728\u591a\u4e48\u6025\u9aa4\u7684 \u8282\u594f\u4e0b\uff0c\u90fd\u8fd8\u662f\u80fd\u4fdd\u6301\u4ed6\u4f18\u7f8e\u67d4\u548c\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u5341\u5e74\u524d\uff0c\u4ed6\u653e\u5f03\u4e86\u4ed6\u6240\u6709\u7684\u4e00\u5207\uff0c\u9eef\u7136\u51fa\u5173\u53bb\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u8def\u8fc7\u8fd9\u91cc\uff0c\u90a3\u65f6\u6b63\u662f\u6625\u6696\u82b1 \u5f00\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u8fd9\u9644\u8fd1\u6709\u4e2a\u5c0f\u5c0f\u7684\u9152\u5bb6\uff0c\u8fdc\u8fdc\u5c31\u53ef\u4ee5\u770b\u5230\u90a3\u9ad8\u6311\u7684\u9752\u5e18\uff0c\u6240\u4ee5\u4ed6\u4e5f\u4f1a\u505c\u4e0b\u8f66\u6765\uff0c \u53bb\u559d\u4e86\u51e0\u65a4\u9152\u3002 \u3000\u3000\u9152\u867d\u4e0d\u4f73\uff0c\u4f46\u90a3\u5730\u65b9\u9762\u5bf9\u9752\u5c71\uff0c\u895f\u5e26\u7eff\u6c34\uff0c\u6625\u65e5\u91cc\u7684\u6e38\u4eba\u5f88\u591a\uff0c\u4ed6\u671b\u7740\u90a3\u4e9b\u6b22\u7b11\u7740\u7684\u7ea2 \u7537\u7eff\u5973\uff0c\u4e00\u676f\u676f\u559d\u7740\u81ea\u5df1\u7684\u82e6\u9152\uff0c\u51c6\u5907\u4ece\u6b64\u5411\u8fd9\u5341\u4e08\u8f6f\u7ea2\u544a\u522b\uff0c\u8fd9\u5370\u8c61\u4ee4\u4ed6\u6c38\u8fdc\u4e5f\u4e0d\u80fd\u5fd8\u8bb0 \u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u60f3\u4e0d\u5230\u81ea\u5df1\u53c8\u56de\u5230\u8fd9\u91cc\uff0c\u7ecf\u8fc7\u4e86\u5341\u5e74\u7684\u5c81\u6708\uff0c\u4eba\u9762\u60f3\u5fc5\u5df2\u5168\u975e\uff0c\u6614\u65e5\u7684\u5782\u9aeb\u5e7c \u5973\uff0c\u5982\u4eca\u4e5f\u8bb8\u5df2\u5ac1\u4f5c\u4eba\u5987\uff0c\u6614\u65e5\u7684\u6069\u7231\u592b\u59bb\uff0c\u5982\u4eca\u4e5f\u8bb8\u5df2\u5f52\u4e8e\u9ec4\u571f\uff0c\u5c31\u8fde\u6614\u65e5\u7684\u6843\u82b1\uff0c\u5982\u4eca \u5df2\u88ab\u63a9\u57cb\u5728\u51b0\u96ea\u91cc\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5e0c\u671b\u90a3\u5c0f\u5c0f\u7684\u9152\u5bb6\u4ecd\u5728\u3002 \u3000\u3000\u4ed6\u8fd9\u4e48\u60f3\uff0c\u5012\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u6355\u6349\u5f80\u65e5\u7684\u56de\u5fc6\uff0c\u800c\u662f\u4ed6\u8ba4\u4e3a\u91d1\u72ee\u67e5\u731b\u4ed6\u4eec\u8bf4\u4e0d\u5b9a\u5c31\u843d\u811a\u5728 \u90a3\u9152\u5bb6\u91cc\u3002 \u3000\u3000\u51b0\u96ea\u4e2d\u7684\u4e16\u754c\uff0c\u867d\u7136\u548c\u6625\u98ce\u4e2d\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u4ed6\u7ecf\u8fc7\u8fd9\u6761\u8def\u65f6\uff0c\u5fc3\u91cc\u4ecd\u4e0d\u7981\u9690\u9690\u611f\u89c9\u5230\u4e00 \u9635\u9635\u523a\u75db\u3002 \u3000\u3000\u8d22\u5bcc\u3001\u6743\u52bf\u3001\u540d\u8a89\u548c\u5730\u4f4d\uff0c\u90fd\u6bd4\u8f83\u5bb9\u6613\u820d\u5f03\uff0c\u53ea\u662f\u90a3\u4e9b\u56de\u5fc6\uff0c\u90a3\u4e9b\u8f9b\u9178\u591a\u4e8e\u751c\u871c\u7684\u56de\u5fc6 \uff0c\u5374\u8c61\u662f\u6c89\u91cd\u7684\u67b7\u9501\uff0c\u662f\u6c38\u8fdc\u4e5f\u629b\u4e0d\u5f00\uff0c\u7529\u4e0d\u8131\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u81ea\u6000\u4e2d\u6478\u51fa\u4e2a\u6241\u6241\u7684\u9152\u74f6\uff0c\u5c06\u74f6\u4e2d\u7684\u9152\u5168\u704c\u8fdb\u5589\u5499\uff0c\u7b49\u54b3\u55fd\u505c\u6b62\u4e4b\u540e\uff0c\u624d\u518d\u5f80\u524d \u8d70\u3002 \u3000\u3000\u4ed6\u679c\u7136\u770b\u5230\u4e86\u90a3\u5c0f\u5c0f\u7684\u9152\u5bb6\u3002 \u3000\u3000\u90a3\u662f\u5efa\u7b51\u5728\u5c71\u811a\u4e0b\u7684\u51e0\u95f4\u655e\u8f69\uff0c\u5c4b\u5916\u56db\u9762\u90fd\u6709\u5bbd\u9614\u7684\u8d70\u5eca\uff0c\u6731\u7ea2\u7684\u680f\u6746\uff0c\u914d\u4e0a\u78a7\u7eff\u7684\u7eb1 \u7a97\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u6625\u65e5\u91cc\u8fd9\u91cc\u56db\u9762\u90fd\u5f00\u904d\u4e86\u4e00\u79cd\u4e0d\u77e5\u540d\u7684\u5c71\u82b1\uff0c\u7f24\u7eb7\u99a5\u90c1\uff0c\u501a\u7740\u6731\u7ea2\u7684\u680f\u6746\u8d4f\u82b1\u996e \u9152\uff0c\u6de1\u9152\u4e5f\u53d8\u6210\u4e86\u4f73\u917f\u3002 \u3000\u3000\u5982\u4eca\u680f\u6746\u4e0a\u7684\u7ea2\u6f06\u5df2\u5265\u843d\uff0c\u7ea2\u82b1\u4e5f\u88ab\u767d\u96ea\u4ee3\u66ff\uff0c\u767d\u96ea\u4e0a\u8f66\u8f99\u9a6c\u8e44\u7eb5\u6a2a\uff0c\u8fd8\u53ef\u4ee5\u542c\u5230\u5c4b\u540e \u6709\u9a6c\u5636\u58f0\u968f\u98ce\u4f20\u51fa\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77e5\u9053\u81ea\u5df1\u6ca1\u6709\u731c\u9519\uff0c\u67e5\u731b\u4ed6\u4eec\u679c\u7136\u843d\u811a\u5728\u8fd9\u91cc\uff01\u56e0\u4e3a\u5728\u8fd9\u79cd\u5929\u6c14\uff0c\u8fd9\u79cd\u5730\u65b9\u7edd\u4e0d \u4f1a\u6709\u5176\u4ed6\u6e38\u5ba2\u7684\u3002 \u3000\u3000\u4ed6\u7684\u884c\u52a8\u66f4\u5feb\uff0c\u66f4\u5c0f\u5fc3\uff0c\u9759\u9759\u5730\u542c\u4e86\u534a\u664c\uff0c\u9152\u5e97\u91cc\u5e76\u6ca1\u6709\u4eba\u58f0\uff0c\u4ed6\u76b1\u4e86\u76b1\u7709\uff0c\u7bad\u4e00\u822c\u7a9c \u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5230\u4e86\u8fd1\u524d\uff0c\u5c31\u53ef\u4ee5\u53d1\u89c9\u8fd9\u9152\u5e97\u5b9e\u5728\u9759\u5f97\u51fa\u5947\uff0c\u9664\u4e86\u5076\u5c14\u6709\u4f4e\u4f4e\u7684\u9a6c\u5636\u5916\uff0c\u522b\u7684\u58f0\u97f3\u4e00\u4e1d \u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u8d70\u5eca\u4e0a\u7684\u5730\u677f\u5df2\u8150\u65e7\uff0c\u674e\u5bfb\u6b22\u7684\u811a\u521a\u8e0f\u4e0a\u53bb\uff0c\u5c31\u53d1\u51fa\u2018\u5431\u2019\u7684\u4e00\u58f0\uff0c\u4ed6\u7acb\u523b\u540e\u9000\u4e86\u5341\u51e0 \u5c3a\u3002 \u3000\u3000\u4f46\u9152\u5e97\u91cc\u4ecd\u7136\u4e00\u70b9\u52a8\u9759\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u4e00\u6c89\u541f\uff0c\u8f7b\u5feb\u5730\u7ed5\u5230\u5c4b\u5b50\u540e\u9762\uff0c\u4ed6\u5fc3\u91cc\u5728\u731c\u6d4b\uff0c\u4e5f\u8bb8\u2018\u91d1\u72ee\u2019\u67e5\u731b\u5e76\u6ca1\u6709\u56de\u5230 \u8fd9\u91cc\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5374\u7acb\u523b\u5c31\u89c1\u5230\u4e86\u67e5\u731b\uff01 \u3000\u3000\u67e5\u731b\u7adf\u6b63\u5728\u76f4\u7740\u773c\u775b\uff0c\u77aa\u7740\u4ed6\uff01 \u3000\u3000\u67e5\u731b\u7684\u773c\u775b\u51e0\u4e4e\u5b8c\u5168\u51f8\u4e86\u51fa\u6765\uff0c\u6de1\u91d1\u8272\u7684\u8138\u770b\u6765\u7adf\u5df2\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u72f0\u72de\u53ef\u6015\uff0c\u4ed6\u5c31\u7ad9\u5728 \u9a6c\u5eca\u524d\u7684\u4e00\u6839\u67f1\u5b50\u65c1\u3002 \u3000\u3000\u5eca\u4e2d\u7684\u9a6c\u5728\u4f4e\u5636\u7740\uff0c\u8e22\u7740\u811a\uff0c\u67e5\u731b\u5374\u53ea\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u65e2\u4e0d\u51fa\u58f0\uff0c\u4e5f\u4e0d\u52a8\uff0c\u5c31\u8c61\u662f\u4e2a\u6ce5\u5851 \u7684\uff0c\u8fd8\u672a\u7740\u8272\u7684\u4eba\u50cf\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff01\u2026\u2026\u201d \u3000\u3000\u4ed6\u53ea\u8bf4\u4e86\u4e09\u4e2a\u5b57\uff0c\u5c31\u7acb\u523b\u505c\u4f4f\u4e86\u5634\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u53d1\u89c9\u67e5\u731b\u662f\u518d\u4e5f\u542c\u4e0d\u5230\u4efb\u4f55\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e86\u3002 \u7b2c\u4e09\u7ae0\u3000\u5b9d\u7269\u52a8\u4eba\u5fc3>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u7ae0\u3000\u5b9d\u7269\u52a8\u4eba\u5fc3 \u3000\u3000\u674e\u5bfb\u6b22\u518d\u4e00\u6ce8\u89c6\uff0c\u90a3\u67e5\u731b\u7684\u54bd\u5589\uff0c\u7adf\u5df2\u88ab\u6d1e\u7a7f\uff01\u6740\u4ed6\u7684\u4eba\u663e\u7136\u4e0d\u613f\u4ed6\u7684\u9c9c\u8840\u6e85\u4e0a\u81ea\u5df1\u7684 \u8863\u88f3\uff0c\u6240\u4ee5\u4e00\u5251\u523a\u7a7f\u4ed6\u7684\u54bd\u5589\u540e\uff0c\u5c31\u7acb\u5373\u585e\u4e86\u56e2\u51b0\u96ea\u5728\u521b\u53e3\u91cc\uff0c\u7b49\u5230\u51b0\u96ea\u88ab\u70ed\u8840\u6eb6\u5316\u7684\u65f6\u5019 \uff0c\u8840\u5374\u4e5f\u5df2\u88ab\u51b0\u51dd\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5c38\u4f53\u4ecd\u7b14\u76f4\u7684\u7ad9\u7740\uff0c\u501a\u7740\u6728\u67f1\u5e76\u6ca1\u6709\u5012\u4e0b\u6765\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u6740\u4ed6\u7684\u90a3\u4eba\uff0c\u8eab\u6cd5\u662f\u591a\u4e48 \u8f7b\uff0c\u591a\u4e48\u5feb\uff01\u4ed6\u4e00\u5251\u523a\u7a7f\u67e5\u731b\u7684\u54bd\u5589\u540e\uff0c\u5c31\u7acb\u5373\u62d4\u51fa\u4e86\u5251\uff0c\u8fde\u4e00\u4e1d\u591a\u9980\u7684\u529b\u91cf\u90fd\u6ca1\u6709\uff0c\u6240\u4ee5 \u624d\u6ca1\u6709\u78b0\u5012\u67e5\u731b\u7684\u5c38\u4f53\u3002 \u3000\u3000\u67e5\u731b\u81ea\u7136\u662f\u51c6\u5907\u62b5\u6297\u7684\uff0c\u4f46\u7b49\u5230\u8fd9\u4e00\u5251\u523a\u7a7f\u54bd\u5589\u540e\uff0c\u4ed6\u7684\u62db\u5f0f\u8fd8\u6ca1\u6709\u4f7f\u51fa\u6765\uff0c\u6240\u4ee5\u4ed6\u7684 \u5c38\u4f53\u4ecd\u5728\u4fdd\u6301\u7740\u5e73\u8861\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u597d\u5feb\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9762\u4e0a\u9732\u51fa\u4e86\u60ca\u5947\u4e4b\u5f69\u8272\uff0c\u4ed6\u77e5\u9053\u2018\u91d1\u72ee\u2019\u67e5\u731b\u6210\u540d\u5df2\u4e8c\u5341\u591a\u5e74\uff0c\u5e76\u6ca1\u6709\u5403\u8fc7\u591a\u5927 \u7684\u4e8f\u3002 \u3000\u3000\u91d1\u72ee\u9556\u5c40\u7684\u62db\u724c\u4e5f\u5f88\u786c\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u67e5\u731b\u5e76\u975e\u5f31\u8005\uff0c\u4f46\u4ed6\u5374\u53cd\u6297\u4e4b\u529b\u90fd\u6ca1\u6709\uff0c\u4e00\u5251\u5c31\u88ab \u4eba\u6d1e\u7a7f\u4e86\u54bd\u5589\uff01 \u3000\u3000\u4ed6\u5c31\u7b97\u662f\u4e2a\u6728\u5934\u4eba\uff0c\u8981\u60f3\u4e00\u5251\u5c06\u8fd9\u6728\u5934\u4eba\u7684\u54bd\u5589\u523a\u7a7f\uff0c\u800c\u4e0d\u5c06\u5b83\u649e\u5012\uff0c\u4e5f\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613 \u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u8f6c\u8eab\uff0c\u7a9c\u5165\u90a3\u9152\u5e97\u91cc\uff0c\u95e8\u4e0a\u5e76\u6ca1\u6709\u6302\u5e18\u5b50\uff0c\u91cc\u9762\u4e5f\u6ca1\u6709\u6446\u4e0a\u684c\u6905\uff0c\u663e\u89c1\u8fd9\u9152\u5e97 \u4e5f\u5e76\u4e0d\u60f3\u5728\u8fd9\u79cd\u5929\u6c14\u505a\u751f\u610f\u3002 \u3000\u3000\u5f88\u5bbd\u655e\u7684\u5c4b\u5b50\u91cc\uff0c\u53ea\u6709\u9760\u7a97\u65c1\u6446\u7740\u4e00\u684c\u83dc\uff0c\u4f46\u83dc\u5927\u591a\u90fd\u6ca1\u6709\u52a8\u8fc7\uff0c\u751a\u81f3\u8fde\u676f\u91cc\u7684\u9152\u90fd\u6ca1 \u6709\u559d\u3002 \u3000\u3000\u6765\u81ea\u6781\u4e50\u5cd2\u7684\u90a3\u56db\u4e2a\u2018\u7ae5\u5b50\u2019\uff0c\u4e5f\u5df2\u53d8\u6210\u4e86\u56db\u4e2a\u6b7b\u5c38\uff01 \u3000\u3000\u6b7b\u5c38\u7684\u5934\u5411\u5916\uff0c\u8db3\u5411\u91cc\uff0c\u50cf\u662f\u2018\u5341\u2019\u5b57\uff0c\u9ec4\u8863\u7ae5\u5b50\u7684\u8db3\u5e95\u548c\u7eff\u8863\u7ae5\u76f8\u5bf9\uff0c\u9ed1\u8863\u7ae5\u548c\u7ea2\u8863 \u7ae5\u76f8\u5bf9\uff0c\u53f3\u624b\u8155\u4e0a\u7684\u91d1\u956f\u5df2\u892a\u4e0b\uff0c\u843d\u5728\u624b\u8fb9\uff0c\u56db\u4eba\u7684\u8138\u4e0a\u8fd8\u5e26\u7740\u72de\u7b11\uff0c\u54bd\u5589\u7adf\u4e5f\u662f\u88ab\u4e00\u5251\u523a \u7a7f\u7684\uff01 \u3000\u3000\u518d\u770b\u865e\u4e8c\u62d0\u5b50\uff0c\u4e5f\u5df2\u5012\u5728\u89d2\u843d\u91cc\u7684\u4e00\u4e2a\u67f1\u5b50\u65c1\uff0c\u4ed6\u7684\u53cc\u624b\u7d27\u63e1\uff0c\u4f3c\u4e4e\u8fd8\u63e1\u7740\u6ee1\u628a\u6697\u5668\u3002 \u3000\u3000\u4f46\u6697\u5668\u8fd8\u672a\u53d1\u51fa\uff0c\u4ed6\u4e5f\u5df2\u88ab\u4e00\u5251\u523a\u7a7f\u54bd\u5589\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u77e5\u662f\u60ca\u5947\uff0c\u8fd8\u662f\u6b22\u559c\uff0c\u53ea\u662f\u4e0d\u4f4f\u5583\u5583\u9053\uff1a\u201c\u597d\u5feb\u7684\u5251\u2026\u2026\u597d\u5feb\u7684\u5251\u2026\u2026\u201d \u3000\u3000\u82e5\u5728\u4e24\u65e5\u4ee5\u524d\uff0c\u4ed6\u5b9e\u5728\u731c\u4e0d\u51fa\u666e\u5929\u4e4b\u4e0b\uff0c\u662f\u8c01\u6709\u8fd9\u4e48\u5feb\u7684\u5251\u6cd5\uff0c\u6614\u5e74\u65e9\u79f0\u5f53\u4ee3\u7b2c\u4e00\u5251\u5ba2 \u7684\u5929\u5c71\u2018\u96ea\u9e70\u5b50\u2019\uff0c\u5251\u6cd5\u867d\u4e5f\u4ee5\u8f7b\u6377\u98d8\u5ffd\u89c1\u957f\uff0c\u4f46\u51fa\u624b\u7edd\u4e0d\u4f1a\u6709\u5982\u6b64\u72e0\u8fa3\uff0c\u4f55\u51b5\u81ea\u4ece\u9e70\u6101\u6da7 \u4e00\u5f79\u4e4b\u540e\uff0c\u8fd9\u4f4d\u4e0d\u53ef\u4e00\u4e16\u7684\u540d\u5251\u5ba2\u5df2\u5c01\u5251\u5f52\u9690\uff0c\u5230\u5982\u4eca\u53ea\u6015\u4e5f\u57cb\u9aa8\u5728\u5929\u5c71\u7edd\u9876\uff0c\u4e98\u53e4\u4e0d\u5316\u7684 \u51b0\u96ea\u4e0b\u4e86\u3002 \u3000\u3000\u81f3\u4e8e\u6614\u65e5\u7eb5\u6a2a\u5929\u4e0b\u7684\u540d\u4fa0\uff0c\u6c88\u6d6a\uff0c\u718a\u732b\u513f\uff0c\u738b\u601c\u82b1\uff0c\u636e\u8bf4\u65e9\u5df2\u90fd\u4e70\u821f\u5165\u6d77\uff0c\u53bb\u5bfb\u6d77\u5916\u7684 \u4ed9\u5c71\uff0c\u4e45\u5df2\u4e0d\u5728\u4eba\u95f4\u4e86\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u4eec\u7528\u7684\u90fd\u4e0d\u662f\u5251\uff01 \u3000\u3000\u9664\u4e86\u8fd9\u4e9b\u4eba\u4e4b\u5916\uff0c\u674e\u5bfb\u6b22\u5b9e\u5728\u60f3\u4e0d\u51fa\u4e16\u4e0a\u8fd8\u6709\u8c01\u7684\u5251\u5982\u6b64\u5feb\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u5df2\u77e5\u9053\u662f\u8fd8 \u6709\u8fd9\u4e48\u4e00\u4e2a\u4eba\u7684\u3002 \u3000\u3000\u5c31\u662f\u90a3\u795e\u79d8\u3001\u5b64\u72ec\uff0c\u800c\u5fe7\u90c1\u7684\u5c11\u5e74\u963f\u98de\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u8d77\u773c\u775b\uff0c\u5f77\u4f5b\u5c31\u53ef\u4ee5\u770b\u5230\u4ed6\u843d\u5bde\u7684\u8d70\u5165\u8fd9\u5c4b\u5b50\u91cc\uff0c\u6781\u4e50\u5cd2\u7684\u62a4\u6cd5\u7ae5\u5b50\u4eec\u7acb\u523b\u8fce \u4e86\u4e0a\u53bb\uff0c\u5c06\u4ed6\u5305\u56f4\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u91d1\u956f\u892a\u4e0b\uff0c\u9762\u4e0a\u7684\u72de\u7b11\u8fd8\u672a\u6d88\u5931\uff0c\u963f\u98de\u7684\u5251\u5df2\u5982\u95ea\u7535\uff0c\u5982\u6bd2\u86c7\u822c\u5c06\u4ed6\u4eec\u7684\u54bd\u5589 \u523a\u7a7f\u3002 \u3000\u3000\u865e\u4e8c\u62d0\u5b50\u5728\u4e00\u65c1\u60f3\u53d1\u6697\u5668\uff0c\u4ed6\u4ee5\u8f7b\u529f\u548c\u6697\u5668\u6210\u540d\uff0c\u624b\u811a\u81ea\u7136\u6781\u5feb\uff0c\u4f46\u4ed6\u7684\u624b\u521a\u6293\u8d77\u6697\u5668 \uff0c\u8fd8\u672a\u53d1\u51fa\uff0c\u5251\u5df2\u98de\u6765\uff0c\u4e00\u5251\u7a7f\u5589\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u73a9\u5177\uff0c\u5c45\u7136\u6709\u4eba\u8bf4\u4ed6\u7684\u5251\u50cf\u73a9\u5177\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u67f1\u5b50\u4e0a\u6709\u7528\u5251\u5c16\u5212\u51fa\u6765\u7684\u5b57\uff1a\u201c\u4f60\u66ff\u6211\u6740\u4e86\u8bf8\u845b\u96f7\uff0c\u6211\u5c31\u66ff\u4f60\u6740\u8fd9\u4e9b\u4eba\uff0c\u6211 \u4e0d\u518d\u6b20\u4f60\u7684\u503a\u4e86\uff0c\u6211\u77e5\u9053\u4e00\u4e2a\u4eba\u7edd\u4e0d\u80fd\u6b20\u503a\uff01\u201d \u3000\u3000\u770b\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u4e0d\u7981\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u66ff\u4f60\u6740\u4e86\u4e00\u4e2a\u4eba\uff0c\u4f60\u5374\u66ff\u6211\u6740\u4e86\u516d\u4e2a\uff0c\u4f60\u77e5\u9053\u4e00 \u4e2a\u4eba\u4e0d\u80fd\u6b20\u503a\uff0c\u4e3a\u4f55\u8981\u6211\u6b20\u4f60\u7684\u503a\u5462\uff1f\u201d\u4ed6\u53c8\u63a5\u7740\u770b\u4e0b\u53bb\uff01 \u3000\u3000\u201c\u6211\u66ff\u4f60\u6740\u7684\u4eba\u867d\u591a\u4e9b\uff0c\u4f46\u60c5\u51b5\u4e0d\u540c\uff0c\u4f60\u6740\u7684\u4e00\u4e2a\u8db3\u53ef\u62b5\u5f97\u4e0a\u8fd9\u516d\u4e2a\uff0c\u6240\u4ee5\u4f60\u4e5f\u4e0d\u6b20\u6211 \uff0c\u6211\u4e5f\u4e0d\u613f\u522b\u4eba\u6b20\u6211\u7684\u503a\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5e10\u7b97\u7684\u4e0d\u592a\u7cbe\u660e\uff0c\u770b\u6765\u4ee5\u540e\u505a\u4e0d\u5f97\u751f\u610f\u3002\u201d \u3000\u3000\u67f1\u5b50\u4e0a\u53ea\u6709\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5374\u8fd8\u6709\u4e2a\u7bad\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u81ea\u7136\u7acb\u523b\u987a\u7740\u8fd9\u7bad\u5934\u6240\u6307\u7684\u65b9\u5411\u8d70\u8fc7\u53bb\uff0c\u521a\u8d70\u8fdb\u4e00\u6247\u95e8\uff0c\u4ed6\u5c31\u542c\u5230\u4e86\u4e00\u58f0\u60ca\u547c\uff01 \u3000\u3000\u6709\u67c4\u5f88\u4eae\u7684\u5251\uff0c\u5251\u5c16\u6b63\u6307\u7740\u4ed6\uff01 \u3000\u3000\u5251\u5c16\uff0c\u5728\u5fae\u5fae\u7684\u98a4\u6296\u7740\uff01 \u3000\u3000\u63e1\u5251\u7684\u662f\u4e2a\u5f88\u53d1\u798f\u7684\u8001\u4eba\uff0c\u80e1\u5b50\u867d\u8fd8\u6ca1\u6709\u767d\uff0c\u4f46\u8138\u4e0a\u7684\u76b1\u7eb9\u5df2\u5f88\u591a\uff0c\u53ef\u89c1\u5e74\u7eaa\u5df2\u4e0d\u5c0f\u4e86 \u3002 \u3000\u3000\u8fd9\u8001\u4eba\u53cc\u624b\u63e1\u5251\uff0c\u5bf9\u7740\u674e\u5bfb\u6b22\u5927\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u5c3d\u91cf\u60f3\u8bf4\u5f97\u5927\u58f0\u4e9b\uff0c\u53ef\u662f\u58f0\u97f3\u504f\u504f\u6709\u4e9b\u53d1\u6296\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u8ba4\u51fa\u4ed6\u662f\u8c01\u4e86\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4eba\uff1f\u201d \u3000\u3000\u8001\u4eba\u53ea\u662f\u5728\u6447\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u5374\u8ba4\u5f97\u4f60\u5c31\u662f\u8fd9\u91cc\u7684\u8001\u677f\uff0c\u5341\u5e74\u524d\uff0c\u4f60\u8fd8\u966a\u8fc7\u6211\u559d\u4e86\u51e0\u676f\u9152\u54e9\u3002\u201d \u3000\u3000\u8001\u4eba\u76ee\u4e2d\u7684\u8b66\u6212\u4e4b\u8272\u5df2\u5c11\u4e86\u4e9b\uff0c\u53cc\u624b\u5374\u8fd8\u662f\u7d27\u63e1\u7740\u5251\u67c4\uff0c\u9053\uff1a\u201c\u5ba2\u5b98\u8d35\u59d3\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u674e\uff0c\u6728\u5b50\u674e\u3002\u201d \u3000\u3000\u8001\u4eba\u8fd9\u624d\u957f\u957f\u5410\u53e3\u6c14\uff0c\u624b\u91cc\u7684\u5251\u4e5f\u2018\u5f53\u2019\u7684\u843d\u5728\u5730\u4e0a\uff0c\u5c55\u989c\u9053\uff1a\u201c\u539f\u6765\u662f\u674e\u2026\u2026\u674e\u63a2\u82b1 \uff0c\u8001\u673d\u5df2\u5728\u8fd9\u91cc\u7b49\u4e86\u534a\u5929\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u7b49\u6211\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u65b9\u624d\u6709\u4f4d\u516c\u5b50\u2026\u2026\u82f1\u96c4\uff0c\u6740\u4e86\u5f88\u591a\u4eba\u2026\u2026\u6076\u4eba\uff0c\u5374\u7559\u4e0b\u4e2a\u6d3b\u5f85\uff0c\u4ea4\u7ed9\u8001\u673d\u770b \u5b88\uff0c\u8bf4\u662f\u6709\u4f4d\u674e\u63a2\u82b1\u5c31\u4f1a\u6765\u7684\uff0c\u8981\u8001\u673d\u5c06\u8fd9\u4eba\u4ea4\u7ed9\u674e\u63a2\u82b1\uff0c\u82e5\u662f\u6b64\u95f4\u51fa\u4e86\u4ec0\u4e48\u5dee\u9519\uff0c\u4ed6\u5c31\u4f1a \u6765\u2026\u2026\u6765\u8981\u8001\u673d\u7684\u547d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u5728\u53a8\u623f\u91cc\u3002\u201d \u3000\u3000\u53a8\u623f\u5e76\u4e0d\u5c0f\uff0c\u800c\u4e14\u5c45\u7136\u5f88\u5e72\u51c0\uff0c\u679c\u7136\u6709\u4e2a\u4eba\u88ab\u53cd\u7ed1\u5728\u6905\u5b50\u4e0a\uff0c\u957f\u5f97\u5f88\u7626\u5c0f\uff0c\u8033\u8fb9\u8fd8\u6709\u64ae \u9ed1\u6bdb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u65e9\u5df2\u60f3\u5230\u963f\u98de\u5c31\u662f\u8981\u5c06\u8fd9\u4eba\u7559\u7ed9\u4ed6\u62f7\u95ee\u7684\uff0c\u4f46\u8fd9\u4eba\u5374\u663e\u7136\u672a\u60f3\u5230\u8fd8\u4f1a\u89c1\u5230\u674e\u5bfb\u6b22 \uff0c\u76ee\u4e2d\u7684\u60ca\u60e7\u4e4b\u8272\u66f4\u6d53\uff0c\u5634\u89d2\u7684\u808c\u8089\u4e5f\u5728\u4e0d\u505c\u7684\u62bd\u6410\u7740\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\u2014\u2014\u963f\u98de\u4e0d\u4f46\u7d27\u7d27\u7684 \u7ed1\u4f4f\u4e86\u4ed6\uff0c\u8fd8\u7528\u5e03\u585e\u4f4f\u4e86\u4ed6\u7684\u5634\u3002 \u3000\u3000\u4ed6\u663e\u7136\u662f\u6015\u8fd9\u4eba\u7528\u5a01\u80c1\u5229\u8bf1\u7684\u8bdd\u6765\u6253\u52a8\u8fd9\u8001\u4eba\uff0c\u6240\u4ee5\u8fde\u5634\u4e5f\u585e\u4f4f\uff0c\u674e\u5bfb\u6b22\u8fd9\u624d\u53d1\u89c9\u4ed6\u5c45 \u7136\u8fd8\u5f88\u7ec6\u5fc3\u3002 \u3000\u3000\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u70b9\u4f4f\u8fd9\u4eba\u7684\u7a74\u9053\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u624b\u91cc\u7684\u5200\u5149\u4e00\u95ea\uff0c\u53ea\u4e0d\u8fc7\u662f\u6311\u53bb\u4e86\u8fd9\u4eba\u5634\u91cc\u585e\u4f4f\u7684\u5e03\u800c\u5df2\uff0c\u8fd9\u4eba\u5374\u5df2\u51e0\u4e4e\u88ab\u5413\u6655 \u4e86\u3002 \u3000\u3000\u4ed6\u60f3\u6c42\u9976\uff0c\u4f46\u5634\u91cc\u4e7e\u5f97\u53d1\u9ebb\uff0c\u4e00\u4e2a\u5b57\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6ca1\u6709\u50ac\u4ed6\uff0c\u5374\u5728\u4ed6\u5bf9\u9762\u5750\u4e0b\uff0c\u53c8\u8bf7\u90a3\u8001\u4eba\u5c06\u5916\u9762\u7684\u9152\u7b49\u5168\u90fd\u642c\u4e86\u8fdb\u6765\uff0c\u4ed6\u5012\u4e86 \u676f\u9152\u559d\u4e0b\u53bb\uff0c\u624d\u5fae\u7b11\u7740\u9053\uff1a\u201c\u8d35\u59d3\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8138\u5df2\u53d1\u9ec4\uff0c\u7528\u53d1\u4e7e\u7684\u820c\u5934\u8210\u7740\u5634\u5507\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u6d2a\u6c49\u6c11\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u559d\u9152\u7684\uff0c\u559d\u4e00\u676f\u5427\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u53c8\u6311\u65ad\u4e86\u8fd9\u4eba\u8eab\u4e0a\u7ed1\u7740\u7684\u7ef3\u5b50\uff0c\u5012\u4e86\u676f\u9152\u9012\u8fc7\u53bb\uff0c\u8fd9\u4eba\u5403\u60ca\u7684\u5f20\u5927\u4e86\u773c\u775b\uff0c\u7528\u529b \u634f\u7740\u81ea\u5df1\u88ab\u56f0\u5f97\u53d1\u9ebb\u7684\u624b\u81c2\uff0c\u65e2\u4e0d\u6562\u4f38\u624b\u6765\u63a5\u8fd9\u676f\u9152\uff0c\u53c8\u4e0d\u6562\u4e0d\u63a5\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u7740\u9053\uff1a\u201c\u6709\u4eba\u82e5\u8bf7\u6211\u559d\u9152\uff0c\u6211\u4ece\u6765\u4e0d\u4f1a\u62d2\u7edd\u7684\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u53ea\u6709\u63a5\u8fc7\u9152\u676f\uff0c\u4ed6\u7684\u624b\u76f4\u6296\uff0c\u867d\u7136\u603b\u7b97\u559d\u4e0b\u53bb\u534a\u676f\u9152\uff0c\u8fd8\u6709\u534a\u676f\u5374\u90fd\u6d12\u5230\u8eab\u4e0a\u4e86 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u53ef\u60dc\u53ef\u60dc\u2026\u2026\u4f60\u82e5\u4e5f\u50cf\u6211\u4e00\u6837\uff0c\u627e\u628a\u5200\u6765\u523b\u523b\u6728\u5934\uff0c\u4ee5\u540e \u624b\u5c31\u4e0d\u4f1a\u53d1\u6296\uff0c\u96d5\u523b\u53ef\u4ee5\u4f7f\u624b\u7a33\u5b9a\uff0c\u8fd9\u662f\u6211\u7684\u79d8\u8bc0\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5012\u4e86\u4e24\u676f\u9152\uff0c\u7b11\u9053\uff1a\u201c\u4f73\u4eba\u4e0d\u53ef\u5510\u7a81\uff0c\u597d\u9152\u4e0d\u53ef\u7cdf\u584c\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u4f60\u4ee5\u540e\u4e00\u5b9a\u8981\u7262\u8bb0 \u5728\u5fc3\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u7528\u4e24\u53ea\u624b\u7aef\u7740\u9152\u676f\uff0c\u8fd8\u751f\u6015\u9152\u6cfc\u4e86\u51fa\u6765\uff0c\u8d76\u7d27\u7528\u5634\u51d1\u4e0a\u53bb\uff0c\u5c06\u4e00\u676f\u9152\u5168\u559d\u4e86\u4e2a\u4e7e \u51c0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u4e00\u751f\u522b\u7684\u90fd\u6ca1\u6709\u5b66\u4f1a\uff0c\u53ea\u5b66\u4f1a\u4e86\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u73b0\u5728\u5df2\u5168\u90fd\u544a\u8bc9\u4e86\u4f60 \uff0c\u4f60\u5e94\u8be5\u600e\u4e48\u6837\u6765\u611f\u8c22\u6211\uff1f\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9053\uff1a\u201c\u5728\u4e0b\u2026\u2026\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u7740\u505a\u522b\u7684\u4e8b\uff0c\u53ea\u8981\u5c06\u90a3\u5305\u88b1\u62ff\u51fa\u6765\uff0c\u6211\u5c31\u5f88\u6ee1\u610f\u4e86\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u7684\u624b\u53c8\u4e00\u6296\uff0c\u5e78\u597d\u676f\u5b50\u91cc\u5df2\u6ca1\u6709\u9152\u4e86\u3002 \u3000\u3000\u4ed6\u957f\u957f\u5438\u8fdb\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5305\u88b1\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u8138\u4e0a\u5f88\u5c3d\u529b\u5730\u6324\u51fa\u4e86\u4e00\u4e1d\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6447\u7740\u5934\u53f9\u9053\uff1a\u201c\u6211\u603b\u4ee5\u4e3a\u559c\u6b22\u559d\u9152\u7684\u4eba\u90fd\u6bd4\u8f83\u76f4\u723d\uff0c\u53ef\u662f\u4f60\u2026\u2026\u4f60\u5b9e\u5728\u4ee4\u6211\u5931\u671b \u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u966a\u7b11\u9053\uff1a\u201c\u674e\u2026\u2026\u674e\u5927\u4fa0\u53ea\u6015\u662f\u8bef\u4f1a\u4e86\uff0c\u5728\u4e0b\u7684\u786e\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u6c89\u4e0b\u8138\uff0c\u9053\uff1a\u201c\u4f60\u559d\u4e86\u6211\u7684\u9152\uff0c\u8fd8\u8981\u9a97\u6211\uff0c\u628a\u9152\u8fd8\u7ed9\u6211\u5427\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9053\uff1a\u201c\u662f\uff0c\u662f\u2026\u2026\u5728\u4e0b\u8fd9\u5c31\u53bb\u4e70\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u65b9\u624d\u559d\u4e0b\u53bb\u7684\u4e24\u676f\uff0c\u4e70\u522b\u7684\u9152\u6211\u4e0d\u8981\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u6014\u4e86\u6014\uff0c\u7528\u8896\u5b50\u76f4\u64e6\u6c57\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u9152\u5df2\u559d\u5728\u809a\u5b50\u91cc\uff0c\u600e\u4e48\u8fd8\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u5012\u5bb9\u6613\u3002\u201d \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u5c0f\u5200\u5df2\u62b5\u4f4f\u4e86\u6d2a\u6c49\u6c11\u7684\u80f8\u819b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u201c\u9152\u65e2\u7136\u5728\u4f60\u809a\u5b50\u91cc\uff0c\u6211\u53ea\u8981\u5c06\u4f60\u7684\u809a\u5b50\u5256\u5f00\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u8138\u8272\u53d1\u767d\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u674e\u5927\u4fa0\u4f55\u5fc5\u5f00\u5c0f\u4eba\u7684\u73a9\u7b11\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u770b\u6211\u8fd9\u50cf\u662f\u5f00\u73a9\u7b11\uff1f\u201d \u3000\u3000\u4ed6\u7684\u624b\u5fae\u5fae\u7528\u4e86\u4e9b\u529b\uff0c\u5c06\u5c0f\u5200\u8f7b\u8f7b\u5728\u6d2a\u6c49\u6c11\u7684\u80f8\u819b\u4e0a\u4e00\u523a\uff0c\u60f3\u5c06\u4ed6\u7684\u80f8\u819b\u523a\u7834\u4e00\u70b9\uff0c\u8ba9 \u4ed6\u6d41\u4e00\u70b9\u8840\u3002 \u3000\u3000\u56e0\u4e3a\u53ea\u6709\u61e6\u592b\u624d\u4f1a\u8bf4\u8c0e\uff0c\u800c\u61e6\u592b\u4e00\u770b\u5230\u81ea\u5df1\u7684\u8840\uff0c\u5c31\u4f1a\u88ab\u9a87\u51fa\u5b9e\u8bdd\u4e86\uff0c\u8fd9\u9053\u7406\u8c01\u4e5f\u4e0d\u4f1a \u6bd4\u674e\u5bfb\u6b22\u66f4\u6e05\u695a\u3002 \u3000\u3000\u8c01\u77e5\u9053\u5200\u5c16\u523a\u4e0b\uff0c\u7adf\u597d\u50cf\u523a\u5728\u4e00\u4e2a\u77f3\u9762\u4e0a\uff0c\u6d2a\u6c49\u6c11\u8fd8\u662f\u6ee1\u9762\u5047\u7b11\uff0c\u4f3c\u4e4e\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1 \u6709\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u4e86\u95ea\uff0c\u624b\u5df2\u505c\u4e86\u4e0b\u6765\uff0c\u8fd9\u61e6\u592b\u5c45\u7136\u5200\u67aa\u4e0d\u5165\uff0c\u674e\u5bfb\u6b22\u5c45\u7136\u4e5f\u5e76\u6ca1\u6709\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u53cd\u800c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u5df2\u6709\u4e0d\u5c11\u65f6\u5019\u4e86\u5427\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u60f3\u4e0d\u5230\u4ed6\u5ffd\u7136\u4f1a\u95ee\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u6014\u4e86\u6014\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5df2\u6709\u4e8c\u5341\u5e74\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u603b\u8be5\u77e5\u9053\u6c5f\u6e56\u4e2d\u6709\u51e0\u4ef6\u5f88\u795e\u5947\u7684\u5b9d\u7269\uff0c\u8fd9\u4e9b\u5b9d\u7269\u867d\u5f88\u5c11\u6709\u4eba\u80fd\u771f\u7684 \u89c1\u5230\uff0c\u4f46\u5374\u5df2\u4f20\u8bf4\u591a\u5e74\uff0c\u5176\u4e2d\u6709\u4e00\u4ef6\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u773c\u775b\u76ef\u7740\u6d2a\u6c49\u6c11\uff0c\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u5c31\u662f\u91d1\u4e1d\u7532\uff0c\u636e\u8bf4\u6b64\u7269\u5200\u67aa\u4e0d\u5165\uff0c\u6c34\u706b\u4e0d\u4f24\uff0c\u4f60 \u65e2\u5df2\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u4e8c\u5341\u5e74\uff0c\u603b\u8be5\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u7684\u8138\u5df2\u7ecf\u53d8\u5f97\u597d\u50cf\u4e00\u5757\u62b9\u684c\u5e03\uff0c\u8df3\u8d77\u6765\u5c31\u60f3\u9003\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6cd5\u5e76\u4e0d\u6162\uff0c\u8e2a\u8eab\u4e00\u63a0\u5230\u4e86\u95e8\u53e3\uff0c\u4f46\u4ed6\u6b63\u8981\u7a9c\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u674e\u5bfb\u6b22\u4e5f\u5df2\u7ad9\u5728\u95e8\u53e3\u4e86 \u3002 \u3000\u3000\u6d2a\u6c49\u6c11\u54ac\u4e86\u54ac\u7259\uff0c\u4e00\u8f6c\u8eab\u5c31\u89e3\u4e0b\u4e86\u6761\u4eae\u94f6\u94fe\u5b50\u67aa\uff0c\u94f6\u5149\u6d12\u5f00\uff0c\u94fe\u5b50\u67aa\u6bd2\u86c7\u822c\u5411\u674e\u5bfb\u6b22\u523a \u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u770b\u6765\u4ed6\u5728\u8fd9\u67c4\u67aa\u4e0a\u81f3\u5c11\u5df2\u6709\u4e8c\u4e09\u5341\u5e74\u7684\u529f\u592b\uff0c\u8fd9\u4e00\u62db\u523a\u51fa\uff0c\u8f6f\u8f6f\u7684\u94fe\u5b50\u67aa\u7adf\u88ab\u6296\u5f97\u7b14\u76f4 \uff0c\u5e26\u7740\u52b2\u98ce\u76f4\u523a\u674e\u5bfb\u6b22\u7684\u54bd\u5589\u3002 \u3000\u3000\u53ea\u542c\u2018\u5f53\u2019\u7684\u4e00\u58f0\uff0c\u674e\u5bfb\u6b22\u53ea\u62ac\u4e86\u62ac\u624b\uff0c\u4ed6\u624b\u91cc\u8fd8\u62ff\u7740\u9152\u676f\uff0c\u5c31\u7528\u8fd9\u9152\u676f\u5957\u4f4f\u4e86\u67aa\u5c16\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u67aa\u5c16\u7adf\u6ca1\u6709\u5c06\u9152\u676f\u51fb\u788e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4ee5\u540e\u82e5\u518d\u6709\u4eba\u529d\u6211\u6212\u9152\uff0c\u6211\u4e00\u5b9a\u8981\u544a\u8bc9\u4ed6\u559d\u9152\u4e5f\u6709\u597d\u5904\u7684\uff0c\u800c\u4e14\u9152\u676f\u8fd8 \u6551\u8fc7\u6211\u4e00\u6b21\u547d\u3002 \u3000\u3000\u6d2a\u6c49\u6c11\u5c31\u50cf\u77f3\u5934\u4eba\u822c\u6014\u5728\u90a3\u91cc\uff0c\u6ee1\u5934\u6c57\u843d\u5982\u96e8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u60f3\u6253\u67b6\u4e86\uff0c\u5c31\u5c06\u8eab\u4e0a\u7684\u91d1\u4e1d\u7532\u8131\u4e0b\u6765\u4f5c\u9152\u8d44\u5427\uff0c\u90a3\u52c9\u5f3a\u4e5f\u53ef\u62b5\u5f97\u8fc7 \u6211\u7684\u4e24\u676f\u9152\u4e86\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u771f\u8981\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u5012\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u8981\u8fd9\u4e1c\u897f\uff0c\u4f60\u80fd\u8d81\u6211\u4e0d\u5907\uff0c\u5c06\u5305\u88b1\u5077\u8d70\uff0c\u4e5f\u7b97\u4f60\u7684\u672c\u4e8b \uff0c\u4f46\u4f60\u5374\u4e0d\u8be5\u5bf9\u522b\u4eba\u8bf4\u5305\u88b1\u662f\u6211\u62ff\u7684\uff0c\u6211\u8fd9\u4eba\u6700\u4e0d\u559c\u88ab\u4eba\u6789\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5305\u88b1\u662f\u2026\u2026\u662f\u5c0f\u4eba\u62ff\u7684\uff0c\u5305\u88b1\u91cc\u4e5f\u7684\u786e\u5c31\u662f\u91d1\u4e1d\u7532\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f \u2026\u2026\u201d \u3000\u3000\u4ed6\u975e\u4f46\u5df2\u6025\u5f97\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u8fde\u773c\u6cea\u90fd\u5feb\u88ab\u6025\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u91d1\u4e1d\u7532\u867d\u7136\u662f\u9632\u8eab\u81f3\u5b9d\uff0c\u4f46\u4f60\u5f97\u4e86\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u4f60\u5c31\u7b97\u7a7f\u7740\u5341\u4ef6\u91d1\u4e1d\u7532\uff0c \u6211\u4e00\u5200\u8fd8\u662f\u53ef\u4ee5\u8981\u4f60\u7684\u547d\uff0c\u4f60\u4f55\u5fc5\u4e3a\u4e86\u5b83\u62fc\u547d\uff1f\u201d \u3000\u3000\u4ed6\u53f9\u606f\u63a5\u7740\u9053\uff1a\u201c\u4e16\u95f4\u7684\u5b9d\u7269\uff0c\u552f\u6709\u5fb7\u8005\u5c45\u4e4b\uff0c\u8fd9\u79cd\u4e1c\u897f\u66f4\u4e0d\u662f\u4f60\u4eec\u8fd9\u79cd\u4eba\u5e94\u8be5\u6709\u7684\uff0c \u4f60\u5c06\u5b83\u9001\u7ed9\u6211\uff0c\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u591a\u6d3b\u51e0\u5e74\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u55c4\u58f0\u9053\uff1a\u201c\u5c0f\u4eba\u4e5f\u77e5\u9053\u4e0d\u914d\u6709\u8fd9\u79cd\u4e1c\u897f\uff0c\u4f46\u5c0f\u4eba\u4e5f\u5e76\u4e0d\u60f3\u5c06\u4e4b\u636e\u4e3a\u5df1\u6709\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u96be\u9053\u4f60\u672c\u6765\u5c31\u60f3\u5c06\u5b83\u9001\u7ed9\u522b\u4eba\u4e48\uff1f\u9001\u7ed9\u8c01\uff1f\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u54ac\u7740\u7259\uff0c\u8fde\u5634\u5507\u90fd\u88ab\u54ac\u51fa\u8840\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u9053\uff1a\u201c\u6211\u6709\u5f88\u591a\u6cd5\u5b50\u80fd\u8981\u4eba\u8bf4\u5b9e\u8bdd\uff0c\u53ef\u662f\u6211\u5e76\u4e0d\u559c\u6b22\u7528\uff0c\u6240\u4ee5\u6211\u5e0c\u671b\u4f60\u83ab\u8981 \u4e5f\u903c\u6211\u7528\u51fa\u6765\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u7ec8\u4e8e\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u8bf4\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u6700\u597d\u4ece\u5934\u8bf4\u8d77\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u6c89\u541f\u7740\u9053\uff1a\u201c\u674e\u5927\u4fa0\u53ef\u77e5\u9053\u6709\u4e2a\u2018\u795e\u5077\u2019\u6234\u4e94\u4e48\uff1f\u8fd9\u79cd\u4e0b\u4e94\u95e8\u7684\u5c0f\u8d3c\uff0c\u674e\u5927\u4fa0\u4e5f \u8bb8\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u6211\u975e\u4f46\u77e5\u9053\u8fd9\u4eba\uff0c\u800c\u4e14\u8fd8\u8ba4\u5f97\u4ed6\uff0c\u4ed6\u7684\u8f7b\u529f\u548c\u624b\u4e0a\u529f\u592b\u90fd\u7b97\u4e0d\u5f31\uff0c\u800c\u4e14 \u9152\u91cf\u4e5f\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9053\uff1a\u201c\u8fd9\u2018\u91d1\u4e1d\u7532\u2019\uff0c\u5c31\u662f\u4ed6\u4e0d\u77e5\u4ece\u90a3\u91cc\u5077\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u90a3\u4e48\uff0c\u53c8\u600e\u4f1a\u5230\u4e86\u4f60\u4eec\u624b\u4e0a\u5462\uff1f\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9053\uff1a\u201c\u4ed6\u548c\u8bf8\u845b\u96f7\u672c\u6765\u4e5f\u662f\u8001\u670b\u53cb\uff0c\u6211\u4eec\u5728\u5f20\u5bb6\u53e3\u9047\u89c1\u4e86\u4ed6\uff0c\u5c31\u5728\u4e00\u8d77\u559d\u9152\uff0c\u4ed6 \u5927\u9189\u4e4b\u4e0b\uff0c\u62ff\u91d1\u4e1d\u7532\u51fa\u6765\u5439\u5618\uff0c\u8bf8\u845b\u96f7\u77a7\u7740\u773c\u7ea2\uff0c\u5c31\u2026\u2026\u5c31\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u677f\u7740\u8138\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u505a\u5f97\u51fa\u8fd9\u79cd\u4e0d\u8981\u8138\u7684\u4e8b\uff0c\u96be\u9053\u8fd8\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u5782\u4e0b\u5934\u53f9\u9053\uff1a\u201c\u6234\u4e94\u660e\u77e5\u8fd9\u91d1\u4e1d\u7532\u73b0\u5728\u662f\u6c5f\u6e56\u4e2d\u6bcf\u4e2a\u4eba\u90fd\u60f3\u5f97\u5230\u7684\u5b9d\u7269\uff0c\u4ed6\u65e2\u7136 \u8eab\u6000\u6b64\u7269\uff0c\u672c\u4e0d\u8be5\u559d\u9189\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u5e76\u4e0d\u662f\u4e0d\u8be5\u559d\u9152\uff0c\u800c\u662f\u4e0d\u8be5\u4ea4\u9519\u4e86\u670b\u53cb\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u60e8\u767d\u7684\u8138\uff0c\u5c45\u7136\u4e5f\u6709\u4e9b\u53d1\u7ea2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u91d1\u4e1d\u7532\u867d\u7136\u53f7\u79f0\u662f\u2018\u6b66\u6797\u4e09\u5b9d\u2019\u4e4b\u4e00\uff0c\u5176\u5b9e\u5e76\u6ca1\u6709\u592a\u5927\u7528\u5904\uff0c\u56e0\u4e3a\u9664\u4e86 \u4e24\u4e2a\u52bf\u5747\u529b\u654c\u7684\u9ad8\u624b\u76f8\u4e89\u65f6\u7528\u5f97\u7740\u5b83\u4e4b\u5916\uff0c\u4e00\u822c\u4eba\u5f97\u5230\u5b83\u8fd8\u662f\u96be\u514d\u9001\u547d\uff0c\u6211\u5012\u4e0d\u61c2\u5b83\u4e3a\u4ec0\u4e48 \u4f1a\u5ffd\u7136\u53d8\u5f97\u5982\u6b64\u62a2\u773c\u4e86\uff0c\u8fd9\u5176\u4e2d\u662f\u5426\u53e6\u6709\u539f\u56e0\uff1f\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5176\u4e2d\u7684\u786e\u6709\u4e2a\u79d8\u5bc6\u2026\u2026\u5176\u5b9e\u8fd9\u79d8\u5bc6\u73b0\u5728\u5df2\u4e0d\u80fd\u7b97\u662f\u79d8\u5bc6\u4e86\uff0c\u53ea\u56e0 \u2026\u2026\u201d \u3000\u3000\u4ed6\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u8fd9\u9152\u5e97\u7684\u4e3b\u4eba\u5df2\u7aef\u7740\u4e24\u58f6\u9152\u8fdb\u6765\uff0c\u966a\u7b11\u9053\uff1a\u201c\u521a\u6e29\u597d\u7684\u9152\uff0c\u63a2\u82b1\u5927\u4eba\u5148 \u559d\u4e00\u676f\u518d\u8bf4\u8bdd\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u6211\u4e0b\u6b21\u518d\u6765\u7167\u987e\u4f60\u7684\u751f\u610f\uff0c\u6700\u597d\u518d\u4e5f\u83ab\u8981\u53eb\u6211\u8fd9\u540d\u5b57\uff0c\u6211\u4e00\u542c \u8fd9\u56db\u4e2a\u5b57\uff0c\u8fde\u9152\u90fd\u559d\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u9152\u676f\u8fd8\u5728\u4ed6\u624b\u4e0a\uff0c\u4ed6\u6ee1\u6ee1\u5012\u4e86\u4e00\u676f\uff0c\u53ea\u89c9\u4e00\u9635\u9152\u9999\u6251\u9f3b\u800c\u6765\uff0c\u4ed6\u8138\u8272\u7acb\u523b\u53c8\u5f00\u6717\u4e86\uff0c\u5c55 \u989c\u9053\uff1a\u201c\u597d\u9152\u3002\u201d \u3000\u3000\u4ed6\u5c06\u8fd9\u676f\u9152\u559d\u4e86\u4e0b\u53bb\uff0c\u53c8\u5f2f\u4e0b\u8170\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u8001\u4eba\u53f9\u606f\u7740\uff0c\u7aef\u4e86\u5f20\u6905\u5b50\u8fc7\u6765\u6276\u7740\u674e\u5bfb\u6b22\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u54b3\u55fd\u6700\u4f24\u8eab\u5b50\uff0c\u8981\u5c0f\u5fc3\u4e9b\uff0c\u8981\u5c0f \u5fc3\u4e9b\u2026\u2026\u201d \u3000\u3000\u4ed6\u82cd\u8001\u7684\u9762\u4e0a\u5ffd\u7136\u9732\u51fa\u4e86\u4e00\u4e1d\u5fae\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4f46\u8fd9\u9152\u4e13\u6cbb\u54b3\u55fd\uff0c\u5ba2\u5b98\u4f60\u559d\u4e86\uff0c\u4ee5\u540e\u5305 \u7ba1\u4e0d\u4f1a\u518d\u54b3\u55fd\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u9152\u82e5\u80fd\u6cbb\u54b3\u55fd\uff0c\u5c31\u771f\u7684\u5341\u5168\u5341\u7f8e\u4e86\uff0c\u4f60\u4e5f\u559d\u4e00\u676f\u5427\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u559d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u5356\u997a\u5b50\u7684\u4eba\u5b81\u53ef\u5403\u9992\u5934\u4e5f\u4e0d\u613f\u5403\u997a\u5b50\uff0c\u5356\u9152\u7684\u4eba\u96be\u9053\u4e5f\u5b81\u53ef\u559d\u6c34 \uff0c\u5374\u4e0d\u559d\u9152\u4e48\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u6211\u5e73\u5e38\u4e5f\u559d\u4e24\u676f\u7684\uff0c\u53ef\u662f\u2026\u2026\u8fd9\u58f6\u9152\u5374\u4e0d\u80fd\u559d\u3002\u201d \u3000\u3000\u4ed6\u5446\u6ede\u7684\u76ee\u5149\u7adf\u4e5f\u53d8\u5f97\u9510\u5229\u72e1\u9ee0\u8d77\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u4f3c\u672a\u66fe\u7559\u610f\uff0c\u8fd8\u662f\u5fae\u7b11\u7740\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8001\u4eba\u76ef\u7740\u4ed6\u624b\u91cc\u7684\u5c0f\u5200\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u56e0\u4e3a\u559d\u4e0b\u6211\u8fd9\u676f\u9152\u540e\uff0c\u53ea\u8981\u7a0d\u4e3a\u4e00\u7528\u771f\u529b\uff0c\u9152\u91cc\u7684 \u6bd2\u7acb\u523b\u5c31\u8981\u53d1\u4f5c\uff0c\u4e03\u5b54\u6d41\u8840\u800c\u6b7b\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5f20\u5634\u7ed3\u820c\uff0c\u4f3c\u5df2\u5446\u4e86\u3002 \u3000\u3000\u6d2a\u6c49\u6c11\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4f1a\u6765\u5e2e\u6211\u7684\u5fd9\uff0c\u65e5\u540e\u6211\u5fc5\u5b9a\u91cd\u91cd\u916c\u8c22\u3002\u201d \u3000\u3000\u8001\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8c22\u6211\u3002\u201d \u3000\u3000\u6d2a\u6c49\u6c11\u9762\u8272\u5fae\u53d8\uff0c\u966a\u7b11\u9053\uff1a\u201c\u524d\u8f88\u771f\u4eba\u4e0d\u9732\u50cf\uff0c\u83ab\u975e\u4e5f\u60f3\u8981\u2026\u2026\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u638c\u4e2d\u7684\u94fe\u5b50\u67aa\u53c8\u5df2\u98de\u821e\u800c\u51fa\u3002 \u3000\u3000\u8001\u4eba\u6012\u53f1\u4e00\u58f0\uff0c\u4f5d\u507b\u7684\u8eab\u5b50\uff0c\u7adf\u4f3c\u5ffd\u7136\u66b4\u957f\u4e86\u4e00\u5c3a\uff0c\u5de6\u624b\u4e00\u53cd\uff0c\u5df2\u6284\u7740\u4e86\u67aa\u5934\uff0c\u5389\u58f0\u9053 \uff1a\u201c\u5c31\u51ed\u4f60\u4e5f\u6562\u8ddf\u6211\u8001\u4eba\u5bb6\u52a8\u624b\uff1f\uff01\u201d \u3000\u3000\u8fd9\u80c6\u5c0f\u6015\u4e8b\u7684\u7cdf\u8001\u5934\u5b50\uff0c\u5728\u77ac\u95f4\u5c31\u5f77\u4f5b\u53d8\u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u8fde\u4e00\u5f20\u8138\u90fd\u53d8\u5f97\u7ea2\u4e2d\u900f\u7d2b\uff0c\u9690\u9690 \u6709\u5149\u3002 \u3000\u3000\u6d2a\u6c49\u6c11\u770b\u5230\u4ed6\u8fd9\u79cd\u5947\u5f02\u7684\u9762\u8272\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u5931\u58f0\u60ca\u547c\u9053\uff1a\u201c\u524d\u8f88\u9976\u547d\uff0c\u5c0f\u4eba\u4e0d \u77e5\u9053\u524d\u8f88\u5c31\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf7\u6c42\u9976\u5df2\u8fdf\u4e86\uff0c\u547c\u58f0\u4e2d\uff0c\u8001\u4eba\u7684\u53f3\u62f3\u5df2\u51fb\u51fa\uff0c\u53ea\u542c\u2018\u7830\u2019\u7684\u4e00\u58f0\uff0c\u6d2a\u6c49\u6c11\u7684\u8eab\u5b50\u7adf\u88ab \u6253\u5f97\u98de\u4e86\u51fa\u6765\uff0c\u7f20\u5728\u624b\u4e0a\u7684\u94fe\u5b50\u4e5f\u65ad\u6210\u4e24\u622a\uff0c\u9c9c\u8840\u4e00\u8def\u6e85\u4e86\u51fa\u6765\uff0c\u4ed6\u8eab\u4e0a\u649e\u5728\u5899\u4e0a\uff0c\u6070\u597d\u843d \u5728\u7076\u4e0a\u7684\u5927\u94c1\u9505\u91cc\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u7684\u529b\u9053\u5b9e\u5728\u60ca\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4f60\u6709\u4e86\u8fd9\u4ef6\u91d1\u4e1d\u7532\uff0c\u53cd\u800c\u4f1a\u6b7b\u5f97\u5feb\u4e9b\u3002\u201d \u3000\u3000\u8001\u4eba\u5c06\u534a\u622a\u94fe\u5b50\u67aa\u7529\u5728\u5730\u4e0a\uff0c\u51fa\u795e\u7684\u671b\u7740\u6d2a\u6c49\u6c11\u7684\u5c38\u8eab\uff0c\u8138\u4e0a\u7684\u76b1\u7eb9\u53c8\u4e00\u6839\u6839\u73b0\u4e86\u51fa\u6765 \uff0c\u5583\u5583\u9053\uff1a\u201c\u4f60\u5df2\u6709\u4e8c\u5341\u5e74\u6ca1\u6709\u6740\u4eba\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u8f7b\u8eab\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f46\u6211\u5e76\u6ca1\u6709\u5fd8\u8bb0\u5982\u4f55\u6740\u4eba\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e3a\u4e86\u8fd9\u79cd\u4e8b\u6740\u4eba\u503c\u5f97\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u4e0d\u4e3a\u4ec0\u4e48\u4e5f\u4f1a\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u73b0\u5728\u5df2\u8fc7\u4e86\u4e8c\u5341\u5e74\uff0c\u4f60\u80fd\u8eb2\u8fc7\u8fd9\u4e8c\u5341\u5e74\uff0c\u5e76\u4e0d\u5bb9\u6613\u3002\u82e5\u4e3a\u4e86\u8fd9\u79cd\u4e8b\u5c06\u81ea \u5df1\u8eab\u4efd\u66b4\u9732\uff0c\u5c82\u975e\u5212\u4e0d\u6765\u3002\u201d \u3000\u3000\u8001\u4eba\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u5df2\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u83ab\u5fd8\u8bb0\uff0c\u2018\u7d2b\u9762\u4e8c\u90ce\u2019\u5b59\u9035\u5728\u4e8c\u5341\u5e74\u524d\u662f\u591a\u4e48\u51fa\u98ce\u5934\u7684\u4eba\u7269\uff0c \u5c45\u7136\u6562\u548c\u6c5f\u5357\u4e03\u5341\u4e8c\u9053\u6c34\u9646\u7801\u5934\u603b\u74e2\u628a\u5b50\u7684\u59bb\u5b50\u79c1\u5954\uff0c\u8fd9\u79cd\u52c7\u6c14\u6211\u5b9e\u5728\u4f69\u670d\u3002\u201d \u3000\u3000\u8001\u4eba\u6012\u9053\uff1a\u201c\u6b64\u65f6\u6b64\u523b\uff0c\u4f60\u8fd8\u6562\u51fa\u8a00\u4e0d\u900a\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u83ab\u4ee5\u4e3a\u6211\u8fd9\u662f\u5728\u8bbd\u523a\u4f60\uff0c\u4e00\u4e2a\u7537\u4eba\u80af\u4e3a\u4e86\u81ea\u5df1\u5fc3\u7231\u7684\u5973\u5b50\u5192\u751f\u547d\u4e4b\u9669\uff0c \u8d1f\u5929\u4e0b\u4e4b\u8c24\uff0c\u751a\u81f3\u4e0d\u60dc\u727a\u7272\u4e00\u5207\uff0c\u8fd9\u79cd\u7537\u4eba\u81f3\u5c11\u5df2\u4e0d\u6127\u662f\u4e2a\u7537\u4eba\uff0c\u6211\u672c\u6765\u7684\u786e\u5bf9\u4f60\u5f88\u4f69\u670d\u7684 \uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4ed6\u6447\u4e86\u6447\u5934\uff0c\u957f\u53f9\u9053\uff1a\u201c\u73b0\u5728\u6211\u5374\u5931\u671b\u5f97\u5f88\uff0c\u56e0\u4e3a\u6211\u60f3\u4e0d\u5230\u7d2b\u9762\u4e8c\u90ce\u5c45\u7136\u4e5f\u662f\u4e2a\u9b3c\u9b3c\u5d07 \u5d07\u7684\u5c0f\u4eba\uff0c\u53ea\u6562\u5728\u6697\u4e2d\u4e0b\u6bd2\uff0c\u5374\u4e0d\u6562\u4ee5\u771f\u529f\u592b\u548c\u4eba\u4e00\u51b3\u80dc\u8d1f\u3002\u201d \u3000\u3000\u5b59\u9035\u6012\u76ee\u671b\u7740\u4ed6\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u7a81\u542c\u4e00\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u4f60\u5012\u83ab\u8981\u51a4\u6789\u4e86\u4ed6\uff0c\u4e0b\u6bd2\u4e5f\u8981\u6709\u5b66\u95ee \u7684\uff0c\u5c31\u51ed\u4ed6\uff0c\u8fd8\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u672c\u4e8b\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u800c\u4e14\u5f88\u52a8\u542c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u65e9\u8be5\u60f3\u5230\u8fd9\u662f\u8537\u8587\u592b\u4eba\u7684\u624b\u6bb5\u4e86\uff0c\u674e\u5bfb\u6b22\u80fd\u6b7b\u5728\u4e8c\u5341\u5e74\u524d\u540d \u6ee1\u6c5f\u6e56\u7684\u7f8e\u4eba\u624b\u4e0a\u5012\u4e5f\u4e0d\u865a\u6b64\u751f\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u5403\u5403\u7b11\u9053\uff1a\u201c\u597d\u4f1a\u8bf4\u8bdd\u7684\u4e00\u5f20\u5634\uff0c\u6211\u82e5\u5728\u4e8c\u5341\u5e74\u524d\u9047\u5230\u4e86\u4f60\uff0c\u53ea\u6015\u5c31\u4e0d\u4f1a\u8ddf\u4ed6\u79c1 \u5954\u4e86\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u5979\u7684\u4eba\u5df2\u626d\u52a8\u7740\u8170\u80a2\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e8c\u5341\u5e74\u4e4b\u540e\uff0c\u5979\u8fd8\u5e76\u4e0d\u663e\u5f97\u592a\u8001\uff0c\u773c\u775b\u8fd8\u662f\u5f88\u6709\u98ce\u60c5\uff0c\u7259\u9f7f\u4e5f\u8fd8\u5f88\u767d\uff0c\u53ef\u662f\u5979\u7684\u8170 \u2014\u2014 \u3000\u3000\u5979\u5b9e\u5728\u5df2\u6ca1\u6709\u8170\u4e86\uff0c\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u4e00\u4e2a\u5e76\u4e0d\u592a\u5927\u7684\u6c34\u7f38\uff0c\u88c5\u7684\u6c34\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u80fd\u704c\u4e24\u4ea9 \u7530\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8868\u60c5\u770b\u6765\u5c31\u50cf\u662f\u521a\u541e\u4e0b\u4e00\u6574\u4e2a\u9e21\u86cb\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u8537\u8587\u592b\u4eba\uff1f\u4ed6\u7b80\u76f4\u65e0\u6cd5\u76f8\u4fe1\u3002 \u3000\u3000\u7f8e\u4eba\u5e74\u534e\u8001\u53bb\uff0c\u672c\u662f\u4ef6\u5f88\u4ee4\u4eba\u60cb\u60dc\uff0c\u4ee4\u4eba\u4f24\u611f\u7684\u4e8b\uff0c\u4f46\u5979\u82e5\u4e0d\u77e5\u9053\u81ea\u5df1\u518d\u4e5f\u4e0d\u662f\u53cc\u5341\u5e74 \u534e\uff0c\u8fd8\u62fc\u547d\u60f3\u7528\u675f\u8170\u624e\u7d27\u8eab\u4e0a\u7684\u80a5\u8089\uff0c\u7528\u8102\u7c89\u63a9\u76d6\u7740\u8138\u4e0a\u7684\u76b1\u7eb9\uff0c\u90a3\u5c31\u975e\u4f46\u4e0d\u518d\u4ee4\u4eba\u4f24\u611f\uff0c \u53cd\u800c\u4ee4\u4eba\u6076\u5fc3\u53ef\u7b11\u3002 \u3000\u3000\u8fd9\u9053\u7406\u672c\u6765\u518d\u4e5f\u660e\u663e\u4e0d\u8fc7\uff0c\u5947\u602a\u7684\u662f\uff0c\u4e16\u4e0a\u5927\u591a\u6570\u5973\u4eba\uff0c\u5bf9\u8fd9\u9053\u7406\u90fd\u4e0d\u77e5\u9053\u2014\u2014\u4e5f\u8bb8\u662f \u6545\u610f\u62d2\u7edd\u77e5\u9053\u3002 \u3000\u3000\u8537\u8587\u592b\u4eba\u7a7f\u7740\u7684\u662f\u4ef6\u7ea2\u7f0e\u7684\u5c0f\u76ae\u8884\uff0c\u68b3\u7740\u4e07\u5b57\u9afb\uff0c\u8fdc\u8fdc\u5c31\u53ef\u4ee5\u55c5\u5230\u4e00\u9635\u9635\u5228\u82b1\u6cb9\u7684\u9999\u6c14 \u3002 \u3000\u3000\u5979\u671b\u7740\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4f4d\u98ce\u6d41\u63a2\u82b1\u90ce\uff0c\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u6211\u5df2\u7ecf\u6709\u4e8c\u5341\u5e74\u6ca1\u6709\u77a7\u89c1 \u8fc7\u8fd9\u4e48\u795e\u6c14\u7684\u7537\u4eba\u4e86\uff0c\u53ef\u662f\u4e8c\u5341\u5e74\u524d\u2026\u2026\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\u6211\u4eec\u5bb6\u91cc\u5374\u603b\u662f\u9ad8\u670b\u6ee1\u5ea7\uff0c\u90a3\u65f6\u4faf\u6c5f\u6e56\u9053\u4e0a\u7684\u5c11\u5e74\u82f1 \u96c4\uff0c\u98ce\u6d41\u5251\u5ba2\uff0c\u6709\u90a3\u4e00\u4e2a\u4e0d\u60f3\u6765\u62dc\u8bbf\u62dc\u8bbf\u6211\uff1f\u53ea\u8981\u80fd\u966a\u6211\u8bf4\u4e24\u53e5\u8bdd\uff0c\u770b\u6211\u4e00\u773c\uff0c\u4ed6\u4eec\u5c31\u597d\u50cf \u5403\u4e86\u4eba\u25a1\u679c\u4f3c\u7684\uff0c\u5f00\u5fc3\u5f97\u8981\u547d\uff0c\u4f60\u4e0d\u4fe1\u95ee\u4ed6\u597d\u4e86\u3002\u201d \u3000\u3000\u5b59\u9035\u6c89\u7740\u8138\uff0c\u62b1\u5b9a\u4e3b\u610f\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u8537\u8587\u592b\u4eba\u8116\u5b50\u4e0a\u5c31\u50cf\u98ce\u4e2d\u8537\u8587\u822c\u5728\u6296\u52a8\u7740\u7684\u80a5\u8089\uff0c\u518d\u770b\u770b\u5b59\u9035\uff0c\u6697\u4e2d\u4e0d\u7981\u53f9 \u606f\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51fa\u8fd9\u8001\u4eba\u8fd9\u4e8c\u5341\u5e74\u7684\u65e5\u5b50\u5e76\u4e0d\u597d\u8fc7\u3002 \u3000\u3000\u8537\u8587\u592b\u4eba\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u5b9e\u5728\u628a\u6211\u8e69\u82e6\u4e86\uff0c\u6bcf\u5929\u8eb2\u5728\u5c4b\u5b50\u91cc\uff0c\u8fde \u4eba\u90fd\u4e0d\u6562\u89c1\uff0c\u6211\u771f\u540e\u6094\u600e\u6837\u4f1a\u8ddf\u7740\u8fd9\u6ca1\u51fa\u606f\u7684\u7537\u4eba\u9003\u8d70\u7684\u3002\u201d \u3000\u3000\u5b59\u9035\u5fcd\u4e0d\u4f4f\u4e5f\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5583\u5583\u9053\uff1a\u201c\u8c01\u4e0d\u540e\u6094\uff0c\u8c01\u662f\u738b\u516b\u86cb\u3002\u201d \u3000\u3000\u8537\u8587\u592b\u4eba\u53eb\u4e86\u8d77\u6765\uff0c\u8df3\u7740\u811a\u9053\uff1a\u201c\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u4f60\u8bf4\uff1f\uff01\u8001\u5a18\u653e\u7740\u597d\u65e5\u5b50\u4e0d\u8fc7\uff0c\u8ddf\u7740\u4f60 \u5230\u8fd9\u4e2a\u9b3c\u5730\u65b9\u6765\u53d7\u82e6\uff0c\u4e00\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u5927\u7f8e\u4eba\uff0c\u88ab\u4f60\u7cdf\u584c\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u597d\u540e\u6094\u7684 \uff0c\u4f60\u8bf4\uff0c\u8bf4\u5440\u3002\u201d \u3000\u3000\u5b59\u9035\u9f3b\u5b50\u91cc\u76f4\u62bd\u6c14\uff0c\u5634\u53c8\u7d27\u7d27\u95ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8537\u8587\u592b\u4eba\u9053\uff1a\u201c\u63a2\u82b1\u90ce\uff0c\u4f60\u8bf4\uff0c\u8fd9\u79cd\u7537\u4eba\u662f\u4e0d\u662f\u6ca1\u6709\u826f\u5fc3\uff0c\u65e9\u77e5\u9053\u4ed6\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff0c\u90a3 \u65f6\u6211\u8fd8\u4e0d\u5982\u2026\u2026\u4e0d\u5982\u6b7b\u4e86\u597d\u4e9b\u3002\u201d \u3000\u3000\u5979\u62fc\u547d\u7528\u624b\u63c9\u7740\u773c\u775b\uff0c\u53ea\u53ef\u60dc\u8fde\u4e00\u6ef4\u773c\u6cea\u4e5f\u6ca1\u6709\u63c9\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u5e78\u597d\u592b\u4eba\u6ca1\u6709\u6b7b\uff0c\u5426\u5219\u5728\u4e0b\u5c31\u771f\u7684\u8981\u9057\u61be\u7ec8\u751f\u4e86\u3002\u201d \u3000\u3000\u8537\u8587\u592b\u4eba\u5a07\u7b11\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u4f60\u771f\u7684\u8fd9\u4e48\u60f3\u89c1\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\uff0c\u50cf\u592b\u4eba\u8fd9\u4e48\u80d6\u7684\u7f8e\u4eba\uff0c\u5230\u54ea\u91cc\u624d\u80fd\u627e\u5230\u7b2c\u4e8c\u4e2a\uff1f\u201d \u3000\u3000\u8537\u8587\u592b\u4eba\u8138\u90fd\u6c14\u767d\u4e86\uff0c\u5b59\u9035\u5374\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5176\u5b9e\u592b\u4eba\u5f97\u5230\u8fd9\u4ef6\u91d1\u4e1d\u7532\u4e5f\u6ca1\u6709\u7528\u7684\uff0c\u56e0\u4e3a\u5c31\u7b97\u5c06\u592b\u4eba\u4ece\u4e2d\u95f4\u5206\u6210\u4e24\u534a\uff0c \u4e5f\u7a7f\u4e0d\u4e0a\u5b83\u3002\u201d \u3000\u3000\u8537\u8587\u592b\u4eba\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u6211\u82e5\u8ba9\u4f60\u6b7b\u5f97\u75db\u5feb\u4e86\uff0c\u6211\u5c31\u5bf9\u4e0d\u8d77\u4f60\u3002\u201d \u3000\u3000\u5979\u81ea\u5934\u4e0a\u62d4\u4e0b\u4e86\u4e00\u6839\u5f88\u7ec6\u5f88\u5c16\u7684\u91d1\u7c2a\uff0c\u54ac\u7740\u7259\u8d70\u5411\u674e\u5bfb\u6b22\uff0c\u674e\u5bfb\u6b22\u5c45\u7136\u8fd8\u662f\u5b89\u5750\u4e0d\u52a8\uff0c \u7a33\u5982\u6cf0\u5c71\u3002 \u3000\u3000\u5b59\u9035\u76b1\u7709\u9053\uff1a\u201c\u91d1\u4e1d\u7532\u65e2\u5df2\u5230\u624b\uff0c\u6211\u4eec\u8fd8\u662f\u8d76\u5feb\u529e\u6b63\u4e8b\u53bb\u5427\uff0c\u4f55\u5fc5\u8ddf\u4ed6\u8fc7\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u8537\u8587\u592b\u4eba\u543c\u9053\uff1a\u201c\u8001\u5a18\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4f60\u7ba1\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7adf\u771f\u7684\u5df2\u4e0d\u80fd\u52a8\uff0c\u773c\u7741\u7741\u7684\u671b\u7740\u5979\u3002 \u3000\u3000\u8c01\u77e5\u5979\u521a\u51b2\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u521a\u60f3\u5c06\u90a3\u6839\u91d1\u7c2a\u524c\u5165\u4ed6\u7684\u773c\u775b\uff0c\u5b59\u9035\u5ffd\u7136\u4ece\u540e\u9762\u98de\u8d77\u4e00\u811a\uff0c \u5c06\u5979\u8e22\u4e0a\u5c4b\u9876\u3002 \u3000\u3000\u5979\u767e\u628a\u65a4\u91cd\u7684\u8eab\u5b50\u649e\u5728\u5c4b\u9876\u4e0a\uff0c\u6574\u4e2a\u5c4b\u5b50\u90fd\u5feb\u88ab\u5979\u9707\u8de8\u4e86\uff0c\u7b49\u5979\u8dcc\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u5df2\u53ea\u5269 \u4e0b\u534a\u53e3\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6709\u4e9b\u60ca\u8bb6\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u96be\u9053\u662f\u4e3a\u4e86\u6551\u6211\u800c\u6740\u5979\u7684\uff1f\u201d \u3000\u3000\u5b59\u9035\u6068\u6068\u9053\uff1a\u201c\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u5df2\u53d7\u591f\u4e86\u5979\u7684\u6c14\uff0c\u5df2\u7ecf\u5feb\u88ab\u5979\u7f20\u75af\u4e86\uff0c\u6211\u82e5\u4e0d\u6740\u4e86\u5979\uff0c \u4e0d\u51fa\u534a\u5e74\u5c31\u8981\u88ab\u5979\u6d3b\u6d3b\u903c\u6b7b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u8fd9\u662f\u4f60\u81ea\u5df1\u5fc3\u7518\u60c5\u613f\u7684\uff0c\u4f60\u83ab\u5fd8\u8bb0\uff0c\u4e8c\u5341\u5e74\u524d\u2026\u2026\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u6211\u52fe\u5f15\u5979\u7684\uff0c\u4f60\u4ee5\u4e3a\u6211\u60f3\u5e26\u7740\u5979\u79c1\u5954\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5b59\u9035\u53f9\u9053\uff1a\u201c\u6211\u9047\u89c1\u5979\u7684\u65f6\u4faf\uff0c\u6839\u672c\u4e0d\u77e5\u9053\u5979\u662f\u6768\u5927\u80e1\u5b50\u7684\u8001\u5a46\uff0c\u6240\u4ee5\u624d\u4f1a\u8ddf\u5979\u2026\u2026\u201d \u3000\u3000\u4ed6\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u8c01\u77e5\u5979\u7adf\u5403\u5b9a\u4e86\u6211\uff0c\u975e\u8ddf\u6211\u8d70\u4e0d\u53ef\uff0c\u90a3\u65f6\u6768\u5927\u80e1\u5b50\u5df2\u5e26\u7740 \u4e8c\u4e09\u5341\u4e2a\u9ad8\u624b\u6765\u4e86\uff01\u6211\u4e0d\u8d70\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u81f3\u5c11\u5979\u662f\u771f\u7684\u559c\u6b22\u4f60\uff0c\u5426\u5219\u5979\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u559c\u6b22\u6211\uff1f\u563f\u563f\u2026\u2026\u201d \u3000\u3000\u4ed6\u54ac\u7740\u7259\u51b7\u7b11\u9053\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u5979\u62c9\u5230\u7684\u66ff\u6b7b\u9b3c\uff0c\u539f\u6765\u5979\u65e9\u5c31\u8d81\u6768\u5927\u80e1 \u5b50\u51fa\u5173\u7684\u65f6\u5019\uff0c\u59d8\u4e0a\u4e86\u4e00\u4e2a\u5c0f\u767d\u8138\uff0c\u800c\u4e14\u6709\u4e86\u5b69\u5b50\uff0c\u5979\u6015\u6768\u5927\u80e1\u5b50\u56de\u6765\u540e\u65e0\u6cd5\u4ea4\u5e10\uff0c\u5c31\u5377\u7740 \u4e9b\u7ec6\u8f6f\u548c\u90a3\u5c0f\u767d\u8138\u79c1\u5954\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u539f\u6765\u5176\u4e2d\u8fd8\u6709\u8fd9\u4e48\u6bb5\u66f2\u6298\u3002\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u8c01\u77e5\u90a3\u5c0f\u767d\u8138\u5374\u53c8\u5c06\u5979\u4ece\u6768\u80e1\u5b50\u90a3\u91cc\u5077\u6765\u7684\u73e0\u5b9d\u5077\u8d70\u4e86\u4e00\u5927\u534a\uff0c\u5979\u4eba\u8d22\u4e24\u7a7a \uff0c\u6b63\u4e0d\u77e5\u600e\u6837\u597d\uff0c\u6070\u5de7\u9047\u4e0a\u4e86\u6211\u8fd9\u5012\u9709\u9b3c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u4e3a\u4f55\u4e0d\u5411\u522b\u4eba\u89e3\u91ca\uff1f\u201d \u3000\u3000\u5b59\u9035\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5979\u540e\u6765\u9152\u9189\u65f6\u624d\u65e0\u5fc3\u6cc4\u9732\u7684\uff0c\u90a3\u65f6\u751f\u7c73\u65e9\u5df2\u716e\u6210\u719f\u996d\uff0c\u6211\u518d\u60f3\u89e3\u91ca \u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5979\u90a3\u5b69\u5b50\u5462\uff1f\u201d \u3000\u3000\u5b59\u9035\u95ed\u7740\u5634\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u65e9\u5c31\u8be5\u6740\u5979\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8981\u7b49\u5230\u73b0\u5728\uff1f\u201d \u3000\u3000\u5b59\u9035\u8fd8\u662f\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53cd\u6b63\u5df2\u79bb\u6b7b\u4e0d\u8fdc\uff0c\u4f60\u544a\u8bc9\u6211\u53c8\u6709\u4ec0\u4e48\u6709\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5b59\u9035\u6c89\u541f\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u5f00\u9152\u5e97\u6709\u4e2a\u597d\u5904\uff0c\u5c31\u662f\u5e38\u5e38\u53ef\u4ee5\u542c\u5230\u4e00\u4e9b\u6709\u8da3\u7684\u4e8b\u2026\u2026 \u4f60\u53ef\u77e5\u9053\u8fd1\u6765\u6c5f\u6e56\u4e2d\u6700\u6709\u8da3\u7684\u4e8b\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u5f00\u9152\u5e97\u3002\u201d \u3000\u3000\u5b59\u9035\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u5c31\u597d\u50cf\u751f\u6015\u6709\u4eba\u5077\u542c\u4f3c\u7684\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4e09\u5341\u5e74\u524d\u6a2a\u884c\u5929\u4e0b\u7684\u2018\u6885\u82b1\u76d7\u2019\u53c8\u51fa\u73b0\u4e86\uff01\u201d \u3000\u3000\u2018\u6885\u82b1\u76d7\u2019\u8fd9\u4e09\u4e2a\u5b57\u8bf4\u51fa\u6765\uff0c\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u52a8\u5bb9\u3002 \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u6885\u82b1\u76d7\u6a2a\u884c\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u4f60\u8fd8\u5c0f\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7684\u5389\u5bb3\uff0c\u4f46\u6211\u5374\u53ef\u4ee5\u544a\u8bc9 \u4f60\uff0c\u5f53\u65f6\u6c5f\u6e56\u4e2d\u6ca1\u6709\u4e00\u4e2a\u4eba\u4e0d\u77e5\u9053\u4ed6\u7684\uff0c\u8fde\u70b9\u82cd\u7684\u638c\u95e8\uff0c\u5f53\u65f6\u53f7\u79f0\u6c5f\u6e56\u7b2c\u4e00\u5251\u5ba2\u7684\u5434\u95ee\u5929\uff0c \u4e5f\u90fd\u6b7b\u5728\u4ed6\u624b\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u6b47\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u800c\u4e14\u6b64\u4eba\u884c\u8e2a\u98d8\u5ffd\uff0c\u795e\u9b3c\u83ab\u6d4b\uff0c\u5434\u95ee\u5929\u521a\u626c\u8a00\u8981\u627e\u4ed6\uff0c\u7b2c\u4e8c\u5929\u5c31 \u6b7b\u5728\u81ea\u5df1\u7684\u9662\u5b50\u91cc\uff0c\u5168\u8eab\u4e00\u65e0\u4f24\u75d5\uff0c\u53ea\u6709\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u53c8\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u50cf\u662f\u751f\u6015\u90a3\u795e\u9b3c\u96be\u6d4b\u7684\u2018\u6885\u82b1\u76d7\u2019\u4f1a\u5728 \u4ed6\u8eab\u540e\u5ffd\u7136\u51fa\u73b0\u3002 \u3000\u3000\u4f46\u56db\u4e0b\u5374\u662f\u4e00\u7247\u6b7b\u5bc2\uff0c\u751a\u81f3\u8fde\u96ea\u82b1\u98d8\u5728\u5c4b\u9876\u4e0a\u7684\u58f0\u97f3\uff0c\u90fd\u542c\u5f97\u5230\uff0c\u5b59\u9035\u8fd9\u624d\u5410\u51fa\u53e3\u6c14\uff0c \u63a5\u7740\u9053\uff1a\u201c\u53ea\u6709\u80f8\u524d\u591a\u4e86\u4e94\u4e2a\u50cf\u6885\u82b1\u822c\u6392\u5217\u7684\u8840\u75d5\uff0c\u8840\u75d5\u5c0f\u5982\u9488\u773c\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u90a3\u662f\u6885\u82b1\u76d7 \u7684\u6807\u5fd7\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7528\u7684\u7a76\u7adf\u662f\u4ef6\u6781\u6bd2\u8fa3\u7684\u6697\u5668\uff1f\u8fd8\u662f\u4ef6\u6781\u5389\u5bb3\u7684\u51fa\u95e8\u5175\u5668\uff1f\u56e0\u4e3a\u548c \u4ed6\u4ea4\u8fc7\u624b\u7684\u4eba\uff0c\u6ca1\u6709\u4e00\u4e2a\u8fd8\u80fd\u6d3b\u7740\u7684\uff0c\u6240\u4ee5\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u672c\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u521a\u505c\u4e0b\u6765\uff0c\u5ffd\u53c8\u63a5\u7740\u9053\uff1a\u201c\u5927\u5bb6\u53ea\u77e5\u9053\u4ed6\u5fc5\u5b9a\u662f\u4e2a\u7537\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u52ab\u8d22\uff0c\u8fd8\u8981\u52ab\u8272\uff0c\u6c5f\u6e56\u4e2d\u65e0\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u90fd\u6068\u4ed6\u5165\u9aa8\uff0c\u5374\u62ff\u4ed6\u4e00 \u70b9\u6cd5\u5b50\u4e5f\u6ca1\u6709\uff0c\u4f46\u53ea\u8981\u6709\u4eba\u8bf4\u51fa\u8981\u548c\u4ed6\u4f5c\u5bf9\u7684\u8bdd\uff0c\u4e0d\u51fa\u4e09\u5929\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u80f8\u524d\u5fc5\u5b9a\u5e26\u7740\u4ed6\u90a3 \u72ec\u95e8\u7684\u6807\u5fd7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u51e1\u662f\u6b7b\u5728\u4ed6\u624b\u4e0a\u7684\u4eba\uff0c\u81f4\u547d\u7684\u4f24\u75d5\u5fc5\u5728\u524d\u80f8\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u524d\u80f8\u8981\u5bb3\uff0c\u672c\u662f\u7ec3\u5bb6\u5b50\u9632\u536b\u6700\u4e25\u5bc6\u4e4b\u5904\uff0c\u4f46\u90a3\u6885\u82b1\u76d7\u5374\u504f\u504f\u8981\u5728\u6b64\u5904 \u4e0b\u624b\uff0c\u4ece\u65e0\u4f8b\u5916\uff0c\u597d\u50cf\u82e5\u4e0d\u5982\u6b64\uff0c\u5c31\u4e0d\u8db3\u4ee5\u663e\u51fa\u4ed6\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u53ea\u8981\u7a7f\u4e0a\u8fd9\u4ef6\u91d1\u4e1d\u7532\uff0c\u5c31\u80fd\u5c06\u6885\u82b1\u76d7\u5236\u4f4f\uff0c\u53ea\u8981\u4f60\u80fd \u5c06\u6885\u82b1\u76d7\u5236\u4f4f\uff0c\u5c31\u53ef\u4ee5\u626c\u7709\u5410\u6c14\uff0c\u626c\u540d\u5929\u4e0b\uff0c\u9ed1\u767d\u4e24\u9053\u7684\u4eba\u90fd\u4f1a\u56e0\u6b64\u800c\u611f\u6fc0\u4f60\uff0c\u518d\u4e5f\u6ca1\u6709\u4eba \u4f1a\u627e\u4f60\u7b97\u90a3\u7b14\u8001\u5e10\u4e86\u3002\u201d \u3000\u3000\u5b59\u9035\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u53ea\u8981\u80fd\u8eb2\u5f97\u8fc7\u4ed6\u524d\u80f8\u81f4\u547d\u4e4b\u4e00\u51fb\uff0c\u5c31\u5df2\u5148\u7acb \u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u5c31\u6709\u673a\u4f1a\u5c06\u4ed6\u5236\u4f4f\uff01\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u795e\u91c7\u98de\u626c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8fd9\u4e00\u51fb\u4ece\u672a\u5931\u624b\uff0c\u6240\u4ee5\u4ed6\u4f5c\u6b64\u4e00\u51fb\u65f6\uff0c\u5c31\u4e0d\u5fc5\u7559\u4ec0 \u4e48\u9000\u8def\uff0c\u5bf9\u81ea\u5df1\u7684\u9632\u536b\u5fc5\u5b9a\u758f\u5ffd\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u542c\u6765\u5012\u50cf\u662f\u86ee\u6709\u9053\u7406\u2026\u2026\u201d \u3000\u3000\u5b59\u9035\u5927\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6ca1\u6709\u9053\u7406\uff0c\u6c5f\u6e56\u4e2d\u4e5f\u4e0d\u4f1a\u90a3\u4e48\u591a\u4eba\u4e00\u5fc3\u60f3\u5c06\u8fd9\u91d1\u4e1d\u7532\u5f04\u5230\u624b\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5728\u8fd9\u91cc\u79cd\u79cd\u82b1\uff0c\u559d\u559d\u9152\uff0c\u4f60\u7684\u5bf9\u5934\u65e9\u5df2\u6e10\u6e10\u5c06\u4f60\u5fd8\u6000\u4e86\uff0c\u4f60\u7684\u65e5\u5b50 \u96be\u9053\u8fc7\u5f97\u8fd8\u4e0d\u591f\u8212\u670d\u4e48\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u627e\u8fd9\u4e9b\u9ebb\u70e6\u5462\uff1f\u201d \u7b2c\u56db\u7ae0\u3000\u7f8e\u8272\u60d1\u4eba\u610f>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u7ae0\u3000\u7f8e\u8272\u60d1\u4eba\u610f \u3000\u3000\u5b59\u9035\u7b11\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u6211\u82e5\u80fd\u5c06\u6885\u82b1\u76d7\u7f6e\u4e4b\u4e8e\u6b7b\u5730\uff0c\u975e\u4f46\u4ece\u6b64\u626c\u7709\u5410\u6c14\uff0c\u800c\u4e14\u2026\u2026 \u800c\u4e14\u90a3\u597d\u5904\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u6885\u82b1\u76d7\u81ea\u4ece\u5728\u4e09\u5341\u5e74\u524d\u9500\u58f0\u533f\u8ff9\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e2d\u4eba\u672c\u90fd\u4ee5\u4e3a\u4ed6\u5df2\u6076\u8d2f\u6ee1\u76c8\uff0c\u8c01 \u77e5\u534a\u5e74\u591a\u4ee5\u524d\u4ed6\u7adf\u5ffd\u53c8\u51fa\u73b0\uff0c\u5c31\u5728\u8fd9\u77ed\u77ed\u4e03\u3001\u516b\u4e2a\u6708\u91cc\uff0c\u4ed6\u5df2\u53c8\u505a\u4e86\u4e03\u516b\u5341\u4ef6\u5de8\u6848\uff0c\u8fde\u534e\u5c71 \u6d3e\u638c\u95e8\u4eba\u7684\u5973\u513f\uff0c\u90fd\u88ab\u4ed6\u7cdf\u8e4b\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u6b64\u4eba\u7b97\u6765\u5df2\u8be5\u6709\u4e03\u5341\u5de6\u53f3\uff0c\u60f3\u4e0d\u5230\u5174\u8da3\u5c45\u7136\u8fd8\u5982\u6b64\u6d53\u539a\u3002\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u81ea\u4ece\u4ed6\u518d\u6b21\u51fa\u73b0\u540e\uff0c\u6c5f\u6e56\u4e2d\u7a0d\u6709\u8d44\u4ea7\u7684\u4eba\uff0c\u90fd\u5df2\u4eba\u4eba\u81ea\u5371\uff0c\u7a0d\u6709\u59ff\u8272\u7684\u5973\u5b50 \uff0c\u66f4\u662f\u5bdd\u98df\u96be\u5b89\u2026\u2026\u201d\u4ed6\u987f\u4e86\u987f\u63a5\u9053\uff1a\u201c\u6240\u4ee5\u5df2\u6709\u4e5d\u5341\u4f59\u5bb6\u4eba\u5728\u6697\u4e2d\u7ea6\u5b9a\uff0c\u65e0\u8bba\u8c01\u6740\u4e86\u6885\u82b1 \u76d7\uff0c\u4ed6\u4eec\u5c31\u5c06\u81ea\u5df1\u7684\u5bb6\u8d22\u5206\u51fa\u4e00\u6210\u6765\u9001\u7ed9\u4ed6\uff0c\u8fd9\u6570\u76ee\u81ea\u7136\u6781\u4e3a\u53ef\u89c2\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u90a3\u5df2\u4e0d\u6210\u4e3a\u79d8\u5bc6\u7684\u79d8\u5bc6\u4e48\uff1f\u201d \u3000\u3000\u5b59\u9035\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u6c5f\u6e56\u4e2d\u516c\u8ba4\u7684\u7b2c\u4e00\u7f8e\u4eba\u4e5f\u66fe\u626c\u8a00\u5929\u4e0b\uff0c\u65e0\u8bba\u50e7\u4fd7\u8001 \u5c11\uff0c\u53ea\u8981\u4ed6\u80fd\u9664\u53bb\u6885\u82b1\u76d7\uff0c\u5979\u5c31\u5ac1\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8d22\u8272\u52a8\u4eba\u5fc3\uff0c\u8fd9\u5c31\u96be\u602a\u4f60\u653e\u7740\u597d\u597d\u7684\u65e5\u5b50\u4e0d\u8fc7\uff0c\u8981\u6765\u6dcc\u8fd9 \u8d9f\u6d51\u6c34\u4e86\uff0c\u4e5f\u5c31\u96be\u602a\u4f60\u8981\u6740\u4e86\u81ea\u5df1\u7684\u8001\u5a46\uff0c\u73b0\u5728\uff0c\u770b\u6765\u53ea\u6015\u8981\u8f6e\u5230\u6211\u4e86\u3002\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u51ed\u826f\u5fc3\u8bb2\uff0c\u6211\u4e5f\u89c9\u5f97\u4f60\u6b7b\u5f97\u5f88\u51a4\u6789\uff0c\u53ef\u662f\u53c8\u975e\u6740\u4e86\u4f60\u4e0d\u53ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u4e86\uff0c\u60a0\u7136\u9053\uff1a\u201c\u51ed\u826f\u5fc3\u8bb2\uff0c\u4f60\u89c9\u5f97\u6740\u6211\u662f\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5b59\u9035\u7684\u94c1\u62f3\u5df2\u5c06\u4e3e\u8d77\uff0c\u6b64\u523b\u53c8\u4e0d\u7981\u653e\u4e0b\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\u671b\u4e86\u534a\u664c\uff0c\u5634\u89d2\u6e10\u6e10\u9732\u51fa\u4e86\u4e00\u4e1d\u5fae \u7b11\uff0c\u9053\uff1a\u201c\u8c61\u4f60\u8fd9\u6837\u7684\u4eba\u5c45\u7136\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u53ef\u89c1\u8981\u6740\u4f60\u5b9e\u5728\u4e0d\u5bb9\u6613\uff0c\u4f46\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u95e8\u5916\u4f20\u6765\u4e00\u9635\u54cd\u4eae\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4e00\u4eba\u5927\u7b11\u9053\uff1a\u201c\u51ed\u826f\u5fc3\u8bb2\uff0c\u4f60\u770b\u4ed6\u73b0\u5728\u8c61\u662f\u4e2d\u4e86\u6bd2\u7684\u6837\u5b50\u4e48\uff1f\u201d \u3000\u3000\u5b59\u9035\u4e00\u60ca\uff0c\u53a8\u623f\u7684\u5c0f\u95e8\u524d\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ad9\u7740\u4e2a\u9752\u8863\u4eba\uff0c\u4ed6\u8eab\u6750\u5e76\u4e0d\u77ee\uff0c\u4e5f\u4e0d\u592a\u9ad8\uff0c\u795e\u60c5 \u60a0\u95f2\u800c\u6f47\u6d12\uff0c\u4e00\u5f20\u8138\u5374\u662f\u9752\u6e17\u6e17\uff0c\u9634\u68ee\u68ee\u7684\uff0c\u4eff\u4f5b\u6234\u7740\u9762\u5177\uff0c\u53c8\u4eff\u4f5b\u8fd9\u5c31\u662f\u4ed6\u672c\u6765\u7684\u9762\u76ee\u3002 \u3000\u3000\u4ed6\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u60a0\u7136\u8e31\u4e86\u8fdb\u6765\uff0c\u5583\u5583\u53f9\u7740\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u60f3\u5728\u9152\u5f92\u7684\u9152\u4e2d\u4e0b\u6bd2\uff0c\u90a3\u4e48\u65e0 \u8bba\u591a\u4e48\u611a\u8822\u7684\u4e8b\u4ed6\u53ea\u6015\u90fd\u80fd\u505a\u5f97\u51fa\u6765\u4e86\u2026\u2026\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u6700\u540e\u4e00\u53e5\u8bdd\u4ed6\u662f\u95ee\u674e\u5bfb\u6b22\u7684\uff0c\u674e\u5bfb\u6b22\u5ffd\u7136\u53d1\u73b0\u8fd9\u4eba\u7adf\u6709\u53cc\u6700\u52a8\u4eba\u7684\u773c\u775b\uff0c\u548c\u4ed6\u7684\u8138\u5b9e\u5728 \u592a\u4e0d\u76f8\u886c\u3002 \u3000\u3000\u90a3\u5c31\u8c61\u662f\u5d4c\u5728\u6b7b\u732a\u8089\u4e0a\u7684\u4e24\u7c92\u73cd\u73e0\u4f3c\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u8fd9\u53cc\u773c\u775b\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u548c\u8d4c\u9b3c\u8d4c\u94b1\u65f6\u5f04\u9b3c\uff0c\u5728\u9152\u9b3c\u9152\u4e2d\u4e0b\u6bd2\uff0c\u5f53\u7740\u81ea\u5df1\u7684 \u8001\u5a46\u8bf4\u522b\u7684\u5973\u4eba\u6f02\u4eae\u2014\u2014\u65e0\u8bba\u8c01\u505a\u4e86\u8fd9\u4e09\u4ef6\u4e8b\uff0c\u90fd\u4e00\u5b9a\u4f1a\u540e\u6094\u7684\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u4eec\u540e\u6094\u65f6\u5927\u591a\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u5b59\u9035\u5446\u5446\u5730\u671b\u7740\u4ed6\u4eec\uff0c\u5ffd\u7136\u51b2\u8fc7\u53bb\u652b\u8d77\u4e86\u90a3\u53ea\u9152\u58f6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u518d\u770b\uff0c\u9152\u4e2d\u7684\u786e\u6709\u6bd2\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5047\u3002\u201d \u3000\u3000\u5b59\u9035\u560e\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u9152\u4e2d\u662f\u5426\u6709\u6bd2\uff0c\u522b\u7684\u4eba\u4e5f\u8bb8\u770b\u4e0d\u51fa\uff0c\u4f46\u8c61\u6211\u8fd9\u6837\u7684\u9152\u9b3c\uff0c\u7528\u9f3b\u5b50\u4e00\u55c5\u5c31\u77e5 \u9053\u9152\u5473\u662f\u5426\u53d8\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u7740\u63a5\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u559d\u9152\u7684\u597d\u5904\uff0c\u4e0d\u559d\u9152\u7684\u4eba\u90fd\u5e94\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u9035\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u6211\u660e\u660e\u770b\u5230\u4f60\u5c06\u90a3\u676f\u9152\u559d\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u559d\u4e86\u4e0b\u53bb\uff0c\u4f46\u54b3\u55fd\u65f6\u53c8\u5168\u90fd\u5410\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u5b59\u9035\u8eab\u5b50\u4e00\u9707\uff0c\u624b\u91cc\u7684\u9152\u58f6\u53e3\u5f53\u7684\u6389\u5728\u5730\u4e0a\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u770b\u6765\u4ed6\u73b0\u5728\u5df2\u89c9\u5f97\u5f88\u540e\u6094\uff0c\u4f46\u662f\u5df2\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u5b59\u9035\u6012\u543c\u4e00\u58f0\uff0c\u543c\u58f0\u4e2d\u5df2\u5411\u8fd9\u9752\u8863\u4eba\u653b\u51fa\u4e09\u62f3\u3002 \u3000\u3000\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u4ed6\u975e\u4f46\u672a\u5c06\u6b66\u529f\u6401\u4e0b\uff0c\u53cd\u800c\u66f4\u6709\u7cbe\u8fdb\uff0c\u8fd9\u4e00\u62f3\u62db\u6c89\u529b\u731b\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u5148\u58f0 \u5df2\u593a\u4eba\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u770b\u51fa\uff0c\u4ed6\u8fd9\u4e09\u62f3\u867d\u7136\u672a\u5fc5\u80fd\u51fb\u77f3\u5982\u7c89\uff0c\u4f46\u8981\u5c06\u4e00\u4e2a\u4eba\u7684\u8111\u888b\u6253\u788e\uff0c\u5374\u662f\u7ef0 \u7ef0\u6709\u4f59\u3002 \u3000\u3000\u90a3\u9752\u8863\u4eba\u5168\u8eab\u90fd\u4f3c\u5df2\u5728\u62f3\u98ce\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u773c\u770b\u975e\u4f46\u65e0\u6cd5\u62db\u67b6\uff0c\u7b80\u76f4\u8fde\u95ea\u907f\u90fd\u672a\u5fc5\u80fd\u95ea\u907f\u5f97 \u5f00\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u65e2\u672a\u62db\u67b6\uff0c\u4e5f\u672a\u95ea\u907f\uff0c\u53ea\u662f\u8f7b\u8f7b\u4e00\u6325\u624b\u3002 \u3000\u3000\u4ed6\u51fa\u624b\u660e\u660e\u5728\u5b59\u9035\u4e4b\u540e\uff0c\u4f46\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u5b59\u9035\u7684\u62f3\u5934\u8fd8\u672a\u6cbe\u7740\u4ed6\u8863\u88f3\uff0c\u4ed6\u8fd9\u4e00\u638c\u5df2\u63b4\u5728 \u5b59\u9035\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u8c61\u62cd\u82cd\u8747\u4f3c\u7684\u8f7b\u8f7b\u63b4\u4e86\u4e00\u638c\uff0c\u4f46\u5b59\u9035\u5374\u6740\u732a\u822c\u72c2\u543c\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u7b4b\u6597\u8dcc\u5012\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u7b49\u4ed6\u6323\u624e\u7740\u60f3\u722c\u8d77\u6765\uff0c\u5de6\u8fb9\u7684\u534a\u8fb9\u8138\u5df2\u80bf\u8d77\u4e86\u534a\u5c3a\u9ad8\uff0c\u7ea2\u91cc\u53d1\u7d2b\uff0c\u7d2b\u4e2d\u900f\u660e\uff0c\u8fde\u773c\u775b\u90fd \u5df2\u88ab\u6454\u5230\u65c1\u8fb9\u53bb\u4e86\u3002 \u3000\u3000\u9752\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u51ed\u826f\u5fc3\u8bb2\uff0c\u4f60\u6b7b\u5f97\u4e5f\u5b9e\u5728\u6709\u4e9b\u51a4\u6789\uff0c\u6211\u672c\u6765\u5e76\u4e0d\u60f3\u6740\u4f60\u7684\uff0c\u53ef\u662f\u6211\u8fd9 \u53cc\u624b\u2026\u2026\u201d \u3000\u3000\u5b59\u9035\u6ca1\u6709\u80bf\u7684\u534a\u8138\u4e0a\u8fde\u4e00\u4e1d\u8840\u8272\u90fd\u6ca1\u6709\uff0c\u6bcf\u4e00\u6839\u808c\u8089\u90fd\u5728\u626d\u7d27\u7740\uff0c\u886c\u7740\u53e6\u534a\u8fb9\u8138\u4e0a\u4e00\u5806 \u6b7b\u8089\uff0c\u90a3\u6a21\u6837\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u72f0\u72de\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u5269\u4e0b\u7684\u4e00\u53ea\u773c\u775b\u91cc\u66f4\u5145\u6ee1\u4e86\u60ca\u60e7\u4e4b\u8272\uff0c\u671b\u7740\u9752\u8863\u4eba\u7684\u4e00\u53cc\u624b\uff0c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u7684\u624b\u2026\u2026 \u4f60\u7684\u624b\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u624b\u4e0a\uff0c\u6234\u7740\u53cc\u6697\u9752\u8272\u7684\u94c1\u624b\u5957\uff0c\u5f62\u72b6\u770b\u6765\u4e11\u6076\u800c\u7b28\u62d9\uff0c\u4f46\u5b83\u7684\u989c\u8272\u5374\u4ee4\u4eba\u4e00\u770b\u5c31 \u4e0d\u7981\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u5b59\u9035\u76ee\u4e2d\u7684\u60ca\u60e7\u5df2\u53d8\u4e3a\u7edd\u671b\uff0c\u58f0\u97f3\u4e5f\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u7a76\u7adf\u4f5c\u4e86\u4ec0\u4e48\u5b7d\uff1f\u7adf\u53eb \u6211\u4eca\u65e5\u8fd8\u89c1\u7740\u9752\u9b54\u624b\uff1f\u2026\u2026\u674e\u2026\u2026\u674e\u63a2\u82b1\uff0c\u4f60\u662f\u4e2a\u597d\u5fc3\u4eba\uff0c\u6c42\u6c42\u4f60\u6740\u4e86\u6211\u5427\uff0c\u5feb\u6740\u4e86\u6211\u5427\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u4ecd\u5750\u5728\u90a3\u91cc\u6ca1\u6709\u52a8\uff0c\u773c\u775b\u4e5f\u76ef\u5728\u9752\u8863\u4eba\u7684\u90a3\u53cc\u624b\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u7528\u811a\u5c16\u5c06\u90a3\u534a\u7ec3\u5b50\u67aa \u5934\u62e8\u5230\u5b59\u9035\u7684\u624b\u8fb9\u3002 \u3000\u3000\u5b59\u9035\u6323\u624e\u7740\u62fe\u8d77\u4e86\u5b83\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u8c22\u8c22\u4f60\uff0c\u6211\u6b7b\u4e5f\u5fd8\u4e0d\u4e86\u4f60\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u4ed6\u7528\u5c3d\u5168\u8eab\u529b\u6c14\uff0c\u5c06\u90a3\u534a\u622a\u7ec3\u5b50\u67aa\u5934\u63d2\u5165\u4e86\u81ea\u5df1\u7684\u54bd\u5589\uff0c\u81ea\u5589\u5934\u6e85\u51fa\u6765\u7684\u9c9c\u8840\uff0c\u5df2\u53d8\u4e3a \u7d2b\u9ed1\u8272\u7684\uff0c\u5c31\u8c61\u662f\u4ece\u9634\u6c9f\u91cc\u6d41\u51fa\u6765\u7684\u6cc9\u6c34\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9616\u8d77\u773c\u775b\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6b66\u6797\u6709\u4e03\u6bd2\uff0c\u6700\u6bd2\u9752\u9b54\u624b\u2026\u2026\u8fd9\u8bdd\u770b\u6765\u5012\u6ca1\u6709 \u5938\u5f20\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4e5f\u5728\u671b\u7740\u81ea\u5df1\u7684\u4e00\u53cc\u624b\uff0c\u5c45\u7136\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u6328\u4e86\u9752\u9b54\u624b\u7684\u4eba\u751f\u4e0d\u5982 \u6b7b\uff0c\u53ea\u60f3\u8d8a\u5feb\u6b7b\u8d8a\u597d\uff0c\u7684\u786e\u6ca1\u6709\u5938\u5f20\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u79fb\u5230\u4ed6\u8138\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f46\u9601\u4e0b\u5374\u5e76\u975e\u2018\u9752\u9b54\u2019\u4f0a\u54ed\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\u6211\u4e0d\u662f\uff0c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4f3c\u4e4e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5012\u4e5f\u5e76\u4e0d\u662f\u60f3\u5192\u5145\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f0a\u54ed\u6ca1\u6709\u5f92\u5f1f\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u662f\u4ed6\u7684\u5f92\u5f1f\uff0c\u5c31\u51ed\u4ed6\uff0c\u505a\u6211\u7684\u5f92\u5f1f\u90fd\u4e0d\u914d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u5728\u5439\u725b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u6211\u5bf9\u9601\u4e0b\u7684\u6765\u5386\u8eab\u4efd\u5e76\u6ca1\u6709\u5174\u8da3\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u52a8\u4eba\u7684\u773c\u775b\u5ffd\u7136\u53d1\u51fa\u4e86\u9510\u5229\u7684\u5149\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u5bf9\u4ec0\u4e48\u6709\u5174\u8da3\uff1f\u91d1\u4e1d\u7532\uff1f \u201d \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u56de\u7b54\uff0c\u53ea\u662f\u7f13\u7f13\u629a\u6478\u7740\u624b\u91cc\u7684\u5c0f\u5200\u3002 \u3000\u3000\u9752\u8863\u4eba\u76ee\u5149\u4e5f\u843d\u5728\u8fd9\u67c4\u5c0f\u5200\u4e0a\uff0c\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u4f60\u2018\u51fa\u624b\u4e00\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\u2019\uff0c\u8fd9\u8bdd\u4e0d\u77e5 \u6709\u6ca1\u6709\u5938\u5f20\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ee5\u524d\u4e5f\u6709\u5f88\u591a\u4eba\u5bf9\u8fd9\u53e5\u8bdd\u8868\u793a\u6000\u7591\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u95ea\u8fc7\u4e00\u4e1d\u8427\u7d22\u4e4b\u610f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u73b0\u5728\u4eba\u90fd\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9ed8\u7136\u534a\u664c\uff0c\u5ffd\u7136\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7b11\u7684\u58f0\u97f3\u5f88\u5947\u7279\uff0c\u5c31\u8c61\u662f\u786c\u903c\u51fa\u6765\u7684\uff0c\u7b11\u58f0\u867d\u5f88\u5927\uff0c\u4ed6\u9762\u4e0a\u5374\u4ecd\u6b7b\u9c7c\u822c\u5168\u65e0\u8868\u60c5\uff0c\u9053 \uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u7684\u786e\u60f3\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u529d\u4f60\u6700\u597d\u4e0d\u8981\u8bd5\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u987f\u4f4f\u7b11\u58f0\uff0c\u53c8\u77aa\u4e86\u674e\u5bfb\u6b22\u51e0\u773c\uff0c\u9053\uff1a\u201c\u91d1\u4e1d\u7532\u5c31\u5728\u9505\u91cc\u90a3\u6b7b\u4eba\u8eab\u4e0a\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u6211\u82e5\u53bb\u52a8\u90a3\u6b7b\u4eba\uff0c\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u53ea\u6015\u4e5f\u8981\u53d8\u6210\u6b7b\u4eba\u4e86\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u6015\u4f60\uff0c\u53ea\u4e0d\u8fc7\u6211\u8fd9\u4eba\u5929\u751f\u4e0d\u559c\u6b22\u8d4c\u535a\uff0c\u4e5f\u4e0d\u559c\u6b22\u5192\u9669 \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u662f\u79cd\u597d\u4e60\u60ef\uff0c\u53ea\u8981\u4f60\u80fd\u4fdd\u6301\uff0c\u4e00\u5b9a\u4f1a\u957f\u547d\u7684\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u76ee\u5149\u95ea\u52a8\u7740\uff0c\u9053\uff1a\u201c\u4f46\u6211\u603b\u6709\u6cd5\u5b50\u80fd\u4ee4\u4f60\u5c06\u8fd9\u91d1\u4e1d\u7532\u8ba9\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u8fd9\u2018\u9752\u9b54\u624b\u2019\u4e43\u662f\u4f0a\u54ed\u91c7\u91d1\u94c1\u4e4b\u82f1\uff0c\u6dec\u4ee5\u767e\u6bd2\uff0c\u953b\u51b6\u4e86\u4e03\u5e74 \u624d\u5236\u6210\u7684\uff0c\u53ef\u8bf4\u662f\u6b66\u6797\u4e2d\u6700\u9738\u9053\u7684\u5175\u5203\u4e4b\u4e00\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u767e\u6653\u751f\u4f5c\u2018\u5175\u5668\u8c31\u2019\uff0c\u9752\u9b54\u624b\u6392\u540d\u7b2c\u4e5d\uff0c\u53ef\u7b97\u73cd\u54c1\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u82e5\u5c06\u8fd9\u9752\u9b54\u624b\u9001\u7ed9\u4f60\uff0c\u4f60\u80af\u4e0d\u80af\u5c06\u91d1\u4e1d\u7532\u8ba9\u7ed9\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u671b\u7740\u624b\u91cc\u7684\u5c0f\u5200\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u8fd9\u628a\u5c0f\u5200\u53ea\u4e0d\u8fc7\u662f\u5927\u51b6\u7684\u94c1\u5320\uff0c\u82b1 \u4e86\u4e09\u4e2a\u65f6\u8fb0\u6253\u597d\u7684\uff0c\u4f46\u767e\u6653\u751f\u54c1\u8bc4\u5929\u4e0b\u5175\u5668\uff0c\u5c0f\u674e\u98de\u5200\u5374\u6392\u540d\u7b2c\u4e09\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u5175\u5668\u7684\u597d\u574f\u5e76\u6ca1\u6709\u5173\u7cfb\uff0c\u4e3b\u8981\u7684\u662f\u8981\u770b\u7528 \u5175\u5668\u7684\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u662f\u806a\u660e\u4eba\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e0d\u80af\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u82e5\u60f3\u8981\u5b83\uff0c\u73b0\u5728\u5b83\u5c31\u4e0d\u4f1a\u5728\u4f60\u7684\u624b\u4e0a\u4e86\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u6c89\u541f\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u81ea\u6000\u4e2d\u53d6\u51fa\u4e2a\u957f\u800c\u6241\u7684\u5323\u5b50\u3002 \u3000\u3000\u4ed6\u5c06\u8fd9\u5323\u5b50\u614e\u91cd\u5730\u653e\u5728\u684c\u4e0a\uff0c\u7528\u4e24\u53ea\u6234\u7740\u94c1\u624b\u5957\u7684\u624b\uff0c\u7b28\u62d9\u5730\u5c06\u5323\u5b50\u6253\u5f00\uff0c\u7acb\u523b\u4fbf\u6709\u4e00 \u9635\u5251\u6c14\u2018\u5b57\u5f62\u5de6\u2018\u77f3\u2019\u53f3\u2018\u4e4f\u201d\u4eba\u808c\u80a4\u3002 \u3000\u3000\u8fd9\u9edd\u9ed1\u7684\u94c1\u5323\u5b50\u91cc\uff0c\u7adf\u662f\u67c4\u5bd2\u5149\u7167\u4eba\u7684\u77ed\u5251\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5b9d\u5251\u8d60\u82f1\u96c4\uff0c\u8fd9\u67c4\u2018\u9c7c\u80a0\u5251\u2019\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u603b\u8be5\u80fd\u914d\u5f97\u8fc7\u4f60\u4e86\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u5bb9\u9053\uff1a\u201c\u9601\u4e0b\u83ab\u975e\u662f\u2018\u85cf\u5251\u5c71\u5e84\u2019\u85cf\u9f99\u8001\u4eba\u7684\u5b50\u5f1f\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u9601\u4e0b\u8fd9\u67c4\u5251\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8001\u9f99\u5df2\u6b7b\u4e86\uff0c\u8fd9\u662f\u4ed6\u513f\u5b50\u6e38\u9f99\u751f\u9001\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u9c7c\u80a0\u5251\u4e0a\u53e4\u795e\u5175\uff0c\u6b66\u6797\u91cd\u5b9d\uff0c\u2018\u85cf\u5251\u5c71\u5e84\u2019\u4e5f\u4ee5\u5251\u800c\u540d\uff0c\u82e5\u975e\u56e0\u4e3a\u85cf\u9f99\u8001 \u4eba\u4e0e\u5c11\u6797\uff0c\u6b66\u5f53\uff0c\u6606\u4ed1\u4e09\u5927\u6d3e\u7684\u638c\u95e8\u4eba\u4ff1\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u6b64\u5251\u65e9\u5df2\u88ab\u4eba\u593a\u53bb\uff0c\u867d\u662f\u5982\u6b64\uff0c\u85cf\u5251 \u5c71\u5e84\u4e3a\u4e86\u6b64\u5251\u8fd8\u662f\u4e0d\u77e5\u7ecf\u8fc7\u591a\u5c11\u6b21\u6d74\u8840\u6218\uff0c\u90a3\u6e38\u5c11\u5e84\u4e3b\u53c8\u600e\u4f1a\u5c06\u8fd9\u4f20\u5bb6\u4e4b\u5b9d\u8f7b\u6613\u9001\u4eba\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u83ab\u8bf4\u662f\u67c4\u5251\uff0c\u6211\u5c31\u7b97\u8981\u4ed6\u5c06\u5934\u9885\u9001\u7ed9\u6211\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u62d2\u7edd\u7684\uff0c \u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u6b64\u5251\u4ef7\u503c\u53ea\u6015\u8fd8\u5728\u91d1\u4e1d\u7532\u4e4b\u4e0a\uff0c\u9601\u4e0b\u4e3a\u4f55\u8981\u4ee5\u8d35\u6613\u8d31\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u8fd9\u4eba\u5929\u751f\u6709\u4e2a\u813e\u6c14\uff0c\u8d8a\u4e0d\u5bb9\u6613\u5230\u624b\u7684\u4e1c\u897f\uff0c\u6211\u8d8a\u60f3\u8981\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6070\u5de7\u6211\u4e5f\u6709\u8fd9\u813e\u6c14\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4e0d\u80af\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u80af\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u6012\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e00\u5b9a\u975e\u8981\u90a3\u91d1\u4e1d\u7532\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u4e8b\u4e0e\u9601\u4e0b\u65e0\u5173\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4e45\u4ef0\u2018\u5c0f\u674e\u63a2\u82b1\u2019\u4e00\u5411\u6de1\u6cca\u540d\u5229\uff0c\u89c6\u5bcc\u8d35\u5982\u6d6e\u4e91\uff0c\u4e8c\u5341 \u5e74\u524d\u5f03\u529f\u540d\u5982\u7caa\uff0c\u5341\u5e74\u524d\u53c8\u6563\u5c3d\u4e86\u4e07\u8d2f\u5bb6\u8d22\uff0c\u9690\u59d3\u57cb\u540d\uff0c\u8427\u7136\u51fa\u5173\u2026\u2026\u8fd9\u6837\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4f1a \u5bf9\u533a\u533a\u4e00\u4ef6\u91d1\u4e1d\u7532\u770b\u5f97\u90a3\u4e48\u91cd\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u6211\u7684\u539f\u56e0\uff0c\u53ea\u6015\u548c\u9601\u4e0b\u4e00\u6837\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u83ab\u975e\u662f\u4e3a\u4e86\u90a3\u5929\u4e0b\u7b2c\u4e00\u7684\u7f8e\u4eba\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u65e9\u5c31\u542c\u8bf4\u8fc7\uff0c\u4f60\u5bf9\u4f73\u4eba\u548c\u7f8e\u9152\uff0c\u662f\u4ece\u6765\u4e0d\u80af\u62d2\u7edd\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u9601\u4e0b\u5e76\u975e\u7edd\u4ee3\u4e4b\u4f73\u4eba\u3002 \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u600e\u77e5\u6211\u4e0d\u662f\uff1f\u201d \u3000\u3000\u2018\u4ed6\u2019\u7684\u7b11\u58f0\u5ffd\u7136\u53d8\u4e86\uff0c\u53d8\u5f97\u94f6\u94c3\u822c\u5a07\u7f8e\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u4ed6\u7f13\u7f13\u8131\u4e0b\u4e86\u90a3\u53cc\u6697\u9752\u8272\u7684\u624b\u5957\uff0c\u9732\u51fa\u4e86\u4ed6\u7684\u624b\u6765\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u7f8e\u4e3d\u7684\u624b\u3002 \u3000\u3000\u2018\u5c0f\u674e\u98ce\u6d41\u2019\uff0c\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u548c\u591a\u5c11\u4f4d\u7edd\u8272\u7f8e\u4eba\u6709\u8fc7\u5e7d\u671f\u5bc6\u4f1a\uff0c\u4ed6\u638c\u4e2d\u6ca1\u6709\u62ff\u7740 \u98de\u5200\u548c\u9152\u676f\u7684\u65f6\u5019\uff0c\u4e5f\u4e0d\u77e5\u63e1\u8fc7\u591a\u5c11\u53cc\u6625\u8471\u822c\u7684\u67d4\u8351\u3002 \u3000\u3000\u7f8e\u4eba\u7684\u624b\uff0c\u5927\u591a\u90fd\u662f\u7f8e\u4e3d\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5374\u53d1\u73b0\u65e0\u8bba\u591a\u7f8e\u7684\u624b\uff0c\u591a\u591a\u5c11\u5c11\u90fd\u6709\u4e00\u4e9b\u7f3a\u9677\uff0c\u6709\u7684\u662f\u80a4\u8272\u7a0d\u9ed1\uff0c\u6709\u7684\u662f\u6307\u7532\u7a0d \u5927\uff0c\u6709\u7684\u662f\u6307\u5c16\u7a0d\u7c97\uff0c\u6709\u7684\u662f\u6bdb\u5b54\u7a0d\u5927\u2026\u2026\u5c31\u8fde\u90a3\u4f7f\u4ed6\u68a6\u7275\u9b42\u8426\uff0c\u6c38\u751f\u96be\u5fd8\u7684\u5973\u4eba\uff0c\u90a3\u53cc\u624b \u4e5f\u5e76\u975e\u5168\u65e0\u7455\u7656\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u4e2a\u6027\u592a\u5f3a\uff0c\u6240\u4ee5\u5979\u7684\u624b\u4e5f\u672a\u514d\u7a0d\u89c9\u5927\u4e86\u4e9b\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5c55\u793a\u5728\u4ed6\u773c\u524d\u7684\u8fd9\u53cc\u624b\uff0c\u5374\u662f\u5341\u5168\u5341\u7f8e\uff0c\u6beb\u65e0\u7f3a\u9677\uff0c\u5c31\u8c61\u662f\u4e00\u5757\u7cbe\u5fc3\u96d5\u78e8\u6210\u7684\u7f8a \u8102\u7f8e\u7389\uff0c\u6ca1\u6709\u4e1d\u6beb\u6742\u8272\uff0c\u53c8\u90a3\u4e48\u67d4\u8f6f\uff0c\u589e\u4e4b\u4e00\u5206\u5219\u592a\u80a5\uff0c\u51cf\u4e4b\u4e00\u5206\u5219\u592a\u7626\uff0c\u65e2\u4e0d\u592a\u957f\uff0c\u4e5f\u4e0d \u592a\u77ed\u3002 \u3000\u3000\u5c31\u7b97\u6700\u4f1a\u6311\u5254\u7684\u4eba\uff0c\u4e5f\u7edd\u5bf9\u6311\u4e0d\u51fa\u4e1d\u6beb\u6bdb\u75c5\u6765\u3002 \u3000\u3000\u9752\u8863\u4eba\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u770b\u6211\u8fd9\u53cc\u624b\u662f\u4e0d\u662f\u6bd4\u9752\u9b54\u624b\u597d\u770b\u4e9b\u5462\uff1f\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u5ffd\u7136\u53d8\u5f97\u90a3\u4e48\u5a07\u7f8e\uff0c\u5c31\u7b97\u7528\u2018\u51fa\u8c37\u9ec4\u83ba\u2019\u8fd9\u56db\u4e2a\u5b57\u6765\u5f62\u5bb9\uff0c\u4e5f\u5acc\u592a\u4fae\u8fb1\u4e86\u5979 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u7528\u8fd9\u53cc\u624b\u6740\u4eba\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u62b5\u6297\u7684\uff0c\u53c8\u4f55\u5fc5\u518d\u7528\u9752\u9b54\u624b\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u5a07\u7b11\u7740\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u518d\u548c\u4f60\u8c08\u5224\u4ea4\u6362\uff0c\u6761\u4ef6\u662f\u4e0d\u662f\u5df2\u597d\u4e86\u4e9b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd8\u4e0d\u591f\u597d\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7528\u5979\u90a3\u6beb\u65e0\u7455\u7656\u7684\u624b\u4e00\u62c9\u8896\u5b50\uff0c\u5979\u7684\u8863\u8896\u5c31\u65ad\u843d\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u4e86\u4e00\u53cc\u4e30\u76c8\u4f46\u4e0d\u89c1 \u8089\uff0c\u7ea4\u7f8e\u800c\u4e0d\u89c1\u9aa8\u7684\u624b\u81c2\u3002 \u3000\u3000\u624b\uff0c\u672c\u6765\u5df2\u7edd\u7f8e\uff0c\u518d\u886c\u4e0a\u8fd9\u53cc\u624b\u81c2\uff0c\u66f4\u4ee4\u4eba\u76ee\u7729\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd8\u4e0d\u591f\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7537\u4eba\u90fd\u8d2a\u5fc3\u5f97\u5f88\uff0c\u5c24\u5176\u662f\u6709\u672c\u4e8b\u7684\u7537\u4eba\uff0c\u8d8a\u6709\u672c\u4e8b\uff0c\u8d2a\u5fc3\u8d8a\u5927\u2026\u2026 \u201d \u3000\u3000\u5979\u8eab\u5b50\u8f7b\u8f7b\u7684\u626d\u52a8\uff0c\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5979\u8eab\u4e0a\u5df2\u53ea\u5269\u4e0b\u4e00\u7f15\u8f7b\u7eb1\u5236\u6210\u7684\u5185\u5fc3\uff0c\u96fe\u91cc\u770b\u82b1\uff0c\u6700 \u662f\u9500\u9b42\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5df2\u5c06\u6ca1\u6709\u6bd2\u7684\u9152\u5012\u4e86\u4e00\u676f\uff0c\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u8d4f\u82b1\u4e0d\u53ef\u65e0\u9152\uff0c\u8bf7\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8fd8\u662f\u89c9\u5f97\u4e0d\u591f\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u7537\u4eba\u90fd\u8d2a\u5fc3\u5f97\u5f88\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u94f6\u94c3\u822c\u7b11\u7740\uff0c\u892a\u4e0b\u4e86\u978b\u889c\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u8131\u978b\u5b50\u7684\u59ff\u6001\u90fd\u4e0d\u4f1a\u597d\u770b\u7684\uff0c\u4f46\u4ed6\u5374\u662f\u4f8b\u5916\uff0c\u4efb\u4f55\u4eba\u7684\u811a\u90fd\u96be\u514d\u6709\u4e9b\u7c97\u7cd9\uff0c\u5979\u4e5f \u662f\u4f8b\u5916\u3002 \u3000\u3000\u5979\u7684\u811a\u8e1d\u662f\u90a3\u4e48\u7ea4\u7f8e\uff0c\u5979\u7684\u811a\u66f4\u4ee4\u4eba\u9500\u9b42\uff0c\u82e5\u8bf4\u8fd9\u4e16\u4e0a\u6709\u5f88\u591a\u7537\u4eba\u60c5\u613f\u88ab\u8fd9\u53cc\u811a\u8e29\u6b7b\u4e5f \u4e00\u5b9a\u4e0d\u4f1a\u6709\u4eba\u6000\u7591\u7684\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5979\u53c8\u9732\u51fa\u4e86\u5979\u90a3\u53cc\u4fee\u957f\u7684\uff0c\u7b14\u76f4\u7684\u817f\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u674e\u5bfb\u6b22\u8fde\u547c\u5438\u90fd\u4f3c\u4e4e\u5df2\u505c\u6b62\u3002 \u3000\u3000\u9752\u8863\u4eba\u67d4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u82e5\u8bf4\u591f\uff0c\u6211\u5c31\u662f\u5446\u5b50\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u60f3\u8c61\u4e16\u4e0a\u7adf\u6709\u5982\u6b64\u5b8c\u7f8e\u7684\u8eaf\u4f53\uff0c\u73b0\u5728\uff0c\u5979\u5df2\u5c06\u8eaf\u4f53\u6beb\u65e0\u4fdd\u7559\u5730\u5c55\u793a\u5728\u674e\u5bfb\u6b22\u773c \u524d\u3002 \u3000\u3000\u5979\u7684\u80f8\u819b\u575a\u633a\uff0c\u53cc\u817f\u7d27\u5e76\u2026\u2026 \u3000\u3000\u5728\u8fd9\u8bf1\u4eba\u7684\u8eaf\u4f53\u540e\uff0c\u5374\u6709\u4e09\u5177\u6b7b\u5c38\uff0c\u4f46\u662f\u975e\u4f46\u6ca1\u6709\u51cf\u4f4e\u5979\u7684\u8bf1\u60d1\uff0c\u53cd\u800c\u66f4\u5e73\u6dfb\u4e86\u51e0\u5206\u6b8b \u9177\u7684\u717d\u52a8\u529b\u3002 \u3000\u3000\u90a3\u5b9e\u5728\u53ef\u4ee5\u4ee4\u4efb\u4f55\u7537\u4eba\u72af\u7f6a\u3002 \u3000\u3000\u552f\u4e00\u7684\u9057\u61be\u662f\uff0c\u5979\u8fd8\u6ca1\u6709\u5c06\u90a3\u9752\u6e17\u6e17\u7684\u9762\u5177\u9664\u4e0b\u6765\u3002 \u3000\u3000\u5979\u53ea\u662f\u7528\u90a3\u53cc\u8bf1\u4eba\u7684\u773c\u775b\u671b\u7740\u674e\u5bfb\u6b22\uff0c\u8f7b\u8f7b\u5598\u606f\u7740\u9053\uff1a\u201c\u73b0\u5728\u603b\u8be5\u591f\u4e86\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u5979\u8138\u4e0a\u7684\u9762\u5177\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5df2\u5dee\u4e0d\u591a\u4e86\uff0c\u53ea\u5dee\u4e00\u70b9\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5df2\u7ecf\u5e94\u8be5\u77e5\u8db3\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5bb9\u6613\u77e5\u8db3\u7684\u7537\u4eba\uff0c\u65f6\u5e38\u90fd\u4f1a\u9519\u8fc7\u5f88\u591a\u597d\u4e1c\u897f\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7684\u80f8\u819b\u8d77\u4f0f\u7740\uff0c\u90a3\u4e00\u53cc\u5ae3\u7ea2\u7684\u84d3\u857e\u9a84\u50b2\u7684\u633a\u7acb\u5728\u674e\u5bfb\u6b22\u773c\u524d\uff0c\u4f3c\u4e4e\u5df2\u5728\u6e10\u6e10\u6da8\u5927 \u2026\u2026 \u3000\u3000\u5979\u8f7b\u8f7b\u98a4\u6296\u7740\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u4e00\u5b9a\u8981\u770b\u6211\u7684\u8138\uff0c\u8fd9\u4e48\u6837\uff0c\u5c82\u975e\u53cd\u800c\u589e\u52a0\u51e0\u5206\u5e7b\u60f3\uff0c\u51e0\u5206\u60c5 \u8da3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u77e5\u9053\u6709\u5f88\u591a\u8eab\u6750\u5f88\u597d\u7684\u5973\u4eba\uff0c\u4e00\u5f20\u8138\u5374\u662f\u4e11\u516b\u602a\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u770b\u6211\u8c61\u4e11\u516b\u602a\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u5012\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e2a\u6b7b\u5fc3\u773c\u7684\u4eba\uff0c\u4f46\u6211\u529d\u4f60\u8fd8\u662f\u83ab\u8981\u770b\u5230\u6211\u7684\u8138\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u548c\u4f60\u4ea4\u6362\u90a3\u91d1\u4e1d\u7532\u540e\uff0c\u7acb\u523b\u5c31\u4f1a\u8d70\u7684\uff0c\u4ee5\u540e\u53ea\u6015\u6c38\u8fdc\u518d\u4e5f\u4e0d\u4f1a\u76f8\u89c1\uff0c\u4f60 \u7ed9\u6211\u91d1\u4e1d\u7532\uff0c\u6211\u7ed9\u4f60\u4e16\u4e0a\u6700\u5927\u7684\u5feb\u4e50\uff0c\u8fd9\u672c\u662f\u5f88\u516c\u9053\u7684\u4ea4\u6613\uff0c\u8c01\u4e5f\u4e0d\u5403\u4e8f\uff0c\u6240\u4ee5\u4ee5\u540e\u8c01\u4e5f\u4e0d \u5fc5\u8bb0\u7740\u8c01\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6709\u7406\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f46\u4f60\u53ea\u8981\u770b\u5230\u6211\u7684\u8138\u540e\uff0c\u5c31\u6c38\u8fdc\u518d\u4e5f\u4e0d\u80fd\u5fd8\u8bb0\u6211\u4e86\uff0c\u800c\u6211\uff0c\u5374\u662f\u4e00\u5b9a\u4e0d\u4f1a \u518d\u8ddf\u4f60\u2026\u2026\u8ddf\u4f60\u8981\u597d\u7684\uff0c\u90a3\u4e48\u4f60\u96be\u514d\u5c31\u8981\u7ec8\u65e5\u76f8\u601d\uff0c\u5c82\u975e\u81ea\u5bfb\u70e6\u607c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5012\u5bf9\u81ea\u5df1\u5f88\u6709\u81ea\u4fe1\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7684\u7ea4\u624b\u81ea\u80f8\u819b\u4e0a\u7f13\u7f13\u6ed1\u4e0b\u53bb\uff0c\u5e26\u7740\u8bf1\u4eba\u7684\u5a9a\u7b11\u9053\uff1a\u201c\u6211\u96be\u9053\u4e0d\u8be5\u6709\u81ea\u4fe1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u4e0d\u80af\u548c\u4f60\u505a\u8fd9\u4ea4\u6613\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u4f3c\u4e4e\u6123\u4e86\u6123\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u80af\uff1f\u201d \u3000\u3000\u5979\u7ec8\u4e8e\u4f38\u8d77\u624b\uff0c\u5c06\u90a3\u9762\u5177\u892a\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u5c31\u9759\u9759\u5730\u671b\u7740\u674e\u5bfb\u6b22\uff0c\u8c61\u662f\u5728\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u4e0d\u80af\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u5f20\u8138\u5b9e\u5728\u7f8e\u4e3d\u5f97\u4ee4\u4eba\u7a92\u606f\uff0c\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\uff0c\u518d\u914d\u4e0a\u8fd9\u6837\u7684\u8eaf\u4f53\uff0c\u4e16\u4e0a\u5b9e\u5728\u5f88\u5c11\u6709\u4eba\u80fd \u6297\u62d2\u3002 \u3000\u3000\u5c31\u7b97\u662f\u778e\u5b50\uff0c\u4e5f\u53ef\u4ee5\u95fb\u5f97\u5230\u5979\u8eab\u4e0a\u6563\u53d1\u51fa\u7684\u90a3\u4e00\u7f15\u7f15\u751c\u9999\uff0c\u4e5f\u53ef\u4ee5\u542c\u5f97\u5230\u5979\u90a3\u9500\u9b42\u8361\u9b44 \u7684\u67d4\u8bed\u3002 \u3000\u3000\u90a3\u5df2\u662f\u7537\u4eba\u65e0\u6cd5\u6297\u62d2\u7684\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u7981\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u96be\u602a\u5c39\u54ed\u90a3\u6837\u7684\u4eba\u4f1a\u5c06\u2018\u9752\u9b54\u624b\u2019\u9001\u7ed9\u4f60\uff0c\u96be\u602a\u6e38\u5c11\u5e84 \u4e3b\u80af\u5fc3\u7518\u60c5\u613f\u5730\u5c06\u4ed6\u4f20\u5bb6\u4e4b\u5b9d\u5949\u732e\u5728\u4f60\u8db3\u4e0b\uff0c\u6211\u73b0\u5728\u5b9e\u5df2\u65e0\u6cd5\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u8fd9\u8d64\u88f8\u7684\u7edd\u4ee3\u7f8e\u4eba\u53ea\u662f\u5fae\u7b11\u7740\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u81ea\u5df1\u5df2\u7528\u4e0d\u7740\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u4f1a\u8bf4\u8bdd\uff0c\u5979\u7684\u5a9a\u7b11\u4f1a\u8bf4\u8bdd\uff0c\u5979\u7684\u624b\uff0c\u5979\u7684\u80f8\u819b\uff0c\u5979\u7684\u817f\u2026\u2026\u5979\u8eab\u4e0a\u6bcf\u5206\u6bcf\u5bf8\u90fd \u4f1a\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u8db3\u591f\u4e86\uff0c\u82e5\u6709\u7537\u4eba\u8fd8\u4e0d\u61c2\u5979\u7684\u610f\u601d\uff0c\u90a3\u4eba\u4e00\u5b9a\u662f\u767d\u75f4\u3002 \u3000\u3000\u5979\u5728\u7b49\u5f85\u7740\uff0c\u4e5f\u5728\u9080\u8bf7\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u504f\u504f\u8fd8\u6ca1\u6709\u7ad9\u8d77\u6765\uff0c\u53cd\u800c\u5012\u4e86\u676f\u9152\uff0c\u7f13\u7f13\u559d\u4e86\u4e0b\u53bb\uff0c\u624d\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5f88 \u4e45\u6ca1\u6709\u8fd9\u4e48\u6837\u7684\u773c\u798f\u4e86\uff0c\u8c22\u8c22\u4f60\u3002 \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u5782\u7740\u5934\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u8fd8\u8981\u559d\u9152\u6765\u58ee\u80c6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u6f02\u4eae\u7684\u5973\u4eba\u4e5f\u90fd\u5f88\u4e0d\u5bb9\u6613\u6ee1\u8db3\u7684\u3002\u201d \u3000\u3000\u5979\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u86c7\u4e00\u822c\u6ed1\u5165\u4e86\u674e\u5bfb\u6b22\u7684\u6000\u62b1\u3002 \u3000\u3000\u9152\u676f\u201c\u5f53\u201d\u7684\u8dcc\u5728\u5730\u4e0a\uff0c\u788e\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u6cbf\u7740\u5979\u5149\u6ed1\u7684\u80cc\u6ed1\u4e86\u4e0b\u53bb\uff0c\u4f46\u4ee4\u4e00\u53ea\u624b\u5374\u4ecd\u63e1\u7740\u90a3\u67c4\u5200\uff0c\u77ed\u800c\u950b\u5229\u7684\u5c0f\u5200\u3002 \u3000\u3000\u5c11\u5973\u7684\u8eaf\u4f53\u626d\u52a8\u7740\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u7537\u4eba\u5728\u505a\u8fd9\u79cd\u4e8b\u7684\u65f6\u5019\uff0c\u624b\u91cc\u4e0d\u8be5\u8fd8\u62ff\u7740\u5200\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u58f0\u97f3\u4e5f\u5f88\u6e29\u67d4\uff0c\u9053\uff1a\u201c\u7537\u4eba\u624b\u91cc\u62ff\u7740\u5200\u65f6\uff0c\u4f60\u5c31\u4e0d\u8be5\u5750\u5728\u4ed6\u6000\u91cc\u3002\u201d \u3000\u3000\u5c11\u5973\u5a9a\u7b11\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u8fd8\u5fcd\u5fc3\u6740\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u5b69\u5b50\u4e0d\u53ef\u4ee5\u5982\u6b64\u81ea\u4fe1\uff0c\u66f4\u4e0d\u53ef\u4ee5\u8131\u5149\u4e86\u6765\u52fe\u5f15\u7537\u4eba\uff0c\u5979\u5e94 \u8be5\u5c06\u8863\u670d\u7a7f\u5f97\u7d27\u7d27\u7684\uff0c\u7b49\u7740\u7537\u4eba\u53bb\u52fe\u5f15\u5979\u624d\u662f\uff0c\u5426\u5219\u7537\u4eba\u5c31\u4f1a\u89c9\u5f97\u65e0\u8da3\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u5df2\u62ac\u8d77\uff0c\u5200\u950b\u81ea\u5979\u8116\u5b50\u4e0a\u8f7b\u8f7b\u5212\u4e86\u8fc7\u53bb\uff0c\u9c9c\u8840\u4e00\u70b9\u6e85\u5728\u5979\u767d\u7389\u4e00\u822c\u7684\u80f8\u819b\u4e0a\uff0c\u5c31 \u8c61\u662f\u96ea\u5730\u4e0a\u4e00\u6735\u6735\u9c9c\u8273\u7684\u6885\u82b1\u3002 \u3000\u3000\u5979\u5df2\u5b8c\u5168\u8d6b\u5446\u4e86\uff0c\u67d4\u8f6f\u7684\u8eaf\u4f53\u5df2\u50f5\u786c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u6709\u90a3\u4e48\u5927\u7684\u81ea\u4fe1\uff0c\u8fd8\u8ba4\u4e3a\u6211\u4e0d\u6562\u6740\u4f60\u5417\uff1f\u201d \u3000\u3000\u5200\u950b\uff0c\u4ecd\u7136\u505c\u7559\u5728\u5979\u7684\u8116\u5b50\u4e0a\u3002 \u3000\u3000\u5979\u7684\u5634\u5507\u98a4\u6296\u7740\uff0c\u90a3\u91cc\u8fd8\u8bf4\u5f97\u51fa\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u4ee5\u540e\u8bb0\u4f4f\u51e0\u4ef6\u4e8b\uff0c\u7b2c\u4e00\uff0c\u7537\u4eba\u90fd\u4e0d\u559c\u6b22\u88ab\u52a8\u7684\uff0c\u7b2c\u4e8c \uff0c\u4f60\u5e76\u6ca1\u6709\u81ea\u5df1\u60f3\u8c61\u4e2d\u90a3\u4e48\u6f02\u4eae\u3002\u201d \u3000\u3000\u5c11\u5973\u7d27\u54ac\u7740\u5634\u5507\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u5df2\u7ecf\u670d\u4e86\u4f60\u4e86\uff0c\u6c42\u6c42\u4f60\u5c06\u5200\u62ff\u5f00\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u8fd8\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u6709\u5f88\u591a\u7537\u4eba\u90fd\u4f1a\u9001\u7ed9\u4f60\uff0c\u6240\u4ee5\u4f60\u7edd\u4e0d\u4f1a\u8d2a\u56fe\u94b1\u8d22\uff0c\u4f60\u81ea\u5df1 \u662f\u4e2a\u5973\u4eba\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u662f\u4e3a\u4e86\u8d2a\u56fe\u7f8e\u8272\uff0c\u90a3\u4e48\u4f60\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u624d\u4e0d\u60dc\u727a\u7272\u4e00\u5207\uff0c\u4e00\u5fc3\u60f3 \u5f97\u5230\u8fd9\u91d1\u4e1d\u7532\u5462\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6211\u65e9\u5df2\u8bf4\u8fc7\u4e86\uff0c\u8d8a\u5f97\u4e0d\u5230\u7684\u4e1c\u897f\uff0c\u6211\u8d8a\u60f3\u8981\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u5c06\u5200\u4ece\u4f60\u7684\u8116\u5b50\u4e0a\u62ff\u5f00\uff0c\u4f60\u96be\u9053\u5c31\u4e0d\u80fd\u5c06\u4f60\u7684\u8116 \u5b50\u4ece\u6211\u7684\u5200\u4e0a\u62ff\u5f00\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u7acb\u523b\u4ece\u4ed6\u6000\u4e2d\u7a9c\u4e86\u51fa\u53bb\uff0c\u5c31\u8c61\u662f\u4e00\u53ea\u88ab\u4e3b\u4eba\u5f04\u75bc\u4e86\u7684\u732b\u3002 \u3000\u3000\u4f46\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u5ffd\u53c8\u7b11\u4e86\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\uff0c\u4f60\u8fd8\u662f\u4e0d\u5fcd\u6740\u6211\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8f7b\u629a\u7740\u624b\u91cc\u7684\u5200\u950b\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u4f60\u82e5\u8fd8\u4e0d\u8d70\uff0c\u8fd9\u67c4\u5200\u5c31\u4f1a\u63d2\u5728\u4f60\u8116\u5b50\u91cc \uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5c11\u5973\u6ca1\u6709\u518d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5979\u54ac\u7740\u7259\uff0c\u652b\u8d77\u4e86\u8863\u670d\uff0c\u732b\u4e00\u822c\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5979\u6076\u6bd2\u5730\u9a82\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u4f60\u4e0d\u662f\u7537\u4eba\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u4e2a\u4eba\uff01\u6839\u672c\u5c31\u4e0d\u4e2d \u7528\uff0c\u96be\u602a\u4f60\u672a\u8fc7\u95e8\u7684\u59bb\u5b50\u4f1a\u8ddf\u4f60\u6700\u597d\u7684\u670b\u53cb\u8dd1\u4e86\uff0c\u6211\u73b0\u5728\u624d\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u5927\u5730\u79ef\u96ea\uff0c\u96ea\u5149\u6620\u7167\u4e0b\uff0c\u5916\u9762\u4eae\u5f97\u5f88\uff0c\u4f46\u8fd9\u53a8\u623f\u5374\u5e7d\u6697\u5f97\u5982\u540c\u575f\u5893\uff0c\u4ee4\u4eba\u518d\u4e5f\u4e0d\u613f\u505c\u7559 \u7247\u523b\u3002 \u3000\u3000\u53ef\u662f\u674e\u5bfb\u6b22\u5374\u4ecd\u7136\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u8fde\u59ff\u52bf\u90fd\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u60b2\u54c0\u548c\u75db\u82e6\uff0c\u90a3\u5c11\u5973\u6240\u8bf4\u7684\u8bdd\uff0c\u5c31\u8c61\u662f\u4e00\u6839\u6839\u9488\uff0c\u6df1\u6df1\u5730\u523a\u5165\u4e86\u4ed6\u7684\u5fc3 \u3002 \u3000\u3000\u672a\u6765\u7684\u59bb\u5b50\u2026\u2026\u6700\u597d\u7684\u670b\u53cb\u2026\u2026 \u7b2c\u4e94\u7ae0\u3000\u98ce\u96ea\u591c\u8ffd\u4eba>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u7ae0\u3000\u98ce\u96ea\u591c\u8ffd\u4eba \u3000\u3000\u674e\u5bfb\u6b22\u6293\u8d77\u9152\u58f6\uff0c\u5c06\u5269\u4e0b\u6765\u7684\u9152\u5168\u90fd\u704c\u4e86\u4e0b\u53bb\uff0c\u7136\u540e\u5c31\u4e0d\u505c\u5730\u54b3\u55fd\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u53c8\u73b0\u51fa \u51c4\u8273\u7684\u8840\u7ea2\u8272\u3002\u4ed6\u624b\u629a\u7740\u80f8\u819b\uff0c\u51c4\u7136\u81ea\u8bed\u9053\uff1a\u201c\u5578\u4e91\uff0c\u8bd7\u97f3\uff0c\u6211\u7edd\u4e0d\u602a\u4f60\u4eec\uff0c\u65e0\u8bba\u522b\u4eba\u600e\u4e48 \u8bf4\uff0c\u6211\u90fd\u4e0d\u4f1a\u602a\u4f60\u4eec\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u4eec\u5e76\u6ca1\u6709\u9519\uff0c\u6240\u6709\u7684\u9519\uff0c\u90fd\u662f\u6211\u4e00\u4e2a\u4eba\u9020\u6210\u7684\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u6728\u677f\u95e8\u7830\u7684\u4e00\u54cd\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u81ea\u95e8\u5916\u722c\u4e86\u8fdb\u6765\uff0c\u4ed6\u770b\u6765\u5c31\u8c61\u662f\u4e2a\u8089\u7403\u4f3c\u7684\uff0c\u8179\u5927\u5982\u9f13\uff0c\u5168\u8eab\u90fd\u6324\u7740\u80a5\u8089\uff0c\u5168\u8eab \u90fd\u6cbe\u67d3\u7740\u6ce5\u57a2\uff0c\u5934\u53d1\u548c\u80e1\u5b50\u66f4\u4e71\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u5c31\u8c61\u662f\u5df2\u6709\u8bb8\u591a\u5e74\u6ca1\u6709\u6d17\u8fc7\u6fa1\uff0c\u8fdc\u8fdc\u5c31\u53ef\u4ee5\u55c5 \u5230\u4e00\u9635\u9635\u9178\u81ed\u6c14\u3002 \u3000\u3000\u4ed6\u722c\u7740\u6eda\u4e86\u8fdb\u6765\uff0c\u56e0\u4e3a\u4ed6\u4e24\u6761\u817f\u5df2\u88ab\u9f50\u6839\u65a9\u65ad\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u670b\u53cb\u82e5\u662f\u6765\u8981\u996d\u7684\uff0c\u53ef\u771f\u662f\u9009\u9519\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u6839\u672c\u8c61\u662f\u6ca1\u542c\u89c1\uff0c\u4ed6\u867d\u7136\u81c3\u80bf\u800c\u6b8b\u5e9f\uff0c\u884c\u52a8\u5374\u5e76\u4e0d\u5446\u7b28\uff0c\u53cc\u624b\u4e00\u6309\uff0c\u8eab\u5b50\u4e00\u6eda\uff0c\u5df2 \u5230\u4e86\u7089\u7076\u524d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8bb6\u7136\u9053\uff1a\u201c\u9601\u4e0b\u96be\u9053\u4e5f\u662f\u4e3a\u4e86\u8fd9\u91d1\u4e1d\u7532\u6765\u7684\u4e48\u3002\u201d \u3000\u3000\u8fd9\u4eba\u4e24\u53ea\u624b\u53c8\u4e00\u6309\uff0c\u86e4\u87c6\u822c\u8df3\u4e0a\u4e86\u7089\u7076\uff0c\u5c38\u4f53\u8fd8\u5728\u8fd9\u5927\u94c1\u9505\u91cc\uff0c\u91d1\u4e1d\u7532\u4e5f\u8fd8\u5728\u8fd9\u5c38\u4f53\u4e0a \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u201c\u5728\u4e0b\u624b\u91cc\u7684\u5200\u5e76\u975e\u6740\u4e0d\u6b7b\u4eba\u7684\uff0c\u9601\u4e0b\u82e5\u8fd8\u4e0d\u4f4f\u624b\uff0c\u8fd9\u91cc\u53ea\u6015\u516d\u53c8\u591a\u4e00 \u4e2a\u6b7b\u4eba\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u7adf\u8fd8\u662f\u4e0d\u7406\u4ed6\uff0c\u4e03\u624b\u516b\u811a\uff0c\u5c31\u5c06\u91d1\u4e1d\u7532\u5265\u4e86\u4e0b\u6765\uff0c\u770b\u6765\u90a3\u53ea\u4e0d\u8fc7\u662f\u4ef6\u91d1\u8272\u7684\u9a6c\u7532\u800c \u5df2\uff0c\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u795e\u5947\u4e4b\u5904\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u674e\u5bfb\u6b22\u7adf\u8fd8\u662f\u5b89\u5750\u4e0d\u52a8\uff0c\u624b\u91cc\u7684\u98de\u5200\u4e5f\u672a\u53d1\u51fa\uff0c\u53ea\u662f\u77aa\u7740\u8fd9\u602a\u4eba\uff0c\u76ee\u4e2d\u53cd\u800c \u9732\u51fa\u4e86\u60ca\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u602a\u4eba\u4e24\u624b\u7d27\u62b1\u7740\u91d1\u4e1d\u7532\uff0c\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u9e6c\u868c\u76f8\u4e89\uff0c\u9c7c\u7fc1\u5f97\u5229\uff0c\u60f3\u4e0d\u5230\u8fd9\u5b9d\u8d1d\u7adf \u5230\u6211\u624b\u91cc\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u201c\u5728\u4e0b\u4eba\u8fd8\u5728\u8fd9\u91cc\uff0c\u5200\u8fd8\u5728\u624b\u4e2d\uff0c\u9601\u4e0b\u8bf4\u8fd9\u8bdd\uff0c\u53ea\u6015\u8fd8\u592a\u65e9\u4e86\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u53c8\u86e4\u87c6\u822c\u8df3\u4e86\u4e0b\u6765\uff0c\u6eda\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u671b\u7740\u674e\u5bfb\u6b22\u54a7\u5634\u4e00\u7b11\uff0c\u9732\u51fa\u4e86\u6ee1\u5634\u53d1\u9ec4\u7684 \u7259\u9f7f\u3002 \u3000\u3000\u4ed6\u683c\u683c\u7684\u7b11\u7740\u9053\uff1a\u201c\u4f60\u7684\u5200\u65e2\u7136\u5728\u624b\u91cc\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\u6211\u5462\u3002\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\u4f60\u98de\u5200 \u4e00\u51fa\uff0c\u6211\u8fd9\u6b8b\u5e9f\u662f\u4e07\u4e07\u8eb2\u4e0d\u5f00\u7684\u5440\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u89c9\u5f97\u4f60\u5f88\u53ef\u7231\uff0c\u6240\u4ee5\u4e0d\u5fcd\u6740\u4f60\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u5927\u7b11\u4e86\u51e0\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u613f\u8bf4\uff0c\u6211\u5c31\u66ff\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u63a5\u9053\uff1a\u201c\u522b\u4eba\u90fd\u4ee5\u4e3a\u4f60\u6ca1\u6709\u4e2d\u6bd2\uff0c\u4f46\u6211\u5374\u77e5\u9053\u4f60\u662f\u4e2d\u6bd2\u4e86\uff0c\u53ea\u4e0d\u8fc7\u4f60\u7684\u786e\u5f88\u6c89 \u5f97\u4f4f\u6c14\uff0c\u6240\u4ee5\u522b\u4eba\u90fd\u4e0a\u4e86\u4f60\u7684\u5f53\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u795e\u8272\u4e0d\u52a8\uff0c\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u9053\uff1a\u201c\u4f46\u4f60\u5374\u4f11\u60f3\u8981\u6211\u4e5f\u4e0a\u5f53\uff0c\u53ea\u56e0\u4e3a\u6211\u77e5\u9053\u4e0b\u5728\u9152\u4e2d\u7684\u6bd2\u662f\u65e2\u65e0\u8272\uff0c\u4e5f\u65e0\u5473\u7684 \uff0c\u4f60\u7684\u9f3b\u5b50\u5c31\u7b97\u6bd4\u72d7\u8fd8\u7075\uff0c\u4e5f\u4f11\u60f3\u95fb\u5f97\u51fa\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u671b\u4e86\u4ed6\u5f88\u4e45\uff0c\u624d\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u771f\u7684\u77e5\u9053\u5f97\u8fd9\u4e48\u6e05\u695a\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u683c\u683c\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u5f97\u5f88\u6e05\u695a\uff0c\u56e0\u4e3a\u6bd2\u5c31\u662f\u6211\u4e0b\u7684\u3002\u4f60\u4e2d\u6bd2\u6ca1\u6709\uff0c\u6211\u4e5f\u770b\u5f97 \u51fa\uff0c\u4f60\u53ef\u4ee5\u9a97\u8fc7\u4e16\u4e0a\u6240\u6709\u7684\u4eba\uff0c\u4f46\u5374\u9a97\u4e0d\u8fc7\u6211\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8138\u8272\u867d\u8fd8\u6ca1\u6709\u53d8\uff0c\u4f46\u773c\u89d2\u7684\u808c\u8089\u5df2\u5728\u4e0d\u505c\u5730\u8df3\u52a8\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u53f9\u606f\u4e86\u4e00 \u58f0\uff0c\u9053\uff1a\u201c\u4e00\u5929\u8fd8\u6ca1\u6709\u8fc7\u5b8c\uff0c\u6211\u9047\u89c1\u51fa\u4eba\u610f\u5916\u7684\u4e8b\u5df2\u6709\u516d\u4e03\u4ef6\u4e86\uff0c\u770b\u6765\u6211\u4eca\u5929\u7684\u8fd0\u6c14\u5b9e\u5728\u4e0d \u9519\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u96be\u9053\u4e0d\u60f3\u77e5\u9053\u662f\u6b7b\u5728\u4ec0\u4e48\u4eba\u624b\u4e0a\u7684\u5417\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6b63\u60f3\u8bf7\u6559\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u535a\u95fb\u5e7f\u89c1\uff0c\u603b\u8be5\u77e5\u9053\u6c5f\u6e56\u4e2d\u6709\u4e03\u4e2a\u6700\u5351\u9119\u65e0\u803b\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5931\u58f0\u9053\uff1a\u201c\u4e03\u5999\u4eba\u2026\u2026\u201d \u3000\u3000\u8fd9\u602a\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u8fd9\u4e03\u5999\u4eba\u5f53\u771f\u662f\u7537\u76d7\u5973\u5a3c\uff0c\u65e0\u803b\u4e4b\u5c24\uff0c\u522b\u7684\u6b66\u529f\u4ed6 \u4eec\u5b66\u4e0d\u597d\uff0c\u4f46\u8ff7\u9999\u4e0b\u6bd2\uff0c\u5077\u9e21\u6478\u72d7\uff0c\u8bf1\u5978\u62d0\u9a97\uff0c\u8fd9\u4e00\u7c7b\u7684\u529f\u592b\u8fd9\u6c5f\u6e56\u4e2d\u5374\u53ef\u7b97\u662f\u9996\u8d8b\u4e00\u6307\uff0c \u72ec\u6b65\u5929\u4e0b\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5f20\u5927\u4e86\u773c\u775b\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u96be\u9053\u4e5f\u662f\u4e03\u5999\u4eba\u5176\u4e2d\u4e4b\u4e00\u4e48\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u9053\uff1a\u201c\u4e03\u5999\u4eba\u4e2d\u53c8\u6709\u4e2a\u6700\u5351\u9119\u65e0\u803b\u7684\u4eba\uff0c\u5c31\u53eb\u505a\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5999\u90ce\u541b\u82b1\u8702\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u7b11\u9053\uff1a\u201c\u9519\u4e86\u4e00\u70b9\uff0c\u4ed6\u7684\u5168\u540d\u662f\u2018\u9ed1\u5fc3\u5999\u90ce\u541b\u2019\uff0c\u6b64\u4eba\u4e0d\u5b66\u65e0\u672f\uff0c\u8fde\u91c7\u82b1\u90fd\u4e0d\u5927 \u6562\uff0c\u53ea\u4f1a\u52fe\u5f15\u826f\u5bb6\u5987\u5973\u9a97\u8d22\u9a97\u8272\uff0c\u4f46\u82e5\u8bba\u8d77\u4e0b\u6bd2\u7684\u529f\u592b\u6765\uff0c\u6709\u65f6\u8fde\u90a3\u4f4d\u4e94\u6bd2\u6781\u4e50\u7ae5\u5b50\u90fd\u8981\u900a \u4ed6\u4e00\u7b79\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u9601\u4e0b\u5bf9\u6b64\u4eba\u5012\u6e05\u695a\u5f97\u5f88\u3002\u201d \u3000\u3000\u8fd9\u602a\u4eba\u7b11\u563b\u563b\u9053\uff1a\u201c\u6211\u5f53\u7136\u5bf9\u6b64\u4eba\u6e05\u695a\u5f97\u5f88\uff0c\u56e0\u4e3a\u6211\u5c31\u662f\u4ed6\uff0c\u4ed6\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u771f\u7684\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u82b1\u8702\u5927\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u5f88\u5947\u602a\u5417\u3002\u5999\u90ce\u541b\u600e\u4f1a\u662f\u4e2a\u5927\u8089\u7403\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u4f60\u9601\u4e0b\u8fd9\u6837\u7684\u4eba\u82e5\u4e5f\u80fd\u52fe\u5f15\u826f\u5bb6\u5987\u5973\uff0c\u90a3\u4e9b\u5973\u4eba\u53ea\u6015\u662f\u778e\u5b50\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u4f60\u53c8\u9519\u4e86\uff0c\u6211\u52fe\u5f15\u7684\u4eba\u975e\u4f46\u4e0d\u662f\u778e\u5b50\uff0c\u800c\u4e14\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u7f8e\u5f97\u5f88\uff0c\u53ea\u4e0d\u8fc7 \u4e00\u4e2a\u4eba\u82e5\u88ab\u65a9\u65ad\u4e86\u817f\u5173\u5728\u5730\u7a96\u91cc\uff0c\u6bcf\u5929\u53ea\u5582\u4ed6\u4e00\u7897\u4e0d\u52a0\u76d0\u7684\u732a\u6cb9\u4f34\u996d\uff0c\u4ed6\u672c\u6765\u5c31\u7b97\u662f\u6f58\u5b89\uff0c \u51e0\u5e74\u540e\u4e5f\u8981\u53d8\u6210\u8089\u7403\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u96be\u9053\u662f\u2018\u7d2b\u9762\u4e8c\u90ce\u2019\u592b\u5987\u4e0b\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u82b1\u8702\u6c89\u541f\u4e86\u534a\u664c\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u521a\u624d\u8bb2\u4e86\u6545\u4e8b\u7ed9\u4f60\u542c\uff0c\u73b0\u5728\u6211\u4e5f\u8bb2\u4e00\u4e2a\uff0c\u53ea\u4e0d\u8fc7\u6211\u8fd9\u6545\u4e8b \u6bd4\u4ed6\u66f2\u6298\u6709\u8da3\u591a\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u90a3\u5e74\u6211\u8fd0\u6c14\u4e0d\u597d\uff0c\u9b3c\u8ff7\u4e86\u773c\uff0c\u7adf\u53bb\u52fe\u5f15\u5927\u80e1\u5b50\u7684\u8001\u5a46\uff0c\u66f4\u5012\u9709\u7684\u662f\uff0c\u5c45\u7136\u8fd8 \u5f04\u51fa\u4e2a\u5b69\u5b50\u6765\uff0c\u6240\u4ee5\u5979\u5c31\u975e\u8ddf\u6211\u8dd1\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8bb6\u7136\u9053\uff1a\u201c\u539f\u6765\u7d2b\u9762\u4e8c\u90ce\u8bf4\u7684\u90a3\u4eba\u5c31\u662f\u4f60\uff0c\u4ed6\u5c31\u662f\u66ff\u4f60\u80cc\u9ed1\u9505\u7684\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u4ed6\u53ea\u8bf4\u9519\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u5c06\u5979\u5377\u5e26\u51fa\u6765\u7684\u73e0\u5b9d\u62d0\u8d70\uff0c\u5c31\u7b97\u6211\u8fd9\u4e48\u60f3\uff0c\u4e5f\u4e0d\u884c\uff0c\u56e0\u4e3a\u8fd9\u5973\u4eba\u6bd4 \u9b3c\u8fd8\u7cbe\uff0c\u6211\u6839\u672c\u5c31\u6ca1\u673a\u4f1a\u4e0b\u624b\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u90a3\u65f6\u5927\u80e1\u5b50\u5df2\u53d1\u89c9\u4e86\u6b64\u4e8b\uff0c\u8ffd\u8e2a\u751a\u6025\uff0c\u6211\u8fd9\u4eba\u80c6\u5b50\u6700\u5c0f\uff0c \u5c31\u60f3\u627e\u4e2a\u4eba\u66ff\u6211\u80cc\u9ed1\u9505\uff0c\u6240\u4ee5\u6211\u5c31\u8981\u5c0f\u8537\u8587\u53bb\u52fe\u5f15\u7d2b\u9762\u4e8c\u5eca\uff0c\u5979\u672c\u6765\u4e0d\u80af\uff0c\u8bf4\u4ed6\u7684\u8138\u4e0d\u767d\uff0c \u5230\u540e\u6765\u624d\u603b\u7b97\u88ab\u6211\u8bf4\u52a8\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e24\u4eba\u7adf\u662f\u4e32\u901a\u597d\u7684\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u90a3\u65f6\u6211\u82e5\u7d22\u6027\u5c06\u8ba1\u5c31\u8ba1\uff0c\u7529\u624b\u4e00\u8d70\uff0c\u5012\u4e5f\u6ca1\u4e8b\u4e86\uff0c\u53ef\u662f\u5c0f\u8537\u8587\u4ece\u5927\u80e1\u5b50\u90a3\u91cc \u5377\u5e26\u51fa\u7684\u73e0\u5b9d\u5b9e\u5728\u4e0d\u5c11\uff0c\u6211\u53c8\u820d\u4e0d\u5f97\uff0c\u6240\u4ee5\u6211\u5c31\u8ddf\u5979\u7ea6\u597d\uff0c\u7b49\u5230\u8fd9\u4ef6\u4e8b\u7a0d\u5fae\u5e73\u9759\u4e9b\u7684\u65f6\u5019\uff0c \u6211\u518d\u6765\u627e\u5979\uff0c\u5c06\u7d2b\u9762\u4e8c\u90ce\u8e22\u5f00\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u4f46\u6211\u5374\u5fd8\u4e86\u5929\u4e0b\u6ca1\u6709\u4e0d\u53d8\u5fc3\u7684\u5973\u4eba\uff0c\u5979\u8ddf\u7d2b\u9762\u4e8c\u90ce\u671d\u5915\u76f8 \u5904\uff0c\u5c45\u7136\u52a8\u4e86\u771f\u60c5\uff0c\u7b49\u6211\u518d\u6765\u627e\u5979\u65f6\uff0c\u4ed6\u4eec\u4e24\u4eba\u7adf\u4e00\u9f50\u52a8\u624b\uff0c\u5c06\u6211\u51fb\u5012\uff0c\u53c8\u65a9\u65ad\u6211\u4e24\u6761\u817f\uff0c \u8ba9\u6211\u53d7\u4e86\u5341\u51e0\u5e74\u7684\u6d3b\u7f6a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u201c\u5979\u4e3a\u4f55\u4e0d\u7d22\u6027\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u82b1\u8702\u82e6\u7b11\u9053\uff1a\u201c\u6211\u82e5\u4e86\u89e3\u5973\u4eba\u7684\u5fc3\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6b21\u4ed6\u53f9\u6c14\u5f97\u66f4\u957f\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ee5\u524d\u6211\u603b\u4ee5\u4e3a\u81ea\u5df1\u5f88\u4e86\u89e3\u5973\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u6709\u8fd9\u79cd\u62a5\u5e94\uff0c \u4e00\u4e2a\u7537\u4eba\u82e5\u662f\u4ee5\u4e3a\u81ea\u5df1\u4e86\u89e3\u5973\u4eba\uff0c\u4ed6\u65e0\u8bba\u53d7\u4ec0\u4e48\u7f6a\u90fd\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u7684\u786e\u6bd4\u521a\u624d\u90a3\u6545\u4e8b\u6709\u8da3\u591a\u4e86\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u6700\u6709\u8da3\u7684\u4e00\u4ef6\u4e8b\u4f60\u8fd8\u672a\u542c\u5230\u54e9\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u6211\u7684\u6bd2\uff0c\u975e\u4f46\u7528\u4e0d\u4e86\u529b\uff0c\u800c\u4e14\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5c31\u975e\u6b7b\u4e0d\u53ef\uff0c\u6240\u4ee5\u6211\u73b0 \u5728\u7edd\u4e0d\u6740\u4f60\uff0c\u8ba9\u4f60\u5750\u5728\u8fd9\u91cc\u6162\u6162\u4eab\u53d7\u7b49\u6b7b\u7684\u6ecb\u5473\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u5012\u7528\u4e0d\u7740\uff0c\u7b49\u6b7b\u7684\u6ecb\u5473\uff0c\u6211\u4e5f\u4eab\u53d7\u8fc7\u8bb8\u591a\u6b21\u4e86\u3002\u201d \u3000\u3000\u82b1\u8702\u72de\u7b11\u9053\uff1a\u201c\u4f46\u6211\u5374\u53ef\u4ee5\u4fdd\u8bc1\u8fd9\u5fc5\u5b9a\u662f\u6700\u540e\u4e00\u6b21\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u9601\u4e0b\u5c31\u8bf7\u4fbf\u5427\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u5916\u9762\u98ce\u96ea\u4ea4\u52a0\uff0c\u51b0\u96ea\u904d\u5730 \uff0c\u9601\u4e0b\u8fd9\u6837\u5b50\uff0c\u80fd\u8d70\u5f97\u8fdc\u4e48\u3002\u201d \u3000\u3000\u82b1\u8702\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u52b3\u9601\u4e0b\u8d39\u5fc3\uff0c\u6ca1\u6709\u817f\u7684\u4eba\uff0c\u4e5f\u53ef\u4ee5\u9a91\u9a6c\u7684\uff0c\u6211\u5df2\u542c\u5230\u5916\u9762\u7684\u9a6c\u5636\uff0c\u800c \u4e14\u4e2d\u6c14\u5f88\u8db3\uff0c\u60f3\u5fc5\u662f\u51e0\u5339\u597d\u9a6c\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u5f80\u5916\u9762\u722c\u4e86\u51fa\u53bb\uff0c\u8fd8\u6325\u7740\u624b\u7b11\u9053\uff1a\u201c\u518d\u89c1\u518d\u89c1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5fae\u7b11\u9053\uff1a\u201c\u6162\u8d70\u6162\u8d70\uff0c\u6055\u5728\u4e0b\u4e0d\u80fd\u8fdc\u9001\u4e86\uff0c\u5b9e\u5728\u62b1\u6b49\u5f97\u5f88\u3002\u201d \u3000\u3000\u5916\u9762\u9a6c\u65af\u4e0d\u7edd\uff0c\u8e44\u58f0\u6e10\u6e10\u8fdc\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9759\u9759\u7684\u5750\u5728\u90a3\u91cc\uff0c\u671b\u7740\u684c\u4e0a\u7684\u9152\u58f6\u3002 \u3000\u3000\u4e00\u58f6\u9152\u5df2\u7a7a\u4e86\uff0c\u4ee4\u4e00\u58f6\u8fd8\u6709\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ff\u8d77\u9152\u58f6\u55c5\u4e86\u55c5\uff0c\u53c8\u5c1d\u4e86\u4e00\u53e3\uff0c\u5583\u5583\u9053\uff1a\u201c\u679c\u7136\u662f\u65e0\u8272\u65e0\u5473\uff0c\u6b64\u541b\u4e0b\u6bd2\u7684\u672c\u4e8b\u7684 \u786e\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u53c8\u559d\u4e86\u4e00\u5927\u53e3\uff0c\u95ed\u8d77\u773c\u775b\u9053\uff1a\u201c\u8fd9\u9152\u4e5f\u7684\u786e\u4e0d\u9519\uff0c\u559d\u4e00\u676f\u4e5f\u662f\u6b7b\uff0c\u559d\u4e00\u58f6\u4e5f\u662f\u6b7b\uff0c\u6211 \u4e3a\u4f55\u4e0d\u591a\u559d\u4e9b\uff0c\u4e5f\u514d\u5f97\u7cdf\u8e4b\u4e86\u5982\u6b64\u597d\u9152\u3002\u201d \u3000\u3000\u4ed6\u7adf\u771f\u7684\u5c06\u4e00\u58f6\u6bd2\u9152\u5168\u90fd\u559d\u4e86\u4e0b\u53bb\u3002\u53c8\u5583\u5583\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u554a\u674e\u5bfb\u6b22\uff0c\u4f60\u65e9\u5c31\u8be5\u6b7b\u7684\uff0c\u6b7b \u53c8\u4f55\u59a8\u3002\u4f46\u81f3\u5c11\u4f60\u603b\u4e0d\u80fd\u6b7b\u5728\u53a8\u623f\u91cc\uff0c\u548c\u8fd9\u4e9b\u6b7b\u4eba\u5728\u4e00\u8d77\u5440\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c\u6447\u6447\u6643\u6643\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u96ea\u5730\u4e0a\u8e44\u5370\u4ea4\u9519\uff0c\u76f4\u5954\u4e1c\u5357\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9009\u4e86\u4e00\u5757\u6700\u5e72\u51c0\u7684\u96ea\u5730\uff0c\u76d8\u819d\u5750\u4e86\u4e0b\u6765\uff0c\u53c8\u81ea\u6000\u4e2d\u6478\u51fa\u90a3\u4e2a\u8fd8\u6ca1\u6709\u523b\u597d\u7684\u4eba\u50cf\u3002 \u3000\u3000\u8fd9\u4eba\u50cf\u5df2\u7a0d\u5177\u8f6e\u5ed3\u4e86\uff0c\u4e00\u53cc\u773c\u775b\u4f3c\u4e4e\u6b63\u5728\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u7709\u68a2\u773c\u89d2\uff0c\u4f3c\u4e4e\u5e26\u7740\u6de1\u6de1\u7684\u5fe7 \u90c1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u770b\u7740\u6211\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4e0d\u53ef\u6551\u836f\u7684\u6d6a\u5b50\uff0c\u9152\u9b3c\uff0c\u4f60\u5ac1\u7ed9 \u5578\u4e91\u662f\u5bf9\u7684\uff0c\u9519\u7684\u53ea\u662f\u6211\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u53bb\u523b\uff0c\u60f3\u5b8c\u6210\u8fd9\u4eba\u50cf\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u624b\u5df2\u4e0d\u7a33\uff0c\u5df2\u5168\u65e0\u529b\u6c14\uff0c\u950b\u5229\u7684\u5200\u7adf\u8fde\u6728\u5934\u90fd\u523b\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u5929\u6c14\u5e7d\u6697\uff0c\u82cd\u7a79\u4f4e\u5782\uff0c\u53c8\u5728\u4e0b\u96ea\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f0f\u5728\u96ea\u5730\u4e0a\u4e0d\u505c\u5730\u54b3\u55fd\uff0c\u6bcf\u4e00\u58f0\u54b3\u55fd\u90fd\u4eff\u4f5b\u662f\u5728\u547c\u5524\u3002 \u3000\u3000\u201c\u8bd7\u97f3\uff0c\u8bd7\u97f3\u2026\u2026\u201d \u3000\u3000\u8bd7\u97f3\u542c\u5f97\u5230\u4e48\u3002 \u3000\u3000\u8bd7\u97f3\u7edd\u4e0d\u4f1a\u542c\u5230\u7684\uff0c\u4f46\u5374\u6709\u4eba\u542c\u5230\u4e86\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u80cc\u8d1f\u7740\u674e\u5bfb\u6b22\uff0c\u5728\u96ea\u5730\u4e0a\u8ffd\u8e2a\u7740\u8e44\u5370\u72c2\u5954\u3002 \u3000\u3000\u201c\u53ea\u6709\u5728\u4e24\u4e2a\u65f6\u8fb0\u5185\uff0c\u627e\u5230\u4e00\u4e2a\u53cc\u817f\u88ab\u65a9\u65ad\uff0c\u5c31\u8c61\u8089\u7403\u4e00\u822c\u7684\u4eba\uff0c\u6211\u4e5f\u8bb8\u8fd8\u6709\u4e00\u7ebf\u751f\u673a \u3002\u56e0\u4e3a\u4e0b\u6bd2\u7684\u4eba\u5fc5\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u7740\u662f\u674e\u5bfb\u6b22\u6240\u80fd\u8bf4\u51fa\u7684\u6700\u540e\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u51e0\u4e4e\u5c06\u6bcf\u4e00\u5206\u6f5c\u529b\u90fd\u4f7f\u4e86\u51fa\u6765\uff0c\u773c\u6cea\u5df2\u5728\u4ed6\u773c\u7736\u4e0b\u51dd\u7ed3\u6210\u51b0\u7c92\uff0c\u5bd2\u98ce\u8fce\u9762\u62ec\u6765 \uff0c\u5c31\u8c61\u662f\u5200\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5bd2\u98ce\u4e2d\u4f20\u6765\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u9762\u8272\u53d8\u4e86\uff0c\u5fae\u4e00\u8fdf\u7591\uff0c\u5168\u529b\u5411\u60e8\u547c\u4f20\u6765\u7684\u65b9\u5411\u5954\u4e86\u8fc7\u53bb\uff0c\u4ed6\u9996\u5148\u53d1\u73b0\u79ef\u96ea\u7684\u677e \u6797\u5916\u5012\u7740\u4e00\u5339\u9a6c\u3002 \u3000\u3000\u4ed6\u7a9c\u5165\u677e\u6797\uff0c\u6574\u4e2a\u4eba\u5c31\u5ffd\u7136\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u603b\u7b97\u627e\u5230\u5999\u90ce\u541b\u82b1\u8702\u4e86\uff0c\u53ef\u662f\u4ed6\u627e\u5230\u7684\u53ea\u662f\u82b1\u8702\u7684\u5c38\u4f53\u3002 \u3000\u3000\u82b1\u8702\u7684\u4eba\u5df2\u53d8\u5f97\u8c61\u662f\u4e2a\u523a\u3002\uff0c\u8eab\u4e0a\u9489\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u6697\u5668\uff0c\u6709\u98de\u9556\uff0c\u6709\u8896\u7bad\uff0c\u6709\u94f6\u9488\uff0c \u4e94\u8292\u73e0\uff0c\u6bd2\u84ba\u85dc\u2026\u2026 \u3000\u3000\u866c\u7136\u5927\u6c49\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u4f24\u611f\u4e4b\u8272\uff0c\u8fd9\u4eba\u7684\u906d\u9047\u5b9e\u5728\u592a\u60e8\uff0c\u4ed6\u88ab\u4eba\u952f\u65ad\u4e86\u4e24\u6761\u817f\u53c8\u88ab\u4eba \u8c61\u732a\u4e00\u822c\u56da\u7981\u4e86\u5341\u4f59\u5e74\uff0c\u5230\u6700\u540e\u8fd8\u88ab\u4eba\u5f53\u6210\u4e86\u4e2a\u6d3b\u9776\u5b50\u3002 \u3000\u3000\u4f46\u60f3\u5230\u8fd9\u4eba\u4e00\u6b7b\uff0c\u674e\u5bfb\u6b22\u53ea\u6015\u4e5f\u8981\u966a\u7740\u4ed6\u6b7b\uff0c\u866c\u7136\u5927\u6c49\u7684\u4f24\u5fc3\u7acb\u523b\u5c31\u53d8\u4e3a\u4e86\u60b2\u6124\u560e\u58f0\u9053 \uff1a\u201c\u5c31\u662f\u8fd9\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u62b1\u7740\u4e07\u4e00\u7684\u5e0c\u671b\uff0c\u5e0c\u671b\u6b7b\u7684\u8fd9\u4eba\u5e76\u4e0d\u662f\u674e\u5bfb\u6b22\u8981\u627e\u7684\u4eba\uff0c\u4f46\u674e\u5bfb\u6b22\u5374\u53f9\u606f\u4e86\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u9519\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u54ac\u4e86\u54ac\u7259\uff0c\u8131\u4e0b\u4e86\u8eab\u4e0a\u7684\u76ae\u8884\uff0c\u94fa\u5728\u6811\u4e0b\uff0c\u518d\u6276\u7740\u674e\u5bfb\u6b22\u5750\u4e86\u4e0b\u6765\uff0c\u52c9\u5f3a\u7b11\u9053 \uff1a\u201c\u89e3\u836f\u4e5f\u8bb8\u5c31\u5728\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u4e00\u6b7b\u53cd\u800c\u7701\u4e8b\u4e86\uff0c\u6211\u53bb\u627e\u627e\u770b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\uff0c\u6697\u5668\u5927\u591a\u6709\u6bd2\uff0c\u5343\u4e07\u83ab\u8981\u5272\u7834\u4e86\u624b\u3002\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u5df2\u547d\u5728\u4fc4\u503e\uff0c\u5374\u8fd8\u662f\u4e00\u5fc3\u60e6\u8bb0\u7740\u522b\u4eba\u7684\u5b89\u5371\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u53ea\u89c9\u80f8\u4e2d\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u52c9\u5f3a\u54bd\u4e0b\u4e86\u5df2\u5feb\u593a\u7736\u800c\u51fa\u7684\u70ed\u6cea\uff0c\u4e00\u6b65\u7a9c\u5230\u4e86\u82b1\u8702\u7684 \u5c38\u4f53\u524d\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u8e72\u5728\u90a3\u8fb9\uff0c\u5306\u5fd9\u7684\u641c\u7d22\u7740\uff0c\u4f46\u8fc7\u4e86\u534a\u664c\uff0c\u4e24\u53ea\u624b\u5c31\u505c\u987f\u4e86\u4e0b\u6765\uff0c\u5374\u4e45\u4e45\u65e0\u6cd5\u7ad9\u8d77 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5589\u5934\u54fd\u54bd\uff0c\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u6211\u7edd\u4e0d\u4f1a\u6709\u8fd9\u4e48\u597d\u7684\u8fd0\u6c14\uff0c\u4ed6\u88ab\u4eba\u56da\u7981\u4e86\u5341\u4f59\u5e74\uff0c \u8eab\u4e0a\u600e\u4e48\u4f1a\u8fd8\u5e26\u7740\u89e3\u836f\u5462\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u63e1\u7d27\u62f3\u5934\uff0c\u6253\u7740\u81ea\u5df1\u7684\u8111\u888b\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u82e5\u77e5\u9053\u662f\u8c01\u6740\u4e86\u4ed6\uff0c\u5c31\u6709\u5e0c\u671b\u4e86\uff0c \u4ed6\u7684\u89e3\u836f\u4e5f\u8bb8\u5c31\u662f\u88ab\u90a3\u4eba\u641c\u8d70\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u8d77\u773c\u775b\uff0c\u6ee1\u9762\u4ff1\u662f\u843d\u5bde\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u7684\uff0c\u4e5f\u8bb8\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4e2d\u7684\u8fd9\u4e9b\u6697\u5668\u90fd\u662f\u6781\u5e38\u89c1\u7684\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u53ef\u80fd\u7528\u8fd9\u4e9b\u6697\u5668\uff0c\u4e94 \u8292\u73e0\u867d\u662f\u65b9\u5916\u4eba\u7528\u7684\uff0c\u4f46\u8fd1\u5e74\u6765\u4e5f\u5df2\u6d41\u4fd7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u4ed6\u8eab\u4e0a\u4e2d\u4e86\u8fd9\u4e48\u591a\u6697\u5668\uff0c\u663e\u7136\u4e0d\u662f\u4e00\u4e2a\u4eba\u4e0b\u7684\u624b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4ed6\u547c\u5438\u6c89\u91cd\uff0c\u7adf\u4f3c\u5df2\u7761\u7740\u4e86\uff0c\u5bf9\u522b\u4eba\u7684\u5b89\u5371\uff0c\u4ed6\u867d\u7136\u5ff5\u5ff5\u4e8e\u6000\uff0c\u5bf9\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u4ed6\u5374\u5168 \u672a\u653e\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u8fd8\u5728\u4e0d\u505c\u5730\u6572\u6253\u7740\u81ea\u5df1\u7684\u624b\uff0c\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u559c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e0b\u624b\u7684\u4eba\u662f\u8c01 \u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5954\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u9053\uff1a\u201c\u4e0b\u624b\u7684\u4eba\u53ea\u662f\u4e00\u4e2a\u4eba\uff0c\u8fd9\u5341\u4e09\u79cd\u6697\u5668\u5168\u662f\u4ed6\u4e00\u4e2a\u4eba\u53d1 \u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u4ed6\u4e2d\u7684\u8fd9\u5341\u4e09\u79cd\u6697\u5668\uff0c\u65e0\u8bba\u4efb\u4f55\u4e00\u79cd\u90fd\u53ef\u4ee5\u5236\u4ed6\u6b7b\u547d\uff0c\u4f46\u90a3\u4eba\u5374\u786c\u8981\u5c06 \u5341\u4e09\u79cd\u6697\u5668\u90fd\u9489\u5728\u4ed6\u8eab\u4e0a\u624d\u8fc7\u763e\uff0c\u8fd9\u79cd\u6b8b\u9177\u6bd2\u8fa3\u7684\u75af\u5b50\uff0c\u6c5f\u6e56\u4e2d\u90a3\u91cc\u8fd8\u627e\u5f97\u51fa\u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u6709\u4e00\u4e2a\uff0c\u5c31\u662f\u90a3\u5343\u624b\u7f57\u5239\u3002\u5999\u90ce\u541b\u5230\u5934\u6765\u8fd8\u662f\u8981\u6b7b\u5728 \u5973\u4eba\u624b\u91cc\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u62cd\u624b\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u9664\u4e86\u5343\u624b\u7f57\u5239\u5916\uff0c\u522b\u4eba\u4e5f\u65e0\u6cd5\u5c06\u5341\u4e09\u79cd\u6697\u5668\u540c\u65f6\u53d1\u51fa\u6765\u2026\u2026 \u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u770b\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u770b\u51fa\u6765\u53c8\u6709\u4ec0\u4e48\u7528\u5462\u3002\u5343\u624b\u7f57\u5239\u884c\u8e2a\u6f02\u5ffd\uff0c\u65e9\u5df2\u4e0d\u77e5 \u8d70\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6211\u4eec\u53cd\u6b63\u662f\u627e\u4e0d\u7740\u7684\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5386\u58f0\u9053\uff1a\u201c\u6211\u4eec\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u627e\u5230\u4ed6\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u627e\u4e86\uff0c\u4f60\u53ea\u8981\u627e\u4e9b\u9152\u7ed9\u6211\u559d\uff0c\u8ba9\u6211\u9676\u7136\u800c\u6b7b\uff0c\u6211\u5df2\u7ecf\u5f88\u611f \u6fc0\u4f60\uff0c\u6211\u73b0\u5728\u5df2\u5f88\u7d2f\u2026\u2026\u975e\u5e38\u7d2f\uff0c\u53ea\u60f3\u597d\u597d\u5730\u4f11\u606f\u4f11\u606f\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5657\u5730\u8dea\u4e86\u4e0b\u6765\uff0c\u70ed\u6cea\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u593a\u7736\u800c\u51fa\uff0c\u560e\u58f0\u9053\uff1a\u201c\u5c11\u7237\uff0c\u6211\u77e5\u9053\u4f60\u5df2\u5f88\u7d2f \u4e86\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u4ece\u6765\u4e5f\u6ca1\u6709\u4e00\u5929\u5feb\u4e50\u8fc7\uff0c\u60b2\u4f24\u548c\u6101\u82e6\uff0c\u7684\u786e\u6bd4\u4efb\u4f55\u4e8b\u90fd\u5bb9\u6613\u4f7f\u4eba\u89c9\u5f97\u52b3\u7d2f \u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7d27\u7d27\u63e1\u8d77\u674e\u5bfb\u6b22\u7684\u80a9\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f46\u5c11\u7237\u4f60\u7edd\u4e0d\u80fd\u6b7b\uff0c\u4f60\u4e00\u5b9a\u8981\u632f\u4f5c\u8d77\u6765\uff0c\u4f60 \u82e5\u5c31\u8fd9\u6837\u4e0d\u660e\u4e0d\u767d\u7684\u6b7b\u4e86\uff0c\u6b7b\u540e\u80cc\u8d1f\u7740\u6d6a\u5b50\uff0c\u9152\u9b3c\u7684\u6076\u540d\uff0c\u8001\u7237\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u4e5f\u4e0d\u4f1a\u7791\u76ee\u7684\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u7d27\u7d27\u95ed\u7740\u773c\u775b\uff0c\u773c\u89d2\u7684\u6cea\u73e0\u5df2\u51dd\u6210\u51b0\u73e0\u3002 \u3000\u3000\u4f46\u4ed6\u5634\u89d2\u8fd8\u662f\u5e26\u7740\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u6d6a\u5b50\uff0c\u9152\u9b3c\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u90a3\u603b\u6bd4\u90a3\u4e9b\u4f2a\u541b\u5b50\uff0c\u5047 \u9053\u5b66\u597d\u5f97\u591a\u4e86\uff0c\u662f\u5417\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u6ee1\u9762\u70ed\u6cea\uff0c\u5636\u58f0\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u5c11\u7237\u4f60\u672c\u8be5\u662f\u5929\u4e0b\u6700\u6709\u4f5c\u4e3a\u7684\u4eba\uff0c\u4f60\u7684\u597d \u5904\u8c01\u4e5f\u6bd4\u4e0d\u4e0a\uff0c\u4f60\u4e3a\u4f55\u5b9a\u8981\u5982\u6b64\u81ea\u66b4\u81ea\u5f03\uff0c\u81ea\u4f24\u81ea\u82e6\uff0c\u4e3a\u4e86\u6797\u8bd7\u97f3\u90a3\u5973\u4eba\uff0c\u8fd9\u503c\u5f97\u5417\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u5ffd\u7136\u5c04\u51fa\u4e86\u5149\u8292\uff0c\u6012\u9053\uff1a\u201c\u4f4f\u53e3\u3002\u4f60\u7adf\u7136\u53eb\u5979\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5782\u4e0b\u4e86\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u53c8\u9602\u8d77\u773c\u775b\uff0c\u53f9\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u627e\uff0c\u6211\u4eec\u5c31\u53bb\u627e\u5427\uff0c\u53ef\u662f\u5929\u5730\u832b\u832b \uff0c\u6211\u4eec\u5269\u4e0b\u7684\u65f6\u5019\u5df2\u4e0d\u591a\u4e86\uff0c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\u627e\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u4e00\u8dc3\u800c\u8d77\uff0c\u5c55\u989c\u9053\uff1a\u201c\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\uff0c\u6211\u4eec\u4e00\u5b9a\u627e\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u4ed6\u521a\u60f3\u80cc\u8d1f\u8d77\u674e\u5bfb\u6b22\uff0c\u7a81\u7136\u95f4\uff0c\u6811\u4e0a\u6709\u7247\u79ef\u96ea\u843d\u4e86\u4e0b\u6765\uff0c\u6389\u5728\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u968f\u624b\u4e00\u62c2\uff0c\u5ffd \u7136\u53d1\u73b0\u8fd9\u7247\u79ef\u96ea\u4e0a\u7adf\u51dd\u7ed3\u7740\u8840\u82b1\u2026\u2026 \u3000\u3000\u79ef\u96ea\u7684\u67af\u679d\u4e0a\uff0c\u7adf\u8fd8\u6709\u4e2a\u4eba\u2026\u2026 \u3000\u3000\u4e00\u4e2a\u6b7b\u4eba\u3002\u4e00\u4e2a\u8d64\u88f8\u88f8\u7684\u6b7b\u4eba\u3002\u5973\u4eba\u3002 \u3000\u3000\u5979\u88ab\u4eba\u585e\u5728\u6811\u6860\u91cc\uff0c\u5168\u8eab\u5df2\u51bb\u5f97\u50f5\u786c\uff0c\u4e00\u53ea\u77ed\u77db\u63d2\u5165\u4e86\u5979\u4e30\u6ee1\u7684\u80f8\u819b\uff0c\u5c06\u5979\u9489\u5728\u6811\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ed6\u4eec\u53ea\u6ce8\u610f\u5230\u96ea\u5730\u4e0a\u82b1\u8702\u7684\u5c38\u4f53\uff0c\u5168\u6ca1\u6709\u7559\u610f\u5230\u5979\uff0c\u866c\u7136\u5927\u6c49\u53cc\u81c2\u4e00\u632f\uff0c\u82cd\u9e70\u822c \u6251\u4e86\u4e0a\u53bb\uff0c\u5c06\u5979\u5378\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u5979\u8138\u4e0a\u5df2\u7ed3\u7740\u4e00\u5c42\u51b0\u971c\uff0c\u770b\u6765\u5c31\u8c61\u662f\u900f\u660e\u7684\uff0c\u4f7f\u4eba\u5b8c\u5168\u770b\u4e0d\u51fa\u5979\u7684\u5e74\u7eaa\uff0c\u53ea\u80fd\u770b\u51fa \u5979\u751f\u524d\u662f\u4e2a\u5f88\u7f8e\u7684\u5973\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60e8\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u679c\u7136\u627e\u5230\u4e86\u5979\uff0c\u8fd9\u53ea\u6015\u4e5f\u7b97\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\u5427\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u6068\u6068\u9053\uff1a\u201c\u5343\u624b\u7f57\u5239\u867d\u7136\u6bd2\u8fa3\uff0c\u4f46\u8fd9\u4eba\u6740\u4e86\u5979\u540e\uff0c\u4e3a\u4f55\u8fd8\u8981\u5265\u5149 \u5979\u7684\u8863\u670d\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u8fd9\u53ea\u602a\u5979\u7a7f\u7684\u8863\u670d\u592a\u503c\u94b1\u4e86\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u773c\u775b\u4e00\u4eae\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u636e\u8bf4\u5343\u624b\u7f57\u5239\u6700\u91cd\u8863\u7740\uff0c\u5979\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\uff0c\u90fd\u662f\u4ee5\u91d1 \u4e1d\u7ec7\u6210\u7684\uff0c\u8fd8\u7f00\u7740\u660e\u73e0\u3001\u7f8e\u7389\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u9e7f\u89d2\u82e5\u65e0\u8338\uff0c\u7f9a\u7f8a\u82e5\u65e0\u89d2\uff0c\u4e5f\u4e0d\u4f1a\u6b7b\u4e8e\u730e\u4eba\u4e4b\u624b\u4e86\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u4f46\u8fd9\u4eba\u6740\u5979\uff0c\u672c\u662f\u4e3a\u4e86\u91d1\u4e1d\u7532\uff0c\u4ed6\u5f97\u5230\u4e86\u91d1\u4e1d\u7532\u8fd9\u6837\u7684\u6b66\u6797\u5f02\u5b9d\u8fd8\u4e0d\u80af \u653e\u8fc7\u4e00\u4ef6\u8863\u670d\uff0c\u5982\u6b64\u8d2a\u5fc3\u7684\u4eba\uff0c\u4e16\u4e0a\u53ea\u6015\u4e5f\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u6709\u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u8fd9\u6b21\u866c\u7136\u5927\u6c49\u5374\u62a2\u7740\u9053\uff1a\u201c\u68fa\u6750\u91cc\u4f38\u624b\uff0c\u6b7b\u8981\u94b1\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u518d\u62d4\u8d77\u5979\u8eab\u4e0a\u8fd9\u6839\u77ed\u77db\u770b\u770b\u3002\u201d \u3000\u3000\u8fd9\u53ea\u77ed\u77db\u5236\u4f5c\u6781\u7cbe\uff0c\u4e0a\u9762\u8fd8\u9576\u7740\u5757\u7fe1\u7fe0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u65bd\u8000\u5148\u89c6\u94b1\u5982\u547d\uff0c\u6740\u4e86\u4eba\u540e\u8fde\u8863\u670d\u90fd\u8981\u5265\u8d70\uff0c\u4ed6\u4f1a\u820d\u5f97\u5c06\u5982\u6b64\u503c\u94b1\u7684\u77ed\u77db \u7559\u4e0b\u5417\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u76b1\u7709\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u7528\u5982\u6b64\u534e\u8d35\u5175\u5203\u7684\u4eba\u672c\u5c31\u4e0d\u591a\uff0c\u8fd9\u83ab\u975e\u662f\u90a3\u8d25\u5bb6\u5b50\u2018\u82b1\u82b1\u5927 \u5c11\u2019\u6f58\u5c0f\u5b89\u7559\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u4ed6\u4eec\u4e24\u4eba\u4e00\u9f50\u52a8\u7684\u624b\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u7231\u8d22\u5982\u547d\uff0c\u4e00\u4e2a\u6325\u91d1\u5982\u571f\uff0c\u5b8c\u5168\u662f\u6c34\u706b\u4e0d\u540c\u7089\uff0c\u53c8\u600e\u4f1a\u51d1 \u5728\u4e00\u8d77\u7684\u5462\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u6f58\u5927\u5c11\u662f\u6709\u540d\u7684\u6d3e\u5934\u5947\u5927\uff0c\u8863\u3001\u98df\u3001\u4f4f\u3001\u884c\uff0c\u6837\u6837\u90fd\u8981\u8bb2\u7a76\uff0c\u65bd\u8000\u5148\u8ddf \u7740\u4ed6\u8d70\uff0c\u4e0d\u4f46\u767d\u5403\u767d\u559d\uff0c\u8fd8\u53ef\u4ee5\u8ddf\u7740\u5145\u5145\u5927\u7237\uff0c\u8fd9\u79cd\u4fbf\u5b9c\u4e8b\uff0c\u65bd\u8000\u5148\u600e\u4f1a\u4e0d\u505a\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u4e00\u62cd\u5df4\u638c\uff0c\u5c55\u989c\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u529e\u4e86\uff0c\u5728\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\u91cc\uff0c\u6f58\u5927\u5c11\u7edd\u4e0d\u80af\u9a91\u5728\u9a6c \u4e0a\u6328\u51bb\uff0c\u66f4\u4e0d\u4f1a\u8d70\u8def\u4e86\uff0c\u4ed6\u4e00\u5b9a\u8981\u5750\u8f66\uff0c\u53ea\u8981\u5750\u8f66\uff0c\u6211\u4eec\u5c31\u8ffd\u5f97\u4e0a\u3002\u201d\u6797\u5916\u96ea\u5730\u4e0a\u679c\u7136\u8fd8\u53ef \u9690\u9690\u8fa8\u51fa\u8f66\u8f99\u9a6c\u8e44\u3002\u8f66\u8f6e\u4e4b\u95f4\uff0c\u7adf\u6709\u516b\u5c3a\uff0c\u4ed6\u4eec\u4e58\u7684\u663e\u7136\u662f\u8f86\u5f88\u5bbd\u655e\u7684\u8f66\u3002 \u3000\u3000\u8fd9\u79cd\u8f66\u5b50\u867d\u8212\u670d\uff0c\u5374\u4e0d\u4f1a\u8d70\u5f97\u592a\u5feb\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u7cbe\u795e\u4e00\u632f\uff0c\u653e\u8db3\u72c2\u5954\uff0c\u8fd9\u6b21\u4ed6\u8ffd\u8e2a\u5c31\u5bb9\u6613\u591a\u4e86\uff0c\u53ea\u9700\u6cbf\u7740\u5927\u9053\u800c\u884c\uff0c\u56e0\u4e3a\u516b\u5c3a \u5bbd\u7684\u5927\u8f66\u7edd\u5bf9\u8d70\u4e0d\u4e0a\u50fb\u9053\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8272\u5df2\u6697\u4e86\u4e0b\u6765\uff0c\u9053\u4e0a\u5168\u65e0\u4eba\u8e2a\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u65bd\u5f00\u8eab\u6cd5\uff0c\u5954\u884c\u4e86\u987f\u996d\u5de5\u592b\uff0c\u4ed6\u8eab\u4e0a\u867d\u7136\u80cc\u8d1f\u4e86\u4e00\u4e2a\u4eba\uff0c\u4f46\u6b65\u5c65\u4ecd\u6781\u8f7b\u5065\uff0c\u8c01 \u4e5f\u60f3\u4e0d\u5230\u6709\u5982\u6b64\u8f7b\u529f\u7684\u4eba\u7adf\u4f1a\u4e3a\u4eba\u5974\u4ec6\uff0c\u800c\u4e14\uff0c\u8f7b\u529f\u5982\u6b64\u9ad8\u660e\u7684\u4eba\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u662f\u6c5f\u6e56\u7684\u65e0\u540d \u4e4b\u8f88\u3002 \u3000\u3000\u53c8\u5954\u884c\u4e86\u7247\u523b\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u524d\u9762\u7684\u8def\u4e0a\u79ef\u96ea\u5e73\u6574\u5982\u955c\uff0c\u6700\u5c11\u5df2\u6709\u4e24\u4e09\u4e2a\u65f6\u8fb0\u6ca1\u6709\u4eba\u8d70\u8fc7 \u4e86\u3002 \u3000\u3000\u90a3\u5927\u8f66\u600e\u4f1a\u5931\u8e2a\u4e86\u5462\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u6123\u4e86\u534a\u664c\uff0c\u53c8\u6298\u4e86\u56de\u53bb\u3002\u8fd9\u6b21\u4ed6\u5df2\u8d70\u5f97\u6162\u4e9b\uff0c\u800c\u4e14\u5206\u5916\u7559\u610f\uff0c\u6298\u56de\u4e86\u534a\u91cc\u8def\u540e \uff0c\u4ed6\u5c31\u53d1\u73b0\u5927\u8f66\u7684\u8f66\u8f99\u534a\u9014\u62d0\u5165\u4e86\u4e00\u6761\u5c94\u8def\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u6ca1\u6709\u7559\u610f\u8fd9\u6761\u5c94\u8def\uff0c\u56e0\u4e3a\u8fd9\u8def\u4e24\u65c1\uff0c\u53e4\u67cf\u68ee\u68ee\uff0c\u8fd8\u6709\u77f3\u7fc1\u4ef2\uff0c\u663e\u7136\u662f\u901a\u5411\u4e00\u4e2a\u5bcc \u8d35\u4eba\u5bb6\u7684\u9675\u5893\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u4f1a\u62d0\u5165\u8fd9\u6761\u5893\u9053\u6b7b\u8def\u4e0a\u6765\u7684\u3002 \u3000\u3000\u8fd9\u679c\u7136\u662f\u6761\u6b7b\u8def\u3002 \u3000\u3000\u5927\u8f66\u5c31\u505c\u5728\u5de8\u5927\u7684\u77f3\u9675\u5893\u524d\uff0c\u62c9\u8f66\u7684\u9a6c\u5df2\u4e0d\u89c1\u4e86\uff0c\u4e09\u4e2a\u7a7f\u7740\u7f8a\u76ae\u8884\u7684\u5927\u6c49\uff0c\u4e5f\u5012\u6bd9\u5728\u96ea \u5730\u4e0a\u3002 \u3000\u3000\u8f66\u7bb1\u91cc\u659c\u659c\u8eba\u7740\u4e00\u4e2a\u8eab\u7a7f\u91cd\u88d8\uff0c\u9762\u8272\u60e8\u767d\uff0c\u5e74\u7eaa\u867d\u5df2\u6709\u56db\u5341\u5de6\u53f3\uff0c\u4f46\u80e1\u5b50\u5374\u62ec\u5f97\u5e72\u5e72\u51c0 \u51c0\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u53ea\u8981\u770b\u4ed6\u624b\u4e0a\u6234\u7740\u7684\u90a3\u4ef7\u503c\u4e0d\u83f2\u7684\u7fe1\u7fe0\u6591\u6307\uff0c\u5c31\u77e5\u9053\u6b64\u4eba\u5fc5\u5b9a\u5c31\u662f\u2018\u91d1\u7389\u5802\u2019\u7684\u8d25\u5bb6\u5b50 \u6f58\u5927\u5c11\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u8fd8\u6709\u4e24\u4e2a\u5999\u9f84\u5c11\u5973\u7684\u5c38\u8eab\uff0c\u4e5f\u548c\u6f58\u5927\u5c11\u4e00\u6837\uff0c\u90fd\u662f\u88ab\u4eba\u4ee5\u91cd\u624b\u6cd5\u70b9\u4e86\u6b7b\u7a74\uff0c\u8f66\u65c1 \u7684\u4e09\u4eba\u5374\u662f\u88ab\u638c\u529b\u9707\u4f24\u5185\u8151\u800c\u6b7b\u7684\u3002 \u3000\u3000\u8fd9\u53c8\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u76b1\u7709\u9053\uff1a\u201c\u83ab\u975e\u662f\u65bd\u8000\u5148\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53c8\u53d1\u73b0\u9675\u5893\u77f3\u7891\u65c1\u4e5f\u5012\u6bd9\u4e86\u4e00\u4e2a\u5c38\u8eab\uff0c\u5934\u4e0a\u5149\u79c3\u79c3\u7684\u5168\u65e0\u5bf8\u53d1\uff0c\u4ef0\u9762\u5012\u5367 \u5728\u51b0\u96ea\u4e0a\uff0c\u4e24\u53ea\u624b\u8fd8\u7d27\u7d27\u5730\u6293\u7740\uff0c\u8c61\u662f\u4e34\u6b7b\u524d\u8fd8\u60f3\u6293\u7d27\u4e00\u6837\u4e1c\u897f\uff0c\u5374\u4ec0\u4e48\u4e5f\u6ca1\u6293\u4f4f\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u65bd\u8981\u5148\uff0c\u4f46\u5374\u518d\u4e5f\u65e0\u6cd5\u81ea\u68fa\u6750\u91cc\u4f38\u51fa\u624b\u6765\u8981\u94b1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u53f9\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u72c2\u5ad6\u6ee5\u7779\u90fd\u6ca1\u5173\u7cfb\uff0c\u53ef\u5343\u4e07\u4e0d\u80fd\u4ea4\u9519\u670b\u53cb\uff0c\u5426\u5219\u5c31\u96be\u514d\u8981\u548c \u6f58\u5927\u5c11\u4e00\u6837\uff0c\u6b7b\u4e86\u8fd8\u4e0d\u77e5\u662f\u8c01\u4e0b\u7684\u624b\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u5c11\u7237\u4f60\u2026\u2026\u4f60\u96be\u9053\u8bf4\u4ed6\u662f\u88ab\u65bd\u8000\u5148\u5bb3\u6b7b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u9762\u8272\u5982\u6b64\u5b89\u8be6\uff0c\u663e\u7136\u662f\u6b63\u5728\u7f8e\u4eba\u6000\u4e2d\u4eab\u798f\u65f6\uff0c\u5c31\u7cca\u91cc\u7cca\u6d82\u88ab\u4eba\u70b9\u4e86 \u6b7b\u7a74\uff0c\u8fd9\u8f66\u91cc\u53ea\u6709\u4ed6\u548c\u65bd\u8000\u5148\uff0c\u9664\u4e86\u65bd\u8000\u5148\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u4e0b\u624b\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u53ef\u662f\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u522b\u7684\u4eba\u9762\u4e0a\u90fd\u5e26\u7740\u60ca\u9a87\u4e4b\u8272\uff0c\u663e\u7136\u5230\u4e34\u6b7b\u8fd8\u4e0d\u76f8\u4fe1\u65bd\u8000 \u5148\u4f1a\u8fd9\u6bd2\u624b\u7684\uff0c\u5c24\u5176\u662f\u8fd9\u4e24\u4e2a\u5973\u5b50\uff0c\u5979\u4eec\u751f\u524d\u8bf4\u4e0d\u5b9a\u8fd8\u548c\u65bd\u8000\u5148\u6709\u8fc7\u7f20\u7ef5\uff0c\u66f4\u4e0d\u76f8\u4fe1\u65bd\u8000\u5148 \u4f1a\u6740\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u6b64\u4eba\u91cd\u5229\u8f7b\u7ea2\u989c\uff0c\u7adf\u4e0d\u61c2\u7ea2\u989c\u6bd4\u9ec4\u91d1\u8fd8\u53ef\u7231\u5f97\u591a\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u636e\u8bf4\u65bd\u8000\u5148\u6307\u4e0a\u7684\u529f\u529b\u5728\u5c71\u897f\u9996\u5c48\u4e00\u6307\uff0c\u539f\u672c\u5c31\u6709\u2018\u4e00\u6307\u8ffd\u9b42\u2019\u7684\u76db\u8a89 \uff0c\u8fd9\u7684\u786e\u8c61\u662f\u4ed6\u4e0b\u7684\u624b\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u53c8\u9053\uff1a\u201c\u65bd\u8000\u5148\u5c06\u6f58\u5927\u5c11\u5f53\u51a4\u5bb6\u7684\u5403\u4e86\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u4e45\u4e86\uff0c\u8fd9\u6b21\u6f58\u5927\u5c11\u60f3\u8981\u91d1 \u4e1d\u7532\uff0c\u65bd\u8000\u5148\u5403\u4eba\u5634\u8f6f\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u4e0d\u884c\uff0c\u4f46\u91d1\u4e1d\u7532\u5374\u53c8\u5b9e\u5728\u8bf1\u4eba\uff0c\u65bd\u8000\u5148\u5fc3\u4e00\u9ed1\uff0c\u7d22\u6027\u5c31\u4e00 \u52b3\u6c38\u9038\uff0c\u4e0b\u4e86\u6bd2\u624b\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u7684\u8bdd\u5934\u5df2\u88ab\u6253\u65ad\u4e86\u4e24\u6b21\uff0c\u8fd9\u6b21\u4ed6\u7b49\u4e86\u534a\u664c\uff0c\u76f4\u7b49\u5230\u674e\u5bfb\u6b22\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4ed6\u624d\u8bf4\u9053 \uff1a\u201c\u53ef\u662f\u65bd\u8000\u5148\u73b0\u5728\u4e5f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6740\u4eba\u8005\u4eba\u6052\u6740\u4e4b\uff0c\u65bd\u8000\u5148\u6740\u4eba\u7684\u65f6\u5019\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u6709\u4e2a\u559c\u7ba1\u95f2\u4e8b\u7684 \u4eba\u6b63\u5728\u8fd9\u9675\u5893\u4e0a\u770b\u7740\uff0c\u4e5f\u8bb8\u65bd\u8000\u5148\u53d1\u73b0\u4ed6\u540e\uff0c\u5c31\u60f3\u4e5f\u5c06\u4ed6\u6740\u4e86\u706d\u53e3\uff0c\u8c01\u77e5\u6740\u4eba\u4e0d\u6210\uff0c\u53cd\u88ab\u4eba \u6740\u4e86\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u76b1\u7709\u9053\uff1a\u201c\u65bd\u8000\u5148\u6b66\u529f\u4e0d\u82e5\uff0c\u662f\u8c01\u6740\u4e86\u4ed6\u5462\u3002\u201d \u3000\u3000\u4ed6\u8d70\u4e0a\u9675\u5893\u524d\u7684\u77f3\u7ea7\uff0c\u5c31\u53d1\u73b0\u65bd\u8000\u5148\u8eab\u4e0a\u4e5f\u6ca1\u6709\u522b\u7684\u4f24\u75d5\uff0c\u53ea\u6709\u54bd\u5589\u4e0a\u591a\u4e86\u4e00\u4e2a\u6d1e\u3002\u201d \u3000\u3000\u662f\u7528\u4e00\u67c4\u5e76\u4e0d\u950b\u5229\u7684\u5251\u523a\u7a7f\u7684\u6d1e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f0f\u5728\u866c\u7136\u5927\u6c49\u7684\u80a9\u5934\uff0c\u4e24\u4eba\u51dd\u6ce8\u4e86\u534a\u664c\uff0c\u4e00\u9f50\u957f\u957f\u5410\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u5634\u89d2\u7adf\u4f3c\u9732\u51fa \u4e86\u7b11\u5bb9\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u7b11\u9053\uff1a\u201c\u98de\u5c11\u7237\u7684\u5251\u6bd4\u98de\u8fd8\u5feb\uff0c\u8fd9\u5c31\u96be\u602a\u65bd\u8000\u5148\u62db\u67b6\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u4e0a\u773c\u775b\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u5b9e\u5728\u592a\u597d\u4e86\uff0c\u91d1\u4e1d\u7532\u5230\u4e86\u4ed6\u624b\u4e0a\uff0c\u8fd8\u662f\u7269 \u5f97\u5176\u4e3b\uff0c\u770b\u6765\u90a3\u6885\u82b1\u76d7\u662f\u5feb\u5012\u9709\u4e86\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u6211\u4eec\u53bb\u627e\u98de\u5c11\u7237\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u8d70\u8fdc\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4f60\u53bb\u627e\u4ed6\u6709\u4ec0\u4e48\u7528\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u89e3\u836f\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u82b1\u8702\u8eab\u4e0a\u5f53\u771f\u6709\u89e3\u836f\uff0c\u771f\u88ab\u5343\u624b\u7f57\u5239\u641c\u53bb\u4e86\u53c8\u88ab\u65bd\u8000\u5148\u52ab\u8d70\uff0c\u90a3\u4e48\uff0c\u73b0\u5728 \u5c31\u4e00\u5b9a\u8fd8\u5728\u65bd\u8000\u5148\u8eab\u4e0a\uff0c\u963f\u98de\u4ed6\u7edd\u4e0d\u4f1a\u5984\u53d6\u522b\u4eba\u4e1c\u897f\u7684\uff0c\u4ed6\u53ea\u5e26\u8d70\u4e86\u90a3\u91d1\u4e1d\u7532\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u8ba4 \u4e3a\u91d1\u4e1d\u7532\u5e94\u8be5\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u671b\u4e86\u671b\u90a3\u4e24\u4e2a\u5c11\u5973\u6234\u7740\u7684\u73e0\u7fe0\uff0c\u53c8\u671b\u4e86\u671b\u6f58\u5927\u5c11\u624b\u4e0a\u7684\u5de8\u5927\u7fe1\u7fe0\u6591\u6307\uff0c\u53f9\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u5c31\u7b97\u662f\u904d\u5730\u90fd\u662f\u91d1\u94b1\uff0c\u98de\u5c11\u7237\u4e5f\u4e0d\u4f1a\u5984\u53d6\u4e00\u6587\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u89e3\u836f\u82e5\u4e0d\u5728\u65bd\u8000\u5148\u8eab\u4e0a\uff0c\u6211\u4eec\u627e\u963f\u98de\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u624b\u6307\u98a4\u6296\u7740\uff0c\u5f00\u59cb\u53bb\u641c\u65bd\u8000\u5148\u7684\u8eab\u5b50\uff0c\u4ed6\u5b9e\u5728\u5f88\u7d27\u5f20\uff0c\u56e0\u4e3a\u8fd9\u5df2\u662f\u6700\u540e\u7684\u4e00\u4e1d \u5e0c\u671b\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5c06\u5c38\u4f53\u90fd\u642c\u4e86\u4e0b\u6765\uff0c\u6276\u7740\u674e\u5bfb\u6b22\u5750\u5165\u9a6c\u8f66\u3002 \u3000\u3000\u8f66\u7bb1\u7684\u677f\u58c1\u4e0a\uff0c\u7adf\u4e5f\u6709\u4e24\u884c\u7528\u5251\u5c16\u5212\u51fa\u6765\u7684\u5b57\uff1a \u3000\u3000\u201c\u6211\u4e3a\u4f60\u590d\u4e86\u4ec7 \u3000\u3000\u6211\u9a91\u8d70\u4e86\u4f60\u7684\u9a6c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u8fd8\u65ad\u5b9a\u53ef\u80fd\u662f\u4ed6\uff0c\u4f46\u73b0\u5728\u53ef\u4ee5\u65ad\u5b9a\u4e86\uff0c\u53ea\u6709\u4ed6\u624d\u662f\u8fde\u6b7b\u4eba\u7684\u4fbf \u5b9c\u90fd\u4e0d\u80af\u5360\u7684\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5b9e\u5728\u53ef\u7231\uff0c\u53ea\u6068\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u4f46\u866c\u7136\u5927\u6c49\u5df2\u77e5\u9053\u4ed6\u672c\u6765\u662f\u60f3\u8bf4\u4ec0\u4e48\u7684\uff0c\u60f3\u6765\u89e3\u836f\u5e76\u4e0d\u5728\u65bd\u8000\u5148 \u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u53ea\u6068\u6b64\u540e\u518d\u4e5f\u89c1\u4e0d\u5230\u8fd9\u53ef\u7231\u7684\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u4f3c\u4e4e\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5df2\u5feb\u5012\u4e0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u4e3a\u6211\u96be\u53d7\uff0c\u6b7b\uff0c\u5e76\u6ca1\u6709\u4f60\u60f3\u8c61\u4e2d\u90a3\u4e48\u53ef\u6015\uff0c\u73b0\u5728\u6211\u9664\u4e86\u8eab\u4e0a \u6ca1\u529b\u6c14\u4e4b\u5916\uff0c\u5fc3\u91cc\u53cd\u800c\u5e73\u9759\u5f97\u53ea\u60f3\u559d\u676f\u9152\u3002\u201d \u7b2c\u516d\u7ae0\u3000\u9189\u4e61\u9047\u6551\u661f>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u7ae0\u3000\u9189\u4e61\u9047\u6551\u661f \u3000\u3000\u866c\u9aef\u5927\u6c57\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u5c06\u8eab\u4e0a\u7684\u8863\u88f3\u5168\u90fd\u8131\u4e0b\u6765\uff0c\u94c1\u4e00\u822c\u7684\u80f8\u819b\u8fce\u7740\u51b0\u96ea\u548c\u5bd2\u98ce\uff0c\u5c06\u8f66 \u8f6d\u80cc\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u7adf\u8c61\u662f\u4e00\u5339\u9a6c\u4f3c\u7684\u5c06\u8fd9\u5927\u8f66\u62c9\u7740\u72c2\u5954\u800c\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5e76\u6ca1\u6709\u963b\u6b62\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ed6\u6ee1\u6000\u7684\u60b2\u75db\u9700\u8981\u53d1\u6cc4\uff0c\u4f46\u8f66\u95e8\u5173\u8d77\u65f6\uff0c\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7981 \u6d41\u4e0b\u4e86\u773c\u6cea\u3002 \u3000\u3000\u5730\u4e0a\u79ef\u96ea\u5df2\u5316\u4e3a\u575a\u51b0\uff0c\u8f66\u8f6e\u5728\u51b0\u4e0a\u6eda\u52a8\uff0c\u866c\u9aef\u5927\u6c57\u5e76\u4e0d\u9700\u8981\u82b1\u5f88\u5927\u529b\u6c14\uff0c\u9a6c\u8f66\u5df2\u75be\u9a70\u5982 \u98de\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u540e\uff0c\u4ed6\u4eec\u5df2\u5230\u4e86\u725b\u5bb6\u5e84\u3002 \u3000\u3000\u725b\u5bb6\u5e84\u662f\u4e2a\u5f88\u7e41\u8363\u7684\u5c0f\u9547\uff0c\u8fd9\u65f6\u5929\u8272\u8fd8\u672a\u5168\u9ed1\uff0c\u96ea\u5df2\u4f4f\u4e86\uff0c\u8857\u9053\u4e24\u65c1\u7684\u5e97\u5bb6\u90fd\u6709\u4eba\u62ff\u7740 \u628a\u626b\u628a\u51fa\u6765\u626b\u81ea\u5df1\u95e8\u524d\u7684\u79ef\u96ea\u3002 \u3000\u3000\u5927\u5bb6\u5ffd\u7136\u770b\u5230\u4e00\u6761\u7cbe\u8d64\u7740\u4e0a\u8eab\u7684\u5927\u6c57\uff0c\u62c9\u7740\u8f86\u9a6c\u8f66\u72c2\u5954\u800c\u6765\uff0c\u5f53\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u6709\u7684\u4eba\u629b \u4e0b\u626b\u628a\u5c31\u8dd1\u3002 \u3000\u3000\u9547\u4e0a\u81ea\u7136\u6709\u9152\u94fa\uff0c\u4f46\u98de\u9a70\u7684\u9a6c\u8f66\u5230\u4e86\u9152\u94fa\u524d\uff0c\u9aa4\u7136\u95f4\u505c\u4e86\u4e0b\u6765\uff0c\u866c\u9aef\u5927\u6c57\u9739\u96f3\u822c\u72c2\u543c\u4e00 \u58f0\uff0c\u7528\u529b\u5f80\u540e\u9762\u4e00\u9760\uff0c\u53ea\u542c\u2018\u7830\u2019\u7684\u4e00\u58f0\uff0c\u8f66\u53a2\u5df2\u88ab\u649e\u7834\u4e2a\u5927\u6d1e\uff0c\u4ed6\u4e00\u53cc\u811a\u4ecd\u6536\u52bf\u4e0d\u4f4f\uff0c\u5374 \u5df2\u9489\u5165\u96ea\u5730\u91cc\uff0c\u5730\u4e0a\u7684\u79ef\u96ea\uff0c\u90fd\u88ab\u94f2\u5f97\u98de\u6fc0\u800c\u8d77\uff01 \u3000\u3000\u5c0f\u9547\u4e0a\u7684\u4eba\u54ea\u91cc\u89c1\u5230\u8fc7\u5982\u6b64\u795e\u529b\uff0c\u90fd\u5df2\u9a87\u5446\u4e86\u3002 \u3000\u3000\u9152\u94fa\u91cc\u7684\u5ba2\u4eba\u770b\u5230\u8fd9\u715e\u795e\u822c\u7684\u5927\u6c57\u8d70\u4e86\u8fdb\u6765\uff0c\u4e5f\u9a87\u5f97\u6e9c\u8d70\u4e86\u4e00\u5927\u534a\uff0c\u866c\u9aef\u5927\u6c57\u5c06\u4e09\u6761\u677f \u51f3\u62fc\u5728\u4e00\u9f50\uff0c\u53c8\u7ad6\u8d77\u5f20\u684c\u5b50\u9760\u5728\u540e\u9762\uff0c\u518d\u94fa\u4e0a\u6f58\u5927\u5c11\u7684\u72d0\u88d8\uff0c\u624d\u5c06\u674e\u5bfb\u6b22\u62b1\u4e86\u8fdb\u6765\uff0c\u8ba9\u4ed6\u80fd \u5750\u5f97\u5f88\u8212\u670d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9762\u4e0a\u5df2\u5168\u65e0\u4e00\u4e1d\u8840\u8272\uff0c\u8fde\u5634\u5507\u90fd\u5df2\u53d1\u9752\uff0c\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u770b\u51fa\u4ed6\u8eab\u60a3\u91cd\u75c5\uff0c\u5feb\u8981\u6b7b \u7684\u75c5\u4eba\u5c45\u7136\u8fd8\u6765\u559d\u9152\uff0c\u8fd9\u9152\u94fa\u5f00\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u5374\u8fd8\u6ca1\u6709\u89c1\u8fc7\u8fd9\u79cd\u5ba2\u4eba\uff0c\u8fde\u638c\u67dc\u7684\u5e26\u4f19\u8ba1\u5168\u90fd \u5728\u53d1\u6123\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c57\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u543c\u9053\uff1a\u201c\u62ff\u9152\u6765\uff0c\u8981\u6700\u597d\u7684\u9152\uff01\u63ba\u4e86\u4e00\u5206\u6c34\u5c31\u8981\u4f60\u4eec\u8111\u888b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u4ed6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u4f60\u4eca\u5929\u624d\u7b97\u6709\u51e0\u5206\u2018\u94c1\u7532\u91d1\u521a \u2019\u7684\u8c6a\u6c14\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c57\u8eab\u5b50\u4e00\u9707\uff0c\u4f3c\u4e4e\u88ab\u2018\u94c1\u7532\u91d1\u521a\u2019\u8fd9\u540d\u5b57\u9707\u60ca\u4e86\uff0c\u4f46\u4ed6\u77ac\u5373\u4ef0\u9996\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a \u201c\u60f3\u4e0d\u5230\u5c11\u7237\u5c45\u7136\u8fd8\u8bb0\u5f97\u8fd9\u540d\u5b57\uff0c\u6211\u5374\u5df2\u5fd8\u6000\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4eca\u5929\u4e5f\u7834\u4f8b\u559d\u676f\u9152\u5427\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c57\u9053\uff1a\u201c\u597d\uff0c\u4eca\u5929\u5c11\u7237\u4f60\u559d\u591a\u5c11\uff0c\u6211\u5c31\u559d\u591a\u5c11\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u80fd\u4ee4\u4f60\u7834\u6212\u559d\u9152\uff0c\u6211\u4e5f\u7b97\u4e0d\u865a\u6b64\u751f\u4e86\uff01\u201d \u3000\u3000\u522b\u4eba\u89c1\u5230\u4ed6\u4eec\u5982\u6b64\u5927\u7b11\uff0c\u53c8\u90fd\u77aa\u5927\u4e86\u773c\u775b\u5077\u5077\u6765\u770b\uff0c\u8c01\u4e5f\u60f3\u4e0d\u901a\u4e00\u4e2a\u5c06\u6b7b\u7684\u75c5\u4eba\u8fd8\u662f\u4ec0 \u4e48\u597d\u5f00\u5fc3\u7684\u3002 \u3000\u3000\u9001\u6765\u7684\u9152\u867d\u975e\u4e0a\u54c1\uff0c\u4f46\u5374\u679c\u7136\u6ca1\u6709\u63ba\u6c34\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e3e\u676f\u9053\uff1a\u201c\u5c11\u7237\uff0c\u6055\u6211\u653e\u8086\uff0c\u6211\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u996e\u800c\u5c3d\uff0c\u4f46\u624b\u5df2\u62ff\u4e0d\u7a33\u9152\u676f\uff0c\u9152\u5df2\u6e85\u4e86\u51fa\u6765\uff0c\u4ed6\u4e00\u9762\u54b3\u55fd\u7740\uff0c\u4e00\u9762\u53bb\u64e6\u6e85\u5728\u8eab \u4e0a\u7684\u9152\uff0c\u4e00\u9762\u8fb9\u7b11\u7740\u9053\uff1a\u201c\u6211\u4ece\u672a\u7cdf\u8e4b\u8fc7\u4e00\u6ef4\u9152\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u4e5f\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u8863\u670d\u966a\u4e86\u6211\u591a\u5e74\uff0c\u786e\u5b9e\u6211\u4e5f\u8be5\u8bf7\u4ed6\u559d\u4e00\u676f\u4e86\uff0c\u6765\u6765\u6765\uff0c\u8863\u670d\u5144\uff0c\u591a \u627f\u4f60\u4f4d\u6211\u5fa1\u5bd2\u853d\u4f53\uff0c\u6211\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u521a\u66ff\u4ed6\u5012\u4e86\u4e00\u676f\u9152\uff0c\u4ed6\u7adf\u5168\u90fd\u5012\u5728\u81ea\u5df1\u8863\u670d\u4e0a\u3002 \u3000\u3000\u638c\u67dc\u7684\u548c\u5e97\u4f19\u9762\u9762\u76f8\u89d1\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u4e0d\u4f46\u6709\u75c5\uff0c\u8fd8\u662f\u4e2a\u75af\u5b50\u3002\u201d \u3000\u3000\u4e24\u4eba\u4f60\u4e00\u676f\uff0c\u6211\u4e00\u676f\u5730\u559d\u4e2a\u4e0d\u505c\uff0c\u674e\u5bfb\u6b22\u8981\u7528\u4e24\u53ea\u624b\u7d27\u63e1\u9152\u676f\uff0c\u624d\u80fd\u52c9\u5f3a\u5c06\u4e00\u676f\u9152\u9001\u8fdb \u5634\u91cc\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u5ffd\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u547c\u9053\uff1a\u201c\u4eba\u751f\u6bcf\u591a\u4e0d\u5e73\u4e8b\uff0c\u4f46\u613f\u957f\u9189\u4e0d\u590d\u9192\uff0c\u6211\u597d\u6068\u5440\uff0c\u597d \u6068\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u76b1\u7709\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u6211\u5e94\u8be5\u5f00\u5fc3\u624d\u662f\uff0c\u8bf4\u4ec0\u4e48\u4e0d\u5e73\u4e8b\uff0c\u8bf4\u4ec0\u4e48\u4e0d\u590d\u9192\uff0c\u4eba\u751f\u5f97\u610f \u987b\u5c3d\u6b22\uff0c\u83ab\u4f7f\u91d1\u6a3d\u7a7a\u5bf9\u6708\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u72c2\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4eba\u751f\u5f97\u610f\u987b\u5c3d\u6b22\uff0c\u5c11\u7237\uff0c\u6211\u518d\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u51c4\u5389\u7684\u7b11\u58f0\uff0c\u9707\u5f97\u9694\u58c1\u4e00\u5f20\u684c\u4e0a\u7684\u9152\u90fd\u6e85\u4e86\u51fa\u6765\uff0c\u4f46\u7b11\u58f0\u672a\u7edd\uff0c\u4ed6\u53c8\u5df2\u6251\u5012\u5728\u684c\u4e0a\uff0c\u75db \u54ed\u5931\u58f0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9762\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u9eef\u7136\u4e4b\u8272\uff0c\u550f\u5618\u9053\uff1a\u201c\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u82e5\u975e\u6709\u4f60\uff0c\u6211\u2026\u2026\u6211\u53ea\u6015\u5df2 \u65e0\u6cd5\u5ea6\u8fc7\uff0c\u6211\u867d\u7136\u77e5\u9053\u4f60\u7684\u82e6\u5fc3\uff0c\u8fd8\u662f\u89c9\u5f97\u59d4\u5c48\u4e86\u4f60\uff0c\u6b64\u5f8c\u4f46\u613f\u4f60\u80fd\u91cd\u632f\u6614\u5e74\u7684\u96c4\u98ce\uff0c\u90a3\u4e48 \u6211\u867d\u2026\u2026\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5ffd\u53c8\u8df3\u8d77\u6765\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5c11\u7237\u4f60\u600e\u5730\u4e5f\u8bf4\u8d77\u8fd9\u4e9b\u626b\u5174\u7684\u8bdd\u6765\u4e86\uff0c\u5f53\u6d6e\u4e00\u5927\u767d\u3002 \u201d \u3000\u3000\u4ed6\u4eec\u5ffd\u54ed\u5ffd\u7b11\uff0c\u53c8\u54ed\u53c8\u7b11\u3002 \u3000\u3000\u5e97\u638c\u67dc\u7684\u548c\u4f19\u8ba1\u53c8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4e24\u4eba\u90fd\u662f\u75af\u5b50\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u89c1\u4e00\u4e2a\u4eba\u8e09\u8e09\u8dc4\u8dc4\u5730\u51b2\u4e86\u8fdb\u6765\uff0c\u6251\u5012\u5728\u67dc\u53f0\u4e0a\uff0c\u560e\u58f0\u9053\uff1a\u201c\u9152\uff0c\u9152\uff0c\u5feb\u62ff \u9152\u6765\u3002\u201d \u3000\u3000\u770b\u4ed6\u7684\u795e\u60c5\uff0c\u5c31\u8c61\u662f\u82e5\u559d\u4e0d\u5230\u9152\u7acb\u523b\u5c31\u8981\u6e34\u6b7b\u4e86\u3002 \u3000\u3000\u638c\u67dc\u7684\u76b1\u8d77\u7709\u5934\uff0c\u6697\u9053\uff1a\u201c\u53c8\u6765\u4e00\u4e2a\u75af\u5b50\u3002\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u4eba\u7a7f\u7740\u4ef6\u5df2\u6d17\u7684\u53d1\u767d\u7684\u84dd\u888d\uff0c\u8896\u5b50\u4e0a\u80f8\u53e3\u4e0a\uff0c\u5374\u53c8\u6cbe\u6ee1\u4e86\u6cb9\u817b\uff0c\u4e00\u53cc\u624b\u7684\u6307\u7532\u91cc \u4e5f\u5168\u662f\u6ce5\u6c61\uff0c\u867d\u7136\u6234\u7740\u9876\u6587\u58eb\u65b9\u5dfe\uff0c\u4f46\u5934\u53d1\u5374\u4e71\u8349\u822c\u9732\u5728\u5916\u9762\uff0c\u4e00\u5f20\u8138\u53c8\u9ec4\u53c8\u7626\uff0c\u770b\u6765\u5c31\u8c61 \u662f\u4e2a\u7a77\u9178\u79c0\u624d\u3002 \u3000\u3000\u4f19\u8ba1\u76b1\u7740\u7709\u4e3a\u4ed6\u7aef\u4e86\u58f6\u9152\u6765\u3002 \u3000\u3000\u8fd9\u7a77\u9178\u79c0\u624d\u4e5f\u4e0d\u7528\u9152\u676f\uff0c\u5982\u957f\u9cb8\u5438\u6c34\u822c\uff0c\u5bf9\u7740\u58f6\u5634\u5c31\u5c06\u4e00\u58f6\u9152\u559d\u4e0b\u53bb\u5927\u534a\uff0c\u4f46\u5ffd\u53c8\u5168\u90fd \u55b7\u4e86\u51fa\u6765\uff0c\u8df3\u811a\u9053\uff1a\u201c\u8fd9\u4e5f\u80fd\u7b97\u9152\u4e48\u3002\u8fd9\u7b80\u76f4\u662f\u918b\uff0c\u800c\u4e14\u8fd8\u662f\u63ba\u4e86\u6c34\u7684\u918b\u2026\u2026\u201d \u3000\u3000\u90a3\u5e97\u4f19\u6a2a\u7740\u773c\u9053\uff1a\u201c\u5c0f\u5e97\u91cc\u5e76\u975e\u6ca1\u6709\u597d\u9152\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u7a77\u9178\u79c0\u624d\u6012\u9053\uff1a\u201c\u4f60\u53ea\u5f53\u5927\u7237\u6ca1\u6709\u94f6\u5b50\u4e70\u9152\u4e48\uff0c\u5454\uff0c\u62ff\u53bb\uff01\u201d \u3000\u3000\u4ed6\u968f\u624b\u4e00\u629b\uff0c\u7adf\u662f\u952d\u4e94\u5341\u4e24\u7684\u5b98\u5b9d\u3002 \u3000\u3000\u5927\u591a\u6570\u5bb6\u5993\u5973\u548c\u5e97\u4f19\u7684\u8138\u8272\uff0c\u4e00\u76f4\u90fd\u662f\u968f\u7740\u94f6\u5b50\u7684\u591a\u5c11\u800c\u6539\u53d8\u7684\uff0c\u8fd9\u5e97\u4f19\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4e8e \u662f\u597d\u9152\u7acb\u523b\u6765\u4e86\u3002 \u3000\u3000\u7a77\u9178\u79c0\u624d\u8fd8\u662f\u6765\u4e0d\u53ca\u7528\u9152\u676f\uff0c\u5634\u5bf9\u5634\u7684\u5c31\u5c06\u4e00\u58f6\u9152\u5168\u559d\u4e86\u4e0b\u53bb\uff0c\u772f\u7740\u773c\u5750\u5728\u90a3\u91cc\uff0c\u5c31\u8c61 \u662f\u4e00\u53e3\u6c14\u5ffd\u7136\u5598\u4e0d\u8fc7\u6765\u4e86\uff0c\u8054\u52a8\u90fd\u4e0d\u52a8\uff0c\u522b\u4eba\u53ea\u9053\u4ed6\u9152\u559d\u5f97\u592a\u6025\uff0c\u5ffd\u7136\u62bd\u4e86\u7b4b\uff0c\u674e\u5bfb\u6b22\u5374\u77e5 \u9053\u4ed6\u8fd9\u53ea\u4e0d\u8fc7\u662f\u5728\u90a3\u91cc\u54c1\u4f4d\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u89c1\u4ed6\u5c06\u8fd9\u53e3\u6c14\u957f\u957f\u900f\u4e86\u51fa\u6765\uff0c\u773c\u775b\u4e5f\u4eae\u4e86\uff0c\u8138\u4e0a\u4e5f\u6709\u4e86\u5149\u5f69\uff0c\u5583\u5583\u9053\uff1a\u201c \u9152\u867d\u7136\u4e0d\u597d\uff0c\u4f46\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u4e5f\u53ea\u597d\u9a6c\u864e\u4e9b\u4e86\u3002\u201d \u3000\u3000\u90a3\u5e97\u4f19\u966a\u7740\u7b11\uff0c\u54c8\u7740\u8170\u9053\uff1a\u201c\u8fd9\u7f50\u9152\u5c0f\u5e97\u5df2\u85cf\u4e86\u5341\u51e0\u5e74\uff0c\u4e00\u76f4\u90fd\u820d\u4e0d\u5f97\u62ff\u51fa\u6765\u3002 \u3000\u3000\u7a77\u9178\u5ffd\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u96be\u602a\u9152\u5473\u592a\u6de1\uff0c\u539f\u6765\u85cf\u5f97\u592a\u4e45\uff0c\u5feb\u627e\u4e00\u575b\u65b0\u917f\u7684\u65b0\u9152\u5151 \u4e0b\u53bb\uff0c\u4e0d\u591a\u4e0d\u5c11\uff0c\u53ea\u80fd\u5151\u4e09\u6210\uff0c\u5728\u5f04\u51e0\u789f\u5c0f\u83dc\u6765\u4e0b\u9152\u3002\u201d \u3000\u3000\u5e97\u4f19\u9053\uff1a\u201c\u4e0d\u77e5\u4f60\u8001\u8981\u70b9\u4e9b\u4ec0\u4e48\u83dc\u3002\u201d \u3000\u3000\u7a77\u9178\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u77e5\u9053\u4f60\u4eec\u8fd9\u79cd\u5730\u65b9\u4e5f\u5f04\u4e0d\u51fa\u4ec0\u4e48\u597d\u4e1c\u897f\u6765\uff0c\u6495\u4e00\u53ea\u51e4\u9e21\uff0c\u518d\u627e\u4e9b\u5ae9 \u59dc\u6765\u7092\u9e26\u80a0\u5b50\uff0c\u4e5f\u5c31\u5bf9\u4ed8\u4e86\uff0c\u4f46\u59dc\u4e00\u5b9a\u8981\u5ae9\uff0c\u51e4\u9e21\u7684\u6bdb\u8981\u53bb\u5f97\u5e72\u51c0\u3002\u201d \u3000\u3000\u8fd9\u4eba\u867d\u7136\u53c8\u7a77\u53c8\u9178\uff0c\u4f46\u5403\u559d\u8d77\u6765\u5374\u4e00\u70b9\u4e5f\u4e0d\u542b\u7cca\uff0c\u674e\u5bfb\u6b22\u8d8a\u770b\u8d8a\u89c9\u5f97\u6b64\u4eba\u6709\u8da3\uff0c\u82e5\u5728\u5e73 \u65f6\uff0c\u5c11\u4e0d\u5f97\u8981\u548c\u4ed6\u840d\u6c34\u76f8\u4ea4\uff0c\u75db\u996e\u4e00\u756a\uff0c\u4f46\u6b64\u756a\u4ed6\u5df2\u968f\u65f6\u968f\u523b\u90fd\u6709\u53ef\u80fd\u5012\u4e0b\u53bb\uff0c\u53c8\u4f55\u82e6\u518d\u8fde \u7d2f\u522b\u4eba\u3002 \u3000\u3000\u90a3\u7a77\u9178\u66f4\u662f\u65c1\u82e5\u65e0\u4eba\uff0c\u9152\u5230\u676f\u5e72\u3002 \u3000\u3000\u4ed6\u773c\u775b\u9664\u4e86\u9152\u4e4b\u5916\uff0c\u4f3c\u4e4e\u518d\u4e5f\u77a7\u4e0d\u89c1\u522b\u7684\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u9635\u6025\u9aa4\u7684\u9a6c\u8e44\u58f0\u54cd\uff0c\u9aa4\u7136\u505c\u5728\u95e8\u5916\uff0c\u8fd9\u7a77\u9178\u7684\u8138\u8272\uff0c\u7adf\u4e5f\u6709\u4e9b\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u7ad9\u8d77\u6765\u5c31\u60f3\u8d70\uff0c\u4f46\u671b\u4e86\u671b\u684c\u4e0a\u7684\u9152\uff0c\u53c8\u5750\u4e86\u4e0b\u53bb\uff0c\u8fde\u559d\u4e86\u4e09\u676f\uff0c\u631f\u4e86\u5757\u9e26\u80a0\u6162\u6162\u5480\u56bc \uff0c\u60a0\u7136\u9053\uff1a\u201c\u9189\u4e61\u8def\u5e38\u81f3\uff0c\u4ed6\u5904\u4e0d\u582a\u884c\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u5927\u543c\u9053\uff1a\u201c\u597d\u4e2a\u9152\u9b3c\uff0c\u4f60\u8fd8\u60f3\u5230\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u53ea\u6709\u5728\u9152\u94fa\u91cc\u624d\u627e\u5f97\u5230\u4ed6\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4e94\u516d\u4e2a\u4eba\u4e00\u9f50\u51b2\u4e86\u8fdb\u6765\uff0c\u5c06\u7a77\u9178\u56f4\u4f4f\u3002\u8fd9\u51e0\u4eba\u52b2\u88c5\u6025\u670d\uff0c\u4f69\u5200\u6302\u5251\uff0c\u770b\u6765\u8eab\u624b \u90fd\u4e0d\u592a\u5f31\u3002 \u3000\u3000\u4e00\u4eba\u7626\u524a\u9880\u957f\uff0c\u624b\u91cc\u63d0\u7740\u9a6c\u97ad\uff0c\u6307\u7740\u7a77\u9178\u7684\u9f3b\u5b50\u9053\uff1a\u201c\u5f97\u4eba\u94b1\u8d22\uff0c\u4e0e\u4eba\u6d88\u707e\uff0c\u4f60\u62ff\u4e86\u54b1 \u4eec\u7684\u8bca\u91d1\uff0c\u4e0d\u66ff\u54b1\u4eec\u6cbb\u75c5\uff0c\u5374\u9003\u51fa\u6765\u559d\u9152\u4e86\uff0c\u8fd9\u7b97\u4ec0\u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u7a77\u9178\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u610f\u601d\u5404\u4f4d\u96be\u9053\u8fd8\u4e0d\u61c2\u4e48\u3002\u53ea\u4e0d\u8fc7\u662f\u9152\u763e\u5927\u53d1\u800c\u5df2\uff0c\u6885\u4e8c\u5148\u751f\u9152 \u763e\u53d1\u4f5c\u65f6\uff0c\u5c31\u7b97\u5929\u584c\u4e0b\u6765\u4e5f\u5f97\u5148\u559d\u4e86\u9152\u518d\u8bf4\uff0c\u54ea\u6709\u5fc3\u60c5\u4e3a\u522b\u4eba\u6cbb\u75c5\u3002\u201d \u3000\u3000\u4e00\u4e2a\u9ebb\u9762\u5927\u6c49\u9053\uff1a\u201c\u8d75\u8001\u5927\uff0c\u4f60\u542c\u89c1\u6ca1\u6709\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u8fd9\u9152\u9b3c\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u53ea\u8981\u94f6\u5b50\u5230 \u624b\uff0c\u7acb\u523b\u5c31\u516d\u4eb2\u4e0d\u8ba4\u4e86\u3002\u201d \u3000\u3000\u9880\u957f\u5927\u6c49\u6012\u9053\uff1a\u201c\u8fd9\u9152\u9b3c\u7684\u6bdb\u75c5\u8c01\u4e0d\u77e5\u9053\uff0c\u4f46\u8001\u56db\u7684\u75c5\u5374\u975e\u4ed6\u6cbb\u4e0d\u53ef\uff0c\u75c5\u6025\u4e71\u6295\u533b\uff0c\u4f60 \u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u672c\u5f53\u8fd9\u4e9b\u4eba\u662f\u6765\u5bfb\u4ec7\u7684\uff0c\u542c\u4e86\u4ed6\u4eec\u7684\u8bdd\uff0c\u624d\u77e5\u9053\u8fd9\u4f4d\u6885\u4e8c\u5148\u751f\u539f\u6765\u662f\u4e2a\u6c5f\u6e56\u90ce\u4e2d \uff0c\u5149\u62ff\u94f6\u5b50\u4e0d\u6cbb\u75c5\u7684\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6765\u52bf\u6c79\u6c79\uff0c\u5927\u56d4\u5927\u53eb\uff0c\u4ed6\u5374\u8fd8\u662f\u7a33\u5982\u6cf0\u5c71\uff0c\u5750\u5728\u90a3\u91cc\u5de6\u4e00\u676f\uff0c\u53f3\u4e00\u676f\u5730\u559d\u4e86\u8d77\u6765 \u3002 \u3000\u3000\u8d75\u8001\u5927\u638c\u4e2d\u9a6c\u97ad\u4e00\u626c\uff0c\u2018\u5237\u2019\u7684\u5c06\u4ed6\u9762\u524d\u9152\u58f6\u5377\u98de\u4e86\u51fa\u53bb\uff0c\u5389\u58f0\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u73b0\u5728 \u54b1\u4eec\u65e2\u5df2\u627e\u7740\u4e86\u4f60\uff0c\u4f60\u5c31\u4e56\u4e56\u5730\u8ddf\u54b1\u4eec\u56de\u53bb\u6cbb\u75c5\u5427\uff0c\u53ea\u8981\u80fd\u5c06\u8001\u56db\u7684\u75c5\u6cbb\u597d\uff0c\u5305\u4f60\u6709\u9152\u559d\u3002 \u201d \u3000\u3000\u90a3\u4f4d\u6885\u4e8c\u5148\u751f\u671b\u7740\u88ab\u6454\u5f97\u7c89\u788e\u7684\u9152\u58f6\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u77e5\u9053\u6885\u4e8c\u5148\u751f\u7684 \u813e\u6c14\uff0c\u5c31\u8be5\u77e5\u9053\u6885\u4e8c\u5148\u751f\u751f\u5e73\u6709\u4e09\u4e0d\u6cbb\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\uff1a\u201c\u54ea\u4e09\u4e0d\u6cbb\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u8bca\u91d1\u4e0d\u5148\u4ed8\uff0c\u4e0d\u6cbb\uff0c\u4ed8\u5c11\u4e86\u4e00\u5206\uff0c\u4e5f\u4e0d\u6cbb\u3002\u201d \u3000\u3000\u9ebb\u9762\u5927\u6c57\u6012\u9053\uff1a\u201c\u54b1\u4eec\u51e0\u65f6\u5c11\u4e86\u4f60\u4e00\u5206\u94f6\u5b50\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u7b2c\u4e8c\uff0c\u793c\u8c8c\u4e0d\u5468\uff0c\u8a00\u8bed\u5931\u656c\u7684\uff0c\u4e0d\u6cbb\uff0c\u7b2c\u4e09\uff0c\u5f3a\u76d7\u5c0f\u5077\uff0c\u6740\u4eba\u8d8a\u8d27\u7684\uff0c \u66f4\u662f\u4e07\u4e07\u4e0d\u6cbb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u4f60\u4eec\u5c06\u8fd9\u4e24\u6761\u5168\u90fd\u72af\u4e86\uff0c\u8fd8\u60f3\u6885\u4e8c\u5148\u751f\u66ff\u4f60\u4eec\u6cbb\u75c5\uff0c\u8fd9\u5c82 \u975e\u662f\u5728\u75f4\u4eba\u8bf4\u68a6\uff0c\u693d\u6728\u6c42\u9c7c\u3002\u201d \u3000\u3000\u90a3\u51e0\u6761\u5927\u6c57\u8116\u5b50\u90fd\u6c14\u7c97\u4e86\uff0c\u6012\u543c\u9053\uff1a\u201c\u4e0d\u6cbb\u5c31\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u8981\u547d\u4e5f\u4e0d\u6cbb\uff01\u201d \u3000\u3000\u9ebb\u9762\u5927\u6c49\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u4ed6\u8fde\u4eba\u5e26\u51f3\u5b50\u90fd\u6253\u5f97\u6eda\u51fa\u4e03\u516b\u5c3a\u5f00\u5916\uff0c\u4f0f\u5728\u5730\u4e0a\uff0c\u987a\u7740\u5634\u76f4\u6d41\u8840 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u4ed6\u5982\u6b64\u9547\u5b9a\uff0c\u672c\u5f53\u4ed6\u662f\u4f4d\u6df1\u85cf\u4e0d\u9732\u7684\u98ce\u5c18\u5f02\u4eba\uff0c\u5982\u4eca\u624d\u77e5\u9053\u4ed6\u4e00\u5f20\u5634\u867d\u786c\uff0c\u4e00 \u53cc\u624b\u5374\u4e0d\u786c\u3002 \u3000\u3000\u8d75\u8001\u5927\u55d6\u5730\u62d4\u51fa\u4e86\u8170\u5200\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5634\u91cc\u82e5\u6562\u518d\u8bf4\u534a\u4e2a\u4e0d\u5b57\uff0c\u5927\u7237\u5c31\u5148\u5378\u4e0b\u4f60\u4e00\u6761\u8180 \u5b50\u518d\u8bf4\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u6342\u7740\u8138\uff0c\u9053\uff1a\u201c\u8bf4\u4e0d\u6cbb\u5c31\u4e0d\u77e5\u65b9\uff0c\u6885\u4e8c\u5148\u751f\u8fd8\u4f1a\u6015\u4e86\u4f60\u4eec\u8fd9\u7fa4\u6bdb\u8d3c\u4e48\u3002 \u3000\u3000\u8d75\u8001\u5927\u6012\u543c\u4e00\u58f0\uff0c\u5c31\u60f3\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u5ffd\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u559d\u9152\u7684\u5730\u65b9\uff0c\u4e0d\u559d\u9152\u7684\u5168\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\u5c31\u4eff\u4f5b\u6674\u7a7a\u4e2d\u6253\u4e0b\u4e2a\u9739\u96f3\uff0c\u8d75\u8001\u5927\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u7531\u81ea\u4e3b\u5012\u9000\u534a\u6b65\uff0c\u77aa\u7740\u4ed6\u9053 \uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u6562\u6765\u7ba1\u5927\u7237\u7684\u95f2\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6eda\u51fa\u53bb\u65e0\u8da3\uff0c\u53eb\u4ed6\u4eec\u722c\u51fa\u53bb\u5427\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u559d\u9053\uff1a\u201c\u5c11\u7237\u53eb\u4f60\u4eec\u722c\u51fa\u53bb\uff0c\u542c\u89c1\u6ca1\u6709\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u89c1\u5230\u8fd9\u4e24\u4eba\u4e00\u4e2a\u5df2\u75c5\u5f97\u6709\u6c14\u65e0\u529b\uff0c\u4e00\u4e2a\u5df2\u9189\u5f97\u4e8e\u4eca\u53d1\u76f4\uff0c\u4ed6\u80c6\u5b50\u7acb\u523b\u53c8\u58ee\u4e86\uff0c\u72de \u7b11\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u4e0d\u77e5\u8da3\uff0c\u5927\u7237\u5c31\u62ff\u4f60\u4eec\u5f00\u5200\u4e5f\u597d\uff01\u201d \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u4ed6\u638c\u4e2d\u5200\u7adf\u5411\u674e\u5bfb\u6b22\u76f4\u5288\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u76b1\u4e86\u76b1\u7709\uff0c\u4e00\u4f38\u624b\uff0c\u5c31\u53bb\u67b6\u5200\u3002 \u3000\u3000\u4ed6\u7adf\u4f3c\u5df2\u9189\u7cca\u6d82\u4e86\uff0c\u7adf\u4ee5\u81ea\u5df1\u7684\u8180\u5b50\u53bb\u67b6\u950b\u5229\u7684\u5200\u950b\uff0c\u638c\u67dc\u7684\u4e0d\u7981\u60ca\u547c\u51fa\u58f0\uff0c\u4ee5\u4e3a\u8fd9\u4e00 \u5200\u5288\u4e0b\uff0c\u4ed6\u8fd9\u6761\u624b\u81c2\u5c31\u8981\u8840\u6dcb\u6dcb\u5730\u88ab\u780d\u4e0b\u6765\u3002 \u3000\u3000\u8c01\u77e5\u4e00\u5200\u780d\u4e0b\u540e\uff0c\u624b\u81c2\u4ecd\u662f\u597d\u751f\u751f\u7684\u7eb9\u98ce\u672a\u52a8\uff0c\u5200\u5374\u88ab\u9707\u5f97\u8131\u624b\u98de\u51fa\uff0c\u8fde\u8d75\u8001\u5927\u7684\u8eab\u5b50 \u90fd\u88ab\u9707\u5f97\u7ad9\u4e0d\u7a33\u4e86\uff0c\u8e09\u8dc4\u540e\u9000\uff0c\u5931\u58f0\u60ca\u547c\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u8eab\u4e0a\u7adf\u6709\u91d1\u949f\u7f69\uff0c\u94c1\u5e03\u886b\u7684\u6a2a\u7ec3\u529f\u592b \uff0c\u54b1\u4eec\u53ea\u6015\u662f\u9047\u89c1\u9b3c\u4e86\uff01\u201d \u3000\u3000\u9ebb\u5b50\u7684\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u966a\u7b11\u9053\uff1a\u201c\u670b\u53cb\u9ad8\u59d3\u5927\u540d\uff0c\u8bf7\u8d50\u4e2a\u4e07\u513f\uff0c\u54b1\u4eec\u4e0d\u6253\u4e0d\u76f8\u8bc6\uff0c\u65e5\u540e\u4e5f \u597d\u4ea4\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u51b7\u51b7\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u548c\u6211\u4ea4\u670b\u53cb\u3002\u6eda\uff01\u201d \u3000\u3000\u8d75\u8001\u5927\u8df3\u8d77\u6765\uff0c\u543c\u9053\uff1a\u201c\u670b\u53cb\u83ab\u8981\u6b3a\u4eba\u592a\u751a\uff0c\u9700\u77e5\u54b1\u4eec\u9ec4\u6cb3\u4e03\u86df\u4e5f\u4e0d\u662f\u597d\u60f9\u7684\uff0c\u82e5\u662f\u2026 \u2026\u201d \u3000\u3000\u4ed6\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u90a3\u9ebb\u5b50\u5ffd\u7136\u5c06\u4ed6\u62c9\u5230\u4e00\u65c1\uff0c\u6084\u6084\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u5077\u5077\u53bb\u77a7\u674e \u5bfb\u6b22\u9152\u676f\u65c1\u7684\u5c0f\u5200\u3002 \u3000\u3000\u8d75\u8001\u5927\u8138\u4e0a\u66f4\u5168\u65e0\u8840\u8272\uff0c\u560e\u58f0\u9053\uff1a\u201c\u4e0d\u4f1a\u662f\u4ed6\u5427\u3002\u201d \u3000\u3000\u9ebb\u5b50\u6084\u6084\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u662f\u8c01\u3002\u534a\u4e2a\u6708\u4ee5\u524d\uff0c\u6211\u5c31\u542c\u9f99\u795e\u5e99\u7684\u8001\u4e4c\u9f9f\u8bf4\u4ed6\u53c8\u5df2\u5165\u5173\u4e86\uff0c\u8001 \u4e4c\u9f9f\u591a\u5e74\u524d\u5c31\u89c1\u8fc7\u4ed6\u4e86\uff0c\u7edd\u4e0d\u4f1a\u770b\u9519\u7684\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u9053\uff1a\u201c\u4f46\u8fd9\u75c5\u9b3c\u2026\u2026\u201d \u3000\u3000\u9ebb\u5b50\u9053\uff1a\u201c\u6b64\u4eba\u5403\u559d\u5ad6\u7779\uff0c\u6837\u6837\u7cbe\u901a\uff0c\u8eab\u4f53\u4e00\u5411\u4e0d\u597d\uff0c\u53ef\u662f\u4ed6\u7684\u5200\u2026\u2026\u201d \u3000\u3000\u63d0\u5230\u8fd9\u67c4\u5200\uff0c\u4ed6\u8fde\u58f0\u97f3\u90fd\u53d8\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4e0d\u9632\u4e00\u4e07\uff0c\u53ea\u9632\u4e07\u4e00\uff0c\u54b1\u4eec\u4ec0\u4e48\u4eba\u4e0d\u597d\u60f9\uff0c \u4f55\u51b5\u60f9\u5230\u4ed6\u5934\u4e0a\u53bb\u3002\u201d \u3000\u3000\u8d75\u8001\u5927\u82e6\u7b11\u9053\uff1a\u201c\u6211\u82e5\u65e9\u77e5\u9053\u4ed6\u5728\u8fd9\u91cc\uff0c\u5c31\u7b97\u62ff\u628a\u5200\u67b6\u5728\u6211\u8116\u5b50\u4e0a\uff0c\u6211\u90fd\u4e0d\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u5e72\u54b3\u4e24\u58f0\uff0c\u966a\u7740\u7b11\u8eac\u8eab\u9053\uff1a\u201c\u5c0f\u4eba\u4eec\u6709\u773c\u65e0\u73e0\uff0c\u4e0d\u8ba4\u5f97\u4f60\u8001\u4eba\u5bb6\uff0c\u6253\u6270\u4e86\u4f60\u8001\u4eba\u5bb6\u7684 \u9152\u5174\uff0c\u5c0f\u4eba\u4eec\u8be5\u6b7b\uff0c\u8fd9\u5c31\u6eda\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u77e5\u542c\u89c1\u4ed6\u8bf4\u7684\u8bdd\u6ca1\u6709\uff0c\u53c8\u5f00\u59cb\u559d\u9152\uff0c\u5f00\u59cb\u54b3\u55fd\uff0c\u5c31\u597d\u8c61\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7 \u4f3c\u7684\u3002 \u3000\u3000\u8001\u864e\u822c\u95ef\u8fdb\u6765\u7684\u5927\u6c49\u4eec\uff0c\u6b64\u523b\u5df2\u8c61\u72d7\u4f3c\u7684\u5939\u7740\u5c3e\u5df4\u9003\u51fa\u53bb\u4e86\uff0c\u90a3\u4f4d\u6885\u4e8c\u5148\u751f\u8fd9\u624d\u6162\u541e\u541e \u7684\u722c\u4e86\u8fdb\u6765\uff0c\u5c45\u7136\u4e5f\u4e0d\u53bb\u5411\u674e\u5bfb\u6b22\u4ed6\u4eec\u9053\u8c22\uff0c\u4e00\u5c41\u80a1\u5750\u5230\u51f3\u5b50\u4e0a\uff0c\u53c8\u4e0d\u505c\u5730\u62cd\u7740\u684c\u5b50\uff0c\u77aa\u7740 \u773c\u9053\uff1a\u201c\u9152\uff0c\u9152\uff0c\u5feb\u62ff\u9152\u6765\u3002\u201d \u3000\u3000\u90a3\u5e97\u4f19\u63c9\u7740\u773c\u775b\uff0c\u7b80\u76f4\u4e0d\u76f8\u4fe1\u65b9\u624d\u88ab\u4eba\u6253\u5f97\u6ee1\u5730\u4e71\u722c\u7684\u4eba\u5c31\u662f\u4ed6\u3002 \u3000\u3000\u9152\u94fa\u91cc\u7684\u4eba\u65e9\u5df2\u90fd\u6e9c\u5149\u4e86\uff0c\u53ea\u5269\u4e0b\u4ed6\u4eec\u4e09\u4e2a\u4eba\uff0c\u628a\u9152\u676f\u4e00\u676f\u676f\u5f80\u5634\u5012\uff0c\u9152\u559d\u5f97\u8d8a\u591a\uff0c\u8bdd \u53cd\u800c\u8d8a\u5c11\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u7a97\u5916\u7684\u5929\u8272\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u9152\u4e4b\u4e00\u7269\uff0c\u771f\u5947\u5999\uff0c\u4f60\u8d8a\u4e0d\u60f3\u559d\u9189\u7684\u65f6\u5019\uff0c\u9189\u5f97 \u8d8a\u5feb\uff0c\u5230\u4e86\u60f3\u559d\u9189\u7684\u65f6\u5019\uff0c\u53cd\u800c\u9189\u4e0d\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u5ffd\u4e5f\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4e00\u9189\u89e3\u5343\u6101\uff0c\u9189\u6b7b\u7b97\u5c01\u4faf\uff0c\u53ea\u53ef\u60dc\u6709\u4e9b\u4eba\u867d\u60f3\u9189\u6b7b\uff0c \u8001\u5929\u5374\u504f\u504f\u4e0d\u8ba9\u4ed6\u6b7b\u5f97\u5982\u6b64\u8212\u670d\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u76b1\u4e86\u76b1\u7709\uff0c\u6885\u4e8c\u5148\u751f\u7adf\u6447\u6447\u6643\u6643\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u76f4\u7740\u773c\u671b\u7740\u674e\u5bfb\u6b22\uff0c\u60a0\u7136\u9053\uff1a\u201c \u9601\u4e0b\u53ef\u77e5\u9053\u81ea\u5df1\u8fd8\u80fd\u6d3b\u591a\u4e45\u4e48\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6d3b\u4e0d\u957f\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u77e5\u9053\u6d3b\u4e0d\u957f\u4e86\uff0c\u8fd8\u4e0d\u5feb\u53bb\u51c6\u5907\u540e\u4e8b\uff0c\u8fd8\u8981\u6765\u559d\u9152\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u751f\u6b7b\u7b49\u95f2\u4e8b\u8033\uff0c\u600e\u53ef\u4e3a\u4e86\u8fd9\u79cd\u4e8b\u800c\u803d\u8bef\u559d\u9152\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9644\u638c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u4e0d\u9519\uff0c\u751f\u6b7b\u4e8b\u5c0f\uff0c\u559d\u9152\u4e8b\u5927\uff0c\u9601\u4e0b\u6b64\u8a00\uff0c\u5b9e\u5f97\u6211\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u77aa\u8d77\u773c\u775b\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u9601\u4e0b\u60f3\u5fc5\u5df2\u77e5\u9053\u6211\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd8\u672a\u8bc6\u8346\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\u5c31\u4e0d\u8ba4\u5f97\uff0c\u565c\u55e6\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u4e5f\u4e0d\u776c\u4ed6\uff0c\u8fd8\u662f\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u6551\u6211\u5e76\u975e\u4e3a\u4e86\u8981\u6211\u4e3a\u4f60\u6cbb\u75c5\u4e86 \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u82e5\u8981\u559d\u9152\uff0c\u4e0d\u59a8\u6765\u5171\u996e\u51e0\u676f\uff0c\u82e5\u8981\u6765\u6cbb\u75c5\uff0c\u5c31\u8bf7\u8d70\u8fdc\u4e9b\u5427\uff0c\u83ab\u8981\u803d \u8bef\u4e86\u6211\u559d\u9152\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u53c8\u77ac\u4e5f\u4e0d\u77ac\u5730\u77aa\u4e86\u4ed6\u5f88\u4e45\uff0c\u5583\u5583\u9053\uff1a\u201c\u597d\u8fd0\u6c14\u5440\u597d\u8fd0\u6c14\uff0c\u4f60\u9047\u89c1\u4e86\u6211\uff0c\u5f53\u771f\u662f \u597d\u8fd0\u6c14\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u65e0\u8bca\u91d1\u53ef\u4ed8\uff0c\u548c\u5f3a\u76d7\u5df2\u5dee\u4e0d\u591a\uff0c\u9601\u4e0b\u8fd8\u662f\u8bf7\u56de\u5427\u3002\u201d \u3000\u3000\u8c01\u77e5\u6885\u4e8c\u5148\u751f\u5374\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\u4e0d\u884c\uff0c\u522b\u4eba\u7684\u75c5\u6211\u4e0d\u6cbb\uff0c\u4f60\u8fd9\u75c5\u6211\u5374\u975e\u6cbb\u4e0d\u53ef\uff0c\u4f60\u82e5\u4e0d \u8981\u6211\u6cbb\u75c5\uff0c\u9664\u975e\u5148\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u65b9\u624d\u522b\u4eba\u8981\u6740\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u80af\u6cbb\u75c5\uff0c\u6b64\u523b\u5374\u786c\u662f\u975e\u8981\u66ff\u4eba\u6cbb\u75c5\u4e0d\u53ef\uff0c\u90a3\u5e97\u4f19\u53ea\u6068\u4e0d\u5f97\u8d76\u5feb \u56de\u5bb6\u53bb\u8499\u5934\u5927\u7761\u4e09\u5929\uff0c\u518d\u4e5f\u83ab\u8981\u89c1\u5230\u8fd9\u4e09\u4e2a\u75af\u5b50\uff0c\u53ea\u56e0\u8001\u662f\u518d\u8fd9\u6837\u6298\u817e\u4e0b\u53bb\uff0c\u4ed6\u53ea\u6015\u4e5f\u8981\u88ab \u6c14\u75af\u4e86\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u5374\u5df2\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u771f\u80fd\u6cbb\u5f97\u4e86\u4ed6\u7684\u75c5\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u50b2\u7136\u9053\uff1a\u201c\u4ed6\u8fd9\u75c5\u9664\u4e86\u6885\u4e8c\u5148\u751f\u5916\uff0c\u5929\u4e0b\u53ea\u6015\u8c01\u4e5f\u6cbb\u4e0d\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u8df3\u8d77\u6765\u4e00\u628a\u63ea\u7740\u4ed6\u8863\u895f\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u8fd9\u662f\u4ec0\u4e48\u75c5\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u773c\u775b\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8c01\u77e5\u9053\uff0c\u4f60\u4ee5\u4e3a\u82b1\u8001\u516d\u771f\u80fd\u914d\u5f97\u51fa\u90a3\u2018\u5bd2\u9e21\u6563\u2019\u4e48 \u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5931\u58f0\u9053\uff1a\u201c\u5bd2\u9e21\u6563\u3002\u4ed6\u4e2d\u7684\u6bd2\u5c31\u662f\u5bd2\u9e21\u6563\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u50b2\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9664\u4e86\u6885\u5bb6\u7684\u2018\u5bd2\u9e21\u6563\u2019\uff0c\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u6bd2\u80fd\u6bd2\u5f97\u6b7b\u674e\u5bfb\u6b22\u3002 \uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u53c8\u60ca\u53c8\u559c\u9053\uff1a\u201c\u82b1\u8702\u7684\u2018\u5bd2\u9e21\u6563\u2019\u662f\u4f60\u914d\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u5927\u7b11\u9053\uff1a\u201c\u9664\u4e86\u6211\u2018\u5999\u90ce\u4e2d\u2019\u6885\u4e8c\u5148\u751f\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u914d\u5f97\u51fa\u5bd2\u9e21\u6563\u3002\u770b\u6765\u4f60\u5f53 \u771f\u662f\u5b64\u964b\u5be1\u95fb\uff0c\u8fde\u8fd9\u79cd\u4e8b\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5927\u559c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5c31\u662f\u2018\u4e03\u5999\u4eba\u2019\u4e2d\u7684\u2018\u5999\u90ce\u4e2d\u2019\uff0c\u539f\u6765\u6bd2\u836f\u5c31\u662f\u4ed6\u914d\u7684\uff0c\u80fd \u914d\u81ea\u7136\u80fd\u89e3\uff0c\u5c11\u7237\u4f60\u6709\u6551\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4e00\u4e2a\u4eba\u60f3\u6d3b\u56fa\u7136\u8270\u82e6\uff0c\u82e5\u8981\u9759\u9759\u5730\u6b7b\uff0c\u4e5f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u9a6c\u8f66\u53c8\u5957\u4e0a\u4e86\u9a6c\uff0c\u5192\u96ea\u6025\u9a70\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u4ed6\u4eec\u5374\u53e6\u5916\u96c7\u4e86\u4e2a\u8d76\u8f66\u7684\uff0c\u866c\u9aef\u5927\u6c49\u7559\u5728\u8f66\u53a2\u4e2d\u4e00\u6765\u662f\u4e3a\u4e86\u7167\u987e\u674e\u5bfb\u6b22\uff0c\u518d\u6765\u4e5f \u662f\u4e3a\u4e86\u76d1\u89c6\u90a3\u2018\u5999\u90ce\u4e2d\u2019\u3002 \u3000\u3000\u4ed6\u663e\u7136\u8fd8\u662f\u4e0d\u653e\u5fc3\uff0c\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u65e2\u80fd\u89e3\u6bd2\uff0c\u4e3a\u4f55\u8981\u53bb\u627e\u522b\u4eba\u3002\u53bb\u627e\u8c01\u3002\u53bb\u54ea\u91cc \u3002\u6765\u5f97\u53ca\u5417\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u76b1\u7740\u7709\u9053\uff1a\u201c\u6211\u627e\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u662f\u6885\u5148\u751f\uff0c\u6211\u5bb6\u8001\u5927\uff0c\u4ed6\u5c31\u5728\u9644\u8fd1\uff0c\u4f60\u653e\u5fc3\uff0c \u6885\u4e8c\u5148\u751f\u80af\u63a5\u624b\u7684\u75c5\u4eba\uff0c\u5c31\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u4e3a\u4f55\u8981\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u56e0\u4e3a\u5bd2\u9e21\u6563\u7684\u89e3\u836f\u5728\u4ed6\u90a3\u91cc\uff0c\u8fd9\u7406\u7531\u4f60\u6ee1\u610f\u4e86\u4e48\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u8fd9\u624d\u95ed\u4e0a\u5634\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u6447\u7740\u5934\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e16\u4e0a\u8fd8\u6709\u4eba\u80af\u7ec3\u8fd9\u79cd\u7b28\u529f\u592b\uff0c\u9664\u4e86\u80fd\u552c\u552c\u90a3\u4e9b\u6bdb\u8d3c\u5916\uff0c \u7b80\u76f4\u8fde\u4e00\u70b9\u7528\u5904\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u51b7\u51b7\u9053\uff1a\u201c\u7b28\u529f\u592b\u603b\u6bd4\u6ca1\u529f\u592b\u597d\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u5c45\u7136\u4e5f\u4e0d\u751f\u6c14\uff0c\u8fd8\u662f\u6447\u7740\u5934\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u7ec3\u94c1\u5e03\u886b\u4e00\u5b9a\u8981\u7ae5\u5b50\u529f\uff0c\u8fd9\u727a\u7272\u672a\u514d \u592a\u5927\u4e86\u4e9b\uff0c\u662f\u5417\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u54fc\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u636e\u8bf4\u8fd1\u4e94\u5341\u5e74\u6765\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u80af\u4e0b\u82e6\u529f\u7ec3\u8fd9\u79cd\u7b28\u529f\u592b\uff0c\u636e\u8bf4\u6b64\u4eba\u53eb\u2018\u94c1 \u7532\u91d1\u521a\u2019\u94c1\u4f20\u7532\uff0c\u4f46\u4e8c\u5341\u5e74\u524d\u5c31\u88ab\u4eba\u4e00\u638c\u81ea\u820d\u8eab\u5d16\u4e0a\u9707\u4e0b\u53bb\u4e86\uff0c\u4e5f\u4e0d\u77e5\u6b7b\u4e86\u6ca1\u6709\uff0c\u4e5f\u8bb8\u5e76\u6ca1 \u6709\u6b7b\uff0c\u8fd8\u80fd\u5750\u7740\u559d\u9152\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7684\u5634\u89d2\u5c31\u8c61\u662f\u54ac\u7262\u4e86\u4e2a\u9e21\u722a\uff0c\u65e0\u8bba\u6885\u4e8c\u5148\u751f\u600e\u4e48\u8bf4\uff0c\u600e\u4e48\u95ee\uff0c\u4ed6\u5374\u518d\u4e5f\u4e0d\u80af\u5f00 \u53e3\u4e86\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u4e5f\u53ea\u597d\u95ed\u8d77\u773c\u775b\uff0c\u517b\u8d77\u795e\u6765\u3002 \u3000\u3000\u8c01\u77e5\u8fc7\u4e86\u534a\u664c\uff0c\u866c\u9aef\u5927\u6c49\u53c8\u5f00\u59cb\u95ee\u4ed6\u4e86\uff0c\u9053\uff1a\u201c\u636e\u8bf4\u2018\u4e03\u5999\u4eba\u2019\u4e2a\u4e2a\u90fd\u662f\u4e0d\u5927\u8981\u8138\u7684\u89d2 \u8272\uff0c\u4f46\u9601\u4e0b\u770b\u6765\u5374\u4e0d\u8c61\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u95ed\u7740\u773c\u9053\uff1a\u201c\u62ff\u4e86\u4eba\u5bb6\u7684\u8bca\u91d1\uff0c\u4e0d\u66ff\u4eba\u6cbb\u75c5\uff0c\u8fd9\u96be\u9053\u8fd8\u8981\u8138\u4e86\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u80af\u66ff\u90a3\u79cd\u4eba\u6cbb\u75c5\uff0c\u624d\u662f\u771f\u4e0d\u8981\u8138\u3002\u62ff\u94b1\u548c\u6cbb\u75c5\u672c\u6765\u5c31\u662f\u4e24\u56de\u4e8b\uff0c \u90a3\u79cd\u4eba\u7684\u94b1\u6b63\u662f\u4e0d\u62ff\u767d\u4e0d\u62ff\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u4eba\u5012\u5e76\u4e0d\u592a\u7b28\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u53f9\u9053\uff1a\u201c\u4e16\u4eba\u773c\u4e2d\u7684\u5c0f\u4eba\uff0c\u56fa\u7136\u672a\u5fc5\u5168\u90fd\u662f\u5c0f\u4eba\uff0c\u4e16\u4eba\u773c\u4e2d\u7684\u541b\u5b50\uff0c\u53c8\u6709\u51e0\u4e2a \u662f\u771f\u541b\u5b50\u5462\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u659c\u501a\u5728\u8f66\u5ea7\u4e0a\uff0c\u5634\u89d2\u5e26\u7740\u6de1\u6de1\u7684\u5fae\u7b11\uff0c\u4eff\u4f5b\u5728\u542c\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u53c8\u4eff\u4f5b\u65e9\u5df2\u795e\u6e38\u7269\u5916 \uff0c\u4e00\u9897\u5fc3\u65e9\u5df2\u4e0d\u77e5\u98de\u5230\u54ea\u91cc\u53bb\u3002 \u3000\u3000\u4eba\u95f4\u7684\u6c61\u79fd\uff0c\u4f3c\u4e4e\u5df2\u5168\u90fd\u88ab\u96ea\u82b1\u6d17\u51c0\uff0c\u81ea\u8f66\u7a97\u4e2d\u671b\u51fa\u53bb\uff0c\u5929\u5730\u4e00\u7247\u94f6\u767d\uff0c\u80fd\u6d3b\u7740\uff0c\u6bd5\u7adf \u8fd8\u662f\u4ef6\u597d\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u53c8\u51fa\u73b0\u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u5979\u7a7f\u7740\u6d45\u7d2b\u8272\u7684\u8863\u670d\uff0c\u62ab\u7740\u6d45\u7d2b\u8272\u7684\u98ce\u6c05\uff0c\u5728\u4e00\u7247\u94f6\u767d\u4e2d\u770b\u6765\uff0c\u5c31\u8c61\u662f\u4e00\u6735\u6e05\u4e3d\u7d2b\u7f57\u5170 \u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u5979\u6700\u559c\u6b22\u96ea\uff0c\u4e0b\u96ea\u7684\u65f6\u5019\uff0c\u5979\u5e38\u5e38\u62c9\u7740\u4ed6\u5230\u79ef\u96ea\u7684\u9662\u5b50\u91cc\u53bb\uff0c\u629b\u4e00\u56e2\u96ea\u7403\u5728\u4ed6\u8eab \u4e0a\uff0c\u7136\u540e\u518d\u5a07\u7b11\u7740\u9003\u8d70\uff0c\u53eb\u4ed6\u53bb\u8ffd\u5979\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u90a3\u5929\u4ed6\u5e26\u9f99\u5578\u4e91\u56de\u53bb\u7684\u65f6\u5019\uff0c\u4e5f\u5728\u4e0b\u7740\u96ea\uff0c\u5979\u6b63\u5750\u5728\u6885\u6797\u7554\u7684\u4ead\u5b50\u91cc\uff0c\u770b\u6885\u82b1\u4e0a \u7684\u96ea\u82b1\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u90a3\u4ead\u5b50\u7684\u680f\u6746\u662f\u7ea2\u7684\uff0c\u6885\u82b1\u4e5f\u662f\u7ea2\u7684\uff0c\u4f46\u5979\u5750\u5728\u680f\u6746\u4e0a\uff0c\u6885\u82b1\u548c\u680f\u6746\u5168\u90fd\u5931\u53bb\u4e86 \u989c\u8272\u3002 \u3000\u3000\u4ed6\u5f53\u65f6\u6ca1\u6709\u89c1\u5230\u9f99\u5578\u4e91\u7684\u8868\u60c5\uff0c\u4f46\u540e\u6765\u4ed6\u5374\u53ef\u60f3\u50cf\u5f97\u5230\uff0c\u9f99\u5578\u4e91\u81ea\u7136\u7b2c\u4e00\u6b21\u770b\u5230\u5979\u65f6\uff0c \u5fc3\u795e\u5c31\u5df2\u9189\u4e86\u3002 \u3000\u3000\u73b0\u5728\uff0c\u90a3\u5ead\u9662\u662f\u5426\u4ecd\u4f9d\u65e7\u3002\u5979\u662f\u5426\u8fd8\u65f6\u5e38\u5750\u5728\u5c0f\u4ead\u7684\u680f\u6746\u4e0a\uff0c\u6570\u6885\u82b1\u4e0a\u7684\u96ea\u82b1\uff0c\u96ea\u82b1\u4e0b \u7684\u6885\u82b1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ac\u5934\u5411\u6885\u4e8c\u5148\u751f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8f66\u4e0a\u6709\u9152\uff0c\u6211\u4eec\u559d\u4e00\u676f\u5427\u3002\u201d \u3000\u3000\u96ea\uff0c\u65f6\u843d\u65f6\u505c\u3002 \u3000\u3000\u8f66\u9a6c\u5728\u6885\u4e8c\u5148\u751f\u7684\u6307\u6325\u4e0b\uff0c\u8f6c\u5165\u4e86\u4e00\u6761\u5c71\u811a\u4e0b\u7684\u5c0f\u9053\uff0c\u8d70\u5230\u4e00\u5ea7\u5c0f\u6865\u524d\uff0c\u5c31\u901a\u4e0d\u8fc7\u53bb\u4e86 \u3002 \u3000\u3000\u5c0f\u6865\u4e0a\u79ef\u96ea\u5982\u65b0\uff0c\u770b\u4e0d\u5230\u4eba\u7684\u8db3\u8ff9\uff0c\u53ea\u6709\u4e00\u884c\u9ec4\u72ac\u7684\u811a\u5370\uff0c\u8c61\u4e00\u8fde\u4e32\u6885\u82b1\u4f3c\u7684\u6d12\u5728\u680f\u6746 \u65c1\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u6276\u7740\u674e\u5bfb\u6b22\u8d70\u8fc7\u5c0f\u6865\uff0c\u5c31\u671b\u89c1\u5728\u6885\u6811\u4e1b\u4e2d\uff0c\u6709\u4e09\u4e94\u77f3\u5c4b\uff0c\u7ea2\u82b1\u767d\u5c4b\uff0c\u98ce\u7269\u5b9b\u5982 \u56fe\u753b\u3002 \u3000\u3000\u6885\u6797\u4e2d\u9690\u9690\u6709\u4eba\u58f0\u4f20\u6765\uff0c\u8d70\u5230\u8fd1\u524d\uff0c\u4ed6\u4eec\u5c31\u89c1\u5230\u4e00\u4e2a\u5ce8\u670d\u9ad8\u51a0\u7684\u8001\u4eba\uff0c\u6b63\u5728\u6307\u6325\u7740\u4e24\u4e2a \u7ae5\u5b50\u6d17\u6811\u4e0a\u7684\u51b0\u96ea\u3002 \u3000\u3000\u866c\u9aef\u5927\u6c49\u6084\u58f0\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6885\u5927\u5148\u751f\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u9664\u4e86\u8fd9\u75af\u5b50\uff0c\u8fd8\u4f1a\u6709\u8c01\u7528\u6c34\u6765\u6d17\u51b0\u96ea\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u4e5f\u4e0d\u7981\u5931\u7b11\u9053\uff1a\u201c\u4ed6\u96be\u9053\u4e0d\u77e5\u9053\u6d17\u8fc7\u4e4b\u540e\uff0c\u96ea\u8fd8\u662f\u8981\u843d\u5728\u6811\u4e0a\uff0c\u6c34\u4e5f\u7acb\u523b\u5c31\u4f1a \u7ed3\u6210\u51b0\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u53ef\u4ee5\u5206\u8fa8\u51fa\u4efb\u4f55\u4e00\u5e45\u753b\u7684\u771f\u4f2a\uff0c\u53ef\u4ee5\u914d\u51fa\u6700\u5389\u5bb3\u7684\u6bd2 \u836f\u548c\u89e3\u836f\uff0c\u4f46\u8fd9\u79cd\u6700\u7b80\u5355\u7684\u9053\u7406\uff0c\u4ed6\u5374\u6c38\u8fdc\u4e5f\u5f04\u4e0d\u61c2\u7684\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\u4f20\u5165\u6885\u6797\uff0c\u90a3\u9ad8\u51a0\u8001\u4eba\u56de\u5934\u770b\u5230\u4e86\u4ed6\u4eec\uff0c\u5c31\u597d\u8c61\u770b\u5230\u4e86\u8ba8\u503a\u9b3c\u4f3c\u7684\uff0c\u7acb \u523b\u5927\u60ca\u5931\u8272\uff0c\u64a9\u8d77\u4e86\u8863\u895f\uff0c\u5c31\u5f80\u91cc\u9762\u8dd1\uff0c\u4e00\u9762\u8fd8\u5927\u547c\u9053\uff1a\u201c\u5feb\uff0c\u5feb\uff0c\u5feb\uff0c\u5feb\u628a\u5385\u91cc\u7684\u5b57\u753b\u5168 \u90fd\u6536\u8d77\u6765\uff0c\u83ab\u8981\u53c8\u88ab\u8fd9\u8d25\u5bb6\u5b50\u770b\u5230\u4e86\uff0c\u5077\u51fa\u53bb\u6362\u9ec4\u6c64\u559d\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u7b11\u9053\uff1a\u201c\u8001\u5927\u4f60\u53ea\u7ba1\u653e\u5fc3\uff0c\u4eca\u5929\u6211\u5df2\u627e\u5230\u4e86\u9152\u4e1c\uff0c\u53ea\u4e0d\u8fc7\u7279\u5730\u5e26\u4e86\u4e24\u4e2a\u670b\u53cb\u6765 \u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6885\u5927\u5148\u751f\u5df2\u7528\u624b\u8499\u8d77\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8981\u770b\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u7684\u670b\u53cb\u8fde\u4e00\u4e2a\u597d \u4eba\u4e5f\u6ca1\u6709\uff0c\u53ea\u8981\u770b\u4e00\u773c\uff0c\u6211\u81f3\u5c11\u5c31\u8981\u5012\u4e09\u5e74\u7684\u9709\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u4e5f\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u770b\u4e0d\u8d77\u6211\uff0c\u6211\u96be\u9053\u5c31\u4e0d\u80fd\u4ea4\u4e0a\u4e2a\u8c61\u6837\u7684\u670b\u53cb\u4e48 \u3002\u597d\u597d\u597d\uff0c\u674e\u63a2\u82b1\uff0c\u4ed6\u65e2\u7136\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u54b1\u4eec\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6b63\u5728\u7740\u6025\u5730\u95ee\uff1a\u201c\u89e3\u836f\u672a\u5f97\uff0c\u600e\u4e48\u80fd\u8d70\u5462\u3002\u201d \u3000\u3000\u8c01\u77e5\u6885\u5927\u5148\u751f\u8fd9\u6b21\u53cd\u800c\u56de\u5934\u8d70\u4e86\u8fc7\u6765\uff0c\u62db\u624b\u9053\uff1a\u201c\u6162\u8d70\u6162\u8d70\uff0c\u4f60\u8bf4\u7684\u53ef\u662f\u4e00\u95e8\u4e03\u8fdb\u58eb\uff0c \u7236\u5b50\u4e09\u53f9\u82b1\u7684\u5c0f\u674e\u63a2\u82b1\u4e48\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u8ba4\u5f97\u7b2c\u4e09\u4e2a\u674e\u63a2\u82b1\u4e0d\u6210\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u76ef\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4f4d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5728\u4e0b\u6b63\u662f\u674e\u5bfb\u6b22\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u4e0a\u4e0a\u4e0b\u4e0b\u671b\u4e86\u4ed6\u51e0\u773c\uff0c\u5ffd\u7136\u4e00\u628a\u62c9\u4f4f\u4ed6\u7684\u624b\uff0c\u5927\u7b11\u9053\uff1a\u201c\u6155\u540d\u4e8c\u5341\u5e74\uff0c\u4e0d\u60f3\u4eca \u65e5\u7ec8\u4e8e\u89c1\u5230\u4f60\u4e86\uff0c\u674e\u5144\u5440\uff0c\u674e\u5144\uff0c\u4f60\u53ef\u771f\u662f\u60f3\u715e\u5c0f\u5f1f\u4e5f\uff01\u201d \u3000\u3000\u4ed6\u524d\u5028\u540e\u606d\uff0c\u5ffd\u7136\u53d8\u5f97\u5982\u6b64\u70ed\u60c5\uff0c\u674e\u5bfb\u6b22\u53cd\u800c\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u6885\u5927\u5148\u751f\u5df2\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u674e\u90ce\u4f11\u602a\u5c0f\u5f1f\u65b9\u624d\u5931\u793c\uff0c\u53ea\u56e0\u6211\u7740\u5144\u5f1f\u5b9e\u5728\u592a\u4e0d\u6210\u6750\uff0c\u4e24 \u5e74\u524d\u5e26\u4e86\u4e2a\u4eba\u56de\u6765\uff0c\u786c\u8bf4\u662f\u9274\u5b9a\u4e66\u753b\u7684\u6cd5\u5bb6\uff0c\u8981\u6211\u5c06\u85cf\u753b\u5c3d\u62ff\u51fa\u6765\u7ed9\u4ed6\u77a7\u77a7\uff0c\u8c01\u77e5\u4ed6\u4eec\u5374\u7528 \u4e24\u5377\u767d\u7eb8\uff0c\u6362\u4e86\u6211\u4e24\u5e45\u66f9\u4e0d\u5174\u7684\u7cbe\u54c1\u8dd1\u4e86\uff0c\u5bb3\u5f97\u6211\u4e09\u4e2a\u6708\u7761\u4e0d\u7740\u89c9\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u201c\u6885\u5927\u5148\u751f\u4e5f\u4f11\u8981\u602a\u4ed6\uff0c\u9152\u763e\u53d1\u4f5c\u65f6\u82e5\u65e0\u94b1\u6253\u9152\uff0c\u90a3\u6ecb\u5473\u786e\u4e0d\u597d\u53d7\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u674e\u5144\u60f3\u5fc5\u4e5f\u662f\u6b64\u9053\u4e2d\u4eba\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u5929\u5b50\u547c\u6765\u4e0d\u4e0a\u8239\uff0c\u81ea\u9053\u81e3\u662f\u9152\u4e2d\u4ed9\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u7b11\u9053\uff1a\u201c\u597d\u597d\u597d\uff0c\u9a91\u9e64\uff0c\u5148\u83ab\u6d17\u6885\u82b1\uff0c\u5feb\u53bb\u5c06\u90a3\u4e24\u575b\u5df2\u85cf\u4e86\u4e8c\u5341\u5e74\u7684\u7af9\u53f6\u9752\u53d6 \u51fa\uff0c\u8bf7\u674e\u63a2\u82b1\u54c1\u5c1d\u54c1\u5c1d\u3002\u201d \u3000\u3000\u4ed6\u542b\u7b11\u63d6\u5ba2\uff0c\u53c8\u9053\uff1a\u201c\u597d\u82b1\u8d60\u4f73\u4eba\uff0c\u597d\u9152\u5f85\u540d\u58eb\uff0c\u5728\u4e0b\u8fd9\u4e24\u575b\u9152\u7a96\u85cf\u4e8c\u5341\u5e74\uff0c\u4e3a\u7684\u5c31\u662f \u8981\u7559\u7740\u6b3e\u5f85\u674e\u5144\u8fd9\u6837\u7684\u5927\u540d\u58eb\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u8fd9\u8bdd\u5012\u4e0d\u5047\uff0c\u522b\u7684\u5ba2\u4eba\u6765\uff0c\u4ed6\u83ab\u8bf4\u4e0d\u80af\u4ee5\u9152\u76f8\u5f85\uff0c\u7b80\u76f4\u8fde\u58f6\u918b\u90fd\u6ca1\u6709\uff0c \u53ea\u4e0d\u8fc7\uff0c\u674e\u5144\u6b64\u6765\uff0c\u5374\u5e76\u975e\u6765\u559d\u9152\u7684\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u53ea\u77a7\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u5c31\u7b11\u9053\uff1a\u201c\u5bd2\u9e21\u4e4b\u6bd2\uff0c\u53ea\u4e0d\u8fc7\u662f\u5c0f\u4e8b\u4e00\u4ef6\u800c\u5df2\uff0c\u674e\u5144\u53ea\u7ba1 \u5f00\u6000\u7545\u996e\uff0c\u8fd9\u4ef6\u4e8b\u5728\u4e0b\u81ea\u6709\u5b89\u6392\u7684\u3002\u201d \u3000\u3000\u8349\u5802\u4e2d\u81ea\u7136\u7cbe\u96c5\uff0c\u7a96\u85cf\u4e8c\u5341\u5e74\u7684\u7af9\u53f6\u9752\u4e5f\u6781\u9999\u51bd\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u6885\u5927\u5148\u751f\u5ffd\u7136\u9053\uff1a\u201c\u636e\u8bf4\u5927\u5185\u6240\u85cf\u7684\u2018\u6e05\u660e\u4e0a\u6cb3\u56fe\u2019\uff0c\u4ea6\u4e3a\u81ba\u54c1\uff0c\u771f\u8ff9\u5374\u5728 \u5c0a\u5e9c\uff0c\u6b64\u8bdd\u4e0d\u77e5\u662f\u771f\u662f\u5047\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u624d\u77e5\u9053\u4ed6\u6bb7\u52e4\u5f85\u5ba2\uff0c\u5176\u610f\u5728\u6b64\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u5012\u4e5f\u4e0d\u5047\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u5927\u559c\u9053\uff1a\u201c\u674e\u5144\u82e5\u80af\u5c06\u4e4b\u501f\u6765\u4e00\u89c2\uff0c\u5728\u4e0b\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6885\u5927\u5148\u751f\u65e2\u7136\u6709\u610f\uff0c\u5728\u4e0b\u5c82\u6709\u4e0d\u80af\u4e4b\u7406\uff0c\u53ea\u53ef\u60dc\uff0c\u5728\u4e0b\u4e5f\u662f\u4e2a\u8d25\u5bb6\u5b50\uff0c\u5341 \u5e74\u524d\u4fbf\u5df2\u5c06\u5bb6\u8d22\u8361\u5c3d\uff0c\u8fde\u8fd9\u5e45\u753b\u4e5f\u65e9\u5df2\u9001\u4eba\u4e86\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u5750\u5728\u90a3\u91cc\uff0c\u8fde\u52a8\u90fd\u4e0d\u4f1a\u52a8\u4e86\uff0c\u770b\u6765\u5c31\u8c61\u662f\u88ab\u4eba\u7528\u68cd\u5b50\u5728\u5934\u4e0a\u91cd\u91cd\u6572\u4e86\u4e00\u4e0b\uff0c\u5634 \u91cc\u4e0d\u4f4f\u5583\u5583\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u4e86\u5341\u51e0\u58f0\u53ef\u60dc\uff0c\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u9a91\u9e64\uff0c\u5feb\u5c06\u5269\u4e0b\u7684\u9152\u518d\u85cf \u8d77\u6765\uff0c\u674e\u63a2\u82b1\u5df2\u559d\u591f\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u76b1\u7709\u9053\uff1a\u201c\u6ca1\u6709\u2018\u6e05\u660e\u4e0a\u6cb3\u56fe\u2019\uff0c\u5c31\u6ca1\u6709\u9152\u559d\u4e86\u4e48\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8fd9\u9152\u672c\u6765\u5c31\u4e0d\u662f\u8bf7\u4eba\u559d\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u975e\u4f46\u4e0d\u751f\u6c14\uff0c\u53cd\u800c\u7b11\u4e86\uff0c\u4ed6\u89c9\u5f97\u8fd9\u4eba\u867d\u7136\u53c8\u5b64\u50fb\uff0c\u53c8\u5c0f\u6c14\uff0c\u4f46\u7387\u6027\u5929\u771f\uff0c\u81f3\u5c11\u4e0d \u662f\u4e2a\u4f2a\u541b\u5b50\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u5374\u5df2\u6c89\u4e0d\u4f4f\u6c14\uff0c\u8df3\u8d77\u6765\u5927\u559d\u9053\uff1a\u201c\u6ca1\u6709\u2018\u6e05\u660e\u4e0a\u6cb3\u56fe\u2019\uff0c\u8fde\u89e3\u836f\u4e5f\u6ca1\u6709\u4e86\u4e48\u3002 \u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u9707\u5f97\u5c4b\u9876\u90fd\u51e0\u4e4e\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6885\u5927\u5148\u751f\u5374\u662f\u9762\u4e0d\u6539\u8272\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fde\u9152\u90fd\u6ca1\u6709\u4e86\uff0c\u54ea\u6709\u4ec0\u4e48\u89e3\u836f\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u52c3\u7136\u5927\u6012\uff0c\u4f3c\u4e4e\u5c31\u60f3\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u62e6\u4f4f\u4e86\u4ed6\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u6885\u5927\u5148\u751f\u4e0e\u6211\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u672c\u6765\u5c31\u4e0d\u662f\u5b9a\u8981\u5c06\u89e3\u836f\u9001\u7ed9 \u6211\u4eec\u7684\uff0c\u6211\u5df2\u53e8\u6270\u4e86\u4eba\u5bb6\u7684\u7f8e\u9152\uff0c\u600e\u53ef\u518d\u5bf9\u4e3b\u4eba\u65e0\u793c\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u560e\u58f0\u9053\uff1a\u201c\u53ef\u662f\u5c11\u7237\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6325\u4e86\u6325\u624b\uff0c\u957f\u63d6\u7b11\u9053\uff1a\u201c\u6068\u672a\u9022\u541b\u6709\u5c3d\u65f6\uff0c\u5728\u4e0b\u7b49\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u8c01\u77e5\u6885\u5927\u5148\u751f\u53cd\u800c\u53c8\u8d70\u4e86\u56de\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u89e3\u836f\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u7269\u5404\u6709\u4e3b\uff0c\u5728\u4e0b\u4ece\u6765\u4e0d\u613f\u5f3a\u6c42\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u82e5\u6ca1\u6709\u89e3\u836f\uff0c\u4f60\u7684\u547d\u4e5f\u6ca1\u6709\u4e86\u4e48\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u751f\u6b7b\u6709\u547d\uff0c\u5728\u4e0b\u5012\u4e5f\u4ece\u672a\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u9519\u4e0d\u9519\uff0c\u8fde\u2018\u6e05\u660e\u4e0a\u6cb3\u56fe\u2019\u90fd\u820d\u5f97\u9001\u4eba\uff0c\u4f55\u51b5\u81ea\u5df1 \u7684\u6027\u547d\u3002\u8fd9\u6837\u7684\u4eba\u5012\u4e5f\u5929\u4e0b\u5c11\u6709\uff0c\u5929\u4e0b\u5c11\u6709\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u5927\u58f0\u9053\uff1a\u201c\u9a91\u9e64\uff0c\u518d\u628a\u9152\u7aef\u51fa\u6765\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u89e3\u836f\u5462\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6709\u4e86\u9152\uff0c\u8fd8\u4f1a\u6ca1\u6709\u89e3\u836f\u3002\u201d \u7b2c\u4e03\u7ae0\u3000\u8bef\u4f24\u6545\u4eba\u5b50>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u7ae0\u3000\u8bef\u4f24\u6545\u4eba\u5b50 \u3000\u3000\u674e\u5bfb\u6b22\u559d\u4e86\u9152\uff0c\u89e3\u836f\u7684\u836f\u529b\u53d1\u52a8\u5f97\u66f4\u5feb\uff0c\u8fd8\u4e0d\u5230\u516d\u4e2a\u65f6\u8fb0\uff0c\u674e\u5bfb\u6b22\u5df2\u89c9\u5f97\u4f53\u529b\u6e10\u6e10\u6062\u590d \u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u521a\u7834\u6653\uff0c\u866c\u7136\u5927\u6c49\u867d\u71ac\u4e86\u4e00\u591c\uff0c\u4f46\u4eba\u9022\u559c\u4e8b\u7cbe\u795e\u723d\uff0c\u53ea\u4e0d\u8fc7\u9152\u559d\u5f97\u592a\u591a\u4e86\uff0c\u5934\u6709 \u4e9b\u75bc\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u4e5f\u7528\u624b\u6342\u4f4f\u8111\u888b\uff0c\u5583\u5583\u9053\uff1a\u201c\u8be5\u6b7b\u8be5\u6b7b\uff0c\u5929\u53c8\u4eae\u4e86\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u9053\uff1a\u201c\u5929\u4eae\u4e86\u6709\u4f55\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u53f9\u9053\uff1a\u201c\u6211\u559d\u9152\u5c31\u6015\u5929\u4eae\uff0c\u82e5\u662f\u5929\u4e0d\u4eae\uff0c\u6211\u4e00\u76f4\u559d\u4e0b\u53bb\u90fd\u6ca1\u5173\u7cfb\uff0c\u4f46\u53ea\u8981\u5929\u4e00 \u4eae\uff0c\u5c31\u4f1a\u7acb\u523b\u5934\u75db\uff0c\u8fde\u9152\u4e5f\u559d\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u672c\u5728\u95ed\u76ee\u517b\u795e\uff0c\u6b64\u523b\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5c82\u6b62\u9601\u4e0b\uff0c\u559d\u9152\u7684\u4eba\u53ea\u6015\u90fd\u6709\u8fd9\u4e2a\u6bdb\u75c5\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8d81\u7740\u5929\u8fd8\u672a\u5927\u4eae\uff0c\u8d76\u5feb\u518d\u559d\u51e0\u676f\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4f60\u6211\u5982\u6b64\u725b\u996e\uff0c\u5927\u5148\u751f\u89c1\u4e86\u53ea\u6015\u8981\u5fc3\u75bc\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u65e9\u5df2\u8eb2\u8fdb\u53bb\u7761\u89c9\u4e86\uff01\u4e50\u5f97\u773c\u4e0d\u89c1\uff0c\u5fc3\u4e0d\u70e6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u559d\u4e86\u676f\u9152\uff0c\u53c8\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u51dd\u6ce8\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u54b3\u55fd\u7684\u6bdb\u75c5\uff0c\u5df2\u6709\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u597d\u8c61\u5df2\u6709\u5341\u5e74\u4e86\u5427\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u76b1\u7709\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8fd8\u662f\u83ab\u8981\u559d\u9152\u7684\u597d\uff0c\u4e45\u54b3\u5fc5\u4f24\u80ba\uff0c\u518d\u559d\u9152\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4f24\u80ba\uff1f\u6211\u8fd8\u6709\u80ba\u53ef\u4f24\u4e48\uff1f\u6211\u7684\u80ba\u65e9\u5df2\u70c2\u5149\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u76ee\u4e2d\u7cbe\u5149\u95ea\u52a8\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u95f4\u53ea\u6015\u53c8\u6709\u8fdc\u5ba2\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u52a8\u5bb9\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\u6765\u7684\u7edd\u4e0d\u4f1a\u662f\u8001\u5927\u7684\u5ba2\u4eba\uff0c\u53ea\u6015\u53c8\u662f\u6765\u627e\u6211\u7684\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u76f4\u7b49\u5230\u73b0\u5728\u624d\u542c\u5230\u5c4b\u5916\u4f20\u6765\u4e00\u9635\u6c99\u6c99\u7684\u811a\u6b65\u58f0\uff0c\u6765\u7684\u4eba\u4f3c\u4e4e\u5e76\u4e0d\u53ea\u4e00\u4e2a\uff0c\u5e03\u5c65\u90fd \u5f88\u8f7b\u5065\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u6717\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u91cc\u53ef\u662f\u6885\u82b1\u8349\u5802\u4e48\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5c31\u542c\u5f97\u6885\u5927\u5148\u751f\u7684\u8bed\u58f0\u5728\u524d\u5385\u54cd\u8d77\uff0c\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\u7684\u95ef\u6765\uff0c\u662f\u5c0f\u5077\u8fd8\u662f\u5f3a \u76d7\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u7b49\u4e13\u7a0b\u6765\u8bbf\uff0c\u4e0d\u4f46\u975e\u5077\u975e\u76d7\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u4efd\u8584\u793c\u5949\u4e0a\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u51b7\u7b11\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\u7684\u6765\u9001\u793c\uff0c\u663e\u7136\u66f4\u6ca1\u6709\u5b58\u597d\u5fc3\uff0c\u5404\u4f4d\u8fd8\u662f\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5728\u4e0b\u7b49\u53ea\u597d\u5c06\u8fd9\u5e45\u738b\u6469\u8bd8\u7684\u753b\u5e26\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u95e8\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u51e0\u4eba\u5148\u6478\u900f\u8001\u5927\u7684\u813e\u6c14\uff0c\u6295\u5176\u6240\u597d\u800c\u6765\uff0c\u5fc5\u6709\u6240\u6c42\uff0c\u6211\u4eec\u770b\u770b\u4ed6 \u4eec\u5230\u5e95\u662f\u54ea\u4e00\u8def\u7684\u4eba\u9a6c\u3002\u201d \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u8d70\u51fa\u53bb\uff0c\u53ea\u5c06\u95e8\u63a8\u5f00\u4e00\u73b0\uff0c\u6084\u6084\u5f80\u5916\u671b\u3002 \u3000\u3000\u53ea\u89c1\u6765\u7684\u4e00\u5171\u6709\u4e09\u4e2a\u4eba\uff0c\u4e00\u4eba\u53ea\u6709\u4e09\u5341\u591a\u5c81\uff0c\u77ed\u5c0f\u7cbe\u608d\uff0c\u76ee\u5149\u70af\u70af\uff0c\u624b\u91cc\u6258\u7740\u4e2a\u957f\u957f\u7684 \u6728\u5323\u5b50\u3002 \u3000\u3000\u7b2c\u4e8c\u4eba\u9762\u5982\u91cd\u67a3\uff0c\u957f\u9aef\u8fc7\u8179\uff0c\u62ab\u7740\u4ef6\u7d2b\u7f0e\u56e2\u82b1\u5927\u6c05\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u76ee\u5351\u7768\u81ea\u96c4\uff0c\u663e\u7136\u662f\u4e2a \u60ef\u4e8e\u53d1\u53f7\u65bd\u4ee4\u7684\u4eba\u7269\u3002 \u3000\u3000\u7b2c\u4e09\u4eba\u5374\u662f\u4e2a\u5341\u6765\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u5706\u5706\u7684\u8138\uff0c\u5706\u5706\u7684\u773c\u775b\uff0c\u7ea2\u6597\u84ec\u4e0a\u9576\u7740\u767d\u5154\u6bdb\u7684\u8fb9\uff0c\u770b \u6765\u5c31\u8c61\u662f\u4e2a\u7c89\u88c5\u7389\u7422\u7684\u7ea2\u5b69\u513f\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u5176\u4f59\u4e24\u4eba\u7709\u76ee\u95f4\u90fd\u5e26\u7740\u5fe7\u95f7\u7126\u6025\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u7cbe\u608d\u6c49\u5b50\u624b\u6258\u6728\u5323\uff0c\u4e00\u8fdb\u6765\u5c31\u8eac\u8eab\u7b11\u9053\uff1a\u201c\u6b64\u753b\u4e43\u662f\u853d\u4e3b\u4eba\u91cd\u91d1\u8d2d\u6765\uff0c\u5df2\u7ecf\u540d\u5bb6\u9274\u5b9a \uff0c\u786e\u662f\u771f\u7ee9\uff0c\u8bf7\u6885\u5927\u5148\u751f\u8fc7\u76ee\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u7684\u773c\u775b\u65e9\u5df2\u76ef\u5728\u5323\u5b50\u4e0a\u4e86\uff0c\u5634\u91cc\u5374\u9053\uff1a\u201c\u65e0\u529f\u4e0d\u53d7\u7984\uff0c\u4f60\u4eec\u8981\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u7b49\u53ea\u6c42\u6885\u5927\u5148\u751f\u6307\u70b9\u4e00\u6761\u660e\u8def\uff0c\u627e\u5230\u6885\u4e8c\u5148\u751f\u3002\u201d \u3000\u3000\u6885\u5927\u5148\u751f\u7acb\u523b\u677e\u4e86\u53e3\u6c14\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u5bb9\u6613\u3002\u201d \u3000\u3000\u4ed6\u4e00\u628a\u5c06\u5323\u5b50\u62a2\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u51fa\u6765\u5427\uff0c\u6709\u4eba\u6765\u627e\u4f60\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u5934\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6709\u4e86\u738b\u6469\u8bd8\uff0c\u8fde\u5144\u5f1f\u90fd\u4e0d\u8981\u4e86\u3002\u201d \u3000\u3000\u7d2b\u888d\u8001\u4eba\u548c\u7cbe\u608d\u6c49\u5b50\u89c1\u5230\u6885\u4e8c\u5148\u751f\uff0c\u90fd\u5df2\u559c\u52a8\u989c\u8272\uff0c\u53ea\u6709\u90a3\u7ea2\u5b69\u513f\u5374\u76f4\u76b1\u7709\u5934\uff0c\u7785\u7740\u6885 \u4e8c\u5148\u751f\u9053\uff1a\u201c\u8fd9\u4eba\u770b\u6765\u8d43\u516e\u516e\u7684\uff0c\u771f\u4f1a\u6cbb\u75c5\u4e48\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u563b\u7684\u4e00\u7b11\u9053\uff1a\u201c\u5927\u75c5\u6cbb\u4e0d\u4e86\uff0c\u5c0f\u75c5\u6b7b\u4e0d\u4e86\uff0c\u9a6c\u9a6c\u864e\u864e\u8fd8\u8fc7\u7684\u53bb\u3002\u201d \u3000\u3000\u7d2b\u888d\u8001\u4eba\u4f3c\u4e4e\u4e5f\u6015\u8fd9\u5b69\u5b50\u518d\u4e71\u8bf4\u8bdd\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u7b49\u4e45\u95fb\u9601\u4e0b\u56de\u6625\u4e4b\u5999\u624b\uff0c \u662f\u4ee5\u7279\u6765\u76f8\u8bf7\u9601\u4e0b\u968f\u6211\u7b49\u4e00\u884c\uff0c\u8bca\u91d1\u65e0\u8bba\u591a\u5c11\uff0c\u6211\u4eec\u90fd\u53ef\u5148\u4ed8\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fde\u6211\u7684\u813e\u6c14\u90fd\u6478\u6e05\u695a\u4e86\uff0c\u4f46\u4f60\u4e0d\u6015\u6211\u8dd1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7d2b\u888d\u8001\u4eba\u6c89\u7740\u8138\u4e0d\u8bf4\u8bdd\uff0c\u5374\u5df2\u65e0\u5f02\u5728\u8bf4\u6015\u8fd9\u5b69\u5b50\u518d\u4e71\u8bf4\u8bdd\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u7b49 \u4e45\u95fb\u9601\u4e0b\u56de\u6625\u4e4b\u5999\u624b\uff0c\u662f\u4ee5\u7279\u6765\u76f8\u8bf7\u9601\u4e0b\u968f\u6211\u7b49\u4e00\u884c\uff0c\u8bca\u91d1\u65e0\u8bba\u591a\u5c11\uff0c\u6211\u4eec\u90fd\u53ef\u5148\u4ed8\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fde\u6211\u7684\u813e\u6c14\u90fd\u6478\u6e05\u695a\u4e86\uff0c\u4f46\u4f60\u4e0d\u6015\u6211\u8dd1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7d2b\u888d\u8001\u4eba\u6c89\u7740\u8138\u4e0d\u8bf4\u8bdd\uff0c\u5374\u5df2\u65e0\u5f02\u5728\u8bf4\uff1a\u201c\u4f60\u8dd1\u4e0d\u4e86\u7684\uff01\u201d \u3000\u3000\u90a3\u77ed\u5c0f\u6c49\u5b50\u7acb\u523b\u966a\u7b11\u9053\uff1a\u201c\u53ea\u8981\u6885\u4e8c\u5148\u751f\u80af\u53bb\uff0c\u9664\u4e86\u5e94\u4ed8\u7684\u8bca\u91d1\u5916\uff0c\u5728\u4e0b\u7b49\u8fd8\u53e6\u6709\u91cd\u916c \u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u9664\u4e86\u8bca\u91d1\u5148\u4ed8\u5916\uff0c\u4f60\u53ef\u77e5\u9053\u6885\u4e8c\u5148\u751f\u8fd8\u6709\u4e09\u4e0d\u6cbb\uff1f\u5f3a\u76d7\u4e0d\u6cbb\uff0c\u5c0f\u5077\u4e0d\u6cbb \uff01\u201d \u3000\u3000\u90a3\u77ed\u5c0f\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5df4\u82f1\uff0c\u867d\u662f\u65e0\u540d\u5c0f\u5352\uff0c\u4f46\u8fd9\u4f4d\u79e6\u5b5d\u4eea\u79e6\u8001\u7237\u5b50\u5728\u6c5f\u6e56\u4e2d\u7684\u4fa0\u540d \uff0c\u6885\u4e8c\u5148\u751f\u591a\u5c11\u603b\u8be5\u6709\u4e9b\u8033\u95fb\u5427\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u79e6\u5b5d\u4eea\uff1f\u53ef\u662f\u94c1\u80c6\u9707\u516b\u65b9\u79e6\u5b5d\u4eea\uff1f\u201d \u3000\u3000\u5df4\u82f1\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u6b63\u662f\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u4eba\u7684\u540d\u5934\u5012\u7684\u786e\u4e0d\u5c0f\uff0c\u597d\uff0c\u8fc7\u51e0\u5929\u4f60\u4eec\u518d\u6765\u5427\uff0c\u5230\u65f6 \u6211\u82e5\u6709\u7a7a\uff0c\u4e5f\u8bb8\u4f1a\u8ddf\u4f60\u4eec\u8d70\u8fd9\u4e00\u8d9f\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u7ea2\u5b69\u513f\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u5927\u7684\u67b6\u5b50\uff0c\u6211\u4eec\u8ddf\u4ed6\u7f57\u55e6\u4ec0\u4e48\uff0c\u628a \u4ed6\u67b6\u56de\u53bb\u4e0d\u5c31\u5b8c\u4e86\u4e48\u3002\u201d \u3000\u3000\u5df4\u82f1\u8d76\u7d27\u62c9\u4f4f\u4e86\u4ed6\uff0c\u966a\u7b11\u9053\uff1a\u201c\u82e5\u662f\u75c5\u4e0d\u6025\uff0c\u8fc7\u4e24\u5929\u672c\u65e0\u59a8\uff0c\u53ef\u662f\u75c5\u4eba\u53d7\u7684\u4f24\u5b9e\u5728\u592a\u91cd \uff0c\u83ab\u8bf4\u8fdf\u51e0\u5929\uff0c\u53ea\u6015\u8fde\u51e0\u4e2a\u65f6\u8fb0\u90fd\u8fdf\u4e0d\u5f97\u7684\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u4f60\u4eec\u7684\u75c5\u4eba\u8981\u7d27\uff0c\u6211\u8fd9\u91cc\u7684\u75c5\u4eba\u96be\u9053\u5c31\u4e0d\u8981\u7d27\uff1f\u201d \u3000\u3000\u5df4\u82f1\u9053\uff1a\u201c\u6885\u4e8c\u5148\u751f\u8fd9\u91cc\u4e5f\u6709\u4f4d\u75c5\u4eba\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u5c06\u4ed6\u7684\u75c5\u6cbb\u597d\uff0c\u6211\u7edd\u4e0d\u80fd\u8d70\u7684\u3002\u201d \u3000\u3000\u5df4\u82f1\u6123\u4e86\u6123\uff0c\u5450\u5450\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u6211\u4eec\u90a3\u8fb9\u7684\u662f\u79e6\u8001\u7237\u5b50\u7684\u5927\u5c11\u7237\uff0c\u4e5f\u662f\u5f53\u4eca\u5c11\u6797\u9986\u5ea7 \u552f\u4e00\u7684\u4fd7\u5bb6\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u4e5f\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u79e6\u5b5d\u4eea\u7684\u513f\u5b50\u53c8\u600e\u6837\uff1f\u5c11\u6797\u548c\u5c1a\u7684\u5f92\u5f1f\u53c8\u600e\u6837\uff1f\u96be\u9053\u4ed6\u7684 \u547d\u5c31\u80fd\u6bd4\u6211\u8fd9\u75c5\u4eba\u7684\u547d\u503c\u94b1\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5b5d\u4eea\u5df2\u662f\u6ee1\u9762\u6012\u5bb9\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u90a3\u7ea2\u5b69\u513f\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u8fd9\u75c5\u4eba\u82e5\u662f\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u6b7b\u4e86\u81ea\u7136\u7528\u4e0d\u7740\u6211\u518d\u6cbb\uff0c\u53ea\u53ef\u60dc\u4ed6\u6b7b\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u563b\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5012\u672a\u5fc5\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u679d\u7bad\u4f3c\u7684\u7a9c\u5165\u4e86\u9694\u58c1\u7684\u5c4b\u5b50\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u8fde\u5c4b\u91cc\u7684\u866c\u9aef\u5927\u6c49\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5df4\u82f1 \u671b\u4e86\u79e6\u5b5d\u4eea\u4e00\u773c\uff0c\u4e24\u4eba\u5c45\u7136\u90fd\u6ca1\u6709\u963b\u62e6\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u7a9c\u5230\u5c4b\u91cc\uff0c\u773c\u775b\u5c31\u77aa\u5728\u91cc\u5bfb\u6b22\u8eab\u4e0a\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5c31\u662f\u90a3\u75c5\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u96be\u9053\u60f3\u6211\u5feb\u4e9b\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u201c\u4e00\u70b9\u513f\u4e5f\u4e0d\u9519\uff0c\u4f60\u6b7b\u4e86\uff0c\u90a3\u810f\u9b3c\u624d\u80af\u53bb\u66ff\u79e6\u5927\u54e5\u6cbb\u75c5\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u8896\u4e2d\u5df2\u98de\u51fa\u4e09\u6839\u5f88\u5c0f\u7684\u8896\u7bad\uff0c\u76f4\u53d6\u674e\u5bfb\u6b22\u7684\u9762\u76ee\u548c\u54bd\u5589\uff0c\u4e0d\u4f46\u5947\u5feb\u5947\u51c6 \uff0c\u800c\u4e14\u52b2\u9053\u5341\u8db3\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u770b\u6765\u5341\u5c81\u8fd8\u4e0d\u5230\u7684\u5c0f\u5b69\u5b50\uff0c\u7adf\u662f\u5982\u6b64\u5fc3\u72e0\u624b\u8fa3\uff0c\u82e5\u975e\u674e\u5bfb\u6b22\uff0c\u6362\u4e86\u522b\u4eba\u53ea \u6015\u7acb\u523b\u5c31\u4f1a\u6b7b\u5728\u4ed6\u7684\u7bad\u4e0b\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u53ea\u4e00\u4f38\u624b\uff0c\u8fd9\u4e09\u679d\u7bad\u4fbf\u5df2\u5230\u4e86\u4ed6\u624b\u91cc\uff0c\u76b1\u7709\u9053\uff1a\u201c\u5c0f\u5b69\u513f\u5df2\u5982\u6b64\u72e0\u6bd2\uff0c\u957f\u5927\u4e86 \u90a3\u8fd8\u5f97\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u81ea\u5df1\u6709\u4e86\u4e24\u624b\u6349\u7bad\u7684\u529f\u592b\uff0c\u5c31\u53ef\u4ee5\u6559\u8bad\u6211\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u51cc\u7a7a\u4e00\u7ffb\uff0c\u624b\u91cc\u5df2\u591a\u4e86\u4e24\u67c4\u7cbe\u5149\u56db\u5c04\u7684\u77ed\u5251\uff0c\u4e0d\u7b49\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u5df2\u95ea\u7535\u822c\u5411\u674e \u5bfb\u6b22\u523a\u51fa\u4e86\u4e03\u62db\u3002\u201d \u3000\u3000\u8fd9\u5b69\u5b50\u4e0d\u4f46\u51fa\u62db\u5feb\uff0c\u53d8\u62db\u5feb\uff0c\u800c\u4e14\u51fa\u624b\u4e4b\u72e0\u6bd2\uff0c\u5c31\u7b97\u591a\u5e74\u7684\u8001\u6c5f\u6e56\u4e5f\u8981\u81ea\u6127\u4e0d\u5982\uff0c\u6bcf\u4e00 \u62db\u51fa\u624b\uff0c\u90fd\u597d\u8c61\u548c\u5bf9\u65b9\u6709\u7740\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\u4f3c\u7684\uff0c\u6068\u4e0d\u5f97\u4e00\u5251\u5c31\u5c06\u674e\u5bfb\u6b22\u523a\u51fa\u4e2a\u5927\u7a9f\u7abf\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5b69\u5b50\u957f\u5927\u4e86\u53c8\u662f\u4e2a\u9634\u65e0\u6781\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6d53\u7709\u7d27\u76b1\uff0c\u9053\uff1a\u201c\u9634\u65e0\u6781\u867d\u6709\u2018\u8840\u5251\u2019\u4e4b\u540d\uff0c\u7f3a\u8fd8\u4e0d\u80af\u5fd8\u6740\u65e0\u8f9c\uff0c\u4f46\u8fd9\u5b69\u5b50\u2026 \u2026\u201d \u3000\u3000\u7ea2\u5b69\u513f\u51b7\u7b11\u9053\uff1a\u201c\u9634\u65e0\u6781\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u6211\u4e03\u5c81\u65f6\u5df2\u6740\u8fc7\u4eba\u4e86\uff0c\u4ed6\u5462\uff1f\u201d \u3000\u3000\u4ed6\u89c1\u5230\u674e\u5bfb\u6b22\u4ecd\u7136\u5750\u5728\u90a3\u91cc\uff0c\u4f46\u4ed6\u8fde\u53d8\u4e86\u4e03\u516b\u79cd\u6bd2\u8fa3\u7684\u5251\u62db\uff0c\u4ecd\u65e0\u6cd5\u4f24\u5f97\u4e86\u522b\u4eba\uff0c\u4e0b\u624b \u66f4\u6bd2\uff0c\u66f4\u72e0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9634\u65e0\u6781\u5e74\u5e7c\u65f6\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4ed6\u5982\u6b64\u72e0\u6bd2\u3002\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u5b50\u957f\u5927\uff0c\u5fc5\u662f\u6b66\u6797\u4e2d\u4e00\u4e2a\u5927\u7978\u5bb3\uff0c\u4e0d\u5982\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53ea\u662f\u6709\u4e9b\u4e0d\u5fcd\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u8fde\u653b\u4e00\u767e\u62db\u5c24\u672a\u5f97\u624b\uff0c\u4e5f\u77e5\u9053\u4eca\u5929\u9047\u89c1\u4e86\u96be\u60f9\u7684\u4eba\u7269\uff0c\u8fde\u773c\u775b\u90fd\u6025\u7ea2\u4e86\uff0c\u54ac\u7740\u7259 \u9053\uff1a\u201c\u4f60\u4eec\u53ef\u77e5\u9053\u6211\u7236\u6bcd\u662f\u8c01\u4e48\uff1f\u53ea\u8981\u4f60\u4eec\u6562\u4f24\u6211\u4e00\u6839\u6beb\u6bdb\uff0c\u4ed6\u4eec\u4e0d\u5c06\u4f60\u4eec\u4e71\u5200\u5206\u5c38\uff0c\u5927\u5378 \u516b\u5757\u624d\u602a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u53ea\u51c6\u4f60\u6740\u4eba\uff0c\u522b\u4eba\u5374\u4e0d\u80fd\u4f24\u4f60\uff1f\u201d \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u201c\u53ea\u8981\u4f60\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\uff0c\u6740\u4e86\u6211\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u6b64\u523b\u8fd8\u4e0d\u613f\u51fa\u624b\uff0c\u53ea\u56e0\u4f60\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u82e5\u6709\u4eba\u4e25\u52a0\u7ba1\u675f\uff0c \u8fd8\u53ef\u6210\u5668\uff0c\u8d81\u6211\u8fd8\u672a\u6539\u53d8\u4e3b\u610f\u65f6\uff0c\u4f60\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u4e5f\u77e5\u9053\u81ea\u5df1\u662f\u4e07\u96be\u5f97\u624b\u7684\u4e86\uff0c\u4e00\u62db\u6536\u5251\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u771f\u4e0d\u9519\uff0c\u4e0d\u77e5\u9053 \u4f60\u7a76\u7adf\u662f\u8c01\u5440\uff1f\u6211\u600e\u4e48\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4f60\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u95ee\u6e05\u6211\u7684\u59d3\u540d\uff0c\u96be\u9053\u8fd8\u60f3\u62a5\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u5b69\u513f\u8138\u4e0a\u9732\u51fa\u4e86\u5929\u771f\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u9976\u4e86\u6211\u7684\u547d\uff0c\u6211\u600e\u4e48\u8fd8\u4f1a\u62a5\u4ec7\u5462\uff1f\u6211\u53ea\u4e0d\u8fc7\u771f \u4f69\u670d\u4f60\uff0c\u6211\u4e00\u5171\u523a\u51fa\u4e86\u4e00\u767e\u96f6\u4e03\u5251\uff0c\u4f60\u5374\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u5ffd\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u5b66\uff1f\u201d \u3000\u3000\u7ea2\u5b69\u513f\u5927\u559c\u9053\uff1a\u201c\u4f60\u80af\u6536\u6211\u505a\u5f92\u5f1f\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u6211\u82e5\u80fd\u66ff\u4f60\u7236\u6bcd\u7ba1\u6559\u7ba1\u6559\u4f60\uff0c\u4f60\u4ee5\u540e\u4e5f\u8bb8\u8fd8\u6709\u5e0c\u671b\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5df2\u62dc\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5e08\u5085\u5728\u4e0a\uff0c\u8bf7\u53d7\u5f92\u513f\u4e00\u62dc\u3002\u201d \u3000\u3000\u8fd9\u2018\u62dc\u2019\u5b57\u521a\u51fa\u53e3\uff0c\u53c8\u662f\u4e09\u9053\u4e4c\u5149\u81ea\u4ed6\u80cc\u5f8c\u6025\u5c04\u800c\u51fa\uff0c\u7adf\u662f\u5de7\u624b\u7cbe\u5236\u7684\u2018\u7d27\u80cc\u4f4e\u5934\u82b1\u88c5 \u5f29\u2019\uff01 \u3000\u3000\u8fd9\u5b69\u5b50\u5c45\u7136\u5168\u8eab\u90fd\u662f\u6697\u5668\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u6b21\u624d\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u82e5\u975e\u8eab\u7ecf\u767e\u6218\uff0c\u53cd\u5e94\u5947\u8fc5\uff0c\u8fd9\u4e00\u6b21\u53ea\u6015\u4e5f\u8981\u4f24\u5728\u8fd9\u6076\u6bd2\u7684\u7ae5 \u5b50\u624b\u91cc\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53c8\u6325\u624b\u6251\u4e86\u8fc7\u53bb\uff0c\u5927\u9a82\u9053\uff1a\u201c\u4f60\u7b97\u4ec0\u4e48\u4e1c\u897f\uff1f\u4e5f\u914d\u66ff\u6211\u7236\u6bcd\u7ba1\u6559\u6211\uff0c \u4e5f\u914d\u6536\u6211\u8fd9\u4e2a\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u866c\u9aef\u5927\u6c49\u9762\u7b3c\u5bd2\u971c\uff0c\u5386\u58f0\u9053\uff1a\u201c\u6b64\u5b50\u5929\u6027\u6076\u6bd2\uff0c\u8c7a\u72fc\u4e4b\u5fc3\uff0c\u7559\u4e0d\u5f97\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u8fd4\u624b\u4e00\u638c\u6325\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u79e6\u5b5d\u4eea\u548c\u5df4\u82f1\u660e\u660e\u5df2\u77e5\u9053\u7ea2\u5b69\u513f\u5728\u91cc\u9762\u8981\u6740\u4eba\uff0c\u4f46\u4e24\u4eba\u8fd8\u5fc3\u5b89\u7406\u5f97\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u7eb9\u4e1d\u4e0d \u52a8\u3002 \u3000\u3000\u6885\u5927\u5148\u751f\u770b\u90a3\u5e45\u753b\u66f4\u5df2\u770b\u5f97\u75f4\u4e86\uff0c\u522b\u7684\u4e8b\u4ed6\u5168\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5e26\u6765\u7684\u5c0f\u5b69\u5b50\u8981\u6740\u4eba\uff0c\u4f60\u4eec\u4e5f\u4e0d\u7ba1\u4e48\uff1f\u201d \u3000\u3000\u5df4\u82f1\u644a\u5f00\u53cc\u624b\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u8bdd\uff0c\u8fd9\u5b69\u5b50\u7684\u4e8b\u8c01\u4e5f\u7ba1\u4e0d\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u88ab\u4eba\u6740\u4e86\uff0c\u4f60\u4eec\u7ba1\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u5df4\u82f1\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u770b\u4f60\u4eec\u5982\u6b64\u653e\u5fc3\uff0c\u663e\u7136\u662f\u8ba4\u4e3a\u4ed6\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u53ea\u6709\u6740\u4eba\uff0c\u7edd\u4e0d\u4f1a\u88ab\u4eba\u6740 \u6b7b\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5df4\u82f1\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u8fd9\u5b69\u5b50\u7684\u6b66\u529f\u7684\u786e\u8fd8\u8fc7\u5f97\u53bb\uff0c\u6709\u5f88\u591a\u8001\u6c5f\u6e56\u90fd\u5df2\u683d\u5728\u4ed6\u624b \u4e0a\uff0c\u4f55\u51b5\u4ed6\u4e0d\u4f46\u6709\u4e2a\u597d\u7238\u7238\uff0c\u8fd8\u6709\u4e2a\u597d\u5988\u5988\uff0c\u522b\u4eba\u5403\u4e86\u4e8f\uff0c\u4e5f\u53ea\u6709\u8ba4\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u4ed6\u7236\u6bcd\u96be\u9053\u4e5f\u4e0d\u7ba1\u4e48\uff1f\u201d \u3000\u3000\u5df4\u82f1\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u806a\u660e\u7684\u513f\u5b50\uff0c\u505a\u7236\u6bcd\u7684\u600e\u4e48\u5fcd\u5fc3\u7ba1\u5f97\u592a\u4e25\u5462\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7236\u6bcd\u770b\u4ed6\u6740\u4e86\u4eba\uff0c\u8868\u9762\u4e0a\u8bf4\u4e0d\u5b9a\u4f1a\u9a82\u4e24\u53e5\uff0c\u5fc3\u91cc\u5374\u4e5f\u8bb8\u6bd4\u8c01\u90fd \u9ad8\u5174\uff0c\u53ef\u662f\u4ed6\u4eca\u5929\u9047\u89c1\u6211\u8fd9\u75c5\u4eba\uff0c\u53ea\u6015\u5c31\u8981\u5012\u9709\u4e86\u3002\u201d \u3000\u3000\u5df4\u82f1\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u9053\uff1a\u201c\u6211\u8fd9\u75c5\u4eba\u53ea\u8981\u4e00\u4f38\u624b\uff0c\u4ed6\u8fd9\u6761\u5c0f\u547d\u5c31\u7b97\u62a5\u9500\u4e86\u3002\u201d \u3000\u3000\u5df4\u82f1\u5931\u7b11\u9053\uff1a\u201c\u4e00\u4f38\u624b\u5c31\u80fd\u8981\u4ed6\u7684\u547d\uff1f\u8fd9\u8bdd\u6211\u4eec\u6709\u4e9b\u4e0d\u4fe1\uff0c\u4f60\u90a3\u75c5\u4eba\u96be\u9053\u8fd8\u80fd\u8c61\u674e\u63a2\u82b1 \u4e00\u6837\uff0c\u98de\u5200\u593a\u547d\uff0c\u4f8b\u4e0d\u865a\u53d1\u4e48\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u8bdd\uff0c\u6211\u8fd9\u75c5\u4eba\u6b63\u662f\u674e\u5bfb\u6b22\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5df4\u82f1\u7684\u8138\u7acb\u523b\u60e8\u767d\u5982\u7eb8\uff0c\u5e72\u7b11\u7740\u9053\uff1a\u201c\u9601\u4e0b\u4f60\u2026\u2026\u4f55\u5fc5\u5f00\u73a9\u7b11\uff1f \u3000\u3000\u6885\u4e8c\u5148\u751f\u60a0\u7136\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e3a\u4f55\u4e0d\u8fdb\u53bb\u77a7\u77a7\uff1f\u201d \u3000\u3000\u5df4\u82f1\u6123\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u51b2\u4e86\u8fdb\u53bb\uff0c\u560e\u58f0\u5927\u547c\u9053\uff1a\u201c\u674e\u63a2\u82b1\uff0c\u674e\u5927\u4fa0\uff0c\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u4e9b\u81ea\u547d\u4fa0\u4e49\u8f88\u7684\u5634\u8138\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u53ea\u6709\u81ea\u5df1\u513f\u5b50\u7684\u547d \u624d\u503c\u94b1\uff0c\u522b\u4eba\u7684\u547d\u5374\u6bd4\u72d7\u90fd\u4e0d\u5982\uff0c\u53ea\u8bb8\u81ea\u5df1\u7684\u513f\u5b50\u6740\u522b\u4eba\uff0c\u5374\u4e0d\u8bb8\u522b\u4eba\u6740\u4ed6\u3002 \u3000\u3000\u79e6\u5b5d\u4eea\u5a01\u4e25\u6c89\u91cd\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u6cdb\u8d77\u4e00\u4e1d\u6076\u6bd2\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4f46\u4ed6\u5c3d\u91cf\u5c06\u8fd9\u79cd\u7b11\u5bb9\u538b\u5236\u63a9\u9970\u7740\uff0c\u5374\u957f\u53f9\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u82e5\u771f\u7684\u6740\u4e86\u90a3\u5b69\u5b50\uff0c\u4ed6\u53ea\u6015\u5c31\u9057 \u61be\u7ec8\u751f\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u638c\u6325\u51fa\uff0c\u770b\u6765\u5e76\u6ca1\u6709\u4ec0\u4e48\u5947\u8be1\u7684\u53d8\u5316\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u5e74\u7eaa\u867d\u5c0f\uff0c\u4e0e\u4eba\u4ea4\u624b\u65f6\u5374\u8001\u5230\u5f97\u51fa\u5947\uff0c\u773c\u770b\u8fd9\u4e00\u638c\u62cd\u6765\uff0c\u7adf\u7136\u4e0d\u907f\u4e0d\u95ea\uff0c\u4ed6\u7adf\u7b97 \u5b9a\u4e86\u5bf9\u65b9\u8fd9\u4e00\u62db\u5fc5\u662f\u865a\u62db\uff0c\u771f\u6b63\u7684\u6740\u624b\u5fc5\u7136\u8fd8\u5728\u540e\u9762\uff0c\u6240\u4ee5\u4ed6\u53ea\u662f\u659c\u659c\u6311\u8d77\u4e86\u5251\u5c16\uff0c\u5982\u5c01\u4f3c \u907f\uff0c\u4e5f\u4ee5\u865a\u62db\u5e94\u5bf9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u4e00\u638c\u65e0\u8bba\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u4ed6\u5251\u52bf\u90fd\u53ef\u968f\u4e4b\u800c\u53d8\uff0c\u674e\u5bfb\u6b22\u8fd9\u4e00\u638c\u82e5\u662f\u5ffd\u7136\u53d8\u4e3a\u5b9e\u62db \uff0c\u4ed6\u8fd9\u4e00\u5251\u4e5f\u53ef\u53d8\u4e3a\u5b9e\u62db\uff0c\u4e58\u52bf\u6d1e\u7a7f\u674e\u5bfb\u6b22\u7684\u624b\u8155\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u7528\u5f97\u5f53\u771f\u5389\u5bb3\u5df2\u6781\uff0c\u90e8\u4f4d\u3001\u65f6\u95f4\u3001\u529b\u9053\u3001\u65e0\u4e00\u4e0d\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u6c5f\u6e56\u4e2d\u7684\u5251 \u624b\u80fd\u4f7f\u5f97\u51fa\u8fd9\u79cd\u62db\u5f0f\u6765\u7684\u4eba\u771f\u8fd8\u4e0d\u591a\uff0c\u663e\u7136\u8fd9\u5b69\u5b50\u975e\u4f46\u5f97\u5230\u4e86\u540d\u5bb6\u7684\u6307\u70b9\uff0c\u800c\u4e14\u5929\u751f\u5c31\u662f\u7ec3 \u6b66\u7684\u597d\u6750\u6599\u3002 \u3000\u3000\u8981\u77e5\u6b66\u529f\u62db\u5f0f\uff0c\u867d\u53ef\u5f97\u81ea\u5e08\u4f20\uff0c\u4f46\u4e34\u654c\u65f6\u7684\u5e94\u53d8\u548c\u5224\u65ad\uff0c\u5374\u662f\u8c01\u4e5f\u4f20\u6388\u4e0d\u4e86\u7684\uff0c\u6b63\u662f\u2018 \u8fd0\u7528\u4e4b\u5999\uff0c\u5b58\u4e4e\u4e00\u5fc3\u2019\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u4eca\u65e5\u7684\u5bf9\u624b\u662f\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u4e00\u638c\u5e76\u6ca1\u6709\u4efb\u4f55\u53d8\u5316\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u7684\u51fa\u624b\u5b9e\u5728\u592a\u5feb\u4e86\uff0c\u5feb\u5f97\u4ee4\u4eba\u6839\u672c\u65e0\u6cd5\u601d\u8bae\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u6240\u6709\u7684\u5bf9\u7b56\uff0c\u7adf\u5168\u90fd\u7528\u4e0d\u4e0a\uff0c\u7b49\u5230\u4ed6\u638c\u4e2d\u5251\u518d\u8981\u53bb\u523a\u674e\u5bfb\u6b22\u624b\u8155\u7684\u65f6\u5019\uff0c\u674e\u5bfb\u6b22 \u7684\u624b\u638c\u5df2\u62cd\u4e0a\u4e86\u4ed6\u80f8\u819b\u3002 \u3000\u3000\u4f46\u7ea2\u5b69\u513f\u5e76\u6ca1\u6709\u611f\u89c9\u5230\u75bc\u75db\uff0c\u4ed6\u53ea\u662f\u89c9\u5f97\u4e00\u80a1\u6696\u6d41\u81ea\u5bf9\u65b9\u7684\u638c\u5fc3\u4f20\u904d\u4e86\u4ed6\u5168\u8eab\uff0c\u5c31\u5b9b\u5982 \u4e25\u5bd2\u4e4b\u4e2d\u559d\u4e0b\u4e86\u4e00\u676f\u9999\u9187\u7684\u70ed\u9152\u3002 \u3000\u3000\u8fd9\u65f6\u5916\u9762\u624d\u4f20\u5165\u5df4\u82f1\u7126\u6025\u7684\u547c\u58f0\u3002 \u3000\u3000\u201c\u674e\u5927\u4fa0\uff0c\u624b\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u4f46\u7b49\u5230\u5df4\u82f1\u51b2\u8fdb\u6765\u65f6\uff0c\u7ea2\u5b69\u513f\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u53c8\u5b9b\u5982\u5927\u9189\u521d\u9192\uff0c\u5168\u8eab\u8f6f\u7ef5\u7ef5\u7684\u518d\u4e5f\u4f7f\u4e0d\u51fa \u4e1d\u6beb\u6c14\u529b\u3002 \u3000\u3000\u5df4\u82f1\u5931\u8272\u60ca\u547c\u9053\uff1a\u201c\u4e91\u5c0f\u7237\uff0c\u4f60\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u5b69\u513f\u663e\u7136\u4e5f\u5df2\u89c9\u51fa\u60c5\u51b5\u4e0d\u5999\uff0c\u773c\u5708\u513f\u90fd\u7ea2\u4e86\uff0c\u560e\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ea\u6015\u5df2\u906d\u4e86\u8fd9\u4eba\u7684 \u6bd2\u624b\uff0c\u4f60\u5feb\u53bb\u53eb\u7239\u7239\u6765\u66ff\u6211\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7ec8\u4e8e\u653e\u58f0\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u5df4\u82f1\u8dfa\u4e86\u8dfa\u811a\uff0c\u6ee1\u5934\u5927\u6c49\u5982\u96e8\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u6b66\u529f\u867d\u5df2\u88ab\u5e9f\uff0c\u4f46\u8fd9\u6761\u5c0f\u547d\u603b\u7b97\u7559\u4e0b\u6765\u4e86\uff0c\u53ea\u56e0\u6211\u5bb6\u5c11\u7237\u51fa \u624b\u65f6\u5ffd\u53c8\u52a8\u4e86\u601c\u60dc\u4e4b\u610f\uff0c\u82e5\u6362\u4e86\u662f\u6211\u2026\u2026\u54fc\uff01\u201d \u3000\u3000\u5df4\u82f1\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u5728\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u5386\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u590d\u4ec7\uff0c\u53ea\u7ba1\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u5df4\u82f1\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u5ffd\u7136\u5411\u674e\u5bfb\u6b22\u5657\u7684\u8dea\u5012\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53cd\u5012\u89c9\u5f97\u6709\u4e9b\u610f\u5916\u4e86\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u662f\u8fd9\u5b69\u5b50\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5df4\u82f1\u9053\uff1a\u201c\u5c0f\u4eba\u5df4\u82f1\uff0c\u674e\u63a2\u82b1\u867d\u4e0d\u8ba4\u5f97\u5c0f\u4eba\uff0c\u5c0f\u4eba\u5374\u8ba4\u5f97\u674e\u63a2\u82b1\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6211\u6700\u597d\uff0c\u4ed6\u7236\u6bcd\u82e5\u60f3\u590d\u4ec7\uff0c\u53eb\u4ed6\u4eec\u6765\u627e\u6211\u5c31\u662f\uff0c\u73b0\u5728\u4f60\u8d76\u5feb\u5e26 \u8fd9\u5b69\u5b50\u56de\u53bb\u5427\uff0c\u82e5\u662f\u8c03\u5236\u5f97\u6cd5\uff0c\u5c06\u6765\u867d\u4e0d\u80fd\u52a8\u6b66\uff0c\u884c\u52a8\u603b\u65e0\u59a8\u7684\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u201c\u54c7\u201d\u7684\u4e00\u58f0\u53c8\u5927\u54ed\u8d77\u6765\uff0c\u5657\u5730\u558a\u9053\uff1a\u201c\u597d\u72e0\u7684\u4eba\uff0c\u4f60\u7adf\u6562\u5e9f\u4e86\u6211\uff0c\u6211\u4e0d\u8981\u6d3b\u4e86 \u2026\u2026\u4e0d\u8981\u6d3b\u4e86\uff01\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u5386\u58f0\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u53eb\u4f60\u4ee5\u540e\u83ab\u8981\u518d\u968f\u610f\u51fa\u624b\u4f24\u4eba\u800c\u5df2\uff0c\u4f60\u4e5f\u8bb8\u53cd\u800c\u53ef\u4ee5\u56e0 \u6b64\u6d3b\u5f97\u957f\u4e9b\uff0c\u5426\u5219\u4f3c\u4f60\u8fd9\u822c\u5fc3\u72e0\u624b\u8fa3\uff0c\u8fdf\u65e9\u5fc5\u906d\u6a2a\u7978\u65e0\u7591\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6740\u624b\u65e0\u60c5\u7684\u674e\u63a2\u82b1\uff0c\u4e3a\u4f55\u81f3\u4eca\u8fd8\u672a\u906d\u6a2a\u6b7b\u5462\uff1f\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u6012\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u7d2b\u9762\u957f\u9aef\u7684\u8001\u4eba\uff0c\u7f13\u7f13\u8d70\u4e86\u8fdb\u6765\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\uff0c\u674e\u63a2\u82b1\u5c31\u4e0d\u8ba4\u5f97\u6545\u4eba\u4e86\u4e48\uff1f \u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u76b1\u7740\u7709\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u2018\u94c1\u80c6\u9707\u516b\u65b9\u2019\u79e6\u5927\u4fa0\uff0c\u8fd9\u5c31\u96be\u602a\u8fd9\u5b69\u5b50 \u6562\u968f\u610f\u6740\u4eba\u4e86\uff0c\u6709\u79e6\u5927\u4fa0\u6491\u8170\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u6740\u4e0d\u5f97\uff01\u201d \u3000\u3000\u79e6\u5b5d\u4eea\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u6740\u7684\u4eba\uff0c\u53ea\u6015\u8fd8\u4e0d\u53ca\u674e\u5144\u4e00\u534a\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u79e6\u5927\u4fa0\u5012\u4e5f\u4e0d\u5fc5\u592a\u8c26\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5728\u4e0b\u82e5\u6740\u4e86\u4eba\uff0c\u4fbf\u662f\u51b7\u9177\u6bd2\u8fa3\uff0c\u9601\u4e0b\u6740\u4e86 \u4eba\uff0c\u4fbf\u662f\u66ff\u5929\u884c\u9053\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4eca\u65e5\u8fd9\u5b69\u5b50\u82e5\u6740\u4e86\u5728\u4e0b\uff0c\u65e5\u540e\u4f20\u8bf4\u51fa\u53bb\uff0c\u5fc5\u7136\u4e0d\u4f1a\u8bf4\u4ed6\u662f\u4e3a\u4e86 \u8981\u62a2\u5927\u592b\u800c\u6740\u4eba\u7684\uff0c\u5fc5\u5b9a\u8981\u8bf4\u4ed6\u548c\u79e6\u5927\u4fa0\u53c8\u4e3a\u6c5f\u6e56\u9664\u4e86\u4e00\u5bb3\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5b5d\u4eea\u7eb5\u7136\u8001\u7ec3\u6c89\u7a33\uff0c\u6b64\u523b\u8138\u4e0a\u4e5f\u4e0d\u89c9\u6709\u4e9b\u53d1\u7ea2\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u672c\u5df2\u542c\u5f97\u53d1\u6123\uff0c\u6b64\u523b\u53c8\u653e\u58f0\u5927\u54ed\u9053\uff1a\u201c\u79e6\u8001\u4f2f\uff0c\u4f60\u8001\u4eba\u5bb6\u8fd8\u4e0d\u51fa\u624b\u66ff\u6211\u62a5\u4ec7\u4e48\uff1f \u201d \u3000\u3000\u79e6\u5b5d\u4eea\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u662f\u522b\u4eba\u4f24\u4e86\u4f60\uff0c\u81ea\u7136\u6709\u4eba\u66ff\u4f60\u62a5\u4ec7\uff0c\u4f46\u674e\u63a2\u82b1\u4f24\u4e86\u4f60\uff0c\u4f60\u6050 \u6015\u53ea\u6709\u8ba4\u547d\u4e86\u3002\u201d \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u201c\u4e3a\u2026\u2026\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5b5d\u4eea\u6a2a\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4f24\u4f60\u7684\u4eba\u662f\u8c01\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u5b69\u513f\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u662f\u4e2a\u5fc3\u9ed1\u624b\u8fa3\u7684\u6076\u5f92\uff01\u201d \u3000\u3000\u79e6\u5b5d\u4eea\u76ee\u4e2d\u53c8\u9732\u51fa\u4e00\u4e1d\u6076\u6bd2\u7684\u7b11\u610f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u540d\u52a8\u516b\u8868\u7684\u2018\u5929\u4e0b\u7b2c\u4e00\u5200\u2019\u674e\u5bfb \u6b22\uff0c\u4e5f\u5c31\u662f\u4f60\u7239\u7239\u7684\u751f\u6b7b\u516b\u62dc\u4e4b\u4ea4\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u7ea2\u5b69\u513f\u56fa\u7136\u5446\u4f4f\u4e86\uff0c\u674e\u5bfb\u6b22\u66f4\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u4eba\u7684\u513f\u5b50 \uff1f\u201d \u3000\u3000\u5df4\u82f1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5c31\u662f\u9f99\u5578\u4e91\u9f99\u56db\u7237\u7684\u5927\u516c\u5b50\uff0c\u9f99\u5c0f\u4e91\uff01\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u674e\u5bfb\u6b22\u5b9b\u5982\u88ab\u5de8\u96f7\u8f70\u9876\uff0c\u9707\u6563\u4e86\u9b42\u9b44\uff01 \u3000\u3000\u4ed6\u6728\u7136\u5750\u5728\u90a3\u91cc\uff0c\u4e00\u53cc\u9510\u5229\u7684\u773c\u775b\u5df2\u53d8\u4e3a\u6b7b\u7070\u8272\uff0c\u773c\u89d2\u7684\u808c\u8089\u5728\u4e0d\u505c\u7684\u62bd\u7f29\u7740\uff0c\u4e00\u6ef4\u6ef4 \u51b7\u6c57\u6cbf\u7740\u9f3b\u6d3c\u6d41\u5230\u5634\u89d2\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u4ea6\u662f\u9762\u8272\u60e8\u53d8\uff0c\u6c57\u51fa\u5982\u6d46\u3002 \u3000\u3000\u53ea\u6709\u4ed6\u6700\u4e86\u89e3\u9f99\u5578\u4e91\u548c\u6797\u8bd7\u97f3\u592b\u59bb\u95f4\u7684\u5173\u7cfb\uff0c\u73b0\u5728\u674e\u5bfb\u6b22\u7adf\u4f24\u4e86\u4ed6\u4eec\u7684\u7231\u5b50\uff0c\u5176\u5fc3\u60c5\u4e4b \u6c89\u75db\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u5df4\u82f1\u53f9\u9053\uff1a\u201c\u8fd9\u771f\u662f\u60f3\u4e0d\u5230\u7684\u4e8b\uff0c\u53ea\u56e0\u79e6\u8001\u7237\u5b50\u7684\u5927\u516c\u5b50\u2018\u7389\u9762\u795e\u62f3\u2019\u79e6\u91cd\uff0c\u5728\u6355\u6349\u2018 \u6885\u82b1\u76d7\u2019\u65f6\uff0c\u4e0d\u5e78\u53d7\u4f24\uff0c\u867d\u4ed7\u7740\u5c11\u6797\u4f5b\u95e8\u5723\u836f\u2018\u5c0f\u8fd8\u4e39\u2019\u6682\u65f6\u4fdd\u4f4f\u4e86\u6027\u547d\uff0c\u4f46\u4ecd\u662f\u5371\u5728\u65e6\u5915 \uff0c\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u2018\u5999\u5927\u592b\u2019\u6885\u4e8c\u5148\u751f\u4e43\u5929\u4e0b\u6551\u6cbb\u5916\u4f24\u7684\u7b2c\u4e00\u628a\u597d\u624b\uff0c\u5c24\u5176\u5584\u4e8e\u6cbb\u7597\u5404\u79cd\u5916\u95e8 \u6697\u5668\uff0c\u662f\u4ee5\u79e6\u8001\u7237\u5b50\u624d\u8f97\u8f6c\u6253\u542c\u5230\u6885\u4e8c\u5148\u751f\u7684\u6d88\u606f\uff0c\u5bfb\u5230\u8fd9\u91cc\u6765\uff0c\u8c01\u77e5\u4e91\u5c11\u7237\u5e74\u8f7b\u6027\u6025\uff0c\u7adf \u51fa\u4e86\u8fd9\u4e2d\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u4e2a\u4eba\u5583\u5583\u81ea\u8bed\uff0c\u4e5f\u4e0d\u77e5\u6709\u6ca1\u6709\u4eba\u5728\u542c\u4ed6\u7684\u3002 \u3000\u3000\u6885\u4e8c\u5148\u751f\u6b64\u523b\u4f3c\u4e5f\u770b\u51fa\u674e\u5bfb\u6b22\u7684\u75db\u82e6\uff0c\u5148\u770b\u4e86\u770b\u7ea2\u5b69\u513f\u7684\u4f24\u52bf\uff0c\u53c8\u628a\u4e86\u628a\u4ed6\u7684\u8109\u606f\u624d\u7ad9 \u8d77\u6765\u9053\uff1a\u201c\u6211\u62c5\u4fdd\u8fd9\u5b69\u5b50\u975e\u4f46\u6027\u547d\u65e0\u788d\uff0c\u800c\u4e14\u4e00\u5207\u90fd\u4e0e\u5e38\u4eba\u65e0\u5f02\u3002\u201d \u3000\u3000\u5df4\u82f1\u5927\u559c\u9053\uff1a\u201c\u6b66\u529f\u5462\uff1f\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u51b7\u51b7\u9053\uff1a\u201c\u4e3a\u4f55\u5b9a\u8981\u4fdd\u5168\u6b66\u529f\uff1f\u96be\u9053\u4ed6\u65e5\u540e\u8fd8\u60f3\u6740\u4eba\u4e48\uff1f\u201d \u3000\u3000\u5df4\u82f1\u6123\u4e86\u534a\u664c\uff0c\u53f9\u9053\uff1a\u201c\u6885\u4e8c\u5148\u751f\u6709\u6240\u4e0d\u77e5\uff0c\u53ea\u56e0\u9f99\u56db\u7237\u53ea\u6709\u8fd9\u4e48\u4e00\u4f4d\u5c11\u7237\uff0c\u800c\u4e14\u53c8\u662f \u7ec3\u6b66\u7684\u5947\u6750\uff0c\u6240\u4ee5\u9f99\u56db\u7237\u592b\u5987\u4e24\u4f4d\u90fd\u5bf9\u4ed6\u671f\u671b\u5f88\u9ad8\uff0c\u5e0c\u671b\u4ed6\u5c06\u6765\u80fd\u5149\u5927\u95e8\u6963\uff0c\u82e5\u662f\u77e5\u9053\u4ed6\u4eec \u7684\u5b69\u5b50\u5df2\u4e0d\u80fd\u7ec3\u6b66\uff0c\u9f99\u56db\u7237\u592b\u5987\u771f\u4e0d\u77e5\u8be5\u600e\u4e48\u4f24\u5fc3\u4e86\u3002\u201d \u3000\u3000\u6885\u4e8c\u5148\u751f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u53ea\u80fd\u602a\u4ed6\u4eec\u7ba1\u6559\u4e0d\u4e25\uff0c\u7eb5\u5b50\u884c\u51f6\uff0c\u6028\u4e0d\u5f97\u522b\u4eba\uff01\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u674e\u5bfb\u6b22\u6839\u672c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u600e\u7684\uff0c\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u601d\u6f6e\u7adf\u53c8\u843d\u5165\u4e86\u56de\u5fc6\u4e2d\uff0c\u8bb8\u591a\u4e0d\u8be5\u60f3\u7684\u4e8b\uff0c\u6b64\u523b\u4ed6\u5168\u90fd\u60f3 \u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u90a3\u5929\u662f\u521d\u4e03\uff0c\u4ed6\u4e3a\u4e86\u4e00\u4ef6\u5f88\u91cd\u8981\u7684\u4e8b\uff0c\u6240\u4ee5\u6ca1\u6709\u8fc7\u5b8c\u5e74\u5c31\u4e00\u5b9a\u8981\u8d76\u7740\u51fa\u95e8\u5230\u53e3\u5916 \u53bb\u3002 \u3000\u3000\u90a3\u5929\u4e5f\u5728\u4e0b\u7740\u96ea\uff0c\u6797\u8bd7\u97f3\u7279\u522b\u4e3a\u4ed6\u505a\u4e86\u4e00\u684c\u5f88\u7cbe\u81f4\u7684\u9152\u83dc\uff0c\u5728\u5979\u81ea\u5df1\u7684\u5c0f\u9662\u4e2d\u966a\u4ed6\u996e\u9152 \u8d4f\u96ea\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4ece\u5c0f\u5c31\u662f\u5728\u4ed6\u4eec\u5bb6\u957f\u5927\u7684\uff0c\u5979\u7684\u7236\u4eb2\uff0c\u662f\u674e\u5bfb\u6b22\u7236\u4eb2\u7684\u59bb\u8205\uff0c\u4e24\u4f4d\u8001\u4eba\u5bb6\u6ca1\u6709\u6b7b \u7684\u65f6\u5019\uff0c\u65e9\u5df2\u8bf4\u5b9a\u8981\u4eb2\u4e0a\u52a0\u4eb2\u4e86\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u548c\u6797\u8bd7\u97f3\u5e76\u6ca1\u6709\u8c61\u4e00\u4e9b\u4e16\u4fd7\u7684\u5c0f\u513f\u5973\u90a3\u6837\u56e0\u907f\u8bb3\u800c\u758f\u8fdc\uff0c\u4ed6\u4eec\u4e0d\u4f46\u662f\u60c5\u4eba\uff0c\u4e5f \u662f\u5f88\u597d\u7684\u670b\u53cb\u3002 \u3000\u3000\u867d\u7136\u8fc7\u4e86\u5341\u5e74\uff0c\u674e\u5bfb\u6b22\u8fd8\u662f\u6e05\u6e05\u695a\u695a\u8bb0\u5f97\u90a3\u4e00\u5929\u3002 \u3000\u3000\u90a3\u5929\u7684\u6885\u82b1\u5f00\u5f97\u597d\u7f8e\uff0c\u5979\u5e26\u7740\u4e09\u5206\u9189\u610f\u7684\u7b11\u989c\u5374\u6bd4\u6885\u82b1\u66f4\u7f8e\uff0c\u90a3\u5929\u771f\u662f\u51b2\u6ee1\u4e86\u5e78\u798f\u548c\u6b22 \u4e50\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4e0d\u5e78\u7684\u4e8b\u7acb\u523b\u5c31\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u53e3\u5916\u56de\u6765\u65f6\uff0c\u4ed6\u7684\u4ec7\u5bb6\u7adf\u52fe\u7ed3\u4e86\u5f53\u5730\u51f6\u540d\u6700\u76db\u7684\u2018\u5173\u5916\u4e09\u51f6\u2019\u5728\u90af\u90f8\u5927\u9053\u4e0a\u5411\u4ed6\u5939 \u51fb\u3002 \u3000\u3000\u4ed6\u867d\u624b\u5203\u4e86\u5341\u4e5d\u4eba\uff0c\u4f46\u6700\u540e\u5374\u4e5f\u5df2\u91cd\u4f24\u4e0d\u652f\uff0c\u773c\u89c1\u5c31\u8981\u4f24\u5728\u5927\u51f6\u535c\u9738\u7684\u4e00\u53cc\u5582\u6bd2\u8de8\u864e\u84dd \u4e4b\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u9f99\u5578\u4e91\u6765\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4ee5\u4e00\u67c4\u94f6\u67aa\u6d3b\u6311\u4e86\u535c\u9738\uff0c\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u53c8\u5c3d\u5fc3\u6cbb\u6108\u4e86\u4ed6\u7684\u4f24\u52bf\uff0c\u4e00\u8def\u62a4\u9001\u4ed6\u56de \u5bb6\u3002 \u3000\u3000\u4ece\u6b64\uff0c\u9f99\u5578\u4e91\u4e0d\u4f46\u662f\u4ed6\u7684\u6069\u4eba\uff0c\u4e5f\u6210\u4e86\u4ed6\u6700\u597d\u7684\u670b\u53cb\u3002 \u3000\u3000\u4f46\u662f\u540e\u6765\u9f99\u5578\u4e91\u5374\u75c5\u4e86\uff0c\u75c5\u5f97\u5f88\u91cd\uff0c\u4e00\u6761\u94c1\u6253\u822c\u7684\u6c49\u5b50\uff0c\u4e0d\u5230\u534a\u4e2a\u6708\u7adf\u5df2\u53d8\u5f97\u9762\u9ec4\u808c\u7626 \uff0c\u5f62\u9500\u9aa8\u7acb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ee\u4e86\u5f88\u4e45\uff0c\u624d\u77e5\u9053\u4ed6\u7adf\u662f\u4e3a\u4e86\u6797\u8bd7\u97f3\u624d\u75c5\u7684\uff0c\u8fd9\u6761\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\u4e3a\u60c5\u6240\u56f0\uff0c\u7adf\u5df2 \u76f8\u601d\u5165\u9aa8\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u77e5\u9053\u674e\u5bfb\u6b22\u548c\u6797\u8bd7\u97f3\u5df2\u8ba2\u4e86\u4eb2\uff0c\u6240\u4ee5\u4ed6\u6c42\u674e\u5bfb\u6b22\u5c06\u2018\u8868\u59b9\u2019\u8bb8\u914d\u7ed9\u4ed6\uff0c\u4ed6\u7b54\u5e94 \u674e\u5bfb\u6b22\u4e00\u5b9a\u4f1a\u597d\u597d\u7167\u987e\u5979\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u600e\u4e48\u80fd\u7b54\u5e94\u4ed6\u5462\uff1f \u3000\u3000\u4f46\u4ed6\u53c8\u600e\u4e48\u80fd\u773c\u89c1\u7740\u4ed6\u7684\u6069\u4eba\u76f8\u601d\u800c\u6b7b\u3002 \u3000\u3000\u800c\u4ed6\u66f4\u4e0d\u80fd\u53bb\u6c42\u6797\u8bd7\u97f3\u53bb\u5ac1\u7ed9\u522b\u4eba\uff0c\u6797\u8bd7\u97f3\u4e5f\u7edd\u4e0d\u4f1a\u7b54\u5e94\u3002 \u3000\u3000\u4ed6\u6ee1\u5fc3\u75db\u82e6\uff0c\u6ee1\u6000\u77db\u76fe\uff0c\u53ea\u6709\u7eb5\u9152\u81ea\u9063\uff0c\u5927\u9189\u4e86\u4e94\u65e5\u540e\uff0c\u4ed6\u7ec8\u4e8e\u4e0b\u4e86\u51b3\u5b9a\uff0c\u90a3\u771f\u662f\u4e2a\u75db \u82e6\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u8981\u8ba9\u6797\u8bd7\u97f3\u81ea\u5df1\u79bb\u5f00\u4ed6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u6c42\u6797\u8bd7\u97f3\u53bb\u7167\u987e\u9f99\u5578\u4e91\u7684\u75c5\uff0c\u4ed6\u81ea\u5df1\u5374\u5f00\u59cb\u7eb5\u60c5\u58f0\u8272\uff0c\u82b1\u5929\u9152\u5730\uff0c\u751a\u81f3\u7ecf\u6708\u7684 \u4e0d\u56de\u5bb6\u3002 \u3000\u3000\u4ed6\u8981\u9020\u6210\u9f99\u5578\u4e91\u548c\u6797\u8bd7\u97f3\u4eb2\u8fd1\u7684\u673a\u4f1a\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u6d41\u7740\u6cea\u529d\u4ed6\u65f6\uff0c\u4ed6\u5374\u5927\u7b11\u7740\u62c2\u8896\u800c\u53bb\uff0c\u53cd\u800c\u53d8\u672c\u52a0\u5229\uff0c\u5c45\u7136\u5c06\u4eac\u57ce\u7684\u660e\u5993\u5c0f\u7ea2\u548c \u5c0f\u7fe0\u5e26\u56de\u5bb6\u6765\u4e86\u3002 \u3000\u3000\u4e24\u5e74\u540e\uff0c\u6797\u8bd7\u97f3\u7ec8\u4e8e\u5fc3\u788e\uff0c\u5931\u671b\u3002 \u3000\u3000\u5979\u7ec8\u4e8e\u9009\u62e9\u4e86\u5bf9\u5979\u60c5\u6df1\u4e00\u5f80\u7684\u9f99\u5578\u4e91\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8ba1\u5212\u7ec8\u4e8e\u6210\u529f\u4e86\uff0c\u4f46\u8fd9\u6210\u529f\u5374\u53c8\u662f\u591a\u4e48\u8f9b\u9178\uff0c\u591a\u4e48\u75db\u82e6\uff0c\u4ed6\u600e\u4e48\u80fd\u518d\u7559\u5728\u8fd9\u91cc \u770b\u6614\u65e5\u7684\u6885\u82b1\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u5c06\u81ea\u5df1\u7684\u5bb6\u56ed\u5168\u9001\u7ed9\u6797\u8bd7\u97f3\u505a\u5ac1\u5986\uff0c\u4e00\u4e2a\u4eba\u8427\u7136\u800c\u53bb\uff0c\u4ed6\u51b3\u5fc3\u6c38\u8fdc\u4e5f\u4e0d\u518d\u89c1\u5979 \u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\uff0c\u4ed6\u5374\u4f24\u4e86\u4ed6\u4eec\u7684\u72ec\u751f\u5b50\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u72ec\u81ea\u541e\u4e0b\u4e86\u8fd9\u676f\u82e6\u9152\uff0c\u4e5f\u54bd\u4e0b\u4e86\u773c\u6cea\uff0c\u7f13\u7f13\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u9f99\u56db\u7237\u5728\u54ea\u91cc\uff1f\u6211\u968f\u4f60 \u4eec\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u6614\u65e5\u7684\u2018\u674e\u56ed\u2019\uff0c\u5982\u4eca\u867d\u5df2\u53d8\u6210\u4e86\u2018\u5174\u4e91\u5e84\u2019\uff0c\u4f46\u5927\u95e8\u524d\u90a3\u4e24\u5e45\u5fa1\u7b14\u4eb2\u4e66\u7684\u95e8\u8054\u5374\u4ecd\u5728 \u3002 \u3000\u3000\u2018\u4e00\u95e8\u4e03\u8fdb\u58eb \u3000\u3000\u7236\u5b50\u4e09\u63a2\u82b1\u3002\u2019 \u3000\u3000\u674e\u5bfb\u6b22\u89c1\u5230\u8fd9\u526f\u5bf9\u8054\uff0c\u5c31\u8c61\u662f\u6709\u4eba\u5728\u4ed6\u7684\u80f8\u53e3\u4e0a\u91cd\u91cd\u8e22\u4e86\u4e00\u811a\uff0c\u4f7f\u5f97\u4ed6\u518d\u4e5f\u65e0\u6cd5\u4e3e\u6b65\u3002 \u3000\u3000\u5df4\u82f1\u65e9\u5df2\u62b1\u7740\u7ea2\u5b69\u513f\u51b2\u4e86\u8fdb\u53bb\uff0c\u79e6\u5b5d\u4eea\u4e5f\u62c9\u7740\u6885\u4e8c\u5148\u751f\u5927\u6b65\u800c\u5165\uff0c\u95e8\u53e3\u7684\u5bb6\u4e01\u5374\u90fd\u5e26\u7740 \u8be7\u5f02\u7684\u773c\u8272\u671b\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u4ed6\u4eec\u50cf\u662f\u5728\u5947\u602a\uff0c\u8fd9\u964c\u751f\u4eba\u7ad9\u5728\u95e8\u53e3\u53d1\u4ec0\u4e48\u5446\uff1f \u7b2c\u516b\u7ae0\u3000\u5f80\u4e8b\u4e0d\u53ef\u8ffd>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u7ae0\u3000\u5f80\u4e8b\u4e0d\u53ef\u8ffd \u3000\u3000\u4f46\u8fd9\u672c\u662f\u674e\u5bfb\u6b22\u81ea\u5df1\u7684\u5bb6\u56ed\uff0c\u4ed6\u4ece\u5c0f\u5c31\u5728\u8fd9\u91cc\u957f\u5927\u7684\uff0c\u5728\u8fd9\u91cc\uff0c\u4ed6\u66fe\u7ecf\u6e21\u8fc7\u4e00\u6bb5\u6700\u5e78\u798f \u7684\u7ae5\u5e74\uff0c\u5f97\u8fc7\u6700\u5927\u7684\u8363\u8000\uff0c\u53ef\u662f\uff0c\u4e5f\u5c31\u5728\u8fd9\u91cc\uff0c\u4ed6\u66fe\u7ecf\u4eb2\u81ea\u5c06\u4ed6\u7236\u6bcd\u548c\u5144\u957f\u7684\u7075\u67e9\u62ac\u51fa\u53bb\u57cb \u846c\u3002 \u3000\u3000\u53c8\u8c01\u80fd\u60f3\u5230\u6b64\u523b\u4ed6\u5728\u8fd9\u91cc\u7adf\u53d8\u6210\u4e2a\u964c\u751f\u4eba\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51c4\u7136\u4e00\u7b11\uff0c\u8033\u65c1\u4f3c\u4e4e\u54cd\u8d77\u4e86\u4e00\u9635\u51c4\u51c9\u7684\u60b2\u6b4c\uff1a\u201c\u773c\u770b\u4ed6\u8d77\u9ad8\u697c\uff0c\u773c\u770b\u4ed6\u5bb4\u5bbe\u5ba2\uff0c \u773c\u770b\u4ed6\u697c\u57ae\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4ed4\u7ec6\u5480\u56bc\u7740\u8fd9\u5176\u4e2d\u7684\u6ecb\u5473\uff0c\u4f53\u5473\u7740\u4eba\u751f\u7684\u79bb\u5408\uff0c\u751f\u547d\u7684\u60b2\u6b4c\uff0c\u66f4\u662f\u6ee1\u6000\u8427\u7d22\uff0c\u7384\u7136\u6b32 \u6ce3\u3002 \u3000\u3000\u866c\u7136\u5927\u6c49\u4e5f\u662f\u795e\u8272\u9eef\u7136\uff0c\u6084\u58f0\u9053\uff1a\u201c\u5c11\u7237\uff0c\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u65e2\u5df2\u6765\u4e86\uff0c\u8fdf\u65e9\u603b\u8981\u8fdb\u53bb\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u8de8\u4e0a\u77f3\u9636\uff0c\u7a81\u542c\u4e00\u4eba\u5927\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u6562\u5f80\u9f99\u56db\u7237\u7684\u95e8\u91cc\u4e71\u95ef\uff1f \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u9526\u7f0e\u7f8a\u76ae\u8884\uff0c\u5374\u655e\u7740\u8863\u895f\uff0c\u624b\u91cc\u63d0\u7740\u4e2a\u9e1f\u7b3c\u7684\u5927\u9ebb\u5b50\u4ece\u65c1\u8fb9\u51b2\u8fc7\u6765\uff0c\u62e6\u4f4f\u4e86\u674e \u5bfb\u6b22\u7684\u53bb\u8def\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u201c\u9601\u4e0b\u662f\u2026\u2026\u201d \u3000\u3000\u9ebb\u5b50\u624b\u53c9\u7740\u8170\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5927\u7237\u5c31\u662f\u8fd9\u91cc\u7684\u7ba1\u5bb6\uff0c\u6211\u7684\u95fa\u5973\u5c31\u662f\u8fd9\u91cc\u9f99\u592b\u4eba\u7684\u5e72\u59b9\u59b9\uff0c \u4f60\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5662\u2014\u2014\u65e2\u662f\u5982\u6b64\uff0c\u5728\u4e0b\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\u5c31\u662f\u3002\u201d \u3000\u3000\u9ebb\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u7b49\u7740\u4e5f\u4e0d\u884c\uff0c\u9f99\u516c\u9986\u7684\u5927\u95e8\u53e3\u542f\u662f\u95f2\u6742\u4eba\u7b49\u53ef\u4ee5\u968f\u610f\u7ad9\u7740\u7684\uff1f\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u6012\u5bb9\u6ee1\u9762\uff0c\u4f46\u4e5f\u77e5\u9053\u6b64\u65f6\u53ea\u6709\u5fcd\u8010\u3002 \u3000\u3000\u8c01\u77e5\u90a3\u9ebb\u5b50\u7adf\u53c8\u6012\u9a82\u9053\uff1a\u201c\u53eb\u4f60\u6eda\u5f00\uff0c\u96be\u9053\u662f\u4f5c\u6b7b\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u867d\u8fd8\u5fcd\u5f97\u4f4f\uff0c\u866c\u7136\u5927\u6c49\u5374\u5fcd\u8010\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u8fc7\u53bb\u7ed9\u8fd9\u4e2a\u9ebb\u5b50\u6559\u8bad\uff0c\u95e8\u91cc\u5df2\u6709\u4eba\u9ad8\u547c\u9053\uff1a\u201c\u5bfb\u6b22\uff0c\u5bfb\u6b22\uff0c\u771f\u662f\u4f60\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u76f8\u8c8c\u5802\u5802\uff0c\u9526\u8863\u534e\u670d\uff0c\u988c\u4e0b\u7559\u7740\u5fae\u987b\u7684\u4e2d\u5e74\u4eba\u5df2\u968f\u58f0\u51b2\u4e86\u51fa\u6765\uff0c\u6ee1\u9762\u4ff1\u662f\u5174\u594b\u6fc0\u52a8 \u4e4b\u8272\uff0c\u4e00\u89c1\u5230\u674e\u5bfb\u6b22\uff0c\u5c31\u7528\u529b\u634f\u7740\u4ed6\u7684\u8116\u5b50\uff0c\u560e\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u771f\u662f\u4f60\u6765\u4e86\u2026\u2026\u771f\u662f\u4f60\u6765\u4e86 \u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u662f\u70ed\u6cea\u76c8\u7736\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u4f55\u5c1d\u4e0d\u662f\u6ee1\u7736\u70ed\u6cea\uff0c\u9053\uff1a\u201c\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u53ea\u5524\u4e86\u8fd9\u4e00\u58f0\u201c\u5927\u54e5\u201d\uff0c\u4ed6\u5df2\u662f\u8bed\u97f3\u54fd\u54bd\uff0c\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u9ebb\u5b50\u89c1\u5230\u8fd9\u5149\u666f\uff0c\u53ef\u771f\u662f\u9a87\u5446\u4e86\u3002 \u3000\u3000\u53ea\u542c\u9f99\u5578\u4e91\u4e0d\u4f4f\u5583\u5583\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u771f\u662f\u60f3\u6b7b\u6211\u4e86\uff0c\u60f3\u6b7b\u6211\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u7ffb\u6765\u590d\u53bb\u4e5f\u4e0d\u77e5\u8bf4\u4e86\u591a\u5c11\u904d\uff0c\u5ffd\u53c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u6211\u5144\u5f1f\u76f8\u89c1\uff0c\u672c\u8be5\u9ad8\u5174\u624d\u662f\uff0c \u600e\u5730\u5374\u773c\u6cea\u5df4\u5df4\u7684\u50cf\u4e2a\u8001\u592a\u5a46\u2026\u2026\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u62e5\u7740\u674e\u5bfb\u6b22\u5f80\u91cc\u8d70\uff0c\u8fd8\u5728\u5927\u547c\u9053\uff1a\u201c\u5feb\u53bb\u8bf7\u592b\u4eba\u51fa\u6765\uff0c\u5927\u5bb6\u5168\u51fa\u6765\uff0c\u6765\u89c1\u89c1\u6211 \u7684\u5144\u5f1f\uff0c\u4f60\u4eec\u53ef\u77e5\u6211\u8fd9\u5144\u5f1f\u662f\u8c01\u4e48\uff1f\u2026\u2026\u54c8\u54c8\uff0c\u6211\u8bf4\u51fa\u6765\u5305\u9669\u4f60\u4eec\u90fd\u8981\u5413\u4e00\u8df3\u3002\u201d \u3000\u3000\u866c\u7136\u5927\u6c49\u671b\u7740\u4ed6\u4eec\uff0c\u773c\u6cea\u4e5f\u5feb\u8981\u6d41\u4e86\u51fa\u6765\uff0c\u4ed6\u5fc3\u91cc\u53ea\u89c9\u9178\u9178\u7684\uff0c\u4e5f\u4e0d\u77e5\u662f\u60b2\u75db\uff1f\u8fd8\u662f\u6b22 \u559c\u3002 \u3000\u3000\u90a3\u9ebb\u5b50\u8fd9\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u6478\u7740\u8111\u888b\u9053\uff1a\u201c\u6211\u7684\u5988\u5440\uff0c\u539f\u6765\u4ed6\u5c31\u662f\u674e\u2026\u2026\u674e\u63a2\u82b1\uff0c\u8fde\u8fd9 \u680b\u623f\u5b50\u542c\u8bf4\u90fd\u662f\u4ed6\u9001\u7684\uff0c\u6211\u5374\u4e0d\u8ba9\u4ed6\u8fdb\u6765\uff0c\u6211\u2026\u2026\u6211\u771f\u8be5\u6b7b\u3002\u201d \u3000\u3000\u90a3\u7ea2\u5b69\u513f\u9f99\u5c0f\u4e91\u6b63\u88ab\u5341\u51e0\u4e2a\u4eba\u56f4\u7740\uff0c\u5750\u5728\u5927\u5385\u674e\u7684\u592a\u5e08\u6905\u4e0a\uff0c\u4ed6\u4e5f\u660e\u767d\u4e86\u4ed6\u7236\u4eb2\u548c\u674e\u5bfb \u6b22\u7684\u5173\u7cfb\uff0c\u5413\u5f97\u8fde\u54ed\u90fd\u4e0d\u6562\u54ed\u4e86\u3002 \u3000\u3000\u4f46\u9f99\u5578\u4e91\u521a\u62e5\u7740\u674e\u5bfb\u6b22\u8d70\u5165\u4e86\u5927\u5385\uff0c\u672c\u6765\u7ad9\u5728\u9f99\u5c0f\u4e91\u65c1\u8fb9\u7684\u4e24\u6761\u5927\u6c49\u5ffd\u7136\u6251\u4e86\u51fa\u6765\uff0c\u6307 \u7740\u674e\u5bfb\u6b22\u7684\u9f3b\u5b50\u9053\uff1a\u201c\u4f24\u4e86\u4e91\u5c11\u7237\u7684\uff0c\u5c31\u662f\u4f60\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u90a3\u5927\u6c49\u6012\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u80c6\u5b50\u771f\u4e0d\u5c0f\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u5de6\u4e00\u53f3\uff0c\u7adf\u5411\u674e\u5bfb\u6b22\u5939\u51fb\u800c\u6765\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5e76\u6ca1\u6709\u56de\u624b\uff0c\u4f46\u9f99\u5578\u4e91\u5ffd\u7136\u6012\u559d\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u8ddf\u7740\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u4e24\u4eba\u90fd\u6253\u5f97 \u6eda\u4e86\u51fa\u53bb\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4eec\u6562\u5bf9\u4ed6\u51fa\u624b\uff1f\u4f60\u4eec\u7684\u80c6\u5b50\u624d\u771f\u4e0d\u5c0f\uff0c\u4f60\u4eec\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u518d\u4e5f\u60f3\u4e0d\u5230\u9a6c\u5c41\u7adf\u62cd\u5230\u9a6c\u817f\u4e0a\u3002 \u3000\u3000\u4e00\u4eba\u6342\u7740\u8138\u5403\u5403\u9053\uff1a\u201c\u6211\u4eec\u53ea\u4e0d\u8fc7\u662f\u60f3\u66ff\u4e91\u5c11\u7237\u2026\u2026\u201d \u3000\u3000\u9f99\u5578\u4e91\u5386\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u600e\u6837\uff0c\u544a\u8bc9\u4f60\u4eec\uff0c\u9f99\u5578\u4e91\u7684\u513f\u5b50\u5c31\u662f\u674e\u5bfb\u6b22\u7684\u513f\u5b50\uff0c\u674e\u5bfb\u6b22\u83ab \u8bf4\u53ea\u4e0d\u8fc7\u6559\u8bad\u4e86\u4ed6\u4e00\u6b21\uff0c\u5c31\u7b97\u5c06\u8fd9\u755c\u751f\u6740\u4e86\uff0c\u4e5f\u662f\u5e94\u8be5\u7684\uff01\u201d \u3000\u3000\u4ed6\u653e\u58f0\u5927\u559d\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u518d\u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u82e5\u6709\u8c01\u6562\u518d\u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u662f\u6210 \u5fc3\u548c\u6211\u9f99\u5578\u4e91\u8fc7\u4e0d\u53bb\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6728\u7136\u800c\u7acb\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u9f99\u5578\u4e91\u82e5\u662f\u75db\u9a82\u4ed6\u4e00\u573a\uff0c\u751a\u81f3\u548c\u4ed6\u7ffb\u8138\uff0c\u4ed6\u4e5f\u8bb8\u8fd8\u4f1a\u89c9\u5f97\u597d\u53d7\uff0c\u4f46\u9f99\u5578\u4e91\u5374\u5982\u6b64\u91cd\u610f\u6c14 \uff0c\u4ed6\u5fc3\u91cc\u53ea\u6709\u66f4\u60ed\u6127\uff0c\u66f4\u96be\u53d7\uff01\u9eef\u7136\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u5b9e\u5728\u4e0d\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u9f99\u5578\u4e91\u7528\u529b\u4e00\u62cd\u4ed6\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u600e\u5730\u4e5f\u53d8\u5f97\u8fd9\u4e48\u5a46\u5a46\u5988\u5988\u8d77\u6765\u4e86\uff1f\u8fd9\u755c\u751f\u88ab \u4ed6\u6bcd\u4eb2\u60ef\u5f97\u5b9e\u5728\u592a\u4e0d\u8c61\u8bdd\u4e86\uff0c\u6211\u672c\u5c31\u4e0d\u8be5\u4f20\u4ed6\u6b66\u529f\u7684\u3002\u201d \u3000\u3000\u4ed6\u5927\u7b11\u7740\u547c\u9053\uff1a\u201c\u6765\u6765\u6765\uff0c\u5feb\u6446\u9152\u4e0a\u6765\uff0c\u4f60\u4eec\u65e0\u8bba\u8c01\u82e5\u80fd\u5c06\u6211\u8fd9\u5144\u5f1f\u704c\u9189\uff0c\u6211\u9a6c\u4e0a\u5c31\u9001 \u4ed6\u4e94\u767e\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u5927\u5385\u4e2d\u7684\u4eba\u591a\u662f\u8001\u6c5f\u6e56\uff0c\u5149\u68cd\u7684\u773c\u775b\u54ea\u6709\u4e0d\u4eae\u7684\uff0c\u65e9\u5df2\u5168\u90fd\u56f4\u4e86\u8fc7\u6765\uff0c\u5411\u674e\u5bfb\u6b22\u966a\u7b11\u95ee \u597d\u3002 \u3000\u3000\u7a81\u542c\u5185\u5802\u4e00\u4eba\u9053\uff1a\u201c\u5feb\u6380\u5e18\u5b50\uff0c\u592b\u4eba\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u7ad9\u5728\u95e8\u53e3\u7684\u7ae5\u5b50\u521a\u5c06\u95e8\u5e18\u6380\u8d77\uff0c\u6797\u8bd7\u97f3\u5df2\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7ec8\u4e8e\u53c8\u89c1\u5230\u6797\u8bd7\u97f3\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4e5f\u8bb8\u5e76\u4e0d\u80fd\u7b97\u662f\u4e2a\u771f\u6b63\u5b8c\u7f8e\u65e0\u6687\u7684\u5973\u4eba\uff0c\u4f46\u8c01\u4e5f\u4e0d\u80fd\u5426\u8ba4\u5979\u662f\u4e2a\u7f8e\u4eba\uff0c\u5979\u7684\u8138\u8272 \u592a\u82cd\u767d\uff0c\u8eab\u5b50\u592a\u5355\u8584\uff0c\u5979\u7684\u773c\u775b\u867d\u660e\u4eae\uff0c\u4e5f\u5acc\u592a\u51b7\u6f20\u4e86\u4e9b\uff0c\u53ef\u662f\u5979\u7684\u98ce\u795e\uff0c\u5979\u7684\u6c14\u8d28\uff0c\u5374\u662f \u65e0\u53ef\u6bd4\u62df\u7684\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u5979\u90fd\u80fd\u4f7f\u4eba\u611f\u89c9\u5230\u5979\u90a3\u72ec\u7279\u7684\u9b45\u529b\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u77a7\u8fc7\u5979\u4e00\u773c\uff0c\u5c31\u6c38 \u8fdc\u65e0\u6cd5\u5fd8\u8bb0\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u5728\u674e\u5bfb\u6b22\u68a6\u4e2d\u5df2\u4e0d\u77e5\u51fa\u73b0\u8fc7\u51e0\u5343\u51e0\u4e07\u6b21\u4e86\uff0c\u6bcf\u4e00\u6b21\u5979\u90fd\u8ddd\u79bb\u5f97\u90a3\u4e48\u9065\u8fdc\uff0c\u4e0d\u53ef\u4f01 \u53ca\u7684\u9065\u8fdc\u3002 \u3000\u3000\u6bcf\u4e00\u6b21\u674e\u5bfb\u6b22\u60f3\u53bb\u62e5\u62b1\u5979\u65f6\uff0c\u90fd\u4f1a\u5ffd\u7136\u81ea\u8fd9\u5fc3\u788e\u7684\u6076\u68a6\u4e2d\u60ca\u9192\uff0c\u4ed6\u53ea\u6709\u8eba\u5728\u4ed6\u81ea\u5df1\u7684\u51b7 \u6c57\u91cc\uff0c\u671b\u7740\u7a97\u5916\u9ed1\u6c89\u6c89\u7684\u591c\u8272\u98a4\u6296\uff0c\u75db\u82e6\u5730\u7b49\u5f85\u7740\u5929\u4eae\uff0c\u53ef\u662f\u5929\u4eae\u7684\u65f6\u5019\uff0c\u4ed6\u8fd8\u662f\u540c\u6837\u75db\u82e6 \uff0c\u540c\u6837\u5bc2\u5bde\u3002 \u3000\u3000\u73b0\u5728\uff0c\u68a6\u4e2d\u4eba\u7ec8\u4e8e\u771f\u5b9e\u7684\u5728\u4ed6\u773c\u524d\u51fa\u73b0\u4e86\uff0c\u4ed6\u751a\u81f3\u53ea\u8981\u4e00\u4f38\u624b\uff0c\u5c31\u53ef\u4ee5\u89e6\u53ca\u5979\uff0c\u4ed6\u77e5\u9053 \u8fd9\u4e0d\u518d\u662f\u68a6\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u53c8\u600e\u80fd\u4f38\u624b\u5462\uff1f \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u8fd9\u53c8\u662f\u4e2a\u68a6\uff0c\u4f46\u771f\u5b9e\u6c38\u8fdc\u6bd4\u68a6\u6b8b\u9177\u5f97\u591a\uff0c\u4ed6\u8fde\u9003\u907f\u90fd\u65e0\u6cd5\u9003\u907f\uff0c\u53ea\u6709\u4ee5\u5fae\u7b11\u6765 \u63a9\u9970\u4f4f\u5fc3\u91cc\u7684\u75db\u82e6\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u4f60\u597d\uff01\u201d \u3000\u3000\u201c\u5927\u5ac2\u201d \u3000\u3000\u9b42\u7275\u68a6\u8426\u7684\u60c5\u4eba\uff0c\u7adf\u5df2\u662f\u5927\u5ac2\uff0c\u866c\u7136\u5927\u6c49\u626d\u8f6c\u4e86\u5934\uff0c\u4e0d\u5fcd\u518d\u770b\uff0c\u56e0\u4e3a\u53ea\u6709\u4ed6\u77e5\u9053\u674e\u5bfb\u6b22 \u8fd9\u4e00\u58f0\u201c\u5927\u5ac2\u201d\u5524\u5f97\u662f\u591a\u4e48\u75db\u82e6\uff0c\u591a\u4e48\u8f9b\u9178\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u81ea\u5df1\u82e5\u5728\u674e\u5bfb\u6b22\u8fd9\u79cd\u60c5\u51b5\u4e2d\u65f6\uff0c\u662f\u5426\u4e5f\u80fd\u5524\u5f97\u51fa\u8fd9\u4e00\u58f0\u201c\u5927\u5ac2\u201d\u6765\uff0c\u4ed6\u4e0d\u77e5\u9053 \u81ea\u5df1\u662f\u5426\u4e5f\u6709\u52c7\u6c14\u6765\u627f\u53d7\u5982\u6b64\u6df1\u7684\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u82e5\u4e0d\u626d\u8f6c\u5934\u53bb\u671b\u9662\u4e2d\u7684\u79ef\u96ea\uff0c\u53ea\u6015\u65e9\u5df2\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u800c\u6797\u8bd7\u97f3\u5374\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u542c\u89c1\u8fd9\u4e00\u58f0\u547c\u5524\u3002 \u3000\u3000\u5979\u7684\u5fc3\u4eff\u4f5b\u5df2\u5168\u8d2f\u6ce8\u5728\u5979\u7684\u513f\u5b50\u8eab\u4e0a\u3002 \u7b2c\u4e5d\u7ae0\u3000\u4f55\u5904\u4e0d\u76f8\u9022>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e5d\u7ae0\u3000\u4f55\u5904\u4e0d\u76f8\u9022 \u3000\u3000\u5c11\u5e74\u542c\u4e86\u674e\u5bfb\u6b22\u7684\u8bdd\uff0c\u6014\u4e86\u6014\uff0c\u563f\u563f\u51b7\u7b11\u9053\uff1a\u6709\u8da3\u6709\u8da3\uff0c\u9601\u4e0b\u7684\u786e\u6709\u8da3\u5f97\u5f88\uff0c\u8c82\u88d8\u4e0a\u5c45 \u7136\u8fd8\u957f\u7740\u773c\u775b\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u6211\u8fd9\u4ef6\u8c82\u88d8\u4e0a\u82e5\u662f\u6ca1\u6709\u957f\u773c\u775b\uff0c\u53c8\u600e\u4f1a\u770b\u89c1\u9601\u4e0b\u7684\u5b9d\u5251\uff0c\u53c8\u600e\u4f1a\u8eb2 \u5f97\u8fc7\u9601\u4e0b\u81ea\u80cc\u540e\u523a\u6765\u7684\u4e00\u5251\u5462\uff1f \u3000\u3000\u5c11\u5e74\u8138\u8272\u7acb\u523b\u53d8\u4e86\uff0c\u4e00\u53cc\u624b\u5df2\u6c14\u5f97\u53d1\u6296\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u5927\u7b11\u9053\uff1a\u4e24\u4f4d\u90fd\u5728\u8bf4\u7b11\uff0c\u85cf\u5251\u5c71\u5e84\u7684\u5c11\u5e84\u4e3b\uff0c\u56fa\u7136\u7edd\u4e0d\u4f1a\u5728\u4e4e\u533a\u533a \u4e00\u67c4\u5251\uff0c\u4f46\u5144\u5f1f\u4f60\u53c8\u600e\u4f1a\u5728\u4e4e\u533a\u533a\u4e00\u88ad\u8c82\u88d8\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u5bb9\u9053\uff1a\u8fd9\u539f\u6765\u5c31\u662f\u6e38\u5c11\u5e84\u4e3b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u6e38\u5144\u4e0d\u4f46\u662f\u85cf\u9f99\u8001\u4eba\u7684\u516c\u5b50\uff0c\u4e5f\u662f\u5f53\u4ee3\u7b2c\u4e00\u5251\u5ba2\u5929\u5c71\u96ea\u9e70\u5b50\u524d\u8f88\u7684 \u552f\u4e00\u4f20\u4eba\uff0c\u4e24\u4f4d\u6b63\u662f\u4e00\u65f6\u4e4b\u745c\u4eae\uff0c\u6b64\u540e\u4e00\u5b9a\u8981\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002 \u3000\u3000\u6e38\u9f99\u751f\u7684\u773c\u775b\u8fd8\u5728\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u51b7\u7b11\u9053\uff1a\u4eb2\u8fd1\u5012\u4e0d\u6562\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4f4d\u670b\u53cb\u9ad8\u59d3\u5927\u540d\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u6e38\u5144\u539f\u6765\u8fd8\u4e0d\u8ba4\u5f97\u6211\u8fd9\u4f4d\u5144\u5f1f\uff0c\u4ed6\u59d3\u674e\uff0c\u53eb\u674e\u5bfb\u6b22\uff0c\u653e\u773c\u5f53\u4eca\u5929\u4e0b\uff0c\u53ea\u6015 \u4e5f\u552f\u6709\u6211\u8fd9\u5144\u5f1f\u591f\u8d44\u683c\u548c\u6e38\u5144\u4f60\u4ea4\u670b\u53cb\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u540d\u5b57\u8bf4\u51fa\u6765\uff0c\u6e38\u9f99\u751f\u8138\u8272\u53c8\u53d8\u4e86\uff0c\u773c\u775b\u76ef\u5728\u674e\u5bfb\u6b22\u624b\u91cc\u90a3\u67c4\u5c0f\u5200\u4e0a\uff0c\u4e45\u4e45\u90fd\u672a \u79fb\u5f00\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u4f3c\u6839\u672c\u672a\u542c\u5230\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\uff0c\u76ee\u4e2d\u53c8\u9732\u51fa\u4e86\u5f02\u6837\u7684\u5149\u8292\uff0c\u5634\u91cc\u5583\u5583\u81ea\u8bed\uff0c\u4eff\u4f5b \u5728\u8bf4\uff1a\u679c\u7136\u53c8\u662f\u4f4d\u540d\u5bb6\u5b50\u5f1f\uff01\u7a81\u89c1\u4e00\u4eba\u51b2\u4e86\u8fdb\u6765\uff0c\u5389\u58f0\u9053\uff1a\u5916\u9762\u90a3\u4eba\u662f\u8c01\u6740\u6b7b\u7684\uff1f \u3000\u3000\u8fd9\u4eba\u98a7\u9aa8\u9ad8\u8038\uff0c\u6ee1\u9762\u5a01\u4e25\uff0c\u82b1\u767d\u7684\u80e1\u5b50\u5e76\u4e0d\u5bc6\uff0c\u9732\u51fa\u4e00\u5f20\u5634\u89d2\u4e0b\u5782\u7684\u9614\u53e3\uff0c\u66f4\u89c9\u5f97\u5a01\u4e25 \u6c89\u91cd\uff0c\u5e73\u65f6\u4e5f\u5e26\u7740\u4e09\u5206\u6740\u6c14\uff0c\u6b63\u662f\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u5bf9\u4ed6\u5e26\u7740\u51e0\u5206\u754f\u60e7\u7684\u94c1\u9762\u65e0\u79c1\u8d75\u6b63\u4e49\u8d75\u5927\u7237 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u9664\u4e86\u6211\u8fd8\u6709\u8c01\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u76ee\u5149\u5982\u5200\uff0c\u77aa\u7740\u4ed6\uff0c\u5389\u58f0\u9053\uff1a\u662f\u4f60\uff0c\u6211\u65e9\u8be5\u60f3\u5230\u662f\u4f60\uff0c\u4f60\u65e0\u8bba\u8d70\u5230\u54ea\u91cc\uff0c\u90fd\u4f1a\u5e26 \u6765\u4e00\u7247\u8840\u8165\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4eba\u4e0d\u8be5\u6740\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u53ea\u53ef\u60dc\u4ed6\u4e0d\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u6012\u9053\uff1a\u4f60\u65e2\u7136\u77e5\u9053\u4ed6\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u4e3a\u4f55\u8fd8\u8981\u4e0b\u6bd2\u624b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u6211\u867d\u4e5f\u4e0d\u60f3\u6740\u4ed6\uff0c\u4f46\u4e5f\u4e0d\u613f\u88ab\u4ed6\u6740\u4e86\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u6740\u4eba\u603b\u6bd4\u88ab\u4eba\u6740\u597d\u4e9b \u3002 \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u4ed6\u5148\u8981\u6740\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u55ef\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u5e73\u767d\u65e0\u6545\uff0c\u4ed6\u4e3a\u4f55\u8981\u6740\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4e5f\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u6b63\u60f3\u95ee\u95ee\u4ed6\uff0c\u53ea\u53ef\u60dc\u4ed6\u4e0d\u7406\u6211\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5927\u6012\u9053\uff1a\u4f60\u4e3a\u4f55\u4e0d\u7559\u4e0b\u4ed6\u7684\u6d3b\u53e3\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4e5f\u5f88\u60f3\u7559\u4e0b\u6d3b\u53e3\uff0c\u53ea\u53ef\u60dc\u6211\u624b\u91cc\u8fd9\u67c4\u5200\u4e00\u53d1\u51fa\u53bb\uff0c\u5bf9\u65b9\u662f\u6d3b\u662f\u6b7b\uff0c\u5c31\u8fde\u6211 \u81ea\u5df1\u4e5f\u65e0\u6cd5\u63a7\u5236\u4e86\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u4f60\u65e2\u5df2\u51fa\u8d70\uff0c\u4e3a\u4f55\u504f\u8fd8\u8981\u56de\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u53ea\u56e0\u6211\u5bf9\u8d75\u5927\u7237\u60f3\u5ff5\u5f97\u5f88\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u56de\u6765\u77a7\u77a7\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u8138\u90fd\u6c14\u9ec4\u4e86\uff0c\u6307\u7740\u9f99\u5578\u4e91\u9053\uff1a\u597d\u597d\u597d\uff0c\u8fd9\u662f\u4f60\u7684\u597d\u5144\u5f1f\u60f9\u4e0b\u6765\u7684\u7978\uff0c\u522b\u4eba\u53ef\u7ba1\u4e0d \u7740\u3002 \u3000\u3000\u9f99\u5578\u4e91\u966a\u7b11\u9053\uff1a\u6709\u8bdd\u597d\u8bf4\uff0c\u5927\u54e5\u4f55\u5fc5\u53d1\u8fd9\u4e48\u5927\u7684\u813e\u6c14\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff01\u6211\u4eec\u5bf9\u4ed8\u4e00\u4e2a\u6885\u82b1\u76d7\uff0c\u5df2\u7ecf\u591f\u5934\u75bc\u7684\u4e86\uff0c\u5982\u4eca\u518d\u52a0\u4e0a\u4e00\u4e2a \u9752\u9b54\u4f0a\u54ed\uff0c\u8c01\u8fd8\u53d7\u5f97\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u6740\u4e86\u4f0a\u54ed\u7684\u7231\u5f92\u4e18\u72ec\uff0c\u4f0a\u54ed\u77e5\u9053\u4e86\u4e00\u5b9a\u4f1a\u6765\u5bfb\u4ec7\uff0c\u4f46\u4ed6\u8981\u627e\u7684 \u4e5f\u53ea\u4e0d\u8fc7\u662f\u6211\u4e00\u4e2a\u4eba\u800c\u5df2\uff0c\u8d75\u5927\u7237\u4f60\u53c8\u4f55\u5fc5\u66ff\u6211\u62c5\u5fc3\u5462\uff1f \u3000\u3000\u9f99\u5578\u4e91\u5ffd\u7136\u9053\uff1a\u4e18\u72ec\u4e09\u66f4\u534a\u591c\u7684\u5230\u8fd9\u91cc\u6765\uff0c\u663e\u7136\u4e5f\u6ca1\u6709\u5b58\u4ec0\u4e48\u597d\u5fc3\uff0c\u5144\u5f1f\u4f60\u6740\u4ed6\u672c\u5c31\u6740 \u5f97\u4e0d\u51a4\uff0c\u4ed6\u82e5\u6211\u63b8\u89c1\uff0c\u6211\u53ea\u6015\u4e5f\u8981\u6740\u6b7b\u4ed6\u7684\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u6c14\u5f97\u626d\u5934\u5c31\u8d70\u3002 \u3000\u3000\u6e38\u9f99\u751f\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u8d75\u5927\u7237\u6bd5\u7adf\u8001\u4e86\uff0c\u813e\u6c14\u8d8a\u6765\u8d8a\u5927\uff0c\u80c6\u5b50\u5374\u8d8a\u6765\u8d8a\u5c0f\uff0c\u5176\u5b9e\u4f0a\u54ed\u6765 \u4e86\u53c8\u6709\u4f55\u59a8\uff0c\u5728\u4e0b\u4e5f\u6b63\u597d\u89c1\u8bc6\u540d\u6ee1\u5929\u4e0b\u7684\u63a2\u82b1\u98de\u5200\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u5176\u5b9e\u9601\u4e0b\u82e5\u679c\u6709\u6b64\u5fc3\uff0c\u5c31\u5e76\u4e0d\u4e00\u5b9a\u8981\u7b49\u4f0a\u54ed\u6765\u4e86\u3002 \u3000\u3000\u6e38\u5728\u751f\u8138\u8272\u53c8\u53d8\u4e86\u53d8\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u77a7\u4e86\u674e\u5bfb\u6b22\u638c\u4e2d\u7684\u5200\u4e00\u773c\uff0c\u7ec8\u4e8e\u4ec0\u4e48\u90fd\u6ca1\u6709\u8bf4 \uff0c\u4e5f\u6389\u9996\u800c\u53bb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u60f3\u8ffd\u51fa\u53bb\uff0c\u53c8\u7ad9\u4f4f\uff0c\u6447\u5934\u53f9\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u8fd9\u53c8\u662f\u4f55\u82e6\uff1f\u5c31\u7b97\u4f60\u77a7\u4e0d\u8d77\u4ed6\u4eec\uff0c\u4e0d\u613f \u548c\u4ed6\u4eec\u4ea4\u670b\u53cb\uff0c\u4e5f\u4e0d\u5fc5\u5f97\u7f6a\u4ed6\u4eec\u5440 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4ed6\u4eec\u53cd\u6b63\u65e9\u5df2\u8ba4\u4e3a\u6211\u662f\u4e0d\u53ef\u6551\u836f\u7684\u4e86\uff0c\u6211\u5f97\u4e0d\u5f97\u7f6a\u4ed6\u4eec\u90fd\u4e00\u6837\uff0c\u5012\u4e0d\u5982\u7d22 \u6027\u5c06\u4ed6\u6c14\u8d70\uff0c\u53cd\u800c\u53ef\u4ee5\u843d\u5f97\u4e2a\u773c\u524d\u5e72\u51c0\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u670b\u53cb\u591a\u4e00\u4e2a\u603b\u6bd4\u5c11\u4e00\u4e2a\u597d\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u80fd\u4e0d\u8d1f\u8fd9\u670b\u53cb\u4e8c\u5b57\uff0c\u50cf\u5927\u54e5\u4f60\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u4ea4\u5230 \u4e00\u4e2a\u5df2\u8db3\u591f\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5927\u7b11\u8d77\u6765\uff0c\u7528\u529b\u62cd\u7740\u674e\u5bfb\u6b22\u7684\u80a9\u5934\uff0c\u9053\uff1a\u597d\uff0c\u5144\u5f1f\uff0c\u53ea\u8981\u80fd\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u6211\u5c31\u7b97 \u5c06\u522b\u7684\u670b\u53cb\u5168\u90fd\u5f97\u7f6a\u4e86\uff0c\u4e5f\u662f\u503c\u5f97\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u5934\u4e00\u9635\u6fc0\u52a8\uff0c\u53c8\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u9f99\u5578\u4e91\u76b1\u7709\u9053\uff1a\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u7684\u54b3\u55fd\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u50cf\u662f\u4e0d\u613f\u542c\u5230\u4ed6\u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u7acb\u523b\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u5927\u54e5\uff0c\u6211\u73b0\u5728\u53ea\u60f3\u89c1\u4e00\u4e2a \u4eba\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u8c01\uff1f \u3000\u3000\u4ed6\u6d53\u7709\u6380\u52a8\uff0c\u4e0d\u7b49\u674e\u5bfb\u6b22\u56de\u7b54\uff0c\u53c8\u9053\uff1a\u662f\u4e0d\u662f\u6797\u4ed9\u513f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u5927\u54e5\u771f\u4e0d\u6127\u4e3a\u6211\u7684\u77e5\u5df2\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5c55\u989c\u5927\u7b11\u9053\uff1a\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u8fdf\u65e9\u5fcd\u4e0d\u4f4f\u8981\u60f3\u89c1\u5979\u7684\uff0c\u674e\u5bfb\u6b22\u82e5\u8fde\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\u90fd \u4e0d\u60f3\u89c1\uff0c\u90a3\u4e48\u674e\u5bfb\u6b22\u5c31\u4e0d\u662f\u674e\u5bfb\u6b22\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\uff0c\u4f3c\u5df2\u9ed8\u8ba4\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48\u5462\uff1f\u9664\u4e86\u4ed6\u81ea\u5df1\u4e4b\u5916\uff0c\u53ea\u6015\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5df2\u62c9\u7740\u4ed6\u5f80\u5916\u8d70\uff0c\u7b11\u7740\u9053\uff1a\u4f46\u4f60\u82e5\u60f3\u5230\u8fd9\u91cc\u6765\u627e\u5979\uff0c\u5374\u627e\u9519\u5730\u65b9\u4e86\uff0c\u81ea\u4ece\u524d\u5929\u665a \u4e0a\u7684\u4e8b\u53d1\u751f\u4e86\u4e4b\u540e\uff0c\u5979\u665a\u4e0a\u5df2\u4e0d\u6562\u518d\u7559\u5728\u51b7\u9999\u5c0f\u7b51\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u8fd9\u4e24\u5929\u665a\u4e0a\uff0c\u5979\u4e00\u76f4\u966a\u7740\u8bd7\u97f3\u5728\u4e00\u8d77\uff0c\u4f60\u4e5f\u6b63\u597d\u987a\u4fbf\u53bb\u770b\u770b\u8bd7\u97f3\u2014\u2014\u5509\uff0c\u5979 \u7a76\u7adf\u662f\u4e2a\u5973\u4eba\uff0c\u4f60\u5c31\u7b97\u53bb\u5b89\u6170\u5b89\u6170\u5979\u53c8\u6709\u4f55\u59a8\u3002 \u3000\u3000\u4ed6\u6839\u672c\u672a\u7559\u610f\u674e\u5bfb\u6b22\u76ee\u4e2d\u7684\u75db\u82e6\u4e4b\u8272\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u5176\u5b9e\uff0c\u5979\u4e5f\u4e0d\u662f\u4e0d\u77e5\u9053\u4e91 \u513f\u7684\u53ef\u6076\uff0c\u7edd\u4e0d\u4f1a\u771f\u7684\u602a\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u4f46\u6211\u4eec\u65e2\u5df2\u6765\u5230\u8fd9\u91cc\uff0c\u4e0d\u5982\u8fd8\u662f\u5230\u51b7\u9999\u5c0f\u7b51\u53bb\u77a7\u77a7\u5427\uff0c\u8bf4\u4e0d\u5b9a\u90a3 \u6797\u59d1\u5a18\u73b0\u5728\u5df2\u56de\u6765\u4e86\u5462\uff1f \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u4e5f\u597d\uff0c\u770b\u6765\u4f60\u4eca\u5929\u665a\u4e0a\u82e5\u89c1\u4e0d\u5230\u5979\uff0c\u53ea\u6015\u8fde\u89c9\u90fd\u7761\u4e0d\u7740\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u5fae\u7b11\u7740\uff0c\u4e5f\u4e0d\u5206\u8fa9\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u773c\u775b\u5374\u5728\u95ea\u7740\u5149\uff0c\u4f3c\u4e4e\u9690\u85cf\u7740\u4ec0\u4e48\u79d8\u5bc6\u3002 \u3000\u3000\u51b7\u9999\u5c0f\u7b51\u91cc\u679c\u7136\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u8d70\u8fdb\u95e8\uff0c\u53c8\u4e00\u811a\u53c8\u8e0f\u5165\u5341\u5e74\u524d\u7684\u56de\u5fc6\u91cc\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u91cc\u7684\u4e00\u5207\u7adf\u90fd\u548c\u5341\u5e74\u524d\u6ca1\u6709\u4e1d\u6beb\u53d8\u5316\uff0c\u4e00\u684c\u4e00\u51e0\uff0c\u4e5f\u4f9d\u65e7\u5168\u90fd\u5b89\u653e\u5728\u5341\u5e74\u524d\u7684\u4f4d \u7f6e\uff0c\u751a\u81f3\u8fde\u684c\u4e0a\u7684\u7b14\u58a8\u4e66\u7c4d\uff0c\u90fd\u6ca1\u6709\u4e1d\u6beb\u53d8\u52a8\uff0c\u82e5\u4e0d\u662f\u5728\u96ea\u591c\uff0c\u90a3\u7a97\u524d\u660e\u6708\u3001\u5c4b\u89d2\u659c\u9633\uff0c\u60f3 \u5fc5\u4e5f\u90fd\u4f9d\u65e7\u65e0\u6059\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4eff\u4f5b\u9aa4\u7136\u53c8\u56de\u5230\u5341\u5e74\u524d\uff0c\u65f6\u5149\u82e5\u5012\u9000\u5341\u5e74\uff0c\u4ed6\u4e5f\u8bb8\u521a\u966a\u6797\u8bd7\u97f3\u6570\u8fc7\u6885\u82b1\uff0c\u4e5f\u8bb8\u6b63 \u60f3\u56de\u6765\u53d6\u4e00\u4ef6\u72d0\u88d8\u4e3a\u5979\u62ab\u4e0a\uff0c\u4e5f\u8bb8\u662f\u56de\u6765\u5c06\u4ed6\u4eec\u65b9\u624d\u541f\u51fa\u7684\u4f73\u53e5\u8bb0\u4e0b\uff0c\u514d\u5f97\u4ee5\u540e\u9057\u5fd8\u3002 \u3000\u3000\u4f46\u73b0\u5728\u674e\u5bfb\u6b22\u60f3\u53bb\u9057\u5fd8\u65f6\uff0c\u624d\u77e5\u9053\u90a3\u4ef6\u4e8b\u662f\u6c38\u8fdc\u65e0\u6cd5\u9057\u5fd8\u7684\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u90a3\u65f6\u4ed6\u53c8\u4f55\u82e6 \u53bb\u7528\u7b14\u58a8\u8bb0\u4e0b\uff1f \u3000\u3000\u96ea\uff0c\u53c8\u5728\u843d\u4e86\u3002 \u3000\u3000\u96ea\u82b1\u8f7b\u8f7b\u5730\u6ef4\u5728\u7a97\u5b50\u4e0a\uff0c\u5b9b\u5982\u60c5\u4eba\u7684\u7ec6\u8bed\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u957f\u957f\u5537\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5341\u5e74\u4e86\u2014\u2014\u4e5f\u8bb8\u5df2\u4e0d\u6b62\u5341\u5e74\u4e86\uff0c\u6709\u65f6\u65f6\u95f4\u4eff\u4f5b\u8fc7\u5f97\u5f88 \u6162\uff0c\u4f46\u7b49\u5b83\u771f\u8fc7\u53bb\u65f6\uff0c\u4f60\u624d\u4f1a\u53d1\u73b0\u5b83\u5feb\u5f97\u4ee4\u4f60\u5403\u60ca\u3002 \u3000\u3000\u9f99\u5578\u4e91\u81ea\u7136\u4e5f\u6709\u5f88\u591a\u611f\u6168\uff0c\u5374\u5ffd\u53c8\u7b11\u9053\uff1a\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u6211\u7b2c\u4e00\u5929\u5230\u8fd9\u91cc\u6765\u7684\u65f6\u5019\uff0c\u90a3\u5929 \u597d\u50cf\u4e5f\u5728\u4e0b\u96ea\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u2014\u2014\u6211\u600e\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5927\u7b11\u9053\uff1a\u6211\u8bb0\u5f97\u90a3\u5929\u6211\u4eec\u4e24\u4eba\u51e0\u4e4e\u5c06\u4f60\u5bb6\u7684\u85cf\u9152\u90fd\u559d\u5149\u4e86\uff0c\u4e5f\u662f\u6211\u552f\u4e00\u770b\u5230\u4f60\u559d \u9189\u7684\u4e00\u6b21\uff0c\u4f46\u4f60\u5374\u786c\u662f\u4e0d\u80af\u627f\u8ba4\u559d\u9189\uff0c\u8fd8\u8981\u548c\u6211\u6253\u8d4c\uff0c\u8bf4\u4f60\u53ef\u4ee5\u7528\u6b63\u6977\u5c06\u675c\u5de5\u90e8\u7684\u300a\u79cb\u5174\u516b \u9996\u300b\u5199\u51fa\u6765\uff0c\u800c\u4e14\u7edd\u5bf9\u4e00\u7b14\u4e0d\u8317\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5728\u684c\u4e0a\u7684\u7b14\u7b52\u91cc\u62bd\u51fa\u4e86\u4e00\u7b14\uff0c\u53c8\u9053\uff1a\u6211\u8fd8\u8bb0\u5f97\u4f60\u7528\u7684\u5c31\u662f\u8fd9\u652f\u7b14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u7b11\u5bb9\u867d\u7136\u90a3\u4e48\u82e6\u6da9\uff0c\u5374\u8fd8\u662f\u7b11\u7740\u9053\uff1a\u6211\u4e5f\u8bb0\u5f97\u90a3\u6b21\u6253\u8d4c\u8fd8\u662f\u6211\u8d62\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u4f46\u4f60\u5927\u6982\u672a\u60f3\u5230\uff0c\u8fc7\u4e86\u5341\u591a\u5e74\u540e\uff0c\u8fd9\u7b14\u8fd8\u4f1a\u5728\u8fd9\u91cc\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u4e0d\u8bed\uff0c\u4f46\u5fc3\u91cc\u5374\u4e0d\u7981\u6cdb\u8d77\u4e00\u9635\u51c4\u51c9\u4e4b\u610f\uff1a\u7b14\u867d\u7136\u4ecd\u5728\uff0c\u600e\u5948\u5df2\u6362\u4e86\u4e3b\u4eba\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u8bf4\u6765\u4e5f\u5947\u602a\uff0c\u6797\u4ed9\u513f\u597d\u50cf\u65e9\u5df2\u7b97\u51c6\u4f60\u8981\u56de\u6765\u4f3c\u7684\uff0c\u867d\u5df2\u4f4f\u5230\u8fd9\u91cc\u597d\u591a\u5e74\u4e86\uff0c \u4f46\u8fd9\u91cc\u7684\u4e00\u8349\u4e00\u6728\u5979\u90fd\u672a\u52a8\u8fc7\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u5979\u672c\u4e0d\u5fc5\u5982\u6b64\u505a\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u6211\u4eec\u5e76\u6ca1\u6709\u8981\u5979\u8fd9\u4e48\u505a\uff0c\u4f46\u5979\u5374\u8bf4\u2014\u2014 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u5524\u9053\uff1a\u56db\u7237\uff0c\u9f99\u56db\u7237\uff01 \u3000\u3000\u9f99\u5578\u4e91\u63a8\u5f00\u7a97\u5b50\uff0c\u76b1\u7709\u9053\uff1a\u6211\u5728\u8fd9\u91cc\uff0c\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u56de\u5934\u9053\uff1a\u5144\u5f1f\u4f60\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u2014\u2014\u6211\u8fd8\u60f3\u5728\u8fd9\u91cc\u770b\u770b\uff0c\u4e0d\u77e5\u9053\u53ef\u4e0d\u53ef\u4ee5\uff1f \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u5f53\u7136\u53ef\u4ee5\uff0c\u8fd9\u672c\u662f\u4f60\u7684\u5730\u65b9\uff0c\u5c31\u7b97\u6797\u4ed9\u513f\u56de\u6765\uff0c\u4e5f\u53ea\u6709\u6b22\u8fce\u7684\u3002 \u3000\u3000\u4ed6\u5306\u5306\u8d70\u4e86\u51fa\u53bb\uff0c\u4e00\u8d70\u51fa\u95e8\uff0c\u7b11\u5bb9\u5c31\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5728\u4e00\u5f20\u5bbd\u5927\u7684\u3001\u94fa\u7740\u864e\u76ae\u7684\u7d2b\u6a80\u6728\u6905\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u8fd9\u5f20\u6905\u5b50\uff0c\u53ea\u6015\u6bd4\u4ed6\u7684\u5e74\u7eaa\u8fd8 \u8981\u5927\u4e9b\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u81ea\u5df1\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u603b\u662f\u559c\u6b22\u722c\u5230\u8fd9\u5f20\u6905\u5b50\u4e0a\u6765\u4e3a\u4ed6\u7684\u7236\u4eb2\u9761\u58a8\uff0c\u4ed6\u53ea\u5e0c\u671b\u80fd\u5feb\u957f \u9ad8\uff0c\u80fd\u5750\u5230\u6905\u5b50\u4e0a\uff0c\u90a3\u65f6\u4ed6\u5fc3\u91cc\u603b\u6709\u4e00\u79cd\u5947\u5999\u7684\u60f3\u6cd5\uff0c\u603b\u662f\u6015\u6905\u5b50\u4e5f\u4f1a\u548c\u4eba\u4e00\u6837\uff0c\u4e5f\u4f1a\u6e10\u6e10 \u957f\u9ad8\u3002 \u3000\u3000\u7ec8\u4e8e\u6709\u4e00\u5929\uff0c\u4ed6\u80fd\u5750\u5230\u6905\u5b50\u4e0a\u4e86\uff0c\u4ed6\u4e5f\u5df2\u77e5\u9053\u6905\u5b50\u7edd\u4e0d\u4f1a\u957f\u9ad8\uff0c\u90a3\u65f6\u4ed6\u53c8\u4e0d\u7981\u6697\u6697\u4e3a\u8fd9 \u5f20\u6905\u5b50\u60b2\u54c0\uff0c\u89c9\u5f97\u5b83\u5f88\u53ef\u601c\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u53ea\u5e0c\u671b\u81ea\u5df1\u80fd\u548c\u8fd9\u5f20\u6905\u5b50\u4e00\u6837\uff0c\u6c38\u4e0d\u957f\u5927\uff0c\u4e5f\u6c38\u8fdc\u6ca1\u6709\u60b2\u4f24\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u6905 \u5b50\u4ecd\u4f9d\u65e7\uff0c\u4eba\u90fd\u5df2\u8001\u4e86\u3002 \u3000\u3000\u8001\u4e86\u2014\u2014\u8001\u4e86\u2014\u2014 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u8f7b\u8f7b\u7b11\u9053\uff1a\u8c01\u8bf4\u4f60\u8001\u4e86\uff1f \u3000\u3000\u4eba\u8fd8\u5728\u7a97\u5916\uff0c\u4f46\u7b11\u58f0\u5df2\u5728\u5c4b\u5b50\u91cc\u8361\u6f3e\u8d77\u4e00\u9635\u6e29\u6696\u4e4b\u610f\uff0c\u5979\u7684\u4eba\u867d\u8fd8\u672a\u8fdb\u6765\uff0c\u5374\u5df2\u5c06\u6625\u5929 \u5e26\u4e86\u8fdb\u6765\uff0c\u7b11\u58f0\u5df2\u7136\u5982\u6b64\uff0c\u4eba\u81ea\u7136\u66f4\u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u773c\u775b\u7acb\u523b\u4eae\u4e86\u8d77\u6765\uff0c\u4f46\u5374\u53ea\u662f\u9759\u9759\u671b\u7740\u90a3\u6247\u95e8\uff0c\u65e2\u6ca1\u6709\u7ad9\u8d77\uff0c\u4e5f\u5e76\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ec8\u4e8e\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\u7684\u773c\u775b\u5e76\u6ca1\u6709\u778e\uff0c\u5979\u7684\u786e\u662f\u4eba\u95f4\u7edd\u8272\uff0c\u82e5\u6709\u4eba\u66fe\u7528\u82b1\u6765\u63cf\u8ff0\u8fc7\u5979\uff0c\u90a3\u4eba\u5b9e\u5728\u662f \u8fb1\u6ca1\u4e86\u5979\u3002 \u3000\u3000\u4e16\u4e0a\u53c8\u6709\u54ea\u79cd\u9c9c\u82b1\u80fd\u53ca\u5979\u5982\u6b64\u52a8\u4eba\uff1f \u3000\u3000\u5979\u5168\u8eab\u867d\u7136\u6ca1\u6709\u4e00\u5904\u4e0d\u4ee4\u4eba\u9500\u9b42\uff0c\u4f46\u6700\u9500\u9b42\u5904\u8fd8\u662f\u5979\u7684\u773c\u775b\uff0c\u6ca1\u6709\u7537\u4eba\u80fd\u6297\u62d2\u5979\u8fd9\u53cc\u773c \u775b\u3002 \u3000\u3000\u8fd9\u662f\u53cc\u4ee4\u4eba\u72af\u7f6a\u7684\u773c\u775b\u3002 \u3000\u3000\u5979\u7684\u6001\u5ea6\u5374\u662f\u90a3\u4e48\u4eb2\u5207\uff0c\u90a3\u4e48\u5927\u65b9\uff0c\u6ca1\u6709\u4e1d\u6beb\u8981\u4ee4\u4eba\u72af\u7f6a\u7684\u610f\u601d\uff0c\u770b\u6765\u53c8\u4eff\u4f5b\u4e16\u4e0a\u6700\u6e29 \u67d4\u3001\u6700\u7eaf\u6d01\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5979\u770b\u6765\u50cf\u4ec0\u4e48\uff0c\u90fd\u5df2\u65e0\u6cd5\u6539\u53d8\u674e\u5bfb\u6b22\u5bf9\u5979\u7684\u5370\u8c61\u4e86\uff0c\u56e0\u4e3a\u674e\u5bfb\u6b22\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\u89c1 \u5230\u5979\u3002 \u3000\u3000\u5c31\u5728\u90a3\u9152\u5e97\u7684\u53a8\u623f\u91cc\uff0c\u5c31\u5728\u8537\u8587\u592b\u4eba\u7684\xd7\u4f53\u65c1\uff0c\u674e\u5bfb\u6b22\u65e9\u5df2\u9886\u6559\u8fc7\u5979\u7684\u6e29\u67d4\uff0c\u5979\u7684\u7eaf\u6d01 \uff01\u4f46\u674e\u5bfb\u6b22\u5374\u51e0\u4e4e\u8fd8\u662f\u96be\u4ee5\u60f3\u5ff5\u773c\u524d\u8fd9\u5973\u4eba\uff0c\u5c31\u662f\u90a3\u5929\u4e00\u5fc3\u8981\u903c\u4ed6\u4ea4\u6362\u91d1\u4e1d\u7532\u7684\u795e\u79d8\u7f8e\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u5979\u7684\u795e\u60c5\u548c\u90a3\u5929\u7684\u786e\u5c31\u597d\u50cf\u662f\u4e24\u4e2a\u4eba\uff0c\u82e5\u4e0d\u662f\u674e\u5bfb\u6b22\u786e\u4fe1\u81ea\u5df1\u7edd\u4e0d\u4f1a\u770b\u9519\uff0c\u90a3 \u4e48\u4ed6\u5c31\u7b80\u76f4\u4e0d\u80fd\u60f3\u5ff5\u90a3\u5929\u90a3\u6bd2\u8fa3\u3001\u6deb\u8361\u3001\u663e\u7136\u5df2\u9971\u7ecf\u6ca7\u6851\u7684\u5973\u5b50\uff0c\u5c31\u662f\u773c\u524d\u8fd9\u7b11\u5f97\u53c8\u5929\u771f\u3001 \u53c8\u751c\u871c\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u95ed\u4e0a\u773c\u775b\uff0c\u96be\u9053\u4e0d\u613f\u89c1\u6211\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u53ea\u4e0d\u8fc7\u662f\u5728\u56de\u60f3\u90a3\u5929\u4f60\u8131\u5149\u4e86\u8863\u670d\u65f6\u7684\u6a21\u6837\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u8138\u4f3c\u4e4e\u7ea2\u4e86\u7ea2\uff0c\u5e7d\u5e7d\u53f9\u9053\uff1a\u6211\u672c\u6765\u5e0c\u671b\u4f60\u8ba4\u4e0d\u51fa\u6211\u7684\uff0c\u53ef\u662f\u6211\u4e5f\u77e5\u9053\u5e0c\u671b\u5e76\u4e0d \u5927\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u82e5\u8fd9\u4e48\u5feb\u5c31\u5c06\u4f60\u5fd8\u8bb0\u4e86\uff0c\u4f60\u5c82\u975e\u4e5f\u4f1a\u89c9\u5f97\u5f88\u5931\u671b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u53ef\u662f\u4f60\u89c1\u5230\u6211\u5e76\u672a\u5403\u5403\u60ca\uff0c\u96be\u9053\u4f60\u65e9\u5df2\u60f3\u5230\u6211\u662f\u8c01\u4e86\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6b66\u6797\u4e2d\u80fd\u88ab\u79f0\u4e3a\u7f8e\u4eba\u7684\u4eba\u5e76\u4e0d\u591a\u5427\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4f60\u89c1\u5230\u4f0a\u54ed\u7684\u5f92\u5f1f\uff0c\u5c31\u60f3\u5230\u4e86\u6211\u90a3\u53cc\u9752\u9b54\u624b\uff0c\u89c1\u5230\u4e86\u6e38\u9f99\u751f \uff0c\u5c31\u60f3\u5230\u4e86\u6211\u7684\u9c7c\u85cf\u5251\uff0c\u662f\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u53ea\u5947\u602a\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u6211\u5728\u8fd9\u91cc\uff0c\u600e\u4e48\u8fd8\u6562\u6765\u89c1\u6211\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u606f\u9053\uff1a\u54ac\u7740\u5634\u5507\u9053\uff1a\u4e11\u5ab3\u5987\u65e2\u7136\u96be\u514d\u89c1\u516c\u5a46\uff0c\u8eb2\u7740\u4e5f\u6ca1\u6709\u7528\u7684\uff0c\u6240\u4ee5\uff0c\u9f99\u56db\u54e5 \u4e00\u53eb\u6211\u6765\uff0c\u6211\u7acb\u523b\u5c31\u8d76\u6765\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f\u662f\u4ed6\u8981\u4f60\u6765\u7684\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u4f60\u96be\u9053\u8fd8\u4e0d\u61c2\u4ed6\u7684\u610f\u601d\uff1f\u4ed6\u65e9\u5c31\u60f3\u4e3a\u6211\u4eec\u62c9\u62e2\u4e86\uff0c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6 \u89c9\u5f97\u6709\u4e9b\u5bf9\u4e0d\u8d77\u4f60\uff0c\u62a2\u4e86\u4f60\u7684\u2014\u2014 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u7684\u8138\u9aa4\u7136\u6c89\u4e86\u4e0b\u6765\uff0c\u56e0\u4e3a\u4ed6\u5df2\u77e5\u9053\u5979\u8981\u8bf4\u4ec0\u4e48\u4e86\uff0c\u4f46\u4ed6\u7684\u8138\u4e00\u6c89\uff0c\u6797 \u4ed9\u513f\u4e5f\u7acb\u523b\u505c\u4f4f\u4e86\u5634\u3002 \u3000\u3000\u5979\u6c38\u8fdc\u4e0d\u4f1a\u8bf4\u522b\u4eba\u4e0d\u7231\u542c\u7684\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u4f3c\u4e4e\u8fd8\u5728\u7b49\u5979\u8bf4\u4e0b\u53bb\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u4ed6\u5e76\u6ca1\u6709\u5bf9\u4e0d\u8d77\u6211\uff0c\u522b\u4eba\u90fd\u6ca1 \u6709\u5bf9\u4e0d\u8d77\u6211\uff0c\u53ea\u6709\u6211\u5bf9\u4e0d\u8d77\u522b\u4eba\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8109\u8109\u5730\u51dd\u6ce8\u7740\u4ed6\uff0c\u9053\uff1a\u4f60\u5bf9\u4e0d\u8d77\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u6211\u5bf9\u4e0d\u8d77\u7684\u4eba\u592a\u591a\u4e86\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u6570\u4e0d\u6e05\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u968f\u4fbf\u4f60\u600e\u4e48\u8bf4\uff0c\u6211\u90fd\u77e5\u9053\u4f60\u7edd\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u77e5\u9053\u6211\u662f\u600e\u6837\u7684\u4e00\u4e2a\u4eba\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u5f53\u7136\u77e5\u9053\uff0c\u6211\u5f88\u5c0f\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u5c31\u542c\u8bf4\u8fc7\u4f60\u7684\u4e8b\u4e86\uff0c\u6240\u4ee5\u5f53\u6211\u77e5\u9053\u8fd9\u5c31\u662f \u4f60\u4ee5\u524d\u4f4f\u7684\u5730\u65b9\u65f6\uff0c\u6211\u5174\u594b\u5f97\u7b80\u76f4\u6ca1\u6cd5\u5b50\u7761\u89c9\u3002 \u3000\u3000\u5979\u8f7b\u76c8\u5730\u8f6c\u4e86\u4e2a\u8eab\uff0c\u9053\uff1a\u4f60\u770b\uff0c\u8fd9\u5c4b\u5b50\u91cc\u6240\u6709\u7684\u4e1c\u897f\uff0c\u662f\u4e0d\u662f\u5168\u90fd\u548c\u4f60\u5341\u5e74\u524d\u79bb\u5f00\u8fd9\u91cc \u65f6\u4e00\u6837\uff1f\u5c31\u8fde\u4f60\u85cf\u5728\u4e66\u67b6\u91cc\u7684\u90a3\u74f6\u9152\uff0c\u6211\u90fd\u6ca1\u6709\u52a8\u8fc7\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u6930\u5bfb\u6b22\u53ea\u662f\u51b7\u51b7\u5730\u671b\u7740\u5979\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4f60\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\uff0c\u4f46\u9488\u9093\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u56e0\u4e3a\u53ea\u6709\u8fd9\u6837\uff0c\u6211\u624d\u611f\u89c9\u5230 \u8fd9\u662f\u4f60\u4f4f\u7684\u5730\u65b9\uff0c\u6709\u65f6\u6211\u751a\u81f3\u89c9\u5f97\u4f60\u8fd8\u5728\u8fd9\u5c4b\u5b50\u91cc\uff0c\u5750\u5728\u8fd9\u6905\u5b50\u4e0a\uff0c\u9759\u9759\u5730\u770b\u7740\u6211\uff0c\u8f7b\u8f7b\u5730 \u966a\u7740\u6211\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u773c\u6ce2\u6e10\u6e10\u6726\u80e7\uff0c\u4f4e\u8bed\u7740\u9053\uff1a\u6709\u65f6\u6211\u534a\u591c\u9192\u6765\uff0c\u603b\u89c9\u5f97\u4f60\u4eff\u4f5b\u5c31\u7761\u5728\u6211\u8eab\u65c1\uff0c\u90a3\u5e8a\u4e0a\u3001 \u6795\u5934\u4e0a\uff0c\u8fd8\u7559\u7740\u4f60\u7684\u6c14\u606f\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u53ea\u6015\u8fd8\u6709\u522b\u7684\u4eba\u5427\uff1f \u3000\u3000\u6797\u4ed9\u513f\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u9053\uff1a\u4f60\u4ee5\u4e3a\u8fd9\u5c4b\u5b50\u8fd8\u6709\u522b\u4eba\u8fdb\u6765\u8fc7\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u8fd9\u5730\u65b9\u5df2\u7ecf\u5c5e\u4e8e\u4f60\uff0c\u4f60\u8ba9\u8c01\u8fdb\u6765\u90fd\u65e0\u59a8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4ee5\u4e3a\u6e38\u9f99\u751f\u3001\u4e18\u72ec\u8fd9\u4e9b\u4eba\u4e00\u5b9a\u8fdb\u6765\u8fc7\uff0c\u662f\u5417\uff1f \u3000\u3000\u5979\u773c\u5708\u4f3c\u5df2\u7ea2\u4e86\uff0c\u9053\uff1a\u544a\u8bc9\u4f60\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u8ba9\u4ed6\u4eec\u8d70\u8fdb\u8fc7\u8fd9\u9053\u95e8\uff0c\u6240\u4ee5\u4ed6\u4eec\u53ea\u6709\u7b49\u5728 \u6885\u6797\u4e2d\uff0c\u6211\u82e5\u80af\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff0c\u4e18\u72ec\u548c\u79e6\u91cd\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u6b7b\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u4f60\u4e3a\u4f55\u4e0d\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff1f \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\u9053\uff1a\u53ea\u56e0\u4e3a\u8fd9\u662f\u4f60\u7684\u5730\u65b9\uff0c\u6211\u8981\u2014\u2014\u66ff\u4f60\u4fdd\u7559\u7740\u2014\u2014 \u3000\u3000\u5979\u4f3c\u4e4e\u4e0d\u77e5\u600e\u4e48\u8bf4\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u5fae\u4e00\u7b11\uff0c\u66ff\u5979\u63a5\u4e0b\u53bb\uff0c\u9053\uff1a\u5473\u9053\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7684\u8138\u7ea2\u4e86\uff0c\u5782\u9996\u9053\uff1a\u6211\u7684\u610f\u601d\uff0c\u4f60\u660e\u767d\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4f46\u6211\u5374\u76f4\u5230\u73b0\u5728\u624d\u77e5\u9053\u6211\u8eab\u4e0a\u662f\u6709\u5473\u9053\u2014\u2014\u662f\u4ec0\u4e48\u5473\u9053\uff1f\u662f\u9999\uff1f\u8fd8\u662f\u81ed\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7684\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u6211\u5bf9\u4f60\u8bf4\u4e86\u8fd9\u4e9b\u8bdd\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u4f60\u803b\u7b11\u6211\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u7684\u610f\u601d\u4f60\u8fd8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u7528\u4e0d\u7740\u522b\u4eba\u62c9\u62e2\uff0c\u6211\u4e5f\u5f88\u6709\u5e0c\u671b\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u82e5\u4e0d\u662f\u6211\u65e9\u5df2\u2014\u2014\u65e9\u5df2\u5bf9\u4f60\u2014\u2014\u90a3\u5929\u6211\u600e\u4e48\u4f1a\u5bf9\u4f60\u2014\u2014 \u3000\u3000\u867d\u7136\u6bcf\u53e5\u8bdd\u5979\u90fd\u53ea\u8bf4\u4e86\u4e00\u534a\uff0c\u4f46\u6709\u65f6\u8bdd\u53ea\u8bf4\u4e00\u534a\uff0c\u6bd4\u5168\u8bf4\u51fa\u6765\u8fd8\u8981\u6709\u6548\u5f97\u591a\uff0c\u4e5f\u6709\u8da3\u5f97 \u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u7b11\u9053\uff1a\u539f\u6765\u4f60\u90a3\u5929\u53ea\u662f\u4e3a\u4e86\u559c\u6b22\u6211\u800c\u90a3\u6837\u505a\u7684\uff0c\u6211\u8fd8\u5f53\u4f60\u662f\u4e3a\u4e86\u91d1\u4e1d\u7532\u54e9\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u2014\u2014\u6211\u5f53\u7136\u4e5f\u662f\u4e3a\u4e86\u91d1\u4e1d\u7532\uff0c\u4f46\u5bf9\u8c61\u82e5\u4e0d\u662f\u4f60\uff0c\u6211\u600e\u4e48\u80af\u2014\u2014\u600e\u4e48\u80af\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u539f\u6765\u4f60\u90a3\u6837\u505a\u662f\u4e00\u4e3e\u4e24\u5f97\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4e00\u5b9a\u8fd8\u5728\u5947\u602a\uff0c\u6211\u4e3a\u4ec0\u4e48\u90a3\u4e48\u60f3\u8981\u91d1\u4e1d\u7532\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u5b9e\u5728\u6709\u70b9\u5947\u602a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u90a3\u53ea\u56e0\u6211\u60f3\u4eb2\u624b\u6740\u6b7b\u6885\u82b1\u76d7\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u65e0\u8bba\u8c01\u6740\u6b7b\u6885\u82b1\u76d7\uff0c\u6211\u90fd\u8981\u5ac1\u7ed9\u4ed6\uff0c\u8fd9\u8bdd\u867d\u662f\u6211\u81ea\u5df1\u8bf4\u7684\uff0c\u53ef \u662f\u5176\u4e2d\u4e5f\u6709\u5f88\u591a\u82e6\u8877\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4f60\u8981\u4eb2\u624b\u6740\u6b7b\u6885\u82b1\u76d7\uff0c\u96be\u9053\u662f\u4e3a\u4e86\u8981\u4f60\u81ea\u5df1\u5ac1\u7ed9\u4f60\u81ea\u5df1\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u8fd9\u6837\u505a\uff0c\u53ea\u662f\u4e3a\u4e86\u6211\u4e0d\u613f\u5ac1\u4eba\uff0c\u6240\u4ee5\u6211\u82e5\u81ea\u5df1\u6740\u6b7b\u6885\u82b1\u76d7\uff0c\u5c31\u7528\u4e0d\u7740\u5ac1\u7ed9 \u522b\u4eba\u4e86\u3002 \u3000\u3000\u5979\u5ffd\u7136\u62ac\u5934\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u5e7d\u5e7d\u9053\uff1a\u53ea\u56e0\u5929\u4e0b\u7684\u7537\u4eba\u6ca1\u6709\u4e00\u4e2a\u662f\u6211\u770b\u5f97\u4e0a\u773c\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u4e5f\u5728\u51dd\u6ce8\u7740\u5979\uff0c\u9053\uff1a\u6211\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7ea2\u7740\u8138\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u4f60\u81ea\u7136\u662f\u4f8b\u5916\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5c0f\u58f0\u9053\uff1a\u56e0\u4e3a\u4f7b\u548c\u522b\u7684\u7537\u4eba\u90fd\u4e0d\u540c\uff0c\u90a3\u4e9b\u4eba\u5c31\u50cf\u72d7\u4e00\u6837\uff0c\u65e0\u8bba\u6211\u600e\u4e48\u5bf9\u4ed6\u4eec\uff0c\u4ed6 \u4eec\u8fd8\u662f\u8981\u6b7b\u7f20\u7740\u6211\uff0c\u53ea\u6709\u4f60\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u90a3\u4e48\u4f60\u4e3a\u4f55\u4e0d\u5c06\u91d1\u4e1d\u7532\u7559\u5728\u6211\u8fd9\u91cc\uff0c\u7b49\u6211\u6740\u6b7b\u4e86\u6885\u82b1\u76d7\uff0c\u518d\u5ac1\u7ed9 \u6211\uff0c\u8fd9\u6837\u5c82\u975e\u4e5f\u4e00\u4e3e\u4e24\u5f97\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u4f3c\u4e4e\u6014\u4e86\u6014\uff0c\u4f46\u968f\u5373\u5ae3\u7b11\u9053\uff1a\u8fd9\u5728\u662f\u597d\u4e3b\u610f\uff0c\u6211\u4e3a\u4f55\u6ca1\u6709\u60f3\u8d77\u5f97\u51fa\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u8fd9\u4e48\u597d\u7684\u4e3b\u610f\uff0c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u60f3\u5f97\u51fa\uff1f \u3000\u3000\u6797\u4ed9\u513f\u4f3c\u4e4e\u542c\u4e0d\u51fa\u4ed6\u8bdd\u4e2d\u8ba5\u8bee\u4e4b\u610f\uff0c\u7d27\u7d27\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u6211\u77e5\u9053\u6885\u82b1\u76d7\u8fd9\u4e24\u5929\u4e00\u5b9a \u4f1a\u6765\uff0c\u660e\u5929\u6211\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u8981\u6211\u660e\u5929\u4e5f\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4ee5\u6211\u4e3a\u9975\uff0c\u5c06\u4ed6\u5f15\u6765\uff0c\u53cd\u6b63\u91d1\u4e1d\u7532\u5728\u4f60\u8eab\u4e0a\uff0c\u4f60\u7eb5\u7136\u5236\u4e0d\u4f4f\u4ed6\uff0c\u4ed6\u65e0\u8bba\u5982 \u4f55\u4e5f\u4f24\u4e0d\u4e86\u4f60\u7684\uff0c\u4f60\u82e5\u5236\u4f4f\u4e86\u4ed6\u2014\u2014\u5979\u53c8\u7ea2\u7740\u8138\u5782\u4e0b\u5934\uff0c\u90a3\u53cc\u773c\u775b\u4ecd\u5728\u6084\u6084\u779f\u7740\u674e\u5bfb\u6b22\uff0c\u5634 \u91cc\u6ca1\u6709\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5df2\u7528\u773c\u775b\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u773c\u775b\u91cc\u4e5f\u5728\u95ea\u7740\u5149\uff0c\u7b11\u9053\uff1a\u597d\uff0c\u660e\u591c\u6211\u4e00\u5b9a\u6765\uff0c\u6211\u82e5\u4e0d\u6765\uff0c\u5c31\u2026\u2026 \u3000\u3000\u6797\u4ed9\u513f\u6084\u6084\u7f29\u56de\u4e86\u624b\uff0c\u4f46\u7ec6\u7ec6\u7684\u6307\u5c16\u4ecd\u5728\u674e\u5bfb\u6b22\u624b\u80cc\u4e0a\u8f7b\u8f7b\u5730\u753b\u7740\u5706\u5708\uff0c\u4f3c\u4e4e\u8981\u5708\u4f4f\u674e \u5bfb\u6b22\u7684\u5fc3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u53c8\u7b11\u9053\uff1a\u4f60\u603b\u7b97\u5df2\u5b66\u4e58\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ea2\u7740\u8138\u9053\uff1a\u6211\u672c\u6765\u5c31\u5f88\u4e58\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u603b\u7b97\u5df2\u5b66\u4f1a\u8ba9\u7537\u4eba\u6765\u4e3b\u52a8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5598\u606f\u5ffd\u7136\u6025\u4fc3\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u4f46\u4f60\u2014\u2014\u4f60\u73b0\u5728\u4e0d\u4f1a\u7684\u2014\u2014\u662f\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u51dd\u6ce8\u7740\u5979\uff0c\u76ee\u5149\u4ecd\u662f\u90a3\u4e48\u51b7\u9759\uff0c\u5c31\u50cf\u662f\u4e00\u6e56\u79cb\u6c34\uff0c\u5634\u91cc\u5374\u5df2\u9732\u51fa\u4e86\u5e76\u4e0d\u51b7\u9759\u7684\u7b11 \u5bb9\uff0c\u9053\uff1a\u600e\u77e5\u9053\u6211\u4e0d\u4f1a\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5403\u5403\u5730\u5a07\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u56e0\u4e3a\u4f60\u662f\u4e2a\u541b\u5b50\uff0c\u662f\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u7b11\u9053\uff1a\u6211\u5e73\u751f\u53ea\u505a\u8fc7\u4e00\u6b21\u541b\u5b50\uff0c\u90a3\u6b21\u6211\u540e\u6094\u4e86\u4e09\u5929\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5a07\u7b11\u7740\uff0c\u4f3c\u4e4e\u60f3\u9003\u8d70\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5df2\u4e00\u628a\u62c9\u4f4f\u4e86\u5979\uff0c\u7b11\u9053\uff1a\u539f\u6765\u4f60\u4e0d\u4f46\u5b66\u4f1a\u4e86\u8ba9\u7537\u4eba\u4e3b\u52a8\uff0c\u8fd8\u5b66\u4f1a\u4e86\u9003\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5624\u549b\u4e00\u58f0\uff0c\u5598\u606f\u7740\u9053\uff1a\u8fd9\u5168\u662f\u4f60\u6559\u6211\u7684\uff0c\u662f\u4f60\u6559\u6211\u8be5\u5982\u4f55\u52fe\u5f15\u4f60\uff0c\u662f\u5417\uff1f \u7b2c\u5341\u7ae0\u3000\u5341\u516b\u5e74\u65e7\u6028>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u7ae0\u3000\u5341\u516b\u5e74\u65e7\u6028 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u6559\u5f97\u592a\u591a\uff0c\u4f60\u4e5f\u5b66\u5f97\u592a\u5feb\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u63a8\u5f00\u4e86\u5979\uff0c\u62cd\u4e86\u62cd\u8863\u88f3\u7ad9\u8d77\u6765\uff0c\u77aa\u7740\u7a97\u5b50\u9053\uff1a\u4eca\u5929\u7684\u620f\u5df2\u6f14\u5b8c\u4e86\uff0c\u9601\u4e0b\u82e5\u662f\u8fd8\u672a \u770b\u591f\uff0c\u660e\u5929\u8bf7\u65e9\u5427\u3002 \u3000\u3000\u7a97\u5916\u4f20\u6765\u4e86\u55e4\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u4e00\u4eba\u9053\uff1a\u9601\u4e0b\u7684\u624b\u6bb5\u679c\u7136\u9ad8\u660e\uff0c\u4f46\u671b\u9601\u4e0b\u7684\u98de\u5200\u4e5f\u540c\u6837\u9ad8\u660e \u624d\u597d\u3002 \u3000\u3000\u8bf4\u5230\u540e\u9762\u4e00\u53e5\u8bdd\uff0c\u8bed\u58f0\u5df2\u8fdc\u5728\u5341\u4e08\u5f00\u5916\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53d8\u8272\u9053\uff1a\u662f\u6e38\u9f99\u751f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u9053\uff1a\u4f60\u6015\u4ed6\u5403\u918b\uff1f \u3000\u3000\u6797\u4ed9\u513f\u76ee\u4e2d\u9732\u51fa\u4e86\u72e0\u6bd2\u4e4b\u610f\uff0c\u51b7\u7b11\u9053\uff1b\u4ed6\u51ed\u4ec0\u4e48\u5403\u918b\uff1f\u2014\u2014\u60f3\u4e0d\u5230\u8fd9\u79cd\u81ea\u547d\u4e0d\u51e1\u7684\u4e16\u5bb6 \u5b50\u5f1f\uff0c\u4e5f\u4f1a\u505a\u8fd9\u79cd\u4e0d\u8981\u8138\u7684\u4e8b\uff0c\u4ee5\u540e\u6211\u82e5\u518d\u7406\u4ed6\u624d\u602a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u4f60\u4e0d\u6015\u4ed6\u5c06\u9c7c\u85cf\u5251\u8981\u56de\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u5c31\u7b97\u5c06\u9c7c\u85cf\u5251\u4e22\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u4e5f\u4e0d\u6562\u6361\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff01 \u3000\u3000\u6797\u4ed9\u513f\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u8fd9\u79cd\u4eba\u5c31\u50cf\u72d7\u4e00\u6837\u5929\u751f\u7684\u8d31\u9aa8\u5934\uff0c\u4f60\u8d8a\u6253\u4ed6\u9a82\u4ed6\uff0c \u4ed6\u8981\u8ddf\u5728\u4f60\u540e\u9762\u6447\u5c3e\u5df4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6709\u6761\u72d7\u8ddf\u5728\u540e\u9762\u6447\u5c3e\u5df4\uff0c\u4e5f\u6ee1\u6709\u8da3\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u62c9\u4f4f\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u4f60\u96be\u9053\u771f\u662f\u8981\u8d70\u4e86\uff01\u4e3a\u4ec0\u4e48\u4e0d\u591a\u5750\u5750\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u6211\u82e5\u518d\u5750\u4e0b\u6765\uff0c\u7b49\u5230\u72d7\u6765\u54ac\u6211\u4e00\u53e3\uff0c\u90a3\u5c31\u65e0\u8da3\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u54fc\uff0c\u4ed6\u6562\u2014\u2014 \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u542c\u6e38\u9f99\u751f\u8fdc\u8fdc\u9053\uff1a\u8fd9\u8fb9\u7684\u620f\u6f14\u5b8c\u4e86\uff0c\u90a3\u8fb9\u53c8\u6709\u620f\u5f00\u9523\uff0c\u9601\u4e0b\u4e0d\u60f3\u53bb\u770b\u770b\u5417 \uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u4f60\u770b\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u8ba9\u6211\u518d\u5750\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6068\u6068\u9053\uff1a\u8ba8\u538c\u9b3c\u3002 \u3000\u3000\u5979\u5ffd\u53c8\u4e00\u7b11\uff0c\u62c9\u7740\u674e\u5bfb\u6b22\u7684\u624b\u9053\uff1a\u4f46\u6211\u4eec\u8fd8\u6709\u660e\u5929\uff0c\u660e\u5929\u665a\u4e0a\u83ab\u5fd8\u4e86\u65e9\u4e9b\u6765\u3002 \u3000\u3000\u6e38\u9f99\u751f\u5df2\u8d70\u4e86\uff0c\u4f46\u674e\u5bfb\u6b22\u4e00\u51fa\u6885\u82b1\u6797\uff0c\u5c31\u542c\u5f97\u8fdc\u5904\u4f20\u6765\u4e86\u4e00\u9635\xd7\u6012\u9a82\u58f0\u548c\u62f3\u98ce\u6fc0\u8361\u58f0\u3002 \u3000\u3000\u4ed6\u5df2\u542c\u51fa\u5176\u4e2d\u6709\u90a3\u5927\u6c49\u7684\u58f0\u97f3\uff0c\u7acb\u523b\u4e00\xd7\u8863\u895f\uff0c\u71d5\u5b50\u4e09\u6284\u6c34\uff0c\u53ea\u4e09\u4e2a\u8d77\u843d\uff0c\u5df2\u8d76\u4e86\u8fc7\u53bb \u3002 \u3000\u3000\u5047\u5c71\u540e\u4e5f\u6709\u4e09\u95f4\u660e\u8f69\uff0c\u8fd9\u65f6\u8f69\u524d\u7684\u7a7a\u5730\u4e0a\u6b63\u6709\u4e24\u4eba\u5728\u6076\u6597\uff0c\u4e24\u4eba\u4ff1\u662f\u62f3\u98ce\u521a\u731b\uff0c\u9707\u5f97\u56db \u4e0b\u79ef\u96ea\u6f2b\u5929\u98de\u8d77\u3002 \u3000\u3000\u53ea\u542c\u5927\u6c49\u6012\u559d\u7740\u9053\uff1a\u59d3\u79e6\u7684\uff0c\u4f60\u81ea\u547d\u4fa0\u4e49\uff0c\u5176\u5b9e\u5374\u4e00\u6587\u4e5f\u4e0d\u503c\uff0c\u4f60\u513f\u5b50\u4f24\u91cd\u4e0d\u6cbb\uff0c\u548c\u522b \u4eba\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f60\u600e\u80fd\u5bf9\u4ed6\u4e0b\u6bd2\u624b\uff1f \u3000\u3000\u548c\u4ed6\u52a8\u624b\u7684\u4eba\uff0c\u6b63\u662f\u94c1\u80c6\u9707\u516b\u65b9\u79e6\u5b5d\u4eea\uff0c\u6b64\u523b\u4e5f\u6012\u543c\u7740\u9053\uff1a\u4f60\u7b97\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f\u4e0d\u95ee\u81ea\u5df1 \u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u5c45\u7136\u6562\u6765\u7ba1\u8001\u592b\u7684\u95f2\u4e8b\uff0c\u8001\u592b\u7d22\u6027\u8fde\u4f60\u4e5f\u4e00\u9f50\u5e9f\u4e86\uff01 \u3000\u3000\u9f99\u5578\u4e91\u6b63\u5728\u4e00\u65c1\u8dfa\u7740\u811a\u76f8\u529d\uff0c\u6e38\u9f99\u751f\u5374\u5728\u8d1f\u624b\u65c1\u89c2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u71d5\u5b50\u822c\u63a0\u4e86\u8fc7\u53bb\uff0c\u9f99\u5578\u4e91\u7acb\u523b\u8fce\u4e0a\u6765\uff0c\u8dfa\u811a\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u5feb\u529d\u529d\u4ed6\u4eec\u5427\uff0c\u6885\u82b1\u76d7 \u8fd8\u672a\u73b0\u8eab\uff0c\u81ea\u5df1\u4eba\u5374\u5148\u6253\u8d77\u6765\u4e86\uff0c\u8fd9\u2014\u2014\u8fd9\u7b97\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u6e38\u9f99\u751f\u51b7\u7b11\u9053\uff1a\u8fd9\u5c31\u53eb\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\uff0c\u60f3\u4e0d\u5230\u674e\u63a2\u82b1\u7684\u95e8\u4e0b\u5974\u4e5f\u6709\u8fd9\u4e48\u5927\u7684\u672c\u4e8b\uff0c\u679c \u7136\u662f\u51f6\u5f97\u5f88\uff0c\u51f6\u5f97\u5f88\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u7684\u786e\u51f6\u5f97\u5f88\uff0c\u4f46\u522b\u4eba\u82e5\u4e0d\u60f3\u60f9\u4ed6\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u51f6\u7684\u3002 \u3000\u3000\u4ed6\u4e0d\u8ba9\u6e38\u9f99\u751f\u518d\u8bf4\u8bdd\uff0c\u5c31\u8f6c\u5411\u9f99\u5578\u4e91\u9053\uff1a\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u9f99\u5578\u4e91\u53f9\u9053\uff1a\u5c31\u56e0\u4e3a\u79e6\u91cd\u4f24\u91cd\u4e0d\u6cbb\uff0c\u6240\u4ee5\u79e6\u4e09\u54e5\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u4ed6\u81ea\u5df1\u513f\u5b50\u4f24\u91cd\u4e0d\u6cbb\uff0c\u96be\u9053\u5c31\u8fc1\u6012\u5728\u6885\u4e8c\u5148\u751f\u8eab\u4e0a\u3002 \u3000\u3000\u9f99\u5578\u4e91\u82e6\u7b11\u9053\uff1a\u4ed6\u4eec\u7236\u5b50\u60c5\u6df1\uff0c\u79e6\u4e09\u54e5\u81ea\u7136\u96be\u514d\u60b2\u75db\uff0c\u4e00\u65f6\u5931\u624b\u4f24\u4e86\u6885\u4e8c\u5148\u751f\uff0c\u4f46\u4f24\u7684 \u4e5f\u5e76\u4e0d\u592a\u91cd\u3002\u674e\u5bfb\u6b22\u51b7\u7b11\u4e86\u7b11\uff0c\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\uff1a\u4f60\u529d\u529d\u4ed6\u5427\uff0c\u6211\u77e5\u9053\u4ed6\u53ea\u542c\u4f60\u4e00\u4e2a\u4eba\u7684\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u6211\u4e3a\u4f55\u8981\u529d\u4ed6\uff0c\u4ed6\u82e5\u4e0d\u51fa\u624b\uff0c\u6211\u4e5f\u8981\u51fa\u7684\u7269\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6014\u4e86\u6014\uff0c\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5927\u6c49\u62f3\u98ce\u864e\u864e\uff0c\u62f3\u62f3\u90fd\u662f\u594b\u4e0d\u987e\u8eab\u7684\u62db\u5f0f\uff0c\u62db\u5f0f\u867d\u672a\u5fc5\u7cbe\u5999\uff0c\u90a3\u4e00\u80a1\u6740\u6c14\u5374\u4ee4\u4eba \u5fc3\u60ca\u3002 \u3000\u3000\u79e6\u5b5d\u4eea\u7adf\u4f3c\u5df2\u88ab\u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u6e38\u9f99\u751f\u51b7\u7b11\u7740\u53c8\u9053\uff1a\u5c0a\u4ec6\u7684\u8fd9\u79cd\u62db\u5f0f\uff0c\u5012\u7684\u786e\u5c11\u89c1\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u4ed6\u6bcf\u62db\u53d1\u51fa\uff0c\u597d\u50cf\u90fd\u51c6\u5907\u5148\u6328\u522b\u4eba\u4e00\u62f3\uff0c\u8fd9\u79cd\u62f3\u6cd5\u5012\u5b9e\u5728\u4ee4\u4eba\u6709\u4e9b\u770b\u4e0d\u61c2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u5176\u5b9e\u8fd9\u9053\u7406\u4e5f\u7b80\u5355\u5f97\u5f88\u3002 \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u54e6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u56e0\u522b\u4eba\u6253\u4ed6\u4e00\u62f3\uff0c\u4ed6\u6839\u672c\u4e0d\u5728\u4e4e\uff0c\u4ed6\u82e5\u6253\u522b\u4eba\u4e00\u62f3\uff0c\u90a3\u4eba\u53ea\u6015\u5c31\u5403\u4e0d\u6d88\u4e86 \u3002 \u3000\u3000\u6e38\u9f99\u751f\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u7a81\u542c\u4e00\u4eba\u6012\u543c\u9053\uff1a\u597d\u4e2a\u72d7\u4ed7\u4eba\u52bf\u7684\u5974\u624d\uff0c\u7adf\u6562\u4ee5\u4e0b\u72af\u4e0a \uff0c\u5f85\u8001\u592b\u6765\u6559\u8bad\u6559\u8bad\u4f60\uff01 \u3000\u3000\u543c\u58f0\u4e2d\uff0c\u8d75\u6b63\u4e49\u5df2\u98de\u4e5f\u4f3c\u5730\u8d76\u6765\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u5411\u90a3\u5927\u6c49\u6251\u8fc7\u53bb\uff0c\u7a81\u542c\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u82e5\u6709\u4eba\u60f3\u4ee5\u4e8c\u5bf9\u4e00\uff0c\u4ee5\u591a\u6b3a\u5c11\uff0c\u5728\u4e0b\u7684\u98de \u5200\u53ea\u597d\u51fa\u624b\uff01\u8d75\u6b63\u4e49\u8eab\u5f62\u7acb\u523b\u987f\u4f4f\uff0c\u518d\u4e5f\u4e0d\u6562\u4f38\u51fa\u4e00\u62f3\uff0c\u5927\u6012\u9053\uff1a\u4f60\u5e26\u6765\u7684\u5974\u624d\u4ee5\u4e0b\u72af\u4e0a\uff0c \u4f60\u975e\u4f46\u4e0d\u7ba1\u6559\u4ed6\uff0c\u53cd\u800c\u8fd8\u6765\u52a9\u957f\u4ed6\u7684\u6c14\u7130\uff0c\u4f60\u4ee5\u4e3a\u6c5f\u6e56\u4e2d\u5df2\u6ca1\u6709\u516c\u9053\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u4ec0\u4e48\u53eb\u6c5f\u6e56\u516c\u9053\uff1f\u96be\u9053\u4e24\u4e2a\u6253\u4e00\u4e2a\u624d\u7b97\u662f\u516c\u9053\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u5389\u58f0\u9053\uff1a\u4f60\u8981\u77e5\u9053\u8fd9\u4e0d\u662f\u6bd4\u6b66\u8f83\u6280\uff0c\u800c\u662f\u66ff\u4f60\u7ba1\u6559\u5974\u624d\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u4e00\u5411\u7528\u4e0d\u7740\u522b\u4eba\u7ba1\u6559\uff0c\u4f46\u8d75\u5927\u7237\u82e5\u662f\u4e5f\u60f3\u548c\u4ed6\u8fc7\u8fc7\u62db\uff0c\u4e0d\u59a8\u5c31\u5c06\u79e6\u4e09\u7237\u6362 \u4e0b\u6765\uff0c\u81ea\u5df1\u4e0a\u53bb\u52a8\u624b\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u6012\uff1a\u4ed6\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f\u914d\u548c\u6211\u52a8\u624b\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u9053\uff1a\u4ed6\u7684\u786e\u4e0d\u662f\u4e1c\u897f\uff0c\u4ed6\u662f\u4eba\u3002 \u3000\u3000\u4ed6\u671b\u7740\u8d75\u6b63\u4e49\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u8d75\u5927\u7237\u4f60\u96be\u9053\u662f\u4e1c\u897f\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u8138\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u9ec4\uff0c\u9f3b\u5b50\u90fd\u4f3c\u5df2\u6c14\u6b6a\u4e86\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u9f99\u5578\u4e91\u4e5f\u4e0d\u80fd\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u7830\u7684\u4e00\u9707\uff0c\u4e24\u62f3\u76f8\u51fb\uff0c\u79e6\u5b5d \u4eea\u7684\u4eba\u5df2\u51e0\uff1f\u4e4e\u88ab\u9707\u5f97\u98de\u4e86\u51fa\u53bb\uff0c\u8e09\u545b\u7740\u8dcc\u5012\u5728\u5730\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u548c\u9f99\u5578\u4e91\u53cc\u53cc\u62a2\u8fc7\u53bb\u6276\u8d77\u4e86\u4ed6\uff0c\u5927\u6c49\u5389\u58f0\u9053\uff1a\u8fd8\u6709\u8c01\u60f3\u6559\u8bad\u6211\u7684\uff0c\u8bf7\u51fa\u624b\u5427\u3002 \u3000\u3000\u6e38\u9f99\u751f\u8d1f\u624b\u51b7\u7b11\u9053\uff1a\u770b\u6765\u4eca\u65e5\u4e3b\u5b50\u975e\u4f46\u6559\u8bad\u4e0d\u4e86\u5974\u624d\uff0c\u5974\u624d\u53cd\u800c\u8981\u6559\u8bad\u4e3b\u5b50\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u79e6\u5b5d\u4eea\u5598\u606f\u7740\u5728\u8d75\u6b63\u4e49\u8033\u7554\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u8d75\u6b63\u4e49\u5ffd\u7136\u957f\u8eab\u800c\u8d77\uff0c\u76ee\u5149\u707c\u707c\uff0c\u77aa\u7740\u90a3 \u5927\u6c49\u9053\uff1a\u60f3\u4e0d\u5230\u670b\u53cb\u4f60\u5c45\u7136\u4e00\u8eab\u6c5f\u6e56\u7f55\u89c1\u7684\u6a2a\u7ec3\u529f\u592b\uff0c\u8fde\u8001\u592b\u90fd\u5c0f\u770b\u4e86\u4f60\uff0c\u96be\u602a\u4e09\u7237\u4e00\u65f6\u4e0d \u5bdf\uff0c\u8981\u88ab\u4f60\u6697\u7b97\u4e86\u3002 \u3000\u3000\u5927\u6c49\u51b7\u7b11\u9053\uff1a\u4f60\u4eec\u82e5\u8d25\u4e86\uff0c\u5c31\u662f\u53d7\u4eba\u6697\u7b97\uff0c\u6211\u82e5\u8d25\u4e86\uff0c\u5c31\u662f\u5b66\u827a\u4e0d\u7cbe\uff0c\u8fd9\u9053\u7406\u6211\u65e9\u5df2\u660e \u767d\u5f97\u5f88\uff0c\u4f60\u4e0d\u8bf4\u4e5f\u7f62\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u6012\u9053\uff1a\u59d3\u94c1\u7684\uff0c\u8001\u592b\u5ff5\u4f60\u662f\u6761\u6c49\u5b50\uff0c\u6709\u5fc3\u4fdd\u5168\u4f60\uff0c\u4f60\u4f11\u60f3\u4e0d\u77e5\u597d\u6b79\u3002 \u3000\u3000\u5927\u6c49\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u6602\u7136\u9053\uff1a\u94c1\u67d0\u6ca1\u6709\u8d75\u5927\u7237\u4fdd\u4f4f\uff0c\u4e5f\u6d3b\u5230\u73b0\u5728\u4e86\uff0c\u6b63\u89c9\u5f97\u5df2\u6d3b\u5f97\u6709\u4e9b\u4e0d \u8010\u70e6\uff0c\u8d75\u5927\u7237\u4f60\u6709\u4ec0\u4e48\u624b\u6bb5\uff0c\u5c3d\u7ba1\u4f7f\u51fa\u6765\u5427\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u77aa\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u4f3c\u5df2\u5192\u51fa\u706b\u6765\uff0c\u51b7\u7b11\uff0c\u5f88\u597d\u2014\u2014\uff0d \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u4e86\u4e94\u516d\u53e5\u5f88\u597d\uff0c\u6276\u8d77\u79e6\u5b5d\u4eea\u5c31\u8d70\u3002 \u3000\u3000\u9f99\u5578\u4e91\u62a2\u5148\u4e00\u6b65\uff0c\u8d54\u7b11\u9053\uff1a\u5404\u4f4d\u6709\u8bdd\u597d\u8bf4\uff0c\u53c8\u4f55\u5fc5\u2014\u2014 \u3000\u3000\u79e6\u5b5d\u4eea\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u60e8\u7b11\u9053\uff1a\u6211\u7236\u5b50\u4e24\u4eba\u4ff1\u5df2\u683d\u5728\u8fd9\u91cc\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff01 \u3000\u3000\u9f99\u5578\u4e91\u540e\u9000\u4e00\u6b65\uff0c\u5782\u4e0b\u4e86\u5934\uff0c\u4e0d\u4f4f\u64e6\u6c57\uff0c\u7b49\u4ed6\u518d\u62ac\u8d77\u5934\u65f6\uff0c\u79e6\u5b5d\u4eea\u548c\u8d75\u6b63\u4e49\u5df2\u8d70\u5f97\u5f88\u8fdc \u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u9053\uff1a\u5927\u54e5\uff0c\u6211\u4e00\u56de\u6765\uff0c\u5c31\u4e3a\u4f60\u60f9\u4e86\u8fd9\u4e48\u591a\u9ebb\u70e6\uff0c\u6211\u2014\u2014\u6211\u65e9\u77e5\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u5144\u5f1f\uff0c\u522b\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u54b1\u4eec\u5f1f\u5144\u513f\u65f6\u6015\u8fc7\u9ebb\u70e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u5144\u5f1f\uff0c\u53ef\u662f\uff0c\u6211\u4e5f\u77e5\u9053\u5927\u54e5\u4f60\u5f88\u4e3a\u96be\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u7528\u4e0d\u7740\u987e\u5fcc\u6211\uff0c\u65e0\u8bba\u4f60\u600e\u4e48\u505a\uff0c\u6211\u603b\u662f\u7ad9\u5728\u4f60\u8fd9\u8fb9\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u80f8\u4e2d\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u70ed\u6cea\u51e0\u4e4e\u5df2\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u9f99\u5578\u4e91\u77a7\u4e86\u90a3\u5927\u6c49\u4e00\u773c\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4e34\u65f6\u5374\u6539\u53e3\u9053\uff1a\u5929\u5df2\u5feb\u4eae\u4e86\uff0c\u6885\u82b1\u76d7\u4eca\u5929\u665a \u4e0a\u60f3\u5fc5\u5df2\u4e0d\u4f1a\u518d\u6765\uff0c\u4f60\u4eec\u65c5\u9014\u52b3\u987f\uff0c\u8fd8\u662f\u65e9\u4e9b\u4e0b\u6765\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6211\u5df2\u53eb\u4eba\u5c06\u542c\u7af9\u8f69\u66ff\u4f60\u6253\u626b\u5e72\u51c0\u4e86\uff0c\u4f46\u4f60\u82e5\u8fd8\u662f\u60f3\u4f4f\u5728\u8001\u5730\u65b9\uff0c\u6211\u53ef\u4ee5\u8bf7\u4ed9 \u513f\u6682\u65f6\u642c\u53bb\u548c\u8bd7\u97f3\u4e00\u5757\u513f\u4f4f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u7528\u4e0d\u7740\uff0c\u542c\u7af9\u8f69\u5c31\u5f88\u597d\u3002 \u3000\u3000\u9f99\u5578\u4e91\u53c8\u77a7\u4e86\u90a3\u5927\u6c49\u4e00\u773c\uff0c\u4f46\u8fd8\u662f\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u9762\u4e0a\u5df2\u4e0d\u7981\u9732\u51fa\u4e86\u5fe7\u90c1\u4e4b\u8272 \uff0c\u663e\u5f97\u5fc3\u4e8b\u91cd\u91cd\u3002 \u3000\u3000\u98ce\u5439\u7740\u7af9\u6797\uff0c\u5b9b\u5982\u6d6a\u6d9b\u3002 \u3000\u3000\u591c\u534a\u542c\u7af9\uff0c\u7eb5\u7136\u5f88\u5feb\u4e50\u7684\u4eba\u4e5f\u4f1a\u89c9\u5f97\u51c4\u51c9\u8427\u7d22\uff0c\u4f55\u51b5\u4e00\u522b\u5341\u4f59\u5e74\uff0c\u8fd4\u6765\u65f6\u5fc3\u4e8b\u5df2\u6210\u7070\u7684 \u674e\u5bfb\u6b22\u5462\uff1f \u3000\u3000\u4e00\u706f\u5982\u8c46\uff0c\u706f\u5149\u4e0b\u770b\u6765\uff0c\u4ed6\u773c\u89d2\u7684\u76b1\u7eb9\u4f3c\u66f4\u6df1\u4e86\u3002 \u3000\u3000\u5927\u6c49\u9eef\u7136\u5371\u5750\uff0c\u6b63\u4e5f\u662f\u5fc3\u4e8b\u5982\u6f6e\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u5ffd\u7136\u54ac\u4e86\u54ac\u7259\uff0c\u50cf\u662f\u4e0b\u4e86\u5f88\u5927\u7684 \u51b3\u5fc3\uff0c\u55c4\u58f0\u9053\uff1a\u5c11\u7237\uff0c\u6211\u6050\u6015\u5df2\u4e0d\u5f97\u4e0d\u8d70\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u5bb9\u9053\uff1a\u4f60\u8981\u8d70\uff1f\u4f60\u4e5f\u8981\u8d70\uff1f \u3000\u3000\u5927\u6c49\u9eef\u7136\u9053\uff1a\u6211\u8eab\u53d7\u5c11\u7237\u4f60\u4eec\u7236\u5b50\u7684\u5927\u6069\uff0c\u672c\u6765\u5df2\u51b3\u5fc3\u4ee5\u8fd9\u52ab\u540e\u7684\u6b8b\u751f\u6765\u62a5\u7b54\u5c11\u7237\u7684\u6069 \u60c5\uff0c\u53ef\u662f\u73b0\u5728\u2014\u2014 \u3000\u3000\u9759\u591c\u4e2d\uff0c\u8fdc\u5904\u5ffd\u7136\u4f20\u6765\u4e00\u58f0\u9a6c\u5636\u3002 \u3000\u3000\u5927\u6c49\u51c4\u7b11\u9053\uff1a\u8d75\u6b63\u4e49\u4ed6\u4eec\u663e\u7136\u5df2\u770b\u51fa\u4e86\u6211\u7684\u6765\u5386\uff0c\u73b0\u5728\u53ea\u63e1\u5df2\u53bb\u901a\u77e5\u6211\u7684\u4ec7\u5bb6\uff0c\u6211\u672c\u5df2 \u672a\u5c06\u751f\u6b7b\u653e\u5728\u5fc3\u4e0a\uff0c\u5012\u4e5f\u4e0d\u6015\u4ed6\u4eec\uff0c\u53ef\u662f\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ef\u662f\u4f60\u5374\u6015\u8fde\u7d2f\u4e86\u6211\uff0c\u662f\u5417\uff1f \u3000\u3000\u5927\u6c49\u53f9\u53f9\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u5c11\u7237\u4f60\u4e0d\u662f\u6015\u88ab\u8fde\u7d2f\u7684\u4eba\uff0c\u53ef\u662f\u5341\u516b\u5e74\u524d\u7684\u90a3\u6bb5\u516c\u6848\uff0c\u5176\u4e2d\u66f2\u6298 \u672c\u662f\u5728\u6211\uff0c\u6211\u600e\u4e48\u80fd\u8ba9\u5c11\u7237\u966a\u7740\u6211\u4e00\u8d77\u53d7\u4eba\u803b\u9a82\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u534a\u664c\uff0c\u957f\u53f9\u9053\uff1a\u90a3\u662f\u4f60\u4e00\u65f6\u7684\u65e0\u5fc3\u4e4b\u5931\uff0c\u8fd9\u5341\u516b\u5e74\u6765\uff0c\u4f60\u53d7\u7684\u82e6\u5df2\u662f\u8db3\u591f\u5f25 \u8865\u4e86\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80fd\u903c\u4eba\u592a\u751a\u3002 \u3000\u3000\u5927\u6c49\u60e8\u7b11\u9053\uff1a\u5c11\u7237\u4f60\u867d\u7136\u8fd9\u4e48\u60f3\uff0c\u4f46\u522b\u4eba\u5374\u4e0d\u4f1a\u8fd9\u4e48\u60f3\uff0c\u6c5f\u6e56\u4e2d\u7684\u8840\u503a\uff0c\u4e00\u5b9a\u8981\u7528\u8840\u624d \u80fd\u6d17\u5f97\u6e05\u7684\uff01\u4ed6\u4e0d\u7b49\u674e\u5bfb\u6b22\u8bf4\u8bdd\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u4f55\u51b5\uff0c\u6211\u8fd8\u8981\u53bb\u770b\u770b\u90a3\u4f4d\u6885\u4e8c\u5148\u751f\uff0c\u4ed6\u8d1f\u4f24\u540e \u4e00\u6012\u800c\u53bb\uff0c\u662f\u5426\u80fd\u8d70\u5f97\u8fdc\uff0c\u8fd8\u8bf4\u4e0d\u5b9a\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u603b\u662f\u51b2\u7740\u6211\u4eec\u624d\u6765\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u624d\u9eef\u7136\u95ee\u9053\uff1a\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f \u3000\u3000\u5927\u6c49\u957f\u53f9\u9053\uff1a\u73b0\u5728\u6211\u4e5f\u4e0d\u77e5\u9053\u8be5\u5230\u54ea\u91cc\u53bb\uff0c\u53ef\u662f\u2014\u2014 \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u53ef\u662f\u6211\u7edd\u4e0d\u4f1a\u8d70\u5f97\u5f88\u8fdc\u7684\uff0c\u6bcf\u5230\u98ce\u6e05\u6708\u767d\u7684\u665a\u4e0a\uff0c\u6211\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u643a\u9152 \u800c\u6765\uff0c\u627e\u5c11\u7237\u4f60\u5171\u8c0b\u4e00\u9189\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u4e00\u8a00\u4e3a\u5b9a\uff1f \u3000\u3000\u5927\u6c49\u9053\uff1a\u4e00\u8a00\u4e3a\u5b9a\uff01 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u90fd\u5df2\u4e0d\u89c9\u70ed\u6cea\u76c8\u7736\uff0c\u4e8e\u662f\u4e24\u90fd\u626d\u8fc7\u4e86\u5934\u2014\u2014\u82f1\u96c4\u4eec\u7684\u522b\u79bb\uff0c\u6709\u65f6\u7adf\u6bd4\u5c0f \u513f\u5973\u7684\u5206\u79bb\u66f4\u4ee4\u4eba\u65ad\u80a0\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7eb5\u6709\u6ee1\u6000\u522b\u7eea\uff0c\u53ea\u662f\u8c01\u4e5f\u4e0d\u613f\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u662f\u6de1\u6de1\u9053\uff1a\u4f60\u8981\u8d70\uff0c\u6211\u4e5f\u4e0d\u62e6\u4f60\uff0c\u4f46\u4f60\u603b\u5f97\u8ba9\u6211\u9001\u4f60\u4e00\u7a0b\u3002 \u3000\u3000\u957f\u8857\u5982\u6d17\uff0c\u79ef\u96ea\u6628\u591c\u5df2\u88ab\u626b\u5728\u9053\u65c1\u3002 \u3000\u3000\u4e00\u5757\u5757\u7c97\u7cd9\u7684\u9752\u77f3\u677f\uff0c\u5728\u71b9\u5fae\u7684\u6668\u5149\u4e2d\u770b\u6765\uff0c\u4eff\u4f5b\u4e00\u5757\u9752\u7389\uff0c\u8fdc\u5904\u5df2\u6709\u5e02\u58f0\u4f20\u6765\uff0c\u5927\u5730 \u5df2\u6e10\u6e10\u82cf\u9192\u3002\u4f46\u5929\u8272\u8fd8\u662f\u6697\u5f97\u5f88\uff0c\u770b\u6765\u4eca\u5929\u8fd8\u662f\u4e0d\u4f1a\u6709\u9633\u5149\u3002 \u3000\u3000\u8fd9\u6761\u8857\u4e5f\u9759\u5f97\u5f88\uff0c\u867d\u6709\u8fdc\u5904\u5076\u5c14\u4f20\u6765\u7684\u9e21\u557c\u548c\u674e\u5bfb\u6b22\u7684\u54b3\u55fd\u58f0\uff0c\u5374\u8fd8\u662f\u6253\u4e0d\u5f00\u8fd9\u4ee4\u4eba\u7a92 \u606f\u7684\u9759\u5bc2\u3002 \u3000\u3000\u5927\u6c49\u5ffd\u7136\u505c\u4e86\u811a\u6b65\uff0c\u52c9\u5f3a\u7b11\u7740\u9053\uff1a\u9001\u541b\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\uff0c\u5c11\u7237\u4f60\u2014\u2014\u4f60\u8fd8\u662f\u56de\u53bb\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u8d70\u4e86\u51e0\u6b65\uff0c\u624d\u7f13\u7f13\u505c\u4e0b\uff0c\u671b\u7740\u957f\u8857\u5c3d\u5934\u4e00\u4f8f\u5b64\u72ec\u7684\u67af\u6811\uff0c\u75f4\u75f4\u5730\u51fa\u4e86\u534a\u5929\u795e\uff0c \u7ec8\u4e8e\u7f13\u7f13\u8f6c\u56de\u8eab\uff0c\u9053\uff1a\u597d\uff0c\u6211\u56de\u53bb\uff0c\u4f60\uff0d\u4f60\u591a\u591a\u4fdd\u91cd\u3002 \u3000\u3000\u5927\u6c49\u70b9\u4e86\u70b9\u5934\uff0c\u55c4\u58f0\u9053\uff1a\u5c11\u7237\u4f60\u81ea\u5df1\u4e5f\u591a\u591a\u4fdd\u91cd\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u518d\u53bb\u671b\u674e\u5bfb\u6b22\uff0c\u4f4e\u5934\u5934\u81ea\u674e\u5bfb\u6b22\u8eab\u65c1\u8d70\u8fc7\u53bb\uff0c\u8d70\u51fa\u4e86\u5341\u51e0\u6b65\uff0c\u5ffd\u53c8\u505c\u4e0b\uff0c\u8f6c\u8eab\u9053\uff1a \u5c11\u7237\u4f60\u82e5\u662f\u6ca1\u6709\u522b\u7684\u4e8b\uff0c\u8fd8\u662f\u5728\u8fd9\u91cc\u591a\u4f4f\u4e9b\u65f6\u5019\u5427\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u9f99\u5927\u7237\u7684\u786e\u662f\u6761\u597d\u6c49\u5b50\uff0c\u597d \u670b\u53cb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ef0\u5929\u53f9\u9053\uff1a\u5f97\u53cb\u80fd\u5982\u9f99\u5578\u4e91\uff0c\u53bb\u590d\u4f55\u6068\uff01 \u3000\u3000\u5927\u6c49\u9053\uff1a\u5c11\u7237\u82e5\u5df2\u51b3\u5b9a\u4f4f\u4e0b\uff0c\u8bf4\u4e0d\u5b9a\u6211\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u627e\u5c11\u7237\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4e5f\u8bb8\u6211\u4f1a\u4f4f\u4e0b\u6765\u7684\uff0c\u53cd\u6b63\u6211\u4e5f\u6ca1\u6709\u522b\u7684\u5730\u65b9\u53ef\u53bb\u3002 \u3000\u3000\u4ed6\u679c\u7136\u5728\u7b11\u7740\uff0c\u4f46\u7b11\u5f97\u5374\u662f\u90a3\u4e48\u3002 \u3000\u3000\u5927\u6c49\u9aa4\u7136\u8f6c\u8eab\uff0c\u54ac\u7d27\u7259\u5173\u5927\u6b65\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5929\u8272\u6e10\u660e\uff0c\u96ea\u610f\u4e5f\u8d8a\u6765\u8d8a\u6d53\u4e86\u3002 \u3000\u3000\u6b7b\u7070\u8272\u7684\u82cd\u7a79\uff0c\u6c89\u91cd\u5f97\u4f3c\u5df2\u5c06\u538b\u4e86\u4e0b\u6765\uff0c\u53ef\u662f\u5927\u6c49\u7684\u5fc3\u60c5\u5374\u6bd4\u8fd9\u5929\u8272\u66f4\u7070\u9eef\u3001\u66f4\u6c89\u91cd\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c\u9003\u7684\uff0c\u603b\u4e4b\u4ed6\u73b0\u5728\u53c8\u8981\u5f00\u59cb\u91cd\u5ea6\u90a3\u65e0\u7a77\u65e0\u5c3d\u7684\u9003\u751f\u751f\u6d3b\u4e86\uff0c\u4ed6\u5df2\u548c \u674e\u5bfb\u6b22\u9003\u4ea1\u4e86\u5341\u5e74\uff0c\u6ca1\u6709\u4eba\u6bd4\u4ed6\u66f4\u6e05\u695a\u9003\u4ea1\u751f\u6d3b\u7684\u75db\u82e6\uff0c\u90a3\u5c31\u50cf\u4e00\u573a\u68a6\uff0c\u5374\u6c38\u8fdc\u6ca1\u6709\u9192\u6765\u7684 \u65f6\u5019\u3002 \u3000\u3000\u4f46\u5728\u90a3\u5341\u5e74\u4e2d\uff0c\u81f3\u5c11\u8fd8\u6709\u674e\u5bfb\u6b22\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u4ed6\u8fd8\u6709\u4e2a\u4eba\u53ef\u4ee5\u7167\u987e\uff0c\u4ed6\u7684\u5fc3\u60c5\u81f3\u5c11\u8fd8\u6709 \u5bc4\u6258\u3002 \u3000\u3000\u800c\u73b0\u5728\uff0c\u4ed6\u5374\u5df2\u5b8c\u5168\u5b64\u72ec\u3002 \u3000\u3000\u4ed6\u82e5\u662f\u4e2a\u61e6\u592b\uff0c\u4e5f\u8bb8\u53cd\u800c\u4e0d\u4f1a\u9003\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u6bd4\u8fd9\u79cd\u5b64\u72ec\u7684\u9003\u4ea1\u751f\u6d3b \u66f4\u75db\u82e6\u3002 \u3000\u3000\u751a\u81f3\u8fde\u6b7b\u4ea1\u90fd\u6ca1\u6709\uff01 \u3000\u3000\u90a3\u79cd\u7edd\u671b\u7684\u5b64\u72ec\uff0c\u5b9e\u5728\u80fd\u903c\u5f97\u4eba\u53d1\u75af\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u975e\u9003\u4e0d\u53ef\uff0c\u773c\u770b\u674e\u6b20\u4f3c\u4e4e\u53c8\u53ef\u4ee5\u5b89\u5b9a\u4e0b\u6765\uff0c\u4ed6\u53ea\u6709\u8d70\uff0c\u4ed6\u65e0\u8bba\u5fcd\u53d7\u4efb\u4f55\u75db\u82e6\u4e5f\u4e0d \u80fd\u8fde\u7d2f\u4e86\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u7406\u5728\uff0c\u4ed6\u672c\u8be5\u9759\u4e0b\u6765\u4ed4\u7ec6\u60f3\u4e00\u60f3\u4eca\u540e\u7684\u53bb\u5411\uff0c\u4ed6\u5374\u4e0d\u6562\u8ba9\u81ea\u5df1\u9759\u4e0b\u6765\uff0c\u4ed6\u8981\u5f80\u4eba\u6700\u591a\u7684 \u5730\u65b9\u8d70\u3002\u4ed6\u832b\u65e0\u76ee\u7684\u5730\u8d70\u7740\uff0c\u4e5f\u4e0d\u77e5\u8d70\u4e86\u591a\u8fdc\uff0c\u5ffd\u7136\u53d1\u73b0\u5df2\u5230\u4e86\u4e00\u4e2a\u83dc\u573a\u91cc\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u7981 \u89c9\u5f97\u6709\u4e9b\u597d\u7b11\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u5230\u8fc7\u591a\u5c11\u79cd\u5730\u65b9\uff0c\u4e0a\u81f3\u4e16\u5bb6\u5927\u65cf\u7684\u79c1\u90b8\uff0c\u4e0b\u81f3\u8d29\u592b\u8d70\u5352\u4f4f\u7684\u5927\u6742\u9662 \uff0c\u4e0a\u81f3\u5343\u91d1\u5c0f\u59d0\u7684\u95fa\u9601\uff0c\u4e0b\u81f3\u82b1\u51e0\u5341\u679a\u5927\u94b1\u5c31\u53ef\u4ee5\u4f4f\u4e00\u591c\u7684\u571f\u5ad6\u9986\u3002\u6700\u51b7\u7684\u5730\u65b9\u4ed6\u5230\u8fc7\uff0d\uff0d \u53ef\u4ee5\u628a\u4eba\u9f3b\u5b50\u90fd\u51bb\u6389\u7684\u9ed1\u9f99\u6c5f\uff1b\u6700\u70ed\u7684\u5730\u65b9\u4ed6\u5230\u8fc7\u2014\u2014\u628a\u9e21\u86cb\u653e\u5728\u5730\u4e0a\u5c31\u53ef\u4ee5\u70e4\u719f\u7684\u5410\u9c81\u756a \u3002 \u3000\u3000\u4ed6\u66fe\u5728\u6cf0\u5c71\u7edd\u9876\u770b\u5bb5\u65e5\u51fa\uff0c\u4e5f\u66fe\u5728\u65e0\u4eba\u7684\u6d77\u6ee9\u4e0a\u770b\u5bb5\u65e5\u51fa\uff0c\u4ed6\u66fe\u7ecf\u88ab\u94b1\u5858\u7684\u98de\u6f6e\u6253\u5f97\u5168 \u8eab\u6e7f\u900f\uff0c\u4e5f\u66fe\u5927\u6f20\u4e0a\u7684\u70c8\u65e5\u6652\u5f97\u5634\u5507\u5e72\u88c2\uff0c\u4ed6\u751a\u81f3\u5728\u8352\u5c71\u4e2d\u548c\u8fdc\u672a\u5f00\u5316\u7684\u571f\u4eba\u4e00\u8d77\u5403\u8fc7\u8840\u6dcb \u7684\u751f\u8089\u3002 \u3000\u3000\u53ef\u662f\u5230\u83dc\u573a\u6765\uff0c\u8fd9\u5012\u8fd8\u662f\u4ed6\u5e73\u751f\u7b2c\u4e00\u6b21\u7ecf\u5386\u3002 \u3000\u3000\u5728\u51ac\u5929\u7684\u65e9\u4e0a\uff0c\u4e16\u4e0a\u53ea\u6015\u518d\u4e5f\u4e0d\u4f1a\u6709\u6bd4\u83dc\u573a\u4eba\u66f4\u591a\u3001\u66f4\u70ed\xd7\u7684\u5730\u65b9\u4e86\uff0c\u65e0\u8bba\u8c01\u8d70\u5230\u8fd9\u91cc \u90fd\u518d\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u5b64\u72ec\u5bc2\u5bde\u3002 \u3000\u3000\u8fd9\u91cc\u6709\u62b1\u7740\u5b69\u5b50\u7684\u5987\u4eba\uff0c\u5e26\u7740\u62d0\u6756\u7684\u8001\u59aa\uff0c\u6ee1\u8eab\u6cb9\u817b\u7684\u53a8\u5b50\uff0c\u6ee1\u5934\u5228\u82b1\u6cb9\u9999\u6c14\u7684\u4fcf\xd7\u5934 \u2014\u2014\uff0d \u3000\u3000\u5404\u5f0f\u5404\u6837\u4e0d\u540c\u7684\u4eba\uff0c\u90fd\u63d0\u7740\u83dc\u7bee\u5728\u4ed6\u8eab\u65c1\u6324\u6765\u6324\u53bb\uff0c\u548c\u5356\u83dc\u7684\u6751\u5987\u3001\u5356\u8089\u7684\u5c60\u592b\u4e3a\u4e86\u4e00 \u6587\u94b1\u4e89\u5f97\u9762\u7ea2\u8033\u8d64\u3002 \u3000\u3000\u7a7a\u6c14\u4e2d\u5145\u6ee1\u4e86\u9c7c\u8089\u7684\u8165\u6c14\u3001\u70b8\u6cb9\u6761\u7684\u6cb9\u70df\u6c14\u3001\u5927\u767d\u83dc\u7684\u6ce5\u571f\u6c14\uff0c\u8fd8\u6709\u9e21\u9e2d\u8eab\u4e0a\u53d1\u51fa\u7684\u90a3 \u79cd\u8bf4\u4e0d\u51fa\u7684\u9a9a\u81ed\u6c14\u3002 \u3000\u3000\u7a81\u542c\u524d\u9762\u4e00\u4eba\u76f4\u7740\u55d3\u5b50\u543c\u9053\uff1a\u4e70\u8089\u4e70\u8089\uff0c\u4e70\u65b0\u9c9c\u7684\u8089\u2014\u2014 \u3000\u3000\u8fd9\u58f0\u97f3\u521a\u54cd\u8d77\u6765\uff0c\u5c31\u88ab\u4e00\u9635\u60ca\u547c\u6253\u65ad\u4e86\u3002 \u3000\u3000\u63a5\u7740\uff0c\u524d\u9762\u7684\u4eba\u90fd\u60ca\u547c\u5411\u540e\u9000\u4e86\u56de\u6765\uff0c\u5927\u4eba\u4eec\u4e00\u4e2a\u4e2a\u8138\u5982\u6b7b\u7070\uff0c\u5b69\u5b50\u4eec\u66f4\u662f\u54ed\u5f97\u4e0a\u6c14\u63a5 \u4e0d\u4e86\u4e0b\u6c14\u3002 \u3000\u3000\u540e\u9762\u7684\u4eba\u7eb7\u7eb7\u5728\u95ee\u9053\uff1a\u4ec0\u4e48\u4e8b\uff1f\u4ec0\u4e48\u4e8b\u8fd9\u6837\u5927\u60ca\u5c0f\u602a\u7684\uff1f \u3000\u3000\u4ece\u524d\u9762\u9003\u56de\u6765\u7684\u4eba\u5598\u606f\u7740\u9053\uff1a\u6709\u4eba\u5728\u5356\u8089\u3002 \u3000\u3000\u540e\u9762\u7684\u4eba\u7b11\u4e86\u9053\uff1a\u8fd9\u91cc\u81f3\u5c11\u6709\u51e0\u5341\u4e2a\u4eba\u5728\u5356\u8089\uff0c\u6709\u4ec0\u4e48\u5bb3\u6015\u7684\uff1f \u3000\u3000\u524d\u9762\u7684\u4eba\u5598\u606f\u7740\u6c14\u9053\uff1a\u4f46\u8fd9\u4eba\u5356\u7684\u8089\u5374\u4e0d\u540c\uff0c\u4ed6\u5356\u7684\u662f\u4eba\u8089\u3002 \u3000\u3000\u6709\u8fd9\u79cd\u602a\u4e8b\u53d1\u751f\uff0c\u8c01\u8fd8\u80af\u8d70\u5462\uff1f \u3000\u3000\u5927\u6c49\u76b1\u4e86\u76b1\u7709\uff0c\u5206\u5f00\u4eba\u7fa4\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4e5f\u7acb\u523b\u53d8\u4e86\u989c\u8272\uff0c\u770b\u6765\u7adf\u4f3c\u6bd4\u4efb\u4f55\u4eba\u90fd\u5403\u60ca\u3002 \u3000\u3000\u6700\u5927\u7684\u4e00\u5bb6\u8089\u6848\u65c1\u7cfb\u7740\u62db\u724c\uff0c\u4e0a\u9762\u5199\u7740\uff1a\u9ec4\u725b\u767d\u7f8a\uff0c\u73b0\u6740\u73b0\u5356\u3002 \u3000\u3000\u8089\u6848\u540e\u9762\u7ad9\u7740\u4e2a\u53c8\u9ad8\u53c8\u5927\u53c8\u80d6\u7684\u72ec\u773c\u5987\u4eba\uff0c\u624b\u91cc\u62ff\u7740\u67c4\u8f66\u8f6e\u822c\u5927\u5c0f\u7684\u5241\u9aa8\u5200\uff0c\u6ee1\u8138\u90fd\u662f \u6a2a\u8089\uff0c\u4e00\u6761\u5200\u75a4\u81ea\u6234\u7740\u9ed1\u773c\u7f69\u7684\u53f3\u773c\u89d2\u76f4\u5212\u5230\u5634\u89d2\uff0c\u4e0d\u7b11\u65f6\u770b\u6765\u4e5f\u4eff\u4f5b\u5e26\u7740\u4e09\u5206\u8be1\u79d8\u7684\u72de\u7b11 \uff0c\u770b\u6765\u6d3b\u50cf\u662f\u51f6\u795e\u4e0b\u51e1\uff0c\u54ea\u91cc\u50cf\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8089\u6848\u4e0a\u6446\u7740\u7684\u662f\u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\uff01 \u3000\u3000\u8fd9\u4eba\u8eab\u4e0a\u7684\u8863\u670d\u5df2\u88ab\u5265\u5149\uff0c\u9732\u51fa\u4e86\u4e00\u8eab\u82cd\u767d\u5f97\u53ef\u601c\u7684\u76ae\u80a4\uff0c\u4e00\u6761\u6761\u808b\u9aa8\uff0c\u4e0d\u505c\u5730\u53d1\u7740\u6296 \uff0c\u7528\u4e24\u6761\u67af\u7626\u7684\u624b\u81c2\u62b1\u7740\u5934\uff0c\u7f29\u7740\u5934\u4f0f\u5728\u8089\u6848\u4e0a\uff0c\u9664\u4e86\u76ae\u5305\u7740\u9aa8\u5934\u4e4b\u5916\uff0c\u7b80\u76f4\u8fde\u4e00\u4e24\u8089\u90fd\u6ca1 \u6709\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u5de6\u624b\u627c\u4f4f\u4e86\u4ed6\u7684\u8116\u5b50\uff0c\u53f3\u624b\u9ad8\u4e3e\u7740\u5241\u9aa8\u5200\uff0c\u72ec\u773c\u91cc\u51f6\u5149\u95ea\u95ea\uff0c\u5145\u6ee1\u4e86\u6028\u6bd2\u4e4b\u610f \uff0c\u4e5f\u5145\u6ee1\u4e86\u6740\u673a\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u77aa\u4e86\u5927\u6c49\u51e0\u773c\uff0c\u624d\u72de\u7b11\u7740\u9053\uff1a\u5927\u7237\u53ef\u662f\u6765\u4e70\u8089\u7684\u4e48\uff1f \u3000\u3000\u5927\u6c49\u4f3c\u5df2\u5446\u4f4f\u4e86\uff0c\u5168\u672a\u542c\u5230\u5979\u5728\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u683c\u683c\u7b11\u9053\uff1a\u8d27\u5356\u8bc6\u5bb6\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u8fd9\u5757\u80a5\u7f8a\u8089\u9664\u4e86\u5927\u7237\u4f60\u4e4b\u5916\uff0c\u522b\u4eba\u7edd\u4e0d\u4f1a\u4e70 \uff0c\u6240\u4ee5\u6211\u65e9\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\u5927\u7237\u4f60\u6765\u4e86\u3002 \u3000\u3000\u5927\u6c49\u8fd9\u624d\u957f\u957f\u53f9\u51fa\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u591a\u5e74\u4e0d\u89c1\uff0c\u5927\u5ac2\u4f60\u4f55\u82e6\u2014\u2014 \u3000\u3000\u72ec\u773c\u5987\u4eba\u5ffd\u7136\u5478\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u75f0\u5f39\u4e38\u4f3c\u5730\u98de\u51fa\u52a8\uff0c\u4e0d\u504f\u4e0d\u501a\uff0c\u6b63\u5410\u5728\u5927\u6c49\u7684\u8138\u4e0a\u3002 \u3000\u3000\u90a3\u5987\u4eba\u5df2\u6012\u543c\u7740\u9053\uff1a\u5927\u5ac2\uff1f\u8c01\u662f\u4f60\u8fd9\u5356\u53cb\u6c42\u8363\u7684\u755c\u751f\u7684\u5927\u5ac2\uff01\u4f60\u82e5\u518d\u53eb\u94b1\u58f0\u5927\uff0c\u6211\u5c31\u5148 \u628a\u4f60\u820c\u5934\u5272\u4e0b\u6765\u3002 \u3000\u3000\u5927\u6c49\u8138\u4e0a\u9635\u9752\u9635\u767d\uff0c\u7adf\u4e0d\u6562\u8fd8\u5634\u3002 \u3000\u3000\u5987\u4eba\u51b7\u7b11\u9053\uff1a\u4f60\u51fa\u5356\u4e86\u7fc1\u5929\u6770\uff0c\u8fd9\u4e9b\u5e74\u6765\u60f3\u5fc5\u5df2\u5927\u5bcc\u5927\u8d35\uff0c\u53d1\u4e86\u5927\u8d22\u7684\u4eba\uff0c\u96be\u9053\u8fde\u51e0\u65a4 \u8089\u90fd\u820d\u4e0d\u5f97\u4e70\u5417\uff1f \u3000\u3000\u5979\u5ffd\u7136\u4e00\u628a\u63ea\u8d77\u4e86\u8089\u6848\u4e0a\u90a3\u4eba\u7684\u5934\u53d1\uff0c\u72de\u7b11\u9053\uff1a\u4f60\u82e5\u4e0d\u4e70\uff0c\u6211\u53ea\u597d\u5c06\u4ed6\u5241\u4e86\u5582\u72d7\uff01 \u3000\u3000\u5927\u6c49\u62ac\u5934\u4e00\u77a7\uff0c\u5931\u58f0\u9053\uff1a\u6885\u4e8c\u5148\u751f\uff0c\u662f\u4f60\uff1f \u3000\u3000\u8089\u6848\u4e0a\u90a3\u4eba\u4f3c\u5df2\u9a87\u5f97\u5b8c\u5168\u9ebb\u6728\uff0c\u53ea\u662f\u76f4\u7740\u773c\u53d1\u5446\uff0c\u53e3\u6c34\u4e0d\u505c\u5728\u6cbf\u7740\u5634\u89d2\u5f80\u4e0b\u6d41\uff0c\u54ea\u91cc\u8fd8 \u8bf4\u5f97\u51fa\u8bdd\u6765\u3002\u5927\u6c49\u55c4\u58f0\u9053\uff1a\u6211\u8981\u4e70\u4ed6\u6574\u4e2a\u4eba \u3000\u3000\u5987\u4eba\u5389\u58f0\u9053\uff1a\u4f60\u82e5\u8981\u4e70\u4ed6\u6574\u4e2a\u4eba\uff0c\u4f60\u5c31\u5f97\u8ddf\u7740\u6211\u8d70\uff01 \u3000\u3000\u5927\u6c49\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u597d\uff0c\u6211\u8ddf\u4f60\u8d70\uff01 \u3000\u3000\u5987\u4eba\u53c8\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u72de\u7b11\u9053\uff1a\u4f60\u4e58\u4e58\u5730\u8ddf\u7740\u6211\u8d70\uff0c\u5c31\u7b97\u4f60\u806a\u660e\uff0c\u6211\u627e\u4e86\u4f60\u5341\u4e03\u5e74\u516b\u6708\u624d \u5c06\u4f60\u627e\u5230\uff0c\u96be\u9053\u8fd8\u4f1a\u518d\u8ba9\u4f60\u8dd1\u4e86\u4e48\uff1f\u5927\u6c49\u4ef0\u5929\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u6211\u65e2\u5df2\u88ab\u4f60\u627e\u5230\uff0c\u4e5f\u5c31\u4e0d\u518d \u77b3\u4e86\uff01 \u3000\u3000\u5c71\u9e93\u4e0b\u7684\u575f\u5806\u65c1\uff0c\u6709\u95f4\u5c0f\u5c0f\u7684\u6728\u5c4b\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u5bb6\u770b\u575f\u4eba\u7684\u4f4f\u5904\uff0c\u5728\u8fd9\u82e6\u5bd2\u4e25\u51ac\u4e2d\uff0c\u8fde \u8352\u575f\u4e2d\u7684\u5b64\u9b3c\u53ea\u6015\u90fd\u5df2\u88ab\u51b7\u5f97\u85cf\u5728\u68fa\u6750\u91cc\u4e0d\u6562\u51fa\u6765\uff0c\u770b\u575f\u7684\u4eba\u81ea\u7136\u66f4\u4e0d\u4e71\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u5374\u6709\u4e2a\u4eba\u5df2\u5728\u8fd9\u5c4b\u91cc\u9017\u7559\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u8fd9\u4eba\u5c31\u76d8\u5750\u5728\u5730\u4e0b\uff0c\u75f4\u75f4\u5730\u671b\u7740\u8fd9\u575b\u5b50\u5728\u51fa\u795e\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u60b2\u6124\u6028\u6068\u4e4b\u8272\uff0c\u75f4\u75f4\u5730\u4e5f\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\uff0c\u5730\u4e0a\u65e9\u5df2\u7ed3\u4e86\u51b0\uff0c\u4ed6\u4f3c\u4e5f \u5168\u4e0d\u89c9\u5f97\u51b7\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u6728\u5c4b\u5916\u5ffd\u7136\u4f20\u6765\u4e00\u9635\u6c99\u6c99\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u8fd9\u4eba\u7acb\u523b\u63e1\u4f4f\u4e86\u65a7\u67c4\uff0c\u6c89\u58f0\u9053\uff1a\u8c01\uff1f \u3000\u3000\u6728\u5c4b\u5916\u4f20\u5165\u4e86\u90a3\u72ec\u773c\u5987\u4eba\u6c99\u54d1\u800c\u51cc\u5389\u7684\u8bed\u58f0\uff1a\u662f\u6211\uff01 \u3000\u3000\u8fd9\u4eba\u795e\u60c5\u7acb\u523b\u7d27\u5f20\u8d77\u6765\uff0c\u55c4\u58f0\u9053\uff1a\u4eba\u662f\u4e0d\u662f\u5728\u57ce\u91cc\uff1f \u3000\u3000\u72ec\u773c\u5987\u4eba\u9053\uff1a\u8001\u4e4c\u9f9f\u7684\u6d88\u606f\u7684\u786e\u53ef\u884c\uff0c\u6211\u5df2\u7ecf\u5c06\u4eba\u5e26\u56de\u6765\u4e86\uff01 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u4eba\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u5657\u5730\u8dea\u4e86\u4e0b\u53bb\uff0c\u76ee\u4e2d\u65e9\u5df2\u70ed\u6cea\u76c8\u7736\uff0c\u4e45\u4e45\u65e0\u6cd5\u7ad9\u8d77\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u95e8\u5916\u53c8\u6709\u4e00\u9635\u811a\u6b65\u58f0\u4f20\u6765\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u6c89\u58f0\u9053\uff1a\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u95e8\u5916\u4e00\u4e2a\u7834\u9523\u822c\u7684\u58f0\u97f3\u9053\uff1a\u662f\u8001\u4e03\u548c\u6211\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u6ee1\u8138\u9ebb\u5b50\u7684\u5927\u6c49\uff0c\u80a9\u4e0a\u62c5\u7740\u5927\u62c5\u7684\u83dc\uff0c\u53e6\u4e00\u4e2a\u957f\u5f97\u7626\u7626\u5c0f\u5c0f\uff0c\u5374\u662f\u4e2a\u5356\u81ed \u8c46\u5e72\u7684\u3002 \u3000\u3000\u6b64\u523b\u4e24\u4eba\u72e0\u72e0\u77aa\u4e86\u5927\u6c49\u4e00\u773c\uff0c\u5356\u767d\u83dc\u7684\u9ebb\u5b50\u4e00\u628a\u63ea\u4f4f\u4ed6\uff0c\u5389\u58f0\u9053\uff1a\u59d3\u94c1\u7684\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48 \u8bdd\u8bf4\uff1f \u3000\u3000\u72ec\u773c\u5987\u4eba\u6c89\u58f0\u9053\uff1a\u653e\u5f00\u4ed6\uff0c\u6709\u4ec0\u4e48\u8bdd\u7b49\u4eba\u6765\u9f50\u4e4b\u540e\u518d\u8bf4\u4e5f\u4e0d\u8fdf\u3002 \u3000\u3000\u9ebb\u5b50\u54ac\u4e86\u54ac\u7259\uff0c\u7ec8\u4e8e\u653e\u5f00\u624b\uff0c\u5411\u684c\u4e0a\u90a3\u9ed1\u575b\u5b50\u53e9\u4e86\u4e09\u4e2a\u5934\uff0c\u76ee\u4e2d\u5df2\u4e0d\u7981\u6cea\u843d\u5982\u96e8\u3002 \u3000\u3000\u534a\u65f6\u8fb0\u4e4b\u5185\uff0c\u53c8\u9646\u7eed\u6765\u4e86\u4e09\u4e2a\u4eba\uff0c\u4e00\u4e2a\u80a9\u80cc\u836f\u7bb1\uff0c\u624b\u63d0\u864e\u638c\uff0c\u662f\u4e2a\u8d70\u6c5f\u6e56\u5356\u91ce\u836f\u7684\u90ce\u4e2d \u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u6ee1\u8eab\u6cb9\u817b\uff0c\u6311\u7740\u526f\u62c5\u5b50\uff0c\u524d\u9762\u662f\u4e2a\u9152\u575b\uff0c\u540e\u9762\u7684\u5c0f\u7eb1\u6a71\u91cc\u88c5\u7740\u51e0\u53ea\u7c97\u7897\u3001\u51e0\u5341\u53ea \u9e2d\u722a\u9e2d\u7fc5\u8180\u3002\u8fd8\u6709\u4e00\u4e2a\u5374\u662f\u4e2a\u6d4b\u5b57\u5356\u535c\u7684\u778e\u5b50\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u89c1\u5230\u90a3\u5927\u6c49\uff0c\u4ea6\u662f\u6ee1\u9762\u6012\u5bb9\u3002\u5916\u9762\u96ea\u5149\u53cd\u6620\uff0c\u5929\u8272\u8fd8\u5f88\u4eae\uff0c\u5c4b\u5b50\u91cc\u5374\u662f\u9ed1\u9edd\u9edd\u7684 \uff0c\u5145\u6ee1\u4e86\u4e00\u79cd\u9634\u68ee\u51c4\u60e8\u4e4b\u610f\uff0c\u8fd9\u4e03\u4eba\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u4e00\u4e2a\u4e2a\u90fd\u94c1\u9752\u7740\u8138\uff0c\u7d27\u54ac\u7740\u7259\uff0c\u770b\u6765\u5c31 \u50cf\u662f\u7fa4\u9b3c\uff0c\u4ece\u5730\u72f1\u9003\u51fa\u6765\u590d\u4ec7\u7684\u3002 \u3000\u3000\u5927\u6c49\u4ea6\u662f\u6ee1\u9762\u60b2\u60e8\u4e4b\u8272\uff0c\u5782\u9996\u65e0\u8bdd\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u5ffd\u7136\u9053\uff1a\u8001\u4e94\uff0c\u4f60\u53ef\u77e5\u9053\u8001\u4e09\u80fd\u4e0d\u80fd\u8d76\u5f97\u5230\uff1f \u3000\u3000\u90a3\u5356\u9152\u7684\u80d6\u5b50\u9053\uff1a\u4e00\u5b9a\u80fd\u8d76\u5f97\u5230\uff0c\u6211\u5df2\u7ecf\u63a5\u5230\u4ed6\u7684\u8baf\u4e86\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u76b1\u7709\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u4ed6\u4e3a\u4f55\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6765\uff1f \u3000\u3000\u90a3\u5356\u535c\u7684\u778e\u5b50\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u4eec\u5df2\u7b49\u4e86\u5341\u4e03\u5e74\uff0c\u5c82\u5728\u4e4e\u518d\u591a\u7b49\u8fd9\u4e00\u65f6\u534a\u523b \u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u4e5f\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u5341\u4e03\u5e74\uff0c\u5341\u4e03\u5e74\u2014\u2014\uff0d \u3000\u3000\u5979\u8fd9\u8fde\u8bf4\u4e86\u4e03\u516b\u904d\uff0c\u8d8a\u8bf4\u58f0\u97f3\u8d8a\u60b2\u60e8\u3002 \u3000\u3000\u8fd9\u5341\u4e03\u5e74\u65e5\u5b50\u663e\u7136\u4e0d\u662f\u597d\u8fc7\u7684\uff0c\u90a3\u5176\u4e2d\u4e5f\u4e0d\u77e5\u5305\u542b\u4e86\u591a\u5c11\u8f9b\u9178\u3001\u591a\u5c11\u8840\u6cea\uff01\u4e03\u4e2a\u4eba\u7684\u773c \u775b\u4e00\u9f50\u77aa\u4f4f\u5927\u6c49\uff0c\u76ee\u4e2d\u5df2\u5c06\u55b7\u51fa\u706b\u6765\u3002 \u3000\u3000\u90a3\u5356\u535c\u7684\u778e\u5b50\u53c8\u9053\uff1a\u8fd9\u5341\u4e03\u5e74\u6765\uff0c\u6211\u65f6\u65f6\u523b\u523b\u90fd\u5728\u60f3\u91cd\u89c1\u94c1\u67d0\u4eba\u4e00\u9762\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u2014\u2014 \u3000\u3000\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u808c\u8089\u4e00\u9635\u62bd\u7f29\uff0c\u55c4\u58f0\u9053\uff1a\u4ed6\u73b0\u5728\u5df2\u53d8\u6210\u4ec0\u4e48\u6a21\u6837\uff1f\u8001\u56db\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\u542c \u597d\u5417\uff1f \u3000\u3000\u5356\u91ce\u836f\u7684\u90ce\u4e2d\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u770b\u8d77\u6765\u4ed6\u8fd8\u662f\u8ddf\u5341\u4e03\u5e74\u524d\u5dee\u4e0d\u591a\uff0c\u53ea\u4e0d\u8fc7\u80e1\u5b50\u957f\u4e86\u4e9b\uff0c\u4eba \u4e5f\u80d6\u4e86\u4e9b\uff1f \u3000\u3000\u778e\u5b50\u4ef0\u9762\u4e00\u9635\u60e8\u7b11\uff0c\u9053\uff1a\u597d\uff0c\u597d\u2014\u2014\u59d3\u94c1\u7684\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u8fd9\u5341\u4e03\u5e74\u6765\uff0c\u65e5\u591c\u90fd\u5728\u6c42\u8001\u5929 \u4fdd\u4f51\u4f60\u8eab\u5b50\u5eb7\u5065\uff0c\u65e0\u75c5\u65e0\u75db\uff0c\u770b\u6765\u8001\u5929\u679c\u7136\u6ca1\u6709\u53eb\u6211\u5931\u671b\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u54ac\u7259\u9053\uff1a\u4ed6\u51fa\u5356\u4e86\u7fc1\u5929\u6770\uff0c\u81ea\u7136\u65e9\u5df2\u5927\u5bcc\u5927\u8d35\uff0c\u600e\u4f1a\u50cf\u6211\u4eec\u8fd9\u6837\u8fc7\u7684\u662f\u8fde\u732a\u72d7 \u90fd\u4e0d\u5982\u7684\u65e5\u5b50\u2014\u2014\uff0d \u3000\u3000\u5979\u6307\u7740\u90a3\u5356\u9152\u7684\u9053\uff1a\u5b89\u4e50\u516c\u5b50\u5f20\u8001\u4e94\u7adf\u4f1a\u6311\u7740\u62c5\u5b50\u5728\u8857\u4e0a\u5356\u9152\uff0c\u6613\u4e8c\u54e5\u5df2\u53d8\u6210\u778e\u5b50- \u8fd9\u4e9b\u4e8b\uff0c\u4f60\u53ea\u6015\u90fd\u6ca1\u6709\u60f3\u5230\u5427\uff1f \u3000\u3000\u5927\u6c49\u7d27\u7d27\u95ed\u7740\u773c\u775b\uff0c\u4e0d\u6562\u5f20\u5f00\uff0c\u4ed6\u53ea\u6015\u4e00\u5f20\u5f00\u773c\u775b\uff0c\u70ed\u6cea\u5c31\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u5341\u4e03\u5e74\u2014\u2014\uff0d\u5341\u4e03\u5e74\u2014\u2014\uff0d \u3000\u3000\u8fd9\u5341\u4e03\u5e74\u4ed6\u6240\u5fcd\u53d7\u7684\u82e6\u96be\uff0c\u53c8\u6709\u8c01\u77e5\u9053\uff1f \u3000\u3000\u7a81\u7136\u5c4b\u5b50\u5916\u4e00\u4eba\u5927\u547c\u9053\uff1a\u5927\u5ac2-\u5927\u5ac2\u2014\u2014\u6211\u6709\u597d\u6d88\u606f\u2014\u2014\uff0d \u7b2c\u5341\u4e00\u7ae0\u3000\u5929\u5916\u6765\u6551\u661f>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u5929\u5916\u6765\u6551\u661f \u3000\u3000\u72ec\u773c\u5987\u4eba\u542c\u6709\u4eba\u5728\u5c4b\u5b50\u5916\u9762\u547c\u53eb\uff0c\u62a2\u4e86\u51fa\u53bb\uff0c\u76b1\u7709\u9053\uff1a\u4ec0\u4e48\u4e8b\u5982\u6b64\u5927\u60ca\u5c0f\u602a\u7684\uff1f \u3000\u3000\u90a3\u4eba\u9053\uff1a\u6211\u65b9\u624d\u89c1\u5230\u94c1\u9762\u65e0\u79c1\u8d75\u6b63\u4e49\uff0c\u4ed6\u8bf4\u90a3\u59d3\u94c1\u7684\u5c31\u5728\u2014\u2014\uff0d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u63a8\u95e8\u8d70\u4e86\u8fdb\u6765\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u6014\u4f4f\uff0c\u56e0\u4e3a\u4ed6\u5df2\u53d1\u73b0\u8981\u627e\u7684\u4eba\u5c31 \u5728\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u683c\u683c\u7b11\u9053\uff1a\u4f60\u60f3\u4e0d\u5230\u5427\uff01 \u3000\u3000\u90a3\u4eba\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u8d75\u6b63\u4e49\u8bf4\u4ed6\u5728\u9f99\u5578\u4e91\u5bb6\u91cc\uff0c\u60f3\u4e0d\u5230\u2014\u2014\uff0d \u3000\u3000\u4ed6\u4e00\u628a\u6293\u4f4f\u72ec\u773c\u5987\u4eba\u7684\u624b\uff0c\u9053\uff1a\u5927\u5ac2\uff0c\u4f60\u4eec\u662f\u600e\u4f1a\u627e\u5230\u4ed6\u7684\uff1f \u3000\u3000\u72ec\u773c\u5987\u4eba\u9053\uff1a\u8fd9\u662f\u9f99\u795e\u5e99\u8001\u4e4c\u9f9f\u6765\u62a5\u7684\u8baf\uff0c\u8bf4\u4ed6\u5df2\u548c\u674e\u5bfb\u6b22\u5f80\u8fd9\u6761\u8def\u4e0a\u8d70\u6765\u4e86\uff0c\u6211\u4eec\u4e00 \u8def\u5bfb\u5230\u8fd9\u91cc\uff0c\u672c\u8fd8\u788d\u7740\u674e\u5bfb\u6b22\uff0c\u4e0d\u4fbf\u5984\u52a8\uff0c\u8c01\u77e5\u4ed6\u7adf\u548c\u674e\u5bfb\u6b22\u5206\u4e86\u624b\u3002 \u3000\u3000\u778e\u5b50\u9634\u607b\u607b\u7b11\u9053\uff1a\u8fd9\u5c31\u53eb\u5929\u593a\u5176\u9b42\uff0c\u9b3c\u8499\u4e86\u4ed6\u7684\u773c\u775b\uff01 \u3000\u3000\u6700\u540e\u8d76\u5230\u7684\u90a3\u4eba\u75be\u88c5\u52b2\u670d\uff0c\u516b\u4e2a\u4eba\u4e2d\u53ea\u6709\u4ed6\u4e0d\u6539\u6c5f\u6e56\u8c6a\u5ba2\u7684\u6253\u626e\uff0c\u8eab\u540e\u659c\u80cc\u4e00\u67c4\u68a8\u82b1\u5927 \u67aa\uff0c\u6bd4\u4ed6\u7684\u4eba\u8fd8\u9ad8\u51fa\u534a\u622a\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u90a3\u6c5f\u6e56\u5ba2\u4e00\u8dc3\u800c\u8d77\uff0c\u77aa\u7740\u5927\u6c49\u5927\u558a\u9053\uff1a\u94c1\u4f20\u7532\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f \u3000\u3000\u94c1\u4f20\u7532\u70b9\u4e86\u70b9\u5934\uff0c\u9eef\u7136\u9053\uff1a\u4f60\u597d\u2014\u2014 \u3000\u3000\u90a3\u6c5f\u6e56\u5ba2\u5e94\u58f0\u9053\uff1a\u6211\u5f53\u7136\u5f88\u597d\uff0c\u8fb9\u6d69\u5e73\u751f\u4e0d\u505a\u4e8f\u5fc3\u4e8b\uff0c\u4e5f\u7528\u4e0d\u7740\u8eb2\u8eb2\u85cf\u85cf\u7684\u4e0d\u6562\u89c1\u4eba\uff0c \u65e5\u5b50\u81f3\u5c11\u603b\u6bd4\u4f60\u8fc7\u5f97\u5f00\u5fc3\u4e9b\uff01 \u3000\u3000\u9ebb\u5b50\u6012\u9053\uff1a\u4e09\u54e5\uff0c\u4f60\u8fd8\u8ddf\u4ed6\xd7\u55e6\u4ec0\u4e48\uff1f\u5feb\u5f00\u4e86\u4ed6\u7684\u80f8\u819b\uff0c\u638f\u51fa\u4ed6\u7684\u5fc3\u6765\u796d\u5927\u54e5\u5728\u5929\u4e4b\u7075 \uff0c\u4e0d\u5c31\u5b8c\u4e86\u4e48\uff1f \u3000\u3000\u8fb9\u6d69\u6c89\u7740\u8138\u9053\uff1a\u8001\u4e03\uff0c\u4f60\u8fd9\u8bdd\u5c31\u4e0d\u5bf9\u4e86\uff0c\u6211\u4eec\u5144\u5f1f\u8981\u6740\u4eba\uff0c\u603b\u8981\u6740\u5f97\u5149\u660e\u6b63\u5927\uff0c\u4e0d\u4f46\u8981 \u53eb\u5929\u4e0b\u4eba\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4e5f\u8981\u53eb\u5bf9\u65b9\u53e3\u670d\u5fc3\u670d\u3002 \u3000\u3000\u778e\u5b50\u60a0\u7136\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u4eec\u65e2\u5df2\u7b49\u4e86\u5341\u4e03\u5e74\uff0c\u53c8\u5c82\u5728\u4e4e\u591a\u7b49\u4e00\u65f6\u534a\u523b\u3002 \u3000\u3000\u4ed6\u5c06\u8fd9\u53e5\u8bdd\u53c8\u8bf4\u4e86\u4e00\u904d\uff0c\u522b\u4eba\u4e5f\u5c31\u4e0d\u80fd\u518d\u8bf4\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u9053\uff1a\u90a3\u4e48\u8001\u4e09\uff0c\u4f60\u7684\u610f\u601d\u8fd8\u60f3\u600e\u4e48\u6837\u5462\uff1f \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u6211\u4eec\u4e0d\u4f46\u8981\u5148\u5c06\u8bdd\u95ee\u6e05\u695a\uff0c\u8fd8\u8981\u627e\u4e2a\u5916\u4eba\u6765\u4e3b\u6301\u516c\uff0c\u82e5\u662f\u4eba\u4eba\u90fd\u8bf4\u94c1\u67d0\u4eba\u8be5\u6740 \uff0c\u90a3\u65f6\u518d\u6740\u4ed6\u4e5f\u4e0d\u8fdf\u3002 \u3000\u3000\u9ebb\u5b50\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u543c\u9053\uff1a\u8fd8\u8981\u95ee\u4e2a\u9e1f\uff0c\u6211\u5c31\u4e0d\u4fe1\u8fd8\u6709\u4eba\u4f1a\u8bf4\u4ed6\u505a\u7684\u4e8b\u4e0d\u8be5\u6740\uff01 \u3000\u3000\u778e\u5b50\u51b7\u51b7\u9053\uff1a\u65e2\u7136\u6ca1\u6709\u4eba\u4f1a\u8bf4\u4ed6\u4e0d\u8be5\u6740\uff0c\u95ee\u95ee\u53c8\u6709\u4f55\u59a8\uff1f \u3000\u3000\u9ebb\u5b50\u54ac\u4e86\u54ac\u7259\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u2014\u2014\u4f60\u60f3\u627e\u8c01\u6765\u4e3b\u6301\u516c\u9053\uff1f \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u6211\u4eec\u627e\u7684\u4eba\u975e\u4f46\u8981\u7edd\u5bf9\u5927\u516c\u65e0\u79c1\uff0c\u800c\u4e14\u8fd8\u8981\u548c\u4e2d\u539f\u516b\u4e49\u53ca\u94c1\u4f20\u7532\u53cc\u65b9\u90fd\u5168\u65e0\u5173 \u7cfb\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u76b1\u7709\u9053\uff1a\u4f60\u627e\u7684\u7a76\u7adf\u662f\u8c01\uff0c\u5feb\u8bf4\u5427\u3002 \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u7b2c\u4e00\u4f4d\u5c31\u662f\u94c1\u9762\u65e0\u79c1\u8d75\u6b63\u4e49\uff0c\u6b64\u4eba\u53ef\u7b97\u662f\u2014\u2014\uff0d \u3000\u3000\u94c1\u4f20\u7532\u5ffd\u7136\u60e8\u7b11\u9053\uff1a\u4f60\u4eec\u7528\u4e0d\u9ebb\u70e6\u4e86\uff0c\u5feb\u6740\u4e86\u6211\u5c31\u662f\uff01\u6211\u81ea\u95ee\u6614\u5e74\u786e\u6709\u5bf9\u4e0d\u8d77\u7fc1\u5929\u6770\u4e4b \u5904\uff0c\u5982\u4eca\u6b7b\u800c\u65e0\u6028\uff01 \u3000\u3000\u72ec\u773c\u5987\u4eba\u51b7\u7b11\u9053\uff1a\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u5bf9\u8d75\u6b63\u4e49\u8fd8\u6709\u6240\u4e0d\u6ee1\u2014\u2014 \u3000\u3000\u778e\u5b50\u6de1\u6de1\u9053\uff1a\u8d75\u6b63\u4e49\u65e2\u7136\u66fe\u627e\u8fc7\u8001\u4e09\u62a5\u544a\u4ed6\u7684\u884c\u8e2a\uff0c\u81ea\u7136\u548c\u4ed6\u6709\u4e9b\u8fc7\u8282\uff0c\u53c8\u600e\u4f1a\u4e3a\u4ed6\u4e3b \u6301\u516c\u9053\uff1f \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u7eb5\u7136\u5982\u6b64\u4e5f\u65e0\u59a8\uff0c\u9664\u4e86\u8d75\u6b63\u4e49\u4e4b\u5916\uff0c\u6211\u8fd8\u627e\u4e86\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u778e\u5b50\u9053\uff1a\u54e6\uff1f \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u5728\u5927\u89c2\u697c\u8bf4\u94c1\u677f\u5feb\u4e66\u7684\u8001\u5148\u751f\uff0c\u53ef\u8bf4\u662f\u6b64\u9053\u7b2c\u4e00\u540d\u5bb6\uff0c\u5374\u548c\u6c5f\u6e56 \u4e2d\u4eba\u5168\u65e0\u5173\u7cfb\uff0c\u53e6\u4e00\u4e2a\u662f\u521d\u51fa\u6c5f\u6e56\u7684\u5c11\u5e74\u2014\u2014 \u3000\u3000\u72ec\u773c\u5987\u4eba\u9053\uff1a\u521d\u51fa\u6c5f\u6e56\u7684\u6bdb\u5934\u5c0f\u4f19\u5b50\uff0c\u61c2\u5f97\u4ec0\u4e48\uff1f \u3000\u3000\u8fb9\u6d69\u9053\uff1a\u6b64\u4eba\u867d\u7136\u521d\u51fa\u6c5f\u6e56\uff0c\u4f46\u6027\u683c\u521a\u5f3a\uff0c\u4e00\u4ecb\u4e0d\u53d6\uff0c\u53ef\u8bf4\u662f\u6761\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u6211\u548c\u4ed6 \u76f8\u8bc6\u867d\u624d\u4e24\u5929\uff0c\u4f46\u786e\u4fe1\u4ed6\u7edd\u4e0d\u662f\u6cb9\u6ed1\u7684\u5c0f\u4eba\uff01 \u3000\u3000\u72ec\u773c\u5987\u4eba\u51b7\u7b11\u9053\uff1a\u76f8\u8bc6\u65b9\u4e24\u5929\uff0c\u5c31\u80fd\u770b\u5f97\u51fa\u4ed6\u662f\u4e0d\u662f\u597d\u5927\u4e86\u4e48\uff1f\u770b\u6765\u4f60\u8fd9\u4e48\u559c\u6b22\u4e71\u4ea4\u670b \u53cb\u7684\u813e\u6c14\uff0c\u7adf\u5230\u4eca\u5929\u8fd8\u672a\u6539\u3002 \u3000\u3000\u5979\u5ffd\u7136\u6012\u543c\u7740\u9053\uff1a\u6614\u5e74\u82e5\u4e0d\u662f\u4f60\u5c06\u8fd9\u59d3\u94c1\u7684\u5e26\u56de\u6765\uff0c\u8bf4\u4ed6\u662f\u597d\u4eba\uff0c\u6211\u4eec\u53c8\u600e\u4f1a\u548c\u4ed6\u4ea4\u670b \u53cb\uff0c\u7fc1\u5929\u6770\u53c8\u600e\u4f1a\u6b7b\u6625\u4e5f\u624b\u91cc\uff1f \u3000\u3000\u8fb9\u6d69\u5782\u4e0b\u4e86\u5934\uff0c\u4e5f\u4e0d\u6562\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u778e\u5b50\u5374\u9053\uff1a\u65e0\u8bba\u5982\u4f55\uff0c\u627e\u51e0\u4e2a\u4eba\u6765\u4f5c\u516c\u8bc1\uff0c\u8fd9\u4e3b\u610f\u603b\u662f\u4e0d\u9519\u7684\uff0c\u4e2d\u539f\u516b\u4e49\u603b\u4e0d\u80fd\u80e1\u4e71\u6740 \u4eba\u3002 \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff1a\u8001\u4e09\u65e2\u7136\u5df2\u5c06\u4eba\u5bb6\u8bf7\u6765\u4e86\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u8ba9\u4eba\u5bb6\u7ad9\u5728\u96ea\u5730\u91cc\u559d\u897f\u5317\u98ce\u5427 \u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u52a8\u5bb9\u9053\uff1a\u4eba\u5df2\u7ecf\u6765\u4e86\uff1f \u3000\u3000\u8fb9\u6d69\u82e6\u7b11\u9053\uff1a\u6211\u672c\u6765\u662f\u60f3\u4ed6\u4eec\u4e00\u4e0b\u8bf7\u5230\u9f99\u5578\u4e91\u90a3\u91cc\u53bb\uff0c\u5f53\u7740\u5927\u5bb6\u7684\u9762\uff0c\u5c06\u6b64\u4e8b\u4f5c\u4e00\u4e86\u65ad \u7684\uff0c\u4e0d\u60f3\u5927\u5ac2\u5df2\u5c06\u94c1\u67d0\u627e\u6765\u4e86\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u9ed8\u9ed8\u534a\u664c\uff0c\u970d\u5730\u62c9\u5f00\u4e86\u95e8\uff0c\u5927\u58f0\u9053\uff1a\u4e09\u4f4d\u65e2\u5df2\u6765\u4e86\uff0c\u5c31\u8bf7\u8fdb\u6765\u5427\u3002 \u3000\u3000\u94c1\u4f20\u7532\u62b1\u5b9a\u4e3b\u610f\uff0c\u518d\u4e5f\u4e0d\u80af\u7741\u5f00\u773c\u775b\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u4ed6\u5b9e\u5728\u4e0d\u613f\u518d\u770b\u90a3\u94c1\u9762\u65e0\u79c1\u8d75\u6b63\u4e49\u4e00 \u773c\u3002 \u3000\u3000\u4ed6\u5df2\u62b1\u5b9a\u4e3b\u610f\u4ec0\u4e48\u90fd\u4e0d\u770b\uff0c\u4ec0\u4e48\u90fd\u4e0d\u8bf4\u3002 \u3000\u3000\u53ea\u542c\u811a\u6b65\u58f0\u97f3\uff0c\u679c\u7136\u6709\u4e24\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7b2c\u4e00\u4eba\u811a\u6b65\u6c89\u7a33\uff0c\u4e0b\u8eab\u663e\u7136\u5f88\u6709\u529f\u592b\uff0c\u5357\u62f3\u5317\u817f\uff0c\u8d75\u6b63\u4e49\u662f\u5317\u65b9\u8c6a\u6770\uff0c\u529f\u592b\u5927\u534a\u90fd\u5728\u4e24 \u6761\u817f\u4e0a\u3002\u7b2c\u4e8c\u4eba\u7684\u811a\u6b65\u5f88\u91cd\uff0c\u5374\u5f88\u6d6e\uff0c\u8d70\u8fdb\u6765\u65f6\uff0c\u8fd8\u5728\u8f7b\u8f7b\u5598\u7740\u6c14\uff0c\u8fd9\u4eba\u8eab\u4e0a\u5c31\u7b97\u6709\u6b66\u529f\uff0c \u4e5f\u597d\u4e0d\u5230\u54ea\u91cc\u53bb\u3002\u94c1\u4f20\u7532\u5e76\u6ca1\u6709\u542c\u5230\u7b2c\u4e09\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u96be\u9053\u7b2c\u4e09\u4e2a\u8d70\u8def\u65f6\u5c45\u7136\u8fde\u4e00\u70b9\u811a\u6b65\u58f0\u90fd\u6ca1\u6709\uff1f \u3000\u3000\u90a3\u778e\u5b50\u4f3c\u4e4e\u7ad9\u4e86\u8d77\u6765\uff0c\u4f20\u58f0\u9053\uff1a\u4e3a\u4e86\u5728\u4e0b\u5144\u5f1f\u6614\u5e74\u7684\u4e00\u70b9\u6069\u6028\uff0c\u65e0\u7aef\u52b3\u52a8\u4e09\u4f4d\u7684\u5927\u9a7e\uff0c \u5df2\u662f\u4e0d\u8be5\uff0c\u53c8\u5bb3\u5f97\u4e09\u4f4d\u5728\u98ce\u96ea\u4e2d\u67af\u5019\u591a\u65f6\uff0c\u66f4\u662f\u8be5\u6b7b\uff0c\u4f46\u8bf7\u4e09\u4f4d\u6055\u7f6a\u3002 \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u6c38\u8fdc\u4e0d\u6025\u4e0d\u5f90\u3001\u51b7\u51b7\u6de1\u6de1\uff0c\u8c01\u4e5f\u542c\u4e0d\u51fa\u4ed6\u8bf4\u7684\u662f\u771f\u5fc3\u8bdd\uff0c\u8fd8\u662f\u610f\u5b58\u8ba5\u8bbd\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8d75\u6b63\u4e49\u7684\u58f0\u97f3\u9053\uff1a\u6211\u8f88\u4e3a\u4e86\u6c5f\u6e56\u516c\u9053\uff0c\u4e24\u808b\u63d2\u5200\u4e5f\u5728\u6240\u4e0d\u8f9e\uff0c\u6613\u4e8c\u5148\u751f\u4f55\u5fc5\u5ba2\u6c14 \u3002 \u3000\u3000\u8fd9\u4eba\u53ea\u8981\u4e00\u5f00\u53e3\uff0c\u5c31\u662f\u5149\u660e\u5802\u7687\u7684\u8bdd\uff0c\u4f46\u8fd9\u79cd\u8bdd\u94c1\u4f20\u7532\u65e9\u5df2\u542c\u817b\u4e86\uff0c\u7b80\u76f4\u60f3\u4f5c\u5455\u3002 \u3000\u3000\u53c8\u542c\u89c1\u4e00\u4e2a\u5f88\u82cd\u8001\u5374\u53c8\u5f88\u6e05\u6717\u7684\u58f0\u97f3\u9053\uff1a\u8001\u673d\u867d\u4e0d\u8fc7\u662f\u4e2a\u8bf4\u4e66\u7684\uff0c\u4f46\u5e73\u65e5\u8bf4\u7684\u4e5f\u662f\u6c5f\u6e56 \u4fa0\u58eb\u4eec\u98ce\u5149\u9701\u6708\u7684\u884c\u5f84\uff0c\u5fc3\u91cc\u66f4\u4e45\u5df2\u4ef0\u6155\u5f97\u5f88\uff0c\u4eca\u65e5\u627f\u8499\u5404\u4f4d\u770b\u5f97\u8d77\uff0c\u80fd\u5230\u8fd9\u91cc\u6765\uff0c\u66f4\u662f\u4e09 \u751f\u6709\u5e78\u3002 \u3000\u3000\u778e\u5b50\u51b7\u9053\uff1a\u53ea\u671b\u9601\u4e0b\u56de\u53bb\u540e\uff0c\u80fd\u5c06\u8fd9\u4ef6\u4e8b\u7684\u662f\u975e\u66f2\u6298\uff0c\u5411\u5929\u4e0b\u4eba\u539f\u539f\u672c\u672c\u5730\u8bf4\u51fa\u6765\uff0c\u6211 \u5144\u5f1f\u5c31\u5f97\u76ca\u975e\u6d45\u4e86\u3002 \u3000\u3000\u90a3\u8bf4\u4e66\u7684\u8d54\u9053\uff1a\u8fd9\u4e00\u70b9\u8001\u673d\u66f4\u662f\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u8001\u673d\u5fc5\u5b9a\u4f1a\u5c06\u4eca\u65e5\u6240\u89c1\uff0c\u4e00\u70b9\u4e0d\u6f0f\u5730\u8bf4\u51fa\u6765 \uff0c\u8fb9\u4e09\u7237\u627e\u8001\u673d\u6765\u53c2\u4e0e\u6b64\u4e8b\uff0c\u4e5f\u5c31\u662f\u8fd9\u610f\u601d\u3002 \u3000\u3000\u94c1\u4f20\u7532\u8fd9\u624d\u77e5\u9053\u8fb9\u6d69\u627e\u8fd9\u4eba\u6765\u7684\u7528\u610f\uff0c\u4ed6\u4e5f\u4e0d\u7981\u5728\u6697\u4e2d\u4f69\u670d\u8fb9\u6d69\u529e\u4e8b\u4e4b\u5468\u5bc6\uff0c\u4ec0\u4e48\u4e8b\u90fd \u60f3\u5230\u4e86\u3002 \u3000\u3000\u7a81\u542c\u72ec\u773c\u5987\u4eba\u9053\uff1a\u4e0d\u77e5\u8fd9\u4f4d\u670b\u53cb\u8d35\u59d3\u5927\u540d\uff0c\u80fd\u5426\u89c1\u544a\uff1f \u3000\u3000\u8fd9\u53e5\u8bdd\u663e\u7136\u662f\u5bf9\u7b2c\u4e09\u4e2a\u4eba\u8bf4\u7684\u3002 \u3000\u3000\u4f46\u7b2c\u4e09\u4e2a\u4eba\u5e76\u6ca1\u6709\u5f00\u8154\uff0c\u8fb9\u6d69\u5374\u9053\uff1a\u8fd9\u4f4d\u670b\u53cb\u7d20\u6765\u4e0d\u613f\u8ba9\u522b\u4eba\u77e5\u9053\u4ed6\u7684\u59d3\u540d\u2014\u2014 \u3000\u3000\u778e\u5b50\u51b7\u51b7\u9053\uff1a\u4ed6\u7684\u59d3\u540d\u548c\u8fd9\u4ef6\u4e8b\u5e76\u6ca1\u6709\u5173\u7cfb\uff0c\u4ed6\u4e0d\u613f\u8bf4\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u95ee\uff0c\u53ef\u662f\u6211\u4eec\u8fd9\u4e9b \u4eba\u7684\u59d3\u540d\uff0c\u4ed6\u5374\u4e0d\u80fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fb9\u6d69\u7acb\u523b\u5c31\u9053\uff1a\u6211\u4eec\u672c\u6709\u516b\u5144\u5f1f\uff0c\u6614\u5e74\u627f\u6c5f\u6e56\u62ac\u7231\uff0c\u6c22\u6211\u4eec\u53eb\u505a\u4e2d\u539f\u516b\u4e49\uff0c\u5176\u5b9e\u8fd9\u4e5f\u4e0d \u8fc7\u662f\u670b\u53cb\u7684\u62ac\u7231\u2014\u2014 \u3000\u3000\u778e\u5b50\u5ffd\u53c8\u622a\u53e3\u9053\uff1a\u8fd9\u5e76\u4e0d\u662f\u670b\u53cb\u4eec\u7684\u62ac\u7231\uff0c\u6211\u5144\u5f1f\u6b66\u529f\u867d\u4e0d\u51fa\u540d\uff0c\u76f8\u8c8c\u66f4\u4e0d\u60ca\u4eba\uff0c\u4f46\u5e73 \u751f\u505a\u7684\u4e8b\uff0c\u83ab\u4e0d\u4ee5\u4e49\u6c14\u4e3a\u5148\uff0c\u7edd\u6ca1\u6709\u89c1\u4e0d\u5f97\u4eba\u7684\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5927\u58f0\u9053\uff1a\u4e2d\u539f\u516b\u4e49\uff0c\u4e49\u8584\u4e91\u5929\uff0c\u6c5f\u6e56\u4e2d\u8c01\u4eba\u4e0d\u77e5\uff0c\u54ea\u4e2a\u4e0d\u6653\u3002 \u3000\u3000\u90a3\u8bf4\u4e66\u7684\u4e5f\u62cd\u624b\u9053\uff1a\u4e2d\u539f\u516b\u4e49\uff0c\u597d\u54cd\u4eae\u7684\u540d\u5b57\uff0c\u8fd9\u4f4d\u8001\u5148\u751f\u60f3\u5fc5\u5c31\u662f\u5927\u4e49\u58eb\u4e86\u3002 \u3000\u3000\u778e\u5b50\u9053\uff1a\u6211\u662f\u8001\u4e8c\uff0c\u53eb\u6613\u660e\u6e56\uff0c\u6614\u65e5\u4eba\u79f0\u795e\u76ee\u5982\u7535\uff0c\u53ef\u662f\u73b0\u5728\u2014\u2014\uff0d \u3000\u3000\u4ed6\u60e8\u7b11\u4e86\u51e0\u58f0\uff0c\u55c4\u58f0\u9053\uff1a\u73b0\u5728\u6211\u7684\u540d\u5b57\u53eb\u6709\u773c\u65e0\u73e0\uff0c\u4f60\u8bb0\u4f4f\u4e86\u5427\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u8d54\u7b11\u9053\uff1a\u5728\u4e0b\u600e\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u5356\u91ce\u836f\u7684\u90ce\u4e2d\u9053\uff1a\u6211\u4e09\u54e5\u5b9d\u9a6c\u795e\u67aa\u8fb9\u6d69\u4f60\u5df2\u89c1\u8fc7\u4e86\uff0c\u6211\u884c\u56db\uff0c\u53eb\u91d1\u98ce\u767d\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u9053\uff1a\u542c\u9601\u4e0b\u53e3\u97f3\uff0c\u597d\u50cf\u662f\u5357\u9633\u5e9c\u7684\u4eba\u3002 \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u6b63\u662f\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u9053\uff1a\u5357\u9633\u5e9c\u4e00\u8d34\u5802\u91d1\u5bb6\u836f\u94fa\uff0c\u662f\u51e0\u5341\u5e74\u7684\u8001\u5b57\u53f7\uff0c\u8001\u673d\u5c0f\u65f6\u4e5f\u66fe\u5403\u8fc7\u4e00\u5e16\u5802\u7684\u9a71 \u866b\u6563\uff0c\u4e0d\u77e5\u9601\u4e0b\u2014\u2014 \u3000\u3000\u91d1\u98ce\u767d\u60e8\u7b11\u9053\uff1a\u8fde\u4e07\u7272\u56ed\u7684\u5c11\u4e1c\u90fd\u5df2\u5728\u5356\u9e2d\u811a\uff0c\u8fd8\u63d0\u4ec0\u4e48\u4e00\u5e16\u5802\u5462\uff1f \u3000\u3000\u8bf4\u4e66\u7684\u5931\u58f0\u9053\uff1a\u4e07\u7272\u56ed\uff1f\u83ab\u975e\u5f20\u8001\u5584\u4eba\u7684\u516c\u5b50\u4e5f\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u55ef\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u9053\uff1a\u662f\u54ea\u4e00\u4f4d\uff1f \u3000\u3000\u90a3\u5356\u9152\u7684\u9053\uff1a\u5c31\u662f\u6211\u8fd9\u5356\u9e2d\u811a\u7684\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4e0d\u80dc\u60ca\u8bb6\uff0c\u53c8\u4e0d\u80dc\u611f\u6168\u3002 \u3000\u3000\u9ebb\u5b50\u62a2\u7740\u9053\uff1a\u6211\u662f\u8001\u4e03\uff0c\u53eb\u516c\u5b59\u96e8\uff0c\u56e0\u4e3a\u6211\u7684\u9ebb\u5b50\u6bd4\u96e8\u70b9\u8fd8\u5bc6\u3002 \u3000\u3000\u5356\u81ed\u8c46\u5e72\u7684\u9053\uff1a\u6211\u662f\u8001\u516b\uff0c\u53eb\u8d74\u6c64\u8e48\u706b\u897f\u95e8\u70c8\uff0c\u73b0\u5728\u679c\u7136\u662f\u4e00\u5934\u6311\u6cb9\u6c64\uff0c\u4e00\u5934\u6311\u70c8\u706b\uff0c \u5356\u7684\u5374\u662f\u81ed\u8c46\u8150\u5e72\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u9053\uff1a\u4e0d\u77e5\u5927\u4e49\u58eb\u5728\u54ea\u91cc\uff1f \u3000\u3000\u516c\u5b59\u96e8\u9053\uff1a\u6211\u5927\u54e5\u4e49\u8584\u4e91\u5929\u7fc1\u5929\u6770\u5df2\u88ab\u4eba\u5bb3\u6b7b\uff0c\u8fd9\u662f\u6211\u5927\u5ac2\u2014\u2014 \u3000\u3000\u72ec\u773c\u5987\u4eba\u9053\uff1a\u6211\u7684\u540d\u5b57\u53ef\u4e0d\u597d\u542c\uff0c\u53eb\u5973\u5c60\u6237\u7fc1\u5927\u5a18\uff0c\u4f46\u4f60\u8fd8\u662f\u597d\u597d\u8bb0\u7740\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u966a\u7b11\u9053\uff1a\u8001\u673d\u867d\u5df2\u5e74\u8001\u660f\u5eb8\uff0c\u4f46\u81ea\u4fe1\u8bb0\u6027\u8fd8\u4e0d\u9519\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u9053\uff1a\u6211\u4eec\u8981\u4f60\u5c06\u540d\u5b57\u8bb0\u4f4f\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u9760\u4f60\u6765\u626c\u540d\u7acb\u5b57\uff0c\u800c\u662f\u8981\u501f\u4f60\u7684\u5634\uff0c\u5c06 \u6211\u4eec\u7684\u8840\u6d77\u6df1\u4ec7\u8bf4\u51fa\u6765\uff0c\u8ba9\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e5f\u597d\u77e5\u9053\u5176\u4e2d\u771f\u76f8\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u9053\uff1a\u8840\u6d77\u6df1\u4ec7\uff1f\u83ab\u975e\u7fc1\u5927\u4e49\u58eb\u2014\u2014\uff0d \u3000\u3000\u516c\u5b59\u96e8\u538b\u58f0\u9053\uff1a\u8fd9\u4eba\u53eb\u94c1\u7532\u91d1\u521a\u94c1\u4f20\u7532\uff0c\u5bb3\u6b7b\u6211\u5927\u54e5\u7684\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u6211\u5144\u5f1f\u516b\u4eba\u60c5\u5982\u624b\u8db3\uff0c\u867d\u7136\u6bcf\u4eba\u90fd\u6709\u81ea\u5df1\u7684\u4e8b\uff0c\u4f46\u6bcf\u5e74\u4e2d\u79cb\u65f6\u90fd\u8981\u5230\u5927\u54e5\u7684 \u5e84\u5b50\u91cc\u53bb\u4f4f\u4e0a\u51e0\u4e2a\u6708\u3002 \u3000\u3000\u5f20\u627f\u52cb\u9053\uff1a\u6211\u5144\u5f1f\u516b\u4eba\u672c\u6765\u5df2\u7ecf\u591f\u70ed\u95f9\u4e86\uff0c\u6240\u4ee5\u4e00\u5411\u6ca1\u6709\u518d\u627e\u522b\u7684\u670b\u53cb\uff0c\u90a3\u4e00\u5e74\u4e09\u54e5\u5374 \u5e26\u4e86\u4e2a\u4eba\u56de\u6765\uff0c\u8fd8\u8bf4\u8fd9\u4eba\u662f\u4e2a\u597d\u670b\u53cb\u3002\u516c\u5b59\u96e8\u6068\u6068\uff1a\u8fd9\u4eba\u5c31\u662f\u5fd8\u6069\u8d1f\u4e49\u3001\u5356\u53cb\u6c42\u8363\u7684\u94c1\u4f20\u7532 \uff01 \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u6211\u5927\u54e5\u672c\u5c31\u662f\u4e2a\u8981\u670b\u53cb\u4e0d\u8981\u547d\u7684\u4eba\uff0c\u89c1\u5230\u8fd9\u59d3\u94c1\u7684\u770b\u6765\u8fd8\u50cf\u662f\u6761\u6c49\u5b50\uff0c\u4e5f\u5c31 \u62ff\u4ed6\u5f53\u81ea\u5df1\u670b\u53cb\u4e00\u822c\u770b\u5f85\uff0c\u8c01\u2014\u2014\u4ed6\u5374\u4e0d\u662f\u4eba\uff0c\u662f\u4e2a\u755c\u751f\uff01 \u3000\u3000\u5f20\u627f\u52cb\u9053\uff1a\u8fc7\u5b8c\u5e74\u540e\u6211\u4eec\u90fd\u6563\u4e86\uff0c\u5927\u54e5\u5374\u786c\u8981\u7559\u4ed6\u591a\u4f4f\u4e24\u4e2a\u6708\uff0c\u8c01\u77e5\u4ed6\u7adf\u5728\u6697\u4e2d\u52fe\u7ed3\u4e86 \u6211\u5927\u54e5\u7684\u4e00\u4e9b\u5bf9\u5934\uff0c\u534a\u591c\u91cc\u95ef\u6765\u884c\u51f6\uff0c\u6740\u4e86\u6211\u5927\u54e5\uff0c\u70e7\u4e86\u7fc1\u5bb6\u5e84\uff0c\u6211\u5927\u5ac2\u867d\u7136\u4fa5\u5e78\u6ca1\u6709\u6b7b\uff0c \u4f46\u4e5f\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u5636\u58f0\u9053\uff1a\u4f60\u4eec\u770b\u89c1\u6211\u8138\u4e0a\u8fd9\u5200\u75a4\u6ca1\u6709\uff1f\u8fd9\u4e00\u5200\u51e0\u4e4e\u5c06\u6211\u8111\u888b\u780d\u6210\u4e24\u534a\uff0c\u82e5\u4e0d\u662f\u4ed6 \u4eec\u4ee5\u4e3a\u6211\u6b7b\u4e86\uff0c\u6211\u4e5f\u96be\u9003\u6bd2\u624b\uff01 \u3000\u3000\u516c\u5b59\u96e8\u543c\u9053\uff1a\u90a3\u65f6\u7fc1\u5bb6\u5e84\u7684\u4eba\u5168\u90fd\u6b7b\u5c3d\u6b7b\u7edd\uff0c\u5c31\u6ca1\u6709\u4eba\u77e5\u9053\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff0c\u4f60\u5012\u8bf4\uff0c\u8fd9 \u4eba\u7684\u5fc3\u9ed1\u4e0d\u9ed1\uff1f\u624b\u8fa3\u4e0d\u8fa3\uff1f \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u6211\u5144\u5f1f\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u540e\uff0c\u7acb\u523b\u629b\u4e0b\u4e86\u4e00\u5207\uff0c\u53d1\u8a93\u8981\u627e\u5230\u8fd9\u53ae\u4e3a\u5927\u54e5\u62a5\u4ec7\uff0c\u4eca \u65e5\u603b\u7b97\u7687\u5929\u6709\u773c\u2014\u2014\u7687\u5929\u6709\u773c\u2014\u2014 \u3000\u3000\u7fc1\u5927\u5a18\u538b\u58f0\uff1a\u73b0\u5728\u6211\u4eec\u5df2\u5c06\u8fd9\u4ef6\u4e8b\u7684\u59cb\u672b\u8bf4\u4e86\u51fa\u6765\uff0c\u4e09\u4f4d\u770b\u8fd9\u94c1\u7684\u662f\u8be5\u6740\uff0c\u8fd8\u662f\u4e0d\u8be5\u6740 \uff1f \u3000\u3000\u8d75\u6b63\u4e49\u6c89\u58f0\u9053\uff1a\u6b64\u4e8b\u82e5\u4e0d\u5047\uff0c\u7eb5\u5c06\u94c1\u4f20\u7532\u5343\u4e07\u4e07\u5250\uff0c\u4e5f\u4e0d\u4e3a\u8fc7\u3002 \u3000\u3000\u516c\u5b59\u96e8\u8df3\u4e86\u8d77\u6765\uff0c\u6012\u543c\u9053\uff1a\u6b64\u4e8b\u5f53\u7136\u662f\u771f\u7684\uff0c\u4e00\u5b57\u4e0d\u5047\uff0c\u4e0d\u4fe1\u4f60\u4eec\u5c31\u95ee\u95ee\u4ed6\u81ea\u5df1\u5427\uff01 \u3000\u3000\u94c1\u4f20\u7532\u54ac\u7740\u7259\u5173\uff0c\u55c4\u58f0\u9053\uff1a\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u7684\u786e\u6127\u5bf9\u7fc1\u5927\u54e5\uff0c\u6b7b\u800c\u65e0\u6028\u3002 \u3000\u3000\u516c\u5b59\u96e8\u5927\u547c\u9053\uff1a\u4f60\u4eec\u542c\u89c1\u6ca1\u6709\u2014\u2014\u4f60\u4eec\u542c\u89c1\u6ca1\u6709\u2014\u2014\u8fd9\u662f\u4ed6\u81ea\u5df1\u8bf4\u7684\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u5389\u58f0\u9053\uff1a\u4ed6\u81ea\u5df1\u65e2\u5df2\u62db\u8ba4\uff0c\u522b\u4eba\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff01 \u3000\u3000\u90a3\u8bf4\u4e66\u7684\u53f9\u606f\uff1a\u8001\u673d\u4e5f\u8bfb\u8fc7\u4e09\u56fd\uff0c\u8bf4\u8fc7\u5cb3\u4f20\uff0c\u4f46\u50cf\u8fd9\u79cd\u5fc3\u9ed1\u624b\u7ae6\u3001\u4e0d\u5fe0\u4e0d\u4e49\u7684\u4eba\uff0c\u53ea\u6015 \u8fde\u66f9\u64cd\u548c\u79e6\u6867\u8fd8\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u4e09\u4f4d\u90fd\u8ba4\u4e3a\u94c1\u4f20\u7532\u662f\u8be5\u6740\u7684\u4e86\uff01 \u3000\u3000\u8bf4\u4e66\u7684\u9053\uff1a\u8be5\u6740\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u4f55\u6b62\u8be5\u6740\uff0c\u7b80\u76f4\u8be5\u5c06\u4ed6\u4e71\u5200\u5206\xd7\uff0c\u4ee5\u8c22\u6c5f\u6e56\uff01 \u3000\u3000\u7a81\u542c\u4e00\u9053\uff1a\u4f60\u53e3\u53e3\u58f0\u58f0\u4e0d\u79bb\u6c5f\u6e56\uff0c\u96be\u9053\u4f60\u4e00\u4e2a\u4eba\u5c31\u4ee3\u8868\u6c5f\u6e56\u4e48\uff1f \u3000\u3000\u8fd9\u58f0\u97f3\u7b80\u77ed\u800c\u6709\u529b\uff0c\u6bcf\u4e2a\u5b57\u90fd\u50cf\u5200\u4e00\u6837\uff0c\u53c8\u51b7\uff0c\u53c8\u5feb\u2014\u2014\uff0d \u3000\u3000\u5728\u8fd9\u822c\u5c4b\u5b50\u91cc\uff0c\u4ed6\u81f3\u4eca\u624d\u7b2c\u4e00\u6b21\u8bf4\u8bdd\uff0c\u663e\u7136\u4ed6\u5c31\u662f\u90a3\u8d70\u8def\u50cf\u91ce\u517d\u822c\uff0c\u53ef\u4ee5\u4e0d\u53d1\u51fa\u4e1d\u6beb\u58f0 \u97f3\u6765\u7684\u7b2c\u4e09\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u94c1\u4f20\u7532\u5fc3\u91cc\u4e00\u8df3\uff0c\u5ffd\u7136\u53d1\u73b0\u8fd9\u58f0\u97f3\u5f88\u719f\u6089\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u5f20\u5f00\u773c\u6765\uff0c\u5c31\u53d1\u73b0\u5750\u5728\u8d75\u6b63\u4e49\u548c\u4e00\u4e2a\u8001\u8005\u4e2d\u95f4\u7684\uff0c\u5c31\u662f\u90a3\u5b64\u72ec\u800c\u51b7\u6f20\u7684\u5c11\u5e74\u963f \u98de\u3002 \u3000\u3000\u98de\u5c11\u7237\uff1f\u4f60\u600e\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f \u3000\u3000\u94c1\u4f20\u7532\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u60ca\u547c\u51fa\u58f0\u6765\uff0c\u4f46\u4ed6\u5374\u53ea\u662f\u66f4\u7528\u529b\u5730\u54ac\u7d27\u4e86\u7259\u5173\uff0c\u6ca1\u6709\u8bf4\u51fa\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5374\u5df2\u53d8\u8272\uff1a\u670b\u53cb\u4f60\u96be\u9053\u8ba4\u4e3a\u8fd9\u79cd\u4eba\u4e0d\u8be5\u6740\u4e48\uff1f \u3000\u3000\u963f\u98de\u51b7\u51b7\u9053\uff1a\u6211\u82e5\u8ba4\u4e3a\u4ed6\u4e0d\u8be5\u6740\uff0c\u4f60\u4eec\u5c31\u8981\u5c06\u6211\u4e5f\u4e00\u540c\u6740\u4e86\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u6613\u660e\u6e56\u7f13\u7f13\u9053\uff1a\u6211\u4eec\u5c06\u670b\u53cb\u8bf7\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u670b\u53cb\u4f60\u4e3b\u6301\u516c\u9053\uff0c\u53ea\u8981\u4f60\u8bf4\u51fa\u6b64\u4eba\u4e3a\u4f55\u4e0d \u8be5\u6740\uff0c\u800c\u4e14\u8bf4\u5f97\u6709\u7406\uff0c\u6211\u4eec\u7acb\u523b\u5c31\u653e\u4e86\u4ed6\u4e5f\u65e0\u59a8\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5389\u58f0\u9053\uff1a\u6211\u770b\u4ed6\u53ea\u4e0d\u8fc7\u662f\u65e0\u7406\u53d6\xd7\u800c\u5df2\uff0c\u5404\u4f4d\u4f55\u5fc5\u5c06\u4ed6\u7684\u7684\u8bdd\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u963f\u98de\u671b\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u8bf4\u522b\u4eba\u5356\u53cb\u6c42\u8363\uff0c\u4f60\u81ea\u5df1\u5c82\u975e\u4e5f\u51fa\u5356\u8fc7\u51e0\u767e\u4e2a\u670b\u53cb\uff0c\u90a3\u5929\u7fc1\u5bb6 \u5e84\u6740\u4eba\u7684\uff0c\u4f60\u5c82\u975e\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u53ea\u4e0d\u5b97\u7fc1\u5927\u5a18\u6ca1\u6709\u89c1\u5230\u4f60\uff01 \u3000\u3000\u4e2d\u539f\u516b\u4e49\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u771f\u6709\u6b64\u4e8b\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4ed6\u8981\u6740\u8fd9\u59d3\u94c1\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u6740\u4eba\u706d\u53e3\u800c\u5df2\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u672c\u6765\u8fd8\u5728\u51b7\u7b11\u7740\u5047\u4f5c\u4e0d\u5c51\u72b6\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u53d1\u6025\u4e86\u3002 \u3000\u3000\u5927\u6012\u9053\uff1a\u653e\u4f60\u5988\u2014\u2014\uff0d \u3000\u3000\u4ed6\u6025\u6012\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u4e5f\u8981\u548c\u516c\u5b59\u96e8\u4e00\u6837\u9a82\u8d77\u7c97\u8bdd\u6765\uff0c\u86e4\u5c41\u5b57\u5230\u4e86\u5634\u8fb9\uff0c\u5ffd\u7136\u60f3\u8d77\u8fd9\u53e5\u8bdd\u9a82 \u51fa\u6765\u5e76\u6ca1\u6709\u6548\u3002 \u3000\u3000\u800c\u4ed6\u51b7\u7b11\u7740\u8bf4\u8bdd\uff1a\u60f3\u4e0d\u5230\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e5f\u5b66\u4f1a\u4e86\u8840\u53e3\u55b7\u4eba\uff0c\u597d\u5728\u4f60\u8fd9\u7247\u9762\u4e4b\u8bcd\uff0c\u6ca1\u6709\u4eba \u76f8\u4fe1\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u7247\u9762\u4e4b\u8bcd\uff1f\u4f60\u4eec\u7684\u7247\u9762\u4e4b\u8bcd\uff0c\u4e3a\u4f55\u5c31\u8981\u522b\u4eba\u76f8\u4fe1\u5462\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u94c1\u67d0\u81ea\u5df1\u90fd\u5df2\u627f\u8ba4\uff0c\u4f60\u96be\u9053\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u542c\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u5b57\u672a\u8bf4\u5b8c\uff0c\u4ed6\u8170\u7554\u7684\u5251\u5df2\u62b5\u4f4f\u4e86\u8d75\u6b63\u4e49\u7684\u54bd\u5589\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u8eab\u7ecf\u767e\u6218\uff0c\u672c\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\uff0c\u4f46\u8fd9\u6b21\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u672a\u770b\u51fa\u8fd9\u5c11\u5e74\u662f\u5982\u4f55\u62d4 \u7684\u5251\uff01 \u3000\u3000\u4ed6\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u5251\u5c16\u5df2\u5230\u4e86\u81ea\u5df1\u54bd\u5589\uff0c\u4ed6\u65e2\u65e0\u6cd5\u95ea\u907f\uff0c\u66f4\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u4e86\uff0c\u55c4\u58f0\u9053\uff1a \u4f60\u2014\u2014\u4f60\u60f3\u600e\u6837\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u53ea\u95ee\u4f60\uff0c\u90a3\u5929\u5230\u7fc1\u5bb6\u5e84\u53bb\u6740\u4eba\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u6709\u4e00\u4efd\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u6012\u9053\uff1a\u4f60\u75af\u4e86\u3002 \u3000\u3000\u963f\u98de\u7f13\u7f13\u9053\uff1a\u4f60\u82e5\u518d\u4e0d\u627f\u8ba4\uff0c\u6211\u5c31\u6740\u4e86\u4f60\uff01 \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u8bf4\u5f97\u5e73\u5e73\u6de1\u6de1\uff0c\u5c31\u597d\u50cf\u662f\u5728\u8bf4\u7b11\u4f3c\u7684\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u6ee1\u8138\u5927\u6c57\u9ec4\u8c46\u822c\u6eda\u4e86\u4e0b\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u6211\u2014\u2014\u6211 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u8fd9\u6b21\u56de\u7b54\u6700\u597d\u5c0f\u5fc3\u4e9b\uff0c\u5343\u4e07\u83ab\u8981\u8bf4\u9519\u4e86\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u963f\u98de\u8170\u4e0a\u63d2\u7740\u7684\u90a3\u67c4\u5251\uff0c\u4eba\u4eba\u90fd\u65e9\u5df2\u770b\u89c1\u4e86\uff0c\u4eba\u4eba\u90fd\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u4f46\u73b0\u5728\uff0c\u5374\u6ca1\u6709\u4eba \u518d\u89c9\u5f97\u597d\u7b11\u4e86\u3002 \u3000\u3000\u963f\u98de\u7f13\u7f13\u9053\uff1a\u6211\u6700\u540e\u518d\u95ee\u4f60\u4e00\u6b21\uff0c\u8fd9\u662f\u6700\u540e\u4e00\u6b21\u4e86\uff01\u7edd\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u6b21\u2014\u2014\u6211\u95ee\u4f60\uff0c\u7fc1\u5929 \u6770\u662f\u4e0d\u662f\u4f60\u5bb3\u6b7b\u7684\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u671b\u7740\u4ed6\u90a3\u53cc\u6f06\u9ed1\u5f97\u770b\u4e0d\u5230\u5e95\u7684\u7738\u5b50\uff0c\u53ea\u89c9\u81ea\u5df1\u7684\u9aa8\u90fd\u5df2\u51b0\u51b7\uff0c\u7adf\u4e0d\u7531\u81ea\u4e3b\u5730\u98a4\u58f0 \u9053\uff1a\u662f \u3000\u3000\u8fd9\u662f\u5b57\u81ea\u4ed6\u5634\u91cc\u8bf4\u51fa\u6765\uff0c\u4e2d\u539f\u516b\u4e49\u4ff1\u90fd\u8038\u7136\u53d8\u8272\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u4e00\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u5404\u4f4d\u4e0d\u5fc5\u751f\u6c14\uff0c\u7fc1\u5929\u6770\u4e4b\u6b7b\uff0c\u548c\u4ed6\u5e76\u6ca1\u6709\u4e1d\u6beb\u5173\u7cfb\u3002 \u3000\u3000\u4e2d\u539f\u516b\u4e49\u53c8\u90fd\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4ed6\u53ea\u4e0d\u8fc7\u8bf4\u660e\u4e86\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u4e00\u4e2a\u4eba\u5728\u88ab\u903c\u65f6\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u6839\u672c\u5c31\u7b97\u4e0d\u5f97\u6570 \u7684\u3002 \u3000\u3000\u4e2d\u539f\u516b\u4e49\u7eb7\u7eb7\u559d\u9053\uff1a\u6211\u4eec\u51e0\u65f6\u903c\u8fc7\u4ed6\uff1f \u3000\u3000\u4f60\u96be\u9053\u8fd8\u8ba4\u4e3a\u8fd9\u662f\u5c48\u6253\u6210\u62db\u4e48\uff1f \u3000\u3000\u4ed6\u82e5\u6709\u59d4\u5c48\uff0c\u81ea\u5df1\u4e3a\u4f55\u4e0d\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u7eb7\u4e71\u4e2d\uff0c\u53ea\u542c\u6613\u660e\u6e56\u7f13\u7f13\u9053\uff1a\u94c1\u4f20\u7532\u4f60\u82e5\u8ba4\u4e3a\u6211\u5144\u5f1f\u51a4\u6789\u4e86\u4f60\uff0c\u6b64\u523b\u6b63\u597d\u5411\u6211\u5144\u5f1f\u89e3\u91ca \uff01 \u3000\u3000\u8fd9\u8bdd\u58f0\u867d\u7f13\u6162\uff0c\u4f46\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u8bf4\u51fa\u6765\uff0c\u7adf\u5c06\u6240\u6709\u7684\u6012\u559d\u58f0\u5168\u90fd\u538b\u4e86\u4e0b\u53bb\uff0c\u6b64\u4eba\u53cc\u76ee\u867d \u76f2\uff0c\u4f46\u5185\u529b\u4e4b\u6df1\uff0c\u539f\u90fd\u8fd8\u5728\u522b\u4eba\u4e4b\u4e0a\u3002 \u3000\u3000\u94c1\u7532\u7d27\u54ac\u7740\u7259\u5173\uff0c\u6ee1\u9762\u4ff1\u662f\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u9053\uff1a\u4f60\u82e5\u662f\u65e0\u8bdd\u53ef\u8bf4\uff0c\u5c31\u8868\u793a\u81ea\u5df1\u62db\u8ba4\u4e86\uff0c\u54b1\u4f4d\u53ef\u6ca1\u6709\u7528\u5200\u903c\u7740\u4f60\u3002 \u3000\u3000\u94c1\u4f20\u7532\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1a\u98de\u5c11\u7237\uff0c\u6211\u5b9e\u5728\u65e0\u8bdd\u53ef\u8bf4\uff0c\u53ea\u597d\xd7\u8d1f\u4f60\u4e00\u7247\u597d\u5fc3\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u65e0\u8bba\u4ed6\u8bf4\u4e0d\u8bf4\u8bdd\uff0c\u6211\u90fd\u4e0d\u60f3\u5ff5\u4ed6\u4f1a\u662f\u5356\u53cb\u6c42\u8363\u7684\u4eba\u3002 \u3000\u3000\u516c\u5b59\u96e8\u6012\u543c\u9053\uff1a\u4e8b\u5b9e\u4ff1\u5728\uff0c\u4f60\u4e0d\u4fe1\u4e5f\u5f97\u4fe1\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u51b7\u7b11\u9053\uff1a\u4ed6\u4e0d\u4fe1\u5c31\u7b97\u4e86\uff0c\u54b1\u4eec\u4f55\u5fc5\u4e00\u5b9a\u8981\u4ed6\u76f8\u4fe1\uff1f \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u6839\u672c\u548c\u4ed6\u6ca1\u6709\u5173\u7cfb\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u65e2\u5df2\u6765\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u548c\u6211\u6709\u5173\u7cfb\u4e86\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u6012\u9053\uff1a\u4f60\u7b97\u54ea\u68f5\u8471\uff0c\u6562\u6765\u7ba1\u54b1\u4eec\u7684\u95f2\u4e8b\uff01 \u3000\u3000\u90a3\u6a35\u592b\u5927\u543c\u9053\uff1a\u8001\u5b50\u504f\u8981\u4f24\u4f24\u4e86\u4ed6\uff0c\u770b\u4f60\u5c0f\u5b50\u600e\u4e48\u6837\u3002 \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u6700\u5c11\uff0c\u52a8\u624b\u5374\u6700\u5feb\uff0c\u8bdd\u97f3\u672b\u4e86\uff0c\u4e00\u67c4\u65a7\u5934\u5df2\u5411\u94c1\u4f20\u7532\u5f53\u5934\u780d\u4e86\u4e0b\u53bb\uff0c\u98ce\u58f0\u864e\u864e \uff0c\u7acb\u5288\u534e\u5c71\uff01\u4ed6\u6614\u5e74\u53f7\u79f0\u7acb\u5288\u534e\u5c71\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u4ed6\u7684\u6210\u540d\u4e4b\u4f5c\u3002\u94c1\u4f20\u7532\u6728\u5934\u4eba\u822c\u5750\u5728\u90a3\u91cc\uff0c \u7eb5\u6709\u4e00\u8eab\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u773c\u89c1\u4e5f\u8981\u88ab\u8fd9\u4e00\u65a7\u5288\u6210\u4e24\u534a\u3002 \u3000\u3000\u90a3\u8bf4\u4e66\u7684\u60ca\u547c\u4e00\u58f0\uff0c\u53ea\u9053\u4ed6\u7acb\u523b\u5c31\u8981\u8840\u6e85\u4e94\u6b65\u3002 \u3000\u3000\u8c01\u77e5\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u597d\u597d\u7684\u4e00\u628a\u5927\u65a7\u7adf\u7136\u65ad\u6210\u4e24\u622a\uff0c\u65a7\u5934\u5f53\u7684\u8dcc\u5728 \u94c1\u4f20\u7532\u9762\u524d\u3002\u8fd9\u53d8\u5316\u867d\u5feb\uff0c\u4f46\u4e2d\u539f\u516b\u4e49\u7a76\u7adf\u90fd\u4e0d\u662f\u996d\u6876\uff0c\u6bcf\u4e2a\u4eba\u90fd\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5927\u5bb6\u90fd\u4e0d \u7981\u4e3a\u4e4b\u9762\u8272\u60e8\u53d8\uff0c\u4e00\u58f0\u60ca\u547c\u5c1a\u672a\u51fa\u53e3\uff0c\u53ea\u89c1\u963f\u98de\u624b\u91cc\u7684\u5251\u4e00\u504f\uff0c\u624b\u63e1\u5251\u80cc\u6258\u7740\u4e86\u90a3\u6a35\u592b\u7684\u4e0b \u5df4\u3002 \u3000\u3000\u90a3\u6a35\u592b\u4ef0\u5929\u4e00\u4e2a\u7b4b\u6597\u6454\u51fa\uff0c\u4eba\u4e5f\u75bc\u5f97\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65b9\u624d\u963f\u98de\u4e00\u5251\u5e1b\u4f4f\u4e86\u8d75\u6b63\u4e49\uff0c\u522b\u4eba\u8fd8\u5f53\u4ed6\u662f\u9aa4\u51fa\u4e0d\u610f\uff0c\u6709\u4e9b\u4fa5\u5e78\uff0c\u73b0\u5728\u8fd9\u4e00\u5251\u4f7f\u51fa\uff0c\u5927 \u5bb6\u624d\u771f\u7684\u88ab\u9a87\u5f97\u53d1\u5446\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u4e4e\u4e0d\u4fe1\u852c\u6709\u8fd9\u4e48\u5feb\u7684\u5251\uff01 \u3000\u3000\u963f\u98de\u6b64\u65f6\u5374\u5df2\u82e5\u65e0\u5176\u4e8b\u5730\u62c9\u8d77\u4e86\u94c1\u4f20\u7532\u7684\u4e8b\uff0c\u9053\uff1a\u8d70\u5427\uff0c\u6211\u4eec\u559d\u9152\u53bb \u3000\u3000\u94c1\u4f20\u7532\u7adf\u8eab\u4e0d\u7531\u5df2\u5730\u88ab\u4ed6\u62c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u516c\u5b59\u96e8\u3001\u91d1\u98ce\u767d\u3001\u8fb9\u6d69\u4e09\u4e2a\u4eba\u540c\u65f6\u62e6\u4f4f\u4e86\u4ed6\u4eec\u7684\u53bb\u8def\u3002 \u3000\u3000\u91d1\u98ce\u767d\u5636\u58f0\u9053\uff1a\u670b\u53cb\u73b0\u5728\u5c31\u60f3\u8d70\u4e86\u4e48\uff1f\u53ea\u6015\u6ca1\u8fd9\u4e48\u5bb9\u6613\u3002 \u3000\u3000\u963f\u98de\u6de1\u6de1\u9053\uff1a\u4f60\u8fd8\u8981\u6211\u600e\u4e48\u6837\uff1f\u4e00\u5b9a\u8981\u6211\u6740\u4e86\u4f60\u4e48\uff1f \u3000\u3000\u6613\u660e\u6e56\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\u8ba9\u4ed6\u8d70\u5427\uff01 \u3000\u3000\u7fc1\u5927\u5a18\u5636\u58f0\u9053\uff1a\u600e\u4e48\u80fd\u8ba9\u4ed6\u8d70\uff1f\u6211\u4eec\u8fd9\u4e48\u591a\u7684\u5fc3\u8840\u96be\u9053\u5c31\u7b97\u2014\u2014 \u3000\u3000\u6613\u660e\u6e56\u51b7\u51b7\u9053\uff1a\u5c31\u7b97\u5582\u4e86\u72d7\u5427\u3002 \u3000\u3000\u4ed6\u8138\u8272\u4ecd\u7136\u9634\u68ee\u68ee\u7684\uff0c\u53ea\u662f\u5411\u963f\u98de\u62f1\u4e86\u62f1\u624b\uff0c\u9053\uff1a\u9601\u4e0b\u8bf7\u5427\uff0c\u6c5f\u6e56\u4e2d\u672c\u6765\u5c31\u662f\u8fd9\u4e48\u56de\u4e8b \uff0c\u8c01\u7684\u5200\u5feb\uff0c\u8c01\u5c31\u6709\u7406\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u591a\u627f\u6307\u6559\uff0c\u8fd9\u53e5\u8bdd\u6211\u4e00\u5b9a\u4e0d\u4f1a\u5fd8\u8bb0\u7684\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u65e9\u5df2\u5fcd\u4e0d\u4f4f\u653e\u58f0\u75db\u54ed\u8d77\u6765\uff0c\u8dfa\u7740\u811a\u9053\uff1a\u4f60\u600e\u4e48\u80fd\u653e\u8d70\uff0c\u600e\u4e48\u80fd\u653e\u4ed6\u8d70\uff01 \u3000\u3000\u6613\u660e\u6e56\u9762\u4e0a\u5374\u6728\u65e0\u8868\u60c5\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u8981\u600e\u4e48\uff1f\u96be\u9053\u771f\u8981\u4ed6\u5c06\u6211\u4eec\u5168\u90fd\u6740\u4e86\u4e48\uff1f \u3000\u3000\u8fb9\u6d69\u9eef\u7136\u9053\uff1a\u4e8c\u54e5\u8bf4\u7684\u4e0d\u9519\uff0c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\uff0c\u53ea\u8981\u6211\u4eec\u6d3b\u7740\uff0c\u603b\u6709\u590d\u4ec7\u7684\u673a \u4f1a\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u5ffd\u7136\u6251\u8fc7\u53bb\uff0c\u63ea\u4f4f\u4ed6\u7684\u8863\u895f\uff0c\u5636\u58f0\u9053\uff1a\u4f60\u8fd8\u6709\u8138\u8bf4\u8bdd\uff1f\u8fd9\u53c8\u662f\u4f60\u5e26\u56de\u6765\u7684\u670b\u53cb\uff0c \u53cc\u662f\u4f60\u2014\u2014\uff0d\u8fb9\u6d69\u60e8\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u662f\u6211\u5e26\u56de\u6765\u7684\uff0c\u6211\u597d\u6b79\u8981\u5bf9\u5927\u5ac2\u6709\u4e2a\u4ea4\u5f85\u3002 \u3000\u3000\u53ea\u542c\u5636\u7684\u4e00\u58f0\uff0c\u4e00\u7247\u8863\u895f\u88ab\u626f\u4e86\u4e0b\u6765\uff0c\u4ed6\u7684\u4eba\u5df2\u8f6c\u8eab\u51b2\u4e86\u51fa\u52a8\uff0c\u7fc1\u5927\u5a18\u6014\u4e86\u6014\uff0c\u5931\u58f0\u9053 \uff1a\u8001\u4e09\uff0c\u4f60\u56de\u6765\u2014\u2014 \u3000\u3000\u4f46\u5979\u8ffd\u51fa\u53bb\u65f6\uff0c\u8fb9\u6d69\u5df2\u8d70\u5f97\u8fde\u5f71\u5b50\u90fd\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6613\u660e\u6e56\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u8ba9\u4ed6\u8d70\u5427\uff0c\u4f46\u613f\u4ed6\u80fd\u5c06\u4ed6\u90a3\u8001\u53cb\u627e\u6765\u3002 \u3000\u3000\u91d1\u98ce\u767d\u773c\u775b\u4e00\u4eae\uff0c\u52a8\u5bb9\u9053\uff1a\u4e8c\u54e5\u8bf4\u7684\u83ab\u975e\u662f\u2014\u2014\u2014\u2014 \u3000\u3000\u6613\u660e\u6e56\u9053\uff1a\u4f60\u65e2\u7136\u77e5\u9053\u662f\u8c01\uff0c\u4f55\u5fc5\u518d\u95ee\uff01 \u3000\u3000\u91d1\u98ce\u767d\u7684\u773c\u775b\u91cc\u53d1\u51fa\u4e86\u5149\uff0c\u5583\u5583\u9053\uff1a\u4e09\u54e5\u82e5\u771f\u80fd\u5c06\u90a3\u4eba\u627e\u51fa\u6765\uff0c\u8fd9\u5c0f\u5b50\u7684\u5251\u518d\u5feb\u4e5f\u6ca1\u6709 \u7528\u4e86\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u5176\u5b9e\u8fb9\u4e09\u4fa0\u7528\u4e0d\u7740\u53bb\u627e\u522b\u4eba\u7684\u3002 \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u54e6\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u6c89\u58f0\u9053\uff1a\u660e\u540e\u4e24\u65e5\uff0c\u672c\u6709\u4e09\u4f4d\u9ad8\u4eba\u8981\u5230\u8fd9\u91cc\u6765\uff0c\u90a3\u5c11\u5e74\u7eb5\u7136\u6709\u4e09\u5934\u516d\u81c2\uff0c\u6211\u4e5f\u8981 \u53eb\u4ed6\u4e09\u4e2a\u8111\u888b\u90fd\u642c\u5bb6\uff01 \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u662f\u54ea\u4e09\u4f4d\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u7f13\u7f13\u9053\uff1a\u5404\u4f4d\u542c\u90a3\u4e09\u4f4d\u7684\u540d\u5b57\uff0c\u53ea\u6015\u8981\u5413\u4e00\u8df3\u2014\u2014\uff0d \u7b2c\u5341\u4e8c\u7ae0\u3000\u540c\u662f\u65ad\u80a0\u4eba>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u540c\u662f\u65ad\u80a0\u4eba \u3000\u3000\u867d\u7136\u662f\u6b63\u5348\uff0c\u5929\u8272\u5374\u9634\u6c89\u5f97\u72b9\u5982\u9ec4\u660f\u3002 \u3000\u3000\u963f\u98de\u4e0d\u6025\u4e0d\u5f90\u5730\u8d70\u7740\uff0c\u5c31\u548c\u94c1\u4f20\u7532\u7b2c\u4e00\u6b21\u770b\u5230\u4ed6\u65f6\u5b8c\u5168\u4e00\u6837\uff0c\u770b\u6765\u662f\u90a3\u4e48\u5b64\u72ec\uff0c\u53c8\u90a3\u4e48 \u75b2\u5026\u3002 \u3000\u3000\u4f46\u94c1\u4f20\u7532\u73b0\u5728\u5df2\u77e5\u9053\uff0c\u53ea\u8981\u4e00\u9047\u5230\u5371\u9669\uff0c\u8fd9\u75b2\u5026\u7684\u5c11\u5e74\u7acb\u523b\u5c31\u4f1a\u632f\u4f5c\u8d77\u6765\uff0c\u53d8\u5f97\u9e70\u4e00\u822c \u654f\u9510\u3001\u77eb\u5065\u3002\u94c1\u4f20\u7532\u8d70\u5728\u4ed6\u8eab\u7554\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u8bdd\u60f3\u8bf4\uff0c\u5374\u53c8\u4e0d\u77e5\u8be5\u5982\u4f55\u8bf4\u8d77\uff0c\u674e\u5bfb\u6b22 \u4e5f\u5e76\u4e0d\u662f\u4e2a\u591a\u8bdd\u7684\u4eba\uff0c\u548c\u674e\u5bfb\u6b22\u5728\u4e00\u8d77\u751f\u6d3b\u4e86\u5341\u51e0\u5e74\uff0c\u4ed6\u5df2\u5b66\u4f1a\u4e86\u7528\u6c89\u9ed8\u6765\u4ee3\u66ff\u8bed\u8a00\uff0c\u4ed6\u53ea \u8bf4\u4e86\u4e24\u4e2a\u5b57\uff1a\u591a\u8c22\u3002 \u3000\u3000\u4f46\u4ed6\u7acb\u523b\u53d1\u73b0\u8fde\u8fd9\u4e24\u4e2a\u5b57\u4e5f\u662f\u591a\u4f59\u7684\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u963f\u98de\u4e5f\u548c\u674e\u5bfb\u6b22\u4e00\u6837\uff0c\u5728\u4ed6\u4eec\u8fd9\u79cd\u4eba \u9762\u524d\uff0c\u4f60\u6c38\u8fdc\u4e0d\u5fc5\u8bf4\u8c22\u5b57\u3002 \u3000\u3000\u9053\u65c1\u6709\u4e2a\u5c0f\u5c0f\u7684\u516d\u89d2\u4ead\uff0c\u5728\u6625\u79cb\u796d\u65e5\uff0c\u8fd9\u91cc\u60f3\u5fc5\u662f\u626b\u5893\u7684\u4eba\u811a\u7684\u5730\u65b9\uff0c\u73b0\u5728\u4ead\u5b50\u91cc\u5374\u53ea \u6709\u79ef\u96ea\uff0c\u963f\u98de\u8d70\u8fc7\u53bb\uff0c\u5ffd\u7136\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\u5c06\u5fc3\u91cc\u7684\u51a4\u5c48\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u94c1\u4f20\u7532\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6709\u4e9b\u8bdd\u6211\u5b81\u6b7b\u4e5f\u4e0d\u80fd\u8bf4\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u662f\u4e2a\u597d\u670b\u53cb\uff0c\u4f46\u4f60\u4eec\u5374\u5f04\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u54e6\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u4eec\u90fd\u4ee5\u4e3a\u6027\u547d\u662f\u81ea\u5df1\u7684\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u6743\u6b7b\uff01 \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u8fd9\u96be\u9053\u9519\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5f53\u7136\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u77aa\u7740\u94c1\u4f20\u7532\uff0c\u9053\uff1a\u4e00\u4e2a\u4eba\u751f\u4e0b\u6765\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u6b7b\u7684\u3002 \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u5446\u662f\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u5230\u4e86\u975e\u6b7b\u4e0d\u53ef\u7684\u65f6\u5019\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u5c31\u7b97\u5230\u4e86\u975e\u6b7b\u4e0d\u53ef\u7684\u65f6\u5019\uff0c\u4e5f\u8981\u594b\u529b\u6c42\u751f\u3002 \u3000\u3000\u4ed6\u77aa\u7740\u94c1\u4f20\u7532\uff0c\u5389\u58f0\u9053\uff1a\u8001\u5929\u4e3a\u4f60\u505a\u7684\u4e8b\u771f\u4e0d\u5c11\uff0c\u4f60\u4e3a\u8001\u5929\u505a\u8fc7\u4ec0\u4e48\u3002 \u3000\u3000\u94c1\u4f20\u7532\u6014\u4e86\u6014\u5782\u9996\u9053\uff1a\u4ec0\u4e48\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u7684\u7236\u6bcd\u517b\u80b2\u4e86\u4f60\uff0c\u6240\u8d39\u7684\u5fc3\u8840\u66f4\u5927\uff0c\u4f60\u53c8\u4e3a\u4ed6\u4eec\u505a\u8fc7\u4ec0\u4e48\uff1f \u3000\u3000\u94c1\u4f20\u7532\u5934\u5782\u5f97\u66f4\u4f4e\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6709\u4e9b\u8bdd\u662f\u4e0d\u80fd\u8bf4\u7684\uff0c\u82e5\u662f\u8bf4\u51fa\u6765\u5c31\u5bf9\u4e0d\u8d77\u670b\u53cb\uff0c\u53ef\u662f\u4f60\u82e5\u5c31\u8fd9\u6837\u6b7b\u4e86 \uff0c\u53c8\u600e\u4e48\u5bf9\u5f97\u8d77\u4f60\u7684\u7236\u6bcd\uff0c\u600e\u4e48\u5bf9\u5f97\u8d77\u8001\u5929\uff1f \u3000\u3000\u94c1\u4f20\u7532\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u638c\u5fc3\u5df2\u4e0d\u7981\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u8bf4\u7684\u8bdd\u867d\u7b80\u5355\uff0c\u5176\u4e2d\u5374\u5305\u542b\u7740\u6700\u9ad8\u6df1\u7684\u54f2\u7406\uff0c\u94c1\u4f20\u7532\u5ffd\u7136\u53d1\u73b0\u4ed6\u6709\u65f6\u867d\u663e\u5f97\u4e0d\u5927 \u61c2\u4e8b\uff0c\u4f46\u601d\u60f3\u4e4b\u5c16\u9510\uff0c\u5934\u8111\u4e4b\u6e05\u695a\uff0c\u51e0\u4e4e\u8fde\u674e\u5bfb\u6b22\u4e5f\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u5bf9\u4e00\u4e9b\u4e16\u4fd7\u7684\u5c0f\u4e8b\uff0c\u4ed6\u4e5f\u4e00 \u7a8d\u4e0d\u901a\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u5c51\u53bb\u6ce8\u610f\u90a3\u4e9b\u4e8b\u3002\u963f\u98de\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4eba\u751f\u4e0b\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u6d3b\u7740\uff0c\u6ca1\u6709 \u4eba\u6709\u6743\u81ea\u5df2\u53bb\u9001\u6b7b\uff01\u201d \u3000\u3000\u94c1\u4f20\u7532\u6ee1\u5934\u5927\u6c57\u6d94\u6d94\u800c\u843d\uff0c\u62ac\u8d77\u5934\u9053\uff1a\u201c\u6211\u9519\u4e86\uff0c\u6211\u9519\u4e86\u2014\u2014\uff0d\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u50cf\u662f\u4e0b\u4e86\u5f88\u5927\u7684\u51b3\u5fc3\uff0c\u62ac\u8d77\u5934\u9053\uff1a\u201c\u6211\u4e0d\u613f\u8bf4\u51fa\u90a3\u4ef6\u4e8b\u5176\u4e2d\u7684\u66f2\u6298\uff0c\u53ea\u56e0\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u4fe1\u4efb\u4f60\uff0c\u4f60\u7528\u4e0d\u7740\u5411\u6211\u89e3\u91ca\u3002\u201d \u3000\u3000\u94c1\u4f20\u7532\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f46\u4f60\u53c8\u600e\u80fd\u65ad\u5b9a\u6211\u4e0d\u662f\u5356\u53cb\u6c42\u8363\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u963f\u98de\u6de1\u6de1\u9053\uff1a\u201c\u201c\u6211\u4e0d\u4f1a\u770b\u9519\u7684\u3002\u201d \u3000\u3000\u4ed6\u773c\u775b\u95ea\u7740\u5149\uff0c\u5145\u6ee1\u4e86\u81ea\u4fe1\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u56e0\u4e3a\u6211\u662f\u5728\u539f\u91ce\u4e2d\u957f\u5927\u7684\uff0c\u5728\u539f\u91ce\u4e2d \u957f\u5927\u7684\u4eba\uff0c\u90fd\u4f1a\u548c\u91ce\u517d\u4e00\u6837\uff0c\u5929\u751f\u5c31\u6709\u4e00\u79cd\u5206\u8fa8\u5584\u6076\u7684\u672c\u80fd\u3002\u201d \u3000\u3000\u5728\u674e\u5bfb\u6b22\u7684\u611f\u89c9\u4e2d\uff0c\u5929\u4e0b\u82e5\u8fd8\u6709\u4ef6\u4e8b\u6bd4\u201c\u4e0d\u559d\u9152\u201d\u66f4\u96be\u53d7\uff0c\u90a3\u5c31\u662f\u201c\u548c\u8ba8\u538c\u7684\u4eba\u5728\u4e00\u8d77 \u559d\u9152\u201d\u3002 \u3000\u3000\u4ed6\u53d1\u73b0\u5728\u201c\u5174\u4e91\u5e84\u201d\u91cc\u7684\u4eba\uff0c\u5b9e\u5728\u4e00\u4e2a\u6bd4\u4e00\u4e2a\u8ba8\u538c\uff0c\u6bd4\u8d77\u6765\u6e38\u9f99\u751f\u8fd8\u662f\u57fa\u4e2d\u6700\u597d\u7684\u4e00\u4e2a \uff0c\u56e0\u4e3a\u4ed6\u591a\u5c11\u4e0d\u62cd\u9a6c\u5c41\u3002 \u3000\u3000\u8ba8\u538c\u7684\u4eba\u82e5\u53c8\u62cd\u9a6c\u5c41\uff0c\u90a3\u7b80\u76f4\u4ee4\u4eba\u6c57\u6bdb\u76f4\u7ad6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u88c5\u75c5\u3002 \u3000\u3000\u9f99\u5578\u4e91\u81ea\u7136\u5f88\u4e86\u89e3\u4ed6\u7684\u813e\u6c14\uff0c\u5e76\u6ca1\u6709\u52c9\u5f3a\u4ed6\uff0c\u4e8e\u662f\u674e\u5bfb\u6b22\u5c31\u4e00\u4e2a\u4eba\u8eba\u5728\u5e8a\u4e0a\uff0c\u9759\u9759\u5730\u7b49 \u7740\u5929\u9ed1\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4eca\u5929\u665a\u4e0a\u4e00\u5b9a\u4e5f\u4f1a\u53d1\u751f\u5f88\u591a\u6709\u8da3\u7684\u4e8b\u3002 \u3000\u3000\u98ce\u5439\u7af9\u53f6\u5982\u8f7b\u6d9b\u62cd\u5cb8\u3002 \u3000\u3000\u5c4b\u9876\u4e0a\u6709\u4e2a\u8718\u86db\u6b63\u5f00\u59cb\u7ed3\u7f51\uff0c\u4eba\u5c82\u975e\u4e5f\u548c\u8718\u86db\u4e00\u6837\uff1f\u4e16\u4e0a\u6bcf\u4e2a\u4eba\u90fd\u5728\u7ed3\u7f51\uff0c\u7136\u540e\u5c06\u81ea\u5df2 \u7f51\u5728\u4e2d\u592e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6709\u4ed6\u7684\u7f51\uff0c\u4ed6\u8fd9\u4e00\u751f\u5374\u518d\u4e5f\u4f11\u60f3\u81ea\u7f51\u4e2d\u9003\u51fa\u6765\uff0c\u56e0\u4e3a\u8fd9\u7f51\u672c\u6765\u5c31\u662f\u4ed6\u81ea\u5df2\u7ed3\u7684 \u3002 \u3000\u3000\u60f3\u5230\u4eca\u5929\u665a\u4e0a\u548c\u6797\u4ed9\u513f\u7684\u7ea6\u4f1a\uff0c\u4ed6\u773c\u6674\u91cc\u4e0d\u7981\u95ea\u51fa\u4e86\u5149\uff0c\u4f46\u60f3\u8d77\u94c1\u4f20\u7532\uff0c\u4ed6\u76ee\u5149\u53c8\u4e0d\u7981 \u9eef\u6de1\u4e0b\u6765\u3002 \u3000\u3000\u5929\u7ec8\u4e8e\u9ed1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u521a\u5750\u8d77\u3002\u5ffd\u7136\u542c\u5230\u96ea\u5730\u4e0a\u6709\u4e00\u9635\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u5411\u8fd9\u8fb9\u8d70\u4e86\u8fc7\u6765\uff0c\u4e8e\u662f\u4ed6\u7acb\u523b\u53c8\u8eba \u4e0b\u3002 \u3000\u3000\u4ed6\u521a\u8eba\u4e0b\uff0c\u811a\u6b65\u58f0\u5df2\u5230\u4e86\u7a97\u5916\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u8010\u7740\uff0c\u6ca1\u6709\u95ee\u4ed6\u662f\u8c01\uff0c\u8fd9\u4eba\u5c45\u7136\u4e5f\u4e0d\u8fdb\u6765\uff0c\u663e\u7136\u6765\u7684\u7edd\u4e0d\u662f\u9f99\u5578\u4e91\uff0c\u82e5\u662f\u9f99\u5578 \u4e91\u5c31\u7edd\u4e0d\u4f1a\u5728\u7a97\u5916\u9021\u5de1\u3002 \u3000\u3000\u90a3\u4e48\u6765\u7684\u662f\u8c01\u5462\uff1f \u3000\u3000\u8bd7\u97f3\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u70ed\u8840\u4e00\u4e0b\u4e86\u5168\u90fd\u51b2\u4e0a\u4e86\u5934\u9876\uff0c\u5168\u8eab\u90fd\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u53d1\u8d77\u6296\u6765\uff0c\u4f46\u8fd9\u65f6\u7a97\u5916\u5df2\u6709\u4eba \u5728\u8f7b\u8f7b\u54b3\u55fd\u3002 \u3000\u3000\u63a5\u7740\u4e00\u4eba\u9053\uff1a\u201c\u674e\u5144\u7761\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u662f\u201c\u85cf\u5251\u5c71\u5e84\u201d\u6e38\u5c11\u5e84\u4e3b\u7684\u58f0\u97f3\u3002\u674e\u5bfb\u6b22\u957f\u79f0\u677e\u4e86\u53e3\u6c14\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u6109\u5feb\uff0c\u8fd8\u662f\u5931\u671b \u3002 \u3000\u3000\u4ed6\u62d6\u7740\u978b\u5b50\u4e0b\u5e8a\uff0c\u62c9\u5f00\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u7a00\u5ba2\u7a00\u5ba2\uff0c\u8bf7\u8fdb\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u6e38\u9f99\u751f\u8d70\u8fdb\u6765\u5750\u4e0b\uff0c\u773c\u775b\u5374\u4e00\u76f4\u6ca1\u6709\u5411\u674e\u5bfb\u6b22\u77a7\u4e00\u773c\uff0c\u674e\u5bfb\u6b22\u71c3\u8d77\u706f\uff0c\u53d1\u73b0\u4ed6\u8138\u8272\u5728\u706f \u5149\u4e0b\u770b\u6765\u6709\u4e9b\u53d1\u9752\u3002 \u3000\u3000\u8138\u8272\u53d1\u9752\u7684\u4eba\uff0c\u5fc3\u91cc\u7edd\u4e0d\u4f1a\u6709\u597d\u610f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u559d\u8336\uff1f\u8fd8\u662f\u559d\u9152\uff1f\u201d \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1f\u201c\u597d\uff0c\u6211\u5c4b\u91cc\u672c\u5c31\u4ece\u6765\u6ca1\u6709\u559d\u8336\u7684\u4eba\u3002\u201d\uff1f \u3000\u3000\u6e38\u9f99\u751f\u8fde\u559d\u4e86\u4e09\u676f\uff0c\u5ffd\u7136\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4f55\u8981\u559d\u9152\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u9152\u79f0\u9493\u8bd7\u94a9\u53c8\u79f0\u626b\u6101\u5e1a\uff0c\u4f46\u6e38\u9f99\u751f\u65e2\u65e0\u6101\u53ef\u626b\uff0c\u60f3\u5fc5\u4e5f\u65e0\u8bd7\u53ef\u9493\uff0c\u559d\u9152 \u83ab\u975e\u662f\u4e3a\u4e86\u58ee\u80c6\u4e48\uff1f \u3000\u3000\u6e38\u9f99\u751f\u77aa\u7740\u4ed6\uff0c\u5ffd\u7136\u4ef0\u9762\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u545b\u5577\u4e00\u58f0\uff0c\u4ed6\u5df2\u62d4\u51fa\u4e86\u8170\u7554\u7684\u5251\u3002 \u3000\u3000\u5251\u5149\u5982\u4e00\u6cd3\u79cb\u6c34\u3002 \u3000\u3000\u6e38\u9f99\u751f\u7a81\u7136\u987f\u4f4f\u7b11\u58f0\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u53ef\u8ba4\u5f97\u8fd9\u67c4\u5251\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7528\u4ed6\u7ea4\u957f\u7684\u624b\u6307\uff0c\u8f7b\u8f7b\u629a\u6478\u7740\u5251\u80cc\uff0c\u5583\u5583\u9053\uff1a\u597d\u5251\uff01\u597d\u5251\uff01 \u3000\u3000\u4ed6\u4f3c\u4e4e\u7981\u4e0d\u5f97\u8fd9\u903c\u4eba\u7684\u5251\u6c14\uff0c\u53c8\u4e0d\u4f4f\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u6e38\u9f99\u751f\u76ee\u5149\u95ea\u52a8\uff0c\u6c89\u58f0\u9053\uff1a\u674e\u5144\u65e2\u7136\u4e5f\u662f\u4e2a\u7231\u5251\u7684\u4eba\uff0c\u60f3\u5fc5\u77e5\u9053\u8fd9\u67c4\u5251\u867d\u7136\u6bd4\u4e0d\u4e0a\u9c7c\u80a0 \u5251\u4e0a\u53e4\u795e\u5175\uff0c\u4f46\u5728\u6b66\u6797\u4e2d\u7684\u540d\u6c14\uff0c\u5374\u7edd\u4e0d\u5728\u9c7c\u80a0\u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u8d77\u773c\u775b\uff0c\u60a0\u7136\u9053\uff1a\u4e13\u8bf8\u9c7c\u80a0\uff0c\u6b66\u4e88\u593a\u60c5\uff0c\u4eba\u4ee5\u5251\u540d\uff0c\u5251\u56e0\u4eba\u4f20\uff0c\u4eba\u5251\u8f89\u6620\uff0c\u6c14 \u51b2\u6597\u725b\u3002 \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u662f\u4e09\u767e\u5e74\u524d\uff0c\u4e00\u4ee3\u5251\u8c6a\u72c4\u6b66\u5b50\u7684\u593a\u60c5\u5251\uff01\u4f46\u6709\u5173\u8fd9\u67c4\u5251\u7684\u638c\u6545\uff0c\u674e \u5144\u4e5f\u8bb8\u8fd8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8bf7\u6559\uff01 \u3000\u3000\u6e38\u9f99\u751f\u76ee\u5149\u51dd\u6ce8\u7740\u5251\u950b\uff0c\u7f13\u7f13\u9053\uff1a\u72c4\u6b66\u5b50\u7231\u5251\u6210\u75f4\uff0c\u5b64\xd7\u7edd\u4e16\uff0c\u76f4\u5230\u4e2d\u5e74\u65f6\uff0c\u624d\u7231\u4e0a\u4e00 \u4f4d\u5973\u58eb\uff0c\u4e24\u4eba\u672c\u6765\u5df2\u6709\u5a5a\u7ea6\uff0c\u8c01\u77e5\u8fd9\u4f4d\u59d1\u5a18\u5374\u5728\u4ed6\u4eec\u6210\u4eb2\u7684\u524d\u5915\uff0c\u548c\u4ed6\u7684\u597d\u53cb\u795e\u5200\u5f6d\u743c\u5728\u6697 \u4e2d\u7ea6\u4f1a\uff0c\u72c4\u6b66\u5b50\u4f24\u5fc3\u6c14\u6124\u4e4b\u4e0b\uff0c\u5c31\u7528\u593a\u60c5\u5251\u6740\u4e86\u5f6d\u743c\uff0c\u4ece\u6b64\u4ee5\u5251\u4e3a\u4f34\uff0c\u4ee5\u5251\u4e3a\u547d\uff0c\u518d\u4e5f\u4e0d\u8c08 \u5a5a\u5a36\u4e4b\u4e8b\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u62ac\u8d77\u5934\uff0c\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u674e\u5144\u4e5f\u8bb8\u4f1a\u89c9\u5f97\u8fd9\u6545\u4e8b\u60c5\u8282\u7b80\u5355\uff0c\u6beb\u65e0\u66f2\u6298\uff0c\u542c\u6765 \u672a\u514d\u6709\u4e9b\u7d22\u7136\u65e0\u5473\uff0c\u4f46\u8fd9\u5374\u662f\u771f\u4eba\u5b9e\u4e8b\uff0c\u7edd\u65e0\u534a\u5206\u865a\u5047\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u53ea\u89c9\u5f97\u8fd9\u4f4d\u72c4\u6b66\u5b50\u5251\u6cd5\u867d\u9ad8\uff0c\u4eba\u5374\u672a\u514d\u592a\u5c0f\u6c14\u4e86\u4e9b\uff0c\u5c82\u4e0d\u95ee\uff0c\u670b \u53cb\u5982\u624b\u8db3\uff0c\u59bb\u5b50\u5982\u8863\u5c65\uff0c\u5802\u5802\u7684\u7537\u5b50\u6c49\uff0c\u5c82\u53ef\u4e3a\u4e86\u513f\u5973\u4e4b\u60c5\uff0c\u5c31\u4f24\u4e86\u670b\u53cb\u4e4b\u4e49\uff01 \u3000\u3000\u6e38\u9f99\u751f\u51b7\u7b11\u9053\uff1a\u4f46\u6211\u5374\u89c9\u5f97\u8fd9\u4f4d\u72c4\u6b66\u5b50\u524d\u8f88\u5b9e\u5728\u53ef\u79f0\u662f\u9876\u5929\u7acb\u5730\u7684\u5927\u82f1\u96c4\uff0c\u4e5f\u552f\u6709\u8fd9\u6837 \u7684\u82f1\u96c4\uff0c\u7528\u60c5\u624d\u4f1a\u5982\u6b64\u4e4b\u6df1\uff0c\u5982\u6b64\u4e4b\u4e13\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u9601\u4e0b\u4eca\u591c\u83ab\u975e\u4e5f\u60f3\u5b66\u5b66\u4e09\u767e\u5e74\u524d\u7684\u72c4\u6b66\u5b50\u4e48\uff1f \u3000\u3000\u6e38\u9f99\u751f\u76ee\u4e2d\u9661\u7136\u5c04\u51fa\u4e86\u5bd2\u5149\uff0c\u51b7\u51b7\u9053\uff1a\u8fd9\u5c31\u8981\u770b\u674e\u5144\u662f\u5426\u8981\u5b66\u4e09\u767e\u5e74\u524d\u7684\u5f6d\u795e\u5200\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6708\u4e0a\u6885\u68a2\uff0c\u4f73\u4eba\u6709\u7ea6\uff0c\u8fd9\u98ce\u5149\u662f\u4f55\u7b49\u7eee\u4e3d\uff0c\u9601\u4e0b\u53c8\u4f55\u82e6\u716e\u9e21\u711a\u60c5 \uff0c\u5927\u715e\u98ce\u666f\u5462\uff1f \u3000\u3000\u6e38\u9f99\u751f\u5389\u58f0\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u9601\u4e0b\u4eca\u591c\u662f\u975e\u53bb\u4e0d\u53ef\u7684\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u82e5\u662f\u8bf7\u6797\u59d1\u5a18\u90a3\u6837\u7684\u4f73\u4eba\u7a7a\u5019\u6708\u4e0b\uff0c\u5728\u4e0b\u5c82\u975e\u6210\u4e86\u98ce\u6d41\u7f6a\u4eba\u3002 \u3000\u3000\u6e38\u9f99\u751f\u82cd\u767d\u7684\u8138\u7a81\u7136\u6da8\u5f97\u901a\u7ea2\uff0c\u6ee1\u5934\u9752\u7b4b\u90fd\u66b4\u9732\u4e86\u51fa\u6765\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u54e7\u7684\u81ea\u674e\u5bfb\u6b22\u7684\u8116 \u5b50\u65c1\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u4ecd\u7136\u9762\u5e26\u5fae\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u4ee5\u9601\u4e0b\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u8981\u5b66\u72c4\u6b66\u5b50\u53ea\u6015\u8fd8\u5acc\u5dee\u4e86\u4e9b\u3002 \u3000\u3000\u6e38\u9f99\u751f\u6012\u9053\uff1a\u5c31\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u8981\u6740\u4f60\u5374\u5df2\u662f\u7ef0\u7ef0\u6709\u4f59\u4e86\u3002 \u3000\u3000\u559d\u58f0\u4e2d\u4ed6\u5df2\u53c8\u523a\u51fa\u5341\u4f59\u5251\uff01 \u3000\u3000\u53ea\u542c\u5251\u98ce\u7834\u7a7a\u4e4b\u58f0\uff0c\u53c8\u6025\u53c8\u54cd\uff0c\u684c\u4e0a\u7684\u9152\u58f6\u7adf\u556a\u7684\u88ab\u5251\u98ce\u9707\u7834\u4e86\uff0c\u58f6\u91cc\u7684\u9152\u6d41\u5230\u684c\u4e0a\uff0c \u53c8\u6d41\u4e0b\u4e86\u5730\u3002\u8fd9\u5341\u4f59\u5251\u5b9e\u662f\u4e00\u5251\u5feb\u8fc7\u4e00\u5251\uff0c\u4f46\u674e\u5bfb\u6b22\u5374\u53ea\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u4eff\u4f5b\u8fde\u52a8\u4e5f\u6ca1\u6709\u52a8\uff0c \u8fd9\u5341\u4f59\u5251\u4e5f\u4e0d\u77e5\u600e\u5730\u5168\u90fd\u523a\u7a7a\u4e86\u3002 \u3000\u3000\u6e38\u9f99\u751f\u54ac\u4e86\u54ac\u7259\uff0c\u51fa\u5251\u66f4\u6025\u3002 \u3000\u3000\u4ed6\u89c1\u5230\u674e\u5bfb\u6b22\u53cc\u624b\u7a7a\u7a7a\uff0c\u662f\u4ee5\u60f3\u4ee5\u6025\u9510\u7684\u5251\u6cd5\uff0c\u903c\u5f97\u674e\u5bfb\u6b22\u65e0\u6687\u62bd\u5200\u3002 \u3000\u3000\u4ed6\u6240\u754f\u60e7\u7684\u53ea\u4e0d\u8fc7\u662f\u5c0f\u674e\u98de\u5200\u800c\u5df2\u3002 \u3000\u3000\u8c01\u77e5\u674e\u5bfb\u6b22\u6839\u672c\u5c31\u6ca1\u6709\u52a8\u5200\u7684\u610f\u601d\uff0c\u7b49\u4ed6\u540e\u9762\u8fd9\u4e00\u8f6e\u6025\u653b\u53c8\u5168\u90fd\u523a\u7a7a\u4e86\u4e4b\u540e\uff0c\u674e\u5bfb\u6b22\u5ffd \u7136\u4e00\u7b11\uff0c\u9053\uff1a\u5e74\u7eaa\u8f7b\u8f7b\u7684\uff0c\u6709\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u5728\u4e00\u822c\u4eba\u8bf4\u6765\u5df2\u662f\u5f88\u96be\u5f97\u7684\u4e86\uff0c\u4f46\u4ee5\u4f60\u7684\u5bb6\u4e16\u548c \u5e08\u627f\u8bf4\u6765\uff0c\u82e5\u4ee5\u8fd9\u6837\u7684\u5251\u6cd5\u53bb\u95ef\u8361\u6c5f\u6e56\uff0c\u4e0d\u51fa\u4e09\u4e94\u5e74\uff0c\u4f60\u7236\u4eb2\u548c\u4f60\u5e08\u5085\u7684\u62db\u724c\u53ea\u6015\u5c31\u8981\u7838\u5728 \u4f60\u624b\u4e0a\u4e86\u3002 \u3000\u3000\u5728\u6f2b\u7a7a\u5251\u5f71\u4e4b\u4e2d\uff0c\u4ed6\u5c45\u7136\u8fd8\u80fd\u597d\u6574\u4ee5\u6687\u7684\u8bf4\u8bdd\uff0c\u6e38\u9f99\u751f\u53c8\u6025\u53c8\u6c14\uff0c\u600e\u5948\u5251\u950b\u504f\u504f\u6cbe\u4e0d\u5230 \u5bf9\u65b9\u8863\u8882\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4e00\u5251\u521a\u8981\u523a\u5411\u674e\u5bfb\u6b22\u54bd\u5589\uff0c\u4fbf\u53d1\u73b0\u674e\u5bfb\u6b22\u8eab\u5b50\u5728\u5411\u5de6\u8f6c\uff0c\u4ed6\u5251\u950b\u5f53\u7136\u7acb\u523b\u8ddf\u7740\u6539 \u5411\u5de6\uff0c\u8c01\u77e5\u674e\u5bfb\u6b22\u8eab\u5b50\u6839\u672c\u672a\u52a8\uff0c\u4ed6\u5251\u52bf\u518d\u53d8\uff0c\u8fd8\u662f\u843d\u7a7a\uff0c\u6240\u4ee5\u4ed6\u8fd9\u6570\u5341\u5251\u867d\u7136\u5251\u5251\u90fd\u662f\u5236 \u4eba\u6b7b\u547d\u7684\u6740\u624b\uff0c\u4f46\u5230\u4e86\u6700\u597d\u4e00\u5239\u90a3\u65f6\uff0c\u5374\u83ab\u540d\u5176\u5999\u7684\u5168\u90fd\u53d8\u6210\u4e86\u865a\u62db\u3002 \u3000\u3000\u6e38\u9f99\u751f\u54ac\u7d27\u7259\u5173\uff0c\u4e00\u5251\u5411\u674e\u5bfb\u6b22\u80f8\u819b\u523a\u51fa\uff0c\u6697\u9053\uff1a\u8fd9\u6b21\u65e0\u8bba\u4f60\u73a9\u4ec0\u4e48\u82b1\u6837\uff0c\u6211\u90fd\u4e0d\u4e0a\u4f60 \u7684\u5f53\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u674e\u5bfb\u6b22\u5de6\u80a9\u5fae\u52a8\uff0c\u8eab\u5b50\u4f3c\u5c06\u53f3\u65cb\u3002 \u3000\u3000\u8981\u77e5\u9ad8\u624b\u76f8\u4e89\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u89c2\u4eba\u4e8e\u5fae\uff0c\u654c\u672a\u52a8\uff0c\u6211\u5148\u52a8\uff0c\u654c\u5c06\u52a8\uff0c\u6211\u5df2\u52a8\uff0c\u6e38\u9f99\u751f\u662f\u540d \u5bb6\u4e4b\u5b50\uff0c\u81ea\u7136\u660e\u767d\u8fd9\u9053\u7406\uff0c\u773c\u795e\u4e4b\u5229\uff0c\u4ea6\u975e\u5e38\u4eba\u80fd\u53ca\u3002\u5bf9\u65b9\u7684\u52a8\u4f5c\u65e0\u8bba\u8f7b\u5fae\uff0c\u90fd\u7edd\u5bf9\u9003\u4e0d\u8fc7 \u4ed6\u773c\u91cc\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u5c31\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u6240\u4ee5\u624d\u4e0a\u4e86\u674e\u5bfb\u6b22\u7684\u5f53\uff0c\u7a7a\u81ea\u523a\u51fa\u6570\u5341\u5251\u865a\u62db\uff0c\u6240\u4ee5\u8fd9\u6b21\u4ed6\u62ff \u5b9a\u4e3b\u610f\uff0c\u674e\u5bfb\u6b22\u65e0\u8bba\u600e\u4e48\u6837\u52a8\uff0c\u4ed6\u5168\u90fd\u89c6\u800c\u4e0d\u89c1\uff0c\u8fd9\u4e00\u5251\u7edd\u4e0d\u518d\u4e2d\u9014\u53d8\u62db\uff0c\u95ea\u7535\u7684\u76f4\u523a\u674e\u5bfb \u6b22\u80f8\u819b\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u6b21\u674e\u5bfb\u6b22\u8eab\u4e0a\u7adf\u771f\u7684\u5411\u53f3\u4e00\u8f6c\uff0c\u6e38\u9f99\u751f\u7684\u5251\u4fbf\u64e6\u7740\u674e\u5bfb\u6b22\u7684\u80f8\u819b\u523a\u4e86\u8fc7\u53bb\uff0c\u53c8\u523a \u7a7a\u4e86\u3002 \u3000\u3000\u7b49\u4ed6\u53d1\u89c9\u62db\u5df2\u7528\u8001\uff0c\u518d\u60f3\u53d8\u62db\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u53ea\u542c\u545b\u7684\u4e00\u58f0\u9f99\u541f\uff0c\u674e\u5bfb\u6b22\u957f\u800c\u6709\u529b\u7684\u624b\u6307 \u5728\u4ed6\u5251\u810a\u4e0a\u8f7b\u8f7b\u4e00\u5f39\uff01 \u3000\u3000\u6e38\u9f99\u751f\u53ea\u89c9\u864e\u53e3\u4e00\u9707\uff0c\u534a\u8fb9\u8eab\u5b50\u90fd\u53d1\u4e86\u9ebb\uff0c\u638c\u4e2d\u5251\u518d\u4e5f\u628a\u6301\u4e0d\u4f4f\uff0c\u9f99\u541f\u4e4b\u58f0\u672a\u7edd\uff0c\u957f\u5251 \u5df2\u95ea\u7535\u822c\u7a7f\u7a97\u800c\u51fa\uff01\u7a7f\u5165\u7af9\u6797\uff0c\u5728\u591c\u8272\u4e2d\u4e00\u95ea\u5c31\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u4e24\u53ea\u811a\u6839\u672c\u672a\u66fe\u79fb\u52a8\u8fc7\u534a\u6b65\u3002 \u3000\u3000\u6e38\u9f99\u751f\u4f46\u89c9\u5168\u8eab\u70ed\u8840\u4e00\u4e0b\u5b50\u5168\u51b2\u4e0a\u5934\u9876\uff0c\u4e00\u4e0b\u5b50\u53c8\u5168\u90fd\u843d\u4e86\u4e0b\u53bb\uff0c\u76f4\u843d\u5230\u811a\u5e95\uff0c\u4ed6\u5168\u8eab \u90fd\u53d1\u8d77\u51b7\u6765\u3002\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\uff0c\u6de1\u6de1\u9053\uff1a\u593a\u60c5\u5251\u975e\u51e1\u54c1\uff0c\u5feb\u53bb\u6361\u56de\u6765\u5427\u3002 \u3000\u3000\u6e38\u9f99\u751f\u8dfa\u4e86\u8dfa\u811a\uff0c\u8f6c\u8eab\u51b2\u51fa\uff0c\u51b2\u5230\u95e8\u53e3\uff0c\u53c8\u505c\u4e0b\u811a\u6b65\uff0c\u98a4\u58f0\u9053\uff1a\u4f60\u2014\u2014\u4f60\u82e5\u6709\u79cd\uff0c\u5c31\u7b49 \u6211\u4e00\u5e74\uff0c\u4e00\u5e74\u540e\u6211\u8a93\u590d\u6b64\u4ec7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e00\u5e74\uff1f\u4e00\u5e74\u53ea\u6015\u4e0d\u591f\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u4f60\u5929\u8d44\u672c\u4e0d\u9519\uff0c\u5251\u6cd5\u4e5f\u4e0d\u5f31\uff0c\u53ea\u53ef\u60dc\u5fc3\u6c14\u592a\u6d6e\uff0c\u662f\u4ee5\u51fa\u5251\u4e71\u800c\u4e0d\u7eaf\uff0c\u6025 \u800c\u4e0d\u5389\uff0c\u800c\u4e14\u592a\u8e81\u8fdb\u6c42\u529f\uff0c\u662f\u4ee5\u4e00\u65e6\u9047\u7740\u6bd4\u4f60\u5f3a\u7684\u5bf9\u624b\uff0c\u4f60\u81ea\u5df1\u5148\u5c31\u4e71\u4e86\uff0c\u5176\u5b9e\u4f60\u82e5\u6c89\u5f97\u4f4f \u6c14\uff0c\u4eca\u65e5\u4e5f\u672a\u5fc5\u4e0d\u80fd\u4f24\u6211\u3002\u6e38\u9f99\u751f\u773c\u775b\u4e00\u4eae\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u674e\u5bfb\u6b22\u5374\u53c8\u5df2\u63a5\u7740\u9053\uff1a\u4f46\u8fd9\u6c89\u5f97\u4f4f \u6c14\u56db\u4e2a\u5b57\uff0c\u8bf4\u6765\u4e0d\u96be\uff0c\u505a\u6765\u5374\u8c08\u4f55\u5bb9\u6613\uff0c\u6240\u4ee5\u4f60\u82e5\u60f3\u80dc\u6211\uff0c\u81f3\u5c11\u8981\u5148\u82e6\u7ec3\u4e03\u5e74\u7ec3\u6c14\u7684\u529f\u592b\uff01 \u3000\u3000\u6e38\u9f99\u751f\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u62f3\u767d\u634f\u5f97\u683c\u683c\u76f4\u54cd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u7b11\u9053\uff1a\u4f60\u53bb\u5427\uff0c\u53ea\u5de5\u6211\u80fd\u518d\u6d3b\u4e03\u5e74\uff0c\u53ea\u7ba1\u6765\u627e\u6211\u590d\u4ec7\u5c31\u662f\uff0c\u4e03\u5e74\u5e76\u4e0d\u7b97\u957f\uff0c\u4f55 \u51b5\u541b\u5b50\u590d\u4ec7\uff0c\u5341\u5e74\u4e5f\u4e0d\u7b97\u665a\u3002 \u3000\u3000\u5929\u5730\u53c8\u6062\u590d\u4e86\u9759\u5bc2\uff0c\u7af9\u6d9b\u4ecd\u5e26\u7740\u5e7d\u9999\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u7a97\u5916\u7684\u591c\u8272\uff0c\u9759\u9759\u7684\u4f2b\u7acb\u4e86\u8bb8\u4e45\uff0c\u53f9\u606f\u7740\u5583\u5583\u9053\uff1a\u5c11\u5e74\u4eba\uff0c\u4f60\u4e0d\u5fc5\u6068\u6211\uff0c\u5176 \u5b9e\u6211\u8fd9\u662f\u6551\u4e86\u4f60\uff0c\u4f60\u82e5\u518d\u548c\u6797\u4ed9\u513f\u7ea0\u7f20\u4e0b\u53bb\uff0c\u8fd9\u4e00\u751f\u53ea\u6015\u5c31\u7b97\u5b8c\u4e86\u3002 \u3000\u3000\u4ed6\u62c2\u4e86\u62c2\u8863\u4e0a\u7684\u5c18\u571f\uff0c\u6b63\u8981\u5f80\u6211\u8d70\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6797\u4ed9\u513f\u73b0\u5728\u5fc5\u5b9a\u5df2\u5728\u7b49\u7740\u4ed6\uff0c\u800c\u4e14\u5fc5\u5b9a\u5df2\u51c6\u5907\u597d\u4e86\u9493\u94a9\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u4e1d\u6beb\u8f85\u60e7\uff0c \u53cd\u800c\u89c9\u5f97\u6709\u8da3\u3002 \u3000\u3000\u6e38\u9f99\u751f\u4e34\u8d70\u65f6\u5019\uff0c\u5df2\u6ca1\u6709\u4ed6\u5e73\u65f6\u90a3\u4e48\u9ad8\u50b2\uff0c\u90a3\u4e48\u51b7\u6f20\uff0c\u4ed6\u5ffd\u7136\u51b2\u52a8\u4e86\u8d77\u6765\uff0c\u5411\u674e\u5bfb\u6b22\u5636 \u58f0\u9053\uff1a\u4f60\u82e5\u771f\u7684\u559c\u6b22\u6797\u4ed9\u513f\uff0c\u8fdf\u65e9\u4f1a\u540e\u6094\u7684\uff0c\u5979\u65e9\u5df2\u662f\u6211\u7684\u4eba\u4e86\uff0c\u65e9\u5df2\u548c\u6211\u6709\u4e86\u2014\u2014\u6709\u4e86\uff0d \u2014\u2014\u4f60\u4f55\u82e6\u5b9a\u4f1a\u62fe\u6211\u7684\u7834\u9774\u5b50\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u6de1\u6de1\u7b11\u9053\uff1a\u65e7\u9774\u5b50\u7a7f\u8d77\u6765\uff0c\u603b\u6bd4\u65b0\u9774\u5b50\u8212\u670d\u5408\u811a\u7684\u3002 \u3000\u3000\u60f3\u8d77\u6e38\u9f99\u751f\u90a3\u65f6\u7684\u8868\u60c5\uff0c\u674e\u5bfb\u6b22\u5c31\u89c9\u5f97\u53c8\u53ef\u601c\uff0c\u53c8\u53ef\u7b11\u2014\u2014\u4f46\u6797\u4ed9\u513f\u771f\u662f\u4ed6\u8bf4\u7684\u90a3\u79cd\u5973 \u5b69\u5b50\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u8d70\u51fa\u95e8\uff0c\u5ffd\u7136\u53d1\u73b0\u6709\u706f\u5149\u7a7f\u6797\u800c\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u5c0f\xd7\uff0c\u63d0\u7740\u4e24\u76cf\u9752\u7eb1\u706f\u7b3c\uff0c\u6b63\u5728\u6084\u6084\u5730\u8bf4\uff0c\u5077\u5077\u5730\u7b11\uff0c\u4e00\u77a7\u89c1\u674e\u5bfb\u6b22\uff0c\u5c31\u8bf4\u4e5f \u4e0d\u8bf4\uff0c\u7b11\u4e5f\u4e0d\u7b11\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53cd\u800c\u5fae\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u662f\u6797\u59d1\u5a18\u8981\u4f60\u4eec\u6765\u63a5\u6211\u7684\uff1f \u3000\u3000\u5de6\u9762\u7684\u9752\u8863\u5c0f\xd7\u5e74\u7eaa\u8f83\u5927\uff0c\u8eab\u6750\u8f83\u9ad8\uff0c\u5782\u9996\u4f5c\u793c\u9053\uff1a\u662f\u592b\u4eba\u53eb\u6211\u4eec\u6765\u8bf7\u674e\u76f8\u516c\u53bb\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u5931\u58f0\u9053\uff1a\u592b\u4eba\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u7d27\u5f20\u8d77\u6765\uff0c\u8ffd\u95ee\u9053\uff1a\u662f\u54ea\u4f4d\u592b\u4eba\uff1f \u3000\u3000\u9752\u8863\u5c0f\xd7\u5fcd\u4e0d\u4f4f\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u4eec\u5e84\u53ea\u6709\u4e00\u4f4d\u592b\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6728\u7acb\u5728\u90a3\u91cc\uff0c\u795e\u601d\u4f3c\u5df2\u98de\u8d8a\u8fc7\u7af9\u6797\uff0c\u98de\u4e0a\u4e86\u90a3\u5c0f\u697c\u2014\u2014 \u3000\u3000\u5341\u7684\u524d\uff0c\u90a3\u5c0f\u697c\u662f\u4ed6\u5e38\u53bb\u7684\u5730\u65b9\uff0c\u4ed6\u8bb0\u5f97\u90a3\u5f20\u94fa\u7740\u5728\u7406\u77f3\u9762\u7684\u684c\u5b50\u4e0a\uff0c\u603b\u5df2\u6446\u597d\u4e86\u51e0\u6837 \u4ed6\u6700\u7231\u5403\u7684\u5c0f\u83dc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u832b\u7136\u8d70\u7740\uff0c\u731b\u62ac\u5934\uff0c\u53c8\u5df2\u5230\u4e86\u5c0f\u697c\u4e0b\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u706f\u5149\u5f88\u67d4\u548c\uff0c\u770b\u6765\u548c\u5341\u5e74\u524d\u6ca1\u6709\u4ec0\u4e48\u4e24\u6837\uff0c\u751a\u81f3\u8fde\u7a97\u6846\u4e0a\u7684\u79ef\u96ea\uff0c\u4e5f\u90fd\u548c\u5341\u5e74 \u524d\u540c\u6837\u6d01\u767d\u53ef\u7231\u3002 \u3000\u3000\u4f46\u5341\u5e74\u6bd5\u7adf\u5df2\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u6f2b\u957f\u7684\u5341\u5e74\u65f6\u5149\uff0c\u65e0\u8bba\u8c01\u4e5f\u8ffd\u4e0d\u56de\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8718\u8e87\u7740\uff0c\u5b9e\u5728\u6ca1\u6709\u52c7\u6c14\u8e0f\u4e0a\u8fd9\u5c0f\u697c\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u4e0d\u80fd\u4e0d\u4e0a\u53bb\u3002 \u3000\u3000\u65e0\u8bba\u5979\u662f\u4e3a\u4ec0\u4e48\u627e\u4ed6\uff0c\u4ed6\u90fd\u6ca1\u6709\u7406\u7531\u63a8\u5374\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u521a\u8e0f\u4e0a\u5c0f\u697c\uff0c\u5c31\u9aa4\u7136\u5446\u4f4f\u3002 \u3000\u3000\u6f2b\u957f\u7684\u5341\u5e74\uff0c\u4f3c\u4e4e\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\u5ffd\u7136\u6d88\u901d\uff0c\u4ed6\u4f3c\u5df2\u53c8\u56de\u5230\u5341\u5e74\u524d\uff0c\u671b\u7740\u90a3\u5782\u7740\u7684\u73e0\u5e18\uff0c \u4ed6\u7684\u5fc3\u5ffd\u7136\u6025\u4fc3\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u8df3\u5f97\u5c31\u50cf\u662f\u4e2a\u6b63\u5760\u5165\u521d\u604b\u7684\u5c11\u5e74\u2014\u2014\u5341\u5e74\u524d\u7684\u6e29\u67d4\u3001\u5341\u5e74\u524d\u7684 \u65e7\u68a6\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\uff0c\u518d\u60f3\u4e0b\u53bb\u4ed6\u975e\u4f46\u5bf9\u4e0d\u4f4f\u9f99\u5578\u4e91\uff0c\u4e5f\u5bf9\u4e0d\u4f4f\u81ea\u5df1\uff0c\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981 \u8f6c\u8eab\u9003\u8d70\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u73e0\u5e18\u5185\u5df2\u4f20\u51fa\u5979\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u8bf7\u5750\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u4ecd\u548c\u5341\u5e74\u524d\u540c\u6837\u67d4\u7f8e\uff0c\u4f46\u5374\u663e\u5f97\u90a3\u4e48\u751f\u758f\uff0c\u90a3\u4e48\u51b7\u6f20\uff0c\u82e5\u4e0d\u662f\u684c\u4e0a\u7684\u90a3\u51e0\u6837\u83dc\uff0c \u4ed6\u5b9e\u96be\u60f3\u5ff5\u5e18\u4e2d\u4eba\u5c31\u662f\u4ed6\u5341\u5e74\u524d\u7684\u65e7\u53cb\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u5750\u4e0b\u6765\uff0c\u9053\uff1a\u591a\u8c22\u3002 \u3000\u3000\u73e0\u5e18\u6380\u8d77\uff0c\u4e00\u4e2a\u4eba\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fde\u547c\u5438\u90fd\u51e0\u4e4e\u505c\u6b62\uff0c\u4f46\u8d70\u51fa\u6765\u7684\u5374\u662f\u90a3\u5b69\u5b50\uff0c\u4ed6\u8eab\u4e0a\u4ecd\u7a7f\u7740\u9c9c\u7ea2\u7684\u8863\u670d\uff0c\u8138\u8272\u5374 \u82cd\u767d\u5982\u7eb8\u3002 \u3000\u3000\u597d\u4ecd\u7559\u5728\u5e18\u540e\uff0c\u53ea\u662f\u6c89\u58f0\u9053\uff1a\u83ab\u8981\u5fd8\u8bb0\u5a18\u65b9\u624d\u5bf9\u4f60\u8bf4\u7684\u8bdd\uff0c\u5feb\u53bb\u5411\u674e\u5927\u53d4\u656c\u9152\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u662f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u4f3c\u5df2\u7ede\u4f4f\u4e86\uff0c\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\uff0c\u5c31\u7b97\u4ed6\u660e\u77e5\u81ea\u5df1\u7edd\u6ca1\u6709\u505a\u9519\uff0c\u6b64\u523b\u671b\u7740\u8fd9\u5b69 \u5b50\u82cd\u767d\u7684\u8138\uff0c\u5fc3\u91cc\u4ecd\u4e0d\u7981\u6709\u79cd\u72af\u7f6a\u7684\u611f\u89c9\u3002 \u3000\u3000\u8bd7\u97f3\uff0c\u8bd7\u97f3\uff0c\u4f60\u627e\u6211\u6765\uff0c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u8981\u5982\u6b64\u6298\u78e8\u6211\u3002 \u3000\u3000\u8fd9\u79cd\u9152\u4ed6\u600e\u4e48\u559d\u5f97\u4e0b\u53bb\uff0c\u53ef\u662f\u4ed6\u53c8\u600e\u80fd\u4e0d\u559d\uff1f \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u4f84\u513f\u4ee5\u540e\u867d\u5df2\u4e0d\u80fd\u7ec3\u6b66\u529f\uff0c\u4f46\u7537\u5b50\u6c49\u603b\u4e5f\u4e0d\u80fd\u7ec8\u751f\u6258\u5e87\u5728\u7236\u6bcd\u819d\u4e0b\uff0c\u4f46\u6c42\u674e \u5927\u53d4\u5ff5\u5728\u6614\u65e5\u4e4b\u60c5\uff0c\u4f20\u6388\u7ed9\u4f84\u513f\u4e00\u6837\u9632\u8eab\u4e4b\u9053\uff0c\u4e5f\u514d\u5f97\u4f84\u513f\u53d7\u5c0f\u4eba\u6b3a\u8d1f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u624b\u4f38\u51fa\u6765\uff0c\u6307\u5c16\u5df2\u631f\u7740\u67c4\u5c0f\u5200\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u5df2\u5728\u5e18\u540e\u9053\uff1a\u674e\u5927\u53d4\u4ece\u672a\u5c06\u98de\u5200\u4f20\u4eba\uff0c\u6709\u4e86\u8fd9\u67c4\u5200\uff0c\u4f60\u5c31\u6709\u4e86\u62a4\u8eab\u7b26\uff0c\u8fd8\u4e0d\u5feb\u591a \u8c22\u674e\u5927\u53d4\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u679c\u7136\xd7\u5012\u5728\u5730\uff0c\u9053\uff1a\u591a\u8c22\u674e\u5927\u53d4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u6697\u4e2d\u53bb\u53f9\u606f\u5fd6\u9053\uff1a\u6bcd\u4eb2\u7684\u7231\u5b50\u4e4b\u5fc3\uff0c\u5b9e\u662f\u65e0\u5fae\u4e0d\u81f3\uff0c\u4f46\u513f\u5b50\u5bf9\u6bcd\u4eb2\u53c8\u5982 \u4f55\u5462\uff1f\u2014\u2014\uff0d \u3000\u3000\u6c89\u95f7\uff0c\u95f7\u5f97\u4ee4\u4eba\u75db\u82e6\u3002 \u3000\u3000\u9752\u8863\u5c0f\xd7\u5df2\u5e26\u5173\u90a3\u5b69\u5b50\u8d70\u4e86\uff0c\u4f46\u6797\u8bd7\u97f3\u4ecd\u5728\u5e18\u540e\uff0c\u5374\u8fd8\u662f\u4e0d\u8ba9\u674e\u5bfb\u6b22\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u672c\u4e0d\u662f\u4e2a\u62d8\u8c28\u7684\u4eba\uff0c\u4f46\u5728\u8fd9\u91cc\uff0c\u4ed6\u5ffd\u7136\u89c9\u81ea\u5df1\u5df2\u53d8\u5f97\u50cf\u5177\u5446\u5b50\u822c\u624b\u8db3\u5931\u63aa\u3002 \u3000\u3000\u591c\u5df2\u6df1\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u662f\u4e0d\u662f\u8fd8\u662f\u7b49\u7740\u4ed6\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u5ffd\u7136\u9053\uff1a\u4f60\u6709\u4e8b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6ca1\u2014\u2014\uff0d\u6ca1\u6709\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9ed8\u7136\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u4e00\u5b9a\u89c1\u8fc7\u4e86\u4ed9\u513f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u89c1\u8fc7\u4e00\u4e24\u6b21\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u5979\u662f\u4e2a\u5f88\u53ef\u601c\u7684\u5973\u5b69\u5b50\uff0c\u8eab\u4e16\u5f88\u60b2\u82e6\uff0c\u4f60\u82e5\u5df2\u89c1\u8fc7\u5979\u7684\u7236\u4eb2\uff0c\u5c31\u53ef\u4ee5\u60f3\u89c1\u5979 \u7684\u4e0d\u5e78\u3002 \u3000\u3000\u55ef\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6709\u4e00\u5e74\u6211\u5230\u820d\u8eab\u5d16\u53bb\u8bb8\u613f\uff0c\u89c1\u5230\u5979\u6b63\u51c6\u5907\u820d\u8eab\u8df3\u5d16\uff0c\u6211\u5c31\u6551\u4e86\u5979\u2014\u2014\u4f60\u53ef\u77e5 \u9053\u5979\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c\u4e0d\u60dc\u8df3\u5d16\u820d\u8eab\u4e48\uff1f\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u5979\u662f\u4e3a\u4e86\u5979\u7236\u4eb2\u7684\u75c5\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u53ea\u6709\u53f9\u4e86\u53e3\u6c14\uff0c\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u5979\u4e0d\u4f46\u806a\u660e\u7f8e\u4e3d\uff0c\u800c\u4e14\u6781\u6709\u4e0a\u5fc3\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u7684\u51fa\u8eab\u592a\u4f4e\uff0c\u6240\u4ee5\u65e0\u8bba\u505a\u4ec0\u4e48 \u4e8b\u90fd\u5206\u5916\u52aa\u529b\uff0c\u603b\u6015\u522b\u4eba\u77a7\u4e0d\u8d77\u5979\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u5982\u4eca\u53ea\u6015\u518d\u4e5f\u4e0d\u4f1a\u6709\u4eba\u77a7\u4e0d\u8d77\u5979\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u8fd9\u4e5f\u662f\u5979\u81ea\u5df1\u594b\u6597\u5f97\u6765\u7684\uff0c\u53ea\u4e0d\u8fc7\u5979\u5e74\u7eaa\u6bd5\u7adf\u592a\u8f7b\uff0c\u5fc3\u80a0\u53c8\u592a\uff0c\u6211\u603b\u662f\u6015\u5979 \u4f1a\u4e0a\u522b\u4eba\u7684\u5f53\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u5fd6\u9053\uff1a\u5979\u4e0d\u8981\u522b\u4eba\u4e0a\u5979\u7684\u5f53\uff0c\u5df2\u7ecf\u8c22\u5929\u8c22\u5730\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6211\u53ea\u5e0c\u671b\u5979\u65e5\u540e\u80fd\u627e\u4e2a\u5f88\u597d\u7684\u5f52\u5bbf\uff0c\u83ab\u8981\u7cca\u91cc\u7cca\u6d82\u7684\u88ab\u4eba\u6b3a\u9a97\uff0c\u4f24\u5fc3\u75db\u82e6\u4e00 \u8f88\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u8981\u5bf9\u6211\u8bf4\u8fd9\u4e9b\u8bdd\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4f60\u8bf4\uff0c\u4f60\u96be\u9053\u4e0d\u660e\u767d\uff1f \u3000\u3000\u4ed6\u7684\u786e\u660e\u767d\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u5c06\u4ed6\u7559\u5728\u8fd9\u91cc\uff0c\u539f\u6765\u5c31\u662f\u4e0d\u613f\u4ed6\u53bb\u8d74\u6797\u4ed9\u513f\u7684\u7ea6\u4f1a\uff0c\u8fd9\u7ea6\u4f1a\u7684\u4e8b\uff0c\u81ea\u7136\u662f\u6e38\u9f99\u751f \u544a\u8bc9\u5979\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u7f13\u7f13\u9053\uff1a\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4eec\u603b\u662f\u591a\u5e74\u7684\u670b\u53cb\uff0c\u6211\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5728\u53d1\u75bc\uff0c\u5374\u5fae\u7b11\u9053\uff1a\u4f60\u8981\u6211\u83ab\u8981\u53bb\u627e\u6797\u4ed9\u513f\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u2014\u2014\u4f60\u4ee5\u4e3a\u6211\u770b\u4e0a\u4e86\u5979\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6211\u4e0d\u7ba1\u4f60\u5bf9\u5979\u600e\u6837\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\u7684\u8981\u6c42\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c06\u9762\u524d\u7684\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u5583\u5583\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u662f\u65e0\u836f\u53ef\u6551\u7684\u6d6a\u5b50\uff0c\u6211\u82e5\u53bb\u627e\u5979\uff0c\u5c31\u662f \u5bb3\u4e86\u5979\u2014\u2014 \u7b2c\u5341\u4e09\u7ae0\u3000\u65e0\u5984\u4e4b\u707e>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u65e0\u5984\u4e4b\u707e \u3000\u3000\u674e\u5bfb\u6b22\u542c\u4e86\u6797\u8bd7\u97f3\u7684\u8bdd\uff0c\u5c06\u9762\u524d\u7684\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u5583\u5583\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u662f\u4e2a\u65e0\u53ef\u6551\u836f\u7684\u6d6a\u5b50 \uff0c\u6211\u82e5\u53bb\u627e\u5979\uff0c\u5c31\u662f\u5bb3\u4e86\u5979\u2014\u2014 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4f60\u7b54\u5e94\u4e86\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u6211\u4e00\u5411\u90fd\u5f88\u559c\u6b22\u5bb3\u4eba\u4e48\uff1f \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4e00\u53ea\u624b\u4f38\u51fa\u6765\uff0c\u7d27\u7d27\u62c9\u7740\u73e0\u5e18\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u662f\u5982\u6b64\u6e29\u67d4\uff0c\u5982\u6b64\u7f8e\u4e3d\uff0c\u5374\u56e0\u63e1\u5f97\u592a\u7d27\uff0c\u767d\u7389\u822c\u7684\u624b\u80cc\u4e0a\u5c31\u73b0\u51fa\u4e86\u4e00\u6761\u6761\u6de1\u9752\u8272 \u7684\u7b4b\u7edc\uff0c\u73e0\u5e18\u65ad\u4e86\uff0c\u73e0\u5b50\u843d\u5728\u5730\u4e0a\uff0c\u4eff\u4f5b\u4e00\u4e32\u7434\u97f3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u8fd9\u53ea\u624b\uff0c\u7f13\u7f13\u7ad9\u8d77\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u544a\u8f9e\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u98a4\u58f0\u9053\uff1a\u4f60\u65e2\u5df2\u8d70\u4e86\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u56de\u6765\uff1f\u6211\u4eec\u672c\u6765\u751f\u6d3b\u5f97\u5f88\u5e73\u9759 \uff0c\u4f60\u2014\u2014\u4f60\u4e3a\u4ec0\u4e48\u53c8\u8981\u6765\u6405\u4e71\u6211\u4eec\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5634\u7d27\u95ed\u7740\uff0c\u4f46\u5634\u89d2\u7684\u808c\u8089\u5374\u5728\u4e0d\u505c\u7684\u62bd\u6410\u2014\u2014\uff0d \u3000\u3000\u6797\u8bd7\u97f3\u5ffd\u7136\u81ea\u5e18\u4e2d\u55c4\u58f0\u9053\uff1a\u4f60\u5bb3\u4e86\u6211\u7684\u5b69\u5b50\u8fd8\u4e0d\u591f\uff1f\u8fd8\u8981\u53bb\u5bb3\u5979\uff1f \u3000\u3000\u5979\u7684\u8138\u662f\u90a3\u4e48\u82cd\u767d\uff0c\u90a3\u4e48\u7f8e\u4e3d\u3002 \u3000\u3000\u5979\u773c\u6ce2\u4e2d\u5145\u6ee1\u4e86\u6fc0\u52a8\uff0c\u53c8\u5145\u6ee1\u4e86\u75db\u82e6\u3002 \u3000\u3000\u5979\u4ece\u4e25\u4e5f\u6ca1\u6709\u5728\u4efb\u4f55\u4eba\u9762\u524d\u5982\u6b64\u5931\u5e38\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u96be\u9053\u4e2d\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u6797\u4ed9\u513f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u56de\u5934\uff0c\u4e0d\u6562\u770b\u5979\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ed6\u6b64\u65f6\u82e5\u662f\u770b\u4e86\u5979\u4e00\u773c\uff0c\u6050\u6015\u5c31\u4f1a\u53d1\u751f\u4e00\u4e9b\u4ee4\u5f7c\u6b64\u90fd\u8981\u75db\u82e6\u7ec8\u751f\u7684\u4e8b\uff0c\u8fd9\u4ee4\u4ed6\u8fde \u60f3\u90fd\u4e0d\u6562\u53bb\u60f3\u2014\u2014 \u3000\u3000\u4ed6\u5f88\u5feb\u5730\u8d70\u4e0b\u697c\uff0c\u5374\u7f13\u7f13\u9053\uff1a\u5176\u5b9e\u4f60\u6839\u672c\u7528\u4e0d\u7740\u6c42\u6211\u7684\uff0c\u56e0\u4e3a\u6211\u6839\u4e00\u5c31\u6ca1\u6709\u770b\u4e0a\u8fc7\u5979\uff01 \u3000\u3000\u6797\u8bd7\u97f3\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u8eab\u5b50\u5ffd\u7136\u8f6f\u8f6f\u7684\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6c34\u6c60\u5df2\u7ed3\u4e86\u51bb\uff0c\u6731\u680f\u5c0f\u6865\u6a2a\u8de8\u5728\u6c34\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u75f4\u75f4\u5730\u5750\u5728\u5c0f\u6865\u7684\u77f3\u9636\u4e0a\uff0c\u75f4\u75f4\u5730\u671b\u7740\u7ed3\u4e86\u51b0\u7684\u8377\u5858\uff0c\u4ed6\u7684\u5fc3\uff0c\u4e5f\u6b63\u548c\u8fd9\u8377\u5858\u4e00 \u6837\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\uff0c\u53ef\u4ee5\u770b\u5230\u51b7\u9999\u5c0f\u7b51\u4e2d\u7684\u706f\u5149\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8fd8\u5728\u7b49\u7740\u4ed6\uff1f \u3000\u3000\u4ed6\u660e\u77e5\u6797\u4ed9\u513f\u4eca\u591c\u8981\u4ed6\u53bb\uff0c\u4e00\u5b9a\u6709\u5979\u7684\u7528\u610f\uff0c\u4ed6\u660e\u77e5\u81ea\u5df1\u53bb\u4e86\u540e\uff0c\u4e00\u5b9a\u4f1a\u53d1\u751f\u8bb8\u591a\u6781\u60ca \u4eba\uff0c\u4e5f\u6781\u6709\u8da3\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u5750\u5728\u8fd9\u91cc\uff0c\u8fdc\u8fdc\u671b\u7740\u90a3\u660f\u9ec4\u7684\u706f\u5149\u3002 \u3000\u3000\u4ed6\u53c8\u4e0d\u505c\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u51b7\u9999\u5c0f\u7b51\u90a3\u8fb9\u6709\u4eba\u5f71\u4e00\u95ea\uff0c\u5411\u9ed1\u6697\u4e2d\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u4e5f\u98de\u8eab\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u4e4b\u5feb\uff0c\u65e0\u53ef\u5f62\u5bb9\uff0c\u4f46\u7b49\u4ed6\u8d76\u5230\u51b7\u9999\u5c0f\u7b51\u90a3\u8fb9\u53bb\u7684\u65f6\u5019\uff0c\u65b9\u624d\u7684\u4eba\u5f71\u65e9\u5df2\u77a7\u4e0d\u89c1\u4e86 \uff0c\u4f3c\u4e4e\u5df2\u88ab\u65e0\u8fb9\u7684\u9ed1\u6697\u541e\u6ca1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fdf\u7591\u7740\uff1a\u96be\u9053\u6211\u770b\u9519\u4e86\uff01 \u3000\u3000\u4f46\u53ea\u6709\u8fd9\u4e00\u53cc\u8db3\u5370\uff0c\u4ed6\u8fd8\u662f\u65e0\u6cd5\u5224\u65ad\u6b64\u4eba\u63a0\u53bb\u7684\u65b9\u5411\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u63a0\u4e0b\u5c4b\uff0c\u7a97\u5185\u706f\u5149\u4ecd\u4eae\u3002 \u3000\u3000\u4ed6\u5f39\u4e86\u5f39\u7a97\u5b50\uff0c\u8f7b\u5524\u9053\uff1a\u6797\u59d1\u5a18\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6ca1\u6709\u5e94\u58f0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u63a0\u548c\u7a97\uff0c\u5ffd\u7136\u53d1\u73b0\u4e94\u53cc\u9152\u676f\uff0c\u8fde\u5e95\u90fd\u5d4c\u5165\u684c\u9762\u91cc\uff0c\u9aa4\u7136\u671b\u53bb\uff0c\u8d6b\u7136\u4e00\u6735\u6885\u82b1\uff01 \u3000\u3000\u6885\u82b1\u76d7\uff01 \u3000\u3000\u6797\u4ed9\u513f\u96be\u9053\u5df2\u843d\u5165\u6885\u82b1\u76d7\u624b\u91cc\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u624b\u6309\u5728\u684c\u4e0a\uff0c\u529b\u900f\u638c\u5fc3\uff0c\u4e94\u53ea\u9152\u676f\u5c31\u5f39\u4e86\u8d77\u6765\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u624b\u91cc\u62ff\u7740\u9152\u676f\uff0c\u638c\u5fc3\u5df2\u4e0d\u89c9\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u54e7\u7684\u4e00\u58f0\uff0c\u684c\u4e0a\u7684\u706f\u5149\uff0c\u9996\u5148\u88ab\u6253\u706d\uff0c\u63a5\u7740\uff0c\u6025\u98ce\u6ee1\u5c4b\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11 \u6697\u5668\uff0c\u4ece\u56db\u767e\u516b\u65b9\u5411\u674e\u5bfb\u6b22\u6253\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f46\u666e\u5929\u4e4b\u4e0b\u7684\u6697\u5668\uff0c\u53c8\u6709\u90a3\u4e00\u6837\u80fd\u6bd4\u5f97\u4e0a\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u8eab\u5b50\u4e00\u8f6c\uff0c\u4e24\u53ea\u624b\u5df2\u63a5\u7740\u4e86\u5341\u4e03\u516b\u4ef6\u6697\u5668\uff0c\u4eba\u5df2\u8ddf\u7740\u98de\u8eab\u800c\u8d77\uff0c\u6ca1\u6709\u4ed6\u63a5\u4f4f\u7684\u6697 \u5668\uff0c\u5c31\u5168\u90fd\u81ea\u4ed6\u8db3\u5e95\u6253\u8fc7\u3002 \u3000\u3000\u5c4b\u5b50\u5916\u8fd9\u65f6\u624d\u54cd\u8d77\u4e86\u547c\u559d\xd7\u54a4\u58f0\uff01 \u3000\u3000\u6885\u82b1\u76d7\uff0c\u4f60\u5df2\u9003\u4e0d\u4e86\uff0c\u5feb\u51fa\u6765\u9001\u6b7b\u5427\uff01 \u3000\u3000\u5c31\u7b97\u4f60\u6709\u901a\u5929\u7684\u672c\u4e8b\uff0c\u6211\u4eec\u4eca\u65e5\u4e5f\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01 \u3000\u3000\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6d1b\u9633\u5e9c\u7684\u7530\u4e03\u7237\u4eca\u5929\u5df2\u8d76\u6765\u4e86\uff0c\u8fd8\u6709\u516c\u5b59\u5927\u4fa0\uff0c\u518d\u52a0\u4e0a\u8d75\u5927\u7237\uff0c\u9f99\u56db\u7237\uff0d \u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u6447\u4e86\u6447\u5934\uff0c\u82e6\u7b11\u6697\u9053\uff1a\u679c\u7136\u662f\u7530\u4e03\u5230\u4e86\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u4eba\u53c8\u9053\uff1a\u670b\u53cb\u65e2\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u4e3a\u4f55\u4e0d\u80af\u51fa\u6765\u76f8\u89c1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u7c97\u7740\u5589\u5499\u9053\uff1a\u5404\u4f4d\u65e2\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u4e3a\u4f55\u4e0d\u80af\u8fdb\u6765\u76f8\u89c1\uff1f \u3000\u3000\u5c4b\u5916\u53c8\u8d77\u4e86\u4e00\u9635\u60ca\u52a8\uff0c\u7eb7\u7eb7\u9053\uff1a\u8fd9\u5c0f\u5b50\u662f\u60f3\u8bf1\u6211\u4eec\u5165\u5c4b\u3002 \u3000\u3000\u8fd9\u65f6\u53c8\u6709\u4e00\u4eba\u7684\u8bed\u58f0\u54cd\u8d77\uff0c\u5c06\u522b\u4eba\u7684\u58f0\u97f3\u5168\u90fd\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u6e05\u4eae\u9ad8\u5197\uff0c\u6717\u58f0\u9053\uff1a\u6885\u82b1\u76d7\u672c\u6765\u5c31\u662f\u53ea\u4f1a\u5728\u6697\u4e2d\u5077\u9e21\u6478\u72d7\u4e4b\u8f88\uff0c\u90a3\u91cc\u6562\u89c1\u4eba\u3002 \u3000\u3000\u8bf7\u5c06\u4e0d\u5982\u6fc0\u5c06\uff0c\u5927\u5bb6\u7acb\u523b\u4e5f\u7eb7\u7eb7\u9a82\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6885\u82b1\u76d7\u786e\u662f\u6709\u4e9b\u9b3c\u9b3c\u795f\u795f\uff0c\u4f46\u548c\u6211\u53c8\u6709 \u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u90a3\u6e05\u6717\u7684\u8bed\u58f0\u9053\uff1a\u201c\u4f60\u662f\u6885\u82b1\u76d7\u662f\u8c01\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u4eba\u9053\uff1a\u201c\u516c\u5b59\u5927\u4fa0\u8fd8\u95ee\u4ed6\u5e72\u4ec0\u4e48\uff0c\u8d75\u5927\u7237\u7edd\u4e0d\u4f1a\u770b\u9519\u7684\uff0c\u6b64\u4eba\u5fc5\u662f\u6885\u82b1\u76d7\u65e0\u7591\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u653e\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8d75\u6b63\u4e49\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u8fd9\u90fd\u662f\u4f60\u73a9\u7684\u82b1\u6837\uff01\u201d\uff0e\u7b11\u58f0\u4e2d \uff0c\u4ed6\u8eab\u5f62\u5df2\u71d5\u5b50\u822c\u63a0\u51fa\u7a97\u6237\uff0c\u7a97\u5916\u7fa4\u8c6a\u6709\u7684\u4eba\u547c\u559d\u7740\u5411\u524d\u6251\uff0c\u6709\u7684\u4eba\u60ca\u53eb\u7740\u5f80\u540e\u9000\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5927\u547c\u9053\uff1a\u201c\u5404\u4f4d\u83ab\u52a8\u624b\uff0c\u8fd9\u662f\u6211\u7684\u5144\u5f1f\u674e\u5bfb\u6b22\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8eab\u5f62\u4e00\u8f6c\u5df2\u627e\u5230\u4e86\u8d75\u6b63\u4e49\uff0c\u63a0\u5230\u4ed6\u9762\u524d\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8d75\u5927\u7237\u4f60\u9ad8\u660e\u7684\u773c\u529b\uff0c\u82e5\u975e \u5728\u4e0b\u624b\u811a\u8fd8\u7b97\u7075\u4fbf\uff0c\u6b64\u523b\u5df2\u505a\u4e86\u6885\u82b1\u76d7\u7684\u66ff\u6b7b\u9b3c\u4e86\uff0c\u90a3\u6b7b\u5f97\u624d\u53eb\u51a4\u6789\u3002\u201d \u3000\u3000\u8d75\u6b63\u4e49\u8138\u8272\u94c1\u9752\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\uff0c\u4e00\u4e2a\u4eba\u9b3c\u9b3c\u5d07\u5d07\u5730\u8eb2\u5728\u8fd9\u91cc\uff0c\u6211\u4e0d\u5c06\u4ed6\u770b\u6210\u6885 \u82b1\u76d7\u5374\u5c06\u4ed6\u770b\u6210\u8c01\uff1f\u6211\u600e\u77e5\u9601\u4e0b\u7684\u75c5\u5ffd\u7136\u597d\u4e86\uff0c\u53c8\u5077\u5077\u6e9c\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u6211\u7528\u4e0d\u7740\u5077\u5077\u6e9c\u5230\u8fd9\u91cc\u6765\uff0c\u65e0\u8bba\u90a3\u91cc\u6211\u90fd\u53ef\u5149\u660e\u6b63\u5927\u5730\u8d70\u6765\u8d70\u53bb\uff0c\u4f55\u51b5 \uff0c\u8d75\u5927\u7237\u53c8\u600e\u77e5\u4e0d\u662f\u6b64\u95f4\u7684\u4e3b\u4eba\u7ea6\u6211\u6765\u7684\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u51b7\u7b11\u9053\uff1a\u6211\u5012\u4e0d\u77e5\u9053\u9601\u4e0b\u548c\u6797\u59d1\u5a18\u6709\u8fd9\u4efd\u4ea4\u60c5\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8c01\u90fd\u77e5\u9053\u6797\u59d1\u5a18\u4eca\u591c\u662f \u7edd\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u6797\u59d1\u5a18\u4e3a\u4e86\u8eb2\u907f\u6885\u82b1\u76d7\uff0c\u4eca\u5929\u4e0b\u5348\u5df2\u642c\u51fa\u4e86\u51b7\u9999\u5c0f\u7b51\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u7eb5\u7136\u5982\u6b64\uff0c\u9601\u4e0b\u5148\u95ee\u6e05\u695a\u4e86\u518d\u4e0b\u6bd2\u624b\u4e5f\u4e0d\u8fdf\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u5bf9\u4ed8\u6885\u82b1\u76d7\u8fd9\u79cd\u4eba\uff0c\u53ea\u6709\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u7b49\u95ee\u6e05\u695a\u518d\u51fa\u624b\u5c31\u8fdf\u4e86\u3002 \u3000\u3000\u4ed6\u53e5\u53e5\u8bdd\u90fd\u8bf4\u5f97\u5408\u60c5\u5408\u7406\uff0c\u65e0\u61c8\u53ef\u51fb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u597d\u4e2a\u5148\u4e0b\u624b\u4e3a\u5f3a\uff01\u5982\u6b64\u8bf4\u6765\uff0c\u674e\u9769\u4eca\u65e5\u82e5\u6b7b\u5728\u8d75\u5927\u7237\u624b\u4e0a\uff0c\u4e5f\u53ea\u80fd\u7b97\u6211 \u6d3b\u8be5\uff0c\u4e00\u70b9\u4e5f\u6028\u4e0d\u5f97\u8d75\u5927\u7237\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5e72\u54b3\u4e24\u58f0\uff0c\u8d54\u7b11\u9053\uff1a\u9ed1\u591c\u4e4b\u95f4\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u5076\u7136\u770b\u9519\u7684\uff0c\u4f55\u51b5\u2014\u2014 \u3000\u3000\u8d75\u6b63\u4e49\u5ffd\u53c8\u51b7\u51b7\u9053\uff1a\u4f55\u51b5\uff0c\u4e5f\u8bb8\u6211\u5e76\u6ca1\u6709\u770b\u9519\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6ca1\u6709\u770b\u9519\uff1f\u96be\u9053\u8d75\u5927\u7237\u8ba4\u4e3a\u674e\u67d0\u5c31\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u51b7\u7b11\u9053\uff1a\u90a3\u4e5f\u96be\u8bf4\u5f97\u5f88\uff0c\u5927\u5bb6\u53ea\u77e5\u9053\u6885\u82b1\u76d7\u8f7b\u529f\u5f88\u9ad8\uff0c\u51fa\u624b\u5f88\u5feb\uff0c\u81f3\u4e8e\u4ed6\u7a76\u7adf\u662f \u59d3\u5f20\uff0c\u8fd8\u662f\u59d3\u674e\uff1f\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u9053\uff1a\u4e0d\u9519\uff0c\u674e\u67d0\u8f7b\u529f\u65e2\u4e0d\u4f4e\uff0c\u51fa\u624b\u4e5f\u4e0d\u6162\uff0c\u6885\u82b1\u76d7\u91cd\u73b0\u6c5f\u6e56\uff0c\u4e5f\u6b63\u662f\u674e\u67d0\u518d \u5ea6\u5165\u5173\u7684\u65f6\u5019\uff0c\u674e\u5bfb\u6b22\u82e5\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u90a3\u624d\u662f\u602a\u4e8b\u4e00\u4ef6\u3002 \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u77aa\u7740\u8d75\u6b63\u4e49\u7f13\u7f13\u9053\uff1a\u4f46\u8d75\u5927\u7237\u65e2\u7136\u8ba4\u5b9a\u4e86\u674e\u67d0\u5c31\u662f\u6885\u82b1\u76d7\uff0c\u6b64\u523b\u4e3a\u4f55\u8fd8\u4e0d\u51fa \u624b\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u65e9\u4e9b\u51fa\u624b\uff0c\u8fdf\u4e9b\u51fa\u624b\u90fd\u65e0\u59a8\uff0c\u6709\u7530\u4e03\u7237\u548c\u6469\u4e91\u5144\u5728\u8fd9\u91cc\uff0c\u4eca\u65e5\u4f60\u8fd8\u60f3\u8d70\u5f97\u4e86 \u4e48\uff1f \u3000\u3000\u9f99\u5578\u4e91\u8138\u8272\u8fd9\u624d\u53d8\u4e86\uff0c\u5f3a\u7b11\u9053\uff1a\u5927\u5bb6\u53ea\u4e0d\u8fc7\u662f\u5728\u5f00\u73a9\u7b11\uff0c\u5343\u4e07\u4e0d\u53ef\u8ba4\u771f\uff0c\u9f99\u5578\u4e91\u6562\u4ee5\u81ea \u5bb6\u6027\u547d\u62c5\u4fdd\uff0c\u674e\u5bfb\u6b22\u7edd\u4e0d\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u65e0\u6b63\u4e49\u6c89\u7740\u8138\u9053\uff1a\u8fd9\u79cd\u4e8b\u81ea\u7136\u4e07\u4e07\u5f00\u4e0d\u5f97\u73a9\u7b11\u7684\uff0c\u4f60\u548c\u4ed6\u5df2\u6709\u5341\u5e74\u4e0d\u89c1\uff0c\u600e\u80fd\u4fdd\u8bc1\u4ed6\uff1f \u3000\u3000\u9f99\u5578\u4e91\u80c0\u7ea2\u4e86\u8138\uff0c\u9053\uff1a\u53ef\u662f\u2014\u2014\uff0d\u53ef\u662f\u6211\u6df1\u77e5\u4ed6\u7684\u4e3a\u4eba\u2014\u2014\uff0d \u3000\u3000\u4e00\u4eba\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u8fd9\u53e5\u8bdd\u9f99\u56db\u7237\u603b\u8be5\u542c\u8bf4\u8fc7\u5427\u3002 \u3000\u3000\u8fd9\u4eba\u7626\u5982\u7af9\u7aff\uff0c\u9762\u8272\u814a\u5171\uff0c\u770b\u8d77\u6765\u4eff\u4f5b\u662f\u4e2a\u75c5\u592b\uff0c\u4f46\u8bf4\u8d77\u8bdd\u6765\u5374\u662f\u8bed\u58f0\u6e05\u6717\uff0c\u6b63\u662f\u4ee5\u6469 \u4e91\u5341\u56db\u540d\u9707\u5929\u4e0b\u7684\u6469\u4e91\u624b\u516c\u5b59\u6469\u4e91\u3002 \u3000\u3000\u4ed6\u80cc\u540e\u4e00\u4eba\u59cb\u7ec8\u9762\u5e26\u7b11\u5bb9\uff0c\u80cc\u8d1f\u53cc\u624b\uff0c\u770b\u6765\u53c8\u4eff\u4f5b\u662f\u4e2a\u517b\u914b\u5904\u4f18\u7684\u5bcc\u5bb6\u7fc1\uff0c\u6b64\u523b\u5ffd\u7136\u54c8 \u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u7530\u4e03\u548c\u674e\u63a2\u82b1\u4e5f\u662f\u6570\u5341\u5e74\u7684\u4ea4\u60c5\uff0c\u4f46\u73b0\u5728\u65e2\u7136\u53d1\u751f\u4e86\u8fd9\u79cd\u4e8b\uff0c\u6211\u4e5f\u53ea \u597d\u5c06\u4ea4\u60c5\u6401\u5728\u4e00\u8fb9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u6211\u670b\u53cb\u867d\u4e0d\u5c11\uff0c\u4f46\u50cf\u7530\u4e03\u8fd9\u4e48\u6837\u6709\u8eab\u4efd\u7684\u670b\u53cb\u5374\u4e00\u4e2a\u4e5f\u6ca1\u6709\uff0c\u7530\u4e03\u4e5f\u7528 \u4e0d\u7740\u6211\u6500\u4ea4\u60c5\u3002 \u3000\u3000\u7530\u4e03\u8138\u8272\u4e00\u6c89\uff0c\u76ee\u4e2d\u7acb\u523b\u73b0\u51fa\u4e86\u6740\u673a\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u7530\u4e03\u7237\u7ffb\u8138\u65e0\u60c5\uff0c\u8138\u4e0a\u4e00\u77a7\u4e0d\u89c1\u7b11\u5bb9\uff0c\u7acb\u523b\u5c31\u8981\u51fa\u624b\u6740\u4eba\uff0c\u8c01\u77e5\u6b64\u756a \u4ed6\u975e\u4f46\u6ca1\u6709\u51fa\u624b\uff0c\u800c\u4e14\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u516c\u5b59\u6469\u4e91\u3001\u8d75\u6b63\u4e49\u3001\u7530\u4e03\uff0c\u4e09\u4e2a\u4eba\u5c06\u674e\u5bfb\u6b22\u56f4\u5728\u4e2d\u95f4\uff0c\u4e09\u4e2a\u4eba\u4ff1\u662f\u8138\u8272\u94c1\u9752\uff0c\u54ac\u7259 \u5207\u9f7f\u3002 \u3000\u3000\u4f46\u4e09\u4eba\u53ea\u662f\u77aa\u7740\u674e\u5bfb\u6b22\u624b\u91cc\u7684\u5200\uff0c\u770b\u6765\u8c01\u4e5f\u6ca1\u6709\u62a2\u5148\u51fa\u624b\u4e4b\u610f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fde\u773c\u89d2\u4e5f\u4e0d\u77a7\u4ed6\u4eec\u4e00\u773c\uff0c\u60a0\u7136\u9053\uff1a\u6211\u77e5\u9053\u4e09\u4f4d\u6b64\u523b\u90fd\u6068\u4e0d\u5f97\u7acb\u523b\u5c06\u6211\u7f6e\u4e8e\u6b7b\u5730\uff0c \u53ea\u56e0\u6740\u4e86\u6211\u8fd9\u6885\u82b1\u76d7\u4e4b\u540e\uff0c\u975e\u4f46\u7acb\u523b\u8363\u534e\u5bcc\u8d35\uff0c\u7f8e\u4eba\u5728\u62b1\uff0c\u800c\u4e14\u8fd8\u53ef\u6362\u5f97\u4e2a\u7559\u82b3\u767e\u4e16\u7684\u7f8e\u540d \u3002 \u3000\u3000\u8d75\u6b63\u4e49\u6273\u7740\u8138\u9053\uff1a\u9ec4\u91d1\u7f8e\u4eba\uff0c\u7b49\u95f2\u4e8b\u8033\uff0c\u6211\u4eec\u6740\u4f60\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u66ff\u6c5f\u6e56\u9664\u5bb3\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u597d\u5149\u660e\u554a\uff0c\u597d\u5802\u7687\uff0c\u679c\u7136\u4e0d\u6127\u4e3a\u94c1\u9762\u65e0\u79c1\uff0c\u4fa0\u4e49\u65e0\u53cc\uff01 \u3000\u3000\u4ed6\u8f7b\u629a\u7740\u624b\u91cc\u7684\u5200\u950b\uff0c\u5f90\u5f90\u9053\uff1a\u4f46\u9601\u4e0b\u4e3a\u4f55\u8fd8\u4e0d\u51fa\u624b\u5462\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u7684\u76ee\u5149\u968f\u7740\u4ed6\u7684\u624b\u8f6c\u6765\u8f6c\u53bb\uff0c\u4e5f\u4e0d\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u7530\u4e03\u7237\u4e00\u6761\u68cd\u68d2\u538b\u5929\u4e0b\uff0c\u4e09\u9897\u94c1\u80c6\u5b9a\u4e7e\u5764\uff0c\u8d75\u5927\u7237\u60f3\u5fc5\u662f\u5728 \u7b49\u7740\u7530\u4e03\u7237\u51fa\u624b\uff0c\u7530\u4e03\u7237\u81ea\u7136\u4e5f\u662f\u4e49\u4e0d\u5bb9\u8f9e\u7684\u4e86\uff0c\u662f\u4e48\uff1f \u3000\u3000\u7530\u4e03\u53cc\u624b\u80cc\u8d1f\u5728\u8eab\u540e\uff0c\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u7530\u4e03\u7237\u96be\u9053\u4e5f\u5728\u7b49\u7740\u516c\u5b59\u5148\u751f\u51fa\u624b\uff1f\u55ef\uff0c\u4e0d\u9519\uff0c\u516c\u5b59\u5148\u751f\u6469\u4e91\u5341\u56db\u5f0f\u77e2\u77eb\u53d8 \u5316\uff0c\u6d77\u5185\u65e0\u53cc\uff0c\u81ea\u7136\u662f\u5e94\u8be5\u8ba9\u516c\u5b59\u5148\u751f\u5148\u51fa\u624b\u7684\u3002 \u3000\u3000\u516c\u5b59\u6469\u4e91\u5c31\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u4e2a\u804b\u5b50\uff0c\u8fde\u52a8\u90fd\u4e0d\u52a8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u8fd9\u5012\u602a\u4e86\uff0c\u4e09\u4f4d\u90fd\u60f3\u5c06\u6211\u6740\u4e4b\u800c\u540e\u5feb\uff0c\u5374\u53c8\u90fd\u4e0d\u80af\u51fa\u624b\uff0c\u83ab\u975e\u4e09\u4f4d \u90fd\u4e0d\u613f\u62a2\u5148\u4e89\u529f\uff0c\u5728\u4e92\u76f8\u5ba2\u6c14\uff1f \u3000\u3000\u516c\u5b59\u6469\u4e91\u7b49\u4e09\u4eba\u5012\u4e5f\u771f\u6c89\u5f97\u4f4f\u6c14\uff0c\u674e\u5bfb\u6b22\u65e0\u8bba\u5982\u4f55\u7b11\u9a82\uff0c\u8fd9\u4e09\u4eba\u5c45\u7136\u8fd8\u662f\u5145\u8033\u4e0d\u95fb\u3002 \u3000\u3000\u5176\u5b9e\u4e09\u4eba\u5fc3\u91cc\u65e9\u5df2\u90fd\u6068\u4e0d\u5f97\u5c06\u674e\u5bfb\u6b22\u8e22\u6b7b\uff0c\u4f46\u5c0f\u674e\u795e\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u674e\u5bfb\u6b22\u53ea\u8981\u4e00\u5200\u5728 \u624b\uff0c\u6709\u8c01\u6562\u5148\u52a8\uff1f \u3000\u3000\u4ed6\u4eec\u4e09\u4eba\u4e0d\u52a8\uff0c\u522b\u4eba\u81ea\u7136\u66f4\u4e0d\u6562\u52ab\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5ffd\u7136\u7b11\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u5230\u73b0\u5728\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\u4ed6\u4eec\u53ea\u4e0d\u8fc7\u662f\u5728\u8ddf\u4f60\u5f00\u73a9\u7b11\uff1f\u8d70\u8d70\u8d70\uff0c \u6211\u4eec\u8fd8\u662f\u559d\u676f\u9152\u53bb\u6321\u6321\u5bd2\u6c14\u5427\u3002 \u3000\u3000\u4ed6\u5927\u7b11\u7740\u8d70\u8fc7\u53bb\uff0c\u633d\u4f4f\u4e86\u674e\u5bfb\u6b22\u7684\u80a9\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9762\u8272\u9aa4\u53d8\uff0c\u5931\u58f0\u9053\uff1a\u5927\u54e5\u4f60\u2014\u2014\uff0d \u3000\u3000\u4ed6\u60f3\u63a8\u5f00\u9f99\u5578\u4e91\uff0c\u5374\u5df2\u8fdf\u4e86\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u547c\u7684\u4e00\u58f0\uff0c\u7530\u4e03\u7684\u624b\u5df2\u81ea\u80cc\u540e\u62bd\u51fa\uff0c\u4e00\u6761\u5c3a\u4e8c\u5bf8\u957f\u7684\u91d1\u4e1d\u5939\u85e4\u8f6f\u68cd\uff0c\u5df2 \u6bd2\u86c7\u822c\u7684\u62bd\u5728\u674e\u5bfb\u6b22\u817f\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u638c\u4e2d\u7a7a\u6709\u72ec\u6b65\u5929\u4e0b\uff0c\u89c1\u8005\u4e27\u80c6\u7684\u5c0f\u674e\u795e\u5200\uff0c\u4f46\u8eab\u5b50\u5df2\u88ab\u9f99\u5578\u4e91\u70ed\u60c5\u7684\u624b\u81c2\u63fd\u4f4f\uff0c \u8fd9\u98de\u5200\u90a3\u91cc\u8fd8\u80fd\u53d1\u5f97\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u556a\u7684\u4e00\u58f0\uff0c\u4ed6\u4e24\u6761\u817f\u5df2\u75bc\u5f97\u8dea\u4e86\u4e0b\u53bb\uff0c\u516c\u5b59\u6469\u4e91\u51fa\u624b\u5982\u98ce\uff0c\u5df2\u70b9\u4e86\u4ed6\u80cc\u540e\u4e03\u5904\u5927\u7a74 \u3002 \u3000\u3000\u8d75\u6b63\u4e49\u8ddf\u7740\u98de\u8d77\u4e00\u817f\uff0c\u5c06\u4ed6\u8e22\u5f97\u6eda\u51fa\u4e24\u4e08\u5916\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u543c\u9053\uff1a\u4f60\u4eec\u600e\u80fd\u5982\u6b64\u51fa\u624b\uff1f\u5feb\u653e\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u72c2\u543c\u7740\u5411\u674e\u5bfb\u6b22\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u51b7\u51b7\u9053\uff1a\u7eb5\u864e\u5bb9\u6613\u64d2\u864e\u96be\uff0c\u653e\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u9f99\u56db\u7237\uff0c\u5f97\u7f6a\u4e86\uff01 \u3000\u3000\u516c\u5b59\u6469\u4e91\u5df2\u6a2a\u8eab\u6321\u4f4f\u4e86\u9f99\u5578\u4e91\u7684\u53bb\u8def\uff0c\u9f99\u5578\u4e91\u53cc\u62f3\u9f50\u51fa\uff0c\u4f46\u7530\u4e03\u7684\u7684\u91d1\u4e1d\u5939\u85e4\u8f6f\u68cd\u5df2\u515c \u4f4f\u4e86\u4ed6\u7684\u817f\u3002 \u3000\u3000\u8f6f\u68cd\u4e00\u6296\uff0c\u9f99\u5578\u4e91\u54ea\u91cc\u8fd8\u7ad9\u5f97\u4f4f\u811a\uff0c\u8d75\u6b63\u4e49\u4e0d\u7b49\u4ed6\u8eab\u5b50\u518d\u62ff\u76f4\u7ad9\u7a33\uff0c\u5df2\u5728\u4ed6\u8f6f\u80c1\u4e0a\u70b9\u4e86 \u4e00\u7a74\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6251\u5730\u8dea\u5012\uff0c\u54fd\u58f0\u9053\uff1a\u8d75\u5927\u54e5\uff0c\u4f60\u2014\u2014\u4f60\u600e\u80fd\u5982\u6b64\u2014\u2014 \u3000\u3000\u8d75\u6b63\u4e49\u6c89\u7740\u8138\u9053\uff1a\u4f60\u6211\u867d\u7136\u4e49\u7ed3\u91d1\u5170\uff0c\u4f46\u6c5f\u6e56\u9053\u4e49\u5374\u8fdc\u91cd\u4e8e\u5144\u5f1f\u4e4b\u60c5\uff0c\u4f46\u613f\u4f60\u4e5f\u80fd\u660e\u767d \u8fd9\u9053\u7406\uff0c\u83ab\u8981\u518d\u4e3a\u8fd9\u6b66\u6797\u8d25\u7c7b\u81ea\u8ba8\u82e6\u5403\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4ed6\u7edd\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u7edd\u4e0d\u662f\uff01 \u3000\u3000\u516c\u5b59\u6469\u4e91\u9053\uff1a\u56db\u7237\uff0c\u4f60\u662f\u6709\u5bb6\u6709\u5ba4\uff0c\u6709\u8eab\u4efd\u3001\u6709\u5730\u4f4d\u7684\u4eba\uff0c\u82e5\u662f\u88ab\u8fd9\u79cd\u6deb\u68cd\u62d6\u7d2f\uff0c\u5c82\u975e \u592a\u4e0d\u503c\u5f97\u4e86\u4e48\uff1f \u3000\u3000\u9f99\u5578\u4e91\u5636\u58f0\u9053\uff1a\u53ea\u8981\u4f60\u4eec\u5148\u653e\u4e86\u4ed6\uff0c\u65e0\u8bba\u591a\u5927\u7684\u7f6a\uff0c\u9f99\u5578\u4e91\u90fd\u5b81\u613f\u66ff\u4ed6\u627f\u5f53\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5389\u58f0\u9053\uff1a\u4f60\u613f\u4e3a\u4ed6\u627f\u5f53\uff1f\u53ef\u662f\u4f60\u7684\u59bb\u5b50\u5462\uff1f\u4f60\u7684\u513f\u5973\u5462\uff1f\u4f60\u96be\u9053\u4e5f\u5fcd\u5fc3\u773c\u770b\u4ed6\u4eec \u88ab\u4f60\u8fde\u7d2f\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9aa4\u7136\u4e00\u9707\uff0c\u5168\u8eab\u90fd\u53d1\u8d77\u6296\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u98de\u5200\u867d\u4ecd\u5728\u624b\uff0c\u600e\u5948\u5df2\u662f\u6c38\u8fdc\u518d\u4e5f\u53d1\u4e0d\u51fa\u53bb\u7684\u4e86\uff01 \u3000\u3000\u8fd9\u4e00\u8eab\u50b2\u9aa8\uff0c\u4e00\u751f\u5bc2\u5bde\u7684\u82f1\u96c4\uff0c\u96be\u9053\u7adf\u8981\u843d\u5f97\u4e2a\u8fd9\u6837\u7684\u4e0b\u573a\uff01 \u3000\u3000\u9f99\u5578\u4e91\u76ee\u4e2d\u4e0d\u7981\u6d41\u4e0b\u6cea\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u5144\u5f1f\uff0c\u5168\u662f\u6211\u5bb3\u4e86\u4f60\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u5bf9\u4e0d\u8d77\u4f60\u2014\u2014 \uff0d \u3000\u3000\u9ece\u660e\u524d\u7684\u4e00\u6bb5\u65f6\u5019\uff0c\u6c38\u8fdc\u662f\u6700\u9ed1\u6697\u7684\u3002\u5c31\u8fde\u5927\u5385\u91cc\u8f89\u714c\u7684\u706f\u5149\uff0c\u4e5f\u90fd\u51b2\u4e0d\u7834\u8fd9\u65e0\u8fb9\u65e0\u9645 \u7684\u9ed1\u6697\u3002 \u3000\u3000\u4e00\u7fa4\u4eba\u805a\u5728\u5385\u5916\u7684\u77f3\u9636\u4e0a\uff0c\u6b63\u7a83\u7a83\u79c1\u8bae\uff01 \u3000\u3000\u7530\u4e03\u7237\u679c\u7136\u4e86\u4e0d\u8d77\uff0c\u4f60\u770b\u4ed6\u90a3\u4e00\u68cd\u51fa\u624b\u6709\u591a\u5feb\uff0c\u5c31\u7b97\u9f99\u56db\u7237\u4e0d\u5728\u90a3\u91cc\u6321\u7740\uff0c\u6211\u770b\u674e\u5bfb\u6b22 \u4e5f\u8eb2\u4e0d\u5f00\u3002 \u3000\u3000\u4f55\u51b5\u65c1\u8fb9\u8fd8\u6709\u516c\u5b59\u5927\u4fa0\u548c\u8d75\u5927\u7237\u5462\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u96be\u602a\u522b\u4eba\u8bf4\u8d75\u5927\u7237\u7684\u4e24\u6761\u817f\u53ef\u503c\u4e07\u4e24\u9ec4\u91d1\uff0c\u4f60\u77a7\u4ed6\u8e22\u51fa\u53bb\u7684\u90a3\u4e00\u811a\uff0c\u8981\u591a\u6f02\u4eae\u6709 \u591a\u6f02\u4eae\u3002 \u3000\u3000\u5e38\u8a00\u9053\uff0c\u5357\u62f3\u5317\u817f\uff0c\u54b1\u4eec\u5317\u65b9\u7684\u8c6a\u6770\uff0c\u817f\u6cd5\u672c\u5c31\u9ad8\u5f3a\u3002 \u3000\u3000\u4f46\u516c\u5b59\u5927\u4fa0\u7684\u638c\u6cd5\u53c8\u4f55\u5c1d\u5f31\u4e86\uff0c\u82e5\u975e\u4ed6\u53ca\u65f6\u51fa\u624b\uff0c\u674e\u5bfb\u6b22\u5c31\u7b97\u6328\u4e86\u4e00\u68cd\u5b50\uff0c\u4e5f\u672a\u5fc5\u4f1a\u5012 \u53bb\u3002 \u3000\u3000\u7530\u4e03\u7237\u3001\u8d75\u5927\u7237\uff0c\u518d\u52a0\u4e0a\u516c\u5b59\u5927\u4fa0\uff0c\u563f\uff0c\u674e\u5bfb\u6b22\u4eca\u65e5\u63b8\u7740\u4ed6\u4eec\u4e09\u4f4d\uff0c\u771f\u662f\u5012\u4e86\u9709\u4e86\u3002 \u3000\u3000\u8bdd\u867d\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f46\u82e5\u975e\u9f99\u56db\u7237\u2014\u2014\uff0d \u3000\u3000\u9f99\u56db\u7237\u53c8\u600e\u6837\uff0c\u4ed6\u5bf9\u674e\u5bfb\u6b22\u8fd8\u4e0d\u591f\u4e49\u6c14\u5417\uff1f \u3000\u3000\u9f99\u56db\u7237\u53ef\u771f\u662f\u4e49\u6c14\u5343\u4e91\uff0c\u674e\u5bfb\u6b22\u80fd\u4ea4\u5230\u4ed6\u8fd9\u79cd\u670b\u53cb\uff0c\u771f\u662f\u8fd0\u6c14\uff01 \u3000\u3000\u9f99\u5578\u4e91\u5750\u5728\u5927\u5385\u91cc\u7684\u7ea2\u6728\u6905\u4e0a\uff0c\u542c\u5230\u8fd9\u4e9b\u8bdd\uff0c\u5fc3\u91cc\u5c31\u50cf\u88ab\u9488\u523a\u4e00\u6837\uff0c\u6ee1\u5934\u6c57\u51fa\u5982\u96e8\u3002 \u3000\u3000\u53ea\u89c1\u674e\u5bfb\u6b22\u4f0f\u5728\u5730\u4e0a\uff0c\u53c8\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5fcd\u4e0d\u4f4f\u6d41\u6cea\u9053\uff1a\u5144\u5f1f\uff0c\u5168\u662f\u6211\u8be5\u6b7b\uff0c\u4f60\u4ea4\u5230\u6211\u8fd9\u670b\u53cb\uff0c\u5b9e\u5728\u662f\u2014\u2014\u662f\u4f60\u7684\u4e0d\u5e78\uff0c \u4f60\u2014\u2014\u4f60\u8fd9\u4e00\u751f\u5168\u662f\u88ab\u6211\u62d6\u7d2f\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52aa\u529b\u5fcd\u4f4f\u54b3\u55fd\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u5927\u54e5\uff0c\u6211\u53ea\u60f3\u8981\u4f60\u660e\u767d\u4e00\u4ef6\u4e8b\uff0c\u82e5\u8ba9\u6211\u8fd9\u4e00\u751f\u91cd\u5934\u518d \u6d3b\u4e00\u6b21\uff0c\u6211\u8fd8\u662f\u4f1a\u6beb\u4e0d\u8003\u8651\u5c31\u4ea4\u4f60\u8fd9\u670b\u53cb\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4f46\u89c9\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u7adf\u653e\u58f0\u5927\u54ed\u9053\uff1a\u53ef\u662f\u2014\u2014\u82e5\u975e\u6211\u963b\u4f4f\u4e86\u4f60\u51fa\u624b\uff0c\u4f60\u53c8\u600e\u4f1a\uff0d \uff0d\u600e\u4f1a\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u6211\u77e5\u9053\u5927\u54e5\u4f60\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u90fd\u662f\u4e3a\u4e86\u6211\u597d\uff0c\u6211\u53ea\u6709\u611f\u6fc0\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f46\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u544a\u8bc9\u4ed6\u4eec\uff0c\u4f60\u4e0d\u662f\u6885\u82b1\u76d7\uff01\u4f60\u4e3a\u4ec0\u4e48\u2014\u2014\u4e3a\u4ec0\u4e48\u8981\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u751f\u6b7b\u7b49\u95f2\u4e8b\u8033\uff0c\u6211\u8fd9\u4e00\u751f\u672c\u5df2\u6d3b\u591f\u4e86\uff0c\u751f\u6709\u4f55\u6b22\uff0c\u6b7b\u6709\u4f55\u601c\uff1f\u4e3a\u4ec0\u4e48 \u8fd8\u8981\u5728\u8fd9\u4e9b\u5339\u592b\u5c0f\u4eba\u9762\u524d\u5351\u8eac\u66f2\u819d\uff01 \u3000\u3000\u7530\u4e03\u4e00\u76f4\u542b\u7b11\u671b\u7740\u4ed6\u4eec\uff0c\u6b64\u523b\u5ffd\u7136\u629a\u638c\u7b11\u9053\uff1a\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u597d\uff01 \u3000\u3000\u516c\u5b59\u6469\u4e91\u51b7\u7b11\u9053\uff1a\u4ed6\u660e\u767d\u4eca\u65e5\u65e0\u8bba\u8bf4\u4ec0\u4e48\uff0c\u6211\u4eec\u90fd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\uff0c\u4e5f\u53ea\u597d\u5b66\u90a3\u6cfc\u5987\u9a82\u8857\uff0c \u4e34\u6b7b\u4e5f\u843d\u5f97\u4e2a\u5634\u4e0a\u723d\u5feb\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u4e0d\u9519\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u4f46\u6c42\u4e00\u6b7b\u800c\u5df2\uff0c\u4f46\u6b64\u523b\u674e\u67d0\u638c\u4e2d\u5df2\u65e0\u98de\u5200\uff0c\u5404\u4f4d\u4e3a \u4f55\u8fd8\u662f\u4e0d\u80af\u51fa\u624b\u5462\uff1f \u3000\u3000\u516c\u5b59\u6469\u4e91\u90a3\u5f20\u67af\u7626\u814a\u9ec4\u7684\u8138\u5c45\u7136\u4e5f\u4e0d\u7981\u7ea2\u4e86\u7ea2\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5374\u4ecd\u662f\u8138\u8272\u94c1\u9752\uff0c\u6c89\u58f0\u9053\uff1a\u6211\u4eec\u82e5\u662f\u6b64\u523b\u5c31\u6740\u4e86\u4f60\uff0c\u6c5f\u6e56\u4e2d\u96be\u514d\u4f1a\u6709\u4f60\u8fd9\u6837\u7684\u4e0d \u8096\u4e4b\u5f92\uff0c\u8981\u8bf4\u6211\u4eec\u662f\u5047\u516c\u6d4e\u79c1\uff0c\u6211\u4eec\u8981\u6740\u4f60\uff0c\u4e5f\u8981\u6740\u5f97\u516c\u516c\u9053\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u8d75\u6b63\u4e49\uff0c\u6211\u771f\u4f69\u670d\u4f60\uff0c\u4f60\u867d\u7136\u6ee1\u809a\u5b50\u7537\u76d7\u5973\u5a3c\uff0c\u4f46\u8bf4\u8bdd\u5374\u662f\u53e5\u53e5 \u4ec1\u4e49\u9053\u5fb7\uff0c\u800c\u4e14\u5c45\u7136\u4e00\u70b9\u4e5f\u4e0d\u8138\u7ea2\u3002 \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u597d\uff0c\u59d3\u674e\u7684\uff0c\u7b97\u4f60\u6709\u80c6\u5b50\uff0c\u4f60\u82e5\u60f3\u5feb\u70b9\u6b7b\uff0c\u6211\u5012\u6709\u4e2a\u6cd5\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u6211\u672c\u6765\u4e5f\u60f3\u9a82\u4f60\u51e0\u53e5\uff0c\u53ea\u4e0d\u8fc7\u5374\u6015\u810f\u4e86\u6211\u7684\u5634\u3002 \u3000\u3000\u7530\u4e03\u542c\u800c\u4e0d\u95fb\uff0c\u8fd8\u662f\u5fae\u7b11\u9053\uff1a\u4f60\u82e5\u80af\u5199\u5f20\u6094\u7f6a\u4e66\uff0c\u62db\u4f9b\u4f60\u7684\u7f6a\u884c\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u8ba9\u4f60\u8212\u8212 \u670d\u670d\u7684\u4e00\u6b7b\uff0c\u4f60\u4e5f\u7b97\u6c42\u4ec1\u5f97\u4ec1\uff0c\u6b7b\u5f97\u4e0d\u51a4\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60f3\u4e5f\u4e0d\u60f3\uff0c\u7acb\u523b\u9053\uff1a\u597d\uff0c\u6211\u8bf4\uff0c\u4f60\u5199\u2014\u2014\uff0d \u3000\u3000\u9f99\u5578\u4e91\u5931\u58f0\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u62db\u4e0d\u5f97\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7406\u4ed6\uff0c\u63a5\u7740\u9053\uff1a\u6211\u7684\u7f6a\u5b7d\u5b9e\u662f\u56db\u66f2\u96be\u6570\uff0c\u7f44\u7af9\u96be\u4e66\uff0c\u6211\u5047\u5192\u4f2a\u5584\uff0c\u5185\u5fc3\u5978\u8bc8 \uff0c\u5939\u79c1\u9677\u6743\uff0c\u6311\u62d4\u79bb\u95f4\uff0c\u8d81\u4eba\u4e0d\u5907\uff0c\u5077\u65bd\u6697\u7b97\uff0c\u4e0d\u4ec1\u4e0d\u4e49\uff0c\u5351\u9119\u65e0\u803b\u7684\u4e8b\u6211\u51e0\u4e4e\u5168\u90fd\u505a\u5c3d\u4e86 \uff0c\u4f46\u5374\u8fd8\u662f\u5927\u6a21\u5927\u6837\u7684\u81ea\u547d\u4e0d\u51e1\uff01 \u3000\u3000\u53ea\u542c\u556a\u7684\u4e00\u58f0\uff0c\u8d75\u6b63\u4e49\u5df2\u53cd\u624b\u4e00\u638c\uff0c\u6253\u5728\u4ed6\u8138\u4e0a\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u8fd8\u662f\u5fae\u7b11\u9053\uff1a\u65e0\u59a8\uff0c\u4ed6\u6253\u6211\u4e00\u5df4\u638c\uff0c\u6211\u53ea\u5f53\u88ab\u75af\u72d7\u54ac\u4e86\u4e00\u53e3\u800c\u5df2\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u6012\u543c\u9053\uff1a\u59d3\u674e\u7684\uff0c\u4f60\u542c\u7740\uff0c\u5c31\u7b97\u6211\u8fd8\u4e0d\u613f\u6740\u4f60\uff0c\u4f46\u6211\u5374\u670b\u4e8b\u68a7\u8ba9\u4f60\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42 \u6b7b\u4e0d\u80fd\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u6211\u82e5\u6015\u4e86\u4f60\u4eec\u8fd9\u4e9b\u5351\u9119\u65e0\u803b\uff0c\u5047\u4ec1\u5047\u4e49\u7684\u5c0f\u4eba\uff0c\u6211\u4e5f\u6789\u4e3a\u7537\u5b50\u6c49\u4e86 \uff01\u4f60\u4eec\u6709\u4ec0\u4e48\u624b\u6bb5\uff0c\u53ea\u7ba1\u4f7f\u51fa\u6765\u5427\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u559d\u9053\uff1a\u597d\uff01 \u3000\u3000\u9f99\u5578\u4e91\u5750\u5728\u6905\u4e0a\uff0c\u5168\u8eab\u76f4\u6296\uff0c\u98a4\u58f0\u9053\uff1a\u5144\u5f1f\uff0c\u539f\u8c05\u6211\uff0c\u4f60\u662f\u82f1\u96c4\uff0c\u4f46\u6211\u2014\u2014\u5374\u662f\u4e2a\u61e6\u592b \uff0c\u6211\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u8fd9\u6028\u4e0d\u5f97\u5927\u54e5\u4f60\uff0c\u6211\u82e5\u4e5f\u6709\u59bb\u5b50\uff0c\u4e5f\u4f1a\u548c\u5927\u54e5\u540c\u6837\u505a\u6cd5\u7684\u3002 \u3000\u3000\u8fd9\u65f6\u8d75\u6b63\u4e49\u7684\u94c1\u638c\u65e9\u5df2\u634f\u4f4f\u4e86\u4ed6\u7684\u8f6f\u9aa8\u9178\u7b4b\uff0c\u90a3\u75db\u82e6\u7b80\u76f4\u975e\u4eba\u6240\u80fd\u5fcd\u53d7\uff0c\u674e\u5bfb\u6b22\u5df2\u75bc\u5f97 \u6d41\u6c57\uff0c\u4f46\u8fd8\u662f\u795e\u8272\u4e0d\u53d8\uff0c\u542b\u7b11\u800c\u8a00\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u5927\u5385\u5916\u6709\u4eba\u9053\uff1a\u6797\u59d1\u5a18\uff0c\u4f60\u662f\u4ece\u54ea\u91cc\u56de\u6765\uff1f\u2014\u2014\u8fd9\u4f4d\u662f\u8c01\uff1f \u3000\u3000\u53ea\u89c1\u6797\u4ed9\u513f\u8863\u886b\u96f6\u4e71\uff0c\u4e91\xd7\u4e0d\u6574\uff0c\u5306\u5306\u5730\u4ece\u5916\u9762\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5979\u8eab\u65c1\u8fd8\u8ddf\u7740\u4e2a\u5c11\u5e74\uff0c\u5728\u5982\u6b64\u4e25\u5bd2\u7684\u5929\u6c14\u91cc\uff0c\u4ed6\u8eab\u4e0a\u53ea\u7a7f\u7740\u4ef6\u5f88\u5355\u8584\u7684\u8863\u886b\uff0c\u4f46\u80cc\u810a\u5374 \u4ecd\u633a\u5f97\u7b14\u76f4\uff0c\u4eff\u4f5b\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u4ee4\u4ed6\u5f2f\u8170\uff01 \u3000\u3000\u4ed6\u8eab\u4e0a\u7adf\u80cc\u7740\u4e2a\u6b7b\u5c38\u3002 \u3000\u3000\u963f\u98de\uff01 \u3000\u3000\u963f\u98de\u600e\u4f1a\u5ffd\u7136\u6765\u4e86\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u4e00\u9635\u6fc0\u52a8\uff0c\u4e5f\u4e0d\u77e5\u662f\u60ca\u662f\u559c\uff1f\u4f46\u4ed6\u7acb\u523b\u626d\u8f6c\u5934\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u88ab\u963f\u98de\u770b\u5230\u4ed6\u5982 \u6b64\u6a21\u6837\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u963f\u98de\u4e3a\u4ed6\u5192\u9669\u51fa\u624b\u3002 \u3000\u3000\u963f\u98de\u8fd8\u662f\u770b\u5230\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u51b7\u6f20\u575a\u5b9a\u7684\u8138\uff0c\u7acb\u523b\u53d8\u5f97\u6fc0\u52a8\u8d77\u6765\uff0c\u5927\u6b65\u51b2\u4e86\u8fc7\u53bb\uff0c\u8d75\u6b63\u4e49\u5e76\u6ca1\u6709\u963b\u62e6\u4ed6\uff0c\u56e0\u4e3a\u8d75\u6b63 \u4e49\u5df2\u9886\u6559\u8fc7\u8fd9\u5c11\u5e74\u7684\u5251\u6cd5\u3002 \u3000\u3000\u4f46\u516c\u5b59\u6469\u4e91\u5374\u4e0d\u77e5\u9053\uff0c\u5df2\u95ea\u8eab\u6321\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u5e94\u58f0\u9053\uff1a\u4f60\u662f\u8c01\uff1f\u60f3\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u60f3\u6559\u8bad\u6559\u8bad\u4f60\uff01 \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4ed6\u5df2\u51fa\u4e86\u624b\u3002 \u3000\u3000\u6797\u4ed9\u513f\uff0c\u5979\u53ea\u662f\u5403\u60ca\u7684\u671b\u7740\u674e\u5bfb\u6b22\uff0c\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u522b\u4eba\uff0c\u81f3\u4e8e\u9f99\u5578\u4e91\uff0c\u4ed6\u4f3c\u5df2\u65e0\u5fc3\u518d \u7ba1\u522b\u4eba\u7684\u95f2\u4e8b\u4e86\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u963f\u98de\u5c45\u7136\u4e5f\u6ca1\u6709\u95ea\u907f\u3002 \u3000\u3000\u53ea\u542c\u7830\u7684\u4e00\u58f0\uff0c\u516c\u5b59\u6469\u4e91\u7684\u62f3\u5934\u5df2\u6253\u5728\u963f\u98de\u80f8\u819b\u4e0a\uff0c\u963f\u98de\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u516c\u5b59\u6469\u4e91\u81ea\u5df1 \u5374\u75bc\u5f97\u5f2f\u4e0b\u8170\u53bb\u3002 \u3000\u3000\u963f\u98de\u518d\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u773c\uff0c\u81ea\u4ed6\u8eab\u65c1\u8d70\u8fc7\uff0c\u8d70\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u9053\uff1a\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u4f60\u770b\u6211\u4f1a\u4e0d\u4f1a\u5730\u6709\u8fd9\u79cd\u670b\u53cb\u3002 \u3000\u3000\u8fd9\u65f6\u516c\u5b59\u6469\u4e91\u53c8\u6012\u543c\u7740\u6251\u4e86\u4e0a\u6765\uff0c\u4e00\u638c\u540e\u5728\u963f\u98de\u7684\u80cc\u5fc3\uff0c\u963f\u98de\u7a81\u7136\u8f6c\u8eab\uff0c\u53ea\u542c\u53c8\u662f\u7830\u7684 \u4e00\u58f0\u3002 \u3000\u3000\u516c\u5b59\u6469\u4e91\u7684\u8eab\u5b50\u7a81\u7136\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u9762\u4e0a\u5168\u90fd\u53d8\u4e86\u989c\u8272\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u540d\u52a8\u6c5f\u6e56\u7684\u6469\u4e91\u624b\u5728\u8fd9\u5c11\u5e74\u9762\u524d\uff0c\u7adf\u53d8\u5f97\u50cf\u662f\u4e2a\u7a3b \u8349\u4eba\u822c\u4e0d\u582a\u4e00\u51fb\u3002 \u3000\u3000\u53ea\u6709\u7530\u4e03\u5374\u5927\u7b11\u9053\uff1a\u670b\u53cb\u597d\u5feb\u7684\u51fa\u624b\uff0c\u5f53\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\uff0c\u6c5f\u6e56\u82f1\u96c4\u51fa\u5c11\u5e74\u3002 \u3000\u3000\u4ed6\u62b1\u62f3\u4e00\u63d6\uff0c\u7b11\u9053\uff1a\u5728\u4e0b\u7530\u4e03\uff0c\u4e0d\u77e5\u6401\u4e0b\u9ad8\u59d3\u5927\u540d\uff0c\u53ef\u613f\u548c\u7530\u4e03\u4ea4\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u6ca1\u6709\u540d\u5b57\uff0c\u4e5f\u4e0d\u613f\u4ea4\u4f60\u8fd9\u79cd\u670b\u53cb\u3002 \u3000\u3000\u522b\u7684\u9762\u8272\u53c8\u53d8\u4e86\uff0c\u7530\u4e03\u5374\u4ecd\u662f\u6ee1\u9762\u7b11\u5bb9\uff0c\u9053\uff1a\u5c11\u5e74\u4eba\u5012\u662f\u5feb\u8bed\uff0c\u53ea\u53ef\u60dc\u4ea4\u7684\u670b\u53cb\u5374\u9009\u9519 \u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff1f \u3000\u3000\u7530\u4e03\u6307\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u662f \u3000\u3000\u7530\u4e03\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u77e5\u9053 \u3000\u3000\u7530\u4e03\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4f60\u4e5f\u77e5\u9053\u4ed6\u5c31\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u52a8\u5bb9\u9053\uff1a\u6885\u82b1\u76d7\uff1f \u3000\u3000\u7530\u4e03\u9053\uff1a\u8fd9\u4ef6\u4e8b\u8bf4\u6765\u7684\u786e\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\uff0c\u53ea\u4e0d\u8fc7\u4e8b\u5b9e\u4ff1\u5728\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5426\u8ba4\u3002 \u3000\u3000\u963f\u98de\u77aa\u7740\u4ed6\uff0c\u9510\u5229\u7684\u76ee\u5149\u5c31\u50cf\u662f\u8981\u523a\u5165\u4ed6\u5fc3\u91cc\u3002 \u3000\u3000\u963f\u98de\u51b7\u51b7\u9053\uff1a\u4f60\u4e0d\u5fc5\u95ee\u4ed6\uff0c\u4ed6\u7edd\u4e0d\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u5ffd\u7136\u5c06\u8098\u4e0b\u5939\u7740\u7684\u6b7b\u5c38\u653e\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u56e0\u4e3a\u8fd9\u624d\u662f\u6885\u82b1\u76d7\uff01 \u3000\u3000\u7fa4\u8c6a\u53c8\u4e00\u60ca\uff0c\u5fcd\u4e0d\u4f4f\u90fd\u9021\u5de1\u7740\u56f4\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u6b7b\u5c38\u53c8\u5e72\u53c8\u7626\uff0c\u8138\u4e0a\u5200\u75a4\u7eb5\u6a2a\uff0c\u4e5f\u770b\u4e0d\u51fa\u4ed6\u672c\u662f\u4f55\u9762\u8c8c\uff0c\u8eab\u4e0a\u7a7f\u7684\u662f\u4ef6\u7d27\u8eab\u9ed1\u8863 \uff0c\u8fde\u808b\u9aa8\u90fd\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u7d27\u54ac\u7740\u7259\u9f7f\uff0c\u7adf\u662f\u6b7b\u4e5f\u4e0d\u80af\u653e\u677e\uff0c\u8eab\u4e0a\u4e5f\u77a7\u4e0d\u89c1\u4ec0\u4e48\u4f24\u75d5\uff0c\u53ea\u6709\u54bd\u5589\u5df2\u88ab\u523a\u7a7f\u4e86\u4e2a\u7a9f \u7abf\u3002 \u3000\u3000\u7530\u4e03\u53c8\u7b11\u4e86\uff0c\u5927\u7b11\u9053\uff1a\u4f60\u8bf4\u8fd9\u6b7b\u4eba\u624d\u662f\u771f\u6b63\u7684\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u4f60\u6bd5\u7adf\u592a\u5e74\u8f7b\uff0c\u4ee5\u4e3a\u522b\u4eba\u4e5f\u548c\u4f60\u540c\u6837\u5bb9\u6613\u4e0a\u5f53\uff0c\u82e5\u662f\u5927\u5bb6\u53bb\u5f04\u4e2a\u6b7b\u4eba\u56de\u6765\uff0c \u5c31\u8bf4\u4ed6\u662f\u6885\u82b1\u76d7\uff0c\u90a3\u5c82\u975e\u5929\u4e0b\u5927\u4e71\u4e86\u4e48\uff1f \u3000\u3000\u963f\u98de\u816e\u65c1\u7684\u808c\u8089\u4e00\u9635\u98a4\u52a8\uff0c\u9053\uff1a\u6211\u4ece\u6765\u4e0d\u9a97\u4eba\uff0c\u4e5f\u4ece\u6765\u4e0d\u4f1a\u4e0a\u4e0d\u5f53\u3002 \u3000\u3000\u7530\u4e03\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u90a3\u4e48\uff0c\u4f60\u600e\u80fd\u8bc1\u660e\u8fd9\u6b7b\u4eba\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u770b\u770b\u4ed6\u7684\u5634\uff01 \u3000\u3000\u7530\u4e03\u53c8\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u6211\u4e3a\u4f55\u8981\u770b\u4ed6\u7684\u5634\uff0c\u96be\u9053\u4ed6\u7684\u5634\u8fd8\u4f1a\u52a8\u8fd8\u4f1a\u8bf4\u8bdd\uff1f \u3000\u3000\u522b\u7684\u4eba\u4e5f\u8ddf\u7740\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u4eec\u867d\u672a\u5fc5\u89c9\u5f97\u5f88\u597d\u7b11\uff0c\u4f46\u7530\u4e03\u7237\u65e2\u7136\u7b11\u5f97\u5982\u6b64\u5f00\u5fc3\uff0c\u4ed6\u4eec\u53c8 \u600e\u80fd\u4e0d\u7b11\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u5954\u8fc7\u6765\uff0c\u5927\u58f0\u9053\uff1a\u6211\u77e5\u9053\u4ed6\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u6b7b\u4eba\u7684\u786e\u5c31\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u54e6\uff1f\u96be\u9053\u662f\u8fd9\u6b7b\u4eba\u81ea\u5df1\u544a\u8bc9\u4f60\u7684\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e0d\u9519\uff0c\u7684\u786e\u662f\u4ed6\u81ea\u5df1\u544a\u8bc9\u6211\u7684\uff01 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e0d\u9519\uff0c\u7684\u786e\u662f\u4ed6\u81ea\u5df1\u544a\u8bc9\u6211\u7684\uff01 \u3000\u3000\u5979\u4e0d\u8ba9\u522b\u4eba\u7b11\u51fa\u6765\uff0c\u62a2\u7740\u53c8\u9053\uff1a\u79e6\u91cd\u6b7b\u7684\u65f6\u5019\uff0c\u6211\u5df2\u770b\u51fa\u4ed6\u662f\u4e2d\u4e86\u4e00\u79cd\u5f88\u6076\u6bd2\u7684\u6697\u5668\uff0c \u4f46\u79e6\u91cd\u8eb2\u4e0d\u5f00\u8fd9\u79cd\u6697\u5668\uff0c\u72b9\u6709\u53ef\u8bf4\uff0c\u4e3a\u4f55\u8fde\u5434\u95ee\u5929\u90a3\u6837\u7684\u9ad8\u4eba\u4e5f\u8eb2\u4e0d\u5f00\u8fd9\u79cd\u6697\u5668\u5462\uff1f\u6211\u4e00\u76f4 \u60f3\u4e0d\u901a\u8fd9\u9053\u7406\uff0c\u56e0\u4e3a\u8fd9\u5c31\u662f\u6885\u82b1\u76d7\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u7530\u4e03\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u4f60\u73b0\u5728\u96be\u9053\u5df2\u60f3\u901a\u4e86\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e0d\u9519\uff0c\u6885\u82b1\u76d7\u7684\u79d8\u5bc6\u5c31\u5728\u4ed6\u5634\u91cc\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u62bd\u51fa\u4e86\u67c4\u5c0f\u5200\uff0c\u7528\u5200\u64ac\u5f00\u4e86\u8fd9\u6b7b\u4eba\u7684\u5634\u3002 \u3000\u3000\u8fd9\u6b7b\u4eba\u7684\u5634\u91cc\uff0c\u7adf\u54ac\u7740\u6839\u6f06\u9ed1\u7684\u94dc\u7ba1\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53ea\u56e0\u4ed6\u8ddf\u522b\u4eba\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u6697\u5668\u5ffd\u7136\u81ea\u4ed6\u5634\u91cc\u5c04\u51fa\u6765\uff0c\u6240\u4ee5\u522b\u4eba\u6839\u672c\u6ca1\u6709\u8b66 \u89c9\uff0c\u4e5f\u5c31\u65e0\u6cd5\u95ea\u907f\uff01 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ed6\u5634\u91cc\u54ac\u7740\u6697\u5668\u94dc\u7ba1\uff0c\u53c8\u600e\u80fd\u518d\u548c\u522b\u4eba\u8bf4\u8bdd\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8fd9\u5c31\u662f\u4ed6\u79d8\u5bc6\u4e2d\u7684\u79d8\u5bc6\uff01 \u3000\u3000\u5979\u773c\u6ce2\u56db\u4e0b\u4e00\u8f6c\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u4ed6\u5e76\u4e0d\u7528\u5634\u8bf4\u8bdd\uff0c\u5374\u7528\u809a\u5b50\u6765\u8bf4\u8bdd\uff0c\u4ed6\u7684\u5634\u662f\u7528\u6765\u6740\u4eba \u7684\uff01 \u3000\u3000\u8fd9\u53e5\u8bdd\u542c\u6765\u867d\u7136\u5f88\u8352\u5510\u53ef\u7b11\uff0c\u4f46\u50cf\u7530\u4e03\u8fd9\u6837\u7684\u8001\u6c5f\u6e56\u90fd\u77e5\u9053\u4e16\u4e0a\u7684\u786e\u6709\u79cd\u795e\u5bc6\u7684\u8179\u8bed\u672f \uff0c\u636e\u8bf4\u662f\u4f20\u81ea\u6ce2\u65af\u5929\u7afa\u4e00\u5e26\uff0c\u672c\u6765\u53ea\u4e0d\u8fc7\u662f\u6c5f\u6e56\u5356\u827a\u8005\u7684\u5c0f\u6280\uff0c\u58f0\u97f3\u542c\u6765\u4e5f\u6709\u4e9b\u6ed1\u7a3d\uff0c\u4f46\u6b66 \u529f\u9ad8\u624b\u518d\u52a0\u4ee5\u771f\u6c14\u63a7\u5236\uff0c\u8bf4\u51fa\u6765\u7684\u58f0\u97f3\u81ea\u7136\u5c31\u4e0d\u5927\u76f8\u540c\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u7530\u4e03\u7237\u5728\u548c\u4eba\u52a8\u624b\u4e4b\u524d\uff0c\u773c\u775b\u4f1a\u77a7\u5728\u4ec0\u4e48\u5730\u65b9\u5462\uff1f \u3000\u3000\u7530\u4e03\u9053\uff1a\u81ea\u7136\u662f\u77a7\u5728\u5bf9\u65b9\u8eab\u4e0a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8eab\u4e0a\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u7530\u4e03\u6c89\u541f\u9053\uff1a\u4ed6\u7684\u80a9\u5934\uff0c\u548c\u4ed6\u7684\u624b\uff01 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u8fd9\u5c31\u5bf9\u4e86\uff0c\u9ad8\u624b\u76f8\u4e89\uff0c\u8c01\u6562\u4e0d\u4f1a\u77aa\u4f4f\u5bf9\u65b9\u7684\u5634\uff0c\u53ea\u6709\u4e24\u6761\u72d7\u6253\u67b6\u65f6 \uff0c\u624d\u4f1a\u77aa\u4f4f\u5bf9\u65b9\u7684\u5634\uff0c\u56e0\u4e3a\u4eba\u4e0d\u50cf\u72d7\uff0c\u7edd\u4e0d\u4f1a\u7528\u5634\u54ac\u4eba\u3002 \u3000\u3000\u522b\u7684\u4eba\u53c8\u8ddf\u7740\u7b11\u4e86\uff0c\u50cf\u6797\u79d8\u8fd9\u6837\u7684\u7f8e\u4eba\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u4ed6\u4eec\u82e5\u89c9\u5f97\u4e0d\u597d\u7b11\uff0c\u5c82\u975e\u663e\u5f97\u81ea\u5df1 \u4e0d\u61c2\u98ce\u8da3\u3002 \u3000\u3000\u8c01\u77e5\u6797\u4ed9\u513f\u5374\u6c89\u4e0b\u4e86\u8138\uff0c\u53f9\u9053\uff1a\u4f46\u6885\u82b1\u76d7\u5374\u504f\u504f\u662f\u7528\u5634\u6765\u6740\u4eba\u7684\uff0c\u5c31\u56e0\u4e3a\u8c01\u4e5f\u60f3\u4e0d\u5230\u4e16 \u4e0a\u4f1a\u6709\u8fd9\u79cd\u4e8b\uff0c\u6240\u4ee5\u624d\u4f1a\u88ab\u4ed6\u6697\u7b97\u2014\u2014\u8d8a\u662f\u9ad8\u624b\uff0c\u8d8a\u5bb9\u6613\u88ab\u4ed6\u6697\u7b97\uff0c\u56e0\u4e3a\u9ad8\u624b\u5bf9\u654c\uff0c\u773c\u775b\u7edd \u4e0d\u4f1a\u77a7\u5230\u5bf9\u65b9\u80a9\u5934\u4ee5\u4e0a\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u8fd9\u79d8\u5bc6\u4f60\u600e\u4f1a\u77e5\u9053\u7684\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u4e5f\u662f\u5728\u7b49\u4ed6\u6697\u5668\u53d1\u51fa\u4e4b\u540e\u624d\u77e5\u9053\u2014\u2014 \u3000\u3000\u7530\u4e03\u5fae\u7b11\u9053\uff1a\u90a3\u4e48\uff0c\u8fd9\u4f4d\u5c11\u5e74\u670b\u53cb\u96be\u9053\u662f\u72d7\uff0c\u4e00\u76f4\u5728\u77aa\u7740\u4ed6\u7684\u5634\u4e48\uff1f \u7b2c\u5341\u56db\u7ae0\u3000\u6709\u53e3\u96be\u8a00>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u6709\u53e3\u96be\u8a00 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u7530\u4e03\u7237\u96be\u9053\u8fd8\u672a\u770b\u51fa\u4ed6\u8eab\u4e0a\u7a7f\u4e86\u91d1\u4e1d\u7532\uff1f \u3000\u3000\u7530\u4e03\u773c\u775b\u4e00\u4eae\uff0c\u629a\u638c\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u5c31\u96be\u602a\u6469\u4e91\u5144\u65b9\u624d\u6253\u4eba\u53cd\u800c\u81ea\u5df1\u624b\u75db\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4eca\u5929\u6211\u672c\u6765\u4e0d\u51c6\u5907\u5230\u51b7\u9999\u5c0f\u7b51\u53bb\u7684\uff0c\u4f46\u5230\u4e86\u665a\u4e0a\uff0c\u6211\u5ffd\u7136\u60f3\u8d77\u5fd8\u62ff\u4ef6\u4e1c\u897f\uff0c \u4f46\u6211\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u4e00\u56de\u5230\u51b7\u9999\u5c0f\u7b51\uff0c\u6885\u82b1\u76d7\u5c31\u53d1\u73b0\u4e86\u3002 \u3000\u3000\u5979\u7f8e\u4e3d\u7684\u9762\u5e9e\u4e0a\u9732\u51fa\u4e86\u6050\u60e7\u4e4b\u8272\uff0c\u9053\uff1a\u4e25\u683c\u8bf4\u6765\uff0c\u90a3\u65f6\u6211\u5e76\u6ca1\u6709\u770b\u5230\u4ed6\uff0c\u53ea\u89c9\u6709\u4e2a\u4eba\u5ffd \u7136\u5230\u4e86\u6211\u8eab\u540e\uff0c\u6211\u60f3\u8f6c\u8eab\uff0c\u4ed6\u5df2\u70b9\u4f4f\u4e86\u6211\u7684\u7a74\u9053\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4eba\u7684\u8f7b\u529f\u4e5f\u4e0d\u9519\uff01 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4ed6\u8eab\u6cd5\u7b80\u76f4\u548c\u9b3c\u9b45\u4e00\u6837\uff0c\u6211\u7cca\u91cc\u7cca\u6d82\u5730\u5c31\u88ab\u4ed6\u631f\u5728\u8098\u4e0b\uff0c\u817e\u4e91\u9a7e \u96fe\u822c\u88ab\u4ed6\u631f\u4e86\u51fa\u53bb\uff0c\u90a3\u65f6\u6211\u5df2\u60f3\u5230\u4ed6\u5c31\u662f\u6885\u82b1\u76d7\uff0c\u5c31\u95ee\u4ed6\uff1a\u4f60\u60f3\u5c06\u6211\u600e\u6837\uff1f\u4e3a\u4f55\u4e0d\u6740\u6211\uff01 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ed6\u600e\u4e48\u8bf4\uff1f \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u4ed6\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u53ea\u662f\u9634\u68ee\u68ee\u5730\u7b11\u3002 \u3000\u3000\u7530\u4e03\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u539f\u6765\u4ed6\u5e76\u6ca1\u6709\u544a\u8bc9\u4f60\u4ed6\u5c31\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4ed6\u7528\u4e0d\u7740\u544a\u8bc9\u6211\uff0c\u90a3\u65f6\u6211\u53ea\u60f3\u65e9\u4e9b\u6b7b\u4e86\u7b97\u4e86\uff0c\u4f46\u5168\u8eab\u504f\u504f\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709 \uff0c\u5c31\u5728\u90a3\u65f6\u5019\uff0c\u6211\u7a81\u7136\u89c1\u5230\u4eba\u5f71\u4e00\u95ea\uff0c\u51fa\u73b0\u5728\u6211\u4eec\u9762\u524d\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u6765\u7684\u4eba\u60f3\u5fc5\u5c31\u662f\u8fd9\u4f4d\u5c11\u5e74\u670b\u53cb\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e0d\u9519\uff0c\u5c31\u662f\u4ed6\u3002 \u3000\u3000\u5979\u779f\u4e86\u963f\u98de\u4e00\u773c\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u6e29\u67d4\u611f\u6fc0\u4e4b\u8272\uff0c\u9053\uff1a\u4ed6\u6765\u5f97\u5b9e\u5728\u592a\u5feb\u4e86\uff0c\u6885\u82b1\u76d7\u4f3c\u4e5f\u5403\u4e00 \u60ca\uff0c\u7acb\u523b\u5c06\u6211\u629b\u5728\u5730\u4e0a\uff0c\u6211\u5c31\u542c\u5230\u4ed6\u8bf4\uff1a\u4f60\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f\u53c8\u542c\u5230\u6885\u82b1\u76d7\u8bf4\uff1a\u662f\u53c8\u600e\u6837\uff1f\u4e0d \u662f\u53c8\u600e\u6837\uff1f\u4f60\u53cd\u6b63\u5df2\u662f\u5feb\u6b7b\u7684\u4eba\u4e86\u2014\u2014 \u3000\u3000\u4ed6\u7684\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u5ffd\u7136\u6709\u4e00\u84ec\u4e4c\u661f\u81ea\u4ed6\u5634\u91cc\u5c04\u4e86\u51fa\u6765\uff0c\u6211\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u5bb3\u6015\uff0c\u773c\u89c1\u7740 \u4e4c\u5149\u5168\u90fd\u5c04\u5728\u8fd9\u2014\u2014\u8fd9\u4f4d\u516c\u5b50\u8eab\u4e0a\uff0c\u6211\u53ea\u5f53\u4ed6\u4e5f\u8981\u548c\u522b\u4eba\u4e00\u6837\uff0c\u6b7b\u5728\u6885\u82b1\u76d7\u7684\u624b\u91cc\u4e86\uff0c\u8c01\u77e5 \u4ed6\u7adf\u8fde\u4e00\u70b9\u4e8b\u90fd\u6ca1\u6709\u2014\u2014 \u3000\u3000\u63a5\u7740\uff0c\u6211\u5c31\u89c1\u5230\u5251\u5149\u4e00\u95ea\uff0c\u6885\u82b1\u76d7\u5c31\u5012\u4e86\u4e0b\u53bb\uff0c\u90a3\u4e00\u5251\u51fa\u624b\u4e4b\u5feb\uff0c\u6211\u5b9e\u5728\u6ca1\u6cd5\u5b50\u5f62\u5bb9\u5f97 \u51fa\u3002 \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4e0d\u7981\u77aa\u5927\u4e86\u773c\u775b\u53bb\u77a7\u963f\u98de\u8170\u5e26\u4e0a\u7684\u90a3\u67c4\u5251\uff0c\u8c01\u4e5f\u4e0d\u76f8\u4fe1\u8fd9\u4e48\u6837\u7684 \u4e00\u67c4\u5251\u80fd\u6740\u5f97\u6b7b\u4eba\uff0c\u80fd\u6740\u5f97\u6b7b\u6885\u82b1\u76d7\uff01 \u3000\u3000\u7530\u4e03\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u4e5f\u5728\u51dd\u6ce8\u7740\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u4ed6\u5634\u89d2\u5ffd\u53c8\u9732\u51fa\u4e86\u5fae\u7b11\uff0c\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u9601\u4e0b\u83ab\u975e\u65e9\u5df2\u7b49\u5728\u90a3\u91cc\u4e86\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u7530\u4e03\u5fae\u7b11\u9053\uff1a\u9601\u4e0b\u4e00\u89c1\u5230\u4ed6\u4eec\uff0c\u5c31\u98de\u8eab\u8fc7\u53bb\u6321\u4f4f\u4e86\u4ed6\uff0c\u5c31\u95ee\u4ed6\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u7530\u4e03\u5fae\u7b11\u9053\uff1a\u96be\u9053\u9601\u4e0b\u603b\u662f\u5b88\u4faf\u5728\u6697\u4e2d\uff0c\u4e00\u89c1\u5230\u591c\u884c\u4eba\uff0c\u5c31\u8fc7\u53bb\u95ee\u4ed6\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u8fd8\u6ca1\u90a3\u4e48\u591a\u529f\u592b\u3002 \u3000\u3000\u7530\u4e03\u5fae\u7b11\u9053\uff1a\u9601\u4e0b\u82e5\u662f\u5076\u5c14\u6709\u529f\u592b\u65f6\uff0c\u5076\u5c14\u9047\u89c1\u4e86\u4e2a\u591c\u884c\u4eba\uff0c\u4f1a\u5982\u4f55\u95ee\u4ed6\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u4e3a\u4f55\u8981\u95ee\u4ed6\uff1f\u4ed6\u662f\u8c01\u4e0e\u6211\u4f55\u5173\uff1f \u3000\u3000\u7530\u4e03\u5ffd\u7136\u4e00\u62cd\u5df4\u638c\uff0c\u7b11\u9053\uff1a\u8fd9\u5c31\u5bf9\u4e86\uff0c\u9601\u4e0b\u7eb5\u7136\u8981\u95ee\uff0c\u4e5f\u53ea\u4f1a\u95ee\u4ed6\u662f\u8c01\uff1f\u8b6c\u5982\u8bf4\uff0c\u9601\u4e0b \u65b9\u624d\u95ee\u516c\u5b59\u6469\u4e91\u65f6\uff0c\u4e5f\u53ea\u95ee\uff1a\u4f60\u662f\u8c01\uff1f\u5e76\u6ca1\u6709\u95ee\uff1a\u4f60\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u660e\u77e5\u4ed6\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u4e3a\u4f55\u8981\u5982\u6b64\u95ee\u4ed6\uff1f \u3000\u3000\u7530\u4e03\u5ffd\u7136\u6c89\u4e0b\u8138\uff0c\u6307\u7740\u5730\u4e0a\u7684\u6b7b\u4eba\u9053\uff1a\u90a3\u4e48\uff0c\u9601\u4e0b\u4e3a\u4f55\u8981\u5982\u6b64\u95ee\u8fd9\u4eba\u5462\uff1f\u96be\u9053\u9601\u4e0b\u65e9\u5df2 \u77e5\u9053\u4ed6\u5c31\u662f\u6885\u82b1\u76d7\uff1f\u9601\u4e0b\u65e2\u5df2\u77e5\u9053\u4ed6\u5c31\u662f\u6885\u82b1\u76d7\uff0c\u4e3a\u4f55\u8fd8\u8981\u95ee\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u53ea\u56e0\u5df2\u6709\u4eba\u544a\u8bc9\u6211\uff0c\u6885\u82b1\u76d7\u8fd9\u4e24\u5929\u5fc5\u5b9a\u4f1a\u5728\u90a3\u9644\u8fd1\u51fa\u73b0\u3002 \u3000\u3000\u7530\u4e03\u773c\u775b\u7785\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u9053\uff1a\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u662f\u6885\u82b1\u76d7\u81ea\u5df1\uff1f\u8fd8\u662f\u6885\u82b1\u76d7\u7684\u670b\u53cb\uff1f \u3000\u3000\u4ed6\u4f3c\u4e4e\u660e\u77e5\u963f\u98de\u7edd\u4e0d\u4f1a\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u53ea\u8981\u95ee\u51fa\u8fd9\u53e5\u8bdd\uff0c\u76ee\u7684\u4fbf\u5df2\u8fbe\u5230\uff0c\u4e5f\u6839 \u672c\u4e0d\u9700\u522b\u4eba\u56de\u7b54\u3002 \u3000\u3000\u5927\u5bb6\u542c\u4e86\u8fd9\u8bdd\uff0c\u773c\u775b\u4e0d\u7ea6\u800c\u540c\u5728\u963f\u98de\u548c\u674e\u5bfb\u6b22\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5fc3\u91cc\u5df2\u90fd\u8ba4\u5b9a\u53ea\u4e0d\u8fc7\u662f\u674e\u5bfb\u6b22 \u548c\u4ed6\u4e32\u901a\u597d\u7684\u5708\u5957\uff0c\u65e0\u8bba\u963f\u98de\u518d\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u518d\u76f8\u4fe1\u5730\u4e0a\u8fd9\u6b7b\u4eba\u771f\u662f\u6885\u82b1\u76d7\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u7530\u4e03\u5ffd\u7136\u8f6c\u8eab\u8d70\u5230\u4e00\u4e2a\u9526\u8863\u5c11\u5e74\u9762\u524d\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u90a3\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u5450\u5450\u9053\uff1a\u6211\u2014\u2014\u6211\u600e\u4f1a\u662f\u4ed6\u2014\u2014\uff0d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7530\u4e03\u5ffd\u7136\u51fa\u624b\u70b9\u4f4f\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5583\u5583\u9053\uff1a\u597d\u5bb6\u4f19\uff0c\u53c8\u6709\u4e2a\u6885\u82b1\u76d7\u88ab\u6211\u6349\u4f4f\u4e86 \u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\u6765\u4e00\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u5404\u4f4d\u53ea\u6015\u4e5f\u60f3\u4e0d\u5230\u6349\u62ff\u6885\u82b1\u76d7\u7adf\u5982\u6b64\u5bb9\u6613\u5427\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u4e0d\u7981\u653e\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u7eb7\u7eb7\u7740\u9053\uff1a\u4f60\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u6211\u770b\u4f60\u624d\u662f\u6885\u82b1\u76d7\uff01 \u3000\u3000\u6885\u82b1\u76d7\u600e\u5730\u8d8a\u6765\u8d8a\u591a\u4e86\uff1f \u3000\u3000\u963f\u98de\u94c1\u9752\u7740\u8138\uff0c\u624b\u5df2\u7f13\u7f13\u89e6\u53ca\u5251\u67c4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u8fd8\u662f\u8d70\u5427\uff01 \u3000\u3000\u963f\u98de\u76ee\u5149\u95ea\u52a8\u9053\uff1a\u8d70\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u6709\u7530\u4e03\u7237\u548c\u8d75\u5927\u7237\u8fd9\u6837\u7684\u5927\u4fa0\u5728\u8fd9\u91cc\uff0c\u600e\u80af\u5c06\u6885\u82b1\u76d7\u7ed9\u4f60\u8fd9\u521d\u51fa\u8305\u5e90\u7684 \u5c11\u5e74\u4eba\u6740\u6b7b\uff1f\u4f60\u65e0\u8bba\u518d\u8bf4\u4ec0\u4e48\uff0c\u90fd\u6ca1\u6709\u7528\u7684\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u7d27\u63e1\u7740\u5251\u67c4\uff0c\u51b7\u51b7\u9053\uff1a\u6211\u4e5f\u4e0d\u60f3\u518d\u8ddf\u8fd9\u79cd\u4eba\u8bf4\u8bdd\u4e86\uff0c\u53ef\u662f\u6211\u7684\u5251\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u5c31\u7b97\u5c06\u4ed6\u4eec\u5168\u90fd\u6740\u4e86\u4e5f\u6ca1\u6709\u7528\uff0c\u8fd8\u662f\u6ca1\u6709\u4eba\u4f1a\u627f\u8ba4\u4f60\u6740\u4e86\u6885\u82b1\u76d7\uff0c\u8fd9\u9053\u7406 \u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\u4e48\uff1f \u3000\u3000\u963f\u98de\u53d1\u4eae\u7684\u773c\u775b\u6e10\u6e10\u53d8\u6210\u7070\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u660e\u767d\u4e86\uff0c\u6211\u660e\u767d\u4e86\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u4f60\u82e5\u60f3\u6210\u540d\uff0c\u6700\u597d\u5148\u660e\u767d\u8fd9\u9053\u7406\uff0c\u5426\u5219\u4f60\u5c31\u4f1a\u50cf\u6211\u4e00\u6837\uff0c\u8fdf\u65e9\u8fd8\u662f\u8981 \u53d8\u6210\u6885\u82b1\u76d7\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u6211\u82e5\u60f3\u6210\u540d\uff0c\u6700\u597d\u5148\u5b66\u4f1a\u542c\u8bdd\uff0c\u662f\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u53ea\u8981\u4f60\u80af\u5c06\u51fa\u98ce\u5934\u7684\u4e8b\u90fd\u8ba9\u7ed9\u8fd9\u4e9b\u5927\u4fa0\u4eec\uff0c\u8fd9\u4e9b\u5927\u4fa0\u4eec\u5c31\u4f1a\u8ba4 \u4e3a\u4f60\u5c11\u5e74\u8001\u6210\uff0c\u662f\u4e2a\u53ef\u9020\u4e4b\u624d\uff0c\u518d\u8fc7\u4e2a\u5341\u5e74\u4e8c\u5341\u5e74\uff0c\u7b49\u5230\u8fd9\u4e9b\u5927\u4fa0\u4eec\u90fd\u8fdb\u4e86\u68fa\u6750\uff0c\u5c31\u4f1a\u8f6e\u5230 \u4f60\u6210\u540d\u4e86\u3002 \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u8fd9\u7b11\u5bb9\u770b\u6765\u662f\u90a3\u4e48\u6f47\u6d12\uff0c\u5374\u53c8\u662f\u90a3\u4e48\u5bc2\u5bde\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u5982\u6b64\u770b\u6765\uff0c\u6211\u53ea\u63e1\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u6210\u540d\u7684\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u5012\u4e5f\u672a\u5c1d\u4e0d\u662f\u597d\u4e8b\u3002 \u3000\u3000\u770b\u5230\u963f\u98de\u7684\u5fae\u7b11\uff0c\u674e\u5bfb\u6b22\u7684\u7b11\u5bb9\u5c31\u66f4\u5f00\u6717\u4e86\uff0c\u4ed6\u4eec\u7b11\u5f97\u5c31\u50cf\u662f\u6b63\u5728\u8bf4\u7740\u4e16\u4e0a\u6700\u6709\u8da3\u7684\u4e8b \u3002 \u3000\u3000\u5927\u5bb6\u6b63\u5728\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e24\u6709\u4ec0\u4e48\u6bdb\u75c5\uff0c\u8c01\u77e5\u5ffd\u7136\u95f4\u963f\u98de\u5df2\u5230\u4e86\u674e\u5bfb\u6b22\u8eab\u65c1\uff0c\u633d\u8d77\u674e\u5bfb \u6b22\u7684\u624b\uff0c\u9053\uff1a\u6210\u540d\u4e5f\u7f62\uff0c\u4e0d\u6210\u540d\u4e5f\u7f62\uff0c\u4f60\u6211\u4eca\u65e5\u76f8\u89c1\uff0c\u597d\u6b79\u603b\u5f97\u559d\u676f\u9152\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u559d\u9152\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u63a8\u8f9e\u8fc7\u7684\uff0c\u53ea\u4e0d\u8fc7\u4eca\u65e5\u2014\u2014 \u3000\u3000\u7530\u4e03\u5fae\u7b11\u7740\u9053\uff1a\u4eca\u65e5\u4ed6\u53ea\u6015\u662f\u4e0d\u80fd\u5949\u966a\u7684\u4e86\u3002 \u3000\u3000\u963f\u98de\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u8c01\u8bf4\u7684\uff1f \u3000\u3000\u7530\u4e03\u5fae\u7b11\u7740\u6325\u4e86\u6325\u624b\uff0c\u5927\u5385\u5916\u5c31\u7acb\u523b\u6709\u4e24\u4e2a\u5927\u6c49\u6251\u4e86\u8fdb\u6765\uff0c\u4e00\u4eba\u5389\u58f0\u9053\uff1a\u662f\u7530\u4e03\u7237\u8bf4\u7684 \uff0c\u7530\u4e03\u7237\u8bf4\u7684\u8bdd\uff0c\u5c31\u662f\u547d\u4ee4\uff01 \u3000\u3000\u53e6\u4e00\u4eba\u8f83\u9ad8\u8f83\u7626\uff0c\u559d\u9053\uff1a\u8c01\u82e5\u6562\u8fdd\u6297\u7530\u4e03\u7237\u7684\u547d\u4ee4\uff0c\u8c01\u5c31\u5f97\u6b7b\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u867d\u7136\u4e00\u76f4\u5782\u624b\u7ad9\u5728\u5385\u5916\uff0c\u5b9b\u5982\u5974\u4ec6\uff0c\u4f46\u6b64\u523b\u8eab\u5f62\u5c55\u52a8\u5f00\u6765\uff0c\u7adf\u662f\u77eb\u5065\uff0c\u5728\u6c5f\u6e56\u4e2d \u5df2\u53ef\u7b97\u662f\u4e00\u6d41\u8eab\u624b\u3002 \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4e24\u67c4\u94a2\u5200\u5df2\u5316\u4e3a\u4e24\u9053\u98de\u8679\uff0c\u5e26\u7740\u51cc\u5389\u7684\u5200\u98ce\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u4e00\u4e0a\u4e00\u4e0b\uff0c\u95ea\u7535\u822c\u5411 \u963f\u98de\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u963f\u98de\u51b7\u51b7\u5730\u77a7\u7740\u4ed6\u4eec\u51fa\u624b\uff0c\u4eff\u4f5b\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u4f46\u5ffd\u7136\u95f4\uff0c\u5bd2\u5149\u95ea\uff0c\u518d\u4e00\u95ea\uff0c\u63a5\u7740\u5c31\u662f \u4e24\u58f0\u60ca\u547c\uff0c\u4e24\u9053\u5200\u5149\u5ffd\u7136\u51b2\u5929\u98de\u8d77\uff0c\u593a\u7684\uff0c\u540c\u65f6\u9489\u5165\u5927\u5385\u7684\u6a2a\u6881\u4e0a\uff0c\u4e24\u4e2a\u5927\u6c49\u5de6\u624b\u7d27\u63e1\u7740\u53f3 \u8155\uff0c\u9762\u4e0a\u5df2\u75bc\u5f97\u53d8\u4e86\u989c\u8272\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u4e00\u4e1d\u9c9c\u8840\u81ea\u638c\u7f1d\u95f4\u6c81\u51fa\uff0c\u6ef4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u518d\u770b\u963f\u98de\u7684\u5251\uff0c\u4ecd\u5728\u8170\u5e26\u4e0a\uff0c\u8c01\u4e5f\u6ca1\u6709\u770b\u6e05\u4ed6\u662f\u5426\u62d4\u51fa\u8fc7\u8fd9\u67c4\u5251\uff0c\u4f46\u5374\u90fd\u5df2\u770b\u6e05\u5251\u5c16\u4e0a \u51dd\u7ed3\u7740\u7684\u4e00\u70b9\u9c9c\u8840\u3002 \u3000\u3000\u597d\u5feb\u7684\u5251\uff01 \u3000\u3000\u7530\u4e03\u9762\u4e0a\u7684\u7b11\u5bb9\u4e5f\u51dd\u7ed3\u4f4f\u4e86\u3002 \u3000\u3000\u963f\u98de\u6de1\u6de1\u9053\uff1a\u7530\u4e03\u7237\u7684\u8bdd\u662f\u547d\u4ee4\uff0c\u53ea\u53ef\u60dc\u6211\u7684\u5251\u5374\u542c\u4e0d\u61c2\u4efb\u4f55\u4eba\u7684\u547d\u4ee4\uff0c\u5b83\u53ea\u4f1a\u6740\u4eba\u3002 \u3000\u3000\u4e24\u5927\u6c49\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u60ca\u60e7\u4e4b\u8272\uff0c\u53c8\u5012\u9000\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u8f6c\u8eab\u593a\u95e8\u800c\u51fa\uff0c\u5229\u5251\u867d\u4e0d\u4f1a\u8bf4\u8bdd\uff0c \u4f46\u5374\u6bd4\u4e16\u4e0a\u4efb\u4f55\u4eba\u7684\u547d\u4ee4\u90fd\u6709\u6548\u3002 \u3000\u3000\u963f\u98de\u53c8\u633d\u8d77\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u9053\uff1a\u8d70\u5427\uff0c\u559d\u9152\u53bb\uff0c\u6211\u4e0d\u4fe1\u8fd8\u6709\u4eba\u6562\u6765\u62e6\u6211\u4eec\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u672a\u8bf4\u8bdd\uff0c\u9f99\u5578\u4e91\u8fd8\u5ffd\u7136\u55c4\u58f0\u9053\uff1a\u4f60\u8981\u4ed6\u8d70\uff0c\u4e3a\u4f55\u4e0d\u89e3\u4ed6\u7684\u7a74\u9053\uff1f \u3000\u3000\u963f\u98de\u5634\u89d2\u7684\u808c\u8089\u4eff\u4f5b\u8df3\u4e86\u8df3\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u5bfb\u6b22\u7684\u5fc3\u4e5f\u8df3\u4e86\u8df3\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u90a3\u5929\u7684\u4e8b \u2014\u2014 \u3000\u3000\u90a3\u5929\uff0c\u963f\u98de\u4e3a\u4ed6\u64d2\u4f4f\u4e86\u6d2a\u6c49\u6c11\uff0c\u7559\u5728\u5b59\u8fbe\u7684\u53a8\u623f\u91cc\uff0c\u8fd8\u5c06\u5c06\u6d2a\u6c49\u6c11\u53cd\u7ed1\u5728\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u90a3\u5929\uff0c\u674e\u5bfb\u6b22\u5c31\u5df2\u5728\u5947\u602a\uff0c\u963f\u98de\u4e3a\u4f55\u4e0d\u7d22\u6027\u70b9\u4f4f\u8fd9\u4eba\u7684\u7a74\u9053\uff1f\u73b0\u5728\u4ed6\u5fc3\u5ff5\u4e00\u95ea\uff0c\u987f\u65f6\u604d \u7136\uff01 \u3000\u3000\u8fd9\u5feb\u5251\u65e0\u53cc\u7684\u5c11\u5e74\uff0c\u7adf\u4e0d\u4f1a\u70b9\u7a74\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\uff0c\u4f46\u9762\u4e0a\u5374\u4e0d\u52a8\u58f0\u8272\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u4eca\u5929\u6211\u8bf7\u4e0d\u8d77\u4f60\u559d\u9152\uff01 \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u624d\u4e00\u5b57\u4e00\u5b57\u9053\uff1a\u6211\u8bf7\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u662f\u6211\u81ea\u5df1\u4e70\u6765\u7684\u9152\uff0c\u6211\u4e5f\u7edd\u4e0d\u559d\u7684\u3002 \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u4ed6\uff0c\u51b7\u6f20\u7684\u76ee\u5149\u4e2d\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u674e\u5bfb\u6b22\u8fd9\u662f\u4e0d\u613f\u4ed6\u5192\u9669\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e2\u4e0d\u80fd\u89e3\u5f00\u674e\u5bfb\u6b22\u7684\u7a74\u9053\uff0c\u5c31\u53ea\u6709\u5c06\u674e\u5bfb\u6b22\u80cc\u51fa\u53bb\uff0c\u4ed6\u82e5\u5c06\u674e\u5bfb\u6b22\u80cc\u5728\u8eab\u4e0a\uff0c\u5c31 \u672a\u5fc5\u80fd\u51b2\u5f97\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u7530\u4e03\u76ee\u5149\u95ea\u52a8\uff0c\u5728\u4ed6\u4eec\u8138\u4e0a\u641c\u7d22\u7740\uff0c\u5ffd\u7136\u5fae\u7b11\u9053\uff1a\u674e\u5bfb\u6b22\u662f\u597d\u6c49\u5b50\uff0c\u7edd\u4e0d\u80af\u7275\u7d2f\u522b\u4eba\u7684 \uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd8\u662f\u81ea\u5df1\u8d70\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77e5\u9053\u8fd9\u8001\u72d0\u72f8\u5df2\u770b\u51fa\u4e86\u963f\u98de\u7684\u5f31\u70b9\uff0c\u7acb\u523b\u4e5f\u5fae\u7b11\u9053\uff1a\u4f60\u7528\u4e0d\u7740\u6fc0\u4ed6\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u4e0a \u4f60\u5f53\u7684\uff0c\u4fdd\u51b5\uff0c\u5c31\u7b97\u4ed6\u5c06\u6211\u80cc\u5728\u8eab\u4e0a\uff0c\u4f60\u4eec\u4e5f\u672a\u5fc5\u662f\u4ed6\u7684\u5bf9\u624b\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u4fdd\u51b5\uff0c\u4f60\u4eec\u4e5f\u77e5\u9053\u6211\u6839\u672c\u4e0d\u4f1a\u8d70\u7684\uff0c\u4eca\u5929\u6211\u82e5\u8d70\u4e86\uff0c\u4f60\u4eec\u8fd9\u4e9b\u5927\u4fa0\u5c82\u975e\u66f4 \u54ac\u5b9a\u4e86\u6211\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u4ed6\u8fd9\u8bdd\u81ea\u7136\u662f\u8bf4\u7ed9\u963f\u98de\u542c\u7684\u3002 \u3000\u3000\u963f\u98de\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u4ed6\u4eec\u8bf4\u4f60\u662f\u6885\u82b1\u76d7\uff0c\u4f60\u5c31\u662f\u6885\u82b1\u76d7\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u6709\u4e9b\u4eba\u8bf4\u7684\u8bdd\uff0c\u548c\u653e\u5c41\u4e5f\u76f8\u5dee\u65e0\u51e0\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u65e2\u7136\u662f\u653e\u5c41\uff0c\u4f60\u53c8\u4f55\u5fc5\u518d\u7ba1\u4ed6\u4eec\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u7a81\u7136\u4e00\u4fef\u8eab\uff0c\u5c06\u674e\u5bfb\u6b22\u80cc\u5728\u80cc\u4e0a\uff0c\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u7530\u4e03\u8d1f\u7740\u7684\u53cc\u624b\u5ffd\u7136\u4f38\u51fa\uff0c\u53ea\u89c1\u68cd\u5f71 \u70b9\u70b9\uff0c\u4e00\u51fa\u624b\u5c31\u70b9\u5411\u963f\u98de\u524d\u80f8\u5341\u4e00\u5927\u7a74\uff0c\u53ea\u8981\u88ab\u4ed6\u7684\u85e4\u68cd\u78b0\u7740\u4e00\u70b9\uff0c\u963f\u98de\u5c31\u518d\u4e5f\u4f11\u60f3\u51fa\u624b\u4e86 \uff01 \u3000\u3000\u963f\u98de\u5e76\u6ca1\u6709\u62d4\u5251\uff01 \u3000\u3000\u4ed6\u4e5f\u548c\u674e\u5bfb\u6b22\u4e00\u6837\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u7edd\u4e0d\u7a7a\u56de\u3002 \u3000\u3000\u4f46\u6b64\u523b\u4ed6\u7684\u5251\u5374\u5df2\u6ca1\u6709\u4f24\u4eba\u7684\u628a\u63e1\u3002 \u3000\u3000\u5927\u5bb6\u671b\u7740\u963f\u98de\u5728\u7530\u4e03\u7684\u68cd\u5f71\u4e2d\u95ea\u52a8\uff0c\u8fd8\u5728\u72b9\u7591\u7740\uff0c\u7530\u4e03\u7684\u85e4\u68cd\u70b9\u7a74\u867d\u662f\u6c5f\u6e56\u4e00\u7edd\uff0c\u4f46\u5374 \u5e76\u672a\u80fd\u5236\u4f4f\u8fd9\u5c11\u5e74\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u9053\uff1a\u6740\u6b7b\u6885\u82b1\u76d7\uff0c\u53ef\u662f\u5929\u5927\u7684\u5149\u5f69\uff0c\u8fd9\u673a\u4f1a\u5404\u4f4d\u4f55\u5fc5\u9519\u8fc7\uff1f \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5df2\u6709\u4e03\u516b\u4ef6\u5175\u5203\u4e00\u9f50\u5411\u963f\u98de\u80cc\u540e\u7684\u674e\u5bfb\u6b22\u5288\u4e86\u4e0b\u53bb\uff0c\u6797\u4ed9\u513f\u51b2\u8fc7\u53bb\u62c9\u4f4f \u9f99\u5578\u4e91\u7684\u624b\uff0c\u9053\uff1a\u56db\u54e5\uff0c\u4f60\u4e3a\u4f55\u4e0d\u62e6\u4f4f\u4ed6\u4eec\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9eef\u7136\u9053\uff1a\u4f60\u96be\u9053\u672a\u770b\u51fa\u6211\u4e5f\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u4e00\u8fde\u4e32\u60e8\u547c\u58f0\u54cd\u8d77\uff0c\u4e09\u4e2a\u4eba\u8e09\u8dc4\u5012\u9000\u3002 \u3000\u3000\u963f\u98de\u7684\u5251\u7ec8\u4e8e\u5df2\u51fa\u624b\uff01 \u3000\u3000\u4ed6\u7684\u5251\u6b64\u523b\u867d\u65e0\u628a\u63e1\u80fd\u4f24\u7530\u4e03\uff0c\u4f46\u522b\u4eba\u8981\u6765\u9001\u6b7b\uff0c\u4ed6\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff0c\u53ea\u89c1\u9c9c\u8840\u968f\u7740\u5251\u5149\u6fc0 \u51fa\u53bb\uff0c\u674e\u5bfb\u6b22\u7684\u8c82\u88d8\u4e0a\u5df2\u67d3\u4e0a\u4e86\u8840\u82b1\u3002 \u3000\u3000\u6240\u6709\u7684\u5175\u5203\u7acb\u523b\u53c8\u5168\u4e0d\u89c1\u4e86\uff0c\u53ea\u6709\u7530\u4e03\u7684\u4e00\u6761\u85e4\u68d2\uff0c\u4ecd\u6bd2\u86c7\u822c\u7f20\u4f4f\u4ed6\u4eec\uff0c\u6bcf\u4e00\u62db\u90fd\u4e0d\u79bb \u963f\u98de\u7684\u8981\u7a74\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6bd5\u7adf\u662f\u8d75\u5927\u7237\u4fa0\u4e49\u65e0\u53cc\uff0c\u7edd\u4e0d\u80af\u4ee5\u591a\u4e3a\u80dc\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u76ee\u5149\u4e00\u95ea\uff0c\u51b7\u51b7\u9053\uff1a\u53ea\u4e0d\u8fc7\u8001\u592b\u5df2\u8bf4\u8fc7\uff0c\u5bf9\u6885\u82b1\u76d7\u8fd9\u79cd\u4eba\u8bb2\u6c5f\u6e56\u9053\u4e49\u4e5f\u65e0\u7528\uff01 \u3000\u3000\u4ed6\u4e00\u6b65\u7a9c\u5230\u5385\u4fa7\uff0c\u81ea\u5175\u5668\u67b6\u4e0a\u6284\u4e86\u67c4\u957f\u67aa\uff0c\u968f\u624b\u4e00\u6296\uff0c\u5c31\u6296\u8d77\u4e86\u6597\u5927\u7684\u67aa\u82b1\uff0c\u76f4\u523a\u674e\u5bfb \u6b22\u80cc\u810a\u3002 \u3000\u3000\u94c1\u9762\u65e0\u79c1\u8d75\u4e49\u5728\u6b66\u6797\u4e2d\u80fd\u4eab\u5927\u540d\uff0c\u5012\u4e5f\u5e76\u975e\u5168\u662f\u6cbd\u540d\u9493\u8a89\uff0c\u8fd9\u67c4\u957f\u67aa\u4e00\u65bd\u5c55\u5f00\u6765\uff0c\u786e\u6709 \u6444\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u67aa\u4e43\u767e\u5175\u4e4b\u7c97\uff0c\u68cd\u4e43\u767e\u5175\u4e4b\u738b\uff0c\u4f55\u51b5\u4e00\u5bf8\u957f\uff0c\u4e00\u5bf8\u5f3a\uff0c\u963f\u98de\u4ee5\u4e00\u67c4\u77ed\u5251\uff0c\u5468\u65cb\u5728\u8fd9\u4e24\u6837 \u81f3\u5f3a\u81f3\u9738\u7684\u5175\u5203\u95f4\uff0c\u5df2\u662f\u5403\u4e8f\u4e0d\u5c11\uff0c\u66f4\u4f55\u51b5\u4ed6\u8eab\u540e\u8fd8\u80cc\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u7530\u4e03\u4ee5\u5df2\u4e4b\u957f\uff0c\u51fb\u4eba\u4e4b\u77ed\uff0c\u672c\u5df2\u5360\u5c3d\u5148\u673a\uff0c\u4f46\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u90a3\u6700\u540e\u4e00\u51fb\uff0c\u603b\u662f\u5dee\u4e86\u4e00\u4e9b \uff0c\u603b\u662f\u65e0\u6cd5\u5c06\u5bf9\u65b9\u51fb\u5012\u3002 \u3000\u3000\u6570\u5341\u62db\u8fc7\u540e\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u5c11\u5e74\u867d\u672a\u8fd8\u624b\uff0c\u4f46\u6b65\u6cd5\u4e4b\u795e\u5999\uff0c\u5374\u662f\u81ea\u5df1\u524d\u6240\u672a\u89c1\uff0c\u81ea\u5df1\u6bcf \u62db\u90e8\u4f4d\u529b\u91cf\u660e\u660e\u90fd\u62ff\u5f97\u6070\u5230\u597d\u5904\uff0c\u660e\u660e\u5df2\u53ef\u70b9\u4f4f\u5bf9\u65b9\u7684\u7a74\u9053\uff0c\u4f46\u8fd9\u5c11\u5e74\u811a\u6b65\u4e5f\u4e0d\u77e5\u600e\u4e48\u6837\u4e00 \u6ed1\uff0c\u81ea\u5df1\u8fd9\u4e00\u62db\u5c31\u843d\u7a7a\u4e86\u3002 \u3000\u3000\u7530\u4e03\u867d\u7136\u89c1\u591a\u8bc6\u5e7f\uff0c\u5374\u4e5f\u770b\u4e0d\u900f\u8fd9\u6b65\u6cd5\u7684\u6765\u5386\uff0c\u5f53\u4e0b\u6697\u5fd6\u9053\uff1a\u8fd9\u5c11\u5e74\u7684\u6765\u5934\u5fc5\u5b9a\u4e0d\u5c0f\uff0c \u6211\u53c8\u4f55\u82e6\u591a\u7ed3\u51a4\u5bb6\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u7acb\u523b\u5fae\u7b11\u9053\uff1a\u5c0f\u5144\u5f1f\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u653e\u4e0b\u4ed6\u5427\uff0c\u5426\u5219\u4ed6\u672a\u8fde\u7d2f\u4f60\uff0c\u4f60\u53cd\u5012\u8fde\u7d2f \u4ed6\u4e86\u3002 \u3000\u3000\u963f\u98de\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u4f60\u4eec\u65e2\u7136\u8981\u6211\u653e\u4e0b\u4ed6\uff0c\u81ea\u5df1\u4e3a\u4f55\u4e0d\u4f4f\u624b\uff1f \u3000\u3000\u7530\u4e03\u4e00\u68cd\u70b9\u51fa\uff0c\u4eba\u5df2\u9000\u540e\u4e03\u5c3a\uff0c\u8d75\u6b63\u4e49\u67aa\u5df2\u523a\u51fa\uff0c\u6536\u52bf\u4e0d\u5fc5\uff0c\u7a81\u7136\u6389\u8f6c\u67aa\u5c16\uff0c\u5411\u5730\u4e0a\u523a \u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u94ee\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u67aa\u5c16\u6298\u65ad\uff0c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u963f\u98de\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\uff0c\u5c06\u674e\u5bfb\u6b22\u6276\u5230\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u53ea\u662f\u674e\u5bfb\u6b22\u80f8\u819b\u8d77\u4f0f\uff0c\u82cd\u767d\u7684\u8138 \u4e0a\u53c8\u6cdb\u8d77\u4e00\u79cd\u51c4\u8273\u7684\u7ea2\u8272\uff0c\u663e\u7136\u4e00\u76f4\u5728\u5f3a\u81ea\u5fcd\u8010\u7740\uff0c\u6ca1\u6709\u54b3\u51fa\u6765\uff0c\u53ea\u56e0\u4e3a\u4ed6\u751f\u6015\u54b3\u55fd\u4f1a\u5f71\u54cd \u963f\u98de\u7684\u51fa\u624b\u3002 \u3000\u3000\u963f\u98de\u53ea\u89c9\u80f8\u4e2d\u70ed\u8840\u4e0a\u6d8c\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u9519\u4e86\uff0c\u6211\u53ea\u987e\u81ea\u5df1\u901e\u5f3a\uff0c\u5374\u5fd8\u4e86\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u65e0\u8bba\u4f60\u662f\u5bf9\u662f\u9519\uff0c\u6211\u90fd\u540c\u6837\u611f\u6fc0\u4f60\u3002 \u3000\u3000\u4ed6\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5c31\u4e0d\u505c\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u4ed6\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u7740\u8d75\u6b63\u4e49\u9053\uff1a\u6211\u53ea\u540e\u6094\u4e00\u4ef6\u4e8b\uff0c\u4e0a\u6b21\u6211\u4e3a \u4f55\u4e0d\u6740\u4e86\u4f60\uff01 \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u8bdd\uff0c\u5251\u5df2\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u4e4b\u5feb\uff0c\u7b80\u76f4\u4e0d\u53ef\u601d\u8bae\uff0c\u8d75\u6b63\u4e49\u90a3\u91cc\u8fd8\u6709\u95ea\u907f\u5f97\u5de5\uff0c\u773c\u89c1\u5c31\u8981\u8840\u6e85\u5f53\u5730\uff0c\u5c31\u5728\u8fd9\u65f6 \uff0c\u7a81\u542c\u5927\u5385\u5916\u6709\u4eba\u53e3\u5ba3\u4f5b\u53f7\u201c\u963f\u5f25\u9640\u4f5b\u201d\u8fd9\u56db\u4e2a\u5b57\u53ea\u8bf4\u4e86\u4e00\u4e2a\u5b57\u65f6\uff0c\u5df2\u6709\u4e00\u80a1\u52b2\u98ce\u5e26\u7740\u4e32\u9ed1 \u5f71\u6253\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8bf4\u5230\u7b2c\u4e8c\u4e2a\u5b57\u65f6\uff0c\u52b2\u98ce\u548c\u9ed1\u5f71\u5df2\u5c06\u51fb\u4e0a\u963f\u98de\u7684\u540e\u80cc\uff0c\u963f\u98de\u5251\u52bf\u660e\u660e\u5df2\u7528\u8001\uff0c\u4f46\u5c31\u5728\u8fd9\u523b \u4e0d\u5bb9\u7f13\u7684\u5239\u90a3\u95f4\uff0c\u7a81\u7136\u56de\u5251\u8f6c\u8eab\u3002 \u3000\u3000\u53ea\u542c\u545b\u7684\u4e00\u54cd\uff0c\u5251\u5c16\u6311\u8d77\u4e86\u9ed1\u5f71\uff0c\u7adf\u662f\u4e32\u4f5b\u73e0\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u65f6\u201c\u963f\u5f25\u9640\u4f5b\u201d\u8fd9\u77ed\u77ed\u56db\u4e2a\u5b57\u624d\u8bf4\u5b8c\uff0c\u4f5b\u73e0\u5df2\u88ab\u5251\u5c16\u6311\u98de\uff0c\u4f46\u5251\u5c16\u72b9\u5728\u55e1\u55e1\u4f5c\u54cd \uff0c\u9707\u52a8\u4e0d\u7edd\uff01 \u3000\u3000\u5251\u4ecd\u5728\u9707\u52a8\uff0c\u963f\u98de\u7684\u4eba\u5374\u5982\u82b1\u5c97\u77f3\u822c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u71b9\u5fae\u7684\u6668\u5149\u4e2d\uff0c\u53ea\u89c1\u4e94\u4e2a\u8292\u978b\u767d\u8865\u889c\u7684\u7070\u888d\u50e7\u4eba\u81ea\u5927\u5385\u5916\u7f13\u7f13\u8d70\u4e86\u8fdb\u6765\uff0c\u5f53\u5148\u4e00\u4eba\xd7\u7709 \u4ff1\u5df2\u82cd\u767d\uff0c\u5728\u6668\u5149\u4e2d\u770b\u6765\u5b9b\u5982\u94f6\u4e1d\uff0c\u4fbf\u8138\u4ecd\u662f\u767d\u4e2d\u900f\u7ea2\uff0c\u7ea2\u4e2d\u900f\u767d\uff0c\u4e00\u53cc\u773c\u775b\u66f4\u662f\u76ee\u5149\u70af\u70af \uff0c\u987e\u76fc\u751f\u5a01\u3002 \u3000\u3000\u4ed6\u53cc\u624b\u5408\u4ec0\uff0c\u90a3\u4e32\u73e0\u4e0d\u77e5\u600e\u5730\u53c8\u56de\u5230\u4ed6\u624b\u4e0a\uff0c\u4e24\u53cc\u624b\u5408\u5728\u4e00\u8d77\uff0c\u539a\u5982\u95e8\u677f\uff0c\u663e\u7136\u5df2\u5c06\u4f5b \u5bb6\u638c\u529b\u7ec3\u81f3\u7089\u706b\u7eaf\u9752\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u60ca\u9b42\u521d\u5b9a\uff0c\u89c1\u5230\u8fd9\u767d\u7709\u50e7\u4eba\uff0c\u7acb\u523b\u8eac\u8eab\u9053\uff1a\u4e0d\u77e5\u5927\u5e08\u6cd5\u9a7e\u5149\u4e34\uff0c\u6709\u5931\u8fdc\u8fce\uff0c\u591a\u8bf7 \u6055\u7f6a\u3002 \u3000\u3000\u767d\u7709\u50e7\u4eba\u53ea\u7b11\u4e86\u7b11\uff0c\u76ee\u5149\u5c31\u76ef\u5728\u963f\u98de\u8138\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u8fd9\u4f4d\u6a80\u8d8a\u597d\u5feb\u7684\u5251\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u7684\u5251\u82e5\u4e0d\u5feb\uff0c\u53ea\u6015\u5c31\u8981\u5927\u5e08\u6765\u8d85\u6e21\u4ea1\u9b42\u4e86\u3002 \u3000\u3000\u767d\u7709\u50e7\u4eba\u9053\uff1a\u8001\u50e7\u4e0d\u613f\u6a80\u8d8a\u591a\u9020\u6740\u5b7d\uff0c\u662f\u4ee5\u624d\u51fa\u624b\uff0c\u987b\u77e5\u6a80\u8d8a\u7684\u5251\u867d\u5feb\uff0c\u5374\u4ecd\u5feb\u4e0d\u8fc7\u6211 \u4f5b\u5982\u6765\u7684\u6cd5\u773c\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5927\u5e08\u7684\u4f5b\u73e0\u96be\u9053\u5c31\u80fd\u5feb\u5f97\u8fc7\u5982\u6765\u7684\u6cd5\u773c\u5417\uff1f\u6211\u82e5\u6b7b\u5728\u5927\u5e08\u7684\u4f5b\u73e0\u4e0b\uff0c\u5c82\u975e\u4e5f\u8981 \u591a\u4e00\u91cd\u6740\u5b7d\uff01 \u3000\u3000\u8d75\u6b63\u4e49\u5389\u58f0\u9053\uff1a\u597d\u5927\u80c6\uff0c\u5728\u5c11\u6797\u62a4\u6cd5\u5927\u5e08\u9762\u524d\uff0c\u4f60\u4e5f\u6562\u5982\u6b64\u65e0\u793c\uff1f \u3000\u3000\u767d\u7709\u50e7\u4eba\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u65e0\u59a8\uff0c\u5c11\u5e74\u7684\u53e3\u820c\u672c\u5c31\u5229\u4e8e\u5200\u5251\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u7b11\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u65e2\u7136\u5e76\u4e0d\u602a\u7f6a\uff0c\u4f60\u8fd8\u4e0d\u5feb\u8d70\uff1f \u3000\u3000\u8d75\u6b63\u4e49\u51b7\u51b7\u9053\uff1a\u4ed6\u65b9\u624d\u4e0d\u8d70\uff0c\u6b64\u523b\u60f3\u8d70\u53ea\u6015\u592a\u8fdf\u4e86\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff0c\u4f60\u96be\u9053\u8fd8\u62e6\u5f97\u4f4f\u6211\uff1f \u3000\u3000\u4ed6\u5634\u5507\u8bf4\u7740\u8bdd\uff0c\u5df2\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u9762\u8272\u53c8\u53d8\u4e86\uff0c\u9053\uff1a\u5927\u5e08\u2014\u2014 \u3000\u3000\u7530\u4e03\u62a2\u7740\u7b11\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u7d20\u6765\u6148\u60b2\u4e3a\u6000\uff0c\u600e\u4f1a\u96be\u4e3a\u8fd9\u79cd\u65e0\u77e5\u5c11\u5e74\uff0c\u8ba9\u4ed6\u8d70\u5427\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u76ee\u5149\u95ea\u52a8\uff0c\u6c89\u58f0\u9053\uff1a\u672c\u6d3e\u638c\u95e8\u5e08\u5144\u63a5\u5230\u81ea\u6cd5\u9640\u5bfa\u8f6c\u53bb\u7684\u98de\u9e3d\u4f20\u4e66\uff0c\u77e5\u9053\u672c\u95e8\u4fd7 \u5bb6\u5f1f\u5b50\u79e6\u91cd\u8d1f\u4e86\u91cd\u4f24\uff0c\u7acb\u523b\u5c31\u4ee4\u8001\u50e7\u517c\u7a0b\u8d76\u6765\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u53f9\u4e86\u4e00\u58f0\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u53ef\u60dc\u5927\u5e08\u8fd8\u662f\u6765\u8fdf\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5929\u5df2\u5f88\u4eae\u4e86\uff0c\u8857\u9053\u4e0a\u884c\u4eba\u5df2\u4e0d\u5c11\uff0c\u963f\u98de\u8d70\u5728\u6628\u591c\u7684\u79ef\u96ea\u4e2d\uff0c\u4ed6\u7684\u811a\u5c65\u867d\u8f7b\u5feb\uff0c\u5fc3\u60c5\u5374\u65e0 \u6bd4\u6c89\u91cd\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u5524\u9053\uff1a\u7b49\u4e00\u7b49\u2014\u2014\u7b49\u4e00\u7b49 \u3000\u3000\u8fd9\u58f0\u97f3\u53c8\u6e05\u8106\uff0c\u53c8\u5a07\u7f8e\uff0c\u963f\u98de\u4e0d\u7528\u56de\u5934\uff0c\u5df2\u77e5\u9053\u662f\u8c01\u6765\u4e86\u3002 \u3000\u3000\u53ea\u56e0\u8857\u9053\u4e0a\u7684\u4eba\u90fd\u5df2\u5f20\u5927\u4e86\u773c\u775b\uff0c\u75f4\u75f4\u7684\u671b\u7740\u4ed6\u8eab\u540e\uff0c\u6b63\u5728\u8d70\u8def\u7684\u90fd\u505c\u4e0b\u4e86\u811a\uff0c\u6b63\u8bf4\u8bdd \u7684\u4e5f\u5fd8\u4e86\u81ea\u5df1\u5728\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u56de\u5934\uff0c\u4f46\u4e5f\u4e0d\u7531\u81ea\u4e3b\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u4e00\u9635\u8f7b\u5fae\u7684\u5598\u606f\u58f0\u5230\u4ed6\u8eab\u540e\uff0c\u4e00\u9635\u9189\u4eba\u7684\u9999\u6c14\uff0c\u4e5f\u5df2\u98d8\u5165\u4ed6\u5fc3\u5934\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4e0d\u56de\u5934\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u72b9\u5728\u5598\u606f\u7740\uff0c\u7f8e\u4e3d\u7684\u9762\u5e9e\u4e0a\u5e26\u7740\u6de1\u6de1\u7684\u4e00\u62b9\u6655\u7ea2\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u5374\u4ecd\u51b7\u6f20\u5f97\u5982\u540c\u5730\u4e0a\u7684\u79ef\u96ea\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u5934\uff0c\u7ea2\u7740\u8138\u9053\uff1a\u6211\u662f\u6765\u5411\u4f60\u9053\u8c26\u7684\uff0c\u6211\u2014\u2014\uff0d \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u597d\u9053\u6b49\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u89d2\uff0c\u8f7b\u8f7b\u8dfa\u811a\u9053\uff1a\u4f46\u90a3\u4e9b\u4eba\u5b9e\u5728\u592a\u65e0\u804a\uff0c\u4e5f\u592a\u65e0\u793c\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u90a3\u4e5f\u4e0e\u4f60\u65e0\u5173\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53ef\u662f\u4f60\u6551\u4e86\u6211\uff0c\u6211\u600e\u80fd\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u6551\u4e86\u4f60\uff0c\u4f46\u5374\u6ca1\u6709\u6551\u4ed6\u4eec\uff0c\u6211\u6551\u4f60\uff0c\u4e5f\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u4f60\u66ff\u4ed6\u4eec\u6765\u9053\u6b49\u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u8fd8\u8981\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5b9e\u5728\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u4e86\uff0c\u5979\u8fd9\u4e00\u8f88\u5b50\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u4eba\uff0c\u5979\u603b\u8ba4\u4e3a\u5c31\u7b97\u662f \u51b0\u5c71\uff0c\u5728\u5979\u9762\u524d\u4e5f\u4f1a\u878d\u5316\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u518d\u89c1\u3002 \u3000\u3000\u4ed6\u626d\u5934\u5c31\u8d70\uff0c\u4f46\u521a\u8d70\u4e86\u4e24\u6b65\uff0c\u6797\u4ed9\u513f\u7a81\u53c8\u5524\u9053\uff1a\u7b49\u4e00\u7b49\uff0c\u6211\u8fd8\u6709\u8bdd\u8bf4\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u4e0d\u5fc5\u627e\u6211\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u76ae\u8f6c\u52a8\uff0c\u9053\uff1a\u90a3\u4e48\uff0c\u674e\u5bfb\u6b22\u6709\u4ec0\u4e48\u4e0d\u6d4b\uff0c\u6211\u8be5\u53bb\u544a\u8bc9\u8c01\u5462\uff1f \u3000\u3000\u963f\u98de\u9aa4\u7136\u56de\u8fc7\u5934\uff0c\u9053\uff1a\u4f60\u77e5\u4e0d\u77e5\u9053\u897f\u95e8\u5916\u7684\u6c88\u5bb6\u7960\u5802\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u83ab\u5fd8\u4e86\uff0c\u6211\u5728\u8fd9\u57ce\u91cc\u5df2\u4e94\u516d\u5e74\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u5c31\u4f4f\u5728\u90a3\u7960\u5802\u91cc\uff0c\u65e5\u843d\u4e4b\u524d\uff0c\u6211\u7edd\u4e0d\u79bb\u5f00\u3002 \u3000\u3000\u6797\u4ed9\u513f\uff1a\u65e5\u843d\u4e4b\u540e\u5462\uff1f \u3000\u3000\u9ed8\u7136\u534a\u664c\uff0c\u4ef0\u9762\u671b\u5929\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u83ab\u5fd8\u4e86\uff0c\u674e\u5bfb\u6b22\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u7684\u670b\u53cb\u5e76\u4e0d\u591a\uff0c\u50cf\u4ed6 \u8fd9\u6837\u7684\u670b\u53cb\u66f4\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\uff0c\u4ed6\u82e5\u6b7b\uff0c\u8fd9\u4e16\u754c\u5c31\u65e0\u8da3\u6781\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u9053\uff1a\u6211\u65e9\u5c31\u77e5\u9053\u4eca\u591c\u4f60\u8fd8\u4f1a\u56de\u6765\u6551\u4ed6\u7684\uff0c\u53ef\u662f\u4f60\u8981\u77e5\u9053\uff0c\u65e0\u8bba\u591a \u597d\u7684\u670b\u53cb\uff0c\u4e5f\u6ca1\u6709\u81ea\u5df1\u7684\u6027\u547d\u91cd\u8981\u3002 \u3000\u3000\u963f\u98de\u970d\u7136\u4f4e\u4e0b\u4e86\u5934\uff0c\u77aa\u7740\u5979\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u6211\u53ea\u5e0c\u671b\u4f60\u4ee5\u540e\u6c38\u8fdc\u83ab\u8981\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u8fd9\u53ea\u5f53\u6ca1 \u6709\u542c\u5230\uff01 \u7b2c\u5341\u4e94\u7ae0\u3000\u60c5\u6df1\u610f\u91cd>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u60c5\u6df1\u610f\u91cd \u3000\u3000\u4e0b\u4e86\u591a\u5929\u7684\u96ea\uff0c\u4eca\u5929\u603b\u7b97\u6709\u4e86\u9633\u5149\u3002 \u3000\u3000\u4f46\u9633\u5149\u5e76\u6ca1\u6709\u7167\u8fdb\u8fd9\u95f4\u5c4b\u5b50\uff0c\u674e\u5bfb\u6b22\u4e5f\u5e76\u4e0d\u5931\u671b\uff0c\u56e0\u4e3a\u4ed6\u5df2\u77e5\u9053\uff0c\u4e16\u4e0a\u5c31\u6709\u8bb8\u591a\u5730\u65b9\u662f \u6c38\u8fdc\u89c1\u4e0d\u5230\u9633\u5149\u7684\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5bf9\u4e8e\u5931\u671b\uff0c\u4ed6\u4e5f\u4e45\u5df2\u4e60\u60ef\u4e86\u3002 \u3000\u3000\u4ed6\u5168\u4e0d\u77e5\u9053\u7530\u3001\u8d75\u6b63\u4e49\u8fd9\u4e9b\u4eba\u8981\u5bf9\u4ed6\u600e\u4e48\u6837\uff0c\u4ed6\u751a\u81f3\u8fde\u60f3\u90fd\u61d2\u5f97\u53bb\u60f3\uff0c\u73b0\u5728\uff0c\u7530\u4e03\u4ed6\u4eec \u5df2\u5c06\u5c11\u6797\u5bfa\u7684\u50e7\u4eba\u5e26\u53bb\u89c1\u79e6\u5b5d\u4eea\u7236\u5b50\u4e86\uff0c\u5374\u5c06\u4ed6\u56da\u7981\u5728\u8fd9\u9634\u6e7f\u7684\u67f4\u623f\u91cc\uff0c\u9f99\u5578\u4e91\u5c45\u7136\u4e5f\u5e76\u6ca1 \u6709\u66ff\u4ed6\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u4e5f\u6ca1\u6709\u602a\u4ed6\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4e5f\u6709\u4ed6\u7684\u82e6\u8877\uff0c\u4f55\u51b5\u4ed6\u5df2\u6839\u672c\u65e0\u80fd\u4e3a\u529b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u674e\u5bfb\u6b22\u53ea\u5e0c\u671b\u963f\u98de\u83ab\u8981\u518d\u6765\u6551\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u5df2\u53d1\u73b0\u963f\u98de\u5251\u867d\u5feb\uff0c\u4f46\u6b66\u529f\u5374\u6709\u8bb8\u591a\u5947 \u602a\u7684\u5f31\u70b9\uff0c\u548c\u4eba\u4ea4\u624b\u7684\u7ecf\u9a8c\u66f4\u5dee\uff0c\u9047\u7740\u7530\u4e03\uff0c\u5fc3\u7709\u5728\u5e08\u8fd9\u6837\u7684\u5f3a\u654c\uff0c\u4ed6\u82e5\u4e0d\u80fd\u4e00\u5251\u5f97\u624b\uff0c\u4e5f \u8bb8\u5c31\u6c38\u8fdc\u65e0\u6cd5\u5f97\u624b\uff01 \u3000\u3000\u53ea\u8981\u518d\u8fc7\u4e09\u5e74\uff0c\u963f\u98de\u5c31\u80fd\u4e5f\u6b66\u529f\u7684\u5f31\u70b9\u5168\u5f25\u8865\u8fc7\u6765\uff0c\u5230\u90a3\u65f6\u4ed6\u4e5f\u8bb8\u5c31\u80fd\u65e0\u654c\u4e8e\u5929\u4e0b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5fc5\u987b\u518d\u591a\u6d3b\u4e24\u4e09\u5e74\u3002 \u3000\u3000\u5730\u4e0a\u5f88\u6f6e\u6e7f\uff0c\u674e\u5bfb\u6b22\u53c8\u4e0d\u505c\u7684\u54b3\u55fd\u8d77\u6765\uff0c\u4ed6\u53ea\u5e0c\u671b\u80fd\u6709\u676f\u9152\u559d\u3002 \u3000\u3000\u53ef\u662f\uff0c\u6b64\u523b\u8fde\u559d\u676f\u9152\u7adf\u90fd\u5df2\u53d8\u6210\u4e86\u4e0d\u53ef\u4f01\u6c42\u7684\u5962\u671b\uff0c\u82e5\u662f\u6362\u4e86\u53e6\u4eba\uff0c\u53ea\u6015\u96be\u514d\u8981\u5fcd\u4e0d\u4f4f \u75db\u54ed\u4e00\u573a\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u7b11\u4e86\uff0c\u4ed6\u89c9\u5f97\u4e16\u4e8b\u7684\u53d8\u5316\u7684\u786e\u5f88\u6709\u8da3\u3002 \u3000\u3000\u8fd9\u5730\u65b9\u672c\u662f\u5c5e\u4e8e\u4ed6\u7684\uff0c\u6240\u6709\u4e00\u5207\u672c\u5c5e\u4e8e\u4ed6\u7684\uff0c\u800c\u73b0\u5728\u4ed6\u5374\u88ab\u4eba\u5f53\u505a\u8d3c\uff0c\u88ab\u4eba\u50cf\u6761\u72d7\u4f3c\u7684 \u5173\u5728\u67f4\u623f\u91cc\uff0c\u8fd9\u79cd\u4e8b\u6709\u8c01\u80fd\u60f3\u5f97\u5230\uff1f \u3000\u3000\u95e8\u5ffd\u7136\u5f00\u4e86\u3002 \u3000\u3000\u96be\u9053\u8d75\u6b63\u4e49\u8fde\u4e00\u523b\u90fd\u7b49\u4e0d\u5f97\uff0c\u73b0\u5728\u5c31\u60f3\u8981\u4ed6\u7684\u547d\uff1f \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u7acb\u523b\u5c31\u77e5\u9053\u6765\u7684\u4eba\u4e0d\u662f\u8d75\u6b63\u4e49\u2014\u2014\u4ed6\u95fb\u5230\u4e00\u80a1\u9152\u9999\uff0c\u63a5\u7740\uff0c\u5c31\u770b\u5230\u4e00\u53ea\u624b\u62ff\u7740 \u676f\u9152\u81ea\u95e8\u7f1d\u91cc\u4f38\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u5f88\u5c0f\uff0c\u624b\u8155\u4e0a\u9732\u51fa\u4e00\u622a\u7ea2\u8272\u7684\u8863\u8896\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5c0f\u4e91\uff0c\u662f\u4f60\uff1f \u3000\u3000\u9152\u676f\u7f29\u4e86\u56de\u53bb\uff0c\u7ea2\u5b69\u513f\u5c31\u7b11\u563b\u563b\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u7528\u4e24\u53ea\u624b\u6367\u7740\u9152\u676f\uff0c\u653e\u5728\u9f3b\u5b50\u4e0b\u55c5\u7740\uff0c\u7b11 \u9053\uff1a\u6211\u77e5\u9053\u4f60\u73b0\u5728\u4e00\u5b9a\u5f88\u60f3\u559d\u9152\uff0c\u662f\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u4f60\u77e5\u9053\u6211\u60f3\u559d\u9152\uff0c\u6240\u4ee5\u624d\u66ff\u6211\u9001\u9152\u6765\u7684\uff1f \u3000\u3000\u7ea2\u5b69\u513f\u70b9\u4e86\u70b9\u5934\uff0c\u5c06\u9152\u676f\u9001\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u674e\u5bfb\u6b22\u521a\u60f3\u5f20\u5f00\u5634\uff0c\u4ed6\u5374\u5ffd\u53c8\u5c06\u9152\u676f\u7f29\u4e86\u56de \u53bb\uff0c\u7b11\u9053\uff1a\u4f60\u80fd\u731c\u5f97\u51fa\u8fd9\u662f\u4ec0\u4e48\u9152\uff0c\u6211\u624d\u7ed9\u4f60\u559d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u4e0a\u773c\u775b\uff0c\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u8fd9\u662f\u9648\u5e74\u7684\u7af9\u53f6\u9752\uff0c\u662f\u6211\u6700\u559c\u6b22\u559d\u7684\u9152\uff0c\u6211\u82e5\u8fde \u8fd9\u79cd\u9152\u7684\u5473\u9053\u90fd\u55c5\u4e0d\u51fa\uff0c\u53ea\u6015\u5c31\u771f\u7684\u8be5\u6b7b\u4e86\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u7b11\u9053\uff1a\u96be\u602a\u522b\u4eba\u90fd\u8bf4\u5c0f\u674e\u63a2\u82b1\u5bf9\u5973\u4eba\u548c\u9152\u90fd\u662f\u4e13\u5bb6\uff0c\u8fd9\u8bdd\u771f\u662f\u4e00\u70b9\u90fd\u4e0d\u9519\uff0c\u4f46\u4f60 \u82e5\u771f\u60f3\u559d\u8fd9\u676f\u9152\uff0c\u8fd8\u5f97\u56de\u7b54\u6211\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ec0\u4e48\u8bdd\uff1f \u3000\u3000\u7ea2\u5b69\u513f\u8138\u4e0a\u5b69\u5b50\u6c14\u7684\u7b11\u5bb9\u5ffd\u7136\u53d8\u5f97\u5f88\u9634\u6c89\u3002 \u3000\u3000\u4ed6\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u95ee\u4f60\uff0c\u4f60\u548c\u6211\u6bcd\u4eb2\u7a76\u7adf\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u5979\u662f\u4e0d\u662f\u5f88\u559c\u6b22\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8138\u8272\u7acb\u523b\u4e5f\u53d8\u4e86\uff0c\u76b1\u7709\u9053\uff1a\u8fd9\u4e5f\u662f\u4f60\u5e94\u8be5\u95ee\u7684\u8bdd\u4e48\uff1f \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8be5\u95ee\uff0c\u6bcd\u4eb2\u7684\u4e8b\uff0c\u513f\u5b50\u5f53\u7136\u6709\u6743\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6012\u9053\uff1a\u4f60\u96be\u9053\u4e0d\u660e\u767d\u4f60\u6bcd\u4eb2\u5168\u5fc3\u5168\u610f\u7684\u7231\u7740\u4f60\uff0c\u4f60\u600e\u6562\u6000\u7591\u4ed6\uff1f \u3000\u3000\u7ea2\u5b69\u513f\u51b7\u7b11\u9053\uff1a\u4f60\u4f11\u60f3\u7792\u6211\uff1f\u4ec0\u4e48\u4e8b\u90fd\u7792\u4e0d\u4f4f\u6211\u7684\u3002 \u3000\u3000\u4ed6\u54ac\u7740\u7259\uff0c\u9053\uff1a\u5979\u4e00\u542c\u5230\u4f60\u7684\u4e8b\uff0c\u5c31\u5173\u4e0a\u623f\u95e8\uff0c\u4e00\u4e2a\u4eba\u8eb2\u7740\u5077\u5077\u5730\u54ed\uff0c\u6211\u5feb\u6b7b\u7684\u65f6\u5019\u5979 \u90fd\u6ca1\u6709\u54ed\u5f97\u8fd9\u4e48\u4f24\u5fc3\uff0c\u6211\u95ee\u4f60\uff0c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5df2\u7ede\u4f4f\u4e86\uff0c\u4ed6\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u53d8\u6210\u4e86\u4e00\u5806\u6ce5\uff0c\u6b63\u5728\u88ab\u4eba\u7528\u529b\u8df5\u8e0f\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45 \uff0c\u4ed6\u624d\u6c89\u91cd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u53ef\u4ee5\u6000\u7591\u4efb\u4f55\u4eba\uff0c\u4f46\u7edd\u4e0d\u80fd\u6000\u7591\u4f60\u7684\u6bcd\u4eb2\uff0c\u5979\u7edd \u5bf9\u6ca1\u6709\u4e1d\u6beb\u80fd\u88ab\u4eba\u6000\u7591\u4e4b\u5904\uff0c\u73b0\u5728\u4f60\u5feb\u5e26\u7740\u4f60\u7684\u9152\u8d70\u5427\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u8fd9\u676f\u9152\u6211\u662f\u5e26\u7ed9\u4f60\u7684\uff0c\u600e\u4e48\u80fd\u5e26\u8d70\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u5c06\u8fd9\u676f\u9152\u5168\u90fd\u6cfc\u5728\u674e\u5bfb\u6b22\u8138\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u751a\u81f3\u4e5f\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\uff0c\u53cd\u800c\u67d4\u58f0\u9053\uff1a\u4f60\u8fd8\u662f\u4e2a\u5b69\u5b50\uff0c\u6211\u4e0d\u602a\u4f60\u2014\u2014 \u3000\u3000\u7ea2\u5b69\u513f\u51b7\u7b11\u9053\uff1a\u6211\u5c31\u7b97\u4e0d\u662f\u5b69\u5b50\uff0c\u4f60\u53c8\u80fd\u5bf9\u6211\u600e\u4e48\u6837\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u62d4\u51fa\u4e00\u67c4\u5200\uff0c\u5728\u674e\u589e\u6b22\u8138\u524d\u626c\u4e86\u626c\u5927\u58f0\u9053\uff1a\u4f60\u770b\u6e05\u4e86\u4e48\uff1f\u8fd9\u662f\u4f60\u7684\u5200\uff0c\u5979\u8bf4\u6211\u6709 \u4e86\u4f60\u7684\u5200\uff0c\u5c31\u7b49\u4e8e\u6709\u4e86\u62a4\u8eab\u7b26\uff0c\u4f46\u73b0\u5728\u4f60\u8fd8\u80fd\u4fdd\u62a4\u6211\u4e48\uff1f\u4f60\u6839\u672c\u8fde\u81ea\u5df1\u90fd\u65e0\u6cd5\u4fdd\u62a4\u81ea\u5df1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u5200\uff0c\u672c\u6765\u662f\u4f24\u5bb3\u4eba\u7684\u5e76\u4e0d\u662f\u4fdd\u62a4\u4eba\u7684\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u8138\u8272\u53d1\u767d\uff0c\u5636\u58f0\uff1a\u4f60\u5bb3\u5f97\u6211\u7ec8\u8eab\u6b8b\u5e9f\uff0c\u73b0\u5728\u6211\u4e5f\u8981\u8ba9\u4f60\u548c\u6211\u53d7\u540c\u6837\u7684\u7f6a\uff0c\u4f60\u2014\u2014 \u3000\u3000\u7a81\u542c\u95e8\u5916\u4e00\u4eba\u9053\uff1a\u5c0f\u4e91\uff1f\u662f\u4f60\u5728\u91cc\u9762\u5417\uff1f \u3000\u3000\u8fd9\u58f0\u97f3\u6e29\u67d4\u800c\u52a8\u542c\uff0c\u4f46\u674e\u5bfb\u6b22\u548c\u7ea2\u5b69\u513f\u4e00\u542c\u5230\u8fd9\u58f0\u97f3\uff0c\u8138\u7acb\u523b\u53c8\u53d8\u4e86\uff0c\u7ea2\u5b69\u513f\u8d76\u85cf\u8d77\u4e86 \u5200\uff0c\u9762\u4e0a\u7a81\u7136\u53c8\u9732\u51fa\u4e86\u90a3\u79cd\u5b69\u5b50\u6c14\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u5a18\uff0c\u662f\u6211\u5728\u8fd9\u91cc\uff0c\u6211\u5e26\u4e86\u676f\u9152\u6765\u7ed9\u674e\u5927\u53d4\u559d \uff0c\u5a18\u5728\u5916\u9762\u4e00\u53eb\uff0c\u5413\u4e86\u6211\u4e00\u8df3\uff0c\u5bb3\u5f97\u6211\u628a\u9152\u90fd\u6cfc\u5728\u674e\u5927\u53d4\u8eab\u4e0a\u4e86\u3002 \u3000\u3000\u4ed6\u8bf4\u8bdd\u65f6\uff0c\u6797\u8bd7\u97f3\u5df2\u51fa\u73b0\u5728\u95e8\u53e3\uff0c\u5979\u4e00\u53cc\u7f8e\u4e3d\u7684\u773c\u775b\u679c\u7136\u5df2\u6709\u4e9b\u53d1\u7ea2\uff0c\u5145\u6ee1\u4e86\u60b2\u75db\uff0c\u4e5f \u5e26\u7740\u4e9b\u6124\u6012\u3002 \u3000\u3000\u4f46\u7b49\u5230\u7ea2\u5b69\u513f\u4f9d\u504e\u8fc7\u53bb\u65f6\uff0c\u5979\u76ee\u5149\u7acb\u523b\u53d8\u5f97\u67d4\u548c\u8d77\u6765\uff0c\u9053\uff1a\u674e\u5927\u53d4\u73b0\u5728\u4e0d\u60f3\u559d\u9152\uff0c\u4f60\u73b0 \u5728\u5374\u8be5\u8eba\u5728\u5e8a\u4e0a\u7684\uff0c\u53bb\u5427\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u9053\uff1a\u674e\u5927\u53d4\u4e00\u5b9a\u53d7\u4e86\u522b\u4eba\u51a4\u6789\uff0c\u6211\u4eec\u4e3a\u4f55\u4e0d\u6551\u4ed6\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u8f7b\xd7\u9053\uff1a\u5c0f\u5b69\u5b50\u4e0d\u8bb8\u4e71\u8bf4\u8bdd\uff0c\u5feb\u53bb\u7761\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u56de\u5934\u4e86\u5bfb\u6b22\u4e00\u7b11\uff0c\u9053\uff1a\u674e\u5927\u53d4\uff0c\u6211\u8d70\u4e86\uff0c\u660e\u5929\u6211\u518d\u66ff\u4f60\u9001\u9152\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u4ed6\u8138\u4e0a\u5b69\u5b50\u6c14\u7684\u7b11\u5bb9\uff0c\u624b\u5fc3\u5df2\u4e0d\u89c9\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u53ea\u542c\u6797\u8bd7\u97f3\u5e7d\u5e7d\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u6211\u672c\u6765\u53ea\u62c5\u5fc3\u5b69\u5b50\u4f1a\u5bf9\u4f60\u6000\u6068\u5728\u5fc3\uff0c\u73b0\u5728\u2014\u2014\u73b0\u5728 \u6211\u624d\u653e\u5fc3\u4e86\uff0c\u4ed6\u6709\u65f6\u867d\u7136\u4f1a\u505a\u9519\u4e8b\uff0c\u4f46\u5374\u5e76\u4e0d\u662f\u4e2a\u574f\u5b69\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4e5f\u6ca1\u6709\u770b\u4ed6\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\u624d\u7f13\u7f13\u9053\uff1a\u4f60\u672c\u4e00\u81f3\u5c11\u8fd8\u662f\u4e2a\u5f88\u5b88\u4fe1\u7684\u4eba\uff0c\u73b0\u5728\u4e3a\u4f55\u53d8 \u4e86\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u5589\u5934\u4f3c\u5df2\u88ab\u585e\u4f4f\uff0c\u4ec0\u4e48\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4f60\u5df2\u7b54\u5e94\u8fc7\u6211\u7edd\u4e0d\u53bb\u627e\u4ed9\u513f\uff0c\u4f46\u4ed6\u4eec\u5374\u662f\u5728\u4ed9\u513f\u7684\u5c4b\u5b50\u91cc\u627e\u5230\u4f60\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u2014\u2014\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u600e\u4e48\u8fd8\u80fd\u7b11\u5f97\u51fa\u6765\uff0c\u4f46\u4ed6\u7684\u786e\u7b11\u4e86\uff0c\u4ed6\u671b\u7740\u81ea\u5df1\u7684 \u811a\u5c16\u7b11\u9053\uff1a\u6211\u8bb0\u5f97\u8fd9\u95f4\u5c4b\u5b50\u662f\u5341\u591a\u5e74\u524d\u624d\u76d6\u8d77\u6765\u7684\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u76b1\u4e86\u7709\uff0c\u9053\uff1a\u55ef\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u73b0\u5728\u8fd9\u5c4b\u5b50\u5374\u5df2\u5f88\u65e7\u4e86\uff0c\u5c4b\u89d2\u5df2\u6709\u88c2\u7f1d\uff0c\u7a97\u6237\u4e5f\u7834\u70c2\u4e86\u2014\u2014\u53ef\u89c1\u5341\u5e74\u7684\u65f6 \u5149\u7684\u786e\u4e0d\u77ed\uff0c\u5728\u5341\u5e74\u4e2d\u5c4b\u5b50\u90fd\u4f1a\u53d8\u7834\u65e7\uff0c\u4f55\u51b5\u4eba\u5462\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u7d27\u63e1\u7740\u53cc\u624b\uff0c\u98a4\u9053\uff1a\u4f60\u2014\u2014\u4f60\u73b0\u5728\u96be\u9053\u5df2\u53d8\u6210\u4e86\u4e2a\u9a97\u5b50\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u672c\u6765\u5c31\u662f\u4e2a\u9a97\u5b50\uff0c\u53ea\u4e0d\u8fc7\u73b0\u5728\u9a97\u4eba\u7684\u7ecf\u9a8c\u66f4\u4e30\u5bcc\u4e86\u4e9b\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u5728\u7b11\u7740\uff0c\u4ed6\u7684\u76ee\u7684\u603b\u7b97\u5df2\u8fbe\u5230\u3002 \u3000\u3000\u4ed6\u5c31\u662f\u8981\u4f24\u5bb3\u5979\uff0c\u8981\u5979\u5feb\u8d70\uff0c\u4e3a\u4e86\u4e0d\u8ba9\u522b\u4eba\u88ab\u81ea\u5df1\u8fde\u7d2f\uff0c\u4ed6\u53ea\u6709\u72e0\u4e0b\u5fc3\uff0c\u6765\u4f24\u5bb3\u8fd9\u4e9b\u5173 \u5fc3\u4ed6\u7684\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e9b\u4eba\u4e5f\u6b63\u662f\u4ed6\u6700\u5173\u5fc3\u7684\u3002 \u3000\u3000\u5f53\u4ed6\u4f24\u5bb3\u4ed6\u4eec\u7684\u65f6\u5019\uff0c\u4e5f\u7b49\u4e8e\u5728\u4f24\u5bb3\u81ea\u5df1\uff0c\u4ed6\u867d\u7136\u8fd8\u5728\u7b11\u7740\uff0c\u4f46\u4ed6\u7684\u5fc3\u5374\u5df2\u7834\u88c2\u2014\u2014\uff0d \u3000\u3000\u4ed6\u7d27\u95ed\u7740\u773c\u4e0d\u8ba9\u773c\u6cea\u6d41\u51fa\u6765\uff0c\u7b49\u4ed6\u518d\u5f20\u5f00\u773c\u775b\u65f6\uff0c\u4ed6\u5c31\u53d1\u73b0\u6797\u8bd7\u97f3\u4e0d\u77e5\u4f55\u65f6\u5df2\u56de\u5230\u5c4b\u5b50 \u91cc\uff0c\u6b63\u5728\u51dd\u6ce8\u7740\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u2014\u2014\uff0d\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u8d70\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6211\u53ea\u60f3\u95ee\u6e05\u695a\uff0c\u4f60\u2014\u2014\u4f60\u7a76\u7adf\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u6211\u662f\u6885\u82b1\u76d7\uff1f\u2014\u2014\uff0d\u4f60\u95ee\u6211\u662f\u4e0d\u662f\u6885\u82b1\u76d7\u2014\u2014 \u3000\u3000\u6797\u8bd7\u97f3\u98a4\u58f0\u9053\uff1a\u6211\u867d\u7136\u7edd\u4e0d\u4fe1\u4f60\u662f\u6885\u82b1\u76d7\uff0c\u4f46\u8fd8\u662f\u8981\u4eb2\u8033\u542c\u5230\u4f60\u81ea\u5df1\u8bf4\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u4f60\u65e2\u7136\u7edd\u4e0d\u4fe1\uff0c\u4e3a\u4f55\u8fd8\u8981\u95ee\uff1f\u6211\u65e2\u7136\u662f\u9a97\u5b50\uff0c\u4f60\u95ee\u4e86\u53c8\u6709\u4f55\u7528\uff1f\u6211\u80fd\u9a97 \u4f60\u4e00\u6b21\uff0c\u5c31\u80fd\u9a97\u4f60\u4e00\u767e\u6b21\uff0c\u4e00\u5343\u6b21\uff01 \u3000\u3000\u6797\u8bd7\u97f3\u7684\u8138\u8272\u8d8a\u6765\u8d8a\u82cd\u767d\uff0c\u8eab\u5b50\u4e5f\u5728\u53d1\u6296\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u5ffd\u7136\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u6211\u653e\u4f60\u8d70\uff0c\u4e0d\u7ba1\u4f60\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u6211\u90fd\u653e\u4f60\u8d70\uff0c\u53ea\u6c42 \u4f60\u8fd9\u6b21\u8d70\u4e86\u540e\uff0c\u83ab\u8981\u518d\u56de\u6765\u4e86\uff0c\u6c38\u8fdc\u83ab\u8981\u518d\u56de\u6765\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u55c4\u58f0\u9053\uff1a\u4f4f\u624b\uff01\u4f60\u600e\u4e48\u80fd\u505a\u8fd9\u79cd\u4e8b\uff1f\u4f60\u4ee5\u4e3a\u6211\u4f1a\u50cf\u6761\u72d7\u4f3c\u7684\u843d\u8352\u800c\u9003\uff1f\u4f60\u5c06\u6211\u770b \u6210\u4ec0\u4e48\u4eba\u4e86\uff1f \u3000\u3000\u8bd7\u97f3\u6839\u672c\u4e0d\u7406\u4ed6\uff0c\u6273\u8fc7\u4ed6\u7684\u8eab\u5b50\uff0c\u5c31\u8981\u89e3\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u4eba\u5389\u58f0\u9053\uff1a\u8bd7\u97f3\uff0c\u4f60\u60f3\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u662f\u9f99\u5578\u4e91\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u970d\u7136\u8f6c\u8eab\uff0c\u77aa\u7740\u7ad9\u5728\u95e8\u53e3\u7684\u9f99\u5578\u4e91\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u6211\u60f3\u505a\u4ec0\u4e48\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u9f99\u5578\u4e91\u8138\u8272\u53d8\u4e86\uff0c\u9053\uff1a\u53ef\u662f\u2014\u2014\uff0d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u53ef\u662f\u4ec0\u4e48\uff1f\u8fd9\u4ef6\u4e8b\u672c\u6765\u5e94\u8be5\u4f60\u6765\u505a\u7684\uff01\u4f60\u96be\u9053\u5fd8\u4e86\u4ed6\u5bf9\u6211\u4eec\u7684\u6069\u60c5\uff1f\u4f60\u96be\u9053 \u5fd8\u4e86\u4ee5\u524d\u7684\u4e8b\uff1f\u4f60\u96be\u9053\u4eb2\u773c\u770b\u4ed6\u88ab\u4eba\u6740\u6b7b\uff1f \u3000\u3000\u5979\u8eab\u5b50\u6296\u5f97\u66f4\u5389\u5bb3\uff0c\u5636\u58f0\u9053\uff1a\u4f60\u65e2\u7136\u4e0d\u6562\u505a\u8fd9\u4ef6\u4e8b\uff0c\u6709\u6211\u6765\u505a\uff0c\u4f60\u96be\u9053\u8fd8\u60f3\u6765\u62e6\u4f4f\u6211\uff1f \u3000\u3000\u9f99\u5578\u4e91\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u5ffd\u7136\u7528\u62f3\u5934\u91cd\u91cd\u7684\u6376\u6253\u7740\u80f8\u819b\uff0c\u9053\uff1a\u6211\u662f\u4e0d\u6562\uff0c\u6211\u662f\u6ca1\u80c6\u5b50\uff0c\u6211\u662f \u61e6\u592b\uff01\u4f46\u4f60\u4e3a\u4f55\u4e0d\u60f3\u60f3\uff0c\u6211\u4eec\u600e\u80fd\u505a\u8fd9\u4ef6\u4e8b\uff01\u6211\u4eec\u6551\u4e86\u4ed6\u4e4b\u540e\uff0c\u522b\u4eba\u4f1a\u653e\u8fc7\u6211\u4eec\u4e48\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u671b\u7740\u4ed6\uff0c\u5c31\u597d\u50cf\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u4f3c\u7684\uff0c\u5979\u7f13\u7f13\u5f80\u540e\u9762\u9000\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u53d8\u4e86 \uff0c\u4f60\u4e5f\u53d8\u4e86\u2014\u2014\u4f60\u4ee5\u524d\u4e0d\u662f\u8fd9\u79cd\u4eba\u7684\uff01\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u9eef\u7136\u9053\uff1b\u4e0d\u9519\uff0c\u6211\u4e5f\u8bb8\u53d8\u4e86\uff0c\u56e0\u4e3a\u6211\u73b0\u5728\u5df2\u6709\u4e86\u59bb\u5b50\uff0c\u6709\u4e86\u5b69\u5b50\uff0c\u6211\u65e0\u8bba\u505a\u4ec0\u4e48 \uff0c\u90fd\u8981\u5148\u66ff\u5979\u4eec\u7740\u60f3\uff0c\u6211\u4e0d\u5fcd\u8ba9\u5979\u4eec\u4e3a\u4e86\u6211\u800c\u2014\u2014 \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6797\u8bd7\u97f3\u5df2\u5931\u58f0\u75db\u54ed\u8d77\u6765\u2014\u2014\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u8bdd\u80fd\u6bd4\u5b69\u5b50\u8fd9\u4e24\u5b57\u66f4\u80fd\u4ee4\u6148\u6bcd \u52a8\u5fc3\u7684\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5ffd\u7136\u8dea\u5012\u5728\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u6d41\u6cea\u9053\uff1a\u5144\u5f1f\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u53ea\u6c42\u4f60\u80fd\u539f\u8c05\u6211\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u539f\u8c05\u4f60\uff1f\u6211\u6839\u672c\u4e0d\u660e\u767d\u4f60\u4eec\u5728\u8bf4\u4ec0\u4e48\uff1f\u6211\u65e9\u5df2\u544a\u8bc9\u8fc7\u4f60\uff0c\u8fd9\u6839\u672c\u4e0d\u5173\u4f60\u4eec\u7684 \u4e8b\uff0c\u6211\u82e5\u8981\u8d70\uff0c\u81ea\u5df1\u4e5f\u6709\u6cd5\u5b50\u8d70\u7684\uff0c\u7528\u4e0d\u7740\u4f60\u4eec\u6765\u6551\u6211\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u5728\u671b\u7740\u81ea\u5df1\u7684\u811a\u5c16\uff0c\u56e0\u4e3a\u4ed6\u5df2\u5b9e\u5728\u4e0d\u80fd\u518d\u770b\u4ed6\u4eec\u4e00\u773c\uff0c\u4ed6\u751f\u6015\u81ea\u5df1\u4f1a\u5fcd\u4e0d\u4f4f\u6d41\u4e0b \u6cea\u6765\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u53d7\u7684\u59d4\u66f2\uff0c\u6211\u5168\u90fd\u77e5\u9053\uff0c\u4f46\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u5bb3\u6b7b\u4f60\u7684\uff0c\u4f60 \u53ea\u8981\u89c1\u5230\u5fc3\u6e56\u5927\u5e08\uff0c\u5c31\u4f1a\u6ca1\u4e8b\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u5fc3\u6e56\u5927\u5e08\uff1f\u4ed6\u4eec\u96be\u9053\u8981\u5c06\u6211\u9001\u5230\u5c11\u6797\u5bfa\u53bb\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4e0d\u9519\uff0c\u79e6\u91cd\u867d\u662f\u5fc3\u6e56\u5927\u5e08\u7684\u7231\u5f92\uff0c\u5fc3\u6e56\u5927\u5e08\u4e5f\u7edd\u4e0d\u4f1a\u80e1\u4e71\u51a4\u6789\u597d\u4eba\u7684\uff0c\u4f55\u51b5 \uff0c\u767e\u6653\u751f\u524d\u8f88\u6b64\u523b\u4e5f\u5728\u5c11\u6797\u5bfa\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u4e3a\u4f60\u4e3b\u6301\u516c\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8bf4\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u5230\u7530\u4e03\u4e86\u3002 \u3000\u3000\u7530\u4e03\u6b63\u5728\u671b\u7740\u4ed6\u5fae\u7b11\u3002 \u3000\u3000\u5c31\u5728\u7530\u4e03\u51fa\u73b0\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u6797\u8bd7\u97f3\u5df2\u6062\u590d\u4e86\u9547\u9759\uff0c\u5411\u7530\u4e03\u5fae\u5fae\u70b9\u9996\uff0c\u7f13\u7f13\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u665a\u98ce\u523a\u9aa8\uff0c\u5979\u8d70\u4e86\u4e24\u6b65\uff0c\u5ffd\u7136\u9053\uff1a\u4e91\u513f\u4f60\u51fa\u6765\u3002 \u3000\u3000\u7ea2\u5b69\u513f\u95ea\u7f29\u7740\u81ea\u5c4b\u89d2\u540e\u6e9c\u51fa\u6765\uff0c\u966a\u7740\u7b11\u9053\uff1a\u5a18\uff0c\u6211\u7761\u4e0d\u7740\uff0c\u6240\u4ee5\u2014\u2014\u6240\u4ee5\u2014\u2014 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6240\u4ee5\u4f60\u5c31\u5c06\u4ed6\u4eec\u5168\u90fd\u627e\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u662f\u4e0d\u662f\uff1f \u3000\u3000\u7ea2\u5b69\u513f\u7b11\u7740\u5954\u8fc7\u6765\uff0c\u5ffd\u7136\u53d1\u73b0\u4ed6\u6bcd\u4eb2\u7684\u8138\u8272\u51e0\u4e4e\u5c31\u548c\u9ece\u660e\u524d\u7684\u5bd2\u591c\u4e00\u6837\u9634\u6c89\uff0c\u4ed6\u505c\u4e0b\u811a \u6b65\uff0c\u5934\u4e5f\u5782\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9759\u9759\u671b\u7740\u4ed6\uff0c\u8fd9\u662f\u5979\u4eb2\u751f\u7684\u513f\u5b50\uff0c\u8fd9\u662f\u5979\u7684\u6027\u547d\uff0c\u5979\u7684\u9aa8\u8840\uff0c\u5979\u521a\u64e6\u5e72\u7684\u773c\u775b\u53c8 \u4e0d\u7981\u6d41\u4e0b\u4e86\u4e24\u6ef4\u773c\u6cea\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u9eef\u7136\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4ef0\u9762\u5411\u5929\uff0c\u5583\u5583\u9053\uff1a\u4e3a\u4ec0\u4e48\u4ec7\u6068\u603b\u662f\u6bd4\u6069\u60c5\u96be\u4ee5\u5fd8\u5374 \u2014\u2014\uff0d \u3000\u3000\u94c1\u4f20\u7532\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u5728\u7960\u5802\u4e2d\u6765\u56de\u7684\u8d70\u7740\uff0c\u4e5f\u4e0d\u77e5\u8d70\u8fc7\u591a\u5c11\u904d\u4e86\uff0c\u706b\u5806\u5df2\u5c06\u7184\uff0c\u4f46\u8c01\u4e5f \u6ca1\u6709\u53bb\u6dfb\u67f4\u6728\u3002 \u3000\u3000\u963f\u98de\u53ea\u662f\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u94c1\u4f20\u7532\u6068\u6068\u9053\uff1a\u6211\u65e9\u5df2\u60f3\u5230\u5c31\u7b97\u4f60\u6740\u6b7b\u4e86\u6885\u82b1\u76d7\uff0c\u90a3\u4e9b\u5927\u4fa0\u4eec\u4e5f\u7edd\u4e0d\u4f1a\u627f\u8ba4\u7684\uff0c\u4e00\u7fa4\u91ce \u72d7\u82e5\u662f\u770b\u5230\u4e86\u80a5\u8089\uff0c\u600e\u80af\u518d\u8ba9\u7ed9\u522b\u4eba\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u529d\u8fc7\u6211\uff0c\u6211\u8fd8\u662f\u8981\u53bb\uff0c\u56e0\u56e0\u6211\u975e\u53bb\u4e0d\u53ef\uff01 \u3000\u3000\u94c1\u4f20\u7532\u53f9\u9053\uff1a\u5e78\u597d\u4f60\u53bb\u4e86\uff0c\u5426\u5219\u4f60\u53ea\u6015\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u4e86\u89e3\u8fd9\u4e9b\u5927\u4fa0\u4eec\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u51dd\u6ce8\u7740\u963f\u98de\u9053\uff1a\u4f60\u771f\u7684\u6ca1\u6709\u89c1\u5230\u6211\u4eec\u7684\u5c11\u7237\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u94c1\u4f20\u7532\u671b\u7740\u5c06\u7184\u7684\u706b\u5806\uff0c\u5446\u5446\u5730\u51fa\u4e86\u4f1a\u795e\uff0c\u5583\u5583\u9053\uff1a\u4e0d\u77e5\u4ed6\u73b0\u5728\u600e\u4e48\u6837\u4e86\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u4ed6\u6c38\u8fdc\u7528\u4e0d\u522b\u4eba\u4e3a\u4ed6\u62c5\u5fc3\u7684\u3002 \u3000\u3000\u94c1\u4f20\u7532\u5c55\u989c\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u90a3\u4e9b\u5927\u4fa0\u4eec\u867d\u7136\u5c06\u4ed6\u770b\u5230\u8089\u4e2d\u523a\uff0c\u773c\u4e2d\u9489\uff0c\u86e4\u5374\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba \u6562\u52a8\u4ed6\u4e00\u6839\u624b\u6307\u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u55ef\u3002 \u3000\u3000\u94c1\u4f20\u7532\u671b\u7740\u95e8\u5916\uff0c\u9053\uff1a\u5929\u5df2\u4eae\u4e86\uff0c\u6211\u8981\u52ab\u8eab\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u597d \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u4f60\u5047\u5982\u89c1\u5230\u6211\u5bb6\u5c11\u7237\uff0c\u5c31\u8bf4\uff0c\u94c1\u4f20\u7532\u82e5\u662f\u80fd\u5c06\u6069\u4ec7\u7b97\u6e05\uff0c\u4e00\u5b9a\u8fd8\u4f1a\u56de\u6765\u627e\u4ed6 \u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u597d\u3002 \u3000\u3000\u94c1\u4f20\u7532\u671b\u7740\u4ed6\u7626\u524a\u7684\u8138\uff0c\u62b1\u62f3\u7740\uff1a\u90a3\u4e48\u2014\u2014\u5c31\u6b64\u522b\u8fc7\u3002 \u3000\u3000\u4ed6\u76ee\u4e2d\u867d\u6709\u4f9d\u604b\u4e4b\u610f\uff0c\u4f46\u5374\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u963f\u98de\u8fd8\u662f\u6ca1\u6709\u52a8\uff0c\u4f46\u662f\u4ed6\u90a3\u53cc\u51b7\u9177\u660e\u4eae\u7684\u7738\u5b50\u91cc\uff0c\u5374\u4eff\u4f5b\u6cdb\u8d77\u4e86\u4e00\u9635\u6f6e\u6e7f\u7684\u96fe\u3002 \u3000\u3000\u963f\u98de\u95ed\u8d77\u773c\u775b\uff0c\u4eff\u4f5b\u7761\u7740\u4e86\uff0c\u773c\u89d2\u5374\u5df2\u6c81\u51fa\u4e86\u4e00\u6ef4\u6cea\u73e0\uff0c\u770b\u6765\u5c31\u50cf\u51dd\u7ed3\u5728\u82b1\u5c97\u77f3\u4e0a\u7684\u4e00 \u6ef4\u51b7\u9732\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5bf9\u94c1\u4f20\u7532\u8bf4\u51fa\u674e\u5bfb\u6b22\u7684\u906d\u9047\uff0c\u53ea\u56e0\u4ed6\u4e0d\u613f\u773c\u89c1\u94c1\u4f20\u7532\u53bb\u4e3a\u674e\u5bfb\u6b22\u62fc\u547d\uff0c\u4ed6\u8981\u81ea\u5df1 \u53bb\u4e3a\u674e\u5bfb\u6b22\u62fc\u547d\uff01 \u3000\u3000\u4e3a\u4e86\u670b\u53cb\u7684\u4e49\u6c14\uff0c\u4e00\u6761\u547d\u53c8\u80fd\u503c\u51e0\u4f55\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u671d\u9633\u5c06\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u8f7b\u8f7b\u5730\u9001\u4e86\u8fdb\u6765\uff0c\u957f\u957f\u7684\u9ed1\u5f71\u76d6\u4e0a\u4e86\u963f\u98de\u7684\u8138\u3002 \u3000\u3000\u963f\u98de\u5e76\u6ca1\u6709\u5f20\u5f00\u773c\u775b\uff0c\u53ea\u662f\u95ee\u9053\uff1a\u662f\u4f60\uff1f\u6709\u6d88\u606f\u4e86\u4e48\uff1f \u3000\u3000\u8fd9\u5c11\u5e74\u7adf\u6709\u7740\u6bd4\u91ce\u517d\u66f4\u7075\u654f\u7684\u89e6\u89c9\uff0c\u95e8\u5916\u6765\u7684\u679c\u7136\u662f\u6797\u4ed9\u513f\uff0c\u5fae\u5fae\u5598\u606f\u7740\u9053\uff1a\u662f\u597d\u6d88\u606f \u3002 \u3000\u3000\u597d\u6d88\u606f\uff1f \u3000\u3000\u963f\u98de\u51e0\u4e4e\u5df2\u4e0d\u80fd\u76f8\u4fe1\uff0c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u597d\u6d88\u606f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4ed6\u867d\u7136\u6682\u65f6\u8fd8\u4e0d\u80fd\u8131\u8eab\uff0c\u4f46\u81f3\u5c11\u5df2\u6ca1\u6709\u5371\u9669\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u7530\u4e03\u4ed6\u4eec\u5df2\u53ea\u6709\u4f9d\u4ece\u5fc3\u7709\u5927\u5e08\u7684\u4e3b\u610f\uff0c\u51b3\u5b9a\u5c06\u4ed6\u9001\u5230\u5c11\u6797\u5bfa\u53bb\uff0c\u5c11\u6797\u6d3e \u7684\u638c\u95e8\u5927\u5e08\u5fc3\u6e56\u548c\u5c1a\u7d20\u6765\u5f88\u6b63\u76f4\uff0c\u800c\u4e14\u542c\u8bf4\u5e73\u6c5f\u767e\u6653\u751f\u4e5f\u5728\u90a3\u91cc\uff0c\u8fd9\u4e24\u4eba\u82e5\u8fd8\u4e0d\u80fd\u6d17\u5237\u4ed6\u7684 \u51a4\u540d\uff0c\u5c31\u6ca1\u6709\u522b\u4eba\u80fd\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u767e\u6653\u751f\u662f\u8c01\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u9053\uff1b\u8fd9\u4eba\u4e43\u662f\u4e16\u4e0a\u7b2c\u4e00\u4f4d\u667a\u8005\uff0c\u65e0\u6240\u4e0d\u77e5\uff0c\u65e0\u6240\u4e0d\u6653\uff0c\u800c\u4e14\u636e\u8bf4\u53ea\u6709\u4ed6 \u80fd\u5206\u5f97\u6885\u82b1\u76d7\u7684\u771f\u5047\u3002 \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u5f20\u5f00\u773c\u6765\uff0c\u77aa\u7740\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4e16\u4e0a\u8ba8\u538c\u7684\u662f\u54ea\u79cd\u4eba\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u83ab\u975e\u662f\u8d75\u6b63\u4e49\u90a3\u6837\u7684\u4f2a\u541b\u5b50\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f2a\u541b\u5b50\u53ef\u6068\uff0c\u4e07\u4e8b\u901a\u624d\u8ba8\u538c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e07\u4e8b\u901a\uff1f\u4f60\u8bf4\u7684\u83ab\u975e\u662f\u767e\u6653\u751f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u79cd\u4eba\u81ea\u4f5c\u806a\u660e\uff0c\u81ea\u547d\u4e0d\u51e1\uff0c\u81ea\u4ee5\u4e3a\u4ec0\u4e48\u4e8b\u90fd\u77e5\u9053\uff0c\u51ed\u4ed6\u4eec\u7684\u4e00\u53e5\u8bdd\u5c31 \u80fd\u51b3\u5b9a\u522b\u4eba\u7684\u547d\u8fd0\uff0c\u4ed6\u4eec\u771f\u6b63\u61c2\u5f97\u7684\u4e8b\u53c8\u6709\u591a\u5c11\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f46\u522b\u4eba\u90fd\u8bf4\u2014\u2014 \u3000\u3000\u963f\u98de\u51b7\u51b7\u7b11\u9053\uff1a\u5c31\u56e0\u4e3a\u522b\u4eba\u90fd\u8bf4\u4ed6\u65e0\u77e5\u4e0d\u77e5\uff0c\u5230\u540e\u6765\u4ed6\u4e5f\u53ea\u6709\u81ea\u5df1\u9a97\u81ea\u5df1\uff0c\u786c\u88c5\u6210\u65e0\u6240 \u4e0d\u77e5\u4e86\u3002 \u3000\u3000\u963f\u98de\u53c8\u9053\uff1a\u6211\u5b81\u53ef\u4fe1\u4efb\u4e00\u4e2a\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u7684\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u60f3\u522b\u4eba\u5bf9\u4ed6\u6709\u597d\u611f\uff0c\u6700\u597d\u7684\u6cd5\u5b50\u5c31\u662f\u5148\u8ba9\u522b\u4eba\u77e5\u9053\u4ed6\u5f88\u559c\u6b22\u81ea\u5df1\u2014\u2014\u8fd9\u6cd5\u5b50\u6797 \u4ed9\u513f\u4e5f\u4e0d\u77e5\u7528\u8fc7\u591a\u5c11\u6b21\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u5979\u5e76\u6ca1\u6709\u7528\u6210\u529f\uff0c\u963f\u98de\u671b\u7740\u95e8\u5916\u7684\u79ef\u96ea\u6c89\u601d\u4e86\u5f88\u4e45\uff0c\u624d\u6c89\u58f0\u95ee\u9053\uff1a\u4ed6\u4eec\u51c6\u5907\u4ec0\u4e48 \u65f6\u5019\u52a8\u8eab\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u660e\u5929\u65e9\u4e0a\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e3a\u4ec0\u4e48\u8981\u7b49\u5230\u660e\u5929\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u4eca\u5929\u665a\u4e0a\u4ed6\u4eec\u8981\u8bbe\u5bb4\u4e3a\u5fc3\u7709\u5927\u5e08\u6d17\u5c18\u3002 \u3000\u3000\u963f\u98de\u970d\u7136\u56de\u9996\uff0c\u95ea\u95ea\u53d1\u5149\u7684\u773c\u775b\u77aa\u7740\u5979\uff0c\u9053\uff1a\u9664\u6b64\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u522b\u7684\u539f\u56e0\u4e86\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8fd8\u8981\u6709\u522b\u7684\u539f\u56e0\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u5fc3\u7709\u7edd\u4e0d\u5730\u53ea\u4e3a\u4e86\u5403\u987f\u996d\u5c31\u803d\u8bef\u4e00\u5929\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u4ed6\u867d\u7136\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u987f\u996d\u800c\u7559\u4e0b\uff0c\u4f46\u5374\u975e\u7559\u6765\u5403\u8fd9\u996d\u4e0d\u53ef\uff0c\u56e0\u4e3a \u4eca\u5929\u665a\u4e0a\u8fd8\u6709\u4e00\u4f4d\u7279\u522b\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u8c01\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u94c1\u7b1b\u5148\u751f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u94c1\u7b1b\u5148\u751f\uff1f\u8fd9\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5f20\u5927\u773c\u775b\uff0c\u4eff\u4f5b\u5f88\u5403\u60ca\uff0c\u9053\uff1a\u4f60\u8fde\u94c1\u7b1b\u5148\u751f\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u77e5\u9053\u4ed6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u56e0\u4e3a\u8fd9\u4f4d\u94c1\u7b1b\u5148\u751f\u5c31\u7b97\u4e0d\u662f\u4eca\u65e5\u6c5f\u6e56\u4e2d\u6700\u8d1f\u76db\u540d\u7684\u4eba\uff0c\u4e5f\u5dee\u4e0d\u591a \u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u636e\u8bf4\u6b64\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u5df2\u4e0d\u5728\u6b66\u6797\u4e03\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4e4b\u4e0b\u3002 \u3000\u3000\u5979\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u7559\u610f\u963f\u98de\u9762\u4e0a\u7684\u795e\u8272\u3002 \u3000\u3000\u4f46\u963f\u98de\u8fd9\u6b21\u53c8\u4ee4\u5979\u5931\u671b\u4e86\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u6839\u672c\u6ca1\u6709\u9732\u51fa\u4e1d\u6beb\u60ca\u60e7\u4e4b\u8272\uff0c\u53cd\u800c\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u539f\u6765\u4ed6\u4eec\u627e\u8fd9\u94c1\u5148\u751f\u5c31\u662f\u5bf9\u4ed8\u6211 \u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u773c\u5e18\uff0c\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u505a\u4e8b\u4e00\u5411\u8c28\u614e\uff0c\u4ed6\u6015\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u4ed6\u6015\u6211\u53bb\u6551\u674e\u5bfb\u6b22\u6240\u4ee5\u5c31\u627e\u94c1\u7b1b\u5148\u751f\u6765\u505a\u4fdd\u9556\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u7eb5\u7136\u4ed6\u4eec\u4e0d\u627e\uff0c\u94c1\u7b1b\u5148\u751f\u4e5f\u975e\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u94c1\u7b1b\u5148\u751f\u7684\u7231\u59be\u5982\u610f\u5df2\u6b7b\u5728\u6885\u82b1\u76d7\u624b\u4e0a\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u90a3\u4e48\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u5403\u8fc7\u665a\u996d\u5c31\u52a8\u8eab\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u60f3\u4e86\u60f3\u9053\uff1a\u4e5f\u8bb8\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e5f\u8bb8\u4ed6\u4eec\u6839\u672c\u6c38\u8fdc\u4e0d\u4f1a\u52a8\u8eab\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u7684\u59bb\u5b50\u82e5\u6b7b\u5728\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u6211\u7edd\u4e0d\u4f1a\u8ba9\u4ed6\u6d3b\u7740\u5230\u5c11\u6797\u5bfa\u53bb\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u52a8\u5bb9\u9053\uff1a\u4f60\u662f\u6015\u94c1\u7b1b\u5148\u751f\u4e00\u6765\u4e86\u5c31\u5bf9\u674e\u5bfb\u6b22\u4e0b\u6bd2\u624b\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u55ef\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6014\u4e86\u534a\u664c\uff0c\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u4e5f\u6709\u53ef\u80fd\uff0c\u94c1\u7b1b\u5148\u751f\u4ece\u4e25\u4e0d\u4e70\u522b\u4eba\u5e10\u7684 \uff0c\u4ed6\u82e5\u8981\u51fa\u624b\uff0c\u5fc3\u7709\u5927\u5e08\u4e5f\u672a\u5fc5\u80fd\u62e6\u5f97\u4f4f\u4ed6\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u7684\u8bdd\u5df2\u8bf4\u5b8c\uff0c\u53ef\u4ee5\u8d70\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53ef\u662f\u4f60\u96be\u9053\u60f3\u5728\u94c1\u7b1b\u5148\u751f\u8d76\u6765\u4e4b\u524d\uff0c\u5148\u53bb\u5c06\u674e\u5bfb\u6b22\u6551\u51fa\u6765\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u600e\u4e48\u60f3\u90fd\u4e0e\u4f60\u65e0\u5173\uff0c\u8bf7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53ef\u662f\u5c31\u51ed\u4f60\u4e00\u4eba\u4e4b\u529b\uff0c\u662f\u7edd\u5bf9\u6551\u4e0d\u4e86\u4ed6\u7684\uff01 \u3000\u3000\u5979\u62a2\u7740\u53c8\u9053\uff1a\u6211\u77e5\u9053\u4f60\u7684\u6b66\u529f\u5f88\u9ad8\uff0c\u4f46\u7530\u4e03\u3001\u8d75\u6b63\u4e49\u4e5f\u90fd\u4e0d\u5f31\uff0c\u5fc3\u7709\u5927\u5e08\u66f4\u662f\u5f53\u4eca\u5c11\u6797 \u7684\u7b2c\u4e8c\u628a\u9ad8\u624b\uff0c\u5185\u529f\u4ff1\u5df2\u7089\u706b\u7eaf\u9752\u2014\u2014\uff0d \u3000\u3000\u963f\u98de\u51b7\u51b7\u5730\u671b\u7740\u5979\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5598\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5174\u4e91\u5e84\u6b64\u523b\u53ef\u8bf4\u662f\u9ad8\u624b\u4e91\u96c6\uff0c\u4f60\u82e5\u60f3\u5728\u767d\u5929\u53bb\u4e0b\u624b\u6551\u4eba\uff0c\u5b9e\u5728\u662f \u2014\u2014\uff0d\u5b9e\u5728\u662f\u2014\u2014\uff0d \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u5b9e\u5728\u662f\u53d1\u75af\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u4e86\u5934\uff0c\u4e0d\u6562\u63a5\u89e6\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u963f\u98de\u5374\u7b11\u4e86\u53c8\u7b11\uff0c\u9053\uff1a\u6bcf\u4e2a\u4eba\u5076\u5c14\u90fd\u4f1a\u53d1\u4e00\u6b21\u75af\u7684\uff0c\u6709\u65f6\u8fd9\u5e76\u4e0d\u662f\u574f\u4e8b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5c31\u56e0\u4e3a\u522b\u4eba\u90fd\u60f3\u4e0d\u5230\u4f60\u6562\u5728\u767d\u5929\u53bb\u4e0b\u624b\uff0c\u6240\u4ee5\u9632\u8303\u4e00\u5b9a\u4e0d\u4e25\u5bc6\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec \u6628\u5929\u665a\u4e0a\u90fd\u5fd9\u4e86\u4e00\u591c\uff0c\u8bf4\u4e0d\u5b9a\u90fd\u4f1a\u7761\u4e2a\u5348\u89c9\u2014\u2014 \u3000\u3000\u963f\u98de\u6de1\u6de1\u9053\uff1a\u4f60\u7684\u8bdd\u5df2\u8bf4\u5f97\u592a\u591a\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u597d\uff0c\u6211\u95ed\u4e0a\u5634\u5c31\u662f\uff0c\u4f46\u4f60\u2014\u2014\u4f60\u8fd8\u662f\u662f\u5e94\u8be5\u5c0f\u5fc3\u4e9b\uff0c\u4e07\u4e00\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c \u83ab\u5fd8\u8bb0\u5174\u4e91\u5e84\u8fd8\u6709\u4e2a\u6b20\u4f60\u4e00\u6761\u547d\u7684\u4eba\u3002 \u3000\u3000\u963f\u98de\u5728\u4e3e\u4e91\u5e84\u5bf9\u9762\u7684\u5c4b\u810a\u540e\u5df2\u8db3\u8db3\u7b49\u4e86\u4e00\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u4ed6\u4f0f\u5728\u90a3\u91cc\uff0c\u5c31\u50cf\u4e00\u53ea\u4e13\u5019\u5728\u9f20\u7a74\u5916\uff0c\u7531\u5934\u5230\u811a\uff0c\u7edd\u6ca1\u6709\u4e1d\u6beb\u52a8\u5f39\uff0c\u53ea\u6709\u4e00\u53cc\u9510\u5229\u7684\u773c \u775b\u59cb\u7ec8\u5728\u95ea\u95ea\u5730\u53d1\u7740\u5149\u7684\u732b\u3002 \u3000\u3000\u98ce\u522e\u5728\u8eab\u4e0a\uff0c\u51b7\u5f97\u50cf\u662f\u5200\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff0c\u4ed6\u5341\u5c81\u7684\u65f6\u5019\uff0c\u4e3a\u4e86\u8981\u6355\u6740\u4e00\u53ea\u72d0\u72f8\uff0c\u5c31\u66fe\u52a8\u4e5f\u4e0d\u52a8\u5730\u5728\u96ea\u4e0a\u7b49 \u4e86\u4e24\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u90a3\u6b21\uff0c\u4ed6\u5fcd\u8010\u662f\u4e3a\u4e86\u9965\u997f\uff0c\u6349\u4e0d\u5230\u90a3\u53ea\u72d0\u72f8\uff0c\u4ed6\u5c31\u53ef\u80fd\u6328\u997f\uff01\u4e00\u4e2a\u4eba\u4e3a\u4e86\u81ea\u5df1\u8981\u6d3b\u7740\u800c \u5fcd\u53d7\u75db\u82e6\uff0c\u5e76\u4e0d\u592a\u56f0\u96be\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u4e3a\u4e86\u8981\u8ba9\u522b\u4eba\u6d3b\u7740\u5fcd\u53d7\u75db\u82e6\uff0c\u5c31\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u901a\u5e38\u5f88\u5c11\u6709\u4eba\u80fd\u505a \u5f97\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u6709\u4e00\u4e2a\u4eba\u5927\u6447\u5927\u6446\u5728\u81ea\u5174\u4e91\u5e84\u91cc\u8d70\u4e86\u51fa\u6765\uff0c\u867d\u7136\u9694\u4e86\u5f88\u8fdc\uff0c\u963f\u98de\u5374\u4e5f\u770b\u6e05\u8fd9\u4eba\u662f \u4e2a\u9ebb\u5b50\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u60f3\u4e0d\u5230\u8fd9\u9ebb\u5b50\u5c31\u662f\u6797\u4ed9\u513f\u7236\u4eb2\uff0c\u4ed6\u53ea\u770b\u51fa\u8fd9\u9ebb\u5b50\u4e00\u5b9a\u662f\u5174\u4e91\u5e84\u91cc\u4e00\u4e2a\u6709\u5934\u6709\u8138\u7684 \u4f63\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u666e\u901a\u7684\u5c0f\u4f63\u4eba\uff0c\u7edd\u4e0d\u4f1a\u50cf\u8fd9\u6837\u8dbe\u9ad8\u6c14\u626c\u7684\u2014\u2014\u82e5\u4e0d\u662f\u4f63\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u5982\u6b64\u8dbe\u9ad8\u6c14\u626c\u4e86 \u3002 \u3000\u3000\u8fd9\u4f4d\u6797\u5927\u603b\u7ba1\u809a\u5b50\u91cc\u918b\u88c5\u7684\u867d\u4e0d\u591a\uff0c\u9152\u88c5\u7684\u5374\u4e0d\u5c11\u3002 \u3000\u3000\u4ed6\u5927\u6447\u5927\u6446\u5730\u8d70\u7740\uff0c\u6b63\u60f3\u5230\u5c0f\u8336\u9986\u91cc\u53bb\u5439\u725b\uff0c\u8c01\u77e5\u521a\u521a\u8d70\u5230\u8857\u89d2\uff0c\u5c31\u5ffd\u7136\u53d1\u73b0\u4e00\u67c4\u5251\u5df2 \u6307\u7740\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u963f\u98de\u5e76\u4e0d\u613f\u5bf9\u8fd9\u79cd\u4eba\u7528\u5251\uff0c\u4f46\u7528\u5251\u8bf4\u8bdd\uff0c\u5374\u6bd4\u7528\u820c\u5934\u6709\u6548\u5f97\u591a\uff0c\u51b7\u51b7\u9053\uff1a\u6211\u95ee\u4e00\u53e5\uff0c\u4f60 \u7b54\u4e00\u53e5\uff0c\u4f60\u7b54\u4e0d\u51fa\uff0c\u6211\u5c31\u6740\u4f60\uff0c\u7b54\u9519\u4e86\uff0c\u6211\u4e5f\u6740\u4f60\uff0c\u660e\u767d\u4e86\u4e48\uff1f \u3000\u3000\u6797\u9ebb\u5b50\u60f3\u70b9\u5934\uff0c\u5374\u6015\u5251\u523a\u4f24\u4e0b\u5df4\uff0c\u60f3\u8bf4\u8bdd\uff0c\u5374\u8bf4\u4e0d\u51fa\uff0c\u809a\u5b50\u91cc\u7684\u9152\u5df2\u53d8\u6210\u51b7\u6c57\uff0c\u6d41\u5f97\u6ee1 \u5934\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u95ee\u4f60\uff0c\u674e\u5bfb\u6b22\u662f\u4e0d\u662f\u8fd8\u5728\u5e84\u5b50\u91cc\uff1f \u3000\u3000\u6797\u9ebb\u5b50\u9053\uff1a\u662f\u2014\u2014 \u3000\u3000\u4ed6\u5634\u5507\u52a8\u597d\u51e0\u6b21\uff0c\u624d\u8bf4\u51fa\u8fd9\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5728\u54ea\u91cc\uff1f \u3000\u3000\u9ebb\u5b50\u9053\uff1a\u67f4\u2014\u2014\u67f4\u623f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5e26\u6211\u53bb\uff01 \u3000\u3000\u6797\u9ebb\u5b50\u5927\u9a87\u9053\uff1a\u6211\u600e\u4e48\u5e26\u4f60\u53bb\u2014\u2014\u6211\u6ca1\u2014\u2014\u6211\u6ca1\u6cd5\u5b50\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u4e00\u5b9a\u80fd\u60f3\u5f97\u51fa\u6cd5\u5b50\u6765\u7684\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53cd\u624b\u4e00\u5251\uff0c\u53ea\u542c\u54e7\u7684\u4e00\u58f0\uff0c\u5251\u950b\u5df2\u523a\u5165\u5899\u91cc\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u5df2\u900f\u5165\u6797\u9ebb\u5b50\u8840\u7ba1\u91cc\uff0c\u51b7\u51b7\u9053\uff1a\u4f60\u4e00\u5b9a\u80fd\u60f3\u51fa\u6cd5\u5b50\u7684\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u6797\u9ebb\u5b50\u7259\u9f7f\u6253\u6218\uff0c\u9053\uff1a\u662f\u2014\u2014\u662f\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u597d\uff0c\u8f6c\u8fc7\u8eab\uff0c\u4e00\u76f4\u8d70\u56de\u53bb\uff0c\u83ab\u5fd8\u4e86\u6211\u5c31\u5728\u4f60\u8eab\u540e\u3002 \u3000\u3000\u800c\u6797\u603b\u7ba1\u663e\u7136\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\u5e26\u670b\u53cb\u56de\u6765\uff0c\u6240\u4ee5\u8fd9\u6b21\u963f\u98de\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u95e8\u53e3\u7684\u5bb6\u4e01\u4e5f\u5e76\u6ca1 \u6709\u7279\u522b\u7559\u610f\u3002 \u3000\u3000\u67f4\u623f\u79bb\u53a8\u623f\u4e0d\u8fdc\uff0c\u53a8\u623f\u5374\u79bb\u4e3b\u623f\u5f88\u8fdc\uff0c\u56e0\u4e3a\u541b\u8fdc\u75b1\u53a8\uff0c\u8fd9\u5174\u4e91\u5e84\u6614\u65e5\u7684\u4e3b\u4eba\u6b63\u662f\u4f4d\u771f\u6b63 \u7684\u541b\u5b50\u3002 \u3000\u3000\u6797\u9ebb\u5b50\u4ece\u5c0f\u8def\u8d70\u5230\u67f4\u623f\uff0c\u5e76\u6ca1\u6709\u9047\u89c1\u4ec0\u4e48\u4eba\uff0c\u5c31\u7b97\u9047\u89c1\u4eba\uff0c\u522b\u4eba\u4e5f\u4ee5\u4e3a\u4ed6\u662f\u5230\u53a8\u623f\u53bb\u62ff \u4e0b\u9152\u83dc\u7684\u3002 \u3000\u3000\u53ea\u89c1\u5b64\u96f6\u96f6\u7684\u4e00\u4e2a\u5c0f\u9662\u5b50\u91cc\uff0c\u6709\u95f4\u5b64\u96f6\u96f6\u7684\u5c0f\u5c4b\u5b50\uff0c\u7834\u7684\u5c0f\u95e8\u5916\u5374\u52a0\u4e86\u67c4\u5f88\u575a\u56fa\u7684\u5927\u9501 \u3002 \u3000\u3000\u6797\u9ebb\u5b50\u9053\uff1a\u674e\u2014\u2014\u674e\u5927\u7237\u5c31\u88ab\u9501\u5728\u8fd9\u5c4b\u91cc\uff0c\u5927\u7237\u4f60\u2014\u2014 \u3000\u3000\u963f\u98de\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u6211\u60f3\u4f60\u4e5f\u4e0d\u6562\u9a97\u6211\u3002 \u3000\u3000\u6797\u9ebb\u5b50\u966a\u7b11\u9053\uff1a\u5c0f\u4eba\u600e\u6562\u8bf4\u8c0e\uff0c\u5c0f\u4eba\u600e\u6562\u62ff\u81ea\u5df1\u7684\u8111\u888b\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5f88\u597d\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u53cd\u624b\u4e00\u70b9\uff0c\u5c06\u8fd9\u9ebb\u5b50\u70b9\u6655\u5728\u5730\u4e0a\uff0c\u4e00\u6b65\u7a9c\u8fc7\u53bb\uff0c\u4e00\u811a\u8e22\u5f00\u4e86\u95e8\u3002 \u7b2c\u5341\u516d\u7ae0\u3000\u5047\u4ec1\u5047\u4e49>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u5047\u4ec1\u5047\u4e49 \u3000\u3000\u95e8\u5916\u5e76\u6ca1\u6709\u4eba\u770b\u5b88\uff0c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u963f\u98de\u6562\u5728\u767d\u5929\u6765\u6551\u4eba\u7684\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a \u5927\u5bb6\u90fd\u60f3\u8d81\u673a\u7761\u4e2a\u5348\u89c9\u3002 \u3000\u3000\u8fd9\u95f4\u67f4\u623f\u53ea\u6709\u4e2a\u5f88\u5c0f\u7684\u7a97\u5b50\uff0c\u5c31\u50cf\u662f\u5929\u751f\u7684\u7262\u623f\u4e00\u6837\u9634\u68ee\u68ee\u800c\u9ed1\u6697\uff0c\u5806\u5f97\u50cf\u662f\u5c0f\u5c71\u822c\u7684 \u67f4\u6728\u4e0b\uff0c\u8737\u4f0f\u7740\u4e00\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u77e5\u662f\u5df2\u6655\u8ff7\uff0c\u8fd8\u662f\u5df2\u7761\u7740\u3002 \u3000\u3000\u4e00\u89c1\u5230\u4ed6\u8eab\u4e0a\u90a3\u4ef6\u8c82\u88d8\uff0c\u963f\u98de\u80f8\u4e2d\u7684\u70ed\u8840\u5c31\u6cb8\u817e\u4e86\u8d77\u6765\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u600e\u4f1a\u5bf9\u8fd9\u4eba \u751f\u51fa\u5982\u6b64\u6df1\u539a\u7684\u53cb\u60c5\u3002 \u3000\u3000\u4ed6\u4e00\u6b65\u7a9c\u8fc7\u53bb\uff0c\u560e\u58f0\u9053\uff1a\u4f60\u2014\u2014\uff0d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8c82\u88d8\u4e0b\u5ffd\u7136\u98de\u8d77\u4e86\u9053\u5251\u5149\uff01 \u3000\u3000\u5251\u5149\u5982\u7535\uff0c\u6025\u524a\u963f\u98de\u53cc\u8db3\uff01 \u3000\u3000\u8fd9\u53d8\u5316\u5b9e\u5728\u592a\u51fa\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u4e00\u5251\u4e5f\u5b9e\u5728\u5f88\u5feb\uff01 \u3000\u3000\u5e78\u597d\u963f\u98de\u624b\u4e0a\u8fd8\u63e1\u7740\u5251\uff0c\u4ed6\u7684\u5251\u66f4\u5feb\uff0c\u5feb\u5f97\u7b80\u76f4\u4e0d\u53ef\u601d\u4e49\uff0c\u90a3\u4eba\u7684\u5251\u867d\u5148\u5df2\u523a\u51fa\uff0c\u963f\u98de \u7684\u5251\u540e\u53d1\u5374\u5148\u81f3\u3002 \u3000\u3000\u53ea\u542c\u545b\u7684\u4e00\u58f0\uff0c\u963f\u98de\u7684\u5251\u5c16\u7adf\u70b9\u5728\u5bf9\u65b9\u7684\u5251\u810a\u4e0a\uff01 \u3000\u3000\u90a3\u4eba\u9aa4\u7136\u89c9\u5f97\u624b\u8155\u4e00\u88c2\uff0c\u638c\u4e2d\u5251\u5df2\u88ab\u6572\u843d\u3002 \u3000\u3000\u4f46\u8fd9\u4eba\u4e5f\u662f\u5c11\u89c1\u7684\u9ad8\u624b\uff0c\u4e34\u5371\u4e0d\u4e71\uff01\u8eab\u5b50\u4e00\u7ffb\uff0c\u5df2\u6eda\u51fa\u4e08\u5916\uff0c\u8fd9\u65f6\u624d\u9732\u51fa\u8138\u6765\uff0c\u5c45\u7136\u662f \u6e38\u9f99\u751f\u53bb\u800c\u590d\u8fd4\u3002 \u3000\u3000\u963f\u98de\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4e5f\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\uff0c\u4e00\u5251\u51fa\u624b\uff0c\u8eab\u5b50\u5df2\u5f80\u540e\u9000\uff0c\u4ed6\u9000\u5f97\u867d\u5feb\uff0c\u600e\u5948\u5374\u5df2\u8fdf \u4e86\u3002 \u3000\u3000\u95e8\u5916\u5df2\u6709\u4e00\u6761\u85e4\u68cd\uff0c\u4e00\u67c4\u91d1\u5200\u5c01\u4f4f\u4e86\u9000\u8def\u3002 \u3000\u3000\u963f\u98de\u521a\u987f\u4f4f\u8eab\u5f62\uff0c\u53ea\u542c\u54d7\u5566\u5566\u4e00\u58f0\u5927\u9707\uff0c\u5c0f\u5c71\u822c\u5806\u8d77\u6765\u7684\u67f4\u6728\u5168\u5d29\u843d\uff0c\u73b0\u51fa\u4e86\u5341\u51e0\u4e2a\u4eba \u6765\u3002 \u3000\u3000\u8fd9\u5341\u51e0\u4e2a\u4eba\u4ff1\u90fd\u6025\u88c5\u52b2\u670d\uff0c\u624b\u6301\u5e11\u5323\uff0c\u5bf9\u51c6\u4e86\u963f\u98de\uff0c\u8fd9\u79cd\u8bf8\u845b\u5f29\u5728\u8fd1\u8ddd\u79bb\u5185\u5a01\u529b\u4e4b\u5f3a\uff0c \u65e0\u53ef\u6bd4\u62df\u3002 \u3000\u3000\u65e0\u8bba\u662f\u4ec0\u4e48\u4eba\uff0c\u65e0\u8bba\u6709\u591a\u5927\u7684\u672c\u4e8b\uff0c\u82e5\u5728\u4e00\u95f4\u67f4\u623f\u91cc\u88ab\u5341\u51e0\u53e3\u8bf8\u845b\u5f29\u56f4\u4f4f\uff0c\u518d\u60f3\u8131\u8eab\uff0c \u53ea\u6015\u5c31\u6bd4\u767b\u5929\u8fd8\u96be\u4e86\uff01 \u3000\u3000\u7530\u4e03\u5fae\u7b11\u9053\uff1a\u9601\u4e0b\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f \u3000\u3000\u963f\u98de\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u8bf7\u52a8\u624b\u3002 \u3000\u3000\u7530\u4e03\u4ef0\u9762\u5927\u7b11\u9053\uff1a\u597d\uff0c\u9601\u4e0b\u5012\u4e0d\u6127\u662f\u4e2a\u75db\u5feb\u7684\u4eba\uff0c\u7530\u67d0\u5c31\u7d22\u6027\u6210\u5168\u4e86\u4f60\u5427\uff01 \u3000\u3000\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u5f29\u7bad\u4fbf\u5df2\u5982\u6025\u96e8\u822c\u5c04\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u963f\u98de\u7a81\u7136\u5c31\u5730\u4e00\u6eda\uff0c\u5de6\u624b\u8d81\u52bf\u6284\u8d77\u4e86\u65b9\u624d\u6e38\u9f99\u751f\u638c\u4e2d\u8dcc\u843d\u7684\u593a\u60c5\u5251\u3002 \u3000\u3000\u5251\u5149\u98de\u821e\uff0c\u5316\u505a\u4e00\u5177\u5149\u5e62\uff0c\u5f29\u7bad\u7adf\u88ab\u56db\u4e0b\u9707\u98de\uff0c\u5149\u5e62\u5df2\u6eda\u73e0\u4e00\u822c\u6eda\u5230\u95e8\u53e3\uff0c\u8d75\u6b63\u4e49\u6012\u543c \u4e00\u58f0\uff0c\u7d2b\u91d1\u5200\u7acb\u5288\u534e\u5c71\u6025\u780d\u800c\u4e0b\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u4e00\u5200\u5c1a\u672a\u780d\u4e0b\uff0c\u5149\u5e62\u4e2d\u7a81\u53c8\u98de\u51fa\u4e00\u9053\u5251\u5149\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u4e4b\u5feb\uff0c\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u8d75\u6b63\u4e49\u5927\u60ca\u53d8\u62db\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u54e7\u7684\uff0c\u5251\u5df2\u523a\u5165\u4e86\u4ed6\u7684\u54bd\u5589\uff0c\u9c9c\u8840\u6807\u51fa\uff0c\u5982\u65d7\u82b1\u706b\u7bad\u3002 \u3000\u3000\u7530\u4e03\u5012\u9000\u534a\u6b65\uff0c\u53cd\u624b\u4e00\u68cd\u62bd\u4e0b\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u5149\u5e62\u53c8\u5df2\u5316\u505a\u4e00\u9053\u98de\u8679\uff0c\u5411\u95e8\u5916\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7530\u4e03\u8981\u60f3\u8ffd\uff0c\u7a81\u53c8\u9a7b\u8db3\uff0c\u53ea\u89c1\u8d75\u6b63\u4e49\u624b\u63a9\u4f4f\u54bd\u5589\uff0c\u5589\u5499\u91cc\u683c\u683c\u4f5c\u54cd\uff0c\u5c45\u7136\u8fd8\u6ca1\u6709\u65ad\u6c14\u3002 \u3000\u3000\u518d\u770b\u963f\u98de\u5df2\u63a0\u5230\u5c0f\u9662\u95e8\u5916\uff0c\u53cd\u624b\u4e00\u63b7\uff0c\u593a\u60c5\u5251\u6807\u67aa\u822c\u523a\u5411\u7530\u4e03\uff0c\u7530\u4e03\u521a\u60f3\u8ffd\u51fa\uff0c\u53c8\u7f29\u4e86 \u56de\u53bb\u3002 \u3000\u3000\u957f\u5251\u593a\u7684\u9489\u7684\u4e86\u5bf9\u9762\u5899\u58c1\u3002 \u3000\u3000\u6e38\u9f99\u751f\u5230\u8fd9\u65f6\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u8fd9\u5c11\u5e74\u597d\u5feb\u7684\u8eab\u624b\uff01 \u3000\u3000\u7530\u4e03\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u4ed6\u7684\u8fd0\u6c14\u4e5f\u4e0d\u9519\u3002 \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u8fd0\u6c14\uff1f \u3000\u3000\u7530\u4e03\u9053\uff1a\u5c11\u5e84\u4e3b\u65b9\u624d\u624d\u96be\u9053\u672a\u77a7\u89c1\u4ed6\u8eab\u4e0a\u5df2\u6328\u4e86\u4e24\u7bad\u4e48\uff1f \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u5df2\u770b\u51fa\u4ed6\u5de6\u624b\u821e\u5251\uff0c\u5251\u5149\u4e2d\u4ecd\u6709\u7834\u7efd\uff0c\u5fc5\u5b9a\u6321\u4e0d\u4f4f\u4e03\u7237\u5c5e\u4e0b\u7684\u795e\u5f29 \uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5c45\u7136\u6ca1\u6709\u53d7\u4f24\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u8fd9\u53ea\u56e0\u4ed6\u8eab\u4e0a\u7a7f\u4e86\u91d1\u4e1d\u7532\uff0c\u6211\u5343\u7b97\u4e07\u7b97\uff0c\u7adf\u5fd8\u4e86\u8fd9\u4e00\u7740\uff0c\u5426\u5219\u4ed6\u7eb5\u6709\u5929\u5927\u7684\u672c \u4e8b\uff0c\u4eca\u65e5\u4e5f\u4f11\u60f3\u80fd\u6d3b\u7740\u8d70\u51fa\u8fd9\u95f4\u67f4\u5c4b\u3002 \u3000\u3000\u6e38\u9f99\u751f\u51fa\u795e\u7684\u671b\u7740\u63d2\u5728\u5899\u4e0a\u7684\u5251\uff0c\u6c89\u91cd\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u4ed6\u4eca\u5929\u4e0d\u8be5\u6765\u7684\u3002 \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u80dc\u8d1f\u5175\u5bb6\u5e38\u4e8b\uff0c\u5c11\u5e84\u4e3b\u53c8\u4fdd\u5fc5\u61ca\u607c\uff0c\u4f55\u51b5\uff0c\u90a3\u53ae\u7eb5\u7136\u51af\u8fc7\u4e86\u6211\u4eec\u8fd9\u4e00\u5173\uff0c\u7b2c \u4e8c\u5173\u4ed6\u8fd8\u80fd\u51af\u5f97\u8fc7\u53bb\u4e48\uff1f \u3000\u3000\u963f\u98de\u521a\u63a0\u51fa\u95e8\uff0c\u7a81\u542c\u4e00\u58f0\u963f\u5f25\u9640\u4f5b\uff0c\u6e05\u90ce\u7684\u4f5b\u53f7\u58f0\u7adf\u4f3c\u56db\u9762\u516b\u65b9\u540c\u65f6\u54cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u5c31\u88ab\u4e94\u4e2a\u7070\u888d\u767d\u889c\u7684\u5c11\u6797\u50e7\u4eba\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u767d\u7709\u957f\xd7\uff0c\u4e0d\u6012\u81ea\u5a01\uff0c\u5de6\u624b\u4e0a\u7f20\u7740\u4e00\u4e32\u53e4\u94dc\u8272\u7684\u4f5b\u73e0\uff0c\u6b63\u662f\u5c11\u6797\u5bfa\u7684\u62a4\u6cd5\u5927\u5e08 \u5fc3\u7709\u3002 \u3000\u3000\u963f\u98de\u76ee\u5149\u56db\u626b\uff0c\u5c45\u7136\u795e\u8272\u4e0d\u53d8\uff0c\u53ea\u662f\u6de1\u6de1\u9053\uff1a\u51fa\u5bb6\u4eba\u539f\u6765\u4e5f\u4f1a\u6253\u57cb\u4f0f\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u6c89\u58f0\u9053\uff1a\u8001\u50e7\u5e76\u65e0\u4f24\u4eba\u4e4b\u5fc3\uff0c\u6a80\u8d8a\u4f55\u5fc5\u901e\u4eba\u820c\u4e4b\u5229\uff0c\u9700\u77e5\u5229\u5728\u53e3\u820c\uff0c\u635f\u5728\u5fc3\u5934 \uff0c\u4e0d\u80fd\u4f24\u4eba\uff0c\u5f92\u4f24\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u9053\u6765\uff0c\u8bf4\u5f97\u4f3c\u4e4e\u5f88\u5e73\u548c\uff0c\u4f46\u4f20\u5165\u963f\u98de\u8033\u4e2d\u540e\uff0c\u6bcf\u4e00\u4e2a\u5b57\u53d8\u5f97\u6709\u5982\u6d2a\u949f\u5de8\u9f13\uff0c\u9707\u5f97 \u4ed6\u8033\u6735\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u548c\u5c1a\u7684\u53e3\u820c\u4e4b\u5229\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u5728\u6a80\u8d8a\u4e4b\u4e0b\u5427\uff01 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u82e5\u662f\u51cc\u7a7a\u8df3\u8d77\uff0c\u4e0b\u76d8\u4fbf\u96be\u514d\u7a7a\u95e8\u5927\u9732\uff0c\u5fc3\u7709\u7684\u4f5b\u73e0\u626b\u6765\uff0c\u4ed6\u4e24\u6761\u817f\u5c31\u7b97\u5e9f\u4e86 \u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u53ea\u6709\u4e58\u673a\u81ea\u65c1\u8fb9\u4e24\u4eba\u4e4b\u95f4\u7684\u7a7a\u9699\u4e2d\u51b2\u51fa\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u8eab\u5b50\u521a\u52a8\uff0c\u5c11\u6797\u50e7\u4eba\u4eec\u4e5f\u5ffd\u7136\u5982\u884c\u4e91\u6d41\u6c34\u822c\u8f6c\u52a8\u8d77\u6765\uff0c\u4e94\u4e2a\u4eba\u56f4\u7740\u963f\u98de\u8f6c\u52a8\u4e0d\u4f11 \u3002 \u3000\u3000\u963f\u98de\u811a\u6b65\u505c\u4e0b\uff0c\u5c11\u6797\u50e7\u4eba\u7684\u811a\u6b65\u4e5f\u7acb\u523b\u505c\u4e0b\u6765\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u51fa\u5bb6\u4eba\u4e0d\u613f\u6740\u751f\uff0c\u6a80\u8d8a\u4f60\u638c\u4e2d\u6709\u5251\uff0c\u811a\u4e0b\u6709\u8db3\uff0c\u53ea\u8981\u80fd\u51b2\u5f97\u51fa\u8001\u50e7\u8fd9\u5c0f\u5c0f \u7684\u7f57\u6c49\u95e8\uff0c\u8001\u50e7\u4fbf\u5fc3\u60a6\u8bda\u670d\uff0c\xd7\u9001\u5982\u4eea\u3002 \u3000\u3000\u963f\u98de\u957f\u957f\u547c\u5438\u4e86\u4e00\u6b21\uff0c\u8eab\u5b50\u5374\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51fa\u8fd9\u4e9b\u5c11\u6797\u50e7\u4eba\u4eec\u975e\u4f46\u529f\u592b\u6df1\u539a\uff0c\u800c\u4e14\u8eab\u5f62\u4e4b\u914d\u5408\uff0c\u66f4\u662f\u5929\u8863\u65e0\u7f1d\uff0c\u7b80\u76f4\u6ef4\u6c34\u4e0d \u6f0f\u3002\u963f\u98de\u516b\u4e5d\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u770b\u5230\u4e00\u53ea\u4ed9\u9e64\u88ab\u4e00\u6761\u5927\u87d2\u86c7\u56f0\u4f4f\uff0c\u90a3\u4ed9\u9e64\u4e4b\u5599\u867d\u5229\uff0c\u4f46\u5374\u59cb\u7ec8\u4e0d \u6562\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u672c\u6765\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u540e\u6765\u624d\u77e5\u9053\u4ed9\u9e64\u6700\u77e5\u86c7\u6027\uff0c\u56e0\u4e3a\u8fd9\u87d2\u86c7\u76d8\u6210\u9635\u540e\uff0c\u9996\u5c3e\u76f8\u5e94\uff0c\u5982\u96f7 \u51fb\u7535\u95ea\uff0c\u5b83\u82e5\u662f\u5411\u86c7\u9996\u76f4\u5582\xd7\uff0c\u53cc\u817f\u5c31\u96be\u514d\u88ab\u86c7\u5c3e\u5377\u4f4f\uff0c\u5b83\u82e5\xd7\u5411\u86c7\u5c3e\uff0c\u4fbf\u96be\u514d\u88ab\u86c7\u9996\u6240\u4f24 \u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4ed9\u9e64\u4e00\u76f4\u7ad9\u7740\u4e0d\u52a8\uff0c\u7b49\u5230\u87d2\u86c7\u4e0d\u8010\uff0c\u5fcd\u4e0d\u4f4f\u5148\u51fa\u51fb\u65f6\uff0c\u4ed9\u9e64\u7684\u94a2\xd7\u6709\u5982\u95ea\u7535\u822c\xd7 \u4f4f\u4e86\u87d2\u86c7\u7684\u4e03\u5bf8\u3002 \u3000\u3000\u82e5\u80fd\u505a\u5230\u4ee5\u9759\u5236\u52a8\uff0c\u4ee5\u9038\u5f85\u52b3\u8fd9\u516b\u5b57\uff0c\u66f4\u80fd\u7a33\u64cd\u80dc\u5238\u3002 \u3000\u3000\u8fd9\u9053\u7406\u4ed6\u59cb\u7ec8\u672a\u66fe\u5fd8\u8bb0\u3002 \u3000\u3000\u662f\u4ee5\u5c11\u6797\u50e7\u4eba\u4e0d\u52a8\uff0c\u963f\u98de\u4e5f\u7edd\u4e0d\u52a8\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u81ea\u5df1\u4f3c\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u9053\uff1a\u6a80\u8d8a\u96be\u9053\u60f3\u675f\u624b\u5c31\u7f1a\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u60f3\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u65e2\u4e0d\u613f\u5c31\u7f1a\uff0c\u4e3a\u4f55\u4e0d\u8d70\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u4e0d\u6740\u6211\uff0c\u6211\u4e5f\u4e0d\u80fd\u6740\u4f60\uff0c\u5c31\u51b2\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u5fc3\u7709\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u6a80\u8d8a\u82e5\u80fd\u6740\u5f97\u4e86\u8001\u50e7\uff0c\u8001\u50e7\u6b7b\u800c\u65e0\u6028\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u597d\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u52a8\u4e86\uff01\u4e00\u52a8\u5c31\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u4f46\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u76f4\u523a\u5fc3\u7709\u5927\u5e08\u7684\u54bd\u5589\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4eba\u8eab\u5f62\u4e5f\u7acb\u523b\u52a8\u4e86\uff0c\u516b\u94c1\u638c\u4e00\u9f50\u5411\u963f\u98de\u62ac\u4e0b\uff01 \u3000\u3000\u8c01\u77e5\u963f\u98de\u5251\u65b9\u523a\u51fa\uff0c\u811a\u4e0b\u5ffd\u7136\u4e00\u53d8\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u811a\u6b65\u662f\u600e\u6837\u53d8\u7684\uff0c\u53ea\u89c9\u4ed6\u8eab\u5b50\u7adf\u5ffd\u7136 \u53d8\u4e86\u4e2a\u65b9\u5411\u3002 \u3000\u3000\u90a3\u4e00\u5251\u672c\u6765\u660e\u660e\u662f\u5411\u5fc3\u7709\u523a\u51fa\u7684\uff0c\u6b64\u523b\u5ffd\u7136\u53d8\u4e86\u65b9\u5411\uff0c\u53e6\u56db\u4eba\u5c31\u50cf\u662f\u8981\u5c06\u81ea\u5df1\u7684\u624b\u638c\u9001 \u53bb\u8ba9\u4ed6\u7684\u5251\u5272\u4e0b\u3002 \u3000\u3000\u5fc3\u7709\u6c89\u58f0\u9053\uff1a\u597d\uff01 \u3000\u3000\u597d\u5b57\u51fa\u53e3\uff0c\u4ed6\u8863\u8896\u5df2\u5377\u8d77\u4e00\u80a1\u52b2\uff0c\u5c11\u6797\u94c1\u8896\uff0c\u5229\u4e8e\u5200\u5203\uff0c\u8fd9\u4e00\u7740\u6b63\u662f\u653b\u8eb2\u907f\u963f\u98de\u5fc5\u6551\u4e4b \u5904\u3002 \u3000\u3000\u56db\u4e2a\u5c11\u6797\u50e7\u4eba\u867d\u9047\u9669\u7740\uff0c\u4f46\u81ea\u5df1\u6839\u672c\u4e0d\u5fc5\u51fa\u624b\u89e3\u6551\uff0c\u8fd9\u4e5f\u5c31\u662f\u5c11\u6797\u7f57\u6c49\u9635\u5a01\u529b\u4e4b\u6240\u5728\u3002 \u3000\u3000\u8c01\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u963f\u98de\u7684\u5251\u65b9\u5411\u7adf\u53c8\u53d8\u4e86\u3002 \u3000\u3000\u522b\u4eba\u7684\u5251\u53d8\u62db\uff0c\u53ea\u4e0d\u8fc7\u662f\u51fa\u624b\u90e8\u4f4d\u6539\u53d8\u800c\u5df2\uff0c\u4f46\u4ed6\u7684\u5251\u4e00\u53d8\uff0c\u5374\u8fde\u6574\u4e2a\u65b9\u5411\u90fd\u6539\u53d8\u4e86\u3002 \u3000\u3000\u672c\u662f\u523a\u5411\u4e1c\u7684\u4e00\u5251\uff0c\u5ffd\u7136\u5c31\u53d8\u6210\u523a\u5411\u897f\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u7684\u5251\u6839\u672c\u672a\u53d8\uff0c\u53d8\u7684\u53ea\u662f\u4ed6\u7684\u811a\u6b65\u3001\u53d8\u5316\u4e4b\u5feb\uff0c\u7b80\u76f4\u4ee4\u4eba\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u4f1a\u6709\u8fd9\u4e48\u6837 \u4e00\u53cc\u817f\u3002 \u3000\u3000\u53ea\u542c\u54e7\u7684\u4e00\u58f0\uff0c\u5fc3\u7709\u8863\u8896\u5df2\u88ab\u51fb\u4e2d\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5251\u5149\u5ffd\u7136\u5316\u505a\u4e00\u6e9c\u9752\u8679\uff0c\u4eba\u4e0e\u5251\u4f3c\u5df2\u63a5\u4e3a\u4e00\u4f53\uff0c\u9752\u8679\u5212\u8fc7\uff0c\u4eba\u5df2\u968f\u7740\u5251\u51b2\u4e86\u51fa\u53bb \u3002 \u3000\u3000\u53ea\u542c\u5fc3\u7709\u5927\u5e08\u6c89\u58f0\u9053\uff1a\u6a80\u8d8a\u6162\u8d70\uff0c\u8001\u50e7\u76f8\u9001\u3002 \u3000\u3000\u963f\u98de\u53ea\u89c9\u80cc\u540e\u4e00\u80a1\u5927\u529b\u63b8\u6765\uff0c\u5c31\u597d\u50cf\u53ea\u94c1\u68f0\u822c\u6253\u5728\u4ed6\u7684\u80cc\u810a\u4e0a\uff0c\u4ed6\u8eab\u4e0a\u867d\u6709\u91d1\u4e1d\u7532\uff0c\u4f46 \u4e5f\u88ab\u6253\u5f97\u80f8\u53e3\u4e00\u70ed\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5c31\u50cf\u65ad\u7ebf\u7eb8\u9e22\u822c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u80e1\u6e23\u5b50\u53d1\u9752\u7684\u5c11\u6797\u50e7\u4eba\u9053\uff1a\u8ffd\uff01 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4e0d\u5fc5\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4eba\u9053\uff1a\u4ed6\u5df2\u9003\u4e0d\u8fdc\u4e86\uff0c\u5e08\u53d4\u4e3a\u4f55\u8981\u653e\u4ed6\u9003\u8d70\uff1f \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4ed6\u65e2\u5df2\u4e0d\u8fdc\u4e86\uff0c\u4e3a\u4f55\u8fd8\u8981\u8ffd\uff1f \u3000\u3000\u90a3\u5c11\u6797\u50e7\u4eba\u60f3\u4e86\u60f3\uff0c\u5782\u9996\u9053\uff1a\u5e08\u53d4\u8bf4\u5f97\u662f\u3002 \u3000\u3000\u5fc3\u7709\u671b\u7740\u963f\u98de\u9003\u8d70\u7684\u65b9\u5411\uff0c\u7f13\u7f13\u9053\uff1a\u51fa\u5bb6\u4eba\u6148\u60b2\u4e3a\u6000\uff0c\u80fd\u4e0d\u4f24\u4eba\uff0c\u8fd8\u662f\u4e0d\u4f24\u4eba\u7684\u597d\u3002 \u3000\u3000\u7530\u4e03\u4e00\u76f4\u5728\u8fdc\u8fdc\u77a7\u7740\uff0c\u6b64\u523b\u54e7\u7684\u4e00\u7b11\uff0c\u5583\u5583\u9053\uff1a\u597d\u4e2a\u51fa\u5bb6\u4eba\u6148\u60b2\u4e3a\u6000\uff0c\u82e5\u6709\u522b\u4eba\u66ff\u4ed6\u6740 \u4eba\uff0c\u4ed6\u81ea\u5df1\u5c31\u4e0d\u80af\u52a8\u624b\u4e86\u3002 \u3000\u3000\u5c11\u6797\u62a4\u6cd5\u7684\u638c\u529b\u679c\u7136\u662f\u96c4\u6d51\u6c89\u539a\uff0c\u4e0d\u540c\u51e1\u54cd\uff0c\u963f\u98de\u76f4\u63a0\u8fc7\u4e24\u91cd\u5c4b\u810a\uff0c\u624d\u52c9\u5f3a\u7ad9\u4f4f\u4e86\u811a\u3002 \u3000\u3000\u7b49\u4ed6\u518d\u6b21\u63a0\u8d77\u65f6\uff0c\u624d\u53d1\u73b0\u81ea\u5df1\u7684\u5185\u529b\u5df2\u53d7\u4e86\u4f24\uff0c\u4f46\u8fd9\u70b9\u4f24\u4ed6\u76f8\u4fe1\u81ea\u5df1\u603b\u8fd8\u80fd\u7ecf\u5f97\u8d77\u3002 \u3000\u3000\u523b\u82e6\u7684\u953b\u70bc\uff0c\u8270\u96be\u7684\u5c81\u6708\uff0c\u5df2\u4f7f\u53d8\u6210\u4e86\u4e2a\u4e0d\u5bb9\u6613\u5012\u4e0b\u53bb\u7684\u4eba\uff0c\u4ed6\u7684\u8eab\u5b50\u51e0\u4e4e\u5c31\u50cf\u662f\u94c1\u6253 \u7684\u3002 \u3000\u3000\u963f\u98de\u82e5\u80fd\u9003\u51fa\u53bb\uff0c\u5df2\u662f\u4e07\u5e78\u2014\u2014\u5728\u5c11\u6797\u62a4\u6cd5\u548c\u56db\u5927\u9ad8\u624b\u7684\u56f4\u653b\u4e4b\u4e0b\uff0c\u5929\u4e0b\u672c\u5c31\u5f88\u5c11\u6709\u4eba \u80fd\u51b2\u51fa\u6765\u7684\u3002 \u3000\u3000\u53ea\u662f\u963f\u98de\u5e76\u4e0d\u60f3\u9003\u8d70\u3002 \u3000\u3000\u7530\u4e03\u4ed6\u4eec\u5c06\u674e\u5bfb\u6b22\u85cf\u5230\u4ec0\u4e48\u5730\u65b9\u5462\uff1f \u3000\u3000\u963f\u98de\u7684\u76ee\u5149\u9e70\u4e00\u822c\u56db\u4e0b\u641c\u7d22\u7740\uff0c\u72f8\u732b\u822c\u63a0\u4e0b\u5c4b\u810a\uff0c\u7a9c\u5165\u540e\u56ed\uff0c\u4e00\u4e2a\u4eba\u5728\u5c4b\u810a\u4e0a\u7684\u76ee\u6807\u592a \u5927\uff0c\u540e\u56ed\u4e2d\u5374\u591a\u7684\u662f\u85cf\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4ed6\u542c\u5230\u6709\u4eba\u5728\u7b11\u3002 \u3000\u3000\u6570\u4e08\u5916\u6709\u5ea7\u5c0f\u4ead\uff0c\u8fd9\u4eba\u5c31\u5750\u5728\u4ead\u5b50\u91cc\uff0c\u501a\u7740\u680f\u6746\u770b\u4e66\uff0c\u770b\u5f97\u5f88\u51fa\u795e\uff0c\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u7559\u610f \u5230\u522b\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u4ef6\u5f88\u7834\u65e7\u7684\u68c9\u888d\u5b50\uff0c\u4e00\u5f20\u8138\u5f88\u7626\uff0c\u5f88\u9ec4\uff0c\u80e1\u5b50\u5f88\u7a00\u758f\uff0c\u770b\u6765\u5c31\u8c61\u662f\u4e2a\u8425\u517b\u4e0d\u826f\u7684 \u8001\u5b66\u7a76\u3002 \u3000\u3000\u4f46\u8001\u5b66\u7a76\u5728\u6570\u4e08\u5916\u53d1\u7b11\uff0c\u53ea\u6709\u5185\u529f\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u624d\u80fd\u5c06\u7b11\u58f0\u9001\u5f97\u8fd9\u4e48\u8fdc\u3002 \u3000\u3000\u963f\u98de\u505c\u4e0b\u811a\uff0c\u9759\u9759\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u8fd9\u8001\u5b66\u7a76\u4f3c\u4e4e\u6ca1\u6709\u770b\u5230\u963f\u98de\uff0c\u7528\u624b\u6307\u8638\u4e86\u70b9\u53e3\u6c34\uff0c\u5c06\u4e66\u7ffb\u4e86\u4e00\u9875\uff0c\u53c8\u6d25\u6d25\u6709\u5473\u5730\u770b\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u963f\u98de\u4e00\u6b65\u6b65\u5411\u540e\u9000\uff0c\u9000\u4e86\u5341\u6b65\uff0c\u970d\u7136\u8f6c\u8eab\u3002 \u3000\u3000\u4e00\u8f6c\u8eab\u4ed6\u5c31\u5df2\u5230\u4e86\u4e09\u4e08\u5916\uff0c\u518d\u4e5f\u4e0d\u56de\u5934\uff0c\u6025\u63a0\u800c\u51fa\uff0c\u4e09\u4e24\u4e2a\u8d77\u843d\uff0c\u5df2\u7a9c\u5165\u4e86\u6885\u6797\u3002 \u3000\u3000\u963f\u98de\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u5c06\u5589\u5934\u4e00\u70b9\u8840\u8165\u5473\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u53d1\u73b0\u81ea\u5df1\u4f24\u52bf\u6bd4\u60f3\u8c61\u4e2d\u91cd\u5f97\u591a\uff0c\u65b9\u624d\u4e00\u52a8\u771f\u6c14\uff0c\u80f8\u4e2d\u4fbf\u4f3c\u6709\u9c9c\u8840\u8981\u6d8c\u51fa\uff0c\u53ea\u6015\u5df2\u96be \u548c\u4eba\u4ea4\u624b\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u9635\u7b1b\u8d77\u54cd\u8d77\u3002 \u3000\u3000\u7b1b\u58f0\u60a0\u626c\u800c\u6e05\u6d0c\uff0c\u6885\u82b1\u4e0a\u7684\u79ef\u96ea\u88ab\u7b1b\u58f0\u6240\u6467\uff0c\u4e00\u7247\u7247\u98d8\u843d\u4e0b\u6765\uff0c\u4e00\u7247\u7247\u843d\u5728\u963f\u98de\u8eab\u4e0a\u3002 \u3000\u3000\u96ea\u82b1\u98d8\u98de\u95f4\uff0c\u53ef\u4ee5\u770b\u5230\u4e00\u4e2a\u4eba\u6b63\u501a\u5728\u6570\u4e08\u5916\u4e00\u682a\u6885\u6811\u4e0b\u5439\u7b1b\uff0c\u8d6b\u7136\u5c31\u662f\u65b9\u624d\u770b\u4e66\u7684\u8001\u5b66 \u7a76\u3002 \u3000\u3000\u7b1b\u58f0\u6e10\u6e10\u81ea\u9ad8\u5197\u8f6c\u4e3a\u4f4e\u8ff7\u66f2\u6298\u5a49\u8f6c\uff0c\u8361\u4eba\u5e7d\u601d\u3002 \u3000\u3000\u963f\u98de\u8fd9\u6b21\u4e0d\u518d\u8d70\u4e86\uff0c\u51dd\u6ce8\u7740\u4ed6\uff0c\u4e00\u5b57\u9053\uff1a\u94c1\u7b1b\u5148\u751f\uff1f \u3000\u3000\u7b1b\u58f0\u9aa4\u987f\u3002 \u3000\u3000\u4ed6\u76ef\u7740\u963f\u98de\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u4f60\u53d7\u4e86\u4f24\uff1f \u3000\u3000\u963f\u98de\u4e5f\u6709\u4e9b\u610f\u5916\uff1a\u8fd9\u4eba\u597d\u5389\u5bb3\u7684\u773c\u529b\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u4f24\u5728\u80cc\u540e\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u5df2\u770b\u51fa\uff0c\u4f55\u5fc5\u518d\u95ee\uff1f \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u662f\u5fc3\u7709\u548c\u5c1a\u4e0b\u7684\u624b\uff1f \u3000\u3000\u963f\u98de\uff1a\u54fc\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\uff0c\u6447\u7740\u5934\u9053\uff1a\u5c11\u6797\u62a4\u6cd5\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u8fc7\u600e\u6837\uff1f \u3000\u3000\u94c1\u7b1b\u5148\u751f\u6de1\u6de1\u9053\uff1a\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u672c\u4e0d\u8be5\u5728\u80cc\u540e\u51fa\u624b\u4f24\u4eba\uff0c\u65e2\u5df2\u4f24\u4e86\u4f60\uff0c\u4fbf\u4e0d\u8be5\u8fd8\u8ba9\u4f60\u80fd \u6d3b\u7740\u8d70\u5230\u6211\u9762\u524d\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u8001\u548c\u5c1a\u8fd9\u96be\u9053\u662f\u60f3\u501f\u5200\u6740\u4eba\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u544a\u8bc9\u4f60\u4e09\u4ef6\u4e8b\uff0c\u7b2c\u4e00\uff0c\u82e5\u4e0d\u5728\u80cc\u540e\u51fa\u624b\uff0c\u4ed6\u6839\u672c\u51fa\u4e0d\u4e86\u624b\uff1b\u7b2c\u4e8c\uff0c\u4ed6\u7eb5\u7136\u51fa \u624b\u4e5f\u6740\u4e0d\u6b7b\u6211\uff1b\u7b2c\u4e09\u4f60\u66f4\u6740\u4e0d\u6b7b\u6211\uff01 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u5927\u7b11\u9053\uff1a\u5c11\u5e74\u4eba\u597d\u5927\u7684\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u7684\u7b11\u58f0\u4e00\u53d1\u5373\u6536\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u65e2\u5df2\u53d7\u4f24\uff0c\u6211\u672c\u4e0d\u613f\u51fa\u624b\uff0c\u4f46\u4f60\u7684\u53e3\u6c14\u592a\u5927\uff0c\u6211\u4e0d\u80fd\u4e0d \u6559\u8bad\u4f60\u3002 \u3000\u3000\u963f\u98de\u4f3c\u5df2\u89c9\u5f97\u8bdd\u8bf4\u592a\u591a\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u613f\u518d\u8bf4\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u5ff5\u5728\u4f60\u5df2\u53d7\u4f24\uff0c\u6211\u8ba9\u4f60\u4e09\u62db\u3002 \u3000\u3000\u963f\u98de\u671b\u7740\u4ed6\uff0c\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u5c06\u5251\u63d2\u56de\u8170\u5e26\u4e0a\uff0c\u626d\u5934\u5c31\u8d70\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u7eb5\u58f0\u957f\u7b11\uff0c\u9053\uff1a\u65e2\u5df2\u89c1\u5230\u4e86\u6211\uff0c\u4f60\u8fd8\u60f3\u8d70\uff1f \u3000\u3000\u963f\u98de\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u6211\u4e0d\u8d70\uff0c\u4f60\u5c31\u5f97\u6b7b\uff01 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u5927\u7b11\u9053\uff1a\u662f\u6211\u6b7b\uff1f\u8fd8\u662f\u4f60\u6b7b\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6ca1\u6709\u4eba\u80fd\u8ba9\u6211\u4e09\u62db\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u6211\u82e5\u8ba9\u4f60\u4e09\u62db\uff0c\u5c31\u975e\u6b7b\u4e0d\u53ef\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u662f\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u4f60\u4e3a\u4f55\u4e0d\u8bd5\u8bd5\uff1f \u3000\u3000\u963f\u98de\u4e0d\u518d\u8bf4\u8bdd\uff0c\u8f6c\u8fc7\u76ee\u5149\uff0c\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u4f46\u4ed6\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u53cc\u773c\u775b\u91cc\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u611f\u60c5\uff0c\u8fd9\u5c11\u5e74\u7684\u773c\u73e0\u5b50\u4e5f\u50cf\u662f\u7528\u77f3\u5934\u5851\u6210\u7684\uff0c\u8fd9\u53cc\u773c\u775b\u77aa \u7740\u4f60\u65f6\uff0c\u5c31\u597d\u50cf\u4e00\u5c0a\u795e\u50cf\u5728\u795e\u6848\u4e0a\u6f20\u7136\u4fef\u89c6\u7740\u82cd\u751f\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u7adf\u4e0d\u7531\u81ea\u4e3b\u540e\u9000\u4e86\u534a\u6b65\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u963f\u98de\u7684\u5251\u5df2\u51fa\u624b\u3002 \u3000\u3000\u4e00\u5251\u523a\u51fa\uff0c\u7edd\u4e0d\u7a7a\u56de\u3002 \u3000\u3000\u8fd9\u662f\u963f\u98de\u7684\u4fe1\u6761\uff0c\u6ca1\u6709\u7edd\u5bf9\u628a\u63e1\u65f6\uff0c\u4ed6\u7684\u5251\u7edd\u4e0d\u51fa\u624b\uff01 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u7684\u8eab\u5b50\u7a81\u53c8\u51cc\u5149\u63a0\u8d77\u51b2\u4e0a\u6885\u68a2\uff0c\u53ea\u542c\u54d7\u5566\u5566\u4e00\u7247\u58f0\u54cd\uff0c\u98de\u6ee1\u534a\u5929\u3002 \u3000\u3000\u767d\u96ea\u548c\u7ea2\u6885\u5728\u534a\u7a7a\u4e2d\u4ea4\u7ec7\u6210\u4e00\u5e45\u7eee\u4e3d\u7684\u56fe\u6848\uff0c\u53ea\u89c1\u94c1\u7b1b\u5148\u751f\u7684\u8eab\u5b50\u5728\u767d\u96ea\u7ea2\u6885\u4e2d\u8f7b\u98d8\u98d8 \u98de\u821e\u3002 \u3000\u3000\u963f\u98de\u6839\u672c\u6ca1\u6709\u62ac\u5934\uff0c\u5251\u5df2\u6536\u8d77\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u5df2\u8f7b\u98d8\u98d8\u843d\u4e86\u4e0b\u6765\uff0c\u4ed6\u843d\u5f97\u90a3\u4e48\u6162\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u4e2a\u7eb8\u624e\u7684\u4eba\uff0c\u96ea\u5730\u4e0a\u5df2\u591a\u4e86 \u4e00\u4e32\u9c9c\u8840\u3002 \u3000\u3000\u963f\u98de\u51dd\u89c6\u7740\u5730\u4e0a\u7684\u8840\uff0c\u7f13\u7f13\u9053\uff1a\u6ca1\u6709\u4eba\u80fd\u8ba9\u6211\u4e09\u62db\uff0c\u4e00\u62db\u90fd\u4e0d\u80fd\uff01 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u501a\u7740\u6885\u6811\uff0c\u5598\u606f\u7740\uff0c\u4ed6\u7684\u8138\u82cd\u767d\uff0c\u54bd\u5589\u4e4b\u4e0b\uff0c\u80f8\u53e3\u4e4b\u4e0a\uff0c\u8840\u8ff9\u6dcb\u6f13\u3002 \u3000\u3000\u4ed6\u90a3\u540d\u9707\u5929\u4e0b\u7684\u94c1\u7b1b\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u51fa\u624b\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f46\u4f60\u6ca1\u6709\u6b7b\uff0c\u4e5f\u56e0\u4e3a\u4f60\u8ba9\u6211\u4e09\u62db\uff0c\u4f60\u6ca1\u6709\u5931\u4fe1\u3002 \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u4f60\u81f3\u5c11\u6bd4\u5fc3\u7709\u5f3a\u5f97\u591a\u3002 \u3000\u3000\u5fc3\u7709\u8bf4\u7edd\u4e0d\u4f24\u4eba\uff0c\u53ea\u8981\u4ed6\u51b2\u51fa\u7f57\u6c49\u9635\uff0c\u4f46\u540e\u6765\u8fd8\u662f\u4f24\u4e86\u4ed6\uff0c\u8fd9\u6559\u8bad\u4ed6\u53d1\u8a93\u6c38\u8fdc\u4e5f\u4e0d\u5fd8\u8bb0 \u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u5598\u606f\uff0c\u5ffd\u7136\u9053\uff1a\u8fd8\u6709\u4e24\u62db\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u8fd8\u6709\u4e24\u62db\uff1f \u3000\u3000\u94c1\u7b1b\u5148\u751f\u54ac\u7259\u5fcd\u53d7\u7740\u75db\u82e6\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u6211\u8ba9\u4f60\u4e09\u62db\uff0c\u4f60\u53ea\u51fa\u624b\u4e00\u62db\u3002 \u3000\u3000\u963f\u98de\uff0c\u51dd\u6ce8\u4e86\u4ed6\u5f88\u4e45\u5f88\u4e45\uff0c\u9053\uff1a\u597d\uff01 \u3000\u3000\u4ed6\u8f7b\u8f7b\u51fa\u624b\uff0c\u5728\u94c1\u7b1b\u5148\u751f\u9762\u524d\u51fb\u4e86\u4e24\u638c\uff0c\u9053\uff1a\u73b0\u5728\u4e09\u62db\u90fd\u5df2\u2014\u2014\uff0d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u53ee\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5341\u4f59\u70b9\u5bd2\u661f\u66b4\u96e8\u822c\u81ea\u94c1\u7b1b\u5148\u751f\u624b\u4e0a\u7684\u94c1\u7b1b\u4e2d\u5c04\u51fa\uff01 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u82cd\u767d\u7684\u8138\u4e0a\u6cdb\u8d77\u4e00\u9635\u5174\u594b\u7684\u7ea2\u5149\uff0c\u5598\u606f\u7740\u9053\uff1a\u4eca\u5929\u6211\u5df2\u5b66\u4f1a\u4e86\u4e00\u4ef6\u4e8b\uff0c\u7edd\u4e0d\u8ba9 \u4efb\u4f55\u4eba\u4e09\u62db\uff0c\u4f60\u4e5f\u8be5\u5b66\u4f1a\u4e00\u4ef6\u4e8b\u2014\u2014\u82e5\u8981\u51fa\u624b\uff0c\u5c31\u4e00\u5b9a\u8981\u4ee4\u5bf9\u65b9\u5012\u4e0b\uff0c\u5426\u5219\u4f60\u5c31\u7edd\u4e0d\u8981\u51fa\u624b \uff01 \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u77a7\u7740\u9489\u5728\u4ed6\u817f\u4e0a\u7684\u4e00\u70b9\u5bd2\u661f\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u8fd9\u4ef6\u4e8b\u6211\u5fd8\u4e0d\u4e86\u7684\uff01 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u597d\uff0c\u4f60\u8d70\u5427\u3002 \u3000\u3000\u963f\u98de\u8fd8\u672a\u8bf4\u8bdd\uff0c\u5df2\u542c\u5f97\u4e00\u9635\u811a\u6b65\u58f0\u54cd\u8d77\u3002 \u3000\u3000\u6709\u4eba\u5728\u547c\u5524\u7740\u9053\uff1a\u524d\u8f88\uff0c\u94c1\u8001\u524d\u8f88\uff0c\u4f60\u5f97\u624b\u4e86\u4e48\uff1f \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u5feb\u8d70\uff0c\u6211\u5df2\u65e0\u529b\u6740\u4f60\uff0c\u4e5f\u4e0d\u613f\u4f60\u6b7b\u5728\u522b\u4eba\u624b\u4e0a\uff01 \u3000\u3000\u963f\u98de\u5c31\u5730\u4e00\u6eda\uff0c\u6eda\u51fa\u4e24\u4e08\u3002 \u3000\u3000\u4ed6\u7684\u817f\u867d\u5df2\u4e0d\u80fd\u8d70\uff0c\u4ed6\u7684\u624b\u5374\u540c\u6837\u6709\u529b\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u6b64\u523b\u5589\u5934\u53c8\u611f\u89c9\u5230\u4e00\u9635\u8840\u8165\u6c14\uff0c\u4ed6\u867d\u7136\u5728\u52c9\u5f3a\u5fcd\u8010\u7740\uff0c\u4f46\u8fd9\u53e3\u8840\u8fdf\u65e9\u662f\u96be\u514d\u8981\u5410 \u51fa\u6765\u3002 \u3000\u3000\u7528\u4e0d\u7740\u522b\u4eba\u6765\u8ffd\uff0c\u4ed6\u81ea\u5df1\u5df2\u652f\u6301\u4e0d\u4e86\u591a\u4e45\uff0c\u4ed6\u53ea\u60f3\u89c1\u674e\u5bfb\u6b22\u6700\u540e\u4e00\u9762\uff0c\u544a\u8bc9\u674e\u5bfb\u6b22\u4ed6\u5df2 \u5c3d\u4e86\u529b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5df2\u6709\u6761\u4eba\u5f71\u5411\u4ed6\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u53ea\u71c3\u7740\u4e00\u70db\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9ed8\u9ed8\u5730\u770b\u7740\u674e\u5bfb\u6b22\uff0c\u7b49\u4ed6\u54b3\u5b8c\u4e86\uff0c\u624d\u9012\u8fc7\u4e00\u676f\u9152\u53bb\uff0c\u9001\u5230\u4ed6\u5634\u8fb9\uff0c\u6162\u6162\u5730\u5012\u5165\u4ed6 \u7684\u5634\u91cc\u3002 \u3000\u3000\u559d\u5b8c\u4e86\u8fd9\u676f\u9152\uff0c\u674e\u5bfb\u6b22\u5c31\u7b11\u4e86\uff0c\u9053\uff1a\u5927\u54e5\uff0c\u4f60\u770b\u6211\u4e00\u6ef4\u9152\u90fd\u6ca1\u6709\u6f0f\u51fa\u6765\u5427\uff0c\u6211\u5c31\u7b97\u88ab\u4eba \u60ac\u7a7a\u5012\u7740\u540a\u8d77\u6765\uff0c\u4f46\u82e5\u6709\u4eba\u5582\u6211\u559d\u9152\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a\u6f0f\u51fa\u6765\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u60f3\u7b11\uff0c\u5374\u6ca1\u6709\u7b11\u51fa\u6765\uff0c\u9eef\u7136\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u89e3\u5f00\u4f60\u7684\u7a74\u9053\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u662f\u4e2a\u7ecf\u4e0d\u8d77\u8bf1\u60d1\u7684\u4eba\uff0c\u4f60\u82e5\u89e3\u5f00\u6211\u7684\u7a74\u9053\uff0c\u6211\u8bf4\u4e0d\u5b9a\u5c31\u60f3\u8dd1\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u73b0\u5728\u2014\u2014\u73b0\u5728\u4ed6\u4eec\u90fd\u4e0d\u5728\u8fd9\u91cc\uff0c\u4f60\u5230\u73b0\u5728\u8fd8\u4e0d\u660e\u767d\u6211\u7684\u610f\u601d\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u5927\u54e5\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\u6211\u7684\u610f\u601d\u4e48\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6211\u660e\u767d\uff0c\u53ef\u662f\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u77e5\u9053\u4f60\u53c8\u60f3\u8bf4\u8fd9\u53e5\u8bdd\u4e86\uff0c\u4f46\u4f60\u5b9e\u5728\u5e76\u6ca1\u6709\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u6211\u7684\u5730\u65b9\uff0c \u4f60\u5c06\u6211\u4ece\u67f4\u623f\u642c\u4e00\u9a7c\u91cc\u6765\uff0c\u53c8\u6709\u9152\u559d\uff0c\u8fd9\u5df2\u4e0d\u4e8f\u6211\u4eec\u5144\u5f1f\u4e00\u573a\u4e86\u3002 \u7b2c\u5341\u4e03\u7ae0\u3000\u539f\u5f62\u6bd5\u9732>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u539f\u5f62\u6bd5\u9732 \u3000\u3000\u9f99\u5578\u4e91\u542c\u4e86\u674e\u5bfb\u6b22\u7684\u8bdd\uff0c\u5782\u4e0b\u4e86\u5934\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u9eef\u7136\u9053\uff1a\u660e\u5929\u2014\u2014\u660e\u5929\u4f60\u5c31\u8981\u8d70 \u4e86\uff0c\u6211\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u5343\u4e07\u83ab\u8981\u518d\u6765\u9001\u6211\uff0c\u6211\u4ece\u6765\u4e0d\u559c\u6b22\u9001\u4eba\u4e0e\u4e0d\u613f\u522b\u4eba\u6765\u9001\u6211\uff0c\u6211\u770b\u5230\u522b\u4eba\u9001 \u884c\u90a3\u79cd\u5982\u4e27\u8003\u59a3\u6a21\u6837\u5c31\u89c9\u5f97\u6076\u5fc3\u3002 \u3000\u3000\u4ed6\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u4f55\u51b5\u6211\u8fd9\u6b21\u53bb\u7684\u5730\u65b9\u53c8\u4e0d\u8fdc\uff0c\u8bf4\u4e0d\u5b9a\u4e09\u4e94\u5929\u5c31\u4f1a\u56de\u6765\u3002 \u3000\u3000\u9f99\u4e91\u4e5f\u6253\u8d77\u4e86\u7cbe\u795e\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u56de\u6765\u6211\u4e00\u5b9a\u63a5\u4f60\uff0c\u90a3\u65f6\u6211\u4eec\u518d\u597d\u597d\u9189\u4e00\u573a\u3002 \u3000\u3000\u7a81\u7136\u4e00\u4eba\u5e7d\u5e7d\u9053\uff1a\u4f60\u4eec\u660e\u77e5\u4ed6\u8fd9\u4e00\u53bb\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u56de\u6765\u4e86\uff0c\u53c8\u4f55\u5fc5\u8fd8\u8981\u81ea\u5df1\u9a97\u81ea\u5df1\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u7f13\u7f13\u8d70\u4e86\u8fc7\u6765\uff0c\u7f8e\u4e3d\u7684\u9762\u5bb9\u4f3c\u53c8\u6194\u60b4\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u7acb\u523b\u9732\u51fa\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u5374\u8fd8\u662f\u7b11\u7740\u9053\uff1a\u6211\u4e3a\u4f55\u4e0d\u4f1a\u56de\u6765\uff1f\u4f60\u4eec\u90fd\u662f\u6211\u6700\u597d\u7684 \u670b\u53cb\uff0c\u6211\u2014\u2014 \u3000\u3000\u6797\u8bd7\u97f3\u6ca1\u6709\u8ba9\u4ed6\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u8c01\u662f\u4f60\u7684\u597d\u670b\u53cb\uff0c\u8fd9\u91cc\u6839\u672c\u6ca1\u6709\u4f60\u7684\u670b\u53cb\u3002 \u3000\u3000\u5979\u5ffd\u7136\u6307\u7740\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u4ee5\u4e3a\u4ed6\u662f\u4f60\u7684\u670b\u53cb\u4e48\uff1f\u4ed6\u82e5\u662f\u4f60\u7684\u670b\u53cb\uff0c\u5c31\u8be5\u7acb\u523b\u8ba9\u4f60\u8d70\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u53ef\u662f\u4ed6\u2014\u2014\uff0d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4ed6\u4e0d\u8d70\uff0c\u662f\u6015\u8fde\u7d2f\u4e86\u4f60\uff0c\u4f46\u4f60\u4e3a\u4f55\u4e0d\u653e\u4ed6\uff1f\u8d70\u4e0d\u8d70\u662f\u4ed6\u7684\u4e8b\uff0c\u653e\u4e0d\u653e\u5374\u662f\u4f60 \u7684\u4e8b\u3002 \u3000\u3000\u5979\u6ca1\u6709\u542c\u9f99\u5578\u4e91\u7b54\u590d\uff0c\u5c31\u5934\u4e5f\u4e0d\u56de\u5730\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u55c4\u58f0\u9053\uff1a\u65e0\u8bba\u4f60\u8d70\u4e0d\u8d70\uff0c\u6211\u90fd\u8be5\u653e\u4e86\u4f60\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6014\u9053\uff1a\u4f60\u2014\u2014\u4f60\u7b11\u4ec0\u4e48\uff1f\uff1a \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u51e0\u65f6\u5b66\u4f1a\u542c\u5973\u4eba\u7684\u8bdd\u4e86\uff1f\u6211\u4ea4\u7684\u662f\u9f99\u5578\uff12\uff0c\u662f\u6761\u597d\u6c49\u5b50\uff0c\u53ef\u4e0d\u662f\u6015\u8001\u5a46\u7684 \u53ef\u601c\u866b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u773c\u6cea\u5df2\u4e0d\u7981\u593a\u7736\u800c\u51fa\uff0c\u98a4\u58f0\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u2014\u2014\u4f60\u5bf9\u6211\u592a\u597d\u4e86\uff0c\u6211\u5e76 \u4e0d\u662f\u4e0d\u61c2\u4f60\u7684\u82e6\u5fc3\uff0c\u53ef\u662f\u2014\u2014\u53ef\u662f\u5374\u53eb\u6211\u8fd9\u4e00\u751f\u5982\u4f55\u62a5\u7b54\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u6b63\u6709\u4ef6\u4e8b\u6c42\u4f60\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4e00\u628a\u6293\u4f4f\u4ed6\u80a9\u5934\uff0c\u9053\uff1a\u4ec0\u4e48\u4e8b\uff1f\u4f60\u53ea\u7ba1\u8bf4\uff0c\u5feb\u8bf4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6628\u5929\u6765\u7684\u90a3\u5c11\u5e74\u963f\u98de\uff0c\u5927\u54e5\u4f60\u603b\u8be5\u8bb0\u5f97\u4ed6\u5427\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u5f53\u7136\u8bb0\u5f97\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u82e5\u6709\u4e86\u4ec0\u4e48\u5371\u9669\uff0c\u5927\u54e5\u4f60\u4e00\u5b9a\u8981\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u624b\u7f13\u7f13\u677e\u5f00\uff0c\u4ef0\u9762\u957f\u53f9\u9053\uff1a\u5230\u4e86\u8fd9\u65f6\u5019\uff0c\u4f60\u8fd8\u53ea\u8bb0\u5f97\u4ed6\uff0c\u4f60\u96be\u9053\u4ece\u6765\u4e0d\u80af\u4e3a\u81ea \u5df1\u60f3\u60f3\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u53ea\u95ee\u4f60\u7b54\u4e0d\u7b54\u5e94\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6211\u5f53\u7136\u7b54\u5e94\uff0c\u53ea\u4e0d\u8fc7\u4e5f\u8bb8\u6211\u518d\u4e5f\u89c1\u4e0d\u7740\u4ed6\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5931\u58f0\u9053\uff1a\u4e3a\u4ec0\u4e48\uff0c\u4ed6\u96be\u9053\u5df2\u2014\u2014\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u4f60\u6628\u5929\u770b\u5230\u4ed6\u8d70\u7684\uff0c\u4f60\u600e\u4e48\u8fd8\u4f1a\u518d\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u4e5f\u5e0c\u671b\u4ed6\u83ab\u8981\u518d\u6765\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4e00\u5b9a\u4f1a\u518d\u6765\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4ed6\u82e5\u4f1a\u6765\u6551\u4f60\uff0c\u4e3a\u4f55\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6765\uff1f \u3000\u3000\u4ed6\u957f\u957f\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u5bf9\u522b\u4eba\u867d\u7136\u4e49\u91cd\u5982\u5c71\uff0c\u4f46\u522b\u4eba\u5bf9\u4f60\u5374\u672a\u5fc5\u4e00\u6837\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4ed6\u5bf9\u6211\u600e\u6837\u662f\u4ed6\u7684\u4e8b\uff0c\u4f46\u6211\u53ea\u6c42\u5927\u54e5\uff0c\u4ee5\u540e\u65e0\u8bba\u5728\u4ec0\u4e48\u5730\u65b9\u9047\u89c1\u4ed6 \uff0c\u90fd\u83ab\u8981\u5fd8\u4e86\u4ed6\u662f\u6211\u7684\u670b\u53cb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u597d\uff0c\u4f60\u7684\u670b\u53cb\uff0c\u5c31\u662f\u6211\u7684\u670b\u53cb\u3002 \u3000\u3000\u7a81\u7136\u5916\u9762\u6709\u4eba\u5524\u9053\uff1a\u9f99\u56db\u7237\u2014\u2014\uff0d\u9f99\u56db\u7237\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7ad9\u8d77\u6765\uff0c\u53c8\u5750\u4e0b\u53bb\uff0c\u9053\uff1a\u5144\u5f1f\uff0c\u4f60\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u6211\u7684\u9152\u5df2\u559d\u591f\u4e86\uff0c\u5927\u54e5\u4f60\u53ea\u7ba1\u53bb\u5427\uff0c\u53ea\u4e0d\u8fc7\u5343\u4e07\u8981\u8bb0\u7740\uff0c\u660e\u5929\u65e9\u4e0a\u5343\u4e07\u83ab \u8981\u518d\u6765\u9001\u6211\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7f13\u7f13\u8d70\u5230\u95e8\u53e3\uff0c\u53ea\u89c1\u7530\u4e03\u7ad9\u5728\u56ed\u5b50\u91cc\u7684\u6811\u5f71\u4e0b\uff0c\u5411\u4ed6\u62db\u624b\u3002 \u3000\u3000\u4ed6\u5feb\u6b65\u8d76\u4e86\u8fc7\u53bb\uff0c\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u5f97\u624b\u4e86\u4e48\uff1f \u3000\u3000\u7530\u4e03\u9053\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5389\u58f0\u9053\uff1a\u6ca1\u6709\uff1f\u4f60\u4eec\u5341\u51e0\u4e2a\u4eba\uff0c\u518d\u52a0\u4e0a\u5fc3\u7709\u5927\u5e08\u548c\u94c1\u7b1b\u5148\u751f\uff0c\u96be\u9053\u7adf\u5bf9\u4ed8\u4e0d\u4e86\u4e00 \u4e2a\u5c0f\u4f19\u5b50\uff1f \u3000\u3000\u7530\u4e03\u82e6\u7b11\u9053\uff1a\u8fd9\u5c0f\u4f19\u5b50\u53ef\u5b9e\u5728\u592a\u5389\u5bb3\u4e86\uff0c\u7b80\u76f4\u6709\u4e9b\u53ef\u6015\uff0c\u8d75\u8001\u5927\u88ab\u4ed6\u4f24\u4e86\u4e0d\u8bf4\uff0c\u8fde\u94c1\u7b1b \u5148\u751f\u90fd\u5df2\u4f24\u5728\u4ed6\u5251\u4e0b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8fde\u8fde\u8dfa\u811a\uff0c\u9053\uff1a\u6211\u65e9\u77e5\u9053\u8fd9\u5c0f\u5b50\u4e0d\u597d\u60f9\uff0c\u4f60\u504f\u8bf4\u94c1\u7b1b\u5148\u751f\u4e00\u5b9a\u53ef\u4ee5\u5bf9\u4ed8\u4ed6\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ed6\u867d\u7136\u9003\u8d70\uff0c\u5374\u8fd8\u662f\u6328\u4e86\u5fc3\u7709\u5927\u5e08\u7684\u4e00\u638c\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u4ed6\u4e00\u5b9a\u9003\u4e0d\u4e86\u7684\uff0c\u4f60\u4eec\u4e3a\u4f55\u4e0d\u8ffd\uff1f \u3000\u3000\u7530\u4e03\u9053\uff1a\u5c11\u6797\u5bfa\u7684\u4eba\u5df2\u8ffd\u4e0b\u53bb\u4e86\uff0c\u6211\u7279\u5730\u8d76\u6765\u901a\u77e5\u4f60\u4e00\u58f0\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6211\u53bb\u770b\u770b\uff0c\u4f60\u53bb\u53eb\u4eba\u5230\u8fd9\u91cc\u6765\u5b88\u7740\u3002 \u3000\u3000\u6811\u7684\u540e\u9762\uff0c\u6709\u5ea7\u5047\u5c71\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u521a\u8d70\uff0c\u5047\u5c71\u540e\u5c31\u5e7d\u7075\u822c\u51fa\u73b0\u4e86\u6761\u4eba\u5f71\uff0c\u5979\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u60ca\u8bb6\u548c\u6000\u7591\uff0c\u4e5f \u5145\u6ee1\u4e86\u60b2\xd7\u548c\u6124\u6068\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u90fd\u5728\u98a4\u6296\u7740\uff0c\u6cea\u6d41\u6ee1\u9762\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u7684\u5fc3\u90fd\u788e\u4e86\uff0c\u5979\u8f7b\u8f7b\u555c\u6ce3\u7740\uff0c\u7136\u540e\uff0c\u50cf\u662f\u4e0b\u4e86\u5f88\u5927\u7684\u51b3\u5fc3\uff0c\u5927\u6b65\u5411\u674e\u5bfb\u6b22\u90a3\u5c4b\u5b50 \u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u5df2\u6709\u9635\u6025\u9aa4\u7684\u811a\u6b65\u58f0\u4f20\u4e86\u8fc7\u6765\uff0c\u6797\u8bd7\u97f3\u8eab\u5b50\u4e00\u95ea\uff0c\u7acb\u523b\u53c8\u9000\u5165\u5047\u5c71\u540e\u7684\u9634 \u5f71\u91cc\u3002 \u3000\u3000\u7530\u4e03\u5e26\u7740\u4e03\u516b\u6761\u52b2\u88c5\u6025\u773c\u7684\u5927\u6c49\u8d76\u8fc7\u6765\u4e86\uff0c\u6c89\u58f0\u9053\uff1a\u5b88\u4f4f\u95e8\uff0c\u83ab\u8981\u8ba9\u4efb\u4f55\u4eba\u8fdb\u53bb\uff0c\u5426\u5219 \u683c\u6740\u65e0\u8bba\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u663e\u7136\u4e5f\u6025\u7740\u53bb\u8ffd\u6355\u963f\u98de\uff0c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u7eb5\u8eab\u63a0\u51fa\uff0c\u5927\u6c49\u4eec\u7acb\u523b\u5f20\u5f13\u642d\u7bad\uff0c\u5b88\u4f4f\u4e86 \u95e8\u53e3\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u7d27\u7d27\u54ac\u7740\u5634\u5507\uff0c\u5df2\u54ac\u5f97\u51fa\u8840\u3002 \u3000\u3000\u5979\u53ea\u6068\u81ea\u5df1\u4ee5\u524d\u4e3a\u4f55\u603b\u662f\u8f7b\u89c6\u6b66\u529f\uff0c\u4e0d\u80af\u4e0b\u82e6\u529f\u53bb\u5b66\u6b66\u3002 \u3000\u3000\u73b0\u5728\u5979\u624d\u77e5\u9053\u6709\u5f88\u591a\u4e8b\u7684\u786e\u975e\u7528\u6b66\u529b\u89e3\u51b3\u4e0d\u53ef\u3002 \u3000\u3000\u5979\u60f3\u4e0d\u51fa\u5982\u4f55\u8d70\u5165\u90a3\u95f4\u5c4b\u5b50\u3002 \u3000\u3000\u7a81\u542c\u4e00\u9635\u8f7b\u5fae\u7684\u5598\u606f\u58f0\uff0c\u4ed6\u811a\u6b65\u867d\u7136\u6709\u4e9b\u4e0d\u7a33\uff0c\u4f46\u8fd8\u662f\u8d70\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u8ba4\u5f97\u8fd9\u4eba\u5c31\u662f\u4eca\u5929\u624d\u8d76\u5230\u7684\u94c1\u7b1b\u5148\u751f\u3002 \u3000\u3000\u53ea\u542c\u94c1\u7b1b\u5148\u751f\u5389\u58f0\u9053\uff1a\u59d3\u674e\u7684\u662f\u4e0d\u662f\u5728\u8fd9\u95f4\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u5927\u6c49\u4eec\u9762\u9762\u76f8\u89c6\uff0c\u9053\uff1a\u6211\u4eec\u4e0d\u5927\u6e05\u695a\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u597d\uff0c\u95ea\u5f00\uff0c\u6211\u8fdb\u53bb\u77a7\u77a7\u3002 \u3000\u3000\u5927\u6c49\u9053\uff1a\u7530\u4e03\uff1f\u7530\u4e03\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f60\u4eec\u53ef\u8ba4\u5f97\u6211\u662f\u8c01\uff1f \u3000\u3000\u90a3\u5927\u6c49\u773c\u775b\u76ef\u7740\u4ed6\u8eab\u4e0a\u7684\u8840\u8ff9\uff0c\u9053\uff1a\u65e0\u8bba\u8c01\u4e5f\u4e0d\u80fd\u8fdb\u53bb\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u5f88\u597d\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5ffd\u7136\u62ac\u4e86\u62ac\uff0c\u53ee\u7684\u5bd2\u661f\u66b4\u5c04\u800c\u51fa\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u7740\u773c\u775b\uff0c\u4f3c\u5df2\u7761\u7740\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5e74\u5230\u4e00\u58f0\u60e8\u53eb\uff0c\u547c\u58f0\u5e76\u4e0d\u54cd\uff0c\u800c\u4e14\u5f88\u77ed\u4fc3\u3002 \u3000\u3000\u4ed6\u76b1\u4e86\u76b1\u7709\uff1a\u96be\u9053\u53c8\u6709\u4eba\u6765\u6551\u6211\u4e86\u4e48\uff1f \u3000\u3000\u63a5\u7740\u4ed6\u5c31\u770b\u5230\u4e00\u4e2a\u624b\u63d0\u7740\u94c1\u7b1b\u7684\u9752\u888d\u4eba\u5927\u6b65\u8d70\u4e86\u8fdb\u6765\uff0c\u8138\u4e0a\u867d\u5df2\u5168\u65e0\u8840\u8272\uff0c\u5374\u6ee1\u542b\u7740\u6740 \u673a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u505c\u7559\u5728\u4ed6\u624b\u4e0a\u7684\u94c1\u7b1b\u4e0a\uff0c\u9053\uff1a\u94c1\u7b1b\u5148\u751f\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u76ef\u7740\u4ed6\u7684\u8138\uff0c\u9053\uff1a\u4f60\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u4f60\u770b\u6211\u9762\u524d\u6709\u9152\u90fd\u6ca1\u6709\u559d\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u662f\u52a8\u4e5f\u4e0d\u80fd\u52a8\u4e86\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u9053\uff1a\u4f60\u65e2\u7136\u5df2\u5168\u65e0\u62b5\u6297\u4e4b\u529b\uff0c\u6211\u5c31\u672c\u4e0d\u8be5\u6740\u4f60\u7684\uff0c\u53ef\u662f\u6211\u5374\u975e\u6740\u4f60\u4e0d\u53ef\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u94c1\u7b1b\u5148\u751f\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u4f60\u4e0d\u95ee\u6211\u4e3a\u4f55\u8981\u6740\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u82e5\u95ee\u4e86\uff0c\u53cd\u800c\u96be\u514d\u8981\u751f\u6c14\uff0c\u8981\u5411\u4f60\u89e3\u91ca\uff0c\u4f60\u4e00\u5b9a\u8fd8\u662f\u4e0d\u4fe1\uff0c\u8fd8 \u662f\u8981\u6740\u6211\uff0c\u6211\u53c8\u4f55\u5fc5\u591a\u8d39\u53e3\u820c\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u6014\u4e86\u6014\uff0c\u5927\u58f0\u9053\uff1a\u4e0d\u9519\uff0c\u65e0\u8bba\u4f60\u8bf4\u4ec0\u4e48\uff0c\u6211\u90fd\u8981\u6740\u4f60\u7684\u2014\u2014 \u3000\u3000\u4ed6\u9762\u4e0a\u6cdb\u8d77\u4e00\u9635\u6fc0\u52a8\u75db\u82e6\u4e4b\u8272\uff0c\u55c4\u58f0\u9053\uff1a\u5982\u610f\uff0c\u4f60\u6b7b\u5f97\u867d\u60e8\uff0c\u4f46\u6211\u603b\u7b97\u4e3a\u4f60\u590d\u4ec7\u4e86\u3002 \u3000\u3000\u94c1\u7b1b\u53c8\u5df2\u62ac\u8d77\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u5982\u610f\uff0c\u4f60\u89c1\u5230\u6211\u65f6\u4e00\u5b9a\u4f1a\u5927\u5403\u4e00\u60ca\u7684\uff0c\u56e0\u4e3a\u4f60\u65e2\u4e0d\u8ba4\u5f97\u6211\uff0c \u6211\u8ba4\u5f97\u4f60\u2014\u2014 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u6797\u8bd7\u97f3\u8d70\u4e86\u8fdb\u6765\uff0c\u5927\u58f0\u9053\uff1a\u7b49\u4e00\u7b49\uff0c\u6211\u6709\u8bdd\u8bf4\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u4e00\u60ca\u56de\u5934\uff0c\u9053\uff1a\u592b\u4eba\uff0c\u662f\u4f60\uff1f\u4f60\u6700\u597d\u83ab\u8981\u62e6\u4f4f\u6211\uff0c\u8c01\u4e5f\u62e6\u4e0d\u4f4f\u6211\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u8138\u8272\u53d1\u9752\uff0c\u9053\uff1a\u6211\u5e76\u4e0d\u60f3\u62e6\u4f60\uff0c\u4f46\u8fd9\u662f\u6211\u7684\u5bb6\uff0c\u6740\u4eba\u81f3\u5c11\u603b\u5f97\u8ba9\u6211\u52a8\u624b\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u76b1\u7709\u9053\uff1a\u4f60\u4e5f\u6740\u4ed6\uff1f\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6211\u8981\u6740\u4ed6\u7684\u7406\u7531\u6bd4\u4f60\u66f4\u5927\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e3a\u59bb\u5b50\u590d\u4ec7\uff0c\u6211\u5374\u662f\u4e3a\u513f\u5b50\u590d\u4ec7\uff0c\u6211 \u53ea\u6709\u4e00\u4e2a\u513f\u5b50\u3002 \u3000\u3000\u5979\u8a00\u4e0b\u4e4b\u610f\uff0c\u81ea\u7136\u662f\u8bf4\uff0c\u4f60\u5374\u4e0d\u6b62\u4e00\u4e2a\u59bb\u5b50\u3002 \u3000\u3000\u94c1\u7b1b\u5148\u751f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9053\uff1a\u597d\uff0c\u6211\u7b49\u4f60\u5148\u51fa\u624b\u4e4b\u540e\u518d\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u81ea\u4fe1\u4ed6\u7684\u94c1\u7b1b\u94f6\u9489\u5feb\u5982\u95ea\u7535\uff0c\u7eb5\u7136\u540e\u53d1\uff0c\u4e5f\u53ef\u5148\u81f3\uff0c\u8c01\u77e5\u6797\u8bd7\u97f3\u8d70\u8fc7\u4ed6\u9762\u524d\uff0c\u5ffd\u7136\u53cd \u624b\u4e00\u638c\uff0c\u5411\u4ed6\u80f8\u819b\u51fb\u51fa\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u867d\u7136\u6b66\u529f\u4e0d\u9ad8\uff0c\u4f46\u6bd5\u7adf\u4e0d\u662f\u5f31\u4e0d\u7981\u98ce\u7684\u5f31\u5973\u5b50\u3002\u8fd9\u4e00\u638c\u5979\u5df2\u7528\u4e86\u5168\u529b\uff0c\u94c1\u7b1b\u5148\u751f \u731d\u4e0d\u53ca\u9632\uff0c\u7adf\u88ab\u6253\u5f97\u63b8\u5230\u5899\u4e0a\u3002 \u3000\u3000\u8981\u77e5\u4ed6\u4f24\u52bf\u672c\u5df2\u96be\uff0c\u5168\u51ed\u6697\u5668\u4f24\u4eba\uff0c\u6b64\u523b\u8eab\u5b50\u4e00\u9707\uff0c\u4f24\u53e3\u8ff8\u88c2\uff0c\u9c9c\u8840\u53c8\u98de\u6e85\u800c\u51fa\uff0c\u4eba\u4e5f \u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u5fc3\u5934\u4e00\u9635\u6fc0\u52a8\uff0c\u51e0\u4e4e\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77e5\u9053\u5979\u4e00\u751f\u4e2d\u7b80\u76f4\u8fde\u8682\u8681\u90fd\u672a\u8e29\u6b7b\u8fc7\uff01\u6b64\u523b\u89c1\u5979\u5c45\u7136\u51fa\u624b\u4f24\u4eba\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u75bc \u662f\u559c\uff0c\u5374\u786c\u4e0b\u5fc3\u80a0\u51b7\u51b7\u9053\uff1a\u4f60\u53c8\u8dd1\u6765\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u6df1\u6df1\u7684\u547c\u5438\u4e86\u51e0\u6b21\uff0c\u8eab\u5b50\u624d\u505c\u6b62\u53d1\u6296\uff0c\u9053\uff1a\u6211\u6765\u653e\u4f60\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u96be\u9053\u8fd8\u6ca1\u6709\u8bf4\u6e05\u695a\u4e48\uff1f\u6211\u4e0d\u8d70\uff0c\u7edd\u4e0d\u8d70\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u9f99\u5578\u4e91\u800c\u4e0d\u80af\u8d70\uff0c\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u2014\u2014\u4ed6 \u3000\u3000\u5979\u600e\u53c8\u98a4\u6296\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u6296\u5f97\u6bd4\u521a\u624d\u66f4\u5389\u5bb3\uff0c\u5979\u7528\u529b\u634f\u7d27\u53cc\u62f3\uff0c\u6307\u7532\u90fd\u5df2\u523a\u5165\u8089\u91cc\uff0c\u7528 \u5c3d\u4e86\u5168\u8eab\u529b\u6c14\uff0c\u6323\u624e\u7740\u9053\uff1a\u4ed6\u5df2\u51fa\u5356\u4e86\u4f60\uff0c\u4ed6\u672c\u6765\u5c31\u548c\u90a3\u4e9b\u4eba\u4e32\u901a\u4e00\u6c14\u7684\u2014\u2014 \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5979\u5df2\u5168\u8eab\u8131\u529b\uff0c\u82e5\u975e\u501a\u7740\u684c\u5b50\uff0c\u5c31\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u5979\u4ee5\u4e3a\u674e\u5bfb\u6b22\u542c\u4e86\u8fd9\u8bdd \uff0c\u5fc5\u5b9a\u4e5f\u96be\u514d\u8981\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8c01\u77e5\u674e\u5bfb\u6b22\u7684\u795e\u8272\u5374\u6ca1\u6709\u4e1d\u6beb\u53d8\u5316\uff0c\u751a\u81f3\u8fde\u773c\u89d2\u7684\u808c\u8089\u90fd\u6ca1\u6709\u8df3\u52a8\uff0c\u53cd\u800c\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1 \u9053\uff1a\u4f60\u53ea\u6015\u662f\u8bef\u4f1a\u4e86\u4ed6\uff0c\u4ed6\u600e\u4f1a\u51fa\u5356\u6211\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u7528\u529b\u6293\u7740\u684c\u5b50\uff0c\u684c\u5b50\u4e0a\u7684\u676f\u76cf\u53ee\u5f53\u76f4\u54cd\u3002 \u3000\u3000\u5979\u5636\u58f0\u9053\uff1a\u6211\u4eb2\u773c\u770b\u5230\u7684\uff0c\u4eb2\u8033\u542c\u5230\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u770b\u9519\u4e86\uff0c\u4e5f\u542c\u9519\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4f60\u5230\u73b0\u5728\u8fd8\u4e0d\u76f8\u4fe1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u8fd9\u4e24\u5929\u4f60\u592a\u7d2f\uff0c\u96be\u514d\u4f1a\u5f04\u9519\u5f88\u591a\u4e8b\uff0c\u8fd8\u662f\u53bb\u597d\u597d\u7761\u4e00\u89c9\u5427\uff0c\u5230\u4e86\u660e\u5929\uff0c \u4f60\u5c31\u4f1a\u77e5\u9053\u4f60\u7684\u4e08\u592b\u662f\u4e2a\u5f88\u53ef\u9760\u7684\u7537\u4eba\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u671b\u7740\u4ed6\uff0c\u5931\u795e\u7684\u5f20\u5927\u4e86\u773c\u775b\uff0c\u770b\u4e86\u4ed6\u5f88\u4e45\u5f88\u4e45\uff0c\u5ffd\u7136\u5012\u5728\u684c\u5b50\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u8d77\u6765 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u8d77\u773c\u775b\uff0c\u4f3c\u4e4e\u5df2\u4e0d\u5fcd\u518d\u770b\u5979\uff0c\u55c4\u58f0\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u2014\u2014 \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u55b7\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4e5f\u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\uff0c\u5341\u51e0\u5e74\u6765\u4e00\u76f4\u63a7\u5236\u7740\u7684\u60c5\u611f\uff0c\u6b64\u523b\u5c31\u50cf\u662f\u5c71\u6d2a\u822c\u5168\u90fd\u7206\u53d1\u4e86\u51fa \u6765\u3002 \u3000\u3000\u5979\u8e09\u8dc4\u6251\u5411\u674e\u5bfb\u6b22\uff0c\u9053\uff1b\u4f60\u4e0d\u8d70\uff0c\u6211\u5c31\u6b7b\u5728\u4f60\u9762\u524d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u54ac\u7d27\u4e86\u7259\u5173\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u4f60\u662f\u6b7b\u662f\u6d3b\uff0c\u4ee5\u6211\u53c8\u6709\u4f55\u5173\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u970d\u7136\u62ac\u5934\uff0c\u77aa\u7740\u4ed6\uff0c\u55c4\u58f0\u9053\uff1a\u4f60\u2014\u2014\u4f60\u2014\u2014\u4f60\u2014\u2014 \u3000\u3000\u5979\u6bcf\u8bf4\u4e00\u4e2a\u4f60\u5b57\uff0c\u5c31\u540e\u9000\u4e00\u6b65\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5979\u53d1\u89c9\u5979\u5df2\u5012\u5728\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u8138\u8272\u6c89\u91cd\u5982\u94c1\u3002 \u3000\u3000\u4ed6\u7d27\u7d27\u7684\u6402\u4f4f\u4e86\u6797\u8bd7\u97f3\u7684\u67d4\u80a9\uff0c\u50cf\u662f\u751f\u6015\u81ea\u5df1\u4e00\u52a8\u624b\uff0c\u6797\u8bd7\u97f3\u4fbf\u8981\u4ece\u4ed6\u8eab\u65c1\u6d88\u5931\uff0c\u800c\u4e14 \u6c38\u4e0d\u590d\u8fd4\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u770b\u5230\u4ed6\u7684\u624b\uff0c\u795e\u60c5\u5ffd\u7136\u9547\u5b9a\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u653e\u5f00\u4f60\u7684\u624b\uff0c\u8bf7\u4f60\u4ee5\u540e\u6c38\u8fdc\u4e5f\u83ab\u8981 \u518d\u78b0\u6211\u3002 \u3000\u3000\u4ed6\u7684\u624b\u7ec8\u4e8e\u7f13\u7f13\u677e\u5f00\uff0c\u51dd\u6ce8\u7740\u6797\u8bd7\u97f3\uff0c\u9053\uff1a\u4f60\u5df2\u5168\u90e8\u77e5\u9053\u4e86\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u51b7\u51b7\u9053\uff1a\u4e16\u4e0a\u7edd\u6ca1\u6709\u80fd\u6c38\u8fdc\u7792\u5f97\u8fc7\u4eba\u7684\u4e8b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u2014\u2014\u5df2\u5168\u90fd\u544a\u8bc9\u4e86\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u5176\u5b9e\u7528\u4e0d\u7740\u5979\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u65e9\u5c31\u77e5\u9053\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4f3c\u4e4e\u4e00\u76f4\u4e0d\u6562\u9762\u5bf9\u4ed6\uff0c\u6b64\u523b\u624d\u970d\u7136\u62ac\u5934\uff0c\u9053\uff1a\u4f60\u77e5\u9053\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u55ef\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u4ec0\u4e48\u65f6\u5019\u77e5\u9053\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5c31\u5728\u4f60\u62c9\u4f4f\u6211\u7684\u624b\uff0c\u8ba9\u7530\u4e03\u70b9\u4e2d\u6211\u7a74\u9053\u7684\u65f6\u5019\uff0c\u4e0d\u8fc7\u2014\u2014\u6211\u867d\u7136 \u77e5\u9053\uff0c\u5374\u5e76\u4e0d\u602a\u4f60\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4f55\u4e0d\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4e3a\u4f55\u8981\u8bf4\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u51dd\u6ce8\u7740\u4ed6\uff0c\u8eab\u5b50\u5ffd\u53c8\u98a4\u6296\u8d77\u6765\uff0c\u9053\uff1a\u4f60\u4e0d\u8d70\uff0c\u662f\u4e0d\u662f\u4e3a\u4e86\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u7709\u9053\uff1a\u4e3a\u4e86\u4f60\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4f60\u6015\u6211\u77e5\u9053\u4e86\u4f1a\u4f24\u5fc3\uff0c\u4f60\u4e0d\u613f\u5c06\u6211\u4eec\u8fd9\u5bb6\u62c6\u6563\uff0c\u56e0\u4e3a\u6211\u4eec\u8fd9\u5bb6\u672c\u5c31\u662f\u4f60\u2014\u2014 \u4f60\u2014\u2014 \u3000\u3000\u5979\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53c8\u6cea\u6d41\u6ee1\u9762\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u5927\u7b11\u9053\uff1a\u5973\u4eba\u4e3a\u4ec0\u4e48\u603b\u662f\u8fd9\u6837\u81ea\u6211\u9676\u9189\uff0c\u6211\u4e0d\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u8bf4 \u4e86\u4e5f\u65e0\u7528\uff0c\u6211\u4e0d\u8d70\uff0c\u53ea\u56e0\u4e3a\u660e\u767d\u4ed6\u4e0d\u4f1a\u8ba9\u6211\u8d70\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u73b0\u5728\u65e0\u8bba\u4f60\u600e\u79d1\u8bf4\u90fd\u6ca1\u5173\u7cfb\u4e86\uff0c\u6211\u53cd\u6b63\u5df2\u77e5\u9053\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u77e5\u9053\uff0c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u4f60\u53ef\u77e5\u9053\u9f99\u5578\u4e91\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u8c01\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u5c31\u662f\u6015 \u6211\u6765\u5c06\u4f60\u4eec\u7684\u5bb6\u62c6\u6563\uff0c\u6240\u4ee5\u8fd9\u6837\u505a\u7684\uff01\u53ea\u56e0\u4e3a\u4ed6\u5c06\u8fd9\u5bb6\u770b\u5f97\u6bd4\u4ec0\u4e48\u90fd\u91cd\uff0c\u66f4\u5c06\u4f60\u770b\u5f97\u6bd4\u4ec0\u4e48 \u90fd\u91cd\u2014\u2014 \u3000\u3000\u6797\u8bd7\u97f3\u671b\u7740\u4ed6\uff0c\u5ffd\u4e5f\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u4ed6\u5bb3\u4e86\u4f60\uff0c\u4f60\u8fd8\u8981\u66ff\u4ed6\u8bf4\u8bdd\uff0c\u5f88\u597d\uff0c\u4f60\u7684\u786e\u5f88\u591f\u670b \u53cb\uff0c\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e5f\u662f\u4eba\u2014\u2014\u4f60\u5bf9\u4e0d\u5bf9\u5f97\u8d77\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5267\u70c8\u7684\u54b3\u55fd\u8d77\u6765\uff0c\u54b3\u51fa\u4e86\u8840\u3002 \u3000\u3000\u9f99\u5578\u4e91\u75af\u72c2\u822c\u5927\u543c\u9053\uff1a\u6211\u672c\u6765\u662f\u8fd9\u5bb6\u7684\u4e3b\u4eba\uff0c\u4f46\u4f60\u4e00\u6765\uff0c\u6211\u5c31\u89c9\u5f97\u597d\u50cf\u53ea\u4e0d\u8fc7\u662f\u5728\u8fd9\u91cc \u4f5c\u5ba2\uff0c\u6211\u672c\u6765\u6709\u4e2a\u597d\u513f\u5b50\uff0c\u4f46\u4f60\u6765\uff0c\u5c31\u53eb\u4ed6\u53d8\u5f97\u534a\u6b7b\u4e0d\u6d3b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u53f9\u9053\uff1a\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u2014\u2014\u6211\u7684\u786e\u4e0d\u8be5\u6765\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u95ed\u7740\u773c\u775b\uff0c\u773c\u89d2\u7684\u6cea\u73e0\u5982\u73cd\u73e0\u822c\u843d\u4e0b\uff0c\u9053\uff1a\u4f60\u82e5\u8fd8\u6709\u4e00\u5206\u4e3a\u6211\u7740\u60f3\uff0c\u5c31\u4e0d\u8be5\u8fd9\u6837 \u505a\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u4e0d\u8be5\u8fd9\u6837\u505a\uff0c\u4f46\u6211\u5374\u5b9e\u5728\u5bb3\u6015\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4f60\u6015\u4ec0\u4e48\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6211\u6015\u4f60\u79bb\u5f00\u6211\uff0c\u56e0\u4e3a\u4f60\u867d\u7136\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u2014\u2014\u4f60\u5e76\u6ca1\u6709\u5fd8\u8bb0\u4ed6\uff0c\u6211\u53ea\u6015 \u4f60\u53c8\u56de\u5230\u4ed6\u90a3\u91cc\u53bb\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u5ffd\u53c8\u8df3\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u62ff\u5f00\u4f60\u7684\u624b\uff0c\u4f60\u4e0d\u4f46\u624b\u72e0\uff0c\u5fc3\u4e5f\u72e0\uff0c\u4f60\u5c06\u6211\u770b\u6210\u4ec0\u4e48\u6837\u7684 \u4eba\u4e86\uff1f\u4f60\u5c06\u4ed6\u770b\u6210\u4ec0\u4e48\u6837\u7684\u4eba\uff01 \u3000\u3000\u5979\u6251\u5012\u5730\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u9053\uff1a\u4f60\u96be\u9053\u5df2\u5fd8\u4e86\u6211\u2014\u2014\u6211\u6bd5\u7adf\u662f\u4f60\u7684\u59bb\u5b50\uff01 \u3000\u3000\u9f99\u5578\u4e91\u7ad9\u5728\u90a3\u91cc\uff0c\u4f3c\u4e4e\u5df2\u53d8\u6210\u4e86\u4e2a\u6728\u5934\u4eba\uff0c\u552f\u6709\u773c\u6cea\u8fd8\u662f\u5728\u4e0d\u505c\u7684\u6d41\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u7740\u4ed6\u4eec\uff0c\u9eef\u7136\u81ea\u8bed\u9053\uff1a\u8fd9\u662f\u8c01\u7684\u9519\u2014\u2014\u8fd9\u7a76\u7adf\u662f\u8c01\u7684\u9519\uff1f \u3000\u3000\u963f\u98de\u53ea\u89c9\u5f97\u8eab\u5b50\u8f6f\u7ef5\u7ef5\u7684\uff0c\u4eff\u4f5b\u8eba\u5728\u4e91\u5806\u91cc\u3002 \u3000\u3000\u4ed6\u9192\u4e86\u8fc7\u6765\uff0c\u5374\u5b9b\u5982\u8fd8\u5728\u68a6\u91cc\u3002 \u3000\u3000\u5728\u4ed6\u68a6\u91cc\uff0c\u4e5f\u6c38\u8fdc\u53ea\u6709\u51b0\u96ea\u3001\u8352\u539f\u3001\u864e\u72fc\uff0c\u6216\u4e00\u8fde\u4e32\u65e0\u7a77\u65e0\u5c3d\u7684\u707e\u7978\uff0c\u6298\u78e8\u3001\u82e6\u96be\u2014\u2014 \uff0d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u8bf4\uff0c\u4f60\u9192\u8fc7\u6765\u4e86\u4e48\uff1f \u3000\u3000\u8fd9\u58f0\u97f3\u662f\u5982\u6b64\u6e29\u67d4\uff0c\u5982\u6b64\u5173\u5207\u3002 \u3000\u3000\u963f\u98de\u5f20\u5f00\u773c\uff0c\u5c31\u770b\u5230\u4e86\u4e00\u5f20\u7edd\u7f8e\u7684\u8138\uff0c\u8138\u4e0a\u5e26\u7740\u4e16\u4e0a\u6700\u6e29\u67d4\u3001\u6700\u53ef\u7231\u7684\u7b11\u5bb9\uff0c\u773c\u6ce2\u91cc\u5e26 \u7740\u6700\u6df1\u539a\u7684\u60c5\u610f\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u6e29\u67d4\u7f8e\u4e3d\u5f97\u51e0\u4e4e\u5c31\u50cf\u662f\u4ed6\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u8fd9\u5f20\u8138\u6e29\u67d4\u7f8e\u4e3d\u5f97\u51e0\u4e4e\u5c31\u50cf\u662f\u4ed6\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u5728\u5c0f\u65f6\u751f\u75c5\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u6bcd\u4eb2\u4e5f\u662f\u8fd9\u4e48\u6837\u5750\u5728\u4ed6\u8eab\u8fb9\uff0c\u4e5f\u662f\u8fd9\u6837\u6e29\u67d4\u7684\u770b\u5b88\u7740\u4ed6 \u3002 \u3000\u3000\u4f46\u8fd9\u5df2\u662f\u8bb8\u4e45\u8bb8\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u4e45\u8fdc\u5f97\u8fde\u4ed6\u81ea\u5df1\u90fd\u5df2\u51e0\u4e4e\u5fd8\u8bb0\u2014\u2014 \u3000\u3000\u963f\u98de\u6323\u624e\u7740\u8981\u8df3\u4e0b\u5e8a\uff0c\u55c4\u58f0\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u4ed6\u8eab\u5b50\u521a\u5750\u8d77\uff0c\u53c8\u5012\u4e0b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6e29\u67d4\u7684\u66ff\u4ed6\u62c9\u8d77\u4e86\u88ab\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u83ab\u8981\u7ba1\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u5c31\u5c06\u8fd9\u91cc\u5f53\u505a\u4f60\u81ea\u5df1\u7684 \u5bb6\u5427\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u7684\u5bb6\uff1f \u3000\u3000\u4ed6\u4ece\u6765\u6ca1\u6709\u5bb6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u201c\u6211\u60f3\u4f60\u7684\u5bb6\u4e00\u5b9a\u5f88\u6e29\u6696\uff0c\u56e0\u4e3a\u4f60\u6709\u90a3\u4e48\u6837\u4e00\u4e2a\u597d\u6bcd\u4eb2\uff0c\u5979\u4e00\u5b9a\u5f88\u6e29\u67d4 \uff0c\u5f88\u7f8e\u4e3d\uff0c\u4e5f\u5f88\u7231\u4f60\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u7740\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u624d\u7f13\u7f13\uff1a\u201c\u6211\u6ca1\u6709\u5bb6\uff0c\u4e5f\u6ca1\u6709\u6bcd\u4eb2\u3002\u201d\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4f60\u6655\u8ff7\u7684\u65f6\u5019\u5374\u4e00\u76f4\u547c\u5524\u7740\u5979\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u52a8\uff0c\u9762\u4e0a\u4e5f\u6ca1\u6709\u8868\u60c5\uff0c\u9053\uff1a\u201c\u6211\u4e03\u5c81\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u867d\u6ca1\u6709\u8868\u60c5\uff0c\u773c\u775b\u5374\u5df2\u6e7f\u6da6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u2026\u2026\u6211\u4e0d\u8be5\u63d0\u8d77\u4e86\u4f60\u7684\u4f24\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u53c8\u6c89\u9ed8\u4e86\u534a\u54cd\uff0c\u963f\u98de\u9053\uff1a\u201c\u662f\u4f60\u6551\u4e86\u6211\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u5df2\u6655\u4e86\u8fc7\u53bb\uff0c\u6240\u4ee5\u6211\u5c31\u6682\u65f6\u5c06\u4f60\u642c\u5230\u8fd9\u91cc\u6765\uff0c\u4f46\u4f60\u53ea\u7ba1\u5b89\u5fc3\u517b\u4f24\uff0c \u7edd\u6ca1\u6709\u4eba\u6562\u95ef\u5230\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u518d\u4e09\u5429\u5490\u8868\uff0c\u53eb\u6211\u6c38\u8fdc\u83ab\u8981\u53d7\u522b\u4eba\u7684\u6069\u60e0\uff0c\u8fd9\u53e5\u8bdd\u6211\u6c38 \u8fdc\u4e5f\u6ca1\u6709\u5fd8\u8bb0\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d\u3002 \u3000\u3000\u4ed6\u5ca9\u77f3\u822c\u7684\u8138\u5ffd\u7136\u6fc0\u52a8\u8d77\u6765\uff0c\u55c4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u6211\u5374\u6b20\u4e86\u4f60\u4e00\u6761\u547d\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u4e5f\u4e0d\u6b20\u6211\uff0c\u83ab\u5fd8\u4e86\uff0c\u6211\u8fd9\u6761\u547d\u4e5f\u662f\u4f60\u6551\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u98de\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u5583\u5583\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u8981\u6551\u6211\uff1f\u4e3a\u4f55\u8981\u6551\u6211\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u8109\u8109\u5730\u671b\u7740\u4ed6\uff0c\u60c5\u4e0d\u81ea\u7981\u4f38\u51fa\u624b\uff0c\u8f7b\u629a\u7740\u4ed6\u7684\u8138\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4ec0\u4e48\u4e5f\u4e0d\u8981 \u60f3\uff0c\u4ee5\u540e\u2014\u2014\u4ee5\u540e\u4f60\u5c31\u4f1a\u77e5\u9053\u6211\u2014\u2014\uff0d\u4e3a\u4ec0\u4e48\u8981\u6551\u4f60\uff1f\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5bf9\u4f60\u3002 \u3000\u3000\u5979\u7684\u624b\u67d4\u82e5\u65e0\u9aa8\uff0c\u6e29\u5982\u7f8e\u7389\u3002 \u3000\u3000\u963f\u98de\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u672a\u60f3\u5230\uff0c\u81ea\u5df1\u7adf\u4e5f\u4f1a\u6709\u8fd9\u79cd\u611f\u60c5\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u53ea\u662f\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u9053\uff1a\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8fd8\u4e0d\u5230\u4e09\u665a\u3002 \u3000\u3000\u963f\u98de\u53c8\u6323\u624e\u7740\u8981\u5750\u8d77\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u2014\u2014\u4f60\u60f3\u5230\u54ea\u91cc\u53bb\uff01 \u3000\u3000\u963f\u98de\u54ac\u7d27\u7259\u5173\uff0c\u9053\uff1a\u6211\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u5c06\u674e\u5bfb\u6b22\u5e26\u8d70\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f46\u4ed6\u5df2\u7ecf\u8d70\u4e86\u3002 \u3000\u3000\u963f\u98de\u5657\u5730\u5012\u5728\u5e8a\u4e0a\uff0c\u6c57\u5982\u96e8\u4e0b\u9053\uff1a\u4f60\u8bf4\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u4e09\u665a\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u73b0\u5728\u662f\u8fd8\u6ca1\u6709\u5230\u4e09\u66f4\uff0c\u4f46\u674e\u5bfb\u6b22\u6628\u5929\u51cc\u6668\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u963f\u98de\u5931\u58f0\u9053\uff1a\u6628\u5929\u51cc\u6668\uff1f\u6211\u96be\u9053\u5df2\u6655\u7761\u4e86\u4e00\u5929\u4e00\u591c\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4f24\u5f97\u5f88\u91cd\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u53ea\u6015\u6ca1\u6709\u522b\u4eba\u80fd\u6328\u5f97\u4f4f\u7684\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u4e00\u5b9a\u8981\u4e58 \u4e58\u5730\u542c\u8bdd\uff0c\u597d\u597d\u5730\u517b\u4f24\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f46\u662f\u674e\u2014\u2014 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u4e0d\u8bb8\u4f60\u518d\u63d0\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u7684\u5904\u5883\u8fdc\u4e0d\u5982\u4f60\u5371\u9669\uff0c\u5c31\u7b97\u4f60\u8981\u6551\u4ed6\uff0c\u4e5f\u5f97\u7b49\u4f60\u517b \u597d\u4e86\u4f24\u518d\u8bf4\u3002 \u3000\u3000\u5979\u6276\u7740\u4ed6\u8eba\u5230\u6795\u5934\u4e0a\uff0c\u9053\uff1a\u4f60\u653e\u5fc3\uff0c\u5fc3\u7709\u5927\u5e08\u65e2\u7136\u8bf4\u8981\u5c06\u4ed6\u5e26\u5230\u5c11\u6797\u5bfa\u53bb\uff0c\u90a3\u4e48\u4ed6\u8fd9\u4e00 \u8def\u4e0a\u5c31\u7edd\u4e0d\u4f1a\u518d\u6709\u4ec0\u4e48\u5371\u9669\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u659c\u501a\u5728\u8f66\u53a2\u91cc\uff0c\u77a7\u7740\u5bf9\u9762\u7684\u5fc3\u7709\u5927\u5e08\u548c\u7530\u4e03\uff0c\u4f3c\u4e4e\u89c9\u5f97\u5f88\u6709\u8da3\uff0c\u5ffd\u7136\u5fcd\u4e0d\u4f4f\u7b11\u4e86 \u3002 \u3000\u3000\u7530\u4e03\u77aa\u7740\u4ed6\u9053\uff1a\u4f60\u89c9\u5f97\u6211\u4eec\u5f88\u6ed1\u7a3d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u53ea\u662f\u89c9\u5f97\u6709\u8da3\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u6709\u8da3\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u95ed\u4e0a\u773c\uff0c\u4f3c\u4e4e\u8981\u7761\u7740\u4e86\u3002 \u3000\u3000\u7530\u4e03\u4e00\u628a\u63ea\u4f4f\u4e86\u4ed6\uff0c\u9053\uff1a\u6211\u54ea\u70b9\u6709\u8da3\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u62b1\u6b49\uff0c\u6211\u8bf4\u7684\u5e76\u4e0d\u662f\u4f60\uff0c\u4e16\u4e0a\u867d\u7136\u6709\u5f88\u591a\u4eba\u90fd\u5f88\u6709\u8da3\uff0c\u4f46\u4f60\u5374\u662f\u4f8b\u5916\uff0c \u4f60\u5b9e\u5728\u65e0\u8da3\u6781\u4e86\u3002 \u3000\u3000\u7530\u4e03\u8138\u8272\u53d8\u4e86\uff0c\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u7ec8\u4e8e\u7f13\u7f13\u677e\u5f00\u4e86\u624b\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\uff0c\u6b64\u523b\u5374\u5fcd\u4e0d\u4f4f\u9053\uff1a\u4f60\u89c9\u5f97\u8001\u50e7\u5f88\u6709\u8da3\uff1f \u3000\u3000\u4ed6\u8fd9\u8f88\u5b50\u8fd8\u6ca1\u6709\u9047\u89c1\u8fc7\u4e00\u4e2a\u8bf4\u4ed6\u6709\u8da3\u7684\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u7b11\u9053\uff1a\u6211\u89c9\u5f97\u4f60\u6709\u8da3\uff0c\u53ea\u56e0\u4e3a\u6211\u8fd8\u672a\u89c1\u8fc7\u4e00\u4e2a\u5750\u8f66\u7684\u548c\u5c1a\uff0c\u6211\u603b \u8ba4\u4e3a\u51fa\u5bb6\u4eba\u65e2\u4e0d\u80fd\u9a91\u9a6c\uff0c\u4e5f\u4e0d\u80fd\u5750\u8f66\u7684\u3002 \u3000\u3000\u5fc3\u7709\u5c45\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u548c\u5c1a\u4e5f\u662f\u4eba\uff0c\u4e0d\u4f46\u8981\u5750\u8f66\uff0c\u8fd8\u8981\u5403\u996d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1b\u4f60\u65e2\u7136\u5df2\u5750\u5728\u8f66\u4e0a\uff0c\u4e3a\u53ef\u4e0d\u5750\u5f97\u8212\u670d\u4e9b\uff0c\u770b\u4f60\u8fd9\u6837\u5750\u7740\uff0c\u603b\u5fcd\u4e0d\u4f4f\u4ee5\u4e3a\u4f60\u957f \u4e86\u75d4\u75ae\u3002 \u3000\u3000\u5fc3\u7709\u8138\u8272\u4e5f\u6c89\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u4f60\u96be\u9053\u60f3\u6211\u585e\u4f4f\u4f60\u7684\u5634\uff1f \u3000\u3000\u5fc3\u7709\u671b\u4e86\u7530\u4e03\u4e00\u773c\uff0c\u7530\u4e03\u7684\u624b\u7f13\u7f13\u4f38\u5230\u674e\u5bfb\u6b22\u7684\u5927\u7a74\u4e0a\uff0c\u60a0\u60a0\u7b11\u9053\uff1a\u6211\u8fd9\u53ea\u624b\u4e00\u6309\uff0c\u4f60 \u77e5\u9053\u5c31\u4f1a\u600e\u4e48\u6837\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4f60\u8fd9\u53ea\u624b\u82e5\u4e00\u6309\uff0c\u5c31\u542c\u4e0d\u5230\u5f88\u591a\u6709\u8da3\u7684\u8bdd\u4e86\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u90a3\u4e48\u5c31\u7b97\u6211\u2014\u2014 \u3000\u3000\u521a\u8bf4\u5230\u8fd9\u91cc\u3002\u4ed6\u7684\u624b\u8fd8\u672a\u6309\u4e0b\u53bb\uff0c\u7a81\u7136\u5065\u9a6c\u4e00\u58f0\u60ca\u5636\uff0c\u8d76\u8f66\u7684\u8fde\u58f0\u6012\xd7\uff0c\u9a6c\u8f66\u9aa4\u7136\u505c\u4e86 \u4e0b\u6765\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ec0\u4e48\u4e8b\uff1f\u96be\u9053\u4f60\u4eec\u2014\u2014\uff0d \u3000\u3000\u4ed6\u7684\u5934\u63a2\u51fa\u8f66\u7a97\uff0c\u5634\u5c31\u95ed\u4e0a\uff0c\u8138\u8272\u53d8\u4e86\uff01 \u3000\u3000\u79ef\u96ea\u7684\u9053\u8def\u65c1\u76f4\u633a\u633a\u5730\u7ad9\u7740\u4e00\u4e2a\u4eba\uff0c\u53f3\u624b\u62c9\u4f4f\u9a6c\u8f66\u8f94\u5934\uff0c\u5065\u9a6c\u957f\u5636\u8df3\u8dc3\uff0c\u4ed6\u7684\u624b\u5374\u5982\u94c1 \u94f8\u822c\u5730\u52a8\u4e5f\u4e0d\u52a8\uff01 \u7b2c\u5341\u516b\u7ae0\u3000\u4e00\u65e5\u6570\u60ca>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u4e00\u65e5\u6570\u60ca \u3000\u3000\u90a3\u4eba\u8eab\u4e0a\u7a7f\u7740\u4ef6\u9752\u5e03\u888d\uff0c\u5927\u8896\u98d8\u98d8\uff0c\u8fd9\u4ef6\u957f\u888d\u65e0\u8bba\u7a7f\u5728\u8c01\u5728\u8eab\u4e0a\u90fd\u4f1a\u5acc\u592a\u957f\uff0c\u4f46\u7a7f\u5728\u4ed6 \u8eab\u4e0a\uff0c\u5e03\u8fd8\u76d6\u4e0d\u5230\u4ed6\u7684\u819d\u76d6\u3002 \u3000\u3000\u4ed6\u672c\u5c31\u5df2\u957f\u5f97\u5413\u4eba\uff0c\u5934\u4e0a\u5374\u504f\u504f\u8fd8\u6234\u7740\u9876\u5947\u5f62\u602a\u72b6\u7684\u9ad8\u5e3d\u5b50\uff0c\u9aa4\u7136\u671b\u53bb\uff0c\u5c31\u8c61\u662f\u4e00\u68f5\u67af \u6811\u3002 \u3000\u3000\u4e00\u53ea\u624b\u5c31\u80fd\u529b\u633d\u5954\u9a6c\uff0c\u8fd9\u4efd\u529b\u91cf\u5b9e\u5728\u5927\u91cf\u5927\u5f97\u53ef\u6015\uff0c\u4f46\u66f4\u53ef\u6015\u7684\u5374\u662f\u4ed6\u7684\u773c\u775b\uff0c\u90a3\u79d1\u4e0d \u50cf\u662f\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u7adf\u662f\u9752\u8272\u7684\uff0c\u773c\u7403\u662f\u9752\u8272\u7684\uff0c\u773c\u767d\u4e5f\u662f\u9752\u8272\u7684\uff0c\u4e00\u95ea\u4e00\u95ea\u7684\u53d1\u7740\u5149\uff0c\u5c31\u50cf\u662f\u661f \u706b\u3002 \u3000\u3000\u7530\u4e03\u7684\u5934\u521a\u4f38\u51fa\u53bb\uff0c\u53c8\u7f29\u4e86\u56de\u6765\uff0c\u5634\u5507\u5df2\u6709\u4e9b\u53d1\u767d\u3002 \u3000\u3000\u5fc3\u7709\u5927\u91cf\u5e08\u9053\uff1a\u201c\u5916\u9762\u6709\u4eba\uff1f\u201d \u3000\u3000\u7530\u4e03\u9053\uff1a\u201c\u4f0a\u54ed\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u670b\u53cb\u4e5f\u50cf\u6211\u522b\u7684\u670b\u53cb\u4e00\u6837\uff0c\u5c31\u60f3\u8981\u6211\u7684\u8111\u888b\u3002\u201d \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9762\u8272\u51dd\u91cd\uff0c\u7f13\u7f13\u63a8\u5f00\u95e8\u8d70\u8fc7\u53bb\uff0c\u5408\u5341\u75b2\u9053\uff1a\u201c\u4f0a\u6a80\u8d8a\uff1f\u201d \u3000\u3000\u9752\u9b54\u624b\u78a7\u68ee\u68ee\u7684\u76ee\u5149\uff0c\u4e0a\u4e0b\u4e00\u626b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u662f\u5fc3\u6e56\uff1f\u8fd8\u662f\u5fc3\u7709\uff1f\u201d \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u4e0d\u6253\u8c0e\u8bed\uff0c\u8f66\u4e0a\u7684\u9664\u4e86\u7530\u4e03\u7237\u5916\u8fd8\u6709\u4e00\u4f4d\u674e\u6a80\u8d8a\u3002\u201d \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c06\u674e\u5bfb\u6b22\u4ea4\u51fa\u6765\uff0c\u6211\u653e\u4f60\u8d70\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u53bb\u8fd8\u662f\u53e5\u8bdd\uff0c\u522b\u4eba\u65e0\u8bba\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u5168\u90fd\u5145\u8033\u4e0d\u95fb\uff0c\u9634\u68ee\u68ee\u7684\u4e00\u5f20\u8138\u66f4\u597d\u50cf\u662f\u6b7b\u4eba\u7684 \u8138\uff0c\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u201c\u50e7\u82e5\u4e0d\u7b54\u5e94\uff0c\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u201c\u90a3\u5c31\u5148\u6740\u4f60\uff0c\u518d\u6740\u674e\u5bfb\u6b22\uff01\u201d \u3000\u3000\u4ed6\u5de6\u81c2\u4e00\u76f4\u5728\u5782\u7740\u7684\uff0c\u5927\u8896\u98d8\u98d8\uff0c\u76d6\u4f4f\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7684\u624b\u5ffd\u7136\u4f38\u4e86\u51fa\u6765\uff0c\u4f46\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u8fce\u9762\u5411\u5fc3\u7709\u5927\u5e08\u6293\u4e86\u8fc7\u6765\uff0c\u6b63\u662f\u6c5f\u6e56\u4e0a\u95fb\u540d \u4e27\u4f46\u7684\u9752\u9b54\u624b\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u4e00\u58f0\u6012\u53f1\uff0c\u8eab\u540e\u5df2\u6709\u56db\u6761\u7070\u5f71\u5e74\u4e86\u8fc7\u6765\uff0c\u5fc3\u7709\u95ea\u8fc7\u4e86\u8fd9\u4e00\u7740\uff0c\u56db\u4e2a\u7070\u8863\u50e7\u4eba\u5df2 \u5c06\u4f0a\u54ed\u56f4\u4f4f\u3002 \u3000\u3000\u51c4\u5389\u7684\u7b11\u58f0\u4e2d\uff0c\u7a81\u6709\u4e00\u4e1d\u9752\u70df\u5c04\u51fa\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u7f15\u9752\u70df\u5316\u4e86\u6ee1\u5929\u9752\u96fe\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u53d8\u8272\u9053\uff1a\u201c\u5feb\u95ed\u6c14\uff01\u201d \u3000\u3000\u4ed6\u53ea\u987e\u8b66\u544a\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5374\u5fd8\u4e86\u81ea\u5df1\uff0c\u8fd9\u201c\u5feb\u201d\u5b57\u6b63\u662f\u4e2a\u5f00\u53e3\u97f3\uff0c\u201c\u5feb\u201d\u5b57\u8bf4\u51fa\uff0c\u4ed6\u5df2\u89c9 \u5f97\u4e00\u8165\u6c14\u6d41\u5165\u4e86\u5634\u91cc\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4eba\u770b\u5230\u4ed6\u9762\u8272\u60e8\u53d8\uff0c\u4e5f\u90fd\u5927\u4e3a\u5931\u8272\u3002 \u3000\u3000\u53ea\u89c1\u5fc3\u7709\u5927\u5e08\u51cc\u7a7a\u4e00\u7ffb\u8eab\uff0c\u63a0\u51fa\u4e09\u4e08\uff0c\u7acb\u523b\u76d8\u819d\u5750\u5730\uff0c\u8981\u4ee5\u6570\u5341\u5e74\u4fdd\u547d\u4ea4\u4fee\u7684\u771f\u6c14\uff0c\u5c06 \u8fd9\u80a1\u6bd2\u903c\u51fa\u6765\u3002 \u3000\u3000\u5c11\u6797\u50e7\u4eba\u8eab\u5f62\u95ea\u52a8\uff0c\u4e00\u6392\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u5230\u4e86\u8fd9\u65f6\uff0c\u4ed6\u4eec\u5728\u6709\u5148\u987e\u5168\u5fc3\u7709\uff0c\u53ea\u6709\u5c06\u674e\u5bfb\u6b22 \u629b\u5728\u4e00\u8fb9\u4e86\u3002 \u3000\u3000\u4f0a\u54ed\u5374\u8fb9\u770b\u4e5f\u4e0d\u518d\u770b\u4ed6\u4eec\u4e00\u773c\uff0c\u4e00\u6b65\u7a9c\u5230\u8f66\u95e8\u524d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ecd\u659c\u5750\u5728\u90a3\u91cc\uff0c\u7530\u4e03\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4f0a\u5668\u77aa\u7740\u674e\u5bfb\u6b22\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4e18\u72ec\u662f\u4f60\u6740\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u201c\u597d\uff0c\u4e18\u72ec\u4e00\u547d\u6362\u674e\u5bfb\u6b22\u4e00\u547d\uff0c\u4e5f\u7b97\u6b7b\u5f97\u4e0d\u51a4\u4e86\u3002 \u3000\u3000\u9752\u9b54\u624b\u53c8\u5df2\u626c\u8d77\u2014\u2014 \u3000\u3000\u963f\u98de\u671b\u7740\u5c4b\u9876\uff0c\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u8def\u4e0a\u7edd\u4e0d\u4f1a\u6709\u5371\u9669\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\uff0c\u6709\u5fc3\u7709\u5927\u5e08\u548c\u7530\u4e03\u4fdd\u62a4\u4ed6\uff0c\u8c01\u6562\u78b0\u4ed6\u4e00\u6839\u624b\u6307\uff1f\u201d \u3000\u3000\u4ed6\u8f7b\u629a\u7740\u963f\u98de\u7684\u5934\u53d1\uff0c\u9053\uff1a\u201c\u4f60\u8981\u60f3\u5ff5\u6211\uff0c\u5c31\u653e\u5fc3\u7761\u5427\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\uff0c\u7edd\u4e0d\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u4ed6\uff0c\u5979\u773c\u6ce2\u662f\u90a3\u4e48\u6e29\u67d4\uff0c\u90a3\u4e48\u771f\u631a\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u5e18\u7ec8\u4e8e\u7f13\u7f13\u95ed\u8d77\u3002 \u3000\u3000\u4f0a\u54ed\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u72de\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u4ed6\u9752\u5149\u95ea\u95ea\u7684\u9752\u9b54\u624b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u6709\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u4f60\u8bf4\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u6765\u9001\u6b7b\uff1f\u201d \u3000\u3000\u4ed6\u7684\u624b\u5ffd\u7136\u6325\u51fa\uff01 \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u4f0a\u54ed\u5df2\u51cc\u7a7a\u4fa7\u7ffb\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u96ea\u5730\u4e0a\u5df2\u591a\u4e86\u7c92\u9c9c\u8840\uff01 \u3000\u3000\u518d\u770b\u4f0a\u54ed\u7684\u8eab\u5f71\u5df2\u8fdc\u5728\u6570\u4e08\u5916\uff0c\u5636\u58f0\u9053\uff1a\u201c\u674e\u5bfb\u6b22\uff0c\u4f60\u8bb0\u7740\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u8bf4\u4ee5\u8fd9\u91cc\uff0c\u4ed6\u58f0\u97f3\u7a81\u7136\u505c\u987f\u3002 \u3000\u3000\u5bd2\u98ce\u5982\u5200\uff0c\u5929\u5730\u8083\u6740\uff0c\u96ea\u5730\u4e0a\u53d8\u5f97\u6b7b\u4e00\u822c\u9759\u5bc2\u3002 \u3000\u3000\u7136\u540e\u7a81\u6709\u4e00\u9635\u638c\u58f0\u54cd\u8d77\uff0c\u7530\u4e03\u81ea\u8f66\u53a2\u540e\u94bb\u4e86\u51fa\u6765\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u597d\uff0c\u5c0f\u674e\u98de\u5200 \uff0c\u679c\u7136\u5200\u65e0\u865a\u53d1\uff0c\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u534a\u54cd\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u82e5\u80af\u5c06\u6211\u7684\u7a74\u9053\u5168\u89e3\u5f00\uff0c\u4ed6\u4e0d\u8dd1\u4e0d\u4e86\u3002\u201d \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u201c\u6211\u82e5\u5c06\u4f60\u7684\u7a74\u9053\u5168\u90fd\u89e3\u5f00\uff0c\u4f60\u5c31\u8981\u8dd1\u4e86\u3002\u201d \u3000\u3000\u4ed6\u62cd\u4e86\u62cd\u674e\u5bfb\u6b22\u7684\u80a9\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u6709\u4e00\u53ea\u624b\u80fd\u52a8\uff0c\u4e00\u67c4\u5200\u53ef\u6740\uff0c\u5374\u8fd8\u662f\u80fd\u4ee4\u4f0a\u54ed\u8d1f \u4f24\u800c\u9003\uff0c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u6211\u5bf9\u4f60\u600e\u80fd\u4e0d\u7279\u522b\u5c0f\u5fc3\uff0c\u5206\u5916\u7559\u610f\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5c11\u6797\u50e7\u4eba\u5df2\u5c06\u5fc3\u7709\u5927\u5e08\u6276\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u8138\u8272\u8721\u9ec4\uff0c\u4e00\u4e0a\u8f66\u5c31\u5598\u7740\u6c14\u9053\uff1a\u201c\u5feb\uff0c\u5feb\u8d70\u201d\u3002 \u3000\u3000\u7b49\u5230\u8f66\u9a6c\u542f\u884c\uff0c\u5fc3\u957f\u957f\u5410\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u6b79\u6bd2\u7684\u9752\u9b54\u624b\u3002\u201d \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u66f4\u6b79\u6bd2\u7684\u5374\u662f\u5c0f\u674e\u98de\u5200\u3002\u201d \u3000\u3000\u5fc3\u7709\u5927\u5e08\u671b\u5411\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5c45\u7136\u80af\u51fa\u76f8\u6551\uff0c\u5012\u51fa\u4e86\u8001\u50e7\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u6551\u7684\u4e0d\u662f\u4f60\u662f\u6211\u81ea\u5df1\uff0c\u4f60\u7528\u4e0d\u7740\u610f\u5916\uff0c\u4e5f\u7528\u4e0d\u7740\u8c22\u6211\u3002\u201d \u3000\u3000\u7530\u4e03\u9053\uff1a\u201c\u6211\u53ea\u95ee\u4ed6\u662f\u60c5\u613f\u548c\u6211\u4eec\u5230\u5c11\u6797\u5bfa\u53bb\uff0c\u8fd8\u662f\u60c5\u613f\u843d\u5728\u4f0a\u54ed\u624b\u91cc\uff0c\u7136\u540e\u53c8\u89e3\u5f00\u4e86 \u4ed6\u4e00\u53ea\u81c2\u7684\u7a74\u9053\uff0c\u7ed9\u4e86\u4ed6\u4e00\u67c4\u98de\u5200\u3002\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u5c31\u5df2\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9eef\u7136\u4e86\u534a\u54cd\uff0c\u5583\u5583\u9053\uff1a\u201c\u5c0f\u674e\u795e\u5200\u2026\u2026\u5509\uff0c\u597d\u5feb\u7684\u5200\uff01\u201d \u3000\u3000\u5fc3\u7709\u5927\u5e08\u7684\u53cd\u5e94\u867d\u4e0d\u591f\u5feb\uff0c\u4f46\u5185\u529b\u5374\u7684\u786e\u6df1\u6c89\uff0c\u5929\u9ed1\u65f6\u5c31\u5df2\u5c06\u6bd2\u6c14\u903c\u51fa\uff0c\u8138\u8272\u53c8\u6062\u590d\u4e86 \u7ea2\u6da6\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4eec\u5c31\u627e\u5230\u4e86\u5bb6\u6e05\u9759\u7684\u5ba2\u6808\u6b47\u4e0b\uff0c\u665a\u996d\u7684\u65f6\u6ca1\u6709\u9152\uff0c\u5c31\u50cf\u662f\u6ca1\u6709\u52a0\u76d0\u7684\u83dc\uff0c\u6de1\u800c\u65e0 \u5473\uff0c\u65e0\u8da3\u6781\u4e86\u3002\u201d \u3000\u3000\u7530\u4e03\u9053\uff1a\u201c\u6709\u996d\u7ed9\u4f60\u5403\u5df2\u9519\u4e86\uff0c\u6211\u770b\u4f60\u9a6c\u864e\u4e9b\u5427\u3002\u201d \u3000\u3000\u5c11\u6797\u5bfa\u679c\u7136\u662f\u95e8\u89c4\u68ee\u4e25\uff0c\u8fd9\u4e9b\u5c11\u6797\u50e7\u4eba\u4eec\u5403\u996d\u65f6\u975e\u4f46\u4e0d\u8bf4\u8bdd\uff0c\u800c\u4e14\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\uff0c\u684c \u5b50\u4e0a\u867d\u53ea\u6709\u51e0\u6837\u852c\u83dc\uff0c\u4f46\u4ed6\u4eec\u672c\u5c31\u7c97\u83dc\u6de1\u996d\u60ef\u4e86\uff0c\u518d\u52a0\u4e0a\u8fde\u65e5\u5954\u75b2\uff0c\u8179\u4e2d\u9965\u997f\uff0c\u6240\u4ee5\u90fd\u5403\u5f97 \u5f88\u591a\u3002 \u3000\u3000\u53ea\u6709\u5fc3\u7709\u5927\u5e08\u5185\u4f24\u521d\u6108\uff0c\u559d\u4e86\u7897\u7528\u7cd6\u62e6\u7684\u7a00\u7ca5\uff0c\u4fbf\u4e0d\u518d\u62ff\u7b77\uff0c\u7530\u4e03\u65e9\u5df2\u53eb\u4e86\u51e0\u6837\u7cbe\u81f4\u7684 \u83dc\uff0c\u51c6\u5907\u4e00\u4e2a\u4eba\u6162\u6162\u4eab\u7528\uff0c\u6b64\u523b\u4ed6\u7559\u7740\u809a\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u631f\u4e86\u5757\u7ea2\u70e7\u8c46\u8150\uff0c\u521a\u631f\u5230\u5634\u65c1\uff0c\u5ffd\u53c8\u653e\u4e0b\uff0c\u53d8\u8272\u9053\uff1a\u8fd9\u83dc\u5403\u4e0d\u5f97\u3002 \u3000\u3000\u7530\u4e03\u60a0\u7136\u9053\uff1a\u63a2\u82b1\u7237\u82e5\u5403\u4e0d\u60ef\u8fd9\u4e9b\u7c97\u83dc\uff0c\u770b\u6765\u5c31\u53ea\u6709\u6328\u997f\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u58f0\u9053\uff1a\u83dc\u4e2d\u6709\u6bd2\uff01 \u3000\u3000\u7530\u4e03\u5927\u7b11\u9053\uff1a\u4e0d\u8ba9\u4f60\u559d\u9152\uff0c\u4f60\u7684\u82b1\u6837\u679c\u7136\u6765\u4e86\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u2014\u2014 \u3000\u3000\u4ed6\u7b11\u58f0\u9aa4\u7136\u987f\u4f4f\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u4eba\u627c\u4f4f\u4e86\u5589\u5499\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u53d1\u73b0\u90a3\u56db\u5e74\u5c11\u6797\u50e7\u4eba\u7684\u8138\u5df2\u53d8\u6210\u6b7b\u7070\u8272\uff0c\u4f46\u4ed6\u4eec\u5374\u4f3c\u6beb\u65e0\u611f\u89c9\uff0c\u4ecd\u7136\u4f4e\u7740\u5934\u5728\u5403 \u996d\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u4e5f\u5df2\u8038\u7136\u5931\u8272\uff0c\u55c4\u58f0\u9053\uff1a\u5feb\uff0c\u5feb\u4ee5\u4e39\u7530\u4e4b\u6c14\u6536\u4f4f\u5fc3\u8109\u3002 \u3000\u3000\u90a3\u4e9b\u5c11\u6797\u50e7\u4eba\u5c45\u7136\u8fd8\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8d54\u7b11\u9053\uff1a\u5e08\u53d4\u662f\u5728\u5429\u5490\u6211\u4eec\uff1f \u3000\u3000\u5fc3\u7709\u5927\u5e08\u6025\u7740\u9053\uff1a\u81ea\u7136\u662f\u5429\u5490\u4f60\u4eec\uff0c\u4f60\u4eec\u4e2d\u4e86\u6bd2\u96be\u9053\u8fde\u4e00\u70b9\u90fd\u611f\u89c9\u4e0d\u51fa\uff1f \u3000\u3000\u5c11\u6797\u50e7\u4eba\u9053\uff1a\u4e2d\u4e86\u6bd2\uff1f\u8c01\u4e2d\u4e86\u6bd2\uff1f\u2014\u2014\uff0d \u3000\u3000\u56db\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u540c\u65f6\u53eb\u4e86\u8d77\u6765\uff0c\u4f60\u7684\u8138\u600e\u7684\u2014\u2014\uff0d \u3000\u3000\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u56db\u4e2a\u4eba\u5df2\u540c\u65f6\u5012\u4e86\u4e0b\u53bb\uff0c\u7b49\u5fc3\u7709\u5927\u5e08\u518d\u770b\u4ed6\u4eec\uff0c\u56db\u5f20\u8138\u90fd\u5df2\u53d8\u4e86\u5f62\u72b6\uff0c \u773c\u9f3b\u4e94\u5b98\u90fd\u5df2\u62bd\u6410\u5230\u4e00\u8d77\u3002 \u3000\u3000\u4ed6\u4eec\u4e2d\u7684\u6bd2\u975e\u4f46\u65e0\u8272\u65e0\u5473\uff0c\u800c\u4e14\u4e2d\u6bd2\u7684\u4eba\u7adf\u4f1a\u65e0\u4e1d\u6beb\u611f\u89c9\uff0c\u7b49\u5230\u4ed6\u4eec\u53d1\u89c9\u65f6\uff0c\u4fbf\u7acb\u523b\u65e0 \u6551\u4e86\uff01 \u3000\u3000\u7530\u4e03\u5fcd\u4e0d\u4f4f\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u55c4\u58f0\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u6bd2\uff1f\u600e\u5730\u5982\u6b64\u5389\u5bb3\uff1f \u3000\u3000\u5fc3\u7709\u5927\u5e08\u867d\u7136\u4fee\u884c\u529f\u6df1\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u6025\u6012\u653b\u5fc3\uff0c\u4e00\u6b65\u7a9c\u4e86\u51fa\u53bb\uff0c\u63d0\u5c0f\u9e21\u822c\u63d0\u4e86\u4e2a\u5e97\u4f19\u8fdb \u6765\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u4eec\u5728\u83dc\u91cc\u4e0b\u4e86\u4ec0\u4e48\u6bd2\uff1f \u3000\u3000\u90a3\u5e97\u4f19\u77a7\u89c1\u5730\u4e0a\u7684\u56db\u4e2a\u6b7b\u4eba\uff0c\u65e9\u5df2\u5413\u5f97\u8fde\u9aa8\u5934\u90fd\u9165\u4e86\uff0c\u7259\u9f7f\u683c\u683c\u7684\u6253\u6218\uff0c\u54ea\u91cc\u8fd8\u8bf4\u5f97\u51fa \u8bdd\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u7b28\u86cb\uff0c\u82e5\u662f\u4ed6\u4e0b\u7684\u6bd2\uff0c\u4ed6\u65e9\u5c31\u8dd1\u4e86\uff0c\u8fd8\u5728\u8fd9\u91cc\u77a7\u4ec0\u4e48\u70ed\u95f9\uff1f \u3000\u3000\u7530\u4e03\u7a9c\u4e86\u51fa\u53bb\uff0c\u521a\u7a9c\u51fa\u95e8\u53c8\u63a0\u56de\u6765\u5c06\u674e\u5bfb\u6b22\u631f\u8d77\uff0c\u51b7\u51b7\u9053\uff1a\u5c31\u7b97\u6211\u4eec\u5168\u90fd\u88ab\u6bd2\u6b7b\uff0c\u4f60\u4e5f \u8dd1\u4e0d\u4e86\u7684\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u90fd\u4f1a\u8981\u4f60\u966a\u7740\u6211\uff0c\u6211\u6d3b\u4f60\u4e5f\u6d3b\uff0c\u6211\u6b7b\u4f60\u4e5f\u5f97\u6b7b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u60f3\u4e0d\u5230\u4f60\u5bf9\u6211\u5012\u771f\u662f\u6df1\u60c5\u6b3e\u6b3e\uff0c\u53ea\u53ef\u60dc\u4f60\u4e0d\u662f\u4e2a\u7edd\u8272\u7684\u7f8e\u4eba\uff0c\u6211\u5bf9 \u7537\u4eba\u53c8\u504f\u504f\u5168\u65e0\u5174\u8da3\u3002 \u3000\u3000\u5403\u996d\u7684\u65f6\u5019\u5df2\u8fc7\u4e86\uff0c\u53a8\u623f\u5df2\u7a7a\u95f2\u4e0b\u6765\uff0c\u5927\u5e08\u5085\u7092\u4e86\u4e24\u6837\u83dc\uff0c\u4e8c\u5e08\u5085\u5f04\u6765\u4e00\u58f6\u9152\uff0c\u4e24\u4eba\u6b63 \u8df7\u7740\u817f\u5728\u90a3\u91cc\u4eab\u53d7\u7740\u8fd9\u4e00\u5929\u4e2d\u6700\u6109\u5feb\u7684\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u6d3b\u7740\uff0c\u4e5f\u662f\u56e0\u4e3a\u6bcf\u5929\u8fd8\u6709\u8fd9\u6837\u4e00\u4e2a\u65f6 \u8fb0\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u867d\u662f\u6025\u6012\u4ea4\u96c6\uff0c\u4e00\u89c1\u5230\u4ed6\u4eec\u5374\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7684\u8138\u7adf\u4e5f\u5df2\u8d6b\u7136\u53d8\u6210\u6b7b\u7070\u8272\uff01 \u3000\u3000\u5927\u5e08\u5085\u5df2\u6709\u4e86\u4e24\u5206\u9152\u610f\uff0c\u7b11\u7740\u62db\u547c\u9053\uff1a\u5927\u5e08\u83ab\u975e\u4e5f\u60f3\u6765\u5077\u7740\u559d\u4e24\u76c5\uff1f\u6b22\u8fce\u2014\u2014\uff0d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4eba\u5df2\u4ef0\u5929\u8dcc\u5012\uff0c\u5012\u5728\u7089\u7076\u4e0a\uff0c\u7076\u4e0a\u7684\u94c1\u9505\u78b0\u5012\u4e86\u6cb9\u74f6\uff0c\u6cb9\u90fd\u6d41\u5728\u94c1\u9505\u91cc\uff0c\u95ea \u95ea\u7684\u53d1\u7740\u6cb9\u5149\u3002 \u3000\u3000\u53d1\u5149\u7684\u6cb9\u91cc\u7adf\u6709\u6761\u706b\u7ea2\u7684\u8708\u86a3\uff01 \u3000\u3000\u6bd2\uff0c\u539f\u6765\u4e0b\u5728\u6cb9\u91cc\u3002 \u3000\u3000\u6bd2\u603b\u7b97\u627e\u51fa\u6765\u4e86\uff0c\u4f46\u4e0b\u6bd2\u7684\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u6cb9\u9505\u91cc\u7684\u8708\u86a3\uff0c\u957f\u53f9\u9053\uff1a\u6211\u65e9\u5c31\u77e5\u9053\u4ed6\u4eca\u65e9\u603b\u4f1a\u6765\u7684\u3002 \u3000\u3000\u7530\u4e03\u5389\u58f0\u9053\uff1a\u8c01\uff1f\u4f60\u77e5\u9053\u4e0b\u6bd2\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e16\u4e0a\u7684\u6bd2\u5927\u81f4\u53ef\u5206\u4e24\u79cd\uff0c\u4e00\u79cd\u662f\u8349\u6728\u4e4b\u6bd2\uff0c\u4e00\u79cd\u662f\u86c7\u866b\u4e4b\u6bd2\uff0c\u80fd\u81ea\u8349\u6728\u4e2d\u63d0 \u70bc\u6bd2\u836f\u7684\u4eba\u8f83\u591a\uff0c\u80fd\u63d0\u53d6\u86c7\u866b\u4e4b\u6bd2\u7684\u4eba\u8f83\u5c11\uff0c\u80fd\u4ee5\u86c7\u866b\u6740\u4eba\u4e8e\u65e0\u5f62\u7684\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u4e0d\u8fc7 \u4ec5\u6709\u4e00\u4e24\u4eba\u800c\u5df2\u3002 \u3000\u3000\u7530\u4e03\u5931\u58f0\u9053\uff1a\u4f60\u2014\u2014\u4f60\u8bf4\u7684\u96be\u9053\u662f\u82d7\u7586\u6781\u4e50\u5cd2\u7684\u4e94\u6bd2\u7ae5\u5b50\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u6211\u4e5f\u5e0c\u671b\u6765\u7684\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ed6\u600e\u4f1a\u5230\u4e2d\u539f\u6765\u4e86\uff1f\u4ed6\u6765\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6765\u627e\u6211\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u674e\u5bfb\u6b22\u7edd\u4e0d\u4f1a\u6709\u8fd9\u79cd\u670b\u53cb\u7684\uff0c\u8bdd\u8bf4\u5230\u4e00\u534a\uff0c\u5c31\u6539\u53e3\u9053\uff1a\u770b\u6765\u4f60\u7684\u670b\u53cb\u5e76\u4e0d\u591a\uff0c \u4ec7\u4eba\u5374\u4e0d\u5c11\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u4ec7\u4eba\u5012\u65e0\u59a8\u591a\u591a\u4e3a\u5584\uff0c\u670b\u53cb\u53ea\u8981\u4e00\u4e24\u4e2a\u4fbf\u5df2\u8db3\u591f\uff0c\u56e0\u4e3a\u6709\u65f6\u670b\u53cb\u6bd4\u4ec7\u4eba \u8fd8\u8981\u53ef\u6015\u5f97\u591a\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u5ffd\u7136\u9053\uff1a\u83dc\u4e2d\u6709\u6bd2\uff0c\u4f60\u662f\u600e\u4e48\u770b\u51fa\u6765\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u5c31\u597d\u50cf\u6211\u62bc\u724c\u4e5d\u4e00\u6837\uff0c\u6211\u82e5\u89c9\u5f97\u54ea\u4e00\u95e8\u8981\u8d62\uff0c\u90a3\u95e8\u5c31\u6709\u8d62\u65e0\u8f93\uff0c\u522b\u4eba\u82e5\u95ee \u6211\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff0c\u6211\u4e5f\u56de\u7b54\u4e0d\u51fa\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u51dd\u89c6\u4e86\u4ed6\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u8fd9\u4e00\u8def\u4e0a\u4ed6\u5403\u4ec0\u4e48\uff0c\u6211\u4eec\u5c31\u5403\u4ec0\u4e48\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u5c06\u4ed6\u5e08\u4f84\u4eec\u7684\xd7\u8eab\u4ea4\u6258\u7ed9\u9644\u8fd1\u4e00\u4e2a\u5bfa\u9662\u540e\uff0c\u5c31\u5306\u5306\u4e0a\u9053\uff0c\u4e00\u8def\u4e0a\u8c01\u4e5f\u4e0d\u613f\u518d\u63d0 \u8d77\u5403\u559d\u4e24\u5b57\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u53ef\u4ee5\u4e0d\u5403\u4e0d\u559d\uff0c\u8d76\u8f66\u7684\u5374\u4e0d\u613f\u966a\u4ed6\u4eec\u6328\u997f\uff0c\u6b63\u7aff\u65f6\u5c31\u627e\u4e86\u4e2a\u5c0f\u5e97\uff0c\u81ea\u5df1\u4e00\u4e2a\u4eba\u53bb \u5403\u559d\u8d77\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u8d76\u8f66\u7684\u7528\u8863\u895f\xd7\u4e86\u51e0\u4e2a\u998d\u998d\uff0c\u4e00\u9762\u5543\uff0c\u4e00\u9762\u8d70\u4e86\u8fc7\u6765\uff0c\u4f3c\u4e4e\u5543\u5f97\u6d25\u6d25\u6709 \u5473\u3002 \u3000\u3000\u7530\u4e03\u76ef\u7740\u4ed6\u7684\u8138\uff0c\u5f88\u6ce8\u610f\u7684\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u8fd9\u998d\u998d\u51e0\u679a\u94b1\u4e00\u4e2a\uff1f \u3000\u3000\u8d76\u8f66\u7684\u7b11\u9053\uff1a\u4fbf\u5b9c\u5f97\u5f88\uff0c\u5473\u9053\u4e5f\u4e0d\u9519\uff0c\u5927\u7237\u8981\u4e0d\u8981\u5c1d\u5c1d\uff1f \u3000\u3000\u8d76\u8f66\u7684\u7acb\u523b\u5c31\u5c06\u998d\u998d\u5168\u90fd\u4ece\u8f66\u7a97\u91cc\u9012\u8fdb\u6765\uff0c\u53c8\u7b49\u4e86\u534a\u664c\uff0c\u8f66\u9a6c\u5df2\u542f\u884c\uff0c\u8d76\u8f66\u7684\u5e76\u6ca1\u6709\u4ec0 \u4e48\u75c7\u72b6\u3002 \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u8fd9\u998d\u998d\u603b\u4e0d\u4f1a\u6709\u6bd2\u5427\uff0c\u5927\u5e08\u8bf7\u7528\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u674e\u6a80\u8d8a\u8bf7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u9053\uff1a\u60f3\u4e0d\u5230\u4e24\u4f4d\u5c45\u7136\u4e5f\u5ba2\u6c14\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u5de6\u624b\u62ff\u4e86\u4e2a\u998d\u998d\uff0c\u56e0\u4e3a\u4ed6\u53ea\u6709\u5de6\u624b\u80fd\u52a8\uff0c\u53ea\u89c1\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u8fd9\u998d\u998d\u4e5f\u5403\u4e0d\u5f97\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4f46\u8d76\u8f66\u7684\u5403\u4e86\u5374\u6ca1\u6709\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u5403\u5f97\u6211\u4eec\u5374\u5403\u4e0d\u5f97\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u6781\u4e50\u7ae5\u5b50\u60f3\u6bd2\u6b7b\u7684\u5e76\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u7530\u4e03\u51b7\u7b11\u9053\uff1a\u4f60\u662f\u60f3\u5bb3\u6211\u4eec\u6328\u997f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e3a\u4f55\u4e0d\u8bd5\u8bd5\uff1f \u3000\u3000\u7530\u4e03\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u5ffd\u7136\u5429\u5490\u505c\u8f66\uff0c\u5c06\u8d76\u8f66\u7684\u53eb\u4e86\u4e0b\u6765\uff0c\u5206\u4e86\u534a\u4e2a\u998d\u998d\u7ed9\u4ed6\uff0c\u770b\u7740\u4ed6\u5403\u4e0b \u53bb\u3002 \u3000\u3000\u8d76\u8f66\u7684\u4e09\u53e3\u4e24\u53e3\u5c31\u5c06\u998d\u998d\u54bd\u4e0b\uff0c\u679c\u7136\u4e00\u70b9\u4e2d\u6bd2\u7684\u8ff9\u8c61\u4e5f\u6ca1\u6709\uff0c\u7530\u4e03\u51b7\u51b7\u9053\uff1a\u4f60\u8fd8\u6562\u8bf4\u8fd9 \u998d\u998d\u5403\u4e0d\u5f97\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd8\u662f\u5403\u4e0d\u5f97\u3002 \u3000\u3000\u4ed6\u61d2\u6d0b\u6d0b\u7684\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u7adf\u4f3c\u7761\u7740\u4e86\u3002 \u3000\u3000\u7530\u4e03\u6068\u6068\u9053\uff1a\u6211\u504f\u8981\u5403\u7ed9\u4f60\u770b\u3002 \u3000\u3000\u4ed6\u5634\u91cc\u867d\u8fd9\u4e48\u8bf4\uff0c\u5374\u6bd5\u7adf\u8fd8\u662f\u4e0d\u6562\u5192\u9669\uff0c\u53ea\u89c1\u4e00\u6761\u91ce\u72d7\u6b63\u5728\u7a97\u524d\u5939\u7740\u5c3e\u5df4\u4e71\u53eb\uff0c\u4f3c\u4e5f\u997f \u6781\u4e86\u3002 \u3000\u3000\u7530\u4e03\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u5c06\u534a\u4e2a\u998d\u998d\u629b\u7ed9\u72d7\u5403\uff0c\u8fd9\u6761\u72d7\u5374\u5bf9\u998d\u998d\u6ca1\u6709\u4ec0\u4e48\u5174\u8da3\uff0c\u53ea\u54ac\u4e86\u4e00\u53e3\uff0c \u5c31\u6ca1\u7cbe\u6253\u91c7\u7684\u8d70\u5f00\u3002 \u3000\u3000\u8c01\u77e5\u5b83\u8fd8\u6ca1\u6709\u8d70\u591a\u8fdc\uff0c\u5ffd\u7136\u72c2\u5420\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u5012\u5728\u5730\u4e0a\u4e00\u9635\u62bd\u6410\uff0c\u5c31\u52a8\u4e5f\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u7530\u4e03\u548c\u5fc3\u7709\u5927\u5e08\u8fd9\u624d\u771f\u7684\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u8bf4\u7684\u4e0d\u9519\u5427\uff0c\u53ea\u53ef\u60dc\u6bd2\u6b7b\u7684\u662f\u6761\u72d7\uff0c\u4e0d\u662f\u4f60\u3002 \u3000\u3000\u7530\u4e03\u4e00\u5411\u4ee5\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\u81ea\u50b2\uff0c\u6b64\u523b\u9762\u4e0a\u4e5f\u4e0d\u7981\u53d8\u4e86\u989c\u8272\uff0c\u6076\u72e0\u72e0\u7684\u77aa\u7740\u90a3\u8d76\u8f66\u7684\uff0c\u5389 \u58f0\u9053\uff1a\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u8d76\u8f66\u7684\u8eab\u5b50\u53d1\u6296\uff0c\u98a4\u58f0\u9053\uff1a\u5c0f\u4eba\u4e0d\u77e5\u9053\uff0c\u998d\u998d\u662f\u5c0f\u4eba\u65b9\u624d\u5728\u90a3\u9762\u5e97\u91cc\u4e70\u7684\u3002 \u3000\u3000\u7530\u4e03\u4e00\u628a\u63ea\u4f4f\u4ed6\uff0c\u72de\u7b11\u9053\uff1a\u72d7\u90fd\u88ab\u6bd2\u6b7b\u4e86\uff0c\u4e3a\u4f55\u672a\u6bd2\u6b7b\u4f60\uff1f\u82e5\u975e\u662f\u4f60\u4e0b\u7684\u6bd2\uff1f \u3000\u3000\u8d76\u8f66\u7684\u5413\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u903c\u4ed6\u4e5f\u6ca1\u6709\u7528\uff0c\u56e0\u4e3a\u4ed6\u7684\u786e\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ed6\u4e0d\u77e5\u9053\uff0c\u8c01\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u7530\u4e03\u6014\u4e86\u6014\uff0c\u9053\uff1a\u4f60\u77e5\u9053\uff1f\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u998d\u998d\u91cc\u6709\u6bd2\uff0c\u9762\u6c64\u91cc\u5374\u6709\u89e3\u836f\u3002 \u3000\u3000\u7530\u4e03\u6014\u4e86\u534a\u664c\uff0c\u6068\u6068\u9053\uff1a\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4eec\u5148\u524d\u4e3a\u4f55\u4e0d\u5403\u9762\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u82e5\u5403\u9762\uff0c\u6bd2\u5c31\u5728\u9762\u91cc\u4e86\u3002 \u3000\u3000\u6781\u4e50\u7ae5\u5b50\u4e0b\u6bd2\u7684\u672c\u4e8b\u7684\u786e\u9632\u4e0d\u80dc\u9632\uff0c\u9047\u7740\u8fd9\u79cd\u5bf9\u624b\uff0c\u9664\u4e86\u7d27\u7d27\u95ed\u7740\u5634\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u522b \u7684\u6cd5\u5b50\uff1f \u3000\u3000\u5fc3\u7709\u5927\u5e08\u6c89\u9053\uff1a\u597d\u5728\u53ea\u6709\u4e00\u4e24\u5929\u5c31\u5230\u4e86\uff0c\u6211\u4eec\u62fc\u7740\u4e24\u65e5\u4e0d\u5403\u4e0d\u559d\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u7530\u4e03\u53f9\u9053\uff1a\u7eb5\u7136\u4e0d\u5403\u4e0d\u559d\uff0c\u4e5f\u672a\u5fc5\u6709\u7528\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u54e6\uff1f \u3000\u3000\u7530\u4e03\u9053\uff1a\u4ed6\u4e5f\u8bb8\u5c31\u8981\u7b49\u5230\u6211\u4eec\u53c8\u997f\u5f97\u65e0\u529b\u65f6\u518d\u51fa\u624b\u3002 \u3000\u3000\u5fc3\u7709\u9ed8\u9ed8\u65e0\u8bed\u3002 \u3000\u3000\u7530\u4e03\u76ee\u5149\u95ea\u52a8\uff0c\u5ffd\u53c8\u9053\uff1a\u6211\u6709\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4ec0\u4e48\u4e3b\u610f\uff1f \u3000\u3000\u7530\u4e03\u4f4e\u58f0\u9053\uff1a\u4ed6\u8981\u6bd2\u6b7b\u7684\u4eba\u65e2\u975e\u5927\u5e08\uff0c\u4ea6\u975e\u5728\u4e0b\u2014\u2014 \u3000\u3000\u4ed6\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u8a00\uff0c\u4f4f\u53e3\u4e0d\u8bed\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u6c89\u4e0b\u4e86\u8138\u9053\uff1a\u8001\u50e7\u65e2\u5df2\u7b54\u5e94\u4e86\u5c06\u4eba\u5e26\u56de\u5c11\u6797\uff0c\u5c31\u4e07\u4e07\u4e0d\u80fd\u8ba9\u4ed6\u5728\u534a\u9014\u800c\u6b7b\uff01 \u3000\u3000\u7530\u4e03\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u4f46\u53ea\u8981\u4e00\u770b\u5230\u674e\u5bfb\u6b22\uff0c\u76ee\u4e2d\u5c31\u5145\u6ee1\u6740\u673a\u3002 \u3000\u3000\u548c\u5c1a\u4e0d\u4f46\u8981\u5403\u996d\u7761\u89c9\uff0c\u4e5f\u8981\u65b9\u4fbf\u7684\u3002 \u3000\u3000\u8c01\u77e5\u5fc3\u7709\u5927\u5e08\u4f3c\u4e5f\u7aa5\u7834\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u65e0\u8bba\u5e72\u4ec0\u4e48\uff0c\u65e0\u8bba\u5230\u54ea\u91cc\u53bb\uff0c\u90fd\u7edd\u4e0d\u8ba9\u674e\u5bfb\u6b22\u843d\u5728 \u81ea\u5df1\u89c6\u7ebf\u4e4b\u5916\u3002 \u3000\u3000\u7530\u4e03\u867d\u7136\u53c8\u6025\u53c8\u6068\uff0c\u5374\u4e5f\u65e0\u6cd5\u53ef\u65bd\u3002 \u3000\u3000\u53ea\u89c1\u8857\u89d2\u6709\u4e9b\u6cb9\u714e\u997c\u7684\u644a\u5b50\uff0c\u751f\u610f\u597d\u5f97\u5f88\uff0c\u5c45\u7136\u6709\u4e0d\u5c11\u4eba\u5728\u6392\u961f\u7b49\u7740\uff0c\u4e70\u5230\u624b\u7684\u7acb\u523b\u5c31 \u7528\u5927\u8471\u8638\u751c\u9762\u9171\u5c31\u7740\u70ed\u997c\u7ad9\u5728\u644a\u5b50\u65c1\u5403\uff0c\u6709\u7684\u5df2\u5403\u5b8c\u4e86\uff0c\u6b63\u5728\u7528\u8896\u5b50\u62b9\u5634\uff0c\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\u88ab \u6bd2\u6b7b\u3002 \u3000\u3000\u7530\u4e03\u5fcd\u4e0d\u4f4f\u9053\uff1a\u8fd9\u997c\u5403\u4e0d\u5f97\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u522b\u4eba\u90fd\u5403\u5f97\uff0c\u552f\u6709\u6211\u4eec\u5403\u4e0d\u5f97\uff0c\u5c31\u7b97\u4e00\u4e07\u4e2a\u4eba\u5403\u4e86\u8fd9\u6cb9\u714e\u997c\u90fd\u6ca1\u6709\u4e8b\uff0c\u4f46\u6211 \u4eec\u4e00\u5403\u5c31\u8981\u88ab\u6bd2\u6b7b\uff01 \u3000\u3000\u8fd9\u8bdd\u82e5\u5728\u524d\u4e24\u5929\u8bf4\uff0c\u7530\u4e03\u81ea\u7136\u7edd\u4e0d\u76f8\u4fe1\uff0c\u4f46\u6b64\u523b\u4ed6\u53ea\u8981\u4e00\u60f3\u5230\u90a3\u6781\u4e50\u7ae5\u5b50\u4e0b\u6bd2\u624b\u6bb5\u4e4b\u795e \u5947\u96be\u6d4b\uff0c\u5c31\u4e0d\u7981\u89c9\u5f97\u6bdb\u9aa8\u8038\u7136\uff0c\u5c31\u7b97\u5403\u4e86\u8fd9\u6cb9\u714e\u997c\u7acb\u523b\u5c31\u80fd\u6210\u4f5b\u767b\u4ed9\uff0c\u4ed6\u4e5f\u662f\u4e07\u4e07\u4e0d\u6562\u518d\u5c1d \u8bd5\u7684\u4e86\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4e2a\u5b69\u5b50\u54ed\u56b7\u7740\u9053\uff1a\u6211\u8981\u5403\u997c\u2014\u2014\u5a18\uff0c\u6211\u8981\u5403\u997c\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4e2a\u4e03\u516b\u5c81\u7684\u5c0f\u5b69\u5b50\u7ad9\u5728\u997c\u644a\u65c1\uff0c\u4e00\u9762\u8df3\uff0c\u4e00\u9762\u53eb\uff0c\u997c\u644a\u65c1\u7684\u6742\u8d27\u5e97\u91cc\u5c31\u6709\u4e2a\u6ee1\u8eab \u6cb9\u817b\u7684\u80a5\u80d6\u5987\u4eba\u8d70\u51fa\u6765\uff0c\u4e00\u4eba\u7ed9\u4e86\u4ed6\u4eec\u4e00\u8033\u5149\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u54ed\u7740\u9053\uff1a\u53d1\u4e86\u8d22\u6211\u5c31\u4e0d\u5403\u6cb9\u714e\u997c\u4e86\uff0c\u6211\u5c31\u8981\u5403\u86cb\u7092\u996d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u542c\u5f97\u6697\u6697\u53f9\u606f\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u8d2b\u5bcc\u4e0d\u5747\uff0c\u5b9e\u5728\u4ee4\u4eba\u53ef\u53f9\uff0c\u5728\u8fd9\u4e24\u4e2a\u5c0f\u5c0f\u5b69\u5b50\u7684\u5fc3\u76ee\u4e2d\uff0c\u8fde\u86cb\u7092\u996d\u90fd\u5feb\u6162\u4e86\u4e0d\u5f97 \u7684\u4eab\u53d7\u4e86\u3002 \u3000\u3000\u8857\u9053\u5f88\u7a84\uff0c\u518d\u52a0\u4e0a\u997c\u644a\u524d\u4eba\u53c8\u591a\u53c8\u6324\uff0c\u662f\u4ee5\u4ed6\u4eec\u7684\u8f66\u8d70\u4e86\u534a\u5929\u8fd8\u672a\u8d70\u8fc7\u53bb\uff0c\u8fd9\u65f6\u90a3\u4e24\u4e2a \u5b69\u5b50\u5df2\u6367\u7740\u4e2a\u7c97\u8336\u7897\u8d70\u4e86\u51fa\u6765\uff0c\u5750\u5728\u9053\u65c1\uff0c\u773c\u5df4\u5df4\u7684\u671b\u7740\u522b\u4eba\u624b\u91cc\u7684\u6cb9\u714e\u997c\uff0c\u8fd8\u5728\u6dcc\u773c\u6cea\u3002 \u3000\u3000\u7530\u4e03\u671b\u7740\u4ed6\u4eec\u7897\u91cc\u7684\u9762\u997c\u997c\uff0c\u5ffd\u7136\u8df3\u4e0b\u8f66\uff0c\u629b\u4e86\u952d\u94f6\u5b50\u5728\u997c\u644a\u4e0a\uff0c\u5c06\u521a\u51fa\u9505\u7684\u5341\u51e0\u4e2a\u6cb9 \u997c\u62ff\u4e86\u5c31\u8d70\u3002 \u3000\u3000\u7530\u4e03\u5c06\u4e00\u53e0\u6cb9\u714e\u997c\u90fd\u6367\u5230\u90a3\u4e24\u4e2a\u5b69\u5b50\u9762\u524d\uff0c\u7b11\u9053\uff1a\u5c0f\u5f1f\u5f1f\uff0c\u6211\u8bf7\u4f60\u5403\u997c\uff0c\u4f60\u8bf7\u6211\u5403\u997d\u997d \uff0c\u597d\u5417\uff1f \u3000\u3000\u90a3\u4e24\u4e2a\u5b69\u5b50\u77aa\u5927\u4e86\u773c\u775b\uff0c\u4f3c\u4e4e\u4e0d\u6562\u76f8\u4fe1\u4e16\u4e0a\u6709\u8fd9\u79cd\u597d\u4eba\u3002 \u3000\u3000\u7530\u4e03\u9053\uff1a\u6211\u518d\u7ed9\u4f60\u4eec\u4e00\u540a\u94b1\u4e70\u7cd6\u5403\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u76ee\u4e2d\u5df2\u4e0d\u89c9\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u770b\u5230\u7530\u4e03\u5df2\u6367\u7740\u4e24\u7897\u997d\u997d\u8d70\u4e0a\u8f66\u6765\uff0c\u5fc3\u7709\u5927\u5e08\u5fcd\u4e0d \u4f4f\u4e00\u7b11\u9053\uff1a\u6a80\u8d8a\u679c\u7136\u662f\u8db3\u667a\u591a\u8c0b\uff0c\u8001\u50e7\u4f69\u670d\u3002 \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u5728\u4e0b\u5012\u4e0d\u662f\u597d\u5403\uff0c\u4f46\u665a\u4e0a\u65e2\u7136\u8fd8\u8981\u8d76\u8def\uff0c\u5c31\u975e\u5f97\u5403\u9971\u4e86\u624d\u6709\u7cbe\u795e\uff0c\u5426\u5219\u534a\u8def \u82e5\u53c8\u6709\u53d8\uff0c\u4f53\u529b\u4e0d\uff0c\u600e\u95ef\u5f97\u8fc7\u53bb\uff1f \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u7530\u4e03\u5c06\u4e00\u7897\u997d\u9001\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u5927\u5e08\u8bf7\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u591a\u8c22\u3002 \u3000\u3000\u8fd9\u7897\u997d\u997d\u867d\u7136\u716e\u5f97\u5c11\u6cb9\u65e0\u76d0\uff0c\u53c8\u9ec4\u53c8\u9ed1\uff0c\u4f46\u5728\u4ed6\u4eec\u8bf4\u6765\uff0c\u5374\u5df2\u65e0\u5f02\u662f\u5c71\u73cd\u6d77\u5473\uff0c\u9f99\u809d\u51e4 \u9ad3\u3002 \u3000\u3000\u56e0\u4e3a\u8c01\u90fd\u53ef\u4ee5\u80af\u5b9a\u8fd9\u997d\u997d\u91cc\u5fc5\u5b9a\u662f\u6ca1\u6709\u6bd2\u7684\u3002 \u3000\u3000\u7530\u4e03\u773c\u89d2\u779f\u7740\u674e\u5bfb\u6b22\uff0c\u7b11\u9053\uff1a\u8fd9\u7897\u997d\u997d\u4f60\u8bf4\u5403\u4e0d\u5403\u5f97\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u672a\u8bf4\u8bdd\uff0c\u53c8\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u7530\u4e03\u5927\u7b11\u9053\uff1a\u6781\u4e50\u7ae5\u5b50\u82e5\u80fd\u5148\u7b97\u51c6\u90a3\u5b69\u5b50\u8981\u5403\u6cb9\u714e\u997c\uff0c\u53c8\u80fd\u7b97\u51c6\u6211\u4f1a\u7528\u6cb9\u997c\u6362\u4ed6\u7684\u9762\uff0c \u80fd\u5148\u5728\u91cc\u9762\u4e0b\u4e86\u6bd2\uff0c\u90a3\u4e48\u6211\u5c31\u7b97\u88ab\u6bd2\u6b7b\u4e5f\u5fc3\u7518\u60c5\u613f\u3002 \u3000\u3000\u4ed6\u5927\u7b11\u7740\u5c06\u4e00\u7897\u997d\u997d\u90fd\u5403\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u4e5f\u8ba4\u4e3a\u6781\u4e50\u7ae5\u5b50\u7eb5\u6709\u975e\u51e1\u7684\u624b\u6bb5\uff0c\u4f46\u6bd5\u7adf\u4e0d\u662f\u795e\u4ed9\uff0c\u81f3\u5c11\u603b\u4e0d\u80fd\u4e8b\u4e8b\u672a\u535c\u5148\u77e5 \uff01 \u7b2c\u5341\u4e5d\u7ae0\u3000\u767e\u53e3\u83ab\u8fa9>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u767e\u53e3\u83ab\u8fa9 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u5403\u7740\u7530\u4e03\u7531\u5c0f\u5b69\u624b\u4e0a\u6362\u6765\u7684\u90a3\u7897\u997d\u997d\uff0c\u4ed6\u4e5f\u5403\u5f97\u5f88\u653e\u5fc3\uff0c\u53ea\u4e0d\u8fc7\u51fa\u5bb6\u4eba\u4e00\u5411\u8bb2 \u7a76\u7ec6\u56bc\u6162\u54bd\uff0c\u7530\u4e03\u4e00\u7897\u5168\u90fd\u4e0b\u4e86\u809a\uff0c\u4ed6\u624d\u5403\u4e86\u4e24\u53e3\u3002 \u3000\u3000\u7530\u4e03\u7b11\u9053\uff1a\u7167\u8fd9\u6837\u8d70\u6cd5\uff0c\u5929\u4eae\u4ee5\u524d\uff0c\u5c31\u53ef\u4ee5\u8d76\u5230\u5d69\u5c71\u4e86\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9762\u4e0a\u4e5f\u9732\u51fa\u4e00\u4e1d\u5bbd\u6170\u4e4b\u8272\uff0c\u9053\uff1a\u8fd9\u4e24\u5929\u5c71\u4e0b\u5fc5\u6709\u4e00\u95e8\u5f1f\u5b50\u63a5\u5e94\uff0c\u53ea\u8981\u80fd\u2014\u2014\uff0d \u3000\u3000\u4ed6\u8bed\u58f0\u7a81\u7136\u505c\u987f\uff0c\u8eab\u5b50\u7adf\u98a4\u6296\u8d77\u6765\uff0c\u8fde\u624b\u91cc\u7aef\u7740\u7684\u4e00\u7897\u997d\u997d\u90fd\u62ff\u4e0d\u7a33\u4e86\uff0c\u9762\u6c64\u6cfc\u51fa\uff0c\u6cbe \u6c61\u4e86\u50e7\u8863\u3002 \u3000\u3000\u7530\u4e03\u53d8\u8272\u9053\uff1a\u5927\u5e08\u4f60\u2014\u2014\uff0d\u4f60\u83ab\u975e\u4e5f\u2014\u2014\uff0d \u3000\u3000\u7a81\u542c\u6ce2\u7684\u4e00\u58f0\uff0c\u9762\u7897\u5df2\u88ab\u5fc3\u7709\u5927\u5e08\u634f\u788e\u3002 \u3000\u3000\u7530\u4e03\u5927\u9a87\u9053\uff1a\u8fd9\u7897\u9762\u997d\u997d\u91cc\u96be\u9053\u4e5f\u6709\u6bd2\uff1f \u3000\u3000\u5fc3\u7709\u5927\u5e08\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u65e0\u8bed\u3002 \u3000\u3000\u7530\u4e03\u4e00\u628a\u63ea\u4f4f\u674e\u5bfb\u6b22\u7684\u8863\u895f\uff0c\u55c4\u58f0\u9053\uff1b\u4f60\u770b\u770b\u6211\u7684\u8138\uff0c\u6211\u7684\u8138\u662f\u4e0d\u662f\u4e5f\u2014\u2014\uff0d \u3000\u3000\u4ed6\u4e5f\u9aa4\u7136\u987f\u4f4f\u8bed\u58f0\uff0c\u56e0\u4e3a\u8fd9\u53e5\u8bdd\u5df2\u7528\u4e0d\u7740\u518d\u95ee\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u6211\u867d\u7136\u4e00\u5411\u90fd\u5f88\u8ba8\u538c\u4f60\uff0c\u5374\u4e5f\u4e0d\u613f\u770b\u7740\u4f60\u6b7b\u3002 \u3000\u3000\u7530\u4e03\u9762\u5982\u6b7b\u7070\uff0c\u5168\u8eab\u53d1\u6296\uff0c\u6068\u6068\u7684\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u773c\u73e0\u5b50\u90fd\u5feb\u51f8\u4e86\u51fa\u6765\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5ffd\u7136 \u72de\u7b11\u9053\uff1a\u4f60\u4e0d\u613f\u770b\u7740\u6211\u6b7b\uff0c\u6211\u5374\u8981\u770b\u7740\u4f60\u6b7b\uff01\u6211\u65e9\u5c31\u8be5\u6740\u4e86\u4f60\u7684\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1b\u4f60\u73b0\u5728\u6740\u6211\u4e0d\u5acc\u592a\u8fdf\u4e86\u4e48\uff1f \u3000\u3000\u7530\u4e03\u54ac\u7259\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u73b0\u5728\u8981\u6740\u4f60\u7684\u786e\u5df2\u8fdf\u4e86\uff0c\u4f46\u8fd8\u4e0d\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u627c\u4f4f\u4e86\u674e\u5bfb\u6b22\u7684\u8116\u5b50\u3002 \u3000\u3000\u963f\u98de\u5df2\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8138\u8272\u8fd8\u662f\u5f88\u96be\u770b\uff0c\u4f46\u8eab\u5b50\u5374\u5df2\u80fd\u7ad9\u5f97\u7b14\u76f4\u3002 \u3000\u3000\u963f\u98de\u5728\u5c4b\u5b50\u91cc\u7f13\u7f13\u8d70\u4e86\u4e24\u5708\uff0c\u5ffd\u7136\u9053\uff1a\u4f60\u770b\u4ed6\u80fd\u4e0d\u80fd\u5e73\u5b89\u5230\u8fbe\u5c11\u6797\u5bfa\uff1f \u3000\u3000\u6797\u4ed9\u513f\u561f\u7740\u5634\u9053\uff1a\u4f60\u5012\u771f\u662f\u4e09\u53e5\u4e0d\u79bb\u672c\u884c\uff0c\u8bf4\u6765\u8bf4\u53bb\u53ea\u77e5\u9053\u4ed6\uff0c\u4ed6\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bf4\u6211 \uff0c\u4e0d\u8bf4\u8bf4\u4f60\uff0c\u4f60\u81ea\u5df1\u3002 \u3000\u3000\u963f\u98de\u9759\u9759\u5730\u671b\u7740\u5979\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u770b\u4ed6\u80fd\u4e0d\u80fd\u5e73\u5b89\u5230\u8fbe\u5c11\u6797\u5bfa\uff1f \u3000\u3000\u65e0\u8bba\u6797\u4ed9\u513f\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u8fd8\u662f\u53ea\u6709\u8fd9\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54c4\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u4f60\u5440\uff01\u6211\u62ff\u4f60\u8fd9\u4eba\u771f\u662f\u6ca1\u6cd5\u5b50\u3002\u5979\u62c9\u7740\u963f\u98de\u5750\u4e0b\uff0c\u67d4\u58f0\u9053\uff1a\u4f46\u4f60 \u53ea\u7ba1\u653e\u5fc3\uff0c\u4ed6\u73b0\u5728\u8bf4\u4e0d\u5b9a\u5df2\u5750\u5728\u5fc3\u6e56\u5927\u5e08\u7684\u65b9\u592a\u5ba4\u559d\u8336\u4e86\uff0c\u5c11\u6797\u5bfa\u7684\u8336\u4e00\u5411\u5f88\u6709\u540d\u3002 \u3000\u3000\u963f\u98de\u795e\u8272\u7ec8\u4e8e\u7f13\u548c\u4e86\u4e9b\uff0c\u5c45\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u5c31\u7b97\u88ab\u4eba\u627c\u4f4f\uff0c\u4e5f\u7edd\u4e0d\u80af\u559d \u8336\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5df2\u5598\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u7530\u4e03\u81ea\u5df1\u7684\u9762\u8272\u4e5f\u8d8a\u6765\u8d8a\u53ef\u6015\uff0c\u51e0\u4e4e\u4e5f\u5df2\u5598\u4e0d\u8fc7\u6c14\u6765\u3002\u4f46\u4ed6\u4e00\u53cc\u9752\u7b4b\u66b4\u9732\u7684\u624b\u5374\u6b7b\u4e5f\u4e0d \u80af\u653e\u677e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u773c\u524d\u6e10\u6e10\u53d1\u9ed1\uff0c\u7530\u4e03\u7684\u4e00\u5f20\u8138\u4f3c\u5df2\u6e10\u6e10\u53d8\u5f97\u5f88\u9065\u8fdc\uff0c\u4ed6\u77e5\u9053\u6b7b\u5df2\u8ddd\u79bb\u4ed6\u6e10\u6e10 \u8fd1\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u751f\u6b7b\u9877\u4fc4\u4e4b\u95f4\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u4f1a\u60f3\u8d77\u5f88\u591a\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u542c\u8bf4\u4e00\u4e2a\u4eba\u4e34\u6b7b\u524d\u603b\u4f1a\u5ffd\u7136\u60f3\u8d77 \u5f88\u591a\u4e8b\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5374\u4ec0\u4e48\u4e5f\u6ca1\u6709\u60f3\u8d77\uff0c\u65e2\u4e0d\u89c9\u5f97\u60b2\xd7\uff0c\u4e5f\u4e0d\u89c9\u5f97\u6050\u60e7\uff0c\u53cd\u800c\u89c9\u5f97\u5f88\u597d\u7b11\uff0c\u51e0\u4e4e\u5fcd\u4e0d \u4f4f\u8981\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4ece\u6765\u4e5f\u672a\u60f3\u5230\u5c45\u7136\u4f1a\u548c\u7530\u4e03\u540c\u65f6\u54bd\u4e0b\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u7eb5\u7136\u5728\u9ec4\u6cc9\u8def\u4e0a\uff0c\u7530\u4e03\u4e5f\u4e0d\u662f \u4e2a\u597d\u65c5\u4f34\u3002 \u3000\u3000\u53ea\u542c\u7530\u4e03\u5636\u58f0\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u4f60\u597d\u957f\u7684\u6c14\uff0c\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u6b7b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u672c\u6765\u60f3\u8bf4\uff1a\u6211\u8fd8\u5728\u7b49\u7740\u4f60\u5148\u6b7b\u54e9\uff01 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u975e\u4f46\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u8fde\u6c14\u90fd\u900f\u4e0d\u51fa\u6765\u4e86\uff0c\u53ea\u89c9\u7530\u4e03\u7684\u8bed\u58f0\u4f3c\u4e5f\u53d8\u5f97\u5f88\u9065\u8fdc\uff0c\u5c31\u4eff \u4f5b\u662f\u81ea\u5730\u72f1\u8fb9\u7f18\u4f20\u6765\u7684\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4ed6\u9690\u9690\u7ea6\u7ea6\u542c\u5230\u4e00\u58f0\u60ca\u547c\uff0c\u547c\u58f0\u4f3c\u4e5f\u5f88\u9065\u8fdc\uff0c\u4f46\u542c\u6765\u53c8\u4eff\u4f5b\u662f\u7530\u4e03\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u5c31\u89c9\u5f97\u80f8\u53e3\u987f\u65f6\u5f00\u6717\uff0c\u773c\u524d\u6e10\u6e10\u660e\u4eae\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u770b\u5230\u4e86\u7530\u4e03\u3002 \u3000\u3000\u7530\u4e03\u5df2\u5012\u5728\u5bf9\u9762\u7684\u8f66\u5ea7\u4e0a\uff0c\u5934\u6b6a\u5230\u4e00\u8fb9\uff0c\u8f6f\u8f6f\u7684\u5782\u4e86\u4e0b\u6765\uff0c\u53ea\u6709\u4e00\u53cc\u6b7b\u9c7c\u822c\u7684\u773c\u775b\u4f3c\u4e4e \u4ecd\u5728\u72e0\u72e0\u7684\u77aa\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u518d\u770b\u5fc3\u7709\u5927\u5e08\u6b63\u5728\u5598\u606f\u7740\uff0c\u663e\u7136\u521a\u7528\u8fc7\u529b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\u9053\uff1a\u662f\u4f60\u6551\u4e86\u6211\uff1f \u3000\u3000\u5fc3\u7709\u5927\u5e08\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u62cd\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u55c4\u58f0\u9053\uff1a\u8d81\u4e94\u6bd2\u7ae5\u5b50\u8fd8\u6ca1\u6709\u6765\uff0c\u4f60\u5feb\u9003 \u547d\u53bb\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u975e\u4f46\u6ca1\u6709\u8d70\uff0c\u751a\u81f3\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u6c89\u6c89\u9053\uff1a\u4f60\u4e3a\u4f55\u8981\u6551\u6211\uff1f\u4f60\u5df2\u77e5\u9053\u6211\u4e0d\u662f\u6885\u82b1 \u76d7\uff1f \u3000\u3000\u5fc3\u7709\u9053\uff1a\u51fa\u5bb6\u4eba\u4e34\u6b7b\u524d\u4e0d\u613f\u591a\u9020\u51a4\u5b7d\uff0c\u65e0\u8bba\u4f60\u662f\u5426\u6885\u82b1\u76d7\uff0c\u90fd\u5feb\u8d70\u5427\uff0c\u7b49\u4e94\u6bd2\u7ae5\u5b50\u4e00\u6765 \uff0c\u4f60\u518d\u60f3\u9003\u5c31\u8fdf\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51dd\u89c6\u7740\u4ed6\u5df2\u53d1\u9ed1\u7684\u8138\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u53ea\u53ef\u60dc\u6211\u4ec0\u4e48\u90fd\u4f1a \uff0c\u5c31\u662f\u4e0d\u4f1a\u9003\u547d\u3002 \u3000\u3000\u5fc3\u7709\u7740\u6025\u9053\uff1a\u73b0\u5728\u4e0d\u662f\u4f60\u901e\u82f1\u96c4\u7684\u65f6\u5019\uff0c\u4f60\u4f53\u529b\u672a\u6062\u590d\uff0c\u4e5f\u4e07\u4e07\u4e0d\u662f\u4e94\u6bd2\u7ae5\u5b50\u7684\u5bf9\u624b\uff0c \u53ea\u8981\u4ed6\u4e00\u6765\uff0c\u4f60\u5c31\u2014\u2014\uff0d \u3000\u3000\u7a81\u542c\u62c9\u8f66\u7684\u9a6c\u4e00\u58f0\u60ca\u5636\uff0c\u8d76\u8f66\u7684\u4e00\u58f0\u60e8\u547c\uff0c\u8f66\u5b50\u659c\u659c\u51b2\u4e86\u51fa\u53bb\uff0c\u8f70\u7684\u63b8\u4e0a\u4e86\u9053\u65c1\u7684\u67af\u6811 \u3002 \u3000\u3000\u5fc3\u7709\u63b8\u5728\u8f66\u58c1\u4e0a\uff0c\u5636\u58f0\u9053\uff1a\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u53bb\uff1f\u96be\u9053\u60f3\u6551\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u4f60\u80fd\u6551\u6211\uff0c\u6211\u4e3a\u4f55\u4e0d\u80fd\u6551\u4f60\uff1f \u3000\u3000\u5fc3\u7709\u9053\uff1a\u53ef\u662f\u6211\u5df2\u79bb\u6b7b\u4e0d\u8fdc\uff0c\u8fdf\u65e9\u603b\u662f\u4e00\u6b7b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u73b0\u5728\u8fd8\u6ca1\u6709\u6b7b\uff0c\u662f\u4e48\uff1f \u3000\u3000\u4ed6\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5374\u81ea\u7530\u4e03\u6000\u4e2d\u641c\u51fa\u4e86\u4e00\u67c4\u5200\u3002 \u3000\u3000\u4e00\u67c4\u5f88\u8f7b\uff0c\u5f88\u8584\u7684\u5200\u3002 \u3000\u3000\u4e00\u67c4\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5634\u89d2\u4f3c\u4e4e\u9732\u51fa\u4e86\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u8f66\u53a2\u5df2\u503e\u5012\uff0c\u8f66\u8f6e\u4ecd\u5728\u4e0d\u505c\u7684\u6eda\u52a8\u7740\uff0c\u53d1\u51fa\u4e00\u9635\u9635\u5355\u8c03\u800c\u4e11\u6076\u7684\u58f0\u97f3\uff0c\u5728\u8fd9\u8352\u51c9\u7684\u9ed1\u591c \u91cc\u542c\u6765\u5206\u5916\u4ee4\u4eba\u4e0d\u6109\u5feb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5583\u5583\u9053\uff1a\u8fd9\u8f66\u8f74\u662f\u8be5\u52a0\u6cb9\u4e86\u2014\u2014 \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u5c45\u7136\u8fd8\u4f1a\u60f3\u8d77\u8f66\u8f74\u8be5\u4e0d\u8be5\u52a0\u6cb9\u7684\u603b\u662f\uff0c\u5fc3\u7709\u5927\u5e08\u8d8a\u6765\u8d8a\u89c9\u5f97\u8fd9\u4eba\u5947\u602a\u5f97\u4e0d \u53ef\u601d\u8bae\u3002 \u3000\u3000\u4ed6\u6d3b\u4e86\u516d\u5341\u591a\u5e74\uff0c\u4ece\u672a\u89c1\u8fc7\u7b2c\u4e8c\u4e2a\u8fd9\u6837\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\u674e\u5bfb\u6b22\u5df2\u6276\u7740\u4ed6\u51fa\u4e86\u8f66\u53a2\uff0c\u523a\u9aa8\u7684\u5bd2\u98ce\u731b\u7136\u5439\u4e0a\u4e86\u4ed6\u4eec\u7684\u8138\uff0c\u90a3\u611f\u89c9\u5c31\u597d\u50cf\u5200\u5272\u4e00 \u6837\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4f60\u672c\u4e0d\u5fc5\u8fd9\u6837\u505a\u7684\uff0c\u4f60\u2014\u2014\u4f60\u8fd8\u662f\u5feb\u8d70\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u501a\u7740\u8f66\u53a2\u5750\u4e86\u4e0b\u6765\uff0c\u5929\u4e0a\u65e0\u661f\u65e0\u6708\uff0c\u5927\u5730\u4e00\u7247\u6c89\u5bc2\uff0c\u5bd2\u98ce\u5439\u7740\u67af\u6811\uff0c\u5b9b\u5982\u9b3c\u9b45 \u5728\u8fce\u98ce\u8d77\u821e\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u7528\u5c3d\u76ee\u529b\uff0c\u4e5f\u77a7\u4e0d\u89c1\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u53ea\u542c\u674e\u5bfb\u6b22\u6717\u58f0\u9053\uff1a\u6781\u4e50\u5cd2\u4e3b\uff0c\u4f60\u6765\u4e86\u4e48\uff1f \u3000\u3000\u5bd2\u98ce\u547c\u5578\uff0c\u5374\u542c\u4e0d\u89c1\u4eba\u58f0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1b\u4f60\u65e2\u4e0d\u6765\uff0c\u6211\u5c31\u8981\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5c06\u5fc3\u7709\u534a\u62d6\u534a\u62b1\u7684\u62c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u4f60\u60f3\u5230\u54ea\u91cc\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u81ea\u7136\u662f\u5c11\u6797\u5bfa\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u5931\u58f0\u9053\uff1a\u5c11\u6797\u5bfa\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4eec\u8fd9\u4e00\u79cd\u62fc\u547d\u7684\u8d76\uff0c\u5c82\u975e\u5c31\u662f\u4e3a\u4e86\u8981\u8d76\u5230\u5c11\u6797\u5bfa\u4e48\uff1f \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4f46\u2014\u2014\u4f46\u73b0\u5728\u4f60\u5df2\u4e0d\u5fc5\u53bb\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u73b0\u5728\u6211\u66f4\u975e\u53bb\u4e0d\u53ef\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u53ea\u6709\u5c11\u6797\u5bfa\u4e2d\u6216\u8bb8\u8fd8\u6709\u6551\u4f60\u7684\u89e3\u836f\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4f60\u4e3a\u4f55\u8981\u6551\u6211\uff1f\u6211\u672c\u662f\u4f60\u7684\u654c\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u6551\u4f60\uff0c\u5c31\u56e0\u4e3a\u4f60\u6bd5\u7adf\u8fd8\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9ed8\u7136\u534a\u664c\uff0c\u957f\u53f9\u9053\uff1a\u82e5\u662f\u771f\u7684\u80fd\u8d76\u5230\u5c11\u6797\uff0c\u6211\u4e00\u5b9a\u4f1a\u8bbe\u6cd5\u8bc1\u660e\u4f60\u7684\u65e0\xd7\uff0c\u73b0\u5728 \u6211\u5df2\u53ef\u65ad\u5b9a\u4f60\u7edd\u975e\u6885\u82b1\u76d7\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u7b11\u4e86\u7b11\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u8bf4\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u53ea\u53ef\u60dc\u4f60\u82e5\u5e26\u7740\u6211\uff0c\u5c31\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u8d76\u5230\u5c11\u6797\u5bfa\u7684\uff0c\u4e94\u6bd2\u7ae5\u5b50\u73b0\u5728\u867d\u7136\u8fd8\u672a\u73b0\u8eab \uff0c\u4f46\u4ed6\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u7684\u54b3\u55fd\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4ee5\u4f60\u7684\u8f7b\u529f\uff0c\u4e00\u4e2a\u4eba\u8d70\u4e5f\u8bb8\u8fd8\u6709\u5e0c\u671b\uff0c\u53c8\u4fdd\u5fc5\u8981\u6211\u6765\u62d6\u7d2f\u4f60\uff1f\u53ea\u8981\u4f60\u6709\u6b64\u5fc3\u610f \uff0c\u8001\u50e7\u5df2\u662f\u6b7b\u800c\u65e0\u61be\u7684\u4e86\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u5403\u5403\u7b11\u9053\uff1a\u9053\u8c8c\u5cb8\u7136\u7684\u5c11\u6797\u548c\u5c1a\uff0c\u5c45\u7136\u4f1a\u548c\u72c2\u5ad6\u4e71\u996e\u7684\u98ce\u6d41\u63a2\u82b1\u4ea4\u4e0a\u670b\u53cb\u4e86\uff0c \u8fd9\u5012\u771f\u662f\u5929\u4e0b\u5947\u95fb\u3002 \u3000\u3000\u7b11\u58f0\u5ffd\u8fdc\u5ffd\u8fd1\uff0c\u4e5f\u4e0d\u77e5\u7a76\u7adf\u662f\u5f80\u54ea\u91cc\u4f20\u6765\u7684\u3002 \u3000\u3000\u5fc3\u7709\u9aa4\u7136\u50f5\u786c\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u6781\u4e50\u5cd2\u4e3b\uff1f \u3000\u3000\u90a3\u58f0\u97f3\u54af\u54af\u7b11\u9053\uff1a\u6211\u716e\u7684\u997d\u997d\u5473\u9053\u8fd8\u4e0d\u9519\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u9601\u4e0b\u65e2\u7136\u60f3\u8981\u6211\u8fd9\u98ce\u6d41\u63a2\u82b1\u7684\u547d\uff0c\u4e3a\u4f55\u53c8\u4e0d\u6562\u73b0\u8eab\u5462\uff1f \u3000\u3000\u6781\u4e50\u5cd2\u4e3b\u9053\uff1a\u6211\u7528\u4e0d\u7740\u73b0\u8eab\uff0c\u4e5f\u53ef\u8981\u4f60\u7684\u547d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6781\u4e50\u5cd2\u4e3b\u7b11\u9053\uff1a\u5230\u4eca\u591c\u4e3a\u6b62\uff0c\u6b7b\u5728\u6211\u624b\u4e0a\u7684\u4eba\u5df2\u6709\u4e09\u767e\u4e5d\u5341\u4e8c\u4e2a\uff0c\u975e\u4f46\u4ece\u4e25\u6ca1\u6709\u4e00\u4e2a\u89c1 \u5230\u8fc7\u6211\uff0c\u6839\u672c\u8fde\u6211\u7684\u5f71\u5b50\u90fd\u770b\u4e0d\u5230\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u6211\u4e5f\u65e9\u5df2\u542c\u8bf4\u9601\u4e0b\u662f\u4e2a\u4f8f\u5112\uff0c\u4e11\u5f97\u4e0d\u6562\u89c1\u4eba\uff0c\u60f3\u4e0d\u5230\u6c5f\u6e56\u4f20\u8bf4\u7adf\u662f\u771f\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u542c\u5230\u6781\u4e50\u5cd2\u4e3b\u7684\u58f0\u97f3\u9053\uff1a\u6211\u82e5\u8ba9\u4f60\u5728\u5929\u4eae\u4e4b\u524d\u5c31\u6b7b\u4e86\uff0c\u7b97\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u6211\u5728\u5929\u4eae\u524d\u81ea\u7136\u4e0d\u4f1a\u6b7b\u7684\uff0c\u9601\u4e0b\u5374\u96be\u8bf4\u5f97\u5f88\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u58f0\u8fd8\u672a\u505c\u987f\uff0c\u7a81\u542c\u4e00\u9635\u5947\u5f02\u7684\u5439\u7af9\u58f0\u54cd\u8d77\u3002 \u3000\u3000\u96ea\u4e0a\u5ffd\u7136\u51fa\u73b0\u4e86\u65e0\u6570\u6761\u8815\u8815\u800c\u52a8\u7684\u9ed1\u5f71\uff0c\u6709\u5927\u6709\u5c0f\uff0c\u6709\u957f\u6709\u77ed\uff0c\u9ed1\u6697\u4e2d\u4e5f\u770b\u4e0d\u51fa\u7a76\u7adf\u662f \u4e9b\u4ec0\u4e48\uff0c\u53ea\u80fd\u55c5\u5230\u4e00\u9635\u9635\u6251\u9f3b\u7684\u8165\u6c14\u3002 \u3000\u3000\u5fc3\u7709\u9053\uff1a\u4e94\u6bd2\u4e00\u51fa\uff0c\u4eba\u5316\u67af\u9aa8\uff0c\u4f60\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u7b49\u4f55\u65f6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u50cf\u662f\u6839\u672c\u6ca1\u542c\u5230\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u6717\u58f0\u9053\uff1a\u636e\u8bf4\u6781\u4e50\u5cd2\u4e2d\u7684\u6bd2\u7269\u6210\u5343\u4e0a\u4e07\uff0c\u6211\u600e\u5730\u53ea\u4e0d \u8fc7\u770b\u5230\u8fd9\u51e0\u6761\u5c0f\u6bdb\u866b\u800c\u5df2\uff0c\u96be\u9053\u5176\u5b83\u7684\u5df2\u5168\u90fd\u6b7b\u5149\u4e86\u4e48\uff1f \u3000\u3000\u5439\u7af9\u4e4b\u58f0\u66f4\u6025\uff0c\u96ea\u4e0a\u7684\u9ed1\u6697\u5df2\u5c06\u674e\u5bfb\u6b22\u548c\u5fc3\u7709\u56f4\u4f4f\uff0c\u6709\u51e0\u6761\u5df2\u6e10\u6e10\u722c\u5230\u4ed6\u4eec\u7684\u811a\u65c1\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u624d\u542c\u5f97\u6781\u4e50\u5cd2\u4e3b\u54af\u54af\u7b11\u9053\uff1b\u6211\u8fd9\u6781\u4e50\u866b\u4e43\u4e03\u79cd\u795e\u7269\u4ea4\u914d\u800c\u6210\uff0c\u975e\u8840\u8089\u4e0d\u9986\uff0c\u7b49\u5230\u4e24 \u4f4d\u8fde\u76ae\u5e26\u9aa8\u90fd\u5df2\u8fdb\u4e86\u4ed6\u4eec\u7684\u809a\u5b50\uff0c\u4f60\u5c31\u4e0d\u4f1a\u5acc\u4ed6\u5c0f\u4e86\u3002 \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u89c1\u5200\u5149\u4e00\u95ea\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\u5df2\u53d1\u51fa\uff01 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5931\u58f0\u9ad8\u547c\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u674e\u5bfb\u6b22\u624b\u91cc\u7684\u98de\u5200\u4e43\u662f\u4ed6\u4eec\u552f\u4e00\u7684\u5e0c\u671b\uff0c\u73b0\u5728\u674e\u5bfb\u6b22\u8fde\u5bf9\u65b9\u7684\u5f71\u5b50\u90fd\u672a\u770b\u5230\uff0c \u98de\u5200\u4fbf\u5df2\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u4e0d\u4e2d\uff0c\u4ed6\u4eec\u4fbf\u8981\u5316\u4e3a\u67af\u9aa8\u3002 \u3000\u3000\u8fd9\u662f\u674e\u5bfb\u6b22\u7684\u5b64\u6ce8\u4e00\u63b7\uff0c\u5374\u62ff\u4ed6\u81ea\u5df1\u7684\u751f\u547d\u4f5c\u8d4c\u6ce8\u3002 \u3000\u3000\u8fd9\u4e00\u6ce8\u8d62\u7684\u673a\u4f1a\u5b9e\u5728\u4e0d\u5927\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u518d\u4e5f\u60f3\u4e0d\u5230\u674e\u5bfb\u6b22\u7adf\u4f1a\u5982\u6b64\u5192\u5931\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u5200\u5149\u4e00\u95ea\u800c\u6ca1\uff0c\u6ca1\u5165\u9ed1\u6697\u4e2d\uff0c\u9ed1\u6697\u4e2d\u5374\u54cd\u8d77\u4e86\u4e00\u9635\u77ed\u4fc3\u4f46\u5374\u523a\u8033\u7684\u60e8\u547c\uff01 \u3000\u3000\u63a5\u7740\uff0c\u4e00\u4e2a\u4eba\u81ea\u9ed1\u6697\u4e2d\u52a8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u77ee\u5c0f\u5982\u5e7c\u7ae5\uff0c\u8eab\u4e0a\u7a7f\u7740\u6761\u77ed\u88d9\uff0c\u9732\u51fa\u4e00\u53cc\u5c0f\u817f\uff0c\u867d\u5728\u5982\u6b64\u98ce\u4e91\u4e25\u5bd2\u4e2d\uff0c\u4e5f\u4e00\u70b9\u4e0d \u89c9\u5f97\u51b7\u3002 \u3000\u3000\u4ed6\u7684\u5934\u4e5f\u5f88\u5c0f\uff0c\u773c\u775b\u5374\u4eae\u5982\u660e\u706f\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u53cc\u773c\u775b\u4eff\u4f5b\u5145\u6ee1\u4e86\u60ca\u60e7\u548c\u6028\u6bd2\uff0c\u72e0\u72e0\u7684\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5589\u5499\u91cc\u53ea \u662f\u683c\u683c\u7684\u53d1\u54cd\uff0c\u4e00\u4e2a\u5b57\u4e5f\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u8d6b\u7136\u53d1\u73b0\u5c0f\u674e\u98de\u5200\u6b63\u523a\u5728\u4ed6\u7684\u54bd\u5589\u4e0a\uff0c\u4e0d\u504f\u4e0d\u501a\u6b63\u63d2\u5728\u4ed6\u7684\u54bd\u5589\u4e0a\uff3f\uff3f\u5c0f\u674e\u98de \u5200\uff0c\u679c\u7136\u662f\u4ece\u4e0d\u865a\u53d1\uff01 \u3000\u3000\u6781\u4e50\u5cd2\u4e3b\u53ea\u89c9\u4e00\u53e3\u6c14\xd7\u5728\u5589\u5499\u91cc\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4f4f\uff0c\u53cd\u624b\u62d4\u51fa\u4e86\u98de\u5200\uff0c\u4e00\u62d4\u51fa\u98de\u5200\uff0c\u8fd9\u53e3\u6c14 \u5c31\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9c9c\u8840\u4e5f\u968f\u4e4b\u98de\u6e85\u800c\u51fa \u3000\u3000\u6781\u4e50\u5cd2\u4e3b\u72c2\u543c\u9053\uff1a\u597d\u6bd2\u7684\u5200\u3002 \u3000\u3000\u8fd9\u65f6\u96ea\u5730\u4e0a\u7684\u6bd2\u866b\uff0c\u5df2\u6709\u722c\u4e0a\u4e86\u674e\u5bfb\u6b22\u7684\u817f\u3002\u4f46\u674e\u5bfb\u6b22\u5374\u8fde\u52a8\u90fd\u4e0d\u52a8\uff0c\u5fc3\u7709\u5927\u5e08\u4e5f\u4e0d\u6562 \u52a8\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u8eab\u5b50\u53d1\u8f6f\uff0c\u51e0\u4e4e\u5df2\u7ad9\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u5c0f\u674e\u98de\u5200\u867d\u9738\u7edd\u5929\u4e0b\uff0c\u4f46\u4ed6\u4eec\u8fd8\u662f\u514d\u4e0d\u4e86\u8981\u5582\u9971\u6bd2\u866b\u3002 \u3000\u3000\u8c01\u77e5\u6781\u4e50\u5cd2\u4e3b\u4e00\u58f0\u72c2\u543c\uff0c\u9c9c\u8840\u6e85\u51fa\uff0c\u6570\u5341\u767e\u6761\u6bd2\u86c7\u7a81\u7136\u7bad\u4e00\u822c\u7a9c\u4e86\u56de\u53bb\uff0c\u4e00\u548c\u6761\u6761\u5168\u90fd \u9489\u5728\u6781\u4e50\u5cd2\u4e3b\u7684\u54bd\u5589\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u6c99\u6c99\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u6781\u4e50\u7ae5\u5b50\u5df2\u5316\u4e3a\u4e00\u5806\u67af\u9aa8\uff0c\u4f46\u6bd2\u866b\u9971\u98df\u4e86\u4ed6\u7684\u8840\u8089\u4e4b\u540e\uff01\u4e5f\u8f6f \u762b\u5728\u5730\uff0c\u4e0d\u80fd\u52a8\u4e86\u3002 \u3000\u3000\u4ed6\u4ee5\u6bd2\u6210\u540d\uff0c\u7ec8\u4e8e\u4e5f\u4ee5\u8eab\u6b89\u6bd2\uff01 \u3000\u3000\u8fd9\u666f\u50cf\u5b9e\u5728\u4ee4\u4eba\u60e8\u4e0d\u5fcd\u7779\u3002 \u3000\u3000\u5fc3\u7709\u8fd9\u624d\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5f20\u5f00\u773c\u6765\uff0c\u671b\u7740\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u6a80\u8d8a\u4e0d\u4f46\u98de\u5200\u5929\u4e0b\u65e0\u53cc\uff0c\u5b9a\u529b \u4e5f\u5f53\u771f\u662f\u5929\u4e0b\u65e0\u53cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4e0d\u6562\u5f53\uff0c\u6211\u53ea\u4e0d\u8fc7\u65e9\u5df2\u7b97\u51c6\u8fd9\u4e9b\u5403\u4eba\u7684\u6bd2\u866b\u4e00\u55c5\u5230\u8840\u8165\u6c14\u5c31\u4f1a\u8d70\u7684 \uff0c\u5176\u5b9e\u6211\u5fc3\u91cc\u4e5f\u5bb3\u6015\u5f97\u5f88\u3002 \u3000\u3000\u5fc3\u7709\u5927\u5e08\u9053\uff1a\u6a80\u8d8a\u4f60\u4e5f\u4f1a\u5bb3\u6015\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u9664\u4e86\u6b7b\u4eba\u5916\uff0c\u4e16\u4e0a\u54ea\u6709\u4e0d\u4f1a\u5bb3\u6015\u7684\u4eba\uff1f \u3000\u3000\u5fc3\u7709\u957f\u53f9\u9053\uff1a\u4e34\u5371\u800c\u4e0d\u4e71\uff0c\u867d\u60e7\u800c\u4e0d\u9981\uff0c\u6a80\u8d8a\u4e4b\u5b9a\u529b\uff0c\u8001\u50e7\u5f53\u771f\u662f\u5fc3\u670d\u53e3\u670d\uff0c\u4e94\u4f53\u6295\u5730 \u4e86\u3002 \u3000\u3000\u4ed6\u8bed\u58f0\u6e10\u6e10\u5fae\u5f31\uff0c\u7ec8\u4e8e\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5750\u5728\u9519\u8ff7\u4e0d\u9192\u7684\u5fc3\u7709\u5927\u5e08\u8eab\u65c1\uff0c\u4f3c\u5df2\u7761\u7740\u3002 \u3000\u3000\u4ed6\u5c06\u6781\u4e50\u7ae5\u5b50\u548c\u90a3\u4e9b\u6781\u4e50\u866b\u90fd\u57cb\u4e86\u8d77\u6765\uff0c\u8d70\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u624d\u5728\u5c0f\u9547\u4e0a\u96c7\u4e86\u8f86\u9aa1\u8f66\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u9aa1\u8f66\u7a81\u7136\u505c\u4e0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51e0\u4e4e\u7acb\u523b\u5c31\u5f20\u5f00\u773c\u6765\uff0c\u6380\u8d77\u8f66\u84ec\u540e\u7684\u5927\u68c9\u5e03\u5e18\u5b50\uff0c\u5bd2\u98ce\u6251\u9762\uff0c\u4ed6\u987f\u89c9\u7cbe\u795e\u4e00\u723d\u3002 \u3000\u3000\u53ea\u542c\u8f66\u592b\u9053\uff1a\u5d69\u5c71\u5df2\u5230\u4e86\uff0c\u9aa1\u8f66\u4e0a\u4e0d\u4e86\u5c71\uff0c\u5927\u7237\u4f60\u53ea\u597d\u81ea\u5df1\u8d70\u5427\u3002 \u3000\u3000\u8fd9\u8d76\u8f66\u7684\u88ab\u674e\u5bfb\u6b22\u4ece\u70ed\u88ab\u7a9d\u91cc\u62c9\u8d77\u6765\uff0c\u53c8\u88ab\u8001\u5a46\u903c\u7740\u63a5\u8d9f\u751f\u610f\uff0c\u6b63\u662f\u6ee1\u809a\u5b50\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u811a\u529b\u94b1\u4e5f\u90fd\u88ab\u8001\u5a46\u5148\u4e0b\u624b\u4e3a\u5f3a\u4e86\uff0c\u82e5\u4e0d\u662f\u8f66\u4e0a\u6709\u4e2a\u548c\u5c1a\uff0c\u4ed6\u53ea\u6015\u534a\u8def\u5c31\u505c\u4e86\u8f66\u3002 \u3000\u3000\u5d69\u5c71\u9644\u8fd1\u6570\u5341\u91cc\uff0c\u5bf9\u51fa\u5bb6\u4eba\u90fd\u5c0a\u656c\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62b1\u7740\u5fc3\u7709\u5927\u5e08\u4e0b\u4e86\u8f66\uff0c\u5ffd\u7136\u585e\u4e86\u952d\u94f6\u5b50\u5728\u8d76\u8f66\u7684\u624b\u91cc\uff0c\u7b11\u9053\uff1a\u8fd9\u662f\u7ed9\u4f60\u7559\u505a\u79c1\u623f \u94b1\u6253\u9152\u559d\u7684\uff0c\u6211\u77e5\u9053\u5a36\u4e86\u8001\u5a46\u7684\u7537\u4eba\u82e5\u6ca1\u6709\u51e0\u4e2a\u79c1\u623f\u94b1\uff0c\u90a3\u65e5\u5b50\u771f\u662f\u96be\u8fc7\u5f97\u5f88\u3002 \u3000\u3000\u8d76\u8f66\u7684\u559c\u51fa\u671b\u5916\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u9053\u8c22\uff0c\u674e\u5bfb\u6b22\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c55\u5f00\u8eab\u6cd5\uff0c\u89c5\u8def\u767b\u5c71\u3002 \u3000\u3000\u5c71\u9e93\u4e0b\u6709\u4e2a\u5c0f\u5c0f\u7684\u5e99\u5b87\uff0c\u51e0\u4e2a\u7070\u888d\u767d\u895f\u7684\u5c11\u6797\u50e7\u4eba\u6b63\u5728\u524d\u6bbf\u4e2d\u70e4\u706b\u53d6\u6696\uff0c\u8fd8\u6709\u4e24\u4eba\u8eb2\u5728 \u95e8\u540e\u7684\u907f\u98ce\u5904\u4e86\u671b\u3002 \u3000\u3000\u77a7\u89c1\u6709\u4eba\u4ee5\u8f7b\u529f\u767b\u5c71\uff0c\u8fd9\u4e24\u4eba\u7acb\u523b\u8fce\u4e86\u51fa\u6765\uff01 \u3000\u3000\u4e00\u4eba\u9053\uff1a\u6a80\u8d8a\u662f\u54ea\u91cc\u6765\u7684\uff1f\u662f\u4e0d\u662f\u2014\u2014 \u3000\u3000\u53e6\u4e00\u4eba\u89c1\u5230\u674e\u5bfb\u6b22\u8eab\u540e\u80cc\u7740\u7684\u662f\u4e2a\u548c\u5c1a\uff0c\u7acb\u523b\u62a2\u7740\u9053\uff1a\u6a80\u8d8a\u80cc\u7684\u662f\u5426\u5c11\u6797\u5f1f\u5b50\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u811a\u6b65\u653e\u7f13\uff0c\u5230\u4e86\u8fd9\u4e24\u4eba\u9762\u524d\uff0c\u7a81\u7136\u4e00\u63a0\u4e09\u4e08\uff0c\u4ece\u4ed6\u4eec\u5934\u9876\u4e0a\u98de\u63a0\u4e86\u8fc7\u53bb\uff0c\u811a\u5c16\u6cbe \u5730\uff0c\u518d\u6b21\u63a0\u8d77\u3002 \u3000\u3000\u5728\u8fd9\u79ef\u96ea\u4e0a\uff0c\u4ed6\u7adf\u8fd8\u80fd\u65bd\u5c55\u6674\u8713\u4e09\u6284\u6c34\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u5c11\u6797\u50e7\u4eba\u7eb5\u7136\u773c\u9ad8\u4e8e\u9876\uff0c\u4e5f\u4e0d\u7981\u4e3a \u4e4b\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u7b49\u5e99\u91cc\u7684\u50e7\u4eba\u8ffd\u51fa\u6765\u65f6\uff0c\u674e\u5bfb\u6b22\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u5373\u662f\u5982\u6b64\uff0c\u4f46\u8d70\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\u624d\u80fd\u770b\u5230\u5c11\u6797\u5bfa\u6062\u5b8f\u7684\u6bbf\u5b87\u3002 \u3000\u3000\u81ea\u83e9\u63d0\u8fbe\u6469\u6881\u6b66\u5e1d\u65f6\u4e1c\u6e21\u4e2d\u58eb\uff0c\u4e8c\u5341\u516b\u4f20\u81f3\u795e\u50e7\u8fe6\u53f6\uff0c\u5c11\u6797\u4ee3\u51fa\u624d\u4eba\uff0c\u4e45\u5df2\u4e3a\u4e2d\u539f\u6b66\u6797 \u4e4b\u5b97\u4e3b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u81ea\u5c71\u540e\u5165\u5bfa\uff0c\u53ea\u89c1\u96ea\u5730\u4e0a\u65e0\u6570\u6797\u7acb\u7740\u5927\u5927\u5c0f\u5c0f\u7684\u820d\u5f97\u5854\uff0c\u4ed6\u77e5\u9053\u8fd9\u6b63\u662f\u5c11\u6797\u5bfa\u7684 \u5723\u5730\u201c\u5854\u6797\u201d\uff0c\u4e5f\u5c31\u662f\u5c11\u6797\u5386\u4ee3\u7956\u5e08\u7684\u57cb\u9aa8\u5904\uff0c\u8fd9\u4e9b\u5927\u5e08\u4eec\u751f\u524d\u540d\u4f20\u516b\u8868\uff0c\u6b7b\u540e\u53c8\u4f55\u66fe\u591a\u5360 \u4e86\u4e00\u5c3a\u5730\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u5230\u4e86\u8fd9\u91cc\uff0c\u90fd\u4e0d\u7981\u4f1a\u6cb9\u7136\u751f\u51fa\u4e00\u79cd\u6452\u7edd\u7ea2\u5c18\uff0c\u7f6e\u8eab\u65b9\u5916\u4e4b\u610f\uff0c\u66f4\u4f55\u51b5\u4e45\u5df2\u538c\u5026\u540d \u5229\u7684\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u53c8\u54b3\u55fd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u6c89\u58f0\u9053\uff1a\u6a80\u95ef\u5c11\u6797\u7981\u5730\uff0c\u6a80\u8d8a\u4e5f\u672a\u514d\u592a\u76ee\u4e2d\u65e0\u4eba\u4e86\u5427\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6717\u58f0\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u8d1f\u4f24\uff0c\u5728\u4e0b\u4e13\u7a0b\u62a4\u9001\u56de\u6765\u7597\u6cbb\uff0c\u4f46\u6c42\u8d35\u6d3e\u65b9\u4e08\u5927\u5e08\u8d50\u89c1\u3002 \u3000\u3000\u51e0\u58f0\u547c\u4e2d\uff0c\u5c11\u6797\u50e7\u4eba\u7eb7\u7eb7\u73b0\u8eab\uff0c\u5408\u4ec0\u9053\uff1a\u591a\u8c22\u6a80\u8d8a\uff0c\u4e0d\u77e5\u9ad8\u59d3\u5927\u540d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5728\u4e0b\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u7af9\u6797\u6df1\u5904\uff0c\u6709\u4e24\u4e2a\u4eba\u6b63\u5728\u4e0b\u68cb\u3002 \u3000\u3000\u53f3\u9762\u7684\u662f\u4f4d\u76f8\u8c8c\u5947\u53e4\u7684\u8001\u548c\u5c1a\u3002 \u3000\u3000\u5de6\u9762\u7684\u662f\u4f4d\u67af\u7626\u77ee\u5c0f\u7684\u8001\u4eba\uff0c\u4f46\u76ee\u5149\u70af\u70af\uff0c\u9686\u9f3b\u5982\u9e70\uff0c\u4f7f\u4eba\u5168\u5fd8\u4e86\u4ed6\u8eab\u6750\u7684\u77ed\u5c0f\uff0c\u53ea\u80fd \u611f\u89c9\u5230\u4e00\u79cd\u65e0\u6bd4\u7684\u6743\u5a01\u548c\u9b44\u529b\u3002 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u80fd\u548c\u5c11\u6797\u638c\u95e8\u5fc3\u6e56\u5927\u5e08\u5bf9\u5750\u4e0b\u68cb\u7684\u4eba\uff0c\u9664\u4e86\u8fd9\u4f4d\u767e\u6653\u751f\u4e4b\u5916\uff0c\u53ea\u6015\u5df2\u5be5\u5be5\u65e0 \u51e0\u3002 \u3000\u3000\u8fd9\u4e24\u4e0b\u68cb\u65f6\uff0c\u5929\u4e0b\u53ea\u6015\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\u80fd\u4ee4\u4ed6\u4eec\u4e2d\u6b62\uff0c\u4f46\u542c\u5230\u674e\u5bfb\u6b22\u8fd9\u540d\u5b57\uff0c\u4e24\u4eba\u7adf\u90fd\u4e0d \u7531\u81ea\u4e3b\u957f\u8eab\u800c\u8d77\u3002 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u6b64\u4eba\u73b0\u5728\u54ea\u91cc\uff1f \u3000\u3000\u8e51\u7740\u811a\u8fdb\u6765\u901a\u62a5\u7684\u5c11\u6797\u5f1f\u5b50\u8eac\u8eab\u9053\uff1a\u5c31\u5728\u4e8c\u5e08\u53d4\u7684\u7985\u623f\u5916\u3002 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u4f60\u4e8c\u5e08\u53d4\u600e\u6837\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u6797\u50e7\u4eba\u9053\uff1a\u4e8c\u5e08\u53d4\u4f24\u5f97\u4eff\u4f5b\u4e0d\u8f7b\uff0c\u56db\u5e08\u53d4\u548c\u4e03\u5e08\u53d4\u6b63\u5728\u63a2\u89c6\u4ed6\u8001\u4eba\u5bb6\u7684\u4f24\u52bf\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8d1f\u624b\u7ad9\u5728\u6a90\u4e0b\uff0c\u9065\u671b\u7740\u5927\u6bbf\u4e0a\u96c4\u4f1f\u7684\u5c4b\u810a\uff0c\u5bd2\u98ce\u4e2d\u9690\u9690\u6709\u68b5\u5531\u4e4b\u58f0\u4f20\u6765\uff0c\u5929\u5730\u95f4 \u5145\u6ee1\u4e86\u53e4\u8001\u800c\u5e84\u4e25\u7684\u795e\u79d8\u3002 \u3000\u3000\u4ed6\u5df2\u611f\u89c9\u5230\u6709\u4eba\u8d70\u8fc7\u6765\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u8f6c\u5934\u53bb\u77a7\uff0c\u5728\u8fd9\u5e84\u4e25\u800c\u795e\u79d8\u7684\u5929\u5730\u4e2d\uff0c\u4ed6\u53c8\u4e0d\u89c9\u795e \u6e38\u7269\u5916\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u548c\u767e\u6653\u751f\u8d70\u5230\u4ed6\u8eab\u5916\u5341\u6b65\u5904\u5c31\u505c\u4e0b\uff0c\u5fc3\u6e56\u5927\u5e08\u867d\u7136\u4e45\u95fb\u5c0f\u674e\u63a2\u82b1\u7684\u540d\u58f0\uff0c\u4f46\u76f4 \u5230\u6b64\u523b\u624d\u89c1\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u60f3\u4e0d\u5230\u8fd9\u61d2\u6563\u800c\u6f47\u6d12\uff0c\u8427\u758f\u5374\u6c89\u7740\uff0c\u5145\u6ee1\u4e86\u8bd7\u4eba\u6c14\u8d28\u7684\u843d\u62d3\u5ba2\uff0c\u5c31\u662f\u540d\u6ee1\u5929\u4e0b\u7684 \u6d6a\u5b50\u6e38\u4fa0\u3002 \u3000\u3000\u4ed6\u4ed4\u7ec6\u7684\u89c2\u5bdf\u7740\u4ed6\uff0c\u7edd\u4e0d\u80af\u9519\u8fc7\u4efb\u4f55\u4e00\u5904\u5730\u65b9\uff0c\u5c24\u5176\u4e0d\u80af\u9519\u8fc7\u4ed6\u90a3\u53cc\u7626\u524a\uff0c\u7ec6\u957f\u7684\u624b\u3002 \u3000\u3000\u8fd9\u53cc\u624b\u7a76\u7adf\u662f\u4ec0\u4e48\u9b54\u529b\uff1f \u3000\u3000\u4e3a\u4f55\u4e00\u67c4\u51e1\u94c1\u94f8\u6210\u7684\u5200\uff0c\u5230\u4e86\u8fd9\u53cc\u624b\u91cc\u5c31\u53d8\u5f97\u90a3\u4e48\u795e\u5947\uff1f \u3000\u3000\u767e\u6653\u751f\u5341\u5e74\u524d\u5c31\u89c1\u8fc7\u4ed6\u7684\uff0c\u53ea\u89c9\u5f97\u8fd9\u5341\u5e74\u6765\u4ed6\u4f3c\u4e4e\u5e76\u6ca1\u6709\u4ec0\u4e48\u6539\u53d8\uff0c\u53c8\u4f3c\u4e4e\u5df2\u53d8\u4e86\u8bb8\u591a \u3002 \u3000\u3000\u65e0\u8bba\u548c\u591a\u5c11\u4eba\u5728\u4e00\u8d77\uff0c\u4ed6\u90fd\u662f\u5b64\u72ec\u7684\u3002 \u3000\u3000\u767e\u6653\u751f\u7ec8\u4e8e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u63a2\u82b1\u90ce\u522b\u6765\u65e0\u6059\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u60f3\u4e0d\u5230\u5148\u751f\u5c45\u7136\u8fd8\u8ba4\u5f97\u5728\u4e0b\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u5408\u5341\u9053\uff1a\u5374\u4e0d\u77e5\u63a2\u82b1\u90ce\u8ba4\u5f97\u8001\u50e7\u5426\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u957f\u63d6\u9053\uff1a\u5927\u5e08\u5fb7\u9ad8\u671b\u91cd\uff0c\u5929\u4e0b\u5949\u4e3a\u6cf0\u5c71\u5317\u6597\uff0c\u5728\u4e0b\u6c5f\u6e56\u672a\u5b66\uff0c\u5e38\u6068\u65e0\u7f18\u8bc6\u8346\uff0c\u4eca \u65e5\u5f97\u89c1\u6cd5\u9a7e\uff0c\u4f55\u5e78\u5982\u4e4b\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u63a2\u82b1\u90ce\u4e0d\u5fc5\u592a\u5acc\uff0c\u5e08\u5f1f\u627f\u8499\u6a80\u8d8a\u62a4\u9001\u4e0a\u95e8\uff0c\u8001\u50e7\u5148\u5728\u6b64\u523b\u8c22\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u6562\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u518d\u6b21\u5408\u5341\uff0c\u9053\uff1a\u7b49\u8001\u50e7\u63a2\u5e08\u5f1f\u7684\u4f24\u52bf\uff0c\u518d\u6765\u966a\u6a80\u8d8a\u53d9\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8bf7\u3002 \u3000\u3000\u7b49\u5fc3\u6e56\u8d70\u8fdb\u5c4b\u4e86\uff0c\u767e\u6653\u751f\u5ffd\u53c8\u4e00\u7b11\u9053\uff1a\u51fa\u5bb6\u4eba\u6db5\u517b\u529f\u592b\u679c\u7136\u6668\u6211\u7b49\u80fd\u53ca\uff0c\u82e5\u6362\u4e86\u662f\u6211\uff0c \u5bf9\u9601\u4e0b\u4eba\u6015\u5c31\u4e0d\u4f1a\u5982\u6b64\u591a\u793c\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u767e\u6653\u751f\u9053\uff1b\u82e5\u6709\u4eba\u4f24\u4e86\u4f60\u7684\u5e08\u5f1f\u548c\u7231\u5f92\uff0c\u4f60\u4f1a\u5bf9\u4ed6\u5982\u6b64\u5ba2\u6c14\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9601\u4e0b\u96be\u9053\u8ba4\u4e3a\u5fc3\u7709\u5927\u5e08\u4e5f\u662f\u88ab\u6211\u6240\u4f24\u7684\uff1f \u3000\u3000\u767e\u6653\u751f\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u4ef0\u9762\u671b\u5929\uff0c\u60a0\u7136\u9053\uff1a\u9664\u4e86\u5c0f\u674e\u63a2\u82b1\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u4f24\u5f97\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u82e5\u662f\u6211\u4f24\u4e86\u4ed6\uff0c\u4e3a\u4f55\u8fd8\u8981\u62a4\u9001\u4ed6\u56de\u5c71\uff1f \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u8fd9\u624d\u662f\u9601\u4e0b\u806a\u660e\u8fc7\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u65e0\u8bba\u8c01\u4f24\u4e86\u5c11\u6797\u62a4\u6cd5\uff0c\u6b64\u540e\u53ea\u63e1\u90fd\u8981\u6c38\u65e0\u5b81\u65e5\uff0c\u5c11\u6797\u5357\u5317\u4e24\u652f\u4e09\u5343\u5f1f\u5b50\uff0c\u662f \u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\uff0c\u8fd9\u529b\u91cf\u8c01\u4e5f\u4e0d\u6562\u5ffd\u89c6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u4ef0\u9762\u7b11\u9053\uff1a\u767e\u6653\u751f\u679c\u7136\u662f\u65e0\u6240\u4e0d\u77e5\uff0c\u96be\u602a\u6c5f\u6e56\u4e2d\u6240\u6709\u7684\u5927\u5e2e\u5927\u6d3e\u90fd\u8981\u4ea4 \u4f60\u8fd9\u670b\u53cb\u4e86\uff0c\u548c\u4f60\u4ea4\u670b\u53cb\u7684\u597d\u5904\u5b9e\u5728\u4e0d\u5c11\u3002 \u3000\u3000\u767e\u6653\u751f\u5c45\u7136\u795e\u8272\u4e0d\u53d8\uff0c\u9053\uff1a\u6211\u8bf4\u7684\u53ea\u4e0d\u8fc7\u662f\u516c\u9053\u8bdd\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u53ef\u60dc\u9601\u4e0b\u5374\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u5fc3\u7709\u5927\u5e08\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4ed6\u81ea\u5df1\u603b\u77e5\u9053\u81ea\u5df1\u662f\u88ab\u8c01\u6240 \u4f24\u7684\uff0c\u5230\u90a3\u65f6\u9601\u4e0b\u5c82\u975e\u5c06\u81ea\u5df1\u8bf4\u51fa\u6765\u7684\u8bdd\u541e\u56de\u53bb\u4e86\u4e48\uff1f \u3000\u3000\u767e\u6653\u751f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u82e5\u662f\u6211\u731c\u7684\u4e0d\u9519\uff0c\u5fc3\u7709\u5e08\u5144\u8fd8\u80fd\u8bf4\u8bdd\u7684\u673a\u4f1a\u53ea\u6015\u4e0d\u591a\u4e86\u3002 \u3000\u3000\u7a81\u542c\u5fc3\u6e56\u5927\u5e08\u5389\u58f0\u9053\uff1a\u5e08\u5f1f\u82e5\u975e\u4f24\u5728\u4f60\u7684\u624b\u4e0b\uff0c\u662f\u4f24\u5728\u8c01\u7684\u624b\u4e0b\uff1f \u3000\u3000\u4ed6\u4e0d\u77e5\u4f55\u65f6\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u9762\u4e0a\u5df2\u7b3c\u8d77\u4e00\u9635\u5bd2\u971c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5927\u5e08\u96be\u9053\u770b\u4e0d\u51fa\u4ed6\u662f\u4e2d\u4e86\u8c01\u7684\u6bd2\uff1f \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u56de\u5934\u5524\u9053\uff1a\u4e03\u5e08\u5f1f\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5fc3\u9274\u5927\u5e08\u9762\u8272\u70db\u9ec4\uff0c\u7ec8\u5e74\u90fd\u4eff\u4f5b\u5e26\u7740\u75c5\u5bb9\uff0c\u4f46\u4e00\u53cc\u773c\u775b\u5374\u662f\u51cc\u51cc\u6709\u5a01\uff0c\u95ea\u7535\u822c\u5728 \u674e\u5bfb\u6b22\u9762\u524d\u4e00\u626b\uff0c\u6c89\u58f0\u9053\uff1a\u4e8c\u5e08\u5144\u4e2d\u7684\u6bd2\u4e43\u662f\u82d7\u7586\u6781\u4e50\u5cd2\u4e3b\u7cbe\u70bc\u6210\u7684\u4e94\u6bd2\u6c34\u6676\uff0c\u6b64\u7269\u65e0\u8272\u65e0 \u5473\uff0c\u900f\u660e\u5982\u6c34\u6676\uff0c\u4e2d\u6bd2\u7684\u4eba\u82e5\u5f97\u4e0d\u5230\u89e3\u836f\uff0c\u5168\u8eab\u808c\u80a4\u4e5f\u4f1a\u6e10\u6e10\u53d8\u5f97\u900f\u660e\u5982\u6c34\u6676\uff0c\u4e94\u810f\u516d\u8151\u5386 \u5386\u53ef\u6570\uff0c\u5230\u4e86\u90a3\u65f6\uff0c\u4fbf\u5df2\u6bd2\u53d1\u65e0\u6551\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u5927\u5e08\u679c\u7136\u9ad8\u660e\u2014\u2014 \u3000\u3000\u5fc3\u9274\u5927\u5e08\u51b7\u51b7\u9053\uff1a\u8d2b\u50e7\u53ea\u77e5\u9053\u4e8c\u5e08\u5144\u4e2d\u7684\u4e43\u662f\u4e94\u6bd2\u6c34\u6676\uff0c\u4f46\u5728\u4e0b\u6bd2\u7684\u4eba\u662f\u8c01\uff0c\u8d2b\u50e7\u5374\u4e0d \u77e5\u9053\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u8bf4\u7684\u597d\uff0c\u6bd2\u662f\u6b7b\u7684\uff0c\u4e0b\u6bd2\u7684\u4eba\u5374\u662f\u6d3b\u7684\u2014\u2014 \u3000\u3000\u5fc3\u9274\u5927\u5e08\u9053\uff1a\u6781\u4e50\u5cd2\u4e3b\u867d\u7136\u884c\u4e8b\u6076\u6bd2\uff0c\u4f46\u4eba\u4e0d\u72af\u4ed6\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u72af\u4eba\uff0c\u672c\u95e8\u4e0e\u4ed6\u7d20\u65e0\u7ea0\u845b \uff0c\u4ed6\u4e3a\u4f55\u8981\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\u6697\u7b97\u4e8c\u5e08\u5144\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u8fd9\u53ea\u56e0\u4ed6\u7684\u5bf9\u50cf\u5e76\u975e\u5fc3\u7709\u5927\u5e08\uff0c\u800c\u662f\u6211\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u8fd9\u8bdd\u66f4\u5999\u4e86\uff0c\u4ed6\u8981\u5bb3\u7684\u4eba\u662f\u4f60\uff0c\u4f60\u5374\u597d\u597d\u7684\u7ad9\u5728\u8fd9\u91cc\uff0c\u4ed6\u5e76\u6ca1\u6709\u52a0\u5bb3\u5fc3\u7709\u5e08 \u5144\u4e4b\u610f\uff0c\u5fc3\u7709\u5e08\u5144\u53cd\u800c\u4e2d\u4e86\u6bd2\u3002 \u3000\u3000\u4ed6\u76ef\u7740\u674e\u5bfb\u6b22\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u4f60\u82e5\u8fd8\u80fd\u8bf4\u5f97\u51fa\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\uff0c\u6211\u5c31\u4f69\u670d\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\u5ffd\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u6211\u8bf4\u4e0d\u51fa\uff0c\u53ea\u56e0\u6211\u65e0\u8bba\u8bf4\u4ec0\u4e48\uff0c\u4f60\u4eec\u90fd\u672a\u5fc5\u4f1a\u76f8\u4fe1\u7684 \u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u9601\u4e0b\u8bf4\u7684\u8bdd\u786e\u5b9e\u5f88\u96be\u4ee4\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u867d\u8bf4\u4e0d\u51fa\uff0c\u4f46\u8fd8\u662f\u6709\u4eba\u80fd\u8bf4\u5f97\u51fa\u7684\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5fc3\u7709\u5927\u5e08\uff0c\u4e3a\u4f55\u4e0d\u7b49\u4ed6\u9192\u6765\u4e4b\u540e\u518d\u95ee\u4ed6\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u51dd\u89c6\u7740\u4ed6\uff0c\u76ee\u5149\u51b7\u5f97\u50cf\u5200\u3002 \u3000\u3000\u5fc3\u9274\u5927\u5e08\u7684\u8138\u4e0a\u4e5f\u7b3c\u7740\u5c42\u5bd2\u971c\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u4e8c\u5e08\u5144\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u9192\u8fc7\u6765\u4e86\uff01 \u7b2c\u4e8c\u5341\u7ae0\u3000\u4eba\u5fc3\u96be\u6d4b>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u7ae0\u3000\u4eba\u5fc3\u96be\u6d4b \u3000\u3000\u51b7\u98ce\u5982\u5200\uff0c\u79ef\u96ea\u7684\u5c4b\u810a\u4e0a\u7a81\u6709\u4e00\u7fa4\u5bd2\u9e26\u60ca\u8d77\uff0c\u63a5\u7740\uff0c\u5c4b\u810a\u540e\u5c31\u54cd\u8d77\u4e86\u4e00\u9635\u6e05\u4eae\u5374\u51c4\u51c9\u7684 \u949f\u58f0\u3002 \u3000\u3000\u8fde\u949f\u58f0\u90fd\u4f3c\u4e4e\u5728\xd7\u6389\u7740\u4ed6\u4eec\u62a4\u6cd5\u5927\u5e08\u7684\u5706\u5bc2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4eff\u4f5b\u7b2c\u4e00\u6b21\u611f\u89c9\u98ce\u4e2d\u7684\u5bd2\u610f\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5267\u70c8\u7684\u54b3\u55fd\u8d77\u6765\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u6124\u6012\uff0c \u662f\u540e\u6094\uff0c\u8fd8\u662f\u96be\u53d7\uff1f \u3000\u3000\u7b49\u4ed6\u54b3\u5b8c\u4e86\uff0c\u5c31\u53d1\u73b0\u6570\u5341\u4e2a\u7070\u8863\u50e7\u4eba\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u81ea\u5c0f\u9662\u7684\u95e8\u5916\u8d70\u4e86\u8fdb\u6765\uff0c\u6bcf\u4e2a\u4eba\u4eba\u8138 \u4e0a\u5374\u50cf\u662f\u51dd\u7ed3\u7740\u4e00\u5c42\u51b0\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u76ef\u7740\u4ed6\uff0c\u5634\u90fd\u95ed\u5f97\u7d27\u7d27\u7684\uff0c\u949f\u58f0\u4e5f\u4e0d\u77e5\u4f55\u65f6\u505c\u987f\uff0c\u6240\u6709\u7684\u58f0\u97f3\u90fd\u4f3c\u5df2\u5728 \u5bd2\u6c14\u4e2d\u51dd\u7ed3\uff0c\u53ea\u6709\u811a\u8e0f\u5728\u96ea\u5730\u7684\uff0c\u6c99\u6c99\u4f5c\u54cd\u3002 \u3000\u3000\u7b49\u5230\u8fd9\u811a\u6b65\u58f0\u4e5f\u505c\u6b62\u4e86\uff0c\u674e\u5bfb\u6b22\u5168\u8eab\u90fd\u4eff\u4f5b\u5df2\u88ab\u51b0\u7ed3\u5728\u4e00\u5c42\u53c8\u4e00\u5c42\u6bd4\u94c5\u8fd8\u6c89\u91cd\u7684\u5bd2\u51b0\u91cc \u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u4f60\u8fd8\u6709\u4f55\u8bdd\u8bf4\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u4f60\u672c\u4e0d\u8be5\u6765\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u4e5f\u8bb8\u6211\u7684\u786e\u4e0d\u8be5\u6765\uff0c\u4f46\u65f6\u5149\u82e5\u80fd\u5012\u8f6c\uff0c\u6211\u53ea\u6015\u8fd8 \u662f\u4f1a\u8fd9\u6837\u505a\u3002 \u3000\u3000\u4ed6\u6de1\u6de1\u63a5\u7740\u9053\uff1a\u6211\u5e73\u751f\u867d\u7136\u6740\u4eba\u65e0\u6570\uff0c\u5374\u4ece\u672a\u89c1\u6b7b\u4e0d\u6551\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u6012\u9053\uff1a\u5230\u6b64\u65f6\uff0c\u4f60\u8fd8\u662f\u60f3\u72e1\u8fa9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u51fa\u5bb6\u4eba\u8bb2\u7a76\u7684\u662f\u56db\u5927\u7686\u7a7a\uff0c\u4e0d\u53ef\u5984\u52a8\u55d4\u5ff5\uff0c\u4e45\u95fb\u5927\u5e08\u4fee\u884c\u529f\u6df1\uff0c\u600e\u5730\u548c\u5728\u4e0b \u4e00\u6837\u6c89\u4e0d\u4f4f\u6c14\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u4e45\u95fb\u63a2\u82b1\u90ce\u5b66\u8bc6\u6e0a\u6e90\uff0c\u600e\u5730\u5374\u5fd8\u4e86\u8fde\u6211\u4f5b\u5982\u6765\u4e5f\u96be\u514d\u8981\u4f5c\u72ee\u5b50\u543c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u5404\u4f4d\u8bf7\u543c\u5427\uff0c\u53ea\u671b\u5404\u4f4d\u83ab\u5728\u543c\u7834\u4e86\u5589\u5499\u3002 \u3000\u3000\u5fc3\u6e56\u5389\u58f0\u9053\uff1a\u5230\u6b64\u65f6\uff0c\u4f60\u8fd8\u8981\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u53ef\u89c1\u5168\u65e0\u6094\u6539\u4e4b\u5fc3\uff0c\u770b\u6765\u4eca\u65e5\u8d2b\u50e7\u5c11\u4e0d\u5f97\u8981 \u7834\u4e00\u7834\u6740\u620e\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u5c3d\u7ba1\u7834\u5427\uff0c\u597d\u5728\u6740\u4eba\u7684\u548c\u5c1a\u5e76\u4e0d\u6b62\u4f60\u4e00\u4e2a\uff01 \u3000\u3000\u5fc3\u9274\u5927\u5e08\u6012\u9053\uff1a\u6211\u6740\u4eba\u5e76\u975e\u4e3a\u4e86\u590d\u4ec7\uff0c\u800c\u662f\u964d\u9b54\uff01 \u3000\u3000\u4ed6\u8eab\u5f62\u65b9\u5f85\u4f5c\u52bf\u6251\u8d77\uff0c\u7a81\u89c1\u5200\u5149\u4e00\u95ea\uff0c\u674e\u5bfb\u6b22\u638c\u4e2d\u4e0d\u77e5\u4f55\u65f6\u5df2\u591a\u4e86\u67c4\u5bd2\u5149\u95ea\u95ea\u7684\u5200\uff0c\u5c0f \u674e\u98de\u5200\uff01 \u3000\u3000\u53ea\u542c\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u529d\u4f60\u8fd8\u662f\u83ab\u8981\u964d\u9b54\u7684\u597d\uff0c\u56e0\u4e3a\u4f60\u7edd\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff01 \u3000\u3000\u5fc3\u6e56\u5389\u58f0\u9053\uff1a\u4f60\u96be\u9053\u8fd8\u60f3\u4f5c\u56f0\u517d\u4e4b\u6597\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u65e5\u5b50\u867d\u4e0d\u597d\u8fc7\uff0c\u6211\u5374\u8fd8\u672a\u5230\u6b7b\u7684\u65f6\u5019\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u5c0f\u674e\u98de\u5200\u7eb5\u7136\u4f8b\u4e0d\u865a\u53d1\uff0c\u4f46\u53c8\u6709\u51e0\u67c4\u98de\u5200\uff1f\u80fd\u6740\u5f97\u4ee5\u51e0\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u7b11\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u5fc3\u6e56\u76ee\u5149\u4e00\u76f4\u76ef\u7740\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u5ffd\u7136\u9053\uff1a\u597d\uff0c\u4e14\u5f85\u8001\u8872\u6765\u9886\u6559\u9886\u6559\u4f60\u7684\u795e\u5200\uff01 \u3000\u3000\u4ed6\u888d\u8863\u4e00\u5c55\u5927\u6b65\u8d70\u51fa\u3002 \u3000\u3000\u4f46\u767e\u6653\u751f\u5374\u62c9\u4f4f\u4e86\u4ed6\uff0c\u6c89\u58f0\u9053\uff1a\u5927\u5e08\u4f60\u5343\u4e07\u4e0d\u53ef\u51fa\u624b\uff01 \u3000\u3000\u5fc3\u6e56\u76b1\u7709\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u767e\u6653\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5929\u4e0b\u8c01\u4e5f\u6ca1\u6709\u628a\u63e1\u80fd\u907f\u5f00\u4ed6\u8fd9\u51fa\u624b\u4e00\u5200\uff01 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u6ca1\u6709\u4eba\u80fd\u907f\u5f97\u5f00\uff1f \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u6ca1\u6709\uff01\u4e00\u4e2a\u4e5f\u6ca1\u6709\uff01 \u3000\u3000\u5fc3\u6e56\u957f\u957f\u547c\u51fa\u53e3\u6c14\uff0c\u7791\u76ee\u9053\uff1a\u6211\u4e0d\u5165\u5730\u72f1\uff01\u8c01\u5165\u5730\u72f1\u3002 \u3000\u3000\u5fc3\u9274\u5927\u5e08\u4e5f\u8d76\u4e86\u8fc7\u6765\uff0c\u55c4\u58f0\u9053\uff1a\u5e08\u5144\u4f60\u2014\u2014\u4f60\u4e00\u8eab\u7cfb\u4f5b\u95e8\u5b89\u5371\uff0c\u600e\u80fd\u8f7b\u8eab\u6d89\u9669\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u4eec\u90fd\u4e0d\u5fc5\u6765\u5192\u9669\u7684\uff0c\u53cd\u6b63\u5c11\u6797\u95e8\u4e0b\u6709\u4e09\u5343\u5f1f\u5b50\uff0c\u53ea\u8981\u4f60\u4eec\u4e00\u58f0\u53f7\u4ee4 \uff0c\u4f1a\u66ff\u4f60\u4eec\u9001\u6b7b\u7684\u4eba\u81ea\u7136\u4e0d\u5c11\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u8138\u4e0a\u53d8\u4e86\u53d8\u989c\u8272\uff0c\u5389\u58f0\u9053\uff1a\u672a\u5f97\u672c\u5ea7\u8bb8\u8bfa\uff0c\u672c\u95e8\u5f1f\u5b50\u8c01\u4e5f\u4e0d\u8bb8\u5984\u52a8\uff0c\u5426\u5219\u4ee5\u95e8 \u89c4\u5904\u6cbb\uff0c\u7edd\u4e0d\u8f7b\u8d37\uff0c\u2014\u2014\u77e5\u9053\u4e86\u4e48\uff1f \u3000\u3000\u5c11\u6797\u50e7\u4eba\u4e00\u9f50\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u7edd\u4e0d\u80af\u773c\u89c1\u95e8\u4e0b\u5f1f\u5b50\u9001\u6b7b\u7684\uff0c\u5c11\u6797\u5bfa\u6bd5\u7adf\u548c\u6c5f\u6e56\u4e2d\u90a3\u4e9b\u73a9 \u547d\u7684\u5e2e\u4f1a\u4e0d\u540c\uff0c\u5426\u5219\u6211\u8fd9\u6fc0\u5c06\u6cd5\u600e\u7528\u5f97\u4e0a\uff1f \u3000\u3000\u767e\u6653\u751f\u51b7\u51b7\u9053\uff1a\u5c11\u6797\u5e08\u5144\u4eec\u7eb5\u7136\u72af\u4e0d\u4e0a\u548c\u4f60\u8fd9\u79cd\u4eba\u62fc\u547d\uff0c\u4f46\u4f60\u96be\u9053\u8fd8\u60f3\u8d70\u5f97\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u8c01\u8bf4\u6211\u60f3\u8d70\u4e86\uff1f \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u4f60\u2014\u2014\u4f60\u4e0d\u60f3\u8d70\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u975e\u672a\u660e\uff0c\u9ed1\u767d\u672a\u5206\uff0c\u5c31\u600e\u4e48\u4e00\u8d70\u4e86\u4e4b\uff1f \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u4f60\u96be\u80fd\u4ee4\u6781\u4e50\u5cd2\u4e3b\u5230\u8fd9\u91cc\u6765\u81ea\u8ba4\u662f\u5bb3\u6b7b\u5fc3\u7709\u5927\u5e08\u5144\u7684\u51f6\u624b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u80fd\uff0c\u53ea\u56e0\u4ed6\u5df2\u6b7b\u4e86\uff01 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u662f\u4f60\u6740\u4e86\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u4ed6\u4e5f\u662f\u4eba\uff0c\u6240\u4ee5\u4ed6\u6ca1\u6709\u8eb2\u8fc7\u6211\u51fa\u624b\u7684\u4e00\u5200\uff01 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u5ffd\u7136\u9053\uff1a\u4f60\u82e5\u80fd\u5bfb\u51fa\u4ed6\u7684\xd7\u8eab\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u8bc1\u660e\u4f60\u5e76\u975e\u5b8c\u5168\u8bf4\u8c0e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u5f97\u5fc3\u91cc\u6709\u4e9b\u53d1\u82e6\uff0c\u82e6\u7b11\u9053\uff1a\u7eb5\u7136\u5bfb\u5f97\u4ed6\u7684\xd7\u9aa8\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u8ba4\u5f97\u51fa\u4ed6\u662f\u8c01\u4e86 \u3002 \u3000\u3000\u767e\u6653\u751f\u51b7\u7b11\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u5929\u4e0b\u8fd8\u6709\u8c01\u80fd\u8bc1\u660e\u4f60\u662f\u65e0\xd7\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u8fd8\u672a\u60f3\u51fa\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u90a3\u4e48\u73b0\u5728\u4f60\u60f3\u600e\u6837\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u73b0\u5728\u6211\u53ea\u60f3\u559d\u676f\u9152\u3002 \u3000\u3000\u963f\u98de\u5750\u7684\u59ff\u52bf\u5f88\u4e0d\u597d\u770b\uff0c\u4ed6\u4ece\u6765\u4e5f\u4e0d\u4f1a\u50cf\u674e\u5bfb\u6b22\u90a3\u6837\uff0c\u8212\u670d\u7684\u5750\u5728\u4e00\u5f20\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u4e2d\u51e0\u4e4e\u5f88\u5c11\u6709\u673a\u4f1a\u80fd\u5750\u4e0a\u4e00\u5f20\u771f\u7684\u6905\u5b50\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8737\u4f0f\u5728\u706b\u7089\u65c1\uff0c\u9762\u5e9e\u88ab\u7089\u706b\u70e4\u5f97\u7ea2\u7ea2\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u5929\uff0c\u5979\u4f3c\u4e4e\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u9616\u8fc7\uff0c\u73b0\u5728\u963f\u98de\u7684\u4f24\u52bf\u4f3c\u5947\u8ff9\u822c\u75ca\u6108\u4e86\uff0c\u5979\u624d\u653e\u5fc3\u7684\u7761\u7740 \u3002 \u3000\u3000\u963f\u98de\u9759\u9759\u5730\u671b\u7740\u5979\uff0c\u4f3c\u5df2\u75f4\u4e86\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u53ea\u6709\u5979\u5747\u5300\u7684\u547c\u5438\u58f0\uff0c\u5916\u9762\u7684\u96ea\u5df2\u6eb6\u5316\uff0c\u5929\u5730\u95f4\u5145\u6ee1\u4e86\u6e29\u6696\u548c\u606c\u9759\u3002 \u3000\u3000\u963f\u98de\u7684\u76ee\u4e2d\u5374\u6e10\u6e10\u9732\u51fa\u4e00\u4e1d\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u6084\u6084\u7a7f\u8d77\u4e86\u9774\u5b50\u3002 \u3000\u3000\u963f\u98de\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5728\u5c4b\u89d2\u7684\u684c\u4e0a\u5bfb\u56de\u4e86\u4ed6\u7684\u5251\uff01\u5899\u4e0a\u6302\u7740\u4e00\u5e45\u5b57\uff0c\u662f\u674e\u5bfb\u6b22\u7684\u624b\u7b14 \uff0c\u5176\u4e2d\u6709\u4e00\u53e5\u662f\uff1a\u6b64\u60c5\u53ef\u5f85\u6210\u8ffd\u5fc6\uff01 \u3000\u3000\u53ea\u6709\u56de\u5fc6\u4e2d\u7684\u751c\u871c\uff0c\u624d\u80fd\u6c38\u8fdc\u4fdd\u6301\u3002 \u3000\u3000\u963f\u98de\u8f7b\u8f7b\u5c06\u5251\u63d2\u5165\u4e86\u8170\u5e26\u3002 \u3000\u3000\u7a81\u542c\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u2014\u2014\u4f60\u8981\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u4e0d\u6562\u56de\u5934\u770b\u5979\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u6211\u8981\u8d70\u4e86\uff01 \u3000\u3000\u6797\u4ed9\u513f\u5931\u9053\uff1a\u8d70\uff1f \u3000\u3000\u5979\u7ad9\u8d77\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u4f60\u8fde\u8bf4\u90fd\u4e0d\u8bf4\u4e00\u58f0\uff0c\u5c31\u8981\u6084\u6084\u7684\u8d70\u4e86\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u65e2\u7136\u8981\u8d70\uff0c\u53c8\u4f55\u5fc5\u8bf4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8eab\u5b50\u4f3c\u4e4e\u5ffd\u7136\u8f6f\u4e86\uff0c\u5012\u5728\u6905\u5b50\u4e0a\uff0c\u4e24\u6ef4\u6cea\u73e0\u5df2\u6eda\u4e0b\u4e86\u9762\u5e9e\u3002 \u3000\u3000\u963f\u98de\u89c9\u5f97\u5fc3\u91cc\u4e00\u9635\u7ede\u75db\uff0c\u4ed6\u4ece\u6765\u672a\u5c1d\u8fc7\u8fd9\u79cd\u65e2\u4e0d\u662f\u6101\uff0c\u4e5f\u4e0d\u662f\u82e6\uff0c\u65e2\u4e0d\u662f\u751c\uff0c\u4e5f\u4e0d\u662f\u9178 \u7684\u6ecb\u5473\u3002 \u3000\u3000\u8fd9\u96be\u9053\u5c31\u662f\u60c5\u7684\u6ecb\u5473\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u6551\u4e86\u6211\uff0c\u6211\u8fdf\u65e9\u4f1a\u62a5\u7b54\u4f60\u7684\u2014\u2014 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u597d\uff0c\u4f60\u5feb\u62a5\u7b54\u6211\u5427\uff0c\u6211\u6551\u4f60\uff0c\u5c31\u4e3a\u7684\u662f\u8981\u4f60\u62a5\u7b54\u6211\u3002 \u3000\u3000\u5979\u5728\u7b11\uff0c\u53ef\u662f\u5979\u773c\u6cea\u5374\u6d41\u5f97\u66f4\u591a\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u610f\uff0c\u4f46\u6211\u4e0d\u80fd\u4e0d\u53bb\u627e\u674e\u5bfb\u6b22\u2014\u2014\uff0d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u600e\u77e5\u6211\u4e0d\u613f\u53bb\u627e\u4ed6\uff0c\u4f60\u4e3a\u4f55\u4e0d\u5e26\u6211\u53bb\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u2014\u2014\u6211\u4e0d\u613f\u8fde\u7d2f\u4f60\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6d41\u6cea\uff0c\u9053\uff1a\u8fde\u7d2f\u6211\uff1f\u4f60\u4ee5\u4e3a\u4f60\u8d70\u4e86\u540e\uff0c\u6211\u5c31\u4f1a\u5f88\u5e78\u798f\u4e48\uff1f \u3000\u3000\u963f\u98de\u60f3\u8bf4\u8bdd\uff0c\u4f46\u5634\u5507\u5374\u6709\u4e9b\u53d1\u6296\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6251\u8fc7\u6765\u62b1\u4f4f\u4e86\u4ed6\uff0c\u7d27\u7d27\u62b1\u7740\u4ed6\uff0c\u50cf\u662f\u8981\u7528\u5168\u5fc3\u5168\u610f\uff0c\u5168\u90e8\u751f\u547d\u62b1\u4f4f\uff0c\u98a4\u58f0\u9053\uff1a\u5e26 \u6211\u8d70\uff0c\u5e26\u6211\u8d70\u5427\uff0c\u4f60\u82e5\u4e0d\u5e26\u6211\u8d70\uff0c\u6211\u5c31\u6b7b\u5728\u4f60\u9762\u524d\u3002 \u3000\u3000\u591c\u5f88\u9759\u3002 \u3000\u3000\u963f\u98de\u8d70\u51fa\u5c4b\u5b50\uff0c\u5c31\u770b\u5230\u4e00\u7247\u79ef\u96ea\u7684\u6885\u82b1\u3002 \u3000\u3000\u539f\u6765\u8fd9\u91cc\u5c31\u662f\u51b7\u9999\u5c0f\u7b51\uff0c\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e24\u5929\u5174\u4e91\u5e84\u5df2\xd7\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u5374\u6ca1\u4e00\u4e2a\u4eba\u5230\u8fd9\u91cc \u6765\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u8981\u641c\u6355\u963f\u98de\uff0c\u4e3a\u4f55\u672a\u641c\u5230\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u4eec\u4e3a\u4f55\u5982\u6b64\u4fe1\u4efb\u6797\u4ed9\u513f\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7d27\u62c9\u7740\u963f\u98de\u7684\u624b\uff0c\u9053\uff1a\u6211\u8981\u53bb\u8ddf\u6211\u59d0\u59d0\u8bf4\u4e00\u53e5\u624d\u80fd\u8d70\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u53bb\u5427\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5507\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u4e0d\u653e\u5fc3\u7559\u4f60\u4e00\u4e2a\u4eba\u5728\u8fd9\u91cc\uff0c\u6211\u8981\u8ddf\u4f60\u4e00\u8d77\u8d70\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u53ef\u662f\u4f60\u7684\u59d0\u59d0\uff01 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u653e\u5fc3\uff0c\u5979\u4e5f\u662f\u674e\u5bfb\u6b22\u7684\u597d\u670b\u53cb\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u8fd8\u6709\u4e00\u70b9\u5b64\u706f\uff0c\u5374\u886c\u5f97\u8fd9\u5c0f\u697c\u66f4\u5b64\u96f6\u8427\u7d22\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u9ec4\u5e54\u4f4e\u5782\uff0c\u4eba\u5374\u672a\u7761\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u6b63\u5b88\u5b64\u706f\uff0c\u75f4\u75f4\u7684\u4e5f\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u6797\u4ed9\u513f\u62c9\u7740\u963f\u98de\u6084\u6084\u8d70\u4e0a\u6765\uff0c\u8f7b\u8f7b\u5524\u9053\uff1a\u5927\u59d0\u2014\u2014\u5927\u59d0\u4e3a\u4f55\u8fd8\u6ca1\u6709\u7761\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u8fd8\u662f\u75f4\u75f4\u5730\u5750\u7740\uff0c\u8fde\u5934\u90fd\u6ca1\u6709\u62ac\u8d77\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5927\u59d0\uff0c\u6211\u2014\u2014\u6211\u662f\u6765\u5411\u4f60\u544a\u522b\u7684\uff0c\u6211\u8981\u8d70\u4e86\uff0c\u8981\u662f\u2014\u2014\u53ef\u662f\u6211\u7edd\u4e0d\u4f1a\u5fd8\u4e86\u5927 \u59d0\u5bf9\u6211\u6441\uff0c\u5217\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u770b\u4f60\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4f3c\u4e4e\u542c\u4e0d\u61c2\u5979\u5728\u8bf4\u4ec0\u4e48\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u4f60\u8d70\u5427\uff0c\u8d70\u4e86\u6700\u597d\uff0c \u8fd9\u91cc\u672c\u5df2\u6ca1\u6709\u4ec0\u4e48\u53ef\u7559\u604b\u4e4b\u5904\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u59d0\u592b\u5462\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u59d0\u592b\uff0c\u8c01\u7684\u59d0\u592b\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u81ea\u7136\u662f\u6211\u7684\u59d0\u592b\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u4f60\u7684\u59d0\u592b\u6211\u4e0d\u77e5\u9053\u2014\u2014\u6211\u4e0d\u77e5\u9053\u2014\u2014\u6211\u4e0d\u77e5\u9053 \u3000\u3000\u6797\u4ed9\u513f\u4f3c\u4e4e\u5446\u4e86\uff0c\u5446\u4e86\u534a\u664c\uff0c\u624d\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u4eec\u73b0\u5728\u8981\u7531\u8fd1\u8def\u8d76\u5230\u5c11\u6797\u5bfa\u53bb\uff01 \u3000\u3000\u6797\u8bd7\u97f3\u7a81\u7136\u8df3\u4e86\u8d77\u6765\u5927\u58f0\u9053\uff1a\u4f60\u8d70\u5427\uff0c\u5feb\u8d70\u2014\u2014\uff0d\u4e2a\u5b57\u90fd\u83ab\u8981\u8bf4\u4e86\uff0c\u5feb\u8d70\uff01 \u3000\u3000\u5979\u6325\u7740\u53cc\u624b\uff0c\u5c06\u6797\u4ed9\u513f\u548c\u963f\u98de\u5168\u90fd\u8d76\u4e86\u4e0b\u53bb\uff0c\u53c8\u7f13\u7f13\u5750\u56de\u706f\u8fb9\uff0c\u773c\u6cea\u5df2\u6d41\u4e0b\u4e86\u9762\u988a\u3002 \u3000\u3000\u4f4e\u5782\u7740\u7684\u9ec4\u5e54\u5916\u7f13\u7f13\u8d70\u51fa\u4e00\u4e2a\u4eba\uff0c\u7adf\u662f\u9f99\u5578\u4e91\u3002 \u3000\u3000\u4ed6\u77aa\u7740\u6797\u8bd7\u97f3\uff0c\u5634\u89d2\u6cdb\u8d77\u4e86\u4e00\u4e1d\u72de\u7b11\uff0c\u51b7\u51b7\u9053\uff1a\u4f60\u4eec\u5c31\u7b97\u5230\u4e86\u5c11\u6797\u5bfa\u4e5f\u6ca1\u7528\u7684\uff0c\u666e\u5929\u4e4b \u4e0b\uff0c\u5df2\u7ecf\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u6551\u5f97\u4e86\u674e\u5bfb\u6b22\u4e86\u2014\u2014 \u3000\u3000\u963f\u98de\u5403\u5f97\u867d\u591a\uff0c\u5e76\u4e0d\u5feb\u3002 \u3000\u3000\u4f46\u4ed6\u53cc\u9a88\u4e0d\u662f\u50cf\u674e\u5bfb\u6b22\u90a3\u6837\u5728\u6162\u6162\u54c1\u8d4f\u7740\u98df\u7269\u7684\u6ecb\u5473\uff0c\u4ed6\u53ea\u662f\u60f3\u5c06\u98df\u7269\u7684\u517b\u4efd\u5c3d\u91cf\u5438\u6536 \uff0c\u8ba9\u6bcf\u4e00\u53e3\u98df\u7269\u80fd\u8ba9\u4ed6\u5728\u4ed6\u8eab\u4f53\u53d1\u6325\u6700\u5927\u7684\u80fd\u91cf\u3002 \u3000\u3000\u4ed6\u5403\u4e86\u4e00\u9910\u996d\u540e\uff0c\u6c38\u8fdc\u4e0d\u77e5\u9053\u7b2c\u4e8c\u9910\u996d\u5728\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u5403\u5f97\u5230\u5634\uff0c\u6240\u4ee5\u6bcf\u4e00\u53e3\u98df\u7269\u4ed6\u90fd \u7edd\u4e0d\u80fd\u6d6a\u8d39\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6258\u7740\u816e\uff0c\u8109\u8109\u542b\u60c5\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u5979\u4ece\u672a\u89c1\u8fc7\u4e00\u4e2a\u5bf9\u98df\u7269\u5982\u6b64\u5c0a\u656c\u7684\u4eba\uff0c\u56e0\u4e3a\u53ea\u6709\u77e5\u9053\u9965\u997f\u53ef\u6015\u7684\u4eba\uff0c\u624d\u61c2\u5f97\u5bf9\u98df\u7269\u5c0a\u656c \u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u7b11\u9053\uff1a\u5403\u9971\u4e86\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u592a\u9971\u4e86\uff01 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u770b\u4f60\u5403\u996d\u771f\u6709\u8da3\uff0c\u4f60\u4e00\u987f\u5403\u7684\u4e1c\u897f\uff0c\u6211\u4e09\u5929\u90fd\u5403\u4e0d\u5b8c\u3002 \u3000\u3000\u963f\u98de\u4e5f\u7b11\uff0c\u9053\uff1a\u4f46\u6211\u53ef\u4ee5\u4e09\u5929\u4e0d\u5403\u996d\uff0c\u4f60\u80fd\u4e0d\u80fd\uff01 \u3000\u3000\u6797\u4ed9\u513f\u770b\u7740\u4ed6\u7684\u7b11\u5bb9\uff0c\u4f3c\u4e5f\u75f4\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u5ffd\u7136\u95ee\u9053\uff1a\u4f60\u5fd8\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u7684\u91d1\u4e1d\u7532\u8fd8\u5728\u6211\u8fd9\u91cc\u3002 \u3000\u3000\u5979\u89e3\u5f00\u5305\u88b1\uff0c\u53d6\u51fa\u4e86\u91d1\u4e1d\u7532\uff0c\u5728\u706f\u5149\u4e0b\u770b\u6765\uff0c\u8fd9\u4ece\u4eba\u5782\u6d8e\u7684\u6b66\u6797\u91cd\u5b9d\uff0c\u7684\u786e\u662f\u8f89\u714c\u70c2\uff0c \u4e0d\u53ef\u65b9\u7269\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e3a\u4e86\u770b\u4f60\u7684\u4f24\u52bf\uff0c\u6211\u53ea\u5f97\u66ff\u4f60\u8131\u4e0b\u6765\uff0c\u4e00\u76f4\u5fd8\u4e86\u8fd8\u7ed9\u4f60\u3002 \u3000\u3000\u963f\u98de\u770b\u4e5f\u6ca1\u6709\u770b\u4e00\u773c\uff0c\u9053\uff1a\u4f60\u7559\u7740\u5427\uff01 \u3000\u3000\u6797\u4ed9\u513f\u76ee\u4e2d\u9732\u51fa\u6b22\u559c\u4e4b\u8272\uff0c\u4f46\u5374\u6447\u5934\u9053\uff1a\u8fd9\u662f\u4f60\u6240\u5f97\u6765\u7684\u4e1c\u897f\uff0c\u4f60\u4ee5\u540e\u4e5f\u8bb8\u8fd8\u4f1a\u9700\u8981\u5b83 \u7684\uff0c\u600e\u4e48\u80fd\u968f\u4fbf\u5c31\u9001\u7ed9\u522b\u4eba\uff1f \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u5979\uff0c\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u5f88\u6e29\u67d4\u9053\uff1a\u6211\u6ca1\u6709\u9001\u7ed9\u522b\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u9001\u522b\u4eba\uff0c\u6211\u53ea\u662f\u7ed9\u4f60 \u3002 \u3000\u3000\u6797\u4ed9\u513f\u75f4\u75f4\u7684\u671b\u7740\u4ed6\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u611f\u6fc0\u548c\u6b23\u559c\uff0c\u6797\u4ed9\u513f\u5ffd\u7136\u6251\u5165\u4ed6\u6000\u91cc\u3002 \u3000\u3000\u963f\u98de\u7684\u5fc3\u5df2\u5267\u70c8\u7684\u8df3\u52a8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u4e2d\u4ece\u672a\u9886\u7565\u8fc7\u5982\u6b64\u6e29\u67d4\u4e5f\u5982\u6b64\u6d88\u9b42\u7684\u6ecb\u5473\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5077\u5077\u5730\u7b11\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u9a84\u50b2\u800c\u5014\u5f3a\u7684\u5c11\u5e74\uff0c\u7ec8\u4e8e\u5b8c\u5168\u88ab\u5979\u5f81\u670d\uff0c\u6b64\u540e\u5fc5\u5c06\u6c38\u8fdc\u5012\u4f0f\u5728\u5979\u811a\u4e0b\u3002 \u3000\u3000\u963f\u98de\u62b1\u8d77\u4e86\u5979\uff0c\u8f7b\u8f7b\u5c06\u5979\u653e\u5728\u5e8a\u4e0a\uff0c\u66ff\u5979\u76d6\u597d\u4e86\u88ab\uff0c\u5728\u4ed6\u4e2d\uff0c\u5979\u5df2\u662f\u7eaf\u6d01\u4e0e\u7f8e\u7684\u5316\u8eab\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8eba\u5728\u5e8a\u4e0a\uff0c\u8fd8\u5728\u5077\u5077\u5730\u7b11\u3002 \u3000\u3000\u7a81\u5ffd\u95f4\uff0c\u7a97\u5b50\u5f00\u4e86\uff0c\u51b7\u98ce\u5439\u4eba\u3002\u6797\u4ed9\u513f\u5750\u4e86\u8d77\u6765\u9053\uff1a\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u5979\u95ee\u8fd9\u53e5\u8bdd\u5c31\u7acb\u523b\u770b\u5230\u4e00\u5f20\u8138\uff0c\u8138\u4e0a\u53d1\u7740\u60e8\u7eff\u8272\u7684\u9752\u5149\uff0c\u5728\u591c\u8272\u4e2d\u770b\u6765\u5c31\u50cf\u9b3c\u9b45\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u8eba\u4e86\u4e0b\u6765\uff0c\u65e2\u6ca1\u6709\u60ca\u547c\uff0c\u4e5f\u6ca1\u6709\u88ab\u5413\u6655\uff0c\u53ea\u662f\u9759\u9759\u7684\u77a7\u7740\u8fd9\u4e2a\u4eba\uff0c\u8138\u4e0a\u751a\u81f3\u8fde \u4e00\u4e1d\u60ca\u60e7\u4e4b\u8272\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4eba\u4e5f\u5728\u77a7\u7740\u5979\uff0c\u4e00\u53cc\u773c\u775b\u5c31\u50cf\u662f\u4e24\u70b9\u9b3c\u706b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53cd\u800c\u7b11\u4e86\uff0c\u60a0\u7136\u9053\uff1a\u4f60\u65e2\u7136\u6765\u4e86\uff0c\u4e3a\u4f55\u4e0d\u8fdb\u6765\uff1f \u3000\u3000\u8bdd\u521a\u8bf4\u5b8c\uff0c\u8fd9\u4eba\u5df2\u5230\u5979\u5e8a\u524d\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6750\u9ad8\u5f97\u53ef\u6015\uff0c\u8138\u5f88\u957f\uff0c\u8116\u5b50\u4e5f\u5f88\u957f\uff0c\u8116\u5b50\u4e0a\u5374\u56f4\u7740\u4e00\u5c42\u767d\u5e03\uff0c\u4f7f\u5f97\u4ed6\u5168\u8eab\u90fd\u50f5\u786c \u8d77\u6765\uff0c\u53c8\u50cf\u4e2a\u50f5\xd7\u3002 \u3000\u3000\u8fd9\u4eba\u77aa\u7740\u773c\uff0c\u5374\u95ed\u7740\u5634\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\u674e\u5bfb\u6b22\u4f24\u4e86\u4f60\uff1f \u3000\u3000\u8fd9\u4eba\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u600e\u4e48\u77e5\u9053\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u80fd\u6740\u6b7b\u4ed6\u7684\uff0c\u8c01\u77e5\u53cd\u800c\u88ab\u4ed6\u4f24\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u8138\u4e0a\u7684\u9752\u6c14\u66f4\u76db\uff0c\u9053\uff1a\u4f60\u600e\u77e5\u6211\u8981\u6740\u4ed6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u4ed6\u6740\u4e86\u4e18\u72ec\uff0c\u4e18\u72ec\u662f\u4f60\u7684\u79c1\u751f\u5b50\uff1f \u3000\u3000\u4f0a\u54ed\u9b3c\u706b\u822c\u7684\u773c\u775b\u76ef\u7740\u5979\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u6211\u4e5f\u8ba4\u5f97\u4f60\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u54e6\uff0c\u90a3\u53ef\u771f\u662f\u8363\u5e78\u5f97\u5f88\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u4e18\u72ec\u6b7b\u7684\u65f6\u5019\uff0c\u9752\u9b54\u624b\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u75b2\u4e4f\uff1a\u7684\u786e\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u4ed6\u5c06\u9752\u9b54\u624b\u9001\u7ed9\u4e86\u4f60\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u597d\u50cf\u662f\u7684\u3002 \u3000\u3000\u4f0a\u54ed\u6012\u9053\uff1a\u4ed6\u82e5\u672a\u5c06\u9752\u9b54\u624b\u9001\u7ed9\u4f60\uff0c\u53c8\u600e\u4f1a\u6b7b\u5728\u674e\u5bfb\u6b22\u624b\u4e0b\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u5e76\u672a\u5c06\u9752\u9b54\u624b\u9001\u7ed9\u6211\uff0c\u5374\u4e5f\u4f24\u5728\u674e\u5bfb\u6b22\u624b\u4e0b\u4e86\uff0c\u662f\u4e48\uff1f \u3000\u3000\u4f0a\u54ed\u54ac\u7740\u7259\uff0c\u7a81\u7136\u4e00\u628a\u79cb\u4f4f\u4e86\u5979\u7684\u5934\u53d1\u3002 \u3000\u3000\u6797\u4ed9\u513f\u975e\u4f46\u8fd8\u662f\u4e0d\u5bb3\u6015\uff0c\u53cd\u800c\u7b11\u5f97\u66f4\u751c\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u5c31\u7b97\u4ed6\u4e3a\u6211\u800c\u6b7b\uff0c\u4e5f\u662f\u4ed6\u81ea\u5df1\u5fc3\u7518 \u60c5\u613f\u7684\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u5f88\u503c\u5f97\u3002 \u3000\u3000\u70db\u706b\u5728\u5979\u7684\u8138\u4e0a\u95ea\u52a8\u7740\uff0c\u4f0a\u54ed\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u72de\u7b11\uff0c\u9053\uff1a\u6211\u5012\u8981\u770b\u770b\u4f60\u662f\u5426\u503c\u5f97\uff1f \u3000\u3000\u4ed6\u7a81\u7136\u5c06\u5979\u8eab\u4e0a\u7684\u68c9\u88ab\u6380\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5a9a\u7b11\u9053\uff1a\u4f60\u770b\u6211\u503c\u5f97\u4e48\uff1f \u3000\u3000\u4f0a\u5668\u7a81\u7136\u53cd\u624b\u4e00\u638c\u63b4\u5728\u5979\u8138\u4e0a\uff0c\u63a5\u7740\uff0c\u5c31\u7d27\u7d27\u6293\u4f4f\u4e86\u5979\u7684\u80a9\u5934\u7528\u529b\u62e7\u7740\u5979\u7684\u8eab\u5b50\u2014\u2014\uff0d \uff0d \u3000\u3000\u4f0a\u5668\u4e00\u62f3\u6253\u5728\u5979\u5c0f\u809a\u5b50\u4e0a\uff0c\u55c4\u58f0\u9053\uff1a\u8d31\u8d27\uff0c\u539f\u6765\u4f60\u559c\u6b22\u6328\u6253\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7adf\u4e5f\u6ca1\u6709\u75db\u82e6\u4e4b\u610f\uff0c\u5374\u5145\u6ee1\u4e86\u6e34\u671b\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u4f60\u4e0d\u6015\u6211\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u4e3a\u4ec0\u4e48\u8981\u6015\u4f60\uff1f\u4f60\u867d\u7136\u4e11\u5f97\u53ef\u6015\uff0c\u4f46\u5374\u8fd8\u662f\u7537\u4eba\u3002 \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u4ee5\u53cb\u4e3a\u8363>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u4ee5\u53cb\u4e3a\u8363 \u3000\u3000\u5c4b\u5b50\u91cc\u53ea\u5269\u4e0b\u5598\u606f\u58f0\u3002 \u3000\u3000\u4f0a\u54ed\u6b63\u7ad9\u5728\u5e8a\u8fb9\u7a7f\u8863\u88f3\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u6797\u4ed9\u513f\u5ffd\u7136\u671b\u7740\u4ed6\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053\u6211\u662f\u4e0d\u662f\u503c\u5f97\u7684\u4e86\u5427\uff1f \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u6211\u771f\u8be5\u6740\u4e86\u4f60\u7684\uff0c\u5426\u5219\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u8981\u6b7b\u5728\u4f60\u624b\u4e0a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u672c\u662f\u6765\u6740\u6211\u7684\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u54fc\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5a9a\u7b11\u9053\uff1a\u4f60\u4e0b\u5f97\u4e86\u624b\uff1f \u3000\u3000\u4f0a\u54ed\u53c8\u76ef\u4e86\u5979\u534a\u664c\uff0c\u95ee\u9053\uff1a\u8ddf\u4f60\u4e00\u8d77\u6765\u7684\u90a3\u5c0f\u4f19\u5b50\u662f\u8c01\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u8981\u95ee\u4ed6\uff0c\u662f\u5403\u918b\uff1f\u8fd8\u662f\u5bb3\u6015\uff1f \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u53c8\u9053\uff1a\u4ed6\u662f\u4e2a\u4e58\u5b69\u5b50\uff0c\u4e0d\u50cf\u8fd9\u600e\u4e48\u574f\uff0c\u65e9\u5c31\u8fdc\u8fdc\u627e\u4e86\u95f4\u5c4b\u5b50\u53bb\u7761\u89c9\u4e86 \uff0c\u4ed6\u82e5\u5728\u9644\u8fd1\u80fd\u542c\u4ee5\u58f0\u97f3\u7684\u5730\u65b9\uff0c\u600e\u4f1a\u8ba9\u4f60\u5982\u6b64\u6b3a\u8d1f\u6211\u3002 \u3000\u3000\u4f0a\u54ed\u51b7\u7b11\u9053\uff1a\u4ed6\u542c\u4e0d\u5230\uff0c\u662f\u4ed6\u7684\u8fd0\u6c14\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u54e6\uff1f\u4f60\u96be\u9053\u8fd8\u60f3\u6740\u4e86\u4ed6\uff1f \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u54fc\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u4f60\u6740\u4e0d\u4e86\u4ed6\u7684\uff0c\u4ed6\u7684\u6b66\u529f\u5f88\u9ad8\uff0c\u800c\u4e14\u662f\u674e\u5bfb\u6b22\u7684\u670b\u53cb\uff0c\u6211\u4e5f\u5f88\u559c\u6b22\u4ed6\u3002 \u3000\u3000\u4f0a\u54ed\u9762\u8272\u7acb\u523b\u53d8\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u4ed6\u5c31\u4f4f\u5728\u524d\u9762\u90a3\u6392\u5c4b\u5b50\u6700\u540e\u4e00\u95f4\uff0c\u4f60\u6562\u53bb\u627e\u4ed6\u4e48\uff1f \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4f0a\u54ed\u5df2\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5979\u5403\u5403\u7684\u7b11\u9996\uff0c\u94bb\u8fdb\u4e86\u88ab\u7a9d\uff0c\u5f00\u5fc3\u5f97\u50cf\u662f\u4e00\u4e2a\u521a\u5077\u4e86\u7cd6\u5403\uff0c\u5374\u6ca1\u6709\u88ab\u5927\u4eba\u53d1\u89c9\u7684\u5b69\u5b50\u3002 \u3000\u3000\u60f3\u5230\u4f0a\u54ed\u7684\u9752\u9b54\u624b\u5c06\u963f\u98de\u5934\u9885\u51fb\u7834\u65f6\u7684\u60c5\u51b5\uff0c\u5979\u773c\u775b\u5c31\u53d1\u4e86\u5149\uff0c\u60f3\u5230\u963f\u98de\u7684\u5251\u523a\u5165\u4f0a\u54ed \u54bd\u5589\u65f6\u7684\u60c5\u51b5\uff0c\u5979\u5168\u8eab\u90fd\u5174\u594b\u5f97\u53d1\u6296\u3002 \u3000\u3000\u60f3\u7740\u60f3\u7740\uff0c\u5979\u5c45\u7136\u7761\u7740\u4e86\uff0c\u7761\u7740\u4e86\u8fd8\u662f\u5728\u7b11\uff0c\u56e0\u4e3a\u65e0\u8bba\u8c01\u6740\u6b7b\u8c01\uff0c\u5979\u90fd\u5f88\u6109\u5feb\u3002 \u3000\u3000\u4eca\u5929\u665a\u4e0a\uff0c\u5979\u5df2\u5f88\u6ee1\u8db3\u4e86\u3002 \u3000\u3000\u5e8a\u5f88\u67d4\u8f6f\uff0c\u88ab\u5355\u4e5f\u5f88\u5e72\u51c0\uff0c\u4f46\u963f\u98de\u5374\u504f\u504f\u7761\u4e0d\u7740\uff0c\u4ed6\u4ece\u672a\u5931\u7720\uff0c\u4ece\u4e0d\u77e5\u9053\u5931\u7720\u7684\u6ecb\u5473\u7adf \u5982\u6b64\u53ef\u6015\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u7ec8\u4e8e\u8ff7\u8ff7\u7cca\u7cca\u7684\u7761\u7740\u4e86\uff0c\u4f46\u7a81\u7136\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u7adf\u4ece\u5e8a\u4e0a\u8df3\u4e86 \u8d77\u6765\u3002 \u3000\u3000\u4ed6\u521a\u5c06\u5251\u63d2\u5165\u8170\u5e26\uff0c\u7a97\u5b50\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u4ed6\u770b\u5230\u4e00\u53cc\u6bd4\u9b3c\u8fd8\u53ef\u6015\u7684\u773c\u775b\u6b63\u5728\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u4f60\u548c\u6797\u4ed9\u513f\u4e00\u9f50\u6765\u7684\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u662f\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u597d\uff0c\u4f60\u51fa\u6765\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4ed6\u4e0d\u559c\u6b22\u8bf4\u8bdd\uff0c\u4ece\u6765\u4e0d\u80af\u5148\u5f00\u53e3\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u6211\u8981\u6740\u4f60\u3002 \u3000\u3000\u963f\u98de\u5374\u6de1\u6de1\u9053\uff1a\u4eca\u5929\u6211\u5374\u4e0d\u613f\u6740\u4eba\uff0c\u4f60\u8d70\u5427\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u4eca\u5929\u6211\u4e5f\u4e0d\u60f3\u6740\u4eba\uff0c\u53ea\u60f3\u6740\u4f60\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\u3002 \u3000\u3000\u4f0a\u54ed\uff1a\u4e50\u4e0d\u8be5\u548c\u6797\u4ed9\u513f\u4e00\u9f50\u6765\u7684\u3002 \u3000\u3000\u963f\u98de\u76ee\u4e2d\u7a81\u7136\u5c04\u51fa\u4e86\u5200\u4e00\u822c\u9510\u5229\u7684\u5149\uff0c\u9053\uff1a\u4f60\u82e5\u518d\u53eb\u5979\u7684\u540d\u5b57\uff0c\u6211\u53ea\u5f97\u6740\u4f60\u4e86\u3002 \u3000\u3000\u4f0a\u54ed\u72de\u7b11\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u56e0\u4e3a\u4f60\u4e0d\u914d\u3002 \u3000\u3000\u4f0a\u54ed\u683c\u683c\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u6211\u4e0d\u4f46\u8981\u53eb\u5979\u7684\u540d\u5b57\uff0c\u8fd8\u8981\u8ddf\u5979\u7761\u89c9\uff0c\u4f60\u53c8\u80fd\u600e\u6837\uff01 \u3000\u3000\u98de\u7684\u8138\u7a81\u7136\u71c3\u70e7\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u539f\u662f\u4e2a\u5f88\u51b7\u9759\u7684\u4eba\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u5982\u6b64\u6124\u6012\u8fc7\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u56e0\u6124\u6012\u800c\u53d1\u6296\u3002 \u3000\u3000\u4ed6\u72c2\u6012\u4e4b\u4e0b\uff0c\u5251\u5df2\u523a\u51fa\u3002 \u3000\u3000\u9752\u9b54\u624b\u4e5f\u5df2\u6325\u51fa\uff01 \u3000\u3000\u53ea\u542c\u53ee\u7684\u4e00\u58f0\uff0c\u5251\u5df2\u6298\u65ad\u3002 \u3000\u3000\u4f0a\u54ed\u72c2\u7b11\u9053\uff1a\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u4e5f\u914d\u548c\u6211\u52a8\u624b\uff0c\u6797\u4ed9\u513f\u8fd8\u8bf4\u4f60\u6b66\u529f\u4e0d\u9519\u3002 \u3000\u3000\u72c2\u7b11\u58f0\u4e2d\uff0c\u9752\u9b54\u624b\u5df2\u653b\u51fa\u4e86\u5341\u4f59\u62db\u3002 \u3000\u3000\u963f\u98de\u51e0\u4e4e\u8fde\u62db\u67b6\u90fd\u65e0\u6cd5\u62db\u67b6\u4e86\uff0c\u4ed6\u624b\u4e0a\u5df2\u53ea\u5269\u4e0b\u56db\u5bf8\u957f\u7684\u4e00\u622a\u65ad\u5251\uff0c\u53ea\u80fd\u4ee5\u53d8\u5316\u8fc5\u901f\u7684 \u6b65\u6cd5\u52c9\u5f3a\u95ea\u907f\u3002 \u3000\u3000\u4f0a\u54ed\u72de\u7b11\u9053\uff1a\u4f60\u82e5\u80af\u8001\u8001\u5b9e\u5b9e\u7684\u56de\u7b54\u6211\u4e24\u53e5\u8bdd\uff0c\u6211\u5c31\u9976\u4e86\u4f60\u3002 \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u9f3b\u5b50\u4e0a\u5df2\u6c81\u51fa\u4e86\u6c57\u73e0\u3002 \u3000\u3000\u4f0a\u54ed\u9053\uff1a\u6211\u95ee\u4f60\uff0c\u6797\u4ed9\u513f\u662f\u4e0d\u662f\u5e38\u5e38\u966a\u4eba\u7761\u89c9\u7684\uff0c\u5979\u548c\u4f60\u7761\u8fc7\u89c9\u6ca1\u6709\uff1f \u3000\u3000\u963f\u98de\u72c2\u543c\u4e00\u58f0\uff0c\u624b\u4e2d\u5229\u5251\u53c8\u523a\u51fa\u3002 \u3000\u3000\u4f0a\u54ed\u7684\u9752\u9b54\u624b\u5df2\u96f7\u7535\u822c\u51fb\u4e0b\uff0c\u963f\u98de\u8fde\u7ad9\u8d77\u6765\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u907f\u5f00\u51e0\u62db \uff0c\u5df2\u7d2f\u5f97\u529b\u62d9\u3002 \u3000\u3000\u4f0a\u54ed\u72de\u7b11\u9053\uff1a\u8bf4\u5440\uff0c\u8bf4\u51fa\u6211\u95ee\u4f60\u7684\u8bdd\uff0c\u6211\u5c31\u9976\u4f60\u4e0d\u6b7b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\uff0c\u6211\u8bf4\uff01 \u3000\u3000\u4f0a\u54ed\u7684\u5927\u7b11\u58f0\u521a\u53d1\u51fa\uff0c\u51fa\u624b\u7a0d\u6162\uff0c\u7a81\u6709\u5251\u5149\u4e00\u95ea\u3002 \u3000\u3000\u4f0a\u54ed\u5e73\u751f\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5feb\u7684\u5251\u5149\uff0c\u7b49\u4ed6\u770b\u5230\u8fd9\u5251\u5149\u65f6\uff0c\u5251\u5df2\u523a\u5165\u4e86\u4ed6\u7684\u54bd\u5589\uff0c\u4ed6\u5589\u5499\u91cc \u683c\u683c\u4f5c\u54cd\uff0c\u9762\u4e0a\u5145\u6ee1\u4e86\u60ca\u60e7\u548c\u6000\u7591\u4e0d\u4fe1\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u4e34\u6b7b\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e00\u5251\u662f\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u4ed6\u6b7b\u4e5f\u4e0d\u76f8\u4fe1\u8fd9\u5c11\u5e74\u80fd\u523a\u5f97\u51fa\u5982\u6b64\u5feb\u7684\u4e00\u5251\uff01 \u3000\u3000\u4f0a\u54ed\u9762\u4e0a\u6bcf\u4e00\u6839\u9aa8\u8089\u90fd\u8d77\u4e86\u75c9\u631b\u3002 \u3000\u3000\u963f\u98de\u7684\u76ee\u5149\u5982\u5bd2\u51b0\uff0c\u77aa\u7740\u4ed6\u4e00\u5b57\u5b57\u9053\uff1a\u8c01\u4fae\u8fb1\u5979\uff0c\u8c01\u5c31\u5f97\u6b7b\u3002 \u3000\u3000\u4f0a\u54ed\u7684\u5589\u5499\u91cc\u8fd8\u5728\u683c\u683c\u7684\u54cd\uff0c\u8fde\u7709\u6bdb\u548c\u773c\u775b\u4e5f\u636e\u66f2\u8d77\u6765\uff0c\u56e0\u4e3a\u4ed6\u60f3\u7b11\uff0c\u8fd8\u60f3\u544a\u8bc9\u963f\u98de\uff1a \u4f60\u8fdf\u65e9\u4e5f\u8981\u6b7b\u5728\u5979\u624b\u4e0a\u7684\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u8fd9\u53e5\u8bdd\u6c38\u8fdc\u90fd\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e00\u9192\uff0c\u5c31\u770b\u5230\u7a97\u4e0a\u6709\u4e2a\u4eba\u7684\u5f71\u5b50\uff0c\u5728\u7a97\u5916\u8d70\u6765\u8d70\u53bb\uff0c\u5979\u77e5\u9053\u8fd9\u4eba\u4e00\u5b9a\u662f\u963f\u98de\uff0c\u867d \u60f3\u8fdb\u6765\uff0c\u5374\u4e0d\u6562\u5435\u9192\u5979\u3002 \u3000\u3000\u82e5\u662f\u4f0a\u54ed\u5c31\u4e0d\u4f1a\u5728\u7a97\u5916\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u770b\u7a97\u4e0a\u7684\u4eba\u5f71\uff0c\u5fc3\u91cc\u89c9\u5f97\u6109\u5feb\u3002 \u3000\u3000\u5979\u6109\u5feb\u7684\u5411\u5728\u5e8a\u4e0a\uff0c\u8ba9\u963f\u98de\u5728\u7a97\u5916\u53c8\u7b49\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u5524\u9053\uff1a\u5916\u9762\u662f\u5c0f\u98de\u5417\uff1f \u3000\u3000\u963f\u98de\u7684\u4eba\u5f71\u505c\u5728\u7a97\u53e3\uff0c\u9053\uff1a\u662f\u6211\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4e3a\u4f55\u4e0d\u8fdb\u6765\uff1f \u3000\u3000\u963f\u98de\u8f7b\u8f7b\u4e00\u63a8\uff0c\u95e8\u5c31\u5f00\u4e86\uff0c\u76b1\u7709\u9053\uff1a\u4f60\u6ca1\u6709\u6813\u95e8\uff1f \u3000\u3000\u963f\u98de\u5ffd\u7136\u8d76\u5230\u5e8a\u524d\uff0c\u76ef\u7740\u5979\u7684\u8138\uff0c\u5979\u7684\u8138\u6709\u4e9b\u53d1\u9752\uff0c\u4e5f\u6709\u4e9b\u53d1\u80bf\uff0c\u963f\u98de\u7684\u8138\u8272\u4e5f\u53d8\u4e86\uff0c \u6025\u6025\u9053\uff1a\u4f60\u2014\u2014\u4f60\u51fa\u4e86\u4e8b\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u6211\u82e5\u6ca1\u6709\u7761\u597d\uff0c\u8138\u5c31\u4f1a\u80bf\u7684\u2014\u2014\u6628\u5929\u665a\u4e0a\u6211\u4e00\u76f4\u7ffb\u6765\u8986\u53bb\u7684\u7761\u4e0d\u7740\u2014\u2014 \u3000\u3000\u963f\u98de\u53c8\u75f4\u4e86\uff0c\u4ed6\u7684\u5fc3\u5df2\u6eb6\u5316\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u5462\uff0c\u4f60\u7761\u5f97\u597d\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u4e5f\u6ca1\u6709\u7761\u597d\uff0c\u6709\u6761\u75af\u72d7\u4e00\u76f4\u5728\u6211\u7a97\u5b50\u5916\u4e71\u53eb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7728\u4e86\u7728\u773c\u775b\uff0c\u75af\u72d7\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u55ef\uff0c\u6211\u5df2\u5bb0\u4e86\u5b83\uff0c\u5c06\u5b83\u629b\u5728\u6cb3\u91cc\u4e86\u3002 \u3000\u3000\u7a81\u542c\u5916\u9762\u4f20\u5165\u4e86\u4e00\u9635\u53ee\u5f53\u7684\u6572\u6253\u58f0\uff0c\u963f\u98de\u5c06\u7a97\u5b50\u5f00\u4e00\u4e9b\uff0c\u5c31\u770b\u5230\u5e97\u4f19\u6b63\u5728\u9662\u5b50\u91cc\u6572\u7740\u6c34 \u58f6\uff0c\u5927\u58f0\u9053\uff1a\u5404\u4f4d\u5ba2\u5b98\u4eec\uff0c\u4f60\u4eec\u53ef\u60f3\u77e5\u9053\u6c5f\u6e56\u4e2d\u6700\u8f70\u52a8\u7684\u6d88\u606f\uff0c\u6b66\u6797\u4e2d\u6700\u8fd1\u53d1\u751f\u7684\u5927\u4e8b\u4e48\uff1f \u4fdd\u8bc1\u65e2\u65b0\u9c9c\uff0c\u53c8\u7d27\u5f20\uff0c\u5404\u4f4d\u8fd8\u53ef\u4ee5\u4e00\u8fb9\u5403\u7740\u996d\u559d\u7740\u9152\u3002 \u3000\u3000\u963f\u98de\u653e\u4e0b\u7a97\u5b50\uff0c\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4e0d\u60f3\u53bb\u542c\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u60f3\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u5012\u60f3\u53bb\u542c\u542c\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u603b\u662f\u8981\u5403\u996d\u7684\u3002 \u3000\u3000\u963f\u98de\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u770b\u6765\u8fd9\u4f19\u8ba1\u62c9\u751f\u610f\u7684\u6cd5\u5b50\u5012\u771f\u7528\u5bf9\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6380\u5f00\u68c9\u88ab\uff0c\u60f3\u5750\u8d77\u6765\uff0c\u7a81\u53c8\u5624\u549b\u4e00\u58f0\uff0c\u7f29\u4e86\u56de\u53bb\uff0c\u7ea2\u7740\u8138\u9053\uff1a\u4f60\u8fd8\u4e0d\u5feb\u628a\u8863\u670d\u62ff \u7ed9\u6211\u3002 \u3000\u3000\u963f\u98de\u7684\u8138\u4e5f\u7ea2\u4e86\uff0c\u4e00\u9897\u5fc3\u7830\u7830\u7684\u8df3\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u996d\u5385\u91cc\u5df2\u5feb\u5750\u6ee1\u4e86\uff0c\u6c5f\u6e56\u4e2d\u7684\u4e8b\u6c38\u8fdc\u5145\u6ee1\u4e86\u523a\u6fc0\uff0c\u65e0\u8bba\u8c01\u90fd\u60f3\u542c\u542c\u7684\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u591a\u5c11 \u603b\u6709\u4e9b\u79ef\u90c1\u3002 \u3000\u3000\u542c\u7740\u8fd9\u4e9b\u6c5f\u6e56\u8c6a\u4fa0\uff0c\u6b66\u6797\u5947\u4fa0\u7684\u6545\u4e8b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u4f1a\u5c06\u81ea\u5df1\u548c\u6545\u4e8b\u7684\u4eba\u7269\u6eb6\u4e3a\u4e00\u4f53\uff0c\u5fc3 \u5934\u7684\u79ef\u90c1\u4e5f\u5c31\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u53d1\u6cc4\u4e86\u3002 \u3000\u3000\u9760\u7a97\u7684\u684c\u5b50\u4e0a\uff0c\u5750\u7740\u4e2a\u7a7f\u7740\u84dd\u5e03\u957f\u886b\u7684\u8001\u8005\uff0c\u6b63\u95ed\u7740\u773c\u775b\u5728\u90a3\u91cc\u62bd\u65f1\u70df\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u8fb9\u6709\u4e2a\u5f88\u5e74\u8f7b\u7684\u5927\u59d1\u5a18\uff0c\u68b3\u7740\u4e24\u6761\u5927\u8fab\u5b50\uff0c\u4e00\u53cc\u5927\u773c\u775b\u53c8\u9ed1\u53c8\u4eae\uff0c\u773c\u6ce2\u4e00\u8f6c\uff0c\u5c31 \u4eff\u4f5b\u53ef\u4ee5\u52fe\u53bb\u7537\u4eba\u7684\u9b42\u9b44\u3002 \u3000\u3000\u963f\u98de\u548c\u6797\u4ed9\u513f\u4e00\u8d70\u8fdb\u6765\uff0c\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u53d1\u4e86\u76f4\uff0c\u8fd9\u4f4d\u8fab\u5b50\u59d1\u5a18\u7684\u5927\u773c\u775b\u6b63\u4e0d\u505c\u7684\u5728\u4ed6 \u4eec\u8eab\u4e0a\u8f6c\u3002 \u3000\u3000\u6797\u4e2a\u513f\u4e5f\u76ef\u7740\u8fd9\u5927\u59d1\u5a18\uff0c\u5ffd\u7136\u62bf\u5634\u4e00\u7b11\uff0c\u6084\u6084\u9053\uff1a\u4f60\u770b\u5979\u90a3\u53cc\u773c\u775b\uff0c\u6211\u5012\u771f\u5f97\u5c0f\u5fc3\u70b9\uff0c \u83ab\u8ba9\u5979\u628a\u4f60\u52fe\u4e86\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u521a\u8981\u4e86\u51e0\u6837\u83dc\u548c\u4e24\u5f20\u997c\uff0c\u90a3\u8001\u4eba\u5c31\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u9053\uff1a\u7ea2\u513f\uff0c\u65f6\u5019\u5230\u4e86\u4e48\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u662f\u65f6\u5019\u4e86\u3002 \u3000\u3000\u8001\u4eba\u8fd9\u624d\u5f20\u5f00\u773c\u6765\uff0c\u4ed6\u7684\u4eba\u867d\u7136\u53c8\u8001\u53c8\u5e72\uff0c\u4f46\u4e00\u53cc\u773c\u775b\u5374\u5f88\u5e74\u8f7b\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u6bcf\u4e2a\u4eba\u90fd \u89c9\u5f97\u4ed6\u773c\u775b\u6b63\u5728\u77aa\u7740\u81ea\u5df1\u3002 \u3000\u3000\u90a3\u8001\u4eba\u5439\u7740\u7897\u91cc\u7684\u8336\u53f6\uff0c\u559d\u4e86\u51e0\u53e3\u8336\uff0c\u5ffd\u7136\u9053\uff1a\u6885\u82b1\u76d7\u65e0\u6076\u4e0d\u4f5c\uff0c\u63a2\u82b1\u90ce\u4ed7\u4e49\u758f\u8d22\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u53c8\u4e00\u626b\uff0c\u9053\uff1a\u5404\u4f4d\u53ef\u77e5\u9053\u6211\u8bf4\u7684\u8fd9\u4e24\u4eba\u662f\u8c01\u4e48\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u8fd9\u4e24\u4eba\u662f\u8c01\u5440\uff1f\u597d\u50cf\u6ca1\u6709\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u4e86\u7b11\u9053\uff1a\u90a3\u4f60\u5c31\u771f\u662f\u5b64\u964b\xd7\u95fb\u4e86\uff0c\u63d0\u8d77\u8fd9\u4e24\u4eba\uff0c\u5f53\u771f\u662f\u5927\u5927\u6709\u540d\uff0c\u6885\u82b1\u76d7\u6570 \u5341\u5e74\uff0c\u53ea\u51fa\u73b0\u8fc7\u4e24\u6b21\uff0c\u4f46\u4e24\u6cb3\u7eff\u6797\u9053\u4e2d\uff0c\u5343\u5343\u767e\u767e\u6761\u597d\u6c49\u6240\u505a\u7684\u6848\u5b50\uff0c\u52a0\u8d77\u6765\u4e5f\u6ca1\u6709\u4ed6\u4e00\u4e2a \u4eba\u591a\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\uff0c\u61a8\u7b11\u9053\uff1a\u597d\u5389\u5bb3\u2014\u2014\u4f46\u90a3\u4f4d\u63a2\u82b1\u90ce\u53c8\u662f\u8c01\u5462\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u6b64\u4eba\u4e43\u662f\u4f4d\u4e16\u5bb6\u516c\u5b50\uff0c\u5386\u4ee3\u7f28\u9f0e\uff0c\u53ef\u8bf4\u662f\u663e\u8d6b\u5df2\u6781\uff0c\u4e09\u4ee3\u4e2d\u5c31\u4e2d\u8fc7\u4e03\u6b21\u8fdb \u58eb\uff0c\u53ea\u53ef\u60dc\u6ca1\u4e2d\u8fc7\u72b6\u5143\uff0c\u5230\u4e86\u674e\u63a2\u82b1\u8fd9\u4e00\u4ee3\uff0c\u819d\u4e0b\u4e24\u4f4d\u5c11\u7237\u66f4\u662f\u5929\u8d44\u7edd\u9876\uff0c\u624d\u6c14\u7eb5\u6a2a\uff0c\u4ed6\u8001 \u4eba\u5bb6\u5c06\u5e0c\u671b\u5168\u90e8\u5bc4\u6258\u5728\u4e24\u4f4d\u516c\u5b50\u8eab\u4e0a\uff0c\u53ea\u671b\u4ed6\u4eec\u80fd\u4e2d\u4e2a\u72b6\u5143\uff0c\u6765\u5f25\u8865\u81ea\u5df1\u7684\u7f3a\u9677\u2014\u2014 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7b11\u9053\uff1a\u63a2\u82b1\u5c31\u5df2\u7ecf\u4e0d\u9519\u4e86\uff0c\u4e3a\u4f55\u4e00\u5b9a\u8981\u4e2d\u72b6\u5143\u5462\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u8c01\u77e5\u5927\u674e\u4e00\u8003\uff0c\u53c8\u662f\u4e2a\u63a2\u82b1\uff0c\u4eba\u90fd\u95f7\u95f7\u4e0d\u6b22\uff0c\u53ea\u671b\u5c0f\u674e\u516c\u5b50\u80fd\u4e89\u6c14\uff0c\u8c01\u77e5 \u547d\u4e0d\u7531\u4eba\uff0c\u4e00\u8003\u4e4b\u4e0b\u53c8\u662f\u4e2a\u63a2\u82b1\u3002\u8001\u63a2\u82b1\u5931\u671b\u4e4b\u4e0b\uff0c\u6ca1\u8fc7\u4e24\u5e74\u5c31\u53bb\u4e16\u4e86\uff0c\u63a5\u7740\uff0c\u5927\u674e\u63a2\u82b1\u4e5f \u5f97\u4e86\u4e0d\u6cbb\u4e4b\u75c7\uff0c\u8fd9\u4f4d\u5c0f\u674e\u63a2\u82b1\u5fc3\u7070\u610f\u51b7\uff0c\u7d22\u6027\u8f9e\u53bb\u4e86\u5b98\u804c\uff0c\u5728\u5bb6\u91cc\u758f\u8d22\u7ed3\u5ba2\uff0c\u4ed6\u7684\u6177\u6168\u4e0e\u8c6a \u723d\uff0c\u5c31\u7b97\u5b5f\u5c1d\u590d\u751f\u4fe1\u9675\u518d\u4e16\uff0c\u53ea\u6015\u4e5f\u6bd4\u4e0d\u4e0a\u4ed6\u3002 \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u53c8\u559d\u4e86\u51e0\u53e3\u8336\u3002 \u3000\u3000\u963f\u98de\u65e9\u542c\u5f97\u5174\u594b\u5df2\u6781\uff0c\u6709\u4eba\u5728\u5938\u8d5e\u674e\u5bfb\u6b22\uff0c\u4ed6\u542c\u4e86\u771f\u6bd4\u5938\u5956\u81ea\u5df1\u8fd8\u8981\u9ad8\u5174\u3002\u53ea\u542c\u8001\u8005\u63a5 \u7740\u9053\uff1a\u8fd9\u4f4d\u63a2\u82b1\u90ce\u4e0d\u4f46\u624d\u9ad8\u516b\u6597\uff0c\u800c\u4e14\u8fd8\u662f\u6587\u6b66\u5168\u624d\uff0c\u5e7c\u5e74\u5c31\u7ecf\u5f02\u4eba\u4f20\u6388\u4ed6\u4e00\u8eab\u60ca\u4e16\u9a87\u4fd7\u7684 \u7edd\u9876\u529f\u592b\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u7237\u7237\u4eca\u5929\u8981\u8bf4\u7684\uff0c\u5c31\u662f\u4ed6\u4eec\u4e24\u4eba\u7684\u6545\u4e8b\u4e48\uff1f \u3000\u3000\u8001\u8005\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7b11\u9053\uff1a\u90a3\u4e00\u5b9a\u597d\u542c\u6781\u4e86\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014\u53ea\u4e0d\u8fc7\u5802\u5802\u7684\u63a2\u82b1\u90ce\uff0c\u53c8\u600e\u4f1a\u548c\u58f0\u540d\u72fc\u7c4d \u7684\u6885\u82b1\u76d7\u7275\u6d89\u5230\u4e00\u9f50\u4e86\u5462\uff1f \u3000\u3000\u8001\u8005\u9053\uff1a\u8fd9\u5176\u4e2d\u81ea\u6709\u9053\u7406\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4ec0\u4e48\u9053\u7406\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u53ea\u56e0\u6885\u82b1\u76d7\u5c31\u662f\u63a2\u82b1\u90ce\uff0c\u63a2\u82b1\u90ce\u5c31\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u963f\u98de\u53ea\u89c9\u4e00\u9635\u6012\u6c14\u4e0a\u6d8c\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u8981\u53d1\u4f5c\uff0c\u8fab\u5b50\u59d1\u5a18\u5374\u5df2\u6447\u5934\u9053\uff1a\u8fd9\u4f4d\u63a2\u82b1\u65e2\u4e0d\u6563\u5c3d\u4e07 \u91d1\u5bb6\u8d22\uff0c\u60f3\u5fc5\u662f\u4e2a\u89c6\u91d1\u94b1\u5982\u7caa\u571f\u7684\u4eba\uff0c\u53c8\u600e\u4f1a\u5ffd\u7136\u53d8\u6210\u4e86\u6253\u5bb6\u52ab\u820d\u3001\u8d2a\u8d22\u597d\u8272\u7684\u6885\u82b1\u76d7\uff1f\u6211 \u4e0d\u4fe1\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u83ab\u8bf4\u4f60\u4e0d\u4fe1\uff0c\u6211\u4e5f\u4e0d\u4fe1\uff0c\u6240\u4ee5\u7279\u5730\u53bb\u6253\u542c\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4f60\u8001\u4eba\u5bb6\u60f3\u5fc5\u4e00\u5b9a\u6253\u542c\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u81ea\u7136\u6253\u542c\u51fa\u6765\u4e86\uff0c\u8fd9\u5176\u4e2d\u7684\u8be6\u60c5\uff0c\u5b9e\u5728\u662f\u66f2\u6298\u590d\u6742\uff0c\u8be1\u8ba1\u79bb\u5947\uff0c\u800c\u4e14\u7d27\u5f20 \u523a\u6fc0\uff0c\u7cbe\u91c7\u7edd\u4f26\u2014\u2014\u8bf4\u5230\u8fd9\u91cc\u4ed6\u5ffd\u7136\u505c\u4f4f\uff0c\u53c8\u95ed\u4e0a\u773c\u775b\u6253\u8d77\u778c\u7761\u6765\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u4f3c\u4e4e\u5f88\u7740\u6025\uff0c\u8fde\u8fde\u9053\uff1a\u4f60\u8001\u4eba\u5bb6\u600e\u4e48\u4e0d\u8bf4\u4e86\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u62bd\u4e86\u53e3\u65f1\u70df\uff0c\u53c8\u5c06\u70df\u6162\u6162\u7684\u5f80\u9f3b\u5b54\u91cc\u55b7\u51fa\u6765\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\xd7\u7740\u5634\uff0c\u9053\uff1a\u521a\u8bf4\u5230\u597d\u542c\u7684\u5730\u65b9\uff0c\u5c31\u4e0d\u8bf4\u4e86\uff0c\u5c82\u975e\u662f\u540a\u4eba\u7684\u80c3\u53e3\u3002 \u3000\u3000\u5979\u5ffd\u7136\u4e00\u62cd\u5df4\u638c\uff0c\u7b11\u9053\uff1a\u6211\u660e\u767d\u4e86\uff0c\u4f60\u8001\u4eba\u5bb6\u539f\u6765\u662f\u60f3\u559d\u9152\u3002 \u3000\u3000\u8fd9\u4e0b\u5b50\u4e0d\u4f46\u5979\u660e\u767d\uff0c\u522b\u4eba\u4e5f\u90fd\u660e\u767d\u4e86\uff0c\u7eb7\u7eb7\u7b11\u7740\u638f\u8170\u5305\uff0c\u6478\u94f6\u5b50\uff0c\u90a3\u5e97\u4f19\u65e9\u62ff\u7740\u4e2a\u76d8\u5b50 \u5728\u65c1\u8fb9\u7b49\u7740\u6536\u94b1\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u8fd9\u624d\u6253\u4e86\u54c8\u6b20\uff0c\u63a5\u7740\u8bf4\u4e0b\u53bb\u9053\uff1a\u4e8b\u60c5\u5f00\u59cb\uff0c\u662f\u53d1\u751f\u5728\u5174\u4e91\u5e84\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u5174\u4e91\u5e84\uff1f\u90a3\u83ab\u975e\u662f\u9f99\u56db\u7237\u4f4f\u7684\u5730\u65b9\u4e48\uff1f\u90a3\u53ef\u662f\u4e2a\u597d\u5730\u65b9\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u4e0d\u9519\uff0c\u4f46\u8fd9\u597d\u5730\u65b9\u5374\u672c\u662f\u674e\u5bfb\u6b22\u9001\u7ed9\u4ed6\u7684\uff0c\u987a\u56e0\u8fd9\u4e24\u4eba\u4e43\u662f\u751f\u6b7b\u516b\xd7\u4e4b\u4ea4 \uff0c\u800c\u4e14\u9f99\u592b\u4eba\u8fd8\u662f\u674e\u63a2\u82b1\u7684\u59d1\u8868\u7684\u4e4b\u4eb2\u2014\u2014\uff0d \u3000\u3000\u8fd9\u7956\u5b59\u4e24\u4e00\u642d\u4e00\u6863\uff0c\u5c45\u7136\u5c06\u524d\u4e9b\u5929\u5728\u5174\u4e91\u5e84\u53d1\u751f\u7684\u4e8b\u60c5\u8bf4\u5f97\u516b\u4e5d\u4e0d\u79bb\u5341\uff0c\u8bf4\u5230\u674e\u5bfb\u6b22\u5982 \u4f55\u8bef\u4f24\u9f99\u5c0f\u4e91\uff0c\u5982\u4f55\u4e2d\u4f0f\u88ab\u64d2\uff0c\u5927\u5bb6\u90fd\u4e0d\u7981\u627c\u8155\u53f9\u606f\uff0c\u8bf4\u5230\u6797\u4ed9\u513f\u5982\u4f55\u4e2d\u591c\u88ab\u52ab\uff0c\u5c11\u5e74\u963f\u98de \u7684\u5251\u5982\u4f55\u5feb\uff0c\u5982\u4f55\u51fa\u624b\u6551\u4e86\u5979\u65f6\uff0c\u5b59\u8001\u5148\u751f\u4e00\u53cc\u70af\u70af\u6709\u5149\u7684\u773c\u775b\uff0c\u4e5f\u4e0d\u77e5\u662f\u6709\u610f\uff0c\u8fd8\u662f\u65e0\u610f \u7684\uff0c\u7adf\u4e00\u76f4\u671b\u7740\u963f\u98de\u548c\u6797\u4ed9\u513f\uff0c\u8fab\u5b50\u59d1\u5a18\u7684\u4e00\u53cc\u5927\u773c\u775b\uff0c\u4e5f\u4e0d\u4f4f\u5f80\u4ed6\u4eec\u8fd9\u8fb9\u779f\u3002 \u3000\u3000\u963f\u98de\u9762\u4e0a\u867d\u4e0d\u52a8\u58f0\u8272\uff0c\u5fc3\u91cc\u5374\u5728\u6697\u601d\u7591\uff1a\u4ed6\u83ab\u975e\u65e9\u5df2\u77e5\u9053\u6211\u4eec\u662f\u8c01\uff1f\u8fd9\u6545\u4e8b\u83ab\u975e\u5c31\u662f\u8bb2 \u7ed9\u6211\u4eec\u542c\u7684\uff1f \u3000\u3000\u53ea\u542c\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u6885\u82b1\u76d7\u83ab\u975e\u5df2\u6b7b\u5728\u90a3\u4f4d\u98de\u5251\u5ba2\u624b\u4e0a\u4e48\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u4f46\u8d75\u5927\u7237\u3001\u7530\u4e03\u7237\uff0c\u5374\u8ba4\u4e3a\u4ed6\u6740\u7684\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u674e\u5bfb\u6b22\u624d\u662f\u771f\u7684\u6885\u82b1\u76d7\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u90a3\u4e48\u7a76\u7adf\u8c01\u624d\u662f\u771f\u7684\u6885\u82b1\u76d7\u5462\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u9053\uff1a\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u771f\u7684\u6885\u82b1\u76d7\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u54ea\u4e2a\u662f\u771f\uff1f\u54ea\u4e2a\u662f\u5047\uff0c\u4f46\u8d75\u5927\u7237 \u3001\u7530\u5927\u7237\u8eab\u4efd\u4e0d\u540c\uff0c\u4e00\u8a00\u4e5d\u9f0e\uff0c\u4ed6\u4eec\u8001\u8bf4\u674e\u5bfb\u6b22\u662f\u6885\u82b1\u76d7\uff0c\u90a3\u522b\u4eba\u4e5f\u53ea\u597d\u8bf4\u674e\u5bfb\u6b22\u662f\u6885\u82b1\u76d7 \u4e86\uff0c\u4e8e\u662f\u5fc3\u7709\u5927\u5e08\u5c31\u8981\u5c06\u4ed6\u62bc\u56de\u5c11\u6797\u5bfa\u3002 \u3000\u3000\u4ed6\u53c8\u62bd\u4e86\u53e3\u70df\uff0c\u5f90\u5f90\u63a5\u7740\u9053\uff1a\u8c01\u77e5\u5230\u5c11\u6797\u5bfa\u65f6\uff0c\u5374\u53d8\u6210\u662f\u674e\u63a2\u82b1\u5c06\u6536\u7709\u5927\u5e08\u9001\u56de\u53bb\u7684\u4e86 \u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u8fde\u6797\u4ed9\u513f\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u963f\u98de\u66f4\u662f\u5927\u89c9\u610f\u5916\uff0c\u4e24\u4eba\u90fd\u731c\u4e0d\u51fa\u8def\u4e0a\u53d1\u751f\u4ec0\u4e48 \u4e8b\uff1f \u3000\u3000\u5e78\u597d\u8fab\u5b50\u59d1\u5a18\u5df2\u66ff\u4ed6\u4eec\u95ee\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u539f\u6765\u62bc\u9001\u4ed6\u7684\u5fc3\u7709\u5927\u5e08\u3001\u7530\u4e03\u548c\u56db\u4f4d\u5c11\u6797\u5f1f\u5b50\u90fd\u5728\u8def\u4e0a\u906d\u4e86\u82d7\u7586\u6781\u4e50\u5cd2\u4e3b \u7684\u6bd2\u624b\uff0c\u5fc3\u7709\u5927\u5e08\u4e2d\u6bd2\u540e\u624d\u91ca\u653e\u4e86\u674e\u5bfb\u6b22\uff0c\u674e\u5bfb\u6b22\u89c1\u4ed6\u4e2d\u6bd2\u5df2\u6df1\uff0c\u53ea\u6709\u5c11\u6797\u5bfa\u4e2d\u8fd8\u53ef\u80fd\u6709\u89e3 \u836f\uff0c\u662f\u4ee5\u5c31\u5c06\u4ed6\u9001\u56de\u53bb\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u4e00\u6311\u5927\u59c6\u6307\uff0c\u8d5e\u9053\uff1a\u8fd9\u4f4d\u674e\u63a2\u82b1\u53ef\u771f\u662f\u4f4d\u5927\u82f1\u96c4\uff0c\u5927\u8c6a\u4fa0\uff0c\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u5728 \u8fd9\u79cd\u60c5\u51b5\u4e0b\u65e9\u5df2\u4e0d\u613f\u800c\u53bb\u4e86\uff0c\u600e\u80af\u6551\u4ed6\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u8bdd\u867d\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u5c11\u6797\u50e7\u4eba\u4eec\u975e\u4f46\u4e0d\u611f\u6fc0\u4ed6\uff0c\u8fd8\u8981\u6740\u4ed6\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u8bb6\u7136\u9053\uff1b\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u56e0\u4e3a\u8fd9\u4e9b\u8bdd\u90fd\u662f\u674e\u63a2\u82b1\u81ea\u5df1\u8bf4\u51fa\u6765\u7684\uff0c\u5c11\u6797\u50e7\u4eba\u4eec\u5bf9\u4ed6\u8bf4\u7684\u8bdd\uff0c\u8fde\u4e00\u4e2a\u5b57 \u90fd\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u5fc3\u7709\u5927\u5e08\u603b\u8be5\u4e3a\u4ed6\u8bc1\u5b9e\u624d\u662f\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u9053\uff1a\u53ea\u53ef\u60dc\u5fc3\u7709\u5927\u5e08\u4e00\u56de\u5230\u5c11\u6797\u5bfa\u540e\uff0c\u5c31\u5df2\u5706\u5bc2\u4e86\uff0c\u9664\u4e86\u5fc3\u7709\u5927\u5e08\u5916\uff0c\u4e16\u4e0a \u518d\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u771f\u76f8\uff01 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u56db\u5ea7\u90fd\u4e0d\u7981\u53d1\u51fa\u4e86\u53f9\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u963f\u98de\u7684\u80f8\u819b\u66f4\u4f3c\u5df2\u7206\u88c2\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u90a3\u4f4d\u674e\u63a2\u82b1\u83ab\u975e\u5df2\u906d\u4e86\u5c11\u6797\u5bfa\u7684\u6bd2\u624b\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u76ee\u4e2d\u4f3c\u6709\u7b11\u610f\uff0c\u7f13\u7f13\u9053\uff1a\u5c11\u6797\u5bfa\u867d\u7136\u9886\u8896\u6b66\u6797\uff0c\u95e8\u4e0b\u5f1f\u5b50\u66f4\u65e0\u4e00\u4e0d\u662f\u7edd\u9876\u9ad8\u624b \uff0c\u4f46\u82e5\u60f3\u6740\u6b7b\u674e\u63a2\u82b1\uff0c\u5374\u5df2\u975e\u6613\u4e8b\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u4e5f\u779f\u4e86\u98de\u4e00\u773c\u9053\uff1a\u4f46\u53cc\u62f3\u96be\u5bf9\u56db\u624b\uff0c\u597d\u6c49\u67b6\u4e0d\u4f4f\u4eba\u591a\uff0c\u674e\u63a2\u82b1\u5c31\u7b97\u5929\u4e0b\u65e0\u654c\uff0c \u53c8\u600e\u80fd\u6321\u5f97\u5c11\u6797\u7684\u516b\u767e\u5f1f\u5b50\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u5c11\u6797\u5bfa\u7eb5\u6709\u516b\u767e\u5f1f\u5b50\uff0c\u65e0\u654c\u597d\u624b\uff0c\u5374\u53c8\u6709\u8c01\u6562\u62a2\u5148\u51fa\u624b\uff1f\u53c8\u6709\u8c01\u6562\u53bb\u63a5\u5c0f \u674e\u63a2\u82b1\u7684\u7b2c\u4e00\u5200\uff1f\uff01 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u62cd\u624b\u9053\uff1a\u4e0d\u9519\uff0c\u5c0f\u674e\u795e\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u5c11\u6797\u5bfa\u7eb5\u6709\u516b\u767e\u5f1f\u5b50\uff0c \u4e5f\u4e00\u5b9a\u4f24\u4e0d\u4e86\u4ed6\u7684\uff0c\u4ed6\u73b0\u5728\u53ea\u6015\u65e9\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u4ed6\u4e5f\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u6014\u4e86\u6014\uff0c\u9053\uff1a\u4e3a\u4ec0\u4e48\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u5c11\u6797\u5f1f\u5b50\u867d\u7136\u65e0\u6cd5\u4f24\u4ed6\uff0c\u4f46\u4ed6\u4e5f\u65e0\u6cd5\u6740\u51fa\u5c11\u6797\u5f1f\u5b50\u7684\u4e4c\u9ed1\uff0c\u6b64\u523b\u662f\u975e\u672a\u660e \uff0c\u771f\u76f8\u672a\u767d\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u8d70\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4ed6\u65e2\u4e0d\u80fd\u8d70\uff0c\u4e5f\u4e0d\u80fd\u6253\uff0c\u90a3\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u5b59\u5148\u751f\u9053\uff1a\u4ed6\u8eab\u5728\u516b\u767e\u5f1f\u5b50\u7684\u5305\u56f4\u4e4b\u4e2d\uff0c\u98de\u5200\u82e5\u4e00\u51fa\u624b\uff0c\u5c31\u5fc5\u6b7b\u65e0\u7591\uff0c\u53ea\u56e0\u5c11\u6797\u5f1f\u5b50\u6015 \u7684\u5c31\u662f\u4ed6\u624b\u4e2d\u4e4b\u5200\uff0c\u800c\u4ed6\u7684\u98de\u5200\u518d\u5f3a\uff0c\u5374\u4e5f\u6740\u4e0d\u5c3d\u516b\u767e\u5f1f\u5b50\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4f46\u8fd9\u6837\u8017\u4e0b\u53bb\u4e5f\u4e0d\u884c\u5440\uff01\u4e00\u4e2a\u4eba\u603b\u6709\u652f\u6301\u4e0d\u4f4f\u7684\u65f6\u5019\u3002 \u3000\u3000\u8fd9\u4e5f\u6b63\u662f\u963f\u98de\u5fc3\u91cc\u7126\u8651\u4e4b\u5904\uff0c\u4ed6\u81ea\u5df1\u662f\u7f6e\u8eab\u5728\u674e\u5bfb\u6b22\u540c\u6837\u7684\u60c5\u51b5\u4e2d\uff0c\u5b9e\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d \u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u5f53\u65f6\u4ed6\u4eec\u8bf4\u8bdd\u4e4b\u5904\u5c31\u662f\u5fc3\u7709\u5927\u5e08\u5706\u5bc2\u7684\u623f\u5916\uff0c\u53cc\u65b9\u8bf4\u50f5\u4e86\uff0c\u674e\u63a2\u82b1\u5c31\u4e58\u673a \u8fdb\u5165\u4e86\u90a3\u7985\u623f\u4e2d\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u5931\u58f0\u9053\uff1a\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u5c82\u975e\u81ea\u5df1\u5c06\u81ea\u5df1\u56f0\u6b7b\u4e86\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u5c11\u6797\u5f1f\u5b50\u6b63\u4e5f\u56e0\u4e3a\u672a\u60f3\u5230\u4ed6\u4e0d\u5411\u5916\u9762\u51b2\uff0c\u53cd\u800c\u81ea\u5165\u7edd\u8def\uff0c\u6240\u4ee5\u624d\u4f1a\u88ab\u4ed6\u51b2 \u5165\u7985\u623f\u53bb\uff0c\u540e\u6094\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u540e\u6094\uff1f\u674e\u5bfb\u6b22\u65e2\u5df2\u81ea\u5165\u7edd\u8def\uff0c\u4ed6\u4eec\u4e3a\u4f55\u8fd8\u8981\u540e\u6094\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u63a5\u9053\uff1a\u7985\u623f\u4e2d\u4e0d\u4f46\u6709\u5fc3\u7709\u7684\u9057\u8715\uff0c\u8fd8\u6709\u4e00\u90e8\u5c11\u6797\u5bfa\u73cd\u85cf\u7684\u7ecf\u5178\uff0c\u4ed6\u4eec\u6295\u9f20\u5fcc\u5668 \uff0c\u66f4\u4e0d\u6562\u8fdb\u53bb\u52a8\u624b\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4f46\u4ed6\u4eec\u8001\u5728\u5916\u9762\u5c06\u8fd9\u7985\u623f\u56f4\u4f4f\uff0c\u7528\u4e0d\u4e86\u51e0\u5929\uff0c\u674e\u63a2\u82b1\u5c82\u975e\u5c31\u8981\u88ab\u997f\u6b7b\uff0c\u6e34 \u6b7b\u4e86\uff01 \u3000\u3000\u5b59\u5148\u751f\u9053\uff1a\u5c11\u6797\u5f1f\u5b50\u60f3\u5fc5\u4e5f\u662f\u6253\u7684\u8fd9\u4e2a\u4e3b\u610f\uff0c\u600e\u5948\u4ed6\u4eec\u7684\u4e94\u5e08\u53d4\u5fc3\u6811\u8fd8\u5728\u90a3\u7985\u623f\uff0c\u800c\u4e14 \u53c8\u88ab\u674e\u63a2\u82b1\u5236\u4f4f\uff0c\u4ed6\u4eec\u96be\u9053\u80fd\u5c06\u4ed6\u4eec\u7684\u4e94\u5e08\u53d4\u4e5f\u4e00\u9f50\u997f\u6b7b\u4e48\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u5f53\u7136\u4e0d\u80fd\u3002 \u3000\u3000\u5b59\u5148\u751f\u9053\uff1a\u6240\u4ee5\u4ed6\u4eec\u53ea\u6709\u5c06\u98df\u7269\u548c\u6c34\u9001\u8fdb\u53bb\uff0c\u5fc3\u6811\u997f\u4e0d\u6b7b\uff0c\u674e\u63a2\u82b1\u81ea\u7136\u4e5f\u997f\u4e0d\u6b7b\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u62cd\u624b\u7b11\u9053\uff1a\u5c11\u6797\u5bfa\u53f7\u79f0\u6b66\u6797\u5723\u5730\uff0c\u6570\u767e\u5e74\u6765\uff0c\u8c01\u4e5f\u4e0d\u6562\u5984\u8d8a\u96f7\u6c60\u4e00\u6b65\uff0c\u4f46\u674e\u63a2 \u82b1\u5355\u67aa\u5339\u9a6c\u4e00\u4e2a\u4eba\uff0c\u5c31\u5c06\u5c11\u6797\u5bfa\xd7\u5f97\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u5c11\u6797\u516b\u767e\u5f1f\u5b50\u975e\u4f46\u62ff\u4ed6\u65e0\u53ef\u5948\u4f55\uff0c\u8fd8\u5f97\u6bcf\u5929 \u8bf7\u4ed6\u5403\u559d\uff0c\u8fd8\u751f\u6015\u9001\u53bb\u7684\u4e1c\u897f\u4e0d\u4e2d\u4ed6\u7684\u610f\u2014\u2014\uff0d \u3000\u3000\u5979\u5403\u5403\u7b11\u9053\uff1a\u8fd9\u4f4d\u674e\u63a2\u82b1\u53ef\u771f\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u8fd9\u6545\u4e8b\u597d\u542c\u6781\u4e86\u3002 \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u98de\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u80fd\u81ea\u4e3b\uff0c\u53ea\u6068\u4e0d\u5f97\u80fd\u8df3\u8d77\u6765\u544a\u8bc9\u522b\u4eba\uff1a\u674e\u5bfb\u6b22\u5c31\u662f\u6211\u7684\u670b \u53cb\uff0c\u597d\u670b\u53cb\u2014\u2014 \u3000\u3000\u65e0\u8bba\u8c01\u6709\u674e\u5bfb\u6b22\u8fd9\u79cd\u670b\u53cb\uff0c\u90fd\u503c\u5f97\u9a84\u50b2\u7684\u3002 \u3000\u3000\u4f46\u90a3\u5b59\u8001\u5148\u751f\u5374\u53c8\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u4e0d\u9519\uff0c\u674e\u63a2\u82b1\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u82f1\u96c4\u8c6a\u4fa0\uff0c\u53ea\u53ef\u60dc\u8fd9 \u4f4d\u5927\u82f1\u96c4\u8fdf\u65e9\u8fd8\u662f\u514d\u4e0d\u4e86\u8981\u57cb\u9aa8\u5c11\u6797\u5bfa\u7684\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u6709\u610f\u65e0\u610f\u95f4\u53c8\u779f\u4e86\u963f\u98de\u4e00\u773c\uff0c\u9053\uff1a\u9664\u975e\u6709\u4eba\u80fd\u8bc1\u660e\u674e\u5bfb\u6b22\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u80fd\u8bc1\u660e \u5fc3\u7709\u5927\u5e08\u7684\u786e\u662f\u88ab\u4e94\u6bd2\u7ae5\u5b50\u6240\u5bb3\uff0c\u5426\u5219\u5c11\u6797\u5bfa\u5f1f\u5b50\u5c31\u7edd\u4e0d\u4f1a\u653e\u4ed6\u8d70\uff01 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u6709\u8c01\u80fd\u4e3a\u4ed6\u8bc1\u660e\u5462\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u9ed8\u7136\u534a\u664c\uff0c\u957f\u53f9\u9053\uff1a\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6015\u8fde\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\uff01 \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u6885\u82b1\u53c8\u73b0>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u6885\u82b1\u53c8\u73b0 \u3000\u3000\u5348\u996d\u7684\u65f6\u5019\u5df2\u8fc7\uff0c\u6545\u4e8b\u4e5f\u8bf4\u5b8c\u4e86\uff0c\u4eba\u5df2\u6e10\u6e10\u79bb\u53bb\uff0c\u8d70\u7684\u65f6\u5019\uff0c\u5927\u5bb6\u90fd\u5728\u7eb7\u7eb7\u5e95\u5ea7\uff0c\u751a\u81f3 \u5728\u4e3a\u674e\u5bfb\u6b22\u60cb\u60dc\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8109\u8109\u7684\u51dd\u6ce8\u7740\u963f\u98de\uff0c\u963f\u98de\u5374\u5728\u6c89\u601d\uff0c\u4ed6\u4eec\u684c\u4e0a\u7684\u996d\u83dc\u90fd\u51e0\u4e4e\u6ca1\u6709\u52a8\u8fc7\uff0c\u4e0a\u9762\u5df2\u7ed3 \u4e86\u4e00\u5c42\u767d\u767d\u7684\u6cb9\uff0c\u5c31\u50cf\u662f\u6c34\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8fab\u5b50\u59d1\u5a18\u7a81\u7136\u653e\u4e0b\u7b77\u5b50\uff0c\u9053\uff1a\u7237\u7237\uff0c\u4f60\u8001\u4eba\u5bb6\u770b\u90a3\u674e\u63a2\u82b1\u662f\u4e0d\u662f\u88ab\u51a4 \u6789\u7684\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u5c31\u7b97\u77e5\u9053\u4ed6\u662f\u51a4\u6789\u7684\uff0c\u53c8\u6709\u4ec0\u4e48\u7528\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4f46\u4ed6\u7684\u670b\u53cb\u5462\uff1f\u96be\u9053\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u80af\u53bb\u6551\u4ed6\uff1f \u3000\u3000\u5b59\u5148\u751f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u4ed6\u82e5\u88ab\u56f0\u5728\u522b\u7684\u5730\u65b9\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4eba\u4f1a\u53bb\u6551\u4ed6\uff0c\u4f46\u4ed6\u88ab\u56f0\u5728\u5c11\u6797 \u5bfa\uff0c\u5929\u4e0b\u53ea\u6015\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u6551\u5f97\u4e86\u4ed6\u2014\u2014 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u90a3\u4e48\u2014\u2014\u90a3\u4e48\u8fd9\u6837\u4e00\u4f4d\u5927\u82f1\u96c4\uff0c\u96be\u9053\u5c31\u8981\u6d3b\u6d3b\u56f0\u6b7b\u4e0d\u6210\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u6cd5\u5b50\u5012\u662f\u6709\u4e00\u4e2a\uff0c\u53ea\u4e0d\u8fc7\u5e0c\u671b\u5f88\u6e3a\u832b\u800c\u5df2\u3002 \u3000\u3000\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u963f\u98de\u7684\u773c\u775b\u4eae\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u95ee\u9053\uff1a\u4ec0\u4e48\u6cd5\u5b50\uff1f \u3000\u3000\u5b59\u5148\u751f\u9053\uff1a\u9664\u975e\u90a3\u771f\u7684\u6885\u82b1\u76d7\u82e5\u8fd8\u6ca1\u6709\u6b7b\uff0c\u53c8\u5ffd\u7136\u51fa\u73b0\u4e86\uff0c\u81ea\u7136\u5c31\u53ef\u8bc1\u660e\u674e\u5bfb\u6b22\u5e76\u4e0d\u662f \u6885\u82b1\u76d7\uff0c\u4ed6\u82e5\u975e\u6885\u82b1\u76d7\uff0c\u81ea\u7136\u4e5f\u5c31\u6ca1\u6709\u5bb3\u6b7b\u5fc3\u7709\u5927\u5e08\u7684\u7406\u7531\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u8fd9\u5e0c\u671b\u5b9e\u5728\u6e3a\u832b\u5f97\u5f88\uff0c\u90a3\u771f\u7684\u6885\u82b1\u76d7\u5c31\u7b97\u6ca1\u6709\u6b7b\uff0c\u4e5f\u4e00\u5b9a\u65e9\u5c31\u8eb2 \u4e86\u8d77\u6765\uff0c\u597d\u6559\u674e\u5bfb\u6b22\u505a\u4ed6\u7684\u66ff\u6b7b\u9b3c\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u5c06\u65f1\u70df\u888b\u5728\u684c\u4e0a\u4e00\u6572\uff0c\u9053\uff1a\u4f60\u7684\u9762\u5403\u5149\u4e86\u4e48\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u6211\u672c\u6765\u997f\u5f97\u5f88\uff0c\u53ef\u662f\u542c\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u518d\u4e5f\u5403\u4e0d\u4e0b\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u5403\u4e0d\u4e0b\u5c31\u8d70\u5427\uff0c\u53cd\u6b63\u6211\u4eec\u5c31\u7b97\u5728\u8fd9\u91cc\u5750\u4e00\u8f88\u5b50\uff0c\u4e5f\u6551\u4e0d\u4e86\u674e\u63a2\u82b1\u7684\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u53c8\u56de\u779f\u4e86\u963f\u98de\u4e00\u773c\uff0c\u5634\u91cc\u4f3c\u4e4e\u5728\u8bf4\uff1a\u4f60\u82e5\u4e00\u76f4\u5750\u5728\u8fd9\u91cc\uff0c\u53c8\u600e\u80fd \u6551\u5f97\u4e86\u4ed6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u76ee\u9001\u4ed6\u4eec\u8d70\u51fa\u4e86\u95e8\uff0c\u624d\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u4f60\u770b\u8fd9\u4e00\u8001\u4e00\u5c11\u4e24\u4e2a\u4eba\u662f\u4ec0\u4e48\u6765\u8def\uff1f \u3000\u3000\u963f\u98de\u6f2b\u5e94\u9053\uff1a\u4ec0\u4e48\u6765\u8def\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8fd9\u8001\u5934\u5b50\u76ee\u4e2d\u795e\u5149\u5f03\u8db3\uff0c\u663e\u7136\u5185\u529f\u4e0d\u5f31\uff0c\u90a3\u5c0f\u59d1\u5a18\u811a\u6b65\u8f7b\u7075\uff0c\u52a8\u4f5c\u7075\u5feb\uff0c\u8f7b \u529f\u4e5f\u7edd\u4e0d\u4f1a\u5728\u6211\u4e4b\u4e0b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff01 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f9d\u6211\u770b\uff0c\u8fd9\u4e24\u4eba\u7edd\u4e0d\u4f1a\u662f\u8d70\u6c5f\u6e56\uff0c\u8bf4\u5927\u4e66\u7684\uff0c\u5fc5\u5b9a\u53e6\u6709\u56fe\u8c0b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u9053\uff1a\u4ed6\u6545\u610f\u5c06\u8fd9\u4ef6\u4e8b\u8bf4\u7ed9\u4f60\u542c\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u8981\u4f60\u53bb\u9001\u6b7b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u9001\u6b7b\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5e7d\u5e7d\u9053\uff1a\u4f60\u65e2\u77e5\u9053\u674e\u5bfb\u6b22\u88ab\u56f0\u5728\u5c11\u6797\uff0c\u81ea\u7136\u5c31\u4f1a\u4e0d\u987e\u4e00\u5207\u8d76\u53bb\u6551\u4ed6 \uff0c\u4f46\u4f60\u4e00\u4e2a\u4eba\u53bb\u600e\u4f1a\u662f\u5c11\u6797\u5bfa\u516b\u767e\u5f1f\u5b50\u7684\u5bf9\u624b\uff1f \u3000\u3000\u963f\u98de\u6c89\u9ed8\u7740\uff0c\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f55\u51b5\uff0c\u4ed6\u4eec\u8bf4\u7684\u4e5f\u8bb8\u5168\u90fd\u662f\u5047\u8bdd\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4f60\u53bb\u4e0a\u5f53\u3002 \u3000\u3000\u4ed6\u63e1\u4f4f\u4e86\u963f\u98de\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u5c31\u7b97\u4ed6\u4eec\u8bf4\u7684\u4e0d\u5047\uff0c\u674e\u5bfb\u6b22\u73b0\u5728\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5371\u9669\uff0c\u4f60\u82e5 \u53bb\u4e86\uff0c\u53cd\u800c\u4f1a\u4ee4\u4ed6\u5206\u5fc3\uff0c\u5c11\u6797\u5f1f\u5b50\u82e5\u662f\u4ee5\u4f60\u6765\u8981\u631f\u4ed6\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u4f1a\u4e0d\u987e\u4e00\u5207\u51fa\u6765\u6551\u4f60\u7684\uff0c\u90a3 \u4e48\u4f60\u975e\u4f46\u4e0d\u662f\u53bb\u6551\u4ed6\uff0c\u53cd\u800c\u662f\u53bb\u5bb3\u4ed6\u3002 \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u8003\u8651\u5f97\u7684\u786e\u6bd4\u6211\u5468\u5230\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u7b54\u5e94\u6211\u7edd\u4e0d\u53bb\u5c11\u6797\u5bfa\u5192\u9669\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u597d\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u7b54\u5e94\u5f97\u5982\u6b64\u75db\u5feb\uff0c\u6797\u4ed9\u513f\u53cd\u800c\u6709\u4e9b\u6000\u7591\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u8d70\u56de\u623f\u5b50\uff0c\u7a81\u542c\u963f\u98de\u9053\uff1a\u4f60\u65e2\u7136\u4e0d\u53bb\u5c11\u6797\u5bfa\u4e86\uff0c\u4f60\u8fd8\u662f\u56de\u53bb\u5427\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u5462\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u2014\u2014\u6211\u60f3\u5230\u522b\u5904\u53bb\u8d70\u8d70\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u624b\u5ffd\u7136\u4e00\u98a4\uff0c\u5931\u58f0\u9053\uff1a\u4f60\u83ab\u975e\u60f3\u53bb\u5047\u5192\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u5979\uff0c\u826f\u4e45\uff0c\u624d\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u662f\u3002 \u3000\u3000\u8fd9\u201c\u662f\u201d\u5b57\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u7edd\u65e0\u633d\u56de\u7684\u4f59\u5730\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5e7d\u5e7d\u9053\uff1a\u90a3\u4e48\u2014\u2014\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u53eb\u6211\u56de\u53bb\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u8fd9\u662f\u6211\u81ea\u5df1\u7684\u4e8b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u7684\u4e8b\u5c31\u662f\u6211\u7684\u4e8b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f46\u674e\u5bfb\u6b22\u5e76\u4e0d\u662f\u4f60\u7684\u670b\u53cb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u7684\u670b\u53cb\uff0c\u5c31\u662f\u6211\u7684\u670b\u53cb\u3002 \u3000\u3000\u963f\u98de\u9762\u9732\u611f\u6fc0\u4e4b\u8272\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u5bf9\u670b\u53cb\u65e2\u7136\u5982\u6b64\u591f\u4e49\u6c14\uff0c\u6211\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u80fd\u5462\uff1f\u6211\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u7528\uff0c\u53ef\u662f\uff0c \u4e24\u4e2a\u4eba\u5728\u4e00\u8d77\uff0c\u9047\u5230\u4e8b\u81f3\u5c11\u603b\u53ef\u4ee5\u5546\u91cf\u5546\u91cf\uff0c\u603b\u6bd4\u4e00\u4e2a\u4eba\u597d\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u63e1\u4f4f\u5979\u7684\u624b\uff0c\u867d\u7136\u8fd8\u662f\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4f46\u4ed6\u7684\u773c\u775b\uff0c\u4ed6\u7684\u8868\u60c5\uff0c\u5df2\u66ff\u4ed6\u8bf4\u51fa\u6765\u4e86 \u3002 \u3000\u3000\u8fd9\u65e0\u58f0\u7684\u8a00\u8bed\uff0c\u6bd4\u6709\u58f0\u7684\u66f4\u52a8\u4eba\u5f97\u591a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u4e00\u7b11\uff0c\u6025\u53c8\u76b1\u7709\u9053\uff1a\u4f60\u82e5\u8981\u5047\u5192\u6885\u82b1\u76d7\uff0c\u5c31\u5f97\u627e\u51e0\u4e2a\u5bf9\u50cf\u4e0b\u624b\u624d\u662f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u55ef\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u4eec\u603b\u4e0d\u80fd\u53bb\u627e\u65e0\xd7\u7684\u4eba\uff0c\u662f\u5417\uff1f \u3000\u3000\u963f\u98de\uff1a\u6211\u4eec\u627e\u7684\u5bf9\u8c61\uff0c\u81ea\u7136\u662f\u90a3\u4e9b\u4e3a\u5bcc\u4e0d\u4ec1\u7684\u6076\u9738\uff0c\u5750\u5730\u5206\u8d43\u7684\u5f3a\u76d7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u6211\u542c\u8bf4\u9644\u8fd1\u5c31\u6709\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u8c01\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6b64\u4eba\u65e9\u5e74\u662f\u4e2a\u7eff\u6797\u5de8\u76d7\uff0c\u4e94\u5341\u51e0\u5c81\u4ee5\u540e\u624d\u91d1\u76c6\u6d17\u624b\uff0c\u4f46\u6697\u4e2d\u8fd8\u662f\u505a\u4e9b\u4e0d\u6e05\u4e0d \u767d\u7684\u4e8b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff1f \u3000\u3000\u6797\u4ed9\u513f\u60f3\u4e86\u60f3\u9053\uff1a\u542c\u8bf4\u4ed6\u672c\u6765\u5c31\u53eb\u5f20\u80dc\u5947\uff0c\u73b0\u5728\u5374\u53eb\u5f20\u5458\u5916\uff0c\u5f20\u5927\u5584\u4e86\u3002 \u3000\u3000\u963f\u98de\u76b1\u7709\u9053\uff1a\u5927\u5584\u4eba\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4ed6\u62a2\u4e86\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u5c31\u7528\u4e00\u767e\u4e24\u53bb\u4fee\u6865\u94fa\u8def\uff0c\u665a\u4e0a\u6740\u4e86\u4e00\u767e\u4e2a\u4eba\uff0c\u767d\u5929\u5374\u6765 \u65bd\u7ca5\u8d60\u83dc\u2014\u2014\uff0d\u4e00\u4e2a\u5f3a\u76d7\u82e5\u662f\u60f3\u505a\u5584\u4eba\uff0c\u6bd4\u4efb\u4f55\u4eba\u90fd\u5bb9\u6613\u591a\u4e86\u3002 \u3000\u3000\u5f20\u80dc\u5947\u8eba\u5728\u8d35\u5983\u69bb\u4e0a\uff0c\u82e5\u6709\u6240\u601d\u7684\u671b\u7740\u9762\u524d\u4e00\u76c6\u718a\u718a\u7684\u7089\u706b\uff0c\u6162\u6162\u7684\u7740\u4e00\u7897\u7528\u6587\u706b\u7096\u6210 \u7684\u71d5\u7a9d\u7ca5\u3002 \u3000\u3000\u5916\u9762\u53c8\u4e0b\u96ea\u4e86\uff0c\u5c4b\u5b50\u91cc\u5374\u6e29\u6696\u5982\u6625\uff0c\u5c4b\u89d2\u7684\u4e00\u76c6\u6c34\u4ed9\u82b1\u5f00\u5f97\u6b63\u597d\uff0c\u4e00\u53ea\u80d6\u80d6\u7684\u5c0f\u82b1\u732b\u6b63 \u8eba\u5728\u82b1\u67b6\u4e0b\u6253\u778c\u7761\u3002 \u3000\u3000\u5f20\u80dc\u5947\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u5583\u5583\u9053\uff1a\u4eca\u5e74\u6625\u5929\u5f97\u597d\u65e9\u2014\u2014\uff0d \u3000\u3000\u745e\u96ea\u5146\u4e30\u5e74\uff0c\u660e\u5e74\u7684\u6536\u6210\u4e5f\u4e00\u5b9a\u4e0d\u9519\u3002 \u3000\u3000\u5979\u95ed\u8d77\u773c\u775b\uff0c\u521a\u60f3\u5c0f\u7761\u7247\u523b\uff0c\u517b\u517b\u7cbe\u795e\uff0c\u7a81\u7136\u90a3\u5c0f\xd7\u5934\u4e00\u58f0\u60ca\u547c\uff0c\u5f53\u7684\u71d5\u7a9d\u7897\u6454\u5f97\u7c89\u788e \u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u5f20\u5f00\u773c\u775b\uff0c\u4e00\u4e2a\u9ed1\u8863\u4eba\u5df2\u5e7d\u7075\u822c\u5ffd\u7136\u51fa\u73b0\u5728\u4ed6\u773c\u524d\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u4ece\u54ea \u91cc\u6765\u7684\u3002 \u3000\u3000\u5f20\u80dc\u5947\u867d\u7136\u6d17\u624b\u591a\u5e74\uff0c\u6b66\u529f\u5374\u6ca1\u6709\u6401\u4e0b\uff0c\u5389\u58f0\u9053\uff1a\u597d\u4e2a\u4e0d\u5f00\u80af\u7684\u5c0f\u8d3c\uff0c\u7adf\u6562\u6765\u592a\u5c81\u5934\u4e0a \u52a8\u571f\uff01 \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4ed6\u5df2\u6284\u8d77\u82b1\u67b6\uff0c\u5411\u8fd9\u9ed1\u8863\u4eba\u5f53\u5934\u6454\u4e0b\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u5bd2\u5149\u4e00\u95ea\u3002 \u3000\u3000\u5f20\u80dc\u5947\u6839\u672c\u6ca1\u6709\u770b\u51fa\u5bf9\u65b9\u662f\u5982\u4f55\u51fa\u624b\u7684\uff0c\u751a\u81f3\u6ca1\u6709\u770b\u6e05\u5bf9\u65b9\u624b\u91cc\u62ff\u7740\u7684\u5175\u5203\u662f\u4f55\u6a21\u6837\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5fc3\u53e3\u7a81\u7136\u4e00\u60ca\uff0c\u5df2\u591a\u4e86\u4e94\u70b9\u8840\u82b1\uff01 \u3000\u3000\u6885\u82b1\u76d7\u53c8\u51fa\u73b0\u4e86\uff01 \u3000\u3000\u83dc\u9986\u91cc\uff0c\u9152\u697c\u4e0a\uff0c\u5f88\u591a\u4eba\u90fd\u5728\u7a83\u7a83\u79c1\u8bed\u3002 \u3000\u3000\u96be\u9053\u6740\u6b7b\u5f20\u80dc\u5947\u7684\u624d\u662f\u771f\u7684\u6885\u82b1\u76d7\uff1f \u3000\u3000\u4ed6\u4e0b\u4e00\u4e2a\u5bf9\u8c61\u4f1a\u662f\u8c01\uff1f \u3000\u3000\u6709\u8d22\u6709\u52bf\u7684\u4eba\uff0c\u665a\u4e0a\u53c8\u7761\u4e0d\u7740\u89c9\u4e86\u3002 \u3000\u3000\u9ec4\u660f\uff0c\u53e4\u5239\u4e2d\u4f20\u51fa\u4e86\u4e00\u58f0\u6e05\u60a6\u60a0\u626c\u7684\u949f\u58f0\uff0c\u4e25\u8083\u800c\u51b7\u6f20\u7684\u5c11\u6797\u50e7\u4eba\uff0c\u4e00\u4e2a\u4e2a\u5782\u9996\u8d70\u5165\u5e84 \u4e25\u7684\u4f5b\u6bbf\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u811a\u6b65\u4f3c\u4e4e\u6bd4\u5e73\u65f6\u8fd8\u8981\u8f7b\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e9b\u5929\u4ee5\u6765\uff0c\u5c11\u6797\u5bfa\u4e2d\u6bcf\u4e2a\u4eba\u7684\u5fc3\u60c5\u90fd\u5206\u5916\u6c89\u91cd \u3002 \u3000\u3000\u5d69\u5c71\u4e4b\u9669\uff0c\u5bd2\u610f\u66f4\u91cd\uff0c\u6ee1\u5c71\u6c34\u96ea\u4e2d\uff0c\u6b63\u6709\u4e00\u4e2a\u4eba\u6025\u884c\u4e0a\u5c71\uff0c\u6b63\u662f\u5c11\u6797\u95e8\u4e0b\u7684\u4fd7\u5bb6\u5f1f\u5b50\u201c \u5357\u9633\u5927\u4fa0\u8427\u9759\u201d\u3002 \u3000\u3000\u8427\u9759\u7684\u811a\u6b65\u4e5f\u5f88\u8f7b\uff0c\u843d\u5730\u65e0\u58f0\uff0c\u4f46\u4ed6\u521a\u8e0f\u5165\u540e\u9662\uff0c\u65b9\u4e08\u5ba4\u5185\u5c31\u54cd\u8d77\u4e86\u5fc3\u6e56\u5927\u5e08\u6c89\u91cd\u7684\u8bed \u58f0\uff0c\u9053\uff1a\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u8427\u9759\u5728\u95e8\u5916\u8fdc\u8fdc\u505c\u4e0b\uff0c\u8eac\u8eab\u9053\uff1a\u5f1f\u5b50\u8427\u9759\uff0c\u7279\u6765\u6709\u8981\u4e8b\u7980\u62a5\u3002 \u3000\u3000\u65b9\u4e08\u5ba4\u4e2d\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u5fc3\u6e56\uff0c\u5fc3\u9274\u548c\u767e\u6653\u751f\u3002 \u3000\u3000\u8427\u9759\u4e0d\u6562\u591a\u8bf4\u5e9f\u8bdd\uff0c\u4e00\u8d70\u8fdb\u53bb\uff0c\u7acb\u523b\u8eac\u8eab\u9053\uff1a\u6c5f\u6e56\u4f20\u8bf4\u6885\u82b1\u76d7\u53c8\u51fa\u73b0\u4e86\uff01 \u3000\u3000\u8427\u9759\u9053\uff1a\u4e09\u5929\u4e4b\u524d\uff0c\u4e45\u5df2\u6d17\u624b\u5f52\u9690\u7684\u72ec\u884c\u76d7\u5f20\u80dc\u5947\u5ffd\u7136\u88ab\u6740\uff0c\u5bb6\u91cc\u7684\u73cd\u5b9d\u4e5f\u88ab\u6d17\u52ab\u4e00\u7a7a \uff0c\u81f4\u547d\u7684\u4f24\u75d5\u662f\u4e94\u70b9\u8840\u8ff9\uff0c\u72b6\u5982\u6885\u82b1\u3002 \u3000\u3000\u5fc3\u9274\uff0c\u767e\u6653\u751f\u5bf9\u671b\u4e00\u773c\uff0c\u8138\u4e0a\u5168\u65e0\u8840\u8272\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5fc3\u6e56\u5927\u5e08\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u6885\u82b1\u76d7\u65e2\u7136\u53c8\u518d\u5ea6\u51fa\u73b0\uff0c\u674e\u5bfb\u6b22\u8bf4\u7684\u90a3\u756a \u8bdd\u4e5f\u8bb8\u4e0d\u662f\u5047\u7684\uff0c\u4e5f\u8bb8\u662f\u6211\u4eec\u51a4\u6789\u4e86\u4ed6\u3002 \u3000\u3000\u767e\u6653\u751f\u671b\u7740\u5fc3\u9274\uff0c\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u5fc3\u9274\u7f13\u7f13\u8e31\u5230\u7a97\u53e3\uff0c\u671b\u7740\u7a97\u5916\u7684\u79ef\u96ea\uff0c\u7f13\u7f13\u9053\uff1a\u4e5f\u8bb8\u8fd9\u53cd\u800c\u66f4\u8bc1\u660e\u4e86\u674e\u5bfb\u6b22\u5c31\u662f\u6885\u82b1\u76d7 \uff01 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u6b64\u8bdd\u600e\u8bb2\uff1f \u3000\u3000\u5fc3\u9274\u9053\uff1a\u6211\u82e5\u662f\u6885\u82b1\u76d7\uff0c\u77e5\u9053\u5df2\u6709\u4eba\u505a\u4e86\u6211\u7684\u66ff\u6b7b\u9b3c\uff0c\u4e00\u5b9a\u4f1a\u6682\u65f6\u7684\u907f\u907f\u98ce\u5934\uff0c\u5426\u5219\u5c82 \u975e\u53cd\u800c\u7b49\u4e8e\u6551\u4e86\u674e\u5bfb\u6b22\uff1f \u3000\u3000\u767e\u6653\u751f\u8fd9\u624d\u70b9\u5934\u9053\uff1a\u4e0d\u9519\uff0c\u6885\u82b1\u76d7\u6b64\u756a\u51fa\u73b0\uff0c\u65e0\u5f02\u662f\u5728\u4e3a\u674e\u5bfb\u6b22\u6d17\u5237\u51a4\u540d\uff0c\u6211\u82e5\u662f\u6885\u82b1 \u76d7\uff0c\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u505a\u8fd9\u4e8b\u7684\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u90a3\u4e48\uff0c\u4f60\u7684\u610f\u89c1\u662f\u2014\u2014\uff0d \u3000\u3000\u5fc3\u9274\u9053\uff1a\u674e\u5bfb\u6b22\u82e5\u771f\u7684\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u4ed6\u7684\u540c\u515a\u4e5f\u5c31\u4e0d\u5fc5\u8fd9\u4e48\u505a\u4e86\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u5728\u65b9\u4e08\u5ba4\u4e2d\u8e31\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u5ffd\u7136\u9a7b\u8db3\u9053\uff1a\u4eca\u65e5\u5728\u83e9\u63d0\u9662\u5f53\u503c\u7684\u662f \u8c01\uff1f \u3000\u3000\u5fc3\u9274\u9053\uff1a\u662f\u4e8c\u5e08\u5144\u5ea7\u4e0b\u7684\u4e00\u8335\u548c\u4e00\u5c18\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u4f20\u4ed6\u4eec\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u8d1f\u624b\u7ad9\u5728\u5899\u89d2\uff0c\u542c\u5230\u4e00\u8335\u548c\u4e00\u5c18\u8d70\u8fdb\u6765\u7684\u811a\u6b65\u58f0\uff0c\u4ed6\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u53ea\u662f\u95ee\u9053\uff1a\u4e94\u5e08\u53d4 \u7684\u665a\u81b3\u4f60\u4eec\u5df2\u9001\u53bb\u4e86\u4e48\uff1f \u3000\u3000\u4e00\u8335\u9053\uff1a\u9001\u53bb\u4e86\uff0c\u53ef\u662f\u2014\u2014\u53ef\u662f\u2014\u2014 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u53ef\u662f\u600e\u6837\uff1f \u3000\u3000\u4e00\u8335\u5782\u9996\u9053\uff1a\u5f1f\u5b50\u4eec\u6309\u7167\u524d\u4e24\u5929\u7684\u89c4\u77e9\uff0c\u8fd8\u662f\u5c06\u81b3\u98df\u653e\u5728\u95e8\u53e3\uff0c\u4efd\u91cf\u4e5f\u548c\u6628\u5929\u7684\u4e00\u6837\uff0c \u6bd4\u5e73\u65f6\u81b3\u98df\u52a0\u4e86\u4e00\u500d\uff0c\u8fd8\u6709\u4e00\u76c2\u6e05\u6c34\u3002 \u3000\u3000\u4e00\u5c18\u63a5\u7740\u9053\uff1a\u98df\u76d8\u662f\u5f1f\u5b50\u4eb2\u81ea\u653e\u5230\u95e8\u53e3\u7684\uff0c\u56e0\u4e3a\u5f1f\u5b50\u60f3\u8d81\u673a\u770b\u770b\u5c4b\u5b50\u91cc\u7684\u52a8\u9759\uff0c\u521a\u8d70\u51fa \u51e0\u6b65\u540e\uff0c\u5c31\u77a7\u89c1\u674e\u5bfb\u6b22\u7684\u624b\u81ea\u95e8\u7f1d\u91cc\u4f38\u51fa\u6765\uff0c\u5c06\u98df\u76d8\u53d6\u53bb\uff0c\u8c01\u77e5\u2014\u2014\u8c01\u77e5\u8fc7\u4e86\u534a\u664c\uff0c\u4ed6\u53c8\u5c06 \u4e00\u76d8\u81b3\u98df\u5168\u90fd\u629b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u4e00\u5c18\u5450\u5450\u9053\uff1a\u4ed6\u5acc\u83dc\u4e0d\u597d\uff0c\u53c8\u6ca1\u6709\u9152\uff0c\u6240\u4ee5\u4e0d\u80af\u5403\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u6ee1\u9762\u4ff1\u662f\u6012\u5bb9\uff0c\u9053\uff1a\u4ed6\u5f53\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u996d\u9986\u5b50\u4e48\uff1f \u3000\u3000\u4e00\u8335\u548c\u4e00\u5c18\u5243\u5ea6\u5df2\u6709\u5341\u4f59\u5e74\uff0c\u8fd8\u4ece\u6765\u6ca1\u6709\u89c1\u5230\u4ed6\u4eec\u7684\u638c\u95e8\u4eba\u52a8\u8fc7\u771f\u6012\uff0c\u4e24\u4eba\u4f4e\u4e0b\u4e86\u53f0\uff0c \u4e0d\u6562\u62ac\u8d77\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5fc3\u6e56\u5927\u5e08\u624d\u6e10\u6e10\u5e73\u606f\uff0c\u53c8\u8f6c\u8fc7\u5934\u53bb\uff0c\u671b\u7740\u7089\u9999\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u4ed6\u8bf4\u8981 \u5403\u4ec0\u4e48\uff1f \u3000\u3000\u4e00\u8335\u9053\uff1a\u4ed6\u5c45\u7136\u5199\u4e86\u5f20\u83dc\u5355\uff0c\u81ea\u65f6\u9762\u629b\u51fa\u6765\uff0c\u53eb\u5f1f\u5b50\u4eec\u7167\u7740\u83dc\u5355\u5b50\u505a\uff0c\u8fd8\u8bf4\u53ea\u8981\u505a\u9519\u4e00 \u6837\uff0c\u4ed6\u5c31\u539f\u5c01\u9000\u56de\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u5c06\u4ed6\u7684\u83dc\u5355\u62ff\u6765\u77a7\u77a7\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u5f20\u7d20\xd7\u4e0a\uff0c\u5199\u7740\u597d\u4e00\u7b14\u7075\u98de\u7ecf\uff0c\u5199\u7684\u662f\uff1a \u3000\u3000\u7ea2\u7116\u51ac\u7b0b \u3000\u3000\u6c49\u7f57\u5b9d \u3000\u3000\u53d1\u83dc\u82b1\u83c7 \u3000\u3000\u7fe1\u7fe0\u83dc\u5fc3\uff0c\u7b0b\u5c16\u51ac\u83c7\u8c46\u8150\u7fb9\u3002 \u3000\u3000\u56db\u83dc\u4e00\u6c64\u4e4b\u5916\uff0c\u4ed6\u5c45\u7136\u8fd8\u8981\u4e09\u65a4\u4e0a\u597d\u7684\u7af9\u53f6\u9752\uff0c\u5802\u5802\u7684\u5c11\u6797\u5bfa\uff0c\u597d\u50cf\u771f\u88ab\u4ed6\u5f53\u6210\u4eac\u57ce\u7684 \u7d20\u83dc\u9986\u5b50\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u4e86\u8fd9\u5f20\u83dc\u5355\u90fd\u514d\u4e0d\u4e86\u8981\u54ed\u7b11\u4e0d\u5f97\uff0c\u52c3\u7136\u5927\u6012\uff0c\u8c01\u77e5\u5fc3\u6e56\u5927\u5e08\u5374\u53ea\u662f\u6de1\u6de1\u5730\u9053\uff1a \u4f60\u4eec\u5c31\u7167\u8fd9\u5f20\u5355\u5b50\u505a\u7ed9\u4ed6\u5427\u3002 \u3000\u3000\u5fc3\u9274\u62a2\u5148\u4e00\u6b65\uff0c\u560e\u58f0\u9053\uff1a\u5e08\u5144\u4f60\u2014\u2014\uff0d\u4f60\u600e\u80fd\u2014\u2014 \u3000\u3000\u5fc3\u6e56\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\uff1a\u674e\u5bfb\u6b22\u82e5\u4e0d\u80af\u5403\uff0c\u4e94\u5e08\u5f1f\u5c82\u975e\u4e5f\u8981\u966a\u7740\u4ed6\u6328\u997f\uff0c\u4ed6\u8eab\u5b50\u4e00\u5411\u5355\u8584 \uff0c\u8fd1\u5e74\u6765\u66f4\u662f\u4e00\u76f4\u7f20\u7ef5\u75c5\u69bb\uff0c\u6211\u4eec\u6562\u80fd\u8ba9\u4ed6\u518d\u53d7\u82e6\u96be\u6298\u78e8\uff1f \u3000\u3000\u5fc3\u9274\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u53ef\u662f\u6211\u4eec\u8fd9\u6837\u505a\uff0c\u90a3\u674e\u5bfb\u6b22\u5c82\u975e\u66f4\u5f97\u610f\u4e86\u4e48\uff1f \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u76ee\u5149\u95ea\u52a8\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u6211\u5fc3\u4e2d\u5df2\u6709\u4e86\u6253\u7b97\uff0c\u5c31\u8ba9\u4ed6\u591a\u5f97\u610f\u4e24\u5929\u53c8\u6709\u4f55\u59a8\uff1f \u3000\u3000\u963f\u98de\u4ef0\u5367\u5728\u5e8a\u4e0a\uff0c\u4ee5\u624b\u4e3a\u6795\u5446\u5446\u7684\u671b\u7740\u5c4b\u9876\u3002 \u3000\u3000\u51e0\u4e4e\u5df2\u6709\u4e24\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u5c31\u8fd9\u6837\u8eba\u7740\uff0c\u52a8\u4e5f\u6ca1\u6709\u52a8\uff0c\u4ed6\u6574\u4e2a\u4eba\u4f3c\u4e4e\u90fd\u5df2\u53d8\u6210\u4e86\u4e00\u5757\u82b1\u5c97\u77f3 \u3002 \u3000\u3000\u4ed6\u53ea\u6709\u7b49\u5f85\uff0c\u53ea\u6709\u5fcd\u8010\uff0c\u53ea\u6709\u4e0d\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\u4e0d\u52a8\u53ef\u4ee5\u8282\u7701\u4f53\u529b\uff0c\u6709\u4e86\u4f53\u529b\u624d\u6709\u98df\u7269\uff0c\u4ed6\u624d\u80fd\u6d3b\u4e0b\u53bb\uff0c\u548c\u5927\u81ea\u7136\u7684\u640f\u6597\u662f\u6c38\u65e0\u4f11 \u6b62\u7684\u3002 \u3000\u3000\u6709\u51e0\u6b21\u751a\u81f3\u8fde\u6700\u673a\u8b66\u72e1\u733e\u7684\u91ce\u5154\u90fd\u8ba4\u4e3a\u4ed6\u53ea\u4e0d\u8fc7\u662f\u5757\u77f3\u5934\uff0c\u90a3\u65f6\u4ed6\u5df2\u997f\u5f97\u8fde\u8df3\u8dc3\u7684\u529b\u6c14 \u90fd\u6ca1\u6709\u4e86\uff0c\u82e5\u4e0d\u662f\u8fd9\u91ce\u5154\u81ea\u5df1\u6295\u5165\u4e86\u4ed6\u638c\u63e1\u4e2d\uff0c\u4ed6\u53ea\u6015\u5df2\u997f\u6b7b\uff0c\u8fde\u72d0\u72f8\u90fd\u6355\u6349\u4e0d\u5230\u7684\u65f6\u5019\u91ce \u5154\u5c45\u7136\u4f1a\u81ea\u6295\u7f57\u7f51\uff0c\u8fd9\u5728\u8352\u91ce\u4e2d\u7b80\u76f4\u662f\u795e\u8bdd\uff0c\u82e5\u6709\u4eba\u80fd\u8bf4\u7ed9\u91ce\u5154\u542c\uff0c\u8fde\u5b83\u4eec\u81ea\u5df1\u90fd\u4e0d\u4f1a\u76f8\u4fe1 \u3002 \u3000\u3000\u8fd8\u6709\u4e00\u6b21\u63a5\u8fde\u534a\u4e2a\u6708\u7684\u66b4\u98ce\u96ea\uff0c\u90a3\u65f6\u4ed6\u8fd8\u53ea\u6709\u5341\u5c81\uff0c\u53c8\u997f\u4e86\u4e24\u5929\uff0c\u5374\u5728\u8fd9\u65f6\u5019\u9047\u5230\u4e00\u5934 \u718a\u3002 \u3000\u3000\u4ed6\u5df2\u5168\u65e0\u62b5\u6297\u4e4b\u529b\uff0c\u5e78\u597d\u718a\u662f\u4e0d\u5403\u6b7b\u4eba\u7684\uff0c\u4ed6\u5c31\u8eba\u4e0b\u6765\u88c5\u6b7b\uff0c\u8c01\u77e5\u4ed6\u9047\u89c1\u7684\u5374\u662f\u5934\u8001\u5978 \u5de8\u733e\u7684\u718a\uff0c\u800c\u4e14\u4e5f\u5feb\u997f\u75af\u4e86\uff0c\u7adf\u4e00\u76f4\u4e0d\u8d70\uff0c\u8fd8\u4e0d\u4f4f\u7528\u9f3b\u5b50\u53bb\u55c5\uff0c\u7528\u811a\u722a\u53bb\u6293\uff0c\u751a\u81f3\u7528\u7259\u9f7f\u53bb \u54ac\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u5168\u90fd\u5fcd\u8010\u4e0b\u6765\u4e86\uff0c\u5c45\u7136\u4e00\u76f4\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4ed6\u627e\u5230\u4e00\u652f\u5df2\u51bb\u50f5\u4e86\u7684\u91ce\u72d7\uff0c\u9971\u9910\u4e00\u987f\u540e\u6062\u590d\u4e86\u4f53\u529b\uff0c\u4e8e\u662f\u4ed6\u5c31\u53bb\u627e\u8fd9\u5934\u718a\u62a5\u590d \u3002 \u3000\u3000\u5f53\u5929\u665a\u4e0a\u4ed6\u5c31\u4eab\u53d7\u4e86\u4e00\u987f\u718a\u638c\uff0c\u867d\u7136\u56e0\u4e3a\u4ed6\u4e0d\u4f1a\u70f9\u8c03\uff0c\u6240\u4ee5\u718a\u638c\u7684\u6ecb\u5473\u5e76\u4e0d\u5982\u4f20\u8bf4\u4e2d\u90a3 \u4e48\u597d\u3002 \u3000\u3000\u8fd9\u79cd\u5fcd\u8010\u529b\u5e76\u4e0d\u662f\u5929\u751f\u7684\uff0c\u90a3\u5f97\u8981\u957f\u4e45\u800c\u8270\u82e6\u7684\u953b\u70bc\u3002 \u3000\u3000\u5f00\u59cb\u65f6\u8fd8\u4e0d\u5230\u7247\u523b\u529f\u592b\uff0c\u4ed6\u5c31\u89c9\u5f97\u5168\u8eab\u90fd\u75d2\u4e86\u8d77\u6765\uff0c\u5fcd\u4e0d\u4f4f\u4e0d\u53bb\u6414\u75d2\uff0c\u4ee5\u540e\u5c31\u6e10\u53d8\u6210\u9ebb \u6728\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5374\u8fde\u9ebb\u6728\u7684\u611f\u89c9\u90fd\u6ca1\u6709\u4e86\uff0c\u53ea\u8981\u4ed6\u8ba4\u4e3a\u6ca1\u6709\u52a8\u7684\u5fc5\u7136\uff0c\u4ed6\u5c31\u53ef\u4ee5\u63a5\u8fde\u51e0\u4e2a\u65f6\u8fb0\u4e0d \u52a8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u56de\u6765\u7684\u65f6\u5019\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u7761\u7740\u4e86\u3002 \u3000\u3000\u4eca\u5929\u6797\u4ed9\u513f\u7684\u88c5\u675f\u5f88\u5947\u602a\uff0c\u4ed6\u7a7f\u7684\u662f\u4ef6\u5bbd\u5927\u7684\u7c97\u5e03\u8863\u670d\uff0c\u5c06\u5979\u5fbd\u6807\u67d4\u548c\u7684\u66f2\u7ebf\u5168\u90fd\u63a9\u6ca1 \u3002 \u3000\u3000\u5979\u5934\u4e0a\u622a\u7740\u9876\u7834\u65e7\u7684\u6be1\u7b20\uff0c\u906e\u76d6\u4e86\u9762\u76ee\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u662f\u4e3a\u4e86\u6253\u542c\u6d88\u606f\u53bb\u7684\uff0c\u5df2\u53bb\u4e86\u4e24\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u5750\u8d77\u6765\u7684\u65f6\u5019\uff0c\u5979\u771f\u5413\u4e86\u4e00\u8df3\uff0c\u63a5\u7740\u7b11\u9053\uff1a\u539f\u6765\u4f60\u662f\u5728\u88c5\u7761\uff0c\u96be\u9053\u6545\u610f\u60f3\u5413\u6211 \uff1f \u3000\u3000\u6797\u4ed9\u513f\u7406\u4e86\u7406\u5934\u53d1\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u4f60\u8ba8\u538c\u6211\uff1f \u3000\u3000\u963f\u98de\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6e29\u67d4\u7684\u671b\u7740\u4ed6\uff0c\u7a81\u7136\u8fc7\u53bb\u4eb2\u4e86\u4eb2\u4ed6\u7684\u8138\uff0c\u67d4\u58f0\u9053\uff1b\u4f60\u771f\u597d\u3002 \u3000\u3000\u963f\u98de\u7ad9\u8d77\u6765\uff0c\u5c06\u5979\u8131\u4e0b\u6765\u7684\u6be1\u7b20\u6302\u5230\u5899\u4e0a\uff0c\u7b49\u81ea\u5df1\u7684\u547c\u5438\u6162\u6162\u7684\u5e73\u606f\u4e86\uff0c\u4ed6\u624d\u56de\u8fc7\u5934\u95ee \u9053\uff1a\u6709\u6d88\u606f\u4e86\u5417\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u90a3\u4e9b\u548c\u5c1a\u8fd8\u4e0d\u80af\u653e\u4ed6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u6c89\u541f\u7740\uff0c\u9053\uff1a\u5c11\u6797\u5bfa\u7684\u4f5c\u98ce\u4e00\u5411\u6700\u7a33\u5065\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u90fd\u8981\u5148\u89c2\u5bdf\u5f88\u4e45\uff0c\u7edd\u4e0d\u80af\u8f7b \u4e3e\u5984\u52a8\uff0c\u5b81\u53ef\u4e0d\u505a\uff0c\u4e5f\u4e0d\u80af\u505a\u9519\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f46\u4ed6\u4eec\u5df2\u7b49\u4e86\u516d\u4e03\u5929\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e5f\u8bb8\u4ed6\u4eec\u8fd8\u4e0d\u80af\u60f3\u5ff5\u6740\u5f20\u80dc\u5947\u7684\u4eba\u662f\u6885\u82b1\u76d7\uff0c\u56e0\u4e3a\u6885\u82b1\u76d7\u505a\u6848\u4e00\u5411\u662f\u8fde\u7740\u6765 \u7684\uff0c\u7edd\u4e0d\u4f1a\u4e00\u6b21\u5c31\u7f62\u624b\u3002 \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1b\u4ed6\u4eec\u603b\u6709\u76f8\u4fe1\u7684\u65f6\u5019\uff0c\u6211\u4e00\u5b9a\u8981\u4ed6\u4eec\u76f8\u4fe1\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u6458\u4e0b\u90a3\u9876\u6be1\u7b20\u6234\u4e0a\uff0c\u9053\uff1a\u4f60\u968f\u6211\u6765\uff0c\u6211\u5e26\u4f60\u53bb\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u53bb\u54ea\u91cc\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53bb\u627e\u4f60\u7684\u7b2c\u4e8c\u4e2a\u5bf9\u8c61\u3002 \u3000\u3000\u9ec4\u660f\u8fc7\u540e\uff0c\u96ea\u5df2\u6eb6\u5316\uff0c\u4ed6\u4eec\u7684\u88c5\u675f\u65e2\u5df2\u6539\u53d8\uff0c\u6240\u4ee5\u8d70\u5728\u4eba\u7fa4\u4e2d\u5e76\u4e0d\u5f15\u4eba\u6ce8\u610f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u6307\u5173\u775b\u5bb6\u5f53\u94fa\u9053\uff1a\u4f60\u770b\u8fd9\u62db\u724c\u3002 \u3000\u3000\u8fd9\u5bb6\u5f53\u94fa\u7684\u89c4\u6a21\u5f88\u5927\uff0c\u9ed1\u5e95\u91d1\u5b57\u62db\u724c\u4e0a\u5199\u7740\uff1a\u201c\u7533\u8bb0\u5f53\u94fa\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u8fd9\u62db\u724c\u53c8\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5e76\u6ca1\u6709\u56de\u5934\u4ed6\u7684\u8bdd\u3002\u53c8\u6307\u7740\u4e00\u5bb6\u9152\u697c\u5916\u60ac\u7740\u7684\u62db\u724c\u9053\uff1a\u4f60\u518d\u770b\u8fd9\u62db\u724c\u3002 \u3000\u3000\u8fd9\u5bb6\u9152\u697c\u7684\u751f\u610f\u5f88\u597d\uff0c\u4e24\u5c42\u697c\u7684\u5730\u65b9\u4f3c\u5df2\u5ea7\u65e0\u865a\u5e2d\uff0c\u5e95\u91d1\u5b57\u62db\u724c\u4e0a\u5199\u7684\u662f\uff1a\u7533\u8bb0\u72b6\u5143\u697c \u3002 \u3000\u3000\u57ce\u91cc\u8f83\u70ed\u95f9\u7684\u5730\u533a\u53ea\u6709\u4e09\u6761\u8857\uff0c\u5728\u8fd9\u4e09\u6761\u8857\u4e0a\uff0c\u6bcf\u9694\u4e94\u4e03\u5bb6\u5e97\u94fa\uff0c\u5c31\u6709\u4e00\u5bb6\u6302\u7684\u662f\u7533\u8bb0 \u91d1\u5b57\u62db\u724c\u3002 \u3000\u3000\u51e1\u662f\u6302\u7740\u7533\u8bb0\u62db\u724c\u7684\u5e97\u94fa\uff0c\u751f\u610f\u5c31\u505a\u5f97\u7279\u522b\u5927\u3002 \u3000\u3000\u963f\u98de\u75b2\u4e4f\uff1a\u8fd9\u4e9b\u5e97\u5168\u90fd\u662f\u4e00\u4e2a\u4eba\u5f00\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u55ef\uff0c\u5168\u90fd\u662f\u7533\u8001\u4e09\u5f00\u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u73b0\u5728\u6211\u4eec\u8fd8\u8981\u5230\u54ea\u91cc\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u8ddf\u6211\u6765\u5c31\u77e5\u9053\u4e86\u3002 \u3000\u3000\u963f\u98de\u672c\u5c31\u4e0d\u662f\u559c\u6b22\u591a\u95ee\u7684\u4eba\uff0c\u4e5f\u4e0d\u518d\u95ee\u5979\u3002 \u3000\u3000\u671b\u7740\u773c\u524d\u7684\u4e00\u7247\u7a7a\u65f7\uff0c\u963f\u98de\u957f\u957f\u547c\u5438\u4e86\u4e00\u6b21\uff0c\u5fc3\u80f8\u4eff\u4f5b\u4e5f\u5f00\u6717\u4e86\u8d77\u6765\uff0c\u5929\u5730\u4f3c\u5df2\u5b8c\u5168\u5c5e \u4e8e\u4ed6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9759\u9759\u7684\u4f9d\u504e\u5728\u4ed6\u8eab\u65c1\uff0c\u4e5f\u6ca1\u6709\u6253\u6270\u8fd9\u4efd\u5e7d\u8da3\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u591c\u7a7a\u4e2d\u4eae\u8d77\u4e86\u4e00\u9053\u6d41\u661f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5f00\u5fc3\u7684\u7b11\u4e86\uff0c\u6b22\u547c\u9053\uff1a\u4f60\u770b\uff0c\u6d41\u661f\u3002 \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u4f60\u8bb8\u4e86\u613f\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u561f\u8d77\u5634\u9053\uff1a\u6d41\u661f\u603b\u662f\u4e00\u970e\u773c\u5c31\u8fc7\u4e86\uff0c\u6ca1\u6709\u4eba\u80fd\u6765\u5f97\u53ca\u8bb8\u613f\u7684\uff0c\u9664\u975e\u4ed6\u65e9\u5df2\u77e5\u9053\u4f1a \u6709\u6d41\u661f\u51fa\u73b0\uff0c\u86e4\u53c8\u6709\u8c01\u80fd\u77e5\u9053\u6d41\u661f\u4f1a\u5728\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\uff1f\u6211\u770b\u8fd9\u5168\u662f\u9a97\u4eba\u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5c31\u7b97\u662f\u9a97\u4eba\uff0c\u4f46\u5b83\u5374\u80fd\u4f7f\u751f\u51fa\u8bb8\u591a\u7f8e\u4e3d\u5e7b\u60f3\uff0c\u6c38\u8fdc\u5e26\u7740\u5b83\uff0c\u4e00\u4e2a\u4eba\u82e5\u80fd\u6c38\u8fdc\u5e26 \u7740\u4efd\u7f8e\u4e3d\u7684\u5e0c\u671b\uff0c\u603b\u662f\u4ef6\u597d\u4e8b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u60f3\u4e0d\u5230\u4f60\u4e5f\u77e5\u9053\u8fd9\u4f20\u8bf4\u3002 \u3000\u3000\u963f\u98de\u76ee\u5149\u9065\u671b\u7740\u8fdc\u65b9\uff0c\u8fdc\u65b9\u7684\u6d41\u661f\u65e9\u5df2\u6d88\u901d\uff0c\u4ed6\u76ee\u4e2d\u5374\u6d41\u9732\u51fa\u4e00\u62b9\u51c4\u51c9\u60b2\u4f24\u4e4b\u610f\uff0c\u60a0\u60a0 \u9053\uff1a\u8fd9\u4f20\u8bf4\u6211\u5f88\u5c0f\u7684\u65f6\u5019\u5c31\u77e5\u9053\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u77a7\u7740\u4ed6\u7684\u773c\u775b\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u53c8\u60f3\u8d77\u4e86\u4f60\u7684\u6bcd\u4eb2\uff1f\u662f\u4e0d\u662f\u5979\u544a\u8bc9\u4f60\u7684\uff1f \u3000\u3000\u963f\u98de\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5ffd\u7136\u5927\u6b65\u5411\u524d\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u665a\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u4e00\u9635\u66f4\u9f13\uff0c\u5df2\u662f\u521d\u66f4\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u53d1\u89c9\u524d\u9762\u6709\u4e00\u7247\u5f88\u5927\u7684\u5e84\u9662\uff0c\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u53cd\u800c\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e5f\u5728\u4ef0\u671b\u7740\u5899\u5934\uff0c\u5583\u5583\u9053\uff1a\u597d\u9ad8\u7684\u5899\uff0c\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u56db\u4e08\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5dee\u4e0d\u591a\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u80fd\u4e0d\u80fd\u63a0\u8fc7\u53bb\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e16\u4e0a\u6ca1\u6709\u4eba\u80fd\u63a0\u8fc7\u56db\u4e08\u9ad8\u5899\uff0c\u4f46\u82e5\u4e00\u5b9a\u8981\u8fdb\u53bb\uff0c\u8fd8\u662f\u6709\u6cd5\u5b50\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6c89\u541f\u7740\uff0c\u6cbf\u7740\u5899\u811a\u8d70\u4e86\u51e0\u6b65\uff0c\u624d\u56de\u5934\u9053\uff1a\u8fd9\u5c31\u662f\u7533\u8001\u4e09\u7684\u5bb6\u3002 \u3000\u3000\u963f\u98de\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u7533\u8001\u4e09\u5c31\u662f\u6211\u7b2c\u4e8c\u4e2a\u4e0b\u624b\u7684\u5bf9\u8c61\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e0d\u613f\u610f\u5411\u751f\u610f\u4eba\u4e0b\u624b\uff0c\u4f46\u751f\u610f\u4eba\u4e5f\u6709\u597d\u591a\u79cd\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4ed6\u662f\u54ea\u4e00\u79cd\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6700\u4e0d\u89c4\u77e9\u7684\u90a3\u4e00\u79cd\u3002 \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4f60\u60f3\uff0c\u89c4\u77e9\u7684\u751f\u610f\u4eba\u600e\u4f1a\u5728\u540c\u4e00\u57ce\u91cc\uff0c\u540c\u4e00\u6761\u8857\u4e0a\u5f00\u5341\u51e0\u5bb6\u94fa\u5b50\uff0c\u89c4\u77e9 \u7684\u751f\u610f\u4eba\u5bb6\u91cc\u600e\u4f1a\u8d77\u8fd9\u4e48\u9ad8\u7684\u5899\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5899\u8d77\u5f97\u9ad8\u4e9b\u5e76\u6ca1\u6709\u9519\uff0c\u94fa\u5b50\u5f00\u5f97\u591a\u4e9b\u4e5f\u4e0d\u72af\u6cd5\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5899\u8d77\u5f97\u9ad8\u662f\u505a\u8d3c\u5fc3\u865a\uff0c\u6015\u4eba\u62a5\u590d\uff0c\u94fa\u5b50\u5f00\u5f97\u591a\u662f\u56e0\u4e3a\u4ed6\u4f1a\u62a2\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u62a2\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u7533\u5bb6\u662f\u5927\u65cf\uff0c\u4e0a\u4e00\u4ee3\u5df2\u6709\u4e94\u623f\uff0c\u5230\u4e86\u8fd9\u4e00\u4ee3\uff0c\u5802\u5144\u5802\u5f1f\u4e00\u5171\u6709\u5341\u516d\u4e2a\u4e4b\u591a\uff0c \u5341\u516d\u4e2a\u5144\u5f1f\u5f00\u4e86\u56db\u5341\u591a\u5bb6\u5e97\u94fa\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u7b97\u6765\u6bcf\u4eba\u53ea\u6709\u4e09\u5bb6\u94fa\u5b50\uff0c\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f46\u73b0\u5728\u56db\u5341\u591a\u5bb6\u94fa\u5b50\u5168\u662f\u7533\u8001\u4e09\u7684\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u8bef\u5165\u7f57\u7f51>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u8bef\u5165\u7f57\u7f51 \u3000\u3000\u6797\u4ed9\u513f\u548c\u963f\u98de\u5728\u665a\u98ce\u4e2d\u6765\u5230\u4e00\u7247\u5f88\u5927\u7684\u5e84\u9662\u524d\uff0c\u6307\u7740\u90a3\u5ea7\u9ad8\u5f97\u51fa\u5947\u7684\u56f4\u5899\u9053\uff1a\u8fd9\u5c31\u662f\u7533 \u8001\u4e09\u7684\u5bb6\uff0c\u4ed6\u4eec\u5802\u5144\u5f1f\u5341\u516d\u4e2a\u5408\u5f00\u4e86\u56db\u5341\u591a\u5bb6\u5e97\u94fa\uff0c\u73b0\u5728\u5168\u662f\u7533\u8001\u4e09\u7684\u4e86\uff0c\u56e0\u4e3a\u4ed6\u7684\u5341\u4e94\u4e2a \u5144\u5f1f\u5168\u90fd\u8fdb\u4e86\u68fa\u6750\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u90a3\u5341\u4e94\u4e2a\u4eba\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u636e\u8bf4\u662f\u75c5\u6b7b\u7684\uff0c\u4f46\u7a76\u7adf\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u522b\u4eba\u53ea\u5947\u602a\u5e73\u65e5\u8eab\u4f53\u5f88\u597d \u7684\u5341\u4e94\u4e2a\u4eba\uff0c\u600e\u4f1a\u5728\u4e24\u4e09\u5e74\u4e4b\u4e2d\u5c31\u6b7b\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5c31\u50cf\u662f\u4e2d\u4e86\u761f\u75ab\u4f3c\u7684\uff0c\u800c\u7533\u8001\u4e09\u8fde\u4e00\u70b9\u5c0f \u6bdb\u75c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u963f\u98de\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u8bf4\u4e86\uff0c\u53ea\u6de1\u6de1\u8bf4\u4e86\u53e5\uff1a\u6211\u660e\u5929\u665a\u4e0a\u5c31\u6765\u627e\u4ed6\u3002 \u3000\u3000\u963f\u98de\u624b\u8db3\u5e76\u7528\uff0c\u58c1\u864e\u822c\u722c\u4e0a\u4e86\u9ad8\u5899\u3002 \u3000\u3000\u722c\u4e0a\u5899\u5934\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u4e00\u7247\u5f88\u5927\u7684\u56ed\u6797\u548c\u4e00\u5c42\u5c42\u623f\u5c4b\uff0c\u8fd9\u65f6\u4eba\u4eec\u591a\u5df2\u7184\u706f\u5c31\u5bdd\uff0c\u504c\u5927\u7684 \u5e84\u56ed\u53ea\u5269\u4e0b\u5be5\u5be5\u51e0\u70b9\u706f\u706b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u662f\u4e2a\u5f88\u80fd\u5e72\u7684\u5973\u4eba\uff0c\u4e5f\u662f\u4e2a\u5f88\u597d\u7684\u5e2e\u624b\uff0c\u5979\u5df2\u4e70\u901a\u4e86\u7533\u5bb6\u4e00\u4e2a\u4ec6\u4eba\uff0c\u4e3a\u5979\u753b\u4e86\u5f20 \u5f88\u8be6\u7ec6\u7684\u56fe\uff0c\u54ea\u91cc\u662f\u5927\u5385\uff0c\u54ea\u91cc\u662f\u4e0b\u623f\uff0c\u54ea\u91cc\u662f\u7533\u8001\u4e09\u7684\u5bdd\u5ba4\uff0c\u8fd9\u5f20\u56fe\u4e0a\u90fd\u753b\u5f97\u975e\u5e38\u8be6\u7ec6\u6e05 \u695a\u3002 \u3000\u3000\u6240\u4ee5\u963f\u98de\u5e76\u6ca1\u6709\u8d39\u4ec0\u4e48\u4e8b\u5c31\u627e\u5230\u4e86\u7533\u8001\u4e09\u3002 \u3000\u3000\u7533\u8001\u4e09\u8fd8\u6ca1\u6709\u7761\uff0c\u5c4b\u5b50\u91cc\u8fd8\u4eae\u7740\u706f\uff0c\u8fd9\u7cbe\u660e\u7684\u751f\u610f\u4eba\u5934\u53d1\u5df2\u82b1\u767d\uff0c\u6b64\u523b\u6b63\u5728\u706f\u4e0b\u62e8\u7740\u7b97 \u76d8\uff0c\u6e05\u7b97\u4e00\u5929\u7684\u5e10\u76ee\u3002 \u3000\u3000\u4ed6\u7b97\u76d8\u6253\u5f97\u5e76\u4e0d\u5feb\uff0c\u56e0\u4e3a\u4ed6\u7684\u624b\u6307\u5f88\u77ed\uff0c\u98df\u6307\uff0c\u4e2d\u6307\uff0c\u65e0\u540d\u6307\uff0c\u51e0\u4e4e\u90fd\u548c\u5c0f\u6307\u5dee\u4e0d\u591a\u957f \u3002 \u3000\u3000\u4f46\u4ed6\u7684\u624b\u6307\u5374\u5f88\u7c97\uff0c\u8fde\u6307\u7532\u597d\u50cf\u90fd\u6ca1\u6709\uff0c\u8fd9\u517b\u5c0a\u5904\u4f18\u7684\u6d4a\u4e16\u516c\u5b50\uff0c\u600e\u4f1a\u6709\u8fd9\u4e48\u4e00\u53cc\u6316\u7164 \u5de5\u4eba\u822c\u7c97\u7cd9\u7684\u624b\uff1f \u3000\u3000\u539f\u6765\u7533\u8001\u4e09\u5c0f\u65f6\u5019\u987d\u52a3\u4e0d\u582a\uff0c\u66fe\u7ecf\u88ab\u4ed6\u7236\u4eb2\u8d76\u51fa\u53bb\u8fc7\uff0c\u5728\u5916\u9762\u6df7\u4e86\u4e94\u5e74\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6 \u6df7\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u6709\u4eba\u8bf4\u4e4b\u4e94\u5e74\u4ed6\u505a\u4e86\u53eb\u5316\u5b50\uff0c\u4e5f\u6709\u4eba\u8bf4\u4ed6\u5165\u4e86\u5c11\u6797\u5bfa\uff0c\u4ece\u6311\u6c34\u7684\u505a\u8d77\uff0c\u867d\u5403\u4e86\u4e0d\u5c11\u82e6\uff0c \u5374\u7ec3\u6210\u4e86\u4e00\u8eab\u6b66\u529f\uff0c\u6240\u4ee5\u540e\u6765\u4ed6\u5144\u5f1f\u6b7b\u7684\u65f6\u5019\uff0c\u867d\u4e5f\u6709\u4e0d\u5c11\u4eba\u6697\u6697\u89c9\u5f97\u6000\u7591\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba \u6562\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u4f20\u8bf4\u5f53\u7136\u4ed6\u5168\u90fd\u5426\u8ba4\uff0c\u4f46\u6709\u4ef6\u4e8b\u662f\u5426\u8ba4\u4e0d\u4e86\u7684\uff0c\u90a3\u5c31\u662f\u4ed6\u7684\u624b\uff0c\u660e\u773c\u4eba\u4e00\u770b\u5c31\u77e5\u9053 \u4ed6\u8fd9\u53cc\u624b\u5fc5\u5b9a\u7ec3\u8fc7\u94c1\u6c99\u638c\u4e00\u7c7b\u7684\u5916\u95e8\u638c\u529b\uff0c\u800c\u4e14\u5df2\u7ec3\u5f97\u6709\u76f8\u5f53\u706b\u5019\uff0c\u5426\u5219\u5802\u623f\u5927\u54e5\u4e5f\u5c31\u4e0d\u4f1a \u5ffd\u7136\u5455\u8840\u800c\u6b7b\u4e86\u3002 \u3000\u3000\u963f\u98de\u7a81\u7136\u63a8\u5f00\u7a97\u5b50\uff0c\u4e00\u63a0\u800c\u5165\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u7528\u4ec0\u4e48\u7279\u6b8a\u7684\u8eab\u6cd5\uff0c\u5f53\u4ed6\u7684\u7684\u5728\u63a8\u7a97\u5b50\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u8dc3\u8d77\uff0c\u7a97\u5b50\u4e00\u5f00\uff0c\u4ed6\u5df2\u7ad9 \u5728\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u7533\u8001\u4e09\u5e76\u4e0d\u662f\u53cd\u5e94\u8fdf\u949d\u7684\u4eba\uff0c\u4f46\u4ed6\u521a\u53d1\u89c9\u7a97\u5b50\u54cd\u52a8\uff0c\u963f\u98de\u5df2\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u4e00 \u4e2a\u4eba\u7684\u884c\u52a8\u80fd\u6709\u8fd9\u79cd\u901f\u5ea6\uff0c\u4ed6\u7adf\u5413\u5446\u4e86\uff0c\u6574\u4e2a\u4eba\u90fd\u50f5\u5728\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u51b7\u51b7\u7684\u76ef\u4f4f\u4ed6\uff0c\u5c31\u597d\u50cf\u5728\u770b\u7740\u4e2a\u6b7b\u4eba\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u4f60\u5c31\u662f\u7533\u8001\u4e09\uff1f \u3000\u3000\u7533\u8001\u4e09\u4e0d\u505c\u7684\u70b9\u5934\uff0c\u4eff\u4f5b\u9664\u4e86\u70b9\u5934\u5916\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u4f1a\u505a\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6211\u662f\u6765\u5e72\u4ec0\u4e48\u7684\uff1f \u3000\u3000\u7533\u8001\u4e09\u8fd8\u662f\u53ea\u6709\u4e0d\u505c\u7684\u70b9\u5934\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f \u3000\u3000\u8fd9\u6b21\u7533\u8001\u4e09\u4e0d\u518d\u70b9\u5934\uff0c\u5374\u5728\u6447\u5934\u4e86\u3002 \u3000\u3000\u963f\u98de\u7684\u5251\u5df2\u62d4\u51fa\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u963f\u6084\u5fc3\u91cc\u7a81\u7136\u89c9\u51fa\u4e00\u79cd\u4e0d\u8be6\u7684\u8b66\u5146\uff0c\u8fd9\u672c\u662f\u91ce\u517d\u72ec\u5177 \u7684\u672c\u80fd\uff0c\u5c31\u5b9b\u5982\u4e00\u53ea\u5154\u5b50\u7a81\u7136\u53d1\u89c9\u6709\u6076\u72fc\u5728\u6697\u4e2d\u7aa5\u89c6\uff0c\u867d\u7136\u4ed6\u5e76\u6ca1\u6709\u542c\u5230\u4efb\u4f55\u58f0\u97f3\uff0c\u66f4\u6ca1\u6709 \u770b\u5230\u90a3\u53ea\u72fc\u7684\u5f71\u5b50\u3002 \u3000\u3000\u963f\u98de\u4e0d\u6562\u518d\u72b9\u7591\uff0c\u4e00\u5251\u523a\u51fa\uff01 \u3000\u3000\u5251\u5149\u5982\u6d41\u661f\u822c\u523a\u5411\u7533\u8001\u4e09\u80f8\u819b\uff0c\u53ea\u542c\u53ee\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u8fd9\u4e00\u5251\u7adf\u5982\u523a\u5728\u94a2\u94c1\u4e4b\u4e0a\u3002 \u3000\u3000\u539f\u6765\u7533\u8001\u4e09\u80f8\u524d\u85cf\u7740\u5757\u94a2\u677f\uff0c\u4e5f\u5c31\u96be\u602a\u4ed6\u523a\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u4e00\u5251\u523a\u51fa\uff0c\u7533\u8001\u4e09\u7684\u4eba\u7acb\u523b\u6eda\u5230\u684c\u4e0b\uff0c\u963f\u98de\u7684\u8eab\u5b50\u5374\u5df2\u51cc\u7a7a\u63a0\u8d77\uff0c\u4ed6\u5df2\u77e5\u9047\u9669\uff0c\u4f46\u6c42\u901f \u9000\u3002 \u3000\u3000\u4f46\u4ed6\u6bd5\u7adf\u8fd8\u662f\u8fdf\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5728\u8fd9\u65f6\uff0c\u5c4b\u9876\u4e0a\u5df2\u6709\u4e00\u5f20\u5de8\u7f51\u6492\u4e0b\uff0c\u8fd9\u662f\u5f20\u548c\u6574\u4e2a\u5c4b\u5b50\u540c\u6837\u5927\u5c0f\u7684\u7f51\uff0c\u53ea\u8981\u662f\u5728\u8fd9\u5c4b\u91cc \u7684\u4eba\uff0c\u65e0\u8bba\u8c01\u90fd\u65e0\u6cd5\u9003\u907f\u3002 \u3000\u3000\u963f\u98de\u8eab\u5b50\u521a\u63a0\u8d77\uff0c\u5df2\u88ab\u7f51\u4f4f\u3002 \u3000\u3000\u5657\u7684\uff0c\u4ed6\u5df2\u88ab\u7f51\u7ed3\u7ea0\u7f20\uff0c\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u65f6\u4ed6\u7684\u5fc3\u60c5\u65e2\u975e\u6124\u6012\uff0c\u4e5f\u975e\u60ca\u614c\uff0c\u53ea\u662f\u611f\u89c9\u5230\u4e00\u79cd\u6df1\u6c89\u7684\u60b2\xd7\uff0c\u56e0\u4e3a\u4ed6\u5ffd \u7136\u4e86\u89e3\u5230\u4e00\u53ea\u731b\u517d\u88ab\u730e\u4eba\u7684\u7f51\u6355\u6349\u5230\u65f6\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u800c\u91ce\u517d\u5374\u6c38\u8fdc\u65e0\u6cd5\u4e86\u89e3\u730e\u4eba\u4e3a\u4f55\u8981\u5f20\u7f51\u3002 \u3000\u3000\u963f\u98de\u4e0d\u518d\u6323\u624e\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6323\u624e\u5df2\u65e0\u7528\uff01 \u3000\u3000\u8fd9\u65f6\u5df2\u6709\u4e24\u6761\u4eba\u5f71\u98de\u9e1f\u822c\u843d\u5728\u7f51\u4e0a\uff0c\u4e24\u4eba\u624b\u4e2d\u5404\u62ff\u7740\u4e2a\u5f88\u957f\u7684\u767d\u8721\u7aff\u5b50\uff0c\u957f\u7aff\u6025\u70b9\uff0c\u963f \u98de\u5df2\u88ab\u70b9\u4e86\u516b\u4e5d\u5904\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u4e24\u6761\u4eba\u5f71\u6b63\u662f\u5c11\u6797\u5bfa\u7684\u5fc3\u9274\u5927\u5e08\u548c\u5e73\u6e56\u767e\u6653\u751f\u3002 \u3000\u3000\u7533\u8001\u4e09\u5df2\u4e0d\u5728\u684c\u4e0b\u4e86\uff0c\u684c\u4e0b\u663e\u7136\u53e6\u6709\u5730\u9053\u3002 \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u6839\u672c\u5c31\u662f\u4e2a\u9677\u9631\u3002 \u3000\u3000\u767e\u6653\u751f\u6ee1\u9762\u5f97\u610f\u4e4b\u8272\uff0c\u7b11\u9053\uff1a\u6211\u65e9\u5c31\u7b97\u51c6\u4f60\u8981\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u4f60\u670d\u6c14\u4e86\u4e48\uff1f \u3000\u3000\u963f\u98de\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u867d\u7136\u4ed6\u7a74\u9053\u88ab\u70b9\u540e\u8fd8\u662f\u53ef\u4ee5\u51fa\u58f0\uff0c\u4f46\u4ed6\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u4e5f\u6ca1\u6709\u95ee\uff1a\u4f60\u4eec\u600e\u4f1a\u7b97\u51c6\u6211\u8981 \u5230\u8fd9\u91cc\u6765\uff1f \u3000\u3000\u4ed6\u773c\u775b\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u50cf\u662f\u5df2\u5168\u65e0\u601d\u60f3\u3002 \u3000\u3000\u4ed6\u662f\u5df2\u4e0d\u80fd\u60f3\uff0c\u8fd8\u662f\u4e0d\u613f\u60f3\uff1f\u4e0d\u5fcd\u60f3\u3002 \u3000\u3000\u767e\u6653\u751f\u60a0\u7136\u9053\uff1a\u6211\u77e5\u9053\u4f60\u662f\u674e\u5bfb\u6b22\u7684\u670b\u53cb\u4e3a\u4e86\u8981\u6551\u674e\u5bfb\u6b22\uff0c\u624d\u5192\u5145\u6885\u82b1\u76d7\u2026\u2026 \u3000\u3000\u963f\u98de\u5389\u58f0\u9053\uff1a\u6211\u5c31\u662f\u6885\u82b1\u76d7\uff0c\u7528\u4e0d\u7740\u5192\u5145\uff0c\u6211\u4e5f\u4e0d\u8ba4\u5f97\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u54e6\u2014\u2014\u5fc3\u9274\u5e08\u5144\uff0c\u4ed6\u8bf4\u4ed6\u5c31\u662f\u6885\u82b1\u76d7\uff0c\u4f60\u53ef\u76f8\u4fe1\uff1f \u3000\u3000\u5fc3\u9274\u9053\uff1a\u4e0d\u4fe1\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u8fd9\u5012\u7684\u786e\u5f88\u96be\u8bc1\u660e\u2014\u2014\u5fc3\u9274\u5e08\u5144\uff0c\u4f60\u53ef\u8bb0\u5f97\u8f70\u5929\u96f7\u662f\u6b7b\u5728\u8c01\u624b\u4e0a\u7684\u4e48\uff1f \u3000\u3000\u5fc3\u9274\u9053\uff1a\u6885\u82b1\u76d7\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u4e5f\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u3000\u3000\u5fc3\u9274\u9053\uff1a\u4ed6\xd7\u8eab\u4e0a\u867d\u4e5f\u6709\u6885\u82b1\u6807\u5fd7\uff0c\u4f46\u81f4\u547d\u4f24\u5374\u5728\u7384\u673a\u7a74\u4e0a\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u6885\u82b1\u76d7\u60f3\u5fc5\u4e5f\u662f\u70b9\u7a74\u7684\u9ad8\u624b\u4e86\u3002 \u3000\u3000\u5fc3\u9274\u9053\uff1a\u6b63\u662f\u3002 \u3000\u3000\u767e\u6653\u751f\u7b11\u4e86\u7b11\uff0c\u8f6c\u5411\u963f\u98de\u9053\uff1a\u53ea\u8981\u4f60\u80fd\u8bf4\u51fa\u6211\u4eec\u65b9\u624d\u70b9\u4e86\u4f60\u54ea\u51e0\u5904\u7a74\u9053\uff0c\u6211\u4eec\u5c31\u627f\u8ba4\u4f60 \u662f\u6885\u82b1\u76d7\uff0c\u800c\u4e14\u7acb\u523b\u653e\u4e86\u674e\u5bfb\u6b22\uff0c\u8fd9\u6837\u505a\u4f60\u6ee1\u610f\u4e86\u4e48\uff1f \u3000\u3000\u963f\u98de\u54ac\u7d27\u4e86\u7259\u9f7f\uff0c\u5df2\u54ac\u51fa\u8840\u6765\u3002 \u3000\u3000\u767e\u6653\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u771f\u4e0d\u6127\u662f\u674e\u5bfb\u6b22\u7684\u597d\u670b\u53cb\uff0c\u4e3a\u4e86\u4ed6\uff0c\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\uff0c\u5374\u4e0d\u77e5\u4ed6 \u5bf9\u4f60\u53c8\uff1f\u4eba\u8981\u4ed6\u80af\u4e3a\u4f60\u8d70\u51fa\u90a3\u95f4\u5c4b\u5b50\uff0c\u4e5f\u5c31\u7b97\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u676f\u4e2d\u6709\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u676f\u5728\u624b\u3002 \u3000\u3000\u89d2\u843d\u4e0a\u5750\u7740\u4e2a\u5f88\u7ea4\u79c0\uff0c\u5f88\u6587\u5f31\u7684\u50e7\u4eba\uff0c\u867d\u7136\u5df2\u8fc7\u4e2d\u5e74\uff0c\u4f46\u5e76\u4e0d\u663e\u5f97\u79c0\u82cd\u8001\uff0c\u770b\u6765\u5e26\u7740\u5f88 \u6d53\u7684\u4e66\u5377\u6c14\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u4ed6\u5c31\u662f\u5c11\u6797\u5bfa\u4e2d\u6700\u7cbe\u7ec3\u7684\u5fc3\u6811\u5927\u5e08\u3002 \u3000\u3000\u4ed6\u867d\u5df2\u505a\u4e86\u674e\u5bfb\u6b22\u7684\u4eba\u8d28\uff0c\u4f46\u795e\u60c5\u4e4b\u95f4\u672a\u663e\u5f97\u5f88\u6124\u6012\uff0c\u5374\u663e\u5f97\u5f88\u6c89\u75db\uff0c\u4e00\u76f4\u9759\u9759\u7684\u5750\u5728 \u90a3\u91cc\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u5411\u5fc3\u6811\u4e3e\u676f\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u60f3\u4e0d\u5230\u5c11\u6797\u5bfa\u5c45\u7136\u4e5f\u6709\u8fd9\u6837\u7684\u597d\u9152\uff0c\u559d\u4e00\u676f\u5982\u4f55\uff1f \u3000\u3000\u5fc3\u6811\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u5728\u4ee4\u5e08\u5144\u7684\u9057\u8715\u65c1\u559d\u9152\uff0c\u4f60\u662f\u5426\u89c9\u5f97\u6211\u6709\u4e9b\u4e0d\u656c\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u9152\u8d28\u6700\u7eaf\uff0c\u66f4\u7eaf\u4e8e\u6c34\uff0c\u662f\u4ee5\u796d\u7940\u7956\u5148\u5929\u5730\u65f6\u90fd\u4ee5\u9152\u4e3a\u793c\uff0c\u65e0\u8bba\u5728\u4efb\u4f55\u5730\u65b9\uff0c\u90fd \u7edd\u65e0\u4e1d\u6beb\u4e0d\u656c\u4e4b\u5904\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9644\u638c\u9053\uff1a\u8bf4\u5f97\u597d\uff0c\u96be\u602a\u4e00\u5165\u7ff0\u82d1\uff0c\u4fbf\u7b80\u5728\u5e1d\u5fc3\u3002 \u3000\u3000\u5fc3\u6811\u5927\u5e08\u5e73\u9759\u7684\u9762\u8272\u7adf\u53d8\u4e86\u53d8\uff0c\u50cf\u662f\u88ab\u4eba\u89e6\u53ca\u4e86\u9690\u75db\u3002 \u3000\u3000\u63a5\u7740\u6c89\u91cd\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u795e\u60c5\u663e\u5f97\u66f4\u54c0\u75db\uff0c\u5374\u4e5f\u4e0d\u77e5\u662f\u4e3a\u4e86\u6b7b\u8005\uff0c\u8fd8\u662f\u4e3a\u4e86\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u7740\u676f\u4e2d\u7425\u73c0\u8272\u7684\u9152\uff0c\u7a81\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5f90\u5f90\u9053\uff1a\u8001\u5b9e\u8bf4\uff0c\u6211\u5b9e\u672a\u60f3\u5230\u8fd9\u6b21 \u6551\u6211\u7684\u4f1a\u662f\u4f60\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6211\u5e76\u672a\u6551\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5341\u56db\u5e74\u524d\uff0c\u6211\u5f03\u5b98\u5f52\u9690\uff0c\u867d\u8bf4\u662f\u4e3a\u4e86\u538c\u5026\u529f\u540d\uff0c\u4f46\u82e5\u975e\u4e3a\u4e86\u4f60\u90a3\u4e00\u9053\u5f39\u7ae0\uff0c \u8bf4\u6211\u8eab\u5728\u5b98\u5e9c\uff0c\u7ed3\u4ea4\u532a\u7c7b\uff0c\u6211\u4e5f\u8bb8\u8fd8\u4e0b\u4e0d\u4e86\u90a3\u51b3\u5fc3\u3002 \u3000\u3000\u5fc3\u6811\u95ed\u4e0a\u773c\u775b\uff0c\u9eef\u7136\u9053\uff1a\u6614\u65e5\u5f39\u52be\u4e50\u7684\u80e1\u4e91\u5180\u65e9\u5df2\u6b7b\u4e86\uff0c\u4f60\u4fdd\u5fc5\u518d\u63d0\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\uff0c\u4e00\u5165\u4f5b\u95e8\uff0c\u4fbf\u5982\u4e24\u4e16\u4e3a\u4eba\uff0c\u4f46\u6211\u81ea\u59cb\u81f3\u7ec8\u90fd\u672a\u57cb\u6028\u8fc7\u4f60\uff0c\u90a3\u65f6\u4f60\u8eab\u4e3a \u5fa1\u53f2\uff0c\u81ea\u7136\u8981\u5c3d\u4e3a\u5b98\u8fd9\u8d23\u2014\u2014 \u3000\u3000\u5fc3\u6811\u7684\u795e\u60c5\u4f3c\u4e4e\u6709\u4e9b\u6fc0\u52a8\uff0c\u6c89\u58f0\u9053\uff1a\u4f60\u5f03\u5b98\u4e4b\u540e\u4e0d\u4e45\uff0c\u6211\u4e5f\u9690\u8eab\u4f5b\u95e8\uff0c\u4e3a\u7684\u5c31\u662f\u81ea\u89c9\u8a00 \u591a\u5fc5\u4eba\uff0c\u5374\u4e0d\u60f3\u6bd5\u7adf\u8fd8\u662f\u9047\u7740\u4f60\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u66f4\u672a\u60f3\u5230\u6614\u65e5\u6587\u9152\u98ce\u6d41\u7684\u94c1\u80c6\u5fa1\u53f2\uff0c\u4eca\u65e5\u7adf\u53d8\u505a\u4e86\u4fee\u884c\u529f\u6df1\u7684\u5f97\u9053\u9ad8\u50e7\uff0c \u800c\u4e14\u4f1a\u5728\u6211\u751f\u6b7b\u4e00\u53d1\u65f6\uff0c\u6551\u4e86\u6211\u4e00\u547d\u3002 \u3000\u3000\u5fc3\u6811\u5f20\u5f00\u773c\u775b\uff0c\u5389\u58f0\u9053\uff1a\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u6211\u5e76\u672a\u6551\u4f60\u3002\u800c\u662f\u6211\u81ea\u5df1\u529f\u529b\u4e0d\u591f\uff0c\u624d\u4f1a\u88ab\u4f60\u6240 \u52ab\u6301\uff0c\u4f60\u4e07\u4e07\u4e0d\u53ef\u5bf9\u6211\u7a0d\u5b58\u611f\u6fc0\u4e4b\u5fc3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u82e5\u975e\u4f60\u5728\u5c4b\u4e2d\u5bf9\u6211\u793a\u610f\uff0c\u6211\u4e5f\u672a\u5fc5\u4f1a\u95ef\u5165\u8fd9\u91cc\uff0c\u53f3\u975e\u4f60\u5168\u65e0\u62b5\u6297\u4e4b\u610f\uff0c\u6211 \u66f4\u65e0\u6cd5\u5c06\u4f60\u7559\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u5fc3\u6811\u5634\u89d2\u7275\u52a8\uff0c\u5374\u672a\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u51fa\u5bb6\u4eba\u620e\u6253\u8bf3\u8bed\uff0c\u4f55\u51b5\uff0c\u8fd9\u91cc\u53c8\u53ea\u6709\u4f60\u6211\u4e24\u4eba\u3002 \u3000\u3000\u5fc3\u6811\u5ffd\u7136\u9053\uff1a\u7eb5\u7136\u6211\u5bf9\u4f60\u6709\u76f8\u52a9\u4e4b\u610f\uff0c\u4e3a\u7684\u4e5f\u5e76\u975e\u6614\u65e5\u4e4b\u60c5\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f3c\u4e4e\u5e76\u672a\u89c9\u5f97\u60ca\u5947\uff0c\u6b63\u8272\u9053\uff1a\u90a3\u4e48\u4f60\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u5fc3\u6811\u51e0\u5907\u6b32\u8a00\u53c8\u6b62\uff0c\u4f3c\u6709\u5f88\u5927\u7684\u96be\u8a00\u4e4b\u9690\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6ca1\u6709\u50ac\u4fc3\u4ed6\uff0c\u53ea\u662f\u6162\u6162\u7684\u5c06\u676f\u4e2d\u9152\u559d\u5b8c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u7a97\u5916\u4e00\u4eba\u559d\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u4f60\u63a8\u5f00\u7a97\u5b50\u6765\u77a7\u77a7\u3002 \u3000\u3000\u8fd9\u662f\u5fc3\u9274\u5927\u5e08\u7684\u58f0\u97f3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u4eba\u7a81\u7136\u95f4\u5df2\u5230\u4e86\u7a97\u53e3\uff0c\u4ece\u7a97\u9699\u95f4\u5411\u5916\u671b\u4e86\u4e00\u773c\u2014\u2014 \u3000\u3000\u4ed6\u7684\u8138\u8272\u7acb\u523b\u53d8\u4e86\uff01 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u963f\u98de\u7adf\u4f1a\u843d\u5728\u5bf9\u65b9\u624b\u91cc\u3002 \u3000\u3000\u767e\u6653\u751f\u8d1f\u7684\u800c\u7acb\uff0c\u6ee1\u9762\u4ff1\u662f\u5f97\u610f\u4e4b\u8272\uff0c\u60a0\u7136\u9053\uff1a\u674e\u63a2\u82b1\uff0c\u4f60\u603b\u8be5\u8ba4\u5f97\u4ed6\u5427\uff0c\u4ed6\u4e3a\u4e86\u4fdd\u4f4f \u4f60\uff0c\u4e0d\u60dc\u80cc\u8d1f\u6885\u82b1\u76d7\u4e4b\u6076\u540d\uff0c\u4f60\u5bf9\u4ed6\u53c8\u5982\u4f55\uff1f \u3000\u3000\u5fc3\u9274\u5389\u58f0\u9053\uff1a\u4f60\u82e5\u60f3\u4fdd\u5168\u4ed6\u7684\u6027\u547d\uff0c\u6700\u597d\u7acb\u523b\u8d1f\u624b\u5c31\u64d2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u7adf\u4e5f\u6709\u4e9b\u98a4\u6296\u8d77\u6765\uff0c\u4ed6\u770b\u4e0d\u5230\u963f\u98de\u7684\u8138\uff0c\u56e0\u4e3a\u963f\u98de\u6574\u4e2a\u4eba\u90fd\u4ed7\u5728\u5730\u4e0a\uff0c\u4f3c\u5df2 \u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u5fc3\u9274\u5ffd\u7136\u6380\u8d77\u963f\u98de\u7684\u5934\u6765\uff0c\u5927\u58f0\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u6211\u7ed9\u4f60\u4e24\u4e2a\u65f6\u8fb0\uff0c\u65e5\u843d\u524d\u4f60\u82e5\u8fd8\u4e0d\u5c06\u6211\u7684 \u516d\u5e08\u5144\u597d\u597d\u9001\u51fa\u6765\uff0c\u5c31\u518d\u4e5f\u89c1\u4e0d\u7740\u4f60\u7684\u597d\u53cb\u4e86\u3002 \u3000\u3000\u767e\u6653\u751f\u60a0\u60a0\u9053\uff1a\u674e\u63a2\u82b1\uff0c\u6b64\u4eba\u5bf9\u4f60\u4e0d\u9519\uff0c\u4f60\u4e5f\u83ab\u8981\u4e8f\u8d1f\u4e86\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f0f\u5728\u7a97\u5b50\u4e0a\uff0c\u4f3c\u4e5f\u9ebb\u6728\u3002 \u3000\u3000\u4ed6\u770b\u5230\u963f\u98de\u88ab\u4ed6\u4eec\u50cf\u72d7\u4e00\u6837\u62d6\u4e86\u51fa\u53bb\uff0c\u4ed6\u4e5f\u770b\u5230\u963f\u98de\u8eab\u4e0a\u7684\u4f24\u524d\uff0c\u4ed6\u77e5\u9053\u963f\u98de\u5fc5\u5b9a\u5df2\u53d7 \u4e86\u8bb8\u591a\u82e6\u3002 \u3000\u3000\u4f46\u8fd9\u5014\u5f3a\u7684\u5c11\u5e74\u5374\u7edd\u672a\u53d1\u51fa\u534a\u58f0\u547b\u541f\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u5411\u7a97\u5b50\u8fd9\u8fb9\u77a7\u4e86\u4e00\u773c\uff0c\u76ee\u5149\u7adf\u662f\u8bf4\u4e0d\u51fa\u7684\u5e73\u9759\uff0c\u50cf\u662f\u5728\u544a\u8bc9\u674e\u5bfb\u6b22\uff0c\u4ed6\u5bf9\u6b7b\u5e76\u65e0 \u754f\u60e7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u9053\uff1a\u597d\u670b\u53cb\uff0c\u597d\u670b\u53cb\uff0c\u2014\u2014\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4f60\u4e0d\u613f\u6211\u53bb\u6551\u4f60\u3002 \u3000\u3000\u5fc3\u6811\u4e00\u76f4\u5728\u51dd\u89c6\u7740\u4ed6\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u4f46\u4f60\u7684\u610f\u601d\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u5e72\u4e86\u4e09\u676f\uff0c\u8d1f\u624b\u800c\u7acb\uff0c\u5fae\u7b11\u9053\uff1a\u6211\u5df2\u51c6\u5907\u8d1f\u624b\u5c31\u7f1a\uff0c\u4f60\u968f\u65f6\u90fd\u53ef\u7ed1\u6211\u51fa\u53bb\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4f60\u4e00\u51fa\u53bb\u4fbf\u5fc5\u6b7b\u65e0\u7591\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f46\u4f60\u8fd8\u662f\u8981\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u8fd8\u662f\u8981\u51fa\u53bb\u3002\u4ed6\u56de\u7b54\u5f97\u7b80\u77ed\u800c\u575a\u5b9a\uff0c\u7adf\u4f3c\u5168\u65e0\u8003\u8651\u7684\u4f59\u5730\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f60\u5982\u6b64\u505a\u5c82\u975e\u592a\u8fc2\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8083\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u6bcf\u4e2a\u4eba\u8fd9\u4e00\u751f\u4e2d\u90fd\u96be\u514d\u8981\u505a\u51e0\u4ef6\u7535\u98ce\u6247\u8822\u4e4b\u4e8b\u7684\uff0c\u4eba\u4eba\u90fd\u53ea\u505a\u806a \u660e\u4e8b\uff0c\u4eba\u751f\u5c82\u975e\u5c31\u4f1a\u53d8\u5f97\u66f4\u65e0\u8da3\u4e86\uff1f \u3000\u3000\u5fc3\u6811\u50cf\u662f\u5728\u4ed4\u7ec6\u5480\u56bc\u4ed6\u5c82\u53e5\u8bdd\u4e2d\u7684\u6ecb\u5473\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u5927\u592a\u592b\u6709\u6240\u4e0d\u4e3a\uff0c\u6709\u6240\u5fc5\u4e3a\uff0c\u4f60\u7eb5 \u7136\u660e\u77e5\u4ed6\u975e\u6b7b\u4e0d\u53ef\uff0c\u8fd8\u662f\u8981\u8fd9\u4e48\u505a\uff0c\u53ea\u56e0\u4e3a\u4f60\u975e\u505a\u4e0d\u53ef\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u4f60\u603b\u7b97\u4e5f\u662f\u6211\u7684\u77e5\u5df2\u3002 \u3000\u3000\u5fc3\u6811\u5583\u5583\u9053\uff1a\u4e49\u6c14\u5f53\u5148\uff0c\u751f\u6b7b\u4e0d\u8ba1\uff0c\u674e\u5bfb\u6b22\u679c\u7136\u4e0d\u6127\u662f\u674e\u5bfb\u6b22\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u770b\u4ed6\uff0c\u9053\uff1a\u6211\u5148\u51fa\u53bb\uff0c\u5c31\u6b64\u522b\u8fc7\u3002 \u3000\u3000\u5fc3\u6811\u5ffd\u7136\u9053\uff1a\u4e14\u6162\uff01 \u3000\u3000\u4ed6\u50cf\u662f\u5df2\u4e0b\u4e86\u5f88\u5927\u7684\u51b3\u5fc3\uff0c\u76ee\u5149\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u65b9\u624d\u8fd8\u6709\u53e5\u8bdd\u6ca1\u6709\u8bf4\u5b8c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6211\u79c0\u8bf4\u8fc7\uff0c\u6211\u6551\u4f60\u522b\u6709\u539f\u56e0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\uff1a\u55ef\u3002 \u3000\u3000\u5fc3\u6811\u795e\u60c5\u51dd\u91cd\uff0c\u9053\uff1a\u8fd9\u662f\u6211\u5c11\u6797\u672c\u95e8\u7684\u79d8\u5bc6\uff0c\u800c\u4e14\u5173\u7cfb\u91cd\u5927\uff0c\u6211\u4e0d\u613f\u5411\u4f60\u63d0\u8d77\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u56de\u8f6c\u8eab\uff0c\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5fc3\u6811\u53c8\u9053\uff1a\u5c11\u6797\u85cf\u7ecf\u4e4b\u4e30\uff0c\u51a0\u7edd\u5929\u4e0b\uff0c\u5171\u4e2d\u975e\u4fbf\u6709\u4e0d\u5c11\u4f5b\u95e8\u91cd\u5178\uff0c\u4e5f\u6709\u8bb8\u591a\u6b66\u6797\u4e2d\u7684\u4e0d \u4f20\u4e4b\u79d8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u6211\u4e5f\u77e5\u9053\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u767e\u5e74\u4ee5\u6765\uff0c\u6c5f\u6e56\u4e2d\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u5984\u751f\u8d2a\u5ff5\uff0c\u8981\u5230\u5c11\u6797\u5bfa\u6765\u76d7\u53d6\u85cf\u7ecf\uff0c\u4f46\u5374\u4ece\u6765 \u672a\u6709\u4e00\u4eba\u80fd\u5982\u613f\u5f97\u624b\uff0c\u5168\u8eab\u800c\u9000\u7684\u3002 \u3000\u3000\u4ed6\u8083\u7136\u63a5\u9053\uff1a\u51fa\u5bb6\u4eba\u867d\u620e\u55d4\u620e\u6740\uff0c\u4f46\u85cf\u7ecf\u4e43\u5c11\u6797\u4e4b\u6839\u672c\uff0c\u662f\u4ee5\u65e0\u8bba\u4ec0\u4e48\u4eba\u6562\u751f\u6b64\u5ff5\uff0c\u5c11 \u6797\u95e8\u4e0b\u90fd\u4e0d\u60dc\u4e0e\u4e4b\u5468\u65cb\u5230\u5e95\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd1\u6765\u6211\u5012\u5f88\u5c11\u542c\u5230\u6709\u4eba\u6562\u6253\u8fd9\u4e3b\u610f\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f60\u662f\u5916\u4eba\uff0c\u81ea\u7136\u4e0d\u77e5\u5185\u60c5\uff0c\u5176\u5b9e\u8fd9\u4e24\u5e74\u6765\uff0c\u672c\u5bfa\u85cf\u7ecf\u5df2\u6709\u4e03\u6b21\u88ab\u7a83\uff0c\u9664\u4e86\u4e00\u90e8 \u8010\u5e73\u5fc3\u7ecf\u5916\uff0c\u5176\u4f59\u90fd\u662f\u4e45\u5df2\u7edd\u4f20\u7684\u6b66\u6797\u79d8\u7b08\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7981\u8038\u7136\u5931\u58f0\uff0c\u9053\uff1a\u8fd9\u76d7\u7ecf\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6700\u5947\u602a\u7684\u5c31\u662f\u8fd9\u4e03\u6b21\u5931\u7a83\u4e8b\u4ef6\uff0c\u4e8b\u5148\u65e2\u65e0\u5146\uff0c\u4e8b\u540e\u4e5f\u6beb\u65e0\u7ebf\u7d22\u53ef\u5bfb\uff0c\u7b2c\u4e00\u3001\u4e8c \u6b21\u53d1\u751f\u4e4b\u540e\uff0c\u85cf\u7ecf\u9601\u7684\u620e\u5907\u81ea\u7136\u66f4\u68ee\u4e25\uff0c\u4f46\u5931\u7a83\u7684\u4e8b\u4ecd\u662f\u63a5\u4e8c\u8fde\u4e09\u7684\u53d1\u751f\uff0c\u672c\u6765\u638c\u85cf\u7ecf\u9601\u7684 \u4e09\u5e08\u5144\uff0c\u4e5f\u56e0\u6b64\u5f15\u548e\u9000\u4f4d\uff0c\u9762\u58c1\u601d\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5982\u6b64\u91cd\u5927\u7684\u4e8b\uff0c\u6c5f\u6e56\u4e2d\u600e\u5730\u5168\u65e0\u98ce\u95fb\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u5c31\u56e0\u6b64\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u6240\u4ee5\u638c\u95e8\u5e08\u5144\u518d\u4e09\u5631\u5490\u4e25\u5b88\u79d8\u5bc6\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u77e5\u9053\u6b64 \u4e8b\u7684\u8fde\u4f60\u4e5f\u53ea\u4e0d\u8fc7\u4e5d\u4e2a\u4eba\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9664\u4e86\u7236\u4eec\u9996\u5ea7\u4e03\u4f4d\u5916\uff0c\u672c\u6765\u8fd8\u6709\u8c01\u77e5\u9053\u6b64\u4e8b\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u767e\u6653\u751f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u4ed6\u53c2\u4e0e\u7684\u4e8b\u5012\u5f53\u771f\u4e0d\u5c11\uff01 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4e09\u5e08\u5144\u662f\u6211\u5e08\u5144\u4e2d\u6700\u8c28\u614e\u6301\u91cd\u7684\u4eba\uff0c\u4ed6\u9000\u4f4d\u4e4b\u540e\uff0c\u85cf\u7ecf\u9601\u4fbf\u7531\u6211\u548c\u4e8c\u5e08\u5144\u8d1f\u8d23 \uff0c\u81f3\u4eca\u53ea\u4e0d\u8fc7\u624d\u534a\u4e2a\u6708\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u65e2\u7136\u8d1f\u6709\u91cd\u8d23\uff0c\u8fd9\u6b21\u4e3a\u4f55\u7adf\u79bb\u5bfa\u800c\u51fa\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u53ea\u56e0\u4e8c\u5e08\u5144\u603b\u6000\u7591\u5931\u7ecf\u4e4b\u4e8b\u4e0e\u6885\u82b1\u76d7\u6709\u5173\uff0c\u662f\u4ee5\u624d\u62a2\u7740\u8981\u53bb\u4e00\u67e5\u7a76\u7adf\uff0c\u8c01\u77e5\u4ed6 \u4e00\u53bb\u7adf\u6210\u6c38\u51b3\u3002 \u3000\u3000\u8bf4\u5230\u8fd9\u65f6\uff0c\u4ed6\u9762\u5bf9\u5fc3\u7709\u9057\u8715\uff0c\u4f3c\u5df2\u6ceb\u7136\u6b32\u6d95\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u7981\u6697\u6697\u53f9\u606f\uff0c\u51fa\u5bb6\u4eba\u867d\u7136\u56db\u5927\u7686\u7a7a\uff0c\u8fd9\u60c5\u5b57\u4e00\u5173\uff0c\u6bd5\u7adf\u8fd8\u662f\u52d8\u4e0d\u7834\u7684\u3002 \u3000\u3000\u5fc3\u6811\u9ed8\u7136\u826f\u4e45\uff0c\u9053\uff1a\u4e8c\u5e08\u5144\u81ea\u5df1\u8001\u6210\u6301\u91cd\uff0c\u79bb\u5bfa\u4e4b\u524d\uff0c\u5df2\u5c06\u6700\u91cd\u8981\u7684\u4e09\u90e8\u85cf\u7ecf\u53d6\u51fa\uff0c\u5206 \u522b\u8302\u5728\u4e09\u4e2a\u9690\u79d8\u4e4b\u5904\uff0c\u9664\u4e86\u638c\u95e8\u5e08\u548c\u6211\u4e4b\u5916\uff0c\u603b\u6ca1\u6709\u7b2c\u4e09\u4eba\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5176\u4e2d\u6709\u4e00\u90e8\u662f\u5426\u5c31\u5728\u8fd9\u5c4b\u5b50\u91cc\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u4e5f\u5c31\u96be\u602a\u4ed6\u4eec\u51fa\u624b\u6709\u5982\u6b64\u591a\u987e\u5fcc\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u5c31\u56e0\u4e3a\u8fd9\u51e0\u6b21\u5931\u7a83\u4e8b\u4ef6\u592a\u8fc7\u79bb\u5947\uff0c\u6240\u4ee5\u4e8c\u5e08\u5144\u548c\u6211\u5728\u79c1\u4e0b\u731c\u6d4b\uff0c\u4e5f\u8ba4\u4e3a\u53ef\u80fd\u662f \u51fa\u81ea\u5185\u8d3c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u5bb9\u9053\uff1a\u5185\u8d3c\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6211\u4eec\u867d\u6709\u6b64\u6000\u7591\uff0c\u4f46\u5374\u4e0d\u6562\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\u9664\u4e86\u6211\u4eec\u9996\u5ea7\u4e03\u4e2a\u4eba\u5916\uff0c\u522b\u7684\u5f1f\u5b50\u8c01 \u4e5f\u4e0d\u80fd\u968f\u610f\u51fa\u5165\u85cf\u7ecf\u9601\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u5077\u7ecf\u7684\u4eba\u6781\u53ef\u80fd\u662f\u4f60\u4eec\u4e03\u4f4d\u5e08\u5f1f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u5fc3\u6811\u6c89\u9ed8\u826f\u4e45\uff0c\u957f\u53f9\u9053\uff1a\u6211\u4eec\u4e03\u4eba\u540c\u95e8\u81f3\u5c11\u5df2\u6709\u5341\u5e74\u4e4b\u4e45\uff0c\u65e0\u8bba\u8c01\u90fd\u5927\u6709\u4e0d\u8be5\uff0c\u662f\u4ee5\u6211 \u4eec\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u5904\u7406\uff0c\u66f4\u4e0d\u80fd\u4e0d\u529b\u6c42\u614e\u91cd\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u53ea\u4e0d\u8fc7\u600e\u6837\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u53ea\u4e0d\u8fc7\u4e8c\u5e08\u79bb\u5bfa\u4e4b\u524d\uff0c\u66fe\u7ecf\u6084\u6084\u5bf9\u6211\u8bf4\uff0c\u4ed6\u5df2\u53d1\u73b0\u6211\u4eec\u4e03\u4eba\u4e2d\u6709\u4e00\u4eba\u5f88\u53ef\u7591\uff0c \u6781\u6709\u53ef\u80fd\u5c31\u662f\u90a3\u5077\u7ecf\u7684\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u8ffd\u95ee\u9053\uff1a\u4ed6\u8bf4\u7684\u662f\u8c01\uff1f \u3000\u3000\u5fc3\u6811\u6447\u4e86\u6447\u5934\uff0c\u53f9\u9053\uff1a\u53ea\u53ef\u60dc\u4ed6\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\u4ed6\u751f\u6015\u9519\u602a\u4e86\u4eba\uff0c\u4ed6\u53ea\u671b\u76d7\u7ecf\u7684\u4eba \u771f\u662f\u6885\u82b1\u76d7\uff0c\u613f\u770b\u5230\u5e08\u95e8\u8499\u7f9e\u2014\u2014 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u58f0\u97f3\u5df2\u6709\u4e9b\u54fd\u54bd\uff0c\u51e0\u9996\u96be\u4ee5\u7ee7\u7eed\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u7684\u8fd9\u756a\u82e6\u5fc3\uff0c\u6211\u4e5f\u61c2\u5f97\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014\u73b0\u5728\u4ed6\u5728\u51a5\u51a5\u4e2d\u773c\u89c1\u7740\u90a3\u4eba\u900d \u9065\u6cd5\u5916\uff0c\u518d\u60f3\u8bf4\u5df2\u4e0d\u80fd\u8bf4\u4e86\uff0c\u4ed6\u5c82\u975e\u8981\u62b1\u61be\u7ec8\u751f\uff0c\u542b\u6068\u4e5d\u6cc9\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4e8c\u5e08\u5e76\u6ca1\u6709\u60f3\u5230\u8fd9\u70b9\uff0c\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u66fe\u5bf9\u6211\u8bf4\uff0c\u4ed6\u6b64\u53bb\u4e07\u4e00\u6709\u4ec0\u4e48\u4e0d\u6d4b\uff0c \u5c31\u8981\u6211\u5c06\u4ed6\u7684\u8bfb\u7ecf\u5256\u8bb0\u62ff\u51fa\u6765\u4e00\u770b\uff0c\u4ed6\u5df2\u5c06\u4ed6\u6240\u6000\u7591\u7684\u90a3\u4e2a\u4eba\u4e4b\u59d3\u540d\u5199\u5728\u5256\u8bb0\u7684\u6700\u540e\u4e00\u9875\u4e0a \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c55\u7709\u9053\uff1a\u90a3\u672c\u5256\u8bb0\u73b0\u5728\u54ea\u91cc\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u672c\u6765\u662f\u548c\u85cf\u7ecf\u5728\u4e00\u8d77\u7684\uff0c\u73b0\u5728\u5df2\u5728\u6211\u8fd9\u91cc\u2014\u2014 \u3000\u3000\u4ed6\u53d6\u51fa\u672c\u6de1\u9ec4\u7684\u7ee2\u518c\uff0c\u674e\u5bfb\u6b22\u7acb\u523b\u63a5\u8fc7\u6765\uff0c\u7ffb\u5230\u6700\u540e\u4e00\u9875\uff0c\u4e0a\u9762\u5199\u7684\u90fd\u662f\u4f5b\u95e8\u8981\u65e8\uff0c\u5e76 \u6ca1\u6709\u4e00\u53e5\u8bdd\u63d0\u5230\u5931\u7ecf\u7684\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u5fc3\u6811\uff0c\u9053\uff1a\u8fd9\u6700\u540e\u4e00\u9875\u83ab\u975e\u5df2\u88ab\u4eba\u6495\u4e0b\u6765\u4e86\uff1f \u3000\u3000\u5fc3\u6811\uff1a\u975e\u4f46\u6700\u540e\u4e00\u9875\u5df2\u88ab\u4eba\u6495\u4e0b\u4e86\uff0c\u90a3\u672c\u85cf\u7ecf\u4e5f\u53d8\u4f5c\u4e86\u767d\u7eb8\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u76d7\u7ecf\u7684\u90a3\u4eba\u60f3\u5fc5\u5df2\u53d1\u73b0\u5fc3\u7709\u5927\u5e08\u6000\u7591\u5230\u4ed6\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u77e5\u9053\u4ed6\u85cf\u7ecf\u4e4b\u5904\u7684\uff0c\u5374\u53ea\u6709\u4f60\u548c\u638c\u95e8\u5fc3\u6e56\u5927\u5e08\u3002 \u3000\u3000\u5fc3\u6811\u7684\u9762\u8272\u5982\u94c5\uff0c\u6c89\u91cd\u7684\u70b9\u5934\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u674e\u589e\u6b22\u9053\uff1a\u96be\u9053\u4f60\u8ba4\u4e3a\u5fc3\u6e56\u5927\u5e08\u5c31\u662f\u2014\u2014\uff0d \u3000\u3000\u5fc3\u6811\u9ed8\u7136\u534a\u664c\uff0c\u9053\uff1a\u8fd9\u5012\u4e0d\u4e00\u5b9a\uff0c\u56e0\u4e3a\u90a3\u4eba\u65e2\u5df2\u53d1\u89c9\u4e8c\u5e08\u5144\u5bf9\u4ed6\u6709\u6240\u6000\u7591\uff0c\u81ea\u7136\u4e5f\u4f1a\u5bf9 \u4e8c\u5e08\u5144\u7684\u884c\u52a8\u5206\u5916\u7559\u610f\uff0c\u4e5f\u8bb8\u5c31\u53ef\u80fd\u56e0\u6b64\u800c\u5728\u6697\u4e2d\u7aa5\u5f97\u4e8c\u5e08\u5144\u7684\u85cf\u79d8\u4e4b\u5904\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u600e\u6837\uff1f \u3000\u3000\u5fc3\u6811\u76ee\u5149\u51dd\u6ce8\u674e\u5bfb\u6b22\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u53ea\u4e0d\u8fc7\u4e8c\u5e08\u56de\u6765\u65f6\u5e76\u6ca1\u6709\u6b7b\uff0c\u7b80\u76f4\u672c\u6765\u4e5f\u4e0d\u81f4\u4e8e\u6b7b\u7684 \uff01 \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u674e\u5bfb\u6b22\u771f\u7684\u4e3a\u4e4b\u8038\u7136\u5931\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u5fc3\u6811\u5927\u5e08\u53cc\u62f3\u7d27\u63e1\uff0c\u63a5\u7740\u9053\uff1a\u6211\u867d\u7136\u5bf9\u4e0b\u6bd2\u5e76\u6ca1\u6709\u4ec0\u4e48\u5f88\u6df1\u7684\u7814\u7a76\uff0c\u4f46\u8fd1\u5e74\u6765\u5bf9\u6b64 \u4e2d\u5178\u7c4d\u5012\u4e5f\u9887\u6709\u6d89\u730e\uff0c\u4e8c\u5e08\u5144\u56de\u6765\u7684\u65f6\u5019\uff0c\u6211\u5df2\u770b\u51fa\u4ed6\u4e2d\u6bd2\u867d\u6df1\uff0c\u4f46\u5374\u7edd\u975e\u65e0\u6551\uff0c\u800c\u4e14\u5728\u77ed \u65f6\u95f4\u4e4b\u5185\u4e5f\u7edd\u4e0d\u4f1a\u6709\u751f\u547d\u4e4b\u5371\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u662f\u8bf4\u2014\u2014\uff0d \u3000\u3000\u5fc3\u6811\u9053\uff1a\u5077\u7ecf\u7684\u90a3\u4eba\u65e2\u77e5\u9053\u79d8\u5bc6\u5df2\u88ab\u4e8c\u5e08\u5144\u53d1\u73b0\u81ea\u7136\u8981\u5c06\u4e4b\u6740\u4e86\u706d\u53e3\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u89c9\u5f97\u8fd9\u5c4b\u5b50\u91cc\u95f7\u5f97\u5f88\uff0c\u51e0\u4e4e\u4ee4\u4eba\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8e31\u4e86\u4e2a\u5708\u5b50\uff0c\u624d\u6c89\u58f0\u95ee\u9053\uff1a\u5fc3\u7709\u56de\u6765\u540e\uff0c\u5012\u8fc7\u8fd9\u5c4b\u5b50\u7684\u6709\u51e0\u4e2a\u4eba\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u5927\u5e08\u5144\u3001\u56db\u5e08\u5144\u3001\u4e94\u5e08\u5144\u548c\u4e03\u5e08\u5f1f\u90fd\u66fe\u8fdb\u6765\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u7684\u610f\u601d\u8bf4\uff0c\u4ed6\u4eec\u90fd\u6709\u53ef\u80fd\u4e0b\u624b\uff1f \u3000\u3000\u5fc3\u6811\u70b9\u4e86\u70b9\u5934\uff0c\u53f9\u9053\uff1a\u8fd9\u662f\u672c\u95e8\u4e4b\u4e0d\u5e78\uff0c\u6211\u672c\u4e0d\u613f\u5bf9\u4f60\u8bf4\u7684\uff0c\u4f46\u73b0\u5728\u6211\u5df2\u53d1\u89c9\u4f60\u7edd\u4e0d\u662f \u51fa\u5356\u670b\u53cb\u7684\u4eba\uff0c\u6240\u4ee5\u6211\u5e0c\u671b\u4f60\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u8981\u6211\u627e\u51fa\u90a3\u51f6\u624b\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u662f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u70af\u70af\uff0c\u76ef\u7740\u4ed6\u7684\u773c\u775b\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u51f6\u624b\u82e5\u662f\u5fc3\u6e56\u5462\uff1f \u3000\u3000\u5fc3\u6811\u7a81\u7136\u6014\u4f4f\u4e86\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u6ee1\u5934\u5927\u6c57\u6d94\u6d94\u800c\u843d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5c31\u7b97\u5c11\u6797\u95e8\u4e0b\u4eba\u4eba\u90fd\u5df2\u77e5\u9053\u5fc3\u6e56\u662f\u51f6\u624b\uff0c\u4e5f\u7edd\u65e0\u4e00\u4eba\u80af\u627f\u8ba4\u7684\uff0c\u662f\u4e48\uff1f \u3000\u3000\u5fc3\u6811\u6ca1\u6709\u8bf4\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u65e0\u8bdd\u53ef\u8bf4\uff0c\u6c5f\u6e56\u4e2d\u4eba\u7d20\u6765\u5c06\u5c11\u6797\u89c6\u4e3a\u540d\u95e8\u6b63\u5b97\uff0c\u5982\u4eca\u5c11\u6797\u82e5\u662f\u6740 \u4eba\u7684\u51f6\u624b\uff0c\u5c11\u6797\u5bfa\u6570\u767e\u5e74\u7684\u58f0\u540d\u548c\u5a01\u671b\u5c82\u975e\u8981\u6bc1\u4e8e\u4e00\u65e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5c31\u7b97\u6211\u80fd\u8bc1\u660e\u5fc3\u6e56\u662f\u51f6\u624b\uff0c\u53ea\u6015\u8fde\u4f60\u4e5f\u4e0d\u80af\u4e3a\u6211\u8bf4\u8bdd\uff0c\u4e3a\u4e86\u4fdd\u5168\u4f60\u4eec\u5c11\u6797\u7684 \u58f0\u540d\uff0c\u4f60\u6050\u6015\u4e5f\u53ea\u6709\u7272\u727a\u522b\u4eba\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u4e0d\u9519\uff0c\u4e3a\u4e86\u4f55\u5168\u5c11\u6797\u5a01\u671b\uff0c\u6211\u7684\u786e\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u90a3\u4e48\u4f60\u53c8\u4f55\u82e6\u8981\u627e\u6211\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6211\u867d\u4e0d\u613f\u505a\u4efb\u4f55\u6709\u635f\u672c\u95e8\u58f0\u540d\u7684\u4e8b\uff0c\u4f46\u4f60\u53ea\u8981\u80fd\u8bc1\u660e\u8c01\u662f\u6740\u6b7b\u5fc3\u7709\u5e08\u5144\u7684\u51f6\u624b \uff0c\u6211\u4e0d\u60dc\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e5f\u8981\u4ed6\u8840\u6e85\u9636\u4e0b\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u51fa\u5bb6\u4eba\u600e\u53ef\u5984\u52a8\u55d4\u5ff5\uff0c\u770b\u6765\u4f60\u8fd9\u548c\u5c1a\u516d\u6839\u8fd8\u4e0d\u6e05\u51c0\u3002 \u3000\u3000\u5fc3\u6811\u5408\u5341\u9053\uff1a\u6211\u4f5b\u5982\u6765\u4e5f\u96be\u514d\u4f5c\u72ee\u5b50\u543c\uff01\u4f55\u51b5\u548c\u5c1a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u970d\u7136\u800c\u8d77\uff0c\u9053\uff1a\u597d\uff0c\u6709\u4e86\u4f60\u8fd9\u53e5\u8bdd\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u52a8\u5bb9\u9053\uff1a\u83ab\u975e\u4f60\u5df2\u77e5\u9053\u51f6\u624b\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u867d\u4e0d\u77e5\u9053\uff0c\u5374\u6709\u4eba\u77e5\u9053\u3002 \u3000\u3000\u5fc3\u6811\u76b1\u7709\u9053\uff1a\u51f6\u624b\u81ea\u5df1\u5f53\u7136\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9664\u4e86\u51f6\u624b\u81ea\u5df1\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\uff0c\u90a3\u4eba\u5c31\u5728\u8fd9\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u5fc3\u6811\u8038\u7136\u9053\uff1a\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6307\u7740\u7985\u5e8a\u4e0a\u5fc3\u7709\u7684\u9057\u8715\u9053\uff1a\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u5fc3\u6811\u5931\u671b\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u53ea\u53ef\u60dc\u4ed6\u5df2\u65e0\u6cd5\u8bf4\u8bdd\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6b7b\u4eba\u6709\u65f6\u4e5f\u4f1a\u8bf4\u8bdd\u7684\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u6380\u8d77\u8986\u5728\u5fc3\u7709\u5c38\u8eab\u4e0a\u7684\u8840\u88ab\u5355\uff0c\u76ee\u5149\u659c\u659c\u81ea\u7a97\u5916\u7167\u8fdb\u6765\uff0c\u7167\u7740\u5fc3\u7709\u67af\u69c1\u5e72\u762a\u7684\u8138 \u3002 \u3000\u3000\u6697\u9ec4\u8272\u7684\u8138\u4e0a\uff0c\u8fd8\u5e26\u7740\u5c42\u8be1\u5f02\u7684\u7070\u9ed1\u8272\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u53ef\u66fe\u770b\u8fc7\u88ab\u6781\u4e50\u7ae5\u5b50\u6bd2\u6b7b\u7684\u4eba\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6ca1\u6709\u3002 \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u9006\u5f92\u6388\u9996>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u9006\u5f92\u6388\u9996 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u5b9e\u9a8c\u4e5f\u6bd2\u6b7b\u7684\u4eba\u5b9e\u5728\u4e0d\u597d\u770b\uff01 \u3000\u3000\u5176\u5b9e\u65e0\u8bba\u8c01\u88ab\u6bd2\u6b7b\u7684\u4eba\u90fd\u4e0d\u4f1a\u597d\u770b\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u8d77\u773c\u775b\uff0c\u7f13\u7f13\u9053\uff1a\u591a\u5e74\u524d\uff0c\u6211\u66fe\u7ecf\u770b\u5230\u4e00\u4e2a\u88ab\u4ed6\u6bd2\u6b7b\u7684\u4eba\uff0c\u90a3\u4eba\u4e2d\u6bd2\u624d\u4e0d\u8fc7\u7247 \u523b\uff0c\u5168\u8eab\u5df2\u7ecf\u53d1\u9ed1\uff0c\u6211\u51fa\u53bb\u6253\u4e2a\u8f6c\uff0c\u518d\u56de\u53bb\u4e00\u770b\uff0c\u90a3\u4eba\u8eab\u4e0a\u7684\u8089\u5df2\u5168\u90fd\u4e0d\u89c1\u4e86\uff0c\u5df2\u53d8\u6210\u4e86\u4e00 \u526f\u9ab7\u9aa8\u2014\u2014\u6f06\u9ed1\u7684\u9ab7\u9aa8\uff01 \u3000\u3000\u5fc3\u6811\u51dd\u89c6\u5fc3\u7709\u7684\u5c38\u8eab\uff0c\u5601\u58f0\u9053\uff1a\u4f46\u73b0\u5728\u4e8c\u5e08\u5144\u4e2d\u6bd2\u5df2\u6709\u597d\u51e0\u5929\u4e86\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u5f20\u5f00\u773c\u775b\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u4e2d\u6bd2\u5df2\u6709\u6570\u65e5\uff0c\u5374\u8fd8\u6ca1\u6709\u53d1\u751f\u90a3\u79cd\u53ef\u6015\u7684\u53d8\u5316\uff0c\u4f60\u53ef\u77e5 \u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5fc3\u6811\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u5b57\u5b57\u9053\uff1a\u8fd9\u53ea\u56e0\u4ed6\u53c8\u4e2d\u4e86\u53e6\u5916\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u6bd2\uff01 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f60\u2014\u2014\u4f60\u662f\u8bf4\u2014\u2014\uff0d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u867d\u4e2d\u4e86\u6781\u4e50\u7ae5\u5b50\u7684\u4e94\u6bd2\u6c34\u6676\uff0c\u4f46\u4e2d\u7684\u6bd2\u5e76\u4e0d\u6df1\uff0c\u518d\u88ab\u4ed6\u4ee5\u5185\u529b\u903c\u4f4f\uff0c\u6240\u4ee5 \u4ed6\u76f4\u5230\u56de\u6765\u540e\u6bd2\u6027\u8fd8\u672a\u53d1\u4f5c\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u51f6\u624b\u4e3a\u4e86\u6015\u4ed6\u8bf4\u51fa\u79d8\u5bc6\uff0c\u4e00\u5fc3\u60f3\u4ed6\u5feb\u4e9b\u6b7b\uff0c\u751f\u6015\u4ed6\u4e2d\u7684\u6bd2\u8fd8\u4e0d\u591f\u6df1\uff0c\u5c31\u53e6 \u7ed9\u4ed6\u670d\u4e86\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u6bd2\u8349\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6740\u4eba\u7684\u6cd5\u5b50\u5f88\u591a\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u662f\u8981\u7528\u6bd2\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u56e0\u65e0\u8bba\u7528\u4ec0\u4e48\u6cd5\u5b50\u6740\u4eba\uff0c\u90fd\u9e21\u514d\u7559\u4e0b\u75d5\u8ff9\uff0c\u5927\u5bb6\u65e2\u5df2\u90fd\u77e5\u9053\u5fc3\u7709\u5927\u5e08\u4e2d\u4e86 \u6bd2\uff0c\u4ed6\u53ea\u6709\u518d\u7528\u4e0b\u6bd2\u8fd9\u6cd5\u5b50\uff0c\u624d\u80fd\u907f\u514d\u522b\u4eba\u7684\u7591\u5fc3\u3002 \u3000\u3000\u5fc3\u6811\u53f9\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u6837\u505a\u4eba\u4eba\u90fd\u8ba4\u4e3a\u4e8c\u5e08\u5144\u5fc5\u662f\u88ab\u6781\u4e50\u7ae5\u5b50\u6bd2\u6b7b\u7684\uff0c\u518d\u4e5f\u4e0d\u4f1a\u6000\u7591\u5230\u4ed6 \u8eab\u4e0a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u6b64\u4eba\u884c\u4e8b\uff0c\u867d\u7136\u8001\u8c0b\u6df1\u7b97\uff0c\u53ea\u53ef\u60dc\u5fd8\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u5fd8\u4e86\u6bd2\u6027\u5fc5\u76f8\u514b\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u4e0b\u7684\u6bd2\u65e2\u70c8\u53c8\u91cd\uff0c\u514b\u4f4f\u4e86\u4e94\u6bd2\u6c34\u6676\u4e4b\u6bd2\uff0c\u6240 \u4ee5\u5fc3\u7709\u5927\u5e08\u7684\u9057\u8715\u5230\u73b0\u5728\u8fd8\u672a\u6709\u90a3\u79cd\u53ef\u6015\u7684\u53d8\u5316\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u5fc3\u7709\u5927\u5e08\u56de\u6765\u4ee5\u540e\uff0c\u53ef\u66fe\u670d\u7528\u8fc7\u4ec0\u4e48\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u53ea\u5403\u8fc7\u4e00\u7897\u836f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u8c01\u5582\u4ed6\u5403\u836f\u7684\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u836f\u662f\u4e03\u5e08\u5f1f\u5fc3\u9274\u914d\u7684\uff0c\u4f46\u5582\u4ed6\u5403\u836f\u7684\u4eba\uff0c\u5374\u662f\u56db\u5e08\u5144\u5fc3\u70db\u548c\u4e03\u5e08\u5f1f\u5fc3\u706f\u3002 \u3000\u3000\u4ed6\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9eef\u7136\u63a5\u7740\u9053\uff1a\u6240\u4ee5\u8fd9\u4e09\u4e2a\u4eba\u90fd\u6709\u4e0b\u6bd2\u7684\u673a\u4f1a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u4e16\u4e0a\u7684\u6bd2\u836f\u5927\u81f4\u5206\u4e8c\u7c7b\uff0c\u7b2c\u4e00\u7c7b\u6bd2\u836f\u867d\u7136\u65e0\u8272\u65e0\u5473\uff0c\u5374\u53ef\u4ee4\u4e2d\u6bd2\u7684\u4eba\u6b7b \u5f97\u5f88\u60e8\uff0c\u53eb\u522b\u4eba\u770b\u4e86\u5bb3\u6015\uff0c\u53ea\u56e0\u8fd9\u7c7b\u6bd2\u4e0d\u4f46\u8981\u53d6\u4eba\u6027\u547d\uff0c\u8fd8\u6709\u8981\u5411\u4eba\u793a\u5a01\u4e4b\u610f\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u90a3\u4e94\u6bd2\u6c34\u6676\u81ea\u7136\u662f\u5c5e\u4e8e\u8fd9\u4e00\u7c7b\u7684\u6bd2\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6b63\u662f\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u7b2c\u4e8c\u7c7b\u6bd2\uff0c\u4e5f\u8bb8\u5e76\u975e\u65e0\u8272\u65e0\u5473\uff0c\u4f46\u5374\u53ef\u4ee4\u88ab\u6bd2\u6b7b\u7684\u4eba\u6b7b\u540e\u5168\u65e0\u5f02\u72b6\uff0c\u751a\u81f3\u53eb \u522b\u4eba\u770b\u4e0d\u51fa\u4ed6\u662f\u88ab\u6bd2\u6b7b\u7684\u3002 \u3000\u3000\u5fc3\u6811\u75b2\u4e4f\uff1a\u4f60\u8bf4\u90a3\u51f6\u624b\u5c31\u662f\u7528\u7684\u8fd9\u79cd\u6bd2\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\uff0c\u53f9\u9053\uff1a\u5c31\u56e0\u4e3a\u4e24\u79cd\u6bd2\u6027\u8fe5\u5f02\uff0c\u662f\u4ee5\u624d\u4f1a\u4e92\u76f8\u514b\u5236\uff0c\u90a3\u7b2c\u4e09\u7c7b\u6bd2\u867d\u53ef\u6015 \uff0c\u8fd9\u7b2c\u4e8c\u7c7b\u6bd2\u5374\u66f4\u9669\u6bd2\uff0c\u6c5f\u6e56\u4e2d\u80fd\u7528\u8fd9\u7c7b\u6bd2\u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u70af\u70af\uff0c\u76ef\u7740\u5fc3\u6811\u9053\uff1a\u5c11\u6797\u95e8\u4e0b\uff0c\u5584\u4e8e\u7528\u6bd2\u7684\u4eba\u6709\u51e0\u4e2a\uff1f \u3000\u3000\u5fc3\u6811\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\u9053\uff1a\u8fd9\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5c11\u6797\u5bfa\u9886\u8896\u6c5f\u6e56\uff0c\u6b66\u6797\u6b63\u5b97\uff0c\u5c11\u6797\u5f1f\u5b50\u4e5f\u4ee5\u6b64\u4e3a\u8363\uff0c\u7edd\u4e0d\u4f1a\u6709\u4eba\u80af\u53bb\u5b66\u8fd9\u79cd \u4e0b\u4e94\u95e8\u7684\u624b\u6bb5\uff0c\u662f\u4e48\uff1f \u3000\u3000\u5fc3\u6811\u65ad\u7136\u9053\uff1a\u5c11\u6797\u4e03\u5341\u4e8c\u7edd\u827a\u4e2d\uff0c\u7edd\u6ca1\u6709\u8fd9\u6bd2\u5b57\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5fc3\u70db\u5927\u5e08\u548c\u5fc3\u706f\u5927\u5e08\u2014\u2014 \u3000\u3000\u5fc3\u6811\u62a2\u7740\u9053\uff1a\u56db\u5e08\u5144\u4e5d\u5c81\u65f6\u4fbf\u5df2\u843d\u53d1\uff0c\u516d\u5e08\u5f1f\u66f4\u5728\u8941\u8913\u4e2d\u4fbf\u5df2\u5165\u4e86\u4f5b\u95e8\uff0c\u4ed6\u4e24\u4eba\u8fd9\u4e00\u751f \u4e2d\u53ea\u6015\u8fd8\u672a\u89c1\u8fc7\u6bd2\u836f\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u4e0b\u6bd2\u7684\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u5fc3\u6811\u8038\u7136\u9053\uff1a\u4f60\u96be\u9053\u8bf4\u7684\u662f\u4e03\u5e08\u5f1f\u5fc3\u9274\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u5fc3\u9274\u5927\u5e08\u4e43\u662f\u534a\u8def\u51fa\u5bb6\uff0c\u5e26\u827a\u6295\u5e08\u7684\u3002\u672a\u5165\u5c11\u6797\u524d\uff0c\u4eba\u79f0\u4e03\u5de7\u4e66\u751f\uff0c\u6b63\u662f\u4f4d\u4e0b\u6bd2\u7684\u5927\u884c \u5bb6\uff01 \u3000\u3000\u5c0f\u505c\u4e2d\u6446\u7740\u4e00\u5c40\u68cb\u3002 \u3000\u3000\u767e\u6653\u751f\u6b63\u8f7b\u8f7b\u5730\u6572\u7740\u68cb\u5b50\uff0c\u4e00\u7247\u7247\u79ef\u96ea\u706f\u82b1\u822c\u968f\u7740\u4ed6\u7684\u6572\u68cb\u58f0\u843d\u4e0b\uff0c\u53c8\u843d\u5728\u65e0\u8fb9\u65e0\u9645\u7684 \u79ef\u96ea\u4e2d\u3002 \u3000\u3000\u591c\u534a\u5f85\u5ba2\u5ba2\u4e0d\u81f3\uff0c\u95f2\u6572\u68cb\u5b50\u843d\u706f\u82b1\u3002 \u3000\u3000\u8fd9\u5883\u754c\u662f\u591a\u4e48\u60a0\u95f2\uff0c\u591a\u4e48\u6f47\u6d12\uff0c\u4f46\u73b0\u5728\uff0c\u5929\u5730\u95f4\u90fd\u4f3c\u5145\u6ee1\u8427\u6740\u4e4b\u6c14\uff0c\u6bcf\u4e2a\u4eba\u7684\u8138\u8272\u66f4\u91cd \u4e8e\u5929\u8272\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\uff0c\u5fc3\u70db\uff0c\u5fc3\u706f\uff0c\u5fc3\u9274\uff0c\u4e5f\u90fd\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u963f\u98de\u8737\u4f0f\u5728\u5c0f\u505c\u7684\u5706\u67f1\u4e0b\uff0c\u8fde\u5934\u90fd\u65e0\u529b\u62ac\u8d77\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u671b\u7740\u4ed6\uff0c\u53cc\u7709\u4e00\u76f4\u672a\u5c55\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u770b\u2014\u2014\u674e\u5bfb\u6b22\u4f1a\u4e0d\u4f1a\u51fa\u6765\uff1f \u3000\u3000\u767e\u6653\u751f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6beb\u65e0\u7591\u95ee\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u4ed6\u8fd9\u79cd\u4eba\u96be\u9053\u8fd8\u4f1a\u4e3a\u4e86\u670b\u53cb\u800c\u727a\u7272\u81ea\u5df1\uff1f \u3000\u3000\u767e\u6653\u751f\u5fae\u7b11\u9053\uff1a\u8fd9\u5c31\u53eb\u76d7\u4ea6\u6709\u9053\u3002 \u3000\u3000\u5fc3\u6e56\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u4f46\u613f\u5982\u6b64\u2014\u2014 \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5ffd\u7136\u4e2d\u65ad\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u51bb\u7ed3\u5728\u5bd2\u98ce\u91cc\u3002 \u3000\u3000\u4ed6\u5df2\u77a7\u89c1\u4e86\u5fc3\u6811\u3002 \u3000\u3000\u5fc3\u6811\u5df2\u8d70\u5165\u4e86\u8fd9\u9662\u5b50\uff0c\u5374\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5fc3\u6e56\u62a2\u5148\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u4f60\u53ef\u5b89\u597d\uff1f \u3000\u3000\u4ed6\u4e0d\u95ee\u522b\u7684\uff0c\u5148\u95ee\u5fc3\u6811\u4e4b\u5b89\u597d\uff0c\u6bd5\u7adf\u4e0d\u6127\u4e3a\u5c11\u6797\u638c\u6559\u3002 \u3000\u3000\u5fc3\u6811\u5408\u4ec0\u9053\uff1a\u591a\u8c22\u5e08\u5144\u5173\u5207\uff0c\u5f1f\u5b50\u4fa5\u5e78\u9003\u8fc7\u4e86\u8fd9\u4e00\u52ab\u3002 \u3000\u3000\u5fc3\u6811\u6de1\u6de1\u9053\uff1a\u4ed6\u53d6\u7ecf\u53bb\u4e86\u3002 \u3000\u3000\u5fc3\u9274\u9053\uff1a\u53d6\u7ecf\uff1f\u53d6\u4ec0\u4e48\u7ecf\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u827a\u7ecf\u9601\u5185\u5931\u7a83\u7684\u7ecf\u3002 \u3000\u3000\u5fc3\u9274\u5634\u89d2\u4e00\u9635\u7275\u52a8\uff0c\u51b7\u7b11\u9053\uff1a\u76d7\u7ecf\u7684\u4eba\u679c\u7136\u662f\u4ed6\uff01\u5e08\u5144\u4f60\u600e\u5730\u653e\u5fc3\u8ba9\u4ed6\u53bb\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u53ea\u56e0\u76d7\u7ecf\u7684\u4eba\u5e76\u4e0d\u662f\u4ed6\uff01 \u3000\u3000\u5fc3\u9274\u9053\uff1a\u4e0d\u662f\u674e\u5bfb\u6b22\u662f\u8c01\uff1f \u3000\u3000\u5fc3\u6811\u76ee\u4e2d\u5bd2\u5149\u66b4\u5c04\uff0c\u5389\u58f0\u9053\uff1a\u662f\u4f60\uff01 \u3000\u3000\u5fc3\u9274\u7684\u5634\u89d2\u53c8\u4e00\u9635\u7275\u52a8\uff0c\u8138\u8272\u5374\u6c89\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u4e94\u5e08\u5144\u600e\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u6211\u5012\u771f \u6709\u4e9b\u4e0d\u61c2\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f60\u4e0d\u61c2\u8fd8\u6709\u8c01\u61c2\uff1f \u3000\u3000\u5fc3\u9274\u8f6c\u5411\u5fc3\u6e56\uff0c\u9053\uff1a\u8fd9\u4ef6\u4e8b\u8fd8\u662f\u8bf7\u5927\u5e08\u5144\u88c1\u593a\uff0c\u5f1f\u5b50\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u5fc3\u70db\u3001\u5fc3\u706f\u3001\u767e\u6653\u751f\u65e9\u5df2\u542c\u5f97\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u5fc3\u6e56\u4e5f\u4e0d\u7981\u53d8\u8272\u9053\uff1a\u4e8c\u5e08\u5f1f\u660e\u660e\u662f\u906d\u4e86\u674e\u5bfb\u6b22\u4e4b\u6bd2\u624b\uff0c\u4f60\u4e3a\u4f55\u8981\u4e3a\u4ed6\u6d17\u8131\uff1f \u3000\u3000\u767e\u6653\u751f\u60a0\u60a0\u9053\uff1a\u82e5\u662f\u5728\u4e0b\u8bb0\u5f97\u4e0d\u9519\uff0c\u5fc3\u6811\u5e08\u5144\u4e0e\u674e\u5bfb\u6b22\u597d\u50cf\u8fd8\u662f\u540c\u699c\u7684\u8fdb\u58eb\u3002 \u3000\u3000\u5fc3\u9274\u51b7\u51b7\u9053\uff1a\u4e94\u5e08\u5144\u53ea\u6015\u4e5f\u4e2d\u4e86\u674e\u5bfb\u6b22\u7684\u6bd2\u4e86\u3002 \u3000\u3000\u5fc3\u6811\u6839\u672c\u4e0d\u7406\u4ed6\u4eec\uff0c\u6c89\u58f0\u9053\uff1a\u771f\u6b63\u4ee4\u4e8c\u5e08\u5144\u81f4\u547d\u7684\u6bd2\u836f\uff0c\u5e76\u975e\u6781\u4e50\u7ae5\u5b50\u7684\u4e94\u6bd2\u6c34\u6676\u2014\u2014 \u3000\u3000\u5fc3\u9274\u62a2\u7740\u9053\uff1a\u5e08\u5144\u4f60\u53c8\u600e\u4f1a\u77e5\u9053\u7684\uff1f \u3000\u3000\u5fc3\u6811\u51b7\u7b11\u9053\uff1a\u4f60\u4ee5\u4e3a\u4f60\u505a\u7684\u4e8b\u771f\u7684\u4eba\u4e0d\u77e5\u3001\u9b3c\u4e0d\u89c9\uff1f\u4f60\u83ab\u975e\u5df2\u5fd8\u4e86\u4e8c\u5e08\u5144\u4e34\u6b7b\u524d\u8fd8\u6709\u8fd9 \u672c\u4e1c\u897f\u7559\u4e0b\u6765\uff1f \u3000\u3000\u4ed6\u7684\u624b\u4e00\u626c\uff0c\u624b\u91cc\u62ff\u7740\u7684\u6b63\u662f\u5fc3\u7709\u4e4b\u300a\u8bfb\u7ecf\u672d\u8bb0\u300b\u3002 \u3000\u3000\u5fc3\u6e56\u76b1\u7709\u9053\uff1a\u8fd9\u53c8\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4e8c\u5e08\u5144\u884c\u4e4b\u524d\uff0c\u5df2\u53d1\u73b0\u4e86\u90a3\u76d7\u7ecf\u7684\u53db\u5f92\uff0c\u53ea\u662f\u4ed6\u5fc3\u5b58\u4ec1\u539a\uff0c\u672a\u7ecf\u8bc1\u5b9e\u524d\uff0c\u8fd8\u4e0d \u613f\u62ab\u9732\u8fd9\u53db\u5f92\u7684\u59d3\u540d\uff0c\u53ea\u4e0d\u8fc7\u5374\u5df2\u5c06\u4e4b\u5199\u5728\u4ed6\u8fd9\u672c\u300a\u8bfb\u7ecf\u672d\u8bb0\u300b\u4e0a\uff0c\u4ee5\u9632\u4e07\u4e00\u4ed6\u82e5\u6709\u4e0d\u6d4b\uff0c \u4e5f\u597d\u7559\u4f5c\u8bc1\u636e\u3002 \u3000\u3000\u5fc3\u6e56\u52a8\u5bb9\u9053\uff1a\u771f\u6709\u6b64\u4e8b\uff1f \u3000\u3000\u5fc3\u9274\u62a2\u7740\u9053\uff1a\u8fd9\u4e0a\u9762\u82e5\u771f\u6709\u6211\u7684\u540d\u5b57\uff0c\u6211\u5c31\u7518\u613f\u2014\u2014 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u4f60\u7518\u613f\u600e\u6837\uff1f\u2014\u2014\u4f60\u867d\u5df2\u5c06\u6700\u540e\u4e00\u9875\u6495\u4e0b\u4e86\uff0c\u53c8\u600e\u77e5\u4e8c\u5e08\u5144\u6ca1\u6709\u8bb0\u5728\u53e6\u4e00\u9875\u4e0a \uff1f \u3000\u3000\u5fc3\u9274\u8eab\u5b50\u4e00\u9707\uff0c\u5ffd\u7136\u4f0f\u5012\u5728\u5730\uff0c\u98a4\u58f0\u9053\uff1a\u4e94\u5e08\u5144\u7adf\u52fe\u7ed3\u5916\u4eba\uff0c\u4ee4\u5f1f\u5b50\u8eab\u906d\u4e0d\u767d\u4e4b\u51a4\uff0c\u6c42 \u5927\u5e08\u5144\u660e\u9274\u3002 \u3000\u3000\u5fc3\u6e56\u6c89\u541f\u7740\uff0c\u76ee\u5149\u5411\u767e\u6653\u751f\u671b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u767e\u6653\u751f\u7f13\u7f13\u9053\uff1a\u767d\u7eb8\u4e0a\u5199\u7684\u867d\u662f\u9ed1\u5b57\uff0c\u4f46\u8fd9\u5b57\u5374\u662f\u4eba\u4eba\u90fd\u53ef\u5199\u7684\u3002 \u3000\u3000\u5fc3\u9274\u9053\uff1a\u4e0d\u9519\uff0c\u5c31\u7b97\u4e8c\u5e08\u5144\u8fd9\u672c\u300a\u8bfb\u7ecf\u672d\u8bb0\u300b\u5199\u7740\u6211\u7684\u540d\u5b57\uff0c\u4f46\u5374\u4e5f\u672a\u5fc5\u662f\u4e8c\u5e08\u5144\u81ea\u5df1 \u5199\u7684\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u636e\u6211\u6240\u77e5\uff0c\u5c0f\u674e\u63a2\u82b1\u6587\u6b66\u53cc\u5168\uff0c\u671d\u82cf\u989c\u67f3\uff0c\u5170\u5ead\u9b4f\u7891\uff0c\u540d\u5bb6\u7684\u5b57\uff0c\u4ed6\u5374\u66fe\u4e0b \u8fc7\u529f\u592b\u4e34\u6479\u3002 \u3000\u3000\u5fc3\u9274\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u82e5\u8981\u5b66\u4e00\u4e2a\u4eba\u7684\u7b14\u8ff9\uff0c\u81ea\u7136\u5bb9\u6613\u5f97\u5f88\u3002 \u3000\u3000\u5fc3\u6e56\u6c89\u4e0b\u4e86\u8138\uff0c\u77aa\u7740\u5fc3\u6811\u9053\uff1a\u4f60\u5e73\u65f6\u7d20\u6765\u8ba4\u771f\uff0c\u8fd9\u6b21\u600e\u5730\u4e5f\u758f\u5ffd\u8d77\u6765\uff1f \u3000\u3000\u5fc3\u6811\u795e\u8272\u4e0d\u53d8\uff0c\u9053\uff1a\u5e08\u5144\u82e5\u8ba4\u4e3a\u8fd9\u8bc1\u636e\u4e0d\u591f\uff0c\u8fd8\u6709\u4e2a\u8bc1\u636e\u3002 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u4f60\u4e14\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u672c\u6765\u85cf\u5728\u4e8c\u5e08\u5144\u623f\u4e2d\u7684\u90a3\u90e8\u300a\u8fbe\u6469\u6613\u7b4b\u7ecf\u300b\u4e5f\u5df2\u5931\u7a8d\u4e86\u3002 \u3000\u3000\u5fc3\u6e56\u52a8\u5bb9\uff1a\u54e6\uff1f \u3000\u3000\u5fc3\u6811\u9053\uff1a\u674e\u63a2\u82b1\u7b97\u51c6\u8fd9\u90e8\u7ecf\u5fc5\u5b9a\u8fd8\u672a\u6765\u5f97\u53ca\u9001\u8d70\uff0c\u5fc5\u5b9a\u8fd8\u85cf\u5728\u5fc3\u9274\u623f\u91cc\uff0c\u662f\u4ee5\u5f1f\u5b50\u5df2\u4ee4 \u503c\u65e5\u7684\u4e00\u5c18\u548c\u4e00\u8335\u76d1\u89c6\u7740\u4ed6\u4e00\u8d77\u53d6\u7ecf\u53bb\u4e86\u3002 \u3000\u3000\u5fc3\u9274\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u547c\u9053\uff1a\u5e08\u5144\u5207\u83ab\u542c\u4ed6\u7684\uff0c\u4ed6\u5012\u771f\u662f\u60f3\u683d\u8d43\uff01 \u3000\u3000\u4ed6\u5634\u91cc\u72c2\u547c\u7740\uff0c\u4eba\u5df2\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u76b1\u4e86\u76b1\u7709\uff0c\u888d\u8896\u4e00\u5c55\uff0c\u4eba\u4e5f\u968f\u4e4b\u63a0\u8d77\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u963b\u6b62\u4ed6\uff0c\u53ea\u662f\u4e0d\u5373\u4e0d\u79bb\u5730\u8ddf \u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u5fc3\u9274\u8eab\u5f62\u8d77\u843d\u95f4\uff0c\u5df2\u63a0\u56de\u4ed6\u81ea\u5df1\u7684\u7985\u623f\u3002 \u3000\u3000\u95e8\u679c\u7136\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u5fc3\u9274\u51b2\u4e86\u8fdb\u53bb\uff0c\u4e00\u638c\u5288\u5f00\u4e86\u6728\u67dc\uff0c\u6728\u67dc\u7adf\u6709\u5939\u5c42\u3002 \u3000\u3000\u6613\u7b4b\u7ecf\u679c\u7136\u5c31\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5fc3\u9274\u5389\u58f0\u9053\uff1a\u8fd9\u90e8\u7ecf\u672c\u5728\u4e8c\u5e08\u5144\u623f\u4e2d\uff0c\u4ed6\u4eec\u6545\u610f\u653e\u5728\u8fd9\u91cc\u4e3a\u7684\u5c31\u662f\u8981\u683d\u8d43\uff0c\u4f46\u8fd9\u79cd\u683d\u8d43 \u7684\u6cd5\u5b50\uff0c\u51e0\u767e\u5e74\u524d\u5df2\u6709\u4eba\u7528\u8fc7\u4e86\uff0c\u5927\u5e08\u5144\u795e\u76ee\u5982\u7535\uff0c\u600e\u4f1a\u88ab\u4f60\u4eec\u8fd9\u79cd\u8096\u5c0f\u4eec\u6240\u6b3a\uff01 \u3000\u3000\u76f4\u7b49\u4ed6\u8bf4\u5b8c\u4e86\uff0c\u5fc3\u6e56\u9053\uff1a\u5c31\u7b97\u6211\u4eec\u662f\u683d\u8d43\uff0c\u4f46\u4f60\u53c8\u600e\u77e5\u6211\u4eec\u4f1a\u5c06\u8fd9\u90e8\u7ecf\u653e\u5728\u8fd9\u6728\u67dc\u91cc\uff1f \u4f60\u4e3a\u4f55\u4e0d\u5230\u53e6\u5904\u53bb\u627e\uff1f\u4e00\u8fdb\u6765\u5c31\u76f4\u5954\u8fd9\u6728\u67dc\uff1f \u3000\u3000\u5fc3\u9274\u9aa4\u7136\u6014\u4f4f\u4e86\uff0c\u6ee1\u5934\u6c57\u5982\u96e8\u3002 \u3000\u3000\u5fc3\u6811\u5410\u51fa\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u674e\u63a2\u82b1\u65e9\u5df2\u7b97\u51c6\u53ea\u6709\u7528\u8fd9\u6cd5\u5b50\uff0c\u624d\u53ef\u4ee4\u4ed6\u4e0d\u6253\u81ea\u62db\u7684\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u5fae\u7b11\u9053\uff1a\u4f46\u6211\u8fd9\u6cd5\u5b50\u5b9e\u5728\u4e5f\u7528\u5f97\u5f88\u5192\u9669\uff0c\u4ed6\u81ea\u5df1\u82e5\u4e0d\u4e0a\u5f53\uff0c\u90a3\u5c31\u8c01\u4e5f\u65e0\u6cd5\u4ee4\u4ed6 \u62db\u8ba4\u4e86\uff01 \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u674e\u5bfb\u6b22\u5df2\u5ffd\u7136\u51fa\u73b0\u3002 \u3000\u3000\u5fc3\u6e56\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5408\u4ec0\u4e3a\u793c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u5fae\u542b\u77e5\uff0c\u62b1\u62f3\u4e00\u63d6\u3002 \u3000\u3000\u8fd9\u4e00\u63d6\u4e00\u793c\u4e2d\u5df2\u5305\u542b\u4e86\u8bb8\u591a\u8bdd\uff0c\u522b\u7684\u5df2\u4e0d\u5fc5\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u5fc3\u9274\u4e00\u6b65\u6b65\u5730\u540e\u9000\uff0c\u4f46\u5fc3\u70db\u548c\u5fc3\u706f\u5df2\u963b\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u4e24\u4eba\u5177\u662f\u9762\u8272\u51dd\u91cd\uff0c\u5cd9\u7acb\u5982\u5c71\u5cb3 \u3002 \u3000\u3000\u5fc3\u6e56\u9eef\u7136\u9053\uff1a\u5355\u9e57\uff0c\u5c11\u6797\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u4e3a\u4f55\u4eca\u65e5\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff1f \u3000\u3000\u5355\u9e57\u6b63\u662f\u5fc3\u9274\u7684\u4fd7\u540d\u3002 \u3000\u3000\u5355\u9e57\u6c57\u51fa\u5982\u6d46\uff0c\u98a4\u58f0\u9053\uff1a\u5f1f\u5b50\u2014\u2014\u5f1f\u5b50\u77e5\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u6251\u5012\u5728\u5730\uff0c\u9053\uff1a\u4f46\u5f1f\u5b50\u4e5f\u662f\u53d7\u4e86\u4ed6\u4eba\u6307\u4f7f\uff0c\u88ab\u4ed6\u4eba\u6240\u8bf1\uff0c\u624d\u4f1a\u4e00\u65f6\u7cca\u6d82\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u5389\u58f0\u9053\uff1a\u4f60\u53d7\u4e86\u8c01\u7684\u6307\u4f7f\uff1f \u3000\u3000\u767e\u6653\u751f\u5ffd\u7136\u9053\uff1a\u6307\u4f7f\u4ed6\u7684\u4eba\uff0c\u6211\u5012\u53ef\u731c\u540c\u4e00\u4e8c\u3002 \u3000\u3000\u5fc3\u6e56\u5927\u5e08\u9053\uff1a\u5148\u751f\u6307\u6559\u3002 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u5927\u5bb6\u4e0d\u7531\u81ea\u4e3b\uff0c\u4e00\u9f50\u968f\u4ed6\u7684\u76ee\u5149\u671b\u4e86\u8fc7\u53bb\uff0c\u4f46\u5374\u4ec0\u4e48\u4e5f\u6ca1\u6709\u77a7\u89c1\uff0c\u7a97\u5916\u7af9\u8349\u7c0c\u7c0c\uff0c\u98ce\u53c8 \u6e10\u6e10\u5927\u4e86\u3002 \u3000\u3000\u56de\u8fc7\u5934\u6765\u65f6\uff0c\u5fc3\u6e56\u7684\u9762\u8272\u5df2\u53d8\u3002 \u3000\u3000\u767e\u6653\u751f\u7684\u624b\uff0c\u5df2\u6309\u5728\u4ed6\u80cc\u540e\uff0c\u94c1\u6307\u5982\uff0c\u5df2\u6263\u4f4f\u4e86\u4ed6\u7684\u56db\u5904\u5927\u7a74\u3002 \u3000\u3000\u5fc3\u6811\u9762\u8272\u4e5f\u53d8\u4e86\uff0c\u9a87\u7136\u9053\uff1a\u6307\u4f7f\u4ed6\u7684\u4eba\u539f\u6765\u662f\u4f60\uff01 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u5728\u4e0b\u53ea\u4e0d\u8fc7\u60f3\u501f\u8d35\u5bfa\u7684\u85cf\u7ecf\u4e00\u9605\u800c\u5df2\uff0c\u8c01\u77e5\u9053\u5404\u4f4d\u7adf\u5982\u6b64\u5c0f\u6c14\uff01 \u3000\u3000\u5fc3\u6e56\u957f\u53f9\u9053\uff1a\u6211\u4e0e\u4f60\u6570\u5341\u5e74\u76f8\u4ea4\uff0c\u4e0d\u60f3\u4f60\u7adf\u5982\u6b64\u5f85\u6211\uff1f \u3000\u3000\u767e\u6653\u751f\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u6211\u672c\u6765\u4e5f\u4e0d\u60f3\u5982\u6b64\u5bf9\u4f60\u7684\uff0c\u600e\u5948\u5355\u9e57\u5b9a\u8981\u62d6\u6211\u4e0b\u6c34\uff0c\u6211\u82e5\u4e0d\u51fa\u624b \u6551\u4ed6\uff0c\u4ed6\u600e\u4f1a\u653e\u8fc7\u6211\u3002 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u53ea\u53ef\u60dc\u8c01\u4e5f\u6551\u4e0d\u4e86\u4ed6\u4e86\uff01 \u3000\u3000\u5355\u9e57\u65e9\u5df2\u8df3\u8d77\uff0c\u4e00\u624b\u6284\u8d77\u4e86\u90a3\u90e8\u6613\u7b4b\u7ecf\uff0c\u72de\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u8c01\u4e5f\u6551\u4e0d\u4e86\u6211\uff0c\u53ea\u6709\u4f60\u624d\u6551\u5f97 \u4e86\u6211\uff0c\u73b0\u5728\u6211\u5c31\u8981\u4f60\u9001\u6211\u4eec\u4e0b\u5c71\u2014\u2014\u4f60\u4eec\u82e5\u8fd8\u8981\u4f60\u4eec\u7684\u638c\u95e8\u4eba\u6d3b\u7740\uff0c\u6700\u597d\u8c01\u4e5f\u83ab\u8981\u5984\u52a8\uff01 \u3000\u3000\u5fc3\u6811\u867d\u7136\u6c14\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u4f46\u5374\u8c01\u4e5f\u4e0d\u6562\u51fa\u624b\u3002 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u4f60\u4eec\u82e5\u4ee5\u5c11\u6797\u4e3a\u91cd\uff0c\u5c31\u83ab\u8981\u7ba1\u6211\uff01\u8fd8\u4e0d\u52a8\u624b\u62ff\u4e0b\u8fd9\u53db\u5f92\uff01 \u3000\u3000\u767e\u6653\u751f\u9053\uff1a\u4f60\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u62ff\u4f60\u7684\u6027\u547d\u6765\u5f00\u73a9\u7b11\u7684\uff0c\u5c11\u6797\u6d3e\u638c\u95e8\u4eba\u7684\u4e00\u6761\u547d \u6bd4\u522b\u4eba\u4e00\u5343\u6761\u547d\u8fd8\u8981\u503c\u94b1\u5f97\u591a\u3002 \u3000\u3000\u591a\u5b57\u51fa\u53e3\uff0c\u4ed6\u8138\u4e0a\u7684\u7b11\u5bb9\u4e5f\u51bb\u7ed3\u4f4f\u4e86\uff01 \u3000\u3000\u5200\u5149\u4e00\u95ea\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\u5df2\u51fa\u624b\uff01 \u3000\u3000\u5200\u5df2\u98de\u5165\u4ed6\u7684\u54bd\u5589\uff01 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u5230\u5c0f\u674e\u98de\u5200\u662f\u5982\u4f55\u51fa\u624b\u7684\uff01 \u3000\u3000\u767e\u6653\u751f\u4e00\u76f4\u4ee5\u5fc3\u6e56\u5927\u5e08\u4e3a\u76fe\u724c\uff0c\u4ed6\u7684\u54bd\u5589\u5c31\u5728\u5fc3\u6e56\u7684\u54bd\u5589\uff0c\u4ed6\u7684\u54bd\u5589\u4ec5\u4ec5\u9732\u51fa\u4e86\u4e00\u5c0f\u534a \u3002 \u3000\u3000\u4ed6\u7684\u54bd\u5589\u968f\u65f6\u53ef\u907f\u5728\u5fc3\u6e56\u7684\u54bd\u5589\u4e4b\u540e\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u6ca1\u6709\u4eba\u6562\u51fa\u624b\u3002 \u3000\u3000\u4f46\u5200\u5149\u4e00\u95ea\uff0c\u6bd4\u95ea\u7535\u66f4\u5feb\u7684\u4e00\u95ea\uff0c\u5c0f\u674e\u7684\u98de\u5200\u5df2\u5728\u4ed6\u54bd\u5589\uff01 \u3000\u3000\u5fc3\u6811\u3001\u5fc3\u70db\u3001\u5fc3\u706f\uff0c\u7acb\u523b\u62a2\u8fc7\u53bb\u62a4\u4f4f\u4e86\u5fc3\u6e56\u3002 \u3000\u3000\u767e\u6653\u751f\u7684\u53cc\u773c\u6012\u51f8\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u8138\u4e0a\u7684\u808c\u8089\u4e00\u6839\u6839\u62bd\u52a8\uff0c\u5145\u6ee1\u4e86\u60ca\u60e7\u3001\u6000\u7591\u548c\u4e0d\u4fe1\uff0d \uff0d \u3000\u3000\u4ed6\u4f3c\u4e4e\u6b7b\u4e5f\u4e0d\u76f8\u4fe1\u674e\u5bfb\u6b22\u7684\u98de\u5200\u4f1a\u523a\u5165\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u7684\u5634\u5507\u8fd8\u5728\u52a8\uff0c\u5589\u5499\u91cc\u201c\u683c\u683c\u201d\u4f5c\u54cd\uff0c\u867d\u7136\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ef\u662f\u770b\u4ed6\u7684\u5634\u5507\u5728\u52a8\uff0c\u5df2\u53ef \u770b\u51fa\u4ed6\u60f3\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u201c\u6211\u9519\u4e86\u2014\u2014\u6211\u9519\u4e86\u2014\u2014\u201d \u3000\u3000\u4e0d\u9519\uff0c\u767e\u6653\u751f\u65e0\u6240\u4e0d\u77e5\uff0c\u65e0\u6240\u4e0d\u6653\uff0c\u53ea\u6709\u4e00\u4ef6\u4e8b\u5f04\u9519\u4e86\u3002 \u3000\u3000\u5c0f\u674e\u98de\u5200\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u8fd8\u8981\u5feb\u5f97\u591a\uff01 \u3000\u3000\u767e\u6653\u751f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u767e\u6653\u751f\u4f5c\u5175\u5668\u8c31\uff0c\u54c1\u8bc4\u5929\u4e0b\u5175\u5668\uff0c\u53ef\u79f0\u6b66\u6797\u667a\u8005\uff0c\u8c01\u77e5\u5230\u5934\u6765\u8fd8 \u662f\u96be\u514d\u6b7b\u5728\u81ea\u5df1\u6240\u54c1\u8bc4\u7684\u5175\u5668\u4e4b\u4e0b\u3002 \u3000\u3000\u5fc3\u6e56\u8d22\u6b21\u5408\u4ec0\u4e3a\u793c\uff0c\u6ee1\u8138\u6127\u8272\uff0c\u9053\uff1a\u8001\u50e7\u4e5f\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u9762\u4e0a\u5ffd\u53c8\u53d8\u8272\uff0c\u5931\u58f0\u9053\uff1a\u90a3\u53db\u5f92\u5462\uff1f \u3000\u3000\u5355\u9e57\u7adf\u8d81\u7740\u65b9\u624d\u90a3\u4e00\u77ac\u606f\u7684\u6df7\u4e71\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u50cf\u5355\u9e57\u8fd9\u79cd\u4eba\uff0c\u662f\u6c38\u8fdc\u4e0d\u4f1a\u9519\u8fc7\u673a\u4f1a\u7684\uff0c\u4ed6\u4e0d\u4f46\u53cd\u5e94\u5feb\uff0c\u8eab\u6cd5\u4e5f\u5feb\uff0c\u4e24\u4e2a\u8d77\u843d\uff0c\u5df2\u63a0\u51fa \u9662\u5b50\u3002 \u3000\u3000\u5c11\u6797\u95e8\u4e0b\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u7eb5\u7136\u770b\u5230\u4ed6\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u62e6\u963b\uff0c\u4f55\u51b5\u8fd9\u662f\u9996\u5ea7\u5927\u5e08\u7684\u5c45\u5ea7\uff0c\u5c11 \u6797\u5f1f\u5b50\u6839\u672c\u4e0d\u6562\u968f\u610f\u95ef\u5165\u3002 \u3000\u3000\u4ed6\u63a0\u8fc7\u90a3\u5c0f\u4ead\u65f6\uff0c\u963f\u98de\u6b63\u5728\u6323\u624e\u7740\u722c\u8d77\u6765\u2014\u2014\u767e\u6653\u751f\u548c\u5355\u9e57\u70b9\u7a74\u7684\u624b\u6cd5\u867d\u91cd\uff0c\u4f46\u4e5f\u8fd8\u662f \u6709\u5931\u6548\u7684\u65f6\u5019\u3002 \u3000\u3000\u5355\u9e57\u77a7\u89c1\u4e86\u4ed6\uff0c\u76ee\u4e2d\u7acb\u523b\u9732\u51fa\u4e86\u51f6\u5149\uff0c\u4ed6\u7adf\u8981\u5c06\u6ee1\u5fc3\u7684\u6028\u6bd2\u5168\u53d1\u6cc4\u5728\u963f\u98de\u8eab\u4e0a\uff0c\u8eab\u5f62\u4e00 \u6298\uff0c\u55d6\u7684\u63a0\u8fc7\u53bb\u3002 \u3000\u3000\u963f\u98de\u5df2\u88ab\u6298\u78e8\u5f97\u5944\u5944\u4e00\u606f\uff0c\u54ea\u6709\u529b\u6c14\u62b5\u6321\u3002 \u3000\u3000\u8981\u6740\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u81ea\u7136\u7528\u4e0d\u7740\u8d39\u4ec0\u4e48\u529f\u592b\u3002 \u3000\u3000\u5355\u9e57\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u94c1\u62f3\u5df2\u51fb\u51fa\uff0c\u5c11\u6797\u795e\u62f3\u540d\u9707\u5929\u4e0b\uff0c\u5355\u9e57\u6295\u5165\u5c11\u6797\u5341\u4f59\u5e74\uff0c\u529f\u592b\u5e76 \u6ca1\u6709\u767d\u7ec3\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u795e\u5145\u6c14\u8db3\uff0c\u62db\u91cd\u529b\u731b\uff0c\u8981\u53d6\u4eba\u6027\u547d\u5c31\u5982\u63a2\u56ca\u53d6\u7269\u2014\u2014\u5355\u9e57\u65e9\u5df2\u7b97\u51c6\u6740\u4e86\u4ed6\u4e4b\u540e\u518d \u9003\u4e5f\u6765\u5f97\u53ca\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u963f\u98de\u7684\u624b\u7a81\u7136\u523a\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u7684\u540e\u53d1\uff0c\u5374\u5148\u81f3\uff01 \u3000\u3000\u5355\u9e57\u53ea\u89c9\u81ea\u5df1\u7684\u54bd\u5589\u9aa4\u7136\u4e00\u9635\u51b0\u51c9\uff0c\u51b0\u51c9\u4e2d\u5e26\u7740\u523a\u75db\uff0c\u547c\u5438\u4e5f\u9aa4\u7136\u505c\u987f\uff0c\u5c31\u4eff\u4f5b\u88ab\u4e00\u53cc \u9b54\u624b\u627c\u4f4f\uff01 \u3000\u3000\u4ed6\u9762\u4e0a\u7684\u808c\u8089\u4e5f\u626d\u66f2\u8d77\u6765\uff0c\u4e5f\u5145\u6ee1\u4e86\u6050\u60e7\u548c\u4e0d\u4fe1\u2014\u2014\u8fd9\u5c11\u5e74\u51fa\u624b\u4e4b\u6109\uff0c\u4ed6\u65e9\u5df2\u77e5\u9053\u7684\u3002 \u3000\u3000\u4f46\u5c11\u5e74\u5374\u53c8\u662f\u7528\u4ec0\u4e48\u523a\u5165\u4ed6\u54bd\u5589\u7684\u5462\uff1f \u3000\u3000\u8fd9\u7b54\u6848\u4ed6\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u77e5\u9053\u4e86\u3002 \u3000\u3000\u5355\u9e57\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u963f\u98de\u501a\u7740\u680f\u6746\uff0c\u6b63\u5728\u5598\u606f\u3002 \u3000\u3000\u5fc3\u6e56\u4ed6\u4eec\u8d76\u6765\u65f6\uff0c\u4e5f\u89c9\u5f97\u5f88\u60ca\u8bb6\uff0c\u56e0\u4e3a\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5c11\u5e74\u5728\u5982\u6b64\u8870\u5f31\u4e2d\uff0c\u4ecd\u53ef\u7f6e\u5355\u9e57\u4e8e \u6b7b\u5730\uff01 \u3000\u3000\u4e00\u6839\u51b0\u67f1\uff0c\u5251\u4e00\u822c\u523a\u5728\u5355\u9e57\u7684\u54bd\u5589\u91cc\u3002 \u3000\u3000\u51b0\u5df2\u5f00\u59cb\u878d\u5316\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u7adf\u53ea\u7528\u4e00\u6839\u51b0\u67f1\uff0c\u5c31\u53d6\u4e86\u53f7\u79f0\u5c11\u6797\u4e03\u5927\u9ad8\u624b\u4e4b\u4e00\u5fc3\u9274\u7684\u6027\u547d\u3002 \u3000\u3000\u5fc3\u6e56\u671b\u7740\u4ed6\u82cd\u767d\u5931\u8840\u7684\u8138\uff0c\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u963f\u98de\u6839\u672c\u6ca1\u6709\u77a7\u4ed6\u4eec\u4e00\u773c\uff0c\u53ea\u662f\u51dd\u89c6\u7740\u674e\u5bfb\u6b22\uff0c\u7136\u540e\u4ed6\u8138\u4e0a\u5c31\u6e10\u6e10\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6b63\u5728\u5fae\u7b11\u3002 \u3000\u3000\u5fc3\u6e56\u7684\u58f0\u97f3\u5f88\u67af\u6da9\uff0c\u5408\u4ec0\u9053\uff1a\u4e24\u4f4d\u8bf7\u5230\u8001\u50e7\u2014\u2014 \u3000\u3000\u963f\u98de\u970d\u7136\u626d\u8fc7\u5934\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u674e\u5bfb\u6b22\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u5fc3\u6e56\u5782\u9996\u9053\uff1a\u4e0d\u662f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u662f\u4e0d\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u5fc3\u6e56\u53f9\u9053\uff1a\u6a80\u8d8a\u4e5f\u4e0d\u662f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u65e2\u7136\u4e0d\u662f\uff0c\u6211\u4eec\u53ef\u4ee5\u8d70\u4e86\u4e48\uff1f \u3000\u3000\u5fc3\u6e56\u52c9\u5f3a\u7b11\u9053\uff1a\u81ea\u7136\u53ef\u4ee5\uff0c\u53ea\u4e0d\u8fc7\u6a80\u8d8a\u2014\u2014\u6a80\u8d8a\u884c\u52a8\u8fd8\u6709\u4e9b\u4e0d\u4fbf\uff0c\u4e0d\u5982\u5148\u8bf7\u5230\u2014\u2014 \u3000\u3000\u963f\u98de\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u8fd9\u4e0d\u7528\u4f60\u8d39\u5fc3\uff0c\u83ab\u8bf4\u6211\u8fd8\u53ef\u4ee5\u8d70\uff0c\u5c31\u7b97\u722c\uff0c\u4e5f\u8981\u722c\u4e0b\u5c71 \u53bb\u3002 \u3000\u3000\u5fc3\u70db\u3001\u5fc3\u706f\u7684\u5934\u4e5f\u5782\u4e86\u4e0b\u53bb\uff0c\u6570\u767e\u5e74\u6765\uff0c\u5929\u4e0b\u4ece\u65e0\u4e00\u4eba\u6562\u5bf9\u5c11\u6797\u638c\u95e8\u5982\u6b64\u65e0\u793c\uff0c\u4ed6\u4eec\u73b0 \u5728\u53c8\u4f55\u5c1d\u4e0d\u89c9\u5f97\u60b2\u6124\u586b\u81ba\uff01 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u4eec\u5374\u53ea\u6709\u5fcd\u8010\uff01 \u3000\u3000\u963f\u98de\u5df2\u62c9\u8d77\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u8d70\u5165\u5bd2\u98ce\u4e2d\uff0c\u4ed6\u7684\u80f8\u819b\u7acb\u523b\u53c8\u633a\u8d77\u2014\u2014\u8fd9\u5c11\u5e74\u7684\u8eab\u5b50\u5c31\u50cf\u662f\u94c1\u6253\u7684\uff0c\u65e0\u8bba\u591a\u5927\u7684\u6298\u78e8 \u90fd\u65e0\u6cd5\u4ee4\u4ed6\u5f2f\u4e0b\u8170\u53bb\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u56de\u9996\u4e00\u7b11\u9053\uff1a\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\uff0c\u4ed6\u65e5\u6216\u5f53\u518d\u89c1\uff0c\u5927\u5e08\u8bf7\u6055\u6211\u7b49\u65e0\u793c\u3002 \u3000\u3000\u5fc3\u6811\u9053\uff1a\u6211\u9001\u4f60\u4eec\u4e00\u7a0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u9001\u5374\u4e0d\u9001\uff0c\u4e0d\u9001\u5373\u9001\uff0c\u5927\u5e08\u4f55\u5fc5\u7740\u76f8\uff1f \u3000\u3000\u5fc3\u6811\u4e5f\u7b11\u9053\uff1a\u65e2\u7136\u9001\u9093\u4e0d\u9001\uff0c\u9001\u53c8\u4f55\u59a8\uff0c\u6a80\u8d8a\u53c8\u4f55\u5fc5\u7740\u76f8\uff1f \u3000\u3000\u76f4\u5230\u4ed6\u4eec\u8eab\u5f62\u53bb\u8fdc\uff0c\u5fc3\u6e56\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u867d\u7136\u5e76\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u4f46\u8fd9\u4e0d\u8bf4\uff0c\u5374\u6bd4\u8bf4\u66f4 \u8981\u96be\u53d7\u3002 \u3000\u3000\u5fc3\u70db\u5ffd\u7136\u9053\uff1a\u5e08\u5144\u4e5f\u8bb8\u4e0d\u8be5\u8ba9\u4ed6\u4eec\u8d70\u7684\u3002 \u3000\u3000\u5fc3\u6e56\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u4e3a\u4f55\u4e0d\u8be5\uff1f \u3000\u3000\u5fc3\u70db\u9053\uff1a\u674e\u5bfb\u6b22\u867d\u672a\u76d7\u7ecf\uff0c\u4e5f\u4e0d\u662f\u6740\u6b7b\u4e8c\u5e08\u5144\u7684\u51f6\u624b\uff0c\u4f46\u8fd9\u8fd8\u662f\u4e0d\u80fd\u8bc1\u660e\u4ed6\u5e76\u975e\u6885\u82b1\u76d7 \uff01 \u3000\u3000\u5fc3\u6e56\u9053\uff1a\u4f60\u8981\u600e\u6837\u8bc1\u660e\uff1f \u3000\u3000\u5fc3\u70db\u9053\uff1a\u9664\u975e\u4ed6\u80fd\u5c06\u90a3\u771f\u7684\u6885\u82b1\u76d7\u627e\u51fa\u6765\u3002 \u3000\u3000\u5fc3\u6e56\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u60f3\u4ed6\u4e00\u5b9a\u4f1a\u627e\u51fa\u6765\u7684\uff0c\u800c\u4e14\u4e00\u5b9a\u4f1a\u9001\u5230\u8fd9\u91cc\uff0c\u8fd9\u90fd\u7528\u4e0d\u6211\u4eec\u5173\u5fc3 \uff0c\u53ea\u6709\u90a3\u516d\u90e8\u7ecf\u2014\u2014 \u3000\u3000\u76d7\u7ecf\u7684\u4eba\u867d\u5df2\u627e\u5230\uff0c\u4f46\u4ee5\u524d\u7684\u516d\u90e8\u85cf\u7ecf\u90fd\u65e9\u5df2\u88ab\u4ed6\u4eec\u9001\u8d70\u4e86\uff0c\u4ed6\u4eec\u5df2\u5c06\u8fd9\u516d\u90e8\u7ecf\u9001\u7ed9\u4e86 \u8c01\uff1f \u3000\u3000\u8fd9\u4ef6\u4e8b\u5e55\u540e\u662f\u5426\u53e6\u6709\u4e3b\u8c0b\u7684\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u559c\u6b22\u8d70\u8def\uff0c\u5c24\u5176\u4e0d\u559c\u6b22\u5728\u51b0\u5929\u96ea\u5730\u4e2d\u8d70\u8def\uff0c\u4f46\u73b0\u5728\u5374\u975e\u8d70\u4e0d\u53ef\uff0c\u5bd2\u98ce\u5982\u5200\uff0c\u56db \u4e0b\u54ea\u6709\u8f66\u9a6c\uff1f \u3000\u3000\u963f\u98de\u5374\u8d70\u60ef\u4e86\uff0c\u8d70\u8def\u5728\u522b\u4eba\u662f\u52b3\u52a8\uff0c\u5728\u4ed6\u5374\u662f\u79cd\u4f11\u606f\uff0c\u6bcf\u8d70\u4e00\u6bb5\u8def\uff0c\u4ed6\u7cbe\u529b\u5c31\u4f3c\u4e4e\u6062\u590d \u4e86\u4e00\u5206\u3002 \u3000\u3000\u4ed6\u4ed6\u4eec\u5df2\u5c06\u81ea\u5df1\u7684\u906d\u9047\u5168\u90fd\u8bf4\u4e86\u51fa\u6765\uff0c\u73b0\u5728\u674e\u5bfb\u6b22\u6b63\u5728\u6c89\u601d\uff0c\u4ed6\u773a\u671b\u7740\u8fdc\u65b9\uff0c\u7f13\uff1a\u4e50\u8bf4 \u4f60\u4e0d\u662f\u6885\u82b1\u76d7\uff0c\u6211\u4e5f\u4e0d\u662f\uff0c\u90a3\u4e48\u6885\u82b1\u76d7\u662f\u8c01\u5462\uff1f \u3000\u3000\u963f\u98de\u7684\u76ee\u5149\u4e5f\u843d\u5728\u8fdc\u65b9\uff0c\u9053\uff1a\u6885\u82b1\u76d7\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4ed6\u771f\u7684\u6b7b\u4e86\uff1f\u4f60\u6740\u6b7b\u7684\u90a3\u4eba\u771f\u662f\u6885\u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u6c89\u9ed8\u7740\uff0c\u7738\u5b50\u91cc\u4e00\u7247\u7a7a\u767d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4e0d\u77e5\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u6885\u82b1\u76d7\u4e5f\u8bb8\u4e0d\u662f\u7537\u4eba\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u662f\u7537\u4eba\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4e0d\u662f\u7537\u4eba\u81ea\u7136\u662f\u5973\u4eba\u3002 \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u5251\u65e0\u60c5\u4eba\u5374\u591a\u60c5>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u5251\u65e0\u60c5\u4eba\u5374\u591a\u60c5 \u3000\u3000\u963f\u98de\u542c\u8bf4\u6885\u82b1\u76d7\u662f\u5973\u4eba\uff0c\u4e0d\u7531\u7b11\u9053\uff1a\u5973\u4eba\u4f1a\u5f3a\u5978\u5973\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u4e5f\u8bb8\u6b63\u662f\u5979\u5728\u6545\u5e03\u7591\u9635\uff0c\u8ba9\u522b\u4eba\u90fd\u60f3\u4e0d\u9053\u6885\u82b1\u76d7\u662f\u5973\u4eba\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u5973\u4eba\u6ca1\u6cd5\u5b50\u5f3a\u5978\u5973\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6709\u6cd5\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u5730\u54b3\u55fd\u7740\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u90a3\u6885\u82b1\u76d7\u82e5\u679c\u771f\u662f\u5973\u4eba\uff0c\u5979\u53ef\u4ee5\u7528\u4e00\u4e2a\u7537\u4eba\u505a\u5080\u5121\uff0c\u66ff\u5979 \u505a\u8fd9\u79cd\u4e8b\uff0c\u5230\u4e86\u5fc5\u8981\u7684\u65f6\u5019\uff0c\u518d\u627e\u673a\u4f1a\u5c06\u8fd9\u7537\u4eba\u9664\u53bb\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u60f3\u5f97\u592a\u591a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u6211\u7684\u786e\u60f3\u5f97\u592a\u591a\u4e86\uff0c\u4f46\u60f3\u5f97\u591a\u4e9b\uff0c\u603b\u6bd4\u4e0d\u60f3\u597d\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e5f\u8bb8\u2014\u2014\u4e0d\u60f3\u5c31\u662f\u60f3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u8bf4\u5f97\u597d\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e5f\u8bb8\u2014\u2014\u597d\u5c31\u662f\u4e0d\u597d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u60f3\u4e0d\u5230\u4f60\u4e5f\u5b66\u4f1a\u4e86\u548c\u5c1a\u6253\u673a\u950b\u2014\u2014 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u6885\u82b1\u76d7\u4e09\u5341\u5e74\u524d\u5df2\u51fa\u73b0\u8fc7\uff0c\u5982\u4eca\u81f3\u5c11\u5df2\u8be5\u6709\u4e94\u5341\u5c81\u4ee5\u4e0a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e09\u5341\u5e74\u524d\u7684\u6885\u82b1\u76d7\uff0c\u4e5f\u8bb8\u5e76\u4e0d\u662f\u8fd9\u6b21\u51fa\u73b0\u7684\u6885\u82b1\u76d7\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u662f\u5e08\u5f92\uff0c\u4e5f\u8bb8 \u662f\u7236\u5973\u3002 \u3000\u3000\u963f\u98de\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u767e\u6653\u751f\u4e5f\u7edd\u4e0d\u662f\u76d7\u7ecf\u7684\u4e3b\u8c0b\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u65e0\u6cd5\u4ee4\u5fc3\u9274 \u4e3a\u4ed6\u5192\u9669\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5fc3\u9274\u672a\u5165\u5c11\u6797\u524d\uff0c\u5df2\u6a2a\u884c\u6c5f\u6e56\uff0c\u82e5\u662f\u60f3\u8981\u94b1\u8d22\uff0c\u5f53\u771f\u662f\u6613\u5982\u53cd\u638c\uff0c\u6240\u4ee5\u8d22\u5e1b \u5229\u8bf1\u7edd\u5bf9\u6253\u4e0d\u52a8\u4ed6\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u54e6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u767e\u6653\u751f\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u5165\u4e86\u5c11\u6797\u5bfa\u5c31\u7528\u65e0\u7528\u6b66\u4e4b\u5730\u4e86\uff0c\u6240\u4ee5\u5fc3\u9274\u4e5f\u7edd\u4e0d\u53ef\u80fd\u662f \u88ab\u4ed6\u5a01\u80c1\u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e5f\u8bb8\u4ed6\u6709\u628a\u67c4\u88ab\u767e\u6653\u751f\u634f\u5728\u624b\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u4ec0\u4e48\u628a\u67c4\u5462\uff1f \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u672a\u5165\u5c11\u6797\u524d\uff0c\u5355\u9e57\u7684\u6240\u505a\u6240\u4e3a\uff0c\u5df2\u548c\u5fc3\u9274\u65e0\u5173\u4e86\uff0c\u56e0\u4e3a\u51fa\u5bb6\u4eba\u8bb2\u7a76\u7684\u662f\u653e\u4e0b \u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff0c\u767e\u6653\u751f\u7edd\u4e0d\u53ef\u80fd\u4ee5\u4ed6\u51fa\u5bb6\u524d\u6240\u505a\u7684\u4e8b\u6765\u5a01\u80c1\u4ed6\uff0c\u4ed6\u65e2\u5df2\u5165\u4e86\u5c11\u6797\uff0c\u4e5f\u4e0d\u53ef \u80fd\u518d\u505a\u51fa\u4ec0\u4e48\u4e8b\u6765\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f55\u4ee5\u89c1\u5f97\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u4ed6\u82e5\u60f3\u505a\u574f\u4e8b\uff0c\u5c31\u4e0d\u5fc5\u5165\u5c11\u6797\u4e86\uff0c\u5c11\u6797\u5bfa\u6e05\u89c4\u4e4b\u4e25\uff0c\u5929\u4e0b\u7686\u77e5\uff0c\u4ed6\u7edd\u4e0d \u6562\u5192\u8fd9\u4e2a\u9669\uff0c\u9664\u975e\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u9664\u975e\u600e\u6837\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9664\u975e\u53c8\u6709\u4ef6\u4e8b\u80fd\u6253\u52a8\u4ed6\uff0c\u80fd\u6253\u52a8\u4ed6\u7684\u4e8b\uff0c\u7edd\u4e0d\u662f\u540d\uff0c\u4e5f\u4e0d\u662f\u5229\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u540d\u5229\u65e2\u4e0d\u80fd\u6253\u52a8\u4ed6\uff0c\u8fd8\u6709\u4ec0\u4e48\u80fd\u6253\u52a8\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u80fd\u6253\u52a8\u4ed6\u8fd9\u79cd\u4eba\u7684\uff0c\u53ea\u6709\u7edd\u4ee3\u4e4b\u7ea2\u989c\uff0c\u503e\u56fd\u4e4b\u7f8e\u8272\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u6885\u82b1\u76d7\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\uff01\u53ea\u6709\u6885\u82b1\u76d7\u8fd9\u79cd\u5973\u4eba\u624d\u80fd\u4ee4\u4ed6\u4e0d\u60dc\u505a\u5c11\u6797\u7684\u53db\u5f92\uff0c\u53ea\u6709\u6885\u82b1\u76d7\u8fd9\u79cd\u5973 \u4eba\u624d\u6562\u76d7\u5c11\u6797\u7684\u85cf\u7ecf\uff01 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u53c8\u600e\u77e5\u6885\u82b1\u76d7\u5fc5\u5b9a\u662f\u4e2a\u7edd\u8272\u7f8e\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\u9053\uff1a\u4e5f\u8bb8\u6211\u731c\u9519\u4e86\u2014\u2014\u4f46\u613f\u6211\u731c\u9519\u4e86\uff01 \u3000\u3000\u963f\u98de\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u51dd\u89c6\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u662f\u4e0d\u662f\u8981\u91cd\u56de\u5174\u4e91\u5e84\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u5b9e\u5728\u4e5f\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u5730\u65b9\u53ef\u53bb\u3002 \u3000\u3000\u591c\uff0c\u6f06\u9ed1\u7684\u591c \u3000\u3000\u53ea\u6709\u5c0f\u697c\u4e0a\u7684\u4e00\u76cf\u706f\u8fd8\u5728\u4eae\u7740\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u75f4\u75f4\u5730\u671b\u7740\u8fd9\u9b3c\u706b\u822c\u7684\u5b64\u706f\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u7136\u53d6\u51fa\u5757\u4e1d\u5dfe\uff0c\u63a9\u4f4f\u5634\u4e0d\u505c\u5730 \u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u9c9c\u8840\u6e85\u5728\u4e1d\u5dfe\u4e0a\uff0c\u5b9b\u5982\u88ab\u5bd2\u98ce\u6467\u843d\u5728\u96ea\u5730\u4e0a\u7684\u6b8b\u6885\uff0c\u674e\u5bfb\u6b22\u6084\u6084\u5c06\u4e1d\u5dfe\u85cf\u5165\u8863\u91cc\uff0c\u7b11\u7740 \u9053\uff1a\u6211\u5ffd\u7136\u4e0d\u60f3\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u963f\u98de\u4f3c\u4e4e\u5e76\u672a\u53d1\u89c9\u4ed6\u7b11\u5bb9\u7684\u8f9b\u9178\uff0c\u9053\uff1a\u4f60\u65e2\u4e3a\u4e86\uff0c\u4e3a\u4f55\u4e0d\u8fdb\u53bb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u505a\u7684\u4e8b\u6709\u8bb8\u591a\u6ca1\u6709\u539f\u56e0\u7684\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u89e3\u91ca\u4e0d\u51fa\u3002 \u3000\u3000\u963f\u98de\u7684\u7738\u5b50\u5728\u591c\u8272\u4e2d\u770b\u6765\u5c31\u50cf\u662f\u5200\u3002 \u3000\u3000\u4ed6\u7684\u8bdd\u4e5f\u50cf\u5200\uff0c\u9053\uff1a\u9f99\u5578\u4e91\u5982\u6b64\u5bf9\u4e0d\u8d77\u4f60\uff0c\u4f60\u4e0d\u60f3\u627e\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5374\u53ea\u662f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4ed6\u5e76\u6ca1\u6709\u5bf9\u4e0d\u8d77\u6211\u2014\u2014\u4e00\u4e2a\u4eba\u4e3a\u4e86\u81ea\u5df1\u7684\u59bb\u5b50\u548c\u513f\u5973\uff0c\u65e0\u8bba \u505a\u51fa\u4ec0\u4e48\u4e8b\u6765\uff0c\u90fd\u503c\u5f97\u522b\u4eba\u539f\u8c05\u7684\u3002 \u3000\u3000\u963f\u98de\u77aa\u7740\u4ed6\u826f\u4e45\u3001\u826f\u4e45\uff0c\u6162\u6162\u5730\u5782\u4e0b\u5934\uff0c\u9eef\u7136\u9053\uff1a\u4f60\u662f\u4e2a\u4ee4\u4eba\u65e0\u6cd5\u4e86\u89e3\u7684\u4eba\uff0c\u5374\u4e5f\u662f\u4e2a \u4ee4\u4eba\u65e0\u6cd5\u5fd8\u8bb0\u7684\u670b\u53cb\u3002 \u3000\u3000\u5bfb\u6b22\u9053\uff1a\u4f60\u81ea\u7136\u4e0d\u4f1a\u5fd8\u8bb0\u6211\uff0c\u56e0\u4e3a\u6211\u4eec\u4ee5\u540e\u8fd8\u65f6\u5e38\u4f1a\u89c1\u9762\u7684\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u73b0\u5728\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u73b0\u5728\u6211\u77e5\u9053\u4f60\u6709\u4ef6\u4e8b\u8981\u53bb\u505a\uff0c\u4f60\u53ea\u7ba1\u53bb\u5427\u3002 \u3000\u3000\u4e24\u4eba\u5c31\u8fd9\u6837\u7ad9\u7740\uff0c\u8c01\u4e5f\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u98ce\u5439\u8fc7\u5927\u5730\uff0c\u50cf\u5728\u545c\u54bd\u3002 \u3000\u3000\u8fdc\u5904\u4f20\u6765\u96f6\u843d\u7684\u66f4\u9f13\uff0c\u9065\u8fdc\u5f97\u5c31\u50cf\u662f\u773c\u6cea\u6ef4\u843d\u5728\u67af\u8349\u4e0a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6ca1\u6709\u661f\u5149\uff0c\u6ca1\u6709\u6708\u8272\uff0c\u53ea\u6709\u96fe\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u8d77\u96fe\u4e86\uff0c\u660e\u5929\u4e00\u5b9a\u662f\u597d\u5929\u6c14\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u662f\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5589\u5499\u91cc\u50cf\u662f\u88ab\u4ec0\u4e48\u4e1c\u897f\u585e\u4f4f\uff0c\u8fde\u58f0\u97f3\u90fd\u53d1\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u80f4\u4f53\u4e0e\u751f\u547d\u90fd\u4f3c\u5df2\u548c\u9ed1\u6697\u878d\u4e3a\u4e00\u4f53\u3002 \u3000\u3000\u963f\u98de\u63a0\u8fc7\u9ad8\u5899\uff0c\u624d\u53d1\u73b0\u51b7\u9999\u5c0f\u7b51\u90a3\u8fb9\u4e5f\u6709\u706f\u706b\u4eae\u7740\uff0c\u660f\u9ec4\u7684\u7a97\u7eb8\u4e0a\uff0c\u6620\u7740\u4e00\u4e2a\u4eba\u7ea4\u7ea4\u7684 \u8eab\u5f71\u3002 \u3000\u3000\u963f\u98de\u7684\u5fc3\u4f3c\u5728\u6536\u7f29\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u4eba\u5bf9\u7740\u5b64\u706f\uff0c\u4f3c\u5728\u770b\u4e66\uff0c\u53c8\u4f3c\u5728\u60f3\u5fc3\u4e8b\u3002 \u3000\u3000\u963f\u98de\u9aa4\u7136\u63a8\u5f00\u4e86\u95e8\u2014\u2014 \u3000\u3000\u4ed6\u63a8\u5f00\u95e8\uff0c\u5c31\u77a7\u89c1\u4e86\u4ed6\u65e6\u5915\u4e0d\u5fd8\u7684\u4eba\uff0c\u4ed6\u63a8\u5f00\u4e86\u95e8\uff0c\u5c31\u6728\u7acb\u5728\u95e8\u53e3\uff0c\u518d\u4e5f\u79fb\u4e0d\u52a8\u534a\u6b65\u3002 \u3000\u3000\u6797\u4ed9\u513f\u970d\u7136\u8f6c\u8eab\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5a07\u7b11\u9053\uff1a\u539f\u6765\u662f\u4f60\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u662f\u6211\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u81ea\u5df1\u7684\u58f0\u97f3\u4f3c\u4e4e\u4e5f\u5f88\u9065\u8fdc\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u542c\u4e0d\u6e05\u3002 \u3000\u3000\u6797\u4ed9\u513f\u62cd\u7740\u80f8\u53e3\uff0c\u5996\u7b11\u9053\uff1a\u4f60\u770b\u4f60\uff0c\u5dee\u70b9\u628a\u6211\u7684\u9b42\u90fd\u5413\u98de\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u4ee5\u4e3a\u6211\u5df2\u6b7b\u4e86\uff0c\u770b\u5230\u6211\u624d\u4f1a\u5413\u4e00\u8df3\uff0c\u662f\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7728\u7740\u773c\uff0c\u9053\uff1a\u4f60\u5728\u8bf4\u4ec0\u4e48\u5440\uff1f\u8fd8\u4e0d\u5feb\u8fdb\u6765\uff0c\u5c0f\u5fc3\u7740\u51c9\u3002 \u3000\u3000\u5979\u62c9\u7740\u963f\u98de\u7684\u624b\uff0c\u5c06\u963f\u98de\u62c9\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u963f\u98de\u7529\u5f00\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u4f60\u5728\u751f\u6c14\u2014\u2014\u662f\u5728\u751f\u8c01\u7684\u6c14\uff1f\u544a\u8bc9\u6211\uff0c\u6211\u66ff\u4f60\u51fa\u6c14\u3002 \u3000\u3000\u5979\u4f9d\u504e\u5728\u963f\u98de\u6000\u91cc\u3002 \u3000\u3000\u963f\u98de\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u5979\u6454\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8e09\u556e\u540e\u9000\uff0c\u8dcc\u5012\uff0c\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u773c\u6cea\u6162\u6162\u6d41\u4e0b\uff0c\u5782\u9996\u9053\uff1a\u6211\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u4f60\uff1f\u4f60\u4e3a\u4f55\u8981\u8fd9\u6837\u5bf9 \u6211\uff1f\u6211\u5bf9\u4f60\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u88ab\u4f60\u6253\u6b7b\u4e5f\u7518\u5fc3\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u7d27\u63e1\uff0c\u4f3c\u5df2\u5c06\u81ea\u5df1\u7684\u5fc3\u634f\u788e\u3002 \u3000\u3000\u4ed6\u5df2\u53d1\u73b0\u6797\u4ed9\u513f\u65b9\u624d\u662f\u5728\u770b\u4e66\uff0c\u770b\u7684\u662f\u7ecf\u4e66\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u7684\u85cf\u7ecf\u3002 \u3000\u3000\u963f\u98de\u7766\u770b\u7740\u5979\uff0c\u5c31\u50cf\u662f\u4ece\u672a\u89c1\u8fc7\u5979\u8fd9\u4e2a\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u963f\u98de\u51b7\u51b7\u9053\uff1a\u4f60\u600e\u4e48\u5f85\u6211\uff1f\u4f60\u660e\u77e5\u6211\u4e00\u8d70\u5165\u7533\u8001\u4e09\u7684\u5c4b\u5b50\uff0c\u5c31\u662f\u6709\u53bb\u65e0\u56de\u7684\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u2014\u2014\u4f60\u662f\u4ec0\u4e48\u610f\u601d\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u767e\u6653\u751f\u548c\u5355\u9e57\u5c06\u5c11\u6797\u85cf\u7ecf\u4ea4\u7ed9\u4f60\u65f6\uff0c\u4f60\u5c31\u8981\u4ed6\u4eec\u5728\u7533\u8001\u4e09\u7684\u5c4b\u91cc\u5e03\u4e0b\u9677\u9631\uff0c\u4f60 \u4e0d\u4f46\u8981\u5bb3\u6211\uff0c\u8fd8\u8981\u5bb3\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\u9053\uff1a\u4f60\u771f\u7684\u4ee5\u4e3a\u662f\u6211\u5bb3\u4f60\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u5f53\u7136\u662f\u4f60\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u6211\u4f1a\u53bb\u627e\u7533\u8001\u4e09\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4ee5\u624b\u63a9\u9762\uff0c\u75db\u54ed\u7740\u9053\uff1a\u4f46\u6211\u4e3a\u4ec0\u4e48\u8981\u5bb3\u4f60\uff1f\u4e3a\u4ec0\u4e48\uff1f\u2014\u2014 \u3000\u3000\u963f\u98de\u9053\uff1a\u56e0\u4e3a\u4f60\u5c31\u662f\u6885\u82b1\u76d7\uff01 \u3000\u3000\u6797\u4ed9\u513f\u5c31\u50cf\u662f\u7a81\u7136\u88ab\u62bd\u4e86\u4e00\u97ad\u5b50\uff0c\u6574\u4e2a\u4eba\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u6211\u662f\u6885\u82b1\u76d7\uff1f\u4f60\u7adf\u8bf4\u6211\u662f\u6885 \u82b1\u76d7\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u5c31\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6885\u82b1\u76d7\u5df2\u88ab\u4f60\u6740\u6b7b\u4e86\uff0c\u4f60\u2014\u2014 \u3000\u3000\u963f\u98de\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u6211\u6740\u6b7b\u7684\u90a3\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4f60\u7528\u6765\u6545\u5e03\u7591\u9635\u3001\u8f6c\u79fb\u4ed6\u4eba\u8033\u76ee\u7684\u5080\u5121 \u800c\u5df2\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u4f60\u77e5\u9053\u91d1\u4e1d\u7532\u5df2\u843d\u5165\u674e\u5bfb\u6b22\u624b\u91cc\uff0c\u77e5\u9053\u674e\u5bfb\u6b22\u4e0d\u4f1a\u4e0a\u4f60\u7684\u5f53\uff0c\u5c31\u53d1\u89c9\u81ea\u5df1\u7684 \u5904\u5883\u5df2\u5f88\u5371\u9669\u4e86\uff0c\u6240\u4ee5\u90a3\u5929\u665a\u4e0a\u4f60\u6545\u610f\u7ea6\u597d\u674e\u5bfb\u6b22\u5230\u4f60\u90a3\u91cc\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5e7d\u5e7d\u5730\u9053\uff1a\u90a3\u5929\u665a\u4e0a\u6211\u7684\u786e\u7ea6\u4e86\u674e\u5bfb\u6b22\uff0c\u53ea\u56e0\u90a3\u65f6\u6211\u8fd8\u4e0d\u4fe1\u5f97\u4f60\u3002 \u3000\u3000\u963f\u98de\u6839\u672c\u4e0d\u542c\u5979\u7684\u8bdd\uff0c\u63a5\u7740\u9053\uff1a\u4f60\u8981\u90a3\u5080\u5121\u6545\u610f\u5c06\u4f60\u52ab\u8d70\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u674e\u5bfb\u6b22\u6551\u4f60\uff0c\u8981 \u674e\u5bfb\u6b22\u5c06\u90a3\u5080\u5121\u6740\u6b7b\uff0c\u7b49\u5230\u4e16\u4eba\u90fd\u8ba4\u4e3a\u6885\u82b1\u76d7\u5df2\u6b7b\u4e86\uff0c\u4f60\u5c31\u53ef\u9ad8\u6795\u65e0\u5fe7\u4e86\uff0c\u4f60\u4e0d\u4f46\u8981\u5229\u7528\u674e \u5bfb\u6b22\uff0c\u4e5f\u5229\u7528\u4e86\u4f60\u90a3\u4f19\u4f34\u505a\u66ff\u6b7b\u9b3c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53cd\u800c\u5b89\u9759\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u4f60\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f46\u4f60\u5374\u672a\u7b97\u5230\u674e\u5bfb\u6b22\u7a81\u7136\u6709\u4e86\u610f\u5916\uff0c\u66f4\u672a\u7b97\u5230\u4f1a\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\u6551\u4e86\u4f60\u2014\u2014 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u83ab\u5fd8\u4e86\uff0c\u6211\u4e5f\u6551\u8fc7\u4f60\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u82e5\u662f\u6885\u82b1\u76d7\uff0c\u4e3a\u4f55\u8981\u6551\u4f60\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u53ea\u56e0\u90a3\u65f6\u4e8b\u60c5\u53c8\u6709\u4e86\u53d8\u5316\uff0c\u4f60\u8fd8\u8981\u5229\u7528\u6211\uff0c\u4f60\u5c31\u5c06\u6211\u85cf\u5728\u8fd9\u91cc\uff0c\u5c45\u7136\u6ca1\u6709\u4eba\u6765 \u641c\u67e5\uff0c\u90a3\u65f6\u5df2\u89c9\u5f97\u7591\u5fc3\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u8ba4\u4e3a\u9f99\u5578\u4e91\u4ed6\u4eec\u4e5f\u662f\u548c\u6211\u540c\u8c0b\u7684\u4eba\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4ed6\u4eec\u81ea\u7136\u4e0d\u77e5\u9053\u4f60\u7684\u9634\u8c0b\uff0c\u53ea\u4e0d\u8fc7\u4e5f\u53d7\u4f60\u5229\u7528\u800c\u5df2\uff0c\u4f55\u51b5\u9f99\u5578\u4e91\u65e9\u5df2\u5bf9\u674e\u5bfb\u6b22 \u5ac9\u6068\u5728\u5fc3\uff0c\u4ed6\u8fd9\u4e48\u6837\u505a\u4e5f\u662f\u4e3a\u7684\u81ea\u5df1\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8fd9\u4e9b\u8bdd\u90fd\u662f\u674e\u5bfb\u6b22\u6559\u4f60\u8bf4\u7684\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u4ee5\u4e3a\u5929\u4e0b\u7684\u7537\u4eba\u90fd\u662f\u5446\u5b50\uff0c\u90fd\u53ef\u88ab\u4f60\u73a9\u5f04\uff0c\u4f60\u5fc3\u91cc\u754f\u60e7\u7684\u53ea\u6709\u674e\u5bfb\u6b22\u4e00\u4e2a\u4eba \uff0c\u6240\u4ee5\u5343\u65b9\u767e\u8ba1\u5730\u60f3\u9664\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u7684\u58f0\u97f3\u4e5f\u5728\u98a4\u6296\uff0c\u54ac\u7d27\u7259\u5173\uff0c\u63a5\u7740\u9053\uff1a\u4f60\u4e0d\u4f46\u5fc3\u72e0\u624b\u8fa3\uff0c\u800c\u4e14\u8d2a\u5f97\u65e0\u538c\uff0c\u8fde\u5c11\u6797 \u5bfa\u7684\u85cf\u7ecf\u4f60\u90fd\u60f3\u8981\uff0c\u8fde\u51fa\u5bb6\u4eba\u4f60\u90fd\u4e0d\u80af\u653e\u8fc7\uff0c\u4f60\u2014\u2014\u4f60 \u3000\u3000\u6797\u4ed9\u513f\u7684\u773c\u6cea\u4e5f\u6d41\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u7684\u786e\u770b\u9519\u4e86\u4f60\u3002 \u3000\u3000\u963f\u98de\u4e00\u5b57\u5b57\uff1a\u4f46\u6211\u5374\u672a\u770b\u9519\u4f60\uff01 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u82e5\u8bf4\u8fd9\u90e8\u7ecf\u4e0d\u662f\u767e\u6653\u751f\u548c\u5355\u9e57\u7ed9\u6211\u7684\uff0c\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u662f\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u65e0\u8bba\u8bf4\u4ec0\u4e48\uff0c\u6211\u90fd\u518d\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff01 \u3000\u3000\u6797\u4ed9\u513f\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u603b\u7b97\u660e\u767d\u4e86\u4f60\u7684\u610f\u601d\u2014\u2014\u6211\u603b\u7b97\u660e\u767d\u4e86\u4f60\u7684\u5fc3\uff0d \u3000\u3000\u5979\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5411\u963f\u98de\u8d70\u4e86\u8fc7\u53bb\uff0c\u5979\u8d70\u5f97\u5f88\u6162\uff0c\u4f46\u6b65\u5b50\u5374\u5f88\u575a\u5b9a\uff0c\u50cf\u662f\u5df2\u4e0b\u4e86\u5f88\u5927 \u7684\u51b3\u5fc3\u3002 \u3000\u3000\u98ce\u5728\u547c\u5578\uff0c\u706f\u706b\u98d8\u6447\u3002 \u3000\u3000\u95ea\u52a8\u7740\u7684\u706f\u5149\u6620\u7740\u5979\u82cd\u767d\u7edd\u7f8e\u7684\u8138\uff0c\u6620\u7740\u5979\u79cb\u6c34\u822c\u7684\u773c\u6ce2\uff0c\u5979\u75f4\u75f4\u5730\u671b\u7740\u963f\u98de\uff0c\u5e7d\u5e7d\u9053 \uff1a\u6211\u77e5\u9053\u4f60\u662f\u6765\u6740\u6211\u7684\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u963f\u98de\u7684\u62f3\u7d27\u63e1\uff0c\u5634\u7d27\u95ed\u3002 \u3000\u3000\u5979\u6307\u7740\u81ea\u5df1\u7684\u5fc3\u9053\uff1a\u4f60\u8170\u7554\u65e2\u7136\u6709\u5251\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u624b\uff1f\u6211\u53ea\u671b\u4f60\u80fd\u5f80\u8fd9\u91cc\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u5df2\u63e1\u4f4f\u4e86\u5251\u67c4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9616\u8d77\u773c\u5e18\uff0c\u98a4\u58f0\u9053\uff1a\u4f60\u5feb\u52a8\u624b\u5427\uff0c\u80fd\u6b7b\u5728\u4f60\u624b\uff0c\u6211\u6b7b\u5728\u7518\u5fc3\u3002 \u3000\u3000\u5979\u80f8\u819b\u8d77\u4f0f\uff0c\u4f3c\u5728\u8f7b\u8f7b\u98a4\u6296\u3002 \u3000\u3000\u5979\u957f\u957f\u7684\u776b\u6bdb\u4e30\u773c\u5e18\uff0c\u60ac\u6302\u7740\u4e24\u7c92\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u963f\u98de\u4e0d\u6562\u770b\u5979\uff0c\u5782\u4e0b\u773c\u671b\u7740\u81ea\u5df1\u7684\u5251\u3002 \u3000\u3000\u65e0\u60c5\u7684\u5251\uff0c\u51b7\u800c\u950b\u5229\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u5168\u90fd\u627f\u8ba4\u4e86\uff1f \u3000\u3000\u6797\u4ed9\u513f\u773c\u5e18\u62ac\u8d77\uff0c\u51dd\u6ce8\u7740\u4ed6\u3002 \u3000\u3000\u5979\u773c\u4e2d\u5145\u6ee1\u4e86\u51c4\u51c9\uff0c\u5145\u6ee1\u4e86\u5e7d\u6028\uff0c\u5145\u6ee1\u4e86\u7231\uff0c\u4e5f\u5145\u6ee1\u4e86\u6068\u2014\u2014\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u59a3\u5979\u7684 \u773c\u8272\u66f4\u80fd\u6253\u52a8\u4eba\u7684\u5fc3\u3002 \u3000\u3000\u5979\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u51c4\u51c9\u7684\u5fae\u7b11\uff0c\u5e7d\u5e7d\u9053\uff1a\u4f60\u662f\u6211\u8fd9\u4e00\u751f\u4e2d\u6700\u7231\u7684\u4eba\uff0c\u82e5\u8fde\u4f60\u90fd\u4e0d\u76f8\u4fe1\u6211\uff0c \u6211\u6d3b\u5728\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f \u3000\u3000\u963f\u98de\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u6307\u8282\u5df2\u53d1\u767d\uff0c\u624b\u80cc\u5df2\u9732\u51fa\u9752\u7b4b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9eef\u7136\u9053\uff1a\u53ea\u8981\u4f60\u8ba4\u4e3a\u6211\u662f\u6885\u82b1\u76d7\uff0c\u53ea\u8981\u4f60\u8ba4\u4e3a\u6211\u771f\u662f\u90a3\u4e48\u6076\u6bd2\u7684\u5973\u4eba\uff0c\u4f60\u5c31\u6740\u4e86 \u6211\u5427\uff0c\u6211\u2014\u2014\u6211\u7edd\u4e0d\u6068\u4f60\u3002 \u3000\u3000\u5251\u67c4\u575a\u786c\uff0c\u51b0\u51b7\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u5374\u5df2\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u65e0\u60c5\u7684\u5251\uff0c\u5251\u65e0\u60c5\uff0c\u4f46\u4eba\u5462\uff1f \u3000\u3000\u4eba\u600e\u80fd\u65e0\u60c5\uff1f \u3000\u3000\u706f\u706d\u4e86\u3002 \u3000\u3000\u4f46\u6797\u4ed9\u513f\u7edd\u4ee3\u7684\u98ce\u59ff\uff0c\u5728\u9ed1\u6697\u4e2d\u5374\u66f4\u52a8\u4eba\u3002 \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5728\u8fd9\u7edd\u671b\u7684\u9ed1\u6697\u4e2d\uff0c\u5979\u7684\u547c\u5438\u58f0\u542c\u6765\u5c31\u5b9b\u5982\u6e29\u67d4\u7684\u7ec6\u8bed\uff0c\u53c8\u5b9b\u5982\u4ee4\u4eba\u5fc3 \u788e\u7684\u547b\u541f\u3002 \u3000\u3000\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u529b\u91cf\u80fd\u6bd4\u60c5\u7231\u7684\u529b\u91cf\u66f4\u5927\uff1f \u3000\u3000\u9762\u5bf9\u7740\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u4eba\uff0c\u9762\u5bf9\u7740\u81ea\u5df1\u4e00\u751f\u4e2d\u6700\u5f3a\u70c8\u7684\u60c5\u611f\uff0c\u9762\u5bf9\u7740\u8fd9\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff01 \u2014\u2014 \u3000\u3000\u963f\u98de\u8fd9\u4e00\u5251\u662f\u4e0d\u662f\u8fd8\u80fd\u523a\u5f97\u4e0b\u53bb\uff1f\uff01 \u3000\u3000\u5251\u65e0\u60c5\uff01\u4eba\u5374\u591a\u60c5\uff01"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u5c0f\u5e97\u4e2d\u7684\u602a\u5ba2",content:(0,s.jsx)(s.Fragment,{children:"\u79cb\uff0c\u6728\u53f6\u8427\u8427\u3002 \u3000\u3000\u8857\u4e0a\u7684\u5c3d\u5934\uff0c\u6709\u5ea7\u5de8\u5927\u7684\u5b85\u9662\uff0c\u770b\u6765\u4e5f\u6b63\u548c\u679d\u5934\u7684\u9ec4\u53f6\u4e00\u6837\uff0c\u5df2\u5230\u4e86\u5c06\u8fd1\u67af\u843d\u7684\u65f6\u5019\u3002 \u3000\u3000\u90a3\u4e24\u6247\u6cc9\u6f06\u5927\u95e8\uff0c\u51e0\u4e4e\u5df2\u6709\u4e00\u5e74\u591a\u672a\u66fe\u5f00\u8fc7\u4e86\uff0c\u95e8\u4e0a\u7684\u6cc9\u6f06\u65e9\u5df2\u5265\u843d\uff0c\u94dc\u73af\u4e5f\u5df2\u751f\u4e86\u4e1d \u9508\u3002 \u3000\u3000\u9ad8\u5899\u5185\u4e45\u5df2\u542c\u4e0d\u5230\u4eba\u58f0\uff0c\u53ea\u6709\u5728\u79cb\u521d\u590f\u672b\uff0c\u624d\u5076\u7136\u4f1a\u4f20\u51fa\u79cb\u866b\u4f4e\u8bc9\uff0c\u9e1f\u8bed\u557e\u5541\uff0c\u5374\u66f4\u886c \u51fa\u4e86\u8fd9\u5b85\u9662\u7684\u5bc2\u5bde\u4e0e\u8427\u7d22\u3002 \u3000\u3000\u4f46\u8fd9\u5b85\u9662\u4e5f\u6709\u8fc7\u8f89\u714c\u7684\u65f6\u5019\uff0c\u56e0\u4e3a\u5c31\u5728\u8fd9\u91cc\uff0c\u5df2\u8bde\u751f\u8fc7\u4e03\u4f4d\u8fdb\u58eb\u3001\u4e09\u4f4d\u63a2\u82b1\uff0c\u5176\u4e2d\u8fd8\u6709 \u4f4d\u60ca\u624d\u7edd\u8273\u3001\u76d6\u4e16\u65e0\u53cc\u7684\u6b66\u6797\u540d\u4fa0\u3002 \u3000\u3000\u751a\u81f3\u5c31\u5728\u4e24\u5e74\u524d\uff0c\u5b85\u9662\u5df2\u6362\u4e86\u4e3b\u4eba\u65f6\uff0c\u8fd9\u91cc\u8fd8\u662f\u53d1\u751f\u8fc7\u8bb8\u591a\u4ef6\u8f70\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u4e5f\u5df2\u4e0d \u77e5\u6709\u591a\u5c11\xd7\u54a4\u98ce\u4e91\u7684\u6c5f\u6e56\u9ad8\u624b\u846c\u8eab\u6b64\u5904\u3002 \u3000\u3000\u6b64\u540e\uff0c\u8fd9\u5b85\u9662\u5c31\u7a81\u7136\u6c89\u5bc2\u4e86\u4e0b\u6765\uff0c\u5b83\u4e24\u4ee3\u4e3b\u4eba\u7a81\u7136\u95f4\u5c31\u53d8\u5f97\u6d88\u606f\u6c89\u6c89\uff0c\u4e0d\u77e5\u6240\u7ec8\u3002 \u3000\u3000\u4e8e\u662f\u6c5f\u6e56\u95f4\u5c31\u6709\u79cd\u53ef\u6015\u7684\u4f20\u8bf4\uff0c\u90fd\u8bf4\u8fd9\u5730\u65b9\u662f\u5ea7\u51f6\u5b85\uff01 \u3000\u3000\u73b0\u5728\uff0c\u8fd9\u91cc\u767d\u5929\u5df2\u4e0d\u518d\u6709\u7b11\u8bed\u55a7\u54d7\uff0c\u665a\u4e0a\u4e5f\u65e9\u5df2\u4e0d\u518d\u6709\u8f89\u714c\u706f\u5149\uff0c\u53ea\u6709\u540e\u56ed\u5c0f\u697c\u4e0a\u7684\u4e00 \u76cf\u5b64\u706f\u7ec8\u591c\u4e0d\u7184\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u4f3c\u4e4e\u6709\u4e2a\u4eba\u5728\u65e5\u65e5\u591c\u591c\u7684\u7b49\u5f85\u7740\uff0c\u53ea\u4e0d\u8fc7\u8c01\u4e5f\u4e0d\u77e5\u5979\u7a76\u7adf\u662f\u5728\u7b49\u5f85\u7740\u4ec0\u4e48\uff1f\u2014\u2014 \uff0d \u3000\u3000\u4f46\u65e0\u8bba\u591a\u5351\u8d31\u3001\u591a\u9634\u6697\u7684\u5730\u65b9\uff0c\u90fd\u6709\u4eba\u5728\u9ed8\u9ed8\u5730\u6d3b\u7740\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u522b\u5904\u53ef\u53bb\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u5bf9\u4eba\u751f\u5df2\u538c\u5026\uff0c\u5b81\u613f\u8eb2\u5728\u8fd9\u79cd\u5730 \u65b9\uff0c\u88ab\u4e16\u4eba\u9057\u5fd8\u3002 \u3000\u3000\u5df7\u5802\u91cc\u6709\u4e2a\u9e21\u6bdb\u5c0f\u5e97\uff0c\u524d\u9762\u5356\u4e9b\u7c97\u7c9d\u7684\u996e\u98df\uff0c\u540e\u9762\u6709\u4e09\u4e94\u95f4\u7b80\u964b\u7684\u5ba2\u623f\uff0c\u5e97\u4e3b\u4eba\u5b59\u9a7c\u5b50 \u662f\u4e2a\u6b8b\u5e9f\u7684\u4f8f\u5112\u3002 \u3000\u3000\u4ed6\u867d\u7136\u660e\u77e5\u9053\u8fd9\u5df7\u5802\u91cc\u7edd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u9ad8\u8d35\u7684\u4e3b\u987e\uff0c\u4f46\u5374\u5b81\u613f\u5728\u8fd9\u91cc\u7b49\u7740\u4e9b\u5351\u8d31\u7684\u8fc7\u5ba2\uff0c \u8fdb\u6765\u4ee5\u4f4e\u5fae\u7684\u4ee3\u4ef7\u6362\u53d6\u98df\u5bbf\u3002 \u3000\u3000\u4ed6\u5b81\u613f\u5728\u8fd9\u91cc\u8fc7\u4ed6\u6e05\u82e6\u5351\u8d31\u7684\u751f\u6d3b\uff0c\u4e5f\u4e0d\u613f\u8d70\u51fa\u53bb\u542c\u4eba\u4eec\u7684\u5632\u7b11\uff0c\u56e0\u4e3a\u4ed6\u5df2\u61c2\u5f97\u65e0\u8bba\u591a \u5c11\u8d22\u5bcc\uff0c\u90fd\u65e0\u6cd5\u6362\u6765\u5fc3\u5934\u7684\u5e73\u9759\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u662f\u5bc2\u5bde\u7684\u3002 \u3000\u3000\u4e00\u5e74\u591a\u524d\uff0c\u9ec4\u9519\u7684\u65f6\u5019\uff0c\u8fd9\u5c0f\u5e97\u91cc\u6765\u4e86\u4f4d\u4e0e\u4f17\u4e0d\u540c\u7684\u5ba2\u4eba\uff0c\u5176\u5b9e\u4ed6\u7a7f\u7684\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48\u5f88 \u534e\u8d35\u7684\u8863\u670d\uff0c\u957f\u5f97\u4e5f\u5e76\u4e0d\u7279\u522b\u3002 \u3000\u3000\u4ed6\u8eab\u6750\u867d\u5f88\u9ad8\uff0c\u9762\u76ee\u867d\u4e5f\u8fd8\u7b97\u5f97\u82f1\u4fca\uff0c\u4f46\u770b\u6765\u5374\u5f88\u6194\u7fe0\uff0c\u7ec8\u5e74\u90fd\u5e26\u7740\u75c5\u5bb9\uff0c\u800c\u4e14\u8fd8\u4e0d\u65f6 \u5f2f\u4e0b\u8170\u54b3\u55fd\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u4eba\u3002 \u3000\u3000\u4f46\u5b59\u9a7c\u5b50\u4e00\u773c\u770b\u5230\u4ed6\u65f6\uff0c\u5c31\u89c9\u5f97\u4ed6\u6709\u8bb8\u591a\u4e0e\u4f17\u4e0d\u540c\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u5bf9\u5b59\u9a7c\u5b50\u7684\u6b8b\u5e9f\u5e76\u6ca1\u6709\u5632\u7b11\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\uff0c\u66f4\u6ca1\u6709\u88c5\u51fa\u7279\u522b\u601c\u60af\u7684\u540c\u60c5\u795e\u8272\u3002 \u3000\u3000\u8fd9\u79cd\u540c\u60c5\u6709\u65f6\u6bd4\u5632\u7b11\u8fd8\u8981\u4ee4\u4eba\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u4ed6\u5bf9\u4e8e\u9152\u65e2\u4e0d\u6311\u5254\uff0c\u4e5f\u4e0d\u8d5e\u7f8e\u3002\u4ed6\u6839\u672c\u5c31\u5f88\u5c11\u8bf4\u8bdd\u3002 \u3000\u3000\u6700\u5947\u602a\u7684\u662f\uff0c\u81ea\u4ece\u4ed6\u7b2c\u4e00\u6b21\u8d70\u8fdb\u8fd9\u5c0f\u5e97\uff0c\u5c31\u6ca1\u6709\u8d70\u51fa\u53bb\u8fc7\u3002 \u3000\u3000\u7b2c\u4e00\u6b21\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u9009\u4e86\u89d2\u843d\u91cc\u7684\u4e00\u5f20\u684c\u5b50\u5750\u4e0b\uff0c\u8981\u4e00\u789f\u8c46\u5e72\u3001\u4e00\u789f\u725b\u8089\u3001\u4e24\u4e2a\u9992\u5934\u548c \u4e03\u58f6\u9152\u3002 \u3000\u3000\u4e03\u58f6\u9152\u559d\u5b8c\u4e86\uff0c\u4ed6\u5c31\u53eb\u5b59\u9a7c\u5b50\u518d\u52a0\u9152\uff0c\u7136\u540e\u5c31\u5230\u6700\u540e\u9762\u7684\u4e00\u95f4\u5c4b\u5b50\u91cc\u5750\u4e0b\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929 \u9ec4\u660f\u624d\u8d70\u51fa\u6765\u3002 \u3000\u3000\u7b49\u4ed6\u51fa\u6765\u65f6\uff0c\u8fd9\u4e03\u58f6\u9152\u4e5f\u5df2\u559d\u5149\u4e86\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5df2\u8fc7\u4e86\u4e00\u5e74\u591a\uff0c\u6bcf\u5929\u665a\u4e0a\u4ed6\u90fd\u662f\u5750\u5728\u89d2\u843d\u91cc\u90a3\u684c\u5b50\u4e0a\uff0c\u8fd8\u662f\u8981\u4e00\u789f\u8c46\u5e72\u3001\u4e00\u789f\u725b \u8089\u3001\u4e24\u4e2a\u9992\u5934\u548c\u4e03\u58f6\u9152\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u54b3\u55fd\uff0c\u4e00\u9762\u559d\u9152\uff0c\u7b49\u4e03\u58f6\u9152\u559d\u5b8c\uff0c\u4ed6\u5c31\u5e26\u7740\u53e6\u4e03\u58f6\u9152\u56de\u5230\u6700\u540e\u9762\u90a3\u95f4\u5c4b\u5b50\u91cc\uff0c\u4e00 \u76f4\u5230\u7b2c\u4e8c\u5929\u9ec4\u660f\u624d\u9732\u9762\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u4e5f\u662f\u4e2a\u9152\u5f92\uff0c\u5bf9\u8fd9\u4eba\u7684\u9152\u91cf\u4ed6\u5b9e\u5728\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u80fd\u559d\u5b8c\u5341\u56db\u58f6\u9152\u800c\u4e0d\u9189\u7684\u4eba \uff0c\u4ed6\u4e00\u751f\u4e2d\u8fd8\u672a\u89c1\u5230\u8fc7\u3002 \u3000\u3000\u6709\u65f6\u4ed6\u4e5f\u5fcd\u4e0d\u4f4f\u95ee\u95ee\u8fd9\u4eba\u7684\u59d3\u540d\uff0c\u5374\u8fd8\u662f\u5fcd\u4f4f\u4e86\uff0c\u56e0\u4e3a\u77e5\u9053\u5373\u4f7f\u95ee\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u5f97\u5230\u7b54\u8986 \u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5e76\u4e0d\u662f\u4e2a\u591a\u5634\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u6837\u8fc7\u4e86\u597d\u51e0\u4e2a\u6708\uff0c\u6709\u4e00\u9635\u5929\u6c14\u7279\u522b\u5bd2\u51b7\uff0c\u63a5\u8fde\u4e0b\u4e86\u5341\u51e0\u5929\u96e8\uff0c\u665a\u4e0a\u5b59\u9a7c\u5b50\u5230\u540e\u9762\u53bb\uff0c \u53d1\u73b0\u90a3\u95f4\u5c4b\u5b50\u7684\u95e8\u662f\u5f00\u7740\u7684\uff0c\u8fd9\u5947\u602a\u7684\u5ba2\u4eba\u5df2\u54b3\u5012\u5728\u5730\u4e0a\uff0c\u8138\u8272\u7ea2\u5f97\u53ef\u6015\uff0c\u7b80\u76f4\u7ea2\u5f97\u50cf\u8840\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u6276\u8d77\u4e86\u4ed6\uff0c\u534a\u591c\u4e09\u66f4\u53bb\u66ff\u4ed6\u6293\u836f\u3001\u714e\u836f\uff0c\u770b\u987e\u4e86\u4ed6\u4e09\u5929\uff0c\u4e09\u5929\u540e\u4ed6\u521a\u8d77\u5e86\uff0c\u5c31\u53c8 \u5f00\u59cb\u8981\u9152\u3002 \u3000\u3000\u90a3\u65f6\u5b59\u9a7c\u5b50\u624d\u77e5\u9053\u8fd9\u4eba\u662f\u5728\u81ea\u5df1\u627e\u6b7b\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u529d\u4ed6\uff1a\u50cf\u8fd9\u6837\u559d\u4e0b\u53bb\uff0c\u4efb\u4f55\u4eba\u90fd\u6d3b\u4e0d\u957f \u7684\u3002 \u3000\u3000\u8fd9\u4eba\u5374\u53ea\u662f\u6de1\u6de1\u5730\u7b11\u4e86\u7b11\uff0c\u53cd\u95ee\u4ed6\uff1a\u4ed6\u4ee5\u4e3a\u6211\u4e0d\u559d\u9152\u5c31\u80fd\u6d3b\u5f97\u957f\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4ece\u90a3\u5929\u4e4b\u540e\uff0c\u4e24\u4eba\u5c31\u53d8\u6210\u4e86\u670b\u53cb\u3002 \u3000\u3000\u6ca1\u6709\u5ba2\u4eba\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4f1a\u627e\u5b59\u9a7c\u5b50\u966a\u4ed6\u559d\u9152\uff0c\u4e1c\u626f\u897f\u62c9\u5730\u95f2\u804a\u7740\uff0c\u5b59\u9a7c\u5b50\u53d1\u73b0\u8fd9\u4eba\u61c2\u7684 \u53ef\u771f\u4e0d\u5c11\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u4e00\u4ef6\u4e8b\u4e0d\u80af\u8bf4\uff0c\u90a3\u5c31\u662f\u4ed6\u7684\u59d3\u540d\u6765\u5386\u3002 \u3000\u3000\u6709\u4e00\u6b21\u5b59\u9a7c\u5b50\u5fcd\u4e0d\u4f4f\u95ee\u4ed6\uff1a\u6211\u4eec\u5df2\u662f\u670b\u53cb\uff0c\u6211\u8be5\u600e\u4e48\u79f0\u547c\u4f60\u5462\uff1f \u3000\u3000\u4ed6\u8fdf\u7591\u4e86\u534a\u5929\uff0c\u624d\u7b11\u7740\u56de\u7b54\uff1a\u6211\u662f\u4e2a\u9152\u9b3c\uff0c\u4e0d\u6298\u4e0d\u6263\u7684\u9152\u9b3c\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53eb\u6211\u9152\u9b3c\u5462\uff1f \u3000\u3000\u4e8e\u662f\u5b59\u9a7c\u5b50\u53c8\u53d1\u73b0\u8fd9\u4eba\u5fc5\u5b9a\u6709\u6bb5\u6781\u4f24\u5fc3\u7684\u5f80\u4e8b\uff0c\u6240\u4ee5\u8fde\u81ea\u5df1\u7684\u59d3\u540d\u90fd\u4e0d\u613f\u63d0\u8d77\uff0c\u60c5\u613f\u5c06 \u4e00\u751f\u57cb\u846c\u5728\u9152\u58f6\u91cc\u3002 \u3000\u3000\u9664\u4e86\u559d\u9152\u5916\uff0c\u4ed6\u8fd8\u6709\u4e2a\u5947\u602a\u7684\u55dc\u597d\u3002 \u3000\u3000\u90a3\u5c31\u662f\u96d5\u523b\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u603b\u662f\u62ff\u7740\u628a\u5c0f\u5200\u5728\u523b\u6728\u5934\uff0c\u4f46\u5b59\u9a7c\u5b50\u5374\u4ece\u4e0d\u77e5\u9053\u4ed6\u5728\u523b\u4ec0\u4e48\uff0c\u56e0\u4e3a\u4ed6\u4ece\u672a\u5c06\u624b\u91cc \u523b\u7740\u7684\u96d5\u50cf\u5b8c\u6210\u8fc7\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4e2a\u5947\u602a\u7684\u5ba2\u4eba\uff0c\u602a\u5f97\u53ef\u6015\u3002 \u3000\u3000\u4f46\u6709\u65f6\u5b59\u9a7c\u5b50\u5374\u5e0c\u671b\u4ed6\u6c38\u8fdc\u4e5f\u4e0d\u8981\u8d70\u3002 \u3000\u3000\u8fd9\u5929\u65e9\u4e0a\uff0c\u5b59\u9a7c\u5b50\u8d77\u5e86\u65f6\u53d1\u89c9\u5929\u6c14\u5df2\u8d8a\u6765\u8d8a\u51c9\u4e86\uff0c\u7279\u522b\u4ece\u7bb1\u5b50\u91cc\u627e\u51fa\u4ef6\u8001\u68c9\u8884\u7a7f\u4e0a\uff0c\u624d \u8d70\u5230\u524d\u9762\u3002 \u3000\u3000\u4ed6\u521a\u5750\u4e0b\u5c31\u770b\u5230\u6709\u4e24\u4e2a\u4eba\u9a91\u7740\u9a6c\u4ece\u524d\u9762\u7ed5\u8fc7\u6765\u3002 \u3000\u3000\u5df7\u5802\u91cc\u9a91\u9a6c\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u5b59\u9a7c\u5b50\u4e5f\u4e0d\u7981\u591a\u77a7\u4e86\u4e24\u773c\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e24\u4eba\u90fd\u7a7f\u7740\u674f\u9ec4\u8272\u7684\u957f\u886b\uff0c\u524d\u9762\u4e00\u4eba\u6d53\u7709\u5927\u773c\uff0c\u540e\u9762\u4e00\u4eba\u9e70\u9f3b\u5982\uff0c\u4e24\u4eba\u51f3\u4e0b\u90fd\u7559 \u7740\u77ed\u987b\uff0c\u770b\u8d77\u6765\u90fd\u53ea\u6709\u4e09\u5341\u591a\u5c81\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u76f8\u8c8c\u5e76\u4e0d\u51fa\u4f17\uff0c\u4f46\u8eab\u4e0a\u7a7f\u7684\u674f\u9ec4\u8272\u957f\u886b\u5374\u6781\u8000\u773c\uff0c\u4e24\u4eba\u90fd\u6ca1\u6709\u7559\u610f\u5b59\u9a7c\u5b50\uff0c\u5374\u4e0d \u65f6\u4ef0\u8d77\u5934\u5411\u9ad8\u5899\u5185\u63a2\u671b\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u7ee7\u7eed\u9761\u4ed6\u7684\u8c46\u8150\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u4e24\u4eba\u7edd\u4e0d\u4f1a\u662f\u4ed6\u7684\u4e3b\u987e\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u8d70\u8fc7\u5df7\u5802\uff0c\u679c\u7136\u53c8\u7ed5\u5230\u524d\u9762\u53bb\u4e86\uff0c\u53ef\u662f\u8fd8\u6ca1\u8fc7\u591a\u4e45\uff0c\u4e24\u4eba\u53c8\u4ece\u53e6\u4e00\u5934\u7ed5\u4e86\u56de\u6765 \u3002 \u3000\u3000\u8fd9\u6b21\u4e24\u4eba\u7adf\u5728\u5c0f\u5e97\u524d\u4e0b\u4e86\u9a6c\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u813e\u6c14\u867d\u53e4\u602a\uff0c\u6bd5\u7adf\u662f\u505a\u751f\u610f\u7684\u4eba\uff0c\u7acb\u523b\u505c\u4e0b\u624b\u95ee\u9053\uff1a\u4e24\u4f4d\u53ef\u8981\u5403\u559d\u70b9\u4ec0\u4e48\uff1f \u3000\u3000\u6d53\u7709\u5927\u773c\u7684\u9ec4\u886b\u4eba\u9053\uff1a\u54b1\u4eec\u4ec0\u4e48\u4e5f\u4e0d\u8981\uff0c\u53ea\u60f3\u95ee\u4f60\u4e24\u53e5\u8bdd\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u53c8\u5f00\u59cb\u9761\u8c46\u8150\uff0c\u4ed6\u5bf9\u8bf4\u8bdd\u5e76\u4e0d\u611f\u5174\u8da3\u3002 \u3000\u3000\u9e70\u9f3b\u5982\u52fe\u7684\u9ec4\u886b\u4eba\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u54b1\u4eec\u5c31\u8981\u4e70\u4f60\u7684\u8bdd\uff0c\u4e00\u53e5\u8bdd\u4e00\u94b1\u94f6\u5b50\uff0c\u5982\u4f55\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u7684\u5174\u8da3\u6765\u4e86\uff0c\u70b9\u5934\u9053\uff1a\u597d\u3002 \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u5df2\u4f38\u51fa\u4e86\u4e00\u6839\u624b\u6307\u5934\u3002 \u3000\u3000\u6d53\u7709\u5927\u773c\u7684\u9ec4\u886b\u4eba\u7b11\u9053\uff1a\u8fd9\u4e5f\u7b97\u4e00\u53e5\u8bdd\u4e48\uff1f\u4f60\u505a\u751f\u610f\u7684\u95e8\u69db\u5012\u771f\u7cbe\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u8fd9\u5f53\u7136\u7b97\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u4f38\u51fa\u4e86\u4e24\u6839\u6307\u5934\u3002 \u3000\u3000\u9e70\u9f3b\u4eba\u9053\uff1a\u4f60\u5728\u8fd9\u91cc\u5df2\u4f4f\u4e86\u591a\u4e45\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4e8c\u4e09\u5341\u5e74\u4e86\u3002 \u3000\u3000\u9e70\u9f3b\u4eba\u9053\uff1a\u4f60\u5bf9\u9762\u8fd9\u5ea7\u5b85\u9662\u662f\u8c01\u7684\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u662f\u674e\u5bb6\u7684\u3002 \u3000\u3000\u9e70\u9f3b\u5b50\u9053\uff1a\u540e\u6765\u7684\u4e3b\u4eba\u5462\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u59d3\u9f99\uff0c\u53eb\u9f99\u5578\u4e91\u3002 \u3000\u3000\u9e70\u9f3b\u4ece\u9053\uff1a\u4f60\u89c1\u8fc7\u4ed6\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u9e70\u9f3b\u4eba\u9053\uff1a\u4ed6\u7684\u4eba\u5462\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\uff1a\u51fa\u95e8\u4e86\u3002 \u3000\u3000\u9e70\u9f3b\u5b50\u9053\uff1a\u4ec0\u4e48\u65f6\u5019\u51fa\u95e8\u7684\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4e00\u5e74\u591a\u4ee5\u524d\u3002 \u3000\u3000\u9e70\u9f3b\u4eba\u9053\uff1a\u4ee5\u540e\u6709\u6ca1\u6709\u56de\u6765\u8fc7\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u9e70\u9f3b\u4eba\u9053\uff1a\u4f60\u65e2\u672a\u89c1\u8fc7\u4ed6\uff0c\u600e\u4f1a\u5bf9\u4ed6\u77e5\u9053\u5f97\u5982\u6b64\u8be6\u7ec6\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\uff1a\u4ed6\u4eec\u5bb6\u7684\u53a8\u5b50\u5e38\u5728\u8fd9\u4e70\u9152\u3002 \u3000\u3000\u9e70\u9f3b\u4eba\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9053\uff1a\u8fd9\u4e24\u5929\u6709\u6ca1\u6709\u964c\u751f\u4eba\u6765\u95ee\u8fc7\u4f60\u7684\u8bdd\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u6ca1\u6709\u2014\u2014\u82e5\u662f\u6709\uff0c\u8fd9\u53ea\u6015\u65e9\u5df2\u53d1\u8d22\u4e86\u3002 \u3000\u3000\u6d53\u7709\u5927\u773c\u9ec4\u886b\u4eba\u7b11\u9053\uff1a\u4eca\u5929\u5c31\u8ba9\u4f60\u53d1\u4e2a\u5c0f\u8d22\u5427\u3002 \u3000\u3000\u4ed6\u629b\u4e86\u952d\u94f6\u5b50\u51fa\u6765\uff0c\u4e24\u4eba\u518d\u4e5f\u4e0d\u95ee\u522b\u7684\uff0c\u4e00\u9f50\u4e0a\u9a6c\u800c\u53bb\uff0c\u5728\u8def\u4e0a\u8fd8\u662f\u4e0d\u4f4f\u63a2\u9996\u5411\u9ad8\u5899\u5185 \u7aa5\u671b\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u770b\u7740\u624b\u91cc\u7684\u94f6\u5b50\uff0c\u5583\u5583\u9053\uff1a\u539f\u6765\u6709\u65f6\u5019\u8d5a\u94b1\u4e5f\u5bb9\u6613\u5f97\u5f88\u2014\u2014 \u3000\u3000\u4ed6\u8f6c\u8fc7\u5934\uff0c\u5ffd\u7136\u53d1\u73b0\u90a3\u9152\u9b3c\u4e0d\u77e5\u4f55\u65f6\u5df2\u51fa\u6765\uff0c\u6b63\u7ad9\u5728\u90a3\u91cc\u5411\u9ec4\u886b\u4eba\u7684\u53bb\u8def\u51dd\u89c6\u7740\uff0c\u9762\u4e0a \u5e26\u7740\u79cd\u6df1\u601d\u7684\u8868\u60c5\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u7b11\u4e86\u7b11\u9053\uff1a\u4f60\u4eca\u5929\u5012\u65e9\u3002 \u3000\u3000\u90a3\u9152\u9b3c\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6628\u5929\u665a\u4e0a\u6211\u559d\u5f97\u5feb\uff0c\u4eca\u5929\u4e00\u65e9\u5c31\u65ad\u7cae\u4e86\u3002 \u3000\u3000\u4ed6\u4f4e\u4e0b\u5934\uff0c\u54b3\u55fd\u4e86\u4e00\u9635\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\u4e86\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4e5d\u6708\u5341\u56db\u3002 \u3000\u3000\u90a3\u9152\u9b3c\u82cd\u767d\u7684\u8138\u53c8\u8d77\u4e86\u4e00\u9635\u5f02\u6837\u7684\u7ea2\u6655\uff0c\u76ee\u5149\u832b\u7136\u51dd\u89c6\u7740\u8fdc\u65b9\uff0c\u6c89\u9ed8\u4e86\u8bb8\u591a\uff0c\u624d\u6162\u6162\u5730 \u95ee\u9053\uff1a\u660e\u5929\u5c31\u662f\u4e5d\u6708\u5341\u4e94\u4e86\u4e48\uff1f \u3000\u3000\u90a3\u9152\u9b3c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u5f2f\u4e0b\u8170\u53bb\uff0c\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\uff0c\u4e00\u9762\u54b3\u55fd\uff0c\u4e00\u9762\u6307\u7740\u684c\u4e0a\u7684\u7a7a \u9152\u58f6\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u5934\u9053\uff1a\u82e5\u662f\u4eba\u4eba\u90fd\u50cf\u4f60\u8fd9\u4e48\u6837\u559d\u9152\uff0c\u5356\u9152\u7684\u65e9\u5c31\u90fd\u53d1\u8d22\u4e86\u3002 \u3000\u3000\u9ec4\u660f\u65f6\uff0c\u540e\u56ed\u7684\u5c0f\u697c\u4e0a\u5c31\u6709\u4e86\u706f\u5149\u3002 \u3000\u3000\u90a3\u9152\u9b3c\u65e9\u5c31\u5750\u5728\u4ed6\u7684\u8001\u5730\u65b9\u5f00\u59cb\u559d\u9152\u4e86\u3002 \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u5c0f\u5e97\u53c8\u6765\u602a\u5ba2>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u5c0f\u5e97\u53c8\u6765\u602a\u5ba2 \u3000\u3000\u4eca\u5929\u90a3\u9152\u9b3c\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\uff0c\u4ed6\u7684\u9152\u559d\u5f97\u7279\u522b\u6162\uff0c\u773c\u775b\u7279\u522b\u4eae\uff0c\u624b\u91cc\u6ca1\u6709\u523b\u6728\u5934\uff0c\u800c\u4e14\u8fd8 \u7279\u5730\u5c06\u4ed6\u684c\u4e0a\u7684\u8721\u70db\u79fb\u5230\u522b\u7684\u684c\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u4e00\u76f4\u5728\u770b\u7740\u95e8\uff0c\u4f3c\u4e4e\u5728\u7b49\u4eba\u7684\u6a21\u6837\u3002 \u3000\u3000\u4f46\xd7\u65f6\u65e9\u5df2\u8fc7\u4e86\uff0c\u5c0f\u5e97\u91cc\u5374\u8fde\u4e00\u4e2a\u4e3b\u987e\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u957f\u957f\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u6253\u7740\u5475\u6b20\u9053\uff1a\u4eca\u5929\u770b\u6837\u5b50\u53c8\u6ca1\u6709\u5ba2\u4eba\u4e0a\u95e8\u4e86\uff0c\u8fd8\u662f\u8d81\u65e9\u6253\u70ca\u5427 \uff0c\u4e5f\u597d\u966a\u4f60\u559d\u4e24\u676f\u3002 \u3000\u3000\u90a3\u201c\u9152\u9b3c\u201d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u522b\u7740\u6025\uff0c\u6211\u7b97\u5b9a\u4e86\u4f60\u4eca\u5929\u7684\u4e70\u5356\u5fc5\u5b9a\u7279\u522b\u597d\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4f60\u600e\u4e48\u77e5\u9053\uff1f \u3000\u3000\u90a3\u201c\u9152\u9b3c\u201d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u4f1a\u7b97\u547d\u3002 \u3000\u3000\u4ed6\u679c\u7136\u4f1a\u7b97\u547d\uff0c\u800c\u4e14\u7075\u5f97\u5f88\uff0c\u8fd8\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c0f\u5e97\u91cc\u679c\u7136\u4f1a\u4e00\u4e0b\u5b50\u5c31\u6765\u4e86\u4e09\u56db\u6279\u5ba2\u4eba \u3002 \u3000\u3000\u7b2c\u4e00\u6279\u662f\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u6ee1\u5934\u767d\u53d1\u82cd\u82cd\uff0c\u624b\u91cc\u62ff\u7740\u65f1\u70df\u7684\u84dd\u886b\u8001\u4eba\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u60f3\u5fc5\u662f\u4ed6\u7684\u5b59\u5973\u513f\uff0c\u68b3\u7740\u4e24\u6761\u53c8\u9ed1\u53c8\u4eae\u7684\u5927\u8fab\u5b50\uff0c\u4e00\u53cc\u6c34\u6c6a\u6c6a\u7684\u5927\u773c\u6674\uff0c\u5374\u6bd4 \u8fab\u5b50\u8fd8\u8981\u9ed1\uff0c\u8fd8\u8981\u4eae\u3002 \u3000\u3000\u7b2c\u4e8c\u6279\u4e5f\u662f\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e0d\u4e24\u4eba\u90fd\u662f\u6ee1\u9762\u866c\uff38\uff0c\u8eab\u9ad8\u4f53\u58ee\uff0c\u4e0d\u4f46\u88c5\u675f\u6253\u626e\u4e00\u6a21\u4e00\u6837\uff0c\u8170\u4e0a\u6302\u7684\u5200\u4e5f\u4e00\u6a21\u4e00\u6837\uff0c\u4e24 \u4eba\u5c31\u50cf\u662f\u4e00\u4e2a\u6a21\u5b50\u91cc\u94f8\u51fa\u6765\u7684\u3002 \u3000\u3000\u7b2c\u4e09\u6279\u6765\u7684\u4eba\u6700\u591a\uff0c\u4e00\u5171\u6709\u56db\u4e2a\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u4e00\u4e2a\u9ad8\u5927\uff0c\u4e00\u4e2a\u77ee\u5c0f\uff0c\u7d2b\u9762\u819b\u7684\u5e74\u8f7b\u4eba\u80a9\u4e0a\u5c45\u7136\u625b\u7740\u6839\u957f\u67aa\uff0c\u8fd8\u6709\u4e2a\u5374\u662f\u7a7f\u7740\u7eff \u8863\u88f3\u3001\u6234\u7740\u91d1\u9996\u9970\u7684\u5973\u5b50\uff0c\u8d70\u8d77\u8def\u6765\u4e00\u626d\u4e00\u626d\u7684\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u5927\u59d1\u5a18\uff0c\u8bba\u5e74\u9f84\u5374\u662f\u5927\u59d1 \u5a18\u7684\u5988\u4e86\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u53ea\u63e1\u5979\u4e00\u4e0d\u5c0f\u5fc3\u4f1a\u628a\u8170\u626d\u65ad\u3002 \u3000\u3000\u6700\u540e\u6765\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u7626\u5f97\u51fa\u5947\uff0c\u8eab\u4e0a\u5e76\u6ca1\u6709\u4f69\u5200\u6302\u5200\uff0c\u4f46\u8170\u56f4\u4e0a\u9f13\u8d77\u4e86\u4e00\u73af\uff0c\u800c\u4e14\u5f88\u89e6\u76ee\uff0c\u663e\u7136\u662f\u5e26\u7740 \u6761\u5f88\u7c97\u957f\u7684\u8f6f\u5175\u5203\u3002 \u3000\u3000\u5c0f\u5e97\u4e00\u5171\u53ea\u6709\u4e94\u5f20\u684c\u5b50\uff0c\u8fd9\u56db\u6279\u4eba\u4e00\u6765\u7acb\u523b\u5c31\u5168\u5750\u6ee1\u4e86\uff0c\u5b59\u9a7c\u5b50\u5fd9\u5f97\u56e2\u56e2\u8f6c\uff0c\u53ea\u5e0c\u671b\u660e \u5929\u7684\u751f\u610f\u4e0d\u8981\u8fd9\u4e48\u597d\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u56db\u6279\u4eba\u90fd\u5728\u559d\u7740\u95f7\u9152\uff0c\u8bf4\u8bdd\u7684\u5f88\u5c11\uff0c\u5c31\u7b97\u8bf4\u8bdd\uff0c\u4e5f\u662f\u4f4e\u58f0\u7ec6\u8bed\uff0c\u4eff\u4f5b\u751f\u6015\u522b\u4eba\u542c \u5230\u3002 \u3000\u3000\u559d\u4e86\u51e0\u676f\u9152\uff0c\u90a3\u80a9\u4e0a\u625b\u7740\u67aa\u7684\u7d2b\u9762\u5c11\u5e74\u773c\u775b\u5c31\u76ef\u5728\u90a3\u5927\u8fab\u5b50\u59d1\u5a18\u8eab\u4e0a\u4e86\uff0c\u8fab\u5b50\u59d1\u5a18\u5012\u4e5f \u5927\u65b9\u5f97\u5f88\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u7d2b\u800c\u5c11\u5e74\u5ffd\u7136\u7b11\u9053\uff1a\u8fd9\u4f4d\u59d1\u5a18\u53ef\u662f\u5356\u5531\u7684\u5417\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u6447\u4e86\u6447\u5934\uff0c\u8fab\u5b50\u9ad8\u9ad8\u5730\u7529\u4e86\u8d77\u6765\uff0c\u6a21\u6837\u770b\u6765\u66f4\u5a07\u3002 \u3000\u3000\u7d2b\u9762\u5c11\u5e74\u7b11\u9053\uff1a\u5c31\u7b97\u4e0d\u5356\u5531\uff0c\u603b\u4e5f\u4f1a\u5531\u4e24\u53e5\u5427\uff0c\u53ea\u8981\u5531\u5f97\u597d\uff0c\u7237\u4eec\u91cd\u91cd\u6709\u8d4f\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u62bf\u7740\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u4e0d\u4f1a\u5531\uff0c\u53ea\u4f1a\u8bf4\u3002 \u3000\u3000\u7d2b\u9762\u5c11\u5e74\u9053\uff1a\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u8bf4\u4e66\uff0c\u8bf4\u6545\u4e8b\u3002 \u3000\u3000\u7d2b\u9762\u5c11\u5e74\u7b11\u9053\uff1a\u90a3\u66f4\u597d\u4e86\uff0c\u5374\u4e0d\u77e5\u4f60\u4f1a\u8bf4\u4ec0\u4e48\u4e66\uff1f\u540e\u82b1\u56ed\u624d\u5b50\u4f1a\u4f73\u4eba\uff1f\u5bb0\u76f8\u5343\u91d1\u629b\u7ee3\u7403 \uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u90fd\u4e0d\u5bf9\uff0c\u6211\u8bf4\u7684\u662f\u6c5f\u6e56\u4e2d\u6700\u8f70\u52a8\u7684\u6d88\u606f\uff0c\u6b66\u6797\u4e2d\u6700\u8fd1\u53d1\u751f\u7684\u5927 \u4e8b\uff0c\u4fdd\u8bc1\u53c8\u65b0\u9c9c\u3001\u53c8\u7d27\u5f20\u3002 \u3000\u3000\u7d2b\u9762\u5c11\u5e74\u62ca\u638c\u7b11\u9053\uff1a\u5999\u6781\u5999\u6781\uff0c\u8fd9\u79cd\u4e8b\u6211\u60f3\u5728\u5ea7\u7684\u8bf8\u4f4d\u90fd\u559c\u6b22\u542c\u7684\uff0c\u4f60\u5feb\u8bf4\u5427\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\uff1a\u6211\u4e0d\u4f1a\u8bf4\uff0c\u6211\u7237\u7237\u4f1a\u8bf4\u3002 \u3000\u3000\u7d2b\u9762\u5c11\u5e74\u77aa\u4e86\u90a3\u8001\u5934\u5b50\u4e00\u773c\uff0c\u76b1\u7740\u7709\u9053\uff1a\u4f60\u4f1a\u4ec0\u4e48\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u5ae3\u7136\u9053\uff1a\u6211\u53ea\u4f1a\u66ff\u7237\u7237\u5e2e\u8154\u3002 \u3000\u3000\u5979\u773c\u775b\u8fd9\u4e48\u4e00\u8f6c\uff0c\u7d2b\u9762\u5c11\u5e74\u7684\u9b42\u90fd\u98de\u4e86\u3002 \u3000\u3000\u8001\u5934\u5b50\u772f\u7740\u773c\uff0c\u559d\u4e86\u676f\u9152\uff0c\u53c8\u62bd\u4e86\u53e3\u65f1\u70df\uff0c\u624d\u6162\u541e\u541e\u5730\u8bf4\u9053\uff1a\u4f60\u53ef\u542c\u8bf4\u8fc7\u674e\u5bfb\u6b22\u8fd9\u540d\u5b57 \uff1f \u3000\u3000\u9664\u4e86\u90a3\u7d2b\u9762\u5c11\u5e74\u5916\uff0c\u5927\u5bb6\u672c\u8fd8\u4e0d\u5927\u7406\u4f1a\u8fd9\u7956\u5b59\u4e24\u4eba\uff0c\u4f46\u4e00\u542c\u5230\u674e\u5bfb\u6b22\u8fd9\u540d\u5b57\uff0c\u6bcf\u4e2a\u4eba\u7684 \u8033\u6735\u90fd\u7ad6\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7b11\u9053\uff1a\u6211\u5f53\u7136\u542c\u8bf4\u8fc7\uff0c\u4e0d\u5c31\u662f\u90a3\u4f4d\u4ed7\u4e49\u758f\u8d22\uff0c\u5927\u540d\u9f0e\u9f0e\u7684\u5c0f\u674e\u63a2\u82b1\uff1f \u3000\u3000\u8001\u5934\u5b50\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u542c\u8bf4\uff0c\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u76f4\u5230\u5982\u4eca\u4e3a\u6b62\uff0c\u8fd8\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u8eb2\u5f00\u8fc7\uff0c\u8fd9 \u53e5\u8bdd\u4e0d\u77e5\u9053\u662f\u771f\u662f\u5047\uff1f \u3000\u3000\u8001\u5934\u5b50\u9053\uff1a\u4f60\u82e5\u4e0d\u76f8\u4fe1\uff0c\u4e0d\u59a8\u53bb\u95ee\u95ee\u5e73\u6e56\u767e\u6653\u751f\uff0c\u53bb\u95ee\u95ee\u4e94\u6bd2\u7ae5\u5b50\uff0c\u4f60\u5c31\u77e5\u9053\u8fd9\u53e5\u8bdd\u662f \u771f\u662f\u5047\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u767e\u6653\u751f\u548c\u4e94\u6bd2\u7ae5\u5b50\u5c82\u975e\u65e9\u5c31\u5168\u90fd\u6b7b\u4e86\u4e48\uff1f \u3000\u3000\u8001\u5934\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u4eec\u90fd\u6b7b\u4e86\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u76f8\u4fe1\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u90a3\u9762\u5e26\u9752\u8bb0\u7684\u7626\u957f\u6c49\u5b50\u9f3b\u5b54\u91cc\u4f3c\u4e4e\u4f4e\u4f4e\u54fc\u4e86\u4e00\u58f0\uff0c\u53ea\u4e0d\u8fc7\u5927\u5bb6\u90fd\u5df2\u88ab\u8fd9\u7956\u5b59\u4e24\u4eba\u7684\u5bf9\u7b54 \u6240\u5438\u5f15\uff0c\u8c01\u4e5f\u6ca1\u6709\u7559\u610f\u4ed6\u3002 \u3000\u3000\u53ea\u6709\u90a3\u9152\u9b3c\u4ed7\u5728\u684c\u4e0a\uff0c\u4f3c\u4e4e\u5df2\u9189\u4e86\u3002 \u3000\u3000\u8001\u5934\u5b50\u559d\u4e86\u53e3\u8336\uff0c\u63a5\u7740\u9053\uff1a\u53ea\u53ef\u60dc\u50cf\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u5982\u4eca\u4e5f\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7136\u9053\uff1a\u6b7b\u4e86\uff1f\u8c01\u6709\u90a3\u4e48\u5927\u7684\u672c\u4e8b\u6740\u4e86\u4ed6\uff1f \u3000\u3000\u8001\u5934\u5b50\uff1a\u8c01\u4e5f\u6ca1\u6709\u90a3\u4e48\u5927\u7684\u672c\u4e8b\uff0c\u6709\u672c\u4e8b\u6740\u4ed6\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u8c01\uff1f \u3000\u3000\u8001\u5934\u5b50\uff1a\u5c31\u662f\u4ed6\u81ea\u5df1\uff01 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u6014\u4e86\u6014\uff0c\u53c8\u7b11\u9053\uff1a\u4ed6\u81ea\u5df1\u600e\u4e48\u4f1a\u6740\u6b7b\u81ea\u5df1\u5462\uff1f\u6211\u770b\u4ed6\u4e00\u5b9a\u8fd8\u6d3b\u5728\u4e16\u4e0a\u3002 \u3000\u3000\u8001\u5934\u5b50\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5c31\u7b97\u4ed6\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u4e5f\u548c\u6b7b\u5dee\u4e0d\u591a\u4e86\u2014\u2014\u53ef\u53f9\u5440\u53ef\u53f9\uff0c\u53ef\u60dc \u5440\u53ef\u60dc\u2014\u2014 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u53ef\u79f0\u5f97\u4e0a\u662f\u82f1 \u96c4\u5462\uff1f \u3000\u3000\u8001\u5934\u5b50\uff1b\u4f60\u53ef\u542c\u8bf4\u8fc7\u963f\u98de\u8fd9\u540d\u5b57\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u597d\u50cf\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u5979\u773c\u73e0\u4e00\u8f6c\uff0c\u53c8\u9053\uff1a\u542c\u8bf4\u6b64\u4eba\u5251\u6cd5\u4e4b\u5feb\uff0c\u4e3e\u4e16\u65e0\u53cc\uff0c\u5374\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f \u3000\u3000\u8001\u5934\u5b50\uff1a\u4f0a\u54ed\u7684\u6b66\u529f\u5982\u4f55\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u5175\u5668\u8c31\u4e2d\uff0c\u9752\u9b54\u624b\u6392\u540d\u7b2c\u4e5d\uff0c\u6b66\u529f\u81ea\u7136\u597d\u5f97\u5f88\u4e86\u3002 \u3000\u3000\u8001\u5934\u5b50\u9053\uff1a\u94c1\u7b1b\u5148\u751f\u3001\u5c11\u6797\u5fc3\u9274\u3001\u8d75\u6b63\u4e49\u3001\u7530\u4e03\u8fd9\u4e9b\u4eba\u7684\u6b66\u529f\u53c8\u5982\u4f55\uff1f \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u8fd9\u51e0\u4f4d\u90fd\u662f\u6c5f\u6e56\u4e2d\u4e00\u7b49\u7684\u9ad8\u624b\uff0c\u8c01\u90fd\u77e5\u9053\u7684\u3002 \u3000\u3000\u8001\u5934\u5b50\u9053\uff1a\u963f\u98de\u7684\u5251\u6cd5\u82e5\u4e0d\u5feb\uff0c\u8fd9\u4e9b\u4eba\u600e\u4f1a\u8d25\u5728\u4ed6\u5251\u4e0b\uff1f \u3000\u3000\u8fab\u5b50\u9053\uff1a\u5982\u4eca\u8fd9\u4f4d\u963f\u98de\u7684\u4eba\u5462\uff1f \u3000\u3000\u8001\u5934\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4ed6\u4e5f\u548c\u5c0f\u674e\u63a2\u82b1\u4e00\u6837\uff0c\u5ffd\u7136\u4e0d\u89c1\u4e86\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u6d88\u606f\uff0c\u53ea\u77e5 \u9053\u4ed6\u662f\u548c\u6797\u4ed9\u513f\u540c\u65f6\u5931\u8e2a\u7684\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u6797\u4ed9\u513f\uff1f\u4e0d\u5c31\u662f\u90a3\u4f4d\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\u7684\u6797\u59d1\u5a18\uff1f \u3000\u3000\u8001\u5934\u5b50\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u6f2b\u58f0\u9053\uff1a\u60c5\u662f\u4f55\u7269\uff1f\u504f\u53eb\u4e16\u4eba\u90fd\u4e3a\u60c5\u82e6\uff0c\u800c\u4e14\u8fd8\u65e0\u5904\u6295\u8bc9\u2014\u2014 \u3000\u3000\u90a3\u7d2b\u9762\u5c11\u5e74\u4f3c\u5df2\u6709\u4e9b\u4e0d\u8010\uff0c\u76b1\u7709\u9053\uff1a\u95f2\u8bdd\u5c11\u8bf4\uff0c\u4e66\u5f52\u6b63\u4f20\uff0c\u4f60\u8bf4\u7684\u6545\u4e8b\u5462\uff1f \u3000\u3000\u8001\u5934\u5b50\u957f\u53f9\u7740\u6447\u5934\u9053\uff1a\u50cf\u963f\u98de\u548c\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u90fd\u5df2\u4e0d\u77e5\u4e0b\u843d\uff0c\u6c5f\u6e56\u4e2d\u8fd8\u4f1a\u53d1\u751f\u4ec0 \u4e48\u5927\u4e8b\uff1f\u6211\u8001\u5934\u5b50\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u3002 \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u51b7\u7b11\u4e00\u58f0\uff1a\u90a3\u5012\u4e5f\u4e0d\u89c1\u5f97\u3002 \u3000\u3000\u8001\u5934\u9053\uff1a\u54e6\uff1f\u9601\u4e0b\u7684\u6d88\u606f\u6bd4\u6211\u8001\u5934\u5b50\u8fd8\u7075\u901a\uff1f \u3000\u3000\u90a3\u6c49\u5b50\u76ee\u5149\u56db\u8f6c\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u636e\u6211\u6240\u77e5\uff0c\u4e0d\u4e45\u5c31\u8981\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u4e8b\u53d1\u751f\u4e86\u3002 \u3000\u3000\u8001\u5934\u5b50\uff1a\u5728\u54ea\u91cc\u53d1\u751f\uff1f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\uff1f \u3000\u3000\u7626\u5b50\u6c49\u5b50\u62cd\u7684\u4e00\u62cd\u684c\u5b50\uff0c\u5389\u58f0\u9053\uff1a\u5c31\u5728\u6b64\u65f6\uff0c\u5c31\u5728\u6b64\u5730\uff01 \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\uff0c\u90a3\u5b6a\u751f\u5144\u5f1f\u548c\u4e09\u6279\u6765\u7684\u56db\u4e2a\u4eba\u9762\u4e0a\u5168\u90fd\u53d8\u4e86\u989c\u8272\uff0c\u90a3\u7eff\u8863\u5987\u4eba\u773c\u6ce2\u6d41\u52a8\u5a07 \u7b11\u9053\uff1a\u6211\u5012\u770b\u4e0d\u51fa\u6b64\u65f6\u6b64\u5730\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e86\u4e0d\u5f97\u7684\u5927\u4e8b\u3002 \u3000\u3000\u7626\u957f\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u636e\u6211\u6240\u77e5\uff0c\u81f3\u5c11\u6709\u516d\u4e2a\u4eba\u9a6c\u4e0a\u5c31\u8981\u6b7b\u5728\u8fd9\u91cc\uff01 \u3000\u3000\u7eff\u8863\u4eba\u9053\uff1a\u54ea\u516d\u4e2a\u4eba\u3002 \u3000\u3000\u7626\u957f\u6c49\u5b50\u559d\u4e86\u53e3\u9152\uff0c\u7f13\u7f13\u9053\uff1a\u80e1\u975e\uff0c\u6bb5\u5f00\u5c71\uff0c\u6768\u627f\u7956\uff0c\u80e1\u5a9a\u548c\u671d\u5bb6\u5144\u5f1f\uff01 \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u4e86\u8fd9\u516d\u4e2a\u7684\u540d\u5b57\uff0c\u90a3\u5b6a\u751f\u5144\u5f1f\u548c\u7b2c\u4e09\u6279\u6765\u7684\u56db\u4e2a\u503c\u5f97\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u7eb7\u7eb7\u62cd \u7740\u684c\u5b50\u9a82\u9053\uff1a\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6562\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\uff1f \u3000\u3000\u58f0\u97f3\u558a\u5f97\u6700\u5927\u7684\u6b63\u662f\u90a3\u5927\u529b\u795e\u6bb5\u5f00\u5c71\u3002 \u3000\u3000\u6b64\u4eba\u7ad9\u8d77\u6765\u5c31\u548c\u534a\u622a\u5854\u4f3c\u7684\uff0c\u671d\u5bb6\u5144\u5f1f\u8eab\u6750\u867d\u9ad8\u5927\uff0c\u6bd4\u8d77\u4ed6\u6765\u8fd8\u662f\u77ee\u4e86\u534a\u4e2a\u5934\u3002 \u3000\u3000\u4ed6\u9a82\u4e86\u4e24\u53e5\u4e0d\u8fc7\u763e\uff0c\u63a5\u7740\u9053\uff1a\u6211\u770b\u4f60\u624d\u662f\u4e00\u8138\u5012\u9709\u50cf\uff0c\u4f11\u60f3\u6d3b\u5f97\u8fc7\u4eca\u5929\u665a\u4e0a\u2014\u2014 \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u90a3\u7626\u957f\u6c49\u5b50\u53ea\u4e00\u62ac\u817f\uff0c\u5ffd\u7136\u5c31\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u5288\u5288\u62cd\u62cd\u7ed9\u4e86\u4ed6\u5341\u4e03\u516b\u4e2a \u8033\u5149\u3002 \u3000\u3000\u6bb5\u5f00\u5c71\u660e\u660e\u6709\u4e24\u53ea\u624b\uff0c\u504f\u504f\u5c31\u65e0\u6cd5\u62db\u67b6\uff0c\u660e\u660e\u6709\u4e24\u6761\u817f\uff0c\u504f\u504f\u5c31\u65e0\u6cd5\u95ea\u907f\uff0c\u8fde\u5934\u90fd\u4f3c\u5df2 \u88ab\u6253\u6655\u4e86\uff0c\u52a8\u90fd\u52a8\u4e0d\u5f97\u3002 \u3000\u3000\u522b\u7684\u4eba\u4e5f\u770b\u5446\u4e86\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u7626\u957f\u6c49\u5b50\u9053\uff1a\u4f60\u4ee5\u4e3a\u662f\u6211\u8981\u6740\u4f60\u4eec\uff1f\u51ed\u4f60\u4eec\u8fd8\u4e0d\u914d\u8ba9\u6211\u52a8\u624b\uff01\u6211\u8fd9\u53ea\u4e0d\u8fc7\u662f\u6559\u8bad \u6559\u8bad\u4f60\u4eec\uff0c\u8981\u4f60\u4eec\u8bf4\u8bdd\u65af\u6587\u4e9b\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\u8bdd\uff0c\u4e00\u9762\u5df2\u6162\u6162\u8d70\u4e86\u56de\u53bb\u3002 \u3000\u3000\u6768\u627f\u7956\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u6162\u8d70\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\u662f\u8c01\u8981\u6740\u6211\u4eec\uff1f \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4ed6\u4e00\u76f4\u653e\u5728\u624b\u8fb9\u7684\u957f\u67aa\u5df2\u6bd2\u86c7\u822c\u523a\u51fa\u3002 \u3000\u3000\u53ea\u89c1\u67aa\u82b1\u6735\u6735\uff0c\u7adf\u662f\u6b63\u5b97\u7684\u6768\u5bb6\u67aa\u6cd5\u3002 \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u5934\u4e5f\u672a\u56de\uff0c\u6de1\u6de1\u9053\uff1a\u8981\u6740\u4f60\u4eec\u7684\u4eba\u5c31\u5feb\u6765\u4e86\uff01\u2014\u2014 \u3000\u3000\u53ea\u89c1\u4ed6\u8170\u4e00\u95ea\uff0c\u5df2\u5c06\u957f\u67aa\u631f\u5728\u80c1\u4e0b\uff0c\u6768\u627f\u7956\u7528\u5c3d\u5168\u8eab\u529b\u6c14\u90fd\u62bd\u4e0d\u51fa\u6765\uff0c\u4e00\u5f20\u7d2b\u9762\u5df2\u6025\u5f97 \u53d8\u6210\u732a\u809d\u8272\u3002 \u3000\u3000\u7626\u957f\u6c49\u5b50\u9053\uff1b\u4f60\u4eec\u53cd\u6b63\u9003\u4e0d\u4e86\u7684\uff0c\u8fd8\u662f\u6162\u6162\u5730\u7b49\u7740\u77a7\u5427\u3002 \u3000\u3000\u6768\u627f\u7956\u7684\u67aa\u5c16\u5df2\u4e0d\u77e5\u4f55\u65f6\u88ab\u4eba\u6298\u65ad\u4e86\uff01 \u3000\u3000\u4f46\u542c\u5f97\u593a\u7684\u4e00\u58f0\uff0c\u7626\u957f\u6c49\u5b50\u5c06\u67aa\u5c16\u63d2\u5728\u684c\u5b50\u4e0a\uff0c\u6162\u6162\u5730\u5012\u4e86\u676f\u9152\u559d\u4e86\u4e0b\u53bb\uff0c\u5c31\u597d\u50cf\u4ec0\u4e48 \u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u4e00\u6837\u3002 \u3000\u3000\u4f46\u97e9\u5bb6\u5144\u5f1f\u3001\u6768\u627f\u7956\u3001\u80e1\u975e\u3001\u6bb5\u5f00\u5c71\u3001\u80e1\u5a9a\uff0c\u8fd9\u516d\u4e2a\u4eba\u5c31\u6ca1\u6709\u4ed6\u8fd9\u4e48\u597d\u8fc7\u4e86\uff0c\u4e00\u4e2a\u4e2a\u9762 \u9762\u76f8\u89d1\uff0c\u4ff1\u662f\u9762\u5982\u6b7b\u7070\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u5728\u60f3\uff1a\u662f\u8c01\u8981\u6765\u6740\u6211\u4eec\uff1f\u662f\u8c01\u2014\u2014\uff0d \u3000\u3000\u5916\u9762\u98ce\u6e10\u6e10\u5927\u4e86\u3002\u706f\u5149\u95ea\u52a8\uff0c\u6620\u5f97\u90a3\u7626\u957f\u6c49\u5b50\u4e00\u5f20\u9752\u60e8\u7684\u8138\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u8fd9\u4eba\u53c8\u662f\u8c01\uff1f \u3000\u3000\u4ee5\u4ed6\u6b66\u529f\u4e4b\u9ad8\uff0c\u60f3\u5fc5\u662f\u4e00\u7b49\u4e00\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u6211\u4eec\u600e\u4f1a\u4e0d\u8ba4\u5f97\u4ed6\uff1f \u3000\u3000\u4ed6\u600e\u4f1a\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u7684\uff1f \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u662f\u5fd0\u5fd1\u4e0d\u5b9a\uff0c\u54ea\u91cc\u8fd8\u80fd\u559d\u5f97\u4e0b\u4e00\u53e3\u9152\u53bb\uff1f \u3000\u3000\u6709\u7684\u4eba\u5df2\u60f3\u6e9c\u4e4b\u5927\u5409\uff0c\u4f46\u8fd9\u6837\u5c31\u8d70\uff0c\u672a\u514d\u592a\u4e22\u4eba\u4e86\uff0c\u65e5\u540e\u82e5\u662f\u4f20\u8bf4\u51fa\u53bb\uff0c\u8fd8\u80fd\u5728\u6c5f\u6e56\u4e2d \u6df7\u4e48\uff1f \u3000\u3000\u4f55\u51b5\uff0c\u4ed6\u4eec\u5c31\u7b97\u60f3\u9003\uff0c\u4e5f\u9003\u4e0d\u4e86\uff01 \u3000\u3000\u4ed6\u4eec\u516d\u4e2a\u4eba\u5408\u5728\u4e00\u8d77\uff0c\u5c31\u8fde\u6bb5\u5f00\u5c71\u548c\u6768\u627f\u7956\u7684\u80c6\u6c14\u4e5f\u4e0d\u89c9\u58ee\u4e86\u8d77\u6765\u3002 \u3000\u3000\u516d\u4e2a\u4eba\u6b63\u5728\u4f60\u4e00\u53e5\uff0c\u6211\u4e00\u53e5\uff0c\u4f60\u6367\u6211\uff0c\u6211\u6367\u4f60\u3002 \u3000\u3000\u7a81\u542c\u95e8\u5916\u6709\u4eba\u4e00\u58f0\u51b7\u7b11\u3002 \u3000\u3000\u516d\u4e2a\u4eba\u7684\u8138\u8272\u7acb\u523b\u53d8\u4e86\uff0c\u5589\u5499\u4e5f\u50cf\u662f\u7a81\u7136\u88ab\u4eba\u627c\u4f4f\uff0c\u975e\u4f46\u518d\u4e5f\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\uff0c\u8fde\u547c\u5438\u90fd \u4f3c\u5df2\u5c06\u505c\u987f\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u65e9\u5df2\u9a87\u5446\u4e86\uff0c\u4f46\u516d\u4eba\u5374\u6bd4\u4ed6\u8fd8\u8981\u6015\u5f97\u5389\u5bb3\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u968f\u7740\u4ed6\u62c9\u7684\u76ee\u5149\u77a7\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u95e8\u53e3\u51fa\u73b0\u4e86\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u90fd\u7a7f\u7740\u989c\u8272\u6781\u9c9c\u660e\u7684\u674f\u9ec4\u8272\u957f\u886b\uff0c\u5176\u4e2d\u4e00\u4e2a\u6d53\u7709\u5927\u773c\uff0c\u4e00\u4e2a\u9e70\u9f3b\u5982\uff0c\u6b63\u662f\u4eca\u5929 \u65e9\u4e0a\u5411\u4ed6\u6253\u542c\u6d88\u606f\u7684\u90a3\u4e24\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u5230\u4e86\u95e8\u53e3\uff0c\u5374\u6ca1\u6709\u8d70\u8fdb\u6765\uff0c\u53ea\u662f\u5782\u624b\u7ad9\u5728\u90a3\u8fb9\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u770b\u6765\u4e00\u70b9\u4e5f\u4e0d\u53ef\u6015 \u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5b9e\u5728\u60f3\u4e0d\u901a\u65b9\u624d\u8fd8\u76db\u6c14\u51cc\u4eba\u7684\u516d\u4e2a\u4eba\uff0c\u600e\u4f1a\u5bf9\u4ed6\u4eec\u5982\u6b64\u5bb3\u6015\uff0c\u770b\u8fd9\u516d\u4e2a\u4eba\u7684\u8868\u60c5 \uff0c\u8fd9\u56db\u4e2a\u9ec4\u886b\u4eba\u7b80\u76f4\u4e0d\u662f\u4eba\uff0c\u662f\u9b3c\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u4e9b\u7fa1\u6155\u90a3\u9152\u9b3c\u4e86\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u77a7\u89c1\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u542c\u89c1\uff0c\u81ea\u7136\u4ec0\u4e48\u90fd\u7528\u4e0d\u7740\u5bb3\u6015\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u90a3\u7956\u5b59\u4e24\u4eba\u6709\u4e00\u4e2a\u5df2\u5feb\u8001\u6389\u4e86\u7259\uff0c\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u4eff\u4f5b\u88ab\u98ce\u4e00\u5439\u5c31\u8981\u5012\u3002 \u3000\u3000\u4f46\u4e24\u4eba\u6b64\u523b\u5c45\u7136\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u5e76\u6ca1\u6709\u9732\u51fa\u4ec0\u4e48\u5bb3\u6015\u7684\u6837\u5b50\u6765\uff0c\u90a3\u8001\u5934\u5b50\u5c45\u7136\u8fd8\u80fd\u559d\u5f97\u4e0b \u9152\u3002 \u3000\u3000\u518d\u770b\u95e8\u53e3\u90a3\u56db\u4e2a\u4eba\uff0c\u5df2\u95ea\u51fa\u4e86\u4e00\u6761\u8def\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u7eaa\u5f88\u8f7b\u7684\u5c11\u5e74\u4eba\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u8eab\u4e0a\u7a7f\u7684\u4e5f\u662f\u674f\u9ec4\u8272\u7684\u957f\u886b\uff0c\u957f\u5f97\u5f88\u79c0\u6c14\uff0c\u6001\u5ea6\u4e5f\u5f88\u65af\u6587\uff0c\u4ed6\u548c\u53e6\u56db\u4eba\u552f\u4e00\u4e0d\u540c \u7684\u5730\u65b9\uff0c\u5c31\u662f\u9ec4\u886b\u4e0a\u8fd8\u9576\u7740\u91d1\u8fb9\u3002 \u3000\u3000\u4ed6\u957f\u5f97\u867d\u79c0\u6c14\uff0c\u9762\u4e0a\u5374\u662f\u51b7\u51b0\u51b0\u7684\uff0c\u65e0\u4e1d\u6beb\u8868\u60c5\uff0c\u773c\u775b\u76ef\u5728\u90a3\u9752\u9762\u7626\u957f\u6c49\u5b50\u8eab\u4e0a\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u81ea\u5df1\u559d\u7740\u9152\uff0c\u4e5f\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5634\u89d2\u6162\u6162\u5730\u9732\u51fa\u4e00\u4e1d\u51b7\u7b11\uff0c\u6162\u6162\u5730\u8f6c\u8fc7\uff0c\u51b0\u51b7\u7684\u76ee\u5149\u5728\u627f\u7956\u7b49\u516d\u4eba\u8eab\u4e0a\u4e00\u626b\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u6162\u6162\u5730\u8d70\u4e86\u8fc7\u53bb\uff0c\u81ea\u6000\u4e2d\u53d6\u51fa\u516d\u679a\u9ec4\u94dc\u94f8\u6210\u7684\u5236\u94b1\uff0c\u5728\u516d\u4e2a\u4eba\u7684\u5934\u4e0a\u5404\u653e\u4e86\u4e00 \u679a\u3002 \u3000\u3000\u516d\u4e2a\u4eba\u7adf\u4f3c\u4e4e\u90fd\u53d8\u6210\u4e86\u6728\u5934\u4eba\uff0c\u773c\u7741\u7741\u5730\u77a7\u7740\u8fd9\u4eba\u5c06\u4e1c\u897f\u968f\u968f\u4fbf\u4fbf\u5730\u6446\u5728\u81ea\u5df1\u5934\u4e0a\uff0c\u8fde \u4e2a\u5c41\u90fd\u4e0d\u6562\u653e\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u8fd8\u5269\u4e0b\u51e0\u4e2a\u94dc\u94b1\uff0c\u5728\u624b\u91cc\u53ee\u53ee\u5f53\u5f53\u5730\u6447\u7740\uff0c\u7f13\u7f13\u8d70\u5230\u90a3\u8001\u4eba\u548c\u8fab\u5b50\u59d1\u5a18\u7684\u684c\u524d \u3002 \u3000\u3000\u8001\u5934\u5b50\u7b11\u9053\uff1a\u670b\u53cb\u82e5\u662f\u60f3\u559d\u9152\uff0c\u5c31\u5750\u4e0b\u6765\u559d\u4e24\u676f\u5427\uff0c\u6211\u8bf7\u4f60\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u6709\u4e9b\u9189\u4e86\uff0c\u5634\u89d2\u5c31\u597d\u50cf\u542b\u7740\u4e2a\u9e21\u86cb\u4f3c\u7684\uff0c\u820c\u5934\u4e5f\u6bd4\u5e73\u65f6\u5927\u4e86\u4e09\u500d\uff0c\u8bf4\u7684\u8bdd\u7b80\u76f4\u6ca1 \u4eba\u80fd\u542c\u5f97\u6e05\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u6c89\u7740\u8138\uff0c\u51b7\u51b7\u5730\u77a7\u7740\u4ed6\uff0c\u7a81\u4f38\u624b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u6446\u5728\u8001\u5934\u5b50\u9762\u524d\u7684\u4e00\u789f\u82b1\u751f\u7c73\u5c31 \u7a81\u7136\u5168\u90e8\u4ece\u789f\u5b50\u91cc\u8df3\u4e86\u8d77\u6765\uff0c\u66b4\u96e8\u822c\u5411\u8001\u5934\u5b50\u8138\u4e0a\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u8001\u5934\u5b50\u4e5f\u4e0d\u77e5\u662f\u770b\u5446\u4e86\uff0c\u8fd8\u662f\u5413\u5446\u4e86\uff0c\u8fde\u95ea\u907f\u90fd\u5fd8\u4e86\u95ea\u907f\uff0c\u51e0\u5341\u7c92\u82b1\u751f\u7c73\u773c\u770b\u5df2\u5feb\u6253 \u5728\u4ed6\u8138\u4e0a\u3002\u9ec4\u886b\u5c11\u5e74\u957f\u8896\u7a81\u7136\u53c8\u4e00\u5377\uff0c\u5c06\u82b1\u751f\u7c73\u5168\u90fd\u5377\u5165\u8896\u4e2d\uff0c\u4ed6\u888d\u8896\u4e00\u6296\uff0c\u82b1\u751f\u7c73\u5c31\u53c8\u4e00 \u8fde\u4e32\u843d\u56de\u789f\u5b50\u3002 \u3000\u3000\u90a3\u8fab\u5b50\u59d1\u5a18\u62cd\u624b\u5a07\u7b11\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u8fd9\u628a\u620f\u771f\u597d\u770b\u6781\u4e86\uff0c\u60f3\u4e0d\u5230\u4f60\u539f\u6765\u662f\u4e2a\u53d8\u620f\u6cd5\u7684\uff0c\u4f60 \u518d\u53d8\u51e0\u4e4e\u7ed9\u6211\u4eec\u77a7\u77a7\u597d\u4e0d\u597d\uff1f\u6211\u4e00\u5b9a\u8981\u7237\u7237\u8bf7\u4f60\u559d\u9152\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u9732\u4e86\u624b\u6781\u9ad8\u5999\u7684\u63a5\u6697\u5668\u529f\u592b\uff0c\u8c01\u77e5\u5374\u9047\u7740\u4e2a\u4e0d\u8bc6\u8d27\u7684\u4e70\u4e3b\uff0c\u5c45\u7136\u5c06\u4ed6\u770b\u6210\u53d8\u620f \u6cd5\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u9ec4\u886b\u5c11\u5e74\u4e00\u70b9\u4e5f\u6ca1\u6709\u751f\u6c14\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e86\u8fab\u5b50\u59d1\u5a18\u51e0\u773c\uff0c\u76ee\u4e2d\u4f3c\u4e4e\u5e26\u4e9b\u7b11\u610f\uff0c\u6162 \u6162\u5730\u8d70\u5f00\u53bb\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u6025\u9053\uff1a\u4f60\u7684\u620f\u6cd5\u4e3a\u4ec0\u4e48\u4e0d\u53d8\uff1f\u6211\u8fd8\u60f3\u770b\u54e9\u3002 \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u7a81\u7136\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u8fd9\u79cd\u620f\u6cd5\u8fd8\u662f\u5c11\u770b\u4e9b\u4e3a\u5999\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u4f60\u4eec\u82e5\u662f\u4f1a\u6b66\u529f\uff0c\u4ed6\u65b9\u624d\u4e24\u4e24\u624b\u620f\u6cd5\u53ea\u6015\u5df2\u5c06\u4f60\u4eec\u53d8\u6b7b\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u5077\u5077\u779f\u4e86\u9ec4\u886b\u5c11\u5e74\u4e00\u773c\uff0c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u4fe1\uff0c\u5374\u5df2\u4e0d\u6562\u518d\u95ee\u4e86\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u6839\u672c\u5c31\u6ca1\u6709\u4e0e\u65e5\u4ff1\u589e\u90a3\u6c49\u5b50\u5728\u8bf4\u4ec0\u4e48\uff0c\u6162\u6162\u5730\u8d70\u5230\u90a3\u9152\u9b3c\u7684\u684c\u5b50\u524d\uff0c\u53ee\u53ee\u5f53\u5f53 \u5730\u6447\u7740\u624b\u91cc\u7684\u5236\u94b1\u3002 \u3000\u3000\u90a3\u9152\u9b3c\u65e9\u5df2\u4eba\u4e8b\u4e0d\u77e5\uff0c\u4ed7\u5728\u684c\u4e0a\u7761\u5f97\u597d\u50cf\u6b7b\u4eba\u4e00\u6837\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u51b7\u7b11\u7740\uff0c\u4e00\u628a\u62ce\u8d77\u4ed6\u7684\u5934\u53d1\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u90fd\u62ce\u4e86\u8d77\u6765\uff0c\u4ed4\u7ec6\u770b\u4e86\u4e24\u773c\uff0c\u624b\u624d\u653e \u677e\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4e00\u677e\uff0c\u8fd9\u9152\u9b3c\u5c31\u7830\u7684\u53c8\u8dcc\u56de\u684c\u5b50\u4e0a\uff0c\u8fd8\u662f\u4eba\u4e8b\u4e0d\u77e5\uff0c\u53c8\u547c\u547c\u5927\u7761\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6c49\u5b50\u51b7\u51b7\u9053\uff1a\u4e00\u9189\u89e3\u5343\u6101\uff0c\u8fd9\u8bdd\u5012\u771f\u4e0d\u9519\uff0c\u559d\u9189\u4e86\u7684\u4eba\u786e\u5b9e\u6bd4\u6e05\u9192\u7684\u5360\u4fbf\u5b9c\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u4e0d\u7406\u4ed6\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u80e1\u975e\u3001\u6bb5\u5f00\u5c71\u3001\u6768\u627f\u7956\u3001\u80e1\u5a9a\u3001\u671d\u6591\u3001\u671d\u660e\uff0c\u8fd9\u516d\u4eba\u4e5f\u7acb\u523b\u4e00\u8fde\u4e32\u8ddf\u4e86\u51fa\u53bb \uff0c\u5c31\u6709\u6761\u7ef3\u5b50\u7275\u7740\u4f3c\u7684\u3002 \u3000\u3000\u8fd9\u516d\u4eba\u4e00\u4e2a\u4e2a\u90fd\u54ed\u4e27\u7740\u8138\uff0c\u76f4\u7740\u8116\u5b50\uff0c\u811a\u4e0b\u867d\u5728\u4e00\u6b65\u6b65\u5f80\u524d\u8d70\uff0c\u4e0a\u534a\u8eab\u5374\u8fde\u52a8\u4e5f\u4e0d\u6562\u52a8 \uff0c\u751f\u6015\u5934\u4e0a\u7684\u94dc\u94b1\u4f1a\u6389\u4e0b\u6765\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u6d3b\u4e86\u51e0\u5341\u5e74\uff0c\u5012\u771f\u8fd8\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u602a\u4e8b\u3002 \u3000\u3000\u4ed6\u4ee5\u524d\u66fe\u7ecf\u542c\u4eba\u8bf4\u8fc7\uff0c\u6df1\u5c71\u5927\u6cfd\u4e2d\u5f80\u5f80\u4f1a\u51fa\u73b0\u5c71\u9b45\u6728\u5ba2\uff0c\u6700\u559c\u5403\u7334\u8111\uff0c\u9ad8\u5174\u65f6\u5c31\u5c06\u5168\u5c71 \u7684\u7334\u5b50\u5168\u53ec\u6765\uff0c\u770b\u5230\u4e2d\u610f\u7684\u666f\u653e\u5757\u77f3\u5934\u5728\u5b83\u8111\u888b\u4e0a\uff0c\u88ab\u770b\u4e2d\u7684\u7334\u5b50\uff0c\u7edd\u4e0d\u6562\u53cd\u6297\uff0c\u4e5f\u7edd\u4e0d\u6562 \u9003\u8d70\uff0c\u53ea\u662f\u9876\u7740\u90a3\u5757\u77f3\u5934\uff0c\u7b49\u6b7b\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u516d\u4eba\u7684\u6b66\u529f\uff0c\u65e0\u8bba\u9047\u89c1\u4ec0\u4e48\u4eba\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u62fc\u4e00\u62fc\uff0c\u4e3a\u4f55\u4e00\u89c1\u5230\u8fd9\u9ec4\u886b\u5c11\u5e74\u5c31\u597d \u50cf\u8001\u9f20\u9047\u89c1\u4e86\u732b\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5b9e\u5728\u4e0d\u660e\u767d\u3002 \u3000\u3000\u4ed6\u4e5f\u5e76\u4e0d\u60f3\u53bb\u5f04\u660e\u767d\uff0c\u6d3b\u5230\u4ed6\u8fd9\u4e48\u5927\u5e74\u7eaa\u7684\u4eba\uff0c\u5c31\u77e5\u9053\u6709\u4e9b\u4e8b\u8fd8\u662f\u7cca\u6d82\u4e9b\u597d\uff0c\u592a\u660e\u767d\u4e86 \u53cd\u800c\u70e6\u607c\u3002 \u3000\u3000\u597d\u4e45\u6ca1\u6709\u4e0b\u96e8\u4e86\uff0c\u5df7\u5802\u91cc\u7684\u98ce\u6c99\u5f88\u5927\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u9ec4\u886b\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df2\u5728\u5730\u4e0a\u753b\u4e86\u51e0\u5341\u4e2a\u5706\u5708\uff0c\u6bcf\u4e2a\u5706\u5708\u90fd\u53ea\u4e0d\u8fc7\u88c5\u6c64\u7684\u6d77\u7897\u90a3\u4e48\u5927 \u3002 \u3000\u3000\u6bb5\u5f00\u5c71\u7b49\u516d\u4eba\u8d70\u51fa\u6765\uff0c\u4e5f\u4e0d\u7b49\u522b\u4eba\u5429\u5490\uff0c\u5c31\u7ad9\u5230\u8fd9\u4e9b\u5706\u5708\u53bb\u4e86\uff0c\u4e00\u4e2a\u4eba\u7ad9\u4e00\u4e2a\u5706\u5708\uff0c\u6070 \u597d\u80fd\u5c06\u811a\u6446\u5728\u5706\u5708\u91cc\u3002 \u3000\u3000\u516d\u4e2a\u4eba\u7acb\u523b\u53c8\u50cf\u662f\u53d8\u6210\u4e86\u516d\u5757\u6728\u5934\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u53c8\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u6162\u6162\u8d70\u56de\u5c0f\u5e97\uff0c\u5728\u6bb5\u5f00\u5c71\u4ed6\u4eec\u65b9\u624d\u5750\u8fc7\u7684\u90a3\u5f20\u684c\u5b50\u65c1\u5750\u4e0b\u3002 \u3000\u3000\u90a3\u8138\u4e0a\u59cb\u7ec8\u51b7\u51b0\u51b0\u7684\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\u8fde\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u8fc7\u4e86\u7ea6\u6478\u4e24\u76cf\u8336\u7684\u65f6\u5019\uff0c\u53cc\u6709\u4e2a\u9ec4\u886b\u4eba\u8d70\u5165\u4e86\u5df7\u5802\u3002 \u3000\u3000\u8fd9\u4eba\u5e74\u9f84\u6bd4\u8f83\u5927\u4e9b\uff0c\u8033\u6735\u88ab\u4eba\u524a\u6389\u4e86\u4e00\u4e2a\uff0c\u773c\u775b\u4e5f\u778e\u4e86\u4e00\u53ea\uff0c\u5269\u4e0b\u7684\u4e00\u53ea\u72ec\u773c\u4e2d\uff0c\u95ea\u95ea \u7684\u53d1\u7740\u51f6\u5149\u3002 \u3000\u3000\u4ed6\u7a7f\u7684\u674f\u9ec4\u8272\u957f\u886b\u4e0a\u4e5f\u9576\u7740\u9ec4\u8272\uff0c\u8eab\u540e\u4e5f\u4e00\u8fde\u4e32\u8ddf\u7740\u4e03\u516b\u4e2a\u4eba\uff0c\u6709\u8001\u6709\u5c11\uff0c\u6709\u9ad8\u6709\u77ee\u3002 \u3000\u3000\u770b\u4ed6\u4eec\u7684\u88c5\u675f\u6253\u626e\uff0c\u663e\u7136\u5e76\u4e0d\u662f\u6ca1\u540d\u6ca1\u59d3\u7684\u4eba\uff0c\u4f46\u73b0\u5728\u4e5f\u548c\u6bb5\u5f00\u5c71\u4ed6\u4eec\u4e00\u6837\uff0c\u4e00\u4e2a\u4e2a\u90fd \u54ed\u4e27\u7740\u8138\uff0c\u76f4\u7740\u8116\u5b50\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8ddf\u5728\u90a3\u72ec\u773c\u4eba\u8eab\u540e\uff0c\u8d70\u5230\u5c0f\u5e97\u524d\uff0c\u5c31\u5730\u7ad9\u5728\u5706\u5708\u91cc\u3002 \u3000\u3000\u5176\u4e2d\u6709\u4e2a\u4eba\u9edd\u9ed1\u7626\u524a\uff0c\u6ee1\u9762\u90fd\u662f\u7cbe\u608d\u4e4b\u8272\u3002 \u3000\u3000\u6bb5\u5f00\u5c71\u7b49\u516d\u4eba\u770b\u5230\u4ed6\uff0c\u90fd\u663e\u5f97\u5f88\u8be7\u5f02\uff0c\u4f3c\u4e4e\u5728\u5947\u602a\uff0c\u600e\u4e48\u4ed6\u4e5f\u6765\u4e86\uff1f \u3000\u3000\u72ec\u773c\u4eba\u76ee\u5149\u5728\u6bb5\u5f00\u5c71\u7b49\u516d\u4eba\u9762\u4e0a\u4e00\u626b\uff0c\u5634\u89d2\u5e26\u7740\u51b7\u7b11\uff0c\u4e5f\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u6162\u6162\u5730\u8d70\u5165\u4e86\u5c0f \u5e97\uff0c\u5728\u9ec4\u886b\u5c11\u5e74\u5bf9\u9762\u5750\u4e0b\u3002 \u3000\u3000\u4e24\u4eba\u4e92\u76f8\u770b\u4e86\u4e00\u773c\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u76cf\u8336\u65f6\u5019\uff0c\u5df7\u5802\u91cc\u53c8\u6709\u4e2a\u9ec4\u886b\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u770b\u6765\u663e\u5f97\u66f4\u82cd\u8001\uff0c\u987b\u53d1\u4ff1\u5df2\u82b1\u767d\uff0c\u8eab\u4e0a\u7a7f\u7684\u674f\u9ec4\u8272\u957f\u886b\u4e0a\u4e5f\u9576\u7740\u91d1\u8fb9\uff0c\u8eab\u540e\u4e5f\u4e00\u8fde \u4e32\u8ddf\u9612\u5341\u6765\u4e2a\u4eba\u3002 \u3000\u3000\u8fdc\u8fdc\u770b\u6765\uff0c\u4ed6\u957f\u5f97\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5f02\u6837\uff0c\u4f46\u8d70\u5230\u8fd1\u524d\uff0c\u624d\u53d1\u73b0\u8fd9\u4eba\u7684\u8138\u8272\u7adf\u662f\u7eff\u7684\uff0c\u886c\u7740\u4ed6 \u82b1\u767d\u5934\u53d1\uff0c\u66f4\u663e\u5f97\u8be1\u5f02\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u8138\u662f\u7eff\u7684\uff0c\u624b\u4e5f\u662f\u7eff\u7684\u3002 \u3000\u3000\u7ad9\u5728\u5c0f\u5e97\u5916\u7684\u4eba\u4e00\u770b\u8fd9\u7eff\u9762\u767d\u53d1\u7684\u9ec4\u886b\u5ba2\uff0c\u5c31\u597d\u50cf\u770b\u5230\u4e86\u9b3c\u4f3c\u7684\uff0c\u90fd\u4e0d\u89c9\u5012\u62bd\u4e86\u53e3\u51c9\u6c14 \uff0c\u6709\u7684\u4eba\u751a\u81f3\u5df2\u5728\u53d1\u6296\u3002 \u3000\u3000\u8fd8\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df7\u5802\u91cc\u5730\u4e0a\u753b\u7684\u51e0\u5341\u4e2a\u5706\u5708\u90fd\u5df2\u7ad9\u6ee1\u4e86\u4eba\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5c4f\u606f\u9759\u6c14\uff0c\u5664\u82e5 \u5bd2\u8749\uff0c\u65e2\u4e0d\u6562\u52a8\uff0c\u4e5f\u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u7a7f\u91d1\u8fb9\u9ec4\u886b\u7684\u4eba\u5df2\u5230\u4e86\u56db\u4e2a\uff0c\u6700\u540e\u4e00\u4e2a\u662f\u4e2a\u987b\u53d1\u7686\u767d\u7684\u8001\u4eba\uff0c\u8eab\u5f62\u5df2\u4f5d\u507b\uff0c\u6b65\u5c65\u5df2\u8e52\u8dda \uff0c\u770b\u6765\u6bd4\u90a3\u8bf4\u6ca1\u4e8b\u7684\u8001\u5934\u5b50\u8fd8\u8981\u5927\u51e0\u5c81\uff0c\u7b80\u76f4\u8001\u5f97\u8fde\u8def\u90fd\u8d70\u4e0d\u52a8\u4e86\uff0c\u4f46\u5e26\u6765\u7684\u4eba\u5374\u504f\u504f\u6700\u591a \u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u5404\u636e\u684c\u5b50\u7684\u4e00\u65b9\uff0c\u4e00\u8d70\u8fdb\u6765\u5c31\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u8c01\u4e5f\u4e0d\u5f00\u53e3\uff0c\u56db\u4e2a\u4eba\u4eff\u4f5b\u90fd\u662f \u54d1\u5427\u3002 \u3000\u3000\u5916\u9762\u7ad9\u5728\u5708\u5b50\u91cc\u7684\u4e00\u7fa4\u4eba\uff0c\u5634\u66f4\u597d\u50cf\u5168\u90fd\u7f1d\u8d77\u6765\u4e86\uff0c\u91cc\u9762\u5916\u5916\u9664\u4e86\u547c\u5438\u58f0\u5916\uff0c\u4ec0\u4e48\u58f0\u97f3 \u90fd\u542c\u4e0d\u5230\u3002 \u3000\u3000\u8fd9\u5c0f\u5e97\u7b80\u76f4\u53d8\u5f97\u50cf\u5ea7\u575f\u5893\uff0c\u8fde\u5b59\u9a7c\u5b50\u90fd\u5df2\u53d7\u4e0d\u4e86\uff01\u90a3\u7956\u5b59\u4e24\u4eba\u548c\u9752\u9762\u6c49\u5b50\u5374\u504f\u504f\u8fd8\u662f\u4e0d \u80af\u8d70\u3002 \u3000\u3000\u4ed6\u4eec\u96be\u9053\u8fd8\u5728\u7b49\u7740\u770b\u628a\u620f\u3002 \u3000\u3000\u8fd9\u7b80\u76f4\u662f\u8981\u547d\u7684\u628a\u620f\uff01 \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u8981\u4eba\u547d\u7684\u91d1\u94b1>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u8981\u4eba\u547d\u7684\u91d1\u94b1 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5df7\u5802\u5c3d\u5934\u7a81\u7136\u4f20\u6765\u4e00\u9635\u7b03\u3001\u7b03\u3001\u7b03\u3001\u7b03\u2014\u2014\u4e4b\u58f0\uff0c\u58f0\u97f3\u5355\u8c03\u800c\u6c89\u95f7\u3002 \u3000\u3000\u4f46\u8fd9\u58f0\u97f3\u5728\u8fd9\u79cd\u65f6\u5019\u542c\u6765\uff0c\u5374\u53e6\u6709\u4e00\u79cd\u9634\u68ee\u8be1\u79d8\u4e4b\u610f\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u5934\u90fd\u597d\u50cf\u88ab\u68cd\u5b50\u5728\u6572\u3002 \u3000\u3000\u7b03\u3001\u7b03\u3001\u7b03\u2014\u2014\u7b80\u76f4\u8981\u628a\u4eba\u7684\u9b42\u90fd\u6572\u6563\u4e86\u3002 \u3000\u3000\u56db\u4e2a\u9ec4\u886b\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u4e00\u9f50\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u51c4\u51c9\u7684\u591c\u8272\u4e2d\uff0c\u6162\u6162\u5730\u51fa\u73b0\u4e86\u4e00\u6761\u4eba\u5f71\uff01 \u3000\u3000\u8fd9\u4eba\u7684\u5de6\u817f\u5df2\u9f50\u6839\u65ad\u53bb\uff0c\u67f1\u7740\u6839\u62d0\u6756\u3002 \u3000\u3000\u6697\u6de1\u7684\u706f\u5149\u4ece\u5c0f\u5e97\u91cc\u7167\u51fa\u6765\uff0c\u7167\u5728\u8fd9\u4eba\u8138\u4e0a\uff0c\u53ea\u89c1\u8fd9\u4eba\u84ec\u5934\u6563\u53d1\uff0c\u9762\u5982\u9505\u5e95\uff0c\u8138\u4e0a\u6ee1\u662f \u5200\u75a4\uff01 \u3000\u3000\u4e09\u89d2\u773c\uff0c\u626b\u5730\u7709\uff0c\u9f3b\u5b50\u5927\u5f97\u51fa\u5947\uff0c\u5634\u4e5f\u5927\u5f97\u51fa\u5947\uff0c\u8fd9\u5f20\u8138\u4e0a\u5c31\u7b97\u6ca1\u6709\u5200\u75a4\uff0c\u4e5f\u5df2\u4e11\u5f97\u591f \u5413\u4eba\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u5230\u8fd9\u4eba\uff0c\u5fc3\u91cc\u96be\u514d\u8981\u5192\u51fa\u4e00\u80a1\u5bd2\u6c14\u3002 \u3000\u3000\u56db\u4e2a\u9ec4\u886b\u4eba\u7adf\u4e00\u9f50\u8fce\u4e86\u51fa\u53bb\uff0c\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u8fd9\u72ec\u817f\u4eba\u5df2\u6446\u4e86\u6446\u624b\u3002 \u3000\u3000\u7b03\u3001\u7b03\u3001\u7b03\u2014\u2014\u4eba\u4e5f\u8d70\u5165\u4e86\u5c0f\u5e97\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u8fd9\u65f6\u770b\u51fa\u4ed6\u8eab\u4e0a\u7a7f\u7684\u4e5f\u662f\u4ef6\u674f\u9ec4\u8272\u7684\u957f\u886b\uff0c\u5374\u5c06\u4e0b\u6446\u6396\u5728\u8170\u5e26\u91cc\uff0c\u5df2\u810f\u5f97\u8fde\u989c\u8272 \u90fd\u5206\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u8fd9\u4ef6\u810f\u5f97\u8981\u547d\u7684\u9ec4\u886b\u4e0a\uff0c\u5374\u9576\u7740\u4e24\u9053\u91d1\u8fb9\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u77a7\u89c1\u8fd9\u4eba\u8d70\u8fdb\u6765\uff0c\u8138\u8272\u4f3c\u4e5f\u53d8\u4e86\u53d8\u3002 \u3000\u3000\u90a3\u8fab\u5b50\u59d1\u5a18\u66f4\u65e9\u5df2\u626d\u8fc7\u5934\u53bb\uff0c\u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u72ec\u817f\u4eba\u4e09\u89d2\u773c\u91cc\u5149\u8292\u95ea\u52a8\uff0c\u56db\u4e0b\u4e00\u626b\uff0c\u770b\u5230\u90a3\u9752\u9762\u6c49\u5b50\u65f6\uff0c\u4ed6\u4f3c\u4e4e\u76b1\u4e86\u76b1\u7709\uff0c\u8f6c\u8eab\u9053\uff1a \u4f60\u4eec\u8f9b\u82e6\u4e86\u3002 \u3000\u3000\u4ed6\u76f8\u8c8c\u51f6\u6076\uff0c\u8bf4\u8d77\u6765\u5374\u6e29\u548c\u5f97\u5f88\uff0c\u58f0\u97f3\u4e5f\u597d\u542c\u3002 \u3000\u3000\u56db\u4e2a\u9ec4\u886b\u4eba\u9f50\u5730\u8eac\u8eab\u9053\uff1a\u4e0d\u6562\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u5168\u90fd\u5e26\u6765\u4e86\u4e48\uff1f \u3000\u3000\u9ec4\u886b\u4eba\u9053\uff1a\u4e00\u5171\u56db\u5341\u4e5d\u4eba\uff0c\u5168\u90fd\u5230\u9f50\u4e86\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u4f60\u80fd\u786e\u5b9a\u4ed6\u4eec\u662f\u4e3a\u90a3\u4ef6\u4e8b\u6765\u7684\u4e48\uff1f \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u5728\u4e0b\u7b49\u5df2\u8c03\u67e5\u786e\u5b9e\uff0c\u8fd9\u4e9b\u4eba\u90fd\u5728\u4e09\u5929\u5185\u8d76\u6765\u7684\uff0c\u60f3\u5fc5\u90fd\u662f\u4e3a\u4e86\u90a3\u4ef6\u4e8b\u800c\u6765 \uff0c\u5426\u5219\u600e\u4f1a\u4e0d\u7ea6\u800c\u540c\u5730\u6765\u5230\u8fd9\u91cc\uff1f \u3000\u3000\u72ec\u817f\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u8c03\u67e5\u6e05\u695a\u4e86\u5c31\u597d\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u9519\u602a\u4e86\u597d\u4eba\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u662f\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u54b1\u4eec\u7684\u610f\u601d\uff0c\u8fd9\u4e9b\u4eba\u660e\u767d\u6ca1\u6709\uff1f \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u53ea\u6015\u8fd8\u672a\u660e\u767d\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u90a3\u4e48\u4f60\u5c31\u53bb\u5411\u4ed6\u4eec\u8bf4\u660e\u767d\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u662f\u3002 \u3000\u3000\u4ed6\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u5404\u4f4d\u60f3\u5fc5\u5df2\u77e5\u9053\u4e86\uff0c\u5404\u4f4d\u7684\u6765\u610f\uff0c\u6211\u4eec\u4e5f \u6e05\u695a\u5f97\u5f88\u3002 \u3000\u3000\u63a5\u7740\u9053\uff1a\u5404\u4f4d\u60f3\u5fc5\u90fd\u63a5\u5230\u4e86\u540c\u6837\u7684\u4e00\u5c01\u4fe1\uff0c\u624d\u8d76\u5230\u8fd9\u91cc\u6765\u7684\u3002 \u3000\u3000\u5927\u5bb6\u65e2\u4e0d\u6562\u70b9\u5934\uff0c\u53c8\u63e1\u8bf4\u9519\u4e86\u8bdd\uff0c\u53ea\u80fd\u5728\u9f3b\u5b50\u91cc\u55ef\u4e86\u4e00\u58f0\uff0c\u51e0\u5341\u4e2a\u4eba\u9f3b\u5b50\u91cc\u540c\u65f6\u51fa\u58f0\uff0c \u90a3\u58f0\u97f3\u5b9e\u5728\u5947\u602a\u5f97\u5f88\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u4f46\u51ed\u5404\u4f4d\u7684\u8fd9\u70b9\u672c\u4e8b\uff0c\u5c31\u60f3\u6765\u8fd9\u91cc\u6253\u4e3b\u610f\uff0c\u53ea\u6015\u8fd8\u4e0d\u914d\uff0c\u6240\u4ee5\u5404\u4f4d\u8fd8\u662f\u7ad9 \u5728\u8fd9\u91cc\uff0c\u7b49\u4e8b\u5b8c\u518d\u77b3\u7684\u597d\uff0c\u6211\u4eec\u53ef\u4ee5\u4fdd\u8bc1\u5404\u4f4d\u7684\u5b89\u5168\uff0c\u53ea\u8981\u5404\u4f4d\u7ad9\u7740\u4e0d\u52a8\uff0c\u7edd\u6ca1\u6709\u4eba\u4f1a\u6765\u4f24 \u53ca\u5404\u4f4d\u6beb\u53d1\u3002 \u3000\u3000\u4ed6\u6de1\u7b11\u9053\uff1a\u5404\u4f4d\u60f3\u5fc5\u90fd\u77e5\u9053\uff0c\u6211\u4eec\u4e0d\u5230\u4e0d\u5f97\u5df2\u65f6\uff0c\u662f\u4e0d\u4f24\u4eba\u7684\u3002 \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u6709\u4eba\u6253\u4e86\u4e2a\u55b7\xd7\u3002 \u3000\u3000\u90a3\u4eba\u6b63\u662f\u6c34\u86c7\u80e1\u5a9a\u3002 \u3000\u3000\u5973\u4eba\u4e3a\u4e86\u6015\u81ea\u5df1\u7684\u8170\u80a2\u770b\u6765\u592a\u7c97\uff0c\u5b81\u53ef\u51bb\u6b7b\u4e5f\u4e0d\u80af\u591a\u7a7f\u4ef6\u8863\u670d\u7684\uff0c\u5927\u591a\u6570\u5973\u4eba\u90fd\u6709\u8fd9\u79cd \u6bdb\u75c5\u3002 \u3000\u3000\u80e1\u5a9a\u8fd9\u79cd\u6bdb\u75c5\u66f4\u91cd\u3002 \u3000\u3000\u5979\u7a7f\u5f97\u65e2\u5c11\uff0c\u5df7\u5802\u91cc\u7684\u98ce\u53c8\u5927\uff0c\u5979\u4e00\u4e2a\u4eba\u7ad9\u5728\u6700\u524d\u9762\uff0c\u6070\u597d\u8fce\u7740\u98ce\u53e3\uff0c\u5439\u4e86\u534a\u4e2a\u591a\u65f6\u8fb0 \uff0c\u600e\u4f1a\u4e0d\u7740\u51c9\u3002 \u3000\u3000\u80e1\u5a9a\u4e00\u6253\u55b7\xd7\uff0c\u5934\u4e0a\u9876\u7740\u7684\u94dc\u94b1\u5c31\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u542c\u53ee\u7684\u4e00\u58f0\uff0c\u94dc\u94b1\u6389\u5728\u5730\u4e0a\uff0c\u9aa8\u788c\u788c\u6d41\u901a\u51fa\u53bb\u597d\u8fdc\uff0c\u4e0d\u4f46\u80e1\u5a9a\u7acb\u523b\u9762\u65e0\u4eba\u8272\uff0c\u522b\u7684\u4eba \u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u76b1\u4e86\u7709\u9053\uff1a\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u4f60\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u80e1\u5a9a\u98a4\u58f0\u9053\uff1a\u77e5\u2014\u2014\u77e5\u9053\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u65e2\u7136\u77e5\u9053\uff0c\u4f60\u5c31\u672a\u514d\u592a\u4e0d\u5c0f\u5fc3\u4e86\u3002 \u3000\u3000\u80e1\u5a9a\u8eab\u5b50\u53d1\u6296\u9053\uff1a\u665a\u8f88\u7edd\u4e0d\u662f\u6545\u610f\u7684\uff0c\u6c42\u524d\u8f88\u9976\u6211\u8fd9\u4e00\u6b21\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u4f1a\u662f\u6545\u610f\u7684\uff0c\u5374\u4e5f\u4e0d\u80fd\u574f\u4e86\u89c4\u77e9\uff0c\u89c4\u77e9\u4e00\u574f\uff0c\u5a01\u4fe1\u65e0\u5b58\uff0c\u4f60 \u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u8fd9\u9053\u7406\u4f60\u603b\u8be5\u660e\u767d\u3002 \u3000\u3000\u80e1\u5a9a\u8f6c\u8fc7\u5934\uff0c\u4ef0\u9762\u671b\u7740\u80e1\u975e\uff0c\u54c0\u5524\u9053\uff1a\u5927\u54e5\uff0c\u4f60\u2014\u2014\u4e5f\u4e0d\u66ff\u6211\u8bf4\u53e5\u8bdd\uff1f \u3000\u3000\u80e1\u975e\u7f13\u7f13\u95ed\u8d77\u773c\u775b\uff0c\u9762\u988a\u4e0a\u808c\u8089\u4e0d\u505c\u98a4\u52a8\uff0c\u9053\uff1a\u6211\u8bf4\u4e86\u8bdd\u53c8\u6709\u4ec0\u4e48\u7528\uff1f \u3000\u3000\u80e1\u5a9a\u51c4\u7b11\u9053\uff1a\u6211\u660e\u767d\u2014\u2014\u6211\u4e0d\u602a\u4f60\uff01 \u3000\u3000\u5979\u76ee\u5149\u79fb\u5411\u6768\u627f\u7956\uff1a\u5c0f\u6768\u4f60\u5462\uff1f\u2014\u2014\u6211\u5c31\u8981\u8d70\u4e86\uff0c\u4f60\u4e5f\u6ca1\u6709\u8bdd\u5bf9\u6211\u8bf4\uff1f \u3000\u3000\u6768\u627f\u7956\u773c\u775b\u76f4\u52fe\u52fe\u5730\u77aa\u7740\u524d\u9762\uff0c\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u80e1\u5a9a\u9053\uff1a\u4f60\u96be\u9053\u8fde\u770b\u90fd\u4e0d\u613f\u770b\u6211\u4e00\u773c\uff1f \u3000\u3000\u6768\u627f\u7956\u7d22\u6027\u5c06\u773c\u775b\u4e5f\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u80e1\u5a9a\u7a81\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u6307\u7740\u6768\u627f\u7956\u9053\uff1a\u4f60\u4eec\u5927\u5bb6\u770b\u770b\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u60c5\u4eba\uff0c\u8fd9\u4eba\u6628\u5929\u665a\u4e0a\u8fd8 \u5bf9\u6211\u8bf4\uff0c\u53ea\u8981\u6211\u5bf9\u4ed6\u597d\uff0c\u4ed6\u4e0d\u60dc\u4e3a\u6211\u6b7b\u7684\uff0c\u4f46\u73b0\u5728\u5462\uff1f\u73b0\u5728\u4ed6\u8fde\u770b\u90fd\u4e0d\u6562\u770b\u6211\uff0c\u597d\u50cf\u53ea\u8981\u770b \u4e86\u6211\u4e00\u773c\uff0c\u5c31\u4f1a\u5f97\u9ebb\u75af\u75c5\u4f3c\u7684\u2014\u2014 \u3000\u3000\u5979\u7b11\u58f0\u6e10\u6e10\u4f4e\u6c89\uff0c\u773c\u6cea\u5374\u5df2\u6d41\u4e0b\u9762\u988a\uff0c\u5583\u5583\u9053\uff1a\u4ec0\u4e48\u53eb\u505a\u60c5\uff1f\u4ec0\u4e48\u53eb\u505a\u7231\uff1f\u4e00\u4e2a\u4eba\u6d3b\u7740 \u53c8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u771f\u4e0d\u5982\u6b7b\u4e86\u53cd\u5012\u597d\u4e9b\uff0c\u4e5f\u514d\u5f97\u70e6\u607c\u2014\u2014 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u5ffd\u7136\u5c31\u5730\u4e00\u6eda\uff0c\u6eda\u51fa\u4e03\u516b\u5c3a\uff0c\u53cc\u624b\u9f50\u626c\uff0c\u53d1\u51fa\u4e86\u6570\u5341\u70b9\u5bd2\u661f\uff0c\u5e26\u7740\u5c16\u9510\u7684 \u98ce\u58f0\uff0c\u51fb\u5411\u90a3\u9ec4\u886b\u8001\u4eba\u3002 \u3000\u3000\u5979\u8eab\u5b50\u4e5f\u5df2\u51cc\u7a7a\u63a0\u8fc7\uff0c\u4f3c\u4e4e\u60f3\u63a0\u5165\u9ad8\u5899\u3002 \u3000\u3000\u6c34\u86c7\u80e1\u5a9a\u4ee5\u6697\u5668\u8f7b\u529f\u89c1\u957f\uff0c\u8eab\u624b\u679c\u7136\u4e0d\u4fd7\uff0c\u53d1\u51fa\u7684\u6697\u5668\u53c8\u591a\u3001\u53c8\u6025\u3001\u53c8\u51c6\u3001\u53c8\u72e0\uff01 \u3000\u3000\u9ec4\u886b\u8001\u4eba\uff0c\u5374\u53ea\u662f\u6de1\u6de1\u5730\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u8fd9\u53cc\u4f55\u82e6\uff1f \u3000\u3000\u4ed6\u8bf4\u8bdd\u8d70\u8def\u90fd\u662f\u6162\u541e\u541e\u7684\uff0c\u51fa\u624b\u5374\u5feb\u5f97\u60ca\u4eba\uff0c\u8fd9\u77ed\u77ed\u56db\u4e2a\u5b57\u8bf4\u5b8c\uff0c\u6570\u5341\u70b9\u5bd2\u661f\u5df2\u90fd\u88ab\u4ed6 \u5377\u5165\u8896\u4e2d\u3002 \u3000\u3000\u80e1\u5a9a\u4eba\u521a\u63a0\u8d77\uff0c\u9aa4\u7136\u89c9\u5f97\u4e00\u80a1\u5927\u529b\u88ad\u6765\uff0c\u8eab\u5b50\u4e0d\u7531\u81ea\u4e3b\u7830\u7684\u649e\u5230\u5899\u4e0a\uff0c\u81ea\u5899\u4e0a\u6ed1\u843d\uff0c\u8033 \u9f3b\u4e94\u5b98\u90fd\u5df2\u6c81\u51fa\u4e86\u9c9c\u8840\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u4f60\u672c\u6765\u53ef\u4ee5\u6b7b\u5f97\u8212\u670d\u4e9b\u7684\uff0c\u53c8\u4f55\u82e6\u591a\u6b64\u4e00\u4e3e\u3002 \u3000\u3000\u80e1\u5a9a\u624b\u6342\u7740\u80f8\u819b\uff0c\u4e0d\u505c\u5730\u54b3\u55fd\uff0c\u54b3\u4e00\u58f0\uff0c\u4e00\u53e3\u8840\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u4f46\u4f60\u4e34\u6b7b\u524d\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u7b54\u5e94\u4f60\u4e00\u4e2a\u8981\u6c42\u3002 \u3000\u3000\u80e1\u5a9a\u9053\uff1a\u8fd9\u4e5f\u662f\u4f60\u4eec\u7684\u89c4\u77e9\uff1f \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u80e1\u5a9a\u9053\uff1a\u6211\u65e0\u8bba\u8981\u6c42\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4eec\u90fd\u7b54\u5e94\u6211\uff1f \u3000\u3000\u9ec4\u886b\u9053\uff1a\u4f60\u82e5\u6709\u4ec0\u4e48\u672a\u4e86\u7684\u5fc3\u613f\uff0c\u6211\u4eec\u53ef\u4ee5\u66ff\u4f60\u53bb\u505a\uff0c\u4f60\u82e5\u6709\u4ec7\u672a\u62a5\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u66ff\u4f60 \u53bb\u590d\u4ec7\uff01 \u3000\u3000\u4ed6\u6de1\u6de1\u5730\u7b11\u4e86\u7b11\uff0c\u60a0\u7136\u63a5\u7740\u9053\uff1a\u80fd\u6b7b\u5728\u6211\u4eec\u624b\u4e0a\u7684\u4eba\uff0c\u8fd0\u6c14\u5e76\u4e0d\u9519\u3002 \u3000\u3000\u80e1\u5a9a\u9732\u51fa\u4e86\u4e00\u79cd\u5f02\u6837\u7684\u5149\u8292\uff0c\u9053\uff1a\u6211\u65e2\u5df2\u975e\u6b7b\u4e0d\u53ef\uff0c\u4e0d\u77e5\u53ef\u4e0d\u53ef\u4ee5\u9009\u4e2a\u4eba\u6765\u6740\u6211\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u90a3\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u5374\u4e0d\u77e5\u4f60\u60f3\u9009\u7684\u662f\u8c01\uff1f \u3000\u3000\u80e1\u5a9a\u54ac\u7740\u5634\u5507\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u5c31\u662f\u4ed6\uff0c\u6768\u627f\u7956\uff01 \u3000\u3000\u6768\u627f\u7956\u8138\u8272\u7acb\u523b\u53d8\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4f60\u96be\u9053\u60f3\u5bb3\u6211\uff1f \u3000\u3000\u80e1\u5a9a\u9053\uff1a\u4f60\u5bf9\u6211\u867d\u662f\u865a\u60c5\u5047\u610f\uff0c\u6211\u5bf9\u4f60\u5374\u662f\u60c5\u771f\u610f\u6d53\uff0c\u53ea\u8981\u80fd\u6b7b\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u6211\u6b7b\u4e5f\u7518 \u5fc3\u4e86\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u9053\uff1a\u6740\u4eba\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\uff0c\u4f60\u96be\u9053\u4ece\u672a\u6740\u8fc7\u4eba\u4e48\uff1f \u3000\u3000\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u5c31\u6709\u4e2a\u9ec4\u886b\u5927\u6c49\u62d4\u51fa\u4e86\u8170\u5200\uff0c\u8d70\u8fc7\u53bb\u9012\u7ed9\u6768\u627f\u7956\uff0c\u7b11\u9053\uff1a\u8fd9\u628a\u5200\u5feb\u5f97\u5f88\uff0c \u6740\u4eba\u4e00\u5b9a\u7528\u4e0d\u7740\u7b2c\u4e8c\u5200\uff01 \u3000\u3000\u6768\u627f\u7956\u60c5\u4e0d\u81ea\u7981\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u6211\u4e0d\u2014\u2014 \u3000\u3000\u521a\u8bf4\u5230\u4e0d\u5b57\uff0c\u4ed6\u5934\u9876\u4e0a\u7684\u94dc\u94b1\u4e5f\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ee\u7684\u4e00\u58f0\uff0c\u94dc\u94b1\u6389\u5728\u5730\u4e0a\uff0c\u76f4\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6768\u627f\u7956\u6574\u4e2a\u4eba\u5413\u5446\u4e86\u3002 \u3000\u3000\u80e1\u5a9a\u75af\u72c2\u822c\u5927\u7b11\u8d77\u6765\uff0c\u683c\u683c\u7b11\u9053\uff1a\u4f60\u8bf4\u8fc7\uff0c\u6211\u82e5\u6b7b\u4e86\uff0c\u4f60\u4e5f\u6d3b\u4e0d\u4e0b\u53bb\uff0c\u73b0\u5728\u4f60\u679c\u7136\u8981\u966a \u6211\u6b7b\u4e86\uff0c\u4f60\u8fd9\u4eba\u603b\u7b97\u8fd8\u6709\u51e0\u5206\u826f\u5fc3\u2014\u2014 \u3000\u3000\u6768\u627f\u7956\u5168\u8eab\u53d1\u6296\uff0c\u7a81\u7136\u72c2\u543c\u4e00\u58f0\uff0c\u5927\u9a82\u9053\uff1a\u4f60\u8fd9\u5a07\u5987\uff0c\u4f60\u597d\u6bd2\u7684\u5fc3\u80a0\uff01 \u3000\u3000\u4ed6\u72c2\u543c\u7740\u593a\u8fc7\u90a3\u628a\u5200\uff0c\u4e00\u5200\u780d\u5728\u80e1\u5a9a\u8116\u5b50\u4e0a\uff0c\u9c9c\u8840\u4f3c\u7bad\u4e00\u822c\u98de\u6e85\u800c\u51fa\uff0c\u67d3\u7ea2\u4e86\u6768\u627f\u7956\u7684 \u8863\u670d\u3002 \u3000\u3000\u4ed6\u5598\u7740\u6c14\uff0c\u53d1\u7740\u6296\uff0c\u6162\u6162\u5730\u62ac\u8d77\u5934\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5728\u51b7\u51b7\u5728\u671b\u7740\u4ed6\u3002 \u3000\u3000\u591c\u8272\u51c4\u8ff7\uff0c\u4e0d\u77e5\u4f55\u65f6\u8d77\u4e86\u4e00\u7247\u4e73\u767d\u8272\u7684\u6d53\u96fe\u3002 \u3000\u3000\u6768\u627f\u7956\u8dfa\u4e86\u8dfa\u811a\uff0c\u53cd\u624b\u4e00\u5200\u5411\u81ea\u5df1\u7684\u8116\u5b50\u4e0a\u62b9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u5c38\u4f53\u6b63\u597d\u5012\u5728\u80e1\u5a9a\u8eab\u4e0a\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u8fd9\u624d\u660e\u767d\u8fd9\u4e9b\u4eba\u8d70\u8def\u65f6\u4e3a\u4f55\u90a3\u822c\u5c0f\u5fc3\u4e86\uff0c\u539f\u6765\u8981\u662f\u4ea7\u4e00\u4e0d\u5c0f\u5fc3\u5c06\u5934\u9876\u4e0a\u7684\u94dc\u94b1\u6389 \u843d\uff0c\u5c31\u975e\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u4e9b\u9ec4\u886b\u4eba\u7684\u89c4\u77e9\u4e0d\u4f46\u592a\u53ef\u6015\uff0c\u4e5f\u592a\u53ef\u6076\u3002 \u3000\u3000\u90a3\u9752\u9762\u6c49\u5b50\u6839\u672c\u65e0\u52a8\u4e8e\u8877\uff0c\u5bf9\u8fd9\u79cd\u4e8b\u4f3c\u5df2\u53f8\u7a7a\u89c1\u60ef\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u72ec\u817f\u4eba\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u5730\u8d70\u5230\u90a3\u9752\u9762\u7626\u957f\u6c49\u5b50\u7684\u684c\u524d\uff0c\u5728\u5bf9\u9762\u5750\u4e0b\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u6162\u6162\u5730\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5b59\u9a7c\u5b50\u5374\u5ffd\u7136\u7d27\u5f20\u8d77\u6765\uff0c\u5c31\u597d\u50cf\u6709\u4ec0\u4e48\u53ef\u6015\u7684\u4e8b\u7acb\u523b\u5c31\u8981\u53d1\u751f\u4e86 \u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u8fd9\u4e24\u4eba\u7684\u773c\u775b\u90fd\u50cf\u662f\u5200\uff0c\u6068\u4e0d\u5f97\u4e00\u5200\u523a\u5165\u5bf9\u65b9\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u96fe\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u72ec\u817f\u4eba\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u7b11\u5f97\u5f88\u7279\u522b\uff0c\u5f88\u5947\u602a\uff0c\u4e00\u7b11\u8d77\u6765\uff0c\u5c31\u4ee4\u4eba\u7acb\u523b\u5fd8\u4e86\u4ed6\u7684\u51f6\u6076\u548c\u4e11\u964b\uff0c\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u6e29 \u67d4\u4eb2\u5207\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u9601\u4e0b\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u4eec\u5df2\u77e5\u9053\u4e86\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u54e6\uff01 \u3000\u3000\u87af\u817f\u4eba\u9053\uff1a\u6211\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u9601\u4e0b\u60f3\u5fc5\u4e5f\u5df2\u77e5\u9053\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u51b7\u9053\uff1a\u8fd1\u4e24\u5e74\u6765\u4e0d\u77e5\u9053\u4f60\u4eec\u7684\u4eba\uff0c\u53ea\u6015\u5f88\u5c11\u3002 \u3000\u3000\u72ec\u817f\u4eba\u7b11\u4e86\u7b11\uff0c\u6162\u6162\u5730\u81ea\u6000\u4e2d\u53d6\u51fa\u4e86\u4e00\u5c01\u4fe1\u3002 \u3000\u3000\u8fd9\u5c01\u4fe1\u548c\u90a3\u9ec4\u886b\u4eba\u53d6\u51fa\u7684\u4e00\u6837\uff0c\u770b\u6765\u5e76\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u4f46\u5c31\u8fde\u5b59\u9a7c\u5b50\u4e5f\u5fcd\u4e0d\u4f4f\u60f3\u77a7 \u77a7\u4fe1\u5c01\u4e0a\u5199\u7684\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u72ec\u817f\u4eba\u5c06\u8fd9\u5c01\u4fe1\u7528\u624b\u538b\u5728\u684c\u4e0a\u4e86\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u9601\u4e0b\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u60f3\u5fc5\u4e5f\u662f\u4e3a\u4e86\u8fd9\u5c01\u4fe1 \u6765\u7684\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u9601\u4e0b\u53ef\u77e5\u9053\u8fd9\u5c01\u4fe1\u662f\u8c01\u5199\u7684\u4e48\uff1f \u3000\u3000\u9752\u9762\u6c49\u5b50\uff1a\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u636e\u6211\u4eec\u6240\u77e5\uff0c\u6c5f\u6e56\u4e2d\u63a5\u5230\u8fd9\u6837\u4fe1\u7684\u81f3\u5c11\u6709\u4e00\u767e\u591a\u4f4d\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u4fe1 \u662f\u8c01\u5199\u7684\uff0c\u6211\u4eec\u4e5f\u66fe\u56db\u4e0b\u6253\u542c\uff0c\u5374\u8fde\u4e00\u70b9\u7ebf\u7d22\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u82e5\u8fde\u4f60\u4eec\u4e5f\u6253\u542c\u4e0d\u51fa\uff0c\u8fd8\u6709\u8c01\u80fd\u6253\u542c\u5f97\u51fa\uff01 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u6211\u4eec\u867d\u4e0d\u77e5\u9053\u4fe1\u662f\u8c01\u5199\u7684\uff0c\u4f46\u4ed6\u7684\u7528\u610f\u6211\u4eec\u5374\u5df2\u660e\u767d\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u54e6\uff1f \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u4ed6\u5c06\u6c5f\u6e56\u4e2d\u6210\u540d\u7684\u8c6a\u6770\u5f15\u5230\u8fd9\u91cc\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u5927\u5bb6\u4e89\u5948\u57cb\u5728\u8fd9\u91cc\u7684\u5b9d\u7269\uff0c \u7136\u540e\u81ea\u76f8\u6b8b\u6740\uff01\u4ed6\u624d\u597d\u5f97\u6e14\u7fc1\u4e4b\u5229\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u4e3a\u4f55\u8981\u6765\uff1f \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u6b63\u56e0\u4ed6\u5c45\u5fc3\u9669\u6076\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u975e\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u54e6\uff1f \u3000\u3000\u72ec\u817f\u4eba\u7b11\u4e86\u7b11\u9053\uff1a\u6211\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u4e3a\u7684\u662f\u8981\u529d\u5404\u4f4d\u83ab\u8981\u4e0a\u90a3\u4eba\u7684\u5f53\uff0c\u53ea\u8981\u5404\u4f4d\u80af\u653e\u624b \uff0c\u8fd9\u4e00\u573a\u7978\u4e8b\u5c31\u53ef\u4ee5\u6d88\u5f25\u65e0\u5f62\u4e86\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u4f60\u4eec\u7684\u5fc3\u80a0\u5012\u771f\u4e0d\u9519\u3002 \u3000\u3000\u72ec\u817f\u4eba\u4f3c\u4e4e\u6839\u672c\u542c\u4e0d\u51fa\u4ed6\u8bdd\u4e2d\u7684\u523a\uff0c\u8fd8\u662f\u5fae\u7b11\u9053\uff1a\u6211\u4eec\u53ea\u5e0c\u671b\u80fd\u5c06\u5927\u4e8b\u5316\u5c0f\u4e8b\uff0c\u5c0f\u4e8b\u5316 \u65e0\u4e8b\uff0c\u8ba9\u5927\u5bb6\u90fd\u80fd\u5b89\u5b89\u9759\u9759\u5730\u8fc7\u51e0\u5e74\u592a\u5e73\u65e5\u5b50\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u5176\u5b9e\u6b64\u95f4\u662f\u5426\u771f\u6709\u5b9d\u85cf\uff0c\u5927\u5bb6\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u72ec\u817f\u4eba\u62ca\u638c\u9053\uff1a\u6b63\u662f\u5982\u6b64\uff0c\u6240\u4ee5\u5927\u5bb6\u82e5\u662f\u4e3a\u4e86\u8fd9\u79cd\u4e8b\u800c\u62fc\u547d\uff0c\u5c82\u975e\u592a\u4e0d\u503c\u5f97\u4e86\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u9053\uff1a\u4f46\u6211\u65e2\u5df2\u6765\u4e86\uff0c\u597d\u6b79\u4e5f\u5f97\u770b\u4ed6\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u5c82\u662f\u522b\u4eba\u4e09\u8a00\u4e24\u8bed\u5c31\u80fd\u6211\u6253\u53d1 \u8d70\u7684\u3002 \u3000\u3000\u72ec\u817f\u4eba\u7acb\u523b\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u9601\u4e0b\u662f\u4e0d\u80af\u653e\u624b\u7684\u4e86\uff1f \u3000\u3000\u9752\u9762\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u6211\u5c31\u7b97\u653e\u4e86\u624b\uff0c\u53ea\u6015\u4e5f\u8f6e\u4e0d\u5230\u4f60\u4eec\uff01 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u9664\u4e86\u9601\u4e0b\u5916\uff0c\u6211\u5012\u60f3\u4e0d\u51fa\u8fd8\u6709\u8c01\u80fd\u8ddf\u6211\u4eec\u4e00\u4e89\u957f\u77ed\u7684\u3002 \u3000\u3000\u4ed6\u5c06\u624b\u91cc\u7684\u94c1\u62d0\u91cd\u91cd\u4e00\u987f\uff0c\u53ea\u542c\u7b03\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u56db\u5c3a\u591a\u957f\u7684\u94c1\u62d0\uff0c\u8d6b\u7136\u5df2\u6709\u4e09\u5c3a \u591a\u63d2\u5165\u5730\u4e0b\u3002 \u3000\u3000\u9752\u9762\u6c49\u5b50\u795e\u8272\u4e0d\u53d8\uff0c\u51b7\u51b7\u9053\uff1a\u679c\u7136\u597d\u529f\u592b\uff0c\u96be\u602a\u767e\u6653\u751f\u4f5c\u5175\u5668\u8c31\uff0c\u8981\u5c06\u4f60\u8fd9\u53ea\u94c1\u62d0\u6392\u540d \u7b2c\u516b\u3002 \u3000\u3000\u72ec\u817f\u4eba\u5389\u58f0\u9053\uff1a\u9601\u4e0b\u7684\u86c7\u97ad\u6392\u540d\u7b2c\u4e03\uff0c\u6211\u65e9\u5c31\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4e86\uff01 \u3000\u3000\u9752\u9762\u6c49\u5b50\uff1a\u6211\u4e5f\u6b63\u60f3\u8981\u4f60\u4eec\u89c1\u8bc6\u89c1\u8bc6\uff01 \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u957f\u773c\u775b\u7684\u97ad\u5b50>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u957f\u773c\u775b\u7684\u97ad\u5b50 \u3000\u3000\u53ea\u9752\u9762\u6c49\u5b50\u5de6\u624b\u8f7b\u5728\u684c\u4e0a\u4e00\u6309\uff0c\u4eba\u5df2\u51cc\u7a7a\u98de\u8d77\uff0c\u53ea\u542c\u547c\u7684\u4e00\u58f0\uff0c\u98ce\u58f0\u6fc0\u8361\uff0c\u53f3\u624b\u91cc\u4e0d\u77e5 \u4f55\u65f6\u5df2\u591a\u4e86\u6761\u4e4c\u9ed1\u7684\u957f\u97ad\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4e00\u6296\uff0c\u957f\u97ad\u5df2\u5e26\u7740\u98ce\u58f0\u5411\u5706\u5708\u91cc\u7684\u4e00\u7fa4\u4eba\u5934\u9876\u4e0a\u5377\u4e86\u8fc7\u53bb\uff0c\u53ea\u542c\u53ee\u53ee\u5f53\u5f53\u4e00\u8fde\u4e32 \u58f0\u97f3\uff0c\u56db\u5341\u591a\u679a\u94dc\u94b1\u4e00\u9f50\u8dcc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u56db\u5341\u51e0\u4eba\u53ef\u8bf4\u6ca1\u6709\u4e00\u4e2a\u4e0d\u662f\u89c1\u591a\u8bc6\u5e7f\u7684\u8001\u6c5f\u6e56\uff0c\u4f46\u80fd\u5c06\u4e00\u6761\u97ad\u5b50\u4f7f\u5f97\u5982\u6b64\u51fa\u795e\u5165\u5316\u7684 \uff0c\u5374\u662f\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u97ad\u5b50\u5230\u4e86\u4ed6\u624b\u4e0a\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u53d8\u6d3b\u4e86\uff0c\u800c\u4e14\u8fd8\u957f\u4e86\u773c\u775b\u3002 \u3000\u3000\u56db\u5341\u51e0\u4eba\u4e92\u76f8\u77a7\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u540c\u65f6\u5c55\u52a8\u8eab\u5f62\uff0c\u7a7f\u5899\u7684\u7a7f\u5899\uff0c\u4e0a\u623f\u7684\u4e0a\u623f\uff0c\u4f46\u89c1\u6ee1\u5929\u4eba\u5f71 \u98de\u821e\uff0c\u5239\u90a3\u95f4\u5c31\u9003\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u90a3\u9ec4\u886b\u8001\u4eba\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u8981\u4e86\u4ed6\u4eec\u7684\u593a\u547d\u91d1\u94b1\uff0c\u96be\u9053\u662f\u51c6\u5907\u66ff\u4ed6\u4eec\u9001\u547d\u4e48\uff1f \u3000\u3000\u72ec\u817f\u4eba\u51b7\u7b11\u9053\uff1a\u6709\u795e\u97ad\u897f\u95e8\u67d4\u7684\u4e00\u6761\u547d\uff0c\u4e5f\u53ef\u62b5\u5f97\u8fc7\u4ed6\u4eec\u56db\u5341\u51e0\u6761\u547d\u4e86\uff01 \u3000\u3000\u4ed6\u94c1\u62d0\u659c\u626c\uff0c\u4e00\u53ea\u811a\u7ad9\u5728\u5730\u4e0a\uff0c\u6574\u4e2a\u4eba\u597d\u50cf\u9489\u5728\u5730\u4e0a\u4f3c\u7684\uff0c\u7a33\u5982\u6cf0\u5c71\u3002 \u3000\u3000\u9ec4\u886b\u8001\u4eba\u53cc\u624b\u4e00\u4f38\u4e00\u7f29\uff0c\u81ea\u957f\u8896\u4e2d\u9000\u51fa\u4e86\u4e00\u5bf9\u5224\u5b98\u7b14\u3002 \u3000\u3000\u6562\u7528\u8fd9\u79cd\u5175\u5668\u7684\u6b66\u529f\u5c31\u4e0d\u4f1a\u5f31\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u8eab\u5f62\u5c55\u52a8\uff0c\u5df2\u5c06\u90a3\u9752\u9762\u6c49\u5b50\u897f\u95e8\u67d4\u56f4\u4f4f\u3002 \u3000\u3000\u53ea\u6709\u90a3\u72ec\u773c\u9ec4\u8863\u4eba\u5374\u9000\u4e86\u51e0\u6b65\uff0c\u53cd\u624b\u62c9\u5f00\u4e86\u8863\u895f\uff0c\u9732\u51fa\u4e86\u524d\u80f8\u7684\u4e24\u6392\u5200\u5e26\uff0c\u5e26\u4e0a\u5bc6\u5bc6\u5730 \u63d2\u7740\u4e03\u4e03\u56db\u5341\u4e5d\u67c4\u6807\u67aa\uff0c\u6709\u957f\u6709\u77ed\uff0c\u957f\u7684\u4e00\u5c3a\u4e09\u5bf8\uff0c\u77ed\u7684\u516d\u5bf8\u4e94\u5206\uff0c\u67aa\u5934\u7684\u7ea2\u7f28\u9c9c\u7ea2\u5982\u8840\uff01 \u3000\u3000\u4e94\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u77ac\u4e5f\u4e0d\u77ac\u5730\u76ef\u5728\u897f\u95e8\u67d4\u624b\u91cc\u7684\u957f\u97ad\u4e0a\uff0c\u663e\u7136\u90fd\u5bf9\u8fd9\u6761\u4f3c\u4e4e\u957f\u7740\u773c\u775b\u7684\u97ad \u5b50\u6709\u4e9b\u6212\u60e7\u4e4b\u5fc3\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9634\u607b\u607b\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u8fd9\u56db\u4f4d\u670b\u53cb\u7684\u6765\u5386\uff0c\u9601\u4e0b\u60f3\u5fc5\u5df2\u770b\u51fa\u6765\u4e86\u5427\u3002 \u3000\u3000\u897f\u95e8\u67d4\u9053\uff1a\u6211\u65e9\u5c31\u770b\u51fa\u6765\u4e86\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u6309\u7406\u8bf4\uff0c\u4ee5\u6211\u4eec\u4e94\u4eba\u7684\u8eab\u4efd\uff0c\u672c\u4e0d\u8be5\u8054\u624b\u5bf9\u4ed8\u4f60\u4e00\u4e2a\uff0c\u53ea\u4e0d\u8fc7\u4eca\u65e5\u7684\u60c5\u51b5\u5374 \u4e0d\u540c\u3002 \u3000\u3000\u897f\u95e8\u67d4\u51b7\u7b11\u9053\uff1a\u6c5f\u6e56\u4e2d\u4ee5\u591a\u4e3a\u80dc\u7684\u5c0f\u4eba\u6211\u4e5f\u89c1\u5f97\u591a\u4e86\uff0c\u53c8\u4e0d\u6b62\u4f60\u4eec\u4e94\u4e2a\u3002 \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u6211\u672c\u4e0d\u60f3\u53d6\u4f60\u6027\u547d\uff0c\u4f46\u4f60\u65e2\u72af\u4e86\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u6211\u4eec\u600e\u80fd\u518d\u653e\u4f60\u8d70\uff0c\u89c4\u77e9\u4e00\u574f \uff0c\u5a01\u4fe1\u65e0\u5b58\uff0c\u8fd9\u9053\u7406\u4f60\u81ea\u7136\u4e5f\u660e\u767d\u3002 \u3000\u3000\u897f\u95e8\u67d4\u9053\uff1b\u6211\u82e5\u4e00\u5b9a\u8981\u8d70\u5462\uff1f \u3000\u3000\u72ec\u817f\u4eba\u9053\uff1a\u4f60\u8d70\u4e0d\u4e86\u7684\uff01 \u3000\u3000\u897f\u95e8\u67d4\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u6211\u82e5\u771f\u8981\u8d70\u65f6\uff0c\u51ed\u4f60\u4eec\u8fd8\u4f11\u60f3\u62e6\u5f97\u4f4f\u6211\uff01 \u3000\u3000\u72ec\u817f\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u94c1\u62d0\u6a2a\u626b\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62d0\u626b\u51fa\uff0c\u867d\u662f\u4e00\u62db\u5e73\u5e73\u5e38\u6a2a\u626b\u5343\u519b\uff0c\u4f46\u529b\u9053\u4e4b\u5f3a\uff0c\u6c14\u52bf\u4e4b\u58ee\uff0c\u5374\u5f53\u771f\u65e0\u53ef\u4f26\u6bd4\uff01 \u3000\u3000\u897f\u95e8\u67d4\u957f\u7b11\u4e0d\u7edd\uff0c\u97ad\u5b50\u65cb\u8f6c\u66f4\u6025\uff0c\u4ed6\u7684\u4eba\u5df2\u7a81\u7136\u51b2\u5929\u98de\u8d77\u3002 \u3000\u3000\u90a3\u72ec\u773c\u5927\u6c49\u53cc\u624b\u9f50\u626c\uff0c\u4e00\u970e\u95f4\u53d1\u51fa\u4e86\u5341\u4e09\u67c4\u6807\u67aa\uff0c\u4f46\u89c1\u7ea2\u7f28\u95ea\u52a8\uff0c\u5e26\u7740\u547c\u5578\u7684\u98ce\u5411\u897f\u95e8 \u67d4\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u957f\u7684\u6807\u67aa\u5148\u53d1\uff0c\u77ed\u7684\u6807\u67aa\u5374\u5148\u81f3\uff0c\u53ea\u542c\u5580\u5693\u3001\u5580\u5693\u8fde\u4e32\u7684\u58f0\u97f3\uff0c\u957f\u957f\u77ed\u77ed\u4e00\u5341\u4e09\u6839\u6807\u67aa \u5168\u90fd\u88ab\u65cb\u8f6c\u7684\u97ad\u5b50\u62d7\u65ad\uff0c\u65ad\u4e86\u7684\u6807\u67aa\u5411\u56db\u9762\u516b\u65b9\u98de\u51fa\uff0c\u6709\u7684\u98de\u5165\u9ad8\u5899\uff0c\u6709\u7684\u9489\u5728\u5899\u4e0a\uff0c\u4f59\u529b \u72b9\u672a\u5c3d\uff0c\u534a\u622a\u67aa\u6746\u4ecd\u5728\u55e1\u55e1\u7684\u5f39\u52a8\u4e0d\u6b47\uff0c\u67aa\u5934\u7684\u7ea2\u7f28\u90fd\u88ab\u6296\u6563\u4e86\uff0c\u4e00\u6839\u6839\u843d\u4e0b\u6765\uff0c\u968f\u98ce\u98de\u821e \u3002 \u3000\u3000\u897f\u95e8\u67d4\u7684\u4eba\u5374\u50cf\u662f\u9635\u9f99\u5377\u98ce\u8d8a\u8f6c\u8d8a\u5feb\u8d8a\u8f6c\u8d8a\u9ad8\uff0c\u518d\u51e0\u8f6c\u4fbf\u8f6c\u5165\u6d53\u96fe\u4e2d\uff0c\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u72ec\u817f\u4eba\u559d\u9053\uff1a\u8ffd\uff01 \u3000\u3000\u4ed6\u94c1\u62d0\u7b03\u7684\u4e00\u70b9\uff0c\u4eba\u4e5f\u51b2\u5929\u98de\u8d77\uff0c\u8fd9\u4e00\u6761\u817f\u7684\u4eba\u7adf\u6bd4\u4e24\u6761\u817f\u7684\u4eba\u8f7b\u529f\u8fd8\u9ad8\u5f97\u591a\uff0c\u970e\u773c\u95f4 \u4e5f\u6d88\u5931\u5728\u6d53\u96fe\u4e2d\u3002 \u3000\u3000\u4f46\u94c1\u62d0\u626b\u52a8\u65f6\u6240\u5e26\u8d77\u7684\u98ce\u58f0\u4ecd\u8fdc\u8fdc\u4f20\u6765\uff0c\u6240\u6709\u7684\u9ec4\u886b\u4eba\u7acb\u523b\u90fd\u8ddf\u7740\u8fd9\u98ce\u58f0\u8ffd\u4e86\u4e0b\u53bb\uff0c\u5df7 \u5802\u91cc\u7acb\u523b\u53c8\u6062\u590d\u4e86\u6614\u65e5\u7684\u5e73\u9759\uff0c\u53ea\u7559\u4e0b\u4e00\u6ee9\u8840\u6cca\u3001\u4e24\u5177\u5c38\u4f53\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u8fd9\u4e24\u5177\u5c38\u8eab\uff0c\u5b59\u9a7c\u5b50\u771f\u4ee5\u4e3a\u8fd9\u53ea\u4e0d\u8fc7\u662f\u573a\u68a6\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8001\u5934\u5b50\u4e0d\u77e5\u4f55\u65f6\u5df2\u6e05\u9192\u4e86\uff0c\u773c\u775b\u91cc\u8fde\u4e00\u70b9\u9152\u610f\u4e5f\u6ca1\u6709\uff0c\u4ed6\u76ee\u9001\u9ec4\u8863\u4eba\u4e00\u4e2a\u4e2a\u8d70\u8fdc \uff0c\u624d\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u96be\u602a\u897f\u95e8\u67d4\u7684\u86c7\u97ad\u6392\u540d\u8fd8\u5728\u9752\u9b54\u624b\u4e4b\u4e0a\uff0c\u770b\u4ed6\u9732\u4e86\u8fd9\u4e24\u624b\uff0c\u5c31\u5df2\u4e0d\u6127\u795e\u97ad \u4e24\u5b57\uff0c\u767e\u6653\u751f\u6bd5\u7adf\u8fd8\u662f\u6709\u773c\u5149\u7684\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u6b66\u6797\u4e2d\u7528\u97ad\u5b50\u7684\u4eba\uff0c\u96be\u9053\u771f\u6ca1\u6709\u4e00\u4e2a\u80fd\u5f3a\u8fc7\u4ed6\u5417\uff1f \u3000\u3000\u8001\u5934\u5b50\u9053\uff1a\u8f6f\u5175\u5203\u80fd\u7ec3\u5230\u4ed6\u8fd9\u79cd\u706b\u4faf\u7684\uff0c\u4e09\u5341\u5e74\u6765\u8fd8\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u90a3\u4e00\u6761\u817f\u7684\u602a\u7269\u5462\uff1f \u3000\u3000\u8001\u5934\u5b50\u9053\uff1a\u90a3\u4eba\u53eb\u8bf8\u845b\u521a\uff0c\u6c5f\u6e56\u4e2d\u4eba\u53c8\u79f0\u4ed6\u6a2a\u626b\u5343\u519b\uff0c\u638c\u4e2d\u4e00\u91d1\u94a2\u94c1\u62d0\u91cd\u516d\u5341\u4e09\u65a4\uff0c\u5929 \u4e0b\u6b66\u6797\u8c6a\u6770\u6240\u4f7f\u7684\u5175\u5668\uff0c\u6ca1\u6709\u4e00\u4e2a\u6bd4\u4f7f\u66f4\u91cd\u7684\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7b11\u9053\uff1a\u4e00\u4e2a\u53eb\u897f\u95e8\u67d4\uff0c\u4e00\u4e2a\u53eb\u8bf8\u845b\u521a\uff0c\u770b\u6765\u4e24\u4eba\u5012\u771f\u662f\u5929\u751f\u7684\u51a4\u5bb6\u5bf9\u5934\u3002 \u3000\u3000\u90a3\u8001\u5934\u5b50\u53d6\u51fa\u952d\u94f6\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u6276\u7740\u4ed6\u5b59\u5973\u513f\u7684\u80a9\u5934\uff0c\u8e52\u8dda\u7740\u8d70\u4e86\u51fa\u53bb\uff0c\u4e5f\u6e10\u6e10\u5730\u6d88\u5931 \u5728\u65e0\u5c3d\u7684\u591c\u96fe\u91cc\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u53c8\u51fa\u4e86\u534a\u5929\u795e\uff0c\u56de\u8fc7\u5934\uff0c\u624d\u53d1\u73b0\u9152\u9b3c\u4e0d\u77e5\u4f55\u65f6\u4e5f\u5df2\u9192\u4e86\uff0c\u800c\u4e14\u5df2 \u8d70\u5230\u795e\u97ad\u897f\u95e8\u67d4\u65b9\u624d\u5750\u8fc7\u7684\u684c\u5b50\u524d\uff0c\u62ff\u8d77\u4e86\u8bf8\u845b\u521a\u65b9\u624d\u7559\u5728\u684c\u4e0a\u7684\u90a3\u5c01\u4fe1\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u7b11\u9053\uff1a\u4f60\u4eca\u5929\u53ef\u771f\u4e0d\u8be5\u559d\u9189\u7684\uff0c\u5e73\u767d\u9519\u8fc7\u4e86\u8bb8\u591a\u573a\u597d\u620f\u3002 \u3000\u3000\u90a3\u9152\u9b3c\u7b11\u4e86\u7b11\uff0c\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u771f\u6b63\u7684\u597d\u620f\u4e5f\u8bb8\u8fd8\u5728\u540e\u5934\u54e9\uff0c\u53ea\u6015\u6211\u60f3\u4e0d\u770b\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u76b1\u4e86\u76b1\u7709\uff0c\u4ed6\u89c9\u5f97\u4eca\u5929\u6bcf\u4eba\u8bf4\u8bdd\u90fd\u597d\u50cf\u6709\u70b9\u9634\u9633\u602a\u6c14\uff0c\u597d\u50cf\u6bcf\u4e2a\u4eba\u5403\u9519\u4e86\u836f\u4f3c\u7684 \u3002 \u3000\u3000\u90a3\u9152\u9b3c\u5df2\u62bd\u51fa\u4e86\u4fe1\uff0c\u53ea\u77a7\u4e86\u4e24\u773c\uff0c\u82cd\u767d\u7684\u8138\u4e0a\u7a81\u7136\u53c8\u6cdb\u8d77\u4e86\u4e00\u9635\u9635\u5f02\u6837\u7684\u7ea2\u6655\uff0c\u5f2f\u4e0b\u8170 \u53bb\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u4fe1\u4e0a\u5199\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u90a3\u9152\u9b3c\u9053\uff1a\u6ca1\u2014\u2014\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u542c\u8bf4\u90a3\u4e9b\u4eba\u5168\u90fd\u662f\u4e3a\u4e86\u8fd9\u5c01\u4eba\u6765\u7684\u3002 \u3000\u3000\u90a3\u9152\u9b3c\u9053\uff1a\u54e6\uff1f \u3000\u3000\u9632\u9a7c\u5b50\u7b11\u9053\uff1a\u4ed6\u4eec\u8fd8\u8bf4\u8fd9\u91cc\u6709\u4ec0\u4e48\u5b9d\u85cf\uff0c\u90a3\u624d\u771f\u662f\u6d3b\u89c1\u9b3c\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u62b9\u7740\u684c\u5b50\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u4f60\u60f3\u4e0d\u60f3\u559d\u9152\uff1f\u4eca\u5929\u6211\u8bf7\u4f60\u3002 \u3000\u3000\u4ed6\u542c\u4e0d\u5230\u56de\u7b54\uff0c\u8f6c\u8fc7\u5934\uff0c\u53ea\u89c1\u90a3\u9152\u9b3c\u6b63\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u51fa\u795e\u5730\u9065\u671b\u7740\u8fdc\u65b9\uff0c\u4e5f\u4e0d\u77e5\u5728 \u77a7\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u76ee\u4e2d\u867d\u4e5f\u6ca1\u6709\u9189\u610f\uff0c\u5374\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u51c4\u51c9\u8427\u7d22\u4e4b\u610f\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u987a\u7740\u4ed6\u7684\u76ee\u5149\u671b\u4e86\u8fc7\u53bb\uff0c\u5c31\u770b\u5230\u4e86\u9ad8\u5899\u5185\u5c0f\u697c\u4e0a\u7684\u90a3\u4e00\u70b9\u5b64\u706f\uff0c\u5728\u6d53\u96fe\u4e2d\u770b\u6765\uff0c \u8fd9\u4e00\u5b64\u706f\u4eff\u4f5b\u66f4\u9065\u8fdc\u4e86\u2014\u2014\uff0d \u3000\u3000\u5b59\u9a7c\u5b50\u56de\u5230\u540e\u9662\u7684\u65f6\u5019\uff0c\u4e09\u66f4\u65e9\u5df2\u8fc7\u4e86\u3002 \u3000\u3000\u9662\u5b50\u91cc\u6c38\u8fdc\u662f\u90a3\u4e48\u9759\u5bc2\uff0c\u90a3\u9152\u9b3c\u5c4b\u5b50\u91cc\u706f\u5149\u8fd8\u5728\u4eae\u7740\uff0c\u95e8\u5374\u6ca1\u6709\u5173\u8d77\uff0c\u88ab\u98ce\u4e00\u5439\uff0c\u5431\u5431 \u5730\u53d1\u54cd\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u60f3\u8d77\u5730\u5929\u665a\u4e0a\u7684\u4e8b\uff0c\u7acb\u523b\u5c31\u8d70\u4e86\u8fc7\u53bb\uff0c\u6572\u7740\u95e8\u9053\uff1a\u4f60\u7761\u4e86\u4e48\uff1f\u4e3a\u4f55\u6ca1\u5173\u95e8\uff1f \u3000\u3000\u5c4b\u5b50\u91cc\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5c06\u95e8\u8f7b\u8f7b\u63a8\u5f00\u4e00\u7ebf\uff0c\u63a2\u5934\u8fdb\u53bb\uff0c\u53ea\u5e8a\u4e0a\u7684\u88ab\u5b50\u53e0\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u7761\u8fc7 \u3002 \u3000\u3000\u90a3\u9152\u9b3c\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e09\u66f4\u534a\u591c\u7684\uff0c\u4ed6\u4f1a\u8dd1\u5230\u54ea\u91cc\u53bb\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u76b1\u4e86\u76b1\u7709\uff0c\u63a8\u95e8\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5f88\u51cc\u4e71\uff0c\u5e8a\u4e0a\u5806\u7740\u5341\u4e03\u516b\u5757\u6728\u5934\uff0c\u4f46\u5374\u77a7\u4e0d\u89c1\u90a3\u628a\u523b\u6728\u5934\u7684\u5c0f\u5200\uff0c\u684c\u5b50\u4e0a\u8fd8\u6709\u559d \u5269\u4e0b\u7684\u534a\u58f6\u9152\u3002 \u3000\u3000\u9152\u58f6\u65c1\u6709\u4e00\u56e2\u63c9\u7ec9\u4e86\u7684\u7eb8\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u8ba4\u5f97\u8fd9\u5f20\u7eb8\u6b63\u662f\u8bf8\u845b\u521a\u7559\u4e0b\u6765\u7684\u90a3\u5c01\u4fe1\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u7528\u624b\u5c06\u4fe1\u7eb8\u644a\u5e73\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\uff1a\u4e5d\u6708\u5341\u4e94\u65e5\uff0c\u5174\u4e91\u5e84\u6709\u91cd\u5b9d\u5c06\u73b0\uff0c\u76fc\u9601\u4e0b\u52ff \u5931\u4e4b\u4ea4\u81c2\u3002 \u3000\u3000\u5c31\u53ea\u8fd9\u77ed\u751c\u7f8e\u7684\u4e09\u53e5\u8bdd\uff0c\u4e0b\u9762\u4e5f\u6ca1\u6709\u7f72\u540d\uff0c\u4f46\u4fe1\u4e0a\u8bf4\u7684\u8d8a\u5c11\uff0c\u53cd\u800c\u8d8a\u80fd\u5f15\u8d77\u522b\u4eba\u7684\u597d\u5947 \u4e4b\u5fc3\u3002 \u3000\u3000\u5199\u4fe1\u7684\u8fd9\u4eba\uff0c\u5b9e\u5728\u5f88\u61c2\u5f97\u4eba\u7684\u5fc3\u7406\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u76b1\u8d77\u4e86\u7709\uff0c\u9762\u4e0a\u4e5f\u9732\u51fa\u4e00\u79cd\u5947\u5f02\u7684\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5174\u4e91\u5e84\u5c31\u662f\u4ed6\u5c0f\u5e97\u5bf9\u9762\u90a3\u5de8\u5927\u7684\u5b85\u7b2c\uff0c\u4f46\u5374\u518d\u4e5f\u60f3\u4e0d\u51fa\u90a3\u9152\u9b3c\u4f1a\u548c\u5174\u4e91\u5e84\u6709\u4ec0\u4e48 \u8054\u7cfb\uff01 \u7b2c\u4e09\u5341\u7ae0\u3000\u6f2b\u6f2b\u7684\u957f\u591c>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u7ae0\u3000\u6f2b\u6f2b\u7684\u957f\u591c \u3000\u3000\u591c\u96fe\u51c4\u8ff7\uff0c\u6728\u53f6\u51cb\u96f6\uff0c\u8377\u5858\u5185\u843d\u6ee1\u4e86\u67af\u53f6\uff0c\u5c0f\u8def\u4e0a\u8352\u8349\u6ca1\u5f84\uff0c\u6614\u65e5\u82b1\u7ea2\u67f3\u7eff\u3001\u6885\u9999\u83ca\u51b7 \u7684\u5ead\u9662\uff0c\u5982\u4eca\u7adf\u5145\u6ee1\u4e86\u68ee\u68ee\u9b3c\u6c14\u3002 \u3000\u3000\u5c0f\u6865\u7684\u5c3d\u5934\uff0c\u6709\u4e09\u4e94\u7cbe\u820d\uff0c\u6b63\u662f\u51b7\u9999\u5c0f\u7b51\u3002 \u3000\u3000\u5728\u8fd9\u91cc\u4f4f\u8fc7\u7684\u6709\u6b66\u6797\u4e2d\u7b2c\u4e00\u4f4d\u540d\u4fa0\uff0c\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u4f4d\u7075\u4eba\uff0c\u6614\u65e5\u6b64\u65f6\uff0c\u6885\u82b1\u5df2\u5c06\u5410\u8273\uff0c\u9999 \u6c14\u9189\u6c81\u4eba\u5fc3\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5899\u89d2\u7ed3\u7740\u86db\u7f51\uff0c\u7a97\u53f0\u79ef\u7740\u7070\u5c18\uff0c\u65e9\u5df2\u4e0d\u590d\u518d\u73b0\u6614\u65e5\u7684\u98ce\u6d41\u9057\u8ff9\uff0c\u8fde\u4e0d\u8001\u7684\u6885\u6811 \u90fd\u5df2\u67af\u840e\u3002 \u3000\u3000\u6f2b\u6f2b\u957f\u591c\u5df2\u5c06\u5c3d\uff0c\u6d53\u96fe\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5934\u53d1\u84ec\u4e71\uff0c\u8863\u886b\u4e0d\u6574\uff0c\u770b\u6765\u662f\u90a3\u4e48\u843d\u62d3\u3001\u6194\u7fe0\uff0c\u4f46\u4ed6\u7684\u795e\u91c7\u770b\u6765\u5374\u4ecd\u7136\u662f\u90a3\u4e48\u6f47 \u6d12\uff0c\u76ee\u5149\u4e5f\u4eae\u5f97\u50cf\u662f\u79cb\u591c\u7684\u5bd2\u661f\u3002 \u3000\u3000\u4ed6\u8427\u7136\u8d70\u8fc7\u5c0f\u6865\uff0c\u770b\u5230\u67af\u840e\u7684\u6885\u6811\uff0c\u4ed6\u4e0d\u7981\u53d1\u51fa\u4e86\u6df1\u957f\u7684\u53f9\u606f\uff0c\u6885\u82b1\u672c\u4e5f\u662f\u4ed6\u6614\u65e5\u7684\u826f \u4f34\uff0c\u4eca\u65e5\u5374\u548c\u4eba\u540c\u6837\u6194\u60b4\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7684\u4eba\u5ffd\u7136\u5982\u71d5\u5b50\u822c\u98de\u8d77\uff01 \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u7a97\u5b50\u662f\u5173\u7740\u7684\u3002 \u3000\u3000\u7a97\u68c2\u4e0a\u767e\u6761\u88c2\u75d5\uff0c\u4ece\u8fd9\u88c2\u75d5\u4e2d\u671b\u8fdb\u53bb\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u90a3\u5b64\u96f6\u5bc2\u5bde\u7684\u4eba\uff0c\u6b63\u9762\u5bf9\u7740\u5b64\u706f\uff0c\u5728 \u7f1d\u7740\u8863\u670d\u3002 \u3000\u3000\u5979\u7684\u8138\u8272\u82cd\u767d\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u4e5f\u5df2\u63a8\u52a8\u4e86\u6614\u65e5\u7684\u5149\u91c7\u3002 \u3000\u3000\u5979\u5168\u4e0a\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u770b\u6765\u662f\u90a3\u4e48\u51b7\u6de1\uff0c\u4f3c\u4e4e\u65e9\u5df2\u5fd8\u5374\u4e86\u4eba\u95f4\u7684\u6b22\u4e50\uff0c\u4e5f\u5df2\u5fd8\u5374\u4e86\u7ea2\u5c18\u7684 \u6101\u82e6\u3002 \u3000\u3000\u5979\u53ea\u662f\u5750\u5728\u90a3\u91cc\uff0c\u4e00\u9488\u9488\u5730\u7f1d\u5173\uff0c\u8ba9\u9752\u6625\u5728\u9488\u5c16\u6e9c\u8d70\u3002 \u3000\u3000\u8863\u670d\u4e0a\u7684\u7834\u6d1e\u53ef\u4ee5\u7f1d\u8865\uff0c\u4f46\u5fc3\u7075\u4e0a\u7684\u521b\u4f24\u5374\u662f\u8c01\u4e5f\u7f1d\u8865\u4e0d\u4e86\u7684\u2014\u2014 \u3000\u3000\u5750\u5728\u597d\u5bf9\u9762\u7684\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u957f\u5f97\u5f88\u6e05\u79c0\uff0c\u4e00\u53cc\u7075\u6d3b\u7684\u773c\u775b\u4f7f\u4ed6\u770b\u6765\u66f4\u806a\u660e\uff0c\u4ed6\u7684\u8138\u8272\u4e5f\u90a3\u4e48\u82cd\u767d\uff0c\u82cd\u767d\u5f97\u4f7f\u4eba\u5fd8 \u4e86\u4ed6\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u6b63\u5782\u7740\u5934\uff0c\u5728\u4e00\u7b14\u7b14\u5730\u7ec3\u5b57\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u5df2\u5b66\u4f1a\u4e86\u5fcd\u8010\u5bc2\u5bde\u3002 \u3000\u3000\u90a3\u843d\u62d3\u7684\u4eba\u5e7d\u7075\u822c\u4f0f\u5728\u7a97\u5916\uff0c\u9759\u9759\u5730\u77a7\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u4ed6\u773c\u6709\u5df2\u73b0\u51fa\u4e86\u6cea\u75d5\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u90a3\u5b69\u5b50\u5ffd\u7136\u505c\u4e0b\u4e86\u7b14\uff0c\u62ac\u8d77\u4e86\u5934\uff0c\u671b\u7740\u684c\u4e0a\u95ea\u52a8\u7684\u706b\u7130\uff0c\u75f4\u75f4\u5730\u51fa\u4e86 \u795e\u3002 \u3000\u3000\u90a3\u5987\u4eba\u4e5f\u505c\u4e0b\u9488\u7ebf\uff0c\u770b\u5230\u4e86\u5979\u7684\u5b69\u5b50\uff0c\u5979\u76ee\u4e2d\u5c31\u6d41\u9732\u51fa\u8bf4\u4e0d\u5c3d\u7684\u6e29\u67d4\uff0c\u8f7b\u8f7b\u9053\uff1a\u5c0f\u4e91\uff0c \u4f60\u5728\u60f3\u4ec0\u4e48\uff1f \u3000\u3000\u5b69\u5b50\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u6211\u6b63\u5728\u60f3\uff0c\u7239\u7239\u4e0d\u77e5\u5728\u5230\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u56de\u6765\u3002 \u3000\u3000\u5987\u4eba\u7684\u624b\u4e00\u9635\u98a4\u6296\uff0c\u9488\u5c16\u624e\u5728\u5979\u81ea\u5df1\u7684\u624b\u6307\u4e0a\uff0c\u4f46\u5374\u4f3c\u4e4e\u4e00\u672a\u611f\u89c9\u5230\u75db\u82e6\uff0c\u5979\u7684\u75db\u82e6\u5728 \u5fc3\u91cc\u3002\u90a3\u5b69\u5b50\u9053\uff1a\u5988\uff0c\u7239\u7239\u4e3a\u4ec0\u4e48\u7a81\u7136\u8d70\u4e86\u5462\uff1f\u5230\u73b0\u5728\u5df2\u4e24\u5e74\u4e86\uff0c\u8fde\u97f3\u8baf\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5987\u4eba\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u7a81\u7136\u9732\u51fa\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u72e1\u9ee0\u4e4b\u8272\uff0c\u9053\uff1a\u4f46\u6211\u5374\u77e5\u9053\u4ed6\u662f\u4e3a\u4ec0\u4e48\u8d70\u7684\u3002 \u3000\u3000\u5987\u4eba\u8f7b\u8f7b\u9053\uff1a\u4f60\u5c0f\u5c0f\u7684\u5b69\u5b50\uff0c\u77e5\u9053\u4ec0\u4e48\uff1f \u3000\u3000\u90a3\u5b69\u5b50\u9053\uff1a\u6211\u5f53\u7136\u77e5\u9053\uff0c\u7239\u7239\u662f\u4e3a\u4e86\u6015\u674e\u5bfb\u6b22\u56de\u6765\u627e\u4ed6\u62a5\u4ec7\u624d\u8d70\u7684\uff0c\u4ed6\u53ea\u8981\u4e00\u542c\u5230\u674e\u5bfb \u6b22\u8fd9\u540d\u5b57\uff0c\u8138\u8272\u5c31\u7acb\u523b\u6539\u53d8\u4e86\u3002 \u3000\u3000\u5987\u4eba\u60f3\u8bf4\u8bdd\uff0c\u5230\u540e\u6765\u6240\u6709\u7684\u8bdd\u90fd\u53d8\u505a\u4e86\u4e00\u58f0\u957f\u957f\u7684\u53f9\u606f\u3002 \u3000\u3000\u5979\u4e5f\u77e5\u9053\u5b69\u5b50\u61c2\u5f97\u5f88\u591a\uff0c\u4e5f\u8bb8\u592a\u591a\u4e86\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u53c8\u9053\uff1a\u4f46\u674e\u5bfb\u6b22\u5374\u59cb\u7ec8\u6ca1\u6709\u6765\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6765\u770b\u770b\u5988\u5462\uff1f \u3000\u3000\u5987\u4eba\u7684\u8eab\u5b50\u4f3c\u53c8\u8d77\u4e00\u9635\u98a4\u6296\uff0c\u5927\u58f0\u9053\uff1a\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6765\u770b\u6211\uff1f \u3000\u3000\u5c0f\u5b69\u7b11\u9053\uff1a\u6211\u77e5\u9053\u4ed6\u4e00\u76f4\u662f\u5988\u7684\u597d\u670b\u53cb\uff0c\u4e0d\u662f\u5417\uff1f \u3000\u3000\u5987\u4eba\u7684\u8138\u8272\u66f4\u82cd\u767d\uff0c\u5ffd\u7136\u677f\u7740\u8138\u9053\uff1a\u5929\u5df2\u5feb\u4eae\u4e86\uff0c\u8fd8\u4e0d\u53bb\u7761\uff1f \u3000\u3000\u5b69\u5b50\u7728\u4e86\u7728\u773c\u775b\uff0c\u9053\uff1a\u6211\u4e0d\u7761\uff0c\u662f\u4e3a\u4e86\u966a\u5988\u7684\uff0c\u56e0\u5988\u8fd9\u4e24\u5e74\u6765\u665a\u4e0a\u603b\u662f\u7761\u4e0d\u7740\uff0c\u8fde\u5b69\u513f \u6211\u770b\u4e86\u5fc3\u91cc\u90fd\u96be\u53d7\u5f97\u5f88\u3002 \u3000\u3000\u5987\u4eba\u7f13\u7f13\u5730\u9616\u8d77\u773c\u775b\uff0c\u4e00\u8fde\u4e32\u773c\u6cea\u6d41\u4e0b\u9762\u988a\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u7ad9\u8d77\u6765\u7b11\u9053\uff1a\u4f46\u6211\u4e5f\u8be5\u53bb\u7761\u4e86\uff0c\u660e\u5929\u5c31\u662f\u5988\u7684\u751f\u65e5\uff0c\u6211\u5f97\u65e9\u4e9b\u8d77\u6765\u2014\u2014 \u3000\u3000\u4ed6\u7b11\u7740\u8d70\u8fc7\uff0c\u5728\u90a3\u5987\u4eba\u7684\u9762\u988a\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u9053\uff1a\u5988\u4e5f\u8be5\u7761\u4e86\uff0c\u660e\u5929\u89c1\u3002 \u3000\u3000\u4ed6\u7b11\u7740\u8d70\u4e86\u51fa\u53bb\uff0c\u4e00\u5230\u95e8\u5916\uff0c\u7b11\u5bb9\u5c31\u7acb\u523b\u77a7\u4e0d\u89c1\u4e86\uff0c\u76ee\u4e2d\u9732\u51fa\u4e00\u79cd\u6028\u6bd2\u4e4b\u8272\uff0c\u9053\uff1a\u674e\u5bfb \u6b22\uff0c\u522b\u4eba\u90fd\u6015\u4f60\uff0c\u6211\u4e0d\u6015\u4f60\uff0c\u603b\u6709\u4e00\u5929\uff0c\u6211\u8981\u4f60\u6b7b\u5728\u6211\u624b\u4e0a\u7684\u3002 \u3000\u3000\u5987\u4eba\u76ee\u9001\u7740\u5b69\u5b50\u8d70\u51fa\u95e8\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u75db\u82e6\uff0c\u4e5f\u5145\u6ee1\u4e86\u601c\u60dc\uff0c\u8fd9\u5b9e\u5728\u662f\u4e2a\u806a\u660e\u7684\u5b69\u5b50\u3002 \u3000\u3000\u5979\u53ea\u6709\u8fd9\u4e48\u4e00\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u662f\u5979\u7684\u547d\uff0c\u4ed6\u5c31\u771f\u505a\u4e86\u4ec0\u4e48\u4ee4\u5979\u4f24\u5fc3\u7684\u4e8b\uff0c\u771f\u8bf4\u4e86\u4ec0\u4e48\u4ee4\u5979\u4f24\u5fc3\u7684\u8bdd\uff0c\u5979\u90fd\u8fd8\u662f \u540c\u6837\u5730\u75bc\u7231\u4ed6\u3002 \u3000\u3000\u6bcd\u4eb2\u5bf9\u5b69\u5b50\u7684\u7231\uff0c\u662f\u6c38\u65e0\u6b62\u5883\uff0c\u6c38\u65e0\u6761\u4ef6\u7684\u3002 \u3000\u3000\u5979\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u5c06\u706f\u706b\u6311\u5f97\u66f4\u4eae\u4e86\u4e9b\u3002 \u3000\u3000\u6bcf\u5929\u591c\u8272\u964d\u4e34\u7684\u65f6\u5019\uff0c\u5979\u7684\u5fc3\u91cc\u5c31\u4f1a\u751f\u51fa\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u754f\u60e7\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5979\u542c\u5230\u7a97\u5916\u4f20\u6765\u4e86\u4e00\u9635\u8f7b\u8f7b\u7684\u54b3\u55fd\u58f0\u3002 \u3000\u3000\u5979\u8138\u8272\u7acb\u523b\u53d8\u4e86\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u4f3c\u4e4e\u5df2\u82e5\u7136\u50f5\u6728\uff0c\u5446\u5446\u5730\u5750\u5728\u90a3\u91cc\uff0c\u75f4\u75f4\u5730\u671b\u7740\u90a3\u7a97\u5b50\uff0c\u76ee\u4e2d\u4f3c\u4e4e\u5e26\u7740\u4e9b\u6b23\u559c \uff0c\u53c8\u4f3c\u4e4e\u5e26\u7740\u4e9b\u6050\u60e7\u2014\u2014 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u624d\u6162\u6162\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u53e3\uff0c\u7528\u4e00\u53ea\u6b63\u5728\u98a4\u6296\u7684\u624b\uff0c\u6162\u6162\u5730\u63a8\u5f00 \u7a97\u6237\uff0c\u98a4\u58f0\u9053\uff1a\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u56db\u4e0b\u54ea\u6709\u4ec0\u4e48\u4eba\u5f71\u3002 \u3000\u3000\u90a3\u5987\u4eba\u76ee\u5149\u8292\u7136\u56db\u4e0b\u641c\u7d22\u7740\uff0c\u51c4\u7136\uff1a\u6211\u77e5\u9053\u4f60\u6765\u4e86\uff0c\u4f60\u65e2\u7136\u6765\u4e86\uff0c\u4e3a\u53ef\u4e0d\u51fa\u6765\u548c\u6211\u76f8\u89c1 \u5462\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u58f0\uff0c\u4e5f\u6ca1\u6709\u56de\u5e94\u3002 \u3000\u3000\u90a3\u5987\u4eba\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9eef\u7136\uff1b\u4f60\u4e0d\u613f\u548c\u6211\u76f8\u89c1\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\uff0c\u6211\u4eec\u7684\u786e\u5bf9\u4e0d\u8d77\u4f60\uff0c\u5bf9\u4e0d \u8d77\u4f60\u2014\u2014 \u3000\u3000\u5979\u58f0\u97f3\u8d8a\u6765\u8d8a\u8f7b\uff0c\u53c8\u5446\u5446\u7684\u7acb\u4e86\u826f\u4e45\uff0c\u624d\u7f13\u7f13\u5173\u8d77\u7a97\u5b50\u3002 \u3000\u3000\u5927\u5730\u4f3c\u5df2\u5b8c\u5168\u88ab\u9ed1\u6697\u6240\u541e\u6ca1\u3002 \u3000\u3000\u9ece\u660e\u524d\u7684\u4e00\u6bb5\u65f6\u5019\uff0c\u6c38\u8fdc\u662f\u6700\u9ed1\u6697\u7684\u3002 \u3000\u3000\u4f46\u9ed1\u6697\u6bd5\u7adf\u4e5f\u6709\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u4e1c\u65b9\u7ec8\u4e8e\u73b0\u51fa\u4e86\u4e00\u4e1d\u66d9\u5149\u3002\u5c0f\u697c\u524d\u7684\u68a7\u6850\u6811\u540e\uff0c\u6e10\u6e10\u73b0\u51fa \u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u5c31\u8fd9\u4eb2\u621a\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4e5f\u4e0d\u77e5\u5df2\u7ad9\u4e86\u591a\u4e45\uff0c\u4ed6\u7684\u5934\u53d1\u3001\u8863\u670d\uff0c\u51e0\u4e4e\u90fd\u5df2\u88ab\u9732 \u6c34\u6e7f\u900f\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u59cb\u7ec8\u75f4\u671b\u7740\u90a3\u5c0f\u697c\u4e0a\u7684\u7a97\u6237\uff0c\u4eff\u4f5b\u4ece\u672a\u79fb\u52a8\u8fc7\uff0c\u4ed6\u770b\u6765\u662f\u90a3\u4e48\u82cd\u8001\u3001\u75b2\u5026\u3001\u6194\u60b4 \u2014\u2014 \u3000\u3000\u4ed6\u6b63\u662f\u6628\u591c\u90a3\u5b9b\u5982\u5e7d\u7075\u822c\u767d\u96fe\u4e2d\u51fa\u73b0\u7684\u4eba\uff0c\u4e5f\u6b63\u662f\u90a3\u5728\u5b59\u9a7c\u5b50\u5c0f\u5e97\u7ec8\u65e5\u6c89\u9189\u4e0d\u9192\u7684\u9152\u9b3c \uff01 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ef\u662f\u5fc3\u91cc\u5374\u5728\u547c\u5524\uff1a \u3000\u3000\u8bd7\u97f3\uff0c\u8bd7\u97f3\uff0c\u4f60\u5e76\u6ca1\u6709\u5bf9\u4e0d\u8d77\u6211\uff0c\u662f\u6211\u5bf9\u4e0d\u8d77\u4f60\u2014\u2014 \u3000\u3000\u6211\u867d\u4e0d\u80fd\u89c1\u4f60\u7684\u9762\uff0c\u53ef\u662f\u8fd9\u4e24\u5e74\u6765\uff0c\u6211\u65e5\u65e5\u591c\u591c\u90fd\u5728\u4f60\u9644\u8fd1\uff0c\u4fdd\u62a4\u7740\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u5417\uff1f \u3000\u3000\u4e00\u7ebf\u9a84\u9633\u5212\u7834\u4e86\u6668\u96fe\uff0c\u5929\u8272\u66f4\u4eae\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u4ee5\u624b\u63a9\u7740\u5634\uff0c\u52c9\u5f3a\u5fcd\u4f4f\u54b3\u55fd\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u7f13\u7f13\u8d70\u5230\u90a3\u95e8\u623f\u5c0f\u5c4b\u524d\u3002 \u3000\u3000\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u4ed6\u8f7b\u8f7b\u63a8\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u63a8\u5f00\u95e8\uff0c\u7acb\u523b\u5c31\u6709\u4e00\u80a1\u5ec9\u4ef7\u7684\u52a3\u9152\u6c14\u6251\u9f3b\u800c\u6765\uff0c\u5c4b\u91cc\u53c8\u810f\u53c8\u4e71\uff0c\u4e00\u4e2a\u4eba\u4f0f\u5728\u684c\u4e0a\uff0c\u624b \u91cc\u8fd8\u7d27\u7d27\u5730\u6293\u7740\u4e2a\u9152\u74f6\u3002 \u3000\u3000\u53c8\u662f\u4e2a\u9152\u9b3c\u3002 \u3000\u3000\u4ed6\u81ea\u5632\u5730\u7b11\u4e86\u7b14\uff0c\u5f00\u59cb\u6572\u95e8\u3002 \u3000\u3000\u4f0f\u5728\u684c\u4e0a\u7684\u4eba\u7ec8\u4e8e\u9192\u4e86\uff0c\u62ac\u8d77\u5934\uff0c\u624d\u770b\u51fa\u6ee1\u9762\u90fd\u662f\u9ebb\u5b50\uff0c\u6ee1\u9762\u90fd\u662f\u88ab\u52a3\u9152\u4fb5\u8680\u7684\u76b1\u7eb9\uff0c \u987b\u53d1\u5df2\u767d\u4e86\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u4f1a\u60f3\u5230\u4ed6\u5c31\u662f\u6b66\u6797\u7b2c\u4e00\u7f8e\u4eba\u6797\u4ed9\u513f\u7684\u4eb2\u751f\u7236\u4eb2\u3002 \u3000\u3000\u4ed6\u9189\u773c\u60fa\u5fea\u7684\u56db\u9762\u77a7\u7740\uff0c\u5583\u5583\u9053\uff1a\u5927\u6e05\u65e9\u5c31\u6709\u4eba\u6765\u6572\u95e8\uff0c\u649e\u89c1\u9b3c\u4e86\u4e48\uff1f \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u624d\u771f\u7684\u89c1\u5230\u90a3\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\uff0c\u76b1\u7709\u9053\uff1a\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u600e\u4e48\u8dd1\u5230\u8fd9\u91cc\u6765 \u4e86\uff1f\u4f60\u600e\u4e48\u6765\u7684\uff1f \u3000\u3000\u4ed6\u55d3\u5b50\u8d8a\u6765\u8d8a\u5927\uff0c\u4f3c\u53c8\u6062\u590d\u4e86\u51e0\u5206\u5927\u7ba1\u5bb6\u7684\u6c14\u6d3e\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u7b11\u9053\uff1a\u4e24\u5e74\u524d\u6211\u4eec\u89c1\u8fc7\u9762\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f \u3000\u3000\u9ebb\u5b50\u770b\u4e86\u4ed6\u51e0\u773c\uff0c\u60ca\u559c\u9053\uff1a\u539f\u6765\u662f\u674e\u2014\u2014 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u4e0d\u7b49\u4ed6\u8dea\u4e0b\uff0c\u5df2\u6276\u4f4f\u4e86\u4ed6\uff0c\u5fae\u7b11\u7740\u7f13\u7f13\u9053\uff1a\u4f60\u8fd8\u8ba4\u5f97\u6211\u5c31\u597d\uff0c\u6211\u4eec\u5750\u4e0b\u6765 \u8bf4\u8bdd\u3002 \u3000\u3000\u9ebb\u5b50\u966a\u7740\u7b11\u9053\uff1a\u5c0f\u4eba\u600e\u4f1a\u4e0d\u8ba4\u5f97\u5927\u7237\u4f60\u5462\uff1f\u4e0a\u6b21\u5c0f\u4eba\u6709\u773c\u65e0\u73e0\uff0c\u8fd9\u6b21\u518d\u4e5f\u4e0d\u4f1a\u4e86\uff0c\u53ea\u4e0d \u8fc7\uff0c\u5927\u7237\u4f60\u8fd9\u4e24\u5e74\u6765\u7684\u786e\u8001\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u611f\u53f9\u9053\uff1a\u4f60\u4e5f\u8001\u4e86\uff0c\u5927\u5bb6\u90fd\u8001\u4e86\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u4f60\u4eec\u65e5\u5b50\u8fc7\u5f97\u8fd8 \u597d\u4e48\uff1f \u3000\u3000\u9ebb\u5b50\u53f9\u9053\uff1a\u5728\u522b\u4eba\u9762\u524d\uff0c\u6211\u4e5f\u8bb8\u8fd8\u4f1a\u5439\u725b\uff0c\u4f46\u5728\u5927\u7237\u9762\u524d\u2014\u2014 \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u4e0d\u7792\u5927\u7237\uff0c\u8fd9\u4e24\u5e74\u7684\u65e5\u5b50\uff0c\u8fde\u6211\u90fd\u4e0d\u77e5\u600e\u4e48\u6df7\u8fc7\u53bb\u7684\uff0c\u4eca\u5929 \u5356\u5e45\u5b57\u753b\uff0c\u660e\u5929\u5356\u5f20\u6905\u5b50\u6765\u5ea6\u65e5\uff0c\u5509\u2014\u2014 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u76b1\u7709\u9053\uff1a\u5bb6\u91cc\u96be\u9053\u8fde\u65e5\u5b50\u90fd\u8fc7\u4e0d\u4e0b\u53bb\u4e86\uff1f \u3000\u3000\u9ebb\u5b50\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u9053\uff1a\u9f99\u56db\u7237\u8d70\u7684\u65f6\u5019\uff0c\u96be\u9053\u6ca1\u6709\u7559\u4e0b\u5b89\u5bb6\u7684\u8d39\u7528\u3002 \u3000\u3000\u9ebb\u5b50\u6447\u4e86\u6447\u5934\uff0c\u773c\u775b\u90fd\u7ea2\u4e86\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u8138\u8272\u66f4\u82cd\u767d\uff0c\u53c8\u4e0d\u4f4f\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u9ebb\u5b50\u9053\uff1a\u592b\u4eba\u81ea\u5df1\u672c\u8fd8\u6709\u4e9b\u9996\u9970\uff0c\u4f46\u5979\u7684\u5fc3\u80a0\u5b9e\u5728\u592a\u597d\u4e86\uff0c\u90fd\u5206\u7ed9\u4e86\u4e0b\u4eba\u4eec\uff0c\u53eb\u4ed6\u4eec\u53d8 \u5356\u4e86\u505a\u4e9b\u5c0f\u751f\u610f\u53bb\u8c0b\u751f\u2014\u2014\u5979\u5b81\u53ef\u81ea\u5df1\u53d7\u82e6\uff0c\u4e5f\u4e0d\u613f\u4e8f\u5f85\u4e86\u522b\u4eba\u3002 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u8bed\u58f0\u5df2\u6709\u4e9b\u54fd\u54bd\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u611f\u53f9\u9053\uff1a\u4f46\u4f60\u5374\u6ca1\u6709\u8d70\uff0c\u5b9e\u5728\u662f\u4e2a\u5f88\u5fe0\u5fc3\u7684\u4eba\u3002 \u3000\u3000\u9ebb\u5b50\u7b11\u4e86\uff0c\u5450\u5450\u9053\uff1a\u5c0f\u4eba\u53ea\u4e0d\u8fc7\u662f\u65e0\u5904\u53ef\u53bb\u7f62\u4e86\u2014\u2014 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u67d4\u58f0\u9053\uff1a\u4f60\u4e5f\u7528\u4e0d\u81ea\u8c26\uff0c\u6211\u5f88\u4e86\u89e3\u6709\u4e9b\u4eba\u7684\u813e\u6c14\u867d\u7136\u4e0d\u597d\uff0c\u5fc3\u5374\u662f\u5f88\u597d\u7684 \uff0c\u53ea\u53ef\u60dc\u5f88\u5c11\u6709\u4eba\u4e86\u89e3\u4ed6\u4eec\u800c\u5df2\u3002 \u3000\u3000\u9ebb\u5b50\u7684\u773c\u775b\u4f3c\u53c8\u7ea2\u4e86\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u8fd9\u9152\u4e0d\u597d\uff0c\u5927\u4eba\u82e5\u4e0d\u5acc\u5f03\uff0c\u5c06\u5c31\u7740\u559d\u4e24\u676f\u5427\u3002 \u3000\u3000\u4ed6\u6bb7\u52e4\u5730\u5012\u9152\uff0c\u624d\u53d1\u73b0\u9152\u74f6\u5df2\u7a7a\u4e86\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u5c55\u989c\u7b11\u9053\uff1b\u6211\u5012\u4e0d\u60f3\u559d\u9152\uff0c\u53ea\u60f3\u559d\u676f\u8336\u2014\u2014\u4f60\u8bf4\u5947\u4e0d\u5947\u602a\uff0c\u6211\u4e5f\u5c45\u7136\u60f3\u559d \u8336\u4e86\uff0c\u8bb8\u591a\u5e74\u6765\uff0c\u8fd9\u5012\u7834\u9898\u513f\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u9ebb\u5b50\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u8fd9\u5bb9\u6613\uff0c\u6211\u8fd9\u53bb\u66ff\u5927\u7237\u70e7\u58f6\u6c34\uff0c\u597d\u597d\u5730\u6c8f\u58f6\u8336\u6765\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u9053\uff1a\u4f60\u65e0\u8bba\u9047\u7740\u8c01\uff0c\u5343\u4e07\u90fd\u83ab\u8981\u63d0\u8d77\u6211\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u9ebb\u5b50\u7b11\u9053\uff1a\u5927\u7237\u4f60\u653e\u5fc3\uff0c\u5c0f\u4eba\u73b0\u5728\u65e9\u5df2\u4e0d\u6562\u518d\u591a\u5634\u4e86\u3002 \u3000\u3000\u4ed6\u5174\u51b2\u51b2\u5730\u8d70\u4e86\u51fa\u53bb\uff0c\u5c45\u7136\u8fd8\u672a\u5fd8\u8bb0\u63a9\u95e8\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u795e\u8272\u7acb\u523b\u53c8\u9eef\u6de1\u4e86\u4e0b\u6765\uff0c\u9eef\u7136\u81ea\u8bed\uff1a\u8bd7\u97f3\uff0c\u8bd7\u97f3\uff0c\u4f60\u5982\u6b64\u53d7\u82e6\uff0c\u90fd\u662f\u6211\u5bb3 \u4e86\u4f60\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u4fdd\u62a4\u4f60\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4efb\u4f55\u4eba\u4f24\u5bb3\u5230\u4f60\uff01 \u3000\u3000\u9633\u5149\u7167\u4e0a\u7a97\u6237\uff0c\u5929\u5df2\u5b8c\u5168\u4eae\u4e86\u3002 \u3000\u3000\u8336\u53f6\u5e76\u4e0d\u597d\u3002 \u3000\u3000\u4f46\u8336\u53ea\u8981\u662f\u6eda\u70eb\u7684\uff0c\u559d\u8d77\u6765\u603b\u4e0d\u4f1a\u4ee4\u4eba\u89c9\u5f97\u96be\u4ee5\u4e0b\u54bd\uff0c\u8fd9\u6b63\u5982\u5973\u4eba\uff0c\u53ea\u8981\u5e74\u8f7b\uff0c\u5c31\u4e0d\u4f1a \u4ee4\u4eba\u89c9\u5f97\u592a\u8ba8\u538c\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6162\u6162\u5730\u555c\u7740\u8336\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u6211\u4ee5\u524d\u6709\u4e2a\u5f88\u806a\u660e\u7684\u670b\u53cb\uff0c\u66fe\u7ecf\u8bf4\u8fc7\u53e5\u5f88\u6709\u8da3 \u7684\u8bdd\u3002 \u3000\u3000\u9ebb\u5b50\u966a\u7b11\u9053\uff1a\u5927\u7237\u4f60\u81ea\u5df1\u8bf4\u8bdd\u5c31\u6709\u8da3\u5f97\u5f88\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u9053\uff1a\u4ed6\u8bf4\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u559d\u4e0d\u9189\u7684\u9152\uff0c\u4e5f\u7edd\u6ca1\u6709\u96be\u770b\u7684\u5c11\u5973\uff0c\u4ed6\u8fd8\u8bf4\uff0c\u4ed6\u5c31 \u662f\u4e3a\u4e86\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u6240\u4ee5\u624d\u6d3b\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u4ed6\u76ee\u4e2d\u5e26\u7740\u7b11\u610f\uff1a\u5176\u5b9e\u771f\u6b63\u597d\u7684\u9152\u8981\u5e74\u4ee3\u8d8a\u4e45\u624d\u8d8a\u9999\uff0c\u771f\u6b63\u597d\u7684\u5973\u4eba\u4e5f\u8981\u5e74\u7eaa\u8d8a\u5927\u624d\u8d8a \u6709\u5473\u9053\u3002 \u3000\u3000\u9ebb\u5b50\u663e\u7136\u8fd8\u4e0d\u80fd\u9886\u7565\u4ed6\u8fd9\u53e5\u8bdd\u7684\u5473\u9053\uff0c\u6014\u4e86\u534a\u664c\uff0c\u66ff\u8fd9\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u5012\u4e86\u676f\u8336\uff0c\u624d\u95ee\u9053 \uff1a\u5927\u7237\u4f60\u8fd9\u6b21\u56de\u6765\uff0c\u53ef\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6c89\u9ed8\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u7f13\u7f13\u9053\uff1a\u6709\u4eba\u8bf4\u4e86\uff0c\u8fd9\u5730\u65b9\u6709\u5b9d\u85cf\u2014\u2014\uff0d \u3000\u3000\u9ebb\u5b50\u5927\u7b11\u9053\uff1a\u5b9d\u85cf\uff1f\u8fd9\u5730\u65b9\u5f53\u771f\u6709\u5b9d\u85cf\uff0c\u90a3\u5c31\u597d\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u53c8\u655b\u53bb\u4e86\u7b11\u5bb9\uff0c\u773c\u89d2\u5077\u5077\u779f\u7740\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\uff0c\u8bd5\u63a2\u7740\u9053\uff1a\u8fd9\u5730\u65b9\u82e5\u771f\u6709\u5b9d\u85cf\uff0c\u5927\u7237 \u4f60\u603b\u8be5\u77e5\u9053\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u4f60\u6211\u867d\u4e0d\u4fe1\u8fd9\u91cc\u6709\u5b9d\u85cf\uff0c\u600e\u5948\u522b\u4eba\u76f8\u4fe1\u7684\u5374\u4e0d\u5c11\u3002 \u3000\u3000\u9ebb\u5b50\uff1a\u9020\u8c23\u7684\u4eba\u662f\u8c01\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u8981\u9020\u8fd9\u79cd\u8c23\uff1f \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6c89\u541f\u7740\u9053\uff1a\u4ed6\u4e0d\u5916\u6709\u4e24\u79cd\u7528\u610f\uff0c\u7b2c\u4e00\u60f3\u5c06\u4e00\u4e9b\u8d2a\u5fc3\u7684\u4eba\u5f15\u5230\u8fd9\u91cc\u6765\uff0c\u4e92\u76f8 \u4e89\u593a\uff0c\u4e92\u76f8\u6b8b\u6740\uff0c\u4ed6\u4e5f\u597d\u6df7\u6c34\u6478\u9c7c\u3002 \u3000\u3000\u9ebb\u5b50\uff1a\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u610f\u601d\uff1f \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u76ee\u5149\u95ea\u52a8\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u5df2\u6709\u8bb8\u591a\u5e74\u672a\u66fe\u9732\u9762\u4e86\uff0c\u6c5f\u6e56\u4e2d\u8bb8\u591a\u4eba\u90fd\u5728\u6253\u542c\u6211 \u7684\u884c\u8e2a\uff0c\u4ed6\u8fd9\u4e48\u6837\u505a\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u5f15\u6211\u73b0\u8eab\uff0c\u8bf1\u6211\u51fa\u624b\uff01 \u3000\u3000\u9ebb\u5b50\u633a\u80f8\u9053\uff1a\u51fa\u624b\u5c31\u51fa\u624b\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4e5f\u597d\u8ba9\u90a3\u4e9b\u4eba\u77a7\u77a7\u5927\u7237\u4f60\u7684\u672c\u4e8b\u3002 \u3000\u3000\u843d\u62d3\u4e2d\u5e74\u4eba\u82e6\u7b11\u9053\uff1a\u8fd9\u6b21\u6765\u7684\u90a3\u4e9b\u4eba\u4e4b\u4e2d\u6709\u51e0\u4e2a\u8fde\u6211\u90fd\u5bf9\u4ed8\u4e0d\u4e86\uff01 \u3000\u3000\u9ebb\u5b50\u5403\u60ca\u9053\uff1a\u8fd9\u4e16\u4e0a\u96be\u9053\u771f\u8fd8\u6709\u8fde\u5927\u7237\u4f60\u90fd\u5bf9\u4ed8\u4e0d\u4e86\u7684\u4eba\u4e48\uff1f \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u8fd8\u672a\u8bf4\u8bdd\uff0c\u7a81\u7136\u5927\u95e8\u5916\u4f20\u6765\u4e00\u9635\u6572\u95e8\u58f0\u3002\u4e00\u4e2a\u6e05\u4eae\u7684\u58f0\u97f3\u5728\u558a\u9053\uff1a\u501f\u95ee\u8fd9 \u91cc\u53ef\u662f\u9f99\u56db\u7237\u7684\u516c\u9986\u4e48\uff1f\u5728\u4e0b\u7b49\u7279\u6765\u8bbf\u3002 \u3000\u3000\u9ebb\u5b50\u5583\u5583\u9053\uff1a\u5947\u602a\uff0c\u8fd9\u91cc\u5df2\u6709\u4e24\u5e74\u8fde\u9b3c\u90fd\u6ca1\u6709\u4e0a\u95e8\u3002\u4eca\u5929\u600e\u4e48\u4f1a\u7a81\u7136\u6765\u4e86\u5ba2\u4eba\u3002 \u3000\u3000\u8fc7\u4e86\u7ea6\u534a\u4e2a\u65f6\u8fb0\uff0c\u9ebb\u5b50\u624d\u7b11\u563b\u563b\u5730\u56de\u6765\uff0c\u4e00\u8fdb\u95e8\u5c31\u7b11\u9053\uff1a\u4eca\u5929\u539f\u6765\u662f\u592b\u4eba\u7684\u751f\u65e5\uff0c\u8fde\u6211 \u90fd\u5fd8\u4e86\uff0c\u96be\u4e3a\u8fd9\u4e9b\u4eba\u5012\u8fd8\u8bb0\u5f97\uff0c\u662f\u7279\u5730\u6765\u5411\u592b\u4eba\u795d\u5bff\u7684\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6c89\u601d\u7740\uff0c\u95ee\u9053\uff1a\u6765\u7684\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u9ebb\u5b50\uff1a\u4e00\u5171\u6709\u4e94\u4f4d\uff0c\u4e00\u4f4d\u662f\u5f88\u6709\u6c14\u6d3e\u7684\u8001\u4eba\u5bb6\uff0c\u4e00\u4f4d\u662f\u4e2a\u5f88\u5e05\u7684\u5c0f\u4f19\u5b50\uff0c\u8fd8\u6709\u4f4d\u662f\u4e2a\u72ec \u773c\u9f99\uff0c\u6700\u53ef\u6015\u7684\u662f\u4e2a\u8138\u8272\u53d1\u7eff\u7684\u4eba\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u76b1\u7709\u9053\uff1a\u5176\u4e2d\u662f\u5426\u8fd8\u6709\u4f4d\u4e00\u6761\u817f\u7684\u8ddb\u5b50\uff1f \u3000\u3000\u9ebb\u5b50\u70b9\u5934\u9053\uff1a\u4e0d\u9519\u2014\u2014\u5927\u7237\u4f60\u600e\u4f1a\u77e5\u9053\u7684\uff0c\u96be\u9053\u4e5f\u8ba4\u5f97\u4ed6\u4eec\u4e48\uff1f \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u4f4e\u4f4e\u5730\u54b3\u55fd\uff0c\u76ee\u4e2d\u5374\u5df2\u9732\u51fa\u4e86\u6bd4\u5200\u8fd8\u9510\u5229\u7684\u5149\u8292\u3002 \u3000\u3000\u9ebb\u5b50\u5374\u672a\u6ce8\u610f\uff0c\u7b11\u7740\u53c8\u9053\uff1a\u8fd9\u4e94\u4eba\u957f\u5f97\u867d\u6709\u4e9b\u5947\u5f62\u602a\u72b6\uff0c\u4f46\u9001\u7684\u793c\u5012\u771f\u4e0d\u8f7b\uff0c\u5c31\u8fde\u9f99\u56db \u7237\u4ee5\u524d\u5728\u7684\u65f6\u5019\uff0c\u90fd\u6ca1\u6709\u4eba\u9001\u8fc7\u8fd9\u4e48\u91cd\u7684\u793c\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u9053\uff1a\u54e6\uff1f \u3000\u3000\u9ebb\u5b50\uff1a\u4ed6\u4eec\u9001\u7684\u516b\u8272\u793c\u7269\u4e2d\uff0c\u6709\u4e2a\u7528\u7eaf\u91d1\u6253\u6210\u7684\u5927\u94b1\uff0c\u81f3\u5c11\u4e5f\u6709\u56db\u4e94\u65a4\u91cd\uff0c\u6211\u5012\u771f\u8fd8\u672a \u89c1\u8fc7\u6709\u4eba\u51fa\u624b\u8fd9\u4e48\u5927\u65b9\u7684\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u4ed6\u4eec\u9001\u7684\u793c\uff0c\u592b\u4eba\u53ef\u6536\u4e0b\u4e86\u4e48\uff1f \u3000\u3000\u9ebb\u5b50\u9053\uff1a\u592b\u4eba\u672c\u6765\u4e0d\u80af\u6536\u7684\uff0c\u4f46\u90a3\u4e9b\u4eba\u5374\u5750\u5728\u5ba2\u5385\u91cc\u4e0d\u80af\u8d70\uff0c\u597d\u6b9a\u4e5f\u8981\u89c1\u592b\u4eba\u4e00\u9762\uff0c\u8fd8 \u8bf4\u4ed6\u4eec\u672c\u662f\u9f99\u56db\u7237\u7684\u597d\u670b\u53cb\uff0c\u592b\u4eba\u6ca1\u6cd5\u5b50\uff0c\u53ea\u597d\u53eb\u5c11\u7237\u5230\u5ba2\u5385\u91cc\u53bb\u966a\u4ed6\u4eec\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u9053\uff1a\u5927\u7237\u83ab\u770b\u5c11\u7237\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5bf9\u4ed8\u4eba\u53ef\u771f\u6709\u4e00\u5957\uff0c\u8bf4\u8d77\u8bdd\u6765\u6bd4\u5927\u4eba\u8fd8\u8001\u5230\uff0c\u90a3\u51e0\u4f4d \u5ba2\u4eba\u6ca1\u6709\u4e00\u4e2a\u4e0d\u5938\u4ed6\u806a\u660e\u7edd\u9876\u7684\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u51dd\u6ce8\u7740\u676f\u4e2d\u7684\u8336\uff0c\u5583\u5583\u9053\uff1a\u8fd9\u4e94\u4eba\u65e2\u5df2\u6765\u4e86\uff0c\u8fd8\u4f1a\u6709\u4e9b\u4ec0\u4e48\u4eba\u6765\u5462\uff1f\u8fd8\u6709 \u4ec0\u4e48\u4eba\u6562\u6765\u5462\uff1f \u3000\u3000\u8bf8\u845b\u521a\u3001\u9ad8\u884c\u7a7a\u3001\u71d5\u53cc\u98de\u3001\u5510\u72ec\u548c\u4e0a\u5b98\u98de\u6b64\u523b\u6b63\u5728\u90a3\u5177\u5df2\u5927\u534a\u88ab\u642c\u7a7a\u7684\u5927\u5385\u91cc\uff0c\u4e00\u548c\u4e2a \u7a7f\u7ea2\u8863\u670d\u7684\u5b69\u5b50\u8bf4\u8bdd\u3002 \u3000\u3000\u8fd9\u4e94\u4eba\u867d\u7136\u90fd\u662f\u76ee\u7a7a\u4e00\u5207\u7684\u6c5f\u6e56\u67ad\u96c4\uff0c\u6b64\u523b\u5bf9\u8fd9\u5b69\u5b50\u5e76\u6ca1\u6709\u4e1d\u6beb\u8f7b\u6162\u4e4b\u6001\uff0c\u8bf4\u8bdd\u4e5f\u5ba2\u6c14 \u5f97\u5f88\u3002 \u3000\u3000\u53ea\u6709\u4e0a\u5b98\u98de\u4ecd\u7136\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e16\u4e0a\u597d\u50cf\u6ca1\u6709\u4ec0\u4e48\u4e8b\u80fd\u4f7f\u8fd9\u51b7\u6f20\u7684\u5c11\u5e74\u5f00 \u53e3\u7684\u3002 \u3000\u3000\u8bf8\u845b\u521a\u9762\u4e0a\u53c8\u9732\u51fa\u4e86\u4eb2\u5207\u548c\u853c\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u5c11\u5e84\u4e3b\u60ca\u624d\u7edd\u8273\uff0c\u610f\u6c14\u98de\u53d1\uff0c\u4ed6\u65e5\u7684\u6210\u5c31\uff0c \u5fc5\u7136\u4e0d\u53ef\u9650\u91cf\uff0c\u4f46\u671b\u5c11\u5e84\u90a3\u65f6\u83ab\u8981\u5c06\u6211\u4eec\u8fd9\u4e9b\u8001\u5e9f\u7269\u89c6\u5982\u964c\u8def\uff0c\u5728\u4e0b\u7b49\u5c31\u9ad8\u5174\u5f97\u5f88\u4e86\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u4e5f\u7b11\u9053\uff1a\u665a\u8f88\u4ed6\u65e5\u7684\u6210\u5c31\u82e5\u80fd\u6709\u524d\u8f88\u4eec\u4e00\u534a\uff0c\u5c31\u5fc3\u6ee1\u610f\u8db3\uff0c\u4f46\u90a3\u4e5f\u5168\u5f97\u4ef0\u4ed7\u524d\u8f88 \u4eec\u7684\u63d0\u643a\u3002 \u3000\u3000\u8bf8\u845b\u521a\u62ca\u638c\u5927\u7b11\u9053\uff1a\u5c11\u5e84\u4e3b\u771f\u662f\u4f1a\u8bf4\u8bdd\uff0c\u96be\u602a\u9f99\u56db\u7237\u2014\u2014 \u3000\u3000\u4ed6\u7b11\u58f0\u7a81\u7136\u505c\u987f\uff0c\u76ee\u5149\u51dd\u6ce8\u7740\u5385\u5916\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u9ebb\u5b50\u53c8\u5df2\u8083\u5bb9\u800c\u5165\uff0c\u8ddf\u7740\u4ed6\u8d70\u8fdb\u6765\u7684\uff0c\u662f\u4e2a\u9ed1\u5e03\u9ed1\u888d\u3001\u9ed1\u978b\u9ed1\u889c\u3001\u80cc\u540e\u659c\u80cc\u7740\u67c4 \u4e4c\u9798\u957f\u5251\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4ed6\u8eab\u6750\u9ad8\u5927\u800c\u9b41\u4f1f\uff0c\u6bd4\u90a3\u9ebb\u5b50\u51e0\u4e4e\u5bbd\u4e00\u500d\uff0c\u4f46\u770b\u6765\u5374\u4e1d\u6beb\u4e0d\u89c1\u81c3\u80bf\uff0c\u53cd\u800c\u663e\u5f97\u5f88\u7626\u524a\u77eb \u5065\u3002\u4ed6\u9762\u4e0a\u5e26\u7740\u79cd\u5947\u5f02\u7684\u6b7b\u7070\u8272\uff0c\u53cc\u7709\u659c\u98de\uff0c\u76ee\u5149\u7765\u7768\u95f4\uff0c\u9a84\u6c14\u903c\u4eba\uff0c\u988c\u4e0b\u51e0\u7f15\u758f\u758f\u7684\u80e1\u5b50 \uff0c\u968f\u98ce\u98d8\u6563\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u770b\u6765\u663e\u5f97\u65e2\u9ad8\u50b2\u3001\u53c8\u6f47\u6d12\uff0c\u65e2\u4e25\u8083\u3001\u53c8\u4e0d\u7f81\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u53ea\u8981\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u5c31\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u662f\u4e2a\u5e73\u51e1\u7684\u4eba\u3002 \u3000\u3000\u8bf8\u845b\u521a\u7b49\u4e94\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4f3c\u4e4e\u4e5f\u90fd\u5728\u63a2\u8be2\u6b64\u4eba\u7684\u6765\u5386\u3002 \u3000\u3000\u90a3\u7a7f\u7ea2\u8863\u88f3\u7684\u5b69\u5b50\u65e9\u5df2\u8fce\u4e0b\u77f3\u9636\uff0c\u62b1\u62f3\u7b11\u9053\uff1a\u5927\u9a7e\u5149\u4e34\uff0c\u84ec\u58c1\u751f\u8f89\uff0c\u665a\u8f88\u9f99\u5c0f\u4e91\u2014\u2014 \u3000\u3000\u9ed1\u8863\u4eba\u4e0a\u4e0b\u6253\u91cf\u4e86\u4ed6\u4e00\u773c\uff0c\u622a\u53e3\u9053\uff1a\u4f60\u5c31\u662f\u9f99\u5578\u4e91\u7684\u513f\u5b50\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u8eac\u8eab\u9053\uff1a\u6b63\u662f\uff0c\u524d\u8f88\u60f3\u5fc5\u662f\u5bb6\u7236\u7684\u6545\u4ea4\uff0c\u4e0d\u77e5\u9ad8\u59d3\u5927\u540d\uff1f \u3000\u3000\u9ed1\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u6211\u7684\u540d\u59d3\u8bf4\u51fa\u6765\u4f60\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u5927\u6b65\u8d70\u4e0a\u77f3\u9636\uff0c\u6602\u7136\u5165\u5385\u3002 \u3000\u3000\u8bf8\u845b\u521a\u7b49\u4e94\u4eba\u7ad9\u8d77\u76f8\u8fce\uff0c\u8bf8\u845b\u521a\u62b1\u62f3\u7b11\u9053\uff1a\u5728\u4e0b\u2014\u2014\uff0d \u3000\u3000\u4ed6\u53ea\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff0c\u9ed1\u8863\u4eba\u5c31\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4eec\uff0c\u4f60\u4eec\u5374\u4e0d\u5fc5\u6253\u542c\u6211\u7684\u6765\u5386 \u3002 \u3000\u3000\u8bf8\u845b\u521a\u9053\uff1a\u53ef\u662f\u2014\u2014 \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u6211\u7684\u6765\u610f\u548c\u4f60\u4eec\u4e0d\u540c\uff0c\u6211\u53ea\u662f\u6765\u77a7\u77a7\u7684\u3002 \u3000\u3000\u8bf8\u845b\u521a\u5c55\u989c\u7b11\u9053\uff1a\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u771f\u662f\u518d\u5979\u4e5f\u6ca1\u6709\u4e86\uff0c\u7b49\u6b64\u95f4\u4e8b\u5b8c\uff0c\u5728\u4e0b\u7b49\u5fc5\u6709\u8c22\u610f\uff01 \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u6211\u4e0d\u7ba1\u4f60\u4eec\uff0c\u4f60\u4eec\u4e5f\u83ab\u8981\u7ba1\u6211\uff0c\u5927\u5bb6\u4e92\u4e0d\u76f8\u6d89\uff0c\u4e3a\u4ec0\u4e48\u8981\u8c22\u8c22\uff1f \u3000\u3000\u4ed6\u627e\u4e86\u5f20\u6905\u5b50\u5750\u4e0b\uff0c\u7adf\u95ed\u76ee\u517b\u8d77\u795e\u6765\u3002 \u3000\u3000\u8bf8\u845b\u521a\u7b49\u4e94\u4eba\u53c8\u5bf9\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u9ad8\u884c\u7a7a\u5fae\u7b11\u9053\uff1a\u4e45\u95fb\u6b64\u95f4\u4e43\u6c5f\u6e56\u7b2c\u4e00\u540d\u56ed\uff0c\u4e0d\u77e5\u5c11\u5e84\u4e3b\u53ef\u5426\u5e26\u9886\u5728\u4e0b\u7b49\u56db\u5904\u77a7\u77a7\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u665a\u8f88\u65e0\u80fd\uff0c\u81f4\u4f7f\u5bb6\u9053\u4e2d\u843d\uff0c\u5ead\u56ed\u8352\u5e9f\u2014\u2014\uff0d \u3000\u3000\u9ad8\u884c\u7a7a\u6b63\u8272\u622a\u53e3\u9053\uff1a\u5c71\u4e0d\u5728\u9ad8\uff0c\u6709\u4ed9\u5219\u540d\uff0c\u6c34\u4e0d\u5728\u6df1\uff0c\u6709\u9f99\u5219\u7075\uff0c\u5341\u5e74\u6765\u6b64\u95f4\u540d\u4fa0\u7f8e\u4eba \u9ad8\u58eb\u8f88\u51fa\uff0c\u7eb5\u662f\u4e09\u4e94\u82ad\u820d\uff0c\u4e5f\u5df2\u662f\u4ee4\u4eba\u5927\u5f00\u773c\u754c\u4e86\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u5404\u4f4d\u8bf7\u3002 \u3000\u3000\u5fe7\u7684\u4e00\u58f0\uff0c\u5bd2\u9e26\u63a0\u8d77\u3002 \u3000\u3000\u4e00\u884c\u4eba\u7a7f\u8fc7\u5c0f\u5f84\uff0c\u6f2b\u6b65\u800c\u6765\u3002 \u3000\u3000\u5f53\u5148\u5e26\u8def\u7684\u662f\u9f99\u5c0f\u4e91\uff0c\u8d70\u5728\u6700\u540e\u7684\u5c31\u662f\u90a3\u9ed1\u8863\u4eba\uff0c\u4ed6\u773c\u775b\u534a\u5f20\u534a\u5408\uff0c\u53cc\u624b\u90fd\u7f29\u5728\u8896\u4e2d\uff0c \u795e\u60c5\u4f3c\u4e4e\u5341\u5206\u8427\u7d22\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u6307\u7740\u8fdc\u5904\u4e00\u7247\u67af\u840e\u4e86\u7684\u6885\u6797\uff0c\u9053\uff1a\u90a3\u8fb9\u5c31\u662f\u51b7\u9999\u5c0f\u7b51\u3002 \u3000\u3000\u71d5\u53cc\u98de\u773c\u4e2d\u5149\u8292\u95ea\u52a8\uff0c\u9053\uff1a\u542c\u8bf4\u5c0f\u674e\u63a2\u82b1\u6614\u65e5\u5c31\u4f4f\u5728\u90a3\u91cc\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u4f4e\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u71d5\u53cc\u98de\u624b\u638c\u8f7b\u629a\u7740\u9690\u5f62\u5728\u957f\u886b\u4e2d\u7684\u98de\u67aa\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u4ed6\u662f\u98de\u5200\uff0c\u6211\u662f\u98de\u67aa\uff0c\u6709\u4e00\u65e5\u82e5\u80fd \u548c\u4ed6\u8f83\u91cf\u8f83\u91cf\uff0c\u5012\u4e5f\u662f\u5feb\u4e8b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fdc\u8fdc\u5730\u7ad9\u7740\uff0c\u9053\uff1a\u4f60\u82e5\u771f\u80fd\u548c\u4ed6\u8f83\u91cf\uff0c\u90a3\u5c31\u662f\u602a\u4e8b\u4e86\u3002 \u3000\u3000\u71d5\u53cc\u98de\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u6012\u76ee\u77aa\u7740\u4ed6\u3002 \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u5c0f\u674e\u98de\u5200>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u5c0f\u674e\u98de\u5200 \u3000\u3000\u9f99\u5c0f\u4e91\u89c1\u71d5\u53cc\u98de\u4f3c\u5df2\u6012\u6781\uff0c\u8d76\u7d27\u7b11\u9053\uff1a\u4ed6\u7684\u98de\u5200\u4e5f\u662f\u51e1\u94c1\u6240\u94f8\uff0c\u53c8\u4e0d\u662f\u4ec0\u4e48\u4ed9\u5175\u795e\u5668\uff0c \u4f46\u6c5f\u6e56\u4e2d\u4eba\u5374\u8bf4\u5f97\u4ed6\u5c31\u597d\u50cf\u4f20\u8bf4\u4e2d\u5251\u4ed9\u4e00\u6837\uff0c\u6211\u6709\u65f6\u542c\u4e86\u771f\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u542c\u8bf4\u4ed6\u5e9f\u53bb\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u4f60\u5bf9\u4ed6\u60f3\u5fc5\u662f\u4e00\u76f4\u6000\u6068\u5728\u5fc3\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u7b11\u9053\uff1a\u674e\u5927\u53d4\u672c\u662f\u6211\u957f\u8f88\uff0c\u957f\u8f88\u6559\u8bad\u665a\u8f88\uff0c\u665a\u8f88\u600e\u6562\u8d77\u6000\u6068\u4e4b\u5fc3\uff0c\u4f55\u51b5\u4e00\u4e2a\u4eba\u4e0d \u4f1a\u6b66\u529f\uff0c\u4e5f\u672a\u5fc5\u5c31\u4e0d\u80fd\u505a\u5927\u4e8b\u7684\uff0c\u524d\u8f88\u4f60\u8bf4\u662f\u4e48\uff1f \u3000\u3000\u4ed6\u7b11\u5f97\u662f\u90a3\u4e48\u65e0\u90aa\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51dd\u6ce8\u7740\u4ed6\uff0c\u4f3c\u4e5f\u770b\u4e0d\u900f\u8fd9\u5b69\u5b50\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u8bf8\u845b\u521a\u5374\u5df2\u62ca\u638c\u7b11\u9053\uff1a\u6709\u5fd7\u6c14\uff0c\u679c\u7136\u6709\u5fd7\u6c14\uff0c\u5c31\u8fd9\u53e5\u8bdd\uff0c\u5df2\u4e0d\u6127\u4e3a\u9f99\u56db\u7237\u7684\u516c\u5b50\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u8eac\u8eab\u9053\uff1a\u524d\u8f88\u8fc7\u5956\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7a81\u7136\u9053\uff1a\u542c\u8bf4\u6797\u4ed9\u513f\u672c\u4e5f\u4f4f\u5728\u90a3\u91cc\u7684\uff0c\u662f\u4e48\uff1f \u3000\u3000\u4ed6\u6bd5\u7adf\u662f\u5f00\u53e3\u4e86\uff0c\u8fde\u9f99\u5c0f\u4e91\u90fd\u4f3c\u89c9\u5f97\u6709\u4e9b\u8be7\u5f02\uff0c\u966a\u7b11\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u5979\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u6797\u963f\u59e8\u662f\u5728\u4e24\u5e74\u524d\u7684\u4e00\u4e2a\u665a\u4e0a\u7a81\u7136\u5931\u8e2a\u7684\uff0c\u8fde\u81ea\u5df1\u7684\u8863\u670d\u9996\u9970\u90fd\u672a\u5e26\u8d70\uff0c\u8c01 \u4e5f\u4e0d\u77e5\u9053\u5979\u53bb\u4e86\u54ea\u91cc\uff0c\u6709\u4eba\u8bf4\uff0c\u5979\u662f\u88ab\u963f\u98de\u63b3\u8d70\u7684\uff0c\u4e5f\u6709\u4eba\u8bf4\u5979\u5df2\u6b7b\u5728\u963f\u98de\u624b\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u76b1\u4e86\u76b1\u7709\uff0c\u95ed\u4e0a\u5634\u518d\u4e5f\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4e00\u884c\u4eba\u8d70\u8fc7\u5c0f\u6865\uff0c\u6765\u5230\u4e86\u90a3\u5c0f\u697c\u524d\u3002 \u3000\u3000\u8bf8\u845b\u521a\u76ee\u5149\u95ea\u52a8\uff0c\u4f3c\u4e4e\u5bf9\u8fd9\u5c0f\u697c\u7279\u522b\u611f\u5174\u8da3\u3002 \u3000\u3000\u9ad8\u884c\u7a7a\u95ee\u9053\uff1a\u4e0d\u77e5\u8fd9\u53c8\u662f\u4ec0\u4e48\u6240\u5728\uff1f \u3000\u3000\u201c\u662f\u5bb6\u6bcd\u7684\u5c45\u5904\u201d\u3002 \u3000\u3000\u9ad8\u884c\u7a7a\u9053\uff1a\u5728\u4e0b\u7b49\u672c\u662f\u5411\u4ee4\u5802\u5927\u4eba\u795d\u5bff\u7684\uff0c\u4e0d\u77e5\u5c11\u5e84\u4e3b\u53ef\u5bb9\u6211\u7b49\u4e0a\u697c\u89c1\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u5bb6\u6bcd\u4e00\u5411\u4e0d\u613f\u89c1\u5ba2\uff0c\u5f85\u665a\u8f88\u5148\u4e0a\u53bb\u8bf4\u4e00\u53e5\u597d\u4e48\uff1f \u3000\u3000\u9ad8\u884c\u7a7a\u9053\uff1a\u8bf7\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u6162\u6162\u5730\u8d70\u4e0a\u697c\uff0c\u8eab\u5f62\u7adf\u5df2\u6709\u4e9b\u4f5d\u507b\uff0c\u5168\u65e0\u5c11\u5e74\u4eba\u7684\u6d3b\u6cfc\u4e4b\u6001\u3002 \u3000\u3000\u5510\u72ec\u7b11\u9053\uff1a\u50cf\u4ed6\u8fd9\u6837\u7684\u5c0f\u5b69\u5b50\uff0c\u80fd\u6d3b\u5f97\u957f\u624d\u662f\u602a\u4e8b\u3002 \u3000\u3000\u8bf8\u845b\u521a\u9762\u4e0a\u7b11\u5bb9\u5df2\u4e0d\u89c1\uff0c\u6c89\u58f0\u9053\uff1a\u4f60\u8ba4\u6e05\u695a\u4e86\u5c31\u662f\u8fd9\u5730\u65b9\u4e48\uff1f \u3000\u3000\u9ad8\u884c\u7a7a\u58f0\u97f3\u538b\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u6211\u5df2\u5c06\u6628\u591c\u6765\u7684\u90a3\u5c01\u4fe1\u4ed4\u7ec6\u7814\u7a76\u8fc7\u6570\u6b21\uff0c\u674e\u5bb6\u7684\u5b9d\u85cf\uff0c\u5c31\u5728 \u8fd9\u5c0f\u697c\u91cc\uff0c\u636e\u8bf4\u4ed6\u4eec\u6570\u4ee3\u9ad8\u5b98\uff0c\u73cd\u5b9d\u805a\u96c6\u4e4b\u4e30\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u53ca\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u7528\u773c\u89d2\u779f\u7740\u90a3\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fdc\u8fdc\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u6b63\u4f4e\u7740\u5934\u5728\u770b\u8349\u4e1b\u4e2d\u4e24\u53ea\u87cb\u87c0\u76f8\u6597\uff0c\u4f3c\u4e4e\u6839\u672c\u672a\u6ce8\u610f\u5230\u4ed6\u4eec\u5728 \u8bf4\u8bdd\u3002 \u3000\u3000\u8bf8\u845b\u521a\u773c\u775b\u53d1\u7740\u5149\uff0c\u9053\uff1a\u73cd\u5b9d\u5012\u8fd8\u662f\u5c0f\u4e8b\uff0c\u4f46\u8001\u674e\u63a2\u82b1\u7684\u53e4\u73a9\u5b57\u753b\uff0c\u548c\u5c0f\u674e\u63a2\u82b1\u7684\u6b66\u529f \u79d8\u7b08\uff0c\u5374\u662f\u5e2e\u4e3b\u5fd7\u5728\u5fc5\u5f97\u7684\uff0c\u4f60\u6211\u4eca\u65e5\u4e07\u4e07\u4e0d\u53ef\u7a7a\u624b\u800c\u56de\u3002 \u3000\u3000\u9ad8\u884c\u7a7a\u70b9\u5934\uff0c\u9f99\u5c0f\u4e91\u5df2\u8d70\u4e0b\u4e86\u697c\u3002 \u3000\u3000\u8bf8\u845b\u521a\u7acb\u523b\u5c55\u989c\u800c\u7b11\uff0c\u9053\uff1a\u4ee4\u5802\u5927\u4eba\u53ef\u66fe\u7b54\u5e94\u4e86\u4e48\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u9762\u4e0a\u5e26\u7740\u8be7\u5f02\u4e4b\u8272\uff0c\u6447\u5934\u9053\uff1a\u5bb6\u6bcd\u4e0d\u5728\u697c\u4e0a\u3002 \u3000\u3000\u8bf8\u845b\u521a\u6de1\u6de1\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u665a\u8f88\u4e5f\u5728\u5947\u602a\uff0c\u5bb6\u6bcd\u4e00\u5411\u5f88\u5c11\u4e0b\u697c\u7684\u3002 \u3000\u3000\u8bf8\u845b\u521a\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u60f3\u5fc5\u5c31\u4f1a\u56de\u6765\u7684\uff0c\u6211\u4eec\u4e0a\u697c\u53bb\u7b49\u5979\u5427\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u4e2a\u9ec4\u886b\u4eba\u5feb\u6b65\u5954\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u5f85\u5c5e\u4e0b\u7b49\u5148\u4e0a\u53bb\u6253\u626b\u6253\u626b\uff0c\u518d\u8bf7\u5802\u4e3b\u4e0a\u697c\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u672c\u6765\u7ad9\u5f97\u6bd4\u90a3\u9ed1\u8863\u4eba\u8fd8\u8fdc\uff0c\u6b64\u523b\u98de\u6b65\u800c\u6765\uff0c\u9f99\u5c0f\u4e91\u4f3c\u4e4e\u60f3\u963b\u62e6\uff0c\u53c8\u4e0d\u6562\u963b\u62e6\uff0c\u7ec8 \u4e8e\u8fd8\u662f\u8ba9\u5f00\u8def\u3002 \u3000\u3000\u53ea\u542c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e09\u4e08\u957f\u97ad\u5ffd\u7136\u6296\u51fa\u4e86\u4e09\u4e2a\u5706\u5708\uff0c\u4e0d\u504f\u4e0d\u501a\u6070\u5de7\u5957\u4e0a\u4e86\u8fd9\u4e09\u4eba\u7684\u8116\u5b50 \u3002 \u3000\u3000\u957f\u97ad\u4e00\u7d27\uff0c\u683c\u7684\u4e00\u54cd\uff0c\u53c8\u677e\u5f00\u3002 \u3000\u3000\u7b2c\u4e00\u4eba\u8fde\u58f0\u97f3\u90fd\u672a\u53d1\u51fa\uff0c\u5c31\u5012\u4e86\u4e0b\u53bb\uff0c\u8116\u5b50\u7adf\u5df2\u751f\u751f\u88ab\u957f\u97ad\u52d2\u65ad\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u4eba\u60e8\u547c\u4e86\u4e00\u58f0\uff0c\u4ef0\u5929\u8dcc\u5012\uff0c\u820c\u5934\u5df2\u5410\u51fa\u6765\uff0c\u7ec8\u4e8e\u8fd8\u662f\u65ad\u4e86\u6c14\u3002 \u3000\u3000\u7b2c\u4e09\u4eba\u624b\u63a9\u7740\u54bd\u5589\uff0c\u5954\u51fa\u6570\u6b65\uff0c\u624d\u6251\u9762\u8dcc\u5012\uff0c\u8eab\u5b50\u4e0d\u505c\u5730\u98a4\u52a8\u7740\uff0c\u5589\u5499\u53d1\u51fa\u4e86\u4e00\u8fde\u4e32\u683c \u683c\u4e4b\u58f0\u3002 \u3000\u3000\u4ed6\u4fa5\u5e78\u672a\u6b7b\uff0c\u5374\u6bd4\u6b7b\u8fd8\u8981\u75db\u82e6\u5341\u500d\u3002 \u3000\u3000\u4ed6\u4e00\u97ad\u6325\u51fa\uff0c\u5c31\u6709\u4e09\u4eba\u5012\u5730\uff0c\u8fde\u8bf8\u845b\u521a\u90fd\u4e0d\u7981\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u53ea\u6709\u90a3\u9ed1\u8863\u4eba\u9762\u4e0a\u9732\u51fa\u4e86\u4e0d\u5c51\u4e4b\u8272\uff0c\u6de1\u6de1\u9053\uff1a\u97ad\u795e\u86c7\u97ad\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002 \u3000\u3000\u4ed6\u4ef0\u8d77\u5934\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u610f\u5174\u4f3c\u4e4e\u66f4\u8427\u7d22\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u89c9\u5f97\u5f88\u5931\u671b\u3002 \u3000\u3000\u8981\u77e5\u897f\u95e8\u67d4\u8fd9\u4e00\u97ad\u529b\u9053\u82e5\u662f\u7528\u8db3\uff0c\u90a3\u4e09\u4eba\u4fbf\u5f97\u7acb\u523b\u540c\u65f6\u6b7b\u5728\u4ed6\u97ad\u4e0b\uff0c\u6b64\u523b\u4e09\u4eba\u6b7b\u65f6\u65e2\u6709 \u5148\u540e\uff0c\u6b7b\u6cd5\u4e5f\u4e0d\u4e00\u6837\uff0c\u663e\u89c1\u897f\u95e8\u67d4\u8fd9\u4e00\u97ad\u529b\u91cf\u62ff\u634f\u5f97\u8fd8\u672a\u80fd\u6070\u5230\u597d\u5904\uff0c\u662f\u4ee5\u97ad\u4e0a\u7684\u529b\u9053\u5206\u5e03 \u4e0d\u5300\uff0c\u706b\u5019\u8fd8\u5dee\u4e86\u534a\u5206\u3002 \u3000\u3000\u8bf8\u845b\u521a\u773c\u775b\u4eae\u4e86\uff0c\u7b11\u9053\uff1a\u897f\u95e8\u67d4\uff0c\u6628\u591c\u4f60\u4fa5\u5e78\u9003\u8131\uff0c\u4eca\u65e5\u770b\u4f60\u8fd8\u80fd\u9003\u5f97\u4e86\u4e48\uff1f \u3000\u3000\u897f\u95e8\u67d4\u94c1\u9752\u7740\u8138\uff0c\u638c\u4e2d\u86c7\u97ad\u7a81\u53c8\u98de\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u97ad\u6765\u5f97\u65e0\u58f0\u65e0\u606f\uff0c\u76f4\u5230\u97ad\u68a2\u5377\u5230\u540e\uff0c\u624d\u542c\u5230\u547c\u7684\u4e00\u58f0\u6025\u54cd\uff0c\u663e\u89c1\u4ed6\u8fd9\u4e00\u97ad\u901f\u5ea6\u4e4b\u5feb \uff0c\u72b9\u5728\u58f0\u97f3\u4e4b\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8bf8\u845b\u521a\u8eab\u5b50\u7a81\u7136\u5012\u7ffb\u800c\u8d77\uff0c\u94c1\u62d0\u51cc\u7a7a\u8fce\u4e0a\u4e86\u957f\u97ad\uff0c\u97ad\u68a2\u53cd\u5377\uff0c\u7acb\u523b\u6bd2\u86c7\u822c\u5c06 \u94c1\u62d0\u5377\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u7b03\u7684\u4e00\u58f0\uff0c\u94c1\u62d0\u63d2\u5165\u5730\u4e0b\u3002 \u3000\u3000\u8bf8\u845b\u521a\u5355\u8db3\u671d\u5929\uff0c\u5012\u7acb\u5728\u94c1\u62d0\u4e0a\uff0c\u6574\u4e2a\u4eba\u5ffd\u7136\u6709\u5982\u9640\u87ba\u822c\u65cb\u8f6c\u8d77\u6765\uff0c\u94c1\u62d0\u4e5f\u56f4\u7740\u4ed6\u8f6c\u3002 \u3000\u3000\u7f20\u5728\u94c1\u62d0\u4e0a\u7684\u957f\u97ad\uff0c\u8d8a\u7f20\u8d8a\u7d27\uff0c\u8d8a\u5377\u8d8a\u77ed\uff0c\u897f\u95e8\u67d4\u7684\u4eba\uff0c\u4e5f\u4e0d\u7531\u81ea\u4e3b\u88ab\u62c9\u4e86\u8fc7\u6765\uff0c\u4e09\u4e08 \u957f\u7684\u86c7\u97ad\u8f6c\u77ac\u95f4\u5df2\u6709\u5927\u534a\u88ab\u5377\u5728\u94c1\u62d0\u4e0a\u3002 \u3000\u3000\u53ea\u56e0\u897f\u95e8\u67d4\u5355\u624b\u6325\u97ad\uff0c\u8bf8\u845b\u521a\u5374\u662f\u5168\u8eab\u90fd\u5728\u94c1\u62d0\u4e0a\uff0c\u662f\u4ee5\u897f\u95e8\u67d4\u97ad\u4e0a\u7684\u529b\u9053\uff0c\u65e0\u8bba\u5982\u4f55 \u4e5f\u4e07\u4e07\u6bd4\u4e0d\u4e0a\u94c1\u62d0\u4e4b\u5f3a\u3002 \u3000\u3000\u4ed6\u9762\u8272\u7531\u9752\u53d8\u7ea2\uff0c\u7531\u7ea2\u53d8\u767d\uff0c\u4e00\u7c92\u7c92\u6c57\u73e0\u7531\u9f3b\u5b50\u4e24\u4fa7\u6c81\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8bf8\u845b\u521a\u5927\u559d\u4e00\u58f0\uff0c\u5012\u7acb\u5728\u94c1\u62d0\u4e0a\u7684\u8eab\u5b50\u5ffd\u7136\u6a2a\u626b\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u770b\u6765\u6d3b\u8131\u8131\u6b63\u53c8\u662f\u4e00\u7740\u6a2a\u626b\u5343\u519b\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4ee5\u4eba\u4f5c\u62d0\u626b\u51fa\uff0c\u5374\u4ee5\u62d0\u4f5c\u4eba\u9489\u5728\u5730\u4e0a \u3002 \u3000\u3000\u897f\u95e8\u67d4\u82e5\u5c06\u97ad\u6492\u624b\uff0c\u81ea\u7136\u53ef\u4ee5\u907f\u5f00\u8fd9\u4e00\u7740\uff0c\u53ea\u662f\u4ed6\u4ee5\u97ad\u795e\u4e3a\u53f7\uff0c\u82e5\u5c06\u957f\u97ad\u6492\u624b\uff0c\u4ee5\u540e\u8fd8 \u6709\u4f55\u9762\u76ee\u89c1\u4eba\u3002 \u3000\u3000\u4ed6\u957f\u97ad\u82e5\u4e0d\u6492\u624b\uff0c\u53ea\u6709\u4ee5\u5269\u4e0b\u7684\u5de6\u624b\u786c\u78b0\u786c\u53bb\u63a5\u8fd9\u4e00\u811a\uff0c\u624b\u4e0a\u7684\u529b\u91cf\u600e\u53ca\u811a\u4e0a\u5f3a\uff0c\u8fd9\u4e00 \u62db\u63a5\u4e0b\u624b\uff0c\u4ed6\u8fd9\u53ea\u624b\u52bf\u5fc5\u8981\u88ab\u8e22\u788e\u3002 \u3000\u3000\u897f\u95e8\u67d4\u6bd5\u7adf\u4e5f\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u8f7b\u4e00\u58f0\uff0c\u8eab\u5f62\u5ffd\u7136\u5c55\u52a8\uff0c\u56f4\u7740\u94c1\u62d0\u98de\u8f6c\u4e0d\u505c \u3002 \u3000\u3000\u4ed6\u81ea\u7136\u662f\u60f3\u5c06\u7f20\u5728\u94c1\u62d0\u4e0a\u7684\u957f\u97ad\u64a4\u51fa\uff0c\u600e\u5948\u8bf8\u845b\u521a\u5374\u4e5f\u65e9\u5df2\u7b97\u51c6\u4e86\u4ed6\u8fd9\u4e00\u7740\uff0c\u8db3\u5c16\u4e00\u8e22 \uff0c\u8eab\u5b50\u5982\u5012\u626f\u98ce\u65d7\uff0c\u4e5f\u968f\u7740\u65cb\u8f6c\u8d77\u6765\uff0c\u8db3\u5c16\u59cb\u7ec8\u4e0d\u79bb\u897f\u95e8\u67d4\u524d\u80f8\u65b9\u5bf8\u4e4b\u95f4\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u5982\u86c6 \u9644\u9aa8\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u53d8\u5316\u4e4b\u751f\u529f\u5947\u79d8\uff0c\u59d4\u5b9e\u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u53ea\u6709\u90a3\u9ed1\u8863\u4eba\u5374\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u91d1\u521a\u94c1\u62d0\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u2014\u2014 \u3000\u3000\u8981\u77e5\u8bf8\u845b\u521a\u8fd9\u62db\u65f6\u95f4\u90e8\u4f4d\u82e5\u771f\u62ff\u634f\u5f97\u5206\u6beb\u4e0d\u5dee\uff0c\u8fd9\u811a\u8e22\u51fa\uff0c\u897f\u95e8\u67d4\u4fbf\u8be5\u65e0\u5904\u95ea\u907f\u5e94\u58f0\u5012 \u5730\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u62db\u4f7f\u5f97\u663e\u7136\u8fd8\u6162\u4e86\u4e00\u4e9b\uff0c\u4f46\u7eb5\u7136\u5982\u6b64\uff0c\u897f\u95e8\u67d4\u5df2\u662f\u88ab\u903c\u5165\u6b7b\u5730\uff0c\u5371\u5728\u9877\u523b\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u867d\u5feb\uff0c\u4f46\u7ed5\u7740\u5706\u5708\u5728\u5916\u98de\u8f6c\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u5982\u5706\u5fc3\u4e2d\u7684\u94c1\u62d0\u6025\uff0c\u80af\u89c1\u957f\u97ad\u5df2\u8d8a\u6536 \u8d8a\u77ed\uff0c\u4ed6\u82e5\u4e0d\u6492\u624b\u629b\u97ad\uff0c\u5c31\u5f97\u4f24\u5728\u8bf8\u845b\u521a\u8db3\u4e0b\u3002 \u3000\u3000\u5510\u72ec\u76ee\u5149\u95ea\u52a8\uff0c\u9634\u607b\u607b\u7b11\u9053\uff1a\u6b7b\u5230\u4e34\u5934\uff0c\u53c8\u4f55\u5fc5\u518d\u4f5c\u56f0\u517d\u4e4b\u4e89\uff0c\u6211\u6765\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u5427\uff01 \u3000\u3000\u4ed6\u53cc\u624b\u4e00\u4f38\u4e00\u7f29\uff0c\u5df2\u6492\u51fa\u4e86\u4ed6\u7684\u72ec\u95e8\u957f\u5203\u87b3\u8782\u5200\uff0c\u53ea\u89c1\u60e8\u78a7\u8272\u7684\u5149\u534e\u4e00\u95ea\uff0c\u4ea4\u526a\u822c\u5411\u897f \u95e8\u67d4\u540e\u80cc\u5212\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u5200\u521a\u6325\u51fa\uff0c\u4eba\u521a\u8dc3\u8d77\uff0c\u7a81\u7136\u50cf\u662f\u88ab\u4e00\u53ea\u65e0\u5f62\u7684\u624b\u8fce\u9762\u51fb\u4e86\u4e00\u62f3\uff0c\u6574\u4e2a\u4eba\u7a81\u7136\u5012\u7ffb \u800c\u51fa\uff0c\u4ef0\u5929\u8dcc\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u8fde\u4e00\u58f0\u60e8\u53eb\u58f0\u8fd8\u672a\u53d1\u51fa\uff0c\u547c\u5438\u5df2\u7acb\u523b\u505c\u987f\u4e86\uff01\u56e0\u4e3a\u4ed6\u54bd\u5589\u4e0a\u5df2\u63d2\u7740\u4e00\u628a\u5200\uff01 \u3000\u3000\u4e00\u628a\u770b\u6765\u5e76\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5c0f\u5200\uff01 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u8138\u8272\u90fd\u53d8\u4e86\u3002 \u3000\u3000\u8bf8\u845b\u521a\u773c\u89d2\u4e5f\u89c1\u4e86\u8fd9\u67c4\u5200\uff0c\u7acb\u523b\u5931\u58f0\u9053\uff1a\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u8fd9\u4e00\u58f0\u5524\u51fa\uff0c\u4ed6\u5fc3\u795e\u5df2\u5206\uff0c\u771f\u529b\u5df2\u6563\uff0c\u8eab\u5b50\u7a81\u7136\u5411\u53cd\u65b9\u5411\u8f6c\u52a8\u8d77\u6765\uff0c\u4f46\u5374\u5df2\u662f\u8eab\u4e0d\u7531\u5df2 \u3002 \u3000\u3000\u897f\u95e8\u67d4\u624b\u8155\u4e00\u7d27\uff0c\u5df2\u62bd\u51fa\u4e86\u4ed6\u7684\u86c7\u97ad\uff01 \u3000\u3000\u8bf8\u845b\u521a\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5012\u63a0\u4e24\u4e08\uff0c\u7b03\u7684\u4e00\u58f0\uff0c\u94c1\u62d0\u843d\u5730\uff0c\u4ed6\u7684\u4eba\u4e5f\u7acb\u523b\u53c8\u4f3c\u9489\u5728\u5730\u4e0a\uff0c \u7a33\u5982\u6cf0\u5c71\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u773c\u775b\u5374\u662f\u60ca\u614c\u4e0d\u5b9a\uff0c\u53ea\u89c1\u5c0f\u697c\u5916\u5df2\u6162\u6162\u5730\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u8863\u886b\u843d\u62d3\uff0c\u5934\u53d1\u84ec\u4e71\uff0c\u770b\u6765\u662f\u90a3\u4e48\xd7\u5012\uff0c\u90a3\u4e48\u6194\u60b4\uff0c\u4f46\u4ed6\u7684\u4e00\u53cc\u773c\u775b\u5374\u6bd4\u5200\u8fd8\u8981\u9510 \u5229\u3002 \u3000\u3000\u8bf8\u845b\u521a\u7684\u624b\u7d27\u63e1\u94c1\u62d0\uff0c\u6307\u8282\u5374\u5df2\u56e0\u7528\u529b\u800c\u53d1\u767d\uff0c\u55c4\u58f0\u9053\uff1a\u5c0f\u674e\u63a2\u82b1\uff1f \u3000\u3000\u8fd9\u4eba\u6de1\u6de1\u7b11\u4e86\u7b11\u9053\uff1a\u4e0d\u6562\u3002 \u3000\u3000\u7b03\u7684\uff0c\u8bf8\u845b\u521a\u4e0d\u7531\u81ea\u4e3b\u9000\u540e\u4e00\u6b65\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u6211\u7d20\u65e0\u51a4\u4ec7\uff0c\u4f60\u4f55\u82e6\u6765\u8ddf\u6211\u4eec\u4f5c\u5bf9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u6211\u4ece\u4e0d\u613f\u548c\u4eba\u4f5c\u5bf9\uff0c\u5374\u4e5f\u4e0d\u559c\u6b22\u522b\u4eba\u8ddf\u6211\u4f5c\u5bf9\u3002 \u3000\u3000\u4ed6\u8f7b\u629a\u7740\u624b\u91cc\u7684\u5200\u950b\uff0c\u60a0\u60a0\u9053\uff1a\u8fd9\u91cc\u5e76\u6ca1\u6709\u4ec0\u4e48\u5b9d\u85cf\uff0c\u5404\u4f4d\u5f92\u52b3\u5f80\u8fd4\uff0c\u6211\u4e5f\u89c9\u62b1\u6b49\u5f97\u5f88 \u2014\u2014\u5404\u4f4d\u8d70\u7684\u65f6\u5019\uff0c\u5c31\u8bf7\u5c06\u5e26\u6765\u7684\u793c\u7269\u518d\u5e26\u8d70\u5427\u3002 \u3000\u3000\u8bf8\u845b\u521a\u3001\u4e0a\u5b98\u98de\u3001\u9ad8\u884c\u7a7a\u773c\u775b\u76ef\u7740\u4ed6\u624b\u91cc\u7684\u5200\u950b\uff0c\u54bd\u5589\u91cc\u5c31\u50cf\u662f\u5df2\u88ab\u4e00\u4ef6\u51b0\u51b7\u7684\u4e1c\u897f\u585e \u4f4f\uff0c\u518d\u4e5f\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u71d5\u53cc\u98de\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u6211\u4eec\u82e5\u4e0d\u8d70\u53c8\u5f85\u5982\u4f55\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u5949\u529d\u9601\u4e0b\uff0c\u4e0d\u5982\u8fd8\u662f\u8d70\u4e86\u7684\u597d\uff1f \u3000\u3000\u71d5\u53cc\u98de\u5389\u58f0\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u6211\u65e9\u5c31\u60f3\u548c\u4f60\u4e00\u8f83\u9ad8\u4f4e\u4e86\uff0c\u522b\u4eba\u6015\u4f60\uff0c\u6211\u71d5\u53cc\u98de\u5374\u4e0d\u6015\u4f60\uff01 \u3000\u3000\u4ed6\u53cd\u624b\u626f\u5f00\u4e86\u957f\u886b\uff0c\u9732\u51fa\u4e86\u4e24\u6392\u98de\u67aa\u3002 \u3000\u3000\u53ea\u89c1\u7ea2\u7f28\u98d8\u98de\uff0c\u67aa\u5c16\u5728\u79cb\u76ee\u4e0b\u95ea\u95ea\u5730\u53d1\u7740\u5149\uff0c\u5c31\u50cf\u662f\u4e24\u6392\u91ce\u517d\u7684\u7259\u9f7f\uff0c\u5728\u7b49\u7740\u62e9\u4eba\u800c\u566c \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u8fde\u77a7\u4e5f\u672a\u77a7\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u71d5\u53cc\u98de\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u624b\u9f50\u6325\uff0c\u970e\u773c\u95f4\u5df2\u53d1\u51fa\u4e5d\u67c4\u98de\u67aa\uff0c\u4f46\u89c1\u7ea2\u7f28\u6f2b\u5929\uff0c\u8fd8\u672a\u51fb\u5230\u674e\u5bfb\u6b22 \u9762\u524d\uff0c\u7a81\u53c8\u7eb7\u7eb7\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u518d\u770b\u71d5\u53cc\u98de\uff0c\u7adf\u5df2\u4ef0\u5929\u8dcc\u5012\uff0c\u54bd\u5589\u4e0a\u8d6b\u7136\u5df2\u591a\u4e86\u67c4\u96ea\u4eae\u7684\u5200\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u8c01\u4e5f\u672a\u770b\u51fa\u8fd9\u67c4\u5200\u662f\u4f55\u65f6\u523a\u5165\u4ed6\u54bd\u5589\u7684\uff0c\u4f46\u663e\u7136\u5c31\u5728\u4ed6\u53cc\u624b\u521a\u6325\u51fa\u7684\u90a3\u4e00\u5239\u90a3\u95f4\u3002 \u3000\u3000\u4ed6\u624b\u4e0a\u7684\u529b\u91cf\u8fd8\u672a\u5b8c\u5168\u4f7f\u51fa\uff0c\u5200\u5df2\u523a\u5165\u4e86\u4ed6\u54bd\u5589\uff0c\u662f\u4ee5\u53d1\u51fa\u53bb\u7684\u98de\u67aa\u52bf\u529b\u4e5f\u4e0d\u8db3\uff0c\u624d\u4f1a \u534a\u9014\u8dcc\u843d\u5728\u5730\u3002 \u3000\u3000\u597d\u5feb\u7684\u5200\uff01 \u3000\u3000\u71d5\u53cc\u98de\u6b7b\u4e5f\u4e0d\u4fe1\u4e16\u4e0a\u7adf\u6709\u5982\u6b64\u5feb\u7684\u5200\uff01 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u4fef\u9996\u77a7\u4e86\u77a7\u71d5\u53cc\u624b\u7684\u5c38\u8eab\uff0c\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u51b7\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u4f60\u82e5\u80fd \u548c\u4ed6\u8f83\u91cf\uff0c\u90a3\u624d\u662f\u602a\u4e8b\uff0c\u4f60\u5982\u4eca\u76f8\u4fe1\u4e86\u4e48\uff1f \u3000\u3000\u4ed6\u7f13\u7f13\u62ac\u8d77\u5934\uff0c\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\u4e00\u5b57\u5b57\u9053\uff1a\u5c0f\u674e\u98de\u5200\u679c\u7136\u672a\u4ee4\u6211\u5931\u671b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9601\u4e0b\u662f\u2014\u2014 \u3000\u3000\u9ed1\u8863\u4eba\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u4e45\u4ef0\u5c0f\u674e\u63a2\u82b1\u4e4b\u540d\uff0c\u4eca\u65e5\u76f8\u89c1\uff0c\u65e0\u4ee5\u4e3a\u656c\u2014\u2014 \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\u7a81\u7136\u65cb\u8eab\u3002 \u3000\u3000\u53ea\u542c\u545b\u7684\u4e00\u58f0\u9f99\u541f\uff0c\u5251\u5df2\u51fa\u624b\u3002 \u3000\u3000\u5251\u8eab\u4e5f\u662f\u4e4c\u9ed1\u8272\u7684\uff0c\u4e0d\u89c1\u5149\u534e\uff0c\u4f46\u5251\u4e00\u51fa\u9798\uff0c\u68ee\u5bd2\u7684\u5251\u6c14\u5df2\u903c\u4eba\u7709\u776b\u3002 \u3000\u3000\u9ad8\u884c\u7a7a\u53ea\u89c9\u5fc3\u5934\u4e00\u5bd2\uff0c\u4e4c\u9ed1\u7684\u5251\u5df2\u65e0\u58f0\u606f\u5230\u4e86\u4ed6\u53cc\u76ee\u4e4b\u95f4\uff0c\u5251\u6c14\u5df2\u9488\u4e00\u822c\u523a\u5165\u4e86\u4ed6\u773c\u775b \u3002 \u3000\u3000\u4ed6\u521a\u95ed\u4e0a\u773c\u775b\uff0c\u75bc\u75db\u5df2\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8bf8\u845b\u521a\u53ea\u770b\u5230\u94c1\u5251\u4e00\u6325\uff0c\u9ad8\u884c\u7a7a\u7709\u5fc3\u7684\u8840\u5c31\u5df2\u7bad\u4e00\u822c\u6807\u51fa\uff0c\u975e\u4f46\u6ca1\u6709\u62db\u67b6\uff0c\u4e5f\u6ca1\u6709\u95ea\u907f \u3002 \u3000\u3000\u53ef\u662f\u8fd9\u65f6\u4ed6\u5df2\u6ca1\u6709\u601d\u7d22\u7684\u4f59\u5730\uff0c\u4ed6\u53ea\u89c9\u4e00\u9635\u782d\u4eba\u808c\u4f53\u7684\u5bd2\u6c14\u88ad\u6765\uff0c\u5f53\u4e0b\u5927\u559d\u4e00\u58f0\uff0c\u94c1\u62d0 \u5e26\u7740\u98ce\u58f0\u6a2a\u626b\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u53f7\u79f0\u6a2a\u626b\u5343\u519b\uff0c\u4ee5\u6a2a\u626b\u5343\u519b\u6210\u540d\uff0c\u8fd9\u4e00\u62db\u6a2a\u626b\u5343\u519b\u4f7f\u51fa\u6765\uff0c\u5b9e\u5728\u662f\u795e\u5145\u6c14\u8db3\uff0c\u5a01\u4e0d\u53ef \u6321\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u94c1\u5251\u53cd\u624b\u6325\u51fa\u3002 \u3000\u3000\u53ea\u542c\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u516d\u5341\u4e09\u65a4\u7684\u91d1\u521a\u94c1\u62d0\u8fce\u7740\u5251\u950b\u4fbf\u5df2\u65ad\u6210\u4e24\u622a\uff0c\u94c1\u5251\u4f59\u52bf\u66f4\u731b \uff01 \u3000\u3000\u8bf8\u845b\u521a\u4f46\u89c9\u9762\u76ee\u4e00\u5bd2\uff0c\u4e5f\u4e0d\u518d\u6709\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u53ea\u4e0d\u8fc7\u662f\u9877\u523b\u95f4\u4e8b\u3002\u897f\u95e8\u67d4\u5ffd\u7136\u4ef0\u5929\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1b\u770b\u6765\u4eca\u65e5\u6c5f\u6e56\uff0c\u5df2\u65e0\u6211\u897f \u95e8\u67d4\u4e89\u96c4\u4e4b\u5730\u4e86\u2014\u2014 \u3000\u3000\u4ed6\u8dfa\u4e86\u811a\uff0c\u51b2\u5929\u63a0\u8fc7\uff0c\u53ea\u4e00\u95ea\u4fbf\u5df2\u6d88\u5931\u5728\u5c4b\u810a\u540e\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u521a\u63a0\u8d77\uff0c\u4e0a\u5b98\u98de\u8eab\u5f62\u4e5f\u5c55\u52a8\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5251\u6c14\u5df2\u6251\u9762\u800c\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u957f\u5578\u4e00\u58f0\uff0c\u638c\u4e2d\u5b50\u6bcd\u94a2\u73af\u7a81\u51fa\u3002 \u3000\u3000\u53c8\u662f\u53ee\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u94a2\u73af\u7adf\u5c06\u94c1\u5251\u751f\u751f\u5939\u4f4f\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8f7b\u9053\uff1a\u597d\uff01 \u3000\u3000\u597d\u5b57\u51fa\u53e3\uff0c\u4ed6\u94c1\u5251\u4e00\u6a2a\uff0c\u94a2\u73af\u9f50\u65ad\u3002 \u3000\u3000\u5251\u5df2\u903c\u4f4f\u4e86\u4e0a\u5b98\u98de\u54bd\u5589\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u9762\u4e0a\u4ecd\u662f\u51b7\u51b7\u6de1\u6de1\uff0c\u5168\u65e0\u8868\u60c5\uff0c\u8fd9\u5c11\u5e74\u7684\u5fc3\u80a0\u5c31\u50cf\u662f\u94c1\u77f3\u6240\u94f8\uff0c\u65e2 \u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u60ca\u614c\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u6050\u60e7\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u76ef\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u4f60\u53ef\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u95e8\u4e0b\u5f1f\u5b50\uff1f \u3000\u3000\u4e0a\u5b98\u98de\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u6211\u5251\u4e0b\u672c\u6765\u65e0\u6d3b\u53e3\uff0c\u4f46\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u80fd\u63a5\u6211\u4e00\u5251\u4e5f\u7b97\u4e0d\u6613\u2014\u2014 \u3000\u3000\u4ed6\u5e73\u8f6c\u5251\u8f6c\uff0c\u8f7b\u8f7b\u5728\u4e0a\u5b98\u98de\u80a9\u5934\u4e00\u62cd\uff0c\u9053\uff1a\u9976\u4f60\u53bb\u5427\uff01 \u3000\u3000\u4e0a\u5b98\u98de\u8fd8\u662f\u7ad9\u7740\u4e0d\u52a8\uff0c\u7f13\u7f13\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u77aa\u7740\u9ed1\u8863\u4eba\u9053\uff1a\u4f60\u867d\u4e0d\u6740\u6211\uff0c\u4f46\u6709\u53e5\u6211\u5374\u8981\u5bf9 \u4f60\u8bf4\u660e\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u4f60\u8bf4\u5427\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u4eca\u65e5\u4f60\u867d\u653e\u4e86\u6211\uff0c\u4ed6\u65e5\u6211\u5374\u5fc5\u62a5\u6b64\u4ec7\uff0c\u5230\u90a3\u65f6\u6211\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\uff01 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u7a81\u7136\u4ef0\u5929\u5927\u7b11\u8d77\u6765\uff0c\u597d\uff0c\u679c\u7136\u4e0d\u6127\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u513f\u5b50\u2014\u2014 \u3000\u3000\u4ed6\u7b11\u58f0\u9aa4\u7136\u505c\u987f\uff0c\u77aa\u7740\u4e0a\u5b98\u98de\u9053\uff1a\u4ed6\u65e5\u4f60\u82e5\u80fd\u4ee4\u6211\u6b7b\u5728\u4f60\u624b\u4e0a\uff0c\u6211\u975e\u4f46\u7edd\u4e0d\u602a\u4f60\uff0c\u800c\u4e14 \u8fd8\u4f1a\u5f15\u4ee5\u4e3a\u50b2\uff0c\u56e0\u4e3a\u6bd5\u7adf\u6ca1\u6709\u770b\u9519\u4e86\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u9762\u4e0a\u4ecd\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u5728\u4e0b\u5c31\u544a\u8f9e\u4e86\uff01 \u3000\u3000\u9ed1\u8863\u4eba\u6325\u624b\u9053\uff1a\u4f60\u597d\u597d\u5e72\u53bb\u5427\uff0c\u6211\u7b49\u7740\u4f60\uff01 \u3000\u3000\u9ed1\u8863\u4eba\u7a81\u7136\u53c8\u559d\u9053\uff1a\u4e14\u6162\uff01 \u3000\u3000\u4e0a\u5b98\u98de\u6162\u6162\u5730\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u4f60\u8bb0\u5f97\uff0c\u4eca\u65e5\u6211\u653e\u4f60\uff0c\u5e76\u975e\u56e0\u4e3a\u4f60\u662f\u4e0a\u5b98\u91d1\u8679\u4e4b\u5b50\uff0c\u800c\u662f\u56e0\u4e3a\u4f60\u81ea\u5df1\uff01 \u3000\u3000\u4e0a\u5b98\u98de\u6ca1\u6709\u52a0\u51cf\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u76ee\u9001\u7740\u4e0a\u5b98\u98de\u7684\u80cc\u5f71\uff0c\u826f\u4e45\u8f6c\u8fc7\u8eab\uff0c\u6de1\u9053\uff1a\u4eca\u65e5\u76f8\u89c1\uff0c\u65e0\u4ee5\u4e3a\u656c\uff0c\u8c28\u4ee5\u6b64\u4e8c\u4eba\u4e3a \u656c\uff0c\u804a\u8868\u5bf8\u5fc3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u7740\uff0c\u51dd\u6ce8\u7740\u4ed6\u638c\u4e2d\u94c1\u5251\uff0c\u5ffd\u7136\u9053\uff1a\u5d69\u9633\u94c1\u5251\uff1f \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u6b63\u662f\u90ed\u5d69\u9633\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5d69\u9633\u94c1\u5251\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff01 \u3000\u3000\u90ed\u5d69\u9633\u4e5f\u4fef\u9996\u51dd\u6ce8\u7740\u81ea\u5df1\u638c\u4e2d\u7684\u94c1\u5251\uff0c\u9053\uff1a\u5374\u4e0d\u77e5\u5d69\u9633\u94c1\u5251\u6bd4\u8d77\u5c0f\u674e\u98de\u5200\u53c8\u5982\u4f55\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u5012\u4e0d\u60f3\u77e5\u9053\u8fd9\u7b54\u6848\u3002 \u3000\u3000\u90ed\u5d69\u9633\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u2014\u2014\u4f60\u6211\u65e0\u8bba\u8c01\u60f3\u77e5\u9053\u8fd9\u7b54\u6848\uff0c\u53ea\u6015\u90fd\u8981\u540e\u6094\u7684\u3002 \u3000\u3000\u90ed\u5d69\u9633\u970d\u7136\u62ac\u5934\u3002 \u3000\u3000\u4ed6\u7070\u8272\u7684\u8138\u4e0a\uff0c\u4f3c\u5df2\u8d77\u4e86\u6fc0\u52a8\u7684\u7ea2\u6655\uff0c\u5927\u58f0\u9053\uff1a\u4f46\u8fd9\u4ef6\u4e8b\u8fdf\u65e9\u8fd8\u662f\u8981\u5f04\u660e\u7684\uff0c\u662f\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u7740\uff0c\u5583\u5583\u9053\uff1a\u6211\u53ea\u5e0c\u671b\u8d8a\u8fdf\u8d8a\u597d\u2014\u2014 \u3000\u3000\u90ed\u5d69\u9633\u5389\u58f0\u9053\uff1a\u6211\u5012\u5e0c\u671b\u8d8a\u65e9\u8d8a\u597d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f60\u6211\u4e00\u65e5\u4e0d\u5206\u9ad8\u4e0b\uff0c\u6211\u5c31\u4e00\u65e5\u4e0d\u80fd\u5b89\u5fc3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u9053\uff1a\u4f60\u60f3\u5728\u4ec0\u4e48\u65f6\u5019\uff1f \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u5c31\u5728\u4eca\u65e5\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5c31\u5728\u6b64\u5730\uff1f \u3000\u3000\u90ed\u5d69\u9633\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u51b7\u7b11\u9053\uff1a\u6b64\u95f4\u672c\u662f\u4f60\u7684\u65e7\u5c45\uff0c\u82e5\u5728\u6b64\u5730\u4e0e\u4f60\u4ea4\u624b\uff0c\u5df2\u88ab\u4f60\u5148\u5360\u4e86 \u5730\u5229\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\u9053\uff1a\u4e0d\u9519\uff0c\u5c31\u51ed\u8fd9\u53e5\u8bdd\uff0c\u9601\u4e0b\u5df2\u4e0d\u6127\u4e3a\u7edd\u9876\u9ad8\u624b\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f46\u65f6\u95f4\u65e2\u5df2\u7531\u6211\u6765\u9009\uff0c\u5730\u65b9\u8be5\u7531\u4f60\u6765\u51b3\u5b9a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u90a3\u5012\u4e5f\u4e0d\u5fc5\u3002 \u3000\u3000\u90ed\u5d69\u9633\u4e5f\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u624d\u65ad\u7136\u9053\uff1a\u597d\uff0c\u65e2\u662f\u5982\u6b64\uff0c\u8bf7\u968f\u6211\u6765\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8bf7\u3002 \u3000\u3000\u4ed6\u8d70\u4e86\u4e24\u6b65\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u56de\u5934\u5411\u5c0f\u697c\u4e0a\u671b\u4e86\u4e00\u773c\u3002\u4ed6\u8fd9\u624d\u53d1\u73b0\u9f99\u5c0f\u4e91\u4e00\u76f4\u72e0\u72e0\u5730\u76ef\u7740\u4ed6 \uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\u4e4b\u8272\u3002 \u3000\u3000\u90ed\u5d69\u9633\u7684\u94c1\u5251\u65e0\u8bba\u591a\u795e\u5999\uff0c\u8bf8\u845b\u521a\u65e0\u8bba\u6b7b\u5f97\u591a\u4e48\u60e8\uff0c\u672a\u80fd\u4f7f\u8fd9\u5b69\u5b50\u7684\u76ee\u5149\u79fb\u5f00\u7247\u523b\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u4e00\u770b\u5230\u4ed6\uff0c\u4ed6\u7acb\u523b\u5c31\u7b11\u4e86\uff0c\u8eac\u8eab\u9053\uff1a\u674e\u5927\u53d4\uff0c\u4f60\u8001\u4eba\u5bb6\u597d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6697\u4e2d\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u4f60\u597d\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u5bb6\u6bcd\u65f6\u65f6\u523b\u523b\u5728\u60e6\u8bb0\u7740\u4f60\u8001\u4eba\u5bb6\uff0c\u5927\u4f60\u5e94\u8be5\u5e38\u6765\u770b\u770b\u6211\u4eec\u624d\u662f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u7684\u8bdd\uff0c\u5e38\u5e38\u90fd\u4f7f\u4ed6\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u624d\u597d\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u7a81\u7136\u62c9\u4f4f\u4e86\u4ed6\u7684\u8863\u8896\uff0c\u6084\u58f0\u9053\uff1a\u90a3\u4eba\u770b\u6837\u5b50\u5f88\u51f6\u6076\uff0c\u5927\u53d4\u8fd8\u662f\u83ab\u8981 \u8ddf\u4ed6\u53bb\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u957f\u5927\u4e86\u5c31\u4f1a\u77e5\u9053\uff0c\u6709\u4e9b\u4e8b\u4f60\u7eb5\u7136\u4e0d\u613f\u610f\u53bb\u505a\uff0c\u5374\u4e5f\u975e\u505a\u4e0d\u53ef\u7684\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u2014\u2014\u5927\u53d4\u4f60\u82e5\u4e07\u4e00\u6709\u4e2a\u4e09\u957f\u4e24\u77ed\uff0c\u8fd8\u6709\u8c01\u4f1a\u6765\u4fdd\u62a4\u6211\u4eec\u6bcd\u5b50\u4e24 \u4eba\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u518d\u6b21\u62ac\u8d77\u5934\u7684\u65f6\u5019\uff0c\u624d\u53d1\u73b0\u6797\u8bd7\u97f3\u4e0d\u77e5\u4f55\u65f6\u5df2\u51fa\u73b0\u5728\u697c\u5934\uff0c\u6b63\u4fef\u9996 \u51dd\u6ce8\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u5979\u76ee\u4e2d\u867d\u6709\u53d9\u4e0d\u5c3d\u7684\u6028\u82e6\uff0c\u5374\u53c8\u5e26\u7740\u4e9b\u6b23\u6170\u4e4b\u8272\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u5fc3\u91cc\u4e00\u9635\u523a\u75db\uff0c\u7adf\u4e0d\u6562\u518d\u62ac\u5934\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u5df2\u9ad8\u58f0\u5524\u9053\uff1a\u5988\uff0c\u4f60\u770b\uff0c\u674e\u5927\u53d4\u521a\u6765\u5c31\u8981\u8d70\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u674e\u5927\u53d4\u6709\u4e8b\uff0c\u4ed6\u2014\u2014\u4ed6\u4e0d\u80fd\u4e0d\u8d70\u7684\u3002 \u3000\u3000\u5979\u7684\u7b11\u5bb9\u6301\u6765\u662f\u90a3\u4e48\u51c4\u51c9\uff0c\u90a3\u4e48\u5e7d\u6028\uff0c\u674e\u5bfb\u6b22\u6b64\u523b\u82e5\u662f\u62ac\u5934\u770b\u5230\uff0c\u4ed6\u7684\u5fc3\u53ea\u6015\u8981\u788e\u4e86\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u5988\uff0c\u4f60\u96be\u9053\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8981\u8ddf\u4f60\u5927\u53d4\u8bf4\u4e48\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u7684\u5634\u5507\u8f7b\u8f7b\u98a4\u6296\u7740\uff0c\u9053\uff1a\u6709\u4ec0\u4e48\u8bdd\u7b49\u4ed6\u56de\u6765\u65f6\u518d\u8bf4\u4e5f\u4e0d\u8fdf\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u561f\u8d77\u4e86\u5634\uff0c\u7728\u7740\u773c\u9053\uff1a\u6211\u770b\u2014\u2014\u674e\u5927\u53d4\u8fd9\u4e00\u53bb\uff0c\u53ea\u6015\u5c31\u518d\u4e5f\u4e0d\u56de\u6765\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u8f7b\u9053\uff1a\u80e1\u8bf4\uff01\u5feb\u4e0a\u6765\uff0c\u8ba9\u674e\u5927\u53d4\u8d70\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\uff0c\u653e\u5f00\u674e\u5bfb\u6b22\u7684\u8863\u8896\uff0c\u5782\u9996\u9053\uff1a\u597d\uff0c\u5927\u53d4\u4f60\u8d70\u5427\uff0c\u4e5f\u4e0d\u5fc5\u518d\u8bb0\u6302\u6211 \u4eec\uff0c\u6211\u6bcd\u5b50\u53cd\u6b63\u662f\u65e0\u4f9d\u65e0\u9760\u60ef\u4e86\uff0c\u90fd\u4e0d\u5fc5\u4e3a\u6211\u4eec\u62c5\u5fc3\u3002 \u3000\u3000\u4ed6\u63c9\u7740\u773c\u775b\uff0c\u4f3c\u5df2\u5728\u557c\u54ed\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5df2\u8d70\u4e0a\u4e86\u5c0f\u6865\u5934\uff0c\u6b63\u62b1\u7740\u624b\u5728\u51b7\u51b7\u5730\u77a7\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7ec8\u4e8e\u8f6c\u8eab\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u65e2\u6ca1\u6709\u62ac\u5934\u77a7\u4e00\u773c\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u65e0\u8bba\u8bf4\u4ec0\u4e48\u90fd\u5df2\u662f\u591a\u4f59\u7684\uff0c\u4f55\u51b5\uff0c\u4ed6\u4e5f\u6839\u672c\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u6562\u518d\u770b\u6797 \u8bd7\u97f3\u7684\u773c\u8272\u3002 \u3000\u3000\u4e00\u4e2a\u82e5\u7528\u60c5\u592a\u4e13\uff0c\u770b\u6765\u53cd\u5012\u4f3c\u65e0\u60c5\u4e86\u3002 \u3000\u3000\u5899\u5916\u7684\u79cb\u8272\u4f3c\u4e4e\u6bd4\u5899\u5185\u66f4\u6d53\u3002 \u3000\u3000\u90ed\u5d69\u9633\u53cc\u624b\u7f29\u5728\u8863\u8896\u4e2d\uff0c\u6162\u6162\u5730\u5728\u524d\u9762\u8d70\u7740\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u9ed8\u5730\u8ddf\u7740\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u8def\u5f88\u957f\uff0c\u7a84\u800c\u66f2\u6298\uff0c\u4e5f\u4e0d\u77e5\u5c3d\u5934\u5904\u5728\u54ea\u91cc\u3002 \u3000\u3000\u79cb\u98ce\u745f\u745f\uff0c\u8def\u65c1\u7684\u8349\u8272\u5df2\u67af\u9ec4\u3002 \u3000\u3000\u90ed\u5d69\u9633\u8d70\u5f97\u867d\u6162\uff0c\u6b65\u5b50\u5374\u5f88\u5927\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u51dd\u6ce8\u7740\u4ed6\u7684\u811a\u6b65\uff0c\u4f3c\u770b\u5f97\u51fa\u795e\u3002 \u3000\u3000\u8def\u4e0a\u7684\u571f\u8d28\u5f88\u677e\uff0c\u90ed\u5d69\u9633\u6bcf\u8d70\u4e00\u6b65\uff0c\u5c31\u7559\u4e0b\u4e2a\u6d45\u6d45\u7684\u811a\u5370\uff0c\u6bcf\u4e2a\u811a\u5370\u7684\u6df1\u6d45\u90fd\u5b8c\u5168\u4e00\u6837 \u3002 \u3000\u3000\u6bcf\u4e2a\u811a\u6b65\u95f4\u7684\u8ddd\u79bb\u4e5f\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u770b\u6765\u867d\u4f3c\u5728\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8d70\u7740\uff0c\u5176\u5b9e\u5374\u6b63\u5728\u6697\u4e2d\u50ac\u52a8\u7740\u8eab\u4f53\u5185\u7684\u5185\u529b\uff0c\u4ed6\u7684\u624b\u8db3\u56db\u80a2\u5df2 \u5b8c\u5168\u534f\u8c03\u3002\u662f\u4ee5\u4ed6\u6bcf\u4e00\u6b65\u8e0f\u51fa\uff0c\u90fd\u7edd\u4e0d\u4f1a\u5dee\u9519\u5206\u6beb\u3002 \u3000\u3000\u7b49\u4ed6\u7684\u5185\u529b\u50ac\u52a8\u5230\u6781\u81f4\uff0c\u8eab\u4f53\u56db\u80a2\u7684\u914d\u5408\u534f\u8c03\u4e5f\u5230\u4e86\u5dc5\u5cf0\u65f6\uff0c\u4ed6\u7acb\u523b\u5c31\u4f1a\u505c\u4e0b\u6765\u2014\u2014\u90a3 \u5c31\u662f\u8def\u7684\u5c3d\u5934\u3002 \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u77e5\u5df1\u4ec7\u654c>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u77e5\u5df1\u4ec7\u654c \u3000\u3000\u5230\u4e86\u90a3\u91cc\uff0c\u4ed6\u4eec\u4e24\u4eba\u4e2d\u5c31\u6709\u4e00\u4eba\u7684\u751f\u547d\u4e5f\u5230\u4e86\u5c3d\u5934\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5f88\u660e\u767d\u8fd9\u70b9\u3002 \u3000\u3000\u90ed\u5d69\u9633\u7684\u786e\u662f\u5f88\u53ef\u6015\u7684\u5bf9\u624b\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u8bb8\u76f4\u5230\u4eca\u5929\u624d\u9047\u7740\u4e2a\u771f\u6b63\u7684\u5bf9\u624b\uff01 \u3000\u3000\u6240\u4ee5\u6709\u4eba\u4e0d\u60dc\u201c\u6c42\u8d25\u201d\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u53ea\u8981\u80fd\u9047\u7740\u4e00\u4e2a\u771f\u6b63\u7684\u5bf9\u624b\uff0c\u7eb5\u7136\u8d25\u4e86\uff0c\u4e5f\u662f\u6109\u5feb \u7684\u3002 \u3000\u3000\u4f46\u674e\u589e\u6b22\u6b64\u523b\u7684\u5fc3\u60c5\u5374\u4e00\u70b9\u4e5f\u4e0d\u6109\u5feb\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u4e71\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ee5\u81ea\u5df1\u6b64\u523b\u8fd9\u79cd\u5fc3\u60c5\uff0c\u53bb\u548c\u90ed\u5d69\u9633\u8fd9\u6837\u7684\u5bf9\u624b\u6597\uff0c\u80dc\u7b97\u5b9e\u5728\u4e0d\u591a\uff0c\u81ea\u5df1\u8fd9\u4e00\u53bb\uff0c \u80fd\u56de\u6765\u7684\u673a\u4f1a\u53ea\u6015\u5f88\u5c11\u3002 \u3000\u3000\u8fd9\u6761\u8def\u7684\u5c3d\u5934\u5904\uff0c\u4e5f\u8bb8\u5c31\u662f\u4ed6\u751f\u547d\u7684\u5c3d\u5934\u5904\uff01 \u3000\u3000\u8fd9\u6761\u8def\u4e5f\u8bb8\u5c31\u662f\u4ed6\u7684\u6b7b\u8def\uff01 \u3000\u3000\u4ed6\u5e76\u4e0d\u6015\u6b7b\uff0c\u53ef\u662f\u4ed6\u73b0\u5728\u80fd\u6b7b\u4e48\uff1f \u3000\u3000\u56db\u91ce\u8d8a\u6765\u8d8a\u7a7a\u65f7\uff0c\u8fdc\u8fdc\u53ef\u4ee5\u671b\u89c1\u4e00\u7247\u67ab\u6797\u3002 \u3000\u3000\u67ab\u53f6\u7ea2\u5982\u8840\uff01 \u3000\u3000\u201c\u96be\u9053\u90a3\u5c31\u662f\u8def\u7684\u5c3d\u5934\uff1f\u201d \u3000\u3000\u90ed\u5d69\u9633\u7684\u6b65\u5b50\u8d8a\u6765\u8d8a\u5927\uff0c\u7559\u4e0b\u6765\u7684\u811a\u5370\u5374\u8d8a\u6765\u8d8a\u6de1\u4e86\uff0c\u663e\u89c1\u4ed6\u8eab\u4f53\u5185\u5916\u4e00\u5207\u90fd\u5df2\u6e10\u6e10\u5230 \u8fbe\u5dc5\u5cf0\u3002 \u3000\u3000\u5230\u90a3\u65f6\uff0c\u4ed6\u7684\u7cbe\u795e\u3001\u5185\u529b\u3001\u8089\u4f53\uff0c\u90fd\u5c06\u548c\u4ed6\u7684\u5251\u878d\u800c\u4e3a\u4e00\uff0c\u4ed6\u7684\u5251\u5c31\u5df2\u4e0d\u518d\u662f\u65e0\u77e5\u7684\u94a2 \u94c1\uff0c\u800c\u6709\u4e86\u7075\u6027\u3002 \u3000\u3000\u5230\u90a3\u65f6\uff0c\u4ed6\u4e00\u5251\u523a\u51fa\uff0c\u5fc5\u5c06\u662f\u65e0\u575a\u4e0d\u6467\u3001\u52bf\u4e0d\u53ef\u6321\u7684\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u53d1\u51fa\u4e1d\u6beb\u58f0\u97f3\uff0c\u4f46\u90ed\u5d69\u9633\u5374\u5df2\u611f\u89c9\u5230\u4e86\uff0c\u7cbe\u795e\u5df2\u8fdb\u5165\u865a\u660e\uff0c\u5df2\u6d51 \u7136\u5fd8\u6211\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u56de\u5934\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u5c31\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u4eca\u5929\u2014\u2014\u6211\u4e0d\u80fd\u548c\u4f60\u4ea4\u624b\uff01 \u3000\u3000\u90ed\u5d69\u9633\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u76ee\u5149\u5200\u4e00\u822c\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5782\u4e0b\u4e86\u5934\uff0c\u5fc3\u5728\u523a\u75db\u7740\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5230\u4e86\u8fd9\u65f6\u518d\u8bf4\u4e0d\u80fd\u4ea4\u624b\uff0c\u5b9e\u65e0\u5f02\u4e34\u9635\u8131\u9003\uff0c\u8fd9\u79cd\u4e8b\u4ed6\u672c\u6765\u5b81\u53ef\u6b7b\u4e5f\u4e0d\u80af\u505a\u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5374\u975e\u505a\u4e0d\u53ef\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5389\u58f0\u9053\uff1a\u4f60\u8bf4\u4f60\u4e0d\u80fd\u548c\u6211\u4ea4\u624b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u65e0\u8a00\u5730\u70b9\u4e86\u5934\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u627f\u8ba4\u8d25\u4e86\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5f20\u5927\u4e86\u773c\u775b\uff0c\u77aa\u7740\u4ed6\uff0c\u5c31\u50cf\u662f\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u826f\u4e45\uff0c\u90ed\u5d69\u9633\u5ffd\u4e5f\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u674e\u5bfb\u6b22\uff0c\u4f60\u679c\u7136\u4e0d\u6127\u4e3a\u5f53\u4e16\u7684\u82f1\u96c4\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u82f1\u96c4\uff1f\u50cf\u6211\u8fd9\u6837\u7684\u4eba\u80fd\u7b97\u662f\u82f1\u96c4\uff1f \u3000\u3000\u90ed\u5d69\u9633\u6447\u4e86\u6447\u5934\uff0c\u53f9\u606f\u7740\u9053\uff1a\u666e\u5929\u4e4b\u4e0b\uff0c\u4e5f\u8bb8\u53ea\u6709\u4f60\u624d\u80fd\u7b97\u5f97\u4e0a\u662f\u82f1\u96c4\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u90ed\u5d69\u9633\u5df2\u63a5\u7740\u9053\uff1a\u4f60\u8bf4\u4f60\u627f\u8ba4\u8d25\u4e86\uff0c\u662f\u4e48\u2014\u2014\u4f46\u6211\u5374\u77e5\u9053\u4e00\u4e2a\u4eba\u80af \u8ba4\u8f93\u65f6\u9700\u8981\u591a\u5927\u7684\u52c7\u6c14\uff0c\u8fd9\u53e5\u8bdd\u6211\u4e5f\u8bb8\u5b81\u6b7b\u4e5f\u4e0d\u613f\u8bf4\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u4f46\u6b7b\u5374\u5bb9\u6613\u591a\u4e86\uff0c\u80fd\u4e3a\u4e86\u522b\u4eba\u800c\u5b81\u53ef\u81ea\u5df1\u8ba4\u8f93\uff0c\u81ea\u5df1\u53d7\u59d4\u5c48\uff0c\u8fd9\u624d \u662f\u771f\u6b63\u7684\u82f1\u96c4\uff01\u771f\u6b63\u7684\u7537\u5b50\u6c49\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u2014\u2014 \u3000\u3000\u4ed6\u53ea\u89c9\u5fc3\u5934\u6fc0\u52a8\uff0c\u4e0d\u80fd\u81ea\u5df1\uff0c\u53ea\u8bf4\u4e00\u4e2a\u5b57\u5589\u5499\u5c31\u4f3c\u5df2\u88ab\u585e\u4f4f\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u5f88\u4e86\u89e3\u4f60\uff0c\u4f60\u8bf4\u4f60\u4e0d\u80fd\u548c\u6211\u4ea4\u624b\uff0c\u53ea\u56e0\u4f60\u89c9\u5f97\u4f60\u81ea\u5df1\u73b0\u5728\u8fd8\u4e0d\u80fd\u6b7b\uff0c\u4f60\u77e5 \u9053\u8fd8\u6709\u4eba\u9700\u8981\u4f60\u7167\u987e\uff0c\u4f60\u4e0d\u80fd\u629b\u4e0b\u5979\u4e0d\u7ba1\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u4e0d\u8bed\uff0c\u70ed\u6cea\u51e0\u4e4e\u5c06\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4e00\u4e2a\u6700\u53ef\u9760\u7684\u670b\u53cb\uff0c\u56fa\u7136\u5f80\u5f80\u66fe\u662f\u4f60\u6700\u53ef\u6015\u7684\u4ec7\u654c\uff0c\u4f46\u4e00\u4e2a\u53ef\u6015\u7684\u5bf9\u624b\uff0c\u5f80\u5f80\u4e5f\u4f1a\u662f\u4f60 \u6700\u77e5\u5fc3\u7684\u670b\u53cb\u3002 \u3000\u3000\u56e0\u4e3a\u6709\u8d44\u683c\u505a\u4f60\u5bf9\u624b\u7684\u4eba\uff0c\u624d\u6709\u8d44\u683c\u505a\u4f60\u7684\u77e5\u5df1\u3002 \u3000\u3000\u56e0\u4e3a\u53ea\u6709\u8fd9\u79cd\u4eba\u624d\u80fd\u4e86\u89e3\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u9ad8\u5174\uff1f\u662f\u96be\u53d7\uff1f\u8fd8\u662f\u611f\u6fc0\uff1f\u53ea\u4e0d\u8fc7\u65e0\u8bba\u662f\u54ea\u79cd\u611f\u60c5\uff0c\u90fd\u662f\u4ed6\u65e0\u6cd5\u8bf4 \u51fa\u53e3\u7684\u3002 \u3000\u3000\u90ed\u5d69\u9633\u53c8\u9053\uff1a\u4f46\u6211\u4eca\u65e5\u8fd8\u662f\u975e\u548c\u4f60\u4ea4\u624b\u4e0d\u53ef\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6123\u4e86\u6123\uff0c\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u90ed\u5d69\u9633\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u666e\u5929\u4e4b\u4e0b\uff0c\u53c8\u6709\u51e0\u4e2a\u674e\u5bfb\u6b22\uff1f\u4eca\u65e5\u6211\u82e5\u4e0d\u4e0e\u4f60\u4ea4\u624b\uff0c\u4ed6\u65e5\u518d\u60f3\u627e \u4f60\u8fd9\u6837\u5bf9\u624b\uff0c\u53ea\u6015\u662f\u6c38\u8fdc\u627e\u4e0d\u5230\u7684\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u8981\u6b64\u95f4\u4e8b\u4e86\uff0c\u9601\u4e0b\u4ed6\u65e5\u76f8\u9080\uff0c\u6211\u968f\u65f6\u5949\u966a\u3002 \u3000\u3000\u90ed\u5d69\u9633\u6447\u4e86\u6447\u5934\u9053\uff1a\u5230\u90a3\u65f6\uff0c\u4f60\u6211\u53ea\u6015\u66f4\u65e0\u6cd5\u4ea4\u624b\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u90ed\u5d69\u9633\u76ee\u5149\u79fb\u5411\u8fdc\u65b9\uff0c\u8fdc\u65b9\u5728\u4e0a\u6b63\u6709\u6735\u767d\u4e91\u5189\u5189\u98d8\u52a8\u3002 \u3000\u3000\u4ed6\u9762\u4e0a\u5e26\u7740\u5fae\u7b11\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u5230\u90a3\u65f6\uff0c\u4f60\u6211\u8bf4\u4e0d\u5b9a\u5df2\u6210\u4e86\u670b\u53cb\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9eef\u7136\u9053\uff1a\u5b81\u53ef\u4e0e\u6211\u4e3a\u654c\uff0c\u5374\u4e0d\u613f\u505a\u6211\u7684\u670b\u53cb\uff1f \u3000\u3000\u90ed\u5d69\u9633\u6c89\u4e0b\u4e86\u8138\uff0c\u5389\u58f0\u9053\uff1a\u90ed\u67d0\u6b64\u751f\u5df2\u732e\u4e0e\u6b66\u9053\uff0c\u54ea\u6709\u4f59\u529b\u518d\u4ea4\u670b\u53cb\uff1f\u4f55\u51b5\u2014\u2014 \u3000\u3000\u4ed6\u8bed\u58f0\u6e10\u6e10\u7f13\u548c\uff0c\u63a5\u7740\u9053\uff1a\u670b\u53cb\u6613\u5f97\uff0c\u80fd\u809d\u80c6\u76f8\u7167\u7684\u5bf9\u624b\u5374\u65e0\u5904\u53ef\u5bfb\u2014\u2014 \u3000\u3000\u8fd9\u201c\u809d\u80c6\u76f8\u7167\u201d\u56db\u5b57\uff0c\u672c\u662f\u7528\u6765\u5f62\u5bb9\u670b\u53cb\u7684\uff0c\u4ed6\u6b64\u523b\u5374\u7528\u6765\u5f62\u5bb9\u4ec7\u654c\uff0c\u82e5\u662f\u522b\u4eba\u542c\u5230\uff0c \u975e\u4f46\u96be\u4ee5\u660e\u4e86\uff0c\u53ea\u6015\u8fd8\u4f1a\u53d1\u7b11\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u5f88\u4e86\u89e3\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u653e\u773c\u5929\u4e0b\u80fd\u4e0e\u6211\u4e00\u51b3\u751f\u6b7b\u7684\u5bf9\u624b\uff0c\u81ea\u7136\u4e0d\u6b62\u4f60\u4e00\u4eba\uff0c\u4f46\u6b66\u529b\u7eb5\u7136\u5f3a\u80dc\u6211\u5341\u500d \u7684\u4eba\uff0c\u6211\u4e5f\u672a\u5fc5\u653e\u5728\u773c\u91cc\uff0c\u82e5\u8981\u6211\u6b7b\u5728\u4ed6\u4eec\u624b\u4e0a\uff0c\u66f4\u662f\u5fc3\u6709\u4e0d\u7518\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\uff0c\u8981\u627e\u4e2a\u80fd\u4ee4\u4f60\u5c0a\u656c\u7684\u670b\u53cb\u5e76\u4e0d\u56f0\u96be\uff0c\u8981\u627e\u4e2a\u80fd\u4ee4\u4f60\u5c0a\u656c\u7684\u4ec7\u654c\u5374\u592a\u96be \u4e86\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5389\u58f0\u9053\uff1a\u6b63\u662f\u5982\u6b64\uff0c\u662f\u4ee5\u4eca\u65e5\u4f60\u6211\u4e00\u6218\uff0c\u52bf\u5728\u5fc5\u884c\uff0c\u90ed\u5d69\u9633\u4eca\u65e5\u7eb5\u7136\u6b7b\u4e8e\u4f60\u624b\uff0c \u4ea6\u662f\u6b7b\u800c\u65e0\u61be\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u53ef\u662f\u6211\u2014\u2014 \u3000\u3000\u90ed\u5d69\u9633\u626c\u624b\u6253\u6d88\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u4f60\u7684\u610f\u601d\u6211\u90fd\u4e86\u89e3\uff0c\u4eca\u65e5\u4f60\u82e5\u4e0d\u5e78\u6218\u6b7b\uff0c\u4f60\u7684\u672a\u4e86\u5fc3\u613f \uff0c\u6211\u5fc5\u66ff\u4f60\u5b8c\u6210\uff0c\u4f60\u6240\u8981\u4fdd\u62a4\u7684\u4eba\uff0c\u6211\u7edd\u4e0d\u5bb9\u4ed6\u4eba\u4f24\u53ca\u5979\u6beb\u53d1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u63d6\u5728\u5730\uff0c\u8083\u7136\u9053\uff1a\u5f97\u6b64\u4e00\u8a00\uff0c\u674e\u5bfb\u6b22\u6b7b\u6709\u4f55\u61be\uff1f\u2014\u2014\u591a\u8c22 \u3000\u3000\u4ed6\u751f\u5e73\u4ece\u672a\u5411\u4eba\u8bf4\u8fc7\u201c\u8c22\u201d\u5b57\uff0c\u6b64\u523b\u8fd9\u201c\u591a\u8c22\u201d\u4e8c\u5b57\u5374\u662f\u53d1\u81ea\u5fc3\u5e95\u7684\u3002 \u3000\u3000\u90ed\u5d69\u9633\u4e5f\u8fd8\u4e86\u63d6\uff0c\u8083\u7136\u9053\uff1a\u591a\u8c22\u6210\u5168\uff0c\u8bf7\uff01 \u3000\u3000\u674e\u5bfb\u6b22\uff1a\u8bf7\uff01 \u3000\u3000\u670b\u53cb\u95f4\u80fd\u4e92\u76f8\u5c0a\u656c\uff0c\u56fa\u7136\u53ef\u8d35\uff0c\u4f46\u4ec7\u654c\u95f4\u7684\u656c\u610f\u5374\u5f80\u5f80\u66f4\u96be\u5f97\uff0c\u4e5f\u66f4\u4ee4\u4eba\u611f\u52a8\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u8fd9\u79cd\u60c5\u611f\u6c38\u8fdc\u662f\u522b\u4eba\u6700\u96be\u4e86\u89e3\u7684\uff01 \u3000\u3000\u98ce\u5439\u8fc7\uff0c\u5377\u8d77\u4e86\u6f2b\u5929\u7ea2\u53f6\u3002 \u3000\u3000\u5251\u6c14\u88ad\u4eba\uff0c\u5929\u5730\u95f4\u5145\u6ee1\u4e86\u51c4\u51c9\u8083\u6740\u4e4b\u610f\u3002 \u3000\u3000\u90ed\u5d69\u9633\u53cd\u624b\u62d4\u5251\uff0c\u5e73\u4e3e\u5f53\u80f8\uff0c\u76ee\u5149\u59cb\u7ec8\u4e0d\u79bb\u674e\u5bfb\u6b22\u7684\u624b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u662f\u53ea\u53ef\u6015\u7684\u624b\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6b64\u523b\u5df2\u50cf\u662f\u53d8\u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u4ed6\u5934\u53d1\u867d\u7136\u662f\u90a3\u4e48\u84ec\u4e71\uff0c\u8863\u886b\u867d\u4ecd\u90a3\u4e48\u843d\u62d3\uff0c\u4f46\u770b\u6765 \u5df2\u4e0d\u518d\xd7\u5012\uff0c\u4e0d\u518d\u6194\u60b4\uff01 \u3000\u3000\u4ed6\u6194\u60b4\u7684\u8138\u4e0a\u5df2\u7115\u53d1\u51fa\u4e00\u79cd\u8000\u773c\u7684\u5149\u8f89\uff01 \u3000\u3000\u8fd9\u4e24\u5e74\u6765\uff0c\u4ed6\u5c31\u50cf\u662f\u4e00\u67c4\u88ab\u85cf\u5728\u5323\u4e2d\u7684\u5251\uff0c\u97ec\u5149\u517b\u6666\uff0c\u950b\u8292\u4e0d\u9732\uff0c\u6240\u4ee5\u6ca1\u6709\u80fd\u770b\u5230\u5b83\u707f \u70c2\u7684\u5149\u534e\uff01 \u3000\u3000\u6b64\u523b\u5251\u5df2\u51fa\u5323\u4e86\uff01 \u3000\u3000\u4ed6\u7684\u624b\u4f38\u51fa\uff0c\u624b\u91cc\u5df2\u591a\u4e86\u67c4\u5200\uff01 \u3000\u3000\u4e00\u5200\u5c01\u5589\uff0c\u4f8b\u65e0\u865a\u53d1\u7684\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u90ed\u5d69\u9633\u94c1\u5251\u8fce\u98ce\u6325\u51fa\uff0c\u4e00\u9053\u4e4c\u9ed1\u7684\u5bd2\u5149\u76f4\u53d6\u674e\u5bfb\u6b22\u54bd\u5589\u3002\u5251\u8fd8\u672a\u5230\uff0c\u68ee\u5bd2\u7684\u5251\u6c14\u5df2\u523a\u788e \u4e86\u897f\u98ce\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u811a\u6b65\u4e00\u6e9c\uff0c\u540e\u9000\u4e86\u4e03\u5c3a\uff0c\u80cc\u810a\u5df2\u8d34\u4e0a\u68f5\u6811\u5e72\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5251\u5251\u5df2\u968f\u7740\u53d8\u62db\uff0c\u7b14\u76f4\u523a\u51fa\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9000\u65e0\u53ef\u9000\uff0c\u8eab\u5b50\u5ffd\u7136\u6cbf\u7740\u6811\u5e72\u6ed1\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u90ed\u5d69\u9633\u957f\u5578\u4e00\u58f0\uff0c\u51b2\u5929\u98de\u8d77\uff0c\u94c1\u5251\u4e5f\u5316\u505a\u4e86\u4e00\u9053\u98de\u8679\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u4e0e\u5251\u5df2\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u903c\u4eba\u7684\u5251\u6c14\uff0c\u6467\u5f97\u679d\u5934\u7684\u7ea2\u53f6\u90fd\u98d8\u98d8\u843d\u4e0b\u3002 \u3000\u3000\u8fd9\u666f\u8c61\u51c4\u7edd\uff01\u4ea6\u8273\u7edd\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u53cc\u81c2\u4e00\u632f\uff0c\u5df2\u63a0\u8fc7\u4e86\u5251\u6c14\u98de\u8679\uff0c\u968f\u7740\u7ea2\u53f6\u98d8\u843d\u3002 \u3000\u3000\u90ed\u5d69\u9633\u957f\u5578\u4e0d\u7edd\uff0c\u51cc\u7a7a\u5012\u7ffb\uff0c\u4e00\u5251\u957f\u8679\u7a81\u7136\u5316\u505a\u4e86\u65e0\u6570\u5149\u5f71\uff0c\u5411\u674e\u5bfb\u6b22\u5f53\u5934\u6d12\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u4e4b\u5a01\uff0c\u5df2\u8db3\u4ee5\u9707\u6563\u4eba\u7684\u9b42\u9b44\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5468\u56f4\u65b9\u5706\u4e09\u4e08\u4e4b\u5185\uff0c\u5374\u5df2\u5728\u5251\u6c14\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u65e0\u8bba\u4efb\u4f55\u65b9\u5411\u95ea\u907f\uff0c\u90fd\u4f3c\u5df2\u95ea\u907f\u4e0d\u5f00 \u7684\u4e86\u3002 \u3000\u3000\u53ea\u542c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u624b\u91cc\u7684\u5c0f\u5200\uff0c\u7adf\u4e0d\u504f\u4e0d\u501a\u8fce\u4e0a\u4e86\u5251\u950b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6ee1\u5929\u5251\u6c14\u7a81\u7136\u6d88\u5931\u65e0\u5f71\uff0c\u8840\u96e8\u822c\u7684\u67ab\u53f6\u5374\u8fd8\u672a\u843d\u4e0b\uff0c\u90ed\u5d69\u9633\u6728\u4ea7\u7acb\u5728\u8840 \u96e8\u4e2d\uff0c\u4ed6\u7684\u5251\u4ecd\u5e73\u4e3e\u5f53\u80f8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5200\u4e5f\u8fd8\u5728\u624b\u4e2d\uff0c\u5200\u950b\u5374\u5df2\u88ab\u94c1\u5251\u6298\u65ad\uff01 \u3000\u3000\u4ed6\u9759\u9759\u5730\u671b\u7740\u90ed\u5d69\u9633\uff0c\u90ed\u5d69\u9633\u4e5f\u9759\u9759\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u9762\u4e0a\u90fd\u5168\u65e0\u4e1d\u6beb\u8868\u60c5\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u4eba\u5fc3\u91cc\u90fd\u77e5\u9053\uff0c\u674e\u5bfb\u6b22\u8fd9\u4e00\u5200\u5df2\u65e0\u6cd5\u51fa\u624b\u3002 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff0c\u6025\u5982\u95ea\u7535\uff0c\u5c31\u56e0\u4e3a\u5200\u950b\u7834\u98ce\uff0c\u5176\u52bf\u65b9\u6025\uff0c\u6b64\u523b\u5200\u950b\u65e2\u5df2\u6298\uff0c\u901f\u5ea6\u4fbf\u8981\u5927\u53d7\u5f71 \u54cd\u3002 \u3000\u3000\u5c0f\u674e\u98de\u5200\u7eb5\u7136\u51fa\u624b\uff0c\u4e5f\u662f\u65e0\u6cd5\u4f24\u4eba\u7684\u4e86\uff01 \u3000\u3000\u5e38\u80dc\u4e0d\u8d25\u7684\u5c0f\u674e\u98de\u5200\uff0c\u6b64\u523b\u7adf\u662f\u6709\u8d25\u65e0\u80dc\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u7f13\u7f13\u5782\u4e0b\uff01 \u3000\u3000\u6700\u540e\u7684\u4e00\u70b9\u67ab\u53f6\u788e\u7247\u5df2\u843d\u4e0b\uff0c\u67ab\u6797\u4e2d\u53c8\u6062\u590d\u4e86\u9759\u5bc2 \u3000\u3000\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9762\u4e0a\u867d\u4ecd\u65e0\u8868\u60c5\uff0c\u76ee\u4e2d\u5374\u5e26\u7740\u79cd\u8427\u7d22\u4e4b\u610f\uff0c\u9eef\u7136\u9053\uff1a\u6211\u8d25\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8c01\u8bf4\u4f60\u8d25\u4e86\uff1f \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u627f\u8ba4\u8d25\u4e86\uff01 \u3000\u3000\u4ed6\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u8fd9\u53e5\u8bdd\u6211\u672c\u6765\u4ee5\u4e3a\u6b7b\u4e5f\u4e0d\u80af\u8bf4\u7684\uff0c\u73b0\u5728\u8bf4\u51fa\u4e86\uff0c\u5fc3\u91cc\u53cd\u89c9\u75db\u5feb\u5f97\u5f88\uff0c \u75db\u5feb\u5f97\u5f88\uff0c\u75db\u5feb\u5f97\u5f88\u2014\u2014 \u3000\u3000\u4ed6\u4e00\u8fde\u8bf4\u4e86\u4e09\u904d\uff0c\u5ffd\u7136\u4ef0\u5929\u800c\u7b11\u3002 \u3000\u3000\u51c4\u51c9\u7684\u7b11\u58f0\u4e2d\uff0c\u4ed6\u5df2\u8f6c\u8eab\u5927\u6b65\u8d70\u51fa\u4e86\u67ab\u6797\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u9001\u4ed6\u8fdc\u53bb\uff0c\u53c8\u5f2f\u4e0b\u8170\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u7136\u4e00\u4eba\u62cd\u624b\u9053\uff1a\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d\u8d77\uff0c\u5b9e\u5728\u592a\u4e86\u4e0d\u8d77\u2014\u2014 \u3000\u3000\u58f0\u97f3\u6e05\u8106\uff0c\u5982\u51fa\u8c37\u9ec4\u83ba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ac\u8d77\u5934\uff0c\u7adf\u662f\u90a3\u8bf4\u4e66\u8001\u4eba\u7684\u5b59\u5973\u513f\u3002 \u3000\u3000\u5979\u8fde\u90a3\u53cc\u52a8\u4eba\u7684\u5927\u773c\u775b\u91cc\u90fd\u5e26\u7740\u7b11\u610f\uff0c\u9053\uff1a\u80fd\u770b\u5230\u4e24\u4f4d\u4eca\u65e5\u4e00\u6218\uff0c\u8fde\u6211\u4e5f\u6b7b\u800c\u65e0\u61be\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u8bb8\u8fd8\u6ca1\u6709\u8bf4\u8bdd\u7684\u5fc3\u60c5\uff0c\u6240\u4ee5\u53ea\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u6614\u65e5\u5e1d\u738b\u8c37\u4e3b\u8427\u5b59\u4e0e\u84dd\u5927\u5148\u751f\u6218\u4e8e\u6cf0\u5c71\u7edd\u9876\uff0c\u84dd\u5927\u5148\u751f\u6301\u767e\u65a4\u5927\u94c1\u9525\uff0c\u8427 \u738b\u5b59\u7528\u7684\u5374\u662f\u6839\u8863\u5e26\uff0c\u4ed6\u4ee5\u81f3\u67d4\u654c\u81f3\u521a\uff0c\u4ee5\u84dd\u5927\u5148\u751f\u6076\u6218\u4e00\u663c\u591c\uff0c\u636e\u8bf4\u5929\u5730\u7686\u4e3a\u4e4b\u53d8\u8272\uff0c\u65e5 \u6708\u4e5f\u5931\u5374\u5149\u5f69\u3002 \u3000\u3000\u5979\u5a07\u7b11\u9053\uff1a\u4f60\u8bf4\u8fd9\u4e00\u6218\u7cbe\u5f69\u4e0d\u7cbe\u5f69\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u542c\u59d1\u5a18\u8bf4\u5f97\u5982\u6b64\u751f\u52a8\uff0c\u6211\u51e0\u4e4e\u4e5f\u50cf\u662f\u5230\u4e86\u6cf0\u5c71\u7edd\u9876\uff0c\u5f97\u89c1\u5e1d\u738b\u8c37\u4e3b\u4e0e\u84dd \u5927\u5148\u751f\u7684\u96c4\u98ce\uff0c\u5b9e\u5728\u662f\u7cbe\u5f69\u6781\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u62bf\u5634\u7b11\u9053\uff1a\u60f3\u4e0d\u5230\u4f60\u8bf4\u7684\u8bdd\u6bd4\u4f60\u7684\u98de\u5200\u8fd8\u8981\u5389\u5bb3\u5f97\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff01 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u5a07\u7b11\u9053\uff1a\u4f60\u4e00\u5251\u867d\u7136\u53ef\u4ee5\u8981\u4eba\u7684\u547d\uff0c\u4f46\u4f60\u53ea\u8981\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5374\u53ef\u4ee4\u5973\u5b69\u5b50\u4eec\u5c06\u5fc3 \u90fd\u4ea4\u7ed9\u4f60\uff0c\u8981\u5973\u4eba\u7684\u5fc3\uff0c\u5c82\u975e\u8981\u7537\u4eba\u7684\u547d\u56f0\u96be\u591a\u4e86\u4e48\uff1f \u3000\u3000\u5979\u7528\u90a3\u53cc\u52fe\u9b42\u7684\u5927\u773c\u775b\u779f\u7740\u4ed6\uff0c\u8fde\u674e\u5bfb\u6b22\u90fd\u5df2\u89c9\u5f97\u6709\u4e9b\u53d7\u4e0d\u4e86\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u8fd9\u5c0f\u59d1\u5a18\u7adf \u5982\u6b64\u53ef\u6015\u3002 \u3000\u3000\u5979\u53c8\u5a07\u7b11\u7740\u95ee\uff1a\u4f60\u8bf4\u8fd9\u4e00\u6218\u7cbe\u5f69\u4e0d\u7cbe\u5f69\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u6562\u518d\u591a\u8bdd\uff0c\u70b9\u5934\u7b11\u9053\uff1a\u7cbe\u5f69\u6781\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u8fd9\u4e9b\u6218\u5f79\u867d\u7136\u60ca\u5929\u52a8\u5730\uff0c\u800c\u4e14\u8fd8\u80fd\u540d\u7559\u5343\u53e4\uff0c\u4f46\u6bd4\u8d77\u4e24\u4f4d\u65b9\u624d\u90a3\u4e00\u6218\u6765\uff0c \u5374\u8fd8\u662f\u5dee\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u6211\u4e00\u5411\u4e0d\u662f\u4e2a\u8c26\u865a\u7684\u4eba\uff0c\u5374\u4e5f\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u59d1\u5a18\u672a\u514d\u592a\u8fc7\u5956\u4e86\u5427\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u6b63\u8272\uff1a\u6211\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u4f60\u672c\u6709\u4e09\u6b21\u5730\u53ef\u81f4\u90ed\u5d69\u9633\u7684\u6b7b\u547d\uff0c\u4f46\u5374\u90fd\u672a\u51fa\u624b\uff0c\u5230\u540e \u6765\u4f60\u6740\u6c14\u5df2\u7aed\uff0c\u5200\u950b\u5df2\u6298\uff0c\u90ed\u5d69\u9633\u8bf4\u4e0d\u5b9a\u5df2\u53ef\u5c06\u4f60\u7f6e\u4e4b\u4e8e\u6b7b\u5730\uff0c\u4f46\u4ed6\u5374\u5fc3\u7518\u60c5\u613f\u7684\u8ba4\u8d25\u670d\u8f93 \u4e86\u2014\u2014 \u3000\u3000\u5979\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u50cf\u4f60\u4eec\u8fd9\u6837\uff0c\u624d\u771f\u6b63\u662f\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u624d\u771f\u6b63\u65e0\u6127\u4e8e\u82f1\u96c4\u672c\u8272\uff0c \u4f60\u82e5\u4e00\u5200\u6740\u4e86\u4ed6\uff0c\u4ed6\u82e5\u4e00\u5200\u6740\u4e86\u4f60\uff0c\u4f60\u4eec\u7684\u6b66\u529f\u5c31\u7b97\u518d\u9ad8\uff0c\u6211\u4e5f\u4e0d\u4f1a\u77a7\u5728\u773c\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u534a\u664c\uff1a\u90ed\u5d69\u9633\u7684\u786e\u4e0d\u6127\u4e3a\u771f\u82f1\u96c4\uff01 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4f60\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u7740\u6447\u4e86\u6447\u5934\u9053\uff1a\u6211\uff1f\u2014\u2014\u6211\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u6211\u95ee\u4f60\uff0c\u4ed6\u7b2c\u4e00\u5251\u6325\u51fa\u7528\u7684\u662f\u4ec0\u4e48\u62db\u5f0f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u98ce\u5377\u6d41\u4e91\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u7b2c\u4e8c\u62db\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6d41\u661f\u8ffd\u6708\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4ed6\u7531\u7b2c\u4e00\u62db\u201c\u98ce\u6d41\u5377\u4e91\u201d\uff0c\u53d8\u4e3a\u7b2c\u4e8c\u62db\u201c\u6d41\u661f\u8ffd\u6708\u201d\u65f6\uff0c\u53d8\u5316\u592a\u6025\uff0c\u662f\u4ee5 \u5251\u6cd5\u4e2d\u5c31\u6709\u4e86\u7834\u9699\uff0c\u4f60\u7684\u98de\u5200\u82e5\u662f\u90a3\u4e00\u5239\u90a3\u95f4\u51fa\u624b\uff0c\u662f\u4e0d\u662f\u7acb\u523b\u53ef\u4ee5\u8981\u4ed6\u7684\u547d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u60ca\u4eba\u4e4b\u8bed>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u60ca\u4eba\u4e4b\u8bed \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u8fd9\u662f\u4f60\u9519\u8fc7\u6740\u4ed6\u7684\u7b2c\u4e00\u6b21\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u6211\u518d\u8bf4\u7b2c\u4e8c\u6b21\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u4e0d\u8bf4\u4e5f\u7f62\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u51b7\u7b11\u9053\uff1a\u522b\u4eba\u90fd\u8bf4\u674e\u5bfb\u6b22\u662f\u771f\u6b63\u7684\u7537\u4eba\uff0c\u60f3\u4e0d\u5230\u539f\u6765\u4e9b\u5a18\u5a18\u8154\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5e73\u751f\u4e5f\u6328\u8fc7\u4e0d\u5c11\u9a82\uff0c\u4f46\u88ab\u7a7a\u9a82\u505a\u201c\u5a18\u5a18\u8154\u201d\uff0c\u8fd9\u5012\u8fd8\u771f\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\uff0c\u4ed6\u5b9e\u5728\u6709 \u4e9b\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7684\u5927\u773c\u775b\u7785\u7740\u4ed6\uff0c\u9053\uff1a\u4f60\u65e2\u6ca1\u6709\u8bdd\u8bf4\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u54b3\u55fd\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff1a\u59d1\u5a18\u76ee\u5149\u5982\u70ac\uff0c\u60f3\u5fc5\u4e5f\u662f\u4f4d\u9ad8\u4eba\uff0c\u6211\u5012\u5931\u656c\u4e86\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u7a81\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u62bf\u7740\u5634\u9053\uff1a\u4f60\u5c11\u6367\u6211\uff0c\u6211\u8fd8\u6ca1\u4f60\u80a9\u8180\u9ad8\uff0c\u600e\u4e48\u80fd\u7b97\u662f\u9ad8\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u679c\u7136\u5df2\u5fcd\u4e0d\u4f4f\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u67d4\u58f0\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e00\u5411\u4e0d\u613f\u81ea\u5938\u81ea\u8d5e\uff0c\u603b\u662f\u66ff\u522b\u4eba\u5439\u5618\uff0c\u8fd9\u662f\u4f60\u7684\u597d\u5904\uff0c\u5374\u4e5f \u662f\u6b63\u662f\u4f60\u7684\u6bdb\u75c5\uff0c\u4e00\u4e2a\u4eba\u65e2\u7136\u6d3b\u7740\uff0c\u5c31\u4e0d\u80fd\u592a\u59d4\u5c48\u81ea\u5df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u59d1\u5a18\u2014\u2014 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u561f\u7740\u5634\uff0c\u9053\uff1a\u6211\u65e2\u4e0d\u59d3\u59d1\uff0c\u4e5f\u4e0d\u53eb\u505a\u5a18\uff0c\u4f60\u4e3a\u4ec0\u4e48\u603b\u53eb\u6211\u59d1\u5a18\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u5973\u5b69\u5f88\u6709\u8da3\u3002 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u677f\u7740\u8138\u9053\uff1a\u6211\u59d3\u5b59\uff0c\u53eb\u5b59\u5c0f\u7ea2\uff0c\u53ef\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\u90a3\u4e2a\u8679\uff0c\u800c\u662f\u7ea2\u9ec4\u84dd\u767d\u90a3\u4e2a\u7ea2 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5728\u4e0b\u674e\u2014\u2014 \u3000\u3000\u8fab\u5b50\u59d1\u5a18\u9053\uff1a\u4f60\u7684\u540d\u5b57\u6211\u65e9\u5c31\u77e5\u9053\u4e86\uff0c\u800c\u4e14\u662f\u5c31\u60f3\u627e\u4f60\u6597\u4e00\u6597\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6115\u7136\u9053\uff1a\u6597\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u683c\u683c\u7b11\u9053\uff1a\u6211\u81ea\u7136\u4e0d\u4f1a\u627e\u4f60\u6597\u6b66\u529f\uff0c\u82e5\u8bba\u6b66\u529f\uff0c\u6211\u518d\u7ec3\u4e00\u767e\u5e74\u4e5f\u6bd4\u4e0d\u4e0a\u4f60\uff0c\u6211\u662f \u60f3\u627e\u4f60\u6597\u9152\u7684\uff0c\u6211\u53ea\u8981\u542c\u8bf4\u6709\u4eba\u9152\u91cf\u6bd4\u6211\u597d\uff0c\u5fc3\u91cc\u5c31\u4e0d\u670d\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u6211\u77e5\u9053\u559d\u9152\u7684\u4eba\u90fd\u6709\u8fd9\u6bdb\u75c5\uff0c\u5374\u60f3\u4e0d\u5230\u4f60\u4e5f\u6709\u540c\u75c5\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u53ea\u4e0d\u8fc7\u6211\u73b0\u5728\u627e\u4f60\u6597\u9152\uff0c\u672a\u514d\u5360\u4e86\u4f60\u7684\u5168\u5bb6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u677f\u8d77\u4e86\u8138\uff0c\u6b63\u8272\uff1a\u65b9\u624d\u548c\u4eba\u62da\u547d\uff0c\u4f53\u529b\u81ea\u7136\u5dee\u4e9b\uff0c\u9152\u91cf\u4e5f\u672a\u514d\u8981\u6253\u4e2a\u6298\u6263\uff0c\u6e34\u9152 \u4e5f\u548c\u6bd4\u6b66\u4e00\u6837\uff0c\u5929\u65f6\u5730\u5229\u4eba\u548c\uff0c\u8fd9\u4e09\u6837\u662f\u4e00\u6837\u4e5f\u5dee\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5c31\u51ed\u4f60\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5df2\u4e0d\u6127\u4e3a\u9152\u4e2d\u9ad8\u624b\uff0c\u80fd\u4e0e\u4f60\u8fd9\u6837\u7684\u9ad8\u624b\u6597\u9152\uff0c\u9189\u4ea6\u65e0\u61be\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5927\u773c\u775b\u91cc\u53d1\u51fa\u4e86\u5149\uff0c\u90a3\u662f\u79cd\u6b23\u559c\u7684\u5149\u8292\uff0c\u4e5f\u662f\u79cd\u8d5e\u8d4f\u7684\u5149\u8292\uff0c\u4f46\u5979\u7684\u8138\u5374\u8fd8\u662f\u6545 \u610f\u677f\u7740\u8138\uff0c\u9053\uff1a\u90a3\u4e48\uff0c\u2014\u2014\u6211\u65e2\u5df2\u53f6\u4e86\u5929\u65f6\uff0c\u5c31\u4e0d\u80fd\u518d\u5360\u5730\u5229\uff0c\u8fd9\u5730\u65b9\u5c31\u7531\u4f60\u6765\u9009\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u9053\uff1a\u65e2\u662f\u5982\u6b64\uff0c\u8bf7\u968f\u6211\u6765\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u8bf7\uff01 \u3000\u3000\u9ec4\u660f\u4e4b\u524d\uff0c\u6b63\u662f\u4e00\u5929\u751f\u610f\u6700\u6e05\u6de1\u7684\u65f6\u5019\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5750\u5728\u95e8\u53e3\u6652\u592a\u9633\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u674e\u5bfb\u6b22\u5e26\u7740\u5b59\u5c0f\u7ea2\u6765\u4e86\u3002\u5b59\u9a7c\u5b50\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e24\u4eba\u4f1a\u51d1\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u6709 \u8bf4\u6709\u7b11\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4f1a\u6210\u670b\u53cb\uff0c\u5012\u771f\u662f\u4ef6\u602a\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6545\u610f\u4e0d\u53bb\u770b\u5b59\u9a7c\u5b50\u7684\u8868\u60c5\uff0c\u5fc3\u91cc\u5374\u4e5f\u89c9\u5f97\u5f88\u597d\u7b11\u3002 \u3000\u3000\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u8bf4\u8d77\u8bdd\u6765\u5c31\u50cf\u662f\u767e\u7075\u9e1f\uff0c\u4e00\u5f00\u53e3\u5c31\u201c\u5431\u55b3\u201d\u5730\u8bf4\u4e2a\u4e0d\u505c\uff0c\u800c\u4e14\u6709\u65f6\u7b80\u76f4\u53eb\u4eba \u62db\u67b6\u4e0d\u4f4f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u5411\u8ba4\u4e3a\u4e16\u4e0a\u53ea\u6709\u4e24\u4ef6\u4e8b\u6700\u4ee4\u4eba\u5934\u75bc\u3002 \u3000\u3000\u7b2c\u4e00\u4ef6\u662f\u5403\u996d\u65f6\u5ffd\u7136\u53d1\u73b0\u6ee1\u684c\u4e0a\u7684\u4eba\u90fd\u4e0d\u662f\u559d\u9152\u7684\u3002 \u3000\u3000\u7b2c\u4e8c\u4ef6\u5c31\u662f\u5ffd\u7136\u9047\u7740\u4e2a\u591a\u5634\u7684\u5973\u4eba\u3002 \u3000\u3000\u8fd9\u7b2c\u4e8c\u4ef6\u4e8b\u5f80\u5f80\u6bd4\u7b2c\u4e00\u4ef6\u66f4\u4ee4\u4ed6\u5934\u75bc\u5341\u500d\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u73b0\u5728\u975e\u4f46\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5934\u75bc\uff0c\u53cd\u800c\u89c9\u5f97\u6109\u5feb\u3002 \u3000\u3000\u8fd9\u62fc\u9152\u7684\u5bf9\u624b\u82e5\u662f\u4e2a\u6f02\u4eae\u5973\u4eba\uff0c\u90a3\u5c31\u66f4\u4ee4\u4eba\u6109\u5feb\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u82e5\u662f\u53c8\u806a\u660e\u3001\u53c8\u6f02\u4eae\u3001\u53c8\u4f1a\u559d\u9152\uff0c\u5c31\u7b97\u591a\u5634\u4e9b\uff0c\u7537\u4eba\u4e5f\u53ef\u4ee5\u5fcd\u53d7\u7684\u2014\u2014\u4f46\u9664\u4e86 \u8fd9\u79cd\u5973\u4eba\u5916\uff0c\u522b\u7684\u5973\u4eba\u8fd8\u662f\u5c11\u591a\u5634\u7684\u597d\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u674e\u5bfb\u6b22\u5df2\u77e5\u9053\uff0c\u90a3\u8bf4\u4e66\u7684\u8001\u5934\u53eb\u5b59\u767d\u53d1\uff0c\u5c31\u662f\u8fd9\u4f4d\u5b59\u5c0f\u7ea2\u7684\u7237\u7237\uff0c\u5979\u7236\u6bcd\u5f88\u65e9 \u5c31\u6b7b\u4e86\uff0c\u4e00\u76f4\u90fd\u662f\u8ddf\u7740\u7237\u7237\u8fc7\u6d3b\u7684\uff0c\u7956\u5b59\u4e24\u4eba\u76f8\u4f9d\u4e3a\u547d\uff0c\u7b80\u76f4\u4ece\u6765\u4e5f\u6ca1\u6709\u4e00\u5929\u79bb\u5f00\u8fc7\u3002 \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u95ee\u5979\uff1a\u90a3\u4e48\u4f60\u7237\u7237\u73b0\u5728\u4e3a\u4f55\u6ca1\u6709\u5728\u4f60\u8eab\u8fb9\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u8fd9\u6b21\u56de\u7b54\u5012\u7b80\u5355\u3002\u5979\u8bf4\uff1a\u6211\u7237\u7237\u5230\u57ce\u5916\u63a5\u4eba\u53bb\u4e86\u3002 \u3000\u3000\u674e\u589e\u6b22\u672c\u6765\u8fd8\u60f3\u5979\uff1a\u63a5\u4eba\u4e3a\u4f55\u8981\u5230\u57ce\u5916\u53bb\u63a5\uff1f \u3000\u3000\u201c\u63a5\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u65e2\u7136\u53ea\u4e0d\u8fc7\u662f\u53bb\u63a5\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u4f60\u53bb\uff1f \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u4e00\u5411\u5f88\u8bc6\u76f8\uff0c\u4e5f\u4e00\u5411\u4e0d\u613f\u88ab\u4eba\u770b\u6210\u662f\u4e2a\u591a\u5634\u7684\u7537\u4eba\u2014\u2014\u548c\u5b59\u5c0f\u7ea2\u5728\u4e00\u8d77\uff0c\u4e5f\u6839 \u672c\u5c31\u6ca1\u6709\u673a\u4f1a\u8ba9\u4ed6\u591a\u5634\u3002 \u3000\u3000\u5979\u597d\u50cf\u5b58\u5fc3\u4e0d\u8ba9\u674e\u5bfb\u6b22\u518d\u95ee\u7b2c\u4e8c\u53e5\u8bdd\uff0c\u5df2\u62a2\u5148\u95ee\u4ed6\uff1a \u3000\u3000\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u4f60\u8fd9\u624b\u98de\u5200\u662f\u600e\u4e48\u7ec3\u51fa\u6765\u7684\u5462\uff1f \u3000\u3000\u542c\u8bf4\u4f60\u6709\u4e2a\u597d\u670b\u53cb\u53eb\u963f\u98de\uff0c\u4ed6\u51fa\u624b\u4e4b\u5feb\uff0c\u4e5f\u548c\u4f60\u5dee\u4e0d\u591a\uff0c\u4f46\u73b0\u5728\u4ed6\u5ffd\u7136\u5931\u8e2a\u4e86\uff0c\u4f60\u77e5\u4e0d \u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f \u3000\u3000\u4f60\u4e5f\u5931\u8e2a\u4e86\u4e24\u5e74\uff0c\u6c5f\u6e56\u4e2d\u8c01\u4e5f\u60f3\u4e0d\u5230\u4f60\u539f\u6765\u4e00\u76f4\u8eb2\u5728\u5b59\u9a7c\u5b50\u7684\u5c0f\u5e97\u91cc\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8eb2\u5728 \u90a3\u91cc\uff1f \u3000\u3000\u73b0\u5728\u4f60\u884c\u85cf\u65e2\u9732\uff0c\u4ee5\u540e\u6765\u627e\u4f60\u7684\u4eba\u4e00\u5b9a\u4e0d\u5c11\uff0c\u4f60\u662f\u4e0d\u662f\u8fd8\u6253\u7b97\u7559\u5728\u8fd9\u91cc\uff1f\u5982\u679c\u4f60\u60f3\u8d70\uff0c \u53c8\u8981\u53bb\u54ea\u91cc\uff1f \u3000\u3000\u6885\u82b1\u76d7\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u4ed6\u5df2\u6709\u4e24\u5e74\u672a\u9732\u9762\uff0c\u662f\u4e0d\u662f\u5df2\u88ab\u4eba\u9664\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u88ab\u8c01\u9664\u53bb\u7684\uff0c\u662f\u4e0d\u662f\u4f60\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u95ee\u7684\u8fd9\u4e9b\u8bdd\uff0c\u674e\u5bfb\u6b22\u8fde\u4e00\u53e5\u4e5f\u6ca1\u6709\u7b54\u8986\u2014\u2014\u6709\u4e9b\u8bdd\u56fa\u7136\u662f\u613f\u56de\u7b54\u7684\uff0c\u6709\u4e9b\u8bdd\u5374\u8fde \u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u731c\u51fa\u6797\u4ed9\u513f\u5c31\u662f\u6885\u82b1\u76d7\u3002 \u3000\u3000\u4ed6\u4e5f\u65e9\u5df2\u77e5\u9053\u963f\u98de\u662f\u7edd\u4e0d\u5fcd\u5411\u6797\u4ed9\u513f\u4e0b\u624b\u7684\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u963f\u98de\u5fc5\u5b9a\u662f\u5e26\u7740\u6797\u4ed9\u513f\u8d70\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u4ee5\u540e\u662f\u4e0d\u662f\u66fe\u6d17\u5fc3\u9769\u9762\uff0c\u91cd\u65b0\u505a\u4eba\uff1f \u3000\u3000\u6797\u4ed9\u513f\u662f\u4e0d\u662f\u771f\u7684\u66fe\u5bf9\u963f\u98de\u751f\u51fa\u611f\u60c5\uff1f \u3000\u3000\u60f3\u8d77\u8fd9\u4e9b\u603b\u662f\uff0c\u674e\u5bfb\u6b22\u5c31\u4e0d\u514d\u8981\u53f9\u606f\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u77e5\u9053\u4eca\u540e\u81ea\u5df1\u8be5\u600e\u4e48\u6253\u7b97\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u76f4\u7785\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5e26\u7740\u6e29\u67d4\u7684\u7b11\u610f\uff0c\u4eff\u4f5b\u5979\u4e0d\u4f46\u5f88\u6b23\u8d4f\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u5f88\u4e86\u89e3\u8fd9\u4e2a \u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ac\u8d77\u5934\uff0c\u63a5\u89e6\u5230\u5979\u7684\u6e29\u67d4\u7684\u773c\u5149\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u5c45\u7136\u8df3\u4e86\u8df3\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ae3\u7136\u9053\uff1a\u73b0\u5728\u6211\u4eec\u53ef\u4ee5\u5f00\u59cb\u62fc\u9152\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u597d\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u90a3\u4e48\uff0c\u4f60\u8bf4\u6211\u4eec\u8be5\u5982\u4f55\u62fc\u6cd5\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u62fc\u9152\u96be\u9053\u8fd8\u6709\u8bb8\u591a\u79cd\u65b9\u6cd5\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u5f53\u7136\u4e86\uff0c\u4f60\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u53ea\u77e5\u9053\u8fd9\u4e00\u79cd\u65b9\u6cd5\uff0c\u90a3\u5c31\u662f\u5927\u5bb6\u90fd\u628a\u9152\u559d\u5230\u809a\u5b50\u91cc\u53bb\uff0c\u8c01\u559d\u7684\u9152\u5148\u5230\u809a\u5b50 \u91cc\u9020\u53cd\uff0c\u8c01\u5c31\u8f93\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u7b11\uff0c\u6447\u7740\u5934\u9053\uff1a\u5982\u6b64\u770b\u6765\uff0c\u4f60\u559d\u9152\u7684\u5b66\u95ee\u8fd8\u662f\u4e0d\u591f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u62fc\u9152\u6709\u6587\u62da\uff0c\u6709\u6b66\u62da\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6587\u62da\u662f\u5982\u4f55\u62da\u6cd5\uff1f\u6b66\u62da\u53c8\u662f\u5982\u4f55\u62da\u6cd5\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u521a\u521a\u8bf4\u7684\u6cd5\u5b50\uff0c\u5c31\u662f\u6b66\u62da\uff0c\u90a3\u7b80\u76f4\u662f\u725b\u996e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u725b\u996e\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u5927\u5bb6\u76f4\u7740\u8116\u5b50\uff0c\u628a\u9152\u62da\u547d\u5f80\u5634\u91cc\u5012\uff0c\u4e0d\u662f\u725b\u996e\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u628a\u9152\u5f80\u5634\u91cc\u5012\uff0c\u96be\u9053\u5f80\u8033\u6735\u91cc\u5012\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u7b11\u9053\uff1a\u4f60\u8981\u771f\u80fd\u7528\u8033\u6735\u559d\u9152\uff0c\u6211\u5012\u771f\u6bd4\u4e0d\u8fc7\u4f60\uff0c\u53ea\u597d\u7b97\u4f60\u8d62\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u7528\u8033\u6735\u559d\u9152\u592a\u6162\uff0c\u6211\u53ef\u6ca1\u90a3\u4e48\u65af\u6587\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6211\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u600e\u4e48\u80fd\u8ddf\u4f60\u6b66\u62da\uff0c\u4f46\u6587\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u4f60\u53ef\u4ee5\u968f\u4fbf\u9009\u4e00\u79cd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6709\u54ea\u51e0\u79cd\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6709\u731c\u62f3\u884c\u4ee4\u3001\u51fb\u9f13\u4f20\u82b1\uff0c\u4f46\u8fd9\u4e9b\u6cd5\u5b50\u90fd\u592a\u4fd7\u6c14\uff0c\u50cf\u6211\u4eec\u8fd9\u79cd\u4eba\u62da\u9152\uff0c\u81ea\u7136\u4e0d \u80fd\u7528\u8fd9\u4e48\u4fd7\u6c14\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u8fd8\u5269\u4e0b\u51e0\u79cd\u6cd5\u5b50\u6765\u8ba9\u6211\u9009\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u53ea\u5269\u4e0b\u4e00\u79cd\u6cd5\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002\u5b59\u5c0f\u7ea2\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002\u9053\uff1a\u867d\u7136\u53ea\u5269\u4e0b\u4e00\u79cd\u6cd5\u5b50\uff0c\u4f46\u8fd9\u79cd\u6cd5\u5b50 \u4e0d\u4f46\u6700\u65b0\u5947\uff0c\u4e5f\u6700\u6709\u8da3\uff0c\u5c31\u7b97\u6709\u4e00\u4e07\u79cd\u6cd5\u5b50\uff0c\u4f60\u4e5f\u4e00\u5b9a\u4f1a\u9009\u8fd9\u79cd\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9152\u5df2\u5728\u684c\uff0c\u6211\u53ea\u60f3\u5feb\u70b9\u559d\u4e0b\u53bb\uff0c\u7528\u4ec0\u4e48\u6cd5\u5b50\u90fd\u65e0\u59a8\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u597d\uff0c\u4f60\u542c\u7740\uff0c\u8fd9\u6cd5\u5b50\u5176\u5b9e\u4e5f\u7b80\u5355\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u597d\u542c\u7740\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6211\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u4f60\u82e5\u80fd\u56de\u7b54\uff0c\u5c31\u7b97\u6211\u8d62\u4e86\uff0c\u6211\u5c31\u5f97\u559d\u4e00\u5927\u676f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\uff1a\u82e5\u7b54\u4e0d\u51fa\uff0c\u5c31\u7b97\u8f93\u4e86\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u5c31\u7b97\u56de\u7b54\u4e0d\u51fa\uff0c\u4e5f\u4e0d\u7b97\u8f93\uff0c\u76f4\u5230\u6211\u5c06\u81ea\u5df1\u95ee\u7684\u8fd9\u95ee\u9898\u56de\u7b54\u51fa\u6765\uff0c\u4f60\u624d\u7b97\u8f93 \u3002 \u3000\u3000\u5979\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u4f60\u8bf4\u8fd9\u6cd5\u5b50\u516c\u5e73\u4e0d\u516c\u5e73\uff1f\u597d\u4e0d\u597d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u82e5\u8f93\u4e86\uff0c\u5c31\u8f6e\u5230\u6211\u6765\u95ee\u4f60\u4e86\uff0c\u662f\u5417\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u6447\u5934\u9053\uff1a\u4e0d\u5bf9\uff0c\u8d62\u7684\u4eba\u53ef\u4ee5\u4e00\u76f4\u95ee\u4e0b\u53bb\uff0c\u76f4\u5230\u8f93\u4e3a\u6b62\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u82e5\u4e00\u76f4\u95ee\u6211\u4e9b\u4f60\u7684\u79c1\u4eba\u7410\u4e8b\uff0c\u6211\u5c82\u975e\u8981\u4e00\u76f4\u8f93\u5230\u5e95\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u6211\u5f53\u7136\u4e0d\u80fd\u95ee\u4f60\u90a3\u4e9b\u8bdd\uff0c\u6211\u82e5\u95ee\u4f60\uff0c\u6211\u6bcd\u4eb2\u662f\u8c01\uff1f\u6211\u5144\u5f1f\u6709\u51e0\u4eba\uff1f \u6211\u6709\u51e0\u5c81\uff1f\u2014\u2014\u4f60\u5f53\u7136\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4e48\uff0c\u4f60\u51c6\u5907\u95ee\u4e9b\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u53ea\u8981\u62da\u9152\u4e00\u5f00\u59cb\uff0c\u4f60\u5c31\u53ef\u4ee5\u542c\u5230\u6211\u8981\u95ee\u4e9b\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u6211\u5df2\u5728\u51c6\u5907\u8f93\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u597d\uff0c\u4f60\u542c\u7740\uff0c\u6211\u73b0\u5728\u5c31\u5f00\u59cb\u95ee\u4f60\u7b2c\u4e00\u53e5\u8bdd\uff1f \u3000\u3000\u5979\u5ffd\u7136\u655b\u53bb\u4e86\u7b11\u5bb9\uff0c\u76ee\u5149\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u4f60\u77e5\u4e0d\u77e5\u90a3\u5c01\u4fe1\u662f\u8c01\u5199\u7684\uff1f \u3000\u3000\u8fd9\u53e5\u8bdd\u5b9e\u5728\u95ee\u5f97\u5f88\u60ca\u4eba\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u773c\u775b\u7acb\u523b\u4eae\u4e86\uff0c\u5931\u58f0\u9053\uff1a\u6211\u4e0d\u77e5\u9053\u2014\u2014\u4f60\u96be\u9053\u77e5\u9053\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u6211\u82e5\u4e0d\u77e5\u9053\uff0c\u5c31\u4e0d\u4f1a\u95ee\u4f60\u4e86\uff0c\u5199\u90a3\u5c01\u4fe1\u7684\u4eba\u5c31\u662f\u2014\u2014 \u3000\u3000\u5979\u6545\u610f\u505c\u4f4f\u8bed\u58f0\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u5c31\u662f\u6797\u4ed9\u513f\uff01 \u3000\u3000\u8fd9\u95ee\u9898\u7684\u56de\u7b54\u66f4\u60ca\u4eba\uff01\u674e\u5bfb\u6b22\u867d\u7136\u4e00\u5411\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u4f60\u600e\u4e48 \u77e5\u9053\u662f\u5979\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u60a0\u60a0\uff1a\u73b0\u5728\u8fd8\u672a\u8f6e\u5230\u4f60\u95ee\u6211\uff0c\u5148\u559d\u4e86\u8fd9\u676f\u9152\u518d\u8bf4\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u963f\u98de\u73b0\u5728\u7684\u60c5\u51b5\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u867d\u7136\u8fd8\u662f\u548c\u6797\u4ed9\u513f\u5728\u4e00\u8d77\uff0c\u4f46\u6797\u4ed9\u513f\u505a\u7684\u4e8b\uff0c\u4ed6\u5374\u5b8c\u5168\u88ab\u8499\u5728\u9f13\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6025\u7740\u95ee\u9053\uff1a\u4ed6\u73b0\u5728\u4f55\u5904\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u6447\u5934\uff0c\u53f9\u9053\uff1a\u4f60\u600e\u4e48\u5982\u6b64\u6027\u6025\uff0c\u7b49\u4f60\u8d62\u4e86\u65f6\u518d\u95ee\u4e5f\u4e0d\u8fdf\u5440\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u597d\u5c06\u7b2c\u4e8c\u676f\u9152\u4e5f\u559d\u4e86\u4e0b\u53bb\uff0c\u8fd9\u676f\u5b50\u6bd4\u7897\u8fd8\u5927\uff0c\u4ed6\u559d\u5f97\u6bd4\u5e73\u65f6\u66f4\u5feb\uff0c\u56e0\u4e3a\u4ed6\u6025\u7740 \u8981\u542c\u7b2c\u4e09\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6797\u4ed9\u513f\u4e3a\u4f55\u8981\u5199\u90a3\u5c01\u4fe1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u867d\u5df2\u9690\u7ea6\u7684\u731c\u51fa\u4e86\u6797\u4ed9\u513f\u7684\u76ee\u7684\uff0c\u5374\u8fd8\u662f\u65e0\u6cd5\u786e\u5b9a\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u56e0\u4e3a\u5979\u77e5\u9053\u53ea\u8981\u6709\u4eba\u60f3\u5bf9\u9f99\u592b\u4eba\u6797\u8bd7\u97f3\u4e0d\u5229\uff0c\u4f60\u5c31\u4e00\u5b9a\u4f1a\u633a\u8eab\u800c\u51fa\u7684\uff0c\u5979\u8981 \u8bf1\u4f60\u73b0\u8eab\uff0c\u518d\u627e\u4eba\u6740\u4f60\uff01\u56e0\u4e3a\u5979\u4e00\u76f4\u5c06\u4f60\u5f53\u505a\u6700\u5927\u7684\u5bf9\u5934\uff0c\u6700\u6015\u7684\u662f\u4f60\uff0c\u6700\u6068\u7684\u4e5f\u662f\u4f60\uff0c\u4f60 \u82e5\u4e0d\u6b7b\uff0c\u5979\u5c31\u4e0d\u6562\u51fa\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u559d\u4e86\u7b2c\u4e09\u676f\u9152\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u7b2c\u4e00\u4e2a\u8981\u6740\u4f60\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8981\u6740\u6211\u7684\u4eba\u592a\u591a\u4e86\uff0c\u53c8\u5c82\u6b62\u4e00\u4e2a\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f46\u80fd\u6740\u5f97\u4e86\u4f60\u7684\u4eba\u5374\u53ea\u6709\u4e24\u4e09\u4e2a\uff0c\u7b2c\u4e00\u4e2a\u5c31\u662f\u4e0a\u5b98\u91d1\u8679\uff01 \u3000\u3000\u8fd9\u56de\u7b54\u5e76\u672a\u51fa\u674e\u5bfb\u6b22\u610f\u6599\uff0c\u4ed6\u559d\u4e0b\u7b2c\u56db\u676f\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u4ed6\u73b0\u5728\u6765\u4e86\u4e48\uff1f \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u60ca\u4eba\u7684\u6d88\u606f>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u60ca\u4eba\u7684\u6d88\u606f \u3000\u3000\u5b59\u5c0f\u7ea2\u6447\u7740\u5934\u7b11\u9053\uff1a\u4f60\u770b\u4f60\uff0c\u8001\u6bdb\u75c5\u53c8\u72af\u4e86\uff0c\u8fd8\u672a\u8f6e\u5230\u4f60\u95ee\u7684\u65f6\u5019\uff0c\u4f60\u504f\u8981\u95ee\uff1f \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u4e0a\u5b98\u91d1\u8679\u8fd9\u4eba\u7684\u813e\u6c14\uff0c\u4f60\u5f53\u7136\u77e5\u9053\uff0c\u666e\u901a\u7684\u5b9d\u85cf\uff0c\u81ea\u7136\u4e0d\u80fd\u4ee4\u4ed6\u52a8\u5fc3\uff0c\u8fd9 \u6b21\u4ed6\u600e\u4e48\u4f1a\u52a8\u4e86\u5fc3\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u56e0\u4e3a\u4ed6\u542c\u8bf4\u6614\u5e74\u5929\u4e0b\u7b2c\u4e00\u4f4d\u540d\u4fa0\u6c88\u6d6a\u662f\u4ee4\u5c0a\u7684\u597d\u670b\u53cb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6c88\u5927\u4fa0\u7684\u786e\u662f\u5148\u7236\u7684\u9053\u4e49\u4e4b\u4ea4\uff0c\u4f46\u4ed6\u591a\u5e74\u524d\u4fbf\u4e70\u6389\u4e1c\u6e21\uff0c\u9000\u9690\u4e8e\u6d77\u5916\u4e4b\u4ed9\u5c71 \uff0c\u5374\u548c\u8fd9\u4ef6\u4e8b\u6709\u4f55\u5173\u7cfb\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u6211\u5c31\u8ba9\u4f60\u5148\u95ee\u4e00\u95ee\u5427\uff0c\u4e0d\u7136\u6211\u770b\u4f60\u771f\u8981\u95f7\u6b7b\u4e86\uff0c\u4f46\u4f60\u5374\u5f97\u5148\u559d\u4e09\u5927\u676f\uff0c\u6211 \u624d\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u5979\u4eff\u4f5b\u5b58\u5fc3\u60f3\u5c06\u674e\u5bfb\u6b22\u704c\u9189\u4f3c\u7684\uff0c\u53ea\u4e0d\u8fc7\u5979\u7684\u95ee\u9898\u5b9e\u5728\u592a\u60ca\u4eba\uff0c\u56de\u7b54\u66f4\u60ca\u4eba\uff0c\u674e\u5bfb\u6b22\u660e \u77e5\u8981\u559d\u9189\uff0c\u4e5f\u53ea\u5f97\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8fd9\u624d\u63a5\u7740\u9053\uff1a\u56e0\u4e3a\u4ed6\u542c\u8bf4\u6c88\u5927\u4fa0\u5f52\u9690\u4e4b\u524d\uff0c\u66fe\u6258\u4ee4\u5c0a\u4fdd\u7ba1\u4e24\u672c\u4e66\uff0c\u8fd9\u4e24\u672c\u4e66\u5c31\u662f \u4ed6\u6bd5\u751f\u6240\u7ec3\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u4f60\u53ea\u7ec3\u4e86\u5176\u4e2d\u7684\u4e00\u672c\uff0c\u5c0f\u674e\u98de\u5200\u5c31\u5df2\u65e0\u654c\u4e8e\u5929\u4e0b\uff0c\u82e5\u662f\u4e24\u672c\u90fd\u7ec3\u6210 \uff0c\u90a3\u8fd8\u5f97\u4e86\uff0c\u6240\u4ee5\u8fde\u4e0a\u5b98\u91d1\u8679\u90a3\u6837\u7684\u4eba\u4e5f\u65e0\u6cd5\u4e0d\u52a8\u5fc3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6014\u4e86\u534a\u664c\uff0c\u9053\uff1a\u82e5\u771f\u6709\u8fd9\u56de\u4e8b\uff0c\u600e\u4f1a\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u8fd9\u5168\u662f\u6797\u4ed9\u513f\u9020\u51fa\u6765\u7684\u8c23\u8a00\uff0c\u6c88\u5927\u4fa0\u7edd\u4e16\u5947\u624d\uff0c\u6700\u4e86\u89e3\u4eba\u5fc3\u4e4b\u5f31\u70b9 \uff0c\u53c8\u600e\u4f1a\u7559\u4e0b\u4ec0\u4e48\u6b66\u529f\u79d8\u7b08\u6765\u8ba9\u540e\u4eba\u4e89\u5948\u3002 \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u5c31\u7b97\u4ed6\u6709\u6b66\u529f\u79d8\u7b08\u8981\u7559\u4e0b\uff0c\u4e5f\u4e0d\u4f1a\u7559\u5728\u4f60\u5bb6\uff0c\u4ed6\u548c\u4ee4\u5c0a\u65e2\u7136\u662f\u9053\u4e49 \u4e4b\u4ea4\uff0c\u53c8\u600e\u4f1a\u5728\u4f60\u5bb6\u7559\u4e0b\u7978\u80ce\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u4e00\u5b9a\u6709\u5f88\u591a\u95ee\u9898\u60f3\u95ee\u6211\uff0c\u6211\u82e5\u4e0d\u8ba9\u4f60\u8d62\u4e00\u6b21\uff0c\u4f60\u4e0d\u6025 \u6b7b\u624d\u602a\uff0c\u6240\u4ee5\u6211\u73b0\u5728\u8981\u95ee\u4f60\u7684\uff0c\u4f60\u4e00\u5b9a\u56de\u7b54\u5f97\u51fa\u3002 \u3000\u3000\u5979\u773c\u775b\u7785\u7740\u674e\u5bfb\u6b22\uff0c\u95ee\u9053\uff1a\u4f60\u73b0\u5728\u5fc3\u91cc\u5934\u662f\u4e0d\u662f\u8fd8\u53ea\u6709\u5979\u4e00\u4e2a\u4eba\uff1f\u751a\u81f3\u4e0d\u60dc\u4e3a\u5979\u800c\u6b7b\uff0d \uff0d\u6211\u8bf4\u7684\u5979\u662f\u8c01\uff0c\u4f60\u81ea\u7136\u77e5\u9053\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u60f3\u5230\u5b59\u5c0f\u7ea2\u4f1a\u95ee\u8fd9\u4e48\u6837\u4e00\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u95ee\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u672c\u7edd\u4e0d\u4f1a\u56de\u7b54\u7684\u2014\u2014\u8fd9\u662f\u4ed6\u4e00\u751f\u4e2d\u6700\u75db\u82e6\u7684\u79d8\u5bc6\uff0c\u4e5f\u662f\u4ed6\u6700\u79d8\u5bc6 \u7684\u75db\u82e6\u3002 \u3000\u3000\u82e5\u6709\u4eba\u95ee\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u65e0\u5f02\u5c06\u4e00\u628a\u5200\u523a\u5165\u4ed6\u5fc3\u91cc\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u61c2\u5b59\u5c0f\u7ea2\u4e3a\u4f55\u8981\u95ee\u51fa\u6765\uff1f \u3000\u3000\u5c11\u5973\u4eec\u5927\u591a\u597d\u5947\uff0c\u5979\u96be\u9053\u4e5f\u53ea\u662f\u4e3a\u4e86\u597d\u5947\u3002 \u3000\u3000\u5979\u81ea\u7136\u7edd\u4e0d\u4f1a\u662f\u4e3a\u4e86\u8981\u4f24\u5bb3\u674e\u5bfb\u6b22\u7684\uff0c\u5426\u5219\u5979\u600e\u4f1a\u5411\u674e\u5bfb\u6b22\u8bf4\u51fa\u90a3\u4e48\u591a\u79d8\u5bc6\uff1f\u800c\u4e14\u6bcf\u4ef6 \u79d8\u5bc6\u8bf4\u51fa\u540e\u90fd\u53ea\u6709\u5bf9\u674e\u5bfb\u6b22\u6709\u5229\u3002 \u3000\u3000\u4f46\u5979\u7a76\u7adf\u662f\u8c01\u5462\uff1f \u3000\u3000\u5979\u600e\u4e48\u77e5\u9053\u90a3\u4e48\u591a\u79d8\u5bc6\uff1f \u3000\u3000\u5979\u7684\u7956\u7236\u663e\u7136\u4e5f\u662f\u4f4d\u98ce\u4e91\u5f02\u4eba\uff0c\u5b59\u767d\u53d1\u770b\u6765\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u5316\u540d\uff0c\u90a3\u4e48\uff0c\u4ed6\u672c\u6765\u7684\u540d\u5b57\u662f \u4ec0\u4e48\u5462\uff1f \u3000\u3000\u4ed6\u51fa\u57ce\u53bb\u63a5\u7684\u662f\u8c01\uff1f\u662f\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\uff1f \u3000\u3000\u963f\u98de\u548c\u6797\u4ed9\u513f\u7a76\u7adf\u85cf\u5728\u54ea\u91cc\uff1f \u3000\u3000\u8fd9\u8bb8\u591a\u603b\u662f\u6b63\u662f\u674e\u5bfb\u6b22\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u4e5f\u5f97\u77e5\u9053\u7684\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1a\u53ea\u9053\u65e0\u60c5\u5374\u6709\u60c5\uff0c\u60c5\u5230\u6d53\u65f6\u60c5\u8f6c\u8584 \u2014\u2014\u662f\u65e0\u60c5\uff1f\u662f\u6709\u60c5\uff1f\u53c8\u6709\u8c01\u53d1\u5f97\u6e05\uff1f\u53c8\u6709\u8c01\uff1f\u2014\u2014 \u3000\u3000\u4ed6\u8bed\u58f0\u8d8a\u6765\u8d8a\u4f4e\uff0c\u7ec8\u4e8e\u8fde\u542c\u4e5f\u542c\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5e7d\u5e7d\u9053\uff1a\u591a\u60c5\u81ea\u53e4\u7a7a\u4f59\u6068\uff0c\u4f60\u8fd9\u53c8\u662f\u4f55\u82e6\uff1f\u2014\u2014\u53c8\u662f\u4f55\u82e6\uff1f \u3000\u3000\u5979\u58f0\u97f3\u66f4\u4f4e\uff0c\u7b80\u76f4\u8fde\u5979\u81ea\u5df1\u90fd\u542c\u4e0d\u6e05\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u5ffd\u7136\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u8fd9\u6b21\u6211\u8ba4\u8f93\u4e86\uff0c\u4f60\u95ee\u5427\uff0c\u60a8\u53ef\u4ee5\u7ee7\u7eed\u95ee \u4e0b\u53bb\uff0c\u4f46\u6211\u82e5\u80fd\u56de\u7b54\uff0c\u8fd8\u7b97\u662f\u4f60\u8f93\uff0c\u4f60\u8fd8\u662f\u8981\u559d\u4e00\u676f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u9053\uff1a\u963f\u98de\u73b0\u5728\u7a76\u7adf\u5728\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u7b2c\u4e00\u53e5\u8981\u95ee\u7684\u5c31\u662f\u8fd9\u53e5\u8bdd\uff0c\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u963f\u98de\u6050\u6015\u5c31 \u662f\u4f60\u6700\u5173\u5fc3\u7684\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u65e0\u8bba\u8c01\u4ea4\u5230\u4ed6\u90a3\u79cd\u670b\u53cb\uff0c\u90fd\u65e0\u6cd5\u4e0d\u5173\u5fc3\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u60a0\u60a0\u7b11\u9053\uff1a\u82e5\u6709\u4eba\u80fd\u4ea4\u5230\u4f60\u8fd9\u79cd\u670b\u53cb\uff0c\u5c82\u975e\u4e5f\u4e00\u6837\u65e0\u6cd5\u4e0d\u5173\u5fc3\u4f60\u3002 \u3000\u3000\u5979\u7b11\u5f97\u4f3c\u4e4e\u6709\u4e9b\u5947\u602a\uff0c\u5ffd\u7136\u81ea\u6000\u4e2d\u53d6\u51fa\u4e2a\u7eb8\u5377\uff0c\u9053\uff1a\u8fd9\u5c31\u662f\u963f\u98de\u4f4f\u7684\u5730\u65b9\uff0c\u4f60\u6309\u56fe\u5bfb\u8bbf \uff0c\u5c31\u80fd\u627e\u5230\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7d27\u63e1\u4f4f\u4e86\u8fd9\u7eb8\u5377\uff0c\u9053\uff1a\u591a\u8c22\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u540c\u4e00\u5929\u5185\u7b2c\u4e8c\u6b21\u8bf4\u8c22\u5b57\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u6211\u5bf9\u4f60\u8bf4\u51fa\u4e86\u4f60\u6700\u5207\u8eab\u7684\u79d8\u5bc6\uff0c\u4f60\u4e0d\u8c22\u6211\uff0c\u6211\u544a\u8bc9\u4f60\u662f\u8c01\u8981\u6740\u4f60\uff0c \u4f60\u4e5f\u4e0d\u8c22\u6211\uff0c\u73b0\u5728\u4f60\u4e3a\u4f55\u8981\u8c22\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u7740\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u7eb5\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\uff0c\u56e0\u4e3a\u4f60\u6709\u4e86\u8fd9\u5f20\u56fe\uff0c\u5c31\u53ef\u4ee5\u627e\u5230\u963f\u98de\uff0c\u4f60\u53ea\u6709\u627e\u5230\u4ed6 \uff0c\u624d\u80fd\u6551\u4ed6\uff0c\u529d\u4ed6\u83ab\u8981\u5bf9\u4e00\u4e2a\u4e0d\u503c\u5f97\u7684\u5973\u4eba\u592a\u8ff7\u604b\uff0c\u529d\u4ed6\u83ab\u8981\u6bc1\u4e86\u81ea\u5df1\uff0c\u4f60\u662f\u4e3a\u4e86\u4ed6\u624d\u8c22\u6211 \u7684\u3002 \u3000\u3000\u5979\u7b11\u5f97\u4eff\u4f5b\u5f88\u51c4\u51c9\uff0c\u5e7d\u5e7d\u9053\uff1a\u8fd9\u6b63\u5982\u4f60\u4e3a\u4e86\u6797\u8bd7\u97f3\u800c\u8c22\u90ed\u5d69\u9633\u4e00\u6837\u2014\u2014\u4f60\u96be\u9053\u6c38\u8fdc\u4e5f\u4e0d \u4f1a\u4e3a\u81ea\u5df1\u8bf4\u4e2a\u8c22\u5b57\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u6c89\u9ed8\u7740\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u6ce8\u7740\u4ed6\uff0c\u8f7b\u8f7b\u53f9\u606f\u9053\uff1a\u6211\u7237\u7237\u5e38\u8bf4\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u603b\u4e0d\u4e3a\u81ea\u5df1\u7740\u60f3\uff0c\u6d3b\u7740\u4e5f\u672a\u514d \u592a\u53ef\u601c\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u6c89\u9ed8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u4ed4\u7ec6\u5480\u56bc\u7740\u674e\u5bfb\u6b22\u8fd9\u4e24\u53e5\u8bdd\u4e2d\u7684\u6ecb\u5473\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5634\u89d2\u624d\u6e10\u6e10\u9732\u51fa\u4e00\u4e1d\u6e29\u67d4\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u603b\u662f\u4e3a\u81ea\u5df1\u7740\u60f3\uff0c\u6d3b\u7740\u4e5f\u5b9e\u5728\u65e0\u8da3\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u559d\u4e86\u676f\u9152\uff0c\u9053\uff1a\u5b59\u8001\u7237\u5b50\u51fa\u57ce\u53bb\u63a5\u4eba\uff0c\u5374\u4e0d\u77e5\u63a5\u7684\u662f\u8c01\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u5176\u5b9e\u4ed6\u5e76\u4e0d\u662f\u53bb\u63a5\u4eba\uff0c\u800c\u662f\u53bb\u9001\u4eba\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9001\u4eba\uff1f\u9001\u8c01\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u5b57\u5b57\u9053\uff1a\u4e0a\u5b98\u91d1\u8679\uff01 \u3000\u3000\u8fd9\u56de\u7b54\u53c8\u4f7f\u674e\u5bfb\u6b22\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u8ffd\u95ee\u9053\uff1a\u4e0a\u5b98\u91d1\u8679\u6839\u672c\u8fd8\u672a\u5165\u57ce\uff0c\u600e\u4f1a\u5c31\u8981\u8d70\u4e86\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u6211\u7237\u7237\u65e2\u7136\u662f\u4e13\u7a0b\u53bb\u9001\u4ed6\u7684\uff0c\u4ed6\u600e\u4e48\u597d\u610f\u601d\u4e0d\u8d70\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u83ab\u975e\u5b59\u8001\u7237\u5b50\u2014\u2014 \u3000\u3000\u4ed6\u53c8\u5f2f\u4e0b\u8170\u53bb\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u4e00\u5f2f\u4e0b\u8170\uff0c\u4ed6\u5c31\u5ffd\u7136\u89c9\u5f97\u4e00\u9635\u9152\u610f\u4e0a\u6d8c\uff0c\u5934\u7adf\u6709\u4e9b\u6655\u4e86\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u4e00\u76f4\u8fdc\u8fdc\u7684\u7ad9\u7740\uff0c\u6b64\u523b\u5fcd\u4e0d\u4f4f\u8d70\u8fc7\u6765\uff0c\u76b1\u7740\u7709\u9053\uff1a\u4f60\u4eca\u5929\u559d\u7684\u592a\u591a\uff0c\u4e5f\u592a\u5feb\uff0c\u6709 \u4ec0\u4e48\u8bdd\uff0c\u4e0d\u5ba1\u7559\u5230\u660e\u5929\u518d\u95ee\u5427\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u8fd9\u4e2a\u4eba\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\uff1a\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u559d\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\uff0c\u9053\uff1a\u4f60\u53c8\u6ca1\u6709\u8ddf\u6211\u4eec\u62fc\u9152\uff0c\u8fd9\u676f\u9152\u4f60\u81ea\u7136\u7528\u4e0d\u7740\u559d\u7684\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u770b\u7740\u4ed6\uff0c\u773c\u775b\u90fd\u53d1\u4e86\u76f4\uff0c\u597d\u50cf\u4ece\u6765\u672a\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u4f3c\u7684\uff0c\u56e0\u4e3a\u4ed6\u4ece\u672a\u770b\u5230\u8fd9\u4eba\u5982\u6b64 \u5927\u7b11\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5df2\u63a5\u7740\u9053\uff1a\u4f46\u6211\u5374\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4e0a\u5b98\u91d1\u8679\u81ea\u547d\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e00\u5411\u773c\u9ad8\u4e8e\u9876\uff0c \u76ee\u7a7a\u4e00\u5207\uff0c\u4ece\u6765\u4e5f\u4e0d\u80af\u4e70\u4efb\u4f55\u4eba\u7684\u5e10\uff0c\u8fd9\u6b21\u5374\u4e70\u4e86\u5b59\u8001\u5148\u751f\u7684\u5e10\uff0c\u90a3\u4e48\u4f60\u731c\uff0c\u8fd9\u5b59\u8001\u5148\u751f\u4f1a \u662f\u4ec0\u4e48\u6837\u7684\u4eba\u5462\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u6211\u731c\u4e0d\u51fa\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4e5f\u731c\u4e0d\u51fa\uff0c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u95ee\uff0c\u975e\u95ee\u660e\u767d\u4e0d\u53ef\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4f60\u95ee\u7684\u592a\u591a\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u9189\u4e86\uff0c\u975e\u9189\u4e0d\u53ef\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u9189\u4e86\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4eba\u751f\u96be\u5f97\u51e0\u56de\u9189\uff1f \u3000\u3000\u4ed6\u53c8\u4e3e\u8d77\u4e86\u9152\u676f\uff0c\u9053\uff1a\u5b59\u59d1\u5a18\uff0c\u6211\u95ee\u4f60\uff0c\u5b59\u8001\u7237\u5b50\u7a76\u7adf\u662f\u8c01\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u5b59\u8001\u7237\u5c31\u662f\u6211\u7236\u4eb2\u7684\u7236\u4eb2\uff0c\u6211\u81ea\u5df1\u7684\u7237\u7237\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u4e0d\u9519\u4e0d\u9519\uff0c\u8fd9\u56de\u7b54\u7b80\u76f4\u6b63\u786e\u6781\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u559d\u5b8c\u4e86\u8fd9\u676f\u9152\uff0c\u4ed6\u76ee\u5149\u5df2\u6726\u80e7\uff0c\u5583\u5583\u9053\uff1a\u6211\u8fd8\u6709\u53e5\u8bdd\u8981\u95ee\u4f60\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u773c\u775b\u5374\u4eae\u7740\u5f88\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u8d81\u4f60\u8fd8\u672a\u9189\u7684\u65f6\u5019\uff0c\u8d76\u5feb\u95ee\u5427\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u95ee\u4f60\uff0c\u4f60\u4e3a\u4f55\u4e00\u5fc3\u60f3\u8981\u704c\u9189\u6211\uff1f\u4e3a\u4ec0\u4e48\u2014\u2014 \u3000\u3000\u5b59\u5c0f\u7ea2\u66ff\u4ed6\u5c06\u6ee1\u676f\u5012\u6ee1\uff0c\u624d\u542b\u7b11\u9053\uff1a\u56e0\u4e3a\u6211\u672c\u5c31\u662f\u8981\u8ddf\u4f60\u62fc\u9152\u7684\uff0c\u81ea\u7136\u8981\u5c06\u4f60\u704c\u5012\uff0c\u6bcf \u4e2a\u559d\u9152\u7684\u4eba\u90fd\u5e0c\u671b\u522b\u4eba\u6bd4\u81ea\u5df1\u5148\u9189\u5012\uff0c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5bf9\uff0c\u5bf9\uff0c\u5bf9\uff0c\u5bf9\u6781\u4e86\u2014\u2014 \u3000\u3000\u559d\u5b8c\u4e86\u8fd9\u676f\u9152\uff0c\u4ed6\u7ec8\u4e8e\u4ed7\u5012\u5728\u684c\u4e0a\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u771f\u7684\u9189\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u548c\u5b59\u9a7c\u5b50\u4e24\u4e2a\u4eba\u90fd\u6ca1\u6709\u8bdd\u8bf4\uff0c\u53ea\u662f\u9759\u9759\u7684\u770b\u7740\u674e\u5bfb\u6b22\uff0c\u4eff\u4f5b\u8fd8\u8981\u770b\u4ed6\u662f\u771f\u9189\uff1f\u8fd8 \u662f\u5047\u9189\uff1f \u3000\u3000\u5929\u5df2\u7ecf\u9ed1\u4e86\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u638c\u8d77\u4e86\u706f\uff0c\u5583\u5583\u9053\uff1a\u5403\u665a\u996d\u7684\u65f6\u5019\u5230\u4e86\uff0c\u53ea\u6015\u53c8\u6709\u5ba2\u4eba\u8981\u4e0a\u95e8\u2014\u2014 \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u5ffd\u7136\u8d70\u8fc7\u53bb\uff0c\u5c06\u4e24\u6247\u95e8\u677f\u4e0a\u4e86\u8d77\u6765\uff0c\u4e5f\u4e0d\u51c6\u5907\u8ba9\u5b59\u5c0f\u7ea2\u51fa\u53bb\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5c45\u7136\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u95e8\u677f\u5f88\u91cd\uff0c\u5b59\u9a7c\u5b50\u4e0a\u95e8\u65f6\u672c\u6765\u4e00\u5411\u5f88\u5403\u529b\uff0c\u4f46\u4eca\u5929\u4ed6\u529b\u6c14\u597d\u50cf\u5ffd\u7136\u53d8\u5927\u4e86\u5341\u500d\uff0c\u642c\u8d77\u95e8 \u677f\u6765\u5c31\u597d\u50cf\u5728\u642c\u4e00\u6839\u7a3b\u8349\u4f3c\u7684\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8d39\u529b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u522b\u4eba\u90fd\u8bf4\u4e8c\u53d4\u4f60\u662f\u5929\u751f\u795e\u529b\uff0c\u504f\u504f\u53ea\u6709\u6211\u5230\u4eca\u5929\u624d\u89c1\u5230\u2014\u2014 \u3000\u3000\u5b59\u9a7c\u5b50\u8f6c\u8fc7\u5934\uff0c\u76b1\u7740\u7709\u9053\uff1a\u8c01\u662f\u4f60\u4e8c\u53d4\uff1f\u59d1\u5a18\u4f60\u83ab\u975e\u4e5f\u9189\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u4e8c\u53d4\u88c5\u5f97\u771f\u50cf\uff0c\u4f46\u73b0\u5728\u53c8\u4f55\u5fc5\u8fd8\u8981\u88c5\u5462\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u77aa\u7740\u5979\u4e00\u773c\uff0c\u76ee\u4e2d\u7a81\u6709\u5bd2\u5149\u66b4\u5c04\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u53cc\u773c\u775b\u54ea\u91cc\u8fd8\u662f\u5b59\u9a7c\u5b50\u7684\u773c\u775b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u82e5\u662f\u770b\u5230\u8fd9\u53cc\u773c\u775b\uff0c\u5fc3\u91cc\u4e5f\u4e00\u5b9a\u4f1a\u4f69\u670d\u5f97\u5f88\uff0c\u56e0\u4e3a\u4ed6\u4eec\u671d\u5915\u76f8\u5904\u4e86\u5c06\u8fd1\u4e24\u5e74\uff0c\u674e \u5bfb\u6b22\u7adf\u4e5f\u672a\u770b\u51fa\u8fd9\u9a7c\u5b50\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u674e\u5bfb\u6b22\u73b0\u5728\u4ec0\u4e48\u4e5f\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6211\u77e5\u9053\u4ed6\u4eca\u5929\u662f\u771f\u7684\u9189\u4e86\uff0c\u7edd\u4e0d\u662f\u88c5\u9189\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u6c89\u58f0\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u9152\u91cf\uff1f\u4ed6\u600e\u4f1a\u9189\u5f97\u8fd9\u4e48\u5feb\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e8c\u53d4\u8fd9\u5c31\u4e0d\u61c2\u4e86\uff0c\u4e00\u4e2a\u4eba\u559d\u9152\u65f6\u7684\u5fc3\u60c5\u82e5\u4e0d\u597d\uff0c\u4f53\u529b\u53c8\u5dee\uff0c\u5c31\u7b97\u4ed6\u9152\u91cf\u518d\u597d \uff0c\u4e5f\u5f88\u5bb9\u6613\u88ab\u4eba\u704c\u9189\u7684\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4f60\u4e3a\u4f55\u8981\u704c\u9189\u4ed6\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e8c\u53d4\u4f60\u4e5f\u4e0d\u77e5\u9053\uff01\u8fd9\u662f\u7237\u7237\u7684\u5429\u5490\u5440\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u54e6\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u73b0\u5728\u884c\u8e2a\u5df2\u9732\uff0c\u8981\u627e\u4ed6\u9ebb\u70e6\u7684\u4eba\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u8fd9\u4e24\u5929\u5c31\u8981\u63a5\u4e8c\u8fde\u4e09\u7684\u6765 \u4e86\uff0c\u6240\u4ee5\u7237\u7237\u5c31\u60f3\u5c06\u4ed6\u5e26\u5230\u522b\u5730\u65b9\u53bb\u907f\u4e00\u907f\u98ce\u5934\u3002 \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f46\u4e8c\u53d4\u4f60\u4e5f\u8be5\u77e5\u9053\u4ed6\u7684\u813e\u6c14\uff0c\u82e5\u4e0d\u704c\u9189\u4ed6\uff0c\u600e\u4e48\u80fd\u628a\u4ed6\u5e26\u5f97\u8d70\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u54fc\u4e86\u58f0\uff0c\u9053\uff1a\u8001\u5b9e\u8bf4\uff0c\u4f60\u7237\u7237\u505a\u7684\u4e8b\uff0c\u6211\u5b9e\u5728\u6709\u70b9\u4e0d\u61c2\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e0d\u61c2\uff1f\u4ec0\u4e48\u5730\u65b9\u4e0d\u61c2\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u674e\u5bfb\u6b22\u58f0\u6c14\u6d88\u6c89\uff0c\u4e0d\u613f\u89c1\u4eba\u7684\u65f6\u5019\uff0c\u4ed6\u8001\u4eba\u5bb6\u603b\u662f\u60f3\u6fc0\u4ed6\u51fa\u624b\uff0c\u73b0\u5728\u674e\u5bfb\u6b22 \u603b\u7b97\u51fa\u624b\u4e86\uff0c\u4ed6\u8001\u4eba\u5bb6\u53cd\u800c\u53c8\u8981\u4ed6\u8eb2\u8d77\u6765\u907f\u98ce\u5934\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6447\u4e86\u6447\u5934\u9053\uff1a\u4e8c\u53d4\u4f60\u8fd9\u5c31\u9519\u4e86\uff0c\u5fd7\u6c14\u6d88\u6c89\u548c\u907f\u98ce\u5934\u5b8c\u5168\u662f\u4e24\u56de\u4e8b\uff0c\u600e\u4e48\u53ef\u4ee5\u4e00\u6982 \u800c\u8bba\uff1f \u3000\u3000\u5979\u77a7\u4e86\u4f0f\u5728\u684c\u4e0a\u7684\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u60f3\u8981\u8fd9\u9897\u5934\u9885\u7684\u4eba\u6709\u591a\u5c11\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u51b7\u7b11\u9053\uff1a\u65e0\u8bba\u6709\u591a\u5c11\u4eba\uff0c\u9664\u4e86\u4e0a\u5b98\u91d1\u8679\u5916\uff0c\u522b\u7684\u4eba\u53c8\u4f55\u8db3\u60e7\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u9053\uff1a\u4e8c\u53d4\u4f60\u53c8\u9519\u4e86\uff0c\u6562\u5728\u674e\u5bfb\u6b22\u8111\u888b\u4e0a\u6253\u4e3b\u610f\u7684\u4eba\uff0c\u81ea\u7136\u5c31\u7edd\u4e0d\u4f1a\u662f\u5bb9\u6613\u6253\u7684 \u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u90a3\u4e9b\u4eba\u90fd\u662f\u4e9b\u4ec0\u4e48\u6837\u7684\u89d2\u8272\uff1f\u4f60\u8bf4\u7ea6\u6211\u542c\u542c\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u7537\u4eba\u6731\u8bf4\uff0c\u5148\u8bf4\u5973\u4eba\uff0c\u5176\u4e2d\u5c31\u6709\u82d7\u7586\u5927\u6b22\u559c\u5973\u83e9\u8428\u548c\u5173\u5916\u84dd\u874e\u5b50\u2014\u2014 \u3000\u3000\u5979\u53ea\u8bf4\u4e86\u4e24\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u5b59\u9a7c\u5b50\u5df2\u76b1\u8d77\u7709\u5934\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u767e\u6653\u751f\u91cd\u7537\u8f7b\u5973\uff0c\u5175\u5668\u8c31\u4e0a\u4e0d\u5217\u5973\u5b50\u9ad8\u624b\uff0c\u4f46\u8fd9\u4e24\u4e2a\u6bcd\u591c\u53c9\u7684\u540d\u5b57\uff0c\u4e8c\u53d4\u4f60 \u603b\u4e5f\u8be5\u542c\u8fc7\u7684\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u6c89\u7740\u8138\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u84dd\u874e\u5b50\u662f\u9752\u9b54\u624b\u7684\u60c5\u4eba\uff0c\u5927\u6b22\u559c\u5973\u83e9\u8428\u662f\u4e94\u6bd2\u7ae5\u5b50\u7684\u5e72\u5a18\uff0c\u5979\u4eec\u65e9\u5df2\u5728\u8981\u542c \u674e\u5bfb\u6b22\u7684\u884c\u8e2a\uff0c\u82e5\u542c\u8bf4\u4ed6\u5728\u8fd9\uff0c\u4e00\u5b9a\u4f1a\u7acb\u523b\u8d76\u6765\u3002 \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5979\u4eec\u4e24\u4eba\u4e2d\u53ea\u6709\u4e00\u4e2a\u8d76\u5230\uff0c\u5c31\u591f\u4ed6\u53d7\u7684\u4e86\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u62ff\u8d77\u5757\u62b9\u5e03\uff0c\u6162\u6162\u7684\u62b9\u7740\u684c\u5b50\u3002 \u3000\u3000\u4ed6\u5fc3\u60c5\u4e0d\u597d\u7684\u65f6\u5019\uff0c\u5c31\u559c\u6b22\u62b9\u684c\u5b50\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u8bf4\u5b8c\u4e86\u5973\u7684\uff0c\u518d\u8bf4\u7537\u7684\u3002 \u3000\u3000\u5979\u95ed\u4e0a\u773c\u775b\uff0c\u642c\u7740\u624b\u6307\u5934\u9053\uff1a\u7537\u7684\u6709\u4e0a\u5b98\u91d1\u8679\uff0c\u5415\u51e4\u5148\uff0c\u8346\u65e0\u547d\uff0c\u8fd8\u6709\u2014\u2014\u8fd8\u6709\u4e2a\u4eba\u4e8c \u53d4\u4f60\u4e00\u5b9a\u731c\u4e0d\u51fa\u662f\u8c01\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u8fd8\u662f\u5728\u6162\u6162\u7684\u62b9\u7740\u684c\u5b50\uff0c\u5934\u4e5f\u4e0d\u62ac\uff0c\u9053\uff1a\u8c01\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u80e1\u4e0d\u5f52\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u970d\u7136\u62ac\u5934\uff0c\u60ca\u95ee\u9053\uff1a\u80e1\u4e0d\u5f52\uff1f\u662f\u4e0d\u662f\u90a3\u80e1\u75af\u5b50\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u4eba\u4e00\u5411\u75af\u75af\u98a0\u98a0\uff0c\u7528\u7684\u662f\u67c4\u7af9\u5251\uff0c\u636e\u8bf4\u4ed6\u7684\u5251\u6cd5\u4e5f\u8ddf\u4ed6\u7684\u4eba\u4e00\u6837\uff0c \u75af\u75af\u98a0\u98a0\u7684\uff0c\u6709\u7684\u7cbe\u5947\u7edd\u4fd7\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u6709\u65f6\u5374\u53c8\u7cdf\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u7b80\u76f4\u8fde\u770b\u90fd\u770b\u4e0d\u5f97\uff0c\u6240\u4ee5 \u767e\u6653\u751f\u4f5c\u5175\u5668\u8c31\u65f6\uff0c\u624d\u6ca1\u6709\u5c06\u4ed6\u7684\u540d\u5b57\u5217\u4e0a\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u8138\u8272\u66f4\u6c89\u91cd\uff0c\u9053\uff1a\u9ad8\u662f\u771f\u7684\uff0c\u7cdf\u662f\u5047\u7684\u2014\u2014 \u3000\u3000\u4ed6\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u63a5\u7740\u9053\uff1a\u53ea\u4e0d\u8fc7\u6b64\u4eba\u4e00\u5411\u4e0d\u8ddf\u522b\u4eba\u6253\u4ea4\u9053\uff0c\u8fd9\u6b21\u4e3a\u4f55\u8981\u627e\u674e\u5bfb\u6b22\u7684\u9ebb \u70e6\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u542c\u8bf4\u4ed6\u662f\u88ab\u9f99\u5578\u4e91\u8bf7\u51fa\u6765\u7684\uff0c\u9f99\u5578\u4e91\u7684\u5e08\u7236\u4ee5\u524d\u597d\u50cf\u5e2e\u8fc7\u4ed6\u7684\u5fd9\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u76b1\u7709\u9053\uff1a\u8fd9\u4eba\u4e00\u5411\u96be\u627e\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff0c\u9f99\u5578\u4e91\u80fd\u627e\u5230\u4ed6\uff0c\u672c\u4e8b\u5012\u771f\u4e0d\u5c0f \u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u5c31\u56e0\u4e3a\u6b64\u4eba\u96be\u627e\uff0c\u6240\u4ee5\u9f99\u5578\u4e91\u624d\u4f1a\u4e00\u53bb\u4e24\u5e74\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4f60\u521a\u521a\u8bf4\u7684\u90a3\u5415\u51e4\u5148\uff0c\u5c31\u662f\u5175\u5668\u8c31\u4e0a\u540d\u5217\u7b2c\u4e94\u7684\u6e29\u5019\u94f6\u621f\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u627e\u7684\u5e76\u4e0d\u5355\u53ea\u662f\u674e\u5bfb\u6b22\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u4ed6\u8fd8\u60f3\u627e\u8c01\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6b64\u4eba\u8fd1\u5e74\u6765\u7ec3\u4e86\u51e0\u624b\u5f88\u7279\u522b\u7684\u529f\u592b\uff0c\u6240\u4ee5\u51e1\u662f\u5175\u5668\u8c31\u4e0a\u5217\u540d\u5728\u4ed6\u4e4b\u81c6\u7684\u4eba\uff0c \u4ed6\u90fd\u60f3\u627e\u6765\u6597\u4e00\u6597\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u90a3\u8346\u2014\u2014\u8346 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u8346\u65e0\u547d\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u8346\u65e0\u547d\u662f\u4e0a\u5b98\u91d1\u8679\u5c5e\u4e0b\u7b2c\u4e00\u53f7\u7684\u6253\u624b\uff01 \u3000\u3000\u5b59\u9a7c\u5b50\u76b1\u7709\u9053\uff1a\u4f60\u600e\u4f1a\u4ece\u672a\u542c\u8bf4\u8fc7\u4ed6\u7684\u540d\u5b57\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6b64\u4eba\u51fa\u9053\u624d\u4e0d\u8fc7\u4e24\u5e74\u591a\uff0c\u542c\u7237\u7237\u8bf4\uff0c\u6b66\u6797\u540e\u4ee3\u4e00\u4ee3\u7684\u9ad8\u624b\u4e2d\uff0c\u6700\u5389\u5bb3\u7684\u4e24\u4e2a \u5c31\u662f\u8fd9\u8346\u65e0\u547d\u548c\u963f\u98de\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u54e6\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u7528\u7684\u4e5f\u662f\u5251\uff0c\u51fa\u624b\u4e5f\u548c\u963f\u98de\u4e00\u6837\uff0c\u53c8\u72e0\u3001\u53c8\u51c6\u3001\u53c8\u5feb\uff01\u9664\u6b64\u4e4b\u5916\uff0c\u8fd9\u4eba\u8fd8 \u6709\u4e00\u6837\u6700\u53ef\u6015\u7684\u5730\u65b9\uff01 \u3000\u3000\u5b59\u9a7c\u5b50\u5728\u542c\u7740\uff0c\u542c\u5f97\u5f88\u7559\u795e\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u5e73\u65f6\u5f88\u5c11\u51fa\u624b\uff0c\u4f46\u53ea\u8981\u4e00\u548c\u4eba\u4ea4\u4e0a\u624b\uff0c\u5c31\u8fde\u81ea\u5df1\u7684\u6027\u547d\u90fd\u4e0d\u8981\u4e86\uff0c\u6bcf\u4e00\u62db \u7528\u7684\u90fd\u662f\u62fc\u547d\u7684\u62db\u5f0f\uff0c\u4ed6\u81ea\u79f0\u8346\u65e0\u547d\uff0c\u610f\u601d\u5c31\u662f\u8bf4\u4ed6\u8fd9\u6761\u547d\u65e9\u5df2\u548c\u4eba\u62fc\u6389\u4e86\uff0c\u6240\u4ee5\u6839\u672c\u5c31\u4e0d \u628a\u81ea\u5df1\u7684\u6b7b\u6d3b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u5b59\u9a7c\u5b50\u6c89\u9ed8\u5f97\u66f4\u4e45\uff0c\u624d\u95ee\u9053\uff1a\u4f60\u7237\u7237\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u8001\u4eba\u5bb6\u548c\u6211\u7ea6\u597d\u5728\u57ce\u5916\u89c1\u9762\u2014\u2014 \u3000\u3000\u5979\u62bf\u5634\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4ed6\u8001\u4eba\u5bb6\u77e5\u9053\u6211\u4e00\u5b9a\u6709\u6cd5\u5b50\u5c06\u674e\u5bfb\u6b22\u5e26\u53bb\u7684\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u6c89\u91cd\u7684\u9762\u5bb9\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u6447\u7740\u5934\u9053\uff1a\u4f60\u8fd9\u5c0f\xd7\u5934\u5012\u771f\u662f\u4e2a\u9b3c\u7075\u7cbe\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u561f\u8d77\u5634\uff0c\u4e0d\u4f9d\u9053\uff1a\u4eba\u5bb6\u5df2\u7ecf\u5feb\u4e8c\u5341\u4e86\uff0c\u4e8c\u53d4\u8fd8\u8bf4\u4eba\u5bb6\u662f\u5c0f\xd7\u5934\u3002 \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u5403\u4eba\u7684\u874e\u5b50>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u5403\u4eba\u7684\u874e\u5b50 \u3000\u3000\u5b59\u9a7c\u5b50\u7a81\u53c8\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u7684\u786e\u5df2\u7ecf\u4e0d\u5c0f\u4e86\uff0c\u4e0a\u6b21\u6211\u770b\u5230\u4f60\u7684\u65f6\u5019\uff0c \u4f60\u8fd8\u53ea\u6709\u4e94\u516d\u5c81\uff0c\u4f46\u73b0\u5728\u4f60\u5df2\u7ecf\u662f\u5927\u4eba\u4e86\u2014\u2014 \u3000\u3000\u4ed6\u5782\u5934\u671b\u7740\u624b\u91cc\u7684\u62b9\u5e03\uff0c\u53c8\u5f00\u59cb\u6162\u6162\u7684\u62b9\u7740\u684c\u5b50\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u4f4e\u4e0b\u4e86\u5934\uff0c\u9053\uff1b\u4e8c\u53d4\u5df2\u6709\u5341\u4e09\u56db\u5e74\u6ca1\u6709\u56de\u8fc7\u5bb6\u4e86\u4e48\uff1f\u5b59\u9a7c\u5b50\u6c89\u91cd\u7684\u70b9\u4e86\u70b9\u5934\uff0c \u9053\uff1a\u4e0d\u9519\uff0c\u5341\u56db\u5e74\uff0c\u8fd8\u5dee\u51e0\u5929\u5c31\u662f\u5341\u56db\u5e74\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e8c\u53d4\u4e3a\u4ec0\u4e48\u4e0d\u56de\u5bb6\u77a7\u77a7\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u5ffd\u7136\u91cd\u91cd\u4e00\u62cd\u684c\u5b50\uff0c\u5389\u58f0\u9053\uff1a\u6211\u65e2\u5df2\u7b54\u5e94\u5728\u8fd9\u91cc\u66ff\u4eba\u5bb6\u5b88\u62a4\u5341\u4e94\u5e74\uff0c\u5c31\u5f97\u5728\u8fd9\u91cc \u5341\u4e94\u5e74\uff0c\u8fde\u4e00\u5929\u90fd\u4e0d\u80fd\u5c11\uff0c\u4f60\u4eec\u8fd9\u79cd\u4eba\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5c31\u5f97\u50cf\u9489\u5728\u5899\u4e0a\u4e00\u6837\u7262\u9760\uff0c\u8fd9\u9053\u7406\u4f60\u660e \u4e0d\u660e\u767d\u3002 \u3000\u3000\u5c0f\u7ea2\u9053\uff1a\u6211\u660e\u767d\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5b59\u9a7c\u5b50\u7684\u76ee\u5149\u624d\u53c8\u56de\u5230\u624b\u91cc\u7684\u62b9\u5e03\u4e0a\u3002 \u3000\u3000\u5f53\u4ed6\u5f00\u59cb\u62b9\u684c\u5b50\u7684\u65f6\u5019\uff0c\u4ed6\u9510\u5229\u7684\u76ee\u5149\u5c31\u9eef\u6de1\u4e86\u4e0b\u6765\uff0c\u90a3\u79cd\u5484\u5484\u903c\u4eba\u7684\u51c4\u5389\u5149\u5f69\uff0c\u7acb\u523b \u5c31\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u5df2\u62b9\u4e86\u5341\u56db\u5e74\u684c\u5b50\uff0c\u65e0\u8bba\u4ed6\u4ee5\u524d\u662f\u4ec0\u4e48\u4eba\uff0c\u90fd\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff0c\u56e0\u4e3a\u5f53\u4ed6\u5728\u62b9 \u684c\u5b50\u6cb9\u57a2\u7684\u65f6\u5019\uff0c\u4e5f\u5c31\u662f\u5728\u62b9\u7740\u81ea\u5df1\u7684\u5149\u5f69\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5f90\u5f90\u9053\uff1a\u8fd9\u4e9b\u5e74\u6765\uff0c\u5bb6\u91cc\u7684\u4eba\u90fd\u8fd8\u597d\u5417\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u8fd9\u624d\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u90fd\u5f88\u597d\uff0c\u5927\u5ac2\u548c\u4e09\u5ac2\u4eca\u5e74\u90fd\u6709\u5b9d\u5b9d\uff0c\u6700\u5999\u7684\u662f\uff0c\u56db\u59d1\u5c45\u7136\u4e5f \u751f\u4e86\u5bf9\u53cc\u80de\u80ce\uff0c\u6240\u4ee5\u4eca\u5e74\u56db\u53d4\u548c\u5927\u54e5\u3001\u4e09\u54e5\uff0c\u90fd\u4e00\u5b9a\u4f1a\u8d76\u56de\u53bb\u8fc7\u5e74\u2014\u2014\u4eca\u5e74\u8fc7\u5e74\u4e00\u5b9a\u4f1a\u6bd4\u5f80 \u5e74\u66f4\u70ed\u95f9\u591a\u4e86\uff0c\u5979\u773c\u89d2\u770b\u89c1\u5b59\u9a7c\u5b50\u6697\u6de1\u7684\u9762\u8272\uff0c\u7acb\u523b\u505c\u4f4f\u4e86\u5634\uff0c\u5782\u9996\u9053\uff1a\u5927\u5bb6\u90fd\u5728\u76fc\u671b\u7740\u4e8c \u53d4\u80fd\u5feb\u4e9b\u56de\u53bb\uff0c\u4e0d\u77e5\u9053\u2014\u2014 \u3000\u3000\u5b59\u9a7c\u5b50\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u4f60\u56de\u53bb\u544a\u8bc9\u4ed6\u4eec\uff0c\u7b49\u660e\u5e74\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u53ef\u4ee5\u56de\u53bb\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u62cd\u624b\u9053\uff1a\u597d\u6781\u4e86\uff0c\u6211\u8fd8\u8bb0\u5f97\u4e8c\u53d4\u505a\u7684\u70df\u82b1\u6700\u597d\u2014\u2014 \u3000\u3000\u5b59\u9a7c\u5b50\u7b11\u9053\uff1a\u660e\u5e74\u6211\u4e00\u5b9a\u66ff\u4f60\u505a\uff0c\u4f46\u73b0\u5728\u2014\u2014\u73b0\u5728\u4f60\u8fd8\u662f\u5feb\u8d70\u5427\uff0c\u514d\u5f97\u4f60\u7237\u7237\u7b49\u5f97\u7740\u6025 \u3002 \u3000\u3000\u4ed6\u77a7\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u53c8\u76b1\u7709\u9053\uff1a\u4f46\u8fd9\u4e48\u5927\u4e00\u4e2a\u4eba\uff0c\u4f60\u600e\u4e48\u80fd\u5e26\u5f97\u8d70\u5462\uff1f \u3000\u3000\u5c0f\u7ea2\u9053\uff1a\u6211\u5c31\u5f53\u4ed6\u662f\u6761\u9189\u732b\uff0c\u5f80\u8eab\u4e0a\u4e00\u80cc\u5c31\u884c\u4e86\u3002 \u3000\u3000\u5979\u521a\u7ad9\u8d77\u6765\uff0c\u7a81\u7136\u4e00\u4eba\u51b7\u51b7\u9053\uff1a\u4f60\u53ef\u4ee5\u8d70\uff0c\u4f46\u8fd9\u8981\u9189\u732b\u5374\u5f97\u7559\u4e0b\u6765\uff01 \u3000\u3000\u8fd9\u58f0\u97f3\u6025\u4fc3\u3001\u4f4e\u6c89\uff0c\u800c\u4e14\u8fd8\u6709\u4e9b\u5636\u54d1\uff0c\u4f46\u5374\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u9b45\u529b\uff0c\u4eff\u4f5b\u53ef\u4ee5\u5524\u8d77\u7537\u4eba\u7684 \u60c5\u6b32\u3002 \u3000\u3000\u8fd9\u65e0\u7591\u662f\u4e2a\u5973\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u548c\u5b59\u5c0f\u7ea2\u9762\u5bf9\u7740\u524d\u95e8\uff0c\u8fd9\u58f0\u97f3\u5374\u662f\u81ea\u901a\u5411\u540e\u9662\u7684\u5c0f\u95e8\u65c1\u53d1\u51fa\u6765\u7684\uff0c\u5979\u4ec0\u4e48\u65f6\u5019\u8fdb \u4e86\u8fd9\u5c4b\u5b50\uff0c\u5b59\u5c0f\u7ea2\u548c\u5b59\u9a7c\u5b50\u7adf\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u8138\u8272\u4e00\u6c89\uff0c\u53cd\u624b\u5c06\u62b9\u5e03\u7529\u4e86\u51fa\u3002 \u3000\u3000\u4ed6\u62b9\u4e86\u5341\u56db\u5e74\u684c\u5b50\uff0c\u6bcf\u5929\u82e5\u662f\u62b9\u4e8c\u5341\u6b21\uff0c\u4e00\u5e74\u5c31\u662f\u4e03\u5343\u4e09\u767e\u6b21\uff0c\u5341\u56db\u5e74\u5c31\u662f\u5341\u4e07\u96f6\u4e24\u5343 \u4e24\u767e\u6b21\u3002\u65e0\u8bba\u8c01\u62b9\u4e86\u5341\u4e07\u591a\u6b21\u684c\u5b50\uff0c\u7528\u52b2\u603b\u8981\u6bd4\u5e73\u5e38\u4eba\u5927\u4e9b\u3002 \u3000\u3000\u4f55\u51b5\u5b59\u9a7c\u5b50\u7684\u5927\u9e70\u722a\u529b\u672c\u5df2\u9a70\u540d\u6c5f\u6e56\uff0c\u6b64\u523b\u5c06\u8fd9\u5806\u62b9\u5e03\u7529\u51fa\u53bb\uff0c\u631f\u7740\u52b2\u98ce\uff0c\u529b\u9053\u7edd\u4e0d\u5728 \u5929\u4e0b\u4efb\u4f55\u4e00\u79cd\u6697\u5668\u4e4b\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u7830\u7684\u4e00\u58f0\uff0c\u5c18\u571f\u98de\u626c\uff0c\u7816\u5899\u7adf\u88ab\u8fd9\u5806\u62b9\u5e03\u6253\u51fa\u4e86\u4e2a\u5927\u6d1e\uff0c\u4f46\u7ad9\u5728\u95e8\u65c1\u7684\u4eba\u8fd8\u662f\u597d\u597d \u5730\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5979\u8eab\u5b50\u597d\u50cf\u5e76\u6ca1\u6709\u79fb\u52a8\u8fc7\uff0c\u770b\u5979\u73b0\u5728\u7ad9\u7684\u5730\u65b9\uff0c\u8fd9\u5806\u62b9\u5e03\u672c\u8be5\u5c06\u5979\u7684\u80f8\u53e3\u6253\u51fa\u4e2a\u5927\u6d1e\u6765 \u624d\u662f\u3002 \u3000\u3000\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u8fd9\u5806\u62b9\u5e03\u504f\u6ca1\u6709\u6253\u7740\u5979\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u7684\u8170\u5f88\u7ec6\uff0c\u6240\u4ee5\u626d\u8d77\u6765\u7279\u522b\u65b9\u4fbf\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u52a8\u4eba\u7684\u5730\u65b9\u5e76\u4e0d\u6b62\u5979\u7684\u7ec6\u8170\u3002 \u3000\u3000\u5979\u7684\u817f\u5f88\u957f\u3001\u5f88\u76f4\uff0c\u8be5\u7626\u7684\u5730\u65b9\u5979\u7edd\u4e0d\u80d6\uff0c\u8be5\u80d6\u7684\u5730\u65b9\uff0c\u5979\u4e5f\u7edd\u4e0d\u7626\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u957f\u800c\u5a9a\uff0c\u5634\u5374\u5f88\u5927\uff0c\u5634\u5507\u5f88\u539a\u3002 \u3000\u3000\u5979\u7684\u76ae\u80a4\u867d\u767d\uff0c\u4f46\u5374\u5f88\u7c97\u7cd9\uff0c\u800c\u4e14\u6bdb\u53d1\u5f88\u6d53\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u80fd\u7b97\u662f\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u4f46\u5374\u6709\u53ef\u4ee5\u8bf1\u4eba\u72af\u7f6a\u7684\u5a9a\u529b\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u56de\u5934\uff0c\u76ef\u7740\u5979\u3002 \u3000\u3000\u5979\u4e5f\u5728\u76ef\u7740\u5b59\u9a7c\u5b50\uff0c\u90a3\u773c\u8272\u770b\u6765\u5c31\u597d\u50cf\u5979\u5df2\u5c06\u5b59\u9a7c\u5b50\u5f53\u505a\u4e16\u4e0a\u6700\u82f1\u4fca\u3001\u6700\u53ef\u7231\u7684\u4eba\uff0c\u5df2 \u5c06\u5b59\u9a7c\u5b50\u5f53\u7740\u5979\u7684\u60c5\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u4f46\u7b49\u5979\u7684\u76ee\u5149\u5230\u5b59\u5c0f\u7ea2\u65f6\uff0c\u5c31\u7acb\u523b\u89c9\u5f97\u51b7\u9177\u8d77\u6765\u3002 \u3000\u3000\u5979\u5bf9\u4efb\u4f55\u5973\u4eba\u90fd\u8ba8\u538c\u5f97\u5f88\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u84dd\u874e\u5b50\uff1f \u3000\u3000\u84dd\u874e\u5b50\u7b11\u4e86\u3002 \u3000\u3000\u5979\u7b11\u8d77\u6765\u7684\u65f6\u5019\uff0c\u773c\u775b\u772f\u5f97\u66f4\u7ec6\uff0c\u66f4\u957f\uff0c\u5c31\u50cf\u662f\u4e00\u6761\u7ebf\u3002 \u3000\u3000\u5979\u5a9a\u7b11\u9053\uff1a\u4f60\u771f\u662f\u597d\u773c\u529b\uff0c\u6709\u773c\u5149\u7684\u7537\u4eba\uff0c\u6211\u603b\u662f\u559c\u6b22\u7684\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u677f\u7740\u8138\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u4e0d\u559c\u6b22\u5bf9\u4ed8\u5987\u4eba\uff0c\u4ed6\u6839\u672c\u4e0d\u4f1a\u5bf9\u4ed8\u5973\u4eba\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4f46\u6211\u7684\u773c\u5149\u4e5f\u4e0d\u9519\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u8c01\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u5389\u58f0\u9053\uff1a\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u5c45\u7136\u4e0d\uff1f \u3000\u3000\u84dd\u874e\u5b50\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u672c\u662f\u4e0d\u613f\u5f97\u7f6a\u4f60\u4eec\uff0c\u4f46\u8fd9\u9189\u732b\u6211\u5374\u975e\u5e26\u8d70\u4e0d\u53ef\u3002 \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u4e5f\u8bb8\u4e0d\u77e5\u9053\uff0c\u6211\u8981\u627e\u4e2a\u80fd\u4ee4\u6211\u6ee1\u610f\u7684\u7537\u4eba\u6709\u591a\u4e48\u56f0\u96be\uff0c\u597d\u5bb9 \u6613\u627e\u5230\u4e00\u4e2a\uff0c\u5374\u88ab\u8fd9\u9189\u732b\u6740\u6b7b\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u9053\uff1a\u4f0a\u54ed\u53ef\u4e0d\u662f\u4ed6\u6740\u6b7b\u7684\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u65e0\u8bba\u662f\u4e0d\u662f\u4ed6\u6740\u6b7b\u7684\uff0c\u8fd9\u7b14\u5e10\u6211\u5374\u5df2\u7b97\u5230\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u65e0\u8bba\u4f60\u600e\u4e48\u6837\u7b97\u5e10\uff0c\u90fd\u4f11\u60f3\u80fd\u5e26\u5f97\u8d70\u5979\u3002 \u3000\u3000\u84dd\u874e\u5b50\u53f9\u7740\u6c14\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u4f60\u4eec\u4e0d\u4f1a\u8fd9\u4e48\u5bb9\u6613\u8ba9\u6211\u5e26\u8d70\u7684\uff0c\u6211\u53c8\u4e0d\u592a\u613f\u610f\u8ddf\u4f60\u4eec\u52a8\u624b\uff0c \u8fd9\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u5979\u5ffd\u7136\u5411\u540e\u9762\u62db\u4e86\u62db\u624b\uff0c\u8f7b\u5524\u9053\uff1b\u4f60\u8fc7\u6765\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u8fd9\u624d\u770b\u4e00\u540e\u9662\u4e2d\u8fd8\u6709\u6761\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u5f88\u9ad8\u5927\uff0c\u84dd\u874e\u5b50\u4e00\u62db\u624b\uff0c\u4ed6\u5c31\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u8863\u886b\u534e\u4e3d\uff0c\u6f06\u4eae\u7684\u80e1\u5b50\u4fee\u9970\u5f97\u5f88\u6574\u9f50\uff0c\u8170\u5e26\u4e0a\u6302\u7740\u67c4\u4e5d\u73af\u5200\uff0c\u770b\u6765\u5f53\u771f\u662f\u76f8\u8c8c\u5802 \u5802\u3001\u5a01\u98ce\u51db\u51db\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4f60\u4eec\u53ef\u8ba4\u5f97\u4ed6\u662f\u8c01\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u521a\u6447\u4e86\u6447\u5934\uff0c\u5b59\u5c0f\u7ea2\u5df2\u62a2\u7740\u9053\uff1a\u6211\u8ba4\u5f97\u4ed6\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4f60\u771f\u7684\u8ba4\u5f97\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u59d3\u695a\uff0c\u53eb\u695a\u76f8\u7fbd\uff0c\u5916\u53f7\u53eb\u6d3b\u9738\u738b\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5a9a\u7b11\u7740\u779f\u4e86\u8fd9\u4f4d\u6d3b\u9738\u738b\u4e00\u773c\uff0c\u9053\uff1a\u8fde\u5c0f\u59b9\u90fd\u8ba4\u5f97\u4f60\uff0c\u770b\u6765\u4f60\u7684\u540d\u5b57\u53ef\u771f\u4e0d\u5c0f\u3002 \u3000\u3000\u6d3b\u9738\u738b\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u5f97\u610f\u4e4b\u8272\uff0c\u8170\u633a\u5f97\u66f4\u76f4\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6c5f\u6e56\u4e2d\u6709\u540d\u6c14\u7684\u4eba\uff0c\u5927\u5927\u5c0f\u5c0f\u6211\u5012\u5dee\u4e0d\u591a\u5168\u8ba4\u8bc6\uff0c\u4f46\u6211\u5374\u4e0d\u77e5\u9053\u8fd9\u4f4d\u695a\u76f8\u7fbd \u600e\u4e48\u4f1a\u548c\u4f60\u8d70\u5728\u4e00\u8d77\u3002 \u3000\u3000\u84dd\u874e\u5b50\u7b11\u9053\uff1a\u4ed6\u662f\u5728\u8def\u4e0a\u540a\u4e0a\u6211\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u4e86\uff0c\u9053\uff1a\u662f\u4ed6\u540a\u4e0a\u4f60\uff0c\u8fd8\u662f\u4f60\u540a\u4e0a\u4ed6\uff1f \u3000\u3000\u84dd\u874e\u5b50\u7b11\u9053\uff1a\u5f53\u7136\u662f\u4ed6\u540a\u4e0a\u6211\u2014\u2014\u4f60\u4eec\u53ea\u77e5\u9053\u695a\u76f8\u7fbd\u7684\u540d\u6c14\u54cd\u3001\u6b66\u529f\u9ad8\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u540a \u5973\u4eba\u7684\u672c\u4e8b\u66f4\u662f\u9ad8\u4eba\u4e00\u7b79\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u65e9\u5df2\u6ee1\u9762\u6012\u5bb9\uff0c\u5fcd\u4e0d\u4f4f\u559d\u9053\uff1a\u4f60\u5e26\u8fd9\u4eba\u6765\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u8fd9\u4f4d\u695a\u76f8\u7fbd\u7684\u786e\u5f97\u8fc7\u771f\u4f20\uff0c\u4e5d\u4e5d\u516b\u5341\u4e00\u624b\u4e07\u80dc\u8fde\u73af\u5200\u4f7f\u51fa\u6765\uff0c\u7b49\u95f2\u4e03\u516b\u4e0b\u5341 \u4eba\u4e5f\u4f11\u60f3\u8fd1\u5f97\u4e86\u4ed6\u7684\u8eab\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u9053\uff1a\u54fc\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u6211\u82e5\u8bf4\u6211\u4e00\u62db\u5c31\u80fd\u8981\u4ed6\u7684\u547d\uff0c\u4f60\u4eec\u4fe1\u4e0d\u4fe1\uff1f \u3000\u3000\u695a\u76f8\u7fbd\u4e00\u772d\u5f97\u610f\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5931\u58f0\u9053\uff1a\u4f60\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u84dd\u874e\u5b50\u67d4\u58f0\u9053\uff1a\u6211\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\uff0c\u53ea\u4e0d\u8fc7\u8bf4\u60f3\u8981\u4f60\u7684\u547d\u800c\u5df2\u3002 \u3000\u3000\u695a\u76f8\u7fbd\u8138\u8272\u53d1\u9752\uff0c\u6014\u4e86\u534a\u664c\uff0c\u9053\uff1a\u4f60\u5728\u8bf4\u7b11\u8bdd\u3002 \u3000\u3000\u84dd\u874e\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5e38\u8a00\u9053\uff0c\u4e00\u591c\u592b\u59bb\u767e\u65e5\u6069\uff0c\u4f60\u81ea\u7136\u4ee5\u4e3a\u6211\u4e0d\u4f1a\u6740\u4f60\u7684\uff0c\u662f\u5417\uff1f \u3000\u3000\u695a\u76f8\u7fbd\u9053\uff1a\u6211\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff0c\u874e\u5b50\u5728\u6211\u4eec\u5317\u65b9\u6700\u591a\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u90a3\u4e48\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6bcd\u874e\u5b50\u5374\u6709\u79cd\u5947\u602a\u7684\u6bdb\u75c5\u3002 \u3000\u3000\u695a\u76f8\u7fbd\u9762\u8272\u6709\u4e9b\u53d8\u4e86\uff0c\u8fd8\u662f\u52c9\u5f3a\u7b11\u9053\uff1a\u4f46\u4f60\u5374\u4e0d\u662f\u874e\u5b50\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5a9a\u7b11\u9053\uff1a\u8c01\u8bf4\u6211\u4e0d\u662f\u874e\u5b50\uff1f\u6211\u660e\u660e\u662f\u84dd\u874e\u5b50\u5440\uff0c\u4f60\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u695a\u76f8\u7fbd\u7684\u4eba\u7acb\u523b\u8df3\u4e86\u8d77\u6765\uff0c\u5f80\u540e\u9762\u8df3\u5f00\u4e03\u516b\u5c3a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u684c\u5b50\u4e5f\u88ab\u4ed6\u649e\u7ffb\u4e86\uff0c\u4ed6\u4e0b\u76d8 \u5012\u5f88\u7a33\uff0c\u5e76\u6ca1\u6709\u88ab\u7ffb\u5012\u3002 \u3000\u3000\u53ea\u542c\u54d7\u5566\u5566\u4e00\u58f0\uff0c\u4ed6\u5df2\u62d4\u51fa\u4e86\u8170\u7554\u7684\u4e5d\u73af\u5200\u3002 \u3000\u3000\u4ed6\u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u81ea\u7136\u542c\u8fc7\u84dd\u874e\u5b50\u7684\u5927\u540d\uff0c\u4f46\u4ed6\u5374\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u6bd4\u5c0f\u9c7c\u8fd8\u5bb9\u6613\u4e0a\u7684\u5973\u4eba \uff0c\u5c31\u662f\u84dd\u874e\u5b50\u3002 \u3000\u3000\u84dd\u874e\u5b50\u67d4\u58f0\u9053\uff1a\u6211\u529d\u4f60\uff0c\u4e0b\u6b21\u4f60\u82e5\u60f3\u5728\u8def\u4e0a\u540a\u5973\u4eba\uff0c\u6700\u597d\u5148\u5f04\u6e05\u695a\u5979\u7684\u5e95\u7ec6\uff0c\u53ea\u53ef\u60dc\uff0d \uff0d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u8d70\u5411\u695a\u76f8\u7fbd\uff0c\u9053\uff1a\u53ea\u53ef\u60dc\u4f60\u6c38\u8fdc\u6ca1\u6709\u4e0b\u6b21\u4e86\u3002 \u3000\u3000\u695a\u76f8\u7fbd\u5927\u543c\u9053\uff1a\u7ad9\u4f4f\uff0c\u4f60\u518d\u5f80\u524d\u8d70\u4e00\u6b65\uff0c\u6211\u5c31\u5bb0\u4e86\u4f60\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5a9a\u773c\u5982\u4e1d\uff0c\u817b\u58f0\u9053\uff1a\u597d\uff0c\u4f60\u5bb0\u4e86\u6211\u5427\uff0c\u6211\u5012\u771f\u60f3\u6b7b\u5728\u4f60\u624b\u91cc\u3002 \u3000\u3000\u695a\u76f8\u7fbd\u5927\u559d\u4e00\u58f0\uff0c\u4e5d\u73af\u5200\u6a2a\u626b\u800c\u51fa\u3002 \u3000\u3000\u5200\u98ce\u864e\u864e\uff0c\u5200\u73af\u76f8\u51fb\uff0c\u58f0\u52bf\u679c\u7136\u60ca\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u53ea\u4f7f\u51fa\u4e86\u8fd9\u4e00\u5200\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u9053\u84dd\u6676\u6676\uff0c\u78a7\u68ee\u68ee\u7684\u5bd2\u5149\u4e00\u95ea\uff0c\u695a\u76f8\u7fbd\u5df2\u60e8\u547c\u7740\u5012\u4e86\u4e0b\u53bb\uff0c\u751a\u81f3\u8fde\u8fd9\u58f0\u60e8\u547c\u90fd\u6ca1 \u6709\u5b8c\u5168\u53d1\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u4f24\u75d5\uff0c\u53ea\u662f\u54bd\u5589\u4e0a\u591a\u4e86\u4e24\u70b9\u9c9c\u7ea2\u7684\u8840\u8ff9\uff0c\u6b63\u5b9b\u5982\u88ab\u874e\u5b50\u54ac\u8fc7\u4e00\u6837\uff0c \u84dd\u874e\u5b50\u7684\u8863\u670d\u867d\u7d27\uff0c\u8896\u5b50\u5374\u5f88\u957f\uff0c\u8fd9\u4f7f\u5979\u770b\u6765\u6709\u4e9b\u98d8\u98d8\u6b32\u4ed9\u7684\u611f\u89c9\uff0c\u4f7f\u5979\u7684\u98ce\u59ff\u770b\u6765\u66f4\u7f8e\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u548c\u5b59\u5c0f\u7ea2\u51b7\u8a00\u65c1\u89c2\uff0c\u5e76\u6ca1\u6709\u51fa\u624b\u62e6\u963b\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u613f\u51fa\u624b\u2014\u2014\u4e00\u4e2a\u968f \u4fbf\u5c31\u5728\u8def\u4e0a\u540a\u5973\u4eba\u7684\u7537\u4eba\uff0c\u603b\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\u3002 \u3000\u3000\u84dd\u874e\u5b50\u8fd8\u5728\u4fef\u9996\u77a7\u7740\u695a\u76f8\u7fbd\u3002 \u3000\u3000\u5979\u77a7\u4e86\u5f88\u4e45\uff0c\u4eff\u4f5b\u662f\u5728\u6b23\u8d4f\u7740\u81ea\u5df1\u7684\u6210\u7ee9\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u53c8\u7b11\u4e86\uff0c\u7b11\u5f97\u66f4\u5a9a\u3002 \u3000\u3000\u5979\u5a9a\u7b11\u7740\u9053\uff1a\u6211\u53ea\u7528\u4e86\u4e00\u62db\uff0c\u4f60\u4eec\u73b0\u5728\u603b\u8be5\u76f8\u4fe1\u4e86\u5427\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u548c\u5b59\u5c0f\u7ea2\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u6211\u7684\u6b66\u529f\u8fd8\u7b97\u4e0d\u9519\u5427\u3002 \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u4eba\u56de\u7b54\uff0c\u84dd\u874e\u5b50\uff1a\u4f0a\u54ed\u7684\u9752\u9b54\u624b\u867d\u7136\u5728\u5175\u5668\u4e2d\u6392\u5217\u7b2c\u4e5d\uff0c\u4f46\u767e\u6653\u751f\u82e5\u662f\u5c06\u6211\u4e5f \u7b97\u4e0a\uff0c\u4ed6\u81f3\u5c11\u8981\u9000\u5230\u7b2c\u5341\uff0c\u4e24\u4f4d\u8bf4\u5bf9\u4e0d\u5bf9\uff1f \u3000\u3000\u8fd9\u5012\u4e0d\u662f\u5047\u8bdd\u3002\u5979\u51fa\u624b\u7684\u786e\u6bd4\u4f0a\u54ed\u66f4\u5feb\uff0c\u66f4\u6bd2\uff01 \u3000\u3000\u84dd\u874e\u5b50\u773c\u775b\u779f\u7740\u5b59\u9a7c\u5b50\uff0c\u67d4\u58f0\u9053\uff1a\u51ed\u6211\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u603b\u53ef\u4ee5\u5c06\u8fd9\u9189\u732b\u5e26\u8d70\u4e86\u5427\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u677f\u7740\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u4e0d\u53ef\u4ee5\uff01 \u3000\u3000\u84dd\u874e\u5b50\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u6211\u7a76\u7adf\u8981\u600e\u4e48\u6837\u624d\u80fd\u5c06\u4ed6\u5e26\u8d70\u5462\uff1f\u96be\u9053\u8981\u6211\u966a\u4f60\u4e0a\u5e8a\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u6012\u559d\u4e00\u58f0\uff0c\u53cc\u624b\u9f50\u51fa\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5de6\u624b\u5982\u722a\uff0c\u5de6\u62f3\u51fb\u51fa\uff0c\u77f3\u7834\u5929\u60ca\uff0c\u53f3\u722a\u5982\u94a9\uff0c\u53d8\u5316\u4e07\u5343\uff0c\u867d\u662f\u8d64\u624b\u7a7a\u62f3\uff0c\u4f46\u5a01\u52bf \u5374\u6bd4\u695a\u76f8\u7fbd\u65b9\u624d\u90a3\u4e00\u5200\u66f4\u5f3a\u5341\u500d\u3002 \u3000\u3000\u84dd\u874e\u5b50\u8170\u80a2\u4e00\u626d\uff0c\u5ffd\u7136\u5c31\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u4e00\u62db\u51fb\u51fa\uff0c\u5979\u5df2\u5230\u4e86\u5b59\u9a7c\u5b50\u8eab\u540e\u3002 \u3000\u3000\u5e78\u597d\u5b59\u9a7c\u5b50\u975e\u5eb8\u624b\uff0c\u5de6\u62f3\u7a81\u66f2\uff0c\u5c06\u8fd9\u4e00\u62f3\u51fb\u51fa\u53bb\u7684\u529b\u91cf\u677e\u5f00\uff0c\u53f3\u722a\u5374\u7a81\u7136\u7d27\u63e1\u6210\u62f3\uff0c\u5c06 \u8fd9\u4e00\u722a\u6293\u51fa\u53bb\u7684\u529b\u91cf\u786c\u751f\u751f\u6536\u4e86\u56de\u6765\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u624b\uff0c\u6700\u96be\u7684\u5c31\u662f\u5c06\u5df2\u51fb\u51fa\u7684\u62db\u5f0f\u534a\u9014\u800c\u5e9f\u6536\u56de\uff0c\u8981\u77e5\u4e00\u62db\u51fb\u51fa\uff0c\u4fbf\u5982\u7bad\u5df2\u79bb\u5f26\uff0c \u82e5\u662f\u534a\u9014\u6492\u62db\uff0c\u603b\u96be\u514d\u6709\u4e9b\u751f\u786c\u52c9\u5f3a\u3002 \u3000\u3000\u4f46\u5b59\u9a7c\u5b50\u6b64\u523b\u8fd9\u4e00\u62db\u6536\u53d1\u4e4b\u95f4\uff0c\u5374\u7edd\u4e0d\u62d6\u6ce5\u5e26\u6c34\u3002 \u3000\u3000\u522b\u4eba\u82e5\u662f\u5c06\u624b\u4e0a\u529b\u91cf\u64a4\u56de\uff0c\u8eab\u5b50\u4e5f\u96be\u514d\u8981\u968f\u7740\u540e\u9000\uff0c\u90a3\u6b63\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u9001\u5230\u84dd\u874e\u5b50\u624b\u91cc \u3002 \u3000\u3000\u4f46\u5b59\u9a7c\u5b50\u5e78\u597d\u662f\u4e2a\u9a7c\u5b50\uff0c\u4ed6\u624b\u4e0a\u529b\u91cf\u4e0a\u64a4\uff0c\u5c31\u5168\u90fd\u805a\u5728\u4ed6\u80cc\u540e\u7684\u9a7c\u950b\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u80a9\u4e00\u7f29\uff0c\u9a7c\u5cf0\u5df2\u5411\u84dd\u874e\u5b50\u649e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u6b63\u4e5f\u662f\u5b59\u9a7c\u5b50\u7684\u6210\u540d\u7edd\u6280\u4e4b\u4e00\uff0c\u4ed6\u80cc\u540e\u86c7\u5cf0\u5df2\u7ec3\u5f97\u575a\u8fc7\u7cbe\u94a2\uff0c\u8fd9\u4e00\u649e\u4e4b\u529b\uff0c\u4f55\u6b62 \u767e\u65a4\u3002 \u3000\u3000\u84dd\u874e\u5b50\u81ea\u7136\u662f\u8bc6\u8d27\u7684\uff0c\u8170\u80a2\u4e00\u626d\uff0c\u957f\u8896\u98de\u821e\uff0c\u4eba\u5df2\u5230\u4e86\u5b59\u9a7c\u5b50\u9762\u524d\uff0c\u9053\uff1a\u4f60\u4e0d\u4f46\u773c\u5149\u9ad8 \uff0c\u6b66\u529f\u4e5f\u9ad8\uff0c\u53ea\u8981\u4f60\u8bf4\u4e00\u58f0\uff0c\u4ec0\u4e48\u5730\u65b9\u6211\u90fd\u8ddf\u4f60\u53bb\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5389\u58f0\u9053\uff1a\u4f60\u53bb\u6b7b\u5427\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5a9a\u773c\u5982\u4e1d\uff0c\u9053\uff1a\u6211\u8981\u6b7b\uff0c\u4e5f\u5f97\u6b7b\u5728\u5e8a\u4e0a\uff01 \u3000\u3000\u9762\u5bf9\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u5973\u4eba\uff0c\u770b\u7740\u5979\u7684\u5a9a\u7b11\uff0c\u624b\u4e0b\u4e5f\u5c31\u96be\u514d\u8981\u7559\u4e09\u5206\u60c5\u3002 \u3000\u3000\u4f46\u4f60\u7559\u60c5\uff0c\u5979\u5374\u4e0d\u7559\u60c5\u3002\u6240\u4ee5\u5341\u5e74\u6765\uff0c\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u7537\u4eba\u6b7b\u5728\u5979\u624b\u4e0b\u3002\u53ea\u53ef\u60dc\u5979\u4eca\u5929\u9047 \u89c1\u7684\u662f\u5b59\u9a7c\u5b50\u3002\u5b59\u9a7c\u5b50\u770b\u5230\u5973\u4eba\uff0c\u4e00\u70b9\u5174\u8da3\u4e5f\u6ca1\u6709\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u94c1\u722a\u53c8\u5df2\u51fb\u51fa\u3002 \u3000\u3000\u84dd\u874e\u5b50\u957f\u8896\u4e00\u5377\uff0c\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u9053\uff1a\u7b49\u4e00\u7b49\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u518d\u6b21\u64a4\u62db\u9053\uff1a\u8fd8\u7b49\u4ec0\u4e48\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4f60\u5c31\u7b97\u4e00\u5b9a\u8981\u903c\u6211\u51fa\u624b\uff0c\u5148\u770b\u770b\u6211\u7528\u7684\u5175\u5203\u4e5f\u4e0d\u8fdf\u5440\u3002 \u3000\u3000\u5979\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u8896\u4e2d\u5df2\u6709\u4e00\u9053\u84dd\u6676\u6676\u3001\u78a7\u68ee\u68ee\u7684\u5bd2\u5149\u98de\u51fa\uff0c\u53d1\u95ea\u7535\u822c\u659c\u523a\u5b59\u9a7c\u5b50\u9762\u76ee \u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u5927\u559d\u4e00\u58f0\uff0c\u94c1\u722a\u8fce\u5411\u84dd\u5149\uff0c\u6293\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u4ed6\u4e0e\u4eba\u4ea4\u624b\uff0c\u7d20\u6765\u559c\u6b22\u901f\u5440\u901f\u5feb\uff0c\u6240\u4ee5\u4ed6\u867d\u7136\u77e5\u9053\u84dd\u874e\u7528\u7684\u5fc5\u662f\u4ef6\u6781\u5947\u7279\u7684\u6b6a\u95e8\u5175\u5203\uff0c \u4f46\u4ed7\u7740\u81ea\u5df1\u82e6\u7ec3\u56db\u5341\u5e74\u7684\u9e70\u722a\u529b\uff0c\u60f3\u5728\u4e00\u62db\u95f4\u5c31\u593a\u4e0b\u5979\u7684\u5175\u5203\uff0c\u4ec5\u5979\u6839\u672c\u6ca1\u6709\u8fd8\u624b\u7684\u4f59\u5730\uff01 \u3000\u3000\u8fd9\u4e00\u6293\u66f4\u662f\u5a01\u4e0d\u53ef\u6321\uff01 \u3000\u3000\u5bf9\u65b9\u7528\u7684\u5175\u5203\u7eb5\u7136\u9510\u5229\uff0c\u7eb5\u7136\u80fd\u5272\u7834\u4ed6\u7684\u624b\uff0c\u4f46\u5175\u65a5\uff12\u8fd8\u662f\u8981\u88ab\u4ed6\u593a\u4e0b\uff0c\u5b59\u9a7c\u5b50\u5bf9\u81ea\u5df1 \u8fd9\u4e00\u6293\uff0c\u7d20\u6765\u81ea\u4fe1\u5f97\u5f88\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u7684\u81ea\u4fe1\u4e5f\u8bb8\u592a\u5f3a\u4e86\u4e9b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u76f4\u7ad9\u5728\u90a3\u91cc\uff0c\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u51fa\u624b\u7684\u610f\u601d\uff0c\u4f46\u5979\u7684\u773c\u775b\u5374\u59cb\u7ec8\u672a\u66fe\u79bb\u5f00\u8fc7\u84dd\u874e\u5b50 \u7684\u8863\u8896\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u5feb\u5f97\u5f88\u3002 \u3000\u3000\u90a3\u9053\u9752\u84dd\u8272\u7684\u5bd2\u5149\u4e00\u98de\u51fa\uff0c\u5979\u5df2\u770b\u6e05\u695a\u4e86\u3002 \u3000\u3000\u5979\u4ece\u672a\u770b\u8fc7\u5982\u6b64\u5947\u5f02\u7684\u5175\u5203\u3002 \u3000\u3000\u90a3\u770b\u6765\u5c31\u50cf\u662f\u4e00\u653e\u5927\u4e86\u5341\u51e0\u500d\u7684\u874e\u5b50\u6bd2\u5c3e\uff0c\u957f\u957f\u7684\uff0c\u5f2f\u5f2f\u7684\uff0c\u4f3c\u8f6f\u5b9e\u786c\uff0c\u53c8\u53ef\u4ee5\u968f\u610f\u66f2 \u6298\u3002 \u3000\u3000\u6700\u53ef\u6015\u7684\u662f\uff0c\u8fd9\u5175\u5203\u7531\u5934\u5230\u5c3e\uff0c\u90fd\u5e26\u7740\u94a9\u5b50\u822c\u7684\u5012\u523a\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u81ea\u7136\u4e5f\u5bf9\u5979\u4e8c\u53d4\u7684\u5927\u9e70\u722a\u529b\u5f88\u6709\u4fe1\u5fc3\uff0c\u4f46\u5979\u77e5\u9053\u53ea\u8981\u4ed6\u7684\u624b\u4e00\u6293\u7740\u559d\u5b50\u7684\u5175\u5203\uff0c \u4e5f\u96be\u514d\u8981\u88ab\u8fd9\u4e2a\u4e13\u5403\u7537\u4eba\u7684\u6bd2\u874e\u5b50\u5403\u4e0b\u53bb\uff01 \u3000\u3000\u84dd\u874e\u5b50\u7684\u51fa\u624b\u56fa\u7136\u5feb\uff0c\u5b59\u5c0f\u7ea2\u77e5\u9053\u81ea\u5df1\u65e0\u8bba\u5982\u4f55\u4e5f\u62e6\u963b\u4e0d\u53ca\u4e86\uff0c\u5979\u60f3\u4e0d\u5230\u5979\u4e8c\u53d4\u62b9\u4e86\u5341 \u56db\u5e74\u7684\u684c\u5b50\u540e\uff0c\u813e\u6c14\u8fd8\u5982\u6b64\u66b4\u70c8\uff01 \u3000\u3000\u5979\u5374\u4e0d\u77e5\u9053\u5b59\u9a7c\u5b50\u6b63\u56e0\u4e3a\u5df2\u5fcd\u4e86\u5341\u56db\u5e74\uff0c\u813e\u6c14\u65e9\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u6240\u4ee5\u6b64\u523b\u4e00\u6709\u673a\u4f1a\u51fa\u624b\uff0c\u5c31 \u4e0d\u987e\u4e00\u5207\uff0c\u60f3\u4e00\u51fb\u5f97\u624b\u3002 \u3000\u3000\u5979\u60c5\u6025\u4e4b\u4e0b\uff0c\u5fcd\u4e0d\u4f4f\u60ca\u547c\u51fa\u58f0\u6765\uff01 \u3000\u3000\u8fd9\u624b\u7684\u52a8\u4f5c\u7adf\u6bd4\u5979\u7684\u58f0\u97f3\u8fd8\u5feb\uff0c\u5979\u60ca\u547c\u4e4b\u58f0\u521a\u53d1\u51fa\uff0c\u8fd9\u624b\u5df2\u534a\u9014\u6293\u4f4f\u4e86\u84dd\u874e\u5b50\u7684\u624b\u3002 \u3000\u3000\u53ea\u542c\u5580\u5693\u4e00\u58f0\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u84dd\u5149\u843d\u5730\u3002 \u3000\u3000\u84dd\u5149\u843d\u5730\u65f6\uff0c\u84dd\u874e\u5b50\u7684\u4eba\u5df2\u9000\u51fa\u4e00\u4e08\u5916\uff0c\u5979\u9000\u5f97\u592a\u4ed3\u731d\uff0c\u4e5f\u592a\u5feb\uff0c\u7adf\u7830\u7684\u649e\u5728\u5899\u4e0a\u3002 \u3000\u3000\u7136\u540e\u6240\u6709\u7684\u4e00\u5207\u58f0\u97f3\uff0c\u6240\u6709\u7684\u4e00\u5207\u52a8\u4f5c\u5c31\u5168\u90fd\u505c\u987f\u4e86\u4e0b\u6765\uff0c\u5c4b\u5b50\u91cc\u7a81\u7136\u53d8\u5f97\u6b7b\u4e00\u822c\u9759\u5bc2 \uff0c\u8fde\u7a7a\u6c14\u90fd\u4eff\u4f5b\u5df2\u51dd\u7ed3\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u77f3\u50cf\u822c\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5403\u60ca\u7684\u671b\u7740\u8fd9\u53ea\u624b\uff0c\u84dd\u874e\u5b50\u773c\u775b\u91cc\u4e0d\u4f46\u5145\u6ee1\u4e86\u60ca\u8bb6\uff0c\u4e5f\u5145\u6ee1\u4e86\u6050\u6016\u75db\u82e6 \uff01 \u3000\u3000\u5979\u7684\u624b\u8155\u5df2\u88ab\u6298\u65ad\u4e86\uff01 \u3000\u3000\u8fd9\u53cc\u4ee4\u4eba\u5403\u60ca\uff0c\u4ee4\u4eba\u6050\u60e7\u7684\u624b\u7ec8\u4e8e\u7f29\u4e86\u56de\u53bb\uff0c\u5b83\u4f38\u51fa\u6765\u867d\u5feb\uff0c\u7f29\u56de\u65f6\u5374\u5f88\u6162\u3002 \u3000\u3000\u7136\u540e\uff0c\u4e00\u4e2a\u4eba\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u5374\u6b63\u662f\u90a3\u5df2\u9189\u5982\u6ce5\u7684\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u53c8\u60ca\u53c8\u559c\uff0c\u5931\u58f0\u9053\uff1a\u539f\u6765\u4f60\u6ca1\u6709\u9189\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u6211\u7684\u5fc3\u60c5\u867d\u7136\u4e0d\u597d\uff0c\u4f53\u529b\u867d\u7136\u4e0d\u652f\uff0c\u9152\u91cf\u5374\u4e00\u5411\u4e0d\u9519\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u77aa\u7740\u4ed6\uff0c\u5145\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u611f\u60c5\uff0c\u4e5f\u4e0d\u77e5\u662f\u60ca\u5947\uff1f\u662f\u6b22\u559c\uff1f\u662f\u4f69\u670d\uff1f\u8fd8\u662f\u5931\u671b\uff1f \u3000\u3000\u5979\u6bd5\u7adf\u8fd8\u662f\u6ca1\u6709\u704c\u9189\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u84dd\u874e\u5b50\u773c\u775b\u91cc\u7684\u5a9a\u6001\u5374\u65e9\u5df2\u4e0d\u89c1\u4e86\uff0c\u5269\u4e0b\u7684\u53ea\u6709\u60ca\u614c\u548c\u6050\u6016\u3002 \u3000\u3000\u56e0\u4e3a\u674e\u5bfb\u6b22\u7684\u624b\u91cc\u4e0d\u77e5\u4f55\u65f6\u5df2\u591a\u4e86\u4e00\u628a\u5200\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\u7eb5\u672a\u51fa\u624b\uff0c\u4e5f\u8db3\u4ee5\u4ee4\u4eba\u4e27\u80c6\u2014\u2014\u5c0f\u674e\u98de\u5200\u6700\u53ef\u6015\u7684\u65f6\u5019\uff0c\u4e5f\u5c31\u662f\u5b83\u8fd8\u672a\u51fa\u624b\u7684 \u65f6\u5019\u3002 \u3000\u3000\u56e0\u4e3a\u5b83\u51fa\u624b\u4e4b\u540e\uff0c\u5bf9\u65b9\u5c31\u5df2\u4e0d\u77e5\u9053\u4ec0\u4e48\u53eb\u53ef\u6015\u4e86\u3002 \u3000\u3000\u6b7b\u4eba\u662f\u4e0d\u77e5\u9053\u5bb3\u6015\u7684\uff01 \u3000\u3000\u5c4b\u5b50\u91cc\u53ea\u5269\u4e0b\u547c\u5438\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u6c89\u91cd\u7684\u547c\u5438\u5374\u6bd4\u5b8c\u5168\u9759\u5bc2\u8fd8\u4ee4\u4eba\u89c9\u5f97\u9759\u5bc2\uff0c\u7b80\u76f4\u9759\u5bc2\u5f97\u4ee4\u4eba\u7a92\u606f\uff0c\u4ee4\u4eba\u53d7\u4e0d\u4e86\uff0c\u4ee4\u4eba \u8981\u53d1\u75af\u3002 \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u5947\u5f02\u7684\u611f\u60c5>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u516d\u7ae0\u3000\u5947\u5f02\u7684\u611f\u60c5 \u3000\u3000\u84dd\u874e\u5b50\u5934\u4e0a\u7684\u51b7\u6c57\u4e0d\u505c\u5730\u6d41\u4e0b\u6765\uff0c\u4e00\u7c92\u6bd4\u4e00\u7c92\u5927\u2026\u2026 \u3000\u3000\u5979\u5168\u8eab\u90fd\u5728\u98a4\u6296\u7740\uff0c\u5ffd\u7136\u5927\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u4f60\u98de\u5200\u4e3a\u4f55\u4e0d\u4e0d\u51fa\u624b\uff1f\u4f60\u4e3a\u4f55\u8fd8\u4e0d\u6740\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u80af\u4e0d\u987e\u4e00\u5207\u6765\u4e3a\u4f0a\u54ed\u590d\u4ec7\uff0c\u603b\u7b97\u4f60\u8fd8\u6709\u771f\u60c5\uff0c\u4ed6\u6b7b\u4e86\uff0c\u4f60\u81ea\u7136\u5f88\u75db\u82e6\u2014\u2014 \u5f88\u75db\u82e6\u2014\u2014 \u3000\u3000\u5979\u51dd\u6ce8\u7740\u624b\u91cc\u7684\u5200\u950b\uff0c\u76ee\u4e2d\u4f3c\u4e4e\u5e26\u7740\u4e00\u4e1d\u75db\u82e6\u4e4b\u8272\uff0c\u6697\u7136\u9053\uff1a\u6211\u5f88\u4e86\u89e3\u8fd9\u79cd\u75db\u82e6\uff01\u5f88\u4e86 \u89e3\u2014\u2014\u6211\u53ea\u5e0c\u671b\u4f60\u660e\u767d\uff0c\u8fd9\u79cd\u75db\u82e6\u7edd\u4e0d\u662f\u6740\u4eba\u5c31\u80fd\u51cf\u8f7b\u7684\uff0c\u4f60\u65e0\u8bba\u6740\u591a\u5c11\u4eba\uff0c\u4e5f\u4e0d\u80fd\u5c06\u8fd9\u79cd \u75db\u82e6\u51cf\u8f7b\u534a\u5206\u3002 \u3000\u3000\u5bd2\u5149\u4e00\u95ea\uff0c\u5c0f\u674e\u98de\u5200\u7a81\u7136\u51fa\u624b\u3002 \u3000\u3000\u53ea\u542c\u89c1\u78c1\u7684\u4e00\u58f0\uff0c\u96ea\u4eae\u7684\u5200\u5df2\u9489\u5728\u84dd\u874e\u5b50\u8eab\u65c1\u7684\u95e8\u6963\u4e0a\u3002 \u3000\u3000\u674e\u9053\uff1a\u4f60\u8d70\u5427\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\u3002\u5979\u5ffd\u7136\u95ee\u9053\uff1a\u90a3\u4e48\uff0c\u8fd9\u79cd\u75db\u82e6\u8981\u600e\u6837\u624d\u80fd\u51cf\u8f7b\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u4e5f\u4e0d\u77e5\u9053\u2014\u2014\u4e5f\u8bb8\u4f60\u60f3\u5230\u53e6\u4e00\u4e2a\u80fd\u4ee3\u66ff\u4ed6\u65f6\uff0c\u8fd9\u79cd\u75db\u82e6\u5c31\u80fd\u51cf \u8f7b\u4e86\uff0c\u6211\u53ea\u5e0c\u671b\u4f60\u80fd\u627e\u5f97\u5230\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5446\u5446\u671b\u7740\u4ed6\uff0c\u76ee\u4e2d\u7a81\u7136\u6d41\u4e0b\u4e86\u773c\u6cea\u2014\u2014\uff0d \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u5728\u75f4\u75f4\u5730\u671b\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u5979\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u771f\u6709\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u5979\u76ef\u7740\u4ed6\uff0c\u4eff\u4f5b\u60f3\u770b\u900f\u4ed6\u7684 \u5fc3\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5df2\u8d70\u4e86\uff0c\u662f\u5e26\u7740\u773c\u6cea\u8d70\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4f60\u4e00\u5b9a\u5f88\u5947\u602a\uff0c\u4e3a\u4f55\u6211\u6ca1\u6740\u5979\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u4e00\u76f4\u5782\u9996\u671b\u7740\u5730\u4e0a\u90a3\u4ef6\u5947\u5f02\u7684\u5175\u5203\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u662f\u56e0\u4e3a\u6211\u4e00\u5411\u603b\u4eba\u4e3a\u4e00\u4e2a\u4eba\u82e5\u8fd8\u6709\u6cea\u53ef\u6d41\uff0c\u5c31\u4e0d\u8be5\u6b7b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u7b11\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e0d\u559c\u6b22\u6740\u4eba\uff0c\u4f60\u4e0d\u6740\u5979\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u5947\u602a\uff0c\u6211\u53ea\u5947\u602a\u4f60\u660e\u660e \u6ca1\u9189\uff0c\u4e3a\u4f55\u8981\u88c5\u9189\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u4f60\u4e5f\u662f\u559d\u9152\u7684\u4eba\uff0c\u603b\u8be5\u77e5\u9053\u88c5\u9189\u6bd4\u771f\u9189\u6709\u8da3\u591a\u4e86\uff0c\u82e5\u662f\u771f\u7684\u70c2\u9189\u5982\u6ce5\uff0c \u975e\u4f46\u5f53\u65f6\u65e0\u8da3\uff0c\u7b2c\u4e8c\u5929\u5934\u75bc\u8d77\u6765\u66f4\u8981\u4eba\u7684\u547d\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6709\u9053\u7406\u3002 \u3000\u3000\u674e\u589e\u6b22\u9053\uff1a\u4f46\u53ea\u8981\u662f\u559d\u9152\u7684\u4eba\uff0c\u5c31\u6ca1\u6709\u6c38\u8fdc\u4e0d\u9189\u7684\uff0c\u4f60\u82e5\u771f\u60f3\u704c\u9189\u6211\uff0c\u4ee5\u540e\u7684\u673a\u4f1a\u8fd8\u591a \u5f97\u5f88\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u7728\u773c\u9053\uff1a\u53ef\u662f\u6211\u81ea\u5df1\u5fc3\u91cc\u660e\u767d\uff0c\u8fd9\u6b21\u6211\u65e2\u5df2\u9519\u8fc7\u673a\u4f1a\uff0c\u4ee5\u540e\u53ea\u6015\u5c31\u4f11 \u60f3\u704c\u5f97\u9189\u4f60\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5176\u5b9e\u6211\u2014\u2014 \u3000\u3000\u4ed6\u7684\u8bdd\u672a\u8bf4\u51fa\uff0c\u7a81\u89c1\u5b59\u9a7c\u5b50\u5927\u6b65\u8d70\u5230\u67dc\u53f0\u540e\uff0c\u63d0\u8d77\u4e00\u575b\u9152\uff0c\u4e00\u638c\u62cd\u5f00\u6ce5\u5c01\uff0c\u4ef0\u8d77\u8116\u5b50\u5c31 \u5f80\u5634\u91cc\u5012\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u77e5\u9053\u704c\u4e86\u591a\u5c11\uff0c\u5c0f\u7ea2\u624d\u603b\u7b97\u593a\u4e0b\u4e86\u4ed6\u624b\u91cc\u7684\u9152\u575b\u5b50\uff0c\u8dfa\u811a\u9053\uff1a\u4eba\u5bb6\u5b81\u53ef\u88c5\u4f6f\u4e5f\u4e0d \u613f\u88ab\u4eba\u704c\u9189\uff0c\u4e8c\u53d4\u4f60\u4e3a\u4f55\u8981\u81ea\u5df1\u704c\u9189\u81ea\u5df1\u5462\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u773c\u775b\u5df2\u53d1\u76f4\uff0c\u5583\u5583\u9053\uff1a\u4e00\u9189\u89e3\u5343\u6101\uff0c\u8fd8\u662f\u9189\u4e86\u7684\u597d\u2014\u2014\u9189\u4e86\u7684\u597d\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\u7a81\u8df3\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u4f60\u95ee\u6211\u4e3a\u4ec0\u4e48\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u56e0\u4e3a\u6211\u4e0d\u613f\u53d7\u4eba\u7684\u6069\u60e0\uff0c\u65e0\u8bba\u8c01 \u7684\u6069\u60e0\u6211\u90fd\u53d7\u4e0d\u4e86\uff0c\u6211\u5b81\u53ef\u88ab\u5439\u4e00\u5200\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u53c8\u5012\u5728\u6905\u4e0a\uff0c\u4ee5\u624b\u8499\u7740\u8138\uff0c\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u674e\u5bfb\u6b22\uff0c\u4f60\u4e3a\u4f55\u8981\u6551\u6211\uff1f\u6211\u88ab\u4eba\u6551\u8fc7\u4e00 \u6b21\uff0c\u5df2\u591f\u53d7\u7684\u4e86\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u8fd9\u4e9b\u5e74\u6765\u7684\u65e5\u5b50\u662f\u600e\u4e48\u8fc7\u7684\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u60f3\u95ee\u4ed6\uff1a\u8c01\u66fe\u7ecf\u6551\u8fc7\u4f60\uff1f \u3000\u3000\u201c\u4f60\u4e3a\u53ef\u8981\u7b54\u5e94\u4ed6\u5728\u8fd9\u91cc\u5b88\u62a4\u5341\u4e94\u5e74\u3002 \u3000\u3000\u4f60\u5b88\u62a4\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u4f46\u5b59\u9a7c\u5b50\u8bed\u58f0\u8d8a\u6765\u8d8a\u4f4e\uff0c\u4e5f\u4e0d\u77e5\u662f\u9189\u4e86\uff1f\u8fd8\u662f\u7761\u7740\u4e86\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u4e86\u77a7\u5b59\u5c0f\u7ea2\uff0c\u4e5f\u60f3\u95ee\u5979\uff0c\u4f46\u4e00\u770b\u5230\u5b59\u5c0f\u7ea2\u90a3\u53cc\u7075\u6d3b\u3001\u8c03\u76ae\u7684\u5927\u773c\u775b\uff0c\u4ed6\u5c31\u7acb\u523b \u6253\u6d88\u4e86\u8fd9\u4e3b\u610f\u3002 \u3000\u3000\u8c61\u5b59\u5c0f\u7ea2\u8fd9\u79cd\u5973\u5b69\u5b50\uff0c\u4f60\u82e5\u60f3\u95ee\u5979\u4ec0\u4e48\u79d8\u5bc6\uff0c\u90a3\u662f\u4e00\u5b9a\u95ee\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u4e8c\u53d4\u771f\u4e0d\u6127\u662f\u5927\u4e08\u592b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7528\u773c\u89d2\u779f\u7740\u4ed6\uff0c\u7b11\u9053\uff1a\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\u53ea\u6709\u5927\u4e08\u592b\u624d\u4f1a\u771f\u7684\u9189\u5f97\u8fd9\u4e48\u5feb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u53ea\u6709\u5927\u4e08\u592b\u624d\u80af\u4e00\u8bfa\u5343\u91d1\uff0c\u81f3\u6b7b\u4e0d\u6539\uff0c\u53ea\u6709\u5927\u4e08\u592b\u624d\u4e0d\u613f\u53d7 \u4eba\u7684\u6069\u60e0\uff0c\u53ea\u6709\u5927\u4e08\u592b\u624d\u80af\u4e3a\u4e86\u522b\u4eba\uff0c\u727a\u7272\u81ea\u5df1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u6240\u4ee5\u4f60\u4e5f\u8981\u4e3a\u4e86\u4fdd\u62a4\u522b\u4eba\u800c\u7559\u5728\u8fd9\u91cc\uff0c\u662f\u4e0d\u662f\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u7740\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u65e0\u8bba\u4e3a\u4e86\u4ec0\u4e48\u539f\u56e0\uff0c\u4f60\u90fd\u4e0d\u80af\u8d70\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u6c89\u9ed8\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u53ef\u662f\uff0c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u963f\u98de\u5462\uff1f\u4f60\u4e0d\u60f3\u53bb\u770b\u770b\u4ed6\uff1f\u4ed6\u96be\u9053\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9053\uff1a\u4ed6\u81f3\u5c11\u5e94\u8be5\u80fd\u7167\u987e\u81ea\u5df1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6211\u5e38\u542c\u4eba\u8bf4\uff0c\u6797\u4ed9\u513f\u770b\u6765\u867d\u50cf\u662f\u5929\u4e0a\u7684\u4ed9\u5b50\uff0c\u4f46\u5374\u4e13\u95e8\u5e26\u7537\u4eba\u5165\u5730\u72f1\u3002\u5979\u4e00 \u5b57\u5b57\u9053\uff1a\u4f60\u4e0d\u63e1\u4f60\u7684\u670b\u53cb\u88ab\u5979\u5e26\u5165\u5730\u72f1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5634\u53c8\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u53e3\u6c14\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u5bf9\u4e0d\u80af\u8d70\uff0c\u4e3a\u4e86\u5979\uff0c\u4f60\u522b\u7684\u4e8b\u90fd\u53ef\u4ee5\u653e\u4e0b\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b \u90fd\u53ef\u4ee5\u653e\u4e0b\uff01\u2014\u2014 \u3000\u3000\u5979\u773c\u6ce2\u5ffd\u7136\u53d8\u5f97\u65e0\u9650\u6e29\u67d4\uff0c\u671b\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u53ef\u662f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u627e\u4e2a\u4eba\u6765\u4ee3\u66ff\u5979\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6cdb\u8d77\u4e86\u4e00\u9635\u75db\u82e6\u4e4b\u8272\uff0c\u53c8\u5f2f\u4e0b\u8170\u53bb\u4e0d\u505c\u5730\u54b3\u55fd\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u4e0d\u613f\u8d70\uff0c\u6211\u4e5f\u4e0d\u80fd\u52c9\u5f3a\u4f60\uff0c\u53ef\u662f\u4f60\u81f3\u5c11\u5e94\u8be5\u53bb\u770b\u770b\u6211\u7684\u7237\u7237\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52c9\u5f3a\u5fcd\u4f4f\u54b3\u55fd\uff0c\u9053\uff1a\u4ed6\u2014\u2014\u4ed6\u5728\u54ea\u91cc\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4ed6\u8001\u4eba\u5bb6\u5728\u57ce\u5916\u7684\u957f\u4ead\u7b49\u6211\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u957f\u4ead\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u4e00\u5b9a\u4f1a\u7ecf\u8fc7\u90a3\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u9053\uff1a\u4e0a\u5b98\u91d1\u8679\u7eb5\u7136\u7ecf\u8fc7\u90a3\u91cc\uff0c\u4ed6\u4e5f\u672a\u5fc5\u770b\u5f97\u5230\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e00\u5b9a\u80fd\u770b\u5f97\u4ee5\uff0c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u4ece\u4e0d\u4e58\u8f66\uff0c\u4e5f\u4e0d\u9a91\u9a6c\uff0c\u4ed6\u4e00\u5411\u559c\u6b22\u8d70\u8def\u7684\uff0c\u4ed6 \u5e38\u8bf4\u4e00\u4e2a\u4eba\u751f\u7740\u4e24\u6761\u817f\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u8d70\u8def\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u7b11\uff0c\u9053\uff1a\u4f60\u77e5\u9053\u7684\u5012\u771f\u4e0d\u5c11\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u7684\u786e\u4e0d\u5c11\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u4e0d\u4f46\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u8981\u6765\uff0c\u8fd8\u77e5\u9053\u4ed6\u4f1a\u4ece\u54ea\u91cc\u6765\uff0c\u4f60\u4e0d\u4f46\u77e5\u9053\u90a3\u5c01\u4fe1\u662f\u6797\u4ed9 \u513f\u5199\u7684\uff0c\u8fd8\u77e5\u9053\u5979\u9690\u85cf\u5728\u90a3\u91cc\u2014\u2014 \u3000\u3000\u4ed6\u76ef\u7740\u5b59\u5c0f\u7ea2\u7684\u773c\u775b\uff0c\u95ee\u9053\uff1a\u8fd9\u4e9b\u4e8b\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u5a07\u7b11\u9053\uff1a\u6211\u6709\u6211\u7684\u6cd5\u5b50\uff0c\u6211\u504f\u4e0d\u544a\u8bc9\u4f60\u3002 \u3000\u3000\u591c\u6df1\u6c89 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u6b65\u5b50\u5f88\u8f7b\u5feb\uff0c\u5c31\u50cf\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u75b2\u5026\u4f3c\u7684\uff0c\u56e0\u4e3a\u65e0\u8bba\u5bf9\u4ec0\u4e48\u4e8b\uff0c\u5979\u90fd\u6709\u5f88\u5927\u7684 \u5174\u8da3\u3002 \u3000\u3000\u5979\u5bf9\u751f\u547d\u6b63\u5145\u6ee1\u4e86\u70ed\u7231\u3002 \u3000\u3000\u5979\u8fd8\u5e74\u8f7b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8d70\u5728\u5979\u8eab\u65c1\uff0c\u548c\u5979\u6b63\u662f\u4e2a\u6781\u5f3a\u70c8\u7684\u5bf9\u6bd4\u3002 \u3000\u3000\u4ed6\u5f88\u7fa1\u6155\u5979\uff0c\u751a\u81f3\u6709\u70b9\u6de1\u6de1\u7684\u5992\u5fcc\uff0c\u7b49\u4ed6\u53d1\u73b0\u81ea\u5df1\u8fd9\u79cd\u5992\u5fcc\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u5ffd\u7136\u5403\u4e86\u4e00\u60ca \u3002 \u3000\u3000\u6211\u96be\u9053\u5df2\u771f\u7684\u8001\u4e86\uff1f \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u552f\u6709\u8001\u4eba\u624d\u4f1a\u5bf9\u5e74\u8f7b\u4eba\u7684\u70ed\u7231\u751f\u51fa\u5992\u5fcc\u3002 \u3000\u3000\u4ed6\u81ea\u5632\u7684\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u82e5\u662f\u5728\u5341\u5e74\u524d\uff0c\u6211\u4e00\u5b9a\u4e0d\u4f1a\u548c\u4f60\u8d70\u5f97\u8fd9\u4e48\u8fd1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u6211\u662f\u4e2a\u6d6a\u5b50\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u548c\u6211\u8d70\u5728\u4e00\u8d77\uff0c\u522b\u4eba\u770b\u5230 \u5c31\u96be\u514d\u8981\u8bf4\u95f2\u8bdd\u7684\u3002 \u3000\u3000\u4ed6\u7b11\u4e86\u77e5\uff0c\u63a5\u7740\u9053\uff1a\u5e78\u597d\u6211\u73b0\u5728\u5df2\u8001\u4e86\uff0c\u522b\u4eba\u770b\u5230\u6211\u4eec\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u6211\u662f\u4f60\u7684\u7236\u4eb2\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u6211\u7684\u7236\u4eb2\uff1f\u4f60\u4ee5\u4e3a\u4f60\u771f\u7684\u6709\u90a3\u4e48\u8001\u4e86\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5f53\u7136\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u7b11\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6211\u7b11\u4f60\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5f88\u6015\u6211\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u6015\u4f60\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u773c\u775b\u4eae\u5f97\u50cf\u5929\u4e0a\u7684\u661f\u661f\u3002 \u3000\u3000\u5979\u5403\u5403\u5730\u7b11\u7740\u9053\uff1a\u5c31\u56e0\u4e3a\u4f60\u6015\u6211\uff0c\u624d\u4f1a\u5bf9\u6211\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u4f60\u6015\u4f60\u81ea\u5df1\u4f1a\u5bf9\u6211\u2014\u2014\u5bf9\u6211\uff0c\u6240 \u4ee5\u624d\u786c\u8bf4\u81ea\u5df1\u662f\u8001\u5934\u5b50\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u5176\u5b9e\uff0c\u4f60\u82e5\u662f\u8001\u5934\u5b50\uff0c\u6211\u5c31\u662f\u8001\u592a\u5a46\u4e86\u3002 \u3000\u3000\u5979\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u671b\u7740\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u53ea\u6709\u81ea\u5df1\u5148\u89c9\u5f97\u8001\u4e86\u7684\u4eba\uff0c\u624d\u4f1a\u771f\u7684\u53d8\u8001\uff0c\u6211\u7237 \u7237\u5c31\u4ece\u6765\u4e0d\u80af\u670d\u8001\uff0c\u4f60\u8fd8\u5e74\u8f7b\u5f97\u5f88\uff0c\u6c42\u6c42\u4f60\u4ee5\u540e\u83ab\u8981\u518d\u8bf4\u81ea\u5df1\u8001\u4e86\u597d\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u770b\u5230\u8fd9\u53cc\u773c\u775b\uff0c\u5ffd\u7136\u60f3\u8d77\u5341\u4f59\u5e74\u524d\u7684\u6797\u8bd7\u97f3\u3002 \u3000\u3000\u90a3\u65f6\u7684\u6797\u8bd7\u97f3\u5c82\u975e\u4e5f\u5982\u6b64\u7eaf\u771f\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6697\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u907f\u5f00\u5979\u7684\u76ee\u5149\uff0c\u9065\u671b\u524d\u65b9\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u4f60\u770b\uff0c\u524d\u9762\u5df2\u662f\u957f\u4ead\uff0c\u6211 \u4eec\u5feb\u8d70\u5427\uff0c\u83ab\u8981\u8ba9\u4f60\u7237\u7237\u7b49\u5f97\u7740\u6025\u3002 \u3000\u3000\u9ed1\u6c89\u6c89\u7684\u591c\u8272\u4e2d\uff0c\u53ea\u770b\u5230\u957f\u4ead\u4e2d\u6709\u4e00\u70b9\u706b\u5149\uff0c\u5ffd\u660e\u5ffd\u663e\uff0c\u706b\u5149\u5230\u4eae\u7684\u65f6\u5019\uff0c\u624d\u80fd\u770b\u51fa\u4e00 \u4e2a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4f60\u770b\u5230\u90a3\u70b9\u706b\u5149\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u770b\u5230\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u4f60\u731c\u90a3\u662f\u4ec0\u4e48\uff1f\u731c\u5f97\u51fa\uff0c\u6211\u4f69\u670d\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u662f\u4f60\u7237\u7237\u5728\u62bd\u65f1\u70df\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u5440\uff0c\u4f60\u771f\u662f\u4e2a\u5929\u624d\u513f\u7ae5\uff0c\u6211\u771f\u4f69\u670d\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u4e5f\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u548c\u8fd9\u5973\u5b69\u5b50\u5728\u4e00\u8d77\uff0c\u4ed6\u7b11\u7684\u65f6\u5019\u5c31\u597d\u50cf\u591a\u4e86\u4e9b\uff0c \u54b3\u55fd\u7684\u65f6\u5019\u5374\u5c11\u4e86\u4e9b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u4e0d\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u6765\u8fc7\u4e86\u6ca1\u6709\uff1f\u4ed6\u8001\u4eba\u5bb6\u662f\u5426\u5df2\u5c06\u4ed6\u9001\u8d70\uff1f \u3000\u3000\u8bf4\u7740\uff0c\u5979\u76ee\u5149\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u5fe7\u90c1\u4e4b\u8272\uff0c\u9053\uff1a\u6211\u4eec\u8d76\u5feb\u8fc7\u53bb\u5427\uff0c\u770b\u770b\u2014\u2014 \u3000\u3000\u5979\u8bdd\u672a\u8bf4\u5b8c\uff0c\u674e\u5bfb\u6b22\u5ffd\u7136\u626f\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u5fc3\u4e00\u8df3\uff0c\u8138\u6709\u4e9b\u53d1\u70eb\u3002 \u3000\u3000\u5979\u5077\u5077\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u624d\u53d1\u73b0\u674e\u5bfb\u6b22\u7684\u795e\u60c5\u4eff\u4f5b\u5f88\u51dd\u91cd\uff0c\u4e00\u53cc\u9510\u5229\u7684\u773c\u795e\uff0c\u6b63\u51fa\u795e\u7684 \u77a7\u7740\u8fdc\u65b9\u7684\u9053\uff11\u3002 \u3000\u3000\u8fdc\u65b9\u7684\u9053\u8def\u4e0a\uff0c\u5df2\u51fa\u73b0\u4e86\u4e24\u70b9\u706b\u5149\u3002 \u3000\u3000\u90a3\u662f\u4e24\u76cf\u706f\u7b3c\u3002 \u3000\u3000\u706f\u7b3c\u662f\u91d1\u9ec4\u8272\u7684\uff0c\u7528\u4e00\u6839\u7ec6\u7af9\u7aff\u9ad8\u9ad8\u6311\u8d77\u3002 \u3000\u3000\u9ec4\u5f97\u8be1\u79d8\uff0c\u9ec4\u5f97\u53ef\u6015\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8eab\u5f62\u4e00\u95ea\uff0c\u5df2\u5c06\u5b59\u5c0f\u7ea2\u62c9\u5230\u9053\u65c1\u7684\u6811\u540e\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u964d\u4f4e\u4e86\u8bed\u58f0\uff0c\u9053\uff1a\u91d1\u94b1\u5e2e\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u76b1\u7740\u7709\u9053\uff1a\u539f\u6765\u4e0a\u5b98\u91d1\u8679\u73b0\u5728\u624d\u5230\uff0c\u83ab\u975e\u4ed6\u8def\u4e0a\u4e5f\u9047\u7740\u4ec0\u4e48\u4e8b\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u56e0\u4e3a\u4ed6\u53ea\u6709\u4e24\u6761\u817f\uff0c\u6240\u4ee5\u8d70\u4e0d\u5feb\u3002 \u3000\u3000\u53ea\u89c1\u524d\u9762\u4e24\u76cf\u706f\u7b3c\uff0c\u540e\u9762\u8fd8\u6709\u4e24\u76cf\u706f\u7b3c\uff0c\u76f8\u9694\u7ea6\u6478\u4e09\u4e08\u3002 \u3000\u3000\u524d\u9762\u7684\u706f\u7b3c\u4e0e\u540e\u9762\u7684\u706f\u7b3c\u95f4\uff0c\u8fd8\u6709\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u4eba\u7684\u8eab\u6750\u90fd\u5f88\u9ad8\uff0c\u90fd\u7a7f\u7740\u91d1\u9ec4\u8272\u7684\u8863\u886b\uff0c\u524d\u9762\u4e00\u4eba\u7684\u886b\u89d2\u5f88\u957f\uff0c\u51e0\u4e4e\u5df2\u8986\u76d6\u5230\u811a\u9762\uff0c \u4f46\u8d70\u8d77\u8def\u6765\u957f\u886b\u5374\u7eb9\u98ce\u4e0d\u52a8\u3002 \u3000\u3000\u540e\u9762\u7684\u4e00\u4eba\u886b\u89d2\u5f88\u77ed\uff0c\u53ea\u80fd\u63a9\u53ca\u819d\u76d6\u3002 \u3000\u3000\u524d\u9762\u7684\u4e00\u4eba\u8d64\u624b\u7a7a\u62f3\uff0c\u5e76\u6ca1\u6709\u5e26\u4ec0\u4e48\u5175\u5668\u3002 \u3000\u3000\u540e\u9762\u7684\u4e00\u4eba\u8170\u5e26\u4e0a\u5374\u63d2\u7740\u4e00\u67c4\u5251\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u53d1\u73b0\u8fd9\u4eba\u63d2\u5251\u7684\u6cd5\u5b50\u548c\u963f\u98de\u5dee\u4e0d\u591a\uff0c\u53ea\u4e0d\u8fc7\u963f\u98de\u662f\u5c06\u5251\u63d2\u5728\u8170\u5e26\u4e2d\u592e\uff0c\u5251\u67c4 \u5411\u53f3\u3002 \u3000\u3000\u8fd9\u4eba\u5374\u5c06\u5251\u63d2\u5728\u8170\u5e26\u53f3\u8fb9\uff0c\u5251\u67c4\u5411\u5de6\u3002 \u3000\u3000\u4ed6\u7528\u7684\u83ab\u975e\u662f\u5de6\u624b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u53cc\u7709\u4e5f\u76b1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5f88\u4e0d\u559c\u6b22\u4f7f\u5de6\u624b\u5251\u5bf9\u624b\uff0c\u56e0\u4e3a\u5de6\u624b\u4f7f\u5251\uff0c\u5251\u6cd5\u5fc5\u5b9a\u548c\u522b\u4eba\u76f8\u53cd\uff0c\u62db\u5f0f\u5fc5\u5b9a\u66f4\u8f9b\u8fa3\u8be1\u79d8 \uff0c\u53cd\u96be\u5bf9\u4ed8\u3002 \u3000\u3000\u800c\u4e14\u5251\u5df2\u51fa\u9798\uff0c\u51fa\u624b\u5fc5\u5feb\uff01 \u3000\u3000\u8fd9\u662f\u4ed6\u591a\u5e74\u7684\u7ecf\u9a8c\uff0c\u4ed6\u4e00\u80af\u5c31\u770b\u51fa\u8fd9\u662f\u4e2a\u5f88\u5f3a\u7684\u5bf9\u624b\uff01 \u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u8001\u4eba>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u4e03\u7ae0\u3000\u8001\u4eba \u3000\u3000\u674e\u5bfb\u6b22\u6ce8\u610f\u90a3\u4f7f\u5de6\u624b\u5251\u7684\u6c49\u5b50\uff0c\u5b59\u5c0f\u7ea2\u6ce8\u610f\u7684\u5374\u662f\u53e6\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8d70\u5f97\u5f88\u6162\uff0c\u6b65\u5b50\u5f88\u5927\uff0c\u770b\u6765\u548c\u5e73\u5e38\u4eba\u8d70\u8def\u5e76\u6ca1\u6709\u4ec0\u4e48\u4e0d\u540c\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c \u5979\u603b\u89c9\u5f97\u8fd9\u4e24\u4eba\u8d70\u8d77\u8def\u6765\u6709\u4e9b\u7279\u522b\u3002 \u3000\u3000\u5979\u6ce8\u610f\u5f88\u4e45\uff0c\u624d\u53d1\u73b0\u662f\u4ec0\u4e48\u539f\u56e0\u4e86\u3002 \u3000\u3000\u5e73\u5e38\u4e24\u4e2a\u4eba\u8d70\u6b65\u4f10\u5fc5\u5b9a\u662f\u76f8\u540c\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u4eba\u8d70\u8def\u5374\u5f88\u7279\u522b\uff0c\u540e\u9762\u7684\u4e00\u4eba\u6bcf\u4e00\u6b65\u8e0f\u4e0b\uff0c\u5374\u6070\u5de7\u5728\u524d\u9762\u4e00\u4eba\u7684\u7b2c\u4e00\u6b65\u548c\u7b2c\u4e8c\u6b65 \u4e4b\u95f4\u3002 \u3000\u3000\u8fd9\u6761\u817f\u770b\u6765\u5c31\u597d\u50cf\u957f\u5728\u4e00\u4e2a\u4eba\u8eab\u4e0a\u4f3c\u7684\u3002 \u3000\u3000\u524d\u9762\u4e00\u4eba\u8e0f\u4e0b\u7b2c\u4e00\u6b65\uff0c\u540e\u9762\u4e00\u4eba\u8e0f\u5165\u7b2c\u4e8c\u6b65\uff0c\u524d\u9762\u4e00\u4eba\u8e0f\u4e0b\u7b2c\u4e09\u6b65\uff0c\u540e\u9762\u4e00\u4eba\u8e0f\u4e0b\u7b2c\u56db \u6b65\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u8d70\u9519\u4e00\u6b65\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u5230\u8fc7\u4e24\u4e2a\u4eba\u50cf\u8fd9\u6837\u5b50\u8d70\u8def\u7684\uff0c\u5979\u500d\u89c9\u5f97\u65b0\u5947\u6781\u4e86\uff0c\u4e5f\u6709\u8da3\u6781\u4e86\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u6709\u8da3\u3002 \u3000\u3000\u4ed6\u975e\u4f46\u4e0d\u89c9\u5f97\u6709\u8da3\uff0c\u53cd\u800c\u89c9\u5f97\u6709\u4e9b\u53ef\u6015\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8d70\u8def\u65f6\u7684\u6b65\u4f10\u914d\u5408\u5f97\u5982\u6b64\u5947\u5999\uff0c\u663e\u89c1\u5f97\u4e24\u4eba\u5fc3\u795e\u95f4\u5df2\u6709\u4e00\u79cd\u65e0\u6cd5\u89e3\u91ca\u7684\u5947\u5f02\u9ed8\u5951 \u3002 \u3000\u3000\u4ed6\u4eec\u5e73\u5e38\u8d70\u8def\u65f6\uff0c\u5df2\u5728\u8bad\u7ec3\u7740\u8fd9\u79cd\u5947\u5f02\u7684\u914d\u5408\uff0c\u4e24\u4eba\u82e5\u662f\u8054\u624b\u5730\u654c\uff0c\u62db\u5f0f\u4e0e\u62db\u5f0f\u95f4\u4e00\u5b9a \u914d\u5408\u5f97\u66f4\u795e\u5947\u3002 \u3000\u3000\u5355\u53ea\u4e0a\u5b98\u91d1\u8679\u4e00\u4eba\uff0c\u5df2\u662f\u6b66\u6797\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u82e5\u518d\u52a0\u775b\u4e2a\u8346\u65e0\u547d\uff0c\u90a3\u8fd8\u5f97\u4e86\uff1f \uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5728\u6536\u7f29\u7740\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u51fa\u4e16\u4e0a\u6709\u4efb\u4f55\u5730\u5b50\u80fd\u5c06\u8fd9\u4e24\u4eba\u7684\u914d\u5408\u653b\u7834\uff01 \u3000\u3000\u4ed6\u4e5f\u4e0d\u76f8\u4fe1\u957f\u4ead\u4e2d\u8fd9\u8001\u4eba\u80fd\u5c06\u8fd9\u4e24\u4eba\u9001\u8d70\u3002 \u3000\u3000\u957f\u4ead\u4e2d\u7684\u8001\u4eba\u4ecd\u5728\u5438\u7740\u65f1\u70df\uff0c\u706b\u5149\u5ffd\u660e\u5ffd\u6697\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u53d1\u73b0\u8fd9\u70b9\u706b\u5149\u660e\u706d\u4e4b\u95f4\uff0c\u4e5f\u6709\u79cd\u5947\u5f02\u7684\u8282\u594f\uff0c\u5ffd\u660e\u7684\u65f6\u5019\u957f\uff0c\u5ffd\u800c\u706d\u7684\u65f6\u5019 \u957f\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8fd9\u70b9\u706b\u5149\u4eae\u5f97\u597d\u50cf\u4e00\u76cf\u706f\u4e00\u6837\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ece\u672a\u770b\u5230\u4e00\u4e2a\u4eba\u62bd\u65f1\u70df\uff0c\u80fd\u62bd\u51fa\u8fd9\u4e48\u4eae\u7684\u706b\u5149\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u663e\u7136\u4e5f\u53d1\u73b0\u4e86\uff0c\u56e0\u4e3a\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5df2\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u957f\u4ead\u7684\u706b\u5149\u7a81\u7136\u706d\u4e86\u3002 \u3000\u3000\u8001\u4eba\u7684\u8eab\u5f62\u987f\u65f6\u88ab\u9ed1\u6697\u541e\u6ca1\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6728\u7acb\u5728\u9053\u65c1\uff0c\u826f\u4e45\uff0c\u624d\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u7f13\u7f13\u8d70\u4e0a\u957f\u4ead\uff0c\u9759\u9759\u5730\u7ad9\u5728\u8001\u4eba\u5bf9\u9762\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u8d70\u5230\u54ea\u91cc\uff0c\u8346\u65e0\u547d\u90fd\u8ddf\u5728\u4ed6\u8eab\u65c1\uff0c\u5bf8\u6b65\u4e0d\u79bb\u3002 \u3000\u3000\u4ed6\u770b\u6765\u5c31\u50cf\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u5f71\u5b50\u3002 \u3000\u3000\u56db\u76cf\u9ad8\u6311\u7684\u706f\u7b3c\u4e5f\u79fb\u4e86\u8fc7\u53bb\uff0c\u56f4\u5728\u957f\u4ead\u56db\u65b9\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f4e\u7740\u5934\uff0c\u5c06\u9762\u76ee\u5168\u90fd\u85cf\u5728\u6597\u7b20\u7684\u9634\u5f71\u4e2d\uff0c\u4eff\u4f5b\u4e0d\u613f\u8ba9\u4eba\u770b\u5230\u4ed6\u9762\u4e0a \u7684\u8868\u60c5\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u773c\u775b\u5374\u4e00\u76f4\u5728\u76ef\u7740\u8001\u4eba\u7684\u624b\uff0c\u89c2\u5bdf\u7740\u8001\u4eba\u7684\u6bcf\u4e00\u4e2a\u52a8\u4f5c\uff0c\u89c2\u5bdf\u5f97\u975e\u5e38\u4ed4\u7ec6\u3002 \u3000\u3000\u8001\u4eba\u81ea\u70df\u888b\u4e2d\u6162\u6162\u5730\u53d6\u51fa\u4e00\u64ae\u70df\u4e1d\uff0c\u6162\u6162\u5730\u88c5\u5165\u70df\u6597\u91cc\uff0c\u585e\u7d27\uff0c\u7136\u540e\u53c8\u53d6\u51fa\u4e00\u67c4\u706b\u9570\uff0c \u4e00\u5757\u706b\u77f3\u3002 \u3000\u3000\u4ed6\u7684\u52a8\u4f5c\u5f88\u6162\uff0c\u4f46\u624b\u5374\u5f88\u7a33\u5b9a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u8d70\u4e86\u8fc7\u53bb\uff0c\u62ff\u8d77\u4e86\u77f3\u684c\u4e0a\u7684\u7eb8\u5a92\u3002 \u3000\u3000\u5728\u706f\u706b\u4e0b\u53ef\u4ee5\u770b\u51fa\u8fd9\u7eb8\u5a92\u6413\u5f97\u5f88\u7ec6\u3001\u5f88\u7d27\uff0c\u7eb8\u7684\u7eb9\u7406\u4e5f\u5206\u5e03\u5f97\u5f88\u5747\u5300\uff0c\u7edd\u6ca1\u6709\u4e1d\u6beb\u7c97\u7ec6 \u4e0d\u5747\u4e4b\u5904\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7528\u4e24\u6839\u624b\u6307\u62c8\u8d77\u7eb8\u5a92\uff0c\u5f88\u4ed4\u7ec6\u5730\u77a7\u4e86\u4e24\u773c\uff0c\u624d\u5c06\u7eb8\u5a92\u6162\u6162\u5730\u51d1\u8fd1\u706b\u9570\u548c\u706b\u77f3\u3002 \u3000\u3000\u53ee\u7684\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\u3002 \u3000\u3000\u7eb8\u5a92\u5df2\u88ab\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6162\u6162\u5730\u5c06\u71c3\u7740\u7684\u7eb8\u5a92\u51d1\u7684\u8001\u4eba\u7684\u70df\u6597\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u548c\u5b59\u5c0f\u7ea2\u7ad9\u7684\u5730\u65b9\u867d\u7136\u79bb\u4ead\u5b50\u5f88\u8fdc\uff0c\u4f46\u4ed6\u4eec\u7ad9\u5728\u6697\u5904\uff0c\u8001\u4eba\u548c\u4e0a\u5b98\u91d1\u8679\u6bcf\u4e00\u52a8\u4f5c \u4ed6\u4eec\u90fd\u770b\u548c\u5f88\u6e05\u695a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ee\u9053\uff1a\u8981\u4e0d\u8981\u8fc7\u53bb\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u5374\u6447\u5934\u9053\uff1a\u7528\u4e0d\u7740\uff0c\u6211\u7237\u7237\u4e00\u5b9a\u6709\u6cd5\u5b50\u5c06\u4ed6\u4eec\u6253\u53d1\u8d70\u7684\u3002 \u3000\u3000\u5979\u8bf4\u5f97\u5f88\u80af\u5b9a\uff0c\u4f46\u73b0\u5728\u674e\u5bfb\u6b22\u5374\u53d1\u89c9\u5979\u7684\u624b\u5ffd\u7136\u53d8\u5f97\u51b0\u51b0\u51b7\u51b7\uff0c\u800c\u4e14\u8fd8\u50cf\u662f\u5df2\u6c81\u51fa\u4e86\u51b7 \u6c57\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u77e5\u9053\u5979\u5728\u4e3a\u4ec0\u4e48\u62c5\u5fc3\u3002 \u3000\u3000\u65f1\u70df\u7ba1\u53ea\u6709\u4e24\u5c3a\u957f\uff0c\u73b0\u5728\u4e0a\u5b98\u91d1\u8679\u7684\u624b\u8ddd\u79bb\u4eba\u5df2\u4e0d\u53ca\u4e24\u5c3a\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef\u4ee5\u88ad\u51fb\u8001\u4eba\u9762\u4e0a \u7684\u4efb\u4f55\u4e00\u5904\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u6ca1\u6709\u51fa\u624b\uff0c\u53ea\u4e0d\u8fc7\u5728\u7b49\u5f85\u673a\u4f1a\u800c\u5df2\u3002 \u3000\u3000\u8001\u4eba\u8fd8\u5728\u62bd\u70df\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u56e0\u4e3a\u70df\u53f6\u592a\u6f6e\u6e7f\uff0c\u8fd8\u662f\u56e0\u4e3a\u585e\u5f97\u592a\u7d27\uff0c\u70df\u6597\u8bb8\u4e45\u90fd\u6ca1\u6709\u71c3\u7740\uff0c\u7eb8\u5374\u5df2\u5c06\u71c3\u5c3d\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u662f\u7528\u5de6\u624b\u7684\u62c7\u6307\u548c\u98df\u6307\u62c8\u7740\u7eb8\u5a92\uff0c\u5176\u4f59\u7684\u4e09\u6839\u624b\u6307\u5fae\u5fae\u5f2f\u66f2\u3002 \u3000\u3000\u8001\u4eba\u7684\u65e0\u540d\u5c0f\u6307\u8ddd\u79bb\u4ed6\u7684\u8155\u8109\u8fd8\u4e0d\u5230\u4e03\u5bf8\u3002 \u3000\u3000\u706b\u7130\u5df2\u5c06\u70e7\u5230\u4e0a\u5b98\u91d1\u8679\u7684\u624b\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5374\u4f3c\u8fde\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u70df\u6597\u4e2d\u7684\u70df\u53f6\u7ec8\u4e8e\u88ab\u71c3\u7740\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u4e09\u6839\u624b\u6307\u4f3c\u4e4e\u52a8\u4e86\u52a8\uff0c\u8001\u4eba\u7684\u65e0\u540d\u6307\u548c\u5c0f\u6307\u4e5f\u52a8\u4e86\u52a8\uff0c\u4ed6\u4eec\u7684\u52a8\u4f5c\u90fd\u5f88\u5feb\uff0c \u5374\u5f88\u8f7b\u5fae\uff0c\u800c\u4e14\u4e00\u52a8\u4e4b\u540e\u5c31\u505c\u6b62\u3002 \u3000\u3000\u4e8e\u662f\u4e0a\u5b98\u91d1\u8679\u5f00\u59cb\u540e\u9000\u3002 \u3000\u3000\u8001\u4eba\u5f00\u59cb\u62bd\u65f1\u70df\u3002 \u3000\u3000\u4e24\u4eba\u4ece\u5934\u5230\u5c3e\u90fd\u4f4e\u7740\u5934\uff0c\u8c01\u4e5f\u6ca1\u6709\u53bb\u770b\u5bf9\u65b9\u4e00\u773c\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u65f6\uff0c\u674e\u5bfb\u6b22\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5728\u522b\u4eba\u770b\u6765\uff0c\u4ead\u5b50\u4e2d\u7684\u4e24\u4e2a\u4eba\u53ea\u4e0d\u8fc7\u5728\u70b9\u70df\u800c\u5df2\uff0c\u4f46\u674e\u5bfb\u6b22\u5374\u77e5\u9053\u90a3\u5b9e\u5728\u557b\u662f\u4e00\u573a\u60ca\u5fc3 \u52a8\u9b44\u7684\u51b3\u6597\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e00\u76f4\u5728\u7b49\u7740\u673a\u4f1a\uff0c\u53ea\u8981\u8001\u4eba\u7684\u795e\u5fd7\u7a0d\u6709\u677e\u61c8\uff0c\u624b\u8155\u7a0d\u4e0d\u7a33\u5b9a\uff0c\u4ed6\u7acb\u523b\u4fbf\u8981\u51fa\u624b \u3002 \u3000\u3000\u4f46\u4ed6\u59cb\u7ec8\u627e\u4e0d\u5230\u8fd9\u673a\u4f1a\u3002 \u3000\u3000\u5230\u6700\u540e\u4ed6\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5f2f\u957f\u7740\u7684\u4e09\u6839\u624b\u6307\u5df2\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u4ed6\u6bcf\u6839\u624b\u6307\u7684\u6bcf\u4e00\u4e2a\u52a8\u4f5c\u4e2d\u90fd \u85cf\u7740\u7cbe\u5fae\u7684\u53d8\u5316 \u3000\u3000\u600e\u5948\u8001\u4eba\u7684\u65e0\u540d\u6307\u548c\u5c0f\u6307\u5df2\u7acb\u523b\u5c06\u4ed6\u6bcf\u4e00\u4e2a\u53d8\u5316\u90fd\u5c01\u6b7b\u3002 \u3000\u3000\u8fd9\u5176\u95f4\u53d8\u5316\u4e4b\u7ec6\u817b\u7cbe\u5999\uff0c\u81ea\u7136\u4e5f\u53ea\u6709\u674e\u5bfb\u6b22\u8fd9\u79cd\u4eba\u624d\u80fd\u6b23\u8d4f\uff0c\u56e0\u4e3a\u90a3\u6b63\u662f\u6b66\u529f\u4e2d\u6700\u6df1\u5965 \u7684\u4e00\u90e8\u4efd\u3002 \u3000\u3000\u4e24\u4eba\u867d\u53ea\u4e0d\u8fc7\u5c06\u624b\u6307\u52a8\u4e86\u52a8\uff0c\u4f46\u5374\u5f53\u771f\u662f\u5343\u53d8\u4e07\u5316\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8fd9\u5371\u673a\u603b\u7b97\u5df2\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u540e\u9000\u4e09\u6b65\uff0c\u53c8\u9000\u56de\u539f\u6765\u7684\u5730\u65b9\u3002 \u3000\u3000\u8001\u4eba\u6162\u6162\u7684\u5438\u4e86\u53e3\u70df\uff0c\u624d\u5fae\u5fae\u7b11\u9053\uff1a\u4f60\u6765\u4e86\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u662f\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u4f60\u6765\u8fdf\u4e86\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u9601\u4e0b\u5728\u6b64\u76f8\u5019\uff0c\u83ab\u975e\u5df2\u7b97\u5c3d\u4e86\u8fd9\u662f\u6211\u5fc5\u7ecf\u4e4b\u8def\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u6211\u53ea\u76fc\u4f60\u83ab\u8981\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u56e0\u4e3a\u4f60\u5c31\u7b97\u6765\u4e86\uff0c\u8fd8\u662f\u7acb\u523b\u8981\u8d70\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u6211\u82e5\u4e0d\u60f3\u8d70\u5462\uff1f \u3000\u3000\u8001\u4eba\u6de1\u6de1\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u8d70\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u624b\uff0c\u5ffd\u7136\u7d27\u7d27\u63e1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u957f\u4ead\u4e2d\u4f3c\u4e4e\u7acb\u523b\u5c31\u5145\u6ee1\u4e86\u6740\u673a\u3002 \u3000\u3000\u8001\u4eba\u5374\u53ea\u662f\u957f\u957f\u5438\u4e86\u53e3\u70df\uff0c\u53c8\u6162\u6162\u5730\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u81ea\u4ed6\u53e3\u4e2d\u5410\u51fa\u6765\u7684\uff0c\u672c\u6765\u662f\u4e00\u6761\u5f88\u7ec6\u5f88\u957f\u7684\u70df\u67f1\u3002 \u3000\u3000\u7136\u540e\uff0c\u8fd9\u70df\u67f1\u5c31\u6162\u6162\u53d1\u751f\u4e86\u4e00\u79cd\u5f88\u5947\u7279\u7684\u5f2f\u66f2\u548c\u53d8\u5316\uff0c\u7a81\u7136\u4e00\u6298\uff0c\u5c04\u5230\u4e0a\u5b98\u91d1\u8679\u9762\u524d\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u70df\u96fe\u5df2\u5ffd\u7136\u95f4\u6d88\u6563\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u957f\u957f\u4e00\u63d6\uff0c\u9053\uff1a\u4f69\u670d\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u4e0d\u6562\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u5de5\u5341\u4e03\u5e74\u524d\u4e00\u4f1a\uff0c\u4eca\u65e5\u522b\u8fc7\uff0c\u518d\u89c1\u4e0d\u77e5\u4f55\u65f6\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u76f8\u89c1\u771f\u4e0d\u5982\u4e0d\u89c1\uff0c\u89c1\u53c8\u4f55\u59a8\uff1f\u4e0d\u89c1\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u7740\uff0c\u4f3c\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u672a\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u8001\u4eba\u53c8\u5f00\u59cb\u62bd\u70df\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8346\u65e0\u547d\u5f71\u5b50\u822c\u8ddf\u5728\u4ed6\u8eab\u540e\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u5374\u8fd8\u505c\u7559\u5728\u706f\u5149\u6d88\u5931\u5904\uff0c\u770b\u6765\u4eff\u4f5b\u6709\u4ec0\u4e48\u5fc3\u4e8b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8d70\u7684\u65f6\u5019\uff0c\u4f3c\u6709\u610f\uff0c\u4f3c\u65e0\u610f\uff0c\u66fe\u62ac\u8d77\u5934\u5411\u4ed6\u8fd9\u8fb9\u77a7\u4e86\u4e00\u773c\uff0c\u4ed6\u7b2c\u4e00\u6b21\u770b\u5230\u8fd9\u4e0a \u5b98\u91d1\u8679\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u9634\u68ee\uff0c\u5982\u6b64\u9510\u5229\u7684\u76ee\u5149\u3002 \u3000\u3000\u4ed6\u4ece\u8fd9\u53cc\u773c\u775b\uff0c\u5df2\u53ef\u5224\u65ad\u51fa\u4e0a\u5b98\u91d1\u8679\u7684\u5185\u529b\u6b66\u529f\u4e5f\u8bb8\u6bd4\u4f20\u8bf4\u4e2d\u8fd8\u8981\u53ef\u6015\uff01 \u3000\u3000\u4f46\u6700\u53ef\u6015\u7684\uff0c\u8fd8\u662f\u8346\u65e0\u547d\u7684\u773c\u775b\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u88ab\u8fd9\u53cc\u773c\u775b\u77a7\u4e86\u4e00\u773c\uff0c\u5fc3\u91cc\u90fd\u4f1a\u89c9\u5f97\u5f88\u4e0d\u8212\u670d\uff0c\u5f88\u95f7\uff0c\u95f7\u5f97\u50cf\u662f\u8981\u7a92\u606f\uff0c\u751a\u81f3\u60f3 \u5455\u5410\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u6839\u672c\u4e0d\u662f\u53cc\u4eba\u7684\u773c\u775b\uff0c\u4e5f\u4e0d\u662f\u91ce\u517d\u7684\u773c\u775b\u3002 \u3000\u3000\u4f46\u8fd9\u53cc\u773c\u775b\u5374\u662f\u6b7b\u7684\u3002 \u3000\u3000\u4ed6\u6f20\u89c6\u4e00\u5207\u60c5\u611f\uff0c\u4e00\u4e16\u751f\u547d\u2014\u2014\u751a\u81f3\u4ed6\u81ea\u5df1\u7684\u751f\u547d\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u5374\u5168\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u56e0\u4e3a\u5979\u6b63\u5728\u51dd\u89c6\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u8fd9\u662f\u5979\u7b2c\u4e00\u6b21\u770b\u6e05\u4e86\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u867d\u7136\u5728\u9ed1\u6697\u4e2d\uff0c\u4f46\u674e\u5bfb\u6b22\u9762\u4e0a\u7684\u8f6e\u5ed3\u6301\u6765\u5374\u4ecd\u662f\u90a3\u4e48\u806a\u660e\uff0c\u5c24\u5176\u662f\u4ed6\u773c\u775b\u548c\u9f3b\u5b50\uff0c\u7ed9\u4eba \u7684\u5370\u8c61\u66f4\u6df1\u523b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u6df1\u9083\u800c\u660e\u4eae\uff0c\u5145\u6ee1\u4e86\u667a\u6167\uff0c\u4ed6\u76ee\u5149\u4e2d\u867d\u5e26\u7740\u4e00\u4e9b\u538c\u5026\uff0c\u4e00\u4e9b\u5632\u5f04\uff0c\u5374\u53c8\u5145\u6ee1\u4e86 \u4f1f\u5927\u7684\u540c\u60c5\u3002 \u3000\u3000\u4ed6\u7684\u9f3b\u5b50\u76f4\u800c\u633a\uff0c\u8c61\u5f81\u7740\u4ed6\u7684\u575a\u5f3a\u3001\u6b63\u76f4\u548c\u65e0\u754f\u3002 \u3000\u3000\u4ed6\u7684\u773c\u89d2\u867d\u5df2\u6709\u4e86\u76b1\u7eb9\uff0c\u5374\u4f7f\u4ed6\u770b\u6765\u66f4\u6210\u719f\uff0c\u66f4\u6709\u5438\u5f15\u529b\uff0c\u66f4\u6709\u5b89\u5168\u611f\uff0c\u4f7f\u4eba\u89c9\u5f97\u5b8c\u5168 \u53ef\u4ee5\u4fe1\u4efb\uff0c\u5b8c\u5168\u53ef\u4ee5\u501a\u9760\u7684\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5927\u591a\u6570\u5c11\u5973\u68a6\u60f3\u4e2d\u7537\u4eba\u7684\u5178\u578b\u3002 \u3000\u3000\u4ed6\u4eec\u5168\u672a\u53d1\u73b0\u90a3\u8001\u4eba\u5df2\u5411\u4ed6\u4eec\u8d70\u4e86\u8fc7\u6765\uff0c\u6b63\u5fae\u7b11\u7740\u5728\u77a7\u4ed6\u4eec\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u6b23\u6170\u3002 \u3000\u3000\u4ed6\u9759\u9759\u7684\u77a7\u4e86\u4ed6\u4eec\u5f88\u4e45\uff0c\u624d\u5fae\u7b11\u7740\u9053\uff1a\u4f60\u4eec\u53ef\u6709\u4eba\u613f\u610f\u966a\u8001\u5934\u5b50\u804a\u5929\u4e48\uff1f \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\u6708\u5df2\u5347\u8d77\u3002 \u3000\u3000\u8001\u4eba\u548c\u674e\u5bfb\u6b22\u8d70\u5728\u524d\u9762\uff0c\u5b59\u5c0f\u7ea2\u9ed8\u9ed8\u7684\u8ddf\u5728\u4ed6\u4eec\u8eab\u540e\u3002 \u3000\u3000\u5979\u867d\u7136\u5782\u7740\u5934\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5fc3\u91cc\u5374\u6109\u5feb\u5f97\u7ad9\u60f3\u5450\u558a\uff0c\u56e0\u4e3a\u4ed6\u53ea\u8981\u4e00\u62ac\u5934\uff0c\u5c31\u53ef\u89c1\u5230\u5979\u5fc3 \u76ee\u4e2d\u6700\u4f69\u670d\u7684\u7537\u4eba\uff0c\u548c\u6700\u53ef\u7231\u7684\u7537\u4eba\u3002 \u3000\u3000\u5979\u89c9\u5f97\u5e78\u798f\u6781\u4e86\u3002 \u3000\u3000\u8001\u4eba\u5410\u4e00\u53e3\u70df\uff0c\u9053\uff1a\u6211\u8001\u65e9\u5c31\u542c\u8bf4\u8fc7\u4f60\uff0c\u8001\u65e9\u5c31\u60f3\u627e\u4f60\u559d\u9152\uff0c\u4eca\u5929\u624d\u53d1\u73b0\uff0c\u8ddf\u4f60\u804a\u5929\u7684 \u786e\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u5b59\u5c0f\u7ea2\u5374\u8d64\u7684\u7b11\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u4f46\u4ed6\u76f4\u5230\u73b0\u5728\uff0c\u9664\u4e86\u5411\u4f60\u8001\u4eba\u5bb6\u95ee\u597d\u4e4b\u5916 \uff0c\u522b\u7684\u8bdd\u8fde\u4e00\u4e2a\u5b57\u6ca1\u6709\u8bf4\u5440\u3002 \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u8fd9\u6b63\u662f\u4ed6\u7684\u597d\u5904\uff0c\u4e0d\u8be5\u8bf4\u7684\u8bdd\u4ed6\u4e00\u53e5\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4e0d\u8be5\u95ee\u7684\u8bdd\u4e00\u53e5\u4e5f\u6ca1\u6709\u95ee\uff0c \u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u4e00\u5b9a\u65e9\u5df2\u6ca1\u6cd5\u63a2\u542c\u6211\u4eec\u7684\u6765\u5386\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u6211\u65e9\u5df2\u731c\u7740\u4e86\u524d\u8f88\u7684\u6765\u5386\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u54e6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u666e\u5929\u4e4b\u4e0b\uff0c\u80fd\u5c06\u4e0a\u5b98\u91d1\u8679\u60ca\u9000\u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u8001\u4eba\u7b11\u4e86\u9053\uff1a\u4f60\u82e5\u4ee5\u4e3a\u4e0a\u5b98\u91d1\u8679\u662f\u88ab\u6211\u5413\u8d70\u7684\uff0c\u4f60\u5c31\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u7b49\u674e\u5bfb\u6b22\u8bf4\u8bdd\uff0c\u63a5\u7740\u9053\uff1a\u4e0a\u5b98\u91d1\u8679\u7684\u6b66\u529f\uff0c\u4f60\u60f3\u5fc5\u4e5f\u770b\u51fa\uff0c\u5bf8\u6b65\u4e0d\u79bb\u8ddf\u7740\u4ed6\u7684\u90a3\u5c11 \u5e74\u4eba\uff0c\u66f4\u662f\u53ef\u6015\u7684\u5bf9\u624b\uff0c\u4ee5\u4ed6\u4eec\u4e24\u4eba\u8054\u624b\u4e4b\u529b\uff0c\u5929\u4e0b\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u4eba\u4eba\u80fd\u62b5\u6321\u4ed6\u4eec\u4e09\u767e\u62db\uff0c \u66f4\u83ab\u8bf4\u8981\u80dc\u8fc7\u4ed6\u4eec\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u524d\u8f88\u4e5f\u4e0d\u80fd\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u6211\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u4ed6\u4eec\u5374\u8fd8\u662f\u8d70\u4e86\u3002 \u3000\u3000\u8001\u4eba\uff1a\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u89c9\u5f97\u73b0\u5728\u8fd8\u6ca1\u6709\u5fc5\u8981\u6740\u6211\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u65e9\u5df2\u53d1\u89c9\u4f60\u5728\u8fd9\u91cc\uff0c \u4ed6\u4eec\u6ca1\u6709\u628a\u63e1\u80fd\u80dc\u8fc7\u6211\u4eec\u4e24\u4eba\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53c8\u5fcd\u4e0d\u4f4f\u9053\uff1a\u4ed6\u4eec\u5c31\u7b97\u5df2\u53d1\u89c9\u6811\u540e\u6709\u4eba\uff0c\u53c8\u600e\u4e48\u662f\u674e\u2014\u2014\u674e\u63a2\u82b1\u5462\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u50cf\u674e\u63a2\u82b1\u8fd9\u6837\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u5c31\u7b97\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\u4e0d\u52a8\uff0c\u4f46\u8981\u4ed6\u5fc3\u91cc\u5bf9\u67d0\u4eba\u751f\u51fa \u4e86\u654c\u610f\uff0c\u5c31\u4f1a\u6563\u53d1\u51fa\u4e00\u79cd\u6740\u6c14\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6740\u6c14\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u4e0d\u9519\uff0c\u6740\u6c14\uff01\u4f46\u8fd9\u79cd\u6740\u6c14\u81ea\u7136\u4e5f\u53ea\u6709\u4e0a\u5b98\u91d1\u8679\u90a3\u6837\u7684\u9ad8\u624b\u624d\u80fd\u611f\u89c9\u5f97\u51fa\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u8001\u4eba\u5bb6\u8bf4\u5f97\u592a\u7384\u5999\u7684\u4e8b\uff0c\u6211\u4e0d\u61c2\u3002 \u3000\u3000\u8001\u4eba\u8083\u7136\u9053\uff1a\u6b66\u529f\u672c\u5c31\u662f\u4ef6\u5f88\u7384\u5999\u7684\u4e8b\uff0c\u61c2\u5f97\u7684\u4eba\u672c\u5c31\u4e0d\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u65e0\u8bba\u4ed6\u4eec\u662f\u4e3a\u4f55\u8d70\u7684\uff0c\u524d\u8f88\u76f8\u52a9\u4e4b\u60c5\uff0c\u603b\u662f\u2014\u2014 \u3000\u3000\u8001\u4eba\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u6211\u53ea\u662f\u559c\u6b22\u770b\u89c1\u4f60\u8fd9\u79cd\u4eba\u597d\u597d\u7684\u6d3b\u7740\uff0c\u56e0\u4e3a\u50cf\u4f60\u8fd9\u6837\u7684 \u4eba\uff0c\u6d3b\u5728\u4e16\u4e0a\u7684\u5df2\u4e0d\u591a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u662f\u5fae\u7b11\uff0c\u53ea\u6709\u6c89\u9ed8\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u4f60\u6211\u867d\u7136\u521d\u6b21\u76f8\u89c1\uff0c\u4f46\u4f60\u7684\u813e\u6c14\u6211\u5f88\u4e86\u89e3\uff0c\u6240\u4ee5\u6211\u4e5f\u5e76\u4e0d\u60f3\u529d\u4f60\u79bb\u5f00\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u6211\u53ea\u5e0c\u671b\u4f60\u80fd\u660e\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u524d\u8f88\u6307\u6559\u3002 \u3000\u3000\u8001\u4eba\u6b63\u8272\u9053\uff1a\u6797\u8bd7\u97f3\u662f\u7528\u4e0d\u7740\u4f60\u6765\u4fdd\u62a4\u7684\uff0c\u4f60\u8d70\u4e86\u5bf9\u5979\u53ea\u6709\u597d\u5904\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u4e3a\u4e4b\u9ed8\u7136\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u6797\u8bd7\u97f3\u672c\u4eba\u5e76\u4e0d\u662f\u522b\u4eba\u4f24\u5bb3\u7684\u5bf9\u8c61\uff0c\u522b\u4eba\u60f3\u4f24\u5bb3\u5979\uff0c\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u4f60\uff0c\u6362\u53e5\u8bdd \u8bf4\uff0c\u522b\u4eba\u8981\u4f24\u5bb3\u5979\uff0c\u5c31\u56e0\u4e3a\u4f60\u5728\u4fdd\u62a4\u5979\uff0c\u4f60\u82e5\u4e0d\u4fdd\u62a4\u5979\uff0c\u4e5f\u5c31\u6839\u672c\u6ca1\u6709\u4eba\u8981\u4f24\u5bb3\u5979\u4e86\u2014\u2014\u8fd9 \u9053\u7406\u4f60\u660e\u767d\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u597d\u50cf\u88ab\u4eba\u62bd\u4e86\u4e00\u97ad\uff0c\u75db\u82e6\u5f97\u5168\u8eab\u90fd\u4eff\u4f5b\u6536\u7f29\u4e86\u8d77\u6765\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u4eff\u4f5b\u53ea\u6709\u4e09 \u5c3a\u9ad8\u3002 \u3000\u3000\u8001\u4eba\u5374\u5168\u672a\u7559\u610f\u5230\u4ed6\u7684\u75db\u82e6\uff0c\u53c8\u9053\uff1a\u4f60\u82e5\u89c9\u5f97\u5979\u592a\u5bc2\u5bde\uff0c\u60f3\u966a\u4f34\u5979\uff0c\u73b0\u5728\u4e5f\u5df2\u7528\u4e0d\u7740\uff0c \u56e0\u4e3a\u9f99\u5578\u4e91\u5df2\u7ecf\u56de\u6765\u4e86\uff0c\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u53ea\u6709\u589e\u52a0\u5979\u70e6\u607c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u832b\u7136\u51dd\u795e\u7740\u8fdc\u65b9\u7684\u9ed1\u6697\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u4e86\uff0c\u6211\u9519\u4e86\uff0c\u6211 \u53c8\u9519\u4e86\u2014\u2014 \u3000\u3000\u5979\u7684\u8170\u4f3c\u4e5f\u5f2f\u4e86\u4e0b\u53bb\uff0c\u80cc\u4e5f\u65e0\u6cd5\u633a\u76f4\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5fc3\u91cc\u53c8\u662f\u601c\u60dc\uff0c\u53c8\u662f\u540c\u60c5\u3002 \u3000\u3000\u5979\u77e5\u9053\u7237\u7237\u662f\u5728\u6545\u610f\u523a\u6fc0\u4ed6\uff0c\u6545\u610f\u4ee4\u4ed6\u75db\u82e6\uff0c\u5979\u4e5f\u77e5\u9053\u8fd9\u6837\u505a\u5bf9\u4ed6\u53ea\u6709\u597d\u5904\uff0c\u4f46\u5979\u5374\u4e0d \u5fcd\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u9f99\u5578\u4e91\u5ffd\u7136\u56de\u6765\uff0c\u53ea\u56e0\u4ed6\u5df2\u627e\u5230\u4e2a\u4ed6\u81ea\u4fe1\u53ef\u4ee5\u5bf9\u4ed8\u674e\u5bfb\u6b22\u7684\u5bf9\u624b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u53c8\u4f55\u5fc5\u627e\u4eba\u5bf9\u4ed8\u6211\uff1f\u6211\u8fd8\u662f\u5c06\u4ed6\u5f53\u505a\u6211\u7684\u670b\u53cb\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u4f46\u4ed6\u5374\u4e0d\u8fd9\u4e48\u60f3\uff1d\uff1d\u4f60\u53ef\u77e5\u9053\u4ed6\u627e\u6765\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u80e1\u4e0d\u5f52\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u4e0d\u9519\uff0c\u6b63\u662f\u90a3\u75af\u5b50\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u63d2\u5634\u9053\uff1a\u80e1\u75af\u5b50\u7684\u6b66\u529f\u771f\u7684\u90a3\u4e48\u5389\u5bb3\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u666e\u5929\u4e4b\u4e0b\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u6211\u59cb\u7ec8\u4f30\u4e0d\u900f\u4ed6\u4eec\u6b66\u529f\u4e4b\u6df1\u6d45\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u54ea\u4e24\u4e2a\u4eba\uff1f \u3000\u3000\u8001\u4eba\u542b\u7b11\u9053\uff1a\u5176\u4e2d\u4e00\u4eba\u662f\u674e\u63a2\u82b1\uff0c\u53e6\u4e00\u4eba\u5c31\u662f\u80e1\u75af\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77e5\u9053\uff1a\u524d\u8f88\u8fc7\u5956\u4e86\uff0c\u636e\u6211\u6240\u77e5\uff0c\u6211\u7684\u670b\u53cb\u963f\u98de\u6b66\u529f\u5c31\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u8fd8\u6709\u8346\u65e0\u547d \u2014\u2014 \u3000\u3000\u8001\u4eba\u622a\u53e3\u9053\uff1a\u963f\u98de\u548c\u8346\u65e0\u547d\u4e00\u6837\uff0c\u4ed6\u4eec\u6839\u672c\u4e0d\u61c2\u5f97\u6b66\u529f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6115\u7136\u9053\uff1a\u524d\u8f88\u8bf4\u4ed6\u4eec\u4e0d\u61c2\u6b66\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u4eec\u975e\u4f46\u4e0d\u61c2\u6b66\u529f\uff0c\u800c\u4e14\u4e0d\u914d\u8c08\u6b66\u2014\u2014 \u3000\u3000\u4ed6\u51b7\u51b7\u9053\uff1a\u4ed6\u4eec\u53ea\u4f1a\u6740\u4eba\uff0c\u53ea\u61c2\u5f97\u6740\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u963f\u98de\u548c\u8346\u65e0\u547d\u8fd8\u662f\u4e0d\u540c\u7684\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u6709\u4f55\u4e0d\u540c\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u4ed6\u4eec\u6740\u4eba\u7684\u65b9\u6cd5\u5e76\u65e0\u4e0d\u540c\uff0c\u4f46\u6740\u4eba\u7684\u76ee\u7684\u5374\u7edd\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u54e6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u963f\u98de\u53ea\u6709\u5728\u4e07\u4e0d\u5f97\u5df2\u65f6\u624d\u6740\u4eba\uff0c\u8346\u65e0\u547d\u5374\u53ea\u662f\u4e3a\u4e86\u6740\u4eba\u800c\u6740\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u6211\u2014\u2014 \u3000\u3000\u8001\u4eba\u9053\uff1a\u4f60\u82e5\u60f3\u770b\u770b\u4ed6\uff0c\u73b0\u5728\u6b63\u662f\u65f6\u5019\uff0c\u5426\u5219\u53ea\u6015\u5c31\u592a\u8fdf\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u633a\u8d77\u80f8\uff0c\u9053\uff1a\u597d\uff0c\u6211\u8fd9\u5c31\u53bb\u627e\u4ed6\uff1f \u3000\u3000\u8001\u4eba\u76ee\u4e2d\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u9053\uff1a\u4f60\u77e5\u9053\u4ed6\u4f4f\u7684\u5730\u65b9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u8d76\u5230\u524d\u9762\uff0c\u9053\uff1a\u4f46\u4f60\u4e5f\u8bb8\u8fd8\u662f\u627e\u4e0d\u7740\uff0c\u8fd8\u662f\u8ba9\u6211\u5e26\u4f60\u53bb\u7684\u597d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u672a\u5f00\u53e3\uff0c\u8001\u4eba\u677f\u7740\u8138\u9053\uff1a\u4f60\u8fd8\u6709\u4f60\u7684\u4e8b\uff0c\u674e\u63a2\u82b1\u4e5f\u7528\u4e0d\u7740\u4f60\u5e26\u8def\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u561f\u8d77\u5634\uff0c\u770b\u6837\u5b50\u51e0\u4e4e\u8981\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u9053\uff1a\u5c31\u6b64\u522b\u8fc7\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u672c\u6709\u8bb8\u591a\u8bdd\u8981\u8bf4\uff0c\u5374\u53ea\u8bf4\u4e86\u8fd9\u56db\u4e2a\u5b57\u3002 \u3000\u3000\u8001\u4eba\u4e00\u6311\u5927\u62c7\u6307\uff0c\u9053\uff1a\u5bf9\uff0c\u8bf4\u8d70\u5c31\u8d70\uff0c\u8fd9\u624d\u662f\u7537\u5b50\u6c49\uff0c\u5927\u4e08\u592b\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u679c\u7136\u8bf4\u8d70\u5c31\u8d70\uff0c\u800c\u4e14\u6ca1\u6709\u56de\u987e\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u76ee\u9001\u4ed6\u8fdc\u53bb\uff0c\u773c\u5708\u513f\u90fd\u7ea2\u4e86\u3002 \u3000\u3000\u8001\u4eba\u62cd\u4e86\u5979\u80a9\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u5fc3\u91cc\u662f\u4e0d\u662f\u5f88\u96be\u53d7\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u8001\u4eba\u7b11\u4e86\uff0c\u7b11\u5bb9\u4e2d\u5e26\u7740\u65e0\u9650\u6148\u7965\uff0c\u9053\uff1a\u50bb\xd7\u5934\uff0c\u4f60\u4ee5\u4e3a\u7237\u7237\u4e0d\u77e5\u9053\u4f60\u7684\u5fc3\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u561f\u7740\u5634\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff1a\u7237\u7237\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u966a\u4ed6\u53bb\u3002 \u3000\u3000\u8001\u4eba\u67d4\u58f0\u9053\uff1a\u4f60\u8981\u77e5\u9053\uff0c\u50cf\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u53ef\u4e0d\u662f\u5bb9\u6613\u80fd\u5f97\u5230\u7684\u3002\u4ed6\u76ee\u4e2d\u95ea\u7740\u4e16\u6545 \u7684\u667a\u6167\u4e4b\u5149\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u4f60\u8981\u5f97\u5230\u4ed6\u7684\u4eba\uff0c\u5c31\u5148\u8981\u5f97\u5230\u4ed6\u7684\u5fc3\uff0c\u90a3\u53ef\u4e0d\u7b80\u5355\uff0c\u4e00\u5b9a\u8981\u6162\u6162\u5730 \u60f3\u6cd5\u5b50\uff0c\u4f46\u4f60\u82e5\u8ffd\u5f97\u4ed6\u592a\u7d27\uff0c\u5c31\u4f1a\u5c06\u4ed6\u5413\u8dd1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u867d\u7136\u8bf4\u8d70\u5c31\u8d70\uff0c\u867d\u7136\u6ca1\u6709\u56de\u987e\uff0c\u4f46\u4ed6\u7684\u5fc3\u5374\u4ecd\u7136\u88ab\u4e00\u6839\u65e0\u5f62\u7684\u7ebf\u7275\u7740\uff0c\u7275\u5f97\u7d27\u7d27 \u7684\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9\u4e00\u8d70\uff0c\u53c8\u4e0d\u77e5\u4f55\u65f6\u624d\u80fd\u518d\u89c1\u6797\u8bd7\u97f3\u4e86\u3002 \u3000\u3000\u76f8\u89c1\u65f6\u96be\uff0c\u522b\u4ea6\u96be\uff01 \u3000\u3000\u8fd9\u5341\u4f59\u5e74\u6765\uff0c\u4ed6\u53ea\u89c1\u5230\u6797\u8bd7\u97f3\u4e09\u6b21\u3002\u6bcf\u6b21\u90fd\u53ea\u6709\u5306\u5306\u4e00\u9762\uff0c\u6709\u65f6\u751a\u81f3\u8fde\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u8bf4 \uff0c\u4f46\u7275\u5728\u4ed6\u5fc3\u4e0a\u7684\u7ebf\uff0c\u5374\u6c38\u8fdc\u662f\u63e1\u5728\u6797\u8bd7\u97f3\u624b\u91cc\u7684\u3002\u53ea\u8981\u80fd\u89c1\u5230\u5979\uff0c\u751a\u81f3\u53ea\u8981\u80fd\u611f\u89c9\u5230\u5979\u5c31 \u5728\u81ea\u5df1\u9644\u8fd1\uff0c\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u3002 \u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u7956\u5b59>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u516b\u7ae0\u3000\u7956\u5b59 \u3000\u3000\u79cb\u98ce\u6251\u9762\uff0c\u5df2\u6709\u51ac\u610f\u3002 \u3000\u3000\u6b8b\u79cb\u5df2\u6b8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5883\u4e5f\u6b63\u5982\u8fd9\u6b8b\u79cb\u822c\u8427\u7d22\u3002 \u3000\u3000\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u53ea\u6709\u589e\u52a0\u5979\u7684\u70e6\u607c\u548c\u75db\u82e6\u2014\u2014 \u3000\u3000\u8001\u4eba\u7684\u8bdd\uff0c\u4f3c\u4e4e\u8fd8\u5728\u4ed6\u8033\u8fb9\u54cd\u8d77\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u975e\u4f46\u4e0d\u8be5\u518d\u89c1\u5979\uff0c\u8fde\u60f3\u90fd\u4e0d\u8be5\u60f3\u5979\u3002 \u3000\u3000\u90a3\u8001\u4eba\u4e0d\u4f46\u662f\u667a\u8005\uff0c\u5fc5\u5b9a\u662f\u4f4d\u98ce\u5c18\u5f02\u4eba\uff0c\u7edd\u9876\u9ad8\u624b\u3002\u4e16\u4e0a\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u4f3c\u4e4e\u90fd\u79c0\u5c11\u6709 \u4e0d\u77e5\u9053\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u8eab\u4efd\u5b9e\u5728\u592a\u795e\u79d8\u3002 \u3000\u3000\u4ed6\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u7a76\u7adf\u9690\u85cf\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5b59\u9a7c\u5b50\uff0c\u674e\u5bfb\u6b22\u5f88\u4f69\u670d\u3002 \u3000\u3000\u4e00\u4e2a\u82e5\u80fd\u5728\u62b9\u5e03\u548c\u626b\u628a\u95f4\u9690\u5fcd\u5341\u4e94\u5e74\uff0c\u65e0\u8bba\u4ed6\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u90fd\u662f\u503c\u5f97\u4eba\u6df1\u6df1\u4f69\u670d\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u662f\u4e3a\u4e86\u8c01\u624d\u8fd9\u6837\u505a\uff1f \u3000\u3000\u4ed6\u4eec\u5b88\u62a4\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u81f3\u4e8e\u5b59\u5c0f\u7ea2\u2014\u2014\u5c0f\u7ea2\u7684\u5fc3\u610f\uff0c\u4ed6\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u80fd\u63a5\u53d7\uff0c\u4e5f\u4e0d\u6562\u63a5\u53d7\u3002 \u3000\u3000\u603b\u4e4b\uff0c\u8fd9\u4e00\u5bb6\u4eba\u90fd\u5145\u6ee1\u4e86\u795e\u79d8\uff0c\u795e\u79d8\u5f97\u51e0\u4e4e\u5df2\u6709\u4e9b\u6709\u53ef\u6015\u2014\u2014 \u3000\u3000\u5c71\u6751\u3002 \u3000\u3000\u5c71\u811a\u4e0b\uff0c\u9ad8\u9ad8\u6311\u8d77\u4e00\u9762\u9752\u5e03\u9152\u65d7\u3002 \u3000\u3000\u9152\u94fa\u7684\u540d\u5b57\u5f88\u96c5\uff0c\u6709\u4e03\u4e2a\u5b57\uff1a\u505c\u8f66\u9189\u7231\u67ab\u6797\u665a\u3002 \u3000\u3000\u53ea\u770b\u8fd9\u540d\u5b57\uff0c\u674e\u5bfb\u6b22\u5c31\u5df2\u5c06\u9189\u4e86\u3002 \u3000\u3000\u9152\u4e0d\u9187\uff0c\u5374\u5f88\u6e05\uff0c\u5f88\u51bd\uff0c\u662f\u5c71\u6cc9\u917f\u6210\u7684\u3002 \u3000\u3000\u5c71\u6cc9\u7531\u540e\u5c71\u6d41\u5165\u8fd9\u91cc\uff0c\u6e05\u53ef\u89c1\u5e95\uff0c\u674e\u5bfb\u6b22\u77e5\u9053\u6cbf\u7740\u8fd9\u9053\u6cc9\u6c34\u8d70\u5230\u540e\u5c71\uff0c\u5c31\u53ef\u5728\u4e00\u7247\u6885\u6797 \u6df1\u5904\u627e\u5230\u4e09\u4e94\u95f4\u7cbe\u81f4\u7684\u6728\u5c4b\u3002 \u3000\u3000\u963f\u98de\u548c\u6797\u4ed9\u513f\u5c31\u5728\u90a3\u6728\u5c4b\u91cc\u3002 \u3000\u3000\u60f3\u5230\u963f\u98de\u90a3\u82f1\u4fca\u7626\u524a\u7684\u8138\uff0c\u90a3\u660e\u4eae\u9510\u5229\u7684\u773c\u775b\uff0c\u90a3\u5b64\u50b2\u5014\u5f3a\u7684\u8868\u60c5\uff0c\u674e\u589e\u6b22\u7684\u8840\u90fd\u4f3c\u6cb8 \u817e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u6700\u4ee4\u4eba\u96be\u4ee5\u5fd8\u6000\u7684\uff0c\u8fd8\u662f\u4ed6\u90a3\u96be\u5f97\u89c1\u5230\u7684\u7b11\u5bb9\uff0c\u8fd8\u6709\u4ed6\u90a3\u9897\u9690\u85cf\u5728\u51b0\u96ea\u540e\u7684\u706b\u70ed\u7684\u5fc3 \u3002 \u3000\u3000\u8fd1\u4e61\u60c5\u602f\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u963f\u98de\u8fd9\u4e24\u5e74\u6765\u5df2\u53d8\u6210\u4ec0\u4e48\u6a21\u6837\uff1f \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u6797\u4ed9\u513f\u8fd9\u4e24\u5e74\u6765\u662f\u600e\u4e48\u6837\u5bf9\u5f85\u4ed6\u7684\uff1f \u3000\u3000\u5979\u867d\u7136\u50cf\u662f\u5929\u5c71\u7684\u4ed9\u5b50\uff0c\u5374\u4e13\u95e8\u5e26\u7537\u5b50\u5165\u5730\u72f1\uff1f \u3000\u3000\u963f\u98de\u662f\u4e0d\u662f\u5df2\u843d\u5165\u5730\u72f1\u4e2d\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u6562\u53bb\u60f3\uff0c\u4ed6\u5f88\u4e86\u89e3\u963f\u98de\uff0c\u4ed6\u77e5\u9053\u50cf\u963f\u98de\u8fd9\u79cd\u4eba\uff0c\u82e5\u4e3a\u4e86\u7231\u60c5\uff0c\u662f\u4e0d\u60dc\u6d3b\u5728\u5730\u72f1 \u4e2d\u7684\u3002 \u3000\u3000\u9ec4\u660f\uff0c\u53c8\u662f\u9ec4\u660f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5750\u7684\u4f4d\u7f6e\uff0c\u662f\u8fd9\u5c0f\u5e97\u6700\u9634\u6697\u7684\u89d2\u843d\u91cc\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u4e60\u60ef\uff0c\u56e0\u4e3a\u5750\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u4ed6\u53ef\u4ee5\u4e00\u773c\u5c31\u770b\u5230\u8d70\u8fdb\u6765\u7684\u4eba\uff0c\u800c\u522b\u4eba\u5374\u5f88\u96be\u53d1\u73b0 \u4ed6\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u7edd\u672a\u60f3\u5230\u7b2c\u4e00\u4e2a\u8d70\u8fdb\u6765\u7684\u4eba\u7adf\u662f\u4e0a\u5b98\u98de\u3002 \u3000\u3000\u4ed6\u4e00\u8d70\u8fdb\u6765\u5c31\u5728\u6700\u9760\u8fd1\u95e8\u7684\u4f4d\u7f6e\u4e0a\u5750\u4e0b\uff0c\u773c\u775b\u4e00\u76f4\u77aa\u7740\u95e8\u5916\uff0c\u4eff\u4f5b\u662f\u5728\u7b49\u4eba\uff0c\u795e\u60c5\u7adf\u663e \u5f97\u6709\u4e9b\u7126\u6025\uff0c\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u8fd9\u548c\u4ed6\u5f80\u6614\u90a3\u79cd\u9634\u6c89\u9547\u9759\u7684\u6001\u5ea6\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u4ed6\u7b49\u7684\u663e\u7136\u662f\u4e2a\u5f88\u91cd\u8981\u7684\u4eba\u3002\u800c\u4e14\u4ed6\u5355\u8eab\u524d\u6765\uff0c\u672a\u5e26\u968f\u4ece\uff0c\u663e\u89c1\u8fd9\u7ea6\u4f1a\u975e\u4f46\u5f88\u91cd\u8981\uff0c\u800c \u4e14\u5f88\u79d8\u5bc6\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u504f\u50fb\u7684\u5c71\u6751\uff0c\u600e\u4f1a\u6709\u4ee4\u4ed6\u89c9\u5f97\u91cd\u8981\u7684\u4eba\u7269\uff1f \u3000\u3000\u90a3\u4e48\u4ed6\u7b49\u7684\u662f\u8c01\u5462\uff1f \u3000\u3000\u4ed6\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u4e0d\u662f\u548c\u963f\u98de\u4e0e\u6797\u4ed9\u513f\u6709\u5173\u7cfb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ee5\u624b\u652f\u5934\uff0c\u5c06\u9762\u76ee\u9690\u85cf\u8d77\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7684\u773c\u775b\u4e00\u76f4\u77aa\u7740\u95e8\u53e3\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u5411\u522b\u7684\u5730\u65b9\u770b\u4e00\u773c\u3002 \u3000\u3000\u5c0f\u5e97\u4e2d\u7ec8\u4e8e\u6302\u8d77\u4e86\u706f\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7684\u795e\u60c5\u663e\u5f97\u66f4\u7126\u8e81\uff0c\u66f4\u4e0d\u5b89\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5df2\u6709\u4e24\u9876\u7eff\u6ce5\u5c0f\u6865\u505c\u5728\u95e8\u53e3\uff0c\u62ac\u8f7f\u7684\u90fd\u662f\u5341\u5341\u6765\u5c81\u7684\u5e74\u8f7b\u5c0f\u4f19\u5b50\u3002 \u3000\u3000\u7b2c\u4e00\u9876\u5c0f\u8f7f\u4e2d\u5df2\u8d70\u4e0b\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u7ea2\u8863\u59d1\u5a18\uff0c\u867d\u7136\u8fd8\u6ca1\u6709\u5438\u5f15\u7537\u4eba\u7684\u9b45\u529b\uff0c\u4f46\u7ea4\u8170\u4e00\u63e1 \uff0c\u5012\u4e5f\u695a\u695a\u52a8\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u521a\u62ff\u8d77\u9152\u676f\uff0c\u7a81\u7136\u653e\u4e0b\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u526a\u6c34\u822c\u7684\u53cc\u77b3\u56db\u4e0b\u4e00\u8f6c\uff0c\u5df2\u76c8\u76c8\u6765\u5230\u4ed6\u9762\u524d\uff0c\u9053\uff1a\u516c\u5b50\u4e45\u5019\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u4f60\u662f\u2014\u2014 \u3000\u3000\u7ea2\u8863\u5c0f\u59d1\u5a18\u773c\u6ce2\u56db\u4e0b\u4e00\u8f6c\uff0c\u6084\u58f0\u9053\uff1a\u505c\u8f66\u9189\u7231\u67ab\u6797\u665a\uff0c\u5a07\u9762\u7ea2\u4e8e\u4e8c\u6708\u82b1\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u5979\u5462\uff1f\u5979\u4e0d\u80fd\u6765\uff1f \u3000\u3000\u7ea2\u8863\u5c0f\u59d1\u5a18\u62bf\u5634\u7b11\u9053\uff1a\u516c\u5b50\u4e14\u83ab\u5fc3\u7126\uff0c\u8bf7\u968f\u6211\u6765\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u7740\u4e0a\u5b98\u98de\u8d70\u51fa\u95e8\uff0c\u5750\u4e0a\u4e86\u7b2c\u4e8c\u9876\u5c0f\u8f7f\uff0c\u770b\u7740\u8f7f\u592b\u4eec\u5c06\u8f7f\u5b50\u62ac\u8d77\uff0c\u4ed6\u5c31\u53d1\u89c9\u4e00\u4ef6 \u5f88\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u4e9b\u8f7f\u592b\u4eec\u4e00\u4e2a\u4e2a\u90fd\u662f\u5e74\u8f7b\u529b\u58ee\uff0c\u884c\u52a8\u77eb\u5065\uff0c\u7b2c\u4e00\u9876\u5c0f\u8f7f\u7684\u8f7f\u592b\u62ac\u8f7f\u65f6\u6839\u672c\u4e0d\u8d39\u5439\u7070\u4e4b \u529b\u3002 \u3000\u3000\u4f46\u7b2c\u4e8c\u9876\u5c0f\u8f7f\u7684\u8f7f\u592b\u62ac\u8f7f\u65f6\u5374\u663e\u5f97\u5403\u529b\u591a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u968f\u7740\u4ed8\u6e05\u4e86\u9152\u5e10\uff0c\u8d70\u51fa\u4e86\u95e8\u3002 \u3000\u3000\u4ed6\u672c\u4e0d\u559c\u6b22\u591a\u7ba1\u522b\u4eba\u7684\u95f2\u4e8b\uff0c\u66f4\u4e0d\u613f\u7aa5\u63a2\u522b\u4eba\u7684\u9690\u79c1\uff0c\u4f46\u73b0\u5728\u4ed6\u5374\u51b3\u5b9a\u8981\u5c3e\u968f\u4e0a\u5b98\u98de\uff0c \u770b\u770b\u4ed6\u7ea6\u4f1a\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u674e\u5bfb\u6b22\u603b\u89c9\u5f97\u4ed6\u5230\u8fd9\u91cc\u6765\uff0c\u5fc5\u5b9a\u548c\u963f\u98de\u6709\u5173\u7cfb\u3002 \u3000\u3000\u8f7f\u5b50\u5df2\u8d70\u5165\u67ab\u6797\u3002 \u3000\u3000\u7a81\u7136\uff0c\u8f7f\u5b50\u91cc\u4f20\u51fa\u4e00\u58f0\u7b11\u3002 \u3000\u3000\u7b11\u58f0\u53c8\u5a07\uff0c\u53c8\u5a9a\uff0c\u800c\u4e14\uff0c\u8fd8\u5e26\u7740\u8f7b\u8f7b\u7684\u5598\u606f\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\uff0c\u53ea\u8981\u4ed6\u662f\u7537\u4eba\uff0c\u542c\u4e86\u8fd9\u79cd\u77e5 \u58f0\u90fd\u65e0\u6cd5\u4e0d\u52a8\u5fc3\u3002 \u3000\u3000\u4f46\u8f7f\u5b50\u91cc\u5750\u7684\u660e\u660e\u662f\u4e0a\u5b98\u98de\u3002\u96be\u9053\u4e0a\u5b98\u98de\u5df2\u53d8\u6210\u4e86\u5973\u4eba\uff1f \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u8f7f\u5b50\u91cc\u53d1\u51fa\u4e00\u58f0\u5a07\u557c\uff1a\u5c0f\u98de\uff0c\u4e0d\u8981\u8fd9\u6837\u2014\u2014\u5728\u8fd9\u91cc\u4e0d\u53ef\u4ee5\u2014\u2014 \u3000\u3000\u201c\u539f\u6765\u4f60\u4e5f\u548c\u522b\u7684\u7537\u4eba\u4e00\u6837\uff0c\u60f3\u6211\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u6b3a\u8d1f\u6211\u3002\u201d \u3000\u3000\u8bed\u58f0\u8d8a\u6765\u8d8a\u4f4e\uff0c\u6e10\u6e10\u6a21\u7cca\uff0c\u7ec8\u4e8e\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u8f7f\u5b50\u5df2\u4e0a\u5c71\u5761\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u501a\u5728\u5c71\u5761\u4e0b\u7684\u4e00\u682a\u67ab\u6811\u540e\uff0c\u5728\u4f4e\u4f4e\u5730\u54b3\u55fd\u3002 \u3000\u3000\u539f\u6765\u8f7f\u5b50\u91cc\u6709\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4eba\u81ea\u7136\u662f\u4e0a\u5b98\u98de\u3002 \u3000\u3000\u4f46\u4e00\u76f4\u5728\u8f7f\u91cc\u7b49\u7740\u4ed6\u7684\u5973\u4eba\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u4e00\u5411\u5bf9\u5973\u4eba\u79c0\u6709\u7ecf\u9a8c\uff0c\u4ed6\u77e5\u9053\u4e16\u4e0a\u4f1a\u6492\u5a07\u7684\u5973\u4eba\u867d\u7136\u4e0d\u5c11\uff0c\u4f46\u6492\u8d77\u5a07\u6765\u771f\u80fd\u4ee4\u7537\u4eba\u52a8 \u5fc3\u7684\u5374\u4e0d\u591a\u3002 \u3000\u3000\u4ed6\u7b80\u76f4\u5df2\u53ef\u8bf4\u51fa\u8f7f\u5b50\u91cc\u8fd9\u5973\u4eba\u7684\u540d\u5b57\u3002 \u3000\u3000\u4f46\u4ed6\u4e0d\u6562\u8bf4\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u6ca1\u6709\u786e\u5b9a\u3002 \u3000\u3000\u65e0\u8bba\u5bf9\u4ec0\u4e48\uff0c\u4ed6\u90fd\u4e0d\u80af\u8f7b\u6613\u5224\u65ad\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u518d\u6709\u9519\u8bef\uff0c\u5bf9\u4ed6\u8bf4\u6765\uff0c\u4e00\u6b21\u9519\u8bef\u5c31\u5df2\u592a\u591a \u4e86\u3002 \u3000\u3000\u4ed6\u5224\u65ad\u9519\u4e00\u6b21\uff0c\u4e0d\u4f46\u5bb3\u4e86\u4ed6\u81ea\u5df1\u4e00\u751f\uff0c\u4e5f\u5bb3\u4e86\u522b\u4eba\u4e00\u751f\u3002 \u3000\u3000\u8f7f\u5b50\u5df2\u5728\u8fd9\u5c0f\u697c\u524d\u505c\u4e0b\u6765\uff0c\u540e\u9762\u7684\u8f7f\u592b\u6b63\u5728\u64e6\u6c57\uff0c\u524d\u9762\u8f7f\u5b50\u90a3\u5c0f\u59d1\u5a18\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u8d70\u4e0a \u5c0f\u697c\u65c1\u7684\u68af\u5b50\uff0c\u6b63\u5728\u6572\u95e8\u3002 \u3000\u3000\u7b03\uff0c\u7b03\uff0c\u7b03\uff0c\u5979\u53ea\u6572\u4e86\u4e09\u58f0\uff0c\u95e8\u5c31\u5f00\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u9876\u8f7f\u5b50\u91cc\u76f4\u5230\u8fd9\u65f6\u624d\u8d70\u51fa\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u4e0d\u5230\u5979\u7684\u8138\uff0c\u53ea\u770b\u51fa\u5979\u7684\u8863\u670d\u548c\u5934\u53d1\u90fd\u5df2\u5f88\u51cc\u4e71\uff0c\u8eab\u6bb5\u5f88\u8bf1\u4eba\uff0c\u8d70\u8def\u7684\u59ff\u52bf\u66f4 \u8bf1\u4eba\u3002 \u3000\u3000\u8fd9\u79cd\u59ff\u6001\u674e\u5bfb\u6b22\u770b\u6765\u4e5f\u5f88\u719f\u6089\u3002 \u3000\u3000\u53ea\u89c1\u5979\u76c8\u76c8\u4e0a\u4e86\u5c0f\u697c\uff0c\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u5411\u521a\u8d70\u51fa\u8f7f\u5b50\u7684\u4e0a\u5b98\u98de\u62db\u4e86\u62db\u624b\uff0c\u624d\u95ea\u8eab\u5165\u4e86\u95e8 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u80fd\u770b\u5230\u5979\u534a\u8fb9\u8138\u3002 \u3000\u3000\u5979\u7684\u8138\u767d\u4e2d\u8235\u5de5\uff0c\u4eff\u4f5b\u8fd8\u5e26\u7740\u4e00\u62b9\u6625\u8272\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u674e\u5bfb\u6b22\u7ec8\u4e8e\u786e\u5b9a\u4e86\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u679c\u7136\u662f\u6797\u4ed9\u513f\uff01 \u3000\u3000\u6797\u4ed9\u513f\u5728\u8fd9\u91cc\uff0c\u963f\u98de\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u771f\u60f3\u51b2\u8fdb\u53bb\u95ee\u5979\uff0c\u5374\u53c8\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u662f\u4e2a\u5f88\u5947\u602a\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5e76\u4e0d\u662f\u541b\u5b50\uff0c\u4f46\u4ed6\u505a\u7684\u4e8b\u5374\u662f\u5927\u591a\u6570\u201c\u541b\u5b50\u201d\u4e0d\u4f1a\u505a\uff0c\u4e0d\u613f\u505a\uff0c\u4e5f\u6c38\u8fdc\u65e0\u6cd5\u505a\u5f97 \u5230\u7684\u3002 \u3000\u3000\u4ed6\u505a\u7684\u4e8b\u7b80\u76f4\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u505a\u5f97\u5230\uff0c\u56e0\u4e3a\u4e16\u4e0a\u53ea\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u674e\u5bfb\u6b22\uff0c\u4ee5\u524d\u56fa\u7136\u6ca1\u6709\uff0c \u4ee5\u540e\u6050\u6015\u4e86\u4e0d\u4f1a\u518d\u6709\u4e86\u3002 \u3000\u3000\u662f\u4ee5\u4e16\u4e0a\u867d\u6709\u4e9b\u4eba\u4e00\u5fc3\u53ea\u5e0c\u671b\u674e\u5bfb\u6b22\u5feb\u4e9b\u6b7b\uff0c\u4f46\u4e5f\u6709\u4e9b\u4eba\u60c5\u613f\u4e0d\u60dc\u727a\u7272\u4e00\u5207\uff0c\u8ba9\u4ed6\u6d3b\u4e0b \u53bb\u3002 \u3000\u3000\u591c\u6df1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u5728\u7b49\u7740\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5728\u7b49\u5f85\u7684\u65f6\u5019\uff0c\u603b\u4f1a\u60f3\u8d77\u8bb8\u591a\u4e8b\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u7b2c\u4e00\u6b21\u89c1\u5230\u963f\u98de\u7684\u65f6\u5019\u2014\u2014 \u3000\u3000\u90a3\u5929\u674e\u5bfb\u6b22\u5e76\u4e0d\u5bc2\u5bde\uff0c\u8fd8\u6709\u94c1\u4f20\u7532\u548c\u4ed6\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u53c8\u60f3\u4e86\u94c1\u4f20\u7532\uff0c\u60f3\u8d77\u4e86\u4ed6\u90a3\u5f20\u548c\u5584\u5fe0\u8bda\u7684\u8138\uff0c\u60f3\u8d77\u4e86\u4ed6\u90a3\u94c1\u9489\u822c\u7684\u80f4\u4f53\u2014\u2014 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u7684\u80f4\u4f53\u867d\u5982\u94a2\u94c1\u822c\u575a\u5f3a\uff0c\u4f46\u4e00\u9897\u5fc3\u5374\u662f\u90a3\u4e48\u8106\u5f31\uff0c\u90a3\u4e48\u5bb9\u6613\u88ab\u611f\u52a8\uff0c\u6240\u4ee5\u4ed6\u6d3b \u5728\u4e16\u4e0a\uff0c\u603b\u662f\u75db\u82e6\u591a\u4e8e\u6b22\u4e50\u3002 \u3000\u3000\u60f3\u7740\u60f3\u7740\uff0c\u674e\u5bfb\u6b22\u7a81\u7136\u53c8\u60f3\u559d\u9152\u4e86\u3002 \u3000\u3000\u4ed6\u53d6\u51fa\u9152\u74f6\uff0c\u5c06\u5269\u4e0b\u7684\u9152\u5168\u90e8\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53c8\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e0d\u80af\u4e3a\u81ea\u5df1\u8003\u8651\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5c0f\u697c\u7684\u95e8\u5f00\u4e86\u3002\u4e0a\u5b98\u98de\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u770b\u6765\u6bd4\u5e73\u65f6\u6109\u5feb\u591a\u4e86\uff0c\u53ea\u4e0d\u8fc7\u663e\u5f97\u6709 \u4e9b\u75b2\u5026\u3002 \u3000\u3000\u95e8\u91cc\u9762\u4f38\u51fa\u4e00\u53cc\u624b\uff0c\u62c9\u7740\u4ed6\u7684\u624b\u3002 \u3000\u3000\u665a\u98ce\u4e2d\u4f20\u6765\u4f4e\u4f4e\u7684\u7ec6\u8bed\uff0c\u4f3c\u5728\u73cd\u91cd\u518d\u89c1\uff0c\u518d\u4e09\u53ee\u5631\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u90a3\u53cc\u624b\u624d\u7f13\u7f13\u677e\u5f00\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u6162\uff0c\u4e0d\u4f4f\u56de\u987e\uff0c\u663e\u7136\u8fd8\u820d\u4e0d\u5f97\u8d70\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u5c0f\u697c\u4e0a\u7684\u95e8\u5df2\u5173\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u4ef0\u9996\u671b\u5929\uff0c\u811a\u6b65\u7a81\u7136\u52a0\u5feb\uff0c\u4f46\u795e\u60c5\u770b\u6765\u8fd8\u6709\u4e9b\u75f4\u8ff7\uff0c\u65f6\u800c\u5fae\u7b11\uff0c\u65f6\u800c\u53f9\u606f\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u4e5f\u88ab\u5e26\u5165\u4e86\u5730\u72f1\uff1f \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u706f\u5149\u5f88\u67d4\u548c\uff0c\u5c06\u7a97\u7eb8\u90fd\u6620\u6210\u7c89\u7ea2\u8272\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7ec8\u4e8e\u8d70\u4e86\uff0c\u674e\u5bfb\u6b22\u5ffd\u7136\u89c9\u5f97\u8fd9\u5c11\u5e74\u4e5f\u5f88\u53ef\u601c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5927\u6b65\u5411\u5c0f\u697c\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7b03\uff0c\u674e\u5bfb\u6b22\u5148\u6572\u4e86\u4e00\u58f0\u95e8\uff0c\u53c8\u7b03\u7b03\u63a5\u8fde\u6572\u4e86\u4e24\u58f0\uff0c\u4ed6\u65e9\u5df2\u53d1\u89c9\u90a3\u5c0f\u59d1\u5a18\u6572\u95e8\u7528\u7684\u6b63\u662f\u8fd9 \u79cd\u6cd5\u5b50\u3002 \u3000\u3000\u7b03\uff0c\u7b03\u7b03\uff0c\u6572\u4e86\u4e09\u58f0\u540e\uff0c\u95e8\u679c\u7136\u5f00\u4e86\u4e00\u7ebf\u3002 \u3000\u3000\u4e00\u4eba\u9053\uff1a\u4f60\u2014\u2014 \u3000\u3000\u5979\u53ea\u8bf4\u4e86\u4e00\u4e2a\u5b57\uff0c\u5c31\u770b\u6e05\u674e\u5bfb\u6b22\u4e86\uff0c\u7acb\u523b\u5c31\u60f3\u63a9\u95e8\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5df2\u63a8\u5f00\u95e8\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5f00\u95e8\u7684\u7adf\u4e0d\u662f\u6797\u4ed9\u513f\uff0c\u4e5f\u4e0d\u662f\u90a3\u7a7f\u7ea2\u8863\u670d\u7684\u5c0f\u59d1\u5a18\uff0c\u800c\u662f\u4e2a\u767d\u53d1\u82cd\u82cd\uff0c\u6ee1\u9762\u76b1\u7eb9\u7684\u8001\u592a \u5a46\u3002 \u3000\u3000\u5979\u5403\u60ca\u5730\u77a7\u7740\u674e\u5bfb\u6b22\uff0c\u98a4\u58f0\u9053\uff1a\u4f60\u2014\u2014\u662f\u8c01\uff1f\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u6765\u627e\u4e2a\u8001\u670b\u53cb\u3002 \u3000\u3000\u8001\u592a\u5a46\u8bf4\uff1a\u8001\u670b\u53cb\uff1f\u8c01\u662f\u4f60\u7684\u8001\u670b\u53cb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u5979\u770b\u5230\u6211\u65f6\uff0c\u4e00\u5b9a\u4f1a\u8ba4\u5f97\u7684\u3002 \u3000\u3000\u4ed6\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u4eba\u5df2\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8001\u592a\u5a46\u62e6\u4f4f\u4ed6\uff0c\u53c8\u4e0d\u6562\uff0c\u5927\u58f0\u9053\uff1a\u8fd9\u91cc\u6ca1\u6709\u4f60\u7684\u8001\u670b\u53cb\uff0c\u8fd9\u91cc\u53ea\u6709\u6211\u548c\u6211\u5b59\u5973\u4e24\u4eba\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u4e00\u5171\u9694\u51fa\u4e09\u95f4\u5c4b\u5b50\uff0c\u4e00\u95f4\u5ba2\u5c4b\uff0c\u4e00\u95f4\u996d\u5385\uff0c\u4e00\u95f4\u5367\u5ba4\uff0c\u5e03\u7f6e\u5f97\u81ea\u7136\u90fd\u5f88\u7cbe\u96c5\u3002 \u3000\u3000\u4f46\u4e09\u95f4\u5c4b\u5b50\u91cc\u90fd\u770b\u4e0d\u5230\u6797\u4ed9\u513f\u7684\u5f71\u5b50\u3002 \u3000\u3000\u90a3\u7a7f\u7ea2\u8863\u670d\u7684\u5c0f\u59d1\u5a18\u8c61\u662f\u5bb3\u6015\u5f97\u5f88\uff0c\u8138\u90fd\u5413\u767d\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u5976\u5976\uff0c\u8fd9\u4eba\u662f\u5f3a\u76d7\u4e48\uff1f \u3000\u3000\u8001\u592a\u5a46\u5413\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u89c9\u5f97\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\uff0c\u82e6\u7b11\u9053\uff1a\u4f60\u770b\u6211\u50cf\u4e0d\u50cf\u5f3a\u76d7\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u54ac\u7740\u5634\u5507\u9053\uff1a\u4f60\u82e5\u4e0d\u662f\u5f3a\u76d7\uff0c\u4e3a\u4ec0\u4e48\u4e09\u66f4\u534a\u591c\u95ef\u5230\u4eba\u5bb6\u91cc\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u662f\u6765\u627e\u6797\u59d1\u5a18\u7684\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u8c61\u662f\u89c9\u5f97\u4ed6\u5f88\u548c\u6c14\uff0c\u5df2\u4e0d\u592a\u5bb3\u6015\u4e86\uff0c\u7728\u7740\u773c\u9053\uff1a\u8fd9\u91cc\u6ca1\u6709\u6797\uff0c\u53ea\u6709\u4f4d\u5468\u59d1\u5a18\u3002 \u3000\u3000\u6797\u4ed9\u513f\u83ab\u975e\u7528\u4e86\u5316\u540d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u8ffd\u987a\uff1a\u5468\u59d1\u5a18\u5728\u54ea\u91cc\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u9053\uff1a\u6211\u59d3\u5468\uff0c\u5468\u59d1\u5a18\u5c31\u662f\u6211\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u7b80\u7766\u8c61\u662f\u4e2a\u5446\u5b50\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u4f3c\u4e4e\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u9053\uff1a\u4f46\u6211\u5374\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f60\u4e3a\u4f55\u6765\u627e\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u6211\u627e\u7684\u662f\u4f4d\u5927\u59d1\u5a18\uff0c\u4e0d\u662f\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u8fd9\u91cc\u6ca1\u6709\u5927\u59d1\u5a18\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u91cc\u521a\u521a\u6ca1\u6709\u4eba\u6765\u8fc7\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u6709\u4eba\u6765\u8fc7\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u95ee\u9053\uff1a\u8c01\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u6211\u548c\u6211\u5976\u5976\uff0c\u6211\u4eec\u521a\u4ece\u9547\u4e0a\u56de\u6765\u3002 \u3000\u3000\u5979\u773c\u73e0\u5b50\u8f6c\u52ab\uff0c\u53c8\u9053\uff1a\u8fd9\u91cc\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u5c0f\u7684\u662f\u6211\uff0c\u5927\u7684\u662f\u6211\u5976\u5976\uff0c\u4f46\u5979\u4e5f\u65e9\u5c31\u4e0d\u662f\u59d1 \u5a18\u4e86\uff0c\u4f60\u603b\u4e0d\u4f1a\u662f\u627e\u5979\u5427\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u5f88\u7b28\u7684\u65f6\u5019\uff0c\u603b\u662f\u4f1a\u53d1\u7b11\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u786e\u6ca1\u6709\u770b\u5230\u6709\u4eba\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u4e5f\u5374\u660e\u660e\u770b\u5230\u6797\u4ed9\u513f\u8d70\u8fdb\u6765\u3002 \u3000\u3000\u96be\u9053\u4ed6\u771f\u7684\u89c1\u7740\u9b3c\u4e86\u4e48\uff1f \u3000\u3000\u96be\u9053\u4ece\u8f7f\u5b50\u91cc\u8d70\u51fa\u6765\u7684\u90a3\u5973\u4eba\uff0c\u5c31\u662f\u8fd9\u8001\u592a\u5a46\uff1f \u3000\u3000\u8001\u592a\u5a46\u5ffd\u7136\u8dea\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u6211\u4eec\u7956\u5b59\u90fd\u662f\u53ef\u601c\u4eba\uff0c\u8fd9\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u503c\u94b1\u7684\u4e1c\u897f\uff0c\u5927\u7237\u4f60 \u65e0\u8bba\u770b\u4e0a\u4e86\u4ec0\u4e48\uff0c\u53ea\u7ba1\u62ff\u8d70\u5c31\u662f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u597d\u3002 \u3000\u3000\u996d\u5385\u7684\u684c\u4e0a\u6709\u74f6\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ff\u8d77\u4e86\u8fd9\u74f6\u9152\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5c0f\u59d1\u5a18\u5728\u540e\u9762\u5077\u5077\u5730\u7b11\u7740\u9053\uff1a\u539f\u6765\u8fd9\u4eba\u5e76\u4e0d\u662f\u5f3a\u76d7\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u9152\u9b3c\u800c\u5df2\u3002 \u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u963f\u98de>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e09\u5341\u4e5d\u7ae0\u3000\u963f\u98de \u3000\u3000\u6708\u4ecd\u672a\u7f3a\u3002 \u3000\u3000\u5c71\u6cc9\u5728\u6708\u5149\u4e0b\u770b\u6765\u5c31\u50cf\u662f\u6761\u95ea\u7740\u5149\u7684\u94f6\u5e26\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6cbf\u7740\u5c71\u6cc9\uff0c\u6162\u6162\u7684\u8d70\u7740\uff0c\u8d70\u5f97\u5e76\u4e0d\u6025\u3002\u4ed6\u4e0d\u613f\u5728\u5929\u8fd8\u672a\u4eae\u65f6\u5c31\u8d70\u5230\u963f\u98de\u4f4f\u7684\u5730\u65b9 \uff0c\u514d\u5f97\u60ca\u6270\u4ed6\u7684\u597d\u68a6\u3002 \u3000\u3000\u4ed6\u4ece\u4e0d\u613f\u6253\u6270\u522b\u4eba\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u4ec0\u4e48\u4eba\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u6765\u6253\u6270\u4ed6\uff0c\u90fd\u6ca1\u6709\u5173\u7cfb\u3002 \u3000\u3000\u90a3\u8001\u592a\u5a46\uff0c\u7edd\u4e0d\u662f\u6797\u4ed9\u513f\u6539\u626e\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u63c9\u4e86\u63c9\u81ea\u5df1\u7684\u773c\u775b\uff1a\u96be\u9053\u6211\u5df2\u8001\u773c\u660f\u82b1\uff1f \u3000\u3000\u5929\u7ec8\u4e8e\u4eae\u4e86\uff0c\u79cb\u5df2\u6b8b\uff0c\u6885\u82b1\u5df2\u6e10\u6e10\u5f00\u653e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u95fb\u5230\u4e00\u9635\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u62ac\u8d77\u5934\uff0c\u6885\u6797\u5df2\u5728\u671b\u3002 \u3000\u3000\u6885\u6797\u6df1\u5904\uff0c\u5df2\u9690\u7ea6\u53ef\u4ee5\u671b\u89c1\u6728\u5c4b\u4e00\u89d2\u3002 \u3000\u3000\u9762\u5bf9\u7740\u8fd9\u4e00\u7247\u6885\u6797\uff0c\u674e\u5bfb\u6b22\u4f3c\u4e4e\u53c8\u53d8\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u6885\u82b1\u65c1\uff0c\u5c31\u662f\u6cc9\u6c34\u7684\u5c3d\u5934\u3002 \u3000\u3000\u4e00\u7ebf\u98de\u6cc9\uff0c\u81ea\u534a\u5c71\u4e2d\u5012\u6302\u800c\u4e0b\uff0c\u886c\u7740\u8fd9\u7247\u6885\u82b1\uff0c\u66f4\u5b9b\u5982\u56fe\u753b\u3002 \u3000\u3000\u56fe\u753b\u4e2d\u7adf\u6709\u4e2a\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u770b\u4e0d\u5230\u8fd9\u4eba\u7684\u8138\uff0c\u53ea\u770b\u51fa\u4ed6\u7a7f\u7740\u5957\u5f88\u5e72\u51c0\uff0c\u5f88\u65b0\u7684\u9752\u5e03\u886b\u88e4\uff0c\u5934\u53d1\u4e5f\u68b3\u7406\u5f97\u5f88 \u5149\u5f88\u4eae\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u63d0\u7740\u6c34\u6876\uff0c\u7a7f\u8fc7\u6885\u6797\uff0c\u8d70\u5165\u6728\u5c4b\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u8eab\u6750\u867d\u7136\u548c\u963f\u98de\u5dee\u4e0d\u591a\uff0c\u4f46\u674e\u5bfb\u6b22\u5374\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u662f\u963f\u98de\u3002 \u3000\u3000\u90a3\u4e48\u8fd9\u4eba\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u60f3\u4e0d\u51fa\u6709\u8c01\u4f1a\u548c\u963f\u98de\u4f4f\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u8d76\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6728\u5c4b\u7684\u95e8\uff0c\u662f\u5f00\u7740\u7684\uff0c\u5c4b\u5b50\u91cc\u867d\u6ca1\u6709\u4ec0\u4e48\u534e\u4e3d\u7684\u9648\u8bbe\uff0c\u4f46\u5374\u6536\u62fe\u5f97\u7a97\u660e\u5e72\u51c0\uff0c\u4e00\u5c18\u4e0d\u67d3 \u3002 \u3000\u3000\u684c\u5b50\u7684\u89d2\u843d\u91cc\uff0c\u6709\u5f20\u516b\u4ed9\u684c\uff0c\u90a3\u7a7f\u65b0\u8863\u7684\u5c11\u5e74\u6b63\u4ece\u6c34\u6876\u91cc\u62e7\u51fa\u4e00\u5757\u62b9\u5e03\uff0c\u5f00\u59cb\u62b9\u684c\u5b50\u3002 \u3000\u3000\u4ed6\u62b9\u5f97\u6bd4\u5b59\u9a7c\u5b50\u8fd8\u6162\uff0c\u8fd8\u4ed4\u7ec6\uff0c\u770b\u6765\u597d\u50cf\u8fd9\u684c\u5b50\u4e0a\u53ea\u8981\u6709\u4e00\u70b9\u7070\u5c18\u7559\u4e0b\u6765\uff0c\u4ed6\u5c31\u89c1\u4e0d\u5f97 \u4eba\u4f3c\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ece\u80cc\u540e\u8d70\u8fc7\u53bb\uff0c\u89c9\u5f97\u4ed6\u80cc\u5f71\u5b9e\u5728\u5f88\u50cf\u963f\u98de\u3002 \u3000\u3000\u4f46\u4ed6\u7edd\u4e0d\u4f1a\u662f\u963f\u98de\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b80\u76f4\u65e0\u6cd5\u60f3\u50cf\u963f\u98de\u62b9\u684c\u5b50\u7684\u6a21\u6837\uff0c\u4f46\u8fd9\u4eba\u65e2\u4e5f\u4f4f\u5728\u8fd9\u91cc\uff0c\u81ea\u7136\u4e00\u5b9a\u662f\u8ba4\u5f97\u963f\u98de\u7684 \u3002 \u3000\u3000\u4ed6\u81f3\u5c11\u5e94\u8be5\u77e5\u9053\u963f\u98de\u5728\u54ea\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u5e0c\u671b\u8fd9\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u4ed6\u624d\u597d\u5411\u4ed6\u6253\u542c\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u53cd\u5e94\u5e76\u4e0d\u5feb\uff0c\u4f46\u603b\u7b97\u8fd8\u662f\u6162\u6162\u7684\u56de\u8fc7\u5934\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u8ba4\u4e3a\u7edd\u4e0d\u4f1a\u662f\u963f\u98de\u7684\u4eba\uff0c\u8d6b\u7136\u5c31\u662f\u963f\u98de\u3002 \u3000\u3000\u963f\u98de\u7684\u5bb9\u8c8c\u5f53\u7136\u5e76\u6ca1\u6709\u53d8\uff0c\u4ed6\u7684\u773c\u775b\u8fd8\u662f\u5f88\u5927\uff0c\u9f3b\u5b50\u8fd8\u662f\u5f88\u633a\uff0c\u770b\u6765\u8fd8\u662f\u5f88\u82f1\u4fca\uff0c\u6bd4\u4ee5 \u524d\u66f4\u82f1\u4fca\u4e86\u4e9b\u3002 \u3000\u3000\u86e4\u4ed6\u7684\u795e\u60c5\u5374\u5df2\u53d8\u4e86\uff0c\u53d8\u5f97\u5f88\u591a\u3002 \u3000\u3000\u4ed6\u773c\u775b\u91cc\u5df2\u5931\u53bb\u4e86\u6614\u65e5\u90a3\u79cd\u6444\u4eba\u7684\u9b54\u529b\uff0c\u9762\u4e0a\u90a3\u79cd\u575a\u5f3a\uff0c\u5b64\u50b2\u7684\u795e\u60c5\u4e5f\u6ca1\u6709\u4e86\uff0c\u7adf\u53d8\u5f97 \u5f88\u5e73\u548c\uff0c\u751a\u81f3\u6709\u4e9b\u5446\u677f\u3002 \u3000\u3000\u4ed6\u770b\u6765\u4e5f\u8bb8\u6bd4\u4ee5\u524d\u597d\u770b\u591a\u4e86\uff0c\u5e72\u51c0\u591a\u4e86\uff0c\u4f46\u4ee5\u524d\u4ed6\u90a3\u79cd\u5484\u5484\u903c\u4eba\u7684\u795e\u91c7\uff0c\u90a3\u79cd\u4ee4\u4eba\u7729\u76ee \u7684\u5149\u8292\uff0c\u5982\u4eca\u5374\u5df2\u4e0d\u590d\u518d\u89c1\u3002 \u3000\u3000\u8fd9\u771f\u7684\u5c31\u662f\u963f\u98de\uff1f \u3000\u3000\u8fd9\u771f\u7684\u5c31\u662f\u6614\u65e5\u7684\u90a3\u5b64\u72ec\u5730\u8d70\u5728\u51b0\u96ea\u4e2d\uff0c\u6b7b\u4e5f\u4e0d\u80af\u63a5\u53d7\u522b\u4eba\u7684\u5c11\u5e74\uff1f\u771f\u7684\u5c31\u662f\u90a3\u5feb\u5251\u5982 \u98ce\uff0c\u8db3\u4ee5\u4ee4\u5929\u4e0b\u7fa4\u96c4\u80c6\u5bd2\u7684\u5c11\u5e74\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b80\u76f4\u65e0\u6cd5\u60f3\u8c61\uff0c\u73b0\u5728\u8fd9\u8eab\u4e0a\u7a7f\u7740\u65b0\u8863\u670d\uff0c\u624b\u91cc\u62ff\u7740\u5757\u62b9\u5e03\u7684\u4eba\uff0c\u5c31\u662f\u4ee5\u524d\u4ed6\u6240\u8ba4 \u8bc6\u7684\u963f\u98de\uff01 \u3000\u3000\u963f\u98de\u81ea\u7136\u4e5f\u770b\u5230\u4e86\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u4ed6\u5148\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u8868\u60c5\u6709\u4e9b\u53d1\u6014\uff0c\u7136\u540e\u8138\u4e0a\u624d\u7ec8\u4e8e\u6e10\u6e10\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u2014\u2014\u8c22\u5929\u8c22\u5730\uff0c\u4ed6 \u7b11\u5f97\u603b\u7b97\u8fd8\u548c\u4ee5\u524d\u540c\u6837\u52a8\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u9762\u4e0a\u867d\u7136\u5728\u7b11\uff0c\u5fc3\u5934\u5374\u6709\u4e9b\u53d1\u82e6\u3002 \u3000\u3000\u4e24\u4eba\u5c31\u8fd9\u6837\u9762\u5bf9\u9762\u7684\u77a7\u7740\uff0c\u9762\u5bf9\u9762\u7684\u7b11\u7740\u3002\u8c01\u4e5f\u6ca1\u6709\u79fb\u52a8\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ef\u662f\u4e24\u4eba\u7684 \u773c\u775b\u5374\u5df2\u6e10\u6e10\u6e7f\u6da6\uff0c\u6e10\u6e10\u53d1\u7ea2\u2014\u2014\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u963f\u98de\u624d\u7f13\u7f13\u9053\uff1a\u662f\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u6211\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u4f60\u6bd5\u7adf\u8fd8\u662f\u6765\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u6bd5\u7adf\u6765\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u8bf4\u8bdd\u90fd\u5f88\u6162\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u8bed\u58f0\u5df2\u6709\u4e9b\u54fd\u54bd\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e24\u4eba\u7a81\u53c8\u95ed\u4e0a\u5634\uff0c\u50cf\u662f\u65e0\u8bdd \u53ef\u8bf4\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u963f\u98de\u7a81\u7136\u4ece\u5c4b\u5b50\u91cc\u51b2\u4e86\u51fa\u6765\uff0c\u674e\u5bfb\u6b22\u4e5f\u7a81\u7136\u4ece\u5916\u9762\u51b2\u4e86\u8fdb\u53bb\uff0c\u4e24\u4eba\u5728\u95e8\u53e3 \u51e0\u4e4e\u649e\u5012\u4e00\u8d77\uff0c\u4e92\u76f8\u7d27\u7d27\u63e1\u4f4f\u4e86\u624b\u3002 \u3000\u3000\u4e24\u4eba\u7684\u547c\u5438\u90fd\u4f3c\u5df2\u505c\u987f\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u674e\u5bfb\u6b22\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\u6765\uff0c\u9053\uff1a\u8fd9\u4e24\u5e74\u6765\uff0c\u4f60\u8fc7\u5f97 \u4e0d\u5b84\u4e48\uff1f \u3000\u3000\u963f\u98de\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u6211\u2014\u2014\u6211\u5f88\u597d\uff0c\u4f60\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\uff1f\u6211\u8fd8\u662f\u8001\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u4e3e\u8d77\u4e86\u53e6\u4e00\u53cc\u624b\u4e0a\u7684\u9152\u74f6\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u4f60\u770b\uff0c\u6211\u8fd8\u662f\u6709\u9152\u559d\uff0c\u8fde\u6211\u90a3\u54b3\u55fd\u7684\u6bdb\u75c5\uff0c\u8fd9 \u4e24\u5e74\u90fd\u597d\u50cf\u5df2\u7ecf\u88ab\u9152\u51b2\u8d70\u4e86\uff0c\u4f60\u2014\u2014 \u3000\u3000\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u53c8\u54b3\u55fd\u8d77\u6765\uff0c\u54b3\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u963f\u98de\u9759\u9759\u7684\u671b\u7740\u4ed6\uff0c\u4f3c\u5df2\u6709\u6cea\u5c06\u843d\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u4f60\u770b\u4f60\uff0c\u674e\u5927\u54e5\u6765\u4e86\uff0c\u4f60\u4e5f\u4e0d\u8bf7\u4eba\u5bb6\u5230\u5c4b\u91cc\u5750\uff0c\u5730\u50cf\u4e2a\u5446\u5b50\u822c\u7ad9\u5728\u95e8\u53e3\uff0c \u4e5f\u4e0d\u6015\u4eba\u5bb6\u770b\u4e86\u7b11\u8bdd\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7ec8\u4e8e\u9732\u9762\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u8fd8\u662f\u4e00\u70b9\u4e5f\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u5979\u8fd8\u662f\u90a3\u4e48\u5e74\u8f7b\uff0c\u90a3\u4e48\u7f8e\u4e3d\uff0c\u7b11\u8d77\u6765\u4e5f\u8fd8\u662f\u90a3\u4e48\u662f\u6717\uff0c\u90a3\u4e48\u53ef\u7231\uff0c\u5979\u7684\u773c\u775b\u8fd8\u662f\u53d1\u7740\u5149 \uff0c\u4eae\u5f97\u5c31\u50cf\u662f\u5929\u4e0a\u7684\u660e\u661f\u3002 \u3000\u3000\u5979\u5c31\u7ad9\u5728\u90a3\u91cc\uff0c\u6e29\u67d4\u5730\u77a7\u7740\u674e\u5bfb\u6b22\uff0c\u67d4\u58f0\u9053\uff1a\u5feb\u4e24\u5e74\u4e86\uff0c\u674e\u5927\u54e5\u4e5f\u4e0d\u6765\u770b\u770b\u6211\u4eec\uff0c\u96be\u9053 \u5df2\u7ecf\u5c06\u6211\u4eec\u5fd8\u4e86\u5417\uff1f \u3000\u3000\u65e0\u8bba\u8c01\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u90fd\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u674e\u5bfb\u6b22\u65e9\u5df2\u77e5\u9053\u4ed6\u4eec\u4f4f\u7684\u5730\u65b9\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u6765\u63a2\u671b\u4ed6 \u4eec\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u4f60\u53c8\u6ca1\u6709\u7528\u8f7f\u5b50\u6765\u63a5\u6211\uff0c\u6211\u600e\u4e48\u6765\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7728\u4e86\u7728\u773c\u775b\uff0c\u7b11\u9053\uff1a\u8bf4\u8d77\u8f7f\u5b50\uff0c\u6211\u5012\u4e5f\u771f\u60f3\u5750\u4e00\u6b21\uff0c\u770b\u770b\u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\u9053\uff1a\u4f60\u6ca1\u6709\u5750\u8fc7\u8f7f\u5b50\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u4e86\u5934\uff0c\u5e7d\u5e7d\u9053\uff1a\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c\u54ea\u6709\u5750\u8f7f\u5b50\u7684\u798f\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f46\u6628\u591c\u9547\u4e0a\uff0c\u6211\u770b\u5230\u6709\u4e2a\u4eba\u5750\u8f7f\u7ecf\u8fc7\uff0c\u90a3\u4eba\u771f\u50cf\u4f60\u3002 \u3000\u3000\u4ed6\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u7684\u76ef\u7740\u6797\u4ed9\u513f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9762\u4e0a\u5374\u8fde\u4e00\u70b9\u60ca\u614c\u7684\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u53cd\u800c\u7b11\uff1a\u90a3\u4e00\u5b9a\u662f\u6211\u5728\u68a6\u4e2d\u8d70\u51fa\u53bb\u7684\u2014\u2014\u4f60\u8bf4 \u662f\u5417\uff1f \u3000\u3000\u540e\u9762\u4e00\u53e5\u8bdd\uff0c\u5979\u662f\u5bf9\u963f\u98de\u8bf4\u7684\u3002 \u3000\u3000\u963f\u98de\u7acb\u523b\u9053\uff1a\u6bcf\u5929\u665a\u4e0a\u5979\u90fd\u7761\u7740\u5f88\u65e9\uff0c\u4ece\u6765\u6ca1\u6709\u51fa\u53bb\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u53c8\u6253\u4e86\u4e2a\u7ed3\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u963f\u98de\u7edd\u4e0d\u4f1a\u5728\u4ed6\u9762\u524d\u8bf4\u8c0e\u7684\uff0c\u4f46\u6797\u4ed9\u513f\u82e5\u4e00\u76f4\u6ca1\u6709\u51fa\u53bb\uff0c\u6628\u5929\u665a\u4e0a\u4ece\u8f7f\u5b50\u51fa\u6765\u7684 \u90a3\u5973\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5df2\u9760\u8fd1\u963f\u98de\u8eab\u65c1\uff0c\u5c06\u963f\u98de\u672c\u6765\u5df2\u5f88\u633a\u7684\u8863\u670d\u53c8\u626f\u5e73\u4e86\u4e9b\uff0c\u76ee\u4e2d\u5e26\u7740\u65e0\u9650\u6e29\u67d4\uff0c\u8f7b \u8f7b\u9053\uff1a\u6628\u5929\u665a\u4e0a\u4f60\u7761\u8fd8\u597d\u4e48\uff1f \u3000\u3000\u963f\u98de\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u90a3\u4e48\u4f60\u5c31\u966a\u674e\u5927\u54e5\u5230\u5916\u9762\u8d70\u8d70\uff0c\u6211\u5230\u53a8\u623f\u53bb\u505a\u51e0\u6837\u83dc\uff0c\u66ff\u5927\u54e5\u63a5\u98ce\u3002 \u3000\u3000\u5979\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u5ae3\u7136\u9053\uff1a\u5916\u9762\u7684\u6885\u82b1\u5df2\u5feb\u5f00\u4e86\uff0c\u6211\u77e5\u9053\u674e\u5927\u54e5\u6700\u559c\u6b22\u6885\u82b1\u2014\u2014\u662f\u5417 \uff1f \u3000\u3000\u963f\u98de\u8d70\u8def\u7684\u59ff\u52bf\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u4ee5\u524d\u8d70\u8def\u65f6\u8eab\u5b50\u867d\u7136\u6c38\u8fdc\u633a\u5f97\u7b14\u76f4\uff0c\u6bcf\u4e00\u6b65\u8fc8\u51fa\u53bb\uff0c\u867d\u7136\u90fd\u6709\u4e00\u5b9a\u7684\u8ddd\u79bb\uff0c\u4f46\u4ed6\u7684\u808c \u8089\u5730\u662f\u5b8c\u5168\u653e\u677e\u7684\u3002 \u3000\u3000\u522b\u4eba\u8d70\u8def\u662f\u52b3\u52a8\uff0c\u800c\u4ed6\uff0c\u5374\u662f\u4f11\u606f\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u8d70\u8def\u65f6\u8eab\u5b50\u5df2\u6ca1\u6709\u4ee5\u524d\u90a3\u4e48\u633a\u4e86\uff0c\u4eff\u4f5b\u6709\u4e9b\u795e\u4e0d\u601d\u5c5e\uff0c\u5fc3\u4e0d\u5728\xd7\uff0c\u5374\u53c8\u663e\u5f97\u6709\u4e9b \u7d27\u5f20\u3002 \u3000\u3000\u4ed6\u663e\u7136\u5df2\u4e0d\u80fd\u5b8c\u5168\u653e\u677e\u81ea\u5df1\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u4e86\u5f88\u957f\u7684\u4e00\u6bb5\uff0c\u674e\u5bfb\u6b22\u8fd8\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u95ee\u963f\u98de\uff0c\u4e3a\u4ec0\u4e48\u8981\u8eb2\u5230\u8fd9\u91cc\u6765\uff1f\u6797\u4ed9\u513f\u662f\u5426\u5df2\u627f\u8ba4\u4e86\u81ea\u5df1\u7684\u7f6a\u884c\uff1f\u5979\u52ab\u6765\u7684\u8d22\u5bcc \u662f\u5426\u5df2\u8fd8\u7ed9\u4e86\u5931\u4e3b\uff1f \u3000\u3000\u4f46\u4ed6\u90fd\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u610f\u89e6\u53ca\u963f\u98de\u7684\u9690\u75db\u3002 \u3000\u3000\u963f\u98de\u4e5f\u6c89\u9ed8\uff0c\u8d70\u4e86\u5f88\u957f\u7684\u4e00\u6bb5\u8def\uff0c\u5ffd\u7136\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u4e3a\u4e86\u6551\u6211\uff0c\u4e0d\u60dc\u81ea\u8ba4\u4e3a\u6885\u82b1\u76d7\uff0c\u751a\u81f3\u8fde\u81ea\u5df1\u7684\u6027\u547d\u90fd\u4e0d\u8981\u4e86 \uff0c\u8fd9\u6837\u82e5\u4e5f\u7b97\u5bf9\u4e0d\u8d77\u6211\uff0c\u6211\u5012\u771f\u5e0c\u671b\u5929\u4e0b\u4eba\u90fd\u5bf9\u4e0d\u8d77\u6211\u4e86\u3002 \u3000\u3000\u963f\u98de\u4f3c\u4e4e\u5168\u6ca1\u6709\u542c\u4ed6\u8bf4\u8bdd\uff0c\u63a5\u8bf4\u9053\uff1a\u6211\u8d70\u7684\u65f6\u5019\uff0c\u81f3\u5c11\u5e94\u8be5\u544a\u8bc9\u4f60\u4e00\u58f0\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u6709\u4f60\u7684\u82e6\u8877\uff0c\u6211\u4e0d\u602a\u4f60\u3002 \u3000\u3000\u963f\u98de\u9eef\u7136\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u6211\u4e0d\u8be5\u8fd9\u4e48\u505a\uff0c\u53ef\u662f\u6211\u65e0\u8bba\u5982\u4f55\u4e5f\u65e0\u6cd5\u5bf9\u5979\u4e0b\u624b\uff0c\u6211\u2014\u2014\u6211\u5b9e\u5728 \u5df2\u79bb\u4e0d\u5f00\u5979\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4e00\u4e2a\u7537\u4eba\u7231\u4e0a\u4e86\u4e00\u4e2a\u5973\u4eba\uff0c\u672c\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u9519\uff0c\u4f60\u4e3a\u4ec0\u4e48 \u504f\u8981\u8d23\u602a\u81ea\u5df1\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u2014\u2014 \u3000\u3000\u4ed6\u795e\u60c5\u7a81\u7136\u6fc0\u52a8\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u53ef\u662f\u6211\u5374\u5bf9\u4e0d\u8d77\u4f60\uff0c\u4e5f\u5bf9\u4e0d\u8d77\u90a3\u4e9b\u53d7\u4e86\u6885\u82b1\u76d7\u4e4b\u5bb3\u7684\u4eba \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u8bd5\u63a2\u95ee\u9053\uff1a\u4f46\u5979\u6539\u8fc7\u4e86\uff0c\u662f\u5417\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u4eec\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u5979\u5df2\u5c06\u6240\u6709\u52ab\u6765\u7684\u8d22\u7269\u90fd\u8fd8\u7ed9\u4e86\u522b\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u65e2\u7136\u5982\u6b64\uff0c\u8fd8\u96be\u53d7\u4ec0\u4e48\uff1f\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff0c\u8fd9\u53e5\u8bdd\u4f60\u4e0d\u61c2\uff1f \u3000\u3000\u4ed6\u4e0d\u613f\u963f\u98de\u518d\u60f3\u8fd9\u4ef6\u4e8b\uff0c\u5ffd\u7136\u62ac\u5934\u7b11\u9053\uff1a\u4f60\u770b\uff0c\u8fd9\u68f5\u6811\u4e0a\u7684\u6885\u82b1\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u55ef\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u5df2\u5f00\u4e86\u591a\u5c11\u6735\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u5341\u4e03\u6735\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u6c89\u843d\u4e86\u4e0b\u53bb\uff0c\u7b11\u5bb9\u4e5f\u51bb\u7ed3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6570\u8fc7\u6885\u82b1\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u4e00\u4e2a\u4eba\u5728\u6570\u6885\u82b1\u65f6\uff0c\u90a3\u662f\u591a\u4e48\u5bc2\u5bde\u3002 \u3000\u3000\u963f\u98de\u4e5f\u62ac\u8d77\u5934\uff0c\u9053\uff1a\u770b\u6765\u53c8\u6709\u4e00\u6735\u8981\u5f00\u4e86\uff0c\u4e3a\u4f55\u5b83\u4eec\u8981\u5f00\u5f97\u8fd9\u4e48\u65e9\u5462\uff1f\u5f00\u5f97\u65e9\u7684\u82b1\u6735\uff0c \u843d\u5f97\u5c82\u975e\u4e5f\u65e9\u4e9b\u2014\u2014 \u3000\u3000\u6728\u5c4b\u4e00\u5171\u6709\u4e94\u95f4\uff0c\u4e00\u95f4\u5ba2\u5385\uff0c\u4e00\u95f4\u8d2e\u7269\uff0c\u540e\u9762\u7684\u662f\u53a8\u5395\uff0c\u5269\u4e0b\u7684\u4e24\u95f4\u5c4b\u5b50\u91cc\uff0c\u90fd\u6446\u7740\u5e8a \u3002 \u3000\u3000\u8f83\u5927\u7684\u4e00\u95f4\u9648\u8bbe\u7cbe\u81f4\uff0c\u8fd8\u6709\u5986\u53f0\u3002\u963f\u98de\u9053\uff1a\u4ed9\u513f\u5c31\u7761\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u8f83\u5c0f\u7684\u4e00\u95f4\u4e5f\u6536\u62fe\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4e00\u5c18\u4e0d\u67d3\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u8fd9\u662f\u6211\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u77e5\u9053\u963f\u98de\u548c\u6797\u4ed9\u513f\u539f\u6765\u4e00\u76f4\u8fd8\u662f\u5206\u5f00\u6765\u7761\u7684\u3002\u4e24\u4eba\u5728\u8fd9\u91cc\u5171\u540c\u751f\u6d3b\u4e86\u4e24\u5e74\uff0c\u800c\u963f \u98de\u53c8\u662f\u8840\u6c14\u65b9\u521a\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u4e5f\u5f88\u4f69\u670d\u3002 \u3000\u3000\u963f\u98de\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u7b11\uff0c\u9053\uff1a\u4f60\u82e5\u77e5\u9053\u8fd9\u4e24\u5e74\u6765\u6211\u7761\u5f97\u591a\u65e9\uff0c\u4e00\u5b9a\u4f1a\u5947\u602a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u5929\u4e00\u9ed1\u6211\u5c31\u7761\u4e86\uff0c\u4e00\u6cbe\u6795\u5934\u5c31\u7761\u7740\uff0c\u800c\u4e14\u4e00\u89c9\u7761\u5230\u5929\u4eae\uff0c\u4ece\u4e0d\u4f1a\u9192\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u751f\u6d3b\u6709\u4e86\u89c4\u5f8b\uff0c\u7761\u5f97\u81ea\u7136\u597d\u3002 \u7b2c\u56db\u5341\u7ae0\u3000\u5978\u60c5>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u7ae0\u3000\u5978\u60c5 \u3000\u3000\u963f\u98de\u9053\uff1a\u8fd9\u4e24\u5e74\u6765\uff0c\u6211\u65e5\u5b50\u7684\u786e\u8fc7\u5f97\u5f88\u5e73\u9759\u2014\u2014\u6211\u4e00\u751f\u4e2d\u4ece\u672a\u6709\u8fc7\u5982\u6b64\u5b89\u5b9a\u5e73\u9759\u7684\u65e5\u5b50 \uff0c\u5979\u2014\u2014\u5979\u4e5f\u7684\u786e\u5bf9\u6211\u5f88\u597d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u542c\u5230\u4f60\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u6211\u4e5f\u5f88\u9ad8\u5174\uff0c\u592a\u9ad8\u5174\u4e86\u2014\u2014 \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u613f\u88ab\u963f\u98de\u770b\u51fa\u4ed6\u7b11\u5f97\u6709\u4e9b\u4e0d\u81ea\u7136\uff0c\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u5934\u5df2\u8f6c\u4e86\u8fc7\u53bb\uff0c\u56db\u9762\u89c2\u671b\u7740\uff0c \u7a81\u7136\u53c8\u9053\uff1a\u4f60\u7684\u5251\u5462\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u5df2\u4e0d\u7528\u5251\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u624d\u771f\u7684\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u4f60\u4e0d\u7528\u5251\u4e86\uff1f\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u5251\u662f\u51f6\u5668\uff0c\u800c\u4e14\u603b\u4f1a\u8ba9\u6211\u60f3\u8d77\u90a3\u8fc7\u53bb\u7684\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u662f\u4e0d\u662f\u5979\u529d\u4f60\u7684\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u5979\u81ea\u5df1\u4e5f\u653e\u5f03\u4e86\u4e00\u5207\uff0c\u6211\u4eec\u90fd\u60f3\u5fd8\u8bb0\u8fc7\u53bb\uff0c\u4ece\u5934\u505a\u8d77\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u5934\uff0c\u9053\uff1a\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u5f88\u597d\u2014\u2014 \u3000\u3000\u4ed6\u672c\u6765\u50cf\u662f\u8fd8\u6709\u8bdd\u8981\u8bf4\u7684\uff0c\u4f46\u8fd9\u65f6\u6797\u4ed9\u513f\u7684\u547c\u58f0\u5df2\u54cd\u8d77\uff0c\u83dc\u5df2\u6446\u4e0a\u684c\u4e86\uff0c\u8001\u7237\u4eec\u8fd8\u4e0d\u60f3 \u56de\u6765\u4e48\uff1f \u3000\u3000\u83dc\u4e0d\u591a\uff0c\u5374\u5f88\u7cbe\u81f4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u83dc\u5c45\u7136\u70e7\u5f97\u8fd9\u597d\uff0c\u5012\u4e5f\u662f\u4ef6\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u9664\u4e86\u83dc\u4e4b\u5916\uff0c\u684c\u4e0a\u5f53\u7136\u8fd8\u6709\u9152\uff0c\u4f46\u9152\u676f\u91cc\u88c5\u7684\u5374\u662f\u8336\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5c71\u5c45\u7b80\u964b\uff0c\u4ed3\u4fc3\u95f4\u65e0\u9152\u4e3a\u656c\uff0c\u53ea\u597d\u4ee5\u8336\u4f5c\u9152\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u5e78\u597d\u6211\u8fd8\u5e26\u4e86\u534a\u74f6\u9152\u6765\u2014\u2014 \u3000\u3000\u4ed6\u76ee\u5149\u56db\u8f6c\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u65b9\u624d\u6446\u5728\u6905\u5b50\u89d2\u843d\u91cc\u7684\u90a3\u9152\u74f6\uff0c\u5148\u5c06\u81ea\u5df1\u676f\u4e2d\u7684\u8336\u4e00\u996e\u800c\u5c3d\uff0c \u5411\u963f\u98de\u9053\uff1a\u6765\uff0c\u4f60\u4e5f\u5feb\u628a\u8336\u559d\u5b8c\uff0c\u6211\u66ff\u4f60\u5012\u9152\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u6211\u620e\u9152\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u4f60\u620e\u9152\u4e86\uff1f\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u9152\u559d\u591a\u4e86\uff0c\u5bf9\u8eab\u4f53\u603b\u4e0d\u592a\u597d\u7684\uff0c\u674e\u5927\u54e5\uff0c\u4f60\u8bf4\u662f\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7b11\u4e86\uff1a\u4e0d\u9519\uff0c\u9152\u559d\u591a\u4e86\uff0c\u5c31\u4f1a\u53d8\u5f97\u50cf\u6211\u8fd9\u6837\u5b50\uff0c\u6211\u82e5\u80fd\u5012\u9000\u5341\u51e0\u4e8c \u5341\u5e74\uff0c\u6211\u4e00\u5b9a\u4e5f\u8981\u620e\u9152\u7684\u3002 \u3000\u3000\u963f\u98de\u4f4e\u4e0b\u5934\uff0c\u5f00\u59cb\u5403\u996d\u3002 \u3000\u3000\u4ed6\u770b\u6765\u53c8\u6709\u4e9b\u5fc3\u4e0d\u5728\xd7\uff0c\u521a\u5939\u8d77\u4e2a\u8089\u4e38\uff0c\u5c31\u6389\u5728\u684c\u4e0a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u4f60\u770b\u4f60\uff0c\u5403\u996d\u5c31\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\uff0c\u8fd9\u4e48\u4e0d\u5c0f\u5fc3\u3002 \u3000\u3000\u963f\u98de\u9ed8\u9ed8\u7684\uff0c\u53c8\u5c06\u6389\u5728\u684c\u4e0a\u7684\u8089\u4e38\u5939\u8d77\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u770b\u4f60\uff0c\u8089\u4e38\u6389\u5728\u684c\u4e0a\uff0c\u600e\u4e48\u8fd8\u80fd\u5403\u5462\uff1f \u3000\u3000\u5979\u81ea\u5df1\u5939\u8d77\u4e2a\u8089\u4e38\uff0c\u9001\u5230\u963f\u98de\u5634\u91cc\u3002 \u3000\u3000\u665a\u996d\u7684\u83dc\u6bd4\u5348\u996d\u66f4\u597d\uff0c\u7136\u540e\uff0c\u5929\u5c31\u9ed1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7761\u5728\u963f\u98de\u7684\u5e8a\u4e0a\uff0c\u963f\u98de\u7761\u5728\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4eb2\u81ea\u4e3a\u4ed6\u4eec\u6362\u4e0a\u4e86\u5e72\u51c0\u7684\u88ab\u5355\uff0c\u94fa\u597d\u5e8a\uff0c\u53c8\u5c06\u4e00\u5957\u5e72\u51c0\u7684\u8863\u670d\u653e\u5728\u963f\u98de\u7684\u5e8a\u5934\u3002 \u3000\u3000\u6211\u559c\u6b22\u5c0f\u98de\u6bcf\u5929\u6362\u8863\u670d\u3002 \u3000\u3000\u4e34\u7761\u524d\uff0c\u5979\u6253\u4e86\u76c6\u6c34\uff0c\u770b\u7740\u963f\u98de\u6d17\u624b\u6d17\u8138\uff0c\u7b49\u963f\u98de\u6d17\u597d\u4e86\uff0c\u5979\u53c8\u5c06\u624b\u5dfe\u62ff\u8fc7\u6765\uff0c\u66ff\u963f\u98de \u64e6\u8033\u6735\u3002 \u3000\u3000\u963f\u98de\u7761\u4e0b\u53bb\uff0c\u5979\u5c31\u66ff\u4ed6\u76d6\u597d\u88ab\u3002 \u3000\u3000\u8fd9\u91cc\u6bd4\u8f83\u51b7\uff0c\u5c0f\u5fc3\u665a\u4e0a\u7740\u4e86\u51c9\u3002 \u3000\u3000\u5979\u5bf9\u963f\u98de\u670d\u4f8d\u5f97\u5b9e\u5728\u662f\u65e0\u5fae\u4e0d\u81f3\uff0c\u5c31\u7b97\u662f\u4e00\u4e2a\u6700\u7ec6\u5fc3\u7684\u6bcd\u4eb2\uff0c\u5bf9\u5979\u81ea\u5df1\u7684\u5b69\u5b50\u4e5f\u672a\u5fc5\u6709 \u5982\u6b64\u4f53\u8d34\u3002 \u3000\u3000\u963f\u98de\u5e94\u8be5\u7b97\u662f\u5e78\u798f\u6781\u4e86\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u674e\u5bfb\u6b22\u5374\u6709\u70b9\u4e0d\u660e\u767d\uff0c\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u963f\u98de\u8fd9\u79cd\u751f\u6d3b\u662f\u5e78\u798f\uff1f\u8fd8\u662f\u75db\u82e6 \uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u77e5\u662f\u89c9\u5f97\u53ef\u7b11\uff0c\u8fd8\u662f\u5f88\u53ef\u60b2\u3002 \u3000\u3000\u5916\u9762\u9f3b\u606f\u6c89\u6c89\uff0c\u963f\u98de\u679c\u7136\u4e00\u6cbe\u6795\u5934\u5c31\u5df2\u7761\u7740\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u6ca1\u6709\u8fd9\u4e48\u597d\u7684\u795e\u6c14\uff0c\u81ea\u4ece\u4e09\u5c81\u4ee5\u540e\uff0c\u4ed6\u5c31\u4ece\u6765\u4e86\u6ca1\u6709\u8fd9\u4e48\u65e9\u7761\u8fc7\uff0c\u6740\u4e86\u4ed6\u4e5f\u7761 \u4e0d\u7740\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u5c4b\u91cc\u4e00\u70b9\u52a8\u9759\u90fd\u6ca1\u6709\uff0c\u4e5f\u50cf\u662f\u7761\u7740\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ab\u8863\u8d77\u5e86\uff0c\u6084\u6084\u8d70\u4e86\u51fa\u53bb \u3000\u3000\u6709\u5f88\u591a\u4e8b\u4ed6\u90fd\u60f3\u627e\u963f\u98de\u804a\u804a\u3002 \u3000\u3000\u4f46\u963f\u98de\u5374\u7761\u7740\u5f88\u6c89\uff0c\u63a8\u4e5f\u63a8\u4e0d\u9192\uff0c\u5c31\u7b97\u662f\u6761\u732a\u4e5f\u4e0d\u4f1a\u7761\u5f97\u8fd9\u4e48\u6c89\u7684\uff0c\u4f55\u51b5\u662f\u6bd4\u72fc\u8fd8\u6709\u8b66 \u89c9\u7684\u963f\u98de\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7ad9\u5728\u963f\u98de\u5e8a\u5934\uff0c\u6c89\u601d\u7740\uff0c\u9762\u4e0a\u9732\u51fa\u4e86\u6124\u6124\u7684\u8868\u60c5 \u3000\u3000\u201c\u5979\u6bcf\u5929\u90fd\u7761\u5f97\u5f88\u65e9\u2014\u2014\u4ece\u4e0d\u51fa\u53bb\u2014\u2014\u201d \u3000\u3000\u201c\u5929\u4e00\u9ed1\u6211\u5c31\u7761\u4e86\uff0c\u4e00\u89c9\u7761\u5230\u5929\u4eae\uff0c\u4ece\u4e0d\u4f1a\u9192\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8bb0\u5f97\u4eca\u5929\u665a\u4e0a\u5403\u7684\u6c64\u662f\u6392\u9aa8\u6c64\uff0c\u7096\u5f97\u5f88\u597d\uff0c\u963f\u98de\u559d\u4e86\u5f88\u591a\uff0c\u6797\u4ed9\u513f\u4e5f\u4e00\u76f4\u5728\u529d\u7740 \u674e\u5bfb\u6b22\u591a\u559d\u4e9b\u3002 \u3000\u3000\u5e78\u597d\u6392\u9aa8\u6c64\u662f\u7528\u7b0b\u5b50\u7096\u7684\uff0c\u674e\u5bfb\u6b22\u867d\u4e0d\u4fd7\uff0c\u5374\u4ece\u6765\u4e0d\u5403\u7b0b\u3002\u5e78\u597d\u4ed6\u53cc\u662f\u4e2a\u4ece\u4e0d\u5fcd\u5f53\u9762\u62d2 \u7edd\u522b\u4eba\u597d\u610f\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u62d2\u7edd\uff0c\u5374\u8d81\u6797\u4ed9\u513f\u5230\u53a8\u623f\u53bb\u6dfb\u996d\u7684\u65f6\u5019\uff0c\u5c06\u5979\u76db\u7ed9\u4ed6\u7684\u4e00\u5927\u7897\u6c64\u963f\u98de\u559d\u4e86\u3002 \u3000\u3000\u4ed6\u8bb0\u5f97\u6797\u4ed9\u513f\u56de\u6765\u770b\u5230\u4ed6\u7684\u6c64\u5df2\u7a7a\uff0c\u7b11\u5f97\u66f4\u751c\u3002 \u3000\u3000\u5979\u5728\u6c64\u91cc\u653e\u4e86\u4ec0\u4e48\u8ff7\u836f\uff1f \u3000\u3000\u6bcf\u5929\u665a\u4e0a\u4e00\u5927\u7897\u6c64\uff0c\u6240\u4ee5\u963f\u98de\u6bcf\u5929\u90fd\u7761\u5f97\u5f88\u6c89\u3002 \u3000\u3000\u963f\u98de\u7761\u6c89\u4e86\uff0c\u5979\u65e0\u8bba\u53bb\u505a\u4ec0\u4e48\uff0c\u963f\u98de\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u4f46\u5979\u4e3a\u4f55\u4e0d\u7d22\u6027\u5728\u6c64\u91cc\u653e\u4e9b\u6bd2\u836f\uff1f \u3000\u3000\u8fd9\u81ea\u7136\u662f\u56e0\u4e3a\u963f\u98de\u8fd8\u6709\u5229\u7528\u7684\u4ef7\u503c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u5c04\u51fa\u4e86\u6012\u706b\uff0c\u7a81\u7136\u8f6c\u8eab\uff0c\u7528\u529b\u53bb\u62cd\u6797\u4ed9\u513f\u7684\u95e8\u3002 \u3000\u3000\u95e8\u91cc\u6ca1\u6709\u58f0\u97f3\uff0c\u6ca1\u6709\u56de\u5e94\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u751f\u4e2d\u4ece\u672a\u8e22\u7834\u8fc7\u522b\u4eba\u7684\u623f\u95e8\uff0c\u95ef\u5165\u522b\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u6b21\u5374\u662f\u4f8b\u5916\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u679c\u7136\u6ca1\u6709\u4eba\uff0c\u6797\u4ed9\u513f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u7b97\u51c6\u6797\u4ed9\u513f\u5fc5\u5b9a\u5728\u8fd9\u5c0f\u697c\u4e0a\u3002 \u3000\u3000\u4ed6\u6b63\u8003\u8651\u662f\u5426\u73b0\u5728\u5c31\u95ef\u8fdb\u53bb\uff0c\u5c0f\u697c\u4e0a\u7684\u95e8\u7a81\u7136\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6162\u6162\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u770b\u6765\u4e5f\u548c\u4e0a\u5b98\u98de\u4e00\u6837\uff0c\u795e\u60c5\u867d\u7136\u5f88\u6109\u5feb\uff0c\u5374\u663e\u5f97\u6709\u4e9b\u75b2\u5026\u3002 \u3000\u3000\u4ece\u95e8\u91cc\u5c04\u51fa\u7684\u706f\u5149\uff0c\u7167\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u672c\u4e0d\u662f\u4e2a\u5bb9\u6613\u5403\u60ca\u7684\u4eba\uff0c\u4f46\u4e00\u770b\u5230\u4ed6\uff0c\u5c31\u53c8\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u4ece\u8fd9\u6247\u95e8\u91cc\u8d70\u51fa\u7684\u4eba\uff0c\u7adf\u662f\u90ed\u5d69\u9633\uff01 \u3000\u3000\u53ea\u89c1\u95e8\u91cc\u9762\u4f38\u51fa\u4e00\u53cc\u767d\u751f\u751f\u7684\u624b\uff0c\u62c9\u7740\u90ed\u5d69\u9633\u7684\u624b\u3002 \u3000\u3000\u665a\u98ce\u4e2d\u4f20\u6765\u4e00\u9635\u9635\u4f4e\u8bed\uff0c\u4f3c\u5728\u73cd\u91cd\u518d\u89c1\uff0c\u518d\u4e09\u53ee\u549b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u90ed\u5d69\u9633\u624d\u6162\u6162\u8d70\u4e0b\u697c\u68af\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u6162\uff0c\u4e0d\u65f6\u56de\u5934\uff0c\u663e\u7136\u8fd8\u6709\u4e9b\u820d\u4e0d\u5f97\u8d70\u3002 \u3000\u3000\u4f46\u5c0f\u697c\u4e0a\u7684\u95e8\u5374\u5df2\u5173\u4e86\u2014\u2014 \u3000\u3000\u8fd9\u5c0f\u697c\u4e0a\u7a76\u7adf\u662f\u5929\u5802\uff0c\u8fd8\u662f\u5730\u72f1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u4f46\u89c9\u5f97\u5f88\u60b2\u54c0\uff0c\u4e5f\u5f88\u6124\u6012\uff0c\u4ed6\u60b2\u54c0\u662f\u4e3a\u4e86\u963f\u98de\u800c\u60b2\u54c0\uff0c\u6124\u6012\u4e5f\u662f\u4e3a\u4e86\u963f\u98de\u800c\u6124 \u6012\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u4ece\u672a\u5982\u6b64\u6124\u6012\u8fc7\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u5df2\u5fcd\u4e0d\u4f4f\u8981\u51b2\u8fc7\u53bb\uff0c\u5f53\u9762\u63ed\u7a7f\u6797\u4ed9\u513f\u7684\u79d8\u5bc6\uff0c\u4f46\u90ed\u5d69\u9633\u4e5f\u53ef\u7b97\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u800c\u4e14 \u4e5f\u662f\u4e2a\u7537\u5b50\u6c49\uff01 \u3000\u3000\u4ed6\u4e0d\u5fcd\u4ee4\u90ed\u5d69\u9633\u96be\u582a\u3002 \u3000\u3000\u53ea\u89c1\u90ed\u5d69\u9633\u4ef0\u9996\u671b\u5929\uff0c\u957f\u957f\u5438\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4f46\u8d70\u4e86\u4e24\u6b65\uff0c\u4ed6\u811a\u6b65\u7a81\u53c8\u505c\u4f4f\uff0c\u5389\u58f0\u9053\uff1a\u662f\u4ec0\u4e48\u4eba\u8eb2\u5728\u90a3\u91cc\uff0c\u51fa\u6765\uff01 \u3000\u3000\u5d69\u9633\u94c1\u5251\u679c\u7136\u4e0d\u6127\u662f\u5f53\u4eca\u5929\u4e0b\u9876\u5c16\u9ad8\u624b\uff0c\u4ed6\u7684\u8b66\u89c9\u4e4b\u9ad8\uff0c\u53cd\u5e94\u4e4b\u5feb\uff0c\u90fd\u7edd\u975e\u4e0a\u5b98\u98de\u53ef\u6bd4 \u3002 \u3000\u3000\u65e0\u8bba\u4ece\u4ec0\u4e48\u5730\u65b9\u8d70\u51fa\u6765\uff0c\u4ed6\u5934\u8111\u8fd8\u662f\u80fd\u4fdd\u6301\u6e05\u9192\uff0c\u4f46\u4ed6\u5374\u4e5f\u7edd\u5bf9\u60f3\u4e0d\u5230\u4ece\u6811\u540e\u8d70\u51fa\u6765\u7684 \u4eba\u7adf\u662f\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u4ece\u5c0f\u697c\u5230\u505c\u8f66\u7231\u9189\u67ab\u6797\u665a\u5e76\u4e0d\u8fdc\uff0c\u4e24\u4eba\u5728\u8fd9\u6bb5\u8def\u4e0a\u8bf4\u7684\u8bdd\u4e5f\u4e0d\u591a\uff0c\u800c\u4e14\u90fd\u6ca1\u6709\u8bf4\u51fa\u81ea\u5df1 \u5fc3\u91cc\u60f3\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u4f46\u6709\u4e9b\u8bdd\u8fdf\u65e9\u603b\u662f\u8981\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u5750\u5728\u9152\u5e97\u7684\u5c4b\u810a\u4e0a\uff0c\u5f00\u59cb\u559d\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5728\u5f88\u591a\u5730\u65b9\u90fd\u559d\u8fc7\u9152\uff0c\u4f46\u5750\u5728\u5c4b\u810a\u4e0a\u559d\u9152\uff0c\u8fd8\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\uff0c\u4ed6\u53d1\u89c9\u8fd9\u771f\u662f\u559d\u9152 \u7684\u597d\u5730\u65b9\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e00\u575b\u9152\u4e5f\u53ea\u5269\u4e0b\u534a\u575b\u4e86\u3002 \u3000\u3000\u90ed\u5d69\u9633\u559d\u5f97\u771f\u4e0d\u5c11\u2014\u2014\u6709\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u9152\u4f34\uff0c\u6709\u6e05\u98ce\u660e\u6708\u6cbd\u9152\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u591a\u559d\u51e0\u676f\u7684 \u3002 \u3000\u3000\u90ed\u5d69\u9633\u5ffd\u7136\u9053\uff1a\u4f60\u2014\u2014\u4f60\u81ea\u7136\u77e5\u9053\u6211\u5230\u90a3\u697c\u4e0a\u53bb\u505a\u4ec0\u4e48\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u4f60\u662f\u7537\u4eba\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f60\u81ea\u7136\u4e5f\u77e5\u9053\u5728\u90a3\u697c\u4e0a\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u2014\u2014\u5e76\u4e0d\u5e38\u6765\u627e\u5979\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u53ea\u6709\u5728\u5fc3\u60c5\u4e0d\u597d\u7684\u65f6\u5019\uff0c\u624d\u4f1a\u6765\u627e\u5979\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u9ed8\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u4e5f\u8ba4\u5f97\u5f88\u591a\u5973\u4eba\uff0c\u4f46\u5979\u5374\u662f\u6700\u80fd\u4ee4\u6211\u6109\u5feb\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u5979\u662f\u600e\u6837\u7684\u4e00\u4e2a\u5973\u4eba\u4e48\uff1f \u3000\u3000\u90ed\u5d69\u9633\u559d\u4e86\u53e3\u9152\uff0c\u9053\uff1a\u6211\u8ba4\u5f97\u5979\u5df2\u6709\u5f88\u4e45\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5979\u5bf9\u4f60\u600e\u6837\uff1f \u3000\u3000\u90ed\u5d69\u9633\u7b11\u4e86\uff0c\u9053\uff1a\u5979\u4f1a\u5bf9\u6211\u600e\u6837\uff1f\u8fd9\u79cd\u5973\u4eba\u5bf9\u4efb\u4f55\u4eba\u90fd\u662f\u4e00\u6837\u7684\uff0c\u53ea\u770b\u90a3\u7537\u4eba\u662f\u4e0d\u662f\u6709 \u88ab\u5979\u5229\u7528\u7684\u4ef7\u503c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u4e5f\u77e5\u9053\u5979\u5728\u5229\u7528\u4f60\uff1f \u3000\u3000\u90ed\u5d69\u9633\u53c8\u7b11\u4e86\u9053\uff1a\u6211\u5f53\u7136\u77e5\u9053\uff0c\u4f46\u6211\u5374\u4e00\u70b9\u4e5f\u4e0d\u5728\u610f\uff0c\u56e0\u4e3a\u6211\u4e5f\u5728\u5229\u7528\u5979\u3002\u53ea\u8981\u5979\u80fd\u7ed9 \u6211\u6109\u5feb\uff0c\u6211\u4ed8\u51fa\u4ee3\u4ef7\u6709\u4f55\u59a8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u8fd9\u7684\u786e\u662f\u5f88\u516c\u5e73\u7684\u4ea4\u6613\uff0c\u53ef\u662f\u2014\u2014\u4f60\u4eec\u7684\u4ea4\u6613\u82e5\u662f\u4f24\u5bb3\u5230\u522b\u4eba\uff0c \u4f60\u4e5f\u4e0d\u5728\u610f\u4e48\uff1f \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f1a\u4f24\u5bb3\u5230\u8c01\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u81ea\u7136\u662f\u7231\u5979\u7684\u4eba\u3002 \u3000\u3000\u90ed\u5d69\u9633\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u6709\u65f6\u771f\u4e0d\u61c2\uff0c\u5973\u4eba\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u4f24\u5bb3\u7231\u5979\u7684\u4eba\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5979\u53ea\u80fd\u4f24\u5bb3\u7231\u5979\u7684\u4eba\uff0c\u4f60\u82e5\u4e0d\u7231\u5979\uff0c\u600e\u4e48\u88ab\u5979\u4f24\u5bb3\uff1f \u4f60\u82e5\u4e0d\u7231\u5979\uff0c\u5979\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u4f60\u6839\u672c\u90fd\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5fae\u7b11\u9053\uff1a\u4f60\u5bf9\u5973\u4eba\u597d\u50cf\u4e86\u89e3\u5f97\u5f88\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u7537\u4eba\u80fd\u771f\u7684\u4e86\u89e3\u5973\u4eba\uff0c\u82e5\u6709\u8c01\u8ba4\u4e3a\u81ea\u5df1\u5f88\u4e86\u89e3\u5973\u4eba\uff0c\u4ed6 \u5403\u7684\u82e6\u5934\u4e00\u5b9a\u6bd4\u522b\u4eba\u66f4\u5927\u3002 \u3000\u3000\u90ed\u5d69\u9633\u6c89\u9ed8\u5f88\u4e45\u7f13\u7f13\u9053\uff1a\u963f\u98de\u771f\u7684\u5f88\u7231\u5979\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u77e5\u9053\u5979\u662f\u963f\u98de\u7684\u670b\u53cb\uff0c\u4e5f\u77e5\u9053\u963f\u98de\u662f\u4f60\u7684\u670b\u53cb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f46\u6211\u5374\u4e0d\u8ba4\u5f97\u963f\u98de\uff0c\u4e5f\u4ece\u672a\u89c1\u5230\u8fc7\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u7528\u4e0d\u7740\u89e3\u91ca\uff0c\u6211\u5e76\u6ca1\u6709\u602a\u4f60\u3002 \u3000\u3000\u90ed\u5d69\u9633\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u95ee\u9053\uff1a\u963f\u98de\u73b0\u5728\u8fd8\u548c\u5979\u5728\u4e00\u8d77\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u4ed6\u7231\u5979\u867d\u6bd4\u4f60\u6df1\u5f97\u591a\uff0c\u4f46\u4ed6\u548c\u5979\u7684\u5173\u7cfb\u5374\u8fd8\u4e0d\u53ca\u4f60\u4eb2\u5bc6\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5f88\u8be7\u5f02\u9053\uff1a\u96be\u9053\u5979\u6ca1\u6709\u548c\u4ed6\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\uff0c\u5c31\u662f\u4ed6\u4e0d\u53ef\u4ee5\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u4ed6\u5c0a\u656c\u5979\uff0c\u4ece\u4e0d\u613f\u52c9\u5f3a\u5979\uff0c\u5979\u662f\u4ed6\u5fc3\u76ee\u4e2d\u7684\u5723\u5973\u2014\u2014\u5979\u81ea\u7136\u5e0c\u671b\u4ed6\u6c38\u8fdc \u4fdd\u7559\u8fd9\u79cd\u5370\u8c61\u3002 \u3000\u3000\u4ed6\u82e6\u7b11\u9053\uff1a\u5176\u5b9e\u5973\u4eba\u662f\u751f\u6765\u88ab\u4eba\u7231\u7684\uff0c\u800c\u4e0d\u662f\u88ab\u4eba\u5c0a\u656c\u7684\uff0c\u7537\u4eba\u82e5\u5bf9\u4e00\u4e2a\u6839\u672c\u4e0d\u503c\u5f97\u5c0a \u656c\u7684\u5973\u4eba\u5c0a\u656c\uff0c\u6362\u6765\u7684\u4e00\u5b9a\u662f\u75db\u82e6\u548c\u70e6\u607c\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u5982\u6b64\u8bf4\u6765\uff0c\u5979\u7684\u6240\u505a\u6240\u4e3a\uff0c\u963f\u98de\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5b8c\u5168\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f60\u4e3a\u4f55\u4e0d\u544a\u8bc9\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u7eb5\u7136\u544a\u8bc9\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4e00\u4e2a\u7537\u4eba\u82e5\u662f\u7231\u4e0a\u4e86\u4e00\u4e2a\u5973\u4eba\uff0c\u4ed6\u7684\u8033\u6735\u5c31 \u4f1a\u53d8\u804b\u5b50\uff0c\u773c\u775b\u4e5f\u4f1a\u53d8\u778e\u5b50\uff0c\u660e\u660e\u5f88\u806a\u660e\u7684\u4eba\u4e5f\u4f1a\u53d8\u5446\u5b50\u3002 \u3000\u3000\u90ed\u5d69\u9633\u6c89\u541f\u9053\uff1a\u4f60\u96be\u9053\u8981\u6211\u53bb\u544a\u8bc9\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\uff1a\u4ed6\u662f\u4e2a\u5f88\u6709\u4f5c\u4e3a\u7684\u9752\u5e74\uff0c\u4e5f\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u6211\u4e0d\u5fcd\u5fc3\u773c\u770b\u4ed6\u8d25\u5728\u8fd9\u79cd\u5973\u4eba \u7684\u624b\u4e0a\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9ed8\u9ed8\u65e0\u8bed\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u751f\u5e73\u4ece\u672a\u6c42\u4eba\uff0c\u4f46\u8fd9\u4e00\u6b21\u2014\u2014 \u3000\u3000\u90ed\u5d69\u9633\u7a81\u7136\u6253\u65ad\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u53ef\u662f\u2014\u2014\u6211\u8bf4\u7684\u8bdd\uff0c\u4ed6\u5c31\u4f1a\u76f8\u4fe1\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u81f3\u5c11\u4f60\u548c\u5979\u7684\u5173\u7cfb\uff0c\u5979\u603b\u4e0d\u80fd\u5b8c\u5168\u5426\u8ba4\u7684\u3002 \u3000\u3000\u90ed\u5d69\u9633\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u597d\uff0c\u6211\u966a\u4f60\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7d27\u63e1\u4f4f\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u6211\u7684\u786e\u6ca1\u6709\u770b\u9519\u4f60\uff0c\u6211\u76f8\u4fe1\u4f60\u548c\u963f\u98de\u4e5f\u4e00\u5b9a\u4f1a\u53d8\u6210\u5f88\u597d\u7684\u670b \u53cb\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u597d\u670b\u53cb\u53ea\u8981\u6709\u4e00\u4e2a\u5c31\u5df2\u8db3\u591f\uff0c\u4ed6\u80fd\u4ea4\u5230\u4e00\u4e2a\u50cf\u4f60\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u5df2\u53ef\u7b97\u662f\u4e0d\u865a \u6b64\u751f\u4e86\uff01 \u3000\u3000\u6728\u5c4b\u91cc\u7adf\u6ca1\u6709\u4eba\uff01 \u3000\u3000\u963f\u98de\u7761\u8fc7\u7684\u5e8a\uff0c\u8fd8\u94fa\u5728\u5ba2\u5385\u91cc\uff0c\u53a8\u623f\u91cc\u8fd8\u6446\u4e9b\u6628\u591c\u5403\u5269\u4e0b\u7684\u8336\uff0c\u4f46\u7096\u6c64\u7684\u6c64\u9505\u5374\u5df2\u7a7a\u4e86 \uff0c\u800c\u4e14\u4e5f\u5df2\u6d17\u5f97\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u5367\u623f\u91cc\u4e00\u5207\u4e1c\u897f\u90fd\u8fd8\u662f\u8001\u6837\u5b50\uff0c\u88ab\u674e\u5bfb\u6b22\u95ef\u7834\u7684\u95e8\u5728\u98ce\u4e2d\u5fae\u5fae\u6447\u6643\u7740\uff0c\u4e0d\u65f6\u53d1 \u51fa\u5431\u5431\u7684\u58f0\u54cd\u3002 \u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u72e1\u5154>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u4e00\u7ae0\u3000\u72e1\u5154 \u3000\u3000\u963f\u98de\u5c4b\u5b50\u91cc\u7684\u4e1c\u897f\u4e5f\u6ca1\u6709\u79fb\u52a8\u8fc7\uff0c\u751a\u81f3\u8fde\u90a3\u5957\u8863\u670d\u90fd\u8fd8\u6446\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u4eba\u5374\u5df2\u8d70\u4e86\uff01\u663e\u7136\u8d70\u5f97\u5f88\u5306\u5fd9\u3002 \u3000\u3000\u963f\u98de\u7adf\u7136\u53c8\u4e0d\u8f9e\u800c\u522b\uff0c\u674e\u5bfb\u6b22\u7b80\u76f4\u4e0d\u80fd\u76f8\u4fe1\uff0c\u671b\u7740\u90a3\u6247\u88ab\u4ed6\u649e\u7834\u7684\u95e8\uff0c\u4ed6\u5ffd\u53c8\u5f2f\u4e0b\u8170\u53bb \u5267\u70c8\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u90ed\u5d69\u9633\u80cc\u7740\u53cc\u624b\uff0c\u9759\u9759\u7684\u671b\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u8bf4\u963f\u98de\u662f\u4f60\u7684\u597d\u670b\u53cb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f46\u4f60\u5374\u4e0d\u77e5\u9053\u4ed6\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u534a\u664c\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4e5f\u8bb8\uff0c\u4ed6\u9047\u7740\u4ec0\u4e48\u610f\u5916\uff0c\u4e5f\u8bb8\u2014\u2014 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u6bd4\u8f83\u542c\u5973\u4eba\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u4e0d\u8ba9\u674e\u5bfb\u6b22\u53cd\u9a73\uff0c\u7acb\u523b\u53c8\u63a5\u7740\u95ee\u9053\uff1a\u4ed6\u4eec\u5df2\u5728\u8fd9\u91cc\u4f4f\u4e86\u5f88\u4e45\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5feb\u4e24\u5e74\u4e86\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4f46\u4e24\u5e74\u4ee5\u524d\uff0c\u5979\u5df2\u7ea6\u6211\u5728\u90a3\u5c0f\u697c\u4e0a\u89c1\u8fc7\u9762\u4e86\u3002\u8fd9\u5730\u65b9\u8bf4\u4e0d\u5b9a\u5c31\u662f\u5979\u7684\u8001\u7a9d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u72e1\u5154\u4e09\u7a9f\uff0c\u5979\u7684\u7a9d\u5fc5\u5b9a\u4e0d\u6b62\u8fd9\u4e00\u5904\u3002 \u3000\u3000\u90ed\u5d69\u9633\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u53ef\u60dc\u6211\u5374\u53ea\u77e5\u9053\u8fd9\u4e00\u5904\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8bf4\u8bdd\uff0c\u6162\u6162\u7684\u8d70\u5165\u6797\u4ed9\u513f\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u4e00\u5f20\u5e8a\u3001\u4e00\u5f20\u67dc\u3001\u4e00\u5f20\u684c\u3002 \u3000\u3000\u67dc\u5b50\u91cc\u7684\u8863\u670d\u5e76\u4e0d\u591a\uff0c\u800c\u4e14\u90fd\u5f88\u6734\u7d20\uff0c\u684c\u4e0a\u6709\u4e2a\u5c0f\u5c0f\u7684\u5986\u5323\uff0c\u91cc\u9762\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u82b1\u7c89\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u4e5f\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u90a3\u5c0f\u697c\u624d\u662f\u5979\u66f4\u8863\u5316\u5986\u7684\u5730\u65b9\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5979\u7559\u5728\u697c\u4e0a\uff0c\u73b0\u5728\u5979\u5374\u5df2\u56de\u6765\u8fc7\uff0c\u800c\u4e14\u5df2\u7ecf\u5c06\u963f\u98de\u5e26\u8d70\u4e86\uff0c \u6211\u4eec\u5728\u8def\u4e0a\u7adf\u672a\u53d1\u73b0\u5979\u7684\u8e2a\u8ff9\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u58f0\u9053\uff1a\u8fd9\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5979\u8d70\u7684\u662f\u53e6\u5916\u4e00\u6761\u8def\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u53e6\u5916\u4e00\u6761\u8def\uff0c\u8fd9\u91cc\u56db\u9762\u73af\u5c71\uff0c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u6377\u5f84\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u63ed\u8d77\u4e86\u5e8a\u677f\u3002 \u3000\u3000\u5e8a\u4e0b\u679c\u7136\u6709\u6761\u79d8\u9053\u2014\u2014 \u3000\u3000\u5c71\u8179\u4e2d\u7a7a\uff0c\u79d8\u9053\u7a7f\u8fc7\u5c71\u8179\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u8d70\u4e0b\u53bb\uff0c\u5c31\u5df2\u77e5\u9053\u51fa\u53e3\u5728\u54ea\u91cc\u4e86\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u4ee5\u4f60\u770b\uff0c\u8fd9\u6761\u8def\u7684\u51fa\u53e3\u662f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u5c0f\u697c\u4e0a\u7684\u5e8a\u4e0b\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\u2014\u2014 \u3000\u3000\u4ed6\u51b7\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u4e0b\u4e86\u8fd9\u5f20\u5e8a\uff0c\u5c31\u4e0a\u90a3\u5f20\u5e8a\uff0c\u5979\u505a\u4e8b\u5012\u771f\u4e0d\u80af\u6d6a\u8d39\u65f6\u95f4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u5979\u7684\u7ea6\u4f1a\u5f88\u5fd9\uff0c\u65f6\u95f4\u81ea\u7136\u5b9d\u8d35\u5f97\u5f88\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9762\u8272\u53d8\u4e86\u53d8\u2014\u2014\u4ed6\u867d\u7136\u4e5f\u660e\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u542c\u5230\u522b\u4eba\u5f53\u9762\u8bf4\u51fa\u6765\uff0c\u5fc3\u91cc\u8fd8 \u662f\u6709\u4e9b\u4e0d\u8212\u670d\u3002 \u3000\u3000\u7537\u4eba\u4eec\u5e38\u5632\u7b11\u5973\u4eba\u4eec\u7684\u6c14\u91cf\u5c0f\uff0c\u5176\u5b9e\u7537\u4eba\u81ea\u5df1\u7684\u6c14\u91cf\u4e5f\u672a\u5fc5\u5c31\u6bd4\u5973\u4eba\u5927\u591a\u5c11\uff0c\u800c\u4e14\u8fdc\u6bd4 \u5973\u4eba\u81ea\u79c1\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u4eec\u5c31\u7b97\u6709\u4e86\u4e00\u4e07\u4e2a\u5973\u4eba\uff0c\u5374\u8fd8\u662f\u5e0c\u671b\u8fd9\u4e00\u4e07\u4e2a\u5973\u4eba\u90fd\u53ea\u6709\u4ed6\u4e00\u4eba\u7537\u4eba\uff0c\u4ed6\u5c31\u7b97\u65e9\u5df2\u4e0d \u559c\u6b22\u90a3\u5973\u4eba\uff0c\u5374\u8fd8\u662f\u5e0c\u671b\u90a3\u5973\u4eba\u6c38\u8fdc\u53ea\u559c\u6b22\u4ed6\u3002 \u3000\u3000\u79d8\u9053\u81ea\u7136\u4e0d\u4f1a\u592a\u957f\u3002 \u3000\u3000\u79d8\u9053\u7684\u51fa\u53e3\uff0c\u679c\u7136\u5c31\u5728\u90a3\u5c0f\u697c\u4e0a\u5367\u5ba4\u4e2d\u7684\u5e8a\u4e0b\u3002 \u3000\u3000\u8fd9\u5f20\u5e8a\u53ef\u6bd4\u90a3\u5f20\u5e8a\u6f02\u4eae\u591a\u4e86\uff0c\u9526\u5e10\u4e0a\u7684\u6d41\u82cf\u7f28\u7edc\u7f24\u7eb7\uff0c\u5e8a\u4e0a\u7684\u9e45\u6bdb\u88ab\u8f6f\u5f97\u5c31\u50cf\u4e91\u5806\uff0c\u53eb \u4eba\u4e00\u9677\u8fdb\u53bb\uff0c\u5c31\u722c\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u81ea\u7136\u4e0d\u4f1a\u5728\uff0c\u5c4b\u5b50\u91cc\u53ea\u6709\u90a3\u7a7f\u7ea2\u8863\u670d\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u5979\u6b63\u5750\u5728\u5986\u53f0\u65c1\u5f88\u4e13\u5fc3\u7684\u7ee3\u7740\u82b1\uff0c\u7ee3\u7684\u662f\u4e00\u9762\u9e33\u9e2f\u620f\u6c34\u7684\u6795\u5934\uff0c\u8fd9\u6b63\u548c\u5c4b\u5b50\u91cc\u7684\u60c5\u9ad8\u6b4c \u975e\u5e38\u914d\u5408\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ed6\u4eec\u7a81\u7136\u8d70\u51fa\u6765\uff0c\u5979\u5e76\u6ca1\u6709\u5403\u60ca\u3002 \u3000\u3000\u5979\u50cf\u662f\u65e9\u5df2\u7b97\u51c6\u4ed6\u4eec\u4f1a\u6765\u4e86\u3002 \u3000\u3000\u5979\u53ea\u662f\u7528\u773c\u89d2\u779f\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u9053\uff1a\u539f\u6765\u4f60\u4eec\u662f\u8ba4\u5f97\u7684\u3002 \u3000\u3000\u90ed\u5d69\u9633\u6c89\u7740\u8138\uff0c\u5389\u58f0\u9053\uff1a\u8fd9\u91cc\u53ea\u5269\u4e0b\u4f60\u4e00\u4e2a\u4eba\u4e86\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u561f\u8d77\u5634\u9053\uff1a\u4f60\u8fd9\u4e48\u51f6\u5e72\u4ec0\u4e48\uff1f\u6bcf\u6b21\u4f60\u6765\u7684\u65f6\u5019\uff0c\u66ff\u4f60\u94fa\u5e8a\u7684\u662f\u6211\uff0c\u66ff\u4f60\u53e0\u88ab\u7684\u4e5f \u662f\u6211\uff0c\u4f60\u96be\u9053\u5df2\u5fd8\u4e86\u4e48\uff1f \u3000\u3000\u90ed\u5d69\u9633\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u7684\u5927\u773c\u775b\u5728\u674e\u5bfb\u6b22\u8eab\u4e0a\u4e00\u8f6c\uff0c\u9053\uff1a\u4f60\u5c31\u662f\u674e\u63a2\u82b1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u60a0\u60a0\u9053\uff1a\u522b\u4eba\u90fd\u8bf4\u674e\u5bfb\u6b22\u4e0d\u4f46\u6b66\u529f\u6700\u9ad8\uff0c\u4eba\u4e5f\u6700\u7cbe\u660e\uff0c\u6700\u80fd\u5e72\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u60f3\u5230 \u4f60\u4e5f\u4f1a\u88ab\u4eba\u9a97\uff0c\u4e0a\u4eba\u7684\u5f53\u3002 \u3000\u3000\u5979\u7728\u7740\u773c\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u4e0a\u6b21\u6211\u9a97\u4e86\u4f60\uff0c\u771f\u62b1\u6b49\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6ca1\u5173\u7cfb\uff0c\u5076\u5c14\u88ab\u5c0f\u5b69\u5b50\u9a97\u4e00\u6b21\uff0c\u4e5f\u662f\u4ef6\u5f88\u5f00\u5fc3\u7684\u4e8b\uff0c\u6211\u81ea\u4ece\u88ab\u4f60\u9a97\u8fc7\u4e00\u6b21\u540e \uff0c\u5c31\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u5e74\u8f7b\u591a\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u773c\u775b\u76ef\u7740\u4ed6\uff0c\u4eff\u4f5b\u4e5f\u6e10\u6e10\u89c9\u5f97\u8fd9\u4eba\u7684\u786e\u5f88\u6709\u8da3\u4e86\u2014\u2014\u50cf\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u4eba\uff0c\u672c\u5c31\u4e0d \u662f\u5e38\u5e38\u80fd\u89c1\u5f97\u5230\u7684\u3002 \u3000\u3000\u5979\u7b11\u9053\uff1a\u6211\u770b\u4f60\u5c31\u7b97\u6ca1\u6709\u88ab\u6211\u9a97\uff0c\u672c\u6765\u4e5f\u5e74\u8f7b\u5f97\u5f88\uff0c\u82e5\u662f\u518d\u88ab\u6211\u9a97\u51e0\u6b21\uff0c\u53ea\u6015\u5c31\u8981\u53d8\u6210 \u5c0f\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4ee5\u540e\u4e00\u5b9a\u4f1a\u5f88\u5c0f\u5fc3\u2014\u2014\u56db\u5341\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u5c82\u975e\u8981\u88ab\u4eba\u5f53\u505a\u5996\u602a\u4e86\u4e48\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u7b11\u9053\uff1a\u4f60\u53ea\u7ba1\u653e\u5fc3\uff0c\u4e0a\u6b21\u6211\u9a97\u4e86\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u8fd8\u662f\u4e2a\u964c\u751f\u4eba\uff0c\u5976\u5976\u4ece\u5c0f\u5c31\u544a\u8bc9\u6211 \uff0c\u5343\u4e07\u4e0d\u80fd\u5bf9\u964c\u751f\u4eba\u8bf4\u8001\u5b9e\u8bdd\uff0c\u5426\u5219\u4e5f\u8bb8\u5c31\u4f1a\u88ab\u4eba\u62d0\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u73b0\u5728\u5462\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u73b0\u5728\u6211\u4eec\u5df2\u8ba4\u8bc6\uff0c\u6211\u81ea\u7136\u4e0d\u4f1a\u518d\u9a97\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4e48\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u521a\u521a\u53ef\u66fe\u770b\u5230\u6709\u4eba\u4ece\u8fd9\u91cc\u51fa\u6765\u4e48\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u5979\u7728\u4e86\u7728\u773c\u775b\uff0c\u53c8\u9053\uff1a\u4f46\u6211\u5374\u770b\u5230\u6709\u4eba\u4ece\u5916\u9762\u8fdb\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u662f\u4e2a\u7537\u4eba\uff0c\u6211\u4e0d\u8ba4\u8bc6\u4ed6\u3002 \u3000\u3000\u5979\u5403\u5403\u7684\u7b11\u9053\uff1a\u9664\u4e86\u4f60\u5916\uff0c\u6211\u8ba4\u5f97\u7684\u7537\u4eba\u4e0d\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u597d\u88c5\u4f5c\u6ca1\u6709\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u95ee\u9053\uff1a\u4ed6\u662f\u6765\u5e72\u4ec0\u4e48\u7684\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u90a3\u4eba\u957f\u5f97\u5f88\u51f6\u72e0\uff0c\u4e00\u5634\u5927\u80e1\u5b50\uff0c\u8138\u4e0a\u8fd8\u6709\u4e2a\u5200\u75a4\uff0c\u4e00\u8d70\u8fdb\u6765\u5c31\u95ee\u6211\uff0c\u8ba4\u4e0d\u8ba4 \u5f97\u674e\u5bfb\u6b22\uff1f\u674e\u5bfb\u6b22\u4f1a\u4e0d\u4f1a\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u53ea\u56e0\u4e3a\u6211\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u6240\u4ee5\u5c31\u6545\u610f\u9a97\u4ed6\uff0c\u8bf4\u6211\u8ba4\u5f97\u4f60\uff0c\u4f60\u9a6c\u4e0a\u5c31\u4f1a\u6765\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4e48\u4ed6\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u7728\u773c\u9053\uff1a\u4ed6\u5c31\u4ea4\u7ed9\u6211\u4e00\u5c01\u4fe1\uff0c\u8981\u6211\u8f6c\u4ea4\u7ed9\u4f60\uff0c\u8fd8\u8bf4\u4e00\u5b9a\u8981\u6211\u4ea4\u7ed9\u4f60\u672c\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1b\u90a3\u4f60\u5c31\u6536\u4e0b\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u6211\u5f53\u7136\u6536\u4e0b\u4e86\u2014\u2014\u6211\u82e5\u4e0d\u6536\u4e0b\uff0c\u8c0e\u8bdd\u5c82\u975e\u5c31\u8981\u88ab\u63ed\u7a7f\u4e86\u4e48\uff1f\u90a3\u4eba\u51f6\u5f97\u5f88\uff0c\u82e5 \u77e5\u9053\u6211\u5728\u8bf4\u8c0e\uff0c\u4e0d\u6253\u7834\u6211\u7684\u5934\u624d\u602a\u3002 \u3000\u3000\u5979\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u5973\u5b69\u5b50\u7684\u5934\u82e5\u88ab\u6253\u7834\uff0c\u4e00\u5b9a\u75bc\u5f97\u5f88\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\u7b11\u9053\uff1a\u7537\u5b69\u5b50\u7684\u5934\u88ab\u6253\u7834\uff0c\u4e5f\u75bc\u5f97\u5f88\u7684\u3002 \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u6709\u79cd\u672c\u4e8b\uff0c\u5979\u65e0\u8bba\u8bf4\u4ec0\u4e48\u8bdd\u90fd\u5b8c\u5168\u50cf\u771f\u7684\u4e00\u6837\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u4e00\u5b9a\u4f1a\u95ee\u5979\uff1a \u3000\u3000\u9001\u4fe1\u7684\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u600e\u4f1a\u5c06\u4ea4\u7ed9\u6211\u7684\u4fe1\u9001\u5230\u8fd9\u91cc\uff1f \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5e76\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u4e5f\u6709\u79cd\u672c\u4e8b\uff0c\u90a3\u5c31\u662f\u65e0\u8bba\u522b\u4eba\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u90fd\u597d\u50cf\u5f88\u76f8\u4fe1\uff0c\u6240\u4ee5\u6709\u5f88\u591a\u4eba\u90fd\u5e38\u5e38\u4ee5\u4e3a\u81ea \u5df1\u5df2\u7ecf\u9a97\u8fc7\u4e86\u4ed6\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u679c\u7136\u53d6\u51fa\u4e86\u5c01\u4fe1\uff0c\u4fe1\u4e0a\u679c\u7136\u5199\u7740\u674e\u5bfb\u6b22\u7684\u540d\u5b57\uff0c\u4fe1\u662f\u5bc6\u5c01\u7740\u7684\uff0c\u8fd9\u5c0f\u59d1\u5a18\u5c45\u7136\u6ca1 \u6709\u5077\u770b\u3002 \u3000\u3000\u4fe1\u4e0a\u5199\u7684\u662f\uff1a\u5bfb\u6b22\u5148\u751f\u8db3\u4e0b\uff0c\u4e45\u6155\u82f1\u540d\uff0c\u6781\u76fc\u4e00\u6664\uff0c\u5341\u6708\u521d\u4e00\u5f53\u5019\u6559\u4e8e\u6b64\u5c71\u4e2d\u98de\u6cc9\u4e4b\u4e0b \uff0c\u8db3\u4e0b\u541b\u5b50\uff0c\u5fc5\u4e0d\u81f4\u4ee4\u6211\u5931\u671b\u3002 \u3000\u3000\u4e0b\u9762\u7684\u7f72\u540d\u8d6b\u7136\u7adf\u662f\uff1a\u4e0a\u5b98\u91d1\u8679\uff01 \u3000\u3000\u8fd9\u5c01\u4fe1\u5199\u5f97\u5f88\u7b80\u5355\uff0c\u4e5f\u5f88\u5ba2\u6c14\uff0c\u4f46\u65e0\u8bba\u8c01\u63a5\u5230\u8fd9\u5c01\u4fe1\uff0c\u5c31\u7b97\u4e0d\u7acb\u523b\u53bb\u51c6\u5907\u540e\u4e8b\uff0c\u4e5f\u8981\u5413 \u4e00\u8df3\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u82e5\u5411\u4e00\u4e2a\u4eba\u6311\u6218\uff0c\u90a3\u4eba\u8fd8\u80fd\u6d3b\u5f97\u957f\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u53e0\u8d77\u4fe1\uff0c\u653e\u56de\u4fe1\u5c01\uff0c\u85cf\u5165\u6000\u4e2d\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u5c45\u7136\u8fd8\u5728\u7b11\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u4e00\u76f4\u76ef\u7740\u4ed6\uff0c\u6b64\u523b\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u4fe1\u4e0a\u5199\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6ca1\u6709\u4ec0\u4e48\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u77a7\u4f60\u7b11\u5f97\u8fd9\u4e48\u5f00\u5fc3\uff0c\u8fea\u5c01\u4fe1\u53ea\u6015\u662f\u5973\u4eba\u5199\u7ed9\u4f60\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u731c\u5bf9\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u5979\u662f\u4e0d\u662f\u60f3\u7ea6\u4f60\u89c1\u9762\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53c8\u731c\u5bf9\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u561f\u8d77\u5634\uff0c\u9053\uff1a\u65e9\u77e5\u9053\u662f\u5973\u4eba\u7684\u4fe1\uff0c\u6211\u624d\u4e0d\u4ea4\u7ed9\u4f60\u54e9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u82e5\u4e0d\u4ea4\u7ed9\u6211\uff0c\u5979\u4e00\u5b9a\u4f1a\u5f88\u4f24\u5fc3\u7684\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u5979\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff1f\u6f02\u4e0d\u6f02\u4eae\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5f53\u7136\u6f02\u4eae\uff0c\u5426\u5219\u6211\u65e9\u5c31\u5c06\u8fd9\u5c01\u4fe1\u7529\u5230\u4e00\u8fb9\u53bb\u4e86\uff0c\u5973\u4eba\u957f\u5f97\u4e11\uff0c\u7b80\u76f4\u6bd4\u7537\u4eba\u751f \u5f97\u7b28\u8fd8\u8981\u53ef\u6015\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u5979\u6709\u591a\u5927\u5e74\u9f84\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5e74\u7eaa\u4e5f\u4e0d\u5927\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u7528\u529b\u5c06\u7ee3\u82b1\u9488\u5f80\u5e03\u6805\u4e0a\u4e00\u63d2\uff0c\u677f\u7740\u8138\u9053\uff1a\u65e2\u7136\u6709\u8fd9\u4e48\u6837\u4e00\u4f4d\u6f02\u4eae\u7684\u8001\u592a\u5a46\u7ea6\u4f60\uff0c \u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d76\u5feb\u53bb\u89c1\u5979\uff0c\u8fd8\u5446\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u505a\u4e3b\u4eba\u7684\uff0c\u600e\u4e48\u53ef\u4ee5\u8d76\u5ba2\u4eba\u8d70\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u51b7\u51b7\u9053\uff1a\u6211\u5c31\u7b97\u4e0d\u8d76\u4f60\uff0c\u53cd\u6b63\u4e5f\u662f\u8981\u8d70\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u82e5\u4e0d\u8d70\u5462\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u9053\uff1a\u4f60\u82e5\u4e0d\u8d70\uff0c\u6211\u8fd9\u505a\u4e3b\u4eba\u7684\u5f53\u7136\u8981\u60f3\u6cd5\u5b50\u62db\u5f85\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u771f\u7684\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u5f53\u7136\u662f\u771f\u7684\uff0c\u6211\u867d\u7136\u4e0d\u5927\u65b9\uff0c\u53ef\u4e5f\u4e0d\u662f\u5c0f\u6c14\u9b3c\uff0c\u4f60\u82e5\u8981\u5728\u8fd9\u91cc\u8eba\u5341\u5929\uff0c\u6211\u5c31 \u62db\u5f85\u4f60\u5341\u5929\uff0c\u4f60\u82e5\u8981\u5728\u8fd9\u91cc\u8eba\u4e00\u8f88\u5b50\uff0c\u6211\u4e5f\u2014\u2014\u4e5f\u4e0d\u4f1a\u8d76\u4f60\u8d70\u7684\u3002 \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u7684\u8138\u5df2\u7ea2\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u7684\u8138\u82e5\u7ea2\uff0c\u90a3\u5c31\u8868\u793a\u5b9e\u5728\u5df2\u4e0d\u5c0f\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1b\u597d\uff0c\u90a3\u4e48\u6211\u5c31\u7559\u5728\u8fd9\u91cc\u2014\u2014 \u3000\u3000\u4ed6\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u5c0f\u59d1\u5a18\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u4f60\u8bf4\u7684\u662f\u771f\u8bdd\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u5f53\u7136\u662f\u771f\u7684\uff0c\u96be\u5f97\u9047\u5230\u4f60\u8fd9\u4e48\u597d\u7684\u4e3b\u4eba\uff0c\u6211\u600e\u4e48\u4f1a\u8d70\u5462\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u5c55\u989c\u7b11\u9053\uff1a\u6211\u77e5\u9053\u4f60\u559c\u6b22\u559d\u9152\uff0c\u6211\u8fd9\u5c31\u53bb\u66ff\u4f60\u51c6\u5907\uff0c\u8fd9\u5730\u65b9\u522b\u7684\u6ca1\u6709\uff0c\u9152\u5374\u662f\u591a \u5f97\u5f88\u2014\u2014\u591a\u5f97\u53ef\u4ee5\u6df9\u6b7b\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9664\u4e86\u9152\u4e4b\u5916\uff0c\u8fd9\u8fd8\u8981\u51e0\u5757\u6728\u5934\uff0c\u8d8a\u786c\u8d8a\u597d\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u6014\u4e86\u6014\u9053\uff1a\u6728\u5934\uff1f\u8981\u6728\u5934\u5e72\u4ec0\u4e48\uff1f\u96be\u9053\u4f60\u8981\u7528\u6728\u5934\u6765\u4e0b\u9152\uff1f\u4f60\u7684\u7259\u9f7f\u5012\u771f\u4e0d\u9519\u3002 \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u81ea\u5df1\u5148\u7b11\u4e86\uff0c\u9053\uff1a\u4f46\u4f60\u65e2\u7136\u8981\u6728\u5934\uff0c\u6211\u5c31\u66ff\u4f60\u62ff\u6728\u5934\u6765\uff0c\u65e0\u8bba\u4f60\u60f3\u8981\u4ec0\u4e48 \uff0c\u5c31\u7b97\u60f3\u8981\u5929\u4e0a\u7684\u6708\u4eae\uff0c\u6211\u4e5f\u4f1a\u66ff\u4f60\u642c\u68af\u5b50\u7684\u3002 \u3000\u3000\u90ed\u5d69\u9633\u4e00\u76f4\u5728\u6ce8\u610f\u674e\u5bfb\u6b22\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u6b64\u523b\u5ffd\u7136\u9053\uff1a\u6211\u4e0d\u5403\u6728\u5934\uff0c\u6211\u5403\u86cb\uff0c\u65e0\u8bba\u662f\u9e21\u86cb \u3001\u9e2d\u86cb\u3001\u76ae\u86cb\u3001\u54b8\u86cb\uff0c\u53ea\u8981\u662f\u86cb\u5c31\u53ef\u4ee5\uff0c\u8d8a\u591a\u8d8a\u597d\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u7684\u8138\u677f\u4e86\u8d77\u6765\uff0c\u4e0a\u4e0b\u77aa\u4e86\u4ed6\u4e24\u773c\u9053\uff1a\u4f60\u4e5f\u8981\u7559\u5728\u8fd9\u91cc\uff01 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u96be\u5f97\u9047\u5230\u4f60\u8fd9\u4e48\u597d\u7684\u4e3b\u4eba\uff0c\u6211\u600e\u4e48\u80af\u8d70\u5462\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u561f\u7740\u5634\u8d70\u51fa\u53bb\uff0c\u5634\u91cc\u8fd8\u5728\u5583\u5583\u9053\uff1a\u8fd9\u4e16\u4e0a\u4e0d\u8bc6\u76f8\u7684\u4eba\u5012\u771f\u4e0d\u5c11\uff0c\u4ec0\u4e48\u4e8b\u4e0d\u597d\u505a\uff0c \u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u715e\u522b\u4eba\u7684\u98ce\u666f\u5462\uff1f\u2014\u2014 \u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u6076\u6bd2>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u4e8c\u7ae0\u3000\u6076\u6bd2 \u3000\u3000\u5c4b\u5b50\u5f88\u5927\uff0c\u88ab\u5355\u662f\u65b0\u6362\u7684\uff0c\u6d17\u5f97\u5f88\u767d\uff0c\u6d46\u5f97\u5f88\u633a\uff0c\u8336\u58f6\u5e76\u6ca1\u6709\u7f3a\u53e3\uff0c\u8336\u676f\u5e72\u51c0\u5f97\u5f88\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6b63\u5750\u5728\u5e8a\u5934\uff0c\u5728\u4e00\u4ef6\u7537\u4eba\u7684\u8863\u670d\u4e0a\u7f1d\u94ae\u6263\uff0c\u5979\u7528\u9488\u663e\u7136\u6ca1\u6709\u7528\u5251\u719f\u6089\uff0c\u65f6\u5e38\u4f1a\u624e \u7740\u81ea\u5df1\u7684\u624b\u3002 \u3000\u3000\u963f\u98de\u7ad9\u5728\u7a97\u53e3\uff0c\u671b\u7740\u7a97\u5916\u7684\u591c\u8272\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u9ebd\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7f1d\u5b8c\u4e86\u4e00\u7c92\u6263\u5b50\uff0c\u6447\u5934\u9053\uff1a\u6211\u5b9e\u5728\u4e0d\u559c\u6b22\u4f4f\u5728\u5ba2\u5e97\uff0c\u65e0\u8bba\u591a\u9ebd\u597d\u7684\u5ba2\u5e97\uff0c\u623f\u95f4\u4e5f \u50cf\u662f\u4e2a\u7b3c\u5b50\u4f3c\u7684\uff0c\u6211\u4e00\u8d70\u8fdb\u53bb\u5c31\u89c9\u5f97\u95f7\u5f97\u614c\u3002 \u3000\u3000\u963f\u98de\uff1a\u55ef\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u5e38\u542c\u522b\u4eba\u8bf4\uff0c\u91d1\u7a9d\u94f6\u7a9d\uff0c\u4e5f\u4e0d\u5982\u81ea\u5df1\u7684\u72d7\u7a9d\uff0c\u65e0\u8bba\u4ec0\u9ebd\u5730\u65b9\u603b\u4e0d\u5982\u81ea\u5df1\u5bb6 \u91cc\u8212\u670d\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u55ef\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u6211\u628a\u4f60\u4ece\u5bb6\u91cc\u62c9\u51fa\u6765\uff0c\u4f60\u4e00\u5b9a\u5f88\u4e0d\u5f00\u5fc3\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6ca1\u6709\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u77e5\u9053\u674e\u5bfb\u6b22\u662f\u4f60\u7684\u597d\u670b\u53cb\uff0c\u4e5f\u4e0d\u662f\u4e0d\u613f\u610f\u4f60\u8ddf\u4ed6\u4ea4\u670b\u53cb\uff0c\u4f46\u6211 \u4eec\u65e2\u7136\u5df2\u51b3\u5b9a\u5fd8\u8bb0\u8fc7\u53bb\uff0c\u91cd\u5934\u505a\u8d77\uff0c\u5c31\u4e0d\u80fd\u4e0d\u79bb\u5f00\u4ed6\uff0c\u50cf\u4ed6\u90a3\u79cd\u4eba\uff0c\u65e0\u8bba\u8d70\u5230\u4ec0\u9ebd\u5730\u65b9\uff0c\u90fd \u4f1a\u6709\u9ebb\u70e6\u8ddf\u7740\u4ed6\u7684\u3002 \u3000\u3000\u5979\u67d4\u58f0\u9053\uff1a\u6211\u4eec\u5df2\u53d1\u8a93\u4e0d\u518d\u60f9\u9ebb\u70e6\u4e86\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u963f\u98de\uff1a\u662f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f55\u51b5\uff0c\u4ed6\u505a\u4eba\u867d\u7136\u5f88\u591f\u4e49\u6c14\uff0c\u4f46\u9152\u559d\u5f97\u592a\u591a\uff0c\u4e00\u4e2a\u4eba\u9152\u82e5\u559d\u5f97\u592a\u591a\uff0c\u5c31\u96be\u514d \u6709\u4e9b\u6bdb\u75c5\uff0c\u6bdb\u75c5\u72af\u7684\u65f6\u5019\uff0c\u8fde\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5c31\u56e0\u4e3a\u8fd9\u6837\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u649e\u7834\u6211\u7684\u95e8\uff0c\u8981\u5bf9\u6211\u2014\u2014 \u3000\u3000\u963f\u98de\u5ffd\u7136\u8f6c\u56de\u5934\uff0c\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u90a3\u4ef6\u4e8b\u4f60\u6c38\u8fdc\u83ab\u8981\u518d\u8bf4\u4e86\uff0c\u597d\u4e0d\u597d\uff1f \u3000\u3000\u6797\u4ed9\u513f\u6e29\u67d4\u4e00\u7b11\uff0c\u9053\uff1a\u5176\u5b9e\u6211\u65e9\u5df2\u539f\u8c05\u4ed6\u4e86\uff0c\u56e0\u4e3a\u4ed6\u662f\u4f60\u7684\u670b\u53cb\u3002 \u3000\u3000\u963f\u98de\u76ee\u4e2d\u9732\u51fa\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u9053\uff1a\u6211\u6ca1\u6709\u670b\u53cb\u2014\u2014\u6211\u53ea\u6709\u4f60\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ad9\u8d77\u6765\uff0c\u8d70\u8fc7\u53bb\u62c9\u4f4f\u4ed6\u7684\u624b\uff0c\u5c06\u4ed6\u62c9\u5230\u81ea\u5df1\u7684\u8eab\u65c1\uff0c\u67d4\u58f0\u9053\uff1a\u6211\u4e5f\u53ea\u6709\u4f60\u3002 \u3000\u3000\u5979\u57ab\u8d77\u811a\u5c16\uff0c\u5c06\u81ea\u5df1\u7684\u8138\u8d34\u5728\u4ed6\u8138\u4e0a\uff0c\u9053\uff1a\u6211\u53ea\u6709\u4f60\u5c31\u5df2\u8db3\u591f\u4e86\uff0c\u4ec0\u9ebd\u90fd\u4e0d\u60f3\u518d\u8981\u3002 \u3000\u3000\u963f\u98de\u5f20\u5f00\u624b\uff0c\u7d27\u7d27\u7684\u62b1\u4f4f\u4e86\u5979\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u5149\u660e\u6b63\u5927\u7684\u5a36\u6211\uff0c\u8ba9\u522b\u4eba\u90fd\u77e5\u9053\u6211\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u6562 \uff1f\u6211\u4ee5\u524d\u505a\u9519\u7684\u4e8b\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u80fd\u539f\u8c05\u6211\uff1f\u4f60\u96be\u9053\u4e0d\u662f\u771f\u5fc3\u7684\u7231\u6211\uff1f \u3000\u3000\u963f\u98de\u9762\u4e0a\u7684\u8868\u60c5\u66f4\u75db\u82e6\uff0c\u7f13\u7f13\u677e\u5f00\u624b\u3002 \u3000\u3000\u4f46\u6797\u4ed9\u513f\u5374\u5c06\u4ed6\u62b1\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u963f\u98de\u8eba\u5728\u5e8a\u4e0a\uff0c\u4f3c\u5df2\u5d29\u6e83\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5145\u6ee1\u4e86\u6094\u6068\uff0c\u4e5f\u5145\u6ee1\u4e86\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u6068\u81ea\u5df1\uff0c\u4ed6\u77e5\u9053\u4e0d\u8be5\u8fd9\u9ebd\u505a\uff0c\u4f46\u4ed6\u5df2\u65e0\u6cd5\u81ea\u62d4\uff0c\u6709\u65f6\u4ed6\u751a\u81f3\u60f3\u53bb\u6b7b\uff0c\u5374\u53c8\u820d\u4e0d\u5f97\u79bb\u5f00 \u5979\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u6b63\u5728\u5bf9\u7740\u955c\u5b50\u68b3\u5934\u53d1\uff0c\u5979\u8138\u4e0a\u7ea2\u7ea2\u7684\uff0c\u4e00\u53cc\u6c34\u6c6a\u6c6a\u7684\u773c\u775b\u91cc\u4eff\u4f5b\u8fd8 \u5e26\u7740\u6625\u8272\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\uff0c\u53ea\u6709\u963f\u98de\u4e0d\u53ef\u4ee5\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u7b11\u5f97\u7684\u786e\u7f8e\u4e3d\uff0c\u5374\u5f88\u6b8b\u9177\uff0c\u5979\u559c\u6b22\u6298\u78e8\u7537\u4eba\uff0c\u5979\u89c9\u5f97\u4e16\u4e0a\u518d \u4e5f\u6ca1\u6709\u6bd4\u8fd9\u66f4\u6109\u5feb\u7684\u4eab\u53d7\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u7136\u6709\u4eba\u7528\u529b\u7684\u6572\u95e8\u3002 \u3000\u3000\u4e00\u4eba\u5927\u58f0\u9053\uff1a\u5f00\u95e8\uff0c\u5feb\u5f00\u95e8\uff0c\u6211\u77e5\u9053\u4f60\u5728\u91cc\u9762\uff0c\u6211\u65e9\u5c31\u770b\u89c1\u4f60\u4e86\u3002 \u3000\u3000\u963f\u98de\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u5389\u58f0\u9053\uff1a\u4ec0\u9ebd\u4eba\uff1f \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u95e8\u5df2\u88ab\u649e\u5f00\uff0c\u4e00\u4e2a\u4eba\u76f4\u95ef\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u6307\u7740\u6797\u4ed9\u513f\uff0c\u683c\u683c\u7b11\u9053\uff1a\u4f60\u867d\u7136\u5047\u88c5\u770b\u4e0d\u89c1\u6211\uff0c\u6211\u5374\u770b\u5230\u4f60\u4e86\uff0c\u4f60\u8fd8\u60f3\u8d70\u9ebd\uff1f \u3000\u3000\u6797\u4ed9\u513f\u8138\u4e00\u4e1d\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u9053\uff1a\u4f60\u662f\u4ec0\u9ebd\u4eba\uff1f\u6211\u4e0d\u8ba4\u5f97\u4f60\uff01 \u3000\u3000\u8fd9\u5c11\u5e74\u5927\u7b11\u9053\uff1a\u4f60\u4e0d\u8ba4\u5f97\u6211\uff1f\u4f60\u771f\u7684\u4e0d\u8ba4\u5f97\u6211\uff1f\u4f60\u96be\u9053\u5fd8\u4e86\u90a3\u5929\u7684\u4e8b\uff1f\u2014\u2014\u597d\u597d\u597d\uff0c\u6211 \u8f9b\u8f9b\u82e6\u82e6\u66ff\u4f60\u9001\u4e86\u51e0\u5341\u5c01\u4fe1\uff0c\u4f60\u73b0\u5728\u5374\u4e0d\u8ba4\u5f97\u6211\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u6251\u8fc7\u53bb\uff0c\u60f3\u62b1\u4f4f\u6797\u4ed9\u513f\uff0c\u9053\uff1a\u4f46\u6211\u5374\u8ba4\u5f97\u4f60\uff0c\u6211\u6b7b\u4e5f\u5fd8\u4e0d\u4e86\u4f60\u2014\u2014 \u3000\u3000\u6797\u4ed9\u513f\u5f53\u7136\u4e0d\u4f1a\u88ab\u4ed6\u62b1\u4f4f\uff0c\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5c31\u8eb2\u5f00\u4e86\uff0c\u60ca\u547c\u9053\uff1a\u8fd9\u4eba\u559d\u9189\u4e86\uff0c\u4e71\u53d1\u9152\u75af\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u6251\u8fc7\u53bb\uff0c\u4f46\u963f\u98de\u5df2\u6321\u4f4f\u4e86\u4ed6\uff0c\u5389\u58f0\u9053\uff1a\u6eda\u51fa\u53bb\uff01 \u3000\u3000\u5c11\u5e74\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u4f60\u662f\u4ec0\u9ebd\u4eba\uff1f\u51ed\u4ec0\u9ebd\u8981\u6211\u6eda\u51fa\u52a8\uff0c\u4f60\u60f3\u8ba8\u597d\u5979\uff0c\u544a\u8bc9\u4f60\uff0c\u5979\u968f\u65f6\u968f \u523b\u90fd\u4f1a\u5c06\u4f60\u5fd8\u4e86\u7684\uff0c\u5c31\u50cf\u5fd8\u4e86\u6211\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u7a81\u53c8\u5927\u7b11\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u65e0\u8bba\u8c01\u4ee5\u4e3a\u5979\u771f\u7684\u5bf9\u4ed6\u597d\uff0c\u5c31\u662f\u5446\u5b50\uff0c\u5446\u5b50\u2014\u2014\u5979\u81f3\u5c11\u5df2\u8ddf\u4e00 \u767e\u591a\u4e2a\u7537\u4eba\u4e0a\u8fc7\u5e8a\u4e86\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u963f\u98de\u7684\u62f3\u5934\u5df2\u4f38\u51fa\uff01 \u3000\u3000\u53ea\u542c\u7830\u7684\u4e00\u58f0\uff0c\u5c11\u5e74\u5df2\u98de\u4e86\u51fa\u53bb\uff0c\u4ef0\u5929\u8dcc\u5728\u9662\u5b50\u91cc\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7a81\u7136\u63a9\u9762\u54ed\u8d77\u6765\uff0c\u54ed\u7740\u9053\uff1a\u6211\u7a76\u7adf\u505a\u9519\u4e86\u4ec0\u9ebd\uff1f\u4e3a\u4ec0\u9ebd\u8fd9\u4e9b\u4eba\u8981\u6765\u51a4\u6789\u6211\uff0c\u8981\u6765 \u5bb3\u6211\u2014\u2014 \u3000\u3000\u963f\u98de\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u6402\u4f4f\u4e86\u5979\uff0c\u9053\uff1a\u53ea\u8981\u6709\u6211\u5728\uff0c\u4f60\u5c31\u4e0d\u7528\u5bb3\u6015\u3002 \u3000\u3000\u826f\u4e45\uff0c\u6797\u4ed9\u513f\u7684\u54ed\u58f0\u624d\u4f4e\u4e86\u4e0b\u6765\uff0c\u8f7b\u6ce3\u9053\uff1a\u5e78\u597d\u8fd8\u6709\u4f60\uff0c\u53ea\u8981\u4f60\u4e86\u89e3\u6211\uff0c\u522b\u4eba\u65e0\u8bba\u5bf9\u6211 \u600e\u6837\u90fd\u6ca1\u5173\u7cfb\u4e86\u3002 \u3000\u3000\u963f\u98de\u76ee\u4e2d\u5e26\u7740\u6012\u706b\uff0c\u54ac\u7259\u9053\uff1a\u4ee5\u5f8c\u82e5\u6709\u4eba\u6562\u518d\u6765\u6b3a\u8d1f\u4f60\uff0c\u6211\u7edd\u4e0d\u9976\u4ed6\uff01 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u65e0\u8bba\u4ec0\u9ebd\u4eba\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u65e0\u8bba\u4ec0\u9ebd\u4eba\u90fd\u4e00\u6837\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5624\u549b\u4e00\u58f0\uff0c\u6402\u5f97\u4ed6\u66f4\u7d27\u3002 \u3000\u3000\u4f46\u5979\u7684\u773c\u775b\u5374\u5728\u671b\u7740\u53e6\u4e00\u4e2a\u4eba\uff0c\u76ee\u4e2d\u975e\u4f46\u5168\u6ca1\u6709\u60b2\u75db\u4e4b\u8272\uff0c\u53cd\u800c\u5145\u6ee1\u4e86\u7b11\u610f\u3002 \u3000\u3000\u9662\u5b50\u91cc\u4e5f\u6709\u4e2a\u4eba\u6b63\u5728\u671b\u7740\u5979\u3002 \u3000\u3000\u8fd9\u4eba\u5c31\u7ad9\u5728\u5012\u4e0b\u53bb\u7684\u90a3\u5c11\u5e74\u8eab\u65c1\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6750\u5f88\u9ad8\u3001\u5f88\u7626\uff0c\u8170\u5e26\u4e0a\u659c\u63d2\u7740\u4e00\u67c4\u5251\uff01 \u3000\u3000\u9662\u5b50\u91cc\u867d\u6709\u706f\u5149\uff0c\u5374\u4e0d\u660e\u4eae\uff0c\u53ea\u6709\u9690\u7ea6\u770b\u51fa\u4ed6\u8138\u4e0a\u6709\u53c1\u6761\u5200\u75a4\u3002 \u3000\u3000\u4f46\u6700\u53ef\u6015\u7684\uff0c\u8fd8\u662f\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u7adf\u662f\u6b7b\u7070\u8272\u7684\uff0c\u65e2\u6ca1\u6709\u60c5\u611f\uff0c\u4e5f\u6ca1\u6709\u751f\u547d\uff01 \u3000\u3000\u4ed6\u51b7\u51b7\u7684\u76ef\u7740\u6797\u4ed9\u513f\uff0c\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u7136\u5f8c\u8f6c\u8fc7\u8eab\uff0c\u5411\u671d\u5357\u7684\u4e00\u6392\u5c4b\u5b50\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u5c31\u6709\u4e24\u4e2a\u4eba\u8dd1\u6765\u5c06\u9662\u5b50\u91cc\u90a3\u5c11\u5e74\u62ac\u8d70\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u8f7b\u6ce3\u58f0\u8fd9\u624d\u5b8c\u5168\u505c\u6b62\u4e86\u3002 \u3000\u3000\u591c\u66f4\u6df1\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u4f20\u51fa\u963f\u98de\u5747\u5300\u7684\u9f3b\u606f\u58f0\uff0c\u4ed6\u663e\u7136\u53c8\u7761\u7740\u5f88\u6c89\u4e86\u2014\u2014\u6797\u4ed9\u513f\u5012\u7ed9\u4ed6\u7684\u4e00\u676f\u8336\u4e4b\u5f8c\uff0c \u4ed6\u5c31\u7acb\u523b\u7761\u7740\u3002 \u3000\u3000\u9662\u5b50\u91cc\u9759\u5f97\u5f88\uff0c\u53ea\u6709\u98ce\u5439\u7740\u68a7\u6850\uff0c\u4f3c\u5728\u53f9\u606f\u3002 \u3000\u3000\u7136\u5f8c\uff0c\u95e8\u5f00\u4e86\u3002 \u3000\u3000\u53ea\u5f00\u4e86\u4e00\u7ebf\uff0c\u4e00\u4e2a\u6084\u6084\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u53c8\u6084\u6084\u7684\u63a9\u8d77\u95e8\u3002\u6084\u6084\u7684\u7a7f\u8fc7\u9662\u5b50\uff0c\u5411\u671d\u5357\u7684\u90a3\u6392 \u5c4b\u5b50\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u6392\u5c4b\u5b50\u91cc\u8fd8\u6709\u4e00\u6247\u7a97\u5b50\uff0c\u91cc\u9762\u706f\u706b\u662f\u4eae\u7740\u7684\u3002 \u3000\u3000\u660f\u9ec4\u7684\u706f\u5149\u4ece\u7a97\u5b50\u91cc\u7167\u51fa\u6765\uff0c\u7167\u5728\u5979\u7684\u8138\u4e0a\u3002 \u3000\u3000\u662f\u6797\u4ed9\u513f\uff0c\u5979\u5df2\u5f00\u59cb\u6572\u95e8\u3002 \u3000\u3000\u53ea\u6572\u4e86\u4e00\u58f0\uff0c\u95e8\u91cc\u5c31\u4f20\u51fa\u4e00\u4e2a\u4f4e\u6c89\u800c\u5636\u54d1\u7684\u58f0\u97f3\uff0c\u51b7\u51b7\u9053\uff1a\u95e8\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8f7b\u8f7b\u4e00\u63a8\uff0c\u95e8\u679c\u7136\u5f00\u4e86\u3002 \u3000\u3000\u65b9\u624d\u7ad9\u5728\u9662\u5b50\u91cc\u7684\u90a3\u4e2a\u4eba\uff0c\u5c31\u4eff\u4f5b\u4e00\u5c0a\u81ea\u4e98\u53e4\u4ee5\u6765\u5c31\u5750\u5728\u90a3\u91cc\u7684\u77f3\u50cf\u3002 \u3000\u3000\u8ddd\u79bb\u8fd1\u4e86\uff0c\u6797\u4ed9\u513f\u624d\u770b\u6e05\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u77b3\u5b54\u5f88\u5927\uff0c\u6240\u4ee5\u5f53\u4ed6\u770b\u7740\u4f60\u7684\u65f6\u5019\uff0c\u597d\u50cf\u5e76\u5728\u770b\u4f60\uff0c\u4ed6\u5e76\u6ca1\u6709\u770b\u4f60\u7684\u65f6\u5019\uff0c\u53c8\u597d\u50cf \u5728\u770b\u4f60\u3002 \u3000\u3000\u8fd9\u53cc\u773c\u775b\u65e2\u4e0d\u660e\u4eae\uff0c\u4e5f\u4e0d\u9510\u5229\uff0c\u4f46\u5374\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u90aa\u6076\u5996\u5f02\u4e4b\u529b\uff0c\u5c31\u8fde\u6797\u4ed9\u513f\u770b\u4e86\u5fc3\u5934 \u90fd\u6709\u4e9b\u53d1\u51b7\uff0c\u4f3c\u4e4e\u4e00\u76f4\u51b7\u5230\u9aa8\u9ad3\u91cc\u3002 \u3000\u3000\u86e4\u5979\u8138\u4e0a\u5374\u662f\u8fd8\u662f\u5e26\u7740\u52a8\u4eba\u7684\u751c\u7b11\u3002 \u3000\u3000\u9047\u5230\u7684\u4eba\u8d8a\u53ef\u6015\uff0c\u5979\u5c31\u7b11\u5f97\u8d8a\u53ef\u7231\uff0c\u8fd9\u662f\u5979\u7528\u6765\u5bf9\u4ed8\u7537\u4eba\u7684\u7b2c\u4e00\u79cd\u6b66\u5668\uff0c\u5979\u5df2\u5c06\u8fd9\u79cd\u6b66 \u5668\u4f7f\u7528\u5f97\u5341\u5206\u719f\u7ec3\uff0c\u5341\u5206\u6709\u6548\u3002 \u3000\u3000\u5979\u7b11\u9053\uff1a\u662f\u8346\u5148\u751f\u5417\uff1f \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u7684\u76ef\u7740\u5979\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u70b9\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u5f97\u66f4\u751c\uff0c\u9053\uff1a\u8346\u5148\u751f\u7684\u5927\u540d\uff0c\u6211\u65e9\u5df2\u542c\u8bf4\u8fc7\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u51b7\u51b7\u7684\u76ef\u7740\u5979\uff0c\u5728\u4ed6\u773c\u4e2d\uff0c\u8fd9\u4f4d\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\u7b80\u76f4\u5c31\u548c\u4e00\u5757\u6728\u5934\u6ca1\u4ec0\u9ebd\u4e24\u6837 \u3002 \u3000\u3000\u8346\u65e0\u547d\u7a81\u7136\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u4f60\u5728\u6211\u9762\u524d\u8bf4\u8bdd\u65f6\uff0c\u6700\u597d\u8bb0\u5f97\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53ea\u8981\u8346\u5148\u751f\u8bf4\u51fa\u6765\uff0c\u6211\u4e00\u5b9a\u4f1a\u8bb0\u7740\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6211\u53ea\u53d1\u95ee\uff0c\u4e0d\u56de\u7b54\uff0c\u4f60\u660e\u767d\u5417\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u660e\u767d\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f46\u6211\u95ee\u7684\u8bdd\uff0c\u4e00\u5b9a\u8981\u6709\u56de\u7b54\uff0c\u800c\u4e14\u8981\u56de\u7b54\u5f97\u5f88\u6e05\u695a\uff0c\u5f88\u7b80\u5355\uff0c\u6211\u4e0d\u559c\u6b22\u542c\u4eba \u5e9f\u8bdd\u2014\u2014\u4f60\u660e\u767d\u5417\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u660e\u767d\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u5c31\u662f\u6797\u4ed9\u513f\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u662f\u4f60\u7ea6\u6211\u4eec\u5728\u8fd9\u91cc\u89c1\u9762\u7684\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u5df2\u66ff\u6211\u4eec\u7ea6\u597d\u4e86\u674e\u5bfb\u6b22\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u4e3a\u4f55\u8981\u8fd9\u6837\u505a\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u77e5\u9053\u4e0a\u5b98\u5e2e\u4e3b\u4e00\u76f4\u5728\u627e\u674e\u5bfb\u6b22\uff0c\u56e0\u4e3a\u674e\u5bfb\u6b22\u603b\u559c\u6b22\u6321\u522b\u4eba\u7684\u8def\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u662f\u60f3\u5e2e\u6211\u4eec\u7684\u5fd9\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u77b3\u5b54\u7a81\u7136\u6536\u7f29\u4e86\u8d77\u6765\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u4e3a\u4f55\u8981\u5e2e\u6211\u4eec\u7684\u5fd9\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u6211\u6068\u674e\u5bfb\u6b22\uff0c\u6211\u60f3\u8981\u4ed6\u7684\u547d\uff01 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u4e3a\u4f55\u4e0d\u81ea\u5df1\u52a8\u624b\u6740\u4ed6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u6740\u4e0d\u4e86\u4ed6\uff0c\u5728\u4ed6\u9762\u524d\uff0c\u6211\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\uff0c\u56e0\u4e3a\u4ed6\u4e00\u773c\u5c31\u80fd\u770b\u7a7f \u522b\u4eba\u7684\u5fc3\u4e8b\uff0c\u4e00\u5200\u5c31\u80fd\u8981\u522b\u4eba\u7684\u547d\uff01 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4ed6\u771f\u6709\u90a3\u9ebd\u5389\u5bb3\uff01 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u9053\uff1a\u4ed6\u5b9e\u5728\u6bd4\u6211\u8bf4\u7684\u8fd8\u8981\u53ef\u6015\uff0c\u60f3\u6740\u4ed6\u7684\u4eba\u90fd\u5df2\u6b7b\u5728\u4ed6\u624b\u4e0a\uff0c\u9664\u4e86\u8346\u5148\u751f\u548c\u4e0a \u5b98\u5e2e\u4e3b\u5916\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u522b\u4eba\u80fd\u6740\u5f97\u6b7b\u4ed6\uff01 \u3000\u3000\u5979\u62ac\u8d77\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u8346\u5148\u751f\u7684\u5251\u6cd5\u867d\u672a\u8fc7\uff0c\u4e5f\u80fd\u60f3\u8c61\u5f97\u5230\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1b\u4f60\u51ed\u4ec0\u9ebd\u80fd\u60f3\u8c61\u5f97\u5230\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5c31\u51ed\u8346\u5148\u751f\u8fd9\u4efd\u6c89\u7740\u548c\u51b7\u9759\uff0c\u6211\u867d\u7136\u4e0d\u4f1a\u5251\uff0c\u5374\u4e5f\u77e5\u9053\u9ad8\u624b\u76f8\u4e89\u65f6\uff0c\u5251\u6cd5\u7684 \u53d8\u5316\u548c\u51fa\u624b\u7684\u5feb\u6162\u5e76\u4e0d\u662f\u6700\u91cd\u8981\u7684\uff0c\u6700\u91cd\u8981\u7684\u5c31\u662f\u6c89\u7740\u548c\u51b7\u9759\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4e3a\u4ec0\u9ebd\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u5251\u6cd5\u62db\u5f0f\u7684\u53d8\u5316\uff0c\u57fa\u672c\u4e0a\u5e76\u6ca1\u6709\u4ec0\u9ebd\u592a\u5927\u7684\u5dee\u5f02\uff0c\u6b66\u529f\u7ec3\u5230\u4e00\u79cd\u9636\u6bb5\u5f8c \uff0c\u51fa\u624b\u7684\u5feb\u6162\u4e5f\u4e0d\u4f1a\u6709\u592a\u5927\u5206\u522b\uff0c\u90a3\u65f6\u5c31\u770b\u8c01\u6bd4\u8f83\u51b7\u9759\uff0c\u8c01\u6bd4\u8f83\u6c89\u7740\uff0c\u8c01\u80fd\u591f\u627e\u51fa\u5bf9\u65b9\u7684\u5f31 \u70b9\uff0c\u8c01\u5c31\u662f\u80dc\u5229\u8005\u3002 \u3000\u3000\u6797\u4ed9\u513f\xd7\u7ef4\u4eba\u7684\u672c\u4e8b\u7684\u786e\u5df2\u5230\u5bb6\u4e86\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5979\u5bf9\u4ed8\u7537\u4eba\u7684\u7b2c\u53c1\u79cd\u6b66\u5668\u3002 \u3000\u3000\u5979\u77e5\u9053\u7537\u4eba\u90fd\u662f\u559c\u6b22\u88ab\u4eba\xd7\u7ef4\u7684\uff0c\u5c24\u5176\u662f\u88ab\u5973\u4eba\xd7\u7ef4\uff0c\u8981\u670d\u4f8d\u4e00\u4e2a\u7537\u4eba\u7684\u5fc3\uff0c\u5973\u4eba\u7684\u4e00 \u53e5\xd7\u7ef4\u8bdd\u5f80\u5f80\u6bd4\u5343\u519b\u4e07\u9a6c\u8fd8\u6709\u6548\u3002 \u3000\u3000\u8346\u65e0\u547d\u9762\u4e0a\u5374\u8fd8\u662f\u8fde\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u9053\uff1a\u4f60\u7ea6\u7684\u65e5\u5b50\u662f\u5341\u6708\u521d\u4e00\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\uff0c\u56e0\u4e3a\u6211\u7b97\u51c6\u8346\u5148\u751f\u548c\u4e0a\u5b98\u5e2e\u4e3b\u5728\u90a3\u5929\u4e00\u5b9a\u53ef\u4ee5\u8d76\u5230\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f46\u4f60\u600e\u77e5\u674e\u5bfb\u6b22\u4e5f\u4e00\u5b9a\u4f1a\u5230\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u77e5\u9053\u4ed6\u4e00\u5b9a\u4f1a\u63a5\u5230\u90a3\u5c01\u4fe1\uff0c\u53ea\u8981\u4ed6\u63a5\u5230\u90a3\u5c01\u4fe1\uff0c\u5c31\u4e00\u5b9a\u4f1a\u53bb\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u6709\u628a\u63e1\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4ed6\u5e76\u4e0d\u6015\u6b7b\uff0c\u56e0\u4e3a\u4ed6\u53cd\u6b63\u4e5f\u6d3b\u4e0d\u957f\u4e86\u3002 \u3000\u3000\u5979\u7b11\u5bb9\u53c8\u6d88\u5931\u4e86\uff0c\u9053\uff1a\u5c31\u56e0\u4e3a\u4ed6\u5df2\u81ea\u77e5\u6d3b\u4e0d\u957f\uff0c\u6240\u4ee5\u624d\u53ef\u6015\uff0c\u4f60\u6b66\u529f\u867d\u7136\u6bd4\u4ed6\u9ad8\uff0c\u548c\u4ed6 \u4ea4\u624b\u65f6\u4e5f\u8981\u5c0f\u5fc3\u4e9b\uff0c\u8fd9\u79cd\u4eba\u52a8\u8d77\u624b\u6765\u5e38\u4f1a\u4e0d\u8981\u547d\u7684\u3002 \u3000\u3000\u5979\u76ee\u4e2d\u5145\u6ee1\u4e86\u5173\u6000\u548c\u4f53\u8d34\uff0c\u8fd9\u6b63\u662f\u5979\u5bf9\u4ed8\u7537\u4eba\u7684\u7b2c\u56db\u79cd\u6b66\u5668\u3002 \u3000\u3000\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\u82e5\u80fd\u5f88\u9002\u5f53\u7684\u7528\u8fd9\u56db\u79cd\u6b66\u5668\u2014\u2014\u4e00\u767e\u4e2a\u7537\u4eba\u4e2d\u6700\u5c11\u4e5f\u6709\u4e5d\u5341\u4e5d\u4e2a\u534a\u8981\u5012 \u5728\u5979\u7684\u811a\u4e0b\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u6797\u4ed9\u513f\u8fd9\u6b21\u9047\u89c1\u7684\u5374\u504f\u504f\u662f\u4f8b\u5916\u2014\u2014\u5979\u9047\u7740\u7684\u975e\u4f46\u4e0d\u662f\u4e2a\u7537\u4eba\uff0c\u7b80\u76f4\u4e0d\u662f\u4e2a\u4eba\uff01 \u3000\u3000\u5e78\u597d\u5979\u8fd8\u6709\u6837\u6700\u6709\u6548\u7684\u6b66\u5668\u3002 \u3000\u3000\u90a3\u662f\u5979\u6700\u5f8c\u7684\u6b66\u5668\uff0c\u4e5f\u662f\u5973\u4eba\u6700\u539f\u59cb\u7684\u4e00\u79cd\u6b66\u5668\u3002\u5973\u4eba\u6709\u65f6\u80fd\u5f81\u670d\u7537\u4eba\uff0c\u5c31\u56e0\u4e3a\u5979\u4eec\u6709 \u8fd9\u79cd\u6b66\u5668\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u6b66\u5668\u5bf9\u8346\u65e0\u547d\u662f\u5426\u4e5f\u540c\u6837\u6709\u6548\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u8fdf\u7591\u7740\u3002 \u3000\u3000\u82e5\u975e\u7edd\u5bf9\u6709\u628a\u63e1\uff0c\u5979\u7edd\u4e0d\u80af\u5c06\u8fd9\u79cd\u6b66\u5668\u8f7b\u6613\u4f7f\u51fa\u6765\u3002 \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u9053\uff1a\u4f60\u8981\u8bf4\u7684\u8bdd\u5df2\u8bf4\u5b8c\u4e86\u9ebd\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u662f\u3002 \u3000\u3000\u8346\u65e0\u547d\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u684c\u5b50\u65c1\uff0c\u80cc\u5bf9\u7740\u5979\uff0c\u7adf\u518d\u4e5f\u4e0d\u770b\u5979\u4e00\u773c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53ea\u6709\u82e6\u7b11\uff0c\u9053\uff1a\u8346\u5148\u751f\u82e5\u6ca1\u6709\u522b\u7684\u5429\u5490\uff0c\u6211\u5c31\u544a\u8f9e\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u4e0d\u7406\u5979\uff0c\u81ea\u6000\u4e2d\u53d6\u51fa\u7c92\u836f\u4e38\uff0c\u5c31\u7740\u8336\u6c34\u541e\u4e0b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e5f\u770b\u4e0d\u51fa\u4ed6\u5728\u5e72\u4ec0\u9ebd\uff0c\u5979\u4e5f\u6ca1\u6cd5\u5b50\u518d\u803d\u4e0b\u589e\uff0c\u53ea\u6709\u8d70\u3002 \u3000\u3000\u4f46\u5979\u8fd8\u672a\u8d70\u5230\u95e8\u53e3\uff0c\u8346\u65e0\u547d\u5ffd\u7136\u9053\uff1b\u542c\u8bf4\u4f60\u5f88\u559c\u6b22\u52fe\u5f15\u7537\u4eba\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u6797\u4ed9\u513f\u6014\u4f4f\u4e86\u3002\u8346\u65e0\u547d\u9053\uff1a\u4f60\u4e00\u8d70\u8fdb\u8fd9\u95f4\u5c4b\u5b50\uff0c\u5c31\u5728\u52fe\u5f15\u6211\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u6211\u559c\u6b22\u80fd\u6c89\u5f97\u4f4f\u6c14\u7684\u7537\u4eba\u3002 \u3000\u3000\u8346\u65e0\u547d\u970d\u7136\u8f6c\u8eab\u9053\uff1a\u90a3\u9ebd\uff0c\u4f60\u73b0\u5728\u4e3a\u4f55\u653e\u5f03\u4e86\uff1f \u3000\u3000\u5979\u7684\u8138\u5df2\u7ea2\u4e86\uff0c\u5782\u9996\u9053\uff1a\u4f60\u7684\u5fc3\u5c31\u50cf\u662f\u94c1\u6253\u7684\uff0c\u6211\u2014\u2014\u6211\u4e0d\u6562\u2014\u2014 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f46\u6211\u7684\u4eba\u5374\u4e0d\u662f\u94c1\u6253\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u53c8\u9053\uff1a\u4f60\u518d\u52fe\u5f15\u6211\uff0c\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\uff0c\u6700\u76f4\u63a5\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ea2\u7740\u8138\u9053\uff1a\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u6559\u6211\uff1f \u3000\u3000\u8346\u65e0\u547d\u6162\u6162\u5411\u5979\u8d70\u4e86\u8fc7\u6765\uff0c\u51b7\u9053\uff1a\u8fd9\u6cd5\u5b50\u4f60\u8fd8\u7528\u5f97\u6211\u6765\u6559\u4f60\u9ebd\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u53cd\u624b\u4e00\u638c\uff0c\u63b4\u5728\u5979\u8138\u4e0a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u6253\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u5012\u5728\u5e8a\u4e0a\uff0c\u8f7b\u8f7b\u7684\u547b\u541f\u7740\u5979\u7684\u8138\u867d\u5df2\u56e0\u75db\u82e6\u800c\u626d\u66f2 \uff0c\u4f46\u76ee\u4e2d\u5374\u5c04\u51fa\u4e86\u72c2\u70ed\u7684\u706b\u82b1\u2014\u2014 \u3000\u3000\u6797\u4ed9\u513f\u8d70\u51fa\u8fd9\u5c4b\u5b50\u7684\u65f6\u5019\uff0c\u5929\u5df2\u5feb\u4eae\u4e86\u3002 \u3000\u3000\u5979\u770b\u6765\u662f\u90a3\u9ebd\u72fc\u72c8\uff0c\u90a3\u9ebd\u75b2\u5026\uff0c\u8fde\u817f\u90fd\u65e0\u6cd5\u62ac\u8d77\uff0c\u4f46\u5979\u7684\u795e\u60c5\u5374\u662f\u8bf4\u4e0d\u51fa\u7684\u6ee1\u8db3\u3001\u5e73\u9759 \u3002 \u3000\u3000\u6bcf\u6b21\u5979\u71c3\u8d77\u963f\u98de\u7684\u706b\u7130\u5f8c\uff0c\u81ea\u5df1\u5fc3\u91cc\u4e5f\u71c3\u8d77\u4e86\u4e00\u56e2\u706b\uff0c\u6240\u4ee5\u5979\u6bcf\u6b21\u90fd\u8981\u627e\u4e00\u4e2a\u4eba\u53d1\u6cc4\uff0c \u5c06\u8fd9\u56e2\u706b\u7184\u706d\u3002 \u3000\u3000\u5979\u559c\u6b22\u88ab\u6298\u78e8\uff0c\u4e5f\u559c\u6b22\u6298\u78e8\u522b\u4eba\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4ef0\u9762\u671b\u7740\u4e1c\u65b9\u7684\u66d9\u8272\uff0c\u9053\uff1a\u4eca\u5929\u5df2\u662f\u4e5d\u6708\u5341\u4e94\u65e5\u4e86\uff0c\u8fd8\u6709\u4e94\u5929\u2014\u2014\u53ea\u6709\u4e94\u5929\u2014\u2014 \u3000\u3000\u5979\u5634\u89d2\u4e0d\u7981\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f60\u6700\u591a\u4e5f\u4e0d\u8fc7\u53ea\u80fd\u518d\u6d3b\u4e94\u5929\u4e86\uff01 \u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u751f\u6b7b\u4e4b\u95f4>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u4e09\u7ae0\u3000\u751f\u6b7b\u4e4b\u95f4 \u3000\u3000\u674e\u5bfb\u6b22\u5728\u96d5\u7740\u6728\u5934\u3002\u90a3\u7a7f\u7ea2\u8863\u7684\u5c0f\u59d1\u5a18\u4e00\u76f4\u5728\u65c1\u75f4\u75f4\u7684\u77a7\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u4f60\u7a76\u7adf\u96d5\u4ec0 \u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u4f60\u770b\u4e0d\u51fa\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u6211\u770b\u4f60\u597d\u50cf\u662f\u60f3\u96d5\u4e00\u4e2a\u4eba\u7684\u50cf\uff0c\u4f46\u4e3a\u4ec0\u4e48\u4f60\u6bcf\u6b21\u90fd\u4e0d\u5b8c\u6210\u5b83\u5462\uff1f\u4e5f\u597d\u8ba9\u6211\u770b \u770b\u4f60\u96d5\u7684\u8fd9\u4eba\u6f02\u4e0d\u6f02\u4eae\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u7b11\u5bb9\u6d88\u5931\u4e86\uff0c\u4e0d\u505c\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u56e0\u4e3a\u4e0d\u613f\u88ab\u4eba\u770b\u5230\u4ed6\u96d5\u7684\u662f\u8c01\uff0c\u6240\u4ee5\u6bcf\u6b21\u90fd\u6ca1\u6709\u5c06\u96d5\u50cf\u5b8c\u6210\uff0c\u867d\u7136\u4ed6\u4e5f\u53ef\u4ee5\u96d5\u53e6\u4e00\u4e2a \u4eba\u7684\u50cf\uff0c\u4f46\u4ed6\u7684\u624b\u5374\u5df2\u4eff\u4f5b\u4e0d\u542c\u4ed6\u7684\u8bdd\uff0c\u5c31\u7b97\u4ed6\u96d5\u7684\u4e0d\u662f\u5979\uff0c\u96d5\u51fa\u6765\u7684\u8f6e\u5ed3\u4e5f\u50cf\u662f\u5979\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e0\u6cd5\u4e0d\u60f3\u5979\u3002 \u3000\u3000\u7a97\u5916\u7684\u5929\u8272\u5df2\u6e10\u6e10\u9eef\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u62ac\u8d77\u624b\uff0c\u624b\u91cc\u7684\u5200\u950b\u5728\u706f\u5149\u4e0b\u6563\u53d1\u7740\u6de1\u6de1\u7684\u9752\u5149\uff0c\u5149\u8292\u5728\u95ea\u52a8\u7740\u3002 \u3000\u3000\u201c\u96be\u9053\u6211\u7684\u624b\u771f\u5728\u53d1\u6296\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u6e10\u6e10\u5f80\u4e0b\u6c89\uff0c\u4ed6\u5c31\u6015\u6709\u8fd9\u4e48\u4e00\u5929\uff0c\u4e0d\u559d\u9152\u624b\u5c31\u4f1a\u6296\uff0c\u4e00\u53cc\u98a4\u6296\u7684\u624b\u600e\u80fd\u53d1\u5f97 \u51fa\u81f4\u4eba\u6b7b\u547d\u7684\u98de\u5200\uff1f \u3000\u3000\u4ed6\u7528\u529b\u63e1\u7740\u5200\u67c4\uff0c\u6307\u8282\u90fd\u5df2\u56e0\u7528\u529b\u800c\u53d1\u767d\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u5782\u4e0b\u624b\uff0c\u671b\u7740\u7a97\u5916\u7684\u5929\u8272\uff0c\u9053\uff1a\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u4e5d\u6708\u4e09\u5341\u65e5\uff0c\u660e\u5929\u5c31\u662f\u521d\u4e00\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u95ed\u8d77\u773c\u775b\uff0c\u9053\uff1a\u90ed\u5148\u751f\u5462\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u4ed6\u8bf4\u4ed6\u8981\u5230\u9547\u4e0a\u53bb\u8d70\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5782\u9996\u671b\u7740\u81ea\u5df1\u7684\u5200\u950b\uff0c\u5ffd\u7136\u7528\u529b\u523b\u4e0b\u4e86\u4e00\u5200\u3002 \u3000\u3000\u4ed6\u523b\u5f97\u5f88\u5feb\uff0c\u672c\u5df2\u5c06\u53d8\u6210\u7684\u4eba\u50cf\uff0c\u5f88\u5feb\u5c31\u5b8c\u6210\u4e86\uff0c\u90a3\u6e05\u79c0\u7684\u8f6e\u90ed\uff0c\u633a\u76f4\u7684\u9f3b\u5b50\uff0c\u770b\u6765\u8fd8 \u662f\u90a3\u4e48\u5e74\u8f7b\u3002 \u3000\u3000\u4f46\u4eba\u5462\uff1f\u4eba\u5df2\u8001\u4e86\u3002 \u3000\u3000\u4eba\u5728\u5fe7\u6101\u4e2d\uff0c\u603b\u662f\u8001\u5f97\u7279\u522b\u5feb\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u75f4\u75f4\u7684\u671b\u7740\u8fd9\u4eba\u50cf\uff0c\u76ee\u5149\u518d\u4e5f\u820d\u4e0d\u5f97\u79fb\u5f00\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ece\u4eca\u540e\uff0c\u5df2\u518d\u4e5f\u89c1\u4e0d\u7740\u5979 \u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u8fd9\u4eba\u50cf\u597d\u7f8e\uff0c\u662f\u8c01\u5440\uff1f\u662f\u4f60\u7684\u60c5\u4eba\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u5df2\u56de\u6765\u4e86\uff0c\u624b\u91cc\u6258\u7740\u4e2a\u76d8\u5b50\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u5230\u4e86\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\uff1a\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u662f\u8c01\uff0c\u4e5f\u8bb8\u662f\u5929\u4e0a\u7684\u4ed9\u5973\u5427\u2014\u2014 \u3000\u3000\u5c0f\u59d1\u5a18\u7728\u7740\u773c\uff0c\u6447\u7740\u5934\u9053\uff1a\u4f60\u9a97\u6211\uff0c\u5929\u4e0a\u7684\u4ed9\u5973\u90fd\u5f88\u5feb\u6d3b\uff0c\u5979\u770b\u6765\u5374\u662f\u90a3\u4e48\u5fe7\u4f24\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5730\u4e0a\u65e2\u7136\u6709\u8bb8\u591a\u5feb\u6d3b\u7684\u4eba\uff0c\u5929\u4e0a\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6709\u5fe7\u4f24\u7684\u4ed9\u5b50\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u53ef\u662f\u4f60\u5374\u5e76\u4e0d\u5feb\u6d3b\uff0c\u56e0\u4e3a\u4f60\u559c\u6b22\u5979\uff0c\u5374\u5f97\u4e0d\u5230\u5979\uff0c\u5bf9\u4e0d\u5bf9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8138\u8272\u53d8\u4e86\uff0c\u4e00\u9897\u5fc3\u4e5f\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u4f60\u7528\u4e0d\u7740\u518d\u7792\u6211\uff0c\u770b\u4f60\u7684\u8138\u8272\uff0c\u6211\u5c31\u77e5\u9053\u731c\u7684\u4e0d\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u5df2\u662f\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u65e2\u7136\u662f\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\uff0c\u4f60\u4e3a\u4f55\u76f4\u5230\u73b0\u5728\u8fd8\u5fd8\u4e0d\u4e86\u5979\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\u9053\uff1a\u7b49\u4f60\u6d3b\u5230\u6211\u8fd9\u6837\u7684\u5e74\u7eaa\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\u4f60\u6700\u60f3\u5fd8\u8bb0\u7684\u4eba\uff0c\u4e5f\u6b63\u662f\u4f60 \u6700\u5fd8\u4e0d\u4e86\u7684\uff01\u2014\u2014 \u3000\u3000\u5c0f\u59d1\u5a18\u70b9\u4e86\u70b9\u5934\uff0c\u6162\u6162\u7684\u5480\u56bc\u7740\u4ed6\u8fd9\u4e24\u53e5\u8bdd\u4e2d\u7684\u6ecb\u5473\uff0c\u4f3c\u4e4e\u6709\u4e9b\u75f4\u4e86\uff0c\u8fde\u624b\u91cc\u6258\u7740\u7684\u76d8 \u5b50\u90fd\u5fd8\u8bb0\u653e\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u5e7d\u5e7d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u522b\u4eba\u90fd\u8bf4\u4f60\u53c8\u51b7\u9177\uff0c\u53c8\u65e0\u60c5\uff0c\u4f46\u4f60\u5374\u4e0d\u662f\u90a3\u6837\u7684\u4eba \u5440\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u770b\u6211\u662f\u4e2a\u600e\u6837\u7684\u4eba\u5462\uff1f \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u6211\u770b\u4f60\u65e2\u591a\u6101\u3001\u53c8\u5584\u611f\uff0c\u6b63\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u7684\u591a\u60c5\u79cd\u5b50\uff0c\u4f60\u82e5\u771f\u7684\u559c\u6b22\u4e0a\u4e00\u4e2a \u5973\u4eba\uff0c\u53ef\u771f\u662f\u90a3\u5973\u4eba\u7684\u795e\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6211\u8fd8\u672a\u559d\u9152\uff0c\u6211\u559d\u4e86\u9152\u540e\uff0c\u5c31\u4f1a\u53d8\u5f97\u9ebb\u6728\u4e86\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u7b11\u4e86\u7b11\u9053\uff1a\u90a3\u4e48\u6211\u8fd8\u662f\u8d76\u5feb\u559d\u4e9b\u9152\u5427\uff0c\u6211\u4e5f\u60f3\u53d8\u5f97\u9ebb\u6728\u4e9b\uff0c\u4e5f\u514d\u5f97\u82e6\u607c\u3002 \u3000\u3000\u5979\u7a81\u7136\u62ff\u8d77\u4e86\u76d8\u5b50\u4e0a\u7684\u9152\u58f6\uff0c\u5c06\u534a\u58f6\u9152\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8d8a\u662f\u5e74\u8f7b\u7684\u4eba\uff0c\u9152\u559d\u548c\u8d8a\u5feb\uff0c\u56e0\u4e3a\u559d\u9152\u4e5f\u9700\u8981\u52c7\u6c14\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u7684\u8138\u5df2\u7ea2\u5982\u6843\u82b1\uff0c\u5ffd\u7136\u77aa\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u4f60\u53eb\u674e\u5bfb\u6b22\uff0c\u4f60\u53ef\u77e5\u6211\u53eb\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u6ca1\u6709\u8bf4\uff0c\u6211\u600e\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u4f60\u6ca1\u6709\u95ee\u6211\uff0c\u6211\u4e3a\u4f55\u8981\u8bf4\uff1f \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u63a5\u7740\u9053\uff1a\u4f60\u4e0d\u4f46\u6ca1\u6709\u95ee\u6211\u7684\u540d\u5b57\uff1f\u4e5f\u6ca1\u6709\u95ee\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u600e\u4f1a\u4e00\u4e2a\u4eba\u7559\u5728 \u8fd9\u91cc\uff1f\u522b\u7684\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4f60\u4ec0\u4e48\u90fd\u4e0d\u95ee\uff0c\u662f\u4e0d\u662f\u89c9\u5f97\u4f60\u5df2\u5feb\u6b7b\u4e86\uff0c\u6240\u4ee5\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u60f3\u77e5\u9053 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u9189\u4e86\uff0c\u5973\u5b69\u5b50\u559d\u9189\u4e86\uff0c\u6700\u597d\u8d76\u5feb\u53bb\u7761\u89c9\u3002 \u3000\u3000\u5c0f\u59d1\u5a18\u9053\uff1a\u4f60\u4e0d\u60f3\u542c\uff0c\u662f\u4e0d\u662f\uff0c\u6211\u504f\u8981\u544a\u8bc9\u4f60\uff0c\u6211\u6ca1\u6709\u7239\uff0c\u4e5f\u6ca1\u6709\u5a18\uff0c\u6240\u4ee5\u4e5f\u4e0d\u77e5\u9053\u81ea \u5df1\u59d3\u4ec0\u4e48\uff0c\u4e94\u5e74\u524d\u5c0f\u59d0\u628a\u6211\u4e70\u4e86\u4e0b\u6765\uff0c\u6240\u4ee5\u6211\u5c31\u53eb\u59d3\u6797\uff0c\u5c0f\u59d0\u559c\u6b22\u53eb\u6211\u94c3\u94c3\uff0c\u6240\u4ee5\u6211\u5c31\u53eb\u505a \u6797\u94c3\u94c3\u2014\u2014 \u3000\u3000\u5979\u5403\u5403\u7684\u7b11\u7740\uff0c\u63a5\u7740\u9053\uff1a\u6797\u94c3\u94c3\uff0c\u4f60\u8bf4\u8fd9\u540d\u5b57\u597d\u4e0d\u597d\uff1f\u5c31\u8c61\u662f\u4eba\u94c3\uff0c\u522b\u4eba\u6447\u4e00\u6447\uff0c\u6211\u5c31 \u6797\u94c3\u94c3\u7684\u54cd\uff0c\u522b\u4eba\u4e0d\u6447\uff0c\u6211\u5c31\u4e0d\u80fd\u54cd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u624d\u77e5\u9053\u8fd9\u5c0f\u59d1\u5a18\u4e5f\u6709\u6bb5\u8f9b\u9178\u7684\u5f80\u4e8b\uff0c\u5e76\u4e0d\u5982\u5979\u8868\u9762\u770b\u6765\u90a3\u4e48\u5f00\u5fc3\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6211\u603b\u662f\u9047\u4e0d\u4e00\u4e2a\u771f\u6b63\u5feb\u4e50\u7684\u4eba\u5462\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u4eba\u7559\u5728\u8fd9\u91cc\uff0c\u544a\u8bc9\u4f60\u4e5f\u6ca1\u5173\u7cfb\uff0c\u5c0f\u59d0\u53eb\u6211\u7559\u5728\u8fd9\u91cc\uff0c\u5c31 \u662f\u8981\u6211\u770b\u7740\u4f60\uff0c\u6bcf\u5929\u60f3\u6cd5\u5b50\u8ba9\u4f60\u559d\u9152\uff0c\u8ba9\u4f60\u7684\u624b\u53d1\u6296\uff0c\u5979\u8bf4\u53ea\u8981\u4f60\u7684\u624b\u4e00\u5f00\u59cb\u53d1\u6296\uff0c\u4f60\u5c31\u6d3b \u4e0d\u957f\u4e86\u3002 \u3000\u3000\u5979\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u8c61\u662f\u5728\u7b49\u7740\u4ed6\u53d1\u813e\u6c14\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u53ea\u662f\u4e00\u7b11\uff0c\u9053\uff1a\u5341\u5e74\u524d\u5c31\u5df2\u6709\u4eba\u8bf4\u6211\u5feb\u6b7b\uff0c\u4f46\u6211\u5374\u8fd8\u662f\u6d3b\u5230\u73b0\u5728\uff0c\u4f60\u8bf4\u5947\u602a \u4e0d\u5947\u602a\uff1f \u3000\u3000\u94c3\u94c3\u77aa\u7740\u773c\uff0c\u9053\uff1a\u6211\u5df2\u544a\u8bc9\u4f60\uff0c\u6211\u662f\u5728\u5bb3\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u9a82\u6211\uff1f \u3000\u3000\u4ed6\u957f\u53f9\u9053\uff1a\u6bcf\u4e2a\u4eba\u90fd\u6d3b\u5728\u4e16\u4e0a\uff0c\u90fd\u96be\u514d\u8981\u505a\u522b\u4eba\u7684\u94c3\u94db\uff0c\u4f60\u662f\u522b\u4eba\u7684\u94c3\u94db\uff0c\u6211\u53c8\u4f55\u5c1d\u4e0d \u662f\uff0c\u90a3\u6447\u94c3\u7684\u4eba\u81ea\u5df1\u8eab\u4e0a\u8bf4\u4e0d\u5b9a\u4e5f\u6709\u6839\u7ef3\u5b50\u88ab\u522b\u4eba\u62ce\u5728\u624b\u91cc\u3002 \u3000\u3000\u94c3\u94c3\u77aa\u7740\u773c\u9053\uff1a\u6211\u73b0\u5728\u624d\u53d1\u89c9\u4f60\u8fd9\u4eba\u771f\u4e0d\u9519\uff0c\u5c0f\u59d0\u4e3a\u4ec0\u4e48\u504f\u504f\u60f3\u8981\u4f60\u6b7b\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u7b11\u9053\uff1a\u4e00\u5fc3\u60f3\u522b\u4eba\u6b7b\u7684\u4eba\uff0c\u81ea\u5df1\u4e5f\u8fdf\u65e9\u8981\u6b7b\u7684\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f46\u6709\u4e9b\u4eba\u6b7b\u4e86\uff0c\u5927\u5bb6\u53cd\u800c\u4f1a\u89c9\u5f97\u5f88\u5f00\u5fc3\uff0c\u6709\u4e9b\u4eba\u6b7b\u4e86\uff0c\u5927\u5bb6\u90fd\u96be\u514d\u8981\u6d41\u6cea\u2014\u2014 \u3000\u3000\u5979\u5782\u4e0b\u5934\uff0c\u63a5\u7740\u9053\uff1a\u4f60\u82e5\u6b7b\u4e86\uff0c\u6211\u8bf4\u4e0d\u5b9a\u4e5f\u4f1a\u6d41\u6cea\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u56e0\u4e3a\u6211\u4eec\u5df2\u7ecf\u662f\u670b\u53cb\u2014\u2014\u81f3\u5c11\u6211\u4eec\u5df2\u8ba4\u8bc6\u4e86\u8bb8\u591a\u5929\u3002 \u3000\u3000\u94c3\u6447\u5934\u9053\uff1a\u90a3\u5012\u4e0d\u89c1\u5f97\uff0c\u6211\u8ba4\u8bc6\u90a3\u4f4d\u90ed\u5148\u751f\u6bd4\u4f60\u4e45\u5f97\u591a\uff0c\u4ed6\u82e5\u6b7b\u4e86\uff0c\u6211\u5c31\u7edd\u4e0d\u4f1a\u6d41\u4e00\u6ef4 \u773c\u6cea\uff01 \u3000\u3000\u5979\u81ea\u5df1\u7b11\u4e86\u7b11\uff0c\u53c8\u8865\u5145\u9053\uff1a\u56e0\u4e3a\u6211\u82e5\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u6d41\u6cea\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u8ba4\u4e3a\u4ed6\u7684\u5fc3\u80a0\u5f88\u786c\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u82e5\u771f\u7684\u8fd9\u4e48\u60f3\uff0c\u4f60\u5c31\u9519\u4e86\uff0c\u6709\u4e9b\u4eba\u7684\u8868\u9762\u770b\u6765\u867d\u7136\u5f88\u51b7\u9177\uff0c\u5176\u5b9e\u662f\u4e2a\u6709\u8840\u6027 \uff0c\u591f\u4e49\u6c14\u7684\u670b\u53cb\uff0c\u8d8a\u662f\u4e0d\u80af\u8f7b\u6613\u5c06\u771f\u60c5\u6d41\u9732\u51fa\u6765\u7684\u4eba\uff0c\u4ed6\u7684\u60c5\u611f\u5f80\u5f80\u5c31\u8d8a\u771f\u631a\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u50cf\u662f\u6709\u5f88\u591a\u611f\u89e6\uff0c\u7adf\u672a\u53d1\u89c9\u90ed\u5d69\u9633\u7ad9\u5728\u95e8\u5916\u5df2\u5f88\u4e45\u2014\u2014\u4ed6\u7684\u786e\u662f\u4e2a\u4e0d\u5bb9\u6613\u52a8\u60c5\u611f \u7684\u4eba\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u8fd8\u662f\u9759\u9759\u7684\u7ad9\u5728\u95e8\u540e\uff0c\u9762\u4e0a\u8fde\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u9633\u5149\u5f88\u65e9\u5c31\u7167\u4eae\u4e86\u5927\u5730\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9192\u5f97\u66f4\u65e9\uff0c\u4ed6\u51e0\u4e4e\u6839\u672c\u5c31\u6ca1\u6709\u7761\u7740\u8fc7\u3002 \u3000\u3000\u5929\u6ca1\u4eae\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7528\u51b7\u6c34\u6d17\u4e86\u6fa1\uff0c\u5c06\u987b\u53d1\u4e5f\u6d17\u5e72\u51c0\u4e86\uff0c\u6362\u4e0a\u4e86\u4e09\u5929\u524d\u4ed6\u81ea\u5df1\u4ece\u9547\u4e0a\u4e70 \u7684\u4e00\u5957\u9752\u5e03\u8863\u670d\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6750\u65e2\u4e0d\u80d6\uff0c\u4e5f\u4e0d\u7626\uff0c\u6240\u4ee5\u867d\u7136\u4e70\u7684\u662f\u5957\u5f88\u7c97\u7cd9\u7684\u8863\u670d\uff0c\u4f46\u7a7f\u5728\u4ed6\u8eab\u4e0a\u5374\u5f88\u5408\u8eab\u3002 \u3000\u3000\u73b0\u5728\uff0c\u9762\u5bf9\u7740\u7a97\u5916\u7684\u9633\u5149\uff0c\u4ed6\u89c9\u5f97\u7cbe\u795e\u597d\u591a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4eca\u5929\u662f\u4e2a\u5f88\u7279\u522b\u7684\u65e5\u5b50\u3002 \u3000\u3000\u5230\u4e86\u4eca\u5929\u665a\u4e0a\uff0c\u4ed6\u8bf4\u4e0d\u5b9a\u5df2\u4e0d\u518d\u6d3b\u5728\u8fd9\u4e16\u4e0a\uff0c\u4f46\u4ed6\u6d3b\u7740\u65f6\u65e2\u7136\u662f\u5e72\u5e72\u51c0\u51c0\u7684\uff0c\u6b7b\uff0c\u4e5f\u5f97 \u5e72\u5e72\u51c0\u51c0\u7684\u6b7b\uff01 \u3000\u3000\u4eca\u5929\u8fd9\u4e00\u6218\uff0c\u4ed6\u7684\u80dc\u7b97\u5e76\u4e0d\u5927\uff0c\u80fd\u6d3b\u7740\u7684\u673a\u4f1a\u5b9e\u5728\u5f88\u5c11\uff0c\u4f46\u53ea\u8981\u8fd8\u6709\u4e00\u5206\u5e0c\u671b\uff0c\u4ed6\u5c31\u7edd \u4e0d\u653e\u5f03\uff01 \u3000\u3000\u4ed6\u4e0d\u6015\u6b7b\uff0c\u5374\u4e5f\u4e0d\u613f\u6b7b\u5728\u4e00\u53cc\u80ae\u810f\u7684\u624b\u4e0b\u3002 \u3000\u3000\u4ed6\u7528\u4e00\u6761\u9752\u5e03\u5e26\u675f\u8d77\u4e86\u5934\u53d1\uff0c\u6b63\u51c6\u5907\u522e\u8138\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u4f60\u7684\u5934\u8111\u8fd8\u8fd9\u4e48\u4e71\uff0c\u600e\u4e48\u80fd\u53bb\u4f1a\u4f73\u4eba\uff1f\u6211\u518d\u66ff\u4f60\u68b3\u68b3\u5427\u3002 \u3000\u3000\u94c3\u94c3\u4e0d\u77e5\u4f55\u65f6\u8d70\u4e86\u8fdb\u6765\uff0c\u773c\u775b\u7ea2\u7ea2\u7684\uff0c\u4f3c\u4e4e\u8fd8\u5bbf\u9152\u672a\u9192\uff0c\u53c8\u4f3c\u4e4e\u6628\u591c\u66fe\u7ecf\u5077\u5077\u7684\u54ed\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u7a81\u7136\u95f4\u53c8\u60f3\u8d77\u4e86\u5341\u4f59\u5e74\u7684\u4e8b\u3002 \u3000\u3000\u90a3\u5929\uff0c\u5929\u6c14\u4e5f\u6b63\u548c\u4eca\u5929\u540c\u6837\u6674\u6717\uff0c\u7a97\u5916\u7684\u83ca\u82b1\u5f00\u5f97\u6b63\u8273\uff0c\u4ed6\u5750\u5728\u5c0f\u697c\u7a97\u524d\uff0c\u4e5f\u6709\u4e2a\u4eba\u5728 \u66ff\u4ed6\u68b3\u5934\u53d1\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u4f3c\u4e4e\u8fd8\u80fd\u611f\u89c9\u5230\u90a3\u53cc\u624b\u7684\u7ec6\u5fc3\u548c\u6e29\u67d4\u3002 \u3000\u3000\u90a3\u5929\uff0c\u4ed6\u4e5f\u662f\u6b63\u51c6\u5907\u52a8\u8eab\u8fdc\u884c\u4e86\uff0c\u6240\u4ee5\u5979\u68b3\u5f97\u7279\u522b\u6162\u3002 \u3000\u3000\u5979\u6162\u6162\u7684\u68b3\u7740\uff0c\u4f3c\u4e4e\u60f3\u7559\u4f4f\u4ed6\uff0c\u591a\u7559\u4e00\u523b\u4e5f\u662f\u597d\u7684\uff0c\u68b3\u5230\u6700\u540e\u65f6\uff0c\u5979\u5a46\u5a46\u5c31\u4e0d\u7981\u6ef4\u5728\u4ed6 \u5934\u53d1\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u90a3\u6b21\u8fdc\u884c\u56de\u6765\u65f6\uff0c\u4ed6\u9047\u7740\u4e86\u5f3a\u654c\uff0c\u51e0\u4e4e\u4e27\u547d\uff0c\u591a\u4e8f\u9f99\u5578\u4e91\u6551\u4e86\u4ed6\uff0c\u8fd9\u4e5f\u662f\u4ed6\u6c38\u8fdc\u5fd8 \u4e0d\u4e86\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5fd8\u4e86\u9f99\u5578\u4e91\u867d\u6551\u4e86\u4ed6\u4e00\u6b21\uff0c\u5374\u6bc1\u4e86\u4ed6\u4e00\u751f\u2014\u2014\u6709\u4e9b\u4eba\u4e3a\u4ec0\u4e48\u6c38\u8fdc\u53ea\u8bb0\u5f97\u522b\u4eba\u7684\u597d \u5904\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u7740\u773c\u775b\uff0c\u82e6\u7b11\u9053\uff1a\u90a3\u5929\u6211\u8d70\u4e86\u540e\u603b\u7b97\u8fd8\u56de\u53bb\u4e86\uff0c\u4eca\u65e5\u6211\u4e00\u53bb\u4e4b\u540e\uff0c\u8fd8\u80fd\u6d3b\u7740\u56de \u6765\u5417\uff1f\u90a3\u4e00\u6b21\u6211\u82e5\u5c31\u5df2\u4e00\u53bb\u4e0d\u8fd4\uff0c\u5c82\u975e\u8fd8\u597d\u5f97\u591a\uff1f\u2014\u2014 \u3000\u3000\u4ed6\u4e0d\u613f\u518d\u60f3\u4e0b\u53bb\uff0c\u6162\u6162\u5c06\u773c\u5e18\u5f20\u5f00\u4e00\u7ebf\uff0c\u5ffd\u7136\u611f\u89c9\u5230\u73b0\u5728\u6b63\u66ff\u4ed6\u68b3\u7740\u5934\u53d1\u7684\u4e00\u53cc\u624b\uff0c\u5979 \u68b3\u5f97\u90a3\u4e48\u6162\uff0c\u90a3\u4e48\u6e29\u67d4\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u56de\u8fc7\u5934\uff0c\u5c31\u53d1\u89c9\u6709\u4e00\u7c92\u6676\u83b9\u7684\u6cea\u73e0\u4e5f\u6b63\u4ece\u94c3\u94c3\u7684\u8138\u4e0a\u5f80\u4e0b\u6d41\u843d\uff0c\u7ec8\u4e8e\u4e5f\u6ef4\u843d\u5728\u4ed6 \u5934\u53d1\u4e0a\u3002 \u3000\u3000\u540c\u6837\u6e29\u67d4\u7684\u624b\uff0c\u540c\u6837\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4eff\u4f5b\u53c8\u56de\u5230\u5341\u4f59\u5e74\u524d\u90a3\u9633\u5149\u540c\u6837\u70c2\u707f\u7684\u65e9\u4e0a\uff0c\u604d\u604d\u60da\u60da\u62c9\u4f4f\u4e86\u5979\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a \u4f60\u54ed\u4e86\uff1f \u3000\u3000\u94c3\u94c3\u7ea2\u4e86\u8138\uff0c\u626d\u8f6c\u5934\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u6211\u77e5\u9053\u4f60\u7684\u7ea6\u4f1a\u5c31\u662f\u4eca\u5929\uff0c\u6240\u4ee5\u624d\u4f1a\u6253\u626e\u5f97\u8fd9\u4e48\u6f02 \u4eae\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8bf4\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u5df2\u53d1\u73b0\u8fd9\u53cc\u624b\u6bd5\u7adf\u4e0d\u662f\u5341\u5e74\u524d\u7684\u90a3\u53cc\u624b\uff0c\u5341\u5e74\u524d\u7684\u65f6\u5149\u4e5f\u6c38\u8fdc \u56de\u4e0d\u6765\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u63a5\u7740\u9053\uff1a\u4f60\u5c31\u8981\u53bb\u4f1a\u4f60\u7684\u4f73\u4eba\u4e86\uff0c\u6211\u5fc3\u91cc\u5f53\u7136\u96be\u53d7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u653e\u4e0b\u4e86\u5979\u7684\u624b\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4f60\u8fd8\u662f\u4e2a\u5b69\u5b50\uff0c\u96be\u53d7\u7a76\u7adf\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4f60\u73b0\u5728 \u6839\u672c\u8fd8\u4e0d\u61c2\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6211\u4ee5\u524d\u4e5f\u8bb8\u8fd8\u4e0d\u61c2\uff0c\u73b0\u5728\u5374\u5df2\u61c2\u4e86\uff0c\u6628\u5929\u4e5f\u8bb8\u8fd8\u4e0d\u61c2\uff0c\u4eca\u5929\u5df2\u61c2\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u4f60\u4e00\u5929\u4e4b\u4e2d\u5c31\u957f\u5927\u4e86\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u5f53\u7136\uff0c\u6709\u4eba\u5728\u4e00\u591c\u95f4\u5c31\u8001\u5f97\u8fde\u5934\u53d1\u90fd\u5b8c\u5168\u767d\u4e86\uff0c\u8fd9\u6545\u4e8b\u4f60\u96be\u9053\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u662f\u4e3a\u4e86\u81ea\u5df1\u7684\u751f\u6b7b\u800c\u5fe7\u8651\uff0c\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u6211\u662f\u4e3a\u4e86\u4f60\u2014\u2014\u4f60\u4eca\u5929\u4e00\u53bb\uff0c\u8fd8\u4f1a\u56de\u6765\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u957f\u957f\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u4f60\u5df2\u77e5\u9053\u6211\u4eca\u5929\u53bb\u4f1a\u7684\u662f\u8c01\u4e86\uff1f \u3000\u3000\u94c3\u94c3\u6c89\u91cd\u7684\u70b9\u4e86\u5934\uff0c\u5c06\u4ed6\u7684\u5934\u53d1\u7406\u53d1\u4e00\u675f\uff0c\u7528\u90a3\u6761\u9752\u5e03\u5e26\u624e\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u6211\u77e5\u9053\u4f60\u65e0\u8bba \u5982\u4f55\u4e00\u5b9a\u8981\u53bb\u7684\uff0c\u8c01\u4e5f\u7559\u4e0d\u4f4f\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u4f60\u957f\u5927\u540e\u5c31\u4f1a\u77e5\u9053\uff0c\u6709\u4e9b\u4e8b\u4f60\u975e\u505a\u4e0d\u53ef\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u826f\u4e45\uff0c\u9762\u4e0a\u9732\u51fa\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u9053\uff1a\u6211\u5e76\u6ca1\u6709\u4e3a\u5979\u7559\u4e0b\u6765\u2014\u2014\u6211\u4ece\u6765\u6ca1\u6709\u4e3a\u5979 \u505a\u8fc7\u4efb\u4f55\u4e8b\uff0c\u6211\u2014\u2014 \u3000\u3000\u4ed6\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u8be5\u8d70\u4e86\u2014\u2014 \u3000\u3000\u8fd9\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90ed\u5d69\u9633\u5df2\u8d70\u4e86\u8fdb\u6765\uff0c\u5927\u58f0\u9053\uff1a\u6211\u521a\u56de\u6765\uff0c\u4f60\u5c31\u8981\u8d70\u4e86\u4e48\uff1f \u3000\u3000\u4ed6\u624b\u91cc\u63d0\u7740\u74f6\u9152\uff0c\u4eba\u8fd8\u672a\u8d70\u8fdb\u5c4b\u5b50\uff0c\u5df2\u6709\u4e00\u9635\u9152\u6c14\u6251\u9f3b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u539f\u6765\u90ed\u5144\u591c\u665a\u7adf\u5728\u4e0e\u4eba\u4f5c\u957f\u591c\u4e4b\u996e\uff0c\u4e3a\u4f55\u4e5f\u4e0d\u6765\u901a\u77e5\u6211\u4e00\u58f0\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5927\u7b11\u9053\uff1a\u6709\u65f6\u4e24\u4e2a\u4eba\u5bf9\u996e\u624d\u597d\uff0c\u591a\u4e00\u4eba\u5c31\u592a\u6324\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u538b\u4f4e\u8bed\u58f0\uff0c\u4e00\u53cc\u624b\u642d\u7740\u674e\u5bfb\u6b22\u80a9\u5934\uff0c\u9053\uff1a\u5c0f\u5f1f\u5fc3\u60c5\u4e0d\u597d\u65f6\u559c\u6b22\u505a\u4ec0\u4e48\u4e8b\uff0c\u4f60\u603b\u8be5 \u77e5\u9053\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u539f\u6765\u2014\u2014 \u3000\u3000\u4ed6\u4e24\u4e2a\u5b57\u521a\u8bf4\u51fa\uff0c\u90ed\u5d69\u9633\u7684\u624b\u5df2\u95ea\u7535\u822c\u70b9\u4e86\u4ed6\u4e03\u5904\u7a74\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u4eba\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u94c3\u94c3\u5927\u60ca\u5931\u58f0\uff0c\u8d76\u8fc7\u53bb\u6276\u4f4f\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u90ed\u5d69\u9633\u7684\u9152\u610f\u5df2\u5b8c\u5168\u6e05\u9192\uff0c\u4e00\u5f20\u8138\u7acb\u523b\u53c8\u53d8\u5f97\u5982\u5ca9\u77f3\u822c\u51b7\u9177\uff0c\u6c89\u7740\u8138\u9053\uff1a \u4ed6\u9192\u6765\u65f6\u4f60\u5bf9\u4ed6\u8bf4\uff0c\u4e0e\u4e0a\u5b98\u91d1\u8679\u4ea4\u624b\u7684\u673a\u4f1a\uff0c\u5e76\u4e0d\u662f\u65f6\u5e38\u90fd\u6709\u7684\uff0c\u8fd9\u673a\u4f1a\u6211\u7edd\u4e0d\u80fd\u9519\u8fc7\uff01 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u2014\u2014\u4f60\u96be\u9053\u8981\u66ff\u4ed6\u53bb\uff01 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u77e5\u9053\u4ed6\u7edd\u4e0d\u80af\u8ba9\u6211\u966a\u4ed6\u53bb\uff0c\u6211\u4e5f\u4e0d\u613f\u8ba9\u4ed6\u966a\u6211\u53bb\uff0c\u8fd9\u4e5f\u6b63\u5982\u559d\u9152\u4e00\u6837\uff0c\u6709 \u65f6\u8981\u4e24\u4e2a\u4eba\u5bf9\u996e\u624d\u597d\uff0c\u591a\u4e00\u4eba\u5c31\u65e0\u8da3\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u76ee\u4e2d\u5ffd\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u9eef\u7136\u9053\uff1a\u4ed6\u8bf4\u7684\u4e0d\u9519\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u4e2a\u597d\u4eba\u3002 \u3000\u3000\u90ed\u5d69\u9633\u9053\uff1a\u6211\u65e0\u8bba\u662f\u6b7b\u662f\u6d3b\uff0c\u90fd\u4e0d\u613f\u89c1\u5230\u6709\u4eba\u4e3a\u6211\u6d41\u6cea\uff0c\u770b\u5230\u5973\u4eba\u7684\u773c\u6cea\u6211\u5c31\u6076\u5fc3\uff0c\u4f60 \u7684\u773c\u6cea\u8fd8\u662f\u7559\u7ed9\u522b\u4eba\u5427\uff01 \u3000\u3000\u4ed6\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u8fde\u5934\u4e5f\u4e0d\u56de\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u867d\u7136\u4e0d\u80fd\u52a8\uff0c\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u5374\u8fd8\u662f\u6709\u77e5\u89c9\u7684\uff0c\u671b\u7740\u90ed\u5d69\u9633\u8d70\u51fa\u95e8\uff0c\u4ed6\u76ee\u4e2d\u4f3c\u5df2\u6709\u70ed \u6cea\u5c06\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u8d77\u773c\u775b\uff0c\u5fc3\u91cc\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u96be\u53d7\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u60c5\u611f\uff0c\u6709\u65f6\u5b9e\u5728\u5f88 \u96be\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u7684\u786e\u4e3a\u5f88\u591a\u4eba\u505a\u8fc7\u8bb8\u591a\u4e8b\uff0c\u90a3\u4e9b\u4eba\u6709\u7684\u5df2\u80cc\u5f03\u4e86\u4ed6\uff0c\u6709\u7684\u5df2\u9057\u5fd8\u4e86\uff0c\u6709\u7684\u751a\u81f3\u51fa\u5356\u8fc7 \u4ed6\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u4e3a\u90ed\u5d69\u9633\u505a\u8fc7\u4ec0\u4e48\uff0c\u4f46\u90ed\u5d69\u9633\u5374\u4e0d\u60dc\u4e3a\u4ed6\u53bb\u6b7b\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u771f\u6b63\u7684\u53cb\u60c5\u3002 \u3000\u3000\u8fd9\u79cd\u53cb\u60c5\u65e2\u4e0d\u80fd\u6536\u4e70\uff0c\u4e5f\u4e0d\u662f\u53ef\u4ee5\u4ea4\u6362\u5f97\u5230\u7684\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4e16\u95f4\u8fd8\u6709\u8fd9\u79cd\u53cb\u60c5\u5b58\u5728\uff0c\u6240 \u4ee5\u4eba\u7c7b\u7684\u5149\u8f89\u624d\u80fd\u6c38\u5b58\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u9aa4\u7136\u6697\u4e86\u8d77\u6765\u3002 \u3000\u3000\u94c3\u94c3\u63a9\u8d77\u4e86\u95e8\uff0c\u5173\u597d\u4e86\u7a97\u5b50\uff0c\u9759\u9759\u7684\u5750\u5728\u674e\u5bfb\u6b22\u8eab\u65c1\uff0c\u6e29\u67d4\u7684\u671b\u7740\u4ed6\uff0c\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u518d\u8bf4 \u3002 \u3000\u3000\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff1f \u3000\u3000\u90ed\u5d69\u9633\u662f\u4e0d\u662f\u5df2\u5f00\u59cb\u548c\u4e0a\u5b98\u91d1\u8679\u3001\u8346\u65e0\u547d\u4ed6\u4eec\u4f5c\u751f\u6b7b\u4e4b\u6597\uff1f \u3000\u3000\u4ed6\u7684\u751f\u6b7b\u4e5f\u8bb8\u5df2\u53ea\u662f\u547c\u5438\u95f4\u7684\u4e8b\uff0c\u4f46\u6211\u5374\u53cd\u800c\u5b89\u9759\u9759\u7684\u8eba\u5728\u8fd9\u91cc\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u4e3a\u4ed6\u505a\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u7684\u5fc3\u597d\u4f3c\u5df2\u5c06\u88c2\u5f00\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u697c\u68af\u4e0a\u54cd\u8d77\u4e86\u4e00\u9635\u811a\u6b65\u58f0\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5916\u9762\u4f20\u5165\u4e86\u6572\u95e8\u58f0\uff1a\u7b03\uff0c\u7b03\u7b03\uff01 \u3000\u3000\u94c3\u94c3\u9aa4\u7136\u7d27\u5f20\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6765\u7684\u4f1a\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u662f\u4e0d\u662f\u90ed\u5d69\u9633\u5df2\u906d\u4e86\u4ed6\u4eec\u7684\u6bd2\u624b\uff0c\u4ed6\u4eec\u73b0\u5728\u53c8\u6765\u627e\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u7b03\uff0c\u7b03\u7b03\uff01 \u3000\u3000\u8fd9\u6b21\u6572\u95e8\u7684\u58f0\u97f3\u66f4\u54cd\u3002 \u3000\u3000\u94c3\u94c3\u9762\u4e0a\u5df2\u6c81\u51fa\u4e86\u51b7\u6c57\uff0c\u5ffd\u7136\u62b1\u8d77\u674e\u5bfb\u6b22\uff0c\u56db\u4e0b\u5f20\u671b\u7740\uff0c\u4f3c\u4e4e\u60f3\u627e\u4e2a\u5730\u65b9\u5c06\u674e\u5bfb\u6b22\u85cf\u8d77 \u6765\u3002 \u3000\u3000\u6572\u95e8\u58f0\u4e0d\u505c\u7684\u54cd\u4e86\u8d77\u6765\uff0c\u5916\u9762\u7684\u4eba\u663e\u7136\u5f88\u7126\u6025\uff0c\u82e5\u662f\u518d\u4e0d\u53bb\u5f00\u95e8\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u5c31\u8981\u7834\u95e8\u800c \u5165\u3002 \u3000\u3000\u94c3\u94c3\u54ac\u7740\u5634\u5507\u5927\u58f0\u9053\uff1a\u6765\u4e86\uff0c\u6025\u4ec0\u4e48\uff1f\u603b\u8981\u7b49\u4eba\u5bb6\u7a7f\u597d\u8863\u670d\u624d\u80fd\u5f00\u95e8\u5440\uff01 \u3000\u3000\u5979\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u7528\u811a\u5c16\u6311\u5f00\u4e86\u8863\u67dc\u7684\u95e8\uff0c\u5c06\u674e\u5bfb\u6b22\u85cf\u4e86\u8fdb\u53bb\uff0c\u53c8\u6293\u4e86\u4e9b\u8863\u670d\u5806\u5728\u674e\u5bfb \u6b22\u8eab\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u867d\u7136\u4ece\u4e0d\u613f\u9003\u907f\u8eb2\u85cf\uff0c\u600e\u5948\u4ed6\u73b0\u5728\u8fde\u4e00\u6839\u5c0f\u6307\u5934\u90fd\u52a8\u4e0d\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u94c3\u94c3\u5bf9\u7740\u8863\u67dc\u4e0a\u7684\u94dc\u955c\u6574\u4e86\u6574\u886b\uff0c\u7406\u4e86\u7406\u5934\u53d1\uff0c\u53c8\u64e6\u5e72\u4e86\u989d\u89d2\u548c\u9f3b\u5b50\u4e0a\u7684\u51b7\u6c57\u3002 \u3000\u3000\u5979\u5ffd\u7136\u5c06\u8863\u67dc\u7684\u95e8\u7d27\u7d27\u5173\u4e0a\uff0c\u683c\u7684\u4e00\u58f0\u4e0a\u4e86\u9501\u3002 \u3000\u3000\u5979\u5634\u91cc\u81ea\u8bed\u9053\uff1a\u597d\u5bb9\u6613\u5077\u7a7a\u7761\u4e2a\u5348\u89c9\uff0c\u504f\u53c8\u6709\u4eba\u6765\u4e86\uff0c\u6211\u8fd9\u4eba\u600e\u5730\u5982\u6b64\u547d\u82e6\u3002 \u3000\u3000\u58f0\u97f3\u6e10\u8fdc\u4e86\uff0c\u674e\u5bfb\u6b22\u5c31\u542c\u5230\u5f00\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u95e8\u5f00\u4e86\uff0c\u58f0\u97f3\u5374\u53cd\u800c\u7a81\u7136\u505c\u987f\uff0c\u94c3\u94c3\u4f3c\u4e4e\u662f\u5728\u5403\u60ca\u53d1\u6014\uff0c\u95e8\u5916\u7684\u663e\u7136\u662f\u4e24\u4e2a\u548c\u5979\u4ece\u672a\u89c1 \u8fc7\u9762\u7684\u4eba\u3002 \u3000\u3000\u6765\u7684\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\u4e0e\u8346\u65e0\u547d\uff01 \u3000\u3000\u95e8\u5916\u7684\u4eba\u4e5f\u6ca1\u6709\u5148\u5f00\u53e3\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u542c\u5f97\u94c3\u94c3\u9053\uff1a\u4e24\u4f4d\u8981\u627e\u8c01\u5440\uff1f\u83ab\u975e\u662f\u627e\u9519\u5730\u65b9\u4e86 \u4e48\uff1f \u3000\u3000\u95e8\u5916\u7684\u4eba\u8fd8\u662f\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u53ea\u542c\u7830\u7684\u4e00\u58f0\uff0c\u94c3\u94c3\u4f3c\u4e4e\u88ab\u4ed6\u4eec\u63a8\u5f97\u649e\u5230\u95e8\u4e0a\uff0c\u7136\u540e\u5c31\u53ef\u4ee5\u542c\u51fa\u6709\u4e24\u4e2a\u4eba\u7684\u811a\u6b65\u8d70\u4e86\u8fdb \u6765\u3002 \u7b2c\u56db\u5341\u56db\u7ae0\u3000\u4e24\u4e16\u4e3a\u4eba>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u56db\u7ae0\u3000\u4e24\u4e16\u4e3a\u4eba \u3000\u3000\u8863\u6a71\u91cc\u53c8\u6697\u3001\u53c8\u95f7\uff0c\u82e5\u662f\u6362\u4e86\u522b\u4eba\u5728\u674e\u5bfb\u6b22\u8fd9\u79cd\u60c5\u51b5\u4e0b\u88ab\u5173\u5728\u8863\u6a71\u91cc\uff0c\u53ea\u6015\u8981\u7d27\u5f20\u5f97\u53d1 \u75af\u3002\u6765\u7684\u4eba\u663e\u7136\u4e0d\u6000\u597d\u610f\uff0c\u5426\u5219\u600e\u4f1a\u5bf9\u94c3\u94c3\u5982\u6b64\u7c97\u9c81\u3002 \u3000\u3000\u4f46\u674e\u589e\u6b22\u8fd9\u65f6\u53cd\u800c\u5e73\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u51e0\u4e4e\u60f3\u53d1\u7b11\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u81ea\u5df1\u90a3\u5929\u6765\u7684\u65f6\u5019\uff0c\u94c3\u94c3\u4e5f\u5c06\u4ed6\u5f53\u4f5c\u5f3a\u76d7\uff0c\u8fd9\u5c0f\u59d1\u5a18\u522b\u7684\u672c\u4e8b\u6ca1\u5b66\u4f1a\uff0c\u88c5\u8154\u8bf4\u8c0e \u7684\u672c\u4e8b\u5012\u5df2\u771f\u5b66\u5f97\u548c\u6797\u4ed9\u513f\u5dee\u4e0d\u591a\u4e86\u3002 \u3000\u3000\u4f46\u6765\u7684\u8fd9\u4e24\u4eba\u5374\u5b8c\u5168\u4e0d\u776c\u5979\uff0c\u5728\u5916\u9762\u4e24\u95f4\u5c4b\u5b50\u91cc\u8d70\u4e86\u4e00\u5708\uff0c\u4f3c\u4e4e\u5728\u56db\u4e0b\u641c\u5bfb\u7740\uff0c\u7136\u540e\u5c31 \u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u94c3\u94c3\u51b2\u4e86\u8fdb\u6765\uff0c\u5927\u58f0\u9053\uff1a\u8fd9\u662f\u6211\u4eec\u5bb6\u7684\u5c0f\u59d0\u7684\u95fa\u623f\uff0c\u4f60\u4eec\u600e\u4e48\u53ef\u4ee5\u968f\u4fbf\u5f80\u91cc\u9762\u95ef\uff1f \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u6765\u7684\u8fd9\u4e24\u4eba\u7ec8\u4e8e\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u4e00\u4eba\u9053\uff1a\u6211\u4eec\u6b63\u662f\u6765\u627e\u4f60\u4eec\u5bb6\u5c0f\u59d0\u7684\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u7adf\u7136\u5f88\u6e29\u67d4\uff0c\u5f88\u597d\u542c\uff0c\u800c\u4e14\u8bf4\u8bdd\u65f6\u8fd8\u4f3c\u5e26\u7740\u7b11\u610f\u3002 \u3000\u3000\u6765\u7684\u7adf\u662f\u5973\u4eba\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u7981\u4e5f\u89c9\u5f97\u610f\u5916\u3002 \u3000\u3000\u53ea\u542c\u94c3\u94c3\u9053\uff1a\u4f60\u4eec\u662f\u6765\u627e\u6211\u5bb6\u5c0f\u59d0\u7684\uff0c\u4f60\u4eec\u8ba4\u5f97\u5979\uff01 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u5f53\u7136\u8ba4\u5f97\u2014\u2014\u4e0d\u4f46\u8ba4\u5f97\uff0c\u800c\u4e14\u8fd8\u662f\u597d\u670b\u53cb\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u65e2\u7136\u5982\u6b64\uff0c\u4e24\u4f4d\u4e3a\u4f55\u4e0d\u65e9\u8bf4\uff0c\u5bb3\u5f97\u6211\u8fd8\u5c06\u4e24\u4f4d\u5f53\u571f\u532a\u54e9\u3002 \u3000\u3000\u90a3\u5973\u5b50\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u6211\u4eec\u7684\u6837\u5b50\u770b\u6765\u96be\u9053\u5f88\u50cf\u571f\u532a\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e24\u4f4d\u8fd9\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u73b0\u5728\u7684\u571f\u532a\u5df2\u7ecf\u8ddf\u4ee5\u524d\u4e0d\u4e00\u6837\uff0c\u6709\u7684\u7b80\u76f4\u6bd4\u4e24\u4f4d\u8fd8\u8981\u65af\u6587 \uff0c\u8fd8\u8981\u6f02\u4eae\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u7684\u8eab\u4efd\u6765\u3002 \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u5f53\u771f\u662f\u4e2a\u9b3c\u7cbe\u7075\uff0c\u9a82\u8d77\u4eba\u6765\u4e00\u4e2a\u810f\u5b57\u4e5f\u4e0d\u5e26\u3002 \u3000\u3000\u90a3\u5973\u5b50\u8fd8\u672a\u8bf4\u8bdd\uff0c\u53e6\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u9053\uff1a\u4f60\u5bb6\u5c0f\u59d0\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u8bf7\u5979\u51fa\u6765\u597d\u4e48\uff1f \u3000\u3000\u8fd9\u58f0\u97f3\u5f88\u4f4e\uff0c\u8bf4\u8bdd\u7684\u4eba\u55d3\u5b50\u4f3c\u4e4e\u6709\u4e9b\u5636\u54d1\uff0c\u4f46\u4e5f\u5f88\u597d\u542c\u3002\u674e\u5bfb\u6b22\u89c9\u5f97\u8fd9\u58f0\u97f3\u4eff\u4f5b\u5f88\u719f\u6089 \uff0c\u4f46\u60f3\u4e0d\u8d77\u5979\u662f\u8c01\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e24\u4f4d\u6765\u5f97\u771f\u5de7\uff0c\u5c0f\u59d0\u524d\u51e0\u5929\u5c31\u51fa\u95e8\u4e86\uff0c\u53ea\u7559\u6211\u4e00\u4e2a\u4eba\u5728\u8fd9\u6668\u770b\u5bb6\uff0c\u4e24\u4f4d\u6709\u4ec0\u4e48 \u4e8b\uff0c\u544a\u8bc9\u6211\u4e5f\u662f\u4e00\u6837\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u5979\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e0d\u77e5\u9053\u2014\u2014\u5c0f\u4e14\u6ca1\u6709\u8bf4\uff0c\u6211\u600e\u4e48\u6562\u95ee\uff1f \u3000\u3000\u53e6\u4e00\u5973\u5b50\u7a81\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u6211\u4eec\u4e00\u6765\uff0c\u5979\u5c31\u51fa\u95e8\u4e86\uff0c\u6211\u4eec\u4e0d\u6765\uff0c\u5979\u5929\u5929\u90fd\u5728\u8fd9\u91cc\uff0c\u96be \u9053\u5979\u77e5\u9053\u6211\u4eec\u8981\u6765\uff0c\u5c31\u8eb2\u8d77\u6765\u4e0d\u6562\u89c1\u4eba\u4e48\uff1f \u3000\u3000\u8fd9\u51b2\u950b\u662f\u5f88\u4e0d\u5ba2\u6c14\uff0c\u679c\u7136\u50cf\u662f\u6765\u627e\u9ebb\u70e6\u7684\u3002 \u3000\u3000\u94c3\u94c3\u8fd8\u662f\u5728\u7b11\uff0c\u9053\uff1a\u4e24\u4f4d\u65e2\u662f\u5c0f\u59d0\u7684\u670b\u53cb\uff0c\u5979\u8981\u77e5\u9053\u4e24\u4f4d\u5230\u4e86\uff0c\u6b22\u559c\u8fd8\u6765\u4e0d\u53ca\uff0c\u600e\u4f1a\u8eb2 \u8d77\u6765\u5462\uff1f \u3000\u3000\u90a3\u5973\u5b50\u7b11\uff1a\u6709\u4e9b\u4eba\u4ec0\u4e48\u4eba\u90fd\u6562\u89c1\uff0c\u5c31\u4e0d\u6562\u89c1\u670b\u53cb\uff0c\u4f60\u8bf4\u5947\u602a\u4e0d\u5947\u602a\uff1f \u3000\u3000\u53e6\u4e00\u5973\u5b50\u51b7\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5979\u5bf9\u4e0d\u8d77\u670b\u53cb\u7684\u4e8b\u505a\u5f97\u592a\u591a\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u7b11\u9053\uff1a\u4e24\u4f4d\u771f\u4f1a\u8bf4\u7b11\u8bdd\uff0c\u8fd9\u5730\u65b9\u8fd9\u4e48\u5c0f\uff0c\u4e00\u4e2a\u5927\u4eba\u5c31\u7b97\u8981\u8eb2\u8d77\u6765\uff0c\u4e5f\u6ca1\u5730\u65b9\u8eb2\u5440\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u54e6\uff0c\u662f\u4e48\uff1f\u8fd9\u5730\u65b9\u6211\u867d\u7136\u4e0d\u719f\uff0c\u4f46\u6211\u82e5\u8981\u8eb2\u8d77\u6765\uff0c\u5012\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u627e\u5230\u5730\u65b9\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u59d1\u5a18\u9664\u975e\u8eb2\u5230\u8fd9\u8863\u6a71\u91cc\u3002 \u3000\u3000\u5979\u5403\u5403\u7684\u7b11\u9053\uff1a\u4f46\u4e00\u4e2a\u4eba\u82e5\u8eb2\u5728\u8863\u6a71\u91cc\uff0c\u5c82\u975e\u95f7\u4e5f\u8981\u88ab\u51b3\u6b7b\u4e86\uff0c\u90a3\u6ecb\u5473\u4e00\u5b9a\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u90a3\u5973\u4eba\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u4eec\u5bb6\u5c0f\u59d0\u91d1\u679d\u7389\u53f6\uff0c\u81ea\u7136\u4e0d\u80af\u8eb2\u5230\u8863\u6a71\u91cc\u53bb\u7684\u2014\u2014 \u3000\u3000\u4e24\u4eba\u90fd\u7b11\u5f97\u5f88\u5f00\u5fc3\uff0c\u4eff\u4f5b\u90fd\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u6ed1\u7a3d\u5f97\u5f88\u3002 \u3000\u3000\u7b11\u4e86\u5f88\u4e45\uff0c\u90a3\u5973\u5b50\u624d\u9053\uff1a\u53ea\u4e0d\u8fc7\uff0c\u4f60\u5bb6\u5c0f\u59d0\u65e2\u7136\u4e0d\u80af\u8eb2\u5230\u8863\u6a71\u91cc\uff0c\u73b0\u5728\u8863\u6a71\u91cc\u8fd9\u4eba\u662f\u8c01 \u5462\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8c01\uff1f\u2014\u2014\u8863\u6a71\u91cc\u6709\u4eba\uff1f\u600e\u4e48\u8fde\u6211\u90fd\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u90a3\u5973\u5b50\uff1a\u8863\u6a71\u91cc\u82e5\u6ca1\u6709\u4eba\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u6321\u5728\u524d\u9762\u5462\uff1f\u96be\u9053\u6015\u6211\u4eec\u5077\u4f60\u4eec\u5c0f\u59d0\u7684\u8863\u670d\u5417 \uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6ca1\u6709\u5440\uff1f\u2014\u2014\u6211\u54ea\u91cc\u6321\u5728\u524d\u9762\u2014\u2014 \u3000\u3000\u90a3\u5973\u5b50\u67d4\u58f0\u9053\uff1a\u5c0f\u59b9\u59b9\uff0c\u4f60\u867d\u7136\u5f88\u806a\u660e\uff0c\u5f88\u4f1a\u8bf4\u8bdd\uff0c\u53ea\u53ef\u60dc\u5e74\u7eaa\u8fd8\u662f\u592a\u5c0f\u4e9b\uff0c\u8981\u60f3\u9a97\u8fc7 \u6211\u4eec\u8fd9\u4e24\u4e2a\u8001\u72d0\u72f8\uff0c\u6050\u6015\u8fd8\u8981\u7b49\u51e0\u5e74\u3002 \u3000\u3000\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u88ab\u4eba\u53d1\u73b0\u8eb2\u5728\u8863\u6a71\u91cc\uff0c\u90a3\u5b9e\u5728\u4e0d\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\uff0c\u4ed6\u60f3\u4e0d\u51fa\u8fd9\u4e24\u4e2a\u5973\u5b50\u4f1a \u5c06\u4ed6\u770b\u6210\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u4e5f\u731c\u4e0d\u51fa\u5979\u4eec\u7a76\u7adf\u662f\u600e\u6837\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u8f7b\u8a00\u7ec6\u8bed\uff0c\u813e\u6c14\u4eff\u4f5b\u6e29\u67d4\u6781\u4e86\uff0c\u4f46\u6bcf\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u8bdd\u91cc\u90fd\u5e26\u7740\u523a\uff0c\u663e\u89c1\u5f97\u5fc5\u5b9a\u662f \u4e2a\u6df1\u6c89\uff0c\u53c8\u5389\u5bb3\u7684\u89d2\u8272\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5973\u5b50\u8bdd\u867d\u8bf4\u5f97\u4e0d\u591a\uff0c\u4f46\u4e00\u6b66\u5668\u5c31\u662f\u5728\u627e\u9ebb\u70e6\uff0c\u4f3c\u4e4e\u5bf9\u6797\u4ed9\u513f\u5f88\u4e0d\u6ee1\uff0c\u4e00\u5fc3\u60f3\u6765\u627e \u6797\u4ed9\u513f\u7b97\u5e10\u7684\u3002 \u3000\u3000\u542c\u5979\u4eec\u7684\u811a\u6b65\u58f0\uff0c\u6b66\u529f\u90fd\u4e0d\u5f31\uff0c\u5e76\u4e0d\u5728\u6797\u4ed9\u513f\u4e4b\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u94c3\u94c3\u4e00\u58f0\u8f7b\u547c\uff0c\u8863\u6a71\u7684\u95e8\u5df2\u88ab\u62c9\u5f00\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u4e0a\u773c\u775b\uff0c\u53ea\u5e0c\u671b\u8fd9\u4e24\u4e2a\u5973\u4eba\u5343\u4e07\u83ab\u8981\u8ba4\u8bc6\u4ed6\u3002 \u3000\u3000\u90a3\u5973\u5b50\u663e\u7136\u4e5f\u672a\u60f3\u5230\u8863\u6a71\u91cc\u8eb2\u7740\u4e2a\u7537\u4eba\uff0c\u4e5f\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u6014\u4e86\u534a\u664c\uff0c\u624d\u542c\u5979\u5403\u5403\u7b11\u9053\uff1a\u5c0f\u59b9\u59b9\uff0c\u8fd9\u4eba\u662f\u8c01\u5440\uff0c\u7761\u7740\u4e86\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u2014\u2014\u4ed6\u662f\u6211\u7684\u8868\u54e5\u3002 \u3000\u3000\u90a3\u5973\u4eba\u7b11\u9053\uff1a\u6709\u8da3\u6709\u8da3\uff0c\u6709\u8da3\u6781\u4e86\uff0c\u6211\u5c0f\u65f6\u5019\u4e5f\u5e38\u5e38\u5c06\u6211\u7684\u60c5\u4eba\u85cf\u5728\u8863\u6a71\u91cc\uff0c\u6709\u4e00\u6b21\u88ab \u4eba\u53d1\u73b0\u4e86\uff0c\u6211\u4e5f\u8bf4\u6211\u7684\u8868\u54e5\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7b11\uff1a\u8fd9\u4f4d\u5c0f\u59b9\u59b9\u5012\u771f\u662f\u5e74\u8f7b\u6709\u4e3a\uff0c\u770b\u6837\u5b50\u8fde\u6211\u4eec\u90fd\u6bd4\u5979\u5dee\u591a\u4e86\uff0c\u8fd9\u624d\u5177\u53eb\u505a\u540e\u751f \u53ef\u754f\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5973\u5b50\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u9053\uff1a\u6797\u4ed9\u513f\u65e2\u7136\u4e0d\u5728\u8fd9\u91cc\uff0c\u6211\u4eec\u8d70\u5427\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u6025\u4ec0\u4e48\uff1f\u6211\u4eec\u65e2\u7136\u6765\u4e86\uff0c\u591a\u5750\u5750\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u8863\u6a71\u7684\u95e8\u4e00\u5f00\uff0c\u674e\u5bfb\u6b22\u5c31\u95fb\u5230\u4e00\u80a1\u8bf1\u4eba\u7684\u9999\u6c14\uff0c\u73b0\u5728\u8fd9\u9999\u6c14\u66f4\u8fd1\u4e86\uff0c\u90a3\u5973\u5b50\u597d\u50cf\u5df2\u8d70\u5230 \u4ed6\u9762\u524d\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u53c8\u7b11\u7740\u9053\uff1a\u5c0f\u59b9\u59b9\uff0c\u4f60\u5e74\u7eaa\u867d\u5c0f\uff0c\u9009\u62e9\u7537\u4eba\u7684\u773c\u5149\u5012\u771f\u4e0d\u9519\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8fd9\u5730\u65b9\u7684\u7537\u4eba\u4e0d\u591a\uff0c\u597d\u7684\u90fd\u88ab\u5c0f\u59d0\u6311\u8d70\u4e86\uff0c\u6211\u4e5f\u53ea\u597d\u5c06\u5c31\u4e9b\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u8fd9\u6837\u7684\u7537\u4eba\u4f60\u8fd8\u4e0d\u6ee1\u610f\u4e48\uff1f\u4f60\u770b\u4ed6\u65e2\u4e0d\u80d6\uff0c\u4e5f\u4e0d\u7626\uff0c\u8138\u957f\u5f97\u4e5f\u4e0d\u8ba8\u4eba\u538c\uff0c\u800c \u4e14\u770b\u6837\u5b50\u5bf9\u5973\u4eba\u5f88\u6709\u7ecf\u9a8c\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u522b\u7684\u5012\u4e5f\u8fd8\u4e0d\u9519\uff0c\u5c31\u662f\u592a\u559c\u6b22\u7761\u89c9\uff0c\u4e00\u7761\u7740\u5c31\u9192\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u592a\u7d2f\u4e86\u2014\u2014\u9047\u7740\u4f60\u8fd9\u6837\u7684\u5c0f\u72d0\u72f8\uff0c\u4ed6\u600e\u4f1a\u4e0d\u7d2f\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u5e74\u7eaa\u4e5f\u592a\u5927\u4e86\u4e9b\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u55ef\uff0c\u4e0d\u9519\uff0c\u4ed6\u914d\u4f60\u7684\u786e\u5acc\u592a\u5927\u4e86\u4e9b\uff0c\u914d\u6211\u5012\u521a\u597d\u3002 \u3000\u3000\u94f6\u94c3\u822c\u7684\u7b11\u7740\u63a5\u9053\uff1a\u5c0f\u59b9\u59b9\uff0c\u4f60\u82e5\u4e0d\u4e2d\u610f\uff0c\u5c31\u628a\u4ed6\u8ba9\u7ed9\u6211\u5427\uff0c\u8fc7\u4e24\u5929\uff0c\u6211\u4e00\u5b9a\u627e\u4e2a\u5e74\u8f7b \u7684\u6765\u966a\u4f60\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u672c\u6765\u8fd8\u597d\u50cf\u86ee\u6587\u9759\uff0c\u86ee\u6e29\u67d4\u7684\uff0c\u4f46\u4e00\u89c1\u7537\u4eba\uff0c\u5c31\u5b8c\u5168\u53d8\u4e86\uff0c\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u5c45\u7136\u5df2 \u5c06\u674e\u5bfb\u6b22\u62b1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u60f3\u4e0d\u5f20\u5f00\u773c\u775b\u4e5f\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u4e00\u5f20\u5f00\u773c\uff0c\u4ed6\u53c8\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u62b1\u7740\u4ed6\u7684\u5973\u5b50\u5e74\u7eaa\u5e76\u4e0d\u592a\u5927\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u53ea\u6709\u4e8c\u5341\u4e94\u516d\uff0c\u957f\u5f97\u4e5f\u7684\u786e\u4e0d\u96be\u770b\uff0c\u82e5\u5c06\u5979\u4e00 \u4e2a\u4eba\u5206\u6210\u4e09\u4e2a\uff0c\u5f53\u771f\u662f\u7f8e\u4eba\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u5979\u4e0b\u5df4\u6709\u4e09\u4e2a\uff0c\u674e\u5bfb\u6b22\u88ab\u5979\u62b1\u5728\u6000\u91cc\uff0c\u7b80\u76f4\u5c31\u597d\u50cf\u7761\u5728\u4e00\u5806\u68c9\u82b1\u4e0a\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u8bf4\u8bdd\u90a3\u4e48\u6e29\u67d4\uff0c\u7b11\u58f0\u90a3\u4e48\u597d\u542c\u7684\u4e00\u4e2a\u5973\u5b50\u7adf\u80a5\u5f97\u5982\u6b64\u53ef\u6015\uff0c\u7b80\u76f4\u80a5\u5f97\u4e0d\u50cf \u8bdd\u4e86\u3002 \u3000\u3000\u66f4\u4ee4\u674e\u5bfb\u6b22\u5403\u60ca\u7684\uff0c\u8fd8\u662f\u53e6\u4e00\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u5f88\u7f8e\uff0c\u4e5f\u5f88\u5a9a\uff0c\u6c34\u86c7\u822c\u7684\u7ec6\u8170\uff0c\u7a7f\u7740\u4e00\u5957\u5408\u8eab\u7684\u84dd\u8863\u670d\uff0c\u8863\u8896\u5374\u5f88\u5bbd\uff0c\u5c31\u7b97\u7ad9\u7740 \u4e0d\u52a8\uff0c\u4e5f\u6709\u79cd\u98d8\u98d8\u6b32\u4ed9\u4e4b\u611f\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u8d6b\u7136\u7adf\u662f\u88ab\u674e\u5bfb\u6b22\u6298\u65ad\u4e00\u53ea\u624b\u8155\u7684\u84dd\u874e\u5b50\uff01 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u84dd\u874e\u5b50\u5c45\u7136\u4f3c\u4e4e\u5df2\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u8138\u4e0a\u4e00\u70b9\u7279\u522b\u7684\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709 \u591a\u770b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u90a3\u80a5\u5973\u4eba\u8fd8\u5728\u7b11\u7740\uff0c\u5979\u4e00\u7b11\u8d77\u6765\uff0c\u674e\u5bfb\u6b22\u5c31\u89c9\u5f97\u597d\u50cf\u5728\u5730\u9707\u4e00\u6837\u3002 \u3000\u3000\u94c3\u94c3\u53d1\u614c\u4e86\uff0c\u9053\uff1a\u8fd9\u4eba\u810f\u5f97\u5f88\uff0c\u5e38\u5e38\u51e0\u4e2a\u6708\u4e0d\u6d17\u6fa1\uff0c\u59d1\u5a18\u5343\u4e07\u83ab\u8981\u62b1\u4ed6\uff0c\u4ed6\u8eab\u4e0a\u4e0d\u4f46\u6709 \u8df3\u86a4\u8fd8\u6709\u81ed\u866b\u3002 \u3000\u3000\u90a3\u80d6\u5973\u4eba\u9053\uff1a\u810f\uff0c\u8c01\u8bf4\u4ed6\u810f\uff1f\u4f55\u51b5\u4ed6\u8eab\u4e0a\u5c31\u7b97\u6709\u81ed\u866b\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u7537\u4eba\u8eab\u4e0a\u7684\u81ed\u866b\uff0c\u4e00 \u5b9a\u4e5f\u6709\u7537\u4eba\u7684\u5473\u9053\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u53ef\u662f\u2014\u2014\u4ed6\u975e\u4f46\u53c8\u810f\u53c8\u61d2\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u9152\u9b3c\u3002 \u3000\u3000\u90a3\u80d6\u5973\u4eba\u9053\uff1a\u9152\u9b3c\u66f4\u597d\uff0c\u9152\u91cf\u597d\u7684\u7537\u4eba\uff0c\u624d\u6709\u7537\u5b50\u6c49\u6c14\u6982\u3002 \u3000\u3000\u5979\u773c\u775b\u779f\u7740\u674e\u5bfb\u6b22\u5ae3\u7136\u4e00\u7b11\uff0c\u8f7b\u8f7b\u7684\u63a5\u7740\u9053\uff1a\u597d\u5904\u5728\u54ea\u91cc\uff0c\u4f60\u9a6c\u4e0a\u5c31\u4f1a\u77e5\u9053\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u90a3\u80d6\u5987\u4eba\u77aa\u7740\u773c\uff1a\u4f60\u7b11\u4ec0\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6211\u7b11\u4f60\u771f\u662f\u8272\u80c6\u5305\u5929\uff0c\u8fde\u4ed6\u7684\u8111\u7b4b\u4f60\u90fd\u6562\u52a8\u3002 \u3000\u3000\u90a3\u80d6\u5973\u4eba\u9053\uff1a\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u52a8\u4ed6\u7684\u8111\u7b4b\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\u4e48\uff1f \u3000\u3000\u90a3\u80d6\u5973\u4eba\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8c01\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u603b\u4e0d\u662f\u4ed6\u7684\u8868\u59b9\u5427\u3002 \u3000\u3000\u90a3\u80d6\u5973\u4eba\u9053\uff1a\u4f60\u53ef\u542c\u8bf4\u8fc7\u5927\u6b22\u559c\u5973\u83e9\u8428\u8fd9\u540d\u5b57\uff0c\u6211\u5c31\u662f\u5973\u83e9\u8428\u5ea7\u4e0b\u7684\u81f3\u5c0a\u5b9d\uff0c\u53ea\u8981\u662f\u7537 \u4eba\u6211\u5c31\u7edf\u5403\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u82e5\u6562\u5403\u4ed6\uff0c\u5c0f\u5fc3\u5403\u4e0b\u53bb\u54fd\u7740\u5589\u5499\uff0c\u5410\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u9053\uff1a\u6211\u5403\u4eba\u4ece\u6765\u4e0d\u5410\u9aa8\u5934\u7684\u3002 \u3000\u3000\u94c3\u94c3\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u4f60\u96be\u9053\u5c31\u4e0d\u60f3\u77e5\u9053\u4ed6\u662f\u8c01\u5417\uff1f \u3000\u3000\u81f3\u5c0a\u5b9d\u9053\uff1a\u6211\u82e5\u60f3\u77e5\u9053\uff0c\u6211\u81ea\u5df1\u4f1a\u95ee\u4ed6\uff0c\u7528\u4e0d\u7740\u4f60\u64cd\u5fc3\uff0c\u4f55\u51b5\u2014\u2014\u6211\u53ea\u8981\u4ed6\u662f\u4e2a\u7537\u4eba\u5c31 \u591f\u4e86\u3002 \u3000\u3000\u5979\u8f6c\u8fc7\u5934\u5411\u84dd\u874e\u5b50\u4e00\u7b11\uff0c\u9053\uff1a\u5e2e\u5e2e\u5fd9\uff0c\u628a\u8fd9\u5c0f\xd7\u5934\u5f04\u51fa\u53bb\uff0c\u8fd9\u5730\u65b9\u8fd8\u4e0d\u9519\uff0c\u6211\u60f3\u6682\u501f\u7528 \u4e00\u4e0b\uff0c\u4f60\u53ef\u4e0d\u51c6\u5077\u770b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5168\u8eab\u7684\u8089\u90fd\u9ebb\u4e86\uff0c\u60f3\u5410\u4e5f\u5410\u4e0d\u51fa\uff0c\u60f3\u6b7b\u4e5f\u6b7b\u4e0d\u4e86\uff0c\u53ea\u5e0c\u671b\u84dd\u874e\u5b50\u6765\u627e\u4ed6\u62a5\u4ec7\uff0c\u5feb \u4e9b\u7ed9\u4ed6\u4e00\u5200\u3002 \u3000\u3000\u600e\u5948\u84dd\u874e\u5b50\u5374\u50cf\u662f\u5b8c\u5168\u4e0d\u8ba4\u5f97\u4ed6\u4e86\uff0c\u4e00\u76f4\u51b7\u51b7\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u8fde\u770b\u90fd\u4e0d\u770b\u4ed6\u4e00\u773c\uff0c\u6b64\u523b\u5ffd \u7136\u4e00\u5b57\u5b57\u9053\uff1a\u8fd9\u7537\u4eba\u6211\u4e5f\u8981\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u7684\u9762\u8272\u9aa4\u7136\u53d8\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u4ec0\u4e48\uff1f\u4f60\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9762\u65e0\u8868\u60c5\uff0c\u8fd8\u662f\u4e00\u5b57\u5b57\u9053\uff1a\u8fd9\u7537\u4eba\u6211\u4e5f\u8981\uff01 \u3000\u3000\u81f3\u5c0a\u5b9d\u77aa\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u9732\u51fa\u4e86\u51f6\u5149\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u6562\u8ddf\u6211\u62a2\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u62a2\u5b9a\u4e86\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u8138\u4e0a\u4e00\u9635\u9752\uff0c\u4e00\u9635\u767d\u5ffd\u53c8\u7b11\u9053\uff1a\u4f60\u82e5\u771f\u60f3\u8981\u4ed6\uff0c\u6211\u4eec\u59d0\u59b9\u4fe9\u7684\u4e8b\u597d\u5546\u91cf\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u6211\u4e0d\u662f\u8981\u4ed6\u7684\u4eba\uff0c\u6211\u662f\u8981\u4ed6\u7684\u547d\uff01 \u3000\u3000\u81f3\u5c0a\u5b9d\u989c\u7b11\u9053\uff1a\u8fd9\u5c31\u66f4\u597d\u529e\u4e86\uff0c\u7b49\u6211\u8981\u8fc7\u4ed6\u7684\u4eba\uff0c\u4f60\u518d\u8981\u4ed6\u7684\u547d\u4e5f\u4e0d\u8fdf\u5440\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u7b49\u6211\u8981\u8fc7\u4ed6\u7684\u547d\uff0c\u4f60\u518d\u8981\u4ed6\u7684\u4eba\u5427\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u76ee\u4e2d\u867d\u5df2\u53c8\u6709\u6012\u610f\uff0c\u8fd8\u662f\u52c9\u5f3a\u7b11\u9053\uff1a\u6211\u867d\u7136\u5f88\u559c\u6b22\u7537\u4eba\uff0c\u4f46\u5bf9\u6b7b\u4eba\u5374\u6ca1\u4ec0\u4e48\u5174\u8da3 \u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4f60\u73b0\u5728\u5c82\u975e\u548c\u6b7b\u4eba\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u7b11\u9053\uff1a\u4ed6\u73b0\u5728\u4e0d\u80fd\u52a8\uff0c\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u6211\u81ea\u7136\u6709\u6cd5\u5b50\u8981\u4ed6\u52a8\u7684\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u7b49\u4ed6\u80fd\u52a8\u7684\u65f6\u5019\uff0c\u6211\u518d\u60f3\u8981\u4ed6\u7684\u547d\u5c31\u8fdf\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u60a0\u7136\u7b11\u9053\uff1a\u4e0d\u9519\uff0c\u7b49\u4ed6\u80fd\u52a8\u7684\u65f6\u5019\uff0c\u53ea\u8981\u4ed6\u7684\u624b\u4e00\u52a8\uff0c\u4f60\u4eec\u5c31\u518d\u89c1\u4e86\uff01 \u3000\u3000\u81f3\u5c0a\u5b9d\u52a8\u5bb9\u9053\uff1a\u4f60\u8bf4\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u5c31\u662f\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u81f3\u5c0a\u5b9d\u5446\u4f4f\u4e86\uff0c\u624d\u6447\u5934\u9053\uff1a\u6211\u4e0d\u4fe1\uff0c\u4ed6\u82e5\u771f\u662f\u674e\u5bfb\u6b22\uff0c\u600e\u4f1a\u770b\u4e0a\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u5c0f\xd7\u5934\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u5e76\u6ca1\u6709\u770b\u4e0a\u6211\uff0c\u662f\u6211\u770b\u4e0a\u4ed6\uff0c\u6240\u4ee5\u624d\u5e0c\u671b\u4f60\u4eec\u5feb\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6211\u5bb6\u5c0f\u59d0\u544a\u8bc9\u6211\uff0c\u4f60\u82e5\u770b\u4e0a\u4e00\u4e2a\u7537\u4eba\uff0c\u4ed6\u5374\u770b\u4e0d\u4e0a\u4f60\uff0c\u90a3\u4e48\u4f60\u5c31\u5b81\u53ef\u8981\u4e86\u4ed6\u7684 \u547d\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u843d\u5230\u522b\u7684\u5973\u4eba\u624b\u4e0a\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u60f3\u4e0d\u5230\u8fd9\u5c0f\xd7\u5934\u7684\u5fc3\u80a0\u7adf\u6bd4\u6211\u8fd8\u8981\u6bd2\u8fa3\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u96be\u9053\u4f60\u8fd8\u60f3\u8981\u4ed6\u7684\u4eba\u4e48\uff1f\u4f60\u771f\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\uff1f \u3000\u3000\u81f3\u5c0a\u5b9d\u6c89\u541f\u9053\uff1a\u7261\u4e39\u82b1\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u98ce\u6d41\uff0c\u80fd\u548c\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u540d\u7537\u4eba\u4f5c\u4e00\u591c\u592b\u59bb\uff0c\u5c31\u7b97 \u6b7b\u4e5f\u4e0d\u51a4\u6789\u4e86\u3002 \u3000\u3000\u5979\u53c8\u5411\u84dd\u874e\u5b50\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u4f46\u4f60\u4e5f\u4e0d\u5fc5\u7740\u6025\uff0c\u6211\u8981\u4ed6\u7684\u4eba\u4e4b\u540e\uff0c\u8fd8\u662f\u6709\u6cd5\u5b50\u73b0\u4f60\u8981\u4ed6 \u7684\u547d\u3002 \u3000\u3000\u84dd\u874e\u5b50\u6c89\u7740\u8138\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u9053\uff1a\u4f60\u83ab\u5fd8\u4e86\uff0c\u6211\u8fd9\u6b21\u6765\uff0c\u662f\u4e3a\u4e86\u8981\u5e2e\u4f60\u7684\u5fd9\uff0c\u4f60\u597d\u6b9a\u4e5f\u5f97\u7ed9\u6211\u4e2a\u9762\u5b50\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9ed8\u7136\u534a\u664c\u9053\uff1a\u7537\u4eba\u7684\u624b\u82e5\u88ab\u780d\u4e86\uff0c\u4f60\u8fd8\u6709\u5174\u8da3\u4e48\uff1f \u3000\u3000\u81f3\u5c0a\u5b9d\u9053\uff1a\u624b\u65ad\u4e86\u5012\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53ea\u8981\u522b\u7684\u5730\u65b9\u4e0d\u65ad\u5c31\u884c\u4e86\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u90a3\u4e48\u6211\u5c31\u8981\u4ed6\u7684\u4e00\u53ea\u624b\uff01 \u3000\u3000\u81f3\u5c0a\u5b9d\u60f3\u4e86\u60f3\u9053\uff1a\u5de6\u624b\u8fd8\u662f\u53f3\u624b\uff1f \u3000\u3000\u84dd\u874e\u5b50\u6068\u6068\uff1a\u4ed6\u6298\u65ad\u4e86\u6211\u7684\u53f3\u624b\uff0c\u6211\u4e5f\u8981\u4ed6\u7684\u4e00\u53ea\u53f3\u624b\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u597d\uff0c\u4f60\u6765\u5427\u2014\u2014\u4f46\u5207\u83ab\u5f04\u5f97\u9c9c\u8840\u6dcb\u6f13\uff0c\u53eb\u4eba\u6076\u5fc3\uff0c\u7528\u4f60\u90a3\u6839\u874e\u5b50 \u5c3e\u5df4\u968f\u4fbf\u5728\u4ed6\u624b\u4e0a\u87ab\u4e00\u4e0b\u5c31\u7b97\u4e86\u5427\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\u3002 \u3000\u3000\u5979\u6162\u6162\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u773c\u775b\u95ea\u7740\u4eae\u5149\u3002 \u3000\u3000\u94c3\u94c3\u5927\u58f0\u9053\uff1a\u4f60\u4eec\u771f\u6562\u8fd9\u4e48\u6837\u5bf9\u4ed6\uff1f \u3000\u3000\u81f3\u5c0a\u5b9d\u67d4\u58f0\u9053\uff1a\u5c0f\u59b9\u59b9\uff0c\u96be\u9053\u4f60\u53c8\u5fc3\u75bc\u4e86\u4e48\uff1f \u3000\u3000\u5979\u8bdd\u672a\u8bf4\u5b8c\u3002 \u3000\u3000\u84dd\u874e\u5b50\u8863\u8896\u4e2d\u5df2\u98de\u51fa\u4e00\u9053\u9752\u84dd\u8272\u7684\u7535\u5149\uff0c\u95ea\u7535\u822c\u5411\u674e\u5bfb\u6b22\u53f3\u81c2\u523a\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u4e00\u58f0\u60e8\u547c\uff0c\u5386\u4e45\u4e0d\u7edd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u4eba\uff0c\u7830\u7684\u8dcc\u5728\u5730\u4e0a\uff01 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u58f0\u60e8\u547c\u7adf\u662f\u81f3\u5c0a\u5b9d\u53d1\u51fa\u7684\u3002 \u3000\u3000\u60e8\u547c\u58f0\u4e2d\uff0c\u5979\u5df2\u629b\u4e0b\u4e86\u674e\u5bfb\u6b22\uff0c\u75af\u72c2\u822c\u5411\u84dd\u874e\u5b50\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u84dd\u874e\u5b50\u8170\u80a2\u4e00\u626d\uff0c\u6ed1\u5f00\u4e86\u4e03\u516b\u5c3a\u3002 \u3000\u3000\u8c01\u77e5\u81f3\u5c0a\u5b9d\u7684\u8170\u80a2\u867d\u6bd4\u6c34\u6876\u8fd8\u7c97\uff0c\u52a8\u4f5c\u53cd\u5e94\u5374\u5947\u5feb\u65e0\u6bd4\u3002\u9aa4\u7136\u4e00\u7ffb\u8eab\uff0c\u5df2\u6293\u4f4f\u4e86\u84dd\u874e\u5b50 \u7684\u624b\u3002 \u3000\u3000\u84dd\u874e\u5b50\u7684\u8138\u90fd\u5413\u767d\u4e86\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u4e00\u5f20\u8138\u53d8\u6210\u9752\u84dd\u8272\uff0c\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u72f0\u72de\u53ef\u6016\uff0c\u54ac\u7259\u9053\uff1a\u4f60\u2014\u2014\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u6562 \u6697\u7b97\u6211\uff0c\u6211\u8981\u4f60\u7684\u547d\uff01 \u3000\u3000\u53ea\u542c\u5494\u5693\u4e00\u58f0\uff0c\u84dd\u874e\u5b50\u7684\u4e00\u53ea\u624b\u5df2\u88ab\u8fde\u7740\u8863\u8896\u62e7\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u84dd\u874e\u5b50\u53c8\u6ed1\u5f00\u6570\u5c3a\uff0c\u8138\u4e0a\u7adf\u8fde\u534a\u70b9\u75db\u82e6\u4e4b\u8272\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u62e7\u65ad\u7684\u662f\u5979\u7684\u4e00\u53ea\u53f3\u624b\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5df2\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u683c\u683c\u7b11\u9053\uff1a\u4f60\u518d\u770b\u770b\u4f60\u624b\u91cc\u6293\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u81f3\u5c0a\u5b9d\u4e00\u62ac\u624b\uff0c\u53ea\u89c1\u88f9\u5728\u534a\u622a\u8863\u8896\u4e2d\u7684\u53ea\u4e0d\u8fc7\u662f\u4e00\u6bb5\u95ea\u7740\u9752\u5149\u7684\u874e\u5b50\u5c3e\u5df4\uff0c\u539f\u6765\u84dd\u874e\u5b50 \u53f3\u624b\u88ab\u674e\u5bfb\u6b22\u65a9\u65ad\u540e\uff0c\u5c31\u5c06\u81ea\u5df1\u7528\u7684\u5175\u5668\u63a5\u5728\u65ad\u8155\u4e0a\uff0c\u7528\u5979\u90a3\u5bbd\u5927\u7684\u8863\u8896\u906e\u4f4f\u8c01\u4e5f\u770b\u4e0d\u51fa\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4e2d\u4e86\u6211\u874e\u5c3e\u4e4b\u6bd2\uff0c\u8d70\u4e0d\u51fa\u4e03\u6b65\u5fc5\u6b7b\u65e0\u7591\uff0c\u5c31\u7b97\u4f60\u8eab\u5b50\u6bd4\u522b\u4eba\u5927\u4e9b\uff0c\u6bd2\u6027\u53d1\u4f5c \u6162\u7766\uff0c\u4f60\u80fd\u518d\u8d70\u4e09\u6b65\u8fd8\u4e0d\u5012\u4e0b\uff0c\u6211\u4f69\u670d\u4f60\u3002 \u3000\u3000\u81f3\u5c0a\u5b9d\u72c2\u543c\u4e00\u58f0\uff0c\u53c8\u51b2\u51fa\u3002 \u3000\u3000\u5979\u679c\u7136\u8fd8\u672a\u51b2\u51fa\u4e09\u6b65\uff0c\u5c31\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u84dd\u874e\u5b50\u518d\u4e5f\u4e0d\u770b\u5979\u4e00\u80af\uff0c\u8f6c\u8eab\u8d70\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u5782\u7740\u5934\uff0c\u51b7\u51b7\u671b\u7740\u4ed6\uff0c\u624d\u9053\uff1a\u4f0a\u54ed\u5c31\u662f \u4e3a\u4e86\u53bb\u627e\u6797\u4ed9\u513f\u624d\u4f1a\u6b7b\u7684\uff0c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u672c\u662f\u4e3a\u4e86\u8981\u627e\u6797\u4ed9\u513f\u7b97\u5e10\uff0c\u548c\u4f60\u672c\u65e0\u5173\u7cfb\u3002 \u3000\u3000\u94c3\u94c3\u53c8\u63d2\u5634\u9053\uff1a\u4f60\u82e5\u60f3\u4ed6\u8bf4\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u89e3\u5f00\u4ed6\u7684\u7a7a\u9053\uff1f \u3000\u3000\u84dd\u874e\u5b50\u4e0d\u7406\u5979\uff0c\u53c8\u9053\uff1a\u4f60\u867d\u7136\u5e9f\u4e86\u6211\u7684\u4e00\u53ea\u624b\uff0c\u5374\u672a\u8981\u6211\u7684\u547d\uff0c\u603b\u7b97\u5bf9\u6211\u6709\u6069\uff0c\u6211\u8fd9\u4eba \u4e00\u751f\u6069\u6028\u6700\u5206\u660e\uff0c\u4f60\u5bf9\u6211\u6709\u70b9\u6c34\u4e4b\u6069\uff0c\u6211\u5c31\u4e0d\u80fd\u773c\u770b\u7740\u4f60\u88ab\u90a3\u732a\u7cdf\u584c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6697\u4e2d\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4ed6\u5b9e\u672a\u770b\u51fa\u84dd\u874e\u5b50\u7adf\u662f\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u84dd\u874e\u5b50\u51b7\u51b7\u9053\uff1a\u73b0\u5728\u6211\u65e2\u5df2\u8fd8\u4e86\u4f60\u7684\u503a\uff0c\u4f60\u6b20\u6211\u7684\u81ea\u7136\u4e5f\u975e\u8fd8\u4e0d\u53ef\uff0c\u6211\u4e5f\u8981\u4f60\u4e00\u53ea\u53f3\u624b \uff0c\u8fd9\u603b\u4e0d\u7b97\u8fc7\u4efd\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u6162\u6162\u5c06\u53f3\u624b\u4f38\u4e86\u51fa\u6765\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5446\u4f4f\u4e86\uff0c\u94c3\u94c3\u4e5f\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u7adf\u5df2\u80fd\u6d3b\u52a8\uff0c\u7adf\u672a\u53d1\u51fa\u4ed6\u7684\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u84dd\u874e\u5b50\u671b\u7740\u8fd9\u53ea\u624b\uff0c\u54ea\u91cc\u8fd8\u80fd\u8bf4\u5f97\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u94c3\u94c3\u5374\u5fcd\u4e0d\u4f4f\u9053\uff1a\u4f60\u8fd9\u53ea\u624b\u600e\u4e48\u80fd\u52a8\u4e86\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u6211\u672c\u5c31\u5728\u8fd0\u6c14\u89e3\u7a74\uff0c\u53ea\u53ef\u60dc\u529f\u592b\u4e0d\u5230\u5bb6\uff0c\u4e00\u76f4\u65e0\u6cd5\u51b2\u7834\u6700\u540e\u4e00\u5173\uff0c\u8c01\u77e5 \u65b9\u624d\u90a3\u4e00\u8dcc\uff0c\u5374\u5e2e\u4e86\u6211\u7684\u5fd9\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u4f60\u4e3a\u4f55\u5982\u6b64\u542c\u8bdd\uff0c\u5979\u8981\u4f60\u8fd9\u53ea\u624b\uff0c\u4f60\u5c31\u4f38\u51fa\u6765\u7ed9\u5979\uff0c\u4f60\u2014\u2014\u4e3a\u4f55\u4e0d\u7ed9\u5979\u4e00 \u5200\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u4e0b\u4e86\u8138\uff0c\u4e5f\u4e0d\u7406\u5979\u4e86\uff0c\u7f13\u7f13\u9053\uff1a\u84dd\u59d1\u5a18\uff0c\u4f60\u8981\u7684\u5b9e\u4e0d\u8fc7\u4efd\uff0c\u6211\u4e5f\u6beb\u65e0\u6028\u8a00\uff0c\u8bf7 \u3002 \u3000\u3000\u84dd\u874e\u5b50\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u4e16\u4e0a\u7adf\u771f\u6709\u8fd9\u6837\u7684\u4eba\u2014\u2014 \u3000\u3000\u5979\u5c06\u8fd9\u53e5\u8bdd\u4e00\u8fde\u8bf4\u4e86\u4e24\u904d\uff0c\u7a81\u7136\u8dfa\u4e86\u8dfa\u811a\uff0c\u6389\u5934\u5c31\u8d70\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u4e0d\u77e5\u4f55\u65f6\u5df2\u8dc3\u8d77\uff0c\u6321\u4f4f\u4e86\u5979\u7684\u53bb\u8def\uff0c\u9053\uff1a\u8bf7\u7b49\u4e00\u7b49\u3002 \u7b2c\u56db\u5341\u4e94\u7ae0\u3000\u5343\u94a7\u4e00\u53d1>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u4e94\u7ae0\u3000\u5343\u94a7\u4e00\u53d1 \u3000\u3000\u84dd\u874e\u5b50\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u4ece\u4f60\u4f38\u51fa\u624b\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4f60\u5c31\u5df2\u5c06\u4f60\u7684\u503a\u8fd8\u6e05\u4e86\uff0c\u6211\u867d\u7136\u662f\u4e2a\u5973\u4eba\uff0c\u5374\u4e5f \u8fd8\u61c2\u5f97\u9053\u4e49\u4e24\u5b57\u201d \u3000\u3000\u94c3\u94c3\u7728\u7740\u773c\uff0c\u63d2\u5634\u9053\uff1a\u5973\u4eba\u5929\u751f\u5c31\u53ef\u4ee5\u4e0d\u8bb2\u9053\u4e49\uff0c\u8fd9\u672c\u662f\u5973\u4eba\u7684\u6743\u529b\uff0c\u7537\u4eba\u5929\u751f\u6bd4\u5973\u4eba \u5f3a\uff0c\u6240\u4ee5\u672c\u8be5\u8ba9\u5973\u4eba\u51e0\u5206\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1b\u8fd9\u8bdd\u662f\u8c01\u8bf4\u7684\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u5f53\u7136\u662f\u6211\u4eec\u5bb6\u5c0f\u59d0\u8bf4\u7684\uff1f \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u4f60\u5f88\u542c\u5979\u7684\u8bdd\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u5979\u662f\u5728\u4e3a\u6211\u4eec\u5973\u4eba\u8bf4\u8bdd\uff0c\u53ea\u8981\u662f\u5973\u4eba\uff0c\u5c31\u8be5\u542c\u5979\u7684\u3002 \u3000\u3000\u84dd\u874e\u5b50\u5ffd\u7136\u8d70\u8fc7\u53bb\uff0c\u6b63\u6b63\u53cd\u53cd\u7ed9\u4e86\u5979\u5341\u51e0\u4e2a\u8033\u5149\u3002 \u3000\u3000\u94c3\u94c3\u88ab\u6253\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u84dd\u874e\u5b50\u51b7\u51b7\u9053\uff1a\u6211\u4e5f\u548c\u4f60\u4eec\u4e00\u6837\uff0c\u5e76\u4e0d\u662f\u597d\u4eba\uff0c\u4f46\u6211\u5374\u8981\u6253\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u54ac\u7740\u7259\uff0c\u9053\uff1a\u56e0\u4e3a\u4f60\u2014\u2014\u4f60\u662f\u4e2a\u2014\u2014 \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u63a9\u7740\u8138\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u84dd\u874e\u5b50\u9053\uff1a\u5c31\u56e0\u4e3a\u4e16\u4e0a\u6709\u4e86\u4f60\u4eec\u8fd9\u79cd\u5973\u4eba\uff0c\u6240\u4ee5\u5973\u4eba\u624d\u4f1a\u88ab\u7537\u4eba\u770b\u4e0d\u8d77\uff0c\u5c31\u56e0\u4e3a\u7537\u4eba\u770b \u4e0d\u8d77\u5973\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u8981\u62a5\u590d\uff0c\u624d\u4f1a\u505a\u51fa\u90a3\u4e9b\u4e8b\u3002 \u3000\u3000\u5979\u58f0\u97f3\u6e10\u6e10\u4f4e\u4e86\u4e0b\u6765\uff0c\u4f3c\u5df2\u6709\u4e9b\u54fd\u54bd\uff0c\u9053\uff1a\u6211\u505a\u90a3\u4e9b\u4e8b\u7684\u8fdb\u4fee\uff0c\u5fc3\u91cc\u4e5f\u77e5\u9053\uff0c\u90a3\u4e0d\u4f46\u662f \u5728\u6bc1\u522b\u4eba\uff0c\u4e5f\u662f\u8981\u6bc1\u6211\u81ea\u5df1\uff0c\u6211\u8fd9\u4e00\u751f\uff0c\u5c31\u662f\u88ab\u6211\u81ea\u5df1\u8fd9\u6837\u6bc1\u4e86\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u8fc7\u53bb\u7684\u4e8b\u5df2\u8fc7\u53bb\u4e86\uff0c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u8fd8\u53ef\u4ee5\u4ece\u5934\u505a\u8d77\u3002 \u3000\u3000\u84dd\u874e\u5b50\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u4e5f\u8bb8\u4f60\u662f\u8fd9\u4e48\u60f3\uff0c\u4f46\u522b\u4eba\u5462\u2014\u2014\u522b\u4eba\u5462 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u8981\u81ea\u5df1\u95ee\u5fc3\u65e0\u6127\uff0c\u4f55\u5fc5\u53bb\u7ba1\u522b\u4eba\u600e\u4e48\u60f3\uff0c\u4e00\u4e2a\u4eba\u662f\u4e3a\u4e86\u81ea\u5df1\u6d3b\u7740\uff0c\u5e76\u4e0d\u662f \u4e3a\u4e86\u522b\u4eba\u3002 \u3000\u3000\u84dd\u874e\u5b50\u62ac\u8d77\u5934\uff0c\u51dd\u6ce8\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u4f60\u662f\u5b8c\u5168\u4e3a\u81ea\u5df1\u6d3b\u7740\u7684\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u2014\u2014 \u3000\u3000\u84dd\u874e\u5b50\u8fd8\u662f\u5728\u51dd\u6ce8\u4ed6\uff0c\u5634\u89d2\u9732\u51fa\u4e00\u4e1d\u51c4\u51c9\u7684\u5fae\u7b11\uff0c\u9053\uff1a\u80fd\u8ba4\u8bc6\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d \u4f1a\u540e\u6094\u7684\uff0c\u53ea\u53ef\u60dc\u6211\u4e3a\u4f55\u6ca1\u6709\u5728\u5341\u5e74\u524d\u8ba4\u8bc6\u4f60\u5462\uff1f\u2014\u2014 \u3000\u3000\u8fd9\u53e5\u8bdd\u5979\u5e76\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5df2\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5979\u8bed\u58f0\u8fdc\u8fdc\u4f20\u6765\uff1a\u5c06\u81f3\u5c0a\u5b9d\u7684\u5c38\u8eab\u7559\u7740\uff0c\u6211\u4f1a\u6765\u5b89\u6392\u5979\u7684\u540e\u4e8b\uff0c\u6211\u505a\u7684\u4e8b\uff0c\u4e00\u5411\u7528 \u4e0d\u7740\u522b\u4eba\u66ff\u6211\u64cd\u5fc3\u2014\u2014\u8bf4\u5230\u6700\u540e\u4e00\u5b57\uff0c\u4eba\u5df2\u8fdc\u53bb\u3002 \u3000\u3000\u94c3\u94c3\u672c\u6765\u8fd8\u5728\u8f7b\u8f7b\u54ed\u6ce3\u7740\uff0c\u6b64\u523b\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u9053\uff1a\u660e\u660e\u662f\u81ea\u5df1\u505a\u9519\u4e86\u4e8b\uff0c\u5374\u504f\u8981\u6028\u522b\u4eba \uff0c\u81ea\u5df1\u660e\u660e\u4e0d\u662f\u4e2a\u597d\u4e1c\u897f\uff0c\u5374\u504f\u8fd8\u8981\u901e\u82f1\u96c4\uff0c\u5145\u597d\u6c49\uff0c\u8fd9\u79cd\u4eba\u6211\u89c1\u4e86\u6700\u6076\u5fc3\uff0c\u6076\u5fc3\u5f97\u8981\u547d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5176\u5b9e\u5979\u5012\u5e76\u4e0d\u662f\u4f60\u60f3\u50cf\u4e2d\u7684\u90a3\u79cd\u4eba\u3002 \u3000\u3000\u94c3\u94c3\u6487\u4e86\u6487\u5634\uff0c\u9053\uff1a\u5979\u505a\u7684\u90a3\u4e9b\u4e8b\uff0c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u9053\uff1a\u65e0\u8bba\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u4f46\u5979\u7684\u672c\u6027\u8fd8\u662f\u5584\u826f\u7684\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u672c\u6027\u5584\u826f\uff0c\u5c31\u8fd8\u6709 \u6551\u6551\u836f\u3002 \u3000\u3000\u94c3\u94c3\u773c\u5708\u53c8\u7ea2\u4e86\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u4f60\u4e00\u5b9a\u8ba4\u4e3a\u6211\u7684\u672c\u6027\u5f88\u574f\uff0c\u5df2\u65e0\u53ef\u6551\u836f\u4e86\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u4f60\u8fd8\u662f\u4e2a\u5b69\u5b50\uff0c\u4f60\u4e0d\u61c2\u4ec0\u4e48\u662f\u5584\uff1f\u4ec0\u4e48\u662f\u6076\uff1f\u4ec0\u4e48\u662f\u5bf9\uff1f\u4ec0\u4e48\u662f\u9519\uff1f \u53ea\u8981\u6709\u4e2a\u4eba\u80fd\u597d\u597d\u6559\u6559\u4f60\uff0c\u8fd8\u6765\u5f97\u53ca\u3002 \u3000\u3000\u94c3\u94c3\u7728\u4e86\u773c\uff0c\u9053\uff1a\u4f60\u80af\u6559\u6211\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u8981\u6709\u673a\u4f1a\uff0c\u4ee5\u540e\u2014\u2014 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ee5\u540e\uff1f\u4e3a\u4ec0\u4e48\u8981\u7b49\u5230\u4ee5\u540e\uff0c\u73b0\u5728\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u77e5\u9053\u6211\u73b0\u5728\u4e00\u5b9a\u8981\u53bb\u627e\u90ed\u5d69\u9633\uff0c\u53ea\u8981\u6211\u8fd8\u80fd\u56de\u6765\u2014\u2014 \u3000\u3000\u94c3\u94c3\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u6211\u77e5\u9053\uff0c\u4f60\u8fd9\u4e00\u53bb\u5c31\u6c38\u8fdc\u518d\u4e5f\u4e0d\u4f1a\u56de\u5230\u8fd9\u91cc\u6765\u7684\u4e86\uff0c\u6211\u53ea\u4e0d\u8fc7 \u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u5927\u4eba\u7269\uff0c\u600e\u4e48\u4f1a\u4e3a\u4e86\u6211\u56de\u6765\uff1f \u3000\u3000\u5979\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u63a5\u7740\u9053\uff1a\u4f55\u51b5\uff0c\u6211\u672c\u6765\u4e0d\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff0c\u6211\u5c06\u6765\u662f\u597d\u662f\u574f\uff0c\u4f60\u6839\u672c\u5c31\u4e0d \u4f1a\u5173\u5fc3\uff0c\u6211\u5c06\u6765\u5c31\u7b97\u53d8\u5f97\u6bd4\u84dd\u874e\u5b50\u8fd8\u574f\u5341\u500d\uff0c\u4e5f\u548c\u4f60\u6ca1\u5173\u7cfb\uff0c\u6211\u5c31\u7b97\u88ab\u6740\u6b7b\u5728\u8def\u4e0a\uff0c\u4f60\u4e5f\u4e0d \u4f1a\u6765\u66ff\u6211\u6536\u5c38\u3002 \u3000\u3000\u5979\u8d8a\u8bf4\u8d8a\u4f24\u5fc3\u3002\u597d\u50cf\u5979\u4ee5\u540e\u82e5\u4e0d\u80fd\u5b66\u597d\uff0c\u5c31\u5b8c\u5168\u662f\u674e\u5bfb\u6b22\u5bb3\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u82e6\u7b11\u9053\uff1a\u6211\u4e00\u5b9a\u4f1a\u56de\u6765\u770b\u4f60\u7684\u2014\u2014 \u3000\u3000\u94c3\u94c3\u7528\u624b\u63a9\u7740\u8138\u9053\uff1a\u50cf\u4f60\u8fd9\u6837\u7684\u5fd9\u4eba\uff0c\u7b49\u4f60\u60f3\u5230\u6211\uff0c\u518d\u56de\u6765\u7684\u65f6\u5019\uff0c\u6211\u8bf4\u4e0d\u5b9a\u65e9\u6b7b\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u2014\u2014 \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u94c3\u94c3\u4e0d\u54ed\u4e86\uff0c\u9053\uff1a\u771f\u7684\u5f88\u5feb\uff1f\u4f60\u8bf4\u4ec0\u4e48\u65f6\u5019\uff1f\u6211\u7b49\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u8981\u6211\u8fd8\u6d3b\u7740\uff0c\u7b49\u89c1\u5230\u90ed\u5d69\u9633\u540e\uff0c\u6211\u4e00\u5b9a\u5148\u56de\u6765\u770b\u4f60\u4e00\u6b21\u3002 \u3000\u3000\u94c3\u94c3\u8df3\u4e86\u8d77\u6765\uff0c\u7834\u6d95\u4e3a\u7b11\uff0c\u9053\uff1a\u4f60\u771f\u662f\u4e2a\u597d\u4eba\uff0c\u4e3a\u4e86\u4f60\uff0c\u6211\u4e00\u5b9a\u4e5f\u8981\u505a\u4e2a\u597d\u4eba\uff0c\u53ef\u662f\u4f60 \u5343\u4e07\u4e0d\u80fd\u9a97\u6211\uff0c\u5426\u5219\u6211\u5c31\u4e0d\u4f1a\u5b66\u597d\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u4e0a\u7684\u8d1f\u62c5\u672c\u6765\u5df2\u591f\u91cd\u7684\u4e86\uff0c\u73b0\u5728\u5374\u53c8\u91cd\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u94c3\u94c3\u8fd9\u4e00\u751f\u662f\u597d\u662f\u574f\uff0c\u73b0\u5728\u7adf\u4f3c\u5df2\u53d8\u6210\u4e86\u4ed6\u7684\u8d23\u4efb\uff0c\u8fde\u63a8\u4e5f\u63a8\u4e0d\u6389\u4e86\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5 \u9053\u600e\u4e48\u4f1a\u5c06\u8fd9\u70eb\u5c71\u828b\u63a5\u5230\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u63a5\u4ee5\u7684\u70eb\u5c71\u828b\u7684\u786e\u592a\u591a\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5fc3\u91cc\u53ea\u6709\u4e00\u4ef6\u4e8b\uff01 \u3000\u3000\u53ea\u5e0c\u671b\u90ed\u5d69\u9633\u6ca1\u6709\u9047\u5230\u8346\u65e0\u547d\u548c\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u81ea\u5df1\u8d76\u53bb\u8fd8\u4e0d\u592a\u8fdf\u3002 \u3000\u3000\u73b0\u5728\u7684\u786e\u8fd8\u4e0d\u592a\u8fdf\u3002 \u3000\u3000\u79cb\u65e5\u4ecd\u672a\u843d\u5230\u5c71\u540e\uff0c\u6cc9\u6c34\u5728\u9633\u5149\u91cc\u95ea\u70c1\u5982\u91d1\u3002 \u3000\u3000\u91d1\u9ec4\u8272\u7684\u6cc9\u6c34\u4e2d\uff0c\u5ffd\u7136\u98d8\u6765\u4e00\u7247\u67ab\u53f6\uff0c\u63a5\u7740\u662f\u4e24\u7247\uff0c\u4e09\u7247\u2014\u2014\u65e0\u6570\u7247 \u3000\u3000\u79cb\u5c1a\u672a\u6b8b\uff0c\u67ab\u53f6\u600e\u4e48\u4f1a\u843d\u5462\uff1f \u3000\u3000\u96be\u9053\u8fd9\u4e9b\u67ab\u53f6\u4f1a\u662f\u88ab\u8346\u65e0\u547d\u548c\u90ed\u5d69\u9633\u7684\u5251\u6c14\u6467\u843d\u7684\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u60c5\u66f4\u6c89\u91cd\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4ece\u8fd9\u4e9b\u843d\u53f6\u4e2d\u770b\u51fa\u4e86\u4e24\u4ef6\u4e8b\u3002 \u3000\u3000\u90ed\u5d69\u9633\u548c\u8346\u65e0\u547d\u3001\u4e0a\u5b98\u91d1\u8679\u7684\u51b3\u6218\u5fc5\u5df2\u5f00\u59cb\uff01 \u3000\u3000\u8fd9\u4e00\u573a\u51b3\u6218\u5fc5\u5b9a\u662f\u60ca\u5fc3\u52a8\u9b44\uff0c\u60e8\u70c8\u65e0\u6bd4\u3002 \u3000\u3000\u90ed\u5d69\u9633\u5fc5\u5df2\u9690\u5165\u82e6\u6597\u4e4b\u4e2d\uff0c\u662f\u4ee5\u67ab\u6797\u624d\u4f1a\u88ab\u4ed6\u4eec\u7684\u5251\u6c14\u6467\u6b8b\u5f97\u5982\u6b64\u4e4b\u5267\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u4ed6 \u81f3\u5c11\u5df2\u652f\u6301\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4ed6\u662f\u5426\u8fd8\u80fd\u652f\u6301\u4e0b\u53bb\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6068\u4e0d\u80fd\u808b\u751f\u53cc\u7fc5\uff0c\u7acb\u523b\u98de\u5230\u90a3\u91cc\u3002 \u3000\u3000\u6ee1\u5c71\u7ea2\u53f6\u7adf\u5df2\u88ab\u5251\u6c14\u6467\u843d\u5341\u4e4b\u516d\u4e03\u3002\u5929\u5730\u8427\u6740\uff0c\u843d\u53f6\u5728\u79cb\u98ce\u4e2d\u5377\u821e\uff0c\u770b\u6765\u5c31\u5b9b\u5982\u6ee1\u5929\u8840 \u4e91\u3002 \u3000\u3000\u6076\u6218\u83ab\u975e\u5df2\u7ed3\u675f\uff1f \u3000\u3000\u6218\u80dc\u7684\u662f\u8c01\uff1f \u3000\u3000\u67ab\u6797\u4e2d\u5bc2\u65e0\u4eba\u5f71\uff0c\u79cb\u98ce\u7eb5\u80fd\u8bed\uff0c\u5374\u4e5f\u65e0\u6cd5\u8bf4\u51fa\u674e\u5bfb\u6b22\u60f3\u77e5\u9053\u7684\u6d88\u606f\uff0c\u53ea\u6709\u6d41\u6c34\u7684\u545c\u54bd\uff0c \u4eff\u4f5b\u5728\u4e3a\u6218\u8d25\u7684\u4eba\u60b2\u60dc\u3002 \u3000\u3000\u90ed\u5d69\u9633\u82e5\u5df2\u6218\u6b7b\uff0c\u4ed6\u7684\u5c38\u8eab\u5728\u54ea\u91cc\uff1f \u3000\u3000\u6cc9\u6c34\u4e2d\u7684\u843d\u53f6\u6e10\u8fdc\u3001\u6e10\u758f\u3002 \u3000\u3000\u79cb\u65e5\u7ec8\u4e8e\u5df2\u6ca1\u5165\u5c71\u540e\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u672c\u6765\u6781\u6e05\u6f88\u7684\u6cc9\u6c34\uff0c\u6b64\u523b\u7adf\u5e26\u7740\u4e00\u4e1d\u6de1\u6de1\u7684\u7ea2\u8272\u3002 \u3000\u3000\u662f\u4e0d\u662f\u6218\u8d25\u8005\u7684\u9c9c\u8840\u5c06\u6d41\u6c34\u67d3\u7ea2\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u62ac\u8d77\u5934\uff0c\u5927\u6b65\u6cc9\u6c34\u5c3d\u5934\u5904\u8d70\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e00\u7f15\u98de\u6cc9\uff0c\u81ea\u5c71\u5dc5\u5012\u6302\u800c\u4e0b\uff0c\u4e00\u6cfb\u767e\u592a \uff0c\u77eb\u82e5\u795e\u9f99\u3002 \u3000\u3000\u5728\u8fd9\u767e\u592a\u98de\u6cc9\u4e2d\uff0c\u7adf\u5b64\u96f6\u96f6\u7684\u6302\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u5c31\u6302\u5728\u79bb\u5730\u9762\u4e24\u4e09\u592a\u5904\uff0c\u6cc9\u6c34\u4e00\u6cfb\u6570\u5341\u592a\uff0c\u5230\u4e86\u8fd9\u91cc\uff0c\u6c34\u529b\u6700\u731b\uff0c\u5374\u4e5f\u672a\u80fd\u5c06\u8fd9\u4eba \u51b2\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u7a7f\u7684\u4eff\u4f5b\u662f\u4ef6\u9ed1\u8272\u7684\u8863\u670d\uff0c\u76f4\u633a\u633a\u7684\u6302\u5728\u90a3\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5931\u58f0\u9053\uff1a\u90ed\u5d69\u9633\u2014\u2014\u90ed\u5144\u2014\u2014 \u3000\u3000\u4ed6\u8eab\u5f62\u5df2\u968f\u7740\u547c\u58f0\u98de\u63a0\u800c\u8d77\uff0c\u53ea\u89c9\u773c\u524d\u6c34\u96fe\u8ff7\u8499\uff0c\u5bd2\u6c14\u88ad\u4eba \u3000\u3000\u4ed6\u7684\u4eba\u5374\u5df2\u94bb\u5165\u4e86\u98de\u6cc9\uff0c\u62c9\u4f4f\u4e86\u90a3\u4eba\u7684\u624b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u770b\u9519\uff0c\u6302\u5728\u98de\u6cc9\u4e2d\u7684\u8fd9\u4eba\u7684\u786e\u662f\u90ed\u5d69\u9633\u3002 \u3000\u3000\u674e\u5168\u8eab\u51b0\u51c9\uff0c\u5df2\u5168\u65e0\u4e1d\u6beb\u6696\u610f\uff0c\u4f46\u4ed6\u7684\u4e00\u53ea\u624b\u5374\u8fd8\u662f\u7d27\u7d27\u7684\u63e1\u7740\u5251\u67c4\uff0c\u6b7b\u4e5f\u4e0d\u80af\u653e\u677e\u3002 \u3000\u3000\u4ed6\u90a3\u67c4\u540d\u52a8\u5929\u4e0b\u7684\u5d69\u9633\u94c1\u5251\uff0c\u5df2\u9f50\u67c4\u6ca1\u5165\u4e86\u5c71\u77f3\u4e2d\uff0c\u663e\u89c1\u4ed6\u662f\u5728\u4e34\u6b7b\u4e4b\u524d\u62fc\u5c3d\u6700\u540e\u4e00\u5206 \u529b\u6c14\uff0c\u5c06\u8fd9\u67c4\u5251\u63d2\u5165\u5c71\u77f3\uff0c\u5c06\u81ea\u5df1\u7684\u4eba\u6302\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u521a\u5c06\u4ed6\u7684\u5c38\u8eab\u89e3\u4e0b\uff0c\u5e73\u653e\u5728\u6cc9\u6c34\u65c1\u7684\u77f3\u5934\u4e0a\uff0c\u5c31\u542c\u5230\u8eab\u540e\u6709\u4eba\u95ee\uff1a\u4ed6\u8fd9\u6837\u505a\u662f\u4e3a \u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u6839\u672c\u7528\u4e0d\u7740\u56de\u5934\u770b\uff0c\u674e\u5bfb\u6b22\u5c31\u5df2\u542c\u51fa\u8fd9\u662f\u94c3\u94c3\u7684\u58f0\u97f3\uff0c\u8fd9\u4f4d\u59d1\u5a18\u597d\u8c61\u5df2\u51b3\u5fc3\u8981\u7f20\u7740\u5979\uff0c \u7adf\u5728\u540e\u9762\u8ddf\u7740\u6765\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u63a5\u7740\u9053\uff1a\u4ed6\u4e3a\u4ec0\u4e48\u8981\u628a\u81ea\u5df1\u6302\u5230\u90a3\u91cc\u53bb\uff1f\u96be\u9053\u4ed6\u6015\u4f60\u627e\u4e0d\u7740\u4ed6\uff1f\u96be\u9053\u4ed6\u4e34\u6b7b\u524d\u8fd8\u60f3 \u5c06\u81ea\u5df1\u51b2\u6d17\u5e72\u51c0\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u4e00\u4e2a\u4eba\u5e72\u5e72\u51c0\u51c0\u7684\u6765\uff0c\u672c\u8be5\u5e72\u5e72\u51c0\u51c0\u7684\u8d70\uff0c\u53ea\u4e0d\u8fc7\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6 \u5f53\u7136\u8fd8\u6709\u522b\u7684\u610f\u601d\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ec0\u4e48\u610f\u601d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u4ed6\u4e0d\u613f\u522b\u4eba\u5c06\u4ed6\u7684\u5c38\u8eab\u57cb\u846c\uff0c\u4e5f\u4e0d\u613f\u522b\u4eba\u5c06\u4ed6\u5e26\u8d70\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8fd9\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u96be\u9053\u4ed6\u8fd8\u8981\u5728\u8fd9\u91cc\u7b49\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u4ed6\u6b63\u662f\u4e3a\u4e86\u8981\u7b49\u6211\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u4eba\u5df2\u6b7b\u4e86\uff0c\u8fd8\u7b49\u4f60\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4ef0\u9762\u5411\u5929\uff0c\u4e00\u5b57\u5b57\uff1a\u56e0\u4e3a\u4ed6\u6709\u4e9b\u8bdd\u8981\u544a\u8bc9\u6211\u3002 \u3000\u3000\u94c3\u94c3\u6014\u4f4f\u4e86\uff0c\u53ea\u89c9\u8eab\u4e0a\u6709\u4e9b\u51c9\u98d5\u98d5\u7684\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u5403\u5403\u9053\uff1a\u4f60\u2014\u2014\u4f60\u8bf4\u4ed6\u8fd8\u6709\u8bdd\u8981\u544a \u8bc9\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u60f3\u544a\u8bc9\u4f60\u4ec0\u4e48\uff1f\u4f60\u96be\u9053\u5df2\u77e5\u9053\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u5df2\u77e5\u9053\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u5df2\u544a\u8bc9\u4e86\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u4f60\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u6b7b\u4e86\u3002 \u7b2c\u56db\u5341\u516d\u7ae0\u3000\u82f1\u96c4\u4e0e\u67ad\u96c4>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u516d\u7ae0\u3000\u82f1\u96c4\u4e0e\u67ad\u96c4 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u4e86\u770b\u90ed\u5d69\u9633\u7684\u5c38\u4f53\uff0c\u957f\u53f9\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u6bd5\u7adf\u8fd8\u662f\u6765\u8fdf\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u7684\u4eba\u65e2\u7136\u5df2\u6b7b\u4e86\uff0c\u8fd8\u80fd\u5bf9\u4f60\u8bf4\u8bdd\uff1f\u2014\u2014\u96be\u9053\u6b7b\u4eba\u8fd8\u80fd\u8bf4\u8bdd\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6709\u4e9b\u8bdd\uff0c\u7528\u4e0d\u7740\u8bf4\u51fa\uff0c\u6211\u4e5f\u53ef\u4ee5\u542c\u5230\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u6211\u600e\u4e48\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u5979\u8d8a\u6765\u8d8a\u4e0d\u61c2\u4e86\uff0c\u6240\u4ee5\u6765\u8d8a\u5bb3\u6015\u3002 \u3000\u3000\u4eba\u4eec\u5bf9\u81ea\u5df1\u4e0d\u61c2\u7684\u4e8b\uff0c\u603b\u4f1a\u89c9\u5f97\u6709\u4e9b\u5bb3\u6015\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u4f60\u4e5f\u60f3\u77e5\u9053\u4ed6\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5176\u5b9e\u4ed6\u4e5f\u5df2\u5c06\u90a3\u4e9b\u8bdd\u544a\u8bc9\u4e86\u4f60\uff0c\u53ea\u4e0d\u8fc7\u4f60\u6ca1\u6709\u6ce8\u610f\u53bb\u542c\u800c\u5df2\uff0c\u8981\u77e5\u9053\u6b7b\u4eba\u544a \u8bc9\u4f60\u7684\u8bdd\uff0c\u5f80\u5f80\u662f\u6700\u53ef\u8d35\u7684\uff0c\u56e0\u4e3a\u8fd9\u662f\u4ed6\u4ee5\u81ea\u5df1\u751f\u547d\u6362\u6765\u7684\u6559\u8bad\uff0c\u4f60\u82e5\u80fd\u5b66\u4f1a\u542c\u6b7b\u4eba\u8bf4\u8bdd\uff0c \u5c31\u53ef\u4ee5\u591a\u61c2\u5f97\u8bb8\u591a\u4e8b\u3002 \u3000\u3000\u94c3\u94c3\u5634\u5507\u5df2\u6709\u4e9b\u53d1\u767d\uff0c\u9053\uff1a\u53ef\u662f\u6b7b\u4eba\u8bf4\u8bdd\u6211\u600e\u4e48\u80fd\u542c\u5230\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8981\u5b66\u4f1a\u542c\u6b7b\u4eba\u8bf4\u7684\u8bdd\uff0c\u81ea\u7136\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u4f46\u4f60\u82e5\u60f3\u591a\u6d3b\u51e0\u5e74\uff0c\u6d3b\u5f97\u597d\u4e9b\uff0c \u5c31\u8be5\u60f3\u6cd5\u5b50\u5b66\u4f1a\u3002 \u3000\u3000\u4ed6\u795e\u8272\u5f88\u90d1\u91cd\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u5f00\u73a9\u7b11\u7684\u610f\u601d\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6211\u2014\u2014\u6211\u4e0d\u77e5\u9053\u8be5\u600e\u6837\u5b66\uff0c\u4f60\u80af\u6559\u6211\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u518d\u4ed4\u7ec6\u542c\u542c\u3002 \u3000\u3000\u94c3\u94c3\u95ed\u8d77\u4e86\u773c\u775b\u3002 \u3000\u3000\u5979\u7684\u786e\u662f\u5728\u4e00\u5fc3\u4e00\u610f\u7684\u542c\uff0c\u53ef\u662f\u5979\u8fde\u4e00\u4e2a\u5b57\u90fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u4f46\u8981\u8033\u6735\u542c\uff0c\u8fd8\u8981\u7528\u773c\u775b\u542c\u3002 \u3000\u3000\u94c3\u94c3\u5f20\u5f00\u4e86\u773c\u775b\u3002 \u3000\u3000\u53ea\u89c1\u90ed\u5d69\u9633\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u672c\u5df2\u88ab\u5251\u950b\u523a\u7834\u4e86\u5f88\u591a\u5904\uff0c\u518d\u88ab\u6731\u6c34\u51b2\u6fc0\uff0c\u6b64\u523b\u51e0\u4e4e\u4e5f\u662f\u8d64\u88f8 \u7740\u7684\u3002 \u3000\u3000\u4ed6\u7684\u808c\u80a4\u5df2\u53d8\u6210\u7070\u8272\uff0c\u56e0\u4e3a\u4ed6\u7684\u8840\u5df2\u6d41\u5c3d\uff0c\u518d\u7ecf\u8fc7\u6cc9\u6c34\u51b2\u6d17\uff0c\u4e00\u8fd8\u662f\u793e\u7684\u76ae\u8089\u90fd\u7ffb\u4e86\u8d77 \u6765\uff0c\u5374\u770b\u4e0d\u5230\u4e1d\u6beb\u8840\u8ff9\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u674e\u5bfb\u6b22\u95ee\uff1a\u4f60\u5df2\u542c\u51fa\u4e86\u4ec0\u4e48\uff1f\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6211\u2014\u2014\u6211\u770b\u51fa\u4ed6\u8eab\u53d7\u4e86\u5f88\u591a\u5904\u4f24\uff0c\u4e00\u5171\u6709\u5341\u2014\u2014\u5341\u4e5d\u5904\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f55\u4ee5\u89c1\u5f97\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u56e0\u4e3a\u4ed6\u7684\u4f24\u53e3\u90fd\u5f88\u77ed\uff0c\u4e5f\u4e0d\u592a\u6df1\uff0c\u663e\u89c1\u53ea\u662f\u4e00\u79cd\u5175\u5203\u7684\u5c16\u950b\u523a\u7834\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u662f\u5251\u5c16\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u56e0\u4e3a\u5200\u5c16\u67aa\u5c16\u90fd\u4e0d\u53ef\u80fd\u6709\u8fd9\u4e48\u950b\u5229\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u5f88\u597d\uff0c\u4f60\u5df2\u5b66\u4f1a\u5f88\u591a\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u7b11\u9053\uff1a\u7531\u6b64\u53ef\u89c1\uff0c\u4f24\u4ed6\u7684\u4eba\u4e00\u5b9a\u662f\u8346\u65e0\u547d\uff0c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u7528\u7684\u662f\u9f99\u51e4\u73af\uff0c\u4e0d\u662f\u5251\u3002 \u4e0a\u5b98\u91d1\u8679\u4e5f\u8bb8\u5e76\u6ca1\u6709\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u4ed6\u867d\u7136\u6765\u4e86\uff0c\u5374\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u94c3\u94c3\u70b9\u7740\u5934\uff0c\u5ffd\u7136\u9053\uff1a\u8fd9\u4e9b\u5251\u4f24\u90fd\u662f\u659c\u7684\uff0c\u4e0b\u9762\u8f83\u6df1\uff0c\u4e0a\u9762\u8f83\u6d45\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u7531\u6b64\u53ef\u89c1\uff0c\u5bf9\u65b9\u7684\u5251\u6bcf\u4e00\u5251\u90fd\u662f\u7531\u4e0b\u9762\u53cd\xd7\u4e0a\u53bb\uff0c\u8fd9\u79cd\u5251\u6cd5\u4e00\u5b9a\u5947\u602a\u5f97\u5f88\uff0c\u6211 \u5e38\u542c\u4eba\u8bf4\u8346\u65e0\u547d\u7684\u5251\u6cd5\u8be1\u5f02\u8fc5\u6025\uff0c\u6b66\u6797\u7f55\u7779\uff0c\u5982\u4eca\u770b\u6765\u679c\u7136\u4e0d\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\uff0c\u4ed6\u7684\u5251\u6cd5\u4e0d\u4f46\u8be1\u79d8\u602a\u5f02\uff0c\u800c\u4e14\u4e13\u8d70\u504f\u950b\uff0c\u6bcf\u4e00\u5251\u51fa\u624b\u7684\u90e8\u4f4d\uff0c\u90fd\u662f\u5bf9 \u65b9\u7edd\u4e0d\u4f1a\u60f3\u5230\u7684\u3002 \u3000\u3000\u4ed6\u6307\u7740\u90ed\u5d69\u9633\u819d\u76d6\u4e0a\u4e00\u5904\u4f24\u53e3\u9053\uff1a\u4f60\u770b\u8fd9\u4e00\u5251\u2014\u2014\u8fd9\u4e00\u5251\u82e5\u662f\u81ea\u4e0a\u523a\u4e0b\uff0c\u90a3\u5012\u4e5f\u5e73\u5e73\u65e0 \u5947\uff0c\u4f46\u8fd9\u4f24\u53e3\u4e5f\u662f\u4e0b\u6df1\u4e0a\u6d45\uff0c\u53ef\u89c1\u5bf9\u65b9\u8fd9\u4e00\u5251\u4e5f\u662f\u4ece\u4e0b\u9762\u53cd\xd7\u4e0a\u6765\u7684\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u7531\u6b64\u53ef\u89c1\u8346\u65e0\u547d\u51fa\u624b\u7684\u90e8\u4f4d\uff0c\u5fc5\u5b9a\u5728\u819d\u76d6\u4ee5\u4e0b\uff0c\u7528\u7684\u5c31\u5fc5\u5b9a\u662f\u8155\u529b\uff0c\u6211\u82e5\u4e0d \u770b\u5230\u8fd9\u4f24\u53e3\uff0c\u5c31\u4e5f\u60f3\u4e0d\u5230\u6709\u4eba\u4f1a\u5728\u8fd9\u79cd\u90e8\u4f4d\u51fa\u624b\u3002 \u3000\u3000\u94c3\u94c3\u53ea\u6709\u70b9\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u770b\u5230\u7684\u53ea\u662f\u4ed6\u6b63\u9762\uff0c\u4ed6\u80cc\u540e\u8fd8\u6709\u4e03\u5904\u4f24\u53e3\uff0c\u4ee5\u90ed\u5d69\u9633\u7684\u6b66\u529f\uff0c\u7edd\u4e0d\u4f1a\u5c06\u540e \u80cc\u90fd\u5356\u7ed9\u5bf9\u65b9\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u82e5\u548c\u4eba\u4ea4\u624b\u65f6\uff0c\u4e5f\u4e0d\u4f1a\u5c06\u80cc\u5bf9\u7740\u4eba\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u7531\u6b64\u53ef\u89c1\uff0c\u4ed6\u8fd9\u4e9b\u4f24\u53e3\u4e00\u5b9a\u662f\u5728\u4e24\u4eba\u8eab\u5f62\u4ea4\u9519\u65f6\u88ab\u8346\u65e0\u547d\u6240\u4f24\u7684\uff0c\u90a3\u4e48\u8346\u65e0 \u547d\u7684\u5251\u53ea\u6709\u4ece\u81ea\u5df1\u7684\u808b\u4e0b\u7a7f\u51fa\uff0c\u624d\u80fd\u523a\u5f97\u5230\u5bf9\u65b9\u3002 \u3000\u3000\u4ed6\u53f9\u606f\u9053\uff1a\u81ea\u80c1\u4e0b\u51fa\u624b\u672c\u5df2\u4e0d\u662f\u5e38\u89c1\u7684\u5251\u6cd5\uff0c\u6700\u602a\u7684\u662f\uff0c\u8fd9\u51e0\u5251\u4e5f\u662f\u81ea\u4e0b\u9762\u53cd\xd7\u4e0a\u53bb\u7684 \uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u8346\u65e0\u547d\u5fc5\u5b9a\u5df2\u5728\u4e24\u4eba\u8eab\u5f62\u4ea4\u9519\u65f6\u90a3\u4e00\u77ac\u95f4\uff0c\u6539\u53d8\u4e86\u63e1\u5251\u7684\u59ff\u52bf\uff0c\u53ef\u4e58\u52bf\u5c06\u5251\u53cd \u523a\u800c\u51fa\uff0c\u4ed6\u53d8\u52bf\u4e0e\u51fa\u624b\uff0c\u663e\u89c1\u53ea\u662f\u4e00\u4e2a\u52a8\u4f5c\uff0c\u6240\u4ee5\u901f\u5ea6\u5fc5\u5b9a\u5feb\u5f97\u53ef\u6015\uff01 \u3000\u3000\u94c3\u94c3\u542c\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u539f\u6765\u4ed6\u5c31\u662f\u8981\u544a\u8bc9\u4f60\u8fd9\u4e9b\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u82e5\u975e\u5982\u6b64\uff0c\u4ee5\u4ed6\u7684\u6b66\u529f\uff0c\u672c\u4e0d\u8be5\u53d7\u8fd9\u591a\u5904\u4f24\u7684\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9ad8\u624b\u51b3\u6597\uff0c\u80dc\u8d1f\u5f80\u5f80\u53ea\u5728\u4e00\u62db\u4e4b\u95f4\uff0c\u65e0\u8bba\u8c01\u7684\u5251\u6cd5\u6709\u4e86\u4e1d\u6beb\u7834\u9699\uff0c\u5bf9\u65b9\u7edd\u4e0d \u4f1a\u653e\u8fc7\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8fd9\u6211\u660e\u767d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u60f3\uff0c\u5d69\u9633\u94c1\u5251\u4eab\u8a89\u6b66\u6797\u4e8c\u5341\u5e74\uff0c\u5355\u4ee5\u5251\u6cd5\u800c\u8bba\uff0c\u5df2\u53ef\u7b97\u662f\u5f53\u4eca\u5929\u4e0b\u6570\u4e00\u6570 \u4e8c\u7684\u9ad8\u624b\uff0c\u53c8\u600e\u4f1a\u5728\u4e00\u573a\u6bd4\u6597\u4e2d\u63a5\u8fde\u9732\u51fa\u4e8c\u5341\u516d\u5904\u7834\u7efd\uff0c\u63a5\u8fde\u88ab\u5bf9\u65b9\u523a\u4f24\u4e8c\u5341\u516d\u5904\u5462\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8fd9\u5012\u7684\u786e\u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd8\u6709\uff0c\u8346\u65e0\u547d\u7684\u5251\u6cd5\u65e2\u7136\u90a3\u4e48\u6bd2\u8fa3\uff0c\u90ed\u5d69\u9633\u8fd9\u4e8c\u5341\u516d\u5904\u4f24\u53e3\u90fd\u662f\u8f7b\u4f24\uff0c\u8346\u65e0 \u547d\u53c8\u600e\u4f1a\u5728\u4ed6\u63a5\u8fde\u9732\u51fa\u4e86\u4e8c\u5341\u516d\u6b21\u7834\u7efd\u540e\uff0c\u8fd8\u4e0d\u80fd\u4e00\u5251\u523a\u6b7b\u4ed6\u5462\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u662f\u5440\u2014\u2014\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u91cd\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u8fd9\u53ea\u56e0\u90ed\u5d69\u9633\u8fd9\u4e8c\u5341\u516d\u6b21\u7834\u7efd\uff0c\u90fd\u662f\u6545\u610f\u9732\u51fa\u7684\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6545\u610f\u9732\u51fa\u6765\u7684\u2014\u2014\u4ed6\u96be\u9053\u6545\u610f\u8981\u8346\u65e0\u547d\u523a\u4f24\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\uff0c\u5c31\u56e0\u4e3a\u4ed6\u7834\u7efd\u662f\u6545\u610f\u9732\u51fa\u6765\u7684\uff0c\u6240\u4ee5\u6bcf\u6b21\u90fd\u80fd\u53ca\u65f6\u95ea\u907f\uff0c\u6240\u4ee5\u4ed6\u6bcf\u6b21 \u53d7\u7684\u4f24\u90fd\u4e0d\u592a\u91cd\u3002 \u3000\u3000\u94c3\u94c3\u66f4\u4e0d\u61c2\u4e86\uff0c\u9053\uff1a\u4ed6\u8fd9\u4e48\u505a\u53c8\u662f\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u9053\uff1a\u4ed6\u8fd9\u6837\u505a\uff0c\u53ea\u4e3a\u4e86\u5c06\u8346\u65e0\u547d\u51fa\u624b\u7684\u90e8\u4f4d\u544a\u8bc9\u6211\uff01 \u3000\u3000\u94c3\u94c3\u7b80\u76f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u53c8\u6d41\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u6211\u672c\u6765\u4ee5\u4e3a\u4e16\u4e0a\u8fde\u4e00\u4e2a\u597d\u4eba\u90fd\u6ca1\u6709\uff0c\u4eba\u4eec\u4ea4\u670b\u53cb\uff0c\u4e5f\u662f \u4e3a\u4e86\u4e92\u76f8\u5229\u7528\uff0c\u6240\u4ee5\u4e00\u4e2a\u4eba\u82e5\u8981\u597d\u597d\u7684\u6d3b\u7740\uff0c\u5c31\u5f97\u5148\u5b66\u4f1a\u5982\u4f55\u53bb\u5229\u7528\u522b\u4eba\uff0c\u6b3a\u9a97\u522b\u4eba\uff0c\u5343\u4e07 \u4e0d\u80fd\u8bb2\u4ec0\u4e48\u9053\u4e49\uff0c\u5426\u5219\u5403\u4e8f\u7684\u4e00\u5b9a\u662f\u81ea\u5df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u4e9b\u8bdd\uff0c\u81ea\u7136\u4e5f\u5168\u90fd\u662f\u6797\u4ed9\u513f\u6559\u4f60\u7684\u3002 \u3000\u3000\u94c3\u94c3\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u4f46\u73b0\u5728\u6211\u5374\u77e5\u9053\uff0c\u8fd9\u4e16\u4e0a\u6bd5\u7adf\u662f\u6709\u597d\u4eba\u7684\uff0c\u6c5f\u6e56\u95f4\u4e5f\u7684\u786e\u6709\u8f7b\u751f\u6b7b \uff0c\u91cd\u4e49\u6c14\u7684\u670b\u53cb\u3002 \u3000\u3000\u5979\u5ffd\u7136\u5728\u90ed\u5d69\u9633\u5c38\u8eab\u524d\u8dea\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u90ed\u5148\u751f\uff0c\u4f60\u867d\u7136\u4e0d\u5e78\u6b7b\u4e86\uff0c\u53ef\u662f\u4f60\u4e0d\u4f46\u5e2e\u52a9\u4e86\u4f60 \u7684\u670b\u53cb\uff0c\u4e5f\u4f7f\u6211\u660e\u767d\u4e86\u505a\u4eba\u7684\u9053\u7406\uff0c\u4f60\u2014\u2014\u4f60\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u8be5\u7791\u76ee\u4e86\u3002 \u3000\u3000\u5c71\u5916\u7684\u53e4\u9053\u4e0a\uff0c\u6b63\u6709\u4e24\u4e2a\u4eba\u5728\u884c\u8d70\u7740\uff0c\u659c\u9633\u7684\u4f59\u8f89\u7167\u7740\u4ed6\u4eec\u7684\u8863\u670d\uff0c\u4ed6\u4eec\u7684\u8863\u670d\u4e0a\u4e5f\u95ea \u8000\u7740\u4e00\u79cd\u8be1\u5f02\u7684\u91d1\u5149\u3002 \u3000\u3000\u4ed6\u4eec\u8d70\u5f97\u4e0d\u5feb\u4e5f\u4e0d\u6162\uff0c\u770b\u6765\u90fd\u5f88\u5b89\u8be6\uff0c\u9664\u4e86\u811a\u6b65\u79fb\u52a8\u5916\uff0c\u4e24\u4eba\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55 \u522b\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u8eab\u4e0a\u4f3c\u4e4e\u5e26\u7740\u79cd\u65e0\u5f62\u7684\u6740\u6c14\uff0c\u4ed6\u4eec\u8fd8\u672a\u8d70\u5165\u6811\u6797\uff0c\u6797\u4e2d\u7684\u5f52\u9e26\u5df2\u88ab\u8fd9\u79cd\u6740\u6c14\u6240 \u60ca\uff0c\u7eb7\u7eb7\u98de\u8d77\u3002 \u3000\u3000\u751f\u547d\uff0c\u5728\u4ed6\u773c\u4e2d\u770b\u6765\u6839\u672c\u5c31\u5df2\u8db3\u8f7b\u91cd\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u5141\u8bb8\u4efb\u4f55\u6709\u751f\u547d\u4e4b\u7269\u538b\u5728\u4ed6\u5934\u4e0a\uff01 \u3000\u3000\u6811\u6797\u91cc\u5f88\u660f\u6697\u3002 \u3000\u3000\u8d70\u5230\u8fd9\u91cc\uff0c\u524d\u9762\u4e00\u4eba\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u51e0\u4e4e\u4e5f\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\uff0c\u540e\u9762\u4e00\u4eba\u7684\u811a\u6b65\u4e5f\u968f\u7740 \u505c\u4e0b\u3002 \u3000\u3000\u524d\u9762\u4e00\u4e2a\u6b63\u662f\u4e0a\u5b98\u91d1\u8679\uff0c\u6b64\u523b\u9053\uff1a\u90ed\u5d69\u9633\u7684\u5251\u6cd5\u5982\u4f55\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u597d\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u5f88\u597d\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u5f88\u597d\uff0c\u5728\u4e03\u5927\u5251\u6d41\u638c\u95e8\u4e4b\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f46\u4ed6\u4e0e\u4f60\u4ea4\u624b\u65f6\uff0c\u9732\u51fa\u7684\u7834\u7efd\u5374\u8fbe\u4e8c\u5341\u516d\u6b21\u4e4b\u591a\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4e8c\u5341\u4e5d\u6b21\uff0c\u6709\u4e09\u6b21\u6211\u672a\u51fa\u624b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u4e0d\u9519\uff0c\u6709\u4e09\u6b21\u4f60\u672a\u51fa\u624b\uff0c\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u56e0\u4e3a\u90a3\u4e09\u6b21\u6211\u82e5\u51fa\u624b\uff0c\u4fbf\u53ef\u8981\u4ed6\u7684\u547d\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u5df2\u770b\u51fa\u4ed6\u90a3\u4e9b\u7834\u7efd\u662f\u6545\u610f\u9732\u51fa\u6765\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4e0d\u9519\uff0c\u6240\u4ee5\u6211\u4e0d\u613f\u4ed6\u6b7b\u5f97\u592a\u5feb\uff0c\u6211\u6b63\u597d\u62ff\u4ed6\u6765\u7ec3\u5251\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u9732\u51fa\u90a3\u4e9b\u7834\u7efd\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4e0d\u77e5\u9053\uff0c\u6211\u6ca1\u6709\u53bb\u60f3\u3002 \u3000\u3000\u9664\u4e86\u6740\u4eba\u7684\u5251\u6cd5\u5916\uff0c\u4ed6\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u613f\u53bb\u60f3\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4ed6\u6545\u610f\u9732\u51fa\u90a3\u4e9b\u7834\u7efd\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4f60\u523a\u4f24\u4ed6\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u54e6\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4ed6\u81ea\u77e5\u7edd\u975e\u6211\u4eec\u654c\u624b\uff0c\u6240\u4ee5\u624d\u8fd9\u4e48\u6837\u505a\uff0c\u597d\u8ba9\u674e\u5bfb\u6b22\u770b\u4e86\u4ed6\u8eab\u4e0a\u7684\u4f24\u53e3\uff0c \u5c31\u53ef\u770b\u51fa\u4f60\u51fa\u624b\u7684\u90e8\u4f4d\u3002 \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u63a5\u7740\u9053\uff1a\u7531\u6b64\u53ef\u89c1\uff0c\u4ed6\u5fc5\u5b9a\u65e9\u5df2\u77e5\u9053\u674e\u5bfb\u6b22\u4eba\u8ddf\u7740\u53bb\u7684\uff0c\u4f60\u6211\u73b0\u5728\u82e5\u662f\u52a0\u5934 \uff0c\u5fc5\u5b9a\u53ef\u4ee5\u5728\u90a3\u91cc\u627e\u5230\u4ed6\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6b63\u5728\u963f\u98de\u7684\u6728\u5c4b\u4e2d\u627e\u7740\u67c4\u9504\u5934\uff0c\u6b63\u5728\u6398\u575f\uff0d\u6b7b\u5728\u54ea\u91cc\uff0c\u5c31\u846c\u5728\u54ea\u91cc\uff0c\u8fd9\u6b63\u662f\u5927\u591a \u6570\u6c5f\u6e56\u4eba\u7684\u5f52\u5bbf\u3002 \u3000\u3000\u94c3\u94c3\u4e00\u76f4\u5728\u65c1\u8fb9\u770b\u7740\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u94c3\u94c3\u52a8\u624b\uff0c\u4ed6\u8981\u4e00\u4e2a\u4eba\u6398\u6210\u8fd9\u4e2a\u575f\u5893\uff0c\u4ed6\u8be5\u505a\u7684\u4e8b \uff0c\u4ece\u4e0d\u613f\u4efb\u4f55\u4eba\u63d2\u624b\u3002 \u3000\u3000\u6b64\u523b\u94c3\u94c3\u9053\uff1a\u4f60\u771f\u7684\u8981\u5c06\u90ed\u5148\u751f\u846c\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e00\u4e2a\u4eba\u53ea\u8981\u6b7b\u5f97\u5149\u8363\uff0c\u65e0\u8bba\u846c\u5728\u54ea\u91cc\u90fd\u662f\u4e00\u6837\u7684\uff0c\u662f\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u4f60\u5c31\u4e0d\u8be5\u5c06\u4ed6\u846c\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u846c\u5728\u8fd9\u91cc\uff0c\u846c\u5728\u54ea\u91cc\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u5e94\u8be5\u5c06\u4ed6\u518d\u6302\u5230\u90a3\u8fb9\u7684\u98de\u6cc9\u4e2d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u7740\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8c61\u4e0a\u5b98\u91d1\u8679\u548c\u8346\u65e0\u547d\u8fd9\u6837\u7684\u89d2\u8272\uff0c\u8fdf\u65e9\u5fc5\u5b9a\u4f1a\u770b\u7834\u90ed\u5148\u751f\u7684\u5fc3\u610f\uff0c\u662f\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\uff1a\u662f\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u8346\u65e0\u547d\u81ea\u7136\u4e0d\u613f\u8ba9\u4f60\u770b\u7834\u4ed6\u5251\u6cd5\u51fa\u624b\u7684\u90e8\u4f4d\uff0c\u6240\u4ee5\u53ea\u8981\u4ed6\u4eec\u4e00\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u5c31 \u5fc5\u5b9a\u4f1a\u7acb\u523b\u56de\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u4eec\u56de\u6765\u65f6\uff0c\u82e5\u662f\u53d1\u73b0\u90ed\u5148\u751f\u7684\u5c38\u4f53\u5df2\u4e0d\u5728\u539f\u6765\u7684\u5730\u65b9\u4e86\uff0c\u5c31\u5fc5\u5b9a\u4f1a\u60f3\u5230\u4f60\u6765 \u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\uff0c\u7b49\u5230\u4ed6\u4eec\u548c\u4f60\u4ea4\u624b\u65f6\uff0c\u5c31\u5fc5\u5b9a\u4f1a\u5c06\u5251\u6cd5\u6539\u53d8\u4e86\uff0c\u662f\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u90ed\u5148\u751f\u7684\u8fd9\u4e00\u7ffb\u5fc3\u95f4\u5c82\u975e\u5c31\u767d\u5e9f\u4e86\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u5728\u7ee7\u7eed\u6325\u52a8\u7740\u4ed6\u7684\u9504\u5934\uff0c\u575f\u5893\u5df2\u5c06\u5893\u6398\u6210\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u65e2\u662f\u90ed\u5148\u751f\u7684\u597d\u670b\u53cb\uff0c\u5c31\u5e94\u8be5\u8ba9\u4ed6\u6b7b\u5f97\u6709\u4ef7\u503c\uff0c\u6240\u4ee5\u4f60\u5c31\u5e94\u8be5\u5c06\u4ed6\u57cb\u846c\u5728\u8fd9 \u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u8bf4\u7684\uff0c\u4e5f\u90fd\u60f3\u5230\u8fc7\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e3a\u4f55\u4e0d\u5c06\u90ed\u5148\u751f\u7684\u5c38\u8eab\u6302\u56de\u539f\u6765\u7684\u5730\u65b9\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u5b57\u5b57\uff1a\u6211\u4e0d\u80fd\u8fd9\u6837\u505a\uff0c\u4ed6\u4e3a\u6211\u800c\u6b7b\uff0c\u6211\u2014\u2014 \u3000\u3000\u94c3\u94c3\u9053\uff1b\u5c31\u56e0\u5014\u662f\u4e3a\u4f60\u800c\u6b7b\u7684\uff0c\u6240\u4ee5\u4f60\u624d\u4e00\u5b9a\u8981\u8fd9\u6837\u505a\uff0c\u5426\u5219\u4ed6\u5c82\u975e\u7b49\u4e8e\u767d\u6b7b\u4e86\uff1f\u4ed6\u6b7b \u5f97\u80fd\u7791\u76ee\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9053\uff1a\u6211\u6562\u6253\u8d4c\uff0c\u4e0a\u5b98\u91d1\u8679\u548c\u8346\u65e0\u547d\u7edd\u4e0d\u4f1a\u518d\u56de\u5230\u8fd9\u91cc\u6765\u7684\uff01 \u3000\u3000\u8346\u65e0\u547d\u5df2\u56de\u8fc7\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\uff1a\u4f60\u8981\u56de\u53bb\u627e\u4ed6\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u662f\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e45\u60f3\u4e0e\u5c0f\u674e\u98de\u5200\u51b3\u4e00\u6b7b\u6218\uff0c\u53ef\u662f\u4f60\u73b0\u5728\u7edd\u4e0d\u80fd\u53bb\uff01 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u73b0\u5728\u82e5\u662f\u53bb\u4e86\uff0c\u5fc5\u8d25\u65e0\u7591\uff01 \u3000\u3000\u8346\u65e0\u547d\u7684\u624b\u970d\u7136\u63e1\u4f4f\u4e86\u5251\u67c4\uff0c\u58f0\u97f3\u4e5f\u53d8\u5f97\u5636\u54d1\uff0c\u9053\uff1a\u4f60\u600e\u4e48\u77e5\u6211\u5fc5\u8d25\u65e0\u7591\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u5df2\u6740\u4e86\u90ed\u5d69\u9633\uff0c\u6740\u6c14\u5df2\u51cf\uff0c\u674e\u5bfb\u6b22\u6b64\u523b\u5374\u6b63\u662f\u60b2\u6124\u586b\u81ba\uff0c\u4f60\u82e5\u4e0e\u4ed6\u4ea4\u624b \uff0c\u5728\u6c14\u52bf\u4e0a\u4f60\u5df2\u8f93\u7ed9\u4ed6\u4e09\u5206\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u54fc\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u5df2\u7ecf\u4e00\u6218\uff0c\u518d\u52a0\u4ee5\u957f\u9014\u8dcb\u6d89\uff0c\u4f53\u529b\u96be\u514d\u66f4\u5f31\u4e9b\uff0c\u674e\u5bfb\u6b22\u5728\u90a3\u91cc\u4ee5\u9038\u5f85\u52b3 \uff0c\u53c8\u5360\u4e86\u4e09\u5206\u4fbf\u5b9c\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u53ef\u662f\u4f60\u2014\u2014 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\uff1a\u4f60\u6211\u82e5\u662f\u8054\u624b\uff0c\u81ea\u7136\u80fd\u81f4\u4ed6\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014\u4f60\u600e\u4e48\u77e5\u674e\u5bfb\u6b22\u662f\u4e00\u4e2a\u4eba\u53bb\u7684\uff1f \u4ed6\u82e5\u662f\u548c\u5b59\u8001\u513f\u5728\u4e00\u8d77\u53c8\u5982\u4f55\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u51ed\u4ed6\u4eec\u4e24\u4eba\uff0c\u4e5f\u672a\u5fc5\u80fd\u2014\u2014 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5389\u58f0\u9053\uff1a\u6211\u65e9\u5df2\u544a\u8bc9\u8fc7\u4f60\uff0c\u6211\u6b64\u6b21\u51fa\u6c5f\u6e56\uff0c\u53ea\u8bb8\u80dc\uff0c\u4e0d\u8bb8\u8d25\uff0c \u4e00\u5b9a\u6709\u5341\u4e8c\u5206\u7684\u628a\u63e1\uff0c\u624d\u80fd\u51fa\u624b\uff01 \u3000\u3000\u8346\u65e0\u547d\u9ed8\u7136\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f55\u51b5\uff0c\u4eca\u65e5\u4e4b\u4f60\uff0c\u5df2\u975e\u6614\u65e5\u4e4b\u4f60\u4e86\uff01 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6211\u8fd8\u662f\u6211\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f46\u5982\u4eca\u4f60\u6709\u60c5\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6709\u60c5\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u80fd\u80dc\u4eba\uff0c\u5c31\u56e0\u4e3a\u4f60\u7684\u65e0\u60c5\uff0c\u5982\u4eca\u4f60\u65e2\u5df2\u6709\u60c5\uff0c\u4f60\u7684\u4eba\u4e0e\u5251\u52bf\u5fc5\u90fd\u8981\u65e5\u6e10 \u8f6f\u5f31\u2014\u2014 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u9053\uff1a\u4f60\u4ece\u4e0d\u52a8\u5fc3\uff0c\u5982\u4eca\u600e\u4f1a\u6709\u60c5\uff0c\u662f\u8c01\u6253\u52a8\u4e86\u4f60\uff1f \u3000\u3000\u8346\u65e0\u547d\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u6211\u4e5f\u4e0d\u60f3\u95ee\u4f60\u90a3\u4eba\u662f\u8c01\uff0c\u4f46\u4f60\u82e5\u60f3\u80dc\u8fc7\u522b\u4eba\uff0c\u82e5\u60f3\u80dc\u8fc7\u674e\u589e\u6b22\uff0c\u5c31\u5f97\u6062\u590d \u6614\u65e5\u7684\u4f60\uff0c\u4f60\u82e5\u60f3\u6062\u590d\u6614\u65e5\u7684\u4f60\uff0c\u5c31\u5f97\u5148\u6740\u4e86\u90a3\u4ee4\u4f60\u52a8\u5fc3\u7684\u5973\u4eba\u3002 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u8d70\u5165\u4e86\u6811\u6797\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u624b\u5df2\u7d27\u63e1\u4f4f\u4e86\u5251\u67c4\uff01 \u3000\u3000\u591c\uff0c\u79cb\u591c\uff0c\u591c\u5df2\u6df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u60c5\u5c31\u548c\u4ed6\u7684\u811a\u6b65\u4e00\u6837\u6c89\u91cd\u3002 \u3000\u3000\u90ed\u5d69\u9633\u7ec8\u4e8e\u5df2\u846c\u4e86\uff0c\u8fd9\u540d\u9707\u5929\u4e0b\u7684\u5251\u5ba2\uff0c\u5f52\u5bbf\u4e5f\u6b63\u548c\u8bb8\u591a\u5e73\u51e1\u7684\u4eba\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u676f \u9ec4\u571f\u3002 \u3000\u3000\u4ed6\u6b7b\u5f97\u662f\u5426\u6bd4\u522b\u4eba\u6709\u4ef7\u503c\u5f97\u591a\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\uff0c\u4ed6\u4e0d\u77e5\u9053\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u4ed6\u53ea\u77e5\u9053\u90ed\u5d69\u9633\u672c\u53ef\u4e0d\u5fc5\u6b7b\uff0c\u4e0d\u5fc5\u6b7b\u7684\u4eba\u6b7b\uff0c\u5c82 \u975e\u6709\u4e9b\u75f4\uff1f \u3000\u3000\u4e5f\u8bb8\u53e4\u5f80\u4eca\u6765\u7684\u82f1\u96c4\u4eec\u591a\u5c11\u90fd\u6709\u4e9b\u75f4\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u75f4\uff1f \u3000\u3000\u94c3\u94c3\u7d27\u968f\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u4f60\u600e\u4e48\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u518d\u6765\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u56e0\u4e3a\u4ed6\u662f\u5f53\u4ee3\u7684\u67ad\u96c4\uff0c\u67ad\u96c4\u4eec\u7684\u884c\u4e8b\u603b\u548c\u522b\u4eba\u4e0d\u540c\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u4eec\u4e00\u51fb\u51fa\u624b\uff0c\u65e0\u8bba\u4e2d\u4e0e\u4e0d\u4e2d\uff0c\u90fd\u7acb\u523b\u5168\u8eab\u800c\u9000\uff0c\u518d\u7b49\u7b2c\u4e8c\u6b21\u6709\u5229\u7684\u673a\u4f1a\uff0c \u4ed6\u4eec\u7edd\u4e0d\u4f1a\u505a\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u67ad\u96c4\u7edd\u4e0d\u4f1a\u75f4\uff0c\u6240\u4ee5\u548c\u82f1\u96c4\u4e0d\u540c\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u82f1\u96c4\u90fd\u5f88\u75f4\u4e48\uff1f \u7b2c\u56db\u5341\u4e03\u7ae0\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u4e03\u7ae0\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u75f4\u5e76\u4e0d\u53ef\u7b11\uff0c\u56e0\u4e3a\u552f\u6709\u81f3\u60c5\u7684\u4eba\uff0c\u624d\u80fd\u5b66\u5f97\u4f1a\u8fd9\u201c\u75f4\u201d\u5b57\u3002 \u3000\u3000\u94c3\u94c3\u7b11\u4e86\uff0c\u9053\uff1a\u8fd9\u75f4\u4e5f\u8981\u5b66\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5f53\u7136\uff0c\u65e0\u8bba\u8c01\u60f3\u5b66\u4f1a\u8fd9\u75f4\u5b57\uff0c\u90fd\u4e0d\u662f\u4ef6\u6613\u4e8b\uff0c\u56e0\u4e3a\u75f4\u548c\u5446\u4e0d\u540c\uff0c\u53ea\u6709\u75f4\u4e8e\u5251 \u7684\u4eba\uff0c\u624d\u80fd\u7ec3\u6210\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u53ea\u6709\u75f4\u4e8e\u60c5\u7684\u4eba\uff0c\u624d\u80fd\u5f97\u5230\u522b\u4eba\u7684\u771f\u60c5\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u4e0d\u75f4\u7684\u4eba\u662f \u4e0d\u4f1a\u61c2\u7684\u3002 \u3000\u3000\u94c3\u94c3\u5782\u4e0b\u4e86\u5934\uff0c\u4f3c\u5728\u5480\u56bc\u7740\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u4e2d\u7684\u6ecb\u5473\u3002 \u3000\u3000\u8fc7\u548c\u5f88\u4e45\uff0c\u5979\u624d\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u548c\u4f60\u5728\u4e00\u8d77\uff0c\u6211\u7684\u786e\u61c2\u5f97\u4e86\u8bb8\u591a\u4e8b\uff0c\u53ea\u53ef\u60dc\u2014\u2014\u53ea\u53ef\u60dc \u4f60\u5c31\u8981\u8d70\u4e86\uff0c\u800c\u4e14\u7edd\u4e0d\u4f1a\u5e26\u6211\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u534a\u664c\u9053\uff1a\u81f3\u5c11\u6211\u4f1a\u5148\u966a\u4f60\u56de\u53bb\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\uff0c\u6211\u4eec\u4e3a\u4f55\u4e0d\u8d70\u5730\u9053\uff1f\u90a3\u6761\u8def\u5c82\u975e\u8fd1\u5f97\u591a\u4e48\uff1f \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u53ea\u6709\u90a3\u4e9b\u89c1\u4e0d\u5f97\u5929\u65e5\u7684\u4eba\uff0c\u624d\u559c\u6b22\u8d70\u5730\u9053\uff0c\u4e00\u4e2a\u4eba\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u7684 \u65f6\u5019\uff0c\u8fd8\u662f\u83ab\u8981\u8d70\u5730\u9053\u7684\u597d\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5fc3\u60c5\u867d\u7136\u6c89\u91cd\uff0c\u5374\u603b\u662f\u60f3\u4ee4\u522b\u4eba\u89c9\u5f97\u5f00\u5fc3\u4e9b\u3002 \u3000\u3000\u94c3\u94c3\u679c\u7136\u7b11\u4e86\u7b11\u9053\uff1a\u597d\uff0c\u6211\u542c\u4f60\u7684\u8bdd\uff0c\u4ee5\u540e\u7edd\u4e0d\u505a\u8001\u9f20\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ef0\u9762\u5411\u5929\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4f60\u770b\uff0c\u8fd9\u91cc\u6709\u6e05\u98ce\uff0c\u6709\u660e\u6708\uff0c\u8fd8\u6709\u5982\u6b64\u6e05\u7684\u6d41\u6c34\uff0c\u8fd9 \u4e9b\uff0c\u90a3\u4e9b\u4e13\u8d70\u5730\u9053\u7684\u4eba\u54ea\u91cc\u80fd\u4eab\u53d7\u5230\u3002 \u3000\u3000\u94c3\u94c3\u7b11\u9053\uff1a\u6211\u5012\u5b81\u613f\u5929\u4e0a\u6302\u7684\u662f\u6708\u997c\uff0c\u5730\u4e0a\u6d41\u7684\u662f\u7f8e\u9152\u2014\u2014 \u3000\u3000\u5979\u54bd\u4e86\u53e3\u53e3\u6c34\uff0c\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u8001\u5b9e\u8bf4\uff0c\u6211\u809a\u5b50\u5b9e\u5728\u997f\u4e86\uff0c\u997f\u5f97\u8981\u547d\uff0c\u56de\u53bb\u540e\uff0c\u7b2c\u4e00 \u4ef6\u4e8b\u6211\u5c31\u8981\u4e0b\u53a8\u623f\uff0c\u505a\u51e0\u6837\u597d\u5403\u7684\u2014\u2014 \u3000\u3000\u5979\u8bed\u58f0\u5ffd\u7136\u987f\u4f4f\uff0c\u56e0\u4e3a\u5979\u5df2\u55c5\u5230\u4e00\u9635\u9152\u83dc\u7684\u9999\u6c14\uff0c\u968f\u98ce\u4f20\u6765\uff0c\u8fd9\u5473\u9053\u5728\u6df1\u5c71\u4e2d\u81ea\u7136\u4f20\u64ad \u5f97\u7279\u522b\u8fdc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u70b8\u5b50\u9e21\u3001\u7ea2\u70e7\u8089\u3001\u7ae6\u6912\u2014\u2014\u8fd8\u6709\u6781\u597d\u7684\u9648\u5e74\u82b1\u96d5\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u4e5f\u95fb\u5230\u5473\u9053\u4e86\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5e74\u7eaa\u5927\u4e86\u7684\u4eba\uff0c\u8033\u6735\u867d\u4e5f\u8bb8\u4f1a\u53d8\u5f97\u6709\u70b9\u804b\uff0c\u773c\u775b\u4e5f\u4f1a\u53d8\u5f97\u6709\u4e9b\u82b1\uff0c\u4f46\u9f3b\u5b50\u5374 \u8fd8\u662f\u7167\u6837\u7075\u5f97\u5f88\u7684\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1b\u4f60\u53ef\u55c5\u5f97\u51fa\u8fd9\u5473\u9053\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u6211\u53ea\u77e5\u9053\u9547\u4e0a\u90a3\u5c0f\u5e97\u7edd\u6ca1\u6709\u8fd9\u4e48\u597d\u7684\u9152\uff0c\u4e5f\u505a\u4e0d\u51fa\u8fd9\u4e48\u597d\u7684\u83dc\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f55\u51b5\u90a3\u5c0f\u5e97\u65e9\u5c31\u5173\u95e8\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u662f\u54ea\u5bb6\u597d\u5403\u7684\u4eba\u6b63\u5728\u505a\u5bb5\u591c\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u7edd\u4e0d\u4f1a\uff0c\u8fd9\u9547\u4e0a\u4f4f\u7684\u51e0\u5341\u6237\u4eba\u5bb6\u6211\u90fd\u77e5\u9053\uff0c\u4ed6\u4eec\u7684\u65e5\u5b50\u8fc7\u5f97\u5f88\u8282\u7701\uff0c\u5c31\u7b97\u5076\u5c14 \u60f3\u5f04\u987f\u5bb5\u591c\u5403\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u716e\u7897\u9762\uff0c\u6253\u4e24\u4e2a\u86cb\u800c\u5df2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u9053\uff1a\u4e5f\u8bb8\u4ed6\u4eec\u5bb6\u6709\u8fdc\u5ba2\u6765\u4e86\uff0c\u6240\u4ee5\u7279\u522b\u62db\u5f85\u2014\u2014 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e5f\u4e0d\u4f1a\uff0c\u7edd\u6ca1\u6709\u4e00\u5bb6\u7684\u5ab3\u5987\uff0c\u80fd\u70e7\u5f97\u51fa\u8fd9\u4e48\u9999\u7684\u83dc\u3002 \u3000\u3000\u5979\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u8fd9\u91cc\u80fd\u70e7\u5f97\u51fa\u597d\u83dc\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u542b\u7b11\u9053\uff1a\u8c01\uff1f \u3000\u3000\u94c3\u94c3\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u5c31\u662f\u6211\u3002 \u3000\u3000\u5979\u53c8\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u6240\u4ee5\u6211\u624d\u5947\u602a\uff0c\u6211\u8fd8\u6ca1\u6709\u4e0b\u53a8\u623f\uff0c\u8fd9\u9152\u83dc\u7684\u9999\u6c14\u662f\u4ece\u54ea\u91cc\u6765\u7684\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u9152\u83dc\u7684\u9999\u6c14\uff0c\u5c31\u662f\u4ece\u4f60\u90a3\u5c0f\u697c\u4e0a\u4f20\u6765\u7684\u3002 \u3000\u3000\u957f\u8857\u9759\u5bc2\u3002 \u3000\u3000\u5c71\u6797\u4e2d\u7684\u4eba\u90fd\u7761\u5f97\u65e9\uff0c\u5bb6\u5bb6\u6237\u6237\u7684\u706f\u706b\u90fd\u5df2\u7184\u706d\u4e86\uff0c\u4f46\u4e00\u8f6c\u5165\u67ab\u6797\uff0c\u5c31\u53ef\u53d1\u73b0\u90a3\u5c0f\u697c\u4e0a \u4f9d\u7136\u662f\u706f\u706b\u901a\u660e\u3002 \u3000\u3000\u4e0d\u4f46\u90a3\u9152\u83dc\u7684\u9999\u6c14\u662f\u4ece\u5c0f\u697c\u4e0a\u4f20\u6765\u7684\uff0c\u800c\u4e14\u697c\u4e0a\u8fd8\u9690\u7ea6\u53ef\u4ee5\u542c\u89c1\u4e00\u9635\u9635\u7537\u5973\u6df7\u6742\u7684\u7b11\u58f0 \u3002 \u3000\u3000\u94c3\u94c3\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u9053\uff1a\u83ab\u975e\u662f\u4f60\u4eec\u5bb6\u5c0f\u59d0\u56de\u6765\u4e86\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u7edd\u4e0d\u4f1a\uff0c\u5979\u8bf4\u8fc7\u81f3\u5c11\u8981\u7b49\u4e09\u4e94\u4e2a\u6708\u540e\u624d\u4f1a\u56de\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u4eec\u5bb6\u7684\u5ba2\u4eba\u672c\u4e0d\u5c11\uff0c\u4e5f\u8bb8\u53c8\u6709\u8fdc\u5ba2\u6765\u4e86\uff0c\u4e3b\u4eba\u65e2\u4e0d\u5728\uff0c\u5c31\u81ea\u5df1\u52a8\u624b\u5f04\u4e9b\u9152 \u83dc\u5403\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u6211\u5148\u4e0a\u53bb\u77a7\u77a7\uff0c\u4f60\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd8\u662f\u6211\u5148\u4e0a\u53bb\u7684\u597d\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f\u8fd9\u4e9b\u4eba\u65e2\u7136\u5728\u697c\u4e0a\u53c8\u70e7\u83dc\uff0c\u53c8\u559d\u9152\uff0c\u95f9\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u663e\u7136\u5e76\u6ca1\u6709\u4ec0\u4e48 \u6076\u610f\uff0c\u4f60\u96be\u9053\u8fd8\u6015\u6211\u5148\u4e0a\u53bb\u6709\u5371\u9669\u4e0d\u6210\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u53ea\u4e0d\u8fc7\u4e5f\u5f88\u997f\u4e86\u3002 \u3000\u3000\u4ed6\u62a2\u5148\u8d70\u4e0a\u5c0f\u697c\u65c1\u7684\u68af\u5b50\uff0c\u8d70\u5f97\u5f88\u5c0f\u5fc3\uff0c\u4f3c\u4e4e\u611f\u5230\u6709\u4eba\u5728\u5c0f\u697c\u4e0a\u5e03\u4e86\u4e2a\u9677\u9631\uff0c\u6b63\u7b49\u7740\u4ed6 \u4e0a\u53bb\u3002 \u3000\u3000\u697c\u4e0a\u7684\u95e8\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u8d70\u5230\u95e8\u53e3\uff0c\u5c31\u4eff\u4f5b\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u672a\u66fe\u89c1\u8fc7\u8fd9\u4e48\u591a\u3001\u8fd9\u4e48\u80d6\u7684\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\u89c1\u5230\u7684\u80d6\u5973\u4eba\uff0c\u52a0\u8d77\u6765\u8fd8\u6ca1\u6709\u73b0\u5728\u4e00\u534a\u591a\u3002 \u3000\u3000\u5c4b\u5b50\u6668\u5750\u7740\u5341\u6765\u4e2a\u5973\u4eba\uff0c\u5979\u4eec\u90fd\u5750\u5728\u5730\u4e0a\uff0c\u56e0\u4e3a\u65e0\u8bba\u591a\u4e48\u5927\u7684\u6905\u5b50\u5979\u4eec\u4e5f\u5750\u4e0d\u4e0b\uff0c\u5c31\u7b97 \u5750\u4e0b\u53bb\uff0c\u6905\u5b50\u4e5f\u8981\u88ab\u5750\u57ae\u3002 \u3000\u3000\u4f46\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u5979\u4eec\u662f\u732a\uff0c\u56e0\u4e3a\u8c61\u5979\u4eec\u8fd9\u4e48\u80d6\u7684\u732a\u4e16\u4e0a\u8fd8\u5c11\u89c1\u5f97\u5f88\uff0c\u800c\u4e14\u732a\u4e5f\u7edd\u6ca1\u6709\u5979\u4eec \u5403\u5f97\u8fd9\u4e48\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8d70\u5230\u95e8\u53e3\u65f6\u5019\uff0c\u6070\u5de7\u6709\u4e00\u5927\u76d8\u70b8\u5b50\u9e21\u521a\u7aef\u4e0a\u6765\uff0c\u8fd9\u5341\u51e0\u4e2a\u80d6\u5973\u4eba\u6b63\u597d\u4e00\u9f50\u5728\u5403\u70b8 \u5b50\u9e21\u3002 \u3000\u3000\u5806\u9152\u83dc\u7684\u684c\u5b50\u65c1\u94fa\u7740\u4e03\u516b\u5e8a\u4e1d\u88ab\uff0c\u6700\u80d6\u7684\u4e00\u4e2a\u5973\u4eba\u5c31\u5750\u5728\u90a3\u91cc\uff0c\u8fd8\u6709\u4e94\u516d\u4e2a\u7537\u4eba\u5728\u65c1\u8fb9 \u56f4\u7740\u5979\u3002 \u3000\u3000\u8fd9\u4e9b\u7537\u4eba\u4e00\u4e2a\u4e2a\u90fd\u7a7f\u7740\u6781\u9c9c\u8273\u7684\u8863\u88f3\uff0c\u5e74\u7eaa\u4e5f\u5f88\u8f7b\uff0c\u957f\u5f97\u4e5f\u90fd\u4e0d\u7b97\u96be\u770b\uff0c\u6709\u7684\u8138\u4e0a\u8fd8\u64e6 \u7740\u7c89\u3002 \u3000\u3000\u4ed6\u4eec\u8eab\u6750\u5176\u5b9e\u4e5f\u4e0d\u80fd\u7b97\u5341\u5206\u7626\u5c0f\uff0c\u4f46\u548c\u8fd9\u5973\u4eba\u4e00\u6bd4\uff0c\u7b80\u76f4\u5c31\u6d3b\u50cf\u4e2a\u5c0f\u7334\u5b50\u3002 \u3000\u3000\u90a3\u4e94\u516d\u4e2a\u7537\u4eba\u6709\u7684\u6b63\u5728\u66ff\u5979\u6572\u817f\uff0c\u6709\u7684\u5728\u66ff\u5979\u6376\u80cc\uff0c\u6709\u7684\u66ff\u5979\u6247\u6247\u5b50\uff0c\u6709\u7684\u624b\u91cc\u6367\u7740\u91d1 \u676f\uff0c\u5728\u5582\u5979\u559d\u9152\u3002 \u3000\u3000\u8fd8\u6709\u4e24\u4e2a\u8138\u4e0a\u64e6\u7740\u7c89\u7684\uff0c\u8737\u4f0f\u5728\u5979\u811a\u4e0b\uff0c\u5979\u624b\u91cc\u6495\u7740\u70b8\u9e21\uff0c\u9ad8\u5174\u4e86\u5c31\u6495\u4e00\u5757\u5582\u5230\u4ed6\u4eec\u5634 \u91cc\u3002 \u3000\u3000\u5e78\u597d\u674e\u5bfb\u6b22\u5f88\u4e45\u6ca1\u5403\u4e1c\u897f\uff0c\u5426\u5219\u4ed6\u6b64\u523b\u53ea\u6015\u65e9\u5c31\u5410\u4e86\u51fa\u6765\uff0c\u4ed6\u5e73\u751f\u518d\u6ca1\u6709\u77a7\u89c1\u8fc7\u6bd4\u8fd9\u4ee4 \u4eba\u6076\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5e76\u6ca1\u6709\u56de\u5934\uff0c\u53cd\u800c\u5927\u6b65\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6240\u6709\u7684\u58f0\u97f3\u7acb\u523b\u5168\u90fd\u505c\u6b62\u4e86\uff0c\u6240\u6709\u7684\u773c\u775b\u5168\u90fd\u5728\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u90fd\u4f1a\u53d8\u5f97\u5f88\u5c40\u4fc3\uff0c\u5f88\u4e0d\u5b89\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5e76\u6ca1\u6709\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u5fc3\u91cc\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u8868\u9762\u4e5f\u7edd\u5bf9\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u968f\u4fbf\u5730\u8d70\u7740\uff0c\u5c31\u7b97\u662f\u8d70\u4e0a\u91d1\u6bbf\u65f6\uff0c\u4ed6\u4e5f\u662f\u8fd9\u6837\u5b50\uff0c\u4ed6\u5c31\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u65e0\u8bba\u8c01\u4e5f \u6ca1\u6cd5\u5b50\u4f7f\u4ed6\u6539\u53d8 \u3000\u3000\u90a3\u6700\u80d6\u6700\u5927\u7684\u5973\u4eba\u773c\u775b\u5df2\u772f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u773c\u775b\u5e76\u4e0d\u5c0f\uff0c\u73b0\u5728\u5374\u88ab\u8138\u4e0a\u7684\u80a5\u8089\u6324\u6210\u4e86\u4e00\u6761\u7ebf\uff0c\u5979\u8116\u5b50\u672c\u6765\u4e5f\u8bb8\u5e76\u4e0d\u77ed\uff0c\u73b0\u5728\u5374\u5df2 \u88ab\u4e00\u53e0\u53e0\u7684\u80a5\u8089\u586b\u6ee1\u4e86\u3002 \u3000\u3000\u5979\u5750\u5728\u90a3\u91cc\u7b80\u76f4\u5c31\u50cf\u662f\u4e00\u5ea7\u5c71\u3001\u8089\u5c71\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9759\u9759\u5730\u7ad9\u5728\u5979\u9762\u524d\uff0c\u6de1\u6de1\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u5927\u6b22\u559c\u5973\u83e9\u8428\uff1f \u3000\u3000\u8fd9\u5973\u4eba\u7684\u773c\u775b\u4eae\u4e86\uff0c\u9053\uff1a\u4f60\u77e5\u9053\u6211\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e45\u4ef0\u5f97\u5f88\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u4f46\u4f60\u5374\u6ca1\u6709\u9003\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4e3a\u4f55\u8981\u9003\u8d70\uff1f \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u5979\u5f00\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u8fd8\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u53d8\u5316\uff0c\u4f46\u5ffd\u7136\u95f4\uff0c\u5979\u5168\u8eab\u7684\u80a5\u8089\u90fd\u5f00\u59cb\u9707\u52a8\u4e86\u8d77\u6765 \u3002 \u3000\u3000\u6ee1\u5c4b\u5b50\u7684\u4eba\u90fd\u968f\u7740\u5979\u9707\u52a8\u4e86\u8d77\u6765\uff0c\u672c\u6765\u4f0f\u5728\u5979\u80cc\u4e0a\u7684\u4e00\u4e2a\u7a7f\u7eff\u8863\u670d\u7684\u7537\u4eba\uff0c\u7adf\u88ab\u5f39\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u684c\u4e0a\u7684\u676f\u76d8\u7897\u76cf\u53ee\u5f53\u76f4\u54cd\uff0c\u5c31\u50cf\u5730\u9707\u3002 \u3000\u3000\u5e78\u597d\u5979\u7b11\u58f0\u7acb\u523b\u5c31\u505c\u6b62\u4e86\uff0c\u76ef\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u867d\u8fd8\u4e0d\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4f46\u4f60\u7684\u6765\u610f\u6211\u5df2\u77e5\u9053 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u4f60\u662f\u4e3a\u4e86\u84dd\u874e\u5b50\u6765\u7684\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\uff01 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u5979\u6740\u6b7b\u6211\u90a3\u5b9d\u8d1d\u5f92\u5f1f\uff0c\u5c31\u662f\u4e3a\u4e86\u4f60\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u6240\u4ee5\u4f60\u60f3\u6765\u6551\u5979\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u773c\u775b\u772f\u4e86\u8d77\u6765\uff0c\u5e26\u7740\u7b11\u610f\u9053\uff1a\u60f3\u4e0d\u5230\u4f60\u8fd9\u7537\u4eba\u5012\u8fd8\u6709\u70b9\u826f\u5fc3\uff0c\u5979\u4e3a\u4f60\u6740\u4eba \u5012\u8fd8\u4e0d\u51a4\u6789\u3002 \u3000\u3000\u5979\u4e00\u6311\u5927\u62c7\u6307\u9053\uff1a\u4f46\u84dd\u874e\u5b50\u4e5f\u771f\u53ef\u7b97\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u5973\u4eba\uff0c\u8bb2\u4e49\u6c14\uff0c\u6709\u9aa8\u5934\uff0c\u5979\u6740\u4e86\u6211\u7684 \u5f92\u5f1f\uff0c\u975e\u4f46\u6ca1\u6709\u9003\u8d70\uff0c\u53cd\u800c\u6562\u6765\u89c1\u6211\uff0c\u4ee5\u524d\u6211\u5012\u771f\u672a\u60f3\u5230\u5979\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u8ddf\u4f60\u5012\u53ef\u7b97\u662f \u5929\u751f\u7684\u4e00\u5bf9\u513f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8fa9\u9a73\uff0c\u5fae\u7b11\u9053\uff1a\u5973\u83e9\u8428\u82e5\u80af\u6210\u5168\uff0c\u5728\u4e0b\u611f\u6fc0\u4e0d\u5c3d\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u4f60\u60f3\u5c06\u5979\u5e26\u8d70\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u6211\u82e5\u5df2\u6740\u4e86\u5979\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4e48\u2014\u2014\uff0d\u6211\u4e5f\u8bb8\u5c31\u8981\u66ff\u5979\u62a5\u4ec7\u4e86\uff01 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u597d\uff0c\u4f60\u4e0d\u4f46\u6709\u826f\u5fc3\uff0c\u4e5f\u6709\u80c6\u5b50\uff0c\u6211\u5012\u8fd8\u771f\u820d\u4e0d\u5f97\u6740\u4f60\u3002 \u3000\u3000\u5979\u7684\u817f\u4e00\u4f38\uff0c\u5c06\u4f0f\u5728\u5979\u817f\u4e0a\u7684\u4e00\u4e2a\u7537\u4eba\u5f39\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u53bb\uff0c\u66ff\u8fd9\u4f4d\u5ba2\u4eba\u5012\u9152\u3002 \u3000\u3000\u8fd9\u7537\u4eba\u7a7f\u4ef6\u6eda\u7740\u82b1\u8fb9\u7684\u7d2b\u7ea2\u8863\u670d\uff0c\u8eab\u6750\u672c\u4e0d\u77ee\uff0c\u6b64\u523b\u5374\u5df2\u7f29\u4e86\u8d77\u6765\uff0c\u8138\u4e0a\u5c45\u7136\u8fd8\u62b9\u7740\u539a \u539a\u7684\u4e00\u5c42\u7c89\u3002 \u3000\u3000\u770b\u4ed6\u7684\u4e94\u5b98\u8f6e\u5ed3\uff0c\u770b\u4ed6\u7684\u773c\u775b\uff0c\u4ed6\u4ee5\u524d\u60f3\u5fc5\u4e5f\u662f\u4e2a\u5f88\u82f1\u4fca\u7684\u7537\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u624b\u6367\u7740\u91d1\u676f\uff0c\u7b11\u563b\u563b\u9001\u5230\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u9053\uff1a\u8bf7\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u843d\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u5c45\u7136\u8fd8\u7b11\u5f97\u51fa\u53e3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6697\u4e2d\u53f9\u6c14\uff0c\u63a5\u7740\u91d1\u676f\u9053\uff1a\u591a\u8c22\u3002 \u3000\u3000\u4ed6\u65e0\u8bba\u5bf9\u4ec0\u4e48\u4eba\u90fd\u5f88\u5ba2\u6c14\uff0c\u4ed6\u89c9\u5f97\u4eba\u603b\u662f\u4eba\uff0c\u4ed6\u4e00\u5411\u4e0d\u613f\u4f24\u5bb3\u522b\u4eba\uff0c\u5c31\u7b97\u90a3\u4eba\u81ea\u5df1\u5728\u4f24 \u5bb3\u81ea\u5df1\u3002 \u3000\u3000\u91d1\u676f\u7684\u5bb9\u91cf\u5f88\u5927\uff0c\u8db3\u53ef\u5bb9\u9152\u534a\u6597\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u597d\uff0c\u597d\u9152\u91cf\uff01\u597d\u9152\u91cf\u7684\u7537\u4eba\u624d\u662f\u597d\u7537\u4eba\uff0c\u6211\u8fd9\u4e9b\u7537\u4eba\u8c01\u4e5f\u6bd4\u4e0d\u4e0a\u4f60\u3002 \u3000\u3000\u90a3\u7a7f\u7d2b\u8863\u670d\u7684\u7537\u4eba\u53c8\u6367\u4e86\u676f\u9152\u8fc7\u6765\uff0c\u9053\uff1a\u674e\u63a2\u82b1\u5343\u676f\u4e0d\u9189\uff0c\u8bf7\uff0c\u518d\u5c3d\u8fd9\u4e00\u676f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u7537\u4eba\u5c45\u7136\u8ba4\u5f97\u4ed6\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u76b1\u7709\u9053\uff1a\u4f60\u53eb\u4ed6\u674e\u63a2\u82b1\uff1f\u54ea\u4e2a\u674e\u63a2\u82b1\uff1f \u3000\u3000\u90a3\u7537\u4eba\u9053\uff1a\u674e\u63a2\u82b1\u53ea\u6709\u4e00\u4e2a\uff0c\u5c31\u662f\u5927\u540d\u7684\u5c0f\u674e\u98de\u5200\uff0c\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6240\u6709\u7684\u773c\u775b\u90fd\u53d1\u4e86\u76f4\u3002 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u8fd1\u5341\u4f59\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u51e0\u9996\u5df2\u6ca1\u6709\u6bd4\u4ed6\u66f4\u54cd\u4eae\u7684\u540d\u5b57\uff01 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7a81\u53c8\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u597d\uff0c\u4e45\u95fb\u674e\u63a2\u82b1\u4e0d\u4f46\u6709\u8272\u80c6\uff0c\u4e5f\u6709\u9152\u80c6\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c \u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u522b\u4eba\u4e5f\u6ca1\u6709\u80c6\u5b50\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u90a3\u7537\u4eba\u9053\uff1a\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u98de\uff0c\u8fd9\u5c31\u53eb\u827a\u9ad8\u80c6\u5927\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u7766\u5728\u76ef\u7740\u4ed6\u7684\u8138\u9053\uff1a\u5374\u4e0d\u77e5\u9053\u9601\u4e0b\u662f\u2014\u2014 \u3000\u3000\u90a3\u7537\u4eba\u7b11\u9053\uff1a\u674e\u63a2\u82b1\u771f\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\uff0c\u8fde\u8001\u670b\u53cb\u90fd\u4e0d\u8ba4\u5f97\u4e86\u4e48\uff1f \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u4f60\u7684\u4eba\u4ed6\u867d\u5df2\u4e0d\u8ba4\u5f97\uff0c\u4f60\u7684\u5251\u6cd5\u4ed6\u60f3\u5fc5\u8fd8\u662f\u8ba4\u5f97\u7684\u3002 \u3000\u3000\u90a3\u7537\u4eba\u7b11\u9053\uff1a\u6211\u7684\u5251\u6cd5\u2014\u2014\u6211\u7684\u5251\u6cd5\u8fde\u6211\u81ea\u5df1\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u4f60\u6ca1\u6709\u5fd8\uff0c\u5feb\u53bb\u62ff\u4f60\u7684\u5251\u6765\u3002 \u3000\u3000\u90a3\u7537\u4eba\u5012\u771f\u542c\u8bdd\uff0c\u8d70\u5230\u540e\u9762\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u7537\u4eba\u7684\u8eab\u5f62\u867d\u5df2\u6709\u4e9b\u4f5d\u507b\uff0c\u4f46\u8d70\u8d77\u8def\u6765\u5012\u4e0d\u6162\uff0c\u8fd8\u4e0d\u5230\u534a\u76cf\u8336\u529f\u592b\uff0c\u5c31\u6367\u7740\u67c4\u9e1f\u9798\u957f \u5251\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u6765\uff0c\u9732\u4e00\u624b\u7ed9\u4ed6\u77a7\u77a7\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u5df2\u5c06\u624b\u4e2d\u7684\u5927\u534a\u53ea\u9e21\u5411\u8fd9\u7537\u4eba\u629b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u53ee\u7684\u4e00\u58f0\uff0c\u5251\u5149\u4e00\u95ea\u3002 \u3000\u3000\u8fd9\u7537\u62e7\u8eab\uff0c\u62d4\u5251\uff0c\u5251\u5149\u5339\u7ec3\u822c\u98de\u51fa\u3002 \u3000\u3000\u5927\u534a\u53ea\u9e21\u5df2\u53d8\u5f97\u56db\u7247\uff0c\u4e00\u8fde\u4e32\u7a7f\u5728\u5251\u4e0a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5931\u58f0\u9053\uff1a\u597d\u5251\u6cd5\uff01 \u3000\u3000\u4ed6\u5b9e\u5728\u6ca1\u6709\u60f3\u5230\u8fd9\u7537\u4eba\u7adf\u6709\u5982\u6b64\u9ad8\u660e\u7684\u5251\u6cd5\uff0c\u5982\u6b64\u8fc5\u901f\u7684\u51fa\u7684\uff0c\u6700\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4f7f\u51fa\u7684 \u8fd9\u4e00\u62db\u5251\u6cd5\u674e\u5bfb\u6b22\u770b\u6765\u719f\u6089\u5f97\u5f88\uff0c\u4eff\u4f5b\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\uff0c\u800c\u4e14\u8fd8\u4eff\u4f5b\u66fe\u7ecf\u548c\u4ed6\u4ea4\u8fc7\u624b\u3002 \u3000\u3000\u8fd9\u7537\u4eba\u5df2\u7b11\u563b\u563b\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u8fd9\u9e21\u70b8\u5f97\u8fd8\u4e0d\u9519\uff0c\u674e\u63a2\u82b1\u8bf7\u5c1d\u4e00\u5757\u3002 \u3000\u3000\u9ec4\u6f84\u6f84\u7684\u70b8\u9e21\u4e32\u5728\u62d3\u68ee\u68ee\u7684\u5251\u4e0a\uff0c\u679c\u7136\u663e\u5f97\u5206\u5916\u8bf1\u4eba\u3002 \u3000\u3000\u78a7\u68ee\u68ee\u7684\u5251\u5149\u5b9b\u5982\u4e00\u6c60\u79cb\u6c34\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8038\u7136\u5931\u58f0\uff0c\u7adf\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u593a\u60c5\u5251\uff01\u201d \u3000\u3000\u8fd9\u7537\u4eba\u638c\u4e2d\u7684\u5251\uff0c\u7adf\u662f\u593a\u60c5\u5251\uff01 \u3000\u3000\u671b\u7740\u8fd9\u7537\u4eba\uff0c\u674e\u5bfb\u6b22\u5168\u8eab\u90fd\u5728\u53d1\u51b7\uff0c\u9053\uff1a\u6e38\u9f99\u751f\uff0c\u9601\u4e0b\u83ab\u975e\u662f\u85cf\u5251\u5c71\u5e84\u7684\u6e38\u5c11\u5e84\u4e3b\u3002 \u3000\u3000\u8fd9\u7537\u4eba\u7b11\u563b\u9053\uff1a\u8001\u670b\u53cb\u6bd5\u7adf\u662f\u8001\u670b\u53cb\uff0c\u4f60\u5230\u5e95\u662f\u6ca1\u6709\u5fd8\u4e86\u6211\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u7b11\u5f97\u592a\u591a\uff0c\u8138\u4e0a\u7c89\u5728\u7c0c\u7c0c\u5730\u5f80\u4e0b\u843d\u3002 \u3000\u3000\u8fd9\u771f\u7684\u5c31\u662f\u6e38\u9f99\u751f\uff1f\u8fd9\u771f\u7684\u5c31\u662f\u4e24\u5e74\u524d\u96c4\u59ff\u82f1\u53d1\uff0c\u4e0d\u53ef\u4e00\u4e16\u7684\u5c11\u5e74\u8c6a\u6770\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u5168\u8eab\u7684\u6c57\u6bdb\u90fd\u575a\u4e86\u8d77\u6765\uff0c\u4ed6\u4e0d\u4f46\u4e3a\u4ed6\u60b2\u75db\uff0c\u4e5f\u4e3a\u4ed6\u60cb\u60dc\u3002 \u3000\u3000\u4f46\u6e38\u9f99\u751f\u81ea\u5df1\u5374\u4f3c\u5df2\u5b8c\u5168\u9ebb\u6728\u4e86\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7b11\u9053\uff1a\u85cf\u5251\u5c71\u5e84\u7684\u53a8\u5b50\u505a\u4e0d\u51fa\u8fd9\u4e48\u597d\u7684\u9e21\u6765\u4e48\uff1f \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u4ed6\u4eec\u505a\u51fa\u6765\u7684\u70b8\u9e21\u7b80\u76f4\u5c31\u50cf\u6728\u5934\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u82e5\u4e0d\u662f\u6211\uff0c\u4f60\u80fd\u5403\u5230\u8fd9\u79cd\u70b8\u9e21\u4e48\uff1f \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u5403\u4e0d\u5230\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u4f60\u8ddf\u6211\u5728\u4e00\u8d77\uff0c\u65e5\u5b50\u8fc7\u5f97\u5f00\u5fc3\u4e0d\u5f00\u5fc3\uff1f \u3000\u3000\u6e38\u9f99\u751f\u7b11\u9053\uff1a\u5f00\u5fc3\u6b7b\u4e86\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u84dd\u874e\u5b50\u548c\u6211\uff0c\u82e5\u8981\u4f60\u9009\u4e00\u4e2a\uff0c\u4f60\u9009\u8c01\uff1f \u3000\u3000\u6e38\u9f99\u751f\u4f3c\u4e4e\u722c\u5230\u5979\u811a\u4e0b\u53bb\uff0c\u7b11\u563b\u563b\u9053\uff1a\u5f53\u7136\u662f\u9009\u6211\u4eec\u7684\u5973\u83e9\u8428\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u629a\u7740\u809a\u5b50\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u597d\uff0c\u8fd9\u5c0f\u5b50\u603b\u7b97\u662f\u6709\u773c\u5149\u7684\uff0c\u4e5f\u4e0d\u6789\u6211\u75bc\u4f60\u4e00\u573a \uff01 \u3000\u3000\u5979\u5ffd\u7136\u6307\u7740\u81ea\u5df1\u7684\u54bd\u5589\uff0c\u9053\uff1a\u6765\uff0c\u5f80\u6211\u8fd9\u5730\u65b9\u523a\u4e00\u523a\uff0c\u7ed9\u674e\u63a2\u82b1\u77a7\u77a7\u3002 \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u90a3\u4e0d\u884c\uff0c\u82e5\u662f\u4f24\u4e86\u5973\u83e9\u8428\uff0c\u90a3\u600e\u4e48\u5f97\u4e86\uff0c\u6211\u4e5f\u8981\u5fc3\u75bc\u6b7b\u4e86\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7b11\u9a82\u9053\uff1a\u5c0f\u5154\u5d3d\u5b50\uff0c\u8d41\u4f60\u4e5f\u80fd\u4f24\u5f97\u4e86\u6211\uff0c\u653e\u5fc3\u523a\u8fc7\u6765\u5427\uff01 \u3000\u3000\u5979\u5c45\u7136\u62ac\u8d77\u4e86\u5934\uff0c\u4f38\u76f4\u4e86\u8116\u5b50\u5728\u7b49\u3002 \u3000\u3000\u770b\u6e38\u9f99\u751f\u8fdf\u7591\u7740\uff0c\u773c\u73e0\u5b50\u4e0d\u505c\u5730\u5728\u8f6c\uff0c\u7a81\u7136\u9053\uff1a\u597d\uff01 \u3000\u3000\u4f46\u89c1\u5bd2\u5149\u95ea\u52a8\uff0c\u5982\u60ca\u8679\uff0c\u5982\u63a3\u7535\u3002 \u3000\u3000\u6e38\u9f99\u751f\u5251\u6cd5\u4e4b\u5feb\uff0c\u867d\u4e0d\u53ca\u963f\u98de\uff0c\u4f46\u4e5f\u53ef\u7b97\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u7684\u9ad8\u624b\uff0c\u674e\u5bfb\u6b22\u66fe\u7ecf\u548c\u4ed6\u4ea4\u8fc7\u624b \uff0c\u5bf9\u4ed6\u7684\u5251\u6cd5\u81ea\u7136\u6e05\u695a\u5f97\u5f88\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7aef\u6b63\u5730\u5750\u5728\u90a3\u91cc\uff0c\u5c45\u7136\u8fde\u52a8\u90fd\u4e0d\u52a8\uff0c\u5979\u82e5\u662f\u4e2a\u7537\u4eba\uff0c\u5012\u771f\u50cf\u4e00\u5c0a\u5f25\u9640\u4f5b\u3002 \u3000\u3000\u5251\u5149\u5df2\u95ea\u7535\u822c\u523a\u5165\u4e86\u5979\u54bd\u5589\uff01 \u7b2c\u56db\u5341\u516b\u7ae0\u3000\u5973\u5de8\u4eba>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u516b\u7ae0\u3000\u5973\u5de8\u4eba \u3000\u3000\u6e38\u9f99\u751f\u4e0d\u4f46\u5251\u6cd5\u5feb\uff0c\u624b\u91cc\u7528\u7684\u593a\u60c5\u5251\u4e5f\u53ef\u7b97\u662f\u67c4\u5439\u6bdb\u65ad\u53d1\u7684\u5229\u5668\uff0c\u674e\u5bfb\u6b22\u5bf9\u8fd9\u67c4\u5251\u7684\u950b \u5229\u4e5f\u6e05\u695a\u5f97\u5f88\u3002 \u3000\u3000\u4ed6\u4e0d\u4fe1\u6709\u4efb\u4f55\u4eba\u7684\u8840\u8089\u4e4b\u8eaf\u80fd\u6321\u5f97\u4f4f\u8fd9\u4e00\u5251\uff01 \u3000\u3000\u53ea\u542c\u4e00\u58f0\u60ca\u547c\uff0c\u6e38\u9f99\u751f\u7684\u4eba\u7adf\u7a81\u7136\u5f39\u4e86\u51fa\u6765\uff0c\u8dcc\u5750\u5728\u674e\u5bfb\u6b22\u8eab\u65c1\u7684\u4e00\u4e2a\u80d6\u5973\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u5403\u5403\u7b11\u7740\uff0c\u6402\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u518d\u770b\u90a3\u67c4\u5251\uff0c\u8fd8\u63d2\u5728\u5927\u6b22\u559c\u5973\u83e9\u8428\u7684\u54bd\u5589\u4e0a\u3002 \u3000\u3000\u4f46\u5927\u6b22\u559c\u5973\u83e9\u8428\u5374\u8fd8\u662f\u597d\u597d\u5750\u5728\u90a3\u91cc\uff0c\u7b11\u772f\u772f\u5730\u77a7\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b80\u76f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4f4d\u5927\u6b22\u559c\u5973\u83e9\u8428\uff0c\u7adf\u4ee5\u8116\u5b50\u4e0a\u7684\u80a5\u8089\uff0c\u5c06\u8fd9\u67c4\u5251\u5939\u4f4f\uff01 \u3000\u3000\u8fd9\u79cd\u529f\u592b\u522b\u4eba\u975e\u4f46\u522b\u4eba\u6ca1\u6709\u770b\u5230\u8fc7\uff0c\u7b80\u76f4\u8fde\u542c\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u53ea\u542c\u5979\u7b11\u9053\uff1a\u80d6\u5973\u4eba\u4e5f\u6709\u80d6\u5973\u4eba\u7684\u597d\u5904\uff0c\u8fd9\u8bdd\u73b0\u5728\u4f60\u603b\u8be5\u76f8\u4fe1\u4e86\u5427\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5973\u83e9\u8428\u7684\u529f\u592b\uff0c\u679c\u7136\u975e\u5e38\u4eba\u80fd\u53ca\u3002 \u3000\u3000\u8fd9\u4e00\u70b9\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u56e0\u4e3a\u8c01\u4e5f\u6ca1\u6709\u5979\u90a3\u4e48\u591a\u80a5\u8089\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u6211\u4e5f\u542c\u8bf4\u8fc7\u4f60\u7684\u98de\u5200\uff0c\u767e\u53d1\u767e\u4e2d\uff0c\u8fde\u6211\u90a3\u5b9d\u8d1d\u5e72\u513f\u5b50\u90fd\u8eb2\u4e0d\u5f00\u4f60\u7684\u4e00 \u5200\uff0c\u4f60\u81ea\u5df1\u5f53\u7136\u4e5f\u89c9\u5f97\u81ea\u5df1\u6ee1\u4e0d\u9519\u4e86\uff0c\u662f\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u4f60\u5c31\u662f\u4ed7\u7740\u4f60\u7684\u5200\uff0c\u624d\u6562\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u662f\u5417\uff1f \u3000\u3000\u5979\u5e26\u7740\u7b11\u9053\uff1a\u4f46\u4f60\u90a3\u624b\u98de\u5200\u80fd\u6740\u5f97\u4e86\u6211\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6740\u4e0d\u4e86\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7b11\u4e86\uff0c\u9053\uff1a\u4f60\u73b0\u5728\u8fd8\u60f3\u4e0d\u60f3\u5c06\u84dd\u874e\u5b50\u5e26\u8d70\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u60f3\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u8138\u8272\u4e5f\u4e0d\u7981\u53d8\u4e86\u53d8\uff0c\u4f46\u7acb\u523b\u7b11\u9053\uff1a\u6709\u8da3\u6709\u8da3\uff0c\u4f60\u8fd9\u4eba\u771f\u6709\u8da3\u6781\u4e86\uff0c\u4f60\u60f3\u7528 \u4ec0\u4e48\u6cd5\u5b50\u5c06\u84dd\u874e\u5b50\u5e26\u8d70\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u75b2\u4e4f\uff1a\u6211\u6162\u6162\u5730\u60f3\uff0c\u603b\u4f1a\u60f3\u51fa\u4e2a\u6cd5\u5b50\u6765\u7684\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u773c\u53c8\u772f\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u597d\uff0c\u90a3\u4e48\u4f60\u5c31\u7559\u5728\u6211\u8fd9\u91cc\uff0c\u6162\u6162\u5730\u60f3\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u91cc\u65e2\u7136\u6709\u9152\uff0c\u6211\u591a\u7559\u51e0\u65e5\u4e5f\u65e0\u59a8\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u6211\u8fd9\u9152\u53ef\u4e0d\u662f\u767d\u559d\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u60f3\u8981\u6211\u600e\u6837\uff1f \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7b11\u9053\uff1a\u672c\u6765\u6211\u8fd8\u5acc\u4f60\u7a0d\u8001\u4e86\u4e00\u70b9\uff0c\u4f46\u73b0\u5728\u5374\u8d8a\u770b\u4f60\u8d8a\u949f\u610f\u4e86\uff0c\u6240\u4ee5\uff0c\u4f60\u4e5f \u7528\u4e0d\u7740\u518d\u60f3\u522b\u7684\u6cd5\u5b50\uff0c\u53ea\u8981\u4f60\u7559\u5728\u8fd9\u91cc\u966a\u6211\u51e0\u5929\uff0c\u6211\u5c31\u8ba9\u4f60\u5c06\u84dd\u874e\u5b50\u5e26\u8d70\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u5728\u7b11\uff0c\u60a0\u60a0\u9053\uff1a\u4f60\u4e0d\u5acc\u6211\u624d\uff0c\u6211\u5374\u5acc\u4f60\u592a\u80d6\u4e86\uff0c\u4f60\u82e5\u80fd\u5c06\u8eab\u4e0a\u7684\u8089\u53bb\u6389\u4e00\u4e24 \u767e\u65a4\uff0c\u5c31\u7b97\u966a\u4f60\u51e0\u4e2a\u6708\u4e5f\u65e0\u59a8\uff0c\u73b0\u5728\u4e48\u2014\u2014 \u3000\u3000\u4ed6\u6447\u4e86\u5934\uff0c\u6de1\u6de1\u9053\uff1a\u73b0\u5728\u6211\u5b9e\u5728\u6ca1\u6709\u8fd9\u4e48\u597d\u7684\u80c3\u53e3\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9762\u4e0a\u9aa4\u7136\u53d8\u4e86\u989c\u8272\uff0c\u51b7\u7b11\u9053\uff1a\u4f60\u656c\u9152\u4e0d\u5403\uff0c\u8981\u5403\u7f5a\u9152\uff0c\u597d\u3002 \u3000\u3000\u5979\u5ffd\u7136\u4e00\u6325\u624b\u3002 \u3000\u3000\u5750\u5728\u674e\u5bfb\u6b22\u56db\u4fa7\u7684\u51e0\u4e2a\u80d6\u5973\u4eba\u7acb\u523b\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u4eec\u7684\u4eba\u867d\u7136\u80d6\uff0c\u4f46\u52a8\u4f5c\u5374\u4e0d\u6162\uff0c\u817f\u4e00\u4f38\uff0c\u56db\u9762\u516b\u65b9\u5730\u5411\u674e\u5bfb\u6b22\u5305\u56f4\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c4b\u9876\u5f88\u4f4e\uff0c\u674e\u5bfb\u6b22\u65e2\u4e0d\u80fd\u5f80\u4e0a\u8dc3\uff0c\u4e5f\u4e0d\u80fd\u5f80\u5916\u51b2\u2014\u2014\u770b\u5230\u8fd9\u4e9b\u5973\u4eba\u8eab\u4e0a\u7684\u80a5\u8089\uff0c\u4ed6\u7b80\u76f4 \u4e00\u770b\u7740\u5c31\u6076\u5fc3\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u5973\u4eba\u5374\u6324\u8d8a\u8fd1\uff0c\u7adf\u4f3c\u60f3\u5c06\u4ed6\u5939\u5728\u4e2d\u95f4\uff0c\u4ed6\u7684\u98de\u5200\u82e5\u51fa\u624b\uff0c\u7eb5\u80fd\u51fb\u5012\u4e00\u4e2a\uff0c\u522b\u7684\u4eba \u7167\u6837\u8fd8\u662f\u8981\u51b2\u4e0a\u6765\u7684\u3002 \u3000\u3000\u82e5\u771f\u7684\u88ab\u5979\u4eec\u5939\u4f4f\uff0c\u90a3\u6ecb\u5473\u674e\u5bfb\u6b22\u7b80\u76f4\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u53ea\u542c\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u6211\u77e5\u9053\u8fde\u5c11\u6797\u5bfa\u7684\u7f57\u6c49\u9635\u90fd\u56f0\u4e0d\u4f4f\u4f60\uff0c\u4f46\u4f60\u82e5\u80fd\u7834\u4e86\u6211 \u8fd9\u8089\u9635\uff0c\u624d\u771f\u7684\u7b97\u4f60\u6709\u672c\u4e8b\u3002 \u3000\u3000\u5979\u7b11\u58f0\u8d8a\u6765\u8d8a\u5927\uff0c\u5c0f\u697c\u4e0b\u7684\u6728\u67b6\uff0c\u4e5f\u88ab\u538b\u5f97\u5431\u5431\u53d1\u54cd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u773c\u775b\u4eae\u4e86\uff0c\u4ed6\u5ffd\u7136\u60f3\u5230\u4e86\u94c3\u94c3\u3002 \u3000\u3000\u94c3\u94c3\u6ca1\u6709\u4e0a\u697c\u3002 \u3000\u3000\u5979\u4e0d\u4f1a\u773c\u770b\u7740\u674e\u5bfb\u6b22\u88ab\u56f0\u6b7b\uff0c\u5979\u4e00\u5b9a\u5728\u60f3\u6cd5\u5b50\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u3002\u53ea\u542c\u8f70\u7684\u4e00\u58f0\uff0c\u6574\u5ea7\u697c\u90fd\u57ae\u4e86\u4e0b\u53bb\uff0c\u53ea\u542c\u54ce\u54df\uff0c\u5657\u549a\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u6ee1\u5c4b\u5b50 \u7684\u4eba\u4e5f\u968f\u7740\u8dcc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c4b\u9876\u4e5f\u88c2\u5f00\u4e86\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8eab\u5f62\u63a0\u8d77\uff0c\u71d5\u5b50\u822c\u81ea\u6d1e\u53e3\u7a7f\u51fa\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u5927\u6b22\u559c\u5973\u83e9\u8428\u4e00\u5b9a\u4e5f\u8dcc\u4e86\u4e0b\u53bb\uff0c\u5979\u8eab\u5b50\u81f3\u5c11\u4e5f\u6709\u4e09\u56db\u767e\u65a4\uff0c\u8fd9\u4e00\u8dcc\u4e0b\u53bb\uff0c\u7eb5\u7136\u80fd \u722c\u8d77\u6765\uff0c\u81f3\u5c11\u4e5f\u5f97\u8d39\u534a\u5929\u52b2\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u5927\u6b22\u559c\u5973\u83e9\u8428\u4e0d\u4f46\u53cd\u5e94\u5feb\u5f97\u60ca\u4eba\uff0c\u8f7b\u529f\u4e5f\u7edd\u4e0d\u6bd4\u522b\u4eba\u5dee\uff0c\u674e\u5bfb\u6b22\u8eab\u5b50\u521a\u63a0\u51fa\uff0c\u5c31 \u542c\u5f97\u53c8\u662f\u8f70\u7684\u4e00\u58f0\u5927\u9707\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u53c8\u5c06\u5c4b\u9876\u649e\u7834\u4e86\u4e2a\u5927\u6d1e\uff0c\u5c31\u50cf\u662f\u4e2a\u5927\u6c14\u7403\u4f3c\u5730\u98de\u4e86\u51fa\u6765\uff0c\u8fde\u661f\u5149\u6708\u8272\u90fd\u88ab \u5979\u906e\u4f4f\u3002 \u3000\u3000\u5c0f\u697c\u8fd8\u5728\u7ee7\u7eed\u5f80\u4e0b\u5012\u584c\uff0c\u7070\u571f\u5f25\u6f2b\uff0c\u74e6\u783e\u7eb7\u98de\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5934\u4e5f\u4e0d\u56de\uff0c\u63a0\u4e0b\u5730\u9762\u3002 \u3000\u3000\u53ea\u542c\u5927\u6b22\u559c\u5973\u83e9\u8428\u7b11\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u4f60\u65e2\u5df2\u88ab\u6211\u770b\u4e2d\uff0c\u5c31\u518d\u4e5f\u4f11\u60f3\u8dd1\u5f97\u4e86\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u5979\u6574\u4e2a\u4eba\u5df2\u5411\u674e\u5bfb\u6b22\u6251\u4e86\u8fc7\u6765\u3002\u674e\u5bfb\u6b22\u53ea\u89c9\u98ce\u58f0\u547c\u547c\uff0c\u5c31\u4eff\u4f5b\u6574\u5ea7\u5c71\u5cf0\u90fd\u5df2\u5411 \u4ed6\u538b\u4e0b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u7a81\u7136\u5411\u540e\u6325\u51fa\u3002\u4f46\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u5c0f\u674e\u98de\u5200\u7ec8\u4e8e\u51fa\u624b\uff01 \u3000\u3000\u51fa\u624b\u4e00\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff01 \u3000\u3000\u9c9c\u8840\u98de\u6cc9\u822c\u81ea\u5927\u6b22\u559c\u5973\u83e9\u8428\u8138\u4e0a\u6807\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u674e\u5bfb\u6b22\u98de\u5200\u53d6\u7684\u5e76\u975e\u5979\u54bd\u5589\uff0c\u800c\u662f\u5979\u7684\u53f3\u773c\uff01\u4ed6\u7684\u98de\u5200\u4e00\u51fa\u624b\uff0c\u5c31\u77e5\u9053\u7edd\u4e0d\u4f1a\u843d \u7a7a\u3002 \u3000\u3000\u4ed6\u6709\u8fd9\u4fe1\u5fc3\u3002 \u3000\u3000\u4f46\u5927\u6b22\u559c\u5973\u83e9\u8428\u7684\u7b11\u58f0\u5374\u4ecd\u672a\u505c\u987f\uff0c\u7b11\u5f97\u674e\u5bfb\u6b22\u6709\u70b9\u6bdb\u9aa8\u609a\u7136\u3002\u4ed6\u5fcd\u4e0d\u4f4f\u56de\u5934\uff0c\u53ea\u89c1\u5927 \u6b22\u559c\u5973\u83e9\u8428\u6b63\u4e00\u6b65\u5411\u4ed6\u8d70\u8fc7\u6765\uff0c\u9762\u4e0a\u9c9c\u8840\u6d41\u4e2a\u4e0d\u505c\uff0c\u98de\u5200\u8fd8\u63d2\u5728\u5979\u773c\u7736\u91cc\u3002 \u3000\u3000\u4f46\u5979\u5374\u4e1d\u6beb\u4e0d\u89c9\u75db\u82e6\uff0c\u683c\u683c\u7b11\u9053\uff1a\u674e\u5bfb\u6b22\uff0c\u6211\u5df2\u770b\u4e0a\u4f60\uff0c\u4f60\u5c31\u8dd1\u4e0d\u4e86\u7684\uff0c\u4f60\u8fd8\u6709\u51e0\u628a\u98de \u5200\uff0c\u4e00\u9f50\u4f7f\u51fa\u6765\u5427\uff0c\u50cf\u8fd9\u4e48\u5927\u7684\u5200\uff0c\u5c31\u7b97\u6709\u4e00\u767e\u628a\u90fd\u63d2\u5728\u6211\u8eab\u4e0a\uff0c\u6211\u4e5f\u4e0d\u5728\u4e4e\uff01\u5979\u5ffd\u7136\u53cd\u624b \u62d4\u51fa\u90a3\u628a\u5200\uff0c\u653e\u5728\u5634\u91cc\u5927\u56bc\u8d77\u6765\u3002 \u3000\u3000\u4e00\u67c4\u7cbe\u94a2\u94f8\u6210\u7684\u98de\u5200\uff0c\u7adf\u88ab\u5979\u751f\u751f\u56bc\u788e\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u7981\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u7b80\u76f4\u4e0d\u662f\u4eba\uff0c\u7b80\u76f4\u662f\u4e2a\u4e0a\u53e4\u6d2a\u8352\u65f6\u4ee3\u7684\u5de8\u517d\u3002 \u3000\u3000\u86e4\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u5927\u6b22\u559c\u5973\u83e9\u8428\u53d1\u51fa\u4e00\u58f0\u60ca\u5929\u52ab\u5730\u822c\u7684\u72c2\u543c\uff0c\u6574\u4e2a\u6811\u6797\u90fd\u4f3c\u5df2\u88ab\u8fd9\u543c\u58f0 \u9707\u5f97\u6447\u52a8\u8d77\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c1\u5230\u4e00\u70b9\u78a7\u68ee\u68ee\u7684\u5251\u5c16\u5ffd\u7136\u81ea\u5979\u524d\u80f8\u7a81\u51fa\uff0c\u63a5\u7740\uff0c\u5c31\u6709\u4e00\u80a1\u9c9c\u8840\u66b4\u96e8\u822c\u98de\u6e85\u4e86 \u51fa\u6765\u3002 \u3000\u3000\u7136\u540e\uff0c\u624d\u89c1\u5230\u6e38\u9f99\u751f\u53cc\u624b\u63e1\u7740\u593a\u60c5\u5251\u7684\u5251\u67c4\uff0c\u4e00\u628a\u4e09\u5c3a\u4e03\u5bf8\u957f\u7684\u593a\u60c5\u5251\uff0c\u5df2\u5168\u90fd\u523a\u5165\u4e86 \u5927\u6b22\u559c\u5973\u83e9\u8428\u7684\u540e\u80cc\u3002 \u3000\u3000\u5251\u5c16\u81ea\u540e\u80cc\u523a\u5165\uff0c\u524d\u5fc3\u7a7f\u51fa\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u7684\u4eba\u8ddf\u7740\u5012\u4e0b\uff0c\u6070\u5de7\u538b\u5728\u6e38\u9f99\u751f\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5580\u5693\u4e4b\u58f0\u4e00\u8fde\u4e32\u7684\u54cd\uff0c\u6e38\u9f99\u751f\u5168\u8eab\u7684\u9aa8\u5934\u90fd\u4f3c\u5df2\u88ab\u5979\u538b\u65ad\uff0c\u4f46\u4ed6\u5374\u54ac\u7d27\u7259\u5173\uff0c\u4e0d\u51fa \u4e00\u58f0\u3002 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u725b\u4e00\u822c\u5730\u5598\u606f\u7740\uff0c\u9053\uff1a\u662f\u4f60\u2014\u2014\u539f\u6765\u662f\u4f60\uff01 \u3000\u3000\u6e38\u9f99\u751f\u4e5f\u5598\u606f\uff1a\u4f60\u60f3\u4e0d\u5230\u5427\u2014\u2014 \u3000\u3000\u5927\u6b22\u559c\u5973\u83e9\u8428\u9053\uff1a\u6211\u5bf9\u4f60\u4e0d\u574f\uff0c\u4e3a\u4f55\u8981\u6697\u7b97\u6211\uff1f \u3000\u3000\u6e38\u9f99\u751f\u8138\u4e0a\u7684\u51b7\u6c57\u4e00\u7c92\u5f80\u5916\u5192\uff0c\u54ac\u7259\u9053\uff1a\u6211\u4e00\u76f4\u6ca1\u6709\u6b7b\uff0c\u5c31\u4e3a\u7684\u662f\u5728\u7b49\u7740\u8fd9\u4e48\u6837\u7684\u4e00\u5929 \u2014\u2014 \u3000\u3000\u5979\u5df2\u88ab\u538b\u5f97\u8fde\u547c\u5438\u90fd\u5df2\u5c06\u505c\u6b62\uff0c\u773c\u524d\u6e10\u6e10\u53d1\u9ed1\uff0c\u53ea\u89c9\u5f97\u5927\u6b22\u559c\u5973\u83e9\u8428\u8eab\u5b50\u4e00\u9635\u62bd\u6410\uff0c\u5ffd \u7136\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u5230\u4e86\u674e\u5bfb\u6b22\u90a3\u53cc\u6c38\u8fdc\u90fd\u5e26\u7740\u4e00\u62b9\u5fe7\u90c1\u7684\u773c\u775b\uff0c\u4ed6\u4e5f\u611f\u5230\u6709\u4e00\u53cc\u7a33\u5b9a\u7684\u624b\u6b63 \u5728\u66ff\u4ed6\u64e6\u7740\u989d\u4e0a\u7684\u51b7\u6c57\u3002 \u3000\u3000\u8fd9\u53cc\u624b\u867d\u7136\u968f\u65f6\u90fd\u53d6\u4eba\u7684\u6027\u547d\uff0c\u5374\u53c8\u968f\u65f6\u90fd\u5728\u51c6\u5907\u7740\u5e2e\u52a9\u522b\u4eba\uff0c\u8fd9\u53cc\u624b\u91cc\u6709\u65f6\u63e1\u7740\u7684\u867d \u662f\u6740\u4eba\u7684\u5200\uff0c\u4f46\u6709\u65f6\u5374\u63e1\u7740\u6ee1\u628a\u540c\u60c5\u3002 \u3000\u3000\u6e38\u9647\u751f\u52c9\u5f3a\u6324\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u5374\u5931\u8d25\u4e86\uff0c\u53ea\u80fd\u6323\u624e\u9053\uff1a\u6211\u4e0d\u662f\u6e38\u9f99\u751f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u534a\u664c\uff0c\u624d\u6c89\u91cd\u5730\u70b9\u4e86\u5934\uff0c\u9053\uff1a\u4f60\u4e0d\u662f\u3002 \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u6e38\u9f99\u751f\u65e9\u5df2\uff0d\u65e9\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\uff0c\u6211\u660e\u767d\u3002 \u3000\u3000\u6e38\u9f99\u751f\u9053\uff1a\u4f60\u4eca\u65e5\u6839\u672c\u672a\u89c1\u5230\u6e38\u9f99\u751f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u53ea\u77e5\u9053\u4ed6\u662f\u6211\u7684\u670b\u53cb\uff0c\u522b\u7684\u6211\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6e38\u9f99\u751f\u5634\u89d2\u7ec8\u4e8e\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u55c4\u58f0\u9053\uff1a\u80fd\u4ea4\u5230\u4f60\u8fd9\u79cd\u670b\u53cb\u7684\u4eba\uff0c\u5b9e\u5728\u662f\u8fd0\u6c14\uff0c\u6211\u53ea\u6068 \u2014\u2014 \u3000\u3000\u4ed6\u53ea\u89c9\u4e00\u53e3\u6c14\u4f3c\u5df2\u63d0\u4e0d\u8d77\u6765\uff0c\u7528\u5c3d\u5168\u8eab\u529b\u6c14\uff0c\u5927\u547c\u9053\uff1a\u6211\u53ea\u6068\u4e3a\u4f55\u4e0d\u6b7b\u5728\u4f60\u624b\u91cc\uff01 \u3000\u3000\u9ece\u660e\u3002 \u3000\u3000\u67ab\u6797\u5916\u6dfb\u4e86\u4e09\u5806\u65b0\u575f\u3002\u662f\u6e38\u9f99\u751f\u3001\u84dd\u874e\u5b50\u548c\u5927\u6b22\u559c\u5973\u83e9\u8428\u7684\u575f\u2014\u2014\u6398\u575f\u7684\u6b63\u662f\u5979\u81ea\u5df1\u7684 \u95e8\u4e0b\u3002 \u3000\u3000\u5979\u4eec\u5bf9\u5927\u6b22\u559c\u7684\u6b7b\uff0c\u7adf\u4e1d\u6beb\u4e0d\u89c9\u5f97\u60b2\u6124\uff0c\u663e\u89c1\u8fd9\u4f4d\u5973\u83e9\u8428\u5e76\u975e\u771f\u7684\u6709\u83e9\u8428\u5fc3\u80a0\uff0c\u6d3b\u7740\u65f6 \u4e5f\u5e76\u4e0d\u8ba8\u4eba\u6b22\u559c\u3002 \u3000\u3000\u4f7f\u8fd9\u5c0f\u697c\u5012\u584c\u7684\uff0c\u679c\u7136\u662f\u94c3\u94c3\u3002 \u3000\u3000\u5979\u81ea\u5df1\u89c9\u5f97\u5f97\u610f\uff1a\u6211\u53ea\u4e0d\u8fc7\u5f04\u677e\u4e86\u4e00\u6839\u67f1\u5b50\uff0c\u5c0f\u697c\u5c31\u5012\u4e86\u4e0b\u6765\uff0c\u82e5\u4e0d\u662f\u6211\u89c1\u673a\u5f97\u5feb\uff0c\u9669 \u4e9b\u5c31\u8981\u88ab\u6d3b\u6d3b\u538b\u6b7b\u3002 \u3000\u3000\u89c1\u5230\u5927\u6b22\u559c\u7684\u95e8\u4e0b\u4e00\u4e2a\u4e2a\u5168\u90fd\u8d70\u4e86\uff0c\u5979\u53c8\u89c9\u5f97\u5f88\u5947\u602a\uff01 \u3000\u3000\u5979\u4eec\u4e3a\u4ec0\u4e48\u6ca1\u6709\u66ff\u5e08\u5085\u62a5\u4ec7\u7684\u610f\u601d\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u90a3\u4f4d\u5973\u83e9\u8428\u53ea\u987e\u7740\u62fc\u547d\u586b\u5979\u4eec\u7684\u809a\u5b50\uff0c\u5374\u5fd8\u4e86\u53bb\u7167\u987e\u5979\u4eec\u7684\u5fc3 \u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4e0d\u9519\uff0c\u4e00\u4e2a\u4eba\u7684\u809a\u5b50\u82e5\u592a\u9971\uff0c\u5c31\u61d2\u5f97\u7528\u5fc3\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u7684\u5c0f\u5634\u561f\u4e86\u8d77\u6765\uff0c\u6068\u6068\u9053\uff1a\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u53ea\u6709\u84dd\u874e\u5b50\uff0c\u5979\u7684\u8170\u6bd4\u6211\u7ec6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u4ee5\u4e3a\u6211\u5fc3\u91cc\u53ea\u6709\u84dd\u874e\u5b50\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u5f53\u7136\uff0c\u4e3a\u4e86\u5979\uff0c\u4f60\u4e0d\u60dc\u5192\u90a3\u4e48\u5927\u7684\u9669\uff0c\u4e0d\u60dc\u53bb\u62fc\u547d\uff0c\u5176\u5b9e\u5979\u65e9\u5df2\u6b7b\u4e86\uff0c\u6839\u672c\u5c31 \u7528\u4e0d\u7740\u4f60\u4e3a\u5979\u64cd\u5fc3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5979\u6d3b\u7740\u65f6\u82e5\u662f\u6211\u7684\u670b\u53cb\uff0c\u6b7b\u4e86\u4e5f\u662f\u6211\u7684\u670b\u53cb\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u2014\u2014\u6211\u96be\u9053\u5c31\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5f53\u7136\u662f\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4f60\u65e2\u7136\u80af\u4e3a\u6b7b\u4e86\u7684\u670b\u53cb\u53bb\u62fc\u547d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u66ff\u6d3b\u7740\u7684\u670b\u53cb\u60f3\u60f3\u5462\uff1f \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u773c\u5708\u53c8\u7ea2\u4e86\uff0c\u9053\uff1a\u6211\u672c\u6765\u5c31\u6ca1\u6709\u4eb2\u4eba\uff0c\u73b0\u5728\u8fde\u5bb6\u90fd\u6ca1\u6709\u4e86\uff0c\u4f60\u96be\u9053\u771f\u80fd\u773c \u770b\u7740\u6211\u6d3b\u5728\u4e16\u4e0a\uff0c\u6bcf\u5929\u5411\u4eba\u5bb6\u8981\u5269\u996d\u5403\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u73b0\u5728\u7684\u5973\u5b69\u5b50\u8d8a\u6765\u8d8a\u4f1a\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u94c3\u94c3\u4ece\u6307\u7f1d\u91cc\u5077\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u60a0\u60a0\u9053\uff1a\u4f55\u51b5\uff0c\u4f60\u82e5\u4e0d\u5e26\u6211\u8d70\uff0c\u600e\u80fd\u627e\u5230\u6211\u5bb6\u5c0f\u59d0\u5462\uff1f\u4f60 \u82e5\u627e\u4e0d\u5230\u6211\u5bb6\u5c0f\u59d0\uff0c\u53c8\u600e\u80fd\u627e\u5230\u4f60\u7684\u670b\u53cb\u963f\u98de\uff1f \u3000\u3000\u963f\u98de\u6b63\u5728\u559d\u6c64\u3002 \u3000\u3000\u725b\u8089\u6c64\uff0c\u7096\u5f97\u5f88\u9999\uff0c\u5f88\u6d53\u3002 \u3000\u3000\u963f\u98de\u6367\u5728\u624b\u91cc\u6162\u6162\u5730\u555c\u7740\uff0c\u773c\u775b\u832b\u7136\u76f4\u89c6\u7740\u6c64\u7684\u8fb9\u7f18\uff0c\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u4eff\u4f5b\u6839\u672c\u8fa8\u4e0d \u51fa\u8fd9\u7897\u6c64\u7684\u6ecb\u5473\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5750\u5728\u5bf9\u9762\uff0c\u624b\u6258\u7740\u816e\uff0c\u6e29\u67d4\u5730\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u6700\u8fd1\u4f60\u8138\u8272\u4e0d\u592a\u597d\uff0c\u591a\u559d\u4e9b\u6c64\u5427\uff0c\u8fd9 \u6c64\u6ecb\u8865\u5f97\u5f88\uff0c\u51b7\u4e86\u5c31\u4e0d\u597d\u5403\u4e86\u3002 \u3000\u3000\u963f\u98de\u4ef0\u8d77\u5934\uff0c\u5c06\u4e00\u5927\u7897\u6c64\u5168\u90fd\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8f7b\u8f7b\u66ff\u4ed6\u62b9\u4e86\u62b9\u5634\uff0c\u9053\uff1a\u597d\u4e0d\u597d\u559d\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u597d\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8981\u4e0d\u8981\u518d\u66ff\u4f60\u6dfb\u4e00\u7897\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u8981\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u8fd9\u5c31\u5bf9\u4e86\uff0c\u6700\u8fd1\u4f60\u5403\u996d\u5403\u5f97\u6bd4\u4ee5\u524d\u5c11\u5f97\u591a\uff0c\u5c31\u8be5\u591a\u559d\u51e0\u7897\u6c64\u3002 \u3000\u3000\u5c4b\u5b50\u5f88\u7b80\u964b\uff0c\u5374\u662f\u65b0\u7c89\u5237\u8fc7\u7684\uff0c\u8fde\u53a8\u623f\u7684\u5899\u90fd\u6ca1\u6709\u88ab\u6cb9\u70df\u718f\u9ed1\uff0c\u56e0\u4e3a\u4ed6\u4eec\u521a\u642c\u6765\u4e0d\u5230\u4e24 \u5929\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u6dfb\u4e86\u7897\u6c64\uff0c\u6367\u5230\u963f\u98de\u9762\u524d\uff0c\u7b11\u9053\uff1a\u8fd9\u5730\u65b9\u867d\u4e0d\u5927\uff0c\u83dc\u5e02\u573a\u5374\u4e0d\u5c0f\uff0c\u53ea\u4e0d\u8fc7\u5356\u8089 \u7684\u6709\u70b9\u6b3a\u751f\uff0c\u4e00\u65a4\u8089\u5c31\u8981\u591a\u7b97\u6211\u5341\u6587\u94b1\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u660e\u5929\u6211\u4eec\u4e0d\u559d\u725b\u8089\u6c64\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f\u4f60\u4e0d\u559c\u6b22\uff1f \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u559c\u6b22\uff0c\u53ef\u662f\u6211\u4eec\u559d\u4e0d\u8d77\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u7528\u4e0d\u7740\u4e3a\u94b1\u53d1\u6101\uff0c\u8fd9\u51e0\u5e74\u72d0\u76ae\u8863\u670d\u6b63\u98ce\u884c\uff0c\u4e0a\u4e2a\u6708\u4f60\u6253\u7684\u72d0\u72f8 \uff0c\u6211\u4e00\u5171\u5356\u4e86\u4e8c\u5341\u4e03\u4e24\u94f6\u5b50\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u7528\u5b8c\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u603b\u8981\u7528\u5b8c\u7684\uff0c\u8fd9\u5730\u65b9\u53c8\u6ca1\u6709\u72d0\u72f8\u53ef\u6253\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u7b49\u7528\u5b8c\u65f6\u518d\u8bf4\u5427\uff0c\u4f55\u51b5\uff0c\u6211\u8fd8\u6709\u79c1\u623f\u94b1\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u6211\u4e0d\u80fd\u7528\u4f60\u7684\u94b1\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u5708\u7acb\u523b\u7ea2\u4e86\uff0c\u4f4e\u5934\u9053\uff1a\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u8fd9\u4e9b\u94b1\u65e2\u4e0d\u662f\u5077\u6765\u7684\uff0c\u4e5f\u4e0d\u662f\u62a2\u6765\u7684\uff0c\u662f \u6211\u66ff\u4eba\u5bb6\u7f1d\u8865\uff0c\u7528\u5341\u6839\u624b\u6307\u8f9b\u82e6\u8d5a\u6765\u7684\u3002 \u7b2c\u56db\u5341\u4e5d\u7ae0\u3000\u5404\u6709\u5b89\u6392>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u56db\u5341\u4e5d\u7ae0\u3000\u5404\u6709\u5b89\u6392 \u3000\u3000\u6797\u4ed9\u513f\u8bf4\u7740\u8bf4\u7740\uff0c\u773c\u6cea\u5df2\u6d41\u4e0b\u6765\uff0c\u5e7d\u5e7d\u5730\u9053\uff1a\u4f60\u77e5\u9053\uff0c\u4ee5\u524d\u6211\u90a3\u4e9b\u94b1\uff0c\u90fd\u5df2\u542c\u4f60\u7684\u8bdd\u5206 \u7ed9\u4eba\u5bb6\u4e86\uff0c\u4f60\u96be\u9053\u4e0d\u4fe1\uff1f \u3000\u3000\u963f\u98de\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u4e0d\u662f\u4e0d\u4fe1\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u6211\u5e94\u8be5\u517b\u4f60\u7684\uff0c\u6211\u4e0d\u80fd\u8ba9\u4f60\u53d7\u593a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6d41\u7740\u6cea\u9053\uff1a\u6211\u4eec\u4e24\u4eba\u65e2\u7136\u5df2\u8fd9\u4e48\u597d\u4e86\uff0c\u4f60\u5c31\u8be5\u518d\u5206\u4ec0\u4e48\u4f60\u7684\uff0c\u6211\u7684\uff0c\u8fde\u6211\u7684\u5fc3\u90fd \u5df2\u662f\u4f60\u7684\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u963f\u98de\u95ed\u4e0a\u773c\u775b\uff0c\u5c06\u5979\u7684\u4e00\u53cc\u624b\u7d27\u63e1\u5728\u624b\u91cc\uff0c\u53ea\u8981\u80fd\u6c38\u8fdc\u63e1\u4f4f\u8fd9\u53cc\u624b\uff0c\u4ed6\u518d\u4e5f\u4e0d\u8981\u4ec0\u4e48\u522b \u7684\u3002 \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u7761\u7740\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5c06\u81ea\u5df1\u7684\u624b\u6084\u6084\u5730\u4ece\u4ed6\u624b\u91cc\u62bd\u51fa\u6765\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u6084\u6084\u8d70\u4e86\u51fa\u53bb\uff0c\u6084\u6084\u5730\u5173\u8d77\u4e86\u95e8\uff0c\u56de\u5230\u4e86\u81ea\u5df1\u7684\u5c4b\u91cc\uff0c\u4ece\u4e00\u53cc\u7b80\u964b\u7684\u5c0f\u6728\u7bb1\u91cc \uff0c\u53d6\u51fa\u4e86\u4e2a\u5c0f\u6728\u74f6\u3002 \u3000\u3000\u5979\u5012\u4e86\u676f\u8336\uff0c\u53c8\u4ece\u6728\u74f6\u4e2d\u5012\u51fa\u4e9b\u95ea\u7740\u94f6\u5149\u7684\u7c89\u672b\uff0c\u5c31\u7740\u8336\u541e\u4e86\u4e0b\u53bb\uff0c\u8fd9\u4e9b\u94f6\u7c89\u5979\u6bcf\u5929\u90fd \u4e0d\u4f1a\u5fd8\u8bb0\u5403\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u662f\u73cd\u73e0\u78e8\u6210\u7684\u7c89\uff0c\u636e\u8bf4\u5973\u4eba\u5403\u4e86\uff0c\u5c31\u53ef\u4f7f\u9752\u6625\u6c38\u9a7b\u3002 \u3000\u3000\u671b\u7740\u624b\u91cc\u7684\u5c0f\u6728\u74f6\uff0c\u6797\u4ed9\u513f\u4e0d\u89c9\u7b11\u4e86\u3002 \u3000\u3000\u963f\u98de\u82e5\u77e5\u9053\u8fd9\u74f6\u73cd\u73e0\u7c89\u503c\u591a\u5c11\u94b1\uff0c\u4e00\u5b9a\u4f1a\u5413\u4e00\u8df3\u3002 \u3000\u3000\u5979\u53d1\u89c9\u7537\u4eba\u90fd\u5f88\u5bb9\u6613\u53d7\u9a97\uff0c\u5c24\u5176\u5bb9\u6613\u88ab\u81ea\u5df1\u5fc3\u7231\u7684\u5973\u4eba\u6b3a\u9a97\uff0c\u6240\u4ee5\u5979\u4e00\u5411\u89c9\u5f97\u7537\u4eba\u4e0d\u4f46 \u5f88\u53ef\u601c\uff0c\u4e5f\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u5979\u4ece\u672a\u9047\u5230\u8fc7\u4e00\u4e2a\u4ece\u4e0d\u53d7\u9a97\u7684\u7537\u4eba\u3002 \u3000\u3000\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u2014\u2014\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u4e00\u60f3\u8d77\u674e\u5bfb\u6b22\uff0c\u5979\u7684\u5fc3\u7acb\u523b\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4eca\u5929\u5df2\u7ecf\u662f\u5341\u6708\u521d\u4e94\u4e86\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u662f\u4e0d\u662f\u5df2\u6b7b\u4e86\uff1f\u4e3a\u4ec0\u4e48\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6d88\u606f\uff1f \u3000\u3000\u8fdc\u5904\u5ffd\u4f20\u6765\u4e00\u9635\u811a\u6b65\u58f0\uff0c\u4e24\u4e2a\u77ee\u5065\u7684\u9752\u8863\u5c11\u5e74\u62ac\u7740\u9876\u5c0f\u8f7f\u5065\u6b65\u5982\u98de\u800c\u6765\uff0c\u5c31\u5728\u95e8\u53e3\u505c\u4e0b \u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u6797\u4ed9\u513f\u6084\u6084\u8d70\u4e86\u51fa\u6765\uff0c\u63a9\u8d77\u95e8\uff0c\u5750\u4e0a\u8f7f\uff0c\u5c06\u56db\u9762\u7684\u5e18\u5b50\u90fd\u653e\u843d\uff0c\u7af9\u5e18\u5e76\u4e0d\u5bc6 \uff0c\u522b\u4eba\u867d\u77a7\u4e0d\u89c1\u5979\uff0c\u5979\u5374\u53ef\u4ee5\u77a7\u89c1\u522b\u4eba\u3002 \u3000\u3000\u8f7f\u5b50\u5df2\u62ac\u8d77\uff0c\u5411\u6765\u8def\u5954\u53bb\u3002 \u3000\u3000\u5f80\u524d\u8d70\uff0c\u5c31\u662f\u7247\u6811\u53f6\u8fd8\u672a\u67af\u843d\u7684\u5bc6\u6797\uff0c\u5bc6\u6797\u5de6\u9762\u6709\u4e2a\u5c0f\u5c0f\u7684\u571f\u5730\u5e99\uff0c\u53f3\u9762\u662f\u4e00\u5806\u5806\u8352\u575f \u3002 \u3000\u3000\u8f7f\u5b50\u5c31\u5728\u8fd9\u91cc\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u524d\u9762\u7684\u8f7f\u592b\uff0c\u81ea\u8f7f\u5e95\u53d6\u51fa\u4e86\u4e2a\u706f\u7b3c\uff0c\u71c3\u8d77\u4e86\u70db\u706b\uff0c\u9ad8\u9ad8\u6311\u8d77\uff0c\u4e0a\u9762\u8fd8\u753b\u7740\u4e00\u6735\u6735\u9c9c\u7ea2\u7684 \u6885\u82b1\u3002 \u3000\u3000\u706f\u7b3c\u4e00\u71c3\u8d77\uff0c\u5c31\u5ffd\u7136\u9b3c\u9b45\u822c\u51fa\u73b0\u51e0\u6761\u4eba\u5f71\uff0c\u5206\u5728\u56db\u4e2a\u65b9\u5411\uff0c\u5411\u8f7f\u5b50\u8fd9\u8fb9\u5954\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u811a\u6b65\u90fd\u4e0d\u6162\uff0c\u795e\u60c5\u4f3c\u4e4e\u90fd\u5f88\u5174\u594b\uff0c\u4f46\u53d1\u73b0\u9664\u4e86\u81ea\u5df1\u5916\u8fd8\u6709\u522b\u4eba\u65f6\uff0c\u56db\u4e2a\u4eba\u811a\u6b65\u90fd \u7acb\u523b\u53d8\u4e86\uff0c\u811a\u6b65\u4e5f\u7f13\u4e0b\uff0c\u5f7c\u6b64\u77aa\u4e86\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u90fd\u5e26\u7740\u4e9b\u8b66\u6212\u4e4b\u8272\uff0c\u8fd8\u5e26\u7740\u4e9b\u654c\u610f\u3002 \u3000\u3000\u4ece\u6811\u6797\u91cc\u8d70\u51fa\u6765\u7684\u662f\u4e2a\u5706\u8138\u7684\u4e2d\u5e74\u4eba\uff0c\u8eab\u4e0a\u7a7f\u5f97\u5f88\u534e\u4e3d\uff0c\u770b\u6765\u50cf\u662f\u4e2a\u4e70\u5356\u505a\u5f97\u5f88\u53d1\u8d22\u7684 \u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u884c\u52a8\u5374\u5f88\u77eb\u5065\uff0c\u6b66\u529f\u7684\u6839\u57fa\u663e\u7136\u4e0d\u5f31\u3002 \u3000\u3000\u4ece\u575f\u5806\u95f4\u8d70\u51fa\u7684\u6709\u4e24\u4e2a\u4eba\uff0c\u53f3\u9762\u7684\u4e00\u4e2a\u77ed\u5c0f\u7cbe\u608d\uff0c\u770b\u6765\u4eff\u4f5b\u6709\u4e9b\u9b3c\u9b3c\u5d07\u5d07\uff0c\u8f7b\u529f\u5374\u53ef\u7b97 \u6b66\u6797\u4e2d\u7684\u9ad8\u624b\u3002 \u3000\u3000\u5de6\u9762\u4e00\u4eba\u4e0d\u9ad8\u4e0d\u77ee\uff0c\u4e0d\u80d6\u4e0d\u7626\uff0c\u7a7f\u7684\u8863\u670d\u4e5f\u5f88\u666e\u901a\uff0c\u770b\u6765\u4e1d\u6beb\u4e0d\u8d77\u773c\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u8f7b\u529f\u5374\u4f3c\u6bd4\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u9ed1\u8863\u4eba\u8fd8\u9ad8\u4e00\u7b79\u3002 \u3000\u3000\u4ece\u7960\u5802\u8d70\u51fa\u7684\u4e00\u4eba\u5e74\u7eaa\u6700\u8f7b\uff0c\u6c14\u6d3e\u4e5f\u6700\u5927\uff0c\u867d\u65bd\u5c55\u8f7b\u529f\uff0c\u4f46\u811a\u6b65\u6c89\u7a33\uff0c\u76ee\u5149\u70af\u70af\uff0c\u6b66\u529f \u4e5f\u663e\u7136\u6bd4\u522b\u4eba\u9ad8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u663e\u7136\u77e5\u9053\u6765\u7684\u662f\u8fd9\u56db\u4e2a\u4eba\uff0c\u4e5f\u6ca1\u6709\u6380\u5e18\u5b50\u77a7\u4e00\u773c\uff0c\u66f4\u6ca1\u6709\u4e0b\u8f7f\u5b50\uff0c\u53ea\u662f\u94f6\u94c3\u822c\u7b11 \u4e86\u7b11\uff0c\u9053\uff1a\u56db\u4f4d\u8fdc\u6765\u8f9b\u82e6\u4e86\uff0c\u8fd9\u91cc\u4e5f\u6ca1\u6709\u5907\u9152\u66ff\u56db\u4f4d\u6d17\u5c18\u63a5\u98ce\uff0c\u771f\u662f\u62b1\u6b49\u5f97\u5f88\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u542c\u5230\u5979\u7684\u58f0\u97f3\u90fd\u60c5\u4e0d\u81ea\u7981\u5730\u9732\u51fa\u4e86\u7b11\u5bb9\uff0c\u672c\u6765\u4eff\u4f5b\u60f3\u62a2\u7740\u8bf4\u8bdd\u7684\uff0c\u4f46\u5f7c\u6b64\u77a7\u4e86\u4e00 \u773c\uff0c\u53c8\u90fd\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\uff1a\u6211\u77e5\u9053\u56db\u4f4d\u90fd\u6709\u8bdd\u8981\u8bf4\uff0c\u4f46\u8c01\u5148\u8bf4\u5462\uff1f \u3000\u3000\u90a3\u6a21\u6837\u6700\u73b0\u51e1\u7684\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u8fd8\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u4f3c\u4e4e\u4e0d\u6562\u548c\u522b\u4eba\u4e89\u5148\u3002 \u3000\u3000\u90a3\u84dd\u8863\u5c11\u5e74\u76b1\u4e86\u76b1\u7709\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u50b2\u7136\u8f6c\u8fc7\u4e86\u5934\uff0c\u4ed6\u663e\u7136\u4e0d\u5c51\u548c\u8fd9\u4e9b\u4eba\u4e3a\u4f0d\uff0c\u662f\u4ee5\u4e5f \u4e0d\u613f\u4e89\u5148\u3002 \u3000\u3000\u90a3\u5706\u8138\u7684\u4e2d\u5e74\u4eba\u8138\u4e0a\u5806\u6ee1\u4e86\u5fae\u7b11\uff0c\u5411\u9ed1\u8863\u4eba\u62f1\u624b\uff0c\u9053\uff1a\u5144\u53f0\u5148\u8bf7\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5012\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u5df2\u5230\u8f7f\u524d\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u4e24\u4e2a\u6708\u4e0d\u89c1\uff0c\u4f60\u7684\u8f7b\u529f\u66f4\u9ad8\u4e86\uff0c\u771f\u662f\u8981\u559c\u53ef\u8d27\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9634\u9e37\u7684\u8138\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u5f97\u610f\u4e4b\u8272\uff0c\u62b1\u62f3\u9053\uff1a\u59d1\u5a18\u8fc7\u5956\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u6c42\u4f60\u505a\u7684\u4e24\u4ef6\u4e8b\uff0c\u60f3\u5fc5\u5b9a\u662f\u9a6c\u5230\u6210\u529f\uff0c\u6211\u77e5\u9053\u4f60\u4ece\u672a\u4ee4\u6211\u5931\u671b\u7684\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u81ea\u6000\u4e2d\u53d6\u51fa\u4e86\u4e00\u53e0\u94f6\u7968\uff0c\u53cc\u624b\u6367\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u5b9d\u5e86\u90a3\u4e00\u5e10\u90fd\u5df2\u5b8c\u5168\u6536\u9f50\u4e86\u3002\u8fd9\u91cc \u5171\u4e5d\u5343\u516b\u767e\u4e94\u5341\u4e24\uff0c\u5f00\u7684\u662f\u5c71\u897f\u540c\u798f\u53f7\u7684\u94f6\u7968\u3002 \u3000\u3000\u6797\u4ed9\u513f\u81ea\u8f7f\u5b50\u91cc\u4f38\u51fa\u4e00\u53cc\u6625\u8471\u822c\u7684\u7ea4\u7ea4\u7389\u624b\uff0c\u5c06\u90a3\u53e0\u94f6\u7968\u5168\u90fd\u63a5\u4e86\u8fc7\u53bb\uff0c\u4f3c\u4e4e\u5148\u70b9\u4e86\u70b9 \u6570\u76ee\uff0c\u624d\u7b11\u9053\uff1a\u8fd9\u6b21\u8f9b\u82e6\u4f60\u4e86\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u611f\u6fc0\u4f60\u624d\u597d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u773c\u775b\u8fd8\u76ef\u5728\u6797\u4ed9\u513f\u7684\u624b\u65b9\u624d\u4f38\u51fa\u6765\u7684\u5730\u65b9\uff0c\u4f3c\u5df2\u770b\u5f97\u75f4\u4e86\uff0c\u8fd9\u65f6\u624d\u52c9\u5f3a\u7b11\u9053\uff1a\u8c22 \u5b57\u4e0d\u6562\u5f53\uff0c\u53ea\u8981\u59d1\u5a18\u8fd8\u8bb0\u5f97\u6211\u8fd9\u4eba\u5c31\u884c\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f46\u90a3\u8bf4\u4e66\u7684\u5b59\u8001\u5934\u548c\u4ed6\u90a3\u5b59\u5973\u5462\uff1f\u4f60\u60f3\u5fc5\u5df2\u8ffd\u51fa\u4e86\u4ed6\u4eec\u7684\u4e0b\u843d\u5427\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u6211\u672c\u6765\u4e00\u76f4\u8ddf\u7740\u4ed6\u4eec\u7684\uff0c\u4f46\u5230\u4e86\u4e2d\u9053\u4e0a\uff0c\u8fd9\u4e24\u4eba\u5c31\u5ffd\u7136\u5931\u8e2a\u4e86\uff0c\u8fd9 \u4e24\u4eba\u5c31\u50cf\u2014\u2014\u5c31\u50cf\u5ffd\u7136\u4ece\u5730\u4e0a\u6d88\u5931\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8f7b\u7b11\uff1a\u8fd9\u4e24\u4eba\u7684\u884c\u8e2a\u5b9e\u5728\u795e\u79d8\uff0c\u8868\u9762\u4e0a\u867d\u88c5\u505a\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u6211\u7edd\u4e0d\u60f3\u5ff5\uff0c\u53ea\u8981\u59d1 \u5a18\u518d\u7ed9\u6211\u4e9b\u65e5\u5b50\uff0c\u6211\u4e00\u5b9a\u80fd\u8ffd\u51fa\u4ed6\u4eec\u7684\u6765\u5386\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6c89\u9ed8\u534a\u664c\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u4e0d\u5fc5\u4e86\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u4e00\u5b9a\u8ddf\u4e0d\u4f4f\u4ed6\u4eec\u7684\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u867d\u672a \u505a\u6210\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\uff0c\u7b49\u4f1a\u513f\u6211\u8fd8\u6709\u8981\u6c42\u4f60\u5e2e\u5fd9\u7684\u4e8b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u7ad9\u5230\u4e00\u65c1\uff0c\u4e5f\u4e0d\u6562\u591a\u8bdd\u4e86\u3002 \u3000\u3000\u90a3\u5706\u8138\u7684\u4e2d\u5e74\u4eba\u8fd9\u624d\u5411\u53e6\u4e24\u4eba\u62b1\u62f3\u966a\u7b11\u9053\uff1a\u5931\u793c\uff0c\u5931\u793c\u2014\u2014 \u3000\u3000\u4ed6\u4e00\u9762\u5411\u8f7f\u5b50\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u4e00\u9762\u4e0d\u505c\u5730\u4f5c\u63d6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u505a\u751f\u610f\u8bb2\u7a76\u7684\u5c31\u662f\u548c\u6c14\u751f\u8d22\uff0c\u4f60\u73b0\u5728\u771f\u4e0d\u6127\u662f\u4e2a\u5927\u8001\u677f\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u4eba\u4e00\u63d6\u4ee5\u5730\uff0c\u6ee1\u8138\u5e26\u7740\u7b11\uff0c\u9053\uff1a\u6211\u53ea\u4e0d\u8fc7\u662f\u59d1\u5a18\u624b\u4e0b\u7684\u4e00\u4e2a\u5c0f\u4f19\u8ba1\u800c\u5df2\uff0c\u59d1\u5a18\u82e5\u4e0d\u8d4f \u996d\u5403\uff0c\u6211\u5c31\u5f97\u5377\u94fa\u76d6\uff0c\u5927\u8001\u677f\u8fd9\u4e09\u5b57\uff0c\u6211\u662f\u4e07\u4e07\u4e0d\u6562\u4e0d\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u8bf4\u4ec0\u4e48\u8001\u677f\uff0c\u8bb2\u4ec0\u4e48\u4f19\u8ba1\uff0c\u6211\u7684\u751f\u610f\u5c31\u662f\u4f60\u7684\u751f\u610f\uff0c\u53ea\u8981\u597d\u597d\u5730\u53bb\u505a\uff0c \u8fd9\u751f\u610f\u603b\u6709\u4e00\u5929\u662f\u4f60\u7684\u3002 \u3000\u3000\u8fd9\u4e2d\u5e74\u4eba\u6ee1\u8138\u8d77\u4e86\u7ea2\u5149\u3002 \u3000\u3000\u4ed6\u4e00\u8fde\u8c22\u4e86\u6797\u4ed9\u513f\u597d\u51e0\u904d\uff0c\u624d\u4ece\u6000\u4e2d\u53d6\u51fa\u53e0\u94f6\u7968\uff0c\u53cc\u624b\u6367\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u8fd9\u91cc\u662f\u53bb\u5e74\u4e00\u5e74 \u8d5a\u7684\u7eaf\u5229\uff0c\u4e5f\u5f00\u7684\u662f\u540c\u798f\u53f7\u7684\u94f6\u7968\uff0c\u8bf7\u59d1\u5a18\u8fc7\u76ee\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u771f\u8f9b\u82e6\u4f60\u4e86\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4e0d\u4f46\u8001\u5b9e\u53ef\u884c\uff0c\u800c\u4e14\u4eba\u53c8\u80fd\u5e72\u2014\u2014 \u3000\u3000\u5979\u65e9\u5df2\u5c06\u94f6\u7968\u63a5\u4e86\u8fc7\u53bb\uff0c\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u6e05\u70b9\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u53e3\u6c14\u5ffd\u7136\u53d8\u4e86\uff0c\u518d\u4e5f\u6ca1\u6709\u4e1d \u6beb\u7b11\u5bb9\uff0c\u9053\uff1a\u600e\u4e48\u53ea\u6709\u516d\u5343\u4e24\uff1f \u3000\u3000\u4e2d\u5e74\u4eba\u966a\u7b11\u9053\uff1a\u662f\u516d\u5343\u4e09\u767e\u4e24\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53bb\u5e74\u5462\uff1f \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u4e5d\u5343\u56db\u767e\u4e24\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u524d\u5e74\u5462\uff1f \u3000\u3000\u4e2d\u5e74\u4eba\u64e6\u4e86\u64e6\u6c57\uff0c\u9053\uff1a\u524d\u5e74\u597d\u50cf\u2014\u2014\u6709\u4e00\u4e07\u591a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u51b7\u7b11\u9053\uff1a\u4f60\u672c\u4e8b\u53ef\u771f\u4e0d\u5c0f\uff0c\u5c45\u7136\u628a\u4e70\u5356\u8d8a\u505a\u8d8a\u56de\u53bb\u4e86\uff0c\u7167\u8fd9\u4eb2\u621a\u518d\u505a\u4e24\u5e74\uff0c\u54b1\u4eec \u5c82\u975e\u5c31\u8981\u8d34\u8001\u672c\u4e86\u4e48\uff1f \u3000\u3000\u4e2d\u5e74\u4eba\u4e0d\u505c\u5730\u64e6\u6c57\uff0c\u5403\u5403\u9053\uff1a\u8fd9\u4e24\u5e74\u4e0d\u5174\u7f0e\u5b50\u8863\u670d\uff0c\u5e9c\u7ef8\u7684\u8d5a\u5934\u4e5f\u4e0d\u5927\uff0c\u7b49\u5230\u660e\u5e74\u6625\u5929 \u7684\u65f6\u5019\uff0c\u5c31\u4e00\u5b9a\u4f1a\u6709\u8f6c\u673a\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9ed8\u7136\u534a\u664c\uff0c\u58f0\u97f3\u53d8\u5f97\u6e29\u67d4\uff0c\u9053\uff1a\u8fd9\u4e24\u5e74\u6765\uff0c\u6211\u77e5\u9053\u4f60\u5f88\u8f9b\u82e6\uff0c\u4e5f\u8be5\u56de\u5bb6\u53bb\u4eab\u51e0\u5e74 \u6e05\u798f\u4e86\u3002 \u3000\u3000\u4e2d\u5e74\u4eba\u9762\u8272\u9aa4\u7136\u5927\u53d8\uff0c\u98a4\u58f0\u9053\uff1a\u53ef\u662f\u2014\u2014\u53ef\u662f\u90a3\u8fb9\u7684\u751f\u610f\u2014\u2014 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u81ea\u7136\u4f1a\u627e\u4eba\u53bb\u63a5\uff0c\u4f60\u4e0d\u7528\u64cd\u5fc3\u3002 \u3000\u3000\u4e2d\u5e74\u4eba\u6ee1\u9762\u60ca\u6050\u4e4b\u8272\uff0c\u8eab\u5b50\u4e00\u6b65\u6b65\u5f80\u540e\u9000\uff0c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u7136\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u98de\u4e5f\u4f3c\u5730\u5411 \u6697\u6797\u90a3\u8fb9\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u521a\u9003\u51e0\u6b65\uff0c\u7a81\u89c1\u5bd2\u5149\u4e00\u95ea\u3002 \u3000\u3000\u60e8\u547c\u58f0\u4e2d\uff0c\u8840\u5149\u56db\u6e85\uff0c\u4ed6\u7684\u4eba\u5df2\u5012\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u90a3\u84dd\u886b\u5c11\u5e74\u638c\u4e2d\u5df2\u591a\u4e86\u67c4\u9752\u94a2\u957f\u5251\uff0c\u5251\u5c16\u72b9\u5728\u6ef4\u8840\u3002 \u3000\u3000\u90a3\u7070\u8863\u4eba\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u9762\u4e0a\u4ecd\u7136\u4e0d\u52a8\u58f0\u8272\uff0c\u53ea\u662f\u6de1\u9053\uff1a\u597d\u5251\u6cd5\u3002 \u3000\u3000\u84dd\u886b\u5c11\u5e74\u8fde\u77a7\u90fd\u4e0d\u77a7\u4ed6\u4e00\u773c\uff0c\u5c06\u5251\u4e0a\u7684\u8840\u6e0d\u5728\u978b\u4e0b\u64e6\u4e86\u64e6\uff0c\u633d\u624b\u6296\u51fa\u4e86\u4e2a\u5251\u82b1\uff0c\u545b\u7684\uff0c \u5251\u53c8\u5165\u9798\u3002 \u3000\u3000\u7070\u8863\u4eba\u9759\u9759\u5730\u7ad9\u7740\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4ed6\u7b49\u4e86\u5f88\u4e45\uff0c\u89c1\u5230\u8fd9\u84dd\u886b\u5c11\u5e74\u5e76\u6ca1\u6709\u548c\u4ed6\u62a2\u5148\u610f\u601d\uff0c\u624d\u5fae\u5fae\u62f1\u4e86\u62f1\u624b\uff0c\u6162\u6162\u5730\u5411\u8f7f\u5b50\u524d \u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e5f\u8bb8\u65e9\u5df2\u77e5\u9053\u8fd9\u4eba\u4e0d\u662f\u4e24\u53e5\u597d\u8bdd\u53ef\u4ee5\u4e70\u52a8\u7684\uff0c\u4e5f\u6ca1\u6709\u8ddf\u4ed6\u5ba2\u6c14\uff0c\u4e00\u6b66\u5668\u5c31\u95ee\uff1a\u9f99 \u5578\u4e91\u5df2\u56de\u4e86\u5174\u4e91\u5e84\uff1f \u3000\u3000\u7070\u8863\u4eba\u9053\uff1a\u5df2\u56de\u53bb\u5feb\u534a\u4e2a\u6708\u4e86\uff0c\u548c\u4ed6\u540c\u884c\u7684\u9664\u4e86\u80e1\u4e0d\u5f52\u4e4b\u5916\uff0c\u8fd8\u6709\u4e2a\u59d3\u5415\u7684\uff0c\u636e\u8bf4\u662f\u5415 \u5949\u5148\u7684\u5e38\u5f1f\uff0c\u7528\u7684\u4e5f\u662f\u53cc\u621f\uff0c\u770b\u6837\u5b50\u6b66\u529f\u4e5f\u4e0d\u5f31\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u90a3\u5356\u9152\u7684\u9a7c\u5b50\u5462\uff1f \u3000\u3000\u7070\u8863\u4eba\u9053\uff1a\u8fd8\u5728\u90a3\u91cc\u5356\u9152\uff0c\u8fd9\u4eba\u5012\u771f\u662f\u6df1\u85cf\u4e0d\u9732\uff0c\u8c01\u4e5f\u731c\u4e0d\u900f\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u4f46\u6211\u4e50\u2014\u2014\u4f60\u5fc5\u5b9a\u5df2\u6253\u542c\u51fa\u4e00\u70b9\u6765\u4e86\uff0c\u65e0\u8bba\u90a3\u4eba\u662f\u4ec0\u4e48\u631b\uff0c\u8981\u7792\u8fc7\u4f60\u8fd9\u53cc \u773c\u775b\u5374\u56f0\u96be\u5f97\u5f88\u3002\u7070\u8863\u4eba\u7b11\u7b11\u9053\uff1a\u82e5\u662f\u6211\u731c\u7684\u4e0d\u9519\uff0c\u90a3\u9a7c\u5b50\u5fc5\u5b9a\u548c\u8bf4\u4e66\u7684\u5b59\u8001\u5934\u6709\u4e9b\u5173\u7cfb\uff0c \u8bf4\u4e0d\u5b9a\u5c31\u662f\u6614\u5e74\u90a3\u80cc\u4e0a\u4e00\u5ea7\u5c71\uff0c\u5c71\u4e5f\u538b\u5012\u7684\u5b59\u8001\u4e8c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4f3c\u4e5f\u89c9\u60ca\u5f02\uff0c\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u4f60\u518d\u53bb\u6253\u542c\u6253\u542c\uff0c\u660e\u5929\u2014\u2014 \u3000\u3000\u5979\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u7070\u8863\u4eba\u53ea\u6709\u51d1\u8fc7\u5934\u53bb\u542c\uff0c\u542c\u4e86\u51e0\u53e5\uff0c\u4ed6\u5e73\u5e73\u677f\u677f\u7684\u4e00\u5f20\u8138\u4e0a\u4e5f\u9732\u51fa\u4e86 \u6b22\u559c\u4e4b\u8272\uff0c\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u6b65\u5b50\u4e5f\u53d8\u5f97\u8f7b\u5feb\u8d77\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u786e\u6709\u4ee4\u7537\u4eba\u670d\u8d34\u7684\u672c\u4e8b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u773c\u775b\u4e00\u76f4\u76ef\u7740\u90a3\u7070\u8863\u4eba\uff0c\u4f3c\u4e4e\u6068\u4e0d\u5f97\u7ed9\u4ed6\u4e00\u5200\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u6797\u4ed9\u513f\u5df2\u53c8\u4ece\u8f7f\u5b50\u91cc\u4f38\u51fa\u624b\uff0c\u5411\u4ed6\u62db\u4e86\u62db\u3002 \u3000\u3000\u6625\u8471\u822c\u7684\u624b\uff0c\u5728\u591c\u8272\u4e2d\u770b\u6765\u66f4\u662f\u83b9\u767d\u5982\u7389\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u75f4\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u4f60\u8fc7\u6765\uff0c\u6211\u6709\u8bdd\u544a\u8bc9\u4f60\uff0c\u540e\u5929\u665a\u4e0a\u2014\u2014 \u3000\u3000\u5979\u6084\u6084\u5730\u5728\u9ed1\u8863\u4eba\u8033\u65c1\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6ee1\u9762\u90fd\u662f\u559c\u8272\uff0c\u4e0d\u505c\u5730\u70b9\u5934\u9053\uff1a\u662f\uff0c\u662f\uff0c\u662f\uff0c\u6211\u660e\u767d\uff0c\u6211\u600e\u4e48\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u4eba\u4f3c\u5df2\u957f\u9ad8\u4e86\u4e09\u5c3a\u3002 \u3000\u3000\u7b49\u4ed6\u8d70\u4e86\uff0c\u90a3\u84dd\u886b\u5c11\u5e74\u624d\u8d70\u4e86\u8fc7\u6765\uff0c\u51b7\u9053\uff1a\u6797\u59d1\u5a18\u4f60\u5012\u771f\u662f\u5fd9\u5f97\u5f88\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6709\u4ec0\u4e48\u6cd5\u5b50\u5462\uff1f\u4ed6\u4eec\u53ef\u4e0d\u50cf\u4f60\u8ddf\u6211\uff0c\u6211\u603b\u5f97\u6577\u884d\u4ed6\u4eec\u3002 \u3000\u3000\u5979\u53c8\u4f38\u51fa\u624b\uff0c\u63e1\u4f4f\u4e86\u8fd9\u5c11\u5e74\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u4f60\u751f\u6c14\u4e86\u4e48\uff1f \u3000\u3000\u84dd\u886b\u5c11\u5e74\u677f\u7740\u8138\u9053\uff1a\u54fc\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5403\u5403\u7b11\u9053\uff1a\u4f60\u77a7\u4f60\uff0c\u5c31\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\uff0c\u5feb\u4e0a\u8f7f\u5b50\uff0c\u6211\u66ff\u4f60\u6d88\u6c14\u3002 \u3000\u3000\u84dd\u886b\u5c11\u5e74\u8fd8\u60f3\u677f\u7740\u8138\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u4e00\u58f0\u51c4\u5389\u7684\u60e8\u547c\u2014\u2014 \u3000\u3000\u58f0\u97f3\u662f\u4ece\u6811\u6797\u91cc\u4f20\u51fa\u6765\u7684\u3002 \u3000\u3000\u7070\u8863\u4eba\u672c\u5df2\u8d70\u5165\u6811\u6797\uff0c\u6b64\u523b\u53c8\u4e00\u6b65\u6b65\u9000\u4e86\u51fa\u6765\uff0c\u4ed6\u4e00\u6b65\u5f80\u540e\u9000\uff0c\u9c9c\u8840\u4e5f\u968f\u7740\u5f80\u4e0b\u843d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e5f\u6b63\u60f3\u5f80\u6811\u6797\u91cc\u53bb\uff0c\u77a7\u4ed6\u8fd9\u6837\u5b50\uff0c\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u521a\u505c\u4e86\u811a\uff0c\u7070\u8863\u4eba\u5df2\u5012\u5728\u4ed6\u811a\u4e0b \u3002 \u3000\u3000\u4ed6\u83ab\u975e\u5728\u6811\u6797\u91cc\u9047\u89c1\u4e86\u9b3c\u4e48\uff1f \u3000\u3000\u6740\u7684\u5389\u9b3c\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u60c5\u4e0d\u81ea\u7981\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u4e00\u4f38\u624b\uff0c\u62d4\u51fa\u4e86\u978b\u7b52\u91cc\u7684\u6bd4\u9996\uff0c\u773c\u775b\u77aa\u7740\u90a3\u9ed1\u9edd\u9edd\u7684\u5bc6\u6797 \uff0c\u55c4\u58f0\u9053\uff1a\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u6811\u6797\u91cc\u5bc2\u65e0\u4eba\u58f0\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u6162\u6162\u5730\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u9ad8\u800c\u9880\u957f\uff0c\u5e26\u7740\u9876\u5bbd\u5927\u7684\u7b20\u5e3d\uff0c\u7d27\u538b\u5728\u7709\u9645\uff0c\u906e\u53bb\u4e86\u9762\u76ee\uff0c\u4ed6\u4e0d\u4f46\u8d70\u8def\u7684\u59ff\u52bf\u5f88\u5947 \u7279\uff0c\u4f69\u5251\u7684\u6cd5\u5b50\u4e5f\u548c\u522b\u4eba\u4e0d\u540c\uff0c\u53ea\u662f\u968f\u4fbf\u5730\u659c\u63d2\u5728\u8170\u5e26\u4e0a\u3002 \u3000\u3000\u5251\u4e0d\u957f\uff0c\u8fd8\u672a\u51fa\u9798\u3002 \u3000\u3000\u8fd9\u4eba\u770b\u6765\u4e5f\u5e76\u4e0d\u5341\u5206\u51f6\u6076\uff0c\u4f46\u9ed1\u8863\u4eba\u4e00\u77a7\u89c1\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u5168\u8eab\u90fd\u53d1\u8d77\u51b7\u6765\uff0c\u638c\u5fc3\u4e5f \u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u4e0a\u7adf\u4f3c\u5e26\u7740\u79cd\u65e0\u58f0\u7684\u6740\u6c14\u3002 \u3000\u3000\u8346\u65e0\u547d\u3002 \u3000\u3000\u8346\u65e0\u547d\u65e2\u7136\u8fd8\u6d3b\u7740\uff0c\u6b7b\u7684\u81ea\u7136\u662f\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u3002 \u3000\u3000\u4f46\u5979\u53ea\u662f\u7b11\u5fc3\u91cc\uff0c\u9762\u4e0a\u5374\u50cf\u662f\u6015\u5f97\u8981\u547d\u3002\u5c06\u90a3\u84dd\u8863\u5c11\u5e74\u7684\u624b\u63e1\u7740\u66f4\u7d27\uff0c\u98a4\u58f0\u9053\uff1a\u8fd9\u4eba\u597d \u53ef\u6015\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u52c9\u5f3a\u7b11\u9053\uff1a\u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u6709\u6211\u5728\u8fd9\u91cc\uff0c\u4f60\u8fd8\u6015\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u6211\u4e0d\u6015\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u4fdd\u62a4\u6211\u7684\uff0c\u53ea\u8981\u5728\u4f60\u8eab\u65c1\uff0c\u5c31\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u6562 \u6765\u78b0\u6211\u4e00\u6839\u624b\u6307\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u633a\u8d77\u80f8\uff0c\u9053\uff1a\u5bf9\uff0c\u65e0\u8bba\u4ed6\u662f\u8c01\uff0c\u53ea\u8981\u4ed6\u6562\u8fc7\u6765\uff0c\u6211\u5c31\u8981\u4ed6\u7684\u547d\uff01 \u3000\u3000\u5176\u5b9e\u4ed6\u5df2\u4e5f\u88ab\u8346\u65e0\u547d\u7684\u6740\u6c14\u6240\u6151\uff0c\u624b\u5fc3\u91cc\u5df2\u5728\u5192\u7740\u51b7\u6c57\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u8fd8\u5e74\u8f7b\uff0c\u5728\u81ea\u5df1\u5fc3\u7231 \u7684\u5973\u4eba\u9762\u524d\uff0c\u6b7b\u4e5f\u4e0d\u80af\u793a\u5f31\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u5df2\u8d70\u5230\u90a3\u9ed1\u8863\u4eba\u9762\u524d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u624b\u91cc\u867d\u63e1\u7740\u67c4\u6bd4\u9996\uff0c\u4ed6\u7528\u8fd9\u67c4\u6bd4\u9996\u5df2\u4e0d\u77e5\u6740\u8fc7\u591a\u5c11\u4eba\uff0c\u4f46\u6b64\u523b\u4e5f\u4e0d\u77e5\u600e\u5730\uff0c\u786c\u662f \u4e0d\u6562\u5c06\u8fd9\u67c4\u6bd4\u9996\u523a\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u5230\u4e86\u8346\u65e0\u547d\u90a3\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\u3002 \u3000\u3000\u8346\u65e0\u547d\u5374\u4f3c\u4e4e\u6839\u672c\u8fde\u77a7\u90fd\u6ca1\u6709\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u4f60\u624b\u91cc\u8fd9\u628a\u5200\u80fd\u6740\u5f97\u6b7b\u4eba\u4e48\uff1f \u3000\u3000\u9ed1\u8863\u4eba\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u53e5\u987a\u5f97\u5b9e\u5728\u6709\u70b9\u4ee4\u4eba\u54ed\u7b11\u4e0d\u5f97\uff0c\u4f46\u522b\u4eba\u65e2\u5df2\u95ee\u4e86\u51fa\u6765\uff0c\u4ed6\u4e5f\u6ca1\u6cd5\u5b50\u4e0d\u56de\u7b54\uff0c\u53ea\u6709\u786c\u7740 \u5934\u76ae\u9053\uff1a\u81ea\u7136\u80fd\u6740\u5f97\u6b7b\u4eba\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u597d\uff0c\u6765\u6740\u6211\u5427\u3002\u9ed1\u8863\u4eba\u6014\u4f4f\u4e86\uff0c\u534a\u664c\u9053\uff1a\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u4f55\u8981\u6740\u4f60\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u56e0\u4e3a\u4f60\u4e0d\u6740\u6211\uff0c\u6211\u4e5f\u8981\u6740\u4f60\u3002\u9ed1\u8863\u4eba\u4e0d\u7531\u81ea\u4e3b\u540e\u9000\u4e24\u6b65\uff0c\u7a81\u7136\u54ac\u4e86\u54ac\u7259\uff0c\u6bd4 \u9996\u5df2\u95ea\u7535\u822c\u523a\u51fa\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u6bd4\u9996\u521a\u523a\u51fa\uff0c\u5251\u5149\u5df2\u98de\u8d77\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5c31\u662f\u4e00\u58f0\u60e8\u547c\uff0c\u518d\u770b\u8346\u65e0\u547d\u7684\u5251\u5df2\u53c8\u56de\u5230\u9798\u4e2d\uff0c\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u62d4\u51fa\u6765\u8fc7\u3002 \u3000\u3000\u597d\u5feb\u7684\u5251\uff01 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u4e5f\u662f\u4f7f\u5251\u7684\u540d\u5bb6\uff0c\u81ea\u5df1\u4e00\u5411\u89c9\u5f97\u5251\u6cd5\u5df2\u591f\u5feb\u4e86\uff0c\u4ece\u4e25\u4e5f\u4e0d\u4fe1\u4e16\u4e0a\u8fd8\u6709\u4eba\u7684\u5251\u6cd5 \u80fd\u6bd4\u4ed6\u66f4\u5feb\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u4ed6\u624d\u76f8\u4fe1\u3002 \u3000\u3000\u6797\u4ed9\u513f\u770b\u4ed6\u7684\u80af\u89d2\u7684\u808c\u8089\u5728\u4e0d\u505c\u5730\u8df3\u52a8\uff0c\u5ffd\u7136\u653e\u5f00\u4e86\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u8fd9\u4eba\u7684\u51fa\u624b\u592a\u5feb\uff0c\u4f60 \u2014\u2014\u4f60\u8fd8\u662f\u5feb\u9003\u8d70\u5427\uff0c\u7528\u4e0d\u7740\u7ba1\u6211\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u82e5\u5df2\u6709\u56db\u4e94\u5341\u5c81\uff0c\u5c31\u4e00\u5b9a\u4f1a\u542c\u8bdd\u5f97\u79c0\u5f88\uff0c\u4e00\u4e2a\u4eba\u6d3b\u5230\u56db\u4e94\u5341\u5c81\u65f6\uff0c\u5c31\u4f1a\u61c2\u5f97\u6027 \u547d\u6bd5\u7adf\u8981\u6bd4\u9762\u5b50\u53ef\u8d35\u5f97\u591a\uff0c\u82e5\u6709\u4eba\u8bf4\uff1a\u751f\u547d\u56fa\u53ef\u8d35\uff0c\u7231\u60c5\u4ef7\u66f4\u9ad8\uff0c\u8fd9\u5b9a\u662f\u5e74\u8f7b\u5c0f\u4f19\u5b50\u8bf4\u51fa\u6765 \u7684\u3002 \u3000\u3000\u8bf4\u8fd9\u8bdd\u7684\u4eba\u4e00\u5b9a\u6d3b\u4e0d\u5230\u4e94\u5341\u5c81\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u54ac\u7740\u7259\uff0c\u55c4\u58f0\u9053\uff1a\u4f60\u7528\u4e0d\u7740\u5bb3\u6015\uff0c\u6211\u8ddf\u4ed6\u62fc\u4e86\uff01 \u3000\u3000\u4ed6\u53e3\u6c14\u8fd8\u4e0d\u5341\u5206\u575a\u51b3\uff0c\u4e5f\u6ca1\u6709\u51b2\u8fc7\u53bb\u7684\u610f\u601d\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e0d\u2014\u2014\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u8fd8\u6709\u7236\u6bcd\u59bb\u5b50\uff0c\u8fd8\u662f\u8d76\u5feb\u9003\u56de\u53bb\u5427\uff0c\u6211\u66ff\u4f60\u6321\u7740\u4ed6\uff0c\u53cd \u6b63\u6211\u53ea\u662f\u5b64\u4f36\u7684\u4e00\u4e2a\u4eba\uff0c\u6b7b\u4e5f\u4e5f\u6ca1\u5173\u7cfb\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u82e5\u8981\u7537\u4eba\u4e3a\u5979\u62fc\u547d\uff0c\u6700\u597d\u7684\u6cd5\u5b50\u5c31\u662f\u5148\u8ba9\u4ed6\u77e5\u9053\u5979\u662f\u7231\u4ed6\u7684\uff0c\u800c\u4e14\u4e5f\u4e0d\u60dc\u4e3a\u4ed6 \u6b7b\u3002 \u3000\u3000\u8fd9\u6cd5\u5b50\u6797\u4ed9\u513f\u4e5f\u4e0d\u77e5\u7528\u8fc7\u591a\u5c11\u6b21\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u5931\u8d25\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u4e0d\u4f46\u5fc3\u91cc\u5728\u7b11\uff0c\u8138\u4e0a\u4e5f\u5728\u7b11\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u8fd9\u84dd\u8863\u5c11\u5e74\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u518d\u770b\u5230\u4e86\u3002 \u3000\u3000\u8fd9\u84dd\u8863\u5c11\u5e74\u4e0d\u4f46\u5251\u6cd5\u9887\u9ad8\uff0c\u7528\u7684\u4e5f\u662f\u628a\u597d\u5251\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u4ed6\u5df2\u5411\u8346\u65e0\u547d\u523a\u51fa\u4e86\u4e94\u5251\uff0c\u5374\u8fde\u4e00\u53e5\u90fd\u6ca1\u8bf4\uff0c\u4ed6\u65e9\u5df2\u770b\u51fa\u65e0\u8bba\u8bf4\u4ec0\u4e48\u4e5f\u6ca1\u6709\u7528 \u3002 \u3000\u3000\u8346\u65e0\u547d\u5c45\u7136\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u8fd9\u4e94\u5251\u660e\u660e\u90fd\u662f\u5411\u4ed6\u8981\u5bb3\u4e4b\u5904\u523a\u8fc7\u53bb\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u5168\u90fd\u523a\u4e86\u4e2a\u7a7a\u3002 \u3000\u3000\u8346\u65e0\u547d\u5ffd\u7136\u9053\uff1a\u4f60\u662f\u70b9\u82cd\u95e8\u4e0b\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u7684\u624b\u505c\u4f4f\u4e86\uff0c\u7b2c\u516d\u5251\u518d\u4e5f\u523a\u4e0d\u51fa\u53bb\uff0c\u8fd9\u4eba\u4e00\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\u4eff\u4f5b\u6839\u672c\u5c31\u6ca1\u6709\u770b \u4ed6\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u61c2\u8fd9\u4eba\u600e\u4f1a\u770b\u51fa\u4ed6\u7684\u5e08\u627f\u5251\u6cd5\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u8c22\u5929\u7075\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u9053\uff1a\u662f\u2014\u2014\u662f\u5bb6\u5e08\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u90ed\u5d69\u9633\u5df2\u6b7b\u5728\u6211\u5251\u4e0b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u65e0\u5934\u65e0\u5c3e\u5730\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u597d\u50cf\u524d\u8a00\u4e0d\u5bf9\u540e\u8bed\u3002 \u3000\u3000\u4f46\u8fd9\u84dd\u8863\u5c11\u5e74\u5374\u79c0\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u7b2c\u4e94\u5341\u7ae0\u3000\u6e29\u67d4\u9677\u9631>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u7ae0\u3000\u6e29\u67d4\u9677\u9631 \u3000\u3000\u8c22\u5929\u7075\u4e43\u70b9\u82cd\u638c\u95e8\uff0c\u53f7\u79f0\u5929\u5357\u7b2c\u4e00\u5251\u5ba2\uff0c\u5e73\u751f\u7eb5\u6a2a\u65e0\u654c\uff0c\u5374\u66fe\u5728\u90ed\u5d69\u9633\u624b\u4e0b\u8d25\u8fc7\u4e09\u6b21\uff0c \u800c\u4e14\u8d25\u5f97\u5fc3\u670d\u53e3\u670d\u3002 \u3000\u3000\u5982\u4eca\u8fde\u90ed\u5d69\u9633\u90fd\u5df2\u6b7b\u5728\u4ed6\u5251\u4e0b\uff0c\u8c22\u5929\u7075\u81ea\u7136\u66f4\u4e0d\u662f\u4ed6\u7684\u654c\u624b\uff0c\u8c22\u5929\u7075\u7684\u5f1f\u5b50\u5c31\u66f4\u4e0d\u5fc5\u8bf4 \u4e86\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u53ef\u770b\u51fa\u8346\u65e0\u547d\u7edd\u4e0d\u662f\u4e2a\u8bf4\u5927\u8bdd\u7684\u4eba\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6211\u4e00\u51fa\u624b\u5c31\u53ef\u53d6\u4f60\u6027\u547d\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u54ac\u7740\u7259\uff0c\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u8346\u65e0\u547d\u7684\u5251\u4e0d\u77e5\u4f55\u65f6\u5df2\u51fa\u624b\u3002 \u3000\u3000\u51b0\u51c9\u7684\u5251\u5c16\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u62b5\u4f4f\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u6211\u4e00\u51fa\u624b\u5c31\u53ef\u53d6\u4f60\u6027\u547d\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u6c57\u5982\u96e8\u4e0b\uff0c\u5634\u5507\u5df2\u54ac\u5f97\u51fa\u8840\uff0c\u55c4\u58f0\u9053\uff1a\u4f60\u4e3a\u4f55\u4e0d\u7d22\u6027\u6740\u4e86\u6211\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u4f60\u60f3\u6b7b\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u5927\u58f0\u9053\uff1a\u5927\u4e08\u592b\u6b7b\u6709\u4f55\u60e7\uff1f\u4f60\u53ea\u7ba1\u4e0b\u624b\u5427\uff01 \u3000\u3000\u4ed6\u867d\u7136\u62fc\u547d\u60f3\u88c5\u51fa\u89c6\u6b7b\u5982\u5f52\u7684\u8c6a\u6c14\uff0c\u5374\u88c5\u5f97\u5e76\u4e0d\u592a\u9ad8\u660e\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6211\u82e5\u4e0d\u60f3\u6740\u4f60\uff0c\u4f60\u4e5f\u60f3\u6b7b\u4e48\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u82e5\u662f\u8fd8\u80fd\u597d\u597d\u5730\u6d3b\u7740\uff0c\u6709\u8c01\u4f1a\u771f\u7684\u60f3\u6b7b\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6211\u77e5\u9053\u4f60\u672c\u60f3\u4e3a\u5979\u800c\u6b7b\uff0c\u8981\u5979\u89c9\u5f97\u4f60\u662f\u4e2a\u82f1\u96c4\uff0c\u4f46\u4f60\u82e5\u771f\u7684\u6b7b\u4e86\uff0c\u5979\u8fd8\u4f1a\u559c \u6b22\u4f60\u4e48\uff1f \u3000\u3000\u4ed6\u51b7\u51b7\u9053\uff1a\u5979\u82e5\u6b7b\u4e86\uff0c\u4f60\u8fd8\u4f1a\u4e0d\u4f1a\u559c\u6b22\u5979\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u597d\u51b0\u51b7\u7684\u5251\u950b\u5df2\u79bb\u5f00\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u5c31\u50cf\u662f\u4e2a\u5446\u5b50\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u5728\u5973\u4eba\u773c\u4e2d\uff0c\u4e00\u767e\u4e2a\u6b7b\u4e86\u7684\u82f1\u96c4\uff0c\u6bd4\u4e0d\u4e0a\u4e00\u4e2a\u6d3b\u7740\u7684\u61e6\u592b\uff0c\u8fd9\u6b63\u5982\u5728\u4f60\u773c\u4e2d \uff0c\u4e00\u767e\u4e2a\u6b7b\u4e86\u7684\u7f8e\u4eba\uff0c\u4e5f\u6bd4\u4e0d\u4e0a\u4e00\u4e2a\u6d3b\u7740\u7684\u5973\u4eba\u2026\u2026\u8fd9\u9053\u7406\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u64e6\u4e86\u64e6\u6c57\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u6211\u660e\u767d\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u73b0\u5728\u4f60\u8fd8\u60f3\u6b7b\u4e48\uff1f \u3000\u3000\u84dd\u8863\u5c11\u5e74\u7ea2\u7740\u8138\u9053\uff1a\u6d3b\u7740\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u5f88\u597d\uff0c\u4f60\u603b\u7b97\u60f3\u901a\u4e86\u3002 \u3000\u3000\u4ed6\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u6211\u7d20\u6765\u4e0d\u559c\u591a\u8bdd\uff0c\u4eca\u65e5\u5374\u8bf4\u4e86\u5f88\u591a\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4f60\u60f3\u901a\u8fd9\u9053\u7406\u2026\u2026\u7b49\u4f60 \u60f3\u901a\u8fd9\u9053\u7406\uff0c\u6211\u624d\u597d\u6740\u4e86\u4f60\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u9a87\u7136\u9053\uff1a\u4f60\u8981\u6740\u6211\uff1f \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u6211\u4ece\u6765\u53ea\u53d1\u95ee\uff0c\u4e0d\u56de\u7b54\uff0c\u53ea\u6709\u5bf9\u5feb\u6b7b\u7684\u4eba\u662f\u4f8b\u5916\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u9053\uff1a\u53ef\u662f\u2026\u2026\u53ef\u662f\u4f60\u65e2\u7136\u8981\u6740\u6211\uff0c\u4e3a\u4f55\u53c8\u8981\u8bf4\u90a3\u4e9b\u8bdd\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u56e0\u4e3a\u6211\u4ece\u4e0d\u6740\u81ea\u5df1\u60f3\u6b7b\u7684\u4eba\u2026\u2026\u4f60\u82e5\u672c\u60f3\u6b7b\uff0c\u6211\u6740\u4e86\u4f60\u4e5f\u65e0\u8da3\u5f97\u5f88\u3002 \u3000\u3000\u84dd\u8863\u5c11\u5e74\u72c2\u543c\u4e00\u58f0\uff0c\u4e00\u5251\u523a\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u543c\u4e5f\u5f88\u77ed\u4fc3\uff0c\u56e0\u4e3a\u4ed6\u7684\u624b\u521a\u62ac\u8d77\uff0c\u8346\u65e0\u547d\u7684\u5251\u5df2\u523a\u5165\u4e86\u4ed6\u7684\u5634\uff0c\u90a3\u51b0\u51b7\u7684\u5251\u950b\u5c31\u8d34 \u5728\u4ed6\u820c\u5934\u4e0a\u3002 \u3000\u3000\u662f\u54b8\u7684\u3002 \u3000\u3000\u4ed6\u6bd5\u7adf\u5c1d\u5230\u4e86\u6b7b\u7684\u6ecb\u5473\u3002 \u3000\u3000\u5251\u5df2\u5165\u9798\u3002 \u3000\u3000\u8346\u65e0\u547d\u6709\u4e2a\u5f88\u5947\u7279\u7684\u4e60\u60ef\uff0c\u90a3\u5c31\u662f\u4ed6\u6bcf\u6b21\u6740\u4e86\u4e2a\u4eba\u540e\uff0c\u4e00\u5b9a\u5c06\u5251\u5f88\u5feb\u5730\u63d2\u56de\u5251\u9798\uff0c\u5c31\u597d \u50cf\u4ed6\u5df2\u4e0d\u6253\u7b97\u73b0\u7528\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u522b\u4eba\u770b\u5230\u4ed6\u7684\u5251\u8fd8\u5728\u9798\u4e2d\u65f6\uff0c\u603b\u4f1a\u6bd4\u8f83\u758f\u5ffd\u5927\u610f\u4e9b\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u758f\u5ffd\u5927\u610f\u7684\u4eba\uff0c\u8fd9\u79cd\u4eba\u6b7b\u5f97\u901a\u5e38\u662f\u6bd4\u8f83\u5feb\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e00\u76f4\u5728\u77a7\u7740\u4ed6\uff0c\u4ed4\u7ec6\u89c2\u5bdf\u7740\u4ed6\u6bcf\u4e00\u4e2a\u52a8\u4f5c\uff0c\u5979\u76ee\u4e2d\u4e00\u76f4\u5e26\u7740\u6e29\u67d4\u7684\u7b11\u610f\uff0c\u5c31\u4eff\u4f5b \u521d\u604b\u7684\u5c11\u5973\u5728\u77a7\u7740\u81ea\u5df1\u7684\u60c5\u4eba\u3002 \u3000\u3000\u8346\u65e0\u547d\u59cb\u7ec8\u6ca1\u6709\u5411\u5979\u8fd9\u8fb9\u77a7\u8fc7\u4e00\u773c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5df2\u6446\u51fa\u4e86\u6700\u52a8\u4eba\u7684\u59ff\u52bf\uff0c\u5728\u8fce\u63a5\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u5411\u5979\u77a7\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u867d\u8fd8\u5728\u7b11\u7740\uff0c\u77b3\u5b54\u5374\u5df2\u6536\u7f29\u3002 \u3000\u3000\u5979\u5df2\u53d1\u89c9\u6709\u4e9b\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u548c\u5979\u597d\u8fc7\u7684\u7537\u4eba\u82e5\u518d\u89c1\u7740\u5979\uff0c\u90a3\u53cc\u773c\u775b\u4e00\u5b9a\u4f1a\u50cf\u997f\u732b\u822c\u76ef\u7740\u5979\uff0c\u4f46\u8fd9\u7537\u4eba\u5374\u8fde\u773c\u89d2\u90fd\u672a \u779f\u8fc7\u5979\uff0c\u5c31\u597d\u50cf\u5979\u8eab\u4e0a\u6709\u6bd2\u4e00\u6837\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u8170\u80a2\u626d\u52a8\u7740\uff0c\u90a3\u4e24\u4e2a\u5e74\u8f7b\u7684\u8f7f\u592b\u773c\u775b\u65e9\u5df2\u53d1\u76f4\u4e86\uff0c\u6839\u672c\u672a\u77a7\u89c1\u90a3\u6bd4\u95ea\u7535\u8fd8\u5feb\u7684 \u5251\u5149\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u60e8\u547c\u521a\u51fa\uff0c\u8346\u65e0\u547d\u7684\u5251\u5df2\u5165\u9798\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5df2\u5230\u4e86\u6797\u4ed9\u513f\u9762\u524d\u3002 \u3000\u3000\u4f46\u4ed6\u90a3\u53cc\u6b7b\u7070\u7684\u773c\u775b\uff0c\u8fd8\u662f\u7a7a\u7a7a\u6d1e\u6d1e\u5730\u51dd\u6ce8\u7740\u8fdc\u65b9\u3002 \u3000\u3000\u8fdc\u65b9\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6562\u770b\u6211\uff1f\u96be\u9053\u6015\u770b\u4e86\u6211\u4e00\u773c\u540e\uff0c\u5c31\u4e0d\u5fcd\u6740\u6211\u4e86\u4e48\uff1f \u3000\u3000\u8346\u65e0\u547d\u5634\u89d2\u7684\u808c\u8089\u76f4\u62bd\u6410\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5389\u58f0\u9053\uff1a\u4f60\u5df2\u77e5\u9053\u6211\u8981\u6765\u6740\u4f60\uff1f \u3000\u3000\u6797\u4ed9\u513f\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u6211\u77e5\u9053\u2026\u2026\u4e00\u4e2a\u4eba\u65e0\u8bba\u591a\u51b7\u9177\uff0c\u591a\u65e0\u60c5\uff0c\u4f46\u8981\u6740\u4ed6\u81ea\u5df1\u6240\u7231\u7684\u4eba \u65f6\uff0c\u795e\u8272\u770b\u6765\u603b\u4f1a\u6709\u4e9b\u4e0d\u540c\u7684\u3002 \u3000\u3000\u5979\u51c4\u7136\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u6211\u53ea\u60f3\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u6211\u65e2\u7136\u4e5f\u5feb\u6b7b\u4e86\uff0c\u4f60\u603b\u8be5\u56de\u7b54\u6211\u5427\uff1f \u3000\u3000\u8346\u65e0\u547d\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u51b7\u51b7\u9053\uff1a\u4f60\u95ee\u5427\uff0c\u5bf9\u5c06\u6b7b\u7684\u4eba\uff0c\u6211\u4ece\u4e0d\u8bf4\u8c0e\u3002 \u3000\u3000\u6797\u4ed9\u513f\u51dd\u6ce8\u7740\u4ed6\u7684\u8138\uff0c\u4e00\u5b57\u5b57\uff1a\u6211\u53ea\u95ee\u4f60\uff0c\u662f\u8c01\u8981\u4f60\u6765\u6740\u6b7b\u6211\u7684\uff1f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u8346\u65e0\u547d\u7684\u624b\u7d27\u63e1\uff0c\u5389\u58f0\u9053\uff1a\u6ca1\u6709\u522b\u4eba\uff0c\u4e5f\u6ca1\u6709\u7406\u7531\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4e00\u5b9a\u6709\u522b\u4eba\u2026\u2026\u8981\u6740\u6211\u7684\u4eba\uff0c\u4e00\u5b9a\u4e0d\u662f\u4f60\u81ea\u5df1\u3002\u5979\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u66f4\u51c4\u51c9\uff0c\u7136 \u540e\u624d\u5e7d\u5e7d\u5730\u63a5\u7740\u9053\uff1a\u6211\u77e5\u9053\u4f60\u7231\u6211\uff0c\u7edd\u4e0d\u5fcd\u6740\u6211\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u51e0\u4e4e\u5df2\u53ef\u542c\u5230\u4ed6\u7684\u9aa8\u8282\u5728\u54cd\u3002 \u3000\u3000\u4f46\u4ed6\u9762\u4e0a\u8fd8\u662f\u6beb\u65e0\u8868\u60c5\uff0c\u53cd\u800c\u51b7\u7b11\u9053\uff1a\u4f60\u771f\u7684\u77e5\u9053\uff1f\u4f60\u6709\u628a\u63e1\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u6709\u628a\u63e1\uff0c\u4f60\u82e5\u4e0d\u7231\u6211\uff0c\u5c31\u4e0d\u4f1a\u6740\u6b7b\u8fd9\u4e9b\u4eba\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u5c45\u7136\u6ca1\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u53cd\u800c\u5728\u7b49\u7740\u5979\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u6740\u4ed6\u4eec\uff0c\u53ea\u56e0\u4f60\u5728\u5ac9\u5992\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u5ac9\u5992\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53ea\u8981\u78b0\u8fc7\u6211\u7684\u4eba\uff0c\u751a\u81f3\u770b\u8fc7\u6211\u7684\u4eba\uff0c\u4f60\u5c31\u60f3\u8981\u4ed6\u4eec\u7684\u547d\uff0c\u8fd9\u5c31\u662f\u5ac9\u5992\uff0c\u5c31\u662f \u5403\u918b\uff0c\u4f60\u82e5\u4e0d\u7231\u6211\uff0c\u600e\u4f1a\u5403\u918b\uff1f \u3000\u3000\u8346\u65e0\u547d\u7684\u8138\u8272\u53d1\u767d\uff0c\u51b7\u51b7\u9053\uff1a\u6211\u53ea\u77e5\u9053\u6211\u8981\u6740\u4f60\uff0c\u6211\u8981\u6740\u7684\u4eba\uff0c\u5c31\u518d\u4e5f\u4f11\u60f3\u6d3b\u4e0b\u53bb\uff01 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4f60\u82e5\u771f\u8981\u6740\u6211\uff1f\u4e3a\u4ec0\u4e48\u8fde\u770b\u90fd\u4e0d\u770b\u6211\uff1f\u4f60\u4e0d\u6562\uff1f \u3000\u3000\u8346\u65e0\u547d\u7684\u624b\u7d27\u7d27\u63e1\u7740\u5251\u67c4\uff0c\u751a\u81f3\u5728\u8fd9\u79cd\u9eef\u6de1\u7684\u706f\u5149\u4e0b\uff0c\u4e5f\u53ef\u770b\u51fa\u4ed6\u8138\u4e0a\u6b63\u5728\u4e00\u7c92\u7c92\u5730\u5192 \u7740\u6c57\u3002 \u3000\u3000\u51b7\u6c57\u3002 \u3000\u3000\u6797\u4ed9\u513f\u76ef\u7740\u4ed6\u7684\u8138\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u82e5\u8fde\u770b\u90fd\u4e0d\u6562\u770b\u6211\uff0c\u5c31\u7b97\u6740\u4e86\u6211\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u540e\u6094\u7684\u3002 \u3000\u3000\u5979\u8bd5\u63a2\u7740\uff0c\u6162\u6162\u5730\u4f38\u51fa\u4e86\u624b\u3002 \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u624b\u7ec8\u4e8e\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u7136\u540e\u5979\u7684\u4eba\u4e5f\u504e\u5165\u4e86\u4ed6\u7684\u6000\u91cc\uff0c\u5979\u7684\u624b\u4e5f\u4ece\u4ed6\u624b\u81c2\u6ed1\u4e0a \u4ed6\u7684\u80f8\u819b\u67d4\u58f0\u9053\uff1a\u4f60\u81ea\u5df1\u82e5\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u5c31\u5e26\u6211\u53bb\u89c1\u4ed6\u5427\u3002 \u3000\u3000\u5979\u7684\u624b\u6307\u52a8\u5f97\u5f88\u7075\u5de7\uff0c\u800c\u4e14\u603b\u77e5\u9053\u5e94\u8be5\u5728\u4ec0\u4e48\u5730\u65b9\u505c\u4f4f\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u547c\u5438\u548c\u808c\u8089\u90fd\u5df2\u7d27\u5f20\uff0c\u55c4\u58f0\u9053\uff1a\u4f60\u2026\u2026\u4f60\u8981\u89c1\u8c01\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u53bb\u89c1\u90a3\u8981\u4f60\u6765\u6740\u6211\u7684\u4eba\uff0c\u6211\u4e00\u5b9a\u53ef\u4ee5\u8ba9\u4ed6\u6539\u53d8\u4e3b\u610f\u2026\u2026 \u3000\u3000\u5979\u54ac\u7740\u4ed6\u7684\u8033\u6735\u8f7b\u8f7b\u5730\u63a5\u7740\u9053\uff1a\u4f60\u653e\u5fc3\uff0c\u6211\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u540e\u6094\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u6ca1\u6709\u770b\u5979\uff0c\u5374\u7f13\u7f13\u8f6c\u8fc7\u5934\uff0c\u671b\u7740\u90a3\u9edd\u9ed1\u7684\u6811\u6797\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u6084\u6084\u9053\uff1a\u4ed6\u5c31\u5728\u90a3\u6811\u6797\u91cc\uff1f \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u56de\u7b54\uff0c\u5df2\u7528\u4e0d\u7740\u56de\u7b54\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u597d\uff0c\u6211\u53bb\u89c1\u4ed6\uff0c\u4ed6\u82e5\u4e00\u5b9a\u4e0d\u80af\u653e\u8fc7\u6211\uff0c\u4f60\u518d\u6740\u6211\u8fd8\u6765\u5f97\u53ca\u3002 \u3000\u3000\u8346\u65e0\u547d\u7b49\u5979\u8f6c\u8fc7\u8eab\uff0c\u76ee\u5149\u624d\u7ec8\u4e8e\u6295\u6ce8\u5728\u5979\u7684\u80cc\u5f71\u4e0a\uff0c\u4ed6\u90a3\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\u91cc\uff0c\u7b2c\u4e00\u6b21\u6709 \u4e86\u611f\u60c5\u3002 \u3000\u3000\u662f\u4ec0\u4e48\u611f\u60c5\u5462\uff1f\u662f\u6b22\u6109\uff1f\u662f\u60b2\u4f24\uff1f\u662f\u6094\u6068\uff1f \u3000\u3000\u8fd9\u8fde\u4ed6\u81ea\u5df1\u4e5f\u5206\u4e0d\u6e05\u3002 \u3000\u3000\u9edd\u9ed1\u7684\u6811\u6797\u91cc\uff0c\u770b\u4e0d\u5230\u4e00\u70b9\u5149\u3002 \u3000\u3000\u6797\u4ed9\u513f\u867d\u7136\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u8fd8\u662f\u51e0\u4e4e\u649e\u5728\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u7ad9\u5728\u90a3\u91cc\uff0c\u5c31\u50cf\u662f\u4e00\u5ea7\u5c71\uff0c\u51b0\u5c71\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u7684\u8eab\u6750\u4e5f\u4e0d\u7b97\u5341\u5206\u9ad8\u5927\uff0c\u4f46\u770b\u8d77\u6765\u5374\u4ee4\u4eba\u89c9\u5f97\u9ad8\u4e0d\u53ef\u6500\u3002 \u3000\u3000\u6797\u4ed9\u513f\u672c\u6765\u5f53\u7136\u53ef\u4ee5\u907f\u5f00\u7684\uff0c\u4f46\u5979\u5e76\u6ca1\u6709\u8fd9\u4e48\u6837\u505a\uff0c\u6574\u4e2a\u4eba\u5df2\u5012\u5165\u4e86\u8fd9\u4eba\u7684\u6000\u91cc\u3002 \u3000\u3000\u8fd9\u4eba\u5c45\u7136\u6ca1\u6709\u4f38\u624b\u53bb\u6276\u5979\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5598\u606f\u7740\uff0c\u81ea\u5df1\u7ad9\u7a33\u4e86\uff0c\u5598\u606f\u7740\u9053\uff1a\u8fd9\u91cc\u771f\u9ed1\u2014\u2014\uff0d\u771f\u5bf9\u4e0d\u8d77\u3002 \u3000\u3000\u5979\u7ad9\u5f97\u548c\u8fd9\u4eba\u8ddd\u79bb\u8fd8\u4e0d\u5230\u4e00\u5c3a\uff0c\u5979\u76f8\u4fe1\u8fd9\u4eba\u4e00\u5b9a\u53ef\u4ee5\u55c5\u5f97\u5230\u5979\u7684\u547c\u5438\uff0c\u5979\u76f8\u4fe1\u5979\u7684\u547c\u5438 \u4e00\u5b9a\u53ef\u4ee4\u7537\u4eba\u5fc3\u52a8\u3002 \u3000\u3000\u8fd9\u4eba\u5374\u53ea\u662f\u7f13\u7f13\u9053\uff1a\u4f60\u80fd\u4ee4\u8346\u65e0\u547d\u4e0d\u6740\u4f60\uff0c\u7528\u7684\u5c31\u662f\u8fd9\u79cd\u6cd5\u5b50\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8981\u4ed6\u6740\u6211\u7684\u4eba\u5c31\u662f\u4f60\uff1f\u4f60\u5c31\u662f\u4e0a\u5b98\u5e2e\u4e3b\uff1f \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4f60\u8fd9\u79cd\u6cd5\u5b50\uff0c\u5bf9\u6211\u662f\u6ca1\u6709\u7528\u7684\u3002 \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u65e2\u4e0d\u51b7\u9177\uff0c\u4e5f\u4e0d\u9669\u68ee\uff0c\u53ea\u662f\u5e73\u5e73\u6de1\u6de1\u7684\uff0c\u7edd\u4e0d\u5e26\u4e1d\u6beb\u611f\u60c5\uff0c\u65e0\u8bba\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u90fd \u597d\u50cf\u662f\u5728\u5ff5\u4e66\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u90a3\u4e48\uff0c\u6211\u8981\u7528\u4ec0\u4e48\u6cd5\u5b50\uff0c\u624d\u80fd\u6253\u52a8\u4f60\u5462\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4f60\u6709\u4ec0\u4e48\u6cd5\u5b50\uff0c\u4e0d\u59a8\u90fd\u7528\u51fa\u6765\u8bd5\u8bd5\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u5f88\u5bb9\u6613\u5c31\u88ab\u5973\u4eba\u6253\u52a8\u7684\uff0c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u8981\u8346\u65e0\u547d\u6740\u6211\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u968f\u65f6\u8981\u6740\u7684\u4eba\uff0c\u5c31\u4e0d\u80fd\u6709\u611f\u60c5\uff0c\u8981\u8bad\u7ec3\u51fa\u4e00\u4e2a\u5168\u65e0\u611f\u60c5\u7684\u4eba\u5e76\u4e0d\u5bb9\u6613\uff0c\u6211 \u4e0d\u80fd\u770b\u7740\u4ed6\u6bc1\u5728\u4f60\u624b\u4e0a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\uff0c\u9053\uff1b\u4f46\u4f60\u82e5\u8981\u4ed6\u6740\u4e86\u6211\uff0c\u4f60\u7684\u635f\u5931\u5c31\u66f4\u5927\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u81ea\u7136\u6bd4\u8346\u65e0\u547d\u6709\u7528\u5f97\u591a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u54e6\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u8346\u65e0\u547d\u53ea\u4f1a\u6740\u4eba\uff0c\u6211\u4e5f\u4f1a\u6740\u4eba\uff0c\u4ed6\u6740\u4eba\u8fd8\u8981\u7528\u5251\uff0c\u8fd8\u8981\u6d41\u8840\uff0c\u8fd9\u5df2\u7ecf\u843d\u4e86\u4e0b \u4e58\uff0c\u6740\u4eba\u975e\u4f46\u770b\u4e0d\u89c1\u8840\uff0c\u4e5f\u7528\u4e0d\u7740\u5200\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4ed6\u6740\u4eba\u81f3\u5c11\u6bd4\u4f60\u5feb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5feb\u56fa\u7136\u4e0d\u9519\uff0c\u4f46\u6162\u4e5f\u6709\u6162\u7684\u597d\u5904\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u4f60\u9664\u4e86\u4f1a\u6740\u4eba\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u5904\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u5f88\u6709\u94b1\uff0c\u6211\u7684\u94b1\u5df2\u591a\u5f97\u8fde\u6570\u90fd\u6570\u4e0d\u6e05\uff0c\u591a\u5f97\u53ef\u4ee5\u8981\u4eba\u53d1\u75af\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u8fd9\u597d\u5904\u7684\u786e\u4e0d\u5c0f\u3002 \u3000\u3000\u4ed6\u58f0\u97f3\u91cc\u4f3c\u5df2\u6709\u4e86\u7b11\u610f\uff0c\u56e0\u4e3a\u4ed6\u5f88\u4e86\u89e3\u94b1\u7684\u7528\u5904\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u5f53\u7136\u4e5f\u5f88\u806a\u660e\uff0c\u53ef\u4ee5\u5e2e\u4f60\u505a\u5f88\u591a\u4e8b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u4e00\u5b9a\u5f88\u806a\u660e\uff0c\u7b28\u4eba\u662f\u7edd\u4e0d\u4f1a\u6709\u94b1\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u5f53\u7136\u8fd8\u6709\u522b\u7684\u597d\u5904\u2026\u2026 \u3000\u3000\u5979\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u5f88\u4f4e\uff0c\u5f88\u5a9a\uff0c\u7b11\u9053\uff1a\u53ea\u8981\u4f60\u662f\u7537\u4eba\uff0c\u5f88\u5feb\u5c31\u4f1a\u77e5\u9053\u6211\u8bf4\u7684\u4e0d\u5047\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u6211\u662f\u7537\u4eba\u3002 \u3000\u3000\u6811\u6797\u91cc\uff0c\u5df2\u5f00\u59cb\u6709\u96fe\u3002 \u3000\u3000\u8346\u65e0\u547d\u5168\u8eab\u5df2\u88ab\u96fe\u6c34\u6e7f\u900f\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u5c31\u50cf\u662f\u5df2\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u96fe\u5f88\u6d53\uff0c\u4ec0\u4e48\u90fd\u77a7\u4e0d\u89c1\u3002 \u3000\u3000\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u662f\u547b\u541f\uff1f\u8fd8\u662f\u5598\u606f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u5929\u5df2\u5feb\u4eae\u4e86\uff0c\u6211\u8fd8\u662f\u8981\u53bb\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u6709\u4eba\u5728\u7b49\u6211\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u8c01\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u963f\u98de\uff0c\u4f60\u5f53\u7136\u542c\u8bf4\u8fc7\u4ed6\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u6211\u53ea\u5947\u602a\u4f60\u4e3a\u4f55\u8fd8\u6ca1\u6709\u6740\u4e86\u4ed6\uff0c\u4f60\u6740\u4eba\u7684\u786e\u592a\u6162\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u6211\u4e0d\u80fd\u6740\u4ed6\uff0c\u4e5f\u4e0d\u6562\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u6211\u82e5\u6740\u4e86\u4ed6\uff0c\u674e\u5bfb\u6b22\u5c31\u4e00\u5b9a\u4f1a\u6740\u6b7b\u6211\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u77e5\u9053\u4f60\u4e5f\u6ca1\u6709\u6740\u6b7b\u674e\u5bfb\u6b22\uff0c\u5426\u5219\u4e5f\u5c31\u4e0d\u4f1a\u8981\u8346\u65e0\u547d\u6765\u6740\u6211\u4e86\uff0c \u4f60\u5c31\u662f\u8981\u8346\u65e0\u547d\u53bb\u5bf9\u4ed8\u674e\u5bfb\u6b22\uff0c\u6240\u4ee5\u624d\u6015\u4ed6\u53d8\u5f97\u8f6f\u5f31\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u5f88\u4e45\u9053\uff1a\u4f60\u5f88\u6015\u674e\u5bfb\u6b22\uff1f \u3000\u3000\u6797\u4ed9\u513f\u53f9\u9053\uff1a\u7b80\u76f4\u6015\u5f97\u8981\u547d\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4ed6\u6bd4\u6211\u5982\u4f55\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4ed6\u6bd4\u4f60\u8fd8\u53ef\u6015\uff0c\u56e0\u4e3a\u6211\u53ef\u4ee5\u6253\u52a8\u4f60\uff0c\u5374\u7edd\u5bf9\u65e0\u6cd5\u6253\u52a8\u4ed6\u3002 \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4ed6\u8fd9\u4eba\u4ec0\u4e48\u90fd\u4e0d\u8981\uff0c\u8fd9\u5c31\u662f\u4ed6\u6700\u53ef\u6015\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4ed6\u4e5f\u662f\u4eba\uff0c\u4ed6\u60f3\u5fc5\u4e5f\u6709\u5f31\u70b9\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u4ed6\u552f\u4e00\u7684\u5f31\u70b9\u5c31\u662f\u6797\u8bd7\u97f3\uff0c\u4f46\u6211\u5374\u4e0d\u6562\u7528\u6797\u8bd7\u97f3\u53bb\u8981\u631f\u4ed6\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u56e0\u4e3a\u6211\u6ca1\u6709\u628a\u63e1\uff0c\u53ea\u8981\u4ed6\u7684\u5200\u5728\u624b\uff0c\u6211\u65e0\u8bba\u505a\u4ec0\u4e48\u90fd\u6ca1\u6709\u628a\u63e1\u3002 \u3000\u3000\u5979\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff1a\u6240\u4ee5\u53ea\u8981\u4ed6\u6d3b\u7740\uff0c\u6211\u5c31\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u4f60\u653e\u5fc3\uff0c\u4ed6\u6d3b\u4e0d\u957f\u7684\u3002 \u7b2c\u4e94\u5341\u4e00\u7ae0\u3000\u5947\u5cf0\u8fed\u8d77>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u4e00\u7ae0\u3000\u5947\u5cf0\u8fed\u8d77 \u3000\u3000\u96fe\u6de1\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u90a3\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\uff0c\u6b63\u832b\u7136\u671b\u7740\u4e00\u6ef4\u9732\u6c34\u81ea\u4ed6\u7684\u7b20\u5e3d \u8fb9\u7f18\u6ef4\u843d\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u6ca1\u6709\u770b\u5230\u4e0a\u5b98\u91d1\u8679\u4e00\u4e2a\u4eba\u8d70\u51fa\u4e86\u6811\u6797\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e5f\u6ca1\u6709\u77a7\u4e00\u773c\uff0c\u4e0d\u5feb\u4e0d\u6162\u5730\u4ece\u4ed6\u9762\u524d\u8d70\u8fc7\uff0c\u6de1\u6de1\u9053\uff1a\u4eca\u5929\u6709\u96fe\uff0c\u4e00\u5b9a\u662f\u597d\u5929\u6c14 \u3002 \u3000\u3000\u8346\u65e0\u547d\u9ed8\u7136\u534a\u664c\uff0c\u9053\uff1a\u4eca\u5929\u6709\u96fe\u4e00\u5b9a\u662f\u597d\u5929\u6c14\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u8f6c\u8fc7\u8eab\uff0c\u4e0d\u5feb\u4e0d\u6162\u5730\u8ddf\u5728\u4e0a\u5b98\u91d1\u8679\u8eab\u540e\uff0c\u4e24\u4eba\u4e00\u524d\u4e00\u540e\uff0c\u7ec8\u4e8e\u90fd\u6d88\u5931\u5728\u6de1\u6de1\u7684\u6668 \u96fe\u4e2d\u3002 \u3000\u3000\u8fd9\u6761\u8857\u95f9\u5f97\u5f88\uff0c\u51e0\u4e4e\u5c31\u548c\u5317\u5e73\u7684\u5929\u6865\u4e00\u6837\uff0c\u4ec0\u4e48\u6837\u7684\u73a9\u610f\u4e70\u5356\u90fd\u6709\uff0c\u73b0\u5728\u867d\u7136\u6ca1\u5230\u6b63\u5348 \uff0c\u4f46\u8857\u9053\u4e24\u65c1\u5df2\u6446\u8d77\u5404\u5f0f\u5404\u6837\u7684\u644a\u5b50\uff0c\u5356\u5404\u5f0f\u5404\u6837\u7684\u96f6\u98df\uff0c\u800d\u5404\u5f0f\u5404\u6837\u7684\u628a\u620f\uff0c\u7b49\u5f85\u7740\u5404\u5f0f \u5404\u6837\u7684\u4e3b\u987e\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u91cc\uff0c\u94c3\u94c3\u7684\u773c\u775b\u90fd\u82b1\u4e86\uff0c\u7b80\u76f4\u4ece\u6765\u4e5f\u6ca1\u8fd9\u4e48\u5f00\u5fc3\u3002 \u3000\u3000\u5979\u6bd5\u7adf\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f1a\u5e26\u5979\u5230\u8fd9\u91cc\u6765\u901b\u8857\uff0c\u5979\u5b9e\u5728\u6ca1\u60f3\u5230\u3002 \u3000\u3000\u539f\u6765\u4ed6\u6709\u4e9b\u5b69\u5b50\u6c14\u3002 \u3000\u3000\u770b\u5230\u674e\u5bfb\u6b22\u624b\u91cc\u8fd8\u62ff\u7740\u4e32\u7cd6\u846b\u82a6\uff0c\u94c3\u94c3\u5c31\u5fcd\u4e0d\u4f4f\u60f3\u7b11\u3002 \u3000\u3000\u7cd6\u846b\u82a6\u662f\u521a\u4e70\u6765\u7684\uff0c\u4e70\u4e86\u597d\u51e0\u4e32\uff0c\u9c9c\u7ea2\u7684\u5c71\u6942\u4e0a\uff0c\u6d47\u7740\u4eae\u6676\u6676\u7684\u51b0\u7cd6\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u4e32 \u4e32\u53d1\u5149\u7684\u5b9d\u77f3\u3002 \u3000\u3000\u6ca1\u6709\u4e00\u4e2a\u5973\u5b69\u5b50\u4e0d\u7231\u5b9d\u77f3\uff0c\u94c3\u94c3\u5435\u7740\u5c06\u521a\u505a\u597d\u7684\u51e0\u4e32\u5168\u4e70\u4e86\u4e0b\u6765\uff0c\u53ea\u53ef\u60dc\u5979\u53ea\u6709\u4e24\u53ea\u624b \uff0c\u62ff\u4e0d\u4e86\u8fd9\u4e48\u591a\u3002 \u3000\u3000\u5973\u5b69\u5b50\u4e70\u4e1c\u897f\uff0c\u53ea\u4f1a\u5acc\u5c11\uff0c\u4e0d\u4f1a\u5acc\u591a\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u66ff\u5979\u62ff\u7740\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u81ea\u5df1\u4e5f\u4e70\u8fc7\u7cd6\u846b\u82a6\uff0c\u90a3\u81ea\u7136\u5df2\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u90a3\u65f6\u4ed6\u8fd8\u4e0d\u77e5\u9053\u4ec0\u4e48\u53eb\u5fe7 \u6101\uff0c\u4ec0\u4e48\u53eb\u70e6\u607c\u3002 \u3000\u3000\u73b0\u5728\u5462\uff1f \u3000\u3000\u73b0\u5728\u4ed6\u4e5f\u6ca1\u6709\u7a7a\u70e6\u607c\uff0c\u4ed6\u4e00\u76f4\u5728\u76ef\u7740\u4e00\u4e2a\u4eba\uff0c\u5df2\u76ef\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u8fd9\u4eba\u5c31\u8d70\u5728\u4ed6\u524d\u9762\uff0c\u8eab\u4e0a\u80cc\u7740\u4e2a\u7834\u9ebb\u888b\uff0c\u811a\u4e0b\u62d6\u7740\u4e00\u53cc\u70c2\u8349\u978b\uff0c\u5934\u4e0a\u538b\u7740\u9876\u65e7\u6be1\u5e3d\uff0c\u59cb \u7ec8\u4e5f\u6ca1\u6709\u62ac\u8d77\u5934\u6765\uff0c\u5c31\u597d\u50cf\u89c1\u4e0d\u5f97\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u8d70\u8d77\u8def\u6765\u867d\u7136\u5f2f\u8170\u9a7c\u80cc\uff0c\u8fde\u8116\u5b50\u90fd\u7f29\u4e86\u8d77\u6765\uff0c\u4f46\u80a9\u8180\u5374\u5f88\u5bbd\uff0c\u82e5\u662f\u633a\u76f4\u4e86\u8170\uff0c\u60f3\u5fc5\u662f \u6761\u9b41\u4f1f\u7684\u6c49\u5b50\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u4eba\u770b\u6765\u5e76\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u843d\u62d3\u5931\u610f\u7684\u6c5f\u6e56\u5ba2\uff0c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7 \u662f\u4e5e\u4e10\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u4e00\u770b\u5230\u4ed6\uff0c\u5c31\u76ef\u4e0a\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u54ea\u91cc\u674e\u5bfb\u6b22\u5c31\u76ef\u5230\u54ea\u91cc\uff0c\u6240\u4ee5\u624d\u4f1a\u5230\u8fd9\u6761\u8857\u6765\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u76ef\u7740\u4ed6\u7684\uff0c\u5c45\u7136\u8fd8\u4e0d\u6b62\u674e\u5bfb\u6b22\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u672c\u6765\u60f3\u8d76\u8fc7\u53bb\u77a7\u77a7\u4ed6\u7684\u8138\uff0c\u5374\u5ffd\u7136\u53d1\u73b0\u4ed6\u540e\u9762\u6709\u4e2a\u4eba\u4e00\u76f4\u5728\u6697\u6697\u5730\u5c3e\u968f\u4ed6\u3002 \u3000\u3000\u8fd9\u4eba\u5f88\u7626\uff0c\u5f88\u9ad8\uff0c\u811a\u6b65\u5f88\u8f7b\u5065\uff0c\u7a7f\u7684\u867d\u662f\u5957\u5f88\u666e\u901a\u7684\u7c97\u5e03\u8863\u670d\uff0c\u4f46\u76ee\u5149\u95ea\u52a8\u95f4\uff0c\u7cbe\u6c14\u6bd5 \u9732\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u773c\u5c31\u770b\u51fa\u4ed6\u7edd\u4e0d\u662f\u666e\u901a\u4eba\u3002 \u3000\u3000\u4ed6\u5012\u5e76\u6ca1\u6709\u7559\u610f\u674e\u5bfb\u6b22\uff0c\u56e0\u4e3a\u4ed6\u5168\u4ed8\u7cbe\u795e\u90fd\u5df2\u653e\u5728\u524d\u9762\u90a3\u4e5e\u4e10\u8eab\u4e0a\uff0c\u90a3\u4e5e\u4e10\u8d70\u5f97\u5feb\u4e9b\uff0c \u4ed6\u4e5f\u8d70\u5f97\u5feb\u4e9b\uff0c\u90a3\u4e5e\u4e10\u505c\u4e0b\u811a\uff0c\u4ed6\u4e5f\u7acb\u523b\u505c\u4e0b\u811a\uff0c\u88c5\u505a\u5728\u62cd\u8863\u670d\uff0c\u63d0\u978b\u5b50\uff0c\u4e00\u53cc\u773c\u775b\u5374\u59cb\u7ec8 \u672a\u66fe\u653e\u677e\u3002 \u3000\u3000\u4ed6\u770b\u6765\u6b63\u662f\u4e2a\u5c3e\u968f\u76ef\u68a2\u7684\u5927\u884c\u5bb6\u3002 \u3000\u3000\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u76ef\u7740\u4e2a\u7a77\u4e5e\u4e10\u5462\uff1f \u3000\u3000\u4ed6\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4ed6\u548c\u524d\u9762\u90a3\u4e5e\u4e10\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u5374\u4f3c\u5168\u4e0d\u77e5\u9053\u540e\u9762\u6709\u4eba\u5728\u5c3e\u968f\u7740\u4ed6\uff0c\u53ea\u662f\u5f2f\u7740\u8170\uff0c\u9a7c\u7740\u80cc\uff0c\u5728\u524d\u9762\u6162\u6162\u5730\u8d70\u7740\uff0c \u4ece\u6765\u4e5f\u672a\u66fe\u56de\u5934\u3002 \u3000\u3000\u8def\u4e0a\u6709\u4eba\u7ed9\u4ed6\u94b1\uff0c\u4ed6\u5c31\u6536\u4e0b\uff0c\u6ca1\u4eba\u7ed9\u4ed6\u94b1\uff0c\u4ed6\u4e5f\u4e0d\u8ba8\u3002 \u3000\u3000\u94c3\u94c3\u773c\u73e0\u5b50\u4e0d\u505c\u5730\u8f6c\uff0c\u5ffd\u7136\u62c9\u4f4f\u674e\u5bfb\u6b22\u8863\u89d2\uff0c\u6084\u6084\u9053\uff1a\u6211\u4eec\u662f\u76ef\u90a3\u8981\u996d\u7684\u68a2\u4e48\uff1f \u3000\u3000\u8fd9\u5c0f\u59d1\u5a18\u5012\u771f\u662f\u4e2a\u9b3c\u7cbe\u7075\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u597d\u70b9\u4e86\u70b9\u5934\uff0c\u8f7b\u58f0\u9053\uff1a\u6240\u4ee5\u4f60\u8bf4\u8bdd\u4e00\u5b9a\u8981\u5c0f\u58f0\u4e9b\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u4e3a\u4ec0\u4e48\u8981\u76ef\u4ed6\u7684\u68a2\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u4e0d\u61c2\u7684\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u5c31\u56e0\u4e3a\u6211\u4e0d\u61c2\uff0c\u6240\u4ee5\u624d\u8981\u95ee\uff0c\u4f60\u4e0d\u544a\u8bc9\u6211\uff0c\u6211\u5c31\u8981\u5927\u58f0\u95ee\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u56e0\u4e3a\u4ed6\u770b\u6765\u5f88\u50cf\u6211\u4e00\u4e2a\u591a\u5e74\u4e0d\u89c1\u7684\u670b\u53cb\u3002 \u3000\u3000\u94c3\u94c3\u66f4\u5947\u602a\u4e86\uff0c\u9053\uff1a\u4f60\u7684\u670b\u53cb\uff1f\u96be\u9053\u662f\u4e10\u5e2e\u95e8\u4e0b\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u662f\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u4ed6\u662f\u8c01\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u6211\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002 \u3000\u3000\u94c3\u94c3\u6c89\u9ed8\u534a\u664c\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\uff1a\u4eec\u524d\u9762\u4e5f\u6709\u4e2a\u4eba\u5728\u76ef\u7740\u4ed6\uff0c\u4f60\u770b\u51fa\u6765\u6ca1\u6709\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u4f60\u773c\u5149\u5012\u4e0d\u9519\uff1f \u3000\u3000\u94c3\u94c3\u4e5f\u7b11\u4e86\u9053\uff1a\u90a3\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u4ed6\u4e5f\u662f\u4f60\u7684\u670b\u53cb\u5417\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u662f\u3002 \u3000\u3000\u94c3\u94c3\u773c\u73e0\u5b50\u53c8\u5728\u8f6c\uff0c\u9053\uff1a\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\uff1f\u96be\u9053\u662f\u4ed6\u7684\u4ec7\u5bb6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u2014\u2014 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u544a\u8bc9\u4ed6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u90a3\u670b\u53cb\u5f88\u5947\u602a\uff0c\u4ece\u4e0d\u613f\u522b\u4eba\u5e2e\u4ed6\u7684\u5fd9\u3002 \u3000\u3000\u94c3\u94c3\u9053\uff1a\u53ef\u662f\u4ed6\uff1d\uff1d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u4e86\u4e00\u534a\uff0c\u5979\u7684\u5634\u7ec8\u4e8e\u4e5f\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u65f6\u5979\u5df2\u5728\u5fd9\u7740\u7528\u773c\u775b\u53bb\u77a7\uff0c\u5979\u773c\u775b\u5df2\u77a7\u5f97\u53d1\u76f4\u3002 \u3000\u3000\u8fd9\u6761\u8857\u5f88\u957f\uff0c\u4ed6\u4eec\u8d70\u4e86\u5f88\u4e45\uff0c\u624d\u8d70\u4e86\u4e00\u534a\u3002 \u3000\u3000\u90a3\u4e5e\u6b63\u8d70\u5230\u4e00\u4e2a\u5356\u9984\u949d\u7684\u644a\u5b50\u9762\u524d\u3002 \u3000\u3000\u79bb\u9984\u9968\u644a\u4e0d\u8fdc\u5904\uff0c\u6709\u4e2a\u4eba\u6b63\u6311\u7740\u62c5\u5b50\u5728\u5356\u9152\uff0c\u51e0\u4e2a\u4eba\u6b63\u8e72\u5728\u62c5\u5b50\u524d\u559d\u9152\uff0c\u5176\u4e2d\u8fd8\u6709\u4e2a\u5356 \u535c\u7b97\u547d\u7684\u778e\u5b50\uff0c\u8138\u8272\u4f3c\u4e4e\u6709\u4e9b\u53d1\u9752\u3002 \u3000\u3000\u8857\u5bf9\u9762\uff0c\u5c4b\u6a90\u4e0b\uff0c\u7ad9\u7740\u4e2a\u9752\u8863\u5927\u6c49\u3002 \u3000\u3000\u4e00\u4e2a\u5356\u6cb9\u70b8\u81ed\u8c46\u8150\u7684\u6b63\u6311\u7740\u62c5\u5b50\uff0c\u5f80\u8def\u524d\u9762\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53e6\u5916\u8fd8\u6709\u4e2a\u5f88\u9ad8\u5927\u7684\u5987\u4eba\uff0c\u4e00\u76f4\u4f4e\u7740\u5934\u7ad9\u5728\u82b1\u7c89\u644a\u5b50\u524d\u9762\u4e70\u9488\u7ebf\uff0c\u6b64\u523b\u4e00\u62ac\u5934\uff0c\u624d\u770b\u51fa \u5979\u773c\u775b\u5df2\u6536\u5272\u554a\u4e00\u53ea\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u521a\u8d70\u5230\u8fd9\u91cc\u2014\u2014 \u3000\u3000\u5356\u9152\u7684\u5ffd\u7136\u653e\u4e0b\u62c5\u5b50\u3002 \u3000\u3000\u559d\u9152\u7684\u778e\u5b50\u4e5f\u7acb\u523b\u653e\u4e0b\u9152\u7897\u3002 \u3000\u3000\u9752\u8863\u5927\u6c49\u4e00\u6b65\u4ece\u5c4b\u6a90\u4e0b\u7a9c\u51fa\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u4e00\u8f6c\u8eab\uff0c\u51e0\u4e4e\u5c06\u82b1\u7c89\u644a\u5b50\u90fd\u649e\u7ffb\u4e86\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u90a3\u4e00\u76f4\u76ef\u5728\u540e\u9762\u7684\u7626\u957f\u6c5f\u6e56\u5ba2\uff0c\u51e0\u4e2a\u4eba\u7adf\u5ffd\u7136\u5206\u6210\u56db\u9762\u516b\u65b9\u7684\u5411\u90a3\u4e5e\u4e10\u4e4c\u9ed1\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u90a3\u5356\u81ed\u8c46\u8150\u5e72\u7684\u62c5\u5b50\u4e00\u6a2a\uff0c\u6b63\u597d\u6321\u4f4f\u4e86\u90a3\u4e5e\u4e10\u7684\u53bb\u8def\uff01 \u3000\u3000\u8857\u4e0a\u867d\u4e0d\u6b62\u8fd9\u51e0\u4e2a\u4eba\uff0c\u4f46\u8fd9\u51e0\u4eba\u5374\u65e0\u7591\u5206\u5916\u4ee4\u4eba\u89e6\u76ee\u3002\u8fde\u94c3\u94c3\u90fd\u5df2\u770b\u4e86\u4e0d\u5bf9\u4e86\uff0c\u674e\u5bfb\u6b22 \u9762\u4e0a\u66f4\u4e0d\u7981\u5df2\u53d8\u4e86\u989c\u8272\uff0c\u4ed6\u65e9\u5c31\u89c9\u5f97\u8fd9\u4e5e\u4e10\u770b\u6765\u5f88\u50cf\u94c1\u4f20\u7532\uff0c\u73b0\u5728\u66f4\u6beb\u65e0\u7591\u95ee\u3002 \u3000\u3000\u4ed6\u66f4\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u51e0\u4eba\u548c\u94c1\u4f20\u7532\u90fd\u6709\u7740\u4e0d\u53ef\u5316\u89e3\u7684\u6df1\u4ec7\u5927\u6068\uff0c\u8fd9\u6b21\u51fa\u624b\uff0c\u5fc5\u5df2\u8ba1\u5212\u5f97\u6781\u4e3a\u5468 \u5bc6\uff0c\u7edd\u4e0d\u5bb9\u94c1\u4f20\u7532\u518d\u9003\u51fa\u4ed6\u4eec\u7684\u638c\u63e1\uff0c\u82e5\u77e5\u9053\u6709\u4eba\u51fa\u624b\u6551\u4ed6\uff0c\u4e5f\u8bb8\u5730\u4e0d\u987e\u4e00\u5207\uff0c\u5148\u7f6e\u4ed6\u4e8e\u6b7b \u5730\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5b81\u53ef\u81ea\u5df1\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u94c1\u4f20\u7532\u53d7\u5230\u4efb\u4f55\u4f24\u5bb3\uff0c\u4ed6\u751f\u5e73\u53ea\u6b20\u8fc7\u51e0\u4e2a\u4eba\u7684\u60c5\uff0c\u94c1\u4f20\u7532 \u6b63\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u635f\u5931\u94c1\u4f20\u7532\u8fd9\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u51e0\u4e2a\u4eba\u5df2\u5c06\u90a3\u4e5e\u4e10\u6324\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u5bd2\u5149\u95ea\uff0c\u5df2\u6709\u4e09\u67c4\u5229\u5203\u62b5\u4f4f\u4e86\u4ed6\u7684\u524d\u5fc3\u548c\u540e\u80cc\uff0c\u56db\u4e0b\u8fd9\u624d\u53d1\u89c9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u7acb\u523b\u7eb7\u7eb7\u6563 \u5f00\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u613f\u5377\u5165\u8fd9\u79cd\u6c5f\u6e56\u4ec7\u6740\u7684\u4e8b\u4ef6\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5356\u535c\u7684\u778e\u5b50\u51b7\u51b7\u9053\uff1a\u6162\u6162\u5730\u8ddf\u7740\u6211\u4eec\u8d70\uff0c\u4e00\u4e2a\u5b57\u90fd\u4e0d\u8981\u8bf4\uff0c\u660e\u767d\u4e86\u5417\uff1f \u3000\u3000\u90a3\u9752\u8863\u5927\u6c49\u54ac\u7740\u7259\uff0c\u5389\u58f0\u9053\uff1a\u4f60\u8001\u8001\u5b9e\u5b9e\u5730\u542c\u8bdd\uff0c\u8fd8\u53ef\u591a\u6d3b\u4e9b\u65f6\uff0c\u82e5\u662f\u6562\u4e71\u6253\u4e3b\u610f\uff0c\u54b1 \u4eec\u7acb\u523b\u5c31\u8981\u4f60\u7684\u547d\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u53cd\u5e94\u4f3c\u4e4e\u8fdf\u949d\u5df2\u6781\uff0c\u76f4\u5230\u73b0\u5728\u624d\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u7528\u529b\u5728\u4ed6\u80a9\u4e0a\u4e00\u63a8\uff0c\u54ac\u7740\u7259\u9053\uff1a\u5feb\u8d70\uff0c\u8fd8\u7b49\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u4e0d\u63a8\u4e5f\u5c31\u7f62\u4e86\uff0c\u8fd9\u4e00\u63a8\uff0c\u51e0\u4e2a\u4eba\u5168\u90fd\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u5934\u4e0a\u7684\u7834\u6be1\u5e3d\u5df2\u88ab\u63a8\u5f97\u8dcc\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u4e86\u8138\u3002 \u3000\u3000\u9ec4\u6e17\u6e17\u7684\u4e00\u5f20\u8138\uff0c\u4eff\u4f5b\u5927\u75c5\u521d\u6108\uff0c\u4e2d\u95f4\u5374\u6709\u4e2a\u7ea2\u901a\u901a\u7684\u9152\u7cdf\u9f3b\u5b50\uff0c\u6b63\u54a7\u5f00\u5927\u5634\uff0c\u77a7\u7740\u8fd9 \u51e0\u4eba\u563b\u563b\u5730\u50bb\u7b11\u3002 \u3000\u3000\u8fd9\u54ea\u91cc\u662f\u94c1\u4f20\u7532\uff0c\u7b80\u76f4\u6d3b\u8131\u8131\u50cf\u662f\u4e2a\u767d\u75f4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u72ec\u773c\u5987\u4eba\u5df2\u6c14\u5f97\u4eba\u8eab\u90fd\u5728\u53d1\u6296\uff0c\u5389\u58f0\u9053\uff1a\u8001\u4e94\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u7626\u957f\u7684\u6c5f\u6e56\u5ba2\u8138\u8272\u53d1\u7eff\uff0c\u5c31\u50cf\u662f\u89c1\u4e86\u9b3c\u4f3c\u7684\uff0c\u98a4\u58f0\u9053\uff1a\u660e\u660e\u662f\u94c1\u4f20\u7532\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u653e\u5f00 \u8fc7\u4ed6\uff0c\u600e\u4e48\u4f1a\u2014\u2014\u600e\u4e48\u4f1a\u53d8\u2014\u2014\u53d8\u4e86\u3002 \u3000\u3000\u9752\u8863\u5927\u6c49\u6068\u6068\u8dfa\u4e86\u8dfa\u811a\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u6253\u5728\u90a3\u4e5e\u4e10\u7684\u8138\u4e0a\uff0c\u5927\u543c\u9053\uff1a\u4f60\u662f\u8c01\uff1f\u7a76\u7adf\u662f\u8c01\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u624b\u6342\u7740\u8138\uff0c\u8fd8\u662f\u5728\u50bb\u7b11\uff0c\u9053\uff1a\u6211\u662f\u6211\uff0c\u4f60\u662f\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6253\u6211\uff1f \u3000\u3000\u5356\u9152\u7684\u6c49\u5b50\u9053\uff1a\u4e5f\u8bb8\u8fd9\u53ae\u5c31\u662f\u94c1\u4f20\u7532\u6539\u626e\u7684\uff0c\u5148\u5265\u4e0b\u4ed6\u8138\u4e0a\u4e00\u5c42\u76ae\u518d\u8bf4\u3002 \u3000\u3000\u5356\u535c\u7684\u778e\u5b50\u5ffd\u7136\u51b7\u51b7\u9053\uff1a\u7528\u4e0d\uff0c\u8fd9\u4eba\u7edd\u4e0d\u4f1a\u662f\u94c1\u4f20\u7532\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\uff0c\u53ea\u6709\u4ed6\u8138\u4e0a\u8fd8\u662f\u51b7\u51b7\u51b0\u51b0\u7684\u4e0d\u52a8\u58f0\u8272\u3002 \u3000\u3000\u9752\u8863\u5927\u6c49\u9053\uff1a\u4e8c\u54e5\u542c\u5f97\u51fa\u4ed6\u7684\u58f0\u97f3\uff1f\u778e\u5b50\u51b7\u51b7\u9053\uff1a\u94c1\u4f20\u7532\u5b81\u6b7b\u4e5f\u4e0d\u4f1a\u88ab\u4f60\u6253\u4e00\u5df4\u638c\u4e0d\u56de \u624b\u7684\u3002 \u3000\u3000\u7626\u957f\u7684\u6c5f\u6e56\u5ba2\u8138\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u767d\uff0c\u9053\uff1a\u8fd9\u4eba\u4e00\u5b9a\u662f\u548c\u94c1\u4f20\u7532\u4e32\u901a\u597d\u4e86\u7684\uff0c\u6545\u610f\u6389\u4e86\u5305\uff0c \u5c06\u6211\u4eec\u5f15\u5230\u8fd9\u91cc\uff0c\u597d\u8ba9\u90a3\u59d3\u94c1\u7684\u4e58\u673a\u9003\u8d70\u3002 \u3000\u3000\u72ec\u773c\u5987\u4eba\u6012\u9053\uff1a\u4f60\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u600e\u4f1a\u8ba9\u4ed6\u4eec\u6389\u4e86\u5305\u3002 \u3000\u3000\u90a3\u6c5f\u6e56\u5ba2\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u4e5f\u8bb8\u2014\u2014\u4ed6\u4e0a\u5395\u6240\u7684\u65f6\u5019\uff0c\u6211\u603b\u4e0d\u80fd\u2014\u2014 \u3000\u3000\u9752\u8863\u5927\u6c49\u6012\u543c\u9053\uff1a\u539f\u6765\u4f60\u548c\u90a3\u59d3\u94c1\u7684\u662f\u540c\u515a\uff0c\u6211\u5bb0\u4e86\u4f60\u3002 \u3000\u3000\u4ed6\u62a2\u7740\u6839\u6241\u62c5\uff0c\u5c31\u5f80\u90a3\u4e5e\u4e10\u5934\u4e0a\u6253\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u674e\u5bfb\u6b22\u5df2\u4e0d\u80fd\u4e0d\u51fa\u624b\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u8fd9\u4e5e\u4e10\u662f\u4e0d\u662f\u771f\u7684\u75f4\u5446\uff0c\u662f\u4e0d\u662f\u94c1\u4f20\u7532\u7684\u670b\u53cb\uff0c\u4ed6\u603b\u7b97\u5e2e\u4e86\u94c1\u4f20\u7532\u7684\u5fd9\uff0c\u674e\u5bfb\u6b22\u603b \u4e0d\u80fd\u773c\u89c1\u7740\u4ed6\u88ab\u4eba\u6253\u6b7b\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u82e5\u60f3\u77e5\u9053\u94c1\u4f20\u7532\u7684\u6d88\u606f\uff0c\u4e5f\u5f97\u4ece\u8fd9\u4eba\u8eab\u4e0a\u6253\u542c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8eab\u5b50\u5df2\u6ed1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u4e00\u6b65\u521a\u6ed1\u51fa\uff0c\u7a81\u53c8\u7f29\u56de\uff0c\u8fd9\u4e00\u6536\u4e00\u53d1\uff0c\u4e00\u52a8\u4e00\u9759\u5f53\u771f\u662f\u53d8\u5316\u5982\u7535\uff0c\u522b\u4eba\u6839\u672c\u5c31\u672a\u770b \u51fa\u3002 \u3000\u3000\u4ed6\u5df2\u7528\u4e0d\u7740\u51fa\u624b\u3002 \u3000\u3000\u53ea\u542c\u683c\u7684\u4e00\u58f0\uff0c\u90a3\u9752\u8863\u5927\u6c49\u6253\u4e0b\u53bb\u7684\u6241\u62c5\u7a81\u7136\u5e73\u7a7a\u65ad\u6210\u4e86\u4e24\u622a\uff0c\u9752\u8863\u5927\u6c49\u4e00\u4e0b\u5b50\u6253\u5bbd\uff0c \u81ea\u5df1\u8eab\u5b50\u9669\u4e9b\u683d\u5012\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u770b\u6e05\u662f\u4ec0\u4e48\u4e1c\u897f\u5c06\u8fd9\u6839\u6241\u62c5\u6253\u65ad\u7684\uff0c\u6bcf\u4e2a\u4eba\u9762\u4e0a\u90fd\u4e0d\u7981\u53d8\u4e86\u989c\u8272\uff0c\u7eb7\u7eb7\u559d\u9053\uff1a\u662f \u4ec0\u4e48\u4eba\u6562\u591a\u4e8b\u51fa\u624b\uff1f \u3000\u3000\u5c4b\u6a90\u4e0b\u4e00\u4eba\u6de1\u6de1\u9053\uff1a\u662f\u6211\u3002 \u3000\u3000\u5927\u5bb6\u9f50\u968f\u58f0\u671b\u4e86\u8fc7\u53bb\uff0c\u624d\u53d1\u73b0\u8bf4\u8bdd\u7684\u662f\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u767d\u8863\u4eba\uff0c\u6b63\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u4ef0\u9762\u89c2\u8d4f \u7740\u6302\u5728\u5c4b\u6a90\u4e0b\u7684\u4e00\u6392\u9e1f\u7b3c\u3002 \u3000\u3000\u7b3c\u4e2d\u9e1f\u8bed\u5541\u557e\u3002 \u3000\u3000\u8fd9\u767d\u8863\u4eba\u4f3c\u4e4e\u89c9\u5f97\u9e1f\u6bd4\u4eba\u6709\u8da3\u5f97\u591a\uff0c\u8fde\u773c\u89d2\u90fd\u672a\u5411\u8fd9\u8fd9\u4e9b\u5bfb\u4ec7\u7684\u6c5f\u6e56\u5ba2\u4eec\u77a7\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u773c\u89d2\u4e5f\u6709\u4e86\u76b1\u7eb9\uff0c\u4f46\u5251\u7709\u661f\u76ee\uff0c\u9762\u767d\u5982\u7389\uff0c\u8fdc\u8fdc\u770b\u6765\u4ecd\u662f\u4f4d\u7fe9\u7fe9\u6d4a\u4e16\u7684\u4f73\u516c\u5b50\uff0c\u8c01\u4e5f \u731c\u4e0d\u51fa\u4ed6\u7684\u5e74\u7eaa\u3002 \u3000\u3000\u9752\u8863\u5927\u6c49\u5927\u543c\u9053\uff1a\u5c31\u662f\u4f60\u8fd9\u5c0f\u5b50\u6253\u65ad\u4e86\u6211\u7684\u6241\u62c5\uff1f \u3000\u3000\u767d\u8863\u4eba\u8fd9\u6b21\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u9752\u8863\u5927\u6c49\u3001\u72ec\u773c\u5987\u4eba\uff0c\u7eb7\u7eb7\u6012\u559d\u7740\uff0c\u4f3c\u4e4e\u5df2\u60f3\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u7a81\u542c\u90a3\u5356\u535c\u7684\u778e\u5b50\u8f7b\u559d\u9053\uff1a\u505c\u4f4f\u3002 \u3000\u3000\u4ed6\u5df2\u81ea\u5730\u4e0a\u62fe\u8d77\u4e86\u952d\u94f6\u5b50\uff0c\u51b7\u51b7\u9053\uff1a\u8fd9\u4f4d\u516c\u5b50\u867d\u6253\u65ad\u4e86\u4f60\u7684\u6241\u62c5\uff0c\u4f46\u8fd9\u952d\u94f6\u5b50\u8981\u4e70\u767e\u628a \u6839\u6241\u4e5f\u8db3\u8db3\u6709\u4f59\uff0c\u4f60\u4e0d\u591a\u8c22\u4eba\u5bb6\uff0c\u8fd8\u6562\u5bf9\u4eba\u5bb6\u65e0\u793c\uff1f \u3000\u3000\u9752\u8863\u5927\u6c49\u77a7\u77a7\u624b\u91cc\u534a\u6839\u6241\u62c5\uff0c\u53c8\u77a7\u4e86\u77a7\u778e\u5b50\u624b\u91cc\u7684\u94f6\u952d\uff0c\u4f3c\u4e4e\u518d\u4e5f\u4e0d\u4fe1\u8fd9\u4f4d\u6587\u8d28\u5f6c\u5f6c\u7684 \u767d\u8863\u4eba\u80fd\u7528\u5c0f\u5c0f\u7684\u4e00\u952d\u94f6\u5b50\u6253\u65ad\u4ed6\u7684\u6241\u62c5\u3002 \u3000\u3000\u767d\u8863\u4eba\u5ffd\u7136\u4ef0\u9762\u5927\u7b11\u8d77\u6765\uff0c\u6717\u58f0\u9053\uff1a\u597d\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd9\u778e\u5b50\u7684\u773c\u775b\u7adf\u6bd4\u522b\u4eba\u7684\u90fd\u6709\u7528\uff0c\u8fd9 \u952d\u94f6\u5b50\uff0c\u5c31\u5f52\u4f60\u5427\u3002 \u3000\u3000\u5356\u535c\u7684\u778e\u5b50\u795e\u8272\u4e0d\u53d8\uff0c\u51b7\u51b7\u9053\uff1a\u8001\u673d\u773c\u775b\u867d\u778e\uff0c\u5fc3\u5374\u4e0d\u778e\uff0c\u4ece\u4e0d\u6562\u505a\u5473\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u5c06\u94f6\u5b50\u5728\u624b\u91cc\u62c8\u4e86\u62c8\uff0c\u7f13\u7f13\u9053\uff1a\u6241\u62c5\u53ea\u8981\u4e00\u94b1\u94f6\u5b50\u4e00\u6761\uff0c\u8fd9\u952d\u94f6\u5b50\u5374\u662f\u8db3\u8db3\u6709\u5341\u4e24\u91cd \uff0c\u516c\u4e86\u5b53\u7b97\u8981\u8d54\u6211\u4eec\u7684\u6241\u62c5\uff0c\u4e5f\u7528\u4e0d\u8fd9\u8bb8\u591a\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5c06\u624b\u91cc\u7684\u94f6\u5b50\u6413\u6210\u6761\u94f6\u68cd\uff0c\u5de6\u624b\u4e00\u62d7\uff0c\u62d7\u4e0b\u4e86\u4e00\u5c0f\u5757\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u8fd9 \u4e00\u94b1\u94f6\u5b50\u8001\u673d\u62dc\u9886\uff0c\u591a\u4e0b\u7684\u8fd8\u662f\u7269\u5f52\u539f\u4e3b\u3002 \u3000\u3000\u4f46\u89c1\u94f6\u5149\u4e00\u95ea\uff0c\u4ed6\u7684\u624b\u4e00\u6325\uff0c\u4e09\u5c3a\u957f\u7684\u94f6\u68cd\u5df2\u5939\u5e26\u7740\u98ce\u58f0\u5411\u767d\u8863\u4eba\u523a\u51fa\uff0c\u7528\u7684\u8d6b\u7136\u7adf\u662f \u6b66\u5f53\u4e24\u4eea\u5251\u6cd5\u542c\u4e00\u62db\u5999\u7740\u3002 \u3000\u3000\u4f46\u89c1\u94f6\u5149\u95ea\u52a8\uff0c\u4e00\u62db\u95f4\u5df2\u8fde\u523a\u767d\u8863\u4eba\u524d\u80f8\u4e94\u516d\u5904\u5927\u7a74\u3002 \u3000\u3000\u76f4\u7b49\u94f6\u68cd\u523a\u5230\u773c\u524d\uff0c\u767d\u8863\u4eba\u7a81\u7136\u4f38\u51fa\u4e2d\u98df\u4e24\u6307\u5728\u68cd\u5934\u4e00\u5939\uff0c\u4ed6\u4e24\u6839\u624b\u6307\u7adf\u5b9b\u5982\u7cbe\u521a\u5229\u5288 \uff0c\u968f\u624b\u4e00\u526a\uff0c\u5c31\u5c06\u94f6\u68cd\u526a\u4e0b\u4e86\u4e00\u622a\u3002 \u3000\u3000\u767d\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u4f60\u5251\u6cd5\u5012\u4e0d\u5f31\uff0c\u53ea\u53ef\u60dc\u592a\u6162\u4e86\u4e9b\u3002 \u3000\u3000\u4ed6\u8bf4\u4e00\u4e2a\u5b57\uff0c\u624b\u6307\u4e00\u526a\uff0c\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4e00\u6839\u4e09\u5c3a\u957f\u7684\u94f6\u68cd\u5df2\u88ab\u4ed6\u526a\u6210\u5341\u516d\u4e03\u8282\uff0c\u53ee\u53ee \u5f53\u5f53\u843d\u4e86\u6ee1\u5730\u3002 \u3000\u3000\u94c3\u94c3\u8fdc\u8fdc\u5730\u77a7\u7740\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u6084\u6084\u9053\uff1a\u8fd9\u4eba\u7684\u624b\u96be\u9053\u4e0d\u662f\u8089\u505a\u7684\uff1f \u3000\u3000\u522b\u4eba\u770b\u7740\u90a3\u778e\u5b50\u624b\u91cc\u5269\u4e0b\u7684\u4e00\u5c0f\u6bb5\u94f6\u68cd\uff0c\u4e00\u4e2a\u4e2a\u90fd\u5df2\u9762\u5982\u6b7b\u7070\uff0c\u90a3\u91cc\u8fd8\u8c18\u5f97\u51fa\u534a\u53e5\u8bdd\u6765 \u3002 \u3000\u3000\u767d\u8863\u4eba\u53c8\u80cc\u8d1f\u8d77\u53cc\u624b\uff0c\u51b7\u51b7\u9053\uff1a\u94f6\u5b50\u6211\u5df2\u9001\u51fa\uff0c\u5c31\u662f\u4f60\u7684\uff0c\u4f60\u8fd8\u4e0d\u6361\u8d77\u6765\uff1f \u3000\u3000\u5356\u535c\u7684\u778e\u5b50\u8138\u8272\u66f4\u9752\u5f97\u53ef\u6015\uff0c\u5ffd\u7136\u5f2f\u4e0b\u8170\uff0c\u5c06\u5730\u4e0a\u7684\u94f6\u5b50\u4e00\u5757\u5757\u5730\u6361\u4e86\u8d77\u6765\uff0c\u4e00\u8a00\u4e0d\u53d1 \uff0c\u626d\u5934\u5c31\u8d70\u3002 \u3000\u3000\u9752\u8863\u5927\u6c49\u3001\u72ec\u773c\u5987\u4eba\u4eec\u4e5f\u5782\u7740\u5934\uff0c\u8ddf\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u94c3\u94c3\u7b11\u9053\uff1a\u6765\u5f97\u5a01\u98ce\uff0c\u53bb\u5f97\u7a00\u677e\uff0c\u8fd9\u4e9b\u4eba\u81f3\u5c11\u8fd8\u4e0d\u6127\u4e3a\u8bc6\u65f6\u52a1\u7684\u4fca\u6770\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\u5ffd\u7136\u9053\uff1a\u4f60\u770b\u5230\u90a3\u8fb9\u5356\u5305\u5b50\u6c34\u997a\u7684\u5c0f\u5403\u9762\u94fa\u4e86\u4e48\uff1f \u3000\u3000\u94c3\u94c3\u7b11\u9053\uff1a\u4e0d\u4f46\u65e9\u5c31\u770b\u5230\u4e86\uff0c\u800c\u4e14\u65e9\u5c31\u60f3\u53bb\u5c1d\u5c1d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u597d\uff0c\u4f60\u5c31\u5728\u90a3\u91cc\u7b49\u6211\u3002 \u3000\u3000\u94c3\u94c3\u5446\u4e86\u5446\u9053\uff1a\u4f60\u8981\u53bb\u8ffd\u90a3\u8981\u996d\u7684\u3002 \u3000\u3000\u90a3\u4e5e\u8d77\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u6b63\u7b11\u563b\u563b\u5730\u5f80\u524d\u8d70\uff0c\u65e2\u6ca1\u6709\u8fc7\u53bb\u5411\u90a3\u767d\u8863\u4eba\u9053\u8c22\uff0c\u4e5f\u6ca1\u6709\u77a7\u522b\u4eba\u4e00 \u773c\u3002 \u3000\u3000\u521a\u624d\u53d1\u751f\u7684\u4e8b\uff0c\u4f3c\u4e4e\u90fd\u4e0e\u4ed6\u65e0\u5173\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u6211\u6709\u8bdd\u8981\u95ee\u4ed6\u3002 \u3000\u3000\u94c3\u94c3\u7684\u773c\u5708\u6709\u4e9b\u7ea2\u4e86\uff0c\u4f4e\u5934\u95ee\u9053\uff1b\u6211\u4e0d\u80fd\u966a\u4f60\u53bb\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e0d\u80fd\u3002 \u3000\u3000\u94c3\u94c3\u51e0\u4e4e\u5feb\u54ed\u4e86\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u6211\u77e5\u9053\uff0c\u4f60\u53c8\u60f3\u7529\u5f00\u6211\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u6211\u4e5f\u60f3\u5403\u6c34\u997a\uff0c\u600e\u4e48\u4f1a\u4e0d\u56de\u6765\uff1f \u3000\u3000\u94c3\u94c3\u9053\uff1a\u597d\uff0c\u6211\u5c31\u76f8\u4fe1\u4f60\uff0c\u4f60\u82e5\u9a97\u6211\uff0c\u6211\u5c31\u5728\u90a3\u91cc\u7b49\u4f60\u4e00\u8f88\u5b50\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u8d70\u5f97\u5e76\u4e0d\u5feb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u4e5f\u5e76\u4e0d\u6025\u7740\u60f3\u8ffd\u4e0a\u4ed6\uff0c\u8fd9\u6761\u8857\u7684\u4eba\u5b9e\u5728\u592a\u591a\u3002 \u3000\u3000\u4eba\u591a\u4e86\u8bf4\u8bdd\u6709\u4e9b\u4e0d\u4fbf\uff0c\u4f55\u51b5\uff0c\u4ed6\u53d1\u89c9\u90a3\u767d\u8863\u4eba\u7684\u773c\u775b\u7adf\u4e00\u7766\u5728\u76ef\u7740\u4ed6\uff0c\u4eff\u4f5b\u5ffd\u7136\u89c9\u5f97\u4ed6 \u8fd9\u4eba\u6bd5\u7adf\u8fd8\u662f\u6bd4\u9e1f\u6709\u8da3\u5f97\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5f88\u60f3\u4ed4\u7ec6\u770b\u770b\u8fd9\u767d\u8863\u4eba\uff0c\u65b9\u624d\u4ed6\u9732\u7684\u90a3\u624b\u6307\u526a\u94f6\u68cd\u7684\u529f\u592b\uff0c\u5b9e\u5728\u5df2\u5f15\u8d77\u4e86\u674e\u5bfb \u6b22\u7684\u5174\u8da3\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u50cf\u4ed6\u8fd9\u6837\u7684\u9ad8\u624b\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u674e\u5bfb\u6b22\u4e0d\u60f3\u4e0d\u51fa\u4e16\u4e0a\u8c01\u6709\u4ed6\u8fd9\u6837\u7684\u6307\u4e0a\u529f\u529b\u2014\u2014\u94c3\u94c3\u5f62\u5bb9\u7684\u8bdd\u5e76\u4e0d\u8fc7\u4efd\uff01 \u3000\u3000\u8fd9\u4eba\u7684\u624b\u6307\u7b80\u76f4\u4e0d\u50cf\u662f\u8089\u505a\u7684\u3002 \u3000\u3000\u53ea\u8981\u662f\u7ec3\u6b66\u7684\u4eba\uff0c\u9047\u7740\u8fd9\u6837\u8eab\u6000\u7edd\u6280\u7684\u9ad8\u624b\uff0c\u4e0d\u662f\u60f3\u53bb\u548c\u4ed6\u8f83\u91cf\u8f83\u91cf\uff0c\u5c31\u662f\u60f3\u53bb\u548c\u4ed6\u7ed3 \u4ea4\u7ed3\u4ea4\u3002 \u3000\u3000\u82e5\u6362\u4e86\u5e73\u65e5\uff0c\u674e\u5bfb\u6b22\u4e5f\u4e0d\u4f1a\u4f8b\u5916\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5374\u6ca1\u6709\u8fd9\u79cd\u5fc3\u60c5\uff0c\u4ed6\u5bfb\u627e\u94c1\u4f20\u7532\u5df2\u6709\u5f88\u4e45\uff0c\u59cb\u7ec8\u4e5f\u5f97\u4e0d\u5230\u6d88\u606f\uff0c\u8fd9\u4e00\u6b21\u673a\u4f1a\u4ed6\u7edd \u4e0d\u80fd\u9519\u8fc7\u3002 \u3000\u3000\u767d\u8863\u4eba\u5df2\u5411\u4ed6\u8d70\u8fc7\u6765\u4e86\uff0c\u4f3c\u4e4e\u60f3\u62e6\u4f4f\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u5e78\u597d\u65b9\u624d\u6563\u5f00\u7684\u4eba\u7fa4\u73b0\u5728\u53c8\u805a\u4e86\u8fc7\u6765\uff0c\u4e89\u7740\u4e00\u7779\u90a3\u767d\u8863\u4eba\u7684\u98ce\u91c7\uff0c\u674e\u5bfb\u6b22\u5c31\u8d81\u7740\u8fd9\u673a\u5750 \uff0c\u6324\u51fa\u4e86\u4eba\u4e1b\u3002 \u3000\u3000\u518d\u62ac\u5934\u770b\u65f6\uff0c\u90a3\u4e5e\u4e10\u7adf\u5df2\u8d70\u5230\u8857\u7684\u5c3d\u5934\uff0c\u5411\u5de6\u8f6c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5de6\u8fb9\u7684\u4e00\u6761\u8857\uff0c\u4eba\u5c31\u5c11\u5f97\u591a\u4e86\uff0c\u4e5f\u4e0d\u592a\u957f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5927\u6b65\u8d76\u4e86\u8fc7\u53bb\uff0c\u90a3\u4e5e\u4e10\u7adf\u5df2\u4e0d\u89c1\uff0c\u4e00\u76f4\u8d70\u5b8c\u8fd9\u6761\u8857\uff0c\u518d\u8f6c\u8fc7\u53e6\u4e00\u6761\u8857\uff0c\u7adf\u8fd8\u662f\u77a7 \u4e0d\u89c1\u90a3\u4e5e\u4e10\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u600e\u4f1a\u5ffd\u7136\u5931\u8e2a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u4f4f\u4e86\u6c14\uff0c\u6cbf\u7740\u5899\u89d2\u6162\u6162\u5730\u5411\u524d\u8d70\u3002 \u3000\u3000\u8fd9\u6761\u8857\u4e0a\u4e24\u65c1\u90fd\u662f\u4eba\u5bb6\u540e\u95e8\uff0c\u524d\u9762\u4e00\u4e2a\u95e8\u6d1e\u91cc\uff0c\u4f3c\u4e4e\u8e72\u7740\u4e2a\u4eba\uff0c\u624b\u91cc\u4e5f\u4e0d\u77e5\u62ff\u7740\u4ec0\u4e48\u4e1c \u897f\uff0c\u6b63\u5728\u5f80\u81ea\u5df1\u8eab\u4e0a\u64e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u672a\u770b\u5230\u4ed6\u7684\u4eba\uff0c\u5df2\u770b\u5230\u90a3\u9876\u7834\u5e3d\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u539f\u6765\u8eb2\u5230\u8fd9\u91cc\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u5728\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u60f3\u60ca\u52a8\u4ed6\uff0c\u6162\u6162\u5730\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u8fd8\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u8d76\u7d27\u5c06\u624b\u91cc\u7684\u4e1c\u897f\u5f80\u80cc\u540e\u85cf\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u674e\u5bfb\u6b22\u7684\u773c\u775b\u53ef\u6bd4\u4ed6\u624b\u5feb\u5f97\u591a\u4e86\uff0c\u65e9\u5df2\u770b\u5230\u4ed6\u624b\u91cc\u62ff\u7740\u7684\u662f\u4e00\u5c0f\u6bb5\u94f6\u5b50\uff0c\u663e\u7136\u5c31 \u662f\u65b9\u624d\u90a3\u767d\u8863\u4eba\u526a\u4e0b\u6765\u7684\uff0c\u5df2\u88ab\u4ed6\u64e6\u5f97\u96ea\u4eae\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u670b\u53cb\u8d35\u59d3\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u6211\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u4e5f\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f60\u4e5f\u4e0d\u8ba4\u5f97\u6211 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u5fae\u7b11\u9053\uff1a\u6211\u60f3\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\uff0c\u90a3\u4eba\u4f60\u4e00\u5b9a\u8ba4\u5f97\u7684\u3002 \u7b2c\u4e94\u5341\u4e8c\u7ae0\u3000\u9677\u9631>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u4e8c\u7ae0\u3000\u9677\u9631 \u3000\u3000\u90a3\u4e5e\u4e10\u6447\u7740\u5934\u9053\uff1a\u6211\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u4e00\u4e2a\u4eba\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u4e00\u4e2a\u4eba \u4e5f\u4e0d\u8ba4\u5f97\u6211\u3002 \u3000\u3000\u8fd9\u4eba\u679c\u7136\u6709\u4e9b\u75f4\u75f4\u5446\u5446\uff0c\u660e\u660e\u662f\u5f88\u7b80\u5355\u7684\u4e00\u53e5\u8bdd\uff0c\u4ed6\u5374\u8981\u53cd\u53cd\u8986\u8986\u8bf4\u4e0a\u597d\u51e0\u6b21\uff0c\u800c\u4e14\u8bf4 \u8bdd\u65f6\u5634\u91cc\u5c31\u8c61\u662f\u542b\u7740\u4e2a\u9e21\u86cb\u4f3c\u7684\uff0c\u542b\u7cca\u4e0d\u6e05\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6b63\u60f3\u7528\u522b\u7684\u6cd5\u5b50\u518d\u95ee\u95ee\u4ed6\u65f6\uff0c\u4ed6\u5374\u5df2\u5f80\u674e\u5bfb\u6b22\u814b\u4e0b\u94bb\u4e86\u8fc7\u53bb\uff0c\u4e00\u6e9c\u70df\u4f3c\u5730\u8dd1\u4e86\u3002 \u3000\u3000\u4ed6\u8dd1\u5f97\u5f88\u5feb\uff0c\u5374\u7edd\u4e0d\u8c61\u662f\u6709\u8f7b\u529f\u6839\u57fa\u7684\u4eba\uff0c\u5929\u4e0b\u7684\u4e5e\u4e10\u90fd\u8dd1\u5f97\u5f88\u5feb\uff0c\u8fd9\u4f3c\u4e4e\u65e9\u5df2\u53d8\u6210\u4e5e \u4e10\u7684\u552f\u4e00\u672c\u4e8b\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u81ea\u7136\u6bd4\u4ed6\u8fd8\u8981\u5feb\u5f97\u591a\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u4e00\u9762\u8dd1\uff0c\u4e00\u9762\u5598\u7740\u6c14\uff0c\u9053\uff1a\u4f60\u8fd9\u4eba\u60f3\u5e72\u4ec0\u4e48\uff1f\u60f3\u62a2\u6211\u7684\u94f6\u5b50\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u5927\u53eb\u9053\uff1a\u4e0d\u5f97\u4e86\uff0c\u4e0d\u5f97\u4e86\uff0c\u6709\u5f3a\u76d7\u5728\u62a2\u94f6\u5b50\u5440\uff01 \u3000\u3000\u5e78\u597d\u8fd9\u6761\u8def\u5f88\u50fb\u9759\uff0c\u4e0d\u89c1\u4eba\u8e2a\uff0c\u5426\u5219\u674e\u5bfb\u6b22\u5012\u4e0d\u77e5\u8be5\u600e\u4e48\u529e\u624d\u597d\uff0c\u82e5\u8fde\u4e5e\u4e10\u7684\u94f6\u5b50\u90fd\u8981 \u62a2\uff0c\u5c82\u975e\u53d8\u6210\u4e86\u7b2c\u516b\u6d41\u7684\u5f3a\u76d7\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u53eb\u7684\u58f0\u97f3\u66f4\u5927\uff0c\u9053\uff1a\u5feb\u628a\u94f6\u5b50\u8fd8\u7ed9\u6211\uff0c\u4e0d\u7136\u6211\u8ddf\u4f60\u62fc\u547d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u53ea\u8981\u4f60\u56de\u7b54\u6211\u51e0\u53e5\u8bdd\uff0c\u6211\u4e0d\u4f46\u5c06\u8fd9\u70b9\u94f6\u5b50\u8fd9\u7ed9\u4f60\uff0c\u8fd8\u9001\u4f60\u4e00\u952d\u5927\u7684\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u7728\u7740\u773c\uff0c\u4f3c\u4e4e\u8003\u8651\u5f88\u4e45\uff0c\u624d\u70b9\u5934\u9053\uff1a\u597d\uff0c\u4f60\u8981\u95ee\u6211\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u53ef\u662f\u94c1\u4f20\u7532\u7684\u670b\u53cb\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u6447\u5934\u9053\uff1a\u6211\u6ca1\u6709\u670b\u53cb\uff0d\u7a77\u8981\u996d\u7684\u90fd\u6ca1\u6709\u670b\u53cb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4e48\uff0c\u4f60\u4e3a\u4f55\u8981\u5e2e\u4ed6\u7684\u5fd9\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u5934\u6447\u5f97\u66f4\u5feb\uff0c\u9053\uff1a\u8c01\u7684\u5fd9\u6211\u4e5f\u4e0d\u5e2e\uff0c\u8c01\u4e5f\u6ca1\u5e2e\u8fc7\u6211\u7684\u5fd9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\u9053\uff1a\u4f60\u4eca\u5929\u96be\u9053\u6ca1\u6709\u89c1\u5230\u8fc7\u4e00\u4e2a\u8eab\u6750\u5f88\u9ad8\u5927\uff0c\u76ae\u80a4\u5f88\u9ed1\u7684\uff0c\u8138\u4e0a\u957f\u7740\u5927\u80e1 \u5b50\u7684\u4eba\u4e48\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u60f3\u4e86\u60f3\u9053\uff1a\u6211\u597d\u50cf\u770b\u5230\u4e00\u4e2a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u5728\u54ea\u91cc\u770b\u5230\u4ed6\u7684\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u9053\uff1a\u5728\u8305\u623f\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u8305\u623f\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u9053\uff1a\u8305\u623f\u5c31\u662f\u5927\u4fbf\u7684\u5730\u65b9\uff0c\u6211\u6b63\u5728\u5927\u4fbf\uff0c\u90a3\u5c0f\u5b50\u5ffd\u7136\u95ef\u4e86\u8fdb\u6765\uff0c\u95ee\u6211\u60f3\u4e0d\u60f3\u8d5a\u51e0 \u65a4\u9152\u559d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u8c01\u4e0d\u60f3\u8d5a\u51e0\u65a4\u9152\u559d\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u9053\uff1a\u4f46\u6211\u770b\u90a3\u5c0f\u5b50\u7a7f\u5f97\u6bd4\u6211\u8fd8\u7834\u70c2\uff0c\u54ea\u91cc\u8c61\u6709\u94b1\u4e70\u9152\u7ed9\u6211\u559d\u7684\u6837\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u8d8a\u6709\u94b1\u7684\u4eba\uff0c\u8d8a\u559c\u6b22\u88c5\u7a77\uff0c\u8fd9\u9053\u7406\u4f60\u4e0d\u660e\u767d\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u90a3\u5c0f\u5b50\u679c\u7136\u6709\u952d\u94f6\u5b50\uff0c\u800c\u4e14\u8fd8\u7ed9\u6211\u770b\u4e86\uff0c\u6211\u5c31\u95ee\u4ed6\u8981 \u6211\u600e\u4e48\u6837\u624d\u80fd\u8d5a\u5f97\u5230\u8fd9\u952d\u94f6\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4ed6\u600e\u4e48\u8bf4\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u7b11\u9053\uff1a\u6211\u4ee5\u4e3a\u4ed6\u4e00\u5b9a\u6709\u4ec0\u4e48\u7a00\u5947\u53e4\u602a\u7684\u82b1\u6837\uff0c\u8c01\u77e5\u4ed6\u53ea\u662f\u8981\u6211\u8ddf\u4ed6\u6362\u5957\u8863\u670d\uff0c\u7136 \u540e\u4f4e\u7740\u5934\u8d70\u51fa\u53bb\uff0c\u5343\u4e07\u4e0d\u8981\u62ac\u5934\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u8fd9\u94f6\u5b50\u8d5a\u5f97\u5012\u771f\u5bb9\u6613\u3002 \u3000\u3000\u4ed6\u8fd9\u6b21\u771f\u662f\u4ece\u5fc3\u91cc\u7b11\u51fa\u6765\u7684\uff0c\u8c61\u94c1\u4f20\u7532\u90a3\u6837\u7684\u4eba\uff0c\u73b0\u5728\u5c45\u7136\u4e5f\u4f1a\u7528\u8fd9\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\u4e86\uff0c \u5b9e\u5728\u662f\u4ee4\u4eba\u6b22\u559c\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u7b11\u5f97\u66f4\u5f00\u5fc3\uff0c\u9053\uff1a\u662f\u5440\uff0c\u6240\u4ee5\u6211\u770b\u90a3\u5c0f\u5b50\u4e00\u5b9a\u6709\u6bdb\u75c5\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u4e5f\u6709\u6bdb\u75c5\uff0c\u6211\u7684\u94f6\u5b50\u6bd4\u4ed6\u7684\u66f4\u597d\u8d5a\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u9053\uff1a\u771f\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u628a\u8eab\u4e0a\u6240\u6709\u7684\u94f6\u5b50\u90fd\u62ff\u4e86\u51fa\u6765\u2014\u2014\u4ed6\u5c06\u5bb6\u8d22\u5206\u6563\u7684\u65f6\u5019\uff0c\u94c1\u4f20\u7532\u575a\u6301\u4e3a\u4ed6\u7559\u4e0b\u4e86 \u4e9b\u751f\u6d3b\u7684\u5fc5\u9700\u8d39\u7528\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u5c31\u662f\u4ee5\u6b64\u6e21\u65e5\u7684\uff0c\u5426\u5219\u4ed6\u83ab\u8bf4\u559d\u9152\uff0c\u8fde\u5403\u996d\u90fd\u8981\u6210\u603b\u662f\uff0c \u8fd9\u4e5f\u662f\u4ed6\u8981\u611f\u6fc0\u94c1\u4f20\u7532\u7684\u8bb8\u591a\u79cd\u539f\u56e0\u4e4b\u4e00\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u671b\u7740\u4ed6\u624b\u91cc\u7684\u94f6\u5b50\uff0c\u773c\u775b\u90fd\u76f4\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u53ea\u8981\u4f60\u80fd\u5e26\u6211\u627e\u5230\u90a3\u6709\u6bdb\u75c5\u7684\u5c0f\u5b50\uff0c\u6211\u5c31\u5c06\u8fd9\u4e9b\u94f6\u5b50\u90fd\u7ed9\u4f60\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u7acb\u523b\u62a2\u7740\u9053\uff1a\u597d\uff0c\u6211\u5e26\u4f60\u53bb\uff0c\u4f46\u94f6\u5b50\u4f60\u5374\u4e00\u5b9a\u8981\u5148\u7ed9\u6211\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u7528\u4e24\u624b\u5c06\u94f6\u5b50\u6367\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u8981\u80fd\u627e\u5f97\u5230\u94c1\u4f20\u7532\uff0c\u5c31\u7b97\u8981\u4ed6\u5c06\u5fc3\u6367\u51fa\u6765\uff0c\u4ed6\u4e5f\u613f\u610f\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u7b11\u5f97\u8fde\u53e3\u6c34\u90fd\u6d41\u51fa\u6765\u4e86\uff0c\u4e00\u9762\u5c06\u94f6\u5b50\u624b\u5fd9\u811a\u4e71\u5730\u5f80\u6000\u91cc\u63e3\uff0c\u4e00\u9762\u563b\u563b\u5730\u7b11\u7740\u9053\uff1a \u6211\u770b\u4f60\u8fd9\u94f6\u5b50\u4e00\u5b9a\u662f\u5077\u6765\u7684\uff0c\u5426\u5219\u600e\u4f1a\u5982\u6b64\u8f7b\u6613\u5c31\u9001\u4eba\u3002 \u3000\u3000\u4ed6\u62a2\u94f6\u5b50\u7684\u65f6\u5019\uff0c\u81ea\u7136\u96be\u514d\u8981\u78b0\u5230\u674e\u5bfb\u6b22\u7684\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u521a\u78b0\u5230\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u4e94\u6307\u7a81\u7136\u4e00\u642d\u3001\u4e00\u52fe\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u624b\u8155\u4e0a\u8c61\u662f\u7a81\u7136\u591a\u4e86\u9053\u94c1\u7b8d\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u7684\u4eba\u7adf\u88ab\u62ce\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e5e\u4e10\u4e0d\u4f46\u51fa\u624b\u5feb\u5f97\u9a87\u4eba\uff0c\u8fd9\u4e00\u642d\u3001\u4e00\u52fe\uff0c\u4e24\u4e2a\u52a8\u4f5c\u4e2d\uff0c\u7adf\u5305\u85cf\u4e86\u5f53\u4ee3\u6b66\u6797\u4e2d\u56db\u79cd\u6700\u53ef \u6015\u7684\u6b66\u529f\u3002 \u3000\u3000\u4ed6\u624b\u6307\u521a\u642d\u4e0a\u674e\u5bfb\u6b22\u624b\u6307\u65f6\uff0c\u5c31\u4f7f\u51fa\u4e86\u5185\u5bb6\u6b63\u5b97\u7684\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u5185\u529b\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u88ab\u4ed6 \u6cbe\u7740\uff0c\u90fd\u518d\u4e5f\u4f11\u60f3\u7529\u5f00\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u5c31\u4f7f\u51fa\u4e86\u4f20\u81ea\u6b66\u5f53\u7684\u4e03\u5341\u4e8c\u8def\u64d2\u62ff\u624b\uff0c\u642d\u4f4f\u4e86\u674e\u5bfb\u6b22\u7684\u8109\u95e8\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u7684\u8109 \u95e8\u88ab\u4ed6\u6263\u4f4f\uff0c\u771f\u529b\u5c31\u518d\u4e5f\u4f11\u60f3\u4f7f\u5f97\u51fa\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u518d\u4ee5\u5206\u7b4b\u9519\u9aa8\u624b\u9519\u5f00\u674e\u5bfb\u6b22\u7684\u7b4b\u9aa8\u3002 \u3000\u3000\u6700\u540e\u4ed6\u90a3\u4e00\u62db\uff0c\u7528\u7684\u5374\u662f\u585e\u5916\u6454\u8dcc\u7684\u624b\u6cd5\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u53ea\u8981\u88ab\u4ed6\u62ce\u8d77\uff0c\u6454\u4e0b\uff0c\u5c31\u518d\u4e5f\u4f11 \u60f3\u722c\u5f97\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e5e\u4e10\u5c06\u6bcf\u79cd\u529f\u592b\u90fd\u7ec3\u5f97\u7089\u706b\u7eaf\u9752\uff0c\u6709\u5341\u8db3\u7684\u706b\u5019\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c31\u7b97\u5df2\u770b\u51fa\u4ed6\u4e0d\u662f\u5e38\u4eba\uff0c\u5374\u7edd\u5bf9\u770b\u4e0d\u51fa\u4ed6\u662f\u8fd9\u6837\u7684\u9ad8\u624b\uff0c\u5c31\u7b97\u77e5\u9053\u4ed6\u8eab\u6000\u6b66\u529f\uff0c \u5374\u4e5f\u7edd\u5bf9\u60f3\u4e0d\u5230\u4ed6\u4f1a\u6697\u7b97\u81ea\u5df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u4e00\u751f\u4e2d\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u5982\u6b64\u5403\u60ca\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7adf\u8c61\u6761\u6b7b\u9c7c\u822c\u6454\u5728\u5730\u4e0a\uff0c\u6454\u5f97\u4ed6\u4e24\u773c\u53d1\u9ec4\uff0c\u51e0\u4e4e\u6655\u4e86\u8fc7\u53bb\uff0c\u7b49\u4ed6\u773c\u524d\u7684\u91d1\u661f\u6e10\u6e10 \u6d88\u6563\u65f6\uff0c\u4ed6\u77a7\u89c1\u90a3\u4e5e\u4e10\u7684\u8138\u5c31\u5728\u4ed6\u9762\u524d\uff0c\u6b63\u8e72\u5728\u4ed6\u8eab\u65c1\uff0c\u7528\u4e00\u53ea\u624b\u627c\u4f4f\u4e86\u4ed6\u54bd\u5589\uff0c\u7b11\u563b\u563b\u5730 \u77a7\u7740\u4ed6\u3002 \u3000\u3000\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u8981\u6697\u7b97\u6211\uff1f \u3000\u3000\u96be\u9053\u4ed6\u65e9\u5df2\u8ba4\u51fa\u6211\u662f\u8c01\u4e86\uff1f \u3000\u3000\u4ed6\u548c\u94c1\u4f20\u7532\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u867d\u7136\u6709\u5f88\u591a\u7591\u95ee\uff0c\u5374\u8fde\u4e00\u53e5\u4e5f\u6ca1\u6709\u95ee\u51fa\u6765\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u8fd8\u662f\u95ed\u5634\u597d\u4e9b\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u5374\u5f00\u53e3\u4e86\uff0c\u7b11\u563b\u563b\u9053\uff1a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u9601\u4e0b\u7684\u8116\u5b50\u82e5\u88ab\u4eba\u627c\u4f4f\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u9053\uff1a\u82e5\u6709\u4eba\u6697\u7b97\u4e86\u6211\uff0c\u53c8\u627c\u4f4f\u4e86\u6211\u7684\u8116\u5b50\uff0c\u6211\u4e00\u5b9a\u8981\u5c06\u4ed6\u7956\u5b97\u516b\u4ee3\u90fd\u9a82\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u773c\u775b\u5e76\u6ca1\u6709\u778e\uff0c\u5374\u672a\u770b\u51fa\u9601\u4e0b\u662f\u8eab\u6000\u7edd\u6280\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u8981\u9a82\u4e5f\u53ea\u80fd\u9a82\u6211\u81ea \u5df1\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u7b11\u4e86\uff0c\u6447\u7740\u5934\u9053\uff1a\u4f60\u679c\u7136\u662f\u4e2a\u602a\u4eba\uff0c\u8c61\u4f60\u8fd9\u6837\u7684\u602a\u4eba\u6211\u5012\u672a\u89c1\u8fc7\u2014\u2014\u4f60\u518d\u8bf4\u4e24\u53e5 \uff0c\u5c31\u53ea\u6015\u8981\u8138\u7ea2\u4e86\uff01 \u3000\u3000\u4ed6\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u8fd9\u4eba\u4e0d\u4f46\u662f\u4e2a\u541b\u5b50\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u597d\u4eba\uff0c\u8fd9\u79cd\u4eba\u6211\u4e00\u5411\u6700\u5403\u4e0d\u6d88\uff0c\u4f60\u518d\u4e0d \u51fa\u6765\uff0c\u6211\u53ef\u4e0d\u7ba1\u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8fd8\u6709\u540c\u515a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5b9e\u5728\u731c\u4e0d\u51fa\u4ed6\u7684\u540c\u515a\u662f\u8c01\u3002\u53ea\u542c\u5440\u7684\u4e00\u58f0\uff0c\u65c1\u8fb9\u7684\u95e8\u5ffd\u7136\u5f00\u4e86\uff0c\u8d70\u51fa\u4e86\u516d\u4e03\u4e2a\u4eba \u6765\uff0c\u770b\u5230\u8fd9\u51e0\u4eba\uff0c\u674e\u5bfb\u6b22\u624d\u771f\u7684\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u6c38\u8fdc\u60f3\u4e0d\u5230\u8fd9\u51e0\u4eba\u4e5f\u662f\u90a3\u4e5e\u4e10\u7684\u540c\u515a\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4ef6\u4e8b\u4ece\u5934\u5230\u5c3e\u90fd\u662f\u4ed6\u4eec\u65e9\u5df2\u8ba1\u5212\u597d\u7684\u5708\u5957\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u4ece\u5c0f\u95e8\u91cc\u8d70\u51fa\u6765\u7684\uff0c\u7adf\u662f\u90a3\u5356\u535c\u7684\u778e\u5b50\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5c31\u662f\u90a3\u72ec\u773c\u5987\u4eba\u3001\u9752\u8863\u5927\u6c49\u3001\u4e70\u81ed\u8c46\u5e72\u7684\u5c0f\u8d29\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u5999\u8ba1\u5999\u8ba1\uff0c\u4f69\u670d\u4f69\u670d\u3002 \u3000\u3000\u778e\u5b50\u9762\u4e0a\u4ecd\u662f\u6beb\u65e0\u8868\u60c5\uff0c\u51b7\u51b7\u9053\uff1a\u4e0d\u6562\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u539f\u6765\u8fd9\u4ef6\u4e8b\u6839\u672c\u5c31\u548c\u94c1\u4f20\u7532\u5168\u65e0\u5173\u7cfb\u3002 \u3000\u3000\u778e\u5b50\u7f13\u7f13\u9053\uff1a\u5173\u7cfb\u662f\u6709\u7684\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014 \u3000\u3000\u90a3\u4e5e\u4e10\u62a2\u7740\u9053\uff1a\u53ea\u4e0d\u8fc7\u6211\u4ece\u6765\u672a\u66fe\u89c1\u94c1\u4f20\u7532\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u65b9\u624d\u627e\u4ed6\u4eec\u6f14\u4e86 \u90a3\u51fa\u620f\uff0c\u5b8c\u5168\u662f\u4e3a\u4e86\u8981\u4f60\u770b\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u90a3\u5012\u7684\u786e\u662f\u51fa\u597d\u620f\u3002 \u3000\u3000\u778e\u5b50\u9053\uff1a\u620f\u5012\u7684\u786e\u662f\u51fa\u597d\u620f\uff0c\u5426\u5219\u53c8\u600e\u80fd\u53eb\u674e\u63a2\u82b1\u4e0a\u5f53\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u539f\u6765\u5404\u4f4d\u975e\u4f46\u65e9\u5c31\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff0c\u800c\u4e14\u8fd8\u65e9\u5df2\u89c1\u5230\u4e86\u6211\u3002 \u3000\u3000\u778e\u5b50\u9053\uff1a\u9601\u4e0b\u8fd8\u672a\u5165\u57ce\uff0c\u5df2\u6709\u4eba\u89c1\u5230\u9601\u4e0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5404\u4f4d\u600e\u4f1a\u8ba4\u5f97\u6211\u7684\uff1f \u3000\u3000\u778e\u5b50\u9053\uff1a\u5728\u4e0b\u7b49\u867d\u8ba4\u4e0d\u5f97\u4f60\uff0c\u5374\u6709\u4eba\u8ba4\u5f97\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5404\u4f4d\u65e2\u7136\u4e0d\u8ba4\u5f97\u6211\uff0c\u4e3a\u4f55\u5bf9\u6211\u5982\u6b64\u7167\u987e\uff1f \u3000\u3000\u778e\u5b50\uff1a\u4e3a\u7684\u5c31\u662f\u94c1\u4f20\u7532\uff01 \u3000\u3000\u4ed6\u51b7\u6f20\u7684\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u6028\u6bd2\u4e4b\u610f\uff0c\u63a5\u7740\u9053\uff1a\u4e00\u4e30\u7b49\u5bf9\u4ed6\u90fd\u60f3\u5ff5\u5f97\u5f88\uff0c\u53ea\u82e6\u627e\u4e0d\u5230\u4ed6 \uff0c\u4f46\u4ed6\u82e5\u77e5\u9053\u674e\u63a2\u82b1\u4e5f\u548c\u5728\u4e0b\u7b49\u5728\u4e00\u8d77\uff0c\u5c31\u4f1a\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\u4e5f\u6211\u7b49\u76f8\u89c1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u4ed6\u82e5\u4e0d\u6765\u5462\uff1f\u5404\u4f4d\u5c82\u975e\u767d\u8d39\u4e86\u5fc3\u673a\uff1f \u3000\u3000\u778e\u5b50\u51b7\u51b7\u9053\uff1a\u4ed6\u7684\u4e8b\u4f60\u7edd\u4e0d\u4f1a\u4e0d\u7ba1\uff0c\u4f60\u7684\u4e8b\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u7f6e\u4e4b\u4e0d\u7406\uff0c\u4e24\u4f4d\u7684\u5173\u7cfb\uff0c\u5728\u4e0b\u7b49 \u65e9\u5df2\u6e05\u695a\u5f97\u5f88\uff0c\u5426\u5219\u53c8\u600e\u4f1a\u5b9a\u4e0b\u6b64\u8ba1\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u9601\u4e0b\u80fd\u60f3\u5f97\u51fa\u8fd9\u6837\u7684\u5999\u8ba1\uff0c\u5012\u4e5f\u771f\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u778e\u5b50\u6c89\u9ed8\u534a\u664c\uff0c\u7f13\u7f13\u9053\uff1a\u5728\u4e0b\u82e5\u6709\u5982\u6b64\u667a\u8c0b\uff0c\u8fd9\u53ea\u773c\u775b\u6015\u4e5f\u5c31\u4e0d\u4f1a\u778e\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5b9a\u8ba1\u7684\u4eba\u4e0d\u662f\u4f60\uff1f \u3000\u3000\u778e\u5b50\uff1a\u4e0d\u662f\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u7b11\uff1a\u6c11\u4e0d\u662f\u6211\uff0c\u6211\u8111\u888b\u4e00\u5411\u6709\u6bdb\u75c5\uff0c\u4e00\u60f3\u5230\u8981\u5bb3\u4eba\uff0c\u5c31\u4f1a\u5934\u75bc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u534a\u664c\u9053\uff1a\u539f\u6765\u5404\u4f4d\u5e55\u540e\u8fd8\u53e6\u6709\u4e3b\u8c0b\u4e4b\u4eba\u2014\u2014 \u3000\u3000\u778e\u5b50\u9053\uff1a\u4f60\u4e5f\u7528\u4e0d\u7740\u95ee\u4ed6\u662f\u8c01\uff0c\u53cd\u6b63\u4f60\u603b\u4f1a\u89c1\u7740\u4ed6\u7684\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u7af9\u6756\u4e00\u626c\uff0c\u5df2\u70b9\u4e86\u674e\u5bfb\u6b22\u5de6\u53f3\u53ea\u819d\u7684\u73af\u8df3\u7a74\u51b7\u51b7\u9053\uff1a\u4f60\u89c1\u7740\u4ed6\u65f6\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u89c9\u5f97 \u6d3b\u5728\u4e16\u4e0a\u6839\u672c\u5c31\u662f\u591a\u4f59\u7684\uff0c\u4e0d\u5982\u8fd8\u662f\u65e9\u4e9b\u6b7b\u4e86\u7684\u597d\u3002 \u3000\u3000\u95e8\u867d\u5c0f\u800c\u5899\u9ad8\u3002 \u3000\u3000\u95e8\u5185\u5ead\u9662\u6df1\u6c89\uff0c\u6084\u7136\u65e0\u58f0\u3002 \u3000\u3000\u53ea\u542c\u5c4f\u98ce\u540e\u4e00\u4e2a\u6717\u58f0\u7b11\u9053\uff1a\u5404\u4f4d\u5df2\u5c06\u6211\u90a3\u5144\u5f1f\u8bf7\u6765\u4e86\u4e48\uff1f \u3000\u3000\u4e00\u542c\u5230\u8fd9\u58f0\u97f3\uff0c\u674e\u5bfb\u6b22\u8fde\u6307\u5c16\u90fd\u5df2\u51b0\u51b7\u3002 \u3000\u3000\u8fd9\u8d6b\u7136\u7adf\u662f\u9f99\u5578\u4e91\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e3b\u8c0b\u5b9a\u8ba1\u7684\u4eba\uff0c\u7adf\u662f\u9f99\u5578\u4e91\u3002 \u3000\u3000\u778e\u5b50\u5728\u5c4f\u98ce\u524d\u5c31\u5df2\u505c\u4f4f\u4e86\u811a\uff0c\u6c89\u58f0\u9053\uff1a\u5728\u4e0b\u7b49\u5e78\u4e0d\u8fb1\u547d\uff0c\u603b\u7b97\u5df2\u5c06\u674e\u63a2\u82b1\u8bf7\u6765\u4e86\u3002 \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c4b\u540e\u5df2\u62a2\u5148\u8d70\u51fa\u4e86\u4e00\u4e2a\u4eba\u6765\uff0c\u6ee1\u9762\u7ea2\u5149\uff0c\u5374\u4e0d\u662f\u4e00\u522b\u51e0\u5e74\u7684\u9f99\u5578\u4e91\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u4e00\u884c\u51fa\u6765\uff0c\u5c31\u7d27\u7d27\u63e1\u4f4f\u4e86\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u7b11\u9053\uff1a\u4e00\u522b\u53c8\u662f\u4e24\u5e74\uff0c\u5144\u5f1f\u4f60\u53ef\u60f3\u715e\u5927\u54e5\u6211\u4e86 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\u9053\uff1a\u5927\u54e5\u82e5\u662f\u60f3\u89c1\u6211\uff0c\u53ea\u8981\u5429\u5490\u58f0\uff0c\u6211\u7acb\u523b\u5c31\u5230\uff0c\u53c8\u4f55\u5fc5\u52b3\u52a8\u8fd9\u4e48\u591a\u670b\u53cb \u7684\u5927\u9a7e\u5462\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u5ffd\u7136\u5927\u7b11\u8d77\u6765\uff0c\u62cd\u624b\u9053\uff1a\u8bf4\u5f97\u597d\uff0c\u8bf4\u5f97\u597d\uff0c\u8fde\u6211\u7684\u8138\u90fd\u88ab\u4f60\u8bf4\u7ea2\u4e86\uff0c\u542c\u4e86\u8fd9\u8bdd\u80fd \u9762\u4e0d\u6539\u8272\u7684\u4eba\uff0c\u6211\u771f\u662f\u4f69\u670d\u5f97\u5f88\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5374\u50cf\u662f\u5ffd\u7136\u53d8\u6210\u4e86\u804b\u5b50\uff0c\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u4ed6\u7adf\u4f3c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u542c\u89c1\uff0c\u8fd8\u662f\u63e1\u7740\u674e \u5bfb\u6b22\u7684\u624b\uff0c\u9053\uff1a\u6211\u65e9\u5df2\u7b97\u51c6\u4e86\u5144\u5f1f\u4f60\u4e00\u5b9a\u4f1a\u6765\uff0c\u65e9\u5df2\u51c6\u5907\u597d\u63a5\u98ce\u7684\u9152\uff0c\u4f60\u6211\u5144\u5f1f\u591a\u5e74\u4e0d\u89c1\uff0c \u8fd9\u6b21\u53ef\u5f97\u75db\u5feb\u5730\u559d\u51e0\u676f\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u62a2\u7740\u6276\u8d77\u4e86\u674e\u5bfb\u6b22\uff0c\u4e00\u9762\u542b\u7b11\u63d6\u5ba2\u9053\uff1a\u5404\u4f4d\u5feb\u8bf7\u5165\u5ea7\uff0c\u8bf7\uff0c\u8bf7\u3002 \u3000\u3000\u778e\u5b50\u7684\u811a\u5374\u8c61\u662f\u5df2\u9489\u5728\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u52a8\uff0c\u4ed6\u7684\u5144\u5f1f\u81ea\u7136\u4e5f\u4e0d\u4f1a\u52a8\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u5404\u4f4d\u96be\u9053\u4e0d\u80af\u8d4f\u5149\u4e48\uff1f\u778e\u5b50\u7f13\u7f13\u9053\uff1a\u5728\u4e0b\u7b49\u7b54\u5e94\u9f99\u5927\u7237\u505a\u8fd9\u4ef6\u4e8b\uff0c\u4e3a\u7684\u5b8c \u5168\u662f\u94c1\u4f20\u7532\uff0c\u5982\u4eca\u5728\u4e0b\u4efb\u52a1\u5df2\u4e86\uff0c\u7b49\u90a3\u94c1\u4f20\u7532\u6765\u65f6\uff0c\u53ea\u671b\u9f99\u5927\u7237\u83ab\u8981\u5fd8\u8bb0\u901a\u77e5\u4e00\u58f0\u3002 \u3000\u3000\u4ed6\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u81f3\u4e8e\u9f99\u5927\u7237\u7684\u9152\uff0c\u5728\u4e0b\u7b49\u4e07\u4e07\u4e0d\u6562\u53e8\u6270\uff0c\u9f99\u78ca\u7237\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u5728 \u4e0b\u7b49\u4e5f\u662f\u4e07\u4e07\u9ad8\u6500\u4e0d\u4e0a\u7684\u3002 \u3000\u3000\u4ed6\u7af9\u6756\u70b9\u5730\uff0c\u7adf\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5927\u5385\u4e2d\u5df2\u6446\u8d77\u4e86\u4e00\u684c\u9152\u3002 \u3000\u3000\u83dc\u662f\u73cd\u80b4\uff0c\u9152\u662f\u4f73\u917f\uff0c\u9f99\u56db\u7237\u8bf7\u5ba2\u7684\u8c6a\u723d\uff0c\u662f\u6c5f\u6e56\u95fb\u540d\u7684\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u62a2\u5148\u5728\u9996\u5e2d\u4e0a\u4e00\u5750\uff0c\u5583\u5583\u9053\uff1a\u8001\u5b9e\u8bf4\uff0c\u6211\u672c\u6765\u4e5f\u60f3\u8d70\u7684\uff0c\u4f46\u653e\u7740\u8fd9\u4e48 \u597d\u7684\u9152\u83dc\uff0c\u4e0d\u5403\u5c82\u975e\u53ef\u60dc\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5411\u674e\u5bfb\u6b22\u4e3e\u4e86\u4e3e\u676f\uff0c\u9053\uff1a\u4f60\u4e5f\u559d\u4e00\u676f\u5427\uff0c\u8fd9\u79cd\u4eba\u7684\u9152\u4f60\u4e0d\u559d\u4e5f\u662f\u767d\u4e0d\u559d\uff0c\u559d\u4e86\u4e5f \u662f\u767d\u559d\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6447\u5934\u7b11\u9053\uff1a\u8fd9\u4f4d\u80e1\u5927\u4fa0\uff0c\u5144\u5f1f\u4f60\u53ea\u6015\u8fd8\u4e0d\u8ba4\u5f97\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u80e1\u5927\u4fa0\uff1f\u53f0\u752b\u83ab\u975e\u662f\u4e0d\u5f52\u4e8c\u5b57\uff1f \u3000\u3000\u90a3\u4e5e\u4e10\u7b11\u9053\uff1a\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u80e1\u4e0d\u5f52\u5c31\u662f\u6211\uff01\u4f60\u5634\u91cc\u867d\u79f0\u6211\u80e1\u5927\u4fa0\uff0c\u5fc3\u91cc\u4e00\u5b9a\u5728\u60f3\uff1a\u54e6\uff0c \u539f\u6765\u8fd9\u4eba\u5c31\u662f\u80e1\u75af\u5b50\uff0c\u96be\u602a\u505a\u4e8b\u8bf4\u8bdd\u90fd\u6709\u4e9b\u75af\u75af\u766b\u766b\u7684\u2014\u2014\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u662f\u3002 \u3000\u3000\u80e1\u4e0d\u5f52\u5927\u7b11\u9053\uff1a\u597d\uff0c\u4f60\u8fd9\u4eba\u6709\u610f\u601d\uff0c\u770b\u6765\u53ea\u6015\u4e5f\u662f\u4e2a\u75af\u5b50\u2014\u2014\u4f60\u82e5\u4e0d\u75af\uff0c\u4e5f\u4e0d\u4f1a\u8ddf\u9f99\u5578 \u4e91\u8fd9\u6837\u7684\u4eba\u4ea4\u4e0a\u670b\u53cb\u4e86\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u4e0d\u8bed\u3002 \u3000\u3000\u80e1\u4e0d\u5f52\u9053\uff1a\u4f46\u4f60\u5343\u4e07\u83ab\u8981\u4ee5\u4e3a\u6211\u4e5f\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u6211\u5e2e\u4ed6\u8fd9\u6b21\u5fd9\uff0c\u53ea\u56e0\u4e3a\u6211\u6b20\u8fc7\u4ed6\u7684\u60c5\uff0c \u8fd9\u4ef6\u4e8b\u505a\u5b8c\uff0c\u6211\u548c\u4ed6\u5c31\u518d\u4e5f\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u53c8\u9053\uff1a\u53ea\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u505a\u5f97\u5b9e\u5728\u6709\u6b20\u5149\u660e\uff0c\u5b9e\u5728\u4e22\u4eba\uff0c\u5b9e\u5728\u5dee\u52b2\uff0c\u5b9e\u5728 \u4e0d\u662f\u4e1c\u897f\uff0c\u5b9e\u5728\u6df7\u5e10\u5df2\u6781\u2014\u2014 \u3000\u3000\u8bf4\u7740\u8bf4\u7740\uff0c\u4ed6\u7adf\u7ed9\u4e86\u81ea\u5df1\u5341\u4e03\u516b\u4e2a\u8033\u62ec\u5b50\uff0c\u53c8\u4f0f\u5728\u684c\u4e0a\u5927\u54ed\u8d77\u6765\uff0c\u9f99\u5578\u4e91\u4f3c\u4e4e\u65e9\u5df2\u89c1\u602a \u4e0d\u602a\uff0c\u5c45\u7136\u5145\u8033\u4e0d\u95fb\uff0c\u89c6\u82e5\u65e0\u7779\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53cd\u5012\u89c9\u5f97\u6709\u4e9b\u8fc7\u610f\u4e0d\u53bb\u4e86\uff0c\u7b11\u9053\uff1a\u65e0\u8bba\u5982\u4f55\uff0c\u80e1\u5144\u6700\u540e\u90a3\u51fa\u624b\u4e00\u51fb\uff0c\u6211\u7eb5\u6709\u9632\u5907 \uff0c\u4e5f\u662f\u4e07\u4e07\u95ea\u907f\u4e0d\u5f00\u7684\u3002 \u3000\u3000\u80e1\u4e0d\u5f52\u7a81\u53c8\u62cd\u684c\u5b50\uff0c\u5927\u6012\u9053\uff1a\u653e\u5c41\u653e\u5c41\uff0c\u7b80\u76f4\u662f\u653e\u5c41\uff0c\u6211\u82e5\u4e0d\u7528\u5978\u8ba1\uff0c\u54ea\u80fd\u6cbe\u5f97\u7740\u4f60\uff0c \u6211\u5bb3\u4e86\u4f60\uff0c\u4f60\u53cd\u6765\u5b89\u6170\u6211\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u6709\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u80e1\u4e0d\u5f52\u5583\u5583\u9053\uff1a\u6211\u8fd9\u4eba\u795e\u9b42\u4e0d\u5b9a\uff0c\u610f\u6012\u65e0\u5e38\uff0c\u9ed1\u767d\u4e0d\u5206\uff0c\u98a0\u4e09\u5012\u56db\uff0c\u8bf4\u54ed\u5c31\u54ed\uff0c\u8bf4\u7b11\u5c31 \u7b11\uff0c\u5b9e\u5728\u4ed6\u5988\u7684\u4e0d\u662f\u4e1c\u897f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u77aa\u8d77\u773c\u775b\uff0c\u77aa\u7740\u9f99\u5578\u4e91\u9053\uff1a\u4f46\u4f60\u5374\u6bd4\u6211\u66f4\u4e0d\u662f\u4e1c\u897f\uff0c\u4f60\u513f\u5b50\u6bd4\u4f60\u8fd8\u4e0d\u662f\u4e1c\u897f\uff0c\u4ed6 \u660e\u660e\u6709\u4e24\u6761\u817f\uff0c\u5374\u8981\u5b66\u72d7\u5728\u5730\u4e0a\u722c\uff0c\u96be\u9053\u60f3\u5728\u684c\u5b50\u4e0b\u9762\u6361\u9aa8\u5934\u5403\u4e48\uff1f \u3000\u3000\u9f99\u5578\u4e91\u8138\u4e0a\u4e5f\u4e0d\u7981\u7ea2\u4e86\u7ea2\uff0c\u4f4e\u4e0b\u5934\u4e00\u770b\uff0c\u9f99\u5c0f\u4e91\u679c\u7136\u5df2\u5077\u5077\u94bb\u5230\u684c\u5b50\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u628a\u5200 \u5b50\uff0c\u5df2\u722c\u5230\u674e\u5bfb\u6b22\u9762\u524d\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4e00\u628a\u5c06\u4ed6\u63ea\u4e86\u51fa\u6765\uff0c\u6c89\u7740\u8138\u9053\uff1a\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u5c45\u7136\u795e\u8272\u81ea\u82e5\uff0c\u4ece\u5bb9\u9053\uff1a\u5927\u4e08\u592b\u6069\u6028\u5206\u660e\uff0c\u8fd9\u53e5\u8bdd\u4f60\u8001\u4eba\u5bb6\u8bf4\u5bf9\u4e0d\u5bf9\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u81ea\u7136\u662f\u5bf9\u7684\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u6c5f\u6e56\u82f1\u96c4\u8bb2\u7a76\u7684\u4e5f\u662f\u6709\u4ec7\u5fc5\u62a5\uff0c\u6709\u6069\u5fc5\u507f\uff0c\u4ed6\u5e9f\u53bb\u4e86\u5b69\u513f\u4e00\u8eab\u6b66\u529f\uff0c\u4ee4\u5b69\u513f \u7ec8\u751f\u6b8b\u5e9f\uff0c\u5b69\u513f\u60f3\u8981\u4ed6\u4e24\u6761\u817f\uff0c\u4e5f\u662f\u5929\u7ecf\u5730\u4e49\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8138\u8272\u6709\u4e9b\u53d1\u9752\uff0c\u9053\uff1a\u4f60\u60f3\u590d\u4ec7\uff0c\u662f\u4e48\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u4e0d\u9519\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5389\u58f0\u9053\uff1a\u4f46\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u6211\u53ea\u77e5\u9053\u4ed6\u662f\u6211\u7684\u4ec7\u4eba\u2014\u2014 \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u9f99\u5578\u4e91\u7684\u624b\u5df2\u63b4\u5728\u4ed6\u8138\u4e0a\uff0c\u6012\u9053\uff1a\u4f46\u4f60\u53ef\u77e5\u4ed6\u662f\u4f60\u7236\u4eb2\u7684\u516b\u62dc\u4e4b\u4ea4\uff1f \u4ed6\u65e0\u8bba\u600e\u4e48\u6559\u8bad\u4f60\uff0c\u90fd\u662f\u5e94\u8be5\u7684\uff0c\u4f60\u600e\u53ef\u5bf9\u4ed6\u6709\u590d\u4ec7\u4e4b\u5fc3\uff1f\u600e\u4e5f\u5bf9\u4ed6\u65e0\u793c\uff1f \u3000\u3000\u9f99\u5c0f\u4e91\u88ab\u6253\u5f97\u5446\u4e86\u534a\u664c\uff0c\u773c\u73e0\u5b50\u4e00\u8f6c\uff0c\u5ffd\u7136\u5411\u674e\u5bfb\u6b22\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u4f84\u513f\u5df2\u77e5\u9053\u9519\u4e86\uff0c \u5012\u513f\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u674e\u5927\u53d4\u5343\u4e07\u83ab\u8981\u548c\u4f84\u513f\u4e00\u822c\u89c1\u8bc6\uff0c\u5c31\u9976\u4e86\u4f84\u513f\u8fd9\u4e00\u6b21\u5427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ee1\u8179\u8f9b\u9178\uff0c\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\uff0c\u80e1\u4e0d\u5f52\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u8fd9\u7236\u5b50\u4e24\u4eba\u6211\u5b9e\u5728\u53d7\u4e0d \u4e86\uff0c\u6211\u60f3\u5410\u3002 \u3000\u3000\u4ed6\u5634\u91cc\u5927\u547c\u5927\u53eb\uff0c\u4eba\u5df2\u51b2\u4e86\u51fa\u53bb\u3002 \u7b2c\u4e94\u5341\u4e09\u7ae0\u3000\u9a97\u5c40>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u4e09\u7ae0\u3000\u9a97\u5c40 \u3000\u3000\u9f99\u5578\u4e91\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u4e5f\u8bb8\u4f1a\u8d77\u9519\uff0c\u4f46\u5916\u53f7\u5374\u662f\u7edd\u4e0d\u4f1a\u8d77\u9519\u7684\uff0c\u6709\u7684\u4eba\u660e \u660e\u5176\u7b28\u5982\u725b\uff0c\u4e5f\u53ef\u4ee5\u8d77\u4e2a\u540d\u5b57\u53eb\u806a\u660e\uff0c\u4f46\u4e00\u4eba\u7684\u5916\u53f7\u82e5\u662f\u75af\u5b50\uff0c\u4ed6\u5c31\u4e00\u5b9a\u662f\u4e2a\u75af\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u672c\u6765\u4e0d\u60f3\u8bf4\u8bdd\u7684\uff0c\u5374\u5fcd\u4e0d\u4f4f\u9053\uff1a\u4f46\u4e00\u4e2a\u4eba\u82e5\u662f\u592a\u806a\u660e\u4e86\uff0c\u77e5\u9053\u7684\u4e8b\u592a\u591a\uff0c\u4e5f\u8bb8\u6162 \u6162\u5730\u53d8\u6210\u4e2a\u75af\u5b50\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u54e6\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u56e0\u4e3a\u5230\u4e86\u90a3\u79cd\u65f6\u5019\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u505a\u4e86\u75af\u5b50\u5c31\u4f1a\u53d8\u5f97\u5feb\u4e50\u4e9b\uff0c\u6240\u4ee5\u6709\u4e9b\u4eba \u6700\u5927\u7684\u75db\u82e6\u5c31\u662f\u4ed6\u660e\u660e\u60f3\u505a\u75af\u5b50\uff0c\u5374\u505a\u4e0d\u5230\u3002 \u3000\u3000\u9f99\u5578\u4e91\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u5e78\u597d\u6211\u4e00\u5411\u4e0d\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u6709\u8fd9\u79cd\u70e6\u607c\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u6709\u8fd9\u79cd\u70e6\u607c\uff0c\u4ed6\u6839\u672c\u4e0d\u4f1a\u6709\u4efb\u4f55\u4e00\u79cd\u70e6\u607c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u5c06\u5404\u79cd\u70e6\u607c\u5168\u90fd\u7ed9\u522b\u4eba\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u4f4e\u7740\u5934\uff0c\u6162\u6162\u5730\u559d\u4e86\u676f\u9152\u3002 \u3000\u3000\u9f99\u5578\u4e91\u53ea\u662f\u9759\u9759\u5730\u77a7\u7740\uff0c\u7b49\u7740\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u674e\u5bfb\u6b22\u9152\u559d\u5f97\u5f88\u6162\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u4e00\u5b9a\u6709\u53e5\u5f88\u91cd\u8981\u7684\u8bdd\u8981\u8bf4\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u674e\u5bfb\u6b22\u624d\u62ac\u8d77\u5934\uff0c\u9053\uff1a\u5927\u54e5\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u55ef\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u679c\u7136\u9053\uff1a\u6211\u5fc3\u91cc\u4e00\u76f4\u6709\u53e5\u8bdd\u8981\u8bf4\uff0c\u5374\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u8bf4\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4eec\u5df2\u662f\u591a\u5e74\u7684\u670b\u53cb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4e0d\u662f\u670b\u53cb\uff0c\u662f\u5144\u5f1f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff0c\u5927\u54e5\u4f60\u4e5f\u8be5\u65e9\u5df2\u660e\u767d\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u662f\u2014\u2014 \u3000\u3000\u867d\u7136\u53ea\u8bf4\u4e86\u4e00\u4e2a\u5b57\uff0c\u5374\u8bf4\u5f97\u5f88\u6162\u5f88\u6162\uff0c\u800c\u4e14\u76ee\u4e2d\u8fd8\u4f3c\u4e4e\u5e26\u7740\u4e9b\u60ed\u6127\u3002 \u3000\u3000\u4ed6\u6bd5\u7adf\u4e5f\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u591a\u5c11\u603b\u6709\u4e9b\u4eba\u6027\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u90a3\u4e48\uff0c\u5927\u54e5\u507f\u65e0\u8bba\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u90fd\u8be5\u5f53\u9762\u5bf9\u6211\u8bf4\u660e\u624d\u662f\uff0c\u53ea\u8981\u6211\u80fd\u505a\u5230\u7684\uff0c \u6211\u4e00\u5b9a\u4f1a\u53bb\u60f3\u6cd5\u5b50\u505a\u5230\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6162\u6162\u5730\u4e3e\u8d77\u9152\u676f\uff0c\u4eff\u4f5b\u8981\u7528\u9152\u676f\u6321\u4f4f\u81ea\u5df1\u7684\u8138\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e3a\u4ed6\u505a\u7684\uff0c\u5b9e\u5728\u5df2\u592a\u591a\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u53ef\u662f\u2014\u2014\u65f6\u95f4\u6709\u65f6\u4f1a\u6539\u53d8\u8bb8\u591a\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u7684\u75db\u82e6\u4e4b\u8272\u66f4\u91cd\uff0c\u9eef\u7136\u9053\uff1a\u6211\u4e5f\u77e5\u9053\u5927\u54e5\u4f60\u5bf9\u6211\u6709\u4e9b\u8bef\u4f1a\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u8bef\u4f1a\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u662f\u8bef\u4f1a\uff0c\u5b8c\u5168\u662f\u8bef\u4f1a\uff0c\u4f46\u6709\u4e9b\u4e8b\uff0c\u5927\u54e5\u4f60\u672c\u4e0d\u8be5\u8bef\u4f1a\u6211\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u76ee\u4e2d\u7a81\u4e5f\u9732\u51fa\u4e86\u4e00\u4e1d\u75db\u82e6\u4e4b\u8272\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u4f46\u4e5f\u6709\u4ef6\u4e8b\u6211\u7edd\u6ca1\u6709 \u8bef\u4f1a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54ea\u4ef6\u4e8b\uff1f \u3000\u3000\u8fd9\u53e5\u8bdd\u95ee\u51fa\u6765\uff0c\u4ed6\u5df2\u540e\u6094\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u5c31\u8be5\u77e5\u9053\u7684\uff0c\u53ef\u6015\u7684\u662f\uff0c\u9f99\u5c0f\u4e91\u8fd9\u5341\u6765\u5c81\u7684\u5b69\u5b50\uff0c\u5c45\u7136\u4e5f\u50cf\u662f\u731c\u51fa\u4e86\u4ed6\u7236\u4eb2\u8981\u8bf4\u7684 \u662f\u4ec0\u4e48\u4e86\uff0c\u5f2f\u7740\u8170\uff0c\u6084\u6084\u7684\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7b11\u9053\uff1a\u6211\u77e5\u9053\u4f60\u8fd9\u4e9b\u5e74\u6765\u4e00\u76f4\u90fd\u5f88\u75db\u82e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u52c9\u5f3a\u9053\uff1a\u5927\u591a\u6570\u4eba\u90fd\u6709\u75db\u82e6\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f46\u4f60\u7684\u75db\u82e6\u6bd4\u522b\u4eba\u90fd\u6df1\u5f97\u591a\uff0c\u4e5f\u91cd\u5f97\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u54e6\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u56e0\u4e3a\u4f60\u5c06\u4f60\u6700\u5fc3\u7231\u7684\u4eba\uff0c\u8ba9\u7ed9\u4e86\u522b\u4eba\u505a\u59bb\u5b50\u3002 \u3000\u3000\u676f\u4e2d\u7684\u9152\u6cfc\u51fa\uff0c\u56e0\u4e3a\u674e\u5bfb\u6b22\u7684\u624b\u5728\u6296\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f46\u4f60\u7684\u75db\u82e6\u8fd8\u4e0d\u591f\u6df1\uff0c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u82e5\u662f\u80af\u727a\u7272\u81ea\u5df1\u6210\u672c\u522b\u4eba\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u81ea \u5df1\u5f88\u4f1f\u5927\uff0c\u8fd9\u79cd\u611f\u89c9\u5c31\u4f1a\u5c06\u4ed6\u7684\u75db\u82e6\u51cf\u8f7b\u3002 \u3000\u3000\u8fd9\u8bdd\u4e0d\u4f46\u5f88\u5c16\u9510\uff0c\u800c\u4e14\u4e5f\u4e0d\u80fd\u8bf4\u6ca1\u9053\u7406\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8fd9\u79cd\u9053\u7406\u5e76\u4e0d\u662f\u7edd\u5bf9\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u624b\u4e5f\u5728\u6296\uff0c\u9053\uff1a\u771f\u6b63\u7684\u75db\u82e6\u662f\u4ec0\u4e48\uff0c\u4e5f\u8bb8\u4f60\u8fd8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4e5f\u8bb8\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u5f53\u4e00\u4e2a\u7537\u4eba\u77e5\u9053\u5014\u7684\u59bb\u5b50\u539f\u6765\u662f\u522b\u4eba\u8ba9\u7ed9\u4ed6\u7684\uff0c\u800c\u4e14\u4ed6\u7684\u59bb\u5b50\u4e00\u76f4\u8fd8\u662f\u5728\u7231 \u7740\u90a3\u4e2a\u4eba\uff0c\u8fd9\u624d\u662f\u6700\u5927\u7684\u75db\u82e6\uff01 \u3000\u3000\u8fd9\u7684\u786e\u662f\u6700\u5927\u7684\u75db\u82e6\u3002 \u3000\u3000\u4e0d\u4f46\u662f\u75db\u82e6\uff0c\u800c\u4e14\u8fd8\u662f\u79cd\u7f9e\u8fb1\u3002 \u3000\u3000\u8fd9\u79cd\u8bdd\u672c\u662f\u7537\u4eba\u6b7b\u4e5f\u4e0d\u80af\u8bf4\u51fa\u6765\u7684\uff0c\u56e0\u4e3a\u8fd9\u79cd\u4e8b\u5bf9\u4ed6\u81ea\u5df1\u7684\u4f24\u5bb3\u5b9e\u5728\u592a\u5927\u3001\u592a\u91cd\uff01 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5fcd\u5fc3\u5bf9\u81ea\u5df1\u5982\u6b64\u7f9e\u8fb1\uff0c\u5982\u6b64\u4f24\u5bb3\u3002 \u3000\u3000\u4f46\u9f99\u5578\u4e91\u73b0\u5728\u5374\u5c06\u8fd9\u79cd\u4e8b\u8bf4\u4e86\u51fa\u6765\uff0c\u5728\u674e\u5bfb\u6b22\u9762\u524d\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u4ece\u9f99\u5578\u4e91\u7684\u8fd9\u53e5\u8bdd\u4e2d\uff0c\u53d1\u73b0\u4e24\u4ef6\u4e8b\uff1a\u7b2c\u4e00\uff1a\u9f99\u5578\u4e91\u7684\u786e\u4e5f\u5f88\u75db\u82e6\uff0c\u800c\u4e14\u75db\u82e6\u4e5f\u5f88\u6df1\uff0c \u6240\u4ee5\u4ed6\u624d\u4f1a\u53d8\uff0c\u53d8\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u82e5\u662f\u6362\u4e86\u522b\u7684\u7537\u4eba\uff0c\u6216\u8bb8\u4e5f\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u89c9\u5f97\u4ed6\u4e5f\u662f\u4e2a\u5f88\u53ef\u601c\u7684\u4eba\u3002 \u3000\u3000\u7b2c\u4e8c\uff1a\u9f99\u5578\u4e91\u65e2\u5df2\u5728\u4ed6\u9762\u524d\u8bf4\u51fa\u4e86\u8fd9\u79cd\u8bdd\uff0c\u53ea\u6015\u5c31\u7edd\u4e0d\u4f1a\u518d\u653e\u8fc7\u4ed6\uff01 \u3000\u3000\u751f\u6b7b\u4e4b\u95f4\uff0c\u674e\u5bfb\u6b22\u672c\u770b\u5f97\u5f88\u6de1\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u80fd\u6b7b\u4e48\uff1f \u3000\u3000\u8bdd\u8bf4\u5f97\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u4f46\u6bcf\u53e5\u8bdd\u90fd\u8bf4\u5f97\u5f88\u6162\uff0c\u800c\u4e14\u6bcf\u53e5\u8bdd\u8bf4\u51fa\u6765\u4e4b\u524d\uff0c\u90fd\u8003\u8651\u5f97\u5f88\u4e45\uff0c\u505c\u987f\u5f97\u5f88\u4e45\u3002 \u3000\u3000\u662f\u9634\u5929\uff0c\u5929\u5f88\u4f4e\u3002 \u3000\u3000\u6240\u4ee5\u867d\u7136\u8fd8\u6ca1\u5230\u638c\u706f\u7684\u65f6\u5019\uff0c\u5929\u8272\u5df2\u4e0d\u77e5\u4e0d\u89c9\u5f88\u6697\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u9762\u8272\u5374\u6bd4\u5929\u8272\u8fd8\u6697\u3002 \u3000\u3000\u4ed6\u4e3e\u8d77\u9152\u676f\uff0c\u53c8\u653e\u4e0b\uff0c\u4e3e\u8d77\uff0c\u518d\u653e\u4e0b\u2014\u2014 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u4e0d\u80fd\u559d\u9152\uff0c\u800c\u662f\u4e0d\u613f\u559d\uff0c\u56e0\u4e3a\u4ed6\u89c9\u5f97\u559d\u9152\u4f1a\u4f7f\u4eba\u53d8\u5f97\u51b2\u52a8\uff0c\u6700\u51b7\u9177\u7684\u4eba\uff0c\u82e5\u662f \u51b2\u52a8\u8d77\u6765\uff0c\u4e5f\u4f1a\u53d8\u5f97\u6709\u4e9b\u611f\u60c5\u4e86\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u9f99\u5578\u4e91\u7ec8\u4e8e\u7f13\u7f13\u9053\uff1a\u4eca\u5929\u6211\u8bf4\u7684\u8bdd\uff0c\u672c\u662f\u4e0d\u8be5\u8bf4\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u5730\u7b11\u4e86\uff0c\u9053\uff1a\u6bcf\u4e2a\u4eba\u5076\u5c14\u90fd\u4f1a\u8bf4\u51fa\u4e00\u4e9b\u4ed6\u4e0d\u8be5\u8bf4\u7684\u8bdd\uff0c\u5426\u5219\u4ed6\u5c31\u4e0d\u662f\u4eba\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4eca\u5929\u6211\u8bf7\u4f60\u6765\uff0c\u4e5f\u4e0d\u662f\u4e3a\u4e86\u8981\u8bf4\u8fd9\u4e9b\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u53ef\u77e5\u9053\u6211\u8bf7\u4f60\u6765\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b2c\u4e00\u6b21\u9732\u51fa\u4e86\u60ca\u8bb6\u4e4b\u8272\uff0c\u52a8\u5bb9\u9053\uff1a\u4f60\u77e5\u9053\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u91cd\u590d\u4e86\u4e00\u53e5\uff0c\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u7b49\u9f99\u5578\u4e91\u518d\u95ee\uff0c\u63a5\u7740\u9053\uff1a\u4f60\u8ba4\u4e3a\u5174\u4e91\u5e84\u56ed\u4e2d\u771f\u6709\u5b9d\u85cf\uff1f \u3000\u3000\u9f99\u5578\u4e91\u8fd9\u6b21\u8003\u8651\u5f97\u66f4\u4e45\uff0c\u624d\u56de\u7b54\u4e86\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u662f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u4f60\u8ba4\u4e3a\u6211\u77e5\u9053\u5b9d\u85cf\u5728\u54ea\u91cc\uff1f \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f60\u5e94\u8be5\u77e5\u9053\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u6211\u8fd9\u4eba\u4e00\u5411\u6709\u4e2a\u6bdb\u75c5\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u6bdb\u75c5\uff1f\u4ec0\u4e48\u6bdb\u75c5\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u7684\u6bdb\u75c5\u5c31\u662f\u4e0d\u8be5\u77e5\u9053\u7684\u4e8b\u6211\u5168\u77e5\u9053\uff0c\u8be5\u77e5\u9053\u7684\u6211\u53cd\u800c\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u5634\u95ed\u4e0a\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u5176\u5b9e\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u8fd9\u4ef6\u4e8b\u4ece\u5934\u5230\u5c3e\u5c31\u662f\u4e2a\u9a97\u5c40\u2014\u2014 \u3000\u3000\u9f99\u5578\u4e91\u7a81\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u6211\u76f8\u4fe1\u4f60\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u8bf4\u8c0e\u3002 \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u9053\uff1a\u82e5\u8bf4\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4e00\u4e2a\u6211\u53ef\u4ee5\u4fe1\u4efb\u7684\u4eba\uff0c\u90a3\u4e2a\u4eba\u5c31\u662f\u4f60\uff0c\u82e5\u8bf4 \u8fd9\u4e16\u4e0a\u6211\u8fd8\u6709\u4e00\u4e2a\u670b\u53cb\uff0c\u90a3\u4eba\u4e5f\u662f\u4f60\uff01\u6211\u8bf4\u7684\u4efb\u4f55\u8bdd\u4e5f\u8bb8\u90fd\u662f\u5047\u7684\uff0c\u4f46\u8fd9\u53e5\u8bdd\u5374\u7edd\u4e0d\u662f\u9a97\u4f60 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5728\u51dd\u6ce8\u7740\u4ed6\uff0c\u957f\u957f\u53f9\u606f\u7740\u9053\uff1a\u6211\u4e5f\u76f8\u4fe1\u4f60\uff0c\u56e0\u4e3a\u2014\u2014 \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u53c8\u4e0d\u505c\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u7b49\u4ed6\u54b3\u5b8c\u4e86\uff0c\u9f99\u5578\u4e91\u624d\u66ff\u4ed6\u63a5\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u4f60\u76f8\u4fe1\u6211\uff0c\u56e0\u4e3a\u4f60\u77e5\u9053\u4f60\u5df2\u6ca1\u6709\u88ab\u6211\u5229\u7528\u7684 \u4ef7\u503c\uff0c\u6211\u5df2\u4e0d\u5fc5\u518d\u9a97\u4f60\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4ee5\u6c89\u9ed8\u56de\u7b54\u4e86\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u5730\u8e31\u4e86\u4e24\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5f88\u9759\uff0c\u4ed6\u7684\u811a\u6b65\u58f0\u5374\u8d8a\u6765\u8d8a\u91cd\uff0c\u663e\u89c1\u4ed6\u7684\u5fc3\u4e5f\u6709\u4e9b\u4e0d\u5b89\u2014\u2014\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u662f\u6545\u610f\u8ba9 \u674e\u5bfb\u6b22\u89c9\u5f97\u4ed6\u7684\u5fc3\u5f88\u4e0d\u5b89\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u7a81\u7136\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u505c\u5728\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u9053\uff1a\u4f60\u4e00\u5b9a\u8ba4\u4e3a\u6211\u4f1a\u6740\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u795e\u60c5\u5f88\u5e73\u9759\uff0c\u5e73\u9759\u5f97\u4ee4\u4eba\u65e0\u6cd5\u60f3\u50cf\uff0c\u6de1\u6de1\u9053\uff1a\u65e0\u8bba\u4f60\u600e\u4e48\u6837\u505a\uff0c\u6211\u90fd\u4e0d\u602a\u4f60\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4f46\u6211\u7edd\u4e0d\u4f1a\u6740\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u6211\u77e5\u9053\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u4e0d\u9519\uff0c\u4f60\u5f53\u7136\u77e5\u9053\uff0c\u4f60\u4e00\u5411\u5f88\u4e86\u89e3\u6211\u3002 \u3000\u3000\u4ed6\u7a81\u53c8\u53d8\u5f97\u6709\u4e9b\u6fc0\u52a8\uff0c\u63a5\u7740\u9053\uff1a\u56e0\u4e3a\u6211\u7eb5\u7136\u6740\u4e86\u4f60\uff0c\u4e5f\u633d\u4e0d\u56de\u5979\u7684\u5fc3\uff0c\u53ea\u6709\u4ee4\u5979\u66f4\u6068\u6211 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u4eba\u751f\u4e2d\u672c\u6709\u4e9b\u4e8b\u662f\u8c01\u4e5f\u65e0\u53ef\u5948\u4f55\u7684\u3002 \u3000\u3000\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u8fd9\u56db\u5b57\u770b\u6765\u867d\u5e73\u6de1\uff0c\u5176\u5b9e\u5374\u662f\u4eba\u751f\u4e2d\u6700\u5927\u7684\u60b2\u54c0\uff0c\u6700\u5927\u7684\u75db\u82e6\u3002 \u3000\u3000\u9047\u7740\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u6839\u672c\u65e0\u6cd5\u6323\u624e\uff0c\u65e0\u6cd5\u594b\u6597\uff0c\u65e0\u6cd5\u53cd\u6297\uff0c\u5c31\u7b97\u4f60\u5c06\u81ea\u5df1\u7684\u8089\u4f53\u5272\u88c2\uff0c\u5c06 \u81ea\u5df1\u7684\u5fc3\u4e5f\u5272\u6210\u788e\u7247\uff0c\u8fd8\u662f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u5c31\u7b97\u4f60\u5b81\u53ef\u8eab\u5316\u6210\u7070\uff0c\u6c38\u5815\u9b3c\u72f1\uff0c\u8fd8\u662f\u633d\u4e0d\u56de\u4f60\u6240\u5931\u53bb\u7684\u2014\u2014\u4e5f\u8bb8\u4f60\u6839\u672c\u5c31\u6c38\u8fdc\u672a\u66fe\u5f97 \u5230\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u62f3\u7d27\u63e1\uff0c\u58f0\u97f3\u4e5f\u5636\u54d1\uff0c\u9053\uff1a\u6211\u867d\u4e0d\u6740\u4f60\uff0c\u4e5f\u4e0d\u80fd\u653e\u4f60\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u56e0\u4e3a\u6211\u8fd8\u6709\u88ab\u4f60\u5229\u7528\u7684\u4ef7\u503c\u3002 \u3000\u3000\u4f46\u8fd9\u53e5\u8bdd\u4ed6\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u65e0\u8bba\u9f99\u5578\u4e91\u5982\u4f55\u4f24\u5bb3\u4ed6\uff0c\u51fa\u5356\u4ed6\uff0c\u4f46\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u4f24\u5bb3\u5230\u9f99\u5578\u4e91\u7684\u8bdd\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u62f3\u53cd\u800c\u63e1\u5f97\u66f4\u7d27\uff0c\u56e0\u4e3a\u53ea\u6709\u5728\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u4ed6\u624d\u4f1a\u89c9\u5f97\u81ea\u5df1\u7684\u6e3a\u5c0f\uff0c\u81ea\u5df1\u7684\u5351 \u8d31\u3002 \u3000\u3000\u6240\u4ee5\u674e\u5bfb\u6b22\u90a3\u79cd\u4f1f\u5927\u7684\u53cb\u60c5\u975e\u4f46\u6ca1\u6709\u611f\u52a8\u4ed6\uff0c\u53cd\u800c\u4ed6\u66f4\u6124\u6012\u3002 \u3000\u3000\u4ed6\u7d27\u63e1\u7740\u62f3\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u9053\uff1a\u6211\u8981\u5e26\u4f60\u53bb\u89c1\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u65e9\u5c31\u60f3\u89c1\u4f60\u4e86\uff0c\u4f60\u2014\u2014 \u4f60\u6216\u8bb8\u4e5f\u5f88\u60f3\u89c1\u4ed6\u3002 \u3000\u3000\u5c4b\u5b50\u5f88\u5927\u3002 \u3000\u3000\u8fd9\u4e48\u5927\u7684\u5c4b\u5b50\uff0c\u53ea\u6709\u4e00\u4e2a\u7a97\u6237\uff0c\u5f88\u5c0f\u7684\u7a97\u6237\uff0c\u79bb\u5730\u5f88\u9ad8\u3002 \u3000\u3000\u7a97\u6237\u662f\u5f00\u7740\u7684\uff0c\u770b\u4e0d\u5230\u7a97\u5916\u7684\u666f\u8272\u3002 \u3000\u3000\u95e8\u4e5f\u5f88\u5c0f\uff0c\u80a9\u7a0d\u5bbd\u7684\u4eba\uff0c\u5c31\u53ea\u80fd\u4fa7\u7740\u8eab\u5b50\u51fa\u5165\u3002 \u3000\u3000\u95e8\u4e5f\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u5899\u4e0a\u6f06\u7740\u767d\u8272\u7684\u6f06\uff0c\u6f06\u5f97\u5f88\u539a\uff0c\u4eff\u4f5b\u4e0d\u613f\u4eba\u770b\u51fa\u8fd9\u5899\u662f\u77f3\u58c1\uff0c\u662f\u571f\uff0c\u8fd8\u662f\u94dc\u94c1\u6240\u505a\u3002 \u3000\u3000\u89d2\u843d\u91cc\u6709\u4e24\u5f20\u5e8a\u3002 \u3000\u3000\u6728\u5e8a\u3002 \u3000\u3000\u5e8a\u4e0a\u7684\u88ab\u8925\u5f88\u5e72\u51c0\uff0c\u5374\u5f88\u7b80\u6734\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u5c4b\u91cc\u5c31\u53ea\u6709\u4e00\u5f20\u5f88\u5927\u7684\u684c\u5b50\u3002 \u3000\u3000\u684c\u4e0a\u5806\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u5e10\u518c\u3001\u5377\u5b97\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6b63\u7ad9\u5728\u684c\u5b50\u524d\u7ffb\u9605\u7740\uff0c\u4e0d\u65f6\u7528\u6731\u7b14\u5728\u5377\u5b97\u4e0a\u52fe\u753b\u3001\u6279\u53d1\uff0c\u5634\u91cc\u5076\u5c14\u4f1a\u9732\u51fa\u4e00\u4e1d\u5f97 \u610f\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4ed6\u662f\u7ad9\u7740\u7684\uff01 \u3000\u3000\u56e0\u4e3a\u5c4b\u91cc\u6ca1\u6709\u6905\u5b50\uff0c\u8fde\u4e00\u5f20\u6905\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u8ba4\u4e3a\u4e00\u4e2a\u4eba\u53ea\u8981\u5750\u4e0b\u6765\uff0c\u5c31\u4f1a\u4ee4\u81ea\u5df1\u7684\u7cbe\u795e\u677e\u5f1b\uff0c\u4e00\u4e2a\u4eba\u7684\u7cbe\u795e\u82e5\u677e\u5f1b\uff0c\u5c31\u5bb9\u6613\u9020\u6210 \u9519\u8bef\u3002 \u3000\u3000\u4e00\u70b9\u5fae\u5c0f\u7684\u9519\u8bef\uff0c\u5c31\u53ef\u80fd\u4ee4\u6570\u4ef6\u4e8b\u5931\u8d25\u2014\u2014\u8fd9\u6b63\u5982\u5824\u9632\u4e0a\u53ea\u8981\u6709\u4e00\u4e2a\u5f88\u5c0f\u7684\u88c2\u53e3\uff0c\u5c31\u53ef \u80fd\u5d29\u6e83\u3002 \u3000\u3000\u4ed6\u7684\u7cbe\u795e\u6c38\u677e\u5f1b\u3002 \u3000\u3000\u4ed6\u6c38\u65e0\u9519\u8bef\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u5931\u8d25\u3002 \u3000\u3000\u8fd8\u6709\u4e2a\u4eba\u7ad9\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u8eab\u5b50\u7ad9\u5f97\u66f4\u76f4\u3001\u66f4\u633a\uff0c\u5c31\u50cf\u662f\u67aa\u6746\u3002 \u3000\u3000\u4ed6\u5c31\u8fd9\u6837\u7ad9\u7740\uff0c\u4e5f\u4e0d\u77e5\u7ad9\u4e86\u591a\u4e45\uff0c\u8fde\u4e00\u6839\u624b\u6307\u90fd\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u98de\u6765\u4e00\u4e2a\u868a\u5b50\uff0c\u5728\u4ed6\u773c\u524d\u98de\u6765\u98de\u53bb\uff0c\u6253\u7740\u8f6c\u3002 \u3000\u3000\u4ed6\u773c\u775b\u8fde\u7728\u90fd\u672a\u7728\u3002 \u3000\u3000\u868a\u5b50\u4eea\u5728\u4ed6\u9f3b\u5c16\u4e0a\uff0c\u5f00\u59cb\u5438\u8840\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u4f3c\u5df2\u5b8c\u5168\u9ebb\u6728\uff0c\u65e2\u4e0d\u77e5\u75db\u75d2\uff0c\u4e5f\u4e0d\u77e5\u54c0\u4e50\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e3a\u4ec0\u4e48\u6d3b\u7740\u7684\u3002 \u7b2c\u4e94\u5341\u56db\u7ae0\u3000\u4ea4\u6362>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u56db\u7ae0\u3000\u4ea4\u6362 \u3000\u3000\u8fd9\u4e24\u4eba\u81ea\u7136\u5c31\u662f\u8346\u65e0\u547d\u548c\u4e0a\u5b98\u91d1\u8679\u3002\u50cf\u4ed6\u4eec\u8fd9\u6837\u7684\u4eba\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u8fd8\u627e\u4e0d\u51fa\u7b2c\u4e09\u4e2a\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u58f0\u540d\u6700\u54cd\uff0c\u52bf\u529b\u6700\u5927\uff0c\u8d22\u529b\u4e5f\u6700\u96c4\u539a\u7684\u201c\u91d1\u94b1\u5e2e\u201d\u5e2e\u4f4f\u6240\u7adf\u5982\u6b64\u7c97\u964b\uff0c\u751f\u6d3b\u7adf\u5982 \u6b64\u7b80\u6734\u3002\u8fd9\u7b80\u76f4\u662f\u8c01\u4e5f\u65e0\u6cd5\u60f3\u8c61\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u91d1\u94b1\u5728\u4ed6\u773c\u4e2d\u53ea\u4e0d\u8fc7\u662f\u79cd\u5de5\u5177\uff0c\u5973\u4eba\u4e5f\u662f\u5de5\u5177\u3002\u4e16\u4e0a\u6240\u6709\u7684\u4eab\u53d7\u5728\u4ed6\u773c\u4e2d\u90fd\u662f\u79cd\u5de5 \u5177\uff0c\u4ed6\u5b8c\u5168\u4e0d\u5c51\u4e00\u987e\u3002\u4ed6\u552f\u4e00\u7684\u7231\u597d\u5c31\u662f\u6743\u529b\u3002\u6743\u529b\uff0c\u9664\u4e86\u6743\u529b\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u3002\u4ed6\u4e3a \u6743\u529b\u800c\u751f\uff0c\u751a\u81f3\u4e5f\u53ef\u4ee5\u4e3a\u6743\u5229\u800c\u6b7b\uff1a \u3000\u3000\u9759\u3002\u9664\u4e86\u7ffb\u52a8\u4e66\u518c\u65f6\u53d1\u51fa\u7684\u201c\u6c99\u6c99\u201d\u58f0\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u522b\u7684\u58f0\u97f3\u3002\u706f\u5df2\u71c3\u8d77\u3002\u4ed6\u4eec\u5728\u8fd9\u91cc \uff0c\u5df2\u4e0d\u77e5\u5de5\u4f5c\u4e86\u591a\u4e45\uff0c\u7ad9\u4e86\u591a\u4e45\uff0c\u53ea\u77e5\u9053\u7a97\u5916\u7684\u5de8\u5df2\u7531\u6697\u800c\u660e\uff0c\u53c8\u7531\u660e\u800c\u6697\u3002\u4ed6\u4eec\u4f3c\u4e4e\u6c38\u8fdc \u4e0d\u77e5\u9053\u75b2\u5026\uff0c\u4e5f\u89c9\u4e0d\u51fa\u9965\u997f\u3002\u8fd9\u65f6\u95e8\u5916\u7a81\u7136\u6709\u4e86\u6572\u95e8\u58f0\u3002\u53ea\u6709\u4e00\u58f0\uff0c\u5f88\u8f7b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u624b\u6ca1\u6709\u505c\uff0c\u4e5f\u6ca1\u6709\u62ac\u5934\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u95e8\u5916\u5e94\u58f0\u9053\uff1a\u201c\u4e00\u4e03\u4e5d\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u95e8\u5916\u4eba\u9053\uff1a\u201c\u6709\u4eba\u6c42\u89c1\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u95e8\u5916\u4eba\u9020\uff1a\u201c\u4ed6\u4e0d\u80af\u8bf4\u51fa\u59d3\u540d\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e8b\u6c42\u89c1\uff1f\u201d \u3000\u3000\u95ee\u5916\u4eba\u9053\uff1a\u201c\u4ed6\u4e5f\u8981\u7b49\u89c1\u5230\u5e2e\u4e3b\u4e4b\u9762\u65f6\u624d\u80af\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u9053\uff1a\u201c\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u95e8\u5916\u4eba\u9020\uff1a\u201c\u5c31\u5728\u524d\u9662\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u624b\u672a\u505c\uff0c\u5934\u672a\u62ac\uff0c\u9053\uff1a\u201c\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u95e8\u5916\u4eba\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u53c8\u95ee\u9053\uff1a\u201c\u4eba\u662f\u8c01\u5e26\u6765\u7684\uff1f\u201d \u3000\u3000\u95e8\u5916\u4eba\u9053\uff1a\u201c\u7b2c\u516b\u8235\u4e3b\u5411\u677e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8fde\u5411\u677e\u4e00\u9f50\u67401\u201d \u3000\u3000\u95e8\u5916\u4eba\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u53bb\uff01\u201d \u3000\u3000\u8fd9\u4e24\u5b57\u8bf4\u51fa\uff0c\u4ed6\u7684\u4eba\u5df2\u5728\u95e8\u53e3\uff0c\u62c9\u5f00\u95e8\uff0c\u4e00\u95ea\u800c\u6ca1\u3002\u8981\u6740\u4eba\uff0c\u8346\u65e0\u547d\u4ece\u4e0d\u843d\u540e\uff0c\u4f55\u51b5\uff0c \u5411\u677e\u53f7\u79f0\u201c\u98ce\u96e8\u6d41\u661f\u3001\u4e00\u53cc\u6d41\u661f\u7761\u5728\u201c\u5175\u5668\u8c31\u201d\u4e2d\u6392\u540d\u5341\u4e5d\uff0c\u8981\u6740\u4ed6\u5e76\u4e0d\u5bb9\u6613\u3002\u6765\u627e\u4e0a\u5b98\u91d1 \u8679\u7684\u662f\u8c01\uff1f\u627e\u4ed6\u6709\u4ec0\u4e48\u4e8b\uff1f\u4e0a\u5b98\u91d1\u8679\u7adf\u5b8c\u5168\u4e0d\u5728\u610f\uff0c\u8fd9\u4eba\u7adf\u8fde\u4e00\u4e1d\u597d\u5947\u5fc3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4eba\u5b9e\u5728\u5df2\u6ca1\u6709\u4eba\u6027\u3002 \u3000\u3000\u4ed6\u7684\u5934\u8fd8\u662f\u672a\u62ac\uff0c\u624b\u8fd8\u662f\u672a\u505c\u3002 \u3000\u3000\u95e8\u5f00\uff0c\u8346\u65e0\u547d\u4e00\u95ea\u800c\u5165\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5e76\u6ca1\u6709\u95ee\u201c\u6b7b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u8346\u65e0\u547d\u6740\u4eba\u4ece\u4e0d\u5931\u5b57\u3002\uff1a \u3000\u3000\u4ed6\u53ea\u662f\u8bf4\uff1a\u201c\u53bb\uff01\u5411\u677e\u82e5\u672a\u8fd8\u624b\uff0c\u9001\u4ed6\u5bb6\u5c5e\u9ec4\u91d1\u4e07\u4e24\uff0c\u5411\u677e\u82e5\u8fd8\u624b\uff0c\u706d\u4ed6\u6ee1\u95e8\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6740\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fd9\u624d\u970d\u7136\u62ac\u5934\uff0c\u76ee\u5149\u5200\u4e00\u822c\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u8346\u65e0\u547d\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u9053\uff1a\u201c\u56f0\u4e3a\u4ed6\u5e26\u6765\u7684\u4eba\uff0c\u6211\u4e0d\u80fd\u6740\uff0c\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5389\u58f0\u9053\uff1a\u201c\u4e16\u4eba\u7686\u53ef\u6740\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u80fd\u6740\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u4e0d\u6740\u5b69\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4f3c\u4e5f\u6014\u4f4f\uff0c\u6162\u6162\u7684\u653e\u4e0b\u7b14\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u8981\u89c1\u6211\u7684\u53ea\u662f\u4e2a\u5b69\u5b50\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u662f\u4e2a\u600e\u4e48\u6837\u7684\u5b69\u5b50\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u4e2a\u6b8b\u5e9f\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u5c04\u51fa\u4e86\u5149\uff0c\u6c89\u541f\u7740\uff0c\u7ec8\u4e8e\u9053\uff1a\u201c\u5e26\u4ed6\u8fdb\u6765\uff01\u201d \u3000\u3000\u5c45\u7136\u4f1a\u6709\u5b69\u5b50\u6765\u6c42\u89c1\u4e0a\u5b98\u91d1\u8679\uff0c\u8fd9\u79cd\u4e8b\u7b80\u76f4\u8fdd\u4e0a\u5b98\u91d1\u8679\u81ea\u5df1\u90fd\u65e0\u6cd5\u76f8\u4fe1\u2014\u2014\u8fd9\u5b69\u5b50\u82e5\u975e \u592a\u5927\u80c6\uff0c\u5c31\u662f\u592a\u75af\u72c2\u3002 \u3000\u3000\u4f46\u6765\u7684\u786e\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u8840\u8272\u3002 \u3000\u3000\u4ed6\u76ee\u4e2d\u4e5f\u6ca1\u6709\u5b69\u5b50\u4eec\u7684\u660e\u4eae\u5149\u91c7\uff0c\u76ee\u5149\u5446\u6ede\u800c\u6df1\u6c89\u3002 \u3000\u3000\u4ed6\u884c\u8d70\u5f97\u5f88\u6162\uff0c\u80cc\u4e5f\u662f\u4f5d\u507b\u7740\u7684\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u8001\u4eba\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u7adf\u662f\u9f99\u5c0f\u4e91\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u89c1\u5230\u9f99\u5c0f\u4e91\u8fd9\u6837\u7684\u5b69\u5b50\u90fd\u5fcd\u4e0d\u4f4f\u8981\u591a\u77a7\u51e0\u773c\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u5c31\u50cf\u662f\u5200\u950b\u822c\u5c04\u5728\u9f99\u5c0f\u4e91\u8138\u4e0a\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u89c1\u5230\u4e0a\u5b98\u91d1\u8679\u8fd9\u79cd\u950b\u5229\u903c\u4eba\u7684\u76ee\u5149\uff0c\u7eb5\u4e0d\u53d1\u6296\uff0c\u4e5f\u4f1a\u5413\u5f97\u4e24\u817f\u53d1\u8f6f\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765 \u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u5374\u662f\u4f8b\u5916\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u8d70\u8fdb\u6765\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u665a\u8f88\u9f99\u5c0f\u4e91\uff0c\u53c2\u89c1\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u9f99\u5c0f\u4e91\uff1f\u9f99\u5578\u4e91\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5bb6\u7236\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u662f\u4f60\u7236\u4eb2\u53eb\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u4e3a\u4f55\u4e0d\u6765\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5bb6\u7236\u82e5\u6765\u6c42\u89c1\uff0c\u975e\u4f46\u672a\u80fd\u89c1\u5e2e\u4e3b\u4e4b\u9762\uff0c\u800c\u4e14\u9010\u53ef\u80fd\u6709\u6740\u8eab\u4e4b\u7978\u3002\u201d\u4e0a\u5b98\u91d1 \u8679\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u4e0d\u4f1a\u6740\u4f60\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4e09\u5c3a\u7ae5\u5b50\uff0c\u6027\u547d\u65e9\u5df2\u60ac\u4e8e\u5e2e\u4e3b\u6307\u638c\u4e4b\u95f4\uff0c\u5e2e\u4e3b\u975e\u4e0d\u80fd\u6740\uff0c\u4e43\u4e0d\u5c51\u6740\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9762\u8272\u5c45\u7136\u7f13\u548c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u867d\u5c0f\uff0c\u8eab\u4f53\u8d62\u5f31\uff0c\u80c6\u5b50\u5012\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u6709\u6240\u6c42\uff0c\u65e0\u8bba\u8c01\u7684\u80c6\u5b50\u90fd\u4f1a\u5927\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8bf4\u5f97\u597d\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u56de\u5934\u5411\u8346\u65e0\u547d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ea\u542c\u4ed6\u8bf4\u8bdd\uff0c\u80fd\u542c\u5f97\u51fa\u4ed6\u662f\u4e2a\u5b69\u5b50\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u867d\u7136\u5782\u7740\u5934\uff0c\u5374\u4e00\u76f4\u5728\u7559\u610f\u7740\u4ed6\u4eec\u7684\u8868\u60c5\uff0c\u5bf9\u8fd9\u4e24\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\u4f3c\u4e4e\u5f88\u611f\u5174\u8da3\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7ec8\u4e8e\u5f00\u4e86\u53e3\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u8bf4\u8bdd\uff0c\u662f\u4f60\u6700\u5927\u7684\u957f\u5904\uff0c\u4e0d\u542c\u4eba\u8bf4\u8bdd\uff0c\u5374\u53ef\u80fd\u662f\u4f60 \u7684\u81f4\u547d\u4f24\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u8fd9\u6b21\u7d22\u6027\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u4e0a\u5b98\u91d1\u8679\u624d\u53e3\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u6c42\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u6bcf\u4ef6\u4e8b\u90fd\u6709\u5f88\u591a\u79cd\u8bf4\u6cd5\uff0c\u665a\u8f88\u672c\u4e5f\u53ef\u5c06\u6b64\u4e8b\u8bf4\u5f97\u59d4\u5a49\u4e9b\uff0c\u4f46\u5e2e\u4e3b\u65e5\u7406\u4e07\u673a \uff0c\u665a\u8f88\u4e0d\u6562\u591a\u6270\uff0c\u53ea\u80fd\u9009\u62e9\u6700\u76f4\u63a5\u7684\u8bf4\u6cd5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5bf9\u4ed8\u8bf4\u8bdd\u55dc\u55d3\u7684\u4eba\uff0c\u6211\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\uff0c\u90a3\u5c31\u662f\u5c06\u4ed6\u7684\u820c\u5934\u5272\u4e0b\u6765 \u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u665a\u8f88\u6b64\u6765\uff0c\u53ea\u662f\u8981\u548c\u5e2e\u4e3b\u8c08\u4e00\u7b14\u4ea4\u6613\uff0c\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ea4\u6613\uff1f\u201d \u3000\u3000\u4ed6\u8138\u8272\u66f4\u51b7\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ee5\u524d\u4e5f\u6709\u4eba\u548c\u6211\u8c08\u8fc7\u4ea4\u6613\uff0c\u4f60\u53ef\u613f\u77e5\u9053\u6211\u5bf9\u4ed8\u4ed6\u4eec\u7684\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u665a\u8f88\u5728\u542c\u7740\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u4e5f\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\uff0c\u4e71\u5200\u5206\u5c38\uff01\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u795e\u8272\u4e0d\u53d8\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f46\u8fd9\u4ea4\u6613\u5374\u548c\u522b\u4eba\u4e0d\u540c\uff0c\u5426\u5219\u665a\u8f88\u4e5f\u4e0d\u6562\u6765\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ea4\u6613\u5c31\u662f\u4ea4\u6613\uff0c\u6709\u4f55\u4e0d\u540c\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u8fd9\u4ea4\u6613\u5bf9\u5e2e\u4e3b\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5e2e\u4e3b\u5a01\u9547\u5929\u4e0b\uff0c\u5bcc\u53ef\u654c\u56fd\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u4e1c\u897f\uff0c\u5e2e\u4e3b\u5177\u53ef\u4e88\u53d6\u4e88\u6c42\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\u3002\u201c\u786e\u662f\u5982\u6b64\uff0c\u6240\u4ee5\u6211\u6839\u672c\u4e0d\u5fc5\u548c\u522b\u4eba\u8c08\u4ea4\u6613\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4f46\u4e16\u4e0a\u8fd8\u662f\u6709\u6837\u4e1c\u897f\uff0c\u5e2e\u4e3b\u672a\u5fc5\u80fd\u5f97\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u54e6\uff1f\u201d\u3001 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u8fd9\u6837\u4e1c\u897f\u672c\u8eab\u4ef7\u503c\u4e5f\u8bb8\u5e76\u4e0d\u9ad8\uff0c\u4f46\u5728\u5e2e\u4e3b\u8bf4\u6765\uff0c\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u56e0\u4e3a\u4e16\u4e0a\u53ea\u6709\u5f97\u4e0d\u5230\u7684\u4e1c\u897f\uff0c\u624d\u6700\u73cd\u8d35\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u7684\u547d\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u6f20\u7684\u76ee\u5149\u7a81\u7136\u53d8\u5f97\u70bd\u70ed\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u7684\u547d\u5df2\u5728\u6211\u4eec\u638c\u63e1\u4e4b\u4e2d\uff0c\u53ea\u8981\u5e2e\u4e3b\u613f\u610f\uff0c\u665a\u8f88\u968f\u65f6\u53ef\u5c06\u4ed6\u5949\u4e0a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u6c89\u9ed8\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u7b49\u5230\u4ed6\u70bd\u70ed\u7684\u76ee\u5149\u53c8\u51b7\u6f20\uff0c\u4ed6\u624d\u6de1\u6de1\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u4f55\u8db3\u9053\u54c9\uff0c\u6211\u6839\u672c\u5c31 \u4ece\u672a\u5c06\u4ed6\u653e\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u665a\u8f88\u544a\u9000\u3002\u201d \u3000\u3000\u4ed6\u518d\u4e5f\u4e0d\u8bf4\u7b2c\u4e8c\u53e5\u8bdd\uff0c\u957f\u80c0\u4e00\u63d6\uff0c\u8f6c\u8fc7\u8eab\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u9992\uff0c\u5374\u7edd\u672a\u56de\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e5f\u6ca1\u6709\u518d\u77a7\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u6162\u9992\u7684\u8d70\u5230\u95e8\u53e3\uff0c\u62c9\u5f00\u4e86\u95e8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u9053\uff1a\u201c\u6162\u7740\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u76ee\u4e2d\u9732\u51fa\u4e00\u4e1d\u5f97\u610f\u4e4b\u8272\uff0c\u4f46\u7b49\u4ed6\u56de\u8fc7\u5934\u65f6\uff0c\u76ee\u5149\u5df2\u53c8\u53d8\u5f97\u606d\u8c28\u800c\u5446\u6ede\uff0c\u8eac\u8eab\u9053\uff1a \u201c\u5e2e\u4e3b\u8fd8\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5e76\u6ca1\u6709\u770b\u4ed6\uff0c\u53ea\u6709\u51dd\u6ce8\u7740\u6848\u524d\u7684\u70db\u706b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u60f3\u4ee5\u674e\u5bfb\u6b22\u7684\u547d\u6765\u6362\u4ec0\u4e48 \uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5bb6\u7236\u4e45\u6155\u5e2e\u4e3b\u58f0\u540d\uff0c\u53ea\u6068\u65e0\u7f18\u8bc6\u8346\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u662f\u5e9f\u8bdd\uff0c\u6211\u53ea\u60f3\u542c\u4f60\u8981\u6c42\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5bb6\u7236\u4f46\u6c42\u80fd\u5728\u5929\u4e0b\u82f1\u96c4\u9762\u524d\uff0c\u4e0e\u5e2e\u4e3b\u7ed3\u4e3a\u516b\u62dc\u4e4b\u4ea4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u7a81\u53c8\u5c04\u51fa\u6012\u706b\uff0c\u4f46\u77ac\u5373\u5e73\u606f\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u770b\u6765\u9f99\u5578\u4e91\u5012\u4e5f\u4e0d\u6127\u662f\u4e2a\u806a\u660e\u4eba \uff0c\u53ea\u53ef\u60dc\u8fd9\u4ef6\u4e8b\u5374\u505a\u5f97\u592a\u7b28\u4e86\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u8fd9\u79cd\u505a\u6cd5\u7684\u786e\u5f88\u7b28\uff0c\u4f46\u6700\u7b28\u7684\u6cd5\u5b50\uff0c\u5f80\u5f80\u6700\u6709\u6548\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u8fd9\u4ea4\u6613\u80fd\u8c08\u6210\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u82e5\u65e0\u628a\u63e1\uff0c\u665a\u8f88\u4f55\u5fc5\u5192\u6b7b\u800c\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u9f99\u5578\u4e91\u53ea\u6709\u4f60\u8fd9\u4e00\u4e2a\u72ec\u5b50\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4ed6\u5c31\u4e0d\u8be5\u8981\u4f60\u6765\u7684\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u8fd9\u53ea\u56e0\u82e5\u662f\u6362\u4e86\u522b\u4eba\u524d\u6765\uff0c\u6839\u672c\u65e0\u6cd5\u89c1\u5230\u5e2e\u4e3b\u4e4b\u9762\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u4eec\u672c\u662f\u4ea4\u6613\u7684\u4e70\u4e3b\uff0c\u4f46\u4f60\u4e00\u6765\uff0c\u60c5\u51b5\u5c31\u53d8\u4e86\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5e2e\u4e3b\u8ba4\u4e3a\u53ef\u4ee5\u7528\u6211\u6765\u8981\u80c1\u5bb6\u7236\uff0c\u903c\u4ed6\u4ea4\u51fa\u674e\u5bfb\u6b22\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5e2e\u4e3b\u7d20\u6709\u77e5\u4eba\u4e4b\u660e\uff0c\u4f46\u5bf9\u5bb6\u7236\uff0c\u5374\u770b\u9519\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u7b11\u9053\uff1a\u4e00\u96be\u9053\u4ed6\u5b81\u53ef\u8ba9\u6211\u6740\u4e86\u4f60\uff0c\u4e5f\u4e0d\u80af\u4ea4\u51fa\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u662f\u4eba\uff0c\u4f46\u4eba\u5374\u6709\u5f88\u591a\u79cd\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ed6\u662f\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5bb6\u7236\u548c\u5e2e\u4e3b\u662f\u540c\u6837\u7684\u4e00\u79cd\u4eba\uff0c\u4e3a\u4e86\u8fbe\u5230\u76ee\u7684\uff0c\u4e0d\u62e9\u4e00\u5207\u624b\u6bb5\uff0c\u4e5f\u4e0d\u60dc\u727a\u7272 \u4e00\u5207\u3002\u201d\uff1a \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u5634\u95ed\u4e0a\u4e86\uff0c\u95ed\u6210\u4e00\u6761\u7ebf\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u7f13\u7f13\u9053\uff1a\u201c\u8fd1\u4e8c\u5341\u5e74\u6765\uff0c\u5df2\u6ca1\u6709\u4eba\u6562\u5728\u6211\u9762\u524d\u8bf4\u8fd9\u79cd\u8bdd\u4e86\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5e2e\u4e3b\u662f\u8fd9\u79cd\u4eba\uff0c\u662f\u4ee5\u665a\u8f88\u624d\u6562\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u624d\u80fd\u6253\u52a8\u5e2e\u4e3b\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u7b54\u5e94\uff0c\u4f60\u4eec\u96be\u9053\u5c31\u8981\u653e\u4e86\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u4ed6\u6740\u4e86\u4f60\u4eec\u590d\u4ec7\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4ed6\u662f\u53e6\u4e00\u79cd\u4eba\uff0c\u7edd\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u82e5\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff0c\u906d\u9047\u4e5f\u4e0d\u4f1a\u6709\u4eca\u65e5\u60b2\u60e8\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u7eb5\u7136\u653e\u4e86\u4ed6\uff0c\u53c8\u600e\u77e5\u6211\u4e0d\u80fd\u4eb2\u624b\u6740\u4ed6\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u6de1\u6de1\u9053\uff1a\u201c\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fde\u6211\u4e5f\u8eb2\u4e0d\u8fc7\u4ed6\u7684\u90a3\u4e00\u5200\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u81f3\u5c11\u5e2e\u4e3b\u5e76\u6ca1\u6709\u5341\u5206\u7684\u628a\u63e1\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u54fc\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5343\u91d1\u4e4b\u5b50\uff0c\u5750\u4e0d\u5782\u5802\uff0c\u4ee5\u5e2e\u4e3b\u73b0\u5728\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u53c8\u4f55\u5fc5\u5192\u8fd9\u4e2a\u9669\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u5634\u53c8\u95ed\u4e0a\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5bb6\u7236\u6b66\u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u58f0\u671b\u5730\u4f4d\uff0c\u5fc3\u8ba1\u673a\u667a\uff0c\u90fd\u4e0d\u5728\u522b\u4eba\u4e4b\u4e0b\uff0c\u5e2e \u4e3b\u4e0e\u4ed6\u7ed3\u4e3a\u5144\u5f1f\uff0c\u4e5f\u662f\u6709\u5229\u800c\u65e0\u5bb3\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u4e5f\u662f\u4ed6\u7684\u5144\u5f1f\uff0c\u662f\u4e48\uff1f\u201d\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u662f \u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u65e2\u80fd\u51fa\u5356\u4e86\u674e\u5bfb\u6b22\uff0c\u53c8\u600e\u77e5\u4e0d\u4f1a\u51fa\u5356\u6211\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u56e0\u4e3a\u5e2e\u4e3b\u4e0d\u662f\u674e\u5bfb\u6b22\u3002\u201d \u3000\u3000\u8fd9\u79cd\u8bdd\u8bf4\u5f97\u5f88\u7b80\u5355\uff0c\u4e5f\u5f88\u5c16\u9510\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9f99\u5578\u4e91\u5c31\u7b97\u6709\u80c6\u5b50\u51fa\u5356\u6211\uff0c\u4e5f\u6ca1\u6709\u90a3\u4e48\u5927\u7684\u672c\u4e8b \u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5e2e\u4e3b\u7b54\u5e94\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9aa4\u7136\u987f\u4f4f\u7b11\u58f0\uff0c\u9053\uff1a\u201c\u6211\u600e\u77e5\u9053\u674e\u5bfb\u6b22\u5df2\u5728\u4f60\u4eec\u638c\u63e1\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u53ea\u8981\u5e2e\u4e3b\u53d1\u51fa\u8bf7\u8d34\uff0c\u9080\u8bf7\u5929\u4e0b\u82f1\u96c4\u6765\u53c2\u4e0e\u5bb6\u7236\u4e0e\u5e2e\u4e3b\u7ed3\u62dc\u4e4b\u76db\u5178\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u6562\u6765\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5fae\u7b11\u9053\uff1a\u201c\u6765\u4e0d\u6765\u90fd\u4e0d\u91cd\u8981\uff0c\u53ea\u8981\u5927\u5bb6\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7b11\u51b7\u9053\uff1a\u201c\u4f60\u8003\u8651\u5f97\u5012\u5f88\u5468\u5230\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5e2e\u4e3b\u4e5f\u8bb8\u8fd8\u8981\u8003\u8651\uff0c\u665a\u8f88\u5c31\u843d\u811a\u5728\u57ce\u4e2d\u2018\u5982\u4e91\u5ba2\u6808\u2019\uff0c\u7b49\u5019\u5e2e\u4e3b\u7684 \u6d88\u606f\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u53c8\u63a5\u7740\u9053\uff1a\u201c\u53ea\u8981\u5e2e\u4e3b\u8bf7\u8d34\u53d1\u51fa\uff0c\u6709\u4eba\u6536\u5230\uff0c\u665a\u8f88\u968f\u65f6\u90fd\u53ef\u5c06\u674e\u5bfb\u6b22\u5e26\u5230\u5e2e\u4e3b \u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5e26\u5230\u8fd9\u91cc\u6765\u2026\u2026\u54fc\uff0c\u4f60\u7236\u5b50\u53ea\u6015\u8fd8\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u672c\u4e8b\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u8fd9\u70b9\u665a\u8f88\u81ea\u7136\u4e5f\u77e5\u9053\uff0c\u8fde\u5c11\u6797\u5bfa\u5fc3\u7709\u5927\u5e08\u548c\u7530\u4e03\u7237\u90fd\u505a\u4e0d\u5230\u7684\u4e8b\uff0c\u665a\u8f88\u81ea \u7136\u66f4\u505a\u4e0d\u5230\u4e86\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4e00\u8def\u4e0a\u82e5\u6709\u8346\u5148\u751f\u62a4\u9001\uff0c\u5c31\u53ef\u4e07\u5143\u4e00\u5931\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u541f\u7740\uff0c\u8fd8\u672a\u8bf4\u8bdd\u3002 \u3000\u3000\u8346\u65e0\u547d\u7a81\u7136\u9053\uff1a\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9762\u4e0a\u521d\u6b21\u9732\u51fa\u559c\u8272\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u9ed8\u7136\u826f\u4e45\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u6b66\u529f\u5df2\u88ab\u5e9f\uff0c\u6c38\u96be\u590d\u6108\uff0c\u4e0b\u624b\u7684\u4eba\u662f\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u82cd\u767d\u7684\u9762\u8272\u4e00\u4e0b\u5b50\u53c8\u53d8\u4e3a\u94c1\u9752\uff0c\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ef\u7740\u4ed6\u7684\u8138\uff0c\u4e00\u5b57\u5b57\u95ee\u9053\uff1a\u201c\u4f60\u6068\u4ed6\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7684\u62f3\u5df2\u63e1\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u53c8\u56de\u7b54\u4e86\u4e00\u4e2a\u5b57\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u975e\u4f46\u4e0d\u8be5\u6068\u4ed6\uff0c\u8fd8\u8be5\u611f\u6fc0\u4ed6\u624d\u662f\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u6115\u7136\u62ac\u5934\uff0c\u9053\uff1a\u201c\u611f\u6fc0\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u82e5\u975e\u4ed6\u5df2\u5e9f\u53bb\u4f60\u7684\u6b66\u529f\uff0c\u4eca\u65e5\u4f60\u5df2\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7684\u5934\u53c8\u5782\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5df2\u5982\u6b64\u9634\u6c89\u72e0\u6bd2\uff0c\u4e0d\u51fa\u4e8c\u5341\u5e74\uff0c\u5c31\u53ef\u4e0e\u6211\u4e89\u4e00\u540c\u4e4b\u96c4\u957f\uff0c \u82e5\u975e\u4f60\u5df2\u6b8b\u5e9f\uff0c\u6211\u600e\u4e48\u80fd\u653e\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7d27\u54ac\u7740\u7259\uff0c\u7259\u6839\u5df2\u51fa\u8840\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u5934\u59cb\u7ec8\u672a\u66fe\u62ac\u8d77\u3002 \u7b2c\u4e94\u5341\u4e94\u7ae0\u3000\u8361\u5987>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u4e94\u7ae0\u3000\u8361\u5987 \u3000\u3000\u9ed1\u6697\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u6709\u4eba\u5728\u547b\u541f\uff0c\u5598\u606f\u2026\u2026 \u3000\u3000\u7136\u540e\u4e00\u5207\u58f0\u606f\u90fd\u6c89\u5bc2\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u6709\u5973\u4eba\u7684\u58f0\u97f3\u8f7b\u8f7b\u9053\uff1a\u201c\u6709\u65f6\u6211\u603b\u5fcd\u4e0d\u4f4f\u60f3\u8981\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u5973\u4eba\u58f0\u97f3\u751c\u7b11\u800c\u5a07\u5f31\uff0c\u7537\u4eba\u82e5\u60f3\u62b5\u6297\u8fd9\u79cd\u58f0\u97f3\u7684\u8bf1\u60d1\u529b\uff0c\u53ea\u6709\u53d8\u6210\u804b\u5b50\u3002 \u3000\u3000\u4e00\u4e2a\u7537\u4eba\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u8fd9\u7537\u4eba\u7684\u58f0\u97f3\u5f88\u5947\u7279\uff0c\u4f60\u5728\u5f88\u8fd1\u7684\u5730\u65b9\u542c\u4ed6\u8bf4\u8bdd\uff0c\u58f0\u97f3\u5374\u50cf\u662f\u6765\u81ea\u5f88\u9065\u8fdc\u4e4b\u5904\uff0c\u4f60\u5728\u5f88 \u8fdc\u7684\u5730\u65b9\u542c\uff0c\u58f0\u97f3\u5374\u4eff\u4f5b\u8fd1\u5728\u8033\u7554\u3002 \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u771f\u7684\u662f\u4e2a\u4eba\uff1f\u8fd8\u662f\u94c1\u6253\u7684\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u4f60\u611f\u89c9\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u5973\u4eba\u7684\u58f0\u97f3\u66f4\u751c\u817b\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u771f\u662f\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u6c38\u8fdc\u4e0d\u4f1a\u7d2f\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u4f60\u53d7\u4e0d\u4e86\uff1f\u201d \u3000\u3000\u5973\u4eba\u5403\u5403\u7684\u7b11\u7740\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4f1a\u6c42\u9976\uff1f\u4f60\u4e3a\u4f55\u4e0d\u518d\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u884c\uff01\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u6211\u8981\u4f60\u53bb\u505a\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u90fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u5c31\u53bb\u6740\u4e86\u963f\u98de\u3002\u201d \u3000\u3000\u5973\u4eba\u4f3c\u4e4e\u6014\u4f4f\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u6740\u4ed6\u7684 \u65f6\u5019\u3002\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5df2\u5230\u4e86\u3002\u201d \u3000\u3000\u5973\u4eba\u4f3c\u53c8\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u96be\u9053\u674e\u5bfb\u6b22\u5df2\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u867d\u8fd8\u672a\u6b7b\uff0c\u5df2\u79bb\u6b7b\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u73b0\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u5df2\u5728\u6211\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5973\u4eba\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u51e0\u5929\uff0c\u6211\u51e0\u4e4e\u5929\u5929\u665a\u4e0a\u8ddf\u4f60\u5728\u4e00\u8d77\uff0c\u4f60\u7528\u4ec0\u4e48\u6cd5\u5b50\u5c06\u4ed6\u6293\u6765\u7684\uff1f\u96be \u9053\u4f60\u4f1a\u5206\u8eab\u672f\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u6211\u8981\u7684\u4e1c\u897f\uff0c\u7528\u4e0d\u7740\u6211\u81ea\u5df1\u52a8\u624b\uff0c\u81ea\u7136\u4f1a\u6709\u4eba\u9001\u6765\u3002\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u8c01\u9001\u6765\u7684\uff1f\u8c01\u6709\u90a3\u4e48\u5927\u7684\u672c\u4e8b\u80fd\u6293\u4f4f\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u9f99\u5578\u4e91\u3002\u201d \u3000\u3000\u5973\u4eba\u4f3c\u4e4e\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u7136\u540e\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u7136\u662f\u9f99\u5578\u4e91\uff0c\u53ea\u6709\u674e\u5bfb\u6b22\u7684\u597d\u670b\u53cb \uff0c\u624d\u80fd\u5bb3\u5f97\u4e86\u674e\u5bfb\u6b22\uff0c\u82e5\u60f3\u6253\u5012\u4ed6\uff0c\u65e0\u8bba\u7528\u4ec0\u4e48\u6837\u7684\u5175\u5668\u90fd\u5f88\u56f0\u96be\uff0c\u53ea\u80fd\u7528\u60c5\u611f\u3002\u201d \u3000\u3000\u7537\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5012\u5f88\u4e86\u89e3\u4ed6\u3002\u201d \u3000\u3000\u5973\u4eba\u7b11\u9053\uff1a\u201c\u6211\u5bf9\u654c\u4eba\u4e00\u5411\u6bd4\u670b\u53cb\u4e86\u89e3\u5f97\u6e05\u695a\uff0c\u6bd4\u5982\u8bf4\u2026\u2026\u6211\u5c31\u4e0d\u4e86\u89e3\u4f60\u3002\u201d \u3000\u3000\u5979\u7acb\u523b\u6539\u53d8\u4e86\u8bdd\u9898\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u660e\u767d\u9f99\u5578\u4e91\u7684\u4e3a\u4eba\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u5e73\u767d\u65e0\u6545\u5c06\u674e\u5bfb\u6b22 \u9001\u6765\u7ed9\u4f60\u3002\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4ed6\u4e0d\u613f\u81ea\u5df1\u6740\u6b7b\u674e\u5bfb\u6b22\uff0c\u6240\u4ee5\u624d\u501f\u5200\u6740\u4eba\u3002\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u53ea\u6709\u8fd9\u76ee\u7684\uff1f\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4ed6\u8fd8\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u4ed6\u8fd8\u8981\u6211\u505a\u4ed6\u7684\u7ed3\u62dc\u5144\u5f1f\u3002\u201d \u3000\u3000\u5973\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u771f\u4f1a\u5360\u4fbf\u5b9c\uff0c\u53ef\u662f\u4f60\u2026\u2026\u4f60\u96be\u9053\u7b54\u5e94\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4f60\u96be\u9053\u770b\u4e0d\u51fa\u4ed6\u662f\u60f3\u5229\u7528\u4f60\u3002\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u54fc\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4ed6\u60f3\u5f97\u672a\u514d\u592a\u5929\u771f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u5929\u771f\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u4ed6\u8ba4\u4e3a\u505a\u4e86\u6211\u7684\u7ed3\u4e49\u5144\u5f1f\uff0c\u6211\u5c31\u4e0d\u4f1a\u52a8\u4ed6\u4e86\uff0c\u5176\u5b9e\uff0c\u83ab\u8bf4\u7ed3\u4e49\u5144\u5f1f\uff0c\u5c31\u7b97\u4eb2 \u5144\u5f1f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5973\u4eba\u5a07\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u53ef\u4ee5\u51fa\u5356\u674e\u5bfb\u6b22\uff0c\u4f60\u81ea\u7136\u4e5f\u53ef\u4ee5\u51fa\u5356\u4ed6\u3002\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u9f99\u5578\u4e91\u5728\u6211\u773c\u4e2d\u867d\u4e00\u6587\u4e0d\u503c\uff0c\u4f46\u4ed6\u7684\u513f\u5b50\u5374\u771f\u662f\u4e2a\u5389\u5bb3\u89d2\u8272\u3002\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u90a3\u5c0f\u9b3c\uff1f\u201d \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u8fd9\u6b21\u9f99\u5578\u4e91\u5e76\u6ca1\u6709\u6765\uff0c\u662f\u4ed6\u513f\u5b50\u6765\u7684\u3002\u201d \u3000\u3000\u5973\u4eba\u53c8\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5b69\u5b50\u7684\u786e\u662f\u4eba\u5c0f\u9b3c\u5927\u3002\u201d \u3000\u3000\u7537\u4eba\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u5973\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u6211\u591a\u7559\u4e00\u4f1a\u513f\u5435 \u3000\u3000\u7537\u4eba\u9053\uff1a\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u5973\u4eba\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u522b\u7684\u7537\u4eba\u8ddf\u6211\u5728\u4e00\u8d77\uff0c\u603b\u820d\u4e0d\u5f97\u79bb\u5f00\u6211\uff0c\u591a\u966a\u6211\u4e00\u523b\u4e5f\u662f\u597d\u7684\uff0c\u53ea\u6709 \u4f60\uff0c\u6bcf\u6b21\u53ea\u8981\u4e00\u505a\u5b8c\u4e8b\uff0c\u4f60\u5c31\u8d76\u6211\u8d70\u3002\u201d \u3000\u3000\u7537\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u65e2\u4e0d\u662f\u522b\u7684\u7537\u4eba\uff0c\u4e5f\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff0c\u6211\u4eec\u53ea\u4e0d\u8fc7\u662f\u5728\u4e92\u76f8\u5229\u7528\u800c \u5df2\uff0c\u65e2\u7136\u6211\u4eec\u5fc3\u91cc\u90fd\u5f88\u660e\u81ea\uff0c\u53c8\u4f55\u5fc5\u8fd8\u865a\u60c5\u5047\u610f\uff0c\u8089\u9ebb\u5f53\u6709\u8da3\u3002\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u5f88\u6697\uff0c\u5c4b\u5b50\u5916\u9762\u5374\u6709\u5149\u3002 \u3000\u3000\u6de1\u6de1\u7684\u661f\u5149\u3002 \u3000\u3000\u661f\u5149\u4e0b\u6728\u7acb\u7740\u4e00\u4e2a\u4eba\uff0c\u5b88\u5019\u5728\u5c4b\u5b50\u5916\uff0c\u4e00\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\u832b\u7136\u5730\u6ce8\u89c6\u7740\u8fdc\u65b9\uff0c\u6574\u4e2a\u4eba\u770b \u6765\u5c31\u50cf\u662f\u7528\u4e00\u5757\u7070\u77f3\u523b\u51fa\u6765\u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u8fd9\u53cc\u6b7b\u7070\u8272\u7684\u773c\u775b\u5374\u5e26\u7740\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u7b80\u76f4\u65e0\u6cd5\u518d\u7ad9\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u5fcd\u53d7\u5c4b\u5b50\u91cc\u53d1\u51fa\u7684\u90a3\u4e9b\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u4ed6\u5fc5\u987b\u5fcd\u53d7\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\uff0c\u53ea\u5fe0\u4e8e\u4e00\u4e2a\u4eba\u2014\u2014\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u4ed6\u7684\u751f\u547d\uff0c\u751a\u81f3\u8fde\u4ed6\u7684\u7075\u9b42\u90fd\u662f\u5c5e\u4e8e\u4e0a\u5b98\u91d1\u8679\u7684\u3002 \u3000\u3000\u95e8\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u6761\u7a88\u7a95\u7684\u4eba\u5f71\u6084\u6084\u6765\u5230\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u661f\u5149\u6620\u4e0a\u5979\u7684\u8138\uff0c\u6e05\u65b0\u3001\u7f8e\u4e3d\u3001\u7eaf\u771f\uff0c\u65e0\u8bba\u8c01\u770b\u5230\u5979\uff0c\u90fd\u7edd\u5bf9\u60f3\u4e0d\u5230\u5979\u65b9\u624d\u505a\u8fc7\u4e86\u4ec0\u4e48 \u4e8b\u3002 \u3000\u3000\u4ed9\u5b50\u7684\u5916\u8c8c\uff0c\u9b54\u9b3c\u7684\u7075\u9b42\u2014\u2014\u9664\u4e86\u6797\u4ed9\u513f\u8fd8\u6709\u51c6\uff1f \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ed5\u5230\u4ed6\u9762\u524d\uff0c\u8109\u8109\u5730\u51dd\u6ce8\u7740\u4ed6\u3002 \u3000\u3000\u5979\u7684\u773c\u6ce2\u6e29\u67d4\u5982\u661f\u5149\u3002 \u3000\u3000\u8346\u65e0\u547d\u4ecd\u7136\u51dd\u6ce8\u7740\u8fdc\u65b9\uff0c\u4f3c\u4e4e\u773c\u524d\u6839\u672c\u6ca1\u6709\u5979\u8fd9\u4e2a\u4eba\u5b58\u5728\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u7ea4\u624b\uff0c\u642d\u4e0a\u4e86\u4ed6\u7684\u80a9\uff0c\u6162\u6162\u7684\u6ed1\u4e0a\u53bb\uff0c\u8f7b\u629a\u7740\u4ed6\u7684\u8033\u80cc\u2014\u2014\u5979\u77e5\u9053\u7537\u4eba\u8eab\u4e0a\u6240 \u6709\u654f\u611f\u7684\u90e8\u4f4d\u3002 \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u52a8\uff0c\u4f3c\u5df2\u9ebb\u6728\u2026\u2026 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u5728\u5916\u9762\u4e3a\u6211\u4eec\u5b88\u62a4\uff0c\u53ea\u8981\u77e5\u9053\u4f60\u5728\u5916\u9762\uff0c\u6211\u5c31\u4f1a\u6709\u79cd \u5b89\u5168\u611f\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u90fd\u6109\u5feb\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u9644\u5728\u4ed6\u8033\u8fb9\uff0c\u6084\u6084\u9053\uff1a\u201c\u6211\u8fd8\u8981\u544a\u8bc9\u4f60\u4e2a\u79d8\u5bc6\uff0c\u4ed6\u5e74\u7eaa\u867d\u7136\u5927\uff0c\u5374\u8fd8\u662f\u5f88\u5f3a\u58ee\uff0c \u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u7684\u7ecf\u9a8c\u6bd4\u522b\u4eba\u4e30\u5bcc\u3002\u201d \u3000\u3000\u5979\u94f6\u94c3\u822c\u5a07\u7b11\u7740\uff0c\u8d70\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u6ca1\u6709\u52a8\uff0c\u4f46\u8eab\u4e0a\u7684\u6bcf\u4e00\u6839\u808c\u8089\u90fd\u5df2\u5728\u98a4\u6296\u3002 \u3000\u3000\u5982\u4e91\u5ba2\u6808\u662f\u57ce\u91cc\u6700\u5927\u7684\uff0c\u6700\u6602\u8d35\u7684\u5ba2\u6808\uff0c\u4e5f\u662f\u82b1\u94b1\u7684\u5ba2\u6808\u3002 \u3000\u3000\u4f60\u82e5\u4f4f\u5728\u8fd9\u5ba2\u6808\u91cc\uff0c\u53ea\u8981\u4f60\u6709\u8db3\u591f\u7684\u94b1\uff0c\u6839\u672c\u7528\u4e0d\u7740\u8d70\u51fa\u5ba2\u6808\u7684\u95e8\uff0c\u5c31\u53ef\u4ee5\u83b7\u5f97\u4e00\u5207\u6700 \u597d\u7684\u4eab\u53d7\u3002 \u3000\u3000\u5728\u8fd9\u91cc\u3001\u53ea\u8981\u4f60\u5f00\u53e3\uff0c\u5c31\u6709\u4eba\u4f1a\u5c06\u57ce\u91cc\u6700\u597d\u7684\u83dc\uff0c\u6700\u51fa\u540d\u7684\u6b4c\u5993\uff0c\u6700\u7f8e\u7684\u5973\u4eba\u9001\u5230\u4f60\u5c4b \u91cc\u6765\u3002 \u3000\u3000\u5728\u8fd9\u91cc\uff0c\u767d\u5929\u6bcf\u95f4\u5c4b\u5b50\u91cc\u7684\u95e8\u90fd\u662f\u5173\u7740\u7684\uff0c\u51e0\u4e4e\u542c\u4e0d\u5230\u4efb\u4f55\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u4e00\u5230\u4e86\u665a\u4e0a\uff0c\u6bcf\u6247\u95e8\u90fd\u5f00\u4e86\u3002 \u3000\u3000\u6700\u5148\u4f60\u542c\u5230\u7684\u662f\u6f31\u6d17\u58f0\uff0c\u8d3c\u559d\u4f19\u8ba1\u58f0\uff0c\u9001\u9152\u83dc\u6765\u65f6\u7684\u8c22\u8d4f\u58f0\uff0c\u5973\u4eba\u4eec\u5a07\u7b11\u7740\u5524\u201c\u5f20\u5927\u7237 \uff0c\u738b\u4e09\u7237\u201d\u7684\u8bf7\u5b89\u58f0\u3002 \u3000\u3000\u7136\u540e\uff0c\u5c31\u662f\u731c\u62f3\u884c\u4ee4\u58f0\uff0c\u78b0\u676f\u58f0\uff0c\u5c11\u5973\u4eec\u5403\u5403\u7684\u7b11\u58f0\u548c\u6b4c\u58f0\uff0c\u7537\u4eba\u4eec\u7684\u5439\u725b\u58f0\uff0c\u63b7\u9ab0\u5b50 \u58f0\u2026\u2026 \u3000\u3000\u5728\u8fd9\u91cc\uff0c\u4e00\u5230\u4e86\u665a\u4e0a\uff0c\u4f60\u51e0\u4e4e\u5c31\u53ef\u4ee5\u542c\u5230\u4e16\u4e0a\u6240\u6709\u4e0d\u89c4\u77e9\u7684\u58f0\u97f3\u3002 \u3000\u3000\u53ea\u6709\u4e00\u95f4\u5c4b\u5b50\uff0c\u5374\u4ece\u6ca1\u6709\u58f0\u97f3\u3002 \u3000\u3000\u6709\u7684\u53ea\u662f\u5076\u800c\u4f20\u51fa\u7684\u4e00\u4e24\u58f0\u77ed\u4fc3\u7684\u5973\u4eba\u547b\u541f\uff0c\u54c0\u5524\u58f0\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u7684\u95e8\u4e5f\u59cb\u7ec8\u662f\u5173\u7740\u7684\u3002 \u3000\u3000\u6bcf\u5929\u9ec4\u660f\u65f6\uff0c\u90fd\u4f1a\u6709\u4eba\u5c06\u4e00\u4e2a\u5c0f\u59d1\u5a18\u9001\u51fa\u53bb\uff0c\u8fd9\u4e9b\u5c0f\u59d1\u5a18\u5f53\u7136\u90fd\u5f88\u7f8e\uff0c\u800c\u4e14\u5f88\u5e74\u8f7b\uff0c\u5f88 \u5a07\u5c0f\u3002 \u3000\u3000\u5979\u4eec\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u5f53\u7136\u90fd\u6253\u626e\u5f97\u6f02\u6f02\u4eae\u4eae\uff0c\u4e8e\u5e72\u51c0\u51c0\uff0c\u800c\u4e14\u8138\u4e0a\u5f53\u7136\u90fd\u5e26\u7740\u7b11\uff0c\u7eb5\u7136\u662f \u88ab\u8bad\u7ec3\u51fa\u6765\u7684\u804c\u4e1a\u6027\u7b11\u5bb9\uff0c\u4f46\u5448\u73b0\u5728\u5c11\u5973\u4eec\u7684\u8138\u4e0a\uff0c\u770b\u6765\u5c31\u975e\u4f46\u4e0d\u4f1a\u4ee4\u4eba\u8ba8\u538c\uff0c\u800c\u4e14\u8fd8\u76f8\u5f53 \u52a8\u4eba\u3002 \u3000\u3000\u4f46\u7b49\u5230\u7b2c\u4e8c\u5929\u65e9\u4e0a\u5979\u4eec\u8d70\u51fa\u8fd9\u5c4b\u5b50\u95e8\u65f6\uff0c\u60c5\u51b5\u5c31\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u672c\u6765\u6574\u6574\u9f50\u9f50\u7684\u5934\u53d1\uff0c\u5230\u8fd9\u65f6\u5df2\u84ec\u4e71\uff0c\u751a\u81f3\u8fd8\u88ab\u626f\u843d\u4e86\u4e9b\uff0c\u672c\u6765\u5f88\u660e\u4eae\u7684\u4e00\u53cc\u773c\u775b\uff0c\u5df2 \u53d8\u5f97\u6beb\u65e0\u795e\u91c7\uff0c\u8fde\u773c\u7736\u90fd\u9677\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u672c\u6765\u5145\u6ee1\u4e86\u9752\u6625\u5149\u91c7\u7684\u8138\uff0c\u4e5f\u5df2\u7901\u6dec\uff0c\u800c\u4e14\u8fd8\u5e26\u7740\u6cea\u75d5\u3002 \u3000\u3000\u4e03\u5929\uff0c\u4e03\u5929\u6765\u90fd\u5982\u6b64\u3002 \u3000\u3000\u5f00\u59cb\u65f6\uff0c\u8fd8\u6ca1\u6709\u4eba\u6ce8\u610f\uff0c\u4f46\u540e\u6765\u5927\u5bb6\u90fd\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u4e86\u3002 \u3000\u3000\u51fa\u6765\u5bfb\u6b22\u4f5c\u4e50\u7684\u4eba\uff0c\u5bf9\u8fd9\u79cd\u4e8b\u603b\u662f\u7279\u522b\u7559\u610f\u7684\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u5728\u731c\u6d4b\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u5982\u6b64\u5389\u5bb3\uff1f\u201d \u3000\u3000\u5927\u5bb6\u90fd\u5728\u60f3\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u4e2a\u9b41\u5f62\u5927\u6c49\uff0c\u5f3a\u58ee\u5982\u725b\u3002\u201d \u3000\u3000\u4e8e\u662f\u5927\u5bb6\u5f00\u59cb\u6253\u542c\u3002 \u3000\u3000\u6253\u542c\u51fa\u6765\u7684\u7ed3\u679c\uff0c\u4f7f\u6bcf\u4e2a\u4eba\u90fd\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u201c\u539f\u6765\u8fd9\u5c4b\u5b50\u91cc\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u53d1\u80b2\u4e0d\u5168\u7684\u5c0f\u5b69\u5b50\uff01\u201d \u3000\u3000\u4e8e\u662f\u5927\u5bb6\u66f4\u597d\u5947\uff0c\u6709\u7684\u4eba\u5c31\u5c06\u66fe\u7ecf\u5230\u8fc7\u90a3\u5c4b\u5b50\u91cc\u7684\u5c0f\u59d1\u5a18\u53ec\u6765\u95ee\u3002 \u3000\u3000\u53ea\u8981\u4e00\u95ee\u5230\u8fd9\u4ef6\u4e8b\uff0c\u5c0f\u59d1\u5a18\u4eec\u5c31\u4f1a\u53d1\u6296\uff0c\u773c\u6cea\u5c31\u5f00\u59cb\u5f80\u4e0b\u6d41\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80af\u518d\u63d0\u8d77\u4e00 \u4e2a\u5b57\u3002 \u3000\u3000\u88ab\u95ee\u5f97\u6025\u4e86\uff0c\u5979\u4eec\u53ea\u6709\u4e00\u53e5\u8bdd\uff1a\u201c\u4ed6\u4e0d\u662f\u4eba\u2026\u2026\u4ed6\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u53c8\u662f\u9ec4\u660f\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u7684\u95e8\u4ecd\u662f\u5173\u7740\u7684\u3002 \u3000\u3000\u5bf9\u7740\u95e8\u6709\u6247\u7a97\u5b50\uff0c\u4e00\u4e2a\u8138\u8272\u53d1\u767d\u7684\u5b69\u5b50\u5750\u5728\u7a97\u5b50\u524d\uff0c\u76ee\u5149\u832b\u7136\u671b\u7740\u7a97\u5916\u7684\u4e00\u682a\u68a7\u6850\uff0c\u5df2 \u6709\u5f88\u4e45\u5f88\u4e45\u6ca1\u6709\u79fb\u52a8\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u867d\u5446\u6ede\uff0c\u4f46\u5374\u4e0d\u65f6\u4f1a\u95ea\u52a8\u51fa\u4e00\u4e1d\u72e1\u9ee0\u800c\u72e0\u6bd2\u7684\u5149\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u3002 \u3000\u3000\u684c\u5b50\u4e0a\u7684\u9152\u83dc\uff0c\u5374\u51e0\u4e4e\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u4ed6\u5403\u5f97\u5f88\u5c11\uff0c\u4ed6\u5728\u7b49\uff0c\u7b49\u66f4\u5927\u7684\u4eab\u53d7\uff0c\u5bf9\u4e8e\u201c\u5403\u201d\u4ed6\u4e00\u5411\u4e0d\u611f\u5174\u8da3\uff0c\u4ed6\u8ba4\u4e3a\u4e00\u4e2a\u4eba\u5403\u5f97 \u82e5\u592a\u591a\uff0c\u8111\u888b\u5c31\u4f1a\u88ab\u585e\u4f4f\u3002 \u3000\u3000\u7ec8\u4e8e\u6709\u4e86\u6572\u95e8\u58f0\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u5e76\u6ca1\u6709\u56de\u5934\uff0c\u53ea\u662f\u51b7\u51b7\u9053\uff1a\u201c\u95e8\u662f\u5f00\u7740\u7684\uff0c\u4f60\u81ea\u5df1\u8fdb\u6765\u3002\u201d \u3000\u3000\u95e8\u5f00\u4e86\uff0c\u811a\u6b65\u58f0\u5f88\u8f7b\uff0c\u5f88\u6162\u3002 \u3000\u3000\u6765\u7684\u663e\u7136\u53c8\u662f\u4e2a\u5a07\u5c0f\u7684\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u8fd8\u5e26\u7740\u4e03\u5206\u754f\u602f\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u9f99\u5c0f\u4e91\u6240\u559c\u6b22\u7684\u90a3\u79cd\u5973\u5b69\u5b50\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5f88\u5f31\uff0c\u6240\u4ee5\u4ed6\u559c\u6b22\u505a\uff0c\u2018\u5f3a\u8005\u201d\uff0c\u4e5f\u53ea\u6709\u5728\u8fd9\u79cd\u5973\u5b69\u5b50\u9762\u524d\uff0c\u4ed6\u624d\u4f1a\u89c9\u5f97\u81ea\u5df1\u662f \u4e2a\u5f3a\u8005\u3002 \u3000\u3000\u811a\u6b65\u58f0\u5728\u684c\u5b50\u65c1\u505c\u4e0b\u6765\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5e26\u4f60\u6765\u7684\u4eba\uff0c\u5df2\u8ddf\u4f60\u8bf4\u8fc7\u4ef7\u94b1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u2018\u8fd9\u4ef7\u94b1\u6bd4\u901a\u5e38\u9ad8\u4e24\u500d\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u8be5\u542c\u6211\u7684\u8bdd\uff0c\u7edd\u5bf9\u4e0d\u80fd\u53cd\u6297\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201c\u61c2\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u628a\u8863\u670d\u8131\u4e0b\u6765\uff0c\u5168\u8131\u4e0b\u6765\u3002\u201d \u3000\u3000\u5973\u5b69\u5b50\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u8131\u8863\u670d\u7684\u65f6\u5019\uff0c\u4f60\u4e0d\u770b\uff1f\u201d \u3000\u3000\u58f0\u97f3\u7f8e\u5f97\u51fa\u5947\uff0c\u751c\u5f97\u51fa\u5947\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u4eff\u4f5b\u6014\u4e86\u6014\u3002 \u3000\u3000\u90a3\u5973\u5b69\u5b50\u67d4\u58f0\u7b11\u7740\uff0c\u9053\u3002\u2018\u770b\u5973\u5b69\u5b50\u8131\u8863\u670d\uff0c\u4e5f\u662f\u79cd\u4eab\u53d7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u653e\u5f03\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u4f3c\u5df2\u89c9\u5f97\u6709\u4ec0\u4e48\u4e0d\u5bf9\u4e86\uff0c\u9aa4\u7136\u56de\u5934\u3002 \u3000\u3000\u7136\u540e\u4ed6\u6574\u4e2a\u4eba\u90fd\u6014\u4f4f\u3002 \u3000\u3000\u6765\u7684\u8fd9\u201c\u5973\u5b69\u5b50\u201d\uff0c\u7adf\u662f\u6797\u4ed9\u513f\uff01 \u3000\u3000\u6797\u4ed9\u513f\u8138\u4e0a\u4ecd\u5e26\u7740\u4ed9\u5b50\u822c\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u7684\u8138\u5374\u5df2\u50f5\u6728\u3002\uff1a \u3000\u3000\u4f46\u90a3\u53ea\u4e0d\u8fc7\u662f\u77ed\u77ed\u4e00\u5239\u90a3\u95ee\u7684\u4e8b\uff0c\u4ed6\u77ac\u5373\u7b11\u4e86\uff0c\u7ad9\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u6797\u963f\u59e8\u5728\u5f00\u5c0f \u4f84\u7684\u73a9\u7b11\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u5f97\u66f4\u59a9\u5a9a\uff0c\u9053\uff1a\u201c\u5230\u73b0\u5728\u4f60\u8fd8\u8981\u53eb\u6211\u963f\u59e8\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u966a\u7740\u7b11\uff0c\u9053\uff1a\u201c\u963f\u59e8\u603b\u662f\u963f\u59e8\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9650\u6ce2\u6d41\u52a8\uff0c\u8198\u7740\u4ed6\u9053\uff1a\u201c\u4f46\u73b0\u5728\u4f60\u5df2\u662f\u5927\u4eba\u4e86\uff0c\u662f\u5417\uff1f\u201d\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u60a0\u60a0\u7684\u63a5\u7740\u9053\uff1a\u201c\u624d\u4e24\u4e09\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u4f60\u957f\u5f97\u8fd9\u4e48\u5feb\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5f88\u5de7\u5999\u7684\u907f\u5f00\u4e86\u8fd9\u53e5\u8bdd\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4e09\u5e74\u6765\uff0c\u6211\u4eec\u59cb\u7ec8\u6253\u542c\u4e0d\u51fa\u963f\u59e8\u4f60\u7684\u6d88\u606f\uff0c \u4e00\u76f4\u90fd\u60f3\u5ff5\u5f97\u5f88\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u201c\u4f46\u6211\u5374\u542c\u5230\u8fc7\u4f60\u8bb8\u591a\u4e8b\uff0c\u542c\u8bf4\u2026\u2026\u4f60\u5bf9\u4e2a\u5b69\u5b50\uff0c\u6bd4\u5927\u591a\u6570\u5e74\u7eaa\u6bd4\u4f60\u5927 \u7684\u7537\u4eba\u90fd\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5782\u4e0b\u5934\uff0c\u5374\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f46\u5728\u963f\u59e8\u9762\u524d\uff0c\u6211\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u77aa\u8d77\u4e86\u773c\uff0c\u5a07\u55c5\u9053\uff1a\u201c\u4f60\u8fd8\u53eb\u6211\u963f\u59e8\uff0c\u96be\u9053\u6211\u771f\u7684\u90a3\u4e48\u8001\u4e86\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5fcd\u4e0d\u4f4f\u62ac\u8d77\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5c31\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u968f\u968f\u4fbf\u4fbf\u7684\u7ad9\u7740\uff0c\u4f46\u90a3\u79cd\u98ce\u60c5\uff0c\u90a3\u79cd\u795e\u91c7\uff0c\u90a3\u79cd\u8bf4\u4e0d\u51fa\u7684\u8bf1\u60d1\uff0c \u4e00\u5343\u4e07\u4e2a\u5973\u4eba\u4e2d\u4e5f\u627e\u4e0d\u51fa\u4e00\u4e2a\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u5446\u6ede\u7684\u773c\u775b\u53d1\u4e86\u5149\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u559c\u6b22\u7684\u90fd\u662f\u5c0f\u59d1\u5a18\uff0c\u800c\u6211\u2026\u2026\u6211\u5374\u662f\u4e2a\u8001\u592a\u5a46\u4e86\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u53ea\u89c9\u81ea\u5df1\u7684\u5fc3\u5728\u8df3\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u8001\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u82e5\u6709\u4eba\u8bf4\u4f60\u8001\u4e86\uff0c\u90a3\u4eba\u4e0d\u662f\u5446\u5b50\uff0c\u5c31\u662f\u778e\u5b50\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5a9a\u7b11\u9053\uff1a\u201c\u4f60\u778e\u4e0d\u778e\uff1f\u5446\u4e0d\u5446\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5f53\u7136\u4e0d\u778e\uff0c\u66f4\u4e0d\u5446\u3002 \u3000\u3000\u6797\u4ed9\u513f\u79bb\u5f00\u4ed6\u7684\u65f6\u5019\uff0c\u7adf\u4e5f\u4f3c\u89c9\u5f97\u5f88\u75db\u82e6\u3002 \u3000\u3000\u8fd9\u201c\u5b69\u5b50\u201d\u65e2\u4e0d\u662f\u5b69\u5b50\uff0c\u4e5f\u4e0d\u662f\u778e\u5b50\uff0c\u66f4\u4e0d\u662f\u5446\u5b50\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u75af\u5b50\uff01 \u3000\u3000\u53ef\u6015\u7684\u75af\u5b50\u3002 \u3000\u3000\u8fde\u6797\u4ed9\u513f\u90fd\u6ca1\u6709\u9047\u5230\u8fc7\u8fd9\u6837\u7684\u75af\u5b50\u3002 \u3000\u3000\u4f46\u5979\u76ee\u4e2d\uff0c\u5374\u95ea\u52a8\u7740\u4e00\u79cd\u5f97\u610f\u6109\u5feb\u7684\u5149\u8292\u3002 \u3000\u3000\u5979\u6bd5\u7adf\u8fd8\u662f\u5f97\u5230\u4e86\u5979\u6240\u60f3\u5f97\u5230\u7684\u6d88\u606f\u3002 \u3000\u3000\u5bf9\u7537\u4eba\uff0c\u5979\u4ece\u6ca1\u6709\u5931\u8d25\uff0c\u65e0\u8bba\u90a3\u7537\u4eba\u662f\u5446\u5b50\u662f\u541b\u5b50\uff0c\u8fd8\u662f\u75af\u5b50\uff01 \u3000\u3000\u65e0\u867d\u4eae\u4e86\uff0c\u5bf9\u9762\u7684\u5c4b\u5b50\u91cc\u5374\u8fd8\u6709\u4eba\u5728\u559d\u9152\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6b63\u5728\u5927\u58f0\u7b11\u7740\uff0c\u9053\uff1a\u201c\u559d\u9152\u8981\u5c31\u4e0d\u559d\uff0c\u8981\u559d\u5c31\u559d\u5230\u65e0\u4eae\uff0c\u559d\u5230\u8eba\u4e0b\u53bb\u4e3a\u6b62\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u5e76\u6ca1\u6709\u8bf4\u5b8c\uff0c\u597d\u50cf\u5df2\u7ecf\u8eba\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u542c\u5230\u8fbe\u65ec\u6d3b\uff0c\u6797\u4ed9\u513f\u5ffd\u7136\u60f3\u8d77\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u53c8\u542c\u5230\u90a3\u4eba\u7684\u54b3\u55fd\u58f0\u3002 \u3000\u3000\u60f3\u8d77\u4e86\u8fd9\u4e2a\u4eba\uff0c\u5979\u5c31\u6068\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u5979\u7eb5\u7136\u53ef\u4ee5\u5f81\u670d\u4e16\u4e0a\u6240\u6709\u7684\u7537\u4eba\uff0c\u5374\u6c38\u8fdc\u4e5f\u5f97\u4e0d\u5230\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5f97\u4e0d\u5230\u4ed6\uff0c\u6240\u4ee5\u4e00\u5fc3\u53ea\u60f3\u6bc1\u4e86\u4ed6\uff01 \u3000\u3000\u5979\u5f97\u4e0d\u5230\u7684\uff0c\u4e5f\u4e0d\u613f\u522b\u4eba\u5f97\u5230\u3002 \u3000\u3000\u5979\u54ac\u7740\u7259\uff0c\u5728\u5fc3\u91cc\u8bf4\uff1a\u201c\u6211\u867d\u7136\u60f3\u4f60\u6b7b\uff0c\u4f46\u73b0\u5728\u5374\u4e0d\u80fd\u8ba9\u4f60\u6b7b\uff0c\u5c24\u5176\u4e0d\u80fd\u8ba9\u4f60\u6b7b\u5728\uff1b\u4e0a \u5b98\u91d1\u8679\u624b\u4e0a\uff0c\u5426\u5219\u8fd9\u4e16\u4e0a\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u80fd\u4ee4\u4ed6\u987e\u8651\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u603b\u6709\u4e00\u5929\uff0c\u6211\u8981\u53eb\u4f60\u6b7b\u5728\u6211\u624b\u4e0a\uff0c\u6162\u6162\u7684\u6b7b\u2026\u2026\u6162\u6162\u7684\u2026\u2026\u201d \u7b2c\u4e94\u5341\u516d\u7ae0\u3000\u51fa\u9798\u5251>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u516d\u7ae0\u3000\u51fa\u9798\u5251 \u3000\u3000\u5251\u3002 \u3000\u3000\u4e00\u67c4\u5f88\u8584\u7684\u5251\uff0c\u5f88\u8f7b\uff0c\u8fde\u5251\u67c4\u90fd\u662f\u7528\u6700\u8f7b\u7684\u8f6f\u6728\u5939\u4e0a\u53bb\u3002 \u3000\u3000\u6ca1\u6709\u5251\u9537\u62a4\u624b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u5251\u523a\u51fa\uff0c\u6ca1\u6709\u4eba\u80fd\u524a\u5230\u4ed6\u7684\u624b\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u5175\u5668\uff0c\u90fd\u53ef\u5c06\u8fd9\u67c4\u5251\u51fb\u65ad\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u5251\u523a\u51fa\uff0c\u6ca1\u6709\u4eba\u80fd\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u8fd9\u662f\u67c4\u5f88\u5947\u7279\u7684\u5251\uff0c\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u7528\u8fd9\u79cd\u5251\uff0c\u6562\u7528\u8fd9\u79cd\u5251\u3002 \u3000\u3000\u5251\uff0c\u5c31\u653e\u5728\u5e8a\u8fb9\u7684\u77ee\u684c\u4e0a\uff0c\u548c\u4e00\u5957\u5f88\u5e72\u51c0\u7684\u9752\u5e03\u8863\u670d\u653e\u5728\u4e00\u8d77\u3002 \u3000\u3000\u963f\u98de\u9192\u6765\u65f6\uff0c\u7b2c\u4e00\u773c\u5c31\u770b\u5230\u4e86\u8fd9\u67c4\u5251\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u7acb\u523b\u53d1\u4e86\u5149\u3002 \u3000\u3000\u770b\u5230\u4e86\u8fd9\u67c4\u5251\uff0c\u5c31\u597d\u50cf\u770b\u5230\u4e86\u4ed6\u4e45\u522b\u91cd\u9022\u7684\u7231\u4fa3\uff0c\u591a\u5e74\u672a\u89c1\u7684\u597d\u53cb\u4e00\u6837\uff0c\u4ed6\u5fc3\u91cc\u4eff\u4f5b\u9aa4 \u7136\u89c9\u5f97\u6709\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\u3002 \u3000\u3000\u6162\u6162\u7684\u4f38\u51fa\u624b\uff0c\u53d6\u5251\u3002 \u3000\u3000\u4ed6\u7684\u624b\u751a\u81f3\u5df2\u6709\u4e9b\u98a4\u6296\u3002 \u3000\u3000\u4f46\u7b49\u5230\u4ed6\u624b\u6307\u63a5\u89e6\u5230\u90a3\u8584\u800c\u950b\u5229\u7684\u5251\u950b\u65f6\uff0c\u5c31\u7acb\u523b\u7a33\u5b9a\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u8f7b\u629a\u7740\u5251\u950b\uff0c\u76ee\u5149\u4f3c\u4e4e\u53d8\u5f97\u5f88\u9065\u8fdc\u2026\u2026\u5f88\u9065\u8fdc\u2026\u2026 \u3000\u3000\u4ed6\u7684\u5fc3\u4f3c\u5df2\u5230\u4e86\u8fdc\u65b9\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u7b2c\u4e00\u6b21\u4f7f\u7528\u5251\u7684\u65f6\u5019\uff0c\u60f3\u8d77\u9c9c\u8840\u968f\u7740\u4ed6\u7684\u5251\u950b\u6ef4\u843d\u7684\u60c5\u51b5\uff0c\u60f3\u8d77\u90a3\u8bb8\u8bb8\u591a\u591a\u6b7b\u5728 \u4ed6\u5251\u4e0b\u7684\u4eba\u2014\u2014\u53ef\u6076\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u8840\u5df2\u6cb8\u817e\u3002 \u3000\u3000\u90a3\u6bb5\u65f6\u5019\u867d\u7136\u5145\u6ee1\u4e86\u4e0d\u5e78\u548c\u707e\u96be\uff0c\u4f46\u5374\u662f\u591a\u91c7\u7684\uff0c\u8f89\u714c\u7684\uff01 \u3000\u3000\u201c\u7094\u610f\u601d\u4ec7\u201d\u8fd9\u56db\u5b57\u662f\u4f55\u7b49\u8c6a\u58ee\u3002 \u3000\u3000\u4f46\u90a3\u6bd5\u7adf\u90fd\u5df2\u8fc7\u53bb\uff0c\u8fc7\u53bb\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u4ed6\u5df2\u7b54\u5e94\u8fc7\u4ed6\u6700\u5fc3\u7231\u7684\u4eba\uff0c\u6c38\u8fdc\u5c06\u4ee5\u524d\u7684\u4e8b\u5fd8\u8bb0\uff01 \u3000\u3000\u73b0\u5728\u7684\u751f\u6d3b\u867d\u5e73\u6de1\uff0c\u751a\u81f3\u6709\u4e9b\u5bc2\u5bde\uff0c\u4f46\u90a3\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u80fd\u5e73\u9759\u5b89\u8be6\u7684\u6e21\u8fc7\u4e00\u751f\uff0c\u5c82\u975e \u6b63\u662f\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u7684\u5e0c\u671b\uff1f \u3000\u3000\u6ca1\u6709\u811a\u6b65\u58f0\uff0c\u6797\u4ed9\u513f\u5df2\u51fa\u73b0\u5728\u95e8\u53e3\u3002 \u3000\u3000\u5979\u770b\u6765\u867d\u6709\u4e9b\u75b2\u5026\uff0c\u6709\u4e9b\u7901\u6dec\uff0c\u4f46\u7b11\u5bb9\u4ecd\u5982\u6625\u82b1\u822c\u9c9c\u7f8e\u6e05\u65b0\u3002 \u3000\u3000\u65e0\u8bba\u727a\u7272\u4ec0\u4e48\uff0c\u53ea\u8981\u6bcf\u5929\u80fd\u770b\u5230\u8fd9\u6625\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u5c31\u53ef\u4ee5\u8865\u507f\u4e00\u5207\u3002 \u3000\u3000\u963f\u98de\u7acb\u523b\u653e\u4e0b\u4e86\u5251\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u5929\u4f60\u53ef\u6bd4\u6211\u8d77\u5f97\u65e9\uff0c\u6211\u597d\u50cf\u8d8a\u6765\u8d8a\u61d2\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u559c\u4e0d\u559c\u6b22\u8fd9\u67c4\u5251\uff1f\u201d \u3000\u3000\u963f\u98de\u4e5f\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u80fd\u8bf4\u5b9e\u8bdd\uff0c\u53c8\u4ece\u4e0d\u8bf4\u8c0e\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u67c4\u5251\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u6162\u6162\u7684\u8d70\u8fc7\u53bb\uff0c\u5750\u5728\u4ed6\u8eab\u65c1\u9053\uff1a\u201c\u8fd9\u662f\u6211\u6628\u5929\u665a\u4e0a\u7279\u5730\u66ff\u4f60\u53bb\u627e\u4eba\u94f8\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u663e\u5f97\u5f88\u5403\u60ca\uff0c\u9053\uff1a\u201c\u4f60\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u53d6\u8d77\u5251\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u67c4\u5251\u662f\u4e0d\u662f\u548c\u4f60\u4ee5\u524d\u4f7f\u7528\u7684\u4e00\u6837\uff1f\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u4e0d\u559c\u6b22\uff1f\u201d \u3000\u3000\u963f\u98de\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u66ff\u6211\u505a\u8fd9\u67c4\u5251\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u7528\u5b83\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u8eab\u5b50\u4f3c\u4e4e\u6709\u4e9b\u50f5\u6728\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u6211\u53bb\u6740\u4eba\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4e0d\u662f\u6740\u4eba\uff0c\u662f\u6551\u4eba\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6551\u4eba\uff1f\u6551\u8c01\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u5e73\u751f\u6700\u597d\u7684\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u963f\u98de\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u5931\u58f0\u9053\uff1a\u201c\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9ed8\u9ed8\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u963f\u98de\u82cd\u767d\u7684\u8138\u5df2\u53d1\u7ea2\uff0c\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u53c8\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u5148\u5750\u4e0b\u6765\uff0c\u6162\u9992\u7684\u542c\u6211\u8bf4\uff0c\u8fd9\u79cd\u4e8b\u7740\u6025\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u963f\u98de\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u7ec8\u4e8e\u5750\u4e0b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u9664\u4e86\u4f60\u4e4b\u5916\u201c\u8fd8\u6709\u56db\u4e2a\u6700\u5389\u5bb3\u7684\u9ad8\u624b\uff0c\u4f60\u77e5\u9053\u662f\u8c01\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u81ea\u7136\u662f\u2018\u5929\u673a\u8001\u4eba\u2019\uff0c\u7b2c\u4e8c\u4e2a\u4e0a\u5b98\u91d1\u8679\uff0c\u5f53\u7136\u674e\u5bfb\u6b22\u674e\u5927\u54e5\u4e5f\u4e0d\u4f1a \u6bd4\u4ed6\u4eec\u5dee\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u5462\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4eba\u53eb\u8346\u65e0\u547d\uff0c\u5e74\u7eaa\u6700\u8f7b\uff0c\u4e5f\u6700\u53ef\u6015\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6700\u53ef\u6015\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u662f\u4eba\uff0c\u6ca1\u6709\u4eba\u6027\uff0c\u4ed6\u4e00\u751f\u6700\u5927\u7684\u76ee\u7684\u662f\u6740\u4eba\uff0c\u6700\u5927\u7684\u4eab\u53d7\u4e5f \u662f\u6740\u4eba\uff0c\u9664\u4e86\u6740\u4eba\u5916\uff0c\u4ed6\u4ec0\u4e48\u90fd\u4e0d\u61c2\uff0c\u4e5f\u4e0d\u60f3\u53bb\u61c2\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u91cc\u95ea\u7740\u5149\uff0c\u9053\uff1a\u201c\u4ed6\u7528\u7684\u5175\u5668\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u653e\u4e0b\u90a3\u67c4\u5251\u9053\uff1a\u201c\u662f\u5251\uff01\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u4e0d\u7531\u81ea\u4e3b\u63e1\u8d77\u4e86\u5251\uff0c\u63e1\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u636e\u8bf4\uff0c\u4ed6\u7684\u5251\u6cd5\u548c\u4f60\u540c\u6837\u8f9b\u8fa3\uff0c\u4e5f\u540c\u6837\u5feb\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u5251\u6cd5\uff0c\u6211\u53ea\u61c2\u5982\u4f55\u7528\u5251\u523a\u4eba\u4ec7\u4eba\u7684\u54bd\u5589\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5251\u6cd5\uff0c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u5251\u6cd5\uff0c\u6700\u540e\u7684\u76ee\u7684\u90fd\u662f\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u2026\u2026\u674e\u5bfb\u6b22\u5df2\u843d\u5230\u8fd9\u4eba\u624b\u4e0a\uff1f\u201d\u6797\u4ed9\u513f\u53f9\u606f\u7740\u9053\uff1a\u201c\u4e0d\u4f46\u4ed6\uff0c \u8fd8\u6709\u4e0a\u5b98\u91d1\u8679\u2026\u2026\u4f46\u4e0a\u5b98\u91d1\u8679\u4e5f\u8bb8\u4e0d\u4f1a\u5728\u90a3\u91cc\uff0c\u4f60\u53ea\u8981\u5bf9\u4ed8\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5979\u4e0d\u8ba9\u963f\u98de\u8bf4\u8bdd\uff0c\u5f88\u5feb\u7684\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u7684\uff0c\u6c38\u8fdc\u4e0d\u77e5\u9053\u8fd9\u4eba\u6709\u591a\u53ef\u6015\uff01 \u4f60\u7684\u5251\u4e5f\u8bb8\u6bd4\u4ed6\u7094\uff0c\u53ef\u662f\uff0c\u4f60\u662f\u4eba\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u6211\u53ea\u60f3\u77e5\u9053\u8fd9\u4eba\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u8f7b\u629a\u7740\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e0d\u613f\u4f60\u518d\u4f7f\u5251\uff0c\u518d\u6740\u4eba\uff0c\u66f4\u4e0d\u613f\u4f60\u53bb\u5192\u9669\uff0c\u53ef\u662f\u4e3a\u4e86 \u674e\u5927\u54e5\u2026\u2026\u94b1\u2026\u2026\u6211\u4e0d\u80fd\u4e0d\u8ba9\u4f60\u53bb\uff0c\u6211\u4e0d\u80fd\u90a3\u4e48\u81ea\u79c1\u3002\u201d \u3000\u3000\u963f\u98de\u77a7\u7740\u5979\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u611f\u6fc0\u3002 \u3000\u3000\u6797\u4ed9\u513f\u56ed\u4e2d\u5df2\u6709\u773c\u6cea\u6d41\u4e0b\uff0c\u5782\u7740\u5934\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u7b54\u5e94\u4f60\uff0c\u544a\u8bc9\u4f60\u5982\u4f55\u53bb\u627e\u4ed6\uff0c\u53ef\u662f\u4f60 \u2026\u2026\u4f60\u4e5f\u5f97\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5c06\u4ed6\u7684\u624b\u63e1\u5f97\u5f88\u7d27\uff0c\u5e26\u6cea\u7684\u773c\u51dd\u6ce8\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u7b54\u5e94\u6211\uff0c\u4f60\u4e00\u5b9a \u8981\u56de\u6765\uff0c\u6211\u6c38\u8fdc\u5728\u7b49\u7740\u4f60\u2026\u2026\u201d \u3000\u3000\u8f66\u53a2\u5f88\u5927\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u5750\u5728\u89d2\u843d\u91cc\uff0c\u77a7\u7740\u9762\u524d\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u7ad9\u7740\u7684\u3002 \u3000\u3000\u4e58\u8f66\u65f6\uff0c\u4ed6\u7adf\u4e5f\u4e0d\u80af\u5750\u4e0b\u3002 \u3000\u3000\u65e0\u8bba\u8f66\u9a6c\u98a0\u7c38\u5f97\u591a\u5267\u70c8\uff0c\u8fd9\u4eba\u59cb\u7ec8\u7b14\u76f4\u7684\u7ad9\u7740\u50cf\u4e00\u6746\u67aa\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u4eba\uff0c\u751a\u81f3\u65e0\u6cd5\u60f3\u8c61\u4e16\u4e0a\u4f1a\u6709\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u4ed6\u672c\u89c9\u5f97\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u90fd\u662f\u5446\u5b50\uff0c\u90fd\u53ef\u88ab\u4ed6\u73a9\u5f04\u4e8e\u80a1\u638c\u4e4b\u4e0a\u3002 \u3000\u3000\u4f46\u8feb\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u5728\u8fd9\u4eba\u9762\u524d\uff0c\u4ed6\u5fc3\u91cc\u7adf\u5e26\u7740\u51e0\u5206\u754f\u60e7\u3002 \u3000\u3000\u53ea\u8981\u6709\u8fd9\u4eba\u5728\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u6709\u4e00\u80a1\u4e0d\u53ef\u5f62\u5bb9\u7684\u6740\u6c14\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u53c8\u5f88\u5f97\u610f\u3002 \u3000\u3000\u4ed6\u6240\u8981\u6c42\u7684\uff0c\u4e0a\u5b98\u91d1\u8679\u90fd\u5df2\u7b54\u5e94\u3002 \u3000\u3000\u82f1\u96c4\u5e16\u5df2\u53d1\u51fa\uff0c\u5df2\u6709\u5f88\u591a\u4eba\u63a5\u5230\uff0c\u7ed3\u4e49\u7684\u76db\u5178\uff0c\u8ba2\u5728\u4e0b\u6708\u521d\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6709\u8346\u65e0\u547d\u548c\u4ed6\u540c\u53bb\uff0c\u674e\u5bfb\u6b22\u5fc5\u6b7b\u65e0\u7591\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u51fa\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u6551\u5f97\u4e86\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u4ed6\u5410\u4e86\u53e3\u6c14\uff0c\u95ed\u8d77\u773c\u775b\uff0c\u773c\u524d\u7acb\u523b\u6cdb\u8d77\u4e86\u4e00\u5f20\u751c\u800c\u7f8e\u7684\u7b11\u8138\uff0c\u6b63\u8eba\u5728\u4ed6\u6000\u91cc\uff0c\u5bf9\u4ed6\u4f4e\u4f4e \u871c\u8bed\uff1a\u201c\u4f60\u771f\u7684\u5df2\u4e0d\u662f\u4e2a\u5b69\u5b50\u4e86\uff0c\u4f60\u61c2\u5f97\u7684\u4e8b\u6bd4\u4efb\u4f55\u4eba\u90fd\u591a\uff0c\u6211\u771f\u60f3\u4e0d\u51fa\uff0c\u8fd9\u4e9b\u4e8b\u4f60\u662f\u4ece\u54ea \u91cc\u5b66\u6765\u7684\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u9f99\u5c0f\u4e91\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u4e86\u5fae\u7b11\u3002 \u3000\u3000\u201c\u6709\u4e9b\u4e8b\u662f\u6839\u672c\u4e0d\u5fc5\u5b66\u7684\uff0c\u5230\u4e86\u65f6\u5019\uff0c\u81ea\u7136\u5c31\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u7684\u786e\u5df2\u662f\u4e2a\u5927\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u5df2\u8db3\u4ee5\u4ee4\u5927\u591a\u6570\u8fd8\u672a\u771f\u7684\u957f\u5927\u7684\u5c11\u5e74\u9676\u9189\u3002 \u3000\u3000\u5b69\u5b50\u62fc\u547d\u60f3\u88c5\u6210\u5927\u4eba\u7684\u6a21\u6837\uff0c\u8001\u4eba\u62fc\u547d\u60f3\u8ba9\u522b\u4eba\u89c9\u5f97\u4ed6\u5b69\u5b50\u6c14\u2014\u2014\u8fd9\u4e5f\u662f\u4eba\u7c7b\u8bb8\u591a\u79cd\u65e0 \u53ef\u5948\u4f55\u7684\u60b2\u54c0\u4e4b\u4e00\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u60f3\u5230\u8fd9\u91cc\u65e2\u5df2\u9676\u9189\uff0c\u5c31\u4e0d\u518d\u4f1a\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u9f99\u5c0f\u4e91\u60f3\u5f97\u5374\u66f4\u6df1\u4e00\u5c42\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5bf9\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u4e3a\u4e86\u8981\u6253\u542c\u674e\u5bfb\u6b22\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u5c31\u6e05\u9192\u4e86\u8bb8\u591a\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u8981\u6253\u542c\u674e\u5bfb\u6b22\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u5979\u60f3\u6551\u674e\u5bfb\u6b22\uff1f \u3000\u3000\u8fd9\u5f53\u7136\u7edd\u65e0\u53ef\u80fd\uff0c\u9f99\u5c0f\u4e91\u4e5f\u77e5\u9053\u6797\u4ed9\u513f\u5bf9\u674e\u5bfb\u6b22\u7684\u75db\u6068\uff0c\u4e5f\u77e5\u9053\u5979\u66fe\u7ecf\u8bbe\u8ba1\u8981\u4e0a\u5b98\u91d1\u8679 \u548c\u8346\u65e0\u547d\u6740\u6b7b\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u201c\u90a3\u4e48\uff0c\u5979\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u65e0\u6cd5\u518d\u60f3\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u73b0\u5728\u60c5\u51b5\u5df2\u53d8\u4e86\uff0c\u90a3\u65f6\u6797\u4ed9\u513f\u867d\u7136\u60f3\u501f\u4e0a\u5b98\u91d1\u8679\u4e4b\u624b\u6740\u6b7b\u674e\u5bfb\u6b22\uff0c\u4f46\u73b0\u5728\u60c5\u51b5 \u5374\u53d8\u5f97\u66f4\u5fae\u5999\u3002 \u3000\u3000\u5979\u82e5\u60f3\u548c\u4e0a\u5b98\u91d1\u8679\u4fdd\u6301\u5747\u8861\u7684\u5c40\u52bf\uff0c\u5c31\u4e0d\u80fd\u8ba9\u674e\u5bfb\u6b22\u548c\u963f\u98de\u4e24\u4e2a\u4eba\u6b7b\uff01 \u3000\u3000\u5426\u5219\u4e0a\u5b98\u91d1\u8679\u5c31\u4f1a\u8e29\u5728\u5979\u5934\u4e0a\uff0c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u81ea\u5df1\u5df2\u9732\u51fa\u4e86\u53e3\u98ce\uff0c\u4ed6\u7684\u610f\u601d\u5979\u5df2\u7ecf\u975e\u5e38 \u4e86\u89e3\uff1a\u201c\u6211\u5c31\u662f\u6211\uff0c\u65e2\u4e0d\u662f\u8346\u65e0\u547d\uff0c\u4e5f\u4e0d\u662f\u963f\u98de\uff0c\u6211\u4eec\u53ea\u4e0d\u8fc7\u662f\u5728\u4e92\u76f8\u5229\u7528\u800c\u5df2\uff0c\u7b49\u5230\u8fd9\u5229 \u7528\u7684\u4ef7\u503c\u6d88\u5931\uff0c\u5c31\u53ef\u4ee5\u518d\u89c1\uff01\u201d \u3000\u3000\u6c5f\u6e56\u98ce\u4e91\u7684\u53d8\u5316\uff0c\u6b63\u548c\u5973\u4eba\u7684\u5fc3\u4e00\u6837\uff0c\u7edd\u4e0d\u662f\u4efb\u4f55\u4eba\u6240\u80fd\u731c\u900f\u7684\u3002 \u3000\u3000\u8f66\u9a6c\u5728\u57ce\u5e02\u4e2d\u5fc3\u6700\u7e41\u534e\u70ed\u95f9\u7684\u5730\u533a\u4e2d\u505c\u4e0b\uff0c\u505c\u5728\u4e00\u5bb6\u6c14\u6d3e\u5f88\u5927\u97f5\u7ef8\u7f0e\u5e84\u95e8\u53e3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c31\u88ab\u85cf\u5728\u8fd9\u91cc\u4e48\uff1f \u3000\u3000\u9f99\u5578\u4e91\u7236\u5b50\u679c\u7136\u4e0d\u6127\u4e3a\u5389\u5bb3\u4eba\u7269\uff0c\u5f88\u4e86\u89e3\u201c\u5c0f\u9690\u9690\u4e8e\u5c71\uff0c\u5927\u9690\u9690\u4e8e\u5e02\u201d\u8fd9\u65ec\u8bdd\uff0c\u77e5\u9053\u6700 \u70ed\u95f9\u7684\u5730\u65b9\uff0c\u8d8a\u5bb9\u6613\u907f\u4eba\u8033\u76ee\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u7ad9\u8d77\u6765\uff0c\u966a\u7b11\u9053\uff1a\u201c\u8bf7\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u5148\u8d70\u3002\u201d \u3000\u3000\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4ed6\u53ea\u8ddf\u9f99\u5c0f\u4e91\u8bf4\u4e86\u8fd9\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u8d70\u5728\u522b\u4eba\u524d\u9762\uff0c\u4e0d\u613f\u6709\u4efb\u4f55\u4eba\u8ddf\u5728\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u638c\u67dc\u7684\u548c\u5e97\u4f19\u4eec\u7684\u5949\u8fce\u793c\u7b11\u4e2d\u7a7f\u8fc7\u5e97\u94fa\u3002 \u3000\u3000\u540e\u9762\u5c31\u662f\u5806\u5b58\u7ef8\u7f0e\u7684\u4ed3\u5e93\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u88ab\u85cf\u5728\u7ef8\u7f0e\u4ed3\u5e93\u91cc\u4e48\uff1f\u8fd9\u5230\u771f\u662f\u4e2a\u597d\u5730\u65b9\u3002 \u3000\u3000\u4f46\u9f99\u5c0f\u4e91\u8fd8\u662f\u6ca1\u6709\u505c\u7559\uff0c\u53c8\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u518d\u540e\u9762\u5c31\u662f\u540e\u95e8\u3002 \u3000\u3000\u540e\u95e8\u5916\u4e5f\u505c\u7740\u540c\u6837\u4e00\u8f86\u9a6c\u8f66\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u8fd9\u6b21\u5e76\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u5411\u8346\u65e0\u547d\u8eac\u8eab\u4e00\u793c\uff0c\u5c31\u4e0a\u4e86\u8f66\u3002 \u3000\u3000\u539f\u6765\u674e\u5bfb\u6b22\u5e76\u6ca1\u6709\u85cf\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u8fd9\u6837\u505a\uff0c\u53ea\u4e0d\u8fc7\u662f\u8eb2\u907f\u8ffd\u8e2a\u7684\u70df\u5e55\u3002 \u3000\u3000\u8fd9\u7236\u5b50\u4e24\u4eba\u60f3\u5f97\u6bd4\u4efb\u4f55\u4eba\u97f6\u66f4\u6df1\u4e00\u5c42\u3002 \u3000\u3000\u8f66\u9a6c\u81ea\u540e\u8857\u8f6c\u51fa\uff0c\u98a0\u5411\u90ca\u5916\u3002 \u3000\u3000\u7136\u540e\u5c31\u505c\u5728\u90ca\u5916\u7684\u4e00\u5bb6\u7c73\u4ed3\u524d\uff0c\u4f46\u8fd9\u7c73\u4ed3\u4e5f\u4e0d\u662f\u56da\u7981\u674e\u5bfb\u6b22\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8fd9\u7c73\u4ed3\u540e\u95e8\uff0c\u53c8\u6362\u4e86\u6b21\u8f66\u3002 \u3000\u3000\u8fd9\u6b21\u6362\u7684\u662f\u8f86\u8fd0\u7c73\u8fdb\u57ce\u7684\u725b\u8f66\u3002\u7c73\u5305\u5806\u4e2d\uff0c\u53ea\u6709\u4e24\u4eba\u5bb9\u8eab\u4e4b\u5730\u3002\u9f99\u5c0f\u4e91\u966a\u7b11\u9053\uff1a\u201c\u59d4\u66f2 \u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u725b\u8f66\u53c8\u9a70\u56de\u5e02\u533a\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f46\u8ba1\u5212\u5468\u5bc6\uff0c\u884c\u52a8\u8fc5\u901f\uff0c\u8def\u7ebf\u7684\u8f6c\u53d8\uff0c\u66f4\u51fa\u5165\u610f\u5916\u3002 \u3000\u3000\u5c31\u7b97\u662f\u4ee5\u8ffd\u67e5\u8d3c\u8e2a\u540d\u9707\u9ed1\u9053\u7684\u4e5d\u57ce\u540d\u6355\uff0c\u4eba\u79f0\u201c\u4e5d\u9f3b\u72ee\u5b50\u72d7\u201d\u7684\u4e07\u5143\u4e00\u5931\uff0c\u8ffd\u5230\u8fd9\u91cc\uff0c \u4e5f\u4e07\u4e07\u8ffd\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u4e5f\u77e5\u9053\u8346\u65e0\u547d\u7edd\u4e0d\u4f1a\u5938\u8d5e\u4ed6\u7684\uff0c\u53ea\u4e0d\u8fc7\u5e0c\u671b\u4ed6\u9762\u4e0a\u96c4\u591a\u5c11\u9732\u51fa\u4e00\u4e1d\u8d5e\u7f8e\u7684\u795e\u8272\u3002 \u3000\u3000\u505a\u4e86\u5f97\u610f\u4e8b\u7684\u4eba\u5f97\u4e0d\u5230\u522b\u4eba\u5938\u8d5e\uff0c\u5c31\u597d\u50cf\u7a7f\u4e86\u6700\u5f97\u610f\u7684\u8863\u670d\u7684\u5973\u4eba\u53bb\u4f1a\u89c1\u60c5\u4eba\u65f6\uff0c\u5979\u7684 \u60c5\u4eba\u8fde\u77a7\u90fd\u6ca1\u6709\u77a7\u5979\u8863\u670d\u4e00\u773c\u3002 \u3000\u3000\u5c24\u5176\u9f99\u5c0f\u4e91\u6bd5\u7adf\u8fd8\u6ca1\u6709\u5b8c\u5168\u957f\u5927\u3002 \u3000\u3000\u5728\u7537\u4eba\u4eec\u773c\u4e2d\uff0c\u5b69\u5b50\u548c\u5973\u4eba\u7684\u5fc3\u7406\u5f80\u5f80\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u8346\u65e0\u547d\u8138\u4e0a\u504f\u504f\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u725b\u8f66\u8f6c\u5165\u4e00\u6761\u5e7d\u9759\u7684\u957f\u8857\uff0c\u8fd9\u6761\u8857\u53ea\u6709\u4e03\u6237\u4eba\u5bb6\u3002 \u3000\u3000\u8fd9\u4e03\u6237\u4eba\u5bb6\u4e0d\u662f\u738b\u4faf\u8d35\u80c4\u5c31\u662f\u5f53\u671d\u5927\u5458\u3002 \u3000\u3000\u5b9a\u4e0a\u8fd9\u6761\u8857\uff0c\u5176\u4e2d\u6709\u4e00\u5bb6\u7684\u504f\u95e8\u7a81\u7136\u5f00\u4e86\u3002 \u3000\u3000\u725b\u8f66\u7adf\u76f4\u9a70\u800c\u5165\u3002 \u3000\u3000\u8fd9\u4e00\u5bb6\u8c01\u90fd\u77e5\u9053\u662f\u5f53\u4eca\u6e05\u6d41\u4e4b\u9996\uff0c\u5de6\u90fd\u5fa1\u53f2\u6a0a\u6797\u6cc9\u7684\u5c45\u5904\u3002 \u3000\u3000\u6c5f\u6e56\u8c6a\u6770\u7edd\u4e0d\u53ef\u80fd\u548c\u8fd9\u79cd\u5f53\u671d\u6e05\u8981\u642d\u4e0a\u5173\u7cfb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u96be\u9053\u4f1a\u88ab\u85cf\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u8fd9\u7b80\u76f4\u7edd\u65e0\u53ef\u80fd\u3002 \u3000\u3000\u4f46\u7ad9\u5728\u5927\u5385\u77f3\u9636\u4e0a\u542b\u7b11\u76f8\u8fce\u7684\uff0c\u5374\u504f\u504f\u662f\u9f99\u5578\u4e91\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e00\u4e0b\u725b\u8f66\uff0c\u9f99\u5578\u4e91\u5c31\u8fce\u4e86\u4e0a\u53bb\uff0c\u957f\u63d6\u542b\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u8346\u5148\u751f\u5927\u540d\uff0c\u4eca\u65e5\u5f97\u89c1\uff0c\u5feb \u6170\u5e73\u751f\uff0c\u53ea\u56e0\u6b64\u53f0\u5fc5\u987b\u907f\u4eba\u8033\u76ee\uff0c\u662f\u4ee5\u6709\u5931\u8fdc\u8fce\uff0c\u6055\u7f6a\u6055\u7f6a\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u775b\u53ea\u662f\u51dd\u89c6\u7740\u81ea\u5df1\u7684\u624b\uff0c\u8fde\u77a7\u90fd\u6ca1\u6709\u77a7\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8fd8\u662f\u7b11\u5bb9\u6ee1\u9762\uff0c\u9053\uff1a\u201c\u5802\u4e0a\u5df2\u6446\u4e86\u8fce\u98ce\u4e4b\u9152\uff0c\u4f46\u8bf7\u8346\u5148\u751f\u559d\u4e24\u676f\uff0c\u7a0d\u6da4\u5f81\u5c18\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53ea\u662f\u51b7\u51b7\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u5c31\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u662f\u6a0a\u6797\u516c\u7684\u5bd3\u6240\uff0c\u53ea\u56e0\u6a0a\u8001\u5148\u751f\u65e5\u524d\u7a81\u7136\u52a8\u4e86\u6e38\u5174\uff0c\u7687\u4e0a\u4e5f\u7279\u522b \u6069\u51c6\u7ed9\u5047\u4e09\u6708\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u4e86\u5f97\u610f\u4e4b\u8272\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6a0a\u4f11\u516c\u72ec\u5c45\u7ec8\u751f\uff0c\u4ed6\u8001\u4eba\u5bb6\u65e2\u5df2\u51fa \u6e38\uff0c\u8fd9\u91cc\u7684\u7ba1\u5bb6\u53c8\u6070\u597d\u662f\u5728\u4e0b\u7684\u597d\u53cb\u3002\u662f\u4ee5\u5f80\u4e0b\u624d\u6709\u673a\u4f1a\u501f\u8fd9\u5730\u65b9\u4e00\u7528\u3002\u201d \u3000\u3000\u8bf4\u7a7f\u4e86\uff0c\u4ed6\u80fd\u501f\u5f97\u5230\u8fd9\u5730\u65b9\u5e76\u4e0d\u7a00\u5947\uff0c\u56e0\u4e3a\u201c\u6709\u94b1\u80fd\u4ee4\u9b3c\u63a8\u78e8\u201d\uff0c\u4f46\u522b\u4eba\u5374\u7684\u786e\u662f\u6c38\u8fdc \u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u8fd9\u4e5f\u5b9e\u5728\u96be\u602a\u9f99\u5578\u4e91\u5f97\u610f\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u5728\u51dd\u6ce8\u81ea\u5df1\u7684\u624b\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6ca1\u6709\u4eba\u80fd\u8ffd\u8e2a\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u77ac\u5373\u7b11\u9053\uff1a\u201c\u82e5\u662f\u771f\u7684\u6709\u4eba\u8ffd\u8e2a\u5230\u8fd9\u91cc\uff0c\u5728\u4e0b\u60c5\u613f\u5411\u4ed6\u4eec\u53e9\u5934\u4e3a\u793c \uff0c\u4ee5\u793a\u656c\u610f\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u201c\u597d\uff0c\u4f60\u51c6\u5907\u53e9\u5934\u5427\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u201c\u82e5\u662f\u2026\u2026\u201d \u3000\u3000\u53ea\u8bf4\u4e86\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4ed6\u9762\u4e0a\u7684\u7b11\u5bb9\u7a81\u7136\u51bb\u7ed3\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u968f\u7740\u4ed6\u7236\u4eb2\u7684\u76ee\u5149\u8f6c\u9996\u77a7\u4e86\u8fc7\u53bb\uff0c\u82cd\u767d\u7684\u8138\u8272\u4e5f\u53d1\u4e86\u9752\u3002 \u3000\u3000\u5899\u89d2\u7ad9\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u3002 \u7b2c\u4e94\u5341\u4e03\u7ae0\u3000\u706b\u82b1>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u4e03\u7ae0\u3000\u706b\u82b1 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7740\u5957\u9752\u5e03\u8863\u670d\uff0c\u672c\u6765\u5f88\u65b0\uff0c\u4f46\u73b0\u5728\u5df2\u6ee1\u662f\u6ce5\u6c61\u3001\u6c57\u57a2\uff0c\u65f6\u95f4\u3001\u819d\u5934\u4e5f\u5df2\u88ab\u78e8\u7834 \u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u4e5f\u5f88\u810f\uff0c\u5934\u53d1\u66f4\u4e71\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u8fdc\u8fdc\u7ad9\u5728\u90a3\u91cc\uff0c\u9f99\u5578\u4e91\u90fd\u80fd\u611f\u89c9\u5230\u4e00\u822c\u903c\u4eba\u7684\u6740\u6c14\uff01 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u770b\u6765\u5c31\u5982\u540c\u90a3\u67c4\u63d2\u5728\u4ed6\u8170\u5e26\u4e0a\u7684\u5251\u3002 \u3000\u3000\u4e00\u67c4\u6ca1\u6709\u9798\u7684\u5251\uff01 \u3000\u3000\u662f\u963f\u98de\uff01 \u3000\u3000\u963f\u98de\u6bd5\u7adf\u6765\u4e86\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u8bb8\u53ea\u6709\u963f\u98de\u4e00\u4e2a\u4eba\u80fd\u8ffd\u8e2a\u5230\u8fd9\u91cc\uff01 \u3000\u3000\u6700\u72e1\u733e\uff0c\u6700\u4f1a\u9003\u907f\uff0c\u6700\u4f1a\u8eb2\u85cf\u7684\u52a8\u7269\u662f\u72d0\u72f8\u3002 \u3000\u3000\u6700\u7cbe\u660e\uff0c\u53d7\u8fc7\u6700\u4e25\u683c\u8bad\u7ec3\u7684\u730e\u72ac\uff0c\u4e5f\u672a\u5fc5\u80fd\u8ffd\u5f97\u7740\u72d0\u72f8\u3002 \u3000\u3000\u4f46\u963f\u98de\u5341\u4e00\u5c81\u65f6\u5c31\u66fe\u7ecf\u8d64\u624b\u7a7a\u62f3\u6349\u4f4f\u4e86\u4e00\u6761\u8001\u72d0\u72f8\u3002 \u3000\u3000\u8fd9\u6bb5\u8ffd\u8e2a\u7684\u8def\u7a0b\u663e\u7136\u5f88\u8270\u82e6\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u8fd9\u4e48\u810f\u3002 \u3000\u3000\u4f46\u8fd9\u624d\u662f\u771f\u6b63\u7684\u963f\u98de\u3002 \u3000\u3000\u53ea\u6709\u8fd9\u6837\uff0c\u624d\u80fd\u6613\u51fa\u4ed6\u90a3\u79cd\u527d\u608d\u3001\u51b7\u9177\u3001\u5484\u5484\u903c\u4eba\u7684\u91ce\u6027\uff01 \u3000\u3000\u4e00\u79cd\u6c89\u9759\u7684\u91ce\u6027\uff01\u5947\u7279\u7684\u91ce\u6027\uff01 \u3000\u3000\u9f99\u5578\u4e91\u5c45\u7136\u5f88\u5feb\u6062\u590d\u4e86\u9547\u5b9a\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u963f\u98de\u5144\uff0c\u4e45\u8fdd\u4e45\u8fdd\u3002\u201d \u3000\u3000\u963f\u98de\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u5144\u53f0\u7adf\u771f\u7684\u80fd\u8ffd\u8e2a\u5230\u8fd9\u91cc\uff0c\u4f69\u670d\u4f69\u670d\u3002\u201d \u3000\u3000\u963f\u98de\u8fd8\u662f\u51b7\u51b7\u7684\u77a7\u7740\uff0c\u4ed6\u7684\u773c\u775b\u660e\u4eae\u3001\u9510\u5229\uff0c\u7ecf\u8fc7\u4e24\u5929\u7684\u8ffd\u8e2a\uff0c\u4f3c\u4e4e\u53c8\u6062\u590d\u4e86\u51e0\u5206\u6614\u65e5 \u90a3\u79cd\u5251\u950b\u822c\u7684\u5149\u8292\u3002 \u3000\u3000\u90a3\u548c\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u775b\u6b63\u662f\u79cd\u6781\u5f3a\u70c8\u7684\u5bf9\u6bd4\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5144\u53f0\u8ffd\u8e2a\u7684\u624b\u6bb5\u867d\u9ad8\uff0c\u53ea\u53ef\u60dc\u5374\u4e5f\u88ab\u8fd9\u4f4d\u8346\u5148\u751f\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u5411\u8346\u65e0\u547d\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e5f\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u4e24\u4eba\u7684\u76ee\u5149\u76f8\u9047\uff0c\u5c31\u5b9b\u5982\u4e00\u67c4\u5251\u523a\u4e0a\u4e86\u51b0\u51b7\u7684\u7070\u6697\u7684\u5343\u5e74\u5ca9\u77f3\u3002 \u3000\u3000\u8c01\u4e5f\u731c\u4e0d\u51fa\u662f\u5251\u950b\u9510\u5229\uff1f\u8fd8\u662f\u5ca9\u77f3\u575a\u786c\uff01 \u3000\u3000\u4e24\u4eba\u867d\u7136\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u4e24\u4eba\u7684\u76ee\u5149\u95f4\u5374\u4f3c\u5df2\u51b2\u51fb\u51fa\u4e00\u4e32\u706b\u82b1\uff01 \u3000\u3000\u9f99\u5578\u4e91\u77a7\u4e86\u77a7\u8346\u65e0\u547d\uff0c\u53c8\u77a7\u4e86\u77a7\u963f\u98de\u9053\uff1a\u201c\u8346\u5148\u751f\u867d\u5df2\u53d1\u89c9\u4e86\u4f60\uff0c\u5374\u4e00\u76f4\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c \u4f60\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u7684\u76ee\u5149\u4f3c\u5df2\u88ab\u8346\u65e0\u547d\u5438\u5f15\uff0c\u59cb\u7ec8\u672a\u66fe\u79fb\u5f00\u8fc7\u7247\u523b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u53c8\u7b11\u4e86\u7b11\uff0c\u9992\u9992\u60a0\u7136\uff1a\u201c\u56e0\u4e3a\u8346\u5148\u751f\u672c\u5c31\u5e0c\u671b\u4f60\u6765\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5411\u8346\u65e0\u547d\u63a5\u7740\u7b11\u9053\uff1a\u201c\u8346\u5148\u751f\uff0c\u5728\u4e0b\u731c\u7684\u4e0d\u9519\u5427\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u76ee\u5149\u4f3c\u4e5f\u88ab\u963f\u98de\u6240\u5438\u5f15\uff0c\u4e5f\u59cb\u7ec8\u6ca1\u6709\u79fb\u52a8\u8fc7\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u9f99\u5578\u4e91\u53c8\u5927\u7b11\u9053\uff1a\u201c\u8346\u5148\u751f\u5e0c\u671b\u4f60\u6765\uff0c\u53ea\u6709\u4e00\u4e2a\u539f\u56e0\uff0c\u56e0\u4e3a\u4ed6\u8981\u6740\u4f60\uff01\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u8346\u5148\u751f\u8981\u6740\u7684\u4eba\uff0c\u5230\u4eca\u8fd8\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u6d3b\u7740\u7684\uff01\u201d \u3000\u3000\u963f\u98de\u7684\u76ee\u5149\u8fd9\u624d\u79fb\u5411\u8346\u65e0\u547d\u7684\u5251\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u76ee\u5149\u4e5f\u51e0\u4e4e\u5728\u540c\u4e00\u5239\u90a3\u95f4\u79fb\u5411\u963f\u98de\u8170\u5e26\u4e0a\u63d2\u7740\u7684\u5251\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u4e16\u4e0a\u6700\u76f8\u540c\u7684\u4e24\u67c4\u5251\uff01 \u3000\u3000\u8fd9\u4e24\u67c4\u5251\u65e2\u4e0d\u662f\u795e\u5175\u5229\u5668\uff0c\u4e5f\u4e0d\u662f\u540d\u5320\u6240\u94f8\u3002 \u3000\u3000\u8fd9\u4e24\u67c4\u5251\u867d\u7136\u950b\u5229\uff0c\u4f46\u592a\u8584\uff0c\u592a\u8106\uff01\u90fd\u5f88\u5bb9\u6613\u88ab\u6298\u65ad\u3002 \u3000\u3000\u5251\u867d\u76f8\u540c\uff0c\u4e24\u4eba\u63d2\u5251\u7684\u65b9\u6cd5\u5374\u4e0d\u540c\u3002 \u3000\u3000\u963f\u98de\u7684\u5251\u63d2\u5728\u8170\u4e2d\u592e\uff0c\u5251\u67c4\u662f\u5411\u53f3\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u5251\u5374\u63d2\u5728\u8170\u5e26\u8fb9\u7684\uff0c\u5251\u67c4\u5411\u5de6\u3002 \u3000\u3000\u8fd9\u4e24\u67c4\u5251\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u4e5f\u6709\u79cd\u522b\u4eba\u65e0\u6cd5\u4e86\u89e3\u7684\u5947\u7279\u5438\u5f15\u529b\uff01 \u3000\u3000\u4e24\u4eba\u7684\u76ee\u5149\u4e00\u63a5\u89e6\u5230\u5bf9\u65b9\u7684\u5251\uff0c\u5c31\u4e00\u6b65\u6b65\u5411\u5bf9\u65b9\u8d70\u8fc7\u53bb\uff0c\u4f46\u76ee\u5149\u8fd8\u662f\u59cb\u7ec8\u672a\u79bb\u5f00\u5bf9\u65b9\u7684 \u5251\uff01 \u3000\u3000\u7b49\u5230\u4e24\u4eba\u4e4b\u95f4\u76f8\u8ddd\u4ec5\u6709\u4e94\u5c3a\u65f6\uff0c\u4e24\u4eba\u7a81\u7136\u4e00\u8d77\u505c\u4f4f\u4e86\u811a\u6b65\uff01 \u3000\u3000\u7136\u540e\uff0c\u4e24\u4eba\u5c31\u50cf\u9489\u5b50\u822c\u88ab\u9489\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8346\u65e0\u547d\u7a7f\u7684\u662f\u4ef6\u5f88\u77ed\u7684\u9ec4\u886b\uff0c\u886b\u89d2\u53ea\u80fd\u63a9\u53ca\u819d\u76d6\uff0c\u8896\u53e3\u662f\u7d27\u675f\u7740\u7684\uff0c\u624b\u6307\u7ec6\u800c\u957f\uff0c\u4f46\u9aa8 \u91cc\u51f8\u51fa\uff0c\u663e\u5f97\u5f88\u6709\u529b\uff01 \u3000\u3000\u963f\u98de\u7684\u8863\u6749\u66f4\u77ed\uff0c\u8896\u53e3\u51e0\u4e4e\u5df2\u88ab\u5b8c\u5168\u6495\u4e86\u4e0b\u6765\uff0c\u624b\u80cc\u4e5f\u5f88\u7ec6\uff0c\u5f88\u957f\uff0c\u4f46\u5374\u5f88\u7c97\u7cd9\uff0c\u5b9b\u5982 \u7802\u77f3\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u4e0d\u4fee\u8fb9\u5e45\uff0c\u6307\u7532\u5374\u90fd\u5f88\u77ed\u3002 \u3000\u3000\u800c\u5165\u90fd\u4e0d\u613f\u5b58\u6709\u4efb\u4f55\u4e1c\u897f\u59a8\u788d\u4ed6\u4eec\u51fa\u4e8e\u62d4\u5251\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u4e16\u4e0a\u6700\u76f8\u50cf\u7684\u4e24\u4e2a\u4eba\uff01 \u3000\u3000\u73b0\u5728\u4e24\u4eba\u7ec8\u4e8e\u76f8\u9047\u4e86\u3002 \u3000\u3000\u53ea\u6709\u5728\u4e24\u4eba\u7ad9\u5728\u4e00\u8d77\u65f6\uff0c\u4f60\u4ed4\u7ec6\u89c2\u67e5\uff0c\u624d\u80fd\u53d1\u89c9\u8fd9\u4e24\u4eba\u5916\u8c8c\u867d\u76f8\u4f3c\uff0c\u4f46\u5728\u57fa\u672c\u4e0a\uff0c\u6c14\u8d28 \u5374\u662f\u5b8c\u5168\u4e0d\u540c\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u8138\u4e0a\uff0c\u5c31\u50cf\u662f\u5e26\u7740\u4e2a\u9762\u5177\uff0c\u6c38\u8fdc\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u53d8\u5316\u3002 \u3000\u3000\u963f\u98de\u7684\u8138\u867d\u4e5f\u662f\u6c89\u9759\u7684\uff0c\u51b7\u9177\u7684\uff0c\u4f46\u76ee\u5149\u968f\u65f6\u90fd\u53ef\u80fd\u50cf\u706b\u7130\u822c\u71c3\u70e7\u8d77\u6765\uff0c\u5c31\u7b97\u5c06\u81ea\u5df1\u7684 \u751f\u547d\u548c\u7075\u9b42\u90fd\u70e7\u6bc1\u4e5f\u5728\u6240\u4e0d\u60dc\u3002 \u3000\u3000\u800c\u8346\u65e0\u547d\u7684\u6574\u4e2a\u4eba\u5374\u5df2\u662f\u4e00\u5806\u6b7b\u7070\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u751f\u547d\u8fd8\u672a\u5f00\u59cb\u65f6\uff0c\u5df2\u88ab\u70e7\u6210\u4e86\u6b7b\u7070\u3002 \u3000\u3000\u963f\u98de\u53ef\u4ee5\u5fcd\u8010\uff0c\u53ef\u4ee5\u7b49\uff0c\u4f46\u5374\u7edd\u4e0d\u80fd\u5fcd\u53d7\u4efb\u4f55\u4eba\u7684\u59d4\u66f2\u3002 \u3000\u3000\u8346\u65e0\u547d\u53ef\u4ee5\u4e3a\u4e00\u53e5\u8bdd\u6740\u4eba\uff0c\u751a\u81f3\u4e3a\u4e86\u67d0\u4e00\u79cd\u773c\u8272\u6740\u4eba\uff0c\u4f46\u5230\u4e86\u5fc5\u8981\u65f6\uff0c\u5374\u53ef\u4ee5\u5fcd\u53d7\u4efb\u4f55 \u59d4\u66f2\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u5f88\u5947\u7279\uff0c\u5f88\u5201\u6015\u3002 \u3000\u3000\u8c01\u4e5f\u731c\u4e0d\u9002\u4e0a\u5929\u4e3a\u4ec0\u4e48\u8981\u9020\u51fa\u8fd9\u4e48\u4e24\u4e2a\u4eba\uff0c\u53c8\u504f\u504f\u8981\u4ed6\u4eec\u76f8\u9047\u3002 \u3000\u3000\u79cb\u5df2\u6b8b\u3002 \u3000\u3000\u6728\u53f6\u51cb\u96f6\u3002 \u3000\u3000\u98ce\u4e0d\u5927\uff0c\u4f46\u9ec4\u53f6\u8427\u8427\u800c\u843d\uff0c\u96be\u9053\u662f\u88ab\u4ed6\u4eec\u7684\u6740\u6c14\u6240\u6467\u843d\u7684\uff1f \u3000\u3000\u5929\u5730\u95f4\u7684\u786e\u5145\u6ee1\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u8427\u7d22\u51c4\u51c9\u4e4b\u610f\u3002 \u3000\u3000\u4e24\u4eba\u7684\u5251\u867d\u7136\u8fd8\u90fd\u63d2\u5728\u8170\u5e26\u4e0a\uff0c\u4e24\u4eba\u867d\u7136\u8fd8\u90fd\u8fde\u624b\u6307\u90fd\u6ca1\u6709\u52a8\uff0c\u4f46\u9f99\u5578\u4e91\u7236\u5b50\u5374\u5df2\u7d27\u5f20 \u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5bd2\u5149\u95ea\u52a8\uff01 \u3000\u3000\u5341\u4f59\u9053\u5bd2\u5149\u5e26\u7740\u5c16\u9510\u7684\u98ce\u58f0\uff0c\u51fb\u5411\u963f\u98de\uff01 \u3000\u3000\u9f99\u5578\u4e91\u7adf\u5148\u51fa\u4e86\u624b\u3002 \u3000\u3000\u4ed6\u81ea\u7136\u4e5f\u5e76\u4e0d\u5962\u671b\u8fd9\u4e9b\u6697\u5668\u80fd\u51fb\u5012\u963f\u98de\uff0c\u4f46\u53ea\u8981\u963f\u98de\u56e0\u6b64\u800c\u7a0d\u6709\u5206\u5fc3\uff0c\u8346\u65e0\u547d\u7684\u5251\u5c31\u53ef \u4ee5\u523a\u4ed6\u54bd\u5589\uff01 \u3000\u3000\u5251\u5149\u66b4\u8d77\uff01 \u3000\u3000\u4e00\u8fde\u4e32\u201c\u53ee\u53ee\u201d\u58f0\u97f3\u540e\uff0c\u6ee1\u5929\u5bd2\u5149\u5982\u661f\u96e8\u822c\u5815\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u5251\u5df2\u51fa\u4e8e\uff0c\u5251\u950b\u5c31\u5728\u963f\u98de\u8033\u7554\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u5df2\u63e1\u7740\u5251\u67c4\uff0c\u4f46\u5251\u5c16\u8fd8\u672a\u5b8c\u5168\u79bb\u5f00\u8170\u5e26\u3002 \u3000\u3000\u6697\u5668\u7adf\u662f\u88ab\u8346\u65e0\u547d\u51fb\u843d\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7236\u5b50\u7684\u8138\u8272\u90fd\u53d8\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u548c\u963f\u98de\u76ee\u5149\u4e92\u76f8\u51dd\u6ce8\u7740\uff0c\u9762\u4e0a\u5374\u4ecd\u7136\u5168\u65e0\u4e1d\u6beb\u8868\u6e05\u3002 \u3000\u3000\u7136\u540e\uff0c\u8346\u65e0\u547d\u9992\u6162\u7684\u5c06\u5251\u63d2\u56de\u8170\u5e26\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u4e5f\u5782\u4e0b\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8346\u65e0\u547d\u7a81\u7136\u9053\uff1a\u201c\u4f60\u5df2\u770b\u51fa\u6211\u7684\u5251\u662f\u51fb\u6697\u5668\uff0c\u800c\u975e\u523a\u4f60\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u5f88\u9547\u5b9a\uff01\u201d \u3000\u3000\u6697\u5668\u51fb\u6765\uff0c\u8346\u5143\u547d\u7684\u523a\u51fa\uff0c\u963f\u98de\u9664\u4e86\u4f38\u624b\u62d4\u5251\uff0c\u7edd\u672a\u614c\u5f20\u95ea\u907f\u3002 \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u7b49\u963f\u98de\u7b54\u90a3\u65ec\u6d3b\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u4f60\u53cd\u5e94\u5df2\u6162\u4e86\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u76ee\u4e2d\u9732\u51fa\u4e86\u4e00\u4e1d\u6c89\u75db\u51c4\u51c9\u4e4b\u8272\uff0c\u7ec8\u4e8e\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u80fd\u6740\u4f60\uff01\u201d \u3000\u3000\u963f\u98de\u60f3\u4e5f\u4e0d\u60f3\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u9f99\u5578\u4e91\u7236\u5b50\u4ea4\u6362\u4e86\u773c\u8272\uff0c\u6697\u4e2d\u90fd\u4e0d\u7981\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8346\u65e0\u547d\u7a81\u53c8\u9053\uff1a\u201c\u4f46\u6211\u4e0d\u6740\u4f60\uff01\u201d \u3000\u3000\u9f99\u5578\u4e91\u7236\u5b50\u8138\u8272\u53c8\u90fd\u53d8\u4e86\u3002 \u3000\u3000\u963f\u98de\u51dd\u89c6\u7740\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u8272\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u53ea\u56e0\u4f60\u662f\u963f\u98de\uff01\u201d \u3000\u3000\u4ed6\u6b7b\u7070\u8272\u7684\u773c\u775b\u4e2d\u7a81\u53c8\u9732\u51fa\u4e86\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u75db\u82e6\u4e4b\u8272\uff0c\u8fd9\u79cd\u773c\u8272\u751a\u81f3\u6bd4\u963f\u98de\u73b0\u5728\u7684\u773c \u8272\u8fd8\u6c89\u75db\u3002 \u3000\u3000\u4ed6\u9065\u6ce8\u7740\u8fdc\u65b9\uff0c\u4eff\u4f5b\u8fdc\u5904\u7ad9\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4ed9\u5b50\u4e0e\u9b54\u9b3c\u6df7\u5408\u6210\u7684\u4eba\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6211\u82e5\u662f\u4f60\uff0c\u4eca\u65e5\u4f60\u5c31\u80fd\u6740\u6211\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u8bb8\u8fde\u963f\u98de\u90fd\u542c\u4e0d\u61c2\uff0c\u53ea\u6709\u8346\u65e0\u547d\u81ea\u5df1\u5fc3\u91cc\u660e\u767d\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4eba\uff0c\u82e5\u662f\u8fc7\u4e86\u4e24\u5e74\u963f\u98de\u90a3\u79cd\u751f\u6d3b\uff0c\u53cd\u5e94\u90fd\u4f1a\u53d8\u5f97\u8fdf\u949d\u7684\u3002\u4f55\u51b5\uff0c\u4ed6\u6bcf\u5929\u665a\u4e0a\u90fd \u88ab\u4eba\u9ebb\u9189\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4e00\u79cd\u6709\u9ebb\u9189\u50ac\u7720\u7684\u836f\u7269\uff0c\u90fd\u53ef\u4ee4\u4eba\u53cd\u5e94\u8fdf\u949d\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e0d\u6740\u963f\u98de\uff0c\u7edd\u4e0d\u4f1a\u52a8\u4e86\u540c\u60c5\u607b\u9690\u4e4b\u5fc3\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u5f88\u4e86\u89e3\u963f\u98de\u7684\u75db\u82e6\uff0c\u56e0\u4e3a\u4ed6 \u81ea\u5df1\u4e5f\u548c\u963f\u98de\u6709\u540c\u6837\u7684\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u8981\u963f\u98de\u6d3b\u7740\uff0c\u4e5f\u8bb8\u53ea\u662f\u8981\u963f\u98de\u966a\u7740\u4ed6\u53d7\u82e6\u3002 \u3000\u3000\u2014\u2014\u5931\u604b\u7684\u4eba\u77e5\u9053\u522b\u7684\u4eba\u4e5f\u88ab\u9057\u5f03\uff0c\u75db\u82e6\u5c31\u4f1a\u51cf\u8f7b\u4e9b\uff0c\u8f93\u94b1\u7684\u4eba\u770b\u5230\u6709\u522b\u4eba\u6bd4\u4ed6\u8f93\u5f97\u66f4 \u591a\uff0c\u5fc3\u91cc\u4e5f\u4f1a\u8212\u670d\u4e9b\u3002 \u3000\u3000\u963f\u98de\u6728\u7acb\uff0c\u4f3c\u4e4e\u8fd8\u5728\u5480\u56bc\u4ed6\u65b9\u624d\u7684\u4e24\u65ec\u6d3b\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u970d\u7136\u62ac\u5934\uff0c\u65ad\u7136\u9053\uff1a\u201c\u6211\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u4e0d\u5b9a\uff1f\u8981\u6211\u6740\u4f60\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e3a\u7684\u662f\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u662f\uff0c\u53ea\u8981\u6211\u6d3b\u7740\uff0c\u5c31\u4e0d\u80fd\u8ba9\u4ed6\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7a81\u7136\u5927\u58f0\u9053\uff1a\u201c\u6797\u4ed9\u513f\u5462\uff1f\u4f60\u96be\u9053\u5fcd\u5fc3\u8ba9\u5979\u4e3a\u4f60\u75db\u82e6\uff1f\u201d \u3000\u3000\u963f\u98de\u5fc3\u4e0a\u5b9b\u5982\u7a81\u7136\u88ab\u4eba\u523a\u4e86\u4e00\u9488\uff0c\u80f8\u53e3\u4f3c\u5df2\u7a81\u7136\u75c9\u631b\u3002 \u3000\u3000\u8346\u65e0\u547d\u518d\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u773c\uff0c\u8f6c\u8eab\u8d70\u5411\u9f99\u5578\u4e91\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u559c\u6b22\u6740\u4eba\uff0c\u6211\u559c\u6b22\u81ea\u5df1\u6740\uff0c \u4f60\u660e\u767d\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u6700\u597d\u660e\u767d\uff0c\u5426\u5219\u6211\u5c31\u6740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u4e0d\u518d\u77a7\u9f99\u5578\u4e91\uff0c\u53c8\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u5728\u54ea\u91cc\uff1f\u5e26\u6211\u53bb\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u5077\u5077\u8198\u4e86\u963f\u98de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u201c\u6211\u968f\u65f6\u90fd\u53ef\u6740\u4ed6\uff01\u201d \u3000\u3000\u963f\u98de\u53ea\u89c9\u80c3\u4e5f\u5728\u75c9\u631b\uff0c\u6536\u7f29\uff0c\u7a81\u7136\u5f2f\u4e0b\u8170\u5455\u5410\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5410\u7684\u662f\u82e6\u6c34\uff0c\u53ea\u6709\u82e6\u6c34\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e00\u4e24\u5929\u6765\uff0c\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u5403\u4ec0\u4e48\u3002 \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u8981\u7b54\u5e94\u6211\uff0c\u4f60\u4e00\u5b9a\u8981\u56de\u6765\uff0c\u6211\u6c38\u8fdc\u90fd\u5728\u7b49\u7740\u4f60\u2026\u2026\u201d \u3000\u3000\u8fd9\u662f\u4ed6\u6700\u5fc3\u7231\u7684\u4eba\u8bf4\u7684\u8bdd\u3002\u2026\u2026 \u3000\u3000\u4e3a\u4e86\u8fd9\u53e5\u8bdd\uff0c\u65e0\u8bba\u5982\u4f55\u4ed6\u4e5f\u4e0d\u80fd\u6b7b\u3002 \u3000\u3000\u53ef\u662f\u674e\u5bfb\u6b22\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u4f46\u662f\u4ed6\u6700\u597d\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u4ed6\u5e73\u751f\u542c\u89c1\uff0c\u4eba\u683c\u6700\u4f1f\u5927\u7684\u4eba\uff0c\u4ed6\u80fd\u7ad9\u5728\u8fd9\u91cc\uff0c\u770b\u7740 \u522b\u4eba\u53bb\u6740\u674e\u5bfb\u6b22\u4e48\uff1f \u3000\u3000\u4ed6\u7ee7\u7eed\u5455\u5410\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u5410\u7684\u662f\u8840\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6839\u672c\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u54ea\u91cc\uff0c\u4e5f\u4e0d\u60f3\u77e5\u9053\u81ea\u5df1\u5728\u54ea\u91cc\u3002 \u3000\u3000\u4ed6\u4e5f\u5206\u4e0d\u51fa\u73b0\u5728\u662f\u767d\u5929\uff0c\u8fd8\u662f\u665a\u4e0a\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\uff0c\u56e0\u4e3a\u4ed6\u6240\u6709\u5173\u8282\u5904\u7684\u7a74\u9053\u90e8\u5df2\u88ab\u70b9\u4f4f\u3002 \u3000\u3000\u6ca1\u6709\u98df\u7269\uff0c\u4e5f\u6ca1\u6709\u6c34\u3002 \u3000\u3000\u4ed6\u5df2\u88ab\u56da\u7981\u5728\u8fd9\u91cc\u5341\u591a\u5929\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u7a74\u9053\u6ca1\u6709\u88ab\u56f0\u4f4f\uff0c\u9965\u997f\u4e5f\u65e9\u5df2\u6d88\u8680\u4e86\u4ed6\u7684\u529b\u91cf\u3002 \u3000\u3000\u8346\u65e0\u547d\u5728\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u8f6f\u8f6f\u7684\u5012\u5728\u89d2\u843d\u91cc\uff0c\u5c31\u50cf\u662f\u53ea\u5df2\u88ab\u638f\u7a7a\u4e86\u7684\u9ebb\u888b\u3002 \u3000\u3000\u5730\u5ba4\u4e2d\u5f88\u6697\u3002\u770b\u4e0d\u6e05\u4ed6\u7684\u9762\u8272\u548c\u8868\u60c5\uff0c\u53ea\u80fd\u4f9d\u7a00\u5206\u8fa8\u51fa\u4ed6\u6ee5\u697c\u80ae\u810f\u7684\u8863\u886b\uff0c\u6194\u60b4\u75b2\u5026\u7684 \u795e\u6001\uff0c\u548c\u90a3\u53cc\u5145\u6ee1\u4e86\u60b2\u4f24\u7edd\u671b\u7684\u773c\u775b\u3002 \u3000\u3000\u8346\u65e0\u547d\u7a81\u7136\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u4eff\u4f5b\u6709\u4e9b\u5931\u671b\uff0c\u53c8\u6709\u4e9b\u4e0d\u4fe1\uff0c\u518d\u8ffd\u95ee\u4e86\u4e00\u53e5\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5c0f\u674e\u63a2\u82b1\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7b11\u4e86\u7b11\uff0c\u62a2\u7740\u9053\uff1a\u201c\u5c31\u7b97\u662f\u96c4\u72ee\u731b\u864e\uff0c\u88ab\u997f\u4e86\u5341\u591a\u5929\uff0c\u4e5f\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u53f9\u606f\u7740\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e0d\u613f\u8fd9\u6837\u5bf9\u4ed6\uff0c\u53ef\u662f\u2026\u2026\u4eba\u65e0\u4f24\u864e\u5fc3\uff0c\u864e\u6709\u4f24\u4eba\u610f\uff0c\u7ecf\u8fc7\u4e0a \u6b21\u7684\u6559\u8bad\uff0c\u6211\u4e0d\u613f\u518d\u6709\u4efb\u4f55\u610f\u5916\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7a81\u53c8\u9053\uff1a\u201c\u4ed6\u7684\u5200\u5462\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u8003\u8651\u7740\uff0c\u6c89\u541f\u7740\uff1a\u201c\u8346\u5148\u751f\u662f\u4e0d\u662f\u60f3\u770b\u770b\u4ed6\u7684\u5200\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u56de\u7b54\uff0c\u56e0\u4e3a\u8fd9\u53e5\u8bdd\u6839\u672c\u5c31\u662f\u591a\u95ee\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7ec8\u4e8e\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u67c4\u5200\u3002 \u3000\u3000\u5200\u5f88\u8f7b\uff0c\u5f88\u77ed\uff0c\u5f88\u8584\uff0c\u51e0\u4e4e\u5c31\u5b9b\u5982\u4e00\u7247\u67f3\u53f6\u3002 \u3000\u3000\u8346\u65e0\u547d\u8f7b\u629a\u7740\u5200\u950b\uff0c\u4eff\u4f5b\u4e0d\u5fcd\u91ca\u624b\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u4e0d\u8fc7\u662f\u67c4\u5f88\u666e\u901a\u7684\u5200\uff0c\u5e76\u4e0d\u80fd\u7b97\u662f\u5229\u5668\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u5229\u5668\uff1f\u2026\u2026\u51ed\u4f60\u8fd9\u79cd\u4eba\u4e5f\u914d\u8c08\u8bba\u5229\u5668\uff1f\u201d \u3000\u3000\u4ed6\u773c\u775b\u5ffd\u7136\u626b\u5411\u9f99\u5578\u4e91\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ec0\u4e48\u662f\u5229\u5668\uff1f\u201d \u3000\u3000\u4ed6\u7684\u773c\u775b\u867d\u7136\u7070\u6697\u65e0\u5149\uff0c\u4f46\u5374\u5e26\u7740\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u8be1\u5947\u5996\u5f02\u4e4b\u529b\uff0c\u5c31\u597d\u50cf\u4f60\u5728\u68a6\u4e2d\u89c1\u5230\u7684 \u5a07\u9b54\u4e4b\u773c\uff0c\u4ee4\u4f60\u9192\u6765\u540e\u8fd8\u662f\u89c9\u5f97\u540c\u6837\u53ef\u6015\u3002 \u3000\u3000\u9f99\u5578\u4e91\u89c9\u5f97\u8fde\u547c\u5438\u90fd\u56f0\u96be\u8d77\u6765\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u8bf7\u6307\u6559\u3002\u201d \u3000\u3000\u8346\u5143\u547d\u773c\u775b\u8fd9\u624d\u56de\u5230\u5200\u950b\u4e0a\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u80fd\u6740\u4eba\u7684\uff0c\u5c31\u662f\u5229\u5668\uff0c\u5426\u5219\uff0c\u7eb5\u662f\u5e72\u5c06\u83ab\u90aa\uff0c \u5230\u4e86\u4f60\u8fd9\u79cd\u4eba\u624b\u4e0a\uff0c\u4e5f\u5c31\u7b97\u4e0d\u5f97\u5229\u5668\u4e86\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u966a\u7b11\u9053\uff1a\u201c\u662f\u662f\u662f\uff0c\u8346\u5148\u751f\u89c1\u89e3\u7684\u786e\u7cbe\u8f9f\uff0c\u4ee4\u4eba\u2026\u2026\u201d \u3000\u3000\u8346\u65e0\u547d\u6839\u672c\u6ca1\u6709\u542c\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u7a81\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u81f3\u4eca\u5df2\u6709\u591a\u5c11\u4eba\u6b7b\u5728\u8fd9\u79cd\u5200\u4e0b\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u8fd9\u2026\u2026\u53ea\u6015\u5df2\u6570\u4e0d\u6e05\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6570\u5f97\u6e05\u3002\u201d \u3000\u3000\u91d1\u94b1\u5e2e\u4e4b\u5d1b\u8d77\uff0c\u867d\u7136\u53ea\u6709\u77ed\u77ed\u4e24\u5e74\uff0c\u4f46\u5728\u521b\u7acb\u4e4b\u524d\uff0c\u5374\u5df2\u4e0d\u77e5\u9053\u7ecf\u8fc7\u591a\u4e45\u7684\u7b56\u5212\uff0c\u4e0a\u5b98 \u91d1\u8679\u6700\u670d\u8d5d\u7684\u4e24\u65ec\u8bdd\u5c31\u662f\uff1a \u3000\u3000\u201c\u51e1\u4e8b\u51dd\u5219\u7acb\uff0c\u4e0d\u8c6b\u5219\u5e9f\u3002\u201d \u3000\u3000\u201c\u4e00\u5206\u8015\u8018\uff0c\u4e00\u5206\u6536\u83b7\u3002\u201d \u3000\u3000\u91d1\u94b1\u5e2e\u4e4b\u6240\u4ee5\u80fd\u5728\u77ed\u77ed\u4e24\u5e74\u4e2d\u5a01\u9707\u5929\u4e0b\uff0c\u5e76\u4e0d\u662f\u8fd0\u6c14\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4e5f\u542c\u8bf4\u8fc7\uff0c\u91d1\u94b1\u5e2e\u672a\u521b\u7acb\u4e4b\u524d\uff0c\u5c31\u5df2\u5c06\u6c5f\u6e56\u4e2d\u6bcf\u4e2a\u5c0f\u6709\u540d\u6c14\u7684\u4eba\u7684\u6765\u5386\u5e95\u7ec6\u90fd\u8c03 \u67e5\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8fd9\u8981\u82b1\u591a\u5927\u7684\u4eba\u529b\u7269\u529b\uff1f \u3000\u3000\u9f99\u5578\u4e91\u59cb\u7ec8\u4e0d\u80fd\u76f8\u4fe1\uff0c\u6b64\u523b\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u771f\u7684\u6570\u5f97\u6e05\uff1f\u6709\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4e03\u5341\u516d\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e03\u5341\u516d\u4eba\u4e2d\uff0c\u6ca1\u6709\u4e00\u4eba\u6b66\u529f\u6bd4\u4f60\u5dee\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u53ea\u80fd\u966a\u7b11\uff0c\u76ee\u5149\u7f13\u7f13\u8f6c\u5411\u674e\u5bfb\u6b22\uff0c\u50cf\u662f\u8fd8\u8981\u4ed6\u8bc1\u660e\u4e00\u4e0b\uff0c\u8346\u65e0\u547d\u8bf4\u7684\u8fd9\u6570\u5b57\u662f\u5426 \u53ef\u4fe1\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u4f3c\u8fde\u70b9\u5934\u6447\u5934\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u7728\u7740\u773c\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u81ea\u5df1\u82e5\u4e5f\u6b7b\u5728\u8fd9\u79cd\u5200\u53ef\u90a3\u624d\u771f\u7684\u5927\u5feb\u4eba\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u98de\u5411\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u51e0\u4e4e\u5f00\u5fc3\u5f97\u8981\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u5200\u5149\u5e76\u6ca1\u6709\u7b14\u76f4\u51fb\u5411\u674e\u5bfb\u6b22\u7684\u54bd\u5589\uff0c\u534a\u9014\u4e2d\u7a81\u7136\u4e00\u6298\uff0c\u201c\u5f53\u201d\u7684\uff0c\u843d\u5728\u674e\u5bfb\u6b22\u8eab\u65c1\u7684 \u77f3\u5730\u3002 \u3000\u3000\u539f\u6765\u8346\u65e0\u547d\u7528\u6697\u5668\u7684\u624b\u6cd5\u4e5f\u4e0d\u9519\u3002 \u3000\u3000\u8346\u65e0\u547d\u7a81\u7136\u9053\uff1a\u201c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u6115\u7136\uff0c\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u7ed9\u4ed6\u8bf4\u8bdd\u7684\u673a\u4f1a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u8bf4\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u7236\u5b50\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7acb\u523b\u660e\u767d\u4ed6\u7684\u610f\u601d\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u4e0a\u5b98\u5e2e\u4e3b\u8981\u7684\u53ea\u662f\u674e\u5bfb\u6b22\uff0c\u5e76\u4e0d\u5728\u4e4e\u4ed6\u662f\u6b7b\u7684\uff0c\u8fd8\u662f\u6d3b\u7684\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4e0a\u5bab\u8001\u4f2f\u5df2\u6ef4\u9152\u4e0d\u6cbe\uff0c\u81ea\u7136\u4e5f\u5f88\u8ba8\u538c\u9152\u9b3c\uff0c\u771f\u6b63\u7684\u9152\u9b3c\u53ea\u6709\u6b7b\u624d\u80fd\u4e0d\u559d\u9152 \uff0c\u624d\u4f1a\u4ee4\u4eba\u770b\u5f97\u987a\u773c\u4e9b\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u76ee\u5149\u95ea\u52a8\u7740\uff0c\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5e26\u4e2a\u6b7b\u4eba\u53bb\uff0c\u603b\u6bd4\u5e26\u6d3b\u4eba\u65b9\u4fbf\u5f97\u591a\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u518d\u6709\u4efb \u4f55\u610f\u5916\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4f46\u8346\u5148\u751f\u81ea\u7136\u4e0d\u4f1a\u5411\u4e00\u4e2a\u5168\u65e0\u53cd\u6297\u4e4b\u529b\u7684\u4eba\u51fa\u4e8e\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u8346\u65e0\u547d\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8bdd\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u7b11\u9053\uff1a\u201c\u662f\u662f\u662f\uff0c\u5728\u4e0b\u8fd9\u5c31\u53bb\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u51fa\u624b\u70b9\u7a74\u7684\u4eba\u662f\u4ed6\uff0c\u8981\u89e3\u5f00\u81ea\u7136\u5f88\u5bb9\u6613\u3002 \u3000\u3000\u9f99\u5578\u4e91\u62cd\u4e86\u62cd\u674e\u5bfb\u6b22\u7684\u80a9\u5934\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u770b\u6765\u8346\u5148\u751f\u662f\u60f3\u548c\u4f60\u4e00\u8f83\u9ad8\u4e0b\uff0c\u8346\u5148\u751f \u5251\u6cd5\u9ad8\u7edd\u5929\u4e0b\uff0c\u5144\u5f1f\u4f60\u51fa\u624b\u53ef\u5343\u4e07\u4e0d\u80fd\u5927\u610f\u3002\u201d \u3000\u3000\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u5c45\u7136\u8fd8\u80fd\u5c06\u201c\u5144\u5f1f\u201d\u4e24\u5b57\u53eb\u5f97\u51fa\u53e3\u6765\uff0c\u800c\u4e14\u8bf4\u5f97\u6df1\u60c5\u6b3e\u6b3e\uff0c\u597d\u50cf\u771f\u7684 \u5f88\u5173\u5fc3\u3002 \u3000\u3000\u8fd9\u79cd\u4eba\u4f60\u80fd\u4e0d\u4f69\u670d\u4ed6\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u5df2\u65e0\u8bdd\u53ef\u8bf4\uff0c\u53ea\u662f\u8270\u6da9\u7684\u7b11\u4e86\u7b11\uff0c\u6162\u6162\u7684\u62ac\u8d77\u4e86\u8eab\u65c1\u7684\u5200\u3002 \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u624b\u91cc\u7684\u5200\uff0c\u76ee\u4e2d\u4f3c\u5df2\u6709\u6cea\u5c06\u843d\u3002 \u3000\u3000\u8fd9\u7684\u786e\u662f\u540d\u6ee1\u5929\u4e0b\uff0c\u4f8b\u4e0d\u865a\u53d1\u7684\u5c0f\u674e\u98de\u5200\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5200\u5df2\u56de\u5230\u4ed6\u624b\u91cc\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd8\u6709\u529b\u5c06\u8fd9\u67c4\u5200\u53d1\u51fa\u4e48\uff1f \u3000\u3000\u7f8e\u4eba\u8fdf\u66ae\uff0c\u82f1\u96c4\u672b\u8def\uff0c\u90fd\u662f\u4e16\u4e0a\u6700\u65e0\u53ef\u5948\u4f55\u7684\u60b2\u54c0\u3002 \u3000\u3000\u8fd9\u79cd\u60b2\u54c0\u6700\u4ee4\u4eba\u540c\u60c5\uff0c\u4e5f\u6700\u4ee4\u4eba\u60cb\u501f\u3002 \u3000\u3000\u4f46\u5728\u8fd9\u91cc\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u540c\u60c5\u4ed6\uff0c\u66f4\u6ca1\u6709\u4eba\u60cb\u501f\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u76ee\u4e2d\u95ea\u52a8\u7740\u72e1\u9ee0\u7684\u7b11\u610f\uff0c\u60a0\u7136\u9053\uff1a\u201c\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u8fd9\u4e00\u6b21\u4e0d\u77e5\u9053\u8fd8\u7075\u4e0d \u7075\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u62ac\u5934\u77a7\u4e86\u4ed6\u4e00\u9635\uff0c\u53c8\u6162\u6162\u7684\u5782\u4e0b\u5934\u3002 \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u8981\u6740\u4eba\uff0c\u4e00\u5b9a\u5148\u7ed9\u4eba\u4e00\u4e2a\u673a\u4f1a\uff0c\u8fd9\u5c31\u662f\u4f60\u6700\u540e\u7684\u673a\u4f1a\uff0c\u4f60\u660e\u767d\u4e48\uff1f \u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7ad9\u8d77\u6765\u5427\uff1a\u201c \u3000\u3000\u674e\u5bfb\u6b22\u5598\u606f\u7740\uff0c\u53c8\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u67d4\u58f0\u9053\uff1a\u201c\u674e\u5927\u53d4\u82e5\u5df2\u7ad9\u4e0d\u8d77\uff0c\u5c0f\u4f84\u53ef\u4ee5\u6276\u4f60\u4e00\u628a\u3002\u201d \u3000\u3000\u4ed6\u7728\u4e86\u7728\u773c\uff0c\u7acb\u523b\u53c8\u63a5\u7740\u7b11\u9053\uff1a\u201c\u4f46\u6211\u770b\u6765\u8fd9\u6839\u672c\u662f\u7528\u4e0d\u7740\u7684\uff0c\u636e\u8bf4\u674e\u5927\u53d4\u7684\u98de\u5200\u4e0d\u4f46 \u80fd\u5750\u7740\u53d1\uff0c\u5c31\u8fde\u8eba\u7740\u65f6\u53d1\u51fa\u6765\u4e5f\u540c\u6837\u51c6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u8bdd\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u5df2\u6709\u4e00\u4e2a\u4eba\u51b2\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u963f\u98de\uff01 \u3000\u3000\u963f\u98de\u7684\u8138\u5168\u65e0\u4e1d\u6beb\u8840\u8272\uff0c\u5634\u89d2\u5374\u5e26\u7740\u4e1d\u8840\u75d5\u3002 \u3000\u3000\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u4f3c\u5df2\u8001\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u4ed6\u98de\u4e00\u822c\u51b2\u8fdb\u6765\uff0c\u4f46\u8eab\u5f62\u5728\u4e00\u5239\u90a3\u95f4\u5c31\u505c\u987f\uff0c\u4e00\u505c\u987f\u5c31\u9759\u5982\u5c71\u77f3\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u6b7b\u5fc3\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5934\u5df2\u62ac\u8d77\uff0c\u76ee\u4e2d\u53c8\u4f3c\u6709\u70ed\u6cea\u76c8\u7736\u3002 \u3000\u3000\u963f\u98de\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u5c31\u8f6c\u5934\u9762\u5bf9\u7740\u8346\u65e0\u547d\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u8981\u6740\u4ed6\uff0c\u5c31\u5f97\u5148\u6740 \u6211\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u6c89\u7740\uff0c\u5f88\u9547\u9759\uff0c\u5e76\u6ca1\u6709\u6fc0\u52a8 \u3000\u3000\u8fd9\u66f4\u663e\u793a\u4e86\u4ed6\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u8346\u65e0\u547d\u7070\u8272\u7684\u773c\u775b\u53c8\u8d77\u4e86\u79cd\u5f88\u5947\u7279\u7684\u53d8\u5316\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u4e0d\u518d\u5173\u5fc3\u5979\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u6b7b\u4e86\uff0c\u5979\u8fd8\u662f\u80fd\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u8bf4\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u867d\u7136\u8fd8\u662f\u540c\u6837\u9547\u9759\uff0c\u4f46\u76ee\u4e2d\u5374\u4e0d\u7981\u9732\u51fa\u4e86\u4e00\u4e1d\u75db\u82e6\u4e4b\u8272\uff0c\u547c\u5438\u4f3c\u4e5f \u6709\u4e9b\u56f0\u96be\u3002 \u3000\u3000\u8fd9\u5e76\u6ca1\u6709\u7792\u8fc7\u8346\u65e0\u547d\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u4f3c\u4e4e\u7acb\u523b\u5f97\u5230\u4e86\u67d0\u79cd\u5947\u7279\u7684\u5b89\u6170\u548c\u89e3\u8131\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u5979\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6d3b\u7740\u4e0d\u5b89\uff0c\u5c31\u4e0d\u5982\u6b7b\uff0c\u6211\u82e5\u4e0d\u6b7b\uff0c\u5979\u66f4\u4f24\u5fc3\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u5979\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5f53\u7136\uff01\u201d \u3000\u3000\u5728\u963f\u98de\u5fc3\u76ee\u4e2d\uff0c\u6797\u4ed9\u513f\u4e0d\u4f46\u662f\u4ed9\u5b50\uff0c\u4e5f\u662f\u5723\u5973\u3002 \u3000\u3000\u8346\u65e0\u547d\u5634\u89d2\u7a81\u7136\u9732\u51fa\u4e86\u4e00\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u770b\u5230\u8fc7\u4ed6\u7684\u7b11\uff0c\u8fde\u81ea\u5df1\u90fd\u5df2\u51e0\u4e4e\u5fd8\u5374\u4e0a\u4e00\u6b21\u662f\u4ec0\u4e48\u65f6\u5019\u7b11\u7684\u3002 \u3000\u3000\u4ed6\u7b11\u5f97\u5f88\u5947\u7279\uff0c\u56e0\u4e3a\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u5df2\u4e0d\u4e60\u60ef\u7b11\uff0c\u5df2\u50f5\u786c\uff01 \u3000\u3000\u4ed6\u4ece\u4e0d\u613f\u7b11\uff0c\u56e0\u4e3a\u7b11\u53ef\u4ee4\u4eba\u8f83\u5316\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u7b11\u5374\u4e0d\u540c\u2014\u2014\u8fd9\u79cd\u7b11\u6b63\u5982\u5251\uff0c\u53ea\u4e0d\u8fc7\u5251\u4f24\u7684\u662f\u4eba\u547d\uff0c\u8fd9\u79cd\u7b11\u4f24\u7684\u5374\u662f\u4eba\u5fc3\u3002 \u3000\u3000\u963f\u98de\u7adf\u5b8c\u5168\u4e0d\u61c2\u4ed6\u662f\u4e3a\u4f55\u800c\u7b11\u7684\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7b11\uff0c\u4f60\u867d\u6709\u516b\u6210\u673a\u4f1a\u6740\u6211\uff0c\u4f46\u4e5f\u6709 \u4e24\u6210\u6b7b\u5728\u6211\u5251\u4e0b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u7b11\u5bb9\u5df2\u6d88\u5931\u4e0d\u89c1\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e0d\u6740\u4f60\uff0c\u5c31\u4e00\u5b9a\u4f1a\u7559\u4e0b\u4f60\u7684\u547d\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u8981\u4f60\u6d3b\u7740\uff0c\u770b\u7740\u2026\u2026 \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u672a\u8bf4\u5b8c\uff0c\u5251\u5149\u5df2\u98de\u51fa\uff01 \u3000\u3000\u5251\u5149\u4ea4\u51fb\uff0c\u5982\u95ea\u7535 \u3000\u3000\u4f46\u8fd8\u6709\u4e00\u9053\u5149\u8292\u6bd4\u5251\u66f4\u5feb\uff0c\u90a3\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u9aa4\u7136\u95f4\uff0c\u6240\u6709\u7684\u5149\u8292\u90fd\u6d88\u5931\u3002 \u3000\u3000\u6240\u6709\u7684\u52a8\u4f5c\u4e5f\u4f1a\u90fd\u505c\u6b62\u3002 \u7b2c\u4e94\u5341\u516b\u7ae0\u3000\u82f1\u96c4>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u516b\u7ae0\u3000\u82f1\u96c4 \u3000\u3000\u8346\u65e0\u547d\u7684\u5251\uff0c\u5df2\u523a\u4eba\u4e86\u963f\u98de\u7684\u80a9\u5477\uff0c\u4f46\u53ea\u523a\u4eba\u4e86\u4e24\u5206\u3002 \u3000\u3000\u963f\u98de\u7684\u5251\uff0c\u8ddd\u79bb\u8346\u65e0\u547d\u54bd\u5589\u8fd8\u6709\u56db\u5bf8\u3002 \u3000\u3000\u4ed6\u80a9\u4e0a\u7684\u8840\u5df2\u5f00\u59cb\u6e17\u51fa\uff0c\u6e17\u4eba\u8863\u670d\uff0c\u67d3\u7ea2\u4e86\u8863\u670d\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u5251\u4e3a\u4f55\u6ca1\u6709\u523a\u4e0b\u53bb\uff1f \u3000\u3000\u8346\u65e0\u547d\u7684\u80a9\u5477\u5904\uff0c\u659c\u63d2\u7740\u4e00\u67c4\u5200\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u662f\u4ec0\u4e48\u5947\u5f02\u7684\u9b54\u529b\u4f7f\u674e\u5bfb\u6b22\u80fd\u53d1\u51fa\u8fd9\u67c4\u5200\u6765\u7684\uff1f \u3000\u3000\u9f99\u5578\u4e91\u7236\u4e8e\u7684\u8138\u8272\u82cd\u767d\uff0c\u624b\u5728\u53d1\u6296\uff0c\u4e00\u6b65\u6b65\u5411\u540e\u9000\uff0c\u9047\u5230\u5899\u89d2\uff0c\u4ed6\u7236\u5b50\u5fc3\u91cc\u90fd\u5f88\u5947\u602a\uff0c \u674e\u5bfb\u6b22\u662f\u54ea\u91cc\u6765\u7684\u529b\u91cf\u53d1\u5200\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5df2\u7ad9\u8d77\uff01 \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u8f6c\u8fc7\u5934\uff0c\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u6b7b\u7070\u8272\u7684\u773c\u775b\u4e2d\u8fd8\u662f\u5168\u65e0\u8868\u60c5\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c \u7a81\u7136\u9053\uff1a\u201c\u597d\u5200\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5e76\u4e0d\u5f88\u597d\uff0c\u53ea\u4e0d\u8fc7\u662f\u4f60\u5148\u5bf9\u6211\u6709\u4e86\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u7adf\u5168\u6ca1\u6709\u5c06\u6211\u653e\u5728 \u773c\u91cc\uff0c\u5426\u5219\u6211\u672a\u5fc5\u80fd\u4f24\u4f60\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u7b11\uff1a\u201c\u4f60\u80fd\u9a97\u8fc7\u6211\uff0c\u5c31\u662f\u4f60\u7684\u672c\u4e8b\uff0c\u4f60\u5c31\u6bd4\u6211\u5f3a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u9a97\u4f60\uff0c\u4e5f\u6ca1\u6709\u8bf4\u6211\u4e0d\u80fd\u53d1\u5200\uff0c\u53ea\u4e0d\u8fc7\u662f\u4f60\u81ea\u5df1\u8fd9\u4e48\u60f3\u800c\u5df2\uff0c \u662f\u4f60\u81ea\u5df1\u7684\u773c\u775b\u9a97\u4e86\u81ea\u5df1\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u662f\uff0c\u9519\u7684\u662f\u6211\uff0c\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u867d\u662f\u51f6\u624b\uff0c\u5374\u4e0d\u662f\u5c0f\u4eba\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u773c\u89d2\u779f\u8fc7\u9f99\u5578\u4e91\u7236\u5b50\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c0f\u4eba\u8fd8\u4e0d\u914d\u505a\u51f6\u624b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u6ca1\u6709\u8981\u6740\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u5782\u4e0b\u5934\uff0c\u671b\u7740\u81ea\u5df1\u80a9\u4e0a\u7684\u5200\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f46\u6211\u8fd9\u4e00\u5251\u3002\u672c\u60f3\u5e9f\u53bb\u4ed6\u8fd9\u6761\u624b\u81c2\u7684\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u5200\u5374\u5f88\u8f7b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4eba\u4e88\u6211\u4e00\u5206\uff0c\u6211\u62a5\u4ed6\u4e09\u5206\u3002 \u3000\u3000\u8346\u65e0\u547d\u970d\u7136\u62ac\u5934\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u867d\u7136\u6ca1\u6709\u8bf4\u4e00\u4e2a\u5b57\uff0c\u4f46\u76ee\u4e2d\u7adf\u53c8\u6709\u4e86\u79cd\u5947\u7279\u7684\u53d8\u5316\uff0c\u5c31\u597d \u50cf\u4ed6\u5728\u77a7\u7740\u4e0a\u5b98\u91d1\u8679\u65f6\u4e00\u6837\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u6211\u8fd8\u8981\u544a\u8bc9\u4f60\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u867d\u4f24\u4e86\u4e03\u5341\u516d\u4e2a\u4eba\uff0c\u5176\u4e2d\u5374\u6709\u4e8c\u5341\u516b\u4eba\u5e76\u6ca1\u6709\u6b7b\uff0c\u6b7b\u7684\u90fd\u662f\u5b9e\u5728\u8be5\u6b7b\u7684 \u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9ed8\u7136\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f4e\u4f4e\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\uff0c\u4ece\u672a\u6740\u9519\u8fd9\u4e00\u4e2a\u4eba\uff01\u6240\u4ee5\u2026\u2026\u6211\u53ea\u671b \u4f60\u4ee5\u540e\u5728\u6740\u4eba\u4e4b\u524d\uff0c\u591a\u60f3\u60f3\uff0c\u591a\u8003\u8651\u8003\u8651\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4e5f\u8981\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u4e5f\u5728\u542c\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u613f\u53d7\u4eba\u6069\u60c5\uff0c\u66f4\u4e0d\u613f\u542c\u4eba\u6559\u8bad\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u7a81\u7136\u5728\u80a9\u4e0a\u90a3\u67c4\u5200\u7684\u5200\u67c4\u4e0a\u7528\u529b\u4e00\u62cd\u3002 \u3000\u3000\u9732\u5728\u5916\u9762\u7684\u5200\u950b\uff0c\u76f4\u6ca1\u4eba\u8089\uff0c\u76f4\u81f3\u5200\u67c4\u3002 \u3000\u3000\u9c9c\u8840\u6d8c\u51fa\uff01 \u3000\u3000\u201c\u5f53\u201d\u7684\uff0c\u5251\u4e5f\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u8eab\u5b50\u6447\u4e86\u6447\uff0c\u4f46\u9762\u4e0a\u8fd8\u662f\u51b7\u5982\u5ca9\u77f3\uff0c\u786c\u5982\u5ca9\u77f3\uff0c\u5168\u6ca1\u6709\u534a\u5206\u75db\u82e6\u4e4b\u8272\uff0c\u751a\u81f3\u8fde \u4e00\u6839\u808c\u8089\u90e8\u6ca1\u6709\u98a4\u6296\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u4e5f\u6ca1\u6709\u518d\u77a7\u4efb\u4f55\u4eba\u4e00\u773c\uff0c\u5927\u6b65\u5b9a\u4e86\u51fa\u53bb\uff01 \u3000\u3000\u82f1\u96c4\uff1f\u2026\u2026\u4ec0\u4e48\u53eb\u82f1\u96c4\uff1f\u96be\u9053\u8fd9\u5c31\u662f\u82f1\u96c4\uff1f \u3000\u3000\u82f1\u96c4\u6240\u4ee3\u8868\u7684\u610f\u601d\uff0c\u5f80\u5f80\u5c31\u662f\u51b7\u9177\uff01\u6b8b\u5fcd\uff01\u5bc2\u5bde\uff01\u65e0\u60c5\uff01 \u3000\u3000\u4e5f\u6709\u4eba\u66fe\u7ecf\u66ff\u82f1\u96c4\u4e0b\u8fc7\u79cd\u5b9a\u4e49\uff0c\u90a3\u5c31\u662f\uff1a \u3000\u3000\u6740\u4eba\u5982\u8349\uff0c\u597d\u8d4c\u5982\u72c2\uff0c\u597d\u9152\u5982\u6e34\uff0c\u597d\u8272\u5982\u547d\uff01 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u90fd\u4e0d\u662f\u7edd\u5bf9\u7684\uff0c\u82f1\u96c4\u4e5f\u6709\u53e6\u4e00\u79cd\u3002 \u3000\u3000\u4f46\u50cf\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u82f1\u96c4\u4e16\u4e0a\u53c8\u6709\u51e0\u4eba\uff1f \u3000\u3000\u82f1\u96c4\u4e5f\u8bb8\u53ea\u6709\u4e00\u70b9\u662f\u76f8\u540c\u7684\u2014\u2014\u65e0\u8bba\u8981\u505a\u54ea\u79cd\u82f1\u96c4\uff0c\u90fd\u4e0d\u662f\u4ef6\u597d\u53d7\u7684\u4e8b\u3002 \u3000\u3000\u963f\u98de\u7684\u795e\u60c5\u4e5f\u5f88\u8427\u7d22\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u8fd9\u4e00\u751f\uff0c\u53ea\u6015\u6c38\u8fdc\u4e5f\u4e0d\u80fd\u4f7f\u5251\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u8fd8\u6709\u53f3\u624b\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f46\u4ed6\u4e60\u60ef\u7684\u662f\u5de6\u624b\uff0c\u7528\u53f3\u624b\uff0c\u5c31\u4f1a\u6162\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5bf9\u4f7f\u5251\u7684\u4eba\u8bf4\u6765\u2018\u6162\u2019\u7684\u610f\u601d\u5c31\u662f\u2018\u6b7b\u2019\uff01\u201d \u3000\u3000\u4ed6\u4e00\u5411\u5f88\u5c11\u53f9\u606f\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u53f9\u606f\u7684\u975e\u4f46\u662f\u8346\u65e0\u547d\uff0c\u4e5f\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51dd\u6ce8\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u95ea\u7740\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u53ea\u8981\u6709\u51b3\u5fc3\uff0c\u5c31\u7b97\u4e24\u53ea\u624b\u4e00\u9f50\u65ad\u4e86 \uff0c\u7528\u5634\u54ac\u7740\u5251\uff0c\u4e5f\u4f1a\u540c\u6837\u5feb\u7684\uff0c\u4ed6\u7684\u6c14\u82e5\u5df2\u9981\uff0c\u5c31\u7b97\u53cc\u624b\u4ff1\u5168\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7528\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e16\u4e0a\u53cc\u624b\u4ff1\u5168\u7684\u4eba\u5f88\u591a\uff0c\u4f46\u51fa\u624b\u5feb\u7684\u53c8\u6709\u51e0\u4eba\uff1f\u201d \u3000\u3000\u963f\u98de\u9759\u9759\u7684\u542c\u7740\uff0c\u6697\u6de1\u7684\u773c\u775b\u4e2d\uff0c\u7ec8\u4e8e\u53c8\u9732\u51fa\u4e86\u903c\u4eba\u7684\u795e\u60c5\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u51b2\u8fc7\u53bb\uff0c\u7d27\u63e1\u4f4f\u4e86\u674e\u5bfb\u6b22\u7684\u624b\u81c2\uff0c\u560e\u58f0\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u4e24\u4eba\u90fd\u5df2\u70ed\u6cea\u76c8\u7736\u3002\u82e5\u6709\u7b2c\u4e09\u4eba\u5728\u65c1\u8fb9\u77a7\u89c1\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u88ab\u611f\u52a8\u5f97\u70ed\u6cea\u76c8\u7736 \u3002 \u3000\u3000\u53ea\u53ef\u60dc\u9f99\u5578\u4e91\u7236\u5b50\u90fd\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u4ed6\u4eec\u6b63\u5728\u6084\u6084\u5f80\u5916\u6e9c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u662f\u80cc\u5bf9\u7740\u4ed6\u4eec\u7684\uff0c\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u89c9\u5bdf\u3002 \u3000\u3000\u963f\u98de\u4eff\u4f5b\u77a7\u4e86\u4e00\u773c\uff0c\u5374\u5e76\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u76f4\u5230\u4ed6\u4eec\u7236\u5b50\u90fd\u5df2\u6e9c\u51fa\u4e86\u95e8\uff0c\u963f\u98de\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u8fd8\u662f\u8981\u653e\u4ed6\u4eec\u8d70\u7684\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u6551\u8fc7\u6211\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u53ea\u6551\u8fc7\u4f60\u4e00\u6b21\uff0c\u5374\u5bb3\u8fc7\u4f60\u5f88\u591a\u6b21\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4e5f\u8bb8\u5e76\u4e0d\u662f\u5fd8\u4e86\uff0c\u800c\u662f\u4ece\u672a\u8bb0\u6068\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u6709\u4ed6\u7684\u82e6\u607c\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7a81\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4eba\u751f\u4e2d\u7684\u786e\u6709\u5f88\u591a\u4e8b\u662f\u5b8c\u5168\u4e0d\u516c \u9053\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u516c\u9053\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u516c\u9053\uff0c\u8b6c\u5982\u8bf4\uff0c\u6709\u4e9b\u4eba\u4e00\u751f\u90fd\u5f88\u5584\u826f\uff0c\u53ea\u4e0d\u5e78\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u8fd9\u4ef6\u4e8b\u5f80\u5f80 \u5c31\u4f1a\u4ee4\u4ed6\u62b1\u6068\u7ec8\u751f\uff0c\u975e\u4f46\u522b\u4eba\u4e0d\u80fd\u539f\u8c05\u4ed6\uff0c\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u539f\u8c05\u81ea\u5df1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u3002 \u3000\u3000\u4ed6\u5f88\u4e86\u89e3\u201c\u4e00\u5931\u8db3\u6210\u5343\u53e4\u6068\u201d\u8fd9\u53e5\u8bdd\u7684\u610f\u4e49\u3002 \u3000\u3000\u963f\u98de\u63a5\u7740\u9053\uff1a\u201c\u4f46\u50cf\u9f99\u5578\u4e91\u8fd9\u79cd\u4eba\uff0c\u4ed6\u4e00\u751f\u4e2d\u4e5f\u8bb8\u53ea\u505a\u8fc7\u4e00\u4ef6\u597d\u4e8b\uff0c\u53ea\u6551\u8fc7\u4f60\uff0c\u6240\u4ee5\u4f60 \u5c31\u6c38\u8fdc\u4e0d\u4f1a\u89c9\u5f97\u4ed6\u662f\u4e2a\u5341\u5206\u574f\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e2d\u663e\u7136\u6709\u5f88\u591a\u611f\u6168\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u660e\u767d\u4ed6\u7684\u610f\u601d\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u5728\u4e3a\u6797\u4ed9\u513f\u4e0d\u5e73\u3002 \u3000\u3000\u4ed6\u59cb\u7ec8\u8ba4\u4e3a\u6797\u4ed9\u513f\u8fd9\u4e00\u751f\u4e2d\u53ea\u505a\u9519\u8fc7\u4e00\u4ef6\uff0c\u800c\u674e\u5bfb\u6b22\u5374\u59cb\u7ec8\u4e0d\u80fd\u539f\u8c05\u5979\u3002 \u3000\u3000\u201c\u7231\u201d\u7684\u786e\u662f\u5947\u5999\u7684\uff0c\u6709\u65f6\u5f88\u751c\u871c\uff1b\u6709\u65f6\u5f88\u75db\u82e6\uff0c\u4e5f\u6709\u65f6\u5f88\u53ef\u6015\u2014\u2014\u5b83\u4e0d\u4f46\u80fd\u4ee4\u4eba\u53d8\u6210 \u5446\u5b50\uff0c\u4e5f\u80fd\u4ee4\u4eba\u53d8\u6210\u778e\u5b50\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7236\u5e72\u6e9c\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u4e0d\u4f46\u5f88\u6109\u5feb\uff0c\u4e5f\u5f88\u5f97\u610f\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u8bb0\u7740\uff0c\u522b\u4eba\u7684\u5f31\u70b9\uff0c\u5c31\u662f\u6211\u4eec\u7684\u673a\u4f1a\u3002\u80fd\u628a\u63e1\u4f4f\u673a\u4f1a\u7684\u4eba\uff0c\u5c31 \u6c38\u8fdc\u4e0d\u4f1a\u5931\u8d25\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u7684\u5f31\u70b9\uff0c\u5b69\u513f\u73b0\u5728\u5df2\u5168\u90e8\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u8fdf\u65e9\u603b\u8981\u6b7b\u5728\u6211\u4eec\u624b\u5927\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u542c\u5230\u6709\u4eba\u5728\u7b11\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u5f97\u6709\u4e9b\u51c4\u51c9\uff0c\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u5f88\u96be\u5fc6\u8d77\uff0c\u6709\u4e9b\u4e8b\u5374\u7ec8\u751f\u96be\u4ee5\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u963f\u98de\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u662f\u6709\u4e9b\u4e8b\uff0c\u4f60\u6839\u672c\u62d2\u7edd\u601d\u60f3\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u8bb8\u8fd8\u662f\u672a\u7ecf\u4e16\u6545\u7684\u5c11\u5e74\uff0c\u4f46\u5bf9\u4eba\u751f\u67d0\u4e9b\u4e8b\u7684\u770b\u6cd5\uff0c\u4ed6\u8fdc\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u6df1\u523b\uff0c\u5c16\u9510\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7981\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u62c5\u8fd8\u6709\u4e9b\u4e8b\u4f60\u7eb5\u7136\u62d2\u7edd\u53bb\u60f3\uff0c\u5374\u504f\u504f\u8fd8\u662f\u65f6\u65f6\u523b \u523b\u90fd\u8981\u60f3\u8d77\uff0c\u4eba\uff0c\u6c38\u8fdc\u90fd\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u601d\u60f3\uff0c\u8fd9\u4e5f\u662f\u4eba\u751f\u7684\u8bb8\u591a\u79cd\u75db\u82e6\u4e4b\u4e00\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u771f\u7684\u53ea\u8bb0\u5f97\u4ed6\u6551\u8fc7\u4f60\uff0c\u771f\u7684\u5df2\u5c06\u522b\u7684\u4e8b\u5168\u90fd\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u7b11\u58f0\u662f\u4ece\u5bf9\u9762\u7684\u5c4b\u6a90\u4e0a\u4f20\u4e0b\u6765\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6b63\u7b95\u8e1e\u5728\u5c4b\u6a90\u4e0a\uff0c\u5543\u7740\u6761\u9e21\u817f\uff0c\u5374\u8d6b\u7136\u6b63\u662f\u80e1\u75af\u5b50\u3002 \u3000\u3000\u4ed6\u773c\u775b\u76ef\u5728\u9e21\u817f\u4e0a\uff0c\u5e76\u6ca1\u6709\u77a7\u8fd9\u7236\u5b50\u4e24\u4eba\u4e00\u773c\uff0c\u4eff\u4f5b\u8fde\u8fd9\u9e21\u817f\u90fd\u6bd4\u4ed6\u4eec\u7236\u5b50\u597d\u770b\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4eec\u7528\u4e0d\u7740\u6e9c\u5f97\u8fd9\u4e48\u7094\uff0c\u674e\u5bfb\u6b22\u7edd\u5bf9\u4e0d\u4f1a\u8ffd\u6765\u7684\uff0c\u5426\u5219\u4ed6\u5c31\u6839\u672c\u4e0d\u4f1a\u8ba9 \u4f60\u4eec\u8d70\u51fa\u8fd9\u9053\u95e8\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u7684\u8138\u5df2\u6709\u4e9b\u53d1\u80b2\u3002 \u3000\u3000\u201c\u4ed6\u5df2\u660e\u767d\u674e\u5bfb\u6b22\u7684\u529b\u91cf\u662f\u4ece\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u4f46\u80e1\u75af\u5b50\u4e5f\u662f\u4e0d\u80fd\u5f97\u7f6a\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7a81\u7136\u7b11\u4e86\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\u4f60\u7834\u8d39\u6765\u7167\u987e\u6211\u90a3\u5144\u5f1f\u3002\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u80e1\u75af\u5b50\u60a0\u7136\u9053\uff1a\u201c\u5176\u5b9e\u90a3\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u674e\u5bfb\u6b22\u5403\u5f97\u5e76\u4e0d\u591a\uff0c\u6bcf\u592b\u53ea\u8981\u4e24\u6761\u9e21\u817f\u51e0\u4e2a\u9992\u5934\u5c31 \u591f\u4e86\uff0c\u66ff\u4f60\u5b88\u95e8\u7684\uff0c\u53c8\u662f\u4e2a\u767d\u75f4\uff0c\u6211\u6bcf\u6b21\u70b9\u4e86\u4ed6\u7684\u7761\u7a74\uff0c\u4ed6\u90fd\u4ee5\u4e3a\u662f\u771f\u7684\u81ea\u5df1\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u6697\u4e2d\u54ac\u7740\u7259\uff0c\u53ea\u6068\u4e0d\u5f97\u7acb\u523b\u8ba9\u90a3\u4eba\u957f\u7761\u4e0d\u9192\u3002 \u3000\u3000\u80e1\u75af\u5b50\u63a5\u7740\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u6709\u8fc7\u597d\u5904\uff0c\u6211\u4e5f\u5e2e\u8fc7\u4f60\u7684\u5fd9\uff0c\u6211\u4eec\u5df2\u4e92\u65e0\u8d4a\u6b20\uff0c\u5bf9\u4f60\u8fd9\u79cd\u4eba\uff0c \u6211\u672c\u6765\u8fde\u8bdd\u90fd\u61d2\u5f97\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u53ea\u6709\u966a\u7740\u7b11\uff0c\u542c\u7740\u3002\u201d \u3000\u3000\u80e1\u75af\u5b50\u9053\uff1a\u201c\u4f46\u6709\u53e5\u8bdd\u6211\u5374\u975e\u8bf4\u4e0d\u53ef\uff0c\u6700\u540e\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u80e1\u75af\u5b50\u9053\uff1a\u201c\u4f60\u867d\u662f\u4e2a\u6df7\u86cb\uff0c\u4e0a\u5b98\u91d1\u8679\u66f4\u6df7\u86cb\uff0c\u4f60\u82e5\u771f\u60f3\u548c\u4ed6\u7ed9\u62dc\u5144\u5f1f\uff0c\u8fd8\u4e0d\u5982\u81ea\u5df1\u8d76 \u5feb\u627e\u6839\u7ef3\u5b50\u4e0a\u540a\u597d\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u679c\u7136\u662f\u4ed6\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u4e00\u4e2a\u5b57\u90fd\u4e0d\u8bf4\u4e86\uff0c\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5df2\u843d\u5728 \u5c4b\u80cc\u540e\uff0c\u7728\u773c\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u76ee\u9001\u7740\u4ed6\uff0c\u5634\u89d2\u6e10\u6e10\u9732\u51fa\u4e00\u4e1d\u5f97\u610f\u7684\u5fae\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u548c\u4e0a\u5b98\u91d1\u8679\u7ed3\u62dc \u7684\u4e8b\uff0c\u6c5f\u6e56\u4e2d\u5df2\u6709\u8fd9\u4e48\u591a\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u6cbf\u7740\u5899\u89d2\uff0c\u6162\u6162\u7684\u8d70\u7740\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u548c\u963f\u98de\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u6c89\u9ed8\u901a\u5e38\u90fd\u6bd4\u8a00\u8bed\u66f4\u771f\u631a\uff0c\u66f4\u53ef\u8d35\u3002 \u3000\u3000\u9ec4\u660f\u3002 \u3000\u3000\u9ad8\u5899\u5185\u6709\u4eba\u5728\u5439\u7b1b\uff0c\u7b1b\u58f0\u4e2d\u4e5f\u5e26\u7740\u79cb\u7684\u8427\u745f\u3002 \u3000\u3000\u8fd9\u79cd\u4e50\u58f0\u5f80\u5f80\u6700\u5bb9\u6613\u4ee4\u4eba\u5fc6\u8d77\u5f80\u4e8b\u3001\u4e5f\u6700\u5bb9\u6613\u5f15\u8d77\u76f8\u601d\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u201c\u6211\u5f97\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5979\u5728\u7b49\u4f60\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u5979\u4e00\u5b9a\u5728\u7b49\u4f60\uff1f\u201d \u3000\u3000\u963f\u98de\u7684\u8138\u8272\u53c8\u82cd\u767d\u4e86\u4e9b\uff0c\u6c89\u4e86\u8bb8\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u6b21\u662f\u5979\u8981\u6211\u6765\u6551\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u5f88\u4e86\u89e3\u6797\u4ed9\u513f\uff0c\u4f46\u8fd9\u6b21\u5374\u5f88\u96be\u731c\u5f97\u5230\u5979\u7684\u7528\u610f\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\uff0c\u53ea\u6709\u4e24\u4e2a\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u6211\u5e0c\u671b\u2026\u2026\u4f60\u4eec\u4e5f\u80fd\u505a\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u4ed6\u5206\u4e86\u5f88\u591a\u6b21\u624d\u8bf4\u5b8c\uff0c\u8bf4\u5f97\u5f88\u8270\u6da9\uff0c\u663e\u89c1\u4ed6\u5fc3\u91cc\u5f88\u75db\u82e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u7740\u4ed6\u75db\u82e6\u7684\u773c\u8272\uff0c\u5fc3\u91cc\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684\u601c\u60af\u60b2\u4f24\u3002 \u3000\u3000\u53ea\u6709\u771f\u6b63\u7231\u8fc7\u7684\u4eba\uff0c\u624d\u80fd\u4e86\u89e3\u7231\u60c5\u7684\u529b\u91cf\u662f\u591a\u4e48\u53ef\u6015\u3002 \u3000\u3000\u7b1b\u58f0\u5df2\u8fdc\u4e86\uff0c\u542c\u6765\u5374\u66f4\u51c4\u51c9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u89c1\u89c1\u5979\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u5634\u95ed\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u65b9\u4fbf\uff0c\u4f60\u66ff\u6211\u53bb\u8c22\u8c22\u5979\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ea\u5e0c\u671b\u4f60\u83ab\u8981\u4f24\u5bb3\u5979\u3002\u201d \u3000\u3000\u963f\u98de\u672c\u4e0d\u4f1a\u8bf4\u8fd9\u79cd\u8bf4\u7684\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u674e\u5bfb\u6b22\u4ece\u672a\u4f24\u5bb3\u4efb\u4f55\u4eba\u2014\u2014\u674e\u5bfb\u6b22\u4f24\u5bb3\u7684\u53ea\u662f\u4ed6\u81ea \u5df1\u3002 \u3000\u3000\u53ea\u6709\u4e3a\u4e86\u6797\u4ed9\u513f\u963f\u98de\u624d\u4f1a\u8bf4\u8fd9\u79cd\u8bdd\u3002 \u3000\u3000\u731b\u62ac\u5934\uff0c\u773c\u524d\u4e00\u7247\u706f\u706b\u8f89\u714c\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u4ed6\u4eec\u53c8\u8d70\u56de\u4e86\u90a3\u6761\u957f\u8857\u3002 \u3000\u3000\u8fd9\u6761\u8857\u665a\u4e0a\u6bd4\u767d\u5929\u66f4\u70ed\u95f9\uff0c\u5404\u5f0f\u5404\u6837\u7684\u644a\u5b50\u524d\uff0c\u90fd\u60ac\u7740\u5f88\u4eae\u7684\u706f\u7b3c\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5728\u5927\u58f0\u8d3c \u559d\u7740\uff0c\u5439\u5618\u7740\u81ea\u5df1\u7684\u8d27\u7269\u3002 \u3000\u3000\u4e00\u4e32\u4e32\u4eae\u6676\u6676\u7684\u7cd6\u846b\u82a6\uff0c\u5728\u706f\u5149\u4e0b\u770b\u6765\u66f4\u4eae\u5f97\u5982\u540c\u5b9d\u77f3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u811a\u6b65\u7a81\u7136\u505c\u4e0b\u3002 \u3000\u3000\u6bcf\u4e00\u4e32\u7cd6\u846b\u82a6\uff0c\u4eff\u4f5b\u90fd\u6620\u7740\u4e00\u5f20\u8138\u3002 \u3000\u3000\u4e00\u5f20\u7a7f\u7ea2\u8863\u670d\u7684\u5c0f\u59d1\u5a18\u7684\u8138\uff0c\u5927\u5927\u7684\u773c\u775b\uff0c\u7b11\u8d77\u6765\u4e00\u8fb9\u4e00\u4e2a\u9152\u6da1\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u5230\u4e86\u90a3\u5356\u5305\u5b50\u548c\u6c34\u997a\u7684\u5c0f\u94fa\u3002 \u3000\u3000\u201c\u94c3\u94c3\u662f\u4e0d\u662f\u8fd8\u5728\u7b49\u7740\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u89c9\u5f97\u5f88\u60ed\u6127\uff0c\u4ed6\u5c45\u7136\u5df2\u5c06\u8fd9\u4ef6\u4e8b\u5b8c\u5168\u5fd8\u4e86\u3002 \u3000\u3000\u4ed6\u773c\u89d2\u867d\u5df2\u6709\u4e86\u76b1\u7eb9\uff0c\u4f46\u8c01\u4e5f\u4e0d\u80fd\u8bf4\u4ed6\u5df2\u8001\u4e86\u3002 \u3000\u3000\u90a3\u6b63\u548c\u94c3\u94c3\u7b2c\u4e00\u6b21\u5230\u8fd9\u91cc\u6765\u7684\u773c\u8272\u4e00\u6837\u2014\u2014\u963f\u98de\u4e5f\u4ece\u672a\u5230\u8fc7\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u3002 \u3000\u3000\u770b\u5230\u81ea\u5df1\u7684\u670b\u53cb\u8fd8\u6ca1\u6709\u5931\u53bb\u8d64\u5b50\u4e4b\u5fc3\uff0c\u603b\u662f\u4ee4\u4eba\u6109\u5feb\u7684\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u201c\u6211\u4eec\u5df2\u6709\u6839\u4e45\u6ca1\u6709\u5728\u4e00\u8d77\u559d\u4e24\u676f\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u559d\uff1f\u201d \u3000\u3000\u963f\u98de\u5fae\u7b11\u7740\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff0c\u53ea\u6709\u548c\u4f60\u5728\u4e00\u8d77\u65f6\uff0c\u6211\u624d\u4f1a\u60f3\u559d\u9152\u3002\u201d \u3000\u3000\u4ed6\u9762\u4e0a\u5c45\u7136\u4e5f\u9732\u51fa\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u60c5\u66f4\u5f00\u6717\uff0c\u7b11\u9053\uff1a\u201c\u997a\u5b50\u4e0b\u9152\uff0c\u8d8a\u6765\u8d8a\u6709\u2026\u2026\u6211\u4eec\u5c31\u5230\u90a3\u8fb9\u7684\u997a\u5b50\u94fa\u53bb\u5982\u4f55 \uff1f\u201d \u3000\u3000\u963f\u98de\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u518d\u8d35\u7684\u5730\u65b9\uff0c\u6211\u5c31\u8bf7\u4e0d\u8d77\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e16\u4e0a\u6709\u5f88\u591a\u79cd\u4e8b\u5f88\u5947\u5999\u3002 \u3000\u3000\u8b6c\u5982\u8bf4\uff1a \u3000\u3000\u8d8a\u4e11\u7684\u5973\u4eba\u8d8a\u559c\u6b22\u4f5c\u602a\uff0c\u8d8a\u7a77\u7684\u4eba\u8d8a\u559c\u6b22\u8bf7\u5ba2\u3002 \u3000\u3000\u8bf7\u5ba2\u7684\u786e\u4e5f\u6bd4\u88ab\u8bf7\u6109\u5feb\u5f97\u591a\uff0c\u53ea\u53ef\u60dc\u8fd9\u79cd\u6109\u5feb\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u61c2\u5f97\u4eab\u53d7\u3002 \u3000\u3000\u997a\u5b50\u94fa\u91cc\u7684\u751f\u610f\u5e76\u4e0d\u592a\u597d\uff0c\u56e0\u4e3a\u751f\u610f\u5927\u534a\u5df2\u88ab\u5916\u9762\u7684\u644a\u5b50\u62a2\u8d70\u4e86\uff0c\u6240\u4ee5\u73b0\u5728\u867d\u7136\u6b63\u662f\u5403 \u665a\u996d\u7684\u65f6\u5019\uff0c\u5e97\u91cc\u4e5f\u53ea\u6709\u56db\u4e94\u684c\u5ba2\u4eba\u4e86\u3002 \u3000\u3000\u89d2\u843d\u91cc\u7684\u684c\u5b50\u4e0a\uff0c\u5750\u7740\u4e2a\u767d\u8863\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b2c\u4e00\u773c\u5c31\u77a7\u89c1\u4e86\u4ed6\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4eba\u8d70\u8fdb\u6765\uff0c\u76ee\u5149\u9996\u5148\u5c31\u4f1a\u88ab\u4ed6\u6240\u5438\u5f15\u3002 \u3000\u3000\u867d\u7136\u5750\u5728\u8fd9\u79cd\u70df\u718f\u6cb9\u817b\u7684\u5c0f\u5e97\u91cc\uff0c\u4f46\u8fd9\u4eba\u5168\u8eab\u4e0a\u4e0b\u4ecd\u662f\u4e00\u5c18\u4e0d\u67d3\uff0c\u90a3\u4ef6\u96ea\u767d\u7684\u8863\u670d\u5c31\u50cf \u662f\u521a\u4ece\u70eb\u6597\u4e0b\u62ff\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u7a7f\u5f97\u867d\u7b80\u5355\uff0c\u5374\u5f88\u534e\u8d35\u3002 \u3000\u3000\u4f46\u8fd9\u4e9b\u90fd\u672c\u662f\u4ed6\u5438\u5f15\u4eba\u7684\u5730\u65b9\u4e00\u5438\u5f15\u4eba\u7684\uff0c\u662f\u4ed6\u7684\u6c14\u8d28\u3002 \u3000\u3000\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u50b2\u6c14\u3002 \u3000\u3000\u4ed6\u65c1\u8fb9\u7684\u513f\u5f20\u684c\u5b50\u90fd\u662f\u7a7a\u7740\u7684\uff0c\u56e0\u4e3a\u65e0\u8bba\u8c01\u548c\u4ed6\u5750\u5728\u4e00\u8d77\uff0c\u90fd\u4f1a\u89c9\u5f97\u81ea\u60ed\u5f62\u79fd\uff0c\u6709\u4ed6\u5728 \u8fd9\u91cc\uff0c\u522b\u4eba\u7684\u58f0\u97f3\u90e8\u5c0f\u4e86\u4e9b\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u90a3\u9713\u5728\u5c4b\u6a90\u4e0b\uff0c\u4ee5\u4e00\u5c0f\u952d\u94f6\u5b50\u51fb\u65ad\u9752\u8863\u5927\u6c49\u6241\u62c5\u7684\u4eba\uff0c\u4e5f\u6b63\u662f\u624b\u6307\u5b9b\u5982\u5229\u526a\uff0c\u5c06 \u5356\u535c\u778e\u5b50\u94f6\u68cd\u526a\u65ad\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u7559\u5728\u8fd9\u91cc\uff1f\u96be\u9053\u4e5f\u5728\u7b49\u4eba\u3002 \u3000\u3000\u4ed6\u672c\u6765\u6b63\u5728\u4e3e\u676f\uff0c\u5b6a\u5bfb\u6b22\u4e00\u5b9a\u8fdb\u6765\uff0c\u4ed6\u7684\u52a8\u4f5c\u4e5f\u7acb\u523b\u505c\u6b62\uff0c\u76ee\u5149\u4e5f\u7acb\u523b\u77ac\u4e5f\u4e0d\u77ac\u7684\u76ef\u5728 \u674e\u5bfb\u6b22\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u5bf9\u9762\u8fd8\u5750\u7740\u4e2a\u4eba\uff0c\u662f\u4e2a\u8eab\u82bd\u7ea2\u8863\u88f3\u7684\u5c0f\u59d1\u5a18\uff0c\u8fab\u5b50\u5f88\u957f\u3002 \u7b2c\u4e94\u5341\u4e5d\u7ae0\u3000\u52c7\u6c14>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e94\u5341\u4e5d\u7ae0\u3000\u52c7\u6c14 \u3000\u3000\u5979\u968f\u7740\u4ed6\u7684\u76ee\u5149\u56de\u8fc7\u5934\uff0c\u624d\u53d1\u73b0\u674e\u5bfb\u6b22\uff0c\u7acb\u523b\u96c0\u8dc3\u7740\u51b2\u4e86\u8fc7\u6765\uff0c\u7d27\u7d27\u62c9\u4f4f\u674e\u5bfb\u6b22\u7684\u624b\u5a07 \u7b11\u7740\u9053\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u7684\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u5fd8\u8bb0\u6211\u3002\u201d \u3000\u3000\u94c3\u94c3\u679c\u7136\u8fd8\u5728\u8fd9\u91cc\u7b49\u7740\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u6709\u4e9b\u6fc0\u52a8\uff0c\u53cd\u63e1\u4f4f\u5979\u7684\u624b\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e00\u76f4\u90fd\u5728\u8fd9\u91cc\u7b49\uff1f\u201d \u3000\u3000\u94c3\u94c3\u70b9\u4e86\u70b9\u5934\uff0c\u773c\u7736\u5df2\u7ea2\u4e86\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6765\u5f97\u8fd9\u4e48\u8fdf\uff0c\u4eba\u5bb6\u90fd\u5feb\u7b49\u5f97\u6025\u6b7b \u4e86\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u5728\u7b49\u4ed6\uff1f\u201d \u3000\u3000\u94c3\u94c3\u8fd9\u624d\u770b\u5230\u963f\u98de\uff0c\u795e\u60c5\u7acb\u523b\u53d8\u5f97\u6709\u4e9b\u5f02\u602a\u2014\u2014\u5979\u5f53\u7136\u662f\u8ba4\u5f97\u963f\u98de\u7684\uff0c\u963f\u98de\u5374\u4e0d\u8ba4\u5f97\u5979 \u3002 \u3000\u3000\u4ed6\u975e\u4f46\u672a\u4e0a\u8fc7\u90a3\u5c0f\u697c\uff0c\u751a\u81f3\u8fde\u505a\u68a6\u90fd\u672a\u60f3\u5230\u8fc7\u3002 \u3000\u3000\u94c3\u94c3\u7728\u4e86\u7728\u773c\uff0c\u7ec8\u4e8e\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u7b49\u4ed6\uff0c\u6211\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u7b49\u4eba\uff0c\u4e5f\u6709\u5f88\u591a\u4e8b\u60c5\u53ef\u4ee5\u505a\uff0c\u82e5\u662f\u7b49\u4eba\uff0c\u773c\u775b\u603b\u662f\u770b\u7740\u95e8\u7684\uff0c\u65e0\u8bba\u8c01 \u5728\u7b49\u4eba\uff0c\u90fd\u4e0d\u4f1a\u80cc\u5bf9\u7740\u95e8\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4ece\u672a\u60f3\u5230\u4ed6\u4f1a\u8bf4\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u5e73\u65f6\u672c\u6765\u4e00\u5411\u4e0d\u613f\u523a\u4f24\u4eba\uff0c\u73b0\u5728\u5374\u5ffd\u7136\u53d8\u5f97\u5f88\u5c16\u9510\uff0c\u5c16\u9510\u5f97\u53ef\u6015\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u80fd\u5fcd\u53d7\u522b\u4eba\u6b3a\u9a97\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u5728\u53f9\u606f\u3002 \u3000\u3000\u963f\u98de\u7684\u770b\u6cd5\u4e0d\u4f46\u5c16\u9510\uff0c\u800c\u4e14\u548c\u4efb\u4f55\u4eba\u90fd\u4e0d\u540c\uff0c\u5bf9\u5927\u591a\u6570\u4e8b\u4ed6\u90fd\u770b\u5f97\u6bd4\u522b\u4eba\u900f\u6f88\uff0c\u6bd4\u522b\u4eba \u6e05\u695a\u3002 \u3000\u3000\u5728\u6797\u4ed9\u513f\u9762\u524d\u4ed6\u4e3a\u4ec0\u4e48\u5c31\u4f1a\u53d8\u6210\u778e\u5b50\u5462\uff1f \u3000\u3000\u94c3\u94c3\u773c\u5708\u53c8\u7ea2\u4e86\uff0c\u773c\u6cea\u5df2\u7094\u6d41\u4e86\u4e0b\u6765\uff0c\u51c4\u7136\u9053\uff1a\u201c\u4f60\u82e5\u4e5f\u5728\u540c\u4e00\u4e2a\u5730\u65b9\u7b49\u4e86\u5341\u51e0\u5929\uff0c\u4f60 \u5c31\u4f1a\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u80cc\u5bf9\u7740\u95e8\u4e86\u3002\u201d \u3000\u3000\u5979\u6084\u6084\u62ed\u4e86\u62ed\u6cea\u75d5\uff0c\u5e7d\u5e7d\u7684\u63a5\u7740\u9053\uff1a\u201c\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u6bcf\u4e2a\u4eba\u8d70\u8fdb\u6765\uff0c\u6211\u7684\u5fc3\u90fd\u4f1a\u8df3\uff0c\u603b \u4ee5\u4e3a\u662f\u4ed6\u6765\u4e86\uff0c\u540e\u6765\u624d\u77e5\u9053\uff0c\u4f60\u7b49\u7684\u4eba\u82e5\u4e0d\u6765\uff0c\u5c31\u7b97\u5c06\u773c\u775b\u770b\u7740\u4e5f\u6ca1\u6709\u7528\u7684\uff0c\u7528\u773c\u775b\u76ef\u7740\u95e8 \uff0c\u53ea\u6709\u4ee4\u4f60\u7b49\u5f97\u66f4\u5fc3\u7126\uff0c\u82e5\u518d\u4e0d\u8f6c\u8fc7\u8eab\uff0c\u6211\u7b80\u76f4\u8981\u53d1\u75af\u3002\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u81ea\u5df1\u8bf4\u5f97\u592a\u591a\u4e86\u2026\u2026 \u3000\u3000\u94c3\u94c3\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u90a3\u4f4d\u5415\u2026\u2026\u5415\u5927\u54e5\u597d\u5fc3\u966a\u7740\u6211\uff0c\u53ea\u6015\u6211\u4e5f\u4f1a\u53d1\u75af\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u4e00\u8f6c\u8fc7\u53bb\uff0c\u5c31\u7acb\u523b\u548c\u90a3\u767d\u8863\u4eba\u7684\u76ee\u5149\u76f8\u9047\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\u8d70\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u591a\u8c22\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u4eba\u5ffd\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u66ff\u5979\u8c22\u6211\uff0c\u56e0\u4e3a\u6211\u7559\u5728\u8fd9\u5730\u65b9\uff0c\u5e76\u4e0d\u662f \u4e3a\u4e86\u966a\u5979\uff0c\u800c\u662f\u4e3a\u4e86\u7b49\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u7b49\u6211\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u7b49\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u7b11\u5bb9\u4e2d\u4e5f\u5e26\u7740\u79cd\u903c\u4eba\u7684\u50b2\u6c14\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u4e16\u4e0a\u53ea\u6709\u5c11\u6570\u51e0\u4e2a\u4eba\u503c\u5f97\u6211\u7b49 \uff0c\u5c0f\u674e\u63a2\u82b1\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u672a\u8868\u793a\u51fa\u60ca\u5f02\uff0c\u94c3\u94c3\u5df2\u62a2\u7740\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u544a\u8bc9\u4f60\u6211\u7b49\u7684\u4eba\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u600e\u4f1a \u8ba4\u5f97\u4ed6\u7684\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u5728\u6c5f\u6e56\u4e2d\u8d70\u52a8\uff0c\u82e5\u60f3\u6d3b\u5f97\u957f\u4e9b\uff0c\u5c31\u6709\u51e0\u4e2a\u4eba\u662f\u4f60\u975e\u8ba4\u8bc6\u4e0d\u53ef\u7684 \uff0c\u5c0f\u674e\u63a2\u82b1\u4e5f\u6b63\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u201c\u8fd8\u6709\u51e0\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u773c\u775b\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u522b\u7684\u4eba\u4e0d\u8bf4\uff0c\u81f3\u5c11\u8fd8\u6709\u6211\u548c\u4f60\uff01\u201d \u3000\u3000\u963f\u98de\u77a7\u4e86\u77a7\u81ea\u5df1\u7684\u624b\uff0c\u76ee\u4e2d\u7a81\u7136\u9732\u51fa\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u51c4\u51c9\u8427\u7d22\u4e4b\u610f\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u5728\u65c1\u8fb9 \u7684\u684c\u4e0a\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u9152\uff0c\u767d\u5e72\u3002\u201d \u3000\u3000\u5e97\u4f19\u966a\u7740\u7b11\uff0c\u9053\uff1a\u201c\u5ba2\u5b98\u8981\u4ec0\u4e48\u83dc\u4e0b\u9152\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u9152\uff0c\u9ec4\u9152\u3002\u2026\u2026\u201d \u3000\u3000\u4f1a\u559d\u9152\u7684\u4eba\u90fd\u77e5\u9053\uff0c\u4e00\u4e2a\u4eba\u82e5\u60f3\u5feb\u9189\uff0c\u6700\u597d\u7684\u6cd5\u5b50\u5c31\u662f\u7528\u9152\u6765\u4e0b\u9152\uff0c\u7528\u9ec4\u9152\u6765\u4e0b\u767d\u5e72\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8fd9\u79cd\u6cd5\u5b50\u867d\u7136\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u5374\u5f88\u5c11\u6709\u4eba\u7528\uff0c\u56e0\u4e3a\u4e00\u5206\u4eba\u5fc3\u91cc\u82e5\u6ca1\u6709\u5f88\u6df1\u7684\u75db\u82e6\uff0c \u603b\u5e0c\u671b\u81ea\u5df1\u9189\u5f97\u8d8a\u6162\u8d8a\u597d\u3002 \u3000\u3000\u767d\u8863\u4eba\u4e00\u76f4\u5728\u5f88\u7559\u610f\u7684\u77a7\u7740\u3002 \u3000\u3000\u4ed6\u950b\u5229\u7684\u76ee\u5149\u6e10\u6e10\u677e\u5f1b\uff0c\u751a\u81f3\u8fd8\u9732\u51fa\u79cd\u5931\u671b\u4e4b\u8272\uff0c\u4f46\u5f53\u4ed6\u76ee\u5149\u8f6c\u5411\u674e\u5bfb\u6b22\u65f6 \u3000\u3000\u77b3\u5b54\u7acb\u523b\u53c8\u6536\u7f29\u4e86\u8d77\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6b63\u5728\u77a7\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5927\u540d\u662f\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u5415\u51e4\u5148\u3002\u201d \u3000\u3000\u8fd9\u7684\u786e\u662f\u4e2a\u663e\u8d6b\u7684\u540d\u5b57\uff0c\u8db3\u4ee5\u4ee4\u4eba\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u6ca1\u6709\u89c9\u5f97\u610f\u5916\uff0c\u53ea\u6de1\u6de1\u7684\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u94f6\u621f\u6e29\u4faf\u5415\u5927\u4fa0\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u51b7\u51b7\u9053\uff1a\u201c\u94f6\u621f\u6e29\u4faf\u5341\u5e74\u524d\u5c31\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u6b21\uff0c\u674e\u5bfb\u6b22\u624d\u89c9\u5f97\u6709\u4e9b\u610f\u5916\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u6709\u8ffd\u95ee\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5415\u51e4\u5148\u8fd9\u53e5\u8bdd\u5fc5\u5b9a\u8fd8\u6709\u4e0b\u6587\u3002 \u3000\u3000\u5415\u51e4\u5148\u679c\u7136\u5df1\u63a5\u7740\u9053\uff1a\u201c\u94f6\u621f\u6e29\u4faf\u5df2\u6b7b\u4e86\uff0c\u5415\u51e4\u5148\u5374\u6ca1\u6709\u6b7b\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u7740\uff0c\u4f3c\u5728\u63a2\u7d22\u7740\u8fd9\u53e5\u8bdd\u7684\u771f\u610f\u3002 \u3000\u3000\u5415\u51e4\u5148\u662f\u4e2a\u5f88\u9a84\u50b2\u7684\u4eba\u3002 \u3000\u3000\u767e\u6653\u751f\u5728\u5175\u5668\u8c31\u4e0a\uff0c\u5c06\u4ed6\u7684\u94f6\u5c31\u5217\u540d\u7b2c\u4e94\uff0c\u5728\u522b\u4eba\u8bf4\u6765\u5df2\u662f\u79cd\u5149\u8363\uff0c\u4f46\u5728\u4ed6\u8fd9\u79cd\u4eba\u8bf4\u6765 \uff0c\u5374\u4e00\u5b9a\u4f1a\u8ba4\u662f\u5947\u803b\u5927\u8fb1\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u5fcd\u53d7\u5c48\u5c45\u4eba\u4e0b\u3002\u4f46\u4ed6\u4e5f\u77e5\u9053\u767e\u6653\u751f\u7edd\u4e0d\u4f1a\u770b\u9519\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u6bc1\u4e86\u81ea\u5df1\u7684\u94f6\u621f\uff0c\u7ec3\u6210\u4e86\u53e6\u4e00\u79cd\u66f4\u53ef\u6015\u7684\u6b66\u529f\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u65e9\u8be5\u60f3\u5230\u94f6\u621f\u6e29\u4faf\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u76ef\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5415\u51e4\u5148\u4e5f\u5df2\u6b7b\u4e86\u5341\u5e74\uff0c\u5982\u4eca\u624d\u590d\u6d3b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4e8b\u4ee4\u5415\u5927\u4fa0\u590d\u6d3b\u7684\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u6162\u6162\u7684\u4e3e\u8d77\u4e86\u4e00\u53ea\u624b\uff0c\u53f3\u624b\u3002 \u3000\u3000\u4ed6\u5c06\u8fd9\u53ea\u624b\u5e73\u653e\u5728\u684c\u4e0a\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4ee4\u6211\u590d\u6d3b\u7684\uff0c\u5c31\u662f\u8fd9\u53ea\u624b\uff01\u201d \u3000\u3000\u5728\u522b\u4eba\u770b\u6765\u5e76\u4e0d\u662f\u53ea\u5f88\u5947\u7279\u7684\u624b\u3002 \u3000\u3000\u624b\u6307\u5f88\u957f\uff0c\u6307\u7532\u4fee\u526a\u5f97\u5f88\u5e72\u51c0\uff0c\u76ae\u80a4\u4e5f\u5f88\u5149\u6ed1\uff0c\u5f88\u7ec6\u3002 \u3000\u3000\u8fd9\u6b63\u5f88\u914d\u5408\u5415\u51e4\u5148\u7684\u8eab\u5206\u3002 \u3000\u3000\u4f60\u82e5\u770b\u5f97\u5f88\u4ed4\u7ec6\uff0c\u624d\u4f1a\u53d1\u73b0\u8fd9\u53ea\u624b\u7684\u5947\u7279\u4e4b\u5904\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u7684\u62c7\u6307\u3001\u98df\u6307\u548c\u4e2d\u6307\uff0c\u80a4\u8272\u7adf\u548c\u522b\u7684\u5730\u65b9\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u4e09\u6839\u624b\u6307\u7684\u76ae\u80a4\u867d\u7136\u4e5f\u5f88\u7ec6\u5f88\u81ea\uff0c\u5374\u5e26\u7740\u5947\u7279\u7684\u5149\u91c7\uff0c\u7b80\u76f4\u5c31\u4e0d\u50cf\u662f\u8840\u8089\u9aa8\u9abc\u7ec7\u6210\u7684 \uff0c\u800c\u50cf\u662f\u67d0\u4e00\u79cd\u5947\u602a\u7684\u91d1\u5c5e\u6240\u94f8\u3002 \u3000\u3000\u4f46\u8fd9\u4e09\u6839\u624b\u6307\u5374\u53c8\u660e\u660e\u662f\u957f\u5728\u4ed6\u624b\u4e0a\u7684\u3002 \u3000\u3000\u4e00\u53ea\u6709\u76bf\u6709\u8089\u7684\u624b\u4e0a\uff0c\u600e\u4f1a\u7a81\u7136\u957f\u51fa\u4e09\u6839\u91d1\u5c5e\u94f8\u6210\u7684\u6307\u5934\uff01 \u3000\u3000\u5415\u51e4\u5148\u51dd\u6ce8\u7740\u81ea\u5df1\u7684\u624b\uff0c\u7a81\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u6068\u767e\u6653\u751f\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u4e0d\u6b7b\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u6b7b\uff0c\u6211\u5012\u60f3\u95ee\u95ee\u4ed6\uff0c\u624b\uff0c\u662f\u4e0d\u662f\u4e5f\u53ef\u7b97\u505a\u5175\u5668\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eca\u5929\u624d\u542c\u4eba\u8bf4\u8fc7\u4e00\u65ec\u5f88\u6709\u8da3\u7684\u8bdd\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u8bf4\uff1a\u53ea\u6709\u6740\u4eba\u7684\uff0c\u624d\u53ef\u7b97\u505a\u5229\u5668\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u624b\uff0c\u672c\u6765\u4e0d\u662f\u5175\u5668\uff0c\u4f46\u4e00\u53ea\u80fd\u6740\u4eba\u7684\u624b\uff0c\u5c31\u4e0d\u4f46\u662f\u5175\u5668\uff0c\u800c\u4e14\u662f\u5229\u5668\u3002 \u201d \u3000\u3000\u5415\u51e4\u5148\u6c89\u9ed8\u7740\uff0c\u4eff\u6cb8\u5e76\u6ca1\u6709\u4ec0\u4e48\u4e3e\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u62c7\u6307\uff0c\u98df\u6307\u548c\u4e2d\u6307\uff0c\u5374\u7a81\u7136\u95f4\u5c31\u6ca1\u4eba\u4e86\u684c\u5b50\u91cc\u3002 \u3000\u3000\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\uff0c\u751a\u81f3\u8fde\u676f\u4e2d\u76db\u5f97\u5f88\u6ee1\u7684\u9152\u90fd\u6ca1\u6709\u6ea2\u51fa\uff0c\u4ed6\u624b\u6307\u63d2\u5165\u684c\u5b50\uff0c\u5c31\u597d\u50cf\u7528 \u5feb\u5200\u5207\u8c46\u8150\u90a3\u4e48\u5bb9\u6613\u3002 \u3000\u3000\u5415\u51e4\u5148\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u53ea\u624b\u82e5\u4e5f\u80fd\u7b97\u5175\u5668\uff0c\u4e0d\u77e5\u80fd\u5728\u5175\u5668\u8c31\u4e2d\u6392\u540d\u7b2c\u51e0\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u5f88\u96be\u8bf4\uff0c\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4e00\u4ef6\u5175\u5668\u8981\u5bf9\u4ed8\u7684\u662f\u4eba\uff0c\u4e0d\u662f\u684c\u5b50\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u5f97\u5f88\u505a\uff0c\u4e5f\u5f88\u51b7\u9177\uff0c\u9053\uff1a\u201c\u5728\u6211\u773c\u4e2d\u770b\u6765\uff0c\u4e16\u4eba\u672c\u5c31\u548c\u8fd9\u5f20\u684c\u5b50\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u7f13\u7f13\u9053\uff1a\u201c\u5176\u4e2d\u5f53\u7136\u4e5f\u6709\u51e0\u4eba\u662f\u4f8b\u5916\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54ea\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u51b7\u51b7\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u6709\u516d\u4e2a\uff0c\u73b0\u5728\u624d\u77e5\u9053\u53ea\u6709\u56db\u4e2a\u3002\u201d \u3000\u3000\u4ed6\u6709\u610f\u95f4\u626b\u4e86\u963f\u98de\u4e00\u773c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u90ed\u5d69\u9633\u7684\u4eba\u5df2\u6b7b\u4e86\uff0c\u8fd8\u6709\u4e00\u4e2a\uff0c\u867d\u7136\u6d3b\u7740\u5374\u4e5f \u548c\u6b7b\u4e86\u76f8\u5dee\u65e0\u51e0\u3002\u201d \u3000\u3000\u963f\u98de\u662f\u80cc\u5bf9\u7740\u5415\u51e4\u5148\u7684\uff0c\u6839\u672c\u6ca1\u6709\u770b\u5230\u4ed6\u7684\u8138\u8272\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u8138\u8272\u7a81\u53c8\u53d1\u4e86\u9752\u3002 \u3000\u3000\u4ed6\u663e\u7136\u5df2\u542c\u61c2\u4e86\u5415\u51e4\u5148\u7684\u610f\u601d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4eba\u4e5f\u4f1a\u590d\u6d3b\u7684\uff0c\u800c\u4e14\u7528\u4e0d\u7740\u5341\u5e74\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u80fd\u590d\u6d3b\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u80fd\u590d\u6d3b\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u90a3\u4e0d\u540c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u51b7\u51b7\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u7684\u2018\u6b7b\u2019\u5e76\u4e0d\u662f\u6b7b\u5728\u5973\u4eba\u624b\u4e0a\u7684\uff0c\u800c\u4e14\u5fc3\u4e5f\u4e00\u76f4\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u201c\u55b3\u201d\u7684\uff0c\u963f\u98de\u624b\u91cc\u7684\u9152\u676f\u788e\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u9759\u9759\u7684\u5750\u7740\uff0c\u52a8\u4e5f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5415\u51e4\u5148\u8fde\u77a7\u90fd\u4e0d\u77a7\u4e86\uff0c\u773c\u775b\u76ef\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u51fa\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u627e\u8fd9\u56db\u4e2a\u4eba\uff0c \u8bc1\u660e\u6211\u7684\u624b\u80fd\u4e0d\u80fd\u7b97\u5229\u5668\uff0c\u6240\u4ee5\u6211\u624d\u4f1a\u5728\u8fd9\u5730\u65b9\u7b49\u7740\u4f60\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u8bc1\u660e\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4e00\u5b9a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u8981\u8bc1\u660e\u7ed9\u8c01\u770b\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u7ed9\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u9a97\u5f97\u8fc7\uff0c\u53ea\u6709\u81ea\u5df1\u662f\u6c38\u8fdc\u9a97\u4e0d\u8fc7\u7684\u2026\u2026 \u201d \u3000\u3000\u5415\u51e4\u5148\u970d\u7136\u7ad9\u8d77\u6765\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u5c31\u5728\u5916\u9762\u7b49\u7740\u4f60\uff01\u201d \u3000\u3000\u997a\u5b50\u5e97\u91cc\u7684\u5ba2\u4eba\uff0c\u4e0d\u77e5\u4f55\u65f6\u90fd\u5df2\u8d70\u5f97\u4e8e\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u94c3\u94c3\u54ac\u7740\u5634\u5507\uff0c\u4f3c\u5df2\u5413\u5446\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u94c3\u94c3\u5ffd\u7136\u62c9\u4f4f\u4ed6\u8863\u89d2\uff0c\u6084\u6084\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e00\u5b9a\u8981\u51fa\u53bb\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u5f97\u5f88\u8f9b\u9178\uff0c\u9053\uff1a\u201c\u4eba\u751f\u4e2d\u6709\u4e9b\u4e8b\uff0c\u4f60\u53ea\u8981\u9047\u7740\uff0c\u5c31\u6c38\u8fdc\u518d\u4e5f\u65e0\u6cd5\u9003\u907f\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8f6c\u5411\u963f\u98de\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u5415\u51e4\u5148\u5df2\u5c06\u8d70\u51fa\u4e86\u95e8\u3002 \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u201c\u6162\u7740\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u811a\u6b65\u505c\u4e0b\uff0c\u4e5f\u6ca1\u6709\u8f6c\u8eab\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u6709\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u60f3\u8bc1\u660e\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4f60\u60f3\u8bc1\u660e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u7d27\u63e1\u7740\u9152\u676f\u7684\u788e\u7247\u3002 \u3000\u3000\u9c9c\u8840\uff0c\u6b63\u4e00\u6ef4\u6ef4\u81ea\u4ed6\u624b\u4e2d\u6ef4\u843d\u3002 \u3000\u3000\u4ed6\u4e00\u5b57\u5b57\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53ea\u60f3\u8bc1\u660e\u6211\u7a76\u7adf\u662f\u6d3b\u7740\u7684\u8fd8\u662f\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5415\u51e4\u5148\u970d\u7136\u8f6c\u8eab\u3002 \u3000\u3000\u4ed6\u50cf\u662f\u8fd9\u624d\u7b2c\u4e00\u6b21\u770b\u5230\u4e86\u963f\u98de\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u77b3\u5b54\u53c8\u6e10\u6e10\u6536\u7f29\uff0c\u5634\u89d2\u5374\u9732\u51fa\u4e86\u4e00\u4e1d\u51b7\u9177\u7684\u7b11\uff0c\u9053\uff1a\u2018\u597d\uff0c\u6211\u4e5f\u7b49\u7740\u4f60\uff01\u201d \u3000\u3000\u575f\u5893\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u6bcf\u5929\u90fd\u6709\u51b3\u6597\uff0c\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u4e3a\u4e86\u5404\u79cd\u4e0d\u540c\u7684\u539f\u56e0\u4ee5\u5404\u5f0f\u5404\u6837\u4e0d\u540c\u7684\u65b9\u5f0f\u51b3\u6597 \u3002 \u3000\u3000\u4f46\u51b3\u6597\u7684\u5730\u65b9\u53ea\u6709\u51e0\u79cd\u3002 \u3000\u3000\u8352\u91ce\uff0c\u5c71\u6797\uff0c\u575f\u5893\u2026\u2026 \u3000\u3000\u82e5\u771f\u662f\u4e0d\u6b7b\u4e0d\u4f11\u7684\u51b3\u6597\uff0c\u5341\u6b21\u4e2d\u5fc5\u6709\u4e5d\u6b21\u662f\u9009\u5728\u8fd9\u79cd\u5730\u65b9\u7684\u2014\u2014\u4eff\u4f5b\u8fd9\u79cd\u5730\u65b9\u7684\u672c\u8eab\uff0c \u5c31\u5e26\u7740\u79cd\u201c\u6b7b\u201d\u6c14\u606f\u3002 \u3000\u3000\u591c\u5df2\u6e10\u6df1\uff0c\u6709\u96fe\u3002 \u3000\u3000\u5415\u51e4\u5148\u767d\u8863\u5982\u96ea\uff0c\u9759\u9759\u7684\u7ad9\u5728\u7070\u8272\u7684\u575f\u7891\u524d\uff0c\u5728\u51c4\u8ff7\u7684\u591c\u96fe\u4e2d\u770b\u6765\uff0c\u6b63\u5c31\u597d\u50cf\u6765\u81ea\u5730\u72f1 \u7684\u4f7f\u8005\uff0c\u8981\u5c06\u201c\u6b7b\u201d\u7684\u4fe1\u606f\u5e26\u7ed9\u4e16\u4eba\u3002 \u3000\u3000\u94c3\u94c3\u4f9d\u504e\u5728\u674e\u5bfb\u6b22\u8eab\u65c1\uff0c\u4f3c\u5728\u98a4\u6296\u3002 \u3000\u3000\u662f\u51b7\uff1f\u8fd8\u662f\u6015\uff1f \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u201c\u4f60\u8d70\u5f00\uff01\u201d \u3000\u3000\u94c3\u94c3\u7684\u8eab\u5b50\u53c8\u5f80\u540e\u7f29\u4e86\u7f29\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u3002\u201d \u3000\u3000\u94c3\u94c3\u54ac\u7740\u5634\u5507\uff0c\u62ac\u5934\u53bb\u671b\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u76ee\u5149\u4eff\u4f5b\u5f88\u9065\u8fdc\u3002 \u3000\u3000\u662f\u4ed6\u7684\u5fc3\u5df2\u8fdc\uff1f\u8fd8\u662f\u96fe\u592a\u6d53\uff1f \u3000\u3000\u94c3\u94c3\u5782\u4e0b\u5934\uff0c\u5583\u5583\u7740\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8bf4\u7684\u8bdd\uff0c\u6211\u4e0d\u80fd\u542c\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u542c\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u542c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4eba\u5bb6\u966a\u4e86\u4f60\u5f88\u591a\u5929\uff0c\u4f60\u81f3\u5c11\u4e5f\u8be5\u53bb\u966a\u966a\u4ed6\u3002\u201d \u3000\u3000\u94c3\u94c3\u5782\u7740\u5934\uff0c\u5446\u4e86\u534a\u664c\uff0c\u7a81\u7136\u8dfa\u7740\u811a\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u6839\u672c\u4e0d\u60f3\u7559\u5728\u8fd9\u91cc\uff0c\u6839\u672c\u4e0d\u60f3\u6765\u7684 \uff0c\u4f60\u4eec\u8fd9\u4e9b\u4eba\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u53ea\u77e5\u9053\u6740\u2026\u2026\u4f60\u6740\u6211\uff0c\u6211\u6740\u4f60\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u8fde\u4f60\u4eec\u81ea\u5df1 \u90fd\u4e0d\u77e5\u9053\u2026\u2026\u5047\u5982\u8981\u8fd9\u6837\u624d\u7b97\u82f1\u96c4\uff0c\u6700\u597d\u5929\u4e0b\u7684\u82f1\u96c4\u90fd\u4e00\u9f50\u6b7b\u5149\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\uff0c\u963f\u98de\uff0c\u5415\u51e4\u5148\uff0c\u90fd\u53ea\u662f\u9759\u9759\u7684\u542c\u7740\u3002 \u3000\u3000\u7136\u540e\u518d\u9759\u9759\u7684\u77a7\u7740\u5979\u98de\u5954\u51fa\u53bb\u3002 \u3000\u3000\u963f\u98de\u751a\u81f3\u8fde\u77a7\u90fd\u6ca1\u6709\u77a7\uff0c\u7b49\u5979\u7684\u811a\u6b65\u58f0\u8fdc\uff0c\u624d\u62ac\u5934\u9762\u5bf9\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u6211\u4ece\u672a\u6c42\u8fc7\u4f60\u4ec0 \u4e48\u4e8b\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4ece\u672a\u6c42\u8fc7\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u73b0\u5728\u6211\u5374\u6709\u4e8b\u8981\u6c42\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u4f60\u65e0\u8bba\u5982\u4f55\u518d\u4e5f\u4e0d\u80fd\u963b\u62e6\u6211\uff0c\u4e00\u5b9a\u8981\u8ba9\u6211\u53bb\uff0c\u4f60\u82e5\u62a2\u7740\u51fa\u624b \uff0c\u6211\u2026\u2026\u6211\u5c31\u6b7b\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u795e\u8272\u663e\u5f97\u5f88\u75db\u82e6\uff0c\u9eef\u7136\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u6839\u672c\u7528\u4e0d\u7740\u8fd9\u4e48\u505a\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u8fd9\u4e48\u6837\u505a\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u795e\u60c5\u66f4\u75db\u82e6\uff0c\u60e8\u7136\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u5415\u51e4\u5148\u8bf4\u7684\u5b9e\u5728\u4e0d\u9519\uff0c\u518d\u8fd9\u6837\u4e0b\u53bb\uff0c\u6211\u6e05\u7740\uff0c\u4e5f\u548c \u6b7b\u4e86\u5dee\u4e0d\u591a\uff0c\u6211\u7edd\u4e0d\u80fd\u653e\u8fc7\u8fd9\u673a\u4f1a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u673a\u4f1a\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u82e5\u60f3\u590d\u6d3b\uff0c\u82e5\u60f3\u65b0\u751f\uff0c\u8fd9\u5c31\u662f\u6211\u6700\u540e\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ee5\u540e\u96be\u9053\u5c31\u6ca1\u6709\u673a\u4f1a\u4e86\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u7eb5\u7136\u8fd8\u6709\u673a\u4f1a\uff0c\u53ef\u662f\u6211\u2026\u2026\u4eca\u5929\u6211\u82e5\u5931\u53bb\u4e86\u52c7\u6c14\uff0c\u4ee5\u540e\u5c31\u6c38 \u8fdc\u4e0d\u4f1a\u518d\u6709\u52c7\u6c14\u632f\u4f5c\uff01\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u53d7\u7684\u6253\u51fb\u592a\u5927\uff0c\u5c31\u4f1a\u53d8\u5f97\u6d88\u6c89\uff0c\u82e5\u662f\u6d88\u6c89\u5f97\u592a\u4e45\uff0c\u65e0\u8bba\u591a\u575a\u5f3a\u7684\u4eba\uff0c\u4e5f\u4f1a\u53d8\u5f97\u8f6f \u5f31\uff0c\u52c7\u6c14\u4e5f\u5fc5\u5b9a\u4f1a\u6d88\u5931\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u6211\u660e\u767d\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u6211\u51fa\u624b\u5df2\u6162\u4e86\uff0c\u56e0\u4e3a\u8fd9\u4e24\u5e74\u6765\uff0c\u6211\u4e5f\u5df2\u611f\u89c9\u5230\u81ea\u5df1\u7684 \u53cd\u5e94\u6e10\u6e10\u8fdf\u949d\uff0c\u751a\u81f3\u5df2\u6709\u4e9b\u9ebb\u6728\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u67d4\u58f0\u9053\uff1a\u201c\u53ea\u8981\u4f60\u6709\u51b3\u5fc3\uff0c\u4e00\u5207\u90fd\u4f1a\u6062\u590d\u7684\uff0c\u53ea\u4e0d\u8fc7\u4e00\u73b0\u5728\u8fd8\u4e0d\u662f\u65f6\u5019\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u73b0\u5728\u6b63\u662f\u65f6\u5019\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u73b0\u5728\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u6162\u6162\u7684\u644a\u5f00\u624b\u638c\u3002 \u3000\u3000\u9c9c\u8840\u5df1\u67d3\u7ea2\u4e86\u4ed6\u7684\u624b\uff0c\u9152\u676f\u7684\u788e\u7247\u8fd8\u5d4c\u5728\u8089\u91cc\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u6211\u5ffd\u7136\u53d1\u73b0\uff0c\u8089\u4f53\u4e0a\u7684\u75db\u82e6\u4e0d\u4f46\u53ef\u4ee5\u51cf\u8f7b\u5fc3\u91cc\u7684\u82e6\u607c\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5 \u4f7f\u4eba\u7cbe\u8fdb\uff0c\u632f\u4f5c\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u4eba\u654f\u9510\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u4e0d\u9519\u3002\u75db\u82e6\u672c\u5c31\u53ef\u523a\u6fc0\u4eba\u7684\u795e\u7ecf\uff0c\u4ee4\u4eba\u7684\u53cd\u5e94\u654f\u9510\uff0c\u4e5f\u53ef\u4ee5\u6fc0\u53d1\u4eba\u7684\u6f5c\u529b\u2014\u2014\u5c31 \u7b97\u662f\u4e00\u5339\u9a6c\uff0c\u5f53\u4f60\u97ad\u6253\u5b83\uff0c\u4ee4\u5b83\u89c9\u5f97\u75db\u82e6\u65f6\uff0c\u5b83\u4e5f\u4f1a\u8dd1\u5f97\u5feb\u4e9b\uff0c\u8d1f\u4e86\u4f24\u7684\u91ce\u517d\u4e5f\u901a\u5e38\u90fd\u6bd4\u5e73 \u65f6\u66f4\u53ef\u6015\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u601d\u7740\uff0c\u9053\uff1a\u201c\u4f60\u6709\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u6ca1\u6709\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u7b11\u4e86\uff0c\u7528\u529b\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u5427\uff01\u201d \u7b2c\u516d\u5341\u7ae0\u3000\u53cb\u60c5>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u7ae0\u3000\u53cb\u60c5 \u3000\u3000\u963f\u98de\u5374\u8fd8\u5728\u6c89\u541f\u7740\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u65b9\u624d\u90a3\u5c0f\u59d1\u5a18\u2026\u2026\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5979\u53eb\u94c3\u94c3\uff0c\u4e5f\u5f88\u53ef\u601c\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5979\u5f88\u4f1a\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5979\u5e76\u4e0d\u662f\u771f\u7684\u5728\u7b49\u4f60\u2014\u2014\u5979\u7b49\u4f60\uff0c\u4e5f\u8bb8\u8fd8\u6709\u522b\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5979\u82e5\u771f\u7684\u5728\u7b49\u4f60\uff0c\u81ea\u7136\u4e00\u5b9a\u5bf9\u4f60\u5f88\u5173\u5fc3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u62a2\u7740\u9053\uff1a\u201c\u4f60\u73b0\u5728\u7684\u6837\u5b50\uff0c\u8c01\u90fd\u770b\u5f97\u51fa\u4f60\u5fc5\u5b9a\u53d7\u4e86\u5f88\u591a\u7f6a\uff0c\u53ef\u662f\u5979\u5374\u6839\u672c\u6ca1\u6709\u95ee\u4f60 \u662f\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u79cd\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd8\u6ca1\u6709\u673a\u4f1a\u95ee\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5973\u5b69\u5b50\u82e5\u662f\u771f\u7684\u5173\u5fc3\u4e00\u4e2a\u4eba\uff0c\u7edd\u4e0d\u4f1a\u7b49\u4ec0\u4e48\u673a\u4f1a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u7a81\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u6015\u6211\u4f1a\u4e0a\u5979\u7684\u5f53\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5979\u8bf4\u7684\u4e0d\u662f\u771f\u8bdd\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u6d3b\u5f97\u6109\u5feb\u4e9b\uff0c\u5c31\u5343\u4e07\u4e0d\u8981\u5e0c\u671b\u5973\u4eba\u5bf9\u4f60\u8bf4\u771f\u8bdd\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6bcf\u4e2a\u5973\u4eba\u90fd\u4f1a\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u663e\u7136\u4e0d\u613f\u6b63\u9762\u56de\u7b54\u4ed6\u8fd9\u53e5\u6d3b\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4ee5\u540e\u4e5f\u5343\u4e07\u83ab\u8981\u5f53\u9762\u63ed\u7a7f \u5973\u4eba\u7684\u8c0e\u8bdd\uff0c\u56e0\u4e3a\u4f60\u5c31\u7b97\u63ed\u7a7f\u4e86\uff0c\u5979\u4e5f\u4f1a\u6709\u5f88\u597d\u7684\u89e3\u91ca\uff0c\u4f60\u5c31\u7b97\u4e0d\u76f8\u4fe1\u5979\u7684\u89e3\u91ca\uff0c\u5979\u8fd8\u662f\u7edd \u4e0d\u4f1a\u627f\u8ba4\u81ea\u5df1\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u82e5\u9047\u89c1\u4e86\u4e00\u4e2a\u4f1a\u8bf4\u8c0e\u7684\u5973\u4eba\uff0c\u6700\u597d\u7684\u6cd5\u5b50\uff0c\u662f\u6545\u610f\u88c5\u4f5c \u5b8c\u5168\u76f8\u4fe1\u5979\uff0c\u5426\u5219\u4f60\u5c31\u662f\u5728\u81ea\u627e\u82e6\u5403\u3002\u201d \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u826f\u4e45\u826f\u4e45\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8fd8\u6709\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u963f\u98de\u7a81\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u6709\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u56e0\u4e3a\u6211\u8981\u8bf4\u7684\u4f60\u90fd\u5df2\u77e5\u9053\u3002\u201d \u3000\u3000\u671b\u7740\u963f\u98de\u7684\u80cc\u5f71\uff0c\u674e\u5bfb\u6b22\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u6109\u5feb\u3002\u8fd9\u5014\u5f3a\u7684\u5c11\u5e74\u6bd5\u7adf\u6ca1\u6709\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u800c\u4e14\uff0c\u8fd9\u4e00\u6b21\uff0c\u4ed6\u8bf4\u4e86\u5f88\u591a\u8bdd\uff1a\u5c45\u7136\u5168\u6ca1\u6709\u63d0\u8d77\u6797\u4ed9\u513f\u3002 \u3000\u3000\u7231\u60c5\uff0c\u6bd5\u7adf\u4e0d\u80fd\u5360\u6709\u4e00\u4e2a\u7537\u5b50\u6c49\u7684\u5168\u90e8\u751f\u547d\u3002 \u3000\u3000\u963f\u98de\u6bd5\u7adf\u662f\u4e2a\u7537\u5b50\u6c49\uff01 \u3000\u3000\u7537\u5b50\u6c49\u82e5\u662f\u89c9\u5f97\u81ea\u5df1\u6d3b\u7740\u5df2\u662f\u4ef6\u7f9e\u8fb1\u65f6\uff0c\u4ed6\u5c31\u5b81\u53ef\u6c38\u4e0d\u518d\u89c1\u4ed6\u6240\u7231\u7684\u5973\u4eba\uff0c\u5b81\u53ef\u53bb\u5929\u6daf \u6d41\u6d6a\uff0c\u5b81\u53ef\u6b7b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u89c9\u5f97\u5df2\u65e0\u989c\u89c1\u5979\u3002 \u3000\u3000\u4f46\u963f\u98de\u771f\u80fd\u80dc\u5f97\u4e86\u5415\u51e4\u5148\uff1f \u3000\u3000\u8fd9\u6b21\u4ed6\u82e5\u53c8\u8d25\u4e86\uff0c\u5415\u51e4\u5148\u7eb5\u4e0d\u6740\u4ed6\uff0c\u4ed6\u8fd8\u80fd\u518d\u6d3b\u5f97\u4e0b\u53bb\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5f2f\u4e0b\u8170\uff0c\u5267\u70c8\u5730\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u53c8\u54b3\u51fa\u4e86\u8840\u3002 \u3000\u3000\u5415\u51e4\u5148\u8fd8\u5728\u90a3\u91cc\u7b49\u7740\uff0c\u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u786e\u5f88\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u53ea\u6709\u80fd\u6c89\u5f97\u4f4f\u6c14\u7684\u654c\u4eba\uff0c\u624d\u662f\u53ef\u6015\u7684\u5bf9\u624b\u3002 \u3000\u3000\u963f\u98de\u7a81\u7136\u4e00\u628a\u626f\u4e0b\u4e86\u8863\u886b\uff0c\u7528\u90a3\u53ea\u5df2\u88ab\u9c9c\u8840\u67d3\u7ea2\u4e86\u7684\u624b\u5728\u8eab\u4e0a\u63c9\u7740\u3002 \u3000\u3000\u9152\u676f\u7684\u788e\u7247\u53c8\u523a\u5165\u4e86\u4ed6\u8089\u91cc\u3002 \u3000\u3000\u8840\uff0c\u5373\u4f7f\u5728\u5982\u6b64\u51c4\u8ff7\u7684\u591c\u96fe\u4e2d\uff0c\u770b\u6765\u8fd8\u662f\u9c9c\u7ea2\u7684\uff01 \u3000\u3000\u53ea\u6709\u9c9c\u8840\u624d\u80fd\u6fc0\u53d1\u4eba\u539f\u59cb\u7684\u517d\u6027\u2014\u2014\u60c5\u6b32\u548c\u4ec7\u6068\uff0c\u522b\u7684\u4e1c\u897f\u6216\u8bb8\u4e5f\u80fd\uff0c\u4f46\u5374\u7edd\u6ca1\u6709\u9c9c\u8840 \u5982\u6b64\u76f4\u63a5\u3002 \u3000\u3000\u963f\u98de\u4eff\u4f5b\u53c8\u56de\u5230\u4e86\u539f\u91ce\u4e2d\u3002 \u3000\u3000\u201c\u4f60\u82e5\u8981\u751f\u5b58\uff0c\u5c31\u5f97\u8981\u4f60\u7684\u654c\u4eba\u6b7b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u671b\u7740\u4ed6\u6e10\u6e10\u8d70\u8fd1\uff0c\u7a81\u7136\u89c9\u5f97\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u538b\u529b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u8d70\u8fc7\u6765\u7684\u7b80\u76f4\u4e0d\u662f\u4e2a\u4eba\uff0c\u800c\u662f\u53ea\u91ce\u517d\u3002 \u3000\u3000\u8d1f\u4e86\u4f24\u7684\u91ce\u517d\uff01 \u3000\u3000\u201c\u4ec7\u654c\u4e0e\u670b\u53cb\u95f4\u7684\u5206\u522b\uff0c\u5c31\u6b63\u5982\u751f\u4e0e\u6b7b\u4e4b\u95f4\u7684\u5206\u522b\u3002\u201d \u3000\u3000\u201c\u82e5\u6709\u4eba\u60f3\u8981\u4f60\u6b7b\uff0c\u4f60\u5c31\u5f97\u8981\u4ed6\u6b7b\uff0c\u8fd9\u5176\u95f4\u7edd\u65e0\u9009\u62e9\u7684\u4f59\u5730\uff01\u201d \u3000\u3000\u8fd9\u662f\u539f\u91ce\u4e0a\u7684\u6cd5\u5219\uff01\u4e5f\u662f\u751f\u5b58\u7684\u6cd5\u5219\u3002 \u3000\u3000\u201c\u5bbd\u6055\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5728\u67d0\u4e9b\u5730\u65b9\u662f\u5b8c\u5168\u4e0d\u5b9e\u9645\u7684\u3002 \u3000\u3000\u8840\u5728\u6d41\uff0c\u4e0d\u505c\u7684\u6d41\u3002\u963f\u98de\u8eab\u4e0a\u7684\u6bcf\u6839\u808c\u8089\u90fd\u5df2\u56e0\u75db\u82e6\u800c\u98a4\u6296\uff0c\u4f46\u4ed6\u7684\u624b\uff0c\u5374\u8d8a\u6765\u8d8a\u575a\u5b9a \u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u4e5f\u8d8a\u6765\u8d8a\u51b7\u9177\u3002 \u3000\u3000\u5415\u98ce\u5148\u6c38\u8fdc\u65e0\u6cd5\u4e86\u89e3\u8fd9\u5c11\u5e74\u600e\u4f1a\u5728\u5ffd\u7136\u95f4\u53d8\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5f88\u4e86\u89e3\u963f\u98de\u7684\u5251\u6cd5\u3002 \u3000\u3000\u963f\u98de\u5251\u6cd5\u7684\u53ef\u6015\u4e4b\u5904\u5e76\u4e0d\u5728\u201c\u5feb\u201d\u4e0e\u201c\u72e0\u201d\uff0c\u800c\u662f\u201c\u7a33\u201d\u4e0e\u201c\u51c6\u201d\u3002 \u3000\u3000\u4ed6\u4e00\u51fa\u624b\u5c31\u8981\u7f6e\u4eba\u4e8e\u6b7b\u547d\uff0c\u81f3\u5c11\u4e5f\u5f97\u6709\u4e03\u6210\u628a\u63e1\uff0c\u4ed6\u624d\u4f1a\u51fa\u624b\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5fc5\u987b\u201c\u7b49\u201d\uff01 \u3000\u3000\u7b49\u5bf9\u65b9\u9707\u51fa\u7834\u7efd\uff0c\u9732\u51fa\u5f31\u70b9\uff0c\u7b49\u5bf9\u65b9\u7ed9\u4ed6\u673a\u4f1a\u4e00\u4ed6\u6bd4\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u90fd\u80fd\u7b49\u5f97\u66f4\u4e45\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5415\u51e4\u5148\u4f3c\u5df2\u51b3\u5fc3\u4e0d\u7ed9\u4ed6\u8fd9\u673a\u4f1a\u3002 \u3000\u3000\u5415\u51e4\u5148\u770b\u6765\u867d\u53ea\u662f\u968f\u968f\u4fbf\u4fbf\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5168\u8eab\u4e0a\u4e0b\u6bcf\u4e00\u5904\u770b\u6765\u4eff\u4f5b\u90fd\u662f\u7a7a\u95e8\uff0c\u963f\u98de\u7684\u5251 \u6cd5\u4eff\u4f5b\u53ef\u4ee5\u968f\u4fbf\u523a\u4eba\u4ed6\u8eab\u4e0a\u4efb\u4f55\u90e8\u4f4d\u3002 \u3000\u3000\u4f46\u7a7a\u95e8\u592a\u591a\uff0c\u53cd\u800c\u53d8\u6210\u4e86\u6ca1\u6709\u7a7a\u95e8\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u4f3c\u5df2\u53d8\u6210\u4e86\u4e00\u7247\u7a7a\u7075\u3002 \u3000\u3000\u8fd9\u201c\u7a7a\u7075\u201d\u4e8c\u5b57\uff0c\u4e5f\u6b63\u662f\u6b66\u5b66\u4e2d\u6700\u9ad8\u7684\u5883\u754c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fdc\u8fdc\u7684\u77a7\u7740\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u5fe7\u8651\u3002 \u3000\u3000\u5415\u51e4\u5148\u7684\u786e\u503c\u5f97\u81ea\u50b2\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5b9e\u672a\u60f3\u5230\u4ed6\u7684\u6b66\u529f\u7adf\u5982\u6b64\u9ad8\uff0c\u4e5f\u770b\u4e0d\u51fa\u963f\u98de\u6709\u4efb\u4f55\u5e0c\u671b\u80fd\u80dc\u5f97\u4e86\u4ed6\u2014\u2014\u56e0\u4e3a\u963f\u98de \u7b80\u76f4\u8fde\u51fa\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u591c\u66f4\u6df1\u3002 \u3000\u3000\u8352\u575f\u95f4\u5ffd\u7136\u6709\u78a7\u5149\u95ea\u52a8\uff0c\u662f\u9b3c\u706b\uff01 \u3000\u3000\u5439\u7684\u662f\u897f\u98ce\uff0c\u5415\u98ce\u5148\u7684\u8138\uff0c\u6b63\u662f\u671d\u897f\u7684\u3002 \u3000\u3000\u6709\u98ce\u5439\u8fc7\uff0c\u4e00\u70b9\u9b3c\u706b\u968f\u98ce\u98d8\u5230\u4e86\u5415\u51e4\u5148\u9762\u524d\u3002 \u3000\u3000\u5415\u51e4\u5148\u9547\u9759\u7684\u773c\u795e\u7a81\u7136\u7728\u4e86\u7728\uff0c\u5de6\u624b\u4e5f\u52a8\u4e86\u52a8\u4e00\u50cf\u662f\u8981\u62c2\u53bb\u8fd9\u70b9\u9b3c\u706b\uff0c\u5374\u53c8\u7acb\u523b\u5fcd\u4f4f\u3002 \u3000\u3000\u5728\u751f\u6b7b\u51b3\u6597\u4e2d\uff0c\u4efb\u4f55\u4e0d\u5fc5\u8981\u7684\u52a8\u4f5c\uff0c\u90fd\u53ef\u80fd\u5e26\u6765\u81f4\u547d\u7684\u5371\u9669\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u624b\u867d\u6ca1\u6709\u52a8\uff0c\u4f46\u5de6\u81c2\u7531\u80a9\u7684\u808c\u8089\u5df2\u56e0\u8fd9\u201c\u8981\u52a8\u7684\u5ff5\u5934\u201d\u800c\u7d27\u5f20\u8d77\u6765\uff0c\u5df2\u4e0d\u80fd\u518d \u4fdd\u6301\u90a3\u79cd\u201c\u7a7a\u7075\u201d\u7684\u5883\u754c\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u4e0d\u80fd\u7b97\u662f\u4e2a\u597d\u673a\u4f1a\uff0c\u4f46\u518d\u574f\u7684\u673a\u4f1a\uff0c\u4e5f\u6bd4\u6ca1\u6709\u673a\u4f1a\u597d\u3002 \u3000\u3000\u53ea\u8981\u6709\u673a\u4f1a\uff0c\u963f\u98de\u5c31\u7edd\u4e0d\u4f1a\u9519\u8fc7\u3002 \u3000\u3000\u4ed6\u7684\u5251\u5df2\u51fa\u624b\uff01 \u3000\u3000\u8fd9\u4e00\u5251\u7684\u5173\u7cfb\u5b9e\u5728\u592a\u5927\u3002 \u3000\u3000\u963f\u98de\u4eca\u540e\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u90fd\u5c06\u56e0\u8fd9\u4e00\u5251\u7684\u5f97\u5931\u800c\u6539\u53d8\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u82e5\u5f97\u624b\uff0c\u963f\u98de\u5c31\u4f1a\u4ece\u6b64\u632f\u4f5c\uff0c\u6d17\u6e05\u4e0a\u4e00\u6b21\u5931\u8d25\u7684\u58f9\u8fb1\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u82e5\u5931\u624b\uff0c\u4ed6\u52bf\u5fc5\u4ece\u6b64\u6d88\u6c89\uff0c\u751a\u81f3\u5815\u843d\uff0c\u90a3\u4e48\u4ed6\u5c31\u7b97\u8fd8\u80fd\u6d3b\u7740\uff0c\u4e5f\u4f1a\u53d8\u5f97\u5982\u5415\u51e4\u5148 \u8bf4\u7684\u90a3\u6837\u2014\u2014\u751f\u4e0d\u5982\u6b7b\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5b9e\u5728\u662f\u53ea\u8bb8\u6210\u529f\uff0c\u4e0d\u8bb8\u5931\u8d25\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u5251\u771f\u80fd\u5f97\u624b\u4e48\uff1f \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u505c\u987f\uff01 \u3000\u3000\u201c\u545b\u201d\uff0c\u5251\u5df1\u6298\uff01 \u3000\u3000\u963f\u98de\u540e\u9000\uff0c\u624b\u91cc\u5df2\u53ea\u5269\u4e0b\u7684\u534a\u67c4\u65ad\u5251\u3002 \u3000\u3000\u53e6\u534a\u67c4\u5251\u88ab\u5939\u5728\u5415\u51e4\u5148\u7684\u624b\u6307\u91cc\uff0c\u4f46\u5251\u5c16\u5374\u5df2\u523a\u4eba\u4e86\u4ed6\u80a9\u5934\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5939\u4f4f\u4e86\u963f\u98de\u7684\u5251\uff0c\u4f46\u51fa\u624b\u663e\u7136\u8fd8\u662f\u6162\u4e86\u4e9b\u3002 \u3000\u3000\u9c9c\u8840\u6b63\u4ece\u4ed6\u80a9\u5934\u6d41\u843d\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u6bd5\u7adf\u5f97\u624b\u4e86\uff01 \u3000\u3000\u963f\u98de\u8138\u4e0a\u4eff\u4f5b\u7a81\u7136\u9732\u51fa\u4e86\u4e00\u79cd\u5947\u5f02\u7684\u5149\u8f89\u2014\u2014\u80dc\u5229\u7684\u5149\u8f89\uff01 \u3000\u3000\u5415\u51e4\u5148\u8138\u4e0a\u5374\u8fde\u4e00\u4e1d\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u53ea\u662f\u51b7\u51b7\u7684\u77a7\u7740\u963f\u98de\uff0c\u65ad\u5251\u72b9\u5728\u4ed6\u80a9\u5934\uff0c\u4ed6\u4e5f\u6ca1\u6709\u62d4 \u51fa\u6765\u3002 \u3000\u3000\u963f\u98de\u4e5f\u53ea\u662f\u9759\u9759\u7684\u7ad9\u7740\uff0c\u5e76\u6ca1\u6709\u518d\u51fa\u624b\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u7684\u79ef\u90c1\u548c\u82e6\u95f7\u5df2\u56e0\u8fd9\u4e00\u5251\u800c\u53d1\u6cc4\u3002 \u3000\u3000\u4ed6\u8981\u7684\u53ea\u662f\u201c\u80dc\u5229\u201d\uff0c\u5e76\u4e0d\u662f\u522b\u4eba\u7684\u201c\u751f\u547d\u201d\u3002 \u3000\u3000\u5415\u51e4\u5148\u4f3c\u4e4e\u8fd8\u5728\u7b49\u7740\u4ed6\u51fa\u624b\uff0c\u7b49\u4e86\u5f88\u4e45\uff0c\u7a81\u7136\u9053\uff1a\u201c\u597d\uff0c\u5f88\u597d\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u5f88\u660e\u663e\uff0c\u80fd\u4ece\u4ed6\u8fd9\u79cd\u4eba\u5634\u91cc\u542c\u5230\u8fbe\u53e5\u8bdd\uff0c\u5c31\u5df2\u662f\u4ee4\u4eba\u89c9\u5f97\u632f\u594b\uff0c\u89c9\u5f97\u9a84\u50b2 \u3002 \u3000\u3000\u4f46\u4ed6\u5728\u4e34\u8d70\u524d\uff0c\u5374\u53c8\u7a81\u7136\u52a0\u4e86\u53e5\uff01\u3002 \u3000\u3000\u201c\u674e\u5bfb\u6b22\u679c\u7136\u6ca1\u6709\u8bf4\u9519\uff0c\u4e5f\u6ca1\u6709\u770b\u9519\u4f60\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u674e\u5bfb\u6b22\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\u4ec0\u4e48\uff1f \u3000\u3000\u5415\u51e4\u5148\u7684\u8eab\u5f71\u7ec8\u4e8e\u5728\u591c\u8272\u4e2d\u6d88\u5931\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u7b11\u8138\u5df2\u51fa\u73b0\u5728\u773c\u524d\u3002 \u3000\u3000\u4ed6\u7528\u529b\u62cd\u7740\u963f\u98de\u7684\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4f60\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u90a3\u70b9\u6253\u51fb\u51b3\u4e0d\u4f1a\u4ee4\u4f60\u6cc4\u6c14\u7684\uff0c \u4e16\u4e0a\u672c\u5c31\u6ca1\u6709\u5e38\u80dc\u7684\u5c06\u519b\uff0c\u8fde\u795e\u90fd\u6709\u8d25\u7684\u65f6\u5019\uff0c\u4f55\u51b5\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u7b11\u5f97\u66f4\u5f00\u6717\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u53ef\u662f\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u6211\u5bf9\u4f60\u66f4\u6709\u4fe1\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u4ece\u6b64\u4e0d\u4f1a\u518d\u8d25\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u5415\u51e4\u5148\u7684\u6b66\u529f\uff0c\u5df2\u7edd\u4e0d\u5728\u4efb\u4f55\u4eba\u4e4b\u4e0b\uff0c\u82e5\u8fde\u4ed6\u4e5f\u8eb2\u4e0d\u8fc7\u4f60\u7684\u5251\uff0c\u53ea\u6015\u4e16 \u4e0a\u5c31\u6ca1\u6709\u522b\u4eba\u80fd\u8eb2\u5f97\u8fc7\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u6211\u5374\u89c9\u5f97\u8fd9\u6b21\u80dc\u5f97\u6709\u4e9b\u52c9\u5f3a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u52c9\u5f3a\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u51fa\u624b\u5df2\u4e0d\u5982\u4ee5\u524d\u5feb\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u7528\u4e0d\u7740\u522b\u4eba\u8bf4\uff0c\u6211\u81ea\u5df1\u4e5f\u80fd\u611f\u89c9\u5f97\u51fa\u2026\u2026\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u8fd8\u505c\u7559\u5728\u5415\u51e4\u5148\u8eab\u5f71\u6d88\u5931\u5904\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6211\u89c9\u5f97\u4ed6\u672c\u53ef\u80dc\u6211\u7684\uff0c\u4ed6\u51fa\u624b\u7edd\u4e0d \u8be5\u6bd4\u6211\u6162\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u6b66\u529f\u7684\u786e\u5f88\u9ad8\uff0c\u751a\u81f3\u4e5f\u8bb8\u6bd4\u4f60\u8fd8\u9ad8\uff0c\u4f46\u4f60\u5374\u628a\u63e1\u4f4f\u4e86\u6700\u597d\u7684\u673a\u4f1a\uff0c\u8fd9\u624d \u662f\u522b\u4eba\u7edd\u5bf9\u6bd4\u4e0d\u4e0a\u4f60\u7684\u5730\u65b9\uff0c\u6240\u4ee5\u4f60\u624d\u80fd\u80dc\uff01\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u5415\u51e4\u5148\u867d\u8d25\u4e86\uff0c\u4e5f\u5e76\u6ca1\u6709\u4e0d\u670d\uff0c\u8fde\u4ed6\u8fd9\u79cd\u4eba\u90fd\u5bf9\u4f60\u670d\u4e86\uff0c\u4f60\u81ea \u5df1\u5bf9\u81ea\u5df1\u96be\u9053\u8fd8\u6ca1\u6709\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u7b11\u4e86\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u53d7\u8fc7\u6253\u51fb\u7684\u4eba\u8bf4\u6765\uff0c\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u6bd4\u670b\u53cb\u7684\u9f13\u52b1\u66f4\u73cd\u8d35\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u4ef6\u4e8b\u90fd\u8be5\u5e86\u795d\u2026\u2026\u4f60\u559c\u6b22\u7528\u4ec0\u4e48\u6765\u5e86\u795d\uff1f\u201d \u3000\u3000\u963f\u98de\u7b11\u9053\uff1a\u201c\u9152\uff0c\u5f53\u7136\u662f\u9152\uff0c\u9664\u4e86\u9152\u8fd8\u80fd\u6709\u4ec0\u4e48\u522b\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u7136\u662f\u9152\uff0c\u5e86\u795d\u65f6\u82e5\u6ca1\u6709\u9152\uff0c\u5c82\u975e\u5c31\u597d\u50cf\u7092\u83dc\u65f6\u4e0d\u653e\u76d0\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7b11\u9053\uff1a\u201c\u90a3\u7b80\u76f4\u6bd4\u7092\u83dc\u65f6\u4e0d\u653e\u76d0\u8fd8\u8981\u6de1\u800c\u65e0\u5473\u3002\u201d \u3000\u3000\u963f\u98de\u7761\u4e86\u3002 \u3000\u3000\u9152\uff0c\u7684\u786e\u5f88\u5947\u5999\uff0c\u6709\u65f6\u80fd\u4ee4\u4eba\u5174\u594b\uff0c\u6709\u65f6\u5374\u53c8\u80fd\u4ee4\u4eba\u5b89\u7720\u3002 \u3000\u3000\u8fd9\u51e0\u5929\uff0c\u963f\u98de\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u7761\u8fc7\uff0c\u7eb5\u7136\u7761\u7740\u4e5f\u5f88\u5feb\u5c31\u9192\uff0c\u4ed6\u603b\u60f3\u4e0d\u901a\u81ea\u5df1\u5728\u201c\u5bb6\u201d\u65f6\u600e \u4f1a\u4e00\u8eba\u4e0b\u53bb\u5c31\u7761\u7684\u50cf\u6b7b\u732a\u3002 \u3000\u3000\u7b49\u963f\u98de\u7761\u7740\uff0c\u674e\u5bfb\u6b22\u5c31\u8d70\u51fa\u4e86\u8fd9\u5bb6\u5ba2\u6808\u3002 \u3000\u3000\u8f6c\u8fc7\u8857\uff0c\u8fd8\u6709\u5bb6\u5ba2\u6808\u3002\u674e\u5bfb\u6b22\u7a81\u7136\u98de\u8eab\u63a0\u5165\u4e86\u8fd9\u5bb6\u5ba2\u6808\u7684\u540e\u9662\u3002 \u3000\u3000\u4e09\u66f4\u534a\u591c\uff0c\u4ed6\u7279\u5730\u5230\u8fd9\u5bb6\u5ba2\u6808\u4e2d\u6765\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u5df2\u5c06\u9ece\u660e\uff0c\u540e\u9662\u4e2d\u5374\u6709\u95f4\u623f\u8fd8\u4eae\u7740\u706f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u62cd\u95e8\uff0c\u5c4b\u91cc\u7acb\u523b\u6709\u4e86\u56de\u5e94\uff0c\u4e00\u4eba\u9053\uff1a\u201c\u662f\u5c0f\u674e\u63a2\u82b1\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u95e8\u5f00\u4e86\uff0c\u5f00\u95e8\u7684\u4eba\u7adf\u662f\u5415\u51e4\u5148\u3002 \u3000\u3000\u4ed6\u600e\u4f1a\u5728\u8fd9\u91cc\uff1f\u674e\u5bfb\u6b22\u600e\u4f1a\u77e5\u9053\u4ed6\u5728\u8fd9\u91cc\uff1f\u4e3a\u4ec0\u4e48\u6765\u627e\u4ed6\uff1f \u3000\u3000\u96be\u9053\u4ed6\u4eec\u4e24\u4eba\u8fd8\u6709\u4ec0\u4e48\u79d8\u5bc6\u7684\u7ea6\u5b9a\uff1f \u3000\u3000\u5415\u51e4\u5148\u5634\u89d2\u5e26\u7740\u79cd\u51b7\u6f20\u800c\u5947\u7279\u7684\u5fae\u7b11\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u674e\u63a2\u82b1\u679c\u7136\u662f\u4fe1\u4eba\uff01\u679c\u7136\u6765\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u58f0\u97f3\u63a5\u7740\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u53ea\u8981\u4ed6\u7b54\u5e94\uff0c\u5c31\u7edd\u4e0d\u4f1a\u5931\u4fe1\u3002\u201d \u3000\u3000\u7ad9\u5728\u5415\u51e4\u5148\u8eab\u540e\u7684\uff0c\u7adf\u662f\u94c3\u94c3\u3002 \u3000\u3000\u94c3\u94c3\u600e\u4f1a\u548c\u5415\u51e4\u5148\u5728\u4e00\u8d77\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7a76\u7adf\u7b54\u5e94\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u706f\u5149\u660f\u9ec4\uff0c\u674e\u5bfb\u6b22\u7684\u8138\u5374\u82cd\u767d\u5f97\u53ef\u6015\uff0c\u4ed6\u9ed8\u9ed8\u7684\u8d70\u8fdb\u5c4b\u5b50\uff0c\u7a81\u7136\u5411\u5415\u51e4\u5148\u6df1\u6df1\u4e00\u63d6\u9053\uff1a \u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u5415\u98ce\u5148\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8c22\u6211\uff0c\u56e0\u4e3a\u8fd9\u6839\u672c\u662f\u4ef6\u4ea4\u6613\uff0c\u8c01\u4e5f\u4e0d\u5fc5\u8c22\u51c6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6de1\u6de1\u7684\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u4ea4\u6613\uff0c\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u4f1a\u7b54\u5e94\u7684\uff0c\u6211\u5f53\u7136\u8981\u8c22\u4f60\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u4ef6\u5f88\u7279\u522b\u7684\u4ea4\u6613\u3002\u4f60\u8981\u94c3\u94c3\u5bf9\u6211\u8bf4\u65f6\uff0c\u6211\u7684\u786e\u5403\u4e86\u4e00\u60ca\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u4f1a\u8981\u5979\u89e3\u91ca\u5f97\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u5415\u98ce\u5148\u9053\uff1a\u201c\u5176\u5b9e\u7528\u4e0d\u7740\u89e3\u91ca\uff0c\u6211\u4e5f\u5df2\u4e86\u89e3\uff0c\u4f60\u8981\u6211\u6545\u610f\u8d25\u7ed9\u963f\u98de\uff0c\u53ea\u4e0d\u8fc7\u662f\u5e0c\u671b\u4ed6\u80fd \u56e0\u6b64\u800c\u632f\u4f5c\u8d77\u6765\uff0c\u83ab\u8981\u518d\u6d88\u6c89\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u7684\u786e\u662f\u8fd9\u610f\u601d\uff0c\u56e0\u4e3a\u4ed6\u7684\u786e\u503c\u5f97\u6211\u8fd9\u4e48\u6837\u505a\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u8fd9\u53ea\u56e0\u4f60\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u4f46\u6211\u5374\u4e0d\u662f\uff0c\u2026\u2026\u6211\u7b80\u76f4\u60f3\u4e0d\u5230\u4e16\u4e0a\u4f1a\u6709\u4eba\u4f1a\u5411\u6211 \u63d0\u51fa\u5982\u6b64\u8352\u8c2c\u7684\u8981\u6c42\u6765\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u4f60\u5374\u7ec8\u4e8e\u8fd8\u662f\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u76ee\u5149\u5200\u4e00\u822c\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u7b97\u51c6\u4e86\u6211\u4f1a\u7b54\u5e94\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u81f3\u5c11\u6709\u4e9b\u628a\u63e1\uff0c\u56e0\u4e3a\u6211\u5df1\u770b\u51fa\u4f60\u4e0d\u662f\u51e1\u4fd7\u7684\u4eba\uff0c\u4e5f\u53ea\u6709\u4f60\u8fd9 \u79cd\u975e\u51e1\u7684\u4eba\uff0c\u624d\u4f1a\u7b54\u5e94\u8fd9\u79cd\u975e\u51e1\u7684\u4e8b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u8fd8\u5728\u76ef\u7740\u4ed6\uff0c\u76ee\u5149\u5374\u6e10\u6e10\u548c\u7f13\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e5f\u7b97\u51c6\u4e86\u4ed6\u7edd\u4e0d\u4f1a\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u80dc\u4e86\u4e00\u5206\u5c31\u7edd\u4e0d\u4f1a\u518d\u51fa\u624b\u7684\u3002\u201d\uff1a \u3000\u3000\u5415\u51e4\u5148\u7a81\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u679c\u7136\u6ca1\u6709\u770b\u9519\u4ed6\uff0c\u4e5f\u6ca1\u6709\u770b\u9519\u6211\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u7b54\u5e94\u4f60\u8ba9\u4ed6\u80dc\u4e00\u62db\uff0c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4ed6\u82e5\u518d\u51fa\u624b\uff0c\u6211\u5c31\u8981\u4ed6\u7684\u547d \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u628a\u63e1\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d\u3002 \u3000\u3000\u4e24\u4eba\u53e3\u5149\u76f8\u89c6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u674e\u5bfb\u6b22\u7a81\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u8bb8\uff0c\u5c06\u6765\u5374\u672a\u5fc5\u3002\u201d \u3000\u3000\u5415\u98ce\u5148\u9053\uff1a\u201c\u6240\u4ee5\u6211\u672c\u5c31\u4e0d\u8be5\u7b54\u5e94\u4f60\u7684\uff0c\u8ba9\u4ed6\u6d3b\u7740\uff0c\u5bf9\u6211\u4e5f\u662f\u79cd\u5a01\u80c1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u6709\u4e9b\u4eba\u5c31\u559c\u6b22\u6709\u4eba\u5a01\u80c1\uff0c\u56e0\u4e3a\u5a01\u80c1\u4e5f\u662f\u79cd\u523a\u6fc0\uff0c\u6709\u523a\u6fc0\u624d\u6709\u8fdb\u6b65\uff0c\u4e00\u4e2a \u4eba\u82e5\u662f\u771f\u7684\u5df2\u5230\u2018\u56db\u987e\u65e0\u4eba\u2019\u7684\u5dc5\u5cf0\u5904\uff0c\u5c82\u975e\u4e5f\u5f88\u5bc2\u5bde\u65e0\u8da3\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u2026\u4f46\u6211\u7b54\u5e94\u4f60\uff0c\u5374\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u539f\u6545\u3002\u201d\u2019 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u6211\u7b54\u5e94\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u4ea4\u6362\u7684\u6761\u4ef6\u5f88\u4f18\u539a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u6ca1\u6709\u4f18\u539a\u7684\u6761\u4ef6\uff0c\u600e\u80fd\u548c\u4eba\u8c08\u4ea4\u6613\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u53ea\u8981\u6211\u7b54\u5e94\u4f60\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u4e5f\u4f1a\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u516d\u5341\u4e00\u7ae0\u3000\u627f\u8bfa",content:(0,s.jsx)(s.Fragment,{children:"\u5415\u51e4\u5148\u51b7\u50b2\u7684\u7738\u5b50\u91cc\uff0c\u7a81\u7136\u9732\u51fa\u4e00\u79cd\u5bc2\u5bde\u4e4b\u610f\u2014\u2014\u4e00\u4e2a\u4eba\u89c9\u5f97\u5bc2\u5bde\u7684\u65f6\u5019\uff0c\u5c31\u8868\u793a\u4ed6\u6b63 \u5728\u6e34\u671b\u7740\u53cb\u60c5\u3002\u600e\u5948\u771f\u631a\u7684\u53cb\u60c5\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u80fd\u5f97\u5230\u7684\u3002 \u3000\u3000\u5415\u51e4\u5148\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u80fd\u4e3a\u4ed6\u6b7b\uff0c\u4ed6\u4e5f\u4f1a\u4e3a\u4f60\u6b7b\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u58f0\u97f3\u66f4\u51b7\u9177\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u5df2\u7b97\u51c6\u4e86\u6211\u4e0d\u4f1a\u6740\u4f60\uff0c\u81f3\u5c11\u4e0d\u4f1a\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u6740\u4f60\uff0c\u662f \u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9ed8\u7136\u3002 \u3000\u3000\u6c89\u9ed8\uff0c\u901a\u5e38\u53ea\u4ee3\u8868\u4e24\u79cd\u610f\u601d\u2014\u2014\u9ed8\u8ba4\u548c\u6297\u8bae\u3002 \u3000\u3000\u5415\u51e4\u5148\u77aa\u7740\u4ed6\uff0c\u77b3\u5b54\u6e10\u6e10\u677e\u6563\uff0c\u7a81\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u7684\u786e\u4e0d\u4f1a\u6740\u4f60\u2026\u2026\u4f60\u53ef\u77e5\u9053\u662f \u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5415\u51e4\u5148\u5df2\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u6c38\u8fdc\u6b20\u7740\u6211\u7684\uff0c\u6c38\u8fdc\u89c9\u5f97\u6211\u5bf9\u4f60\u6709 \u6069\u2026\u2026\u201d \u3000\u3000\u4ed6\u7adf\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u82e5\u8981\u6740\u4f60\uff0c\u4ee5\u540e\u8fd8\u6709\u673a\u4f1a\uff0c\u4f46\u8fd9\u79cd\u673a\u4f1a\u4ee5\u540e\u53ea\u6015\u6c38\u8fdc\u4e0d\u4f1a \u518d\u6709\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u7684\u610f\u601d\uff0c\u662f\u4e0d\u662f\u60f3\u4ee5\u6b64\u6362\u5f97\u674e\u5bfb\u6b22\u7684\u53cb\u60c5\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7a81\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u673a\u4f1a\uff1f\u201d \u3000\u3000\u5415\u98ce\u5148\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u8fd8\u8981\u6c42\u4f60\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u77aa\u7740\u4ed6\uff0c\u5c31\u50cf\u662f\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u4f3c\u7684\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7b2c\u4e00\u6b21\u4ea4\u6613\u8fd8 \u672a\u4ed8\u51fa\u4ee3\u4ef7\uff0c\u5c31\u60f3\u8981\u6211\u505a\u7b2c\u4e8c\u4ef6\u4e8b\u4e86\uff1f\u8fd9\u7b97\u662f\u4ec0\u4e48\u6837\u7684\u4ea4\u6613\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4ea4\u6613\uff0c\u662f\u6211\u6c42\u4f60\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u8138\u8272\u867d\u5f88\u9eef\uff0c\u773c\u775b\u5374\u5728\u53d1\u7740\u5149\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u662f\u4ea4\u6613\u3002\u6211\u4e3a\u4f55\u8981\u7b54\u5e94\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u7740\uff0c\u4ed6\u7684\u7738\u5b50\u4e4e\u548c\u3001\u660e\u6717\uff0c\u800c\u771f\u8bda\u3002 \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u5415\u51e4\u5148\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u662f\u6211\u6c42\u4f60\u7684\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u53e3\u7b54\u5f97\u4e0d\u4f46\u5f88\u5999\uff0c\u751a\u81f3\u6709\u4e9b\u72c2\u5984\u3002 \u3000\u3000\u8fd9\u672c\u4e0d\u50cf\u674e\u5bfb\u6b22\u5e73\u65f6\u8bf4\u7684\u6d3b\u3002 \u3000\u3000\u4f46\u5415\u51e4\u5148\u5374\u6ca1\u6709\u751f\u6c14\uff0c\u5fc3\u91cc\u53cd\u800c\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u5947\u7279\u7684\u6e29\u6696\u4e4b\u610f\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4ece\u674e\u5bfb\u6b22\u7684\u7738 \u5b50\u91cc\u770b\u5230\u4e86\u4e00\u4e1d\u53cb\u60c5\u7684\u5149\u8f89\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u5c31\u662f\u552f\u4e00\u80fd\u9a71\u8d70\u4eba\u95f4\u5bc2\u5bde\u4e0e\u9ed1\u6697\u7684\u5149\u8f89\u3002 \u3000\u3000\u8fd9\u662f\u6c38\u6052\u7684\u5149\u8f89\uff0c\u53ea\u8981\u4eba\u6027\u4e0d\u706d\uff0c\u5c31\u6c38\u8fdc\u6709\u53cb\u60c5\u5b58\u5728\u3002 \u3000\u3000\u5415\u51e4\u5148\u5583\u5583\u9053\uff1a\u201c\u522b\u4eba\u90fd\u8bf4\u674e\u5bfb\u6b22\u4ece\u4e0d\u6c42\u4eba\uff0c\u4eca\u65e5\u5c45\u7136\u80af\u6765\u6c42\u6211\uff0c\u770b\u6765\u6211\u7684\u9762\u5b50\u5012\u4e0d\u5c0f \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u6211\u65e2\u5df2\u6b20\u4e86\u4f60\u7684\uff0c\u518d\u591a\u6b20\u4e9b\u53c8\u4f55\u59a8\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u53c8\u7b11\u4e86\uff0c\u8fd9\u6b21\u624d\u662f\u771f\u5fc3\u7684\u7b11\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u9053\uff1a\u201c\u6709\u4eba\u8bf4\uff0c\u5b66\u505a\u751f\u610f\u6700\u5927\u7684\u5b66\u95ee\u5c31\u662f\u8981\u61c2\u5f97\u5982\u4f55\u6b20\u5e10\uff0c\u770b\u6765\u4f60\u672c\u8be5\u53bb\u505a\u751f\u610f \u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u81f3\u5c11\u6211\u73b0\u5728\u8fd8\u672a\u60f3\u51fa\u62d2\u7edd\u7684\u6cd5\u5b50\uff0c\u4f60\u8d81\u6b64\u673a\u4f1a\uff0c\u8d76\u5feb\u8bf4\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u795e\u60c5\u53c8\u53d8\u5f97\u5f88\u6c89\u91cd\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u82e5\u5728\u95f4\u5e74\u524d\u9047\u89c1\u963f\u98de\uff0c\u6211\u7eb5\u4e0d\u6c42 \u4f60\uff0c\u4f60\u53ea\u6015\u4e5f\u8981\u8d25\u5728\u4ed6\u624b\u4e0b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u6c89\u9ed8\u7740\uff0c\u4e5f\u4e0d\u77e5\u662f\u9ed8\u8ba4\uff0c\u8fd8\u662f\u6297\u8bae\uff1f \u3000\u3000\u4ed6\u80fd\u4ee5\u6c89\u9ed8\u8868\u793a\u6297\u8bae\uff0c\u4e5f\u5df2\u5f88\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u82e5\u5728\u4e24\u5e74\u524d\u89c1\u5230\u8fc7\u4ed6\uff0c\u5c31\u4f1a\u53d1\u73b0\u90a3\u65f6\u7684\u4ed6\u548c\u73b0\u5728\u7b80\u76f4\u4e0d\u50cf\u662f\u540c\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u77ed\u77ed\u4e24\u5e74\uff0c\u4ed6\u600e\u4f1a\u6539\u53d8\u5f97\u5982\u6b64\u591a\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u4ed6\u4e0d\u5e78\u9047\u4e0a\u4e86\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u5973\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u81ea\u7136\u662f\u5973\u4eba\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u53ea\u6709\u5973\u4eba\u624d\u80fd\u6539\u53d8\u7537\u4eba\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u6539\u53d8\uff0c\u800c\u662f\u5815\u843d\uff0c\u4e00\u4e2a\u7537\u4eba\u4e3a\u4e86\u5973\u4eba\u800c\u5815\u843d\uff0c\u8fd9\u79cd\u4eba\u975e\u4f46\u4e0d\u503c \u5f97\u540c\u60c5\uff0c\u800c\u4e14\u611a\u8822\u5f97\u53ef\u7b11\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u606f\u7740\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e5f\u8bb8\u4e0d\u9519\uff0c\u53ea\u56e0\u4f60\u8fd8\u672a\u9047\u5230\u8fc7\u90a3\u6837\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u6211\u9047\u89c1\u4e86\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u82e5\u9047\u89c1\u4e86\u5979\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u8bb8\u53d8\u5f97\u548c\u963f\u98de\u4e00\u6837\u7684\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e5f\u662f\u4e2a\u6ca1\u89c1\u8fc7\u5973\u4eba\u7684\u5c0f\u4f19\u5b50\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e5f\u8bb8\u89c1\u8fc7\u5404\u5f0f\u5404\u6837\u7684\u5973\u4eba\uff0c\u53ef\u662f\u5979\u2026\u2026\u5979\u5374\u7edd\u5bf9\u548c\u522b\u7684\u5973\u4eba\u4e0d\u540c\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u66fe\u7ecf\u6709\u4e2a\u4eba\u5c06\u5979\u5f62\u5bb9\u5f97\u5f88\u597d\u2026\u2026\u5979\u770b\u6765\u5982\u4ed9\u5b50\uff0c\u5374\u4e13\u95e8\u5e26\u7537\u4eba\u4e0b\u5730\u72f1\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u76ee\u5149\u95ea\u52a8\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u5df2\u77e5\u9053\u4f60\u8bf4\u7684\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u4f60\u672c\u8be5\u731c\u5230\u7684\uff0c\u56e0\u4e3a\u4e16\u4e0a\u53ea\u6709\u5979\u8fd9\u4e48\u4e00\u4e2a\u5973\u4eba\uff0c\u4e5f\u5e78\u597d\u53ea\u6709\u4e00\u4e2a\uff0c\u5426\u5219 \u53ea\u6015\u5927\u591a\u6570\u7537\u4eba\u90fd\u5df2\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u6709\u5173\u8fd9\u4f4d\u2018\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\u2019\u7684\u4f20\u8bf4\uff0c\u6211\u7684\u786e\u5df2\u542c\u8fc7\u4e0d\u5c11\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u51dd\u6ce8\u7740\u81ea\u5df1\u7684\u6307\u5c16\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u963f\u98de\u73b0\u5728\u603b\u7b97\u5df2\u632f\u4f5c\u8d77\u6765\uff0c\u6211\u4e0d\u80fd\u773c\u770b\u7740\u4ed6\u518d\u6c89 \u6ca6\u4e0b\u53bb\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8981\u6211\u53bb\u6740\u4e86\u5979\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u963f\u98de\u6c38\u8fdc\u83ab\u8981\u518d\u89c1\u5230\u5979\uff0c\u56e0\u4e3a\u53ea\u8981\u4e00\u89c1\u5230\u5979\uff0c\u963f\u98de\u5c31\u65e0\u6cd5\u81ea \u62d4\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u672c\u53ef\u81ea\u5df1\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u53ea\u662f\u6211\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u963f\u98de\u82e5\u77e5\u9053\u4e86\uff0c\u5fc5\u5c06\u6068\u6211\u7ec8\u751f\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4ed6\u5e94\u8be5\u660e\u767d\u4f60\u8fd9\u662f\u4e3a\u4ed6\u597d\u3002\u201d\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u591a\u806a\u660e\u7684\u4eba\uff0c\u82e5\u662f\u9677 \u5165\u60c5\u611f\u800c\u4e0d\u80fd\u81ea\u62d4\uff0c\u90fd\u4f1a\u53d8\u6210\u5446\u5b50\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u7528\u624b\u6307\u8f7b\u6572\u7740\u4e0b\u5df4\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u627e\u522b\u4eba\u505a\u8fd9\u4ef6\u4e8b\uff1f\u4e3a\u4f55\u8981\u627e\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u522b\u4eba\u7eb5\u6709\u529b\u91cf\u80fd\u6740\u5979\uff0c\u89c1\u4e86\u5979\u4e4b\u540e\u53ea\u6015\u4e5f\u4e0d\u5fcd\u4e0b\u624b\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u5415\u51e4\u5148\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6211\u672c\u5c31\u5f88\u96be\u627e\u5230\u4e00\u4e2a\u6211\u53ef\u4ee5\u53bb\u6c42\u4ed6\u7684\u4eba\u3002\u201d \u3000\u3000\u4e24\u4eba\u53e3\u5149\u76f8\u9047\uff0c\u5415\u51e4\u5148\u5fc3\u91cc\u5ffd\u53c8\u5145\u6ee1\u4e86\u6e29\u6696\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u4ece\u674e\u5bfb\u6b22\u7684\u7738\u5b50\u91cc\u770b\u5230\u4e86\u4ed6\u7684\u5bc2\u5bde\u548c\u60b2\u75db\u3002 \u3000\u3000\u90a3\u662f\u82f1\u96c4\u552f\u6709\u7684\u5bc2\u5bde\u548c\u60b2\u75db\u3002 \u3000\u3000\u4e5f\u53ea\u6709\u82f1\u96c4\u624d\u80fd\u4e86\u89e3\u8fd9\u79cd\u5bc2\u5bde\u662f\u591a\u4e48\u51c4\u6e17\uff0c\u8fd9\u79cd\u60b2\u75db\u662f\u591a\u4e48\u6df1\u6c89\u3002 \u3000\u3000\u5415\u51e4\u5148\u7a81\u7136\u9053\uff1a\u201c\u5979\u5728\u54ea\u91cc\uff1f\u201d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u94c3\u94c3\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u94c3\u94c3\u5df2\u6655\u8fc7\u53bb\u5f88\u4e45\uff0c\u5230\u73b0\u5728\u5c45\u7136\u8fd8\u6ca1\u6709\u9192\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u4e86\u5979\u4e00\u773c\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u5979\u5e26\u4f60\u53bb\uff0c\u53ea\u6015\u5e76\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u7b11\u4e86\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u5012\u7528\u4e0d\u7740\u4f60\u62c5\u5fc3\uff0c\u6211\u81ea\u7136\u6709\u6cd5\u5b50\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9192\u6765\u65f6\uff0c\u674e\u5bfb\u6b22\u5df2\u7761\u7740\u3002 \u3000\u3000\u5728\u7761\u68a6\u4e2d\uff0c\u4ed6\u8fd8\u662f\u5728\u4e0d\u505c\u7684\u54b3\u55fd\u7740\uff0c\u6bcf\u5f53\u54b3\u5f97\u5267\u70c8\u65f6\uff0c\u4ed6\u5168\u8eab\u90fd\u56e0\u75db\u82e6\u800c\u626d\u66f2\u75c9\u631b\u2026\u2026 \u3000\u3000\u9633\u5149\u4ece\u7a97\u5916\u659c\u659c\u7167\u8fdb\u6765\u3002 \u3000\u3000\u963f\u98de\u8fd9\u624d\u53d1\u73b0\u4ed6\u5934\u4e0a\u7684\u767d\u53d1\uff0c\u548c\u8138\u4e0a\u7684\u76b1\u7eb9\u90fd\u66f4\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u4e00\u53cc\u773c\u775b\u8fd8\u662f\u5e74\u8f7b\u7684\u3002 \u3000\u3000\u6bcf\u5f53\u4ed6\u95ed\u4e0a\u773c\u775b\u65f6\uff0c\u5c31\u4f1a\u663e\u5f97\u5f88\u6194\u60b4\u3001\u5f88\u82cd\u8001\uff0c\u751a\u81f3\u5f88\u8870\u5f31\u3002 \u3000\u3000\u4ed6\u7684\u8863\u886b\u5df2\u5f88\u9648\u65e7\u6b8b\u7834\uff0c\u5df2\u6709\u591a\u65e5\u672a\u6d17\u6da4\u3002 \u3000\u3000\u53c8\u6709\u8c01\u80fd\u60f3\u5f97\u5230\u5728\u5982\u6b64\u8870\u5f31\uff0c\u5982\u6b64\u50f5\u507b\u7684\u8eaf\u58f3\u91cc\uff0c\u7adf\u85cf\u7740\u90a3\u4e48\u575a\u5f3a\u7684\u610f\u5fd7\uff0c\u90a3\u4e48\u9ad8\u5c1a\u7684 \u4eba\u683c\uff0c\u90a3\u4e48\u4f1f\u5927\u7684\u7075\u9b42\uff1f \u3000\u3000\u963f\u98de\u77a7\u7740\u4ed6\uff0c\u70ed\u6cea\u5df2\u76c8\u7736\u3002 \u3000\u3000\u4ed6\u6d3b\u7740\uff0c\u672c\u5c31\u662f\u5728\u5fcd\u53d7\u7740\u714e\u71ac\u2014\u2014\u5404\u5f0f\u5404\u6837\u4e0d\u540c\u8d2d\u714e\u71ac\uff0c\u6298\u78e8\uff0c\u6253\u51fb\u3002 \u3000\u3000\u4ed6\u4f46\u5374\u8fd8\u662f\u6ca1\u6709\u5012\u4e0b\u53bb\uff01\u4e5f\u5e76\u6ca1\u6709\u89c9\u5f97\u751f\u547d\u662f\u51b7\u9177\u9ed1\u6697\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u53ea\u8981\u6709\u4ed6\u5728\uff0c\u5c31\u6709\u6e29\u6696\uff0c\u5c31\u6709\u5149\u660e\u3002 \u3000\u3000\u4ed6\u5e26\u7ed9\u522b\u4eba\u7684\u6c38\u8fdc\u90fd\u662f\u5feb\u4e50\uff0c\u5374\u5c06\u75db\u82e6\u7559\u7ed9\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u963f\u98de\u7684\u70ed\u6cea\u5df2\u593a\u7736\u800c\u51fa\uff0c\u6d41\u4e0b\u9762\u988a\u2026\u2026 \u3000\u3000\u5b6a\u5bfb\u6b22\u8fd8\u662f\u7761\u7684\u5f88\u6c89\u3002 \u3000\u3000\u7761\u7720\uff0c\u5728\u4ed6\u8bf4\u6765\uff0c\u51e0\u4e4e\u4e5f\u53d8\u6210\u4e86\u4ef6\u5f88\u5962\u4f88\u7684\u4e8b\u3002 \u3000\u3000\u963f\u98de\u867d\u7136\u6025\u7740\u60f3\u56de\u53bb\uff0c\u6025\u7740\u60f3\u770b\u5230\u90a3\u6625\u82b1\u822c\u7684\u7b11\u8138\uff0c\u4f46\u8fd8\u662f\u4e0d\u5fcd\u60ca\u52a8\u4ed6\uff0c\u6084\u6084\u63a9\u8d77\u95e8\uff0c \u4fcf\u4fcf\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd8\u5f88\u65e9\uff0c\u9633\u5149\u521a\u7167\u4e0a\u5c4b\u9876\uff0c\u8d76\u8def\u7684\u4eba\u90fd\u5df2\u8d70\u4e86\uff0c\u6240\u4ee5\u9662\u5b50\u91cc\u5f88\u9759\uff0c\u53ea\u5269\u4e0b\u4e00\u682a\u987d\u5f3a\u7684\u68a7 \u6850\uff0c\u5728\u665a\u79cb\u7684\u5bd2\u98ce\u4e2d\u50b2\u7136\u72ec\u7acb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c82\u975e\u4e5f\u6b63\u5982\u8fd9\u68a7\u6850\u4e00\u6837\uff0c\u867d\u7136\u660e\u77e5\u79cb\u5df2\u5c06\u5c3d\uff0c\u51ac\u5df2\u5c06\u81f3\uff0c\u4f46\u4e0d\u5230\u6700\u540e\u5173\u5934\uff0c\u4ed6\u4eec \u662f\u7edd\u4e0d\u4f1a\u5c48\u670d\u7684\u3002 \u3000\u3000\u963f\u98de\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u6162\u6f2b\u7684\u7a7f\u8fc7\u9662\u5b50\u3002 \u3000\u3000\u68a7\u6850\u7684\u53f6\u5b50\uff0c\u5df2\u5f00\u59cb\u51cb\u96f6\uff0c\u4e00\u7247\u7247\u98d8\u8fc7\u4ed6\u773c\u524d\uff0c\u98d8\u843d\u5728\u4ed6\u8eab\u4e0a\u2026\u2026 \u3000\u3000\u7089\u706b\u72b9\u672a\u7184\uff0c\u8c46\u6d46\uff0c\u6162\u6162\u7684\u5544\u7740\u3002 \u3000\u3000\u4ed6\u5403\u5f97\u4e00\u5411\u4e0d\u5feb\uff0c\u6162\u6162\u7684\u8ba9\u8fd9\u5fae\u6e29\u7684\u8c46\u6d46\u81ea\u820c\u6d41\u5165\u54bd\u5589\uff0c\u6d41\u5165\u80c3\u91cc\u2014\u2014\u4e00\u4e2a\u4eba\u7684\u80c3\u82e5\u5145 \u5b9e\uff0c\u6574\u4e2a\u4eba\u90fd\u4eff\u4f5b\u5145\u5b9e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u559c\u6b22\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u81ea\u534a\u591c\u5c31\u8d77\u6765\u5fd9\u788c\u7684\u5e97\u4f19\uff0c\u5230\u73b0\u5728\u624d\u7b97\u7a7a\u95f2\u4e86\u4e0b\u6765\uff0c\u6b63\u5750\u5728\u7089\u706b\u7684\u4f59\u718f\u65c1\uff0c\u5728\u6162\u6162\u7684\u559d \u7740\u9152\u3002 \u3000\u3000\u4e0b\u9152\u7684\u867d\u53ea\u4e0d\u8fc7\u662f\u6839\u5df2\u51b7\u4e86\u7684\u201c\u6cb9\u70b8\u70e9\u201d\uff0c\u559d\u7684\u867d\u53ea\u4e0d\u8fc7\u662f\u7c97\u52a3\u7684\u70e7\u5200\u5b50\uff0c\u4f46\u770b\u4ed6\u7684\u8868 \u60c5\uff0c\u5374\u50cf\u662f\u6b63\u5728\u4eab\u53d7\u7740\u4e16\u95f4\u6700\u4e30\u7f8e\u7684\u9152\u98df\u3002 \u3000\u3000\u4ed6\u663e\u7136\u5f88\u5feb\u4e50\uff0c\u56e0\u4e3a\u4ed6\u5df2\u5f88\u6ee1\u8db3\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u552f\u6709\u80fd\u6ee1\u8db3\u7684\u4eba\uff0c\u624d\u80fd\u9886\u7565\u5230\u771f\u6b63\u7684\u5feb\u4e50\u3002 \u3000\u3000\u963f\u98de\u5bf9\u8fd9\u79cd\u4eba\u4e00\u5411\u5f88\u7fa1\u6155\uff0c\u5fc3\u91cc\u5b9e\u5728\u4e5f\u60f3\u80fd\u8fc7\u53bb\u559d\u4e24\u676f\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u63a7\u5236\u7740\u81ea\u5df1\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\uff0c\u4eca\u5929\u6211\u5c31\u80fd\u89c1\u5230\u5979\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0d\u613f\u5979\u95fb\u5230\u81ea\u5df1\u5634\u91cc\u6709\u9152\u6c14\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u672c\u5c31\u662f\u4e3a\u4e86\u522b\u4eba\u800c\u8bdd\u97f3\u7684\u2014\u2014\u6709\u4e9b\u662f\u4e3a\u4e86\u81ea\u5df1\u6240\u7231\u7684\u4eba\uff0c\u4e5f\u6709\u4e9b\u662f\u4e3a\u4e86 \u81ea\u5df1\u6240\u6068\u7684\u4eba\u2014\u2014\u8fd9\u4e24\u79cd\u4eba\u90fd\u540c\u6837\u75db\u82e6\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u771f\u6b63\u5feb\u4e50\u7684\u4eba\u672c\u5c31\u4e0d\u591a\u3002 \u3000\u3000\u98ce\u5f88\u5927\uff0c\u7802\u4e0a\u5728\u98ce\u4e2d\u98de\u821e\uff0c\u8def\u4e0a\u7684\u884c\u4eba\u5f88\u5be5\u843d\u3002 \u3000\u3000\u963f\u98de\u62ac\u8d77\u5934\uff0c\u76ee\u5149\u79fb\u5411\u95e8\u5916\u65f6\uff0c\u6b63\u6709\u4e24\u4e2a\u4eba\u81ea\u95e8\u5916\u8d70\u8fc7\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u884c\u8272\u5374\u4f3c\u5f88\u5306\u5fd9\uff0c\u53ea\u7ba1\u4f4e\u7740\u5934\u5f80\u524d\u8d76\u8def\uff0c\u8fde\u70ed\u8c46\u6d46\u7684\u9999\u6c14\u90fd\u672a\u80fd\u5f15 \u52a8\u4ed6\u4eec\u8f6c\u5934\u6765\u77a7\u4e00\u773c\u3002 \u3000\u3000\u524d\u9762\u8d70\u7684\u662f\u4e2a\u8eab\u5f62\u4f5d\u507b\uff0c\u767d\u53d1\u82cd\u82cd\u7684\u8001\u5934\u5b50\uff0c\u624b\u91cc\u63d0\u7740\u7ba1\u65f1\u70df\uff0c\u8eab\u4e0a\u7684\u84dd\u5e03\u886b\u5df2\u6d17\u5f97\u53d1 \u767d\u3002 \u3000\u3000\u540e\u9762\u8ddf\u7684\u662f\u4e2a\u5c0f\u59d1\u5a18\uff0c\u773c\u775b\u5f88\u5927\uff0c\u8fab\u5b50\u5f88\u957f\u3002 \u3000\u3000\u963f\u98de\u8ba4\u5f97\u8fd9\u4e24\u4eba\u6b63\u662f\u4e24\u5e74\u524d\u4ed6\u66fe\u89c1\u8fc7\u4e00\u6b21\u7684\u201c\u8bf4\u4e66\u5148\u751f\u201d\u548c\u5b59\u5973\uff0c\u4ed6\u8fd8\u8bb0\u5f97\u8fd9\u4e24\u4eba\u59d3\u5b59 \u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u5168\u6ca1\u6709\u77a7\u89c1\u963f\u98de\uff0c\u5f88\u5feb\u5c31\u4ece\u95e8\u53e3\u8d70\u8fc7\u3002 \u3000\u3000\u4e00\u4ed6\u95e8\u7740\u662f\u89c1\u5230\u4e86\u963f\u98de\uff0c\u6240\u6709\u7684\u4e00\u5207\u4e8b\u4e5f\u8bb8\u90fd\u4f1a\u5b8c\u5168\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u963f\u98de\u559d\u5b8c\u4e86\u8c46\u6d46\uff0c\u518d\u62ac\u8d77\u5934\uff0c\u53c8\u77a7\u89c1\u4e00\u4e2a\u4eba\u81ea\u95e8\u5916\u8d70\u8fc7\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u5f88\u9ad8\uff0c\u9ec4\u888d\uff0c\u6597\u7b20\uff0c\u7b20\u6a90\u538b\u5f97\u5f88\u4f4e\uff0c\u8d70\u8def\u7684\u59ff\u52bf\u5f88\u5947\u7279\uff0c\u4e5f\u6ca1\u6709\u8f6c\u8fc7\u5934\u6765\u77a7\u4e00 \u773c\uff0c\u884c\u8272\u4eff\u4f5b\u4e5f\u5f88\u5306\u5fd9\u3002 \u3000\u3000\u963f\u98de\u7684\u5fc3\u8df3\u7a81\u7136\u5feb\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\uff01 \u3000\u3000\u8346\u65e0\u547d\u7684\u773c\u775b\u4e00\u5411\u76ef\u4f4f\u524d\u9762\uff1a\u4eff\u4f5b\u6b63\u5728\u8ffd\u8e2a\u65b9\u624d\u8d70\u8fc7\u7684\u90a3\u201c\u8bf4\u4e66\u5148\u751f\u201d\uff0c\u5e76\u6ca1\u6709\u53d1\u89c9\u963f \u98de\u5c31\u5750\u5728\u8def\u65c1\u7684\u5c0f\u5e97\u91cc\u3002 \u3000\u3000\u963f\u98de\u5374\u770b\u5230\u4e86\u4ed6\uff0c\u770b\u5230\u4ed6\u8170\u5e26\u4e0a\u63d2\u7740\u7684\u5251\u3002\u5374\u6ca1\u6709\u770b\u5230\u4ed6\u90a3\u6761\u65ad\u81c2\u4e00\u4e00\u7528\u5e03\u5e26\u7cfb\u7740\u7684\u65ad \u81c2\u3002 \u3000\u3000\u53ea\u8981\u770b\u5230\u8fd9\u67c4\u5251\uff0c\u963f\u98de\u7684\u773c\u775b\u91cc\u5c31\u518d\u4e5f\u5bb9\u4e0d\u4e0b\u522b\u7684\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u67c4\u5251\uff0c\u4ee4\u4ed6\u7b2c\u4e00\u6b21\u5c1d\u5230\u5931\u8d25\u548c\u5c48\u8fb1\u7684\u6ecb\u5473\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u67c4\u5251\uff0c\u4ee4\u4ed6\u51e0\u4e4e\u6c38\u8fdc\u6c89\u6ca6\u4e0b\u201c\u53bb\u3002 \u3000\u3000\u963f\u98de\u7684\u62f3\u5df2\u7d27\u63e1\uff0c\u638c\u5fc3\u7684\u4f24\u53e3\u53c8\u7834\u88c2\uff0c\u9c9c\u8840\u6d41\u51fa\uff0c\u75bc\u75db\u5374\u81ea\u638c\u5fc3\u4f20\u81f3\u5fc3\u5e95\uff0c\u4ed6\u5168\u8eab\u7684\u808c \u8089\u7acb\u523b\u5168\u90fd\u7d27\u5f20\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5df2\u5fd8\u4e86\u8346\u65e0\u547d\u7684\u65ad\u81c2\u3002 \u3000\u3000\u4ed6\u4e00\u5fc3\u53ea\u76fc\u671b\u80fd\u548c\u8346\u65e0\u547d\u518d\u51b3\u9ad8\u4e0b\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u522b\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e5f\u5f88\u5feb\u5c31\u4ece\u95e8\u53e3\u8d70\u8fc7\u3002 \u3000\u3000\u963f\u98de\u7f13\u7f13\u7ad9\u8d77\uff0c\u624b\u63e1\u5f97\u66f4\u5267\u70c8\u3002 \u3000\u3000\u75db\u82e6\u8d8a\u5267\u70c8\uff0c\u4ed6\u7684\u611f\u89c9\u5c31\u8d8a\u654f\u9510\u3002 \u3000\u3000\u5750\u5728\u95e8\u53e3\u7684\u4f19\u8ba1\u7a81\u7136\u611f\u89c9\u5230\u4e00\u9635\u65e0\u6cd5\u5f62\u5bb9\u7684\u5bd2\u610f\u88ad\u6765\uff0c\u8f6c\u8fc7\u5934\uff0c\u5c31\u77a7\u89c1\u4e86\u963f\u98de\u7684\u773c\u775b\u2014 \u2014 \u3000\u3000\u4e00\u53cc\u706b\u7130\u822c\u70bd\u70ed\u7684\u773c\u775b\uff0c\u5374\u4ee4\u4eba\u81ea\u5fc3\u5e95\u53d1\u51b7\u3002\u201c\u9557\u201d\u7684\uff0c\u5e97\u4f19\u624b\u91cc\u7684\u9152\u676f\u8dcc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u9152\u676f\u8fd8\u672a\u8dcc\u5728\u5730\u4e0a\uff0c\u963f\u98de\u7a81\u7136\u4f38\u624b\uff0c\u5df2\u6284\u5728\u624b\u91cc\u3002 \u3000\u3000\u8c01\u4e5f\u77a7\u4e0d\u6e05\u4ed6\u5982\u4f55\u5c06\u8fd9\u9152\u676f\u63a5\u4f4f\u7684\u3002 \u3000\u3000\u5e97\u4f19\u6574\u4e2a\u90fd\u88ab\u5413\u5446\u4e86\u3002 \u3000\u3000\u963f\u98de\u9992\u6162\u7684\u5c06\u9152\u676f\u653e\u5728\u4ed6\u9762\u524d\u7684\u684c\u4e0a\uff0c\u5012\u4e86\u676f\u9152\uff0c\u81ea\u5df1\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5ffd\u7136\u5145\u6ee1\u4e86\u4fe1\u5fc3\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u95e8\u5916\u53c8\u6709\u4e2a\u4eba\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4eba\u4e5f\u662f\u9ec4\u886b\uff0c\u6597\u7b20\u7b20\u6a90\u4e5f\u538b\u5f97\u5f88\u4f4e\uff0c\u8d70\u8def\u7684\u59ff\u6001\u4e5f\u5f88\u5947\u7279\u4e5f\u82cd\u767d\u7684\u8138\uff0c\u5728\u6597\u7b20\u7684\u9634\u5f71 \u4e0b\u770b\u6765\uff0c\u5c31\u5b9b\u5982\u662f\u7528\u7070\u77f3\u96d5\u6210\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u98de\uff01 \u3000\u3000\u963f\u98de\u5e76\u4e0d\u8ba4\u5f97\u4e0a\u5bab\u98de\uff0c\u4f46\u4e00\u773c\u5c31\u770b\u51fa\u8fd9\u4eba\u5fc5\u5b9a\u548c\u8346\u65e0\u547d\u6709\u79cd\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u800c\u4e14\u663e\u7136\u6b63\u5728 \u8ffd\u8e2a\u7740\u8346\u5143\u547d\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u8eab\u6750\u867d\u6bd4\u8346\u65e0\u547d\u77ee\u4e9b\uff0c\u5e74\u7eaa\u4e5f\u8f83\u8f7b\uff0c\u4f46\u90a3\u79cd\u51b7\u9177\u7684\u795e\u60c5\uff0c\u90a3\u79cd\u8d70\u8def\u7684\u59ff\u6001\u5c31\u597d\u50cf \u662f\u8346\u65e0\u547d\u7684\u5144\u5f1f\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u4e5f\u5728\u6697\u4e2d\u8ffd\u8e2a\u8346\u65e0\u547d\u5462\uff1f \u3000\u3000\u8fd9\u5730\u65b9\u672c\u5c31\u5f88\u8352\u50fb\uff0c\u518d\u8f6c\u8fc7\u8fd9\u6761\u8857\uff0c\u56db\u4e0b\u66f4\u770b\u4e0d\u5230\u4eba\u8e2a\u3002 \u3000\u3000\u963f\u98de\u8d70\u5f97\u5f88\u5feb\uff0c\u59cb\u7ec8\u548c\u4e0a\u5b98\u98de\u4fdd\u6301\u7740\u4e00\u6bb5\u8ddd\u79bb\u3002 \u3000\u3000\u524d\u9762\u8d70\u7684\u201c\u8bf4\u4e66\u5148\u751f\u201d\u65e9\u5df2\u77a7\u4e0d\u89c1\u4e86\uff0c\u8346\u65e0\u547d\u4e5f\u53ea\u5269\u4e0b\u4e00\u6761\u6de1\u9ec4\u8272\u7684\u4eba\u5f71\uff0c\u4f46\u4e0a\u5b98\u98de\u4e5f \u8fd8\u662f\u8d70\u5f97\u5f88\u6162\uff0c\u5e76\u4e0d\u7740\u6025\u3002 \u3000\u3000\u963f\u98de\u53d1\u73b0\u8fd9\u5c11\u5e74\u4e5f\u5f88\u61c2\u5f97\u201c\u8ffd\u8e2a\u201d\u7684\u8bc0\u7a8d\u3002 \u3000\u3000\u8981\u8ffd\u8e2a\u4e00\u4e2a\u4eba\u800c\u4e0d\u88ab\u53d1\u89c9\uff0c\u5c31\u4e0d\u80fd\u6025\u8e81\uff0c\u5c31\u8981\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u524d\u9762\u6709\u5ea7\u571f\u5c71\uff0c\u8346\u65e0\u547d\u5df2\u8f6c\u8fc7\u5c71\u5773\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7684\u811a\u6b65\u7a81\u7136\u52a0\u5feb\uff0c\u4f3c\u4e4e\u60f3\u5728\u5c71\u540e\u8ffd\u4e0a\u8346\u65e0\u547d\u3002 \u3000\u3000\u7b49\u4ed6\u7684\u4eba\u4e5f\u6d88\u5931\u5728\u5c71\u540e\uff0c\u963f\u98de\u5c31\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u51b2\u4e0a\u4e0a\u5c71\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5728\u5c71\u4e0a\u4e00\u5b9a\u53ef\u4ee5\u770b\u5230\u4e00\u4e9b\u6709\u8da3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u679c\u7136\u6ca1\u6709\u5931\u671b\u3002 \u3000\u3000\u8346\u65e0\u547d\u4ece\u672a\u611f\u89c9\u5230\u6050\u60e7\u2014\u2014\u4e00\u4e2a\u4eba\u82e5\u8fde\u6b7b\u90fd\u4e0d\u6015\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u76ee\u4e2d\u7adf\u5e26\u7740\u79cd\u6050\u60e7\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u6015\u7684\u662f\u4ec0\u4e48\uff1f \u7b2c\u516d\u5341\u4e8c\u7ae0\u3000\u7edd\u62db>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u4e8c\u7ae0\u3000\u7edd\u62db \u3000\u3000\u8f6c\u8fc7\u5c71\uff0c\u666f\u8272\u66f4\u8352\u51c9\uff0c\u79cb\u98ce\u8427\u6740\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u624b\uff0c\u7a81\u7136\u6309\u4e0a\u4e86\u5251\u67c4\u2014\u2014\u4f46\u8fd9\u662f\u53f3\u624b\uff0c\u5e76\u4e0d\u662f\u4f7f\u5251\u7684\u624b\uff0c\u4ed6\u7684\u5251\u5728\u8fd9\u53ea\u624b\u91cc\uff0c \u5df2\u4e0d\u80fd\u7b97\u662f\u6740\u4eba\u7684\u5229\u5668\uff01 \u3000\u3000\u4ed6\u7684\u624b\u63e1\u8d77\uff0c\u53c8\u653e\u4e0b\u3002 \u3000\u3000\u4ed6\u7684\u811a\u6b65\u4e5f\u505c\u4e0b\uff0c\u4eff\u4f5b\u77e5\u9053\u4ed6\u7684\u8def\u5df2\u8d70\u5230\u5c3d\u5934\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u542c\u5230\u4e86\u4e0a\u5b98\u98de\u7684\u51b7\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u5df2\u5230\u4e86\u4ed6\u8eab\u540e\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u53ef\u4ee5\u4e0d\u5fc5\u518d\u505a\u620f\u4e86\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u56de\u8eab\uff0c\u6b7b\u7070\u8272\u7684\u773c\u775b\u53c8\u53d8\u5f97\u5168\u65e0\u8868\u60c5\uff0c\u6f20\u7136\u51dd\u671b\u7740\u4e0a\u5b98\u98de\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u624d\u4e00 \u5b57\u5b57\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u5728\u505a\u620f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u505a\u620f\uff0c\u4f60\u6545\u610f\u8ddf\u8e2a\u5b59\u8001\u513f\uff0c\u5c31\u662f\u5728\u505a\u620f\uff0c\u56e0\u4e3a\u4f60\u6839\u672c\u6ca1\u6709\u8ffd\u8e2a\u4ed6\u4eec \u7684\u5fc5\u8981\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u8ffd\u8e2a\u4ed6\u4eec\uff0c\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4e3a\u7684\u662f\u6211\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4f60\u65e9\u5df2\u77e5\u9053\u6211\u5728\u76ef\u7740\u4f60\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u4f60\u5e76\u4e0d\u9ad8\u660e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u867d\u4e0d\u9ad8\u660e\uff0c\u73b0\u5728\u5df2\u662f\u80fd\u6740\u4f60\uff0c\u4f60\u5f53\u7136\u4e5f\u65e9\u5c31\u77e5\u9053\u6211\u8981\u6740\u4f60\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u786e\u65e9\u5df2\u77e5\u9053\uff0c\u6240\u4ee5\u4ed6\u5e76\u672a\u611f\u89c9\u5230\u60ca\u5f02\u3002 \u3000\u3000\u60ca\u5f02\u7684\u662f\u963f\u98de\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u672c\u662f\u540c\u4e00\u95e8\u4e0b\uff0c\u4e3a\u4f55\u8981\u81ea\u76f8\u6b8b\u6740\uff1f \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u6211\u5df2\u60f3\u6740\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u62d2\u7edd\u56de\u7b54\u2014\u2014\u4ed6\u4e00\u5411\u53ea\u95ee\uff0c\u4e0d\u7b54\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7a81\u7136\u6fc0\u52a8\u8d77\u6765\uff0c\u76ee\u4e2d\u66f4\u5145\u6ee1\u4e86\u6028\u6bd2\u4e4b\u8272\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u82e5\u662f\u6ca1\u6709\u4f60\uff0c\u6211\u5c31\u53ef \u6d3b\u5f97\u66f4\u597d\u4e9b\uff0c\u4f60\u4e0d\u4f46\u62a2\u8d70\u4e86\u6211\u7684\u5730\u4f4d\uff0c\u4e5f\u62a2\u8d70\u4e86\u6211\u7684\u7236\u4eb2\uff0c\u81ea\u4ece\u4f60\u6765\u4e86\u4e4b\u540e\uff0c\u672c\u6765\u5c5e\u4e8e\u6211\u7684 \u4e00\u5207\uff0c\u5c31\u5ffd\u7136\u90fd\u53d8\u6210\u4e86\u4f60\u7684\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e5f\u53ea\u602a\u4f60\u81ea\u5df1\uff0c\u4f60\u4e00\u5411\u6bd4\u4e0d\u4e0a\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u54ac\u7740\u7259\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u4e5f\u660e\u767d\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u7f18\u6545\uff0c\u90a3\u53ea\u56e0\u2026\u2026\u201d \u3000\u3000\u4ed6\u867d\u7136\u5728\u6781\u529b\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u7206\u53d1\u4e86\u8d77\u6765\uff0c\u7a81\u7136\u5927\u543c\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4f60\u662f\u6211\u7236 \u4eb2\u7684\u79c1\u751f\u5b50\uff0c\u6211\u6bcd\u4eb2\u5c31\u662f\u88ab\u4f60\u7684\u6bcd\u4eb2\u6c14\u6b7b\u7684\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u775b\u7a81\u7136\u6536\u7f29\uff0c\u53d8\u5f97\u5c31\u50cf\u662f\u4e24\u6ef4\u8840\u3002 \u3000\u3000\u4e24\u6ef4\u65e9\u5df2\u5e72\u67af\uff0c\u53d8\u8272\u4e86\u7684\u8840\u3002 \u3000\u3000\u5728\u5c71\u4e0a\u7684\u963f\u98de\uff0c\u76ee\u4e2d\u7a81\u4e5f\u9732\u51fa\u4e86\u6781\u5f3a\u70c8\u7684\u75db\u82e6\u4e4b\u8272\uff0c\u7adf\u4eff\u4f5b\u548c\u8346\u65e0\u547d\u6709\u540c\u6837\u7684\u75db\u82e6\uff0c\u800c \u4e14\u75db\u82e6\u5f97\u6bd4\u8346\u65e0\u547d\u66f4\u6df1\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u4f60\u4eec\u4e00\u76f4\u7792\u7740\u6211\uff0c\u4ee5\u4e3a\u6211\u771f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u201c\u4f60\u4eec\u201d\u6307\u7684\u5c31\u662f\u8346\u65e0\u547d\u548c\u4ed6\u7684\u7236\u4eb2\u3002 \u3000\u3000\u8fd9\u4e24\u5b57\u81ea\u4ed6\u5634\u91cc\u8bf4\u51fa\u6765\uff0c\u5e76\u6ca1\u6709\u4f24\u5bb3\u5230\u522b\u4eba\uff0c\u4f24\u5bb3\u7684\u53ea\u662f\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u66f4\u75db\u820d\uff0c\u6240\u4ee5\u795e\u60c5\u53cd\u800c\u663e\u5f97\u5e73\u9759\u4e86\u4e9b\uff0c\u51b7\u7b11\u7740\u63a5\u9053\uff1a\u201c\u5176\u5b9e\u81ea\u4ece\u4f60\u6765\u7684\u90a3\u4e00\u5929\uff0c\u6211\u5df2 \u7ecf\u77e5\u9053\u4e86\uff0c\u81ea\u4ece\u90a3\u4e00\u5929\uff0c\u6211\u5c31\u5728\u7b49\u7740\u673a\u4f1a\u6740\u4f60\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7684\u673a\u4f1a\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u90a3\u65f6\u6211\u7eb5\u6709\u673a\u4f1a\uff0c\u4e5f\u672a\u5fc5\u4f1a\u4e0b\u624b\uff0c\u56e0\u4e3a\u90a3\u65f6\u4f60\u8fd8\u6709\u5229\u7528\u7684\u4ef7\u503c\uff0c\u4f46\u73b0\u5728\u5374 \u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u5728\u6211\u7236\u4eb2\u773c\u4e2d\uff0c\u5c31\u50cf\u662f\u4e00\u628a\u5200\uff0c\u6740\u4eba\u7684\u5200\uff0c\u6211\u82e5\u6bc1\u4e86\u4ed6\u7684\u5200 \uff0c\u4ed6\u7edd\u4e0d\u4f1a\u9976\u6211\uff0c\u4f46\u73b0\u5728\uff0c\u4f60\u5df1\u53ea\u4e0d\u8fc7\u662f\u5757\u5e9f\u94c1\uff0c\u4f60\u7684\u751f\u6b7b\uff0c\u4ed6\u5df2\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7adf\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u751f\u6b7b\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u672a \u653e\u5728\u5fc3\u4e0a\uff0c\u53c8\u4f55\u51b5\u4ed6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u8fd9\u8bdd\u4f60\u4e5f\u8bb8\u80fd\u9a97\u5f97\u8fc7\u522b\u4eba\uff0c\u9a97\u5f97\u8fc7\u4f60\u81ea\u5df1\uff0c\u5374\u9a97\u4e0d\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u9a97\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u4e0d\u6015\u6b7b\uff0c\u4e3a\u4f55\u8fd8\u8981\u62d6\u5ef6\u9003\u907f\uff1f\u201d \u3000\u3000\u8346\u5143\u547d\u9053\uff1a\u201c\u62d6\u5ef6\uff1f\u9003\u907f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4f60\u6545\u610f\u4f5c\u51fa\u8ffd\u8e2a\u5b59\u8001\u5934\u7684\u59ff\u6001\uff0c\u5c31\u662f\u5728\u62d6\u5ef6\uff0c\u5728\u9003\u907f\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4f60\u8ffd\u8e2a\u7684\u82e5\u4e0d\u662f\u5b59\u8001\u5934\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ba9\u4f60\u5148\u8ffd\u51fa\u4e2a\u7ed3\u679c\u6765\uff0c\u770b\u4f60\u662f\u60f3\u8ffd\u51fa\u4ed6 \u7684\u4e0b\u843d\uff0c\u8fd8\u662f\u5728\u7b49\u673a\u4f1a\u6740\u4ed6\uff0c\u7136\u540e\u6211\u624d\u4f1a\u5bf9\u4f60\u4e0b\u624b\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\uff0c\u63a5\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u9009\u9519\u4e86\u4eba\uff0c\u56e0\u4e3a\u4f60\u6839\u672c\u8ffd\u4e0d\u51fa\u4ed6\u7684\u4e0b\u843d\uff0c\u66f4\u6740\u4e0d\u4e86\u4ed6\uff0c\u4f60 \u6839\u672c\u4e0d\u914d\u8ffd\u8e2a\u4ed6\uff0c\u6839\u672c\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u4ed6\u7b11\u5bb9\u4e0d\u4f46\u5f88\u5947\u7279\uff0c\u800c\u4e14\u8fd8\u4eff\u4f5b\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u8ba5\u6d88\u4e4b\u610f\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u5e76\u6ca1\u6709\u770b\u51fa\u6765\uff0c\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u7684\u8ffd\u8e2a\uff0c\u53ea\u4e0d\u8fc7\u662f\u79cd\u70df\u5e55\uff0c\u8981\u6211\u4e0d\u80fd\u5411\u4f60\u51fa\u624b\u3002 \u201d \u3000\u3000\u4ed6\u76ef\u7740\u8346\u65e0\u547d\uff0c\u5389\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u73b0\u5728\u5df1\u6015\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6015\u6b7b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u7684\u786e\u4e0d\u6015\u6b7b\uff0c\u4f46\u90a3\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u90a3\u65f6\u8fd8\u6ca1\u6709\u4eba\u80fd\u5a01\u80c1\u4f60\u7684\u751f\u547d\uff0c\u6240 \u4ee5\u79f0\u6839\u672c\u8fd8\u65e0\u6cd5\u4e86\u89e3\u6b7b\u7684\u6050\u60e7\u3002\u201d \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u9f99\u51e4\u53cc\u73af\u5df2\u51fa\u624b\uff0c\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u201c\u4f46\u73b0\u5728\u6211\u5df2\u968f\u65f6\u53ef\u6740\u4f60\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u770b\u6765\u4f60\u597d\u50cf\u4ec0\u4e48\u4e8b\u90fd\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u6211\u81f3\u5c11\u6bd4\u4f60\u60f3\u8c61\u4e2d\u9ad8\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7a81\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u8fd8\u6709\u4e00\u4ef6\u4e8b\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u522b\u7684\u4e8b\u4f60\u5168\u4e0d\u77e5\u9053\u4e5f\u4e0d\u8981\u7d27\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u4f60\u82e5\u4e0d\u77e5\u9053\uff0c\u4f60\u5c31\u5f97\u6b7b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u98de\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u82e5\u771f\u7684\u5982\u6b64\u91cd\u8981\uff1f\u6211\u5c31\u7edd\u4e0d\u4f1a\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u7edd\u4e0d\u4f1a\u77e5\u9053\uff0c\u56e0\u4e3a\u8fd9\u662f\u6211\u7684\u79d8\u5bc6\uff0c\u6211\u4ece\u672a\u544a\u8bc9\u8fc7\u522b\u4eba\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u98de\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u51c6\u5907\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u73b0\u5728\u51c6\u5907\u544a\u8bc9\u4f60\uff0c\u4f46\u90a3\u4e5f\u662f\u6709\u4ea4\u6362\u6761\u4ef6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c3e\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u775b\u53c8\u6536\u7f29\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u82e5\u544a\u8bc9\u4e86\u4f60\uff0c\u6211\u5c31\u5f97\u6b7b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u98de\u9053\uff1a\u201c\u4f60\u8981\u6211\u6b7b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u8981\u79f0\u6b7b\uff0c\u56e0\u4e3a\u6d3b\u7740\u7684\u4eba\uff0c\u6ca1\u6709\u4eba\u80fd\u77e5\u9053\u8fd9\u79d8\u5bc6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u77aa\u7740\u4ed6\uff0c\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u7684\u786e\u50cf\u662f\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u4e00\u4e2a\u6b8b\u5e9f\u4e86\u4eba\uff0c\u5c45\u7136\u8fd8\u60f3\u8981\u522b\u4eba\u7684\u547d\uff1f \u3000\u3000\u4e0a\u5b98\u98de\u5927\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u7528\u4ec0\u4e48\u6765\u6740\u6211\uff1f\u7528\u4f60\u7684\u5934\u6765\u649e\uff0c\u7528\u4f60\u7684\u5634\u6765\u54ac\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u56de\u7b54\u5f88\u7b80\u77ed\uff0c\u4e5f\u5f88\u5999\uff0c\u53ea\u6709\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u7684\u7b11\u58f0\u5df2\u6e10\u6e10\u5c0f\u4e86\u3002 \u3000\u3000\u5982\u6b64\u7b80\u77ed\u7684\u56de\u7b54\uff0c\u5df2\u4e0d\u50cf\u662f\u5728\u5413\u4eba\uff0c\u66f4\u4e0d\u50cf\u662f\u5728\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u8981\u6740\u4eba\uff0c\u7528\u7684\u5c31\u662f\u8fd9\u53ea\u624b\uff01\u201d \u3000\u3000\u4ed6\u7684\u624b\u5df2\u62ac\u8d77\uff0c\u662f\u53f3\u624b\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u5df2\u7b11\u5f97\u5f88\u52c9\u5f3a\uff0c\u5374\u8fd8\u662f\u5927\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u53ea\u624b\u2026\u2026\u4f60\u8fd9\u53ea\u624b\u8fde\u72d7\u90fd\u6740\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6211\u53ea\u6740\u4eba\uff0c\u4e0d\u6740\u72d7\uff01\u201d \u3000\u3000\u4e0a\u5b98\u98de\u7b11\u58f0\u7a81\u7136\u505c\u987f\uff0c\u9f99\u51e4\u53cc\u73af\u5df2\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u201d\uff0c\u9f99\u51e4\u53cc\u73af\u672c\u662f\u6b66\u6797\u4e2d\u81f3\u7edd\u81f3\u9669\u4e4b\u5175\u5203\uff0c\u8fd9\u4e00\u7740\u201c\u9f99\u7fd4\u51e4\u821e\u8131\u624b\u53cc \u98de\u201d\u66f4\u662f\u9669\u4e2d\u4e4b\u9669\uff0c\u82e5\u975e\u60c5\u6025\u62fc\u547d\uff0c\u6216\u662f\u660e\u77e5\u5bf9\u65b9\u5df2\u88ab\u903c\u5165\u6b7b\u89d2\u65f6\uff0c\u672c\u4e0d\u8be5\u4f7f\u51fa\u8fd9\u4e00\u7740\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u82e5\u662f\u4f7f\u51fa\uff0c\u5bf9\u65b9\u4e5f\u5c31\u5f88\u96be\u95ea\u907f\u5f97\u5f00\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u5251\u5149\u5df2\u98de\u51fa\u3002 \u3000\u3000\u5251\u5149\u53ea\u4e00\u201c\u95ea\u201d\u5df2\u523a\u5165\u4e86\u4e0a\u5b98\u98de\u54bd\u5589\u3002 \u3000\u3000\u5251\u950b\u4eba\u5589\u4ec5\u4e03\u5206\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u7684\u547c\u5438\u5c1a\u672a\u505c\u987f\uff0c\u989d\u4e0a\u9752\u7b4b\u4e00\u6839\u6839\u66b4\u9732\uff0c\u773c\u73e0\u5b50\u4e5f\u5c06\u51f8\u4e86\u51fa\u6765\uff0c\u6b7b\u9c7c\u822c\u77aa\u7740\u8346\u65e0 \u547d\u3002 \u3000\u3000\u4ed6\u6b7b\u4e5f\u4e0d\u660e\u767d\u8346\u65e0\u547d\u8fd9\u4e00\u5251\u662f\u600e\u4e48\u523a\u51fa\u6765\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e5f\u5728\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u7f13\u7f13\u9053\uff1a\u201c\u6211\u7684\u53f3\u624b\u6bd4\u5de6\u624b\u66f4\u5feb\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u79d8\u5bc6\uff01 \u201d \u3000\u3000\u4e0a\u5b98\u98de\u8eab\u5b50\u7a81\u7136\u4e00\u9635\u62bd\u6410\uff0c\u54bd\u5589\u4e2d\u53d1\u51fa\u4e86\u201c\u683c\u201d\u7684\u4e00\u54cd\u3002 \u3000\u3000\u5251\u62d4\u51fa\uff0c\u9c9c\u8840\u98de\u6fc0\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u6b7b\u9c7c\u822c\u7684\u773c\u775b\u8fd8\u662f\u5728\u77aa\u7740\u8346\u65e0\u547d\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u6000\u7591\uff0c\u60b2\u54c0\uff0c\u60ca\u4ff1\u2026\u2026 \u3000\u3000\u4ed6\u8fd8\u662f\u4e0d\u76f8\u4fe1\uff0c\u6b7b\u4e5f\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u4f46\u4ed6\u5fc5\u987b\u76f8\u4fe1\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u8131\u624b\u51fb\u51fa\u7684\u9f99\u51e4\u53cc\u73af\uff0c\u5df2\u6253\u5165\u4e86\u8346\u65e0\u547d\u7684\u5de6\u81c2\u3002 \u3000\u3000\u65ad\u81c2\u3002 \u3000\u3000\u4ed6\u62fc\u7740\u4ee5\u8fd9\u6761\u65ad\u81c2\uff0c\u53bb\u786c\u63a5\u4e0a\u5b98\u98de\u7684\u53cc\u73af\uff0c\u7136\u540e\u4ee5\u53f3\u624b\u5251\u81ea\u5de6\u808b\u4e4b\u4e0b\u523a\u51fa\uff0c\u4e00\u5251\u523a\u5165\u4e86 \u4e0a\u5b98\u98de\u7684\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u662f\u4f55\u7b49\u8be1\u5f02\u7684\u5251\u6cd5\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u597d\u51c6\uff01\u597d\u6bd2\uff01\u597d\u5feb\uff01\u597d\u72e0\uff01 \u3000\u3000\u201c\u6211\u7684\u53f3\u624b\u6bd4\u5de6\u624b\u66f4\u5feb\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u79d8\u5bc6\uff01\u201d \u3000\u3000\u4ed6\u7684\u786e\u6ca1\u6709\u8bf4\u8c0e\u3002 \u3000\u3000\u4f46\u8fd9\u4e8b\u5b9e\u5374\u53c8\u591a\u4e48\u4ee4\u4eba\u65e0\u6cd5\u601d\u8bae\uff0c\u96be\u4ee5\u76f8\u4fe1\u3002 \u3000\u3000\u4e0a\u5b98\u98de\u548c\u4ed6\u540c\u95e8\u5341\u4f59\u5e74\uff0c\u4ece\u672a\u89c1\u4ed6\u7ec3\u8fc7\u4e00\u5929\u53f3\u624b\u5251\uff0c\u6240\u4ee5\u6b7b\u4e5f\u4e0d\u660e\u767d\u4ed6\u8fd9\u53f3\u624b\u5251\u662f\u5982\u4f55 \u7ec3\u6210\u7684\u3002 \u3000\u3000\u4f46\u4ed6\u5fc5\u987b\u76f8\u4fe1\uff0c\u56e0\u4e3a\u4e16\u4e0a\u7edd\u6ca1\u6709\u6bd4\u201c\u6b7b\u201d\u66f4\u771f\u5b9e\u7684\u4e8b\u3002 \u3000\u3000\u8346\u65e0\u547d\u5782\u9996\u671b\u7740\u4ed6\u7684\u5c38\u8eab\uff0c\u795e\u60c5\u770b\u6765\u4f3c\u4e4e\u6709\u4e9b\u60c6\u6005\uff0c\u5931\u671b\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u4ed6\u7a81\u7136\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5583\u5583\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u8981\u6740\u6211\uff1f\u6211\u4f55\u5fc5\u8981\u6740\u4f60\uff1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8d70\u8def\u7684\u59ff\u52bf\u8fd8\u662f\u90a3\u4e48\u5947\u7279\uff0c\u4eff\u4f5b\u5728\u6697\u4e2d\u914d\u5408\u7740\u67d0\u4e00\u79cd\u5947\u7279\u7684\u97f5\u5f8b\u3002 \u3000\u3000\u90a3\u5bf9\u9f99\u51e4\u53cc\u73af\u8fd8\u662f\u5d4c\u5728\u4ed6\u5de6\u81c2\u91cc\u3002 \u3000\u3000\u6000\u7591\uff0c\u60ca\u60e7\uff0c\u4e0d\u80fd\u76f8\u4fe1\u3002 \u3000\u3000\u8fd9\u4e5f\u6b63\u662f\u963f\u98de\u6b64\u523b\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u5251\u6cd5\u7684\u786e\u53ef\u6015\uff0c\u4e5f\u8bb8\u5e76\u4e0d\u6bd4\u4ed6\u5feb\uff0c\u4f46\u5374\u66f4\u72e0\u6bd2\uff0c\u66f4\u8be1\u79d8\u3002 \u3000\u3000\u201c\u96be\u9053\u6211\u771f\u7684\u65e0\u6cd5\u80dc\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u5c31\u7b97\u660e\u77e5\u8fd9\u662f\u4e8b\u5b9e\uff0c\u4e5f\u662f\u963f\u98de\u8fd9\u79cd\u4eba\u7edd\u5bf9\u65e0\u6cd5\u5fcd\u53d7\u7684\uff01 \u3000\u3000\u671b\u7740\u8346\u65e0\u547d\u9010\u6e10\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u963f\u98de\u7a81\u7136\u89c9\u5f97\u80f8\u4e2d\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u8981\u8df3\u4e0b\u571f\u5c71\uff0c \u8ffd\u4e0a\u53bb\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u7136\u6709\u4e00\u53ea\u624b\u4ece\u540e\u9762\u4f38\u8fc7\u6765\uff0c\u62c9\u4f4f\u4ed6\u3002 \u3000\u3000\u8fd9\u662f\u53ea\u5f88\u7a33\u5b9a\u7684\u624b\uff0c\u7626\u524a\u800c\u6709\u529b\u3002 \u3000\u3000\u963f\u98de\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u5230\u4e86\u674e\u5bfb\u6b22\u90a3\u5bf9\u5145\u6ee1\u4e86\u53cb\u60c5\u548c\u751f\u548c\u70ed\u7231\u7684\u773c\u775b\u3002 \u3000\u3000\u80fd\u62c9\u4f4f\u963f\u98de\u7684\u5e76\u4e0d\u662f\u8fd9\u53ea\u624b\uff0c\u800c\u662f\u8fd9\u53cc\u773c\u775b\u3002 \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u5782\u4e0b\u5934\uff0c\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u771f\u7684\u4e0d\u5982\u4ed6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u53ea\u6709\u4e00\u70b9\u4e0d\u5982\u4ed6\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e00\u70b9\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4e86\u6740\u4eba\uff0c\u8346\u65e0\u547d\u53ef\u4ee5\u4e0d\u62e9\u4e00\u5207\u624b\u6bb5\uff0c\u751a\u81f3\u4e0d\u6070\u727a\u7272\u81ea\u5df1\uff0c\u4f60\u5374\u4e0d\u80fd\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u7684\u786e\u4e0d\u80fd\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\uff0c\u53ea\u56e0\u4f60\u6709\u611f\u60c5\uff0c\u4f60\u7684\u5251\u672f\u867d\u65e0\u60c5\uff0c\u4eba\u5374\u6709\u60c5\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6240\u4ee5\u2026\u2026\u6211\u5c31\u6c38\u8fdc\u65e0\u6cd5\u80dc\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u9519\u4e86\uff0c\u4f60\u5fc5\u80fd\u80dc\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u95ee\uff0c\u53ea\u662f\u5728\u542c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u63a5\u7740\u8bf4\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u6709\u611f\u60c5\uff0c\u624d\u6709\u751f\u547d\uff0c\u6709\u751f\u547d\uff0c\u624d\u6709\u7075\u6c14\uff0c\u624d\u6709\u53d8\u5316\u3002\u201d \u3000\u3000\u963f\u98de\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6f2b\u6f2b\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u8fd9\u8fd8\u5e76\u4e0d\u662f\u6700\u91cd\u8981\u7684\uff0c\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u4f60\u6839\u672c\u4e0d\u5fc5\u6740\u4ed6\uff0c\u4e5f\u4e0d\u80fd\u6740\u4ed6\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5fc5\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u672c\u5df2\u6b7b\u4e86\uff0c\u4f55\u5fc5\u518d\u6740\uff1f\u201d \u3000\u3000\u963f\u98de\u6c89\u601d\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u5fc3\u5b9e\u5df2\u6b7b\u2026\u2026\u4fbf\u65e2\u5df2\u4e0d\u5fc5\uff0c\u4e3a\u4f55\u53c8\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u6d3b\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4e3a\u4f55\u8981\u5728\u6697\u4e2d\u82e6\u7ec3\u53f3\u624b\u5251\u6cd5\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u662f\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u82e5\u662f\u6211\u731c\u5f97\u4e0d\u9519\uff0c\u4ed6\u4e3a\u7684\u5c31\u662f\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u62fc\u7740\u53bb\u6328\u4e0a\u5b98\u98de\u7684\u9f99\u51e4\u53cc\u73af\uff0c\u5c31\u662f\u60f3\u5148\u7ec3\u4e00\u7ec3\u5bf9\u4ed8\u53cc\u73af\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u4e5f\u6b63\u662f\u6211\u7684\u60f3\u6cd5\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6240\u4ee5\u2026\u2026\u4e0a\u5b98\u91d1\u8679\u5bf9\u4ed6\u7684\u6001\u5ea6\u82e5\u662f\u6539\u53d8\u4e86\uff0c\u4ed6\u5c31\u4f1a\u7528\u8fd9\u6cd5\u5b50\u53bb\u6740\u4e0a\u5b98\u91d1\u8679\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u505a\u4e0d\u5230\uff0c\u4f46\u4ed6\u81f3\u5c11\u4f1a\u53bb\u8bd5\u4e00\u8bd5\uff0c\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u76ee\u5149\u5374\u6e10\u6e10\u5728\u9eef\u6de1\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u53c8\u88ab\u89e6\u53ca\u4e86\u4ec0\u4e48\u9690\u60c5\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u7684\u9f99\u51e4\u53cc\u73af\u80fd\u5728\u5175\u5668\u8c31\u4e2d\u540d\u5217\u7b2c\u4e8c\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u62db\u5f0f\u7684\u72e0\u6bd2\uff0c \u8be1\u9669\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u7684\u7a33\u3002\u201d \u3000\u3000\u963f\u98de\u832b\u7136\u9053\uff1a\u201c\u7a33\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u80fd\u5c06\u5929\u4e0b\u81f3\u9669\u7684\u5175\u5668\uff0c\u7ec3\u5230\u4e00\u4e2a\u2018\u7a33\u2019\u5b57\uff0c\u8fd9\u624d\u662f\u4e0a\u5b98\u91d1\u8679\u975e\u4eba\u80fd\u53ca\u4e4b\u5904 \uff0c\u4e0a\u5b98\u98de\u7684\u6b66\u529f\uff0c\u6839\u672c\u96be\u53ca\u4ed6\u7236\u4eb2\u4e4b\u4e07\u4e00\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u98de\u4e4b\u6240\u4ee5\u6068\u8346\u65e0\u547d\uff0c\u4e5f\u662f\u8ba4\u4e3a\u4ed6\u7236\u4eb2\u6ca1\u6709\u5c06\u6b66\u529f\u7684\u5965\u79d8\u4f20\u6388\u7ed9\u4ed6\uff0c\u800c \u4f20\u7ed9\u4e86\u8346\u65e0\u547d\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u82e5\u4e0d\u7528\u2018\u9f99\u7fd4\u51e4\u821e\u8131\u624b\u53cc\u98de\u2019\u90a3\u6837\u7684\u9669\u6bd2\uff0c\u8346\u65e0\u547d\u80fd\u80dc\u4ed6\u7684\u673a\u4f1a \u5c31\u5f88\u5c11\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u4e0a\u5b98\u91d1\u8679\u8bf4\u4e0d\u5b9a\u4f1a\u4f7f\u51fa\u6765\u7684\uff0c\u56e0\u4e3a\u4ed6\u89c1\u5230\u8346\u65e0\u547d\u7684\u5de6\u81c2\u5df2\u65ad\uff0c\u5c31\u4e0d\u4f1a\u518d \u6709\u987e\u8651\uff0c\u518d\u7559\u7740\u4e0d\u7528\uff0c\u6240\u4ee5\u8346\u65e0\u547d\u4e5f\u5e76\u975e\u5b8c\u5168\u6ca1\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u963f\u98de\u50cf\u662f\u7a81\u7136\u81ea\u68a6\u4e2d\u60ca\u9192\uff0c\u5927\u58f0\u9053\uff1a\u201c\u53ef\u662f\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e0a\u5b98\u91d1\u8679\u603b\u662f\u8346\u65e0\u547d\u7684\u7236\u4eb2\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u521a\u624d\u4e0a\u5b98\u98de\u660e\u660e\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u90a3\u53ea\u4e0d\u8fc7\u662f\u4e0a\u5bab\u98de\u7684\u731c\u60f3\uff0c\u800c\u4e14\u731c\u5f97\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4ed6\u8bf4\u7684\u90a3\u4e9b\u8bdd\uff0c\u96be\u9053\u4e5f\u662f\u5047\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e9b\u4e8b\u81ea\u7136\u4e0d\u4f1a\u5047\uff0c\u4f46\u4ed6\u7684\u770b\u6cd5\u5374\u9519\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u770b\u9519\u4e86\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u8bf4\uff0c\u81ea\u4ece\u8346\u65e0\u547d\u4e00\u53bb\uff0c\u4ed6\u7236\u4eb2\u5c31\u5f00\u59cb\u5bf9\u4ed6\u51b7\u6de1\u758f\u8fdc\uff0c\u8fd9\u81ea\u7136\u662f\u4e8b\u5b9e\uff0c\u4f46 \u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u4e48\u505a\uff0c\u4e3a\u7684\u53ea\u662f\u7231\u4ed6\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u65e2\u7136\u7231\u4ed6\uff0c\u4e3a\u4f55\u758f\u8fdc\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u5168\u5fc3\u5168\u610f\u8981\u5c06\u8346\u65e0\u547d\u8bad\u7ec3\u6210\u4ed6\u6740\u4eba\u7684\u5de5\u5177\uff0c\u8346\u65e0\u547d\u8fd9\u4e00\u751f\uff0c \u4e5f\u5c31\u56e0\u6b64\u800c\u6bc1\u5728\u4ed6\u624b\u4e0a\u3002\u201d \u3000\u3000\u963f\u98de\u601d\u7d22\u7740\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u4e2a\u4eba\u82e5\u53ea\u4e3a\u4e86\u6740\u4eba\u800c\u6d3b\u7740\uff0c\u7684\u786e\u662f\u4ef6\u5f88\u60b2\u54c0\u7684\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u2018\u201c\u6240\u4ee5\u6211\u8bf4\u8346\u65e0\u547d\u81ea\u4ece\u89c1\u5230\u4e0a\u5b98\u91d1\u8679\u90a3\u4e00\u53e3\u8d77\uff0c\u5c31\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u963f\u98de\u9ed8\u7136\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u4e0a\u5b98\u91d1\u8679\u4e5f\u662f\u4eba\uff0c\u4eba\u90fd\u6709\u7231\u5b50\u4e4b\u5fc3\uff0c\u81ea\u7136\u4e0d\u5fcd\u5bf9\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u8fd9\u4e48\u505a\uff0c \u6240\u4ee5\u624d\u6ca1\u6709\u5c06\u6b66\u529f\u4f20\u7ed9\u4e0a\u5b98\u98de\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u957f\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4e0a\u5b98\u98de\u5e76\u4e0d\u80fd\u4e86\u89e3\u4ed6\u7236\u4eb2\u7684\u8fd9\u756a\u82e6\u5fc3\u3002\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u201c\u6240\u4ee5\u4e0a\u5b98\u98de\u5176\u5b9e\u4e5f\u7b49\u4e8e\u662f\u6b7b\u5728\u4ed6\u7236\u4eb2\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u6b32\u671b\u82e5\u662f\u592a\u5927\uff0c\u5f80\u5f80\u5c31\u96be\u514d\u4f1a\u505a\u9519\u8bb8\u591a\u4e8b\u2026\u2026 \u7b2c\u516d\u5341\u4e09\u7ae0\u3000\u65ad\u4e49>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u4e09\u7ae0\u3000\u65ad\u4e49 \u3000\u3000\u79cb\u6797\uff0c\u67af\u6797\u3002 \u3000\u3000\u7a7f\u8fc7\u67af\u6797\uff0c\u5c31\u662f\u6761\u5f88\u50fb\u9759\u7684\u5c0f\u8def\u3002 \u3000\u3000\u963f\u98de\u9065\u6307\u7740\u5c0f\u8def\u5c3d\u5934\u5904\u7684\u4e00\u70b9\u5b64\u706f\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u662f\u6211\u7684\u5bb6\u3002\u201d \u3000\u3000\u5bb6\u3002 \u3000\u3000\u8fd9\u4e2a\u5b57\u542c\u5728\u674e\u5bfb\u6b22\u8033\u91cc\uff0c\u7adf\u662f\u90a3\u4e48\u9065\u8fdc\uff0c\u90a3\u4e48\u964c\u751f\u2026\u2026 \u3000\u3000\u963f\u98de\u7684\u76ee\u5149\u8fd8\u5728\u9065\u89c6\u7740\u90a3\u70b9\u706f\u706b\uff0c\u63a5\u7740\u9053\uff1a\u201c\u706f\u4eae\u7740\uff0c\u5979\u5927\u6982\u8fd8\u6ca1\u7761\u3002\u201d \u3000\u3000\u5c0f\u5c4b\u4e2d\uff0c\u4e00\u706f\u83b9\u7136\uff0c\u4e00\u4e2a\u5e03\u8863\u7c97\u88d9\uff0c\u86fe\u7709\u6de1\u626b\u7684\u7edd\u4ee3\u4f73\u4eba\uff0c\u6b63\u5728\u706f\u4e0b\u7f00\u7740\u8863\u886b\uff0c\u7b49\u5019\u81ea \u5df1\u6700\u4eb2\u8fd1\u7684\u4eba\u5f52\u6765\u2026\u2026 \u3000\u3000\u8fd9\u662f\u4e00\u5e45\u591a\u4e48\u7f8e\u4e3d\u7684\u56fe\u753b\u3002 \u3000\u3000\u53ea\u8981\u60f3\u5230\u8fd9\u91cc\uff0c\u963f\u98de\u5fc3\u91cc\u5c31\u5145\u6ee1\u4e86\u751c\u871c\u548c\u6e29\u6696\uff0c\u90a3\u53cc\u9510\u5229\u7684\u773c\u775b\u4e5f\u7acb\u523b\u53d8\u5f97\u6e29\u67d4\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u672c\u662f\u5b64\u72ec\u800c\u5bc2\u5bde\u7684\u4eba\uff0c\u4f46\u73b0\u5728\uff0c\u4ed6\u5374\u77e5\u9053\u6709\u4eba\u5728\u7b49\u7740\u4ed6\u2026\u2026\u4ed6\u6700\u5fc3\u7231\u7684\u4eba\u5728\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u89c9\u7684\u786e\u662f\u5e78\u798f\u7684\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u6bd4\u62df\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u4ee3\u66ff\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u770b\u5230\u963f\u98de\u90a3\u5145\u6ee1\u4e86\u5e78\u798f\u5149\u8f89\u7684\u8138\uff0c\u4ed6\u5ffd\u7136\u6709\u79cd\u8d1f\u7f6a\u4e4b\u611f\u3002 \u3000\u3000\u4ed6\u672c\u4e0d\u5fcd\u4ee4\u963f\u98de\u5931\u671b\u3002 \u3000\u3000\u4ed6\u5b81\u53ef\u81ea\u5df1\u53bb\u80cc\u8d1f\u4e00\u5207\u75db\u82e6\uff0c\u4e5f\u4e0d\u613f\u963f\u98de\u5931\u671b\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5374\u5fc5\u987b\u8981\u4f7f\u963f\u98de\u5931\u671b\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u60f3\u50cf\u963f\u98de\u56de\u53bb\u53d1\u73b0\u6797\u4ed9\u513f\u5df2\u4e0d\u5728\u65f6\uff0c\u4f1a\u53d8\u6210\u4ec0\u4e48\u6a21\u6837\uff1f \u3000\u3000\u867d\u7136\u4ed6\u8fd9\u6837\u53ea\u662f\u4e3a\u4e86\u8981\u963f\u98de\u597d\uff0c\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\uff0c\u5802\u5802\u6b63\u6b63\u7684\u6d3b\u4e0b\u53bb\uff0c\u6d3b\u5f97\u50cf\u662f\u4e2a\u7537\u5b50\u6c49 \u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u5bf9\u4e0d\u8d77\u963f\u98de\u3002 \u3000\u3000\u201c\u957f\u75db\u4e0d\u5982\u77ed\u75db\u3002\u201d \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u963f\u98de\u80fd\u5f88\u5feb\u7684\u6446\u8131\u75db\u82e6\uff0c\u5f88\u5feb\u7684\u5fd8\u8bb0\u5979\u3002 \u3000\u3000\u5979\u65e2\u4e0d\u503c\u5f97\u7231\uff0c\u66f4\u4e0d\u503c\u5f97\u601d\u5ff5\u3002 \u3000\u3000\u4e0d\u5e78\u7684\u662f\uff0c\u4e00\u4e2a\u4eba\u5f80\u5f80\u4f1a\u504f\u504f\u53bb\u7231\u4e00\u4e2a\u4e0d\u503c\u5f97\u7231\u7684\u4eba\uff0c\u56e0\u4e3a\u60c5\u611f\u672c\u5c31\u5982\u4e00\u5339\u8131\u7f30\u7684\u91ce\u9a6c \uff0c\u8c01\u4e5f\u65e0\u6cd5\u63a7\u5236\uff0c\u8c01\u90fd\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u8fd9\u672c\u4e5f\u662f\u4eba\u7c7b\u6700\u6df1\u9042\u7684\u60b2\u54c0\u4e4b\u4e00\u3002 \u3000\u3000\u4e5f\u6b63\u56e0\u5982\u6b64\uff0c\u6240\u4ee5\u4eba\u4e16\u95f4\u6c38\u8fdc\u4e0d\u65ad\u6709\u60b2\u5267\u6f14\u51fa\u3002 \u3000\u3000\u706f\u4eae\u7740\uff0c\u95e8\u5374\u662f\u865a\u63a9\u7740\u7684\u3002 \u3000\u3000\u706f\u5149\u81ea\u9699\u95f4\u7167\u51fa\uff0c\u7167\u5728\u95e8\u5916\u7684\u5c0f\u5f84\u4e0a\u3002 \u3000\u3000\u6628\u591c\u4eff\u4f5b\u6709\u96e8\uff0c\u8def\u662f\u6e7f\u7684\uff0c\u706f\u5149\u4e0b\u53ef\u4ee5\u770b\u51fa\u8def\u4e0a\u6709\u5f88\u591a\u5f88\u96f6\u4e71\u7684\u811a\u5370\u3002 \u3000\u3000\u7537\u4eba\u7684\u811a\u5370\u3002 \u3000\u3000\u201c\u662f\u8c01\u6765\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u963f\u98de\u76b1\u4e86\u76b1\u7709\uff0c\u4f46\u7acb\u523b\u53c8\u5f00\u6717\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u5f88\u4fe1\u4efb\u6797\u4ed9\u513f\uff0c\u4ed6\u786e\u4fe1\u5979\u7edd\u4e0d\u4f1a\u505a\u4efb\u4f55\u5bf9\u4e0d\u8d77\u4ed6\u7684\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fdc\u8fdc\u7684\u8ddf\u5728\u540e\u9762\uff0c\u4eff\u4f5b\u4e0d\u6562\u8e0f\u5165\u8fd9\u5c0f\u5c4b\u3002 \u3000\u3000\u963f\u98de\u56de\u5934\u7b11\u9053\uff1a\u201c\u6211\u5e0c\u671b\u5979\u4eca\u5929\u7096\u7684\u6c64\u91cc\u6ca1\u6709\u653e\u7b0b\u5b50\uff0c\u4f60\u4e5f\u53ef\u4ee5\u559d\u4e00\u70b9\uff0c\u624d\u4f1a\u77e5\u9053\u5979\u505a \u83dc\u7684\u672c\u4e8b\u6bd4\u4f7f\u7528\u5200\u8fd8\u597d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u53c8\u6709\u8c01\u77e5\u9053\u4ed6\u7b11\u5f97\u662f\u591a\u4e48\u9178\u695a\uff1f \u3000\u3000\u90a3\u5927\u7897\u7684\u6392\u9aa8\u6c64\u91cc\u82e5\u6ca1\u6709\u653e\u7b0b\u5b50\uff0c\u674e\u5bfb\u6b22\u4e5f\u8bb8\u8fd8\u4e0d\u80fd\u5b8c\u5168\u53d1\u73b0\u6797\u4ed9\u513f\u7684\u79d8\u5bc6\uff0c\u90a3\u4e48\uff0c\u4eca \u5929\u53d1\u751f\u7684\u4e8b\u4e5f\u8bb8\u5c31\u4f1a\u5b8c\u5168\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b80\u76f4\u65e0\u6cd5\u60f3\u8c61\u4e00\u4e2a\u5973\u4eba\uff0c\u600e\u80fd\u7528\u5982\u6b64\u6b8b\u9177\u7684\u624b\u6bb5\u6765\u6b3a\u9a97\u4e00\u4e2a\u5982\u6b64\u6df1\u7231\u7740\u5979\u7684\u7537\u4eba \u3002 \u3000\u3000\u201c\u4f46\u6211\u53c8\u4f55\u5c1d\u4e0d\u662f\u5728\u6b3a\u9a97\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6562\u544a\u8bc9\u4ed6\uff0c\u6797\u4ed9\u513f\u5df2\u2018\u4e0d\u5728\u2019\u4e86\uff0c\u800c\u4e14\u5b8c\u5168\u662f\u6211\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5f2f\u4e0b\u8170\uff0c\u5267\u70c8\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u963f\u98de\u70b9\u5934\u9053\uff1a\u201c\u4f60\u82e5\u80af\u5728\u6211\u8fd9\u91cc\u591a\u4f4f\u4e9b\u65f6\u5019\uff0c\u54b3\u55fd\u4e5f\u8bb8\u5c31\u4f1a\u597d\u4e9b\uff0c\u56e0\u4e3a\u8fd9\u91cc\u53ea\u6709\u6c64\uff0c\u6ca1 \u6709\u9152\u3002\u201d \u3000\u3000\u4ed6\u6c38\u8fdc\u4e0d\u4f1a\u77e5\u9053\uff1a\u201c\u6c64\u201d\u5bf9\u4ed6\u7684\u4f24\u5bb3\uff0c\u8fdc\u6bd4\u9152\u8fd8\u4e25\u91cd\u5f97\u591a\u3002 \u3000\u3000\u95e8\u91cc\u6ca1\u6709\u4eba\u58f0\u3002 \u3000\u3000\u963f\u98de\u53c8\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u5728\u53a8\u623f\u91cc\uff0c\u6ca1\u6709\u542c\u5230\u6211\u4eec\u8bf4\u8bdd\uff0c\u5426\u5219\u5979\u4e00\u5b9a\u65e9\u5c31\u8fce\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u76f4\u6ca1\u6709\u5f00\u53e3\uff0c\u56e0\u4e3a\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u95e8\uff0c\u7ec8\u4e8e\u88ab\u63a8\u5f00\u3002 \u3000\u3000\u5c0f\u5c0f\u7684\u5ba2\u5385\u91cc\uff0c\u8fd8\u662f\u90a3\u4e48\u5e72\u51c0\u3002 \u3000\u3000\u684c\u4e0a\u7684\u6cb9\u706f\u5e76\u4e0d\u4eae\uff0c\u4f46\u5374\u6709\u79cd\u6e29\u6696\u5b81\u9759\u7684\u611f\u89c9\u3002 \u3000\u3000\u963f\u98de\u957f\u957f\u5410\u51fa\u53e3\u6c14\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u56de\u5230\u5bb6\u4e86\uff0c\u5e73\u5e73\u5b89\u5b89\u7684\u56de\u5230\u5bb6\u4e86\u3002 \u3000\u3000\u4ed6\u6bd5\u7adf\u6ca1\u6709\u4ee4\u6797\u4ed9\u513f\u5931\u671b\u3002 \u3000\u3000\u4f46\u5979\u7684\u4eba\u5462\uff1f\u5728\u54ea\u91cc\uff1f \u3000\u3000\u53a8\u623f\u91cc\u6839\u672c\u8fde\u706f\u5149\u90fd\u6ca1\u6709\uff0c\u66f4\u6ca1\u6709\u83dc\u6c64\u7684\u9999\u6c14\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4f4f\u7684\u90a3\u95f4\u5c4b\u5b50\uff0c\u95e8\u4e5f\u662f\u5173\u7740\u7684\u3002 \u3000\u3000\u963f\u98de\u56de\u5934\u5411\u7ad9\u5728\u95e8\u53e3\u7684\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5979\u4e5f\u8bb8\u5df2\u7761\u4e86\u2026\u2026\u5979\u4e00\u5411\u7761\u5f97\u65e9\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6b63\u60f3\u7b11\u4e00\u7b11\uff0c\u9762\u4e0a\u7684\u808c\u8089\u5df2\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u5df2\u542c\u5230\u4e00\u9635\u9635\u7684\u547b\u541f\u58f0\uff0c\u5973\u4eba\u7684\u547b\u541f\u58f0\u3002 \u3000\u3000\u662f\u5782\u6b7b\u7684\u547b\u541f\uff01 \u3000\u3000\u547b\u541f\u58f0\u6b63\u662f\u4ece\u6797\u4ed9\u513f\u7684\u90a3\u95f4\u5c4b\u5b50\u91cc\u4f20\u51fa\u6765\u7684\u3002 \u3000\u3000\u963f\u98de\u7684\u8138\u8272\u7acb\u523b\u4e5f\u53d8\u4e86\uff0c\u4e00\u6b65\u51b2\u8fc7\u53bb\uff0c\u7528\u529b\u62cd\u95e8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\u4e86\uff1f\u8bf7\u5f00\u95e8\u3002\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u751a\u81f3\u8fde\u547b\u541f\u90fd\u505c\u6b62\u3002 \u3000\u3000\u5979\u663e\u7136\u662f\u60f3\u56de\u7b54\uff0c\u60f3\u547c\u5524\uff0c\u5374\u5df2\u53d1\u4e0d\u51fa\u58f0\u97f3\u3002 \u3000\u3000\u963f\u98de\u7684\u989d\u4e0a\u5df2\u6c81\u51fa\u4e86\u51b7\u6c57\uff0c\u7528\u529b\u4ee5\u80a9\u5934\u649e\u5f00\u4e86\u95e8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u53bb\u770b\u963f\u98de\u6b64\u523b\u9762\u4e0a\u7684\u8868\u60c5\u2014\u2014\u4e00\u4e2a\u4eba\u89c1\u5230\u81ea\u5df1\u5fc3\u4e0a\u7684\u4eba\u6b63\u5728\u4f5c\u5782\u6b7b\u6323\u624e\uff0c\u4f1a\u6709\u4ec0 \u4e48\u6837\u7684\u8868\u60c5\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u975e\u4f46\u4e0d\u6562\u770b\uff0c\u4e0d\u5fcd\u770b\uff0c\u7b80\u76f4\u8fde\u60f3\u90fd\u4e0d\u6562\u53bb\u60f3\u3002 \u3000\u3000\u4f46\u95e8\u88ab\u649e\u5f00\u540e\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u58f0\u97f3\u3002 \u3000\u3000\u963f\u98de\u96be\u9053\u53d7\u4e0d\u4e86\u8fd9\u53ef\u6015\u7684\u6253\u51fb\uff0c\u96be\u9053\u5df2\u6655\u4e86\u8fc7\u53bb\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5f20\u5f00\u773c\u201c\uff0c\u963f\u98de\u8fd8\u6014\u5728\u95e8\u53e3\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u7adf\u53ea\u6709\u60ca\u5f02\uff0c\u5374\u6ca1\u6709\u60b2\u621a\u3002 \u3000\u3000\u90a3\u5c4b\u5b50\u91cc\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u53ea\u6015\u674e\u5bfb\u6b22\u6c38\u8fdc\u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u8840\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b2c\u4e00\u773c\u770b\u5230\u7684\u5c31\u662f\u8840\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u5230\u5012\u5367\u5728\u8840\u6cca\u4e2d\u7684\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u6c38\u8fdc\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5012\u5367\u8840\u6cca\u4e2d\uff0c\u4f5c\u5782\u6b7b\u6323\u624e\u7684\u4eba\u7adf\u662f\u94c3\u94c3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8840\u5df2\u51bb\u7ed3\uff0c\u5fc3\u5df2\u4e0b\u6c89\u3002 \u3000\u3000\u963f\u98de\u9759\u9759\u7684\u77a7\u7740\u4ed6\uff0c\u9762\u4e0a\u7684\u8868\u60c5\u5f88\u5947\u7279\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u5df2\u731c\u51fa\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u95ee\uff1a\u201c\u8fd9\u5c0f\u59d1\u5a18\u662f\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4ed6\u53ea\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u5979\u662f\u4e0d\u662f\u4e5f\u5728\u8fd9\u91cc\u7b49\u4f60\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u4f3c\u88ab\u5272\u88c2\uff0c\u6251\u8fc7\u53bb\uff0c\u62b1\u8d77\u4e86\u8840\u6cca\u4e2d\u7684\u94c3\u94c3\uff0c\u8bd5\u63a2\u5979\u7684\u8109\u640f\u548c\u547c\u5438\u2014\u2014\u4ed6\u53ea\u5e0c \u671b\u8fd8\u80fd\u6551\u6cbb\u5979\u7684\u4e00\u6761\u547d\u3002 \u3000\u3000\u4ed6\u5df2\u7edd\u671b\u3002 \u3000\u3000\u94c3\u94c3\u7ec8\u4e8e\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u770b\u5230\u4e86\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u5979\u773c\u775b\u7acb\u523b\u6d8c\u51fa\u4e86\u6cea\uff0c\u662f\u60b2\u54c0\u7684\u6cea\uff0c\u4e5f\u662f\u6b22\u559c\u7684\u6cea\u3002 \u3000\u3000\u5979\u4e34\u6b7b\u524d\u6bd5\u7adf\u8fd8\u662f\u89c1\u5230\u4e86\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5df2\u6cea\u6c34\u76c8\u7736\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u632f\u4f5c\u4e9b\uff0c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u7edd\u4e0d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u94c3\u94c3\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u800c\u662f\u65ad\u7eed\u7740\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u9519\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u60e8\u7136\u9053\uff1a\u201c\u662f\u6211\u9519\u4e86\u3002\u201d \u3000\u3000\u94c3\u94c3\u9053\uff1a\u201c\u4f60\u8be5\u77e5\u9053\uff0c\u4e16\u4e0a\u672c\u6ca1\u6709\u4e00\u4e2a\u7537\u4eba\u80fd\u5fcd\u5fc3\u6740\u5979\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u58f0\u97f3\u5df2\u5636\u54d1\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u662f\u6211\u5bb3\u4e86\u4f60\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002\u201d \u3000\u3000\u94c3\u94c3\u7a81\u7136\u7528\u529b\u6293\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u5bf9\u6211\u597d\uff0c\u5bb3\u6211\u7684\u4e0d\u662f\u4f60\uff0c\u662f\u4ed6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u3002\u201d \u3000\u3000\u94c3\u94c3\u6cea\u843d\u5982\u96e8\uff0c\u9053\uff1a\u201c\u4ed6\u9a97\u4e86\u6211\uff0c\u6211\u2026\u2026\u6211\u5374\u9a97\u4e86\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u94c3\u94c3\u7684\u6307\u7532\uff0c\u5df2\u523a\u4eba\u4e86\u674e\u5bfb\u6b22\u7684\u8089\u91cc\uff0c\u9053\uff1a\u201c\u6211\u9a97\u4e86\u4f60\u2026\u2026\u6211\u65e9\u5df2\u5931\u8eab\u7ed9\u4ed6\uff0c\u5728\u7b49\u4f60\u7684 \u65f6\u5019\u2026\u2026\u6211\u53ea\u6068\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e00\u76f4\u6ca1\u6709\u52c7\u6c14\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5979\u8bdd\u58f0\u5ffd\u7136\u6e05\u695a\u4e86\u8d77\u6765\uff0c\u4eff\u4f5b\u5df2\u6709\u4e86\u751f\u673a\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u77e5\u9053\u90a3\u53ea\u4e0d\u8fc7\u662f\u56de\u5149\u53cd\u7167\u800c\u5df2\u2014\u2014\u94c3\u94c3\u82e5\u975e\u8fd8\u5982\u6b64\u5e74\u8f7b\uff0c\u4e00\u5b9a\u65e0\u6cd5\u6d3b\u5230\u73b0\u5728 \u3002 \u3000\u3000\u94c3\u94c3\u51c4\u7136\u9053\uff1a\u201c\u6211\u4e00\u76f4\u4e0d\u80af\u6b7b\uff0c\u6323\u624e\u7740\u6d3b\u5230\u73b0\u5728\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u544a\u8bc9\u4f60\u8fd9\u4e9b\u6d3b\uff0c\u53ea\u8981\u4f60\u80fd \u4e86\u89e3\uff0c\u6211\u6b7b\u4e5f\u7518\u5fc3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u672c\u5c31\u662f\u6211\u4e0d\u597d\uff0c\u6211\u672c\u8be5\u597d\u597d\u4fdd\u62a4\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u94c3\u94c3\u5ffd\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u867d\u7136\u9a97\u4e86\u6211\uff0c\u6211\u5e76\u4e0d\u6068\u4ed6\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4ed6\u4e00\u5b9a\u4e5f\u4f1a\u5f97\u5230\u62a5 \u5e94\uff0c\u6bd4\u6211\u8981\u60e8\u5341\u500d\u7684\u62a5\u5e94\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u963f\u98de\u7a81\u7136\u7528\u529b\u63a8\u5f00\u4e86\u4ed6\u3002 \u3000\u3000\u963f\u98de\u77aa\u7740\u94c3\u94c3\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u5e26\u5415\u51e4\u5148\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u94c3\u94c3\u54ac\u7740\u5634\u5507\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u662f\u4ed6\u8981\u4f60\u5e26\u5415\u51e4\u5148\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u94c3\u94c3\u5ffd\u7136\u7528\u5c3d\u6700\u540e\u4e00\u5206\u529b\u6c14\uff0c\u5927\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4ed6\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u4ed6\u4e3a\u7684\u4ec0\u4e48 \uff1f\u4f60\u53ef\u77e5\u9053\u4ed6\u66fe\u7ecf\u4e3a\u4f60\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff1f\u4e3a\u4e86\u4f60\uff0c\u4ed6\u4e0d\u60dc\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u58f0\u97f3\u7a81\u7136\u5636\u88c2\u3002 \u3000\u3000\u5979\u547c\u5438\u5df2\u505c\u987f\u3002 \u3000\u3000\u9759\u5bc2\uff0c\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\uff0c\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u58f0\u97f3\u3002 \u3000\u3000\u82e5\u975e\u8fd8\u6709\u98ce\u5728\u5439\u52a8\uff0c\u8fde\u5927\u5730\u90fd\u4f3c\u5df1\u5931\u53bb\u4e86\u751f\u673a\uff0c\u53d8\u6210\u4e86\u4e00\u5ea7\u575f\u5893\uff0c\u53ef\u4ee5\u57cb\u846c\u6240\u6709\u751f\u547d\u7684 \u575f\u5893\u3002 \u3000\u3000\u4f46\u98ce\u4e5f\u662f\u51c4\u51c9\u7684\uff0c\u98ce\u58f0\u542c\u6765\u4e5f\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u963f\u98de\u624d\u5f90\u5f90\u7ad9\u76f4\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6ca1\u6709\u9762\u5bf9\u7740\u624b\u5bfb\u6b22\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u4e0d\u613f\u518d\u77a7\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u53ea\u662f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u674e\u5bfb\u6b22\u672c\u6765\u5f88\u5bb9\u6613\u56de\u7b54\uff0c\u4f46\u4ed6\u5374\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6709\u4e9b\u8bdd\u82e5\u662f\u8bf4\u4e86\u51fa\u6765\uff0c\u4e0d\u4f46\u4ee4\u81ea\u5df1\u4f24\u5fc3\uff0c\u4e5f\u4ee4\u522b\u4eba\u96be\u53d7\u3002 \u3000\u3000\u963f\u98de\u8fd8\u662f\u6ca1\u6709\u56de\u5934\uff0c\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u5979\u4f7f\u6211\u6d88\u6c89\u7684\uff1f\u4f60\u4ee5\u4e3a\u53ea\u8981\u5979\u79bb\u5f00\u4e86\u6211 \uff0c\u6211\u5c31\u4f1a\u632f\u4f5c\uff1f\u2026\u2026\u4f46\u4f60\u53ef\u77e5\u9053\uff0c\u6ca1\u6709\u4e86\u5979\uff0c\u6211\u6839\u672c\u6d3b\u4e0d\u4e0b\u53bb\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u4e0d\u88ab\u6b3a\u9a97\uff0c\u53ea\u5e0c\u671b\u4f60\u80fd\u627e\u5230\u4e2a\u4f60\u6240\u503c\u5f97\u7231\u7684\u4eba\uff0c\u90a3\u4e48\u2026\u2026 \u4f60\u4f1a\u5c06\u8fd9\u4e9b\u4e0d\u5e78\u7684\u4e8b\u5168\u90e8\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u80f8\u819b\u8d77\u4f0f\uff0c\u58f0\u97f3\u5df2\u6709\u4e9b\u6fc0\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u5979\u5728\u9a97\u6211\uff1f\u4f60\u8ba4\u4e3a\u5979\u4e0d\u503c\u5f97\u6211\u7231\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\uff0c\u81ea\u4ece\u4e00\u5f00\u59cb\uff0c\u5979\u5e26\u7ed9\u4f60\u7684\u5c31\u53ea\u6709\u4e0d\u5e78\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u53c8\u600e\u4e48\u77e5\u9053\u6211\u662f\u5e78\u798f\uff1f\u8fd8\u662f\u4e0d\u5e78\uff1f\u201d \u3000\u3000\u4ed6\u6dec\u7136\u8f6c\u8fc7\u8eab\uff0c\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u4e00\u5b9a\u8981\u5de6\u53f3\u6211\u7684\u601d\u60f3\uff0c\u4e3b \u5bb0\u6211\u7684\u547d\u8fd0\uff1f\u4f60\u6839\u672c\u4ec0\u4e48\u90fd\u4e0d\u662f\uff0c\u53ea\u662f\u4e2a\u81ea\u5df1\u9a97\u81ea\u5df1\u7684\u50bb\u5b50\uff0c\u4e0d\u60dc\u5c06\u81ea\u5df1\u5fc3\u7231\u7684\u4eba\u9020\u5165\u706b\u5751 \uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u505a\u5f97\u5f88\u9ad8\u5c1a\uff0c\u5f88\u4f1f\u5927\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\uff0c\u6bcf\u4e2a\u5b57\u90fd\u50cf\u662f\u4e00\u6839\u9488\u3002 \u3000\u3000\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u522b\u7684\u8bdd\u80fd\u66f4\u4f24\u674e\u5bfb\u6b22\u7684\u5fc3\u3002 \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u5979\u5e26\u7ed9\u6211\u7684\u662f\u4e0d\u5e78\uff0c\u4f60\u5462\uff1f\u4f60\u53c8\u5e26\u7ed9\u4eba\u4ec0\u4e48\uff1f\u6797\u8bd7\u97f3\u4e00\u751f\u7684\u5e78 \u798f\u5df1\u65ad\u9001\u5728\u4f60\u624b\u91cc\uff0c\u4f60\u8fd8\u4e0d\u6ee1\u8db3\uff1f\u8fd8\u60f3\u6765\u65ad\u9001\u6211\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u5728\u98a4\u6296\uff0c\u8fd8\u672a\u5f2f\u4e0b\u8170\uff0c\u5df2\u54b3\u51fa\u4e86\u8840\u3002 \u3000\u3000\u963f\u98de\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u6d82\u5f90\u8f6c\u8eab\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u54b3\u55fd\u8fd8\u672a\u505c\uff0c\u6323\u624e\u7740\u6251\u8fc7\u53bb\uff0c\u6321\u4f4f\u4e86\u95e8\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7528\u8863\u8896\u64e6\u4e86\u64e6\u5634\u89d2\u7684\u8840\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u53bb\u627e\u5979\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u7edd\u4e0d\u80fd\u53bb\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u8bf4\u7684\uff0c\u56e0\u4e3a\u5c31\u7b97\u4f60\u80fd\u5c06\u5979\u518d\u627e\u56de\u6765\uff0c\u4e5f\u53ea\u6709\u66f4\u75db\u82e6\uff0c\u5979\u8fdf\u65e9\u603b\u6709\u4e00\u5929\u8981 \u6bc1\u4e86\u4f60\u2026\u2026\u6211\u7edd\u4e0d\u80fd\u773c\u770b\u7740\u4f60\u6bc1\u5728\u8fd9\u79cd\u5973\u4eba\u624b\u4e0a\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u672c\u5df2\u63e1\u5f97\u5f88\u7d27\uff0c\u674e\u5bfb\u6b22\u6bcf\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4ed6\u5c31\u63e1\u5f97\u66f4\u7d27\u4e00\u5206\u3002 \u3000\u3000\u4ed6\u6307\u8282\u5df2\u56e0\u7528\u529b\u800c\u53d1\u767d\uff0c\u8138\u8272\u66f4\u82cd\u767d\uff0c\u53cc\u76ee\u4e2d\u5374\u5e03\u6ee1\u4e86\u7ea2\u4e1d\uff0c\u6b63\u5982\u4e00\u6761\u6761\u71c3\u70e7\u7684\u706b\u7130\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u5206\u5f00\uff0c\u4f60\u56fa\u7136\u96be\u514d\u75db\u82e6\u4e00\u65f6\uff0c\u4f46\u4f60\u4eec\u82e5\u5728\u4e00\u8d77\uff0c\u4f60\u5374\u8981\u75db\u82e6\u4e00\u751f \uff0c\u4f60\u522b\u7684\u4e8b\u90fd\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4f60\u8fd8\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\uff0c\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f46\u4ee5\u540e\u5374\u4e0d\u662f\u4e86\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u9762\u8272\u60e8\u53d8\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u53ef\u4ee5\u5fcd\u53d7\u4f60\u4fae\u8fb1\u6211\uff0c\u5374\u4e0d\u80fd\u5fcd\u53d7\u4f60\u4fae\u8fb1\u5979\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u60e8\u7136\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u662f\u5728\u4fae\u8fb1\u5979\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e00\u76f4\u5fcd\u53d7\u5230\u73b0\u5728\uff0c\u56e0\u4e3a\u6211\u4eec\u4e00\u76f4\u662f\u670b\u53cb\uff0c\u4f46\u4ee5\u540e\uff0c\u4f60\u82e5\u518d\u4fae\u8fb1\u5979\u4e00\u4e2a\u5b57\uff0c \u8fd9\u4fae\u8fb1\u5c31\u5f97\u8981\u7528\u8840\u6765\u6d17\u6e05\uff01\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u4e5f\u56e0\u6fc0\u52a8\u800c\u98a4\u6296\uff0c\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u65e0\u8bba\u662f\u4f60\u7684\u8840\u8fd8\u662f\u6211\u7684\u8840\uff0c\u90fd\u5f97\u7528\u8840\u6765\u6d17\u6e05 \uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4eff\u4f5b\u9aa4\u7136\u88ab\u4eba\u5f53\u80f8\u6253\u4e86\u4e00\u62f3\uff0c\u8e09\u8dc4\u540e\u9000\uff0c\u9000\u5230\u95e8\u8fb9\u3002 \u3000\u3000\u4ed6\u53c8\u5728\u54b3\u55fd\uff0c\u5374\u6ca1\u6709\u58f0\u97f3\uff0c\u56e0\u4e3a\u4ed6\u7684\u7259\u9f7f\u54ac\u5f97\u5f88\u7d27\uff0c\u5634\u4e5f\u95ed\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u9c9c\u8840\uff0c\u53c8\u4ece\u4ed6\u7d27\u95ed\u7740\u7684\u5634\u89d2\u6c81\u51fa\u3002 \u3000\u3000\u963f\u98de\u518d\u4e5f\u6ca1\u6709\u77a7\u4ed6\u4e00\u773c\uff0c\u560e\u58f0\u9053\uff1a\u201c\u73b0\u5728\u6211\u5c31\u53bb\u627e\u5979\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u627e\u5230\u5979\uff0c\u6211\u5e0c\u671b\u4f60 \u83ab\u8981\u8ddf\u6765\uff0c\u5343\u4e07\u83ab\u8981\u8ddf\u6765\uff0c\u5426\u5219\u4f60\u5fc5\u5c06\u540e\u6094\u7ec8\u751f\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5934\u4e5f\u4e0d\u56e4\u7684\u8d70\u4e86\u51fa\u53bb\uff01 \u3000\u3000\u773c\u6cea\u672c\u662f\u54b8\u7684\u3002 \u3000\u3000\u4f46\u6709\u4e9b\u6cea\u5374\u53ea\u80fd\u5f80\u809a\u91cc\u6d41\uff0c\u90a3\u5c31\u4e0d\u4f46\u54b8\uff0c\u800c\u4e14\u82e6\u3002 \u3000\u3000\u8840\uff0c\u672c\u4e5f\u662f\u54b8\u7684\u3002 \u3000\u3000\u4f46\u4e00\u4e2a\u4eba\u7684\u5fc3\u82e5\u788e\u4e86\uff0c\u81ea\u5fc3\u91cc\u6ef4\u51fa\u7684\u8840\uff0c\u5c31\u6bd4\u6cea\u66f4\u9178\u82e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u77e5\u9053\u5df2\u54b3\u4e86\u591a\u4e45\uff0c\u8863\u8896\u5df1\u88ab\u67d3\u7ea2\u3002 \u3000\u3000\u4ed6\u7684\u8170\u4f3c\u5df2\u65e0\u6cd5\u633a\u76f4\u3002 \u3000\u3000\u5730\u4e0a\u7684\u811a\u5370\uff0c\u662f\u8840\u67d3\u6210\u7684\u811a\u5370\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u60f3\u8d77\u4e86\u95e8\u5916\u90a3\u4e9b\u96f6\u4e71\u7684\u811a\u5370\uff0c\u4ed6\u638c\u5fc3\u7acb\u523b\u51b0\u51b7\u3002 \u3000\u3000\u963f\u98de\u4e00\u5b9a\u80fd\u627e\u5230\u5979\u3002 \u3000\u3000\u56e0\u4e3a\u6797\u4ed9\u513f\u4e00\u76f4\u4f1a\u6545\u610f\u7559\u4e0b\u4e9b\u7ebf\u7d22\uff0c\u8ba9\u4ed6\u627e\u5230\u3002\u4ed6\u5e76\u4e0d\u9700\u8981\u5927\u591a\u7684\u7ebf\u7d22\uff0c\u963f\u98de\u8840\u6db2\u91cc\u5929 \u751f\u5c31\u50cf\u662f\u6709\u79cd\u8ddf\u8e2a\u7684\u672c\u80fd\uff0c\u751a\u81f3\u6bd4\u91ce\u72ac\u8fd8\u7075\u654f\uff0c\u8fd8\u76f4\u63a5\u3002 \u3000\u3000\u4f46\u8ffd\u5230\u4e86\u4ee5\u540e\u5462\uff1f \u3000\u3000\u963f\u98de\u52bf\u5fc5\u8981\u548c\u5415\u98ce\u5148\u4e00\u51b3\u751f\u6b7b\u4e00\u4e00\u6797\u4ed9\u513f\u672c\u5c31\u559c\u6b22\u770b\u7537\u4eba\u4e3a\u5979\u62fc\u547d\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u638c\u5fc3\u5df2\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u963f\u98de\u73b0\u5728\u8fd8\u4e0d\u662f\u5415\u51e4\u5148\u7684\u5bf9\u624b\u3002 \u3000\u3000\u80fd\u6551\u963f\u98de\u547d\u7684\u4eba\uff0c\u53ea\u6709\u674e\u5bfb\u6b22\uff0c\u53ef\u662f\u2026\u2026 \u3000\u3000\u201c\u4f60\u5343\u4e07\u83ab\u8981\u8ddf\u6765\uff0c\u5426\u5219\u5c31\u5fc5\u5c06\u540e\u6094\u7ec8\u751f\uff01\u201d \u3000\u3000\u963f\u98de\u8bf4\u51fa\u7684\u8bdd\uff0c\u4e00\u5411\u6c38\u65e0\u66f4\u6539\uff01 \u3000\u3000\u4f55\u51b5\uff0c\u73b0\u5728\u591c\u8272\u66f4\u6df1\uff0c\u674e\u5bfb\u6b22\u53c8\u6ca1\u6709\u963f\u98de\u90a3\u79cd\u8ffd\u8e2a\u7684\u672c\u80fd\uff0c\u5c31\u7b97\u60f3\u53bb\u8ffd\uff0c\u4e5f\u5f88\u5c11\u6709\u673a\u4f1a \u80fd\u8ffd\u5230\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6323\u624e\u7740\uff0c\u7ad9\u8d77\uff0c\u5c06\u94c3\u94c3\u7684\u5c38\u8eab\u62b1\u4e0a\u5e8a\uff0c\u7528\u5e8a\u5355\u8986\u76d6\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u90fd\u8981\u8ffd\u53bb\uff0c\u4ed6\u5df2\u4e0b\u4e86\u51b3\u5fc3\u3002 \u3000\u3000\u5c31\u7b97\u963f\u98de\u5df2\u4e0d\u518d\u5c06\u4ed6\u5f53\u505a\u670b\u53cb\uff0c\u4f46\u4ed6\u4f9d\u65e7\u6c38\u8fdc\u662f\u963f\u98de\u7684\u670b\u53cb\uff0c\u4ed6\u7684\u53cb\u60c5\u7edd\u4e0d\u4f1a\u56e0\u4efb\u4f55\u4e8b \u800c\u66f4\u6539\u3002 \u3000\u3000\u90a3\u4e5f\u6b63\u5982\u4ed6\u7684\u7231\u60c5\u4e00\u6837\uff0c\u7eb5\u7136\u6d77\u67af\u77f3\u70c2\uff0c\u4ed6\u7684\u5fc3\u6c38\u4e0d\u4f1a\u53d8\u3002 \u3000\u3000\u201c\u8bd7\u97f3\uff0c\u8bd7\u97f3\uff0c\u4f60\u73b0\u5728\u6d3b\u5f97\u8fd8\u597d\u5417\uff1f\u201d \u7b2c\u516d\u5341\u56db\u7ae0\u3000\u7978\u6c34>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u56db\u7ae0\u3000\u7978\u6c34 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u60f3\u5230\u6797\u8bd7\u97f3\uff0c\u4ed6\u7684\u5fc3\u53c8\u662f\u4e00\u9635\u5267\u75db\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u4e0d\u60f3\u53bb\u627e\u5979\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u9f99\u5578\u4e91\u4e00\u5b9a\u4f1a\u597d\u597d\u7684\u7167\u987e\u7740\u5979\u2014\u2014\u9f99\u5578\u4e91\u867d\u5584\u53d8\uff0c\u5bf9\u6797 \u8bd7\u97f3\u7684\u5fc3\u5374\u672a\u53d8\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u5bf9\u6797\u8bd7\u97f3\u7684\u5fc3\u4e0d\u53d8\uff0c\u522b\u7684\u4e00\u5207\u4e8b\u5c31\u5168\u90e8\u53ef\u539f\u8c05\u3002 \u3000\u3000\u6b64\u523b\u9f99\u5578\u4e91\u7684\u5fc3\u60c5\uff0c\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u6109\u5feb\u3002 \u3000\u3000\u518d\u8fc7\u4e24\u4e09\u5929\uff0c\u4ed6\u5c31\u8981\u5750\u4e0a\u91d1\u94b1\u5e2e\u7684\u7b2c\u4e8c\u628a\u4ea4\u6905\uff0c\u6210\u4e3a\u5f53\u4eca\u5929\u4e0b\u6700\u6709\u52bf\u529b\u7684\u4eba\u7684\u7ed3\u62dc\u5144\u5f1f \u3002 \u3000\u3000\u5c31\u8fde\u9f99\u5c0f\u4e91\u7684\u6c14\u8272\u770b\u6765\u90fd\u50cf\u662f\u597d\u5f97\u591a\u4e86\uff1a \u3000\u3000\u552f\u4e00\u4ee4\u4ed6\u89c9\u5f97\u9057\u61be\u7684\uff0c\u662f\u4ed6\u7684\u59bb\u5b50\u3002 \u3000\u3000\u201c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u80af\u8ddf\u6211\u4e00\u9f50\u6765\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u80af\u5206\u4eab\u6211\u7684\u5149\u91c7\u3002\u201d \u3000\u3000\u4ed6\u62d2\u7edd\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u6700\u5927\u7684\u6b32\u671b\u662f\u91d1\u94b1\uff0c\u6709\u4e9b\u4eba\u6700\u5927\u7684\u6b32\u671b\u662f\u6743\u52bf\uff0c\u8fd9\u4e24\u79cd\u6b32\u671b\u82e5\u662f\u80fd\u6ee1\u8db3\uff0c\u60c5\u611f\u4e0a \u7684\u75db\u82e6\u5c31\u6de1\u4e86\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u6b63\u51dd\u89c6\u7740\u7a97\u5916\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u4e48\u5462\u3002 \u3000\u3000\u9f99\u5578\u4e91\u62cd\u4e86\u62cd\u4ed6\u80a9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u8fd9\u6b21\u4e0a\u5b98\u91d1\u8679\u4f1a\u4e0d\u4f1a\u4eb2\u81ea\u6765\u8fce\u63a5\u6211\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u56de\u8fc7\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u4f1a\uff0c\u800c\u4e14\u4eea\u5f0f\u4e00\u5b9a\u4f1a\u5f88\u9686\u91cd\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u4e5f\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u6211\u65e2\u662f\u4ed6\u7684\u5144\u5f1f\uff0c\u4ed6\u7ed9\u6211\u9762\u5b50\uff0c\u5c82\u975e\u4e5f\u6b63\u5982\u7ed9 \u81ea\u5df1\u9762\u5b50\u3002\u201d \u3000\u3000\u4ed6\u6c89\u541f\u4e86\u534a\u664c\uff0c\u5ffd\u53c8\u9053\uff1a\u201c\u4ed6\u6765\u63a5\u6211\u65f6\uff0c\u4f60\u60f3\u6211\u662f\u8be5\u79f0\u4ed6\u5e2e\u4e3b\uff1f\u8fd8\u662f\u8be5\u5524\u4ed6\u5927\u54e5\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5f53\u7136\u8be5\u79f0\u5927\u54e5\uff0c\u5b69\u513f\u4eca\u540e\u4e5f\u8981\u6539\u53e3\uff0c\u5524\u4ed6\u4e00\u58f0\u4f2f\u7236\u4e86\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u4ef0\u9762\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u6cca\u7236\uff0c\u771f\u662f\u4f60\u7684\u8fd0\u6c14\uff0c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u7a81\u53c8\u505c\u987f\uff0c\u76b1\u7709\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u65e2\u7136\u672a\u6b7b\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u98df\u8a00\u53cd\u6094\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u82f1\u96c4\u90fd\u5df2\u77e5\u9053\u6b64\u4e8b\uff0c\u5e16\u5b50\u4e5f\u65e9\u5c31\u53d1\u4e86\u51fa\u53bb\uff0c\u4ed6\u518d\u53cd\u6094\uff0c\u5c82\u975e\u81ea\u98df\u5176 \u8a00\uff0c\u4ee5\u540e\u8bf4\u7684\u8bdd\u8fd8\u6709\u8c01\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b66\u6797\u4e2d\u4eba\u4e4b\u6240\u4ee5\u4fe1\u670d\u4ed6\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4ee4\u51fa\u5982\u5c71\uff0c\u8a00\u51fa\u6cd5\u968f\uff0c \u73b0\u5728\u4ed6\u5c31\u7b97\u60f3\u53cd\u6094\uff0c\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u684c\u4e0a\u7684\u5377\u5b97\u975e\u4f46\u6ca1\u6709\u5c11\uff0c\u53cd\u800c\u4e00\u5929\u5929\u52a0\u591a\u3002 \u3000\u3000\u91d1\u94b1\u5e2e\u7ba1\u8f96\u7684\u8303\u56f4\uff0c\u5df2\u8d8a\u6765\u8d8a\u5e7f\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u8d23\u4efb\u4e5f\u7684\u786e\u8d8a\u6765\u8d8a\u91cd\uff0c\u56e0\u4e3a\u6bcf\u4ef6\u4e8b\u4ed6\u90fd\u8981\u81ea\u5df1\u6765\u51b3\u5b9a\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u4fe1\u4efb\u4efb\u4f55\u4eba\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u5df2\u5de5\u4f5c\u4e86\u4e94\u4e2a\u65f6\u8fb0\uff0c\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u505c\u8fc7\u4e8e\uff0c\u4f46\u4ed6\u975e\u4f46\u4e0d\u89c9\u5f97\u8f9b\u82e6\uff0c\u53cd\u800c\u89c9\u5f97\u8fd9 \u662f\u79cd\u5feb\u4e50\u3002\u201d \u3000\u3000\u95e8\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fde\u5934\u90fd\u6ca1\u6709\u62ac\uff0c\u56e0\u4e3a\u80fd\u76f4\u63a5\u8d70\u8fdb\u8fd9\u5c4b\u5b50\u7684\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8346\u65e0\u547d\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u548c\u5f80\u5e38\u4e00\u6837\uff0c\u4e00\u8d70\u8fdb\u6765\uff0c\u5c31\u7ad9\u5230\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u706f\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u5462\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6dec\u7136\u56de\u5934\uff0c\u77a7\u4e86\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u53ea\u77a7\u4e86\uff5e\u773c\uff0c\u76ee\u5149\u81ea\u4ed6\u65ad\u81c2\u4e0a\u6ed1\u843d\uff0c\u5c31\u53c8\u4f4e\u4e0b\u5934\uff0c\u505a\u81ea\u5df1\u7684\u4e8b\uff0c\u975e\u4f46\u6ca1\u6709\u518d\u8bf4\u4e00\u53e5\u8bdd\uff0c \u8138\u4e0a\u4e5f\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8346\u65e0\u547d\u9762\u4e0a\u4e5f\u5168\u65e0\u8868\u60c5\uff0c\u6b7b\u7070\u8272\u7684\u773c\u775b\u832b\u7136\u51dd\u6ce8\u7740\u8fdc\u65b9\u3002 \u3000\u3000\u4e00\u5207\u4e8b\u4eff\u4f5b\u90fd\u6ca1\u6709\u6539\u53d8\u3002 \u3000\u3000\u65e2\u6ca1\u6709\u8d23\u95ee\uff0c\u4e5f\u6ca1\u6709\u5b89\u6170\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u624b\u65ad\u4e86\u4e5f\u597d\uff0c\u817f\u65ad\u4e86\u4e5f\u597d\uff0c\u5374\u50cf\u662f\u548c\u4e0a\u5b98\u91d1\u8679\u5168\u65e0\u5173\u7cfb\u3002 \u3000\u3000\u53c8\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6709\u4eba\u62cd\u95e8\uff0c\u8bf7\u793a\u3002 \u3000\u3000\u53c8\u6709\u4e00\u5927\u5806\u5377\u5b97\u88ab\u9001\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6de1\u9ec4\u8272\u7684\u5377\u5b97\u4e2d\uff0c\u53ea\u6709\u4e00\u5c01\u4fe1\u662f\u7c89\u7ea2\u8272\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5148\u62bd\u51fa\u4e86\u8fd9\u5c01\u4fe1\uff0c\u4e5f\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u56e0\u4e3a\u4fe1\u4e0a\u53ea\u6709\u51e0\u4e2a\u5b57\uff1a\u201c\u8001\u5730\u65b9\u7b49\u5019\uff0c\u5415\u51e4 \u5148\u4e5f\u5728\u7b49\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9759\u9759\u7684\u7ad9\u7740\uff0c\u4f3c\u5728\u6c89\u601d\uff0c\u7136\u540e\u7acb\u523b\u4e0b\u4e86\u51b3\u5b9a\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u50cf\u5f71\u4e8e\u822c\u8ddf\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u51fa\u95e8\uff0c\u7a7f\u8fc7\u79d8\u9053\uff0c\u8d70\u51fa\u5bbd\u9614\u7684\u9662\u5b50\uff0c\u7a7f\u8fc7\u4e00\u4e2a\u5782\u9996\u8083\u7acb\u7684\u4f8d\u536b\uff0c\u8d70\u5230\u9633\u5149\u4e0b\u3002 \u3000\u3000\u6b8b\u79cb\u7684\u9633\u5149\u5c31\u50cf\u662f\u8fdf\u66ae\u7684\u5973\u4eba\uff0c\u5df2\u4e0d\u518d\u6709\u52a8\u4eba\u7684\u70ed\u529b\u3002 \u3000\u3000\u4e24\u4eba\u8fd8\u662f\u4e00\u524d\u4e00\u540e\u7684\u8d70\u7740\uff0c\u8d70\u7740\u2026\u2026\u8346\u65e0\u547d\u7a81\u7136\u53d1\u89c9\u4e0a\u5b98\u91d1\u8679\u7684\u811a\u6b65\u97f5\u5f8b\u5df2\u53d8\u4e86\u3002 \u3000\u3000\u8346\u65e0\u547d\u5df2\u65e0\u6cd5\u518d\u4e0e\u4ed6\u914d\u5408\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e5f\u5e76\u6ca1\u6709\u52a0\u5feb\uff0c\u4e5f\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u4e24\u4eba\u7684\u8ddd\u79bb\u5374\u5df2\u8d8a\u6765\u8d8a\u8fdc\uff0c\u8d8a\u6765\u8d8a\u8fdc\u2026\u2026 \u3000\u3000\u8346\u65e0\u547d\u7684\u811a\u6b65\u6e10\u7f13\uff0c\u7ec8\u4e8e\u505c\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5e76\u6ca1\u6709\u53e3\u5934\u3002 \u3000\u3000\u671b\u7740\u4ed6\u9010\u6e10\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u775b\u91cc\uff0c\u6e10\u6e10\u9732\u51fa\u4e86\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\uff0c\u6df1\u901f\u7684 \u60b2\u75db\u2026\u2026 \u3000\u3000\u5bc6\u6797\u3002\u677e\u6797\u3002 \u3000\u3000\u677e\u6797\u5e38\u9752\uff0c\u9633\u5149\u7ec8\u5e74\u90fd\u7167\u4e0d\u8fdb\u8fd9\u677e\u6797\u3002 \u3000\u3000\u6797\u95ee\u867d\u9edd\u6697\uff0c\u5374\u4e0d\u6f6e\u6e7f\uff0c\u98ce\u4e2d\u4e5f\u5e26\u7740\u677e\u6728\u7684\u6e05\u9999\u3002 \u3000\u3000\u6797\u4ed9\u513f\u659c\u501a\u5728\u6811\u4e0a\uff0c\u7d27\u63e1\u7740\u5415\u51e4\u5148\u7684\u624b\uff0c\u59cb\u7ec8\u6ca1\u6709\u653e\u5f00\uff0c\u90a3\u65e0\u6bd4\u6e29\u67d4\u7684\u773c\u6ce2\uff0c\u4e5f\u59cb\u7ec8\u6ca1 \u6709\u79bb\u5f00\u8fc7\u5415\u51e4\u5148\u7684\u8138\u3002 \u3000\u3000\u5415\u51e4\u5148\u7684\u8138\u66f4\u82cd\u767d\uff0c\u773c\u89d2\u7684\u76b1\u7eb9\u4e5f\u50cf\u662f\u591a\u4e86\u4e9b\u3002 \u3000\u3000\u79cb\u98ce\u5165\u4e86\u6797\uff0c\u4e5f\u53d8\u5f97\u6e29\u67d4\u8d77\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u540e\u6094\u4e48\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u540e\u6094\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u540e\u6094\uff1f\u6709\u4e86\u4f60\uff0c\u4efb\u4f55\u7537\u4eba\u90fd\u4e0d\u4f1a\u89c9\u5f97\u540e\u6094\u3002 \u201d \u3000\u3000\u6797\u4ed9\u513f\u201c\u6a31\u549b\u201d\u4e00\u58f0\uff0c\u5012\u5165\u4ed6\u6000\u91cc\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u771f\u7684\u90a3\u4e48\u597d\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u6402\u7740\u5979\u7684\u8170\u80a2\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u7136\u597d\uff0c\u6bd4\u6211\u60f3\u8c61\u4e2d\u8fd8\u597d\uff0c\u6bd4\u4efb\u4f55\u4eba\u60f3\u8c61\u4e2d\u90fd\u8981\u597d\u2026 \u2026\u201d \u3000\u3000\u4ed6\u7684\u624b\u5411\u4e0a\u79fb\u52a8\uff0c\u53c8\u5411\u4e0b\u2026\u2026 \u3000\u3000\u6797\u4ed9\u513f\u7684\u547c\u5438\u5f00\u59cb\u6025\u4fc3\uff0c\u5a07\u5598\u7740\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u89d2\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd8\u8981\u7559\u7740\u529b\u6c14\u5bf9\u4ed8\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u5979\u8eab\u5b50\u5de7\u5999\u7684\u626d\u52a8\u7740\uff0c\u4eff\u4f5b\u5728\u95ea\u907f\uff0c\u53c8\u4eff\u4f5b\u5728\u8fce\u51d1\u2026\u2026 \u3000\u3000\u5415\u51e4\u5148\u7684\u624b\u505c\u4e86\u505c\uff0c\u5374\u53c8\u5f00\u59cb\u79fb\u52a8\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u201c\u6211\u5bf9\u4ed8\u4e86\u4f60\uff0c\u8fd8\u53ef\u4ee5\u518d\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u5343\u4e07\u83ab\u8981\u770b\u8f7b\u4e86\u4ed6\uff0c\u4ed6\u7edd\u4e0d\u5982\u4f60\u60f3\u8c61\u4e2d\u90a3\u4e48\u597d\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u4e0d\u5982\u6c60\u5f3a\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5979\u8f7b\u54ac\u7740\u5415\u51e4\u5148\u7684\u8033\u6735\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u53ea\u8981\u6740\u4e86\u4e0a\u5b98\u91d1\u8679\uff0c\u65e0\u4e0b\u5c31\u90fd\u662f\u6211\u4eec\u7684\u4e86\uff0c\u4ee5\u540e \u6211\u4eec\u7684\u65e5\u5b50\u8fd8\u957f\u7740\u54e9\uff0c\u4f60\u73b0\u5728\u4f55\u5fc5\u7740\u6025\u3002\u201d \u3000\u3000\u4eb2\u5bc6\u7684\u8033\u8bed\uff0c\u5728\u6e05\u98ce\u4e2d\u4f3c\u5df2\u5316\u4f5c\u6b4c\u66f2\u3002 \u3000\u3000\u5415\u51e4\u5148\u7684\u5fc3\u5df2\u8f6f\u4e86\uff0c\u624b\u5374\u6402\u5f97\u66f4\u7d27\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u771f\u76fc\u8fd9\u4e48\u5173\u5fc3\u6211\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u7a81\u7684\u505c\u987f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e5f\u7a81\u7136\u79bb\u5f00\u4e86\u4ed6\u7684\u6000\u62b1\u3002 \u3000\u3000\u5bc6\u6797\u4e2d\u5df2\u4f20\u6765\u4e00\u9635\u5947\u7279\u7684\u811a\u6b65\u2014\u2014\u5176\u5b9e\u8fd9\u811a\u6b65\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u5947\u7279\u4e4b\u5904\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0 \u4e48\uff0c\u5374\u4ee4\u4eba\u542c\u6765\u6bcf\u4e00\u6b65\u90fd\u50cf\u662f\u8e0f\u5728\u81ea\u5df1\u5fc3\u4e0a\u3002 \u3000\u3000\u811a\u6b65\u58f0\u5df2\u505c\u987f\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5c31\u7ad9\u5728\u90a3\u8fb9\u4e00\u682a\u677e\u6811\u7684\u9634\u5f71\u4e0b\uff0c\u9759\u9759\u7684\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u5ea7\u51b0\u5c71 \u3002 \u3000\u3000\u9ad8\u4e0d\u53ef\u6500\u7684\u51b0\u5c71\u3002 \u3000\u3000\u5415\u51e4\u5148\u7684\u547c\u5438\u7a81\u7136\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u4e00\u5b57\u5b57\u95ee\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fd8\u662f\u6234\u7740\u9876\u5927\u7af9\u7b20\uff0c\u538b\u4f4f\u4e86\u7709\u76ee\uff0c\u9053\uff1a\u201c\u5415\u51e4\u5148\uff1f\u201d \u3000\u3000\u4ed6\u975e\u4f46\u6ca1\u6709\u56de\u7b54\uff0c\u800c\u4e14\u8fd8\u53cd\u95ee\u3002 \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u56de\u7b54\u4e86\u3002 \u3000\u3000\u4ed6\u56de\u7b54\u4e86\u540e\uff0c\u5c31\u7acb\u523b\u540e\u6094\uff0c\u56e0\u4e3a\u4ed6\u81ea\u89c9\u5728\u6c14\u52bf\u4e0a\u5df2\u5f31\u4e86\u4e00\u5206\uff0c\u4e0a\u5b98\u91d1\u8679\u5df2\u5360\u53d6\u4e86\u4e3b\u52a8\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4f3c\u4e4e\u7b11\u4e86\u7b11\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5415\u51e4\u5148\u603b\u7b97\u8fd8\u503c\u5f97\u6211\u51fa\u624b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u975e\u4e0a\u5b98\u91d1\u8679\uff0c\u6211\u4e5f\u4e0d\u5c51\u6740\u4f60\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u53c8\u540e\u6094\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u867d\u4e5f\u5145\u6ee1\u4e86\u51b7\u505a\u4e4b\u610f\uff0c\u4f46\u542c\u6765\u5374\u50cf\u662f\u8ddf\u4e0a\u5b98\u91d1\u8679\u5b66\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u76ee\u5149\u7a81\u7136\u81ea\u7b20\u6a90\u4e0b\u5c04\u51fa\u626b\u5411\u6797\u4ed9\u513f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8fd8\u7b80\u7740\u90a3\u68f5\u6811\uff0c\u6e29\u67d4\u7684\u773c\u6ce2\u5df2\u6e10\u6e10\u53d8\u5f97\u70bd\u70ed\u2014\u2014 \u3000\u3000\u5979\u77e5\u9053\u5f88\u7094\u5c31\u8981\u770b\u5230\u8840\u3002 \u3000\u3000\u5979\u559c\u6b22\u770b\u7537\u4eba\u4eec\u4e3a\u5979\u6d41\u8840\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u9053\uff1a\u201c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u4eff\u4f5b\u6014\u4e86\u6014\uff0c\u77a7\u4e86\u5415\u51e4\u5148\u4e00\u773c\uff0c\u76ee\u5149\u79fb\u5411\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u5415\u51e4\u5148\u51b7\u7b11\u9053\uff1a\u201c\u5979\u7edd\u4e0d\u4f1a\u8fc7\u53bb\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53c8\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u76ee\u5149\u53c8\u79fb\u5411\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u5979\u77e5\u9053\u73b0\u5728\u5df2\u5fc5\u987b\u5728\u4e24\u4eba\u4e4b\u95f4\u4f5c\u4e00\u4e2a\u9009\u62e9\u3002 \u3000\u3000\u8fd9\u5c31\u50cf\u662f\u5728\u62bc\u5b9d\uff0c\u8fd9\u4e00\u6ce8\u5979\u5fc5\u987b\u8981\u62bc\u5728\u80dc\u7684\u90a3\u4e00\u9762\u3002 \u3000\u3000\u4f46\u80dc\u7684\u4f1a\u662f\u8c01\u5462\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fd8\u662f\u9759\u9759\u7684\u7ad9\u7740\uff0c\u4eff\u4f5b\u5145\u6ee1\u4e86\u81ea\u4fe1\u3002 \u3000\u3000\u5415\u51e4\u5148\u7684\u547c\u5438\u5374\u5df2\u6709\u4e9b\u4e0d\u5300\uff0c\u4f3c\u4e4e\u5df2\u6709\u4e9b\u4e0d\u5b89\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7a81\u7136\u5411\u4ed6\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u4ed6\u521a\u5728\u6697\u4e2d\u5410\u4e86\u53e3\u6c14\uff0c\u6797\u4ed9\u513f\u5374\u5df2\u71d5\u5b50\u822c\u6295\u5411\u4e0a\u5b98\u91d1\u8679\uff01 \u3000\u3000\u5979\u7ec8\u4e8e\u4f5c\u4e86\u9009\u62e9\u3002 \u3000\u3000\u5979\u76f8\u4fe1\u81ea\u5df1\u7edd\u4e0d\u4f1a\u9009\u9519\uff01 \u3000\u3000\u5415\u51e4\u5148\u7684\u77b3\u5b54\u5728\u6536\u7f29\uff0c\u5fc3\u4e5f\u5728\u6536\u7f29\u3002 \u3000\u3000\u751f\u5e73\u7b2c\u4e00\u6b21\uff0c\u4ed6\u5ffd\u7136\u5c1d\u5230\u4e86\u7f9e\u4fae\u7684\u6ecb\u5473\uff0c\u4e5f\u5ffd\u7136\u5c1d\u5230\u4e86\u5931\u8d25\u7684\u6ecb\u5473\u2014\u2014\u8fd9\u662f\u53cc\u91cd\u7684\u75db\u82e6 \uff01 \u3000\u3000\u8fd9\u4e5f\u662f\u53cc\u91cd\u7684\u6253\u51fb\uff0c\u4ed6\u7684\u201c\u81ea\u5c0a\u201c\u548c\u201d\u81ea\u4fe1\u201c\u90fd\u5df2\u88ab\u6253\u5f97\u7c89\u788e\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4f3c\u5df2\u5728\u53d1\u6296\u3002\uff1a \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u5df2\u8d25\u4e86\uff01\u201d \u3000\u3000\u5415\u51e4\u5148\u7684\u624b\u6296\u5f97\u66f4\u5267\u70c8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u56e0\u4e3a\u4f60\u5df2\u4e0d\u503c\u5f97\u6211\u51fa\u624b\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\uff0c\u5927\u6b65\u8d70\u51fa\u677e\u6797\u2026\u2026 \u3000\u3000\u6797\u4ed9\u513f\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u56de\u7738\u5411\u5415\u51e4\u5148\u4e00\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u529d\u4f60\u4e0d\u5982\u8fd8\u662f\u6b7b \u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6218\u5415\u51e4\u5148\u8fd8\u672a\u51fa\u624b\uff0c\u5c31\u5df2\u8d25\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5148\u5df2\u627f\u8ba4\u81ea\u5df1\u8d25\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u4ed6\u867d\u672a\u6d41\u8840\uff0c\u4f46\u6574\u4e2a\u751f\u547d\u4e0e\u7075\u9b42\u5374\u5df2\u5168\u88ab\u6467\u6bc1\uff0c\u4fe1\u5fc3\u548c\u52c7\u6c14\u4e5f\u5df2\u88ab\u6467\u6bc1\u3002 \u3000\u3000\u671b\u7740\u4e0a\u5b98\u91d1\u8679\u8d70\u51fa\u677e\u6797\uff0c\u4ed6\u7adf\u6ca1\u6709\u52c7\u6c14\u8ffd\u51fa\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u867d\u672a\u51fa\u624b\uff0c\u5374\u5df2\u65e0\u5f02\u593a\u53bb\u4e86\u4ed6\u7684\u751f\u547d\u3002 \u3000\u3000\u201c\u6211\u529d\u4f60\u4e0d\u5982\u8fd8\u662f\u6b7b\u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u6d3b\u7740\uff0c\u7684\u786e\u5df2\u5f88\u65e0\u8da3\u4e86\u3002 \u3000\u3000\u5415\u51e4\u5148\u7a81\u7136\u6251\u5012\u5728\u5730\u4e0a\uff0c\u5931\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8d76\u4e0a\u53bb\uff0c\u62c9\u4f4f\u4e0a\u5b98\u91d1\u8679\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u771f\u7684\u773c\u4e86\u4f60\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8346\u65e0\u547d\u6740\u4eba\u51fa\u624b\u867d\u7136\u5feb\uff0c\u4f46\u4f60\u5374\u6bd4\u4ed6\u66f4\u5feb\u5341\u500d\uff01\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u4f60\u6740\u4eba\u6839\u672c \u7528\u4e0d\u7740\u51fa\u624b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u5230\u73b0\u5728\u6211\u8fd8\u672a\u9047\u7740\u4e00\u4e2a\u4eba\u914d\u6211\u51fa\u624b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u60a0\u60a0\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u80fd\u4ee4\u4f60\u51fa\u624b\u7684\u4eba\u786e\u5b9e\u4e0d\u591a\u2026\u2026\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u674e\u5bfb\u6b22\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u80fd\u5012\u4e0b\u53bb\uff0c\u53c8\u597d\u50cf\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5012\u4e0b\u53bb\uff0c\u6709\u65f6 \u5019\u6211\u5b9e\u5728\u60f3\u4e0d\u9002\u4ed6\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff0c\u541b\u5b50\uff1f\u5446\u5b50\uff1f\u8fd8\u662f\u82f1\u96c4\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u597d\u50cf\u4e00\u76f4\u90fd\u5f88\u6709\u5174\u8da3\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u5bf9\u4ed6\u6709\u5174\u8da3\uff0c\u56e0\u4e3a\u6211\u4e0d\u613f\u6b7b\u5728\u4ed6\u624b\u4e0a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5bf9\u81ea\u5df1\u7684\u60c5\u4eba\u5c31\u7b97\u518d\u6709\u5174\u8da3\uff0c\u65e5\u5b50\u4e45\u4e86\uff0c\u4e5f\u4f1a\u6e10\u6e10\u53d8\u6de1\u7684\uff0c\u4f46\u5bf9\u81ea \u5df1\u7684\u654c\u4eba\uff0c\u53cd\u800c\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u5979\u4ef0\u9762\u51dd\u6ce8\u7740\u4e0a\u5b98\u91d1\u8679\uff0c\u9053\uff1a\u201c\u8fd9\u9053\u7406\u6211\u60f3\u4f60\u4e00\u5b9a\u6bd4\u8c01\u90fd\u660e\u767d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5174\u8da3\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u4f60\u662f\u6068\u4ed6\uff1f\u4e00\u6015\u4ed6\uff1f\u8fd8\u662f\u7231\u4ed6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u597d\u50cf\u4e5f\u6e10\u6e10\u53d8\u5f97\u4f1a\u5403\u918b\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u963f\u98de\u5462\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ae3\u7136\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u4e5f\u4f1a\u5403\u918b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u53ea\u662f\u5728\u95ee\u4f60\uff0c\u4f60\u4e3a\u4f55\u4e0d\u6740\u4ed6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u95ee\u4f60\uff0c\u8346\u65e0\u547d\u4e3a\u4f55\u4e0d\u6740\u4ed6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u672c\u8981\u4f60\u81ea\u5df1\u4e0b\u624b\u7684\uff0c\u4f60\u96be\u9053\u4e0d\u5fcd\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u8981\u6740\u4eba\u5f88\u5bb9\u6613\uff0c\u82e5\u8981\u4e00\u4e2a\u4eba\u7518\u5fc3\u542c\u4f60\u7684\u8bdd\uff0c\u90a3\u5c31\u56f0\u96be\u591a\u4e86\uff0c\u5230\u73b0 \u5728\u4e3a\u6b62\uff0c\u6211\u8fd8\u6ca1\u6709\u627e\u5230\u4e00\u4e2a\u50cf\u4ed6\u90a3\u4e48\u6837\u542c\u8bdd\u7684\u4eba\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u5012\u4eba\u4e0a\u5b98\u91d1\u8679\u6000\u91cc\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u6765\u627e\u4f60\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u8ddf\u4f60\u5435\u67b6\uff0c\u4f60\u82e5\u771f\u7684\u8981 \u6211\u6740\u4ed6\uff0c\u4ee5\u540e\u7684\u673a\u4f1a\u8fd8\u591a\u7684\u662f\uff0c\u6211\u4e00\u5b9a\u542c\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5bf9\u5979\u53d1\u813e\u6c14\u3002 \u3000\u3000\u5979\u5c31\u50cf\u662f\u4e00\u6761\u6700\u4e56\u7684\u5c0f\u732b\uff0c\u5c31\u7b97\u5076\u800c\u4f1a\u7528\u722a\u5b50\u6293\u6293\u4f60\uff0c\u4f46\u4f60\u8fd8\u6ca1\u6709\u611f\u89c9\u5230\u75bc\u7684\u65f6\u5019\uff0c\u5979 \u5df2\u7ecf\u5728\u7528\u820c\u5934\u8214\u7740\u4f60\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51dd\u89c6\u7740\u5979\u7684\u8138\u3002 \u3000\u3000\u5979\u7684\u8138\u5728\u6de1\u6de1\u7684\u5915\u9633\u4e0b\u770b\u6765\uff0c\u4eff\u4f5b\u7528\u4e8e\u6307\u8f7b\u8f7b\u4e00\u89e6\u5c31\u4f1a\u7834\u3002\u8fde\u6e29\u67d4\u7684\u6625\u98ce\u4e5f\u6bd4\u4e0d\u4e0a\u5979\u7684 \u547c\u5438\u2026\u2026 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u5934\u4e5f\u6e10\u6e10\u5782\u4e0b\u2026\u2026 \u3000\u3000\u4ed6\u7684\u5634\u5507\u5df2\u5c06\u89e6\u53ca\u5979\uff0c\u5979\u7a81\u7136\u4ece\u4ed6\u6000\u62b1\u4e2d\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u77b3\u5b54\u4e5f\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\u6536\u7f29\u4e86\u8d77\u6765\uff0c\u4f46\u4ed6\u7684\u59ff\u52bf\u8fd8\u662f\u6ca1\u6709\u53d8\uff0c\u8fde\u6307\u5c16\u90fd\u6ca1 \u6709\u52a8\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u53bb\u77a7\u6797\u4ed9\u513f\u4e00\u773c\uff0c\u53ea\u662f\u51b7\u51b7\u7684\u77a7\u7740\u9762\u524d\u4e00\u7247\u5df2\u67af\u9ec4\u7684\u8349\u5730\u3002 \u3000\u3000\u5730\u4e0a\u4ec0\u4e48\u4e5f\u6ca1\u6709\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6f2b\u7684\u73b0\u51fa\u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u6709\u4eba\u6765\u4e86\uff01 \u3000\u3000\u5915\u9633\u5c06\u8fd9\u4eba\u7684\u5f71\u5b50\u62d6\u5f97\u5f88\u957f\u3002 \u3000\u3000\u6ca1\u6709\u811a\u6b65\u58f0\uff0c\u8fd9\u4eba\u7684\u811a\u6b65\u58f0\u8f7b\u5f97\u5c31\u50cf\u662f\u4e00\u5339\u6b63\u5728\u730e\u98df\u7684\u72d0\u72f8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fd8\u662f\u6ca1\u6709\u56de\u5934\uff0c\u5012\u5728\u5730\u4e0a\u7684\u6797\u4ed9\u513f\u5374\u5df2\u5f00\u59cb\u5728\u547b\u541f\u3002 \u3000\u3000\u4eba\u5f71\u66f4\u8fd1\u4e86\uff0c\u5c31\u505c\u5728\u4e0a\u5b98\u91d1\u8679\u8eab\u540e\u3002 \u3000\u3000\u4e00\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e0d\u5728\u80cc\u540e\u6740\u4eba\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u5374\u4e5f\u662f\u4f8b\u5916\uff01\u201d \u3000\u3000\u8fd9\u4eba\u7684\u58f0\u97f3\u672c\u662f\u51b7\u9177\u800c\u575a\u5b9a\u7684\uff0c\u6b64\u523b\u5374\u5df1\u56e0\u7d27\u5f20\u4e0e\u6124\u6012\u800c\u53d1\u6296\u3002 \u3000\u3000\u8fd9\u7684\u786e\u662f\u79cd\u51c6\u5907\u8981\u6740\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u975e\u4f46\u795e\u8272\u4e0d\u53d8\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5730\u4e0a\u7684\u4eba\u5f71\uff0c\u624b\u5df2\u62ac\u8d77\u3002 \u3000\u3000\u624b\u91cc\u6709\u5251\uff0c\u5251\u5374\u8fdf\u8fdf\u672a\u523a\u51fa\uff0c\u7a81\u7136\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u56de\u5934\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u5728\u80cc\u540e\u6740\u4eba\uff0c\u4e5f\u4e00\u6837\u80fd\u6740\u5f97\u6b7b\u7684\uff0c\u53c8\u4f55\u5fc5\u56de\u5934\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u547b\u541f\u58f0\u4e5f\u5df2\u505c\u6b62\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u773c\u775b\u5df2\u5f20\u5f00\uff0c\u7a81\u7136\u5931\u58f0\u800c\u547c\uff1a\u201c\u963f\u98de\uff01\u201d \u3000\u3000\u547c\u58f0\u4e2d\u5979\u5df2\u81ea\u4e0a\u5b98\u91d1\u8679\u8eab\u65c1\u51b2\u4e86\u8fc7\u53bb\uff0c\u5979\u7684\u5f71\u5b50\u7acb\u523b\u548c\u5730\u4e0a\u7684\u4eba\u5f71\u4ea4\u53e0\u5728\u4e00\u9f50\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51dd\u6ce8\u7740\u5730\u4e0a\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u5ffd\u7136\u5f00\u59cb\u6162\u6162\u7684\u5411\u524d\u8d70\u2026\u2026\u6162\u6162\u7684\u8e29\u4e0a\u4e86\u8fd9\u4e24\u6761\u4eba\u5f71 \u3002 \u3000\u3000\u963f\u98de\u624b\u91cc\u7684\u5251\u5df2\u8dcc\u4e0b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u6b63\u53cd\u53cd\u590d\u590d\u7684\u4f4e\u8bed\uff1a\u201c\u4f60\u679c\u7136\u6765\u4e86\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u7684\u2026\u2026\u201d \u3000\u3000\u5c31\u53ea\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5979\u5df2\u4e0d\u77e5\u8bf4\u4e86\u591a\u5c11\u904d\uff0c\u6bcf\u8bf4\u4e00\u904d\uff0c\u5979\u7684\u58f0\u97f3\u5c31\u4f1a\u53d8\u5f97\u66f4\u8f7b\u3001\u66f4\u7f13\u3001\u66f4\u67d4 \u548c\u3001\u66f4\u751c\u7f8e\u3002 \u3000\u3000\u8fd9\u79cd\u58f0\u97f3\u8db3\u4ee5\u4ee4\u51b0\u5c71\u878d\u5316\u3002 \u3000\u3000\u963f\u98de\u7684\u5fc3\u6b63\u5728\u878d\u5316\uff0c\u6240\u6709\u7684\u7d27\u5f20\u3001\u6124\u6012\u3001\u4ec7\u6068\u90fd\u5df2\u878d\u5316\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u56de\u53bb\u89c1\u4e0d\u5230\u6211\uff0c\u4e00\u5b9a\u4f1a\u5f88\u7740\u6025\uff0c\u4e00\u5b9a\u4f1a\u627e\u6211\u3002\u201d \u3000\u3000\u770b\u5230\u963f\u98de\u82cd\u767d\u6a35\u6dec\u7684\u8138\uff0c\u5979\u773c\u5708\u4e5f\u7ea2\u4e86\uff0c\u51c4\u7136\u9053\uff1a\u201c\u4e3a\u4e86\u627e\u6211\uff0c\u4f60\u4e00\u5b9a\u5403\u4e86\u4e0d\u5c11\u82e6\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u58f0\u97f3\u4e5f\u5df2\u6709\u4e9b\u786c\u54bd\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5df2\u627e\u5230\u4f60\uff0c\u8fd9\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u4e0d\u9519\uff0c\u53ea\u8981\u80fd\u627e\u5230\u5979\uff0c\u65e0\u8bba\u8981\u591a\u5927\u7684\u4ee3\u4ef7\uff0c\u4ed6\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u53ea\u8981\u80fd\u627e\u5230\u5979\uff0c\u65e0\u8bba\u4ec0\u4e48\u4ed6\u90fd\u53ef\u5fcd\u53d7\u3002 \u3000\u3000\u201c\u6211\u5df2\u627e\u5230\u4f60\uff0c\u8fd9\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u4e5d\u4e2a\u5b57\uff0c\u53ea\u6709\u77ed\u77ed\u7684\u4e5d\u4e2a\u5b57\uff0c\u4f46\u8fd9\u4e5d\u4e2a\u5b57\u4e2d\u5305\u542b\u7684\u60c5\u610f\uff0c\u7eb5\u7136\u7528\u4e5d\u5341\u4e07\u4e2a\u5b57\uff0c\u4e5f\u672a\u5fc5\u80fd \u5b8c\u5168\u63cf\u8ff0\u5f97\u51fa\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5251\u5149\u4e00\u95ea\u3002 \u3000\u3000\u8dcc\u843d\u5728\u5730\u4e0a\u7684\u5251\u7a81\u7136\u88ab\u6311\u8d77\uff0c\u5251\u5149\u5982\u7075\u86c7\u7684\u4e00\u95ea\uff0c\u843d\u5165\u4e86\u4e00\u4e2a\u4eba\u7684\u624b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e0d\u77e5\u4f55\u65f6\u5df2\u6765\u5230\u4ed6\u4eec\u9762\u524d\u3002 \u3000\u3000\u4ed6\u51b7\u6f20\u7684\u76ee\u5149\u51dd\u6ce8\u7740\u5251\u950b\u2014\u2014\u8fd9\u53ea\u4e0d\u8fc7\u662f\u67c4\u5f88\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u662f\u963f\u98de\u5728\u534a\u9014\u4e2d\u4ece\u4e00\u5177\u9556 \u5ba2\u8eab\u4e0a\u201c\u501f\u201d\u6765\u7684\u3002 \u3000\u3000\u4f46\u4e0a\u5b98\u91d1\u8679\u5374\u50cf\u662f\u5bf9\u8fd9\u67c4\u5251\u5f88\u6709\u5174\u8da3\u3002 \u3000\u3000\u53ea\u8981\u6709\u6797\u4ed9\u513f\u5728\u8eab\u4fa7\uff0c\u5c31\u6ca1\u6709\u522b\u7684\u4e8b\u518d\u80fd\u5438\u5f15\u963f\u98de\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u518d\u60f3\u8d77\u8fd9\u91cc\u8fd8\u6709\u4e2a\u4eba\u2014\u2014\u4ed6\u672c\u6765\u60f3\u6740\u7684\u4eba\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7684\u5251\u5374\u5df2\u5230\u4e86\u8fd9\u4eba\u624b\u4e0a\u3002\u4e00\u53ea\u7a33\u5b9a\u5f97\u51fa\u5947\u7684\u624b\uff0c\u8fd9\u79cd\u624b\u53ea\u8981\u63e1\u4f4f\u4e86\u5251\u67c4\uff0c\u5c31\u968f\u65f6 \u90fd\u53ef\u80fd\u5c06\u5251\u950b\u9001\u5165\u522b\u4eba\u7684\u5fc3\u810f\u3002 \u3000\u3000\u8fd9\u67c4\u5e73\u51e1\u7684\u9752\u94a2\u5251\u4f3c\u4e5f\u7a81\u7136\u53d8\u5f97\u6709\u4e86\u5251\u6c14\uff0c\u6740\u6c14\uff01 \u3000\u3000\u963f\u98de\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u77a7\u4ed6\u4e00\u773c\uff0c\u51b7\u6f20\u7684\u76ee\u5149\u8fd8\u505c\u7559\u5728\u5251\u950b\u4e0a\uff0c\u5634\u89d2\u4eff\u4f5b\u5e26\u7740\u4e00\u4e1d \u5fae\u7b11\uff0c\u8f7b\u8511\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u6de1\u6de1\u7b11\u7740\uff1a\u201c\u4f60\u5c31\u60f3\u7528\u8fd9\u67c4\u5251\u6765\u6740\u6211\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u4e0d\u80fd\u6740\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u5251\uff0c\u90fd\u53ef\u4ee5\u6740\u4eba\u7684\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u8fd9\u5374\u4e0d\u662f\u4f60\u7528\u7684\u5251\uff0c\u4f60\u82e5\u7528\u8fd9\u67c4\u5251\uff0c\u53ea\u80fd\u6740\u5f97\u6b7b\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u5251\u5149\u53c8\u4e00\u95ea\uff0c\u5251\u5df2\u5012\u8f6c\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u624b\u634f\u7740\u5251\u5c16\uff0c\u5c06\u5251\u67c4\u9012\u4e86\u8fc7\u53bb\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u867d\u672a\u4f38\u51fa\uff0c\u81c2\u4e0a\u7684\u808c\u8089\u5df2\u7d27\u5f20\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u5728\u8fd9\u4eba\u9762\u524d\uff0c\u59cb\u7ec8\u603b\u662f\u88ab\u52a8\u7684\uff0c\u5728\u522b\u4eba\u9762\u524d\u4ed6\u672a\u6709\u8fc7\u8fd9\u79cd\u611f\u89c9\uff0c\u8fd9\u79cd\u611f \u89c9\u4ee4\u4ed6\u7d27\u5f20\u5f97\u8fde\u80c3\u90fd\u4f3c\u4e4e\u5728\u6536\u7f29\uff0c\u4f3c\u5df2\u8981\u5455\u5410\u3002 \u3000\u3000\u4f46\u4ed6\u53c8\u600e\u80fd\u4e0d\u5c06\u8fd9\u67c4\u5251\u63a5\u8fc7\u6765\uff1f \u3000\u3000\u4ed6\u7684\u624b\u7ec8\u4e8e\u4f38\u51fa\uff0c\u521a\u4f38\u51fa\uff0c\u5251\u67c4\u5df2\u88ab\u53e6\u4e00\u53ea\u624b\u62a2\u4e86\u8fc7\u53bb\u2014\u2014\u4e00\u53ea\u67d4\u82e5\u65e0\u9aa8\u3001\u6625\u8471\u822c\u7684\u624b \u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u773c\u4e2d\u4f3c\u5df2\u6709\u6cea\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6740\u4ed6\uff1f\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u63a5\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u6069\u4eba\u3002\u201d \u7b2c\u516d\u5341\u4e94\u7ae0\u3000\u5229\u7528>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u4e94\u7ae0\u3000\u5229\u7528 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6069\u4eba\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5415\u51e4\u5148\u4e00\u76f4\u5728\u903c\u6211\uff0c\u6298\u78e8\u6211\uff0c\u6211\u60f3\u6b7b\u90fd\u4e0d\u80fd\uff0c\u7740\u4e0d\u662f\u4ed6\u6551\u4e86\u6211\uff0c\u6211\u53ea\u6015\u5df2 \u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u7684\u6cea\u5df2\u6d41\u4e0b\u3002 \u3000\u3000\u963f\u98de\u6014\u4f4f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6d41\u7740\u6cea\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u4f1a\u4e3a\u6211\u62a5\u7b54\u4ed6\u7684\uff0c\u53ef\u662f\u73b0\u5728\uff0c\u73b0\u5728\u4f60\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u9053\uff1a\u201c\u6740\u4eba\uff0c\u4e5f\u662f\u8bb8\u591a\u79cd\u62a5\u7b54\u7684\u65b9\u6cd5\u4e4b\u4e00\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u8f6c\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u4ed6\u53bb\u4e3a\u4f60\u6740\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ed6\u6b20\u6211\u4e00\u6761\u6dd1\u4e3a\u4f55\u4e0d\u8be5\u5c06\u53e6\u4e00\u4eba\u7684\u547d\u6765\u8fd8\u6211\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u6551\u7684\u662f\u6211\uff0c\u4e0d\u662f\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u7684\u503a\u5c31\u662f\u4ed6\u7684\u503a\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u8f6c\u56de\u5934\uff0c\u51dd\u6ce8\u7740\u963f\u98de\u3002 \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u5979\u7684\u503a\uff0c\u6211\u8fd8\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u4e0d\u6b20\u4eba\u7684\u503a\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ece\u4e0d\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5634\u89d2\u53c8\u6709\u4e86\u7b11\u610f\uff0c\u9053\uff1a\u201c\u4f60\u51c6\u5907\u7528\u8c01\u7684\u547d\u6765\u8fd8\u6211\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u9664\u4e86\u4e00\u4e2a\u4eba\uff0c\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u9664\u4e86\u8c01\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u674e\u5bfb\u6b22\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u53bb\u6740\u4ed6\uff1f\u201d \u3000\u3000\u963f\u98de\u76ee\u4e2d\u5145\u6ee1\u4e86\u75db\u82e6\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u6562\uff0c\u56e0\u4e3a\u6211\u6b20\u4ed6\u7684\u66f4\u591a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5c45\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u65e2\u4e0d\u6b20\u4ed6\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u6b20\u6211\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8981\u6211\u53bb\u6740\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u591c\u5df2\u4e34\uff0c\u963f\u98de\u5e76\u6ca1\u6709\u633d\u7740\u6797\u4ed9\u513f\u7684\u624b\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u7a81\u7136\u611f\u89c9\u5230\u4e00\u9635\u5947\u5f02\u7684\u4e0d\u5b89\uff0c\u5374\u8bf4\u4e0d \u51fa\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8d70\u5728\u4ed6\u524d\u9762\uff0c\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u53ef\u662f\u963f\u98de\u603b\u89c9\u5f97\u81ea\u5df1\u4eff\u4f5b\u8fd8\u662f\u5728\u4ed6\u7684\u76ee\u5149\u903c\u89c6\u4e0b\uff0c\u5fc3\u91cc\u603b\u89c9\u5f97\u6709\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u538b\u529b\u3002 \u3000\u3000\u8d70\u5f97\u8d8a\u8fdc\uff0c\u538b\u529b\u8d8a\u91cd\u3002 \u3000\u3000\u5929\u7554\u5df2\u6709\u661f\u5347\u8d77\uff0c\u56da\u91ce\u7a7a\u6d1e\uff0c\u98ce\u5df2\u4f4f\u3002 \u3000\u3000\u56db\u4e0b\u542c\u4e0d\u5230\u4e00\u4e1d\u58f0\u97f3\uff0c\u8fde\u79cb\u866b\u7684\u4f4e\u8bc9\u90fd\u5df2\u505c\u6b62\u3002 \u3000\u3000\u5929\u5730\u95f4\u552f\u4e00\u7684\u58f0\u97f3\uff0c\u53ea\u5269\u4e0b\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\u2014\u2014 \u3000\u3000\u963f\u98de\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u4e5f\u6709\u4e86\u811a\u6b65\u58f0\uff0c\u800c\u4e14\u4eff\u4f5b\u6b63\u548c\u4e0a\u5b98\u91d1\u8679\u7684\u811a\u6b65\u914d\u5408\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c \u914d\u5408\u6210\u4e00\u79cd\u5947\u7279\u7684\u8282\u594f\u3002 \u3000\u3000\u4e00\u53ea\u87cb\u87c0\u81ea\u67af\u8349\u4e1b\u4e2d\u8dc3\u51fa\uff0c\u7adf\u4f3c\u88ab\u8fd9\u79cd\u5947\u7279\u7684\u811a\u6b65\u58f0\u6240\u60ca\uff0c\u7a81\u53c8\u8dc3\u4e86\u56de\u53bb\u2014\u2014\u8fde\u8fd9\u811a\u6b65 \u58f0\u90fd\u4eff\u4f5b\u5e26\u7740\u79cd\u6740\u6c14\u3002 \u3000\u3000\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u963f\u98de\u8d70\u8def\u4e00\u5411\u6ca1\u6709\u58f0\u97f3\uff0c\u73b0\u5728\u4ed6\u7684\u811a\uff1a\u201c\u600e\u4f1a\u5ffd\u7136\u91cd\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u963f\u98de\u5782\u4e0b\u5934\uff0c\u7a81\u7136\u53d1\u73b0\u4e86\u8fd9\u539f\u56e0\u2014\u2014\u4ed6\u6bcf\u4e00\u6b65\u8e0f\u4e0b\uff0c\u7adf\u90fd\u6070\u5de7\u5728\u4e0a\u5b98\u91d1\u8679\u7684\u524d\u4e00\u6b65\u548c\u540e \u4e00\u6b65\u4e4b\u95f4\u3002 \u3000\u3000\u4ed6\u8e0f\u4e0b\u7b2c\u4e00\u6b65\uff0c\u4e0a\u5b98\u91d1\u8679\u624d\u8e0f\u4e0b\u7b2c\u4e8c\u6b65\uff0c\u4ed6\u8e0f\u4e0b\u7b2c\u4e09\u6b65\uff0c\u4e0a\u5b98\u91d1\u8679\u7acb\u523b\u8e0f\u4e0b\u7b2c\u56db\u6b65\u2014\u2014 \u4ece\u6765\u4e5f\u6ca1\u6709\u9519\u8fc7\u4e00\u6b65\u3002 \u3000\u3000\u4ed6\u82e5\u8d70\u7094\uff0c\u4e0a\u5b98\u91d1\u8679\u4e5f\u8d70\u5feb\uff0c\u4ed6\u82e5\u8d70\u6f2b\uff0c\u4e0a\u5b98\u91d1\u8679\u4e5f\u8d70\u6162\u3002 \u3000\u3000\u5f00\u59cb\u65f6\uff0c\u5f53\u7136\u662f\u4e0a\u5b98\u91d1\u8679\u5728\u914d\u5408\u4ed6\u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4e0a\u5b98\u91d1\u8679\u8d70\u5feb\uff0c\u4ed6\u811a\u6b65\u4e5f\u4e0d\u7531\u81ea\u4e3b\u8ddf\u7740\u5feb\u4e86\uff0c\u4e0a\u5b98\u91d1\u8679\u8d70\u6162\uff0c\u4ed6\u811a\u6b65\u4e5f\u6162\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u4ed6\u7684\u6b65\u6cd5\u7adf\u4f3c\u5df2\u88ab\u4e0a\u5b98\u91d1\u8679\u6240\u63a7\u5236\uff0c\u7adf\u65e0\u6cd5\u6446\u8131\u5f97\u5f00\uff01 \u3000\u3000\u963f\u98de\u638c\u5fc3\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u5fc3\u91cc\u5374\u53c8\u89c9\u5f97\u8fd9\u79cd\u8d70\u6cd5\u5f88\u8212\u670d\uff0c\u89c9\u5f97\u8eab\u4e0a\u6bcf\u4e00\u6839\u808c\u8089\u4e5f\u90fd\u5df2\u653e\u677e \u3002 \u3000\u3000\u4ed6\u8eab\u5fc3\u90fd\u4f3c\u5df2\u88ab\u8fd9\u79cd\u5947\u5f02\u7684\u8282\u594f\u6240\u50ac\u7720\u3002 \u3000\u3000\u8fd9\u8282\u594f\u7adf\u4f3c\u80fd\u6151\u4eba\u7684\u9b42\u9b44\u3002 \u3000\u3000\u6797\u4ed9\u513f\u663e\u7136\u4e5f\u53d1\u89c9\u4e86\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u7a81\u7136\u9732\u51fa\u4e00\u79cd\u6df7\u5408\u7740\u8b66\u60d5\u3001\u6050\u60e7\u548c\u6028\u6068\u7684\u6076\u6bd2\u4e4b\u610f \u3002 \u3000\u3000\u963f\u98de\u662f\u5979\u7684\u3002 \u3000\u3000\u53ea\u6709\u5979\u624d\u80fd\u63a7\u5236\u963f\u98de\u3002 \u3000\u3000\u5979\u7edd\u4e0d\u8bb8\u4efb\u4f55\u4eba\u4ece\u5979\u8fd9\u91cc\u5c06\u963f\u98de\u62a2\u8fc7\u53bb\uff01 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u7ad9\u5728\u65b9\u624d\u4ed6\u811a\u6b65\u505c\u4e0b\u6765\u7684\u5730\u65b9\u3002 \u3000\u3000\u65e5\u659c\u3001\u65e5\u843d\u3001\u591c\u4e34\u3001\u661f\u5347\u8d77\u2026\u2026 \u3000\u3000\u4ed6\u7684\u4eba\u6ca1\u6709\u79fb\u52a8\uff0c\u76ee\u5149\u4e5f\u6ca1\u6709\u79fb\u52a8\uff0c\u8fd8\u662f\u505c\u7559\u5728\u8def\u7684\u5c3d\u8718\u65b9\u624d\u4e0a\u5b98\u91d1\u8679\u7684\u8eab\u5f71\u6b63\u662f\u4ece\u6b64 \u5904\u6d88\u5931\u7684\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e0a\u5b98\u91d1\u8679\u8eab\u5f71\u53c8\u81ea\u6b64\u5904\u51fa\u73b0\u3002 \u3000\u3000\u8346\u65e0\u547d\u9996\u5148\u770b\u5230\u4ed6\u90a3\u9876\u5bbd\u5927\u7684\u6597\u7b20\uff0c\u5bbd\u5927\u7684\u9ec4\u888d\uff0c\u770b\u5230\u4ed6\u624b\u91cc\u7684\u9752\u94a2\u5251\uff0c\u5251\u5149\u5728\u661f\u5149\u4e0b \u95ee\u52a8\u3002 \u3000\u3000\u7136\u540e\uff0c\u8346\u65e0\u547d\u5c31\u770b\u5230\u4e86\u963f\u98de\u3002 \u3000\u3000\u82e5\u662f\u522b\u4eba\u8fdc\u8fdc\u89c1\u5230\uff0c\u4e00\u5b9a\u4f1a\u4ee5\u4e3a\u6b64\u523b\u8d70\u5728\u4e0a\u5b98\u76c6\u8679\u8eab\u540e\u7684\u4eba\u662f\u8346\u65e0\u547d\uff0c\u56e0\u4e3a\u4e24\u4eba\u8d70\u8def\u7684 \u6b65\u4f10\uff0c\u7adf\u5982\u6b64\u5947\u7279\uff1b \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\u963f\u98de\u7adf\u5df2\u53d6\u4ee3\u4e86\u8346\u65e0\u547d\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u8346\u65e0\u547d\u7684\u773c\u8272\u66f4\u7070\u9eef\uff0c\u9eef\u5f97\u5c31\u50cf\u662f\u65e0\u661f\u65e0\u6708\uff0c\u9ece\u660e\u524d\u5c06\u6653\u7684\u591c\u7a7a\uff0c\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u6ca1\u6709\u751f \u547d\uff0c\u751a\u81f3\u8fde\u201c\u6b7b\u201d\u7684\u5473\u9053\u90e8\u6ca1\u6709\u3002 \u3000\u3000\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u7684\u8138\u5374\u6bd4\u773c\u8272\u66f4\u7a7a\u6d1e\uff0c\u66f4\u5446\u6ede\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6e10\u6e10\u8d70\u8fd1\u4e86\uff0c\u7a81\u7136\u5728\u4ed6\u9762\u524d\u505c\u4e0b\u3002 \u3000\u3000\u963f\u98de\u7684\u811a\u6b65\u7adf\u4e5f\u505c\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u5149\u9065\u89c6\u7740\u8fdc\u65b9\uff0c\u5e76\u6ca1\u6709\u77a7\u8346\u65e0\u547d\u4e00\u773c\uff0c\u7a81\u7136\u4f38\u624b\uff0c\u62bd\u51fa\u4e86\u8346\u65e0\u547d\u8170\u5e26\u4e0a\u63d2\u7740 \u7684\u5251\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u4f60\u5df2\u7528\u4e0d\u7740\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4e5f\u7a7a\u6d1e\u5f97\u53ef\u6015\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u80fd\u786e\u5b9a\u662f\u5426\u4ece\u81ea\u5df1\u5634\u91cc\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u624b\u91cc\u8fd8\u634f\u7740\u90a3\u67c4\u9752\u94a2\u5251\u7684\u5251\u5c16\uff0c\u5c06\u5251\u67c4\u9012\u4e86\u8fc7\u53bb\u9053\uff1a\u201c\u8fd9\u67c4\u5251\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6162\u6f2b\u7684\u4f38\u51fa\u624b\uff0c\u63a5\u8fc7\u5251\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7f13\u7f13\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53cd\u6b63\u7528\u4ec0\u4e48\u5251\u90fd\u6ca1\u6709\u5206\u522b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u4eba\u5df2\u8d70\u4e86\u8fc7\u53bb\uff0c\u81ea\u59cb\u81f3\u7ec8\uff0c\u4ece\u672a\u77a7\u8fc7\u8346\u65e0\u547d\u4e00\u773c\u3002 \u3000\u3000\u963f\u98de\u4e5f\u8d70\u4e86\u8fc7\u53bb\uff0c\u4e5f\u6ca1\u6709\u77a7\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u5411\u4ed6\u5ae3\u7136\u4e00\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6b7b\uff0c\u96be\u9053\u771f\u7684\u5f88\u56f0\u96be\u4e48\uff1f\u201d \u3000\u3000\u4e00\u7247\u4e4c\u4e91\u63a9\u4f4f\u4e86\u661f\u5149\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u9739\u96f3\u4e00\u58f0\uff0c\u66b4\u96e8\u503e\u76c6\u3002 \u3000\u3000\u8346\u65e0\u547d\u8fd8\u7687\u52a8\u4e5f\u4e0d\u52a8\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u7ad9\u5728\u66b4\u96e8\u4e2d\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u90fd\u5df2\u6e17\u900f\uff0c\u773c\u89d2\u6709\u6c34\u73e0\u6d41\u843d\uff0c\u662f\u96e8\uff1f\u8fd8\u662f\u6cea\uff1f \u3000\u3000\u8346\u65e0\u547d\u53c8\u600e\u4f1a\u6d41\u6cea\uff1f \u3000\u3000\u4e0d\u6d41\u6cea\u7684\u4eba\uff0c\u901a\u5e38\u53ea\u6d41\u8840\uff01 \u3000\u3000\u5251\uff0c\u8584\u800c\u950b\u5229\uff0c\u4e5f\u6ca1\u6709\u5251\u9537\u3002 \u3000\u3000\u706f\u5149\u5f88\u7a33\u5b9a\uff0c\u5251\u5149\u95ea\u52a8\uff0c\u9752\u5149\u3002 \u3000\u3000\u7a97\u5b50\u662f\u5173\u7740\u7684\uff0c\u7a97\u5916\u96e8\u5982\u6ce8\uff0c\u5c4b\u5b50\u91cc\u6ca1\u6709\u98ce\u3002 \u3000\u3000\u963f\u98de\u5728\u7a33\u5b9a\u7684\u706f\u5149\u4e0b\uff0c\u51dd\u6ce8\u7740\u8fd9\u67c4\u5251\uff0c\u76ee\u5149\u4e5f\u5df2\u4e45\u4e45\u672a\u79fb\u52a8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5374\u5728\u51dd\u6ce8\u7740\u4ed6\uff0c\u60a0\u60a0\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u67c4\u5251\u5982\u4f55\uff1f\u201d \u3000\u3000\u963f\u98de\u957f\u957f\u5410\u4e86\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5f88\u597d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6bd4\u4f60\u4ee5\u524d\u7528\u7684\u5251\u5982\u4f55\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u66f4\u8f7b\u4e9b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u81ea\u4ed6\u624b\u4e2d\u53d6\u8fc7\u5251\uff0c\u7528\u4e24\u6839\u624b\u6307\u5c06\u5251\u5c16\u4e00\u629b\uff0c\u5251\u8eab\u7acb\u523b\u53d8\u6210\u4e86\u5706\u5708\uff0c\u53c8\u201c\u55e1 \u201d\u7684\u4e00\u58f0\uff0c\u53cd\u5f39\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u55e1\u55e1\u201d\u4e4b\u58f0\u5982\u9f99\u541f\uff0c\u826f\u4e45\u4e0d\u7edd\u3002 \u3000\u3000\u963f\u98de\u51b7\u6f20\u7684\u773c\u775b\u5df2\u70bd\u70ed\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5634\u89d2\u5e26\u7740\u7b11\u610f\uff0c\u9053\uff1a\u201c\u8fd9\u53c8\u6bd4\u4f60\u4ee5\u524d\u7528\u7684\u5251\u5982\u4f55\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u7684\u5251\u5982\u6b64\u4e00\u62d7\u5fc5\u65ad\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e00\u53cd\u624b\uff0c\u5251\u524a\u51fa\u3002 \u3000\u3000\u684c\u4e0a\u7684\u8336\u676f\u7acb\u88ab\u524a\u65ad\uff0c\u5982\u524a\u8150\u7af9\u3002 \u3000\u3000\u963f\u98de\u5fcd\u4e0d\u4f4f\u8131\u53e3\u8d5e\u9053\uff1a\u201c\u597d\u5251\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7f13\u7f13\u9053\uff1a\u201c\u7684\u786e\u662f\u67c4\u597d\u5251\uff0c\u867d\u8f7b\u800c\u4e0d\u949d\uff0c\u867d\u8584\u800c\u4e0d\u8106\uff0c\u521a\u4e2d\u5e26\u67d4\uff0c\u521a\u4e2d\u5e26\u97e7\uff0c \u53ea\u56e0\u8fd9\u67c4\u5251\u770b\u6765\u867d\u7c97\u52a3\u7b80\u964b\uff0c\u5176\u5b9e\u5374\u662f\u5f53\u4eca\u94f8\u5251\u7684\u7b2c\u4e00\u9ad8\u624b\u53e4\u5927\u5e08\u7684\u7cbe\u54c1\uff0c\u800c\u4e14\u662f\u7279\u5730\u4e3a\u8346 \u65e0\u547d\u7684\u6dec\u70bc\u7684\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5411\u963f\u98de\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u79f0\u7684\u5251\u8def\uff0c\u4eff\u4f5b\u548c\u8346\u65e0\u547d\u76f8\u540c\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6709\u51e0\u5206\u76f8\u540c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ed6\u51fa\u4e8e\u867d\u6bd4\u4f60\u66f4\u6bd2\u66f4\u72e0\uff0c\u4f46\u4f60\u5374\u6bd4\u4ed6\u66f4\u7a33\u66f4\u51c6\uff0c\u53ea\u56e0\u4f60\u6bd4\u4ed6\u80fd\u7b49\uff0c\u6240\u4ee5 \u8fd9\u67c4\u5251\u4f60\u7528\u6765\u53ef\u80fd\u6bd4\u4ed6\u66f4\u5408\u9002\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u7684\u5251\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5251\u672c\u65e0\u4e3b\uff0c\u80fd\u8005\u5f97\u4e4b\u3002\u201d \u3000\u3000\u4ed6\u6f2b\u6162\u5730\u5c06\u5251\u9012\u8fc7\u53bb\uff0c\u76ee\u4e2d\u95ea\u52a8\u7740\u4e00\u79cd\u5947\u7279\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8fd9\u67c4\u5251\u5df2\u662f\u4f60\u7684\u4e86\u3002 \u201d \u3000\u3000\u963f\u98de\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u8fd8\u662f\u8bf4\u51fa\u4e86\u540c\u6837\u7684\u4e00\u53e5\u8bdd\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u7684\u5251\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u53ea\u6709\u8fd9\u67c4\u5251\uff0c\u624d\u662f\u4f60\u7684\u5251\uff0c\u56e0\u4e3a\u53ea\u6709\u7528\u8fd9\u67c4\u5251\uff0c\u4f60\u624d\u80fd\u6740\u5f97\u4e86\u522b\u4eba\u7684\u3002 \u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8bf4\u4e0d\u5bc7\u4e5f\u80fd\u6740\u5f97\u4e86\u6211\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u963f\u98de\u6c89\u9ed8\u5f97\u66f4\u4e45\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u60a0\u7136\u9053\uff1a\u201c\u4f60\u6b20\u6211\u7684\uff0c\u6240\u4ee5\u8981\u4e3a\u6211\u6740\u4eba\uff0c\u6240\u4ee5\u6211\u7ed9\u79f0\u6740\u4eba\u7684\u5251\uff0c\u8fd9\u672c\u5c31\u5f88\u516c\u9053 \u3002\u201d \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u4f38\u51fa\u624b\uff0c\u63a5\u8fc7\u4e86\u5251\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u597d\uff0c\u5f88\u597d\uff0c\u6709\u4e86\u8fd9\u67c4\u5251\uff0c\u660e\u5929\u4f60\u7684\u503a\u5c31\u53ef\u4ee5\u8fd8\u6e05\u4e86\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8981\u6211\u6740\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7f13\u7f13\u9053\uff1a\u201c\u6211\u8981\u4f60\u6740\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u662f\u4f60\u7684\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u8d70\u4e86\u56de\u53bb\uff0c\u63a9\u8d77\u95e8\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u8bed\u58f0\u5728\u95e8\u5916\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u90fd\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u660e\u65e5\u6b63\u5348\u524d\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u6253\u6270\u3002\u201d \u3000\u3000\u73b0\u5728\uff0c\u5c4b\u5b50\u91cc\u53c8\u53ea\u5269\u4e0b\u963f\u98de\u548c\u6797\u4ed9\u513f\u4e24\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5750\u5728\u90a3\u91cc\uff0c\u5934\u59cb\u7ec8\u672a\u66fe\u62ac\u8d77\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5728\u8fd9\u5c4b\u91cc\u4e5f\u803d\u4e86\u5f88\u4e45\uff0c\u59cb\u7ec8\u6ca1\u6709\u77a7\u8fc7\u4e00\u773c\u3002 \u3000\u3000\u5979\u4e5f\u6ca1\u6709\u5f00\u8fc7\u53e3\uff0c\u53ea\u6709\u5728\u963f\u98de\u4f38\u624b\u53bb\u63a5\u5251\uff0c\u5979\u5634\u5507\u624d\u52a8\u4e86\u52a8\uff0c\u4eff\u4f5b\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u5fcd\u4f4f \u3002 \u3000\u3000\u73b0\u5728\uff0c\u5c4b\u5b50\u91cc\u53ea\u5269\u4e0b\u4ed6\u4eec\u4e24\u4eba\uff0c\u6797\u4ed9\u513f\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u66ff\u4ed6\u53bb\u6740\u4eba\uff1f\u201d \u3000\u3000\u963f\u98de\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u6b20\u4ed6\u7684\uff0c\u800c\u4e14\u6211\u5df1\u7b54\u5e94\u3002\u201d\u6797\u6797\u513f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u8981\u4f60\u53bb\u6740 \u8c01\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u731c\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u5df2\u731c\u51fa\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7f13\u7f13\u9053\uff1a\u201c\u82e5\u662f\u6211\u731c\u7684\u4e0d\u9519\uff0c\u4ed6\u8981\u4f60\u6740\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u9f99\u5578\u4e91\u3002\u201d \u3000\u3000\u963f\u98de\u76b1\u7709\u9053\uff1a\u201c\u9f99\u5578\u4e91\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u9f99\u5578\u4e91\u60f3\u8981\u5229\u7528\u4ed6\uff0c\u4ed6\u5374\u4e00\u5411\u53ea\u4f1a\u5229\u7528\u522b\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9ed8\u7136\u534a\u664c\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u9f99\u5578\u4e91\u672c\u5c31\u65e9\u8be5\u6b7b\u4e86\u7684\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f46\u4f60\u7edd\u4e0d\u80fd\u51fa\u624b\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u6ca1\u6709\u53e3\u7b54\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u4e3a\u4ec0\u4e48\u53eb\u4f60\u66ff\u4ed6\u4e0b\u624b\uff1f\u201d \u3000\u3000\u963f\u98de\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u8981\u522b\u4eba\u53bb\u6740\u4eba\uff0c\u603b\u6bd4\u81ea\u5df1\u53bb\u6740\u5bb9\u6613\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f46\u4e0a\u5b98\u91d1\u8679\u8981\u6740\u9f99\u5578\u4e91\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\uff0c\u4f55\u51b5\uff0c\u91d1\u94b1\u5e2e\u95e8\u4e0b\u9ad8\u624b \u5982\u4e91\uff0c\u83ab\u8bf4\u4e00\u4e2a\u9f99\u5578\u4e91\uff0c\u5c31\u7b97\u6709\u4e00\u767e\u4e2a\uff0c\u4e00\u5343\u4e2a\uff0c\u91d1\u94b1\u5e2e\u8fd8\u662f\u4e00\u6837\u53ef\u4ee5\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u4e0a\u5b98 \u91d1\u8679\u81ea\u5df1\u4e0d\u5c51\u51fa\u624b\uff0c\u4e3a\u4f55\u4e0d\u4ee4\u4ed6\u5c5e\u4e0b\u51fa\u624b\uff1f\u201d\u963f\u98de\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u539f\u56e0\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u2026\u2026\u518d\u8fc7\u4e24\u5929\uff0c\u5c31\u662f\u521d\u4e00\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u521d\u4e00\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6c5f\u6eaf\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u4e0b\u4e2a\u6708\u521d\u4e00\uff0c\u4e0a\u5b98\u91d1\u8679\u5c31\u8981\u548c\u9f99\u5578\u4e91\u7ed3\u4e3a\u5144\u5f1f\u3002\u201d \u3000\u3000\u963f\u98de\u76b1\u7709\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u7684\u773c\u775b\u83ab\u975e\u778e\u4e86\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ed6\u81ea\u7136\u4e0d\u5c51\u548c\u9f99\u5578\u4e91\u7ed3\u4e3a\u5144\u5f1f\uff0c\u5374\u53c8\u4e0d\u613f\u80cc\u4e0a\u5931\u8a00\u80cc\u4fe1\u7684\u6076\u540d\uff0c\u552f\u4e00\u7684\u6cd5 \u5b50\u5c31\u662f\u5c06\u9f99\u5578\u4e91\u6740\u4e86\u3002\u201d \u3000\u3000\u5979\u5fae\u7b11\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6d3b\u4eba\u81ea\u7136\u4e0d\u80fd\u548c\u6b7b\u4eba\u7ed3\u4e3a\u5144\u5f1f\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f46\u4e24\u4eba\u65e2\u5df2\u6709\u7ed3\u4e49\u4e4b\u7ea6\uff0c\u4e0a\u5b98\u91d1\u8679\u81ea\u5df1\u5c31\u4e0d\u80fd\u4e0b\u624b\uff0c\u4e5f\u4e0d\u80fd\u52a8\u7528\u91d1\u94b1\u5e2e\u7684 \u529b\u91cf\uff0c\u6240\u4ee5\u624d\u4f1a\u6765\u5229\u7528\u4f60\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8981\u6740\u9f99\u5578\u4e91\uff0c\u4f60\u7684\u786e\u6bd4\u4efb\u4f55\u4eba\u90fd\u5408\u9002\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u4f60\u4e0d\u662f\u91d1\u94b1\u5e2e\u7684\u4eba\uff0c\u5374\u662f\u674e\u5bfb\u6b22\u7684\u670b\u53cb\uff0c\u9f99\u5578\u4e91\u5bf9\u4e0d\u8d77\u674e\u5bfb\u6b22\uff0c \u6c5f\u6e56\u4e2d\u5df2\u6709\u5f88\u591a\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u6740\u4e86\u9f99\u5578\u4e91\uff0c\u522b\u4eba\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u4f60\u662f\u5728\u66ff\u674e\u5bfb\u6b22\u51fa\u6c14 \uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6000\u7591\u5230\u4e0a\u5b98\u91d1\u8679\u5934\u4e0a\u3002\u201d \u3000\u3000\u963f\u98de\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u7b97\u4e0d\u4e3a\u4efb\u4f55\u4eba\uff0c\u6211\u4e5f\u4e0d\u5bb9\u8fd9\u79cd\u4eba\u6d3b\u5728\u4e16\u4e0a\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u82e5\u6740\u4e86\u9f99\u5578\u4e91\uff0c\u4e0a\u5b98\u91d1\u8679\u5c31\u4f1a\u6740\u4f60\u3002\u201d \u3000\u3000\u963f\u98de\u9ed8\u7136\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ed6\u6740\u4f60\u4e0d\u4f46\u662f\u4e3a\u4e86\u8981\u706d\u53e3\uff0c\u8fd8\u8981\u522b\u4eba\u8ba4\u4e3a\u4ed6\u5728\u66ff\u9f99\u5578\u4e91\u590d\u4ec7\uff0c\u8ba4\u4e3a\u4ed6\u5f88\u591f \u4e49\u6c14\u3002\u201d \u3000\u3000\u963f\u98de\u76ee\u5149\u79fb\u5411\u624b\u4e2d\u7684\u5251\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5ffd\u7136\u6295\u5165\u963f\u98de\u6000\u91cc\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u8d81\u4ed6\u4e0d\u5728\uff0c\u6211\u4eec\u8d76\u5feb\u9003\u5427\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u9003\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4ece\u4e0d\u9003\uff0c\u4f46\u4e3a\u4e86\u6211\uff0c\u4f60\u80fd\u4e0d\u80fd\u59d4\u5c48\u4e00\u6b21\uff1f\u201d\u3002\u963f\u98de\u9053\uff1a\u201c\u4e0d\u80fd\u3002 \u201d \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4e3a\u4e86\u6211\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5df2\u53d1\u6296\uff0c\u6cea\u5df2\u5c06\u843d\u3002 \u3000\u3000\u5979\u53c8\u7528\u51fa\u4e86\u5979\u7684\u6b66\u5668\u3002 \u3000\u3000\u963f\u98de\u5374\u6ca1\u6709\u77a7\u5979\uff0c\u76ee\u5149\u4eff\u4f5b\u5df2\u5230\u4e86\u8fdc\u65b9\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\uff0c\u6211\u624d\u4e0d\u80fd\u8fd9\u4e48\u6837\u505a\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u7f13\u7f13\u9053\uff1a\u201c\u4e3a\u4e86\u4f60\uff0c\u6211\u7edd\u4e0d\u80fd\u505a\u98df\u8a00\u80cc\u4fe1\u7684\u61e6\u592b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u7ec8\u4e8e\u4f0f\u5728\u963f\u98de\u80f8\u819b\u4e0a\uff0c\u75db\u54ed\u8d77\u6765\uff0c\u7ee7\u7eed\u7740\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u82f1\u96c4\u4e5f\u597d\uff0c\u61e6\u592b\u4e5f\u597d\uff0c\u6211 \u7231\u7684\u53ea\u662f\u4f60\uff0c\u6211\u53ea\u60f3\u8981\u4f60\u6d3b\u7740\u966a\u7740\u6211\u3002\u201d \u3000\u3000\u963f\u98de\u51b7\u6f20\u575a\u5b9a\u7684\u76ee\u5149\u4f3c\u5df2\u53c8\u5c06\u878d\u5316\uff0c\u8f7b\u629a\u7740\u5979\u7684\u67d4\u53d1\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u4e0d\u662f\u5728\u966a\u7740\u4f60\u4e48\uff1f \u201d \u3000\u3000\u6797\u4ed9\u513f\u6cea\u53c8\u6d41\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u6709\u65f6\u771f\u4e0d\u660e\u767d\uff0c\u4f60\u5fc3\u91cc\u60f3\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u60f3\u5f97\u5f88\u7b80\u5355\uff0c\u6240\u4ee5\u4e0d\u4f1a\u6539\u53d8\u3002\u201d \u3000\u3000\u8d8a\u7b80\u5355\uff0c\u53d8\u5316\u5c31\u8d8a\u5c11\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u62ac\u8d77\u4e86\u6cea\u773c\uff0c\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u6539\u53d8\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6c38\u8fdc\uff01\u201d \u3000\u3000\u4ed6\u7684\u56de\u7b54\u4e5f\u5f88\u7b80\u5355\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ad9\u8d77\u6765\uff0c\u6162\u6162\u7684\u8d70\u5230\u7a97\u524d\u3002 \u3000\u3000\u7a97\u5916\u6084\u65e0\u4eba\u58f0\uff0c\u751a\u81f3\u8fde\u866b\u9e23\u9e1f\u8bed\u90fd\u542c\u4e0d\u89c1\u2014\u2014\u65e0\u8bba\u662f\u54ea\u4e00\u79cd\u751f\u547d\uff0c\u53ea\u8981\u5230\u4e86\u8fd9\u91cc\uff0c\u751f\u547d \u7684\u4ef7\u503c\u90fd\u4f1a\u7a81\u7136\u53d8\u5f97\u5f88\u5351\u8d31\u3002 \u3000\u3000\u5728\u8fd9\u91cc\uff0c\u6700\u771f\u5b9e\u7684\u611f\u89c9\u5c31\u662f\u201c\u6b7b\u201d\uff0c\u65e0\u8bba\u4f60\u662f\u5750\u7740\uff0c\u8fd8\u662f\u7ad9\u7740\uff0c\u65e0\u8bba\u4f60\u662f\u5728\u7a97\u5185\uff0c\u8fd8\u662f \u5728\u7a97\u5916\uff0c\u968f\u65f6\u968f\u5730\u90fd\u80fd\u611f\u89c9\u5230\u5b83\u7684\u5b58\u5728\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u6797\u4ed9\u513f\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5ffd\u7136\u53d1\u89c9\u4f60\u548c\u674e\u5bfb\u6b22\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5f88\u50cf\u4e0a\u5b98\u91d1 \u8679\u548c\u8346\u65e0\u547d\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8346\u65e0\u547d\u8fd9\u4e2a\u4eba\u51e0\u4e4e\u5b8c\u5168\u662f\u4e3a\u4e86\u4e0a\u5b98\u91d1\u8679\u800c\u6d3b\u7740\u7684\uff0c\u4e0a\u5b98\u91d1\u8679\u5f53\u7136\u4e5f\u5bf9\u4ed6\u5f88 \u597d\uff0c\u76f4\u5230\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u5979\u5634\u89d2\u5e26\u7740\u79cd\u8f9b\u6da9\u7684\u7b11\u610f\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u8346\u65e0\u547d\u5df2\u5931\u53bb\u4e86\u5229\u7528\u7684\u4ef7\u503c\uff0c\u7acb\u523b\u5c31\u88ab \u4e0a\u5b98\u91d1\u8679\u50cf\u91ce\u72d7\u822c\u8d76\u4e86\u51fa\u53bb\uff0c\u8fd9\u6837\u7684\u7ed3\u5c40\uff0c\u53ea\u6015\u662f\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u65e9\u5c31\u60f3\u5230\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ed6\u82e5\u65e9\u77e5\u7ed3\u5c40\u5982\u6b64\uff0c\u8fd8\u4f1a\u90a3\u4e48\u6837\u505a\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u4f1a\uff0c\u56e0\u4e3a\u4ed6\u522b\u65e0\u9009\u62e9\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u963f\u98de\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u5bf9\u4f60\u597d\uff0c\u53ea\u56e0\u4e3a\u8fd9\u4e16\u4e0a\u552f\u6709\u4f60\u80fd\u771f\u6b63\u7684\u5e2e\u52a9\u4ed6\uff0c\u9664\u4e86\u4f60\uff0c\u4ed6\u51e0\u4e4e\u5b8c \u5168\u5b64\u7acb\uff0c\u4f46\u7b49\u4f60\u4e5f\u6ca1\u6709\u5229\u7528\u4ef7\u503c\u7684\u65f6\u5019\uff0c\u4ed6\u662f\u4e0d\u662f\u4e5f\u4f1a\u50cf\u4e0a\u5b98\u91d1\u8679\u5bf9\u8346\u65e0\u547d\u90a3\u6837\u5bf9\u4f60\uff1f\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f60\u56de\u8fc7\u5934\u6765\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u8bf4\u5f97\u5f88\u6162\uff0c\u4f46\u5374\u5f88\u575a\u51b3\uff0c\u5f88\u4e25\u5389\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u5bf9\u6797\u4ed9\u513f\u8fd9\u4e48\u6837\u8bf4\u8fc7\u8bdd\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6276\u5728\u7a97\u6839\u4e0a\u7684\u624b\u5ffd\u7136\u63e1\u7d27\uff0c\u9053\uff1a\u201c\u56de\u8fc7\u5934\u53bb\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u544a\u8bc9\u4f60\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8fd9\u6837\u6211\u4e5f\u80fd\u542c\u5f97\u89c1\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f46\u6211\u5374\u8981\u4f60\u770b\u7740\u6211\uff0c\u6709\u4e9b\u8bdd\uff0c\u4f60\u4e0d\u4f46\u8981\u7528\u8033\u6735\u542c\uff0c\u8fd8\u8981\u7528\u773c\u775b\uff0c\u5426\u5219\u4f60\u5c31\u6c38 \u8fdc\u4e0d\u80fd\u4e86\u89e3\u5b83\u7684\u610f\u601d\u3001 \u3000\u3000\u6797\u4ed9\u513f\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u5374\u7ec8\u4e8e\u8fd8\u662f\u56de\u8fc7\u4e86\u5934\u3002 \u3000\u3000\u5979\u770b\u5230\u963f\u98de\u7684\u773c\u775b\uff0c\u5df2\u4e86\u89e3\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u7a81\u7136\u53d8\u5f97\u51e0\u4e4e\u548c\u4e0a\u5b98\u91d1\u8679\u5b8c\u5168\u4e00\u6837\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u773c\u775b\u82e5\u662f\u53d8\u6210\u8fd9\u6837\u5b50\uff0c\u90a3\u5c31\u8868\u793a\u4ed6\u65e0\u8bba\u8bf4\u4ec0\u4e48\u4f60\u90fd\u53ea\u6709\u542c\u7740\uff0c\u800c\u4e14\u7edd\u4e0d\u80fd\u8fdd\u80cc \u3002 \u3000\u3000\u5426\u5219\u4f60\u5c31\u4e00\u5b9a\u8981\u540e\u6094\u7684\uff01 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6797\u4ed9\u513f\u624d\u77e5\u9053\u81ea\u5df1\u9519\u4e86\u3002 \u3000\u3000\u5979\u672c\u6765\u4e00\u76f4\u4ee5\u4e3a\u81ea\u5df1\u5df2\u5b8c\u5168\u63a7\u5236\u4f4f\u963f\u98de\uff0c\u73b0\u5728\u624d\u77e5\u9053\u8fd9\u60f3\u6cd5\u9519\u5f97\u591a\u4e48\u5389\u5bb3\u3002 \u3000\u3000\u963f\u98de\u7684\u786e\u662f\u7231\u5979\u7684\uff0c\u7231\u5f97\u5f88\u6df1\u3002 \u3000\u3000\u4f46\u5728\u4e00\u4e2a\u7537\u4eba\u7684\u751f\u547d\u4e2d\uff0c\u5374\u8fd8\u6709\u5f88\u591a\u5f88\u591a\u6bd4\u201c\u7231\u201d\u66f4\u91cd\u8981\u7684\u4e8b\u2014\u2014\u6bd4\u751f\u547d\u90fd\u91cd\u8981\u7684\u4e8b\u3002 \u3000\u3000\u963f\u98de\u4ee5\u524d\u4e00\u76f4\u5bf9\u5979\u5f88\u987a\u4ece\uff0c\u90a3\u53ea\u56e0\u4e3a\u5979\u8fd8\u6ca1\u6709\u89e6\u53ca\u8fd9\u4e9b\u4e8b\u4e86\u3002 \u3000\u3000\u5979\u53ef\u4ee5\u8981\u4ed6\u4e3a\u5979\u6b7b\uff0c\u5374\u7edd\u4e0d\u80fd\u8981\u4ed6\u5c06\u8fd9\u4e9b\u4e8b\u629b\u5f03\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u6797\u4ed9\u513f\u624d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5bf9\u6211\u8bf4\u4ec0\u4e48\uff0c\u6211\u5728\u542c\u7740\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u8fd8\u662f\u5f88\u751c\uff0c\u5374\u5df2\u6709\u4e9b\u52c9\u5f3a\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u8981\u4f60\u660e\u767d\uff0c\u674e\u5bfb\u6b22\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u4e0d\u8bb8\u4efb\u4f55\u4eba\u4fae\u8fb1\u6211\u7684\u670b\u53cb\u2026\u2026\u4efb\u4f55\u4eba\uff01 \u201d \u3000\u3000\u6797\u4ed9\u513f\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d\u963f\u98de\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u7684\u90a3\u4e9b\u8bdd\uff0c\u4e0d\u4f46\u4f4e\u4f30\u4e86\u6211\uff0c\u4e5f \u62b5\u4f30\u4e86\u8346\u65e0\u547d\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u970d\u7136\u62ac\u8d77\u5934\uff0c\u76ee\u4e2d\u5145\u6ee1\u4e86\u60ca\u8bb6\u548c\u7591\u95ee\uff0c\u9053\uff1a\u201c\u4ed6\uff1f\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u8d70\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8981\u8d70\uff0c\u5e76\u4e0d\u662f\u88ab\u4eba\u8d76\u8d70\u7684\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u4e0d\u61c2\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u61c2\uff0c\u4f60\u53ea\u8981\u8bb0\u7740\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53c8\u5782\u4e0b\u4e86\u5934\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u6bcf\u4e00\u53e5\u8bdd\uff0c\u6211\u90fd\u6c38\u8fdc\u8bb0\u7740\uff0c\u6211\u53ea\u5e0c\u671b\u4f60\u4e5f\u83ab\u8981\u5fd8 \u8bb0\uff0c\u4f60\u8bf4\u8fc7\u2026\u2026\u4f60\u5bf9\u6211\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u5fc3\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u51dd\u6ce8\u7740\u5979\uff0c\u826f\u4e45\u826f\u4e45\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5c31\u7b97\u6709\u5ea7\u51b0\u5c71\uff0c\u6b64\u523b\u4e5f\u5df2\u88ab\u878d\u5316\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u8d70\u4e86\u8fc7\u53bb\uff0c\u8d70\u5411\u5979\uff0c\u5979\u8eab\u4e0a\u4eff\u4f5b\u6709\u79cd\u5947\u5f02\u7684\u529b\u91cf\u5728\u5438\u5f15\u7740\u4ed6\uff0c\u4ee4\u4ed6\u5b8c\u5168\u4e0d\u80fd\u6297 \u62d2\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u95ea\u5f00\u4e86\uff0c\u4eff\u4f5b\u751f\u6015\u6cbe\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4eca\u5929\u4e0d\u8981\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7684\u8eab\u5b50\u7a81\u7136\u50f5\u786c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u53c8\u7b11\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4eca\u5929\u4f60\u4e00\u5b9a\u8981\u597d\u597d\u4f11\u606f\uff0c\u5feb\u7761\u5427\uff0c\u6211\u4f1a\u5b88\u5728\u4f60\u65c1\u8fb9\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7ad9\u5728\u90a3\u91cc\uff0c\u773c\u775b\u77a7\u7740\u95e8\uff0c\u50cf\u662f\u5728\u7b49\u5f85\u3002 \u3000\u3000\u4ed6\u5728\u7b49\u4ec0\u4e48\uff1f \u3000\u3000\u95e8\u5916\u5b88\u5019\u7684\u4eba\u90fd\u5df2\u64a4\u8d70\uff0c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u5df2\u5429\u5490\u8fc7\u4ed6\u4eec\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u6709\u4eba\u8981\u6765\uff0c\u6211\u4e0d\u8bb8\u4efb \u4f55\u4eba\u6253\u6270\u4ed6\u3002\u201d \u3000\u3000\u662f\u8c01\u8981\u6765\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e3a\u4ec0\u4e48\u5bf9\u4ed6\u5982\u6b64\u91cd\u89c6\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u90fd\u6709\u76ee\u7684\uff0c\u8fd9\u6b21\u4ed6\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u591c\u6df1\uff0c\u66f4\u9759\u3002 \u3000\u3000\u963f\u98de\u95ed\u7740\u773c\uff0c\u547c\u5438\u5f88\u5747\u5300\uff0c\u4f3c\u5df2\u7761\u5f97\u5f88\u9163\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5374\u662f\u5b8c\u5168\u6e05\u9192\u7740\u7684\uff0c\u51e0\u4e4e\u4ece\u6765\u4e5f\u6ca1\u6709\u5982\u6b64\u6e05\u9192\u8fc7\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u5f88\u5c11\u7761\u4e0d\u7740\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u5230\u975e\u5e38\u75b2\u5026\u7684\u65f6\u5019\uff0c\u7edd\u4e0d\u4f1a\u7761\u4e0b\u53bb\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u5374\u662f \u53ea\u8981\u4e00\u6cbe\u7740\u6795\u5934\uff0c\u5c31\u7acb\u523b\u7761\u7740\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5374\u5931\u7720\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5c31\u7761\u5728\u4ed6\u8eab\u65c1\uff0c\u547c\u5438\u5f97\u4e5f\u5f88\u5747\u5300\u3002 \u3000\u3000\u963f\u98de\u53ea\u8981\u4e00\u7ffb\u8eab\uff0c\u5c31\u53ef\u62e5\u62b1\u8d77\u5979\u6e29\u6696\u548c\u67d4\u8f6f\u7684\u55e3\u4f53\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u52c9\u5f3a\u63a7\u5236\u81ea\u5df1\uff0c\u8fde\u770b\u90fd\u4e0d\u6562\u770b\u5979\u4e00\u773c\uff0c\u4ed6\u751f\u6015\u81ea\u5df1\u770b\u4e86\u5979\u4e00\u773c\uff0c\u610f\u5fd7\u5c31\u4f1a\u5b8c\u5168\u5d29 \u6e83\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6c38\u8fdc\u90fd\u5982\u6b64\u4fe1\u4efb\u4ed6\uff0c\u4ed6\u600e\u80fd\u505a\u8fd9\u79cd\u4e8b\uff1f \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u80fd\u611f\u89c9\u5230\u5979\u90a3\u5e26\u7740\u751c\u9999\u7684\u547c\u5438\uff0c\u4ed6\u51e0\u4e4e\u8981\u7528\u51fa\u4ed6\u6240\u6709\u7684\u7cbe\u795e\u548c\u529b\u6c14\uff0c\u624d\u80fd\u52c9 \u5f3a\u5c06\u81ea\u5df1\u63a7\u5236\u3002 \u3000\u3000\u8fd9\u7edd\u4e0d\u662f\u4ef6\u5f88\u597d\u53d7\u7684\u4e8b\u3002 \u3000\u3000\u6b32\u671b\u5c31\u50cf\u662f\u6d6a\u6f6e\uff0c\u4e00\u9635\u5e73\u9759\u4e86\uff0c\u7acb\u523b\u53c8\u6709\u4e00\u9635\u5377\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u65ad\u7684\u5728\u5fcd\u53d7\u7740\u714e\u71ac\uff1b\u7b80\u76f4\u5c31\u50cf\u662f\u4e00\u6761\u5728\u70ed\u9505\u91cc\u7684\u9c7c\u3002 \u3000\u3000\u4ed6\u600e\u4e48\u80fd\u7761\u5f97\u7740\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7684\u547c\u5438\u4eff\u4f5b\u66f4\u6c89\u91cd\uff0c\u53ef\u662f\u5979\u7684\u773c\u775b\u5374\u5df2\u6162\u6162\u7684\u7741\u5f00\u3002 \u3000\u3000\u53d1\u4eae\u7684\u773c\u775b\u5728\u9ed1\u6697\u4e2d\u9759\u9759\u7684\u51dd\u6ce8\u7740\u963f\u98de\u3002 \u3000\u3000\u96f6\u4e71\u7684\u5934\u53d1\uff0c\u642d\u5728\u4ed6\u5bbd\u9614\u7684\u524d\u989d\u4e0a\uff0c\u4ed6\u7761\u5f97\u5c31\u50cf\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u53d1\u73b0\u4ed6\u7684\u776b\u6bdb\u4e5f\u5f88\u957f\uff0c\u4eff\u4f5b\u60f3\u4f38\u624b\u53bb\u8f7b\u8f7b\u629a\u6478\u2026\u2026 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u82e5\u771f\u7684\u4f38\u51fa\u4e86\u624b\uff0c\u963f\u98de\u4ee5\u540e\u4e5f\u8bb8\u5c31\u6c38\u8fdc\u662f\u5979\u7684\u4e86\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u4e3a\u5979\u629b\u5374\u4e00 \u5207\uff0c\u653e\u5f03\u4e00\u5207\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u7684\u76ee\u5149\u662f\u6e29\u67d4\u7684\uff0c\u4f46\u5374\u53ea\u4e0d\u8fc7\u662f\u77ed\u77ed\u7684\u4e00\u77ac\u95f4\u800c\u5df2\uff0c\u5979\u7684\u624b\u5df2\u7f29\u56de\uff0c\u6e29 \u67d4\u7684\u773c\u6ce2\u4e5f\u7ed3\u6210\u4e86\u51b0\uff0c\u5374\u8f7b\u5524\u9053\uff1a\u201c\u5c0f\u98de\u4f60\u7761\u7740\u4e86\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u5f20\u5f00\u773c\u775b\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u3002 \u3000\u3000\u4ed6\u6015\u81ea\u5df1\u2026\u2026 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b49\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u4fcf\u6d88\u7684\u6ed1\u4e0b\u4e86\u5e8a\uff0c\u4fcf\u4fcf\u7684\u63d0\u8d77\u4e86\u978b\u5b50\u3002 \u3000\u3000\u5979\u624b\u63d0\u7740\u978b\uff0c\u6084\u6d88\u7684\u5f00\u95e8\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e48\u665a\u4e86\uff0c\u5979\u8fd8\u8981\u5230\u54ea\u91cc\u53bb\uff1f \u3000\u3000\u963f\u98de\u5fc3\u4e0a\u4eff\u4f5b\u7a81\u7136\u88ab\u523a\u4eba\u4e86\u4e00\u6839\u9488\uff0c\u523a\u5f97\u4ed6\u7684\u5fc3\u5728\u6536\u7f29\u3002 \u3000\u3000\u201c\u8ddf\u4e0d\u89c1\u5fc3\u4e0d\u70e6\uff0c\u6709\u4e9b\u4e8b\uff0c\u4f60\u6c38\u8fdc\u4e0d\u77e5\u9053\u53cd\u800c\u597d\u3002\u201d \u3000\u3000\u963f\u98de\u4e5f\u61c2\u5f97\uff0c\u771f\u5b9e\u5f80\u5f80\u6700\u6b8b\u9177\uff0c\u6700\u4f24\u4eba\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u5374\u518d\u4e5f\u65e0\u6cd5\u63a7\u5236\u4f4f\u81ea\u5df1\u3002 \u3000\u3000\u95e8\u5f00\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u7a81\u7136\u95ea\u8fc7\u4e00\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u4ed6\u7b11\u7684\u65f6\u5019\u751a\u81f3\u6bd4\u4e0d\u7b11\u65f6\u8fd8\u6b8b\u9177\u3002 \u3000\u3000\u6797\u4ed9\u513f\u63a9\u8d77\u95e8\uff0c\u9760\u5728\u95e8\u4e0a\uff0c\u51dd\u6ce8\u7740\u4ed6\uff0c\u201c\u5657\u201d\u7684\uff0c\u624b\u91cc\u63d0\u7740\u7684\u978b\u5b50\u843d\u4e0b\u53bb\u4e00\u53ea\uff0c\u53c8\u843d\u4e0b \u53bb\u4e00\u53ea\u3002 \u3000\u3000\u5979\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u7b97\u51c6\u6211\u4f1a\u6765\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u2026\u2026\u6211\u81ea\u5df1\u5374\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u6765\uff0c\u56e0\u4e3a\u4f60\u5df2\u53d1\u73b0\u963f\u98de\u5e76\u4e0d\u5982\u4f60\u60f3\u8c61\u4e2d\u90a3\u4e48\u53ef\u9760\uff0c\u4f60\u82e5\u8fd8\u60f3\u6d3b\u7740\uff0c\u5c31 \u53ea\u6709\u6765\u6295\u9760\u6211\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53ef\u9760\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u5f97\u95f4\u4f60\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u4e16\u4e0a\u672c\u6ca1\u6709\u7edd\u5bf9\u53ef\u9760\u7684\u7537\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u7537\u4eba\u662f\u5426\u53ef\u9760\uff0c\u5168\u5f97\u8981\u770b\u90a3\u5973\u4eba\u7684\u624b\u6bb5\u5bf9\u4ed6\u662f\u5426\u6709\u6548\u3002 \u3000\u3000\u8fd9\u9053\u7406\u6797\u4ed9\u513f\u5f53\u7136\u5f88\u660e\u767d\u3002 \u3000\u3000\u5979\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u4f1a\u5f88\u53ef\u9760\u7684\uff0c\u56e0\u4e3a\u6211\u6c38\u8fdc\u4e0d\u4f1a\u8ba9\u4f60\u89c9\u5f97\u5931\u671b\u3002\u201d \u3000\u3000\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u5979\u7528\u773c\u775b\u7b11\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u518d\u7528\u624b\uff0c\u7528\u8170\u80a2\u3001\u7528\u817f\u2026\u2026 \u3000\u3000\u5979\u4f3c\u5df2\u4e0b\u51b3\u5fc3\uff0c\u4e0d\u60dc\u7528\u4efb\u4f55\u6cd5\u5b50\uff0c\u90fd\u8981\u5c06\u8fd9\u7537\u4eba\u7f20\u4f4f\u3002 \u3000\u3000\u5979\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\uff0c\u7528\u51fa\u4e86\u5979\u6700\u6709\u6548\u7684\u6b66\u5668\u3002 \u3000\u3000\u5728\u7537\u4eba\u773c\u4e2d\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u6837\u4e1c\u897f\u6bd4\u8d64\u88f8\u7740\u7684\u5973\u4eba\u66f4\u6709\u5438\u5f15\u529b\uff0c\u4f55\u51b5\u662f\u6797\u4ed9\u513f\u8fd9\u6837 \u7684\u5973\u4eba\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4e0a\u5b98\u91d1\u8679\u7684\u773c\u775b\u5374\u8fd8\u662f\u5728\u76ef\u7740\u95e8\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u89c9\u5f97\u8fd9\u6247\u95e8\u6bd4\u5979\u8fd8\u597d\u770b\u5f97\u591a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5598\u606f\u7740\uff0c\u9053\uff1a\u201c\u62b1\u8d77\u6211\uff0c\u6211\u2026\u2026\u6211\u5df2\u7ecf\u8d70\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u62b1\u8d77\u4e86\u5979\uff0c\u4f46\u773c\u775b\u8fd8\u662f\u76ef\u7740\u95e8\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\uff0c\u95e8\u7adf\u88ab\u649e\u5f00\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u649e\u4e86\u8fdb\u6765\uff0c\u5c31\u50cf\u662f\u4e00\u56e2\u71c3\u70e7\u7740\u7684\u706b\u3002 \u3000\u3000\u6012\u706b\uff01 \u3000\u3000\u963f\u98de\uff01 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u963f\u98de\u73b0\u5728\u7684\u6124\u6012\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u60f3\u8c61\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u5374\u5df2\u95ea\u8fc7\u4e00\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u201c\u4ed6\u96be\u9053\u4e5f\u65e9\u5c31\u7b97\u51c6\u963f\u98de\u8981\u6765\u7684\uff1f\u201d \u3000\u3000\u963f\u98de\u50cf\u662f\u5b8c\u5168\u6ca1\u6709\u770b\u5230\u4ed6\u3002 \u3000\u3000\u4ed6\u773c\u775b\u91cc\u7b80\u76f4\u8fde\u4efb\u4f55\u4eba\u90fd\u770b\u4e0d\u89c1\uff0c\u770b\u5230\u7684\u53ea\u662f\u4e2a\u5669\u68a6\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u90fd\u5728\u98a4\u6296\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u970e\u4e00\u970e\uff0c\u8fd8\u662f\u52fe\u7740\u4e0a\u5b98\u91d1\u8679\u7684\u8116\u5b50\u9053\uff1a\u201c\u5230\u4f60\u8fd9\u91cc\u6765\u7684\u4eba\uff0c\u96be\u9053 \u90fd\u4e0d\u6572\u95e8\u7684\u5417\uff1f\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u53cd\u624b\u4e00\u62f3\uff0c\u6253\u5728\u95e8\u4e0a\u3002 \u3000\u3000\u662f\u94c1\u95e8\uff01 \u3000\u3000\u963f\u98de\u7684\u62f3\u5934\u5df2\u51fa\u8840\uff0c\u75bc\u5f97\u5634\u5507\u53d1\u767d\u3002 \u3000\u3000\u4f46\u4e16\u4e0a\u53c8\u6709\u54ea\u79cd\u75db\u82e6\u80fd\u6bd4\u5f97\u4e0a\u4ed6\u5fc3\u91cc\u7684\u75db\u82e6\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5374\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u662f\u75af\u5b50\u3002\u201d \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u7206\u53d1\uff0c\u72c2\u543c\u9053\uff1a\u201c\u539f\u6765\u4f60\u7adf\u662f\u8fd9\u79cd\u5973\u4eba\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u4e48\u2026\u2026\u5176\u5b9e\u6211\u4e00\u76f4\u90fd\u662f\u8fd9\u79cd\u5973\u4eba\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u6539\u53d8\u8fc7\uff0c\u4f60\u60f3 \u4e0d\u5230\u53ea\u56e0\u4e3a\u4f60\u81ea\u5df1\u592a\u611a\u8822\u3002\u201d \u3000\u3000\u5979\u51b7\u7b11\u7740\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u53ea\u8981\u7a0d\u4e3a\u806a\u660e\u4e9b\uff0c\u5c31\u4e0d\u8be5\u6765\u7684\uff01\u201d \u3000\u3000\u963f\u98de\u5389\u58f0\u9053\uff1a\u201c\u6211\u5df2\u6765\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u6765\u4e86\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u96be\u9053\u8fd8\u80fd\u54ac\u6211\u4e00\u53e3\uff1f\u2026\u2026\u6211\u8ddf\u4f60\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u4f60\u80fd \u7ba1\u5f97\u4e86\u6211\uff1f\u6211\u65e0\u8bba\u5e72\u4ec0\u4e48\uff0c\u4f60\u90fd\u53ea\u6709\u770b\u7740\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u91cc\u672c\u4f3c\u6709\u6cea\uff0c\u4f46\u6b64\u523b\u6cea\u4f3c\u5df2\u7a81\u7136\u51dd\u7ed3\u6210\u51b0\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u4f3c\u5df2\u53d8\u6210\u4e86\u6b7b\u7070\u8272\u3002 \u3000\u3000\u7edd\u671b\u7684\u6b7b\u7070\u8272\uff0c\u5c31\u50cf\u662f\u8346\u65e0\u547d\u773c\u775b\u7684\u989c\u8272\u3002 \u3000\u3000\u4ed6\u7684\u8840\u6cea\u4f3c\u5df2\u5728\u8fd9\u4e00\u77ac\u95f4\u6d41\u5c3d\uff0c\u751f\u547d\u4f3c\u5df1\u5728\u8fd9\u4e00\u77ac\u95f4\u7ec8\u6b62\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u7a81\u7136\u53d8\u6210\u4e86\u4e2a\u6b7b\u4eba\uff01 \u3000\u3000\u201c\u4e0d\u8be5\u6765\u7684\uff0c\u7684\u786e\u4e0d\u8be5\u6765\u7684\u2026\u2026\u201d \u3000\u3000\u660e\u77e5\u4e0d\u5e94\u8be5\uff0c\u4e3a\u4ec0\u4e48\u8981\u6765\u5462\uff1f \u3000\u3000\u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u4f1a\u505a\u51fa\u4e9b\u4e0d\u5e94\u505a\u7684\u4e8b\u6765\u4f24\u5bb3\u81ea\u5df1\uff1f \u7b2c\u516d\u5341\u516d\u7ae0\u3000\u81ea\u53d6\u5176\u8fb1>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u516d\u7ae0\u3000\u81ea\u53d6\u5176\u8fb1 \u3000\u3000\u963f\u98de\u4e5f\u4e0d\u77e5\u81ea\u5df1\u662f\u600e\u4e48\u8d70\u51fa\u53bb\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e00\u76f4\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\uff0c\u77a7\u7740\u4ed6\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u900f\u51fa\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u662f\u5168\u5fc3\u5168\u610f\u7684\u5bf9\u4f60\uff0c\u4f60\u73b0\u5728\u603b\u8be5\u76f8\u4fe1\u4e86\u5427\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u53ea\u6709\u4e09\u4e2a\u5b57\uff0c\u4e09\u4e2a\u5b57\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u5c06\u6797\u4ed9\u513f\u91cd\u91cd\u6454\u5728\u5e8a\u4e0a\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u8eab\u5b50\u4e5f\u5df2\u50f5\u786c\u3002 \u3000\u3000\u4f46\u5979\u9762\u4e0a\u7684\u8868\u60c5\u65e2\u4e0d\u662f\u60b2\u54c0\uff0c\u4e5f\u4e0d\u662f\u6124\u6012\uff0c\u800c\u662f\u6050\u60e7\u3002 \u3000\u3000\u5f53\u5979\u53d1\u73b0\u81ea\u5df1\u5e76\u6ca1\u6709\u771f\u7684\u5b8c\u5168\u5f81\u670d\u963f\u98de\u65f6\uff0c\u4e5f\u6709\u8fc7\u8fd9\u79cd\u6050\u60e7\uff0c\u53ea\u4e0d\u8fc7\u6050\u60e7\u5f97\u8fd8\u6ca1\u6709\u5982\u6b64 \u6df1\u3002 \u3000\u3000\u201c\u6211\u7a76\u7adf\u505a\u4e86\u4e9b\u4ec0\u4e48\uff1f\u53c8\u5f97\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u624d\u662f\u771f\u6b63\u53ef\u9760\u7684\uff1f\u201d \u3000\u3000\u5979\u6162\u6f2b\u7684\u7ad9\u8d77\u6765\uff0c\u5c06\u65b9\u624d\u8131\u4e0b\u7684\u8863\u670d\u4e00\u4ef6\u4ef6\u62fe\u8d77\uff0c\u4e00\u4ef6\u4ef6\u53e0\u597d\uff0c\u53e0\u5f97\u5f88\u6162\uff0c\u800c\u4e14\u5f88\u4ed4\u7ec6 \u3002 \u3000\u3000\u7b49\u5979\u56db\u80a2\u7684\u808c\u8089\u53c8\u6062\u590d\u67d4\u8f6f\uff0c\u5979\u5c31\u53c8\u8eba\u4e86\u4e0b\u53bb\uff0c\u6446\u51fa\u6700\u751c\u871c\u7684\u5fae\u7b11\uff0c\u6700\u52a8\u4eba\u7684\u59ff\u52bf\u3002 \u3000\u3000\u5979\u51b3\u5fc3\u8fd8\u8981\u8bd5\u8bd5\u3002 \u3000\u3000\u752c\u9053\u7684\u5c3d\u5934\uff0c\u6709\u9053\u95e8\u69db\u3002 \u3000\u3000\u963f\u98de\u50cf\u9003\u4e00\u822c\u5954\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u7eca\u5230\u4e86\u95e8\u69db\uff0c\u5657\u7684\u8dcc\u51fa\u95e8\u5916\u3002 \u3000\u3000\u4ed6\u5c31\u8fd9\u6837\u5e73\u5e73\u7684\u8dcc\u4e86\u4e0b\u6765\uff0c\u5c31\u8fd9\u6837\u5e73\u5e73\u7684\u4f0f\u5728\u5730\u4e0a\uff0c\u65e2\u6ca1\u6709\u52a8\uff0c\u4e5f\u6ca1\u6709\u722c\u8d77\uff0c\u751a\u81f3\u4ec0\u4e48 \u90fd\u6ca1\u6709\u53bb\u60f3\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u8111\u5b50\u91cc\u7adf\u4f1a\u7a81\u7136\u53d8\u6210\u4e00\u7247\u7a7a\u767d\u3002 \u3000\u3000\u8fd9\u771f\u662f\u4ef6\u5947\u602a\u7684\u4e8b\u2026\u2026 \u3000\u3000\u79cb\u5df2\u6b8b\uff0c\u5e72\u71e5\u7684\u6ce5\u571f\u4e2d\u5e26\u7740\u79cd\u843d\u53f6\u7684\u82ac\u82b3\u3002 \u3000\u3000\u963f\u98de\u7528\u5634\u5543\u7740\u6ce5\u571f\uff0c\u4e00\u53e3\u53e3\u54bd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7c97\u6da9\u5e72\u71e5\u7684\u6ce5\u571f\uff0c\u6162\u6162\u7684\u7ecf\u8fc7\u4ed6\u7684\u54bd\u5589\uff0c\u6d41\u5165\u4ed6\u7684\u80a0\u80c3\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u60f3\u7528\u6ce5\u571f\u6765\u5c06\u81ea\u5df1\u586b\u6ee1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6574\u4e2a\u4eba\u90fd\u5df2\u53d8\u6210\u7a7a\u7684\uff0c\u6ca1\u6709\u601d\u60f3\uff0c\u6ca1\u6709\u611f\u89c9\uff0c\u6ca1\u6709\u8840\u8089\uff0c\u6ca1\u6709\u7075\u9b42\uff0c\u4e8c\u5341\u51e0\u5e74\u7684 \u751f\u547d\uff0c\u5230\u73b0\u5728\u7adf\u53ea\u5269\u4e0b\u4e00\u7247\u7a7a\u767d\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u9759\u9759\u7684\u77a7\u4e86\u4ed6\u534a\u664c\uff0c\u4ece\u4ed6\u8eab\u4e0a\u8de8\u4e86\u8fc7\u53bb\uff0c\u8d70\u5230\u4ed6\u5c4b\u5b50\u91cc\uff0c\u53d6\u51fa\u4e86 \u90a3\u67c4\u5251\u3002 \u3000\u3000\u201c\u557c\u201d\u7684\u4e00\u58f0\uff0c\u5251\u63d2\u4e0b\u3002 \u3000\u3000\u5c31\u8d34\u7740\u963f\u98de\u7684\u8138\uff0c\u63d2\u5165\u4e86\u6ce5\u571f\u4e2d\u3002 \u3000\u3000\u51b0\u51b7\u7684\u5251\u950b\uff0c\u5728\u4ed6\u9762\u988a\u4e0a\u5212\u7834\u4e86\u4e00\u6761\u8840\u53e3\uff0c\u8840\u6cbf\u7740\u5251\u950b\u6e17\u5165\u6ce5\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u58f0\u97f3\u6bd4\u5251\u950b\u66f4\u9510\u5229\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u5251\uff01\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u6b7b\uff0c\u5f88\u5bb9\u6613\uff01\u201d \u3000\u3000\u963f\u98de\u8fd8\u662f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u73b0\u5728\u82e5\u6b7b\u4e86\uff0c\u7edd\u6ca1\u6709\u4eba\u4f1a\u4e3a\u4f60\u60b2\u54c0\uff0c\u66f4\u6ca1\u6709\u4eba\u4f1a\u89c9\u5f97\u53ef\u60dc\uff0c\u4e0d\u51fa\u4e09\u5929 \uff0c\u4f60\u7684\u5c38\u4f53\u5c31\u4f1a\u6a61\u91ce\u72d7\u822c\u8150\u70c2\u5728\u9634\u6c9f\u91cc\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\uff0c\u63a5\u9053\uff1a\u201c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u7740\u4e3a\u4e86\u90a3\u79cd\u5973\u4eba\u800c\u6b7b\uff0c\u7b80\u76f4\u8fde\u72d7\u90e8\u4e0d\u5982\u3002\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u53cd\u624b\u62d4\u51fa\u4e86\u5251\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u8840\u7ea2\uff0c\u5634\u91cc\u585e\u6ee1\u4e86\u6ce5\u571f\uff0c\u770b\u6765\u5c31\u50cf\u662f\u91ce\u517d\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u60f3\u6740\u6211\uff1f\u662f\u4e0d\u662f\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u624b\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u98a4\u6296\uff0c\u624b\u80cc\u4e0a\u4e00\u6839\u6839\u9752\u7b4b\u66b4\u9732\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u53bb\u6740\u5979\uff0c\u6211\u4e5f\u7edd\u4e0d\u963b\u62e6\u4f60\u3002\u201d \u3000\u3000\u963f\u98de\u970d\u7136\u8f6c\u8eab\uff0c\u53c8\u505c\u4f4f\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u73b0\u5728\u5df2\u8fde\u6740\u4eba\u7684\u80c6\u5b50\u90fd\u6ca1\u6709\u4e86\uff1f\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u5f2f\u4e0b\u8170\uff0c\u5455\u5410\u8d77\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u76ee\u5149\u6e10\u6e10\u67d4\u548c\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u73b0\u5728\u6d3b\u7740\u6bd4\u6b7b\u56f0\u96be\u5f97\u591a\uff0c\u4f60\u73b0\u5728\u82e5\u6b7b\u4e86\uff0c \u5c31\u662f\u9003\u907f\uff0c\u6211\u60f3\u4f60\u7edd\u4e0d\u662f\u8fd9\u6837\u7684\u61e6\u592b\u3002\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u7b54\u5e94\u6211\u7684\u4e8b\uff0c\u73b0\u5728\u8fd8\u6ca1\u6709\u505a\u3002 \u3000\u3000\u963f\u98de\u7684\u5455\u5410\u5df2\u505c\u6b62\uff0c\u4e0d\u505c\u7684\u5598\u606f\u7740\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u6709\u52c7\u6c14\u6d3b\u4e0b\u53bb\uff0c\u73b0\u5728\u5c31\u8ddf\u7740\u6211\u8d70\uff01\u201d \u3000\u3000\u4ed6\u9aa4\u7136\u8f6c\u8fc7\u8eab\uff0c\u518d\u4e5f\u4e0d\u77a7\u963f\u98de\u4e00\u773c\u3002 \u3000\u3000\u963f\u98de\u671b\u7740\u81ea\u5df1\u5410\u5728\u5730\u4e0a\u7684\u4e1c\u897f\uff0c\u7a81\u4e5f\u8f6c\u8fc7\u8eab\uff0c\u8ddf\u7740\u4ed6\u8d70\u4e86\u95e8\u53bb\u3002 \u3000\u3000\u4ed6\u59cb\u7ec8\u6ca1\u6709\u6d41\u6cea\u3002 \u3000\u3000\u4e0d\u6d41\u6cea\u7684\u4eba\uff0c\u53ea\u6d41\u8840\uff01 \u3000\u3000\u4ed6\u5df2\u51c6\u5907\u6d41\u8840\uff01 \u3000\u3000\u7a7f\u8fc7\u4fa7\u95e8\uff0c\u8fd8\u6709\u4e2a\u5c0f\u5c0f\u7684\u9662\u5b50\u3002 \u3000\u3000\u9662\u5b50\u91cc\u4e00\u682a\u5b64\u96f6\u96f6\u7684\u767d\u6768\u6b63\u5728\u79cb\u98ce\u4e2d\u53f9\u606f\uff0c\u53f9\u606f\u7740\u751f\u547d\u800c\u77ed\u4fc3\uff0c\u4eba\u7684\u611a\u8822\uff0c\u7adf\u4e0d\u77e5\u5bf9\u8fd9 \u77ed\u4fc3\u7684\u751f\u547d\u591a\u52a0\u73cd\u60dc\u3002 \u3000\u3000\u8fd8\u6709\u706f\u5149\u3002 \u3000\u3000\u706f\u5149\u4ece\u95e8\u7f1d\u91cc\u7167\u51fa\u6765\uff0c\u7167\u5728\u4e0a\u5b98\u91d1\u8679\u811a\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u505c\u4f4f\u4e86\u811a\uff0c\u5ffd\u7136\u8f6c\u8eab\u62cd\u4e86\u62cd\u963f\u98de\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u633a\u8d77\u80f8\u819b\u6765\uff0c\u8d70\u8fdb\u53bb\uff0c\u83ab\u8981\u8ba9 \u4eba\u77a7\u7740\u6076\u5fc3\u3002\u201d \u3000\u3000\u963f\u98de\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u91cc\u6709\u4ec0\u4e48\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e3a\u4ec0\u4e48\u5c06\u4ed6\u5e26\u5230\u8fd9\u91cc\u6765\uff1f \u3000\u3000\u963f\u98de\u6839\u672c\u4e0d\u53bb\u60f3\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u5fc3\u82e5\u5df2\u6b7b\uff0c\u8fd8\u6709\u4f55\u4ff1\uff1f \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u4e03\u4e2a\u4eba\u3002 \u3000\u3000\u516d\u4e2a\u7edd\u9876\u7f8e\u4e3d\u7684\u5973\u4eba\u3002 \u3000\u3000\u4e03\u5f20\u7f8e\u4e3d\u7684\u7b11\u8138\u90fd\u8fce\u7740\u4ed6\uff0c\u4e03\u53cc\u7f8e\u4e3d\u7684\u773c\u775b\u90fd\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u963f\u98de\u6014\u5f80\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u53c8\u95ea\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u60a0\u7136\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4e16\u4e0a\u7f8e\u4e3d\u7684\u5973\u4eba\u5e76\u4e0d\u6b62\u5979\u4e00\u4e2a\uff0c\u662f\u4e48 \uff1f\u201d \u3000\u3000\u5c11\u5973\u4eec\u94f6\u94c3\u822c\u7b11\u4e86\uff0c\u8d70\u8fc7\u6765\uff0c\u62c9\u6ce8\u4e86\u963f\u98de\u7684\u624b\u3002 \u3000\u3000\u8102\u7c89\u4e2d\u8fd8\u6709\u9152\u9999\u3002 \u3000\u3000\u5c4b\u89d2\u5806\u7740\u51e0\u53ea\u7bb1\u5b50\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6253\u5f00\u4e86\u4e00\u53ea\u7bb1\u5b50\uff0c\u706f\u5149\u7acb\u523b\u6697\u6de1\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7bb1\u5b50\u91cc\u73e0\u5149\u5b9d\u6c14\u8f89\u714c\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u53ea\u8981\u6709\u8fd9\u4e48\u6837\u4e00\u53e3\u7bb1\u5b50\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u4e70\u5230\u4e00\u767e\u4e2a\u5c11\u5973\u7684\u5fc3\u3002\u201d \u3000\u3000\u5c11\u5973\u4eec\u5403\u5403\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7684\u5fc3\u5df2\u7ecf\u662f\u4ed6\u7684\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u4e70\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4f1a\u8bf4\u751c\u8a00\u871c\u8bed\u4e5f\u4e0d\u53ea\u5979\u4e00\u4e2a\uff0c\u8fd9\u672c\u662f\u5973\u4eba\u5929\u751f\u5c31\u4f1a\u8bf4\u7684 \u3002 \u3000\u3000\u5c11\u5973\u4eec\u9053\uff1a\u201c\u6211\u4eec\u8bf4\u7684\u662f\u771f\u6d3b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u771f\u5c31\u662f\u5047\uff0c\u5047\u5c31\u662f\u771f\uff0c\u771f\u771f\u5047\u5047\uff0c\u672c\u4e0d\u5fc5\u592a\u8ba4\u771f\u3002\u201d \u3000\u3000\u4ed6\u6162\u9992\u7684\u8d70\u5230\u963f\u98de\u9762\u524d\uff0c\u51dd\u6ce8\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u5c06\u4e00\u58f6\u9152\u5168\u90e8\u559d\u4e86\u4e0b\u53bb\uff0c\u7a81\u7136\u4ef0\u9762\u5927\u7b11\u9053\uff1a\u201c\u6b7b\uff1f\u8c01\u60f3\u6b7b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7b11\u4e86\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u6d3b\u4e0b\u53bb\uff0c\u8fd9\u4e9b\u5168\u90e8\u662f\u4f60\u7684\uff01\u201d \u3000\u3000\u963f\u98de\u7528\u529b\u62b1\u8d77\u4e86\u4e00\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u4ed6\u62b1\u5f97\u8fd9\u4e48\u7d27\uff0c\u4f3c\u4e4e\u60f3\u5c06\u5979\u63c9\u788e\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6084\u6084\u9000\u4e86\u51fa\u53bb\uff0c\u6084\u6084\u63a9\u8d77\u4e86\u95e8\u3002 \u3000\u3000\u7b11\u58f0\u4e0d\u505c\u7684\u4ece\u95e8\u91cc\u4f20\u51fa\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8d1f\u624b\u8d70\u5230\u9662\u4e2d\uff0c\u4ef0\u671b\u7740\u5929\u8fb9\u6b8b\u6708\uff0c\u5583\u5583\u9053\uff1a\u201c\u660e\u5929\u4e00\u5b9a\u4e5f\u662f\u597d\u5929\u6c14\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u559c\u6b22\u597d\u5929\u6c14\u3002 \u3000\u3000\u5929\u6c14\u597d\u7684\u65f6\u5019\uff0c\u8840\u5e72\u5f97\u7094\uff0c\u4eba\u6b7b\u5f97\u4e5f\u5feb\uff01 \u3000\u3000\u597d\u5929\u6c14\uff01 \u3000\u3000\u98de\u7802\u3001\u5c18\u571f\u3001\u957f\u8857\u3002 \u3000\u3000\u9633\u5149\u65b0\u9c9c\u800c\u5f3a\u70c8\u3002 \u3000\u3000\u4e00\u9a91\u8bc0\u9a6c\uff0c\u81ea\u201c\u5982\u4e91\u5ba2\u6808\u201d\u5185\u98de\u9a70\u800c\u51fa\u3002\u9a6c\u4e0a\u4eba\u6d53\u7709\u73af\u773c\u3001\u795e\u60c5\u5f6a\u608d\uff0c\u8eab\u4e0a\u7684\u9ec4\u8863\u670d\u655e \u5f00\uff0c\u94c1\u4e00\u822c\u7684\u80f8\u819b\u8fce\u7740\u9633\u5149\u548c\u98de\u7802\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u53ea\u60f3\u7740\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u201c\u5c06\u963f\u98de\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u8981\u4ed6\u6740\u4e24\u4e2a\u7a7f\u7d2b\u7ea2\u8863\u88f3\u7684\u4eba\uff01\u201d \u3000\u3000\u8fd9\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u547d\u4ee4\uff01 \u3000\u3000\u91d1\u94b1\u5e2e\u5c5e\u4e0b\uff0c\u53ea\u8981\u5f97\u5230\u4e0a\u5b98\u91d1\u8679\u7684\u547d\u4ee4\uff0c\u5fc3\u91cc\u5c31\u518d\u6ce1\u4e0d\u4f1a\u53bb\u60f3\u522b\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u8138\u8272\uff0c\u51e0\u4e4e\u5c31\u548c\u4ed6\u8eab\u4e0a\u7684\u8863\u670d\u4e00\u6837\uff0c\u7ea2\u5f97\u53d1\u7d2b\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u559d\u9152\u3002 \u3000\u3000\u6743\u529b\u4e4b\u9189\u4eba\uff0c\u6bd4\u9152\u66f4\u5f3a\u70c8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5c45\u7136\u4eb2\u81ea\u6765\u8fce\u63a5\u4ed6\uff0c\u8fd9\u662f\u4f55\u7b49\u5a01\u98ce\uff0c\u4f55\u7b49\u5149\u91c7\u3002 \u3000\u3000\u4ed6\u6068\u4e0d\u5f97\u5c06\u6b66\u6797\u4e2d\u6240\u6709\u7684\u4eba\u5168\u90e8\u8bf7\u5230\u8fd9\u91cc\u6765\uff0c\u77a7\u77a7\u4ed6\u4eca\u65e5\u7684\u5a01\u98ce\u548c\u5149\u91c7\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u6765\u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u5728\u6c5f\u7322\u4e2d\u6df7\u7684\u4eba\uff0c\u4e5f\u5e76\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u559c\u6b22\u60f9\u9ebb\u70e6\u7684\u3002 \u3000\u3000\u9152\u7b75\u5df2\u5f20\u3002 \u3000\u3000\u4e09\u676f\u9152\u4e0b\u809a\uff0c\u9f99\u5578\u4e91\u7684\u8138\u66f4\u7ea2\u4e86\uff0c\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u5927\u54e5\u7684\u6d53\u60c5\u539a\u610f\uff0c\u5b9e\u4ee4\u505a\u5144\u5f1f\u7684\u6c38\u751f\u96be \u5fd8\uff0c\u6765\uff0c\u5144\u5f1f\u656c\u5927\u54e5\u4e00\u676f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u6cbe\u9152\u3002\u201d \u3000\u3000\u7ad9\u5728\u8eab\u540e\u7684\u9f99\u5c0f\u4e91\u7acb\u523b\u5012\u4e86\u676f\u8336\u8fc7\u6765\uff0c\u966a\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8001\u6015\u5c31\u4ee5\u8336\u4ee3\u9152\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u559d\u8336\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u6014\u4e86\u6014\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5927\u54e5\u5e73\u65e5\u559d\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6c34\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u53c8\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u53ea\u559d\u6c34\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6c34\u80fd\u6e05\u5fc3\uff0c\u53ea\u559d\u6c34\u7684\u4eba\uff0c\u5fc3\u7edd\u4e0d\u4f1a\u4e71\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5df2\u5012\u4e86\u676f\u6c34\u8fc7\u6765\uff0c\u53cc\u624b\u5949\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u51c0\u6c34\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u53ea\u6709\u6e34\u7684\u65f6\u5019\u624d\u559d\u6c34\uff0c\u73b0\u5728\u6211\u4e0d\u6e34\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u8138\u8272\u5df2\u6709\u4e9b\u53d1\u82e6\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u8fd8\u662f\u9762\u4e0d\u6539\u8272\uff0c\u966a\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c0f\u4f84\u5c31\u66ff\u8001\u4f2f\u559d\u4e00\u676f\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u5012\u7684\uff0c\u4f60\u559d\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5c06\u4e00\u676f\u8336\u3001\u4e00\u676f\u9152\u3001\u4e00\u676f\u6c34\uff0c\u5168\u90e8\u559d\u4e86\u4e0b\u53bb\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53e4\u4eba\u6b43\u8840\u4e3a\u76df\uff0c\u4ee5\u793a\u9ad8 \u4e49\uff0c\u8001\u4f2f\u4e0e\u5bb6\u7236\u90fd\u662f\u901a\u8fbe\u4e4b\u4e0a\uff0c\u81ea\u7136\u4e0d\u5fc5\u5982\u6b64\u770b\u91cd\u5f62\u5f0f\uff0c\u4f46\u9999\u70db\u4e4b\u793c\u5374\u603b\u662f\u4e0d\u53ef\u5c11\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u9999\u70db\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u796d\u5929\u5730\uff0c\u796d\u9b3c\u795e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u9b54\u795e\u4e0d\u6765\u796d\u6211\uff0c\u6211\u4e3a\u4f55\u8981\u796d\u4ed6\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u50cf\u8001\u6015\u8fd9\u6837\u7684\u76d6\u4e16\u82f1\u96c4\uff0c\u9b3c\u795e\u5fc5\u4e5f\u5341\u5206\u76f8\u656c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u4e0d\u656c\u4ed6\uff0c\u4ed6\u4e3a\u4f55\u8981\u656c\u6211\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u966a\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8001\u4f2f\u7684\u610f\u601d\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u677f\u7740\u8138\u9053\uff1a\u201c\u662f\u4ee4\u5c0a\u8981\u548c\u6211\u7ed3\u62dc\uff0c\u8fd8\u662f\u4f60\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u5f53\u7136\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u7ad9\u5230\u4e00\u8fb9\u53bb\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u8eac\u8eab\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u5782\u624b\u9000\u4e0b\uff0c\u5c45\u7136\u8fd8\u662f\u9762\u4e0d\u6539\u8272\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8138\u4e0a\u5374\u5df2\u6709\u4e9b\u53d1\u80b2\uff0c\u52c9\u5f3a\u9053\uff1a\u201c\u72ac\u5b50\u65e0\u793c\uff0c\u5927\u54e5\u5343\u4e07\u83ab\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u6837\u7684\u513f\u5b50\uff0c\u600e\u80fd\u8bf4\u662f\u72ac\u5b50\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u4e0d\u662f\u6211\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u5446\u5728\u90a3\u91cc\uff0c\u8fd8\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u6d53\u7709\u73af\u76ee\u7684\u5927\u6c49\u5306\u5306\u5954\u4e86\u8fdb\u6765\uff0c\u5306\u5306\u78d5\u4e86\u4e2a\u5934\uff0c\u8f6c\u5230\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u8eab\u540e\uff0c\u8eac\u8eab \u4f4e\u8bed\u9053\uff1a\u201c\u4ee4\u5df2\u4f20\u53bb\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u5927\u6c49\u7684\u58f0\u97f3\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u770b\u6765\u4ed6\u5df2\u9189\u4e86\uff0c\u9189\u5f97\u5f88\u5389\u5bb3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u7528\u51b7\u6c34\u6cfc\uff0c\u82e5\u6cfc\u4e0d\u9192\uff0c\u5c31\u7528\u5c3f\u3002\u201d \u3000\u3000\u5927\u6c49\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u5b9e\u5728\u4f69\u670d\u6781\u4e86\u3002 \u3000\u3000\u9664\u4e86\u6b7b\u4eba\u5916\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u8fde\u5c3f\u4e5f\u6cfc\u4e0d\u9192\u7684\u4eba\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4e5f\u6ca1\u6709\u542c\u5230\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\uff0c\u8bd5\u63a2\u7740\u9053\uff1a\u201c\u5927\u54e5\u83ab\u975e\u5728\u7b49\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8c01\u914d\u8981\u6211\u7b49\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u65e2\u7136\u4eba\u90fd\u5df2\u5230\u4e86\uff0c\u5927\u54e5\u4e3a\u4f55\u8fd8\u4e0d\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u5411\u4ed6\u7b11\u4e86\u7b11\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u8d35\u5e9a\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u865a\u957f\u4e94\u5341\u4e00\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u6bd4\u6211\u5927\uff0c\u662f\u5426\u6211\u8be5\u53eb\u4f60\u4e00\u58f0\u5927\u54e5\u624d\u5bf9\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u8d76\u7d27\u79bb\u5e2d\u800c\u8d77\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5e74\u65e0\u957f\u5e7c\uff0c\u80fd\u8005\u4e3a\u5e08\uff0c\u5927\u54e5\u5343\u4e07\u83ab\u6298\u715e\u5c0f\u5f1f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u65e2\u7136\u6211\u662f\u5927\u54e5\uff0c\u4f60\u5c31\u8be5\u542c\u6211\u7684\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u597d\uff0c\u5750\u4e0b\u6765\u559d\u9152\u2026\u2026\u5148\u656c\u8fd9\u4e9b\u670b\u53cb\u4e00\u676f\u3002\u201d \u3000\u3000\u80fd\u5750\u5728\u8fd9\u684c\u5b50\u4e0a\u559d\u9152\u7684\u4eba\uff0c\u9762\u5b50\u5fc5\u5b9a\u4e0d\u5c0f\u3002 \u3000\u3000\u4f46\u5750\u5728\u8fd9\u91cc\u559d\u9152\uff0c\u7b80\u76f4\u662f\u53d7\u7f6a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6839\u672c\u6ca1\u6709\u52a8\u8fc7\u7b77\u5b50\uff0c\u522b\u4eba\u4e5f\u89c9\u5f97\u624b\u91cc\u7684\u8fd9\u53cc\u7b77\u5b50\u4eff\u4f5b\u6709\u51e0\u767e\u65a4\u91cd\uff0c\u54ea\u91cc\u5403\u5f97\u4e0b \u53bb\u3002 \u3000\u3000\u53ea\u542c\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u9152\u83dc\u5df2\u53eb\u6765\uff0c\u4e0d\u5403\u5c31\u662f\u6d6a\u8d39\uff0c\u6211\u6700\u6068\u6d6a\u8d39\uff0c\u5404\u4f4d\u8bf7\u3002\u201d \u3000\u3000\u4e03\u516b\u53cc\u7b77\u5b50\u7acb\u523b\u540c\u65f6\u4f38\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u966a\u7b11\u9053\uff1a\u201c\u8fd9\u9c7c\u8fd8\u65b0\u9c9c\uff0c\u5927\u54e5\u4e3a\u4f55\u4e0d\u4e5f\u5c1d\u4e00\u5c1d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u997f\u7684\u65f6\u5019\u624d\u5403\uff0c\u73b0\u5728\u6211\u4e0d\u997f\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u997f\u7684\u65f6\u5019\u5403\u4e5f\u662f\u6d6a\u8d39\u3002\u201d \u3000\u3000\u7acb\u523b\u53c8\u6709\u51e0\u53cc\u7b77\u5b50\u653e\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4eba\u9762\u767d\u8eab\u957f\uff0c\u624b\u4e0a\u6234\u7740\u597d\u5927\u7684\u4e00\u5757\u7fe1\u7fe0\u6591\u6307\uff0c\u7eff\u5f97\u8000\u773c\uff0c\u8170\u7554\u60ac\u7740\u7684\u4e4c\u9798\u957f\u5251\u4e0a \uff0c\u4e5f\u9576\u7740\u51e0\u5757\u7fe1\u7fe0\u3002 \u3000\u3000\u8fd9\u4eba\u867d\u4e5f\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u7709\u76ee\u95f4\u5374\u5df2\u9690\u9690\u9732\u51fa\u4e0d\u8010\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u7684\u786e\u4ece\u6765\u4e5f\u6ca1\u6709\u53d7\u8fc7\u8fd9\u79cd\u6c14\uff0c\u53ea\u540e\u6094\u8fd9\u6b21\u4e3a\u6cb3\u8981\u6765\u3002 \u3000\u3000\u4ed6\u672c\u4e0d\u8be5\u6765\u7684\u3002 \u3000\u3000\u201c\u78a7\u534e\u8f69\u201d\u91d1\u5b57\u62db\u724c\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u505a\u73e0\u5b9d\u751f\u610f\u7684\u4e00\u542c\u5230\u201c\u78a7\u534e\u8f69\u201d\u4e09\u4e2a\u5b57\uff0c\u5c31\u597d\u50cf\u7ec3\u5200 \u7684\u4eba\u542c\u5230\u201c\u5c0f\u674e\u98de\u5200\u201d\u4e00\u6837\u3002 \u3000\u3000\u201c\u78a7\u534e\u8f69\u201d\u7684\u5c11\u4e3b\u4eba\u897f\u95e8\u7389\uff0c\u66f4\u662f\u4ece\u5c0f\u5c31\u88ab\u4eba\u50cf\u51e4\u51f0\u822c\u6367\u7740\uff0c\u4ed6\u8981\u5f80\u4e1c\uff0c\u7edd\u6ca1\u6709\u4eba\u6562\u8bf4 \u897f\u3002 \u3000\u3000\u4ed6\u8981\u7ec3\u5251\uff0c\u7acb\u523b\u5c31\u6709\u4eba\u5c06\u80fd\u8bf7\u5f97\u5230\u7684\u540d\u5251\u5bb9\u5168\u90e8\u8bf7\u6765\uff0c\u53c8\u6709\u4eba\u8bbe\u6cd5\u66ff\u4ed6\u627e\u6765\u4e00\u67c4\u201c\u677e\u7eb9 \u53e4\u5251\u3002\u201d \u3000\u3000\u5341\u5c81\u7684\u65f6\u5019\uff0c\u897f\u95e8\u7389\u5c31\u7528\u8fd9\u67c4\u5251\u6740\u8fc7\u4eba\u2026\u2026 \u3000\u3000\u6ca1\u6709\u522b\u7684\u539f\u56e0\uff0c\u53ea\u56e0\u4e3a\u4ed6\u60f3\u5c1d\u5c1d\u6740\u4eba\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u6240\u4ee5\u5c31\u6709\u4eba\u60f3\u6cd5\u5b50\u53bb\u627e\u4e2a\u4eba\u6765\u8ba9\u4ed6\u6740 \u3002 \u3000\u3000\u50cf\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u73b0\u5728\u5374\u5750\u5728\u8fd9\u91cc\u53d7\u8fd9\u79cd\u6c14\uff0c\u5c82\u975e\u51a4\u6789\u5f97\u5f88\u3002 \u3000\u3000\u4ed6\u4e5f\u6839\u672c\u6ca1\u6709\u52a8\u8fc7\u7b77\u5b50\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u773c\u775b\u5c31\u76ef\u7740\u897f\u95e8\u7389\u7684\u773c\u775b\u3002 \u3000\u3000\u897f\u95e8\u7389\u672c\u6765\u4e5f\u60f3\u626d\u8fc7\u5934\uff0c\u53bb\u77a7\u522b\u7684\u5730\u65b9\uff0c\u4f46\u4e0a\u5b98\u91d1\u8679\u7684\u76ee\u5149\u5374\u4f3c\u6709\u4e00\u79cd\u5947\u5f02\u7684\u5438\u5f15\u529b\u3002 \u3000\u3000\u4ed6\u82e5\u76ef\u7740\u4e00\u4e2a\u4eba\uff0c\u90a3\u4eba\u7adf\u53ea\u6709\u88ab\u4ed6\u76ef\u7740\u3002 \u3000\u3000\u88ab\u8fd9\u79cd\u76ee\u5149\u76ef\u7740\uff0c\u7684\u786e\u4e0d\u662f\u4ef6\u597d\u53d7\u7684\u4e8b\u3002 \u3000\u3000\u897f\u95e8\u7389\u53ea\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u5b50\u6e10\u6e10\u53d1\u51b7\uff0c\u4ece\u6307\u5c16\u5f00\u59cb\uff0c\u4e00\u76f4\u51b7\u5165\u80cc\u810a\uff0c\u51b7\u5165\u9aa8\u9ad3\uff0c\u51b7\u5230\u5fc3\u91cc \u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u9053\uff1a\u201c\u8fd9\u9152\u83dc\u4e2d\u6709\u6bd2\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u600e\u4f1a\u6709\u6bd2\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u65e2\u7136\u65e0\u6bd2\uff0c\u4f60\u4e3a\u4f55\u4e0d\u5403\uff1f\u201d \u3000\u3000\u897f\u95e8\u738b\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u4e0d\u997f\uff0c\u4e0d\u6562\u6d6a\u8d39\u5e2e\u4e3b\u7684\u9152\u83dc\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u771f\u7684\u4e0d\u997f\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u9053\uff1a\u201c\u771f\u2026\u2026\u771f\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6d6a\u8d39\u8fd8\u53ef\u539f\u8c05\uff0c\u8bf4\u8c0e\u5374\u4e0d\u53ef\u6055\uff0c\u4f60\u660e\u767d\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u7684\u706b\u6c14\u4e5f\u5fcd\u4e0d\u6ce8\u8981\u4e0a\u6765\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u5c0f\u4e8b\uff0c\u5728\u4e0b\u53c8\u4f55\u5fc5\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8bf4\u8c0e\u5c31\u662f\u8bf4\u8c0e\uff0c\u5927\u4e8b\u5c0f\u4e8b\u5168\u90e8\u4e00\u6837\u3002\u201d \u3000\u3000\u897f\u95e8\u7389\u9053\uff1a\u201c\u4e0d\u997f\u5c31\u662f\u4e0d\u997f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u73b0\u5728\u5df2\u8fc7\u4e86\u5348\u996d\u65f6\u5019\uff0c\u4f60\u600e\u4f1a\u4e0d\u997f\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u9053\uff1a\u201c\u4e5f\u8bb8\u5728\u4e0b\u5403\u7684\u65e9\u70b9\u8fd8\u672a\u6d88\u5316\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u65e9\u70b9\u662f\u5728\u57ce\u5357\u2018\u594e\u5143\u9986\u2019\u5403\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u8981\u4e86\u4e00\u7897\u9ebb\u6cb9\u9e21\uff0c\u4e00\u7897\u7206\u9cdd\u9c7c\u9762\uff0c\u5916\u5e26\u4e00\u7b3c\u8089\u5305\uff0c\u9e21\u5403\u4e86\u4e24\u5757 \uff0c\u9762\u4f60\u5403\u4e86\u534a\u7897\uff0c\u8089\u5305\u5403\u4e86\u4e03\u4e2a\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5e2e\u4e3b\u5c06\u5728\u4e0b\u7684\u4e00\u4e3e\u4e00\u52a8\u90fd\u8c03\u67e5\u5f97\u5982\u6b64\u4ed4\u7ec6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u5403\u7684\u8fd9\u4e9b\u4e1c\u897f\u65e2\u7136\u8fd8\u672a\u6d88\u5316\uff0c\u60f3\u5fc5\u8fd8\u7559\u5728\u809a\u5b50\u91cc\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u7389\u9053\uff1a\u201c\u60f3\u5fc5\u8fd8\u5728\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5256\u5f00\u4ed6\u7684\u809a\u5b50\u77a7\u77a7\uff0c\u8fd8\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u5927\u5bb6\u867d\u65e9\u5df2\u770b\u51fa\u4ed6\u662f\u6210\u5fc3\u5728\u627e\u897f\u95e8\u7389\u7684\u9ebb\u70e6\u4e86\uff0c\u5374\u672a\u60f3\u5230\u5ffd\u987b\u7adf\u5982\u6b64\u5927\uff0c\u8fd9\u53e5\u8bdd\u8bf4\u51fa\uff0c \u6bcf\u4e2a\u4eba\u9762\u4e0a\u90fd\u4e0d\u7981\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4ee4\u51fa\u5982\u5c71\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5c31\u4e00\u5b9a\u80fd\u505a\u5f97\u5230\u3002 \u3000\u3000\u897f\u95e8\u7389\u66f4\u662f\u9762\u5982\u6b7b\u7070\uff0c\u5403\u5403\u9053\uff1a\u201c\u5e2e\u4e3b\u83ab\u975e\u662f\u5728\u5f00\u73a9\u7b11\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fde\u7406\u90fd\u4e0d\u518d\u7406\u4ed6\uff0c\u5df2\u6709\u56db\u4e2a\u9ec4\u886b\u4eba\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u897f\u95e8\u7389\u970d\u7136\u8d77\u8eab\uff0c\u53cd\u624b\u62d4\u5251\uff0c\u52a8\u4f5c\u5e72\u51c0\u5229\u843d\uff0c\u5927\u5bb6\u867d\u7136\u8fd8\u672a\u770b\u5230\u4ed6\u51fa\u624b\uff0c\u5df2\u77e5\u9053\u4ed6\u5251\u6cd5 \u5fc5\u5b9a\u4e0d\u5f31\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u957f\u5251\u8fd8\u672a\u51fa\u9798\uff0c\u7a81\u542c\u201c\u5578\u201d\u7684\u4e00\u58f0\uff0c\u4e0a\u5b98\u91d1\u8679\u9762\u524d\u7684\u7b77\u5b50\u7a81\u7136\u98de\u8d77\uff0c\u5df2\u6253\u5728\u897f\u95e8 \u7389\u5de6\u53f3\u53cc\u80a9\u7684\u201c\u80a9\u4e95\u201d\u7a74\u4e0a\u3002 \u7b2c\u516d\u5341\u4e03\u7ae0\u3000\u6b66\u5b66\u98a0\u5cf0>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u4e03\u7ae0\u3000\u6b66\u5b66\u98a0\u5cf0 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u7684\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u8c01\u4e5f\u6ca1\u6709\u770b\u5230\u8fc7\u4ed6\u51fa\u624b\u2014\u2014\u73b0\u5728\u8fd8\u662f\u6ca1\u6709 \u770b\u5230\u4ed6\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6839\u672c\u597d\u50cf\u6ca1\u6709\u52a8\uff0c\u53ea\u4e0d\u8fc7\u5728\u684c\u4e0a\u8f7b\u8f7b\u4e00\u6309\uff0c\u7b77\u5b50\u5df2\u6025\u7bad\u822c\u5c04\u51fa\uff0c\u897f\u95e8\u7389\u8eab\u5b50\u5df2\u8f6f \u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5e26\u4e0b\u53bb\uff0c\u770b\u4ed4\u7ec6\u3002\u201d \u3000\u3000\u9ec4\u886b\u5927\u6c49\u4e00\u4f38\u624b\uff0c\u5df2\u5c06\u897f\u95e8\u7389\u8eab\u5b50\u6284\u8d77\u3002 \u3000\u3000\u897f\u95e8\u7389\u5634\u5507\u5728\u52a8\uff0c\u5374\u5df2\u5413\u5f97\u8fde\u58f0\u97f3\u90fd\u53d1\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u90a3\u4e9b\u4e1c\u897f\u82e5\u771f\u7684\u8fd8\u5728\u4f60\u809a\u5b50\u91cc\uff0c\u6211\u966a\u4f60\u4e00\u6761\u547d\uff0c\u5426\u5219\uff0c\u4f60\u5c31\u767d\u6b7b\u3002 \u201d \u3000\u3000\u6ca1\u6709\u4eba\u6562\u8bf4\u8bdd\uff0c\u6ca1\u6709\u4eba\u6562\u52a8\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u597d\u50cf\u5750\u5728\u9488\u6be1\u4e0a\uff0c\u8863\u670d\u90fd\u5df2\u88ab\u51b7\u6c57\u6e7f\u900f\u3002 \u3000\u3000\u53ea\u542c\u4e00\u58f0\u60e8\u547c\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u9ec4\u886b\u5927\u6c49\u5782\u624b\u800c\u5165\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u5df2\u770b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6709\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ec4\u886b\u5927\u6c49\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u809a\u5b50\u662f\u7a7a\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u597d\u2014\u2014\u201d \u3000\u3000\u4ed6\u76ee\u5149\u7f13\u7f13\u81ea\u6bcf\u4e2a\u4eba\u9762\u4e0a\u626b\u8fc7\u9053\uff1a\u201c\u5728\u6211\u9762\u524d\u8bf4\u8c0e\u8bdd\uff0c\u5c31\u662f\u8fd9\u79cd\u4e0b\u573a\uff0c\u5404\u4f4d\u660e\u767d\u4e86\u4e48\uff1f \u201d \u3000\u3000\u5927\u5bb6\u62fc\u547d\u70b9\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5404\u4f4d\u73b0\u5728\u83ab\u975e\u4e5f\u4e0d\u997f\u4e86\uff1f\u201d \u3000\u3000\u5927\u5bb6\u62a2\u7740\u9053\uff1a\u201c\u997f\u2026\u2026\u997f\u2026\u2026\u201d \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u62a2\u7740\u631f\u4e86\u5757\u83dc\uff0c\u653e\u5728\u5634\u91cc\uff0c\u600e\u5948\u7259\u9f7f\u6253\u6218\uff0c\u54ea\u91cc\u80fd\u54ac\u5f97\u52a8\uff0c\u53ea\u6709\u82e6\u7740\u8138\uff0c\u6574\u5757 \u7684\u54bd\u4e0b\u53bb\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u4e2a\u4eba\u6e7f\u6dcb\u6dcb\u7684\u95ef\u4e86\u8fdb\u6765\uff0c\u7b52\u5728\u95e8\u53e3\uff0c\u6ee1\u5e03\u8840\u4e1d\u7684\u773c\u775b\u5446\u6ede\u800c\u8fdf\u949d\uff0c\u832b\u7136\u56db\u4e0b \u8f6c\u52a8\u7740\uff0c\u5583\u5583\u9053\uff1a\u201c\u7a7f\u7ea2\u8863\u670d\u7684\u4eba\u2026\u2026\u7a7f\u7ea2\u8863\u670d\u7684\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u963f\u98de\uff01 \u3000\u3000\u9f99\u5578\u4e91\u970d\u7136\u957f\u8eab\u800c\u8d77\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u8fd9\u624d\u8f6c\u5230\u4ed6\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u867d\u5df2\u5446\u6ede\uff0c\u795e\u60c5\u867d\u7136\u72fc\u72c8\uff0c\u53ef\u662f\u4ed6\u7684\u624b\u4e0a\u8fd8\u6709\u5251\uff01 \u3000\u3000\u53ea\u8981\u4ed6\u624b\u4e0a\u6709\u5251\uff0c\u5df2\u8db3\u4ee5\u4ee4\u9f99\u5578\u4e91\u5fc3\u5bd2\u80c6\u4e27\u3002 \u3000\u3000\u9f99\u5578\u4e91\u4e0d\u7531\u81ea\u4e3b\u7684\u5f80\u540e\u9000\u3002 \u3000\u3000\u963f\u98de\u5df2\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u5149\u5728\u95ea\u52a8\uff0c\u4ed6\u7684\u811a\u6b65\u4e5f\u548c\u5251\u5149\u540c\u6837\u4e0d\u7a33\u3002 \u3000\u3000\u4f46\u9f99\u5578\u4e91\u53ea\u770b\u5230\u4ed6\u7684\u5251\uff0c\u8f6c\u8eab\u5c31\u9003\u3002 \u3000\u3000\u963f\u98de\u8e09\u8dc4\u7740\u8ffd\u4e86\u8fc7\u53bb\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u5df2\u4f20\u6765\u4e00\u9635\u6251\u9f3b\u7684\u9152\u6c14\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u8138\u8272\u672c\u5df2\u53d8\u4e86\uff0c\u6b64\u523b\u773c\u775b\u7a81\u7136\u4e00\u4eae\uff0c\u6084\u6084\u7528\u811a\u4e00\u52fe\uff0c\u5c06\u9f99\u5578\u4e91\u672c\u6765\u5750\u7684\u6905\u5b50\u52fe\u4e86 \u51fa\u53bb\uff0c\u6321\u4f4f\u4e86\u963f\u98de\u7684\u8def\u3002 \u3000\u3000\u963f\u98de\u7adf\u6ca1\u6709\u77a7\u89c1\uff0c\u201c\u5657\u201d\u7684\uff0c\u4eba\u5df2\u88ab\u6905\u5b50\u7eca\u5012\uff0c\u5e73\u5e73\u7684\u8dcc\u4e86\u4e0b\u53bb\uff0c\u638c\u4e2d\u5251\u4e5f\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u4ed6\u7adf\u8fde\u5251\u90fd\u62ff\u4e0d\u7a33\u4e86\uff01 \u3000\u3000\u9f99\u5578\u4e91\u4e00\u60ca\u4e00\u559c\u8f6c\u8eab\u62fe\u5251\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u903c\u4f4f\u4e86\u963f\u98de\u7684\u540e\u8111\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u5251\u5e76\u6ca1\u6709\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u77a5\u89c1\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u8138\u8272\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8138\u8272\u9634\u6c89\u5f97\u53ef\u6015\uff0c\u77f3\u50cf\u822c\u5750\u5728\u90a3\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u4e0d\u52a8\uff0c\u5c31\u6ca1\u6709\u4eba\u6562\u52a8\u3002 \u3000\u3000\u9f99\u5578\u4e91\u966a\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u7adf\u6562\u5728\u5927\u54e5\u9762\u524d\u6492\u91ce\uff0c\u7f6a\u5df2\u5f53\u6740\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u5c4b\u5916\u6709\u6761\u72d7\uff0c\u4f60\u77a7\u89c1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u597d\u50cf\u662f\u6709\u4e00\u6761\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u82e5\u8981\u6740\u8fd9\u4eba\uff0c\u8fd8\u4e0d\u5982\u6740\u90a3\u6761\u72d7\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u53c8\u6014\u4e86\u6014\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u662f\uff0c\u8fd9\u4eba\u7684\u786e\u8fde\u72d7\u90fd\u4e0d\u5982\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u6211\uff1f\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ed6\u4e0d\u5982\u72d7\uff0c\u4f60\u5374\u8fde\u4ed6\u90fd\u4e0d\u5982\uff0c\u72d7\u89c1\u4e86\u4ed6\uff0c\u4e5f\u4e0d\u4f1a\u9003\u7684\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u8fd9\u6b21\u624d\u771f\u7684\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u626b\u4e86\u5ea7\u4e0a\u7684\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u80af\u548c\u72d7\u62dc\u4e3a\u5144\u5f1f\u4e48\u3002\u201d \u3000\u3000\u5927\u5bb6\u7acb\u523b\u5e94\u58f0\u9053\uff1a\u201c\u7edd\u4e0d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8fde\u4ed6\u4eec\u90fd\u4e0d\u80af\uff0c\u4f55\u51b5\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u773c\u775b\u5ffd\u53c8\u76ef\u7740\u9f99\u5578\u4e91\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u770b\u4f60\u548c\u90a3\u6761\u72d7\u771f\u662f\u96be\u5144\u96be\u5f1f\uff0c\u4e0d\u5982\u5c31\u548c\u5b83\u7ed3\u4e3a\u516b \u62dc\u4e4b\u4ea4\u5427\u3002\u201d\u4ed6\u8bf4\u51fa\u7684\u8bdd\u5c31\u662f\u547d\u4ee4\uff0c\u4f46\u8fd9\u79cd\u7f9e\u8fb1\u8c01\u80fd\u5fcd\u53d7\uff1f \u3000\u3000\u9f99\u5578\u4e91\u6ee1\u5934\u5927\u6c57\u6d0b\u6d0b\u800c\u843d\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5ffd\u7136\u8d70\u8fc7\u6765\uff0c\u62ff\u4e0b\u4e86\u4ed6\u638c\u4e2d\u7684\u5251\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e3b\u610f\u672c\u662f\u665a\u8f88\u51fa\u7684\uff0c\u5374\u4e0d\u60f3\u53cd\u800c \u81ea\u53d6\u5176\u8fb1\uff0c\u800c\u4e14\u7978\u53ca\u5bb6\u7236\uff0c\u665a\u8f88\u65e2\u65e0\u529b\u4e3a\u5bb6\u7236\u6d17\u6e05\u6b64\u8fb1\uff0c\u672c\u5f53\u8840\u6e85\u5f53\u5730\uff0c\u4ee5\u8c22\u5bb6\u7236\uff0c\u53ea\u60dc\u6148 \u6bcd\u5728\u5802\uff0c\u72b9\u672a\u5c3d\u5b5d\uff0c\u4e0d\u6562\u8f7b\u751f\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u4ed6\u5ffd\u7136\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u81ea\u5df1\u5728\u624b\u9f50\u8155\u5241\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u4e0d\u7981\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u5df2\u75bc\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u5374\u8fd8\u662f\u54ac\u7740\u7259\uff0c\u5c06\u65ad\u624b\u62fe\u4e86\u8d77\u6765\uff0c\u653e\u5230\u4e0a\u5b98\u91d1\u8679\u9762\u524d\uff0c\u54ac\u7740\u7259 \u9053\uff1a\u201c\u5e2e\u4e3b\u53ef\u6ee1\u610f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u795e\u8272\u4e0d\u53d8\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u60f3\u4ee5\u8fd9\u53ea\u624b\u8d4e\u56de\u4f60\u7236\u5b50\u7684\u4e24\u6761\u547d\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u560e\u58f0\u9053\uff1a\u201c\u665a\u8f88\u2026\u2026\u201d \u3000\u3000\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u7ec8\u4e8e\u652f\u6301\u4e0d\u4f4f\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5f53\u7136\u4e5f\u662f\u795e\u8272\u60e8\u7136\uff0c\u5374\u8fde\u4e00\u70b9\u8868\u793a\u90fd\u6ca1\u6709\uff0c\u8fd8\u662f\u5446\u5446\u7684\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u770b\u5728\u4f60\u513f\u5b50\u7684\u4efd\u4e0a\uff0c\u4f60\u8d70\u5427\uff0c\u4ee5\u540e\u6700\u597d\u83ab\u8981\u8ba9\u6211\u518d\u89c1\u5230\u4f60\uff01\u201d \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u6839\u672c\u5df2\u5fd8\u4e86\u65b9\u624d\u53d1\u751f\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u4e5f\u6ca1\u6709\u77a7\u89c1\u522b\u7684\u4eba\uff0c\u76ee\u5149\u832b\u7136\u8f6c\u52a8\u7740\uff0c\u5ffd\u7136\u53d1\u73b0 \u684c\u4e0a\u7684\u9152\u58f6\uff0c\u7acb\u523b\u6251\u4e86\u8fc7\u53bb\uff0c\u4e00\u628a\u6293\u5728\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u6293\u5f97\u90a3\u4e48\u7d27\uff0c\u597d\u50cf\u8fd9\u9152\u58f6\u5c31\u662f\u4ed6\u7684\u751f\u547d\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u9152\u58f6\u5374\u7a81\u7136\u88ab\u51fb\u788e\u3002 \u3000\u3000\u9152\u6d41\u4e0b\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u8fd8\u662f\u6293\u7740\u9152\u58f6\u7684\u788e\u7247\uff0c\u4f46\u624b\u5df2\u5728\u53d1\u6296\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u9152\u662f\u7ed9\u4eba\u559d\u7684\uff0c\u4f60\u4e0d\u914d\uff01\u201d \u3000\u3000\u4ed6\u968f\u624b\u6478\u51fa\u5757\u94f6\u5b50\uff0c\u8fdc\u8fdc\u629b\u5728\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u8981\u559d\u9152\uff0c\u81ea\u5df1\u4e70\u53bb\u3002\u201d \u3000\u3000\u963f\u98de\u62ac\u8d77\u5934\uff0c\u832b\u7136\u671b\u7740\u4ed6\uff0c\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u6162\u6162\u7684\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u94f6\u5b50\u5c31\u5728\u4ed6\u811a\u4e0b\u3002 \u3000\u3000\u4ed6\u5446\u5446\u7684\u77a7\u7740\u8fd9\u5757\u94f6\u5b50\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u7ec8\u4e8e\u6162\u6f2b\u7684\u5f2f\u4e0b\u8170\u2026\u2026 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u53c8\u95ea\u8fc7\u4e00\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u2014\u2014\u4ed6\u7b11\u7684\u65f6\u5019\uff0c\u6bd4\u4e0d\u7b11\u66f4\u6b8b\u9177\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5bd2\u5149\u4e00\u95ea\u3002 \u3000\u3000\u4e00\u67c4\u5200\u95ea\u7535\u822c\u98de\u6765\uff0c\u5c06\u8fd9\u5757\u94f6\u5b50\u9489\u5728\u5730\u4e0a\u3002 \u3000\u3000\u963f\u98de\u7684\u8138\u4e00\u9635\u626d\u66f2\uff0c\u62ac\u8d77\u5934\uff0c\u6574\u4e2a\u4eba\u7a81\u7136\u50f5\u786c\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7ad9\u5728\u95e8\u53e3\uff0c\u77a7\u7740\u4ed6\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u9152\u6bd4\u5916\u9762\u7684\u597d\u3002\u4f60\u82e5\u8981\u559d\uff0c\u6211\u53bb\u66ff\u4f60\u5012\u4e00 \u676f\u3002\u201d \u3000\u3000\u684c\u4e0a\u8fd8\u6709\u4e00\u58f6\u9152\u3002 \u3000\u3000\u8fd9\u4eba\u7adf\u771f\u7684\u8d70\u8fc7\u53bb\uff0c\u5012\u4e86\u4e00\u676f\uff0c\u9001\u5230\u963f\u98de\u9762\u524d\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u8bf4\u8bdd\uff0c\u751a\u81f3\u8fde\u547c\u5438\u58f0\u90fd\u5df2\u505c\u987f\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7adf\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u9759\u9759\u7684\u77a7\u7740\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u4e0d\u592a\u9ad8\uff0c\u4f46\u4e5f\u4e0d\u77ee\uff0c\u7a7f\u7684\u8863\u670d\u5f88\u7834\u65e7\uff0c\u4e24\u9b13\u5df2\u6709\u4e86\u534e\u53d1\uff0c\u770b\u6765\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5f88\u843d\u62d3\u3001 \u5f88\u6f66\u5012\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u4f46\u4e0a\u5b98\u91d1\u8679\u773c\u770b\u7740\u4ed6\u5012\u9152\uff0c\u773c\u770b\u7740\u4ed6\u5c06\u8fd9\u676f\u9152\u9001\u7ed9\u963f\u98de\uff0c\u975e\u4f46\u6ca1\u6709\u963b\u6b62\uff0c\u8fde\u4e00\u70b9\u8868\u60c5\u90fd \u6ca1\u6709\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8bf4\u51fa\u7684\u8bdd\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u8fdd\u6297\uff01 \u3000\u3000\u4f46\u8fd9\u6b21\uff0c\u4ed6\u7684\u547d\u4ee4\u5728\u8fd9\u4eba\u8eab\u4e0a\uff0c\u7adf\u50cf\u662f\u5ffd\u7136\u53d8\u4e3a\u65e0\u6548\u4e86\u3002 \u3000\u3000\u9152\u676f\u5df2\u9001\u5230\u963f\u98de\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u75f4\u75f4\u7684\u671b\u7740\u8fd9\u676f\u9152\uff0c\u4e24\u6ef4\u6676\u83b9\u6eda\u5706\u7684\u773c\u6cea\uff0c\u6162\u6162\u7684\u4ece\u773c\u775b\u91cc\u6d41\u4e86\u51fa\u6765\uff0c\u6ef4\u5728\u9152\u676f\u91cc\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u53ea\u80af\u6d41\u8840\uff0c\u4ed6\u7684\u6cea\u4e00\u5411\u6bd4\u8840\u66f4\u73cd\u8d35\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u773c\u7736\u4e5f\u5df2\u6709\u4e9b\u6e7f\u4e86\uff0c\u70ed\u6cea\u5df2\u76c8\u7736\uff0c\u4f46\u5634\u89d2\u5374\u8fd8\u662f\u5e26\u7740\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u8fd9\u5fae\u7b11\u7adf\u4eff\u6cb8\u4f7f\u8fd9\u5e73\u51e1\u800c\u6f66\u5012\u7684\u4eba\u5ffd\u7136\u53d8\u5f97\u8f89\u714c\u660e\u4eae\u4e86\u8d77\u6765\u3002\u65e0\u8bba\u8c01\u4e5f\u60f3\u8c61\u4e0d\u5230\u4e00\u4e2a\u4eba \u5fae\u7b11\u7684\u529b\u91cf\u7adf\u6709\u5982\u6b64\u4f1f\u5927\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u7684\u5fae\u7b11\u548c\u70ed\u6cea\u6240\u8868\u793a\u51fa\u7684\u610f\u601d\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u8bf4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u5728\u6296\uff0c\u4e0d\u505c\u7684\u5728\u6296\uff0c\u5ffd\u7136\u731b\u543c\u4e00\u58f0\uff0c\u5c06\u9152\u676f\u91cd\u91cd\u7684\u6454\u5728\u5730\u4e0a\uff0c\u8f6c\u8eab\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6b63\u60f3\u8ffd\u4e0a\u53bb\u3002 \u3000\u3000\u7a81\u7136\u4e0a\u5b98\u91d1\u8679\u559d\u9053\uff1a\u201c\u7b49\u4e00\u7b49\uff01\u201d \u3000\u3000\u4ed6\u8fdf\u7591\u7740\uff0c\u811a\u6b65\u7ec8\u4e8e\u505c\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7f13\u7f13\u9053\uff1a\u201c\u65e2\u7136\u8981\u8d70\uff0c\u5c31\u4e0d\u8be5\u6765\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u53c8\u4f55\u5fc5\u8d70\uff1f\u201d \u3000\u3000\u843d\u62d3\u7684\u4e2d\u5e74\u4eba\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u53c8\u4f55\u5fc5\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u59cb\u7ec8\u6ca1\u6709\u77a7\u8fc7\u4e0a\u5b98\u91d1\u8679\uff0c\u73b0\u5728\u624d\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\uff0c\u7ec8\u4e8e\u89e6\u53ca\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u76ee\u5149\u3002 \u3000\u3000\u706b\u82b1\uff01 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u9047\uff0c\u7adf\u4f3c\u6fc0\u8d77\u4e86\u4e00\u4e32\u706b\u82b1\u3002 \u3000\u3000\u4e00\u4e32\u65e0\u58f0\u65e0\u5f62\u7684\u706b\u82b1\uff0c\u867d\u7136\u6ca1\u6709\u4eba\u7684\u773c\u775b\u80fd\u77a7\u5f97\u89c1\uff0c\u4f46\u6bcf\u4e2a\u4eba\u7684\u5fc3\u91cc\u5374\u90fd\u80fd\u611f\u89c9\u5f97\u5230\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u5fc3\u90fd\u7a81\u7136\u9707\u52a8\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u773c\u775b\u5c31\u4eff\u4f5b\u85cf\u7740\u53cc\u5996\u9b54\u7684\u624b\uff0c\u80fd\u6293\u6ce8\u4efb\u4f55\u4eba\u7684\u9b42\u9b44\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u773c\u775b\u5374\u5982\u540c\u6d69\u701a\u65e0\u8fb9\u7684\u6d77\u6d0b\uff0c\u78a7\u7a7a\u5982\u6d17\u7684\u7a79\u82cd\uff0c\u8db3\u4ee5\u5c06\u4e16\u4e0a\u6240\u6709\u7684\u5996\u9b54\u9b3c\u602a\u90fd\u5b8c \u5168\u5bb9\u7eb3\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u773c\u775b\u82e5\u662f\u5200\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u773c\u775b\u5c31\u662f\u5200\u7684\u9798\uff01 \u3000\u3000\u770b\u5230\u4e86\u8fd9\u53cc\u773c\u775b\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u518d\u8ba4\u4e3a\u4ed6\u662f\u5e73\u51e1\u7684\u4e86\u3002 \u3000\u3000\u6709\u7684\u4eba\u5df2\u9690\u9690\u731c\u51fa\u4ed6\u662f\u51c6\u3002 \u3000\u3000\u53ea\u542c\u4e0a\u5b98\u91d1\u8679\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u7684\u5200\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7684\u624b\u4e00\u53cd\uff0c\u5200\u5df2\u5728\u6307\u5c16\uff01 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u770b\u5230\u4e86\u8fd9\u67c4\u5200\uff0c\u5927\u5bb6\u624d\u77e5\u9053\u81ea\u5df1\u6ca1\u6709\u731c\u9519\uff01 \u3000\u3000\u662f\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6bd5\u7adf\u6765\u4e86\uff01 \u3000\u3000\u624b\uff0c\u51fa\u5947\u7684\u7a33\u5b9a\uff0c\u5c31\u50cf\u662f\u5df2\u5b8c\u5168\u51dd\u7ed3\u5728\u7a7a\u6c14\u4e2d\u3002 \u3000\u3000\u624b\u6307\u7ea4\u957f\uff0c\u6709\u529b\uff0c\u6307\u7532\u4fee\u526a\u5f97\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u770b\u6765\uff0c\u62ff\u7b14\u8fd8\u6bd4\u62ff\u5200\u5408\u9002\uff0c\u4f46\u5374\u662f\u6b66\u6797\u4e2d\u6700\u6709\u4ef7\u503c\uff0c\u6700\u53ef\u6015\u7684\u4e00\u53ea\u624b\u3002\u5200\uff0c\u672c\u662f \u5f88\u5e73\u51e1\u7684\u4e00\u628a\u5200\u3002 \u3000\u3000\u4f46\u5728\u8fd9\u53ea\u624b\u91cc\uff0c\u8fd9\u628a\u5e73\u51e1\u7684\u5200\uff0c\u4e5f\u53d8\u5f97\u6709\u4e86\u79cd\u903c\u4eba\u7684\u950b\u8292\uff0c\u6740\u6c14\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6f2b\u6f2b\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u7684\u8d70\u5230\u674e\u5bfb\u6b22\u5bf9\u9762\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u8ddd\u79bb\u674e\u5bfb\u6b22\u5df2\u4e0d\u53ca\u4e24\u4e08\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u624b\u8fd8\u5728\u8896\u4e2d\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u201c\u9f99\u51e4\u53cc\u73af\u201d\u4e8c\u5341\u5e74\u524d\u5c31\u5df2\u9707\u60ca\u5929\u4e0b\uff0c\u201c\u5175\u5668\u8c31\u201d\u4e2d\u6392\u540d\u7b2c\u4e8c\uff0c\u540d\u6b21\u8fd8\u5728\u201c \u5c0f\u674e\u98de\u5200\u201d\u4e4b\u4e0a\uff01 \u3000\u3000\u8fd1\u4e8c\u5341\u5e74\u6765\uff0c\u5df2\u6ca1\u6709\u4eba\u89c1\u8fc7\u4ed6\u7684\u53cc\u73af\u51fa\u624b\u3002 \u3000\u3000\u867d\u7136\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\u8fd9\u53cc\u73af\u7684\u53ef\u6015\uff0c\u5374\u6ca1\u6709\u4eba\u77e5\u9053\u5b83\u7a76\u7adf\u5982\u4f55\u53ef\u6015\uff1f \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u7684\u73af\u662f\u5426\u5df2\u5728\u624b\u4e2d\uff1f \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u4ece\u674e\u5bfb\u6b22\u7684\u5200\u4e0a\uff0c\u8f6c\u5411\u4e0a\u5b98\u91d1\u8679\u7684\u624b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u624b\u7ec8\u4e8e\u81ea\u8896\u4e2d\u4f38\u51fa\u3002 \u3000\u3000\u624b\u662f\u7a7a\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u7684\u73af\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u73af\u5df2\u5728\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5728\u5fc3\u91cc\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5fc3\u91cc\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u624b\u4e2d\u867d\u65e0\u73af\uff0c\u5fc3\u4e2d\u5374\u6709\u73af\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u77b3\u5b54\u7a81\u7136\u6536\u7f29\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u73af\uff0c\u7adf\u662f\u770b\u4e0d\u89c1\u7684\uff01 \u3000\u3000\u6b63\u56e0\u4e3a\u770b\u4e0d\u89c1\uff0c\u6240\u4ee5\u5c31\u65e0\u6240\u4e0d\u5728\uff0c\u65e0\u5904\u4e0d\u81f3\u3002\u5b83\u53ef\u80fd\u5df2\u5230\u4e86\u4f60\u773c\u524d\uff0c\u5df2\u5230\u4e86\u4f60\u54bd\u5589\uff0c\u5df2 \u5230\u4e86\u4f60\u7075\u9b42\u4e2d\u3002 \u3000\u3000\u76f4\u5230\u4f60\u6574\u4e2a\u4eba\u90fd\u5df2\u88ab\u5b83\u6467\u6bc1\uff0c\u8fd8\u662f\u770b\u4e0d\u89c1\u5b83\u7684\u5b58\u5728\uff01 \u3000\u3000\u201c\u624b\u4e2d\u65e0\u73af\uff0c\u5fc3\u4e2d\u6709\u73af\uff01\u201d \u3000\u3000\u8fd9\u6b63\u662f\u6b66\u5b66\u7684\u5dc5\u5cf0\uff01 \u3000\u3000\u8fd9\u5df2\u662f\u201c\u4ed9\u4f5b\u201d\u7684\u5883\u754c\uff01 \u3000\u3000\u522b\u4eba\u4e0d\u61c2\uff0c\u674e\u5bfb\u6b22\u5374\u61c2\u5f97\u7684\u3002 \u3000\u3000\u522b\u4eba\u751a\u81f3\u6709\u4e9b\u5931\u671b\u3002 \u3000\u3000\u2014\u2014\u5927\u591a\u6570\u4eba\uff0c\u90fd\u8981\u770b\u5230\u90a3\u6837\u4e1c\u897f\uff0c\u624d\u80af\u627f\u8ba4\u5b83\u7684\u4ef7\u503c\uff0c\u5374\u4e0d\u77e5\u770b\u4e0d\u89c1\u7684\u4e1c\u897f\uff0c\u4ef7\u503c\u8fd8 \u6bd4\u80fd\u770b\u5f97\u89c1\u7684\u9ad8\u51fa\u751a\u591a\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u7684\u5149\u8f89\uff0c\u4f3c\u5df2\u5c06\u674e\u5bfb\u6b22\u538b\u5012\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e03\u5e74\u524d\uff0c\u6211\u624b\u4e2d\u5df2\u65e0\u73af\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f69\u670d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u61c2\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5999\u6e17\u9020\u5316\uff0c\u65e0\u73af\u65e0\u6211\u3002\u65e0\u8ff9\u53ef\u5bfb\uff0c\u65e0\u575a\u4e0d\u6467\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u597d\uff0c\u4f60\u679c\u7136\u61c2\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u61c2\u65e2\u662f\u4e0d\u61c2\uff0c\u4e0d\u61c2\u65e2\u662f\u61c2\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u8bf4\u8bdd\u7adf\u4f3c\u7985\u5b97\u9ad8\u50e7\u5728\u6253\u673a\u950b\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4eec\u4e24\u4eba\u5916\uff0c\u8c01\u4e5f\u4e0d\u61c2\u3002 \u3000\u3000\u4e0d\u61c2\uff0c\u6240\u4ee5\u6050\u60e7\u2026\u2026 \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u4e0d\u7531\u81ea\u4e3b\u6084\u6084\u7ad9\u8d77\uff0c\u6084\u6084\u5f80\u540e\u9000\u5165\u4e86\u5c4b\u89d2\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u7a81\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u679c\u7136\u662f\u674e\u5bfb\u6b22\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u53ea\u4f55\u5c1d\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u672c\u662f\u4e09\u4ee3\u63a2\u82b1\uff0c\u98ce\u6d41\u7ff0\u6797\uff0c\u540d\u7b2c\u9ad8\u534e\uff0c\u5929\u4e4b\u9a84\u5b50\uff0c\u53c8\u4f55\u82e6\u504f\u504f\u8981\u5230\u8fd9 \u80ae\u810f\u6c5f\u6e56\u4e2d\u6765\u505a\u6d6a\u5b50\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u60f3\u6765\u5c31\u6765\uff0c\u60f3\u8d70\u5c31\u8d70\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u8fd8\u80fd\u8d70\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u4e5f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u60f3\u8d70\uff0c\u4e5f\u662f\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u51fa\u62db\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u62db\u5df2\u5728\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e0d\u7531\u81ea\u4e3b\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5728\u5fc3\u91cc\uff0c\u6211\u5200\u4e0a\u867d\u65e0\u62db\uff0c\u5fc3\u4e2d\u5374\u6709\u62db\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u77b3\u5b54\u4e5f\u7a81\u7136\u6536\u7f29\uff01 \u3000\u3000\u8c01\u4e5f\u770b\u4e0d\u89c1\u4e0a\u5b98\u91d1\u8679\u7684\u73af\u5728\u54ea\u91cc\uff0c\u4e5f\u770b\u4e0d\u89c1\u674e\u5bfb\u6b22\u7684\u62db\u5728\u54ea\u91cc\u3002 \u3000\u3000\u4f46\u73af\u5df2\u5728\uff0c\u62db\u5df2\u51fa\uff01 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u4f3c\u5df1\u611f\u89c9\u5230\u5b83\u7684\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u8fd8\u662f\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u4f46\u5374\u4f3c\u5df2\u8fdb\u5165\u751f\u6b7b\u4e00\u53d1\u7684\u60c5\u51b5\u4e2d\uff0c\u751f\u6b7b\u5df2\u53ea\u662f\u547c\u5438\u95f4\u4e8b \uff01 \u3000\u3000\u5927\u5bb6\u867d\u90fd\u5df2\u9000\u5165\u89d2\u843d\u4e2d\uff0c\u5374\u8fd8\u662f\u80fd\u611f\u5230\u90a3\u79cd\u53ef\u6015\u7684\u6740\u6c14\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u5fc3\u90fd\u5728\u6536\u7f29\uff01 \u3000\u3000\u963f\u98de\u5168\u8eab\u7684\u8840\u90fd\u5df2\u6cb8\u817e\uff01 \u3000\u3000\u4ed6\u72c2\u5954\u7740\uff0c\u65e2\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u77e5\u8981\u505a\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u5728\u9003\u907f\u3002 \u3000\u3000\u4f46\u9003\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u9003\u5230\u51e0\u65f6\uff1f \u3000\u3000\u4ed6\u6c38\u8fdc\u4e5f\u9003\u4e0d\u4e86\u7684\uff01\u56e0\u4e3a\u4ed6\u6240\u9003\u907f\u7684\uff0c\u6b63\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\u4ecd\u7136\u5728\u5bf9\u5cd9\u7740\uff0c\u6ca1\u6709\u58f0\u97f3\uff0c\u4e5f\u6ca1\u6709\u52a8\u4f5c\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u53ea\u80fd\u542c\u5230\u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u90fd\u53ea\u80fd\u611f\u5230\u51b7\u6c57\u6b63\u4e00\u7c92\u7c92\u81ea\u6bdb\u5b54\u4e2d\u6c81\u51fa\uff0c\u5728\u76ae\u80a4\u4e0a \u6d41\u8fc7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u53ea\u8981\u4e00\u6709\u52a8\u4f5c\uff0c\u5c31\u5fc5\u5b9a\u662f\u60ca\u5929\u52a8\u5730\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u51b3\u6218\u968f\u65f6\u90fd\u53ef\u80fd\u7206\u53d1\uff0c\u6bcf\u4e00\u5239\u90a3\u90fd\u53ef\u80fd\u7206\u53d1\u3002 \u3000\u3000\u6216\u8005\u4e5f\u5c31\u5728\u90a3\u540c\u4e00\u5239\u90a3\u95f4\u7ec8\u6b62\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u8fd9\u4e24\u4eba\u4e2d\u52bf\u5fc5\u8981\u6709\u4e00\u4e2a\u4eba\u5012\u4e0b\u53bb\uff01 \u3000\u3000\u5012\u4e0b\u53bb\u7684\u662f\u8c01\u5462\uff1f \u3000\u3000\u201c\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff01\u201d \u3000\u3000\u4e8c\u5341\u5e74\u6765\uff0c\u8fd8\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u907f\u8fc7\u5c0f\u674e\u63a2\u82b1\u7684\u8fd9\u4e00\u5200\uff01 \u3000\u3000\u4f46\u4e0a\u5b98\u91d1\u8679\u7684\u53cc\u73af\u6392\u540d\u66f4\u9ad8\uff0c\u662f\u4e0d\u662f\u66f4\u53ef\u6015\uff1f \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u5f88\u9547\u5b9a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4eff\u4f5b\u90fd\u5145\u6ee1\u4e86\u81ea\u4fe1\u3002 \u3000\u3000\u4e16\u4e0a\u53c8\u6709\u8c01\u80fd\u9884\u6599\u8fd9\u4e00\u6218\u7684\u7ed3\u679c\uff1f \u3000\u3000\u963f\u98de\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u5730\u4e0a\u5598\u606f\u7740\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u4ed6\u624d\u62ac\u8d77\u5934\uff0c\u832b\u7136\u56da\u987e\uff0c\u4f3c\u4e4e\u6839\u672c\u4e0d\u77e5 \u9053\u81ea\u5df1\u5df2\u5230\u4e86\u54ea\u91cc\uff1f \u3000\u3000\u8fd9\u91cc\u662f\u4e2a\u5c0f\u5c0f\u7684\u9662\u843d\u3002 \u3000\u3000\u9662\u5b50\u91cc\u4e00\u682a\u5b64\u96f6\u96f6\u7684\u81ea\u6768\u6b63\u5728\u79cb\u98ce\u4e2d\u98a4\u6296\u3002 \u3000\u3000\u5706\u5eca\u4e0a\u6731\u5e18\u534a\u5377\uff0c\u5c0f\u95e8\u865a\u63a9\uff0c\u78a7\u7eb1\u7a97\u5185\u6084\u65e0\u4eba\u58f0\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4ed6\u6628\u591c\u53d1\u72c2\u6c89\u9189\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u600e\u4f1a\u53c8\u5230\u4e86\u8fd9\u91cc\u3002 \u3000\u3000\u865a\u63a9\u7684\u95e8\u5f00\u4e86\uff0c\u4e00\u4e2a\u4eba\u63a2\u51fa\u4e86\u534a\u8fb9\u5a07\u7f8e\u7684\u8138\uff0c\u660e\u5a9a\u7684\u79cb\u6ce2\u5728\u4ed6\u8eab\u4e0a\u4e00\u8f6c\uff0c\u8138\u53c8\u7f29\u4e86\u56de\u53bb \u3002 \u3000\u3000\u8fd9\u6b63\u662f\u6628\u591c\u66fe\u7ecf\u966a\u4ed6\u53d1\u72c2\u6c89\u9189\u8fc7\u7684\u4eba\u3002 \u7b2c\u516d\u5341\u516b\u7ae0\u3000\u795e\u9b54\u4e4b\u95f4>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u516b\u7ae0\u3000\u795e\u9b54\u4e4b\u95f4 \u3000\u3000\u963f\u98de\u7a81\u7136\u8df3\u8d77\u6765\uff0c\u7ad9\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\u95e8\u7adf\u5173\u4e86\uff0c\u800c\u4e14\u4e0a\u4e86\u6813\u3002 \u3000\u3000\u963f\u98de\u7528\u529b\u6572\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u95e8\u91cc\u624d\u6709\u58f0\u97f3\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u963f\u98de\u6728\u7136\u7684\u9053\uff1a\u201c\u6211\u3002\u201d \u3000\u3000\u95e8\u91cc\u7684\u58f0\u97f3\u95ee\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u95e8\u91cc\u7a81\u7136\u4f20\u51fa\u4e00\u9635\u94f6\u94c3\u822c\u7684\u7b11\uff1a\u201c\u8fd9\u4eba\u539f\u6765\u662f\u75af\u5b50\u3002\u201d \u3000\u3000\u201c\u542c\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u5c31\u597d\u50cf\u662f\u8fd9\u91cc\u7684\u4e3b\u4f3c\u7684\u3002\u201d \u3000\u3000\u201c\u8c01\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u201c\u8c01\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u4ed6\u81ea\u5df1\u5728\u6d3b\u89c1\u9b3c\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u58f0\u97f3\u5f88\u719f\u6089\uff0c\u6628\u591c\u4e5f\u4e0d\u77e5\u5bf9\u4ed6\u8bf4\u4e86\u591a\u5c11\u751c\u8a00\u871c\u8bed\uff0c\u8bc9\u4e86\u591a\u5c11\u67d4\u60c5\u871c\u610f\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48 \u5168\u90fd\u53d8\u4e86\uff1f \u3000\u3000\u963f\u98de\u9aa4\u7136\u89c9\u5f97\u4e00\u9635\u706b\u6c14\u51b2\u4e86\u4e0a\u6765\uff0c\u5fcd\u4e0d\u4f4f\u7528\u529b\u649e\u5f00\u4e86\u95e8\u3002 \u3000\u3000\u4e03\u53cc\u7f8e\u4e3d\u7684\u773c\u775b\u5168\u90e8\u5728\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u6628\u591c\u8fd9\u4e03\u53cc\u773c\u775b\u4e2d\u7684\u67d4\u60c5\u5982\u6c34\uff0c\u871c\u610f\u5982\u6cb9\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u4e03\u53cc\u773c\u775b\u4e2d\u7684\u6cb9\u5df2\u70e7\u6210\u70df\uff0c\u6c34\u5df2\u7ed3\u6210\u51b0\u3002 \u3000\u3000\u963f\u98de\u8e09\u8dc4\u51b2\u4e86\u8fdb\u53bb\uff0c\u6293\u8d77\u9152\u58f6\uff0c\u662f\u7a7a\u7684\u3002 \u3000\u3000\u201c\u9152\u5462\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u9152\uff01\u201d \u3000\u3000\u201c\u53bb\u62ff\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u53bb\u62ff\uff1f\u8fd9\u91cc\u53c8\u4e0d\u662f\u5356\u9152\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u6251\u8fc7\u53bb\uff0c\u6293\u4f4f\u4e86\u5979\u7684\u8863\u895f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u96be\u9053\u5168\u90e8\u4e0d\u8ba4\u5f97\u6211\u4e86\uff1f\u201d \u3000\u3000\u7f8e\u4e3d\u7684\u773c\u775b\u51b7\u51b7\u7684\u77a7\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6211\uff1f\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u6307\u4e00\u6839\u6839\u677e\u5f00\uff0c\u832b\u7136\u56db\u987e\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u91cc\u96be\u9053\u4e0d\u662f\u6628\u591c\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u8fd8\u662f\u6628\u591c\u7684\u5730\u65b9\uff0c\u53ea\u4e0d\u8fc7\u4f60\u5df2\u4e0d\u662f\u6628\u591c\u7684\u4f60\u4e86\uff01\u201d \u3000\u3000\u751c\u871c\u7684\u8bed\u58f0\uff0c\u66f4\u719f\u6089\u3002 \u3000\u3000\u963f\u98de\u6574\u4e2a\u4eba\u7a81\u7136\u5267\u70c8\u7684\u98a4\u6296\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u7d27\u7d27\u95ed\u4e86\u8d77\u6765\uff0c\u4e0d\u613f\u53bb\u770b\u5979\uff0c\u4e0d\u6562\u53bb\u770b\u5979\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u672c\u662f\u4ed6\u5728\u68a6\u9b42\u4e2d\u90fd\u5fd8\u4e0d\u4e86\u7684\uff0c\u4ed6\u672c\u6765\u5b81\u53ef\u4e0d\u60dc\u727a\u7272\u4e00\u5207\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u8981\u770b\u770b\u5979 \u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5374\u5b81\u6b7b\u4e5f\u4e0d\u613f\u770b\u5979\u4e00\u773c\u3002 \u3000\u3000\u5979\u8fd8\u662f\u4ee5\u524d\u7684\u5979\u3002 \u3000\u3000\u53ef\u662f\u4ed6\uff0c\u4ed6\u7684\u786e\u5df2\u4e0d\u662f\u4ee5\u524d\u7684\u4ed6\u4e86\uff01 \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u58f0\u97f3\uff0c\u6ca1\u6709\u52a8\u4f5c\u3002 \u3000\u3000\u5c4b\u6881\u4e0a\u7684\u7070\u5c18\uff0c\u7a81\u7136\u4e00\u7247\u7247\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u662f\u88ab\u98ce\u5439\u843d\u7684\uff1f\u8fd8\u662f\u88ab\u4ed6\u4eec\u7684\u6740\u6c14\u6467\u843d\u7684\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u5411\u524d\u8de8\u51fa\u4e86\u4e00\u6b65\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u52a8\uff01 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u52a8\u5373\u662f\u4e0d\u52a8\uff0c\u4e0d\u52a8\u5373\u662f\u52a8\uff0c\u4f60\u660e\u767d\u4e48\uff1f\u201d \u3000\u3000\u58f0\u97f3\u5f88\u82cd\u8001\uff0c\u6bcf\u4e2a\u4eba\u90fd\u542c\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u5374\u770b\u4e0d\u5230\u4ed6\u7684\u4eba\u5728\u54ea\u91cc\uff1f \u3000\u3000\u53e6\u4e00\u4eba\u5e26\u7740\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6253\u5c31\u662f\u4e0d\u6253\uff0c\u4e0d\u6253\u5c31\u662f\u6253\uff0c\u90a3\u4e48\u53c8\u4f55\u5fc5\u6253\u5462\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u6e05\u8106\u800c\u7f8e\uff0c\u5982\u9ec4\u8338\u51fa\u8c37\u3002 \u3000\u3000\u4f46\u5979\u7684\u4eba\uff0c\u8fd8\u662f\u8c01\u90fd\u6ca1\u6709\u77a7\u89c1\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u6253\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u61c2\u6b66\u529f\u4e4b\u771f\u8c1b\u3002\u201d \u3000\u3000\u5c11\u5973\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u4eec\u4e0d\u61c2\uff0c\u4ed6\u4eec\u81ea\u5df1\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u61c2\u5f97\u5f88\u54e9\u3002\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u51fa\uff0c\u9664\u4e86\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5df2\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u5c45\u7136\u6709\u4eba\u6562\u8bf4\u4ed6\u4eec\u4e0d\u61c2\u6b66\u529f\u3002 \u3000\u3000\u82e5\u8fde\u4ed6\u4eec\u90fd\u4e0d\u61c2\uff0c\u4e16\u4e0a\u8fd8\u6709\u8c01\u61c2\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u81ea\u4ee5\u4e3a\u2018\u624b\u4e2d\u65e0\u73af\uff0c\u5fc3\u4e2d\u6709\u73af\u2019\uff0c\u5c31\u5df2\u5230\u4e86\u6b66\u5b66\u7684\u5dc5\u5cf0\uff0c\u5176\u5b9e\u8fd8\u5dee\u5f97\u8fdc \u54e9\uff01\u201d \u3000\u3000\u5c11\u5973\u5403\u5403\u7b11\u9053\uff1a\u201c\u5dee\u591a\u8fdc\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u81f3\u5c11\u8fd8\u5dee\u5341\u4e07\u516b\u5343\u91cc\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u8981\u600e\u4e48\u6837\u624d\u771f\u6b63\u662f\u6b66\u5b66\u7684\u5dc5\u5cf0\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8981\u624b\u4e2d\u65e0\u73af\uff0c\u5fc3\u4e2d\u4e5f\u65e0\u73af\uff0c\u5230\u4e86\u73af\u5373\u662f\u6211\uff0c\u6211\u5373\u662f\u73af\u65f6\uff0c\u5df2\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u5dee\u4e0d\u591a\uff1f\u662f\u4e0d\u662f\u8fd8\u5dee\u4e00\u70b9\uff1f \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8fd8\u5dee\u4e00\u70b9\u3002\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u771f\u6b63\u7684\u6b66\u5b66\u5dc5\u5cf0\uff0c\u662f\u8981\u80fd\u5999\u6e17\u9020\u5316\uff0c\u5230\u65e0\u73af\u65e0\u6211\uff0c\u73af\u6211\u4e24\u5fd8\uff0c\u90a3\u624d\u771f \u7684\u662f\u65e0\u6240\u4e0d\u81f3\uff0c\u65e0\u575a\u4e0d\u6467\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\u9762\u4e0a\u4e5f\u4e0d\u7981\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u542c\u4e86\u4f60\u8001\u4eba\u5bb6\u7684\u8bdd\uff0c\u6211\u5012\u5ffd\u7136\u60f3\u8d77\u4e00\u4e2a\u6545\u4e8b\u6765\u4e86\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u7985\u5b97\u4f20\u9053\u65f6\uff0c\u4e94\u7956\u53e3\u5ff5\u4f5b\u63ed\uff1a\u2018\u8eab\u5982\u83e9\u63d0\u6811\uff0c\u5fc3\u5982\u660e\u955c\u53f0\uff0c\u65f6\u65f6\u52e4\u62c2\u62ed\uff0c\u4e0d \u4f7f\u7559\u5c18\u57c3\u2019\u3002\u8fd9\u5df2\u7ecf\u662f\u5f88\u9ad8\u6df1\u7684\u4f5b\u7406\u4e86\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u9053\u7406\u6b63\u5982\u2018\u73af\u5373\u662f\u6211\uff0c\u6211\u5373\u662f\u73af\u2019\uff0c\u8981\u7ec3\u5230\u8fd9\u4e00\u6b65\uff0c\u5df2\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f46\u516d\u7956\u60e0\u80fd\u8bf4\u7684\u66f4\u5999\uff1a\u2018\u83e9\u63d0\u672c\u975e\u6811\uff0c\u660e\u955c\u4ea6\u975e\u53f0\uff0c\u672c\u6765\u65e0\u4e00\u7269\uff0c\u4f55\u5904\u843d\u5c18 \u57c3\u3002\u2019\u6240\u4ee5\u4ed6\u624d\u627f\u7ee7\u4e86\u7985\u5b97\u7684\u9053\u7edf\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u624d\u771f\u6b63\u662f\u7985\u5b97\u7684\u5999\u8c1b\uff0c\u5230\u4e86\u8fd9\u4e00\u6b65\uff0c\u624d\u771f\u6b63\u662f\u4ed9\u4f5b\u7684\u5883\u754c\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5b66\u7684\u771f\u8c1b\uff0c\u5c82\u975e\u548c\u7985\u5b97\u4e00\u6837\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u4e07\u4e8b\u4e07\u7269\uff0c\u5230\u4e86\u5dc5\u5cf0\u65f6\uff0c\u9053\u7406\u672c\u5c31\u5168\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u8981\u505a\u5230\u2018\u65e0\u4eba\u65e0\u6bef\uff0c\u7269\u6211\u4e24\u5fd8\u2019\u65f6\uff0c\u624d\u80fd\u771f\u6b63\u5230\u8fbe\u5316 \u5883\uff0c\u5230\u8fbe\u5dc5\u5cf0\u3002\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5c11\u5973\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u603b\u7b97\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8001\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6709\u4e9b\u4eba\u8fd8\u4e0d\u660e\u767d\uff0c\u5230\u4e86\u2018\u624b\u4e2d\u65e0\u73af\uff0c\u5fc3\u4e2d\u6709\u73af\u2019\u65f6\uff0c\u5c31\u5df2\u6cbe\u6cbe\u81ea \u559c\uff0c\u5374\u4e0d\u77e5\u8fd9\u53ea\u4e0d\u8fc7\u521a\u5165\u95e8\u9762\u5df2\uff0c\u8981\u767b\u5802\u4eba\u5ba4\uff0c\u8fd8\u5dee\u5f97\u8fdc\u54e9\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u505a\u5230\u8fd9\u4e00\u6b65\u5c31\u5df2\u89c9\u5f97\u81ea\u6ee1\uff0c\u5c82\u975e\u6c38\u8fdc\u518d\u4e5f\u4f11\u60f3\u66f4\u8fdb\u4e00\u6b65\uff1f\u201d \u3000\u3000\u8001\u4eba\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\u989d\u4e0a\u4e5f\u4e0d\u7981\u6c81\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u9053\uff1a\u201c\u662f\u5b59\u8001\u5148\u751f\u4e48\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u7b54\u5e94\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5b59\u8001\u5148\u751f\u65e2\u5df2\u6765\u4e86\uff0c\u4e3a\u4f55\u4e0d\u80af\u73b0\u8eab\u4e00\u89c1\uff1f\u201d \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u4eba\u7b54\u5e94\u3002 \u3000\u3000\u98ce\u5439\u7a97\u6237\uff0c\u5439\u5f97\u7a97\u7eb8\u8258\u8258\u7684\u76f4\u54cd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\u82e5\u662f\u8981\u4ea4\u624b\uff0c\u4e16\u4e0a\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u529d\u963b\u3002 \u3000\u3000\u4f46\u8001\u4eba\u548c\u5c11\u5973\u7684\u4e00\u756a\u5bf9\u8bdd\uff0c\u5374\u4f3c\u5df2\u4f7f\u5f97\u4ed6\u4eec\u7684\u6597\u5fd7\u5b8c\u5168\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u867d\u7136\u8fd8\u662f\u9762\u9762\u76f8\u5bf9\uff0c\u867d\u7136\u8fd8\u662f\u4fdd\u6301\u7740\u539f\u6765\u7684\u59ff\u52bf\uff0c\u4f46\u522b\u7684\u4eba\u5374\u90fd\u900f\u4e86\u53e3\u6c14\uff0c\u7a81\u7136\u89c9 \u5f97\u538b\u529b\u5df2\u6d88\u5931\u3002 \u3000\u3000\u8fd9\u53ea\u56e0\u90a3\u79cd\u53ef\u6015\u7684\u6740\u6c14\u4e5f\u5df2\u6d88\u5931\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u5b59\u8001\u5148\u751f\u5eb6\u51e0\u8fd1\u4e4b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u7740\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u9053\u7406\u4eba\u4eba\u90fd\u4f1a\u8bf4\u7684\uff0c\u95ee\u9898\u662f\u4ed6\u80fd\u4e0d\u80fd\u505a\u5f97\u5230\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u80fd\u8bf4\u5f97\u51fa\u8fd9\u9053\u7406\u6765\uff0c\u5df2\u7ecf\u5f88\u4e0d\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5c31\u542c\u5230\u5916\u9762\u4f20\u6765\u4e86\u4e00\u9635\u9a9a\u52a8\u58f0\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u5230\u56db\u4e2a\u4eba\u62ac\u7740\u53e3\u68fa\u6750\u8d70\u5165\u4e86\u9662\u5b50\u3002 \u3000\u3000\u5d2d\u65b0\u7684\u68fa\u6750\uff0c\u6cb9\u6f06\u90fd\u4eff\u4f5b\u8fd8\u6ca1\u6709\u5b8c\u5168\u5e72\u900f\u3002 \u3000\u3000\u56db\u4eba\u7adf\u7136\u5c06\u53e3\u68fa\u6750\u7b14\u76f4\u62ac\u5165\u4e86\u4e0a\u5b98\u91d1\u8679\u5bb4\u5ba2\u7684\u5927\u5385\u3002 \u3000\u3000\u7acb\u523b\u6709\u6761\u9ec4\u8863\u5927\u6c49\u8fce\u4e86\u4e0a\u53bb\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u9519\u5730\u65b9\u4e86\uff0c\u51fa\u53bb\uff01\u201d \u3000\u3000\u62ac\u68fa\u6750\u7684\u811a\u592b\u56db\u4e0b\u77a7\u4e86\u4e00\u773c\uff0c\u562c\u61e6\u7740\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4f4d\u4e0a\u5b98\u8001\u7237\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u8863\u5927\u6c49\u9053\uff1a\u201c\u4f60\u95ee\u4e0a\u5b98\u8001\u7237\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u811a\u592b\u9053\uff1a\u201c\u90a3\u6211\u4eec\u5c31\u6ca1\u6709\u8d70\u9519\u5730\u65b9\uff0c\u8fd9\u53e3\u68fa\u6750\u5c31\u662f\u9001\u6765\u7ed9\u4e0a\u5b98\u8001\u7237\u7684\u3002\u201d \u3000\u3000\u9ec4\u8863\u5927\u6c49\u6012\u9053\uff1a\u201c\u4f60\u662f\u5728\u627e\u6b7b\uff0c\u8fd9\u53e3\u68fa\u6750\u4f60\u4eec\u521a\u597d\u7528\u5f97\u7740\u3002\u201d \u3000\u3000\u811a\u592b\u966a\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e0a\u597d\u7684\u6960\u6728\u5bff\u6750\uff0c\u6211\u4eec\u54ea\u6709\u8fd9\u4e48\u597d\u7684\u798f\u6c14\u3002\u201d \u3000\u3000\u9ec4\u8863\u5927\u6c49\u7684\u624b\u5df2\u5f80\u4ed6\u8138\u4e0a\u63b4\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u9053\uff1a\u201c\u8fd9\u53e3\u68fa\u6750\u662f\u8c01\u8981\u4f60\u4eec\u9001\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4e00\u53d1\u51fa\uff0c\u9ec4\u8863\u5927\u6c49\u7684\u624b\u5c31\u7acb\u523b\u505c\u4f4f\u3002 \u3000\u3000\u811a\u592b\u9762\u4e0a\u5374\u5df2\u5413\u5f97\u53d8\u4e86\u989c\u8272\uff0c\u6014\u4e86\u534a\u664c\uff0c\u624d\u5403\u5403\u9053\uff1a\u201c\u662f\u4f4d\u59d3\u5b8b\u7684\u8001\u7237\uff0c\u4ed8\u4e86\u56db\u4e24\u94f6\u5b50 \uff0c\u53eb\u5c0f\u4eba\u4eec\u4eca\u5929\u5c06\u8fd9\u53e3\u68fa\u6750\u9001\u5230\u5982\u4e91\u5ba2\u6808\u7684\u2018\u9ad8\u8d35\u5385\u2019\u6765\uff0c\u8fd8\u8981\u5c0f\u4eba\u4eec\u5f53\u9762\u4ea4\u7ed9\u4e0a\u5b98\u8001\u7237\u3002 \u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u59d3\u5b8b\uff1f\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u811a\u592b\u9053\uff1a\u201c\u662f\u4e2a\u7537\u7684\uff0c\u5e74\u7eaa\u597d\u50cf\u4e0d\u592a\u5927\uff0c\u4e5f\u4e0d\u5c0f\u4e86\uff0c\u51fa\u624b\u5f88\u5927\u65b9\uff0c\u6a21\u6837\u5374\u6ca1\u6709\u770b\u89c1\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u4ed6\u662f\u6628\u5929\u534a\u591c\u91cc\u5c06\u5c0f\u4eba\u4eec\u4ece\u5e8a\u4e0a\u53eb\u8d77\u6765\u7684\uff0c\u800c\u4e14\u5148\u5439\u7184\u4e86\u706f\uff0c\u5c0f\u4eba\u4eec\u6839\u672c \u5c31\u6ca1\u6709\u77a7\u89c1\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u7740\u8138\uff0c\u65e2\u4e0d\u89c9\u5f97\u610f\u5916\uff0c\u4e5f\u6ca1\u6709\u518d\u8ffd\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u65e9\u5c31\u77e5\u9053\u95ee\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u90a3\u811a\u592b\u53c8\u9053\uff1a\u201c\u8fd9\u53e3\u68fa\u6750\u7684\u4efd\u91cf\u4e0d\u8f7b\uff0c\u91cc\u9762\u597d\u50cf\u2026\u2026\u597d\u50cf\u6709\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6253\u5f00\u6765\u77a7\u77a7\u3002\u201d \u3000\u3000\u68fa\u76d6\u5e76\u6ca1\u6709\u9489\u5c01\uff0c\u7acb\u523b\u88ab\u6380\u8d77\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4e0a\u5b98\u91d1\u8679\u51b7\u6f20\u7684\u8138\u50cf\u662f\u7a81\u7136\u53d8\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u8138\u4e0a\u8fd8\u662f\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u751a\u81f3\u8fde\u7709\u90fd\u6ca1\u76b1\uff0c\u5634\u89d2\u90fd\u6ca1\u6709\u7275\u52a8\u3002 \u3000\u3000\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u6574\u5f20\u8138\u5374\u4eff\u4f5b\u7a81\u7136\u5168\u90fd\u6539\u53d8\u4e86\u3002 \u3000\u3000\u7adf\u50cf\u662f\u53d8\u6210\u4e86\u53e6\u4e00\u4e2a\u4eba\u7684\u8138\uff0c\u53c8\u50cf\u662f\u7a81\u7136\u6234\u4e0a\u4e86\u4e00\u5c42\u786c\u58f3\u7684\u5047\u9762\u5177\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u8ba9\u4eba\u770b\u5230\u4ed6\u73b0\u5728\u771f\u6b63\u7684\u9762\u76ee\u3002 \u3000\u3000\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u90fd\u6709\u8fd9\u4e48\u4e00\u5f20\u9762\u5177\u7684\uff0c\u5e73\u65f6\u867d\u7136\u770b\u4e0d\u5230\u5b83\uff0c\u4f46\u5230\u4e86\u5fc5\u8981\u65f6\uff0c\u5c31\u4f1a\u5c06\u8fd9\u5f20\u9762 \u5177\u6234\u8d77\u6765\u3002 \u3000\u3000\u6709\u4eba\u662f\u4e3a\u4e86\u8981\u9690\u85cf\u81ea\u5df1\u7684\u60b2\u54c0\uff0c\u6709\u4eba\u662f\u4e3a\u4e86\u8981\u9690\u85cf\u81ea\u5df1\u7684\u6124\u6012\uff0c\u6709\u4eba\u662f\u903c\u4e0d\u5f97\u5df2\uff0c\u4e0d\u5f97 \u4e0d\u4ee5\u7b11\u8138\u8ff7\u4eba\uff0c\u6709\u4eba\u662f\u4e3a\u4e86\u8981\u53eb\u522b\u4eba\u6015\u4ed6\u3002 \u3000\u3000\u4e5f\u6709\u4eba\u662f\u4e3a\u4e86\u8981\u9690\u85cf\u81ea\u5df1\u7684\u6050\u60e7\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u68fa\u6750\u91cc\u679c\u7136\u6709\u4e2a\u6b7b\u4eba\uff01 \u3000\u3000\u8fd9\u6b7b\u4eba\u8d6b\u7136\u7adf\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u72ec\u751f\u513f\u5b50\u4e0a\u5b98\u98de\uff01 \u3000\u3000\u4e0a\u5b98\u98de\u6b7b\u7684\u65f6\u5019\u674e\u5bfb\u6b22\u4e5f\u5728\u77a7\u7740\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u4eb2\u773c\u77a7\u89c1\u8346\u65e0\u547d\u6740\u6b7b\u4e0a\u5b98\u98de\uff0c\u800c\u4e14\u77a7\u89c1\u8346\u65e0\u547d\u5c06\u5c38\u4f53\u57cb\u846c\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8fd9\u5c38\u4f53\u53c8\u600e\u4f1a\u5ffd\u7136\u5728\u8fd9\u91cc\u51fa\u73b0\u4e86\uff1f \u3000\u3000\u662f\u8c01\u6398\u4e86\u8fd9\u5c38\u4f53\uff1f \u3000\u3000\u662f\u8c01\u9001\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u6709\u4ec0\u4e48\u76ee\u7684\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u95ea\u52a8\u7740\uff0c\u4f3c\u4e4e\u60f3\u5f97\u5f88\u591a\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8138\u4e0a\u7684\u9762\u5177\u5374\u4f3c\u8d8a\u6765\u8d8a\u539a\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u76ee\u5149\u7a81\u7136\u5411\u674e\u5bfb\u6b22\u4e00\u5b57\u5b57\u9053\uff1a\u201c \u4ee5\u524d\u4f60\u89c1\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u73b0\u5728\u4f60\u518d\u770b\u5230\u4ed6\u6709\u4f55\u611f\u60f3\uff1f\u201d \u3000\u3000\u5c38\u4f53\u5df2\u88ab\u6d17\u5f97\u5f88\u5e72\u51c0\uff0c\u5e76\u4e0d\u50cf\u662f\u4ece\u6ce5\u571f\u4e2d\u6398\u51fa\u6765\u7684\uff0c\u82bd\u7740\u5d2d\u65b0\u7684\u5bff\u8863\uff0c\u8eab\u4e0a\u65e2\u6ca1\u6ce5\u6c99\uff0c \u4e5f\u770b\u4e0d\u5230\u8840\u6e0d\u3002 \u3000\u3000\u53ea\u6709\u4e00\u70b9\u81f4\u547d\u7684\u4f24\u53e3\u3002 \u3000\u3000\u4f24\u53e3\u5728\u54bd\u5589\u4e0a\uff0c\u5165\u5589\u4e0b\u4e03\u5206\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u6211\u60f3\u2026\u2026\u4ed6\u6b7b\u5f97\u5e76\u4e0d\u75db\u82e6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4ed6\u6b7b\u5f97\u5f88\u5feb\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u6b7b\uff0c\u5e76\u4e0d\u75db\u82e6\uff0c\u75db\u82e6\u7684\u662f\u7b49\u6b7b\u7684\u65f6\u5019\uff0c\u770b\u6765\u4ed6\u5e76\u6ca1\u6709\u7ecf\u8fc7\u8fd9\u6bb5\u65f6\u5019\u3002\u201d \u3000\u3000\u4e0a\u5b98\u98de\u7684\u8138\u770b\u6765\u7684\u786e\u50cf\u662f\u6bd4\u6d3b\u7740\u65f6\u8fd8\u5b89\u8be6\u5e73\u9759\uff0c\u5c31\u50cf\u662f\u5df2\u7761\u7740\u4e86\u3002 \u3000\u3000\u4ed6\u4e34\u6b7b\u524d\u60ca\u60e7\u7684\u8868\u60c5\uff0c\u5df2\u4e0d\u77e5\u88ab\u8c01\u62b9\u5e73\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u8138\u867d\u80fd\u6234\u4e0a\u5c42\u9762\u5177\uff0c\u4f46\u773c\u775b\u5374\u4e0d\u80fd\u3002 \u3000\u3000\u4ed6\u773c\u775b\u4f3c\u6709\u706b\u7130\u71c3\u70e7\uff0c\u76ef\u7740\u674e\u5bfb\u6b22\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u80fd\u8fd9\u4e48\u5feb\u5c31\u5c06\u4ed6\u6740\u6b7b\u7684\u4eba\uff0c\u4e16\u4e0a\u5e76\u4e0d \u591a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u591a\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4f60\u4e5f\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5389\u58f0\u9053\uff1a\u201c\u6211\u600e\u4f1a\u6740\u6b7b\u6c60\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u4f1a\u6740\u4ed6\uff0c\u6211\u7684\u610f\u601d\u53ea\u4e0d\u8fc7\u662f\u8981\u4f60\u660e\u767d\uff0c\u80fd\u6740\u4ed6\u7684\u4eba\uff0c\u5e76\u4e0d\u4e00 \u5b9a\u662f\u8981\u6740\u4ed6\u7684\u4eba\uff0c\u6740\u4e86\u4ed6\u7684\u4eba\uff0c\u4e5f\u5e76\u4e0d\u4e00\u5b9a\u5c31\u662f\u80fd\u6740\u4ed6\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e16\u95f4\u5e38\u5e38\u6709\u5f88\u591a\u610f\u5916\u7684\u4e8b\u53d1\u751f\uff0c\u672c\u4e0d\u662f\u4efb\u4f55\u4eba\u6240\u80fd\u60f3\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e0d\u518d\u8bf4\u8bdd\u4e86\uff0c\u4f46\u773c\u775b\u8fd8\u662f\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u76ee\u5149\u5df2\u53d8\u5f97\u5f88\u6e29\u548c\uff0c\u751a\u81f3\u8fd8\u5e26\u7740\u4e9b\u540c\u60c5\u601c\u60af\u4e4b\u8272\u3002\u4f3c\u4e4e\u5df2\u900f\u8fc7\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u9762 \u76ee\uff0c\u770b\u5230\u4e86\u4ed6\u5fc3\u91cc\u7684\u60b2\u54c0\u548c\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u90fd\u5728\u4fb5\u72af\u522b\u4eba\uff0c\u6253\u51fb\u522b\u4eba\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u81ea\u5df1\u7ec8\u4e8e\u4e5f\u53d7\u5230\u6253\u51fb\uff0c\u800c\u4e14\u4e0d\u77e5\u9053\u8fd9\u6253\u51fb\u662f\u4ece\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u8840\u6d53\u4e8e\u6c34\uff0c\u513f\u5b50\u6bd5\u7adf\u662f\u513f\u5b50\u3002 \u3000\u3000\u65e0\u8bba\u5bf9\u8c01\u8bf4\u6765\uff0c\u8fd9\u6253\u51fb\u90fd\u4e0d\u7b97\u5c0f\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4f3c\u5df2\u6709\u4e9b\u4e0d\u5b89\uff0c\u94c1\u77f3\u822c\u7684\u610f\u5fd7\u4f3c\u5df2\u6e10\u6e10\u52a8\u6447\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u7684\u8fd9\u4efd\u540c\u60c5\u601c\u60af\uff0c\u5c31\u5c06\u662f\u4e00\u67c4\u94c1\u9524\uff0c\u4ed6\u8138\u4e0a\u5200\u523b\u6838\u6843\u58f3\u822c\u7684\u9762\u76ee\uff0c\u51e0\u4e4e\u5df2\u88ab \u6253\u5f97\u7c89\u788e\u3002 \u3000\u3000\u4ed6\u5df2\u65e0\u6cd5\u5fcd\u53d7\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f60\u6211\u8fd9\u4e00\u6218\uff0c\u8fdf\u65e9\u603b\u662f\u514d\u4e0d\u4e86\u7684\u3002\u201d\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a \u201c\u662f\u514d\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4eca\u5929\u2026\u2026\u201d \u7b2c\u516d\u5341\u4e5d\u7ae0\u3000\u662f\u771f\u541b\u5b50>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516d\u5341\u4e5d\u7ae0\u3000\u662f\u771f\u541b\u5b50 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u56e0\u72ec\u5b50\u88ab\u6740\uff0c\u5f02\u5e38\u6c14\u6012\uff0c\u8981\u548c\u674e\u5bfb\u6b22\u51b3\u4e00\u6b7b\u6218\uff0c\u5e76\u628a\u51b3\u6218\u65e5\u671f\u5b9a\u5728\u4eca\u5929\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\u6211\u90fd\u5949\u966a\uff0c\u53ea\u6709\u4eca\u5929\u4e0d\u884c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4eca\u5929\u6211\u2026\u2026\u6211\u53ea\u60f3\u53bb\u559d\u676f\u9152\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u626b\u8fc7\u68fa\u6750\u91cc\u7684\u5c38\u4f53\uff0c\u53f9\u606f\u7740\u63a5\u9053\uff1a\u201c\u6709\u4e9b\u65f6\u5019\u975e\u4f46\u4e0d\u9002\u5408\u51b3\u6597\uff0c\u4e5f\u4e0d\u9002\u5408\u505a\u522b\u7684 \u4e8b\uff0c\u9664\u4e86\u559d\u9152\u5916\uff0c\u51e0\u4e4e\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u80fd\u505a\uff0c\u4eca\u5929\u5c31\u662f\u8fd9\u79cd\u65f6\u5019\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u5a49\u8f6c\uff0c\u522b\u4eba\u4e5f\u8bb8\u6839\u672c\u4e0d\u80fd\u4e86\u89e3\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u4f46\u4e0a\u5b98\u91d1\u8679\u5374\u5f88\u4e86\u89e3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u5f88\u4e86\u89e3\u81ea\u5df1\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u5728\u8fd9\u79cd\u5fc3\u60c5\u4e0b\u548c\u522b\u4eba\u51b3\u6597\uff0c\u5c31\u7b49\u4e8e\u81ea\u5df1\u5df2\u5148\u5c06\u81ea\u5df1\u7684 \u4e00\u53ea\u624b\u94d0\u4f4f\u3002 \u3000\u3000\u4ed6\u5df2\u7ed9\u4e86\u654c\u4eba\u4e00\u4e2a\u6700\u597d\u7684\u673a\u4f1a\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u660e\u660e\u53ef\u4ee5\u5229\u7528\u8fd9\u673a\u4f1a\uff0c\u5374\u4e0d\u80af\u5360\u8fd9\u4fbf\u5b9c\u2014\u2014\u867d\u7136\u4ed6\u4e5f\u77e5\u9053\u8fd9\u79cd\u673a\u4f1a\u5e76\u4e0d\u591a\uff0c\u4ee5\u540e \u53ef\u80fd\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u518d\u6709\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u8bf4\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u5230\u54ea\u91cc\u627e\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u627e\u6211\uff0c\u53ea\u8981\u4f60\u8bf4\uff0c\u6211\u5c31\u4f1a\u53bb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u8bf4\u4e86\uff0c\u4f60\u80fd\u542c\u5230\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u5e2e\u4e3b\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5929\u4e0b\u7686\u95fb\uff0c\u6211\u60f3\u542c\u4e0d\u5230\u90fd\u5f88\u96be\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f60\u8981\u559d\u9152\uff0c\u8fd9\u91cc\u6709\u9152\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u9152\u6211\u914d\u559d\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51dd\u6ce8\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u914d\uff0c\u5c31\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u914d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\u5012\u4e86\u4e24\u5927\u676f\u9152\uff0c\u9053\uff1a\u201c\u6211\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u63a5\u8fc7\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u4ef0\u9762\u957f\u7b11\u9053\uff1a\u201c\u597d\u9152\uff01\u597d\u75db\u5feb\u7684\u9152\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u9152\u4e5f\u5e72\u4e86\uff0c\u51dd\u6ce8\u7740\u7a7a\u4e86\u7684\u9152\u676f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u8fd9\u662f\u6211\u7b2c\u4e00\u6b21\u559d\u9152\u3002 \u201d \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u9152\u676f\u6454\u5728\u5730\u4e0a\uff0c\u7c89\u788e\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5df2\u81ea\u68fa\u4e2d\u62b1\u8d77\u4e86\u4ed6\u513f\u5b50\u7684\u5c38\u4f53\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u9001\u7740\u4ed6\uff0c\u5ffd\u53c8\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u82e5\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\uff0c\u53c8\u4f55\u5c1d \u4e0d\u4f1a\u662f\u6211\u7684\u597d\u670b\u53cb\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u5012\u4e86\u676f\u9152\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u6f2b\u58f0\u9053\uff1a\u201c\u537f\u672c\u4f73\u4eba\uff0c\u5948\u4f55\u505a\u8d3c\uff1f\u201d \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u9152\u676f\u4e5f\u88ab\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7c89\u788e\uff01 \u3000\u3000\u5927\u5bb6\u4f3c\u5df2\u90fd\u53d8\u6210\u4e86\u6728\u5934\u4eba\uff0c\u76f4\u7b49\u674e\u5bfb\u6b22\u4e5f\u8d70\u4e86\u51fa\u53bb\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\u3002 \u3000\u3000\u6709\u7684\u4eba\u5df2\u5728\u7a83\u7a83\u79c1\u8bed\uff01 \u3000\u3000\u201c\u674e\u5bfb\u6b22\u679c\u7136\u4e0d\u6127\u662f\u674e\u5bfb\u6b22\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u4e5f\u53ea\u6709\u674e\u5bfb\u6b22\u624d\u80fd\u8981\u4e0a\u5b98\u5e2e\u4e3b\u656c\u4ed6\u4e00\u676f\u9152\u3002\u201d \u3000\u3000\u201c\u53ea\u53ef\u60dc\u4ed6\u4eec\u6ca1\u6709\u771f\u7684\u6253\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u603b\u89c9\u5f97\u8fd9\u4e24\u4eba\u50cf\u662f\u6709\u4e9b\u76f8\u540c\u7684\u5730\u65b9\u3002 \u3000\u3000\u201c\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\u4f1a\u6709\u76f8\u540c\u4e4b\u5904\uff1f\u2026\u2026\u4f60\u75af\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u4f5c\u98ce\u548c\u884c\u4e8b\u867d\u7136\u5b8c\u5168\u4e0d\u540c\uff0c\u53ef\u662f\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u5168\u90fd\u4e0d\u662f\u4eba\uff0c\u4ed6\u4eec\u505a\u7684\u4e8b\uff0c\u5168\u90e8 \u2018\u662f\u4eba\u2019\u7edd\u5bf9\u505a\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u5012\u6709\u51e0\u5206\u9053\u7406\uff0c\u4ed6\u4eec\u7684\u786e\u90fd\u4e0d\u662f\u4eba\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014\u4e00\u4e2a\u662f\u4ed9\u4f5b\uff0c\u4e00\u4e2a\u5374\u662f\u6076\u9b54\u3002\u201d \u3000\u3000\u5584\u6076\u672c\u5728\u4e00\u5ff5\u4e4b\u95f4\uff0c\u4ed9\u4f5b\u548c\u6076\u9b54\u7684\u8ddd\u79bb\u4e5f\u6b63\u662f\u5982\u6b64\u3002 \u3000\u3000\u201c\u4e0d\u9519\uff0c\u674e\u5bfb\u6b22\u82e5\u4e0d\u662f\u674e\u5bfb\u6b22\uff0c\u4e5f\u8bb8\u5c31\u662f\u53e6\u4e00\u4e2a\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u642c\u4e86\u5f20\u6905\u5b50\uff0c\u5c31\u5750\u5728\u4ed6\u8eab\u540e\uff0c\u5c06\u95e8\u6321\u4f4f\u3002 \u3000\u3000\u5979\u5df2\u5750\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u963f\u98de\u751a\u81f3\u8fde\u59ff\u52bf\u90fd\u6ca1\u6709\u53d8\u8fc7\u3002 \u3000\u3000\u4ed6\u7684\u59ff\u52bf\u770b\u6765\u5f88\u53ef\u7b11\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u50cf\u8fd9\u4e48\u6837\u7ad9\u7740\uff0c\u4f60\u4e0d\u89c9\u5f97\u96be\u53d7\u4e48\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u8212\u8212\u670d\u670d\u7684\u5750\u4e0b\u6765\uff0c\u6211 \u65c1\u8fb9\u5c31\u6709\u5f20\u6905\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u80af\u5750\uff1f\u6211\u4e5f\u77e5\u9053\u4f60\u5750\u4e0d\u4f4f\u7684\uff0c\u5728\u8fd9\u91cc\u5750\u7740\u5b9e\u5728\u4e0d\u662f\u6ecb\u5473\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u867d\u7136\u6321\u7740\u95e8\uff0c\u4f46\u4f60\u968f\u65f6\u90fd\u53ef\u4ee5\u5c06\u6211\u6253\u5012\u7684\u5440\uff0c\u8981\u4e0d\u7136\uff0c\u90a3\u8fb9\u6709\u7a97\u5b50\uff0c\u4f60\u4e5f\u53ef\u4ee5\u50cf\u5c0f \u5077\u4e00\u6837\u8df3\u7a97\u5b50\u9003\u51fa\u53bb\uff0c\u8fd9\u4e24\u79cd\u6cd5\u5b50\u90fd\u5bb9\u6613\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u6562\uff1f\u662f\u4e0d\u662f\uff1f\u201d\u4f60\u5fc3\u91cc\u867d\u7136\u6068\u4e0d\u5f97\u6740\u4e86\u6211\uff0c\u53ef\u662f\u4f60\u8fd8\u662f\u4e0d\u6562\u52a8\u624b\uff0c\u751a\u81f3\u8fde\u78b0\u90fd\u4e0d \u6562\u78b0\u6211\uff0c\u56e0\u4e3a\u4f60\u5fc3\u91cc\u8fd8\u662f\u5728\u7231\u7740\u6211\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u8fd8\u662f\u90a3\u4e48\u6e29\u67d4\uff0c\u90a3\u4e48\u52a8\u542c\u3002 \u3000\u3000\u5979\u7b11\u5f97\u751a\u81f3\u6bd4\u5e73\u5e38\u66f4\u5a07\u5a9a\uff0c\u66f4\u6109\u5feb\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u559c\u6b22\u770b\u4eba\u53d7\u6298\u78e8\uff0c\u5979\u5e0c\u671b\u6bcf\u4e2a\u4eba\u90fd\u53d7\u5979\u7684\u6298\u78e8\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u5979\u53ea\u80fd\u6298\u78e8\u7231\u5979\u7684\u4eba\u3002 \u3000\u3000\u5979\u867d\u7136\u770b\u4e0d\u5230\u963f\u98de\u9762\u4e0a\u75db\u82e6\u7684\u8868\u60c5\uff0c\u5374\u53ef\u4ee5\u6e05\u6e05\u695a\u695a\u7684\u770b\u5230\u963f\u98de\u8116\u5b50\u540e\u7684\u8840\u7ba1\u5728\u81a8\u6da8\uff0c \u4f3c\u5df2\u5c06\u66b4\u88c2\u3002 \u3000\u3000\u5979\u8ba4\u4e3a\u8fd9\u662f\u79cd\u4eab\u53d7\uff0c\u5750\u5f97\u66f4\u8212\u670d\u4e86\uff0c\u6b63\u60f3\u53bb\u5012\u676f\u9152\u2014\u2014 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6905\u5b50\u88ab\u8e22\u7ffb\uff0c\u5979\u7684\u4eba\u4e5f\u51e0\u4e4e\u88ab\u8e22\u5012\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5df2\u56de\u6765\u4e86\uff0c\u5e26\u7740\u4ed6\u72ec\u751f\u513f\u5b50\u7684\u5c38\u4f53\u4e00\u9f50\u6765\u4e86\uff01 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u6905\u5b50\u82e5\u88ab\u8e22\u7ffb\uff0c\u5fc3\u91cc\u603b\u96be\u514d\u6709\u4e9b\u8e69\u626d\u7684\u3002 \u3000\u3000\u4f46\u6797\u4ed9\u513f\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u73b0\u5728\u65e0\u8bba\u8bf4\u4ec0\u4e48\uff0c\u505a\u4ec0\u4e48\uff0c\u90fd\u611a\u8822 \u6781\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u773c\u775b\u4e5f\u76ef\u5728\u963f\u98de\u8116\u5b50\u4e0a\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u56de\u8fc7\u5934\u6765\u3002\u770b\u770b\u8fd9\u4eba\u662f\u8c01\uff01\u201d \u3000\u3000\u963f\u98de\u7684\u8eab\u5b50\u6ca1\u6709\u52a8\uff0c\u8840\u7ba1\u5374\u5728\u8df3\u52a8\uff0c\u7136\u540e\u5934\u624d\u6162\u6162\u7684\u8f6c\u52a8\uff0c\u773c\u89d2\u7ec8\u4e8e\u77a5\u89c1\u4e86\u4e0a\u5b98\u91d1\u8679\u624b \u91cc\u62b1\u7740\u7684\u5c38\u4f53\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7684\u773c\u89d2\u4e5f\u5f00\u59cb\u8df3\u52a8\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ef\u7740\u4ed6\u7684\u773c\u775b\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u963f\u98de\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4ed6\u51e0\u5929\u524d\u8fd8\u6d3b\u7740\u7684\uff0c\u800c\u4e14\u6d3b\u5f97\u5f88\u597d\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u963f\u98de\u53c8\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5ffd\u7136\u770b\u5230\u4ed6\u6b7b\u4e86\uff0c\u4e5f\u672a\u5403\u60ca\uff0c\u53ea\u56e0\u4f60\u65e9\u5c31\u77e5\u9053\u4ed6\u6b7b\u4e86\uff0c\u662f\u4e0d\u662f\uff1f \u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u65e9\u5c31\u77e5\u9053\u4ed6\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5389\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u6740\u6b7b\u4ed6\u7684\u4eba\uff0c\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u4ed6\u968f\u968f\u4fbf\u4fbf\u5c31\u5c06\u8fd9\u53e5\u8bdd\u8bf4\u4e86\u51fa\u6765\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u5b8c\u5168\u4e0d\u77e5\u9053\u8fd9\u53e5\u8bdd\u80fd\u5f15 \u8d77\u4ec0\u4e48\u6837\u7684\u540e\u679c\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u5c11\u5973\u4eec\u90fd\u5413\u5446\u4e86\u3002 \u3000\u3000\u5c31\u8fde\u6797\u4ed9\u513f\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u79cd\u5f88\u5947\u5f02\u7684\u60c5\u611f\uff0c\u7adf\u4eff\u4f5b\u6709\u4e9b\u60b2 \u54c0\uff0c\u6709\u4e9b\u601c\u60dc\u3002 \u3000\u3000\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u600e\u4f1a\u5bf9\u963f\u98de\u6709\u8fd9\u79cd\u611f\u60c5\u3002 \u3000\u3000\u4f46\u5979\u5374\u77e5\u9053\u53ea\u8981\u4e0a\u5b98\u91d1\u8679\u4e00\u51fa\u624b\uff0c\u5c31\u7edd\u4e0d\u4f1a\u518d\u7559\u4e0b\u4ed6\u7684\u547d\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u968f\u65f6\u90fd\u53ef\u80fd\u51fa\u624b\u7684\u3002 \u3000\u3000\u5979\u77a7\u7740\u963f\u98de\uff0c\u90a3\u773c\u8272\u5c31\u597d\u50cf\u5728\u77a7\u7740\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u8822\u5230\u6781\u70b9\u7684\u6b7b\u4eba\u3002 \u3000\u3000\u201c\u8fd9\u4eba\u4e0d\u4f46\u8822\u5f97\u8981\u547d\uff0c\u800c\u4e14\u4e5f\u5df2\u9189\u5f97\u53d1\u660f\uff0c\u5426\u5219\u4e3a\u4f55\u8981\u81ea\u5df1\u627f\u8ba4\uff1f\u8fd9\u79cd\u4eba\u7b80\u76f4\u5df2\u5b8c\u5168\u65e0 \u53ef\u6551\u836f\uff0c\u4ed6\u7684\u6b7b\u6d3b\uff0c\u6211\u53c8\u4f55\u5fc5\u5173\u5fc3\uff1f\u201d \u3000\u3000\u5979\u626d\u8f6c\u5934\uff0c\u518d\u4e5f\u4e0d\u53bb\u77a7\u4ed6\u3002 \u3000\u3000\u5979\u53ea\u5e0c\u671b\u4e0a\u5b98\u91d1\u8679\u5feb\u70b9\u6740\u4e86\u4ed6\uff0c\u8d8a\u5feb\u8d8a\u597d\uff0c\u4e5f\u514d\u5f97\u70e6\u607c\u3002 \u3000\u3000\u4f46\u5979\u5374\u53c8\u4e0d\u7981\u8981\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u6211\u65e2\u7136\u5bf9\u4ed6\u7684\u6b7b\u6d3b\u5168\u4e0d\u5173\u5fc3\uff0c\u53c8\u4f55\u5fc5\u4e3a\u8fd9\u79cd\u4e8b\u70e6\u607c\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7adf\u8fdf\u8fdf\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u8fd8\u5728\u76ef\u7740\u963f\u98de\u7684\u773c\u775b\uff0c\u4eff\u4f5b\u8981\u4ece\u963f\u98de\u773c\u775b\u91cc\u770b\u51fa\u4e00\u4e9b\u4ed6\u8fd8\u4e0d\u80fd\u4e86\u89e3\u7684\u4e8b\u60c5\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4ec0\u4e48\u4e5f\u770b\u4e0d\u5230\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u91cc\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u7684\u786e\u5df2\u4e0d\u50cf\u662f\u6d3b\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u89c9\u5f97\u8fd9\u53cc\u773c\u775b\u5f88\u719f\u6089\uff0c\u4eff\u4f5b\u4ee5\u524d\u5c31\u89c1\u8fc7\u3002 \u3000\u3000\u4ed6\u7684\u786e\u89c1\u8fc7\u591a\u6b21\u3002 \u3000\u3000\u5f53\u4ed6\u5c06\u8346\u65e0\u547d\u7684\u5251\u62d4\u51fa\u6765\u4ea4\u7ed9\u963f\u98de\u65f6\uff0c\u8346\u65e0\u547d\u7684\u773c\u775b\u5c31\u51e0\u4e4e\u548c\u963f\u98de\u73b0\u5728\u7684\u773c\u775b\u5b8c\u5168\u4e00\u6837 \u3002 \u3000\u3000\u5f53\u4ed6\u6740\u6b7b\u4e86\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u7684\u773c\u775b\u8fd8\u6ca1\u6709\u95ed\u8d77\u6765\u65f6\uff0c\u4e5f\u5c31\u662f\u8fd9\u6837\u5b50\u2014\u2014\u65e2\u6ca1\u6709\u611f\u60c5\uff0c\u4e5f\u6ca1 \u6709\u751f\u547d\uff0c\u5bf9\u4e00\u5207\u4e8b\u90fd\u5df2\u5b8c\u5168\u7edd\u671b\u3002 \u3000\u3000\u963f\u98de\u5728\u7b49\u7740\uff0c\u9759\u9759\u7684\u7b49\u7740\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u5728\u7b49\u6b7b\uff1f\u201d \u3000\u3000\u963f\u98de\u62d2\u7edd\u56de\u7b54\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u627f\u8ba4\uff0c\u4e3a\u7684\u5c31\u662f\u5e0c\u671b\u6211\u6740\u6b7b\u4f60\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u62d2\u7edd\u56de\u7b54\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ee\u4e2d\u5ffd\u53c8\u95ea\u8fc7\u4e00\u4e1d\u6b8b\u9177\u7684\u7b11\u610f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5415\u603b\u7ba1\u3002\u201d \u3000\u3000\u4ed6\u53ea\u5524\u4e86\u4e00\u58f0\uff0c\u7acb\u523b\u5c31\u6709\u4e2a\u4eba\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u8c01\u90fd\u4e0d\u77e5\u9053\u8fd9\u4eba\u672c\u6765\u85cf\u5728\u54ea\u91cc\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u9644\u8fd1\u662f\u5426\u8fd8\u85cf\u7740\u522b\u7684\u4eba\uff0c\u4e0a\u5b98\u91d1\u8679\u7684\u9644\u8fd1 \uff0c\u4eff\u4f5b\u6c38\u8fdc\u90fd\u6709\u5f88\u591a\u4eba\u5728\u8eb2\u85cf\u7740\u3002 \u3000\u3000\u522b\u4eba\u770b\u4e0d\u89c1\u7684\u4eba\uff0c\u5c31\u50cf\u662f\u9b3c\u9b42\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8d70\u5230\u54ea\u91cc\uff0c\u8fd9\u4e9b\u9b3c\u9b42\u5c31\u8ddf\u5230\u54ea\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u547d\u4ee4\u5c31\u662f\u9b54\u5492\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u5c06\u8fd9\u4e9b\u9b3c\u9b42\u5524\u51fa\u6765\uff01 \u3000\u3000\u5415\u603b\u7ba1\u82e5\u771f\u7684\u662f\u4e2a\u9b3c\u9b42\uff0c\u81f3\u5c11\u603b\u4e0d\u662f\u997f\u6b7b\u9b3c\u3002 \u3000\u3000\u997f\u6b7b\u9b3c\u6ca1\u6709\u8fd9\u4e48\u80d6\u7684\u3002 \u3000\u3000\u4ed6\u80d6\u5f97\u5c31\u50cf\u662f\u4e2a\u7403\uff0c\u884c\u52a8\u5374\u5f88\u654f\u6377\uff0c\u4e00\u6eda\u5c31\u6eda\u4e86\u51fa\u6765\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u773c\u775b\u8fd8\u662f\u76ef\u7740\u963f\u98de\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u8981\u6b7b\uff0c\u6211\u4eec\u4e0d\u7ed9\u4ed6\u6b7b\u3002\u201d \u3000\u3000\u5415\u603b\u7ba1\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u4eec\u7ed9\u4ed6\u522b\u7684\u3002\u201d \u3000\u3000\u5415\u603b\u7ba1\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u7ed9\u4ed6\u9152\uff0c\u7ed9\u4ed6\u5973\u4eba\uff0c\u4ed6\u8981\u591a\u5c11\uff0c\u5c31\u7ed9\u591a\u5c11\u3002\u201d \u3000\u3000\u5415\u603b\u7ba1\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u65e0\u8bba\u8981\u8c01\uff0c\u90fd\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u5415\u603b\u7ba1\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4ed6\u5634\u91cc\u7b54\u7740\u6d3b\uff0c\u772f\u7740\u7684\u773c\u775b\u5374\u6709\u610f\u65e0\u610f\u95f4\u8198\u4e86\u6797\u4ed9\u513f\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u65e0\u8bba\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u90fd\u4e00\u6837\uff0c\u5c31\u7b97\u4ed6\u8981\u4f60\u7684\u8001\u5a46\uff0c\u4e5f\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u5415\u603b\u7ba1\u7684\u773c\u775b\u5df2\u772f\u6210\u4e86\u4e00\u6761\u7ebf\uff0c\u8eac\u8eab\u7b11\u9053\uff1a\u201c\u5c5e\u4e0b\u660e\u767d\u4e86\uff0c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u5c06\u8001\u5a46\u5e26\u6765\u7ed9\u4ed6 \u770b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\u54ac\u5f97\u5f88\u91cd\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4ed6\u82e5\u8981\u6211\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u65e0\u8bba\u8c01\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u5374\u4e0d\u4e00\u6837\uff0c\u6211\u662f\u4f60\u7684\uff0c\u9664\u4e86\u4f60\uff0c\u8c01\u90fd\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u5979\u5e26\u7740\u7b11\u8d70\u8fc7\u53bb\uff0c\u8d70\u5230\u4e0a\u5b98\u91d1\u8679\u8eab\u65c1\uff0c\u8f7b\u629a\u7740\u4ed6\u7684\u80a9\u3002 \u3000\u3000\u5979\u7b11\u5f97\u90a3\u4e48\u751c\uff0c\u52a8\u4f5c\u90a3\u4e48\u6e29\u67d4\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5374\u8fde\u77a7\u90fd\u4e0d\u77a7\u5979\u4e00\u773c\uff0c\u7a81\u7136\u817e\u51fa\u624b\uff0c\u4e00\u5df4\u638c\u6253\u5728\u5979\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5 \u8981\u4f60\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u6574\u4e2a\u4eba\u90fd\u88ab\u6253\u5f97\u98de\u4e86\u51fa\u53bb\uff0c\u8dcc\u5230\u9662\u5b50\u91cc\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u8981\u4ec0\u4e48\u90fd\u7ed9\u4ed6\uff0c\u5c31\u662f\u4e0d\u80fd\u8ba9\u4ed6\u8d70\uff0c\u6211\u8981\u770b\u4ed6\u4e09\u4e2a\u6708\u540e\u4f1a\u53d8\u6210\u4ec0 \u4e48\u6837\u5b50\u3002\u201d \u3000\u3000\u5415\u603b\u7ba1\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fd9\u624d\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u963f\u98de\u7d27\u7d27\u54ac\u7740\u7259\uff0c\u4f46\u7259\u9f7f\u8fd8\u662f\u4e3b\u201c\u683c\u683c\u201d\u7684\u6253\u6218\uff0c\u5636\u58f0\u9053\uff1a\u201c\u6211\u6740\u4e86\u4f60\u513f\u5b50\uff0c\u4f60\u4e3a\u4ec0\u4e48 \u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5df2\u8d70\u51fa\u4e86\u95e8\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u8ba9\u4f60\u6d3b\u7740\u75db\u82e6\uff0c\u53c8\u6ca1\u6709\u52c7\u6c14\u6b7b\uff01 \u201d \u3000\u3000\u201c\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u8981\u4f60\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u201c\u6d3b\u7740\u75db\u82e6\uff0c\u53c8\u6ca1\u6709\u52c7\u6c14\u6b7b\uff01\u201d \u3000\u3000\u963f\u98de\u8eab\u5b50\u5f80\u540e\u7f29\uff0c\u7f29\u6210\u4e00\u56e2\uff0c\u5c31\u50cf\u662f\u5728\u8eb2\u7740\u6761\u65e0\u5f62\u7684\u97ad\u5b50\u3002 \u3000\u3000\u8fd9\u6761\u97ad\u5b50\u6b63\u4e0d\u505c\u5728\u62bd\u6253\u7740\u4ed6\u3002 \u3000\u3000\u5415\u603b\u7ba1\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u4eba\u751f\u5f97\u610f\u987b\u5c3d\u6b22\uff0c\u83ab\u4f7f\u91d1\u676f\u7a7a\u5bf9\u6708\uff0c\u505a\u4eba\u672c\u5c31\u662f\u8fd9\u4e48 \u56de\u4e8b\uff0c\u53c8\u4f55\u5fc5\u592a\u8ba4\u771f\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8f6c\u5411\u5c11\u5973\uff0c\u8138\u7acb\u523b\u6c89\u4e86\u4e0b\u6765\uff0c\u5389\u58f0\u9053\uff1a\u2018\u8fd8\u4e0d\u5feb\u4e3a\u5c11\u7237\u7f6e\u9152\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5bf9\u4e0a\u5b98\u91d1\u8679\u8bf4\u8bdd\u65f6\u662f\u4e00\u5f20\u8138\uff0c\u5bf9\u963f\u98de\u8bf4\u8bdd\u662f\u4e00\u5f20\u8138\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u5bf9\u8fd9\u4e9b\u5c11\u5973\u4eec\u8bf4\u8bdd\uff0c\u53c8\u662f\u53e6\u4e00\u5f20\u4e0d\u540c\u7684\u8138\u3002 \u3000\u3000\u5927\u591a\u6570\u4eba\u90fd\u6709\u597d\u51e0\u5f20\u4e0d\u540c\u7684\u8138\uff0c\u4ed6\u4eec\u82e5\u8981\u53d8\u8138\u65f6\uff0c\u5c31\u597d\u50cf\u620f\u5b50\u5728\u6362\u9762\u5177\uff0c\u751a\u81f3\u6bd4\u6362\u9762\u5177 \u8fd8\u8981\u7b80\u5355\u3002 \u3000\u3000\u9762\u5177\u6362\u5f97\u591a\u4e86\uff0c\u6e10\u6e10\u5c31\u4f1a\u5fd8\u8bb0\u81ea\u5df1\u672c\u6765\u662f\u4ec0\u4e48\u6837\u7684\u4e00\u5f20\u8138\u3002 \u3000\u3000\u9762\u5177\u6234\u5f97\u4e45\u4e86\uff0c\u5c31\u518d\u4e5f\u4e0d\u613f\u62ff\u4e0b\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5df2\u53d1\u89c9\uff0c\u9762\u5177\u8d8a\u591a\uff0c\u5403\u7684\u4e8f\u5c31\u8d8a\u5c11\u3002 \u3000\u3000\u5e78\u597d\u8fd8\u6709\u4e9b\u4eba\u6ca1\u6709\u9762\u5177\uff0c\u53ea\u6709\u4e00\u5f20\u8138\uff0c\u4ed6\u81ea\u5df1\u7684\u8138\uff01 \u3000\u3000\u65e0\u8bba\u4ed6\u4eec\u9047\u7740\u4ec0\u4e48\u4e8b\uff0c\u5403\u4e86\u591a\u5c11\u4e8f\uff0c\u8fd9\u5f20\u8138\u90fd\u6c38\u8fdc\u4e0d\u4f1a\u6539\u53d8\uff01 \u3000\u3000\u4ed6\u4eec\u8981\u54ed\u5c31\u54ed\uff0c\u8981\u7b11\u5c31\u7b11\uff0c\u8981\u6d3b\u5c31\u6d3b\uff0c\u8981\u6b7b\u5c31\u6b7b\uff01 \u3000\u3000\u4ed6\u4eec\u6b7b\u4e5f\u4e0d\u613f\u6539\u53d8\u81ea\u5df1\u7684\u672c\u8272\uff01\u7537\u513f\u7684\u672c\u8272\uff01 \u3000\u3000\u7537\u4eba\u7684\u672c\u8272\uff01 \u3000\u3000\u4e16\u4e0a\u82e5\u6ca1\u6709\u8fd9\u6837\u7684\u4eba\uff0c\u4eba\u751f\u5c31\u771f\u7684\u50cf\u662f\u4e00\u51fa\u620f\u4e86\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u8fd9\u4e16\u754c\u4e5f\u5c31\u4e0d\u77e5\u4f1a\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\u3002 \u3000\u3000\u9152\u6765\u4e86\u3002 \u3000\u3000\u5415\u603b\u7ba1\u5012\u9152\uff0c\u62ff\u676f\uff0c\u7b11\u9053\uff1a\u201c\u559d\u5427\uff0c\u9152\u559d\u5f97\u591a\u4e86\uff0c\u4f60\u5c31\u4f1a\u53d1\u89c9\u4e16\u4e0a\u6240\u6709\u7684\u5973\u4eba\u672c\u90fd\u662f\u4e00 \u6837\u7684\uff0c\u66f4\u4e0d\u5fc5\u8ba4\u771f\u3002\u201d \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u76ef\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u5415\u603b\u7ba1\u772f\u7740\u773c\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8981\u7684\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u963f\u98de\u773c\u775b\u91cc\u5e03\u6ee1\u8840\u4e1d\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u8981\u4f60\u7684\u8001\u5a46\uff01\u201d \u3000\u3000\u591c\u3002 \u3000\u3000\u591c\u5e02\u3002 \u3000\u3000\u591c\u5e02\u6c38\u8fdc\u662f\u70ed\u95f9\u7684\uff0c\u591c\u5e02\u4e2d\u6c38\u8fdc\u6709\u5404\u5f0f\u5404\u6837\u4e0d\u540c\u7684\u4eba\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u89c9\u5f97\u8fd9\u4e16\u4e0a\u4eff\u4f5b\u5df2\u53ea\u5269\u4e0b\u4ed6\u4e00\u4e2a\u4eba\uff0c\u6839\u672c\u6ca1\u6709\u522b\u4eba\u5b58\u5728\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6240\u7231\u7684\u4eba\u90fd\u79bb\u4ed6\u5f88\u8fdc\uff0c\u592a\u8fdc\u4e86\uff0c\u4eff\u4f5b\u5df2\u53d8\u5f97\u5f88\u98d8\u6e3a\uff0c\u5f88\u865a\u5e7b\uff0c\u4ed6\u51e0\u4e4e\u4e0d\u80fd\u611f\u89c9\u5230 \u4ed6\u4eec\u7684\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u5df2\u542c\u5230\u9f99\u5578\u4e91\u7236\u5b50\u7684\u6d88\u606f\uff0c\u53ef\u662f\u2014\u2014 \u3000\u3000\u6797\u8bd7\u97f3\u5462\uff1f \u3000\u3000\u6ca1\u6709\u8e2a\u8ff9\uff0c\u6ca1\u6709\u6d88\u606f\uff0c\u53ea\u6709\u601d\u5ff5\uff0c\u6c38\u6052\u7684\u601d\u5ff5\u3002 \u3000\u3000\u201c\u5929\u957f\u5730\u4e45\u6709\u5c3d\u65f6\uff0c\u6b64\u6068\u7ef5\u7ef5\u65e0\u7edd\u671f\u3002\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bd7\u7684\u6587\u5b57\u867d\u6d45\u8fd1\uff0c\u5176\u4e2d\u542b\u8574\u7684\u60c5\u611f\u5374\u6df1\u901f\u5982\u6d77\u3002 \u3000\u3000\u4f46\u82e5\u975e\u77e5\u60c5\u7684\u4eba\uff0c\u53c8\u600e\u4e48\u4f53\u4f1a\u5230\u8fd9\u5176\u4e2d\u7684\u8f9b\u9178\u6ecb\u5473\uff1f \u3000\u3000\u8fdc\u5904\u6709\u591c\u7b1b\u5728\u4f34\u7740\u60b2\u6b4c\u3002 \u3000\u3000\u51c4\u51c9\u7684\u591c\u7b1b\uff0c\u5982\u601d\u5982\u6155\uff1a \u3000\u3000\u201c\u4f55\u5fc5\u591a\u60c5\uff1f \u3000\u3000\u4f55\u5fc5\u75f4\u60c5\uff1f \u3000\u3000\u82b1\u82e5\u591a\u60c5\uff0c\u4e5f\u65e9\u51cb\u96f6\u3002 \u3000\u3000\u4eba\u82e5\u591a\u60c5\uff0c\u6194\u60b4\uff0c\u6194\u60b4\u2026\u2026 \u3000\u3000\u4eba\u5728\u5929\u6daf\uff0c\u4f55\u59a8\u6194\u60b4 \u3000\u3000\u9152\u4eba\u91d1\u6a3d\uff0c\u4f55\u59a8\u6c89\u9189\u3002 \u3000\u3000\u9189\u773c\u770b\u522b\u4eba\u6210\u53cc\u4f5c\u5bf9\u3002 \u3000\u3000\u4e5f\u80dc\u8fc7\u65e0\u4eba\u5904\u6697\u5f39\u76f8\u601d\u6cea\u2026\u2026\u201d \u3000\u3000\u201c\u5356\u5531\u7684\u4eba\u672c\u8eab\u5df2\u591f\u60b2\u82e6\uff0c\u53c8\u4f55\u5fc5\u518d\u4ee5\u8fd9\u79cd\u51c4\u51c9\u7684\u6b4c\u58f0\u6765\u8d5a\u4eba\u773c\u6cea\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6ee1\u6ee1\u7684\u559d\u4e86\u676f\u9152\uff0c\u5ffd\u7136\u4ee5\u7b77\u6572\u676f\uff0c\u968f\u7740\u90a3\u51c4\u51c9\u7684\u591c\u7b1b\u6f2b\u58f0\u4f4e\u541f\uff1a \u3000\u3000\u201c\u82b1\u6728\u7eb5\u65e0\u60c5 \u3000\u3000\u8fdf\u65e9\u4e5f\u51cb\u96f6 \u3000\u3000\u65e0\u60c5\u7684\u4eba\uff0c\u4e5f\u603b\u6709\u4e00\u65e5\u6194\u60b4\u3002 \u3000\u3000\u4eba\u82e5\u65e0\u60c5 \u3000\u3000\u6d3b\u7740\u8fd8\u6709\u4f55\u6ecb\u5473\uff1f \u3000\u3000\u7eb5\u7136\u5728\u65e0\u4eba\u5904\u6697\u5f39\u76f8\u601d\u6cea\uff0c\u4e5f\u603b\u6bd4\u65e0\u6cea\u53ef\u6d41\u597d\u51e0\u500d\u3002\u201d \u3000\u3000\u7b1b\u58f0\u72b9\u4f4e\u56de\u4e0d\u5df2\uff0c\u4ed6\u5374\u5df2\u7a81\u7136\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u7b11\u53c8\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f \u3000\u3000\u963f\u98de\u5462\uff1f \u3000\u3000\u8fd9\u534a\u5929\uff0c\u674e\u5bfb\u6b22\u4e00\u76f4\u90fd\u5728\u5bfb\u627e\uff0c\u6253\u542c\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u963f\u98de\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u8c01\u4e5f\u6ca1\u6709\u770b\u5230\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5f53\u7136\u60f3\u4e0d\u5230\u963f\u98de\u7adf\u5230\u4e86\u91d1\u94b1\u5e2e\u7684\u603b\u90e8\u3002 \u3000\u3000\u5c31\u7b97\u4ed6\u60f3\u5230\uff0c\u4e5f\u4e0d\u77e5\u90a3\u5730\u65b9\u5728\u4f55\u5904\u3002 \u3000\u3000\u706f\u5728\u98ce\u4e2d\u6447\u6643\uff0c\u9152\u5728\u676f\u4e2d\u6447\u6643\u3002 \u3000\u3000\u660f\u6d4a\u7684\u9152\uff0c\u9eef\u6de1\u7684\u706f\u5149\u3002 \u3000\u3000\u4ed6\u559d\u9152\u7684\u5730\u65b9\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5f88\u5c0f\u7684\u9762\u644a\u5b50\u3002 \u3000\u3000\u8fd9\u4e00\u6392\u90fd\u662f\u5c0f\u644a\u5b50\uff0c\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u7684\uff0c\u90fd\u662f\u5f88\u5e73\u51e1\u7684\u5c0f\u4eba\u7269\uff0c\u8c01\u90fd\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u8ba4 \u5f97\u522b\u4eba\u3002 \u3000\u3000\u4ed6\u559c\u6b22\u8fd9\u79cd\u60c5\u8c03\uff0c\u5e26\u7740\u4e9b\u8427\u7d22\uff0c\u5e26\u7740\u4e9b\u5bc2\u5bde\uff0c\u5374\u53c8\u5e26\u7740\u51e0\u5206\u6d12\u8131\u3002 \u3000\u3000\u4e16\u95f4\u7684\u8363\u8fb1\uff0c\u751f\u547d\u7684\u60b2\u6b22\uff0c\u5728\u8fd9\u4e9b\u4eba\u5fc3\u76ee\u4e2d\uff0c\u90fd\u5df2\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u53ea\u8981\u6709\u4e00\u676f\u5728\u4e4e\uff0c\u5c31\u5df2 \u8db3\u591f\u3002 \u3000\u3000\u5728\u8fd9\u91cc\uff0c\u65e2\u6ca1\u6709\u5f97\u610f\u7684\u957f\u7b11\uff0c\u4e5f\u6ca1\u6709\u6177\u6168\u7684\u60b2\u6b4c\u3002 \u3000\u3000\u591c\u8272\u662f\u5982\u6b64\u5e73\u9759\uff0c\u5982\u6b64\u6de1\u6f20\u2026\u2026 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5e73\u9759\u4e2d\u8d77\u4e86\u9a9a\u52a8\u3002 \u3000\u3000\u6709\u4eba\u5728\u547c\u559d\uff0c\u53f1\u9a82\uff01 \u3000\u3000\u201c\u9152\u9b3c\uff0c\u4e0d\u8981\u8138\uff0c\u5077\u9152\u559d\uff0c\u5c31\u7b97\u4f60\u559d\u4e0b\u53bb\u6211\u4e5f\u8981\u4f60\u5410\u51fa\u6765\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u8f6c\u8fc7\u5934\u3002 \u3000\u3000\u4ed6\u8f6c\u5934\u53bb\u77a7\uff0c\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u542c\u5230\u201c\u9152\u9b3c\u201d\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u4eba\u62b1\u7740\u4e2a\u9152\u575b\u5b50\uff0c\u867d\u5df2\u88ab\u6253\u5f97\u8eba\u5728\u5730\u4e0a\uff0c\u8fd8\u662f\u6b7b\u4e5f\u4e0d\u80af\u653e\u677e\u62fc\u547d\u7684\u559d\uff0c\u4f38\u8fc7\u5934 \u53bb\u559d\u9152\u3002 \u3000\u3000\u4e00\u4e2a\u8170\u4e0a\u56f4\u7740\u5757\u6cb9\u5e03\u7684\u8001\u5934\u5b50\uff0c\u5634\u91cc\u9a82\u4e2a\u4e0d\u505c\uff0c\u624b\u4e0a\u6253\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6697\u6697\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u8d70\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u8ba9\u4ed6\u559d\u9152\uff0c\u7b97\u6211\u7684\u94b1\u3002\u201d \u3000\u3000\u9a9a\u52a8\u7acb\u523b\u505c\u4e86\uff0c\u624b\u4e5f\u505c\u4e86\u3002 \u3000\u3000\u94b1\u4e0d\u4f46\u80fd\u5c01\u4f4f\u4eba\u7684\u624b\uff0c\u4e5f\u80fd\u585e\u4f4f\u4eba\u7684\u5634\u3002 \u3000\u3000\u8eba\u5728\u5730\u4e0a\u7684\u4eba\u8fde\u7ad9\u90fd\u6765\u4e0d\u53ca\u7ad9\u8d77\u6765\uff0c\u6367\u7740\u9152\u575b\u5b50\u5c31\u5f80\u5634\u91cc\u5012\uff0c\u9152\u5012\u5f97\u4ed6\u6ee1\u8eab\u6ee1\u8138\uff0c\u4ed6\u4e5f \u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u5b81\u613f\u5c06\u81ea\u5df1\u6df9\u6b7b\u5728\u9152\u91cc\u3002 \u3000\u3000\u201c\u82e5\u6ca1\u6709\u4f24\u5fc3\u7684\u4e8b\uff0c\u4e00\u4e2a\u4eba\u53c8\u600e\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u7740\u4e0d\u662f\u591a\u60c5\u7684\u4eba\uff0c\u53c8\u600e\u4f1a\u6709\u4f24\u5fc3\u7684\u4e8b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u5bf9\u8fd9\u4eba\u5f88\u540c\u60c5\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u72ec\u996e\u6700\u65e0\u8da3\uff0c\u6211\u90a3\u8fb9\u8fd8\u6709\u4e0b\u9152\u7684\u83dc\u4f55\u59a8 \u8fc7\u53bb\u4e00\u8d77\u559d\u51e0\u676f\uff1f\u201d \u3000\u3000\u90a3\u4eba\u53c8\u541e\u4e0b\u513f\u53e3\u9152\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u5927\u9a82\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u4f60\u914d\u8ddf\u6211\u4e00\u8d77\u559d\u9152\uff0c\u5c31\u7b97 \u4f60\u518d\u4e70\u4e09\u767e\u575b\u9152\u9001\u7ed9\u6211\uff0c\u4e5f\u4f11\u60f3\u8981\u6211\u966a\u4f60\u2026\u2026\u201d \u3000\u3000\u9a82\u5230\u8fd9\u91cc\uff0c\u4ed6\u58f0\u97f3\u7a81\u7136\u505c\u4f4f\uff0c\u5c31\u50cf\u7a81\u7136\u88ab\u53ea\u624b\u627c\u4f4f\u4e86\u8116\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f3c\u4e4e\u4e5f\u5df2\u6014\u4f4f\u4e86\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u662f\u4f60\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u5ffd\u7136\u201c\u7830\u201d\u7684\u5c06\u9152\u6454\u5728\u5730\u4e0a\uff0c\u6389\u5934\u5c31\u8dd1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7acb\u523b\u4e5f\u8ffd\u4e86\u8fc7\u53bb\uff0c\u547c\u9053\uff1a\u201c\u7b49\u4e00\u7b49\uff0c\u7b49\u4e00\u7b49\u2026\u2026\u5144\u53f0\u83ab\u975e\u4e0d\u8ba4\u5f97\u5c0f\u5f1f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u8dd1\u5f97\u66f4\u5feb\uff0c\u5927\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u5f97\u4f60\uff0c\u6211\u4e0d\u559d\u4f60\u7684\u9152\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u4e00\u4e2a\u8ffd\uff0c\u4e00\u4e2a\u9003\uff0c\u7728\u773c\u95f4\u90fd\u5df2\u8dd1\u5f97\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u662f\u8c01\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u4f1a\u4ee5\u4e3a\u4ed6\u4eec\u6709\u6bdb\u75c5\u3002 \u3000\u3000\u201c\u90a3\u5077\u9152\u7684\u4eba\u539f\u6765\u662f\u4e2a\u75af\u5b50\uff0c\u660e\u77e5\u8981\u6328\u63cd\u4e5f\u6562\u6765\u5077\u9152\u559d\uff0c\u4f46\u7b49\u5230\u522b\u4eba\u8bf7\u4ed6\u559d\u9152\u65f6\uff0c\u4ed6\u53cd \u800c\u9003\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e70\u9152\u7684\u4eba\u66f4\u75af\uff0c\u65e2\u82b1\u4e86\u94b1\uff0c\u53c8\u6328\u4e86\u9a82\uff0c\u8fd8\u8981\u79f0\u90a3\u4eba\u4e3a\u5144\u53f0\uff0c\u50cf\u8fd9\u79cd\u4eba\u6211\u5012\u771f\u6ca1\u6709\u77a7 \u89c1\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u5f53\u7136\u6ca1\u6709\u77a7\u89c1\u8fc7\uff0c\u56e0\u4e3a\u8fd9\u79cd\u4eba\u4e16\u4e0a\u672c\u5c31\u4e0d\u591a\u3002 \u3000\u3000\u9003\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u89c1\u4e86\u674e\u5bfb\u6b22\u5c31\u9003\uff1f \u3000\u3000\u8fd9\u539f\u56e0\u522b\u4eba\u81ea\u7136\u4e0d\u77e5\u9053\uff0c\u5c31\u8fde\u674e\u5bfb\u6b22\u81ea\u5df1\uff0c\u4e5f\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u8fd9\u79cd\u60c5\u51b5\u4e0b\u9047\u5230\u4ed6 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b2c\u4e00\u6b21\u89c1\u5230\u4ed6\u7684\u65f6\u5019\uff0c\u662f\u5728\u4e00\u6761\u957f\u8857\u4e0a\u7684\u5c4b\u6a90\u4e0b\u3002 \u3000\u3000\u90a3\u6761\u8857\u4e0a\u7684\u4eba\u5f88\u591a\u3002 \u3000\u3000\u4ed6\u7684\u767d\u8863\u5982\u96ea\uff0c\u5728\u4eba\u7fa4\u4e2d\u5c31\u50cf\u662f\u9e21\u7fa4\u4e2d\u7684\u9e64\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u663e\u7136\u4e5f\u4e0d\u5c51\u4e0e\u522b\u4eba\u4e3a\u4f0d\uff0c\u5c31\u7b97\u5c06\u4e16\u4e0a\u6240\u6709\u7684\u9ec4\u91d1\u90e8\u5806\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u4e5f\u4e0d\u5c51\u548c\u90a3\u4e9b \u4ed6\u6240\u770b\u4e0d\u8d77\u7684\u4eba\u8bf4\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u53ea\u4e3a\u4e86\u4e00\u575b\u9152\uff0c\u6d4a\u9152\uff0c\u4ed6\u7adf\u4e0d\u501f\u5fcd\u53d7\u522b\u4eba\u7684\u6c55\u7b11\uff0c\u8fb1\u9a82\uff0c\u97ad\u6253\uff0c\u751a\u81f3\u4e0d\u60dc\u50cf\u732a \u4e00\u6837\u88ab\u6253\u5f97\u6eda\u5728\u6ce5\u6d46\u4e2d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b80\u76f4\u65e0\u6cd5\u76f8\u4fe1\u8fd9\u4f1a\u662f\u540c\u4e00\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u6eda\u5728\u6ce5\u6d46\u4e2d\u7684\u4eba\uff0c\u7684\u786e\u5c31\u662f\u6614\u65e5\u90a3\u9ad8\u9ad8\u5728\u4e0a\u7684\u5415\u51e4\u5148\uff01 \u3000\u3000\u662f\u4ec0\u4e48\u4e8b\u4ee4\u4ed6\u6539\u53d8\u7684\uff1f\u6539\u53d8\u7684\u8fd9\u4e48\u7094\uff0c\u8fd9\u4e48\u5927\uff0c\u8fd9\u4e48\u53ef\u6015\uff01 \u3000\u3000\u706f\u706b\u5df2\u5728\u8fdc\u5904\uff0c\u661f\u5149\u5374\u4eff\u4f5b\u8fd1\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u5415\u51e4\u5148\u7a81\u7136\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u4e0d\u518d\u9003\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u548c\u963f\u98de\u4e00\u6837\uff0c\u9003\u907f\u7684\u53ea\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u8bb8\u6709\u5f88\u591a\u4eba\u90fd\u5f88\u60f3\u9003\u907f\u81ea\u5df1\uff0c\u4f46\u5374\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u9003\u5f97\u4e86\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5df2\u8fdc\u8fdc\u505c\u4e0b\uff0c\u5f2f\u4e0b\u8170\uff0c\u4e0d\u505c\u7684\u54b3\u55fd\u3002\u4ed6\u5df2\u53d1\u89c9\u8fd1\u6765\u54b3\u55fd\u7684\u6b21\u6570\u867d\u7136\u5c11\u4e86\u4e9b\uff0c\u4f46 \u4e00\u54b3\u8d77\u6765\uff0c\u5c31\u5f88\u96be\u505c\u6b62\u3002 \u3000\u3000\u8fd9\u5c82\u975e\u6b63\u5982\u201c\u76f8\u601d\u201d\u4e00\u6837\uff1f \u3000\u3000\u4f60\u5c06\u4e00\u4e2a\u4eba\u601d\u5ff5\u7684\u6b21\u6570\u5c11\u4e86\u4e9b\u65f6\uff0c\u5e76\u4e0d\u8868\u793a\u4f60\u5df2\u5fd8\u4e86\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u8fd9\u76f8\u601d\u5df2\u5165\u9aa8\u3002 \u3000\u3000\u7b49\u4ed6\u54b3\u55fd\u5b8c\u4e86\uff0c\u5415\u51e4\u5148\u624d\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u5c3d\u529b\u60f3\u4f7f\u81ea\u5df1\u663e\u5f97\u9547\u5b9a\u4e9b\uff0c\u5374\u5e76\u6ca1\u6709\u6210\u529f\u3002 \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u6296\u5f97\u50cf\u662f\u4e00\u6761\u521a\u4ece\u51b0\u6cb3\u4e2d\u635e\u8d77\u6765\u7684\u5154\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u56de\u7b54\uff0c\u751f\u6015\u81ea\u5df1\u7684\u56de\u7b54\u4f1a\u4f24\u5bb3\u5230\u4ed6\u3002 \u3000\u3000\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u56de\u7b54\u90fd\u53ef\u80fd\u4f24\u5bb3\u5230\u4ed6\u3002 \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u6211\u672c\u4e0d\u6b20\u4f60\u7684\uff0c\u672c\u4e0d\u5fc5\u4e3a\u4f60\u505a\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4f55\u5fc5\u8fd8\u8981\u6765\u903c\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7ec8\u4e8e\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u6b20\u4f60\u7684\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u6b20\u6211\uff0c\u4e5f\u4e0d\u5fc5\u8fd8\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u6b20\u4f60\u7684\uff0c\u672c\u5c31\u65e0\u6cd5\u8fd8\uff0c\u4f46\u4f60\u81f3\u5c11\u4e5f\u8be5\u8ba9\u6211\u8bf7\u4f60\u559d\u676f\u9152\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u83ab\u5fd8\u4e86\uff0c\u4f60\u4e5f\u8bf7\u8fc7\u6211\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u7684\u624b\u4e00\u76f4\u4e0d\u505c\u7684\u53d1\u6296\uff0c\u6296\u5f97\u8fde\u9152\u676f\u90fd\u62ff\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u4ed6\u7528\u4e24\u53ea\u624b\u6367\u7740\u7897\u559d\u9152\uff0c\u4f46\u9152\u8fd8\u662f\u4e0d\u505c\u7684\u4ece\u7897\u91cc\u6e85\u51fa\u6765\uff0c\u4ece\u4ed6\u5634\u89d2\u91cc\u6d41\u51fa\u6765\uff0c\u6e85\u5f97\u4ed6\u81ea \u5df1\u4e00\u8eab\u4e00\u8138\u3002 \u3000\u3000\u5c31\u5728\u51e0\u5929\u524d\uff0c\u8fd9\u53ea\u624b\u8fd8\u662f\u4ef6\u201c\u6740\u4eba\u7684\u5175\u5668\u201d\uff01 \u3000\u3000\u65e0\u8bba\u662f\u4ec0\u4e48\u4e8b\u4ee4\u4ed6\u6539\u53d8\u7684\uff0c\u8fd9\u4ef6\u4e8b\u5bf9\u4ed6\u7684\u6253\u51fb\u90fd\u592a\u53ef\u6015\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b80\u76f4\u65e0\u6cd5\u60f3\u8c61\u3002 \u3000\u3000\u5415\u51e4\u5148\u53c8\u4f38\u51fa\u624b\uff0c\u53bb\u5012\u9152\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\uff0c\u9152\u58f6\u81ea\u4ed6\u624b\u4e2d\u8dcc\u4e0b\u3002 \u3000\u3000\u4ed6\u7684\u8138\u9aa4\u7136\u626d\u66f2\u4e86\u8d77\u6765\uff0c\u76ef\u7740\u81ea\u5df1\u7684\u8fd9\u53ea\u624b\uff0c\u77ac\u4e5f\u4e0d\u77ac\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u7a81\u7136\u72c2\u543c\u4e00 \u58f0\uff0c\u5c06\u8fd9\u53ea\u624b\u585e\u5165\u81ea\u5df1\u5634\u91cc\u3002 \u3000\u3000\u62fc\u547d\u7684\u585e\uff0c\u62fc\u547d\u7684\u54ac\u3002 \u3000\u3000\u8840\uff0c\u6d41\u8fc7\u4ed6\u5634\u89d2\u7684\u9152\u75d5\u3002 \u3000\u3000\u65e0\u8bba\u4ed6\u505a\u4efb\u4f55\u4e8b\uff0c\u674e\u5bfb\u6b22\u672c\u90fd\u4e0d\u613f\u62e6\u963b\u4ed6\u7684\uff0c\u4f46\u73b0\u5728\u5374\u4e0d\u5f97\u4e0d\u62c9\u4f4f\u4ed6\u7684\u624b\u3002 \u3000\u3000\u5415\u51e4\u5148\u72c2\u543c\uff1a\u201c\u653e\u5f00\u6211\uff0c\u6211\u8981\u54ac\u6389\u5b83\uff0c\u4e00\u53e3\u53e3\u56bc\u788e\uff0c\u4e00\u53e3\u53e3\u541e\u4e0b\u53bb\uff01\u201d \u3000\u3000\u8fd9\u53ea\u624b\u672c\u662f\u4ed6\u6700\u81ea\u50b2\uff0c\u6700\u73cd\u60dc\u7684\uff0c\u4e00\u4e2a\u4eba\u5230\u4e86\u771f\u6b63\u75db\u82e6\u65f6\uff0c\u5c31\u60f3\u5c06\u81ea\u5df1\u6700\u73cd\u60dc\u7684\u4e1c\u897f\uff0c \u5c06\u6bc1\u6389\u81ea\u5df1\u6574\u4e2a\u4eba\u7684\u4e1c\u897f\u90e8\u6bc1\u6389\uff01 \u3000\u3000\u56e0\u4e3a\u4e16\u4e0a\u552f\u4e00\u80fd\u89e3\u9664\u8fd9\u79cd\u75db\u82e6\u7684\u6cd5\u5b50\uff0c\u53ea\u6709\u6bc1\u706d\uff01 \u3000\u3000\u5f7b\u5e95\u7684\u6bc1\u706d\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u82e5\u662f\u522b\u4eba\u505a\u4e86\u5bf9\u4e0d\u8d77\u4f60\u7684\u4e8b\uff0c\u8be5\u6b7b\u7684\u662f\u4ed6\uff0c\u4f60\u53c8\u4f55\u82e6\u6298\u78e8\u81ea\u5df1\uff1f\u201d \u3000\u3000\u5415\u51e4\u5148\u5636\u58f0\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u662f\u6211\uff0c\u6211\u81ea\u5df1\u2026\u2026 \u3000\u3000\u4ed6\u62fc\u547d\u60f3\u6323\u8131\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u81ea\u5df1\u5374\u4ece\u51f3\u5b50\u4e0a\u8dcc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u722c\u8d77\uff0c\u5c31\u8fd9\u6837\u4f0f\u5728\u5730\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u65ad\u65ad\u7eed\u7eed\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u6545\u4e8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8033\u6735\u91cc\u542c\u7740\u7684\u662f\u4ed6\u7684\u6545\u4e8b\uff0c\u773c\u775b\u91cc\u770b\u7740\u7684\u662f\u4ed6\u7684\u4eba\uff0c\u4f46\u5fc3\u91cc\u60f3\u5230\u7684\u5374\u662f\u963f\u98de\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5728\u53d1\u51b7\u3002 \u3000\u3000\u963f\u98de\u662f\u4e0d\u662f\u4e5f\u53d7\u4e86\u8fd9\u79cd\u540c\u6837\u7684\u6253\u51fb\uff1f \u3000\u3000\u963f\u98de\u662f\u4e0d\u662f\u4e5f\u5df2\u53d8\u6210\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u672c\u4e0d\u5fcd\u518d\u5bf9\u5415\u51e4\u5148\u8bf4\u4ec0\u4e48\uff0c\u4f46\u73b0\u5728\u5374\u4e0d\u5f97\u4e0d\u8bf4\u4e86\uff1a\u201c\u4f60\u53c8\u4f55\u5fc5\u8fd8\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u6781\u5ea6\u7684\u60b2\u75db\u540e\uff0c\u5f80\u5f80\u662f\u9ebb\u6728\u3002 \u3000\u3000\u5415\u51e4\u5148\u7684\u4eba\u4f3c\u5df2\u9ebb\u6728\uff0c\u832b\u7136\u9053\uff1a\u201c\u4e0d\u7559\u5728\u8fd9\u91cc\uff0c\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56de\u53bb\uff0c\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u5bb6\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u597d\u50cf\u751f\u4e86\u573a\u5927\u75c5\uff0c\u8fd9\u75c5\u53ea\u6709\u4e24\u79cd\u836f\u80fd\u6cbb\u597d\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u9053\uff1a\u201c\u4e24\u79cd\u836f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u7b2c\u4e00\u79cd\u662f\u5bb6\uff0c\u7b2c\u4e8c\u79cd\u662f\u65f6\u95f4\uff0c\u4f60\u53ea\u8981\u56de\u5bb6\u2026\u2026\u201d \u3000\u3000\u5415\u51e4\u5148\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u56de\u5bb6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u98ce\u5148\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u90a3\u5df2\u4e0d\u662f\u6211\u7684\u5bb6\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5bb6\u5c31\u662f\u5bb6\uff0c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d8\u7684\uff0c\u8fd9\u5c31\u662f\u5bb6\u7684\u53ef\u8d35\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u53c8\u5728\u53d1\u6296\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u6c38\u8fdc\u6ca1\u6709\u53d8\uff0c\u6211\u5374\u5df2\u53d8\u4e86\uff0c\u6211\u5df2\u7ecf\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u82e5\u80af\u5728\u5bb6\u91cc\u5b89\u5b89\u9759\u9759\u7684\u8fc7\u4e00\u6bb5\u65f6\u5019\uff0c\u5c31\u4e00\u5b9a\u4f1a\u53d8\u56de\u539f\u6765\u7684\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u60f3\u63a5\u7740\u8bf4\u4e0b\u53bb\uff0c\u8eab\u540e\u5df1\u6709\u4e00\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u82e5\u662f\u6ca1\u6709\u5bb6\u7684\u4eba\uff0c\u8fd9\u79cd\u75c5\u662f\u4e0d\u662f\u5c31\u6c38\u8fdc\u4e5f \u4e0d\u4f1a\u6cbb\u597d\uff1f\u201d \u7b2c\u4e03\u5341\u7ae0\u3000\u6bd2\u5987\u7684\u5fc3>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u7ae0\u3000\u6bd2\u5987\u7684\u5fc3 \u3000\u3000\u8f7b\u67d4\u7684\u58f0\u97f3\uff0c\u5e26\u7740\u79cd\u8bf1\u4eba\u72af\u7f6a\u7684\u97f5\u5f8b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u6ca1\u6709\u56de\u5934\uff0c\u5415\u51e4\u5148\u5df2\u8df3\u8d77\u6765\uff0c\u75af\u72c2\u822c\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u5c31\u597d\u50cf\u7a81\u7136\u89c1\u5230\u9b3c\u4f3c\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7528\u4e0d\u7740\u56de\u5934\uff0c\u5df2\u77e5\u9053\u8bf4\u6d3b\u7684\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e5f\u660e\u767d\u5979\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u3002 \u3000\u3000\u201c\u963f\u98de\u5c31\u662f\u6ca1\u6709\u5bb6\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\uff0c\u62f3\u5df2\u63e1\u7d27\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4f1a\u6765\uff0c\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u3002\u201d \u3000\u3000\u6765\u7684\u5f53\u7136\u5c31\u662f\u6797\u4ed9\u513f\u3002 \u3000\u3000\u5979\u5728\u7b11\u7740\uff0c\u94f6\u94c3\u822c\u7b11\u7740\u9053\uff1a\u201c\u6211\u7684\u786e\u5f88\u5c11\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u4f46\u6211\u5374\u77e5\u9053\u53ea\u6709\u5728\u8fd9\u91cc\u624d\u80fd\u627e \u5f97\u5230\u4f60\uff0c\u53ea\u8981\u80fd\u627e\u5230\u4f60\uff0c\u4ec0\u4e48\u5730\u65b9\u6211\u90fd\u53bb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u672c\u4e0d\u8be5\u6765\u627e\u6211\uff0c\u56e0\u4e3a\u4f60\u4e5f\u8bb8\u8981\u540e\u6094\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u540e\u6094\uff1f\u6211\u4e3a\u4ec0\u4e48\u8981\u540e\u6094\uff1f\u6211\u4eec\u662f\u8001\u670b\u53cb\u4e86\uff0c\u65e2\u7136\u77e5\u9053\u4f60\u5728\u8fd9\u57ce\u91cc\uff0c\u600e\u4e48 \u80fd\u4e0d\u7b49\u7740\u770b\u4f60\uff1f\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u66f4\u6e29\u67d4\uff0c\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u6211\u4e00\u76f4\u90fd\u5f88\u60f3\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u6211\u77e5\u9053\u4f60\u4e5f\u50cf\u5bf9\u5415\u51e4\u5148\u90a3\u6837\u5bf9\u963f\u98de\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u5f88\u5c11\u8bf4\u5a01\u80c1\u522b\u4eba\u7684\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u7528\u4e0d\u7740\u8bf4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u82e5\u50cf\u7529\u5415\u51e4\u5148\u90a3\u6837\uff0c\u7529\u4e86\u963f\u98de\uff0c\u96be\u9053\u4f60\u5c31\u4f1a\u6740\u6211\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\uff0c\u4f60\u5e94\u8be5\u61c2\u5f97\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4f60\u4e00\u76f4\u90fd\u5728\u529d\u4ed6\u79bb\u5f00\u6211\uff0c\u6211\u82e5\u5148\u79bb\u5f00\u4ed6\uff0c\u5c82\u975e\u6b63\u5982\u4f60\u6240\u613f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e0d\u540c\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u79bb\u5f00\u4ed6\uff0c\u5e76\u6ca1\u6709\u8981\u4f60\u6bc1\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u82e5\u5df2\u6bc1\u4e86\u4ed6\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u970d\u7136\u8f6c\u8eab\uff0c\u76ef\u7740\u5979\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u4f1a\u540e\u6094\u4eca\u5929\u4e3a\u4f55\u8981\u6765\u7684\uff01\u201d \u3000\u3000\u4ed6\u795e\u8272\u770b\u6765\u8fd8\u662f\u5f88\u5e73\u9759\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u6797\u4ed9\u513f\u5374\u5ffd\u7136\u611f\u89c9\u5230\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u538b\u529b\uff0c \u538b\u5f97\u5979\u51e0\u4e4e\u8fde\u7b11\u90fd\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5979\u5f88\u5c11\u6709\u7b11\u4e0d\u51fa\u6765\u7684\u65f6\u5019\u3002 \u3000\u3000\u7b11\uff0c\u672c\u662f\u5979\u6700\u6709\u628a\u63e1\u7684\u4e00\u79cd\u6b66\u5668\uff0c\u5979\u53ea\u6709\u5728\u9762\u5bf9\u7740\u4e0a\u5b98\u91d1\u8679\u7684\u65f6\u5019\uff0c\u624d\u89c9\u5f97\u8fd9\u79cd\u6b66\u5668\u5e76 \u4e0d\u5341\u5206\u6709\u6548\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u5ffd\u7136\u53d1\u89c9\u5728\u674e\u5bfb\u6b22\u9762\u524d\u4e5f\u4e00\u6837\u2014\u2014\u4e00\u4e2a\u4eba\u7684\u4fe1\u5fc3\u82e5\u6d88\u5931\uff0c\u7b11\u5f97\u5c31\u7edd\u4e0d\u4f1a\u50cf\u5e73 \u65f6\u90a3\u4e48\u52a8\u4eba\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u6162\u6162\u7684\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u7edd\u4e0d\u4f1a\u5bf9\u6211\u600e\u4e48\u6837\u7684\uff0c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u6211\u81ea\u5df1\u5374\u6ca1\u6709\u628a\u63e1\uff0c\u6709\u65f6\u6211\u4e5f\u4f1a\u505a\u51fa\u4e00\u4e9b\u4ee4\u4eba\u60f3\u4e0d\u5230\u7684\u4e8b\u6765\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u82e5\u4ee4\u6211\u540e\u6094\u4e86\uff0c\u4f60\u81ea\u5df1\u4e00\u5b9a\u5c31\u8981\u540e\u6094\u5f97\u66f4\u5389\u5bb3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u60f3\u518d\u89c1\u5230\u963f\u98de\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9e33\u7136\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u4f3c\u4e4e\u53c8\u6062\u590d\u4e86\u81ea\u4fe1\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u4e5f\u8bb8\u5c31\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\u80fd\u5e26\u4f60\u53bb\u627e\u4ed6\uff0c\u4e5f\u53ea\u6709 \u6211\u4e00\u4e2a\u4eba\u80fd\u6551\u4ed6\u2026\u2026\u6211\u65e2\u7136\u80fd\u6bc1\u4ed6\uff0c\u5c31\u80fd\u6551\u4ed6\uff01\u201d \u3000\u3000\u76f4\u5230\u8fd9\u65f6\uff0c\u674e\u5bfb\u6b22\u7684\u8138\u8272\u5927\u53d8\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u6b21\u5979\u8bf4\u7684\u5e76\u4e0d\u662f\u5047\u8bdd\u3002 \u3000\u3000\u5979\u8bf4\u8c0e\u7684\u65f6\u5019\u56fa\u7136\u5f88\u53ef\u6015\uff0c\u8bf4\u771f\u8bdd\u7684\u65f6\u5019\u5374\u66f4\u53ef\u6015\uff0c\u56e0\u4e3a\u50cf\u5979\u8fd9\u79cd\u4eba\uff0c\u82e5\u4e0d\u662f\u4e3a\u4e86\u8981\u6c42 \u66f4\u9ad8\u7684\u4ee3\u4ef7\uff0c\u5c31\u7edd\u4e0d\u4f1a\u8bf4\u771f\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u7684\u78e8\u64e6\u7740\u81ea\u5df1\u7684\u624b\u6307\uff0c\u4ed6\u89c9\u5f97\u6307\u5c16\u5df2\u6709\u4e9b\u53d1\u51b7\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u5401\u4e86\u53e3\u6c14 \uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u7684\u662f\u4ec0\u4e48\uff0c\u8bf4\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u8109\u8109\u7684\u77a7\u7740\u4ed6\uff0c\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u60f3\u8981\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u53c8\u7b11\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u60f3\u8981\u7684\u4e1c\u897f\u4e00\u76f4\u5f88\u591a\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u6211\u5374\u53ea\u60f3\u591a\u77a7\u4f60\u51e0 \u773c\u3002\u201d \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u5230\u8fc7\u4f60\u53d1\u6012\uff0c\u6211\u4e00\u76f4\u5728\u60f3\uff0c\u674e\u5bfb\u6b22\u53d1\u6012 \u7684\u65f6\u5019\u4f1a\u662f\u4ec0\u4e48\u6837\u5b50\u5462\uff1f\u73b0\u5728\u6211\u7b97\u771f\u770b\u5230\u4e86\uff0c\u8fd9\u673a\u4f1a\u5f88\u96be\u5f97\uff0c\u6211\u600e\u4e48\u80fd\u8f7b\u6613\u9519\u8fc7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u6162\u6162\u7684\u5750\u4e0b\uff0c\u5c06\u684c\u4e0a\u4e00\u76cf\u6cb9\u706f\u79fb\u5230\u81ea\u5df1\u9762\u524d\uff0c\u7136\u540e\u6162\u6162\u7684\u659f\u4e86\u676f\u9152 \u3002 \u3000\u3000\u5979\u8981\u770b\uff0c\u4ed6\u5c31\u8ba9\u5979\u770b\uff0c\u800c\u4e14\u8fd8\u50cf\u662f\u751f\u6015\u5979\u770b\u5f97\u4e0d\u591f\u6e05\u695a\u3002 \u3000\u3000\u201c\u5973\u4eba\u82e5\u8981\u505a\u4e00\u4ef6\u4e8b\uff0c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u8ba9\u5979\u53bb\u505a\uff0c\u5979\u81ea\u5df1\u5f88\u5feb\u5c31\u4f1a\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u5e76\u4e0d\u5982 \u60f3\u8c61\u4e2d\u90a3\u4e48\u6709\u8da3\u7684\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5973\u4eba\u65e0\u8bba\u5bf9\u4ec0\u4e48\u4e8b\u7684\u5174\u8da3\u90fd\u4e0d\u4f1a\u4fdd\u6301\u5f97\u5f88\u4e45\uff0c\u4f46\u4f60\u82e5\u4e0d\u8ba9\u5979\u53bb\u505a\uff0c\u5979\u7684\u5174\u8da3\u53cd\u800c \u4f1a\u66f4\u6d53\u539a\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u8bb8\u5c31\u662f\u5973\u4eba\u6700\u5927\u7684\u6bdb\u75c5\uff0c\u5343\u767e\u5e74\u524d\u7684\u5973\u4eba\u5c31\u6709\u8fd9\u79cd\u6bdb\u75c5\uff0c\u5343\u767e\u5e74\u540e\u7684\u5973\u4eba\u4e5f\u5fc5\u5c06\u6709 \u8fd9\u79cd\u6bdb\u75c5\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u7537\u4eba\u5bf9\u5973\u4eba\u5df2\u7814\u7a76\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u4f46\u80fd\u4e86\u89e3\u5973\u4eba\u8fd9\u79cd\u6bdb\u75c5\u7684\u7537\u4eba\uff0c\u5374\u504f\u504f\u8fd8\u662f \u4e0d\u592a\u591a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5750\u5728\u90a3\u91cc\uff0c\u6162\u6162\u7684\u559d\u7740\u9152\u3002 \u3000\u3000\u6797\u4ed9\u513f\u76ef\u7740\u4ed6\uff0c\u751c\u7b11\u7740\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e2a\u5999\u4eba\uff0c\u4e0d\u4f46\u8bf4\u7684\u8bdd\u5999\uff0c\u505a\u7684\u4e8b\u5999\uff0c\u559d\u9152\u7684\u6837\u5b50\u4e5f \u5999\uff0c\u6bcf\u6b21\u6211\u770b\u5230\u4f60\u559d\u9152\u7684\u65f6\u5019\uff0c\u90fd\u6068\u4e0d\u5f97\u5c06\u81ea\u5df1\u53d8\u6210\u4f60\u624b\u91cc\u7684\u9152\u676f\uff0c\u6211\u603b\u5ffd\u4e0d\u4f4f\u8981\u60f3\uff0c\u4f60\u5bf9 \u5973\u4eba\u662f\u4e0d\u662f\u4e5f\u50cf\u5bf9\u9152\u676f\u8fd9\u4e48\u6e29\u67d4\u5462\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u542c\u7740\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5bf9\u4ed8\u5973\u4eba\u7684\u6cd5\u5b50\u66f4\u5999\uff0c\u4f60\u597d\u50cf\u603b\u6709\u6cd5\u5b50\u77e5\u9053\u5973\u4eba\u4eec\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48 \uff0c\u4f60\u505a\u7684\u6bcf\u4ef6\u4e8b\u90fd\u6070\u597d\u6b63\u662f\u5979\u4eec\u6700\u559c\u6b22\u7684\u2014\u2014\u6709\u65f6\u4f60\u751a\u81f3\u4ec0\u4e48\u90fd\u4e0d\u505a\uff0c\u4e5f\u81ea\u7136\u4f1a\u6709\u4eba\u6765\u4e0a\u4f60 \u7684\u94a9\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u591a\u5389\u5bb3\u7684\u5973\u4eba\uff0c\u53ea\u8981\u9047\u4e0a\u4f60\uff0c\u5c31\u4f11\u60f3\u9003\u5f97\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u662f\u5728\u542c\u7740\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6bcf\u6b21\u6211\u9047\u7740\u4f60\uff0c\u90fd\u89c9\u5f97\u8ddf\u4f60\u804a\u5929\u5f88\u6709\u8da3\uff0c\u540e\u6765\u4ed4\u7ec6\u60f3\u4e00\u60f3\uff0c\u624d\u53d1\u73b0\u4e0a\u4e86\u4f60 \u7684\u5f53\uff0c\u4f60\u6839\u672c\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u6700\u4f1a\u8bf4\u8bdd\u7684\u4eba\uff0c\u5f80\u5f80\u4e5f\u5c31\u662f\u4e0d\u8bf4\u8bdd\u7684\u4eba\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u8fd9\u9053\u7406\u4e5f\u5f88\u5c11\u6709\u4eba\u660e\u767d\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u4f46\u8fd9\u6b21\u6211\u5374\u4e0d\u518d\u4e0a\u4f60\u7684\u5f53\u4e86\uff0c\u8fd9\u6b21\u6211\u8981\u4f60\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u7b49\u4f60\u770b\u591f\u4e86\uff0c\u6211\u518d\u8bf4\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u770b\u591f\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u8fd8\u60f3\u8981\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u76ef\u7740\u4ed6\uff0c\u5047\u5982\u773c\u775b\u91cc\u4e5f\u6709\u7259\u9f7f\uff0c\u674e\u5bfb\u6b22\u65e9\u5df2\u88ab\u5979\u541e\u4e0b\u4e86\u809a\u3002 \u3000\u3000\u88ab\u4e00\u4e2a\u8fd9\u4e48\u6837\u7684\u5973\u4eba\u8fd9\u6837\u76ef\u7740\uff0c\u867d\u7136\u5f88\u6109\u5feb\uff0c\u5374\u53c8\u5b9e\u5728\u6709\u70b9\u53d7\u4e0d\u4e86\uff0c\u5979\u7b80\u76f4\u662f\u60f3\u8981\u4eba\u53d1 \u75af\u3002 \u3000\u3000\u53ea\u6709\u674e\u5bfb\u6b22\u53d7\u5f97\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5634\u5507\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u90fd\u4e0d\u8981\uff0c\u53ea\u8981\u4f60\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8981\u6211\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u201c\u7528\u4f60\u81ea\u5df1\u6765\u6362\u963f\u98de\uff0c\u8fd9\u4ea4\u6613\u5c82\u975e\u5f88\u516c\u9053\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u516c\u9053\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u516c\u9053\uff0c\u4f60\u8ba4\u4e3a\u4ed6\u73b0\u5728\u5df2\u4e0d\u5c5e\u4e8e\u6211\u4e86\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u65e2\u7136\u5df2\u6bc1\u4e86\u4ed6\u2026\u2026\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u5df2\u6bc1\u4e86\u4ed6\uff0c\u6240\u4ee5\u4ed6\u624d\u6c38\u8fdc\u5c5e\u4e8e\u6211\uff0c\u6211\u82e5\u53bb\u6551\u4ed6\uff0c\u4ed6\u5c31\u4e0d\u662f\u6211\u7684\u4e86 \u3002\u8fd9\u9053\u7406\u4f60\u96be\u9053\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5f53\u7136\u61c2\u3002\u5c31\u56e0\u4e3a\u4ed6\u61c2\uff0c\u6240\u4ee5\u624d\u75db\u82e6\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u82e5\u60f3\u8981\u6211\u653e\u4ed6\u8d70\uff0c\u5c31\u5f97\u7528\u4f60\u81ea\u5df1\u6765\u6362\uff0c\u4f60\u82e5\u4e0d\u7b54\u5e94\uff0c\u5c31\u6c38\u8fdc \u518d\u4e5f\u4f11\u60f3\u89c1\u5f97\u5230\u4ed6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u559d\u5b8c\u4e86\u676f\u4e2d\u9152\uff0c\u6162\u6162\u7684\u8d70\u5230\u5979\u9762\u524d\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u770b\u6765\u6211\u53ea\u6709\u7b54\u5e94\u4f60\u4e86\uff0c\u662f \u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u5f97\u66f4\u5a9a\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u4f60\u7edd\u4e0d\u4f1a\u540e\u6094\u7684\u2026\u2026\u3002\u201d \u3000\u3000\u5979\u58f0\u97f3\u7a81\u7136\u505c\u987f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u5df2\u63b4\u5728\u5979\u8138\u4e0a\uff0c\u6b63\u6b63\u53cd\u53cd\u63b4\u4e86\u5979\u5341\u51e0\u4e2a\u8033\u5149\u3002 \u3000\u3000\u6797\u4ed9\u513f\u975e\u4f46\u6ca1\u6709\u8eb2\u907f\uff0c\u53cd\u800c\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u6251\u5165\u4ed6\u6000\u91cc\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u8981\u6253\uff0c\u5c31\u6253\u5427 \uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\uff0c\u6211\u60c5\u613f\u65e5\u65e5\u591c\u591c\u966a\u4f60\u6253\u3002\u201d \u3000\u3000\u7a81\u542c\u4e00\u4eba\u62cd\u624b\u7b11\u9053\uff1a\u201c\u6253\u5f97\u597d\uff0c\u5979\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4e3a\u4f55\u4e0d\u518d\u6253\uff1f\u201d \u7b2c\u4e03\u5341\u4e00\u7ae0\u3000\u4e92\u6597\u5fc3\u673a>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u4e00\u7ae0\u3000\u4e92\u6597\u5fc3\u673a \u3000\u3000\u644a\u5b50\u4e0a\u6311\u7740\u76cf\u706f\u7b3c\uff0c\u706f\u7b3c\u5df2\u88ab\u6cb9\u70df\u718f\u9ed1\u3002 \u3000\u3000\u706f\u5149\u4e0b\u4fcf\u751f\u751f\u7684\u7ad9\u7740\u4e00\u4e2a\u4eba\uff0c\u5927\u5927\u7684\u773c\u775b\uff0c\u957f\u957f\u7684\u8fab\u5b50\u2014\u2014 \u3000\u3000\u674e\u5bfb\u6b22\u5931\u58f0\u9053\uff1a\u201c\u5b59\u59d1\u5a18\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ae3\u7136\u9053\uff1a\u201c\u6211\u672c\u6765\u6700\u6068\u7537\u4eba\u6253\u5973\u4eba\uff0c\u4f46\u8fd9\u6b21\uff0c\u4f60\u5374\u6253\u5f97\u8ba9\u6211\u5f00\u5fc3\u6781\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4e5f\u5f00\u5fc3\u6781\u4e86\uff0c\u6211\u559c\u6b22\u88ab\u4ed6\u6253\u3002\u201d \u3000\u3000\u5979\u53c8\u52fe\u4f4f\u4e86\u674e\u5bfb\u6b22\u7684\u81c2\uff0c\u5a9a\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u5728\u5403\u918b\uff0c\u4e0d\u59a8\u4e5f\u8fc7\u6765\u559d\u676f\u9152\uff0c\u918b\u53ef\u4ee5\u89e3\u9152\uff0c\u9152 \u4e5f\u53ef\u4ee5\u89e3\u918b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5c45\u7136\u771f\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u7528\u674e\u5bfb\u6b22\u7684\u9152\u676f\u5012\u4e86\u676f\u9152\uff0c\u4e00\u53e3\u5c31\u5e72\u4e86\uff0c\u5410\u4e86\u5410\u820c\u5934\uff0c\u76b1\u7709 \u7b11\u9053\uff1a\u201c\u52a3\u9152\u559d\u591a\u4e86\u867d\u7136\u4e5f\u5c31\u548c\u597d\u9152\u5dee\u4e0d\u591a\uff0c\u4f46\u8fd9\u7b2c\u4e00\u53e3\u53ef\u771f\u96be\u559d\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u7b49\u5b59\u59d1\u5a18\u4e0b\u6b21\u5230\u6211\u4eec\u5bb6\u6765\u7684\u65f6\u5019\uff0c\u6211\u4eec\u4e00\u5b9a\u7528\u6700\u597d\u7684\u9152\u6765\u62db\u5f85\u4f60\uff01\u201d \u3000\u3000\u5979\u4ef0\u7740\u9762\uff0c\u7b11\u95ee\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5b59\u5c0f\u7ea2\u5df2\u62a2\u7740\u9053\uff1a\u201c\u4f60\u7b11\u5f97\u771f\u597d\u770b\uff0c\u6211\u867d\u7136\u662f\u5973\u4eba\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u60f3\u591a \u77a7\u51e0\u773c\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5403\u5403\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u59b9\uff0c\u4f60\u8fd8\u4e0d\u662f\u5973\u4eba\uff0c\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u5b69\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c3d\u7ba1\u591a\u7b11\u7b11\u5427\uff0c\u56e0\u4e3a\u4f60\u9a6c\u4e0a\u5c31\u8981\u7b11\u4e0d\u51fa\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u7edd\u4e0d\u4f1a\u7b54\u5e94\u4f60\u7684\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u80fd\u505a\u5f97\u5230\u7684\u4e8b\uff0c\u6211\u4e5f\u80fd\u505a\u5f97\u5230\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u505a\u5f97\u5230\u4ec0\u4e48\uff1f\u5c0f\u5b69\u5b50\u6bd5\u7adf\u662f\u5c0f\u5b69\u5b50\uff0c\u660e\u660e\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u61c2\uff0c\u5374 \u504f\u504f\u8981\u88c5\u51fa\u5f88\u61c2\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u5979\u5403\u5403\u7684\u7b11\u7740\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u867d\u7136\u53ea\u8981\u662f\u5973\u4eba\u5c31\u80fd\u505a\uff0c\u4f46\u505a\u5f97\u597d\u4e0d\u597d\uff0c\u5206\u522b\u5c31\u5f88\u5927\u4e86\u2026\u2026 \u8fd9\u9053\u7406\u4f60\u4e5f\u61c2\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u8138\u4e5f\u5df2\u6709\u4e9b\u53d1\u7ea2\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u6211\u81f3\u5c11\u4e5f\u80fd\u5e26\u4ed6\u53bb\u627e\u963f\u98de\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u627e\u5f97\u5230\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5f53\u7136\uff0c\u800c\u4e14\u6211\u4e5f\u77e5\u9053\u8981\u600e\u4e48\u6837\u624d\u80fd\u6551\u963f\u98de\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8981\u6551\u4ed6\uff0c\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6740\u4e86\u4f60\uff01\u8981\u6551\u4ed6\uff0c\u53ea\u6709\u6740\u4e86\u4f60\uff01\u8fd9\u4e16\u4e0a\u82e5\u5df2\u6ca1\u6709\u4f60\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u5c31\u7edd\u4e0d\u4f1a\u518d \u6709\u82e6\u607c\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u53c8\u5e72\u4e86\u676f\u9152\uff0c\u5927\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u548c\u963f\u98de\u4e00\u6837\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u5927\u591a\u6570\u5973\u4eba\u8bf4\u7684\u8bdd\u90fd\u9760 \u4e0d\u4f4f\u4e48\uff1f\u4f60\u96be\u9053\u771f\u76f8\u4fe1\u5979\u80fd\u5e26\u4f60\u53bb\u627e\u963f\u98de\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u6709\u8bf4\u8c0e\u7684\u7537\u4eba\uff0c\u4e5f\u6709\u8bda\u5b9e\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4f60\u83ab\u5c06\u5929\u4e0b\u7684\u5973\u4eba\u90fd\u770b\u5f97\u548c\u4f60\u81ea\u5df1\u4e00\u6837\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u95ee\u79f0\uff0c\u963f\u98de\u73b0\u5728\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5df2\u8ddf\u6211\u7237\u7237\u5728\u4e00\u8d77\uff0c\u6211\u7237\u7237\u5df2\u5c06\u4ed6\u4ece\u4e0a\u5b98\u91d1\u8679\u90a3\u91cc\u5e26\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b11\u4e86\uff0c\u779f\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u8bdd\u4f60\u4e5f\u76f8\u4fe1\u4e48\uff1f\u5929\u4e0b\u53c8\u6709\u8c01\u80fd\u4ece\u4e0a\u5b98\u91d1\u8679\u624b\u4e0a \u5c06\u4eba\u6551\u51fa\u6765\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u5c31\u662f\u5979\u7684\u7237\u7237\u5b59\u8001\u5148\u751f\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7684\u7b11\u5bb9\u770b\u6765\u5df2\u53c8\u53d8\u5f97\u6709\u4e9b\u751f\u786c\uff0c\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5012\u4e5f\u60f3\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7528\u4e0d\u7740\uff01\u4ed6\u4e0d\u60f3\u89c1\u4f60\u3002\u201d \u3000\u3000\u5979\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u4f60\u6d3b\u7740\u597d\u50cf\u5df2\u662f\u591a\u4f59\u7684\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u60f3\u6211\u6b7b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u8be5\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u53ef\u662f\u4f60\u60f3\u8fc7\u6ca1\u6709\uff0c\u8981\u8c01\u6765\u6740\u6211\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6ca1\u6709\u4eba\u80fd\u4e0b\u5f97\u4e86\u624b\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u7684\u7537\u4eba\uff0c\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u80fd\u5fcd\u5fc3\u4e0b\u5f97\u4e86\u624b\uff0c\u53ef\u662f\u4ed6\u4e5f\u4e0d\u4f1a \u51fa\u624b\u7684\u3002\u201d \u3000\u3000\u5979\u7528\u773c\u89d2\u8198\u7740\u674e\u5bfb\u6b22\uff0c\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ed6\u82e5\u6740\u4e86\u6211\uff0c\u963f\u98de\u8fd8\u662f\u4e00\u6837\u4f1a\u6068\u4ed6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u83ab\u5fd8\u4e86\uff0c\u6211\u4e0d\u662f\u7537\u4eba\uff0c\u6211\u4e5f\u4e0d\u6015\u963f\u98de\u6068\u6211\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u59b9\u59b9\uff0c\u96be\u9053\u8fd9\u5c31\u7b97\u662f\u6311\u6218\u4e48\uff1f\u96be\u9053\u4f60\u60f3\u8ddf\u6211\u51b3\u6597\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u677f\u7740\u8138\uff0c\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u5979\u4e0d\u8ba9\u6797\u4ed9\u513f\u8bf4\u8bdd\uff0c\u53c8\u9053\uff1a\u201c\u5730\u65b9\u53ef\u4ee5\u7531\u4f60\u9009\uff0c\u65f6\u95f4\u5374\u5f97\u7531\u6211\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u52b2\u5c0f\u7ea2\u9053\uff1a\u201c\u5c31\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u770b\u6765\u51b3\u6597\u5e76\u4e0d\u662f\u7537\u4eba\u7684\u4e13\u5229\uff0c\u5973\u4eba\u6709\u65f6\u4e5f\u4f1a\u51b3\u6597\u7684\u3002 \u3000\u3000\u4f46\u5973\u4eba\u51b3\u6597\u7684\u6cd5\u5b50\u662f\u4e0d\u662f\u4e5f\u548c\u7537\u4eba\u4e00\u6837\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u5df2\u6311\u4e86\u65f6\u95f4\uff0c\u73b0\u5728\u4f60\u5c31\u6311\u4e2a\u5730\u65b9\u5427\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u73e0\u5b50\u8f6c\u52a8\u7740\uff0c\u9053\uff1a\u201c\u5730\u65b9\u4e5f\u4e0d\u5fc5\u6311\u4e86\uff0c\u770b\u6765\u8fd9\u91cc\u5c31\u4e0d\u63aa\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u600e\u6837\uff1f\u201d\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4eec\u7528\u54ea\u79cd\u6cd5\u5b50\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u51b3\u6597\u5c31\u662f\u51b3\u6597\uff0c\u96be\u9053\u8fd8\u6709\u591a\u79cd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u60a0\u7136\u9053\uff1a\u201c\u5f53\u7136\u6709\uff0c\u6709\u7684\u53eb\u6587\u6597\uff0c\u6709\u7684\u53eb\u6b66\u6597\uff0c\u6709\u7684\u6597\u5175\u5668\uff0c\u6709\u7684\u6597\u8f7b\u529f\uff0c\u4e5f\u6709 \u7684\u6597\u6bd2\u836f\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u5230\u5e95\u662f\u5973\u4eba\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u81f3\u5c11\u90fd\u5e94\u8be5\u6bd4\u7537\u4eba\u65af\u6587\u4e9b\u624d\u662f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8bf4\u7528\u54ea\u79cd\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u6cd5\u5b50\u4e5f\u7531\u6211\u6765\u9009\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u9053\uff1a\u201c\u53ef\u80fd\u7528\u6bd2\u836f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u751c\u751c\u5bf9\u4ed6\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7528\u6bd2\u836f\u4e5f\u6ca1\u5173\u7cfb\uff0c\u6211\u4e03\u53d4\u4e5f\u662f\u4f7f\u6bd2\u7684\u5927\u884c\u5bb6\uff0c\u7edd\u4e0d\u5728\u4e94\u6bd2 \u7ae5\u5b50\u4e4b\u4e0b\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4f7f\u6bd2\u662f\u4e3a\u4e86\u8981\u6551\u4eba\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u6740\u4eba\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u82e5\u80fd\u7528\u6bd2\u836f\u6551\u4eba\uff0c\u4ed6\u4f7f\u6bd2\u7684\u672c\u4e8b\u5c31\u5fc5\u5b9a\u5df2\u51fa\u795e\u5165\u5316\uff0c\u56e0\u4e3a\u7528\u6bd2\u836f\u6551\u4eba\uff0c\u7684 \u786e\u6bd4\u7528\u6bd2\u836f\u6740\u4eba\u56f0\u96be\u5f97\u591a\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u5012\u771f\u4e0d\u80fd\u7528\u6bd2\u836f\u6765\u8ddf\u4f60\u51b3\u6597\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6de1\u6de1\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u7528\u4ec0\u4e48\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5979\u770b\u6765\u8fd9\u4e48\u6709\u628a\u63e1\uff0c\u674e\u5bfb\u6b22\u4e5f\u4e0d\u518d\u8bf4\u4ec0\u4e48\u3002\u201c\u5b59\u8001\u5148\u751f\u201d\u5ae1\u4f20\u7684\u6b66\u529f\uff0c\u4ed6\u4e5f\u65e9\u5c31\u60f3\u89c1\u8bd5 \u89c1\u8bd5\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53c8\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5728\u5c0f\u674e\u63a2\u82b1\u8fd9\u6837\u7684\u7edd\u9876\u9ad8\u624b\u9762\u524d\uff0c\u6211\u4eec\u82e5\u662f\u62f3\u6253\u811a\u8e22 \u7684\u6253\u4e86\u8d77\u6765\uff0c\u5c82\u975e\u662f\u5728\u73ed\u95e8\u5f04\u65a7\uff0c\u8981\u4eba\u5bb6\u77a7\u7740\u7b11\u8bdd\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u8bf4\u7528\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4eec\u65e2\u7136\u662f\u5973\u4eba\uff0c\u5c31\u5e94\u8be5\u7528\u5973\u4eba\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5973\u4eba\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u7279\u522b\u7684\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u7537\u4eba\u81ea\u4ee5\u4e3a\u5904\u5904\u90fd\u6bd4\u5973\u4eba\u5f3a\uff0c\u4f46\u6709\u4ef6\u4e8b\u5374\u53ea\u6709\u5973\u4eba\u624d\u80fd\u505a\uff0c\u672c\u4e8b\u518d\u5927\u7684\u7537 \u4eba\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u751f\u5b69\u5b50\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u58f0\u9053\uff1a\u201c\u751f\u5b69\u5b50\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u751f\u5b69\u5b50\u624d\u662f\u5973\u4eba\u4eec\u6700\u5927\u7684\u672c\u4e8b\uff0c\u6700\u5927\u7684\u5149\u8363\uff0c\u4e0d\u80fd\u751f\u5b69\u5b50\u7684\u5973\u4eba \uff0c\u8c01\u90fd\u77a7\u4e0d\u8d77\u7684\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u8138\u53c8\u7ea2\u4e86\uff0c\u5403\u5403\u9053\uff1a\u201c\u4f60\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u53ef\u4ee5\u6bd4\u4e00\u6bd4\u8c01\u7684\u5b69\u5b50\u751f\u5f97\u591a\uff0c\u751f\u5f97\u5feb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u75af\u4e86\uff0c\u8fd9\u79cd\u4e8b\u600e\u4e48\u80fd\u6bd4\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u60a0\u7136\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u80fd\uff0c\u96be\u9053\u4f60\u751f\u4e0d\u51fa\u5b69\u5b50\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6da8\u7ea2\u4e86\u8138\uff0c\u65e2\u4e0d\u80fd\u627f\u8ba4\uff0c\u53c8\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u82e5\u5acc\u8fd9\u79cd\u6cd5\u5b50\u592a\u6162\uff0c\u592a\u8d39\u4e8b\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u6362\u4e00\u79cd\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f53\u7136\u8981\u6362\u4e00\u79cd\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8fd8\u6709\u4e9b\u4e8b\u53ea\u8981\u662f\u7537\u4eba\u5c31\u6562\u505a\uff0c\u4f46\u65e0\u8bba\u591a\u5389\u5bb3\u7684\u5973\u4eba\uff0c\u4f60\u82e5\u8981\u5979\u505a\u8fd9\u4e9b\u4e8b\uff0c \u5979\u4e5f\u6ca1\u8fd9\u4e2a\u80c6\u5b50\u3002\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u613f\u610f\u6bd4\u5973\u4eba\u90fd\u80fd\u505a\u7684\u4e8b\uff0c\u6211\u4eec\u5c31\u6bd4\u4e00\u6bd4\u5973\u4eba\u90fd\u4e0d\u6562\u505a\u7684 \u4e8b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u8fdf\u7591\u7740\uff0c\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u8131\u8863\u670d\u2026\u2026\u6211\u4eec\u5c31\u5728\u8fd9\u91cc\u628a\u8863\u670d\u5168\u8131\u4e0b\u6765\uff0c\u770b\u8c01\u8131\u5f97\u5feb\uff0c\u6211\u82e5\u8f93 \u4e86\u60c5\u613f\u628a\u8111\u888b\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8fd9\u91cc\u672c\u662f\u4e2a\u591c\u5e02\uff0c\u5230\u8fd9\u91cc\u6765\u559d\u9152\u7684\u4eba\uff0c\u867d\u7136\u90fd\u4e0d\u613f\u591a\u7ba1\u522b\u4eba\u7684\u95f2\u4e8b\uff0c\u4f46\u82e5\u6709\u5973\u4eba\u5f53\u573a\u8131 \u8863\u670d\uff0c\u6253\u7834\u5934\u4e5f\u8981\u62a2\u7740\u6765\u77a7\u77a7\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u7ea2\u7740\u8138\u9053\uff1a\u201c\u96be\u602a\u806a\u660e\u7684\u7537\u4eba\u90fd\u4e0d\u613f\u627e\u5973\u4eba\u8d4c\u94b1\u3001\u539f\u6765\u5c31\u56e0\u4e3a\u4f60\u4eec\u8fd9 \u79cd\u5973\u4eba\uff0c\u65e0\u8bba\u8d4c\u4ec0\u4e48\u90fd\u8981\u60f3\u51fa\u6cd5\u5b50\u6765\u8d56\u76ae\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u8ddf\u7537\u4eba\u8d56\u76ae\uff0c\u672c\u6765\u5c31\u662f\u5973\u4eba\u7684\u7279\u6743\uff0c\u4e0d\u61c2\u5f97\u5229\u7528\u8fd9\u79cd\u7279\u6743\u7684\u5973\u4eba\uff0c\u4e0d\u662f \u4e11\u516b\u602a\uff0c\u5c31\u662f\u4e2a\u5446\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u8d56\u76ae\uff0c\u2018\u968f\u4fbf\u4f60\u7528\u4ec0\u4e48\u6cd5\u5b50\u2019\u8fd9\u53e5\u8bdd\u96be\u9053\u4e0d\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff1f\u201d\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6012\u9053\uff1a\u201c\u53ef\u662f\u6211\u53c8\u600e\u77e5\u9053\u4f60\u4f1a\u60f3\u5f97\u51fa\u8fd9\u79cd\u4e0d\u8981\u8138\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u4e5f\u53ea\u80fd\u602a\u4f60\u81ea\u5df1\uff0c\u4f60\u8981\u6740\u6211\uff0c\u4e3a\u4f55\u4e0d\u5e72\u5e72\u8106\u8106\u7684\u52a8\u624b\uff0c\u8c01\u53eb\u4f60\u8fd8\u8981 \u591a\u5634\u7684\uff1f\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\u4e86\uff0c\u8fd9\u4e5f\u96be\u602a\u4f60\uff0c\u4e0d\u591a\u5634\u7684\u5973\u4eba\uff0c\u5230\u73b0\u5728\u6211\u8fd8\u6ca1 \u6709\u770b\u5230\u8fc7\u54e9\u3002\u201d \u3000\u3000\u770b\u6765\u201c\u51b3\u6597\u201d\u7684\u786e\u662f\u7537\u4eba\u7684\u4e13\u5229\u3002 \u3000\u3000\u56e0\u4e3a\u51b3\u6597\u65f6\u53ea\u80fd\u7528\u624b\uff0c\u7edd\u4e0d\u80fd\u7528\u5634\u2014\u2014\u65e0\u8bba\u8c01\u82e5\u8bdd\u8bf4\u5f97\u5927\u591a\u4e86\uff0c\u52c7\u6c14\u548c\u6597\u5fd7\u90fd\u4f1a\u6e10\u6e10\u6d88 \u5931\u7684\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u770b\u5230\u4e24\u4e2a\u4eba\u6253\u67b6\u65f6\u82e5\u5148\u55dc\u54e9\u55dc\u55d3\u5435\u4e86\u8d77\u6765\uff0c\u90a3\u573a\u67b6\u5c31\u4e00\u5b9a\u6253\u4e0d\u8d77\u6765 \u4e86\u3002 \u3000\u3000\u800c\u5973\u4eba\u5374\u504f\u6876\u5927\u591a\u662f\u201c\u541b\u5b50\u201d\uff0c\u90fd\u5f88\u61c2\u5f97\u201c\u52a8\u53e3\u4e0d\u52a8\u624b\u201d\u8fd9\u9053\u7406\u3002 \u3000\u3000\u2014\u2014\u79cb\u98ce\u8083\u6740\uff0c\u5915\u9633\u897f\u4e0b\uff0c\u4e24\u4e2a\u5973\u4eba\u4e00\u8a00\u4e0d\u53d1\u7684\u7ad9\u5728\u79cb\u98ce\u843d\u65f6\u4e2d\uff0c\u7b49\u7740\u90a3\u7acb\u5224\u751f\u6b7b\u7684\u4e00 \u5239\u90a3\u2026\u2026 \u3000\u3000\u8fd9\u79cd\u573a\u9762\u53c8\u6709\u8c01\u77a7\u89c1\u8fc7\uff1f \u3000\u3000\u4e0d\u4f46\u6ca1\u6709\u4eba\u77a7\u89c1\u8fc7\uff0c\u7b80\u76f4\u8fde\u542c\u90fd\u672a\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u201c\u5973\u4eba\u5c31\u662f\u5973\u4eba\u3002\u201d \u3000\u3000\u7537\u5973\u867d\u5e73\u7b49\uff0c\u4f46\u4e16\u4e0a\u5374\u504f\u504f\u6709\u4e9b\u4e8b\u662f\u5973\u4eba\u4e0d\u80fd\u505a\uff0c\u4e5f\u505a\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u5973\u4eba\u82e5\u4e00\u5b9a\u60f3\u505a\u8fd9\u4e9b\u4e8b\uff0c\u4e0d\u662f\u201c\u81ea\u4e0d\u91cf\u529b\u201d\u5c31\u662f\u201d\u81ea\u8ba8\u65e0\u8da3\u3002\u201d \u3000\u3000\u201c\u5973\u4eba\u5c31\u662f\u5973\u4eba\u3002\u201d \u3000\u3000\u8fd9\u9053\u7406\u662f\u8c01\u4e5f\u9a73\u4e0d\u5012\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u5f97\u66f4\u751c\uff0c\u66f4\u5f97\u610f\u4e86\u3002 \u3000\u3000\u770b\u7740\u6797\u4ed9\u513f\u7684\u7b11\u8138\uff0c\u674e\u5bfb\u6b22\u5ffd\u7136\u60f3\u8d77\u4e86\u84dd\u874e\u5b50\u3002 \u3000\u3000\u84dd\u874e\u5b50\u867d\u4e5f\u662f\u4e2a\u58f0\u540d\u72fc\u85c9\u7684\u5973\u4eba\uff0c\u4f46\u5374\u6709\u79cd\u975e\u51e1\u7684\u70c8\u6027\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u84dd\u874e\u5b50\u6b7b\u5f97\u5f88\u53ef\u60dc\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6da8\u7ea2\u7684\u8138\u5df2\u6e10\u6e10\u53d1\u9752\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u73b0\u5728\u51b3\u6597\u7684\u65f6\u95f4\u3001\u5730\u70b9\u3001\u65b9\u6cd5\uff0c\u5df2\u5168\u90e8\u51b3\u5b9a\uff0c\u6597\u4e0d\u6597\u5c31\u5168\u770b\u4f60\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u6597\uff0c\u6211\u53ef\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u4e5f\u53ea\u602a\u4f60\u8fd0\u6c14\u4e0d\u597d\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u62bf\u5634\u7b11\u9053\uff1a\u201c\u662f\u4f60\u8fd0\u6c14\u4e0d\u597d\uff1f\u8fd8\u662f\u6211\u8fd0\u6c14\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6211\u8fd0\u6c14\u54ea\u70b9\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u5634\u4e0a\u8bf4\u5f97\u867d\u51f6\uff0c\u4f46\u82e5\u771f\u7684\u52a8\u8d77\u624b\u6765\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u771f\u8981\u4f60\u7684\u547d\uff0c\u6700\u591a\u4e5f\u53ea\u4e0d \u8fc7\u8981\u4f60\u53d7\u70b9\u4f24\uff0c\u53eb\u4f60\u4ee5\u540e\u5bb3\u4e0d\u4e86\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u7684\u8fd0\u6c14\u5c82\u975e\u597d\u6781\u4e86\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u82e5\u5df2\u4f24\u4e86\u4f60\uff0c\u522b\u4eba\u518d\u8981\u6765\u6740\u4f60\uff0c\u6211\u4e00\u5b9a\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u52a8\u624b\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u63a5\u7740\u9053\uff1a\u201c\u4f46\u73b0\u5728\uff0c\u82e5\u6709\u4eba\u8981\u6765\u6740\u4f60\uff0c\u6211\u5c31\u4e0d\u7ba1\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u6797\u4ed9\u513f\u7684\u8eab\u5b50\u5df2\u6253\u4e86\u4e2a\u8f6c\u3002 \u3000\u3000\u5bf9\u67d0\u4e9b\u4e8b\u6797\u4ed9\u513f\u7684\u53cd\u5e94\u7edd\u4e0d\u6bd4\u674e\u5bfb\u6b22\u548c\u963f\u98de\u6162\u3002 \u3000\u3000\u5979\u76ee\u5149\u968f\u7740\u8eab\u5b50\u7684\u8f6c\u52a8\u56db\u9762\u641c\u7d22\uff0c\u5411\u6700\u9ed1\u6697\u7684\u5730\u65b9\u641c\u7d22\u3002 \u3000\u3000\u5979\u5e76\u6ca1\u6709\u77a7\u89c1\u4ec0\u4e48\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5df2\u62c9\u8d77\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff0c\u6211\u4e0d\u559c\u6b22\u770b\u6740\u4eba\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6709\u4eba\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4eba\u5728\u54ea\u91cc\uff0c\u4f60\u77a7\u89c1\u4e86\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u65e2\u4e0d\u627f\u8ba4\uff0c\u4e5f\u4e0d\u5426\u8ba4\u3002 \u3000\u3000\u5979\u65e0\u8bba\u662f\u627f\u8ba4\uff0c\u8fd8\u662f\u5426\u8ba4\uff0c\u90fd\u4e0d\u4f1a\u4ee4\u6797\u4ed9\u513f\u5bb3\u6015\u7684\u3002 \u3000\u3000\u4f46\u6797\u4ed9\u513f\u73b0\u5728\u5374\u663e\u7136\u6709\u70b9\u5bb3\u6015\u4e86\uff0c\u6151\u61e6\u7740\u9053\uff1a\u201c\u6211\u600e\u4e48\u77a7\u4e0d\u89c1\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u7136\u77a7\u4e0d\u89c1\uff0c\u4f60\u82e5\u77a7\u89c1\u65f6\uff0c\u4e5f\u8bb8\u5c31\u592a\u8fdf\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u82e5\u770b\u4e0d\u5230\uff0c\u4f60\u600e\u4e48\u80fd\u770b\u5230\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u8981\u6740\u7684\u5e76\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u5979\u53c8\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u82e5\u8981\u6740\u4f60\uff0c\u6700\u597d\u83ab\u8981\u88ab\u4f60\u770b\u5230\uff0c\u56e0\u4e3a\u82e5\u662f\u5148\u88ab \u4f60\u770b\u5230\uff0c\u4e5f\u8bb8\u5c31\u6740\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u8c01\u8981\u6740\u4f60\uff1f\u4f60\u81ea\u5df1\u672c\u8be5\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u76ee\u5149\u8fd8\u662f\u56db\u4e0b\u641c\u7d22\u7740\uff0c\u76ee\u4e2d\u5df2\u6709\u4e86\u60ca\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u5979\u4e00\u5411\u5f88\u5c11\u5bb3\u6015\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u603b\u6709\u628a\u63e1\u80fd\u4ee4\u90a3\u4e9b\u8981\u6740\u5979\u7684\u4eba\u4e0b\u4e0d\u4e86\u624b\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u6839\u672c\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u5bf9\u65b9\u6839\u672c\u4e0d\u8ba9\u5979\u770b\u5230\uff0c\u5979\u5c31\u7b97\u6709\u4e00\u4e07\u79cd\u6cd5\u5b50\uff0c\u4e5f \u7528\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u96be\u9053\u8fde\u4f60\u81ea\u5df1\u90fd\u60f3\u4e0d\u51fa\u662f\u8c01\u8981\u6740\u4f60\uff1f\u662f\u4e0d\u662f\u4f60\u81ea\u5df1\u4e5f\u77e5\u9053\u8981\u6740\u4f60\u7684\u4eba\u5927\u591a \u4e86\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u60c5\u4e0d\u81ea\u7981\u64e6\u4e86\u64e6\u6c57\u3002 \u3000\u3000\u5979\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u59ff\u6001\u5c31\u4e00\u5411\u5f88\u4f18\u7f8e\u3001\u5f88\u52a8\u4eba\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5979\u8fd9\u64e6\u6c57\u7684\u52a8\u4f5c\u770b\u6765\u7adf\u6709\u4e9b\u7b28\u62d9\uff1a \u3000\u3000\u6240\u4ee5\u4f60\u82e5\u60f3\u51fb\u5012\u4e00\u4e2a\u4eba\uff0c\u6700\u597d\u7684\u65b9\u6cd5\uff0c\u5c31\u662f\u8ba9\u4ed6\u81ea\u5df1\u5fc3\u91cc\u5148\u89c9\u5f97\u6050\u60e7\uff0c\u90a3\u4e48\u7528\u4e0d\u7740\u4f60\u51fa \u624b\uff0c\u4ed6\u81ea\u5df1\u5c31\u5148\u5df2\u5c06\u81ea\u5df1\u51fb\u5012\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u7740\u5b59\u5c0f\u7ea2\uff0c\u5fc3\u91cc\u5fcd\u4e0d\u4f4f\u5728\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u5b59\u5c0f\u7ea2\u5df2\u4e0d\u518d\u662f\u5b69\u5b50\uff0c\u65e0\u8bba\u4ece\u54ea\u65b9\u9762\u770b\uff0c\u5979\u90fd\u5df2\u662f\u4e2a\u5b8c\u5168\u6210\u719f\u7684\u5973\u4eba\u3002 \u3000\u3000\u53ea\u6709\u6210\u719f\u7684\u5973\u4eba\uff0c\u624d\u4e86\u89e3\u6210\u719f\u7684\u5973\u4eba\u3002 \u7b2c\u4e03\u5341\u4e8c\u7ae0\u3000\u4eba\u6027\u65e0\u5584\u6076>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u4e8c\u7ae0\u3000\u4eba\u6027\u65e0\u5584\u6076 \u3000\u3000\u6797\u4ed9\u513f\u548c\u5b59\u5c0f\u7ea2\u7684\u8fd9\u4e00\u6b21\u51b3\u6597\u867d\u672a\u771f\u7684\u4ea4\u624b\uff0c\u5374\u65e0\u5f02\u5df2\u4ea4\u624b\uff0c\u800c\u4e14\u5df2\u4ea4\u624b\u4e86\u4e24\u6b21\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u5979\u4eec\u6597\u7684\u4e0d\u662f\u529b\uff0c\u800c\u662f\u5fc3\u3002 \u3000\u3000\u7b2c\u4e00\u6b21\u6797\u4ed9\u513f\u80dc\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5f88\u4e86\u89e3\u5973\u4eba\u5fc3\u7406\u7684\u5f31\u70b9\uff0c\u800c\u4e14\u61c2\u5f97\u5982\u4f55\u5229\u7528\u5b83\u3002 \u3000\u3000\u7b2c\u4e8c\u6b21\uff0c\u80dc\u7684\u5374\u662f\u5b59\u5c0f\u7ea2\u3002 \u3000\u3000\u5979\u7528\u7684\u4e5f\u662f\u540c\u6837\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u5979\u77e5\u9053\u5973\u4eba\u5bf9\u4ec0\u4e48\u90fd\u8981\u6000\u7591\u3002 \u3000\u3000\u56e0\u4e3a\u6000\u7591\uff0c\u624d\u6709\u754f\u4ff1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u82e5\u662f\u7537\u4eba\uff0c\u4e5f\u8bb8\u65e9\u5df2\u6740\u4e86\u6797\u4ed9\u513f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u82e5\u662f\u7537\u4eba\uff0c\u65e0\u8bba\u5b59\u5c0f\u7ea2\u8bf4\u4ec0\u4e48\uff0c\u5979\u4e5f\u65e9\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u5979\u4eec\u90fd\u662f\u5973\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u9020\u6210\u8fd9\u79cd\u5947\u7279\u7684\u5c40\u9762\u3002 \u3000\u3000\u2014\u2014\u82e5\u8981\u7537\u4eba\u548c\u5973\u4eba\u53bb\u505a\u540c\u4e00\u6837\u4e8b\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u8fc7\u7a0b\u65e2\u4e0d\u4f1a\u76f8\u540c\uff0c\u7ed3\u679c\u66f4\u4e0d\u4f1a\u4e00\u6837\u3002 \u3000\u3000\u201c\u51b3\u6597\u201d\u4e5f\u662f\u5982\u6b64\u3002 \u3000\u3000\u5973\u4eba\u51b3\u6597\u5f53\u7136\u4e0d\u4f1a\u6709\u7537\u4eba\u90a3\u4e48\u6c89\u91cd\u3001\u7d27\u5f20\u3001\u6fc0\u70c8\uff0c\u4f46\u4e5f\u8bb8\u5374\u66f4\u5fae\u5999\uff0c\u66f4\u590d\u6742\uff0c\u66f4\u6709\u8da3\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u5176\u4e2d\u7684\u53d8\u5316\u5fc5\u5b9a\u591a\u4e9b\u3002 \u3000\u3000\u5979\u4eec\u7684\u53d8\u5316\uff0c\u5e76\u4e0d\u7206\u6b66\u529f\u62db\u5f0f\u7684\u53d8\u5316\u90a3\u6837\uff0c\u4eba\u4eba\u90fd\u80fd\u770b\u89c1\u3002\u4e5f\u8fdc\u6bd4\u6b66\u529f\u62db\u5f0f\u7684\u53d8\u5316\u66f4\u590d \u6742\u3001\u66f4\u5feb\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u5979\u4eec\u7684\u53d8\u5316\u662f\u773c\u775b\u770b\u4e0d\u89c1\u7684\u3002 \u3000\u3000\u82e5\u6709\u4eba\u80fd\u770b\u5230\u5973\u4eba\u5fc3\u7406\u590d\u6742\u5fae\u5999\u7684\u53d8\u5316\uff0c\u4e00\u5b9a\u5c31\u4f1a\u89c9\u5f97\u5973\u4eba\u7684\u51b3\u6597\u6bd4\u4e16\u4e0a\u6240\u6709\u7537\u4eba\u7684\u51b3 \u6597\u90fd\u66f4\u7cbe\u91c7\uff0c\u66f4\u522b\u81f4\u3002 \u3000\u3000\u5973\u4eba\u5c31\u662f\u5973\u4eba\uff0c\u6c38\u8fdc\u548c\u7537\u4eba\u4e0d\u540c\u3002 \u3000\u3000\u8c01\u82e5\u60f3\u53cd\u9a73\u8fd9\u9053\u7406\uff0c\u8c01\u5c31\u662f\u5446\u5b50\u3002 \u3000\u3000\u8fd9\u9053\u7406\u65e2\u660e\u767d\uff0c\u53c8\u7b80\u5355\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4e16\u4e0a\u5374\u504f\u504f\u6709\u4e9b\u4eba\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u62c9\u7740\u674e\u5bfb\u6b22\u5728\u524d\u9762\u8d70\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5c45\u7136\u5728\u540e\u9762\u8ddf\u7740\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4eec\u8d70\u6211\u4eec\u7684\uff0c\u4f60\u8d70\u4f60\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8ddf\u6765\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e5f\u60f3\u53bb\u770b\u770b\u963f\u98de\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u770b\u4ed6\u5e72\u4ec0\u4e48\uff1f\u96be\u9053\u4f60\u5bb3\u4ed6\u5bb3\u5f97\u8fd8\u4e0d\u591f\u60e8\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u53ea\u60f3\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u4f1a\u8ba9\u4ed6\u770b\u89c1\u4f60\u7684\uff0c\u4f60\u53bb\u4e86\uff0c\u4e5f\u662f\u767d\u53bb\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u53ea\u60f3\u8fdc\u8fdc\u770b\u4ed6\u4e00\u773c\uff0c\u4ed6\u8981\u4e0d\u8981\u770b\u6211\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u51b7\u51b7\u9053\uff1a\u201c\u817f\u957f\u5728\u4f60\u81ea\u5df1\u8eab\u4e0a\uff0c\u4f60\u4e00\u5b9a\u8ddf\u7740\u6765\uff0c\u6211\u4eec\u4e5f\u6ca1\u6cd5\u5b50\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u4f60\u65e2 \u7136\u8ddf\u7740\u6765\u4e86\uff0c\u5c31\u83ab\u8981\u540e\u6094\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u505a\u4e8b\u4ece\u4e0d\u540e\u6094\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u65e9\u5c31\u7b97\u51c6\u5979\u4f1a\u8ddf\u7740\u6765\u7684\uff0c\u679c\u7136\u6ca1\u6709\u7b97\u9519\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u662f\u5411\u674e\u5bfb\u6b22\u8bf4\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u8981\u5979\u8ddf\u6765\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5f53\u7136\u8981\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u521a\u624d\u65e2\u7136\u5df2\u6ca1\u6cd5\u5b50\u518d\u5bf9\u5979\u4e0b\u624b\uff0c\u5c31\u53ea\u597d\u7b49\u4e0b\u4e00\u6b21\u673a\u4f1a\uff0c\u5979\u82e5\u4e0d\u8ddf\u7740\u6211\u4eec \u6765\uff0c\u6211\u54ea\u6709\u673a\u4f1a\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u60a0\u7136\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u6839\u672c\u4e0d\u5fc5\u7b49\uff0c\u521a\u624d\u4e5f\u53ef\u4ee5\u4e0b\u624b\uff0c\u65e0\u8bba\u5979\u8bf4\u4ec0\u4e48\uff0c\u4f60\u90fd\u53ef\u4ee5\u4e0d\u542c \u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u4eec\u7537\u5b50\u6c49\u8bb2\u7a76\u7684\u662f\u2018\u8bdd\u51fa\u5982\u98ce\uff0c\u4e00\u8bfa\u5343\u91d1\u2019\uff0c\u96be\u9053\u6211\u4eec\u5973\u4eba\u5c31\u53ef\u4ee5\u8bf4\u4e86 \u8bdd\u5f53\u653e\u5c41\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u600e\u77e5\u5979\u4f1a\u8ddf\u7740\u6765\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u60f3\u8981\u6211\u4eec\u4fdd\u62a4\u5979\uff0c\u5979\u8ddf\u2018\u5c0f\u674e\u63a2\u82b1\u2019\u5728\u4e00\u8d77\u65f6\uff0c\u65e0\u8bba\u8c01\u60f3\u6740\u5979\uff0c\u4e5f \u6ca1\u8fd9\u4e2a\u80c6\u5b50\u4e0b\u624b\u7684\u3002\u201d \u3000\u3000\u5979\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u597d\u542c\u4e9b\uff0c\u8fd9\u5c31\u53eb\u505a\u72d0\u5047\u864e\u5a01\uff0c\u8bf4\u5f97\u96be\u542c\u4e9b\uff0c\u8fd9\u5c31\u53eb\u505a\u72d7\u4ed7\u4eba\u52bf\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5931\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u79cd\u8bf4\u6cd5\u597d\u50cf\u90fd\u4e0d\u5927\u597d\u542c\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u82e5\u662f\u505a\u4e86\u8fd9\u4e9b\u4e8b\uff0c\u65e0\u8bba\u522b\u4eba\u8bdd\u8bf4\u5f97\u591a\u96be\u542c\uff0c\u4e5f\u53ea\u597d\u542c\u542c\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\u6797\u4ed9\u513f\u5f53\u7136\u5168\u90e8\u542c\u5f97\u89c1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u672c\u5c31\u662f\u6545\u610f\u8bf4\u7ed9\u5979\u542c\u7684\u3002 \u3000\u3000\u4f46\u6797\u4ed9\u513f\u5374\u88c5\u5f97\u597d\u50cf\u4ec0\u4e48\u90fd\u6ca1\u6709\u542c\u5230\u4f3c\u7684\uff0c\u4e5f\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u5979\u8fd9\u4eba\u5c31\u4eff\u4f5b\u7a81\u7136\u53d8\u5f97\u53c8\u804b\u53c8\u54d1\u3002 \u3000\u3000\u80fd\u88c5\u804b\u4f5c\u54d1\uff0c\u7684\u786e\u662f\u79cd\u5f88\u4e86\u4e0d\u8d77\u7684\u672c\u4e8b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u6539\u53d8\u4e86\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u9f99\u5578\u4e91\u8981\u8ddf\u4e0a\u5b98\u91d1\u8679\u7ed3\u62dc\u7684\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u542c\u8bf4\u8fc7\u2026\u2026\u4f60\u4eec\u5c31\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u6765\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u55ef\uff0c\u56e0\u4e3a\u6211\u4eec\u77e5\u9053\u5728\u8fd9\u91cc\u4e00\u5b9a\u53ef\u4ee5\u9047\u5230\u5f88\u591a\u4eba\u3002\u201d \u3000\u3000\u5979\u8198\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u62bf\u7740\u5634\u7b11\u9053\uff1a\u201c\u6700\u4e3b\u8981\u7684\uff0c\u5f53\u7136\u8fd8\u662f\u56e0\u4e3a\u6211\u77e5\u9053\u53ef\u4ee5\u5728\u8fd9\u91cc\u9047\u89c1\u4f60 \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5728\u77a7\u7740\u5979\uff0c\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u5f88\u6e29\u6696\uff0c\u5c31\u597d\u50cf\u559d\u4e86\u676f\u9187\u9152\u3002 \u3000\u3000\u4ed6\u5df2\u5f88\u4e45\u6ca1\u6709\u611f\u89c9\u5230\u8fd9\u79cd\u6ecb\u6627\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u88ab\u4ed6\u77a7\u7740\uff0c\u6574\u4e2a\u4eba\u90fd\u50cf\u662f\u5728\u6625\u98ce\u91cc\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u674e\u5bfb\u6b22\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u4eec\u6765\uff0c\u8bf4\u4e0d\u5b9a\u6211\u5df2\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u62a2\u7740\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4e0a\u5b98\u91d1\u8679\u5df2\u8fdb\u4e86\u68fa\u6750\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u4e00\u7b11\uff0c\u6ca1\u6709\u518d\u63a5\u7740\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u548c\u4e0a\u5b98\u91d1\u8679\u867d\u7136\u8fdf\u65e9\u96be\u514d\u8981\u4e00\u51b3\u751f\u6b7b\uff0c\u4f46\u4ed6\u5374\u4e0d\u613f\u8c08\u5230\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u5bf9\u8fd9\u4ef6\u4e8b\u60f3\u5f97\u592a\u591a\uff0c\u56e0\u4e3a\u60f3\u5f97\u592a\u591a\uff0c\u5c31\u6709\u7275\u6302\uff0c\u6709\u4e86\u7275\u6302\uff0c\u5fc3\u5c31\u4f1a\u4e71\uff0c\u5fc3\u82e5\u4e71\u4e86 \uff0c\u4ed6\u6218\u80dc\u7684\u673a\u4f1a\u5c31\u66f4\u5c11\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5176\u5b9e\u5bf9\u4e0a\u5b98\u91d1\u8679\u90a3\u79cd\u4eba\uff0c\u4f60\u672c\u4e0d\u5fc5\u8bb2\u9053\u4e49\uff0c\u4f60\u82e5\u5728\u4ed6\u770b\u5230\u4e0a\u5b98\u98de\u5c38\u4f53\u7684\u65f6 \u5019\u51fa\u624b\uff0c\u4e00\u5b9a\u53ef\u4ee5\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u672a\u5fc5\uff1f\u4f60\u8ba4\u4e3a\u4ed6\u770b\u5230\u4ed6\u81ea\u5df1\u513f\u5b50\u6b7b\u4e86\uff0c\u5fc3\u4e5f\u4e0d\u4f1a\u4e71\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8840\u6d53\u4e8e\u6c34\uff0c\u4e0a\u5b98\u91d1\u8679\u591a\u5c11\u4e5f\u6709\u70b9\u4eba\u6027\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4f55\u4e0d\u51fa\u624b\uff1f\u4f60\u8981\u77e5\u9053\uff0c\u4f60\u5bf9\u4ed6\u8bb2\u4ea4\u60c5\uff0c\u4ed6\u53ef\u4e0d\u4f1a\u5bf9\u4f60\u8bb2\u4ea4\u60c5\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u548c\u4ed6\u73b0\u5728\u5df2\u52bf\u4e0d\u4e24\u7acb\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u5bf9\u8c01\u8bb2\u4ea4\u60c5\u3002\u201d \u3000\u3000\u52b2\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u51fa\u624b\uff0c\u53ea\u56e0\u4e3a\u6211\u8fd8\u8981\u7b49\u66f4\u597d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5728\u6211\u770b\u6765\uff0c\u90a3\u65f6\u5df2\u7ecf\u662f\u6700\u597d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u770b\u9519\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u770b\u5230\u81ea\u5df1\u7684\u513f\u5b50\u6b7b\u4e86\uff0c\u5fc3\u867d\u7136\u4f1a\u4e71\uff0c\u4f46\u5fc3\u91cc\u5374\u4f1a\u751f\u51fa\u79cd\u60b2\u6124\u4e4b\u6c14\uff0c\u90a3\u65f6\u6211 \u82e5\u51fa\u624b\uff0c\u4ed6\u5c31\u4f1a\u5c06\u8fd9\u80a1\u6012\u6c14\u53d1\u6cc4\u5728\u6211\u8eab\u4e0a\uff01\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\uff0c\u63a5\u9053\uff1a\u201c\u4eba\u5728\u60b2\u6124\u4e2d\uff0c\u4e0d\u4f46\u529b\u91cf\u8981\u6bd4\u5e73\u65f6\u5927\u5f97\u591a\uff0c\u52c7\u6c14\u4e5f\u8981\u5e73\u65f6\u5927\u5f97\u591a\uff0c\u90a3 \u65f6\u4e0a\u5b98\u91d1\u8679\u82e5\u51fa\u624b\uff0c\u4e00\u51fb\u4e4b\u5a01\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\u80fd\u63a5\u5f97\u4f4f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u77a7\u7740\u4ed6\u7b11\u4e86\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u4e0d\u662f\u6211\u60f3\u8c61\u4e2d\u90a3\u4e48\u597d\u7684\u4eba\uff0c\u6709\u65f6\u4f60\u4e5f\u4f1a\u7528\u5fc3\u673a \u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u82e5\u771f\u50cf\u522b\u4eba\u60f3\u5f97\u90a3\u4e48\u597d\uff0c\u81f3\u5c11\u5df2\u6b7b\u4e86\u516b\u5341\u6b21\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u82e5\u77e5\u9053\u4f60\u7684\u610f\u601d\uff0c\u4e00\u5b9a\u4f1a\u540e\u6094\u559d\u90a3\u676f\u9152\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u7edd\u4e0d\u540e\u6094\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u7684\u610f\u601d\u4ed6\u672c\u5c31\u5f88\u660e\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u8981\u656c\u4f60\u9152\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u656c\u6211\u90a3\u676f\u9152\uff0c\u4e3a\u7684\u5e76\u4e0d\u662f\u6211\u5bf9\u4ed6\u8bb2\u9053\u4e49\u2014\u2014\u8bb2\u9053\u4e49\u7684\u4eba\u5728\u4ed6\u773c\u4e2d\u770b\u6765\uff0c \u7b80\u76f4\u662f\u5446\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u660e\u4e86\u6211\u7684\u610f\u601d\uff0c\u77e5\u9053\u6211\u5e76\u4e0d\u662f\u5446\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u4f60\u4e5f\u548c\u4ed6\u4e00\u6837\uff0c\u80fd\u7b49\uff0c\u80fd\u5fcd\uff0c\u80fd\u628a\u63e1\u673a\u4f1a\uff0c\u4e5f\u80fd\u5224\u65ad\u4ec0\u4e48 \u65f6\u5019\u624d\u662f\u6700\u597d\u7684\u673a\u4f1a\uff0c\u6240\u4ee5\u624d\u656c\u4f60\u7684\u9152\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u89c9\u5f97\u4f60\u4e5f\u548c\u4ed6\u662f\u540c\u6837\u7684\u4eba\uff0c\u6240\u4ee5\u624d\u4f69\u670d\u4f60\uff0c\u6b23\u8d4f\u4f60\u2014\u2014\u4e00\u4e2a\u4eba\u6700\u6b23\u8d4f\u7684 \u4eba\uff0c\u672c\u5c31\u5fc5\u5b9a\u662f\u548c\u4ed6\u81ea\u5df1\u540c\u6837\u7684\u4eba\u3002\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u4e00\u5b9a\u5f88\u6b23\u8d4f\u81ea\u5df1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5f88\u597d\uff0c\u7b80\u76f4\u672c\u50cf\u662f\u8fd9\u79cd\u5e74\u7eaa\u7684\u4eba\u80fd\u8bf4\u5f97\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6487\u4e86\u6487\u5634\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u771f\u7684\u548c\u4ed6\u662f\u540c\u6837\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5728\u67d0\u4e9b\u65b9\u9762\u8bf4\uff0c\u662f\u7684\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u4eec\u751f\u957f\u7684\u73af\u5883\u4e0d\u540c\uff0c\u9047 \u7740\u7684\u4eba\u548c\u4e8b\u4e5f\u4e0d\u540c\uff0c\u6240\u4ee5\u624d\u4f1a\u9020\u6210\u5b8c\u5168\u4e0d\u540c\u7684\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u63a5\u9053\uff1a\u201c\u6709\u4eba\u8bf4\uff1a\u4eba\u6027\u672c\u5584\uff0c\u4e5f\u6709\u4eba\u8bf4\uff0c\u4eba\u6027\u672c\u6076\uff0c\u5728\u6211\u770b\u6765\uff0c\u4eba\u6027\u672c\u65e0\u5584\u6076\uff0c \u4e00\u4e2a\u4eba\u662f\u5584\u662f\u6076\uff0c\u90fd\u662f\u540e\u5929\u7684\u5f71\u54cd\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u6ce8\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u4e0d\u4f46\u5f88\u4e86\u89e3\u522b\u4eba\uff0c\u4e5f\u5f88\u4e86\u89e3\u81ea\u5df1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u8981\u771f\u7684\u5b8c\u5168\u4e86\u89e3\u81ea\u5df1\uff0c\u5e76\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u4ed6\u795e\u8272\u53c8\u9eef\u6de1\u4e86\u4e0b\u6765\uff0c\u76ee\u4e2d\u53c8\u9732\u51fa\u4e86\u75db\u82e6\u548c\u5fe7\u8651\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u8981\u4e86\u89e3\u81ea\u5df1\uff0c\u5fc5\u5b9a\u8981\u5148\u7ecf\u8fc7\u5f88\u591a\u6298\u78e8\uff0c\u5c1d\u8fc7 \u5f88\u591a\u75db\u82e6\u2014\u2014\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5012\u5e0c\u671b\u6c38\u8fdc\u4e0d\u8981\u4e86\u89e3\u81ea\u5df1\u4e86\uff0c\u4e86\u89e3\u5f97\u8d8a\u591a\uff0c\u75db\u82e6\u8d8a\u591a\uff0c\u5b8c \u5168\u4e0d\u4e86\u89e3\uff0c\u4e5f\u8bb8\u53cd\u5012\u5e78\u8fd0\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u6b21\u662f\u674e\u5bfb\u6b22\u6539\u53d8\u4e86\u8bdd\u9898\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u656c\u6211\u9152\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u8fd8\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4eec\u5df2\u7ecf\u8d70\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u90fd\u662f\u6211\u4ee5\u540e\u542c\u4eba\u8bf4\u7684\u3002\u201d \u3000\u3000\u5979\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u548c\u4e0a\u5b98\u91d1\u8679\u90fd\u662f\u4e86\u4e0d\u8d77\u7684\u5927\u4eba\u7269\uff0c\u4f60\u4eec\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u5728\u522b\u4eba\u770b\u6765 \u90fd\u662f\u5927\u6d88\u606f\uff0c\u4eca\u5929\u665a\u4e0a\uff0c\u5728\u8fd9\u57ce\u91cc\uff0c\u81f3\u5c11\u4e5f\u6709\u5341\u4e07\u4e2a\u4eba\u5728\u8c08\u8bba\u4f60\u2026\u2026\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u4f69\u670d\u4f60\u7237\u7237\uff0c\u8eab\u82e5\u6d6e\u4e91\uff0c\u5fc3\u5982\u6b62\u6c34\uff0c\u968f\u5fc3\u6240\u6b32\uff0c\u65e0\u7275\u65e0\u6302\uff0c\u8fd9 \u79cd\u4eba\u624d\u771f\u7684\u662f\u4e86\u4e0d\u8d77\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u7684\u786e\u5df2\u4ec0\u4e48\u4e8b\u90fd\u770b\u7a7f\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u6539\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u53e3\u68fa\u6750\u662f\u8c01\u9001\u53bb\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u731c\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u9001\u68fa\u6750\u53bb\u7684\uff0c\u96be\u9053\u5c31\u662f\u6740\u4e0a\u5b98\u98de\u7684\u4eba\uff1f\u201d \u3000\u3000\u5979\u663e\u7136\u4e5f\u5df2\u77e5\u9053\u6740\u4e0a\u5b98\u98de\u7684\u4eba\u662f\u8c01\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u4e0d\u77e5\u9053\uff0c\u4e00\u76f4\u7ad6\u7740\u8033\u6735\u5728\u542c\uff0c\u53ea\u6068\u4ed6\u4eec\u5374\u504f\u504f\u90fd\u4e0d\u80af\u5c06\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u60f3\u5fc5\u5c31\u662f\u4ed6\uff0c\u56e0\u4e3a\u77e5\u9053\u4e0a\u5b98\u98de\u5c38\u4f53\u5728\u90a3\u91cc\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d\u2026\u2026 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u60f3\u6253\u51fb\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u4e5f\u6068\u4e0a\u5b98\u91d1\u8679\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u541f\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u5e76\u4e0d\u662f\u6068\uff0c\u4ed6\u60f3\u6253\u51fb\u4e0a\u5b98\u91d1\u8679\uff0c\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4e0a \u5b98\u91d1\u8679\u88ab\u6253\u5012\u540e\uff0c\u4ed6\u624d\u6709\u673a\u4f1a\u53bb\u6551\u4ed6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u66f4\u4e0d\u61c2\u4e86\uff0c\u4ed6\u65e2\u7136\u60f3\u6551\u4ed6\uff0c\u4e3a\u4f55\u53c8\u8981\u6253\u51fb\u4ed6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u662f\u8981\u4e0a\u5b98\u91d1\u8679\u540e\u6094\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4eba\u7684\u5fc3\uff0c\u5b9e\u5728\u6bd4\u4ec0\u4e48\u4e8b\u90fd\u96be\u4e86\u89e3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e16\u4e0a\u6700\u96be\u4e86\u89e3\uff0c\u5c31\u662f\u4eba\u5fc3\u548c\u4eba\u6027\uff0c\u4eba\u6027\u7684\u590d\u6742\uff0c\u8fdc\u5728\u5929\u4e0b\u4efb\u4f55 \u4e00\u79cd\u6b66\u529f\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u63a5\u7740\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u4e0d\u80fd\u4e86\u89e3\u4eba\u6027\uff0c\u6b66\u529f\u4e5f\u5c31\u6c38\u8fdc\u65e0\u6cd5\u8fbe\u5230\u5dc5\u5cf0\uff0c\u56e0\u4e3a\u65e0\u8bba\u4ec0\u4e48 \u4e8b\uff0c\u90fd\u662f\u548c\u4eba\u6027\u606f\u606f\u76f8\u5173\u7684\uff0c\u6b66\u529f\u4e5f\u4e0d\u4f8b\u5916\u3002\u201d \u3000\u3000\u8fd9\u79cd\u54f2\u7406\u5bf9\u5b59\u5c0f\u7ea2\u8bf4\u6765\u4e5f\u8bb8\u592a\u6df1\u5965\u4e86\u4e9b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u4e0d\u77e5\u542c\u61c2\u4e86\u6ca1\u6709\uff0c\u6c89\u9ed8\u4e86\u534a\u664c\u624d\u5f00\u53e3\uff0c\u58f0\u97f3\u5982\u98ce\u5728\u8f7b\u8bc9\uff0c\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u90fd\u4e0d\u60f3 \u4e86\u89e3\uff0c\u53ea\u60f3\u4e86\u89e3\u4f60\u3002\u201d \u3000\u3000\u5979\u7684\u773c\u775b\u5728\u51dd\u89c6\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u7684\u795e\u8272\u4e0d\u4ec5\u662f\u8d5e\u8d4f\uff0c\u8fd8\u752d\u770b\u79cd\u4fe1\u8d56\uff0c\u4eff\u4f5b\u5728\u544a\u8bc9\u4ed6\uff0c\u53ea\u6709 \u5728\u4ed6\u9762\u524d\uff0c\u5979\u624d\u4f1a\u5c06\u81ea\u5df1\u7684\u5fc3\u4e8b\u5168\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5fc3\u91cc\u5ffd\u7136\u53c8\u6cdb\u8d77\u4e86\u90a3\u79cd\u6e29\u6696\u4e4b\u610f\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u4f38\u624b\u53bb\u6478\u4e00\u6478\u5979\u90a3\u82f9\u679c\u822c\u7684\u8138\u3002 \u3000\u3000\u4f46\u4ed6\u5f53\u7136\u5e76\u6ca1\u6709\u771f\u7684\u8fd9\u4e48\u6837\u505a\u3002 \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u8fd9\u4e48\u505a\u3002 \u3000\u3000\u4ed6\u6162\u6f2b\u7684\u626d\u8f6c\u5934\uff0c\u8f7b\u8f7b\u7684\u54b3\u55fd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u663e\u7136\u5728\u7b49\u7740\uff0c\u7b49\u4e86\u5f88\u4e45\uff0c\u76ee\u4e2d\u6e10\u6e10\u9732\u51fa\u4e86\u5931\u671b\u4e4b\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f46\u4f60\u5374\u597d\u50cf\u5f88\u6015 \u88ab\u4eba\u4e86\u89e3\uff0c\u6240\u4ee5\u65f6\u65f6\u523b\u523b\u90fd\u5728\u9632\u5907\u7740\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6015\uff1f\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u6015\u522b\u4eba\u7231\u4e0a\u4f60\u3002\u201d \u3000\u3000\u5979\u5f88\u5feb\u7684\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u77e5\u9053\u65e0\u8bba\u8c01\u82e5\u662f\u771f\u6b63\u7684\u4e86\u89e3\u4e86\u4f60\uff0c\u4e00\u5b9a\u5c31\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u7231\u4e0a\u4f60 \u7684\uff0c\u4f60\u5b81\u53ef\u88ab\u4eba\u6068\uff0c\u4e5f\u4e0d\u613f\u88ab\u4eba\u7231\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u73b0\u5728\u7684\u5e74\u4ee3\u7684\u786e\u53d8\u4e86\uff0c\u4ee5\u524d\u7684\u5c0f\u59d1\u5a18\uff0c\u5634\u91cc\u7edd\u4e0d\u4f1a\u8bf4\u51fa\u2018\u7231\u2019\u8fd9\u4e2a \u5b57\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ee5\u540e\u7684\u5c0f\u59d1\u5a18\u4e5f\u672a\u5fc5\u6562\u8bf4\uff0c\u53ef\u662f\u6211\u2026\u2026\u6211\u65e0\u8bba\u751f\u5728\u54ea\u4e2a\u5e74\u4ee3\uff0c\u5c31\u7b97\u662f\u751f\u5728 \u51e0\u767e\u5e74\u4ee5\u524d\uff0c\u53ea\u8981\u662f\u6211\u5fc3\u91cc\u60f3\u8bf4\u7684\u8bdd\uff0c\u6211\u8fd8\u662f\u4e00\u6837\u4f1a\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u4ee3\uff0c\u90fd\u4f1a\u6709\u51e0\u4e2a\u50cf\u5979\u8fd9\u6837\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u79cd\u4eba\u6562\u8bf4\u3001\u6562\u505a\u3001\u6562\u7231\u3001\u4e5f\u6562\u6068\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u4ed6\u4eec\u662f\u6d3b\u5728\u65f6\u4ee3\u524d\u9762\u7684\uff0c\u6240\u4ee5\u5728\u522b\u4eba\u773c\u4e2d\uff0c\u4e5f\u8bb8\u4f1a\u5c06\u4ed6\u4eec\u770b\u6210\u75af\u5b50\u3001\u602a\u7269\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u81ea\u5df1\u5374\u8fd8\u662f\u6d3b\u5f97\u5f88\u597d\uff0c\u5f88\u6109\u5feb\uff0c\u751a\u81f3\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u6109\u5feb\u5f97\u591a\uff0c\u56e0\u4e3a\u65e0\u8bba\u522b\u4eba\u5bf9\u4ed6 \u4eec\u7684\u770b\u6cd5\u5982\u4f55\uff0c\u4ed6\u4eec\u6839\u672c\u5168\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4eca\u591c\u8fd8\u662f\u6709\u96fe\u3002 \u3000\u3000\u73b0\u5728\u867d\u5df1\u662f\u51ac\u5929\uff0c\u4f46\u8fd9\u96fe\uff0c\u5374\u50cf\u662f\u6625\u5929\u7684\u96fe\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5728\u96fe\u4e2d\u6162\u6162\u7684\u8d70\u7740\uff0c\u5c31\u50cf\u662f\u5e0c\u671b\u8fd9\u6bb5\u8def\u6c38\u8fdc\u4e5f\u83ab\u8981\u8d70\u5b8c\u4f3c\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u672c\u6765\u662f\u6025\u7740\u60f3\u53bb\u77a7\u963f\u98de\u7684\uff0c\u4f46\u73b0\u5728\uff0c\u4ed6\u4e5f\u6ca1\u6709\u50ac\u4fc3\u3002 \u3000\u3000\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u7684\u5fc3\u60c5\u4e00\u76f4\u5f88\u6c89\u91cd\uff0c\u5c31\u50cf\u662f\u5df2\u88ab\u4e00\u9053\u5143\u5f62\u7684\u67b7\u9501\u538b\u4f4f\uff0c\u538b\u5f97\u4ed6\u51e0\u4e4e\u8fde\u6c14\u90fd \u900f\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u6709\u5728\u548c\u5b59\u5c0f\u7ea2\u804a\u5929\u7684\u65f6\u5019\uff0c\u4ed6\u624d\u4f1a\u89c9\u5f97\u8f7b\u677e\u4e9b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u5b59\u5c0f\u7ea2\u5b9e\u5728\u5f88\u4e86\u89e3\u4ed6\uff0c\u751a\u81f3\u6bd4\u4ed6\u60f3\u50cf\u4e2d\u8fd8\u8981\u4e86\u89e3\u5f97\u6df1\u3002 \u3000\u3000\u80fd\u548c\u4e86\u89e3\u81ea\u5df1\u7684\u4eba\u804a\u804a\u5929\uff0c\u672c\u662f\u4eba\u751f\u4e2d\u6700\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u5df2\u5f00\u59cb\u60f3\u9003\u907f\u4e86\u3002 \u3000\u3000\u201c\u2026\u2026\u4f60\u5b81\u53ef\u88ab\u4eba\u6068\uff0c\u4e5f\u4e0d\u613f\u88ab\u4eba\u7231\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u5728\u7ede\u75db\u2026\u2026 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u201c\u4e0d\u613f\u201d\uff0c\u800c\u662f\u201c\u4e0d\u80fd\u201d\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u81ea\u5df1\u975e\u4f46\u5df2\u65e0\u6cd5\u518d\u201c\u7ed9\u4e88\u201d\uff0c\u4e5f\u65e0\u6cd5\u518d\u201c\u63a5\u53d7\u201d\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u5e26\u7740\u4ed6\u81ea\u5df1\u7684\u67b7\u9501\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u5916\uff0c\u8c01\u4e5f\u65e0\u6cd5\u66ff\u4ed6\u89e3\u8131\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5982\u6b64\uff0c\u963f\u98de\u4e5f\u5982\u6b64\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u67b7\u9501\u662f\u4e0d\u662f\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u89e3\u8131\uff1f\u96be\u9053\u4ed6\u4eec\u8981\u5e26\u7740\u8fd9\u526f\u67b7\u9501\u8d70\u5165\u575f\u5893\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d \u3000\u3000\u8def\u5f88\u8352\u50fb\uff0c\u8def\u65c1\u6709\u680b\u5c0f\u5c0f\u7684\u5c4b\u5b50\uff0c\u7a97\u5b50\u91cc\u6709\u706f\u5149\u900f\u51fa\u3002 \u3000\u3000\u706f\u5149\u95ea\u52a8\u7740\uff0c\u663e\u5f97\u7279\u522b\u660e\u4eae\uff0c\u8fd9\u4e48\u5c0f\u7684\u5c4b\u5b50\u91cc\uff0c\u672c\u4e0d\u8be5\u6709\u8fd9\u4e48\u660e\u4eae\u7684\u706f\u5149\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u7740\u6797\u4ed9\u513f\uff0c\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4f60\u8ba4\u5f97\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u5f53\u7136\u8ba4\u5f97\uff0c\u8fd9\u672c\u662f\u5979\u548c\u963f\u98de\u7684\u201c\u5bb6\u201d\u3002 \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8e51\u61e6\u7740\u9053\uff1a\u201c\u963f\u98de\u5df2\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u8fdb\u53bb\u770b\u770b\u4ed6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ef\u4ee5\u8fdb\u53bb\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u672c\u662f\u4f60\u7684\u5bb6\uff0c\u4f60\u8981\u8fdb\u53bb\u5c31\u8fdb\u53bb\uff0c\u672c\u4e0d\u5fc5\u95ee\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u6797\u4ed9\u3002\u51e0\u5782\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u73b0\u5728\u5f53\u7136\u4e0d\u540c\u4e86\uff0c\u4f60\u81ea\u5df1\u4e5f\u8be5\u77e5\u9053\uff0c\u8fd9\u79cd\u60c5\u51b5\u662f\u8c01\u9020\u6210\u7684\uff1f\u201d\u5979\u51b7\u7b11\u63a5\u7740 \u9053\uff1a\u201c\u4f60\u672c\u53ef\u5728\u8fd9\u91cc\u5feb\u5feb\u6d3b\u6d3b\uff0c\u5b89\u5b89\u9759\u9759\u7684\u8fc7\u4e00\u751f\uff0c\u53ef\u662f\u4f60\u81ea\u5df1\u4e0d\u613f\u610f\uff0c\u56e0\u4e3a\u79f0\u770b\u4e0d\u8d77\u8fd9\u4e2a \u5bb6\uff0c\u4e5f\u770b\u4e0d\u8d77\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5782\u7740\u5934\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u81ea\u5df1\u9519\u4e86\uff0c\u6211\u8fd8\u80fd\u591f\u6d3b\u7740\uff0c\u5168\u90e8\u662f\u56e0\u4e3a\u4ed6\u5728\u4fdd \u62a4\u6211\uff0c\u82e5\u662f\u6ca1\u6709\u4ed6\uff0c\u6211\u4e5f\u8bb8\u65e9\u5c31\u88ab\u4eba\u6740\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u76ef\u7740\u5979\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u8fd8\u4f1a\u50cf\u4ee5\u524d\u90a3\u6837\u4fdd\u62a4\u4f60\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u6d41\u7740\u773c\u6cea\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u5728\u4e4e\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u53ea\u60f3\u518d\u89c1\u4ed6\u4e00\u9762\uff0c\u5bf9\u4ed6\u8bf4\u4e24\u53e5\u8bdd\uff0c\u7136\u540e\u7acb\u523b\u5c31\u8d70\uff0c\u8fd9\u8981\u6c42 \u65e0\u8bba\u600e\u4e48\u90fd\u4e0d\u8fc7\u5206\uff0c\u4f60\u4eec\u603b\u53ef\u4ee5\u7b54\u5e94\u6211\u5427\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u4e0d\u7b54\u5e94\uff0c\u53ea\u53ef\u60dc\u4f60\u8bf4\u7684\u8bdd\u5f88\u96be\u4ee4\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5c31\u7b97\u6211\u5230\u65f6\u5019\u53c8\u4e0d\u80af\u5b9a\u4e86\uff0c\u4f60\u4eec\u4e5f\u53ef\u4ee5\u8d76\u6211\u8d70\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6c89\u541f\u7740\uff0c\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u76f4\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u5fc3\u4e5f\u5f88\u4e71\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u6700\u5927\u7684\u5f31\u70b9\uff0c\u5c31\u662f\u5fc3\u80a0\u592a\u8f6f\uff0c\u6709\u65f6\u4ed6\u867d\u7136\u660e\u77e5\u8fd9\u4ef6\u4e8b\u662f\u7edd\u4e0d\u80fd\u505a\u7684\uff0c\u5374\u504f\u504f\u8fd8 \u662f\u786c\u4e0d\u8d77\u5fc3\u80a0\u6765\u62d2\u7edd\u3002 \u3000\u3000\u5f88\u591a\u4eba\u90fd\u77e5\u9053\u4ed6\u8fd9\u79cd\u5f31\u70b9\uff0c\u5f88\u591a\u4eba\u90fd\u5728\u5229\u7528\u4ed6\u8fd9\u79cd\u5f31\u70b9\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\uff0c\u5374\u8fd8\u662f\u6ca1\u6cd5\u5b50\u6539\u3002 \u3000\u3000\u4ed6\u5b81\u53ef\u8ba9\u4eba\u5bf9\u4e0d\u8d77\u4ed6\u4e00\u4e07\u6b21\uff0c\u4e5f\u4e0d\u613f\u505a\u4e00\u6b21\u5bf9\u4e0d\u8d77\u522b\u4eba\u7684\u4e8b\uff0c\u6709\u65f6\u4ed6\u751a\u81f3\u660e\u77e5\u522b\u4eba\u5728\u9a97 \u4ed6\uff0c\u5374\u8fd8\u662f\u5b81\u613f\u88ab\u9a97\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u89c9\u5f97\u53ea\u8981\u6709\u4e00\u4e2a\u4eba\u5bf9\u4ed6\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u4ed6\u727a\u7272\u7684\u4ee3\u4ef7\u5c31\u5df2\u503c\u5f97\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c31\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u4f60\u8bf4\u4ed6\u662f\u541b\u5b50\u4e5f\u597d\uff0c\u662f\u5446\u5b50\u4e5f\u597d\uff0c\u81f3\u5c11\u4ed6\u8fd9\u79cd\u4eba\u603b\u662f\u4f60\u8fd9\u4e00 \u8f88\u5b50\u5f88\u96be\u518d\u9047\u89c1\u7b2c\u4e8c\u4e2a\u7684\u3002 \u3000\u3000\u81f3\u5c11\u4f60\u9047\u89c1\u4ed6\u603b\u4e0d\u4f1a\u89c9\u5f97\u540e\u6094\u3002 \u3000\u3000\u4ed6\u5f88\u5c11\u4ee4\u4eba\u8c0e\u6c57\uff0c\u66f4\u5c11\u4ee4\u4eba\u6d41\u8840\uff1b\u8840\u4e0e\u6c57\u4ed6\u60c5\u613f\u81ea\u5df1\u6d41\u3002 \u3000\u3000\u4f46\u4ed6\u505a\u51fa\u7684\u4e8b\uff0c\u603b\u4ee4\u4eba\u5fcd\u4e0d\u4f4f\u8981\u6d41\u6cea\u3002 \u3000\u3000\u662f\u611f\u52a8\u7684\u6cea\uff0c\u4e5f\u662f\u611f\u6fc0\u7684\u6cea\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fc3\u91cc\u5728\u53f9\u606f\u3002 \u3000\u3000\u5979\u65e9\u5df2\u77e5\u9053\u674e\u5bfb\u6b22\u7edd\u4e0d\u5fcd\u62d2\u7edd\u7684\uff0c\u4ed6\u51e0\u4e4e\u4ece\u672a\u62d2\u7edd\u8fc7\u522b\u4eba\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5e7d\u5e7d\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u5c31\u662f\u6211\u6700\u540e\u4e00\u6b21\u89c1\u4ed6\u4e86\uff0c\u4ee5\u540e\u4ed6\u82e5\u77e5\u9053\u4f60\u4eec\u8fde\u6700\u540e\u4e00\u9762\u90fd\u4e0d\u8ba9 \u6211\u53bb\u89c1\u4e00\u6b21\uff0c\u4f1a\u6068\u4f60\u4eec\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u53ea\u8bf4\u4e24\u53e5\u8bdd\uff1f\u8bf4\u5b8c\u4e86\u7acb\u523b\u5c31\u8d70\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u60e8\u7136\u7b11\u9053\uff1a\u201c\u6211\u96be\u9053\u771f\u7684\u90a3\u4e48\u4e0d\u77e5\u8da3\uff1f\u96be\u9053\u771f\u8981\u7b49\u4f60\u4eec\u6765\u8d76\u6211\u8d70\uff1f\u53ea\u8981\u4f60\u4eec\u7b54\u5e94 \u6211\u8fd9\u6700\u540e\u4e00\u4e2a\u8981\u6c42\uff0c\u6211\u6b7b\u800c\u65e0\u6028\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8ba9\u5979\u53bb\u5427\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e24\u53e5\u8bdd\u603b\u5bb3\u4e0d\u4e86\u4eba\u7684\u3002\u201d \u7b2c\u4e03\u5341\u4e09\u7ae0\u3000\u84b8\u7b3c\u548c\u67b7\u9501>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u4e09\u7ae0\u3000\u84b8\u7b3c\u548c\u67b7\u9501 \u3000\u3000\u5c4b\u5b50\u91cc\u5f88\u70ed\uff0c\u70ed\u5f97\u51fa\u5947\u3002\u56e0\u4e3a\u5c4b\u91cc\u751f\u4e86\u56db\u76c6\u706b\uff0c\u706b\u70e7\u5f97\u5f88\u65fa\u3002 \u3000\u3000\u95ea\u52a8\u7684\u706b\u5149\uff0c\u5c06\u5899\u58c1\u548c\u9ad8\u4f4e\u90fd\u7167\u6210\u4e86\u5ae3\u7ea2\u8272\u3002 \u3000\u3000\u963f\u98de\u7684\u8138\u4e5f\u662f\u7ea2\u7684\uff0c\u5168\u8eab\u90fd\u662f\u7ea2\u7684\u3002 \u3000\u3000\u4ed6\u5c31\u8eba\u5728\u56db\u76c6\u706b\u7684\u4e2d\u95f4\uff0c\u8d64\u7740\u4e0a\u8eab\uff0c\u53ea\u7a7f\u7740\u6761\u728a\u9f3b\u88e4\u3002 \u3000\u3000\u88e4\u5b50\u5df2\u6e7f\u900f\u3002 \u3000\u3000\u4ed6\u4ef0\u9762\u8eba\u5728\u76c6\u91cc\uff0c\u4e0d\u505c\u7684\u6d41\u6c57\uff0c\u4e0d\u505c\u7684\u5598\u7740\u6c14\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u90fd\u5df2\u865a\u8131\u3002 \u3000\u3000\u5c4b\u89d2\u91cc\u5750\u7740\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u6e05\u7fdf\u8001\u4eba\uff0c\u6b63\u81ea\u60a0\u95f2\u7684\u62bd\u7740\u65f1\u70df\u3002 \u3000\u3000\u4e00\u7f15\u7f15\u8f7b\u70df\u4ece\u4ed6\u9f3b\u5b50\u91cc\u55b7\u51fa\u6765\uff0c\u4ed6\u7684\u4eba\u5c31\u597d\u50cf\u5750\u5728\u96fe\u91cc\u3002 \u3000\u3000\u4ed6\u7684\u786e\u662f\u4e2a\u96fe\u4e00\u822c\u7684\u4eba\u7269\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4ece\u54ea\u91cc\u6765\u7684\uff0c\u4e5f\u6ca1\u6709\u3002\u4eba\u77e5\u9053\u4ed6\u8981\u5f80\u54ea\u91cc\u53bb\u3002 \u3000\u3000\u751a\u81f3\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7a76\u7adf\u662f\u8c01\uff1f\u4e5f\u8bb8\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u7a77\u6101\u6f66\u5012\u7684\u8bf4\u4e66\u5148\u751f\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u5c31\u662f\u90a3\u9b3c\u795e\u96be\u6d4b\u7684\u201c\u5929\u673a\u8001\u4eba\u201d\uff01\u963f\u98de\u95ed\u7740\u773c\u775b\uff0c\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u53d1\u73b0\u6709\u4eba\u8d70\u8fdb\u6765 \u3002 \u3000\u3000\u4f46\u65e0\u8bba\u8c01\u8d70\u8fdb\u6765\uff0c\u7b2c\u4e00\u773c\u5c31\u4f1a\u770b\u5230\u4ed6\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6014\u4e86\u6014\uff0c\u5931\u58f0\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u8001\u4eba\u5bb6\u8fd9\u662f\u5728\u5e72\u4ec0\u4e48\u3002\u201d\u5b59\u8001\u5148\u751f\u772f\u7740\u773c\uff0c\u55b7\u51fa\u53e3 \u54bd\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u5728\u84b8\u4ed6\u3002\u201d\u5b59\u5c0f\u7ea2\u66f4\u5947\u602a\u4e86\uff0c\u77aa\u5927\u773c\u775b\u9053\uff1a\u201c\u84b8\u4ed6\uff1f\u4ed6\u65e2\u4e0d\u662f\u9992\u5934\uff0c\u53c8\u4e0d \u662f\u8783\u87f9\uff0c\u4e3a\u4ec0\u4e48\u8981\u84b8\u4ed6\uff1f\u201d\u201d \u3000\u3000\u963f\u98de\u73b0\u5728\u770b\u6765\u7684\u786e\u5c31\u597d\u6a61\u4e00\u53ea\u88ab\u84b8\u719f\u4e86\u7684\u8783\u87f9\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u84b8\u4ed6\uff0c\u56e0\u4e3a\u6211\u8981\u5c06\u4ed6\u8eab\u5b50\u91cc\u7684\u9152\u84b8\u51fa\u6765\uff0c\u8ba9\u4ed6\u6e05\u9192\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u51dd\u6ce8\u7740\u674e\u5bfb\u72d0\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5c06\u4ed6\u8840\u91cc\u7684\u52c7\u6c14\u84b8\u51fa\u6765\uff0c\u8ba9\u4ed6\u91cd\u65b0\u505a\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u63d6\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u5012\u4e5f\u7684\u786e\u9700\u8981\u88ab\u84b8\u4e00\u84b8\uff0c\u53ea\u53ef\u60dc\u6211\u8eab\u5b50\u91cc\u7684\u9152\u82e5 \u5b8c\u5168\u88ab\u84b8\u51fa\u6765\uff0c\u6211\u8fd9\u4eba\u53ea\u6015\u4e5f\u5c31\u53d8\u6210\u7a7a\u7684\u4e86\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u76ee\u4e2d\u95f4\u52a8\u7740\u7b11\u610f\uff0c\u9053\uff1a\u201c\u4f60\u8eab\u624b\u91cc\u9664\u4e86\u9152\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u522b\u7684\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u809a\u5b50\u7684\u4e0d\u5408\u65f6\u5b9c\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u67d1\u638c\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u5999\uff0c\u82e5\u6ca1\u6709\u4e00\u809a\u5b50\u5b66\u95ee\uff0c\u600e\u8bf4\u5f97\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u987f\u4f4f\u7b11\uff0c\u7a00\u5618\u9053\uff1a\u201c\u5176\u5b9e\u6211\u5012\u771f\u60f3\u628a\u4f60\u84b8\u4e00\u84b8\uff0c\u770b\u770b\u4f60\u8eab\u5b50\u91cc\u9664\u4e86\u9152\u548c\u5b66\u95ee\u5916\uff0c \u8fd8\u6709\u4ec0\u4e48\u522b\u7684\uff1f\u770b\u8001\u5929\u7a76\u7adf\u7528\u4e9b\u4ec0\u4e48\u4e1c\u897f\u6765\u9020\u6210\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u7136\u540e\u6211\u5c31\u8981\u5c06\u5929\u4e0b\u7684\u4eba\u5168\u90e8\u627e\u6765\uff0c\u628a\u8fd9\u4e9b\u4e1c\u897f\u6a61\u586b\u9e2d\u4f3c\u7684\u585e\u5230\u4ed6\u4eec\u809a\u5b50 \u91cc\u53bb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u585e\u4e00\u70b9\uff1f\u201d \u3000\u3000\u5b64\u8001\u5148\u751f\u9053\uff1a\u201c\u4e0d\u662f\u4e00\u70b9\uff0c\u8d8a\u591a\u8d8a\u597d\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u5929\u4e0b\u7684\u4eba\u5c82\u975e\u90fd\u8981\u53d8\u5f97\u548c\u4ed6\u4e00\u6837\u4e86\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5929\u4e0b\u7684\u4eba\u90e8\u53d8\u5f97\u548c\u4ed6\u4e00\u6837\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e5f\u6709\u70b9\u4e0d\u597d\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u54ea\u70b9\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u5782\u4e0b\u5934\uff0c\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u8fd9\u7956\u5b59\u4e24\u4eba\u4e5f\u8bb8\u662f\u642d\u6863\u8bf4\u4e66\u8bf4\u60ef\u4e86\uff0c\u5e73\u65f6\u8bf4\u8d77\u8bdd\u6765\uff0c\u4e5f\u662f\u4e00\u642d\u4e00\u6863\uff0c\u4e00\u5439\u4e00\u5531\uff0c\u6559\u522b\u4eba \u8fde\u63d2\u5634\u7684\u673a\u4f1a\u90e8\u6ca1\u6709\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u65f6\uff0c\u674e\u5bfb\u6b22\u624d\u6709\u673a\u4f1a\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u82e6\u7b11\u7740\uff0c\u9053\uff1a\u201c\u524d\u8f88\u82e5\u8981\u4ee4\u5929\u4e0b\u4eba\u90fd\u53d8\u5f97\u548c\u6211\u4e00\u6837\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u53ea\u6709\u4e00\u79cd\u4eba\u8d5e\u6210\u8fd9\u4e3b\u610f \u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5356\u9152\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5728\u6211\u770b\u6765\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u4eba\u4e0d\u8d5e\u6210\u6211\u8fd9\u4e3b\u610f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u5b57\u5979\u8131\u53e3\u5c31\u8bf4\u4e86\u51fa\u6765\uff0c\u8bf4\u51fa\u6765\u540e\uff0c\u53c8\u6709\u70b9\u540e\u6094\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5df2\u77e5\u9053\u5979\u7237\u7237\u8bf4\u7684\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u679c\u7136\u5728\u77a7\u7740\u5979\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5b59\u5c0f\u7ea2\u7684\u8138\u5ffd\u7136\u7ea2\u4e86\uff0c\u5782\u7740\u5934\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8d5e\u6210\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u4eba\u82e5\u662f\u90fd\u53d8\u5f97\u548c\u4ed6\u4e00\u6837\uff0c\u4f60\u5c82\u975e\u5c31\u4e0d\u77e5\u9053\u8981\u54ea\u4e2a\u624d\u597d\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u201c\u6a31\u549b\u201d\u4e00\u58f0\u626d\u8f6c\u4e86\u8eab\u5b50\uff0c\u8138\u5df2\u7ea2\u5982\u7089\u706b\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u662f\u4e0d\u662f\u4e5f\u6709\u4e00\u56e2\u706b\uff1f \u3000\u3000\u5c11\u5973\u4eec\u7684\u6625\u706b\uff1f\u5b59\u8001\u5148\u751f\u67d1\u638c\u5927\u5956\uff0c\u7b11\u8fc7\u4e86\uff0c\u5c31\u53c8\u5f00\u59cb\u62bd\u70df\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u6797\u4ed9\u513f\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u6ca1\u6709\u77a7\u5979\u4e00\u773c\uff0c\u4f46\u5374\u8fde\u81ea\u5df1\u70df\u6597\u7684\u70df\u65e9\u5c31\u7184\u4e86 \u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5ffd\u7136\u6c89\u5bc2\u4e86\u4e0b\u6765\uff0c\u53ea\u5269\u4e0b\u677e\u679d\u5728\u706b\u7130\u4e2d\u71c3\u70e7\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5df2\u8d70\u5230\u963f\u98de\u9762\u524d\u3002 \u3000\u3000\u9664\u4e86\u963f\u98de\u5916\uff0c\u5979\u4e5f\u6ca1\u6709\u53bb\u77a7\u522b\u4eba\u4e00\u773c\u3002 \u3000\u3000\u95ea\u52a8\u7740\u706b\u5149\u6620\u7740\u5979\u7684\u8138\uff0c\u5979\u8138\u4e0a\u4e00\u9635\u767d\uff0c\u4e00\u9635\u7ea2\uff0c\u7ea2\u7684\u65f6\u5019\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u5bb3\u7f9e\u7684\u4ed9\u5b50\uff0c \u767d\u7684\u65f6\u5019\u770b\u6765\u5c31\u5982\u5e7d\u7075\u3002 \u3000\u3000\u4eba\u90fd\u6709\u4e24\u79cd\u9762\u76ee\uff0c\u6709\u65f6\u7f8e\u4e3d\uff0c\u6709\u65f6\u4e11\u964b\u3002 \u3000\u3000\u53ea\u6709\u5979\uff0c\u65e0\u8bba\u600e\u4e48\u53d8\uff0c\u90fd\u662f\u7f8e\u4e3d\u7684\u3002 \u3000\u3000\u5979\u82e5\u662f\u4ed9\u5b50\uff0c\u5f53\u7136\u662f\u5929\u4e0a\u6700\u7f8e\u4e3d\u7684\u4ed9\u5b50\uff0c\u5979\u82e5\u662f\u5e7d\u7075\uff0c\u4e5f\u662f\u5730\u72f1\u4e2d\u6700\u7f8e\u4e3d\u7684\u9b3c\u9b42\u3002 \u3000\u3000\u4f46\u963f\u98de\u5374\u50cf\u662f\u5df2\u4e0b\u5b9a\u4e86\u51b3\u5fc3\uff0c\u65e0\u8bba\u5979\u600e\u4e48\u53d8\uff0c\u90fd\u4e0d\u4f1a\u518d\u77a7\u5979\u4e00\u773c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u4e3a\u4e86\u8981\u5bf9\u4f60\u8bf4\u4e24\u53e5\u8bdd\uff0c\u542c\u4e0d\u542c\u90fd\u968f\u4fbf \u4f60\u3002\u201d \u3000\u3000\u963f\u98de\u597d\u50cf\u6839\u672c\u6ca1\u6709\u5728\u542c\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4ed6\u7684\u8eab\u5b50\u4e3a\u4ec0\u4e48\u5374\u53c8\u5df2\u50f5\u786c\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u90a3\u5929\uff0c\u6211\u77e5\u9053\u4f60\u5f88\u4f24\u5fc3\uff0c\u53ef\u662f\u6211\u5374\u4e0d\u80fd\u4e0d\u90a3\u4e48\u505a\uff0c\u56e0\u4e3a\u6211\u4e0d\u613f\u770b \u5230\u4f60\u6b7b\u5728\u4e0a\u5b98\u91d1\u8679\u624b\u4e0a\uff0c\u6211\u53ea\u6709\u7528\u90a3\u79cd\u6cd5\u5b50\uff0c\u4e0a\u5b98\u91d1\u8679\u624d\u4e0d\u4f1a\u6740\u4f60\u3002\u201d \u3000\u3000\u963f\u98de\u597d\u50cf\u8fd8\u662f\u6ca1\u6709\u5728\u542c\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u7684\u62f3\u5df2\u63e1\u7d27\uff1f \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4eca\u5929\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u65e2\u4e0d\u662f\u8981\u6c42\u4f60\u4e86\u89e3\uff0c\u66f4\u4e0d\u662f\u8981\u6c42\u4f60\u539f\u8c05\uff0c\u6211\u81ea\u5df1\u4e5f\u77e5\u9053 \uff0c\u6211\u4eec\u7684\u7f18\u4efd\u5df2\u5c3d\u2026\u2026\u201d \u3000\u3000\u5979\u957f\u957f\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\u8fd9\u4e9b\u8bdd\uff0c\u53ea\u4e3a\u4e86\u8981\u8ba9\u4f60\u5fc3\u91cc\u89c9\u5f97\u597d\u53d7\u4e9b\uff0c \u56e0\u4e3a\u6211\u4e00\u76f4\u90fd\u5e0c\u671b\u4f60\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\uff0c\u81f3\u4e8e\u6211\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5df2\u8bf4\u5f97\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u6162\u6162\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u5df2\u8bf4\u5f97\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u5979\u679c\u7136\u4e00\u4e2a\u5b57\u90fd\u4e0d\u518d\u8bf4\uff0c\u7acb\u523b\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5979\u8d70\u7684\u5e76\u4e0d\u5feb\uff0c\u5374\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u963f\u98de\u8fd8\u662f\u8eba\u5728\u90a3\u91cc\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u5f20\u5f00\u8fc7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u770b\u5df2\u8981\u8d70\u51fa\u95e8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6797\u4ed9\u513f\u4eca\u5929\u53ea\u8981\u8d70\u51fa\u8fd9\u9053\u95e8\uff0c\u963f\u98de\u4ee5\u540e\u53ea\u6015\u5c31\u6c38\u8fdc\u518d\u4e5f\u89c1\u4e0d\u5230\u5979\u3002 \u3000\u3000\u53ea\u8981\u963f\u98de\u4e0d\u518d\u89c1\u5230\u5979\uff0c\u5c31\u5df2\u91cd\u751f\u3002 \u3000\u3000\u6797\u4ed9\u513f\u81ea\u5df1\u5f53\u7136\u4e5f\u5f88\u660e\u767d\u4eca\u5929\u53ea\u8981\u8d70\u51fa\u8fd9\u9053\u95e8\uff0c\u5c31\u7b49\u4e8e\u5df2\u8d70\u51fa\u4e86\u8fd9\u4e16\u754c\u3002 \u3000\u3000\u5979\u811a\u6b65\u867d\u7136\u5e76\u6ca1\u6709\u6162\u4e0b\u6765\uff0c\u4f46\u76ee\u5149\u4e2d\u5374\u5df2\u53c8\u9732\u51fa\u4e86\u6050\u60e7\u4e4b\u610f\u2014\u2014\u5c4b\u5b50\u91cc\u867d\u7136\u4eae\u5982\u767d\u663c\uff0c \u4f46\u95e8\u5916\u5374\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u867d\u7136\u4e5f\u6709\u661f\u5149\uff0c\u4f46\u661f\u5149\u5979\u5e76\u6ca1\u6709\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u5979\u559c\u6b22\u7684\u662f\u4ee4\u4eba\u7729\u76ee\u7684\u5149\u91c7\u3002 \u3000\u3000\u5979\u559c\u6b22\u8d5e\u7f8e\u3001\u963f\u8c00\u3001\u638c\u58f0\uff0c\u559c\u6b22\u5962\u4f88\u3001\u6d6a\u8d39\u3001\u4eab\u53d7\uff0c\u559c\u6b22\u88ab\u4eba\u7231\uff0c\u4e5f\u559c\u6b22\u88ab\u4eba\u6068\u2026\u2026 \u3000\u3000\u5979\u672c\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e9b\u800c\u6d3b\u7740\u7684\u3002 \u3000\u3000\u82e5\u6ca1\u6709\u8fd9\u4e9b\uff0c\u5979\u5c31\u7b97\u8fd8\u80fd\u6d3b\u4e0b\u53bb\uff0c\u4e5f\u5c31\u5982\u6e05\u5728\u575f\u5893\u91cc\u3002 \u3000\u3000\u9ed1\u6697\u5df2\u8d8a\u6765\u8d8a\u8fd1\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u76ee\u4e2d\u7684\u6050\u60e7\u5df2\u6e10\u6e10\u53d8\u4e3a\u6028\u6bd2\u3001\u4ec7\u6068\u3002 \u3000\u3000\u8fd9\u65f6\u5979\u82e5\u6709\u529b\u91cf\uff0c\u5979\u4e00\u5b9a\u4f1a\u5c06\u809a\u4e0a\u6240\u6709\u6d3b\u7740\u7684\u4eba\u90fd\u6740\u6b7b\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u963f\u98de\u7a81\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a \u3000\u3000\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u201c\u7b49\u4e00\u7b49\uff01\u201d \u3000\u3000\u8c01\u90fd\u65e0\u6cd5\u76f8\u4fe1\u8fd9\u7b80\u7b80\u5355\u5355\u7684\u4e09\u4e2a\u5b57\u80fd\u6539\u53d8\u591a\u5c11\u4eba\u7684\u4e00\u751f\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u6797\u4ed9\u513f\u5df2\u7a81\u7136\u5b8c\u5168\u6539\u53d8\u3002 \u3000\u3000\u5979\u773c\u775b\u91cc\u7acb\u523b\u5c31\u53c8\u5145\u6ee1\u4e86\u5f97\u610f\u3001\u81ea\u4fe1\u3001\u9a84\u50b2\uff0c\u5979\u6574\u4e2a\u4eba\u4e5f\u4eff\u4f5b\u7a81\u7136\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u8f89\u714c\u3001 \u7f8e\u4e3d\uff01 \u3000\u3000\u5979\u51e0\u4e4e\u4ece\u6765\u4e5f\u6ca1\u6709\u50cf\u73b0\u5728\u8fd9\u4e48\u7f8e\u4e3d\u8fc7\u3002 \u3000\u3000\u201c\u53ea\u6709\u9a84\u50b2\u548c\u81ea\u4fe1\uff0c\u624d\u662f\u5973\u4eba\u6700\u597d\u7684\u88c5\u9970\u54c1\u3002\u201d \u3000\u3000\u4e00\u4e2a\u6ca1\u6709\u4fe1\u5fc3\uff0c\u6ca1\u6709\u5e0c\u671b\u7684\u5973\u4eba\uff0c\u5c31\u7b97\u5979\u957f\u5f97\u4e0d\u96be\u770b\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6709\u90a3\u79cd\u4ee4\u4eba\u5fc3\u52a8\u7684\u5438\u5f15 \u529b\u3002 \u3000\u3000\u8fd9\u5c31\u6b63\u5982\u5728\u5973\u4eba\u773c\u4e2d\uff0c\u53ea\u8981\u662f\u6210\u529f\u7684\u7537\u4eba\uff0c\u5c31\u4e00\u5b9a\u4e0d\u4f1a\u662f\u4e11\u964b\u7684\u3002 \u3000\u3000\u201c\u53ea\u6709\u4e8b\u4e1a\u7684\u6210\u529f\uff0c\u624d\u662f\u7537\u4eba\u6700\u597d\u7684\u88c5\u9970\u54c1\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u811a\u6b65\u5df2\u505c\u4e0b\uff0c\u8fd8\u662f\u6ca1\u6709\u56de\u5934\uff0c\u5374\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5979\u7684\u53f9\u606f\u58f0\u5f88\u8f7b\u5f88\u8f7b\uff0c\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u5e7d\u6028\u51c4\u82e6\u4e4b\u610f\u3002 \u3000\u3000\u770b\u5230\u5979\u76ee\u4e2d\u795e\u8272\u7684\u4eba\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u5979\u5728\u5982\u6b64\u5f97\u610f\u7684\u65f6\u5019\uff0c\u4e5f\u4f1a\u53d1\u51fa\u8fd9\u4e48\u51c4\u51c9\u7684 \u53f9\u606f\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u53c8\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u97f3\u4e50\uff0c\u4efb\u4f55\u4e00\u79cd\u58f0\u97f3\u80fd\u6bd4\u5979\u8fd9\u79cd\u53f9\u606f\u66f4\u80fd\u6253\u52a8\u7537\u4eba\u7684\u5fc3\uff0c\u7eb5 \u7136\u662f\u79cb\u53f6\u7684\u51cb\u843d\u58f0\uff0c\u6d41\u6c34\u7684\u54c0\u9e23\u58f0\uff0c\u751a\u81f3\u8fde\u6708\u4e0b\u7684\u5bd2\u7434\uff0c\u98ce\u4e2d\u7684\u591c\u7b1b\uff0c\u4e5f\u7edd\u6ca1\u6709\u5979\u8fd9\u79cd\u53f9\u606f \u58f0\u51c4\u5a1c\u52a8\u4eba\u3002 \u3000\u3000\u4ed6\u53ea\u5e0c\u671b\u963f\u98de\u80fd\u77a7\u4ed6\u4e00\u773c\uff0c\u542c\u4ed6\u8bf4\u53e5\u8bdd\u3002 \u3000\u3000\u4f46\u963f\u98de\u73b0\u5728\u773c\u4e2d\u5df2\u53c8\u53ea\u5269\u4e0b\u6797\u4ed9\u513f\u4e00\u4e2a\u4eba\uff0c\u8033\u91cc\u4e5f\u53ea\u80fd\u542c\u5f97\u5230\u5979\u4e2a\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u7684\u8bdd\u5df2\u8bf4\u5b8c\u4e86\u3002\u5df2\u4e0d\u80fd\u518d\u7b49\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u80fd\u7b49\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u7b54\u5e94\u8fc7\u522b\u4eba\uff0c\u53ea\u6765\u8bf4\u4e24\u53e5\u8bdd\uff0c\u8bf4\u5b8c\u4e86\u5c31\u8d70\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u60f3\u8d70\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u9053\uff1a\u201c\u5c31\u7b97\u6211\u4e0d\u60f3\u8d70\uff0c\u4e5f\u6709\u4eba\u4f1a\u6765\u8d76\u6211\u8d70\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u8c01\uff1f\u8c01\u8981\u8d76\u4f60\u8d70\uff1f\u201d \u3000\u3000\u4ed6\u773c\u775b\u91cc\u5ffd\u7136\u53c8\u6709\u4e86\u5149\uff0c\u6709\u4e86\u529b\u91cf\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u88ab\u4eba\u8d76\u8d70\uff0c\u8fd9\u672c\u662f\u4f60\u7684\u5bb6\u3002 \u201d \u3000\u3000\u6797\u4ed9\u513f\u970d\u7136\u8f6c\u8eab\uff0c\u51dd\u6ce8\u7740\u963f\u98de\u3002 \u3000\u3000\u5979\u76ee\u4e2d\u4f3c\u5df2\u6709\u6cea\uff0c\u56e0\u4e3a\u5979\u773c\u6ce2\u672c\u5c31\u67d4\u5982\u6625\u6c34\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u5979\u624d\u53c8\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u51c4\u7136\u9053\uff1a\u201c\u73b0\u5728\u8fd9\u91cc\u8fd8\u662f\u6211\u7684\u5bb6\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5f53\u7136\u662f\u7684\uff0c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u8fd9\u91cc\u5c31\u662f\u4f60\u7684\u5bb6\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7684\u811a\u6b65\u5f00\u59cb\u79fb\u52a8\uff0c\u4eff\u4f5b\u5fcd\u4e0d\u4f4f\u8981\u53bb\u6295\u5165\u963f\u98de\u6000\u91cc\uff0c\u4f46\u5ffd\u7136\u95f4\u53c8\u505c\u4e0b\u811a\u6b65\uff0c\u5782\u5934\u9053 \uff1a\u201c\u6211\u5f53\u7136\u613f\u610f\uff0c\u600e\u5948\u522b\u4eba\u5374\u4e0d\u613f\u610f\u3002\u201d \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u8c01\u4e0d\u613f\u610f\uff0c\u8c01\u5c31\u5f97\u8d70\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u5df2\u4e0d\u6562\u89e6\u53ca\u674e\u5bfb\u6b22\u7684\u76ee\u5149\uff0c\u4e5f\u4e0d\u7ba1\u522b\u4eba\u5bf9\u4ed6\u600e\u4e48\u60f3\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u7684\u786e\u5c06\u4ed6\u8840\u6db2\u91cc\u7684\u9152\u84b8\u4e86\u51fa\u6765\uff0c\u52c7\u6c14\u84b8\u4e86\u51fa\u6765\uff0c\u4ed6\u5374\u5c06\u4ed6\u7684\u60c5\u611f\u5168\u90fd\u84b8\u4e86\u51fa\u6765 \u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8eab\u5b50\u6700\u865a\u5f31\u65f6\uff0c\u60c5\u611f\u5374\u6700\u4e30\u5bcc\u3002 \u3000\u3000\u963f\u98de\u7684\u773c\u775b\u4f3c\u4e4e\u518d\u4e5f\u4e0d\u613f\u79bb\u5f00\u6797\u4ed9\u513f\uff0c\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u5728\u8fd9\u91cc\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u8d76\u4f60\u8d70 \uff0c\u53ea\u6709\u4f60\u624d\u80fd\u8d76\u522b\u4eba\u8d70\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5e26\u7740\u6cea\uff0c\u53c8\u5e26\u7740\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7684\u786e\u5f88\u60f3\u8ddf\u4f60\u5355\u72ec\u5728\u4e00\u8d77\uff0c\u53ef\u662f\uff0c\u4ed6\u4eec\u90fd\u662f\u4f60\u7684\u670b \u53cb\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u613f\u610f\u505a\u4f60\u670b\u53cb\u7684\u4eba\uff0c\u4e5f\u5c31\u4e0d\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u71d5\u5b50\u822c\u6295\u5165\u4ed6\u6000\u91cc\uff0c\u7d27\u7d27\u62e5\u62b1\u4f4f\u4ed6\uff0c\u9053\uff1a\u201c\u53ea\u8981\u80fd\u518d\u542c\u5230\u4f60\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u6211\u5df2 \u7ecf\u5fc3\u6ee1\u610f\u8db3\u4e86\uff0c\u522b\u7684\u6211\u4ec0\u4e48\u90fd\u4e0d\u518d\u60f3\uff0c\u65e0\u8bba\u522b\u4eba\u5bf9\u6211\u600e\u4e48\u6837\uff0c\u6211\u4e5f\u90fd\u4e0d\u518d\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u95e8\uff0c\u662f\u865a\u63a9\u7740\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u8d70\u4e86\u51fa\u53bb\uff0c\u8d70\u5165\u95e8\u5916\u7684\u9ed1\u6697\u4e0e\u5bd2\u591c\u4e2d\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u82e5\u518d\u7559\u5728\u5c4b\u5b50\u91cc\uff0c\u5df2\u662f\u591a\u4f59\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u8ddf\u4e86\u51fa\u6765\uff0c\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u6211\u4eec\u96be\u9053\u5c31\u8fd9\u6837\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4ec0\u4e48\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4ec0\u4e48\u90fd\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u6211\u771f\u6ca1\u60f3\u5230\u4ed6\u7adf\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u5c45\u7136\u8fd8\u5bf9\u5979\u8fd9\u6837\u5b50\uff0c\u8fd9\u79cd\u4eba \u7b80\u76f4\u2026\u2026\u7b80\u76f4\u662f\u5fd8\u6069\u8d1f\u4e49\uff0c\u91cd\u8272\u8f7b\u53cb\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7ec8\u4e8e\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u770b\u9519\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u51b7\u7b11\u7740\uff0c\u6068\u6068\u9053\uff1a\u201c\u6211\u770b\u9519\u4e86\uff1f\u96be\u9053\u4ed6\u4e0d\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u600e\u4e48\u80fd\u505a\u5f97\u51fa\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u8bf4\uff0c\u5b59\u8001\u5148\u751f\u5374\u66ff\u4ed6\u8bf4\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u606f\u9053\uff1a\u201c\u4ed6\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u56e0\u4e3a\u4ed6\u5df2\u4e0d\u80fd\u81ea\u4e3b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u81ea\u4e3b\uff0c\u53c8\u6ca1\u6709\u4eba\u7528\u5200\u903c\u4f4f\u4ed6\uff0c\u7528\u9501\u9501\u4f4f\u4ed6\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u867d\u7136\u6ca1\u6709\u522b\u4eba\u903c\u4ed6\uff0c\u4ed6\u81ea\u5df1\u5374\u5df2\u5c06\u81ea\u5df1\u9501\u4f4f\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4e0d\u53ea\u662f\u4ed6\uff0c\u4e16\u4e0a\u6bcf\u4e2a\u4eba\u90fd\u6709\u4ed6\u81ea\u5df1\u7684\u67b7\u9501\uff0c\u4e5f\u6709\u4ed6\u81ea\u5df1\u7684\u84b8\u7b3c \u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u5c31\u6ca1\u6709\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4f60\u6ca1\u6709\uff0c\u53ea\u56e0\u4e3a\u4f60\u8fd8\u662f\u4e2a\u5b69\u5b50\uff0c\u8fd8\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u662f\u5b69\u5b50\uff1f\u597d\uff0c\u5c31\u7b97\u6211\u8fd8\u662f\u4e2a\u5b69\u5b50\uff0c\u90a3\u4e48\u4ed6\u5462\uff1f\u201d \u3000\u3000\u5979\u6307\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4ed6\u603b\u4e0d\u662f\u5b69\u5b50\u4e86\u5427\uff1f\u96be\u9053\u4ed6\u4e5f\u6709\u4ed6\u7684\u67b7\u9501\uff1f\u4ed6\u7684\u84b8\u7b3c\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u77aa\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4f60\u627f\u8ba4\u4f60\u6709\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u627f\u8ba4\uff0c\u56e0\u4e3a\u6211\u7684\u786e\u6709\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u5bf9\u81ea\u5df1\u4ec0\u4e48\u90fd\u4e0d\u5728\u4e4e\uff0c\u5c31\u7b97\u6709\u4eba\u8fb1\u9a82\u4e86\u4ed6\uff0c\u5bf9\u4e0d\u8d77\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u653e\u5728\u5fc3 \u4e0a\uff0c\u522b\u4eba\u751a\u81f3\u4f1a\u8ba4\u4e3a\u4ed6\u8fde\u52c7\u6c14\u90fd\u5df2\u6d88\u5931\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u5f97\u66f4\u82e6\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4f46\u4ed6\u7684\u670b\u53cb\u82e5\u662f\u6709\u4e86\u5371\u9669\uff0c\u4ed6\u5c31\u4f1a\u4e0d\u987e\u4e00\u5207\u53bb\u6551\u4ed6\uff0c\u751a\u81f3\u8d74\u6c64\u8e48\u706b\uff0c\u4e24 \u808b\u63d2\u5200\u4e5f\u5728\u6240\u4e0d\u60dc\u2026\u2026\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u2018\u670b\u53cb\u2019\u5c31\u662f\u4ed6\u7684\u84b8\u7b3c\uff0c\u53ea\u6709\u8fd9\u6837\u84b8\u7b3c\uff0c\u624d\u80fd\u5c06\u4ed6\u7684\u751f\u547d \u4e4b\u529b\u84b8\u51fa\u6765\uff01\u5c06\u4ed6\u7684\u52c7\u6c14\u84b8\u51fa\u6765\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u9f99\u5578\u4e91\u90a3\u79cd\u4eba\u96be\u9053\u4e5f\u6709\u84b8\u7b3c\u4e48\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5f53\u7136\u4e5f\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ec0\u4e48\u624d\u662f\u4ed6\u7684\u84b8\u7b3c\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u91d1\u94b1\u3001\u6743\u529b\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u8981\u6740\u674e\u5bfb\u6b22\uff0c\u5374\u5e76\u4e0d\u662f\u4e3a\u4e86\u91d1\u94b1\u548c\u6743\u529b\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u674e\u5bfb \u6b22\u662f\u7edd\u4e0d\u4f1a\u548c\u4ed6\u4e89\u6743\u593a\u5229\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u4e00\u5fc3\u8981\u6740\u674e\u5bfb\u6b22\uff0c\u53ea\u56e0\u4e3a\u4ed6\u5fc3\u4e0a\u4e5f\u6709\u67b7\u9501\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u7684\u67b7\u9501\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8138\u8272\u6bd4\u591c\u8272\u66f4\u9eef\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u4e5f\u660e\u767d\u4e86\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6068\u674e\u5bfb\u6b22\uff0c\u56e0\u4e3a\u4ed6\u6000\u7591\uff0c\u4ed6\u5ac9\u5992\uff01 \u3000\u3000\u4ed6\u59cb\u7ec8\u6000\u7591\u674e\u5bfb\u6b22\u4f1a\u5c06\u6240\u6709\u7684\u4e00\u5207\u90fd\u6536\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u5ac9\u5992\u674e\u5bfb\u6b22\u90a3\u79cd\u4f1f\u5927\u7684\u4eba\u683c\u548c\u60c5\u611f\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u6c38\u8fdc\u505a\u4e0d\u5230\u3002 \u3000\u3000\u6000\u7591\u548c\u5ac9\u5992\uff0c\u5c31\u662f\u4ed6\u7684\u67b7\u9501\u3002 \u3000\u3000\u8fd9\u79cd\u67b7\u9501\u4e5f\u8bb8\u4e16\u4e0a\u5927\u591a\u6570\u4eba\u90fd\u6709\u4e00\u526f\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u963f\u98de\u7684\u67b7\u9501\u662f\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u76ee\u5149\u9065\u89c6\u7740\u5929\u9645\u7684\u661f\u5149\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u963f\u98de\u7684\u67b7\u9501\u5c31\u548c\u9f99\u5578\u4e91\u7684\u5b8c\u5168\u4e0d\u540c\u4e86\u2026 \u2026\u963f\u98de\u7684\u67b7\u9501\u662f\u7231\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7231\u4e5f\u662f\u67b7\u9501\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5f53\u7136\u662f\uff0c\u800c\u4e14\u6bd4\u522b\u7684\u67b7\u9501\u90fd\u91cd\u5f97\u591a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f46\u4ed6\u771f\u7684\u90a3\u4e48\u7231\u6797\u4ed9\u513f\u4e48\uff1f\u4ed6\u7231\u5979\uff0c\u662f\u4e0d\u662f\u53ea\u56e0\u4e3a\u4ed6\u5f97\u4e0d\u5230\u5979\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u53e3\u7b54\u5979\u7684\u8bdd\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u95ee\u9898\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u597d\u6b79\u4e5f\u5f97\u60f3\u4e2a\u6cd5\u5b50\u6551\u6551\u4ed6\uff0c \u5c06\u4ed6\u8fd9\u526f\u67b7\u9501\u89e3\u8131\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u56de\u8fc7\u5934\u2014\u2014 \u3000\u3000\u7a97\u5b50\u91cc\u7684\u706b\u5149\u5df2\u9eef\u4e86\uff0c\u5c0f\u5c4b\u5b64\u96f6\u96f6\u7684\u77d7\u7acb\u5728\u897f\u98ce\u548c\u9ed1\u6697\u4e2d\uff0c\u770b\u6765\u5c31\u50cf\u662f\u963f\u98de\u7684\u4eba\u4e00\u6837\uff0c \u90a3\u4e48\u5014\u5f3a\uff0c\u53c8\u90a3\u4e48\u5bc2\u5bde\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5f2f\u4e0b\u8170\uff0c\u4e0d\u505c\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u65e0\u8bba\u8c01\u90fd\u6ca1\u6cd5\u5b50\u5c06\u963f\u98de\u7684\u67b7\u9501\u89e3\u8131\u3002 \u3000\u3000\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u6551\u5f97\u4e86\u4ed6\u3002 \u7b2c\u4e03\u5341\u56db\u7ae0\u3000\u6700\u6177\u6168\u7684\u4eba>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u56db\u7ae0\u3000\u6700\u6177\u6168\u7684\u4eba \u3000\u3000\u706f\u706b\u5df2\u7184\u3002 \u3000\u3000\u73b0\u5728\u5c4b\u5b50\u91cc\u71c3\u70e7\u7740\u7684\u662f\u53e6\u4e00\u79cd\u706b\u3002 \u3000\u3000\u4e00\u6761\u4fee\u957f\uff0c\u6d51\u5706\u7684\u817f\u81ea\u5e8a\u6cbf\u5782\u4e0b\uff0c\u5728\u6726\u80e7\u4e2d\u770b\u6765\u66f4\u767d\u5f97\u8000\u773c\u3002 \u3000\u3000\u817f\u8737\u66f2\uff0c\u4eba\u98a4\u6296\u3002 \u3000\u3000\u963f\u98de\u7d27\u5f20\u7684\u5c31\u50cf\u662f\u4e00\u6839\u5f13\u5f26\u3002 \u3000\u3000\u7bad\u5df2\u5728\u5f26\u4e0a\uff0c\u5bfb\u627e\u7740\u7bad\u579b\u3002 \u3000\u3000\u6709\u7ecf\u9a8c\u7684\u4eba\u90fd\u77e5\u9053\u6781\u5ea6\u75b2\u52b3\u540e\u7684\u7d27\u5f20\u6700\u96be\u4eca\u4eba\u5fcd\u53d7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5f53\u7136\u662f\u6709\u7ecf\u9a8c\u7684\u4eba\u3002 \u3000\u3000\u5979\u95ea\u907f\u7740\uff0c\u63a8\u62d2\u7740\u3001\u5598\u606f\u7740\uff1a\u201c\u7b49\u4e00\u7b49\u2026\u2026\u7b49\u4e00\u7b49\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7684\u56de\u7b54\u4e0d\u662f\u8a00\u8bed\uff0c\u662f\u52a8\u4f5c\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u5df2\u4e0d\u60f3\u518d\u7b49\u3002 \u3000\u3000\u6797\u4ed9\u513f\u54ac\u7740\u5507\uff0c\u671b\u7740\u4ed6\u5e03\u6ee1\u7ea2\u4e1d\u7684\u773c\u775b\u3002 \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u6ca1\u6709\u95ee\u6211\uff1f\u201d \u3000\u3000\u201c\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u95ee\u6211\u662f\u4e0d\u662f\u5df2\u7ecf\u548c\u4e0a\u5b98\u91d1\u8679\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u7684\u52a8\u4f5c\u7a81\u7136\u505c\u4f4f\u3001\u5c31\u50cf\u662f\u88ab\u4eba\u8e22\u4e86\u4e00\u811a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u76ef\u7740\u4ed6\uff1a\u201c\u4f60\u4e00\u76f4\u6ca1\u6709\u95ee\uff0c\u96be\u9053\u4f60\u4e0d\u5728\u4e4e\uff1f\u201d \u3000\u3000\u963f\u98de\u4e0d\u505c\u7684\u6d41\u6c57\uff0c\u6c57\u4f7f\u4eba\u8f6f\u5f31\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5df2\u611f\u89c9\u5230\u4ed6\u7684\u8f6f\u5f31\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5728\u4e4e\u7684\uff0c\u56e0\u4e3a\u4f60\u7231\u6211\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u51c4\u60e8\uff0c\u773c\u775b\u91cc\u5374\u5e26\u7740\u79cd\u6b8b\u9177\u7684\u7b11\u610f\uff0c\u5c31\u50cf\u662f\u4e00\u53ea\u732b\u5728\u770b\u7740\u722a\u4e0b\u7684\u8001\u9f20\uff0c\u5c31\u50cf\u662f \u4e0a\u5b98\u91d1\u8679\u5728\u770b\u7740\u5979\u7684\u65f6\u5019\u3002 \u3000\u3000\u963f\u98de\u7684\u58f0\u97f3\u5636\u54d1\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u606f\u7740\uff1a\u201c\u4e00\u53ea\u8001\u9f20\u82e5\u662f\u843d\u5165\u4e86\u732b\u7684\u624b\u91cc\uff0c\u4f60\u4e0d\u5fc5\u95ee\uff0c\u4e5f\u8be5\u77e5\u9053\u5979\u7684\u7ed3\u679c\u3002\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u5012\u4e86\u4e0b\u53bb\uff0c\u5df2\u6124\u6012\u5f97\u4e0d\u80fd\u518d\u6709\u4efb\u4f55\u52a8\u4f5c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8f7b\u629a\u7740\u4ed6\u7684\u8138\uff0c\u4eff\u4f5b\u5df2\u6709\u6cea\u5c06\u6d41\u843d\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4f1a\u751f\u6c14\uff0c\u53ef\u662f\u6211\u4e0d\u80fd\u4e0d\u8bf4\uff0c\u56e0\u4e3a\u6211\u672c\u60f3\u5c06\u8fd9\u8eab\u5b50\u6e05\u6e05\u767d\u767d\u7684\u4ea4\u7ed9\u4f60\u7684\uff0c\u53ea\u53ef \u60dc\u2026\u2026\u201d \u3000\u3000\u5979\u4f0f\u5728\u963f\u98de\u80f8\u819b\u4e0a\uff0c\u6d41\u7740\u6cea\u9053\u3001\u201c\u6211\u73b0\u5728\u771f\u540e\u6094\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4f60\u7b49\u8fd9\u4e48\u4e45\uff0c\u867d\u7136\u662f\u4e3a\u4e86\u4f60 \uff0c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u5ffd\u7136\u5927\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u6211\uff0c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u8fd8\u4f60\u7684\u6e05\u767d\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u51c4\u7136\u9053\uff1a\u201c\u8fd9\u662f\u6c38\u8fdc\u6ca1\u6cd5\u5b50\u8fd8\u7684\uff0c\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6709\uff01\u6211\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u4ed6\u7d27\u63e1\u7740\u53cc\u624b\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u53ea\u8981\u6740\u4e86\u4e0a\u5b98\u91d1\u8679\uff0c\u6740\u4e86\u73b7\u6c61\u4f60\u7684\u4eba\uff0c\u4f60\u5c31\u8fd8\u662f\u6e05\u767d\u7684\u2026 \u2026\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u5ffd\u7136\u505c\u987f\uff0c\u56e0\u4e3a\u4ed6\u542c\u5230\u7a97\u5916\u6709\u4eba\u5728\u51b7\u7b11\uff1a \u3000\u3000\u4e00\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u8bf4\u6765\uff0c\u4f60\u8981\u6740\u7684\u4eba\u5c31\u592a\u591a\u4e86\uff01\u201d \u3000\u3000\u53e6\u4e00\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6761\u6bcd\u72d7\u8eab\u5b50\u6839\u672c\u5c31\u4ece\u6765\u4e5f\u6ca1\u6709\u6e05\u767d\u7684\u65f6\u5019\uff0c\u53ea\u8981\u662f\u8ddf\u5979\u89c1\u8fc7\u9762\u7684\u7537 \u4eba\uff0c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u8c01\u90fd\u8ddf\u5979\u7761\u8fc7\u89c9\u3002\u201d \u3000\u3000\u7b2c\u4e09\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8981\u5c06\u8ddf\u5979\u7761\u8fc7\u515a\u7684\u7537\u4eba\u5168\u90fd\u6740\u6b7b\uff0c\u5c31\u7b97\u6bcf\u5929\u6740\u516b\u5341\u4e2a\uff0c\u6740\u5230\u4f60\u80e1\u5b50 \u90fd\u767d\u4e86\u7684\u65f6\u5019\uff0c\u4e5f\u6740\u4e0d\u5b8c\u7684\u3002\u201d \u3000\u3000\u8fd9\u5c4b\u5b50\u4e00\u5171\u6709\u4e09\u4e2a\u7a97\u6237\uff0c\u6bcf\u4e2a\u7a97\u6237\u5916\u90e8\u6709\u4e2a\u4eba\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u867d\u4e0d\u540c\uff0c\u5374\u53c8\u6709\u79cd\u5f88\u5947\u7279\u7684\u76f8\u540c\u4e4b\u5904\u3002 \u3000\u3000\u5c16\u9510\uff0c\u88c5\u4f5c\uff0c\u65e0\u8bba\u8c01\u542c\u4e86\u90fd\u60f3\u5410\u3002 \u3000\u3000\u963f\u98de\u8dc3\u8d77\uff0c\u6380\u8d77\u88ab\uff0c\u76d6\u5f80\u4e86\u6797\u4ed9\u513f\u8d64\u88f8\u7684\u8eab\u5b50\uff0c\u8e22\u51fa\u6795\u5934\uff0c\u51fb\u706d\u4e86\u684c\u4e0a\u7684\u706f\uff0c\u5389\u58f0\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u672c\u60f3\u51b2\u51fa\u53bb\uff0c\u4f46\u8eab\u5b50\u8dc3\u8d77\u540e\uff0c\u53c8\u9000\u56de\uff0c\u7d27\u5b88\u5728\u6797\u4ed9\u513f\u8eab\u65c1\u3002 \u3000\u3000\u7a97\u5916\u7684\u4e09\u4e2a\u4eba\u90fd\u5728\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u6015\u8fd9\u6bcd\u72d7\u7684\u8eab\u5b50\u88ab\u6211\u4eec\u770b\u5230\uff1f\u201d \u3000\u3000\u201c\u5979\u65e9\u5c31\u88ab\u4eba\u770b\u60ef\u4e86\uff0c\u6ca1\u6709\u7537\u4eba\u770b\u5979\uff0c\u5979\u53cd\u800c\u4f1a\u89c9\u5f97\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u201c\u7830\u201d\u7684\uff0c\u7a97\u6237\u5ffd\u7136\u540c\u65f6\u88ab\u649e\u5f00\u3002 \u3000\u3000\u4e09\u9053\u5f3a\u70c8\u7684\u5149\u67f1\u4ece\u7a97\u5916\u7167\u8fdb\u6765\uff0c\u96c6\u4e2d\u5728\u6797\u4ed9\u513f\u8eab\u4e0a\u3002 \u3000\u3000\u662f\u5b54\u660e\u706f\u7684\u706f\u5149\u3002 \u3000\u3000\u53ea\u80fd\u770b\u5230\u706f\u5149\uff0c\u5374\u770b\u4e0d\u5230\u706f\u5728\u54ea\u91cc\uff0c\u4e5f\u770b\u4e0d\u5230\u4eba\u5728\u54ea\u91cc\u3002 \u3000\u3000\u7729\u76ee\u7684\u706f\u5149\u4eae\u5f97\u4eba\u773c\u775b\u90fd\u5f20\u4e0d\u5f00\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7528\u624b\u6321\u4f4f\u4e86\u773c\u775b\uff0c\u68c9\u88ab\u4ece\u5979\u8eab\u4e0a\u6162\u6162\u7684\u5f80\u4e0b\u6ed1\uff0c\u6e10\u6e10\u9732\u51fa\u4e86\u5979\u7684\u811a\uff0c\u5979\u7684\u817f\u2026\u2026 \u3000\u3000\u5979\u5e76\u6ca1\u6709\u5c06\u8fd9\u6761\u88ab\u62c9\u4f4f\u7684\u610f\u601d\uff0c\u5979\u7684\u786e\u4e0d\u6015\u88ab\u4eba\u770b\u3002 \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\uff0c\u5c06\u8863\u670d\u6454\u8fc7\u53bb\uff0c\u5389\u58f0\u9053\uff1a\u201c\u7a7f\u8d77\u6765\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u773c\u6ce2\u6d41\u8f6c\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u96be\u9053\u8ba4\u4e3a\u6211\u89c1\u4e0d\u5f97\u4eba\uff1f\u201d \u3000\u3000\u5979\u53c8\u5df2\u51e0\u4e4e\u5b8c\u5168\u8d64\u88f8\uff0c\u53c8\u5728\u5a9a\u7b11\u3002 \u3000\u3000\u5979\u53c8\u540c\u65f6\u7528\u51fa\u4e86\u5979\u7684\u4e24\u79cd\u6b66\u5668\u3002 \u3000\u3000\u963f\u98de\u6284\u8d77\u5f20\u51f3\u5b50\uff0c\u6454\u788e\uff0c\u63e1\u7740\u4e86\u4e24\u53ea\u51f3\u811a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8c01\u6562\u8fdb\u6765\uff0c\u6211\u5c31\u8981\u4ed6\u6b7b\uff01\u201d \u3000\u3000\u5916\u9762\u7684\u4e09\u4e2a\u4eba\u53c8\u7b11\u4e86\uff0c\u8fd9\u6b21\u7b11\u58f0\u662f\u4ece\u95e8\u5916\u4f20\u8fdb\u6765\u7684\uff1a\u201c\u4ed6\u5c45\u7136\u8fd8\u60f3\u8981\u4eba\u7684\u547d\u3002\u201d \u3000\u3000\u201c\u5c31\u51ed\u4ed6\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u8c01\u7684\u547d\u4ed6\u90fd\u4f11\u60f3\u8981\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u81f3\u5c11\u8fd8\u80fd\u8981\u4e00\u4e2a\u4eba\u7684\u547d\u2014\u2014\u8981\u4ed6\u81ea\u5df1\u7684\u547d\uff01\u201d \u3000\u3000\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\u5927\u88c2\uff0c\u539a\u6728\u677f\u505a\u6210\u7684\u95e8\u7a81\u7136\u88ab\u6253\u5f97\u7c89\u788e\u3002 \u3000\u3000\u6728\u5c51\u7eb7\u98de\uff0c\u4e09\u4e2a\u4eba\u6162\u6162\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e09\u4e2a\u9ec4\u8863\u4eba\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u5934\u4e0a\u90fd\u6234\u7740\u9876\u7af9\u7b20\uff0c\u7d27\u7d27\u538b\u5728\u7709\u6bdb\u4e0a\uff0c\u63a9\u8d77\u4e86\u9762\u76ee\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u201c\u91d1\u94b1\u5e2e\u201d\u5c5e\u4e0b\u72ec\u7279\u7684\u6807\u5fd7\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u624b\u4e0a\u7f20\u7740\u6839\u91d1\u94fe\uff0c\u94fe\u5b50\u4e24\u7aef\uff0c\u7cfb\u7740\u74dc\u5927\u7684\u94dc\u9524\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u548c\u7b2c\u4e09\u4eba\u7528\u7684\u662f\u5200\u5251\u3002 \u3000\u3000\u9b3c\u5934\u5200\u548c\u4e27\u95e8\u5251\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u7684\u6b66\u5668\u90fd\u5df2\u5728\u624b\uff0c\u4eff\u4f5b\u751f\u6015\u9519\u8fc7\u4f4f\u4f55\u4e00\u4e2a\u6740\u4eba\u7684\u673a\u4f1a\u3002 \u3000\u3000\u963f\u98de\u7a81\u7136\u9547\u5b9a\u4e86\u4e0b\u6765\uff0c\u6b63\u5982\u4e00\u6761\u9965\u997f\u800c\u6124\u6012\u7684\u72fc\uff0c\u5ffd\u7136\u55c5\u5230\u8840\u8165\u6c14\u65f6\uff0c\u53cd\u800c\u4f1a\u9547\u5b9a\u4e0b\u6765 \u4e00\u6837\u3002 \u3000\u3000\u4ed6\u7684\u53cd\u5e94\u867d\u5df2\u6162\uff0c\u4f53\u529b\u867d\u8870\u9000\uff0c\u53ef\u662f\u4ed6\u7684\u672c\u80fd\u8fd8\u672a\u4e27\u5931\u3002 \u3000\u3000\u4ed6\u5df2\u55c5\u5230\u4e86\u8840\u8165\u6c14\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u8fd8\u5728\u7b11\u7740\uff0c\u7b11\u5f97\u66f4\u5a9a\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u2018\u98ce\u96e8\u53cc\u6d41\u661f\u2019\u5411\u677e\u5411\u8235\u4e3b\u5230\u4e86\uff0c\u5931\u8fce\u5931 \u8fce\u3002\u201d \u3000\u3000\u5411\u677e\u624b\u91cc\u7684\u6d41\u661f\u9524\u4e0d\u505c\u7684\u8f7b\u8f7b\u6447\u6446\u7740\uff0c\u4ed6\u7684\u4eba\u5374\u7a33\u5982\u6cf0\u5c71\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5411\u8235\u4e3b\u8fd9\u6b21\u6765\uff0c\u662f\u5949\u4e86\u4e0a\u5b98\u91d1\u8679\u4e4b\u547d\u6765\u6740\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e0a\u5b98\u91d1\u8679\u8fd9\u4e48\u6025\u7740\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u7528\u4e0d\u7740\u7684\u4eba\uff0c\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u731c\u9519\u4e86\uff0c\u4ed6\u5e76\u4e0d\u662f\u4e3a\u4e86\u8fd9\u539f\u56e0\u624d\u60f3\u6740\u6211\u3002\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ed6\u8981\u6740\u6211\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u6015\u6211\u518d\u53bb\u627e\u522b\u7684\u7537\u4eba\uff0c\u4e22\u4ed6\u7684\u9762\u5b50\u3002\u201d \u3000\u3000\u5411\u677e\u51b7\u51b7\u9053\uff1a\u201c\u4e0a\u5b98\u5e2e\u4e3b\u7684\u547d\u4ee4\u4ece\u6765\u7528\u4e0d\u7740\u89e3\u91ca\uff0c\u53ea\u6267\u884c\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u8198\u4e86\u963f\u98de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u6562\u95ef\u5230\u8fd9\u91cc\u6765\u6740\u6211\uff0c\u60f3\u5fc5\u662f\u8ba4\u4e3a\u4ed6\u5df2\u4e0d\u80fd\u4fdd\u62a4\u6211\u3002\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u4ed6\u4e0d\u59a8\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u6267\u5200\u7684\u4eba\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5df2\u4e0d\u5fc5\u8bd5\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6267\u5200\u7684\u4eba\u9053\uff1a\u201c\u4f60\u6562\u5728\u4ed6\u9762\u524d\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u81ea\u7136\u4e5f\u77e5\u9053\u4ed6\u5df2\u4e0d\u80fd\u4fdd\u62a4\u4f60\u4e86\uff0c\u65e2\u7136\u5927\u5bb6\u90fd\u77e5 \u9053\uff0c\u53c8\u4f55\u5fc5\u8bd5\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u786e\u5df2\u8fde\u81ea\u5df1\u90fd\u4fdd\u62a4\u4e0d\u4e86\uff0c\u6211\u4e5f\u5728\u66ff\u4ed6\u96be\u53d7\uff0c\u53ea\u4e0d\u8fc7\u2026 \u2026\u201d \u3000\u3000\u5979\u6162\u6162\u7684\u7ad9\u8d77\u6765\uff0c\u8d64\u88f8\u88f8\u7684\u7ad9\u5728\u706f\u5149\u4e0b\uff0c\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u81ea\u5df1\u662f\u4e0d\u662f\u8fd8\u80fd\u4fdd \u62a4\u81ea\u5df1\u5462\uff1f\u201d \u3000\u3000\u5979\u80f8\u819b\u9a84\u50b2\u7684\u633a\u7acb\uff0c\u817f\u7b14\u76f4\u3002 \u3000\u3000\u5979\u7684\u76ae\u80a4\u5728\u706f\u5149\u4e0b\u770b\u6765\u5c31\u50cf\u662f\u5976\u6cb9\u8272\u7684\u7f0e\u5b50\u3002 \u3000\u3000\u8fd9\u8eab\u6750\u7684\u786e\u503c\u5f97\u5979\u9a84\u50b2\u3002 \u3000\u3000\u963f\u98de\u7684\u8138\u5df2\u56e0\u75db\u82e6\u800c\u626d\u66f2\uff0c\u51b7\u6c57\u5982\u8c46\uff0c\u4e00\u7c92\u7c92\u6ef4\u843d\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u624b\u5728\u81ea\u5df1\u8eab\u4e0a\u8f7b\u629a\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u6740\u4e86\u6211\uff0c\u4e0d\u4f1a\u89c9\u5f97\u53ef\u60dc\u4e48\uff1f\u201d \u3000\u3000\u5411\u677e\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6709\u4e9b\u5973\u4eba\u62ff\u81ea\u5df1\u7684\u8eab\u5b50\u6765\u4ed8\u5e10\uff0c\u4ed8\u8102\u7c89\u7684\u5e10\uff0c\u4ed8\u7ef8\u7f0e\u7684\u5e10 \uff0c\u65e0\u8bba\u5bf9\u8c01\u90fd\u4ece\u4e0d\u5c0f\u6c14\uff0c\u4f46\u4f60\u5374\u4e0d\u540c\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u540c\u3002\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u4f60\u6bd4\u5979\u4eec\u66f4\u5927\u65b9\uff0c\u4f60\u7528\u4f60\u81ea\u5df1\u7684\u8eab\u5b50\u4ed8\u5c0f\u8d39\uff0c\u751a\u81f3\u8fde\u66ff\u4f60\u5f00\u95e8\u7684\u5e97\u5c0f\u4e8c\uff0c\u53ea \u8981\u4f60\u9ad8\u5174\uff0c\u4f60\u90fd\u4f1a\u8ba9\u4ed6\u6ee1\u610f\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5a9a\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u95ee\u6211\u8981\u5c0f\u8d39\uff1f\u201d \u3000\u3000\u5979\u6162\u6162\u7684\u8d70\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u6765\u62ff\u5427\uff0c\u6211\u4ed8\u7684\u5c0f\u8d39\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u4f1a\u5acc\u591a\u7684\u3002\u201d \u3000\u3000\u5411\u677e\u6728\u7acb\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u60f3\u53bb\u52fe\u4ed6\u7684\u8116\u5b50\u3002 \u3000\u3000\u5411\u677e\u5ffd\u7136\u51fa\u624b\uff0c\u9524\u51fb\u80f8\u819b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u843d\u5728\u5e8a\u4e0a\u6014\u4f4f\u4e86\uff01 \u3000\u3000\u5411\u677e\u5934\u4e0a\u7684\u7af9\u7b20\u5df2\u88ab\u6253\u843d\uff0c\u9732\u51fa\u4e86\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4e00\u5f20\u82cd\u767d\u7684\u8138\uff0c\u6ee1\u662f\u76b1\u7eb9\uff0c\u6ca1\u6709\u80e1\u5b50\uff0c\u4e00\u6839\u80e1\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u96be\u602a\u4e0a\u5b98\u91d1\u8679\u8981\u4f60\u4eec\u6765\u6740\u6211\uff0c\u539f\u6765\u4f60\u662f\u4e2a\u9634\u9633\u4eba\u2014\u2014\u4e0d \u7537\u4e0d\u5973\u7684\u9634\u9633\u4eba\u3002\u201d \u3000\u3000\u5411\u677e\u51b7\u51b7\u7684\u76ef\u7740\u5979\uff0c\u9762\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u3002\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u76ee\u5149\u624d\u8f6c\u5411\u963f\u98de\uff0c\u4e00\u5b57\u5b57\u9053\uff1a \u201c\u4f60\u6700\u597d\u51fa\u53bb\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u51fa\u53bb\uff1f\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u60f3\u4fdd\u62a4\u8fd9\u6761\u6bcd\u72d7\uff1f\u201d \u3000\u3000\u963f\u98de\u7684\u624b\u6e10\u6e10\u5782\u843d\u3002 \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u6700\u597d\u51fa\u53bb\uff0c\u6211\u6740\u5979\u7684\u65f6\u5019\uff0c\u4f60\u6700\u597d\u83ab\u8981\u5728\u65c1\u8fb9\u77a7\u7740\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5411\u677e\u72de\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u82e5\u5728\u65c1\u8fb9\u77a7\u7740\uff0c\u4e00\u5b9a\u4f1a\u5410\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\uff0c\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7684\u7b11\u58f0\u5df2\u505c\u6b62\u3002\u5230\u4e86\u8fd9\u65f6\uff0c\u5979\u4e5f\u5df2\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u963f\u98de\u5df2\u51fa\u624b\uff01 \u3000\u3000\u963f\u98de\u7684\u672c\u80fd\u8fd8\u672a\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u9009\u62e9\u7684\u786e\u5b9e\u662f\u6700\u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u53cd\u5e94\u5df2\u6162\uff0c\u4f53\u529b\u5df2\u8870\u3002 \u3000\u3000\u91d1\u5149\u4e00\u95ea\uff0c\u6d41\u661f\u76f8\u98de\u51fa\u3002 \u3000\u3000\u6728\u5c51\u7eb7\u98de\uff0c\u963f\u98de\u624b\u91cc\u7684\u51f3\u5b50\u811a\u5df2\u88ab\u51fb\u5f97\u7c89\u788e\u3002 \u3000\u3000\u5411\u677e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5949\u547d\u6765\u6740\u5979\uff0c\u4e0d\u662f\u6740\u4f60\uff0c\u6211\u4ece\u4e0d\u613f\u591a\u4e8b\uff0c\u6240\u4ee5\u4f60\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u963f\u98de\u7d27\u63e1\u7740\u4e24\u622a\u5df2\u88ab\u6253\u65ad\u4e86\u7684\u6728\u811a\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u5feb\u6df9\u6b7b\u7684\u4eba\u7d27\u63e1\u7740\u4ed6\u7684\u6700\u540e\u4e00\u7ebf\u5e0c\u671b\u3002 \u3000\u3000\u4f46\u8fd9\u53c8\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5e0c\u671b\uff1f \u3000\u3000\u4ed6\u672c\u662f\u6740\u4eba\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u6740\u4eba\uff0c\u522b\u4eba\u6740\u4ed6\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5df2\u4e0d\u80fd\u6740\u4eba\uff0c\u522b\u4eba\u4e5f\u5df2\u4e0d\u5c51\u6740\u4ed6\u3002 \u3000\u3000\u8fd9\u8868\u793a\u4ed6\u5728\u522b\u4eba\u773c\u4e2d\u5df2\u5168\u65e0\u4ef7\u503c\uff0c\u4ed6\u662f\u6b7b\u662f\u6d3b\uff0c\u522b\u4eba\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u8981\u722c\u8d77\u6765\u5f88\u96be\uff0c\u8981\u8dcc\u4e0b\u53bb\u5374\u5f88\u5bb9\u6613\u3002\u201d \u3000\u3000\u963f\u98de\u7a81\u7136\u60f3\u8d77\u4ed6\u53bb\u6551\u674e\u5bfb\u6b22\u7684\u65f6\u5019\uff0c\u548c\u8346\u65e0\u547d\u51b3\u6597\u7684\u65f6\u5019\u2026\u2026 \u3000\u3000\u90a3\u65f6\u4ed6\u5728\u522b\u4eba\u773c\u4e2d\uff0c\u8fd8\u662f\u4e0d\u53ef\u8f7b\u89c6\u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5462\uff1f \u3000\u3000\u90a3\u53ea\u4e0d\u8fc7\u662f\u51e0\u5929\u524d\u7684\u4e8b\uff0c\u4f46\u73b0\u5728\u60f3\u6765\uff0c\u5374\u5df2\u9065\u8fdc\u5f97\u51e0\u4e4e\u65e0\u6cd5\u8bb0\u5fc6\u3002 \u3000\u3000\u5411\u677e\u7684\u58f0\u97f3\u4f3c\u4e4e\u4e5f\u5df2\u9065\u8fdc\uff1a\u201c\u4f60\u8981\u7559\u5728\u8fd9\u91cc\u4e5f\u65e0\u59a8\uff0c\u6211\u5c31\u8981\u4f60\u770b\u770b\u771f\u6b63\u7684\u6740\u4eba\u662f\u4ec0\u4e48\u6837 \u5b50\u7684\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u61c2\u6740\u4eba\u4e48\uff1f\u4f60\u53ea\u6015\u8fd8\u4e0d\u914d\uff01\u201d \u7b2c\u4e03\u5341\u4e94\u7ae0\u3000\u751f\u6b7b\u4e00\u7ebf\u95f4>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u4e94\u7ae0\u3000\u751f\u6b7b\u4e00\u7ebf\u95f4 \u3000\u3000\u7f13\u6162\u7684\u8bed\u58f0\uff0c\u65e2\u65e0\u9ad8\u4f4e\uff0c\u4e5f\u6ca1\u6709\u60c5\u611f\uff0c\u5411\u677e\u662f\u719f\u6089\u8fd9\u79cd\u58f0\u97f3\u7684\uff0c\u53ea\u6709\u8346\u65e0\u547d\u8bf4\u8bdd\u624d\u662f\u8fd9 \u79cd\u58f0\u97f3\uff01 \u3000\u3000\u8346\u65e0\u547d\uff01 \u3000\u3000\u5411\u677e\u9a87\u7136\u56de\u9996\u679c\u7136\u77a7\u89c1\u4e86\u8346\u65e0\u547d\uff01 \u3000\u3000\u4ed6\u7684\u8863\u886b\u5df2\u7834\u65e7\uff0c\u795e\u60c5\u770b\u6765\u4e5f\u5f88\u6194\u60b4\uff0c\u4f46\u4ed6\u7684\u90a3\u53cc\u773c\u775b\u2014\u2014 \u3000\u3000\u6b7b\u7070\u8272\u7684\u773c\u775b\uff0c\u8fd8\u662f\u51b7\u5f97\u50cf\u51b0\uff0c\u8db3\u4ee5\u4ee4\u4efb\u4f55\u4eba\u7684\u8840\u51dd\u7ed3\u3002 \u3000\u3000\u5411\u677e\u907f\u5f00\u4e86\u4ed6\u7684\u773c\u775b\uff0c\u770b\u5230\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u624b\u8fd8\u662f\u7528\u5e03\u60ac\u7740\uff0c\u624b\u7684\u989c\u8272\u5df2\u53d8\u6210\u6b7b\u7070\u8272\uff0c\u5c31\u50cf\u662f\u521a\u4ece\u68fa\u6751\u91cc\u4f38\u51fa\u6765\u7684\u3002 \u3000\u3000\u8fd9\u672c\u662f\u53cc\u6740\u4eba\u7684\u624b\uff0c\u4f46\u73b0\u5728\u5374\u53ea\u80fd\u4ee4\u4eba\u4f5c\u5455\u3002 \u3000\u3000\u5411\u677e\u7b11\u4e86\uff0c\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u867d\u4e0d\u61c2\u6740\u4eba\uff0c\u5374\u8fd8\u80fd\u6740\uff0c\u8346\u5148\u751f\u867d\u61c2\u5f97\u6740\u4eba\uff0c\u53ea\u53ef\u60dc\u6740\u4eba \u5e76\u4e0d\u662f\u7528\u5634\u7684\uff0c\u662f\u8981\u7528\u624b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u77b3\u5b54\u53c8\u5728\u6536\u7f29\uff0c\u76ef\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u5230\u6211\u7684\u624b\uff1f\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u624b\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u6211\u770b\u5230\u7684\u5e76\u4e0d\u662f\u6740\u4eba\u7684\u624b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u53f3\u624b\u4e0d\u80fd\u6740\u4eba\uff1f\u201d \u3000\u3000\u5411\u677e\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u4e5f\u6709\u6839\u591a\u79cd\uff0c\u6709\u4e9b\u4eba\u5bb9\u6613\u6740\uff0c\u6709\u4e9b\u4eba\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u662f\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u5411\u677e\u5ffd\u7136\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u6740\u4e0d\u6b7b\u7684\u90a3\u4e00\u79cd\u3002\u201d \u3000\u3000\u4ed6\u76ee\u4e2d\u5145\u6ee1\u4e86\u4ec7\u6068\uff0c\u50cf\u662f\u5728\u6fc0\u8346\u65e0\u547d\u51fa\u624b\uff0c\u4ed6\u8981\u627e\u4e2a\u6740\u8346\u65e0\u547d\u7684\u7406\u7531\u3002 \u3000\u3000\u8346\u65e0\u547d\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u548c\u4e0a\u5b98\u91d1\u8679\u4e00\u6837\uff0c\u7b11\u7684\u65f6\u5019\u8fdc\u6bd4\u4e0d\u7b11\u65f6\u66f4\u6b8b\u9177\uff0c\u66f4\u53ef\u6015\u3002 \u3000\u3000\u5411\u677e\u7adf\u4e0d\u7531\u81ea\u4e3b\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u539f\u6765\u4f60\u6068\u6211\uff1f\u201d \u3000\u3000\u5411\u677e\u54ac\u7740\u7259\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u6068\u4f60\u7684\u4eba\u53ea\u6015\u8fd8\u5f88\u5c11\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u60f3\u6740\u6211\uff1f\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u60f3\u6740\u4f60\u7684\u4eba\u4e5f\u4e0d\u6b62\u6211\u4e00\u4e2a\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u8981\u7b49\u5230\u73b0\u5728\uff1f\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u8981\u6740\u4eba\u5c31\u5f97\u7b49\u673a\u4f1a\uff0c\u8fd9\u9053\u7406\u4f60\u672c\u8be5\u6bd4\u8c01\u90fd\u660e\u767d\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u73b0\u5728\u673a\u4f1a\u5df2\u6765\u4e86\uff1f\u201d \u3000\u3000\u5411\u677e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u6709\u4e2a\u79d8\u5bc6\u4f60\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5411\u677e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u6b7b\u7070\u8272\u7684\u773c\u775b\u51dd\u6ce8\u7740\u4ed6\u7684\u54bd\u5589\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53f3\u624b\u4e5f\u80fd\u6740\u4eba\u7684\uff0c\u800c\u4e14\u6bd4\u5de6\u624b\u66f4\u5feb \uff01\u201d \u3000\u3000\u201c\u5feb\u201d\u5b57\u51fa\u53e3\uff0c\u5251\u5df2\u523a\u5165\u4e86\u5411\u677e\u7684\u54bd\u5589\uff01 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u770b\u5230\u8fd9\u67c4\u5251\u662f\u4ece\u54ea\u91cc\u62d4\u51fa\u6765\u7684\uff0c\u66f4\u6ca1\u6709\u77a7\u89c1\u5251\u600e\u4e48\u4f1a\u523a\u5165\u5411\u677e\u7684\u54bd\u5589\u3002 \u3000\u3000\u5927\u5bb6\u53ea\u77a7\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u9c9c\u8840\u5df2\u6d8c\u51fa\uff0c\u53ea\u542c\u5230\u201c\u683c\u201d\u7684\u58f0\u97f3\uff0c\u5411\u677e\u7684\u547c\u5438\u5c31\u5df2\u505c\u987f\uff0c\u8fde\u773c \u73e0\u5b50\u90fd\u51e0\u4e4e\u5b8c\u5168\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u9b3c\u5934\u5200\u201d\u548c\u201c\u4e27\u95e8\u5251\u201c\u7684\u773c\u73e0\u5b50\u4e5f\u50cf\u662f\u8981\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u6b65\u6b65\u5411\u540e\u9000\uff0c\u9000\u5230\u95e8\u53e3\u3002 \u3000\u3000\u8346\u65e0\u547d\u6839\u672c\u6ca1\u6709\u56de\u5934\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u5df2\u542c\u5230\u4e86\u6211\u7684\u79d8\u5bc6\uff0c\u8fd8\u60f3\u8d70\uff1f\u201d \u3000\u3000\u5bd2\u5149\u53c8\u4e00\u95ea\uff01 \u3000\u3000\u9c9c\u8840\u98de\u6e85\uff0c\u5728\u706f\u5149\u4e0b\u770b\u6765\u5c31\u50cf\u662f\u4e00\u4e32\u739b\u7483\u73e0\u7ec3\uff0c\u7ea2\u5f97\u90a3\u4e48\u9c9c\u8273\uff0c\u7ea2\u5f97\u90a3\u4e48\u53ef\u7231\u3002 \u3000\u3000\u826f\u836f\u82e6\u53e3\uff0c\u6bd2\u836f\u5374\u5f80\u5f80\u662f\u751c\u7684\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u7684\u4e8b\u5c31\u8fd9\u4e48\u5947\u602a\u2014\u2014\u6700\u53ef\u6015\uff0c\u6700\u4e11\u964b\u7684\u4e1c\u897f\uff0c\u5728\u67d0\u4e00\u5239\u90a3\u95f4\u770b\u6765\uff0c\u5f80\u5f80\u6bd4\u4ec0\u4e48\u90fd \u7f8e\u4e3d\uff0c\u6bd4\u4ec0\u4e48\u90fd\u53ef\u7231\u3002 \u3000\u3000\u6240\u4ee5\u6740\u4eba\u7684\u5251\u5149\u603b\u662f\u5206\u5916\u660e\u4eae\uff0c\u521a\u6d41\u51fa\u7684\u8840\u603b\u662f\u5206\u5916\u9c9c\u8273\u3002 \u3000\u3000\u6240\u4ee5\u6709\u4eba\u8bf4\uff1a\u201c\u7f8e\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u77ac\u95f4\u7684\u611f\u89c9\uff0c\u53ea\u6709\u771f\u5b9e\u624d\u662f\u6c38\u6052\u7684\u3002\u201d \u3000\u3000\u201c\u771f\u5b9e\u201d\uff0c\u7edd\u4e0d\u4f1a\u6709\u7f8e\u3002 \u3000\u3000\u6740\u4eba\u7684\u5229\u5251\u4e5f\u548c\u83dc\u5200\u4e00\u6837\uff0c\u540c\u6837\u662f\u94c1\uff0c\u95ee\u9898\u53ea\u5728\u4f60\u770b\u5f97\u591f\u4e0d\u591f\u6df1\u8fdc\uff0c\u591f\u4e0d\u591f\u900f\u5f7b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4e5f\u6709\u4eba\u8bf4\uff1a\u201c\u6211\u53ea\u8981\u80fd\u628a\u63e1\u4f4f\u90a3\u4e00\u5239\u95f4\u7684\u7f8e\u5c31\u5df2\u8db3\u591f\uff0c\u6c38\u6052\u7684\u4e8b\u4e14\u7559\u5f85\u4e88\u6c38\u6052\uff0c \u6211\u6839\u672c\u4e0d\u5fc5\u7406\u4f1a\u3002\u201d \u3000\u3000\u5c31\u5728\u4e00\u77ac\u95f4\u4ee5\u524d\uff0c\u5411\u677e\u8fd8\u662f\u4eab\u540d\u6b66\u6797\u7684\u201c\u98ce\u96e8\u53cc\u6d41\u661f\u201d\uff0c\u8fd8\u662f\u201c\u91d1\u94b1\u5e2e\u201d\u7b2c\u516b\u5206\u8235\u7684\u8235 \u4e3b\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5df2\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6b7b\u4eba\uff0c\u548c\u522b\u7684\u6b7b\u4eba\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u8346\u65e0\u547d\u5782\u7740\u5934\u671b\u7740\u4ed6\u7684\u5c38\u9996\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u5947\u7279\uff0c\u5c31\u50cf\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u6b7b\u4eba\u4e00\u6837 \u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u76f4\u5230\u73b0\u5728\u624d\u80fd\u4f53\u4f1a\u5230\u201c\u6b7b\u201d\u7684\u611f\u89c9\uff1f \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4e00\u4e2a\u4eba\u53ea\u6709\u5728\u610f\u5174\u8427\u7d22\u65f6\uff0c\u624d\u80fd\u4f53\u4f1a\u5230\u6b7b\u7684\u611f\u89c9\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7ec8\u4e8e\u957f\u957f\u5410\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8fd9\u53e3\u6c14\u5979\u5df2\u8e69\u4e86\u5f88\u4e45\uff0c\u5230\u73b0\u5728\u624d\u603b\u7b97\u5410\u51fa\u6765\u3002 \u3000\u3000\u5979\u779f\u7740\u8346\u65e0\u547d\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u5982\u8bc9\u5982\u6155\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4f1a\u6765\u6551\u6211\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u62ac\u5934\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u6765\u6551\u4f60\u7684\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u6162\u6162\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u77e5\u9053\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u970d\u7136\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u5979\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u6765\u6551\u6211\uff0c\u53ea\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u8981\u6740\u6211\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u76ef\u7740\u5979\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u6068\u4ed6\uff0c\u6240\u4ee5\u53ea\u8981\u662f\u4ed6\u60f3\u505a\u7684\u4e8b\uff0c\u4f60\u5c31\u8981\u7834\u574f\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u76ef\u7740\u5979\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u76f4\u5230\u73b0\u5728\uff0c\u6211\u624d\u603b\u7b97\u77e5\u9053\u4e86\u4f60\u8fd9\u4e2a\u4eba\uff0c\u624d\u77e5\u9053\u4e0a\u5b98\u98de\u4e5f\u662f\u4f60\u6740 \u7684\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u773c\u775b\u5ffd\u7136\u79fb\u5f00\uff0c\u79fb\u5411\u638c\u4e2d\u7684\u5251\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5f97\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u6740\u6211\uff0c\u56e0\u4e3a\u4f60\u82e5\u6740\u4e86\u6211\uff0c\u5c82\u975e\u6b63\u5982\u4e86\u4e0a\u5b98\u91d1 \u8679\u7684\u5fc3\u613f\uff1f\u201d \u3000\u3000\u5979\u751c\u751c\u7684\u7b11\u7740\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u975e\u4f46\u4e0d\u4f1a\u6740\u6211\uff0c\u4f60\u8fd8\u4f1a\u5e26\u6211\u8d70\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u5e26\u4f60\u8d70\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u65e2\u4e0d\u80fd\u8ba9\u6211\u6b7b\u5728\u4e0a\u5b98\u91d1\u8679\u624b\u4e0a\uff0c\u53c8\u4e0d\u613f\u8ba9\u6211\u6cc4\u9732\u4f60\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u4f60 \u53ea\u6709\u5e26\u6211\u8d70\u3002\u201d \u3000\u3000\u5979\u58f0\u97f3\u66f4\u6e29\u67d4\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u5fc3\u7518\u60c5\u613f\u8ddf\u7740\u79f0\u8d70\uff0c\u65e0\u8bba\u4f60\u8981\u5230\u54ea\u91cc\uff0c\u6211\u90fd\u8ddf\u7740\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u62ac\u5934\u77a7\u4e86\u963f\u98de\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u76f4\u5230\u73b0\u5728\u624d\u53d1\u73b0\u6709\u963f\u98de\u8fd9\u4e48\u4e2a\u4eba\u5b58\u5728\u3002 \u3000\u3000\u963f\u98de\u5374\u5df2\u4f3c\u5fd8\u4e86\u81ea\u5df1\u7684\u5b58\u5728\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e5f\u779f\u4e86\u963f\u98de\u4e00\u773c\uff0c\u5ffd\u7136\u8d70\u8fc7\u53bb\uff0c\u4e00\u53e3\u53e3\u6c34\u91cd\u91cd\u7684\u553e\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u5979\u5e76\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u5979\u5df2\u4e0d\u5fc5\u518d\u8bf4\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7ec8\u4e8e\u8ddf\u7740\u8346\u65e0\u547d\u8d70\u4e86\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u53e3\u6c34\u5e72\u4e86\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u7a97\u7eb8\u53d1\u767d\uff0c\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u963f\u98de\u8fd8\u662f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u5df2\u8eba\u4e86\u4e0b\u6765\uff0c\u5c31\u8eba\u5728\u8840\u6cca\u4e2d\uff0c\u5c38\u4f53\u65c1\u3002 \u3000\u3000\u4ed6\u548c\u6b7b\u4ea1\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u5df2\u53ea\u5269\u4e0b\u4e00\u6761\u7ebf\u2026\u2026 \u3000\u3000\u201c\xd7\xd7\u65e5\uff0c\u8c03\u65f6\uff0c\u51fa\u897f\u57ce\u5341\u91cc\uff0c\u957f\u4ead\u5916\u6797\u4e0b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679 \u3000\u3000\u51ac\u5929\u7ec8\u4e8e\u6765\u4e86\uff0c\u8fde\u6811\u4e0a\u6700\u540e\u4e00\u7247\u67af\u53f6\u4e5f\u5df2\u88ab\u897f\u98ce\u5439\u843d\u3002 \u3000\u3000\u8fd9\u5c01\u4fe1\u7684\u989c\u8272\u5c31\u548c\u67af\u53f6\u4e00\u6837\uff0c\u662f\u9ec4\u7684\uff0c\u5374\u662f\u79cd\u5e26\u7740\u79cd\u6b7b\u5473\u7684\u9ec4\u2014\u2014\u9ec4\u5f97\u6ca1\u6709\u751f\u547d\uff0c\u9ec4\u5f97 \u53ef\u6015\u3002 \u3000\u3000\u8fd9\u5c01\u4fe1\u4e0a\u53ea\u5199\u7740\u8fd9\u5341\u51e0\u4e2a\u5b57\uff0c\u7b80\u5355\uff0c\u660e\u767d\uff0c\u4e5f\u6b63\u5982\u4e0a\u5b98\u91d1\u8679\u6740\u4eba\u7684\u65b9\u6cd5\u4e00\u6837\uff0c\u7edd\u6ca1\u6709\u5e9f \u8bdd\u3002 \u3000\u3000\u4fe1\u662f\u5e97\u4f19\u9001\u6765\u7684\uff0c\u4ed6\u62ff\u7740\u4fe1\u7684\u624b\u4e00\u76f4\u5728\u53d1\u6296\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5b59\u5c0f\u7ea2\u62ff\u7740\u8fd9\u5c01\u4fe1\uff0c\u4f3c\u4e4e\u611f\u89c9\u5230\u4e00\u9635\u9635\u6740\u6c14\u900f\u5165\u80cc\u810a\uff0c\u518d\u4f20\u5230\u5979\u624b\u4e0a\uff0c\u5979\u7684\u624b\u4e5f \u5728\u53d1\u51b7\u3002 \u3000\u3000\u201c\u540e\u5929\uff0c\u5c31\u662f\u540e\u5929\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u770b\u8fd9\u7687\u5386\uff0c\u540e\u5929\u4e0d\u662f\u597d\u65e5\u5b50\u3002\u8bf8\u4e8b\u4e0d\u5b9c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6740\u4eba\u53c8\u4f55\u5fc5\u9009\u597d\u65e5\u5b50\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u6ce8\u7740\u4ed6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u7a81\u7136\u5927\u58f0\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u6740\u4ed6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5634\u95ed\u4e0a\uff0c\u7b11\u5bb9\u4e5f\u6e10\u6e10\u6d88\u5931\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\uff0c\u674e\u5bfb\u6b22\u8fd8\u731c\u4e0d\u51fa\u5979\u51fa\u53bb\u5e72\u4ec0\u4e48\uff0c\u5979\u5df2\u6367\u7740\u7b14\u58a8\u7eb8\u781a \u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u78e8\u597d\u58a8\uff0c\u94fa\u597d\u7eb8\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u59cb\u7ec8\u6ca1\u6709\u518d\u77a7\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u6211\u5199\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6709\u4e9b\u53d1\u6014\uff0c\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u672a\u4e86\u7684\u5fc3\u613f\uff1f\u8fd8\u6709\u4ec0\u4e48\u672a\u505a\u5b8c\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4eff\u4f5b\u5f88\u5e73\u9759\uff0c\u4f46\u63d0\u7740\u7b14\u7684\u624b\u5374\u6709\u4e9b\u53d1\u6296\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u8981\u6211\u8bf4\uff1f\u6211\u8fd8\u6ca1\u6709\u6b7b\u5440\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7b49\u4f60\u6b7b\u4e86\uff0c\u5c31\u8bf4\u4e0d\u51fa\u4e86\u3002\u201d \u3000\u3000\u5979\u4e00\u76f4\u5782\u7740\u5934\uff0c\u77a7\u7740\u624b\u91cc\u7684\u7b14\uff0c\u4f46\u5374\u8fd8\u662f\u65e0\u6cd5\u907f\u5f00\u674e\u5bfb\u6b22\u7684\u76ee\u5149\u3002 \u3000\u3000\u5979\u773c\u775b\u5df2\u6709\u4e9b\u6e7f\u4e86\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u4f60\u90fd\u53ef\u4ee5\u8bf4\u51fa\u6765\uff0c\u8b6c\u5982\u8bf4\u2014\u2014\u963f\u98de\uff0c\u4f60 \u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u5bf9\u4ed6\u8bf4\u7684\uff1f\u8fd8\u6709\u4ec0\u4e48\u4e8b\u8981\u4e3a\u4ed6\u505a\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u5ffd\u7136\u9732\u51fa\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6ca1\u6709\uff1f\u4ec0\u4e48\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u8981\u4ed6\u4e0d\u53bb\u6740\u522b\u4eba\uff0c\u5374\u65e0\u6cd5\u8981\u4ed6\u4e0d\u53bb\u7231\u522b\u4eba\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u522b\u4eba\u82e5\u8981\u6740\u4ed6\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u9178\u695a\uff0c\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u6709\u8c01\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u65e2\u7136\u80af\u653e\u4ed6\u8d70\uff0c\u5c31\u7edd\u4e0d\u4f1a\u518d\u6740\u4ed6\uff0c\u5426\u5219\u4ed6\u73b0\u5728\u65e9\u5c31\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9065\u6ce8\u7740\u7a97\u5916\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u65e0\u8bba\u591a\u957f\u7684\u68a6\uff0c\u90fd\u603b\u6709\u9192\u7684\u65f6\u5019\uff0c\u7b49\u4ed6\u6e05\u9192\u7684\u90a3\u5929\uff0c\u4ec0 \u4e48\u4e8b\u4ed6\u81ea\u5df1\u90fd\u4f1a\u660e\u767d\u7684\uff0c\u73b0\u5728\u6211\u8bf4\u4e86\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u90a3\u4e48\u5979\u5462\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5979\u4f3c\u5df2\u7528\u5c3d\u5168\u8eab\u529b\u6c14\u624d\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u81ea\u7136\u77e5\u9053\u5979\u8bf4\u7684\u201c\u5979\u201d\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u76ee\u4e2d\u7684\u75db\u82e6\u4e4b\u8272\u66f4\u6df1\uff0c\u5ffd\u7136\u8d70\u8fc7\u53bb\uff0c\u7528\u529b\u63a8\u5f00\u4e86\u7a97\u6237\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u7740\u5934\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u82e5\u6709\u4ec0\u4e48\u8bdd\uff0c\u6709\u4ec0\u4e48\u4e8b\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7a81\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5979\u6d3b\u7740\uff0c\u81ea\u7136\u4f1a\u6709\u4eba\u7167\u987e\u5979\uff0c\u5979\u6b7b\u4e86\uff0c\u4e5f\u6709\u4eba\u57cb\u846c\uff0c\u4ec0\u4e48\u90fd\u7528\u4e0d\u7740\u6211\u6765\u5173 \u5fc3\uff0c\u6211\u6b7b\u4e86\u5bf9\u5979\u53ea\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4eff\u4f5b\u4e5f\u5f88\u5e73\u9759\uff0c\u4f46\u5374\u59cb\u7ec8\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6562\u56de\u5934\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u671b\u7740\u4ed6\u7626\u524a\u7684\u80cc\u5f71\uff0c\u4e00\u6ef4\u6cea\u73e0\uff0c\u6ef4\u5728\u7eb8\u4e0a\u3002 \u3000\u3000\u5979\u6084\u6084\u7684\u64e6\u5e72\u4e86\u773c\u6cea\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4f60\u603b\u6709\u4e9b\u8bdd\u8981\u7559\u4e0b\u6765\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\u5bf9\u6211\u8bf4\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6211\u8bf4\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8bf4\u4e86\uff0c\u6211\u5c31\u8bb0\u4e0b\u6765\u4e86\uff0c\u4f60\u82e5\u6b7b\u4e86\uff0c\u6211\u5c31\u4e00\u4ef6\u4ef6\u66ff\u4f60\u53bb\u505a\uff0c\u7136\u540e\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u970d\u7136\u8f6c\u8fc7\u8eab\uff0c\u76ef\u7740\u5979\uff0c\u9053\uff1a\u201c\u7136\u540e\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7136\u540e\u6211\u5c31\u6b7b\uff01\u201d \u3000\u3000\u5979\u633a\u7740\u80f8\uff0c\u76f4\u89c6\u7740\u674e\u5bfb\u6b22\uff0c\u4e0d\u518d\u9003\u907f\uff0c\u4e5f\u4e0d\u518d\u9690\u7792\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u8981\u6b7b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u4e0d\u6b7b\uff0c\u56e0\u4e3a\u4f60\u82e5\u6b7b\u4e86\uff0c\u6211\u6d3b\u7740\u4e00\u5b9a\u6bd4\u6b7b\u66f4\u96be\u53d7\u3002\u201d \u3000\u3000\u5979\u59cb\u7ec8\u76f4\u89c6\u7740\u674e\u5bfb\u6b22\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\u3002 \u3000\u3000\u5979\u7684\u795e\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u5e73\u9759\uff0c\u5f88\u9547\u5b9a\uff0c\u65e0\u8bba\u8c01\u90fd\u53ef\u770b\u51fa\u5979\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8fd9\u79cd\u51b3\u5fc3\u65e0\u8bba\u8c01\u90fd \u6ca1\u6cd5\u5b50\u6539\u53d8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5fc3\u53c8\u5f00\u59cb\u7ede\u75db\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u5f2f\u4e0b\u8170\u5267\u70c8\u7684\u54b3\u55fd\u8d77\u6765\u3002 \u3000\u3000\u7b49\u4ed6\u54b3\u5b8c\u4e86\uff0c\u5b59\u5c0f\u7ea2\u624d\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u4f60\u82e5\u8981\u6211\u6d3b\u7740\uff0c\u4f60\u81ea\u5df1\u5c31\u4e0d\u80fd\u6b7b\u2026\u2026\u4e0a \u5b98\u91d1\u8679\u4e5f\u5e76\u4e0d\u662f\u4e00\u5b9a\u8981\u627e\u4f60\u51b3\u6597\uff0c\u4ed6\u5bf9\u4f60\u59cb\u7ec8\u6709\u51e0\u5206\u754f\u60e7\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u51b2\u8fc7\u53bb\uff0c\u62c9\u4f4f\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u8d70\uff0c\u8d70\u5f97\u8fdc\u8fdc\u7684\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u7ba1\uff0c\u6211 \u2026\u2026\u6211\u53ef\u4ee5\u5e26\u4f60\u56de\u5bb6\uff0c\u90a3\u5730\u65b9\u4ece\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4e0a\u5b98\u91d1\u8679\u5c31\u7b97\u8fd8\u662f\u60f3\u6765\u627e\u4f60\uff0c\u4e5f\u4f11\u60f3\u627e\u5f97\u5230\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u9759\u9759\u7684\u77a7\u7740\u5979\u3002 \u3000\u3000\u6709\u98ce\u5439\u8fc7\uff0c\u4e00\u9635\u70df\u96fe\u98d8\u8fc7\u6765\uff0c\u8ff7\u6f2b\u4e86\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u82cd\u8001\u7684\u58f0\u97f3\u5df2\u54cd\u8d77\uff0c\u5e26\u7740\u53f9\u606f\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u600e\u4e48\u8bf4\uff0c\u4ed6\u90fd\u4e0d\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5507\uff0c\u8dfa\u7740\u811a\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4e0d\u4f1a\u8d70\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u80af\u8d70\u7684\u90a3\u79cd\u4eba\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u8fd9\u4e48\u6837\u5bf9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6014\u4e86\u534a\u664c\uff0c\u5ffd\u7136\u626d\u8f6c\u8eab\uff0c\u63a9\u9762\u8f7b\u6ce3\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u53f9\u9053\uff1a\u201c\u524d\u8f88\u6cf3\u2026\u2026\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u610f\u601d\uff0c\u53ef\u662f\u2026\u2026\u6211\u53ea\u80fd\u8981\u5979\u4e0d\u53bb\u6740\u4eba\uff0c\u5374\u65e0 \u6cd5\u8981\u5979\u4e0d\u53bb\u7231\u4eba\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u7231\uff0c\u8fd9\u4ef6\u4e8b\u672c\u5c31\u662f\u8c01\u90fd\u65e0\u6cd5\u52c9\u5f3a\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u5f00\u59cb\u54b3\u55fd\uff0c\u54b3\u55fd\u5f97\u66f4\u5267\u70c8\u3002 \u3000\u3000\u201c\u51fa\u897f\u57ce\u5341\u91cc\uff0c\u957f\u4ead\u5916\u6797\u4e0b\u3002\u201d \u3000\u3000\u4ead\uff0c\u662f\u516b\u89d2\u4ead\uff0c\u5c31\u5728\u5c71\u811a\u4e0b\u7684\u6811\u6797\u5916\u3002 \u3000\u3000\u6797\u5df2\u67af\uff0c\u516b\u89d2\u4ead\u680f\u6746\u4e0a\u7684\u7ea2\u6f06\u4e5f\u5df2\u5265\u843d\u3002 \u3000\u3000\u897f\u98ce\u8083\u6740\uff0c\u5927\u5730\u8427\u8427\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5f98\u5f8a\u5728\u6797\u4e0b\uff0c\u51e0\u4e4e\u5c06\u8fd9\u91cc\u6bcf\u4e00\u5bf8\u571f\u5730\u90fd\u8e0f\u8fc7\u3002 \u3000\u3000\u201c\u540e\u5929\uff0c\u5c31\u662f\u540e\u5929\u3002\u201d \u3000\u3000\u5915\u9633\u5df2\u897f\uff0c\u53c8\u662f\u4e00\u5929\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u540e\u5929\uff0c\u5c31\u5728\u8fd9\u91cc\uff0c\u5c31\u5728\u8fd9\u5915\u9633\u897f\u4e0b\u7684\u65f6\u5019\uff0c\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\u4e4b\u95f4\u6240\u6709\u7684\u6069\u6028\u90fd\u5c06\u4e86\u7ed3 \u3002 \u3000\u3000\u90a3\u4e5f\u8bb8\u5c31\u662f\u6b66\u6797\u4e2d\u6709\u53f2\u4ee5\u6765\u6700\u60ca\u5fc3\u52a8\u9b44\u7684\u4e00\u6218\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u62ac\u8d77\u5934\u4e00\u5915\u9633\u6ee1\u5929\uff0c\u8273\u4e3d\u5982\u6614\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5728\u4e00\u4e2a\u5782\u6b7b\u7684\u4eba\u773c\u4e2d\uff0c\u8fd9\u6c38\u6052\u7684\u5915\u9633\u662f\u5426\u8fd8\u4f1a\u540c\u6837\u5a07\u8273\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u548c\u5b59\u5c0f\u7ea2\u4e00\u76f4\u9759\u9759\u7684\u5750\u5728\u4ead\u5b50\u91cc\uff0c\u6ca1\u6709\u53bb\u6253\u6270\u4ed6\u3002\u5b59\u5c0f\u7ea2\u7a81\u7136\u95ee\u9053\uff1a\u201c\u51b3\u6597\u7684 \u65f6\u5019\u8fd8\u672a\u5230\uff0c\u4ed6\u5148\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u9ad8\u624b\u95f4\u7684\u51b3\u6597\uff0c\u4e0d\u4f46\u8981\u770b\u6b66\u529f\u4e4b\u5f3a\u5f31\uff0c\u8fd8\u8981\u770b\u5929\u65f6\u3001\u5730\u5229\u3001\u4eba\u548c\uff0c\u4e0a\u5b98 \u91d1\u8679\u9009\u62e9\u8fd9\u91cc\u4f5c\u6218\u573a\uff0c\u5f53\u7136\u6709\u4ed6\u7684\u7528\u610f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u60f3\u5fc5\u5bf9\u8fd9\u91cc\u7684\u5730\u5f62\u5f88\u719f\uff0c\u800c\u4e14\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u5148\u5230\u8fd9\u91cc\u6765\u8bbe\u4e0b\u57cb\u4f0f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\u674e\u5bfb\u6b22\u4e5f\u4e00\u5b9a\u8981\u5148\u5230\u8fd9\u91cc\u6765\u77a7\u77a7\uff0c\u5148\u719f\u6089\u8fd9\u91cc\u5730\u5f62\uff0c\u518d\u770b\u770b\u4e0a\u5b98\u91d1\u8679 \u4f1a\u5728\u4ec0\u4e48\u5730\u65b9\u8bbe\u57cb\u4f0f\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53e4\u6765\u7684\u540d\u5c06\uff0c\u5728\u5927\u6218\u4e4b\u524d\uff0c\u4e5f\u5fc5\u5b9a\u90fd\u4f1a\u5230\u6218\u573a\u4e0a\u53bb\u5de1\u89c6\u4e00\u904d\uff0c\u65e0 \u8bba\u54ea\u4e00\u79cd\u6218\u4e89\uff0c\u82e5\u6709\u4e00\u65b9\u5148\u5360\u4e86\u5730\u5229\uff0c\u5c31\u5360\u4e86\u4f18\u52bf\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u76f4\u8981\u5728\u8fd9\u91cc\u901b\u6765\u901b\u53bb\u5462\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u8fd9\u4e48\u901b\u6765\u901b\u53bb\u5f53\u7136\u4e5f\u6709\u76ee\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u8981\u5148\u5c06\u8fd9\u91cc\u6bcf\u4e00\u5bf8\u571f\u5730\u90fd\u8d70\u4e00\u904d\uff0c\u770b\u770b\u8fd9\u91cc\u7684\u4e0a\u8d28\u662f\u575a\u786c\uff0c\u8fd8\u662f\u67d4\u8f6f \uff1f\u662f\u5e72\u71e5\uff0c\u8fd8\u662f\u6f6e\u6e7f\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u56e0\u4e3a\u571f\u8d28\u7684\u4e0d\u540c\uff0c\u53ef\u4ee5\u5f71\u54cd\u8f7b\u529f\uff0c\u4f60\u540c\u6837\u4f7f\u51fa\u4e03\u5206\u529b\uff0c\u5728\u8f6f\u800c\u6f6e\u6e7f\u7684\u5730 \u4e0a\u82e5\u662f\u53ea\u80fd\u8dc3\u8d77\u4e24\u4e08\uff0c\u5728\u786c\u800c\u5e72\u71e5\u7684\u5730\u4e0a\u5c31\u80fd\u8dc3\u8d77\u4e24\u4e08\u4e94\u5bf8\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u76f8\u5dee\u5f97\u4e5f\u4e0d\u591a\u5440\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ad8\u624b\u76f8\u4e89\uff0c\u662f\u8fde\u4e00\u5206\u4e00\u5bf8\u90fd\u5dee\u4e0d\u5f97\u7684\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u8d70\u4e86\u8fc7\u6765\uff0c\u7ad9\u5728\u4ead\u5916\uff0c\u9762\u5bf9\u7740\u5915\u9633\u7167\u8000\u4e0b\u7684\u67af\u6797\uff0c\u5446\u5446\u7684\u51fa\u8d77\u795e\u6765\uff0c\u4e5f\u4e0d\u77e5 \u5728\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u6084\u6084\u95ee\u9053\uff1a\u201c\u4ed6\u7ad9\u5728\u8fd9\u91cc\u53d1\u5446\uff0c\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u7b2c\u4e03\u5341\u516d\u7ae0\u3000\u9ad8\u660e\u7684\u624b\u6bb5>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u516d\u7ae0\u3000\u9ad8\u660e\u7684\u624b\u6bb5 \u3000\u3000\u5b59\u8001\u5148\u751f\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u540e\u5929\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u4e0a\u5b98\u91d1\u8679\u5fc5\u5b9a\u5df2\u5148\u5230\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u600e\u89c1\u5f97\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u56e0\u4e3a\u5148\u6765\u7684\u4eba\uff0c\u5c31\u6709\u6743\u5148\u5360\u636e\u6700\u4f73\u5730\u52bf\uff0c\u4e0a\u5b98\u91d1\u8679\u5f53\u7136\u4e0d\u80af\u9519\u8fc7\u8fd9\u673a\u4f1a \u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u674e\u5bfb\u6b22\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u4ed6\u4e89\u5148\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4ece\u4e0d\u613f\u548c\u522b\u4eba\u4e89\u5148\uff0c\u4e5f\u8bb8\u2026\u2026\u4ed6\u8fd8\u6709\u522b\u7684\u7528\u610f\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5c0f\u674e\u63a2\u82b1\u5e76\u4e0d\u662f\u4e2a\u666e\u901a\u4eba\uff0c\u4ed6\u7684\u7528\u610f\uff0c\u6709\u65f6\u8fde\u6211\u90fd\u731c\u4e0d\u900f\u3002 \u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\u9053\uff1a\u201c\u4f3c\u6211\u770b\u6765\uff0c\u8fd9\u91cc\u6240\u6709\u7684\u5730\u65b9\u90fd\u5dee\u4e0d\u591a\u2026\u2026\u6211\u5b9e\u5728\u770b\u4e0d\u51fa\u6700\u4f73\u5730\u52bf\u5728 \u54ea\u91cc\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5c31\u5728\u73b0\u5728\u4ed6\u7ad9\u7740\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u7ad9\u7684\u8fd9\u5730\u65b9\u53c8\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u7ad9\u5728\u8fd9\u91cc\uff0c\u674e\u5bfb\u6b22\u52bf\u5fc5\u8981\u5728\u4ed6\u5bf9\u9762\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u51b3\u6597\u7684\u65f6\u5019\uff0c\u6b63\u662f\u592a\u9633\u4e0b\u5c71\u7684\u65f6\u5019\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u62a2\u7740\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u5915\u9633\u5f80\u8fd9\u8fb9\u7167\u8fc7\u53bb\uff0c\u7ad9\u5728\u90a3\u8fb9\u7684\u4eba\uff0c\u96be\u514d\u88ab\u9633\u5149\u523a\u7740\u773c\u73e0 \uff0c\u53ea\u8981\u4ed6\u773c\u775b\u4e00\u5239\u90a3\u770b\u4e0d\u89c1\uff0c\u5c31\u7ed9\u4e86\u5bf9\u65b9\u6740\u4ed6\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u65e2\u7136\u4e00\u5b9a\u4f1a\u7ad9\u5728\u8fd9\u5730\u65b9\uff0c\u4ed6\u7ad9\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u7ad9\u5728\u8fd9\u91cc\uff0c\u624d\u80fd\u53d1\u73b0\u8fd9\u5730\u65b9\u6709\u4ec0\u4e48\u5f31\u70b9\uff0c\u624d\u80fd\u51b3\u5b9a\u81ea\u5df1\u8981\u7ad9\u5728\u4ec0\u4e48\u5730 \u65b9\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u770b\uff0c\u5915\u9633\u7167\u5728\u67af\u6797\u4e0a\uff0c\u4e5f\u6709\u95ea\u5149\uff0c\u56e0\u4e3a\u67af\u679d\u4e0a\u5df2\u6709\u79cb\u971c\uff0c\u6240\u4ee5\u7ad9\u5728\u8fd9 \u91cc\u7684\u4eba\uff0c\u773c\u775b\u4e5f\u6709\u88ab\u95ea\u5149\u523a\u7740\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8fd9\u65f6\u674e\u5bfb\u6b22\u5df2\u8d70\u5230\u5bf9\u9762\u7684\u4e00\u682a\u6811\u4e0b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u76ee\u5149\u4e0d\u7531\u81ea\u4e3b\u8ddf\u7740\u4ed6\u77a7\u4e86\u8fc7\u53bb\uff0c\u5ffd\u7136\u89c9\u5f97\u4e00\u9635\u5149\u8292\u523a\u773c\u2014\u2014\u90a3\u68f5\u6811\u4e0a\u7684\u79ef\u971c\u663e \u7136\u6700\u591a\uff0c\u6298\u5149\u7684\u89d2\u5ea6\u4e5f\u6700\u597d\uff0c\u6240\u4ee5\u53cd\u5149\u4e5f\u5c31\u5f3a\u70c8\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u5fae\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u8fd8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u674e\u5bfb\u6b22\u7a81\u7136\u4e00\u63a0\u4e0a\u6811\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u98de\u63a0\uff0c\u5982\u79cb\u96c1\u56de\u7a7a\uff0c\u5728\u6bcf\u6839\u67af\u679d \u4e0a\u90fd\u70b9\u4e86\u70b9\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u9053\uff1a\u201c\u4e16\u4e0a\u53ea\u77e5\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u5374\u4e0d\u77e5\u4ed6\u8f7b\u529f\u4e4b\u9ad8\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4 \u5f97\u4e0a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f46\u4ed6\u8fd9\u53c8\u662f\u5728\u5e72\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u662f\u5728\u8bd5\u63a2\u90a3\u8fb9\u7684\u67af\u679d\u662f\u5426\u575a\u7262\uff0c\u5bb9\u4e0d\u5bb9\u6613\u6298\u65ad\uff0c\u8fd9\u53c8\u6709\u4e24\u79cd\u4f5c\u7528\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54ea\u4e24\u79cd\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u4ed6\u6015\u4e0a\u5b98\u91d1\u8679\u5728\u67af\u679d\u4e0a\u505a\u624b\u811a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u76b1\u7709\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u624b\u811a\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5f53\u4ed6\u9762\u5bf9\u7740\u4e0a\u5b98\u91d1\u8679\u65f6\uff0c\u6811\u4e0a\u7684\u67af\u679d\u82e5\u662f\u7a81\u7136\u65ad\u4e86\uff0c\u5c31\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u67af\u679d\u65ad\u4e86\uff0c\u81ea\u7136\u5c31\u4f1a\u6389\u4e0b\u6765\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u6389\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5f53\u7136\u662f\u6389\u5728\u5730\u4e0a\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u5ffd\u7136\u4e00\u4eae\uff0c\u5f88\u5feb\u7684\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u6389\u5728\u4ed6\u9762\u524d\uff0c\u4e5f\u8bb8\u5c31\u6389\u5728\u4ed6\u5934\u4e0a\uff0c\u4ed6\u5c31\u96be \u514d\u4f1a\u5206\u5fc3\uff0c\u4e00\u5206\u5fc3\u4e0a\u5b98\u91d1\u8679\u5c31\u53c8\u6709\u4e86\u6740\u4ed6\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u5230\u4e86\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u4ed6\u53ea\u6709\u5f80\u6811\u4e0a\u9000\uff0c\u4ee5\u8f7b\u529f\u6765\u6273\u56de\u52a3\u52bf \uff0c\u90a3\u65f6\u6811\u68a2\u5c31\u6210\u4e86\u4ed6\u4eec\u7684\u6218\u573a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u5fc5\u987b\u5c06\u6bcf\u4e00\u68f5\u6811\u7684\u60c5\u51b5\u90fd\u5148\u63a2\u6d4b\u4e00\u904d\uff0c\u5c31\u6b63\u5982\u4ed6\u63a2\u6d4b\u8fd9\u91cc\u7684\u571f\u8d28\u4e00 \u6837\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u603b\u7b97\u660e\u767d\u4e86\uff0c\u539f\u6765\u51b3\u6597\u4e4b\u524d\u8fd8\u6709\u8fd9\u4e48\u591a\u5b66\u95ee\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u505a\u5230\u9ad8\u6df1\u65f6\uff0c\u5c31\u662f\u79cd\u5b66\u95ee\uff0c\u5c31\u8fde\u505a\u8863\u670d\uff0c\u7092\u83dc\uff0c\u4e5f\u662f\u4e00\u6837 \u3002\u201d \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u51b3\u6597\u4e4b\u671f\u867d\u7136\u5728\u540e\u5929\uff0c\u5176\u5b9e\u8fd8\u5728\u4ed6\u4eec\u7b2c\u4e00\u6b21\u89c1 \u9762\u65f6\u5c31\u5df2\u5f00\u59cb\uff0c\u8fd9\u6bb5\u65f6\u5019\u624d\u662f\u771f\u6b63\u8003\u9a8c\u4ed6\u4eec\u7ec6\u5fc3\uff0c\u8010\u529b\uff0c\u667a\u6167\u7684\u65f6\u5019\u3002\u4ed6\u4eec\u7684\u80dc\u8d1f\uff0c\u5728\u8fd9\u6bb5 \u65f6\u5019\u91cc\u5c31\u5df2\u51b3\u5b9a\uff0c\u5230\u4e86\u771f\u6b63\u51fa\u624b\u65f6\uff0c\u4e00\u5239\u90a3\u95f4\u5c31\u53ef\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u9053\uff1a\u201c\u4f46\u522b\u4eba\u5374\u53ea\u80fd\u770b\u5230\u90a3\u4e00\u77ac\u95f4\u7684\u4e8b\uff0c\u6240\u4ee5\u4eba\u4eec\u5e38\u8bf4\u2018\u6b66\u6797\u9ad8\u624b\u4e00\u62db\u4e89\u2019\uff0c \u53c8\u8c01\u77e5\u4ed6\u4eec\u4e3a\u4e86\u90a3\u4e00\u62db\u66fe\u7ecf\u82b1\u4e86\u591a\u5c11\u5de5\u592b\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u76ee\u4e2d\u5ffd\u7136\u9732\u51fa\u4e00\u79cd\u8427\u7d22\u4e4b\u610f\uff0c\u6572\u71c3\u4e86\u706b\u77f3\uff0c\u70b9\u7740\u4e86\u70df\u6597\uff0c\u671b\u7740\u70df\u6597\u91cc\u95ea\u52a8\u7684\u706b \u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u771f\u6b63\u7684\u9ad8\u624b\u6d3b\u5728\u4e16\u4e0a\uff0c\u5fc5\u5b9a\u662f\u5bc2\u5bde\u7684\uff0c\u56e0\u4e3a\u522b\u4eba\u53ea\u80fd\u770b\u5230\u4ed6\u4eec\u8f89\u714c\u7684\u4e00 \u9762\uff0c\u5374\u770b\u4e0d\u5230\u4ed6\u4eec\u6240\u727a\u7272\u7684\u4ee3\u4ef7\uff0c\u6240\u4ee5\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u80fd\u4e86\u89e3\u4ed6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u7740\u5934\u5f04\u7740\u8863\u89d2\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u662f\u4e0d\u662f\u9700\u8981\u522b\u4eba\u4e86\u89e3\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u64a9\u8d77\u4e86\u8863\u895f\uff0c\u811a\u5c16\u8f7b\u8f7b\u70b9\u5730\uff0c\u5237\u7684\uff0c\u63a0\u4e0a\u4e86\u516b\u89d2\u4ead\u9876\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u957f\u957f\u55b7\u51fa\u4e86\u53e3\u70df\uff0c\u53f9\u9053\uff1a\u201c\u522b\u4eba\u90fd\u4ee5\u4e3a\u674e\u5bfb\u6b22\u662f\u4e2a\u8131\u7565\u884c\u8ff9\uff0c\u758f\u5ffd\u5927\u610f\u7684\u4eba\uff0c \u53c8\u6709\u8c01\u80fd\u770b\u5230\u4ed6\u5c0f\u5fc3\u4ed4\u7ec6\u4f2a\u4e00\u9762\uff0c\u5230\u4e86\u771f\u6b63\u91cd\u8981\u7684\u5173\u5934\uff0c\u4ed6\u771f\u662f\u4e00\u70b9\u5730\u65b9\u90fd\u4e0d\u80af\u653e\u8fc7\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u7740\u5934\uff0c\u53f9\u606f\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4e3a\u4ed6\u653e\u8fc7\u7684\u5df2\u5927\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u5b59\u8001\u5148\u751f\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6218\u65e2\u7136\u65e9\u5df2\u5f00\u59cb\uff0c\u4ee5\u4f60\u8001\u4eba\u5bb6\u770b\uff0c\u5230\u73b0\u5728\u4e3a \u6b62\u4ed6\u4eec\u662f\u8c01\u5360\u4e86\u4f18\u52bf\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u8c01\u4e5f\u6ca1\u6709\u5360\u5230\u4f18\u52bf\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53c8\u5f00\u59cb\u7528\u529b\u53bb\u54ac\u5979\u81ea\u5df1\u7684\u5634\u5507\u3002 \u3000\u3000\u5979\u5fc3\u4e71\u7684\u65f6\u5019\uff0c\u5c31\u4f1a\u54ac\u81ea\u5df1\u7684\u5634\u5507\uff0c\u5fc3\u8d8a\u4e71\uff0c\u54ac\u5f97\u8d8a\u91cd\u3002 \u3000\u3000\u73b0\u5728\u5979\u51e0\u4e4e\u5df2\u5c06\u5634\u5507\u54ac\u7834\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u770b\u2026\u2026\u4e0a\u5b98\u91d1\u8679\u5bf9\u81ea\u5df1\u597d\u50cf\u6bd4\u8f83\u6709\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u53ea\u56e0\u8fd1\u5e74\u6765\u4ed6\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u90fd\u662f\u65e0\u5f80\u4e0d\u5229\uff0c\u4e00\u5e06\u98ce\u987a\uff0c\u53ef\u662f\uff0c \u4ed6\u513f\u5b50\u7684\u6b7b\u5bf9\u4ed6\u5374\u662f\u4e2a\u5f88\u5927\u7684\u6253\u51fb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd8\u6709\u8346\u65e0\u547d\uff0c\u8346\u65e0\u547d\u4e00\u8d70\uff0c\u4ed6\u7684\u635f\u5931\u4e5f\u5f88\u5927\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u6025\u7740\u8981\u627e\u674e\u5bfb\u6b22\u51b3\u6597\uff0c\u4e3a\u7684\u5c31\u662f\u6015\u81ea\u5df1\u7684\u4fe1\u5fc3\u6d88\u5931\u3002\u201d \u3000\u3000\u4ed6\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u4e00\u6218\u4e0d\u4f46\u5173\u7cfb\u4ed6\u4e24\u4eba\u7684\u751f\u6b7b\u80dc\u8d1f\uff0c\u4e5f\u5173\u7cfb\u7740\u6574 \u4e2a\u6b66\u6797\u7684\u547d\u8fd0\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u5173\u7cfb\u8fd9\u4e48\u5927\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u4e00\u56e0\u4e3a\u8fd9\u4e00\u6218\u4e0a\u5b98\u91d1\u8679\u82e5\u662f\u80dc\u4e86\uff0c\u4ed6\u5bf9\u81ea\u5df1\u7684\u4fe1\u5fc3\u5fc5\u5b9a\u66f4\u5f3a\uff0c\u505a\u4e8b\u5fc5\u5b9a\u66f4 \u6ca1\u6709\u987e\u5fcc\uff0c\u5230\u4e86\u90a3\u65f6\uff0c\u4e16\u4e0a\u53ea\u6015\u4e5f\u771f\u6ca1\u6709\u4eba\u80fd\u5236\u5f97\u4f4f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u773c\u73e0\u5b50\u8f6c\u52a8\u7740\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u5ffd\u7136\u89c9\u5f97\u8fd9\u4e00\u6218\u4ed6\u662f\u5fc5\u5b9a\u80dc\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\uff0c\u4ed6\u7684\u98de\u5200\u4ece\u672a\u5931\u624b\u8fc7\uff01\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u4e5f\u4ece\u672a\u8d25\u8fc7\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5df2\u4e0d\u54ac\u5634\u5507\u4e86\uff0c\u62bf\u7740\u5634\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u83ab\u5fd8\u4e86\uff0c\u4ed6\u66fe\u7ecf\u8d25\u8fc7\u4e00\u6b21\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u2018\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u60a0\u60a0\u9053\uff1a\u201c\u90a3\u5929\uff0c\u5728\u6d1b\u9633\u57ce\u5916\u7684\u957f\u4ead\u91cc\uff0c\u4ed6\u5c82\u975e\u5c31\u66fe\u7ecf\u8d25\u5728\u4f60\u8001\u4eba\u5bb6\u624b\u4e0b\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u6c42\u8fc7\u4f60\u8001\u4eba\u5bb6\u4ec0\u4e48\uff0c\u73b0\u5728\uff0c\u6211\u53ea\u6c42\u4f60\u8001\u4eba\u5bb6\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53c8\u55b7\u51fa\u53e3\u70df\uff0c\u5c06\u81ea\u5df1\u7684\u773c\u775b\u85cf\u5728\u70df\u96fe\u91cc\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u53ea\u6c42\u4f60\u8001\u4eba\u5bb6\u5343\u4e07\u83ab\u8981\u8ba9\u674e\u5bfb\u6b22\u6b7b\uff0c\u5343\u4e07\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u6251\u8fc7\u53bb\uff0c\u8dea\u5728\u5979\u7237\u7237\u819d\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u53ea\u6709\u4f60\u8001\u4eba\u5bb6\u4e00\u4e2a\u80fd\u5236\u5f97\u4f4f\u4e0a\u5b98\u91d1\u8679\uff0c \u53ea\u6709\u4f60\u8001\u4eba\u5bb6\u4e00\u4e2a\u4eba\u80fd\u6551\u4ed6\uff0c\u4f60\u8001\u4eba\u5bb6\u603b\u8be5\u77e5\u9053\uff0c\u4ed6\u82e5\u6b7b\u4e86\uff0c\u6211\u4e5f\u6ca1\u6cd5\u5b50\u6d3b\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u70df\u5df2\u6563\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u7684\u773c\u775b\u91cc\u5374\u4eff\u4f5b\u8fd8\u7559\u7740\u4e00\u5c42\u96fe\u3002 \u3000\u3000\u50cf\u79cb\u5929\u7684\u96fe\uff0c\u51c4\u51c9\u3001\u8427\u7d22\u2026\u2026 \u3000\u3000\u4f46\u4ed6\u5634\u89d2\u5374\u5e26\u7740\u7b11\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u9065\u89c6\u7740\u8fdc\u65b9\uff0c\u8f7b\u629a\u7740\u5b59\u5c0f\u7ea2\u7684\u5934\u53d1\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u662f\u6211\u5b59\u5973\u4e2d\u6700\u8c03\u76ae\u7684\u4e00\u4e2a\uff0c\u4f60 \u82e5\u6b7b\u4e86\uff0c\u4ee5\u540e\u8fd8\u6709\u8c01\u4f1a\u6765\u62d4\u6211\u7684\u80e1\u5b50\uff0c\u63ea\u6211\u7684\u5934\u53d1\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u8df3\u4e86\u8d77\u6765\uff0c\u96c0\u8dc3\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e86\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u542b\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u6765\u8bf4\u53bb\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u7b49\u6211\u8bf4\u8fd9\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u8138\u7ea2\u4e86\uff0c\u5782\u7740\u5934\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u603b\u8be5\u77e5\u9053\uff0c\u5973\u5927\u4e0d\u4e2d\u7559\uff0c\u5973\u513f\u7684\u5fc3\uff0c\u603b\u662f\u5411 \u5916\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5927\u7b11\u9053\uff1a\u201c\u4f46\u4f60\u7684\u8138\u76ae\u82e5\u8fd8\u662f\u8fd9\u4e48\u539a\uff0c\u4eba\u5bb6\u6562\u4e0d\u6562\u8981\u4f60\uff0c\u6211\u53ef\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u5634\u51d1\u5230\u4ed6\u8033\u65c1\uff0c\u6084\u6084\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u4e0d\u8981\u6211\u4e5f\u6709\u6cd5\u5b50\u8981\u4ed6\u8981\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u62b1\u4f4f\u4e86\u5979\uff0c\u5c31\u597d\u50cf\u5df2\u56de\u5230\u5341\u51e0\u5e74\u524d\uff0c\u5979\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\u7684\u65f6\u5019\uff0c\u62b1\u7740\u5979\u67d4\u58f0 \u9053\uff1a\u201c\u4f60\u662f\u6211\u6700\u559c\u6b22\u7684\u5b59\u5973\uff0c\u4f46\u5374\u592a\u8c03\u76ae\uff0c\u80c6\u5b50\u4e5f\u592a\u5927\uff0c\u6211\u4e00\u76f4\u62c5\u5fc3\u4f60\u627e\u4e0d\u5230\u5a46\u5bb6\uff0c\u73b0\u5728\u4f60 \u603b\u7b97\u627e\u5230\u4e86\u4e2a\u4f60\u81ea\u5df1\u559c\u6b22\u7684\uff0c\u6211\u4e5f\u66ff\u4f60\u559c\u6b22\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5403\u5403\u7b11\u9053\uff1a\u201c\u6211\u627e\u5230\u4ed6\uff0c\u7b97\u6211\u8fd0\u6c14\uff0c\u4ed6\u627e\u5230\u6211\uff0c\u4e5f\u662f\u4ed6\u7684\u8fd0\u6c14\uff0c\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c \u8fd9\u5929\u4e0b\u4e5f\u8bb8\u8fd8\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u7b80\u76f4\u8fde\u4e00\u4e2a\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u4f0f\u5728\u5979\u7237\u7237\u819d\u4e0a\uff0c\u5fc3\u91cc\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u6109\u5feb\uff0c\u8bf4\u4e0d\u51fa\u7684\u5f97\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e0d\u4f46\u6709\u4e2a\u6700\u503c\u5f97\u9a84\u50b2\u7684\u7956\u7236\uff0c\u4e5f\u6709\u4e2a\u6700\u503c\u5f97\u9a84\u50b2\u7684\u610f\u4e2d\u4eba\u3002 \u3000\u3000\u4eb2\u60c5\uff0c\u7231\u60c5\uff0c\u5979\u5df2\u5168\u90fd\u6709\u4e86\uff0c\u4e00\u4e2a\u5973\u4eba\u8fd8\u60f3\u8981\u6c42\u4ec0\u4e48\u522b\u7684\u5462\uff1f \u3000\u3000\u5979\u89c9\u5f97\u81ea\u5df1\u7b80\u76f4\u5df2\u662f\u4e16\u4e0a\u6700\u5feb\u4e50\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u89c9\u5f97\u524d\u9014\u5145\u6ee1\u4e86\u5149\u660e\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u5927\u5730\u5374\u5df2\u6697\u4e86\u4e0b\u6765\uff0c\u5149\u660e\u5df1\u88ab\u9ed1\u6697\u541e\u6ca1\u3002 \u3000\u3000\u5979\u5374\u5b8c\u5168\u6ca1\u6709\u611f\u89c9\u5230\u3002 \u3000\u3000\u201c\u7231\u60c5\u4ee4\u4eba\u76f2\u76ee\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u542c\u6765\u867d\u7136\u5f88\u4fd7\u6c14\uff0c\u4f46\u5374\u7684\u786e\u6709\u5b83\u6c38\u6052\u4e0d\u53d8\u7684\u9053\u7406\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6b64\u523b\u82e5\u80fd\u5f20\u5f00\u773c\u775b\uff0c\u5c31\u4f1a\u53d1\u73b0\u5979\u7237\u7237\u76ee\u4e2d\u7684\u60b2\u54c0\u548c\u75db\u82e6\u662f\u591a\u4e48\u6df1\u9042\u2014\u2014\u522b\u4eba\u5c31\u7b97 \u80fd\u770b\u5230\uff0c\u4e5f\u6c38\u8fdc\u731c\u4e0d\u51fa\u4ed6\u60b2\u75db\u662f\u4e3a\u4e86\u4ec0\u4e48\u539f\u56e0\uff1f \u3000\u3000\u591c\u4e34\uff0c\u98ce\u66f4\u51b7\u3002 \u3000\u3000\u4e07\u7c41\u65e0\u58f0\u53ea\u5269\u4e0b\u67af\u679d\u4f34\u7740\u8870\u8349\u5728\u98ce\u4e2d\u4f4e\u4f4d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u4eba\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u8dd1\u51fa\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5728\u4e0a\u9762\u5e72\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u4e0b\u6765\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u5e94\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4ed6\u4eba\u5462\uff1f \u3000\u3000\u516b\u89d2\u4ead\u4e0a\u96be\u9053\u771f\u6709\u4ec0\u4e48\u9669\u6076\u7684\u57cb\u4f0f\uff1f\u674e\u5bfb\u6b22\u96be\u9053\u5df2\u906d\u4e86\u6bd2\u624b\uff1f \u3000\u3000\u516b\u89d2\u4ead\u4e0a\u94fa\u7684\u662f\u7ea2\u8272\u7684\u74e6\uff0c\u8fd8\u6709\u4e2a\u91d1\u8272\u7684\u9876\u3002 \u3000\u3000\u91d1\u9876\u4e0a\u5374\u6446\u7740\u4e2a\u5c0f\u5c0f\u7684\u94c1\u5323\u5b50\uff0c\u7528\u4e00\u6839\u9ec4\u8272\u7684\u5e03\u5e26\u6346\u4f4f\u3002 \u3000\u3000\u94c1\u5323\u5b50\u662f\u5f88\u666e\u901a\u7684\u4e00\u79cd\uff0c\u65e2\u6ca1\u6709\u96d5\u7eb9\u88c5\u9970\uff0c\u4e5f\u6ca1\u6709\u673a\u5173\u6d88\u606f\uff0c\u4f60\u82e5\u6253\u5f00\u8fd9\u94c1\u5323\u5b50\uff0c\u91cc\u9762 \u7edd\u4e0d\u4f1a\u98de\u51fa\u4e00\u6280\u5f2f\u7bad\u6765\u5c04\u7a7f\u4f60\u7684\u54bd\u5589\u3002 \u3000\u3000\u201c\u4f46\u8fd9\u94c1\u5323\u5b50\u600e\u4e48\u4f1a\u5230\u4e86\u516b\u89d2\u4ead\u7684\u9876\u4e0a\u5462\uff1f\u201d \u3000\u3000\u94c1\u5323\u5b50\u91cc\u53ea\u6709\u4e00\u675f\u5934\u53d1\u3002 \u3000\u3000\u5934\u53d1\u4e5f\u662f\u5f88\u666e\u901a\u7684\u5934\u53d1\uff0c\u9ed1\u7684\uff0c\u5f88\u957f\uff0c\u65e2\u4e0d\u9999\uff0c\u4e5f\u4e0d\u81ed\uff0c\u5c31\u8ddf\u4e16\u4e0a\u6210\u5343\u4e07\u4e2a\u666e\u901a\u4eba\u7684\u5934 \u53d1\u4e00\u6837\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u4e00\u76f4\u5728\u5446\u5446\u7684\u76ef\u7740\u8fd9\u675f\u5934\u53d1\u770b\uff0c\u5b59\u5c0f\u7ea2\u53eb\u4e86\u4ed6\u51e0\u6b21\uff0c\u4ed6\u90fd\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u8fd9\u5934\u53d1\u7a76\u7adf\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8138\u8272\u5f88\u6c89\u91cd\uff0c\u773c\u775b\u4e5f\u6709\u70b9\u53d1\u7ea2\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4ece\u672a\u770b\u8fc7\u4ed6\u8fd9\u6837\u5b50\uff0c\u5c31\u8fde\u4ed6\u559d\u9189\u7684\u65f6\u5019\uff0c\u4ed6\u773c\u775b\u8fd8\u662f\u4eae\u7684\u3002 \u3000\u3000\u4ed6\u600e\u4f1a\u53d8\u6210\u8fd9\u526f\u6837\u5b50\uff1f \u3000\u3000\u5934\u53d1\u5c31\u653e\u5728\u4ead\u5b50\u91cc\u7684\u77f3\u684c\u4e0a\uff0c\u674e\u5bfb\u6b22\u8fd8\u662f\u5728\u76ef\u7740\u8fd9\u675f\u5934\u53d1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u7684\u5934\u53d1\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u53e3\u7b54\uff0c\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u53ef\u80fd\u6709\u8fd9\u6837\u7684\u5934\u53d1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u6837\u957f\u7684\u5934\u53d1\uff0c\u4e00\u5b9a\u662f\u5973\u4eba\u7684\u3002\u201d \u3000\u3000\u5979\u81ea\u5df1\u5f53\u7136\u4e5f\u77e5\u9053\u8fd9\u5224\u65ad\u5e76\u4e0d\u6b63\u786e\uff0c\u56e0\u4e3a\u7537\u4eba\u7684\u5934\u53d1\u4e5f\u5f88\u957f\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u8eab\u4f53\u53d1\u80a4\uff0c\u53d7\u4e4b\u7236\u6bcd\uff0c\u4e0d\u53ef\u635f\u4f24\u3002\u201d \u3000\u3000\u8c01\u526a\u77ed\u5934\u53d1\uff0c\u8c01\u5c31\u662f\u4e0d\u5b5d\u3002 \u3000\u3000\u6709\u4eba\u8bf4\u6545\u4e8b\uff0c\u8bf4\u5230\u4e00\u4e2a\u4eba\u5973\u626e\u7537\u88c5\u5ffd\u7136\u88ab\u4eba\u53d1\u73b0\u662f\u957f\u5934\u53d1\uff0c\u522b\u4eba\u5c31\u7acb\u523b\u53d1\u89c9\u5979\u662f\u5973\u4eba\u4e86 \u3002 \u3000\u3000\u8bf4\u8fd9\u79cd\u6545\u4e8b\u7684\u4eba\u8111\u7b4b\u4e00\u5b9a\u4e0d\u4f1a\u5f88\u53d1\u8fbe\uff0c\u56e0\u4e3a\u8fd9\u79cd\u4e8b\u6700\u591a\u53ea\u80fd\u9a97\u9a97\u5c0f\u5b69\u5b50\u2014\u2014\u5947\u602a\u7684\u662f\uff0c \u5374\u504f\u504f\u8fd8\u6709\u4eba\u8981\u8bf4\u8fd9\u79cd\u6545\u4e8b\uff0c\u4e0d\u4f46\u8bf4\uff0c\u751a\u81f3\u8fd8\u4ece\u4e0d\u53d8\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8dfa\u4e86\u8dfa\u811a\uff0c\u8bf4\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u51e0\u6839\u5934\u53d1\u800c\u5df2\uff0c\u6709\u4ec0\u4e48\u597d\u5947\u602a\u7684\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5947\u602a\uff0c\u800c\u4e14\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54ea\u70b9\u5947\u602a\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5f88\u591a\u5947\u602a\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5934\u53d1\u600e\u4f1a\u5728\u94c1\u5323\u5b50\u91cc\uff1f\u94c1\u5323\u5b50\u600e\u4f1a\u5728\u4ead\u5b50\u9876\u4e0a\uff1f\u662f\u8c01\u5c06\u5b83\u653e\u4e0a\u53bb\u7684\uff1f\u6709 \u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u82e5\u662f\u6211\u731c\u5f97\u4e0d\u9519\uff0c\u8fd9\u5fc5\u5b9a\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u6770\u4f5c\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5931\u58f0\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\uff1f\u4ed6\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u2018\u5c31\u4e3a\u4e86\u8981\u8ba9\u674e\u5bfb\u6b22\u770b\u5230\u8fd9\u675f\u5934\u53d1\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ed6\u7b97\u51c6\u4e86\u674e\u5bfb\u6b22\u4e00\u5b9a\u4f1a\u5148\u6765\u63a2\u6d4b\u6218\u573a\uff0c\u4e5f\u7b97\u51c6\u4e86\u4ed6\u4e00\u5b9a\u4f1a\u5230\u4ead\u5b50\u4e0a\u53bb\uff0c \u6240\u4ee5\u5c31\u5148\u5c06\u8fd9\u5323\u5b50\u7559\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u5934\u53d1\u53c8\u6709\u4ec0\u4e48\u7279\u522b\u5462\uff1f\u5c31\u7b97\u770b\u5230\u4e86\u4e5f\u4e0d\u4f1a\u600e\u4e48\u6837\u5440\uff0c\u4ed6\u8fd9\u4e48\u6837\u505a\u5c82 \u975e\u5f88\u6ed1\u7a3d\u3002\u201d \u3000\u3000\u5979\u5634\u91cc\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u4e5f\u5ffd\u7136\u611f\u89c9\u5230\u6709\u4e9b\u4e0d\u5bf9\u4e86\uff0c\u5f88\u4e0d\u5bf9\u3002 \u3000\u3000\u50cf\u4e0a\u5b98\u91d1\u8679\u8fd9\u79cd\u4eba\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u505a\u6ed1\u7a3d\u7684\u4e8b\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u773c\u775b\u76ef\u7740\u674e\u5bfb\u6b22\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u662f\u8c01\u7684\u5934\u53d1\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5389\u58f0\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u786e\u5b9a\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u5982\u6b64\u4e25\u5389\uff0c\u674e\u5bfb\u6b22\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u80fd\u786e\u5b9a\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u674e\u5bfb\u6b22\u5f00\u53e3\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u8fd9\u4e48\u6837\u505a\uff0c\u5c31\u662f\u8981\u4f60\u8ba4\u4e3a\u8fd9\u5934\u53d1\u662f\u6797\u8bd7\u97f3\u7684 \uff0c\u8981\u4f60\u8ba4\u4e3a\u5979\u5df1\u843d\u5165\u4ed6\u7684\u638c\u63e1\uff0c\u8981\u4f60\u7684\u5fc3\u4e0d\u5b9a\uff0c\u4ed6\u624d\u597d\u6740\u4f60\uff0c\u4f60\u4e3a\u4f55\u8981\u4e0a\u4ed6\u7684\u5f53\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u62a2\u7740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6797\u59d1\u5a18\u82e5\u771f\u7684\u5df2\u843d\u5165\u4ed6\u624b\u91cc\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u7d22\u6027\u5f53\u9762\u6765\u8981\u80c1\u4f60\uff1f \u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e0d\u80fd\u8fd9\u4e48\u6837\u505a\u2014\u2014\u522b\u4eba\u80fd\uff0c\u4ed6\u5374\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u82e5\u6709\u4eba\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u662f\u7528\u8fd9\u79cd\u624b\u6bb5\u624d\u80dc\u4e86\u674e\u5bfb\u6b22\u7684\uff0c\u5c82\u975e\u8981\u88ab\u5929\u4e0b\u4eba \u803b\u7b11\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f46\u73b0\u5728\u4ed6\u4ec0\u4e48\u4e5f\u6ca1\u6709\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u8ba9\u4f60\u770b\u5230\u4e86\u4e00\u675f\u5934\u53d1\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u4ed6\u7684\u624b\u6bb5\u9ad8\u660e\u4e4b\u5904\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u5934\u53d1\u4e5f\u8bb8\u5e76\u4e0d\u662f\u5979\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e5f\u8bb8\u4e0d\u662f\uff0c\u4e5f\u8bb8\u662f\u2026\u2026\u8c01\u4e5f\u4e0d\u80fd\u786e\u5b9a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u82e5\u5b8c\u5168\u4e0d\u53bb\u7406\u4f1a\uff0c\u5c31\u5f53\u505a\u6839\u672c\u6ca1\u6709\u770b\u5230\uff0c\u4ed6\u7684\u5fc3\u8ba1\u5c82\u975e\u5c31\u767d\u8d39\u4e86\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u5df2\u7ecf\u770b\u5230\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u4ec0\u4e48\u4e5f\u6ca1\u6709\u8bf4\uff0c\u6240\u4ee5\u4f60\u624d\u6000\u7591\uff0c\u5c31\u56e0\u4e3a\u4ed6\u7b97\u51c6\u4e86\u4f60\u4f1a\u6000\u7591\uff0c\u6240\u4ee5 \u624d\u8fd9\u4e48\u6837\u505a\u3002\u4f60\u4e5f\u660e\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff0c\u5374\u504f\u504f\u8fd8\u8981\u843d\u4eba\u4ed6\u7684\u5708\u5957\u3002\u201d \u3000\u3000\u5979\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u8352\u5510\u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u8ba9\u6211\u9047\u5230\uff1f\u201d \u7b2c\u4e03\u5341\u4e03\u7ae0\u3000\u5174\u4e91\u5e84\u7684\u79d8\u5bc6>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u4e03\u7ae0\u3000\u5174\u4e91\u5e84\u7684\u79d8\u5bc6 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4e16\u4e8b\u672c\u5c31\u5982\u6b64\uff0c\u6709\u4e9b\u4e8b\u4f60\u7eb5\u7136\u660e\u77e5\u662f\u4e0a\u5f53\uff0c\u8fd8\u662f\u8981\u53bb\u4e0a\u8fd9\u4e2a \u5f53\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u82e5\u6709\u4eba\u80fd\u4ee4\u6211\u5fc3\u52a8\uff0c\u6211\u4e5f\u4e00\u6837\u4f1a\u4e0a\u5f53\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u8dfa\u4e86\u8dfa\u811a\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u4f60\u4eec\u4e0a\u5f53\uff0c\u6211\u504f\u4e0d\u4e0a\u5f53\u2026\u2026\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5df2\u4e0a\u5f53\u4e86\uff0c\u56e0\u4e3a\u4f60\u4e5f\u5728\u6000\u7591\u8fd9\u5934\u53d1\u662f\u6797\u59d1\u5a18\u7684\uff0c\u4f60\u7684\u5fc3\u4e5f\u5df2\u4e71 \u4e86\uff0c\u73b0\u5728\u4f60\u7740\u548c\u4eba\u51b3\u6597\uff0c\u5bf9\u65b9\u7684\u6b66\u529f\u7eb5\u7136\u4e0d\u5982\u4f60\uff0c\u4f60\u4e5f\u5fc5\u8d25\u65e0\u7591\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u53ef\u662f\u600e\u4e48\u6837\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u76ee\u7684\u5c31\u662f\u8981\u674e\u5bfb\u6b22\u5fc3\u4e71\uff0c\u65e0\u8bba\u674e\u5bfb\u6b22\u662f\u76f8\u4fe1\u4e5f\u597d\uff0c\u662f\u6000\u7591\u4e5f\u597d\uff0c\u53ea\u8981\u4ed6\u53bb\u60f3 \u8fd9\u4ef6\u4e8b\uff0c\u4e0a\u5b98\u91d1\u8679\u7684\u76ee\u7684\u5c31\u5df2\u8fbe\u5230\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u600e\u80fd\u4e0d\u60f3\uff1f \u3000\u3000\u90a3\u672c\u662f\u4ed6\u9b42\u7275\u68a6\u7d20\u7684\u4eba\uff0c\u4ed6\u51e0\u65f6\u5fd8\u8bb0\u8fc7\u5979\uff1f \u3000\u3000\u4ed6\u5c31\u7b97\u660e\u77e5\u8fd9\u5e76\u4e0d\u662f\u5979\u7684\u5934\u53d1\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u7275\u80a0\u6302\u809a\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u5df2\u8ba9 \u4ed6\u60f3\u8d77\u4e86\u5979\u3002 \u3000\u3000\u95ee\u9898\u5e76\u4e0d\u5728\u5934\u53d1\u662f\u8c01\u7684\uff0c\u800c\u5728\u674e\u5bfb\u6b22\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff1f \u3000\u3000\u8fd9\u4e00\u9488\u6b63\u662f\u9488\u5bf9\u674e\u5bfb\u6b22\u800c\u53d1\u7684\uff0c\u82e5\u662f\u7528\u5728\u522b\u4eba\u8eab\u4e0a\uff0c\u4e5f\u8bb8\u5c31\u5b8c\u5168\u6ca1\u6709\u7528\u4e86\uff0c\u56e0\u4e3a\u522b\u4eba\u6839 \u672c\u5c31\u4e0d\u4f1a\u60f3\u5f97\u8fd9\u4e48\u591a\uff0c\u8fd9\u4e48\u8fdc\u3002 \u3000\u3000\u8fd9\u624d\u662f\u4e0a\u5b98\u91d1\u8679\u6700\u53ef\u6015\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u6c38\u8fdc\u77e5\u9053\u5bf9\u4ec0\u4e48\u4eba\u8be5\u7528\u4ec0\u4e48\u6837\u7684\u624b\u6bb5\uff0c\u4ed6\u7684\u624b\u6bb5\u5728\u522b\u4eba\u770b\u6765\u4e5f\u8bb8\u6709\u70b9\u4e0d\u5b9e\u9645\uff0c\u751a\u81f3\u6709 \u70b9\u8352\u5510\uff0c\u4f46\u5374\u6c38\u8fdc\u6700\u6709\u6548\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5f88\u61c2\u5f97\u5175\u6cd5\u4e2d\u6700\u5965\u5999\u7684\u56db\u4e2a\u5b57\uff1a\u201c\u653b\u5fc3\u4e3a\u4e0a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9760\u5728\u680f\u6746\u5750\u4e86\u4e0b\u6765\uff0c\u5c31\u5750\u5728\u5730\u4e0a\uff0c\u5c06\u56db\u80a2\u5c3d\u91cf\u653e\u677e\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5b59\u8001\u5148\u751f\u548c\u5b59\u5c0f\u7ea2\u5374\u90fd\u77e5\u9053\u4ed6\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48\uff1a\u201c\u5230\u5174\u4e91\u5e84\u53bb\uff0c\u770b \u770b\u6797\u8bd7\u97f3\u8fd8\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u5728\u957f\u9014\u8dcb\u6d89\u4e4b\u524d\uff0c\u4ed6\u5fc5\u987b\u5148\u5c06\u75b2\u52b3\u6062\u590d\u3002 \u3000\u3000\u6bcf\u6b21\u4ed6\u4f5c\u4e86\u91cd\u5927\u7684\u51b3\u5b9a\u4e4b\u540e\uff0c\u90fd\u8981\u4f7f\u81ea\u5df1\u7684\u8eab\u5fc3\u5c3d\u91cf\u677e\u5f1b\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u4e60\u60ef\u3002 \u3000\u3000\u8fd9\u65e0\u7591\u662f\u4e2a\u597d\u4e60\u60ef\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u54ac\u5f97\u5f88\u7528\u529b\u3002 \u3000\u3000\u201c\u539f\u6765\u4ed6\u8fd8\u662f\u5fd8\u4e0d\u4e86\u5979\uff0c\u8fd8\u662f\u5c06\u5979\u770b\u6210\u6bd4\u4ec0\u4e48\u90fd\u91cd\u8981\uff0c\u5979\u5728\u4ed6\u5fc3\u91cc\u7684\u5730\u4f4d\uff0c\u65e0\u8bba\u8c01\u90fd\u4e0d \u80fd\u4ee3\u66ff\u2014\u2014\u5c31\u8fde\u6211\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u773c\u5708\u5df2\u7ea2\u4e86\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u53bb\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u6709\u65f6\u4e0d\u56de\u7b54\u5c31\u662f\u56de\u7b54\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u53f9\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u8981\u53bb\uff0c\u56e0\u4e3a\u4ed6\u53ea\u6709\u53bb\u770b\u4e00\u770b\uff0c\u624d\u80fd\u5fc3\u5b89\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u5979\u82e5\u5df2\u4e0d\u5728\u90a3\u91cc\u4e86\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u5149\u9065\u89c6\u7740\u4ead\u5916\u7684\u591c\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u65e0\u8bba\u5979\u5728\u4e0d\u5728\uff0c\u6211\u90fd\u8981\u53bb\u770b\u770b\uff0c\u7136\u540e\u6211\u624d\u80fd \u4e0b\u51b3\u5fc3\uff0c\u51b3\u5b9a\u5e94\u8be5\u600e\u4e48\u6837\u505a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u82e5\u53bb\u4e86\uff0c\u624d\u771f\u6b63\u843d\u5165\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u5708\u5957\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u8fd9\u4e48\u6837\u505a\u6700\u5927\u7684\u76ee\u7684\u5c31\u662f\u8981\u4f60\u5230\u5174\u4e91\u5e84\u53bb\u4e00\u8d9f\uff0c\u51b3\u6218\u7684\u65f6\u5019\u5c31\u5728\u540e\u5929\uff0c \u8fd9\u91cc\u79bb\u5174\u4e91\u5e84\u5e76\u4e0d\u8fd1\uff0c\u4f60\u5c31\u7b97\u80fd\u5728\u4e24\u5929\u4e4b\u5185\u8d76\u56de\u6765\uff0c\u5230\u4e86\u51b3\u6218\u65f6\u4f53\u529b\u4e5f\u5df2\u4e0d\u652f\uff0c\u4ed6\u5728\u8fd9\u4e24\u5929 \u5185\u5374\u4e00\u5b9a\u4f1a\u5c3d\u91cf\u4f11\u606f\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u4ee5\u9038\u5f85\u52b3\uff0c\u4f60\u5728\u4e24\u5929\u4e4b\u5185\u5954\u6ce2\u6570\u767e\u91cc\u4e4b\u540e\uff0c\u518d\u53bb\u8fce\u6218\uff0c \u8fd9\u4e00\u6218\u7684\u80dc\u8d1f\uff0c\u4e5f\u5c31\u4e0d\u95ee\u53ef\u77e5\u4e86\uff0c\u4f55\u51b5\uff0c\u4ed6\u5728\u90a3\u91cc\u8bf4\u4e0d\u5b9a\u8fd8\u53e6\u6709\u57cb\u4f0f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u4f60\u7eb5\u7136\u660e\u77e5\u4e0d\u80fd\u505a\uff0c\u4e5f\u662f\u975e\u505a\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u560e\u58f0\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u53bb\u4e86\uff0c\u5c31\u7b49\u4e8e\u662f\u62ff\u4f60\u81ea\u5df1\u7684\u6027\u547d\u53bb\u5192\u9669\uff0c\u5979\u5bf9\u4f60\u96be\u9053\u5c31\u771f\u7684\u8fd9 \u4e48\u91cd\u8981\uff1f\u6bd4\u4f60\u81ea\u5df1\u7684\u6027\u547d\u8fd8\u91cd\u8981\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u62ac\u8d77\u5934\uff0c\u51dd\u6ce8\u7740\u5979\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u773c\u775b\u5df2\u6e7f\u4e86\uff0c\u626d\u8f6c\u5934\uff0c\u907f\u5f00\u4e86\u4ed6\u7684\u76ee\u5149\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u5b57\u5b57\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53ea\u60f3\u4f60\u660e\u767d\u4e00\u4ef6\u4e8b\uff0c\u4f60\u82e5\u6362\u4e86\u6211\uff0c\u4f60\u4e5f\u4e00\u5b9a\u4f1a\u8fd9\u4e48\u6837\u505a\uff0c\u5979 \u82e5\u6362\u4e86\u4f60\uff0c\u6211\u4e5f\u4f1a\u8fd9\u4e48\u6837\u5bf9\u4f60\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6ca1\u6709\u52a8\uff0c\u5c31\u597d\u50cf\u6839\u672c\u6ca1\u542c\u5230\u4ed6\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u53ef\u662f\u5979\u773c\u6cea\u5374\u5df2\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5973\u4eba\u82e5\u771f\u7684\u7231\u4e0a\u4e86\u4e00\u4e2a\u7537\u4eba\uff0c\u5c31\u5e0c\u671b\u81ea\u5df1\u662f\u4ed6\u5fc3\u76ee\u4e2d\u552f\u4e00\u7684\u5973\u4eba\uff0c\u7edd\u4e0d\u5bb9\u7b2c\u4e09\u8005\u518d\u6765\u52a0 \u5165\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u674e\u5bfb\u6b22\u5fc3\u91cc\u6bd5\u7adf\u5df2\u6709\u4e86\u5979\u3002 \u3000\u3000\u5979\u75f4\u75f4\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u751c\uff1f\u662f\u9178\uff1f\u8fd8\u662f\u82e6\uff1f \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u975e\u505a\u4e0d\u53ef\u7684\u4e8b\uff0c\u5c31\u8ba9\u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u7b11\u5f97\u867d\u8f9b\u9178\uff0c\u5374\u603b\u662f\u7b11\u3002 \u3000\u3000\u5979\u5e26\u7740\u6cea\u7b11\u9053\uff1a\u201c\u6211\u5ffd\u7136\u53d1\u73b0\u6211\u81ea\u5df1\u5b9e\u5728\u662f\u4e2a\u5446\u5b50\uff0c\u4ed6\u8ba4\u5f97\u5979\u5728\u6211\u4e4b\u524d\uff0c\u6211\u8fd8\u6ca1\u6709\u770b\u5230 \u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u4e4b\u95f4\u5df2\u7ecf\u6709\u8bb8\u591a\u8bb8\u591a\u4e8b\u53d1\u751f\u4e86\uff0c\u6211\u662f\u540e\u6765\u624d\u52a0\u5165\u7684\uff0c\u6240\u4ee5\uff0c\u5e94\u8be5\u751f\u6c14\u7684\u662f\u5979 \uff0c\u4e0d\u5e94\u8be5\u662f\u6211\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u4e5f\u7b11\u4e86\u7b11\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u77e5\u9053\u81ea\u5df1\u662f\u5446\u5b50\uff0c\u5c31\u8868\u793a\u8fd9\u4eba\u5df2\u6e10\u6e10\u806a\u660e\u4e86\u3002 \u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u4f46\u4e5f\u6709\u4ef6\u4e8b\u662f\u6211\u975e\u505a\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u8981\u966a\u4ed6\u53bb\uff0c\u975e\u53bb\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u966a\u4ed6\u53bb\u4e5f\u597d\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u8f6c\u5934\u53bb\u77a7\u674e\u5bfb\u6b22\uff0c\u4e0b\u9762\u7684\u8bdd\u663e\u7136\u662f\u8981\u674e\u5bfb\u6b22\u63a5\u7740\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5979\u65e2\u7136\u5df2\u8bf4\u4e86\u975e\u53bb\u4e0d\u53ef\uff0c\u81ea\u7136\u5c31\u662f\u975e\u53bb\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u6d3b\u5230\u516d\u5341\u5c81\u65f6\u624d\u5b66\u4f1a\u4e0d\u53bb\u8ddf\u5973\u4eba\u4e89\u8fa9\uff0c\u4f60\u5b66\u5f97\u6bd4\u6211\u5feb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u65e2\u7136\u8981\u8d70\uff0c\u4eca\u5929\u665a\u4e0a\u5c31\u52a8\u8eab\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u62a2\u7740\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u4ee5\u4e3a\u5973\u4eba\u90fd\u662f\u5a46\u5a46\u5988\u5988\u7684\uff0c\u6709\u7684\u5973\u4eba\u6bd4\u7537\u4eba\u8fd8\u5e72\u8106\u5f97\u591a\uff0c\u4e5f\u4e00 \u6837\u8bf4\u8d70\u5c31\u8d70\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u5230\u4e86\u90a3\u91cc\uff0c\u83ab\u5fd8\u4e86\u5148\u53bb\u627e\u4f60\u4e8c\u53d4\uff0c\u95ee\u95ee\u90a3\u8fb9\u7684\u52a8\u9759\u3002\u201d \u3000\u3000\u5b59\u4e2a\u7ea2\u9053\uff1a\u201c\u6211\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5979\u779f\u4e86\u674e\u5bfb\u6b22\u4e00\u773c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u613f\u6211\u8ddf\u4ed6\u4e00\u9f50\u8fdb\u53bb\uff0c\u6211\u5c31\u5728\u4e8c\u53d4\u90a3\u91cc\u7b49\u4ed6\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u9053\uff1a\u201c\u5b59\u4e8c\u4fa0\u5df2\u5728\u5174\u4e91\u5e84\u5916\u5b88\u5019\u4e86\u5341\u4e8c\u5e74\uff0c\u4ed6\u7a76\u7adf\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u4ed6\u4e00\u76f4\u89c9\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u5341\u4e8c\u5e74\u524d\uff0c\u6b63\u662f\u4ed6\u5c06\u8981\u79bb\u5bb6\u51fa\u8d70\u7684\u65f6\u5019\u3001\u90a3\u65f6\u5b59\u9a7c\u5b50\u5c31\u5df2\u5b88\u5019\u5728\u90a3\u91cc\uff0c\u4ed6\u5b9e\u5728\u731c\u4e0d\u900f\u5b59 \u9a7c\u5b50\u7684\u7528\u610f\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u4e0d\u4f46\u548c\u674e\u5bb6\u7d20\u65e0\u6765\u5f80\uff0c\u548c\u9f99\u5578\u4e91\u4e5f\u5168\u65e0\u5173\u7cfb\uff0c\u81f3\u4e8e\u6797\u8bd7\u97f3\uff0c\u5979\u672c\u662f\u5b64\u5973\uff0c\u5f88\u5c0f\u7684 \u65f6\u5019\u5c31\u5df2\u6765\u6295\u9760\u674e\u5bfb\u6b22\u7684\u7236\u4eb2\u3002 \u3000\u3000\u5979\u672c\u662f\u4e2a\u5f88\u5185\u5411\u7684\u4eba\uff0c\u8fd9\u4e00\u751f\u51e0\u4e4e\u4ece\u672a\u5230\u522b\u7684\u5730\u65b9\u53bb\u8fc7\uff0c\u81ea\u7136\u66f4\u4e0d\u4f1a\u548c\u6c5f\u6e56\u4e2d\u4eba\u6709\u4efb\u4f55 \u6765\u5f80\u4e86\u3002 \u3000\u3000\u82e5\u8bf4\u5b59\u9a7c\u5b50\u662f\u53d7\u4e86\u522b\u4eba\u7684\u6258\u4ed8\uff0c\u90a3\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u4ed6\u8981\u5b59\u9a7c\u5b50\u5b88\u62a4\u7684\u662f\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5047\u5982\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u771f\u76f8\uff0c\u81ea\u7136\u5c31\u662f\u5b59\u8001\u5148\u751f\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u5e76\u4e0d\u662f\u4e2a\u6df1\u6c89\u7684\u4eba\uff0c\u674e\u5bfb\u6b22\u5e0c\u671b\u4ed6\u80fd\u8bf4\u51fa\u8fd9\u79d8\u5bc6\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5931\u671b\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u53c8\u5f00\u59cb\u62bd\u70df\uff0c\u7528\u70df\u5634\u585e\u4f4f\u4e86\u81ea\u5df1\u7684\u5634\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u779f\u4e86\u5979\u7237\u7237\u4e00\u773c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u89c9\u5f97\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u7740\u5979\uff0c\u7b49\u5979\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u9f99\u5c0f\u4e91\u5728\u4e0a\u5b98\u91d1\u8679\u9762\u524d\u780d\u65ad\u4e86\u81ea\u5df1\u7684\u624b\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u70b9\u4e86\u70b9\u5934\uff0c\u53f9\u9053\uff1a\u201c\u4ed6\u672c\u662f\u4e2a\u5f88\u7279\u522b\u7684\u5b69\u5b50\uff0c\u505a\u7684\u4e8b\u4e5f\u7279\u522b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u80fd\u505a\u51fa\u8fd9\u79cd\u4e8b\uff0c\u6211\u5012\u5e76\u4e0d\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u660e\u77e5\u5f53\u65f6\u4e0a\u5b98\u91d1\u8679\u5df2\u52a8\u4e86\u6740\u673a\uff0c\u6240\u4ee5\u5c31\u5148\u53d1\u5236\u4eba\uff0c\u8ba9\u4e0a\u5b98\u91d1\u8679\u65e0\u6240\u53ef\u8bf4 \uff0c\u8fd9\u4e48\u6837\u4e00\u6765\uff0c\u975e\u4f46\u6027\u547d\u80fd\u591f\u4fdd\u5168\uff0c\u800c\u4e0a\u8fd8\u4ee4\u4eba\u89c9\u5f97\u4ed6\u5f88\u6709\u80c6\u8bc6\u5f88\u6709\u5b5d\u5fc3\uff0c\u56e0\u6b64\u66f4\u770b\u91cd\u4ed6\u3002 \u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u8fd9\u4e48\u505a\uff0c\u7684\u786e\u5f88\u806a\u660e\u4e00\u4e5f\u591f\u72e0\u4e86\uff0c\u4f46\u4ed6\u672c\u5c31\u662f\u4e2a\u53c8\u806a\u660e\uff0c\u53c8 \u72e0\u6bd2\u7684\u5b69\u5b50\uff0c\u6240\u4ee5\u6211\u5e76\u4e0d\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u5947\u602a\u4ed6\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u6b66\u529f\u5df2\u88ab\u4f60\u5e9f\u4e86\uff0c\u4f53\u529b\u672c\u8be5\u6bd4\u666e\u901a\u4eba\u8fd8\u8870\u5f31\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u4e0d\u77e5\u9053\u505a\u5f97\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4eba\u7684\u9aa8\u5934\u5f88\u786c\uff0c\u7eb5\u7136\u662f\u5f88\u6709\u8155\u529b\u7684\u4eba\uff0c\u4e5f\u96be\u4e00\u5200\u5c31\u5c06\u81ea\u5df1\u7684\u624b\u780d\u65ad\uff0c\u9664\u975e \u4ed6\u7528\u7684\u662f\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u662f\u5b9d\u5251\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7edd\u4e0d\u662f\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u9f99\u5c0f\u4e91\u968f\u624b\u4e00\u6325\uff0c\u5c31\u5c06\u81ea\u5df1\u7684\u624b\u524a\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u6839\u672c\u5c31\u6ca1\u6709\u7528\u4ec0\u4e48\u529b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u7684\u786e\u6bd4\u6211\u7ec6\u5fc3\uff0c\u542c\u4f60\u4e00\u8bf4\uff0c\u6211\u4e5f\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u666e\u901a\u4eba\u7684\u624b\u82e5\u88ab\u780d\u65ad\uff0c\u4e00\u5b9a\u4e0d\u80fd\u518d\u652f\u6301\uff0c\u7acb\u523b\u5c31\u8981\u6655\u8fc7\u53bb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7eb5\u7136\u662f\u58ee\u6c49\uff0c\u4e5f\u4e07\u4e07\u652f\u6301\u4e0d\u4f4f\uff0c\u9664\u975e\u4ed6\u6709\u6df1\u539a\u7684\u6b66\u529f\u5e95\u5b50\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f46\u9f99\u5c0f\u4e91\u5374\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6b66\u529f\u5df2\u88ab\u5e9f\uff0c\u4f53\u529b\u5f88\u8870\u5f31\u7684\u5b69\u5b50\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u504f\u504f\u80fd \u652f\u6301\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u76ee\u5149\u95ea\u52a8\u7740\uff0c\u4eff\u4f5b\u5df2\u731c\u51fa\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u975e\u4f46\u80fd\u652f\u6301\u5f97\u4f4f\uff0c\u800c\u4e14\u8fd8\u80fd\u4f83\u4f83\u800c\u8c08\uff0c\u8fd8\u80fd\u5c06\u81ea\u5df1\u7684\u65ad\u624b\u6361\u8d77\u6765\uff0c\u4e00\u4e2a \u6ca1\u6709\u6b66\u529f\u7684\u4eba\uff0c\u600e\u4e48\u80fd\u529e\u5f97\u5230\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u96be\u9053\u662f\u8bf4\uff0c\u4ed6\u6b66\u529f\u5df2\u6062\u590d\uff1f\u4ed6\u5e73\u65f6\u90a3\u79cd\u5f31\u4e0d\u7981\u98ce\u7684\u6837\u5b50\uff0c\u90fd\u662f\u6545 \u610f\u88c5\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u5e9f\u4ed6\u6b66\u529f\u7684\u65f6\u5019\uff0c\u7528\u7684\u624b\u6cd5\u5f88\u91cd\uff0c\u6309\u7406\u8bf4\u4ed6\u6b66\u529f\u7edd\u65e0\u6062\u590d\u7684\u53ef\u80fd\uff0c\u9664\u975e \u2026\u2026\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5b59\u5c0f\u7ea2\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u9664\u975e\u90a3\u4f20\u8bf4\u5e76\u4e0d\u5047\uff0c\u5174\u4e91\u5e84\u91cc\u7684\u786e\u85cf\u6709\u90a3\u672c\u7a00\u4e16\u7684\u6b66\u529f\u79d8\u7b08 \uff0c\u65e0\u610f\u4e2d\u88ab\u9f99\u5c0f\u4e91\u5f97\u5230\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5583\u5583\u9053\uff1a\u201c\u5b59\u4e8c\u4fa0\u5728\u90a3\u91cc\u5b88\u62a4\u4e86\u5341\u51e0\u5e74\uff0c\u96be\u9053\u4e3a\u7684\u4e5f\u662f\u8fd9\u672c\u6b66\u529f\u79d8\u7b08\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5ffd\u7136\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u60f3\u544a\u8bc9\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u75db\u75db\u5feb\u5feb\u7684\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u7740\u5934\uff0c\u7528\u773c\u89d2\u5077\u5077\u779f\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u6211\u6015\u6328\u9a82\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u5973\u4eba\u66ff\u4f60\u4fdd\u5b88\u79d8\u5bc6\uff0c\u53ea\u6709\u4e00\u4e2a\u6cd5\u5b50\uff0c\u90a3\u5c31\u662f\u6c38\u8fdc\u83ab\u8981\u8ddf\u5979 \u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u4e00\u4e2a\u5b57\u90fd\u4e0d\u80fd\u63d0\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u561f\u7740\u5634\uff0c\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u8bf4\u51fa\u53bb\u2026\u2026\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u9053\uff1a\u201c\u4f60\u7528\u7684\u6cd5\u5b50\u66f4\u9ad8\u660e\uff0c\u4f60\u81ea\u5df1\u4e0d\u8bf4\uff0c\u5374\u8981\u6211\u66ff\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u62bf\u5634\u9053\uff1a\u201c\u5c31\u7b97\u6211\u8bf4\u4e86\uff0c\u6211\u4e5f\u53ea\u8ddf\u4ed6\u8bf4\uff0c\u4ed6\u2026\u2026\u4ed6\u53c8\u4e0d\u662f\u522b\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u53c8\u4e0d\u662f\u522b\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u674e\u5bfb\u6b22\u542c\u5728\u8033\u91cc\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u53c8\u5df2\u6b20\u4e0b\u4e86\u4e00\u7b14\u503a\uff0c\u8fd9\u8f88\u5b50\u53ea\u6015\u4e5f\u4f11\u60f3\u8fd8\u5f97\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u82e5\u4e0d\u518d\u5c06\u4f60\u5f53\u505a\u201c\u522b\u4eba\u201d\uff0c\u90a3\u5c31\u8868\u793a\u5979\u5df2\u8ddf\u5b9a\u4e86\u4f60\uff0c\u4f60\u5c31\u7b97\u50cf\u9a6c\u4e00\u6837\u957f\u4e86\u56db\u6761 \u817f\uff0c\u4e5f\u4f11\u60f3\u518d\u80fd\u8dd1\u5f97\u4e86\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u7684\u7b11\u58f0\u7a81\u7136\u987f\u4f4f\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u5174\u4e91\u5e84\u91cc\u7684\u786e\u85cf\u7740\u672c\u6b66\u529f\u79d8\u7b08\uff0c\u90a3\u5e76\u4e0d\u662f\u8c23\u8a00 \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u5bb9\u9053\uff1a\u201c\u662f\u8c01\u7684\u6b66\u529f\u79d8\u7b08\uff1f\u6211\u600e\u4f1a\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u5c06\u70df\u6597\u91cd\u65b0\u71c3\u7740\uff0c\u671b\u7740\u8885\u5a1c\u56db\u6563\u7684\u70df\u96fe\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u53ef\u542c\u8bf4\u8fc7\u738b\u601c\u82b1\u8fd9\u4e2a\u4eba \u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u5929\u4e0b\u7686\u77e5\uff0c\u6211\u5f53\u7136\u4e0d\u4f1a\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u738b\u601c\u82b1\u672c\u662f\u6c88\u6d6a\u6c88\u5927\u4fa0\u7684\u6b7b\u654c\uff0c\u540e\u6765\u5374\u53d8\u6210\u6c88\u5927\u4fa0\u7684\u597d\u670b\u53cb\uff0c\u56e0\u4e3a\u4ed6\u8fd9 \u4eba\u672c\u5728\u6b63\u90aa\u4e4b\u95f4\uff0c\u867d\u7136\u90aa\uff0c\u5374\u5e76\u4e0d\u592a\u6076\u6bd2\uff0c\u505a\u4e8b\u867d\u4efb\u6027\uff0c\u4f46\u6709\u65f6\u5374\u4e5f\u5f88\u8bb2\u4e49\u6c14\uff0c\u5f88\u6709\u9aa8\u6c14\u3002 \u6240\u4ee5\uff0c\u4ed6\u867d\u7136\u5bb3\u8fc7\u6c88\u5927\u4fa0\u5f88\u591a\u6b21\uff0c\u6c88\u5927\u4fa0\u8fd8\u662f\u539f\u8c05\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6c88\u6d6a\u548c\u738b\u601c\u82b1\u4e4b\u95f4\uff0c\u5f53\u7136\u4e5f\u6709\u6bb5\u5f88\u66f2\u6298\u7684\u6545\u4e8b\uff0c\u8fd9\u6545\u4e8b\u6211\u66fe\u7ecf\u5728\u201c\u6b66\u6797\u5916\u53f2\u201d\u8fd9\u672c\u4e66\u91cc \u5f88\u4ed4\u7ec6\u7684\u53d9\u8ff0\u8fc7\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u542c\u8bf4\u738b\u601c\u82b1\u5df2\u4e0e\u6c88\u5927\u4fa0\u4f09\u4fea\u7ed3\u4f34\u5f52\u9690\uff0c\u8fdc\u6e38\u6d77\u5916\uff0c\u90a3\u4e5f\u662f\u5f88\u591a\u5e74\u4ee5\u524d\u7684\u4e8b \u4e86\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u540e\u6765\u7684\u786e\u88ab\u6c88\u5927\u4fa0\u6240\u611f\u5316\u3002\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8981\u6740\u4e00\u4e2a\u4eba\u5f88\u5bb9\u6613\uff0c\u8981\u611f\u5316\u4e00\u4e2a\u4eba\u5374\u56f0\u96be\u5f97\u591a\uff0c\u6c88\u5927\u4fa0\u7684\u786e \u662f\u4eba\u6770\uff0c\u4f60\u82e5\u65e9\u751f\u51e0\u5e74\uff0c\u4e00\u5b9a\u4e5f\u662f\u4ed6\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u4e5f\u4e0d\u7981\u9732\u51fa\u4e86\u5411\u5f80\u4e4b\u8272\uff0c\u5374\u4e0d\u77e5\u5343\u767e\u5e74\u540e\uff0c\u4ed6\u4fa0\u540d\u7559\u4f20\u4e4b\u5e7f\uff0c\u53d7\u4eba\u5d07\u656c\u4e4b\u6df1 \uff0c\u7edd\u4e0d\u5728\u4ed6\u6240\u5411\u5f80\u7684\u6c88\u6d6a\u4e4b\u4e0b\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u6c88\u5927\u4fa0\u867d\u662f\u4eba\u6770\uff0c\u4f46\u738b\u601c\u82b1\u5374\u4e5f\u4e0d\u51e1\uff0c\u5426\u5219\u53c8\u600e\u4f1a\u6210\u4e3a\u6c88\u5927\u4fa0\u7684\u6b7b\u654c\uff1f \u201d \u3000\u3000\u4e24\u4e2a\u806a\u660e\u624d\u667a\u76f8\u5dee\u5f88\u8fdc\u7684\u4eba\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u7ed3\u6210\u670b\u53cb\uff0c\u5374\u7edd\u4e0d\u4f1a\u6210\u4e3a\u654c\u4eba\uff0c\u6240\u4ee5\u53ea\u6709\u4e0a\u5b98\u91d1 \u8679\u624d\u6709\u8d44\u683c\u505a\u674e\u5bfb\u6b22\u7684\u4ec7\u654c\uff0c\u522b\u7684\u4eba\u7b80\u76f4\u4e0d\u914d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4eba\u4e43\u662f\u6b66\u6797\u4e2d\u72ec\u4e00\u65e0\u4e8c\u7684\u624d\u5b50\uff0c\u6587\u6b66\u53cc\u5168\uff0c\u60ca\u624d\u7edd\u8273\uff0c\u6240\u5b66\u4e4b\u6742\uff0c \u6d89\u730e\u4e4b\u5e7f\uff0c\u6b66\u6797\u4e2d\u8fd8\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u6bd4\u5f97\u4e0a\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4eba\u4e0d\u4f46\u661f\u535c\u661f\u76f8\uff0c\u7434\u68cb\u4e66\u753b\u90fd\u6765\u5f97\uff0c\u800c\u4e14\u533b\u9053\u4e5f\u5f88\u7cbe\uff0c\u6613\u5bb9\u672f \u4e5f\u5f88\u7cbe\uff0c\u5341\u4e2a\u4eba\u90fd\u5b66\u4e0d\u5168\u7684\uff0c\u4ed6\u4e00\u4e2a\u4eba\u5c31\u5b66\u5168\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u89c1\u730e\u5fc3\u559c\uff0c\u4ec0\u4e48\u90fd\u8981\u5b66\u4e00\u70b9\uff0c\u6240\u4ee5\u6b66\u529f\u624d\u4e0d\u80fd\u767b\u5cf0\u9020\u6781\uff0c \u5426\u5219\u4ee5\u4ed6\u7684\u806a\u660e\u624d\u667a\uff0c\u53c8\u600e\u4f1a\u5c61\u6b21\u8d25\u4e8e\u6c88\u5927\u4fa0\u624b\u4e0b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u60f3\u8d77\u4e86\u963f\u98de\u3002 \u3000\u3000\u963f\u98de\u7684\u806a\u660e\u624d\u667a\u662f\u4e0d\u662f\u6bd4\u738b\u601c\u82b1\u66f4\u9ad8\uff0c\u56e0\u4e3a\u4ed6\u53ea\u5b66\u4e86\u4e00\u6837\u4e8b\uff0c\u53ea\u7ec3\u4e00\u5251\uff0c\u4ed6\u8fd9\u4e00\u5251\u672c\u53ef \u7ec3\u5230\u7a7a\u524d\u7edd\u540e\uff0c\u65e0\u4eba\u80fd\u62b5\u6321\u7684\u5730\u6b65\u3002 \u3000\u3000\u201c\u53ea\u53ef\u60dc\u806a\u660e\u4eba\u504f\u504f\u65f6\u5e38\u8981\u505a\u50bb\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u613f\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u738b\u601c\u82b1\u6539\u90aa\u5f52\u6b63\u540e\uff0c\u5df2\u77e5\u9053\u4ed6\u4ee5\u524d\u6240\u5b66\u4e0d\u4f46\u592a\u6742\uff0c\u4e5f\u592a\u90aa\uff0c\u672c\u60f3\u5c06\u90a3\u672c \u2018\u601c\u82b1\u5b9d\u9274\u2019\u4ed8\u4e4b\u4e00\u70ac\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ec0\u4e48\u2018\u601c\u82b1\u5b9d\u9274\u2019\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u601c\u82b1\u5b9d\u9274\u5c31\u662f\u5c06\u4ed6\u81ea\u5df1\u4e00\u751f\u6240\u5b66\u5168\u8bb0\u8f7d\u5728\u4e0a\u9762\u7684\u4e00\u672c\u4e66\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u60f3\u70e7\u4e86\u5b83\uff1f\u201d \u7b2c\u4e03\u5341\u516b\u7ae0\u3000\u6050\u6016\u7684\u51b3\u6597>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u516b\u7ae0\u3000\u6050\u6016\u7684\u51b3\u6597 \u3000\u3000\u5b59\u8001\u5148\u751f\u8c08\u5230\u738b\u601c\u82b1\u60f3\u5c06\u81ea\u5df1\u6240\u8457\u201c\u601c\u82b1\u5b9d\u9274\u201d\u70e7\u4e86\u7684\u4e8b\uff0c\u674e\u5bfb\u6b22\u4e0d\u7531\u95ee\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0 \u4e48\u60f3\u70e7\u4e86\u5b83\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u4e0a\u9762\u4e0d\u4f46\u6709\u4ed6\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u4e5f\u8bb0\u8f7d\u7740\u4ed6\u7684\u4e0b\u6bd2\u672f\uff0c\u6613\u5bb9\u672f\uff0c\u82d7\u4eba \u653e\u866b\uff0c\u6ce2\u65af\u4f20\u6765\u7684\u6444\u5fc3\u672f\u2026\u2026\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u63a5\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u4e00\u672c\u4e66\u82e5\u662f\u843d\u5728\u4e0d\u8096\u4e4b\u5f92\u7684\u624b\u91cc\uff0c\u540e\u679c\u5c82\u975e\u4e0d\u582a\u8bbe\u60f3\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u53f9\u9053\uff1a\u201c\u90a3\u7684\u786e\u662f\u540e\u60a3\u65e0\u7a77\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4f46\u8fd9\u662f\u4ed6\u4e00\u751f\u5fc3\u8840\u6240\u805a\uff0c\u4ed6\u4e5f\u4e0d\u820d\u5f97\u5c06\u4e4b\u6bc1\u4e8e\u4e00\u65e6\uff0c\u6240\u4ee5\u4ed6\u8fdc\u8d74\u6d77\u5916\u4e4b \u524d\uff0c\u5c31\u5c06\u8fd9\u672c\u4e66\u4ea4\u7ed9\u4e86\u4e00\u4e2a\u4ed6\u8ba4\u4e3a\u6700\u4e3a\u53ef\u9760\u7684\u4eba\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u8bdd\uff0c\u674e\u5bfb\u6b22\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u6765\u9f99\u53bb\u8109\u90fd\u5df2\u4e86\u89e3\uff0c\u4e5f\u5df2\u731c\u5230\u85cf\u5728\u5174\u4e91\u5e84\u91cc\u7684\u90a3\u672c\u6b66\u529f\u79d8 \u7626\uff0c\u5c31\u662f\u201c\u601c\u82b1\u5b9d\u9274\u201d\u3002 \u3000\u3000\u4f46\u8fd8\u6709\u51e0\u4ef6\u4e8b\u4ed6\u60f3\u4e0d\u901a\uff0c\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u4ed6\u8170\u5c06\u8fd9\u672c\u79d8\u53d1\u4ea4\u7ed9\u8c01\u4e86\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4ea4\u7ed9\u4e86\u4f60\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u5c0f\u674e\u63a2\u82b1\u5916\uff0c\u8fd8\u6709\u8c01\u662f\u6700\u53ef\u9760\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4ed6\u5c06\u8fd9\u672c\u2018\u601c\u82b1\u5b9d\u9274\u2019\u4ea4\u6258\u7ed9\u4f60\uff0c\u4e0d\u4f46\u8981\u4f60\u66ff\u4ed6\u4fdd\u5b58\uff0c\u8fd8\u60f3\u8981\u4f60\u66ff\u4ed6\u627e \u4e2a\u5929\u8d44\u9ad8\uff0c\u5fc3\u672f\u597d\u7684\u5f1f\u5b50\uff0c\u4f5c\u4e3a\u4ed6\u7684\u8863\u94b5\u4f20\u4eba\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u4f46\u8fd9\u4ef6\u4e8b\u6211\u5374\u8fde\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u90a3\u65f6\u6070\u5de7\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u601d\u9053\uff1a\u201c\u5341\u4e8c\u5e74\u524d\u2026\u2026\u4e0d\u9519\uff0c\u90a3\u65f6\u6211\u5230\u5173\u5916\u53bb\u4e86\u4e00\u8d9f\uff0c\u56de\u6765\u65f6\u53c8\u9047\u4f0f\u53d7\u4e86\u91cd\u4f24 \uff0c\u82e5\u4e0d\u662f\u9f99\u5578\u4e91\u4ed7\u4e49\u76f8\u6551\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u54bd\u5589\u5934\u4f3c\u5df2\u88ab\u585e\u4f4f\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u672c\u662f\u4ed6\u8fd9\u4e00\u751f\u4e2d\u6700\u96be\u5fd8\u6000\u7684\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u7684\u4e00\u751f\u624d\u4f1a\u6539\u53d8\u2014\u2014\u7531\u5e78\u798f\u53d8\u4e3a\u4e0d\u5e78\uff01 \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u738b\u601c\u82b1\u867d\u672a\u89c1\u7740\u4f60\uff0c\u5374\u89c1\u5230\u4e86\u6797\u59d1\u5a18\uff0c\u90a3\u65f6\u4ed6\u8fdc\u6e38\u5728\u5373\uff0c\u6c88\u5927\u4fa0\u5df2\u5728\u6d77 \u53e3\u7b49\u7740\u4ed6\uff0c\u4ed6\u81ea\u5df1\u4e0d\u80fd\u505c\u7559\uff0c\u6240\u4ee5\u5c31\u5c06\u90a3\u2018\u601c\u82b1\u5b9d\u9274\u2019\u4ea4\u7ed9\u4e86\u6797\u59d1\u5a18\u3002\u201d \u3000\u3000\u7537\u5973\u4e4b\u95f4\u7684\u4e8b\uff0c\u4e16\u4e0a\u53ea\u6015\u5f88\u5c11\u4eba\u80fd\u6bd4\u738b\u601c\u82b1\u4e86\u89e3\u5f97\u66f4\u591a\u4e86\uff0c\u4ed6\u81ea\u5df1\u5df2\u770b\u51fa\u6797\u8bd7\u97f3\u548c\u674e\u5bfb \u6b22\u4e4b\u95f4\u7684\u60c5\u611f\u975e\u6bd4\u5bfb\u5e38\u3002 \u3000\u3000\u4f46\u6797\u8bd7\u97f3\u4e3a\u4f55\u4ece\u672a\u5c06\u8fd9\u4ef6\u4e8b\u5411\u674e\u5bfb\u6b22\u63d0\u8d77\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u8fdf\u7591\u7740\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u77e5\u524d\u8f88\u662f\u4ece\u54ea\u91cc\u542c\u5230\u7684\uff1f\u662f\u4e0d\u662f\u5f88\u53ef\u9760\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u7edd\u5bf9\u53ef\u9760\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u63d2\u5634\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5c31\u662f\u6211\u4e8c\u53d4\u8bf4\u7684\uff0c\u738b\u8001\u524d\u8f88\u5230\u5174\u4e91\u5e84\u2026\u2026\u4e0d\uff0c\u5230\u674e\u56ed\u53bb \u89c1\u6797\u59d1\u5a18\u7684\u65f6\u5019\uff0c\u6211\u4e8c\u53d4\u5c31\u5728\u5916\u9762\u7b49\u7740\u3002\u201d \u3000\u3000\u5979\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u5929\u4e4b\u540e\uff0c\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u6211\u4e8c\u53d4\u5c31\u4ece\u672a\u79bb\u5f00\u8fc7\u90a3\u5730\u65b9\u4e00 \u6b65\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5c31\u662f\u53d7\u4e86\u738b\u601c\u82b1\u7684\u6258\u4ed8\uff0c\u5728\u90a3\u91cc\u76d1\u89c6\u7740\u6211\uff1f\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u738b\u601c\u82b1\u65e2\u7136\u80af\u5c06\u90a3\u4e48\u91cd\u8981\u7684\u4e1c\u897f\u4ea4\u7ed9\u4f60\uff0c\u5c31\u7edd\u4e0d\u4f1a\u5bf9\u4f60\u4e0d\u653e\u5fc3\uff0c\u53ea\u4e0d\u8fc7 \uff0c\u4ed6\u5bf9\u4f60\u7684\u6b66\u529f\u8fd8\u4e0d\u5927\u4fe1\u4efb\uff0c\u751f\u6015\u6709\u4eba\u542c\u5230\u6d88\u606f\uff0c\u4f1a\u53bb\u593a\u4e66\uff0c\u6240\u4ee5\u624d\u4f1a\u8981\u8001\u4e8c\u7559\u5728\u90a3\u91cc\uff0c\u5230 \u4e86\u5fc5\u8981\u65f6\uff0c\u4e5f\u597d\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e8c\u53d4\u5f53\u5e74\u6e38\u4fa0\u6c5f\u6e56\u95f4\uff0c\u66fe\u7ecf\u88ab\u738b\u8001\u524d\u8f88\u6551\u8fc7\u4e00\u547d\uff0c\u4ed6\u8fd9\u4eba\u6700\u662f\u6069\u6028\u5206\u660e \uff0c\u738b\u8001\u524d\u8f88\u8981\u4ed6\u505a\u7684\u4e8b\uff0c\u4ed6\u7684\u786e\u53ef\u8bf4\u662f\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u9053\uff1a\u201c\u4f46\u540e\u6765\u5374\u5728\u65e0\u610f\u4e2d\u542c\u5230\u6797\u59d1\u5a18\u5e76\u6ca1\u6709\u5c06\u90a3\u2018\u601c\u82b1\u5b9d\u9274\u2019\u8f6c\u4ea4\u7ed9\u4f60\uff0c\u6240\u4ee5 \u4f60\u51fa\u5173\u4e4b\u540e\uff0c\u4ed6\u66f4\u4e0d\u653e\u5fc3\uff0c\u66f4\u4e0d\u80af\u79bb\u5f00\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u9053\uff1a\u201c\u53d7\u4eba\u4e4b\u6258\uff0c\u5fe0\u4eba\u4e4b\u4e8b\uff0c\u5b59\u4e8c\u4fa0\u7684\u786e\u4e0d\u6127\u4e3a\u738b\u8001\u524d\u8f88\u7684\u597d\u670b\u53cb\uff0c\u53ea\u4e0d\u8fc7\u2026 \u2026\u201d \u3000\u3000\u4ed6\u76ef\u7740\u5b59\u8001\u5148\u751f\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u5b59\u4e8c\u4fa0\u53c8\u600e\u4f1a\u77e5\u9053\u6797\u59d1\u5a18\u672a\u66fe\u5c06\u2018\u601c\u82b1\u5b9d\u9274\u2019\u8f6c\u4ea4\u7ed9\u6211 \uff1f\u8fd9\u4ef6\u4e8b\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u8001\u5148\u751f\u957f\u957f\u5438\u4e86\u53e3\u70df\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fde\u4f60\u90fd\u4e0d\u77e5\u9053\uff0c\u6211\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8bf4\u4e0d\u51fa\u6d3b\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u672a\u60f3\u5230\u6797\u8bd7\u97f3\u5bf9\u4ed6\u4e5f\u6709\u9690\u7792\u7740\u7684\u4e8b\u3002 \u3000\u3000\u5b59\u8001\u5148\u751f\u53c8\u9053\uff1a\u201c\u738b\u601c\u82b1\u4e0d\u4f46\u6709\u6740\u4eba\u7684\u672c\u4e8b\uff0c\u4e5f\u6709\u6551\u4eba\u7684\u624b\u6bb5\uff0c\u4e2d\u5e74\u540e\u533b\u9053\u66f4\u7cbe\uff0c\u7684\u786e \u53ef\u8bf4\u5df2\u6709\u751f\u6b7b\u4eba\uff0c\u8089\u767d\u9aa8\u7684\u529f\u529b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u9f99\u5c0f\u4e91\u662f\u6797\u59d1\u5a18\u7684\u4eb2\u751f\u513f\u5b50\uff0c\u4e00\u4e2a\u505a\u6bcd\u4eb2\u7684\uff0c\u786e\u662f\u4e0d\u501f\u505a\u4efb\u4f55\u4e8b\u7684\uff0c\u6240\u4ee5 \uff0c\u6211\u60f3\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u7684\u610f\u601d\u674e\u5bfb\u6b22\u5374\u5df2\u542c\u61c2\u2014\u2014\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u542c\u5f97\u61c2\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4e00\u5b9a\u5df2\u5c06\u90a3\u672c\u201c\u601c\u82b1\u5b9d\u9274\u201d\u4f20\u7ed9\u4e86\u5979\u7684\u513f\u5b50\uff0c\u5979\u4e00\u5b9a\u5c06\u8fd9\u672c\u795e\u5947\u7684\u4e66\u4fdd\u5b58\u4e86\u5f88\u591a \u5e74\uff0c\u800c\u4e14\u4fdd\u5b58\u5f97\u5f88\u79d8\u5bc6\u3002 \u3000\u3000\u95ee\u9898\u662f\uff0c\u5979\u4e3a\u4ec0\u4e48\u59cb\u7ec8\u6ca1\u6709\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4ed6\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7b2c\u4e00\u6b21\u770b\u5230\u6797\u8bd7\u97f3\u7684\u65f6\u5019\uff0c\u5979\u4e5f\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u90a3\u5929\u6b63\u662f\u4e0b\u96ea\u3002 \u3000\u3000\u5ead\u56ed\u4e2d\u7684\u6885\u82b1\u5f00\u5f97\u6b63\u597d\uff0c\u6885\u6811\u4e0b\u7684\u96ea\u4e5f\u4eff\u4f5b\u5206\u5916\u6d01\u767d\u3002 \u3000\u3000\u90a3\u5929\u674e\u5bfb\u6b22\u6b63\u5728\u6885\u6811\u4e0b\u5806\u96ea\u4eba\uff0c\u4ed6\u627e\u4e86\u4e24\u5757\u6700\u9ed1\u6700\u4eae\u7684\u7164\uff0c\u6b63\u51c6\u5907\u4e3a\u8fd9\u96ea\u4eba\u5d4c\u4e0a\u4e00\u53cc\u660e \u4eae\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u6700\u6109\u5feb\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u5341\u5206\u559c\u6b22\u5806\u96ea\u4eba\uff0c\u4ed6\u5806\u96ea\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u8981\u4eab\u53d7\u8fd9\u4e00\u5239\u90a3\u95f4\u7684\u6109\u5feb\u2014\u2014\u6bcf\u5f53\u4ed6 \u5c06\u201c\u773c\u775b\u201c\u5d4c\u4e0a\u53bb\u7684\u65f6\u5019\uff0c\u8fd9\u81c3\u80bf\u7684\u800c\u611a\u8822\u7684\u96ea\u4eba\u5c31\u50cf\u662f\u5ffd\u7136\u53d8\u5f97\u6709\u4e86\u751f\u547d\u3002\u6bcf\u5f53\u8fd9\u4e00\u5239\u90a3 \u95ee\uff0c\u4ed6\u603b\u4f1a\u611f\u89c9\u5230\u8bf4\u4e0d\u51fa\u7684\u6ee1\u8db3\u548c\u6109\u5feb\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u559c\u6b22\u5efa\u8bbe\uff0c\u618e\u6076\u7834\u574f\u3002 \u3000\u3000\u4ed6\u70ed\u7231\u7740\u751f\u547d\u3002 \u3000\u3000\u4ed6\u603b\u662f\u4e00\u4e2a\u4eba\u5077\u5077\u7684\u8dd1\u6765\u5806\u96ea\u4eba\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u4efb\u4f55\u4eba\u6765\u5206\u4eab\u4ed6\u8fd9\u79cd\u79d8\u5bc6\u7684\u6b22\u5077\uff0c\u90a3\u65f6\u4ed6 \u8fd8\u4e0d\u77e5\u9053\u6b22\u6109\u662f\u7edd\u4e0d\u4f1a\u56e0\u4e3a\u5206\u7ed9\u522b\u4eba\u800c\u51cf\u5c11\u7684\u3002 \u3000\u3000\u540e\u6765\u4ed6\u624d\u61c2\u5f97\uff0c\u6b22\u4e50\u5c31\u50cf\u662f\u4e2a\u805a\u5b9d\u76c6\uff0c\u4f60\u5206\u7ed9\u522b\u4eba\u7684\u8d8a\u591a\uff0c\u81ea\u5df1\u6240\u5f97\u7684\u4e5f\u8d8a\u591a\u3002 \u3000\u3000\u75db\u82e6\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u4f60\u82e5\u60f3\u8981\u522b\u4eba\u6765\u5206\u62c5\u4f60\u7684\u75db\u82e6\uff0c\u53cd\u800c\u4f1a\u75db\u82e6\u5f97\u66f4\u6df1\u3002 \u3000\u3000\u96ea\u4eba\u7684\u8138\u662f\u5706\u7684\u3002 \u3000\u3000\u4ed6\u6b63\u8003\u8651\u7740\u8be5\u5728\u4ec0\u4e48\u5730\u65b9\u5d4c\u4e0a\u8fd9\u53cc\u773c\u775b\uff0c\u4ed6\u591a\u75c5\u7684\u6bcd\u4eb2\u5ffd\u7136\u7834\u4f8b\u8d70\u5165\u4e86\u5ead\u56ed\uff0c\u8eab\u65c1\u8fd8\u5e26 \u7740\u4e2a\u62ab\u7740\u7ea2\u64ce\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u7329\u7ea2\u7684\u98ce\u84ec\uff0c\u6bd4\u6885\u82b1\u8fd8\u9c9c\u8273\u3002 \u3000\u3000\u4f46\u8fd9\u5973\u5b69\u5b50\u7684\u8138\u5374\u662f\u82cd\u767d\u7684\uff0c\u6bd4\u96ea\u66f4\u767d\u3002 \u3000\u3000\u7ea2\u548c\u81ea\u6c38\u8fdc\u662f\u4ed6\u6700\u559c\u7231\u7684\u989c\u8272\uff0c\u56e0\u4e3a\u201c\u767d\u201d\u8c61\u5f81\u7eaf\u6d01\uff0c\u201c\u7ea2\u201d\u8c61\u5f81\u70ed\u60c5\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u6b21\u770b\u5230\u5979\uff0c\u5c31\u5bf9\u5979\u751f\u51fa\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u540c\u60c5\u548c\u601c\u60dc\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u53bb\u62c9\u4f4f\u5979\u7684\u624b \uff0c\u514d\u5f97\u5979\u88ab\u5bd2\u98ce\u5439\u5012\u3002 \u3000\u3000\u4ed6\u6bcd\u4eb2\u544a\u8bc9\u4ed6\uff1a\u201c\u8fd9\u662f\u4f60\u59e8\u5988\u7684\u5973\u513f\uff0c\u4f60\u59e8\u5988\u5230\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u53bb\u4e86\uff0c\u6240\u4ee5\u5979\u4ece\u4eca\u5929\u5f00 \u59cb\uff0c\u5c31\u8981\u4f4f\u5728\u6211\u4eec\u5bb6\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u603b\u662f\u57cb\u6028\u81ea\u5df1\u6ca1\u6709\u59b9\u59b9\uff0c\u73b0\u5728\u6211\u66ff\u4f60\u627e\u4e86\u4e2a\u59b9\u59b9\u6765\u4e86\uff0c\u4f60\u4e00\u5b9a\u8981\u5bf9\u5979\u597d\u4e9b\uff0c\u7edd\u4e0d\u80fd \u8ba9\u5979\u751f\u6c14\u3002\u201d \u3000\u3000\u53ef\u662f\u4ed6\u51e0\u4e4e\u6ca1\u6709\u542c\u5230\u4ed6\u6bcd\u4eb2\u5728\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5c0f\u5973\u5b69\u5df2\u8d70\u4e86\u8fc7\u6765\uff0c\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u770b\u7740\u4ed6\u7684\u96ea\u4eba\u3002 \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u6ca1\u6709\u773c\u775b\uff1f\u201d\u5979\u5ffd\u7136\u95ee\u3002 \u3000\u3000\u201c\u4f60\u559c\u4e0d\u559c\u6b22\u66ff\u5b83\u88c5\u4e0a\u5bf9\u773c\u775b\uff1f\u201d \u3000\u3000\u5979\u559c\u6b22\uff0c\u5979\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u5c06\u624b\u91cc\u90a3\u53cc\u9ed1\u4eae\u7684\u201c\u773c\u775b\u201d\u9001\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u6b21\u8ba9\u522b\u4eba\u5206\u4eab\u4e86\u4ed6\u7684\u6b22\u6109\u3002 \u3000\u3000\u81ea\u4ece\u8fd9\u4e00\u6b21\u540e\uff0c\u4ed6\u65e0\u8bba\u6709\u4ec0\u4e48\uff0c\u90fd\u8981\u548c\u5979\u4e00\u9f50\u5206\u4eab\uff0c\u751a\u81f3\u8fde\u522b\u4eba\u7ed9\u4ed6\u4e00\u5757\u5c0f\u5c0f\u7684\u91d1\u6a58\u997c \uff0c\u4ed6\u4e5f\u4f1a\u85cf\u8d77\u6765\uff0c\u7b49\u5230\u89c1\u7740\u5979\u65f6\uff0c\u5206\u7ed9\u5979\u4e00\u534a\u3002 \u3000\u3000\u53ea\u8981\u770b\u5230\u5979\u773c\u775b\u91cc\u9732\u51fa\u4e00\u4e1d\u5149\u4eae\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u524d\u6240\u672a\u6709\u7684\u6109\u5feb\uff0c\u6c38\u8fdc\u6ca1\u6709\u4efb\u4f55\u80fd\u4ee3\u66ff\u7684 \u6109\u5feb\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u4e0d\u60dc\u548c\u5979\u5206\u4eab\u81ea\u5df1\u7684\u751f\u547d\u3002 \u3000\u3000\u201c\u5979\u4e5f\u4e00\u6837\u3002\u201d\u4ed6\u77e5\u9053\uff0c\u4ed6\u786e\u4fe1\u3002 \u3000\u3000\u751a\u81f3\u5f53\u4ed6\u4eec\u5206\u79bb\u7684\u65f6\u5019\uff0c\u5728\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u4ed6\u8fd8\u662f\u8ba4\u4e3a\u53ea\u6709\u4ed6\u624d\u80fd\u5206\u4eab\u5979\u7684\u75db\u82e6\uff0c\u5979\u7684\u6b22 \u4e50\uff0c\u5979\u7684\u79d8\u5bc6\uff0c\u5979\u7684\u4e00\u5207\u3002 \u3000\u3000\u4ed6\u786e\u4fe1\u5982\u6b64\uff0c\u76f4\u5230\u73b0\u5728\u2026\u2026 \u3000\u3000\u964b\u5df7\uff0c\u6628\u591c\u79ef\u96ea\u3002 \u3000\u3000\u79ef\u96ea\u5df2\u6eb6\uff0c\u5730\u4e0a\u6ce5\u6cde\u6ca1\u8db3\u3002\u5899\u89d2\u8fb9\u5f53\u7136\u4e5f\u6709\u4e9b\u6bd4\u8f83\u5e72\u71e5\u7684\u8def\uff0c\u4f46\u674e\u5bfb\u6b22\u5374\u60c5\u613f\u8d70\u5728\u6ce5\u6cde \u4e2d\uff0c\u4ed6\u559c\u6b22\u4e00\u811a\u8e0f\u5165\u6ce5\u6cde\u4e2d\u65f6\u90a3\u79cd\u8f6f\u8f6f\u7684\uff0c\u6696\u6696\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u5f80\u5f80\u80fd\u4ee4\u4ed6\u5fc3\u60c5\u677e\u5f1b\u3002 \u3000\u3000\u4ee5\u524d\uff0c\u4ed6\u6700\u618e\u6076\u6ce5\u6cde\uff0c\u4ed6\u60c5\u613f\u591a\u7ed5\u4e2a\u5708\u5b50\u4e5f\u4e0d\u613f\u8d70\u8fc7\u4e00\u5c0f\u6bb5\u6ce5\u6cde\u7684\u8def\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u624d\u53d1\u89c9\u6ce5\u6cde\u4e5f\u6709\u6ce5\u6cde\u7684\u53ef\u7231\u4e4b\u5904\u2014\u2014\u5b83\u9ed8\u9ed8\u7684\u5fcd\u53d7\u7740\u4f60\u7684\u8df5\u8e0f\uff0c\u8fd8\u662f\u4ee5\u5b83\u7684 \u6f6e\u6e7f\u548c\u67d4\u8f6f\u6765\u4fdd\u62a4\u4f60\u7684\u811a\u3002 \u3000\u3000\u4e16\u4e0a\u6709\u4e9b\u4eba\u5c82\u975e\u4e5f\u6b63\u548c\u6ce5\u6cde\u4e00\u6837\uff1f\u4ed6\u4eec\u4e00\u76f4\u5728\u5fcd\u53d7\u7740\u522b\u4eba\u7684\u4fae\u8fb1\u548c\u8f7b\u8511\uff0c\u4f46\u4ed6\u4eec\u5374\u4ece\u65e0 \u6028\u8a00\uff0c\u4ece\u4e0d\u53cd\u51fb\u2026\u2026 \u3000\u3000\u8fd9\u4e16\u4e0a\u82e5\u6ca1\u6709\u6ce5\u6cde\uff0c\u79cd\u7c7d\u53c8\u600e\u4f1a\u53d1\u82bd\uff1f\u6811\u6728\u53c8\u600e\u4f1a\u751f\u6839\uff1f \u3000\u3000\u4ed6\u4eec\u4e0d\u6028\uff0c\u4e0d\u6068\uff0c\u5c31\u56e0\u4e3a\u4ed6\u4eec\u5f88\u4e86\u89e3\u81ea\u5df1\u7684\u4ef7\u503c\u548c\u8d35\u91cd\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u62ac\u8d77\u5934\u3002 \u3000\u3000\u5899\u662f\u65b0\u8fd1\u7c89\u5237\u8fc7\u7684\uff0c\u5b59\u9a7c\u5b50\u90a3\u5c0f\u5e97\u7684\u62db\u724c\u5374\u66f4\u6b8b\u65e7\u4e86\u3002 \u3000\u3000\u4ece\u8fd9\u91cc\u770b\uff0c\u770b\u4e0d\u5230\u5899\u91cc\u7684\u4eba\u3002 \u3000\u3000\u73b0\u5728\u8fd8\u662f\u767d\u5929\uff0c\u5f53\u7136\u4e5f\u770b\u4e0d\u5230\u5899\u91cc\u7684\u706f\u3002 \u3000\u3000\u201c\u5230\u4e86\u665a\u4e0a\uff0c\u5c0f\u697c\u4e0a\u90a3\u76cf\u5b64\u706f\u662f\u5426\u8fd8\u5728\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u4e0d\u4f4f\u53c8\u60f3\u8d77\u4e86\u4ed6\u4e0d\u613f\u60f3\u7684\u4e8b\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u4ed6\u603b\u662f\u5750\u5728\u8fdb\u95e8\u7684\u90a3\u5f20\u684c\u5b50\u4e0a\u7b49\u7740\u90a3 \u76cf\u5b64\u706f\u4eae\u8d77\u3002 \u3000\u3000\u5b59\u9a7c\u5b50\u603b\u662f\u5728\u4e00\u65c1\u9ed8\u9ed8\u7684\u966a\u7740\u3002\u4ed6\u4ece\u4e0d\u5f00\u53e3\uff0c\u4ece\u4e0d\u95ee\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u4e5f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u5403\u665a\u996d\u7684\u65f6\u5019\uff0c\u5ba2\u4eba\u8fd8\u4e0d\u4f1a\u4e0a\u95e8\uff0c \u4e0d\u77e5\u9053\u4e8c\u53d4\u73b0\u5728\u4e8e\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u53c8\u5728\u8d6b\u684c\u5b50\uff1f\u201d \u3000\u3000\u5b59\u9a7c\u5b50\u5e76\u6ca1\u6709\u5728\u62b9\u684c\u5b50\u3002 \u3000\u3000\u6cb9\u6c38\u8fdc\u518d\u4e5f\u4e0d\u80fd\u62b9\u684c\u5b50\u4e86\uff01 \u3000\u3000\u684c\u5b50\u4e0a\u6709\u53ea\u624b\u3002 \u3000\u3000\u624b\u91cc\u8fd8\u6293\u7740\u5757\u62b9\u5e03\uff0c\u6293\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u5c0f\u5e97\u7684\u95e8\u672c\u662f\u5173\u7740\u7684\uff0c\u6572\u95e8\uff0c\u6ca1\u6709\u56de\u5e94\uff0c\u547c\u5524\uff0c\u4e5f\u6ca1\u6709\u56de\u5e94\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6bd4\u674e\u5bfb\u6b22\u66f4\u6025\uff0c\u649e\u5f00\u95e8\uff0c\u5c31\u77a7\u89c1\u4e86\u8fd9\u53ea\u624b\u3002 \u3000\u3000\u4e00\u53ea\u5df2\u88ab\u9f50\u8155\u780d\u4e86\u4e0b\u6765\u7684\u624b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u60ca\uff0c\u51b2\u8fc7\u53bb\uff0c\u6014\u5728\u684c\u5b50\u65c1\u3002 \u3000\u3000\u90a3\u6b63\u662f\u674e\u5bfb\u6b22\u4e24\u5e74\u6765\u6bcf\u5929\u90fd\u5728\u4e0a\u9762\u559d\u9152\u7684\u684c\u5b50\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u8138\u8272\u4e5f\u5df2\u53d1\u9752\uff0c\u4ed6\u8ba4\u5f97\u8fd9\u53ea\u624b\uff0c\u4ed6\u6bd4\u5b59\u5c0f\u7ea2\u66f4\u719f\u6089\uff0c\u4e24\u5e74\u6765\uff0c\u8fd9\u53ea\u624b\u5df2\u4e0d\u77e5\u4e3a \u4ed6\u5012\u8fc7\u591a\u5c11\u6b21\u9152\u3002 \u3000\u3000\u4ed6\u72c2\u9189\u7684\u65f6\u5019\uff0c\u6276\u4ed6\u56de\u623f\u53bb\u7684\u5c31\u662f\u8fd9\u53ea\u624b\u3002 \u3000\u3000\u4ed6\u751f\u75c5\u7684\u65f6\u5019\uff0c\u4f3a\u5019\u4ed6\u6c64\u836f\u7684\u4e5f\u6b63\u662f\u8fd9\u53ea\u624b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8fd9\u53ea\u624b\u5df2\u53d8\u6210\u4e86\u5757\u5e72\u762a\u4e86\u7684\u6b7b\u8089\uff0c\u8840\u5df2\u51dd\u7ed3\uff0c\u7b4b\u5df2\u6536\u7f29\uff0c\u624b\u6307\u7d27\u7d27\u7684\u6293\u7740\u8fd9\u5757\u62b9 \u5e03\uff0c\u5c31\u50cf\u662f\u5728\u6293\u7740\u81ea\u5df1\u7684\u751f\u547d\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u6b63\u5728\u62b9\u684c\u5b50\u7684\u65f6\u5019\u88ab\u4eba\u780d\u65ad\u8fd9\u53ea\u624b\u7684\uff1f \u3000\u3000\u684c\u5b50\u64e6\u5f97\u5f88\u5149\uff0c\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u4ed6\u5728\u62b9\u8fd9\u5f20\u684c\u5b50\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u662f\u4e0d\u662f\u5728\u60f3\u7740\u674e\u5bfb\u6b22\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u89c9\u5f97\u80f8\u4e2d\u4e00\u9635\u7ede\u75db\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u76ee\u4e2d\u7684\u773c\u6cea\u5f00\u59cb\u5411\u5916\u6d41\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u53ea\u624b\u662f\u8c01\u7684\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u91cd\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u560e\u58f0\u9053\uff1a\u201c\u4ed6\u7684\u4eba\u5462\u2026\u2026\u4ed6\u7684\u4eba\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6ca1\u6709\u4eba\uff0c\u5c0f\u5e97\u91cc\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u518d\u5954\u56de\u6765\uff0c\u674e\u5bfb\u6b22\u8fd8\u662f\u7ad9\u5728\u684c\u5b50\u524d\uff0c\u77ac\u4e5f\u4e0d\u77ac\u7684\u76ef\u7740\u8fd9\u53ea\u624b\u3002 \u3000\u3000\u6b7b\u9ed1\u7684\u624b\uff0c\u56db\u6839\u624b\u6307\u90fd\u5df2\u5d4c\u5165\u62b9\u5e03\u91cc\uff0c\u53ea\u6709\u4e00\u6839\u98df\u6307\u5411\u524d\u4f38\u51fa\uff0c\u50f5\u786c\u5f97\u5c31\u50cf\u662f\u4e00\u8282\u8721\uff0c \u7b14\u76f4\u6307\u7740\u524d\u9762\u7684\u7a97\u6237\u3002 \u3000\u3000\u7a97\u6237\u662f\u5f00\u7740\u7684\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u8fd9\u6247\u7a97\u6237\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u76ee\u5149\u4e5f\u968f\u7740\u4ed6\u77a7\u4e86\u8fc7\u53bb\uff0c\u4e24\u4eba\u5ffd\u7136\u540c\u65f6\u63a0\u51fa\u4e86\u7a97\u5b50\u3002 \u3000\u3000\u7a97\u5916\u51b7\u98ce\u523a\u9aa8\uff0c\u51b7\u5f97\u8fde\u6c9f\u6e20\u91cc\u7684\u81ed\u6c34\u90fd\u5df2\u7ed3\u4e86\u51b0\u3002 \u3000\u3000\u4e00\u6761\u66f4\u5c0f\u7684\u5df7\u5b50\uff0c\u6bd4\u6c9f\u6e20\u4e5f\u5bbd\u4e0d\u4e86\u591a\u5c11\uff0c\u4e5f\u8bb8\u8fd9\u6839\u672c\u4e0d\u662f\u6761\u5df7\u5b50\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u6761\u6c9f\u6e20\u3002 \u3000\u3000\u6cbf\u7740\u6c9f\u8d70\uff0c\u8d70\u5230\u5c3d\u5934\uff0c\u5c31\u662f\u4e00\u9053\u5f88\u7a84\u7684\u95e8\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u5bb6\u7684\u540e\u95e8\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u522b \u7684\u8def\u3002 \u3000\u3000\u8fd9\u672c\u662f\u6761\u6b7b\u5df7\u3002 \u3000\u3000\u540e\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u5728\u63a8\u95e8\u7684\u5730\u65b9\u8d6b\u7136\u6709\u4e2a\u6697\u8d64\u8272\u7684\u638c\u5370\u3002 \u3000\u3000\u7528\u8840\u67d3\u6210\u7684\u638c\u5370\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u51b2\u8fc7\u53bb\uff0c\u7a81\u53c8\u987f\u4f4f\uff0c\u6162\u6162\u7684\u8f6c\u56de\u8eab\uff0c\u9762\u5bf9\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u5979\u5634\u5507\u5df2\u88ab\u54ac\u5f97\u51fa\u8840\uff0c\u76ef\u7740\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u4e5f\u65e9\u5df2\u7b97\u51c6\u4e86\u4f60\u8981\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u7740\u5634\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u5148\u5230\u5174\u4e91\u5e84\u53bb\uff0c\u56e0\u4e3a\u4f60\u4e0d\u613f\u518d\u89c1\u5230\u9f99\u5578\u4e91\uff0c\u6240\u4ee5\u4f60\u5fc3\u91cc\u65e0 \u8bba\u591a\u4e48\u6025\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u5148\u5230\u4e8c\u53d4\u5e97\u91cc\u6765\u77a7\u77a7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u95ed\u7740\u5634\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u4e00\u5207\uff0c\u6b63\u90fd\u662f\u4e3a\u4f60\u8bbe\u4e0b\u7684\u5708\u5957\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5634\u95ed\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u7edd\u4e0d\u80fd\u8d70\u8fdb\u8fd9\u6247\u95e8\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u5173\u7cfb\uff0c\u4e0a\u5b98\u91d1\u8679\u5e76\u4e0d\u6025\u7740\u8981\u6740\u6211\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ef\u4ee5\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u975e\u8fdb\u53bb\u4e0d\u53ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u8fd8\u4e0d\u5982\u4e0a\u5b98\u91d1\u8679\u90a3\u4e48\u4e86\u89e3\u6211\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u4ed6\u82e6\u5fc3\u8bbe\u4e0b\u8fd9\u5708\u5957\uff0c\u5c31\u56e0\u4e3a\u4ed6\u77e5\u9053\u6211\u4e5f\u662f\u975e\u8fdb\u53bb\u4e0d\u53ef\u7684\uff0c\u5c31\u7b97\u6709\u4eba\u5df2 \u5c06\u6211\u7684\u4e24\u6761\u817f\u780d\u65ad\uff0c\u6211\u722c\u4e5f\u8981\u722c\u8fdb\u53bb\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u76ef\u7740\u4ed6\uff0c\u70ed\u6cea\u53c8\u5fcd\u4e0d\u4f4f\u8981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u5979\u5ffd\u7136\u6251\u8fc7\u6765\uff0c\u7d27\u7d27\u7684\u62b1\u4f4f\u4e86\u674e\u5bfb\u6b22\uff0c\u70ed\u6cea\u6cbe\u6e7f\u4e86\u4ed6\u7901\u6dec\u7684\u8138\u3002 \u3000\u3000\u5979\u78e8\u64e6\u7740\u4ed6\u7684\u8138\uff0c\u4eff\u4f5b\u8981\u4ee5\u81ea\u5df1\u7684\u773c\u6cea\u6765\u6d17\u53bb\u4ed6\u8138\u4e0a\u7684\u6194\u60b4\u2014\u2014\u4e16\u4e0a\u82e5\u53ea\u6709\u4e00\u6837\u4e8b\u80fd\u6d17 \u53bb\u4eba\u4eec\u7684\u6194\u60b4\uff0c\u90a3\u5c31\u662f\u60c5\u4eba\u7684\u6cea\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u50f5\u786c\u7684\u56db\u80a2\u6e10\u6e10\u67d4\u8f6f\uff0c\u7ec8\u4e8e\u4e5f\u5fcd\u4e0d\u4f4f\u4f38\u51fa\u624b\uff0c\u62b1\u4f4f\u4e86\u5979\u3002 \u3000\u3000\u4ed6\u4eec\u62b1\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u662f\u4ed6\u4eec\u7b2c\u4e00\u6b21\u62e5\u62b1\u2014\u2014\u8bf4\u4e0d\u5b9a\u4e5f\u662f\u6700\u540e\u4e00\u6b21\uff01 \u3000\u3000\u4eff\u4f5b\u8fde\u9633\u5149\u90fd\u4e0d\u613f\u7167\u8000\u6c9f\u6e20\uff0c\u5df7\u5b50\u91cc\u9eef\u5f97\u5c31\u50cf\u662f\u9ec4\u660f\u3002 \u3000\u3000\u95e8\u540e\u9762\u66f4\u9eef\u3002 \u3000\u3000\u63a8\u5f00\u95e8\uff0c\u5c31\u6709\u4e00\u80a1\u4ee4\u4eba\u4f5c\u5455\u7684\u81ed\u6c14\u6251\u9f3b\u800c\u6765\u3002 \u3000\u3000\u662f\u8840\u8165\u6c14\uff01 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4eec\u5c31\u542c\u5230\u4e00\u79cd\u5947\u5f02\u7684\u58f0\u97f3\uff0c\u4eff\u4f5b\u662f\u91ce\u517d\u4e34\u6b7b\u524d\u7684\u5598\u606f\uff0c\u53c8\u4eff\u4f5b\u662f\u9b54\u9b3c\u5728\u5730\u72f1\u4e2d \u5450\u558a\uff01 \u3000\u3000\u58f0\u97f3\u8d6b\u7136\u6b63\u662f\u4ece\u5730\u4e0b\u53d1\u51fa\u6765\u7684\uff01 \u3000\u3000\u5730\u4e0b\u6b63\u6709\u5341\u51e0\u4e2a\u4eba\uff0c\u95ed\u7740\u5634\u54ac\u7740\u7259\uff0c\u5b9b\u5982\u91ce\u517d\u822c\u5728\u4f5c\u6b8a\u6b7b\u640f\u6597\uff01 \u3000\u3000\u6ca1\u6709\u4eba\u5f00\u53e3\uff0c\u751a\u81f3\u8fde\u5200\u780d\u5728\u8eab\u4e0a\u4e5f\u4e0d\u80af\u5f00\u53e3\u3002 \u3000\u3000\u672c\u6765\u4e00\u5171\u6709\u4e8c\u5341\u516d\u4e2a\u4eba\uff0c\u73b0\u5728\u5df2\u6709\u4e5d\u4e2a\u5012\u4e86\u4e0b\u53bb\uff0c\u5269\u4e0b\u7684\u5341\u516b\u4e2a\u5206\u6210\u4e24\u8fb9\uff0c\u5360\u4f18\u52bf\u7684\u4e00 \u8fb9\u4eba\u6570\u8fdc\u6bd4\u53e6\u4e00\u8fb9\u591a\u51fa\u5f88\u591a\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u5341\u4e8c\u4e2a\u4eba\uff0c\u90fd\u7a7f\u7740\u6697\u9ec4\u8272\u7684\u8863\u670d\uff0c\u7528\u7684\u5927\u591a\u6570\u662f\u6c5f\u6e56\u4e2d\u6781\u5c11\u89c1\u7684\u5916\u95e8\u5175\u5203\uff0c\u6709\u4e2a \u4eba\u624b\u91cc\u7528\u7684\u7adf\u662f\u4e2a\u94c1\u6253\u7684\u7b97\u76d8\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u672c\u6709\u4e5d\u4e2a\u4eba\uff0c\u73b0\u5728\u5df2\u53ea\u5269\u4e0b\u4e94\u4e2a\uff0c\u5176\u4e2d\u8fd8\u6709\u4e2a\u662f\u778e\u5b50\u3002 \u3000\u3000\u8fd8\u6709\u6761\u7cbe\u8d64\u7740\u4e0a\u8eab\u7684\u5927\u6c49\uff0c\u4ed6\u6ca1\u6709\u5175\u5203\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5c31\u662f\u94c1\u6253\u7684\uff01 \u3000\u3000\u5bd2\u5149\u4e00\u95ea\uff0c\u4e00\u67c4\u9c7c\u9cde\u5200\u780d\u5728\u4ed6\u5de6\u80a9\u4e0a\uff0c\u5c31\u50cf\u662f\u780d\u5728\u6728\u5934\u91cc\uff0c\u9510\u5229\u7684\u5200\u950b\u7adf\u88ab\u4ed6\u7684\u8089\u5939\u4f4f \uff0c\u5d4c\u5728\u4ed6\u9aa8\u5934\u91cc\uff01 \u3000\u3000\u9ec4\u8863\u4eba\u7528\u529b\u62bd\u5200\u4e0d\u8d77\uff0c\u5927\u6c49\u7684\u94c1\u638c\u5df2\u51fb\u4e0a\u4e86\u4ed6\u80f8\u819b\uff0c\u4ed6\u4eff\u4f5b\u5df2\u542c\u5230\u81ea\u5df1\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3 \u3002 \u3000\u3000\u201c\u7830\u201d\u7684\uff0c\u4ed6\u6574\u4e2a\u4eba\u90fd\u88ab\u6253\u5f97\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u5927\u6c49\u7684\u5de6\u81c2\u4e5f\u5df2\u65e0\u6cd5\u62ac\u8d77\uff0c\u5ffd\u7136\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u9000\uff0c\u6211\u6321\u4f4f\u4ed6\u4eec\u2026\u2026\u5feb\u9000\uff01\u201d \u3000\u3000\u6ca1\u6709\u4eba\u9000\uff0c\u4e5f\u6ca1\u6709\u4eba\u7b54\u6d3b\u3002 \u3000\u3000\u672c\u5df2\u5012\u5728\u5730\u4e0a\u7684\u4e00\u4e2a\u4eba\u7a81\u7136\u8dc3\u8d77\uff0c\u5636\u58f0\u5927\u547c\u9053\uff1a\u201c\u4e0d\u80fd\u9000\uff0c\u6211\u4eec\u6b7b\u4e5f\u8981\u628a\u4ed6\u5e26\u51fa\u53bb\uff01\u201d \u3000\u3000\u8fd9\u662f\u4e2a\u5730\u4e0b\u5ba4\uff0c\u7ec8\u5e74\u90fd\u71c3\u7740\u706f\u3002 \u3000\u3000\u706f\u5d4c\u5728\u5899\u4e0a\uff0c\u9634\u607b\u4fa7\u7684\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u5979\u7adf\u662f\u4e2a\u5973\u4eba\uff0c\u53c8\u9ad8\u53c8\u5927\u53c8\u80d6\u7684\u5973\u4eba\uff0c\u4e00\u6761\u5200\u75a4\u81ea \u5e26\u7740\u9ed1\u773c\u7f69\u7684\u773c\u775b\u76f4\u5212\u5230\u5634\u89d2\u3002 \u3000\u3000\u5979\u7684\u53f3\u773c\u5df2\u778e\u4e86\uff0c\u53ea\u5269\u4e0b\u4e00\u53ea\u5de6\u773c\uff0c\u77aa\u7740\u90a3\u5927\u6c49\u3002 \u3000\u3000\u8fd9\u53ea\u773c\u775b\u91cc\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u4ec7\u6068\uff0c\u4ec7\u6068\u2026\u2026\u81f3\u6b7b\u4e0d\u89e3\u7684\u4ec7\u6068\u3002 \u3000\u3000\u201c\u5973\u5c60\u6237\u201d\u7fc1\u5927\u5a18\uff01 \u3000\u3000\u8fd9\u5927\u6c49\u53c8\u662f\u8c01\uff1f\u96be\u9053\u662f\u4e00\u522b\u591a\u5e74\u65e0\u6d88\u606f\u7684\u94c1\u4f20\u7532\uff01 \u3000\u3000\u4e0d\u9519\uff0c\u7684\u786e\u662f\u4ed6\uff01 \u3000\u3000\u9664\u4e86\u94c1\u4f20\u7532\u5916\uff0c\u8c01\u6709\u8fd9\u4e48\u786c\u7684\u9aa8\u5934\u3002 \u3000\u3000\u7fc1\u5927\u5a18\u6323\u624e\u7740\uff0c\u8fd8\u60f3\u722c\u8d77\u6765\uff0c\u76ef\u7740\u94c1\u4f20\u7532\uff0c\u560e\u58f0\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6211\u4eec\u7684\uff0c\u9664\u4e86\u6211\u4eec\u5916\uff0c\u8c01 \u4e5f\u4e0d\u80fd\u52a8\u4ed6\u4e00\u6839\u624b\u6307\uff0c\u8c01\u4e5f\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u201c\u5220\u201d\u7684\uff0c\u5bd2\u5149\u53c8\u4e00\u95ea\uff0c\u5979\u518d\u6b21\u5012\u4e0b\u3002 \u3000\u3000\u8fd9\u6b21\u5979\u6c38\u8fdc\u90fd\u65e0\u6cd5\u518d\u7ad9\u8d77\u6765\u4e86\uff01 \u3000\u3000\u53ef\u662f\u5979\u5269\u4e0b\u7684\u90a3\u53ea\u773c\u775b\u8fd8\u662f\u77aa\u5f97\u5f88\u5927\uff0c\u8fd8\u662f\u77aa\u7740\u94c1\u4f20\u7532\u3002 \u3000\u3000\u5979\u6b7b\u7684\u65e2\u65e0\u75db\u82e6\uff0c\u4e5f\u65e0\u6050\u60e7\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5fc3\u91cc\u5269\u4e0b\u7684\u53ea\u6709\u4ec7\u6068\uff0c\u9664\u4e86\u4ec7\u6068\u5916\uff0c\u5979\u4ec0\u4e48\u90fd\u611f\u89c9\u4e0d\u5230\u3002 \u3000\u3000\u94c1\u4f20\u7532\u54ac\u7740\u7259\uff0c\u4ed6\u8eab\u4e0a\u53c8\u88ab\u523a\u4e86\u4e00\u5251\uff0c\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u4eec\u771f\u7684\u4e0d\u8d70\uff1f\u2026\u2026\u4f60\u4eec\u82e5\u5168\u90fd\u6b7b\u4e86 \uff0c\u53c8\u600e\u80fd\u5c06\u6211\u5e26\u8d70\uff1f\u201d \u3000\u3000\u778e\u5b50\u5ffd\u7136\u9634\u607b\u607b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5168\u90fd\u6b7b\u4e86\uff0c\u4e5f\u8981\u5c06\u4f60\u7684\u9b3c\u9b42\u5e26\u8d70\uff01\u201d \u3000\u3000\u4ed6\u6b66\u529f\u867d\u7136\u6bd4\u6709\u773c\u775b\u7684\u4eba\u8fd8\u53ef\u6015\uff0c\u53ef\u6bd5\u7adf\u662f\u4e2a\u778e\u5b50\uff0c\u4ea4\u624b\u65f6\u5168\u51ed\u7740\u8033\u6735\u201c\u542c\u98ce\u8fa8\u4f4d\u201d\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u5728\u52a8\u5634\u7684\u65f6\u5019\uff0c\u8033\u6735\u90fd\u4e0d\u4f1a\u50cf\u5e73\u65f6\u90a3\u4e48\u7075\u7684\uff0c\u4ed6\u4e24\u53e5\u6d3b\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u524d\u80f8\u5df2\u88ab\u4e00 \u67c4\u864e\u5934\u94a9\u5212\u7834\u4e86\u9053\u8840\u53e3\uff01 \u3000\u3000\u94a9\u518d\u626c\u8d77\uff0c\u94a7\u950b\u4e0a\u5df2\u6302\u7740\u6761\u8840\u6dcb\u6dcb\u7684\u8089\u3002 \u3000\u3000\u8840\uff0c\u8089\uff01 \u3000\u3000\u94c1\u4f20\u7532\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u3000\u3000\u4ed6\u5df2\u6740\u8fc7\u4eba\uff0c\u4f46\u5374\u7edd\u4e0d\u662f\u51f6\u624b\uff0c\u4ed6\u7684\u9aa8\u5934\u867d\u786c\uff0c\u5fc3\u5374\u662f\u8f6f\u7684\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u51e0\u4e4e\u8fde\u624b\u90fd\u8f6f\u4e86\uff0c\u5df2\u65e0\u6cd5\u518d\u6740\u4eba\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u82e5\u662f\u6b7b\u5728\u4f60\u4eec\u624b\u4e0a\u5462\uff1f\u201d \u3000\u3000\u778e\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b\u672c\u5c31\u548c\u6211\u4eec\u65e0\u5173\uff0c\u6211\u4eec\u672c\u5c31\u662f\u4e3a\u4e86\u4f60\u6765\u7684\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u5389\u58f0\u9053\uff1a\u201c\u4e2d\u539f\u516b\u4e49\u82e5\u4e0d\u80fd\u4eb2\u624b\u53d6\u4f60\u7684\u547d\uff0c\u6b7b\u4e0d\u7791\u76ee\uff01\u201d \u3000\u3000\u8fd9\u4eba\u6ee1\u8138\u9ebb\u5b50\uff0c\u7528\u7684\u662f\u4e00\u957f\u4e00\u77ed\u4e24\u628a\u5200\uff0c\u6b63\u662f\u5317\u6d3e\u201c\u2018\u9634\u9633\u5200\u201d\u7684\u552f\u4e00\u4f20\u4eba\u516c\u5b59\u96e8\u3002 \u3000\u3000\u94c1\u4f20\u7532\u5ffd\u7136\u7b11\u4e86\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4f55\u800c\u7b11\uff1f \u3000\u3000\u4ed6\u7b11\u5f97\u5b9e\u5728\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\uff0c\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u53ea\u4e0d\u8fc7\u60f3\u4eb2\u624b\u6740\u4e86\u6211\uff0c\u8fd9\u5bb9\u6613\u2026\u2026\u201d \u3000\u3000\u4ed6\u53cd\u624b\u4e00\u638c\uff0c\u51fb\u9000\u4e86\u9762\u524d\u7684\u9ec4\u8863\u4eba\uff0c\u8eab\u4f53\u7a81\u7136\u5411\u516c\u5b59\u96e8\u51b2\u4e86\u8fc7\u53bb\u2014\u2014\u5bf9\u51c6\u516c\u5b59\u96e8\u7684\u5200\u950b \u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u516c\u5b59\u96e8\u4e00\u60ca\uff0c\u77ed\u5200\u5df2\u523a\u5165\u4e86\u94c1\u4f20\u7532\u7684\u80f8\u819b\uff01 \u3000\u3000\u94c1\u4f20\u7532\u80f8\u819b\u8fd8\u5728\u5f80\u524d\u633a\uff0c\u725b\u4e00\u822c\u5598\u606f\u7740\uff0c\u9053\uff1a\u201c\u73b0\u5728\u2026\u2026\u6211\u7684\u503a\u603b\u53ef\u8fd8\u6e05\u4e86\u5427\uff01\u4f60\u4eec\u8fd8 \u4e0d\u8d70\uff1f\u201d \u3000\u3000\u516c\u5b59\u96e8\u7684\u8138\u5728\u626d\u66f2\uff0c\u5ffd\u7136\u72c2\u543c\u4e00\u58f0\uff0c\u62d4\u51fa\u4e86\u5200\u3002 \u3000\u3000\u9c9c\u8840\u96e8\u70b9\u822c\u6e85\u5728\u4ed6\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u4ed6\u543c\u58f0\u7a81\u7136\u4e2d\u65ad\uff0c\u6251\u5730\u5012\u4e0b\uff0c\u80cc\u810a\u4e0a\u63d2\u7740\u67c4\u4e09\u5c3a\u82b1\u67aa\u3002 \u3000\u3000\u67aa\u5934\u7684\u7ea2\u7f28\u8fd8\u5728\u4e0d\u505c\u7684\u98a4\u6296\u3002 \u3000\u3000\u94c1\u4f20\u7532\u4e5f\u5df2\u5012\u4e0b\uff0c\u8fd8\u5728\u91cd\u590d\u7740\u90a3\u53e5\u6d3b\u3002 \u3000\u3000\u6211\u7684\u503a\u603b\u7b97\u8fd8\u6e05\u4e86\u2026\u2026\u4f60\u4eec\u4e3a\u4f55\u8fd8\u4e0d\u8d70\u3002 \u3000\u3000\u4ed6\u77a7\u7740\u53e6\u4e00\u67c4\u82b1\u67aa\u5df2\u5411\u4ed6\u523a\u4e86\u4e0b\u6765\uff0c\u65e2\u4e0d\u62db\u67b6\uff0c\u4e5f\u4e0d\u95ea\u907f\u3002 \u7b2c\u4e03\u5341\u4e5d\u7ae0\u3000\u4e49\u6c14\u7684\u670b\u53cb>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u4e03\u5341\u4e5d\u7ae0\u3000\u4e49\u6c14\u7684\u670b\u53cb \u3000\u3000\u516c\u5b59\u96e8\u7a81\u53c8\u5728\u543c\u4e00\u58f0\uff0c\u6251\u5728\u4ed6\u8eab\u4e0a\uff0c\u560e\u58f0\u9053\uff1a\u201c\u6211\u4eec\u4e00\u5b9a\u9519\u4e86\uff0c\u4ed6\u7edd\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u58f0\u97f3\u53c8\u4e2d\u65ad\u3002 \u3000\u3000\u516c\u5b59\u96e8\u80cc\u4e0a\u53c8\u591a\u4e86\u67c4\u82b1\u67aa\uff0c\u67aa\uff01\u53cc\u67aa\uff01 \u3000\u3000\u67aa\u62d4\u8d77\uff0c\u5728\u51c4\u4fa7\u7684\u706f\u5149\u4e0b\u770b\u6765\uff0c\u5730\u5ba4\u4e2d\u5c31\u50cf\u662f\u8ff7\u6f2b\u7740\u4e00\u5c42\u96fe\u3002 \u3000\u3000\u7c89\u7ea2\u8272\u7684\u96fe\u3002 \u3000\u3000\u8840\u96fe\uff01 \u3000\u3000\u4e8c\u5341\u516d\u4eba\u4e2d\uff0c\u5df2\u6709\u5341\u516d\u4eba\u5012\u4e0b\u3002 \u3000\u3000\u6740\u622e\u5374\u4ecd\u672a\u505c\u6b62\uff0c\u5f3a\u5f31\u5df2\u66f4\u60ac\u6b8a\u3002 \u3000\u3000\u4e00\u4e2a\u5356\u8349\u836f\u7684\u90ce\u4e2d\u8eab\u4e0a\u8d1f\u4e86\u516d\u5904\u4f24\uff0c\u5636\u58f0\u9053\uff1a\u201c\u59d3\u94c1\u7684\u65e2\u5df2\u6b7b\u4e86\uff0c\u6211\u4eec\u9000\u5427\uff01\u201d \u3000\u3000\u4ed6\u4eec\u8fd9\u8fb9\u53ea\u5269\u4e0b\u4e09\u4e2a\u4eba\u8fd8\u5728\u8d1f\u9685\u82e6\u6218\uff0c\u5b9e\u5728\u5df2\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u4e00\u4eba\u624b\u6325\u5229\u65a7\uff0c\u4e00\u7740\u201c\u7acb\u5288\u534e\u5c71\u201d\u780d\u4e0b\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u9000\u4e0d\u9000\uff1f\u201d \u3000\u3000\u778e\u5b50\u5389\u58f0\u9053\uff1a\u201c\u9000\uff1f\u4e2d\u539f\u516b\u4e49\u8981\u6b7b\u4e5f\u6b7b\u5728\u4e00\u5904\uff0c\u8c01\u6562\u518d\u8bf4\u9000\u5b57\uff0c\u6211\u5148\u5bb0\u4e86\u4ed6\uff01\u201d \u3000\u3000\u9ec4\u8863\u4eba\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6709\u4e49\u6c14\uff0c\u5927\u7237\u4eec\u4eca\u5929\u5c31\u6210\u5168\u4e86\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u4e5f\u7a81\u7136\u4e2d\u65ad\uff0c\u4e00\u53cc\u773c\u7403\u5b50\u7acb\u523b\u5c31\u5982\u6b7b\u9c7c\u822c\u51f8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6b7b\u4e00\u822c\u9759\u5bc2\u4e2d\uff0c\u53ea\u542c\u4ed6\u5589\u5499\u91cc\u4e0d\u505c\u7684\u201c\u683c\u683c\u201d\u53d1\u54cd\u3002 \u3000\u3000\u4ed6\u8fd9\u53e3\u6c14\u8fd8\u6ca1\u6709\u65ad\uff0c\u5374\u5df2\u5410\u4e0d\u51fa\u6765\uff0c\u7528\u5c3d\u529b\u6c14\u4e5f\u5410\u4e0d\u51fa\u6765\uff0c\u53ea\u56e0\u4ed6\u54bd\u5589\u4e0a\u4e0d\u77e5\u6cb3\u65f6\u5df2\u591a \u4e86\u4e00\u67c4\u5200\u3002 \u3000\u3000\u4e00\u67c4\u4e03\u5bf8\u957f\u7684\u5c0f\u5200\uff1a \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u6240\u6709\u7684\u52a8\u4f5c\u7a81\u7136\u5168\u90e8\u505c\u6b62\uff0c\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd\u5728\u76ef\u7740\u8fd9\u67c4\u5200\uff01 \u3000\u3000\u8c01\u4e5f\u6ca1\u6709\u770b\u5230\u8fd9\u67c4\u5200\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u6765\u7684\uff0c\u4f46\u5374\u5168\u90e8\u77e5\u9053\u662f\u4ec0\u4e48\u4eba\u6765\u4e86\u3002 \u3000\u3000\u5730\u5ba4\u7684\u4eba\u53e3\u5c31\u5728\u89d2\u843d\u91cc\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c31\u5728\u90a3\u7f69\u7ad9\u7740\u3002 \u3000\u3000\u4f46\u5374\u6ca1\u6709\u4eba\u6562\u62ac\u5934\u53bb\u77a7\uff0c\u6bcf\u4e2a\u4eba\u90fd\u751f\u6015\u81ea\u5df1\u4e00\u62ac\u5934\uff0c\u90a3\u67c4\u8ffd\u9b42\u593a\u547d\u7684\u5200\u5c31\u4f1a\u65e0\u5f71\u65e0\u8e2a\u7684 \u98de\u8fc7\u6765\uff0c\u5272\u65ad\u81ea\u5df1\u7684\u5589\u7ba1\uff0c\u523a\u4eba\u81ea\u5df1\u7684\u54bd\u5589\uff01 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u201c\u91d1\u94b1\u5e2e\u201d\u6700\u5fe0\u5b9e\u3001\u6700\u5f97\u529b\u7684\u90e8\u5c5e\uff0c\u7edd\u6ca1\u4e00\u4e2a\u662f\u80c6\u5c0f\u6015\u6b7b\u7684\u4eba\uff0c\u4f46\u73b0\u5728\u4ed6\u4eec\u5df2 \u592a\u7d2f\u3001\u592a\u75b2\u5026\uff0c\u770b\u5230\u4e86\u5927\u591a\u6b7b\u4ea1\uff0c\u5927\u591a\u8840\u8165\u3002 \u3000\u3000\u8fd9\u5df2\u4f7f\u4ed6\u4eec\u4e27\u5931\u4e86\u5927\u90e8\u5206\u52c7\u6c14\uff0c\u4f55\u51b5\uff0c\u201c\u5c0f\u674e\u98de\u5200\u201d\u5728\u6c5f\u6e56\u4eba\u5fc3\u76ee\u4e2d\u5df2\u4e0d\u4ec5\u662f\u4e00\u67c4\u5200\uff0c \u800c\u662f\u4e00\u79cd\u6076\u9b54\u7684\u5316\u8eab\uff01 \u3000\u3000\u73b0\u5728\uff0c\u201c\u5c0f\u674e\u98de\u5200\u201d\u8fd9\u56da\u4e2a\u5b57\u66f4\u51e0\u4e4e\u53d8\u5f97\u548c\u201c\u6b7b\u4ea1\u201d\u540c\u6837\u610f\u4e49\u3002 \u3000\u3000\u4e5f\u8bb8\u76f4\u5230\u73b0\u5728\u4ed6\u4eec\u624d\u61c2\u5f97\u6b7b\u4ea1\u7684\u771f\u6b63\u610f\u4e49\u3002 \u3000\u3000\u4ed6\u4eec\u540c\u4f34\u7684\u5c38\u4f53\uff0c\u5c31\u5012\u5728\u4ed6\u4eec\u811a\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u3002\u4e00\u77ac\u95f4\u4ee5\u524d\uff0c\u4ed6\u8fd8\u662f\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\u3002 \u3000\u3000\u7136\u540e\u5c0f\u674e\u98de\u5200\u5ffd\u7136\u6765\u4e86\uff0c\u4e8b\u5148\u5b8c\u5168\u6ca1\u6709\u4e1d\u6beb\u9884\u5146\uff0c\u8fd9\u6d3b\u751f\u751f\u7684\u4eba\u5ffd\u7136\u5c31\u53d8\u6210\u4e86\u4e00\u5177\u5c38\u4f53 \u3002 \u3000\u3000\u4ed6\u7684\u751f\u547d\u5ffd\u7136\u5c31\u53d8\u5f97\u6beb\u65e0\u610f\u4e49\uff0c\u7edd\u4e0d\u4f1a\u6709\u4eba\u5173\u5fc3\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u6bd4\u8fd9\u79cd\u7a81\u6765\u7684\u53d8\u5316\u66f4\u4ee4\u4eba\u6050\u60e7\uff01\u4ed6\u4eec\u6050\u60e7\u7684\u4e5f\u8bb8\u5e76\u4e0d\u662f\u6b7b\uff0c\u800c\u662f \u8fd9\u79cd\u6050\u60e7\u7684\u672c\u8eab\u3002 \u3000\u3000\u90a3\u778e\u5b50\u7a81\u7136\u9053\uff1a\u201c\u5c0f\u674e\u63a2\u82b1\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u4ec0\u4e48\u77a7\u4e0d\u89c1\uff0c\u4e5f\u6ca1\u6709\u542c\u89c1\u4efb\u4f55\u58f0\u97f3\uff0c\u4f46\u5374\u4e5f\u5df2\u611f\u89c9\u5230\u674e\u5bfb\u6b22\u7684\u5b58\u5728\uff0c\u4ed6\u4f3c\u5df2\u55c5\u5230 \u4e86\u4e00\u79cd\u6151\u4eba\u7684\u6740\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u7684\uff01\u201d \u3000\u3000\u778e\u5b50\u957f\u957f\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u6162\u6162\u7684\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u91d1\u98ce\u767d\u548c\u90a3\u6a35\u592b\u4e5f\u8ddf\u7740\u5750\u4e86\u4e0b\u53bb\uff0c\u5c31\u5750\u5728\u516c\u5b59\u96e8\u548c\u94c1\u4f20\u7532\u7684\u8840\u6cca\u4e2d\uff0c\u53ef\u662f\uff0c\u770b\u4ed6\u4eec\u7684\u795e \u60c5\uff0c\u5374\u50cf\u662f\u5df2\u5750\u5728\u53e6\u4e00\u4e2a\u4e16\u754c\u91cc\u3002 \u3000\u3000\u90a3\u4e16\u754c\u91cc\u65e2\u6ca1\u6709\u4ec7\u6068\uff0c\u4e5f\u6ca1\u6709\u75db\u82e6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u6162\u6162\u7684\u8d70\u5230\u90a3\u4e9b\u9ec4\u8863\u4eba\u9762\u524d\u3002 \u3000\u3000\u4ed6\u7684\u4e00\u53cc\u624b\u662f\u7a7a\u7740\u7684\uff0c\u6ca1\u6709\u5200\u3002 \u3000\u3000\u5200\u4eff\u4f5b\u662f\u5728\u4ed6\u7684\u773c\u775b\u91cc\u3002 \u3000\u3000\u4ed6\u76ef\u7740\u4ed6\u4eec\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u4eec\u5e26\u6765\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u7684\u773c\u775b\u5168\u90fd\u5728\u77a7\u7740\u81ea\u5df1\u7684\u811a\u5c16\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u903c\u4f60\u4eec\uff0c\u5e0c\u671b\u4f60\u4eec\u4e5f\u83ab\u8981\u903c\u6211\u3002\u201d \u3000\u3000\u7ad9\u5728\u4ed6\u5bf9\u9762\u7684\u4e00\u4e2a\u9ec4\u8863\u4eba\u8138\u4e0a\u4e0d\u505c\u7684\u5728\u5192\u6c57\uff0c\u5168\u8eab\u4e0d\u505c\u7684\u53d1\u6296\uff0c\u7a81\u7136\u560e\u58f0\u9053\uff1a\u201c\u4f60\u8981\u627e \u5b59\u9a7c\u5b50\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8fd9\u9ec4\u8863\u4eba\u6d41\u7740\u6c57\u7684\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e86\u4e00\u79cd\u5947\u7279\u7684\u72de\u7b11\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\uff0c\u6211\u5e26\u4f60\u53bb\u627e\u4ed6\uff0c\u4f60 \u8ddf\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u4ed6\u7528\u7684\u662f\u864e\u5934\u94a9\uff0c\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4ed6\u7684\u624b\u5df1\u62ac\u8d77\uff0c\u94a9\u7684\u62a4\u624b\u5df2\u523a\u5165\u4e86\u4ed6\u81ea\u5df1\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u5df2\u65e0\u6cd5\u518d\u5fcd\u53d7\u8fd9\u79cd\u6050\u60e7\uff0c\u6b7b\uff0c\u53cd\u800c\u53d8\u6210\u4e86\u6700\u5feb\u7684\u89e3\u8131\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u770b\u7740\u4ed6\u5012\u4e0b\u53bb\uff0c\u624b\u6e10\u6e10\u63e1\u7d27\u3002 \u3000\u3000\u201c\u5b59\u9a7c\u5b50\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u9ec4\u8863\u4eba\u7684\u6b7b\uff0c\u5c31\u662f\u7b54\u590d\uff01 \u3000\u3000\u4f46\u6797\u8bd7\u97f3\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u4e2d\u5ffd\u53c8\u9732\u51fa\u4e86\u6050\u60e7\u4e4b\u8272\uff0c\u76ee\u5149\u6162\u6162\u7684\u4ece\u8840\u6cca\u4e2d\u7684\u5c38\u4f53\u4e0a\u626b\u8fc7\uff0c\u77b3\u5b54\u6162\u6162\u7684\u6536\u7f29 \u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u542c\u5230\u4e86\u94c1\u4f20\u7532\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u53c8\u5728\u725b\u4e00\u822c\u5598\u606f\u7740\uff0c\u8840\u548c\u6c57\u6df7\u5408\u7740\u4ece\u4ed6\u8138\u4e0a\u6d41\u8fc7\uff0c\u6d41\u8fc7\u4ed6\u7684\u773c\u5e18\uff0c\u4ed6\u8fde\u773c\u775b\u90fd\u5f20\u4e0d\u5f00 \uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u6613\u660e\u5802\u2026\u2026\u6613\u4e8c\u54e5\u2026\u2026\u201d \u3000\u3000\u778e\u5b50\u77f3\u677f\u822c\u7684\u8138\u4e5f\u5df2\u626d\u66f2\uff0c\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u7684\u503a\u8fd8\u6e05\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6613\u660e\u5802\u9053\uff1a\u201c\u4f60\u7684\u503a\u5df2\u8fd8\u6e05\u4e86\u3002\u201d \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u6709\u4ef6\u4e8b\u8981\u8bf4\u3002\u201d \u3000\u3000\u6613\u660e\u5802\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u201c\u6211\u867d\u7136\u5bf9\u4e0d\u8d77\u7fc1\u5927\u54e5\uff0c\u4f46\u5374\u7edd\u6ca1\u6709\u51fa\u5356\u4ed6\uff0c\u6211\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u6613\u660e\u5802\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u8bf4\uff0c\u6211\u5df2\u660e\u767d\u3002\u201d \u3000\u3000\u4ed6\u7684\u786e\u5df2\u660e\u767d\u3002 \u3000\u3000\u4e00\u4e2a\u51fa\u5356\u670b\u53cb\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u5728\u8fd9\u6837\u751f\u6b7b\u5173\u5934\u4e3a\u4e86\u670b\u53cb\u727a\u7272\u81ea\u5df1\u7684\u3002 \u3000\u3000\u8fd9\u4e0d\u4f46\u6613\u660e\u5802\u5df2\u660e\u767d\uff0c\u91d1\u98ce\u767d\u548c\u90a3\u6a35\u592b\u4e5f\u5f88\u660e\u767d\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u4eec\u660e\u767d\u5f97\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u6613\u660e\u5802\u90a3\u5df2\u778e\u4e86\u51e0\u5341\u5e74\u7684\u773c\u775b\u91cc\uff0c\u7adf\u6162\u6162\u7684\u6d41\u51fa\u4e86\u4e24\u6ef4\u773c\u6cea\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5728\u770b\u7740\uff0c\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u6b21\u77e5\u9053\u778e\u5b50\u539f\u6765\u4e5f\u4f1a\u6d41\u6cea\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u662f\u65e9\u5df2\u70ed\u6cea\u76c8\u7736\u3002 \u3000\u3000\u70ed\u6cea\u5c31\u6ef4\u5728\u94c1\u4f20\u7532\u5df2\u9010\u6e10\u53d1\u51b7\u7684\u8138\u4e0a\uff0c\u4ed6\u4fef\u4e0b\u8eab\uff0c\u7528\u8863\u89d2\u8f7b\u8f7b\u64e6\u62ed\u7740\u94c1\u4f20\u7532\u8138\u4e0a\u7684\u8840\u548c \u6c57\u3002 \u3000\u3000\u94c1\u4f20\u7532\u7684\u773c\u775b\u7741\u5f00\uff0c\u8fd9\u624d\u77a7\u89c1\u4ed6\uff0c\u5931\u58f0\u9053\uff1a\u201c\u5c11\u7237\u662f\u4f60\uff0c\u4f60\u2026\u2026\u4f60\u679c\u7136\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u53c8\u60ca\u53c8\u559c\uff0c\u6323\u624e\u7740\u8981\u722c\u8d77\uff0c\u53c8\u8dcc\u4e0b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8dea\u4e86\u4e0b\u53bb\uff0c\u8dea\u5728\u4ed6\u8eab\u65c1\uff0c\u9053\uff1a\u201c\u6211\u6765\u4e86\uff0c\u6240\u4ee5\u6709\u4ec0\u4e48\u8bdd\u4f60\u90fd\u53ef\u4ee5\u7b49\u7740\u6162\u6162\u8bf4\u3002\u201d \u3000\u3000\u94c1\u4f20\u7532\u7528\u529b\u6447\u4e86\u6447\u5934\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u6211\u5df2\u6b7b\u800c\u65e0\u61be\uff0c\u7528\u4e0d\u7740\u518d\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5fcd\u7740\u6cea\uff0c\u9053\uff1a\u201c\u4f46\u6709\u4e9b\u8bdd\u4f60\u8fd8\u662f\u8981\u8bf4\u7684\uff0c\u4f60\u65e2\u7136\u5e76\u6ca1\u6709\u51fa\u5356\u7fc1\u5927\u54e5\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8bf4 \u660e\uff1f\u4e3a\u4ec0\u4e48\u8981\u9003\uff1f\u201d \u3000\u3000\u94c1\u4f20\u7532\u9053\uff1a\u201c\u6211\u9003\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e3a\u4e86\u8c01\uff1f\u201d \u3000\u3000\u94c1\u4f20\u7532\u53c8\u6447\u4e86\u6447\u5934\uff0c\u773c\u5e18\u6162\u6162\u7684\u76cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u56db\u80a2\u867d\u5df2\u56e0\u75db\u82e6\u800c\u75c9\u631b\uff0c\u4f46\u8138\u8272\u5374\u5f88\u5b89\u5b81\uff0c\u5634\u89d2\u751a\u81f3\u8fd8\u5e26\u7740\u4e00\u4e1d\u606c\u9759\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4ed6\u6b7b\u5f97\u5f88\u5e73\u9759\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8981\u6b7b\u5f97\u5e73\u9759\uff0c\u53ef\u771f\u662f\u4e0d\u5bb9\u6613\uff01 \u3000\u3000\u674e\u5bfb\u6b22\u52a8\u4e5f\u4e0d\u52a8\u7684\u8dea\u7740\uff0c\u4f3c\u5df2\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\u94c1\u4f20\u7532\u662f\u4e3a\u4e86\u8c01\u800c\u6b7b\u7684\u3002 \u3000\u3000\u4ed6\u5fc5\u5b9a\u6bd4\u674e\u5bfb\u6b22\u5148\u56de\u5230\u5174\u4e91\u5e84\uff0c\u67e5\u51fa\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u9634\u8c0b\uff0c\u5c31\u62a2\u5148\u8d76\u5230\u8fd9\u91cc\uff0c\u53ea\u8981\u77e5\u9053\u674e \u5bfb\u6b22\u6709\u5371\u9669\uff0c\u65e0\u8bba\u4ec0\u4e48\u5730\u65b9\u4ed6\u90fd\u4f1a\u8d76\u7740\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u53c8\u600e\u4f1a\u77e5\u9053\u4e0a\u5b98\u91d1\u8679\u8fd9\u9634\u8c0b\u7684\u5462\uff1f \u3000\u3000\u4ed6\u548c\u7fc1\u5929\u6770\u7fc1\u8001\u5927\u4e4b\u95f4\uff0c\u7a76\u7adf\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u4e3a\u4f55\u81f3\u6b7b\u8fd8\u4e0d\u80af\u8bf4\u660e\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u5728\u9690\u7792\u7740\u4ec0\u4e48\u79d8\u5bc6\uff1f\u4f60\u81f3\u5c11\u603b\u8be5\u5bf9\u6211\u8bf4\u51fa\u624d\u662f\uff0c\u4f60\u7eb5\u7136\u6b7b\u800c\u65e0 \u61be\uff0c\u53ef\u662f\u6211\uff0c\u6211\u600e\u4e48\u80fd\u5fc3\u5b89\u5462\uff1f\u201d \u3000\u3000\u91d1\u98ce\u767d\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4ed6\u9690\u7792\u7740\u7684\u4e8b\uff0c\u4e5f\u8bb8\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6115\u7136\uff0c\u9053\uff1a\u201c\u4f60\uff1f\u2026\u2026\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u98ce\u767d\u7684\u8138\u672c\u662f\u9edd\u9ed1\u7684\uff0c\u73b0\u5728\u5374\u82cd\u767d\u5f97\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u7528\u529b\u54ac\u7740\u7259\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u7fc1\u8001\u5927\u5bf9\u670b\u53cb\u7684\u4e49\u6c14\uff0c\u5929\u4e0b\u7686\u77e5\uff0c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u201c\u53ea\u8981\u6709\u670b\u53cb\u627e\u4ed6\uff0c\u4ed6\u51e0\u4e4e\u662f\u6709\u6c42\u5fc5\u5e94\uff0c\u6240\u4ee5\u4ed6\u7684\u5f00\u9500\u4e00\u5411\u5f88\u5927\uff0c\u4f46\u4ed6\u5374\u4e0d \u50cf\u4f60\uff0c\u4ed6\u5e76\u6ca1\u6709\u4e00\u4e2a\u505a\u6237\u90e8\u5c1a\u4e66\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u3002 \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4e00\u76f4\u90fd\u5728\u95f9\u7a77\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u53c8\u95f9\u7a77\uff0c\u53c8\u597d\u670b\u53cb\uff0c\u53c8\u8981\u9762\u5b50\uff0c\u5c31\u53ea\u6709 \u5728\u6697\u4e2d\u60f3\u522b\u7684\u6cd5\u5b50\u5728\u5f25\u8865\u4e8f\u7a7a\u3002\u201d \u3000\u3000\u90a3\u6a35\u592b\u8038\u7136\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u2026\u7fc1\u8001\u5927\u5728\u6697\u4e2d\u505a\u6ca1\u672c\u94b1\u7684\u751f\u610f\uff1f\u201d \u3000\u3000\u91d1\u98ce\u767d\u9eef\u7136\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u662f\u6211\u5728\u65e0\u610f\u4e2d\u53d1\u73b0\u7684\uff0c\u53ef\u662f\u6211\u4e00\u76f4\u4e0d\u5fcd\u8bf4\uff0c\u56e0\u4e3a\u7fc1 \u8001\u5927\u90a3\u6837\u505a\uff0c\u7684\u786e\u662f\u60c5\u4e0d\u5f97\u5df2\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u5927\u58f0\u9053\uff1a\u201c\u4f46\u7fc1\u8001\u5927\u4e0b\u624b\u7684\u5bf9\u8c61\uff0c\u5374\u5fc5\u5b9a\u662f\u7f6a\u6709\u5e94\u5f97\u7684\uff0c\u4ed6\u505a\u7684\u867d\u7136\u662f\u6ca1\u6709\u672c\u94b1 \u7684\u4e70\u5356\uff0c\u53ef\u6ca1\u6709\u6127\u5bf9\u81ea\u5df1\u7684\u826f\u5fc3\u3002\u201d \u3000\u3000\u6613\u660e\u5802\u7684\u8138\u8272\u5df2\u53d1\u9752\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u94c1\u4f20\u7532\u548c\u6b64\u4e8b\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u91d1\u98ce\u767d\u9053\uff1a\u201c\u7fc1\u8001\u5927\u505a\u7684\u6848\u5b50\u591a\u4e86\uff0c\u81ea\u7136\u6709\u4eba\u6765\u67e5\u6848\uff0c\u67e5\u6848\u7684\u6070\u5de7\u662f\u94c1\u4f20\u7532\u7684\u597d\u670b\u53cb\uff0c \u4ed6\u4eec\u867d\u5df2\u6000\u7591\u7fc1\u8001\u5927\uff0c\u5374\u8fd8\u662f\u4e0d\u6562\u8ba4\u5b9a\u3002\u201d \u3000\u3000\u6a35\u592b\u9053\uff1a\u201c\u6240\u4ee5\u94c1\u4f20\u7532\u5c31\u6545\u610f\u53bb\u548c\u7fc1\u8001\u5927\u7ed3\u4ea4\uff0c\u7b49\u67e5\u660e\u4e86\u624d\u597d\u52a8\u624b\u3002\u201d \u3000\u3000\u91d1\u98ce\u767d\u53f9\u9053\uff1a\u201c\u60f3\u6765\u5fc5\u5b9a\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u94c1\u4f20\u7532\u4e00\u76f4\u4e0d\u80af\u5c06\u8fd9\u4ef6\u4e8b\u8bf4\u660e\uff0c\u4e3a\u7684\u5c31\u662f\u7fc1\u8001\u5927\u7684\u786e\u5bf9\u4ed6\u4e0d\u9519\uff0c\u4ed6\u4e5f\u8ba4\u4e3a \u7fc1\u8001\u5927\u662f\u4e2a\u597d\u670b\u53cb\uff0c\u82e5\u662f\u8bf4\u51fa\u8fd9\u4ef6\u4e8b\uff0c\u5c82\u975e\u5bf9\u7fc1\u8001\u5927\u6b7b\u540e\u7684\u82f1\u540d\u6709\u635f\uff0c\u6240\u4ee5\u4ed6\u5b81\u53ef\u81ea\u5df1\u53d7\u51a4 \u5c48\u2014\u2014\u4ed6\u4e00\u76f4\u5728\u9003\uff0c\u7684\u786e\u4e0d\u662f\u4e3a\u4e86\u81ea\u5df1\uff01\u201d \u3000\u3000\u6613\u660e\u5802\u5389\u58f0\u9053\uff1a\u201c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u4e0d\u8bf4\u5462\uff1f\u201d \u3000\u3000\u91d1\u98ce\u767d\u60e8\u7136\u9053\uff1a\u201c\u6211\uff1f\u2026\u2026\u6211\u600e\u4e48\u80fd\u8bf4\uff1f\u7fc1\u8001\u5927\u5bf9\u6211\u4e00\u5411\u4e49\u91cd\u5982\u5c71\uff0c\u8fde\u94c1\u4f20\u7532\u90fd\u4e0d\u5fcd\u8bf4 \uff0c\u6211\u53c8\u600e\u4e48\u5fcd\u5fc3\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u6613\u660e\u5802\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7684\u786e\u4e0d\u6127\u662f\u7fc1\u8001\u5927\u7684\u597d\u5144\u5f1f\uff0c\u597d\uff0c\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u51b7\u7b11\uff0c\u8eab\u5b50\u4e00\u9762\u53d1\u6296\u3002 \u3000\u3000\u91d1\u98ce\u5411\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u6211\u8fd9\u4e48\u505a\u5bf9\u4e0d\u8d77\u94c1\u4f20\u7532\uff0c\u53ef\u662f\u6211\u6ca1\u6cd5\u5b50\uff0c\u5b9e\u5728\u6ca1\u6cd5\u5b50\u2026\u2026\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u5ffd\u7136\u53d6\u8d77\u4e86\u4e00\u67c4\u5200\uff0c\u5c31\u662f\u65b9\u624d\u6740\u6b7b\u94c1\u4f20\u7532\u7684\u90a3\u67c4\u5200\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u5411\u81ea \u5df1\u7684\u80f8\u819b\u523a\u4e0b\uff0c\u51e0\u4e4e\u4e5f\u5c31\u548c\u94c1\u4f20\u7532\u90a3\u4e00\u5200\u540c\u6837\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u867d\u4e5f\u75bc\u5f97\u56db\u80a2\u75c9\u631b\uff0c\u5634\u89d2\u5374\u4e5f\u9732\u51fa\u4e86\u548c\u94c1\u4f20\u7532\u540c\u6837\u7684\u5fae\u7b11\uff0c\u4e00\u5b57\u5b57\u6323\u624e\u7740\u9053\uff1a\u201c\u6211\u7684 \u786e\u6b20\u4e86\u4ed6\u7684\uff0c\u53ef\u662f\uff0c\u73b0\u5728\u6211\u7684\u503a\u4e5f\u5df2\u8fd8\u6e05\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6b7b\u5f97\u4e5f\u5f88\u5e73\u9759\u2026\u2026 \u3000\u3000\u201c\u5509\uff0c\u4e00\u4e2a\u4eba\u8981\u6b7b\u5f97\u5e73\u9759\uff0c\u5b9e\u5728\u592a\u4e0d\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u6613\u660e\u5802\u5ffd\u7136\u4ef0\u9762\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u52c7\u6c14\u5c06\u8fd9\u4ef6\u4e8b\u8bf4\u51fa\u6765\uff0c\u6709\u52c7\u6c14\u5c06\u8fd9\u503a\u8fd8\u6e05\uff0c\u4e5f\u4e0d \u6127\u662f\u6211\u7684\u597d\u5144\u5f1f\uff0c\u6211\u4eec\u2018\u4e2d\u539f\u516b\u4e49\u2019\u603b\u7b97\u6ca1\u6709\u505a\u4e22\u4eba\u73b0\u773c\u7684\u4e8b\uff01\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u542c\u6765\u5c31\u50cf\u662f\u8885\u4e4b\u591c\u557c\u3002 \u3000\u3000\u90a3\u6a35\u592b\u5ffd\u7136\u8dea\u4e86\u4e0b\u53bb\uff0c\u5411\u94c1\u4f20\u7532\u53e9\u4e86\u4e2a\u5934\uff0c\u53c8\u5411\u6613\u660e\u5802\u62dc\u4e86\u62dc\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u6211\u8981\u5148\u8d70\u4e00 \u6b65\u4e86\u3002\u201d \u3000\u3000\u6613\u660e\u5802\u7b11\u58f0\u5df2\u505c\u987f\uff01\u7a81\u53c8\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u51b7\u6f20\u5e73\u9759\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u8d70\uff0c\u6211\u5c31\u8d76\u6765\u3002 \u201d \u3000\u3000\u6a35\u592b\u9053\uff1a\u201c\u6211\u7b49\u4f60\u3002\u201d \u3000\u3000\u5229\u65a7\u626c\u8d77\uff0c\u9c9c\u8840\u98de\u6e85\uff0c\u4ed6\u6b7b\u5f97\u66f4\u5feb\uff0c\u66f4\u5e73\u9759\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u82e5\u975e\u4eb2\u773c\u89c1\u5230\uff0c\u7b80\u76f4\u65e0\u6cd5\u76f8\u4fe1\u4e16\u4e0a\u7adf\u6709\u8fd9\u79cd\u89c6\u6b7b\u5982\u5f52\u7684\u4eba\u3002 \u3000\u3000\u6613\u660e\u5802\u8138\u4e0a\uff0c\u5374\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u8d70\uff0c\u53ea\u56e0\u6211\u8fd8\u6709\u8bdd\u8981\u5bf9\u4f60\u8bf4\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u80fd\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u5589\u5934\u5df2\u54fd\u54bd\uff0c\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6613\u660e\u5802\u9053\uff1a\u201c\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u6211\u4eec\u4e00\u76f4\u90fd\u5b88\u5019\u5728\u8fd9\u91cc\uff0c\u56e0\u4e3a\u6211\u4eec\u77e5\u9053\u94c1\u4f20\u7532\u603b\u6709\u4e00\u5929\u8981\u56de \u6765\u7684\uff0c\u6240\u4ee5\u6211\u4eec\u77e5\u9053\u5f88\u591a\u4f60\u4e0d\u77e5\u9053\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u8fd9\u9634\u8c0b\uff0c\u6211\u4eec\u51e0\u4e4e\u4ece\u4e00\u5f00\u59cb\u5c31\u77e5\u9053\u2014\u2014\u9f99\u5578\u4e91\u4e5f\u77e5\u9053\uff0c\u6211 \u4e00\u76f4\u5728\u5947\u602a\uff0c\u4f60\u600e\u4e48\u4f1a\u548c\u8fd9\u79cd\u4eba\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u66f4\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u6613\u660e\u5802\u9053\uff1a\u201c\u94c1\u4f20\u7532\u77e5\u9053\u8fd9\u4ef6\u4e8b\u3001\u5c31\u662f\u9f99\u5578\u4e91\u8bf4\u51fa\u6765\u7684\uff0c\u4ed6\u6545\u610f\u8981\u94c1\u4f20\u7532\u5230\u8fd9\u91cc\u6765\u9001\u6b7b \uff0c\u4f46\u5374\u672a\u60f3\u5230\u6211\u4eec\u4e5f\u4f1a\u8ddf\u7740\u6765\uff0c\u56e0\u4e3a\u6211\u4eec\u7edd\u4e0d\u80fd\u8ba9\u94c1\u4f20\u7532\u6b7b\u5728\u522b\u4eba\u624b\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u81f3\u4e8e\u90a3\u4f4d\u9f99\u2026\u2026\u6797\u8bd7\u97f3\u6797\u59d1\u5a18\uff0c\u5979\u5e76\u6ca1\u6709\u6b7b\uff0c\u4e5f\u6ca1\u6709\u88ab\u4e0a\u5b98\u91d1\u8679\u9a97\u8d70\uff0c \u4f60\u73b0\u5728\u5230\u5174\u4e91\u5e84\u53bb\uff0c\u4e00\u5b9a\u8fd8\u53ef\u4ee5\u89c1\u7740\u5979\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u80f8\u4e2d\u53c8\u662f\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u4e5f\u4e0d\u77e5\u662f\u611f\u6fc0\uff1f\u8fd8\u662f\u6b22\u559c\uff1f \u3000\u3000\u6613\u660e\u5802\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5144\u5f1f\u7684\u6069\u6028\u90fd\u5df2\u6e05\u4e86\uff0c\u53ea\u671b\u4f60\u80fd\u5c06\u6211\u4eec\u5408\u846c\u5728\u4e00\u5904\uff0c\u65e5\u540e\u82e5\u6709\u4eba \u95f4\u8d77\u2018\u4e2d\u539f\u516b\u4e49\u2019\uff0c\u4e5f\u5e0c\u671b\u4f60\u80fd\u544a\u8bc9\u4ed6\u4eec\uff0c\u8fd9\u516b\u4e2a\u4eba\u6d3b\u7740\u65f6\u867d\u7136\u5e38\u5e38\u505a\u9519\u4e8b\uff0c\u4f46\u6b7b\u7684\u65f6\u5019\u603b \u7b97\u5df2\u5c06\u503a\u8fd8\u6e05\u4e86\u3002\u201d \u3000\u3000\u9ec4\u8863\u4eba\u4e0d\u77e5\u4f55\u65f6\u5374\u6084\u6084\u6e9c\u8d70\u4e86\uff0c\u674e\u5bfb\u6b22\u7eb5\u7136\u77a7\u89c1\uff0c\u4e5f\u6ca1\u6709\u963b\u62e6\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u963b\u62e6\u6613\u660e\u5802\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u6613\u660e\u5802\u7684\u786e\u5df2\u6ca1\u6cd5\u5b50\u518d\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u8981\u6b7b\u5f97\u5fc3\u5b89\uff0c\u6b7b\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u6b7b\uff0c\u5728\u4ed6\u4eec\u8bf4\u6765\uff0c\u7b80\u76f4\u5c31\u4e0d\u7b97\u662f\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u73b0\u5728\u77a7\u7740\u6ee1\u5730\u7684\u5c38\u4f53\uff0c\u5374\u89c9\u5f97\u5fcd\u4e0d\u4f4f\u8981\u53d1\u6296\u3002 \u3000\u3000\u4ed6\u53d1\u6296\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u522b\u7684\uff0c\u53ea\u4e3a\u4e86\u4ed6\u4e86\u89e3\u201c\u4ec7\u6068\u201d\u7684\u53ef\u6015\u3002 \u3000\u3000\u53ef\u662f\uff0c\u65e0\u8bba\u591a\u6df1\u7684\u4ec7\u6068\uff0c\u73b0\u5728\u603b\u7b97\u5df2\u4e86\u7ed3\u3002 \u3000\u3000\u6613\u660e\u5802\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u4e9b\u4eba\u6d3b\u7740\u65f6\u867d\u7136\u5e38\u5e38\u505a\u9519\u4e8b\uff0c\u4f46\u6b7b\u7684\u65f6\u5019\u5374\u662f\u5802\u5802\u6b63\u6b63\uff0c\u95ee\u5fc3\u65e0\u6127 \u7684\u3002 \u3000\u3000\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u4eba\u80fd\u50cf\u4ed6\u4eec\u8fd9\u4e48\u6837\u6b7b\u6cd5\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u56db\u80a2\u51b7\u5f97\u53d1\u6296\uff0c\u80f8\u4e2d\u7684\u70ed\u8840\u5374\u50cf\u662f\u4e00\u56e2\u706b\u3002 \u3000\u3000\u4ed6\u53c8\u8dea\u4e86\u4e0b\u6765\uff0c\u8dea\u5728\u4ed6\u4eec\u7684\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u7537\u5b50\u6c49\u7684\u8840\uff01 \u3000\u3000\u4ed6\u5b81\u613f\u8dea\u5728\u8fd9\u91cc\uff0c\u548c\u8fd9\u4e9b\u7537\u5b50\u6c49\u7684\u5c38\u4f53\u4f5c\u4f34\uff0c\u4e5f\u4e0d\u613f\u5230\u5916\u9762\u53bb\u77a7\u90a3\u4e9b\u6d3b\u4eba\u7684\u4e11\u6076\u5634\u8138\u3002 \u3000\u3000\u201c\u5927\u4e08\u592b\u751f\u800c\u4f55\u6b22\uff0c\u6b7b\u800c\u4f55\u60e7\uff01\u201d\u4e8c\u4e2a\u4eba\u82e5\u80fd\u5802\u5802\u6b63\u6b63\uff0c\u95ee\u5fc3\u5143\u6127\u800c\u6b7b\uff0c\u6b7b\u53c8\u7b97\u5f97\u4e86\u4ec0 \u4e48\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8fd9\u4e48\u6837\u6b7b\uff0c\u53ef\u771f\u4e0d\u5bb9\u6613\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u76f4\u6ca1\u6709\u8fdb\u6765\u3002 \u3000\u3000\u5979\u4e0d\u662f\u4e0d\u6562\u8fdb\u6765\uff0c\u800c\u662f\u4e0d\u5fcd\u8fdb\u6765\uff0c\u770b\u5230\u4e86\u8fd9\u4e9b\u7537\u5b50\u6c49\u7684\u6b7b\uff0c\u5979\u624d\u5ffd\u7136\u53d1\u89c9\u771f\u6b63\u7684\u7537\u4eba\u7684 \u786e\u662f\u548c\u5973\u4eba\u4e0d\u540c\u7684\u3002 \u3000\u3000\u5979\u7b2c\u4e00\u6b21\u89c9\u5f97\u80fd\u505a\u5973\u4eba\u5b9e\u5728\u662f\u81ea\u5df1\u7684\u8fd0\u6c14\u3002 \u3000\u3000\u591c\u3002 \u3000\u3000\u5c0f\u5e97\u91cc\u53ea\u6709\u4e00\u76cf\u706f\uff0c\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u706f\u5149\u5f88\u9eef\uff0c\u4ed6\u4eec\u7684\u5fc3\u60c5\u5374\u6bd4\u706f\u5149\u66f4\u9eef\uff0c\u66f4\u6d88\u6c89\u2026\u2026 \u3000\u3000\u706f\uff0c\u5c31\u5728\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u9152\uff0c\u4e5f\u5728\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u4f46\u4ed6\u5374\u4f3c\u4e4e\u5df2\u8fde\u4e3e\u676f\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\uff0c\u53ea \u662f\u5750\u5728\u90a3\u91cc\uff0c\u75f4\u75f4\u7684\u671b\u7740\u9152\u676f\u53d1\u6014\u3002 \u3000\u3000\u706f\u82af\u6311\u8d77\uff0c\u53c8\u71c3\u5c3d\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u674e\u5bfb\u6b22\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d70\u5427\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e5f\u53bb\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u4eec\u4e00\u9f50\u6765\u7684\uff0c\u5f53\u7136\u4e00\u9f50\u56de\u53bb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u56de\u53bb\uff1f\u4f60\u4e0d\u5230\u5174\u4e91\u5e84\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5f88\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u8fd9\u6b21\u6765\uff0c\u5c82\u975e\u4e3a\u4e86\u8981\u5230\u5174\u4e91\u5e84\u53bb\u77a7\u77a7\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u73b0\u5728\u5df2\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u671b\u7740\u95ea\u52a8\u7684\u706f\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6613\u660e\u5802\u65e2\u7136\u8bf4\u5979\u8fd8\u5728\uff0c\u5c31\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u542c\u4e86\u4ed6\u7684\u4e00\u53e5\u8bdd\uff0c\u4f60\u5c31\u5df2\u653e\u5fc3\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u50cf\u4ed6\u90a3\u79cd\u4eba\uff0c\u65e0\u8bba\u8bf4\u4ec0\u4e48\u6211\u90fd\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u4f60\u96be\u9053\u4e0d\u60f3\u53bb\u770b\u770b\u5979\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\uff0c\u5f88\u4e45\u7f13\u7f13\u9053\uff1a\u201c\u76f8\u89c1\u771f\u5982\u4e0d\u89c1\uff0c\u5979\u65e2\u7136\u65e0\u4e8b\uff0c\u6211\u53c8\u4f55\u5fc5\u53bb\u770b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u6765\u4e86\uff0c\u53c8\u4f55\u5fc5\u4e0d\u53bb\u770b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e58\u5174\u800c\u8fd4\uff0c\u65e2\u7136\u5df2\u6765\u4e86\uff0c\u770b\u4e0d\u770b\u4e5f\u5c31\u6ca1\u4ec0\u4e48 \u5206\u522b\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e2a\u602a\u4eba\uff0c\u505a\u7684\u4e8b\u603b\u662f\u6559\u4eba\u4e0d\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u8c08\u9053\uff1a\u201c\u4f60\u6162\u6162\u5c31\u4f1a\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5446\u4e86\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u81f3\u5c11\u4e5f\u8be5\u7b49\u57cb\u846c\u4e86\u4ed6\u4eec\u7684\u5c38\u4f53\u518d\u8d70\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u4eec\u53ef\u4ee5\u7b49\u4e00\u7b49\uff0c\u4e0a\u5b98\u91d1\u8679\u5374\u4e0d\u80fd\u7b49\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6b7b\u4eba\u603b\u6bd4\u6d3b\u4eba\u6709\u8010\u6027\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u7b2c\u516b\u5341\u7ae0\u3000\u53ef\u6015\u7684\u9519\u8bef>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u7ae0\u3000\u53ef\u6015\u7684\u9519\u8bef \u3000\u3000\u5b59\u5c0f\u7ea2\u561f\u8d77\u4e86\u5634\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u5e76\u4e0d\u5341\u5206\u591f\u4e49\u6c14\uff0c\u81f3\u5c11\u5bf9\u6b7b\u4eba\u5c31\u6ca1\u6709\u5bf9\u6d3b\u4eba\u591f\u4e49 \u6c14\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u6628\u5929\u6211\u4eec\u662f\u4ec0\u4e48\u65f6\u5019\u51fa\u53d1\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u665a\u4e0a\uff0c\u5c31\u548c\u73b0\u5728\u5dee\u4e0d\u591a\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4eca\u5929\u6211\u4eec\u662f\u4ec0\u4e48\u65f6\u5019\u8d76\u5230\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u620d\u65f6\u524d\u540e\uff0c\u5929\u8fd8\u6ca1\u6709\u9ed1\u3002\u201d \u3000\u3000\u5b6a\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u4eec\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4eec\u5148\u5750\u8f66\u8d70\u4e86\u6bb5\u8def\uff0c\u7136\u540e\u5c31\u7528\u8f7b\u529f\uff0c\u5230\u4e86\u4eca\u5929\u65e9\u4e0a\uff0c\u518d\u6362\u5feb\u9a6c\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u6211\u4eec\u5c31\u7b97\u7528\u540c\u6837\u7684\u6cd5\u5b50\u8d76\u56de\u53bb\uff0c\u6700\u5feb\u4e5f\u5f97\u8981\u5230\u620d\u65f6\u524d\u540e\u624d\u5230\u5f97\u4e86 \uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5bf9\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u73b0\u5728\u6211\u4eec\u5df2\u6709\u5f88\u4e45\u672a\u4f11\u606f\uff0c\u4f53\u529b\u7edd\u5bf9\u5df2\u4e0d\u5982\u6628\u5929\u665a\u4e0a\u597d\uff0c\u7eb5\u7136\u8fd8\u80fd\u65bd\u5c55 \u8f7b\u529f\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6bd4\u6628\u5929\u665a\u4e0a\u5feb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ae3\u7136\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u6211\u5c31\u5df2\u8d76\u4e0d\u4e0a\u4f60\uff0c\u96be\u602a\u7237\u7237\u8bf4\u4f60\u7684\u8f7b\u529f\u5e76\u4e0d\u6bd4\u4f60\u7684\u5200\u6162\u591a\u5c11 \u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u5c31\u7b97\u73b0\u5728\u52a8\u8eab\uff0c\u4e5f\u672a\u5fc5\u80fd\u53ca\u65f6\u8d76\u53bb\u8d74\u4e0a\u5b98\u91d1\u8679\u7684\u7ea6\u4f1a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u51dd\u6ce8\u7740\u5979\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u672c\u8be5\u50ac\u6211\u5feb\u8d70\u624d\u5bf9\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u6211\u4ece \u4e0d\u613f\u5931\u7ea6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u7740\u5934\uff0c\u54ac\u7740\u5634\u5507\uff0c\u4eff\u4f5b\u5728\u6545\u610f\u9003\u907f\u7740\u674e\u5bfb\u6b22\u7684\u76ee\u5149\u3002 \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u53ea\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u4eec\u5750\u8f66\u8d76\u56de\u53bb\uff0c\u4e0d\u6362\u9a6c\uff0c\u4e5f\u4e0d\u7528\u8f7b\u529f\u8d76\u8def\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u8981\u6211\u5728\u8f66\u4e0a\u4f11\u606f\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5426\u5219\u4f60\u5c31\u65e0\u6cd5\u53ca\u65f6\u8d76\u5230\uff0c\u4f60\u4e00\u5230\u90a3\u91cc\u53ea\u6015\u5c31\u5f97\u8eba\u4e0b\uff0c\u4f60\u603b\u4e0d\u80fd\u7761\u5728 \u5730\u4e0a\u548c\u4e0a\u5b98\u91d1\u8679\u51b3\u6597\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u541f\u7740\uff0c\u7ec8\u4e8e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u542c\u4f60\u7684\uff0c\u6211\u4eec\u5750\u8f66\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7acb\u523b\u5c31\u9ad8\u5174\u4e86\u8d77\u6765\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u53ef\u4ee5\u628a\u9152\u5e26\u5230\u8f66\u4e0a\u53bb\uff0c\u4f60\u82e5\u7761\u4e0d\u7740\uff0c\u6211 \u5c31\u966a\u4f60\u559d\u9152\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u9152\u4e00\u559d\u591a\u4e86\uff0c\u81ea\u7136\u5c31\u4f1a\u7761\u7740\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u53ea\u8981\u4f60\u80fd\u5728\u8f66\u4e0a\u597d\u597d\u7761\u4e00\u89c9\uff0c\u6211\u4fdd\u8bc1\u4e0a\u5b98\u91d1\u8679\u7edd\u4e0d\u662f\u4f60\u7684 \u5bf9\u624b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u5012\u5f88\u6709\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\u775b\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6211\u5bf9\u4f60\u82e5\u6ca1\u6709\u4fe1\u5fc3\uff0c\u53c8\u600e\u4f1a\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u8138\u5ffd\u7136\u7ea2\u4e86\uff0c\u5ffd\u7136\u4e00\u6e9c\u70df\u7a9c\u4e86\u51fa\u53bb\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u6211\u53bb\u96c7\u8f66\uff0c\u4f60\u51c6\u5907\u9152\uff0c\u82e5\u662f\u65f6\u95f4 \u5145\u88d5\uff0c\u4f60\u4e5f\u4e0d\u59a8\u53bb\u77a7\u77a7\u5979\uff0c\u6211\u7edd\u4e0d\u4f1a\u5403\u7784\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u8fab\u5b50\u98de\u626c\uff0c\u970e\u773c\u95f4\u5c31\u8dd1\u5f97\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u76ee\u9001\u7740\u5979\uff0c\u53c8\u75f4\u4e86\u534a\u664c\uff0c\u624d\u7f13\u7f13\u7684\u7ad9\u8d77\u6765\uff0c\u8d70\u51fa\u95e8\u3002 \u3000\u3000\u731b\u62ac\u5934\uff0c\u9ad8\u5899\u5185\u9732\u51fa\u5c0f\u697c\u4e00\u89d2\u3002 \u3000\u3000\u5c0f\u697c\u7684\u5b64\u706f\u53c8\u4eae\u4e86\u3002 \u3000\u3000\u5c0f\u697c\u4e0a\u7684\u4eba\u5462\uff1f \u3000\u3000\u5979\u662f\u4e0d\u662f\u53c8\u5728\u4e3a\u5979\u7684\u7231\u5b50\u5728\u7f1d\u8865\u7740\u8863\u670d\uff1f \u3000\u3000\u6148\u6bcd\u624b\u4e2d\u7684\u7ebf\uff0c\u957f\u5f97\u597d\u6a61\u6c38\u8fdc\u90fd\u7f1d\u4e0d\u5b8c\u4f3c\u7684\u3002 \u3000\u3000\u4f46\u5374\u8fd8\u662f\u6bd4\u4e0d\u4e0a\u5bc2\u5bde\uff0c\u4e16\u4e0a\u6700\u957f\u7684\u5c31\u662f\u5bc2\u5bde\u3002 \u3000\u3000\u4e00\u5e74\u53c8\u4e00\u5e74\uff0c\u4e00\u53e3\u53c8\u4e00\u53e3\uff0c\u7f1d\u4e0d\u5b8c\u7684\u7ebf\uff0c\u7f1d\u4e0d\u5b8c\u7684\u5bc2\u5bde\u2014\u2014 \u3000\u3000\u5979\u5df2\u5c06\u81ea\u5df1\u7684\u751f\u547d\u57cb\u846c\uff0c\u8fd9\u5c0f\u697c\u5c31\u662f\u5979\u7684\u575f\u5893\u3002 \u3000\u3000\u4e00\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u5973\u4eba\uff0c\u82e5\u662f\u5df2\u6ca1\u6709\u9752\u6625\uff0c\u6ca1\u6709\u7231\u60c5\uff0c\u6ca1\u6709\u6b22\u4e50\uff0c\u5979\u8fd8\u8981\u751f\u547d\u4f5c\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u8bd7\u97f3\uff0c\u8bd7\u97f3\u2026\u2026\u4f60\u5b9e\u5728\u592a\u82e6\uff0c\u4f60\u5b9e\u5728\u5df2\u53d7\u5c3d\u4e86\u6298\u78e8\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u5f2f\u4e0b\u8170\uff0c\u4e0d\u505c\u7684\u54b3\u55fd\uff0c\u53c8\u54b3\u51fa\u4e86\u8840\uff01 \u3000\u3000\u4ed6\u5fc3\u91cc\u53c8\u4f55\u5c1d\u4e0d\u60f3\u53bb\u770b\u770b\u5979\uff1f \u3000\u3000\u4ed6\u7684\u4eba\u867d\u7136\u7ad9\u5728\u8fd9\u91cc\uff0c\u5fc3\u5374\u65e9\u5df2\u98de\u4e0a\u4e86\u5c0f\u697c\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u867d\u7136\u5df2\u98de\u4e0a\u4e86\u5c0f\u697c\uff0c\u4f46\u4ed6\u7684\u4eba\u5374\u8fd8\u662f\u4e0d\u5f97\u4e0d\u7559\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u53bb\u770b\u5979\uff0c\u4e5f\u4e0d\u80fd\u53bb\u770b\u5979\uff0c\u7eb5\u7136\u662f\u6700\u540e\u4e00\u6b21\uff0c\u4e5f\u4e0d\u80fd\u2026\u2026\u76f8\u89c1\u4e89\u5982\u4e0d\u89c1\uff0c\u89c1\u4e86\u53c8\u80fd \u5982\u4f55\uff1f \u3000\u3000\u5979\u5df1\u4e0d\u5c5e\u4e8e\u4ed6\uff0c\u5979\u6709\u5979\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u513f\u5b50\uff0c\u6709\u5979\u81ea\u5df1\u7684\u5929\u5730\u3002 \u3000\u3000\u4ed6\u5df2\u5b8c\u5168\u88ab\u6452\u7edd\u5728\u8fd9\u5929\u5730\u4e4b\u5916\u3002 \u3000\u3000\u5979\u672c\u662f\u4ed6\u7684\uff0c\u73b0\u5728\u5374\u8fde\u770b\u5979\u4e00\u773c\u4e5f\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7528\u624b\u80cc\u64e6\u4e86\u5634\u9762\u7684\u8840\u6e0d\uff0c\u5c06\u5634\u91cc\u7684\u8840\u53c8\u54bd\u4e0b\u3002 \u3000\u3000\u8fde\u8840\u90fd\u4eff\u4f5b\u662f\u82e6\u7684\uff0c\u82e6\u7684\u53d1\u6da9\u3002 \u3000\u3000\u201c\u8bd7\u97f3\uff0c\u8bd7\u97f3\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u53ea\u8981\u4f60\u80fd\u5e73\u5e73\u5b89\u5b89\uff0c\u6211\u5c31\u80fd\u5fc3\u6ee1\u610f\u8db3\uff0c\u5929\u4e0a\u5730\u4e0b\uff0c\u6211\u4eec\u603b\u6709 \u76f8\u89c1\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u4f46\u6797\u8bd7\u97f3\u771f\u7684\u80fd\u5e73\u5b89\u4e48\uff1f \u3000\u3000\u98ce\u51c4\u5207\uff0c\u4eba\u6bd4\u9ec4\u82b1\u7626\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5b64\u96f6\u96f6\u7684\u6728\u7acb\u5728\u897f\u98ce\u91cc\uff0c\u662f\u4e0d\u662f\u5e0c\u671b\u98ce\u80fd\u5c06\u4ed6\u5439\u53bb\uff1f \u3000\u3000\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\uff0c\u5b59\u5c0f\u7ea2\u5df2\u53e3\u6765\u4e86\uff0c\u75f4\u75f4\u7684\u77a7\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6ca1\u6709\u53bb\u770b\u5979\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u53bb\u53eb\u8f66\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8f66\u5c31\u505c\u5728\u5df7\u53e3\uff0c\u4f60\u82e5\u771f\u7684\u4e0d\u60f3\u53bb\u770b\u5979\uff0c\u6211\u4eec\u5c31\u8d70\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u8f66\u5728\u8def\u4e0a\u98a0\u6c9b\uff0c\u9152\u5728\u676f\u4e2d\u6447\u6643\u3002 \u3000\u3000\u662f\u9648\u5e74\u7684\u8001\u9152\u3002 \u3000\u3000\u8f66\u5374\u6bd4\u9152\u66f4\u8001\uff0c\u9a6c\u4e5f\u8bb8\u6bd4\u8f66\u8fd8\u8001\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6447\u7740\u5934\u7b11\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u53ea\u6015\u5c31\u662f\u5173\u516c\u9a91\u7684\u8d64\u5154\u9a6c\uff0c\u8f66\u5b50\u4e5f\u65e9\u5df2\u6210\u4e86\u53e4\u8463\uff0c\u4f60\u5c45\u7136 \u80fd\u627e\u5f97\u6765\uff0c\u53ef\u771f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u7acb\u523b\u53c8\u677f\u8d77\u8138\uff0c\u9053\uff1a\u201c\u6211\u505a\u7684\u4e8b\u4f60\u603b\u89c9\u5f97\u4e0d\u6ee1\u610f\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6ee1\u610f\uff0c\u6ee1\u610f\uff0c\u6ee1\u610f\u6781\u4e86\u3002\u201d \u3000\u3000\u4ed6\u95ed\u4e0a\u9650\u775b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u5750\u4e0a\u8fd9\u8f86\u8f66\uff0c\u5c31\u8ba9\u6211\u60f3\u8d77\u4e86\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u54e6\uff1f\u8ba9\u4f60\u60f3\u8d77\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8ba9\u6211\u60f3\u8d77\u5c0f\u65f6\u5019\u73a9\u7684\u90a3\u5339\u6728\u9a6c\uff0c\u73b0\u5728\u6211\u7b80\u76f4\u5c31\u597d\u50cf\u5728\u9a6c\u8f66\u4e0a\u7684\u6447\u7bee\u91cc\u3002 \u3000\u3000\u4ed6\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5ffd\u7136\u89c9\u5f97\u6709\u6837\u4e1c\u897f\u8fdb\u4e86\u4ed6\u7684\u5634\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5403\u5403\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5403\u5b8c\u4e86\u8fd9\u67a3\u5b50\uff0c\u5c31\u8d76\u5feb\u7761\u5427\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u82e6\u7b11\u9053\uff1a\u201c\u82e5\u80fd\u4e00\u7761\u4e0d\u9192\uff0c\u5012\u4e5f\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u53eb\u8fd9\u8f86\u8f66\uff0c\u5c31\u4e3a\u7684\u662f\u8981\u8ba9\u4f60\u597d\u597d\u7761\u4e00\u89c9\uff0c\u53ea\u8981\u4f60\u80fd\u771f\u7684 \u7761\u7740\uff0c\u660e\u5929\u65e9\u4e0a\u6211\u4eec\u518d\u6362\u8f66\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u4e48\u6837\uff0c\u6211\u5c31\u591a\u559d\u51e0\u676f\uff0c\u4e5f\u597d\u7761\u5f97\u6c89\u4e9b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7acb\u523b\u4e3a\u4ed6\u5012\u9152\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u7b97\u662f\u5b69\u5b50\uff0c\u4e5f\u5f97\u5148\u5582\u9971\u5976\u624d\u7761\u5f97\u7740\u3002\u201d \u3000\u3000\u676f\u4e2d\u7684\u9152\u5728\u6447\u6643\uff0c\u5979\u7684\u8fab\u5b50\u4e5f\u5728\u6447\u6643\u3002 \u3000\u3000\u5979\u7684\u773c\u6ce2\u6e29\u67d4\uff0c\u5c31\u5982\u8f66\u7a97\u5916\u7684\u661f\u5149\u3002 \u3000\u3000\u661f\u5149\u5982\u68a6\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f3c\u5df2\u9189\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u6837\u7684\u665a\u4e0a\uff0c\u9762\u5bf9\u7740\u8fd9\u4e48\u6837\u7684\u4eba\uff0c\u8c01\u80fd\u4e0d\u9189\uff1f \u3000\u3000\u65e2\u5df2\u9189\u4e86\uff0c\u600e\u80fd\u4e0d\u7761\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u659c\u501a\u7740\uff0c\u5c06\u4e24\u6761\u817f\u8df7\u5728\u5bf9\u9762\u7684\u8f66\u5ea7\u4e0a\uff0c\u5583\u5583\u9053\uff1a\u201c\u53e4\u6765\u5723\u8d24\u7686\u5bc2\u5bde\u3001\u552f\u6709\u996e\u8005\u7559 \u5176\u540d\u2026\u2026\u4f46\u996e\u8005\u53c8\u4f55\u5c1d\u4e0d\u5bc2\u5bde\u2026\u2026\u201d \u3000\u3000\u58f0\u97f3\u6e10\u4f4e\uff0c\u6e10\u5bc2\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u7761\u7740\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8109\u8109\u7684\u51dd\u6ce8\u7740\u4ed6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u624d\u8f7b\u8f7b\u4f38\u51fa\u624b\uff0c\u8f7b\u629a\u7740\u4ed6\u7684\u5934\u53d1\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u7761 \u5427\uff0c\u597d\u597d\u7761\u5427\uff0c\u7b49\u4f60\u7761\u9192\u65f6\uff0c\u6240\u6709\u7684\u5fe7\u6101\u548c\u70e6\u607c\u4e5f\u8bb8\u90fd\u6210\u4e86\u8fc7\u53bb\uff0c\u5230\u4e86\u90a3\u65f6\uff0c\u6211\u5c31\u4e0d\u4f1a\u8ba9\u4f60 \u559d\u5f97\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u5979\u7684\u7738\u5b50\u6f06\u9ed1\u800c\u4eae\uff0c\u5145\u6ee1\u4e86\u5e78\u798f\u7684\u61a7\u61ac\u3002 \u3000\u3000\u5979\u8fd8\u5e74\u8f7b\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u5bf9\u4e16\u4e0a\u7684\u4e8b\u603b\u662f\u4e50\u89c2\u7684\uff0c\u603b\u8ba4\u4e3a\u6bcf\u4ef6\u4e8b\u90fd\u80fd\u5982\u4eba\u7684\u610f\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u4e16\u4e0a\u201c\u4e0d\u5982\u610f\u4e8b\u5e38\u516b\u4e5d\u201d\uff0c\u4e8b\u5b9e\u6c38\u8fdc\u548c\u4eba\u613f\u5dee\u7740\u5f88\u5927\u7684\u4e00\u6bb5\u8ddd\u79bb\uff0c\u73b0\u5728\u5979\u82e5\u77e5\u9053 \u4ed6\u4eec\u60f3\u7684\u548c\u4e8b\u5b9e\u76f8\u5dee\u5f97\u591a\u4e48\u8fdc\uff0c\u5979\u53ea\u6015\u65e9\u5df2\u6cea\u843d\u6ee1\u8863\u3002 \u3000\u3000\u8d76\u8f66\u7684\u4e5f\u5728\u60a0\u60a0\u95f2\u95f2\u7684\u559d\u7740\u9152\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u6025\u3002 \u3000\u3000\u56e0\u4e3a\u96c7\u4ed6\u8f66\u7684\u59d1\u5a18\u66fe\u7ecf\u5429\u5490\u8fc7\u4ed6\uff01 \u3000\u3000\u201c\u6162\u6162\u7684\u8d70\uff0c\u6211\u4eec\u5e76\u4e0d\u6025\u7740\u8d76\u8def\u3002\u201d \u3000\u3000\u8d76\u8f66\u7684\u4f1a\u5fc3\u5fae\u7b11\uff0c\u4ed6\u82e5\u548c\u81ea\u5df1\u7684\u5fc3\u4e0a\u4eba\u5750\u8f66\uff0c\u4e5f\u4e0d\u4f1a\u6025\u7740\u8d76\u8def\u7684\u3002 \u3000\u3000\u4ed6\u5f88\u7fa1\u6155\u674e\u5bfb\u6b22\uff0c\u89c9\u5f97\u674e\u5bfb\u6b22\u5b9e\u5728\u5f88\u6709\u798f\u6c14\u3002 \u3000\u3000\u4f46\u4ed6\u82e5\u77e5\u9053\u674e\u5bfb\u6b22\u548c\u5b59\u5c0f\u7ea2\u4f1a\u9047\u7740\u4ec0\u4e48\u6837\u7684\u4e8b\uff0c\u4ed6\u7684\u9152\u53ea\u6015\u4e5f\u559d\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u73b0\u5728\u5df2\u7ecf\u662f\u201c\u660e\u5929\u201d\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9192\u7684\u65f6\u5019\uff0c\u7ea2\u65e5\u5df2\u7167\u6ee1\u8f66\u7a97\u3002 \u3000\u3000\u4ed6\u4e0d\u81f3\u4e8e\u7761\u5f97\u8fd9\u4e48\u6c89\u7684\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u592a\u7d2f\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u8fd9\u9152\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u62ff\u8d77\u9152\u676f\u55c5\u4e86\u55c5\uff0c\u53c8\u6162\u6162\u7684\u653e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9a6c\u8f66\u8fd8\u5728\u4e00\u6447\u4e00\u6643\u7684\u8d70\u7740\uff0c\u8d70\u5f97\u5f88\u6162\uff0c\u8d76\u8f66\u7684\u6709\u4e00\u642d\uff0c\u6ca1\u4e00\u642d\u7684\u54fc\u7740\u5c0f\u8c03\uff0c\u4eff\u4f5b\u6b63\u662f\u6253 \u778c\u7761\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u5df2\u7761\u7740\uff0c\u5c31\u6795\u5728\u674e\u5bfb\u6b22\u7684\u819d\u4e0a\u3002 \u3000\u3000\u5979\u957f\u957f\u7684\u5934\u53d1\u6563\u843d\uff0c\u67d4\u5982\u6ce5\u6c34\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u63a2\u51fa\u5934\uff0c\u5730\u4e0a\u770b\u4e0d\u5230\u9a6c\u8f66\u7684\u5f71\u5b50\u3002 \u3000\u3000\u65e5\u6b63\u5f53\u4e2d\u3002 \u3000\u3000\u8d70\u4e86\u6bb5\u8def\uff0c\u8def\u65c1\u6709\u4e2a\u77f3\u7891\uff0c\u523b\u7740\u524d\u9762\u7684\u6751\u540d\u3002 \u3000\u3000\u73b0\u5728\u5df2\u5feb\u5230\u6b63\u5348\uff0c\u8ddd\u79bb\u4e0a\u5b98\u91d1\u8679\u7684\u7ea6\u4f1a\u5df2\u4e0d\u5230\u4e09\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u53ea\u4e0d\u8fc7\u8d70\u4e86\u4e00\u534a\u8def\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u7684\u624b\u5728\u53d1\u51b7\uff0c\u53d1\u6296\u3002 \u3000\u3000\u4ed6\u6709\u65f6\u5fe7\u8651\uff0c\u6709\u65f6\u60b2\u54c0\uff0c\u6709\u65f6\u70e6\u607c\uff0c\u6709\u65f6\u75db\u82e6\uff0c\u4ed6\u751a\u81f3\u4e5f\u6709\u8fc7\u6b22\u559c\u7684\u65f6\u5019\uff0c\u4f46\u5374\u5f88\u5c11\u52a8 \u6012\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7eb5\u672a\u52a8\u6012\uff0c\u4e5f\u5df2\u5dee\u4e0d\u591a\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u9192\u4e86\u8fc7\u6765\uff0c\u611f\u89c9\u5230\u4ed6\u7684\u4eba\u5728\u53d1\u6296\uff0c\u62ac\u8d77\u5934\uff0c\u5c31\u770b\u5230\u4e86\u4ed6\u8138\u4e0a\u7684\u6012\u5bb9\uff0c\u5979\u4ece\u672a \u89c1\u8fc7\u4ed6\u8138\u8272\u5982\u6b64\u53ef\u6015\u3002 \u3000\u3000\u5979\u5782\u4e0b\u5934\uff0c\u773c\u5708\u513f\u5df2\u7ea2\u4e86\uff0c\u55eb\u558f\u7740\u9053\uff1a\u201c\u4f60\u5728\u751f\u6211\u7684\u6c14\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u5634\u95ed\u7740\uff0c\u95ed\u5f97\u5f88\u7d27\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u51c4\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u602a\u6211\uff0c\u4f46\u6211\u8fd8\u662f\u8981\u8fd9\u4e48\u6837\u505a\uff0c\u4f60\u6253\u6211\uff0c\u9a82\u6211\u90fd\u6ca1\u5173\u7cfb \uff0c\u53ea\u8981\u4f60\u660e\u767d\u6211\u8fd9\u4e48\u6837\u505a\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u6574\u4e2a\u4eba\u5df2\u8f6f\u4e86\u4e0b\u6765\uff0c\u5fc3\u4e5f\u8f6f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8fd9\u4e48\u6837\u505a\uff0c\u7684\u786e\u662f\u4e3a\u4e86\u4ed6\u3002 \u3000\u3000\u5979\u505a\u9519\u4e86\u4e48\uff1f\u53ea\u8981\u5979\u662f\u771f\u5fc3\u5bf9\u4ed6\uff0c\u65e0\u8bba\u505a\u4ec0\u4e48\u90fd\u4e0d\u80fd\u7b97\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9eef\u7136\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\uff0c\u6211\u4e0d\u602a\u4f60\uff0c\u53ef\u662f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u660e\u767d\u6211\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u771f\u7684\u8ba4\u4e3a\u6211\u4e0d\u660e\u767d\u4f60\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u82e5\u660e\u767d\u6211\uff0c\u5c31\u8be5\u77e5\u9053\u4f60\u8fd9\u6b21\u5c31\u7b97\u80fd\u62d6\u4f4f\u6211\uff0c\u8ba9\u6211\u4e0d\u80fd\u53bb\u8d74\u4e0a\u5b98\u91d1\u8679\u7684\u7ea6 \uff0c\u4f46\u4ee5\u540e\u5462\uff1f\u6211\u8fdf\u65e9\u8fd8\u662f\u96be\u514d\u8981\u548c\u4ed6\u89c1\u9762\u7684\uff0c\u4e5f\u8bb8\u5c31\u5728\u660e\u5929\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7b49\u5230\u660e\u5929\uff0c\u4e00\u5207\u4e8b\u5c31\u53d8\u5f97\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u660e\u5929\u4f1a\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u60a0\u60a0\u9053\uff1a\u201c\u660e\u5929\u4e0a\u5b98\u91d1\u8679\u8bf4\u4e0d\u5b9a\u5df2\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u8bb8\u8fde\u4eca\u5929\u665a\u4e0a\u90fd\u6d3b\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u65b9\u5f0f\u5f88\u5947\u7279\uff0c\u4eff\u4f5b\u5145\u6ee1\u4e86\u81ea\u4fe1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u60f3\u4e0d\u901a\u5979\u4e3a\u4f55\u4f1a\u5982\u6b64\u6709\u4fe1\u5fc3\uff0c\u6240\u4ee5\u4ed6\u8981\u60f3\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53c8\u9053\uff1a\u201c\u4eca\u5929\u4f60\u5c31\u7b97\u5931\u7ea6\uff0c\u5374\u4e5f\u6ca1\u6709\u4eba\u80fd\u602a\u4f60\uff0c\u56e0\u4e3a\u8fd9\u672c\u662f\u4e0a\u5b98\u91d1\u8679\u5f3a\u8feb\u4f60\u8fd9\u4e48 \u505a\u7684\uff0c\u5426\u5219\u4f60\u53c8\u600e\u4f1a\u8981\u8d76\u5230\u5174\u4e91\u5e84\uff1f\u82e5\u4e0d\u8d70\u8fd9\u4e00\u8d9f\uff0c\u4f60\u53c8\u600e\u4f1a\u5931\u7ea6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8fd8\u5728\u60f3\uff0c\u8138\u8272\u5374\u5df2\u6e10\u6e10\u53d8\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u795e\u60c5\u5374\u5df2\u6109\u5feb\u4e86\u8d77\u6765\uff0c\u5750\u5728\u674e\u5bfb\u6b22\u8eab\u65c1\uff0c\u9053\uff1a\u201c\u7b49\u5230\u4e0a\u5b98\u91d1\u8679\u4e00\u6b7b\uff0c\u66f4\u4e0d\u4f1a\u6709 \u4eba\u8bf4\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u7237\u7237\u8981\u4f60\u8fd9\u4e48\u6837\u505a\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u5ae3\u53d9\u9053\uff1a\u201c\u4e5f\u53ef\u4ee5\u8bf4\u662f\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u4e0d\u662f\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eca\u5929\u665a\u4e0a\u8981\u66ff\u6211\u53bb\u548c\u4e0a\u5b98\u91d1\u8679\u51b3\u6597\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8be5\u77e5\u9053\uff0c\u4e0a\u5b98\u91d1\u8679\u4e00\u89c1\u4e86\u6211\u7237\u7237\uff0c\u7b80\u76f4\u5c31\u597d\u50cf\u8001\u9f20\u89c1\u4e86\u732b \uff0c\u8fd9\u4e16\u4e0a\u4e5f\u8bb8\u5c31\u53ea\u6709\u6211\u7237\u7237\u4e00\u4e2a\u4eba\u80fd\u5236\u5f97\u4f4f\u4ed6\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u62c9\u7740\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u8fd8\u60f3\u518d\u8bf4\u4e9b\u8bdd\u3002 \u3000\u3000\u5979\u6ca1\u6709\u8bf4\uff0c\u56e0\u4e3a\u5979\u5ffd\u7136\u53d1\u89c9\u4ed6\u7684\u624b\u51b7\u5f97\u50cf\u51b0\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u5fc3\u82e5\u6ca1\u6709\u51b7\uff0c\u624b\u7edd\u4e0d\u4f1a\u8fd9\u4e48\u51b7\uff0c\u4e00\u4e2a\u4eba\u5fc3\u91cc\u82e5\u662f\u6ca1\u6709\u6050\u60e7\uff0c\u624b\u4e5f\u7edd\u4e0d\u4f1a\u8fd9\u4e48\u51b7 \u3002 \u3000\u3000\u4ed6\u6050\u60e7\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u770b\u5230\u674e\u5bfb\u6b22\u7684\u795e\u60c5\uff0c\u5b59\u5c0f\u7ea2\u8fde\u95ee\u90fd\u4e0d\u6562\u95ee\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5374\u95ee\u9053\uff1a\u201c\u662f\u4f60\u7237\u7237\u81ea\u5df1\u8981\u53bb\u7684\uff1f\u8fd8\u662f\u4f60\u6c42\u4ed6\u53bb\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u96be\u9053\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6709\uff0c\u4e0d\u4f46\u6709\u5206\u522b\uff0c\u800c\u4e14\u5206\u522b\u8fd8\u5f88\u5927\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u662f\u6211\u6c42\u4ed6\u8001\u4eba\u5bb6\u53bb\u7684\uff0c\u56e0\u4e3a\u6211\u89c9\u5f97\u4e0a\u5b98\u91d1\u8679\u90a3\u6837\u7684\u4eba\uff0c\u4eba\u4eba\u90fd\u5f97\u800c\u8bdb\u4e4b\uff0c \u5e76\u4e0d\u4e00\u5b9a\u8981\u4f60\u53bb\u52a8\u624b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6162\u6162\u7684\u70b9\u7740\u5934\uff0c\u4eff\u4f5b\u5df2\u627f\u8ba4\u5979\u7684\u8bdd\u5f88\u5bf9\u3002 \u3000\u3000\u4f46\u5728\u4ed6\u8138\u4e0a\u7684\u5374\u5b8c\u5168\u662f\u53e6\u5916\u4e00\u79cd\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u6050\u60e7\uff0c\u800c\u4e14\u5fe7\u8651\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u5728\u62c5\u5fc3\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7528\u4e0d\u7740\u56de\u7b54\u8fd9\u53e5\u6d3b\uff0c\u4ed6\u7684\u8868\u60c5\u5df2\u66ff\u4ed6\u56de\u7b54\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u5728\u62c5\u5fc3\u4ec0\u4e48\uff1f\u2026\u2026\u4e3a\u6211\u7237\u7237\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u6c89\u91cd\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u4e3a\u4e86\u4f60\uff0c\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u5728\u4e3a\u6211\u62c5\u5fc3\uff1f\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u505a\u9519\u4e8b\uff0c\u6709\u4e9b\u4e8b\u4f60\u867d\u7136\u505a\u9519\u4e86\u3002\u4ee5\u540e\u8fd8\u53ef\u4ee5\u60f3\u6cd5\u5b50\u633d\u56de\uff0c \u4f46\u8fd8\u6709\u4e9b\u4e8b\u4f60\u82e5\u4e00\u65e6\u505a\u9519\uff0c\u5c31\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u8865\u6551\u3002\u201d \u3000\u3000\u73b0\u5728\u3001\u4ed6\u76ee\u4e2d\u7684\u795e\u60c5\u4e0d\u4f46\u6709\u5fe7\u8651\uff0c\u8fd8\u5e26\u7740\u79cd\u6df1\u6c89\u7684\u60b2\u75db\u3002 \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u5b59\u5c0f\u7ea2\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e00\u751f\u4e2d\u53ea\u8981\u94f8\u4e0b\u4e00\u4ef6\u6c38\u8fdc\u65e0\u6cd5\u8865\u6551\u7684\u5927\u9519\uff0c\u65e0\u8bba \u4ed6\u7684\u51fa\u53d1\u70b9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u7ec8\u751f\u90fd\u5f97\u4e3a\u8fd9\u4ef6\u4e8b\u8d1f\u759a\uff0c\u5c31\u7b97\u522b\u4eba\u5df2\u539f\u8c05\u4e86\u4ed6\uff0c\u4f46\u4ed6\u81ea\u5df1\u5374\u65e0\u6cd5 \u539f\u8c05\u81ea\u5df1\uff0c\u90a3\u79cd\u611f\u89c9\u624d\u771f\u6b63\u53ef\u6015\u3002\u201d \u3000\u3000\u4ed6\u5f53\u7136\u5f88\u4e86\u89e3\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u4e3a\u4e86\u4ed6\u8fd9\u4e00\u751f\u4e2d\u552f\u4e00\u505a\u9519\u7684\u4e00\u4ef6\u4e8b\uff0c\u4ed6\u4ed8\u51fa\u7684\u4ee3\u4ef7\u4e4b\u5927\uff0c\u5b9e\u5728\u5927\u5f97\u53ef\u6015\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u77a7\u7740\u4ed6\uff0c\u5fc3\u91cc\u5ffd\u4e5f\u611f\u89c9\u5230\u4e00\u79cd\u83ab\u540d\u7684\u6050\u60e7\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u5728\u62c5\u5fc3\u6211\u4f1a\u505a\u9519\u4e8b\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u4e00\u76f4\u8ddf\u4f60\u7237\u7237\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u5230\u8fc7\u4ed6\u4f7f\u7528\u6b66\u529f\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u597d\u50cf\u6ca1\u6709\u2026\u2026\u201d \u7b2c\u516b\u5341\u4e00\u7ae0\u3000\u65e0\u5fc3\u94f8\u5927\u9519>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u4e00\u7ae0\u3000\u65e0\u5fc3\u94f8\u5927\u9519 \u3000\u3000\u5b59\u5c0f\u7ea2\u5f88\u5feb\u7684\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u90a3\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u4ed6\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u4f7f\u7528\u6b66\u529f\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u3002 \u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6ca1\u6709\u5fc5\u8981\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6839\u672c\u6ca1\u6709\u5bf9\u624b\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u4e5f\u2026\u2026\u201d \u3000\u3000\u5979\u58f0\u97f3\u5ffd\u7136\u505c\u987f\uff0c\u50cf\u662f\u5ffd\u7136\u60f3\u8d77\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u7684\u6240\u505a\u6240\u4e3a\uff0c\u4f60\u7237\u7237\u662f\u5426\u5df2\u89c9\u5f97\u4e0d\u80fd\u5fcd\u53d7\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u7684\u786e\u5bf9\u4e0a\u5b98\u91d1\u8679\u5f88\u6124\u6012\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u6ca1\u6709\u5411\u4e0a\u5b98\u91d1\u8679\u4e0b\u624b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u76f4\u5728\u5fcd\u53d7\uff1f\u4e3a\u4ec0\u4e48\u8981\u7b49\u4f60\u53bb\u6c42\u4ed6\u65f6\u624d\u80af\u51fa\u624b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u53c8\u62ac\u8d77\u5934\uff0c\u76ee\u4e2d\u7684\u6050\u60e7\u4e4b\u610f\u66f4\u91cd\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u8ba4\u4e3a\u4ed6\u8001\u4eba\u5bb6\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u89c9\u5f97\u5634\u91cc\u53d1\u5e72\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u6b66\u529f\u82e5\u662f\u5230\u4e86\u9876\u5cf0\uff0c\u5fc3\u91cc\u5c31\u4f1a\u4ea7\u751f\u4e00\u79cd\u6050\u60e7\uff0c\u751f\u6015\u522b\u4eba\u4f1a\u8d76 \u4e0a\u4ed6\uff0c\u751f\u6015\u81ea\u5df1\u4f1a\u9000\u6b65\uff0c\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u5f80\u5f80\u4f1a\u60f3\u6cd5\u5b50\u9003\u907f\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u6562\u53bb\u505a\u3002\u201d \u3000\u3000\u4ed6\u9eef\u7136\u53f9\u606f\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8d8a\u4e0d\u53bb\u505a\uff0c\u5c31\u6e10\u6e10\u4f1a\u53d8\u5f97\u771f\u7684\u4e0d\u80fd\u505a\u4e86\uff0c\u6709\u4e9b\u4eba\u5c31\u4f1a\u5ffd\u7136\u5f52\u9690 \uff0c\u6709\u4e9b\u4eba\u751a\u81f3\u4f1a\u53d8\u5f97\u81ea\u66b4\u81ea\u5f03\u2014\u2014\u751a\u81f3\u4e00\u6b7b\u4e86\u4e4b\u2026\u2026\u81ea\u53e4\u4ee5\u6765\uff0c\u8fd9\u6837\u7684\u4f8b\u5b50\u5df2\u6709\u5f88\u591a\uff0c\u9664\u975e \u4ed6\u771f\u7684\u80fd\u8d85\u7136\u7269\u5916\uff0c\u505a\u5230\u2018\u592a\u4e0a\u5fd8\u60c5\u2019\u7684\u5730\u6b65\uff0c\u5bf9\u4e16\u4e0a\u6240\u6709\u7684\u4e00\u5207\u4e8b\u90fd\u4e0d\u518d\u5173\u5fc3\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53ea\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u5b50\u5728\u6e10\u6e10\u50f5\u786c\uff0c\u51b7\u6c57\u5df2\u6e7f\u900f\u4e86\u8863\u670d\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u5979\u7237\u7237\u5e76\u4e0d\u80fd\u201c\u5fd8\u60c5\u201d\u3002 \u3000\u3000\u4ed6\u8fd8\u5728\u5173\u5fc3\u5f88\u591a\u4e8b\uff0c\u5f88\u591a\u4eba\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f46\u613f\u6211\u7684\u60f3\u6cd5\u4e0d\u5bf9\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u6251\u8fc7\u53bb\uff0c\u7d27\u7d27\u62b1\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u5979\u7684\u8eab\u5b50\u6296\u5f97\u50cf\u662f\u5f13\u5f26\u4e0b\u7684\u68c9\u82b1\u3002 \u3000\u3000\u5979\u5728\u6015\uff0c\u6015\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u8f7b\u629a\u7740\u5979\u7684\u5934\u53d1\uff0c\u4e5f\u4e0d\u77e5\u662f\u540c\u60c5\uff0c\u662f\u601c\u501f\uff0c\u8fd8\u662f\u60b2\u54c0\uff1f \u3000\u3000\u4e00\u4e2a\u5b8c\u5168\u6ca1\u6709\u60c5\u611f\u7684\u4eba\uff0c\u5c31\u7edd\u4e0d\u4f1a\u505a\u51fa\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u8fd9\u79cd\u4eba\u51e0\u4e4e\u4ece\u6765\u4e5f\u4e0d\u4f1a\u505a\u9519\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u4f46\u8001\u5929\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u591a\u60c5\u7684\u4eba\u94f8\u4e0b\u6c38\u65e0\u633d\u56de\u7684\u5927\u9519\u5462\uff1f \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u662f\u591a\u60c5\uff0c\u96be\u9053\u4ed6\u5c31\u5df2\u9519\u4e86\u4e48\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u62bd\u6410\u7740\uff0c\u6d41\u7740\u6cea\u9053\uff1a\u201c\u6c42\u6c42\u4f60\uff0c\u5e26\u6211\u8d76\u56de\u53bb\uff0c\u53ea\u8981\u80fd\u53ca\u65f6\u8d76\u5230\u90a3\u91cc\uff0c\u65e0\u8bba\u8981\u6211\u505a \u4ec0\u4e48\u6211\u90fd\u613f\u610f\u3002\u201d \u3000\u3000\u7a97\u5916\u7684\u9a6c\u5636\uff0c\u662f\u4e2a\u9a6c\u5e02\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u867d\u975e\u4f2f\u4e50\uff0c\u5374\u80fd\u76f8\u9a6c\u2014\u2014\u6709\u5f88\u591a\u4eba\u90e8\u77e5\u9053\uff0c\u674e\u5bfb\u6b22\u5bf9\u9a6c\u548c\u5973\u4eba\u90fd\u662f\u4e13\u5bb6\uff0c\u8981\u505a\u8fd9 \u6837\u7684\u4e13\u5bb6\u5e76\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u56e0\u4e3a\u9a6c\u548c\u5973\u4eba\u90fd\u662f\u5f88\u96be\u4e86\u89e3\u7684\u3002 \u3000\u3000\u4ed6\u9009\u4e86\u4e24\u5339\u6700\u5feb\u7684\u9a6c\u3002 \u3000\u3000\u6700\u7f8e\u4e3d\u7684\u5973\u4eba\u5e76\u4e0d\u4e00\u5b9a\u5c31\u662f\u6700\u53ef\u7231\u7684\uff0c\u6700\u5feb\u7684\u9a6c\u4e5f\u4e0d\u4e00\u5b9a\u6700\u5f3a\u58ee\u2014\u2014\u7f8e\u5973\u5f80\u5f80\u7f3a\u5c11\u6e29\u67d4 \uff0c\u5feb\u9a6c\u5f80\u5f80\u7f3a\u5c11\u6301\u4e45\u529b\u3002 \u3000\u3000\u5feb\u9a6c\u5012\u4e0b\u3002 \u3000\u3000\u4eba\u72c2\u5954\u3002 \u3000\u3000\u66ae\u8272\u6e10\u4e34\uff0c\u6e10\u6df1\u3002 \u3000\u3000\u4eba\u4ecd\u5728\u72c2\u5954\uff0c\u4ed6\u4eec\u65e2\u4e0d\u7ba1\u8def\u4eba\u7684\u60ca\u8bb6\uff0c\u4e5f\u4e0d\u987e\u81ea\u5df1\u7684\u4f53\u529b\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u4e0d\u987e\u4e00\u5207\u3002 \u3000\u3000\u591c\u8272\u6e10\u4e34\uff0c\u6e10\u6df1\u3002 \u3000\u3000\u8def\u4e0a\u5df2\u65e0\u4eba\u884c\u3002 \u3000\u3000\u53c8\u662f\u4e2a\u65e0\u661f\u65e0\u6708\u7684\u665a\u4e0a\uff0c\u4e5f\u770b\u4e0d\u5230\u706f\u5149\u3002 \u3000\u3000\u8def\u65c1\u4e00\u7247\u6697\u6797\uff0c\u6797\u5916\u4e00\u5e62\u4ead\u5f71\u3002 \u3000\u3000\u90a3\u5c82\u975e\u5c31\u662f\u4e0a\u5b98\u91d1\u8679\u7ea6\u6218\u7684\u5730\u65b9\uff1f \u3000\u3000\u9ed1\u6c89\u6c89\u7684\u591c\u8272\u4e2d\uff0c\u4eff\u4f5b\u770b\u5230\u957f\u4ead\u4e2d\u4e00\u70b9\u706b\u5149\u3002 \u3000\u3000\u706b\u5149\u5ffd\u660e\u5ffd\u706d\uff0c\u4eae\u7684\u65f6\u5019\uff0c\u5c31\u80fd\u9690\u7ea6\u770b\u5230\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u6574\u4e2a\u4eba\u90fd\u8f6f\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u4e00\u76f4\u80fd\u652f\u6301\u5230\u73b0\u5728\uff0c\u4e5f\u8bb8\u662f\u5947\u8ff9\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5979\u7684\u6050\u60e7\u3002 \u3000\u3000\u6050\u60e7\u5f80\u5f80\u80fd\u6fc0\u53d1\u4eba\u7684\u6f5c\u529b\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u7ec8\u4e8e\u5df2\u770b\u5230\u4e86\uff0c\u5979\u6700\u5e0c\u671b\u770b\u5230\u7684\uff0c\u5979\u4e00\u53e3\u6c14\u5ffd\u7136\u8870\u7aed\u3002 \u3000\u3000\u5979\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7981\u957f\u957f\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51fa\u8fd9\u70b9\u706b\u5149\u660e\u706d\u4e4b\u95f4\uff0c\u4eff\u4f5b\u6709\u79cd\u5947\u5f02\u7684\u8282\u594f\uff0c\u6709\u65f6\u660e\u4eae\u7684\u65f6\u5019\u957f\uff0c\u6709\u65f6\u7184\u706d\u7684\u65f6 \u5019\u957f\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8fd9\u70b9\u706b\u5149\u4eae\u5f97\u597d\u50cf\u4e00\u76cf\u706f\u3002 \u3000\u3000\u90a3\u5929\uff0c\u5728\u53e6\u4e00\u5ea7\u57ce\u5916\uff0c\u53e6\u4e00\u5ea7\u957f\u4ead\u91cc\uff0c\u674e\u5bfb\u6b22\u4e5f\u770b\u5230\u8fc7\u8fd9\u79cd\u540c\u6837\u7684\u706b\u5149\u3002 \u3000\u3000\u90a3\u5929\uff0c\u662f\u5b59\u8001\u5148\u751f\u5728\u957f\u4ead\u91cc\u62bd\u7740\u65f1\u70df\u3002 \u3000\u3000\u9664\u4e86\u5b59\u8001\u5148\u751f\u5916\uff0c\u674e\u5bfb\u6b22\u4ece\u672a\u770b\u5230\u8fc7\u53e6\u5916\u4e00\u4e2a\u4eba\u52be\u70df\u65f6\uff0c\u80fd\u62bd\u51fa\u8fd9\u4e48\u4eae\u7684\u706b\u5149\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53ea\u89c9\u76ee\u4e2d\u4f3c\u4e4e\u5ffd\u7136\u6709\u70ed\u6cea\u76c8\u7736\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5df2\u4f0f\u5728\u5730\u4e0a\uff0c\u4f4e\u4f4e\u7684\u54ed\u6ce3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u662f\u6b22\u559c\u7684\u6cea\uff0c\u4e5f\u662f\u611f\u6fc0\u7684\u6cea\u3002 \u3000\u3000\u8001\u5929\u6bd5\u7adf\u6ca1\u6709\u8981\u5979\u94f8\u4e0b\u5927\u9519\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u6276\u8d77\u4e86\u5979\uff0c\u518d\u5f80\u524d\u8d70\uff0c\u8d70\u5411\u957f\u4ead\u3002 \u3000\u3000\u957f\u4ead\u4e2d\u4eff\u4f5b\u8ff7\u6f2b\u7740\u4e00\u91cd\u70df\u96fe\uff0c\u4eba\uff0c\u5c31\u5750\u5728\u70df\u96fe\u4e2d\u3002 \u3000\u3000\u8fd9\u70df\u7684\u9999\u6c14\uff0c\u4e5f\u6b63\u662f\u5b59\u5c0f\u7ea2\u6240\u719f\u6089\u7684\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u53ea\u89c9\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u6323\u8131\u674e\u5bfb\u6b22\u6276\u7740\u5979\u7684\u624b\uff0c\u98de\u5954\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u4e00\u5fc3\u53ea\u60f3\u51b2\u5230\u5979\u7237\u7237\u7684\u6000\u62b1\u4e2d\uff0c\u5411\u4ed6\u8bf4\u51fa\u5fc3\u91cc\u7684\u611f\u6fc0\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u653e\u58f0\u5927\u547c\uff1a\u201c\u7237\u7237\uff0c\u6211\u4eec\u56de\u6765\u4e86\u2026\u2026\u6211\u4eec\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u957f\u4ead\u4e2d\u7684\u706b\u5149\u5ffd\u7136\u7184\u706d\u3002 \u3000\u3000\u7136\u540e\uff0c\u5c31\u54cd\u8d77\u4e86\u4e00\u4e2a\u4eba\u5e73\u9759\u7684\u58f0\u97f3\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u6b63\u5728\u7b49\u7740\u4f60\u4eec\uff01\u201d \u3000\u3000\u58f0\u97f3\u51b7\u6f20\u3001\u5e73\u9759\u3001\u575a\u5b9a\uff0c\u65e2\u6ca1\u6709\u8282\u594f\uff0c\u4e5f\u5b8c\u5168\u6ca1\u6709\u611f\u60c5\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u6014\u4f4f\uff0c\u80f8\u4e2d\u7684\u70ed\u8840\u7acb\u523b\u51b0\u51b7\uff0c\u51b7\u5f97\u51e0\u4e4e\u8981\u5c06\u5979\u6574\u4e2a\u4eba\u90fd\u51bb\u50f5\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u5c31\u50cf\u662f\u4e00\u4e2a\u68d2\u5b50\uff0c\u4e00\u4e0b\u5b50\u5c31\u5c06\u5979\u4ece\u5929\u5802\u6253\u4e0b\u5730\u72f1\uff01 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u56db\u76cf\u706f\u7b3c\u4eae\u8d77\u3002 \u3000\u3000\u56db\u76cf\u91d1\u9ec4\u8272\u7684\u706f\u7b3c\uff0c\u7528\u7ec6\u7af9\u7aff\u9ad8\u9ad8\u7684\u6311\u7740\u3002 \u3000\u3000\u91d1\u9ec4\u8272\u7684\u706f\u5149\u4e0b\uff0c\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u51b7\u5f97\u50cf\u9ec4\u91d1\uff0c\u786c\u5f97\u50cf\u9ec4\u91d1\uff0c\u8fde\u4ed6\u7684\u5fc3\u90fd\u50cf\u662f\u7528\u9ec4\u91d1\u94f8\u6210 \u7684\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u62bd\u7740\u65e9\u70df\u3002 \u3000\u3000\u4ed6\u62bd\u7684\u662f\u5b59\u8001\u5148\u751f\u7684\u65f1\u70df\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\uff01 \u3000\u3000\u5750\u5728\u957f\u4ead\u91cc\u62bd\u70df\u7684\u4eba\uff0c\u8d6b\u7136\u7adf\u662f\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u98ce\u51c4\u5207\uff0c\u96e8\u98d8\u96f6\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u96e8\u662f\u4ece\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\u4e0b\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u6728\u7acb\u5728\u96e8\u4e2d\uff0c\u5df2\u5b8c\u5168\u50f5\u786c\uff0c\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u5979\u60f3\u5450\u558a\uff0c\u53ef\u662f\u5979\u6ca1\u529b\u6c14\uff0c\u5979\u60f3\u51b2\u8fdb\u53bb\uff0c\u53ef\u662f\u5979\u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u5979\u7684\u80c3\u5728\u75c9\u53d8\uff0c\u6536\u7f29\uff0c\u60f3\u5455\u5410\u3002 \u3000\u3000\u53ef\u662f\u5979\u5374\u8fde\u773c\u6cea\u90fd\u5df2\u6d41\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u672c\u5c31\u8d70\u5f97\u6bd4\u5979\u6162\uff0c\u73b0\u5728\u8fd8\u662f\u5728\u6162\u6162\u7684\u8d70\u7740\uff0c\u811a\u6b65\u5e76\u6ca1\u6709\u505c\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u547c\u5438\u5374\u4f3c\u5df2\u5c06\u505c\u987f\u3002 \u3000\u3000\u4ed6\u6162\u6162\u7684\u8d70\u5230\u957f\u4ead\u5916\uff0c\u9762\u5bf9\u7740\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u751a\u81f3\u6ca1\u6709\u77a7\u4ed6\u4e00\u773c\uff0c\u53ea\u662f\u51dd\u6ce8\u7740\u624b\u91cc\u7684\u65f1\u70df\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u6765\u665a\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u6765\u665a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53ea\u89c9\u81ea\u5df1\u7684\u5634\u91cc\u5f88\u5e72\u71e5\uff0c\u5f88\u82e6\uff0c\u820c\u5934\u5c31\u597d\u50cf\u5728\u7eb8\u7740\u4e00\u679a\u5df2\u751f\u4e86\u9508\u7684\u94dc\u677f\u4e0a\uff0c\u4e5f\u8bf4\u4e0d\u51fa \u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u96be\u9053\u8fd9\u5c31\u662f\u6050\u60e7\u7684\u6ecb\u5473\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6765\u665a\u4e86\u603b\u6bd4\u4e0d\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u672c\u8be5\u77e5\u9053\u6211\u8fdf\u65e9\u603b\u8981\u6765\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8be5\u6765\u7684\u4eba\u6765\u8fdf\uff0c\u4e0d\u8be5\u6765\u7684\u4eba\u53cd\u800c\u5148\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u4e24\u4eba\u5ffd\u7136\u5168\u90fd\u95ed\u4e0a\u4e86\u5634\uff0c\u5c31\u8fd9\u6837\u9762\u5bf9\u9762\u7684\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u663e\u557c\u8981\u7b49\u5230\u6709\u628a\u63e1\u7684\u65f6\u5019\u624d\u52a8\u3002 \u3000\u3000\u8fd9\u4e00\u52a8\u5c31\u4e0d\u53ef\u6536\u62fe\uff01 \u3000\u3000\u98ce\u96e8\u4e2d\uff0c\u6697\u6797\u91cc\uff0c\u8fd8\u6709\u4e24\u4e2a\u4eba\uff0c\u4e24\u53cc\u773c\u775b\u3002 \u3000\u3000\u4e24\u53cc\u773c\u775b\u90fd\u5728\u77ac\u4e5f\u4e0d\u77ac\u7684\u51dd\u89c6\u7740\u674e\u5bfb\u6b22\u548c\u4e0a\u5b98\u91d1\u8679\uff01\u5176\u4e2d\u4e00\u53cc\u773c\u775b\u6e29\u67d4\u5982\u6c34\uff0c\u660e\u4eae\u5982\u661f \uff01 \u3000\u3000\u4f60\u8d70\u904d\u5929\u4e0b\uff0c\u4e5f\u5f88\u96be\u518d\u627e\u5230\u4e00\u53cc\u5982\u6b64\u7f8e\u4e3d\u52a8\u4eba\u7684\u773c\u775b\u3002 \u3000\u3000\u53e6\u4e00\u53cc\u773c\u775b\u5374\u662f\u6b7b\u7070\u7684\uff0c\u51e0\u4e4e\u5df2\u548c\u8fd9\u9634\u68ee\u7684\u591c\u8272\u6eb6\u4e3a\u4e00\u4f53\uff0c\u6bb7\u7b97\u662f\u5728\u5730\u72f1\u4e2d\uff0c\u53ea\u6015\u4e5f\u5f88 \u96be\u627e\u5230\u5982\u6b64\u53ef\u6015\u7684\u773c\u775b\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u5c31\u7b97\u6709\u9b3c\u57df\u9690\u85cf\uff0c\u6b64\u523b\u4e5f\u5e94\u8be5\u65e9\u5df2\u6e9c\u8d70\u3002 \u3000\u3000\u8fd9\u53cc\u773c\u775b\u8fde\u9b3c\u770b\u89c1\u4e86\u90fd\u5c06\u4e3a\u4e4b\u6218\u6817\u3002 \u3000\u3000\u6797\u4ed9\u513f\u548c\u8346\u65e0\u547d\u7adf\u5148\u6765\u5230\u8fd9\u91cc\uff0c\u800c\u4e14\u4eff\u4f5b\u5df2\u6765\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u6797\u4ed9\u513f\u501a\u5728\u8346\u65e0\u547d\u7684\u8eab\u65c1\uff0c\u7d27\u7d27\u6293\u7740\u8346\u65e0\u547d\u7684\u8180\u5b50\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e0d\u54cd\uff0c\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u82e5\u8981\u6740\u4ed6\uff0c\u73b0\u5728\u5c31\u662f\u6700\u597d\u7684\u673a\u4f1a\uff0c\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u5df2\u6709\u4eba\u6740\u4ed6\uff0c\u5df2\u7528\u4e0d\u7740\u6211\u51fa\u624b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8981\u4f60\u53bb\u6740\u674e\u5bfb\u6b22\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6740\u8c01\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\uff0c\u6740\u4e0a\u5b98\u91d1\u8679\uff01\u201d \u3000\u3000\u5979\u5174\u594b\u5f97\u5168\u8eab\u5728\u53d1\u6296\uff0c\u6307\u7532\u90fd\u5df2\u5d4c\u5165\u8346\u65e0\u547d\u7684\u8089\u91cc\u3002 \u3000\u3000\u8346\u65e0\u547d\u4e0d\u52a8\uff0c\u4f3c\u4e5f\u4e0d\u75bc\u3002 \u3000\u3000\u4f46\u4ed6\u76ee\u4e2d\u5374\u5df2\u9732\u51fa\u4e86\u4e00\u79cd\u5947\u7279\u7684\u5149\u8292\uff0c\u5c31\u50cf\u662f\u5730\u72f1\u4e2d\u7684\u706b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u6b63\u5168\u5fc3\u5168\u610f\u8981\u5bf9\u4ed8\u674e\u5bfb\u6b22\uff0c\u7edd\u6ca1\u6709\u4f59\u529b\u518d\u5bf9\u4ed8\u522b\u4eba\uff0c\u4f55\u51b5\uff0c\u4ed6\u8fd8\u4e0d \u77e5\u9053\u4f60\u53f3\u624b\u7684\u79d8\u5bc6\uff0c\u4f60\u4e00\u5b9a\u53ef\u4ee5\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u8fd8\u662f\u4e0d\u52a8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u91d1\u94b1\u5e2e\u7684\u79d8\u5bc6\uff0c\u53ea\u6709\u4f60\u77e5\u9053\u5f97\u6700\u591a\uff0c\u4f60\u6740\u4e86\u4ed6\uff0c\u4f60\u5c31\u662f\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u5979\u4f4e\u4f4e\u7684\u5598\u606f\u7740\u3002 \u3000\u3000\u5979\u7684\u5598\u606f\u58f0\u5e76\u4e0d\u5341\u5206\u597d\u542c\uff0c\u5c31\u50cf\u662f\u6761\u52a8\u4e86\u60c5\u7684\u6bcd\u72d7\u3002 \u3000\u3000\u5979\u5598\u606f\u7740\u53c8\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u4e0d\u60f3\u5f53\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\uff0c\u4f46\u4e5f\u8be5\u8ba9\u4ed6\u770b\u770b\u4f60\u7684\u5389\u5bb3\uff0c\u8ba9\u4ed6\u4e0b\u4e86\u5730 \u72f1\u540e\u8fd8\u8981\u540e\u6094\uff0c\u4ee5\u524d\u4e3a\u4ec0\u4e48\u90a3\u6837\u5bf9\u5f85\u4f60\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u773c\u775b\u4e2d\u82e5\u662f\u85cf\u7740\u5730\u72f1\u7684\u706b\u79cd\uff0c\u73b0\u5728\u706b\u5c31\u5df2\u71c3\u70e7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53bb\uff0c\u5feb\u53bb\uff0c\u9519\u8fc7\u8fd9\u673a\u4f1a\uff0c\u540e\u6094\u7684\u5c31\u662f\u4f60\uff0c\u800c\u4e0d\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u5410\u51fa\u53e3\u6c14\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u5feb\u53bb\u5427\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u53ea\u8981\u4f60\u6210\u529f\uff0c\u6211\u4ee5\u540e\u5c31\u6c38\u8fdc\u662f \u4f60\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u7b49\u6211\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u6014\u4e86\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u8981\u8ddf\u6211\u4e00\u9f50\u53bb\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u89c9\u5f97\u4e8b\u60c5\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u5979\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u521a\u9732\u51fa\u60ca\u60e7\u4e4b\u8272\uff0c\u8346\u65e0\u547d\u5df2\u62e7\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5e76\u4e0d\u65f6\u5e38\u6d41\u6cea\uff0c\u5979\u4ee5\u4e3a\u4e00\u4e2a\u5973\u4eba\u82e5\u53ea\u6709\u7528\u773c\u6cea\u624d\u80fd\u6253\u52a8\u7537\u4eba\u7684\u5fc3\uff0c\u90a3\u5973\u4eba\u4e0d\u662f\u5f88 \u611a\u8822\uff0c\u5c31\u662f\u5f88\u4e11\u964b\u3002 \u3000\u3000\u5979\u6709\u8bb8\u8bb8\u591a\u591a\u66f4\u597d\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u5374\u75bc\u5f97\u7acb\u523b\u5c31\u6d41\u51fa\u4e86\u773c\u6cea\u3002 \u3000\u3000\u5979\u51e0\u4e4e\u80fd\u542c\u5f97\u5230\u81ea\u5df1\u9aa8\u5934\u6298\u65ad\u7684\u58f0\u97f3\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6211\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8981\u8fd9\u6837\u5bf9\u6211\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u8bb8\u53ea\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u8ba4\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u548c\u963f\u98de\u4e00\u6837\u7231\u4f60\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u80cc\u5bf9\u7740\u6811\u6797\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u770b\u5230\u6797\u4e2d\u8d70\u51fa\u6765\u7684\u6797\u4ed9\u513f\u548c\u8346\u65e0\u547d\uff0c\u4ed6\u53ea\u770b\u5230\u4e0a\u5b98\u91d1\u8679\u8138\u4e0a\u7a81\u7136\u8d77\u4e86\u4e00\u79cd\u5f88\u5947 \u5f02\u7684\u53d8\u5316\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u6ce8\u610f\u529b\u7adf\u7a81\u7136\u5206\u6563\u4e86\u3002 \u3000\u3000\u4ed6\u4ece\u672a\u7ed9\u8fc7\u522b\u4eba\u8fd9\u6837\u7684\u673a\u4f1a\uff0c\u4ee5\u540e\u4e5f\u7edd\u4e0d\u4f1a\u518d\u7ed9\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u5e76\u6ca1\u6709\u628a\u63e1\u4f4f\u8fd9\u673a\u4f1a\uff0c\u4ed6\u7684\u98de\u5200\u7adf\u672a\u51fa\u624b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u611f\u89c9\u5230\u80cc\u540e\u6709\u79cd\u53ef\u6015\u7684\u6740\u6c14\u3002 \u3000\u3000\u4ed6\u7684\u98de\u5200\u5e76\u4e0d\u5355\u53ea\u662f\u7528\u624b\u63b7\u51fa\u53bb\u7684\uff0c\u800c\u662f\u7528\u4ed6\u7684\u5168\u526f\u7cbe\u795e\uff0c\u5168\u90e8\u7cbe\u529b\uff0c\u4ed6\u7684\u98de\u5200\u82e5\u51fa\u624b \uff0c\u5c31\u518d\u65e0\u4f59\u529b\u6765\u9632\u5fa1\u8eab\u540e\u7684\u653b\u51fb\u3002 \u3000\u3000\u4ed6\u7684\u811a\u6b65\u4e00\u6ed1\uff0c\u6ed1\u51fa\u4e86\u4e03\u5c3a\uff0c\u7acb\u523b\u5c31\u770b\u5230\u4e86\u8346\u65e0\u547d\u3002 \u3000\u3000\u8346\u65e0\u547d\u5df2\u6765\u5230\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u624d\u770b\u5230\u6797\u4ed9\u513f\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u5979\u4e5f\u4f1a\u53d8\u5f97\u5982\u6b64\u72fc\u72c8\u3002 \u3000\u3000\u96e8\u66f4\u5927\u4e86\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u90fd\u5df2\u6e7f\u900f\u3002 \u3000\u3000\u9ad8\u6311\u7740\u7684\u706f\u7b3c\u867d\u5df2\u79fb\u5230\u957f\u4ead\u6a90\u4e0b\uff0c\u5374\u8fd8\u662f\u7167\u4e0d\u8fdc\u3002 \u3000\u3000\u8346\u65e0\u547d\u5c31\u7ad9\u5728\u706f\u5149\u7167\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u4ed6\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u4e2a\u5f71\u5b50\uff0c\u4eff\u4f5b\u6839\u672c\u5c31\u4e0d\u5b58\u5728\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u7684\u773c\u775b\u5374\u5df2\u4ece\u4e0a\u5b98\u91d1\u8679\u8eab\u4e0a\u79fb\u5f00\uff0c\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u773c\u775b\u4e5f\u5df1\u4ece\u674e\u5bfb\u6b22\u7684\u8eab\u4e0a\u79fb\u5f00\u3002\u4e5f\u5728\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u90fd\u5df2\u611f\u89c9\u5230\u8fd9\u4e00\u6218\u80dc\u8d1f\u7684\u5173\u952e\u5df2\u4e0d\u5728\u4ed6\u4eec\u672c\u8eab\uff0c\u800c\u5728\u8346\u65e0\u547d\u7684\u624b\u4e0a\u3002 \u3000\u3000\u8346\u65e0\u547d\u7a81\u7136\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4ece\u672a\u5982\u6b64\u5927\u7b11\u8fc7\uff0c\u4ed6\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u5427\uff0c\u56e0\u4e3a\u4f60\u7684\u786e\u5e94\u8be5\u7b11\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u7b11\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u7b11\u4e0d\u51fa\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u77e5\u9053\uff0c\u6211\u7684\u786e\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u505c\u4f4f\u7b11\u58f0\uff0c\u6162\u6162\u7684\u7ad9\u76f4\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u53ea\u6709\u6211\u624d\u80fd\u51b3\u5b9a\u4f60\u4eec\u7684\u6b7b\u6d3b\uff0c \u4f46\u4f60\u4eec\u5374\u4e0d\u6562\u5411\u6211\u51fa\u624b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u4e0d\u9519\uff0c\u7684\u786e\u6ca1\u6709\u4eba\u6562\u5411\u4ed6\u51fa\u624b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u82e5\u5411\u4ed6\u51fa\u624b\uff0c\u5c31\u7b97\u80fd\u6740\u4e86\u4ed6\uff0c\u81ea\u5df1\u7684\u80cc\u90e8\u4fbf\u638c\u63e1\u5728\u674e\u5bfb\u6b22\u624b\u91cc\u3002\u4ed6\u5f53\u7136\u4e0d\u4f1a\u7ed9 \u674e\u5bfb\u6b22\u8fd9\u673a\u4f1a\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u60c5\u51b5\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u8346\u65e0\u547d\u7f13\u7f13\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u53ef\u4ee5\u5e2e\u4f60\u6740\u4e86\u674e\u5bfb\u6b22\uff0c\u4e5f\u53ef\u4ee5\u5e2e\u4ed6\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u53ef\u4ee5\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\uff1f\u5728\u4f60\u773c\u4e2d\uff0c\u6211\u5c82\u975e\u5df2\u662f\u4e2a\u6b8b\u5e9f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u770b\u9519\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4f60\u770b\u9519\u4e86\uff1f\u4e5f\u8bb8\u6211\u7684\u786e\u662f\u4e2a\u6b8b\u5e9f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u7684\u53f3\u624b\u6bd4\u5de6\u624b\u66f4\u6709\u529b\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6797\u4ed9\u513f\u5e76\u4e0d\u662f\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5973\u4eba\uff0c\u65e0\u8bba\u8c01\u60f3\u7528\u4e00\u53ea\u624b\u5236\u4f4f\u5979\uff0c\u90fd\u4e0d\u5bb9\u6613 \u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u679c\u7136\u770b\u51fa\u6765\u4e86\uff0c\u53ea\u53ef\u60dc\u592a\u8fdf\u4e86\u4e9b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e5f\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u770b\u9519\uff0c\u4e5f\u505a\u9519\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u4e0d\u8be5\u90a3\u6837\u5bf9\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u7684\u786e\u4e0d\u8be5\u90a3\u6837\u5bf9\u4f60\uff0c\u6211\u672c\u8be5\u6740\u4e86\u4f60\u7684\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6ca1\u6709\u6740\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u4e0d\u5fcd\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u8138\u4e0a\u7a81\u4e5f\u8d77\u4e86\u5947\u5f02\u7684\u53d8\u5316\uff0c\u560e\u58f0\u9053\uff1a\u201c\u4f60\u4e5f\u6709\u4e0d\u5fcd\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u6211\u4e5f\u4e0d\u5fcd\u6740\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8198\u4e86\u6797\u4ed9\u513f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u4e5f\u60f3\u8981\u4f60\u6765\u6740\u6211\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u8981\u6740\u6211\uff0c\u5c31\u4e0d\u4f1a\u5c06\u5979\u5e26\u6765\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u4e5f\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u7684\u4eba\u672c\u5df2\u5012\u5728\u6ce5\u6cde\u4e2d\uff0c\u6b64\u523b\u5ffd\u7136\u7b11\u4e86\uff0c\u5b9e\u5728\u4ee4\u4eba\u5403\u60ca\u3002 \u3000\u3000\u5979\u5927\u7b11\u7740\u9053\uff1a\u201c\u4ed6\u7684\u786e\u4e0d\u6562\u6740\u4f60\uff0c\u56e0\u4e3a\u4f60\u82e5\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u6d3b\u4e0d\u4e0b\u53bb\uff0c\u6211\u73b0\u5728\u624d\u660e\u767d\uff0c\u4ed6\u8fd9 \u4eba\u672c\u5c31\u662f\u4e3a\u4f60\u800c\u6d3b\u7740\u7684\uff0c\u4ed6\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u4e3a\u4e86\u8981\u5728\u4f60\u9762\u524d\u8bc1\u660e\u4ed6\u81ea\u5df1\u662f\u591a\u4e48\u91cd\u8981\uff0c\u53ef\u662f\u5728\u522b \u4eba\u773c\u91cc\uff0c\u4ed6\u6839\u672c\u8fde\u4e00\u6587\u90fd\u4e0d\u503c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f46\u4ed6\u8981\u6740\u4f60\u5374\u5f88\u5bb9\u6613\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u6562\u6740\u6211\uff1f\u2026\u2026\u4f60\u8981\u6740\u6211\uff0c\u4ed6\u5374\u6551\u4e86\u6211\uff0c\u4f60\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8981\u4eb2\u624b\u5728\u6211\u9762\u524d\u6740\u4f60\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u4ed6\u5e76\u4e0d\u662f\u8981\u81ea\u5df1\u4eb2\u624b\u6740\u6211\uff0c\u800c\u662f\u8981\u770b\u4f60\u4eb2\u624b\u6740\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u5927\u7b11\u7740\u9053\uff1a\u201c\u6211\u548c\u4f60\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u4ed6\u5ac9\u5992\u5f97\u53d1\u75af\uff0c\u90a3\u65f6\u6211\u672c\u4ee5\u4e3a\u4ed6\u662f\u4e3a\u4e86\u6211\uff0c\u73b0\u5728 \u6211\u624d\u77e5\u9053\u4ed6\u662f\u4e3a\u4e86\u4f60\uff0c\u53ea\u8981\u662f\u4f60\u559c\u6b22\u7684\u4eba\uff0c\u4ed6\u90fd\u6068\uff0c\u751a\u81f3\u8fde\u4f60\u7684\u513f\u5b50\u4e5f\u4e0d\u4f8b\u5916\u2026\u2026\u4f60\u53ef\u77e5\u9053 \u4f60\u513f\u5b50\u662f\u8c01\u6740\u6b7b\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9762\u4e0a\u5168\u65e0\u8868\u60c5\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u4e3a\u4e86\u6211\u800c\u6740\u4eba\uff0c\u65e0\u8bba\u6740\u8c01\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u77a7\u7740\u4ed6\uff0c\u8138\u4e0a\u7684\u7b11\u6e10\u6e10\u6d88\u5931\uff0c\u7ec8\u4e8e\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e00\u5411\u8ba4\u4e3a\u6211\u5f88\u80fd\u4e86\u89e3 \u7537\u4eba\uff0c\u53ef\u662f\u6211\u5374\u5b9e\u5728\u4e0d\u4e86\u89e3\u4f60\u4eec\uff0c\u5b9e\u5728\u60f3\u4e0d\u901a\u4f60\u4eec\u4e24\u4e2a\u4eba\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u5979\u51b7\u7b11\u7740\u63a5\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u65e0\u8bba\u90a3\u662f\u79cd\u4ec0\u4e48\u6837\u6d3b\u89c1\u9b3c\u7684\u5173\u7cfb\uff0c\u90fd\u4e00\u5b9a\u4ee4\u4eba\u6076\u5fc3\u5f97\u8981\u547d\uff0c \u6240\u4ee5\u4f60\u4eec\u5c31\u7b97\u60f3\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u4e0d\u60f3\u542c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u4e0d\u591a\uff0c\u8bf4\u7684\u5374\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f46\u6211\u65e0\u8bba\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u8bbe\u6cd5\u5b50\u8981\u4f60\u6740\u4ed6\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u6ca1\u6cd5\u5b50\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u8f6c\u8fc7\u8138\uff0c\u8f6c\u5411\u8346\u65e0\u547d\uff0c\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u6ca1\u6cd5\u5b50\u8981\u4f60\u6740\u4ed6\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u53ea\u6709\u8ba9\u4f60\u4eec\u4e24\u4e2a\u4eba\u6765\u6740\u6211\u4e86\u3002\u95ee\u9898\u662f\u8c01\u52a8\u624b\u5462\uff1f\u662f\u4ed6\uff1f \u8fd8\u662f\u4f60\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4e00\u62ac\uff0c\u5c31\u5c06\u6797\u4ed9\u513f\u6454\u4e86\u51fa\u53bb\uff0c\u6454\u5728\u4e0a\u5b98\u91d1\u8679\u811a\u4e0b\u3002 \u3000\u3000\u6797\u4ed9\u513f\u8fd9\u6b21\u65e2\u4e0d\u518d\u6323\u624e\uff0c\u4e5f\u4e0d\u518d\u52a8\uff0c\u5c31\u8fd9\u6837\u8737\u66f2\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4f46\u5979\u6bd5\u7adf\u662f\u5973\u4eba\u3002 \u3000\u3000\u4f60\u53ef\u4ee5\u4ee4\u5979\u4e0d\u52a8\uff0c\u4e0d\u53cd\u6297\uff0c\u5374\u4e0d\u80fd\u8981\u5979\u4e0d\u8bf4\u8bdd\u3002 \u7b2c\u516b\u5341\u4e8c\u7ae0\u3000\u65e0\u8a00\u7684\u6170\u85c9>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u4e8c\u7ae0\u3000\u65e0\u8a00\u7684\u6170\u85c9 \u3000\u3000\u4f60\u82e5\u662f\u591a\u52a0\u6ce8\u610f\uff0c\u5c31\u4f1a\u53d1\u89c9\u4e00\u4e2a\u5973\u4eba\u6b7b\u7684\u65f6\u5019\uff0c\u8eab\u4e0a\u6700\u540e\u50f5\u786c\u7684\u4e00\u4e2a\u5730\u65b9\u5c31\u662f\u5979\u7684\u820c\u5934 \u3002\u8fd9\u53ea\u56e0\u5973\u4eba\u820c\u5934\u4e0a\u7684\u808c\u8089\u6c38\u8fdc\u90e8\u6bd4\u5176\u4ed6\u4efb\u4f55\u5730\u65b9\u7075\u654f\u5f97\u591a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u7136\u662f\u4f60\uff0c\u4ed6\u628a\u6211\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u770b\u4f60\u4eb2\u624b\u6740\u6211\uff0c\u53ea\u6709\u7528 \u8fd9\u6cd5\u5b50\u4ed6\u5fc3\u91cc\u624d\u4f1a\u89c9\u5f97\u8212\u670d\u4e9b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u5462\uff1f\u6b7b\u5728\u6211\u624b\u4e0a\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u89c9\u5f97\u8212\u670d\u4e9b\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u90a3\u5c31\u8981\u770b\u4f60\u7528\u4ec0\u4e48\u6cd5\u5b50\u6765\u6740\u6211\u4e86\uff0c\u6211\u5012\u4e0d\u5e0c\u671b\u6b7b\u5f97\u5f88\u5feb\uff0c\u56e0\u4e3a\u53ea\u6709\u6162\u6162\u7684 \u6b7b\uff0c\u624d\u80fd\u771f\u6b63\u9886\u7565\u5230\u6b7b\u7684\u6ecb\u5473\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e00\u751f\u4e2d\u53ea\u6709\u4e00\u6b21\u8fd9\u4e48\u6837\u7684\u673a\u4f1a\uff0c\u7eb5\u7136\u8981\u6211\u591a\u5fcd\u53d7\u4e9b\u75db\u82e6\uff0c \u4e5f\u662f\u503c\u5f97\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u800c\u4e14\u6b7b\u5f97\u82e5\u6162\u4e9b\uff0c\u4f60\u4e5f\u53ef\u4ee5\u591a\u8bf4\u51e0\u53e5\u6d3b\uff0c\u56e0\u4e3a\u8bf4\u8bdd\u4e0d\u4f46\u80fd\u51cf\u8f7b\u4f60\u7684 \u75db\u82e6\uff0c\u4e5f\u80fd\u51cf\u8f7b\u4f60\u7684\u6050\u60e7\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5f88\u5feb\u5c31\u6740\u4e86\u6211\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u4f60\u672c\u5c31\u559c\u6b22\u770b\u7740\u4eba\u6162\u6162\u7684\u6b7b\uff0c\u4f55 \u51b5\uff0c\u6211\u5bf9\u4f60\u603b\u7b97\u4e0d\u9519\uff0c\u81f3\u5c11\u6211\u8f9b\u8f9b\u82e6\u82e6\u5b58\u7684\u4e00\u70b9\u79c1\u623f\u94b1\uff0c\u5df2\u5168\u90e8\u88ab\u4f60\u60f3\u6cd5\u5b50\u5f04\u8d70\u4e86\uff0c\u4f60\u53eb\u4eba \u53bb\u6740\u6211\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u7ecf\u628a\u6211\u522e\u5f97\u4e8e\u5e72\u51c0\u51c0\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u73b0\u5728\u7684\u786e\u5df2\u4e00\u6587\u4e0d\u503c\uff0c\u6240\u4ee5\u6211\u6839\u672c\u5df1\u61d2\u5f97\u6740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e00\u811a\u5c06\u6797\u4ed9\u513f\u8e22\u4e86\u51fa\u53bb\uff0c\u8e22\u5230\u674e\u5bfb\u6b22\u9762\u524d\u3002 \u3000\u3000\u8fd9\u6b21\u5979\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\uff0c\u6e7f\u900f\u7684\u8863\u670d\u7d27\u7d27\u8d34\u5728\u5979\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u7684\u916e\u4f53\u4f9d\u7136\u662f\u7f8e\u4e3d\u7684\u3002 \u3000\u3000\u8fd9\u672c\u662f\u6b66\u6797\u4e2d\u7684\u7b2c\u4e00\u7f8e\u4eba\uff0c\u4e0d\u4f46\u7f8e\uff0c\u800c\u4e14\u806a\u660e\u3002 \u3000\u3000\u5979\u672c\u53ef\u4ee5\u6d3b\u5f97\u5f88\u597d\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u5374\u8fde\u6b7b\u4e5f\u4e0d\u80fd\u597d\u597d\u7684\u6b7b\u3002 \u3000\u3000\u5979\u672c\u662f\u4e91\u7aef\u4e0a\u7684\u4ed9\u5b50\uff0c\u4f46\u73b0\u5728\u5374\u53d8\u5f97\u5c31\u50cf\u662f\u6761\u6ce5\u6d46\u4e2d\u7684\u91ce\u72d7\u3002 \u3000\u3000\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u662f\u4e0d\u662f\u56e0\u4e3a\u5979\u4ece\u4e0d\u77e5\u9053\u5bf9\u81ea\u5df1\u5e94\u8be5\u73cd\u60dc\u7684\u4e1c\u897f\u591a\u52a0\u73cd\u60dc\uff1f \u3000\u3000\u96e8\u66f4\u5927\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u77a7\u7740\u6ce5\u6cde\u4e2d\u7684\u6797\u4ed9\u513f\uff0c\u5fc3\u91cc\u5ffd\u7136\u5f88\u60b2\u54c0\u5f88\u540c\u60c5\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u540c\u60c5\u5979\uff0c\u800c\u662f\u540c\u60c5\u963f\u98de\u3002 \u3000\u3000\u5979\u672c\u662f\u81ea\u4f5c\u81ea\u53d7\uff0c\u4f46\u963f\u98de\u5462\uff1f \u3000\u3000\u963f\u98de\u5e76\u6ca1\u6709\u9519\u3002 \u3000\u3000\u4ed6\u867d\u7136\u7231\u9519\u4e86\u4eba\uff0c\u4f46\u7231\u7684\u672c\u8eab\u5e76\u6ca1\u6709\u9519\uff0c\u4e5f\u8bb8\u8fd9\u624d\u662f\u6700\u503c\u5f97\u60b2\u54c0\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5374\u5728\u77a7\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4e0d\u6740\u5979\uff0c\u53ea\u56e0\u6211\u89c9\u5f97\u4f60\u6bd4\u6211\u66f4\u6709\u7406\u7531\u6740\u5979\uff0c\u6211 \u8ba9\u7ed9\u4f60\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u53c8\u4f4e\u4f30\u4e86\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53c8\u4f4e\u4f30\u4e86\u4f60\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u6740 \u5979\u7684\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u6740\u4eba\uff0c\u8981\u6740\u6c14\uff0c\u4f60\u7684\u6740\u6c14\u8981\u5168\u90e8\u7559\u7740\u6765\u5bf9\u4ed8\u6211\uff0c\u600e\u4e48\u4f1a\u6d6a\u8d39\u5728\u5979\u8fd9 \u79cd\u4eba\u8eab\u4e0a\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4eba\u4e0d\u5bf9\u56fa\u7136\u4e0d\u80fd\u6740\uff0c\u5730\u65b9\u4e0d\u5bf9\u4e5f\u4e0d\u80fd\u52a8\u624b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u672c\u6765\u662f\u5bf9\u7684\uff0c\u73b0\u5728\u5374\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u73b0\u5728\u592a\u6324\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u662f\u4ed6\u4ee4\u4f60\u4e0d\u5b89\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u60f3\u9690\u7792\uff0c\u8346\u65e0\u547d\u7eb5\u7136\u4e0d\u51fa\u624b\uff0c\u5bf9\u4ed6\u4e5f\u662f\u79cd\u5a01\u80c1\u3002 \u3000\u3000\u4f55\u51b5\u8346\u65e0\u547d\u968f\u65f6\u53ef\u80fd\u51fa\u624b\u7684\u3002\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u62b5\u6321\u4ed6\u548c\u4e0a\u5b98\u91d1\u8679\u7684\u8054\u624b\u4e00\u51fb\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u8138\u53c8\u6c89\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u65e2\u7136\u5df2\u56de\u6765\uff0c\u5c31\u6ca1\u6709\u4eba\u518d \u80fd\u8981\u4ed6\u79bb\u5f00\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8fd9\u6700\u540e\u4e00\u53e5\u8bdd\u81ea\u7136\u662f\u95ee\u8346\u65e0\u547d\u7684\u3002 \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u662f\u7ad9\u5f97\u5f88\u8fdc\uff0c\u4f46\u65e0\u8bba\u8c01\u90fd\u611f\u89c9\u5230\u4ed6\u548c\u4e0a\u5b98\u91d1\u8679\u5df2\u53c8\u7ed3\u6210\u4e86\u4e00\u4f53\uff0c\u7ed3\u6210\u4e86\u4e00\u822c\u65e0\u575a\u4e0d \u6467\u7684\u529b\u91cf\uff0c\u6ca1\u6709\u4eba\u80fd\u6467\u6bc1\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u62b5\u5fa1\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u963f\u98de\uff0c\u963f\u98de\u82e5\u662f\u5728\u8fd9\u91cc\u2026\u2026 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u4f3c\u5df2\u770b\u900f\u4e86\u4ed6\u7684\u5fc3\uff0c\u60a0\u7136\u9053\uff1a\u201c\u963f\u98de\u82e5\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u4e5f\u8bb8\u8fd8\u6709\u673a\u4f1a\uff0c\u53ea\u53ef\u60dc\u2026 \u2026\u4ed6\u5374\u5f88\u4ee4\u4eba\u5931\u671b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u5bf9\u4ed6\u5931\u671b\uff0c\u6709\u4e9b\u4eba\u65e0\u8bba\u5012\u4e0b\u53bb\u591a\u5c11\u6b21\uff0c\u8fd8\u662f\u80fd\u7ad9\u5f97\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6de1\u6de1\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u6ca1\u6709\u770b\u9519\uff0c\u4f46\u7b49\u4ed6\u7ad9\u8d77\u6765\u7684\u65f6\u5019\u3002\u4f60\u5fc5\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u6211\u53ef\u4ee5 \u4fdd\u8bc1\u8fd9\u6b21\u4f60\u4e00\u5012\u4e0b\u53bb\uff0c\u5c31\u6c38\u8fdc\u65e0\u6cd5\u7ad9\u8d77\uff01\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u73b0\u5728\u4f60\u7edd\u5bf9\u6ca1\u6709\u673a\u4f1a\uff0c\u4e00\u5206\u673a\u4f1a\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u81f3\u5c11\u5e94\u8be5\u8ba9\u6211\u9009\u4e2a\u5730\u65b9\uff0c\u4e00\u4e2a\u4eba\u82e5\u5df2\u975e\u6b7b\u4e0d\u53ef\uff0c\u4ed6\u81f3 \u5c11\u6709\u6743\u9009\u62e9\u5728\u54ea\u91cc\u6b7b\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u53c8\u9519\u4e86\uff0c\u6740\u4eba\u7684\u624d\u6709\u6743\uff0c\u88ab\u6740\u7684\u4eba\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u903c\u89c6\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5bf9\u4f60\uff0c\u6211\u4e5f\u8bb8\u4f1a\u7834\u4f8b\u4e00\u6b21\uff0c\u4f60\u4e0d\u4f46\u662f\u4e2a\u5f88\u597d\u7684\u670b\u53cb\uff0c\u4e5f\u662f \u4e2a\u5f88\u597d\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u60f3\u6b7b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u6d3b\u5f97\u592a\u8f9b\u82e6\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u4f1a\u60f3\u8981\u6b7b\u5f97\u8212\u670d\u4e9b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u65e0\u8bba\u600e\u4e48\u6837\u6b7b\uff0c\u90fd\u4e0d\u4f1a\u592a\u8212\u670d\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u627e\u4e2a\u6ca1\u6709\u96e8\u7684\u5730\u65b9\uff0c\u6362\u5957\u5e72\u51c0\u7684\u8863\u670d\uff0c\u6211\u4e0d\u559c\u6b22\u6e7f\u6dcb\u6dcb\u7684\u6b7b\uff0c \u4e0d\u559c\u6b22\u5012\u5728\u6e7f\u6dcb\u6dcb\u7684\u5730\u65b9\u3002\u201d\u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u9664\u4e86\u6d17\u6fa1\u7684\u65f6\u5019\uff0c\u6211\u90fd\u5b81\u613f \u81ea\u5df1\u7684\u8eab\u4e0a\u662f\u5e72\u7740\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7a81\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5e38\u542c\u4eba\u8bf4\u4f60\u4e0d\u6015\u6b7b\uff0c\u4f46\u5374\u4e00\u76f4\u4e0d\u76f8\u4fe1\uff0c\u56e0\u4e3a\u6211\u6839\u672c\u4e0d \u4fe1\u4e16\u4e0a\u771f\u6709\u4e0d\u6015\u6b7b\u7684\u4eba\uff0c\u76f4\u5230\u73b0\u5728\u2014\u2014\u73b0\u5728\u6211\u624d\u6709\u70b9\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u5728\u4e34\u6b7b\u524d\u8fd8\u80fd\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u53ef\u89c1\u4ed6\u5bf9\u751f\u6b7b\u7684\u786e\u5df2\u770b\u5f97\u5f88\u6de1\uff0c\u6240\u4ee5 \u6211\u624d\u66f4\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5947\u602a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u9664\u6b7b\u4e4b\u5916\u65e0\u5927\u4e8b\uff0c\u4e00\u4e2a\u4eba\u82e5\u5bf9\u6b7b\u90fd\u4e0d\u5728\u4e4e\uff0c\u53c8\u600e\u4e48\u4f1a \u5728\u4e4e\u4ed6\u6b7b\u7684\u65f6\u5019\u8eab\u5b50\u662f\u6e7f\u662f\u5e72\u5462\uff1f\u201d \u3000\u3000\u4ed6\u76ef\u7740\u674e\u5bfb\u6b22\uff0c\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u6211\u60f3\uff0c\u4f60\u8fd9\u4e48\u6837\u505a\uff0c\u4e00\u5b9a\u53e6\u6709\u76ee\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u662f\u4ec0\u4e48\u76ee\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u4e5f\u8bb8\u4f1a\u8ba4\u4e3a\u4f60\u8fd9\u53ea\u4e0d\u8fc7\u662f\u6545\u610f\u5728\u62d6\u65f6\u95f4\uff0c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u5c31\u7b97\u5df2\u660e\u77e5 \u5fc5\u6b7b\u65e0\u7591\u5374\u8fd8\u662f\u8981\u5c3d\u91cf\u60f3\u6cd5\u5b50\u62d6\u4e00\u62d6\uff0c\u5e0c\u671b\u80fd\u6709\u5947\u8ff9\u51fa\u73b0\uff0c\u81f3\u5c11\u80fd\u591a\u6d3b\u4e00\u523b\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u4e5f\u8fd9\u4e48\u60f3\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u8fd9\u4e48\u60f3\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u4f4e\u4f30\u4f60\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u4f60\u5f53\u7136\u77e5\u9053\u7edd\u4e0d\u4f1a\u6709\u5947\u8ff9\u51fa\u73b0\uff0c\u8fd9\u4e16\u4e0a\u6839\u672c\u5df2\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u80fd\u6551\u5f97\u4e86\u4f60 \uff0c\u4f55\u51b5\uff0c\u4f60\u6839\u672c\u5c31\u4e0d\u6015\u6b7b\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u600e\u4e48\u60f3\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u60f3\uff0c\u4f60\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u4e0d\u8fc7\u662f\u5728\u627e\u673a\u4f1a\u8ba9\u5979\u4eec\u9003\u8d70\u800c\u5df2\uff0c\u56e0\u4e3a\u4f60\u77e5\u9053\u6211 \u5728\u6740\u4f60\u4e4b\u524d\uff0c\u7edd\u4e0d\u4f1a\u6740\u522b\u7684\u4eba\uff0c\u8fd9\u6b63\u5982\u4e00\u4e2a\u4eba\u82e5\u77e5\u9053\u6709\u5c71\u73cd\u6d77\u5473\u53ef\u5403\uff0c\u5c31\u7edd\u4e0d\u4f1a\u5148\u7528\u9992\u5934\u5927 \u997c\u6765\u586b\u9971\u809a\u5b50\uff0c\u514d\u5f97\u574f\u4e86\u80c3\u53e3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u8fd9\u6bd4\u55bb\u5e76\u4e0d\u597d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4f46\u5374\u4e0d\u5047\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u5f97\u5df2\u6709\u4e9b\u52c9\u5f3a\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u4e0d\u5047\uff0c\u4f46\u4f60\u96be\u9053\u4f1a\u5c06\u5979\u4eec\u7684\u6b7b\u6d3b\u653e\u5728\u5fc3\u4e0a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u4ed6\u7684\u786e\u4e0d\u5fc5\u3002 \u3000\u3000\u5979\u4eec\u6d3b\u7740\uff0c\u5bf9\u4ed6\u5df2\u5168\u65e0\u5a01\u80c1\u3002 \u3000\u3000\u4ed6\u82e5\u8981\u5979\u4eec\u6b7b\uff0c\u968f\u65f6\u968f\u5730\u90fd\u65b9\u4fbf\u5f97\u5f88\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u51e0\u4e4e\u4e0d\u5fcd\u518d\u53bb\u77a7\u5b59\u5c0f\u7ea2\u4e00\u773c\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u73b0\u5728\u603b\u7b97\u8fd8\u6709\u751f\u547d\uff0c\u8fd8\u80fd\u547c\u5438\u3002 \u3000\u3000\u8fd9\u5df2\u8db3\u591f\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u80fd\u4e3a\u5979\u505a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u5df2\u8bf4\u8fc7\uff0c\u6211\u4e3a\u4f60\u7834\u4f8b\u4e00\u6b21\uff0c\u56e0\u4e3a\u4f60\u548c\u522b\u7684\u4eba\u5168\u65e0\u5173\u7cfb\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u4f60\u6d3b\u5f97\u5f88\u5e72\u51c0\uff0c\u6211\u81f3\u5c11\u603b\u4e0d\u80fd\u8ba9\u4f60\u6b7b\u5f97\u592a\u9f8c\u9f8a\u2014\u2014\u81f3\u5c11\u603b\u4e0d\u80fd\u8ba9\u4f60 \u50cf\u91ce\u72d7\u822c\u6b7b\u5728\u6ce5\u5df4\u91cc\u3002\u201d \u3000\u3000\u6b7b\uff0c\u662f\u600e\u4e48\u6837\u6b7b\uff0c\u6b7b\u5728\u54ea\u91cc\uff1f \u3000\u3000\u8fd9\u90fd\u4e0d\u91cd\u8981\u3002 \u3000\u3000\u91cd\u8981\u7684\u662f\u8981\u6b7b\u5f97\u5b89\u5fc3\uff0c\u6b7b\u5f97\u5e72\u51c0\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5462\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u4e00\u76f4\u4e0d\u5fcd\u53bb\u770b\u5979\u4e5f\u4e0d\u80fd\u53bb\u770b\u5979\u3002 \u3000\u3000\u4ed6\u7684\u6ce8\u610f\u529b\u7edd\u4e0d\u80fd\u5206\u6563\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u6ca1\u6709\u542c\u5230\u5b59\u5c0f\u7ea2\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5c31\u8981\u8d70\u4e86\uff0c\u5979\u5f53\u7136\u4e5f\u77e5\u9053\u4ed6\u8fd9\u4e00\u8d70\uff0c\u4ee5\u540e\u4e5f\u8bb8\u5c31\u6c38\u8fdc\u6ca1\u6709\u89c1\u9762\u7684\u65f6\u5019\uff0c\u8fd9\u4e00\u8d70 \u4e5f\u8bb8\u4e0d\u662f\u751f\u79bb\uff0c\u800c\u662f\u6b7b\u522b\u3002 \u3000\u3000\u5979\u600e\u4e48\u80fd\u5c31\u8fd9\u6837\u8ddf\u7740\u4ed6\u8d70\uff1f \u3000\u3000\u4ed6\u751f\u6015\u5979\u4f1a\u8d76\u8fc7\u6765\uff0c\u8981\u8ddf\u4ed6\u4e00\u9f50\u8d70\uff0c\u8981\u966a\u7740\u4ed6\u4e00\u9f50\u6b7b\u3002 \u3000\u3000\u5979\u82e5\u8fd9\u6837\u505a\uff0c\u4ed6\u53ea\u6709\u72e0\u4e0b\u5fc3\uff0c\u5c06\u5979\u6253\u6655\uff0c\u6216\u8005\u70b9\u4f4f\u5979\u7684\u7a74\u9053\uff0c\u7136\u540e\u518d\u544a\u8bc9\u5979\uff0c\u8981\u5979\u597d\u597d \u7684\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u79cd\u573a\u9762\u4e00\u5b9a\u5f88\u60b2\u4f24\u7838\u611f\u4eba\u3002 \u3000\u3000\u4f46\u674e\u5bfb\u6b22\u5374\u4e0d\u5e0c\u671b\u5979\u8fd9\u6837\u505a\uff0c\u73b0\u5728\uff0c\u4ed6\u5fc3\u91cc\u7684\u8d1f\u62c5\u5df2\u591f\u91cd\uff0c\u5979\u82e5\u8fd9\u4e48\u6837\u505a\u4e86\uff0c\u4ed6\u7684\u60c5\u611f \u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u5d29\u6e83\u3002 \u3000\u3000\u4ed6\u7684\u6027\u683c\u867d\u575a\u5f3a\uff0c\u60c5\u611f\u5374\u5f88\u8106\u5f31\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5e76\u6ca1\u6709\u8fd9\u4e48\u6837\u505a\uff0c\u5979\u751a\u81f3\u6ca1\u6709\u8fc7\u6765\u548c\u674e\u5bfb\u6b22\u8bdd\u522b\u3002 \u3000\u3000\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u56de\u8fc7\u5934\uff0c\u77a7\u4e86\u5979\u4e00\u773c\u3002 \u3000\u3000\u5979\u5e76\u6ca1\u6709\u6655\u8fc7\u53bb\uff0c\u4e5f\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u5979\u4e5f\u6b63\u5728\u77a7\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u5979\u795e\u60c5\u867d\u60b2\u4f24\uff0c\u4f46\u76ee\u5149\u5374\u90a3\u4e48\u6e29\u67d4\uff0c\u90a3\u4e48\u575a\u5b9a\uff0c\u5979\u7684\u5634\u867d\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5979\u7684\u773c\u775b\u5374\u5728\u544a \u8bc9\u674e\u5bfb\u6b22\uff1a\u201c\u65e2\u7136\u8fd9\u662f\u4f60\u975e\u5fae\u4e0d\u53ef\u7684\u4e8b\uff0c\u4f60\u5c31\u53ea\u7ba1\u653e\u5fc3\u53bb\u505a\u5427\uff0c\u6211\u7edd\u4e0d\u4f1a\u62c9\u4f4f\u4f60\uff0c\u4e5f\u4e0d\u4f1a\u6253 \u6270\u4f60\uff0c\u65e0\u8bba\u4f60\u505a\u4ec0\u4e48\uff0c\u6211\u90fd\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u505a\u5f97\u5f88\u597d\uff0c\u505a\u5f97\u5f88\u5bf9\u3002\u201d \u3000\u3000\u867d\u7136\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u674e\u5bfb\u6b22\u7684\u5fc3\u60c5\u5c31\u5df2\u4e0d\u518d\u90a3\u4e48\u6c89\u91cd\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u660e\u767d\u5979\u662f\u4e2a\u575a\u5f3a\u7684\u5973\u4eba\uff0c\u7edd\u4e0d\u4f1a\u8981\u4ed6\u64cd\u5fc3\uff0c\u7528\u4e0d\u7740\u4ed6\u8bf4\uff0c\u5979\u4e5f\u4f1a\u597d\u597d\u7684\u6d3b\u4e0b\u53bb \u3002 \u3000\u3000\u5979\u5bf9\u4ed6\u53ea\u6709\u5b89\u6170\uff0c\u53ea\u6709\u9f13\u52b1\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u611f\u6fc0\uff0c\u56e0\u4e3a\u53ea\u6709\u4ed6\u81ea\u5df1\u624d\u77e5\u9053\u5979\u8fd9\u4e48\u505a\u5bf9\u4ed6\u7684\u5e2e\u52a9\u6709\u591a\u4e48\u5927\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u80fd\u9047\u7740\u8fd9\u6837\u7684\u4e00\u4e2a\u5973\u4eba\u5b9e\u5728\u662f\u8fd0\u6c14\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7ec8\u4e8e\u8d70\u4e86\uff0c\u8d70\u7684\u65f6\u5019\uff0c\u6b65\u5c65\u5df2\u8fdc\u6bd4\u6765\u7684\u65f6\u5019\u575a\u5b9a\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9759\u9759\u7684\u77a7\u7740\u4ed6\u8d70\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5c06\u76ee\u5149\u8f6c\u5230\u6797\u4ed9\u513f\u8eab\u4e0a\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6b63\u6323\u624e\u7740\u4ece\u6ce5\u6cde\u4e2d\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u5979\u5c3d\u529b\u60f3\u505a\u51fa\u9a84\u50b2\uff0c\u9ad8\u8d35\u7684\u6837\u5b50\uff0c\u4f46\u5979\u81ea\u5df1\u4e5f\u77e5\u9053\u65e0\u8bba\u600e\u4e48\u505a\u90fd\u662f\u6ca1\u6709\u7528\u7684\uff0c\u56e0\u4e3a\u5979\u81ea \u5df1\u4e5f\u89c9\u5f97\u81ea\u5df1\u5f88\u72fc\u72c8\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4ecd\u5728\u77a7\u7740\u5979\uff0c\u6ca1\u6709\u4e00\u70b9\u8868\u60c5\u3002 \u3000\u3000\u6ca1\u6709\u8868\u60c5\u5c31\u662f\u79cd\u8f7b\u8511\u7684\u8868\u60c5\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u770b\u4e0d\u8d77\u6211\uff0c\u53ef\u662f\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u66f4\u770b\u4e0d\u8d77\u4f60\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u5bb3\u4e86\u4f60\u7237\u7237\uff0c\u4e5f\u5bb3\u4e86\u674e\u5bfb\u6b22\uff0c\u4f46\u4f60\u5374\u53ea\u4e0d\u8fc7\u50cf\u4e2a\u6728\u5934\u4eba\u4f3c\u7684\u7ad9\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u5e94\u8be5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u5e94\u8be5\u77e5\u9053\u2026\u2026\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u81ea\u5df1\u505a\u9519\u4e86\u4e8b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u5e94\u8be5\u5fcf\u6094\uff0c\u5e94\u8be5\u96be\u53d7\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4e0d\u96be\u53d7\uff1f\u4e00\u4e2a\u4eba\u82e5\u662f\u771f\u89c9\u5f97\u5fcf\u6094\uff0c\u89c9\u5f97\u96be\u53d7\uff0c\u5e76\u4e0d\u8981\u7528\u5634\u6765 \u8bf4\u7684\uff0c\u8981\u7528\u884c\u52a8\u6765\u8868\u793a\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u8868\u793a\u4e86\u4ec0\u4e48\uff1f\u505a\u4e86\u4ec0\u4e48\uff1f\u201d\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u73b0\u5728\u6211\u80fd\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u660e\u77e5\u674e\u5bfb\u6b22\u8fd9\u4e00\u53bb\u5fc5\u6b7b\u65e0\u7591\uff0c\u81f3\u5c11\u5e94\u8be5\u62c9\u4f4f\u4ed6\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u80fd\u62c9\u5f97\u4f4f\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u82e5\u53bb\u62c9\u4ed6\uff0c\u53ea\u6709\u4f7f\u4ed6\u7684\u5fc3\u66f4\u4e71\uff0c\u6b7b\u5f97\u66f4\u5feb\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u53ef\u662f\u4f60\u2026\u2026\u4f60\u751a\u81f3\u8fde\u4e00\u6ef4\u773c\u6cea\u90fd\u6ca1\u6709\u6d41\u4e0b\u6765\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6c89\u9ed8\u4e86\u534a\u5929\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u7684\u786e\u60f3\u6d41\u6cea\uff0c\u60f3\u5927\u54ed\u4e00\u573a\uff0c\u4f46\u5374\u4e0d\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u660e\u5929\u2026\u2026\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f46\u660e\u5929\u8fd8\u6709\u660e\u5929\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6c38\u8fdc\u6709\u660e\u5929\uff0c\u6240\u4ee5\u6c38\u8fdc\u6709\u5e0c\u671b\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u6211\u867d\u7136\u505a\u9519\u4e86\uff0c\u4f46\u90a3\u5df2\u8fc7\u53bb\u4e86\uff0c\u6211\u7eb5\u7136\u5728\u6d41\u6cea\uff0c\u4e5f\u4e0d\u59a8\u7b49\u5230\u660e\u5929\uff0c \u56e0\u4e3a\u4eca\u5929\u6211\u8fd8\u6709\u522b\u7684\u4e8b\u8981\u505a\uff01\u201d \u3000\u3000\u53ea\u6709\u61e6\u592b\u548c\u5446\u5b50\u624d\u4f1a\u6c38\u8fdc\u4e3a\u201c\u6628\u5929\u201d\u7684\u4e8b\u800c\u6d41\u6cea\u3002 \u3000\u3000\u771f\u6b63\u6709\u52c7\u6c14\u627f\u8ba4\u81ea\u5df1\u9519\u8bef\u7684\u4eba\uff0c\u4e5f\u5c31\u4f1a\u540c\u6837\u6709\u52c7\u6c14\u9762\u5bf9\u73b0\u5b9e\uff0c\u7edd\u4e0d\u4f1a\u5c06\u81ea\u5df1\u57cb\u846c\u5728\u773c\u6cea \u91cc\u3002 \u3000\u3000\u773c\u6cea\u5e76\u4e0d\u80fd\u6d17\u6e05\u803b\u8fb1\uff0c\u66f4\u4e0d\u80fd\u5f25\u8865\u9519\u8bef\uff0c\u4f60\u82e5\u662f\u771f\u7684\u5fcf\u6094\u3002\u5c31\u5f97\u62ff\u51fa\u52c7\u6c14\u6765\uff0c\u4ece\u4eca\u5929\u4ece \u5934\u505a\u8d77\u3002 \u3000\u3000\u6797\u4ed9\u513f\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u6253\u51fb\u5b59\u5c0f\u7ea2\u3002\u56e0\u4e3a\u5979\u77e5\u9053\u5b59\u5c0f\u7ea2\u770b\u4e0d\u8d77\u5979\uff0c\u5979\u4e5f\u60f3\u8981\u5b59\u5c0f\u7ea2\u81ea \u5df1\u770b\u4e0d\u8d77\u81ea\u5df1\u3002 \u3000\u3000\u4f46\u5979\u5931\u8d25\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8fdc\u6bd4\u5979\u60f3\u8c61\u4e2d\u575a\u5f3a\uff0c\u8fdc\u6bd4\u5979\u60f3\u8c61\u4e2d\u6709\u52c7\u6c14\u3002 \u7b2c\u516b\u5341\u4e09\u7ae0\u3000\u4f1f\u5927\u7684\u7231>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u4e09\u7ae0\u3000\u4f1f\u5927\u7684\u7231 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u6797\u4ed9\u513f\u624d\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u4eca\u5929\u6709\u5f88\u591a\u4e8b\u8981\u505a\uff1f\u4f60\u505a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\u8981\u5e2e\u52a9\u5979\u7684\u7537\u4eba\uff0c\u5e76\u4e0d\u662f\u8981\u53bb\u966a\u4ed6\u6b7b\uff0c\u4e3a\u4ed6\u62fc\u547d\u3002\u800c\u662f\u8981\u9f13 \u52b1\u4ed6\uff0c\u5b89\u6170\u4ed6\uff0c\u8ba9\u4ed6\u80fd\u5b89\u5fc3\u53bb\u505a\u4ed6\u7684\u4e8b\uff0c\u8ba9\u4ed6\u80fd\u89c9\u5f97\u81ea\u5df1\u662f\u91cd\u8981\u7684\uff0c\u5e76\u6ca1\u6709\u88ab\u4eba\u5ffd\u89c6\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5df2\u591f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u53c8\u8fd8\u80fd\u4e3a\u4ed6\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5979\u4e0d\u5fc5\u518d\u505a\u4ec0\u4e48\u3002 \u3000\u3000\u8fd9\u5df2\u8db3\u591f\u3002 \u3000\u3000\u65e0\u8bba\u54ea\u4e2a\u7537\u4eba\u9047\u5230\u5979\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u90fd\u5e94\u8be5\u5341\u5206\u611f\u6fc0\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u5728\u60f3\u6cd5\u5b50\u6253\u51fb\u6211\uff0c\u4f46\u6211\u5e76\u4e0d\u602a\u4f60\uff0c\u56e0\u4e3a\u6211\u5ffd\u7136\u89c9\u5f97\u4f60\u5f88 \u53ef\u601c\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u601c\uff1f\u6211\u6709\u4ec0\u4e48\u597d\u53ef\u601c\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u81ea\u5df1\u5f88\u5e74\u8f7b\uff0c\u5f88\u7f8e\uff0c\u5f88\u806a\u660e\uff0c\u4ee5\u4e3a\u4e16\u4e0a\u7684\u7537\u4eba\u90fd\u4f1a\u62dc\u5012\u5728\u4f60\u811a\u4e0b\uff0c \u6240\u4ee5\u522b\u4eba\u771f\u5fc3\u7684\u5bf9\u4f60\u597d\uff0c\u4f60\u53cd\u800c\u770b\u4e0d\u8d77\u4ed6\uff0c\u8ba4\u4e3a\u4ed6\u662f\u5446\u5b50\uff0c\u53ef\u662f\u4f60\u603b\u6709\u4e00\u592d\u4f1a\u53d1\u73b0\uff0c\u4e16\u4e0a\u5bf9 \u4f60\u771f\u5fc3\u7684\u539f\u6765\u5e76\u6ca1\u6709\u4f60\u60f3\u8c61\u4e2d\u90a3\u4e48\u591a\uff0c\u771f\u60c5\u5e76\u4e0d\u662f\u7528\u9752\u6625\u548c\u7f8e\u8c8c\u5c31\u53ef\u4ee5\u4e70\u5230\u7684\u3002\u201d \u3000\u3000\u5979\u5e7d\u5e7d\u7684\u63a5\u7740\u9053\uff1a\u201c\u5230\u4e86\u90a3\u65f6\uff0c\u4f60\u5c31\u4f1a\u53d1\u73b0\u4f60\u539f\u6765\u4ec0\u4e48\u90fd\u6ca1\u6709\u5f97\u5230\uff0c\u4ec0\u4e48\u90fd\u662f\u7a7a\u7684\u2014\u2014 \u4e00\u4e2a\u5973\u4eba\u8981\u662f\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\u624d\u662f\u6700\u53ef\u601c\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8ba4\u4e3a\u6211\u73b0\u5728\u5df2\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff1f\u201d \u3000\u3000\u5979\u58f0\u97f3\u98a4\u6296\uff0c\u56e0\u4e3a\u5979\u5168\u8eab\u90fd\u5728\u53d1\u6296\uff0c\u4e5f\u4e0d\u77e5\u662f\u6c14\u6124\uff1f\u662f\u51b7\uff1f\u8fd8\u662f\u6050\u60e7\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u51b7\u51b7\u7684\u77a7\u7740\u5979\u8138\u4e0a\u7684\u4e4c\u9752\uff0c\u6ee1\u8eab\u7684\u6ce5\u6c61\uff0c\u8fd9\u5df2\u7ecf\u6bd4\u8bf4\u4efb\u4f55\u8bdd\u90fd\u8981 \u4ee4\u5979\u96be\u53d7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u7a81\u7136\u7b11\u4e86\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u770b\u4e0d\u8d77\u4ed6\uff0c\u6211\u4e00\u76f4\u628a\u4ed6\u5f53\u505a\u5446\u5b50\uff0c\u53ef\u662f\u6211\u73b0 \u5728\u8981\u53bb\u627e\u4ed6\uff0c\u4ed6\u8fd8\u662f\u4e00\u6837\u4f1a\u722c\u7740\u6765\u6c42\u6211\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u53bb\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u8bd5\u5c31\u77e5\u9053\uff0c\u6ca1\u6709\u6211\uff0c\u4ed6\u6839\u672c\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5979\u5634\u91cc\u867d\u5728\u8bf4\u4e0d\u5fc5\uff0c\u4f46\u4eba\u5df2\u8f6c\u8eab\u5954\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5979\u5954\u5f97\u90a3\u4e48\u5feb\uff0c\u5df2\u7528\u51fa\u4e86\u6240\u6709\u7684\u529b\u91cf\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u8fd9\u5df2\u662f\u5979\u6700\u540e\u4e00\u4e2a\u673a\u4f1a\uff0c\u8fd9\u673a\u4f1a\u82e5\u518d \u9519\u8fc7\uff0c\u5979\u624d\u771f\u7684\u6d3b\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7ad9\u5728\u90a3\u91cc\u6014\u4e86\u534a\u664c\uff0c\u624d\u7f13\u7f13\u8f6c\u8fc7\u5934\u3002 \u3000\u3000\u5927\u5730\u4e00\u7247\u9ed1\u6697\uff0c\u96fe\u4e00\u822c\u7684\u96e8\u4e1d\u4e2d\uff0c\u53c8\u51fa\u73b0\u4e86\u4e00\u6761\u4eba\u5f71\u2026\u2026 \u3000\u3000\u8fd9\u4eba\u4e5f\u4e0d\u77e5\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff0c\u4eff\u4f5b\u4e5f\u5df2\u5728\u8fd9\u91cc\u7b49\u5019\u4e86\u5f88\u4e45\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7b2c\u4e00\u773c\u5c31\u770b\u5230\u4e86\u5979\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u53cc\u773c\u775b\u5e76\u4e0d\u660e\u4eae\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6cea\u6d41\u5f97\u5927\u591a\uff0c\u6240\u4ee5\u76ee\u5149\u770b\u6765\u6709\u4e9b\u5446\u6ede\uff0c\u4f46\u5176\u4e2d\u542b\u8574\u7684\u90a3 \u79cd\u60b2\u54c0\u5e7d\u6028\u4e4b\u610f\uff0c\u8fde\u94c1\u77f3\u4eba\u770b\u4e5f\u8981\u52a8\u5fc3\u3002 \u3000\u3000\u7136\u540e\uff0c\u5b59\u5c0f\u7ea2\u5c31\u770b\u5230\u4e86\u5979\u7684\u8138\u3002 \u3000\u3000\u5979\u7684\u8138\u4e5f\u4e0d\u662f\u5b8c\u7f8e\u65e0\u6687\u7684\u3002 \u3000\u3000\u5979\u7684\u8138\u8272\u592a\u82cd\u767d\uff0c\u5c31\u50cf\u662f\u5df2\u6709\u5f88\u4e45\u5f88\u4e45\u672a\u66fe\u89c1\u5230\u9633\u5149\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5b59\u5c0f\u7ea2\u4ece\u7b2c\u4e00\u773c\u770b\u5230\u5979\uff0c\u5c31\u8ba4\u4e3a\u5979\u662f\u81ea\u5df1\u8fd9\u4e00\u751f\u4e2d\u6240\u89c1\u5230\u7684\u6700\u7f8e\u4e3d\u7684 \u5973\u4eba\u3002 \u3000\u3000\u5979\u7684\u5934\u53d1\u5df2\u51cc\u4e71\uff0c\u8863\u886b\u5df2\u6e7f\u900f\uff0c\u770b\u6765\u5f53\u7136\u4e5f\u5e94\u8be5\u5f88\u72fc\u72c8\uff0c\u5947\u602a\u7684\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u4f1a\u89c9\u5f97 \u5979\u72fc\u72c8\u3002 \u3000\u3000\u5979\u770b\u6765\u8fd8\u662f\u90a3\u4e48\u6e05\u4e3d\uff0c\u90a3\u4e48\u9ad8\u8d35\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4efb\u4f55\u60c5\u51b5\u4e0b\uff0c\u5979\u90fd\u80fd\u4ee4\u4eba\u611f\u89c9\u5230\u5979\u90a3\u79cd\u72ec\u7279\u7684\u6c14\u8d28\uff0c\u72ec\u7279\u7684\u8d3c\u529b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4ee5\u524d\u5e76\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4f46\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u5df2\u731c\u51fa\u5979\u662f\u8c01\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\uff01 \u3000\u3000\u53ea\u6709\u5979\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u624d\u80fd\u4ee4\u674e\u5bfb\u6b22\u90a3\u6837\u7684\u7537\u4eba\u98a0\u5012\u7ec8\u751f\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fc3\u91cc\u5728\u53f9\u606f\uff01 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u522b\u4eba\u90fd\u8981\u8bf4\u6797\u4ed9\u513f\u662f\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u7f8e\u4eba\uff0c\u7b2c\u4e00\u7f8e\u4eba\u5e94\u8be5\u662f\u5979\u624d\u5bf9\uff0c\u83ab\u8bf4\u5979\u5e74\u7eaa\u8f7b \u7684\u65f6\u5019\uff0c\u5c31\u662f\u73b0\u5728\uff0c\u5979\u8fd8\u6bd4\u6797\u4ed9\u513f\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u5979\u8fd9\u4e48\u60f3\uff0c\u4e5f\u8bb8\u56e0\u4e3a\u73b0\u5728\u662f\u96e8\u591c\uff0c\u4e5f\u8bb8\u56e0\u4e3a\u5979\u662f\u5973\u4eba\u3002 \u3000\u3000\u5973\u4eba\u770b\u5973\u4eba\u7684\u773c\u5149\uff0c\u603b\u548c\u7537\u4eba\u4e0d\u540c\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u4e5f\u5728\u770b\u7740\u5979\uff0c\u6b63\u6162\u6162\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5c31\u662f\u5b59\u59d1\u5a18\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u70b9\u4e86\u70b9\u5934\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\uff0c\u6211\u5e38\u5e38\u542c\u4ed6\u8bf4\u8d77\u4f60\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u7b11\u4e86\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\u3002 \u3000\u3000\u5979\u5f53\u7136\u77e5\u9053\u5b59\u5c0f\u7ea2\u8bf4\u7684\u201c\u4ed6\u201d\u662f\u8c01\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u4e5f\u65e9\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4ed6\u8981\u5728\u8fd9\u91cc\u51b3\u6597\uff0c\u672c\u6765\u60f3\u8d76\u6765\u8ddf\u4ed6\u8bf4\u51e0\u53e5\u8bdd\u7684\uff0c\u53ef\u662f\uff0c\u6211 \u5df2\u6709\u5f88\u591a\u5e74\u6ca1\u6709\u51fa\u8fc7\u95e8\uff0c\u5df2\u7ecf\u8fde\u8def\u90fd\u4e0d\u8ba4\u8bc6\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u53c8\u9eef\u7136\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4f46\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u8981\u5bf9\u4ed6\u8bf4\u7684\u8bdd\uff0c\u8ddf\u4f60\u8bf4\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u8f7b\uff0c\u5f88\u60e8\uff0c\u4eff\u4f5b\u6bcf\u8bf4\u4e00\u53e5\u8bdd\uff0c\u90fd\u8981\u5148\u8003\u8651\u5f88\u4e45\u3002 \u3000\u3000\u5979\u65e0\u8bba\u8bf4\u4ec0\u4e48\u90fd\u662f\u6e05\u6e05\u7684\uff0c\u6de1\u6de1\u7684\uff0c\u8981\u662f\u522b\u4eba\u542c\u4e86\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u5979\u662f\u4e2a\u5f88\u51b7\u6f20\uff0c\u5f88\u65e0\u60c5\u7684 \u5973\u4eba\u3002 \u3000\u3000\u4f46\u5b59\u5c0f\u7ea2\u5374\u5f88\u4e86\u89e3\u5979\uff0c\u5979\u80fd\u591f\u8bf4\u51fa\u8fd9\u79cd\u51b7\u6f20\u6e05\u6de1\u7684\u8bdd\u6765\uff0c\u90a3\u53ea\u56e0\u5979\u5df2\u75db\u82e6\u5f97\u592a\u591a\uff0c\u6240\u53d7 \u7684\u6298\u78e8\u4e5f\u592a\u591a\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fc3\u91cc\u53ea\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u540c\u60c5\u548c\u4f36\u501f\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4e5f\u60f3\u89c1\u4f60\uff0c\u4f60\u65e2\u7136\u6765\u4e86 \uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u8ddf\u4ed6\u89c1\u9762\u5462\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5979\u672c\u6765\u662f\u60f3\u548c\u674e\u5bfb\u6b22\u89c1\u9762\u7684\uff0c\u4f46\u5979\u6765\u7684\u65f6\u5019\uff0c\u5df2\u6709\u522b\u4eba\u5728\u65c1\u8fb9\uff0c\u6240\u4ee5\u5979\u624d\u4e0d\u6562\u73b0\u8eab\uff0c\u56e0 \u4e3a\u5979\u6015\u522b\u4eba\u770b\u7834\u5979\u548c\u674e\u5bfb\u6b22\u4e4b\u95f4\u7684\u60c5\u611f\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u81ea\u5df1\u8981\u662f\u548c\u674e\u5bfb\u6b22\u89c1\u4e86\u9762\uff0c\u81ea\u5df1\u5c31\u518d\u4e5f\u4e0d\u80fd\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u4e9b\u8bdd\u5979\u7eb5\u7136\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u5b59\u5c0f\u7ea2\u4e5f\u5f88\u4e86\u89e3\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u9053\uff1a\u201c\u4ee5\u524d\u6211\u603b\u4e0d\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u6709\u4e9b\u4eba\u603b\u8981\u542c\u522b\u4eba\u7684\u6446\u5e03\uff0c\u8ba9\u522b\u4eba\u6539\u53d8\u81ea\u5df1\u7684 \u547d\u8fd0\uff1f\u73b0\u5728\u6211\u624d\u660e\u767d\uff0c\u4f60\u542c\u522b\u4eba\u7684\u8bdd\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u4f60\u6015\u4ed6\uff0c\u800c\u662f\u56e0\u4e3a\u4f60\u7231\u4ed6\uff0c\u4f60\u77e5\u9053\u4ed6\u65e0\u8bba \u505a\u4ec0\u4e48\u90fd\u662f\u4e3a\u4e86\u4f60\u597d\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u672c\u6765\u4e00\u76f4\u5728\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4f46\u73b0\u5728\uff0c\u5979\u5374\u518d\u4e5f\u63a7\u5236\u4e0d\u4f4f\u3002 \u3000\u3000\u5979\u7684\u6cea\u5df2\u6d8c\u6cc9\u822c\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u5b59\u5c0f\u7ea2\u7684\u8fd9\u4e9b\u8bdd\uff0c\u6bcf\u4e2a\u5b57\u90fd\u8bf4\u5230\u5979\u5fc3\u91cc\u53bb\uff0c\u6bcf\u4e2a\u5b57\u90fd\u50cf\u662f\u4e00\u6839\u9488\uff0c\u523a\u5f97\u5979\u5fc3\u75bc\u3002 \u3000\u3000\u5979\u66fe\u7ecf\u95ee\u8fc7\u81ea\u5df1\uff1a\u201c\u73b0\u5728\u6211\u4ec0\u4e48\u90fd\u6ca1\u6709\u5f97\u5230\uff0c\u4ec0\u4e48\u90fd\u662f\u7a7a\u7684\uff0c\u6b63\u5982\u6797\u4ed9\u513f\u4e00\u6837\uff0c\u4f46\u8fd9\u60c5 \u51b5\u662f\u8c01\u9020\u6210\u7684\u5462\uff1f\u96be\u9053\u662f\u6211\u7684\u9519\u4e48\uff1f\u201d \u3000\u3000\u5979\u66fe\u7ecf\u57cb\u6028\u8fc7\u674e\u5bfb\u6b22\uff0c\u6068\u8fc7\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u8fd9\u79cd\u60b2\u60e8\u7684\u7ed3\u5c40\uff0c\u5c82\u975e\u6b63\u662f\u674e\u5bfb\u6b22\u6240\u9020\u6210\u7684\uff1f \u3000\u3000\u4f46\u73b0\u5728\u5979\u5374\u77e5\u9053\u9519\u7684\u5e76\u4e0d\u662f\u674e\u5bfb\u6b22\uff0c\u800c\u662f\u5979\u81ea\u5df1\u3002 \u3000\u3000\u201c\u90a3\u65f6\u6211\u4e3a\u4ec0\u4e48\u8981\u542c\u4ed6\u7684\u8bdd\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u660e\u660e\u767d\u767d\u7684\u544a\u8bc9\u4ed6\uff0c\u6211\u662f\u7231\u4ed6\u7684\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c \u6211\u8c01\u4e5f\u4e0d\u5ac1\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u67d4\u58f0\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u592a\u6e05\u695a\u4f60\u4eec\u4e4b\u95f4\u7684\u4e8b\uff0c\u53ef\u662f\u6211\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u6797\u8bd7\u97f3\u5ffd\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e5f\u5df2\u77e5\u9053\uff0c\u6211\u770b\u5230\u4f60\uff0c\u624d\u77e5\u9053\u6211\u9519\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6115\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u6211\u8981\u662f\u4e5f\u548c\u4f60\u4e00\u6837\u6709\u52c7\u6c14\uff0c\u548c\u4f60\u4e00\u6837\u575a\u5f3a\uff0c\u4eca\u5929\u5c31\u4e0d\u4f1a\u6709\u8fd9\u6837\u7684 \u7ed3\u5c40\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u6211\u672c\u5c31\u4e0d\u914d\u505a\u4ed6\u7684\u59bb\u5b50\uff0c\u53ea\u6709\u4f60\u624d\u914d\u5f97\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u6797\u8bd7\u97f3\u6839\u672c\u4e0d\u8ba9\u5979\u8bf4\u8bdd\uff0c\u53c8\u9053\uff1a\u201c\u56e0\u4e3a\u53ea\u6709\u4f60\u624d\u80fd\u5b89\u6170\u4ed6\uff0c\u9f13\u52b1\u4ed6\uff0c\u65e0\u8bba\u4ed6\u505a\u4ec0\u4e48\uff0c\u4f60 \u5bf9\u4ed6\u7684\u4fe1\u5fc3\u90fd\u4e0d\u4f1a\u6539\u53d8\uff0c\u800c\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u9eef\u7136\u53f9\u606f\uff0c\u773c\u6cea\u53c8\u6d41\u4e0b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u7740\u5934\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u4f60\u4ee5\u540e\u8fd8\u662f\u6709\u673a\u4f1a\u89c1\u7740\u4ed6\u7684\uff0c\u4ee5\u524d\u7684 \u4e8b\u90fd\u5df2\u8fc7\u53bb\uff0c\u4ee5\u540e\u4f60\u4eec\u8fd8\u662f\u53ef\u4ee5\u2026\u2026\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u53c8\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u8fd8\u6709\u673a\u4f1a\uff1f\u8fd8\u6709\u5e0c\u671b\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u6709\uff01\u201d \u3000\u3000\u5979\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u522b\u4eba\u770b\u4ed6\u90a3\u6837\u5b50\uff0c\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u4ed6\u5bf9\u81ea\u5df1\u5df2\u5168\u65e0\u4fe1\u5fc3\uff0c\u4e00\u4e2a\u4eba\u82e5\u8fde\u81ea \u5df1\u90fd\u5bf9\u81ea\u5df1\u5931\u5374\u4e86\u4fe1\u5fc3\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u5e0c\u671b\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9eef\u7136\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f46\u6211\u5374\u77e5\u9053\uff0c\u4ed6\u505a\u51fa\u90a3\u6837\u5b50\u6765\uff0c\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u6545\u610f\u8981\u4e0a\u5b98\u91d1\u8679\u8f7b\u89c6\u4ed6\uff0c\u4e0a \u5b98\u91d1\u8679\u82e5\u6709\u4e86\u8f7b\u654c\u4e4b\u5fc3\uff0c\u5c31\u96be\u514d\u6709\u758f\u5ffd\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u91cc\u95ea\u7740\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u8981\u4e0a\u5b98\u91d1\u8679\u4e00\u6709\u758f\u5ffd\uff0c\u4ed6\u5c31\u80fd\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u6797\u8bd7\u97f3\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u5bf9\u81ea\u5df1\u6709\u4fe1\u5fc3\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u77e5\u9053\u4f60\u5bf9\u4ed6\u6709\u4fe1\u5fc3\uff0c\u4f60\u5bf9\u4ed6\u7684\u5e2e \u52a9\u6709\u591a\u4e48\u5927\uff0c\u4e5f\u8bb8\u8fde\u4f60\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5782\u4e0b\u5934\uff0c\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u4e0d\u4f46\u5bf9\u674e\u5bfb\u6b22\u6709\u4fe1\u5fc3\uff0c\u5bf9\u81ea\u5df1\u4e5f\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u77a7\u7740\u5979\uff0c\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u4e5f\u4e0d\u77e5\u662f\u7fa1\u6155\uff1f\u662f\u9178\u695a\uff1f\u662f\u4e3a\u81ea\u5df1\u96be \u53d7\uff1f\u8fd8\u662f\u5728\u4e3a\u674e\u5bfb\u6b22\u9ad8\u5174\u3002 \u3000\u3000\u5b6a\u5bfb\u6b22\u534a\u751f\u6f66\u5012\uff0c\u5fc3\u529b\u4ea4\u7601\uff0c\u4e5f\u5b9e\u5728\u53ea\u6709\u5b59\u5c0f\u7ea2\u8fd9\u6837\u7684\u5973\u4eba\u624d\u80fd\u5b89\u6170\u4ed6\uff0c\u5426\u5219\u4ed6\u8fd9\u6b21\u7eb5 \u80fd\u6218\u80dc\uff0c\u4ee5\u540e\u8fd8\u662f\u8981\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u7eb5\u7136\u6ca1\u6709\u522b\u4eba\u80fd\u51fb\u5012\u4ed6\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4f1a\u5c06\u81ea\u5df1\u51fb\u5012\u7684\uff01 \u3000\u3000\u6797\u8bd7\u97f3\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u4ed6\u80fd\u9047\u5230\u4f60\uff0c\u4e5f\u8bb8\u6b63\u662f\u4e0a\u5929\u5bf9\u4ed6\u7684\u8865\u8d4f\uff0c\u8fd9\u672c\u662f\u4ed6\u5e94\u5f97\u7684\uff0c\u53ef \u662f\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u8346\u65e0\u547d\u5462\uff1f\u4ed6\u5c31\u7b97\u80fd\u51fb\u8d25\u4e0a\u5b98\u91d1\u8679\uff0c\u5374\u65e0\u8bba\u5982\u4f55\u4ed6\u4e0d\u80fd\u62b5\u6321\u4ed6\u4eec\u4e24\u4e2a\u4eba \u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u8346\u65e0\u547d\u4e5f\u8bb8\u4e0d\u4f1a\u51fa\u624b\uff0c\u56e0\u4e3a\u4e0a\u5b98\u91d1\u8679\u65e2\u7136\u81ea\u89c9\u6709\u5fc5\u80dc\u7684\u628a\u63e1\uff0c\u5c31 \u6839\u672c\u4e0d\u7528\u4ed6\u51fa\u624b\uff0c\u90a3\u4e48\uff0c\u7b49\u4ed6\u60f3\u51fa\u624b\u65f6\uff0c\u5c31\u5df2\u592a\u8fdf\u4e86\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u674e\u5bfb\u6b22\u552f\u4e00\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u4eec\u8981\u51fb\u5012\u674e\u5bfb\u6b22\uff0c\u4e5f\u53ea\u6709\u4e00\u6b21\u673a\u4f1a\u2014\u2014\u5c0f\u674e\u98de\u5200\u7edd\u4e0d\u4f1a\u7ed9\u4efb\u4f55\u4eba\u7b2c\u4e8c\u6b21\u673a\u4f1a\u3002 \u3000\u3000\u95ee\u9898\u662f\uff0c\u8c01\u80fd\u628a\u63e1\u4f4f\u8fd9\u4e00\u6b21\u673a\u4f1a\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8346\u65e0\u547d\u82e5\u4e0d\u51fa\u624b\uff0c\u4ed6\u624d\u6709\u673a\u4f1a\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u786e\u5b9a\u8346\u65e0\u547d\u4e0d\u51fa\u624b\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5979\u5f88\u5feb\u7684\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u6211\u5374\u80fd\u786e\u5b9a\uff0c\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4ed6\u4eec\u8c01\u90fd\u4e0d\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u5728\u4e00\u4e2a\u65f6\u8fb0\u5185\uff0c\u4e5f\u4e0d\u4f1a\u6709\u5947\u8ff9\u51fa\u73b0\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f1a\u6709\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u8ff9\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u963f\u98de\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u867d\u7136\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u4f46\u8868\u60c5\u5374\u5f88\u5931\u671b\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u5df2\u5bf9\u963f\u98de\u5931\u671b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u8ba4\u4e3a\u963f\u98de\u5df2\u4e0d\u884c\u4e86\uff0c\u90a3\u53ea\u56e0\u4ed6\u8eab\u4e0a\u80cc\u4e86\u526f\u67b7\u9501\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u67b7\u9501\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u55ef\uff0c\u67b7\u9501\uff0c\u4ed6\u7684\u67b7\u9501\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u89e3\u5f00\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u89e3\u94c3\u8fd8\u9700\u7cfb\u94c3\u4eba\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u2026\u6797\u4ed9\u513f\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7b49\u4ed6\u771f\u6b63\u53d1\u73b0\u6797\u4ed9\u513f\u5e76\u4e0d\u503c\u5f97\u4ed6\u7231\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u67b7\u9501\u5c31\u89e3\u5f00\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e5f\u8bb8\u4e0d\u9519\uff0c\u4ed6\u5df1\u5815\u843d\u5f88\u4e45\uff0c\u53c8\u600e\u80fd\u5728\u77ed\u77ed\u4e00\u4e2a\u65f6\u8fb0\u4e2d \u632f\u4f5c\u8d77\u6765\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4e86\u522b\u7684\u539f\u56e0\uff0c\u4ed6\u5f53\u7136\u4e0d\u80fd\uff0c\u4f46\u4e3a\u4e86\u674e\u5bfb\u6b22\uff0c\u4ed6\u4e5f\u8bb8\u80fd\u7684\u3002\u201d \u3000\u3000\u5979\u7f13\u7f13\u63a5\u7740\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e3a\u4e86\u4ed6\u81ea\u5df1\u6240\u7231\u7684\u4eba\uff0c\u5f80\u5f80\u5c31\u80fd\u505a\u51fa\u8bb8\u591a\u4ed6\u5e73\u65e5\u505a\u4e0d\u5230\u7684\u4e8b\u3002 \u201d \u3000\u3000\u6797\u8bd7\u97f3\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\u6211\u73b0\u5728\u8981\u53bb\u627e\u963f\u98de\uff0c\u5c06\u8fd9\u79cd\u60c5\u5f62\u544a\u8bc9\u4ed6\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u7b49\u4e00\u7b49\uff0c\u6211\u2026\u2026\u6211\u8fd8\u6709\u4e9b\u8bdd\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u5728\u542c\u7740\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u6211\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u5230\u5916\u9762\u6765\u8d70\u52a8\uff0c\u4f46\u5916\u9762\u8fd9\u4e9b\u4eba\u7684\u4e8b\u6211\u90fd\u77e5\u9053\u5f97\u5f88\u6e05\u695a\uff0c\u4f60 \u4e0d\u89c9\u5f97\u5947\u602a\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u5947\u602a\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u6709\u4e2a\u5f88\u806a\u660e\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u53c8\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u603b\u662f\u6211\u7684\u513f\u5b50\uff0c\u6211\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u4ed6\uff0c\u6240\u4ee5\u2026 \u2026\u6211\u5e0c\u671b\u4f60\u8f6c\u544a\u4ed6\uff0c\u8981\u4ed6\u539f\u8c05\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u9053\uff1a\u201c\u4ed6\u4ece\u6ca1\u6709\u6068\u8fc7\u4efb\u4f55\u4eba\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u6c89\u541f\u7740\uff0c\u4eff\u4f5b\u6709\u4e9b\u8bdd\u4e0d\u77e5\u9053\u600e\u4e48\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u544a\u8bc9\u4ed6\u300a\u601c\u82b1\u5b9d\u9274\u300b\u7684\u4e8b\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u6709\u4e9b\u60ca\u8bb6\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4f60\u4e5f\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u672c\u5c31\u662f\u6211\u544a\u8bc9\u4ed6\u7684\uff0c\u6211\u4e8c\u53d4\u2026\u2026\u201d \u3000\u3000\u6797\u8bd7\u97f3\u604d\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u738b\u8001\u524d\u8f88\u6765\u7684\u65f6\u5019\uff0c\u5b59\u4e8c\u5148\u751f\u4e5f\u5728\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u672c\u601c\u82b1\u5b9d\u9274\u7684\u786e\u662f\u5728\u4f60\u624b\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u662f\u7684\uff0c\u4f46\u6211\u5374\u4e00\u76f4\u6ca1\u6709\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4ed6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65f6\u6211\u89c9\u5f97\u6b66\u529f\u975e\u4f46\u5bf9\u4ed6\u6ca1\u6709\u4efb\u4f55\u5e2e\u52a9\u3002\u53cd\u800c\u5bb3\u4e86\u4ed6\uff0c\u4ed6\u7684\u6b66\u529f\u8d8a\u9ad8 \uff0c\u9ebb\u70e6\u4e5f\u8d8a\u591a\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u624d\u5c06\u4ed6\u7792\u4f4f\uff0c\u56e0\u4e3a\u4f60\u53ea\u8981\u4ed6\u505a\u4e00\u4e2a\u5e73\u5e73\u51e1\u51e1\u7684\u4eba\uff0c\u5e73\u5e73\u51e1\u51e1\u7684\u8fc7\u4e00 \u751f\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u51c4\u7136\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u6700\u5927\u7684\u539f\u56e0\uff0c\u522b\u4eba\u4e5f\u8bb8\u4e0d\u4f1a\u76f8\u4fe1\u7684\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u9053\uff1a\u201c\u6211\u82e5\u662f\u4f60\uff0c\u505a\u6cd5\u53ea\u6015\u4e5f\u4f1a\u548c\u4f60\u4e00\u6837\u3002\u201d \u3000\u3000\u53ea\u6709\u5973\u4eba\u624d\u4e86\u89e3\u5973\u4eba\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u53ea\u6709\u5973\u4eba\u624d\u77e5\u9053\u4e00\u4e2a\u5c11\u5973\u4e3a\u4e86\u5979\u6240\u7231\u7684\u7537\u4eba\uff0c\u662f\u65e0\u8bba\u4ec0\u4e48\u90fd\u505a\u5f97\u51fa\u7684\uff0c\u5728\u522b\u4eba\u773c\u4e2d\u770b\u6765 \uff0c\u5979\u6240\u505a\u7684\u4e8b\u4e5f\u8bb8\u5f88\u53ef\u7b11\uff0c\u4f46\u5728\u5979\u4eec\u81ea\u5df1\u770b\u6765\uff0c\u4e16\u4e0a\u6240\u6709\u7684\u539f\u56e0\u90fd\u6ca1\u6709\u8fd9\u4e00\u70b9\u91cd\u8981\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f46\u73b0\u5728\u6211\u5374\u5f88\u540e\u6094\uff0c\u89c9\u5f97\u4e0d\u5e94\u8be5\u7792\u7740\u4ed6\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u7792\u7740\u4ed6\uff0c\u4e5f\u662f\u4e3a\u4ed6\u597d\uff0c\u6709\u4ec0\u4e48\u4e0d\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u4ed6\u82e5\u7ec3\u4e86\u300a\u601c\u82b1\u5b9d\u9274\u300b\u4e0a\u7684\u6b66\u529f\uff0c\u4eca\u5929\u4e0a\u5b98\u91d1\u8679\u548c\u8346\u65e0\u547d\u7eb5\u7136\u8981 \u8054\u624b\u5bf9\u4ed8\u4ed6\uff0c\u4e5f\u6ca1\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u89c9\u5f97\u5f88\u5185\u759a\uff0c\u5e0c\u671b\u4ed6\u80fd\u539f\u8c05\u4f60\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u70b9\u4e86\u70b9\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4ed6\u65e0\u8bba\u5982\u4f55\u90fd\u4e0d\u4f1a\u602a\u6211\uff0c\u53ef\u662f\u6211\u2026\u2026\u6211\u82e5\u4e0d\u5c06\u8fd9 \u4e8b\u4ef6\u8bf4\u51fa\u6765\uff0c\u5fc3\u91cc\u5c31\u66f4\u96be\u53d7\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f46\u4f60\u5374\u9519\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u6211\u9519\u4e86\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u82e5\u7ec3\u4e86\u300a\u601c\u82b1\u5b9d\u9274\u300b\u4e0a\u7684\u6b66\u529f\uff0c\u4e5f\u8bb8\u66f4\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u963f\u98de\u7684\u5251\u4e3a\u4ec0\u4e48\u53ef\u6015\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5feb\uff0c\u6bd4\u4efb\u4f55\u4eba\u90fd\u5feb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u80fd\u6bd4\u522b\u4eba\u5feb\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u2026\u2026\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u5feb\uff0c\u53ea\u56e0\u4e3a\u4ed6\u6bd4\u522b\u4eba\u4e13\u5fc3\uff0c\u2018\u5c0f\u674e\u98de\u5200\u2019\u4e5f\u4e00\u6837\uff0c\u4ed6\u82e5\u662f\u7ec3\u4e86\u522b\u7684\u6b66\u529f \uff0c\u53cd\u800c\u4f1a\u5206\u5fc3\uff0c\u4e5f\u8bb8\u5c31\u4e0d\u80fd\u8fd9\u4e48\u5feb\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u5782\u7740\u5934\uff0c\u60f3\u4e86\u5f88\u4e45\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u662f\u5e0c\u671b\u80fd\u5c06\u6211\u7684\u610f\u601d\u544a\u8bc9\u4ed6\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4ee5\u540e\u8fd8\u6709\u89c1\u9762\u7684\u673a\u4f1a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u544a\u8bc9\u4ed6\uff1f\u201d \u7b2c\u516b\u5341\u56db\u7ae0\u3000\u5ffd\u7136\u60f3\u901a\u4e86>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u56db\u7ae0\u3000\u5ffd\u7136\u60f3\u901a\u4e86 \u3000\u3000\u6797\u8bd7\u97f3\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u62ac\u8d77\u5934\u3002 \u3000\u3000\u5979\u8138\u4e0a\u7684\u795e\u8272\u5ffd\u7136\u53d8\u5f97\u5f88\u5e73\u9759\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u6211\u4eec\u4e5f\u8bb8\u6ca1\u6709\u89c1\u9762\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u5c31\u8981\u5230\u4e00\u4e2a\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e00\u5b9a\u8981\u53bb\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4e00\u5b9a\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u4e0b\u4e86\u51b3\u5fc3\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u8bf4\u4e0d\u51fa\u8bdd\u4e86\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u51c4\u7136\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u6700\u5927\u7684\u5f31\u70b9\uff0c\u5c31\u662f\u6211\u505a\u4e8b\u4ece\u6765\u6ca1\u6709\u51b3\u5fc3\uff0c\u8fd9\u4e5f \u8bb8\u662f\u6211\u7b2c\u4e00\u6b21\u4e0b\u51b3\u5fc3\uff0c\u6211\u4e0d\u5e0c\u671b\u6709\u4eba\u518d\u60f3\u6765\u8981\u6211\u6539\u53d8\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u4eec\u624d\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u73b0\u5728\u8bf4\u8bdd\u7684\u65f6\u5019\u4e5f\u4e0d\u591a\u4e86\uff0c\u4f60\u603b\u8be5\u8ba9\u6211 \u518d\u89c1\u4f60\u4e00\u6b21\uff0c\u6211\u4e5f\u6709\u5f88\u591a\u6d3b\u8981\u5bf9\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u597d\uff0c\u660e\u5929\u6211\u5c31\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u660e\u5929\u65e9\u4e0a\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u4e5f\u8d70\u4e86\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5929\u5730\u95f4\u4eff\u4f5b\u5c31\u53ea\u5269\u4e0b\u5b59\u5c0f\u7ea2\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5979\u4e00\u76f4\u6ca1\u6709\u6d41\u6cea\uff0c\u4f46\u73b0\u5728\uff0c\u5979\u773c\u775b\u5374\u7a81\u7136\u6cc9\u6c34\u822c\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u4e5f\u4e0b\u4e86\u51b3\u5fc3\u3002 \u3000\u3000\u53ea\u8981\u674e\u5bfb\u6b22\u4e0d\u6b7b\uff0c\u5979\u4e00\u5b9a\u8981\u5c06\u4ed6\u5e26\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u81ea\u4ece\u5979\u7b2c\u4e00\u6b21\u770b\u5230\u674e\u5bfb\u6b22\uff0c\u5979\u5c31\u51b3\u5fc3\u8981\u5c06\u81ea\u5df1\u8fd9\u4e00\u751f\u4ea4\u7ed9\u4ed6\u3002 \u3000\u3000\u8fd9\u51b3\u5fc3\u5979\u4ece\u672a\u6539\u53d8\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u5374\u89c9\u5f97\u81ea\u5df1\u5927\u81ea\u79c1\uff0c\u5979\u51b3\u5fc3\u8981\u727a\u7272\u81ea\u5df1\uff01 \u3000\u3000\u56e0\u4e3a\u5979\u5ffd\u7136\u89c9\u5f97\u6797\u8bd7\u97f3\u6bd4\u5979\u66f4\u9700\u8981\u674e\u5bfb\u6b22\uff01 \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u5df2\u53d7\u4e86\u5927\u591a\u82e6\uff0c\u90fd\u6bd4\u6211\u66f4\u6709\u6743\u529b\u4eab\u53d7\u4eba\u751f\uff0c\u6211\u65e0\u8bba\u7528\u4ec0\u4e48\u6cd5\u5b50\uff0c\u90fd\u8981\u5c06\u4ed6\u4eec\u62e2 \u5408\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5979\u672c\u5c31\u5c5e\u4e8e\u4ed6\u7684\uff0c\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u4e0d\u8be5\u62c6\u6563\u4ed6\u4eec\u3002 \u3000\u3000\u201c\u9f99\u5578\u4e91\u4e0d\u80fd\uff0c\u4ed6\u6839\u672c\u4e0d\u914d\uff01\u201d \u3000\u3000\u201c\u81f3\u4e8e\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u51b3\u5fc3\u4e0d\u60f3\u81ea\u5df1\uff0c\u54ac\u7740\u5634\u5507\uff0c\u64e6\u5e72\u4e86\u773c\u6cea\uff0c\u201c\u5c31\u7b97\u8981\u6d41\u6cea\uff0c\u4e5f\u5f97\u7559\u5230\u660e\u5929\uff0c\u4eca\u5929\u6211\u8fd8\u6709 \u8bb8\u591a\u4e8b\u8981\u505a\u2026\u2026\u201d \u3000\u3000\u5979\u62ac\u8d77\u5934\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u73b0\u5728\u7684\u786e\u5f88\u9ed1\u6697\uff0c\u56e0\u4e3a\u591c\u5df2\u66f4\u6df1\u3002 \u3000\u3000\u4f46\u9ed1\u591c\u5373\u6765\u4e86\uff0c\u5149\u660e\u8fd8\u4f1a\u8fdc\u4e48\uff1f \u3000\u3000\u6709\u4e9b\u4eba\u8ba4\u4e3a\u4e16\u4e0a\u53ea\u6709\u4e24\u79cd\u4eba\uff0c\u4e00\u79cd\u597d\u4eba\uff0c\u4e00\u79cd\u574f\u4eba\u3002 \u3000\u3000\u7537\u4eba\u5982\u6b64\uff0c\u5973\u4eba\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5f53\u7136\u662f\u5c5e\u4e8e\u574f\u4eba\u90a3\u4e00\u7c7b\uff0c\u4f46\u6797\u8bd7\u97f3\u548c\u5b59\u5c0f\u7ea2\u5462\uff1f \u3000\u3000\u5979\u4eec\u5f53\u7136\u90fd\u662f\u597d\u4eba\uff0c\u4f46\u5979\u4eec\u4e5f\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u65e0\u8bba\u662f\u4ec0\u4e48\u4e8b\uff0c\u6797\u8bd7\u97f3\u603b\u662f\u5fcd\u53d7\u3001\u5fcd\u53d7\u2026\u2026 \u3000\u3000\u5979\u8ba4\u4e3a\u5973\u4eba\u6700\u5927\u7684\u7f8e\u5fb7\u5c31\u662f\u201c\u5fcd\u53d7\u201d\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u5374\u4e0d\u540c\uff0c\u5979\u8981\u53cd\u6297\uff01 \u3000\u3000\u53ea\u8981\u5979\u8ba4\u4e3a\u662f\u9519\u7684\uff0c\u5979\u5c31\u53cd\u6297\uff01 \u3000\u3000\u5979\u575a\u5b9a\u3001\u660e\u6717\u3001\u6709\u52c7\u6c14\u3001\u6709\u4fe1\u5fc3\u3001\u5979\u6562\u7231\u3001\u4e5f\u6562\u6068\uff0c\u4f60\u5728\u5979\u8eab\u4e0a\uff0c\u6c38\u8fdc\u770b\u4e0d\u5230\u9ed1\u6697\u7684\u4e00 \u9762\uff01 \u3000\u3000\u5c31\u56e0\u4e3a\u4e16\u4e0a\u8fd8\u6709\u5979\u8fd9\u79cd\u5973\u4eba\uff0c\u6240\u4ee5\u4eba\u7c7b\u624d\u80fd\u4e0d\u65ad\u8fdb\u6b65\uff0c\u7ee7\u7eed\u751f\u5b58\u3002 \u3000\u3000\u201c\u6c38\u6052\u7684\u5973\u6027\uff0c\u5f15\u5bfc\u4eba\u7c7b\u4e0a\u5347\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u6b63\u662f\u4e3a\u5979\u8fd9\u79cd\u5973\u4eba\u8bf4\u7684\u3002 \u3000\u3000\u201c\u53ea\u8981\u6211\u53bb\u627e\u4ed6\uff0c\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\uff0c\u4ed6\u8fd8\u662f\u4f1a\u722c\u7740\u6765\u6c42\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u6211\uff0c\u4ed6\u6839\u672c\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u771f\u7684\u8fd9\u4e48\u6709\u628a\u63e1\uff1f \u3000\u3000\u5979\u7684\u786e\u6709\u628a\u63e1\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u963f\u98de\u7231\u5979\u7231\u5f97\u8981\u547d\u3002 \u3000\u3000\u4f46\u963f\u98de\u73b0\u5728\u5728\u4ec0\u4e48\u5730\u65b9\u5462\uff1f \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u8fd8\u5728\u90a3\u5c4b\u5b50\u91cc\uff0c\u56e0\u4e3a\u90a3\u662f\u2018\u6211\u4eec\u7684\u5bb6\u2019\uff0c\u90a3\u91cc\u8fd8\u6709\u6211\u7559\u4e0b\u7684\u4e1c\u897f\uff0c\u7559\u4e0b\u7684\u5473\u9053 \u3002\u201d \u3000\u3000\u201c\u4ed6\u4e00\u5b9a\u8fd8\u5728\u7b49\u7740\u6211\u56de\u53bb\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u6797\u4ed9\u513f\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u8212\u670d\u591a\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e24\u5929\u4ed6\u4e00\u5b9a\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u60f3\u505a\uff0c\u4e00\u5b9a\u8fd8\u662f\u5728\u6574\u5929\u559d\u9152\uff0c\u90a3\u5730\u65b9\u4e00\u5b9a\u88ab\u4ed6\u5f04\u5f97\u4e71\u4e03\u516b\u7cdf \uff0c\u751a\u81f3\u8fde\u90a3\u4e9b\u6b7b\u5c38\u90fd\u8fd8\u6ca1\u6709\u642c\u8d70\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u6797\u4ed9\u513f\u53c8\u4e0d\u7981\u76b1\u4e86\u76b1\u7709\u3002 \u3000\u3000\u201c\u4f46\u662f\u6ca1\u5173\u7cfb\uff0c\u53ea\u8981\u6211\u4e00\u89c1\u4ed6\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u90fd\u4f1a\u62a2\u7740\u53bb\u505a\u4e86\uff0c\u6839\u672c\u4e0d\u7528\u6211\u52a8\u624b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u6ee1\u8db3\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u4e00\u4e2a\u4eba\u5df2\u5230\u4e86\u5979\u8fd9\u79cd\u65f6\u5019\uff0c\u60f3\u5230\u8fd8\u6709\u4e2a\u5730\u65b9\u53ef\u4ee5\u5706\u53bb\uff0c\u8fd8\u6709\u4eba \u5728\u82e6\u82e6\u7684\u7b49\u7740\u5979\uff0c\u8fd9\u79cd\u611f\u89c9\u5b9e\u5728\u4ee4\u4eba\u6109\u5feb\u3002 \u3000\u3000\u201c\u4ee5\u524d\u6211\u5bf9\u4ed6\u4e5f\u8bb8\u7684\u786e\u592a\u72e0\u4e86\u4e9b\uff0c\u5c06\u4ed6\u903c\u5f97\u592a\u7d27\uff0c\u4ee5\u540e\u6211\u4e5f\u8981\u6539\u53d8\u65b9\u9488\u4e86\u3002\u201d \u3000\u3000\u201c\u7537\u4eba\u5c31\u50cf\u662f\u5b69\u5b50\uff0c\u4f60\u8981\u4ed6\u542c\u8bdd\uff0c\u591a\u5c11\u4e5f\u5f97\u7ed9\u4ed6\u70b9\u751c\u5934\u5403\u5403\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5979\u5ffd\u7136\u89c9\u5f97\u5fc3\u91cc\u6709\u70b9\u53d1\u70ed\u3002 \u3000\u3000\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u6bd5\u7adf\u4e0d\u662f\u4e2a\u5f88\u4ee4\u4eba\u8ba8\u538c\u7684\u4eba\uff0c\u751a\u81f3\u6bd4\u6211\u6240\u9047\u89c1\u7684\u90a3\u4e9b\u7537\u4eba\u5168\u90e8\u5f3a\u5f97\u591a\u3002 \u201d \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u8fd8\u662f\u6709\u70b9\u7231\u4ed6\u7684\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u751f\u4e2d\uff0c\u5047\u5982\u8fd8\u6709\u4e2a\u4eba\u80fd\u771f\u7684\u4ee4\u5979\u52a8\u4e00\u70b9\u611f\u60c5\uff0c\u90a3\u4eba\u5c31\u662f\u963f\u98de\u4e86\uff0c\u60f3\u5f97\u8d8a\u591a\uff0c\u5979\u5c31 \u8d8a\u89c9\u5f97\u963f\u98de\u7684\u597d\u5904\u6bd4\u522b\u4eba\u591a\u3002 \u3000\u3000\u201c\u6211\u771f\u8be5\u597d\u597d\u7684\u5bf9\u4ed6\u624d\u662f\uff0c\u50cf\u4ed6\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u4e16\u4e0a\u5e76\u4e0d\u591a\uff0c\u4ee5\u540e\u6211\u4e5f\u8bb8\u518d\u4e5f\u627e\u4e0d\u5230\u4e86\u3002 \u201d \u3000\u3000\u8d8a\u60f3\u5979\u8d8a\u89c9\u5f97\u4e0d\u80fd\u653e\u5f03\u4ed6\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u4e00\u76f4\u90fd\u5728\u7231\u7740\u5979\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u7231\u5f97\u592a\u6df1\u4e86\uff0c\u6240\u4ee5\u624d\u4ee4\u5979\u89c9\u5f97\u65e0\u6240\u8c13\u3002 \u3000\u3000\u4ed6\u7231\u5979\u7231\u5f97\u82e5\u6ca1\u6709\u90a3\u4e48\u6df1\uff0c\u5979\u8bf4\u4e0d\u5b9a\u53cd\u800c\u4f1a\u66f4\u7231\u4ed6\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4eba\u6027\u7684\u5f31\u70b9\uff0c\u4eba\u6027\u7684\u77db\u76fe\u3002 \u3000\u3000\u6240\u4ee5\u806a\u660e\u7684\u7537\u4eba\u5c31\u7b97\u7231\u6781\u4e86\u4e00\u4e2a\u5973\u4eba\uff0c\u4e5f\u53ea\u662f\u85cf\u5728\u5fc3\u91cc\uff0c\u7edd\u4e0d\u4f1a\u5c06\u4ed6\u7684\u7231\u5168\u90e8\u5728\u5979\u9762\u524d \u8868\u73b0\u51fa\u6765\u3002 \u3000\u3000\u201c\u963f\u98de\uff0c\u4f60\u653e\u5fc3\uff0c\u4ee5\u540e\u6211\u7edd\u4e0d\u4f1a\u518d\u4ee4\u4f60\u4f24\u5fc3\u4e86\uff0c\u6211\u4e00\u5b9a\u5929\u5929\u966a\u4f60\uff0c\u4ee5\u524d\u7684\u4e8b\u5168\u5df2\u8fc7\u53bb\uff0c \u73b0\u5728\u6211\u4eec\u518d\u91cd\u5934\u505a\u8d77\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u8fd8\u50cf\u4ee5\u524d\u90a3\u4e48\u6837\u5bf9\u6211\uff0c\u6211\u4ec0\u4e48\u4e8b\u90fd\u53ef\u4ee5\u4f9d\u7740\u4f60\u3002\u201d \u3000\u3000\u4f46\u963f\u98de\u662f\u4e0d\u662f\u8fd8\u4f1a\u50cf\u4ee5\u524d\u90a3\u4e48\u6837\u5bf9\u5979\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u89c9\u5f97\u5e76\u4e0d\u5341\u5206\u6709\u628a\u63e1\uff0c\u5bf9\u81ea\u5df1\u7684\u4fe1\u5fc3\u5df2\u52a8\u6447\u3002 \u3000\u3000\u5979\u4ee5\u524d\u4ece\u672a\u6709\u8fc7\u8fd9\u79cd\u611f\u89c9\uff0c\u90a3\u53ea\u56e0\u5979\u4ee5\u524d\u4ece\u672a\u89c9\u5f97\u963f\u98de\u5bf9\u5979\u6709\u5982\u6b64\u91cd\u8981\uff0c\u65e0\u8bba\u963f\u98de\u5bf9\u5979 \u662f\u597d\u662f\u574f\uff0c\u5979\u90fd\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ea\u6709\u5728\u5f88\u60f3\u201c\u5f97\u5230\u201d\u7684\u65f6\u5019\uff0c\u624d\u4f1a\u6015\u201c\u5931\u201d\u3002 \u3000\u3000\u8fd9\u79cd\u60a3\u5f97\u60a3\u5931\u7684\u611f\u89c9\uff0c\u4e5f\u6b63\u662f\u4eba\u7c7b\u8bb8\u591a\u79cd\u5f31\u70b9\u4e4b\u4e00\u3002 \u3000\u3000\u53ef\u60b2\u7684\u662f\uff0c\u4f60\u60f3\u201c\u5f97\u5230\u201d\u7684\u4eba\u8d8a\u6025\u5207\uff0c\u201c\u5931\u53bb\u201d\u7684\u53ef\u80fd\u5c31\u8d8a\u5927\uff1a \u3000\u3000\u6797\u4ed9\u513f\u62ac\u8d77\u5934\uff0c\u5df2\u770b\u5230\u5c0f\u8def\u65c1\u7684\u5c4b\u5b50\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5c45\u7136\u6709\u706f\u3002 \u3000\u3000\u5979\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u5c06\u8d34\u8eab\u5c0f\u8863\u7684\u8863\u895f\u6495\u4e0b\u4e86\u4e00\u5757\uff0c\u5c31\u7740\u96e8\u6c34\u6d17\u4e86\u6d17\u8138\uff0c\u53c8\u7528\u624b\u6307\u505a\u68b3\u5b50\uff0c \u68b3\u4e86\u68b3\u5934\u53d1\u3002 \u3000\u3000\u5979\u4e0d\u613f\u8ba9\u963f\u98de\u770b\u5230\u5979\u8fd9\u79cd\u72fc\u72c8\u7684\u6837\u5b50\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7edd\u4e0d\u80fd\u518d\u5931\u53bb\u4ed6\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u7684\u706f\u8fd8\u5728\u4eae\u7740\u3002 \u3000\u3000\u706f\u5728\u684c\u4e0a\u3002 \u3000\u3000\u706f\u7684\u65c1\u8fb9\uff0c\u8fd8\u6709\u4e00\u5927\u9505\u7ca5\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5e76\u4e0d\u50cf\u6797\u4ed9\u513f\u60f3\u8c61\u4e2d\u90a3\u4e48\u810f\uff0c\u5c38\u4f53\u5df2\u642c\u8d70\uff0c\u8840\u6e0d\u5df2\u6e05\u626b\uff0c\u5c45\u7136\u6253\u626b\u5f97\u5341\u5206\u5e72\u51c0\u3002 \u3000\u3000\u963f\u98de\u6b63\u5750\u5728\u684c\u65c1\uff0c\u4e00\u53e3\u4e00\u53e3\u7684\u559d\u7740\u7ca5\u3002 \u3000\u3000\u4ed6\u5403\u4e1c\u897f\u7684\u65f6\u5019\u4e00\u76f4\u5f88\u6162\u3002\u56e0\u4e3a\u4ed6\u77e5\u9053\u98df\u7269\u5e76\u4e0d\u6613\u5f97\uff0c\u6240\u4ee5\u8981\u6162\u6162\u7684\u4eab\u53d7\uff0c\u8981\u5c06\u6bcf\u4e00\u53e3 \u98df\u7269\u90fd\u5b8c\u5168\u5438\u6536\uff0c\u5b8c\u5168\u6d88\u5316\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u770b\u6765\u5374\u5e76\u4e0d\u50cf\u662f\u5728\u4eab\u53d7\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u751a\u81f3\u5e26\u7740\u79cd\u538c\u5026\u7684\u795e\u8272\uff0c\u663e\u7136\u662f\u5728\u52c9\u5f3a\u81ea\u5df1\u5403\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u52c9\u5f3a\u81ea\u5df1\u5403\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4e0d\u60f3\u5012\u4e0b\uff1f \u3000\u3000\u591c\u5df2\u6df1\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u9762\u5bf9\u7740\u5b64\u706f\uff0c\u6162\u9992\u7684\u559d\u7740\u7ca5\u3002 \u3000\u3000\u6ca1\u6709\u770b\u5230\u8fc7\u8fd9\u79cd\u666f\u8c61\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u60f3\u5230\u8fd9\u666f\u8c61\u662f\u591a\u4e48\u5bc2\u5bde\uff0c\u591a\u4e48\u51c4\u51c9\u3002 \u3000\u3000\u7136\u540e\uff0c\u95e8\u8f7b\u8f7b\u88ab\u63a8\u5f00\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u51fa\u73b0\u5728\u95e8\u53e3\uff0c\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u5728\u770b\u5230\u963f\u98de\u7684\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u6709\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u5c31\u597d\u50cf\u6d41\u6d6a\u5df2\u4e45\u7684\u6e38\u5b50\u9aa4\u7136 \u89c1\u5230\u4eb2\u4eba\u4e00\u6837\u3002 \u3000\u3000\u5c31\u8fde\u5979\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u5979\u81ea\u5df1\u600e\u4f1a\u6709\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u5979\u7684\u8840\u672c\u662f\u51b7\u7684\u3002 \u3000\u3000\u963f\u98de\u5374\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u53d1\u89c9\u6709\u4eba\u8fdb\u6765\uff0c\u8fd8\u662f\u4f4e\u7740\u5934\uff0c\u4e00\u53e3\u4e00\u53e3\u7684\u559d\u7740\u7ca5\uff0c\u5c31\u597d\u50cf\u4e16\u4e0a\u53ea\u6709 \u8fd9\u7897\u91cc\u7684\u7ca5\u624d\u662f\u771f\u5b9e\u7684\u3002 \u3000\u3000\u4f46\u5979\u8138\u4e0a\u7684\u808c\u8089\u5374\u4f3c\u5728\u9010\u6e10\u50f5\u786c\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5fcd\u4e0d\u4f4f\u8f7b\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u5c0f\u98de\u2026\u2026\u201d \u3000\u3000\u8fd9\u547c\u5524\u7684\u58f0\u97f3\u8fd8\u662f\u90a3\u4e48\u6e29\u67d4\uff0c\u90a3\u4e48\u751c\u871c\u3002 \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u6162\u9992\u7684\u62ac\u8d77\u5934\uff0c\u9762\u5bf9\u7740\u5979\u3002 \u3000\u3000\u4ed6\u7684\u773c\u775b\u8fd8\u662f\u5f88\u4eae\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u6709\u6cea\u5462\uff1f \u3000\u3000\u6797\u4ed9\u513f\u7684\u773c\u775b\u4f3c\u4e5f\u6709\u4e9b\u6e7f\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u6211\u56de\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u52a8\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u4f3c\u5df2\u50f5\u786c\u5f97\u4e0d\u80fd\u6709\u4efb\u4f55\u52a8\u4f5c\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5df2\u6162\u6162\u7684\u5411\u4ed6\u8d70\u4e86\u8fc7\u6765\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4f1a\u7b49\u6211\u7684\uff0c\u56e0\u4e3a\u6211\u5230\u73b0\u5728\u624d\u77e5\u9053\u8fd9 \u4e16\u4e0a\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u662f\u771f\u7684\u5bf9\u6211\u597d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u5979\u6ca1\u6709\u7528\u624b\u6bb5\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5979\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u56e0\u4e3a\u5979\u5df2\u51b3\u5b9a\u8981\u4ee5\u771f\u5fc3\u5bf9\u4ed6\u3002 \u3000\u3000\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u522b\u7684\u4eba\u90fd\u53ea\u4e0d\u8fc7\u662f\u5229\u7528\u6211\u2026\u2026\u6211\u5229\u7528\u4ed6\u4eec\u3002\u4ed6\u4eec\u5229\u7528\u6211\uff01\u8fd9\u672c\u6ca1\u6709\u4ec0\u4e48 \u5403\u4e8f\u7684\uff0c\u53ea\u6709\u4f60\uff0c\u65e0\u8bba\u6211\u600e\u4e48\u6837\u5bf9\u4f60\uff0c\u4f60\u5bf9\u6211\u603b\u662f\u771f\u5fc3\u771f\u610f\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u6ce8\u610f\u963f\u98de\u8138\u4e0a\u8868\u60c5\u7684\u53d8\u5316\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u8ddd\u79bb\u963f\u98de\u5df2\u8d8a\u6765\u8d8a\u8fd1\u4e86\uff0c\u5df2\u8fd1\u5f97\u770b\u4e0d\u6e05\u8bb8\u591a\u5979\u5e94\u8be5\u770b\u5230\u7684\u4e8b\u3002 \u3000\u3000\u201c\u6211\u51b3\u5fc3\u4ee5\u540e\u7edd\u4e0d\u518d\u9a97\u4f60\uff0c\u7edd\u4e0d\u4f1a\u518d\u8ba9\u4f60\u4f24\u5fc3\u4e86\uff0c\u65e0\u8bba\u4f60\u8981\u600e\u4e48\u6837\uff0c\u6211\u90fd\u53ef\u4ee5\u4f9d\u7740\u4f60\uff0c \u90fd\u53ef\u4ee5\u7b54\u5e94\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u81a8\u201d\u7684\uff0c\u963f\u98de\u624b\u91cc\u7684\u7b77\u5b50\u7a81\u7136\u65ad\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u62c9\u8d77\u4ed6\u7684\u624b\uff0c\u653e\u5728\u81ea\u5df1\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u5979\u7684\u58f0\u97f3\u751c\u5f97\u50cf\u871c\u3002 \u3000\u3000\u201c\u4ee5\u524d\u6211\u82e5\u6709\u5bf9\u4e0d\u8d77\u4f60\u7684\u5730\u65b9\uff0c\u4ee5\u540e\u6211\u4e00\u5b9a\u4f1a\u52a0\u500d\u8865\u507f\u4f60\uff0c\u6211\u4f1a\u8981\u4f60\u89c9\u5f97\u65e0\u8bba\u4f60\u5bf9\u6211\u591a \u597d\uff0c\u90fd\u662f\u503c\u5f97\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u80f8\u819b\u6e29\u6696\u800c\u67d4\u8f6f\u3002 \u3000\u3000\u65e0\u8bba\u4efb\u4f55\u4eba\u7684\u624b\u82e5\u653e\u5728\u5979\u80f8\u819b\u4e0a\uff0c\u7edd\u5bf9\u518d\u4e5f\u820d\u4e0d\u5f97\u79fb\u5f00\u3002 \u3000\u3000\u963f\u98de\u7684\u624b\u5ffd\u7136\u81ea\u5979\u80f8\u819b\u4e0a\u79fb\u5f00\u4e86\u3002 \u3000\u3000\u6797\u4ed9\u513f\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u4e1d\u6050\u60e7\u4e4b\u610f\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\uff0c\u2026\u2026\u96be\u9053\u4e0d\u8981\u6211\u4e86\uff1f\u201d \u3000\u3000\u963f\u98de\u9759\u9759\u7684\u77a7\u7740\u5979\uff0c\u5c31\u597d\u50cf\u7b2c\u4e00\u6b21\u770b\u5230\u5979\u8fd9\u4e2a\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u8bf4\u7684\u5168\u90e8\u662f\u771f\u8bdd\uff0c\u4ee5\u524d\u6211\u867d\u7136\u4e5f\u548c\u522b\u7684\u7537\u4eba\u6709\u2026\u2026\u6709\u8fc7\uff0c\u4f46\u6211\u5bf9\u4ed6 \u4eec\u90a3\u5168\u90fd\u662f\u5047\u7684\u2026\u2026\u201d \u3000\u3000\u5979\u58f0\u97f3\u5ffd\u7136\u505c\u987f\uff0c\u56e0\u4e3a\u5979\u5ffd\u7136\u770b\u5230\u4e86\u963f\u98de\u8138\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u963f\u98de\u7684\u8868\u60c5\u5c31\u50cf\u662f\u60f3\u5455\u5410\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e0d\u7531\u81ea\u4e3b\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u4e0d\u613f\u542c\u771f\u8bdd\uff1f\u4f60\u96be\u9053\u559c\u6b22\u6211\u9a97\u4f60\uff1f\u201d \u3000\u3000\u963f\u98de\u76ef\u7740\u5979\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u53ea\u5947\u602a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6797\u4ed9\u513f\u9053\uff1a\u201c\u4f60\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u53ea\u5947\u602a\uff0c\u6211\u4ee5\u524d\u600e\u4e48\u4f1a\u7231\u4e0a\u4f60\u8fd9\u79cd\u5973\u4eba\u7684\uff01\u201d \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u89c9\u5f97\u5168\u8eab\u90fd\u51c9\u4e86\u3002 \u3000\u3000\u963f\u98de\u6ca1\u6709\u518d\u8bf4\u522b\u7684\u3002 \u3000\u3000\u4ed6\u7528\u4e0d\u7740\u518d\u8bf4\u522b\u7684\uff0c\u8fd9\u4e00\u53e5\u8bdd\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u5c31\u5df2\u8db3\u591f\u5c06\u6797\u4ed9\u513f\u63a8\u5165\u4e07\u52ab\u4e0d\u590d\u7684\u6df1\u6e0a\u3002 \u3000\u3000\u963f\u98de\u6162\u6162\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u5df2\u53d7\u8fc7\u65e0\u6570\u6b21\u6253\u51fb\u548c\u4fae\u8fb1\uff0c\u7edd\u4e0d\u4f1a\u4e0d\u53d8\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u53ef\u4ee5\u5fcd\u53d7\u8c0e\u8a00\uff0c\u5374\u7edd\u4e0d\u80fd\u5fcd\u53d7\u90a3\u79cd\u6700\u4e0d\u80fd\u5fcd\u53d7\u7684\u4fae\u8fb1\u2014\u2014\u5973\u4eba\u5982\u6b64\uff0c\u7537\u4eba\u4e5f\u4e00\u6837 \u3002 \u3000\u3000\u505a\u59bb\u5b50\u7684\u5982\u6b64\uff0c\u505a\u4e08\u592b\u7684\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u6797\u4ed9\u513f\u53ea\u89c9\u81ea\u5df1\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\uff0c\u5f80\u4e0b\u6c89\u2026\u2026 \u3000\u3000\u963f\u98de\u5df2\u62c9\u5f00\u95e8\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u8f6c\u8eab\u6251\u8fc7\u53bb\uff0c\u6251\u5012\u5728\u4ed6\u811a\u4e0b\uff0c\u62c9\u4f4f\u4ed6\u7684\u8863\u670d\uff0c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u5c31\u8fd9\u6837\u79bb \u5f00\u6211\u2026\u2026\u6211\u73b0\u5728\u5df2\u53ea\u6709\u4f60\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u56de\u5934\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u6162\u6162\u7684\u5c06\u8863\u670d\u8131\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u7cbe\u8d64\u7740\u4e0a\u8eab\u8d70\u4e86\u51fa\u53bb\uff0c\u8d70\u5165\u96e8\u4e2d\u3002 \u3000\u3000\u96e8\u5f88\u51b7\u3002 \u3000\u3000\u53ef\u662f\u96e8\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u7529\u8131\u4e86\u6797\u4ed9\u513f\uff0c\u7529\u8131\u4e86\u4ed6\u5fc3\u7075\u4e0a\u7684\u67b7\u9501\uff0c\u5c31\u597d\u50cf\u7529\u8131\u4e86\u90a3\u4ef6\u65e9\u5df2\u9648\u65e7\u7834\u70c2\u7684\u8863\u670d \u3002 \u3000\u3000\u6797\u4ed9\u513f\u5374\u8fd8\u5728\u7d27\u7d27\u6293\u7740\u90a3\u4ef6\u8863\u670d\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u9664\u4e86\u8fd9\u4ef6\u8863\u670d\u5916\uff0c\u5c31\u518d\u4e5f\u6293\u4e0d\u4f4f\u522b\u7684\u3002 \u3000\u3000\u201c\u5230\u5934\u6765\u4f60\u603b\u4f1a\u53d1\u73b0\u4f60\u539f\u6765\u4ec0\u4e48\u4e5f\u6ca1\u6709\u5f97\u5230\uff0c\u4ec0\u4e48\u90fd\u662f\u7a7a\u7684\u2026\u2026\u201d \u3000\u3000\u6797\u4ed9\u513f\u6cea\u5df2\u6d41\u4e0b\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u5979\u624d\u53d1\u73b0\u5979\u539f\u6765\u7684\u786e\u662f\u4e00\u76f4\u7231\u7740\u963f\u98de\u7684\u3002 \u3000\u3000\u5979\u6298\u78e8\u4ed6\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u5979\u7231\u4ed6\uff0c\u4e5f\u77e5\u9053\u4ed6\u7231\u5979\u3002 \u3000\u3000\u201c\u5973\u4eba\u4e3a\u4ec0\u4e48\u603b\u559c\u6b22\u6298\u78e8\u6700\u7231\u5979\u7684\u7537\u4eba\u5462\uff1f\u201d \u3000\u3000\u5230\u73b0\u5728\uff0c\u5979\u624d\u77e5\u9053\u963f\u98de\u5bf9\u5979\u662f\u591a\u4e48\u91cd\u8981\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5df2\u5931\u53bb\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u5973\u4eba\u4e3a\u4ec0\u4e48\u603b\u662f\u5bf9\u5f97\u5230\u7684\u4e1c\u897f\u52a0\u4ee5\u8f7b\u8511\uff0c\u4e3a\u4ec0\u4e48\u603b\u8981\u7b49\u5230\u5931\u53bb\u65f6\u624d\u77e5\u9053\u73cd\u60dc\u3002\u201d \u3000\u3000\u4e5f\u8bb8\u4e0d\u53ea\u5973\u4eba\u5982\u6b64\uff0c\u7537\u4eba\u4e5f\u662f\u4e00\u6837\u7684\u3002 \u3000\u3000\u6797\u4ed9\u513f\u5ffd\u7136\u72c2\u7b11\u8d77\u6765\uff0c\u72c2\u7b11\u7740\u5c06\u963f\u98de\u7684\u8863\u670d\u4e00\u7247\u7247\u6495\u788e\u3002 \u3000\u3000\u201c\u6211\u6015\u4ec0\u4e48\uff0c\u6211\u8fd9\u4e48\u6f02\u4eae\uff0c\u53c8\u8fd9\u4e48\u5e74\u8f7b\u2014\u2014\u53ea\u8981\u6211\u559c\u6b22\uff0c\u8981\u591a\u5c11\u7537\u4eba\u5c31\u6709\u591a\u5c11\u7537\u4eba\uff0c\u6211 \u6bcf\u5929\u6362\u5341\u4e2a\u90fd\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u5979\u5728\u7b11\uff0c\u53ef\u662f\u8fd9\u7b11\u5374\u6bd4\u54ed\u66f4\u60b2\u60e8\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e5f\u77e5\u9053\u7537\u4eba\u867d\u5bb9\u6613\u5f97\u5230\uff0c\u4f46\u201c\u771f\u60c5\u201d\u5374\u7edd\u4e0d\u662f\u9752\u6625\u548c\u7f8e\u8c8c\u53ef\u4ee5\u4e70\u5f97\u5230\u7684\u2026\u2026 \u3000\u3000\u6797\u4ed9\u513f\u7684\u4e0b\u573a\u5462\uff1f \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u3002 \u3000\u3000\u5979\u597d\u50cf\u5ffd\u7136\u5c31\u4ece\u8fd9\u4e16\u4e0a\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4e24\u4e09\u5e74\u4ee5\u540e\uff0c\u6709\u4eba\u5728\u957f\u5b89\u57ce\u6700\u8c6a\u534e\u7684\u5993\u9662\u4e2d\uff0c\u53d1\u73b0\u4e00\u4e2a\u5f88\u7279\u522b\u7684\u201c\u5993\u201d\u5973\uff0c\u56e0\u4e3a\u5979\u8981\u7684 \u4e0d\u662f\u94b1\uff0c\u800c\u662f\u7537\u4eba\u3002 \u3000\u3000\u636e\u8bf4\u5979\u6bcf\u5929\u81f3\u5c11\u8981\u6362\u5341\u4e2a\u4eba\u3002 \u3000\u3000\u5f00\u59cb\u65f6\uff0c\u5f53\u7136\u6709\u5f88\u591a\u7537\u4eba\u5bf9\u5979\u6709\u5174\u8da3\uff0c\u4f46\u540e\u6765\u5c31\u6e10\u6e10\u5c11\u4e86\u3002 \u3000\u3000\u90a3\u5e76\u4e0d\u4ec5\u4ec5\u662f\u56e0\u4e3a\u5979\u8001\u5f97\u592a\u5feb\uff0c\u800c\u662f\u56e0\u4e3a\u5927\u5bb6\u6e10\u6e10\u53d1\u73b0\u5979\u7b80\u76f4\u4e0d\u662f\u4e2a\u4eba\uff0c\u662f\u6761\u6bcd\u72fc\uff0c\u4eff \u4f5b\u8981\u5c06\u7537\u4eba\u8fde\u76ae\u5e26\u8089\u90fd\u541e\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u4e0d\u4f46\u559c\u6b22\u6467\u6b8b\u7537\u4eba\uff0c\u5bf9\u81ea\u5df1\u6467\u6b8b\u5f97\u66f4\u5389\u5bb3\u3002 \u3000\u3000\u636e\u8bf4\u5979\u5f88\u50cf\u201c\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u7f8e\u4eba\u201d\u6797\u4ed9\u513f\u3002 \u3000\u3000\u53ef\u662f\u5979\u81ea\u5df1\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u51e0\u5e74\uff0c\u957f\u5b89\u57ce\u91cc\u6700\u5351\u8d31\u7684\u7316\u5bee\u4e2d\uff0c\u4e5f\u51fa\u73b0\u4e86\u4e2a\u5f88\u7279\u522b\u7684\u5973\u4eba\uff0c\u800c\u4e14\u5f88\u6709\u540d\u3002 \u3000\u3000\u5979\u6709\u540d\u5e76\u4e0d\u662f\u56e0\u4e3a\u5979\u7f8e\uff0c\u800c\u662f\u56e0\u4e3a\u4e11\uff0c\u4e11\u5f97\u53ef\u7b11\u3002 \u3000\u3000\u6700\u53ef\u7b11\u7684\u662f\uff0c\u6bcf\u5f53\u5979\u559d\u5f97\u70c2\u9189\u7684\u65f6\u5019\uff0c\u5c31\u81ea\u79f0\u662f\u201c\u6c5f\u6e56\u4e2d\u7684\u7b2c\u4e00\u7f8e\u4eba\u201d\u3002 \u3000\u3000\u5979\u8bf4\u7684\u8bdd\u81ea\u7136\u6ca1\u6709\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u96e8\u5f88\u51b7\u3002 \u3000\u3000\u51b7\u96e8\u6d12\u5728\u963f\u98de\u80f8\u819b\u4e0a\uff0c\u4ed6\u89c9\u5f97\u8212\u670d\u5f97\u5f88\uff0c\u56e0\u4e3a\u8fd9\u96e8\u4ee4\u4ed6\u89c9\u5f97\u81ea\u5df1\u5e76\u4e0d\u662f\u9ebb\u6728\u7684\uff0c\u4e24\u5e74\u6765 \uff0c\u8fd9\u4e5f\u8bb8\u662f\u4ed6\u7b2c\u4e00\u6b21\u6709\u8fd9\u79cd\u611f\u89c9\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u89c9\u5f97\u5f88\u8f7b\u677e\uff0c\u5c31\u50cf\u662f\u521a\u5378\u4e0b\u4e86\u4e00\u4e2a\u6c89\u91cd\u7684\u5305\u88b1\u3002 \u3000\u3000\u8fdc\u5904\u6709\u4eba\u5728\u547c\u5524\uff1a\u201c\u963f\u98de\u2026\u2026\u201d \u3000\u3000\u547c\u58f0\u5f88\u8f7b\uff0c\u82e5\u5728\u51e0\u5929\u524d\uff0c\u4ed6\u4e5f\u8bb8\u6839\u672c\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u7684\u773c\u775b\u5df2\u4e0d\u518d\u778e\uff0c\u8033\u6735\u4e5f\u4e0d\u518d\u804b\u4e86\u3002 \u3000\u3000\u4ed6\u505c\u4e0b\uff0c\u95ee\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u5954\u8fc7\u6765\uff0c\u4e24\u6761\u957f\u957f\u7684\u8fab\u5b50\uff0c\u4e00\u53cc\u5927\u5927\u7684\u773c\u775b\u3002 \u3000\u3000\u662f\u4e2a\u5f88\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\uff0c\u53ea\u4e0d\u8fc7\u663e\u5f97\u6709\u4e9b\u7126\u6025\uff0c\u4e5f\u6709\u4e9b\u7901\u6dec\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7ec8\u4e8e\u4e5f\u627e\u5230\u4e86\u4ed6\u3002 \u3000\u3000\u5979\u5954\u8fc7\u6765\uff0c\u51e0\u4e4e\u51b2\u5230\u963f\u98de\u8eab\u4e0a\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u4e5f\u8bb8\u4e0d\u8bb0\u5f97\u6211\u4e86\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\uff0c\u4e24\u5e74\u524d\u6211\u770b\u5230\u8fc7\u4f60\u4e00\u6b21\uff0c\u4f60\u5f88\u4f1a\u8bf4\u8bdd\uff0c\u524d\u4e24\u5929\u6211 \u53c8\u89c1\u8fc7\u4f60\u4e00\u6b21\uff0c\u4f60\u6ca1\u6709\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7684\u8bb0\u6027\u8fd9\u4e48\u597d\u3002\u201d \u3000\u3000\u5979\u7684\u5fc3\u5883\u5ffd\u7136\u5f00\u6717\uff0c\u56e0\u4e3a\u5979\u53d1\u73b0\u963f\u98de\u53c8\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u7ad9\u5f97\u5f88\u76f4\u3002 \u3000\u3000\u201c\u6709\u4e9b\u4eba\u65e0\u8bba\u88ab\u4eba\u51fb\u5012\u591a\u5c11\u6b21\uff0c\u90fd\u8fd8\u662f\u80fd\u7ad9\u5f97\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u5979\u89c9\u5f97\u674e\u5bfb\u6b22\u7684\u786e\u662f\u963f\u98de\u7684\u77e5\u5df1\u3002 \u3000\u3000\u963f\u98de\u867d\u7136\u77e5\u9053\u5979\u627e\u6765\u4e00\u5b9a\u6709\u4e8b\uff0c\u4f46\u5374\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u81ea\u5df1\u4f1a\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5374\u6ca1\u6709\u8bf4\uff0c\u5979\u8fd8\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u8bf4\u3002 \u3000\u3000\u963f\u98de\u7ec8\u4e8e\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u8bdd\u4f60\u90fd\u53ef\u4ee5\u8bf4\uff0c\u56e0\u4e3a\u4f60\u662f\u674e\u5bfb\u6b22\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u5979\u4e86\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5979\u5462\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5979\u662f\u5979\uff0c\u6211\u662f\u6211\uff0c\u4f60\u4e3a\u4f55\u8981\u95ee\u6211\uff1f\u201d \u3000\u3000\u4ee5\u524d\u6bcf\u5f53\u6709\u4eba\u5728\u4ed6\u9762\u524d\u63d0\u8d77\u6797\u4ed9\u513f\u65f6\uff0c\u4ed6\u90fd\u4f1a\u89c9\u5f97\u4e00\u9635\u8bf4\u4e0d\u51fa\u7684\u6fc0\u52a8\uff0c\u5c31\u8fde\u5979\u7684\u540d\u5b57\u5bf9 \u4ed6\u8bf4\u6765\u90fd\u4eff\u4f5b\u6709\u79cd\u5947\u5f02\u7684\u9b54\u529b\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5374\u5f88\u5e73\u9759\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u89c6\u7740\u4ed6\uff0c\u5ffd\u7136\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u4f60\u679c\u7136\u5df2\u5c06\u4f60\u7684\u67b7\u9501\u7529\u8131\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u67b7\u9501\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u4ed6\u81ea\u5df1\u7684\u84b8\u7b3c\uff0c\u4e5f\u6709\u4ed6\u81ea\u5df1\u7684\u67b7\u9501\uff0c\u53ea\u6709\u5f88\u5c11\u4eba\u624d\u80fd\u5c06\u81ea\u5df1\u7684 \u67b7\u9501\u7529\u8131\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u61c2\uff0c\u4f60\u53ea\u8981\u80fd\u505a\u5230\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u61c2\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u771f\u7684\u61c2\u4e86\uff1f\u2026\u2026\u90a3\u4e48\u6211\u95ee\u4f60\uff0c\u4f60\u662f\u600e\u4e48\u6837\u5c06\u90a3\u526f\u67b7\u9501\u7529\u8131\u7684\uff1f\u201d \u3000\u3000\u963f\u98de\u60f3\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u5ffd\u7136\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u201c\u5ffd\u7136\u60f3\u901a\u4e86\u201d\uff0c\u8fd9\u4e94\u4e2a\u5b57\u8bf4\u6765\u7b80\u5355\uff0c\u8981\u505a\u5230\u53ef\u771f\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u6211\u4f5b\u5982\u6765\u5728\u83e9\u63d0\u6811\u4e0b\u5f97\u9053\uff0c\u5c31\u56e0\u4e3a\u4ed6\u5ffd\u7136\u60f3\u901a\u4e86\u3002 \u3000\u3000\u8fbe\u6469\u7956\u5e08\u9762\u58c1\u5341\u516b\u5e74\uff0c\u624d\u603b\u7b97\u201c\u5ffd\u7136\u60f3\u901a\u4e86\u201d\u3002 \u3000\u3000\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u4f60\u53ea\u8981\u80fd\u201c\u5ffd\u7136\u60f3\u901a\u4e86\u201d\uff0c\u4f60\u5c31\u4e0d\u4f1a\u6709\u70e6\u607c\uff0c\u4f46\u8fbe\u5230\u8fd9\u5730\u6b65\u4e4b\u524d\uff0c\u4f60\u4e00\u5b9a \u5df2\u4e0d\u77e5\u9053\u6709\u8fc7\u591a\u5c11\u70e6\u607c\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u60f3\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u80fd\u60f3\u901a\u4e86\uff0c\u4ed8\u51fa\u7684\u4ee3\u4ef7\u4e00\u5b9a\u4e0d\u5c11\u2026\u2026 \u201d \u3000\u3000\u963f\u98de\u4f3c\u5df2\u4e0d\u613f\u518d\u63d0\u8d77\u8fd9\u4e9b\u4e8b\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u662f\u4ed6\u8981\u4f60\u6765\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u5462\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u7b11\u5bb9\u4e5f\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u963f\u98de\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u55eb\u558f\u7740\u9eef\u7136\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u65e2\u4e0d\u77e5\u9053\u4ed6\u73b0\u5728\u5728\u54ea\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u73b0\u5728\u662f\u6b7b\u662f \u6d3b\uff1f\u201d \u3000\u3000\u963f\u98de\u53d8\u8272\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e5f\u8bb8\u53ef\u4ee5\u627e\u5f97\u5230\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u7684\u6b7b\u6d3b\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u7684\u6b7b\u6d3b\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u89c6\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u662f\u6b7b\u662f\u6d3b\uff0c\u5168\u90e8\u5f97\u770b\u4f60\u4e86\uff01\u201d \u7b2c\u516b\u5341\u4e94\u7ae0\u3000\u9519\u7684\u662f\u8c01\u5462>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u4e94\u7ae0\u3000\u9519\u7684\u662f\u8c01\u5462 \u3000\u3000\u5916\u9762\u867d\u4e0b\u7740\u96e8\uff0c\u5c4b\u5b50\u91cc\u5374\u8fd8\u662f\u5f88\u5e72\u71e5\uff0c\u56e0\u4e3a\u8fd9\u4e48\u5927\u7684\u5c4b\u5b50\uff0c\u53ea\u6709\u4e00\u4e2a\u7a97\u6237\uff0c\u7a97\u6237\u5f88\u5c0f\uff0c \u79bb\u5730\u5f88\u9ad8\u3002 \u3000\u3000\u7a97\u6237\u6c38\u8fdc\u90fd\u662f\u5173\u7740\u7684\uff0c\u9633\u5149\u6c38\u8fdc\u7167\u4e0d\u8fdb\u6765\uff0c\u96e8\u4e5f\u6d12\u4e0d\u8fdb\u6765\u3002 \u3000\u3000\u5899\u4e0a\u6f06\u7740\u767d\u8272\u7684\u6f06\uff0c\u6f06\u5f97\u5f88\u539a\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u8fd9\u5899\u662f\u571f\u77f3\u6240\u7b51\uff0c\u8fd8\u662f\u94dc\u94c1\u6240\u94f8\uff1f\u4f46\u8c01\u90fd\u80fd \u770b\u5f97\u51fa\u8fd9\u5899\u5f88\u539a\uff0c\u539a\u5f97\u8db3\u4ee5\u9694\u7edd\u4e00\u5207\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u9664\u4e86\u4e24\u5f20\u5e8a\u548c\u4e00\u5f20\u5f88\u5927\u7684\u684c\u5b50\u5916\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u2014\u2014\u6ca1\u6709\u6905\uff0c\u6ca1\u6709\u51f3\uff0c\u751a\u81f3\u8fde \u4e00\u53ea\u676f\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u7b80\u76f4\u6bd4\u4e00\u4e2a\u82e6\u884c\u50e7\u6240\u4f4f\u7684\u5730\u65b9\u8fd8\u8981\u7b80\u964b\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u58f0\u540d\u6700\u54cd\uff0c\u52bf\u529b\u6700\u5927\uff0c\u8d22\u529b\u4e5f\u6700\u96c4\u539a\u7684\u201c\u91d1\u94b1\u5e2e\u201d\u5e2e\u4e3b\uff0c\u7adf\u4f1a\u4f4f\u5728\u8fd9\u4e48\u6837\u7684\u5730\u65b9 \u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u4e0d\u7981\u6014\u4f4f\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5c31\u7ad9\u5728\u4ed6\u8eab\u65c1\uff0c\u77a7\u7740\u4ed6\uff0c\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4f60\u6ee1\u610f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u81f3\u5c11\u5f88\u5e72\u71e5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u7684\u786e\u5f88\u5e72\u71e5\uff0c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u8fde\u4e00\u6ef4\u6c34\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u6de1\u6de1\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4e00\u5411\u6ca1\u6709\u8336\uff0c\u6ca1\u6709\u6c34\uff0c\u6ca1\u6709\u9152\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u4eba\u5728\u8fd9\u91cc\u6d41\u8fc7\u4e00\u6ef4 \u773c\u6cea\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u8840\u5462\uff1f\u6709\u6ca1\u6709\u4eba\u5728\u8fd9\u91cc\u6d41\u8fc7\u8840\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u51b7\u51b7\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u2014\u2014\u5c31\u7b97\u6709\u4eba\u60f3\u6b7b\u5728\u8fd9\u91cc\uff0c\u8fd8\u6ca1\u6709\u8d70\u5230\u8fd9\u91cc\u4e4b\u524d\uff0c\u8840\u5c31\u5df2\u6d41 \u5e72\u4e86\u3002\u201d \u3000\u3000\u4ed6\u51b7\u51b7\u63a5\u7740\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u60f3\u8981\u4ed6\u8fdb\u6765\uff0c\u65e0\u8bba\u4ed6\u662f\u6b7b\u662f\u6d3b\uff0c\u90fd\u4f11\u60f3\u8d70\u8fdb\u8fd9\u5c4b\u5b50\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6d3b\u7740\u4f4f\u5728\u8fd9\u91cc\u867d\u7136\u4e0d\u8212\u670d\uff0c\u4f46\u6b7b\u5728\u8fd9\u91cc\u5012\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u5730\u65b9\u672c\u6765\u5c31\u50cf\u662f\u575f\u5893\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u65e2\u7136\u4f60\u559c\u6b22\uff0c\u6211\u4e0d\u59a8\u5c31\u5c06\u4f60\u57cb\u5728\u8fd9\u91cc\u3002\u201d\u4ed6\u76ee\u4e2d\u53c8\u9732\u51fa\u4e00\u4e1d\u6b8b\u9177\u7684\u7b11\u610f \uff0c\u6307\u4e86\u6307\u811a\u4e0b\u7684\u4e00\u5757\u5730\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5c31\u57cb\u5728\u8fd9\u91cc\uff0c\u90a3\u4e48\u4ee5\u540e\u6211\u6bcf\u5929\u7ad9\u5728\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u5c31\u4f1a\u60f3 \u5230\u2018\u5c0f\u674e\u63a2\u82b1\u2019\u5c31\u5728\u6211\u7684\u811a\u4e0b\uff0c\u6211\u505a\u4e8b\u5c31\u4f1a\u66f4\u6e05\u9192\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u6e05\u9192\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u82e5\u4e0d\u80fd\u4fdd\u6301\u6e05\u9192\uff0c\u4e5f\u4e00\u6837\u4f1a\u88ab\u4eba\u8e29\u5728\u811a\u4e0b\u7684\uff0c\u4e00\u60f3\u5230\u4f60\u7684\u699c\u6837\uff0c \u6211\u5f53\u7136\u5c31\u80fd\u8b66\u60d5\u81ea\u5df1\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u4f46\u4e00\u4e2a\u4eba\u6e05\u9192\u7684\u65f6\u5019\u82e5\u662f\u5927\u591a\u4e86\uff0c\u5c82\u975e\u4e5f\u75db\u82e6\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u75db\u82e6\uff0c\u4ece\u6765\u6ca1\u6709\u8fc7\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4f60\u4e5f\u4ece\u6765\u6ca1\u6709\u5feb\u4e50\u8fc7\u2026\u2026\u6709\u65f6\u6211\u5f88\u60f3\u95ee\u95ee\u4f60\uff0c\u4f60\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c \u6d3b\u7740\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u773c\u89d2\u5728\u8df3\u52a8\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u4e5f\u8bb8\u771f\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c \u6d3b\u7740\u7684\uff0c\u4f46\u8fd8\u6709\u4e9b\u5374\u66f4\u53ef\u601c\uff0c\u4ed6\u4eec\u751a\u81f3\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c\u6b7b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u5c31\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c\u6b7b\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u6839\u672c\u4e0d\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u77e5\u9053\u6b7b\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48\u5927\u4e0d\u4e86\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b49\u4e0a\u5b98\u91d1\u8679\u8bf4\u8bdd\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5728\u4f60\u773c\u4e2d\uff0c\u770b\u6765\u6211\u73b0\u5728\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u4e86\uff0c\u662f\u4e0d\u662f\uff1f \u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u4f60\u5012\u5f88\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u65e2\u7136\u6211\u5df2\u6b7b\u5b9a\u4e86\uff0c\u5c31\u4e0d\u5fc5\u518d\u4e3a\u4efb\u4f55\u4e8b\u64cd\u5fc3\uff0c\u4e5f\u4e0d\u518d\u70e6\u607c\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5750\u4e86\u4e0b\u53bb\uff0c\u5c31\u5750\u5728\u5730\u4e0a\uff0c\u957f\u957f\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u60f3\u5750\uff0c\u5c31\u5750\u4e0b\u6765 \uff0c\u60f3\u95ed\u8d77\u773c\u775b\uff0c\u4f60\u80fd\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u62f3\u63e1\u7d27\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u80fd\uff0c\u56e0\u4e3a\u4f60\u8fd8\u8981\u62c5\u5fc3\u5f88\u591a\u4e8b\uff0c\u8fd8\u8981\u63d0\u9632\u6211\u3002\u201d \u3000\u3000\u4ed6\u5750\u5f97\u66f4\u8212\u670d\u4e86\u4e9b\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u81f3\u5c11\u73b0\u5728\u6211\u603b\u6bd4\u4f60\u8212\u670d\u591a\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e2\u7136\u5df2\u7b54\u5e94\u8fc7\u4e0d\u8ba9\u4f60\u6e7f\u6dcb\u6dcb\u7684\u6b7b\uff0c\u672c\u60f3\u7b49\u4f60\u8863\u670d\u4e00\u5e72 \u900f\u5c31\u51fa\u624b\u7684\uff0c\u53ef\u662f\u73b0\u5728\u6211\u4e3b\u610f\u53c8\u53d8\u4e86\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e0d\u4f46\u8981\u7ed9\u4f60\u5957\u5e72\u51c0\u7684\u8863\u670d\uff0c\u8fd8\u8981\u7ed9\u4f60\u4e00\u58f6\u9152\uff0c\u56e0\u4e3a\u4f60\u8bf4\u7684\u8bdd\u5b9e\u5728 \u5f88\u6709\u8da3\uff0c\u80fd\u542c\u5230\u6b7b\u4eba\u8bf4\u5982\u6b64\u6709\u8da3\u7684\u6d3b\uff0c\u5b9e\u5728\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u8721\u66f2\u5728\u88ab\u7a9d\u91cc\uff0c\u4f3c\u5df2\u7761\u7740\uff0c\u4f46\u5730\u4e0a\u5374\u6709\u51e0\u4e2a\u6e7f\u6dcb\u6dcb\u7684\u811a\u5370\u8fd8\u672a\u4e8e\u900f\u3002 \u3000\u3000\u71c3\u7740\u706f\uff0c\u706f\u82af\u5df2\u5c06\u71c3\u5c3d\uff0c\u9eef\u6de1\u7684\u706f\u5149\u4f7f\u8fd9\u534a\u65e7\u7684\u5ba2\u6808\u770b\u6765\u66f4\u9634\u68ee\u68ee\u7684\uff0c\u4eff\u4f5b\u5168\u65e0\u751f\u6c14\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u6084\u6084\u63a8\u5f00\u95e8\uff0c\u6084\u6084\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6148\u6bcd\u7684\u811a\u6b65\u6c38\u8fdc\u90fd\u90a3\u4e48\u8f7b\uff0c\u5979\u4eec\u5b81\u53ef\u81ea\u5df1\u5f7b\u591c\u4e0d\u7720\uff0c\u4e5f\u4e0d\u5fcd\u60ca\u9192\u5b69\u5b50\u7684\u68a6\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u4e5f\u8bb8\u5df2\u4e0d\u518d\u662f\u5b69\u5b50\u4e86\uff0c\u4e5f\u8bb8\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u6df1\u6c89\u4e16\u6545\uff0c\u4f46\u5f53\u4ed6\u7761\u7740\u4e86\u7684\u65f6\u5019\uff0c\u4ed6\u770b \u6765\u5374\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8fd8\u662f\u8fd9\u4e48\u5c0f\uff0c\u8fd9\u4e48\u82cd\u767d\uff0c\u8fd9\u4e48\u7626\u5f31\uff0c\u65e0\u8bba\u4ed6\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u6bd5\u7adf\u8fd8\u662f\u4e2a\u5b64\u72ec\u800c\u65e0 \u52a9\u7684\u5b69\u5b50\uff0c\u5bf9\u4eba\u751f\u8fd8\u662f\u5145\u6ee1\u4e86\u8ff7\u60c6\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u6084\u6084\u7684\u8d70\u5230\u5e8a\u524d\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u5fc3\u91cc\u53ea\u89c9\u5f97\u4e00\u9635\u9178\u695a\u3002 \u3000\u3000\u8fd9\u662f\u5979\u552f\u4e00\u7684\u9aa8\u8089\uff0c\u662f\u5979\u7684\u8840\u4e2d\u4e4b\u8840\uff0c\u8089\u4e2d\u4e4b\u8089\uff0c\u662f\u5979\u5728\u8fd9\u4e16\u4e0a\u552f\u4e00\u7684\u5b89\u6170\uff0c\u552f\u4e00\u7684\u5bc4 \u6258\u3002 \u3000\u3000\u5979\u672c\u6765\u5b81\u6b7b\u4e5f\u4e0d\u613f\u79bb\u5f00\u4ed6\u7684\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u2026\u2026 \u3000\u3000\u6797\u8bd7\u97f3\u731b\u7136\u56de\u8eab\uff0c\u5c06\u706f\u82af\u6311\u8d77\u3002 \u3000\u3000\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u90fd\u8981\u518d\u770b\u4ed6\u51e0\u773c\uff0c\u591a\u770b\u4ed6\u51e0\u773c\uff0c\u4ee5\u540e\u2026\u2026\u201d \u3000\u3000\u4ee5\u540e\u7684\u4e8b\u5979\u4e0d\u6562\u518d\u60f3\uff0c\u4e0d\u5fcd\u518d\u60f3\u3002 \u3000\u3000\u5979\u773c\u6cea\u5df2\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u773c\u775b\u867d\u7136\u95ed\u5f97\u5f88\u7d27\uff0c\u4f46\u773c\u89d2\u4f3c\u4e5f\u6709\u6cea\u75d5\u7559\u4e0b\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u7a81\u7136\u53d1\u6296\uff0c\u662f\u592a\u51b7\uff1f\u8fd8\u662f\u5728\u505a\u5669\u68a6\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u4fef\u4e0b\u8eab\uff0c\u60f3\u4e3a\u4ed6\u5c06\u88ab\u62c9\u7d27\u4e9b\u3002 \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\u88ab\u662f\u6e7f\u7684\uff0c\u9f99\u5c0f\u4e91\u7684\u8863\u670d\u4e5f\u662f\u6e7f\u7684\uff0c\u6e7f\u900f\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u6014\u4f4f\uff0c\u6014\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u51fa\u53bb\u8fc7\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u8fd8\u662f\u95ed\u7740\u773c\uff0c\u95ed\u7740\u5634\uff0c\u95ed\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e00\u76f4\u90fd\u5728\u540e\u9762\u8ddf\u7740\u6211\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u6211\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u4f60\u4e5f\u5168\u90fd\u542c\u89c1\u4e86\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5ffd\u7136\u5f80\u88ab\u7a9d\u91cc\u62ff\u51fa\u4e2a\u7528\u6cb9\u7eb8\u5305\u7740\u7684\u5c0f\u5305\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u9053\uff1a\u201c\u62ff\u53bb\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u8fd8\u662f\u95ed\u7740\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\uff1f\u4f60\u5c82\u975e\u6b63\u662f\u4e3a\u4e86\u8981\u62ff\u8fd9\u4e1c\u897f\u624d\u56de\u6765\u7684\u4e48 \uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u76ee\u4e2d\u9732\u51fa\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u662f\u56de\u6765\u770b\u4f60\u7684\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e3a\u4e86\u8fd9\u4e1c\u897f\uff0c\u4f60\u8fd8\u4f1a\u56de\u6765\u770b\u6211\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5f20\u5f00\u773c\u775b\uff0c\u76ef\u7740\u4ed6\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u4ed6\u76ee\u4e2d\u4e5f\u5145\u6ee1\u4e86\u75db\u82e6\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4f60\u672c\u5c31\u6253\u7b97\u79bb\u5f00\u6211\uff0c\u82e5\u4e0d\u662f\u4e3a\u4e86\u8fd9\u6837\u4e1c\u897f\uff0c\u4f60\u53ea\u6015\u65e9 \u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9eef\u7136\u9053\uff1a\u201c\u6211\u7684\u786e\u51c6\u5907\u5230\u4e00\u4e2a\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u53bb\uff0c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u8981\u5230\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4f60\u8981\u53bb\u6551\u674e\u5bfb\u6b22\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u53c8\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u560e\u58f0\u9053\uff1a\u201c\u4f60\u51c6\u5907\u7528\u8fd9\u672c\u2018\u601c\u82b1\u5b9d\u9274\u2019\u53bb\u6551\u674e\u5bfb\u6b22\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ed6\u5c06\u624b\u91cc\u7684\u6cb9\u7eb8\u5305\u629b\u5230\u6797\u8bd7\u97f3\u9762\u524d\uff0c\u5636\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u62ff\u53bb\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u53bb \uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u8eab\u5b50\u6447\u4e86\u6447\uff0c\u4f3c\u5df2\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u6709\u4e86\u8fd9\u672c\u2018\u601c\u82b1\u5b9d\u9274\u2019\uff0c\u4e0a\u5b98\u91d1\u8679\u4e00\u5b9a\u4f1a\u89c1\u4f60\u7684\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u662f\u7ec3\u6b66\u7684\uff0c\u89c1 \u4e86\u8fd9\u4e1c\u897f\u4e5f\u4f1a\u5fc3\u52a8\u3002\u201d \u3000\u3000\u4ed6\u54ac\u7740\u7259\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u60f3\u5229\u7528\u8fd9\u673a\u4f1a\u8ddf\u4ed6\u62fc\u547d\uff0c\u4f46\u4f60\u5f53\u7136\u4e5f\u77e5\u9053\u8981\u4ed6\u6b7b\u5e76\u4e0d\u5bb9\u6613\uff0c \u6240\u4ee5\u4f60\u8fd9\u4e48\u505a\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u5c06\u4ed6\u5148\u62b1\u4f4f\uff0c\u80fd\u5c06\u4ed6\u591a\u62b1\u4f4f\u4e00\u523b\uff0c\u674e\u5bfb\u6b22\u5c31\u80fd\u591a\u6d3b\u4e00\u523b\uff0c\u963f\u98de\u4e5f \u8bb8\u5c31\u80fd\u53ca\u65f6\u8d76\u53bb\u6551\u4ed6\uff01\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9eef\u7136\u65e0\u8bed\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u7684\u786e\u662f\u4e2a\u6781\u806a\u660e\u7684\u5b69\u5b50\uff0c\u6bcf\u53e5\u8bdd\u90fd\u8bf4\u5230\u5979\u5fc3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u5979\u5df2\u6ca1\u4ec0\u4e48\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u674e\u5bfb\u6b22\u7684\u786e\u5bf9\u4f60\u5f88\u597d\uff0c\u4f60\u4e3a\u4e86\u4ed6\u5c31\u7b97\u8fde\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u81ea\u5df1\u7684\u6027\u547d\u90fd\u4e0d\u8981\u4e86 \uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u8bf4\u4f60\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u4ed6\u6296\u5f97\u66f4\u5389\u5bb3\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6709\u6ca1\u6709\u66ff\u522b\u4eba\u60f3\u8fc7\uff0c\u6709\u6ca1\u6709\u66ff\u6211\u60f3\u8fc7\uff0c\u6211\u6bd5\u7adf\u662f\u4f60 \u7684\u513f\u5b50\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u6797\u8bd7\u97f3\u7684\u5fc3\u5c31\u50cf\u662f\u88ab\u9488\u5728\u523a\u7740\uff0c\u5fcd\u4e0d\u4f4f\u63e1\u7d27\u4e86\u5979\u513f\u5b50\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u66ff\u4f60\u60f3\u8fc7\uff0c \u6211\u2026\u2026\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u7528\u529b\u7529\u8131\u4e86\u5979\u7684\u624b\uff0c\u9053\uff1a\u201c\u4f60\u66ff\u6211\u60f3\u8fc7\uff0c\u6211\u77e5\u9053\uff0c\u4f60\u8981\u6211\u660e\u5929\u65e9\u4e0a\u5230\u90a3\u91cc\u53bb\u7b49\u4ed6 \u4eec\uff0c\u4f60\u65e2\u5df2\u4e3a\u4ed6\u6b7b\u4e86\uff0c\u4ed6\u4eec\u89c1\u5230\u6211\uff01\u81ea\u7136\u4e00\u5b9a\u4f1a\u597d\u597d\u7684\u7167\u987e\u6211\u3002\u201d \u3000\u3000\u4ed6\u560e\u58f0\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u4f60\u53c8\u600e\u77e5\u4e00\u5b9a\u80fd\u6551\u5f97\u4e86\u4ed6\u5462\uff0c\u4ed6\u82e5\u770b\u5230\u4f60\u6b7b\u4e86\uff0c\u5fc3\u91cc\u5c82\u975e\u66f4\u4e71\uff0c \u66f4\u96be\u53d7\uff0c\u5c31\u7b97\u963f\u98de\u80fd\u8d76\u53bb\uff0c\u4ed6\u4e5f\u672a\u5fc5\u80fd\u6d3b\u5f97\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u7684\u8eab\u5b50\u4e5f\u5df2\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u5c31\u7b97\u4ed6\u80fd\u6d3b\u4e0b\u53bb\uff0c\u5c31\u7b97\u4ed6\u80af\u7167\u987e\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8ddf\u7740\u4ed6\u7684\uff0c\u6211\u6839\u672c\u8fde \u770b\u90fd\u4e0d\u613f\u770b\u4ed6\u4e00\u773c\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u51c4\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u6068\u4ed6\uff01\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f46\u662f\u4f60\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u53c8\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u6068\u4ed6\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u5e9f\u4e86\u6211\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u5636\u58f0\u9053\uff1a\u201c\u6211\u6068\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u662f\u6211\u7684\u7236\u4eb2\uff0c\u6211\u4e5f\u6068\u6211\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u662f\u4ed6\u7684\u513f\u5b50\uff0c \u6211\u82e5\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u4f60\u5c82\u975e\u5c31\u4e0d\u4f1a\u79bb\u5f00\u6211\u4e86\uff0c\u4e00\u5207\u4e8b\u5c82\u975e\u5168\u90e8\u4f1a\u597d\u5f97\u591a\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u4f0f\u5728\u6795\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u5fc3\u5df2\u788e\u4e86\uff0c\u6574\u4e2a\u4eba\u5df2\u5d29\u6e83\u3002 \u3000\u3000\u5979\u53ea\u89c9\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u7ec8\u4e8e\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u8eab\u540e\u7684\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u82e5\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u4ed6\u82e5\u662f\u6211\u7684\u4e08\u592b\u2026\u2026\u201d \u3000\u3000\u8fd9\u5ff5\u5934\u5979\u8fde\u60f3\u90fd\u4e0d\u6562\u53bb\u60f3\uff0c\u4f46\u5728\u5979\u5fc3\u5e95\u6df1\u5904\uff0c\u5979\u53c8\u4f55\u5c1d\u6ca1\u6709\u5077\u5077\u7684\u60f3\u8fc7\uff1f \u3000\u3000\u4e0d\u5e78\u7684\u7236\u6bcd\uff0c\u751f\u51fa\u6765\u7684\u5b69\u5b50\u66f4\u4e0d\u5e78\uff0c\u66f4\u75db\u82e6\u3002 \u3000\u3000\u4f46\u9519\u7684\u53ea\u662f\u7236\u6bcd\uff0c\u5b69\u5b50\u5e76\u6ca1\u9519\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u8981\u8ddf\u7740\u53d7\u60e9\u7f5a\uff0c\u8ddf\u7740\u53d7\u82e6\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u6323\u624e\u7740\u722c\u8d77\uff0c\u6251\u5728\u5979\u513f\u5b50\u8eab\u4e0a\uff0c\u6cea\u5982\u96e8\u4e0b\uff0c\u560e\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u5bf9\u4e0d \u8d77\u4f60\u2026\u2026\u50cf\u6211\u4eec\u8fd9\u6837\u7684\u7236\u6bcd\uff0c\u505a\u6211\u4eec\u7684\u5b69\u5b50\u5b9e\u5728\u4e0d\u5bb9\u6613\u2026\u2026\u201d \u3000\u3000\u7a97\u5916\u7a81\u7136\u4f20\u4eba\u4e00\u58f0\u51c4\u51c9\u800c\u6c89\u91cd\u7684\u53f9\u606f\u3002 \u3000\u3000\u4e00\u4eba\u4fbf\u54bd\u7740\u9053\uff1a\u201c\u4f60\u5e76\u6ca1\u6709\u5bf9\u4e0d\u8d77\u4ed6\uff0c\u662f\u6211\u5bf9\u4e0d\u8d77\u4ed6\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u3002 \u3000\u3000\u4ee5\u524d\u89c1\u8fc7\u4ed6\u7684\u4eba\uff0c\u7edd\u5bf9\u60f3\u4e0d\u5230\u4ed6\u4e5f\u4f1a\u53d8\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u5982\u6b64\u6194\u60b4\u3002 \u3000\u3000\u4ed6\u5c31\u7ad9\u5728\u95e8\u53e3\uff0c\u7adf\u4f3c\u6ca1\u6709\u52c7\u6c14\u8d70\u8fdb\u8fd9\u5c4b\u5b50\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u62ac\u8d77\u5934\uff0c\u5634\u5507\u52a8\u4e86\u52a8\uff0c\u4eff\u4f5b\u60f3\u5524\u4ed6\u4e00\u58f0\uff1a\u201c\u7239\u201d\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6ca1\u6709\u53d1\u51fa\u58f0\u97f3\u6765\uff01 \u3000\u3000\u9f99\u5578\u4e91\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u4e0d\u613f\u505a\u6211\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u6dec\u7136\u56de\u9996\u3002 \u3000\u3000\u9f99\u5578\u4e91\u76ee\u5149\u8f6c\u5411\u5979\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u613f\u505a\u6211\u7684\u59bb\u5b50\u3002\u6211\u8fd9\u4eba\u6d3b\u7740\u672c\u5c31\u662f\u591a\u4f59\u7684 \u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u9f99\u5578\u4e91\u4e0d\u8ba9\u5979\u8bf4\u8bdd\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\u6211\u5374\u4e00\u5fc3\u8981\u505a\u4f60\u4eec\u7684\u597d\u7236\u4eb2\uff0c\u4f60\u4eec\u7684\u597d\u4e08\u592b\uff0c\u53ea\u4e0d\u8fc7 \u2026\u2026\u770b\u6765\u6211\u5e76\u6ca1\u6709\u505a\u597d\uff0c\u6211\u4ec0\u4e48\u4e8b\u5168\u90e8\u505a\u9519\u4e86\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u672c\u662f\u4e2a\u6700\u8bb2\u7a76\u8863\u7740\uff0c\u6700\u7740\u610f\u4fee\u9970\u7684\u4eba\uff0c\u4ed6\u672c\u6765\u4e5f\u662f\u4e2a\u76f8\u8c8c\u5802\u5802\u7684\u7537\u5b50\u6c49\uff0c\u6c38\u8fdc\u90fd\u751f\u6c14 \u52c3\u52c3\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5462\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u5fc3\u91cc\u5ffd\u4e5f\u6d8c\u8d77\u4e00\u79cd\u601c\u60dc\u4e4b\u610f\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u4e5f\u5bf9\u4e0d\u8d77\u4f60\uff0c\u6211\u4e5f\u6ca1\u6709\u505a\u4f60\u7684\u597d\u59bb\u5b50 \u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u80fd\u602a\u4f60\uff0c\u53ea\u602a\u6211\uff0c\u6211\u82e5\u6ca1\u6709\u9047\u89c1\u4f60\uff0c\u6ca1\u6709\u9047\u89c1 \u674e\u5bfb\u6b22\uff0c\u4f60\u4eec\u5168\u90e8\u4e0d\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff0c\u5168\u90e8\u4f1a\u5f88\u5e78\u798f\u3002\u201d \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u7684\u547d\u8fd0\u5c82\u975e\u5305\u662f\u56e0\u6b64\u800c\u6539\u53d8\u7684\uff1f \u3000\u3000\u4ed6\u82e5\u6ca1\u6709\u9047\u5230\u674e\u5bfb\u6b22\uff0c\u5c82\u975e\u56f1\u4e0d\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u6797\u8bd7\u97f3\u6cea\u53c8\u6d41\u4e0b\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u4f60\u81f3\u5c11\u4e5f\u662f\u4e3a\u4e86\u8981\u4fdd\u62a4\u4f60\u7684\u5bb6\uff0c\u4fdd\u62a4\u4f60\u7684 \u59bb\u5b50\uff0c\u6240\u4ee5\u2026\u2026\u4f60\u4e5f\u6ca1\u6709\u9519\uff0c\u6211\u7edd\u4e0d\u80fd\u602a\u4f60\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u51c4\u7136\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u4eec\u90fd\u6ca1\u6709\u9519\uff0c\u90a3\u4e48\u9519\u7684\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u76ee\u5149\u832b\u7136\u9065\u89c6\u7740\u7a97\u5916\u7684\u98ce\u96e8\uff0c\u5583\u5583\u9053\uff1a\u201c\u9519\u7684\u662f\u8c01\u5462\uff1f\u2026\u2026\u9519\u7684\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ed6\u65e0\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u672c\u5c31\u6709\u8bb8\u591a\u4e8b\u662f\u4eba\u4eec\u65e0\u6cd5\u89e3\u91ca\uff0c\u65e0\u6cd5\u56de\u7b54\u7684\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7f13\u7f13\u9053\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u518d\u6765\u89c1\u4f60\u4eec\u7684\uff0c\u8fd9\u6b21\u4f60\u51fa\u6765\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u5df2\u4e0b\u4e86\u51b3\u5fc3\u8981\u79bb\u5f00 \u6211\uff0c\u6240\u4ee5\u6211\u65e2\u6ca1\u6709\u529d\u4f60\u7559\u4e0b\uff0c\u4e5f\u4e0d\u60f3\u6c42\u4f60\u56de\u53bb\uff0c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u957f\u53f9\uff0c\u6d41\u6cea\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4e5f\u77e5\u9053\u6211\u6240\u505a\u7684\u90a3\u4e9b\u4e8b\uff0c\u4e0d\u4f46\u4ee4\u4f60\u4f24\u5fc3\uff0c\u4e5f\u4ee4\u4f60\u5931\u671b\uff0c\u4f46\u6211 \u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u6109\u5077\u7684\u8ddf\u4f60\u4eec\u4e00\u9f50\u51fa\u6765\uff0c\u53ea\u8981\u80fd\u8fdc\u8fdc\u7684\u770b\u4f60\u4eec\u4e00\u773c\uff0c\u6211\u5c31\u6ee1\u8db3\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u5931\u58f0\u75db\u54ed\uff0c\u9053\uff1a\u201c\u6c42\u6c42\u4f60\u4e0d\u8981\u518d\u8bf4\u4e86\uff0c\u6c42\u6c42\u4f60\u2026\u2026\u201d \u3000\u3000\u9f99\u5578\u4e91\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u7684\u786e\u4e0d\u8be5\u518d\u8bf4\u4e86\uff0c\u56e0\u4e3a\u73b0\u5728\u65e0\u8bba\u8bf4\u4ec0\u4e48\u90fd\u5df2\u592a\u8fdf\u3002\u201d \u3000\u3000\u6797\u8bd7\u97f3\u6d41\u6cea\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff0c\u6211\u6b20\u4ed6\u7684\u5927\u591a\uff0c\u6211\u4e0d\u80fd\u773c\u770b\u7740\u4ed6\u6b7b\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u6211\u4e5f\u6b20\u4ed6\u7684\uff0c\u6b20\u5f97\u66f4\u591a\uff0c\u6240\u4ee5\uff0c\u6709\u4e9b\u4e8b\u4f60\u5e94\u8be5\u8ba9\u6211\u53bb\u505a\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u5ffd\u7136\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u560e\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u505a\u4ec0\u4e48\uff1f\u4f60\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u9f99\u5578\u4e91\u5ffd\u7136\u51fa\u624b\uff0c\u70b9\u4e86\u5979\u7684\u7a74\u9053\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4e5f\u4e0d\u5e94\u8be5\u6b7b\uff0c\u8be5\u6b7b\u7684\u662f\u6211\uff0c \u6211\u6d3b\u7740\uff0c\u5927\u5bb6\u90fd\u75db\u82e6\uff0c\u6211\u6b7b\u4e86\uff0c\u4f60\u4eec\u53cd\u800c\u80fd\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u4e00\u628a\u63e1\u8d77\u4e86\u90a3\u672c\u7528\u6cb9\u7eb8\u5305\u7740\u7684\u201c\u601c\u82b1\u5b9d\u9274\u201d\uff0c\u4eba\u5df2\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u8bdd\u58f0\u81ea\u98ce\u4e2d\u8fdc\u8fdc\u4f20\u6765\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u597d\u597d\u7167\u987e\u4f60\u7684\u6bcd\u4eb2\uff0c\u81f3\u4e8e\u6211\u8fd9\u7236\u4eb2\u2026\u2026\u4f60\u627f \u4e0d\u627f\u8ba4\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u77aa\u5927\u4e86\u773c\u775b\uff0c\u671b\u7740\u95e8\u5916\u7684\u98ce\u96e8\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u518d\u6d41\u6cea\u3002 \u3000\u3000\u4f46\u4ed6\u90a3\u79cd\u773c\u795e\uff0c\u5374\u6bd4\u6d41\u6cea\u66f4\u4ee4\u4eba\u5fc3\u788e\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u5ffd\u7136\u653e\u58f0\u5927\u547c\uff0c\u9053\uff1a\u201c\u6211\u627f\u8ba4\uff0c\u53ea\u6709\u4f60\u624d\u662f\u6211\u7684\u7236\u4eb2\uff0c\u6211\u4e5f\u53ea\u613f\u610f \u505a\u4f60\u7684\u513f\u5b50\uff0c\u9664\u4e86\u4f60\uff0c\u4ec0\u4e48\u4eba\u6211\u90fd\u4e0d\u8981\uff0c\u65e0\u8bba\u4ec0\u4e48\u4eba\u2026\u2026\u201d \u3000\u3000\u8fd9\u662f\u513f\u5b50\u5bf9\u7236\u4eb2\u7684\u5fcf\u6094\uff0c\u4e5f\u662f\u7236\u5b50\u95f4\u72ec\u6709\u7684\u611f\u60c5\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u4ee3\u66ff\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u505a\u7236\u4eb2\u7684\u5df2\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u53ea\u8981\u662f\u4eba\uff0c\u90fd\u6709\u89c9\u609f\u7684\u65f6\u5019\u3002 \u3000\u3000\u7eb5\u7136\u4ed6\u89c9\u609f\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u5df2\u88ab\u903c\u5f97\u8d70\u6295\u65e0\u8def\uff0c\u4e5f\u8fd8\u662f\u540c\u6837\u503c\u5f97\u5c0a\u656c\u3002 \u3000\u3000\u8840\u6d53\u4e8e\u6c34\u3002 \u3000\u3000\u53ea\u6709\u8840\u624d\u80fd\u6d17\u6e05\u4e00\u5207\u7f9e\u4fae\u4e00\u5207\u4ec7\u6068\u3002 \u3000\u3000\u751f\u547d\u7684\u5f52\u5bbf\u662f\u8840\u3002 \u3000\u3000\u4f46\u65b0\u7684\u751f\u547d\uff0c\u4e5f\u6b63\u662f\u5728\u8840\u4e2d\u8bde\u751f\u7684\u3002 \u7b2c\u516b\u5341\u516d\u7ae0\u3000\u8840\u6d17\u4e00\u8eab\u5b7d>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u516d\u7ae0\u3000\u8840\u6d17\u4e00\u8eab\u5b7d \u3000\u3000\u8fd9\u662f\u5ea7\u5f88\u5e7f\u9614\u7684\u5e84\u9662\u3002 \u3000\u3000\u8fd9\u5ea7\u5e84\u9662\u770b\u6765\u548c\u522b\u7684\u8c6a\u5bcc\u4eba\u5bb6\u7684\u5e84\u9662\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u4f46\u4f60\u53ea\u8981\u8d70\u8fdb\u4e9b\uff0c\u4e00\u8d70\u4e0a\u5927\u95e8\u524d\u7684\u53f0\u9636\uff0c\u4f60\u5c31\u4f1a\u7acb\u523b\u89c9\u5f97\u6709\u79cd\u9634\u68ee\u68ee\u7684\u6740\u6c14\uff0c\u4ee4\u4eba\u4e0d\u5bd2 \u800c\u6817\u3002 \u3000\u3000\u9f99\u5578\u4e91\u5df2\u8d70\u4e0a\u4e86\u53f0\u9636\u3002 \u3000\u3000\u9662\u5b50\u91cc\u9759\u6084\u6084\u7684\uff0c\u4eff\u4f5b\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\uff0c\u4f46\u4ed6\u4e00\u8e0f\u4e0a\u53f0\u9636\uff0c\u5ffd\u7136\u95f4\u5c31\u6709\u5341\u51e0\u4e2a\u4eba\u5e7d\u7075\u822c\u7684 \u51fa\u73b0\u4e86\u3002 \u3000\u3000\u662f\u5341\u516b\u4e2a\u9ec4\u8863\u4eba\uff0c\u9f99\u5578\u4e91\u6839\u672c\u65e0\u6cd5\u5206\u8fa8\u4ed6\u4eec\u7684\u9762\u76ee\u3002 \u3000\u3000\u4f46\u8fd9\u5e76\u4e0d\u91cd\u8981\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u4e0d\u5fc5\u5206\u8fa8\u8fd9\u4e9b\u4eba\u7684\u9762\u76ee\u2014\u2014\u6240\u6709\u91d1\u94b1\u5e2e\u7684\u5c5e\u4e0b\uff0c\u51e0\u4e4e\u90fd\u662f\u5b8c \u5168\u4e00\u6837\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u5634\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u8bf4\u8bdd\uff0c\u7eb5\u7136\u8bf4\u8bdd\uff0c\u4e5f\u90fd\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u773c\u775b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u7528\u770b\u2014\u2014\u4ed6\u4eec\u80fd\u770b\u5f97\u5230\uff0c\u4e5f\u5168\u90fd\u662f\u4e0a\u5b98\u91d1\u8679\u8981\u4ed6\u4eec\u770b\u7684 \u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u6709\u4e00\u4e2a\u5f88\u5c0f\u7684\u8033\u6735\uff0c\u56e0\u4e3a\u4ed6\u4eec\u53ea\u505c\u5f97\u89c1\u4e0a\u5b98\u91d1\u8679\u4e00\u4e2a\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u6ca1\u6709\u7075\u9b42\uff0c\u4fbf\u6bcf\u4e2a\u4eba\u7684\u56db\u80a2\u90fd\u5f88\u7075\u654f\uff0c\u5728\u4e00\u5239\u90a3\u95f4\u5df2\u5c06\u9f99\u5578\u4e91\u56f4\u4f4f\u3002 \u3000\u3000\u9f99\u5578\u4e91\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u91d1\u94b1\u5e2e\u7684\u603b\u8235\u679c\u7136\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u6709\u4eba\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u627e\u4eba\u3002\u201d \u3000\u3000\u6709\u4eba\u9053\uff1a\u201c\u627e\u8c01\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5e2e\u4e3b\u4e0a\u5b98\u91d1\u8679\u662f\u4e0d\u662f\u5df2\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0a\u5b98\u91d1\u8679\u201d\u8fd9\u540d\u5b57\u5c31\u4f3c\u6709\u79cd\u795e\u6c14\u7684\u9b54\u529b\uff0c\u4ed6\u4eec\u7684\u6001\u5ea6\u7acb\u523b\u6539\u53d8\u4e86\u4e9b\u3002 \u3000\u3000\u201c\u5e2e\u4e3b\u5df2\u56de\u6765\u4e86\uff0c\u8bf7\u95ee\u8db3\u4e0b\u2026\u2026\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u6211\u8981\u89c1\u4ed6\uff0c\u6709\u6837\u4e1c\u897f\u60f3\u9001\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u201c\u8bf7\u7a0d\u5019\uff0c\u5e2e\u4e3b\u73b0\u5728\u4e0d\u89c1\u5ba2\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u53c8\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u8fd8\u548c\u674e\u5bfb\u6b22\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u90a3\u4e48\u6211\u73b0\u5728\u5c31\u8981\u89c1\u4ed6\u3002\u201d \u3000\u3000\u201c\u8bf7\u95ee\u5c0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u5389\u58f0\u9053\uff1a\u201c\u59d3\u9f99\uff0c\u6211\u6709\u6837\u6781\u91cd\u8981\u7684\u4e1c\u897f\u73b0\u5728\u975e\u4ea4\u7ed9\u4ed6\u4e0d\u53ef\uff0c\u4f60\u4eec\u82e5\u662f\u803d\u8bef\u4e86\u5927\u4e8b \uff0c\u8fd9\u8d23\u4efb\u8c01\u80fd\u62c5\u5f53\u5f97\u8d77\uff1f\u201d \u3000\u3000\u201c\u59d3\u9f99\u2026\u2026\u524d\u51e0\u5929\u8981\u548c\u5e2e\u4e3b\u7ed3\u62dc\u7684\uff0c\u83ab\u975e\u662f\u4f60\uff1f\u201d \u3000\u3000\u9f99\u5578\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u201d\u5b57\u521a\u51fa\u53e3\uff0c\u5bd2\u5149\u5df2\u98de\u8d77\u3002 \u3000\u3000\u4e00\u628a\u5200\uff0c\u4e24\u67c4\u5251\uff0c\u540c\u65f6\u95ea\u7535\u822c\u5411\u4ed6\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9f99\u5578\u4e91\u6012\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u662f\u8981\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u7684\u559d\u58f0\u867d\u7136\u4eae\uff0c\u5374\u6ca1\u6709\u4eba\u518d\u542c\uff0c\u4e5f\u6ca1\u6709\u4eba\u518d\u56de\u7b54\u3002 \u3000\u3000\u9f99\u5578\u4e91\u72c2\u543c\uff0c\u6325\u62f3\u3002 \u3000\u3000\u4ed6\u7684\u6b66\u529f\u5e76\u4e0d\u5f31\uff0c\u4ed6\u7684\u62f3\u6cd5\u521a\u731b\u8fc5\u6025\uff0c\u4e00\u62f3\u53d1\u51fa\uff0c\u864e\u864e\u751f\u5a01\u3002 \u3000\u3000\u4f46\u4ed6\u53ea\u6709\u4e00\u53cc\u62f3\u3002 \u3000\u3000\u5bf9\u65b9\u7684\u5175\u5203\u5374\u6709\u4e8c\u5341\u4e8c\u4ef6\u2014\u2014\u5176\u4e2d\u6709\u94a9\uff0c\u53cc\u5251\uff0c\u53cc\u97ad\uff0c\u53cc\u7b14\u3002 \u3000\u3000\u7b14\u6700\u77ed\uff0c\u4e5f\u6700\u9669\uff0c\u4f7f\u5f97\u8d6b\u7136\u6b63\u662f\u6614\u65e5\u201c\u751f\u6b7b\u5251\u201d\u5ae1\u4f20\u7684\u6253\u7a74\u5fc3\u6cd5\uff0c\u8fd9\u4eba\u5728\u5175\u5668\u8c31\u4e2d\u7684\u6392 \u540d\uff0c\u7edd\u4e0d\u4f1a\u5728\u201c\u98ce\u96e8\u53cc\u6d41\u661f\u201d\u5411\u677e\u4e4b\u4e0b\u3002 \u3000\u3000\u5251\u662f\u677e\u7eb9\u5251\uff0c\u5251\u6cd5\u9690\u7136\u6709\u53e4\u610f\uff0c\u51fa\u624b\u8427\u758f\uff0c\u610f\u5728\u5251\u5148\u3002 \u3000\u3000\u5f53\u4ee3\u4f7f\u5251\u7684\u9ad8\u624b\uff0c\u7edd\u4e0d\u4f1a\u6709\u5341\u4eba\u4ee5\u4e0a\u80fd\u80dc\u5f97\u8fc7\u4ed6\u3002 \u3000\u3000\u6700\u72e0\u7684\u8fd8\u662f\u5200\u3002 \u3000\u3000\u4e5d\u73af\u5200\uff0c\u73af\u58f0\u4e00\u9707\u4e00\u6d88\u9b42\uff0c\u4e03\u5200\u5288\u4e0b\uff0c\u5200\u98ce\u5df2\u7b3c\u7f69\u9f99\u5578\u4e91\u3002 \u3000\u3000\u5224\u5b98\u7b14\u5c31\u6253\u4e0a\u4e86\u9f99\u5578\u4e91\u7684\u7a74\u9053\u3002 \u3000\u3000\u6ca1\u6709\u547c\u58f0\uff0c\u6ca1\u6709\u547b\u541f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u5589\u7ba1\u4ee5\u88ab\u523a\u7a7f\uff0c\u58f0\u5e26\u5df2\u88ab\u780d\u65ad\u3002 \u3000\u3000\u53ea\u6709\u8840\u3002 \u3000\u3000\u8840\uff0c\u7bad\u4e00\u822c\u81ea\u4ed6\u7684\u5589\u7ba1\u6d41\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u8840\u521a\u597d\u6d12\u843d\u5728\u4ed6\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u6b7b\u4e0d\u7791\u76ee\u3002 \u3000\u3000\u9f99\u5578\u4e91\u7684\u773c\u775b\u8fd8\u662f\u5728\u77aa\u7740\u4ed6\u4eec\uff0c\u773c\u73e0\u5b50\u4f3c\u5df2\u51f8\u51fa\u3002 \u3000\u3000\u4ed6\u672c\u662f\u4e3a\u4e86\u6c42\u6b7b\u800c\u6765\uff0c\u53ef\u662f\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u89c1\u4e0a\u5b98\u91d1\u8679\u4e00\u9762\uff1f \u3000\u3000\u56e0\u4e3a\u201c\u770b\u5230\u9f99\u5578\u4e91\u5c31\u6740\uff01\u201d\u8fd9\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u547d\u4ee4\uff01 \u3000\u3000\u56e0\u4e3a\u65e0\u8bba\u4ec0\u4e48\u4eba\uff0c\u90fd\u4e0d\u80fd\u8ba9\u4ed6\u8d70\u8fdb\u9662\u5b50\u4e00\u6b65\uff01 \u3000\u3000\u8fd9\u4e5f\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u547d\u4ee4\uff01 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6c38\u8fdc\u4ee4\u51fa\u5982\u5c71\uff01 \u3000\u3000\u7528\u6cb9\u7eb8\u5305\u7684\u201c\u601c\u82b1\u5b9d\u9274\u201d\uff0c\u81ea\u6000\u91cc\u6389\u4e86\u51fa\u6765\uff0c\u4e5f\u5df2\u88ab\u8840\u67d3\u7ea2\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u5b83\u4e00\u773c\u3002 \u3000\u3000\u50cf\u9f99\u5578\u4e91\u8fd9\u79cd\u4eba\u8eab\u4e0a\u7684\u4e1c\u897f\uff0c\u53c8\u600e\u4f1a\u88ab\u4eba\u91cd\u89c6\uff1f \u3000\u3000\u4e8e\u662f\u8fd9\u672c\u795e\u6c14\u7684\u201c\u601c\u82b1\u5b9d\u9274\u201d\u4e5f\u548c\u4e16\u4e0a\u5176\u4ed6\u8bb8\u591a\u6b66\u529f\u5bc6\u7b08\u4e00\u6837\uff0c\u4ece\u6b64\u7edd\u4f20\u3002 \u3000\u3000\u8fd9\u662f\u4eba\u7c7b\u7684\u5e78\u8fd0\uff1f\u8fd8\u662f\u4e0d\u5e78\uff1f \u3000\u3000\u6cb9\u7eb8\u5305\u53c8\u88ab\u585e\u5165\u9f99\u5578\u4e91\u6000\u91cc\uff0c\u5c38\u4f53\u88ab\u62ac\u8d70\u3002 \u3000\u3000\u91d1\u94b1\u5e2e\u5c5e\u4e0b\u5bf9\u4e8e\u5904\u7406\u6b7b\u4eba\u7684\u5c38\u4f53\u4e5f\u662f\u4e13\u5bb6\uff0c\u4ed6\u4eec\u5904\u7406\u5c38\u4f53\u6709\u4e00\u5957\u5f88\u7b80\u5355\uff0c\u4e5f\u5f88\u7279\u522b\u7684\u65b9 \u6cd5\u3002 \u3000\u3000\u4eba\uff0c\u7684\u786e\u5f88\u5947\u7279\u3002 \u3000\u3000\u4ed6\u4eec\u5f80\u5f80\u4f1a\u4e3a\u4e86\u4e00\u4e9b\u83ab\u540d\u5176\u5999\u7684\u539f\u56e0\u53bb\u5bfb\u627e\uff0c\u53bb\u62a2\u593a\u67d0\u6837\u4e1c\u897f\uff0c\u751a\u81f3\u4e0d\u60dc\u62fc\u547d\uff0c\u4f46\u7b49\u5230 \u8fd9\u6837\u4e1c\u897f\u771f\u7684\u51fa\u73b0\u65f6\uff0c\u4ed6\u4eec\u5374\u5f80\u5f80\u4f1a\u4e0d\u8ba4\u5f97\uff0c\u5f80\u5f80\u4f1a\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u662f\u4eba\u7c7b\u7684\u611a\u6627\uff1f\u8fd8\u662f\u806a\u660e\uff1f \u3000\u3000\u963f\u98de\u6ca1\u6709\u5251\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e0d\u91cd\u8981\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53c8\u6709\u4e86\u52c7\u6c14\u548c\u4fe1\u5fc3\u3002 \u3000\u3000\u8def\u65c1\u6709\u7247\u7af9\u6797\uff0c\u7ad9\u5728\u8fd9\u91cc\uff0c\u5df2\u53ef\u770b\u89c1\u91d1\u94b1\u5e2e\u7684\u5bb6\u9662\u3002 \u3000\u3000\u963f\u98de\u780d\u4e0b\u6bb5\u7af9\u5b50\uff0c\u4ece\u4e2d\u95f4\u5256\u5f00\uff0c\u5256\u6210\u4e09\u7247\uff0c\u524a\u5c16\uff0c\u6495\u4e0b\u6761\u8863\u895f\uff0c\u7f20\u4f4f\u6ca1\u6709\u524a\u5c16\u7684\u4e00\u7aef\uff0c \u7b97\u505a\u5251\u67c4\u3002 \u3000\u3000\u4ed6\u7684\u52a8\u4f5c\u5f88\u8fc5\u901f\uff0c\u5f88\u786e\u5b9e\uff0c\u7edd\u6ca1\u6709\u6d6a\u8d39\u4e00\u5206\u6c14\u529b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5f88\u7a33\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u76f4\u5728\u65c1\u8fb9\u9759\u9759\u7684\u77a7\u7740\uff0c\u4eff\u4f5b\u89c9\u5f97\u5f88\u65b0\u5947\uff0c\u5f88\u6709\u8da3\u3002 \u3000\u3000\u4f46\u5979\u8fd8\u662f\u4e0d\u514d\u6709\u4e9b\u6000\u7591\uff0c\u62ff\u8d77\u67c4\u7af9\u5251\uff0c\u6382\u4e86\u6382\uff0c\u8f7b\u5f97\u5c31\u50cf\u67f3\u53f6\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u7528\u8fd9\u6837\u7684\u5251\u4e5f\u80fd\u5bf9\u4ed8\u4e0a\u5b98\u91d1\u8679\uff1f\u201d \u7b2c\u516b\u5341\u4e03\u7ae0\u3000\u91cd\u751f>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u4e03\u7ae0\u3000\u91cd\u751f \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u534a\u54cd\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u65e0\u8bba\u7528\u4ec0\u4e48\u6837\u7684\u5251\u4e5f\u4e0d\u80fd\u5bf9\u4ed8\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u2026\u2026\u8981\u7528\u4ec0\u4e48\u624d\u80fd\u5bf9\u4ed8\u4ed6\uff1f\u201d \u3000\u3000\u963f\u98de\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8981\u7528\u4ec0\u4e48\u5bf9\u4ed8\u4e0a\u5b98\u91d1\u8679\uff0c\u53ef\u662f\u4ed6\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8bf4\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9664\u4e86\u4e0a\u5b98\u91d1\u8679\u5916\uff0c\u4f60\u4e5f\u8bb8\u8fd8\u8981\u5bf9\u4ed8\u5f88\u591a\u4eba\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u53ea\u95ee\u4f60\uff0c\u4e0a\u5b98\u91d1\u8679\u662f\u4e0d\u662f\u5df2\u56de\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u60f3\u51b3\u4e0d\u4f1a\u9519\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4ed6\u5728\u8fd9\u5730\u65b9\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u90fd\u7edd\u4e0d\u4f1a\u6709\u4eba\u770b\u5230\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u80fd\u6740\u674e\u5bfb\u6b22\uff0c\u5e76\u4e0d\u4e22\u4eba\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u613f\u88ab\u4eba\u770b\u5230\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53c8\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5728\u505a\u4ed6\u559c\u6b22\u505a\u7684\u4e8b\u65f6\uff0c\u5f80\u5f80\u90fd\u4e0d\u613f\u88ab\u4eba\u770b\u5230\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u6700\u559c\u6b22\u5403\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ec0\u4e48\u90fd\u559c\u6b22\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u6700\u559c\u6b22\u5403\u6838\u6843\uff0c\u6bcf\u6b21\u5403\u6838\u6843\u7684\u65f6\u5019\uff0c\u6211\u90fd\u89c9\u5f97\u662f\u79cd\u4eab\u53d7\uff0c\u5c24\u5176\u662f\u51ac\u5929\u7684 \u665a\u4e0a\uff0c\u4e00\u4e2a\u4eba\u8eb2\u5728\u80cc\u7a9d\u5077\u5077\u7684\u5403\u3002\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u82e5\u6709\u8bb8\u591a\u4eba\u5728\u65c1\u8fb9\u773c\u7741\u7741\u7684\u77a7\u7740\u6211\u5403\uff0c\u90a3\u5c31\u4e0d\u662f\u4eab\u53d7\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u549b\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4e0a\u5b98\u91d1\u8679\u5c06\u6740\u4ed6\u5f53\u4f5c\u4eab\u53d7\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u53f9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u80fd\u786e\u5b9a\u4e0a\u5b98\u91d1\u8679\u7edd\u4e0d\u4f1a\u5f88\u5feb\u7684\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u5047\u5982\u6211\u53ea\u6709\u4e00\u4e2a\u6838\u6843\uff0c\u6211\u4e00\u5b9a\u56de\u7559\u7740\u6162\u6162\u7684\u5403\uff0c\u5403\u5f97\u8d8a\u6162\uff0c\u6211\u4eab\u53d7\u7684\u65f6\u5019 \u8d8a\u957f\uff0c\u5403\u5b8c\u7684\u65f6\u5019\uff0c\u6211\u603b\u4f1a\u89c9\u5f97\u6709\u70b9\u96be\u53d7\u3002\u201d \u3000\u3000\u5176\u5b9e\u90a3\u79cd\u611f\u89c9\u5e76\u4e0d\u662f\u96be\u53d7\uff0c\u800c\u662f\u7a7a\u865a\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u201c\u7a7a\u865a\u201d\u8fd9\u4e24\u5b57\u5979\u4e5f\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u5979\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5728\u4e0a\u5b98\u91d1\u8679\u773c\u4e2d\uff0c\u8fd9\u4e16\u4e0a\u552f\u4e00\u7684\u654c\u4eba\u5c31\u662f\u674e\u5bfb\u6b22\uff0c\u6740\u4e86\u674e\u5bfb\u6b22\uff0c\u4ed6\u4e00\u5b9a \u4e5f\u4f1a\u6709\u6211\u5403\u5b8c\u6838\u6843\u90a3\u79cd\u611f\u89c9\uff0c\u800c\u4e14\u4e00\u5b9a\u6bd4\u6211\u66f4\u96be\u53d7\u5f97\u591a\u3002\u201d \u3000\u3000\u963f\u98de\u6162\u6162\u7684\u5c06\u5251\u63d2\u5165\u8170\u5e26\uff0c\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6740\u4e86\u4ed6\u51b3\u4e0d\u4f1a\u89c9\u5f97\u96be\u53d7\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5e76\u4e0d\u592a\u5feb\uff0c\u56e0\u4e3a\u4ed6\u8981\u51c6\u5907\u2014\u2014\u5bf9\u4ed8\u4e0a\u5b98\u91d1\u8679\u90a3\u6837\u7684\u4eba\uff0c\u5f53\u7136\u4e00\u5b9a\u8981\u5148\u4f5c\u51c6\u5907\u3002 \u3000\u3000\u8d70\u8def\u7684\u65f6\u5019\u4ed6\u5f80\u5f80\u4f1a\u89c9\u5f97\u56db\u80a2\u6e10\u6e10\u534f\u8c03\uff0c\u7d27\u5f20\u6e10\u6e10\u677e\u5f1b\uff0c\u8fd9\u6b63\u662f\u79cd\u6700\u597d\u7684\u51c6\u5907\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u8d70\u4e0a\u53f0\u9636\uff0c\u8d70\u8fdb\u95e8\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4eba\u5df2\u51fa\u73b0\u2014\u2014\u5341\u516b\u4e2a\u9ec4\u8863\u4eba\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u91d1\u94b1\u5e2e\u603b\u8235\u6240\u5728\u5730\u7684\u5b88\u8859\uff0c\u5f53\u7136\u4e5f\u5c31\u662f\u91d1\u94b1\u5e2e\u7684\u7cbe\u9510\u3002 \u3000\u3000\u963f\u98de\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u867d\u4e0d\u613f\u6740\u4eba\uff0c\u4e5f\u4e0d\u613f\u6709\u4eba\u6321\u6211\u7684\u8def\u3002\u201d \u3000\u3000\u4e00\u4eba\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0c\u6321\u4e86\u4f60\u7684\u8def\u80fd\u600e\u6837\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5c31\u5f97\u6b7b\uff01\u201d \u3000\u3000\u90a3\u4eba\u5927\u7b11\uff1a\u201c\u4f60\u8fde\u72d7\u90fd\u6740\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e0d\u6740\u72d7\uff0c\u4f60\u4e0d\u662f\u72d7\uff01\u201d \u3000\u3000\u6ca1\u6709\u5251\u5149\uff0c\u7af9\u5251\u6ca1\u6709\u5149\u3002 \u3000\u3000\u4f46\u7af9\u5251\u4e5f\u80fd\u6740\u4eba\u2014\u2014\u5728\u963f\u98de\u624b\u4e2d\u5c31\u80fd\u6740\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u8fd8\u6ca1\u6709\u7b11\u5b8c\uff0c\u54bd\u5589\u5df2\u88ab\u523a\u7a7f\u3002 \u3000\u3000\u73b0\u5728\u7af9\u5251\u6709\u4e86\u5149\u3002 \u3000\u3000\u8840\u5149\uff01 \u3000\u3000\u5224\u5b98\u7b14\uff0c\u53cc\u94a9\uff0c\u4e5d\u73af\u5200\uff0c\u4e94\u4ef6\u5175\u5203\u5e26\u7740\u98ce\u58f0\u51fb\u5411\u963f\u98de\uff01 \u3000\u3000\u4e24\u67c4\u9510\u5229\u7684\u5200\u53bb\u524a\u4ed6\u624b\u91cc\u7684\u5251\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5728\u62c5\u5fc3\uff0c\u5979\u77e5\u9053\u963f\u98de\u4e0e\u4eba\u4ea4\u624b\u7684\u7ecf\u9a8c\u5e76\u4e0d\u591a\uff0c\u7eb5\u7136\u548c\u4eba\u4ea4\u624b\uff0c\u4e5f\u5927\u90fd\u662f\u4e00\u5bf9\u4e00\uff0c \u5f88\u5c11\u88ab\u4eba\u5939\u51fb\u56f4\u653b\u3002 \u3000\u3000\u4ed6\u7684\u5251\u5bf9\u4ed8\u4e00\u4e2a\u4eba\u56fa\u7136\u5df2\u591f\u5feb\uff0c\u4f46\u82e5\u5bf9\u4ed8\u8fd9\u4e48\u591a\u4eba\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u60f3\u51b2\u8fc7\u53bb\uff0c\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u5979\u8fd8\u6ca1\u6709\u51b2\u8fc7\u53bb\uff0c\u5c31\u5df2\u770b\u5230\u4e09\u4e2a\u4eba\u5012\u4e0b\u3002 \u3000\u3000\u5979\u660e\u660e\u770b\u5230\u5200\u950b\u5df2\u524a\u53ca\u963f\u98de\u624b\u91cc\u7684\u7af9\u5251\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5012\u4e0b\u53bb\u7684\u504f\u504f\u4e0d\u662f\u963f\u98de\uff01 \u3000\u3000\u8fd9\u539f\u56e0\u53ea\u6709\u4f7f\u5224\u5b98\u7b14\u7684\u4eba\u81ea\u5df1\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u8ba4\u7a74\u4e00\u5411\u6781\u51c6\uff0c\u51fa\u624b\u4e00\u5411\u6781\u91cd\uff0c\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u81ea\u5df1\u660e\u660e\u5df2\u6253\u7740\u4e86\u963f\u98de\u7684\u7a74\u9053\u3002 \u3000\u3000\u4f46\u5c31\u5728\u4ed6\u7b14\u5c16\u89e6\u53ca\u963f\u98de\u8863\u886b\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4ed6\u5168\u8eab\u7684\u529b\u6c14\u7a81\u7136\u6d88\u5931\u3002 \u3000\u3000\u7af9\u5251\u5df2\u523a\u7a7f\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u963f\u98de\u5e76\u4e0d\u6bd4\u4ed6\u5feb\u5f88\u591a\uff0c\u53ea\u5feb\u4e00\u5206\u3002 \u3000\u3000\u4e00\u5206\u5c31\u5df2\u8db3\u591f\u4e86\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7ec8\u4e8e\u8fd8\u662f\u51b2\u4e86\u8fc7\u53bb\uff0c\u8eab\u5b50\u5c31\u50cf\u662f\u53ea\u7a7f\u82b1\u7684\u8774\u8776\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u7684\u5973\u5b50\u9ad8\u624b\uff0c\u7279\u957f\u5f80\u5f80\u662f\u8f7b\u529f\u548c\u6697\u5668\u4e00\u7c7b\uff0c\u8f83\u5c0f\u5de7\u800c\u4e14\u4e0d\u5403\u529b\u7684\u6b66\u529f\uff0c\u5f88\u5c11\u542c\u8bf4 \u6709\u5973\u5b50\u7684\u5185\u529b\u6df1\uff0c\u638c\u529b\u5f3a\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u5979\u6697\u5668\u7684\u51fa\u624b\u6781\u5feb\uff0c\u8eab\u6cd5\u66f4\u5feb\uff0c\u811a\u6b65\u7684\u53d8\u5316\u66f4\u5947\u8be1\u7e41\u590d\uff0c\u7b80\u76f4\u4ee4\u4eba\u65e0\u6cd5\u6349\u6478\u3002 \u3000\u3000\u5979\u59cb\u7ec8\u8ba4\u4e3a\u963f\u98de\u7684\u5251\u5bf9\u4ed8\u4e00\u4e2a\u4eba\u56fa\u7136\u6709\u4f59\uff0c\u5bf9\u4ed8\u8fd9\u4e48\u591a\u4eba\u5219\u4e0d\u8db3\u3002 \u3000\u3000\u963f\u98de\u8fd0\u5251\u7684\u65b9\u6cd5\u5947\u7279\uff0c\u5b8c\u5168\u548c\u4efb\u4f55\u4e00\u5bb6\u95e8\u6d3e\u7684\u5251\u6cd5\u90fd\u4e0d\u540c\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u6ca1\u6709\u201c\u524a\u201d\uff0c\u6ca1\u6709\u201c\u622a\u201d\uff0c\u53ea\u6709\u201c\u523a\u201d\uff01 \u3000\u3000\u523a\uff0c\u672c\u6765\u53ea\u6709\u5411\u524d\u523a\u3002 \u3000\u3000\u4f46\u963f\u98de\u65e0\u8bba\u5f80\u54ea\u4e2a\u65b9\u5411\u90fd\u80fd\u523a\uff0c\u65e0\u8bba\u5f80\u54ea\u4e2a\u90e8\u4f4d\u90fd\u80fd\u523a\uff01 \u3000\u3000\u4ed6\u80fd\u5f80\u808b\u4e0b\u523a\uff0c\u5f80\u80ef\u4e0b\u523a\uff0c\u5f80\u8033\u8fb9\u523a\u3002 \u3000\u3000\u4ed6\u80fd\u5411\u524d\u523a\uff0c\u5411\u540e\u523a\uff0c\u5411\u5de6\u53f3\u523a\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4e00\u4e2a\u4eba\u7740\u5730\u6eda\u6765\uff0c\u5200\u82b1\u7ffb\u98de\u3002 \u3000\u3000\u5730\u8d9f\u5200\uff01 \u3000\u3000\u8fd9\u79cd\u5200\u6cd5\u6781\u96be\u7ec3\uff0c\u6240\u4ee5\u7ec3\u6210\u4e86\u5c31\u6781\u6709\u5a01\u529b\u3002 \u3000\u3000\u4f46\u963f\u98de\u7684\u8eab\u540e\u4e5f\u4f3c\u4e4e\u957f\u7740\u773c\u775b\uff0c\u8eab\u5b50\u7a81\u7136\u4e00\u7f29\uff0c\u907f\u5f00\u4e86\u8fce\u9762\u523a\u6765\u7684\u67aa\uff0c\u5251\u5df2\u81ea\u80ef\u4e0b\u53cd\u624b \u5411\u540e\u523a\u51fa\uff0c\u523a\u5165\u90a3\u5730\u8d9f\u5200\u540d\u5bb6\u7684\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u65f6\u53e6\u4e00\u4eba\u5df2\u81ea\u4f7f\u67aa\u7684\u8eab\u540e\u62a2\u51fa\uff0c\u638c\u4e2d\u4e00\u53cc\u5175\u5203\u4ee5\u201c\u63a8\u5c71\u5f0f\u201d\u5411\u963f\u98de\u63a8\u51fa\uff0c\u4e0d\u4f46\u62db\u5f0f\u5947 \u7279\uff0c\u5175\u5203\u4e5f\u5947\u7279\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u4e00\u53cc\u51e4\u7fc5\u6d41\u91d1\u94db\u3002 \u3000\u3000\u8fd9\u79cd\u5175\u5203\u6c5f\u6e56\u4e2d\u66f4\u5c11\u4eba\u7528\uff0c\u94db\u4e0a\u6ee1\u662f\u5012\u523a\uff0c\u6b64\u523b\u7528\u7684\u867d\u662f\u201c\u63a8\u201d\u5b57\u8bc0\uff0c\u4f46\u5374\u540c\u65f6\u517c\u5e26\u201c \u6495\u201d\u3001\u201c\u6302\u201d\u4e24\u8bc0\u7684\u5999\u7528\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u53ea\u8981\u88ab\u5b83\u6cbe\u4e0a\u4e00\u70b9\uff0c\u76ae\u8089\u7acb\u523b\u5c31\u8981\u88ab\u6495\u5f97\u56db\u5206\u4e94\u88c2\u2014\u2014\u8fd9\u4e00\u7740\u201c\u63a8\u7a97\u671b\u6708\u201d\u4e0b\u9762 \u7684\u62db\u5f0f\uff0c\u6b63\u662f\u201c\u91ce\u9a6c\u5206\u9b03\u201d\uff01 \u3000\u3000\u963f\u98de\u672c\u6765\u5e94\u8be5\u5411\u540e\u8dc3\u3002 \u3000\u3000\u4ed6\u82e5\u5411\u540e\u9000\uff0c\u5c31\u96be\u514d\u5931\u5374\u5148\u673a\uff0c\u522b\u7684\u5175\u5203\u7acb\u523b\u5c31\u53ef\u80fd\u81f4\u4ed6\u7684\u6b7b\u547d\uff01 \u3000\u3000\u4f46\u4ed6\u5f53\u7136\u66f4\u4e0d\u80fd\u5411\u524d\u8fce\uff0c\u82e5\u5411\u524d\u8fce\uff0c\u6d41\u91d1\u94db\u7acb\u523b\u5c31\u8981\u81f4\u4ed6\u7684\u6b7b\u547d\u3002 \u3000\u3000\u8fd9\u9053\u7406\u65e0\u8bba\u8c01\u90fd\u80fd\u60f3\u5f97\u901a\u3002 \u3000\u3000\u8c01\u77e5\u963f\u98de\u5374\u50cf\u504f\u504f\u60f3\u4e0d\u901a\uff0c\u4ed6\u8eab\u5b50\u504f\u504f\u5411\u4e0a\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u773c\u89d2\u77a5\u89c1\uff0c\u51e0\u4e4e\u5df2\u5c06\u5931\u58f0\u60ca\u547c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u963f\u98de\u7684\u5251\u5df2\u81ea\u80ef\u4e0b\u6311\u8d77\uff0c\u81ea\u53cc\u94db\u95f4\u5411\u4e0a\u523a\u51fa\u3002 \u3000\u3000\u201c\u54e7\u201d\u7684\uff0c\u5251\u523a\u5165\u5bf9\u65b9\u7684\u54bd\u5589\u3002 \u3000\u3000\u6d41\u91d1\u94db\u867d\u5df2\u63a8\u4e0a\u963f\u98de\u7684\u80f8\u819b\uff0c\u4f46\u4f7f\u94db\u7684\u4eba\u53ea\u89c9\u5589\u5934\u4e00\u9635\u5947\u7279\u7684\u523a\u6fc0\uff0c\u5168\u8eab\u7a81\u7136\u6536\u7f29\uff0c\u65e0 \u8bba\u5982\u4f55\u4e5f\u65e0\u6cd5\u5c06\u94db\u7fc5\u518d\u63a8\u51fa\u534a\u5206\u3002 \u3000\u3000\u4ed6\u53cc\u773c\u6e10\u6e10\u51f8\u51fa\uff0c\u5168\u8eab\u7684\u808c\u8089\u90fd\u6e10\u6e10\u5931\u5374\u63a7\u5236\uff0c\u7a81\u7136\u89c9\u5f97\u80ef\u4e0b\u4e00\u7247\u51c9\uff0c\u5927\u5c0f\u4fbf\u4e00\u8d77\u6d8c\u51fa \uff0c\u53cc\u817f\u6e10\u6e10\u5411\u4e0b\u5f2f\u66f2\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u5145\u6ee1\u4e86\u60ca\u8bb6\u548c\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u80fd\u76f8\u4fe1\u4e16\u4e0a\u7adf\u6709\u8fd9\u4e48\u5feb\u7684\u5251\uff0c\u8fd9\u4e48\u51c6\u7684\u5251\uff01 \u3000\u3000\u53ef\u662f\u4ed6\u975e\u76f8\u4fe1\u4e0d\u53ef\uff01 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u56db\u4e0b\u4e00\u7247\u6b7b\u5bc2\uff0c\u6ca1\u6709\u4eba\u518d\u51fa\u624b\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u773c\u7741\u7741\u7684\u77a7\u7740\u8fd9\u6d41\u91d1\u94db\u540d\u5bb6\u53ef\u6015\u7684\u6b7b\u6cd5\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5df2\u55c5\u5230\u4ed6\u8eab\u4e0a\u7a81\u7136\u53d1\u51fa\u7684\u6076 \u81ed\u3002 \u3000\u3000\u6709\u7684\u4eba\u80c3\u91cc\u5df2\u5728\u7ffb\u817e\uff0c\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u3000\u3000\u4ee4\u4ed6\u4eec\u5455\u5410\u7684\u5e76\u4e0d\u662f\u8fd9\u6076\u81ed\uff0c\u800c\u662f\u6050\u60e7\uff0c\u4ed6\u4eec\u4eff\u4f5b\u76f4\u5230\u73b0\u5728\u624d\u7a81\u7136\u53d1\u73b0\u201c\u6b7b\u201d\u7adf\u662f\u5982\u6b64 \u53ef\u6015\uff0c\u5982\u6b64\u4e11\u6076\u3002 \u3000\u3000\u4ed6\u4eec\u5e76\u4e0d\u6015\u6b7b\uff0c\u4f46\u8fd9\u79cd\u6b7b\u6cd5\u5374\u5b9e\u5728\u4ee4\u4eba\u65e0\u6cd5\u5fcd\u53d7\uff01 \u3000\u3000\u963f\u98de\u6ca1\u6709\u518d\u51fa\u624b\uff0c\u4ece\u4eba\u7fa4\u4e2d\u9759\u9759\u7684\u7a7f\u8fc7\u3002 \u3000\u3000\u5269\u4e0b\u7684\u8fd8\u6709\u4e5d\u4e2a\u4eba\uff0c\u773c\u7741\u7741\u7684\u77a7\u7740\uff0c\u4e00\u4e2a\u4eba\u7a81\u7136\u5f2f\u8170\u5455\u5410\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7a81\u7136\u653e\u58f0\u75db\u54ed\uff0c\u53e6\u4e00\u4e2a\u4eba\u7a81\u7136\u5012\u5728\u5730\u4e0a\uff0c\u62bd\u8d77\u7b4b\u6765\u3002 \u3000\u3000\u8fd8\u6709\u4e2a\u4eba\u7a81\u7136\u8f6c\u8eab\u98de\u5954\u800c\u51fa\uff0c\u5954\u5411\u5395\u6240\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u53c8\u4f55\u5c1d\u4e0d\u60f3\u75db\u54ed\u5455\u5410\uff1f\u5979\u5fc3\u91cc\u4e0d\u4f46\u6050\u60e7\uff0c\u4e5f\u5f88\u60b2\u54c0\u3002\u4ed6\u60f3\u4e0d\u5230\u4eba\u7684\u751f\u547d\u6709\u65f6\u7adf\u4f1a \u53d8\u5f97\u5982\u6b64\u5351\u8d31\u3002 \u3000\u3000\u963f\u98de\u5728\u524d\u9762\u8d70\uff0c\u624b\u91cc\u63d0\u7740\u5251\u3002 \u3000\u3000\u5251\u72b9\u5728\u6ef4\u8840\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u67c4\u5251\uff0c\u4e0d\u4f46\u593a\u53bb\u4eba\u7684\u751f\u547d\uff0c\u4e5f\u524a\u593a\u4e86\u4eba\u7684\u5c0a\u4e25\u3002 \u3000\u3000\u5251\u7adf\u662f\u5982\u6b64\u65e0\u60c5\uff01 \u3000\u3000\u4ed6\u7684\u4eba\u5462\uff1f \u3000\u3000\u752c\u9053\u7684\u5c3d\u5934\u6709\u6247\u95e8\u3002 \u3000\u3000\u95e8\u5173\u5f97\u5f88\u7d27\uff0c\u800c\u4e14\u4ece\u91cc\u9762\u4e0a\u4e86\u62f4\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4e0a\u5b98\u5e2e\u4e3b\u7684\u5bdd\u5ba4\uff0c\u4e0a\u5b98\u5e2e\u4e3b\u5c31\u5728\u91cc\u9762\uff0c\u90a3\u674e\u5bfb\u6b22\u4e5f\u5728\u91cc\u9762\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8fd8\u6ca1\u6709\u51fa\u6765\uff0c\u674e\u5bfb\u6b22\u663e\u7136\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5fc3\u91cc\u4e00\u9635\u6b22\u8dc3\uff0c\u5927\u6b65\u51b2\u4e86\u8fc7\u53bb\uff0c\u51b2\u5230\u95e8\u524d\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u7a81\u7136\u50f5\u4f4f\uff01 \u3000\u3000\u95e8\u662f\u94c1\u94f8\u7684\uff0c\u81f3\u5c11\u6709\u4e00\u5c3a\u539a\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u90fd\u649e\u5f00\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u81ea\u7136\u66f4\u4e0d\u4f1a\u81ea\u5df1\u5728\u91cc\u9762\u5c06\u95e8\u6253\u5f00\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u89c9\u5f97\u4e00\u9635\u6655\u7729\uff0c\u5c31\u50cf\u662f\u4e00\u811a\u8e29\u7a7a\uff0c\u843d\u5165\u4e86\u4e07\u4e08\u6df1\u6e0a\uff01 \u3000\u3000\u5979\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\uff0c\u4eba\u5012\u5728\u95e8\u4e0a\uff0c\u6cea\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u5979\u6574\u4e2a\u7684\u8ba1\u5212\u90fd\u5df2\u6210\u7a7a\uff0c\u6240\u6709\u7684\u5fc3\u8840\u5168\u90fd\u767d\u8d39\u3002 \u3000\u3000\u8fd9\u8ba1\u5212\u82e5\u662f\u4ece\u5934\u5c31\u5931\u8d25\uff0c\u4e5f\u8bb8\u53cd\u5012\u597d\u4e9b\uff0c\u6700\u75db\u82e6\u7684\u662f\uff0c\u660e\u660e\u773c\u770b\u7740\u5b83\u5df2\u5230\u4e86\u6210\u529f\u7684\u8fb9\u7f18 \uff0c\u624d\u7a81\u7136\u5931\u8d25\u3002 \u3000\u3000\u8fd9\u79cd\u6253\u51fb\u624d\u6700\u4ee4\u4eba\u4e0d\u80fd\u5fcd\u53d7\uff01 \u3000\u3000\u963f\u98de\u6014\u5728\u90a3\u91cc\uff0c\u7a81\u7136\u95f4\uff0c\u4ed6\u597d\u50cf\u5df2\u53d8\u6210\u4e86\u4e00\u53ea\u75af\u72c2\u7684\u91ce\u517d\uff0c\u7528\u5c3d\u5168\u529b\u5411\u94c1\u95e8\u4e0a\u649e\u4e86\u8fc7\u53bb \u3002 \u3000\u3000\u4ed6\u7684\u4eba\u88ab\u649e\u5f97\u5f39\u4e86\u51fa\u6765\uff0c\u8dcc\u5012\uff0c\u518d\u51b2\u51fb\uff0c\u5168\u529b\u523a\u51fa\u4e00\u5251\uff01 \u3000\u3000\u5251\u6298\u65ad\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e00\u67c4\u5251\u80fd\u6d1e\u7a7f\u8fd9\u94c1\u95e8\uff0c\u4f55\u51b5\u662f\u67c4\u7af9\u5251\uff01 \u7b2c\u516b\u5341\u516b\u7ae0\u3000\u80dc\u8d25>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u516b\u7ae0\u3000\u80dc\u8d25 \u3000\u3000\u963f\u98de\u7684\u817f\u5f2f\u4e0b\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u5728\u62bd\u6410\uff0c\u4ed6\u53c8\u6709\u4e86\u90a3\u79cd\u201c\u65e0\u53ef\u5948\u4f55\u201d\u7684\u611f\u89c9\uff0c\u8fd9\u79cd\u611f\u89c9\u6bcf\u6b21 \u90fd\u8981\u4ee4\u4ed6\u53d1\u75af\u3002 \u3000\u3000\u4f46\u53d1\u75af\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u5c31\u5728\u8fd9\u6247\u95e8\u91cc\uff0c\u6162\u6162\u7684\u53d7\u7740\u6b7b\u7684\u6298\u78e8\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u53ea\u80fd\u5728\u5916\u9762\u7b49\u7740\u3002 \u3000\u3000\u7b49\u4ec0\u4e48\u5462\uff0c\u7b49\u4e0a\u5b98\u91d1\u8679\u81ea\u5df1\u5f00\u95e8\u8d70\u51fa\u6765\uff1f \u3000\u3000\u4ed6\u51fa\u6765\u7684\u65f6\u5019\uff0c\u674e\u5bfb\u6b22\u5c31\u4e0d\u4f1a\u518d\u6d3b\u7740\u3002 \u3000\u3000\u7b49\u4ec0\u4e48\u5462\uff1f\u53ea\u4e0d\u8fc7\u662f\u5728\u7b49\u6b7b\u800c\u5df2\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u81ea\u7136\u4e5f\u51b3\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u6d3b\u7740\uff0c\u4ed6\u51fa\u6765\u7684\u65f6\u5019\uff0c\u4e5f\u5c31\u662f\u4ed6\u4eec\u6b7b\u7684\u65f6\u5019\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u8d70\u8fc7\u6765\uff0c\u7528\u529b\u62c9\u8d77\u963f\u98de\uff0c\u9053\uff1a\u201c\u4f60\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53eb\u6211\u8d70\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4f60\u975e\u8d70\u4e0d\u53ef\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5782\u7740\u5934\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u4e0d\u540c\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4ed6\u6b7b\u4e86\uff0c\u6211\u4e5f\u4e0d\u80fd\u72ec\u6d3b\uff0c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u966a\u4ed6\u6b7b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u8be5\u8d70\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u8d70\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u662f\u4e3a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u8981\u4e3a\u4ed6\u590d\u4ec7\uff0c\u4f46\u90a3\u4e5f\u7528\u4e0d\u7740\u6025\u5728\u4e00\u65f6\uff0c\u4f60\u53ef\u4ee5\u7b49\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u80fd\u7b49\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e0d\u80fd\u7b49\u5c31\u2026\u2026\u5c31\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5c31\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5634\u5507\u5df2\u54ac\u51fa\u8840\uff0c\u9053\uff1a\u201c\u5c31\u6b7b\uff01\u201d \u3000\u3000\u963f\u98de\u51dd\u89c6\u7740\u7af9\u5251\u4e0a\u7684\u8840\u8ff9\u3002 \u3000\u3000\u8840\u5df2\u5e72\u67af\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u4e00\u5b9a\u8fd8\u60f3\u8bd5\u8bd5\uff0c\u4f46\u90a3\u4e5f\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u7559\u5728\u8fd9\u91cc\u966a\u4ed6\u6b7b\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u963f\u98de\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u7559\u4e0b\u6765\uff0c\u53ea\u56e0\u4e3a\u6709\u4ef6\u4e8b\u4f60\u7eb5\u7136\u660e\u77e5\u505a\u4e86\u6ca1\u6709\u7528\uff0c\u8fd8\u662f\u975e\u505a\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u7684\u53e3\u6c14\u8d8a\u6765\u8d8a\u50cf\u4ed6\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u65e0\u8a00\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u627f\u8ba4\uff0c\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u53ea\u8981\u662f\u4eba\uff0c\u53ea\u8981\u548c\u674e\u5bfb\u6b22\u63a5\u89e6\u8f83\u6df1\uff0c\u5c31\u65e0\u6cd5\u4e0d\u88ab\u4ed6\u90a3\u79cd\u4f1f\u5927\u7684\u4eba\u683c\u611f\u52a8\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u9047\u89c1\u674e\u5bfb\u6b22\uff0c\u963f\u98de\u53ea\u6015\u65e9\u5df2\u5bf9\u4eba\u7c7b\u5931\u53bb\u4e86\u4fe1\u5fc3\u3002 \u3000\u3000\u201c\u7edd\u4e0d\u8981\u4fe1\u4efb\u4efb\u4f55\u4eba\uff0c\u4e5f\u7edd\u4e0d\u8981\u53d7\u4efb\u4f55\u4eba\u7684\u597d\u5904\uff0c\u5426\u5219\u4f60\u5fc5\u5c06\u75db\u82e6\u4e00\u751f\u3002\u201d \u3000\u3000\u963f\u98de\u7684\u6bcd\u4eb2\u8fd9\u4e00\u751f\u663e\u7136\u5145\u6ee1\u4e86\u75db\u82e6\u548c\u4e0d\u5e78\uff0c\u963f\u98de\u51e0\u4e4e\u4ece\u672a\u770b\u5230\u5979\u7b11\u8fc7\uff0c\u5979\u6b7b\u5f97\u5f88\u65e9\uff0c\u53ea \u56e0\u5979\u5bf9\u4eba\u751f\u5df2\u6beb\u65e0\u5e0c\u671b\u3002 \u3000\u3000\u201c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u6211\u672c\u8be5\u7b49\u4f60\u957f\u5927\u540e\u518d\u6b7b\u7684\uff0c\u53ef\u662f\u6211\u5df2\u4e0d\u80fd\u7b49\uff0c\u6211\u5b9e\u5728\u592a\u7d2f\u4e86\u2026\u2026\u6211\u4ec0\u4e48 \u90fd\u6ca1\u6709\u7559\u7ed9\u4f60\uff0c\u9664\u4e86\u90a3\u51e0\u53e5\u8bdd\uff0c\u90a3\u662f\u6211\u81ea\u5df1\u4eb2\u8eab\u5f97\u5230\u7684\u6559\u8bad\uff0c\u4f60\u7edd\u4e0d\u53ef\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u963f\u98de\u4ece\u6765\u4e5f\u6ca1\u6709\u5fd8\u8bb0\u3002 \u3000\u3000\u4ed6\u4ece\u8352\u91ce\u4e2d\u8d70\u5165\u7ea2\u5c18\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u6d3b\u5f97\u597d\u4e9b\uff0c\u800c\u662f\u4e3a\u4e86\u8981\u8c61\u4eba\u7c7b\u62a5\u590d\uff0c\u4e3a\u4ed6\u7684\u6bcd\u4eb2\u62a5 \u590d\u3002 \u3000\u3000\u4f46\u4ed6\u7b2c\u4e00\u4e2a\u4eba\u5c31\u9047\u89c1\u4e86\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4f7f\u4ed6\u89c9\u5f97\u4eba\u751f\u5e76\u4e0d\u5982\u4ed6\u60f3\u8c61\u4e2d\u90a3\u4e48\u75db\u82e6\uff0c\u6740\u4eba\u4e5f\u5e76\u4e0d\u50cf\u4ed6\u60f3\u5f97\u90a3\u4e48\u4e11\u6076\uff0c\u4ed6\u5728\u674e \u5bfb\u6b22\u8eab\u4e0a\u53d1\u73b0\u4e86\u8bb8\u591a\u8bb8\u591a\u7f8e\u5fb7\u3002 \u3000\u3000\u4ed6\u672c\u6765\u6839\u672c\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u6709\u8fd9\u4e9b\u7f8e\u5fb7\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u7740\u4e00\u751f\u53d7\u674e\u5bfb\u6b22\u7684\u5f71\u54cd\u5b9e\u5728\u592a\u591a\uff0c\u751a\u81f3\u6bd4\u4ed6\u7684\u6bcd\u4eb2\u8fd8\u591a\u3002 \u3000\u3000\u56e0\u4e3a\u674e\u5bfb\u6b22\u6559\u7ed9\u4ed6\u7684\u662f\u201c\u7231\u201d\uff0c\u4e0d\u662f\u6068\u3002 \u3000\u3000\u7231\u6c38\u8fdc\u6bd4\u6068\u66f4\u5bb9\u6613\u4ee4\u4eba\u63a5\u53d7\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\uff0c\u4ed6\u5374\u4e0d\u80fd\u4e0d\u6068\uff01 \u3000\u3000\u4ed6\u6068\u5f97\u60f3\u6bc1\u706d\uff0c\u6bc1\u706d\u522b\u4eba\uff0c\u6bc1\u706d\u81ea\u5df1\uff0c\u6bc1\u706d\u4e00\u5207\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u8fd9\u592a\u4e0d\u516c\u5e73\uff0c\u50cf\u674e\u5bfb\u6b22\u8fd9\u6837\u7684\u4eba\uff0c\u672c\u4e0d\u8be5\u8fd9\u4e48\u6837\u6b7b\u7684\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u51c4\u7136\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u82e5\u77e5\u9053\u6211\u4eec\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u4e00\u5b9a\u5f00\u5fc3\u7684\u5f88\u3002 \u201d \u3000\u3000\u963f\u98de\u54ac\u7740\u7259\u9053\uff1a\u201c\u5c31\u8ba9\u4ed6\u5f00\u5fc3\u5427\uff0c\u8fd9\u4e16\u4e0a\u672c\u5c31\u53ea\u6709\u597d\u4eba\u624d\u75db\u82e6\uff0c\u5f00\u5fc3\u7684\u672c\u6765\u5c31\u662f\u6076\u4eba\uff01 \u201d \u3000\u3000\u7a81\u7136\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u94c1\u95e8\u867d\u6c89\u91cd\uff0c\u4f46\u5f00\u95e8\u7684\u58f0\u97f3\u5374\u4e0d\u4f1a\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\u3002 \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\u95e8\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u4ece\u95e8\u91cc\u6162\u6162\u8d70\u51fa\u6765\u7684\u4eba\uff0c\u8d6b\u7136\u7adf\u662f\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u4ed6\u770b\u6765\u663e\u5f97\u5f88\u75b2\u5026\uff0c\u4f46\u8fd8\u662f\u6d3b\u7740\u7684\u3002 \u3000\u3000\u6d3b\u7740\uff0c\u8fd9\u624d\u662f\u6700\u91cd\u8981\u7684\u4e8b\uff01 \u3000\u3000\u963f\u98de\u548c\u5b59\u5c0f\u7ea2\u731d\u7136\u56de\u9996\uff0c\u6014\u4f4f\uff0c\u773c\u6cea\u6162\u6162\u7684\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u662f\u6b22\u559c\u7684\u773c\u6cea\uff0c\u559c\u6781\u65f6\u4e5f\u548c\u60b2\u54c0\u65f6\u4e00\u6837\uff0c\u9664\u4e86\u6d41\u6cea\u5916\uff0c\u4ec0\u4e48\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd \u4e0d\u80fd\u505a\uff0c\u751a\u81f3\u8fde\u52a8\u90fd\u65e0\u6cd5\u52a8\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u5df2\u6709\u70ed\u6cea\u76c8\u7736\uff0c\u5634\u89d2\u5374\u5e26\u7740\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u8fd9\u4e16\u4e0a\u7684\u597d\u4eba\u662f\u6c38\u8fdc\u4e0d\u4f1a \u5bc2\u5bde\u7684\uff0c\u6076\u4eba\u75db\u82e6\u7684\u65f6\u5019\u4e5f\u6c38\u8fdc\u8981\u6bd4\u5f00\u5fc3\u7684\u65f6\u5019\u591a\u5f97\u591a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7a81\u7136\u6251\u8fc7\u53bb\uff0c\u6251\u5728\u4ed6\u6000\u91cc\uff0c\u4e0d\u505c\u7684\u555c\u6ce3\u8d77\u6765\u3002 \u3000\u3000\u5979\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u8981\u559c\u6781\u800c\u6ce3\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u963f\u98de\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\u3002 \u3000\u3000\u201c\u4e0a\u5b98\u91d1\u8679\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u8f7b\u629a\u7740\u5b59\u5c0f\u7ea2\u7684\u67d4\u53d1\uff0c\u9053\uff1a\u201c\u60f3\u5fc5\u4e5f\u5f88\u75db\u82e6\uff0c\u56e0\u4e3a\u4ed6\u6bd5\u7adf\u8fd8\u662f\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4ed6\u505a\u9519\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u7684\u786e\u6709\u5f88\u591a\u673a\u4f1a\u80fd\u6740\u6211\uff0c\u4ed6\u751a\u81f3\u53ef\u4ee5\u4ee4\u6211\u6839\u672c\u65e0\u6cd5\u8fd8\u624b\uff0c\u53ef\u662f\u4ed6\u5374\u6545\u610f \u5c06\u673a\u4f1a\u9519\u8fc7\u4e86\u3002\u201d \u3000\u3000\u50cf\u4e0a\u5b98\u91d1\u8679\u8fd9\u6837\u7684\u4eba\uff0c\u600e\u4f1a\u5c06\u673a\u4f1a\u9519\u8fc7\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u4e5f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u59cb\u7ec8\u60f3\u8d4c\u4e00\u8d4c\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7738\u5b50\u91cc\u53d1\u51fa\u4e86\u5149\uff0c\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u4e0d\u76f8\u4fe1\u2018\u5c0f\u674e\u98de\u5200\uff0c\u4f8b\u4e0d\u865a\u53d1\u2019\u8fd9\u53e5\u8bdd\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4ed6\u4e0d\u4fe1\u2014\u2014\u4efb\u4f55\u4eba\u4ed6\u90fd\u4e0d\u4fe1\uff0c\u8fd9\u4e16\u4e0a\u6839\u672c\u6ca1\u6709\u4e00\u4ef6\u80fd\u8ba9\u4ed6\u76f8\u4fe1\u7684\u4e8b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u7ed3\u679c\u5462\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u6de1\u6de1\u9053\uff1a\u201c\u4ed6\u8f93\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8f93\u4e86\uff01 \u3000\u3000\u8fd9\u53ea\u4e0d\u8fc7\u662f\u7b80\u7b80\u5355\u5355\u7684\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u51b3\u5b9a\u80dc\u8d1f\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e00\u5239\u90a3\u95f4\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u5239\u90a3\u5374\u662f\u4f55\u7b49\u7d27\u5f20\uff0c\u4f55\u7b49\u523a\u6fc0\u7684\u4e00\u5239\u90a3\uff01 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u5bf9\u6c5f\u6e56\u7684\u5f71\u54cd\u53c8\u662f\u4f55\u7b49\u6df1\uff01 \u3000\u3000\u90a3\u4e00\u95ea\u7684\u5200\u5149\u53c8\u662f\u4f55\u7b49\u60ca\u5fc3\uff01\u4f55\u7b49\u58ee\u4e3d\uff01 \u3000\u3000\u5b59\u5c0f\u7ea2\u53ea\u6068\u81ea\u5df1\u6ca1\u6709\u4eb2\u773c\u770b\u5230\u7740\u4e00\u5239\u90a3\u95f4\u53d1\u751f\u7684\u4e8b\uff01 \u3000\u3000\u751a\u81f3\u4e0d\u5fc5\u4eb2\u773c\u770b\u5230\uff0c\u53ea\u8981\u53bb\u60f3\u4e00\u60f3\uff0c\u5979\u7684\u547c\u5438\u90fd\u4e0d\u7981\u4e3a\u4e4b\u505c\u987f\uff01 \u3000\u3000\u6d41\u661f\u4e5f\u5f88\u7f8e\uff0c\u5f88\u58ee\u4e3d\u3002 \u3000\u3000\u6d41\u661f\u5212\u7834\u9ed1\u6697\u65f6\u6240\u53d1\u51fa\u7684\u5149\u8292\uff0c\u4e5f\u603b\u662f\u4ee4\u4eba\u5174\u594b\uff0c\u611f\u52a8\u3002 \u3000\u3000\u4f46\u5c31\u8fde\u6d41\u661f\u7684\u5149\u8292\u4e5f\u65e0\u6cd5\u548c\u90a3\u4e00\u95ea\u7684\u5200\u8292\u6bd4\u62df\u3002 \u3000\u3000\u6d41\u661f\u7684\u5149\u8292\u77ed\u66b4\u3002 \u3000\u3000\u8fd9\u4e00\u95ea\u5200\u5149\u7559\u4e0b\u7684\u5149\u8292\uff0c\u5374\u8db3\u4ee5\u7167\u8000\u6c38\u6052\uff01 \u3000\u3000\u95e8\u5df2\u7ecf\u5f00\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u6c38\u8fdc\u5c06\u6574\u4e2a\u4e16\u754c\u90fd\u9694\u79bb\u5728\u95e8\u5916\u3002 \u3000\u3000\u4f60\u82e5\u60f3\u548c\u4e16\u4eba\u9694\u7edd\uff0c\u5fc5\u5148\u88ab\u4e16\u4eba\u629b\u5f03\uff01 \u3000\u3000\u963f\u98de\u8d70\u8fdb\u4e86\u8fd9\u6247\u95e8\u3002 \u3000\u3000\u7b2c\u4e00\u773c\uff0c\u4ed6\u5c31\u770b\u5230\u4e86\u90a3\u67c4\u5200\uff0c\u90a3\u67c4\u795e\u5947\u7684\u5200\u3002 \u3000\u3000\u5c0f\u674e\u98de\u5200\uff01 \u3000\u3000\u5200\u5e76\u6ca1\u6709\u76f4\u63a5\u63d2\u5165\u4e0a\u5b98\u91d1\u8679\u7684\u54bd\u5589\uff0c\u4f46\u5374\u8db3\u4ee5\u81f4\u547d\uff01 \u3000\u3000\u5200\u950b\u662f\u4ece\u5589\u7ed3\u4e0b\u64e6\u7740\u9501\u9aa8\u659c\u659c\u5411\u4e0a\u523a\u5165\u7684\uff0c\u8fd9\u4e00\u5200\u51fa\u624b\u7684\u90e8\u4f4d\u663e\u7136\u5f88\u4f4e\u3002 \u3000\u3000\u8fd9\u4e00\u4ee3\u67ad\u96c4\u6b7b\u7684\u65f6\u5019\uff0c\u4e5f\u548c\u5176\u4ed6\u90a3\u4e9b\u4ed6\u6240\u5351\u89c6\u7684\u4eba\u6ca1\u4ec0\u4e48\u4e24\u6837\uff0c\u4e5f\u540c\u6837\u56de\u60ca\u614c\uff0c\u540c\u6837\u4f1a \u6050\u60e7\u3002 \u3000\u3000\u751f\u547d\u539f\u662f\u5e73\u7b49\u7684\uff0c\u5c24\u5176\u662f\u5728\u6b7b\u7684\u9762\u524d\uff0c\u4eba\u4eba\u90fd\u5e73\u7b49\uff0c\u4f46\u6709\u4e9b\u4eba\u5374\u504f\u504f\u8981\u7b49\u5230\u6700\u540e\u7ed3\u5c40\u65f6 \u624d\u61c2\u5f97\u8fd9\u9053\u7406\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u8138\u4e0a\u4e5f\u5145\u6ee1\u4e86\u60ca\u60e7\uff0c\u6000\u7591\uff0c\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u4e5f\u50cf\u522b\u4eba\u4e00\u6837\uff0c\u4e0d\u4fe1\u8fd9\u4e00\u5200\u4f1a\u5982\u6b64\u5feb\uff01 \u3000\u3000\u751a\u81f3\u8fde\u963f\u98de\u90fd\u5f88\u96be\u76f8\u4fe1\uff0c\u4ed6\u751a\u81f3\u60f3\u4e0d\u901a\u8fd9\u4e00\u5200\u662f\u5982\u4f55\u51fa\u624b\u7684\u3002 \u3000\u3000\u4ed6\u6068\u4e0d\u5f97\u674e\u5bfb\u6b22\u80fd\u5c06\u5f53\u65f6\u7684\u60c5\u51b5\u8bf4\u5f97\u8be6\u7ec6\u4e9b\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\u674e\u5bfb\u6b22\u662f\u4e0d\u4f1a\u8bf4\u3002 \u3000\u3000\u90a3\u4e00\u77ac\u95f4\u7684\u5149\u8292\uff0c\u90a3\u4e00\u5200\u7684\u901f\u5ea6\uff0c\u6839\u672c\u5c31\u6ca1\u4eba\u80fd\u8bf4\u5f97\u51fa\u3002 \u3000\u3000\u201c\u4ed6\u8f93\u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u624b\u7d27\u63e1\uff0c\u4eff\u4f5b\u8fd8\u6293\u4f4f\u4ec0\u4e48\uff0c\u4ed6\u662f\u4e0d\u662f\u8fd8\u4e0d\u8ba4\u8f93\uff1f \u3000\u3000\u53ea\u53ef\u60dc\u73b0\u5728\u4ed6\u4ec0\u4e48\u90fd\u518d\u4e5f\u6293\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u963f\u98de\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u5f88\u95f7\uff0c\u5ffd\u7136\u5bf9\u8fd9\u4eba\u89c9\u5f97\u5f88\u540c\u60c5\u5fc3\uff0c\u8fd9\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4e5f\u8bb8\u4ed6\u540c\u60c5\u7684\u4e0d\u662f\u4e0a\u5b98\u91d1\u8679\uff0c\u800c\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u662f\u4eba\uff0c\u4e0a\u5b98\u91d1\u8679\u4e5f\u662f\u4eba\uff0c\u4eba\u90fd\u6709\u76f8\u540c\u7684\u60b2\u54c0\u548c\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u8f93\uff0c\u53ef\u662f\u4ed6\u53c8\u6293\u4f4f\u4e86\u4ec0\u4e48\uff1f\u5f97\u5230\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u963f\u98de\u624d\u8f6c\u8fc7\u5934\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u770b\u5230\u8346\u65e0\u547d\u3002 \u3000\u3000\u8346\u65e0\u547d\u5374\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u53d1\u73b0\u522b\u4eba\u8fdb\u6765\uff0c\u4ed6\u867d\u7136\u5c31\u7ad9\u5728\u963f\u98de\u8eab\u65c1\u7684\u90a3\u5f20\u5927\u684c\u5b50\u540e\u9762\uff0c\u5374\u4eff \u4f5b\u662f\u7ad9\u5728\u53e6\u4e00\u4e2a\u4e16\u754c\u91cc\u3002 \u3000\u3000\u4ed6\u773c\u775b\u867d\u662f\u5728\u77a7\u7740\u4e0a\u5b98\u91d1\u8679\uff0c\u5176\u5b9e\u5374\u662f\u5728\u77a7\u7740\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u7684\u751f\u547d\u5c31\u662f\u4ed6\u7684\u751f\u547d\uff0c\u4ed6\u5c31\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u5f71\u5b50\u3002 \u3000\u3000\u751f\u547d\u82e5\u5df2\u6d88\u5931\uff0c\u54ea\u91cc\u8fd8\u6709\u5f71\u5b50\uff1f \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u53ea\u8981\u8346\u65e0\u547d\u5728\u90a3\u91cc\uff0c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u611f\u89c9\u5230\u4e00\u79cd\u65e0\u5f62\u7684\u5a01\u80c1\uff0c\u65e0\u5f62\u7684\u6740\u6c14\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u8fd9\u79cd\u611f\u89c9\u5df2\u4e0d\u5b58\u5728\u4e86\u3002 \u3000\u3000\u963f\u98de\u8d70\u8fdb\u7740\u5c4b\u5b50\u91cc\u7684\u65f6\u5019\uff0c\u751a\u81f3\u6839\u672c\u6ca1\u6709\u611f\u89c9\u5230\u6709\u4ed6\u8fd9\u4e2a\u4eba\u5b58\u5728\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6d3b\u7740\uff0c\u5374\u5df2\u53ea\u4e0d\u8fc7\u5269\u4e0b\u4e00\u4e2a\u7a7a\u7a7a\u7684\u8eaf\u58f3\u800c\u5df2\uff0c\u6b63\u5982\u4e00\u628a\u65e0\u950b\u7684\u5251\uff0c\u5c31\u7b97\u8fd8\u80fd\u5b58\u5728 \uff0c\u4e5f\u5df2\u5931\u53bb\u4e86\u610f\u4e49\u3002 \u3000\u3000\u963f\u98de\u53c8\u4e0d\u7981\u5728\u6697\u4e2d\u53f9\u606f\uff0c\u4ed6\u5f88\u4e86\u89e3\u8346\u65e0\u547d\u6b64\u65f6\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u66fe\u6709\u8fc7\u8fd9\u79cd\u7ecf\u5386\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8346\u65e0\u547d\u5ffd\u7136\u8d70\u4e86\u8fc7\u6765\uff0c\u7528\u4e00\u53ea\u624b\u6258\u8d77\u4e0a\u5b98\u91d1\u8679\u7684\u5c38\u9996\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u6ca1\u6709\u770b\u522b\u4eba\u4e00\u773c\uff0c\u6162\u6162\u7684\u5411\u5916\u8d70\uff0c\u773c\u770b\u5df2\u5c06\u8d70\u51fa\u95e8\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u6ca1\u6709\u56de\u5934\uff0c\u8fde\u811a\u6b65\u90fd\u6ca1\u6709\u505c\u3002 \u3000\u3000\u963f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6562\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u811a\u6b65\u9aa4\u7136\u505c\u4e0b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u8170\u4e0a\u65e2\u7136\u8fd8\u6709\u5251\uff0c\u4e3a\u4f55\u4e0d\u6562\u62bd\u51fa\u6765\uff1f\u96be\u9053\u4f60\u7684\u5251\u53ea\u662f\u6446\u6446\u6837\u5b50\u7684\u4e48\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u970d\u7136\u56de\u8eab\u3002 \u3000\u3000\u5c38\u4f53\u5df2\u843d\u4e0b\uff0c\u5251\u5df2\u51fa\u624b\uff01 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u523a\u5411\u963f\u98de\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u51fa\u624b\u8fd8\u662f\u5f88\u5feb\uff0c\u751a\u81f3\u8fd8\u662f\u548c\u4ee5\u524d\u540c\u6837\u5feb\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u8fd9\u4e00\u5251\u8ddd\u79bb\u963f\u98de\u54bd\u5589\u8fd8 \u6709\u534a\u5c3a\u65f6\uff0c\u963f\u98de\u624b\u91cc\u7684\u7af9\u5251\u5df2\u5148\u5230\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u963f\u98de\u524a\u4e86\u4e09\u67c4\u5251\uff0c\u8fd9\u662f\u7b2c\u4e8c\u67c4\u3002 \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u8346\u65e0\u547d\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u5f88\u5feb\uff0c\u4f46\u4e0d\u80fd\u6740\u4eba\u4e86\uff0c\u4f60\u53ef\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u5251\u5782\u4e0b\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u56e0\u4e3a\u4f60\u6bd4\u522b\u4eba\u66f4\u60f3\u6b7b\uff0c\u5f53\u7136\u5c31\u6740\u4e0d\u4e86\u522b\u4eba\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u672c\u5168\u65e0\u751f\u547d\u7684\u773c\u775b\u91cc\uff0c\u5ffd\u7136\u9732\u51fa\u4e00\u4e1d\u6c89\u75db\u51c4\u51c9\u4e4b\u8272\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u9eef\u7136\u9053\uff1a\u201c \u662f\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u5374\u80fd\u6740\u4f60\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f46\u6211\u4e0d\u6740\u4f60\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u4f60\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u662f\u8346\u65e0\u547d\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u7684\u8138\u5ffd\u7136\u626d\u66f2\u3002 \u3000\u3000\u4ed6\u5df2\u5fc6\u8d77\u8fd9\u53e5\u8bdd\u6b63\u548c\u90a3\u5929\u4ed6\u7b2c\u4e00\u6b21\u9047\u5230\u963f\u98de\u65f6\u5b8c\u5168\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u90a3\u5929\u4ed6\u8bf4\u7684\u8bdd\uff0c\u73b0\u5728\u5374 \u53d8\u6210\u963f\u98de\u5728\u8bf4\u4e86\u3002 \u3000\u3000\u4ed6\u4ed4\u7ec6\u5480\u56bc\u7740\u8fd9\u51e0\u53e5\u8bdd\uff0c\u773c\u775b\u91cc\u4f3c\u6709\u706b\u7130\u71c3\u8d77\uff0c\u5c31\u50cf\u662f\u4e00\u5806\u6b7b\u7070\u590d\u71c3\u3002 \u3000\u3000\u963f\u98de\u51dd\u89c6\u7740\u4ed6\uff0c\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u8346\u65e0\u547d\u9053\uff1a\u201c\u8d70\uff1f\u2026\u2026\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u4f60\u7ed9\u4e86\u6211\u4e00\u6b21\u673a\u4f1a\uff0c\u6211\u4e5f\u7ed9\u4f60\u4e00\u6b21\u2026\u2026\u6700\u540e\u4e00\u6b21\u3002\u201d \u3000\u3000\u963f\u98de\u77a7\u7740\u8346\u65e0\u547d\u8d70\u4e86\u51fa\u53bb\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u201c\u4ee5\u7259\u8fd8\u7259\uff0c\u4ee5\u8840\u8fd8\u8840\uff01\u201d \u3000\u3000\u8346\u65e0\u547d\u4ee5\u524d\u6240\u7ed9\u4ed6\u7684\uff0c\u73b0\u5728\u4ed6\u5df2\u540c\u6837\u8fd8\u7ed9\u4e86\u8346\u65e0\u547d\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u5fc3\u82e5\u5df2\u6b7b\uff0c\u53ea\u6709\u4e24\u79cd\u529b\u91cf\u624d\u80fd\u4ee4\u4ed6\u518d\u751f\u3002 \u3000\u3000\u4e00\u79cd\u662f\u7231\uff0c\u4e00\u79cd\u662f\u6068\u3002 \u3000\u3000\u963f\u98de\u81ea\u5df1\u5c31\u662f\u9760\u4e86\u7231\u7684\u529b\u91cf\u800c\u91cd\u751f\u7684\uff0c\u73b0\u5728\uff0c\u4ed6\u5374\u8981\u4ee5\u6068\u7684\u529b\u91cf\u6765\u6fc0\u53d1\u8346\u65e0\u547d\u751f\u547d\u7684\u6f5c \u529b\u3002 \u3000\u3000\u4ed6\u60f3\u8981\u8346\u65e0\u547d\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u5047\u5982\u8fd9\u4e5f\u7b97\u662f\u62a5\u590d\uff0c\u90a3\u4e48\u8fd9\u79cd\u62a5\u590d\u53ea\u6015\u5c31\u662f\u4e16\u4e0a\u6700\u4f1f\u5927\u7684\u62a5\u590d\u4e86\uff0c\u5047\u5982\u4e16\u4e0a\u7684\u62a5\u590d\u90fd\u548c \u4ed6\u4e00\u6837\uff0c\u4eba\u7c7b\u7684\u5386\u53f2\u5fc5\u5b9a\u66f4\u8f89\u714c\uff0c\u4eba\u7c7b\u7684\u751f\u547d\u5fc5\u5c06\u6c38\u5b58\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u62a5\u590d\u603b\u662f\u6109\u5feb\u7684\u3002 \u3000\u3000\u4f46\u963f\u98de\u73b0\u5728\u771f\u89c9\u5f97\u5f88\u6109\u5feb\u4e48\uff1f \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u5f88\u75b2\u5026\uff0c\u5f88\u75b2\u5026\u2026\u2026\u4ed6\u624b\u91cc\u7684\u5251\u5df2\u6389\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u4e00\u76f4\u9759\u9759\u7684\u77a7\u7740\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u624d\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u8981\u6740\u4e00\u4e2a\u4eba\u5f88\u5bb9\u6613\uff0c\u4f46\u82e5\u8981\u4ed6\u597d\u597d\u7684\u6d3b\u7740\uff0c\u5c31\u96be\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u674e\u5bfb\u6b22\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u65e0\u8bba\u5bf9\u4ec0\u4e48\u4eba\uff0c\u5bf9\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u7684\u51fa\u53d1\u70b9\u90fd\u662f\u7231\uff0c\u4e0d\u662f\u6068\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6068\u6240\u9020\u6210\u7684\u53ea\u6709\u6bc1 \u706d\uff0c\u7231\u5374\u53ef\u4ee4\u4eba\u6c38\u751f\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u80f8\u6c38\u8fdc\u662f\u90a3\u4e48\u5bbd\u9614\uff0c\u4eba\u683c\u6c38\u8fdc\u662f\u90a3\u4e48\u4f1f\u5927\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5b59\u5c0f\u7ea2\u53d1\u73b0\u963f\u98de\u4e5f\u51e0\u4e4e\u53d8\u5f97\u548c\u4ed6\u5b8c\u5168\u4e00\u6837\u4e86\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u779f\u4e86\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4eff\u4f5b\u4e5f\u5f88\u75b2\u5026\u5f97\u8fde\u8bdd\u90fd\u4e0d\u60f3\u8bf4\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u89c6\u7740\u4ed6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u6b66\u529f\u6700\u9ad8\u7684\u4e24\u4e2a\u5df2\u88ab\u4f60\u4eec\u51fb\u8d25\u4e86 \uff0c\u5929\u4e0b\u52bf\u529b\u6700\u5927\u7684\u4e00\u4e2a\u5e2e\u4f1a\u4e5f\u5df2\u5728\u4f60\u4eec\u624b\u4e2d\u74e6\u89e3\uff0c\u4f60\u4eec\u672c\u8be5\u89c9\u5f97\u5f88\u5f00\u5fc3\uff0c\u5f88\u5f97\u610f\u624d\u5bf9\uff0c\u4f46\u4f60 \u4eec\u770b\u8d77\u6765\u5374\u8fde\u4e00\u70b9\u9ad8\u5174\u7684\u6837\u5b50\u90fd\u6ca1\u6709\uff0c\u7b80\u76f4\u5c31\u597d\u8c61\u662f\u8d25\u7684\u662f\u4f60\u4eec\u81ea\u5df1\u4e00\u6837\u3002\u201d \u7b2c\u516b\u5341\u4e5d\u7ae0\u3000\u86c7\u8db3>> \u53e4\u9f99\u300a\u591a\u60c5\u5251\u5ba2\u65e0\u60c5\u5251\u300b \u7b2c\u516b\u5341\u4e5d\u7ae0\u3000\u86c7\u8db3 \u3000\u3000\u674e\u5bfb\u6b22\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u80dc\u5229\u4e4b\u540e\uff0c\u603b\u4f1a\u89c9\u5f97\u5f88\u75b2\u5026\uff0c\u5f88\u5bc2 \u5bde\u7684\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u5b8c\u5168\u80dc\u5229\uff0c\u5b8c\u5168\u6210\u529f\u4e86\uff0c\u5df2\u6ca1\u6709\u4ec0\u4e48\u4e8b\u597d\u518d\u8ba9\u4ed6\u53bb\u594b\u6597\u7684\uff0c\u4e00 \u4e2a\u5931\u8d25\u4e86\u7684\u4eba\u7cbe\u795e\u53cd\u800c\u4f1a\u632f\u4f5c\u4e9b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u54ac\u7740\u5634\u5507\uff0c\u60a0\u60a0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6210\u529f\u7684\u6ecb\u5473\u5c82\u975e\u4e5f\u4e0d\u597d\u53d7\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u867d\u7136\u4e5f\u4e0d\u592a\u597d\u53d7\uff0c\u4f46\u81f3\u5c11\u603b\u6bd4\u5931\u8d25\u597d\u5f97\u591a\u3002 \u201d \u3000\u3000\u80dc\u5229\u548c\u6210\u529f\u5e76\u4e0d\u80fd\u4ee4\u4eba\u771f\u7684\u6ee1\u8db3\uff0c\u4e5f\u4e0d\u80fd\u4ee4\u4eba\u771f\u7684\u5feb\u4e50\u3002 \u3000\u3000\u771f\u6b63\u7684\u5feb\u4e50\u662f\u4f60\u6b63\u5411\u4e0a\u594b\u6597\u7684\u65f6\u5019\u3002 \u3000\u3000\u4f60\u53ea\u8981\u7ecf\u5386\u8fc7\u8fd9\u79cd\u5feb\u4e50\uff0c\u4f60\u5c31\u6ca1\u6709\u767d\u6d3b\u3002 \u3000\u3000\u957f\u4ead\uff0c\u81ea\u53e4\u4ee5\u6765\u5c31\u662f\u4eba\u4eec\u996f\u522b\u4e4b\u5730\uff0c\u79bb\u522b\u603b\u662f\u4ee4\u4eba\u9eef\u7136\u795e\u4f24\uff0c\u8fd9\u4f7f\u5f97\u201c\u957f\u4ead\u201d\u8fd9\u4e24\u4e2a\u5b57 \u672c\u8eab\u5c31\u4eff\u4f5b\u5e26\u7740\u51c4\u51c9\u8427\u7d22\u4e4b\u610f\u3002 \u3000\u3000\u96e8\u5df2\u4f4f\uff0c\u8352\u8349\u51c4\u51c4\u3002 \u3000\u3000\u957f\u4ead\u5916\uff0c\u5c0f\u9053\u65c1\uff0c\u6b63\u6709\u4e00\u53cc\u5c11\u5e74\u7537\u5973\u6bb7\u6bb7\u8bdd\u522b\u3002 \u3000\u3000\u82f1\u633a\u7684\u5c11\u7537\uff0c\u591a\u60c5\u7684\u5c11\u5973\uff0c\u4ed6\u4eec\u663e\u7136\u662f\u76f8\u7231\u7684\uff0c\u4ed6\u672c\u8be5\u5b88\u5728\u4e00\u8d77\uff0c\u4eab\u53d7\u9752\u6625\u7684\u6b22\u5a31\uff0c\u4e3a \u4ec0\u4e48\u8981\u8f7b\u8a00\u96be\u79bb\u5462\uff1f \u3000\u3000\u5c11\u7537\u7684\u8eab\u4e0a\u8d1f\u7740\u5251\uff0c\u4f46\u65e0\u8bba\u591a\u950b\u5229\u7684\u5251\u90fd\u65a9\u4e0d\u65ad\u591a\u60c5\u513f\u5973\u7684\u79bb\u6101\u522b\u7eea\uff0c\u4ed6\u773c\u775b\u7ea2\u7ea2\u7684\uff0c \u4eff\u4f5b\u4e5f\u66fe\u6d41\u8fc7\u6cea\u3002 \u3000\u3000\u201c\u9001\u5230\u8fd9\u91cc\u5c31\u591f\u4e86\uff0c\u4f60\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u5c11\u5973\u5782\u7740\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u5c11\u7537\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u4e5f\u8bb8\u4e00\u4e24\u5e74\uff0c\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u5c11\u5973\u7684\u6cea\u53c8\u6d41\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6211\u7b49\u90a3\u4e48\u4e45\uff1f\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u8d70\uff1f\u201d \u3000\u3000\u5c11\u7537\u7684\u8170\u633a\u5f97\u66f4\u76f4\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u6211\u8981\u627e\u5230\u90a3\u4e9b\u4eba\uff0c\u5c06\u4ed6\u4eec\u51fb\u8d25\uff01\u201d \u3000\u3000\u4ed6\u51dd\u6ce8\u7740\u8fdc\u65b9\uff0c\u773c\u775b\u91cc\u53d1\u7740\u5149\uff0c\u63a5\u7740\u9053\uff1a\u201c\u90a3\u4e9b\u5728\u5175\u5668\u8c31\u4e0a\u5217\u540d\u7684\u4eba\uff0c\u4e0a\u5b98\u91d1\u8679\uff0c\u674e\u5bfb \u6b22\uff0c\u90ed\u5d69\u9633\uff0c\u5415\u51e4\u5148\u2026\u2026\u6211\u8981\u8ba9\u4ed6\u4eec\u77e5\u9053\uff0c\u6211\u6bd4\u4ed6\u4eec\u66f4\u5f3a\uff0c\u7136\u540e\u2026\u2026\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u7136\u540e\u600e\u4e48\u6837\uff1f\u6211\u4eec\u73b0\u5728\u5df2\u7ecf\u5f88\u5feb\u4e50\u4e86\uff0c\u4f60\u5c06\u4ed6\u4eec\u51fb\u8d25\u540e\uff0c\u6211\u4eec\u4f1a\u66f4\u5feb\u4e50\u5417\uff1f \u201d \u3000\u3000\u5c11\u7537\u9053\uff1a\u201c\u4e5f\u8bb8\u4e0d\u4f1a\uff0c\u53ef\u662f\u6211\u4e00\u5b9a\u8981\u53bb\u505a\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c11\u7537\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u80fd\u5c31\u8c61\u8fd9\u6837\u9ed8\u9ed8\u65e0\u95fb\u7684\u8fc7\u4e00\u8f88\u5b50\uff0c\u6211\u4e00\u5b9a\u8981\u6210\u540d\uff0c\u8981\u8c61\u4e0a\u5b98\u91d1\u8679\u548c \u674e\u5bfb\u6b22\u90a3\u4e48\u6709\u540d\uff0c\u800c\u4e14\u6211\u4e00\u5b9a\u80fd\u505a\u5230\uff01\u201d \u3000\u3000\u4ed6\u7d27\u63e1\u62f3\uff0c\u663e\u5f97\u90a3\u4e48\u575a\u5f3a\uff0c\u90a3\u4e48\u5174\u594b\u3002 \u3000\u3000\u5c11\u5973\u671b\u7740\u4ed6\uff0c\u76ee\u5149\u5e26\u7740\u53d9\u4e0d\u5c3d\u7684\u67d4\u60c5\u5bc6\u610f\uff0c\u7ec8\u4e8e\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60 \u4e00\u5b9a\u80fd\u505a\u5230\u7684\uff0c\u65e0\u8bba\u4f60\u8981\u53bb\u591a\u4e45\uff0c\u6211\u90fd\u7b49\u7740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5fc3\u91cc\u5145\u6ee1\u4e86\u79bb\u522b\u7684\u75db\u82e6\uff0c\u4e5f\u5145\u6ee1\u4e86\u5bf9\u672a\u6765\u5e78\u798f\u7684\u61a7\u61ac\u3002 \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u4e0d\u4f1a\u6ce8\u610f\u522b\u4eba\u3002 \u3000\u3000\u6797\u4e0b\u5374\u6709\u4eba\u4e00\u76f4\u5728\u6ce8\u610f\u4ed6\u4eec\u3002 \u3000\u3000\u76f4\u5230\u90a3\u5c11\u5e74\u6602\u9996\u9614\u6b65\uff0c\u8e0f\u4e0a\u5f81\u9014\uff0c\u5b59\u5c0f\u7ea2\u624d\u53f9\u4e86\u53e3\u6c14\u60a0\u60a0\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u82e5\u77e5\u9053\u4e0a\u5b98\u91d1\u7ea2 \u7684\u7ed3\u5c40\uff0c\u53ea\u6015\u5c31\u4e0d\u4f1a\u79bb\u5f00\u4ed6\u7684\u60c5\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u6210\u540d\u4e4b\u540e\u53c8\u600e\u4e48\u6837\u5462\uff1f \u3000\u3000\u5b59\u5c0f\u7ea2\u51dd\u89c6\u7740\u674e\u5bfb\u6b22\uff0c\u76ee\u5149\u91cc\u4f3c\u4e5f\u6709\u6cea\uff0c\u6084\u6084\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u60f3\u548c\u4f60\u4e00\u6837\u6709\u540d\uff0c\u53ef\u662f\u4f60\u2026 \u2026\u4f60\u662f\u4e0d\u662f\u5c31\u6bd4\u4ed6\u5feb\u4e50\uff1f\u6211\u60f3\u2026\u2026\u4f60\u82e5\u662f\u4ed6\uff0c\u4e00\u5b9a\u5c31\u4e0d\u4f1a\u50cf\u4ed6\u8fd9\u4e48\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7684\u76ee\u5149\u8fd8\u505c\u7559\u5728\u90a3\u5c11\u5e74\u7684\u8eab\u5f71\u6d88\u5931\u5904\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u624d\u6c89\u58f0\u9053\uff1a\u201c\u6211\u82e5\u662f\u4ed6\uff0c\u4e5f\u4f1a \u8fd9\u4e48\u6837\u505a\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u6115\u7136\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u4eba\u6d3b\u7740\uff0c\u5c31\u8981\u6709\u7406\u60f3\uff0c\u6709\u76ee\u7684\uff0c\u5c31\u8981\u4e0d\u987e\u4e00\u5207\u53bb\u594b\u6597\uff0c\u81f3\u4e8e\u594b\u6597\u7684\u7ed3\u679c\u662f \u4e0d\u662f\u6210\u529f\uff1f\u662f\u4e0d\u662f\u5feb\u4e50\uff1f\u4ed6\u4eec\u5e76\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u5634\u89d2\u5e26\u7740\u5fae\u7b11\uff0c\u773c\u4e2d\u53d1\u7740\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u4e5f\u8bb8\u4f1a\u8ba4\u4e3a\u90a3\u79cd\u4eba\u50bb\uff0c\u4f46\u4e16\u4e0a\u82e5\u6ca1\u6709 \u8fd9\u79cd\u4eba\uff0c\u8fd9\u4e16\u754c\u65e9\u5c31\u4e0d\u77e5\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\u4e86\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u76ee\u4e2d\u5ffd\u7136\u4e5f\u5145\u6ee1\u4e86\u548c\u521a\u624d\u90a3\u5c11\u5973\u540c\u6837\u7684\u67d4\u60c5\u5bc6\u610f\uff0c\u5979\u4e5f\u548c\u90a3\u5c11\u5973\u4e00\u6837\uff0c\u6b63\u5728\u4e3a\u5979 \u7684\u7537\u4eba\u9a84\u50b2\u3002 \u3000\u3000\u963f\u98de\u7ad9\u5728\u66f4\u8fdc\u4e9b\uff0c\u73b0\u5728\u624d\u6162\u6162\u7684\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f46\u5b59\u5c0f\u7ea2\u8fd8\u662f\u7d27\u7d27\u62c9\u7740\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u6ca1\u6709\u677e\u5f00\uff0c\u5979\u5e76\u4e0d\u5bb3\u7f9e\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u5979\u7684\u611f\u60c5\u5e76\u6ca1 \u6709\u89c1\u4e0d\u5f97\u4eba\u7684\u5730\u65b9\u3002 \u3000\u3000\u5979\u7b80\u76f4\u6068\u4e0d\u5f97\u5c06\u5979\u7684\u611f\u60c5\u5f53\u7740\u5168\u4e16\u754c\u7684\u4eba\u8868\u9732\u51fa\u6765\u3002 \u3000\u3000\u963f\u98de\u7a81\u7136\u9053\uff1a\u201c\u6211\u60f3\u5979\u4e00\u5b9a\u4e0d\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5728\u8fd9\u91cc\u7b49\u6797\u8bd7\u97f3\u7684\u3002 \u3000\u3000\u6797\u8bd7\u97f3\u548c\u9f99\u5578\u4e91\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\uff0c\u6b63\u5982\u4e0a\u5b98\u91d1\u8679\u7684\u906d\u9047\uff0c\u90a3\u5c11\u5e74\u4e5f\u4e0d\u77e5\u4e00 \u6837\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\u4e0d\u77e5\u9053\u53cd\u800c\u6bd4\u77e5\u9053\u597d\u3002 \u3000\u3000\u542c\u5230\u201c\u4ed6\u201d\u60f3\u5230\u6797\u8bd7\u97f3\uff0c\u5b59\u5c0f\u7ea2\u7684\u53d7\u624d\u4e0d\u77e5\u4e0d\u89c9\u79fb\u5f00\u3002 \u3000\u3000\u4f46\u5979\u7acb\u523b\u53c8\u63e1\u7d27\uff0c\u63e1\u7684\u66f4\u7d27\uff0c\u9053\uff1a\u201c\u5979\u8ddf\u6211\u7ea6\u597d\uff0c\u4e00\u5b9a\u4f1a\u6765\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5979\u4e0d\u4f1a\u6765\uff01\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u81ea\u5df1\u4e5f\u8be5\u77e5\u9053\uff0c\u5979\u5df2\u4e0d\u5fc5\u6765\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u672c\u662f\u5b59\u5c0f\u7ea2\u95ee\u4ed6\u7684\uff0c\u4f46\u4ed6\u5728\u56de\u7b54\u7684\u65f6\u5019\uff0c\u773c\u775b\u5374\u51dd\u89c6\u7740\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u4e5f\u6ca1\u6709\u653e\u5f00\u5b59\u5c0f\u7ea2\u7684\u624b\u3002 \u3000\u3000\u4ee5\u524d\u4ed6\u6bcf\u6b21\u542c\u522b\u4eba\u8bf4\u8d77\u6797\u8bd7\u97f3\uff0c\u5fc3\u91cc\u603b\u4f1a\u89c9\u5f97\u6709\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u6b49\u759a\u548c\u75db\u82e6\uff0c\u90a3\u4e5f\u6b63\u50cf\u662f \u4e00\u628a\u9501\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u9501\u4f4f\u3002 \u3000\u3000\u4ed6\u603b\u8ba4\u4e3a\u81ea\u5df1\u5fc5\u5c06\u6c38\u8fdc\u8d1f\u62c5\u7740\u8fd9\u75db\u82e6\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u7684\u75db\u82e6\u5374\u4f3c\u5df2\u4e0d\u5982\u6614\u65e5\u5f3a\u70c8\uff0c\u662f\u4ec0\u4e48\u529b\u91cf\u5c06\u4ed6\u7684\u9501\u89e3\u5f00\u7684\u5462\uff1f \u3000\u3000\u4ed6\u548c\u6797\u8bd7\u97f3\u7684\u60c5\u611f\u662f\u6162\u6162\u79ef\u7d2f\u7684\uff0c\u6240\u4ee5\u624d\u4f1a\u90a3\u4e48\u6df1\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u548c\u4ed6\u7684\u60c5\u611f\u867d\u8f83\u77ed\u6682\uff0c\u4f46\u7ecf\u5386\u4e86\u6700\u5927\u7684\u60a3\u96be\u6298\u78e8\uff0c\u7ecf\u5386\u4e86\u51fa\u751f\u5165\u6b7b\u7684\u5371\u9669\u3002 \u3000\u3000\u8fd9\u79cd\u611f\u60c5\u662f\u4e0d\u662f\u66f4\u5f3a\u70c8\uff1f \u3000\u3000\u8fd9\u65f6\u6797\u8bd7\u97f3\u5df2\u79bb\u5f00\u4ed6\u4eec\u5f88\u8fdc\u4e86\u3002 \u3000\u3000\u963f\u98de\u8bf4\u7684\u4e0d\u9519\u2014\u2014\u5979\u6ca1\u6709\u6765\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u4e0d\u5fc5\u6765\u3002 \u3000\u3000\u9f99\u5c0f\u4e91\u66fe\u7ecf\u95ee\u8fc7\u5979\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u53bb\u89c1\u4ed6\u6700\u540e\u4e00\u6b21\uff1f\u201d \u3000\u3000\u6797\u8bd7\u97f3\u5c31\u53c8\u95ee\u5979\u7684\u513f\u5b50\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u53bb\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u9f99\u5c0f\u4e91\u56de\u7b54\u7684\u65f6\u5019\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u6211\u81f3\u5c11\u8981\u8ba9\u4ed6\u77e5\u9053\uff0c\u6211\u7236\u4eb2\u662f\u4e3a\u4e86\u4ec0\u4e48\u6b7b\u7684\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u65e0\u8bba\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u73b0\u5728\u90fd\u4ee5\u7528\u8840\u6d17\u6e05\u4e86\u3002 \u3000\u3000\u4f5c\u513f\u5b50\u7684\u81ea\u7136\u5e0c\u671b\u522b\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4f46\u6797\u8bd7\u97f3\u5374\u4e0d\u8fd9\u4e48\u60f3\uff1a\u201c\u4ed6\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u56e0\u4e3a\u4ed6\u81ea\u5df1\u89c9\u5f97\u5e94\u8be5\u8fd9\u4e48\u6837\u505a\uff0c\u5e76\u4e0d\u662f\u8981\u6c42\u522b \u4eba\u539f\u8c05\uff0c\u4e5f\u5e76\u4e0d\u662f\u60f3\u8981\u522b\u4eba\u77e5\u9053\u3002\u201d\u5979\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u4e0d\u4f46\u4e3a\u81ea\u5df1\u6d17\u6e05\u4e86\u503a\uff0c\u4e5f\u4e3a\u6211\u4eec \u8fd8\u6e05\u4e86\u503a\uff0c\u53ea\u8981\u6211\u4eec\u80fd\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\uff0c\u4ed6\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u5979\u4e0d\u60f3\u53bb\u89c1\u674e\u5bfb\u6b22\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u89c1\u4e86\u53ea\u6709\u4ee4\u5f7c\u6b64\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u6ca1\u6709\u518d\u53bb\u5bfb\u627e\u9f99\u5578\u4e91\u7684\u5c38\u8eab\uff0c\u56e0\u4e3a\u6c5f\u6e56\u4e2d\u4eba\u90fd\u77e5\u9053\uff0c\u91d1\u94b1\u5e2e\u5bf9\u5904\u7406\u5c38\u4f53\u7684\u65b9\u6cd5\u4e0d \u4f46\u5f88\u7279\u522b\uff0c\u800c\u4e14\u5f88\u8fc5\u901f\u3002 \u3000\u3000\u4ed6\u4eec\u82e5\u53bb\u5bfb\u627e\uff0c\u627e\u5230\u7684\u4e5f\u53ea\u6709\u75db\u82e6\u2014\u2014\u8fd9\u4e5f\u6b63\u5982\u5b59\u5c0f\u7ea2\u6240\u77e5\u9053\u7684\u4e00\u6837\uff0c\u5979\u7237\u7237\u7684\u5c38\u8eab\u4e5f \u6c38\u8fdc\u4e5f\u627e\u4e0d\u5230\u7684\u4e86\u3002 \u3000\u3000\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u65e0\u53ef\u5948\u4f55\u7684\u4e8b\uff0c\u65e0\u8bba\u8c01\u90fd\u65e0\u80fd\u4e3a\u529b\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u867d\u75db\u82e6\uff0c\u4f46\u4e00\u4e2a\u4eba\u82e5\u8981\u6d3b\u7740\uff0c\u5c31\u5f97\u60f3\u6cd5\u5b50\u5c06\u8fd9\u79cd\u75db\u82e6\u7529\u6389\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u51b3\u5fc3\u8981\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\uff01\u56e0\u4e3a\u6b7b\u4e5f\u4e0d\u662f\u89e3\u51b3\u8fd9\u79cd\u95ee\u9898\u7684\u597d\u6cd5\u5b50\u2014\u2014\u6b7b\u6839\u672c\u5c31\u4e0d\u662f\u89e3 \u51b3\u4efb\u4f55\u4e8b\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u957f\u4ead\u5185\u53c8\u6709\u4eba\u5728\u996f\u522b\u3002 \u3000\u3000\u8fd9\u6b21\u8981\u53bb\u7684\u662f\u963f\u98de\uff0c\u4ed6\u8bf4\u4ed6\u8981\u5230\u201c\u6d77\u4e0a\u201d\u53bb\u770b\u770b\uff0c\u627e\u627e\u662f\u4e0d\u662f\u771f\u6709\u957f\u751f\u7684\u4ed9\u8349\uff0c\u4e0d\u6b7b\u7684 \u795e\u4ed9\u3002 \u3000\u3000\u4ed6\u8bf4\u7684\u5f53\u7136\u4e0d\u662f\u771f\u8bdd\uff0c\u4f46\u674e\u5bfb\u6b22\u4e5f\u5e76\u6ca1\u6709\u963b\u6321\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u8eab\u4e16\u59cb\u7ec8\u662f\u4e2a\u8c1c\uff0c\u751a\u81f3\u5728\u674e\u5bfb\u6b22\u9762\u524d\uff0c\u4ed6\u4e5f\u4ece\u6765\u4e0d\u613f\u63d0\u8d77\uff0c\u4f46\u6bcf\u5f53\u674e\u5bfb\u6b22\u8bf4\u8d77 \u6c88\u6d6a\uff0c\u718a\u732b\u513f\uff0c\u738b\u601c\u82b1\uff0c\u6731\u4e03\u4e03\uff0c\u8fd9\u4e9b\u4f20\u5947\u4eba\u7269\u7684\u4f20\u5947\u6545\u4e8b\u65f6\uff0c\u4ed6\u8138\u4e0a\u603b\u4f1a\u73b0\u51fa\u4e00\u79cd\u5f88\u5947\u602a \u7684\u8868\u60c5\u3002 \u3000\u3000\u96be\u9053\u4ed6\u548c\u8fd9\u4e9b\u524d\u8f88\u540d\u4fa0\u6709\u67d0\u79cd\u5f88\u5947\u7279\u5fae\u5999\u7684\u5173\u7cfb\uff1f \u3000\u3000\u4ed6\u8fd9\u6b21\u8981\u8fdc\u6e38\u6d77\u5916\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u53bb\u5bfb\u8bbf\u4ed6\u4eec\uff1f \u3000\u3000\u674e\u5bfb\u6b22\u5e76\u6ca1\u6709\u95ee\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u4e00\u4e2a\u4eba\u7684\u8eab\u4e16\u5e76\u4e0d\u91cd\u8981\u2014\u2014\u4eba\u65e2\u4e0d\u662f\u72d7\uff0c\u4e5f\u4e0d\u662f\u9a6c\uff0c\u4e00\u5b9a\u8981\u201c\u540d\u79cd\u201d\u624d\u597d\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8981\u6210\u4e3a\u600e\u4e48\u6837\u7684\u4eba\uff0c\u5168\u90fd\u8981\u770b\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u624d\u662f\u6700\u91cd\u8981\u7684\u3002 \u3000\u3000\u670b\u53cb\u95f4\u7684\u79bb\u522b\u603b\u5c11\u4e0d\u4e86\u795d\u798f\uff0c\u4e5f\u514d\u4e0d\u4e86\u4f24\u611f\uff0c\u4f46\u4ed6\u4eec\u7684\u79bb\u522b\u5374\u53ea\u6709\u795d\u798f\uff0c\u6ca1\u6709\u4f24\u611f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u786e\u4fe1\u5f7c\u6b64\u90fd\u4f1a\u597d\u597d\u7684\u6d3b\u7740\uff0c\u786e\u4fe1\u4ee5\u540e\u8fd8\u6709\u89c1\u9762\u7684\u65e5\u5b50\u3002 \u3000\u3000\u5c24\u5176\u5f53\u963f\u98de\u770b\u5230\u674e\u5bfb\u6b22\u7684\u624b\uff0c\u4ed6\u89c9\u5f97\u66f4\u653e\u5fc3\u4e86\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u624b\u8fd8\u662f\u548c\u5b59\u5c0f\u7ea2\u7684\u7d27\u7d27\u63e1\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8fd9\u53cc\u624b\u63e1\u5200\u7684\u65f6\u5019\u592a\u591a\uff0c\u4e3e\u676f\u7684\u65f6\u5019\u4e5f\u592a\u591a\u4e86\uff0c\u5200\u592a\u51b7\uff0c\u9152\u676f\u4e5f\u592a\u51b7\u4e86\uff0c\u73b0\u5728\u6b63\u5e94\u8be5\u8ba9 \u5b83\u4eab\u53d7\u6e29\u67d4\u7684\u6ecb\u5473\u3002 \u3000\u3000\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u6bd4\u60c5\u4eba\u7684\u624b\u66f4\u6e29\u67d4\u7684\u5462\uff1f \u3000\u3000\u963f\u98de\u77e5\u9053\u5b59\u5c0f\u7ea2\u4e00\u5b9a\u4f1a\u6bd4\u4efb\u4f55\u4eba\u90fd\u73cd\u60dc\u8fd9\u53cc\u624b\uff0c\u8fd9\u53cc\u624b\u7eb5\u7136\u8fd8\u6709\u5251\u75d5\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u6e10\u6e10\u5e73 \u6108\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u81ea\u5df1\uff0c\u4ed6\u5f53\u7136\u4e5f\u6709\u8fc7\u5251\u4f24\u3002 \u3000\u3000\u4f46\u4ed6\u4e0d\u613f\u518d\u63d0\u3002 \u3000\u3000\u201c\u8fc7\u53bb\u7684\uff0c\u5168\u90fd\u5df2\u8fc7\u53bb\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u770b\u6765\u4eff\u4f5b\u5f88\u7b80\u5355\uff0c\u5176\u5b9e\u771f\u80fd\u505a\u5230\u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u5e78\u4e8f\u674e\u5bfb\u6b22\u548c\u963f\u98de\u5168\u90fd\u5df2\u505a\u5230\u4e86\u3002 \u3000\u3000\u963f\u98de\u5ffd\u7136\u9053\uff1a\u201c\u4e09\u5e74\u540e\uff0c\u6211\u4e00\u5b9a\u4f1a\u56de\u6765\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u77a7\u7740\u4ed6\u4eec\u7684\u624b\uff0c\u53c8\u9053\uff1a\u201c\u6211\u56de\u6765\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u5f53\u7136\u8981\u8bf7\u6211\u559d\u9152\u3002\u201d \u3000\u3000\u674e\u5bfb\u6b22\u9053\uff1a\u201c\u5f53\u7136\uff0c\u53ea\u53ef\u60dc\u4e09\u5e74\u672a\u514d\u592a\u957f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u8981\u559d\u7684\u90a3\u79cd\u9152\u5f88\u7279\u522b\uff0c\u4e0d\u77e5\u9053\u4f60\u4eec\u80af\u4e0d\u80af\u8bf7\uff1f\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u5f3a\u7740\u9053\uff1a\u201c\u4f60\u8981\u559d\u4ec0\u4e48\u9152\uff1f\u201d \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u5f53\u7136\u662f\u559c\u9152\u3002\u201d \u3000\u3000\u559c\u9152\uff0c\u5f53\u7136\u662f\u559c\u9152\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u8981\u559d\u559c\u9152\uff0c\u6240\u4ee5\u624d\u8981\u7b49\u4e09\u5e74\u2014\u2014\u65e0\u8bba\u4e3a\u8c01\u5b88\u4e27\uff0c\u4e09\u5e74\u90fd\u5df2\u8db3\u591f\u3002 \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u8138\u7ea2\u4e86\u3002 \u3000\u3000\u963f\u98de\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u9152\u90fd\u559d\u8fc7\uff0c\u5c31\u662f\u6ca1\u559d\u8fc7\u559c\u9152\uff0c\u53ea\u5e0c\u671b\u4f60\u4eec\u83ab\u4ee4\u6211\u5931\u671b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u7684\u8138\u66f4\u7ea2\uff0c\u5782\u4e0b\u5934\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u5077\u5077\u53bb\u77a7\u674e\u5bfb\u6b22\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u7684\u795e\u60c5\u5f88\u7279\u522b\uff0c\u201c\u559c\u9152\u201d\u4e24\u4e2a\u5b57\uff0c\u4f3c\u4e4e\u4ee4\u4ed6\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u7f13\u7f13 \u9053\uff1a\u201c\u6211\u4ec0\u4e48\u9152\u90fd\u8bf7\u4eba\u559d\u8fc7\uff0c\u5c31\u662f\u4ece\u672a\u8bf7\u4eba\u559d\u8fc7\u559c\u9152\uff0c\u4f60\u53ef\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u98de\u5f53\u7136\u4e0d\u77e5\u9053\uff0c\u674e\u5bfb\u6b22\u4e5f\u4e0d\u60f3\u8981\u4ed6\u56de\u7b54\u3002 \u3000\u3000\u674e\u5bfb\u6b22\u81ea\u5df1\u8bf4\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u559c\u9152\u592a\u8d35\u4e86\u3002\u201d \u3000\u3000\u963f\u98de\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u592a\u8d35\uff1f\u201d \u3000\u3000\u674e\u5bfb\u6b22\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u4e00\u4e2a\u7537\u4eba\u82e5\u8981\u8bf7\u4eba\u559d\u559c\u9152\uff0c\u90a3\u5c31\u8868\u793a\u4ed6\u4e00\u8f88\u5b50\u90fd\u5f97\u6162\u6162\u7684\u6765\u4ed8 \u8fd9\u7b14\u5e10\uff0c\u53ea\u53ef\u60dc\u6211\u53c8\u504f\u504f\u4e0d\u613f\u4ee4\u670b\u53cb\u5931\u671b\u3002\u201d \u3000\u3000\u5b59\u5c0f\u7ea2\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u6295\u5165\u4ed6\u6000\u91cc\u3002 \u3000\u3000\u963f\u98de\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u5f88\u4e45\u72e0\u4e45\u6ca1\u6709\u8fd9\u4e48\u6837\u7b11\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u7b11\uff0c\u4f7f\u4ed6\u9aa4\u7136\u89c9\u5f97\u81ea\u5df1\u53c8\u5e74\u8f7b\u4e86\u8d77\u6765\uff0c\u5bf9\u81ea\u5df1\u53c8\u5145\u6ee1\u4e86\u52c7\u6c14\u548c\u4fe1\u5fc3\uff0c\u5bf9\u4eba\u751f\u53c8\u5145\u6ee1 \u4e86\u5e0c\u671b\u3002 \u3000\u3000\u5c31\u8fde\u90a3\u51cb\u96f6\u7684\u6728\u53f6\uff0c\u5728\u4ed6\u773c\u4e2d\u90fd\u5145\u6ee1\u4e86\u751f\u673a\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u5728\u90a3\u91cc\u8fd8\u6709\u65b0\u7684\u751f\u547d\uff0c\u4e0d\u4e45\u5c31 \u8981\u6709\u65b0\u82bd\u8301\u957f\u3002 \u3000\u3000\u4ed6\u4ece\u4e0d\u77e5\u9053\u201c\u7b11\u201d\u7adf\u6709\u8fd9\u4e48\u5927\u7684\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u4f69\u670d\u674e\u5bfb\u6b22\uff0c\u4e5f\u5f88\u611f\u6fc0\uff0c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u80fd\u4f7f\u81ea\u5df1\u6c38\u4fdd\u7b11\u97f3\uff0c\u56fa\u7136\u5df2\u5f88\u4e0d\u5bb9\u6613\uff0c\u82e5\u8fd8 \u80fd\u8ba9\u522b\u4eba\u7b11\uff0c\u624d\u771f\u6b63\u4f1f\u5927\uff01 \u3000\u3000\u201c\u753b\u86c7\u6dfb\u8db3\u201d\u4e0d\u4f46\u662f\u591a\u4f59\u7684\uff0c\u800c\u4e14\u662f\u53ef\u7b11\u3002 \u3000\u3000\u4f46\u4e16\u4e0a\u592a\u591a\u70e6\u607c\uff0c\u5c82\u975e\u5c31\u56e0\u4e3a\u7b11\u5f97\u592a\u5c11\uff1f \u3000\u3000\u7b11\uff0c\u5c31\u50cf\u662f\u9999\u6c34\uff0c\u4e0d\u4f46\u80fd\u4ee4\u81ea\u5df1\u82ac\u82b3\uff0c\u4e5f\u80fd\u4ee4\u522b\u4eba\u5feb\u4e50\u3002 \u3000\u3000\u4f60\u82e5\u80fd\u4ee4\u522b\u4eba\u7b11\u4e00\u7b11\uff0c\u7eb5\u7136\u505a\u505a\u611a\u8822\u7684\u4e8b\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000  \uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);